"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[98694],{31757:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u7b2c\u4e00\u7ae0\u3000\u94c1\u621f\u6e29\u4faf",content:(0,t.jsx)(t.Fragment,{children:"\u8305\u5e97\u9e21\u58f0\u65b9\u9e23\u2014\u2014 \u3000\u3000\u5728\u4e25\u51ac\u6e05\u6668\u51db\u51bd\u7684\u5bd2\u98ce\u91cc\uff0c\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\uff0c\u82f1\u59ff\u98d2\u723d\u7684\u5c11\u5e74\u4fca\u5f66\uff0c\u6084\u7136\u63a8\u5f00\u4e86\u5728\u8fd9\u8352\u6751 \u91cc\u552f\u4e00\u7684\u5c0f\u84d6\u6808\u90a3\u6247\u767d\u6768\u6728\u677f\u7684\u5e97\u95e8\uff0c\u7275\u51fa\u4ed6\u90a3\u89c6\u82e5\u6027\u547d\u822c\u706b\u7ea2\u4f3c\u7684\u540d\u9a79\uff0c\u4ef0\u5929\u957f\u957f\u5438\u4e86\u53e3 \u6c14\uff0c\u5bd2\u98ce\uff0c\u5f88\u5feb\u5730\u5c31\u51b2\u8fdb\u4ed6\u706b\u70ed\u7684\u80f8\u819b\u91cc\u3002 \u3000\u3000\u4ed6\u5634\u89d2\u6302\u7740\u4e00\u4e1d\u6df7\u5408\u7740\u50b2\u6162\u548c\u8ba5\u8bbd\u7684\u5fae\u7b11\uff0c\u500f\u7136\u4e0a\u4e86\u9a6c\uff0c\u9a6c\u8ff9\u5728\u96ea\u5730\u4e0a\u7559\u4e0b\u4e00\u8fde\u4e32\u8e44 \u75d5\uff0c\u9a6c\u978d\u65c1\u6302\u7740\u7684\u4e24\u4ef6\u6c88\u91cd\u7684\u7269\u4ef6\uff0c\u867d\u7136\u88ab\u4e25\u5bc6\u7684\u5305\u5728\u6cb9\u5e03\u91cc\uff0c\u7136\u800c\u5f53\u5b83\u4eec\u649e\u51fb\u7740\u9a6c\u978d\u6216\u662f \u9a6c\u94e0\u65f6\uff0c\u4ecd\u7136\u53d1\u51fa\u4e00\u9635\u9635\u58f0\u97f3\uff0c\u800c\u8fd9\u79cd\u58f0\u97f3\uff0c\u5f88\u660e\u663e\u5730\u53ef\u4ee5\u8ba9\u4eba\u542c\u51fa\u90a3\u662f\u5c5e\u4e8e\u4e24\u4ef6\u94c1\u5668\u649e\u51fb \u65f6\u7279\u6709\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\uff0c\u5e76\u6ca1\u6709\u5f15\u8d77\u4efb\u4f55\u4eba\u7684\u6ce8\u610f\uff0c\u56e0\u4e3a\u6b64\u523b\u6839\u672c\u6ca1\u6709\u4efb\u4f55\u4eba\u613f\u610f\u5192\u7740\u5bd2\u51b7\u7ad9\u5728\u8fd9\u6668\u98ce\u91cc\u3002 \u4f46\u82e5\u6709\u4eba\u77e5\u9053\u4ed6\u662f\u8c01\u65f6\uff0c\u90a3\u60c5\u51b5\u5c31\u4f1a\u5927\u4e0d\u76f8\u540c\u4e86\u3002 \u3000\u3000\u4ed6\uff0c\u5c31\u662f\u8fd1\u5e74\u6765\u5728\u6c5f\u6e56\u4e0a\u58f0\u540d\u663e\u8d6b\u7684\u94c1\u621f\u6e29\u4faf\u5415\u5357\u4eba\uff0c\u800c\u4ed6\u80fd\u5728\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u535a\u5f97\u8fd9 \u5982\u6b64\u76db\u540d\uff0c\u662f\u6709\u5176\u539f\u56e0\u7684\u3002 \u3000\u3000\u4e00\u5339\u7a00\u4e16\u7f55\u6709\u7684\u5b9d\u9a6c\uff0c\u548c\u4e00\u8eab\u7edd\u9876\u7684\u8f6f\u786c\u529f\u592b\uff0c\u518d\u52a0\u4e0a\u4e24\u4ef6\u5947\u95e8\u5175\u5203\u2014\u2014\u5bd2\u94c1\u53cc\u621f\uff0c\u8fd9 \u4f7f\u5f97\u4ed6\u5728\u51e0\u5e74\u4e4b\u5185\u51fb\u8d25\u4e86\u6240\u6709\u60f3\u548c\u4ed6\u4e3a\u96be\u7684\u6b66\u6797\u4eba\u7269\uff0c\u800c\u90a3\u5176\u4e2d\u5f53\u7136\u4e0d\u4e4f\u8bb8\u591a\u662f\u77e5\u540d\u9ad8\u624b\u3002 \u3000\u3000\u53e6\u5916\uff0c\u4ed6\u82f1\u4fca\u7684\u4eea\u8868\uff0c\u5374\u4f7f\u5f97\u4ed6\u5b34\u5f97\u4e86\u5f53\u65f6\u6b66\u6797\u7b2c\u4e00\u7f8e\u4eba\u859b\u82e5\u74a7\u4e3a\u59bb\uff0c\u4e8e\u662f\u94c1\u621f\u6e29\u4faf\u548c \u9500\u9b42\u592b\u4eba\u6210\u4e86\u6b66\u6797\u4e2d\u6700\u4ee4\u4eba\u7fa1\u6155\u7684\u4e00\u5bf9\u592b\u59bb\u3002\u5f53\u7136\uff0c\u548c\u7fa1\u6155\u6c38\u8fdc\u4e0d\u4f1a\u5206\u5f00\u7684\u4e24\u4e2a\u5b57\u5c31\u662f\u201c\u5992 \u5fcc\u201d\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u50cf\u5f80\u5e38\u4e00\u6837 \u3000\u3000\u94c1\u621f\u6e29\u4faf\u5415\u5357\u4eba\u6f47\u6d12\u800c\u677e\u6563\u5730\u9a91\u5728\u4ed6\u90a3\u5339\u9a6c\u4e0a\uff0c\u9a6c\u8e44\u5982\u98de\uff0c\u4ed6\u7684\u53f3\u624b\u575a\u5b9a\u5730\u6293\u7740 \u3000\u3000\u7ef3\uff0c\u9a6c\u7684\u7f8e\u4e3d\u7684\u9b03\u6bdb\u5728\u5bd2\u98ce\u4e2d\u98d8\u6d6e\u7740\u3002 \u3000\u3000\u4eba\u9a6c\u8fc7\u5904\uff0c\u6380\u8d77\u4e00\u9635\u6df7\u5408\u7740\u51b0\u96ea\u6ce5\u6c99\u7684\u5c18\u571f\uff0c\u94c1\u621f\u6e29\u4faf\u90a3\u82f1\u4fca\u7684\u8138\u5b54\u548c\u95ea\u95ea\u53d1\u5149\u7684\u773c \u775b\uff0c\u770b\u6765\u5f88\u5bb9\u6613\u5730\u4f7f\u4eba\u60f3\u5230\u6614\u5e74\u53f1\u5412\u98ce\u4e91\u7684\u6e29\u4faf\u5415\u5e03\uff0c\u8fd9\u96be\u602a\u4ed6\u6c38\u8fdc\u4e0d\u613f\u610f\u5355\u8eab\u4e0a\u8def\uff0c\u56e0\u4e3a \u4ed6\u751f\u6015\u6c5f\u6e56\u4e0a\u4e00\u4e9b\u672a\u5a5a\u5973\u5b50\u7684\u7ea0\u7f20\uff0c\u4e5f\u8bb8\u662f\u8fd9\u79cd\u7ea0\u7f20\u4ed6\u9047\u89c1\u7684\u592a\u591a\u4e86\u5427\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u4e3a\u4ec0\u4e48\u4ed6\u6b64\u523b\u662f\u5b64\u8eab\u800c\u884c\u5462\uff1f\u90a3\u548c\u4ed6\u65f6\u523b\u4e0d\u79bb\u7684\u9500\u9b42\u592b\u4eba\u859b\u82e5\u74a7\u5728\u90a3\u91cc\u5462.\uff0c\u4e3a\u4ec0\u4e48\u5728 \u4ed6\u90a3\u60ef\u6709\u7684\u7b11\u5bb9\u540e\u9762\uff0c\u7adf\u9690\u85cf\u7740\u4e00\u7247\u9634\u973e\u5462\uff1f \u3000\u3000\u9a6c\u884c\u867d\u6025\uff0c\u7136\u800c\u4ed6\u5374\u50cf\u662f\u6f2b\u65e0\u76ee\u7684\u4f3c\u7684\uff0c\u5e76\u6ca1\u6709\u4e00\u4e2a\u4e00\u5b9a\u7684\u65b9\u5411\u3002 \u3000\u3000\u5230\u4e86\u4fdd\u5b9a\u5e9c\uff0c\u4ed6\u5374\u5e76\u4e0d\u8fdb\u57ce\uff0c\u53ea\u662f\u5728\u57ce\u95e8\u5916\u515c\u7740\u5708\u5b50\uff0c\u50cf\u662f\u6545\u610f\u5728\u5438\u5f15\u7740\u522b\u4eba\u7684\u6ce8\u610f\u529b \u4f3c\u7684\uff0c\u4ed6\u751a\u81f3\u5c06\u672c\u6765\u5305\u6cb9\u5e03\u91cc\u7684\u5bd2\u94c1\u53cc\u621f\u62ff\u4e86\u51fa\u6765\uff0c\u673a\u68b0\u5730\u62ff\u5728\u624b\u4e0a\u642c\u5f04\u7740\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u4fdd\u5b9a\u5e9c\u91cc\u5c31\u4f20\u51fa\u94c1\u621f\u6e29\u4faf\u5728\u57ce\u5916\u5f98\u5f8a\u7684\u6d88\u606f\uff0c\u57ce\u91cc\u7684\u4e00\u4e9b\u6b66\u6797\u8c6a\u58eb\u90fd \u975e\u5e38\u5947\u602a\uff0c\u4ed6\u8fd9\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u4ed6\u5f53\u7136\u662f\u6709\u7740\u7f18\u56e0\u7684\uff0c\u56e0\u4e3a\u4ed6\u90a3\u6e05\u4fca\u7684\u8138\u4e0a\uff0c\u6b64\u523b\u8fd8\u6709\u4e9b\u671f\u5f85\u7684\u795e\u8272\uff0c\u503c\u5f97\u4ed6\u6240\u671f\u5f85\u7684 \u4e8b\u7269\uff0c\u4e5f\u8be5\u662f\u975e\u5e38\u5947\u5f02\u7684\u5427\uff1f \u3000\u3000\u201c\u524d\u9762\u5c31\u662f\u6587\u5e99\u524d\u7684\u57ce\u95e8\u53e3\u4e86\u3002\u201d\u4ed6\u5728\u5fc3\u4e2d\u6697\u5fd6\u7740\uff0c\u4f46\u662f\u4ed6\u4f9d\u7136\u4e0d\u7b56\u9a6c\u8fdb\u57ce\uff0c\u53ea\u662f\u5728\u62a4 \u57ce\u6cb3\u5916\u6f2b\u65e0\u76ee\u7684\u5730\u8e31\u7740\u9a6c\uff0c\u4e24\u53ea\u70af\u7136\u6709\u5149\u7684\u773c\u775b\uff0c\u4e0d\u65f6\u5730\u671b\u7740\u90a3\u655e\u5f00\u7740\u7684\u4fdd\u5b9a\u5e9c\u57ce\u95e8\u3002 \u3000\u3000\u679c\u7136\u57ce\u95e8\u91cc\u98ce\u4e5f\u4f3c\u5730\u51b2\u51fa\u6765\u5339\u5065\u9a6c\uff0c\u7565\u4e00\u5f20\u671b\uff0c\u7acb\u523b\u5411\u4ed6\u6240\u5728\u7684\u8fd9\u4e2a\u65b9\u5411\u5954\u9a70\u4e86\u8fc7\u6765\uff0c \u4ed6\u671b\u89c1\u9a6c\u4e0a\u7684\u9a91\u58eb\u4e2d\uff0c\u6709\u4e00\u4e2a\u7adf\u7a7f\u7740\u91d1\u8272\u7684\u8863\u670d\u65f6\uff0c\u50b2\u6162\u7684\u5634\u89d2\uff0c\u96be\u4ee5\u89c9\u5bdf\u7684\u5fae\u7b11\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u90a3\u7fa4\u5065\u9a6c\u5230\u4ed6\u8eab\u524d\u534a\u7bad\u4e4b\u5730\u5c31\u9f50\u90fd\u4e0b\u4e86\u9a6c\uff0c\u4e00\u4e2a\u9ed1\u8863\u58ee\u6c49\u7275\u7740\u9a6c\u8fdc\u8fdc\u7684\u8d70\u5f00\u4e86\uff0c\u53e6\u5916\u4e09 \u4e2a\u7a7f\u7740\u84dd\u8272\u8863\u670d\u7684\u6c49\u5b50\uff0c\u968f\u7740\u90a3\u91d1\u886b\u4eba\u5927\u8e0f\u6b65\u5411\u5415\u5357\u4eba\u8d70\u4e86\u8fc7\u6765\uff0c\u6b65\u5c65\u7a33\u5065\uff0c\u90fd\u6709\u4e0d\u574f\u7684\u6b66 \u529f\u6839\u57fa\u3002 \u3000\u3000\u5c24\u5176\u662f\u91d1\u886b\u4eba\uff0c\u90a3\u662f\u4e00\u4e2a\u77ee\u80d6\u7684\u8001\u8005\u2014\u2014\u8bf4\u4ed6\u662f\u8001\u8005\uff0c\u4e5f\u8bb8\u8fd8\u592a\u65e9\u4e86\u4e9b\uff0c\u4f46\u662f\u4ed6\u9762\u4e0a\u677e \u5f1b\u7684\u76ae\u8089\uff0c\u5374\u4f7f\u4eba\u770b\u8d77\u6765\u5728\u4ed6\u7684\u771f\u5b9e\u5e74\u9f84\u4e0a\u52a0\u4e86\u5341\u5c81\u2014\u2014\u4ed6\u6bcf\u4e00\u8e0f\u6b65\uff0c\u90fd\u50cf\u662f\u4e00\u53ea\u5de8\u8c61\u4f3c \u7684\uff0c\u4f7f\u4eba\u4e0d\u80fd\u4e0d\u88ab\u4ed6\u8fd9\u79cd\u6c88\u91cd\u7684\u811a\u529b\u6709\u6240\u60ca\u5f02\u3002 \u3000\u3000\u201c\u8fd9\u662f\u8c01\u5462\uff1f\u201d\u5415\u5357\u4eba\u5728\u5fc3\u4e2d\u6781\u5feb\u7684\u4e00\u8f6c\uff0c\u4ed8\u9053\uff1a\u201c\u6731\u7802\u638c\u5c24\u5927\u541b\uff1f\u4e00\u70b9\u4e0d\u9519\uff0c\u5c31\u662f\u6b64 \u4eba\u4e86\uff0c\u4ed6\u5012\u6b63\u5408\u6211\u7684\u7528\u573a\u3002\u201d \u3000\u3000\u770b\u5230\u6b64\u4eba\uff0c\u4ed6\u7adf\u50cf\u975e\u5e38\u9ad8\u5174\u4f3c\u7684\uff0c\u90a3\u53c8\u662f\u56e0\u4e3a\u4ec0\u4e48\u539f\u56e0\u5462\uff1f \u3000\u3000\u4ed6\u52a8\u4e5f\u4e0d\u52a8\u5730\u5750\u5728\u9a6c\u4e0a\uff0c\u90a3\u56db\u4eba\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u7acb\u523b\u6563\u5f00\uff0c\u8ba9\u90a3\u91d1\u886b\u7684\u80d6\u5b50\u2014\u2014\u6731\u7802\u638c\uff0c \u7a33\u5982\u5c71\u5cb3\u5730\u7ad9\u5728\u4ed6\u9762\u524d\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u5427\uff1f\u201d\u5415\u5357\u4eba\u8ba5\u8bbd\u5730\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u4f1a\u4ece\u6c5f\u5357\u8001\u8fdc\u8dd1\u5230\u6b64\u5730\u6765\u5427\uff1f\u201d \u3000\u3000\u5c24\u5927\u541b\u7684\u8138\u4e0a\uff0c\u679c\u7136\u6709\u60ca\u7591\u7684\u8868\u60c5\uff0c\u4f46\u5374\u88ab\u4ed6\u8138\u4e0a\u65e9\u5df2\u7ecf\u677e\u5f1b\u4e86\u7684\u80a5\u8089\u63a9\u9970\u5f97\u5f88\u597d\uff0c\u4ed6 \u6c88\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7684\u786e\u5947\u602a\u3002\u201d\u4ed6\u6545\u610f\u5728\u58f0\u97f3\u91cc\u653e\u8fdb\u4e9b\u5bd2\u610f\uff0c\u9053\uff1a\u201c\u53ea\u662f\u6211\u5947\u602a\u7684\u5e76\u4e0d\u662f\u4f60\u8dd1\u5230\u8fd9\u91cc \u6765\uff0c\u800c\u662f\u4f60\u5c45\u7136\u8fd8\u6562\u5728\u6b64\u9732\u9762\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u4ef0\u5929\u957f\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6562\u9732\u9762\uff0c\u96be\u9053\u6211\u8fd8\u6015\u4e86\u4f60\u4eec\uff1f\u201d\u4ed6\u7684\u8138\u4e0a\u6e10\u6e10\u7f69 \u4e0a\u4e00\u5c42\u5bd2\u971c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53eb\u6211\u5415\u5357\u4eba\u65e0\u5bb6\u53ef\u5f52\uff0c\u6211\u4e5f\u53eb\u4f60\u4eec\u4e0d\u5f97\u5b89\u5b81\uff0c\u6211\u5728\u6c5f\u5357\u7684\u8001\u5de2\u6597 \u4e0d\u8fc7\u4f60\u4eec\uff0c\u96be\u9053\u5728\u8fd9\u91cc\u6211\u8fd8\u6015\u4e86\u4f60\u4eec\u51e0\u4e2a\u9f20\u8f88\uff01\u201d \u3000\u3000\u5c24\u5927\u541b\u7acb\u523b\u5927\u6012\u8d77\u6765\uff0c\u8138\u5b54\u6da8\u5f97\u901a\u7ea2\uff0c\u4e24\u8fb9\u7684\u592a\u9633\u7a74\u8d8a\u53d1\u9f13\u8d77\u4e86\uff0c\u201c\u597d\uff0c\u597d\uff01\u201d\u4ed6\u5389\u58f0 \u9053\uff1a\u201c\u6211\u59d3\u5c24\u7684\u5c31\u53eb\u4f60\u770b\u770b\u54b1\u5929\u4e89\u6559\u5728\u4e24\u6cb3\u7684\u529b\u91cf\uff01\u201d\u7565\u4e3a\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u4ed6\u4f3c\u4e4e\u89c9\u5f97\u6240\u8bf4\u7684 \u8bdd\u8fd8\u4e0d\u80fd\u8868\u793a\u4ed6\u7684\u5c0a\u4e25\uff0c\u4e8e\u662f\u53c8\u52a0\u4e86\u53e5\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\u670b\u53cb\u4e0d\u53bb\u9003\u547d\uff0c\u8fd8\u60f3\u548c\u5929\u4e89\u6559\u8f83\u8f83\u52b2\uff0c\u90a3\u6562\u60c5\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u59d3\u5415\u7684\uff0c\u4f60\u4e0b\u6765\uff0c \u8ba9\u54b1\u6559\u8bad\u6559\u8bad\u4f60\uff01\u201d \u3000\u3000\u5415\u5357\u4eba\u53c8\u662f\u4e00\u9635\u957f\u7b11\uff0c\u968f\u7740\u7b11\u58f0\uff0c\u4ed6\u7075\u5de7\u800c\u5feb\u6377\u5730\u4e0b\u4e86\u9a6c\uff0c\u5c06\u624b\u4e2d\u7684\u53cc\u621f\u4e00\u5206\uff0c\u90a3\u4e48\u6c88 \u91cd\u7684\u5175\u5203\uff0c\u5728\u4ed6\u624b\u4e2d\u7adf\u50cf\u8349\u82a5\u4f3c\u7684\uff0c\u201c\u670b\u53cb\uff0c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u8d76\u5feb\u4eae\u201c\u9752\u5b50\u201d\u52a8\u624b\u5427\uff01\u201d\u4ed6\u6c88\u58f0 \u559d\u9053\u3002 \u3000\u3000\u201c\u6211\u59d3\u5c24\u7684\u52a8\u624b\uff0c\u8fd8\u6ca1\u6709\u7528\u8fc7\u5175\u5203\u3002\u201d\u84e6\u5730\uff0c\u5c24\u5927\u541b\u5389\u559d\u4e00\u58f0\uff0c\u4e5f\u672a\u89c1\u4ed6\u4f5c\u52bf\uff0c\u624b\u638c\u4e00 \u626c\uff0c\u4e00\u6643\u773c\u4fbf\u5df2\u7a9c\u5230\u5415\u5357\u4eba\u9762\u524d\u3002 \u3000\u3000\u4ed6\u638c\u5fc3\u8840\u7ea2\uff0c\u5415\u5357\u4eba\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u53ae\u7684\u6731\u7802\u638c\u7adf\u5df2\u6709\u4e86\u4e5d\u5206\u706b\u5019\u3002\u201d\u51b7\u7b11\u58f0 \u4e2d\uff0c\u811a\u6b65\u4e00\u9519\uff0c\u7adf\u5c06\u638c\u4e2d\u53cc\u621f\u629b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u201c\u8ddf\u4f60\u8fd9\u79cd\u9f20\u8f88\u52a8\u624b\uff0c\u5927\u7237\u4e5f\u7528\u4e0d\u7740\u52a8\u7528\u5175\u5203\u3002\u201d\u5415\u5357\u4eba\u4e5f\u5389\u58f0\u9053\u3002\u8fd9\u8bdd\u679c\u7136\u5c06\u6731\u7802\u638c \u66f4\u4e3a\u6fc0\u6012\uff0c\u63c9\u8eab\u8fdb\u6b65\uff0c\u4e00\u638c\u5411\u4ed6\u5929\u7075\u76d6\u5288\u4e0b\u3002 \u3000\u3000\u638c\u98ce\u864e\u864e\uff0c\u638c\u529b\u7684\u786e\u60ca\u4eba\uff0c\u5415\u5357\u4eba\u5374\u4e5f\u4f3c\u4e0d\u6562\u786c\u63a5\uff0c\u4e00\u6643\u8eab\u95ea\u4e86\u5f00\u53bb\uff0c\u6731\u7802\u638c\u66b4\u559d\u8fde \u8fde\uff0c\u9519\u6b65\u8f6c\u8eab\uff0c\u53c8\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6731\u7802\u638c\u79f0\u96c4\u4e24\u6cb3\u591a\u5e74\uff0c\u5728\u6b66\u6797\u8bba\u638c\u529b\uff0c\u5df2\u53ef\u6570\u4e00\u6d41\u4eba\u7269\uff0c\u662f\u4ee5\u5728\u5a01\u6151\u6b66\u6797\u7684\u5929\u4e89\u6559\u91cc\uff0c \u4e5f\u5360\u7740\u6781\u4e3a\u91cd\u8981\u7684\u5730\u4f4d\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u638c\u529b\u867d\u96c4\u539a\uff0c\u8eab\u6cd5\u5374\u4e0d\u751a\u7075\u4fbf\uff0c\u867d\u7136\u4ed6\u8fd9\u79cd\u8db3\u4ee5\u5f00\u5c71\u88c2\u77f3\u7684\u638c\u529b\uff0c\u5df2\u53ef\u5f25\u8865\u4ed6\u8eab \u6cd5\u4e0a\u7684\u4e0d\u8db3\uff1b\u4f46\u82e5\u771f\u7684\u9047\u5230\u7edd\u9876\u9ad8\u624b\uff0c\u4fbf\u8981\u5403\u4e8f\uff0c\u8fd9\u70b9\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u7684\u6781\u4e3a\u6e05\u695a\uff0c\u662f\u4ee5\u4ed6\u6b64 \u523b\u638c\u638c\u90fd\u662f\u715e\u62db\uff0c\u800c\u4e14\u90fd\u7528\u4e0a\u4e86\u4e5d\u6210\u529f\u529b\uff0c\u5b58\u5fc3\u5c06\u8fd9\u5e74\u7eaa\u867d\u8f7b\uff0c\u5728\u6c5f\u6e56\u4e0a\u5374\u5df2\u5927\u6709\u76db\u540d\u7684\u94c1 \u621f\u6e29\u4faf\u4e27\u5728\u638c\u4e0b\u3002 \u3000\u3000\u638c\u98ce\u5982\u5c71\uff0c\u638c\u5f71\u5982\u4e91\uff0c\u98ce\u4e91\u4e4b\u4e2d\uff0c\u94c1\u621f\u6e29\u4faf\u770b\u8d77\u6765\u5df2\u65e0\u8fd8\u624b\u4e4b\u529b\u4e86\uff01\u5728\u65c1\u8fb9\u864e\u89c6\u803d\u803d\u7740 \u7684\u90a3\u4e09\u4e2a\u84dd\u8863\u4eba\uff0c\u6b64\u523b\u9762\u4e0a\u90fd\u9732\u51fa\u4e86\u559c\u8272\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5fd6\u9053\uff1a\u201c\u8fd9\u59d3\u5415\u7684\u4e00\u4e22\u4e0b\u5175\u5203\u7adf\u8fd9\u4e48 \u4e0d\u6d4e\u4e8b\u3002\u201d\u6b23\u559c\u4e4b\u4e2d\uff0c\u5374\u53c8\u4e0d\u7981\u6709\u4e9b\u540e\u6094\u3002 \u3000\u3000\u201c\u65e9\u77e5\u9053\u6211\u4eec\u4e0a\u53bb\u4e5f\u662f\u4e00\u6837\u80fd\u5c06\u8fd9\u59d3\u5415\u7684\u6536\u62fe\u4e0b\u6765\uff0c\u662f\u4f55\u7b49\u9732\u8138\u7684\u4e8b\uff01\u6559\u4e3b\u77e5\u9053\u4e86\uff0c\u6015 \u4e0d\u628a\u6211\u4eec\u8fde\u5347\u51e0\u7ea7\uff1f\u201d\u4ed6\u4eec\u8d2a\u5a6a\u5730\u671b\u4e86\u5c24\u5927\u541b\u8eab\u4e0a\u7684\u91d1\u886b\u4e00\u773c\uff0c\u5fd6\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u4e5f\u53ef\u4ee5\u7a7f \u4e0a\u91d1\u8863\u88f3\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5728\u5fc3\u4e2d\u641e\u9b3c\uff0c\u5c24\u5927\u541b\u8138\u4e0a\u53c8\u4f55\u5c1d\u4e0d\u662f\u6ee1\u9762\u559c\u8272\uff0c\u638c\u62db\u66f4\u89c1\u72e0\u8fa3\uff0c\u6068\u4e0d\u5f97\u4e00\u638c\u5c31\u5c06\u5415 \u5357\u4eba\u7f6e\u4e4b\u6b7b\u5730\uff0c\u8fd9\u9664\u4e86\u5929\u4e89\u6559\u548c\u94c1\u621f\u6e29\u4faf\u4e4b\u95f4\u7684\u4ec7\u6028\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4efd\u4ed6\u81ea\u5df1\u60f3\u85c9\u7740\u51fb\u8d25\u540d\u4f20 \u56db\u6d77\u7684\u94c1\u621f\u6e29\u4faf\uff0c\u800c\u80fd\u5728\u6b66\u6797\u4e2d\u66f4\u589e\u957f\u51e0\u5206\u58f0\u671b\u7684\u96c4\u5fc3\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5f88\u660e\u663e\u5730\u5360\u4e86\u4e0a\u98ce\uff0c\u4f46\u4e00\u65f6\u534a\u523b\u4e4b\u95f4\uff0c\u5374\u662f\u65e0\u6cd5\u53d6\u80dc\u3002\u53c8\u662f\u5341\u6570\u4e2a\u7167\u9762\u8fc7\u53bb\uff0c\u94c1 \u621f\u6e29\u4faf\u8eab\u624b\u4f3c\u4e4e\u8d8a\u53d1\u4e0d\u5982\u5148\u524d\u7075\u4fbf\u4e86\u3002 \u3000\u3000\u6731\u7802\u638c\u7cbe\u795e\u9661\u957f\uff0c\u500f\u7136\u4f7f\u4e86\u4e2a\u9669\u62db\uff0c\u201c\u6012\u9a6c\u5206\u9b03\u201d\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u80f8\u524d\u7a7a\u95e8\u5927\u9732\u3002\u5415\u5357\u4eba \u5634\u89d2\u53c8\u95ea\u8fc7\u4e00\u4e1d\u96be\u4ee5\u89c9\u5bdf\u7684\u5fae\u7b11\uff0c\u62a2\u6b65\u8fdb\u8eab\uff0c\u9a88\u8d77\u53cc\u6307\uff0c\u671d\u4ed6\u5de6\u80c1\u7684\u201c\u671f\u95e8\u201d\u7a74\u70b9\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u679c\u7136\u4e0a\u5f53\u4e86\u3002\u201d\u8fd9\u5ff5\u5934\u5728\u6731\u7802\u638c\u7684\u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\u3002\u4ed6\u66b4\u559d\u4e00\u58f0\uff0c\u80f8\u8179\u4e00\u5438\uff0c \u201c\u5357\u4eba\u7684\u624b\u6307\u582a\u582a\u591f\u4e0d\u4e0a\u90e8\u95e8\uff0c\u5c31\u5728\u5415\u5357\u4eba\u64a4\u62db\u9000\u6b65\u4e4b\u95f4\uff0c\u5c24\u5927\u541b\u624b\u638c\u4e00\u7ffb\uff0c\u7830\u5730\u51fb\u5728\u4ed6\u80f8 \u819b\u4e0a\u3002 \u3000\u3000\u6731\u7802\u638c\u4ee5\u638c\u529b\u79f0\u96c4\u6b66\u6797\uff0c\u8fd9\u4e00\u638c\u529b\u9053\u4f55\u7b49\u4e4b\u5f3a\uff0c\u94c1\u621f\u6e29\u4faf\u72c2\u543c\u4e00\u58f0\uff0c\u53cc\u811a\u70b9\u5904\uff0c\u7bad\u4e00\u822c \u8dd1\u63a0\u4e86\u51fa\u53bb\u3002\u7075\u5de7\u5730\u63a0\u5230\u90a3\u5339\u59cb\u7ec8\u7b49\u5019\u5728\u65c1\u8fb9\u7684\u7075\u9a79\u978d\u4e0a\uff0c\u53cc\u817f\u4e00\u5939\uff0c\u4e00\u652f\u7bad\u4e5f\u4f3c\u7684\u7a9c\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u8f7b\u529f\u5012\u4e0d\u5f31\u3002\u201d\u6731\u7802\u638c\u4e00\u638c\u5f97\u624b\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u867d\u7136\u8f6c\u8fc7\u8fd9\u4e2a\u5ff5\u5934\uff0c\u4f46\u5374\u672a\u53bb\u60f3 \u4eba\u5bb6\u7684\u8f7b\u529f\u600e\u4f1a\u5982\u6b64\u9ad8\u660e\u3002 \u3000\u3000\u53e6\u5916\u4e09\u4e2a\u84dd\u8863\u4eba\u5728\u6012\u559d\u58f0\u4e2d\uff0c\u90fd\u8ffd\u4e86\u8fc7\u53bb\u3002\u4f46\u77ac\u606f\u4e4b\u95f4\uff0c\u94c1\u621f\u6e29\u4faf\u4eba\u9a6c\u90fd\u5df2\u63a0\u51fa\u5f88\u8fdc\u3002 \u3000\u3000\u5c24\u5927\u541b\u5f88\u5f97\u610f\u5730\u7b11\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u53ae\u4e2d\u4e86\u6211\u4e00\u638c\uff0c\u7109\u80fd\u8fd8\u6709\u547d\u5728\u3002\u201d\u4ed6\u72c2\u7b11\u7740\u9053\uff1a\u201c\u6211\u4eec\u6162\u6162\u8ffd\u53bb\u4e0d\u8fdf\uff0c\u5c31\u7b49\u7740\u53bb\u6536 \u4ed6\u7684\u5c38\u597d\u4e86\u3002\u201d\u4ee5\u6731\u7802\u638c\u5c24\u5927\u541b\u7684\u638c\u529b\u800c\u8a00\uff0c\u4ed6\u6b64\u8bdd\u5012\u5e76\u975e\u662f\u5938\u72c2\u4e4b\u8bed\u3002\u53e6\u4e09\u4e2a\u84dd\u8863\u4eba\uff0c\u81ea \u7136\u4e5f\u76f8\u4fe1\uff0c\u53ea\u662f\u4ed6\u4eec\u5374\u4e0d\u77e5\u9053\uff0c\u4e8b\u60c5\u5374\u51fa\u4e8e\u4ed6\u4eec\u610f\u6599\u4e4b\u5916\u54e9\uff01 \u3000\u3000\u94c1\u621f\u6e29\u4faf\u98ce\u4e5f\u4f3c\u5730\u5954\u9a70\u4e86\u4e00\u9635\uff0c\u5fd6\u91cf\u5df2\u5c06\u4ed6\u4eec\u629b\u4e0b\u5f88\u8fdc\uff0c\u4fbf\u5728\u4e00\u4e2a\u8352\u50fb\u5730\u65b9\u500f\u7136\u4f4f\u4e86 \u9a6c\uff0c\u6781\u5feb\u7684\u7ffb\u8eab\u800c\u4e0b\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u56db\u8f6c\uff0c\u786e\u5b9a\u4e86\u6b64\u5904\u9664\u4ed6\u4e4b\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u522b\u4eba\u7684\u8e2a\u8ff9\u3002\u518d\u770b\u62a4\u57ce\u6cb3\uff0c\u4e0a\u9762\u867d\u7ed3\u7740 \u51b0\uff0c\u4f46\u5c1a\u672a\u7ed3\u6210\u4e00\u5c42\uff0c\u53ea\u662f\u5728\u6cb3\u4e0a\u6d6e\u7740\u4e9b\u51b0\u5757\uff0c\u4e8e\u662f\uff0c\u4ed6\u4f3c\u4e4e\u9887\u4e3a\u6ee1\u610f\u7b11\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u201c\u4e00\u5207\u90fd\u5f88\u53f0\u4e4e\u7406\u60f3\u3002\u201d\u4ed6\u6697\u6697\u5fd6\u9006\u3002\u84e6\u5730\uff0c\u4ed6\u6495\u5f00\u4e0a\u8863\uff0c\u8863\u670d\u91cc\u9762\u7684\u76ae\u6bdb\uff0c\u7acb\u523b\u7ffb\u4e86 \u51fa\u6765\uff0c\u5bd2\u98ce\u4e5f\u6781\u5feb\u5730\u5439\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u6beb\u65e0\u5728\u610f\uff0c\u624b\u638c\u52a8\u5904\uff0c\u4ed6\u7adf\u81ea\u9774\u7b52\u4e2d\u62bd\u51fa\u4e00\u628a\u96ea\u4eae\u7684\u5315\u9996\uff0c\u5728\u81ea\u5df1\u7684\u5de6\u81c2\u4e0a \u6781\u5feb\u7684\u4e00\u5212\uff0c\u9c9c\u8840\u500f\u7136\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u975e\u5e38\u5c0f\u5fc3\u5730\u4e0d\u8ba9\u8840\u6cbe\u5728\u4ed6\u8863\u88f3\u4e0a\uff0c\u624b\u6307\u637a\u865a\uff0c\u9c9c\u8840\u4fbf\u5728\u96ea\u5730\u4e0a\u7559\u4e0b\u4e00\u5927\u6ee9\u9c9c\u7ea2\u7684\u8840 \u8ff9\uff0c\u800c\u8fd9\u4e9b\u8840\u8ff9\uff0c\u4efb\u4f55\u4eba\u90fd\u5206\u8fa8\u4e0d\u51fa\u90a3\u662f\u56e0\u53d7\u4e86\u5916\u4f24\u800c\u6d41\u51fa\u7684\uff0c\u6291\u6216\u662f\u56e0\u4e3a\u53d7\u4e86\u5185\u4f24\u800c\u4ece\u53e3 \u4e2d\u55b7\u51fa\u7684\u3002 \u3000\u3000\u5728\u6781\u4e3a\u77ed\u6682\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u5b8c\u6210\u4e86\u8fd9\u4e9b\u52a8\u4f5c\uff0c\u7136\u540e\u4ed6\u5728\u4ece\u81ea\u5df1\u7acb\u9a6c\u4e4b\u5904\u5230\u6cb3\u5cb8\u4e4b\u95f4\uff0c\u5f04 \u4e86\u4e9b\u51cc\u4e71\u7684\u811a\u5370\uff0c\u4f7f\u4e00\u5207\u770b\u8d77\u6765\uff0c\u90fd\u8ba9\u4eba\u4e0d\u5f97\u4e0d\u76f8\u4fe1\u94c1\u621f\u6e29\u5019\u5728\u4e2d\u4e86\u6731\u7802\u638c\u5c24\u5927\u541b\u7684\u4e00\u638c\u4e4b \u540e\uff0c\u5728\u4fdd\u5b9a\u57ce\u5916\uff0c\u5410\u8840\u800c\u4ea1\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4e0d\u7518\u613f\u81ea\u5df1\u7684\u5c38\u8eab\u843d\u5728\u5929\u4e89\u6559\u624b\u4e0a\uff0c\u6240\u4ee5\u4ed6\u5c3d\u4e86\u6700\u540e \u4e00\u5206\u529b\u91cf\uff0c\u6323\u624e\u7740\u8dc3\u8fdb\u6cb3\u91cc\u3002 \u3000\u3000\u4ed6\u50cf\u4e00\u4e2a\u604b\u4eba\u4f3c\u7684\uff0c\u6781\u4e3a\u7559\u8fde\u5730\u7559\u4e86\u90a3\u5339\u66fe\u88ab\u65e0\u6570\u4eba\u7fa1\u6155\uff0c\u5992\u5fcc\uff0c\u7ecf\u8fc7\u65e0\u6570\u6b21\u4e89\u6597\u800c\u4e14 \u81ea\u5df1\u7edd\u4e0d\u613f\u653e\u5f03\u7684\u5b9d\u9a6c\u4e00\u773c\uff0c\u7136\u540e\u6781\u4e3a\u6c89\u91cd\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4e3a\u4e86\u4f7f\u4eba\u786e\u4fe1\u4ed6\u7684\u6b7b\uff0c\u4ed6\u53ea\u5f97\u653e \u5f03\u8fd9\u5339\u9a6c\u4e86\uff0c\u8fd9\u662f\u4ed6\u8fd9\u4e2a\u8ba1\u5212\u4e2d\u6700\u96be\u505a\u5230\u7684\u4e00\u70b9\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5fc5\u9700\u8fd9\u6837\u505a\uff0c\u5047\u82e5\u6ca1\u6709\u8fd9\u5339\u9a6c\u7559\u4e0b\u6765\uff0c\u90a3\u4e48\u7eb5\u7136\u4ed6\u4ed7\u7740\u65e9\u5df2\u51c6\u5907\u597d\u7684\u91d1\u4e1d\u7f20\u7740\u53d1 \u4e1d\u7684\u80cc\u5fc3\uff0c\u548c\u80cc\u5fc3\u91cc\u4e00\u5757\u4e0a\u9762\u8fd8\u8fde\u7740\u9c9c\u8840\u7684\u517d\u76ae\uff0c\u800c\u80fd\u5947\u8ff9\u4f3c\u7684\u6328\u8fc7\u6731\u7802\u638c\u5c24\u5927\u541b\u529b\u80fd\u5f00\u5c71 \u5288\u77f3\u7684\u4e00\u638c\uff0c\u4f46\u4eba\u4eec\u4e5f\u4e00\u5b9a\u4f1a\u6000\u7591\u94c1\u621f\u6e29\u4faf\u600e\u4f1a\u5982\u6b64\u8f7b\u6613\u5730\u6b7b\u53bb\uff01 \u3000\u3000\u4ed6\u53c8\u6c89\u91cd\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u60f3\u518d\u591a\u7559\u604b\u4e00\u4f1a\u513f\uff0c\u7136\u800c\u8fd9\u65f6\u5019\uff0c\u98ce\u58f0\u4e2d\u5df2\u6709\u9a6c\u5636\u58f0\u4f20\u6765\uff0c\u4ed6 \u77e5\u9053\u6b64\u523b\u4ed6\u2014\u2014\u94c1\u621f\u6e29\u4faf\u79bb\u5f00\u4eba\u4e16\u7684\u65f6\u5019\u5df2\u7ecf\u5230\u4e86\uff0c\u867d\u7136\u4ed6\u8fd8\u6709\u56de\u5230\u4eba\u4e16\u7684\u673a\u4f1a\uff0c\u4f46\u8fd9\u5e0c\u671b \u5728\u4ed6\u6b64\u65f6\u770b\u6765\uff0c\u5c31\u50cf\u6df1\u591c\u4e2d\u7684\u5b64\u661f\u4e00\u6837\u6e3a\u832b\uff01 \u3000\u3000\u4ed6\u7684\u9a6c\u5fae\u5636\u4e86\u4e00\u58f0\uff0c\u4ed6\u4f38\u8d77\u624b\u5728\u773c\u89d2\u5fae\u5fae\u64e6\u62ed\u4e00\u4e0b\uff0c\u662f\u6709\u773c\u6cea\u6d41\u4e0b\uff0c\u6291\u6216\u662f\u6709\u98ce\u6c99\u5462\uff1f \u3000\u3000\u8eab\u5f62\u731b\u4e00\u987f\u632b\uff0c\u811a\u5c16\u5728\u6cb3\u5cb8\u8fb9\u731b\u70b9\uff0c\u7626\u957f\u7684\u8eab\u8eaf\u7adf\u4ece\u8fd9\u51e0\u8fbe\u56db\u4e08\u5bbd\u7684\u62a4\u57ce\u6cb3\u4e0a\u63a0\u4e86\u8fc7 \u53bb\uff0c\u5728\u5730\u9762\u4e0a\u53ea\u5fae\u5fae\u4e00\u70b9\uff0c\u518d\u4e00\u957f\u8eab\uff0c\u8eab\u5f62\u66b4\u8d77\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u7adf\u8dc3\u4e0a\u4fdd\u5b9a\u5e9c\u7684\u57ce\u5899\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4ee5\u7edd\u9876\u7684\u8f7b\u529f\uff0c\u6d88\u5931\u5728\u4fdd\u5b9a\u5e9c\u57ce\u5899\u4e0a\u7684\u65f6\u5019\uff0c\u968f\u7740\u4ed6\u7684\u9a6c\u5728\u96ea\u5730\u4e0a\u7559\u4e0b\u7684\u8e44\u5370\uff0c \u6731\u7802\u638c\u5c24\u5927\u541b\u7b49\u56db\u9a6c\u4e5f\u8ffd\u4e86\u6765\u3002\u9996\u5148\uff0c\u4ed6\u4eec\u6240\u770b\u5230\u7684\u5c31\u662f\u90a3\u5339\u6c5f\u6e56\u4e0a\u72ec\u4e00\u65e0\u4e8c\u7684\u706b\u7ea2\u8272\u7684\u5b9d \u9a6c\uff0c\u5b64\u96f6\u800c\u65e0\u52a9\u5730\u4f2b\u7acb\u5728\u4e25\u51ac\u9ec4\u660f\u7684\u5bd2\u98ce\u91cc\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u5415\u5357\u4eba\u6240\u7f6e\u4e0b\u7684\u4e00\u5207\uff0c\u4e8e\u662f\u94c1\u621f\u6e29\u4faf\u6b7b\u4e86\u7684\u8fd9\u6d88\u606f\uff0c\u7b2c\u4e8c\u5929\u4fbf\u5f88\u5feb\u5730\u5728\u6b66\u6797\u4e2d\u4f20 \u64ad\u7740\uff0c\u4f7f\u5f97\u6b66\u6797\u4e2d\u7684\u8c6a\u58eb\uff0c\u5bf9\u4e8e\u201c\u6731\u7802\u638c\u5c24\u5927\u541b\u201d\u8fd9\u4e2a\u540d\u5b57\u4e5f\u5f88\u5feb\u5730\u6362\u4e86\u4e00\u79cd\u770b\u6cd5\u3002 \u3000\u3000\u5bf9\u8fd9\u4ef6\u4e8b\u552f\u4e00\u6709\u4e9b\u6000\u7591\u7684\uff0c\u5374\u662f\u94c1\u621f\u6e29\u4faf\u201c\u5fe0\u5b9e\u7684\u201d\u59bb\u5b50\u2014\u2014\u9500\u9b42\u592b\u4eba\u859b\u82e5\u74a7\uff0c\u56e0\u4e3a\u5979 \u6df1\u77e5\u5979\u4e08\u592b\u7684\u6b66\u529f\u3002 \u3000\u3000\u4f46\u662f\u5979\u5374\u4e5f\u4e0d\u6562\u5c06\u5979\u7684\u6000\u7591\uff0c\u5728\u5979\u7684\u65b0\u6b22\u2014\u2014\u72ec\u9738\u6c5f\u6e56\u7684\u5929\u4e89\u6559\u6559\u4e3b\u8427\u65e0\u9762\u524d\u63d0\u8d77\u3002 \u3000\u3000\u5929\u4e89\u6559\u4e3b\u867d\u7136\u4e9f\u6b32\u5415\u5357\u4eba\u6b7b\u547d\u3002\u4f46\u4ed6\u542c\u5230\u8fd9\u6d88\u606f\u540e\uff0c\u53ea\u6de1\u6de1\u4e00\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\uff0c\u548c\u4e00\u4e2a\u201c\u4eba\u201d\u4e89\uff0c\u662f\u592a\u65e0\u804a\u4e86\u4e9b\uff0c\u4ed6\u4eec\u8981\u4e89\u6597\u7684\u5bf9\u8c61\uff0c\u5374\u662f\u53e4\u5f80\u4eca\u6765\u4ece\u672a \u6709\u4eba\u6562\u4e89\u6597\u7684\u2014\u2014\u6b64\u6240\u4ee5\u4e3a\u201c\u5929\u4e89\u6559\u201d\u4e5f\u3002 \u3000\u3000\u4e8e\u662f\u94c1\u621f\u6e29\u4faf\u5728\u5931\u53bb\u4e86\u5bb6\u548c\u59bb\u5b50\u4e4b\u540e\uff0c\u81ea\u5df1\u5728\u6b66\u6797\u4e2d\u4e5f\u6d88\u5931\u4e86\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u7ae0\u3000\u9690\u8ff9\u6f5c\u8e2a>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u7ae0\u3000\u9690\u8ff9\u6f5c\u8e2a \u3000\u3000\u5728\u57ce\u579b\u540e\u7684\u9634\u6697\u4e4b\u5904\uff0c\u5415\u5357\u4eba\u9690\u4f0f\u4e86\u5f88\u4e45\uff0c\u7136\u540e\u4ed6\u5c06\u8eab\u4e0a\u7a7f\u7740\u7684\u6b66\u58eb\u77ed\u8884\u8131\u4e86\u4e0b\u6765\uff0c\u53d6 \u4e0b\u4e86\u4e00\u4e2a\u4ed6\u7d27\u7d27\u7cfb\u5728\u8eab\u4e0a\u7684\u5305\u88b1\uff0c\u90a3\u91cc\u9762\u662f\u4e00\u5957\u5728\u5f53\u65f6\u6700\u4e3a\u666e\u901a\u7684\u8863\u886b\uff0c\u548c\u4e00\u9876\u5317\u529b\u5e38\u89c1\u7684 \u76ae\u98ce\u5e3d\u3002 \u3000\u3000\u4e8e\u662f\u5f53\u4ed6\u6f2b\u6b65\u4ece\u57ce\u4e0a\u8d70\u4e0b\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u53d8\u6210\u4e00\u4e2a\u6781\u4e3a\u666e\u901a\u7684\u4eba\uff0c\u90a3\u548c\u4fdd\u5b9a\u5e9c\u7ec8\u65e5\u5728\u8857\u4e0a \u7199\u6765\u6518\u5f80\u7684\u5c0f\u5546\u4eba\u6beb\u65e0\u4e8c\u81f4\uff0c\u53ea\u662f\u4ed6\u5fc3\u4e2d\u6240\u60f3\u7684\uff0c\u5374\u662f\u548c\u90a3\u4e9b\u4eba\u7edd\u4e0d\u76f8\u540c\u7684\u7ecf\u5386\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\uff0c\u50cf\u88ab\u6bd2\u86c7\u556e\u566c\u822c\u75db\u82e6\uff0c\u4ee5\u81f4\u4ed6\u7684\u8138\u66f4\u4e3a\u82cd\u767d\u4e86\uff0c\u9690\u85cf\u5728\u98ce\u5e3d\u4e0b\u7684\u4e00\u53cc\u773c\u775b\uff0c\u4e5f \u56e0\u7740\u6124\u6068\u548c\u6028\u6bd2\u800c\u53d8\u5f97\u8840\u7ea2\u3002 \u3000\u3000\u4ed6\u5728\u82cf\u5dde\u57ce\u90ca\u7684\u5e90\u820d\uff0c\u539f\u672c\u662f\u6e29\u6696\u7684\uff0c\u4ed6\u548c\u4ed6\u7684\u59bb\u5b50\uff0c\u539f\u6765\u4e5f\u662f\u6109\u5feb\u7684\uff0c\u4ed6\u6267\u70c8\u5730\u559c\u7231 \u7740\u4eba\u7c7b\uff0c\u56e0\u6b64\u4ed6\u4e0d\u613f\u50cf\u5927\u591a\u6570\u6b66\u6797\u4e2d\u7684\u540d\u4eba\u4e00\u6837\uff0c\u5c06\u81ea\u5df1\u7684\u4f4f\u5904\uff0c\u5b89\u6392\u5728\u6df1\u5c71\u91cc\u3002\u800c\u53ea\u662f\u5728 \u82cf\u5dde\u57ce\u91cc\uff0c\u548c\u4ed6\u90a3\u4ee5\u7f8e\u4e3d\u51fa\u540d\u7684\u59bb\u5b50\u4eab\u53d7\u7740\u5927\u591a\u6570\u5e74\u8f7b\u800c\u5bcc\u8db3\u7684\u592b\u5987\u6240\u4eab\u53d7\u7740\u7684\u606c\u9759\uff0c\u6e29 \u6696\uff0c\u800c\u6109\u5feb\u7684\u751f\u6d3b\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4f1a\u6709\u5f88\u591a\u6b66\u6797\u8c6a\u58eb\u6765\u6155\u540d\u62dc\u8bbf\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u4f1a\u5728\u8202\u6df1\u79cb\u521d\uff0c\u90a3\u4e9b\u7f8e\u4e3d\u7684\u65e5\u5b50\u91cc\u5e76\u80a9\u800c\u51fa\uff0c\u9a70\u9a8b\u6c5f\u6e56\uff0c\u4eab\u53d7\u7740\u4eba\u4eec\u8273\u7684\u76ee\u5149\u3002 \u3000\u3000\u7eb5\u7136\u6709\u4e9b\u4ec7\u5bb6\uff0c\u4f46\u4e5f\u5728\u4ed6\u90a3\u4e00\u53cc\u5bd2\u94c1\u77ed\u621f\u4e4b\u4e0b\u6151\u4f0f\u4e86\u3002 \u3000\u3000\u4f46\u662f\u6076\u8fd0\u5374\u5e76\u672a\u653e\u8fc7\u4ed6\uff0c\u5728\u4e94\u5e74\u4e4b\u5185\uff0c\u5c31\u5a01\u6151\u5929\u4e0b\u6b66\u6797\u7684\u5929\u4e89\u6559\u4e3b\uff0c\u88ab\u6b66\u6797\u4e2d\u76ee\u4e3a\u767e\u5e74 \u6765\u4ec5\u89c1\u7684\u5947\u624d\u2014\u2014\u8427\u65e0\uff0c\u5728\u5076\u7136\u7684\u673a\u4f1a\u548c\u859b\u82e5\u74a7\u9082\u9005\u4e4b\u540e\uff0c\u88ab\u5415\u5357\u4eba\u4e00\u76f4\u8ba4\u4e3a\u975e\u5e38\u5fe0\u5b9e\u7684\u59bb \u5b50\uff0c\u7adf\u5bf9\u4ed6\u4e0d\u518d\u5fe0\u5b9e\uff0c\u5c45\u7136\u79c1\u5954\u5230\u5929\u4e89\u6559\u4e3b\u6000\u91cc\u53bb\u4e86\uff01 \u3000\u3000\u800c\u4e14\uff0c\u5929\u4e89\u6559\u4e3b\u8427\u65e0\uff0c\u7adf\u8fd0\u7528\u4e86\u4ed6\u7684\u7edd\u9876\u6b66\u529b\uff0c\u7edd\u9ad8\u667a\u6167\u548c\u7edd\u5927\u6bc5\u529b\u5728\u6b66\u6797\u4e2d\u57f9\u690d\u6210\u7684 \u52bf\u529b\uff0c\u8981\u94f2\u9664\u8fd9\u94c1\u621f\u6e29\u4faf\u3002 \u3000\u3000\u5415\u5357\u4eba\u662f\u9ad8\u50b2\u7684\uff0c\u4ed6\u7acb\u523b\u5168\u529b\u53cd\u6297\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5931\u8d25\u4e86\uff0c\u50cf\u6b66\u6797\u4e2d\u5176\u4ed6\u7684\u4eba\u4e00\u6837\uff0c\u4ed6\u65e0\u6cd5\u548c\u5929\u4e89\u6559\u5e9e\u5927\u7684\u52bf\u529b\u76f8\u6297\u3002 \u3000\u3000\u6709\u597d\u51e0\u6b21\uff0c\u4ed6\u90fd\u51e0\u4e4e\u6b7b\u5728\u5929\u4e89\u6559\u91cc\u5730\u4f4d\u6700\u9ad8\u7684\u91d1\u886b\u9999\u4e3b\u4eec\u7684\u73af\u653b\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e0d\u7518\u5c31\u6b7b\uff0c\u4e8e\u662f\u4ed6\u8d39\u5c3d\u5fc3\u529b\uff0c\u9003\u51fa\u6c5f\u5357\u3002\u7528\u5047\u6b7b\u9a97\u8fc7\u4e86\u5929\u4e89\u6559\uff0c\u4e5f\u9a97\u8fc7\u4e86\u6240\u6709 \u7684\u6b66\u6797\u4e2d\u7684\u8c6a\u58eb\uff0c\u9690\u8ff9\u6f5c\u8e2a\u8d77\u6765\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u4f1a\u60f3\u5230\u4ed6\u66fe\u5728\u4fdd\u5b9a\u5e9c\u91cc\u4e00\u6761\u6700\u7e41\u76db\u7684\u8857\u9053\u4e0a\uff0c\u9690\u85cf\u4e86\u81ea\u5df1\u3002\u4e5f\u6ca1\u6709\u4eba\u4f1a\u60f3\u5230\u548c\u8bb8 \u591a\u4e2a\u843d\u7b2c\u79c0\u624d\u4e00\u9f50\u4f4f\u5728\u4e00\u680b\u5927\u56db\u5408\u9662\u91cc\u7684\u6c5f\u5357\u79c0\u624d\u2014\u2014\u4f0a\u98ce\uff0c\u4f1a\u662f\u66fe\u7ecf\u5728\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684 \u94c1\u621f\u6e29\u4faf\u3002 \u3000\u3000\u8fd9\u4e2a\u56db\u5408\u9662\u91cc\uff0c\u7ec8\u65e5\u4e66\u58f0\u6717\u6717\u3002\u843d\u7b2c\u7684\u79c0\u624d\u4eec\u5728\u4e66\u4e2d\u5bfb\u627e\u7740\u81ea\u5df1\u7684\u68a6\u60f3\uff0c\u53ea\u8981\u4e00\u65e6\u5927\u9b41 \u5929\u4e0b\uff0c\u90a3\u65f6\u5019\u5c31\u4e00\u8dc3\u800c\u81f3\u4e07\u4eba\u4e4b\u4e0a\u4e86\u3002 \u3000\u3000\u50cf\u90a3\u4e9b\u79c0\u624d\u4e00\u6837\uff0c\u4f0a\u98ce\u4e5f\u5728\u8bfb\u7740\u4e66\u2014\u2014\u5404\u79cd\u7684\u4e66\u3002 \u3000\u3000\u4ed6\u4ece\u5c0f\u4e60\u6b66\uff0c\u6839\u672c\u6ca1\u6709\u65f6\u95f4\u8bfb\u4e66\uff0c\u6e10\u6e10\u5728\u4e66\u4e2d\u5bfb\u5f97\u4e86\u4e00\u4efd\u5b89\u6170\u548c\u6ee1\u8db3\uff0c\u4f7f\u4ed6\u80fd\u9759\u5fc3\u671f\u5f85 \u7740\uff0c\u671f\u5f85\u7740\u4e00\u4e2a\u4ed6\u80fd\u591f\u590d\u4ec7\u7684\u673a\u4f1a\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u6bb5\u975e\u5e38\u8270\u82e6\u7684\u65e5\u5b50\uff0c\u4e00\u4e2a\u4eba\u7531\u76db\u540d\u4e0b\u8fd4\u56de\u62d9\u5883\uff0c\u90a3\u79cd\u5fc3\u60c5\u5f80\u5f80\u662f\u7edd\u5927\u591a\u6570\u4eba\u65e0\u6cd5 \u5fcd\u53d7\u7684\uff0c\u4f46\u662f\u4ed6\u5374\u6371\u8fc7\u4e86\u3002 \u3000\u3000\u4e24\u5e74\u4e4b\u540e\u2014\u2014 \u3000\u3000\u5f53\u4eba\u4eec\u5df2\u6e10\u6e10\u5f00\u59cb\u6de1\u5fd8\uff0c\u751a\u81f3\u5df2\u5b8c\u5168\u5fd8\u8bb0\u94c1\u621f\u6e29\u4faf\u8fd9\u4e2a\u540d\u5b57\u7684\u65f6\u5019\uff0c\u4ed6\u63d0\u7740\u4e00\u7b50\u4e66\uff0c\u7a7f \u7740\u4e00\u9886\u84dd\u886b\uff0c\u7528\u836f\u7684\u9ec4\u8272\u63a9\u9970\u7740\u8138\u4e0a\u7684\u82cd\u767d\uff0c\u4f4e\u7740\u5934\uff0c\u50cf\u4e00\u4e2a\u5931\u610f\u7684\u6e38\u5b66\u58eb\u5b50\u4e00\u6837\uff0c\u53c8\u5f00\u59cb \u4e86\u4ed6\u7684\u5f81\u9014\uff0c\u53ea\u662f\u4ed6\u5df2\u4e0d\u662f\u53f1\u5412\u98ce\u4e91\u7684\u4eba\u7269\u4e86\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e09\u7ae0\u3000\u654c\u6697\u6211\u660e>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u7ae0\u3000\u654c\u6697\u6211\u660e \u3000\u3000\u6709\u65f6\u5019\uff0c\u5f53\u4e00\u5339\u5065\u9a6c\u63d0\u7740\u8e44\u5954\u8fc7\uff0c\u5f53\u90a3\u5339\u9a6c\u6e85\u8d77\u7684\u6ce5\u6c34\u6e85\u5230\u4ed6\u8eab\u4e0a\u65f6\uff0c\u4ed6\u4f1a\u53d1\u73b0\u90a3\u9a6c\u4e0a \u7684\u9a91\u58eb\uff0c\u66fe\u7ecf\u8eac\u7740\u8eab\u5b50\u53bb\u8bf7\u6c42\u4ed6\u7684\u6307\u6559\u3002 \u3000\u3000\u6709\u65f6\u5019\uff0c\u5f53\u4ed6\u5750\u5728\u5608\u6742\u7684\u8336\u9986\u91cc\u542c\u5230\u4e00\u4e9b\u7c97\u4fd7\u7684\u6c49\u5b50\u53e3\u6d19\u6a2a\u98de\u5730\u8c08\u8bba\u7740\u6b66\u6797\u4e2d\u4e8b\u7684\u65f6 \u5019\uff0c\u4ed6\u80f8\u4e2d\u79ef\u8574\u5df2\u4e45\u7684\u70ed\u8840\uff0c\u4e5f\u4f1a\u6cb8\u817e\u4e00\u4e0b\uff0c\u4f46\u77ac\u5373\u5c31\u88ab\u81ea\u5df1\u6309\u637a\u4f4f\u4e86\u3002 \u3000\u3000\u5f88\u5feb\u5730\uff0c\u4ed6\u5c31\u53d1\u73b0\u5929\u4e89\u6559\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u65e5\u6e10\u589e\u957f\uff0c\u6614\u65e5\u6b66\u6797\u4e2d\u7684\u540d\u95e8\u5b97\u6d3e\uff0c\u8fd1\u5e74\u6765\u4eba \u6750\u51cb\u96f6\uff0c\u6c5f\u6e56\u4e2d\u5df2\u5f88\u96be\u542c\u5230\u6709\u51e0\u4e2a\u65b0\u5d1b\u8d77\u7684\u9ad8\u624b\uff0c\u5373\u4f7f\u6709\uff0c\u4e5f\u4f1a\u88ab\u5929\u4e89\u6559\u7f51\u7f57\u4e86\u53bb\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u624d\u4e8c\u5341\u516d\u5c81\u7684\u4ed6\uff0c\u5fc3\u60c5\u5374\u5df2\u50cf\u516d\u5341\u4e8c\u5c81\u822c\u6d88\u6c89\u800c\u843d\u5bde\u4e86\u3002 \u3000\u3000\u53ea\u662f\u90a3\u4e00\u4efd\u6df1\u9083\u7684\u4ec7\u6068\uff0c\u5374\u4f7f\u4ed6\u4ecd\u7136\u5728\u7b49\u5f85\u548c\u671f\u671b\u7740\u3002 \u3000\u3000\u6709\u8bb8\u591a\u4eba\u5176\u6240\u4ee5\u80fd\u591f\u5728\u4e16\u4e0a\u6d3b\u4e0b\u53bb\uff0c\u4e5f\u662f\u5168\u51ed\u7740\u7b49\u5f85\u548c\u671f\u671b\u7684\u529b\u91cf\u7684\u3002 \u3000\u3000\u5f53\u4ed6\u5f00\u59cb\u5026\u538c\u57ce\u5e02\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u5230\u5c71\u91ce\u4e2d\u53bb\u3002\u5728\u4ed6\u5df2\u786e\u5b9a\u65e0\u4eba\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u4f1a\u7528\u4ed6\u90a3\u4e00 \u8eab\u672a\u5c1d\u4e00\u65e5\u8352\u5e9f\u7684\u6b66\u529f\uff0c\u6500\u767b\u5230\u5e38\u4eba\u65e0\u6cd5\u6500\u767b\u7684\u7a77\u5c71\u7edd\u5cad\u4e2d\u53bb\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u662f\u5728\u5180\u6c42\u7740\u5947\u8ff9\u3002 \u3000\u3000\u4f46\u662f\u5947\u8ff9\u4f1a\u4e0d\u4f1a\u5728\u4e00\u4e2a\u50cf\u662f\u7a77\u9014\u672b\u8def\u7684\u4eba\u8eab\u4e0a\u51fa\u73b0\u5462\uff1f \u3000\u3000\u534e\u5c71\u4e43\u4e94\u5cb3\u4e4b\u4e00\uff0c\u5c71\u5ce6\u633a\u79c0\uff0c\u98ce\u7269\u7edd\u80dc\uff0c\u6625\u79cb\u4f73\u65e5\uff0c\u672c\u4e3a\u9a9a\u4eba\u58a8\u5ba2\u6e38\u548f\u4e4b\u5730\u3002 \u3000\u3000\u4f46\u662f\u5728\u8fd9\u4e25\u5bd2\u7684\u65e9\u6625\uff0c\u7eb5\u7136\u6709\u4eba\u4f1a\u63d0\u7740\u5174\u81f4\u6765\u8d4f\u96ea\uff0c\u4f46\u4e5f\u53ea\u5230\u4e86\u5c71\u8170\u4e4b\u4e0b\uff0c\u6d45\u5c1d\u5373\u6b62\u3002 \u5f88\u5c11\u6709\u4eba\u4f1a\u5192\u7740\u4ece\u5c71\u4e0a\u6ed1\u4e0b\u7684\u5371\u9669\uff0c\u5728\u79ef\u96ea\u4e2d\u722c\u4e0a\u53bb\u7684\u3002 \u3000\u3000\u8fd9\u5929\u534e\u5c71\u7edd\u9876\u7684\u5c71\u9634\u4e4b\u5904\uff0c\u6377\u82e5\u733f\u7334\u5730\u722c\u4e0a\u4e00\u4eba\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u8fd9\u4e2a\u8eab\u624b\u7edd\u9ad8\u7684\u4eba\u7269\uff0c\u7adf \u7136\u4ece\u5934\u5230\u811a\u770b\u4e0d\u51fa\u4e00\u4e1d\u6b66\u6797\u4eba\u7269\u7684\u8ff9\u8c61\u6765\u3002\u4ed6\u5f53\u7136\u5c31\u662f\u5415\u5357\u4eba\u2014\u2014\u4f0a\u98ce\u4e86\u3002 \u3000\u3000\u6797\u6728\u65e9\u5c31\u67af\u6b7b\u4e86\uff0c\u4ed6\u5728\u6ee1\u662f\u79ef\u96ea\u7684\u5c71\u5ca9\u4e0a\u7eb5\u8dc3\u7740\uff0c\u6781\u76ee\u56db\u671b\uff0c\u767d\u4e91\u7691\u7136\u4e00\u7247\uff0c\u7a79\u82cd\u7693 \u7693\uff0c\u98ce\u98d2\u6728\u7acb\uff0c\u5bd2\u5a01\u88ad\u4eba\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u4ed6\u624d\u771f\u6b63\u89c9\u5230\u81ea\u5df1\u7684\uff0e\u6e3a\u5c0f\uff01\u80f8\u4e2d\u7684\u95f7\u6c14\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ff1\u90fd\u6e3a\u832b\u8d77\u6765\uff0c\u53ea\u89c9 \u5f97\u5fc3\u4e2d\u5766\u8361\u8361\u5730\uff0c\u8212\u670d\u5df2\u6781\u3002 \u3000\u3000\u4ed6\u6068\u4e0d\u5f97\u5f15\u542d\u9ad8\u5578\u3002 \u3000\u3000\u82e5\u662f\u5728\u6570\u5e74\u524d\uff0c\u4ed6\u4f1a\u6beb\u4e0d\u8fdf\u7591\u5730\u53bb\u505a\u3002 \u3000\u3000\u4f46\u662f\u6b64\u523b\uff0c\u4ed6\u5374\u53ea\u6709\u957f\u53f9\u4e00\u58f0\u7684\u52c7\u6c14\uff0c\u5f77\u4f5b\u4ed6\u82e5\u957f\u5578\u4e00\u58f0\uff0c\u5c31\u4f1a\u60ca\u52a8\u4e86\u4ec0\u4e48\u4eba\u4f3c\u7684\uff0c\u4f46 \u662f\u8fd9\u79cd\u5730\u65b9\u4f1a\u6709\u4eba\u5728\u5417.\uff1f \u3000\u3000\u4ed6\u5446\u5446\u5730\u4f2b\u7acb\u5728\u4e00\u5757\u7a81\u51fa\u7684\u5c71\u77f3\u4e4b\u4e0a\uff0c\u5c71\u98ce\u5439\u6765\uff0c\u4ed6\u6574\u4e2a\u4eba\u5f77\u4f5b\u5c31\u8981\u968f\u98ce\u800c\u8d77\uff0c\u8fd9\u65f6\u5019 \u4ed6\u5df2\u5b8c\u5168\u6c89\u7f05\u4e8e\u81ea\u7136\u98ce\u7269\u4e4b\u4e2d\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4e00\u4e2a\u6781\u4e3a\u8f7b\u5fae\u7684\u58f0\u97f3\u5728\u4ed6\u8eab\u540e\u54cd\u8d77\u3002 \u3000\u3000\u201c\u5988\uff0c\u4ed6\u66fe\u6389\u4e0b\u53bb\u5417\uff1f\u201d\u4e00\u4e2a\u6e05\u8106\u7684\u5973\u5b69\u5b50\u53e3\u97f3\u8bf4\u9053\uff01\u201c\u6211\u770b\u4ed6\u4e5f\u4f1a\u6b66\u529f\u5462\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u867d\u7136\u6781\u5176\u597d\u542c\uff0c\u7136\u800c\u5374\u4f7f\u5f97\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u6761\u7136\u8f6c\u8eab\uff0c\u540e\u800c\u662f\u4e00\u7247\u5c71\u58c1\uff0c\u5c71\u58c1\u4e0a\u9644 \u751f\u7684\u6797\u6728\uff0c\u88ab\u98ce\u5439\u5f97\u76f4\u6643\uff0c\u5c71\u58c1\u524d\u662f\u4e00\u7247\u5d0e\u5c96\u4e0d\u5e73\u7684\u8352\u5730\uff0c\u8352\u5730\u4e0a\u7684\u6797\u6728\u5728\u590f\u65e5\u4e5f\u8bb8\u662f\u7e41\u76db \u7684\uff0c\u4f46\u6b64\u65f6\u4e00\u773c\u671b\u53bb\uff0c\u5c31\u53ef\u89c1\u5e95\uff0c\u90a3\u91cc\u6709\u4eba\u5728\uff01 \u3000\u3000\u4efb\u4f55\u4eba\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u90fd\u4f1a\u5413\u5f97\u5168\u8eab\u609a\u6817\u7684\uff0c\u4f55\u51b5\u662f\u4e3a\u4e86\u907f\u4ec7\uff0c\u7adf\u4e0d\u60dc\u4f2a\u88c5\u4e00\u6b7b\u7684\u4ed6\uff1f \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u6709\u4e00\u9635\u51b7\u6c57\u5192\u51fa\uff0c\u773c\u5149\u4ecd\u5728\u56db\u4e0b\u626b\u52a8\u7740\uff0c\u7a81\u5730\uff0c\u5728\u4e00\u5904\u505c\u4e0b\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5728\u5c71\u58c1 \u4e0a\u7684\u4e00\u4e2a\u6d1e\u7a74\u91cc\u9762\uff0c\u770b\u5230\u4e00\u53cc\u8f6c\u52a8\u7740\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u8d70\u524d\u4e00\u6b65\uff0c\u5168\u8eab\u5df2\u5728\u4e3a\u5c06\u8981\u53d1\u751f\u7684\u4efb\u4f55\u4e00\u79cd\u4e8b\u800c\u5bc6\u5207\u51c6\u5907\u7740\uff0c\u56e0\u4e3a\u8fd9\u4e5f\u8bb8\u5c31\u662f\u4ed6\u7684\u654c \u4eba\u3002 \u3000\u3000\u5728\u7ecf\u8fc7\u5f88\u957f\u4e00\u6bb5\u65f6\u671f\u606c\u6de1\u7684\u751f\u6d3b\u4e4b\u540e\uff0c\u518d\u78b0\u5230\u8fd9\u7c7b\u4e8b\u60c5\uff0c\u4ed6\u7adf\u4e0d\u514d\u6709\u4e9b\u7d27\u5f20\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u5730\u4e00\u6b65\u6b65\u5411\u524d\u8d70\u53bb\uff0c\u6b64\u65f6\u4ed6\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u53ea\u8981\u90a3\u4eba\u6709\u4efb\u4f55\u4e00\u70b9\u53ef\u7591\u4e4b\u5904\uff0c\u4ed6\u5c31\u8981 \u4e0d\u62e9\u624b\u6bb5\u5c06\u90a3\u4eba\u9664\u53bb\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u80fd\u5141\u8bb8\u6709\u4efb\u4f55\u4eba\u53d1\u73b0\u4ed6\u7684\u8e2a\u8ff9\uff0c\u5426\u5219\u5c31\u662f\u81ea\u5df1\u7684\u6b7b\u4ea1\uff01 \u3000\u3000\u4ed6\u548c\u90a3\u5bf9\u773c\u775b\u7684\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4ed6\u53d1\u89c9\u90a3\u5bf9\u773c\u775b\u4e5f\u5728\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u7740\u4ed6\u3002\u4f46\u56e0\u5149\u7ebf\u592a \u9eef\uff0c\u4ed6\u65e0\u6cd5\u770b\u6e05\u90a3\u5bf9\u773c\u775b\u662f\u5c5e\u4e8e\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u7a81\u5730\uff0c\u90a3\u5bf9\u773c\u775b\u500f\u5730\u7a9c\u4e86\u51fa\u6765\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u9519\u6b65\u6325\u638c\uff0c\u6781\u4e3a\u5f3a\u70c8\u7684\u638c\u98ce\u7acb\u523b\u4ece\u4ed6\u638c\u4e0a \u53d1\u51fa\uff0c\u7830\u5730\uff0c\u90a3\u5bf9\u773c\u775b\u548c\u4ed6\u7684\u8eab\u8eaf\uff0c\u7adf\u88ab\u8fd9\u4e00\u638c\u4e4b\u529b\uff0c\u9707\u5f97\u649e\u5230\u5c71\u77f3\u4e0a\uff0c\u60e8\u55e5\u4e00\u58f0\u6b7b\u53bb\u3002 \u3000\u3000\u4ed6\u60ca\u9b42\u521d\u5b9a\uff0c\u5b9a\u775b\u671b\u53bb\uff0c\u90a3\u5bf9\u773c\u775b\u7adf\u5c5e\u4e8e\u4e00\u53ea\u5c71\u732b\uff0c\u4ed6\u4e0d\u7981\u6697\u7b11\u81ea\u5df1\u7684\u7d27\u5f20\u3002 \u3000\u3000\u4f46\u662f\uff0c\u201c\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u53c8\u662f\u4ece\u90a3\u91cc\u53d1\u51fa\u7684\uff1f\u201d\u4ed6\u5728\u60f3\u3002 \u3000\u3000\u968f\u7740\u4ed6\u7684\u6697\u7b11\u4e4b\u540e\uff0c\u4ed6\u4e0d\u7981\u5f00\u59cb\u66f4\u4e3a\u60ca\u6050\uff0c\u56e0\u4e3a\u9690\u85cf\u7740\u7684\u8fd9\u4e2a\u4eba\uff0c\u6781\u53ef\u80fd\u662f\u4ed6\u7684\u4ec7\u654c\u3002 \u800c\u4ee5\u6b64\u65f6\u7684\u60c5\u51b5\u770b\u6765\uff0c\u6b64\u4eba\u82e5\u662f\u4ed6\u7684\u4ec7\u654c\uff0c\u5374\u662f\u4e00\u4e2a\u6781\u4e3a\u4e0d\u5bb9\u6613\u5bf9\u4ed8\u7684\u5389\u5bb3\u89d2\u8272\u54e9\uff01 \u3000\u3000\u4ed6\u8eab\u5f62\u56db\u8f6c\uff0c\u771f\u6c14\u5df2\u805a\u3002\u4ed6\u81ea\u4fe1\u5fc5\u8981\u65f6\u7684\u5168\u529b\u4e00\u51fb\uff0c\u529b\u91cf\u8db3\u4ee5\u60ca\u4eba\u7684\u3002 \u3000\u3000\u4f46\u5c71\u98ce\u5439\u5904\uff0c\u666f\u7269\u4f9d\u7136\uff0c\u8fd8\u662f\u6ca1\u6709\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u6c89\u58f0\u53d1\u8bdd\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u4f0a\u98ce\uff0c\u5076\u6e38\u534e\u5c71\uff0c\u662f\u90a3\u4e00\u4f4d\u524d\u8f88\u9ad8\u4eba\u51fa\u8a00\uff0c\u52a1\u8bf7\u73b0\u8eab\u6307\u6559\uff01\u201d \u3000\u3000\u58f0\u97f3\u4e2d\u5df2\u5931\u5374\u4e86\u4ed6\u5e73\u7d20\u4e60\u60ef\u7684\u9547\u9759\uff0c\u56e0\u4e3a\u4efb\u4f55\u4e00\u4ef6\u4e0d\u53ef\u77e5\u7684\u4e8b\uff0c\u90fd\u662f\u4ee4\u4eba\u4f1a\u611f\u5230\u6050\u60e7 \u7684\u3002 \u3000\u3000\u8bed\u58f0\u843d\u5904\uff0c\u4f9d\u7136\u6ca1\u6709\u56de\u7b54\u3002\u4ed6\u7684\u773c\u5149\u9510\u5229\u5730\u56db\u4e0b\u641c\u7d22\u7740\uff0c\u8eab\u5f62\u5374\u4e0d\u6562\u632a\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6015\u5728\u81ea\u5df1\u79bb\u5f00\u65f6\uff0c\u8eb2\u5728\u6697\u4e2d\u7684\u90a3\u4eba\uff0c\u4e5f\u4e58\u9699\u6e9c\u8d70\u3002\u4ed6\u4e5f\u6015\u5728\u8eab\u5f62\u79fb\u52a8\u65f6\uff0c\u53d7\u5230\u522b \u4eba\u7684\u6697\u7b97\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u4ed6\u592a\u8fc7\u8651\uff0c\u987b\u77e5\u4ed6\u5728\u53d7\u5230\u5929\u4e89\u6559\u8ffd\u6740\u7684\u90a3\u4e00\u6bb5\u65f6\u5019\uff0c\u4ed6\u5982\u4e0d\u662f\u51ed\u7740\u8fd9\u4e00\u4efd\u5c0f\u5fc3 \u548c\u673a\u667a\uff0c\u4ed6\u6015\u4e0d\u65e9\u5df2\u6b7b\u53bb\u5341\u6b21\uff01 \u3000\u3000\u6b64\u65f6\u5728\u8fd9\u79cd\u6df1\u5c71\u7a77\u58d1\u91cc\uff0c\u4ed6\u66f4\u4e0d\u6562\u6709\u4e00\u4e9b\u758f\u5ffd\u3002\u56e0\u4e3a\u4efb\u4f55\u4e00\u4ef6\u758f\u5ffd\uff0c\u90fd\u53ef\u80fd\u9020\u6210\u4ed6\u81f4\u547d \u7684\u6253\u51fb\u3002\u662f\u4ee5\u4ed6\u867d\u7136\u542c\u5f97\u90a3\u662f\u4e00\u4e2a\u5973\u5b69\u5b50\u53d1\u51fa\u7684\u58f0\u97f3\uff0c\u4ed6\u5fc3\u4e2d\u7684\u6050\u60e7\uff0c\u5374\u672a\u56e0\u6b64\u800c\u4e1d\u6beb\u51cf \u9000\u3002 \u3000\u3000\u56e0\u4e3a\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u600e\u4f1a\u6709\u5973\u5b69\u5b50\u7684\u58f0\u97f3\uff0c\u800c\u90a3\u58f0\u97f3\u4e3a\u4ec0\u4e48\u5728\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u4e4b\u540e\uff0c\u7acb\u523b\u518d\u4e5f \u4e0d\u54cd\uff1f\u800c\u4e14\u4e5f\u4e0d\u73b0\u51fa\u8eab\u5f62\u6765\uff01 \u3000\u3000\u201c\u8fd9\u663e\u89c1\u5f97\u5176\u4e2d\u6709\u4ec0\u4e48\u9634\u8c0b\u3002\u201d\u4ed6\u6697\u5fd6\u7740\uff0c\u8d8a\u53d1\u4e0d\u6562\u6709\u4e1d\u6beb\u677e\u61c8\u3002 \u3000\u3000\u4e00\u4e2a\u65f6\u8fb0\u8fc7\u53bb\uff0c\u7b2c\u4e8c\u4e2a\u65f6\u8fb0\u5230\u6765\uff0c\u5c71\u9634\u4e4b\u5904\uff0c\u9759\u5f97\u50cf\u662f\u5929\u4e0b\u6240\u6709\u7684\u751f\u7269\u90fd\u6b7b\u5149\u4e86\u4f3c\u7684\uff0c \u8fde\u4e00\u58f0\u9e1f\u9e23\uff0c\u6216\u662f\u517d\u5636\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u7d27\u77aa\u7740\u7684\u773c\u775b\uff0c\u56e0\u4e3a\u957f\u4e45\u7684\u6ca1\u6709\u4f11\u606f\uff0c\u800c\u5fae\u5fae\u6709\u4e9b\u9178\u75db\u3002\u4ed6\u7684\u8033\u6735\uff0c\u5df2\u53ef\u5728\u98ce\u58f0\u4e2d\u8fa8 \u51fa\u4e00\u6839\u5fae\u679d\u6298\u65ad\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4ec0\u4e48\u4e5f\u6ca1\u6709\u770b\u5230\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u542c\u5230\uff0c\u4e8e\u662f\u8fd9\u4e2a\u65f6\u8fb0\u53c8\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u6000\u7591\u81ea\u5df1\u7684\u770b\u6cd5\uff0c\u201c\u96be\u9053\u8bf4\u8bdd\u7684\u90a3\u4eba\u65e9\u5df2\u7ecf\u8d70\u4e86\uff0c\u6211\u5374\u4e00\u80a1\u50bb\u52b2\u5730\u5728\u6b64\u6b7b\u7b49\uff0c\u4e0d \u7136\uff0c\u4ed6\u7edd\u4e0d\u53ef\u80fd\u85cf\u8fd9\u4e48\u4e45\u5440\uff1f\u201d \u3000\u3000\u4f46\u662f\u4ed6\u5374\u786e\u4fe1\u5728\u81ea\u5df1\u542c\u5230\u90a3\u53e5\u8bdd\uff0c\u548c\u81ea\u5df1\u56de\u8f6c\u8eab\u6765\u7684\u90a3\u4e00\u5239\u90a3\u95f4\uff0c\u65ad\u4e0d\u4f1a\u6709\u4efb\u4f55\u4eba\u80fd\u591f \u4ece\u81ea\u5df1\u8eab\u540e\u9003\u51fa\u81ea\u5df1\u89c6\u7ebf\u4e4b\u5916\u3002 \u3000\u3000\u201c\u9664\u975e\u4ed6\u4f1a\u98de\u3002\u201d\u4ed6\u6697\u5fd6\u7740\u3002 \u3000\u3000\u201c\u4f46\u5047\u5982\u4ed6\u5e76\u672a\u8d70\uff0c\u53ea\u662f\u8eb2\u5728\u4e00\u4e2a\u6211\u4e0d\u77e5\u9053\u7684\u5730\u65b9\uff0c\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u66ff\u81ea\u5df1\u627e\u4e86\u4e2a\u7b54\u6848\uff1a\u201c\u4e00\u5b9a\u662f\u8981\u5bf9\u6211\u4e0d\u5229\uff0c\u6015\u6211\u770b\u5230\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u7591\u5fc3\u4e00\u8d77\uff0c\u66f4\u4e0d\u80af\u653e\u677e\u81ea\u5df1\u5df2\u7ecf\u7ef7\u5f97\u592a\u7d27\u4e86\u7684\u795e\u7ecf\u3002 \u3000\u3000\u66ae\u8272\u6e10\u4e34\u7684\u65f6\u5019\uff0c\u4ed6\u4f9d\u7136\u4f2b\u7acb\u5728\u90a3\u91cc\uff0c\u5fc3\u91cc\u5374\u4e0d\u514d\u66f4\u7740\u6025\uff0c\u56e0\u4e3a\u591c\u8272\u4e00\u4e34\uff0c\u4ed6\u81ea\u5df1\u6240\u5904 \u7684\u5730\u4f4d\uff0c\u5c31\u4f1a\u66f4\u52a0\u4e0d\u5229\u3002\u672c\u6765\u5df2\u662f\u201c\u654c\u6697\u6211\u660e\u201d\u4e86\uff0c\u518d\u52a0\u4e0a\u591c\u8272\u7684\u63a9\u62a4\uff0c\u6697\u4e2d\u7684\u90a3\u4eba\u8981\u9003 \u8d70\uff0c\u6216\u662f\u8981\u6697\u7b97\u81ea\u5df1\uff0c\u4e0d\u662f\u5c31\u66f4\u65b9\u4fbf\u5f97\u591a\u4e86\u5417\uff1f \u3000\u3000\u987b\u77e5\u8fd9\u5e76\u4e0d\u662f\u4ed6\u591a\u8651\uff0c\u800c\u662f\u4e00\u4e2a\u5728\u7ecf\u8fc7\u8bb8\u591a\u6b21\u751f\u6b7b\u7cfb\u4e8e\u4e00\u53d1\u7684\u707e\u96be\u4e4b\u540e\uff0c\u6240\u65e0\u6cd5\u907f\u514d\u7684 \u73b0\u8c61\u3002 \u3000\u3000\u56e0\u4e3a\u5728\u4ed6\u7684\u90a3\u79cd\u5904\u5883\u4e4b\u4e2d\uff0c\u751f\u6b7b\u4e4b\u95f4\u7684\u9650\u754c\uff0c\u7684\u786e\u662f\u5206\u5f97\u5e76\u4e0d\u5341\u5206\u660e\u663e\u7684\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u56db\u7ae0\u3000\u534e\u5c71\u4e4b\u9634>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u7ae0\u3000\u534e\u5c71\u4e4b\u9634 \u3000\u3000\u7a81\u5730\u2014\u2014\u4ed6\u7684\u7b49\u5f85\u679c\u6b8a\u6ca1\u6709\u5931\u671b\u2014\u2014\u4ed6\u542c\u5230\u4e00\u4e2a\u6781\u4e3a\u8f7b\u5fae\u7684\u58f0\u97f3\uff0c\u7136\u800c\u4ed6\u7acb\u523b\u65ad\u5b9a\u90a3\u662f \u4ece\u4e00\u4e2a\u4eba\u8eab\u4e0a\u53d1\u51fa\u7684\u3002\u8fd9\u662f\u4ed6\u95ef\u8361\u6c5f\u6e56\u591a\u5e74\u6240\u5f97\u5230\u7684\u7ecf\u9a8c\uff0c\u800c\u4ed6\u76f8\u4fe1\uff0c\u8fd9\u79cd\u7ecf\u9a8c\u51b3\u5bf9\u4e0d\u4f1a\u6b3a \u9a97\u4ed6\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6d88\u6d88\u4f38\u624b\u4eba\u5bf0\uff0c\u6293\u4e86\u4e00\u628a\u5236\u94b1\uff0c\u4ee5\u201c\u91d1\u94b1\u9556\u201d\u4e2d\u7684\u201c\u6f2b\u5929\u82b1\u96e8\u201d\u7684\u624b\u6cd5\uff0c\u6d12\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u628a\u5236\u94b1\uff0c\u5f53\u7136\u4e0d\u53ca\u201c\u91d1\u94b1\u9556\u201d\u950b\u5229\uff0c\u7136\u800c\u4ece\u4ed6\u8fd9\u79cd\u5185\u5bb6\u9ad8\u624b\u624b\u4e2d\u53d1\u51fa\uff0c\u5a01\u529b\u4ecd\u6b8a\u76f8 \u5f53\u60ca\u4eba\u3002\u5236\u94b1\u5212\u7834\u5c71\u98ce\uff0c\u5e26\u7740\u5c16\u9510\u7684\u58f0\u97f3\uff0c\u88ad\u5411\u4ed6\u786e\u8ba4\u4e3a\u6709\u4eba\u7684\u5730\u65b9\u3002 \u3000\u3000\u90a3\u4e5f\u662f\u4e00\u4e2a\u6d1e\u7a74\uff0c\u4f46\u662f\u6781\u5c0f\uff0c\u4e5f\u5f88\u6df1\uff0c\u6839\u672c\u65e0\u6cd5\u770b\u51fa\u90a3\u91cc\u9762\u7684\u52a8\u9759\u3002 \u3000\u3000\u53ea\u662f\u90a3\u4e00\u628a\u5236\u94b1\u6295\u8fdb\u53bb\u540e\uff0c\u7adf\u5982\u77f3\u6c89\u5927\u6d77\uff0c\u5168\u65e0\u8e2a\u8ff9\u3002 \u3000\u3000\u4f0a\u98ce\u66f4\u60ca\uff0c\u56e0\u4e3a\u6b64\u523b\u4ed6\u66f4\u786e\u5b9a\u4e86\uff0c\u6697\u4e2d\u7684\u90a3\u4eba\u5c31\u662f\u8eb2\u5728\u8fd9\u6d1e\u7a74\u91cc\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e5f\u4e0d\u6562\u5f80\u90a3\u6d1e\u7a74\u524d\u9762\u8d70\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8eb2\u5728\u6697\u4e2d\u7684\u4eba\u5047\u5982\u6709\u610f\u4f24\u81ea\u5df1\uff0c\u90a3\u8fdc\u6bd4\u5728\u660e \u5904\u7684\u4eba\u8981\u5bb9\u6613\u8981\u591a\u3002 \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u518d\u4e5f\u8eb2\u4e0d\u4e86\u5566\uff01\u662f\u597d\u6c49\uff0c\u5c31\u51fa\u6765\uff01\u201d\u4ed6\u5389\u58f0\u559d\u7740\u3002 \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u6d1e\u4e2d\u4e5f\u6709\u4e00\u4e2a\u58f0\u97f3\uff0c\u5c16\u9510\u5730\u53d1\u51fa\u6765\u3002 \u3000\u3000\u201c\u51fa\u6765\u5c31\u51fa\u6765\uff0c\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff01\u201d \u3000\u3000\u968f\u7740\u8bdd\u58f0\u4e00\u6761\u9ed1\u5f71\u7535\u5c04\u800c\u51fa\uff0c\u4e0d\u7b49\u4f0a\u98ce\u53d1\u62db\uff0c\u5df2\u6709\u5341\u51e0\u7f15\u5c16\u98ce\uff0c\u5411\u4f0a\u98ce\u88ad\u5230\u3002 \u3000\u3000\u90a3\u6b63\u662f\u5148\u524d\u4f0a\u98ce\u81ea\u5df1\u53d1\u51fa\u7684\u6697\u5668\uff0c\u6b64\u523b\u88ab\u4eba\u5bb6\u56de\u656c\u8fc7\u6765\uff0c\u624b\u6cd5\u4ea6\u7adf\u5f02\u5e38\u9ad8\u5999\uff0c\u5728\u9ed1\u6697 \u4e2d\uff0c\u7adf\u8ba4\u5f97\u51fa\u81ea\u5bb6\u7684\u7a74\u9053\u3002 \u3000\u3000\u66f4\u4ee4\u4ed6\u60ca\u5f02\u7684\u662f\uff1a\u5f88\u663e\u7136\u5730\u81ea\u5df1\u65b9\u624d\u53d1\u51fa\u7684\u6697\u5668\uff0c\u662f\u88ab\u4eba\u5bb6\u4ee5\u7edd\u5999\u624b\u6cd5\u63a5\u4e86\u53bb\u3002\u4ed6\u867d\u7136 \u79f0\u96c4\u6c5f\u6e56\uff0c\u4e5f\u4e0d\u7981\u4e3a\u8fd9\u79cd\u624b\u6cd5\u60ca\u5f02\u3002\u624b\u638c\u6325\u5904\uff0c\u6765\u4e0d\u53ca\u591a\u52a0\u601d\u7d22\uff0c\u5c06\u90a3\u4e00\u628a\u6697\u5668\uff0c\u5168\u9707\u5f97\u98de \u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4f46\u662f\u90a3\u4eba\u5f71\u5feb\u5982\u7535\u5149\u4e00\u95ea\uff0c\u51e0\u4e4e\u548c\u90a3\u6697\u5668\u540c\u65f6\u5230\u8fbe\u4f0a\u98ce\u9762\u524d\uff0c\u638c\u98ce\u9510\u5229\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2 \u4ece\u4e0d\u540c\u7684\u90e8\u4f4d\uff0c\u5411\u4f0a\u98ce\u653b\u4e86\u4e09\u638c\u3002 \u3000\u3000\u8fd9\u4e09\u62db\u5feb\u5982\u98d8\u98ce\uff0c\u662f\u4ee5\u867d\u662f\u4e09\u62db\uff0c\u4f46\u4f0a\u98ce\u5374\u89c9\u5f97\u5f77\u4f5b\u6709\u4e09\u53ea\u624b\u638c\u540c\u65f6\u5411\u4ed6\u88ad\u6765\uff0c\u5728\u8fd9\u79cd \u60c5\u51b5\u4e0b\uff0c\u53ef\u6ca1\u6709\u65f6\u95f4\u5141\u8bb8\u4ed6\u5148\u770b\u6e05\u4eba\u5bb6\u7684\u4eba\u5f71\uff0c\u62d7\u6b65\u8f6c\u8eab\uff0c\u8eab\u5f62\u6e9c\u6e9c\u7684\u4e00\u8f6c\uff0c\u500f\u5730\u5de6\u638c\u7a7f \u51fa\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u5728\u6781\u77ed\u65f6\u671f\uff0c\u5728\u6b66\u6797\u4e2d\u80fd\u4eab\u76db\u540d\uff0c\u6b66\u529f\u81ea\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u662f\u4ee5\u4ed6\u5728\u9aa4\u7136\u88ab\u88ad\u7684\u60c5\u51b5 \u4e0b:\u4ecd\u80fd\u653b\u51fa\u4e00\u62db\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u8eab\u6cd5\u4e4b\u5feb\uff0c\u66f4\u662f\u60ca\u4e16\u9a87\u4fd7\uff0c\u5de6\u624b\u624b\u8155\u4e00\u7ffb\uff0c\u624b\u6307\u4e0a\u70b9\u4f0a\u98ce\u7684\u201c\u8109\u8155\u7a74\u201d\uff0c\u53f3\u624b\u5708 \u4e86\u4e2a\u534a\u5708\uff0c\u500f\u5730\u53c8\u662f\u4e00\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u8fde\u653b\u5e26\u5b88\uff0c\u66f4\u662f\u5999\u7edd\uff01\u4f0a\u98ce\u731d\u9047\u5f3a\u654c\uff0c\u8155\u8098\u5fae\u4e00\u66f2\u4f38\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u5410\u6c14\u5f00\u58f0\uff0c\u53cc \u638c\u4e00\u9f50\u63a8\u51fa\uff0c\u7adf\u7528\u4e86\u5341\u6210\u638c\u529b\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u62db\u5f0f\u867d\u5947\u5999\uff0c\u4f46\u4f0a\u98ce\u8fd9\u4e00\u638c\u5b8c\u5168\u662f\u786c\u529f\u592b\uff0c\u6ca1\u6709\u4e1d\u6beb\u4e00\u4e9b\u53d6\u5de7\u4e4b\u5904\uff0c\u90a3\u4eba\u5f71\u5374\u4e5f \u4e0d\u6562\u786c\u63a5\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u65b9\u624d\u907f\u5f00\u3002 \u3000\u3000\u4f0a\u98ce\u95ef\u8361\u6c5f\u6e56\uff0c\u4e0d\u77e5\u6253\u8fc7\u591a\u5c11\u6b21\u786c\u4ed7\uff0c\u6b64\u523b\u600e\u80af\u8ba9\u5bf9\u65b9\u6709\u5598\u6c14\u7684\u673a\u4f1a\uff0c\u53f3\u8db3\u731b\u8fdb\u4e00\u6b65\uff0c \u95f7\u559d\u4e00\u58f0\uff0c\u500f\u5730\u53c8\u662f\u77f3\u7834\u5929\u60ca\u7684\u4e00\u638c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u89c9\u5bdf\u5230\uff0c\u90a3\u4eba\u5f71\u6cd5\u867d\u5feb\uff0c\u638c\u62db\u4ea6\u5999\uff0c\u4f46\u5185\u529b\u5374\u4f3c\u6709\u900a\u4e8e\u5df1\uff0c\u662f\u4ee5\u4ed6\u624d\u786c\u6321\u786c \u62ff\uff0c\u4f7f\u51fa\u8fd9\u79cd\u201c\u5927\u9a6c\u91d1\u5200\u201d\u7684\u62db\u5f0f\u3002\u8fd9\u5c31\u662f\u4ed6\u4ece\u5bf9\u654c\u7ecf\u9a8c\u6240\u5f97\u5230\u7684\u5224\u65ad\u3002 \u3000\u3000\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0c\u90a3\u4eba\u5f71\u4e0d\u6562\u6321\u5176\u950b\uff0c\u53c8\u9000\u4e00\u6b65\uff0c\u4f3c\u4e4e\u4e3a\u4ed6\u8fd9\u79cd\u638c\u529b\u6240\u6151\u3002 \u3000\u3000\u4f0a\u98ce\u7cbe\u795e\u9661\u957f\uff0c\u4f46\u662f\u5728\u6b64\u523b\uff0c\u4ed6\u5374\u53d1\u73b0\u4e00\u4ef6\u5947\u4e8b \u3000\u3000\u539f\u6765\u90a3\u8eab\u624b\u5999\u7edd\u7684\u4eba\u5f71\uff0c\u5728\u66ae\u8272\u4e4b\u4e2d\uff0c\u770b\u8d77\u6765\u7adf\u662f\u4e00\u4e2a\u5c11\u5973\uff0c\u800c\u4e14\u8eab\u8eaf\u5f31\u5c0f\uff0c\u6700\u591a\u4e5f\u53ea \u6709\u5341\u56db\uff0c\u4e94\u5c81\u5149\u666f\u3002 \u3000\u3000\u201c\u602a\u4e0d\u5f97\u5979\u5185\u529b\u4e0d\u6d4e\u3002\u201d\u4f0a\u98ce\u5fd6\u9053\u3002 \u3000\u3000\u4f46\u8fd9\u5c11\u5973\u7684\u8fd9\u79cd\u8eab\u624b\uff0c\u5df2\u8db3\u4ee5\u4f7f\u4ed6\u5927\u4e3a\u5403\u60ca\u4e86\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u5df2\u7ecf\u6210\u540d\u7acb\u4e07\u7684\u82f1\u96c4\u2014\u2014\u5305\u62ec\u4e86\u9ed1\u9053\u4e2d\u7684\u8c6a\u58eb\u548c\u767d\u9053\u4e2d\u7684\u5251\u624b\uff0c\u5728\u4ed6\u624b\u4e0b\u8fc7\u62db\u52a8 \u624b\u7684\uff0c\u4e0d\u77e5\u51e1\u51e0\uff0c\u4ed6\u5374\u4ece\u672a\u6709\u8fc7\u548c\u6b64\u523b\u88ab\u8fd9\u5c11\u5973\u4e00\u62a2\u4e0a\u98ce\u65f6\u90a3\u6837\u5730\u72fc\u72c8\u3002 \u3000\u3000\u6362\u53e5\u8bdd\u8bf4\uff0c\u5c31\u662f\u8fd9\u5c11\u5973\u7684\u6b66\u529f\uff0c\u7adf\u5728\u8bb8\u591a\u6210\u540d\u9732\u8138\u7684\u4eba\u7269\u4e4b\u4e0a\uff01 \u3000\u3000\u90a3\u4e48\u8fd9\u5c11\u5973\u7684\u6765\u5386\u548c\u8eab\u4efd\uff0c\u5c31\u5f88\u503c\u5f97\u63a8\u6572\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u5f97\u4e0d\u6000\u7591\u81ea\u5df1\u662f\u5426\u8be5\u548c\u4eba\u5bb6\u52a8\u624b\uff1f\u65e0\u8bba\u5982\u4f55\uff0c\u4ee5\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u548c\u8fd9\u4e48\u4e2a\u5c11\u5973\u52a8\u624b\uff0c \u603b\u662f\u4e0d\u8be5\u3002\u4f55\u51b5\u4eba\u5bb6\u7a76\u7adf\u5bf9\u81ea\u5df1\u6709\u4f55\u7528\u610f\uff0c\u5c1a\u5728\u4e0d\u53ef\u77e5\u4e4b\u6570\u3002 \u3000\u3000\u5148\u524d\u4ed6\u8ba4\u4e3a\u5bf9\u65b9\u662f\u654c\u975e\u53cb\u7684\u770b\u6cd5\uff0c\u6b64\u523b\u5df2\u56e0\u4e3a\u5bf9\u65b9\u53ea\u662f\u4e2a\u5c11\u5973\uff0c\u800c\u6709\u6240\u52a8\u6447\u4e86\u3002 \u3000\u3000\u601d\u5fd6\u4e4b\u95f4\uff0c\u4e24\u4eba\u53c8\u62c6\u4e86\u51e0\u62db\uff0c\u8fd9\u51e0\u62db\u4e0b\u6765\uff0c\u90a3\u5c11\u5973\u53c8\u62a2\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u4f0a\u98ce\u77e5\u9053\u90a3\u662f\u56e0\u4e3a\u81ea\u5df1\u7684\u5fc3\u795e\u4e0d\u4e13\u4e4b\u6545\u3002\u4f46\u7531\u6b64\u53ef\u89c1\uff0c\u8fd9\u5341\u56db\uff0c\u4e94\u5c81\u7684\u5c11\u5973\u7684\u8eab\u624b\uff0c\u9664 \u4e86\u5185\u529b\u4e0d\u5982\u81ea\u5df1\u4e4b\u5916\uff0c\u548c\u81ea\u5df1\u4e0d\u8fc7\u53ea\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\u3002 \u3000\u3000\u201c\u90a3\u4e48\u8fd9\u5c11\u5973\u7684\u5e08\u957f\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u8d8a\u53d1\u4e0d\u5b9a\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7adf\u4e5f\u662f\u4e00\u5473\u95f7\u6253\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u5f77\u4f5b\u548c\u4f0a\u98ce\u6709\u7740\u4ec0\u4e48\u4ec7\u6028\u4f3c\u7684\u3002 \u3000\u3000\u8fd9\u79cd\u53cc\u65b9\u90fd\u6ca1\u6709\u95ee\u6e05\u5bf9\u65b9\u7684\u6765\u5386\u5c31\u52a8\u624b\u8d77\u6765\u7684\u60c5\u5f62\uff0c\u5fc5\u5b9a\u662f\u5176\u4e2d\u6709\u4e86\u4ec0\u4e48\u8bef\u4f1a\u3002 \u3000\u3000\u4f0a\u98ce\u5728\u52a8\u7740\u624b\u65f6\u3002\u5fc3\u4e2d\u5374\u5728\u601d\u7d22\u7740\u5982\u4f55\u5e94\u4ed8\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u90a3\u77e5\u624b\u5e95\u4e00\u6162\uff0c\u8155\u8098\u7adf\u5fae\u5fae\u4e00\u9ebb\uff0c\u81ea\u5df1\u53f3\u624b\u8098\u95f4\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u7adf\u88ab\u4eba\u5bb6\u6307\u950b\u626b\u7740\u4e00 \u4e9b\uff0c\u8fc7\u624b\u4e4b\u95f4\uff0c\u5c31\u6709\u4e86\u4e0d\u751a\u7075\u4fbf\u7684\u611f\u89c9\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8fde\u5fd9\u6536\u6444\u4f4f\u77ee\u795e\uff0c\u5c06\u4e00\u5207\u4e8b\u90fd\u6682\u65f6\u629b\u5f00\uff0c\u4e13\u5fc3\u5e94\u654c\u3002 \u3000\u3000\u4e24\u4eba\u8eab\u624b\u90fd\u5feb\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u62c6\u4e86\u6570\u5341\u62db\u3002\u4f0a\u98ce\u5fc3\u4e2d\u6709\u4e9b\u987e\u5fcc\uff0c\u662f\u4ee5\u5e76\u672a\u4f7f\u51fa\u5168\u529b\uff0c\u52a8 \u624b\u4e4b\u95f4\uff0c\u4e0d\u514d\u7a0d\u53d7\u9650\u5236\u3002 \u3000\u3000\u4f46\u90a3\u5c11\u5973\u62db\u5f0f\u5374\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u800c\u4e14\u51fa\u624b\u751a\u5947\uff0c\u8fde\u4f0a\u98ce\u90a3\u4e48\u6df1\u7684\u9605\u5386\uff0c\u7adf\u770b\u4e0d\u51fa\u8fd9\u5c11\u5973 \u7684\u8eab\u624b\uff0c\u5012\u5e95\u662f\u5c5e\u4e8e\u4f55\u95e8\u4f55\u6d3e\u6765\u3002 \u3000\u3000\u4e24\u4eba\u8fc7\u624b\u4e4b\u5904\uff0c\u4e0d\u8fc7\u53ea\u662f\u5728\u67af\u6728\u4e4b\u95f4\u7684\u4e00\u5c0f\u904d\u7a7a\u5730\u4e0a\uff0c\u9ad8\u624b\u8fc7\u62db\uff0c\u867d\u672c\u4e0d\u9700\u4ec0\u4e48\u7a7a\u9614\u4e4b \u5904\uff0c\u4f46\u4f0a\u98ce\u638c\u529b\u6240\u53ca\uff0c\u56db\u5468\u7684\u67af\u6728\uff0c\u81ea\u7136\u7b71\u4ed6\u8fd9\u79cd\u51cc\u5389\u7684\u638c\u98ce\u6467\u6bc1\u4e0d\u5c11\u3002\u52a8\u624b\u95f4\uff0c\u4e5f\u96be\u514d\u4f1a \u53d1\u51fa\u4e9b\u58f0\u97f3\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u6709\u4e9b\u7740\u6025\uff0c\u6b64\u5730\u867d\u662f\u6df1\u5c71\uff0c\u4f46\u6df1\u5c71\u4e4b\u4e2d\uff0c\u6b63\u662f\u6b66\u6797\u8c6a\u58eb\u51fa\u6ca1\u4e4b\u5904\uff0c\u4ed6\u53ef\u4e0d\u613f\u610f \u88ab\u4eba\u770b\u51fa\u8eab\u5206\u3002\u4f46\u4ed6\u4e5f\u52bf\u5fc5\u4e0d\u80fd\u5728\u7cca\u91cc\u7cca\u6d82\u7684\u6253\u4e86\u4e00\u573a\u540e\uff0c\u5c31\u7a81\u7136\u6e9c\u8d70\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5f88\u60f3\u559d\u4f4f\u5bf9\u65b9\uff0c\u95f4\u6e05\u6765\u5386\u3002\u5047\u5982\u5bf9\u65b9\u6839\u672c\u548c\u81ea\u5df1\u65e0\u5173\uff0c\u4e5f\u8ba4\u4e0d\u51fa\u81ea\u5df1\u662f\u8c01\u65f6\uff0c\u90a3 \u4e48\u81ea\u5df1\u53ef\u5b9e\u5728\u6ca1\u6709\u52a8\u624b\u7684\u5fc5\u8981\u3002 \u3000\u3000\u201c\u4eba\u5bb6\u6216\u8bb8\u4e5f\u662f\u9690\u8ff9\u5728\u6b64\u5730\u7684\u6b66\u6797\u4eba\u7269\uff0c\u662f\u4ee5\u4e5f\u4e0d\u613f\u610f\u88ab\u4eba\u63a2\u67e5\u3002\u5979\u5927\u6982\u4e5f\u8bef\u4f1a\u4e86\u81ea\u5df1 \u5bf9\u5979\u6709\u7740\u4ec0\u4e48\u6076\u610f\uff0c\u662f\u4ee5\u624d\u4f1a\u95f7\u58f0\u4e0d\u54cd\u5730\u4e00\u5473\u52a8\u624b\u3002\u201d \u3000\u3000\u4e5f\u5728\u5fc3\u4e2d\u6781\u5feb\u5730\u8f6c\u4e86\u51e0\u8f6c\uff0c\u786e\u5207\u5730\u8ba4\u4e3a\u53ea\u6709\u8fd9\u4e2a\u60f3\u6cd5\u548c\u4e8b\u5b9e\u6700\u4e3a\u63a5\u8fd1\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u8d77\u4e8e\u5e38\u4eba\u7684\u5730\u65b9\uff0c\u56e0\u4e3a\u4ed6\u5728\u6b64\u60c5\u51b5\u4e4b\u4e2d\uff0c\u8fd8\u80fd\u4e3a\u5bf9\u65b9\u8bbe\u60f3\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u51fa\u62db\u4e4b\u95f4\uff0c\u66f4\u662f\u5b88\u52bf\u591a\u4e8e\u653b\u52bf\uff0c\u5fc3\u4e2d\u4e5f\u5728\u76d8\u7b97\u7740\uff0c\u8be5\u5982\u4f55\u53d1\u8bdd\uff0c\u4f7f\u81ea\u5df1\u80fd\u5206\u51fa\u8fd9 \u4e2a\u5c11\u5973\u5012\u5e95\u662f\u654c\u662f\u53cb\uff1f \u3000\u3000\u4f46\u662f\u4ed6\u7684\u601d\u7d22\uff0c\u5374\u5f88\u5feb\u5730\u88ab\u53e6\u5916\u7a81\u7136\u800c\u751f\u7684\u4e8b\u6240\u6253\u65ad\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u773c\u89d2\u52a8\u5904\uff0c\u7adf\u53d1\u73b0\u5728\u90a3\u5c11\u5973\u7684\u51fa\u5904\uff0c\u53c8\u6709\u4e00\u6761\u4eba\u5f71\u7535\u5c04\u800c\u51fa\uff0c\u4f0a\u98ce\u4e0d\u7981\u6697\u53eb\u4e00\u58f0 \u201c\u7cdf\u201d\uff01\u5047\u5982\u8fd9\u4eba\u4e5f\u50cf\u90a3\u5c11\u5973\u4e00\u6837\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u5c31\u51fa\u624b\uff0c\u90a3\u81ea\u5df1\u5c82\u975e\u8981\u7cdf\uff1f \u3000\u3000\u4ed6\u53ef\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u4ef6\u4e8b\u7684\u53d1\u751f\uff0c\u4e3b\u8981\u539f\u56e0\u662f\u56e0\u4e3a\u4ed6\u81ea\u5df1\u7684\u591a\u8651\uff0c\u867d\u7136\u8fd9\u5e76\u4e0d\u80fd\u602a\u4ed6\uff0c\u53ef \u662f\u4e5f\u7edd\u4e0d\u80fd\u602a\u7740\u4eba\u5bb6\u5440\uff01 \u3000\u3000\u90a3\u5c11\u5973\u4e00\u770b\u5230\u53e6\u4e00\u4eba\u5f71\uff0c\u7acb\u523b\u5a07\u559d\u9053\uff01 \u3000\u3000\u201c\u5988\uff01\u8fd9\u4eba\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u6050\u6015\u662f\u6765\u67e5\u63a2\u6211\u4eec\u7684\uff0c\u53ef\u7edd\u4e0d\u80fd\u653e\u4ed6\u8d70\u4e86\uff01\u201d \u3000\u3000\u4f0a\u98ce\u542c\u4e86\uff0c\u7709\u5934\u4e0d\u7981\u4e0d\u76b1\uff01 \u3000\u3000\u90a3\u4eba\u5f71\u5374\u500f\u7136\u987f\u4f4f\u8eab\u5f62\uff0c\u9053\uff1a\u201c\u7433\u513f\uff01\u5148\u4f4f\u624b\uff0c\u8ba9\u6211\u95ee\u95ee\u4ed6\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u542c\u4e86\uff0c\u6781\u4e0d\u60c5\u613f\u5730\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u500f\u5730\u63a0\u540e\u56db\u5c3a\u3002 \u3000\u3000\u4f0a\u98ce\u81ea\u7136\u4e5f\u4e0d\u4f1a\u518d\u62a2\u524d\u51fa\u624b\uff0c\u53cc\u62f3\u4e00\u62b1\uff0c\u5353\u7136\u800c\u7acb\u6b63\u5f85\u51fa\u8a00\uff0c\u540e\u6765\u90a3\u4eba\u5df2\u8bf4\u9053\uff1a\u201c\u670b\u53cb \u662f\u90a3\u91cc\u6765\u7684\uff0c\u5230\u8fd9\u91cc\u6765\u662f\u5e72\u4ec0\u4e48\uff1f\u201d\u8bed\u6c14\u51b7\u51b0\u51b0\u5730\uff0c\u5927\u6709\u4f60\u4e0d\u8bf4\u6e05\u695a\uff0c\u4f60\u5c31\u522b\u60f3\u8d70\u4e86\u4e4b\u610f\u3002 \u3000\u3000\u4f0a\u98ce\u95ea\u76ee\u6253\u91cf\uff0c\u5374\u89c1\u8fd9\u4eba\u662f\u4e2a\u5c11\u5987\uff0c\u66ae\u8272\u4e2d\u5374\u770b\u4e0d\u751a\u6e05\uff0c\u4f46\u80e7\u6726\u95f4\u5df2\u770b\u51fa\u59ff\u8272\u751a\u7f8e\uff0c\u5c24 \u5176\u4f53\u6001\u5a40\u5a1c\uff0c\u52a8\u4eba\u5df2\u6781\u3002 \u3000\u3000\u4ed6\u65b9\u6014\u4e86\u6014\uff0c\u90a3\u5c11\u5987\u53c8\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u5230\u8fd9\u534e\u5c71\u6765\uff0c\u82e5\u662f\u60f3\u627e\u6211\u6bcd\u5973\u7684\u9709\u6c14\uff0c\u90a3\u4e48\uff0c \u670b\u53cb\uff01\u4eca\u5929\u4e5f\u522b\u60f3\u518d\u8d70\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5b8c\u5168\u662f\u6c5f\u6e56\u53e3\u543b\uff0c\u663e\u89c1\u5f97\u4ee5\u524d\u4e5f\u662f\u95ef\u8361\u6c5f\u6e56\u7684\u4eba\u7269\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u6709\u6c14\uff0c\u6697\u5fd6\uff1a\u201c\u96be\u9053\u534e\u5c71\u662f\u4f60\u7684\uff0c\u6211\u5c31\u6765\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u201c\u5988\uff0c\u8fd9\u4eba\u9b3c\u9b3c\u795f\u795f\u5730\uff0c\u5728\u8fd9\u91cc\u803d\u4e86\u4e09\uff0c\u56db\u4e2a\u65f6\u8fb0\u8fd8\u4e0d\u8d70\uff0c\u53c8\u5728\u6211\u4eec\u8fd9\u91cc\u4e1c\u5f20\u897f\u671b\u7684\uff0c \u4e00\u5b9a\u662f\u90a3\u5bb6\u4f19\u7684\u72d7\u817f\u5b50\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5973\u7684\u8bdd\uff0c\u7adf\u662f\u8ba4\u5b9a\u4e86\u4ed6\u4e0d\u662f\u597d\u4eba\u3002 \u3000\u3000\u4f0a\u98ce\u77e5\u9053\u8bef\u4f1a\u5df2\u6df1\uff0c\u4f46\u4ed6\u5982\u4f55\u624d\u80fd\u89e3\u91ca\u6b64\u4e8b\u5462\uff1f\u4ed6\u4e00\u65f6\u95f4\u7adf\u60f3\u4e0d\u51fa\u9002\u5f53\u7684\u8bdd\u6765\u3002 \u3000\u3000\u201c\u5728\u4e0b\u4f0a\u98ce\uff0c\u5076\u6e38\u534e\u5c71\uff0c\u5bf9\u4e24\u4f4d\u7edd\u65e0\u6076\u610f\u3002\u201d\u4ed6\u5450\u5450\u5730\u8bf4\u9053\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u5df2\u786e\u4fe1\u8fd9\u6bcd\u5973\u4e24\u4eba\u7edd\u5bf9\u4e0d\u662f\u81ea\u5df1\u7684\u5bf9\u5934\uff0c\u5fc3\u4e2d\u6240\u5e0c\u671b\u7684\uff0c\u53ea\u662f\u8fd9\u6bcd\u5973\u4e24\u4eba\u4e5f\u660e\u767d \u81ea\u5df1\u4e0d\u662f\u5979\u4eec\u7684\u5bf9\u5934\u5c31\u597d\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u5076\u6e38\u534e\u5c71\uff0c\u53ef\u662f\u4f60\u5e72\u4ec0\u4e48\u8981\u5728\u8fd9\u5757\u5730\u65b9\u4e00\u803d\u5c31\u662f\u597d\u51e0\u4e2a\u65f6\u8fb0\u5462\uff1f\u96be\u9053\u8fd9\u5757\u5730\u65b9\u6709\u4ec0 \u4e48\u5b9d\u8d1d\u5417\uff1f\u201d \u3000\u3000\u201c\u4ee5\u9601\u4e0b\u7684\u8eab\u624b\uff0c\u8be5\u662f\u6c5f\u6e56\u4e2d\u6210\u540d\u7acb\u4e07\u7684\u4eba\u7269\u3002\u201d\u90a3\u5c11\u5987\u51b7\u51b7\u4e00\u54fc\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u662f\u201c\u4f0a \u98ce\u201d\u8fd9\u540d\u5b57\uff0c\u6211\u5374\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u8fd9\u6bcd\u5973\u4e24\u4eba\uff0c\u8bcd\u950b\u7280\u5229\u3002 \u3000\u3000\u4f0a\u98ce\u62c2\u7136\u9053\uff1a\u201c\u5728\u4e0b\u5bf9\u4e24\u4f4d\u786e\u5b9e\u6ca1\u6709\u6076\u610f\uff0c\u4e5f\u4e0d\u77e5\u9053\u4e24\u4f4d\u662f\u8c01\uff0c\u4e24\u4f4d\u5982\u679c\u4e0d\u80af\u76f8\u4fe1\uff0c\u5728 \u4e0b\u4e5f\u65e0\u6cd5\u89e3\u91ca\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u5728\u4e0b\u4e5f\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u4e24\u4f4d\u5982\u80fd\u4f53\u8c05\uff0c\u5728\u4e0b\u4e5f\u7edd\u4e0d\u4f1a\u5c06\u6709\u5173 \u4eca\u65e5\u4e4b\u4e8b\u8bf4\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u751f\u6027\u4ea6\u6781\u9ad8\u50b2\uff0c\u51fa\u5e08\u672a\u4e45\uff0c\u5373\u4eab\u76db\u540d\uff0c\u51e0\u65f6\u53d7\u8fc7\u8fd9\u6837\u7684\u76d8\u8bd8.\uff0c\u6b64\u65f6\u8bed\u6c14\u4e2d\uff0c\u5145\u6ee1\u4e0d \u60a6\u4e4b\u611f\uff0c\u8a00\u4e0b\u5927\u6709\u201c\u4fe1\u4e0d\u4fe1\u7531\u4f60\u201d\u4e4b\u610f\u3002 \u3000\u3000\u90a3\u77e5\u90a3\u5c11\u5987\u7684\u795e\u8272\uff0c\u5374\u5927\u89c1\u548c\u7f13\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u5374\u53c8\u600e\u80fd\u4fe1\u5f97\u8fc7\u4f60\u5462\uff1f\u201d \u3000\u3000\u8bed\u867d\u4ecd\u662f\u76d8\u8bd8\uff0c\u4f46\u8bed\u6c14\u5374\u5df2\u4e0d\u518d\u51b7\u51b0\u51b0\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u6014\u4e86\u6014\uff0c\u60f3\u201c\u8fd9\u6bcd\u5973\u4e24\u4eba\u5fc5\u5b9a\u4e5f\u662f\u4e3a\u62a5\u4ec7\uff0c\u9690\u8ff9\u534e\u5c71\u4e4b\u9634\uff0c\u5979\u4e24\u4eba\u6b66\u529f\u6781\u4e00\u5c40\uff0c \u5979\u4eec\u7684\u4ec7\u5bb6\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5728\u5fc3\u4e2d\u601d\u91cf\uff0c\u5df2\u7ecf\u4e86\u89e3\u4e86\u4eba\u5bb6\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u4e48\u7d27\u5f20\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e5f\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u6b64\u523b\u4eba\u5bb6\u8fd9\u6837\u95ee\u4ed6\uff0c\u4ed6\u77e5\u9053\u5018\u82e5\u4e0d\u5584\u4e3a\u7b54\u8986\uff0c\u5fc5\u5b9a\u5f88\u96be\u4f7f\u4eba\u5bb6\u6ee1\u610f\uff0c\u53ef\u662f\u8fd9\u79cd\u95ee\u8bdd\uff0c\u81ea \u5df1\u53c8\u5c06\u5982\u4f55\u7b54\u8986\u5462\uff1f \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e94\u7ae0\u3000\u593a\u547d\u53cc>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e94\u7ae0\u3000\u593a\u547d\u53cc \u3000\u3000\u4e09\u4eba\u516d\u76ee\u76f8\u5bf9\uff0c\u9759\u5f97\u9664\u4e86\u98ce\u58f0\u4e4b\u5916\uff0c\u5176\u4ed6\u4efb\u4f55\u58f0\u97f3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u84e6\u5730\uff0c\u5c31\u5728\u4f0a\u98ce\u5148\u524d\u4f2b\u7acb\u7684\u90a3\u5757\u5c71\u77f3\u4e4b\u5904\uff0c\u6084\u6084\u5730\u9732\u51fa\u4e86\u56db\u53ea\u773c\u775b\u6765\uff0c\u6ce8\u89c6\u7740\u4ed6\u4eec\u3002\u8fd9 \u4e24\u4eba\u4ece\u5c71\u4e0b\u6765\uff0c\u4f0a\u98ce\u548c\u90a3\u6bcd\u5973\u4e24\u4eba\u7adf\u6ca1\u6709\u89c9\u5bdf\u5230\uff0c\u8f7b\u529f\u80fd\u7792\u8fc7\u4ed6\u4eec\u4e09\u4eba\u7684\uff0c\u5b9a\u662f\u9ad8\u7edd\u7684\u8eab \u624b\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u4f0a\u98ce\u7b49\u4e09\u4eba\u5168\u5fc3\u5728\u6ce8\u610f\u654c\u65b9\uff0c\u800c\u65e0\u6687\u987e\u53ca\u5176\u4ed6\u7684\u5173\u7cfb\u3002 \u3000\u3000\u4f0a\u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u5b9e\u662f\u65e0\u610f\u95ef\u5165\u534e\u5c71\uff0c\u5bf9\u4e24\u4f4d\u66f4\u6beb\u65e0\u4f01\u56fe\uff0c\u4e24\u4f4d\u5982\u4e0d\u89c1\u4fe1\uff0c\u5728\u4e0b\u4e5f\u5b9e\u5728\u6ca1\u6709\u4ec0\u4e48\u65b9 \u6cd5\u53ef\u4ee5\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u8bf4\u51fa\u4f60\u5b9e\u5728\u7684\u6765\u5386\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5987\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u987b\u77e5\u9601\u4e0b\u867d\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u6211\u6bcd\u5973\u4e5f\u6709\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6c88\u541f\u4e0b\u534a\u664c\uff0c\u6168\u7136\u9053\uff1a \u3000\u3000\u201c\u6211\u60f3\u4e24\u4f4d\u5fc5\u662f\u4e3a\u4e86\u907f\u4ec7\uff0c\u53ea\u662f\u5728\u4e0b\u7684\u4ec7\u5bb6\uff0c\u6050\u6015\u6bd4\u4e24\u4f4d\u7684\u4ec7\u5bb6\u8fd8\u8981\u5389\u5bb3\uff0c\u5728\u4e0b\u5b9e \u5728\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5973\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5988\uff01\u60a8\u8ddf\u4ed6\u591a\u565c\u82cf\u5e72\u4ec0\u4e48\uff1f\u6211\u770b\u4ed6\u8bf4\u8bdd\u541e\u541e\u5410\u5410\u5730\u4e00\u5b9a\u6000\u7740\u9b3c\u80ce\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u201c\u5929\u4e89 \u6559\u201d\u4e0b\u7684\u72d7\u817f\u5b50\u3002\u201d \u3000\u3000\u201c\u5929\u4e89\u6559\u201d\u8fd9\u4e09\u5b57\u4e00\u51fa\u53e3\uff0c\u4f0a\u98ce\u4e0d\u7531\u84e6\u5730\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u5979\u4eec\u7684\u4ec7\u5bb6\uff0c\u4e5f\u662f\u5929\u4e89 \u6559\uff01\u201d\u540c\u4ec7\u4e4b\u5ff5\uff0c\u6cb9\u7136\u800c\u751f\uff0c\u6b63\u6b32\u8bf4\u51fa\u81ea\u5df1\u7684\u6765\u5386\u3002 \u3000\u3000\u4ed6\u8fd8\u672a\u5f00\u53e3\uff0c\u7a81\u7136\u6709\u4e00\u9635\u523a\u8033\u7684\u7b11\u58f0\uff0c\u4ece\u4ed6\u8eab\u540e\u53d1\u51fa\u2026\u2026\u3002 \u3000\u3000\u7b11\u58f0\u4f7f\u5f97\u8fd9\u4e09\u4eba\u540c\u65f6\u4e00\u60ca\uff0c\u5413\u7136\u671b\u53bb\uff0c\u5374\u89c1\u4e24\u4eba\u5e76\u80a9\u7acb\u5728\u90a3\u5757\u7a81\u51fa\u7684\u5c71\u77f3\u4e4b\u4e0a\uff0c\u8eab\u5f62\u968f \u98ce\u98d8\u52a8\uff0c\u50cf\u662f\u7ad9\u7acb\u4e0d\u7a33\u4f3c\u7684\uff0c\u5bbd\u5927\u8863\u886b\u4e2d\u7684\u8eab\u8eaf\uff0c\u597d\u6bd4\u4e24\u6839\u7af9\u7aff\uff0c\u7626\u5f97\u50cf\u662f\u79cb\u65e5\u7530\u91ce\u95f4\uff0c\u519c \u5bb6\u7528\u4ee5\u9632\u96c0\u7684\u7a3b\u8349\u4eba\uff0c\u5728\u90a3\u67af\u67f4\u822c\u8eab\u8eaf\u4e0a\u7684\uff0e\u4e24\u9897\u5934\u9885\uff0c\u5374\u538b\u5f97\u90a3\u7ec6\u5f31\u7684\u8116\u5b50\u50cf\u662f\u4e0d\u582a\u8d1f\u8377 \u4f3c\u7684\u3002 \u3000\u3000\u8fd9\u5f62\u72b6\u867d\u7136\u5413\u4eba\uff0c\u53ef\u662f\u66f4\u4ee4\u4f0a\u98ce\u5403\u60ca\u7684\u662f\uff1a\u8fd9\u4e24\u4eba\u8eab\u4e0a\u7a7f\u7740\u7684\u7adf\u662f\u91d1\u8272\u957f\u886b\uff01 \u3000\u3000\u201c\u5929\u4e89\u6559\uff01\u201d\u8fd9\u4e09\u5b57\u5728\u4f0a\u98ce\u548c\u90a3\u6bcd\u5973\u4e24\u4eba\u7684\u5fc3\u91cc\uff0c\u50cf\u662f\u9739\u96f3\u4f3c\u7684\uff0c\u8f70\u5730\u4e00\u58f0\uff0c\u76f4\u900f\u5fc3 \u5e95\u3002 \u3000\u3000\u201c\u563f\uff01\u563f\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u602a\u4eba\u540c\u65f6\u5f00\u53e3\uff0c\u751f\u50cf\u662f\u65e9\u5df2\u7ea6\u597d\u4f3c\u7684\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u597d\u6781\u4e86\uff01\u6211\u5144\u5f1f\u771f\u662f\u6709\u5e78\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u7a77\u5c71\u4e4b\u4e2d\uff0c\u5374\u89c1\u5230\u540d\u6ee1\u5929\u4e0b\u7684\u4e09\u6e58\u5927\u4fa0\u51cc\u5317\u4fee \u7684\u592b\u4eba\uff0c\u771f\u662f\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5987\u8138\u8272\u7acb\u65f6\u7070\u767d\uff0c\u5728\u591c\u8272\u4e2d\u770b\u8d77\u6765\uff0c\u8fd9\u79cd\u5168\u7136\u6ca1\u6709\u8840\u8272\u7684\u9762\u5b54\uff0c\u6700\u4ee4\u4eba\u89c9\u5f97\u53ef\u6016\u3002 \u3000\u3000\u5979\u6068\u6068\u5730\u671b\u4e86\u4f0a\u98ce\u4e00\u773c\uff0c\u4f0a\u98ce\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u6218\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u5979\u4e00\u5b9a\u8bef\u4f1a\u8fd9\u4e24\u4eba\u662f\u88ab\u4ed6\u5f15\u6765\u7684\uff0c\u4e8e\u662f\u4e0d\u89c9\u6709\u4e9b\u51a4\u6789\u3002 \u3000\u3000\u201c\u53ef\u662f\uff0c\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u600e\u4f1a\u504f\u504f\u8fd9\u4e2a\u65f6\u5019\u6765\u5462\uff1f\u201d \u3000\u3000\u4e0d\u7528\u591a\u4f5c\u601d\u7d22\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e24\u4e2a\u602a\u4eba\uff0c\u5c31\u662f\u6c5f\u6e56\u4e0a\u4eba\u4eba\u95fb\u540d\u8272\u53d8\u7684\u201c\u593a\u547d\u53cc\u96c4\u201d\u3002 \u3000\u3000\u56e0\u4e3a\u6b66\u6797\u4e4b\u4e2d\uff0c\u4e5f\u552f\u6709\u8fd9\u4e24\u4eba\u624d\u6709\u8fd9\u526f\u957f\u50cf\u3002 \u3000\u3000\u8fd9\u201c\u593a\u547d\u53cc\u96c4\u201d\u662f\u4e00\u5bf9\u5b6a\u751f\u5144\u5f1f\uff0c\u81ea\u5e7c\u751f\u606f\u76f8\u901a\uff0c\u957f\u5927\u540e\u4e5f\u6ca1\u6709\u4e00\u523b\u5206\u5f00\u8fc7\u3002\u624b\u5e95\u4e4b\u72e0 \u8fa3\uff0c\u5728\u6b66\u6797\u4e2d\u65e9\u8d1f\u76db\u540d\u3002\u638c\u6307\u7684\u529f\u529b\uff0c\u81ea\u6210\u4e00\u5bb6\uff0c\u8170\u4e2d\u5341\u4e03\u8282\u4eae\u94f6\u957f\u97ad\u201c\u6cfc\u98ce\u5341\u4e09\u6253\u201d\uff0c\u66f4 \u662f\u79f0\u8a89\u6b66\u6797\uff0c\u5c24\u5176\u5389\u5bb3\u7684\u662f\uff01\u52a8\u624b\u53d1\u62db\uff0c\u4e24\u4eba\u4e4b\u95f4\u7684\u914d\u5408\uff0c\u5929\u8863\u65e0\u7f1d\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u751f\u6027\u5947\u7279\uff0c\u5374\u4e0d\u77e5\u9053\u600e\u5730\u4e5f\u4e3a\u201c\u5929\u4e89\u6559\u201d\u6240\u7f51\u7f57\uff0c\u5728\u5929\u4e89\u6559\u4e0b\u91d1\u8863\u575b\u91cc\uff0c\u4e3a\u5341\u4e5d \u4e2a\u91d1\u8863\u9999\u4e3b\u4e2d\u6b66\u529f\u9887\u5f3a\u8005\u4e4b\u4e00\u3002 \u3000\u3000\u90a3\u4fdd\u5b9a\u5e9c\u5916\u548c\u5415\u5357\u4eba\u52a8\u624b\u7684\u6731\u7802\u638c\uff0c\u5374\u5728\u91d1\u8863\u9999\u4e3b\u4e2d\u5360\u7740\u7b2c\u5341\u516b\u4f4d\uff0c\u6bd4\u8d77\u4ed6\u4eec\u4e24\u4eba\u6765\uff0c \u81ea\u662f\u5927\u5927\u4e0d\u5982\u3002 \u3000\u3000\u539f\u6765\u5929\u4e89\u6559\u81ea\u6559\u4e3b\u4ee5\u4e0b\u3001\u4f9d\u6b66\u529f\u5f3a\u5f31\uff0c\u5171\u5206\u4e94\u575b\uff0c\u6b66\u529f\u6700\u5f3a\u8005\uff0c\u5c31\u662f\u91d1\u8863\u575b\uff0c\u4ee5\u4e0b\u624d\u662f \u7d2b\u8863\uff0c\u84dd\u8863\uff0c\u8910\u8863\uff0c\u81f3\u4e8e\u4e4c\u8863\u575b\uff0c\u5c31\u662f\u6700\u57fa\u5c42\u7684\u6559\u4f17\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5987\u679c\u7136\u5c31\u662f\u4e09\u6e58\u5927\u4fa0\u51cc\u5317\u4fee\u7684\u672a\u4ea1\u4eba\u5b59\u654f\u3002 \u3000\u3000\u4e09\u6e58\u5927\u4fa0\u4ee5\u638c\u4e2d\u5251\u72ec\u9738\u4e09\u6e58\u591a\u5e74\uff0c\u7adf\u5728\u5929\u4e89\u6559\u6269\u5145\u52bf\u529b\u81f3\u4e09\u6e58\u65f6\uff0c\u5728\u91d1\u8863\u575b\u4e2d\u7684\u4e03\u7075\u98de \u8679\u5370\u5b9d\u6797\uff0c\u4e07\u6bd2\u7ae5\u5b50\u5510\u66f4\u7684\u4e24\u4ef6\u795e\u5947\u5175\u5203\u548c\u7edd\u6bd2\u6697\u5668\u4e4b\u4e0b\uff0c\u53d7\u4f24\u800c\u4e0d\u6cbb\u3002 \u3000\u3000\u5929\u4e89\u6559\u7d20\u6765\u8d76\u5c3d\u6559\u7edd\uff0c\u8fd9\u5b64\u82e6\u65e0\u4f9d\u7684\u6bcd\u5973\uff0c\u624d\u907f\u4ec7\u81f3\u8fd9\u534e\u5c71\u4e4b\u9634\u6765\uff0c\u82e6\u7ec3\u6b66\u529f\uff0c\u5180\u6c42\u590d \u4ec7\u3002 \u3000\u3000\u90a3\u77e5\u5374\u5728\u6b64\u65f6\uff0c\u53c8\u9047\u89c1\u4e86\u6c5f\u6e56\u4e2d\u7684\u715e\u661f\u2014\u2014\u593a\u547d\u53cc\u96c4\u3002 \u3000\u3000\u201c\u51cc\u592b\u4eba\uff01\u201d \u3000\u3000\u593a\u547d\u53cc\u96c4\u9634\u68ee\u68ee\u5730\u9f50\u58f0\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u6559\u4e3b\u60f3\u5ff5\u4f60\u5f97\u5f88\uff01\u61ca\u95fb\u4f60\u662f\u6b66\u6797\u4e2d\u7684\u7f8e\u4eba\uff0c\u600e\u4e48\u5fcd\u5fc3\u8ba9\u6211\u4eec\u6559\u4e3b\u60f3\u5f97\u8fd9\u4e48\u60e8\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u4f7f\u4eba\u770b\u4e86\u4e0d\u7981\u6bdb\u53d1\u609a\u7136\u3002 \u3000\u3000\u4ed6\u4eec\u7f13\u6162\u5730\u8fc8\u7740\u6b65\u5b50\u8d70\u8fc7\u6765\uff0c\u53e3\u4e2d\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\uff01\u8fd8\u662f\u8ddf\u7740\u6211\u4eec\u4e00\u9f50\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u2014\u2014\u4e09\u6e58\u5927\u4fa0\u7684\u7231\u5973\u51cc\u7433\uff0c\u6c14\u5f97\u4ea6\u662f\u9762\u76ee\u53d8\u8272\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u4e24\u4e2a\u602a\u7269\u5c11\u5e9f\u8bdd\uff0c\u8981\u627e\u6b7b\uff0c\u59d1\u5a18\u5c31\u9001\u4f60\u4eec\u56de\u8001\u5bb6\u53bb\uff01\u201d \u3000\u3000\u201c\u602a\u7269\uff01\u201d\u593a\u547d\u53cc\u96c4\u5bab\u6c0f\u5144\u5f1f\u4e00\u9f50\u88c2\u5f00\u5927\u53e3\uff0c\u602a\u7b11\u7740\u8bf4\uff1a \u3000\u3000\u201c\u8fd9\u5c0f\u59d1\u5a18\u8bf4\u8bdd\u771f\u6709\u610f\u601d\uff0c\u55ef\uff01\u957f\u5927\u4e86\u548c\u4f60\u5988\u5988\u4e00\u6837\uff0c\u4e5f\u662f\u4e2a\u7f8e\u4eba\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ed6\u4eec\u5df2\u8d70\u5230\u4f0a\u98ce\u8eab\u4fa7\uff0c\u5374\u8fde\u773c\u89d2\u4e5f\u4e0d\u5411\u4f0a\u98ce\u779f\u4e00\u4e0b\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u6709\u770b\u89c1\u4ed6\u4f3c \u7684\u3002\u201c\u4e0d\u77e5\u4ed6\u4eec\u8ba4\u4e0d\u8ba4\u5f97\u6211\uff01\u201d\u4f0a\u98ce\u6697\u5fd6\u3002\u4ed6\u7684\u8fd9\u4e2a\u7591\u95ee\uff0c\u7acb\u523b\u5c31\u83b7\u5f97\u4e86\u7b54\u8986\u3002 \u3000\u3000\u593a\u547d\u53cc\u96c4\u4e2d\u7684\u5bab\u7533\u2014\u2014\u56e0\u4e3a\u4ed6\u662f\u5728\u7533\u65f6\u843d\u5730\u7684\u2014\u2014\u4e00\u4f38\u624b.\u63a8\u5f00\u4e86\u4f0a\u98ce\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u8fd9\u4f4d\u670b\u53cb\u50cf\u662f\u548c\u51cc\u592b\u4eba\u4e5f\u6709\u672a\u4e86\u4e4b\u4e8b\uff0c\u4e0d\u8fc7\u90a3\u4e9b\u4e8b\u5374\u51b2\u7740\u6211\u5144\u5f1f\u7684\u9762\u5b50\u4e0a\u63ed\u8fc7\u4e86\u3002\u670b\u53cb\uff0c \u4f60\u95ea\u5f00\uff01\u201d \u3000\u3000\u5bab\u9149\u4e5f\u671b\u7740\u4ed6\u4e00\u7b11\uff0c\u4f3c\u4e4e\u5bf9\u4ed6\u751a\u6709\u597d\u611f\u3002 \u3000\u3000\u4f0a\u98ce\u9000\u5f00\u4e00\u6b65\uff0c\u6697\u5fd6\uff1a\u201c\u4ed6\u4eec\u679c\u7136\u4e0d\u8ba4\u5f97\u6211\u4e86\u3002\u201d \u3000\u3000\u770b\u7740\u593a\u547d\u53cc\u96c4\u548c\u5b59\u654f\u4e4b\u95f4\u7684\u8ddd\u79bb\u66f4\u77ed\uff0c\u201c\u4e0d\u77e5\u9053\u51cc\u5927\u4fa0\u7684\u59bb\u5973\uff0c\u662f\u4e0d\u662f\u8fd9\u4e24\u4e2a\u602a\u7269\u7684\u654c \u624b\uff1f\u201d \u3000\u3000\u4e09\u6e58\u5927\u4fa0\u867d\u662f\u9887\u6709\u4fa0\u540d\uff0c\u4f46\u4f0a\u98ce\u4ecd\u4e0d\u7981\u4e3a\u8fd9\u6bcd\u5973\u4e24\u4eba\u62c5\u5fc3\u3002 \u3000\u3000\u6700\u4e3b\u8981\u7684\u662f\uff1a\u4ed6\u5bf9\u8fd9\u6bcd\u5973\u4e24\u4eba\u6beb\u65e0\u6076\u611f\uff0c\u4f55\u51b5\u8fd8\u662f\u540c\u4ec7\u654c\u5ffe\u5462\uff1f \u3000\u3000\u4f46\u662f\u4ed6\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u6211\u81ea\u8eab\u5c1a\u4e14\u96be\u4fdd\uff0c\u867d\u7136\u4ed6\u4eec\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u8fd8\u662f\u4e00\u8d70\u4e86\u4e4b\u3002\u6211\u82e5\u4e00\u51fa\u624b\uff0c\u8fd9\u4e24\u4e2a\u602a \u7269\u5fc5\u5b9a\u53ef\u4ee5\u770b\u51fa\u6211\u7684\u6765\u8def\uff0c\u90a3\u65f6\u5019\u4ed6\u4eec\u7684\u5bf9\u8c61\u6050\u6015\u5c06\u662f\u6211\uff0c\u800c\u4e0d\u662f\u8fd9\u6bcd\u5973\u4e24\u4eba\u4e86\u3002\u201d \u3000\u3000\u4ed6\u56de\u8f6c\u5934\uff0c\u4e0d\u53bb\u770b\u90a3\u8fb9\u7684\u60c5\u51b5\u3002 \u3000\u3000\u201c\u53cd\u6b63\u8fd9\u6bcd\u5973\u4e24\u4eba\uff0c\u6211\u53c8\u4e0d\u8ba4\u8bc6\uff0c\u4f55\u51b5\u5979\u4eec\u8fd8\u8981\u903c\u7740\u6211\u52a8\u624b\uff0c\u6211\u5c31\u662f\u4e0d\u7ba1\u5979\u4eec\uff0c\u4e5f\u6ca1\u4ec0 \u4e48\u8bf4\u4e0d\u8fc7\u53bb\u3002\u201d\u4f0a\u98ce\u66ff\u81ea\u5df1\u89e3\u91ca\u7740\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u89c9\u5f97\u826f\u5fc3\u6709\u4e9b\u4e0d\u5b89\u3002 \u3000\u3000\u4ed6\u5f80\u90a3\u8fb9\u8d70\u4e86\u51e0\u6b65\uff0c\u65b9\u60f3\u7eb5\u8eab\u79bb\u53bb\uff0c\u5374\u7a81\u7136\u542c\u5230\u4e00\u58f0\u60e8\u547c\u2026\u2026\u3002 \u3000\u3000\u4ed6\u6115\u7136\u56de\u5934\u800c\u671b\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u5979\u4eec\u51b3\u5b9a\u4e0d\u4f1a\u5728\u5982\u6b64\u77ed\u7684\u65f6\u95f4\u4e2d\uff0c\u5c31\u5206\u51fa\u80dc\u8d1f\u7684\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u56de\u5934\uff0c\u4f7f\u5f97\u540e\u6765\u8bb8\u591a\u4e8b\u60c5\u6539\u53d8\u4e86\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e00\u751f\u7684\u547d\u8fd0\uff0c\u4e5f\u5728\u8fd9\u4e00\u56de\u987e\u4e4b\u95f4\uff0c\u51b3 \u5b9a\u4e86\u4e00\u5207\u3002 \u3000\u3000\u7136\u800c\u65e0\u8bba\u4ed6\u8fd9\u4e00\u56de\u987e\u662f\u5bf9\u4ed6\u6709\u5229\u6291\u6216\u6709\u5bb3\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u80fd\u4e0d\u56de\u5934\u5417\uff1f \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u516d\u7ae0\u3000\u62ab\u661f\u6234\u6708>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u7ae0\u3000\u62ab\u661f\u6234\u6708 \u3000\u3000\u593a\u547d\u53cc\u96c4\u5bab\u6c0f\u5144\u5f1f\uff0c\u8fdc\u6e38\u534e\u5c71\uff0c\u7adf\u4e00\u53bb\u4e0d\u8fd4\uff0c\u5929\u4e89\u6559\u60ca\u7591\u4e4b\u4e0b\uff0c\u5927\u641c\u534e\u5c71\uff0c\u7adf\u5728\u534e\u5c71\u4e4b \u9634\u53d1\u73b0\u593a\u547d\u53cc\u96c4\u7684\u4e24\u5177\u5c38\u8eab\u3002 \u3000\u3000\u8fd9\u53f7\u79f0\u201c\u53cc\u96c4\u201d\u7684\u4e24\u4e2a\u6b66\u6797\u6563\u661f\uff0c\u771f\u7684\u53d8\u6210\u4e86\u201c\u53cc\u96c4\u201d\u4e86\u3002 \u3000\u3000\u800c\u4e14\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\uff0c\u6b7b\u72b6\u751a\u60e8\uff0c\u4e00\u4e2a\u9762\u76ee\u8840\u8089\u72fc\u85c9\uff0c\u751f\u50cf\u662f\u88ab\u4eba\u4ee5\u5927\u529b\u9e70\u722a\u529f\u6293\u5728\u8138 \u4e0a\uff0c\u4e00\u6293\u800c\u6bd9\u547d\u3002\u53e6\u4e00\u4e2a\u5374\u662f\u8eab\u53d7\u4e94\u5904\u638c\u4f24\uff0c\u9aa8\u65ad\u7b4b\u6298\uff0c\u6050\u73c0\u8fde\u809d\u80a0\u4e94\u80f0\u90fd\u88ab\u9707\u5f97\u5bf8\u5bf8\u65ad\u843d \u4e86\uff01 \u3000\u3000\u8fd9\u4ef6\u4e8b\u7acb\u523b\u9707\u60ca\u6b66\u6797\uff0c\u800c\u4e14\u7eb7\u7eb7\u731c\u6d4b\uff0c\u8c01\u662f\u51fb\u6bd9\u593a\u547d\u53cc\u96c4\u7684\u4eba\u7269\u3002 \u3000\u3000\u5929\u4e89\u6559\u66f4\u662f\u51fa\u52a8\u4e86\u7edd\u5927\u7684\u529b\u91cf\uff0c\u51e0\u4e4e\u5c06\u534e\u5c71\u4e0a\u7684\u4e00\u8349\u4e00\u6728\u90fd\u641c\u7d22\u6b86\u5c3d\uff0c\u53ef\u662f\u4ed6\u4eec\u5374\u90a3\u91cc \u627e\u5f97\u51fa\u4eba\u5bb6\u5462\uff1f. \u3000\u3000\u53ea\u662f\u6559\u4e2d\u84dd\u8863\u575b\u4e0b\u4e00\u4e2a\u672c\u85c9\u85c9\u65e0\u540d\u7684\u9999\u4e3b\uff0c\u7adf\u5728\u534e\u5c71\u4e4b\u9634\u53d1\u73b0\u4e86\u4e00\u6761\u79d8\u5f84\uff0c\u7531\u6b64\u79d8\u5f84\u7a7f \u5165\uff0c\u5c45\u7136\u67f3\u6697\u82b1\u660e\uff0c\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5ce1\u8c37\uff0c\u8c37\u91cc\u70df\u706b\u72fc\u85c9\uff0c\u5730\u4e0a\u6ee1\u662f\u70e7\u6b8b\u7684\u6728\u6599\uff0c\u5f77\u4f5b\u50cf\u662f\u672c \u6765\u6b64\u95f4\u6709\u4e2a\u4eba\u5bb6\uff0c\u4f46\u5374\u5728\u6700\u8fd1\u88ab\u4eba\u7eb5\u706b\u6240\u70e7\u3002 \u3000\u3000\u4e8e\u662f\u5f88\u5bb9\u6613\u5730\u5c31\u53ef\u4ee5\u8054\u60f3\u5230\uff0c\u5728\u8fd9\u72ed\u8c37\u4e2d\u672c\u6765\u4e00\u5b9a\u662f\u4f4f\u7740\u4e2a\u907f\u4ec7\u7684\u6b66\u6797\u4eba\u58eb\uff0c\u800c\u4e14\u663e\u7136 \u5730\uff0c\u8fd9\u4eba\u6240\u907f\u7684\u4ec7\u5bb6\u5c31\u662f\u5929\u4e89\u6559\uff0c\u5728\u593a\u547d\u53cc\u96c4\u53d1\u73b0\u6b64\u4eba\u540e\uff0c\u81ea\u7136\u4e0d\u514d\u6709\u4e00\u573a\u6076\u6597\uff0c\u4f46\u4ee5\u638c\u6307 \u548c\u79d8\u6280\u9707\u60ca\u6b66\u6797\u7684\u5bab\u6c0f\u5144\u5f1f\uff0c\u7adf\u4e0d\u662f\u8fd9\u4eba\u7684\u5bf9\u624b\u3002 \u3000\u3000\u800c\u8fd9\u4eba\u5728\u51fb\u6bd9\u5bab\u6c0f\u5144\u5f1f\u4e4b\u540e\uff0c\u4e5f\u81ea\u77e5\u65e0\u6cd5\u518d\u5728\u534e\u5c71\u9690\u8ff9\uff0c\u4e8e\u662f\u4ed6\u81ea\u5df1\u70e7\u6bc1\u4e86\u81ea\u5df1\u7684\u623f \u5b50\uff0c\u800c\u5f00\u59cb\u7b2c\u4e8c\u6b21\u7684\u6f5c\u9003\u3002 \u3000\u3000\u8fd9\u731c\u6d4b\u81ea\u7136\u975e\u5e38\u8fd1\u4e8e\u60c5\u7406\uff0c\u53ea\u662f\u8fd9\u4eba\u4f1a\u662f\u8c01\u5462\uff1f\u7adf\u80fd\u51fb\u6bd9\u593a\u547d\u53cc\u96c4\u3002 \u3000\u3000\u6709\u4eba\u53c8\u731c\u6d4b\u9690\u8ff9\u5728\u534e\u5c71\u907f\u4ec7\u7684\u6050\u6015\u4e0d\u6b62\u4e00\u4eba\uff0c\u53ef\u80fd\u662f\u592b\u5987\uff0c\u53ef\u80fd\u662f\u5e08\u5f92\uff0c\u53ef\u80fd\u662f\u7236\u5b50\uff0c \u53ef\u80fd\u662f\u5144\u5f1f\u2026\u2026. \u3000\u3000\u79cd\u79cd\u731c\u6d4b\uff0c\u4e0d\u4e00\u800c\u8db3\uff0c\u4f46\u662f\u6b66\u6797\u4e2d\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u6b64\u4e8b\u7684\u771f\u8c61\u3002 \u3000\u3000\u5c31\u5728\u5929\u4e89\u6559\u5927\u641c\u534e\u5c71\u7684\u65f6\u5019\uff0c\u5728\u5f80\u957f\u5b89\u7684\u8def\u4e0a\uff0c\u6709\u4e00\u8f86\u5927\u8f66\u75be\u884c\u751a\u6025\uff0c\u5957\u8f66\u7684\u7272\u53e3\u7b4b\u5f3a \u9aa8\u58ee\uff0c\u4f46\u6b64\u523b\u5df2\u7d2f\u5f97\u5634\u89d2\u4e0d\u65ad\u5730\u6d41\u7740\u767d\u6cab\u4e86\uff0c\u663e\u89c1\u5f97\u8fd9\u5339\u7272\u53e3\u5728\u5f88\u77ed\u65f6\u95f4\u4e2d\u8d70\u4e86\u5f88\u591a\u7684\u8def\u3002 \u3000\u3000\u53ef\u662f\u8d76\u8f66\u7684\u8f66\u628a\u5f0f\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u56e0\u4e3a\u81ea\u5df1\u7684\u7272\u53e3\u5403\u4e86\u4e8f\uff0c\u800c\u6709\u4e0d\u60a6\u7684\u8868\u60c5\uff1a\u76f8\u53cd\u5730\uff0c\u4ed6 \u53cd\u800c\u5174\u9ad8\u91c7\u70c8\uff0c\u5f77\u4f5b\u63a5\u4e86\u4e00\u5b97\u5f88\u597d\u7684\u4e70\u5356\u3002 \u3000\u3000\u8fd9\u4e00\u8f86\u5927\u8f66\u56db\u9762\u7684\u8f66\u7a97\u5374\u5173\u5f97\u4e25\u5bc6\u7684\uff0c\u8fd9\u79cd\u666f\u8c61\u5728\u4e25\u51ac\u7684\u65f6\u5019\u5e76\u4e0d\u7279\u6b8a\uff0c\u56e0\u4e3a\u5728\u8def\u4e0a\u6240 \u6709\u8d76\u8def\u7684\u8f66\u5b50\uff0c\u90fd\u662f\u5982\u6b64\u60c5\u5f62\u3002 \u3000\u3000\u53ef\u662f\u5947\u602a\u7684\u5374\u662f\u8fd9\u8f66\u4e0a\u7684\u4eba\uff0c\u5e76\u4e0d\u5728\u901a\u5546\u5927\u9547\u4e0a\u6253\u5c16\u6b47\u606f\uff0c\u665a\u4e0a\u4e5f\u603b\u662f\u5728\u8352\u50fb\u6751\u843d\u7684\u8305 \u5e97\u91cc\u3002 \u3000\u3000\u8f66\u628a\u5f0f\u5fc3\u91cc\u5728\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u8f66\u4e0a\u7684\u4eba\uff0c\u4e0d\u662f\u6c5f\u6e56\u5927\u76d7\u624d\u602a\uff01\u5df4\u8fde\u8fd9\u5973\u7684\uff0c\u90fd\u900f\u7740\u4e9b\u4e0d\u6b63\u7684\u5473\u9053\uff0c\u53d7\u4f24\u7684\u4e24\u4e2a\uff0c \u6050\u6015\u51c6\u662f\u88ab\u5b98\u5e9c\u7684\u516c\u5dee\u780d\u4f24\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7684\u8138\u4e0a\uff0c\u5c31\u9732\u51fa\u4e86\u4e0d\u5b89\u4efd\u7684\u72e1\u7b11\uff0c\u4ed6\u5fc3\u91cc\u8f6c\u7740\u7684\u5ff5\u5934\uff0c\u4e5f\u5c31\u8d8a\u6765\u8d8a\u6ca1\u6709\u4eba\u5473\u513f \u4e86\u3002 \u3000\u3000\u53ea\u662f\u8f66\u4e2d\u7684\u4eba\uff0c\u5374\u4e00\u70b9\u513f\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5927\u8f66\u91cc\u94fa\u7740\u5f88\u539a\u7684\u68c9\u88ab\uff0c\u56e0\u4e3a\u6015\u53d7\u4f24\u7684\u4eba\u5728\u8def\u4e0a\u98a0\u7c38\uff1b\u5728\u8f66\u7684\u4e2d\u95f4\uff0c\u5012\u5367\u7740\u4e24\u4e2a\u4eba\uff0c\u4e00 \u957f\u4e00\u5c11\uff0c\u4e00\u7537\u4e00\u5973\u3002 \u3000\u3000\u8f66\u7684\u89d2\u843d\u91cc\u76d8\u819d\u5750\u7740\u4e00\u4e2a\u4e09\u5341\u56db\uff0c\u4e94\u7684\u5c11\u5987\uff0c\u9edb\u7709\u6df1\u9501\uff0c\u59ff\u5bb9\u7edd\u7f8e\u3002\u5979\u7684\u5e74\u7eaa\uff0c\u975e\u4f46\u6ca1 \u6709\u5e26\u7ed9\u5979\u534a\u4e1d\u8001\u6001\uff0c\u800c\u4e14\u5e26\u7ed9\u5979\u4e00\u79cd\u6210\u719f\u7684\u98ce\u81f4\uff0c\u4f7f\u5979\u770b\u8d77\u6765\uff0c\u66f4\u4ee4\u4eba\u4e3a\u4e4b\u610f\u52a8\uff01 \u3000\u3000\u8fd9\u62ab\u661f\u6234\u6708\uff0c\u6512\u7a0b\u6025\u884c\u7684\u4e09\u4eba\uff0c\u4e0d\u95f4\u53ef\u77e5\uff0c\u4fbf\u662f\u4e09\u6e58\u5927\u4fa0\u7684\u672a\u4ea1\u4eba\u2014\u2014\u5b59\u654f\uff0c\u51cc\u7433\u6bcd \u5973\uff0c\u548c\u9690\u8ff9\u6f5c\u8e2a\uff0c\u6613\u540d\u6362\u59d3\u7684\u4f0a\u98ce\u3002 \u3000\u3000\u6101\u5bb9\u6ee1\u9762\u7684\u5b59\u654f\uff0c\u6b64\u65f6\u5fc3\u4e2d\u7d0a\u4e71\u5df2\u6781\uff01\u5728\u5979\u9762\u524d\uff0c\u6709\u53d7\u7740\u91cd\u4f24\u7684\u4e24\u4eba\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u5979 \u7684\u72ec\u751f\u7231\u5973\uff0c\u4e00\u4e2a\u5374\u662f\u4e3a\u4e86\u6551\u5979\u800c\u8eab\u53d7\u91cd\u4f24\u7684\u964c\u751f\u4eba\u3002 \u3000\u3000\u6b64\u523b\u5979\u77e5\u9053\u81ea\u5df1\u5728\u5192\u7740\u751f\u547d\u7684\u5371\u9669\uff0c\u56e0\u4e3a\u5979\u7684\u884c\u8e2a\uff0c\u53ea\u8981\u88ab\u4efb\u4f55\u4e00\u4e2a\u5929\u4e89\u6559\u5f92\u77e5\u9053\uff0c\u4fbf \u662f\u4e0d\u5f97\u4e86\uff01 \u3000\u3000\u4f55\u51b5\uff0c\u5979\u8fd8\u8981\u5e26\u7740\u8fd9\u4e24\u4e2a\u91cd\u4f24\u7684\u4eba\uff0c\u524d\u9014\u832b\u832b\uff0c\u8fde\u4e00\u4e2a\u6295\u5954\u7684\u5730\u65b9\u90fd\u6ca1\u6709\uff01 \u3000\u3000\u5979\u867d\u7136\u8eab\u6000\u7edd\u6280\uff0c\u4f46\u5f3a\u715e\u4e5f\u53ea\u662f\u4e00\u4e2a\u5973\u5b50\u3002\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u600e\u4e48\u4f1a\u4e0d\u6df1\u9501\u9edb\u7709\uff0c\u5b50\u80a0\u5343 \u8f6c\uff0c\u62ff\u4e0d\u5b9a\u4e00\u4e2a\u4e3b\u610f\u5462\uff1f \u3000\u3000\u5979\u671b\u4e86\u8eba\u5728\u5979\u9762\u524d\u7684\u964c\u751f\u4eba\u4e00\u773c\uff0c\u60f3\u8d77\u5f53\u65f6\u7684\u60c5\u666f\uff0c\u7684\u786e\u662f\u4e5d\u6b7b\u4e00\u751f\uff0c\u593a\u547d\u53cc\u90a3\u4e24\u5f20\u72f0 \u72de\u7684\u9762\u5b54\uff0c\u5728\u5979\u8111\u6d77\u4e2d\u4ecd\u7136\u62ed\u62b9\u4e0d\u53bb\u3002 \u3000\u3000\u5979\u60f3\u5979\u7684\u7231\u5973\u51cc\u7433\uff0c\u867d\u7136\u6b66\u529f\u4ea6\u5f97\u6709\u771f\u4f20\uff0c\u4f46\u5e74\u7eaa\u592a\u8f7b\uff0c\u53c8\u6beb\u65e0\u4e34\u654c\u7ecf\u9a8c\uff0c\u7adf\u5728\u593a\u547d\u53cc \u96c4\u4e00\u6b65\u6b65\u903c\u8fd1\u5979\u4eec\u65f6\uff0c\u8d2a\u529f\u5984\u8fdb\uff0c\u4ee5\u81f4\u524d\u80f8\u88ab\u8fd9\u5bab\u6c0f\u5144\u5f1f\u7684\u6307\u98ce\u6240\u626b\uff0c\u5728\u8fd9\u5144\u5f1f\u4e24\u4eba\u82e6\u7ec3\u591a \u5e74\u7684\u201c\u9634\u98ce\u6307\u201d\u4e0b\uff0c\u53d7\u4e86\u6781\u91cd\u7684\u4f24\u3002 \u3000\u3000\u60f3\u5230\u90a3\u65f6\uff0c\u5979\u4ecd\u4e0d\u7981\u5168\u8eab\u8d77\u4e86\u4e00\u9635\u609a\u6817\u3002 \u3000\u3000\u201c\u771f\u662f\u751f\u6b7b\u5173\u5934\uff01\u8981\u4e0d\u662f\u8fd9\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u611f\u6fc0\u5730\u671b\u4e86\u4f0a\u98ce\u4e00\u773c\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8981\u4e0d\u662f\u4ed6\uff0c\u6050\u6015\u6211\u4e5f\u8981\u4f24\u5728\u8fd9\u4e24\u4e2a\u715e\u661f\u7684\u638c\u4e0b\uff0c\u73b0\u5728\u6211\u5c31\u662f\u4e3a\u4e86\u8981\u770b\u62a4\u4ed6\u800c\u591a\u53d7\u4e9b \u82e6\uff0c\u4f46\u6bd4\u8d77\u4ed6\u4e3a\u6211\u7af9\u6240\u505a\u7684\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\u5462\uff1f\u201d\u539f\u6765\uff0c\u4f0a\u98ce\u542c\u5230\u7684\u90a3\u4e00\u58f0\u60e8\u547c\uff0c\u6b63\u662f\u51cc\u7433 \u7eb5\u8eab\u4e00\u63a0\uff0c\u4ee5\u201c\u9965\u9e70\u640f\u5154\u201d\u4e4b\u5f0f\uff0c\u6251\u5411\u6b65\u6b65\u8fdb\u903c\u7684\u593a\u547d\u53cc\u96c4\u800c\u53d7\u4f24\u65f6\u6240\u53d1\u51fa\u7684\u3002 \u3000\u3000\u201c\u9965\u9e70\u640f\u5154\u201d\u867d\u662f\u9887\u5177\u5a01\u529b\u7684\u4e00\u62db\uff0c\u4f46\u4ee5\u540d\u987e\u4e4b\uff0c\u8fd9\u4e00\u62db\u5927\u591a\u7528\u4ee5\u5bf9\u5fd6\u6b66\u529f\u7a0d\u5f31\u4e8e\u81ea\u5df1 \u7684\u5bf9\u624b\u3002\u51cc\u7433\u5c11\u4e0d\u66f4\u4e8b\uff0c\u7adf\u4ee5\u8fd9\u4e00\u62db\u7528\u5728\u6210\u540d\u6b66\u6797\u591a\u5e74\u7684\u201c\u593a\u547d\u53cc\u96c4\u201d\u5bab\u6c0f\u5144\u5f1f\u8eab\u4e0a\uff0c\u6b63\u662f \u72af\u4e86\u6b66\u5bb6\u5927\u5fcc\uff01 \u3000\u3000\u5bab\u6c0f\u5144\u5f1f\u51b7\u7b11\u4e00\u58f0\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u56db\u6761\u957f\u81c2\u4e00\u9f50\u4f38\u51fa\u3002\u5bab\u7533\u7684\u5de6\u638c\u548c\u5bab\u9149\u7684\u53f3\u638c\uff0c\u7830\u7136\u4e00 \u58f0\uff0c\u786c\u63a5\u4e86\u51cc\u7433\u7684\u5168\u529b\u4e00\u653b\u3002 \u3000\u3000\u4f46\u662f\u5bab\u7533\u7684\u53f3\u638c\u548c\u5bab\u9149\u4e00\u7684\u5de6\u638c\uff0c\u5374\u5404\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u500f\u7136\u51fb\u51fa\uff0c\u867d\u672b\u6253\u5b9e\uff0c\u4f46\u4ed6\u4eec\u6240\u53d1 \u51fa\u7684\u6307\u98ce\uff0c\u5df2\u4f7f\u5f97\u51cc\u7433\u9707\u98de\u6570\u5c3a\u4e4b\u5916\u3002 \u3000\u3000\u5b59\u654f\u6025\u6012\u653b\u5fc3\uff0c\u5a07\u53f1\u4e00\u58f0\uff0c\u4fbf\u548c\u8fce\u4e0a\u6765\u7684\u5bab\u6c0f\u53cc\u51f6\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u8fd9\u4e5f\u5c31\u662f\u4f0a\u98ce\u56de\u5934\u7684\u90a3\u4e00\u5239\u90a3\u3002 \u3000\u3000\u201c\u89c1\u6b7b\u4e0d\u6551\u201d\uff0c\u4f0a\u98ce\u662f\u7edd\u5bf9\u4e0d\u4f1a\u505a\u51fa\u7684\uff0c\u7eb5\u7136\u4ed6\u660e\u77e5\u4e00\u52a8\u624b\uff0c\u4fbf\u4f1a\u5e26\u7ed9\u4ed6\u5f88\u5927\u7684\u9ebb\u70e6\uff0c \u4f46\u662f\uff0c\u4ed6\u5374\u5df2\u522b\u65e0\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5389\u559d\u4e00\u58f0\uff0c\u4e00\u63a0\u800c\u524d\uff0c\u53cc\u638c\u62cd\u51fa\uff0c\u653b\u5411\u5bab\u9149\u7684\u5de6\u80c1\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u52a8\u624b\uff0c\u548c\u5728\u4fdd\u5b9a\u57ce\u5916\u72ec\u6597\u6731\u7802\u638c\u65f6\u53c8\u5927\u4e0d\u76f8\u540c\u3002\u987b\u77e5\u4ed6\u90a3\u65f6\u662f\u60f3\u5229\u7528\u5c24\u5927\u541b\u5b8c\u6210 \u4ed6\u7684\u8ba1\u5212\uff0c\u800c\u6b64\u523b\uff0c\u4ed6\u5374\u662f\u7acb\u5fc3\u5c06\u8fd9\u4e24\u4eba\u6bd9\u4e8e\u638c\u4e0b\u3002 \u3000\u3000\u662f\u4ee5\u4e00\u4e0a\u624b\uff0c\u4ed6\u4fbf\u662f\u62db\u62db\u6740\u7740\u3002 \u3000\u3000\u5bab\u6c0f\u5144\u5f1f\u5389\u559d\u8fde\u8fde\uff0c\u7a81\u5730\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u597d\u4fca\u7684\u8eab\u624b\uff01\u600e\u5730\u5374\u548c\u6211\u5144\u5f1f\u52a8\u8d77\u624b\u6765\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u95f7\u58f0\u4e0d\u54cd\u3002 \u3000\u3000\u5bab\u6c0f\u5144\u5f1f\u53c8\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u770b\u670b\u53cb\u7684\u8eab\u624b\uff0c\u5012\u5f88\u50cf\u662f\u548c\u6b7b\u53bb\u7684\u4e00\u4e2a\u670b\u53cb\u4e00\u6837\uff0c\u60f3\u6765\u9601\u4e0b\u4e5f\u662f\u6b7b\u4e86\u4e00\u6b21\uff0c\u518d\u6d3b\u56de\u6765 \u7684\u5427\uff1f\u201d \u3000\u3000\u4ed6\u6b64\u8a00\u4e00\u51fa\uff0c\u4f0a\u98ce\u7acb\u65f6\u9762\u8272\u5927\u53d8\uff0c\u4ed6\u679c\u7136\u7792\u4e0d\u8fc7\u8fd9\u5978\u72e1\u51f6\u987d\u7684\u201c\u593a\u547d\u53cc\u96c4\u201d\u5bab\u6c0f\u5144\u5f1f\u3002 \u3000\u3000\u987b\u77e5\u4efb\u4f55\u4e8b\u90fd\u53ef\u4ee5\u4f2a\u88c5\uff0c\u4f46\u662f\uff0c\u4e00\u4e2a\u6b66\u6797\u9ad8\u624b\u5728\u62da\u547d\u8fc7\u62db\u65f6\uff0c\u4ed6\u7684\u8eab\u6cd5\uff0c\u5374\u4e07\u4e07\u7792\u4e0d\u8fc7 \u660e\u773c\u4eba\u7684\u3002 \u3000\u3000\u4e0d\u51fa\u4ed6\u5148\u524d\u6240\u6599\uff0c\u5bab\u6c0f\u5144\u5f1f\u7684\u6740\u62db\uff0c\u679c\u7136\u5927\u591a\u62db\u547c\u5230\u4ed6\u8eab\u4e0a\u6765\u3002 \u3000\u3000\u201c\u670b\u53cb\uff01\u4eca\u5929\u4f60\u5c31\u518d\u6b7b\u4e00\u6b21\u5427\uff01\u201d\u4ed6\u4eec\u5389\u58f0\u559d\u7740\u3002 \u3000\u3000\u8fd9\u593a\u547d\u53cc\u96c4\u7684\u6b66\u529f\uff0c\u81ea\u6210\u4e00\u5bb6\uff0c\u7adf\u5728\u4f0a\u98ce\u66fe\u7ecf\u5bf9\u654c\u7684\u8bb8\u591a\u201c\u5929\u4e89\u6559\u201d\u4e0b\u7684\u91d1\u8863\u9999\u4e3b\u4e4b \u4e0a\u3002 \u3000\u3000\u800c\u4e14\uff0c\u6700\u4ee4\u4ed6\u4e0d\u89e3\u7684\u662f\uff1a\u8fd9\u4e09\u6e58\u5927\u4fa0\u672a\u4ea1\u4eba\u7684\u6b66\u529f\uff0c\u7adf\u4e0d\u5982\u5979\u5df2\u7ecf\u53d7\u4f24\u7684\u5973\u513f\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u5b59\u654f\u7684\u6b66\u529f\uff0c\u53ea\u662f\u5ac1\u7ed9\u51cc\u5317\u4fee\u4e4b\u540e\u624d\u5b66\u6210\u76841\u81ea\u7136\u4e0d\u53ca\u81ea\u5e7c\u5373\u6253\u4e0b\u4e86\u6781\u957f\u597d\u6839 \u57fa\u7684\u51cc\u7433\u3002 \u3000\u3000\u6b64\u523b\u4ea4\u624b\u4e4b\u4e0b\uff0c\u4f0a\u98ce\u627f\u53d7\u4e86\u5927\u90e8\u4efd\u538b\u529b\uff0c\u867d\u7136\u4e0d\u81f4\u843d\u8d25\uff0c\u8981\u53d6\u80dc\u5374\u4e5f\u4e0d\u6613\uff01 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u81ea\u5df1\u77e5\u9053\uff0c\u4eca\u65e5\u4e00\u6218\uff0c\u9664\u975e\u5c06\u8fd9\u5bab\u6c0f\u5144\u5f1f\u5168\u6bd9\u5728\u638c\u4e0b\uff0c\u5426\u5219\u81ea\u5df1\u65e5\u540e\u6c38\u65e0\u5b81 \u65e5\uff0c\u56e0\u4e3a\u4eba\u5bb6\u5df2\u8bc6\u7834\u4e86\u81ea\u5df1\u7684\u771f\u76f8\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u51fa\u62db\u4e0d\u4f46\u62db\u62db\u81f4\u547d\uff0c\u800c\u4e14\u6709\u65f6\u7adf\u662f\u62da\u4e86\u81ea\u5df1\u4e5f\u4e2d\u4e0a\u4e00\u638c\u7684\u8def\u6570\u3002 \u3000\u3000\u5b59\u654f\u5927\u4e3a\u611f\u52a8\uff0c\u53d7\u4e86\u4ed6\u7684\u5f71\u54cd\uff0c\u4e5f\u62da\u8d77\u547d\u6765\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5bab\u6c0f\u5144\u5f1f\u53ef\u6ca1\u6709\u62da\u547d\u7684\u5fc5\u8981\u3002\u89c1\u4e86\u4ed6\u4eec\u8fd9\u79cd\u6253\u6cd5\uff0c\u5fc3\u91cc\u4e0d\u7981\u5403\u60ca\uff0c\u4f46\u662f\u81ea\u5bb6\u5374\u88ab \u903c\u5f97\u8fde\u4eae\u51fa\u8170\u7554\u5175\u5203\u7684\u65f6\u95f4\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u56db\u4eba\u7247\u523b\u4e4b\u95f4\u5df2\u62c6\u4e86\u6570\u5341\u62db\u3002 \u3000\u3000\u5bab\u6c0f\u5144\u5f1f\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5ffd\u5730\u9f50\u58f0\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u6323\u547d\u4e5f\u6ca1\u6709\u7528\u3002\u4e0d\u51fa\u7247\u523b\uff0c\u91d1\u8863\u575b\u91cc\u7684\u53e6\u5916\u4e09\u4e2a\u9999\u4e3b\u4e5f\u8981\u6765\u4e86\u3002\u670b\u53cb\uff01\u662f\u8bc6\u76f8 \u7684\uff0c\u8fd8\u662f\u8ba4\u547d\u4e86\u5427\uff01\u514d\u5f97\u7b49\u4f1a\u518d\u591a\u5403\u82e6\u3002\u201d \u3000\u3000\u6b64\u8bdd\u679c\u7136\u4f7f\u5f97\u5b59\u654f\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u4f0a\u98ce\u8d70\u5357\u95ef\u5317\uff0c\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u6839\u672c\u5c06\u4ed6\u4eec\u7684\u8bdd\u6ca1\u6709\u653e\u5728 \u5fc3\u4e0a\u3002\u638c\u98ce\u864e\u864e\uff0c\u51fa\u62db\u66f4\u89c1\u51cc\u5389\u3002 \u3000\u3000\u53cc\u96c4\u7709\u5934\u5fae\u76b1\uff0c\u76ee\u6807\u81ea\u7136\u8f6c\u5230\u5b59\u654f\u8eab\u4e0a\uff0c\u9f50\u58f0\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u51cc\u592b\u4eba\uff01\u6211\u4eec\u5144\u5f1f\u662f\u5148\u541b\u5b50\u540e\u5c0f\u4eba\uff0c\u6b79\u8bdd\u5148\u8bf4\u5728\u524d\u9762\u3002\u592b\u4eba\u6b64\u523b\u82e5\u4e0d\u8ddf\u7740\u6211\u4eec\u8d70\uff0c\u7b49 \u4f1a\u90a3\u4e09\u4f4d\u6765\u4e86\uff0c\u53ef\u6bd4\u4e0d\u4e0a\u6211\u5144\u5f1f\u597d\u8bf4\u8bdd\u5462\uff01\u201d \u3000\u3000\u4ed6\u4eec\u96be\u542c\u6781\u4e4b\u5730\u7b11\u4e86\u4e00\u9635\uff0c\u53c8\u5e26\u7740\u66f4\u523a\u8033\u7684\u58f0\u97f3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e09\u4f4d\u9999\u4e3b\u522b\u7684\u4e0d\u8bf4\uff0c\u53ef\u6709\u70b9\u2026\u2026\u201d \u3000\u3000\u4ed6\u4eec\u6545\u610f\u987f\u4f4f\u8bdd\uff0c\u4e0d\u6000\u597d\u610f\u5730\u201c\u563b\u563b\u201d\u7b11\u4e86\u4e24\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4e09\u4f4d\u770b\u89c1\u592b\u4eba\u8fd9\u822c\u7f8e\u4eba\u513f\uff0c\u53ef\u5305\u4e0d\u51c6\u8981\u51fa\u4ec0\u4e48\u4e8b\u5462\uff01\u201d \u3000\u3000\u8fd9\u79cd\u9887\u4e3a\u9732\u9aa8\u7684\u8bdd\uff0c\u7acb\u523b\u4f7f\u5f97\u5b59\u654f\u7ea2\u751f\u53cc\u988a\uff0c\u52a8\u624b\u53d1\u62db\u95f4\uff0c\u679c\u7136\u56e0\u4e3a\u7f9e\u6012\u800c\u663e\u5f97\u6ca1\u6709\u5148 \u524d\u7684\u51cc\u5389\u3002 \u3000\u3000\u8fd9\u79cd\u60c5\u5f62\uff0c\u88ab\u4f0a\u98ce\u770b\u5728\u773c\u91cc\uff0c\u5389\u559d\u9053\uff1a \u3000\u3000\u201c\u59d3\u5bab\u7684\uff01\u5c11\u7ed9\u201c\u5929\u4e89\u6559\u201d\u73b0\u773c\u5427\uff01\u7528\u8fd9\u79cd\u6c5f\u6e56\u4e0b\u4e09\u95e8\u7684\u6280\u4fe9\uff0c\u8fd8\u5728\u6b66\u6797\u4e2d\u9053\u4ec0\u4e48\u5b57 \u53f7.\uff1f\u201d \u3000\u3000\u5bab\u6c0f\u53cc\u96c4\u5de6\u53f3\u53cc\u638c\u540c\u65f6\u6325\u51fa\uff0c\u5728\u4e2d\u9014\u500f\u7136\u53d8\u4e86\u4e2a\u65b9\u5411\uff0c\u731b\u51fb\u4f0a\u98ce\u7684\u524d\u80f8\u548c\u5b59\u654f\u7684\u5de6\u80a9\u3002 \u3000\u3000\u8fd9\u5144\u5f1f\u4e24\u4eba\u8054\u624b\u653b\u654c\uff0c\u914d\u5408\u4e4b\u4f73\uff0c\u5999\u5230\u6beb\u98a0\uff01\u4f7f\u4e24\u4eba\u672c\u5df2\u4e0d\u51e1\u7684\u6b66\u529b\uff0c\u4f55\u6b62\u52a0\u4e86\u4e00\u500d\uff01 \u3000\u3000\u4ed6\u4eec\u51b7\u7b11\u7740\u6545\u610f\u6ee1\u6000\u8f7b\u8511\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u5c31\u5c11\u7ba1\u7ba1\u95f2\u4e8b\u5427\uff01\u8fde\u81ea\u5df1\u7684\u592a\u592a\u90fd\u7ba1\u4e0d\u4e86\uff0c\u8fd8\u5728\u8fd9\u91cc\u88c5\u4ec0\u4e48\u4f6f\uff0c\u53d1\u4ec0\u4e48 \u5a01.\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u679c\u7136\u4f7f\u5f97\u4f0a\u98ce\u4e5f\u6c14\u5f97\u5931\u53bb\u4e86\u5e38\u6001\u3002\u811a\u6b65\u4e00\u9519\uff0c\u907f\u5f00\u5bab\u6c0f\u53cc\u96c4\u7684\u4e00\u62db\uff0c\u53cc\u638c\u518d\u6b21\u4ea4\u9519 \u62cd\u51fa\u65f6\uff0c\u7adf\u53d1\u51fa\u4e86\u5341\u6210\u529f\u529b\u3002 \u3000\u3000\u8fd9\u79cd\u4e0d\u7559\u9000\u6b65\u7684\u6253\u6cd5\uff0c\u4e5f\u662f\u72af\u4e86\u6b66\u5bb6\u7684\u5927\u5fcc\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u79cd\u60ca\u4eba\u7684\u638c\u529b\uff0c\u5374\u4f7f\u5f97\u5bab\u6c0f\u53cc\u96c4\u8138\u4e0a\u867d\u4ecd\u5e26\u7740\u51b7\u7b11\uff0c\u5fc3\u4e2d\u5df2\u6709\u602f\u654c\u4e4b\u610f\u3002 \u3000\u3000\u53c8\u662f\u5341\u6765\u4e2a\u7167\u9762\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u591c\u8272\u6108\u6d53\uff0c\u56db\u4eba\u7684\u638c\u98ce\u5c06\u8fd9\u5c71\u4fa7\u7684\u679d\u6728\uff0c\u51fb\u5f97\u679d\u679d\u65ad\u843d\u3002 \u3000\u3000\u5bd2\u98ce\u51db\u6d0c\uff0c\u8fd9\u56db\u4eba\u7684\u989d\u4e0a\uff0c\u90fd\u5df2\u5fae\u5fae\u6e17\u51fa\u6c57\u73e0\u6765\u3002 \u3000\u3000\u5bab\u6c0f\u53cc\u96c4\u8eab\u5f62\u5404\u8f6c\u534a\u5708\uff0c\u907f\u5f00\u4f0a\u98ce\u7684\u4e00\u638c\uff0c\u4ed6\u4eec\u7684\u201c\u9634\u98ce\u6307\u201d\u529b\uff0c\u7adf\u4e0d\u6562\u548c\u4f0a\u98ce\u90a3\u79cd\u5f00 \u5c71\u88c2\u77f3\u7684\u638c\u529b\u786c\u62da\u3002 \u3000\u3000\u5c31\u4ed6\u4eec\u4e24\u9762\u76f8\u63a5\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4e24\u4eba\u53c8\u5404\u81ea\u4ea4\u6362\u4e86\u4e00\u4e2a\u542b\u6709\u6df1\u610f\u7684\u76ee\u5149\u3002 \u3000\u3000\u8fd9\u5144\u5f1f\u4e24\u4eba\uff0c\u81ea\u5e7c\u5fc3\u610f\u76f8\u901a\uff0c\u8fde\u8bf4\u8bdd\u90fd\u50cf\u662f\u4e00\u4e2a\u6a21\u5b50\u91cc\u94f8\u51fa\u4f3c\u7684\u3002\u6b64\u523b\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c \u7684\uff0c\u5374\u6709\u4e86\u201c\u626f\u6d3b\u201d\u7684\u5ff5\u5934\u3002 \u3000\u3000\u201c\u53cd\u6b63\u4ed6\u4eec\u7684\u843d\u811a\u4e4b\u5904\u548c\u865a\u5b9e\uff0c\u5df2\u7ecf\u88ab\u6211\u4eec\u63a2\u5f97\uff0c\u6211\u4eec\u53c8\u4f55\u82e6\u8981\u5728\u8fd9\u91cc\u548c\u4ed6\u4eec\u62da\u547d\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u5634\u89d2\u90fd\u6302\u7740\u4e00\u4e1d\u72de\u7b11\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u8fd8\u80fd\u5728\u6211\u4eec\u5929\u4e89\u6559\u7684\u624b\u4e0b\uff0c\u518d\u9003\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u957f\u5578\u4e00\u58f0\uff0c\u638c\u5f71\u7a81\u7136\u5982\u843d\u53f6\u822c\u843d\u5728\u6b66\u529f\u8f83\u5f31\u7684\u5b59\u654f\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u8f6c\u53d8\uff0c\u4f7f\u5f97\u4f0a\u98ce\u9664\u4e86\u653b\u654c\u4e4b\u5916\uff0c\u8fd8\u5f97\u7559\u610f\u5b59\u654f\u7684\u5b89\u5168\u3002 \u3000\u3000\u5578\u58f0\u518d\u8d77\uff0c\u593a\u547d\u53cc\u96c4\u5728\u5168\u529b\u653b\u51fa\u4e00\u638c\u540e\uff0c\u7a81\u5730\u4e00\u98de\u8eab\uff0c\u8eab\u5f62\u5012\u63a0\u51fa\u53bb\u3002 \u3000\u3000\u201c\u5931\u966a\u4e86\uff01\u201d\u4ed6\u4eec\u51b7\u559d\u9053\u3002\u4e24\u4eba\u53c8\u9000\u5728\u5de8\u77f3\u4e4b\u4fa7\u3002 \u3000\u3000\u4f0a\u98ce\u600e\u80af\u8ba9\u4ed6\u4eec\u5c31\u6b64\u4e00\u8d70\uff0c\u5982\u5f71\u9644\u5f62\u822c\uff0c\u4e5f\u63a0\u4e86\u8fc7\u53bb\uff0c\u638c\u82b1\u9519\u843d\uff0c\u51fb\u5411\u5bab\u7533\u80cc\u540e\u7684\u201c\u7075 \u53f0\u201d\uff0c\u201c\u4e92\u6c64\u201d\uff0c\u201c\u7b4b\u7f29\u201d\u7b49\u4e09\u4e2a\u5927\u7a74\u3002 \u3000\u3000\u5bab\u7533\u731b\u4e00\u584c\u8170\uff0c\u4e0a\u8eab\u5fae\u5fae\u524d\u4f38\uff0c\u53f3\u8db3\u5374\u5411\u540e\u5012\u8e22\u51fa\u53bb\uff0c \u3000\u3000\u8fd9\u4e00\u62db\u4ee5\u653b\u4e3a\u5b88\uff0c\u5374\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u4e4b\u5904\uff0c\u786e\u662f\u5999\u7740\u3002 \u3000\u3000\u90a3\u77e5\u4f0a\u98ce\u6b64\u523b\u5df2\u6a2a\u4e86\u5fc3\uff0c\u5fae\u5fae\u4e00\u8ba9\uff0c\u7adf\u62da\u7740\u81ea\u5df1\u53d7\u4f24\uff0c\u53cc\u638c\u8fde\u73af\u4e09\u638c\uff0c\u90fd\u7740\u7740\u5b9e\u5b9e\u5730\u51fb \u5728\u5bab\u7533\u7684\u80cc\u4e0a\uff0c\u81ea\u5df1\u4e0b\u809a\u7684\u5de6\u4fa7\uff0c\u4e5f\u4e2d\u4e86\u4e00\u811a\u3002 \u3000\u3000\u5bab\u7533\u60e8\u547c\u4e00\u58f0\uff0c\u8f6c\u8fc7\u8eab\u540e\uff0c\u5c3d\u4e86\u6700\u540e\u4e4b\u529b\uff0c\u53c8\u53d1\u51fa\u4e00\u638c\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u638c\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u4f0a\u98ce\u53cc\u81c2\u4e00\u683c\uff0c\u53cc\u638c\u4e00\u7ffb\uff0c\u638c\u5c16\u521a\u521a\u642d\u4e0a\u5bab\u7533\u7684\u524d\u80f8\uff0c\u731b\u5730\u5410 \u6c14\u5f00\u58f0\uff0c\u7adf\u4ee5\u5185\u5bb6\u201c\u5c0f\u5929\u661f\u201d\u7684\u638c\u529b\uff0c\u51fb\u5728\u5bab\u7533\u524d\u80f8\u3002\u5bab\u7533\u518d\u6b21\u60e8\u547c\uff0c\u4e00\u53e3\u9c9c\u8840\uff0c\u7adf\u55b7\u5728\u4f0a \u98ce\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u8fb9\u5bab\u9149\u5df2\u5c06\u5b59\u654f\u903c\u5f97\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u4f46\u662f\u5bab\u7533\u8fd9\u4e24\u58f0\u60e8\u547c\uff0c\u5374\u4f7f\u5f97\u4ed6\u5fc3\u80c6\u4ff1\u88c2\uff01\u60e8\u5389\u5730\u957f\u5578\u4e00\u58f0\uff0c\u6251\u5411\u4f0a\u98ce\u3002 \u3000\u3000\u4f0a\u98ce\u4e0b\u809a\u4e2d\u4e86\u4e00\u811a\uff0c\u867d\u7136\u907f\u8fc7\u8981\u5bb3\uff0c\u4f46\u53d7\u4f24\u5df2\u81ea\u4e0d\u8f7b\uff01 \u3000\u3000\u65b9\u81ea\u5598\u606f\u95f4\uff0c\u5bab\u9149\u7684\u8eab\u5f62\u5df2\u5feb\u5982\u95ea\u7535\u822c\uff0c\u63a0\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u5144\u5f1f\u8fde\u5fc3\uff0c\u5bab\u7533\u6bd9\u547d\uff0c\u5bab\u9149\u6b64\u523b\u7528\u7684\u4e5f\u662f\u62da\u547d\u7684\u62db\u6570\u3002 \u3000\u3000\u4ed6\u4eba\u5c1a\u672a\u5230\uff0c\u53cc\u638c\u5148\u5df2\u6bd5\u76f4\u4f38\u51fa\uff0c\u5341\u6307\u7b95\u5f20\uff0c\u731b\u6293\u5411\u4f0a\u98ce\u80f8\u524d\u7684\u201c\u4e73\u6cc9\u201d\uff0c\u201c\u671f\u95e8\u201d\uff0c \u201c\u5c06\u53f0\u201d\uff0c\u201c\u7075\u6839\u201d\u7b49\u51e0\u5904\u5927\u7a74\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u52bf\u5982\u538b\u9876\u4e4b\u6cf0\u5c71\uff0c\u4f0a\u98ce\u65e0\u6cd5\u786c\u63a5\uff0c\u4f46\u6b64\u523b\u4ed6\u4e0b\u90e8\u53d7\u4f24\uff0c\u8f6c\u4fa7\u5df2\u4e0d\u7075\u4fbf\uff0c\u53ea\u5f97\u5f80\u4e0b \u4e00\u584c\u8170\uff0c\u8ba9\u5bab\u9149\u7684\u53cc\u6293\u4ece\u80a9\u4e0b\u9012\u7a7a\uff0c\u81ea\u5bb6\u5de6\u638c\u5e73\u4f38\uff0c\u53f3\u638c\u5374\u81ea\u4e0b\u800c\u4e0a\uff0c\u5288\u5411\u5bab\u9149\u7684\u9762\u95e8\u3002 \u3000\u3000\u90a3\u77e5\u5bab\u9149\u6b64\u523b\u4e5f\u662f\u5fc3\u5b58\u62da\u547d\uff0c\u5bf9\u8fd9\u81f4\u547d\u7684\u4e24\u62db\uff0c\u4ea6\u662f\u4e0d\u907f\u4e0d\u95ea\uff0c\u53cc\u6293\u5fae\u5fae\u4e00\u6c89\uff0c\u500f\u7136\u4e0b \u6293\u4f0a\u98ce\u7684\u5de6\u53f3\u4e24\u8fb9\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u4f0a\u98ce\u5927\u5413\u4e4b\u4e0b\uff0c\u8eab\u8eaf\u731b\u5730\u4e00\u8f6c\uff0c\u4f46\u53f3\u80a9\u4e0a\u5df2\u4e2d\u4e86\u5bab\u9149\u5feb\u5982\u95ea\u7535\u7684\u4e00\u6293\uff0c\u5728\u4ed6\u5c1a\u672a\u56e0\u75db\u800c \u6655\u7edd\u7684\u8fd9\u4e00\u5239\u90a3\uff0c\u4ed6\u5de6\u638c\u81ea\u5bab\u9149\u53cc\u81c2\u4e2d\u7a7f\u51fa\uff0c\u6293\u5728\u5bab\u9149\u8138\u4e0a\uff0c\u98df\u6307\u53ca\u65e0\u540d\u6307\uff0c\u7adf\u6df1\u6df1\u9677\u4e2d\u5bab \u9149\u7684\u53cc\u76ee\uff0c\u4e94\u6307\u5e76\u529b\u4e00\u6293\uff0c\u593a\u547d\u53cc\u96c4\u4e2d\u7684\u5bab\u9149\uff0c\u5c31\u4f24\u5728\u4ed6\u9f13\u7740\u6700\u540e\u4e00\u4e1d\u771f\u6c14\u4f7f\u51fa\u7684\u201c\u5927\u529b\u9e70 \u722a\u795e\u529f\u201d\u4e4b\u4e0b\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5462\uff1f\u8eab\u53d7\u4e24\u5904\u91cd\u4f24\uff0c\u671b\u7740\u5782\u6b7b\u5bab\u9149\u60e8\u7b11\u4e86\u4e00\u58f0\uff0c\u4fbf\u81ea\u6655\u7edd\uff01 \u3000\u3000\u5b59\u654f\u63a0\u8fc7\u6765\u65f6\uff0c\u8fd9\u9707\u60ca\u6b66\u6797\u7684\u593a\u547d\u53cc\u96c4\uff0c\u4e0d\u4f46\u5728\u540c\u5e74\u800c\u751f\uff0c\u7adf\u4e5f\u5728\u540c\u65f6\u800c\u6b7b\uff01\u4ed6\u4eec\u6b7b\u72b6 \u81f3\u60e8\u7684\u4e24\u5177\u5c38\u8eab\uff0c\u5012\u5367\u5728\u4f0a\u98ce\u7684\u5de6\u53f3\u4e24\u4fa7\u3002 \u3000\u3000\u4f0a\u98ce\u4ea6\u5df2\u5168\u8eab\u6d74\u8840\uff0c\u53f3\u638c\u4f9d\u7136\u6293\u5728\u5bab\u9149\u7684\u5de6\u638c\u4e0a\uff0c\u8138\u4e0a\u6beb\u65e0\u4e00\u4e1d\u8840\u8272\uff0c\u7259\u5173\u7d27\u54ac\u7740\uff0c\u4f46 \u5634\u89d2\u5374\u7559\u7740\u4e00\u4e1d\u5b89\u6170\u7684\u5fae\u7b11\u3002 \u3000\u3000\u5b59\u654f\u4e00\u751f\u4e2d\u4e0d\u77e5\u66fe\u89c1\u8fc7\u591a\u5c11\u60e8\u70c8\u7684\u573a\u9762\uff0c\u4f46\u6b64\u60c5\u6b64\u666f\uff0c\u5374\u4ecd\u4f7f\u5f97\u5979\u89c9\u5f97\u6709\u4e00\u4e1d\u51c9\u610f\uff0c\u76f4 \u900f\u80cc\u810a\u3002\u5bd2\u98ce\uff0c\u73b0\u5728\u624d\u4f7f\u5979\u611f\u89c9\u5f97\u51b7\u3002 \u3000\u3000\u5979\u5446\u5446\u5730\u4f2b\u7acb\u4e86\u4e00\u4f1a\uff0c\u8ba9\u81ea\u5df1\u5728\u51ac\u591c\u7684\u5bd2\u98ce\u4e2d\uff0c\u7a0d\u4e3a\u51b7\u9759\u4e00\u4e0b\uff0c\u6e05\u9192\u4e00\u4e0b\u3002 \u3000\u3000\u7b49\u5230\u5979\u5fc3\u4e2d\u7684\u5de8\u8df3\u6e10\u6e10\u5e73\u590d\u4e86\u7684\u65f6\u5019\uff0c\u5979\u8d70\u5230\u4f0a\u98ce\u5012\u5367\u7740\u7684\u8eab\u8eaf\u65c1\uff0c\u6478\u4e86\u6478\u4ed6\u7684\u9f3b\u606f\u548c \u80f8\u53e3\uff0c\u77e5\u9053\u8fd9\u62da\u7740\u751f\u547d\u6765\u4fdd\u62a4\u522b\u4eba\u7684\u5e74\u8f7b\u4eba\uff0c\u867d\u7136\u8eab\u8d1f\u91cd\u4f24\uff0c\u5374\u5c1a\u672a\u6b7b\u53bb\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5979\u518d\u8d70\u5230\u81ea\u5df1\u5973\u513f\u8eab\u4fa7\uff0c\u5979\u552f\u4e00\u7684\u7231\u5973\uff0c\u6b64\u523b\u4ea6\u662f\u6c14\u606f\u5944\u5944\uff0c\u4f46\u662f\u4e5f\u5e76\u672a\u6b7b\u53bb\uff0c \u6240\u53d7\u7684\u4f24\uff0c\u751a\u81f3\u8fdc\u8fdc\u6bd4\u90a3\u5e74\u8f7b\u4eba\u8f7b\u5f97\u591a\uff01 \u3000\u3000\u5979\u89c9\u5f97\uff0c\u81ea\u5df1\u7684\u773c\u775b\u6709\u4e9b\u6f6e\u6e7f\uff0c\u5979\u4e0d\u77e5\u9053\u8fd9\u662f\u56e0\u4e3a\u5979\u5bf9\u90a3\u5e74\u8f7b\u4eba\u7684\u611f\u6fc0\uff0c\u6291\u6216\u662f\u5bf9\u4e0a\u82cd \u7684\u611f\u6fc0\uff0c\u4f46\u603b\u4e4b\u8fd9\u662f\u611f\u6fc0\u7684\u6cea\u73e0\u3002 \u3000\u3000\u4e5f\u8bb8\u8fd9\u4e24\u79cd\u611f\u6fc0\u90fd\u6709\u4e9b\uff0c\u56e0\u4e3a\uff0c\u8fd9\u4e24\u8005\u4f7f\u5979\u548c\u5979\u7684\u5973\u513f\uff0c\u5947\u8ff9\u822c\u5730\u4fdd\u5168\u4e86\u6027\u547d\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e03\u7ae0\u3000\u6d45\u6c34\u4e4b\u9f99>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u7ae0\u3000\u6d45\u6c34\u4e4b\u9f99 \u3000\u3000\u8fd9\u4efd\u611f\u6fc0\uff0c\u6b64\u523b\u5c1a\u505c\u7559\u5728\u5750\u5728\u8f66\u4e2d\u7684\u5b59\u654f\u5fc3\u4e2d\uff0c\u56e0\u4e3a\u5979\u4e00\u56de\u5fc6\u5230\u8fd9\u4e9b\uff0c\u5979\u7684\u773c\u775b\u5c31\u53c8\u5f00 \u59cb\u6e7f\u6da6\u8d77\u6765\uff0c\u50cf\u662f\u5927\u591a\u6570\u611f\u6069\u56fe\u62a5\u7684\u4eba\u4e00\u6837\uff0c\u5979\u5bf9\u4f0a\u98ce\u7684\u6069\u60c5\uff0c\u662f\u6c38\u4e16\u4e0d\u4f1a\u5fd8\u6000\u7684\u3002 \u3000\u3000\u5f53\u7136\uff0c\u5979\u6b64\u523b\u80fd\u5728\u201c\u5929\u4e89\u6559\u201d\u5927\u641c\u534e\u5c71\u4e4b\u524d\uff0c\u5c31\u5b89\u5168\u5730\u9003\u51fa\uff0c\u8fd8\u662f\u9760\u7740\u81ea\u5df1\uff0c\u5979\u81ea\u5df1\u90a3 \u79cd\u5728\u5371\u6025\u4e2d\u4ecd\u7136\u4fdd\u5b58\u7684\u660e\u786e\u7684\u5224\u65af\u529b\u3002 \u3000\u3000\u5728\u5979\u795e\u667a\u6e05\u9192\u4e4b\u540e\uff0c\u5979\u7acb\u523b\u5c06\u81ea\u5df1\u7684\u5973\u513f\u548c\u4f0a\u98ce\u5e26\u56de\u9690\u5c45\u4e4b\u5904\uff0c\u4e3a\u4f0a\u98ce\u4e0a\u4e86\u6781\u597d\u7684\u91d1\u521b \u836f\u3002 \u3000\u3000\u4f46\u662f\u5bf9\u4ed6\u4eec\u2014\u2014\u51cc\u7433\u548c\u4f0a\u98ce\u2014\u2014\u6240\u53d7\u7684\u5185\u4f24\uff0c\u5979\u5374\u675f\u624b\u65e0\u7b56\uff0c\u6ca1\u6709\u4efb\u4f55\u529e\u6cd5\u3002 \u3000\u3000\u5979\u5f53\u7136\u7740\u6025\uff0c\u4f46\u662f\u5728\u7740\u6025\u4e4b\u4e2d\uff0c\u5979\u4ecd\u60f3\u5230\u4e86\u6b64\u4e8b\u53ef\u80fd\u53d1\u751f\u7684\u540e\u679c\u3002 \u3000\u3000\u4e8e\u662f\u5979\u70e7\u6bc1\u4e86\u81ea\u5df1\u8f9b\u82e6\u642d\u6210\u7684\u8349\u5c4b\uff0c\u53d7\u5c3d\u5343\u8f9b\u4e07\u82e6\uff0c\u5c06\u81ea\u5df1\u7684\u5973\u513f\u548c\u6551\u4e86\u5979\u4eec\u7684\u6069\u4eba\uff0c \u4ece\u534e\u5c71\u7edd\u9876\u4e0a\u642c\u5230\u5c71\u4e0b\u53bb\u3002 \u3000\u3000\u5728\u4e00\u591c\u4e4b\u4e2d\uff0c\u5b8c\u6210\u7684\u8fd9\u4e9b\u4e8b\uff0c\u5f53\u7136\u662f\u9760\u7740\u5979\u7684\u6b66\u529f\u548c\u5979\u90a3\u79cd\u575a\u5fcd\u7684\u6bc5\u529b\uff0c \u3000\u3000\u201c\u53ef\u662f\u5f80\u90a3\u91cc\u53bb\u5462\uff1f\u201d\u63a5\u7740\uff0c\u8fd9\u95ee\u9898\u53c8\u5728\u56f0\u6270\u7740\u5979\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5979\u4e0d\u60dc\u82b1\u4e86\u6bd4\u5e94\u8be5\u4ed8\u51fa\u7684\u4ef7\u94b1\uff0c\u8d35\u4e86\u597d\u51e0\u500d\u7684\u9ad8\u4ef7\uff0c\u96c7\u4e86\u8f86\u5927\u8f66\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u6211\u4eec\u5148\u5f80\u504f\u50fb\u70b9\u7684\u5730\u65b9\u53bb\u5427\uff01\u201d\u5979\u66ff\u81ea\u5df1\u4e0b\u4e86\u4e2a\u51b3\u5b9a\u3002 \u3000\u3000\u5176\u5b9e\u6b64\u523b\u9664\u4e86\u5979\u81ea\u5df1\u4e4b\u5916\uff0c\u53c8\u6709\u8c01\u80fd\u5e2e\u52a9\u5979\u4eec\u5462\uff1f \u3000\u3000\u4e8e\u662f\u8fd9\u8f86\u5927\u8f66\u7531\u534e\u5c71\u7684\u5c71\u811a\uff0c\u5954\u6ce2\u8fde\u65e5\uff0c\u663c\u591c\u6512\u884c\uff0c\u8d76\u5230\u8fd9\u91cc\u3002 \u3000\u3000\u4f46\u662f\u5b59\u654f\u77e5\u9053\u201c\u5929\u4e89\u6559\u201d\u7684\u52bf\u529b\uff0c\u904d\u5e03\u4e2d\u539f\uff0c\u6b64\u523b\u4ecd\u672a\u9003\u51fa\u4eba\u5bb6\u7684\u624b\u638c\uff0c\u518d\u52a0\u4e0a\u53d7\u4f24\u4e24 \u4eba\u60c5\u52bf\u6108\u53d1\u5371\u6b86\uff0c\u5979\u82b3\u5fc3\u64a9\u4e71\uff0c\u6025\u5f97\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff01 \u3000\u3000\u201c\u9996\u5148\uff0c\u6211\u8be5\u60f3\u529e\u6cd5\u5c06\u4ed6\u4eec\u4e24\u4eba\u7684\u4f24\u6cbb\u597d\u624d\u884c\u5440\uff01\u201d\u5979\u6697\u5fd6\u7740\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u79cd\u88ab\u5185\u5bb6\u9ad8\u624b\u6240\u91cd\u521b\u7684\u5185\u4f24\uff0c\u53c8\u5c82\u662f\u666e\u901a\u4eba\u53ef\u4ee5\u6cbb\u5f97\u4e86\u7684\uff1f\u5979\u867d\u7136\u4e5f\u77e5\u9053\u51e0\u4e2a\u4ee5 \u533b\u9053\u95fb\u540d\u6c5f\u6e56\u7684\u4eba\u7269\uff0c\u4f46\u81ea\u5bb6\u5728\u8fd9\u79cd\u60c5\u51b5\u4e2d\uff0c\u53c8\u5c82\u80fd\u968f\u4fbf\u6c42\u6551\uff1f\u4e07\u4e00\u5bf9\u65b9\u8fd1\u5e74\u6765\u5df2\u548c\u201c\u5929\u4e89 \u6559\u201d\u6709\u4e86\u8fde\u7edc\uff0c\u90a3\u4e48\u81ea\u5df1\u4e00\u53bb\uff0c\u5c82\u975e\u7f8a\u5165\u864e\u53e3\uff01 \u3000\u3000\u5c31\u7b97\u4e0d\u81f4\u5982\u6b64\uff0c\u4f46\u5979\u4e5f\u660e\u767d\u81ea\u5df1\u6b64\u523b\u5df2\u662f\u60f9\u7978\u7684\u6839\u82d7\uff0c\u5979\u53c8\u600e\u80fd\u518d\u8ba9\u522b\u4eba\u60f9\u7978\uff1f \u3000\u3000\u4f46\u662f\uff0c\u8fd9\u53d7\u7740\u91cd\u4f24\u7684\u4e24\u4eba\uff0c\u53c8\u8be5\u600e\u6837\u5462\uff1f \u3000\u3000\u5979\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u6084\u6084\u5730\u5c06\u8f66\u7a97\u63a8\u5f00\u4e00\u7ebf\uff0c\u53d1\u89c9\u5916\u9762\u5929\u5df2\u7ecf\u6697\u4e86\uff0c\u98ce\u5f88\u5927\uff0c\u4ece\u7a97\u9699\u4e2d\u5439\u8fdb \u6765\uff0c\u4f7f\u5f97\u5979\u6253\u4e86\u4e2a\u5bd2\u6218\u3002 \u3000\u3000\u4e8e\u662f\u5979\u63a9\u4e0a\u7a97\u5b50\uff0c\u671d\u524d\u9762\u8d76\u8f66\u7684\u8f66\u628a\u5f0f\u9ad8\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u524d\u9762\u6709\u6b47\u606f\u4e00\u4f1a\u7684\u5730\u65b9\u5417\uff1f\u201d \u3000\u3000\u8f66\u628a\u5f0f\u626c\u8d77\u9a6c\u97ad\uff0c\u547c\u54e8\u4e00\u54cd\uff0c\u9053\uff1a \u3000\u3000\u201c\u65b9\u624d\u6211\u4eec\u7ecf\u8fc7\u4e24\u5904\u5927\u9547\uff0c\u4f60\u90fd\u4e0d\u80af\u6253\u5c16\uff0c\u73b0\u5728\u5440\uff0c\u53ef\u627e\u4e0d\u5230\u4ec0\u4e48\u5730\u65b9\u4e86\uff01\u5df4\u662f\u6709\uff0c\u6050 \u6015\u4e5f\u662f\u50cf\u6628\u5929\u4e00\u6837\u90a3\u79cd\u8fde\u70ed\u6c34\u90fd\u6ca1\u6709\u7684\u5c0f\u5e97\u3002\u5509\uff01\u8fd9\u4e48\u8d76\u8f66\uff0c\u5b9e\u5728\u771f\u662f\u5728\u53d7\u6d3b\u7f6a\uff01\u201d \u3000\u3000\u5b59\u654f\u4e00\u76b1\u7709\uff0c\u5979\u5bf9\u8f66\u628a\u5f0f\u8bf4\u8bdd\u7684\u8fd9\u79cd\u6001\u5ea6\uff0c\u975e\u5e38\u4e0d\u6ee1\u610f\uff1b\u5c24\u5176\u8fd9\u8f66\u628a\u5f0f\u7adf\u76f4\u622a\u4e86\u5f53\u5730\u79f0 \u5979\u4e3a\u201c\u4f60\u201d\uff0c\u66f4\u4f7f\u8fd9\u5e73\u7d20\u6781\u53d7\u4eba\u5c0a\u91cd\u7684\u4e09\u6e58\u5927\u4fa0\u7684\u592b\u4eba\uff0c\u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u6c14\u6124\uff0c\u51e0\u4e4e\u8981\u6253\u5f00\u524d \u9762\u7684\u7a97\u5b50\uff0c\u5c06\u8fd9\u65e0\u793c\u7684\u7c97\u6c49\uff0c\u4ece\u5ea7\u4e0a\u62c9\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5979\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u5fcd\u4f4f\u7740\u6c14\uff0c\u81ea\u5bb6\u5df2\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u53c8\u4f55\u82e6\u4e3a\u4e86\u4e9b\u5c0f\u4e8b\uff0c\u548c\u8fd9\u79cd\u7c97 \u9119\u7684\u8f66\u628a\u5f0f\uff0c\u518d\u5455\u4e9b\u95f2\u6c14\u5462\uff1f \u3000\u3000\u5979\u89c9\u5f97\uff1a\u81ea\u5df1\u597d\u50cf\u662f\u88ab\u56f0\u5728\u6d45\u6c34\u91cc\u7684\u86df\u9f99\uff0c\u8fde\u9c7c\u867e\u7684\u6c14\uff0c\u90fd\u8981\u5fcd\u53d7\u4e86\u3002\u672c\u6765\u5df2\u7ecf\u6f6e\u6da6\u7684 \u773c\u775b\uff0c\u4e0d\u7981\u66f4\u6f6e\u6da6\u4e86\u4e9b\u3002 \u3000\u3000\u4f46\u5979\u6bd5\u7adf\u662f\u521a\u5f3a\u7684\u5973\u5b50\uff0c\u800c\u4e14\u524d\u9014\u8fd8\u6709\u8bb8\u591a\u4e8b\u60c5\u7b49\u5979\u53bb\u505a\uff0c\u8fd9\u53d7\u91cd\u4f24\u7684\u4e24\u4e2a\u4eba\u7684\u6027\u547d\uff0c \u4e5f\u5168\u64cd\u5728\u5979\u7684\u624b\u4e0a\uff0c\u5bb9\u4e0d\u5f97\u5979\u6c14\u9981\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5f3a\u81ea\u6309\u637a\u4f4f\u4e86\u5fc3\u4e2d\u7684\u6012\u6c14\uff0c\u548c\u90a3\u79cd\u88ab\u5c48\u8fb1\u7684\u611f\u89c9.\u8bf4\u9053\uff1a \u3000\u3000\u201c\u968f\u4fbf\u627e\u4e2a\u5730\u65b9\u6b47\u4e0b\u597d\u4e86\uff0c\u7b49\u4f1a\u2026\u2026\u7b49\u4f1a\u513f\u6211\u518d\u52a0\u4f60\u7684\u8f66\u94b1\u3002\u201d \u3000\u3000\u90a3\u8f66\u628a\u5f0f\u547c\u5730\u53c8\u4e00\u62a1\u97ad\u5b50\uff0c\u5c06\u9a6c\u6253\u5f97\u556a\u556a\u4f5c\u54cd\uff0c\u563b\u7740\u5634\u9053\uff1a \u3000\u3000\u201c\u4e0d\u662f\u6211\u603b\u662f\u8981\u4f60\u52a0\u8f66\u94b1\uff0c\u76f4\u5728\u56e0\u4e3a\u8fd9\u79cd\u5929\u6c14\uff0c\u5192\u7740\u8fd9\u4e48\u5927\u7684\u98ce\uff0c\u665a\u4e0a\u8fde\u53e3\u70ed\u6c34\u90fd\u559d\u4e0d \u7740\uff0c\u4f60\u8bf4\u8fd9\u4e2a\u7f6a\u662f\u4e0d\u662f\u96be\u53d7\uff1f\u201d \u3000\u3000\u8fd9\u8f66\u628a\u5f0f\u8bb2\u7684\u8bdd\uff0c\u4fbf\u5979\u6781\u4e3a\u8ba8\u538c\uff0c\u4f46\u662f\u5979\u5374\u6ca1\u6709\u529e\u6cd5\u4e0d\u542c\u3002 \u3000\u3000\u4e8e\u662f\u5979\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e3a\u53d7\u4f24\u7684\u4e24\u4eba\u6574\u7406\u4e00\u4e0b\u51cc\u4e71\u7684\u88ab\u8925\uff0c\u4ed6\u4eec\u53d1\u51fa\u7684\u547b\u541f\u4e4b\u58f0\uff0c\u51e0\u4e4e\u4f7f\u5f97 \u5979\u7684\u5fc3\uff0c\u90fd\u788e\u505a\u4e00\u7247\u4e00\u7247\u7684\u5c0f\u904d\u4e86\u3002 \u3000\u3000\u8f66\u5b50\u7a81\u5730\u505c\u4f4f\uff0c\u8f66\u628a\u5f0f\u56de\u8fc7\u5934\u6765\u5406\u559d\u9053\uff1a \u3000\u3000\u201c\u5230\u4e86\uff0c\u4e0b\u8f66\u5427\uff01\u201d \u3000\u3000\u5750\u5728\u8f66\u53a2\u7684\u5b59\u654f\uff0c\u770b\u4e0d\u5230\u8f66\u5916\u90a3\u8f66\u628a\u5f0f\u5634\u89d2\u6302\u7740\u7684\u4e11\u7b11\uff0c\u7565\u4e3a\u6d3b\u52a8\u4e86\u4e00\u4e0b\u7b4b\u9aa8\u3002 \u3000\u3000\u8fd9\u4e9b\u5929\u6765\uff0c\u4e3a\u4e86\u770b\u62a4\u53d7\u4f24\u7684\u4eba\uff0c\u5979\u51e0\u4e4e\u6ca1\u6709\u7761\u8fc7\uff0c\u6b64\u523b\u5979\u4f38\u817f\u76f4\u8170\u4e4b\u95f4\uff0c\u624d\u89c9\u5f97\u81ea\u5df1\u7684 \u8170\u817f\uff0c\u90fd\u6709\u4e9b\u9178\u4e86\u3002 \u3000\u3000\u5979\u4e0b\u4e86\u8f66\uff0c\u624d\u53d1\u73b0\u9762\u524d\u7684\u8fd9\u5bb6\u5ba2\u6808\uff0c\u679c\u7136\u5c0f\u5f97\u53ef\u601c\uff0c\u4f46\u662f\u5979\u5374\u8ba4\u4e3a\u5f88\u6ee1\u610f\u3002\u56de\u5934\u5411\u8f66\u628a \u5f0f\u9053\uff1a \u3000\u3000\u201c\u5e2e\u6211\u5fd9\u628a\u75c5\u4eba\u6276\u4e0b\u6765\uff01\u201d \u3000\u3000\u8f66\u628a\u5f0f\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u7b11\u4e86\u7b11\uff0c\u5148\u5e2e\u7740\u5979\u6276\u4e0b\u4f0a\u98ce\uff0c\u62ac\u5230\u90a3\u5bb6\u5ba2\u6808\u7684\u4e00\u95f4\u9634\u6697\u7684\u5c0f\u623f\u5b50 \u91cc\uff0c\u518d\u51fa\u53bb\u62ac\u8f66\u91cc\u7684\u51cc\u7433\u3002 \u3000\u3000\u5b59\u654f\u53d1\u73b0\u8fd9\u8f66\u628a\u5f0f\u548c\u8fd9\u5c0f\u84d6\u6808\u7684\u4f19\u8ba1\u548c\u638c\u67dc\u7684\uff0c\u90fd\u975e\u5e38\u719f\u6089\uff0c\u4f46\u662f\u5979\u4e5f\u672a\u5728\u610f\u3002 \u3000\u3000\u53ef\u662f\uff0c\u90a3\u8f66\u628a\u5f0f\u5728\u5e2e\u7740\u5979\u62ac\u51cc\u7433\u65f6\uff0c\u4e58\u673a\u5728\u5979\u624b\u4e0a\u6478\u4e86\u4e00\u628a\uff0c\u5374\u4f7f\u5f97\u5979\u7684\u6012\u706b\uff0c\u500f\u7136\u5347 \u8d77\uff01 \u3000\u3000\u5979\u7684\u76ee\u5149\uff0c\u5200\u4e00\u6837\u5730\u77aa\u5411\u90a3\u8f66\u628a\u5f0f\u8eab\u4e0a\uff0c\u90a3\u8f66\u628a\u5f0f\u4e5f\u4e0d\u7981\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u5e97\u4f19\u5374\u5728\u65c1\u8fb9\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u5c0f\u738b\u5934\u8fd8\u61c2\u5f97\u4f4e\u5934\u5440\uff01\u201d \u3000\u3000\u5b59\u654f\u5982\u5200\u7684\u76ee\u5149\uff0c\u7acb\u523b\u8f6c\u5411\u90a3\u5e97\u4f19\u3002 \u3000\u3000\u90a3\u5e97\u4f19\u8038\u4e86\u8038\u80a9\uff0c\u8868\u793a\uff1a\u6211\u53c8\u6ca1\u6709\u8bb2\u4f60\uff0c\u4f60\u77aa\u6211\u5e72\u4ec0\u4e48\uff01\u201d\u6837\u5b50\u66f4\u4e3a\u8ba8\u538c\u3002 \u3000\u3000\u5b59\u654f\u4e5f\u89c9\u5f97\u8fd9\u5e97\u4f19\u6709\u4e9b\u4e0d\u5bf9\u8def\uff0c\u4f46\u662f\u5979\u81ea\u6043\u8eab\u624b\uff0c\u600e\u4f1a\u5c06\u8fd9\u4e9b\u5c0f\u4eba\u653e\u5728\u773c\u91cc\uff01 \u3000\u3000\u5176\u5b9e\uff0c\u5979\u5e74\u9f84\u867d\u5927\uff0c\u4f46\u4e00\u5411\u517b\u5c0a\u5904\u4f18\uff0c\u5c31\u662f\u8ddf\u7740\u51cc\u5317\u4fee\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u4e5f\u662f\u50cf\u7687\u540e\u822c\u88ab \u4eba\u5c0a\u91cd\uff0c\u8fd9\u79cd\u5b64\u8eab\u95ef\u8361\u6c5f\u6e56\u7684\u7ecf\u9a8c\uff0c\u53ef\u8bf4\u5c11\u4e4b\u53c8\u5c11\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u5979\u4e0d\u77e5\u9053\u4e16\u95f4\u6700\u53ef\u6015\u7684\uff0c\u5c31\u662f\u8fd9\u4e9b\u5c0f\u4eba\uff01\u771f\u6b63\u7eff\u6797\u8c6a\u5ba2\uff0c\u8bb2\u7a76\u7684\u662f\u660e\u5200\u771f\u67aa\uff0c\u4e09 \u5200\u516d\u773c\uff0c\u5351\u9119\u9f8c\u9f8a\u7684\u4e8b\u5374\u5f88\u5c11\u505a\u3002 \u3000\u3000\u5979\u4e0d\u6562\u548c\u53d7\u4f24\u7684\u4eba\u5206\u623f\u800c\u7761\uff0c\u665a\u4e0a\uff0c\u5979\u53ea\u80fd\u9760\u5728\u6905\u4e0a\u6253\u76f9\u3002 \u3000\u3000\u5979\u56e0\u4e3a\u592a\u8fc7\u75b2\u52b3\uff0c\u5728\u8fd9\u5c0f\u84d6\u623f\u7684\u6728\u6905\u4e0a\u7adf\u7761\u7740\u4e86\uff0c\u6726\u6726\u80e7\u80e7\u95f4\uff0c\u6709\u4eba\u8f7b\u8f7b\u63a8\u5f00\u623f\u95e8\uff0c\u5979 \u6b63\u60ca\u89c9\uff0c\u4e24\u81c2\u5df2\u88ab\u56db\u6761\u5f3a\u800c\u6709\u529b\u7684\u624b\u6293\u4f4f\uff0c\u5979\u8fd9\u624d\u4ece\u6c89\u7761\u4e2d\u5b8c\u5168\u6e05\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u201c\u8001\u5200\u5b50\uff01\u8fd9\u5a18\u513f\u4eec\u6765\u8def\u53ef\u4e0d\u6b63\uff0c\u8bf4\u4e0d\u5b9a\u624b\u5e95\u4e0b\u4e5f\u6709\u4e24\u4e0b\u5b50\uff0c\u4f60\u53ef\u5f97\u7559\u70b9\u795e\uff01\u201d \u3000\u3000\u8fd9\u662f\u53eb\u505a\u201c\u5c0f\u738b\u5934\u201d\u7684\u90a3\u8f66\u628a\u5f0f\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u8001\u5200\u5b50\u201d\u5c31\u662f\u90a3\u5e97\u4f19\uff0c\u602a\u7b11\u7740\u8bf4\uff1a \u3000\u3000\u201c\u5c0f\u738b\u5934\uff0c\u4f60\u5c31\u5fc3\u5b9a\u5427\uff01\u8fde\u4e2a\u5a18\u513f\u4eec\u90fd\u505a\u4e0d\u7ffb\uff0c\u6211\u5b8b\u8001\u5200\u8fd8\u51fa\u6765\u73b0\u4ec0\u4e48\u4e16\uff01\u201d \u3000\u3000\u5b59\u654f\u5fc3\u91cc\u5927\u6012\uff0c\u201c\u539f\u6765\u8fd9\u8f66\u628a\u5f0f\u4e0d\u662f\u597d\u4e1c\u897f\uff01\u201d \u3000\u3000\u5979\u65b9\u5728\u6697\u5fd6\uff0c\u5374\u542c\u5f97\u201c\u5b8b\u8001\u5200\u201d\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u770b\u5e8a\u4e0a\u8eba\u7740\u7684\u4e24\u4e2a\uff0c\u516b\u6210\u513f\u662f\u6c5f\u6d0b\u5927\u76d7\uff0c\u8bf4\u4e0d\u5b9a\u5c06\u4ed6\u4eec\u9001\u5230\u5b98\u5e9c\u91cc\u53bb\uff0c\u8fd8\u53ef\u4ee5\u9886\u8d4f \u54e9\uff01\u201d \u3000\u3000\u5b59\u654f\u77e5\u9053\u81ea\u5df1\u53ea\u8981\u4e00\u62ac\u624b\uff0c\u51ed\u7740\u81ea\u5df1\u7684\u529f\u529b\uff0c\u4e0d\u96be\u5c06\u8fd9\u4e24\u4e2a\u8349\u5305\u629b\u51fa\u53bb\uff0c\u4f46\u5979\u5fc3\u4e2d\u8f6c\u4e86 \u51e0\u8f6c\uff0c\u5374\u4ecd\u5047\u88c5\u7761\u7740\uff0c\u6ca1\u6709\u4efb\u4f55\u4e3e\u52a8\u3002 \u3000\u3000\u201c\u522b\u7684\u6211\u90fd\u4e0d\u7ba1\uff0c\u6211\u53ea\u8981\u8fd9\u5a18\u513f\u966a\u6211\u7761\u51e0\u665a\u3002\u201d\u5c0f\u738b\u5934\u6deb\u7b11\u7740\u9053\u3002 \u3000\u3000\u201c\u8fd9\u51e0\u5929\u6211\u53ea\u8981\u4e00\u770b\u7740\u5979\uff0c\u5fc3\u91cc\u5c31\u75d2\u75d2\u7684\uff01\u201d\u4ed6\u54c8\u4e86\u4e00\u58f0\u53c8\u9053\u3002 \u3000\u3000\u201c\u6211\u5c0f\u738b\u5934\u5c31\u662f\u8fd9\u4e2a\u6bdb\u75c5\uff0c\u94f6\u5b50\uff0c\u6211\u5012\u4e0d\u5728\u4e4e\u3002\u201d \u3000\u3000\u5b59\u654f\u6781\u5feb\u5730\u5728\u5fc3\u4e2d\u52a8\u4e86\u51e0\u52a8\uff0c\u79cd\u79cd\u7684\u5fe7\u60a3\u5df2\u4f7f\u5979\u5728\u505a\u4efb\u4f55\u4e00\u4ef6\u4e8b\u4e4b\u5148\uff0c\u5c31\u5148\u8003\u8651\u5230\u9000 \u8def\u3002 \u3000\u3000\u5979\u60f3\u5230\u82e5\u5c06\u8fd9\u4e24\u4e2a\u6df7\u86cb\u9664\u6389\uff0c\u90a3\u4ee5\u540e\u5979\u5c31\u5f97\u81ea\u5df1\u8d76\u8f66\uff0c\u6bcf\u4e00\u4ef6\u4e8b\u5c31\u90fd\u5f97\u81ea\u5df1\u52a8\u624b\u505a\u4e86\u3002 \u3000\u3000\u6211\u662f\u4e0d\u662f\u80fd\u505a\u5f97\u5230\u5462\uff1f\u5979\u8003\u8651\u7740\u3002 \u3000\u3000\u201c\u8fd9\u5a18\u513f\u5012\u7761\u5f97\u6c89\uff0c\u50cf\u662f\u73a9\u4e86\u516b\u6b21\u4e00\u6837\u3002\u201d\u5b8b\u8001\u5200\u602a\u7b11\u7740\u3002 \u3000\u3000\u5b59\u654f\u66f4\u5927\u6012\uff1a\u201c\u6211\u5c82\u80fd\u88ab\u8fd9\u79cd\u4eba\u4fae\u8fb1\uff01\u201d\u5979\u867d\u7136\u4e8b\u4e8b\u90fd\u8003\u8651\u5468\u8be6\uff0c\u4f46\u672c\u6027\u4e5f\u662f\u5b81\u6298\u6bcb\u5f2f \u7684\u6027\u5b50\uff0c\u600e\u80af\u53d7\u8fb1\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5979\u6697\u5c06\u771f\u6c14\u8fd0\u884c\u4e00\u8f6c\u3002 \u3000\u3000\u201c\u5b8b\u8001\u5200\uff0c\u6211\u5f97\u501f\u4f60\u7684\u5e8a\u7528\u7528\uff0c\u4e0d\u7792\u4f60\u8001\u54e5\u8bf4\uff0c\u6211\u5b9e\u5728\u71ac\u4e0d\u4f4f\u4e86\uff0c\u5c24\u5176\u770b\u5230\u8fd9\u5a18\u513f\u8138\u4e0a \u7684\u8fd9\u2026\u2026\u201d \u3000\u3000\u5c0f\u738b\u5934\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u5730\u8eab\u5b50\u76f4\u98de\u4e86\u51fa\u53bb\uff0c\u7830\u5730\u649e\u5230\u571f\u5899\u4e0a\uff0c\u53c8\u7830\u5730\u843d\u4e86\u4e0b\u6765\uff0c\u773c\u524d\u91d1\u661f \u4e71\u5192\uff0c\u5c41\u80a1\u75db\u5f97\u50cf\u662f\u88c2\u4e86\u5f00\u6765\u3002\u5c0f\u5e97\u91cc\u90a3\u7528\u6ce5\u548c\u571f\u7816\u505a\u7684\u571f\u5899\uff0c\u88ab\u4ed6\u8fd9\u4e00\u649e\uff0c\u4e5f\u6447\u6447\u6b32\u5012\u3002 \u3000\u3000\u90a3\u8fb9\u5b8b\u8001\u5200\u4e5f\u88ab\u8dcc\u5f97\u4e03\u6655\u516b\u7d20\u3002 \u3000\u3000\u5b59\u654f\u5374\u5927\u4e3a\u5947\u602a\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u52a8\u624b\u5440\uff0c\u8fd9\u4e24\u4eba\u5374\u600e\u7684\u4e86\uff01\u201d \u3000\u3000\u56de\u5934\u4e00\u770b\uff0c\u53c8\u9669\u4e9b\u60ca\u5524\u51fa\u58f0\u3002 \u3000\u3000\u5728\u5979\u8eab\u4fa7\uff0c\u5353\u7136\u7ad9\u7740\u4e00\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u95f4\u6597\u5ba4\u4e2d\u7684\u9634\u6697\uff0c\u662f\u4ee5\u5979\u770b\u4e0d\u6e05\u8fd9\u4eba\u7684\u9762\u8c8c\uff0c\u53ea\u89c9\u5f97\u6b64\u4eba\u8863\u886b\u5bbd\u5927\uff0c\u98ce\u5ea6\u751a\u4e3a\u6f47 \u6d12\u3002 \u3000\u3000\u5b59\u654f\u53ea\u770b\u5f97\u89c1\u4ed6\u7684\u4e00\u53cc\u773c\u775b\uff0c\u864e\u864e\u6709\u5a01\uff0c\u6b63\u5f85\u8bf4\u51e0\u53e5\u611f\u8c22\u7684\u8bdd\uff0c\u90a3\u4eba\u5374\u6446\u624b\u9053\u201c\u4f60\u4e0d\u7528 \u8c22\u6211\uff01\u6211\u4e5f\u4e0d\u662f\u7279\u5730\u6765\u6551\u4f60\u7684\u3002\u201d \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u516b\u7ae0\u3000\u4e07\u5251\u4e4b\u5c0a>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u7ae0\u3000\u4e07\u5251\u4e4b\u5c0a \u3000\u3000\u5b59\u654f\u7acb\u523b\u5fd6\u9053\u201c\u8fd9\u4eba\u7684\u813e\u6c14\uff0c\u600e\u5730\u5982\u6b64\u4e4b\u602a\uff1f\u201d \u3000\u3000\u5374\u89c1\u90a3\u4eba\u4e00\u62ac\u817f\uff0c\u5df2\u8de8\u5230\u201c\u5c0f\u738b\u5934\u201d\u8eab\u4fa7\uff0c\u51b7\u7136\u9053\uff1a \u3000\u3000\u201c\u4f60\u7f6a\u867d\u4e0d\u81f4\u6b7b\uff0c\u4f46\u4e5f\u5dee\u4e0d\u591a\u4e86\u3002\u6211\u82e5\u4e0d\u9664\u4e86\u4f60\uff0c\u53ea\u6015\u53c8\u6709\u522b\u7684\u5987\u5973\u8981\u574f\u5728\u4f60\u7684\u624b \u4e0a\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u51b0\u51b7\uff0c\u58f0\u8c03\u65e2\u65e0\u9ad8\u4f4e\uff0c\u8bed\u6c14\u4e5f\u7edd\u65e0\u53d8\u5316\uff0c\u5728\u4ed6\u8bf4\u4e24\u79cd\u7edd\u5bf9\u6027\u8d28\u4e0d\u540c\u7684\u8bdd\u7684\u65f6\u5019\uff0c \u5374\u7edd\u5bf9\u662f\u540c\u6837\u7684\u97f3\u8c03\u3002 \u3000\u3000\u90a3\u5c31\u662f\u8bf4\u2014\u2014\u4ed6\u8bed\u6c14\u4e4b\u95f4\uff0c\u7edd\u5bf9\u6ca1\u6709\u4e1d\u6beb\u60c5\u611f\u5b58\u5728\uff0c\u50cf\u662f\u4e00\u4e2a\u5b66\u7ae5\u5728\u80cc\u8bf5\u7740\u4e66\u4e0a\u7684\u5bf9\u8bdd \u4f3c\u7684\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c0f\u738b\u5934\u542c\u4e86\uff0c\u5374\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u54c0\u58f0\u9053\u201c\u5927\u7237\u9976\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u201c\u547d\u201d\u5c1a\u672a\u8bf4\u51fa\uff0c\u90a3\u4eba\u8863\u8896\u8f7b\u8f7b\u4e00\u62c2\uff0c\u5c0f\u738b\u5934\u7684\u8eab\u4f53\u5c31\u8f6f\u762b\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u8fb9\u5b8b\u8001\u5200\u5927\u53eb\u4e00\u58f0\uff0c\u722c\u8d77\u6765\u5c31\u8dd1\u3002 \u3000\u3000\u90a3\u4eba\u8fde\u5934\u90fd\u672a\u56de\uff0c\u811a\u4e0b\u50cf\u662f\u6709\u4eba\u6258\u7740\u4f3c\u7684\uff0c\u500f\u7136\u5df2\u6321\u5230\u95e8\u53e3\uff0c\u521a\u597d\u5c31\u6321\u5728\u201c\u5b8b\u8001\u5200\u201d\u8eab \u524d\uff0c\u51b7\u7136\u9053\uff1a\u201c\u4f60\u8981\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5b8b\u8001\u5200\u51b7\u6c57\u6d94\u6d94\u800c\u843d\uff0c\u5f20\u53e3\u7ed3\u820c\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u4f60\u7684\u5925\u4f34\u6b7b\u4e86\uff0c\u4f60\u4e00\u4e2a\u4eba\u9003\u8d70\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\u5427\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd8\u6709\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d\u90a3\u4eba\u51b7\u7b11\u9053\u3002 \u3000\u3000\u5b8b\u8001\u5200\u51f6\u6027\u4e00\u53d1\uff0c\u731b\u5730\u81ea\u6000\u4e2d\u62d4\u51fa\u4e00\u628a\u5315\u9996\uff0c\u6ca1\u5934\u6ca1\u8111\u5730\u5411\u90a3\u4eba\u7684\u80f8\u524d\u523a\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5b8b\u8001\u5200\u7684\u5315\u9996\uff0c\u5374\u523a\u4e86\u4e2a\u7a7a\uff0c\u90a3\u4eba\u5df2\u51ed\u7a7a\u540e\u9000\u4e00\u5c3a\uff0c\u888d\u8896\u518d \u4e00\u62c2\uff0c\u5b8b\u8001\u5200\u201c\u54ce\u5440\u201d\u4e8c\u5b57\uff0c\u5c1a\u672a\u51fa\u53e3\uff0c\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5750\u5728\u6905\u4e0a\u7684\u5b59\u654f\uff0c\u770b\u5f97\u51b7\u6c57\u76f4\u6d41\u3002\u5979\u867d\u662f\u5927\u4fa0\u4e4b\u59bb\uff0c\u4f46\u5979\u6709\u751f\u4ee5\u6765\uff0c\u5374\u4ece\u672a\u770b\u8fc7\u8fd9\u79cd\u60ca \u4e16\u9a87\u4fd7\u7684\u6b66\u529f\uff0c\u4e5f\u6ca1\u6709\u770b\u8fc7\u50cf\u8fd9\u4eba\u8fd9\u4e48\u51b7\u786c\u7684\u5fc3\u80a0\uff01\u522b\u4eba\u7684\u751f\u6b7b\uff0c\u4ed6\u770b\u8d77\u6765\u90fd\u50cf\u662f\u4e1d\u6beb\u4e0d\u8db3 \u8f7b\u91cd\u7684\uff0c\u800c\u4ed6\u5c31\u50cf\u4f5b\u7956\u4f3c\u7684\uff0c\u53ef\u4ee5\u4e3b\u5bb0\u7740\u522b\u4eba\u7684\u751f\u6b7b\u3002 \u3000\u3000\u90a3\u4eba\u8eab\u5f62\u4e00\u6643\uff0c\u53c8\u5230\u5979\u7684\u9762\u524d\u3002 \u3000\u3000\u5b59\u654f\u5fc3\u4e2d\u5927\u52a8\uff1a\u201c\u6709\u4e86\u6b64\u4eba\u4e4b\u52a9\uff0c\u6211\u4eec\u4e0d\u80fd\u89e3\u51b3\u7684\u95ee\u9898\uff0c\u4e0d\u662f\u90fd\u53ef\u4ee5\u5b8c\u5168\u8fce\u5203\u800c\u89e3\u4e86 \u5417\uff1f\u201d \u3000\u3000\u90a3\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4ee5\u540e\u7761\u89c9\u65f6\u8981\u5c0f\u5fc3\u4e9b\uff01\u522b\u7684\u5730\u65b9\u53ef\u6ca1\u6709\u8fd9\u4e48\u51d1\u5de7\uff0c\u518d\u4f1a\u78b0\u5230\u4e00\u4e2a\u50cf\u6211\u8fd9 \u6837\u7684\u4eba\uff0c\u4e5f\u4f4f\u5728\u4f60\u540c\u4e00\u5bb6\u5ba2\u6808\u91cc\u3002\u201d \u3000\u3000\u5b59\u654f\u6015\u4ed6\u53c8\u4ee5\u90a3\u79cd\u60ca\u4eba\u7684\u8eab\u6cd5\u63a0\u8d70\uff0c\u8fde\u5fd9\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5374\u89c1\u95e8\u53e3\u5ffd\u7136\u706b\u5149\u4e00\u4eae\uff0c\u4e00\u4eba\u638c\u7740\u706f\u8dd1\u4e86\u8fc7\u6765\uff0c\u770b\u5230\u8eba\u5728\u95e8\u53e3\u7684\u5b8b\u8001\u5200\uff0c\u54ce\u5440\u4e00\u58f0\uff0c\u60ca \u5524\u4e86\u51fa\u6765\uff0c\u624b\u4e2d\u7684\u706f\u4e5f\u6389\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c31\u5728\u90a3\u76cf\u706f\u4ece\u4ed6\u624b\u4e2d\u843d\u5728\u5730\u4e0a\u7684\u90a3\u4e00\u5239\u90a3\u95f4\uff0c\u5b59\u654f\u53ea\u89c9\u5f97\u773c\u524d\u4e00\u82b1\uff0c\u90a3\u76cf\u706f\u7adf\u6ca1 \u6709\u6389\u5230\u5730\u4e0a\uff0c\u800c\u5e73\u5e73\u7a33\u7a33\u5730\u62ff\u5728\u90a3\u6b66\u529f\u7edd\u9ad8\u7684\u5947\u4eba\u624b\u91cc\uff0c\u5979\u4e0d\u7981\u88ab\u8fd9\u4eba\u8fd9\u79cd\u8f7b\u529f\uff0c\u60ca\u5f97\u8bf4\u4e0d \u51fa\u8bdd\u6765\u3002 \u3000\u3000\u638c\u7740\u706f\u8d70\u8fdb\u6765\u7684\u5e97\u638c\u67dc\uff0c\u6b64\u65f6\u5b9b\u5982\u6ce5\u5851\u822c\u7ad9\u5728\u95e8\u53e3\uff0c\u539f\u6765\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\uff0c\u4ed6\u4e5f\u88ab\u90a3\u5947 \u4eba\u70b9\u4e2d\u4e86\u8eab\u4e0a\u7684\u7a74\u9053\u3002 \u3000\u3000\u5b59\u654f\u76ee\u5b9a\u53e3\u5446\uff0c\u90a3\u4eba\u5374\u7f13\u7f13\u8d70\u4e86\u8fc7\u6765\uff0c\u5c06\u706f\u653e\u5728\u684c\u4e0a\uff0c\u706f\u5149\u4e2d\u5b59\u654f\u53ea\u89c1\u4ed6\u8138\u5b54\u96ea\u4e5f\u4f3c\u7684 \u82cd\u767d\uff0c\u7709\u9aa8\u9ad8\u8038\uff0c\u53cc\u76ee\u6df1\u9677\uff0c\u9f3b\u5b57\u9ad8\u800c\u633a\u79c0\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c9\u5f97\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u6765\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u4eba\u5e76\u4e0d\u80fd\u8bf4\u6f02\u4eae\uff0c\u7136\u800c\u5374\u4ee4\u4eba\u89c1\u4e86\u4e00\u9762\uff0c\u5c31\u6c38\u8fdc\u65e0\u6cd5\u5fd8\u53bb\uff0c\u800c\u4e14\u90a3\u79cd\u6210\u719f\u7684\u7537\u6027\u4e4b \u7f8e\uff0c\u66f4\u4ee4\u4eba\u611f\u52a8\uff01 \u3000\u3000\u4ed6\u5e74\u7eaa\u4e5f\u50cf\u662f\u4e2a\u8c1c\uff0c\u56e0\u4e3a\u4ed6\u53ef\u80fd\u662f\u4ece\u4e8c\u5341\u4e94\u5c81\u5230\u56db\u5341\u4e94\u5c81\u4e4b\u95f4\uff0c\u4efb\u4f55\u4e00\u4e2a\u5e74\u9f84\u3002 \u3000\u3000\u5b59\u654f\u51fa\u795e\u5730\u671b\u7740\u4ed6\uff0c\u7adf\u5fd8\u8bb0\u4e86\u4e00\u4e2a\u5973\u5b50\u662f\u4e0d\u5e94\u8be5\u8fd9\u4e48\u770b\u7740\u4e00\u4e2a\u7537\u5b50\u7684\uff0c\u5c24\u5176\u662f\u5979\u624d\u7b2c\u4e00 \u6b21\u548c\u8fd9\u7537\u5b50\u89c1\u9762\u3002 \u3000\u3000\u90a3\u4eba\u4e00\u8f6c\u8138\uff0c\u76ee\u5149\u505c\u7559\u5728\u5b59\u654f\u7684\u8138\u4e0a\uff0c\u8138\u4e0a\u7684\u808c\u8089\uff0c\u4f3c\u4e4e\u7a0d\u4e3a\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u5b59\u654f\u7b2c\u4e8c\u6b21\u60f3\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u90a3\u4eba\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u81ea\u5931\u53bb\u8e2a\u5f71\u3002 \u3000\u3000\u5c31\u50cf\u662f\u795e\u9f99\u4e00\u822c\uff0c\u4ed6\u7ed9\u5b59\u654f\u5e26\u6765\u4e86\u5f88\u4e45\u7684\u601d\u7d22\u3002 \u3000\u3000\u7136\u540e\u5979\u8d70\u5230\u5e8a\u524d\uff0c\u4fef\u8eab\u53bb\u770b\u90a3\u4e24\u4e2a\u53d7\u4f24\u7684\u4eba\uff0c\u7709\u5934\u4e0d\u7981\u7d27\u7d27\u76b1\u5230\u4e00\u5904\u3002 \u3000\u3000\u539f\u6765\u4f0a\u98ce\u548c\u51cc\u7433\uff0c\u7adf\u4ecd\u662f\u660f\u8ff7\u4e0d\u9192\uff0c\u4f24\u52bf\u5012\u5e95\u5982\u4f55\uff1f\u5b59\u654f\u4e5f\u4e0d\u77e5\u9053\u3002\u5979\u5373\u4f7f\u6025\u5f97\u5fc3\u788e\uff0c \u5374\u4e5f\u65e0\u6cd5\u53ef\u60f3\u3002 \u3000\u3000\u5979\u6478\u4e86\u6478\u4e24\u4eba\u7684\u5634\u5507\uff0c\u90fd\u5df2\u5e72\u5f97\u53d1\u71e5\u4e86\uff0c\u5979\u56de\u8f6c\u8eab\u60f3\u53bb\u62ff\u4e9b\u6c34\u6765\uff0c\u6da6\u6da6\u4ed6\u4eec\u7684\u5634\u5507\u3002 \u3000\u3000\u4f46\u5979\u4e00\u56de\u8eab\uff0c\u5374\u53c8\u662f\u4e00\u60ca\uff01 \u3000\u3000\u539f\u6765\u5148\u524d\u90a3\u4f4d\u5947\u4eba\uff0c\u6b64\u523b\u53c8\u51b7\u7136\u7ad9\u5728\u5979\u8eab\u540e\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u9b3c\u9b45\u4f3c\u7684\uff01\u4ed6\u7b2c\u4e8c\u6b21\u53c8\u795e\u4e0d \u77e5\uff0c\u9b3c\u4e0d\u89c9\u5730\u51fa\u73b0\u4e86\uff0c\u50cf\u662f\u4e00\u9053\u8f7b\u70df\u3002\u65e0\u8bba\u6765\u7684\u65f6\u5019\uff0c\u6291\u6216\u662f\u53bb\u7684\u65f6\u5019\uff0c\u90fd\u7edd\u5bf9\u6ca1\u6709\u4e00\u4e1d\u58f0 \u606f\u3002 \u3000\u3000\u5b59\u654f\u5fcd\u4f4f\u4e86\u5c06\u8981\u53d1\u51fa\u6765\u7684\u60ca\u547c\u4e4b\u58f0\uff1a\u201c\u524d\u8f88\u2026\u2026\u201d\u8fd9\u662f\u5979\u5728\u89c1\u5230\u8fd9\u4eba\u4e4b\u540e\uff0c\u7b2c\u4e00\u6b21\u80fd\u591f \u8bf4\u51fa\u8bdd\u6765\uff0c\u4f46\u4ec5\u4ec5\u8bf4\u4e86\u8fd9\u4e24\u5b57\uff0c\u5c31\u88ab\u90a3\u4eba\u76ee\u5149\u4e2d\u6240\u53d1\u51fa\u7684\u4e00\u79cd\u5149\u8292\u6b62\u4f4f\u4e86\uff0c\u65e0\u6cd5\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u671b\u7740\u4ed6\u7684\u773c\u775b\uff0c\u50cf\u662f\u8981\u7a92\u606f\u4f3c\u7684\uff0c\u8fde\u624b\u6307\u90fd\u65e0\u6cd5\u52a8\u5f39\u4e00\u4e0b\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u53ef\u4ee5\u7edd\u5bf9\u5730\u5f71\u54cd\u5230\u51e1\u662f\u770b\u5230\u4ed6\u7684\u4eba\uff0c\u800c\u6b64\u4eba\u4fbf\u662f\u5c5e\u4e8e\u8fd9\u4e00\u79cd\u4eba\u3002 \u3000\u3000\u201c\u6211\u662f\u6765\u6551\u4f60\u7684\uff0c\u4e0d\u662f\u6765\u66ff\u4f60\u627e\u9ebb\u70e6\u7684\u2026\u2026\u201d \u3000\u3000\u4ed6\u5411\u5b8b\u8001\u5200\u548c\u5c0f\u738b\u5934\u7684\u5c38\u8eab\u4e00\u6307\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f46\u662f\u8fd9\u4e24\u5177\u5c38\u4f53\uff0c\u5374\u4e00\u5b9a\u4f1a\u66ff\u4f60\u627e\u6765\u9ebb\u70e6\u3002\u201d \u3000\u3000\u4ed6\u4ecd\u7136\u662f\u90a3\u79cd\u51b7\u51b0\u51b0\u7684\u8bed\u6c14\u3002\u4f46\u662f\u5b59\u654f\u5374\u4f3c\u4e4e\u4ece\u4ed6\u8fd9\u79cd\u51b7\u51b0\u51b0\u7684\u8bed\u8c03\u91cc\uff0c\u5bfb\u627e\u5230\u4e00\u4efd\u6e29 \u6696\u3002 \u3000\u3000\u4e8e\u662f\u5979\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u524d\u8f88\uff01\u201d \u3000\u3000\u7b49\u5979\u8bf4\u5b8c\u4e86\u8bdd\uff0c\u5979\u624d\u604d\u7136\u53d1\u89c9\u5728\u6700\u8fd1\u51e0\u5e74\u6765\uff0c\u8fd9\u8fd8\u662f\u5979\u7b2c\u4e00\u6b21\u7b11\u51fa\u6765\u54e9\uff01 \u3000\u3000\u90a3\u4eba\u76ee\u5149\u4e00\u8f6c\uff0c\u4f3c\u4e4e\u5728\u907f\u5f00\u5979\u773c\u4e2d\u7684\u90a3\u4efd\u6e29\u6696\u7684\u7b11\u610f\u3002 \u3000\u3000\u201c\u53d7\u4e86\u4f24\uff01\u201d\u4ed6\u7b80\u77ed\u5730\u95ee\u9053\u3002\u5b59\u654f\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u5e8a\u524d\uff0c\u6380\u5f00\u4f0a\u98ce\u7684\u88ab\uff0c\u626b\u76ee\u4e00\u671b\uff0c\u7565\u4e3a\u63a2\u4e86\u63a2\u8109\u606f\uff0c\u4e24\u9053\u957f\u800c\u6d53\u7684\u5251\u7709\uff0c\u5fae\u5fae\u76b1 \u4e86\u76b1\u3002 \u3000\u3000\u5b59\u654f\u5173\u5207\u5730\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u6551\u5417\uff1f\u201d \u3000\u3000\u4ed6\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u5e76\u4e0d\u5f88\u5feb\u5730\u56de\u7b54\uff0c\u5374\u9053\uff1a\u201c\u4ed6\u6b66\u529f\u4e0d\u5f31\uff0c\u4f46\u662f\u4f24\u7684\u4e5f\u5f88\u91cd\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u77aa\u5728\u5b59\u654f\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff01\u201d \u3000\u3000\u5b59\u654f\u53c8\u5728\u5fc3\u4e2d\u8f6c\u4e86\u51e0\u8f6c\uff0c\u201c\u6211\u8be5\u4e0d\u8be5\u5c06\u6211\u771f\u5b9e\u6765\u5386\u544a\u8bc9\u4ed6\u5462\uff01\u201d\u62ac\u5934\u518d\u671b\u4e86\u4ed6\u90a3\u51b7\u7136\u7684 \u76ee\u5149\u4e00\u671b\uff0c\u575a\u5b9a\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5148\u592b\u51cc\u5317\u4fee\u2026\u2026\u201d \u3000\u3000\u5979\u5c06\u81ea\u5df1\u7684\u8eab\u4efd\u548c\u5979\u4eec\u6240\u7ecf\u5386\u7684\u4e8b\uff0c\u5b8c\u5168\u5728\u8fd9\u5979\u8fde\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\u7684\u964c\u751f\u4eba\u9762\u524d\uff0c\u8bf4\u4e86\u51fa \u6765\u3002 \u3000\u3000\u4e8e\u662f\u5979\u7684\u773c\u775b\u53c8\u5df2\u7ecf\u6f6e\u6e7f\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4eba\u7684\u9762\u524d\uff0c\u5979\u7a81\u7136\u611f\u89c9\u5230\u81ea\u5df1\u53ea\u662f\u4e00\u4e2a\u8f6f\u5f31\u7684\u5973\u5b50\uff0c\u5979\u9700\u8981\u4e00\u53cc\u5f3a\u800c\u6709\u529b\u7684\u624b\uff0c\u518d \u6765\u4fdd\u62a4\u5979\uff0c\u5c31\u50cf\u4ee5\u524d\u51cc\u5317\u4fee\u4fdd\u62a4\u5979\u4e00\u6837\uff0c\u8fd9\u79cd\u611f\u89c9\u7684\u7531\u6765\uff0c\u8fde\u5979\u81ea\u5df1\u90fd\u832b\u7136\u3002 \u3000\u3000\u90a3\u4eba\u542c\u5979\u8bf4\u7740\uff0c\u6ca1\u6709\u53d1\u51fa\u4efb\u4f55\u4e00\u70b9\u58f0\u97f3\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u800c\u4e0a\u4ecd\u662f\u6beb\u65e0\u8868\u60c5\uff0c\u7136\u800c\u4ed6\u90a3\u575a\u5b9a \u7684\u76ee\u5149\uff0c\u5374\u4e5f\u8d77\u4e86\u6ce2\u52a8\u3002 \u3000\u3000\u201c\u5929\u4e89\u6559\uff01\u201d\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u600e\u5730\u6211\u8fd1\u6765\u603b\u662f\u542c\u5230\u8fd9\u4e2a\u540d\u5b57\uff1f\u201d \u3000\u3000\u7a81\u7136\u8bed\u950b\u4e00\u8f6c\uff0c\u6307\u7740\u660f\u8ff7\u4e0d\u9192\u7684\u4f0a\u98ce\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u4e2a\u4eba\u53eb\u505a\u4ec0\u4e48\u540d\u5b57\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053 \u5417\uff1f\u201d \u3000\u3000\u5b59\u654f\u70b9\u4e86\u9edc\u5934\u3002 \u3000\u3000\u90a3\u4eba\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u4e5f\u96be\u5f97\u7684\u5f88\uff01\u201d\u7565\u4e00\u505c\u987f\uff0c\u53c8\u9053\uff1a\u201c\u78b0\u5230\u6211\uff0c\u8fd9\u4e5f\u7b97\u4ed6\u8fd0\u6c14\uff0c \u4ed6\u8eab\u53d7\u4e24\u5904\u91cd\u4f24\uff0c\u53c8\u7ecf\u8fc7\u8fd9\u4e48\u4e9b\u65e5\u5b50\u7684\u5954\u6ce2\uff0c\u53d7\u4f24\u7684\u786e\u5f88\u91cd\u3002\u201d \u3000\u3000\u201c\u8bf7\u524d\u8f88\u65e0\u8bba\u5982\u4f55\u6551\u6551\u4ed6\u4eec\uff01\u201d\u5b59\u654f\u51c4\u695a\u5730\u8bf4\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u4ee5\u4e00\u79cd\u7c7b\u4f3c\u75db\u54ed\u7684\u58f0\u97f3\uff0c\u7ed3\u675f\u4e86\u5979\u7684\u8bdd\u3002 \u3000\u3000\u90a3\u4eba\u53c8\u6c89\u541f\u534a\u664c\uff0c\u7a81\u7136\u9053\uff1a\u201c\u4f60\u4ee5\u540e\u4e0d\u8981\u53eb\u6211\u524d\u8f88\u3002\u201d\u4ed6\u53c8\u505c\u987f\u4e00\u4e0b\uff0c\u50cf\u662f\u8003\u8651\u7740\u8be5\u4e0d \u8be5\u8bf4\u51fa\u4ed6\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u5728\u8fd9\u505c\u987f\u7684\u4e00\u6bb5\u65f6\u95f4\u91cc\uff0c\u5b59\u654f\u70ed\u5207\u5e0c\u671b\u4ed6\u80fd\u8bf4\u51fa\u4ed6\u7684\u540d\u5b57\u6765\uff0c\u56e0\u4e3a\u6b64\u523b\uff0c\u4e0d\u5982\u600e\u7684\uff0c\u5979 \u5bf9\u8fd9\u4eba\u7adf\u6709\u8bf4\u4e0d\u51fa\u7684\u5173\u5207\u3002 \u3000\u3000\u524d\u4eba\u90fd\u53eb\u6211\u5251\u5148\u751f\uff0c\u4f60\u2014\u2014\u4f60\u4e0d\u59a8\u4e5f\u53eb\u6211\u8fd9\u4e2a\u540d\u5b57\u5427\uff01\u201d \u3000\u3000\u4ed6\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u9053\uff0c\u50cf\u662f\u4efb\u4f55\u4e00\u4e2a\u666e\u901a\u4eba\uff0c\u5728\u8bf4\u4ec0\u4e48\u7684\u540d\u5b57\u65f6\u7684\u795e\u6001\u3002 \u3000\u3000\u7136\u800c\u201c\u5251\u5148\u751f\u201d\u8fd9\u4e09\u4e2a\u5b57\uff0c\u5374\u4f7f\u5f97\u5b59\u654f\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u5979\u60ca\u5f02\u5730\u671b\u7740\u5979\u9762\u524d\u7684 \u8fd9\u4e2a\u5947\u4eba\uff0c\u5fc3\u4e2d\u5374\u6709\u5982\u4e00\u4e2a\u987d\u7ae5\u65e0\u610f\u4e2d\u786e\u5b9a\u4e86\u88ab\u4ed6\u9047\u5230\u7684\u4e00\u4eba\uff0c\u7adf\u662f\u4ed6\u6240\u770b\u8fc7\u7684\u7ae5\u8bdd\u4e2d\u7684\u82f1 \u96c4\u4e00\u6837\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u5251\u5148\u751f\u201d\u8fd9\u4e09\u5b57\uff0c\u4e8c\u5341\u5e74\u6765\u5728\u6b66\u6797\u6240\u7684\u4ee3\u8868\u7684\u610f\u601d\uff0c\u5c31\u662f\u795e\u79d8\uff0c\u795e\u5947\u548c\u795e\u5723\u7684\u6df7 \u5408\uff01\u800c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u4eba\u4eec\u53ea\u542c\u5230\u4ed6\u6240\u505a\u8fc7\u7684\u5947\u4e8b\uff0c\u548c\u4ed6\u7684\u4fa0\u4e49\u884c\u4e3a\uff0c\u5374\u4ece\u6765\u6ca1\u6709\u4eba\u80fd\u548c\u4ed6\u9762 \u5bf9\u9762\u5730\u8bf4\u8bdd\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u5b59\u654f\u6b64\u65f6\u7684\u5fc3\u60c5\uff0c\u5c31\u5f88\u5bb9\u6613\u4e86\u89e3\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e5f\u548c\u5927\u591a\u6570\u4eba\u4e00\u6837\uff0c\u65e9\u5c31\u542c\u5230\u8fc7\u201c\u5251\u5148\u751f\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u5979\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u80fd\u78b0\u5230 \u4ed6\uff01\u4e5f\u66f4\u60f3\u4e0d\u5230\u9762\u524d\u8fd9\u770b\u6765\u6781\u4e3a\u5e74\u8f7b\u7684\u4eba\uff0c\u7adf\u662f\u4e8c\u5341\u591a\u5e74\u6765\uff0c\u88ab\u6b66\u6797\u4e2d\u4eba\u89c6\u4e3a\u5251\u4ed9\u4e00\u6d41\u4eba\u7269 \u7684\u201c\u4e07\u5251\u4e4b\u5c0a\u201d\u5251\u5148\u751f\uff01 \u3000\u3000\u6597\u5ba4\u4e2d\u500f\u7136\u9759\u5bc2\u8d77\u6765\uff0c\u7136\u800c\u7a97\u5916\u5374\u5df2\u6709\u96c4\u9e21\u7684\u557c\u58f0\uff01 \u3000\u3000\u5251\u5148\u751f\u773c\u4e2d\u6cdb\u8d77\u4e00\u4e1d\u96be\u4ee5\u89c9\u5bdf\u7684\u7b11\u610f\uff0c\u7136\u800c\u8138\u4e0a\u5374\u4ecd\u7136\u662f\u90a3\u79cd\u65e0\u52a8\u4e8e\u8877\u7684\u795e\u8272\uff0c\u5f77\u4f5b\u662f \u4e16\u95f4\u6ca1\u6709\u4efb\u4f55\u4eba\u6216\u4efb\u4f55\u4e8b\uff0c\u53ef\u4ee5\u611f\u52a8\u4ed6\u4f3c\u7684\u3002 \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u53d7\u8fc7\u5f88\u6df1\u7684\u523a\u6fc0\u3002\u201d\u5b59\u654f\u76f4\u89c9\u5730\u60f3\u5230\u3002\u773c\u5149\u81ea\u4ed6\u8138\u4e0a\u6e9c\u4e0b\uff0c\u53d1\u89c9\u4ed6\u5728\u8fd9\u4e48\u51b7\u7684 \u5929\u6c14\u91cc\uff0c\u7a7f\u7740\u7684\u4e0d\u8fc7\u662f\u4ef6\u5939\u8863\u3002 \u3000\u3000\u201c\u6b64\u5730\u5df2\u4e0d\u80fd\u4e45\u7559\u3002\u201d\u5251\u5148\u751f\u9053\uff1a\u201c\u6211\u4e5f\u662f\u56db\u5904\u98d8\u6e38\uff0c\u6ca1\u6709\u4e00\u4e2a\u56fa\u5b9a\u7684\u4f4f\u6240\uff0c\u4e0d\u8fc7\u6211\u53ef \u4ee5\u5c06\u4f60\u4eec\u5e26\u5230\u6211\u7684\u4e00\u4e2a\u81f3\u53cb\u4e4b\u5904\u3002\u201d \u3000\u3000\u5b59\u654f\u6697\u5fd6\uff1a\u201c\u539f\u6765\u4ed6\u4e5f\u662f\u6709\u670b\u53cb\u7684\u3002\u201d \u3000\u3000\u5374\u542c\u5f97\u5251\u5148\u751f\u53c8\u9053\uff1a \u3000\u3000\u201c\u90a3\u6240\u5728\u79bb\u6b64\u5e76\u4e0d\u751a\u8fdc\uff0c\u6211\u4eec\u5148\u5230\u90a3\u91cc\uff0c\u6cbb\u597d\u8fd9\u4e24\u4eba\u7684\u4f24\u518d\u8bf4\u3002\u201d\u4ed6\u8bf4\u5f97\u6781\u5feb\u3002 \u3000\u3000\u7136\u800c\u5728\u4ed6\u5fc3\u4e2d\uff0c\u5374\u95ea\u8fc7\u4e00\u70b9\u4ed6\u5df2\u7ecf\u591a\u5e74\u6765\u6ca1\u6709\u7684\u611f\u89c9\u3002\u201c\u6211\u600e\u4f1a\u53c8\u60f9\u6765\u4e86\u8fd9\u4e9b\u9ebb \u70e6.\uff0e\u201d\u4ed6\u6697\u81ea\u602a\u7740\u81ea\u5df1\u3002 \u3000\u3000\u6b63\u5982\u5b59\u654f\u6240\u6599\uff0c\u8fd9\u6b66\u6797\u4e2d\u7684\u5947\u4eba\u201c\u5251\u5148\u751f\u201d\uff0c\u786e\u662f\u53d7\u8fc7\u5f88\u6df1\u7684\u523a\u6fc0\uff0c\u662f\u4ee5\u591a\u5e74\u6765\u4ed6\u7edd\u6ca1 \u6709\u548c\u4efb\u4f55\u4e00\u4eba\uff0c\u8bf4\u8fc7\u8fd9\u4e48\u591a\u7684\u8bdd\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u81ea\u5df1\u4e5f\u5728\u5947\u602a\u7740\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u8fd9\u4e2a\u5973\u5b50\u8fd9\u4e48\u5173\u5207\uff1f\u4ed6\u5916\u8868\u770b\u6765\u5e74\u7eaa\u867d\u4e0d\u5927\uff0c\u7136\u800c \u90a3\u4e0d\u8fc7\u662f\u56e0\u4e3a\u4ed6\u5176\u6df1\u5982\u6d77\u7684\u5185\u529f\u6240\u81f4\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u8ba4\u4e3a\u81ea\u5df1\u5df2\u7ecf\u5230\u4e86\u5fd8\u5374\u201c\u7537\u5973\u4e4b\u60c5\u201d\u7684\u5e74\u9f84\u3002 \u3000\u3000\u7136\u800c\u4e16\u4e8b\u5374\u5982\u6b64\u5947\u602a\uff1a\u5728\u4f60\u8ba4\u4e3a\u5df2\u7ecf\u7edd\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\uff0c\u5374\u5f80\u5f80\u662f\u6700\u53ef\u80fd\u7684\uff01 \u3000\u3000\u4ed6\u671d\u7a97\u5916\u671b\u4e86\u4e00\u773c\uff0c\u90a3\u5c0f\u7a97\u7684\u7a97\u7eb8\uff0c\u7adf\u5df2\u73b0\u51fa\u9c7c\u767d\u8272\u4e86\uff0c\u751a\u81f3\u8fd8\u6709\u4e9b\u5149\u7ebf\u5c04\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u518d\u770b\u4e86\u90a3\u4e24\u5177\u5c38\u8eab\u548c\u90a3\u88ab\u4ed6\u70b9\u4e2d\u7a74\u9053\u7684\u5e97\u638c\u67dc\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4f1a\u5957\u8f66\u5417\uff1f\u201d \u3000\u3000\u5b59\u654f\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u60f3\u8fd9\u4eba\u771f\u662f\u5947\u602a\uff0c\u65e2\u7136\u5e2e\u4e86\u4eba\u5bb6\u7684\u5fd9\uff0c\u5374\u53eb\u4eba\u5bb6\u5973\u5b50\u53bb\u5957\u8f66\u3002 \u3000\u3000\u201c\u6211\u5c06\u8fd9\u4e24\u5177\u5c38\u8eab\u4e22\u6389\uff0c\u4f60\u5feb\u53bb\u5957\u8f66\uff01\u8fd8\u6709\u8fd9\u53ae\u867d\u88ab\u6211\u70b9\u4e2d\u7a74\u9053\uff0c\u8033\u6735\u5374\u4ecd\u542c\u5f97\u5230\uff0c\u4e5f \u4e07\u4e07\u7559\u4ed6\u4e0d\u5f97\uff01\u201d\u4ed6\u5e73\u9759\u5730\u8bf4\u9053\u3002 \u3000\u3000\u5b59\u654f\u77e5\u9053\u5728\u4ed6\u8fd9\u5e73\u9759\u7684\u51e0\u53e5\u8bdd\u4e2d\uff0c\u53c8\u51b3\u5b9a\u4e86\u4e00\u4eba\u7684\u751f\u6b7b\u4e4b\u95f4\u65f6\uff0c\u5979\u4e5f\u604d\u7136\u4e86\u89e3\u4e86\u4ed6\u4e3a\u4ec0 \u4e48\u8981\u81ea\u5df1\u5957\u8f66\u7684\u539f\u56e0\u3002 \u3000\u3000\u4e8e\u662f\u5979\u8f6c\u8eab\u5916\u8d70\u3002 \u3000\u3000\u90a3\u77e5\u521a\u8d70\u51fa\u623f\u95e8\uff0c\u53c8\u4e0d\u7981\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\uff0c\u8e6c\uff0c\u8e6c\uff0c\u8e6c\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u773c\u4e2d\u5e26\u7740\u6050\u60e7\u4e4b\u8272\uff0c \u671b\u7740\u95e8\u5916\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e5d\u7ae0\u3000\u4e09\u5fc3\u795e\u541b>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u7ae0\u3000\u4e09\u5fc3\u795e\u541b \u3000\u3000\u5b59\u654f\u5386\u52ab\u4e4b\u4f59\uff0c\u5e26\u7740\u53d7\u4f24\u7684\u7231\u5973\u51cc\u7433\uff0c\u548c\u529b\u6bd9\u201c\u593a\u547d\u53cc\u96c4\u201d\u540e\u81ea\u5df1\u4e5f\u53d7\u4e86\u91cd\u4f24\u7684\u6551\u547d\u6069 \u4eba\uff0c\u8fde\u591c\u5954\u4e0b\u534e\u5c71\uff0c\u5728\u9669\u88ab\u8f66\u592b\u6240\u8fb1\u7684\u60c5\u51b5\u4e0b\uff0c\u5374\u9047\u89c1\u89c1\u4e86\u6b66\u6797\u4e2d\u76db\u4f20\u5df2\u4e45\u7684\u5f02\u4eba\u2014\u2014\u5251\u5148 \u751f\u3002 \u3000\u3000\u81ea\u4e09\u6e58\u5927\u4fa0\u51cc\u5317\u4fee\u4e3a\u7fa4\u5c0f\u6240\u4e58\u800c\u6b7b\u540e\uff0c\u5b59\u654f\u8fd9\u4e9b\u5e74\u6765\uff0c\u53ef\u8bf4\u662f\u5386\u5c3d\u8270\u8f9b\uff0c\u65e0\u8bba\u5728\u90a3\u4e00\u65b9 \u9762\uff0c\u90fd\u6bd4\u4ee5\u524d\u575a\u5f3a\u5f97\u591a\u3002 \u3000\u3000\u53ef\u662f\u5728\u5979\u8d70\u5230\u95e8\u53e3\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5979\u4ecd\u4e0d\u7981\u88ab\u95e8\u5916\u7684\u4e00\u4e8b\u9a87\u5f97\u8131\u53e3\u800c\u547c\u2026\u2026 \u3000\u3000\u6b64\u65f6\u6653\u8272\u65b9\u5f00\uff0c\u4f46\u95e8\u5916\u7684\u8d70\u5eca\u4ecd\u7136\u9634\u6697\u5f97\u5f88\uff0c\u5899\u89d2\u660f\u9ec4\u7684\u706f\u7b3c\u72b9\u81ea\u6709\u5149\uff0c\u5728\u8fd9\u79cd\u5149\u7ebf \u4e0b\uff0c\u8d70\u5eca\u91cc\u5f53\u95e8\u800c\u7acb\u7ad9\u7740\u4e00\u6761\u4eba\u5f71\uff0c\u4f9d\u7a00\u671b\u53bb\uff0c\u8fd9\u6761\u4eba\u5f71\u8eab\u4e0a\u7a7f\u7740\u7684\u8863\u886b\uff0c\u8d6b\u7136\u4ea6\u662f\u91d1\u8272\u3002 \u3000\u3000\u5b59\u654f\u60ca\u5f13\u4e4b\u9e1f\uff0c\u81ea\u7136\u96be\u514d\u9a87\u6781\u800c\u547c\u3002 \u3000\u3000\u5c31\u5728\u5979\u60ca\u547c\u7684\u5c3e\u97f3\u65b9\u4f4f\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u201c\u5251\u5148\u751f\u201d\u7626\u957f\u7684\u8eab\u9a71\uff0c\u5df2\u5982\u7535\u706b\u4e00\u95ea\u63a0\u4e86\u8fc7\u6765\uff0c \u4f4e\u559d\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8fd9\u4f4e\u6c88\u800c\u575a\u5b9a\u7684\u58f0\u97f3\uff0c\u7acb\u523b\u5e26\u7ed9\u5979\u6781\u5927\u7684\u5b89\u5168\u4e4b\u611f\uff01 \u3000\u3000\u4f46\u662f\u5979\u7684\u76ee\u5149\uff0c\u4ecd\u4e0d\u7981\u60ca\u9a87\u5730\u671b\u7740\u90a3\u6761\u4eba\u5f71\u2014\u2014\u7a7f\u7740\u91d1\u886b\u7684\u4eba\u5f71\u3002 \u3000\u3000\u201c\u96be\u9053\u5929\u4e89\u6559\u7adf\u771f\u7684\u5982\u6b64\u795e\u901a\u75d2\u5927\u3002\u201d\u5979\u6697\u5fd6\u7740\uff1a\u201c\u6211\u8fd9\u6837\u9690\u85cf\u81ea\u5df1\u7684\u884c\u8ff9\uff0c\u600e\u5730\u8fd8\u662f \u88ab\u4ed6\u4eec\u8ffd\u8e2a\u800c\u6765\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u53c8\u5fd6\u9053\uff1a\u201c\u53ef\u662f\u6211\u53c8\u4f55\u5fc5\u5bb3\u6015\u5462\uff1f\u6211\u65c1\u8fb9\u7ad9\u7740\u7684\u8fd9\u4eba\u2026\u2026\u201d \u3000\u3000\u5979\u4fa7\u76ee\u53bb\u770b\u201c\u5251\u5148\u751f\u201d\uff0c\u90a3\u4f4d\u6b66\u6797\u5f02\u4eba\u6b63\u4ee5\u4ed6\u90a3\u79cd\u60ef\u6709\u7684\u51b7\u9759\u4e4b\u6001\uff0c\u51dd\u76ee\u95e8\u5916\uff0c\u4ed6\u6c38\u8fdc \u8ba9\u4eba\u5bb6\u65e0\u6cd5\u731c\u900f\u4ed6\u7684\u5fc3\u610f\u3002 \u3000\u3000\u90a3\u6761\u4eba\u5f71\u6b64\u523b\u53c8\u5411\u4ed6\u4eec\u7f13\u7f13\u8d70\u6765\uff0c\u5c45\u7136\u4e5f\u662f\u51b0\u5c71\u822c\u5730\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\u9732\u51fa\u3002\u76f4\u5230\u4ed6\u9762\u5bf9\u9762 \u5730\u7ad9\u5728\u201c\u5251\u5148\u751f\u201d\u9762\u524d\uff0c\u5b59\u654f\u7adf\u4ece\u4ed6\u90a3\u4e5f\u662f\u82cd\u767d\u5f97\u6ca1\u6709\u4e00\u4e1d\u8840\u8272\u7684\u8138\u5b54\u4e0a\uff0c\u770b\u5230\u4e00\u4e1d\u7b11\u5bb9\u3002 \u3000\u3000\u5979\u518d\u4e00\u671b\u201c\u5251\u5148\u751f\u201d\uff0c\u5374\u89c1\u8fd9\u5947\u4fa0\u8138\u4e0a\u4e5f\u6b63\u6709\u4e00\u4e1d\u76f8\u540c\u7684\u7b11\u5bb9\u6162\u6162\u6cdb\u8d77\u3002\u5979\u5fc3\u91cc\u4e0d\u7981\u5947 \u602a\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u7adf\u662f\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u540d\u95fb\u6b66\u6797\u7684\u4e07\u5251\u4e4b\u5c0a\uff0c\u53c8\u600e\u4f1a\u548c\u5929\u4e89\u6559\u5f92\u662f\u670b\u53cb\uff1f\u201d\u5979\u53c8\u7981\u60ca\u614c\u8d77\u6765\uff1a\u201c\u96be\u9053\u8fd9 \u6614\u5e74\u4ee5\u4e00\u67c4\u94c1\u5251\uff0c\u8fde\u95ef\u6b66\u6797\u4e03\u5927\u5251\u6d3e\u6240\u5e03\u4e0b\u7684\u4e5d\u79cd\u5251\u9635\u7684\u5f02\u4eba\uff0c\u4e5f\u548c\u5929\u4e89\u6559\u6709\u7740\u4ec0\u4e48\u5173\u8fde \u5417\uff1f\u201d \u3000\u3000\u987b\u77e5\u5979\u8eab\u5904\u5883\uff0c\u81ea\u7136\u4ec0\u4e48\u4e8b\u90fd\u4f1a\u5f80\u6700\u574f\u7684\u90a3\u4e00\u65b9\u9762\u53bb\u60f3\uff0c\u4e8e\u662f\u5979\u6084\u6084\u8ba9\u5f00\u4e24\u6b65\uff0c\u76ee\u5149\u5374 \u7d27\u7d27\u5730\u7559\u610f\u7740\u4ed6\u4eec\u7684\u52a8\u6001\u3002 \u3000\u3000\u84e6\u5730\uff0c\u5251\u5148\u751f\u548c\u90a3\u91d1\u886b\u4eba\u540c\u65f6\u4f38\u51fa\u4e86\u624b\uff0c\u7d27\u7d27\u63e1\u5728\u4e00\u8d77\u3002 \u3000\u3000\u201c\u5440\uff01\u4ed6\u4eec\u679c\u7136\u662f\u670b\u53cb\u3002\u201d\u5b59\u654f\u4e3a\u81ea\u5df1\u786e\u5b9a\u7740\uff0c\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5df2\uff0c\u4e0d\u77e5\u9053\u53c8\u4f1a\u6709\u4ec0\u4e48\u5669\u8fd0 \u8981\u843d\u5728\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4e24\u4eba\u7d27\u63e1\u7740\u7684\u624b\u7adf\u4ecd\u672a\u5206\u5f00\uff0c\u4ed6\u4eec\u90a3\u540c\u6837\u82cd\u767d\u7684\u9762\u5e9e\u4e0a\u6cdb\u8d77\u7684\u540c\u6837\u5730\u7b11\u5bb9\uff0c\u4e5f \u4ecd\u81ea\u6302\u5728\u5634\u89d2\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ece\u4ed6\u4eec\u90a3\u56db\u53ea\u6ee1\u805a\u795e\u5149\u7684\u773c\u775b\u91cc\uff0c\u5374\u53ef\u4ee5\u770b\u5230\u4ed6\u4eec\u7684\u51dd\u91cd\u4e4b\u6001\uff0c\u65e2\u50cf\u662f\u4e45\u522b\u91cd\u9022 \u7684\u6545\u53cb\uff0c\u5374\u53c8\u50cf\u662f\u4e92\u7ed3\u6df1\u4ec7\u7684\u654c\u4eba\u3002 \u3000\u3000\u8fd9\u5374\u8ba9\u5b59\u654f\u8d8a\u53d1\u4e0d\u61a7\u4e86\u3002 \u3000\u3000\u826f\u4e45\uff0c\u90a3\u4e2a\u91d1\u886b\u4eba\u5634\u89d2\u7684\u7b11\u5bb9\uff0c\u6e10\u6e10\u6d88\u5931\uff0c\u800c\u5c06\u8584\u800c\u51b7\u5ced\u7684\u5634\u5507\uff0c\u7d27\u95ed\u6210\u4e00\u9053\u5f27\u7ebf\uff0c\u5634 \u89d2\u5fae\u5fae\u4e0b\u5782\uff0c\u50cf\u662f\u91cc\u9762\u7684\u7259\u9f7f\u4e5f\u5728\u7d27\u7d27\u54ac\u7740\u3002 \u3000\u3000\u5b59\u654f\u8d76\u7d27\u518d\u53bb\u770b\u5251\u5148\u751f\u9762\u4e0a\u7684\u795e\u60c5\uff0c\u5374\u89c1\u4ed6\u8138\u4e0a\u7684\u7b11\u5bb9\u4ecd\u81ea\u672a\u6d88\uff0c\u5979\u6697\u81ea\u677e\u4e86\u53e3\u6c14\u3002\u56e0 \u4e3a\u5979\u77e5\u9053\uff0c\u82e5\u8fd9\u4e24\u4eba\u662f\u654c\u975e\u53cb\uff0c\u800c\u4ed6\u4eec\u4e5f\u662f\u5728\u4e92\u8f83\u5185\u529b\u800c\u975e\u63e1\u624b\u8a00\u6b22\u7684\u8bdd\uff0c\u90a3\u4e48\u7167\u76ee\u524d\u7684\u60c5 \u51b5\u770b\u6765\uff0c\u6beb\u65e0\u7591\u95ee\u7684\u662f\uff1a\u201c\u5251\u5148\u751f\u201d\u5df2\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u8fd9\u662f\u5979\u6697\u677e\u4e00\u53e3\u6c14\u7684\u539f\u56e0\u4e4b\u4e00\uff0c\u4f55\u51b5\u5979\u4ee5\u60c5\u51b5\u63e3\u6d4b\uff0c\u8fd9\u4e24\u4eba\u663e\u7136\u5728\u8f83\u91cf\u7740\u5185\u529b\uff0c\u800c\u5e76\u975e \u5979\u5148\u524d\u6240\u60f3\u7684\u63e1\u624b\u8a00\u6b22\u3002 \u3000\u3000\u5979\u9ad8\u5174\u4e4b\u4f59\uff0c\u53c8\u4e0d\u7981\u60ca\u9a87\uff01\u201c\u8fd9\u91d1\u886b\u4eba\u7684\u5185\u529b\uff0c\u7adf\u5df2\u5230\u4e86\u80fd\u548c\u201c\u4e07\u5251\u4e4b\u5c0a\u201d\u4e00\u8f83\u77ed\u957f\u7684 \u5730\u6b65\uff0c\u5929\u4e89\u6559\u4e2d\uff0c\u4f55\u6765\u5982\u6b64\u9ad8\u624b\uff1f\u201d \u3000\u3000\u5979\u5fc3\u5ff5\u9891\u8f6c\uff0c\u76ee\u5149\u518d\u843d\u56de\u201c\u5251\u5148\u751f\u201d\u8eab\u4e0a\uff0c\u5374\u89c1\u5251\u5148\u751f\u500f\u7136\u4e00\u677e\u624b\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\u76ca\u89c1\u5f00 \u6717\u3002 \u3000\u3000\u90a3\u91d1\u886b\u4eba\u5df2\u64a4\u56de\u624b\uff0c\u6014\u4e86\u7247\u523b\uff0c\u5374\u4e5f\u5f20\u53e3\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u53ef\u662f\u5b59\u654f\u89c1\u4e86\u8fd9\u4eba\u7684\u795e\u60c5\uff0c\u5374\u4e0d\u7981\u89c9\u5f97\u6709\u4e00\u9635\u51c9\u610f\uff0c\u81ea\u811a\u8ddf\u5347\u8d77\u3002 \u3000\u3000\u539f\u6765\u8fd9\u91d1\u886b\u4eba\u770b\u8d77\u6765\u867d\u662f\u7b11\u5f97\u6781\u4e3a\u5f00\u5fc3\uff0c\u7136\u800c\u5374\u7edd\u65e0\u4e00\u4e1d\u7b11\u58f0\u53d1\u51fa\uff0c\u53ea\u662f\u8138\u90e8\u7684\u808c\u8089\u626d \u66f2\u6210\u4e00\u4e2a\u7b11\u7684\u5f62\u72b6\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u4f7f\u5f97\u5b59\u654f\u51e0\u4e4e\u4ee5\u4e3a\u81ea\u5df1\u5df2\u7ecf\u53d8\u6210\u804b\u5b50\uff0c\u4f46\u662f\u522b\u7684\u58f0\u97f3\uff0c\u5979\u5374\u53c8\u53ef\u4ee5\u7167\u5e38\u542c\u5f97\u5230 \u5462\u3002 \u3000\u3000\u5b59\u654f\u609a\u6817\u4e4b\u4f59\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e0d\u7981\u6697\u7b11\u81ea\u5df1\uff1a\u201c\u6211\u867d\u4e0d\u804b\uff0c\u53ef\u662f\u4ed6\u5374\u4e00\u5b9a\u662f\u54d1\u5df4\u3002\u5509\uff01\u6211 \u600e\u4e48\u8fde\u8fd9\u70b9\u90fd\u6ca1\u6709\u60f3\u5230\u5462\uff1f\u201d \u3000\u3000\u5979\u60ca\u60b8\u4e4b\u4e0b\uff0c\u5fc3\u601d\u4e5f\u4e0d\u5927\u5982\u524d\u7075\u654f\u4e86\u3002\u4eba\u7c7b\u7684\u601d\u60f3\uff0c\u672c\u5c31\u662f\u53d7\u7740\u73af\u5883\u5f71\u54cd\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8fd9\u4e00\u76f8\u89c6\u800c\u7b11\uff0c\u5b59\u654f\u5df2\u89c9\u4e0d\u5999\u3002\u518d\u770b\u89c1\u90a3\u91d1\u886b\u4eba\u7adf\u53c8\u4e00\u5f20\u81c2\uff0c\u62e5\u4f4f\u201c\u5251\u5148\u751f\u201d\u7684 \u80a9\u5934\uff0c\u53e3\u4e2d\u5634\u76ae\u8fde\u52a8\uff0c\u50cf\u662f\u5728\u8bf4\u7740\u4ec0\u4e48\u8bdd\u3002\u5b59\u654f\u5fc3\u5934\u53c8\u4e00\u51c9\uff0c\u5148\u524d\u7684\u8bbe\u60f3\uff0c\u53c8\u5168\u90e8\u63a8\u7ffb\u3002 \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u8fd8\u662f\u670b\u53cb\uff01\u201d\u5979\u73b0\u5728\u5df2\u88ab\u4ed6\u4eec\u8fd9\u79cd\u7384\u865a\u7684\u4e3e\u52a8\uff0c\u5f04\u5f97\u975e\u5e38\u83ab\u540d\u5176\u5999\u3002\u800c\u4ed6\u4eec\u5012 \u5e95\u662f\u654c\u662f\u53cb\uff01\u5979\u518d\u4e5f\u4e0d\u80fd\u5984\u52a0\u63a8\u65ad\u3002 \u3000\u3000\u53ea\u662f\u5979\u5374\u66f4\u4e3a\u6ce8\u610f\u5730\u671b\u7740\u4ed6\u4eec\uff0c\u56e0\u4e3a\u5979\u8ba4\u4e3a\uff1a\u8fd9\u4e24\u4eba\u82e5\u662f\u670b\u53cb\uff0c\u90a3\u5979\u81ea\u8eab\u5b89\u5168\uff0c\u5c31\u53ef\u80fd \u4e0d\u4fdd\uff0c\u56e0\u4e3a\u8fd9\u91d1\u886b\u4eba\u663e\u7136\u662f\u5929\u4e89\u6559\u4e0b\u7684\u91d1\u8863\u9999\u4e3b\u5440\uff01 \u3000\u3000\u63a5\u7740\uff0c\u53e6\u4e00\u4e8b\u53c8\u4f7f\u5f97\u8fd9\u53ef\u601c\u7684\u5987\u4eba\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u7684\u773c\u775b\uff01 \u3000\u3000\u539f\u6765\u201c\u5251\u5148\u751f\u201d\u6b64\u523b\u5634\u76ae\u4e5f\u5728\u8fde\u8fde\u52a8\u7740\uff0c\u53ea\u662f\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u58f0\u97f3\u53d1\u51fa\u3002 \u3000\u3000\u5b59\u654f\u63c9\u4e86\u63c9\u81ea\u5df1\u7684\u8033\u6735\uff0c\u201c\u96be\u9053\u6211\u771f\u7684\u804b\u4e86\u5417\uff01\u201d\u5979\u6697\u81ea\u5403\u60ca\u3002\u4f46\u662f\u7a97\u5916\u4e00\u58f0\u9e21\u557c\uff0c\u5374 \u53c8\u4f7f\u5979\u8bc1\u5b9e\u4e86\u81ea\u5df1\u201c\u542c\u201d\u7684\u80fd\u529b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5979\u662f\u5b8c\u5168\u8ff7\u60d8\u4e86\uff0c\u5979\u4e0d\u77e5\u9053\u81ea\u5df1\u8be5\u600e\u4e48\u529e\uff01\u5047\u5982\u8fd9\u4e24\u4eba\u5bf9\u5979\u6709\u6076\u610f\uff0c\u90a3\u4e48\u5979\u65e0\u8bba \u5982\u4f55\u4e5f\u8dd1\u4e0d\u4e86\uff0c\u8fd9\u662f\u5979\u6781\u4e3a\u6e05\u695a\u7684\u3002\u5251\u5148\u751f\u4e00\u8f6c\u8eab\uff0c\u548c\u90a3\u91d1\u886b\u4eba\u5e76\u80a9\u8d70\u5230\u5e8a\u524d\uff0c\u4ed6\u4eec\u9762\u4e00\u80cc \u7740\u5b59\u654f\uff0c\u5b59\u654f\u66f4\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u505a\u4ec0\u4e48\u3002\u53ea\u770b\u5230\u5251\u5148\u751f\u7684\u624b\uff0c\u5f77\u4f5b\u5411\u81ea\u5df1\u6307\u4e86\u6307\uff0c\u90a3\u91d1\u886b\u4eba\u5c31 \u56de\u8fc7\u5934\uff0c\u51b7\u7136\u671b\u4e86\u5979\u4e00\u773c\u3002 \u3000\u3000\u5b59\u654f\u5fc3\u91cc\u53c8\u4e0d\u7981\u201c\u6251\u901a\u201d\u4e00\u8df3\u3002 \u3000\u3000\u8fd9\u91d1\u886b\u4eba\u7684\u4e24\u9053\u76ee\u5149\uff0c\u7adf\u6bd4\u79cb\u96e8\u4e2d\u7684\u95ea\u7535\u8fd8\u8981\u9510\u5229\uff0c\u4f7f\u5f97\u5979\u4e0d\u5f97\u4e0d\u907f\u5f00\u4eba\u5bb6\u7684\u76ee\u5149\uff0c\u754f \u7f29\u5730\u7ad9\u5728\u95e8\u53e3\u3002\u6e10\u5df2\u521a\u5f3a\u7684\u5979\uff0c\u5728\u8fd9\u8be1\u5f02\u7684\u4e24\u4f4d\u5947\u4eba\u4e4b\u524d\uff0c\u53c8\u53d8\u5f97\u50cf\u662f\u56de\u5230\u4e8c\u5341\u5e74\u524d\uff0c\u4ecd\u662f \u4e91\u82f1\u672a\u5ac1\u7684\u5f31\u5973\u90a3\u4e48\u61e6\u5f31\u4e86\uff01 \u3000\u3000\u90a3\u91d1\u886b\u4eba\u76ee\u5149\u5728\u5979\u8eab\u4e0a\u8f6c\u4e86\u51e0\u8f6c\uff0c\u7a81\u7136\u9053\uff1a \u3000\u3000\u201c\u5979\u4e09\u6839\u672c\u5f31\uff0c\u79ef\u52b3\u53c8\u91cd\uff0c\u82e5\u518d\u4e0d\u9759\u517b\uff0c\u90a3\u4e48\u5185\u5916\u4ea4\u4fb5\uff0c\u66f4\u662f\u4e0d\u6cbb\u4e4b\u75c7\uff01\u201d \u3000\u3000\u4ed6\u53c8\u4e00\u6307\u69bb\u4e0a\u7684\u4e24\u4eba\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u4eba\u53d7\u4e86\u9634\u5bd2\u638c\u529b\u6240\u4f24\uff0c\u867d\u7136\u4ed7\u7740\u6839\u57fa\u597d\uff0c\u4f46\u547d\u95e8\u4e4b\u706b\u5df2\u51b7\uff0c\u66f4\u662f\u5371\u5728\u65e6\u5915\uff01\u201d \u3000\u3000\u4e5f\u548c\u5251\u5148\u751f\u4e00\u6837\uff0c\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4ea6\u662f\u6beb\u65e0\u987f\u632b\u9ad8\u4f4e\u3002 \u3000\u3000\u4f46\u662f\u4f7f\u5b59\u654f\u60ca\u5f02\u7684\u5374\u4e0d\u662f\u8fd9\u4e9b\uff0c\u800c\u662f\u8fd9\u4e2a\u5979\u4ee5\u4e3a\u4eba\u5bb6\u662f\u54d1\u5df4\u7684\u4eba\uff0c\u7adf\u7136\u5f00\u53e3\u8bf4\u4e86\u8bdd\u3002\u8bed \u6c14\u4e4b\u4e2d\uff0c\u5bf9\u81ea\u5df1\u4e0d\u4f46\u7edd\u65e0\u6076\u610f\uff0c\u800c\u4e14\u5f77\u4f5b\u533b\u9053\u751a\u7cbe\uff0c\u50cf\u662f\u80af\u4e3a\u7231\u5973\u4ed6\u4eec\u7597\u4f24\u7684\u6837\u5b50\u3002 \u3000\u3000\u5979\u60ca\u5f02\u4e4b\u4f59\uff0c\u53c8\u89c9\u5f97\u9ad8\u5174\u5f97\u5f88\u3002\u81f3\u4e8e\u4ed6\u6240\u8bf4\u6709\u5173\u81ea\u5df1\u7684\u75c5\uff0c\u5979\u5374\u5b8c\u5168\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002\u5929 \u4e0b\u7236\u6bcd\u4e3a\u5b50\u5973\u8005\uff0c\u5f80\u5f80\u5982\u662f\u3002 \u3000\u3000\u4f46\u662f\uff0c\u90a3\u91d1\u886b\u4eba\u8bf4\u4e86\u8fd9\u4e24\u53e5\u8bdd\u540e\uff0c\u5374\u4f4f\u58c1\u4e0d\u518d\u53d1\u8a00\u3002\u5b59\u654f\u4e0d\u81ea\u89c9\u5730\u671d\u524d\u8d70\u53bb\uff0c\u8033\u7554\u5374\u542c \u5251\u5148\u751f\u7684\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u91d1\u886b\u4eba\u5c31\u662f\u6614\u5e74\u7684\u4e09\u5fc3\u795e\u541b\uff0c\u4f60\u6709\u5e78\u5f97\u9047\u89c1\u6b64\u4eba\uff0c\u4ee4\u5a9b\u548c\u90a3\u4e2a\u5e74\u8f7b\u4eba\u7684\u4f24 \u52bf\u2026\u2026\u201d \u3000\u3000\u5b59\u654f\u65b9\u542c\u5230\u6b64\u5904\uff0c\u5374\u89c1\u91d1\u886b\u4eba\u888d\u8896\u4e00\u626c\uff0c\u5251\u5148\u751f\u7684\u8bed\u58f0\u7adf\u7a81\u7136\u4e2d\u65ad\u3002\u90a3\u91d1\u886b\u4eba\u5374\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u53ae\u53c8\u5728\u56bc\u4ec0\u4e48\u820c\u5934\uff01\u6211\u8001\u4eba\u5bb6\u867d\u7136\u591a\u5e74\u6765\u4e0d\u95ee\u4eba\u95f4\u4e4b\u4e8b\uff0c\u4f46\u662f\u770b\u5728\u4f60\u7684\u9762\u4e0a\uff0c\u8fd9 \u4e24\u4eba\u6211\u4e00\u5b9a\u7ba1\u4e86\u5c31\u662f\u3002\u201d \u3000\u3000\u4ed6\u5634\u89d2\u53c8\u6cdb\u8d77\u7b11\u5bb9\uff0c\u4f46\u8bed\u58f0\u4e2d\u5374\u4ecd\u65e0\u7b11\u610f\u3002 \u3000\u3000\u800c\u5b59\u654f\u6b64\u523b\u5fc3\u4e2d\uff0c\u5374\u95ea\u7535\u822c\u8f6c\u8fc7\u65e0\u6570\u5ff5\u5934\uff1a \u3000\u3000\u201c\u5440\uff01\u6b64\u4eba\u7adf\u662f\u4e09\u5fc3\u795e\u541b\uff01\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u5929\u4e89\u6559\u7684\u91d1\u8863\u9999\u4e3b\u5462\u3002\u6211\u771f\u662f\u7b28\uff01\u96be\u9053\u6240\u6709\u7a7f \u91d1\u886b\u7684\u4eba\uff0c\u90fd\u662f\u5929\u4e89\u6559\u4e0b\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u771f\u5e78\u8fd0\uff0c\u5c45\u7136\u5728\u540c\u4e00\u5929\u665a\u4e0a\uff0c\u9047\u89c1\u4e86\u4e24\u4e2a\u6b66\u6797\u4e2d\u53ea\u95fb\u5176\u540d\uff0c\u5374\u6781\u5c11\u4eba\u6709\u7f18\u4e00\u89c1\u7684\u5947 \u4eba\uff01\u5c24\u5176\u8fd9\u4e09\u5fc3\u795e\u541b\uff0c\u6b66\u529f\u867d\u7edd\u9ad8\uff0c\u884c\u4e8b\u5374\u53cd\u8986\u65e0\u5e38\uff0c\u8fd9\u5c31\u662f\u4eba\u5bb6\u4e3a\u4ec0\u4e48\u53eb\u4ed6\u201c\u4e09\u5fc3\u795e\u541b\u201d \u7684\u539f\u56e0\u3002\u800c\u4e14\u6b66\u6797\u4f20\u8bf4\uff0c\u6b64\u4eba\u9664\u4e86\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\u5916\uff0c\u8bd7\u8bcd\u7edd\u5999\uff0c\u533b\u672f\u66f4\u662f\u901a\u795e\uff0c\u51e0\u4e4e\u5df2\u6709\u8d77 \u6b7b\u56de\u751f\u4e4b\u529b\uff01\u7433\u513f\u548c\u90a3\u4f4d\u5e74\u8f7b\u4e49\u58eb\uff0c\u6709\u4e86\u4ed6\u7684\u5e2e\u5fd9\uff0c\u5927\u6982\u4e0d\u4f1a\u6709\u4ec0\u4e48\u95ee\u9898\u4e86\uff01\u201d \u3000\u3000\u6b64\u523b\u5979\u5fc3\u4e2d\u7684\u6b23\u559c\uff0c\u771f\u662f\u96be\u4ee5\u5f62\u5bb9\uff01\u62ac\u5934\u4e00\u671b\uff0c\u8fd9\u4e24\u4f4d\u5947\u4eba\u53c8\u5728\u5fae\u7b11\u7740\u8bf4\u8bdd\uff0c\u4f46\u662f\u4ed6\u4eec \u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u81ea\u5df1\u4ecd\u7136\u4e00\u53e5\u4e5f\u542c\u4e0d\u5230\uff0c\u5979\u5fc3\u4e2d\u53c8\u4e00\u60ca\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u5df2\u5c06\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185 \u529f\uff0c\u7ec3\u5230\u968f\u610f\u53ef\u4ee5\u63a7\u5236\u81ea\u5df1\u58f0\u97f3\u7684\u5883\u754c\u5417\uff1f \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u7ae0\u3000\u7ec8\u5357\u5c71\u53bb>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u7ae0\u3000\u7ec8\u5357\u5c71\u53bb \u3000\u3000\u4e09\u5fc3\u795e\u541b\u548c\u5251\u5148\u751f\uff0c\u4e92\u4ee5\u5185\u5bb6\u7edd\u9876\u529f\u592b\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u8bf4\u8bdd\uff0c\u5012\u5e76\u4e0d\u662f\u4e0d\u613f\u8ba9\u5b59\u654f\u542c\u5230\uff0c \u800c\u4ec5\u4ec5\u662f\u4ed6\u4eec\u751f\u6027\u5982\u6b64\uff0c\u9ad8\u5174\u8fd9\u4e48\u505a\u800c\u5df2\u3002\u4ed6\u4eec\u6240\u8bf4\u7684\u8bdd\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e92\u9053\u8fd9\u6570\u5341\u5e74\u7684\u7ecf\u8fc7\u7f62 \u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5b59\u654f\u5374\u4e0d\u8fd9\u4e48\u60f3\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\u8bdd\u5462\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u542c\u5230\uff1f\u201d \u3000\u3000\u5979\u6697\u5bf8\u7740\uff1a\u6b64\u523b\u5979\u82e5\u6709\u4e09\u5fc3\u795e\u541b\u7684\u529f\u529b\uff0c\u4e5f\u4f1a\u4e00\u638c\u9707\u6563\u4ed6\u4eec\u7684\u58f0\u6ce2\u3002 \u3000\u3000\u5979\u5782\u7740\u5934\uff0c\u56e0\u4e3a\u5979\u4e0d\u6562\u53bb\u63a5\u89e6\u4eba\u5bb6\u7684\u76ee\u5149\u3002\u800c\u5979\u8138\u4e0a\u6240\u5e26\u7740\u7684\u90a3\u79cd\u4f3c\u559c\u4f3c\u6028\u7684\u6de1\u6de1\u5fe7\u90c1 \u4e4b\u8272\uff0c\u4efb\u4f55\u4eba\u89c1\u4e86\uff0c\u90fd\u4e0d\u514d\u751f\u601c\uff0c \u3000\u3000\u5251\u5148\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u53ea\u662f\u4ed6\u7684\u7b11\u5bb9\uff0c\u5374\u5f88\u96be\u88ab\u4eba\u5bb6\u53d1\u73b0\u3002 \u3000\u3000\u201c\u4e09\u5fc3\u795e\u541b\uff0c\u867d\u5177\u65e0\u4e0a\u795e\u901a\uff0c\u4f46\u662f\u4ed6\u4fe9\u7684\u4f24\uff0c\u5374\u4e5f\u4e0d\u662f\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u53ef\u4ee5\u533b\u6108\u7684\u3002\u201d \u4ed6\u5411\u5b59\u654f\u8bf4\u9053\uff0c\u8bed\u6c14\u5df2\u4e0d\u5982\u5148\u524d\u7684\u51b7\u6f20\u751f\u786c\u3002 \u3000\u3000\u7136\u540e\u4ed6\u76ee\u5149\u4e00\u626b\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u6211\u4eec\u4e5f\u52bf\u96be\u4e45\u7559\u3002\u201d \u3000\u3000\u4ed6\u4fa7\u76ee\u5411\u4e09\u5fc3\u795e\u541b\u9053\uff1a \u3000\u3000\u201c\u521a\u521a\u4f60\u6ca1\u6709\u6765\u7684\u65f6\u5019\uff0c\u6211\u672c\u6765\u51c6\u5907\u5c06\u4ed6\u4eec\u9001\u5f80\u7ec8\u5357\u5c71\u2014\u2014\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u7acb\u523b\u6253\u65ad\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a \u3000\u3000\u201c\u7ec8\u5357\u5c71\u90a3\u8001\u725b\u9f3b\u5b50\u8fd8\u6ca1\u6709\u6b7b\u5440\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u5f7c\u6b64\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u968f\u4fbf\u5df2\u6781\uff0c\u5168\u7136\u4e0d\u9075\u5b88\u5f53\u65f6\u4e16\u4eba\u8bf4\u8bdd\u65f6\u90a3\u79cd\u5f6c\u5f6c\u6709\u793c\u7684\u89c4\u8303\uff0c \u53ea\u662f\u4efb\u610f\u8bf4\u51fa\u800c\u5df2\u3002 \u3000\u3000\u5251\u5148\u751f\u9053\uff1a \u3000\u3000\u201c\u7389\u673a\u9053\u4eba\u547d\u53ef\u6ca1\u6709\u4f60\u957f\uff0c\u4e03\u5e74\u524d\u5df2\u7ecf\u7fbd\u5316\u767b\u4ed9\u4e86\u3002\u53ef\u662f\u4ed6\u7684\u9996\u5f92\u5999\u7075\uff0c\u5374\u5df2\u662f\u7ec8\u5357\u6d3e \u7684\u638c\u95e8\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4e00\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c31\u662f\u6614\u5e74\u4f60\u6211\u5728\u7ec8\u5357\u5c71\u4e0a\u5bf9\u5955\u65f6\uff0c\u90a3\u59cb\u7ec8\u7b49\u5019\u5728\u6211\u4eec\u65c1\u8fb9\uff0c\u4f60\u4ee5\u4e2d\u62bc\u80dc\u4e86\u6211\u4e00\u5c40\u4e4b \u540e\uff0c\u8fd8\u4f20\u7ed9\u4ed6\u4e00\u624b\u201c\u4e94\u79bd\u8eab\u6cd5\u201d\u7684\u90a3\u4e2a\u7a1a\u9f84\u9053\u7ae5\uff0c\u73b0\u5728\u4eba\u5bb6\u5df2\u662f\u9655\u7518\u4e00\u5e26\u6b66\u6797\u4e2d\u7684\u540d\u5251\u5ba2 \u4e86\uff01\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u55ef\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5b59\u654f\u5374\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a \u3000\u3000\u201c\u53ef\u5c31\u662f\u7ec8\u5357\u5251\u5ba2\u7384\u95e8\u4e00\u9e64\u5999\u7075\u9053\u4eba\u5417\uff1f\u201d \u3000\u3000\u5251\u5148\u751f\u5fae\u4e00\u9894\u9996\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u8001\u5b9e\u8bf4\uff0c\u8fd9\u4e24\u4eba\u53d7\u4f24\u592a\u91cd\uff0c\u6211\u4e5f\u675f\u624b\u65e0\u7b56\uff0c\u60f3\u5230\u90a3\u5999\u7075\u9053\u4eba\uff0c\u6614\u5e74\u4ece\u4f60\u5904\u4e5f\u5b66\u4e86\u4e0d\u5c11 \u533b\u9053\uff0c\u672c\u6765\u60f3\u5230\u4ed6\u90a3\u91cc\u4e00\u8bd5\uff0c\u53ef\u662f\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u5f92\u5f1f\u8fd8\u6ca1\u6709\u89c1\u7740\uff0c\u5374\u5148\u89c1\u7740\u5e08\u5085\u4e86\u3002\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u4e5f\u662f\u4eba\u8d8a\u8001\u8d8a\u6ed1\uff0c\u53ea\u8981\u4f60\u80af\u62da\u8017\u4e00\u4e9b\u771f\u6c14\uff0c\u4e3a\u8fd9\u4e24\u4eba\u6253\u901a\u5947\u7ecf\u516b\u8109\uff0c\u8fd9\u4e24\u4eba \u4f24\u52bf\u518d\u91cd\uff0c\u8fd8\u7528\u5f97\u7740\u522b\u4eba\u51fa\u624b\u5417\uff1f\u73b0\u5728\u6211\u5df2\u5c06\u8fd9\u4e8b\u62db\u63fd\u4e86\u8fc7\u6765\uff0c\u53ef\u4e5f\u5bb9\u4e0d\u5f97\u4f60\u592a\u8212\u670d\uff0c\u4e8b\u5b8c \u4e4b\u540e\uff0c\u6211\u4e5f\u6709\u4ef6\u4e8b\uff0c\u8981\u9ebb\u70e6\u9ebb\u70e6\u4f60\u66ff\u6211\u505a\u505a\u54e9\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4f60\u5012\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\u4e86\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u6614\u5e74\u7ec3\u529f\u65f6\uff0c\u68cb\u5dee\u4e00\u6b65\uff0c\u867d\u5c06\u7384\u91ca \u4e24\u95e8\u90fd\u89c6\u4e3a\u79d8\u6280\u7684\u5148\u5929\u4e4b\u6c14\u7ec3\u6210\uff0c\u4f46\u56e0\u521d\u6b65\u529f\u592b\uff0c\u6c42\u901f\u592a\u6025\uff0c\u4ee5\u81f4\u73b0\u5728\u5f04\u5f97\u771f\u6c14\u4e00\u53d1\uff0c\u4fbf\u96be \u6536\u62fe\uff0c\u52bf\u5fc5\u4f24\u4eba\u800c\u540e\u5df2\uff0c\u60f3\u4ee5\u6b64\u7597\u4f24\uff0c\u4e0d\u662f\u505a\u4e0d\u5230\uff0c\u53ea\u6015\u5728\u7d27\u8981\u5173\u5934\uff0c\u6211\u6240\u7528\u4e4b\u529b\u8fc7\u521a\uff0c\u4e0d \u4f46\u4e0d\u80fd\u52a9\u4eba\uff0c\u53cd\u800c\u5bb3\u4eba\uff0c\u662f\u4ee5\u6211\u5c31\u6ca1\u6709\u8f7b\u6613\u51fa\u624b\u7f62\u4e86\u3002\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u76ee\u5149\u4e00\u8f6c\uff0c\u8138\u4e0a\u5374\u9732\u51fa\u559c\u8272\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e00\u4e0b\u5148\u524d\u6211\u6240\u8bf4\u4e4b\u4e8b\uff0c\u4e0d\u4f46\u4e0d\u662f\u6211\u6c42\u4f60\uff0c\u5374\u662f\u4f60\u8981\u6c42\u6211\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6545\u610f\u8bdd\u58f0\u4e00\u987f\uff0c\u679c\u7136\u671b\u89c1\u5251\u5148\u751f\u8138\u4e0a\u6709\u4e9b\u5fc3\u52a8\u4e4b\u8272\u3002 \u3000\u3000\u201c\u53ea\u662f\u73b0\u5728\u8bf4\u51fa\uff0c\u4e3a\u65f6\u8fd8\u65e9\uff0c\u65e5\u540e\u4f60\u53ea\u8981\u5e2e\u6211\u90a3\u4e8b\u5b8c\u6210\uff0c\u6211\u4e5f\u53ef\u4ee5\u5c06\u4f60\u8fd9\u5927\u6210\u4e2d\u7684\u5c0f\u7f3a \u5f25\u8865\u3002\u201d\u4e09\u5fc3\u795e\u541b\u9053\u3002 \u3000\u3000\u5251\u5148\u751f\u795e\u8272\u679c\u7136\u53c8\u4e00\u52a8\uff0c\u5f20\u53e3\u60f3\u8bf4\u8bdd\uff0c\u628a\u4eba\u5bb6\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u4ed6\u5fae\u6307\u7a97\u5916\u3002\u53c8\u9053\uff1a \u3000\u3000\u201c\u6b64\u523b\u5929\u5df2\u5927\u4eae\uff0c\u6211\u4eec\u5728\u6b64\u95f4\u4e00\u65e5\u884c\u7a0b\uff0c\u5927\u6982\u5c31\u53ef\u4ee5\u8d76\u5230\u7ec8\u5357\u3002\u201d\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u6614\u65e5\u7ec8\u5357\u4e00\u522b\uff0c\u81f3\u6b64\u5df2\u6709\u4e8c\u5341\u4f59\u5e74\uff0c\u6211\u8bb0\u5f97\u5728\u7ec8\u5357\u7edd\u9876\u4e4b\u4e0a\uff0c\u4f60\u6211\u8fd8\u6709\u4e00\u5c40\u6b8b\u68cb \u672a\u7adf\u5462\u3002\u4f60\u90a3\u65f6\u88ab\u6211\u56f4\u53bb\u4e00\u89d2\uff0c\u63a8\u8bf4\u6709\u4e8b\uff0c\u7adf\u8d56\u6389\u4e86\uff0c\u53ef\u662f\u73b0\u5728\u6211\u5374\u5bb9\u4e0d\u5f97\u4f60\u518d\u5982\u6b64\u63a8\u8bff \u4e86\u3002\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u597d\uff01\u4f60\u53ef\u77e5\u9053\uff0c\u8fd9\u4e8c\u5341\u591a\u5e74\u6765\uff0c\u6211\u9664\u4e86\u517b\u82b1\u91c7\u836f\u4e4b\u5916\uff0c\u5929\u5929\u90fd\u5728\u60f3\u7740\u90a3\u4e00\u5c40 \u6b8b\u68cb\u7684\u7834\u6cd5\uff0c\u8fd9\u6b21\u4f60\u53c8\u8f93\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5b59\u654f\u542c\u7740\u8fd9\u4e24\u4eba\u7684\u5bf9\u7b54\uff0c\u77e5\u9053\u8fd9\u4e24\u4eba\u867d\u662f\u5947\u884c\u5f02\u7656\uff0c\u4f46\u5374\u90fd\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u5c24\u5176\u8fd9\u4e07\u5251\u4e4b \u5c0a\uff0c\u4ed6\u51fa\u9053\u6c5f\u6e56\u540e\uff0c\u4ece\u672a\u793a\u4eba\u59d3\u540d\u6765\u5386\u3002\u81ea\u5df1\u521d\u89c1\u4ed6\u65f6\uff0c\u4ea6\u89c9\u5f97\u4ed6\u6027\u60c5\u51b7\u6f20\uff0c\u4e0d\u901a\u4eba\u60c5\u3002\u4f46 \u6b64\u523b\u4e00\u770b\uff0c\u4ed6\u5728\u90a3\u51b0\u5c71\u822c\u7684\u5916\u8868\u4e0b\uff0c\u4e5f\u6709\u7740\u6ee1\u8154\u548c\u5e38\u4eba\u4e00\u6837\u7684\u70ed\u8840\u54e9\uff01\u53ea\u662f\u4ed6\u9690\u85cf\u5f97\u8f83\u4e25 \u5bc6\uff0c\u522b\u4eba\u65e0\u6cd5\u53d1\u73b0\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u6295\u5bbf\u7684\u5c0f\u5e97\uff0c\u662f\u5728\u65b9\u8fc7\u4e34\u6f7c\uff0c\u4e0d\u5230\u957f\u5b89\u7684\u4e00\u4e2a\u5c0f\u9547\u4e0a\u3002 \u3000\u3000\u5b59\u654f\u5957\u597d\u8f66\u9a6c\uff0c\u4fbf\u5728\u5929\u867d\u5df2\u660e\uff0c\u4f46\u8fb0\u5149\u4ecd\u65e9\u4e4b\u9645\uff0c\u79bb\u5e97\u800c\u53bb\u3002 \u3000\u3000\u5251\u5148\u751f\u548c\u4e09\u5fc3\u795e\u541b\u6e38\u620f\u98ce\u5c18\uff0c\u968f\u610f\u6240\u4e4b\uff0c\u90fd\u672a\u66fe\u9a91\u9a6c\u3002\u5b59\u654f\u8f66\u867d\u5957\u597d\uff0c\u4f46\u5979\u5374\u53c8\u52bf\u5fc5\u4e0d \u80fd\u5750\u5728\u524d\u5ea7\uff0c\u6743\u5145\u9a6c\u592b\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u662f\u56e0\u4e3a\u4f24\u75c5\u4e4b\u4eba\uff0c\u4ecd\u987b\u5979\u5728\u8f66\u5185\u7167\u987e\uff0c\u518d\u8005\u5979\u4ee5\u4e00\u4e2a\u5973\u5b50\uff0c\u603b\u4e0d\u80fd\u5728\u9053\u4e0a\u5982\u6b64\u629b \u5934\u9732\u9762\u5440\uff01 \u3000\u3000\u4f55\u51b5\u5728\u65c1\u864e\u89c6\u803d\u803d\u7684\u8fd8\u6709\u5bc6\u5e03\u6c5f\u6e56\u7684\u5929\u4e89\u6559\uff0c\u5979\u4e5f\u4e0d\u80fd\u4e0d\u4e3a\u4e4b\u987e\u5fcc\u3002\u56e0\u6b64\uff0c\u5979\u4e3a\u96be\u5730\u6014 \u4f4f\u4e86\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u76ee\u5149\u4e00\u626b\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u6b64\u884c\u867d\u975e\u9065\uff0c\u4f46\u82e5\u5e26\u7740\u4e24\u4e2a\u91cd\u4f24\u4e4b\u4eba\uff0c\u5374\u975e\u6613\u4e8b\u3002\u6211\u770b\u5c31\u59d4\u5c48\u6211\u4eec\u8fd9\u4f4d\u4e07\u5251\u4e4b\u5c0a\u4e00 \u4e0b\uff0c\u4e3a\u59d1\u5a18\u6743\u5145\u8f66\u592b\u597d\u4e86\u3002\u201d \u3000\u3000\u65e5\u5149\u4e0b\uff0c\u4ed6\u773c\u89d2\u989d\u4e0a\u5df2\u53ef\u770b\u51fa\u4e0d\u5c11\u76b1\u7eb9\uff0c\u4ed6\u5185\u529f\u867d\u5df2\u53c2\u900f\u9020\u5316\uff0c\u4f46\u5c81\u6708\u4fb5\u4eba\uff0c\u4ed6\u4ecd\u65e0\u6cd5 \u6297\u62d2\u81ea\u7136\u7684\u5a01\u529b\uff0c\u53ea\u662f\u4ed6\u7387\u6027\u800c\u4e3a\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u5374\u4ecd\u50cf\u4e2a\u672a\u7ecf\u4e16\u6545\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u53ea\u662f\uff0c\u4ed6\u90a3\u79cd\u8bf4\u8bdd\u7684\u58f0\u8c03\uff0c\u4f7f\u4eba\u542c\u8d77\u6765\uff0c\u4ecd\u6709\u4e00\u4efd\u51b7\u51b0\u51b0\u7684\u611f\u89c9\u3002 \u3000\u3000\u5b59\u654f\u611f\u6fc0\u5730\u671b\u4ed6\u4e00\u773c\uff0c\u5bf9\u8fd9\u58f0\u540d\u4f20\u904d\u5b87\u5185\uff0c\u5947\u884c\u9707\u64bc\u6b66\u6797\u7684\u5947\u4eba\uff0c\u5927\u6709\u597d\u611f\u3002 \u3000\u3000\u76ee\u5149\u52a8\u5904\uff0c\u53c8\u843d\u5728\u50b2\u9aa8\u51cc\u4e91\u7684\u5251\u5148\u751f\u8eab\u4e0a\uff0c\u5979\u5b9e\u5728\u4e0d\u6562\u60f3\u50cf\u8fd9\u4f4d\u6b66\u6797\u5de8\u4eba\uff0c\u4f1a\u4e3a\u81ea\u5df1\u5145 \u5f53\u8f66\u592b\u3002 \u3000\u3000\u90a3\u77e5\u5251\u5148\u751f\u5374\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u83ab\u4ee5\u4e3a\u8fd9\u96be\u5012\u4e86\u6211\uff0c\u5f53\u5f53\u8f66\u592b\uff0c\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002\u53ef\u662f\u6211\u5374\u8981\u4f60\u8de8\u5728\u8f66\u8f95\u4e0a\uff0c\u505a\u4e00\u4e2a\u7275 \u9a6c\u63d0\u78f4\u7684\u968f\u884c\u5c0f\u53ae\uff0c\u4f60\u81ea\u8be9\u2026\u2026\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u63a5\u53e3\u7b11\u9053\uff1a \u3000\u3000\u201c\u53ea\u8981\u6211\u9ad8\u5174\uff0c\u4ec0\u4e48\u4e8b\u6211\u90fd\u80fd\u505a\uff0c\u505a\u505a\u5c0f\u53ae\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u4ed6\u8f6c\u8138\u5411\u5b59\u654f\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u59d1\u5a18\u7684\u8fd9\u8f66\u592b\u548c\u5c0f\u53ae\uff0c\u8d70\u904d\u5929\u4e0b\uff0c\u6050\u6015\u4e5f\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4efd\u54e9\uff01\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u6e05\u60a6\uff0c\u4e1d\u6beb\u6ca1\u6709\u4e0d\u6ee1\u4e4b\u610f\u3002 \u3000\u3000\u8fd9\u7c7b\u5947\u4eba\u884c\u4e8b\uff0c\u5e38\u4eba\u5b9e\u5728\u65e0\u6cd5\u63e3\u6d4b\uff0c\u5750\u5728\u8f66\u91cc\u7684\u5b59\u654f\uff0c\u5fc3\u4e2d\u4e0d\u77e5\u5982\u4f55\u60f3\u6cd5\u3002\u201c\u5251\u5c0a\u8f66 \u592b\u201d\uff0c\u201c\u795e\u541b\u5c0fX\u201d\uff0c\u8fd9\u4ee4\u5979\u7b80\u76f4\u4e0d\u76f8\u4fe1\u4f1a\u662f\u4e8b\u5b9e\uff01\u4f46\u4fef\u76ee\u6240\u89c1\uff0c\u65e5\u5149\u5374\u5df2\u4ece\u8f66\u7a97\u4e2d\u4f9d\u7a00\u7167 \u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5979\u671b\u7740\u88ab\u77f3\u5149\u6240\u7167\u7740\u7684\u7231\u5973\u51cc\u7433\uff0c\u5a07\u7f8e\u5982\u82b1\uff0c\u4f46\u5374\u6194\u60b4\u4e0d\u582a\u7684\u9762\u9765\uff0c\u548c\u90a3\u5979\u5c1a\u4e0d\u77e5\u9053\u59d3 \u540d\uff0c\u4eba\u5bb6\u5c31\u4e3a\u5979\u5192\u6b7b\u5374\u654c\u5c11\u5e74\u7684\u4fca\u7f8e\u8138\u5b54\uff0c\u4e0d\u7981\u5347\u8d77\u4e00\u7f15\u5e78\u798f\u4e4b\u9050\u601d\uff01 \u3000\u3000\u5979\u7a81\u7136\u89c9\u5f97\u81ea\u5df1\u7531\u4e00\u4e2a\u5e73\u51e1\u7684\u5987\u4eba\uff0c\u800c\u53d8\u5f97\u6709\u7687\u540e\u822c\u5c0a\u8d35\u3002\u56e0\u4e3a\u5373\u4f7f\u662f\u7687\u540e\uff0c\u4e5f\u65e0\u6cd5\u53eb \u8fd9\u4e24\u4f4d\u5947\u4eba\u6765\u5145\u5f53\u81ea\u5df1\u7684\u201c\u8f66\u592b\u201d\u548c\u201c\u5c0f\u53ae\u201d\u3002 \u3000\u3000\u8fd9\u4efd\u5c0a\u8363\uff0c\u662f\u4e16\u95f4\u6240\u6709\u7684\u4e00\u5207\uff0c\u90fd\u65e0\u6cd5\u6362\u53d6\u7684\u3002 \u3000\u3000\u201c\u800c\u6211\uff1f\u201d\u5979\u601d\u5fd6\u7740\uff1a\u201c\u5374\u5f97\u5230\u4e86\uff01\u201d \u3000\u3000\u8fd9\u7a81\u6765\u7684\u5e78\u798f\uff0c\u4f7f\u5f97\u5979\u8ff7\u60d8\u4e86\u8d77\u6765\u3002\u8fd9\u4e5f\u8bb8\u662f\u5979\u6240\u53d7\u7684\u82e6\u96be\uff0c\u5df2\u7ecf\u591f\u591a\u4e86\u5427\uff01 \u3000\u3000\u8f66\u58f0\u8f9a\u8f9a\u2014\u2014 \u3000\u3000\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u5979\u5df2\u7761\u53bb\u3002\u8fd9\u4e48\u591a\u5929\u6765\u7684\u52b3\u987f\uff0c\u5979\u672c\u5df2\u5026\u6781\uff1a\u6b64\u523b\u5fc3\u795e\u5927\u5b9a\uff0c\u81ea\u7136\u7761\u5f97 \u6781\u719f\u3002 \u3000\u3000\u76ee\u5149\u9690\u6ca1\uff0c\u5df2\u4ea4\u620c\u65f6\uff0c\u9a6c\u8f66\u8d8a\u8fc7\u957f\u5b89\uff0c\u6765\u5230\u7ec8\u5357\u5c71\u811a\u3002 \u3000\u3000\u7ec8\u5357\u5c71\u4f4d\u4e8e\u957f\u5b89\u4e4b\u5357\uff0c\u4e3a\u9053\u6559\u540d\u5c71\u4e4b\u4e00\u3002\u7ec8\u5357\u5251\u6d3e\uff0c\u5728\u4e2d\u539f\u4e03\u5927\u5b97\u6d3e\u5916\uff0c\u81ea\u6210\u4e00\u5bb6\u3002\u6614 \u5e74\u7ec8\u5357\u6d3e\u638c\u95e8\u4eba\u7389\u673a\u9053\u4eba\uff0c\u4ee5\u638c\u4e2d\u677e\u7eb9\u5251\uff0c\u548c\u7ec8\u5357\u9547\u5c71\u4e4b\u201c\u4e03\u4e03\u56db\u5341\u4e5d\u624b\u56de\u98ce\u5251\u6cd5\u201d\uff0c\u79f0\u8a89 \u6b66\u6797\u3002 \u3000\u3000\u7389\u673a\u9053\u4eba\u867d\u7136\u8eab\u6000\u7edd\u6280\uff0c\u4f46\u5374\u7edd\u4e0d\u8f7b\u6613\u70ab\u9732\uff0c\u6536\u5f92\u53c8\u6781\u4e25\uff0c\u662f\u4ee5\u7ec8\u5357\u5f1f\u5b50\u4e5f\u5927\u591a\u662f\u5185\u5916 \u517c\u4fee\uff0c\u6e05\u51c0\u65e0\u4e3a\u7684\u7384\u95e8\u9053\u8005\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u7ec8\u5357\u6d3e\u867d\u56e0\u4e0d\u5e38\u6d89\u8db3\u6b66\u6797\uff0c\u662f\u4ee5\u540d\u58f0\u8f7b\u5fae\uff1b\u4f46\u662f\u6b66 \u529f\u5374\u65e5\u6e10\u7cbe\u8fdb\uff0c\u5076\u4e00\u51fa\u624b\uff0c\u4fbf\u662f\u60ca\u4eba\u4e4b\u7b14\u3002\u4e0d\u50cf\u6b66\u5f53\uff0c\u5d06\u5cd2\u7b49\u5176\u4ed6\u7384\u95e8\u5251\u6d3e\uff0c\u5230\u540e\u6765\u7adf\u53d8\u5f97 \u6709\u5982\u6c5f\u6e56\u5e2e\u4f1a\u4e00\u6837\u3002 \u3000\u3000\u6b64\u65f6\u7ec8\u5357\u6d3e\u7684\u638c\u95e8\u4eba\u5999\u7075\u9053\u4eba\uff0c\u63a5\u638c\u7ec8\u5357\u95e8\u6237\uff0c\u867d\u53ea\u4e03\u5e74\uff0c\u4f46\u5df2\u5c06\u7ec8\u5357\u6d3e\u6574\u987f\u5f97\u66f4\u662f\u65e5 \u6e10\u5176\u660c\u3002\u591a\u5e74\u6765\u4ed6\u867d\u53ea\u51fa\u5c71\u4e00\u6b21\uff0c\u4f46\u7ec8\u5357\u5251\u5ba2\u7384\u95e8\u4e00\u9e64\u7684\u540d\u58f0\uff0c\u5728\u6b66\u6797\u4e2d\u5df2\u662f\u975e\u540c\u5c0f\u9119\uff01 \u3000\u3000\u7ec8\u5357\u5c71\u591a\u5e74\u6765\uff0c\u90fd\u662f\u6e05\u5b81\u5b89\u8be6\uff0c\u6781\u5c11\u6709\u6c5f\u6e56\u4e2d\u4eba\uff0c\u6597\u80c6\u5230\u8fd9\u540d\u5c71\u4e0a\u751f\u4e8b\u3002\u662f\u4ee5\u5251\u5148\u751f\u624d \u4f1a\u9009\u4e2d\u8fd9\u5730\u65b9\uff0c\u4f5c\u4e3a\u5b59\u654f\u6bcd\u5973\u7b49\u7684\u517b\u606f\u4e4b\u5730\u3002 \u3000\u3000\u90a3\u77e5\u4e8b\u60c5\u5374\u5927\u51fa\u610f\u5916 \u3000\u3000\u5915\u971e\u5df2\u9000\uff0c\u591c\u5e55\u6df1\u5782\uff0c\u6e38\u620f\u4eba\u95f4\uff0c\u7387\u6027\u6c5f\u6e56\u7684\u5251\u5148\u751f\uff0c\u7aef\u5750\u5728\u9a6c\u8f66\u524d\u5ea7\u4e4b\u4e0a\uff0c\u624b\u4e2d\u9a6c\u97ad \u500f\u7136\u626c\u8d77\uff0c\u5de6\u624b\u628a\u7ef3\u5fae\u5e26\uff0c\u8f7b\u8f7b\u547c\u5578\u4e00\u58f0\uff0c\u9a6c\u8f66\u4fbf\u5728\u7ec8\u5357\u5c71\u5165\u5c71\u4e4b\u53e3\u505c\u4e0b\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u4e5f\u98d8\u7136\u4e0b\u4e86\u8f66\u8f95\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u770b\u4e0d\u51fa\u4f60\u9664\u4e86\u67c4\u94c1\u5251\u4e0a\u6709\u4e9b\u73a9\u610f\u4e4b\u5916\uff0c\u8d76\u8f66\u7684\u672c\u4e8b\u4e5f\u4e0d\u5c0f\u3002\u8fd9\u4e00\u70b9\uff0c\u6211\u53c8\u662f\u4e07\u4e07\u4e0d\u53ca \u7684\uff01\u201d \u3000\u3000\u5251\u5148\u751f\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u9b54\u5934\uff01\u5c11\u901e\u53e3\u820c\u4e4b\u5229\uff0c\u8fd8\u662f\u7559\u70b9\u5fc3\u601d\uff0c\u5728\u90a3\u5c40\u6b8b\u68cb\u4e0a\u591a\u4e0b\u70b9\u529f\u592b\u5427\uff01\u201d \u3000\u3000\u56de\u8eab\u8f7b\u53e9\u8f66\u53a2\uff0c\u793a\u610f\u5b59\u654f\u5730\u5934\u5df2\u5230\u4e86\u3002 \u3000\u3000\u5b59\u654f\u8fd9\u624d\u81ea\u8ff7\u60d8\uff0c\u6df7\u4e71\uff0c\u4f46\u5374\u5e26\u7740\u4e9b\u751c\u610f\u7684\u68a6\u4e2d\u9192\u6765\u3002\u8f66\u53a2\u4e2d\u9ed1\u9edd\u9edd\u5730\uff0c\u5979\u77e5\u9053\u5929\u5df2\u9ed1 \u4e86\u3002\u518d\u63a2\u9996\u7a97\u5916\uff0c\u773c\u524d\u9ad8\u5c71\u5728\u671b\uff0c\u4e00\u6761\u867d\u7136\u5bbd\u9614\uff0c\u4f46\u5374\u5341\u5206\u5d0e\u5c96\u7684\u5c71\u8def\uff0c\u873f\u8712\u5165\u5c71\u800c\u53bb\u3002 \u3000\u3000\u5979\u8d76\u7d27\u8df3\u4e0b\u8f66\uff0c\u7565\u7565\u7406\u4e86\u7406\u9b13\u53d1\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5c31\u662f\u7ec8\u5357\u5c71\u5417\uff1f\u201d \u3000\u3000\u9edb\u7709\u4e00\u76b1\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u9a6c\u8f66\u65e2\u7136\u4e0d\u80fd\u4e0a\u5c71\uff0c\u8f66\u5b50\u91cc\u53d7\u4f24\u7684\u4e24\u4eba\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u5251\u5148\u751f\u6c88\u541f\u4e00\u4e0b\uff0c\u8fd8\u672a\u7b54\u8a00\uff0c\u4e09\u5fc3\u795e\u541b\u5374\u53c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e00\u56de\u4e0d\u8981\u4f60\u505a\u8f66\u592b\uff0c\u4f46\u5374\u8981\u4f60\u505a\u9a6c\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6f5c\u5c45\u6df1\u5c71\u4e8c\u5341\u4f59\u5e74\uff0c\u6bcf\u65e5\u9664\u4e86\u542c\u98ce\u542c\u96e8\uff0c\u4ee5\u53ca\u9e1f\u8bed\u866b\u9e23\u4e4b\u5916\uff0c\u5bc2\u5bde\u5df2\u6781\uff01\u800c\u8fd9\u79cd\u96be\u582a \u7684\u5bc2\u5bde\uff0c\u5374\u4fbf\u4ed6\u672c\u6765\u6349\u6478\u4e0d\u5b9a\u7684\u6027\u683c\uff0c\u6539\u53d8\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u662f\u4ee5\u5f53\u4ed6\u548c\u51e0\u4e4e\u662f\u4ed6\u4e16\u95f4\u552f\u4e00\u53cb\u4eba\u2014\u2014\u5251\u5148\u751f\u5de7\u9047\u4e4b\u540e\uff0c\u867d\u7136\u77e5\u9053\u81ea\u5df1\u6f5c\u4fee\u7684\u5185\u529f\uff0c\u4ecd \u7136\u6bd4\u4e0d\u4e0a\u4eba\u5bb6\uff0c\u4f46\u662f\u5fc3\u60c5\u5374\u6109\u5feb\u5df2\u6781\uff01 \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u8bf4\u4ed6\u5df2\u5c06\u80dc\u8d1f\u4e4b\u55d4\u770b\u5f97\u6de1\u4e86\uff0c\u800c\u662f\u6545\u53cb\u91cd\u9022\u7684\u90a3\u4e00\u4efd\u559c\u60a6\uff0c\u8fdc\u80dc\u4e8e\u4ed6\u5bf9\u80dc\u8d1f\u4e4b \u95f4\u7684\u55d4\u5ff5\u3002 \u3000\u3000\u5fc3\u60c5\u8f7b\u60a6\u4e4b\u4e0b\uff0c\u662f\u4ee5\u4ed6\u6bcf\u4e00\u51fa\u53e3\uff0c\u591a\u662f\u5e26\u7740\u4e9b\u8bd9\u8c10\u8c03\u4f83\u610f\u5473\u7684\u8bdd\u3002\u800c\u843d\u843d\u5be1\u5408\uff0c\u5b64\u50b2\u65e0 \u6bd4\u7684\u5251\u5148\u751f\uff0c\u6df1\u77e5\u5176\u4eba\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u5fe4\u3002 \u3000\u3000\u4ed6\u6b64\u8bdd\u4e00\u51fa\uff0c\u5b59\u654f\u8fd8\u5f04\u4e0d\u6e05\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5251\u5148\u751f\u5df2\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f5b\u8bf4\uff1a\u82b8\u82b8\u4f17\u751f\uff0c\u7686\u53ef\u6210\u4f5b\uff0c\u4eba\u4ea6\u662f\u751f\uff0c\u9a6c\u4ea6\u662f\u751f\uff0c\u6789\u4f60\u6f5c\u4fee\u591a\u5e74\uff0c\u8fde\u8fd9\u70b9\u7985\u673a\u90fd\u53c2 \u4e0d\u900f\uff01\u6765\uff0c\u6765\uff01\u4f60\u4e5f\u662f\u9a6c\uff0c\u6211\u4e5f\u662f\u9a6c\uff0c\u4f60\u6211\u5c31\u5c06\u8fd9\u8f86\u9a6c\u8f66\uff0c\u62d6\u4e0a\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u5b59\u654f\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u4e0d\u5230\uff0c\u51b7\u6f20\u5982\u51b0\u7684\u5251\u5148\u751f\uff0c\u6b64\u523b\u4e5f\u4f1a\u8bf4\u51fa\u8fd9\u7b49\u8bdd\u6765\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u8de8\u524d\u4e00\u6b65\uff0c\u624b\u638c\u8f7b\u8f7b\u4e00\u6325\uff0c\u90a3\u5957\u7740\u9a6c\u7684\u4e24\u6761\u8f66\u8f95\uff0c\u5ffd\u5730\u4e00\u9f50\u6298\u65ad\uff0c\u50cf\u662f\u88ab\u6781\u950b \u5229\u7684\u5200\u65a7\u6b23\u8fc7\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u5c06\u624b\u638c\u5f80\u8f66\u53a2\u4e0a\u4e00\u8d34\uff0c\u5de6\u624b\u888d\u8896\u4e00\u62c2\uff0c\u5c06\u90a3\u5339\u5df2\u7ecf\u81ea\u7531\u4e86\u7684\u9a6c\uff0c\u9a71\u5f97\u843d\u8352\u800c \u53bb\u3002\u53e3\u4e2d\u5374\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5251\u5148\u751f\u8bf4\uff1a\u201c\u4ed6\u5c31\u662f\u9a6c\uff0c\u9a6c\u5c31\u662f\u4ed6\u3002\u201d\u6b64\u523b\u6211\u653e\u4e86\u9a6c\uff0c\u5c31\u5982\u540c\u653e\u4e86\u4ed6\u4e00\u6837\uff01\u201d \u3000\u3000\u8f6c\u5934\u5411\u5251\u5148\u751f\u7b11\u9053\uff1a \u3000\u3000\u201c\u5582\uff0c\u8fd9\u7b49\u6df1\u6069\uff0c\u4f60\u8be5\u5982\u4f55\u62a5\u6cd5\uff01\u201d \u3000\u3000\u5b59\u654f\u4e0d\u7981\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u6765\uff0c\u5979\u7684\u5fc3\u5883\u65e0\u6cd5\u5f62\u5bb9\u7684\u5f00\u6717\uff0c\u56e0\u4e3a\u5979\u8bb8\u591a\u60ac\u5fc3\u4e0d\u4e0b\u7684\u4e8b\uff0c\u90fd\u6709\u4e86\u89e3\u51b3\u3002 \u3000\u3000\u5251\u5148\u751f\u4e5f\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u867d\u7136\u4f7f\u5f97\u5b59\u654f\u56f0\u96be\uff0c\u8fce\u5203\u800c\u89e3\uff0c\u53ef\u662f\u5b59\u654f\uff0c\u5374\u4e5f\u4f7f\u5f97\u8fd9\u5b64\u50fb\u7684\u5947 \u4eba\uff0c\u6c88\u90c1\u591a\u5e74\u7684\u5fc3\u5883\uff0c\u8f7b\u60a6\u8d77\u6765\u4e86\u54e9\u3002 \u3000\u3000\u4ed6\u5728\u4e09\u5fc3\u795e\u541b\u7684\u53e6\u4e00\u4fa7\uff0c\u4e5f\u5c06\u624b\u638c\u5728\u8f66\u53a2\u4e0a\u4e00\u6309\uff0c\u4e24\u4eba\u540c\u65f6\u5fae\u5fae\u4e00\u7b11\uff0c\u597d\u50cf\u638c\u4e0a\u6709\u7740\u7edd \u5927\u7684\u5438\u529b\u4f3c\u7684\uff0c\u7adf\u5c06\u90a3\u8f86\u6c88\u91cd\u7684\u5927\u8f66\u5438\u4e86\u8d77\u6765\uff0c\u5939\u5728\u4e24\u4eba\u7684\u624b\u638c\u4e4b\u4e2d\uff0c\u4ece\u5bb9\u5411\u5c71\u4e0a\u8d70\u53bb\u3002 \u3000\u3000\u5b59\u654f\u5df2\u77e5\u4ed6\u4e24\u4eba\u7684\u529f\u529b\uff0c\u5012\u4e5f\u5e76\u4e0d\u60ca\u5f02\uff0c\u8ddf\u7740\u4ed6\u4eec\uff0c\u4e0a\u5c71\u800c\u53bb\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u4e00\u7ae0\u3000\u540d\u5c71\u751f\u53d8>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u540d\u5c71\u751f\u53d8 \u3000\u3000\u591c\u8272\u6df1\u91cd\uff0c\u5c71\u8def\u5d0e\u5c96\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u5728\u666e\u901a\u4eba\u773c\u4e2d\u975e\u5e38\u8270\u96be\u7684\u9053\u8def\uff0c\u600e\u4f1a\u653e\u5728\u4e07\u5251\u4e4b\u5c0a\u548c\u4e09\u5fc3\u795e\u541b\u5fc3\u4e0a\uff0c\u4ed6\u4eec\u65bd\u7136\u800c \u884c\uff0c\u5f77\u4f5b\u662f\u6e38\u6625\u8e0f\u9752\u7684\u96c5\u58eb\u3002 \u3000\u3000\u5c31\u8fde\u8d70\u5728\u65c1\u8fb9\u7684\u5b59\u654f\uff0c\u6b65\u5c65\u4ea6\u662f\u8f7b\u677e\u5df2\u6781\u3002\u53ea\u662f\u8fd9\u6df1\u5c71\u7684\u5bc2\u9759\uff0c\u5374\u4f7f\u5f97\u5979\u5fc3\u91cc\u6c89\u91cd\u5f97 \u5f88\uff01\u56e0\u4e3a\u6b64\u523b\u5df2\u662f\u4e25\u51ac\uff0c\u8fde\u866b\u9e23\u7684\u58f0\u97f3\u90fd\u6ca1\u6709\u3002\u53ea\u6709\u98ce\u5439\u67af\u679d\uff0c\u7c0c\u7c0c\u4f5c\u54cd\uff0c\u5bc2\u9759\u4e2d\u5df2\u6709\u8427\u7d22 \u4e4b\u610f\u3002 \u3000\u3000\u8f6c\u8fc7\u51e0\u5904\u5c71\u6e7e\uff0c\u9053\u8def\u66f4\u89c1\u7a84\u72ed\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u5bf9\u5251\u5148\u751f\u7b11\u9053\uff1a \u3000\u3000\u201c\u770b\u6765\u771f\u662f\u4e00\u4ee3\u4e0d\u5982\u4e00\u4ee3\uff0c\u7389\u673a\u9053\u4eba\u7684\u5f1f\u5b50\uff0c\u679c\u7136\u4e0d\u5982\u5e08\u7236\uff0c\u5c06\u8fd9\u4e9b\u7ec8\u5357\u9053\u58eb\uff0c\u5f04\u5f97\u8fd9 \u4e48\u758f\u61d2\uff0c\u4f60\u770b\uff01\u201d \u3000\u3000\u4ed6\u624b\u5fae\u6307\u5c71\u540e\uff0c\u9053\uff1a \u3000\u3000\u201c\u6b64\u65f6\u65b9\u8fc7\u620c\u65f6\uff0c\u6b63\u662f\u665a\u8bfe\u4e4b\u65f6\uff0c\u4f46\u6b64\u523b\u975e\u4f46\u542c\u4e0d\u5230\u8bf5\u7ecf\u4e4b\u58f0\uff0c\u8fde\u9053\u89c2\u949f\u9e23\u90fd\u6ca1\u6709\uff0c\u60f3 \u662f\u90a3\u822c\u9053\u58eb\u90fd\u8010\u4e0d\u4f4f\u5929\u610f\uff0c\u7f29\u8fdb\u88ab\u7a9d\u91cc\u8499\u5934\u5927\u7761\u4e86\uff0c\u6211\u89c1\u7740\u90a3\u5c0f\u9053\u7ae5\uff0c\u5012\u8981\u8bad\u4ed6\u51e0\u53e5\u3002\u201d \u3000\u3000\u5b59\u654f\u542c\u4ed6\u5c06\u7ec8\u5357\u638c\u95e8\u7384\u95e8\u4e00\u9e64\uff0c\u79f0\u505a\u5c0f\u9053\u7ae5\uff0c\u4e0d\u7981\u6697\u4e2d\u597d\u7b11\uff0c\u5fc3\u4e2d\u5374\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4ed6\u770b\u8d77\u6765\u6700\u591a\u4e5f\u53ea\u6709\u56db\u4e94\u5341\u5c81\uff0c\u4f46\u662f\u6210\u540d\u6c5f\u6e56\u5374\u4e5f\u6709\u56db\u4e94\u5341\u5e74\u4e86\uff0c\u53ea\u6015\u4ed6\u5b9e\u9645\u7684\u5e74 \u9f84\uff0c\u5df2\u7ecf\u5f88\u9ad8\uff0c\u770b\u6765\u8fd9\u5185\u5bb6\u529f\u592b\uff0c\u4e00\u5165\u5316\u5883\uff0c\u786e\u6709\u4e0d\u53ef\u601d\u8bae\u7684\u6548\u80fd\uff0c\u5c31\u8fde\u4e16\u95f4\u4f20\u8bf4\u7684\u9a7b\u989c\u4e4b \u672f\uff0c\u4e5f\u662f\u53ef\u4ee5\u505a\u5230\u7684\u54e9\uff01\u201d \u3000\u3000\u5251\u5148\u751f\u5374\u53cc\u7709\u5fae\u76b1\uff0c\u52a0\u5feb\u4e86\u811a\u6b65\uff0c\u671d\u5c71\u6df1\u4e4b\u5904\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u518d\u8f6c\u8fc7\u4e00\u5904\u5c71\u6e7e\uff0c\u524d\u9762\u6709\u4e00\u7247\u9edd\u9ed1\u7684\u4e1b\u6797\uff0c\u4ed6\u4eec\u7b14\u76f4\u671d\u524d\u8d70\u53bb\uff0c\u4e1b\u6797\u95f4\u7684\u5c0f\u8def\uff0c\u4e0a\u9762\u6ee1 \u94fa\u7740\u788e\u77f3\uff0c\u4f46\u662f\u62ac\u7740\u4e00\u8f86\u5927\u8f66\u7684\u4e07\u5251\u4e4b\u5c0a\u548c\u4e09\u5fc3\u795e\u541b\uff0c\u811a\u4e0b\u5374\u4f9d\u7136\u6ca1\u6709\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\u6765\u3002 \u3000\u3000\u518d\u8d70\u524d\u51e0\u6b65\uff0c\u5b59\u654f\u624d\u770b\u89c1\u4e1b\u6797\u91cc\u7684\u9053\u89c2\uff0c\u5979\u5fc3\u4e2d\u5374\u4e5f\u4e0d\u7981\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u65f6\u8fb0\u5c1a\u65e9\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u9053\u98a7\u91cc\u7684\u706f\u5149\u5982\u6b64\u9eef\u6de1\uff0c\u771f\u50cf\u662f\u9053\u4eba\u4eec\u90fd\u7761\u7740\u4e86\u4e00\u6837\uff0c\u96be\u9053\u8fd9\u7ec8 \u5357\u6d3e\u91cc\uff0c\u771f\u7684\u90fd\u662f\u61d2\u866b\uff1f\u201d \u3000\u3000\u5251\u5148\u751f\u66f4\u89c9\u5f97\u4e8b\u6709\u8e4a\u8df7\uff0c\u8eab\u5f62\u5fae\u957f\uff0c\u7adf\u5355\u624b\u6258\u7740\u90a3\u8f86\u5927\u8f66\u671d\u524d\u7eb5\u53bb\u3002. \u3000\u3000\u4e09\u5fc3\u795e\u541b\u4e5f\u6536\u8d77\u4e86\u73a9\u7b11\u4e4b\u6001\uff0c\u63a0\u524d\u6570\u4e08\uff0c\u5982\u9759\u591c\u4e2d\u4e4b\u7070\u9e64\uff0c\u8bf4\u4e0d\u51fa\u5730\u90a3\u4e48\u8f7b\u7075\u66fc\u5999\uff0c\u7edd \u65e0\u4e1d\u6beb\u52c9\u5f3a\u9020\u4f5c\u3002 \u3000\u3000\u5b59\u654f\u4e5f\u8d76\u7d27\u8ddf\u4e0a\u53bb\u3002 \u3000\u3000\u5374\u89c1\u90a3\u9053\u89c2\u524d\u6731\u7ea2\u8272\u7684\u5927\u95e8\u7adf\u7d27\u95ed\u7740\uff0c\u89c2\u4e2d\u4e5f\u4e1d\u6beb\u6ca1\u6709\u4eba\u58f0\uff0c\u8fd9\u666f\u8c61\u4e0d\u662f\u9759\u5bc2\uff0c\u800c\u662f\u6b7b \u6c14\u6c89\u6c89\u4e86\uff01 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u6b63\u7ad9\u5728\u89c2\u95e8\u524d\u62cd\u95e8\uff0c\u5c06\u90a3\u53ea\u7d2b\u94dc\u95e8\u73af\u53e9\u5f97\u94db\u94db\u4f5c\u54cd\uff0c\u4f46\u5374\u4ecd\u7136\u6ca1\u6709\u4eba\u8d70\u6765\u7684\u8ff9 \u8c61\uff0c\u4ed6\u671d\u5251\u5148\u751f\u671b\u4e86\u4e00\u773c\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u8fdb\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u888d\u8896\u4e00\u62c2\u5c31\u8981\u4ece\u90a3\u4e24\u4e08\u591a\u9ad8\u7684\u56f4\u5899\u4e0a\u7eb5\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u89c2\u4e2d\u7a81\u7136\u4f20\u51fa\u4e00\u9053\u5389\u53f1\uff0c\u4e00\u4e2a\u4e25\u5389\u7684\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b59\u654f\u4e0d\u7981\u6697\u5fd6\uff1a\u201c\u8fd9\u7ec8\u5357\u9053\u4eba\u600e\u5730\u8fd9\u4e48\u5927\u706b\u6c14\uff1f\u201d \u3000\u3000\u968f\u7740\u4e00\u58f0\u5389\u53f1\uff0c\u5927\u95e8\u5440\u5730\u5f00\u4e86\uff0c\u4e00\u4e2a\u957f\u888d\u9053\u4eba\u5f53\u95e8\u800c\u7acb\uff0c\u76ee\u5149\u70af\u7136\u671b\u7740\u95e8\u5916\uff0c\u795e\u60c5\u4e4b \u4e2d\uff0c\u5f77\u4f5b\u6212\u5907\u68ee\u4e25\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u6781\u4e3a\u4e0d\u60a6\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u671d\u90a3\u9053\u4eba\u4e00\u671b\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u7ec8\u5357\u5c71\u81ea\u4ece\u7389\u673a\u8001\u9053\u6b7b\u540e\uff0c\u6392\u573a\u8d8a\u53d8\u8d8a\u5927\uff0c\u4f60\u53bb\u544a\u8bc9\u4f60\u4eec\u638c\u95e8\u4eba\uff0c\u5c31\u8bf4\u6709\u6545\u4eba \u6765\u62dc\u8bbf\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u5c06\u201c\u62dc\u8bbf\u201d\u4e24\u5b57\uff0c\u8bf4\u5f97\u7279\u522b\u523a\u8033\u800c\u6c89\u91cd\u3002 \u3000\u3000\u90a3\u9053\u4eba\u53c8\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u5ffd\u7136\u60ca\u5524\u4e86\u51fa\u6765\uff1a \u3000\u3000\u201c\u6155\u5bb9\u5e08\u4f2f\uff01\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u6014\u4e86\u4e00\u4e0b\uff0c\u60f3\u4e0d\u901a\u8fd9\u5f00\u95e8\u7684\u9053\u4eba\u600e\u4f1a\u8ba4\u5f97\u81ea\u5df1\uff0c\u548c\u81ea\u5df1\u90a3\u6781\u5c11\u4e3a\u5916\u4eba\u6240\u77e5\u7684\u540d \u59d3\u2014\u2014\u201c\u6155\u5bb9\u5fd8\u543e\u201d\uff1f \u3000\u3000\u5b59\u654f\u89c9\u5f97\u8eab\u4fa7\u8f7b\u98ce\u4e00\u95ea\uff0c\u5251\u5148\u751f\u4e5f\u63a0\u4e86\u524d\u53bb\u3002 \u3000\u3000\u90a3\u957f\u888d\u9053\u4eba\u5374\u6251\u5730\u8dea\u5728\u89c2\u95e8\u9053\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u8001\u4eba\u5bb6\u4e0d\u8bc6\u5f97\u5c0f\u4f84\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u76ee\u5149\u4e0a\u4e0b\u6253\u91cf\u8fd9\u9053\u4eba\u3002 \u3000\u3000\u5251\u5148\u751f\u5374\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u5426\u5999\u7075\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u62ac\u5934\u4e00\u671b\uff0c\u5728\u4f9d\u7a00\u7684\u591c\u8272\u4e2d\uff0c\u8ba4\u6e05\u4e86\u9762\u524d\u7684\u4e24\u4eba\uff0c\u72c2\u559c\u9053\uff1a \u3000\u3000\u201c\u5440\uff01\u5251\u5e08\u4f2f\u4e5f\u6765\u4e86\uff01\u5c0f\u4f84\u5c31\u662f\u5999\u7075\u3002\u4e24\u4f4d\u5e08\u4f2f\u4e00\u53bb\u7ec8\u5357\uff0c\u5df2\u7ecf\u4e09\u5341\u5e74\u3002\u53ef\u662f\u98ce\u59ff\u7b11 \u8c8c\uff0c\u5374\u4e00\u70b9\u4e5f\u6ca1\u6709\u6539\u53d8\u54e9\uff01\u201d \u3000\u3000\u4e09\u795e\u541b\u9894\u9996\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u5374\u53d8\u4e86\u4e0d\u5c11\uff0c\u60f3\u4e0d\u5230\u4ee5\u524d\u7aef\u7740\u8336\u676f\u7684\u9053\u7ae5\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u540d\u95fb\u6b66\u6797\u7684\u5927\u5251\u5ba2\uff0c\u7ec8\u5357\u5251 \u6d3e\u7684\u638c\u95e8\u4eba\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8f6c\u8138\u5411\u5251\u5148\u751f\u9053\uff1a \u3000\u3000\u201c\u5c81\u6708\u50ac\u4eba\uff0c\u65f6\u5149\u4e0d\u518d\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u6050\u6015\u6211\u4eec\u4e5f\u8981\u5165\u571f\u4e86\uff01\u201d \u3000\u3000\u5b59\u654f\u671b\u7740\u90a3\u6b63\u4f0f\u5728\u89c2\u95e8\u524d\u7684\u9053\u4eba\uff0c\u60ca\u5f02\u7684\u6697\u5fd6\uff1a\u201c\u96be\u9053\u4ed6\u5c31\u662f\u7ec8\u5357\u5251\u5ba2\uff0c\u7384\u95e8\u4e00\u9e64\uff01\u9119 \u662f\u4ed6\u4ee5\u638c\u95e8\u4eba\u7684\u8eab\u4efd\uff0c\u5374\u600e\u4f1a\u81ea\u5df1\u8d70\u51fa\u6765\u5f00\u95e8\u5462\uff1f\u201d \u3000\u3000\u4e0d\u602a\u5979\u5982\u662f\u60ca\u5f02\uff0c\u65e0\u8bba\u4efb\u4f55\u4e00\u4e2a\u5b97\u6d3e\uff0c\u4e5f\u65ad\u6ca1\u6709\u638c\u95e8\u4eba\u4eb2\u81ea\u6765\u5f00\u95e8\u7684\u9053\u7406\u3002 \u3000\u3000\u5251\u5148\u751f\u624b\u4e00\u62ac\uff0c\u5c06\u8f66\u6258\u4e86\u8d77\u6765\u3002\u76ee\u5149\u671b\u7740\u89c2\u5185\uff0c\u6b63\u6bbf\u4e0a\u53ea\u6709\u83b9\u7136\u4e00\u76cf\u5b64\u706f\uff0c\u6563\u7740\u660f\u9ec4\u4e4b \u5149\u3002\u518d\u671b\u5230\u5999\u7075\u8138\u4e0a\uff0c\u5374\u89c1\u4ed6\u6e05\u77bf\u7684\u8138\u4e0a\uff0c\u6194\u60b4\u5df2\u6781\u3002\u5c31\u77e5\u9053\u8fd9\u7ec8\u5357\u5251\u6d3e\uff0c\u4e00\u5b9a\u53d1\u751f\u4e86\u4ec0\u4e48 \u91cd\u5927\u7684\u53d8\u6545\u3002 \u3000\u3000\u201c\u771f\u662f\u82cd\u5929\u6709\u773c\uff01\u5c0f\u4f84\u518d\u4e5f\u60f3\u4e0d\u5230\u4e24\u4f4d\u5e08\u4f2f\u7684\u4ed9\u9a7e\uff0c\u7adf\u4f1a\u6765\u5230\u6b64\u95f4\uff01\u201d \u3000\u3000\u5999\u7075\u8bf4\u8bdd\u58f0\u97f3\u4e2d\u7684\u559c\u60a6\uff0c\u5374\u6e17\u5408\u7740\u8bb8\u591a\u60b2\u4f24\u3002\u4ed6\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u5e08\u6015\u4e00\u6765\uff0c\u7ec8\u5357\u6d3e\u91cc\u56db\u767e\u4e8c\u5341\u4e5d\u4e2a\u5f1f\u5b50\u7684\u6027\u547d\uff0c\u7b97\u662f\u6361\u56de\u4e00\u534a\u4e86\uff01\u201d \u3000\u3000\u5251\u5148\u751f\u548c\u4e09\u5fc3\u795e\u541b\u6155\u5bb9\u5fd8\u543e\uff0c\u867d\u7136\u77e5\u9053\u8fd9\u7ec8\u5357\u6d3e\uff0c\u4e00\u5b9a\u53d1\u751f\u4e86\u4ec0\u4e48\u91cd\u5927\u7684\u53d8\u6545\uff1a\u53ef\u662f\u4e00 \u95fb\u5999\u7075\u9053\u4eba\u6b64\u8a00\uff0c\u575a\u6bc5\u51b7\u6f20\u7684\u8138\u5b54\uff0c\u4ecd\u4e0d\u7981\u5fae\u5fae\u53d8\u8272\u3002 \u3000\u3000\u662f\u4ec0\u4e48\u91cd\u5927\u7684\u53d8\u6545\uff0c\u80fd\u4f7f\u8fd9\u7ec8\u5357\u6d3e\u5927\u5c0f\u6570\u767e\u4e2a\u9053\u4eba\uff0c\u540c\u65f6\u547d\u5728\u5782\u5371\u5462\uff1f \u3000\u3000\u987b\u77e5\u7ec8\u5357\u6d3e\u521b\u7acb\u4ee5\u6765\uff0c\u9ad8\u624b\u8f88\u51fa\uff0c\u95e8\u4e0b\u5f1f\u5b50\u4e5f\u5e76\u975e\u662f\u65e0\u80fd\u4e4b\u8f88\u3002\u90a3\u4e48\uff0c\u6b64\u4e8b\u5c82\u975e\u592a\u8fc7\u60ca \u4eba\u5417\uff1f \u3000\u3000\u5251\u5148\u751f\u8be7\u7136\u95ee\u9053\uff1a\u201c\u8d24\u5951\u4e00\u522b\u7ecf\u5e74\uff0c\u5df2\u81ea\u957f\u6210\uff0c\u53ef\u8d3a\u53ef\u559c\uff01\u53ea\u662f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u76ee \u5149\u56db\u626b\u3002\u53c8\u9053\uff1a\u201c\u8fd9\u7ec8\u5357\u5c71\u4e0a\uff0c\u662f\u5426\u6709\u53d8\uff1f\u201d \u3000\u3000\u5999\u7075\u9053\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u5ffd\u7136\u770b\u5230\u7ad9\u5728\u5251\u5148\u751f\u8eab\u540e\u7684\u5b59\u654f\uff0c\u4e5f\u4e0d\u514d\u5728\u6697\u4e2d\u60ca\u5f02\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7ec8\u5357\u6d3e\u786e\u662f\u9047\u7740\u6570\u767e\u5e74\u6765\u672a\u6709\u4e4b\u52ab\u96be\uff0c\u5c0f\u4f84\u65e0\u80fd\uff0c\u5b9e\u5728\u675f\u624b\u65e0\u7b56\u3002\u82e5\u4e0d\u662f\u4e24\u4f4d\u5e08\u4f2f\u524d \u6765\uff0c\u8fd9\u5f00\u6d3e\u5df2\u6570\u767e\u5e74\u7684\u7ec8\u5357\u6d3e\uff0c\u6015\u5c31\u662f\u4ece\u6b64\u65ad\u9001\u4e86\u3002\u201d \u3000\u3000\u8bdd\u4e2d\u60c5\u5f62\u4e4b\u4e25\u91cd\uff0c\u4f7f\u5f97\u4e0d\u52a8\u58f0\u8272\u7684\u5251\u5148\u751f\uff0c\u4e3a\u4e4b\u53c8\u5fae\u5fae\u8272\u53d8\u3002 \u3000\u3000\u5999\u7075\u9053\u4eba\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u7136\u540e\u8f7b\u97f3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6b64\u5730\u4e0d\u662f\u8c08\u8bdd\u4e4b\u5904\uff0c\u4e24\u4f4d\u5e08\u4f2f\u8bf7\u8fdb\u89c2\u53bb\uff0c\u5c0f\u4f84\u518d\u8be6\u7ec6\u8bf4\u51fa\u3002\u201d \u3000\u3000\u5251\u5148\u751f\u548c\u6155\u5bb9\u5fd8\u543e\u5c06\u5927\u8f66\u6258\u4e86\u8fdb\u53bb\uff0c\u5b59\u654f\u4e5f\u4f4e\u9996\u800c\u5165\u3002 \u3000\u3000\u5999\u7075\u770b\u5230\u7adf\u6709\u4e00\u7edd\u7f8e\u5973\u5b50\u548c\u4ed6\u7d20\u6765\u6700\u4e3a\u656c\u4ef0\u7684\uff0c\u81ea\u5df1\u901d\u4e16\u5e08\u5c0a\u7684\u4e24\u4f4d\u81f3\u53cb\u4e07\u5251\u4e4b\u5c0a\u548c\u4e09 \u5fc3\u795e\u541b\u5728\u4e00\u8d77\uff0c\u5fc3\u91cc\u867d\u7136\u5947\u602a\uff0c\u4f46\u53e3\u4e2d\u5374\u4e0d\u6562\u95ee\u51fa\u6765\uff0c\u53ea\u662f\u606d\u8c28\u5730\u5782\u7acb\u4e00\u65c1\u3002. \u3000\u3000\u5927\u6bbf\u4e2d\u706f\u5149\u5982\u8c46\uff0c\u5c06\u8fd9\u5bbd\u9614\u5b8f\u5927\u7684\u795e\u6bbf\uff0c\u7b3c\u4e0a\u51c4\u51c9\u4e4b\u8272\uff0c\u6b63\u4e2d\u795e\u50cf\uff0c\u7fbd\u8863\u661f\u51a0\uff0c\u53f3\u624b\u5fae \u5fae\u63e1\u7740\u5251\u67c4\uff0c\u6b63\u662f\u7fa4\u4ed9\u4e2d\u6700\u4e3a\u6f47\u6d12\u7684\u7eaf\u9633\u771f\u4eba\uff0c\u5728\u8fd9\u79cd\u706f\u5149\u4e0b\uff0c\u66f4\u663e\u5f97\u6829\u6829\u5982\u751f\uff0c\u76f4\u5982\u771f \u4ed9\uff01 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u4eba\u8d70\u8fdb\u6b64\u6bbf\uff01\u5fc3\u60c5\u4e5f\u4f1a\u4e3a\u4e4b\u4e00\u6c89\u3002\u5b59\u654f\u66f4\u50cf\u662f\u6709\u7740\u4ec0\u4e48\u4e1c\u897f\uff0c\u7a81\u7136\u538b\u5230\u5fc3\u4e0a\uff0c \u8fde\u6c14\u90fd\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6765\u4f3c\u7684\uff01 \u3000\u3000\u8fd9\u504c\u5927\u7684\u4e00\u5ea7\u9053\u98a7\uff0c\u9664\u4e86\u5999\u7075\u9053\u4eba\u5916\uff0c\u7adf\u518d\u4e5f\u770b\u4e0d\u5230\u4e00\u6761\u4eba\u5f71\uff0c\u5b59\u654f\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u89c1 \u8fc7\u6bd4\u8fd9\u91cc\u518d\u51c4\u51c9\u7684\u5730\u65b9\u3002 \u3000\u3000\u5251\u5148\u751f\u548c\u6155\u5bb9\u5fd8\u543e\u9762\u8272\u51dd\u91cd\uff0c\u5c06\u4f0a\u98ce\u548c\u51cc\u7433\u81ea\u8f66\u4e2d\u6258\u51fa\u3002 \u3000\u3000\u5999\u7075\u9053\u4eba\u8fde\u5fd9\u8fc7\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u5e08\u53d4\uff01\u6682\u4e14\u5c06\u8fd9\u4e24\u4f4d\u75c5\u4eba\uff0c\u9001\u5230\u5c0f\u4f84\u7684\u623f\u4e2d\u53bb\u3002\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fd9\u9053\u89c2\u4e2d\u9664\u4e86\u5c0f\u4f84\u4e4b\u5916\uff0c\u90fd\u5df2\u547d\u5982\u6e38\u4e1d\uff0c\u671d\u4e0d\u4fdd\u5915\u4e86\uff01\u201d \u3000\u3000\u9634\u6697\u7684\u706f\u5149\u4e0b\uff0c\u4ed6\u60e8\u9eef\u7684\u9762\u5bb9\u66f4\u4e3a\u6194\u60b4\uff0c\u7d27\u76b1\u7740\u7684\u53cc\u7709\u4e2d\uff0c\u9690\u4f0f\u7740\u7684\u5fe7\u90c1\uff0c\u4f7f\u5f97\u8eab\u4e3a\u5c40 \u5916\u4eba\u7684\u5b59\u654f\uff0c\u4e5f\u4e0d\u514d\u4e3a\u4e4b\u6697\u6697\u53f9\u606f\u3002 \u3000\u3000\u4eba\u6750\u6d4e\u6d4e\uff0c\u9ad8\u624b\u8f88\u51fa\uff0c\u540d\u6ee1\u6b66\u6797\u7684\u7ec8\u5357\u5251\u6d3e\uff0c\u7a76\u7adf\u4e3a\u7740\u4ec0\u4e48\u53d8\u6545\uff0c\u4f1a\u6f14\u53d8\u6210\u8fd9\u79cd\u5730\u6b65 \u5462\uff01 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u4e8c\u7ae0\u3000\u5929\u6bd2\u6559\u4e3b>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u5929\u6bd2\u6559\u4e3b \u3000\u3000\u539f\u6765\u8fd9\u4e00\u6708\u6765\uff0c\u7ec8\u5357\u6d3e\u8fed\u751f\u5de8\u53d8\uff0c\u95e8\u4e0b\u5f1f\u5b50\uff0c\u8fde\u8fde\u75c5\u5012\uff0c\u5f97\u75c5\u4e4b\u4eba\uff0c\u4e0d\u4f46\u660f\u8ff7\u4e0d\u9192\uff0c\u800c \u4e14\u547c\u5438\u65e5\u6e10\u5fae\u5f31\uff0c\u75c5\u52bf\u6c89\u91cd\u5df2\u6781\uff01 \u3000\u3000\u8d77\u5148\uff0c\u8fd8\u4ee5\u4e3a\u53ea\u662f\u60a3\u75c5\u800c\u5df2\uff0c\u4f46\u662f\u5f97\u75c5\u4e4b\u4eba\uff0c\u8d8a\u6765\u8d8a\u591a\uff0c\u800c\u4e14\u90fd\u662f\u7a81\u7136\u75c5\u53d1\u3002\u5999\u7075\u9053\u4eba \u4ea6\u9887\u77e5\u533b\u7406\uff0c\u4f46\u770b\u89c6\u4e4b\u4e0b\uff0c\u7adf\u770b\u4e0d\u51fa\u75c5\u6e90\u6765\uff0c\u4ed6\u8fd9\u624d\u5927\u60ca\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u533b\u672f\u4f20\u81ea\u4e09\u5fc3\u795e\u541b\uff0c\u4e0d\u77e5\u8981\u6bd4\u4e16\u4fd7\u4e2d\u7684\u540d\u533b\uff0c\u5f3a\u4e0a\u591a\u5c11\u500d\uff01\u800c\u8fd9\u75c5\u6e90\uff0c\u7adf\u8fde\u4ed6\u90fd \u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u53ea\u662f\u5f97\u75c5\u4e4b\u4eba\uff0c\u4e09\u6839\u6781\u5f31\uff0c\u7b4b\u7edc\u4e0d\u901a\uff0c\u7adf\u6709\u4e9b\u50cf\u50cf\u662f\u88ab\u5185\u5bb6\u9ad8\u624b\u70b9\u4e2d\u6655\u7a74\uff0c\u4f46\u8840\u6db2\u5982 \u5e38\uff0c\u5374\u53c8\u4e0d\u50cf\u3002 \u3000\u3000\u5230\u540e\u6765\uff0c\u5999\u7075\u9053\u4eba\u7684\u518d\u4f20\u5f1f\u5b50\uff0c\u548c\u51e0\u4e2a\u6839\u57fa\u7a0d\u5f31\u7684\u5f1f\u5b50\uff0c\u7adf\u76f8\u7ee7\u6b7b\u53bb\u3002\u5c31\u8fde\u4ed6\u7684\u51e0\u4e2a\u5e08 \u5f1f\uff0c\u4e5f\u65e0\u6545\u75c5\u5012\u3002\u7ec8\u5357\u5c71\u4e0a\uff0c\u7acb\u523b\u6101\u4e91\u6ee1\u6016\uff0c\u6ca1\u6709\u75c5\u5012\u7684\u4eba\uff0c\u7adf\u5c31\u5269\u4e0b\u638c\u95e8\u4eba\u7384\u95e8\u4e00\u9e64\u5999\u7075 \u9053\u4eba\u4e00\u4e2a\uff01 \u3000\u3000\u8fd9\u79cd\u4e25\u91cd\u4e4b\u4e8b\uff0c\u4f7f\u5f97\u4e00\u5411\u7cbe\u660e\u5e72\u7ec3\u7684\u5999\u7075\u9053\u4eba\uff0c\u4e5f\u4e3a\u4e4b\u675f\u624b\u65e0\u7b56\u3002\u4ed6\u5b8c\u5168\u4e0d\u77e5\u9053\u539f\u56e0\uff0c \u66f4\u4e0d\u77e5\u9053\u5bf9\u7b56\u3002\u5c31\u662f\u6c42\u52a9\uff0c\u4e5f\u65e0\u6cd5\u53ef\u6c42\u3002 \u3000\u3000\u5999\u7075\u9053\u4eba\uff0c\u773c\u671b\u7740\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e2a\u4e2a\u90fd\u662f\u547d\u5982\u7d2f\u5375\uff0c\u5fc3\u60c5\u4e4b\u6006\u75db\u60f6\u6025\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u4ed6\u52bf\u4e0d\u80fd\u5750\u4ee5\u5f85\u6bd9\uff0c\u4f46\u4e5f\u522b\u65e0\u4ed6\u6cd5\u3002\u5947\u602a\u7684\u5374\u662f\u4ed6\u81ea\u5df1\u672a\u66fe\u75c5\u5012\uff0c\u50cf\u662f\u4eba\u5bb6\u7279\u5730\u5c06\u4ed6\u4e00 \u4eba\u7559\u4e0b\u6765\u7684\u6837\u5b50\u3002 \u3000\u3000\u540e\u6765\uff0c\u4ed6\u679c\u7136\u8bc1\u5b9e\u4e86\u8fd9\u60f3\u6cd5\u7684\u6b63\u786e\u3002 \u3000\u3000\u4e00\u65e5\u6e05\u6668\uff0c\u5415\u7956\u6b63\u6bbf\u7684\u6a2a\u6881\u4e0a\uff0c\u7a81\u7136\u53d1\u73b0\u4e00\u5f20\u9ed1\u8272\u7eb8\u7b3a\uff0c\u4ed6\u53d6\u6765\u4e00\u770b\uff0c\u90a3\u5f20\u9ed1\u8272\u7eb8\u7b3a \u4e0a\uff0c\u7adf\u4e0d\u77e5\u7528\u4f55\u7269\u5199\u4e0a\u767d\u8272\u900f\u660e\u7684\u5b57\u8ff9\uff0c\u5999\u7075\u9053\u4eba\u4e00\u770b\uff0c\u5b57\u5b57\u60ca\u4eba\uff01 \u3000\u3000\u539f\u6765\u4e0a\u9762\u5199\u7740\uff1a \u3000\u3000\u201c\u5b57\u8c15\u7ec8\u5357\u5c71\u7384\u5999\u89c2\u4e3b\u5999\u7075\u5176\u4eba\uff1a\u767e\u5341\u5e74\u6765\uff0c\u4e2d\u539f\u5f0f\u6797\u6c89\u6ca6\uff0c\u516b\u65b9\u4fa0\u58eb\u65e0\u4e3b\uff0c\u4ee5\u81f4\u5f0f\u6797 \u4e89\u7aef\u767e\u8d77\uff0c\u4ec7\u6740\u65e5\u591a\u3002 \u3000\u3000\u672c\u6559\u4e3b\u4e0a\u4f53\u592d\u610f\uff0c\u4e0b\u9274\u4e16\u6001\uff0c\u4e0d\u5f97\u4e0d\u5728\u6b64\u7eb7\u4e89\u7d0a\u4e71\u4e4b\u65e5\uff0c\u51fa\u4e16\u4e3a\u4eba\uff0c\u4e00\u7edf\u5929\u4e0b\u5f0f\u6797\u4e4b\u6df7 \u4e71\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u672c\u6559\u4e3b\u62df\u4ee5\u7ec8\u5357\u5c71\u4e3a\u672c\u6559\u6839\u636e\u4e4b\u5730\uff0c\u6b64\u4e00\u540d\u5c71\uff0c\u65e5\u540e\u5fc5\u56e0\u672c\u6559\u4e4b\u660c\uff0c\u800c\u66f4\u5149\u5927\uff0c \u89c2\u4e3b\u5fc5\u4e5f\u4e50\u4e8e\u95fb\u6b64\u4e5f\u3002 \u3000\u3000\u518d\u8005\uff0c\u89c2\u4e3b\u5929\u59ff\u82f1\u53d1\uff0c\u82e5\u7ec8\u751f\u4e3a\u7ec8\u5357\u6240\u56f0\uff0c\u5b9e\u4e3a\u4e0d\u667a\u3002\u56e0\u4e4b\u672c\u6559\u4e3b\u73bb\u683c\u5c06\u6c5d\u6536\u4e3a\u5f1f\u5b50\uff0c \u4f46\u671b\u89c2\u4e3b\u8fbe\u610f\uff0c\u58f0\u8a00\u7ec8\u5357\u6d3e\u4ece\u6b64\u5f52\u4f9d\u672c\u6559\uff0c\u5219\u7ec8\u5357\u5c71\u4e0a\u6570\u767e\u5f1f\u5b50\uff0c\u5f53\u53ef\u4e0d\u836f\u800c\u6108\u3002\u56e0\u672c\u6559\u4e3b \u7edd\u4e0d\u4ee4\u95e8\u4eba\u65e5\u5915\u6c89\u4e8e\u75c5\u75db\u4e5f\u3002\u201d \u3000\u3000\u4e0b\u9762\u5177\u540d\uff1a\u201c\u5929\u6bd2\u6559\u4e3b\u201d\u3002\u8fd9\u6587\u7406\u867d\u4e0d\u751a\u901a\u987a\uff0c\u4f46\u8bcd\u610f\u5374\u975e\u5e38\u60ca\u4eba\u7684\u7eb8\u7b3a\uff0c\u4f7f\u5f97\u5999\u7075\u9053 \u4eba\u770b\u5b8c\u4e4b\u540e\uff0c\u9762\u5982\u6b7b\u7070\uff01 \u3000\u3000\u4ed6\u8fd9\u624d\u77e5\u9053\uff1a\u95e8\u4e0b\u5f1f\u5b50\uff0c\u90fd\u662f\u4e2d\u6bd2\u3002 \u3000\u3000\u4f46\u8fd9\u5929\u6bd2\u6559\u4e3b\u65bd\u6bd2\u4e4b\u6cd5\uff0c\u4ee5\u53ca\u6240\u65bd\u4e4b\u6bd2\uff0c\u90fd\u662f\u8be1\u79d8\u7384\u5947\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u800c\u4e14\u5f88\u663e\u7136\u5730\uff0c\u5999 \u7075\u9053\u4eba\u82e5\u4e0d\u7b54\u5e94\u8fd9\u8352\u8c2c\u5df2\u6781\u7684\u201c\u5efa\u8bae\u201d\uff0c\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u4fbf\u65e0\u836f\u53ef\u6cbb\uff01 \u3000\u3000\u8fd9\u201c\u5929\u6bd2\u6559\u201d\u4e09\u5b57\uff0c\u5999\u7075\u9053\u4eba\u4ece\u672a\u5165\u8033\u3002\u5929\u6bd2\u6559\u4e3b\u662f\u8c01\uff01\u600e\u4f1a\u6709\u7adf\u80fd\u4f7f\u7ec8\u5357\u5c71\u6570\u767e\u9053 \u4fa3\uff0c\u5728\u65e0\u5f62\u4e2d\u53d7\u6bd2\u7684\u795e\u901a\uff01\u4ed6\u90fd\u832b\u7136\u3002 \u3000\u3000\u6700\u4ee4\u5999\u7075\u9053\u4eba\u60ca\u9a87\u9707\u6012\u7684\uff0c\u5374\u662f\u8fd9\u5929\u6bd2\u6559\u4e3b\uff0c\u4e0d\u4f46\u8981\u81ea\u5df1\u5c06\u8fd9\u5148\u4eba\u521b\u4e1a\u591a\u5e74\u7684\u57fa\u4e1a\uff0c\u53cc \u624b\u5949\u9001\uff1a\u8fd8\u8981\u81ea\u5df1\u58f0\u8a00\u5929\u4e0b\u6b66\u6797\uff0c\u7387\u9886\u5f00\u5b97\u7acb\u6d3e\u5df2\u6570\u767e\u5e74\u7684\u7ec8\u5357\u6d3e\uff0c\u5f52\u4f9d\u5230\u4ed6\u90a3\u4ece\u672a\u542c\u8fc7\u540d \u5b57\u7684\u201c\u5929\u6bd2\u201d\u4e0b\u3002 \u3000\u3000\u8fd9\u4e8b\u522b\u4eba\u542c\u6765\uff0c\u4e5f\u8bb8\u6781\u4e3a\u8352\u8c2c\u53ef\u7b11\uff0c\u4f46\u5999\u7075\u9053\u4eba\uff0c\u5374\u7edd\u5bf9\u6ca1\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u56e0\u4e3a\u4ed6\u6df1\u6df1\u5730 \u4f53\u4f1a\u5230\u8fd9\u5f20\u5b57\u7b3a\u7684\u4e25\u91cd\uff01 \u3000\u3000\u56e0\u4e3a\uff0c\u5982\u679c\u4ed6\u4e0d\u7b54\u8986\uff0c\u95e8\u4e0b\u5782\u5371\u4e4b\u5f1f\u5b50\uff0c\u663e\u7136\u65e0\u6551\u3002\u800c\u4ed6\u867d\u662f\u7ec8\u5357\u6d3e\u7684\u638c\u95e8\uff0c\u5374\u53c8\u600e\u80fd \u7b54\u5e94\u8fd9\u65f7\u53e4\u672a\u95fb\u7684\u8981\u80c1\u5462\uff1f\u4ed6\u5fc3\u60c5\u7d0a\u4e71\uff0c\u60f6\u6050\u4e07\u72b6\uff01 \u3000\u3000\u53ef\u662f\uff0c\u5c31\u5728\u4ed6\u63a5\u5230\u90a3\u5f20\u201c\u8c15\u793a\u201d\u7684\u7b2c\u4e09\u5929\uff0c\u7ec8\u5357\u5c71\u4e0a\u7adf\u6765\u4e86\u6551\u661f\u3002 \u3000\u3000\u5728\u7ec8\u5357\u4e0a\u7384\u5999\u89c2\u540e\u56ed\u7af9\u6797\u4e2d\u7684\u4e39\u623f\u91cc\uff0c\u5999\u7075\u9053\u4eba\uff0c\u6ee1\u6000\u6006\u75db\u5730\u5c06\u8fd9\u4e8b\u539f\u539f\u672c\u672c\u8bf4\u4e86\u51fa \u6765\u3002 \u3000\u3000\u51dd\u795e\u503e\u542c\u7740\u7684\u4e24\u5927\u6b66\u6797\u5f02\u4eba\u2014\u2014\u4e07\u5251\u4e4b\u5c0a\u548c\u4e09\u5fc3\u795e\u541b\uff0c\u867d\u7136\u7d20\u6765\u884c\u4e8b\u602a\u5f02\uff0c\u5374\u4e5f\u4ece\u672a\u542c \u8fc7\u8fd9\u6837\u5947\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u81ea\u53e4\u4ee5\u6765\uff0c\u6b66\u6797\u4e2d\u65e0\u8bba\u6210\u7acb\u4efb\u4f55\u5b97\u6d3e\uff0c\u5e2e\u4f1a\uff0c\u90fd\u7edd\u65e0\u5728\u521b\u6559\u4e4b\u65f6\uff0c\u4ee5\u8981\u80c1\u624b\u6bb5\uff0c\u8981 \u6c42\u53e6\u4e00\u5b97\u6d3e\uff0c\u5168\u90e8\u5f52\u4f9d\u4e8e\u81ea\u5df1\u7684\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u201c\u4e0a\u4f53\u5929\u5fc3\uff0c\u4e00\u7edf\u6b66\u6797\u3002\u201d\u54fc\uff01\u6211\u8001\u4eba\u5bb6\u8fd8\u6ca1\u6709\u542c\u8fc7\u6709\u8fd9\u79cd\u72c2\u4eba\uff01\u4e5f\u4ece\u4e0d\u77e5\u9053\u5929\u4e0b\u8fd8 \u6709\u6211\u8001\u4eba\u5bb6\u4e0d\u80fd\u89e3\u7684\u6bd2\u3002\u5999\u7075\uff01\u4f60\u5f15\u6211\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u5251\u5148\u751f\u5fae\u4e00\u6c89\u541f\uff0c\u5374\u9053\uff1a \u3000\u3000\u201c\u4e0d\u770b\u4e5f\u7f62\u3002\u636e\u6211\u63e3\u6d4b\uff0c\u8fd9\u79cd\u65e0\u8272\u65e0\u81ed\uff0c\u80fd\u5728\u65e0\u5f62\u4e2d\u4f7f\u6570\u767e\u4eba\u4e2d\u6bd2\uff0c\u800c\u4e2d\u6bd2\u4e4b\u4eba\u5728\u660f\u8ff7 \u4e0d\u9192\u4e2d\u6e10\u6e10\u6b7b\u7684\u6bd2\u836f\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u6614\u5e74\u4e94\u6bd2\u771f\u541b\u4ee5\u5b88\u5bab\u4e4b\u7cbe\uff0c\u8718\u86db\u4e4b\u6db2\uff0c\u6bd2\u86c7\u4e4b\u8840\uff0c\u8d64 \u7ec3\u4e4b\u6c41\uff0c\u8708\u86a3\u4e4b\u553e\uff0c\u548c\u4ee5\u82d7\u7586\u6df1\u5c71\u7edd\u671b\u4e2d\u7684\u7634\u6bd2\u8349\uff0c\u518d\u52a0\u4e0a\u51e0\u79cd\u6bd2\u7269\u548c\u6210\u7684\u201c\u8680\u9aa8\u5723\u6c34\u201d\u4e4b \u5916\uff0c\u6050\u6015\u518d\u4e5f\u6ca1\u6709\u4e00\u79cd\u6bd2\u6709\u6b64\u5a01\u529b\uff01\u201d\u4ed6\u5fae\u5fae\u7f13\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u4e94\u6bd2\u771f\u541b\u5236\u6210\u6b64\u7269\u4e4b\u540e\uff0c\u9002\u9022 \u5929\u4e0b\u6b66\u6797\u540c\u9053\u7684\u541b\u5c71\u4e4b\u4f1a\uff0c\u4e94\u6bd2\u771f\u541b\u7adf\u60f3\u4ee5\u6b64\u7269\u5c06\u5929\u4e0b\u6b66\u6797\u9ad8\u624b\u4e00\u7f51\u6253\u5c3d\uff0c\u53ea\u662f\u90a3\u201c\u8680\u9aa8\u5723 \u6c34\u201d\u4e5f\u59d4\u5b9e\u5389\u5bb3\uff0c\u6570\u767e\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u679c\u7136\u4e00\u9f50\u4e2d\u6bd2\uff0c\u4e94\u6bd2\u771f\u541b\u6b63\u81ea\u626c\u626c\u5f97\u610f\uff0c\u90a3\u77e5\u5f53\u65f6\u5df2\u529f \u53c2\u9020\u5316\u7684\u4e00\u4e2a\u5947\u4eba\uff0c\u867d\u7136\u4e2d\u6bd2\uff0c\u4f46\u5374\u529f\u529b\u672a\u5931\u2014\u2014\u903c\u7740\u4e94\u6bd2\u771f\u541b\u53d6\u51fa\u89e3\u836f\uff0c\u624d\u514d\u4e86\u6b66\u6797\u8fd9\u4e00 \u573a\u6d69\u52ab\u3002\u201d \u3000\u3000\u5ba4\u4e2d\u8bf8\u4eba\u90fd\u51dd\u89c6\u7740\u4ed6\uff0c\u5c31\u8fde\u4e09\u5fc3\u795e\u541b\uff0c\u4e5f\u5728\u9759\u542c\u4ed6\u7684\u4e0b\u6587\u3002 \u3000\u3000\u4ed6\u5fae\u559f\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e94\u6bd2\u771f\u541b\u4e5f\u88ab\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u4e00\u638c\u5288\u6b7b\uff0c\u53ea\u662f\u4ed6\u4eec\u5236\u4f5c\u7684\u4e00\u6a3d\u201c\u8680\u9aa8\u5723\u6c34\u201d\uff0c\u636e\u8bf4\u53ea \u7528\u4e86\u6570\u6ef4\uff0c\u5176\u4f59\u7684\u7adf\u4e0d\u77e5\u4e0b\u843d\u4e86\u3002\u201d \u3000\u3000\u5b59\u654f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a \u3000\u3000\u201c\u90a3\u6bd2\u6c34\u53ea\u7528\u4e86\u51e0\u6ef4\uff0c\u5c31\u80fd\u4f7f\u6570\u767e\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u4e00\u9f50\u4e2d\u6bd2\u5417\uff1f\u201d \u3000\u3000\u5251\u5148\u751f\u7f13\u7f13\u9053\uff1a\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u90a3\u4e94\u6bd2\u771f\u541b\u662f\u5c06\u6bd2\u6c41\u6ef4\u5165\u98df\u6c34\u4e4b\u5185\uff0c\u867d\u4ec5\u4ec5\u6570\u6ef4\uff0c\u5374 \u5df2\u4f7f\u90a3\u6ee1\u6eaa\u4e4b\u6c34\uff0c\u90fd\u53d8\u6210\u4e86\u6781\u4e3a\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u6211\u4e00\u542c\u5999\u7075\u8d24\u5951\u6240\u8bf4\u7684\u60c5\u5f62\uff0c\u4fbf\u77e5\u9053\u90a3\u201c\u8680\u9aa8 \u5723\u6c34\u201d\uff0c\u53c8\u518d\u6b21\u51fa\u73b0\u3002\u60f3\u6765\u4e5f\u5fc5\u662f\u7ec8\u5357\u5c71\u7684\u98df\u6c34\u6eaa\u4e2d\uff0c\u88ab\u4eba\u65bd\u4e86\u8fd9\u79cd\u6bd2\u6c41\uff0c\u800c\u4e2d\u6bd2\u4e4b\u4eba\uff0c\u529f \u529b\u6df1\u6d45\u4e0d\u540c\uff0c\u662f\u4ee5\u53d1\u4f5c\u7684\u65f6\u95f4\uff0c\u4e5f\u524d\u540e\u5404\u5f02\u3002\u201d \u3000\u3000\u5999\u7075\u9053\u4eba\u5374\u6000\u7591\u5730\u95ee\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e48\u5c0f\u4f84\u4e5f\u66fe\u996e\u8fc7\u6eaa\u6c34\uff0c\u5374\u600e\u7684\u4e1d\u6beb\u6ca1\u6709\u4e2d\u6bd2\u7684\u8ff9\u8c61\u5462\uff1f\u201d \u3000\u3000\u5251\u5148\u751f\u7709\u5fc3\u7d27\u76b1\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u53ef\u80fd\u662f\u65bd\u6bd2\u4e4b\u4eba\uff0c\u4e3a\u4e86\u7559\u4f60\u6709\u7528\uff0c\u662f\u4ee5\u4e58\u4f60\u4e0d\u89c9\u65f6\uff0c\u5728\u4f60\u98df\u7269\u4e2d\u6697\u6697\u653e\u4e0b\u89e3\u836f\u2014 \u2014\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u5374\u9053\uff1a \u3000\u3000\u201c\u4f60\u5374\u53c8\u600e\u80fd\u5982\u6b64\u786e\u5b9a\uff0c\u8fd9\u6bd2\u5c31\u662f\u90a3\u201c\u8680\u9aa8\u5723\u6c34\u201d\u5462\uff1f\u6614\u5e74\u541b\u5c71\u4e4b\u4f1a\uff0c\u6211\u867d\u672a\u53ca\u8d76\u4e0a\uff0c \u4f46\u4e5f\u66fe\u542c\u4eba\u8bf4\u8fc7\uff0c\u53ea\u662f\u6ca1\u6709\u8fd9\u822c\u8be6\u5c3d\u7f62\u4e86\u3002\u96be\u9053\u5929\u4e0b\u5c31\u6ca1\u6709\u7b2c\u4e8c\u79cd\u5982\u6b64\u6bd2\u7684\u6bd2\u836f\u5417\uff1f\u201d \u3000\u3000\u5251\u5148\u751f\u5fae\u559f\u4e00\u58f0\uff0c\u53f9\u9053\uff1a \u3000\u3000\u201c\u6211\u4e4b\u6240\u4ee5\u5982\u6b64\u786e\u5b9a\uff0c\u56e0\u4e3a\u6211\u90a3\u65f6\u5e74\u9f84\u867d\u6781\u5e7c\u5c0f\uff0c\u5374\u4e5f\u968f\u7740\u5148\u5e08\u53c2\u4e0e\u6b64\u4f1a\uff0c\u4e5f\u4e2d\u4e86\u5982\u6b64 \u4e4b\u6bd2\u3002\u201d \u3000\u3000\u201c\u8fd1\u5e74\u6211\u6d6a\u8ff9\u5929\u6daf\uff0c\u5728\u6ec7\u897f\u4e00\u5e26\uff0c\u5c31\u66fe\u542c\u5230\u4e00\u4f4d\u6545\u4eba\u8bf4\u8d77\uff0c\u4e94\u6bd2\u771f\u541b\u7684\u201c\u8680\u9aa8\u5723\u6c34\u201d\uff0c \u53c8\u91cd\u73b0\u6c5f\u6e56\uff0c\u5374\u60f3\u4e0d\u5230\u7ec8\u5357\u5f1f\u5b50\uff0c\u7adf\u90fd\u4e2d\u4e86\u6b64\u6bd2\uff01\u201d \u3000\u3000\u5b59\u654f\u867d\u7136\u6ca1\u6709\u542c\u8fc7\u6570\u5341\u5e74\u524d\u7684\u9b54\u5934\u2014\u2014\u4e94\u6bd2\u771f\u541b\u7684\u540d\u5b57\uff0c\u4f46\u542c\u5251\u5148\u751f\u8bf4\u5f97\u5982\u6b64\u6c89\u91cd\uff0c\u5c31 \u77e5\u9053\u6b64\u6bd2\u5fc5\u5b9a\u975e\u540c\u5c0f\u9119\uff0c\u9edb\u7709\u4e0d\u7981\u7d27\u76b1\u3002 \u3000\u3000\u800c\u5999\u7075\u9053\u4eba\u66f4\u662f\u60f6\u6050\u4e0b\u5df2\uff0c\u6ee1\u8138\u60b2\u6006\u4e4b\u8272\u3002 \u3000\u3000\u53ea\u6709\u4e09\u5fc3\u795e\u541b\uff0c\u4e24\u773c\u5fae\u95ed\uff0c\u4f3c\u4e4e\u9677\u5165\u6c89\u601d\u3002\u826f\u4e45\uff0c\u4ed6\u624d\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4ee5\u4e03\u79cd\u4ee5\u4e0a\u7684\u7edd\u6bd2\u4e4b\u7269\uff0c\u5408\u6210\u7684\u6bd2\u836f\uff0c\u6211\u4e5f\u65e0\u6cd5\u53ef\u89e3\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u76ee\u6ce8\u5251\u5148\u751f\u9053\uff1a \u3000\u3000\u201c\u6570\u5341\u5e74\u6765\uff0c\u6211\u59cb\u7ec8\u65e0\u6cd5\u731c\u900f\u4f60\u7684\u5e08\u627f\u6765\u5386\uff0c\u4f60\u4e00\u8bf4\u6b64\u4e8b\uff0c\u6211\u5012\u60f3\u8d77\u6765\u4e86\uff0c\u90a3\u89e3\u836f\u653e\u5728 \u4f55\u5904\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u4e0d\u7981\u4e00\u6014\uff01 \u3000\u3000\u5251\u5148\u751f\u4e5f\u81ea\u9762\u8272\u5fae\u53d8\uff0c\u4f46\u4ecd\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u6211\u4e4b\u5e08\u627f\u6765\u5386\uff0c\u672c\u65e0\u4e0d\u53ef\u544a\u4eba\u4e4b\u5904\uff0c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u5c31\u8be5\u77e5\u9053\u6211\u7684\u82e6\u8877\u3002\u81f3\u4e8e\u90a3\u89e3\u836f\uff0c \u6614\u5e74\u679c\u6709\u5269\u4e0b\uff0c\u4f46\u90a3\u4f4d\u524d\u8f88\u5947\u4eba\uff0c\u540e\u6765\u4e3a\u7740\u4e00\u4e8b\uff0c\u75db\u6068\u5929\u4e0b\u4eba\uff0c\u5c06\u6b64\u89e3\u836f\u8fde\u540c\u4e00\u672c\u4e0a\u9762\u8bb0\u8f7d \u7740\u4ed6\u4e00\u751f\u6b66\u529f\u7cbe\u7cb9\u7684\u79d8\u7b08\uff0c\u548c\u4e00\u9897\u4e24\u767e\u5e74\u524d\u4e1c\u6d77\u5c60\u9f99\u4ed9\u5b50\u6240\u5236\uff0c\u80fd\u593a\u5929\u5730\u9020\u5316\u4e4b\u529f\u7684\u201c\u6bd2\u9f99 \u4e38\u201d\uff0c\u90fd\u5c01\u5728\u4e00\u4e2a\u7edd\u79d8\u5bc6\u7684\u6240\u5728\u3002\u58f0\u8a00\uff1a\u65e5\u540e\u82e5\u6709\u4e00\u4eba\u987b\u5403\u4e86\u4ed6\u5f53\u65f6\u6240\u8eab\u53d7\u4e4b\u82e6\u8005\uff0c\u624d\u80fd\u5f97 \u5230\u6b64\u7269\u3002\u800c\u90a3\u4f4d\u6b66\u529f\u5999\u7edd\u5929\u4eba\u7684\u5f02\u4eba\uff0c\u7adf\u5728\u4e07\u5ff5\u4ff1\u7070\u7684\u5fc3\u5883\u4e0b\uff0c\u5f15\u5200\u81ea\u51b3\u4e86\uff01\u201d \u3000\u3000\u5b59\u654f\u548c\u5999\u7075\u9053\u4eba\uff0c\u90fd\u65e0\u6cd5\u63e3\u900f\u5251\u5148\u751f\u53e3\u4e2d\u7684\u6b66\u6797\u5f02\u4eba\uff0c\u5012\u5e95\u662f\u8c01\uff01 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u5374\u4fef\u9996\u6c89\u601d\uff0c\u7a81\u7136\u51dd\u805a\u771f\u6c14\uff0c\u4ee5\u4f20\u97f3\u4e4b\u6cd5\uff0c\u5411\u5251\u5148\u751f\u9053\uff1a \u3000\u3000\u201c\u6211\u548c\u4f60\u76f8\u4ea4\u591a\u5e74\uff0c\u8be5\u7b97\u77e5\u53cb\uff0c\u6b64\u523b\u6211\u53ea\u95ee\u4f60\u4e00\u8a00\uff0c\u6b66\u66f2\u661f\u541b\u72ec\u5b64\u7075\u662f\u4f60\u4f55\u4eba\uff01\u4ed6\u90a3\u672c \u201c\u5929\u661f\u79d8\u7b08\u201d\u7684\u85cf\u5904\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u662f\u5426\u53ea\u6709\u4f60\u4e00\u4eba\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b59\u654f\u548c\u5999\u7075\u9053\u4eba\uff0c\u832b\u7136\u671b\u7740\u4e09\u5fc3\u795e\u541b\uff0c\u4e0d\u77e5\u9053\u4ed6\u5728\u8bf4\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u5251\u5148\u751f\u9762\u4e0a\u7684\u795e\u8272\uff0c\u867d\u7136\u6781\u529b\u63a7\u5236\uff0c\u4f46\u4ecd\u5927\u53d8\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u51dd\u6ce8\u4e09\u5fc3\u795e\u541b\uff0c\u4e5f\u4ee5\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u4e4b\u6cd5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u65e2\u5df2\u731c\u7834\uff0c\u591a\u8a00\u4f55\u76ca\uff1f\u6614\u5e74\u4e4b\u4e8b\uff0c\u4ee4\u6211\u7ec8\u751f\u96be\u5b89\uff0c\u662f\u4ee5\u6211\u4ece\u6765\u4e0d\u4ee5\u771f\u9762\u76ee\u793a\u4eba\u3002\u90a3 \u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\u7684\u85cf\u5904\uff0c\u7684\u786e\u5929\u4e0b\u53ea\u6709\u6211\u4e00\u4eba\u77e5\u9053\uff0c\u4f46\u6211\u9664\u975e\u9047\u5230\u90a3\u4f4d\u5947\u4eba\u53e3\u4e2d\u6240\u8bf4\u4e4b\u4eba\uff0c \u4e5f\u7edd\u4e0d\u4f1a\u5bf9\u4eba\u8bf4\u51fa\u3002\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u53cc\u773c\u4e00\u5f20\uff0c\u4f46\u5374\u7acb\u523b\u95ed\u4e86\u8d77\u6765\uff0c\u82e5\u6709\u6240\u5931\u5730\u8bf4\u9053\uff1a\u201c\u6211\u591a\u5e74\u6f5c\u5c45\uff0c\u6b64\u6b21\u4e0b\u5c71\uff0c \u591a\u534a\u5c31\u662f\u4e3a\u4e86\u8fd9\u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u4f46\u6211\u7adf\u5c06\u9690\u5c45\u4e8e\u9752\u6d77\u7a46\u9c81\u4e4c\u82cf\u6cb3\uff0c\u5e03\u514b\u9a6c\u56e0\u5c71\u53e3\u7684\u65e0\u540d\u602a \u53df\uff0c\u8ba4\u505a\u662f\u6b66\u66f2\u661f\u541b\u72ec\u5b64\u7075\u7684\u552f\u4e00\u5f1f\u5b50\u3002\u6211\u4eca\u6668\u624d\u8bf4\u6709\u4e8b\u6c42\u4f60\u76f8\u52a9\uff0c\u5c31\u662f\u8981\u4f60\u540c\u5f80\u9752\u6d77\uff0c\u5bfb \u627e\u8fd9\u201c\u5929\u661f\u79d8\u7b08\u201d\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u7adf\u4e0d\u518d\u4f20\u97f3\uff0c\u653e\u58f0\u9053\uff1a \u3000\u3000\u201c\u90a3\u77e5\u6211\u5dee\u4e4b\u6beb\u5398\uff0c\u8c2c\u4e4b\u5343\u91cc\uff0c\u8fd9\u5fc3\u613f\u53ea\u6709\u843d\u7a7a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u53cc\u773c\u518d\u6b21\u5f20\u5f00\uff0c\u4e24\u9053\u795e\u5149\uff0c\u5229\u5203\u822c\u5730\u843d\u5728\u5251\u5148\u751f\u8138\u4e0a\uff0c\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u4f60\u82e5\u4e0d\u8bf4\u51fa\u90a3\u89e3\u836f\u7684\u4e0b\u843d\uff0c\u96be\u9053\u5fcd\u5fc3\u773c\u770b\u7389\u673a\u8001\u9053\u7684\u6570\u767e\u5f1f\u5b50\uff0c\u90fd\u846c\u9001\u5728\u8fd9\u201c\u4e94 \u6bd2\u771f\u541b\u201d\u7684\u201c\u8680\u9aa8\u5723\u6c34\u201d\u4e4b\u4e0b\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4f4d\u795e\u8272\u51b7\u6f20\u7684\u5f02\u4eba\uff0c\u6b64\u65f6\u5374\u90fd\u5927\u5931\u5e38\u6001\uff1a\u5c24\u5176\u662f\u5251\u5148\u751f\uff0c\u8138\u4e0a\u7adf\u9732\u51fa\u75db\u82e6\u4e4b\u8272\uff0c\u663e \u89c1\u5f97\u5185\u5fc3\u4e4b\u77db\u76fe\uff0c\u5df2\u8fbe\u6781\u5904\uff01 \u3000\u3000\u5b59\u654f\u7f13\u7f13\u8e31\u5230\u5e8a\u524d\uff0c\u7a81\u7136\u770b\u5230\u90a3\u5192\u6b7b\u6551\u5979\u7684\u9752\u5e74\u4fa0\u58eb\uff0c\u8138\u5b54\u5728\u706f\u5149\u4e0b\u82cd\u767d\u53ef\u6016\uff0c\u8f7b\u8f7b\u4f38 \u624b\u4e00\u63a2\uff0c\u9f3b\u606f\u7adf\u5df2\u5728\u82e5\u6709\u82e5\u65e0\u4e4b\u95f4\uff0c\u5979\u5927\u9a87\u4e4b\u4e0b\uff0c\u5fcd\u4e0d\u4f4f\u201c\u54ce\u5440\u201d\u4e00\u58f0\uff0c\u8131\u53e3\u60ca\u547c\u4e86\u51fa\u6765\uff01 \u3000\u3000\u8fd9\u4e00\u58f0\u60ca\u547c\uff0c\u4f7f\u5f97\u4e39\u623f\u4e2d\u53e6\u5916\u4e09\u4eba\uff0c\u76ee\u5149\u90fd\u8f6c\u5230\u5979\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u770b\u6837\u5b50\u4e0d\u6210\u4e86\uff01\u201d\u5b59\u654f\u60f6\u6025\u5730\u8bf4\u9053\uff0c\u7126\u8651\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u8d70\u5230\u5e8a\u524d\u9053\uff1a \u3000\u3000\u201c\u6211\u6551\u5f97\u4e00\u4eba\uff0c\u4e14\u6551\u4e00\u4eba\u3002\u201d \u3000\u3000\u4fa7\u76ee\u4e00\u671b\u5251\u5148\u751f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u81f3\u4e8e\u5176\u4ed6\u7684\u6570\u767e\u6761\u4eba\u547d\uff0c\u5c31\u5168\u64cd\u5728\u4f60\u7684\u624b\u4e0a\u4e86\uff01\u201d\u8bed\u58f0\u6c89\u91cd\u3002 \u3000\u3000\u5b59\u654f\u5fae\u559f\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u4eba\u8a00\u771f\u7684\u4e0d\u53ef\u5c3d\u4fe1\uff0c\u6c5f\u6e56\u4e0a\u4f20\u8a00\u4e09\u5fc3\u795e\u541b\u6076\u540d\u5f70\u7740\uff0c\u90a3\u77e5\u5374\u662f \u4e2a\u5b85\u5fc3\u4ec1\u539a\u7684\u4fa0\u58eb\uff01\u201d \u3000\u3000\u5979\u5374\u4e0d\u77e5\u9053\uff0c\u4e09\u5fc3\u795e\u541b\uff0c\u6f5c\u5c45\u4e8c\u5341\u4f59\u5e74\u4e4b\u540e\uff0c\u65e9\u5df2\u5927\u5927\u5730\u6539\u53d8\u4e86\u6027\u60c5\u54e9\uff01 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u4e09\u7ae0\u3000\u4e0d\u582a\u56de\u9996>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u4e0d\u582a\u56de\u9996 \u3000\u3000\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u660f\u8ff7\u4e0d\u9192\uff0c\u547d\u5982\u6e38\u4e1d\u7684\u4f0a\u98ce\uff0c\u7f13\u7f13\u7741\u5f00\u773c\u6765\uff0c\u53d1\u73b0\u81ea\u5df1\u5728\u4e00\u95f4\u623f\u9876\u751a\u9ad8 \u7684\u623f\u95f4\u91cc\uff0c\u56db\u80a2\u767e\u9ab8\uff0c\u5374\u90fd\u50cf\u662f\u6563\u4e86\u4e00\u6837\uff0c\u4e24\u53ea\u7099\u70ed\u7684\u624b\u638c\uff0c\u5728\u4ed6\u8eab\u540e\u7f13\u7f13\u79fb\u7740\uff0c\u638c\u5fc3\u53d1\u51fa \u7684\u70ed\u529b\uff0c\u4f7f\u5f97\u81ea\u5df1\u8eab\u4f53\u91cc\u9762\uff0c\u53d1\u751f\u4e86\u4e00\u9635\u9635\u5947\u5999\u7684\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u662f\u6709\u4e00\u4e2a\u5185\u5bb6\u9ad8\u624b\uff0c\u6b63\u4e0d\u60dc\u8017\u635f\u5143\u6c14\uff0c\u6765\u4e3a\u4ed6\u6253\u901a\u5947\u7ecf\u516b\u8109\u3002\u4ed6\u4e0d\u77e5\u9053\u4eba\u5bb6\u662f \u8c01\uff1f\u5fc3\u91cc\u4e5f\u6726\u6726\u80e7\u80e7\u7684\uff0c\u6df7\u6c8c\u4e00\u7247\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u60f3\u8d77\u4e86\u81ea\u5df1\u6655\u8ff7\u4ee5\u524d\u7684\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u5730\u5947\u602a\u3002 \u3000\u3000\u8fd9\u4e9b\u5929\u6765\uff0c\u4ed6\u4e00\u76f4\u9677\u4e8e\u660f\u8ff7\u4e2d\uff0c\u6240\u6709\u53d1\u751f\u7684\u4e8b\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u3002\u6b64\u523b\u4ed6\u867d\u5df2\u6062\u590d\u77e5\u89c9\uff0c\u4f46 \u65e0\u8bba\u6c14\u529b\u548c\u5fc3\u667a\uff0c\u90fd\u8fd8\u8870\u5f31\u5f97\u5f88\uff0c\u751a\u81f3\u65e0\u6cd5\u96c6\u4e2d\u601d\u60f3\u53bb\u601d\u7d22\u4efb\u4f55\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u7684\u547d\u603b\u7b97\u6361\u56de\u6765\u4e86\uff0c\u4ed6\u8eab\u53d7\u201c\u593a\u547d\u53cc\u96c4\u201d\u7684\u4e24\u5904\u91cd\u521b\uff0c\u8fde\u65e5\u8f66\u9a6c\u5954\u6ce2\uff0c\u518d\u52a0\u4e0a \u8fd9\u4e9b\u65e5\u5b50\u6765\u5fc3\u4e2d\u4e00\u76f4\u79ef\u90c1\u672a\u6d88\u3002\u4e8e\u662f\u5916\u72fc\u5185\u864e\uff0c\u4ea4\u76f8\u714e\u71ac\uff0c\u5230\u4e86\u5999\u7075\u9053\u4eba\u7684\u4e39\u623f\u4e2d\uff0c\u751f\u547d\u4e2d \u6240\u5269\u4e0b\u7684\u7cbe\u529b\uff0c\u5df2\u7ecf\u5f88\u96be\u652f\u6301\u4ed6\u518d\u6d3b\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u68c0\u89c6\u4e4b\u4e0b\uff0c\u624d\u53d1\u73b0\u4ed6\u7684\u4f24\u52bf\uff0c\u7adf\u6bd4\u81ea\u5df1\u60f3\u50cf\u4e2d\u8fd8\u8981\u4e25\u91cd\u5f97\u591a\uff01\u4f46\u4e3a\u4e86\u81ea\u5df1\u66fe\u7ecf \u5bf9\u4eba\u5bb6\u7684\u5141\u8bfa\uff0c\u7adf\u4e0d\u60dc\u4ee5\u591a\u5e74\u6765\u91c7\u96c6\u800c\u6210\u7684\u7075\u836f\uff0c\u8d39\u4e86\u65e0\u7a77\u5fc3\u8840\u624d\u5236\u6210\u7684\u201c\u518d\u9020\u4e38\u201d\uff0c\u589e\u5f3a \u4e86\u4f0a\u98ce\u751f\u547d\u7684\u673a\u80fd\u3002\u7136\u540e\u518d\u62da\u8017\u81ea\u5bb6\u7684\u771f\u6c14\uff0c\u4e3a\u4ed6\u6253\u901a\u5947\u7ecf\u516b\u8109\uff0c\u9664\u4e86\u4e09\u5fc3\u795e\u541b\u4e4b\u5916\uff0c\u4e16\u4e0a \u6050\u6015\u5f88\u5c11\u6709\u4eba\u80fd\u81ea\u51a5\u51a5\u4e2d\uff0c\u593a\u56de\u4ed6\u5341\u6210\u5df2\u6b7b\u4e86\u4e5d\u6210\u7684\u751f\u547d\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u81ea\u5df1\uff0c\u53ef\u4e0d\u77e5\u9053\u81ea\u5bb6\u6240\u9047\u7684\u7edd\u4e16\u5947\u7f18\uff0c\u53ea\u89c9\u5f97\u5728\u81ea\u5df1\u8eab\u4e0a\u79fb\u52a8\u7684\u624b\u638c\uff0c\u6108\u6765\u6108\u6025\uff0c \u540e\u624b\u7adf\u6539\u629a\u4e3a\u62cd\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u81ea\u5df1\u8eab\u4e0a\u7684\u4e00\u767e\u53e6\u516b\u5904\u5927\u7a74\uff0c\u90fd\u88ab\u4eba\u6781\u5feb\u5730\u62cd\u4e86\u4e00\u904d\uff0c\u5fc3\u4e2d\u4e00 \u7545\uff0c\u6d4a\u6c14\u6b32\u51fa\uff0c\u201c\u5440\u201d\u5730\uff0c\u5410\u51fa\u4e00\u5806\u5e26\u7740\u8840\u4e1d\u7684\u6d53\u75f0\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u4f4f\u624b\u7684\u65f6\u5019\uff0c\u989d\u4e0a\u5df2\u7ecf\u5fae\u5fae\u6c81\u51fa\u6c57\u73e0\uff0c\u4ed6\u4ecd\u76d8\u5750\u672a\u52a8\uff0c\u6084\u7136\u5408\u4e0a\u773c\u775b\uff0c\u8ba9\u81ea\u5df1 \u7684\u771f\u6c14\u5728\u8017\u635f\u4e4b\u540e\uff0c\u6062\u590d\u4e00\u4e0b\u3002 \u3000\u3000\u5ba4\u4e2d\u9759\u5f97\u6015\u4eba\uff0c\u5999\u7075\u9053\u4eba\u5782\u624b\u800c\u7acb\uff0c\u6ee1\u8138\u60b2\u6006\uff0c\u50cf\u662f\u4e00\u5c0a\u77f3\u50cf\u4f3c\u7684\uff0c\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5251\u5148\u751f\u5782\u76ee\u800c\u5750\uff0c\u9762\u4e0a\u867d\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u4f46\u4ece\u4ed6\u7d27\u63e1\u7740\u7684\u624b\u638c\u4e2d\uff0c\u4e0d\u96be\u770b\u51fa\u8fd9\u4f4d\u6b66\u6797\u5f02\u4eba \u7684\u601d\u60f3\uff0c\u6b63\u9677\u5165\u6781\u5ea6\u77db\u76fe\u4e4b\u4e2d\u3002 \u3000\u3000\u5b59\u654f\u5219\u7741\u7740\u773c\u775b\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u6b63\u5728\u4e3a\u81ea\u5df1\u7684\u6069\u4eba\u7597\u4f24\u4e2d\u7684\u4e09\u5fc3\u795e\u541b\uff0c\u76f4\u5230\u4f0a\u98ce\u9192 \u6765\uff0c\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u5979\u624d\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u81f3\u4e8e\u51cc\u7433\uff0c\u5979\u7684\u4f24\u52bf\u8f83\u8f7b\uff0c\u65b9\u624d\u670d\u8fc7\u4e09\u5fc3\u795e\u541b\u7684\u7075\u836f\uff0c\u5df2\u81ea\u6c89\u6c89\u5165\u7761\u3002\u5a07\u7f8e\u5982\u82b1\u7684\u9762\u9765 \u4e0a\uff0c\u5df2\u9690\u9690\u6cdb\u51fa\u7ea2\u8272\u3002 \u3000\u3000\u4f24\u8005\u5df2\u6108\uff0c\u5b59\u654f\u5fc3\u4e8b\u987f\u677e\u3002\u8f6c\u773c\u4e00\u671b\uff0c\u770b\u5230\u5251\u5148\u751f\u7684\u795e\u8272\uff0c\u53c8\u4e0d\u7981\u607b\u7136\uff01 \u3000\u3000\u5979\u867d\u4e0d\u77e5\u9053\u8fd9\u4f4d\u5bf9\u5979\u7279\u522b\u597d\u7684\u5f02\u4eba\uff0c\u6709\u4ec0\u4e48\u4e8b\u53d1\u751f\uff0c\u4f46\u5374\u77e5\u9053\u4ed6\u4e00\u5b9a\u6709\u7740\u6781\u5927\u7684\u56f0\u96be\u3002 \u800c\u6b64\u523b\uff0c\u5979\u4e0d\u7981\u7981\u6df1\u6df1\u5e0c\u671b\u81ea\u5df1\u6709\u8fd9\u4efd\u80fd\u529b\u53bb\u5e2e\u52a9\u4ed6\u3002 \u3000\u3000\u826f\u4e45\uff0c\u4e39\u623f\u4e2d\u624d\u4ece\u6b7b\u5bc2\u82cf\u9192\u8fc7\u6765\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\uff0c\u98d8\u7136\u4e0b\u5e8a\uff0c\u76ee\u4e2d\u795e\u91c7\uff0c\u53c8\u590d\u83b9\u7136\u3002\u5728\u4ed6\u8017\u635f\u4e86\u5982\u8bb8\u771f\u6c14\u4e4b\u540e\uff0c\u8fd8\u80fd\u5982\u6b64\uff0c\u5176 \u5185\u529f\u4e4b\u6df1\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8d70\u5230\u5251\u5148\u751f\u8eab\u524d\uff0c\u51dd\u89c6\u4e86\u7247\u523b\uff0c\u624d\u6c89\u91cd\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u6570\u5341\u5e74\u76f8\u4ea4\uff0c\u6211\u6df1\u77e5\u4f60\u7684\u4e3a\u4eba\uff0c\u5173\u4e8e\u6b64\u4e8b\uff0c\u4f60\u5fc3\u4e2d\u5b9a\u6709\u7740\u6781\u5927\u56f0\u96be\uff0c\u4f46\u4f60\u5374\u600e\u80fd \u773c\u770b\u7740\u6570\u767e\u6761\u4eba\u547d\u6b7b\u53bb\u5462\uff1f\u201d \u3000\u3000\u5b59\u654f\u8d70\u5230\u5e8a\u4fa7\uff0c\u89c1\u5230\u4f0a\u98ce\u53cc\u773c\u7d27\u95ed\uff0c\u4e5f\u4f3c\u4e4e\u9677\u4e8e\u6c89\u7761\u4e2d\u3002\u542c\u5230\u4e09\u5fc3\u795e\u541b\u7684\u8bdd\uff0c\u661f\u76ee\u4e00 \u5f20\uff0c\u7a81\u7136\u8f6c\u8eab\u9053\uff1a \u3000\u3000\u201c\u7167\u8001\u524d\u8f88\u65b9\u624d\u7684\u63a8\u6d4b\uff0c\u90a3\u81ea\u79f0\u5929\u6bd2\u6559\u4e3b\u4e4b\u4eba\uff0c\u5fc5\u5b9a\u6709\u7740\u89e3\u836f\uff0c\u90a3\u4e48\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u4ee5 \u4ece\u4ed6\u8eab\u4e0a\uff0c\u903c\u51fa\u89e3\u836f\u5462\uff1f\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u51b7\u7136\u9053\uff1a \u3000\u3000\u201c\u8bdd\u867d\u4e0d\u9519\uff0c\u4f46\u90a3\u5929\u6bd2\u6559\u4e3b\u662f\u8c01\uff0c\u90fd\u65e0\u6cd5\u77e5\u9053\uff0c\u9664\u975e\u4ed6\u73b0\u8eab\u51fa\u6765\uff0c\u5426\u5219\u5374\u4f55\u5904\u627e\u4ed6 \u53bb\uff1f\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f46\u8fd9\u7ec8\u5357\u95e8\u4e0b\u7684\u6570\u767e\u5f1f\u5b50\uff0c\u5374\u662f\u4eba\u4eba\u5371\u5728\u65e6\u5915\uff0c\u82e5\u662f\u6b7b\u7b49\uff0c\u90a3\u4e48\uff0c\u591a\u7b49\u4e00\u5929\uff0c\u53c8\u4e0d\u77e5 \u8981\u727a\u7272\u591a\u5c11\u4eba\u547d\uff1f\u987b\u77e5\u4eba\u547d\u5173\u5929\uff0c\u4efb\u4f55\u4eba\u7684\u6027\u547d\uff0c\u90fd\u662f\u53ef\u8d35\u7684\u3002\u82e5\u662f\u4f60\u7684\u5b50\u5973\u4e5f\u4e2d\u4e86\u6b64\u6bd2\uff0c \u60f3\u6765\u4f60\u4e0d\u4f1a\u8bf4\u51fa\u6b64\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u9010\u6e10\u4e25\u5389\uff0c\u5b59\u654f\u4e0d\u7981\u60ed\u6127\u5f97\u5782\u4e0b\u8138\u53bb\uff0c\u5fc3\u4e2d\u53ea\u6709\u81ea\u8d23\uff0c\u5374\u6ca1\u6709\u4e00\u4e1d\u602a\u4ed6\u8bf4\u8bdd\u592a\u91cd \u4e4b\u610f\u3002\u56e0\u4e3a\u4ed6\u4eec\u8bf4\u7684\u8bdd\uff0c\u4e8e\u60c5\u4e8e\u7406\uff0c\u90fd\u662f\u65e0\u61c8\u53ef\u51fb\u7684\u3002 \u3000\u3000\u5251\u5148\u751f\u8138\u8272\u66f4\u662f\u6c89\u91cd\u3002\u7a81\u5730\u5f20\u76ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u8981\u602a\u6211\u4e0d\u8fd1\u4eba\u60c5\uff0c\u5176\u5b9e\u7389\u673a\u9053\u513f\u4e0e\u6211\u6570\u5341\u5e74\u76f8\u4ea4\uff0c\u6211\u5c82\u6709\u5bf9\u4ed6\u95e8\u4e0b\u5f1f\u5b50\uff0c\u6f20\u4e0d\u5173 \u5fc3\u7684\u9053\u7406\uff1f\u5c31\u975e\u5982\u6b64\uff0c\u6211\u4e5f\u65ad\u7136\u4e0d\u4f1a\u5ffd\u89c6\u4eba\u547d\uff0c\u4f55\u51b5\u8fd9\u8fd8\u5173\u7cfb\u7740\u7ec8\u5357\u4e00\u6d3e\u7684\u751f\u6b7b\uff1f\u4f46\u662f\u2026\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u773c\u5e18\u53c8\u662f\u4e00\u5782\u3002 \u3000\u3000\u59cb\u7ec8\u4e00\u8a00\u672a\u53d1\u7684\u5999\u7075\u9053\u4eba\uff0c\u5374\u7a81\u7136\u9053\uff1a \u3000\u3000\u201c\u5251\u5e08\u4f2f\u65b9\u624d\u8bf4\uff1a\u53ea\u6709\u4e00\u4e2a\u548c\u6614\u5e74\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\u53d7\u8fc7\u540c\u6837\u75db\u82e6\u7684\u4eba\uff0c\u4fbf\u53ef\u5192\u96be\u53d6\u4e50\u3002\u90a3 \u4e48\uff0c\u5251\u5e08\u4f2f\u53ef\u5426\u5c06\u90e1\u4f4d\u524d\u8f88\u5f02\u4eba\u6240\u53d7\u4e4b\u82e6\u8bf4\u51fa\u6765\uff1f\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u5251\u5148\u751f\u4e00\u6446\u624b\uff0c\u963b\u6b62\u4e86\u4ed6\u7684\u8bdd\uff0c\u8138\u4e0a\u7adf\u9732\u51fa\u75db\u82e6\u7684\u795e\u8272\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u5185\u529f\u5df2\u81fb\u7edd\u9876\uff0c\u51e0\u6210\u4e0d\u574f\u4e4b\u8eab\u3002\u767e\u5e74\u6765\u5c31\u5df2\u7ecf\u540d\u626c\u5929\u4e0b\uff0c\u53ea\u662f \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u7136\u540e\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5728\u53e4\u7a00\u4e4b\u5e74\uff0c\u7adf\u5a36\u4e86\u4e00\u4f4d\u5c11\u5973\u4e3a\u59bb\uff0c\u8fd8\u751f\u4e00\u5b50\u3002\u201d \u3000\u3000\u5b59\u654f\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5374\u542c\u4ed6\u5fae\u4e00\u505c\u987f\uff0c\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u5728\u541b\u5c71\u5927\u4f1a\u4e0a\u6551\u4e2d\u539f\u6b66\u6797\u4e00\u8109\u4e4b\u540e\uff0c\u5c31\u88ab\u4eba\u5c0a\u4e3a\u5929\u4e0b\u81f3\u5c0a\uff0c\u6c5f\u6e56\u4e0a\u65e0 \u8bba\u4f55\u4e8b\uff0c\u53ea\u8981\u4ed6\u7247\u8a00\u53ea\u5b57\uff0c\u4fbf\u53ef\u89e3\u51b3\uff0c\u8fd9\u4e5f\u662f\u5927\u5bb6\u611f\u6069\u4e4b\u610f\uff0c\u90a3\u77e5\u540e\u6765\u2014\u2014\u201d \u3000\u3000\u5251\u5148\u751f\u5728\u53d9\u8bf4\u8fd9\u4e8b\u65f6\uff0c\u66fe\u7ecf\u6570\u5ea6\u505c\u987f\uff0c\u50cf\u662f\u5185\u5fc3\u60c5\u611f\u6fc0\u52a8\u751a\u5de8\uff1a\u53c8\u50cf\u662f\u8fd9\u4e8b\u5176\u4e2d\u6709\u4e9b \u8bdd\uff0c\u662f\u4ed6\u975e\u5e38\u96be\u4ee5\u51fa\u53e3\u7684\uff0c\u4f46\u662f\u4ed6\u7ec8\u4e8e\u8bf4\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u7684\u59bb\u5b50\u5374\u5047\u501f\u4ed6\u7684\u540d\u58f0\uff0c\u7a7f\u4e86\u8499\u9762\u4e4b\u8863\uff0c\u4f7f\u51fa\u4ed6\u6240\u4f20\u6388\u7684\u6b66\u529f\uff0c\u505a\u4e86\u8bb8\u591a\u5929\u6012\u4eba\u6028 \u7684\u4e8b\uff0c\u6b66\u6797\u4e2d\u4eba\uff0c\u867d\u7136\u4e3a\u4e86\u611f\u8c22\u4ed6\u7684\u6df1\u601d\uff0c\u4f46\u65e5\u5b50\u4e45\u4e86\uff0c\u8fd8\u662f\u65e0\u6cd5\u5fcd\u53d7\u5fc3\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u591a \u5e74\u5efa\u7acb\u7684\u5a01\u671b\uff0c\u7adf\u88ab\u4ed6\u7684\u59bb\u5b50\uff0c\u5728\u4e09\u5e74\u4e4b\u4e2d\uff0c\u7834\u574f\u6b86\u5c3d\uff01\u201d \u3000\u3000\u6b64\u523b\u5df2\u662f\u591c\u6df1\uff0c\u4f46\u5ba4\u4e2d\u8bf8\u4eba\uff0c\u4e2a\u4e2a\u90fd\u5728\u51dd\u795e\u542c\uff0c\u4e1d\u6beb\u6ca1\u6709\u5026\u610f\u3002 \u3000\u3000\u4e91\u5e8a\u4e0a\u9f3b\u606f\u6c89\u6c89\uff0c\u7a97\u5916\u98ce\u58f0\u7c0c\u7c0c\uff0c\u706f\u5149\u7167\u5f97\u7a97\u7eb8\u4e00\u7247\u8721\u9ec4\u3002 \u3000\u3000\u5251\u5148\u751f\u7565\u4e3a\u79fb\u52a8\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u540e\u6765\u90a3\u4f4d\u524d\u8f88\u5f02\u7684\u59bb\u5b50\uff0c\u552f\u6050\u4e8b\u53d1\uff0c\u7adf\u7136\u8fdc\u5954\u6d77\u5916\uff0c\u6295\u5230\u6d77\u5916\u4e00\u4f4d\u9b54\u541b\u4e4b\u5904\uff0c\u505a\u4e86\u90a3 \u4eba\u7684\u4f8d\u59be\u3002\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\u5fc3\u6000\u521b\u75db\uff0c\u4e5f\u4e0d\u613f\u5230\u6d77\u5916\u53bb\u5bfb\u4ec7\uff0c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u60c5\u611f\u4e4b\u4e8b\uff0c\u6700\u4e3a\u4e0d\u53ef \u52c9\u5f3a\uff0c\u4f24\u5fc3\u4e4b\u4f59\uff0c\u5c31\u6ee1\u8154\u7231\u5ff5\uff0c\u5168\u5782\u6ce8\u5728\u4ed6\u7684\u72ec\u5b50\u8eab\u4e0a\u3002\u201d \u3000\u3000\u5b59\u654f\u4e0d\u7981\u4e3a\u4e4b\u5e7d\u5e7d\u4e00\u53f9\uff0c\u5999\u7075\u9053\u4eba\u548c\u4e09\u5fc3\u795e\u541b\uff0c\u4e5f\u6709\u607b\u7136\u4e4b\u5bb9\uff0c\u4f3c\u4e4e\u90a3\u4f24\u5fc3\u6b32\u7edd\u7684\u8001 \u4eba\uff0c\u643a\u7740\u4ed6\u7684\u7231\u5b50\uff0c\u6b64\u523b\u6b63\u7ad9\u5728\u4ed6\u4eec\u773c\u524d\u4e00\u6837\u3002 \u3000\u3000\u5251\u5148\u751f\u5fae\u5fae\u8f6c\u8fc7\u5934\u6765\uff0c\u671b\u7740\u5899\u89d2\u95f4\u7684\u4e00\u7247\u7a7a\u767d\uff0c\u53c8\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f46\u662f\u771f\u76f8\u672a\u767d\uff0c\u6b66\u6797\u4e2d\u5c06\u8fd9\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u8bcb\u6bc1\u5f97\u4e0d\u503c\u4e00\u6587\uff01\u6c5f\u6e56\u6d41\u8a00\u56db\u8d77\u3002\u8fd8\u6709\u4e9b \u4eba\uff0c\u8981\u7fa4\u7ed3\u6b66\u6797\u9ad8\u624b\uff0c\u53bb\u5bfb\u90a3\u5f02\u4eba\u590d\u4ec7\u3002 \u3000\u3000\u201c\u540e\u6765\u90a3\u8001\u4eba\u7684\u552f\u4e00\u7231\u5b50\uff0c\u7adf\u4e5f\u8bef\u4f1a\u4e86\u4ed6\u7684\u7236\u4eb2\uff0c\u5728\u4e00\u4e2a\u6708\u660e\u4e4b\u665a\uff0c\u7559\u4e66\u51fa\u8d70\uff0c\u58f0\u8a00\u81ea \u5df1\u4e0d\u518d\u8ba4\u8fd9\u4e2a\u7236\u4eb2\u3002\u201d \u3000\u3000\u5b59\u654f\u6084\u6084\u64e6\u4e86\u773c\u89d2\uff0c\u7adf\u7136\u6709\u6cea\u73e0\u6cdb\u8d77\u3002 \u3000\u3000\u5251\u5148\u751f\u5374\u53c8\u53f9\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u5fc3\u4e2d\u5df2\u662f\u6ee1\u6000\u521b\u75db\uff0c\u518d\u52a0\u4e0a\u8fd9\u4e2a\u6253\u51fb\uff0c\u3001\u5fc3\u5fd7\u7adf\u7136\u5931\u5e38\uff0c\u4ece\u9690\u5c45\u4e4b\u5904 \u590d\u51fa\u6c5f\u6e56\u3002\u4f46\u662f\u6c5f\u6e56\u4e0a\u4eba\uff0c\u53ea\u8981\u770b\u5230\u4ed6\u7684\u5f71\u5b50\uff0c\u5c31\u8fdc\u8fdc\u907f\u5f00\u3002\u8fde\u4e00\u4e9b\u7eff\u6797\u5de8\u76d7\uff0c\u90fd\u4e0d\u613f\u4e0e\u4e4b \u4e3a\u4f0d\uff0c\u540e\u6765\u201d \u3000\u3000\u4ed6\u8f7b\u8f7b\u5730\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u50cf\u662f\u63a9\u9970\u7740\u81ea\u5df1\u7684\u592a\u591a\u60b2\u75db\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\u5728\u76db\u6012\u4e4b\u4e0b\uff0c\u518d\u52a0\u4ee5\u795e\u5fd7\u5931\u5e38\uff0c\u7adf\u5c06\u6700\u6700\u770b\u4e0d\u8d77\u4ed6\u7684\u91d1\u9675\u4e09\u6770\u51fb\u6b7b\u3002\u7b49 \u5230\u9c9c\u8840\u67d3\u5230\u4ed6\u624b\u4e0a\u65f6\uff0c\u4ed6\u624d\u4ece\u6df7\u4e71\u4e4b\u4e2d\uff0c\u6e05\u9192\u8fc7\u6765\uff0c\u4f46\u662f\u53c8\u5df2\u94f8\u6210\u4e00\u9519\uff0c\u8fd9\u91d1\u9675\u4e09\u6770\uff0c\u672c\u662f \u4e49\u58f0\u9887\u7740\u7684\u4fa0\u58eb\uff0c\u8eab\u6b7b\u4e4b\u4e8b\uff0c\u7acb\u523b\u53c8\u6fc0\u8d77\u4e86\u6b66\u6797\u516c\u6124\u3002\u201d \u3000\u3000\u987b\u77e5\u4e16\u95f4\u6700\u60e8\u4e4b\u4e8b\uff0c\u83ab\u8fc7\u4e8e\u88ab\u4eba\u51a4\u5c48\u800c\u65e0\u6cd5\u4f38\u8bc9\uff01\u5ba4\u4e2d\u8bf8\u4eba\u542c\u4e86\uff0c\u90fd\u89c9\u5f97\u5fc3\u4e2d\u6c89\u91cd\u5df2 \u6781\u3002\u4e09\u5fc3\u795e\u541b\u9762\u4e0a\uff0c\u66f4\u6709\u5f02\u6837\u7684\u96be\u53d7\uff01 \u3000\u3000\u5251\u5148\u751f\u8bf4\u4e0b\u53bb\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u77e5\u9053\u4e8b\u60c5\u65e0\u6cd5\u89e3\u91ca\uff1b\u4f55\u51b5\u5230\u6b64\u65f6\uff0c\u4ed6\u8fd8\u6df1\u7231\u7740\u4ed6\u59bb\u5b50\uff0c\u4e5f\u4e0d\u613f\u89e3\u91ca\u3002 \u4e3a\u4e86\u514d\u5f97\u81ea\u5bb6\u624b\u4e0a\u518d\u67d3\u9c9c\u8840\u8d77\u89c1\uff0c\u4ed6\u8fdc\u9041\u7a77\u8352\uff1b\u53ea\u662f\u6b64\u523b\uff0c\u4ed6\u5df2\u4e0d\u518d\u662f\u5148\u524d\u7684\u4ed6\u4e86\uff01\u4ed6\u4e07\u5ff5 \u4ff1\u7070\uff0c\u59bb\u79bb\u5b50\u6563\u4e4b\u540e\uff0c\u518d\u906d\u5230\u8fd9\u79cd\u4e8b\uff0c\u4efb\u4f55\u4eba\u4e5f\u65e0\u6cd5\u5fcd\u53d7\u7684\u3002\u4e8e\u662f\u4ed6\u5c06\u81ea\u5df1\u751f\u5e73\u6b66\u529f\uff0c\u6284\u5f55 \u6210\u96c6\uff0c\u548c\u4e00\u9897\u8d39\u4e86\u65e0\u6570\u5fc3\u529b\u624d\u5f97\u6765\uff0c\u51c6\u5907\u7ed9\u4ed6\u7231\u5b50\u670d\u7528\u7684\u201c\u6bd2\u9f99\u4e38\u201d\uff0c\u4ee5\u53ca\u201c\u8680\u9aa8\u5723\u6c34\u201d\u7684 \u89e3\u836f\uff0c\u90fd\u57cb\u5165\u6ec7\u8fb9\u65e0\u91cf\u5c71\u6df1\u4e4b\u5904\u3002 \u3000\u3000\u201c\u4ed6\u7684\u513f\u5b50\u79bb\u5f00\u4ed6\u4e4b\u540e\uff0c\u904d\u5386\u6c5f\u6e56\uff0c\u77e5\u9053\u4ed6\u7236\u4eb2\u7684\u53bb\u5904\uff0c\u5230\u5e95\u7236\u5b50\u60c5\u6df1\uff0c\u8fde\u591c\u5954\u53bb\uff0c\u4f46 \u662f\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u5df2\u5728\u4e07\u5ff5\u4ff1\u7070\u4e4b\u4e0b\uff0c\u81ea\u884c\u8fd0\u529f\u9707\u7834\u5929\u7075\u3002\u4ed6\u7684\u7231\u5b50\u8d76\u5230\u7684\u65f6\u5019\uff0c\u4e5f\u5c31\u662f\u4ed6 \u4e34\u7ec8\u7684\u4e00\u523b\uff01\u201d \u3000\u3000\u4ed6\u7a81\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u5ba4\u4e2d\u7acb\u523b\u53c8\u9759\u5f97\u50cf\u575f\u5893\u4e00\u6837\uff01\u7136\u540e\uff0c\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u8bf4\uff0c\u4f60\u4eec\u60f3\u4e5f\u731c\u51fa\uff0c\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\uff0c\u5c31\u662f\u5148\u7236\uff1b\u800c\u6211\uff0c\u5c31\u90a3\u6ee1\u8eab\u7f6a\u5b7d\u7684\u513f\u5b50\u3002\u5728 \u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u6211\u53c8\u600e\u80fd\u8fdd\u80cc\u5148\u7236\u9057\u547d\uff0c\u5c06\u90a3\u85cf\u5b9d\u4e4b\u5730\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u201c\u6570\u5341\u5e74\u6765\uff0c\u6211\u9690\u59d3\u57cb\u540d\uff0c\u98d8\u6d41\u5929\u6daf\uff0c\u5c31\u662f\u60f3\u627e\u5230\u4e00\u4e2a\u5982\u6b64\u75db\u82e6\u4e4b\u4eba\u3002\u4f46\u4e16\u95f4\u75db\u82e6\u4e4b\u4eba \u867d\u591a\uff0c\u6211\u5374\u4ece\u6765\u6ca1\u6709\u53d1\u73b0\u4efb\u4f55\u4eba\u4e4b\u75db\u82e6\uff0c\u6df1\u4e8e\u5148\u7236\u7684\uff01 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u56db\u7ae0\u3000\u56e0\u7978\u5f97\u798f>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u56e0\u7978\u5f97\u798f \u3000\u3000\u4e39\u623f\u4e2d\uff0c\u6b7b\u4e00\u822c\u5730\u6c89\u5bc2 \u3000\u3000\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u51fa\u58f0\u5b89\u6170\u90a3\u6781\u4e3a\u60b2\u4f24\u7684\u5251\u5148\u751f\uff0c\u66f4\u6ca1\u6709\u4efb\u4f55\u4e00\u4eba\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8fd8\u80fd\u8bf4 \u51fa\u903c\u7740\u5251\u5148\u751f\u8bb2\u660e\u85cf\u5b9d\u4e4b\u5904\u7684\u8bdd\u6765\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e91\u5e8a\u4e0a\u7a81\u7136\u54cd\u52a8\u4e00\u4e0b\uff0c\u4e00\u4e2a\u5fae\u5f31\u7684\u58f0\u97f3\u201c\u6211\u6709\u8bdd\u8bf4\u2014\u2014\u201d \u3000\u3000\u4f17\u4eba\u4e0d\u7981\u5927\u4e3a\u60ca\u5947\uff0c\u76ee\u5149\u8f6c\u5230\u5e8a\u4e0a\uff0c\u5b59\u654f\u66f4\u8dd1\u4e86\u8fc7\u53bb\uff0c\u5374\u89c1\u5979\u90a3\u5e74\u8f7b\u7684\u6069\u4eba\uff0c\u6b63\u6323\u624e\u7740 \u8981\u722c\u8d77\u6765\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u91cd\u521b\u521d\u6108\uff0c\u867d\u7136\u5185\u670d\u7075\u4e39\uff0c\u53c8\u6253\u901a\u4e86\u5947\u7ecf\u516b\u8109\uff0c\u90a3\u4e48\u9634\u6bd2\u7684\u638c\u529b\uff0c\u5374\u4e5f\u4e0d\u662f\u4e00\u65f6 \u534a\u523b\u4e4b\u95f4\uff0c\u5c31\u53ef\u4ee5\u6062\u590d\u8fc7\u6765\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u653e\u5f03\u4e86\u6323\u624e\uff0c\u4ef0\u5367\u5e8a\u4e0a\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u5fc3\u4e2d\u5374\u4e00\u52a8\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u53ef\u662f\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\u51fa\u6765.\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u5f31\u5730\u5e94\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u5fc3\u4e2d\u6781\u5feb\u5730\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u91cd\u4f24\u521d\u6108\uff0c\u82e5\u518d\u591a\u8a00\uff0c\u5fc5\u5b9a\u53c8\u8981\u8d39\u6211\u4e00\u756a\u624b \u811a\u3002\u201d\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u60f3\u8981\u8bf4\u8bdd\uff0c\u5fc5\u5b9a\u548c\u6b64\u4e8b\u6709\u5173\u7cfb\uff0c\u83ab\u975e\u2026\u2026\u201d\u4e8e\u662f \u4ed6\u4e5f\u8d70\u5230\u5e8a\u524d\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u5c3d\u8bf4\u65e0\u59a8\uff0c\u6211\u4eec\u90fd\u542c\u5f97\u89c1\u7684\u3002\u201d \u3000\u3000\u5b59\u654f\u5fc3\u4e2d\u5927\u5947\uff1a\u201c\u4ed6\u5c1a\u672a\u590d\u5143\uff0c\u4e09\u5fc3\u795e\u541b\u5374\u600e\u5730\u8ba9\u4ed6\u8bf4\u8bdd\u5462\uff1f\u201d \u3000\u3000\u4f46\u4e5f\u4e0d\u80fd\u8bf4\u51fa\u4efb\u4f55\u53cd\u5bf9\u7684\u8bdd\u6765\uff0c\u5979\u60f3\u5230\u4e09\u5fc3\u795e\u541b\u6b64\u4e3e\uff0c\u5fc5\u6709\u6df1\u610f\u3002 \u3000\u3000\u5999\u7075\u9053\u4eba\u4e0d\u7981\u7f13\u7f13\u79fb\u52a8\u811a\u6b65\uff0c\u8d70\u5230\u5e8a\u524d\u3002 \u3000\u3000\u539f\u6765\uff0c\u4f0a\u98ce\u5e76\u672a\u6c89\u7761\uff0c\u65b9\u624d\u5ba4\u4e2d\u8bf8\u4eba\u6240\u8bf4\u4e4b\u8bdd\uff0c\u4ed6\u5b8c\u5168\u542c\u5230\u4e86\uff01\u5fc3\u4e2d\u7a81\u7136\u5347\u8d77\u4e86\u4e00\u79cd\u5f3a \u70c8\u7684\u5e0c\u671b\uff0c\u4f7f\u4ed6\u80fd\u591f\u6709\u6c14\u529b\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u867d\u7136\u542c\u6e05\u4e86\u8fd9\u4e8b\u7684\u7ecf\u8fc7\uff0c\u5374\u4ecd\u4e0d\u77e5\u9053\u8bf4\u8bdd\u7684\u4eba\uff0c\u7adf\u662f\u6570\u5341\u5e74\u524d\u5373\u5df2\u5782\u540d\u6b66\u6797\u7684\u4e07 \u5251\u4e4b\u5c0a\u3002 \u3000\u3000\u4ed6\u6323\u624e\u7740\u5fae\u5f31\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u65b9\u624d\u6211\u542c\u4e86\u90a3\u4f4d\u524d\u8f88\u6240\u8bf4\u4e4b\u4e8b\uff0c\u7684\u786e\u662f\u60e8\u7edd\u4eba\u5bf0\uff01\u4f46\u90a3\u4f4d\u524d\u8f88\u6240\u8bf4\uff1a\u201c\u4e16\u95f4\u65e0\u4eba\u7684\u75db \u82e6\u66f4\u6df1\u4e8e\u6b64\u8005\uff0c\u201d\u5c0f\u9119\u5374\u4e0d\u4ee5\u4e3a\u7136\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8bdd\u4e00\u51fa\uff0c\u8bf8\u4eba\u90fd\u5fae\u9732\u5f02\u5bb9\u3002\u5c31\u8fde\u5251\u5148\u751f\uff0c\u4e5f\u4e0d\u7981\u62ac\u8d77\u5934\u6765\u3002 \u3000\u3000\u4ed6\u8bed\u58f0\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u75db\u82e6\u7684\u79cd\u7c7b\uff0c\u5404\u6709\u4e0d\u540c\uff0c\u81ea\u7136\u4ea6\u6709\u6df1\u6d45\u4e4b\u5206\u3002\u4f46\u662f\uff0c\u82e5\u6709\u4e24\u79cd\u6027\u8d28\u5b8c\u5168\u4e0d\u540c\u7684\u75db\u82e6\uff0c \u5176\u6df1\u6d45\u4fbf\u65e0\u6cd5\u53ef\u6bd4\u3002\u4f55\u51b5\u65e0\u8bba\u4efb\u4f55\u4e00\u79cd\u75db\u82e6\uff0c\u82e5\u975e\u4eb2\u8eab\u7ecf\u5386\uff0c\u8c01\u4e5f\u65e0\u6cd5\u6e05\u695a\u5730\u4e86\u89e3\u5176\u4e2d\u6ecb \u5473\uff01 \u3000\u3000\u201c\u90a3\u4f4d\u524d\u8f88\u7684\u5c0a\u4eba\uff0c\u867d\u662f\u75db\u82e6\u7edd\u4f26\uff0c\u4f46\u82e5\u8bf4\u4e16\u95f4\u65e0\u4eba\u4e4b\u75db\u82e6\u66f4\u751a\u4e8e\u6b64\u8005\uff0c\u5374\u662f\u672a\u5fc5\u3002\u90a3 \u4f4d\u524d\u8f88\u904d\u5386\u5929\u4e0b\uff0c\u6ca1\u6709\u770b\u5230\u6709\u4eba\u4e4b\u75db\u82e6\u66f4\u6df1\u8005\uff0c\u53ea\u662f\u56e0\u4e3a\u522b\u4eba\u7684\u75db\u82e6\uff0c\u524d\u8f88\u672a\u66fe\u4eb2\u8eab\u4f53\u4f1a \u8fc7\uff0c\u53c8\u600e\u80fd\u7528\u4ee5\u548c\u81ea\u8eab\u66fe\u4f53\u4f1a\u5230\u7684\u75db\u82e6\u76f8\u6bd4\u5462\uff1f\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u867d\u7136\u5fae\u5f31\uff0c\u4f46\u8a00\u4e2d\u4e4b\u610f\uff0c\u5374\u662f\u5b57\u5b57\u9535\u7136\uff01\u4e09\u5fc3\u795e\u541b\u4e0d\u7981\u5fae\u5fae\u9894\u9996\u3002\u5b59\u654f\u63e1\u7740\u5979\u7231 \u5973\u7684\u624b\uff0c\u66f4\u662f\u542c\u5f97\u51fa\u795e\u3002 \u3000\u3000\u5251\u5148\u751f\u66f4\u662f\u8083\u7136\u52a8\u5bb9\uff0c\u6709\u751f\u4ee5\u6765\uff0c\u8fd8\u672a\u66fe\u6709\u4eba\u5728\u4ed6\u9762\u524d\u8bf4\u8fc7\u7c7b\u4f3c\u7684\u8bdd\u3002\u56e0\u4e3a\u5f88\u5c11\u6709\u4eba\uff0c \u80fd\u5c06\u201c\u75db\u82e6\u201d\u4e24\u5b57\uff0c\u5206\u6790\u5f97\u5982\u6b64\u7cbe\u8f9f\uff01 \u3000\u3000\u4f0a\u98ce\u53c8\u9053\uff1a \u3000\u3000\u201c\u8b6c\u5982\u8bf4\uff1a\u4e00\u4e2a\u666e\u901a\u4eba\uff0c\u4ed6\u59bb\u79bb\u5b50\u6563\uff0c\u53c8\u53d7\u5230\u5404\u79cd\u6076\u52bf\u529b\u7684\u6b3a\u51cc\uff0c\u751a\u81f3\u53ef\u80fd\u4eba\u5bb6\u5f53\u7740\u5730 \u9762\u51cc\u8fb1\u4ed6\u7684\u59bb\u5b50\uff0c\u8fd9\u79cd\u75db\u82e6\u53c8\u5982\u4f55\uff01\u4ed6\u4e4b\u6240\u4ee5\u4e0d\u540c\u4e8e\u90a3\u4f4d\u524d\u8f88\u7684\u5c0a\u4eba\u8005\uff0c\u53ea\u662f\u56e0\u4e3a\u4ed6\u4e0d\u4f1a\u6b66 \u529f\uff0c\u5f53\u7136\u4e0d\u53ef\u80fd\u548c\u90a3\u4f4d\u524d\u8f88\u7684\u5c0a\u4eba\u6709\u540c\u6837\u7684\u7ecf\u5386\u3002\u4f46\u662f\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u5fc3\u4e2d\u75db\u82e6\u7684\u7a0b\u5ea6\uff0c\u5374\u7edd \u4e0d\u4f1a\u7a0d\u5f31\u7684\uff01\u201d \u3000\u3000\u5251\u5148\u751f\u76ee\u5149\u51dd\u6ce8\uff0c\u4ed4\u7ec6\u5730\u4f53\u4f1a\u7740\u4ed6\u8bdd\u4e2d\u7684\u610f\u601d\u3002\u76ee\u5149\u4e4b\u4e2d\uff0c\u6e10\u6e10\u9732\u51fa\u4e00\u79cd\u522b\u4eba\u65e0\u6cd5\u4e86\u89e3 \u7684\u5149\u8292\uff0c\u50cf\u662f\u63a5\u53d7\uff0c\u53c8\u50cf\u662f\u53cd\u5bf9\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c31\u4ee5\u5c0f\u9119\u6765\u8bf4\uff1a\u5c0f\u9119\u7684\u59bb\u5b50\uff0c\u88ab\u5929\u4e89\u6559\u4e3b\u6240\u8bf1\u80c1\uff0c\u80cc\u53db\u4e86\u6211\uff0c\u4e0e\u4eba\u6deb\u5954\u3002\u5c0f\u9119\u672c\u662f\u6781 \u4e3a\u6e29\u6696\u7684\u5bb6\uff0c\u4e5f\u88ab\u5929\u4e89\u6559\u4e0b\u6240\u6bc1\u3002\u5c0f\u9119\u867d\u7136\u5fc3\u6000\u6028\u75db\uff0c\u4f46\u53c8\u600e\u80fd\u6597\u5f97\u8fc7\u5728\u6c5f\u6e56\u4e0a\u5a01\u52bf\u7edd\u4f26\u7684 \u5929\u4e89\u6559\uff1f\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u53cc\u7709\u4e00\u76b1\u3002\u4f0a\u98ce\u53c8\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u5929\u4e89\u6559\u4e3b\u66f4\u975e\u89c1\u5c0f\u9119\u4e4b\u6b7b\u624d\u7518\u5fc3\u3002\u5c0f\u9119\u4e0d\u5f97\u5df2\uff0c\u624d\u4f2a\u88c5\u6b7b\u53bb\uff0c\u8eb2\u8fc7\u5929\u4e89\u6559 \u7684\u8ffd\u7f09\u3002\u629b\u53bb\u4e86\u4e00\u5207\u5e94\u5f97\u4e4b\u7269\uff0c\u8fde\u590d\u4ec7\u7684\u5e0c\u671b\u90fd\u6ca1\u6709\uff01\u524d\u8f88\u770b\u6765\uff0c\u8fd9\u79cd\u75db\u82e6\u53c8\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u8bf4\u5230\u540e\u6765\uff0c\u4ed6\u5fae\u5f31\u7684\u8bed\u58f0\u91cc\uff0c\u5df2\u662f\u6ee1\u6000\u60b2\u6028\uff01\u5b59\u654f\u60f3\u4e0d\u5230\u8fd9\u5e74\u8f7b\u4eba\uff0c\u7adf\u4e5f\u53d7\u8fc7\u8fd9\u4e48\u6df1\u7684 \u75db\u82e6\u3002\u5999\u7075\u9053\u4eba\u8d70\u524d\u4e00\u6b65\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u53ef\u5426\u5c31\u662f\u6b66\u6797\u4e2d\u4eba\u79f0\u201c\u94c1\u621f\u6e29\u4faf\u201d\u7684\u5415\u5927\u4fa0\uff1f\u300d \u3000\u3000\u4f0a\u98ce\u5fae\u5f31\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5c0f\u9119\u4ee5\u524d\u5c31\u662f\u5415\u5357\u4eba\uff0c\u4f46\u5415\u5357\u4eba\u73b0\u5728\u5df2\u7ecf\u6b7b\u53bb\uff0c\u9664\u975e\u2014\u2014\u9664\u975e\u4ed6\u80fd\u96ea\u6e05\u593a\u59bb\u4e4b \u803b\uff0c\u903c\u547d\u4e4b\u4ec7\uff01\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u5374\u6012\u9053\uff1a\u201c\u5929\u4e89\u6559\u53c8\u662f\u4f55\u7269\uff1f\u600e\u5730\u5982\u6b64\u6b3a\u4eba\uff1f\u201d\u5b59\u654f\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7a81\u7136\u9053\uff1a\u201c\u5929 \u4e89\u6559\uff0c\u5929\u6bd2\u6559\uff0c\u83ab\u975e\u8fd9\u4e24\u8005\u4e4b\u95f4\uff0c\u6709\u4ec0\u4e48\u5173\u8fde\u5417\uff1f\u201d\u5251\u5148\u751f\u59cb\u7ec8\u4fef\u9996\u6c89\u601d\uff0c\u6b64\u523b\u7a81\u7136\u7ad9\u4e86\u8d77 \u6765\uff0c\u5728\u4e39\u623f\u4e2d\u8e31\u4e86\u4e24\u8f6c\uff0c\u7709\u5934\u7adf\u5df2\u6df1\u76b1\uff0c\u50cf\u662f\u5728\u8003\u8651\u7740\u4ec0\u4e48\u91cd\u5927\u7684\u51b3\u5b9a\u4e00\u6837\u3002 \u3000\u3000\u6b64\u65f6\u82e5\u6709\u66f4\u9f13\uff0c\u8be5\u5df2\u8fc7\u4e86\u4e09\u66f4\u3002\u7a97\u5916\u7adf\u4e0b\u8d77\u96e8\u6765\uff0c\u50cf\u662f\u82cd\u5929\u5728\u542c\u4e86\u8fd9\u4e48\u591a\u60b2\u4f24\u7684\u4e8b\u540e\uff0c \u4e5f\u4e0d\u7981\u843d\u6cea\u3002 \u3000\u3000\u5999\u7075\u9053\u4eba\u79fb\u76ee\u7a97\u524d\uff0c\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u4eca\u591c\u4e0d\u77e5\u53c8\u6b7b\u53bb\u51e0\u4eba\uff1f\u201d \u3000\u3000\u5251\u5148\u751f\u7a81\u5730\u4e00\u8f6c\u8eab\uff0c\u8eab\u5f62\u79fb\u5230\u5e8a\u524d\u671b\u7740\u4f0a\u98ce\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u6b64\u523b\u6211\u613f\u4ee5\u5148\u5929\u4e4b\u6c14\uff0c\u52a9\u4f60\u6253\u901a\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\uff0c\u4f46\u662f\u6211\u5148\u5929\u4e4b\u6c14\uff0c\u6613\u53d1\u96be\u6536\uff0c\u4e00 \u4e2a\u4e0d\u597d\uff0c\u4f60\u4fbf\u6781\u4e3a\u53ef\u80fd\u88ab\u6211\u9707\u4f24\u5185\u8151\uff0c\u65e0\u6551\u800c\u6b7b\u3002\u5982\u679c\u4f60\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\u6253\u901a\uff0c\u4e0d\u4f46\u4f24\u52bf \u7acb\u6108\uff0c\u529f\u529b\u4e5f\u53ef\u589e\u8fdb\u51e0\u500d\uff0c\u590d\u4ec7\u4ea6\u53ef\u6709\u671b\u3002\u4f60\u662f\u5426\u6709\u4ee5\u81ea\u5df1\u7684\u6027\u547d\uff0c\u6765\u640f\u53d6\u8fd9\u4e9b\u7684\u52c7\u6c14\uff01\u201d \u3000\u3000\u4f0a\u98ce\u60e8\u7136\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u5df2\u662f\u6b7b\u53bb\u4e4b\u4eba\uff0c\u6027\u547d\u6839\u672c\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u4e0d\u8981\u8bf4\u8005\u524d\u8f88\u8fd9\u7b49\u6210\u529f\u5e0c\u671b\u6781\u5927\u4e4b\u4e8b\uff0c\u5c31 \u662f\u5927\u6d77\u5bfb\u9488\uff0c\u53ea\u8981\u590d\u4ec7\u6709\u671b\uff0c\u5c0f\u9119\u4e5f\u8981\u53bb\u4e00\u8bd5\u7684\u3002\u524d\u8f88\u4e0d\u5fc5\u518d\u95ee\uff0c\u53ea\u7ba1\u52a8\u624b\u5c31\u662f\u3002\u6b64\u4e3e\u82e5 \u6210\uff0c\u5c0f\u9119\u6765\u65e5\u809d\u8111\u6d82\u5730\uff0c\u5fc5\u62a5\u6df1\u601d\uff01\u82e5\u4e0d\u6210\uff0c\u5c0f\u9119\u4ea6\u662f\u5fc3\u5b89\u7406\u5f97\u5730\u6b7b\u53bb\uff0c\u51b3\u4e0d\u4f1a\u6709\u4efb\u4f55\u6028\u8a00 \u7684\u3002\u201d \u3000\u3000\u5251\u5148\u751f\u53f9\u9053\uff1a \u3000\u3000\u201c\u770b\u6765\u4e16\u4e0a\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u7684\u4eba\uff0c\u6bd5\u7adf\u8fd8\u6709\u4e0d\u5c11\uff01\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u8bdd\u9898\uff0c\u5411\u5999\u7075\u9053\uff1a \u3000\u3000\u201c\u85cf\u836f\u4e4b\u5904\uff0c\u5728\u65e0\u91cf\u5c71\u4e2d\uff0c\u6b64\u4eba\u5c31\u7b97\u201c\u7763\u201d\u201c\u4efb\u201d\u4e8c\u8109\u53ef\u901a\uff0c\u660e\u65e5\u4e0a\u8def\uff0c\u4f46\u4e5f\u51b3\u975e\u4e09\uff0c \u4e94\u65e5\u4e2d\uff0c\u53ef\u4ee5\u8d76\u5f97\u56de\u6765\u7684\u3002\u800c\u4e14\u5148\u7236\u85cf\u5b9d\u4e4b\u5904\uff0c\u8fd8\u6709\u4ec0\u4e48\u9669\u963b\uff0c\u6211\u4e5f\u4e0d\u77e5\u3002\u6b64\u4eba\u662f\u5426\u6709\u6b64\u6bc5 \u529b\uff0c\u8fbe\u6210\u5fc3\u613f\uff0c\u8fd8\u5728\u672a\u53ef\u77e5\u4e4b\u6570\u54e9\uff01\u201d \u3000\u3000\u4ed6\u6b64\u8a00\u4e00\u51fa\uff0c\u65e0\u5f02\u5df2\u8bf4\u660e\u613f\u4ee5\u85cf\u5b9d\u4e4b\u5904\uff0c\u544a\u8bc9\u4f0a\u98ce\u3002 \u3000\u3000\u5b59\u654f\u4e0d\u7981\u4ee3\u8fd9\u5e74\u8f7b\u4eba\u6b22\u559c\u3002\u4f0a\u98ce\u81ea\u5df1\uff0c\u66f4\u662f\u4e0d\u76f8\u4fe1\u79cd\u7edd\u4e16\u5947\u7f18\uff0c\u4f1a\u8fd9\u4e48\u8f7b\u6613\u5730\u843d\u5728\u81ea\u5df1 \u8eab\u4e0a\u3002\u4e24\u773c\u4e4b\u4e2d\uff0c\u6cea\u5149\u83b9\u7136\uff0c\u4f46\u5df2\u975e\u60b2\u75db\u4e4b\u6cea\u4e86\u3002\u5999\u7075\u9053\u4eba\u5374\u7a81\u5730\u671d\u5251\u5148\u751f\uff0c\u201c\u5657\u201d\u5730\uff0c\u8dea \u4e86\u4e0b\u53bb\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f84\u65e0\u80fd\uff0c\u4ee5\u81f3\u7ec8\u5357\u8499\u6b64\u60e8\u53d8\uff01\u5251\u5e08\u4f2f\u5982\u6b64\uff0c\u5c0f\u4f84\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\uff01\u81f3\u4e8e\u80fd\u5426\u6210\u529f\uff0c\u5374 \u662f\u5929\u547d\u3002\u5c0f\u4f84\u53ea\u6709\u2026\u2026\u201d \u3000\u3000\u4ed6\u54fd\u54bd\u7740\uff0c\u7adf\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u5374\u6c89\u541f\u7740\u9053\uff1a \u3000\u3000\u201c\u8fd9\u201c\u8680\u9aa8\u5723\u6c34\u201d\u4e4b\u6bd2\uff0c\u6211\u867d\u65e0\u6cd5\u53ef\u89e3\uff0c\u4f46\u81ea\u4fe1\u4ee5\u6211\u7684\u201c\u62a4\u5fc3\u795e\u65b9\u201d\uff0c\u591a\u4fdd\u4ed6\u4eec\u51e0\u5929\u6d3b \u547d\uff0c\u8fd8\u4e0d\u6210\u95ee\u9898\u3002\u53ea\u671b\u82cd\u5929\u6148\u60b2\uff0c\u4e00\u5207\u4e8b\u90fd\u80fd\u987a\u5229\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u7387\u6027\u800c\u884c\u7684\u5947\u4eba\uff0c\u6b64\u523b\u5c45\u7136\u4e5f\u4fe1\u8d77\u5929\u547d\u6765\u4e86\u3002 \u3000\u3000\u5251\u5148\u751f\u8eab\u5f62\u7a81\u5730\u4e00\u98d8\uff0c\u6beb\u672a\u4f5c\u52bf\uff0c\u5df2\u7aef\u5750\u5728\u4e91\u5e8a\u4e4b\u4e0a\uff0c\u9053\uff1a \u3000\u3000\u201c\u6b64\u523b\u6211\u5c31\u4e3a\u4ed6\u6253\u901a\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\u3002\u53ea\u662f\u6b64\u4e3e\u592a\u8fc7\u5371\u9669\uff0c\u4f60\u4eec\u6700\u597d\u51fa\u53bb\uff0c\u514d\u5f97\u6211\u5fc3 \u601d\u4e00\u5206\uff0c\u4fbf\u662f\u5de8\u7978\u3002\u201d \u3000\u3000\u5b59\u654f\u4e00\u8a00\u4e0d\u53d1\uff0c\u8d70\u8fc7\u53bb\u6a2a\u62b1\u8d77\u7231\u5973\u51cc\u7433\uff0c\u51cc\u7433\u7a81\u7136\u79c0\u76ee\u5fae\u5f20\uff0c\u7adf\u8f7b\u8f7b\u53eb\u4e86\u4e00\u58f0\u201c\u5988 \u5988\uff01\u201d\u539f\u6765\u5979\u5df2\u7ecf\u82cf\u9192\u8fc7\u6765\u4e86\u3002 \u3000\u3000\u5b59\u654f\u4e0d\u7981\u72c2\u559c\uff01 \u3000\u3000\u5999\u7075\u9053\u4eba\u6084\u6084\u4e00\u62db\u624b\uff0c\u5c06\u4ed6\u4eec\u5f15\u5230\u8fd9\u95f4\u4e39\u623f\u65c1\u8fb9\u7684\u4e00\u95f4\u6597\u5ba4\u4e2d\u53bb\u3002\u4e09\u5fc3\u795e\u541b\u63a9\u597d\u623f\u95e8\uff0c \u4e5f\u8ddf\u7740\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6597\u5ba4\u4e2d\u706f\u5149\u4eae\u8d77\uff0c\u51cc\u7433\u6a2a\u5367\u5728\u5c0f\u5e8a\u4e0a\uff0c\u5b59\u654f\u8f7b\u8f7b\u629a\u7740\u5979\u7684\u79c0\u53d1\uff0c\u5fc3\u4e2d\u5374\u4e0d\u514d\u6709\u4e9b\u7d27\u5f20\uff1a \u201c\u4e07\u4e00\u5251\u5148\u751f\u7684\u5148\u5929\u771f\u6c14\u7a0d\u4e00\u8fc7\u731b\uff0c\u90a3\u5415\u5357\u4eba\u2014\u2014\u201d\u5979\u95ed\u4e0a\u773c\u775b\uff0c\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\u3002 \u3000\u3000\u4f46\u5979\u4e5f\u77e5\u9053\uff0c\u8fd9\u79cd\u5947\u7f18\uff0c\u53ef\u8bf4\u5c11\u4e4b\u53c8\u5c11\u3002\u56e0\u4e3a\u6b66\u6797\u4e2d\u80fd\u7ec3\u6210\u5148\u5929\u4e4b\u6c14\u7684\u4eba\uff0c\u5df2\u662f\u7edd\u65e0\u4ec5 \u6709\uff1b\u80af\u8017\u53bb\u81ea\u8eab\u529f\u529b\uff0c\u4e3a\u4eba\u5bb6\u6253\u901a\u8fd9\u201c\u7763\u201d\u201c\u4efb\u201d\u4e8c\u8109\u7684\uff0c\u66f4\u662f\u8fde\u542c\u90fd\u6ca1\u6709\u542c\u8fc7\u4e86\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u9053\uff1a \u3000\u3000\u201c\u90a3\u59d3\u5415\u7684\u5c0f\u5b69\u5b50\uff0c\u5012\u771f\u7684\u798f\u7f18\u975e\u6d45\uff01\u8fde\u6211\u8001\u4eba\u5bb6\u7684\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\uff0c\u90fd\u662f\u4e94\u5341\u5c81\u4ee5 \u540e\u624d\u901a\u7684\u3002\u8fd9\u4e00\u4e0b\u4ed6\u5982\u4fa5\u5e78\u4e0d\u6b7b\uff0c\u6b66\u6797\u4e2d\u53c8\u591a\u4e86\u4e00\u4e2a\u597d\u624b\u4e86\u3002\u8fd9\u771f\u7684\u53ef\u8bf4\u662f\u56e0\u7978\u800c\u5f97\u798f \u4e86\uff01\u201d \u3000\u3000\u65f6\u5149\u6e10\u6e10\u8fc7\u53bb\uff0c\u4e0d\u4e45\u5929\u5df2\u4eae\u4e86\uff0c\u96e8\u58f0\u5df2\u4f4f\uff0c\u53ea\u6709\u6a90\u524d\u6ef4\u6c34\u4e4b\u58f0\u4ecd\u5728\u8f7b\u5fae\u5730\u54cd\u7740\u3002\u4f46\u7d27\u95ed \u7740\u7684\u4e39\u623f\u4e2d\uff0c\u4ecd\u6ca1\u6709\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u8fd9\u5176\u4e2d\u6700\u4e3a\u7126\u6025\u7684\u8be5\u7b97\u5999\u7075\u9053\u4eba\u4e86\uff0c\u56e0\u4e3a\u5415\u5357\u4eba\u4f0a\u98ce\u7684\u751f\u6b7b\uff0c\u4e5f\u5173\u7cfb\u7740\u7ec8\u5357\u95e8\u4e0b\u6570\u767e\u4e2a \u5f1f\u5b50\u7684\u6027\u547d\u3002 \u3000\u3000\u5b59\u654f\u548c\u4e09\u5fc3\u795e\u541b\u53c8\u4f55\u5c1d\u4e0d\u6697\u6697\u7740\u6025\u3002\u53ef\u662f\u53c8\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5929\u5149\u5df2\u5b8c\u5168\u4eae\u4e86\uff0c\u6597\u5ba4\u4e2d\u706f \u6cb9\u65e9\u67af\u3002\u5251\u5148\u751f\u548c\u4f0a\u98ce\uff0c\u4ecd\u662f\u6beb\u65e0\u52a8\u9759\u3002 \u3000\u3000\u84e6\u5730\uff0c\u623f\u95e8\u4e00\u63a8\uff0c\u5251\u5148\u751f\u9762\u5e26\u7b11\u5bb9\uff0c\u7f13\u7f13\u5730\u8d70\u4e86\u51fa\u6765\u2026\u2026\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u4e94\u7ae0\u3000\u98ce\u5c18\u4ec6\u4ec6>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u98ce\u5c18\u4ec6\u4ec6 \u3000\u3000\u4e0b\u7ec8\u5357\u5c71\uff0c\u81f3\u5348\u53e3\uff0c\u6e21\u5b50\u5348\u6cb3\uff0c\u81f3\u57ce\u56fa\uff0c\u8fc7\u6c49\u4e2d\uff0c\u7ecf\u5929\u9669\u4e4b\u5df4\u8c37\u5173\uff0c\u6cbf\u7c73\u4ed3\u9053\uff0c\u800c\u81f3\u5df4 \u4e2d\u5e9c\u3002\u4f0a\u98ce\u98ce\u5c18\u4ec6\u4ec6\uff0c\u663c\u591c\u5954\u9a70\uff0c\u5e0c\u671b\u65e9\u4e00\u5929\u80fd\u8d76\u5230\u65e0\u91cf\u5c71\u3002 \u3000\u3000\u4ed6\u5728\u4e00\u5929\u4e4b\u4e2d\uff0c\u8fde\u53d7\u5f53\u4ee3\u4e24\u5927\u9ad8\u624b\u7684\u8c03\u6cbb\uff0c\u5c24\u5176\u5251\u5148\u751f\u4ee5\u5148\u5929\u771f\u6c14\uff0c\u4e3a\u4ed6\u6253\u901a\u201c\u7763\u201d \u201c\u4efb\u201d\u4e24\u8109\uff0c\u8fd9\u4e9b\u6b66\u5b66\u7684\u7cbe\u7cb9\u4e4b\u5904\uff0c\u5c31\u6709\u90a3\u4e48\u795e\u5947\u7684\u529f\u7528\uff0c\u8eab\u53d7\u91cd\u4f24\u7684\u4f0a\u98ce\uff0c\u7b2c\u4e8c\u5929\u5c45\u7136\u5c31 \u80fd\u8d76\u8def\u4e86\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4ed6\u81ea\u5df1\u77e5\u9053\uff0c\u81ea\u5bb6\u7684\u529f\u529b\uff0c\u5728\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\uff0c\u4e00\u901a\u4e4b\u540e\uff0c\u4e0d\u77e5\u589e\u8fdb\u4e86\u82e5\u5e72\u3002 \u4ed6\u8fd9\u51e0\u5929\u663c\u591c\u517c\u7a0b\uff0c\u9664\u4e86\u767d\u5929\u96c7\u4e9b\u8f66\u9a6c\u4e4b\u5916\uff0c\u665a\u4e0a\u90fd\u662f\u4ee5\u8f7b\u529f\u8d76\u8def\uff0c\u4f46\u662f\u5374\u4e00\u4e1d\u4e5f\u4e0d\u89c9\u5f97 \u7d2f\u3002\u5c31\u62ff\u8fd9\u4ef6\u4e8b\u6765\u8bf4\uff0c\u529f\u529b\u4e4b\u5806\u8fdb\uff0c\u53ef\u77e5\u4e00\u6591\u3002 \u3000\u3000\u56db\u5ddd\u7701\u56db\u9762\u73af\u5c71\uff0c\u5230\u4e86\u5df4\u4e2d\u540e\uff0c\u5730\u52bf\u624d\u8f83\u5e73\u5766\u3002\u4f0a\u98ce\u60e6\u8bb0\u7740\u81ea\u5df1\u8eab\u4e0a\u6240\u62c5\u8d1f\u7684\u4efb\u52a1\uff0c\u5728 \u5df4\u4e2d\u53ea\u8349\u8349\u6253\u4e86\u4e2a\u5c16\uff0c\u4fbf\u96c7\u4e86\u8f86\u8f66\u5f80\u524d\u8d76\u8def\uff0c\u4ed6\u5374\u4f0f\u5728\u8f66\u53a2\u91cc\u6253\u76f9\uff0c\u517b\u7cbe\u795e\uff0c\u5230\u4e86\u665a\u4e0a\u597d\u518d \u8d76\u8def\u3002 \u3000\u3000\u6700\u5947\u5999\u7684\u662f\uff1a\u5f80\u5f80\u4e24\uff0c\u4e09\u5929\u4e2d\uff0c\u4ed6\u53ea\u8981\u7565\u4e3a\u9759\u5750\u8c03\u606f\uff0c\u771f\u6c14\u8fd0\u884c\u4e00\u4e0b\uff0c\u4fbf\u53c8\u7cbe\u795e\u7115\u53d1\u3002 \u4ed6\u77e5\u9053\u4e86\u81ea\u5df1\u5185\u529f\u7684\u8fdb\u5883\uff0c\u7b80\u76f4\u5feb\u5f97\u4e0d\u53ef\u601d\u8bae\uff01 \u3000\u3000\u8fd9\u4e48\u624d\u8fc7\u4e86\u56db\u5929\u591a\uff0c\u4ed6\u7adf\u80fd\u5947\u8ff9\u822c\u5730\u8d8a\u8fc7\u56db\u5ddd\uff0c\u6765\u5230\u5ddd\u6ec7\u4ea4\u754c\u65c1\u7684\u53d9\u5dde\u3002\u5230\u8fd9\u65f6\u5019\uff0c\u4ed6 \u624d\u89c9\u5f97\u81ea\u5df1\u771f\u7684\u8981\u4f11\u606f\u4e00\u4e0b\u4e86\u3002 \u3000\u3000\u4ed6\u4e3a\u4e86\u907f\u4eba\u8033\u76ee\uff0c\u7a7f\u7684\u662f\u6700\u4e0d\u5f15\u4eba\u6ce8\u76ee\u7684\u670d\u88c5\u3002\u56e0\u4e3a\u662f\u51ac\u5929\uff0c\u4ed6\u53ef\u4ee5\u5c06\u6be1\u5e3d\u5e26\u5f97\u5f88\u4f4e\uff0c \u751a\u81f3\u5634\u4e0a\u90fd\u7559\u4e86\u4e9b\u80e1\u987b\u3002 \u3000\u3000\u5230\u4e86\u53d9\u5dde\uff0c\u4ed6\u6295\u5728\u57ce\u5916\u7684\u4e00\u5bb6\u5c0f\u5e97\u91cc\uff0c\u81ea\u7136\u4e5f\u662f\u907f\u5f00\u5929\u4e89\u6559\u7684\u773c\u7ebf\u3002\u522b\u7684\u8fd8\u597d\uff0c\u65f6\u95f4\u5374 \u662f\u4e00\u523b\u4e5f\u803d\u8bef\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u77e5\u4e00\u5165\u5e97\u95e8\uff0c\u4ed6\u5c31\u53d1\u89c9\u4e8b\u60c5\u6709\u5f02\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u6697\u53eb\u8d77\u82e6\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u5e97\u6808\u867d\u5728\u57ce\u5916\uff0c\u89c4\u6a21\u5374\u4e0d\u5c0f\uff0c\u4e00\u8fdb\u5e97\u95e8\u662f\u4e00\u9762\u67dc\u53f0\uff0c\u67dc\u53f0\u524d\u9762\uff0c\u5374\u6563\u653e\u7740\u5341\u4f59 \u5f20\u6905\u5b50\uff0c\u60f3\u662f\u501f\u4eba\u6b47\u811a\u7528\u7684\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u4e9b\u6905\u5b50\u4e0a\uff0c\u5374\u90fd\u5750\u6ee1\u4e86\u9ed1\u8863\u52b2\u88c5\u7684\u5927\u6c49\uff0c\u4e00\u4e2a\u4e2a\u76f4\u773c\u77aa\u76ee\u3002\u4f0a\u98ce\u6697\u53eb\u201c\u4e0d\u5999\u201d\uff01 \u4ed6\u6697\u5fd6\uff01\u201c\u8fd9\u4e9b\u4eba\u770b\u6765\uff0c\u90fd\u662f\u5929\u4e89\u6559\u4e0b\u3002\u201d\u4e0d\u7981\u6697\u602a\u81ea\u5df1\uff0c\u600e\u5730\u9009\u6765\u9009\u53bb\uff0c\u5374\u9009\u4e2d\u8fd9\u4e2a\u5730 \u65b9\uff1f \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u52bf\u5fc5\u4e0d\u80fd\u9000\u51fa\uff0c\u53ea\u5f97\u786c\u7740\u5934\u76ae\u8d70\u4e0a\u53bb\uff0c\u5e0c\u671b\u8fd9\u5e97\u91cc\u6ca1\u6709\u8ba4\u5f97\u81ea\u5df1\u672c\u6765\u9762\u76ee\u7684 \u4eba\uff0c\u66f4\u5e0c\u671b\u5e97\u5c0f\u4e8c\u8bf4\u6ca1\u6709\u623f\u95f4\u4e86\u3002 \u3000\u3000\u4f46\u662f\u5e97\u5c0f\u4e8c\u5374\u9053\uff1a\u201c\u4f60\u8001\u8fd0\u6c14\u597d\uff0c\u53ea\u5269\u4e0b\u51e0\u95f4\u623f\u4e86\u3002\u201d\u5e26\u7740\u4ed6\u8d70\u5230\u897f\u9762\u8de8\u9662\u7684\u4e00\u95f4\u623f \u5b50\uff0c\u91cc\u9762\u5012\u7684\u786e\u662f\u6bd4\u57ce\u91cc\u5ba2\u6808\u5bbd\u655e\uff0c\u5e7d\u9759\u5f97\u591a\u3002\u8fd9\u4e5f\u662f\u8bb8\u591a\u4eba\u5b81\u613f\u5728\u57ce\u5916\u6295\u5bbf\u7684\u539f\u56e0\u3002\u5e97\u5c0f \u4e8c\u8d70\u8fdb\u53bb\u6536\u62fe\uff0c\u4ed6\u7ad9\u5728\u9662\u5b50\u91cc\uff0c\u76d8\u7b97\u7740\u8def\u9014\u3002\u7a81\u7136\u80cc\u540e\u6709\u811a\u6b65\u58f0\uff0c\u4ed6\u4e5f\u6ca1\u6709\u56de\u5934\u53bb\u671b\uff0c\u90a3\u77e5 \u80a9\u4e0a\u5374\u88ab\u4eba\u91cd\u91cd\u62cd\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u4ed6\u4e00\u60ca\uff0c\u56de\u987e\u5374\u89c1\u4e00\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u7ad9\u5728\u4ed6\u80cc\u540e\uff0c\u7c97\u58f0\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff1f\u4f60\u662f\u90a3\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u66f4\u60ca\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u91cc\u771f\u6709\u4eba\u8ba4\u5f97\u6211\uff1f\u4e0d\u7136\uff0c\u600e\u5730\u8fd9\u5929\u4e89\u6559\u5f92\u4f1a\u8dd1\u6765\u95ee\u6211\uff1f\u201d \u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4ece\u5317\u8fb9\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4ece\u5934\u5230\u811a\u6253\u91cf\u7740\u4ed6\uff0c\u4f3c\u4e4e\u5728\u5fae\u5fae\u70b9\u5934\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u5fae\u60ca\uff0c\u4ed6\u5012\u4e0d\u662f\u6015\u8fd9\u4e2a\u7c97\u6c49\uff0c\u800c\u662f\u6015\u751f\u51fa\u4e89\u7aef\uff0c\u8bef\u4e86\u884c\u7a0b\u3002 \u3000\u3000\u90a3\u77e5\u90a3\u9ed1\u8863\u6c49\u5b50\u5374\u7b11\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff0c\u4f60\u8d70\u8fd0\u5566\uff01\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u6014\u3002\u4ed6\u53c8\u9053\uff1a \u3000\u3000\u201c\u4ece\u4eca\u5929\u8d77\uff0c\u4f60\u5c31\u662f\u6211\u5144\u5f1f\uff0c\u5927\u5757\u5403\u8089\uff0c\u5927\u7897\u559d\u9152\u3002\u6211\u770b\u4f60\u4e70\u5356\u4e5f\u4e0d\u89c1\u5f97\u5f97\u610f\uff0c\u8e09\u7740\u6211 \u4eec\u5f1f\u5144\u5728\u4e00\u8d77\uff0c\u4fdd\u7ba1\u6709\u4f60\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u8fd9\u9ed1\u8863\u6c49\u5b50\u6ca1\u5934\u6ca1\u8111\u8bf4\u51fa\u4e00\u756a\u8bdd\uff0c\u5012\u4e70\u7684\u5c06\u4f0a\u98ce\u6014\u4f4f\u4e86\u3002\u773c\u73e0\u4e00\u8f6c\uff0c\u6b63\u60f3\u7b54\u8bdd\uff0c\u8fd9\u9ed1\u8863 \u6c49\u5b50\u6ca1\u5934\u6ca1\u8111\u8bf4\u51fa\u4e00\u756a\u8bdd\uff0c\u5012\u771f\u7684\u5c06\u4f0a\u98ce\u6014\u4f4f\u4e86\u3002\u773c\u73e0\u4e00\u8f6c\uff0c\u6b63\u60f3\u7b54\u8bdd\uff0c\u90a3\u6c49\u5b50\u5374\u5df2\u4e0d\u8010\u70e6 \u7684\u50ac\u4fc3\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u54e5\u7684\u76db\u60c5\uff0c\u5c0f\u5f1f\u5fc3\u9886\u4e86\uff0c\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u90a3\u9ed1\u8863\u58ee\u6c49\u5df2\u6012\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b50\u4e0d\u8981\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u8001\u5b50\u770b\u4e0a\u4e86\u4f60\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u8001\u5b50\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u4e00\u4e2a\u8001\u5b50\uff0c\u4f0a\u98ce\u4e0d\u77e5\u9053\u8fd9\u662f\u8700\u4eba\u7684\u53e3\u8bed\uff0c\u6db5\u517b\u518d\u597d\uff0c\u4e5f\u4e0d\u7981\u5927\u6012\u8d77\u6765\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4f4f\u58c1\uff01\u8198\u7ed9\u6211\u6eda\u5f00\uff01\u201d \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u8fd8\u771f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u559d\u51fa\u6765\uff0c\u4ed6\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u968f\u5373\u5927\u6012\uff0c\u5de6\u624b\u4e00\u9886\u4f0a\u98ce\u7684\u773c\u795e\uff0c \u53f3\u62f3\u515c\u5e95\u800c\u51fa\uff0c\u4e00\u62f3\u201c\u51b2\u5929\u70ae\u201d\uff0c\u6253\u5411\u4f0a\u98ce\u7684\u4e0b\u989a\u3002 \u3000\u3000\u4f0a\u98ce\u662f\u4f55\u7b49\u6b66\u529f\uff0c\u600e\u4f1a\u88ab\u8fd9\u79cd\u5e84\u7a3c\u628a\u5f0f\u6253\u4e2d\uff0c\u4f46\u4ed6\u8111\u4e2d\u5ff5\u5934\u6781\u5feb\u5730\u4e00\u95ea\uff0c\u7adf\u672a\u51fa\u624b\uff0c\u4f38 \u7740\u5934\u8ba9\u90a3\u5927\u6c49\u6253\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u90a3\u5927\u6c49\u53c8\u4e00\u6014\uff0c\u5ffd\u7136\u6367\u7740\u624b\u8d70\u4e86\uff0c\u5927\u7ea6\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u78b0\u7740\u4e86\u9ad8\u624b\u3002. \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u5fc3\u4e2d\u70ed\u8840\u500f\u7136\u800c\u6d8c\u3002\u8fd9\u79cd\u5929\u6027\u7684\u4eba\uff0c\u662f\u4e0d\u4f1a\u6c38\u8fdc\u7518\u4e8e\u5bc2\u5bde\u7684\uff0c\u5c24\u5176\u662f \u4ed6\u81ea\u77e5\u529f\u529b\u5df2\u731b\u8fdb\uff0c\u4f46\u5374\u672a\u80fd\u4e00\u8bd5\u7684\u65f6\u5019\u3002\u4ed6\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u5c31\u7b97\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u6211\u529e\u5b8c\u4e4b\u540e\u4e00 \u8d70\uff0c\u5c31\u51ed\u6211\u7684\u811a\u7a0b\uff0c\u4ed6\u4eec\u8fd8\u4f1a\u8d76\u5f97\u4e0a\u6211\uff01\u201d \u3000\u3000\u4ed6\u8d70\u5230\u4e1a\u5df2\u6536\u62fe\u597d\u7684\u623f\u95f4\u91cc\u3002\u5e97\u5c0f\u4e8c\u966a\u7740\u7b11\u8fc7\u6765\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u8001\u771f\u662f\u5927\u4eba\u5927\u91cf\uff0c\u4e0d\u8ddf\u90a3\u822c\u4eba\u4e00\u6837\u89c1\u8bc6\uff0c\u8fd9\u624d\u53eb\u4e0d\u5403\u773c\u524d\u4e8f\u7684\u5927\u4e08\u592b\uff01\u4f60\u8001\u770b\uff1a\u8fde \u97e9\u4fe1\u4ee5\u524d\u90fd\u4ece\u4eba\u5bb6\u7684\u88e4\u88c6\u4e0b\uff0c\u94bb\u8fc7\u53bb\u8fc7\u54e9\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u6325\u624b\u53eb\u4ed6\u8d70\u4e86\u3002\u5173\u597d\u95e8\uff0c\u7565\u4e3a\u7ed3\u675f\u4e00\u4e0b\u3002\u4ed6\u60f3\u5728\u8fd9\u5ddd\u6ec7\u8fb9\u5883\u7684\u5c0f\u5e97\u91cc\uff0c \u715e\u4e00\u715e\u5929\u4e89\u6559\u65e5\u6e10\u56a3\u5f20\u7684\u51f6\u5a01\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u679c\u7136\u53c8\u6709\u4eba\u53e9\u95e8\u3002\u4f0a\u98ce\u51b7\u7b11\u5fd6\u9053\uff1a\u201c\u90a3\u8bdd\u513f\u679c\u7136\u6765\u4e86\u3002\u201d\u500f\u7136\u62c9\u5f00\u623f\u95e8\uff0c\u773c \u524d\u4e00\u4eae\uff0c\u95e8\u5916\u7adf\u7ad9\u7740\u4e2a\u7edd\u7f8e\u7684\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7a7f\u7740\u7fe0\u7eff\u957f\u886b\uff0c\u5fae\u5fae\u9732\u51fa\u6563\u82b1\u88e4\u811a\uff0c\u4e0a\u9762\u5bab\u9b13\u9ad8\u633d\uff0c\u6709\u51e0\u4e1d\u4e71\u53d1\uff0c\u62ab\u5728\u8033\u7554\u4e00\u53cc \u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u671b\u4e86\u4f0a\u98ce\u4e00\u773c\u4e4b\u540e\uff0c\u76ee\u5149\u4e2d\u539f\u6765\u542b\u7740\u7684\u6012\u706b\uff0c\u53d8\u6210\u4e86\u53e6\u5916\u4e00\u79cd\u4f3c\u7b11\u975e\u7b11\u7684 \u795e\u8272\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u98ce\u81f4\u5374\u6210\u719f\u5f97\u5f88\u3002\u773c\u4e2d\u7684\u7b11\u610f\uff0c\u4f7f\u4eba\u89c1\u4e86\uff0c\u4e0d\u514d\u60f3\u5165\u975e\u975e\u3002\u5634\u89d2\u6302 \u7740\u4e03\u5206\u98ce\u60c5\uff0c\u6a31\u53e3\u5fae\u5f20\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u542c\u6211\u4eec\u90a3\u51e0\u4e2a\u4e0d\u6210\u6750\u7684\u5974\u624d\u8bf4\uff0c\u6709\u4e2a\u9ad8\u4eba\uff0c\u7528\u5185\u52b2\u9707\u4e86\u4ed6\u7684\u624b\u3002\u6211\u5c31\u8bf4\uff01\u8fd9\u5c0f\u5e97\u91cc \u600e\u4e48\u6765\u4e86\u4e2a\u9ad8\u4eba\u5440\uff01\u8d76\u7d27\u8d70\u8fc7\u6765\u770b\u770b\u3002\u90a3\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5979\u4ee5\u4e00\u58f0\u8361\u4eba\u5fc3\u9b44\u7684\u7b11\uff0c\u7ed3\u675f\u4e86\u5979\u5c1a\u672a\u8bf4\u5b8c\u7684\u8bdd\uff0c\u4e00\u53e3\u6e05\u8106\u7684\u4eac\u7247\u5b50\uff0c\u4f7f\u5979\u8f7b\u5feb\u7684\u8bed \u8c03\uff0c\u66f4\u4e3a\u52a8\u542c\u3002 \u3000\u3000\u4f0a\u98ce\u5947\u602a\uff01\u201c\u8fd9\u5c11\u5973\u662f\u8c01\uff0c\u96be\u9053\u4e5f\u662f\u5929\u4e89\u6559\u4e0b\u7684\u9ad8\u624b\u5417\uff1f\u201d\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u672c\u6765\u4ed6\u7559\u5728\u53e3 \u8fb9\u7684\u4f24\u4eba\u4e4b\u8bed\uff0c\u6b64\u523b\u5374\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u90a3\u7fe0\u88c5\u5c11\u5973\u5374\u53c8\u5a07\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u8bf4\u60a8\u54ea\uff01\u9ad8\u59d3\u5927\u540d\u5440\uff1f\u5c31\u51ed\u60a8\u90a3\u4e48\u4fca\u7684\u5185\u529f\uff0c\u4e00\u5b9a\u662f\u6b66\u6797\u4e2d\u6210\u540d\u9732\u9762\u7684\u5927\u82f1\u96c4\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u7adf\u4e0d\u7b49\u4f0a\u98ce\u62db\u547c\uff0c\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u6781\u4e3a\u4e0d\u60a6\u5730\u4e00\u76b1\u7709\u3002\u6697\u5fd6\uff1a\u201c\u8fd9\u5c11\u5973\u597d\u751f\u8f7b\u4f7b\uff01\u4f46\u4eba\u5bb6\u8bdd\u8bf4\u5f97\u90a3\u4e48\u5ba2\u6c14\uff0c\u81ea\u5df1\u5728\u6ca1 \u6709\u6478\u6e05\u4eba\u5bb6\u6765\u5386\u4e4b\u524d\uff0c\u4e5f\u4e0d\u4fbf\u4f5c\u4f55\u8868\u793a\u3002\u4f46\u5979\u7684\u8bdd\uff0c\u5374\u53c8\u5982\u6b64\u96be\u4ee5\u7b54\u8986\u3002\u201d \u3000\u3000\u4ed6\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u53ea\u7565\u901a\u4e24\u624b\u7c97\u628a\u5f0f\uff0c\u90a3\u91cc\u662f\u4ec0\u4e48\u9ad8\u4eba\uff0c\u66f4\u8c08\u4e0d\u4e0a\u6210\u540d\u9732\u8138\u4e86\u3002\u65b9\u624d\u4e00\u65f6\u5931\u624b\uff0c\u4f24 \u4e86\u8d35\u2014\u2014\u8d35\u7ba1\u5bb6\uff0c\u8fd8\u671b\u59d1\u5a18\u6055\u7f6a\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u7684\u76ee\u5149\uff0c\u5728\u4f0a\u98ce\u8138\u4e0a\u4e0d\u505c\u6253\u8f6c\uff0c\u7b11\u5bb9\u5982\u767e\u5408\u6012\u653e\uff0c\u5a07\u58f0\u9053\uff1a. \u3000\u3000\u201c\u60a8\u4e0d\u80af\u8bf4\uff0c\u6211\u4e5f\u6ca1\u529e\u6cd5\u3002\u90a3\u8822\u624d\u53d7\u4e86\u4f24\uff0c\u662f\u4ed6\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c\u81ea\u5df1\u6d3b\u8be5\u5012\u9709\uff01\u4e0d\u8fc7\u201d \u3000\u3000\u5979\u8f7b\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u60a8\u80af\u4e0d\u80af\u548c\u6211\u505a\u4e2a\u670b\u53cb\u54e9\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u53c8\u5fae\u4e00\u76b1\u7709\uff0c\u4ed6\u66f4\u53d1\u89c9\u4e86\u8fd9\u7fe0\u88c5\u5c11\u5973\u7684\u8f7b\u4f7b\u3002\u4f46\u4ed6\u6614\u5e74\u884c\u8d70\u6c5f\u6e56\u65f6\uff0c\u8fd9\u79cd\u4e8b\u4e5f\u66fe\u9047 \u5230\u8fc7\uff0c\u662f\u4ee5\u4e5f\u5e76\u4e0d\u89c9\u5f97\u5403\u60ca\u3002 \u3000\u3000\u5730\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u627f\u59d1\u5a18\u62ac\u7231\uff0c\u5c0f\u9119\u5b9e\u611f\u6709\u5e78\u3002\u4f46\u5c0f\u9119\u6b64\u523b\u5c1a\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u7a0d\u606f\u7247\u523b\u4fbf\u5f97\u79bb\u53bb\uff0c\u65e5\u540e\u5982 \u6709\u673a\u7f18\uff0c\u518d\u2026\u2026\u201d \u3000\u3000\u90a3\u7fe0\u670d\u5c11\u5973\u660e\u7738\u4e00\u8f6c\uff0c\u53c8\u751c\u751c\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u622a\u4f4f\u4ed6\u7684\u8bdd\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f60\u662f\u4e0d\u662f\u80af\u4ea4\u6211\u8fd9\u4e2a\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u8bed\u58f0\u4e4b\u5a07\u8106\u6e05\u5ae9\uff0c\u66f4\u5b9b\u5982\u51fa\u8c37\u4e4b\u83ba\uff0c\u4f7f\u4eba\u6709\u4e00\u79cd\u4e0d\u5fcd\u62d2\u7edd\u5979\u4efb\u4f55\u8981\u6c42\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u5728\u6c89\u541f\u4e86\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u7b54\u8986\uff01 \u3000\u3000\u4f46\u4ed6\u5374\u5e76\u975e\u88ab\u8fd9\u5c11\u5973\u6240\u60d1\uff0c\u53ea\u662f\u4e0d\u5fcd\u7ed9\u5c11\u5973\u8fc7\u4e8e\u96be\u582a\uff1b\u56e0\u4e3a\u65e0\u8bba\u5982\u4f55\uff0c\u4eba\u5bb6\u603b\u662f\u5bf9\u4ed6\u4e00 \u756a\u597d\u610f\uff0c\u4eba\u4eec\u5e38\u5e38\u65e0\u6cd5\u62d2\u7edd\u4eba\u5bb6\u7684\u597d\u610f\uff0c\u81f3\u4e8e\u8fd9\u79cd\u597d\u610f\u6b63\u6216\u4e0d\u6b63\uff0c\u90a3\u5374\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002 \u4f55\u51b5\u8fd9\u5c11\u5973\u660e\u7738\u5584\u7750\uff0c\u867d\u7136\u663e\u5f97\u8f7b\u4f7b\u4e9b\uff0c\u5374\u7edd\u975e\u6deb\u8361\u4e4b\u6001\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4fcf\u751f\u751f\u7acb\u5728\u4ed6\u9762\u524d\uff0c\u7a81\u7136\u67f3\u8170\u4e00\u8f6c\uff0c\u5411\u5916\u8d70\u53bb\uff0c\u4e00\u8fb9\u5a07\u7b11\u9053\uff1a \u3000\u3000\u201c\u60a8\u65e2\u7136\u6709\u6025\u4e8b\uff0c\u6211\u53ef\u4e5f\u4e0d\u80fd\u591a\u6253\u6270\u60a8\uff0c\u53ef\u662f\u4e0b\u6b21\u89c1\u9762\u7684\u65f6\u5019\uff0c\u60a8\u53ef\u4e0d\u80fd\u518d\u4e0d\u7406\u6211 \u4e86\uff01\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u9001\u5979\u7684\u5029\u5f71\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u90a3\u65f6\u5979\u5374\u53c8\u7a81\u5730\u56de\u8f6c\u8eab\u6765\uff0c\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u7269\uff0c\u653e\u5230\u684c \u4e0a\uff0c\u53c8\u5a07\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u8fd9\u2014\u2014\u8fd9\u662f\u6211\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u67f3\u8170\u5fae\u6298\uff0c\u8f7b\u98ce\u4f3c\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u6014\u4e86\u534a\u664c\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u5230\u5979\u7adf\u5728\u684c\u4e0a\u7559\u4e0b\u4e00\u5f20\u7c89\u7ea2\u8272\u7684\u5c0f\u7eb8\u7247\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u62ff\u8d77\u4e00 \u770b\uff0c\u5374\u89c1\u4e0a\u9762\u5199\u7740\uff1a \u3000\u3000\u201c\u5929\u5a9a\u6559\u4e0b\uff0c\u7a1a\u51e4\u9ea6\u6167\u3002\u201d \u3000\u3000\u201c\u5929\u5a9a\u6559\u201d\u4e09\u5b57\u4e00\u4eba\u76ee\uff0c\u4f0a\u98ce\u5fc3\u5934\u4e00\u51db\uff01\u4f46\u90a3\u5c0f\u7eb8\u7247\u4e0a\u6240\u6563\u53d1\u51fa\u7684\u8f7b\u6de1\u9999\u6c14\uff0c\u5374\u4f7f\u4ed6\u795e \u601d\u4e00\u9635\u660f\u6175\u3002\u7b49\u4ed6\u53d1\u89c9\u4e4b\u65f6\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\uff01 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u8f6f\u8f6f\u5730\u5012\u5230\u5730\u4e0a\u2026\u2026 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u516d\u7ae0\u3000\u5929\u5a9a\u4e4b\u6559>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u5929\u5a9a\u4e4b\u6559 \u3000\u3000\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u56db\u80a2\u767e\u9ab8\uff0c\u4ecd\u7136\u6ca1\u6709\u4e1d\u6beb\u529b\u6c14\uff0c\u90a3\u867d\u7136\u8fd1\u4f3c\u88ab\u4eba\u70b9\u4e2d\u7a74\u9053\uff0c\u5374\u53c8\u548c\u88ab\u4eba \u70b9\u4e2d\u7a74\u9053\u7684\u6ecb\u5473\uff0c\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4ed6\u8111\u6d77\u4e2d\u4e5f\u4ecd\u7136\u6709\u4e9b\u660f\u6655\u4e4b\u610f\uff0c\u4ed6\u4e0d\u7981\u5927\u9a87\uff1a\u201c\u662f\u4ec0\u4e48\u8ff7\u836f.\u6709\u7740\u8fd9\u7b49\u6548\u529b.\u201d\u987b \u77e5\u4ed6\u81ea\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\u4e00\u901a\u4e4b\u540e\uff0c\u529f\u529b\u6bd4\u8d77\u4ee5\u524d\uff0c\u4f55\u6b62\u589e\u8fdb\u5341\u500d\uff0c\u5c31\u7b97\u4ee5\u524d\uff0c\u666e\u901a\u7684\u8ff7\u836f \u4e5f\u4e07\u4e07\u8ff7\u4e0d\u5012\u4ed6\u3002\u6700\u602a\u7684\u662f\uff0c\u90a3\u5c0f\u7eb8\u7247\u770b\u6765\uff0c\u4e1d\u6beb\u6ca1\u6709\u4e00\u4e9b\u5f02\u72b6\uff0c\u8c01\u53c8\u60f3\u5f97\u5230\u90a3\u5176\u4e2d\u7adf\u9644\u6709 \u5982\u6b64\u5389\u5bb3\u4e4b\u8ff7\u836f\uff01 \u3000\u3000\u4ed6\u7741\u773c\u6253\u91cf\u56db\u5468\uff0c\u4eba\u76ee\u4ff1\u90fd\u662f\u7c89\u7ea2\u8272\u3002\u623f\u95f4\u867d\u6b8a\u4e0d\u5927\uff0c\u4f46\u662f\u5374\u88c5\u6f62\u5f97\u7eee\u4e3d\u5802\u7687\u5df2\u6781\uff0c\u7adf \u50cf\u662f\u4ec0\u4e48\u5bcc\u5bb6\u5343\u91d1\u7684\u95fa\u623f\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7acb\u523b\u660e\u767d\u4e86\u8fd9\u662f\u751a\u4e48\u56de\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u538c\u6076\u5730\u4e00\u553e\u3002\u7acb\u523b\u8bd5\u7740\u4ee5\u5185\u529f\u903c\u51fa\u4f53\u4e2d\u5c1a\u6b8b \u5b58\u7684\u8ff7\u836f\uff0c\u90a3\u77e5\u773c\u524d\u7a81\u7136\u4e00\u6697\u2014\u2014 \u3000\u3000\u7b49\u5230\u5149\u7ebf\u91cd\u660e\u4e4b\u65f6\uff0c\u4ed6\u7acb\u523b\u53c8\u53d1\u89c9\u4e00\u5e55\u5947\u5883\uff0c\u623f\u4e2d\u7adf\u591a\u4e86\u56db\u4e2a\u8eab\u62ab\u8f7b\u7eb1\u7684\u5c11\u5973\uff0c\u800c\u90a3\u7a1a \u51e4\u9ea6\u6167\uff0c\u8d6b\u7136\u4ea6\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u8f7b\u7eb1\u5c11\u5973\uff0c\u59ff\u5bb9\u4ff1\u90fd\u7edd\u7f8e\uff0c\u4f53\u6001\u4e4b\u4e2d\uff0c\u9690\u542b\u7740\u4e00\u79cd\u6d88\u9b42\u8680\u9aa8\u4e4b\u610f\uff0c\u5a40\u5a1c\u5730\u8d70\u5230\u4f0a \u98ce\u7684\u5e8a\u524d\uff0c\u7adf\u90fd\u5750\u5230\u4ed6\u7684\u5e8a\u4fa7\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u771f\u6c14\u65b9\u51dd\uff0c\u90a3\u77e5\u8fd9\u56db\u4e2a\u5c11\u5973\u660e\u7738\u5e26\u5a9a\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u516b\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u7adf\u90fd\u642d\u5230\u4f0a \u98ce\u8eab\u4e0a\uff0c\u7389\u6307\u8f7b\u52a8\u3002\u4f0a\u98ce\u5fc3\u4e2d\uff0c\u7adf\u731b\u5730\u4e00\u8361\uff0c\u4ed6\u4e0d\u7981\u5927\u9a87\uff01 \u3000\u3000\u4f46\u6b64\u523b\u4ed6\u56db\u80a2\u8f6f\u5f97\u4e00\u4e1d\u529b\u6c14\u4e5f\u6ca1\u6709\uff0c\u4e5f\u65e0\u6cd5\u53cd\u6297\u3002\u90a3\u56db\u4e2a\u5c11\u5973\u7b11\u58f0\u6108\u5a9a\uff0c\u7389\u6307\u8fde\u629a\uff0c\u4f0a \u98ce\u5fc3\u4e2d\uff0c\u7adf\u6e10\u6e10\u50cf\u662f\u6709\u4e9b\u628a\u6301\u4e0d\u4f4f\u7684\u6837\u5b50\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u529f\u529b\u4e4b\u6df1\uff0c\u8fe5\u5f02\u5e38\u4eba\uff0c\u7406\u667a\u5c1a\u672a\u5b8c\u5168\u6d88\u5931\uff0c\u5fc3\u5ff5\u7a81\u5730\u4e00\u52a8\uff0c\u4ed6\u5f3a\u81ea\u6536\u6444\u795e\u8272\uff0c\u5c06 \u65b9\u624d\u51dd\u96c6\u7684\u4e00\u4e1d\u771f\u6c14\uff0c\u5b8c\u5168\u903c\u5230\u8138\u4e0a\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5c11\u5973\u773c\u4e2d\uff0c\u53ea\u89c9\u4ed6\u9762\u5e9e\u706b\u8d64\uff0c\u4fca\u76ee\u8ff7\u7cca\uff0c\u5982\u9189\u5982\u75f4\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\uff0c\u8eab\u6750\u5fae\u77ee\uff0c\u4f53\u6001\u8f83\u4e30\uff0c\u7709\u76ee\u4e4b\u95f4\uff0c\u8361\u610f\u7279\u522b\u6d53\u539a\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u884c\u4e86\uff01\u201d\u5979\u5411\u7a1a\u51e4\u9ea6\u6167\u548c\u53e6\u4e00\u4e2a\u5c11\u5973\u9053\uff1a \u3000\u3000\u201c\u4e09\u59b9\uff01\u56db\u59b9\uff01\u4f60\u4eec\u53bb\u62db\u547c\u6559\u4e3b\u6765\u5427\uff01\u8fd9\u5c0f\u5b50\u4e5f\u4e0d\u89c1\u5f97\u6d4e\u4e8b\uff0c\u8fd8\u5bb3\u5f97\u6211\u4eec\u56db\u4e2a\uff0c\u4eb2\u81ea\u51fa \u9a6c\u3002\u201d \u3000\u3000\u7a1a\u51e4\u9ea6\u6167\u671b\u4e86\u4f0a\u98ce\u4e00\u773c\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u4ed6\u5c06\u5e72\u4e03\u53cc\u8155\u9707\u4f24\u7684\u624b\u6cd5\uff0c\u786e\u5b9e\u9ad8\u660e\u5f97\u5f88\uff01\u6211\u4ee5\u4e3a\u4ed6\u4e00\u5b9a\u86ee\u6709\u529f\u592b\u54e9\uff01\u90a3\u77e5\u9053\u2014\u2014\u201d \u5979\u4fcf\u54fc\u4e86\u4e00\u58f0\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u4e2d\u7528\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u62c9\u4e86\u90a3\u8eab\u6750\u6700\u9ad8\uff0c\u80a4\u8272\u6d01\u767d\u5982\u7389\u7684\u5c11\u5973\uff0c\u6084\u7136\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\uff0c\u6839\u5feb\u5730\u95ea\u8fc7\u51e0\u4e2a\u5ff5\u5934\uff0c\u4ed6\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5929\u5a9a\u6559\u770b\u6765\u679c\u7136\u6709\u4e9b\u95e8\u9053\uff0c\u6211\u82e5\u4e0d \u5f3a\u81ea\u628a\u6301\uff0c\u4eca\u65e5\u6050\u96be\u514d\u906d\u6b64\u52ab\u96be\uff01\u201d\u4e00\u9762\u95ed\u4e0a\u773c\u775b\uff0c\u5374\u5728\u6697\u4e2d\u8c03\u606f\u7740\u3002 \u3000\u3000\u53e6\u5916\u8fd8\u7559\u5728\u5ba4\u4e2d\u7684\u4e24\u4e2a\u5c11\u5973\uff0c\u5374\u4f3c\u6781\u4e3a\u6deb\u8361\uff0c\u8a00\u8bed\u624b\u811a\u4e4b\u95f4\uff0c\u8202\u610f\u76ce\u7136\u3002 \u3000\u3000\u4f46\u4f0a\u98ce\u4e00\u7ecf\u8c03\u606f\uff0c\u5fc3\u5883\u7acb\u5373\u7a7a\u7075\uff0c\u4e09\u82b1\u805a\u9876\uff0c\u4e94\u6c14\u671d\u5143\uff0c\u4ed6\u820c\u5c16\u5fae\u62b5\u4e0a\u989a\uff0c\u5916\u8868\u867d\u4f3c\u75f4 \u9189\uff0c\u4f46\u5176\u5b9e\u5374\u4e0d\u7136\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ba4\u5916\u7b11\u8bed\u4e4b\u58f0\u4f20\u6765\uff0c\u542c\u5f97\u7a1a\u51e4\u9ea6\u6167\u8f7b\u8106\u7684\u53e3\u97f3\u9053\uff1a \u3000\u3000\u201c\u6559\u4e3b\u6765\u4e86\uff01\u201d \u3000\u3000\u4f0a\u98ce\u6210\u7af9\u5728\u80f8\uff0c\u5012\u60f3\u89c1\u8bc6\u8fd9\u201c\u5929\u5a9a\u6559\u4e3b\u201d\uff0c\u5012\u5e95\u662f\u600e\u4e48\u6837\u4e2a\u4eba\u7269\uff01\u95e8\u5e18\u4e00\u6380\uff0c\u7a1a\u51e4\u9ea6 \u6167\uff0c\u548c\u53e6\u4e00\u5c11\u5973\uff0c\u6276\u7740\u4e00\u4eba\u8fdb\u6765\u3002\u4f0a\u98ce\u76ee\u5149\u95ea\u5904\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6cdb\u8d77\u4e86\u4e00\u79cd\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u5374 \u4e5f\u53c8\u6709\u4e9b\u5931\u671b\u7684\u5473\u9053\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u5148\u524d\u5fd6\u6d4b\uff0c\u8fd9\u201c\u5929\u5a9a\u6559\u4e3b\u201d\uff0c\u8bf4\u4e0d\u5b9a\u662f\u600e\u4e48\u6837\u4e2a\u7edd\u4e16\u7f8e\u4eba\uff1a\u90a3\u77e5\u5165\u76ee\u4e4b\u4e0b\uff0c\u5374\u9669\u4e9b \u5c06\u65e5\u524d\u6240\u5403\u4e4b\u996d\uff0c\u90fd\u5455\u4e86\u51fa\u6765\uff01 \u3000\u3000\u90a3\u201c\u5929\u5a9a\u6559\u4e3b\u201d\uff0c\u5728\u4e00\u4e2a\u62e5\u80bf\u4e0d\u582a\u7684\u8eaf\u4f53\u4e0a\uff0c\u7a7f\u7740\u6253\u626e\u548c\u90a3\u56db\u4e2a\u5c11\u5973\u540c\u6837\u7684\u900f\u660e\u8f7b\u7eb1\uff0c \u5728\u8fd9\u4e0a\u9762\uff0c\u662f\u4e00\u5f20\u5176\u4e11\u65e0\u6bd4\uff0c\u4e0a\u9762\u5374\u6d82\u6ee1\u4e86\u8102\u7c89\u7684\u9762\u5b54\uff01\u89c1\u4e86\u4f0a\u98ce\uff0c\u5c31\u5f20\u5f00\u5979\u90a3\u975e\u5e38\u5927\u7684 \u5634\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u54ce\u54df\uff01\u60f3\u4e0d\u5230\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u8fd8\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u89d2\u8272\uff01\u6167\u513f\uff01\u4f60\u771f\u4e56\uff01\u201d \u3000\u3000\u4f0a\u98ce\u6068\u4e0d\u5f97\u8d76\u7d27\u63a9\u4e0a\u8033\u6735\uff0c\u4e00\u4e2a\u6c99\u54d1\u7c97\u4fd7\u5374\u53c8\u77eb\u63c9\u9020\u4f5c\u7684\u58f0\u97f3\uff0c\u5176\u96be\u542c\u7684\u7a0b\u5ea6\uff0c\u53ef\u60f3\u800c \u89c1\uff01 \u3000\u3000\u4ed6\u6697\u6697\u5947\u602a\uff0c\u8fd9\u79cd\u5947\u4e11\u4e4b\u4eba\uff0c\u600e\u4f1a\u662f\u201c\u5929\u5a9a\u201d\u6559\u4e3b\uff01\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u8fd9\u5929\u5a9a\u6559\u4e3b\uff0c\u4e07\u5999\u4ed9 \u5a18\uff0c\u5374\u751f\u5177\u4e00\u526f\u5a9a\u9aa8\uff0c\u4e0e\u4e4b\u4ea4\u53f0\uff0c\u9c9c\u6709\u4e0d\u6b32\u4ed9\u6b32\u6b7b\u8005\uff01\u53ea\u662f\uff0c\u5979\u81ea\u5df1\u4e5f\u672a\u5c1d\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u5c0a \u5bb9\uff0c\u662f\u4ee5\u624d\u4f1a\u8ba9\u56db\u4e2a\u59ff\u8272\u7edd\u7f8e\u7684\u5973\u5f1f\u5b50\uff0c\u5148\u60d1\u4eba\u4e4b\u5fc3\u667a\uff0c\u7136\u540e\u624d\u2014\u2014 \u3000\u3000\u4f0a\u98ce\u7d22\u6027\u4e0d\u52a8\uff0c\u770b\u770b\u8fd8\u6709\u4ec0\u4e48\u82b1\u6837\u3002\u5929\u5a9a\u6559\u4e3b\u4e00\u6325\u624b\uff0c\u90a3\u56db\u4e2a\u5c11\u5973\u4fbf\u62bf\u7740\u5634\uff0c\u9000\u4e86\u51fa \u53bb\u3002\u4f0a\u98ce\u6697\u6697\u76b1\u7709\uff0c\u51c6\u5907\u968f\u65f6\u51fa\u624b\u4e00\u51fb\u3002 \u3000\u3000\u4e07\u5999\u4ed9\u5a18\u5f77\u4f5b\u8feb\u4e0d\u53ca\u5f85\u4f3c\u7684\uff0c\u6b3e\u6b3e\u5730\u8d70\u5230\u5e8a\u524d\uff0c\u5f80\u5e8a\u8fb9\u4e00\u5750\uff0c\u4fbf\u4f38\u51fa\u84b2\u6247\u822c\u7684\u624b\u638c\uff0c\u7adf \u8981\u53bb\u6478\u4f0a\u98ce\u7684\u8138\u988a\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u8bd5\u4e00\u8fd0\u6c14\uff0c\u81ea\u89c9\u771f\u6c14\u5df2\u65e0\u6ede\u963b\uff0c\u65b9\u624d\u7684\u90a3\u79cd\u660f\u6175\uff0c\u8ff7\u8361\u7684\u795e\u667a\uff0c\u6b64\u523b\u5df2\u4e0d\u590d\u518d \u6709\u3002\u5c31\u5728\u4e07\u5999\u4ed9\u5a18\u7684\u624b\uff0c\u5feb\u8981\u63a5\u89e6\u4f0a\u98ce\u7684\u9762\u988a\u65f6\uff0c\u4ed6\u5934\u5fae\u4fa7\uff0c\u53cc\u624b\u500f\u7136\u5982\u7535\u4f38\u51fa\uff0c\u5206\u70b9\u90a3\u5929 \u5a9a\u6559\u4e3b\u7684\u80c1\u4e0b\u201c\u7389\u673a\u201d\u548c\u524d\u80f8\u201c\u5c06\u53f0\u201d\uff0c\u4e24\u5904\u5927\u7a74\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62db\u51fa\u624b\u5982\u98ce\uff0c\u4f55\u51b5\u662f\u5728\u5bf9\u65b9\u4e07\u4e07\u4e0d\u4f1a\u9632\u5907\u4e4b\u65f6\u51fb\u51fa\uff0c\u7adf\u7528\u4e86\u4e5d\u6210\u771f\u529b\uff0c\u7acb\u523b\u5c06\u8fd9 \u6deb\u8361\u4e11\u602a\u4e4b\u4eba\uff0c\u6bd9\u4e8e\u638c\u4e0b\u3002 \u3000\u3000\u4e07\u5999\u4ed9\u5a18\u679c\u7136\u5927\u60ca\uff0c\u5979\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5e74\u8f7b\u5c0f\u4f19\u5b50\u5728\u53d7\u4e86\u5979\u7684\u201c\u8ff7\u9b42\u7c89\u201d\u548c\u201c\u86c7\u5973\u6307\u201d\u4e24 \u79cd\u8ff7\u9b42\u4e4b\u672f\u540e\uff0c\u4ecd\u80fd\u51fa\u624b\u5fa1\u654c\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5979\u4e5f\u6709\u4ee4\u4f0a\u98ce\u60f3\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u7adf\u5728\u8fd9\u7535\u5149\u4e00\u95ea\u822c\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4f38\u51fa\u53bb\u6478\u4f0a\u98ce\u9762\u988a \u7684\u624b\uff0c\u7adf\u4e5f\u500f\u7136\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u53cc\u6307\u5982\u5251\uff0c\u76f4\u70b9\u4f0a\u98ce\u9f3b\u4e0b\u7684\u201c\u95fb\u9999\u201d\u7a74\u3002\u6307\u98ce\u51cc\u5389\uff0c\u663e\u7136\u529f\u529b \u6df1\u539a\uff0c\u4ea6\u81fb\u7edd\u9876\uff01 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u4f0a\u98ce\u7eb5\u7136\u80fd\u70b9\u4e2d\u5979\u7684\u4e24\u5904\u5927\u7a74\uff0c\u81ea\u5df1\u53ef\u4e5f\u514d\u4e0d\u4e86\u53d7\u4e0a\u4e00\u6307\u3002\u4ee5\u4e07\u5999\u4ed9\u5a18\u7684\u8fd9 \u79cd\u6307\u529b\u800c\u8a00\uff0c\u4ed6\u7109\u80fd\u8fd8\u6709\u547d\u5728\uff01 \u3000\u3000\u4f55\u51b5\u4ed6\u6b64\u523b\u8eab\u5728\u654c\u7a9f\uff0c\u53ea\u8981\u81ea\u5df1\u7a74\u9053\u88ab\u626b\u4e0a\u4e00\u70b9\uff0c\u771f\u529b\u5fae\u4e00\u53d7\u963b\uff0c\u95e8\u5916\u90a3\u56db\u4e2a\u5c11\u5973\uff0c\u663e \u89c1\u4ea6\u662f\u9ad8\u624b\uff0c\u4ed6\u4e5f\u662f\u51f6\u591a\u5409\u5c11\uff01 \u3000\u3000\u4ed6\u6b64\u65f6\u529f\u529b\uff0c\u867d\u589e\u8fdb\u6570\u500d\uff0c\u4f46\u4e34\u654c\u4e4b\u65f6\uff0c\u6240\u7528\u7684\u8fd8\u662f\u4ee5\u524d\u7684\u62db\u672f\uff0c\u5bf9\u4ed8\u666e\u901a\u4e00\u822c\u6c5f\u6e56\u9ad8 \u624b\uff0c\u867d\u5df2\u7ef0\u7ef0\u6709\u4f59\uff1b\u4f46\u773c\u524d\u8fd9\u5947\u4e11\u5987\u4eba\u7684\u529f\u529b\uff0c\u5374\u7edd\u975e\u666e\u901a\u4e00\u822c\u6c5f\u6e56\u9ad8\u624b\uff0c\u53ef\u4ee5\u6bd4\u62df\u7684\u54e9\uff01 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u4e03\u7ae0\u3000\u4e14\u65bd\u5999\u8ba1>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u4e14\u65bd\u5999\u8ba1 \u3000\u3000\u4f0a\u98ce\u5c61\u83b7\u5947\u7f18\uff0c\u7adf\u5f97\u5230\u6570\u5341\u5e74\u6765\u6b66\u6797\u4e2d\u76db\u4f20\u7684\u5947\u4eba\u2014\u2014\u5251\u5148\u751f\u4ee5\u5148\u5929\u4e4b\u771f\u6c14\uff0c\u4e3a\u4ed6\u6253\u901a \u4e86\u5185\u5bb6\u6700\u96be\u8d2f\u901a\u7684\u201c\u7763\u201d\uff0c\u201c\u4efb\u201d\u4e24\u8109\uff0c\u800c\u4e14\u8fd8\u5f97\u5230\u6ec7\u4e2d\u65e0\u91cf\u5c71\u7684\u85cf\u5b9d\u4e4b\u56fe\u3002 \u3000\u3000\u662f\u4ee5\u663c\u591c\u517c\u7a0b\uff0c\u7531\u5ddd\u4eba\u6ec7\uff0c\u671f\u671b\u80fd\u5f97\u5230\u767e\u5341\u5e74\u524d\u4e00\u4f4d\u6b66\u6797\u524d\u8f88\u5f02\u4eba\u5728\u4e34\u6b7b\u4e4b\u9645\uff0c\u85cf\u4eba\u65e0 \u91cf\u6df1\u5c71\u4e2d\u7684\u79d8\u7b08\uff0c\u7075\u4e39\u548c\u89e3\u836f\uff0c\u6765\u89e3\u6551\u7ec8\u5357\u5c71\u91cc\u6570\u767e\u4e2a\u5944\u5944\u4e00\u606f\u7684\u7ec8\u5357\u5f1f\u5b50\u3002 \u3000\u3000\u90a3\u77e5\u5929\u8fdd\u4eba\u613f\uff0c\u4ed6\u4e00\u65f6\u5927\u610f\uff0c\u7adf\u4e2d\u4e86\u201c\u5929\u5a9a\u6559\u201d\u4e0b\u7a1a\u51e4\u9ea6\u6167\u7684\u6781\u5999\u8ff7\u836f\uff0c\u660f\u8ff7\u4e2d\u88ab\u63b3\u4eba \u5929\u5a9a\u6559\u4e3b\u4e07\u5999\u4ed9\u5a18\u7684\u8273\u7a9f \u3000\u3000\u6b64\u523b\u60c5\u51b5\u5371\u6b86\u5df2\u6781\u3002\u4f0a\u98ce\u77e5\u9053\uff0c\u81ea\u5df1\u7eb5\u7136\u80fd\u4f24\u5f97\u8fd9\u5947\u4e11\u7684\u5929\u5a9a\u6559\u4e3b\uff0c\u4f46\u81ea\u5bb6\u4e5f\u96be\u514d\u88ab\u70b9 \u4e2d\u7a74\u9053\u3002 \u3000\u3000\u90a3\u4e48\u4e00\u6765\uff0c\u81ea\u5bb6\u8eab\u5904\u864e\u7a74\uff0c\u7a74\u9053\u82e5\u88ab\u70b9\uff0c\u540e\u679c\u5c82\u975e\u4e0d\u582a\u8bbe\u60f3\uff01 \u3000\u3000\u8bf4\u6765\u867d\u957f\uff0c\u7136\u800c\u5f53\u65f6\u7684\u60c5\u51b5\uff0c\u5374\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u4ed6\u5fc5\u9700\u7acb\u523b\u4f5c\u4e2a\u660e\u786e\u7684\u51b3\u5b9a\uff0c\u800c\u4ed6\u81ea\u8eab\u7684\u6027\u547d\uff0c\u4fbf\u60ac\u4e8e\u4ed6\u7684\u51b3\u5b9a\u4e4b\u4e0a\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u624b\u4e2d\u7684\u529b\u9053\u731b\u6361\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4e70\u529b\u56de\u6536\u4e4b\u9645\uff0c\u4ed6\u7684\u8eab\u5f62\u4e5f\u85c9\u52bf\u540e\u7f29\u4e8c\u5bf8\uff0c\u540c\u65f6\u5f20\u5f00\u5634\u5df4\u3002 \u3000\u3000\u8fd9\u4e48\u4f7f\u6210\u4e86\u90a3\u5929\u5a9a\u6559\u4e3b\u5982\u679c\u4e0d\u4e5f\u7acb\u523b\u64a4\u62db\uff0c\u90a3\u4e48\u5979\u7684\u4e00\u6307\uff0c\u4fbf\u6070\u597d\u70b9\u5728\u4f0a\u98ce\u7684\u5634\u91cc\uff0c\u751a \u81f3\u53ef\u80fd\u88ab\u4ed6\u54ac\u4e0a\u4e00\u53e3\u3002 \u3000\u3000\u4e07\u5999\u4ed9\u5a18\u88c2\u5634\u4e00\u7b11\uff0c\u8eab\u5f62\u500f\u7136\u6ed1\u5f00\u4e24\u5c3a\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b69\u5b50\u529f\u592b\u4e0d\u9519\u561b\u3002\u201d \u3000\u3000\u5de6\u624b\u8f7b\u98d8\u98d8\u7684\u4e00\u626c\uff0c\u4f3c\u4e4e\u6709\u4e00\u80a1\u8ff7\u8499\u70df\u6c32\uff0c\u81ea\u5979\u90a3\u8f7b\u7eb1\u7684\u9614\u8896\u4e2d\u9038\u51fa\u3002 \u3000\u3000\u4f0a\u98ce\u8d76\u7d27\u5c4f\u4f4f\u547c\u5438\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5df2\u6df1\u77e5\u4eba\u5bb6\u8ff7\u836f\u7684\u5389\u5bb3\uff0c\u77e5\u9053\u81ea\u5bb6\u53ea\u8981\u95fb\u7740\u4e00\u70b9\uff0c\u90a3\u4e48\u53c8\u662f\u56db\u80a2\u65e0\u529b\uff0c\u5f97\u542c\u51ed\u4eba \u5bb6\u7684\u6446\u5e03\u3002 \u3000\u3000\u4ed6\u6bd5\u7adf\u4e45\u8d70\u6c5f\u6e56\uff0c\u975e\u4e00\u822c\u521d\u51fa\u9053\u7684\u5ae9\u624b\u53ef\u6bd4\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8fd8\u80fd\u4fdd\u6301\u4f4f\u81ea\u5df1\u5fc3\u795e\u7684\u9547 \u5b9a\u3002 \u3000\u3000\u95ed\u76ee\u56db\u671b.\u8fd9\u7eee\u4e3d\u7684\u623f\u95f4\u4e2d\uff0c\u7adf\u6ca1\u6709\u7a97\u5b50\u3002 \u3000\u3000\u8fd9\u4f7f\u4ed6\u539f\u5148\u6253\u7b97\u5148\u4ece\u7a97\u53e3\u9003\u51fa\u7684\u60f3\u6cd5\uff0c\u987f\u65f6\u843d\u7a7a\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u95e8\u5916\u5fc5\u7136\u6709\u90a3\u56db\u4e2a\u5973\u5b50\u5b88\u5019\uff0c\u4ed6\u82e5\u593a\u95e8\u800c\u51fa\uff0c\u90a3\u56db\u4e2a\u5973\u5b50\u600e\u4f1a\u653e\u4ed6\u8d70\uff01\u53ea\u8981\u7a0d\u4e00 \u803d\u8bef\uff0c\u81ea\u5df1\u5c31\u53ef\u80fd\u8d70\u4e0d\u4e86\u5566\uff01 \u3000\u3000\u4ed6\u5fc3\u601d\u767e\u8f6c\uff0c\u7136\u800c\u5e76\u6ca1\u6709\u8d39\u53bb\u591a\u5c11\u65f6\u5019\uff0c\u90a3\u8ff7\u8499\u70df\u6c32\uff0c\u4e5f\u5140\u81ea\u672a\u6563\u3002 \u3000\u3000\u6b64\u523b\u90a3\u5929\u5a9a\u6559\u4e3b\u5374\u4e5f\u9759\u7acb\u672a\u52a8\uff0c\u5fc3\u4e2d\u4e5f\u5728\u6253\u7b97\u7740\u3002\u5979\u5df2\u77e5\u9053\u8fd9\u5e74\u8f7b\u4eba\u529f\u529b\u7edd\u9ad8\uff0c\u800c\u5e74\u8f7b \u4eba\u6709\u7740\u5982\u6b64\u529f\u529b\u7684\uff0c\u5fc5\u5b9a\u5927\u6709\u6765\u5934\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e07\u5999\u4ed9\u5a18\u4e00\u5411\u5c45\u4e8e\u82d7\u7586\uff0c\u6d89\u8db3\u4e2d\u539f\u6b66\u6797\uff0c\u8fd8\u6ca1\u6709\u591a\u4e45\uff0c\u4eba\u867d\u4e11\u964b\uff0c\u8d2a\u6deb\uff0c\u7136\u800c\u5fc3 \u601d\u5374\u6781\u7f1c\u5bc6\uff0c\u6b66\u529f\u4e5f\u6781\u9ad8\u3002 \u3000\u3000\u6b64\u523b\u5979\u5012\u4e0d\u662f\u754f\u60e7\u4f0a\u98ce\u7684\u6b66\u529f\uff0c\u800c\u662f\u6050\u6015\u4ed6\u548c\u6709\u5173\u81ea\u5df1\u7684\u5176\u4ed6\u6559\u6d3e\u6709\u6240\u5173\u8fde\uff0c\u81ea\u5df1\u82e5\u4e3a \u4e86\u8fd9\u79cd\u4e8b\u800c\u5f97\u7f6a\u4e00\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u5374\u53c8\u4f55\u5fc5.\uff1f \u3000\u3000\u800c\u5979\u81ea\u5df1\u4e5f\u77e5\u9053\u81ea\u5df1\u6b64\u6b21\u80fd\u5728\u4e2d\u539f\u6b66\u6797\u521b\u7acb\u6559\u6d3e\uff0c\u5173\u7cfb\u7740\u4e00\u4e2a\u6781\u5927\u7684\u8ba1\u753b\uff0c\u662f\u4ee5\u5979\u4e4b\u884c \u4e8b\uff0c\u4e5f\u683c\u5916\u6765\u5f97\u5c0f\u5fc3\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u4e24\u4eba\u7684\u5f62\u51b5\uff0c\u5c31\u53d8\u5f97\u6781\u4e3a\u5947\u7279\uff0c\u4e00\u4e2a\u7741\u7740\u53cc\u773c\u8eba\u5728\u5e8a\u4e0a\uff0c\u53e6\u4e00\u4e2a\u5374\u6014\u6014\u5730\u7ad9\u5728\u5e8a \u8fb9\u3002\u4e24\u4eba\u4e4b\u95f4\uff0c\u6709\u4e00\u80a1\u8ff7\u8499\u7684\u767d\u8272\u70df\u6c32\uff0c\u4e45\u4e45\u672a\u6563\uff1b\u5374\u7ed9\u8fd9\u79cd\u4e0d\u8c03\u548c\u7684\u5f62\u51b5\uff0c\u63c9\u5408\u4e86\u4e9b\u8c03\u548c \u7684\u5473\u9053\u3002\u4e24\u4eba\u5fc3\u4e2d\uff0c\u5404\u6709\u6240\u60e7\uff0c\u4e45\u4e45\u6ca1\u6709\u4e3e\u52a8\u3002 \u3000\u3000\u5c24\u5176\u662f\u4f0a\u98ce\uff0c\u4ed6\u66f4\u6478\u4e0d\u6e05\u8fd9\u5929\u5a9a\u6559\u4e3b\u7684\u6df1\u6d45\uff0c\u601d\u8651\u767e\u7ed3\u4e4b\u4e0b\uff0c\u5fc3\u5ff5\u4e5f\u7a81\u5730\u4e00\u52a8\uff1a \u3000\u3000\u201c\u9664\u4e86\u5929\u4e89\u6559\u4e4b\u5916\uff0c\u7ec8\u5357\u5f1f\u5b50\u53d7\u7684\u662f\u201c\u5929\u6bd2\u6559\u201d\u4e4b\u6bd2\uff0c\u800c\u6b64\u523b\u53c8\u591a\u4e86\u4e00\u4e2a\u201c\u5929\u5a9a\u6559\u201d\uff0c \u96be\u9053\u8fd9\u4e09\u8005\u4e4b\u95f4\uff0c\u6709\u6240\u5173\u8fde\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u672c\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u5fc3\u4e2d\u8f6c\u5ff5\u4e4b\u540e\uff0c\u5c31\u7d27\u7d27\u6293\u7740\u8fd9\u4e00\u70b9\u7aef\u502a\u800c\u8ffd\u5bfb\u4e0b\u53bb\uff0c\u4ee5\u6c42\u5bfb\u5f97 \u81ea\u5df1\u7684\u751f\u673a\u3002 \u3000\u3000\u4ed6\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6b64\u523b\u654c\u5f3a\u6211\u5f31\uff0c\u4f55\u51b5\u6211\u6709\u7740\u90a3\u4e48\u91cd\u8981\u7684\u4e8b\u8981\u505a\uff0c\u53ef\u4e0d\u80fd\u548c\u8fd9\u4e9b\u65e0\u803b\u7684\u5973\u5b50\u591a\u7f20 \u3000\u3000\u201c\u4f46\u662f\u4ee5\u6211\u7684\u529b\u91cf\uff0c\u53c8\u7edd\u4e0d\u80fd\u9664\u53bb\u5979\u4eec\uff0c\u552f\u4e00\u7684\u529e\u6cd5\u2014\u2014\u201d \u3000\u3000\u90a3\u5929\u5a9a\u6559\u4e3b\u89c1\u8fd9\u5e74\u8f7b\u4eba\u7741\u7740\u5927\u773c\u775b\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4e5f\u6ca1\u6709\u4e1d\u6beb\u88ab\u8ff7\u7684\u8ff9\u8c61\uff0c\u8d8a\u53d1\u5730\u83ab\u540d\u5176\u7384 \u865a\u3002 \u3000\u3000\u4f0a\u98ce\u53cc\u8098\u4e00\u652f\uff0c\u4e0a\u8eab\u4fa7\u4fa7\u5750\u4e86\u8d77\u6765\u3002\u53e3\u4e2d\u5374\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u9119\u5949\u4e86\u5929\u4e89\u6559\u4e3b\u4e4b\u547d\uff0c\u6709\u4e8b \u4eba\u6ec7\u3002\u4e0d\u77e5\u4e4b\u4e2d\uff0c\u5192\u72af\u4e86\u771f\u6559\uff0c\u8fd8\u671b\u9601\u4e0b\uff0c\u9ad8\u62ac\u8d35\u624b\uff0c\u653e\u8fc7\u5c0f\u9119\uff0c\u65e5\u540e\u655d\u6559\u6559\u4e3b\uff0c\u5fc5\u6709\u8865 \u62a5\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u65b9\u624d\u5fc3\u5ff5\u52a8\u5904\uff0c\u77e5\u9053\u81ea\u5bb6\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u53ea\u5f97\u4e14\u65bd\u8be1\u8ba1\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u62ac\u51fa\u5929\u4e89\u6559\u7684\u62db\u724c\u6765\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\uff1a\u82e5\u662f\u8fd9\u5929\u5a9a\u6559\u771f\u7684\u548c\u5929\u4e89\u6559\u6709\u7740\u5173\u7cfb\uff0c\u90a3\u81ea\u662f\u6700\u597d\uff1b\u5982\u82e5\u4e0d\u7136\uff0c\u5bf9\u65b9\u4e5f\u53ef\u80fd\u4f1a \u5356\u5929\u4e89\u6559\u4e00\u4e2a\u4ea4\u60c5\u3002\u4ed6\u6717\u58f0\u8bf4\u7f62\uff0c\u5929\u5a9a\u6559\u4e3b\u679c\u7136\u4e00\u6014\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u81ea\u5f97\u610f\uff1a \u3000\u3000\u201c\u8fd9\u5e74\u8f7b\u4eba\u679c\u7136\u662f\u540c\u4e00\u7ebf\u4e0a\u4e4b\u4eba\uff0c\u5e78\u597d\u6211\u6ca1\u6709\u5982\u4f55\uff0c\u5426\u5219\u4f20\u51fa\u53bb\u5c82\u975e\u7b11\u8bdd\uff01\u201d \u3000\u3000\u5979\u5bf9\u4e2d\u539f\u6b66\u6797\u6781\u4e3a\u751f\u758f\uff0c\u662f\u4ee5\u4f0a\u98ce\u8bef\u6253\u8bef\u649e\uff0c\u624d\u4f1a\u649e\u4e2a\u6b63\u7740\u3002\u5426\u5219\u5929\u4e0b\u90a3\u4f1a\u6709\u8fd9\u4e48\u7b80\u5355 \u7684\u4e8b.\uff0c \u3000\u3000\u4f0a\u98ce\u89c1\u4e86\u5979\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u77e5\u9053\u8ba1\u5df2\u5f97\u901e\u3002\u90a3\u77e5\u8111\u4e2d\u53c8\u662f\u4e00\u9635\u6655\u65cb\uff0c\u4f0a\u98ce\u6697\u53eb\u4e00\u58f0 \u82e6\u4e5f\uff01\u53c8\u660f\u8ff7\u5730\u5012\u5728\u5e8a\u4e0a\u4e86\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5f00\u53e3\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u81ea\u7136\u5c31\u4e0d\u80fd\u591f\u5c4f\u7740\u547c\u5438\uff0c\u662f\u4ee5\u53c8\u5438\u8fdb\u4e00\u4e9b\uff0c\u90a3\u5386\u4e45\u4e0d\u6563\u7684\u70df\u6c32\uff1b \u800c\u8fd9\u70df\u6c32\uff0c\u6b63\u662f\u4e07\u5999\u4ed9\u5a18\u7684\u79d8\u4f20\u8ff7\u836f\u3002 \u3000\u3000\u4ed6\u660f\u8ff7\u4e4b\u4e2d\uff0c\u5ffd\u89c9\u9f3b\u4e2d\u55c5\u5230\u4e00\u79cd\u6781\u4e3a\u8f9b\u8fa3\u7684\u5473\u9053\uff0c\u5fcd\u4e0d\u4f4f\u6253\u4e86\u4e2a\u55b7\u568f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u82cf\u9192\u4e86\u3002 \u3000\u3000\u7741\u773c\u4e00\u671b\uff0c\u4e00\u4e2a\u5947\u4e11\u7684\u9762\u5b54\uff0c\u6b63\u671b\u7740\u4ed6\u563b\u563b\u800c\u7b11\uff0c\u90a3\u6b63\u662f\u5c5e\u4e8e\u5929\u5a9a\u6559\u4e3b\u7684\u3002 \u3000\u3000\u8fd9\u5947\u4e11\u7684\u7b11\u5bb9\u4f7f\u5f97\u4ed6\u5fc3\u91cc\u611f\u5230\u4e00\u9635\u6076\u5fc3\uff0c\u95ed\u8d77\u773c\u775b\uff0c\u4e0d\u53bb\u770b\u5979\u3002 \u3000\u3000\u7136\u800c\u8033\u4e2d\u5374\u542c\u5230\u5929\u5a9a\u6559\u4e3b\uff0c\u4ee5\u4e00\u79cd\u548c\u5979\u90a3\u5947\u4e11\u9762\u5bb9\u6781\u4e3a\u914d\u5408\u7684\u96be\u542c\u58f0\u8c03\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b69\u5b50\uff01\u4e0d\u8981\u6015\uff0c\u5f20\u5f00\u773c\u775b\u597d\u4e86\uff0c\u672c\u6559\u4e3b\u53c8\u4e0d\u4f1a\u5403\u4e86\u4f60\u3002\u201d \u3000\u3000\u4e07\u5999\u4ed9\u5a18\u5728\u6781\u5e7c\u5e74\u65f6\uff0c\u5c31\u5c45\u4e8e\u82d7\u7586\uff0c\u5979\u867d\u7136\u6ca1\u6709\u5c06\u4e2d\u539f\u65b9\u8a00\u5fd8\u53bb\uff0c\u7136\u800c\u8bf4\u51fa\u6765\uff0c\u5374\u751f\u786c \u5f97\u5f88\uff1b\u518d\u52a0\u4e0a\u5979\u90a3\u79cd\u5982\u591c\u67ad\u822c\u523a\u8033\u7684\u58f0\u8c03\uff0c\u90a3\u79cd\u96be\u542c\uff0c\u5b9e\u5728\u662f\u975e\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\u7684\u3002 \u3000\u3000\u7136\u800c\u4f0a\u98ce\u5374\u4e0d\u5f97\u4e0d\u5f20\u5f00\u773c\u6765\u3002 \u3000\u3000\u4e07\u5999\u4ed9\u5a18\uff0c\u53c8\u563b\u5f00\u5927\u5634\u7b11\u9053\uff1a \u3000\u3000\u201c\u672c\u6559\u4e3b\u65e9\u5c31\u731c\u5230\u4f60\u662f\u5929\u4e89\u6559\u4e0b\u7684\u5f92\u5f1f\uff0c\u201c\u4e09\u5929\u201d\u4e4b\u5916\uff0c\u82e5\u8fd8\u6709\u50cf\u4f60\u8fd9\u6837\u7684\u5e74\u8f7b\u597d\u624b\uff0c \u90a3\u4e48\uff0c\u6211\u4eec\u90a3\u4f4d\u8001\u5934\u5b50\u53c8\u8981\u6c14\u6b7b\u4e86\u3002\u5582\uff0c\u6211\u8bf4\u2026\u2026\u201d \u3000\u3000\u5979\u5520\u5520\u53e8\u53e8\u53c8\u8bf4\u4e86\u4e9b\u8bdd\uff0c\u4f0a\u98ce\u5374\u6ca1\u6709\u518d\u5f80\u4e0b\u9762\u542c\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u53c8\u5728\u6c89\u601d\u7740\uff1a \u3000\u3000\u201c\u8fd9\u201c\u5929\u4e89\u201d\u201c\u5929\u6bd2\u201d\u201c\u5929\u5a9a\u201d\u4e09\u6559\uff0c\u679c\u7136\u6e90\u51fa\u4e3a\u4e00\uff0c\u6240\u4ee5\u8fd9\u4e11\u516b\u602a\u624d\u4f1a\u6709\u201c\u4e09\u5929\u201d\u8fd9 \u4e2a\u8bf4\u6cd5\u3002\u800c\u4e14\u542c\u5979\u7684\u53e3\u6c14\uff0c\u5728\u4e09\u4e2a\u6559\u4e3b\u4e4b\u4e0a\uff0c\u4f3c\u4e4e\u8fd8\u53e6\u6709\u4e00\u4e2a\u201c\u8001\u5934\u5b50\u201d\uff0c\u9ad8\u9ad8\u5728\u4e0a\uff0c\u6697\u4e2d \u63a7\u5236\u7740\u8fd9\u201c\u4e09\u5929\u6559\u201d\u7684\u6d3b\u52a8\uff0c\u53ea\u662f\u8fd9\u201c\u8001\u5934\u5b50\u201d\uff0c\u53c8\u662f\u4f55\u4eba\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u7591\u5ff5\u4e1b\u751f\uff0c\u53e3\u4e2d\u5374\u5728\u552f\u552f\u5730\u7b54\u7740\u90a3\u5929\u5a9a\u6559\u4e3b\u7684\u8bdd\u3002 \u3000\u3000\u201c\u8fd9\u201c\u8001\u5934\u5b50\u201d\u7ec4\u6b64\u6027\u8d28\uff0c\u65b9\u6cd5\uff0c\u624b\u8155\u90fd\u7edd\u5bf9\u4e0d\u540c\u7684\u4e09\u4e2a\u6559\u6d3e\uff0c\u5fc5\u5b9a\u6709\u7740\u6781\u5927\u7684\u91ce\u5fc3\uff0c \u770b\u6837\u5b50\u7adf\u60f3\u5c06\u5929\u4e0b\u6b66\u6797\u8c6a\u58eb\u4e00\u7f51\u6253\u5c3d\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u6697\u4e2d\u4e00\u51db\uff0c\u60f3\u5230\u81ea\u5df1\u548c\u201c\u5929\u4e89\u6559\u201d\u7684\u6df1\u4ec7\uff0c\u590d\u4ec7\u6050\u5c06\u66f4\u4e3a\u6e3a\u832b\uff0c\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3 \u6c14\u3002\u5374\u542c\u90a3\u5929\u5a9a\u6559\u4e3b\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5144\u5f1f\uff0c\u4e5f\u662f\u6211\u8ddf\u4f60\u6295\u7f18\uff0c\u8fd8\u820d\u4e0d\u5f97\u653e\u4f60\u8d70\uff0c\u6211\u770b\u4f60\u8981\u662f\u4e0d\u6025\u7684\u8bdd\uff0c\u8fd8\u662f\u5728\u8fd9\u91cc\u591a\u803d \u51e0\u5929\u5427\u3002\u201d \u3000\u3000\u6324\u7709\u5f04\u773c\uff0c\u4e11\u6001\u6bd5\u9732\u3002 \u3000\u3000\u4f0a\u98ce\u8fde\u5fd9\u9053\uff1a \u3000\u3000\u201c\u6559\u4e3b\u5ba0\u53ec\uff0c\u5c0f\u9119\u4f55\u5e78\u5982\u4e4b\uff01\u53ea\u662f\u5c0f\u9119\u5b9e\u5728\u6709\u6025\u4e8b\uff0c\u4e00\u523b\u4e5f\u803d\u8bef\u4e0d\u5f97\u3002\u201d \u3000\u3000\u4ed6\u770b\u5230\u90a3\u5929\u5a9a\u6559\u4e3b\u76ee\u5149\u4e00\u51db\uff0c\u8d76\u7d27\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u5c0f\u9119\u6ec7\u4e2d\u4e4b\u4e8b\u4e00\u5b8c\uff0c\u5fc5\u5b9a\u5c3d\u8198\u8d76\u6765\u5411\u6559\u4e3b\u95ee\u5b89\u7684\u3002\u201d \u3000\u3000\u4e07\u5999\u4ed9\u5a18\u4e0a\u4e0a\u4e0b\u4e0b\u770b\u4e86\u4ed6\u51e0\u773c\uff0c\u624d\u820d\u4e0d\u5f97\u4f3c\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u662f\u771f\u6709\u6025\u4e8b\uff0c\u4f60\u5c31\u5feb\u53bb\u3002\u53ef\u662f\u4f60\u56de\u6765\u7684\u65f6\u5019\uff0c\u53ef\u4e0d\u8981\u5fd8\u4e86\u518d\u6765\u770b\u6211\u5440\uff01\u4e0d\u7136\uff0c\u4e0b \u6b21\u518d\u8ba9\u6211\u649e\u7740\uff0c\u4e0d\u628a\u4f60\u8fd9\u5c0f\u9b3c\u6495\u6210\u4e24\u534a\u624d\u602a\uff01\u201d \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u5fc3\u6025\u5982\u711a\uff0c\u53ea\u8981\u653e\u4ed6\u8d70\uff0c\u4ed6\u5c31\u8c22\u5929\u8c22\u5730\u4e86\u3002 \u3000\u3000\u4e07\u5999\u4ed9\u5a18\u4e00\u51fb\u638c\uff0c\u90a3\u56db\u4e2a\u5c11\u5973\u7acb\u523b\u62e5\u4e86\u8fdb\u6765\uff0c\u563b\u563b\u54c8\u54c8\u5730\u7b11\u4e2a\u4e0d\u4f4f\u3002 \u3000\u3000\u7a1a\u51e4\u9ea6\u6167\u8d70\u5728\u6700\u524d\u9762\uff0c\u7b11\u5411\u4f0a\u98ce\u9053\uff1a \u3000\u3000\u201c\u606d\u559c\u4f60\u5440\uff01\u201d \u3000\u3000\u4f0a\u98ce\u8138\u4e0a\u500f\u7136\u4e00\u7ea2\uff0c\u53e6\u5916\u4e09\u4e2a\u5c11\u5973\u53c8\u54af\u54af\u7b11\u4e86\u8d77\u6765\uff0c\u4e00\u9762\u8fd8\u5411\u4f0a\u98ce\u629b\u7740\u5a9a\u773c\u3002\u4f0a\u98ce\u76f4\u89c9 \u5982\u8292\u523a\u5728\u80cc\uff0c\u6068\u4e0d\u5f97\u7acb\u523b\u5c31\u51b2\u51fa\u6b64\u95f4\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u516b\u7ae0\u3000\u65e0\u91cf\u5c71\u91cc>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u516b\u7ae0\u3000\u65e0\u91cf\u5c71\u91cc \u3000\u3000\u7b49\u5230\u4f0a\u98ce\u8131\u8eab\u51fa\u6765\u7684\u65f6\u5019\uff0c\u4e1c\u65b9\u7684\u5929\u8272\uff0c\u5df2\u662f\u9ece\u660e\u7684\u82cd\u767d\u4e86\u3002 \u3000\u3000\u4ed6\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u603b\u7b97\u9003\u51fa\u4e86\u8fd9\u8273\u9b54\u4e4b\u7a9f\u3002 \u3000\u3000\u4f46\u4ed6\u601d\u5fd6\u4e4b\u4e0b\uff0c\u53c8\u4e0d\u7981\u89c9\u5f97\u6709\u4e9b\u60ed\u6127\uff0c\u56e0\u4e3a\u81ea\u5df1\u6240\u7528\u7684\uff0c\u7a76\u7adf\u4e0d\u662f\u6b63\u5927\u5149\u660e\u7684\u624b\u6bb5\u3002 \u3000\u3000\u201c\u5bf9\u4ed8\u8fd9\u79cd\u4eba\uff0c\u7528\u8fd9\u79cd\u624b\u6bb5\uff0c\u6b63\u662f\u518d\u6070\u5f53\u4e5f\u6ca1\u6709\u3002\u53e4\u4eba\u4e0d\u4e5f\u8bf4\u8fc7\uff1a\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176 \u4eba\u4e4b\u8eab\u5417\uff01\u6211\u53c8\u4f55\u5c1d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5982\u6b64\u4e00\u60f3\uff0c\u4ed6\u53c8\u89c9\u6cf0\u7136\u3002 \u3000\u3000\u884c\u884c\u91cd\u884c\u884c \u3000\u3000\u4f0a\u98ce\u6bd5\u7adf\u6765\u5230\u4e86\u65e0\u91cf\u5c71\uff0c\u65e0\u91cf\u4e43\u6ec7\u4e2d\u540d\u5c71\uff0c\u7ef5\u4e98\u6570\u767e\u91cc\uff0c\u4e3b\u5cf0\u5728\u666f\u4e1c\u4e4b\u897f\uff0c\u5c71\u9ad8\u4e07\u4ede\u3002 \u3000\u3000\u4f0a\u98ce\u65e5\u843d\u81f3\u666f\u4e1c\uff0c\u5c06\u606f\u4e00\u591c\uff0c\u5306\u5306\u51c6\u5907\uff0c\u6b21\u6668\u4fbf\u7edd\u65e9\u4e0a\u5c71\u3002 \u3000\u3000\u6653\u70df\u672a\u9000\uff0c\u5bd2\u610f\u4fb5\u4eba\uff0c\u5c71\u4e0a\u6e3a\u65e0\u4eba\u8ff9\u3002\u4f0a\u98ce\u76d8\u65cb\u800c\u4e0a\uff0c\u53ea\u89c9\u5bd2\u610f\u8d8a\u6765\u8d8a\u6d53\uff0c\u968f\u4fbf\u5bfb\u4e86\u4e2a \u907f\u98ce\u4e4b\u5904\uff0c\u76d8\u819d\u5deb\u4e0b\u3002 \u3000\u3000\u771f\u6c14\u8fd0\u884c\u4e00\u8f6c\uff0c\u6b63\u662f\u6240\u8c13\uff1a\u201c\u4e09\u82b1\u805a\u9876\uff0c\u4e94\u6c14\u671d\u5143\u3002\u201d\u4f0a\u98ce\u624d\u89c9\u5f97\u5df2\u6062\u590d\u6b63\u5e38\u4f53\u6e29\u3002 \u3000\u3000\u5c06\u90a3\u85cf\u5b9d\u4e4b\u56fe\u53d6\u51fa\u518d\u8be6\u7ec6\u770b\u4e86\u4e00\u904d\uff0c\u56fe\u867d\u8be6\u5c3d\uff0c\u7136\u800c\u5728\u8fd9\u7ef5\u4e98\u767e\u91cc\u7684\u6df1\u5c71\u4e2d\uff0c\u5bfb\u627e\u4e00\u5904 \u6d1e\u7a74\uff0c\u5374\u4e5f\u4e0d\u662f\u6613\u4e8b\u54e9\u3002 \u3000\u3000\u4ed6\u6781\u76ee\u56db\u671b\uff0c\u8fdc\u5904\u5c71\u5cf0\u53e0\u8d77\uff0c\u7fa4\u5c71\u4e4b\u4e2d\uff0c\u4e00\u5cf0\u9ad8\u8038\u5165\u4e91\uff0c\u5c31\u662f\u90a3\u85cf\u5b9d\u4e4b\u5904\u4e86\u3002 \u3000\u3000\u4ed6\u7565\u7565\u7528\u4e86\u4e9b\u5e72\u7cae\uff0c\u4fbf\u53c8\u89c5\u8def\u800c\u53bb\u3002\u8eab\u5f62\u52a8\u5904\uff0c\u5c71\u9e1f\u7fa4\u98de\uff0c\u800c\u4ed6\u90a3\u79cd\u8f7b\u7075\u5feb\u8fc5\uff0c\u5374\u4e5f\u4e0d \u5728\u5c71\u9e1f\u4e4b\u4e0b\u54e9\u3002 \u3000\u3000\u6500\u8d8a\u8fc7\u51e0\u5904\u5c71\u5cf0\uff0c\u4ed6\u7adf\u89c9\u5f97\u6709\u4e9b\u70ed\u4e86\uff0c\u4e5f\u6709\u4e9b\u7d2f\uff0c\u4f46\u6b64\u523b\u76ee\u7684\u5728\u671b\uff0c\u4ed6\u8fde\u6b47\u606f\u4e5f\u4e0d\u80af\u6b47 \u606f\u4e00\u4e0b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u82e5\u4e0d\u662f\u81ea\u5df1\u7684\u5185\u529f\u7684\u7cbe\u8fdb\uff0c\u6b64\u523b\u6015\u4e0d\u65e9\u5c31\u7d2f\u5f97\u8eba\u4e0b\u4e86\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u627e\u5230\u90a3\u5ea7\u9ad8\u5cf0\uff0c\u4ed6\u6beb\u4e0d\u505c\u7559\u5730\u6500\u8d8a\u800c\u4e0a\uff0c\u677e\u7c41\u5fae\u9e23\uff0c\u5b9b\u5982\u4ed9\u7c41\u3002 \u3000\u3000\u4ed6\u601d\u5fd6\u7740\u56fe\u4e0a\u6240\u793a\uff0c\u90a3\u85cf\u5b9d\u4e4b\u5730\uff0c\u662f\u5728\u5c71\u9633\u5904\u7684\u4e00\u4e2a\u5c71\u5773\u91cc\uff0c\u800c\u8fd9\u5c71\u5773\u5374\u5728\u4e00\u9053\u6eaa\u6c34\u7684 \u5c3d\u5934\u3002 \u3000\u3000\u6e10\u884c\u6e10\u8fdc\uff0c\u767d\u4e91\u5f77\u4f5b\u751f\u4e8e\u811a\u5e95\uff0c\u4f0a\u98ce\u9f13\u52c7\u524d\u884c\uff0c\u4f46\u662f\u90a3\u85cf\u5b9d\u4e4b\u5730\uff0c\u867d\u7136\u5728\u6b64\u5c71\u4e4b\u4e2d\uff0c\u5374 \u662f\u4e91\u6df1\u4e0d\u77e5\u5176\u5904\u3002 \u3000\u3000\u66ae\u4e91\u56db\u53f0\u3002 \u3000\u3000\u4f0a\u98ce\u9010\u6e10\u7740\u6025\uff0c\u5ffd\u7136\u542c\u5f97\u5728\u677e\u6d9b\u58f0\u4e2d\uff0c\u7adf\u9690\u9690\u6709\u6d41\u6c34\u6f7a\u6f7a\u4e4b\u58f0\u4f20\u6765\uff0c\u4ed6\u7684\u7cbe\u795e\u4e00\u632f\uff0c\u8fde \u5fd9\u5411\u6c34\u58f0\u53d1\u51fa\u4e4b\u5904\uff0c\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8f6c\u8fc7\u4e00\u5904\u5c71\u6e7e\uff0c\u679c\u6709\u4e00\u9053\u6cc9\u6c34\uff0c\u6cbf\u7740\u5c71\u6da7\u6d41\u4e0b\uff0c\u6f8e\u6e43\u5954\u817e\uff0c\u98de\u6e85\u7740\u7684\u65e0\u6570\u6c34\u73e0\uff0c\u5728\u5929\u8272 \u5c06\u9ed1\u672a\u9ed1\u4e4b\u9645\uff0c\u5206\u5916\u60a6\u76ee\u3002 \u3000\u3000\u4f0a\u98ce\u6cbf\u7740\u5c71\u6da7\uff0c\u66f2\u6298\u4e0a\u884c\uff0c\u98de\u6e85\u7740\u7684\u6c34\u73e0\uff0c\u6e10\u5c06\u4ed6\u7684\u978b\u889c\u6e85\u6e7f\u3002\u5bd2\u98ce\u5439\u8fc7\uff0c\u4ed6\u811a\u4e0a\u51c9\u51c9 \u7684\uff0c\u8eab\u4e0a\u53c8\u5fae\u5fae\u6709\u4e86\u4e9b\u5bd2\u610f\u3002 \u3000\u3000\u4fef\u9996\u4e0b\u671b\uff0c\u767d\u4e91\u7f2d\u7ed5\u3002\u4ef0\u9996\u800c\u671b\uff0c\u5df2\u662f\u5c71\u5cf0\u8fd1\u5dc5\u4e4b\u5904\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u56db\u76fc\uff0c\u5ffd\u89c1\u524d\u9762\u4e24\u58c1\u5939\u5cd9\uff0c\u800c\u8fd9\u5c71\u6da7\u4fbf\u662f\u4ece\u5bf9\u9762\u90a3\u5c71\u5773\u91cc\u6d41\u51fa\u3002\u4ed6\u7cbe\u795e\u4e00\u632f\uff0c \u8eab\u5f62\u4e00\u5f13\uff0c\u4e24\u4e2a\u8d77\u843d\uff0c\u4fbf\u8d8a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u6781\u5feb\u5730\u7a7f\u8fc7\u90a3\u4e24\u58c1\u5939\u5cd9\u4e4b\u95f4\u7684\u5c71\u9053\u3002 \u3000\u3000\u6b64\u523b\u591c\u8272\u867d\u5df2\u6d53\uff0c\u5bd2\u610f\u4e5f\u8d8a\u91cd\uff0c\u4f46\u4f0a\u98ce\u5fc3\u4e2d\u5374\u6ee1\u6000\u70ed\u671b\uff0c\u56e0\u4e3a\u4ed6\u7ec8\u7a76\u5df2\u5bfb\u5f97\u85cf\u5b9d\u4e4b\u5904\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u90a3\u4e9b\u88ab\u6b66\u6797\u4e2d\u4e0d\u77e5\u591a\u5c11\u8c6a\u58eb\u5782\u6d8e\u4e86\u591a\u5e74\u7684\u79d8\u85cf\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u81ea\u5df1\u4fbf\u53ef\u4ee5\u5f97\u5230\uff0c\u5fc3 \u4e2d\u4e0d\u7981\u4e00\u9635\u5267\u8df3\uff0c\u811a\u4e0b\u66f4\u52a0\u5feb\u4e86\u901f\u5ea6\u3002 \u3000\u3000\u4f46\u662f\u4e00\u8fdb\u5c71\u5773\uff0c\u4ed6\u5374\u4e0d\u7981\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u5c71\u5773\u91cc\u9762\u751a\u4e3a\u5bbd\u9614\uff0c\u5bf9\u9762\u4e00\u5904\u9ad8\u5c71\u6d41\u4e0b\u4e00\u80a1\u7011\u5e03\uff0c\u5b9b\u5982\u4e00\u9053\u767d\u7ec3\uff0c\u6447\u66f3\u5929\u9645\uff0c\u6f8e\u6e43\u6d41 \u4e0b\u540e\uff0c\u518d\u6cbf\u7740\u5c71\u6da7\u6d41\u4e0b\u3002 \u3000\u3000\u4ee4\u4f0a\u98ce\u60ca\u6115\u7684\u5374\u662f\uff1a\u5728\u7011\u5e03\u4e4b\u4fa7\uff0c\u7adf\u6709\u51e0\u5904\u4eba\u95f4\u706f\u706b\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u987f\u4f4f\u8eab\u5f62\uff0c\u76ee\u5149\u56db\u626b\uff0c\u8bc1\u660e\u6b64\u5730\u7684\u786e\u548c\u56fe\u4e2d\u6240\u8bb0\uff0c\u6ca1\u6709\u534a\u70b9\u5dee\u9519\u3002\u85cf\u79d8\u4e4b\u5730\uff0c\u5c31 \u662f\u5728\u90a3\u7011\u5e03\u540e\u4fa7\u7684\u4e00\u4e2a\u6d1e\u7a74\u91cc\u3002 \u3000\u3000\u201c\u4f46\u662f\u8fd9\u91cc\u4e3a\u4ec0\u4e48\u4f1a\u6709\u706f\u5149\u5462\uff1f\u662f\u4ec0\u4e48\u4eba\u4f1a\u4f4f\u5728\u8fd9\u79cd\u5730\u65b9\uff1f\u96be\u9053\u90a3\u6b66\u66f2\u661f\u541b\u7684\u85cf\u5b9d\uff0c\u5df2 \u7ecf\u88ab\u522b\u4eba\u6377\u8db3\u5148\u5f97\u4e86\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ed6\u60ca\u7591\u5730\u601d\u5fd6\u7740\uff0c\u4e0d\u6562\u5192\u5931\u5730\u518d\u5f80\u524d\u8d70\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u80fd\u591f\u4f4f\u5728\u8fd9\u79cd\u5730\u65b9\u7684\u4eba\uff0c\u4e0d\u662f\u907f\u4ec7\uff0c\u4fbf\u662f\u606f\u9690\uff0c\u6216\u8005\u662f\u4e3a\u7740\u67d0\u4e00\u79cd\u6b66\u529f\u7684\u4fee\u4e3a\u3002 \u3000\u3000\u7136\u4e0d\u7ba1\u600e\u6837\uff0c\u5374\u5fc5\u7136\u4e00\u5b9a\u662f\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u53c8\u7edd\u4e0d\u80af\u5c31\u6b64\u56de\u8eab\u4e00\u8d70\u3002 \u3000\u3000\u4ed6\u81ea\u5bb6\u7684\u5f97\u5931\uff0c\u8fd8\u5728\u5176\u6b21\uff0c\u7ec8\u5357\u5c71\u91cc\u7684\u6570\u767e\u6761\u4eba\u547d\uff0c\u4e5f\u5168\u62c5\u5f53\u5728\u4ed6\u8eab\u4e0a\uff0c\u6b64\u523b\u4ed6\u662f\u6709\u8fdb \u65e0\u9000\u7684\u3002 \u3000\u3000\u6c34\u58f0\u742e\u742e\uff0c\u98ce\u58f0\u5982\u9e23\u3002 \u3000\u3000\u4f0a\u98ce\u5c31\u85c9\u7740\u8fd9\u4e9b\u58f0\u97f3\u7684\u63a9\u62a4\uff0c\u6781\u5feb\u5730\u63a0\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u85c9\u7740\u5fae\u5f31\u7684\u706f\u5149\uff0c\u4f0a\u98ce\u53ef\u4ee5\u770b\u5230\u7011\u5e03\u65c1\u5c71\u58c1\u4e0b\uff0c\u6709\u4e00\u5ea7\u77f3\u5c4b\uff0c\u4e24\u8fb9\u5404\u5404\u5f00\u4e86\u4e24\u4e2a\u7a97\u5b50\uff0c \u706f\u5149\u4fbf\u662f\u4ece\u7a97\u53e3\u9732\u51fa\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u53c8\u53d1\u73b0\uff0c\u4ece\u8fd9\u7a97\u4e2d\u5c04\u51fa\u7684\u5149\u7ebf\uff0c\u5206\u5916\u523a\u76ee\uff0c\u4e0d\u662f\u666e\u901a\u706f\u5149\u7684\u660f\u9ec4\u8272\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u77f3\u5c4b\u4e0a\u722c\u6ee1\u7684\u67af\u85e4\uff0c\u5c71\u5773\u91cc\u9634\u68ee\u7684\u591c\u98ce\uff0c\u5c71\u58c1\u4e0a\u6f8e\u6e43\u7684\u6d41\u6c34\u3002\u56db\u5468\u6b7b\u4e00\u822c\u7684\u9759\u548c \u9ed1\u6697\u3002 \u3000\u3000\u4f0a\u98ce\u53ea\u89c9\u5f97\u4e00\u80a1\u5bd2\u610f\uff0c\u76f4\u900f\u80cc\u810a\uff0c\u638c\u5fc3\u4e5f\u4e0d\u7981\u6ccc\u51fa\u51b7\u6c57\u3002 \u3000\u3000\u4ed6\u53c8\u5446\u7acb\u4e86\u534a\u664c\uff0c\u7a81\u5730\u6697\u9a82\u81ea\u5df1\uff1a \u3000\u3000\u201c\u5415\u5357\u4eba\u5440\uff01\u5415\u5357\u4eba\uff01\u4f60\u600e\u5730\u5982\u6b64\u80c6\u602f\uff01\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u7ec8\u5357\u5c71\u7684\u6570\u767e\u5f1f\u5b50\u4e4b\u547d\uff0c\u4ee5\u53ca\u4f60 \u81ea\u5df1\u7684\u5207\u9aa8\u6df1\u4ec7\uff0c\u5168\u90fd\u5728\u6b64\u4e3e\u4e0a\uff01\u4f60\u82e5\u662f\u5982\u6b64\u80c6\u602f\uff0c\u4f60\u8fd8\u6709\u4f55\u9762\u76ee\u89c1\u4eba\uff01\u4f60\u8fd8\u6709\u4f55\u9762\u76ee\u89c1\u81ea \u5df1\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4e00\u54ac\u7259\uff0c\u63d0\u6c14\u5411\u524d\u7eb5\u53bb\uff0c\u6781\u529b\u5730\u4e0d\u8ba9\u81ea\u5df1\u53d1\u51fa\u4e00\u4e1d\u58f0\u97f3\u6765\u3002 \u3000\u3000\u9690\u5728\u9634\u5f71\u4e2d\uff0c\u4ed6\u6084\u6084\u5f80\u7a97\u5185\u4e00\u671b\uff0c\u5c4b\u4e2d\u7684\u666f\u8c61\uff0c\u5374\u4f7f\u5f97\u4ed6\u51e0\u4e4e\u60ca\u5524\u51fa\u58f0\u6765\u3002\u4e24\u53ea\u773c\u775b\uff0c \u52a8\u4e5f\u4e0d\u52a8\u5730\u671d\u91cc\u9762\u671b\u7740 \u3000\u3000\u53ea\u89c1\u90a3\u77f3\u5c4b\u751a\u4e3a\u5bbd\u5927\uff0c\u4e1c\uff0c\u897f\u4e24\u7aef\uff0c\u5404\u5806\u7740\u4e9b\u5c71\u85af\uff0c\u832f\u82d3\uff0c\u9ec4\u7cbe\uff0c\u9996\u4e4c\u4e00\u7c7b\u7684\u5c71\u679c\uff0c\u5176 \u4e2d\u4e5f\u8fd8\u6709\u4e9b\u4eba\u95f4\u7684\u5e72\u7cae\u3002 \u3000\u3000\u5357\uff0e\u5317\u4e24\u9762\uff0c\u5374\u5806\u653e\u7740\u4e0d\u8ba1\u5176\u6570\u7684\u73e0\u5b9d\uff0c\u7487\u5149\u91c7\u8272\uff0c\u7eda\u4e3d\u593a\u76ee\uff0c\u7adf\u5c06\u8fd9\u504c\u5927\u7684\u4e00\u4e2a\u77f3 \u5ba4\uff0c\u6620\u5f97\u901a\u660e\u3002 \u3000\u3000\u4f0a\u98ce\u8fd9\u624d\u604d\u7136\u4e3a\u4ec0\u4e48\u7a97\u53e3\u7684\u706f\u5149\uff0c\u4f1a\u548c\u666e\u901a\u706f\u5149\u7684\u90a3\u79cd\u660f\u9ec4\u4e4b\u8272\uff0c\u8fe5\u7136\u4e0d\u540c\u3002 \u3000\u3000\u8fd9\u4e9b\u5df2\u7ecf\u8db3\u591f\u4f0a\u98ce\u60ca\u5f02\u7684\u4e86\u3002 \u3000\u3000\u7136\u800c\u6700\u4ee4\u4f0a\u98ce\u5403\u60ca\u7684\uff0c\u5374\u662f\uff1a \u3000\u3000\u77f3\u5ba4\u4e2d\u592e\uff0c\u5bf9\u5750\u7740\u4e24\u4eba\uff0c\u671d\u4e1c\u7684\u4e00\u4eba\uff0c\u5de6\u817f\u76d8\u7740\uff0c\u53f3\u817f\u652f\u8d77\uff0c\u7a7f\u7740\u6cb9\u5149\u56db\u817b\u7684\u9e51\u8863\uff0c\u50cf \u662f\u5df2\u6709\u591a\u5e74\u672a\u66fe\u6362\u8fc7\u3002\u8d64\u7740\u53cc\u8db3\uff0c\u4e0d\u505c\u5730\u7528\u624b\u6307\u53bb\u6413\u7740\u811a\u4e2b\u91cc\u7684\u81ed\u6ce5\uff0c\u5934\u4e0a\u4e5f\u662f\u4e71\u53d1\u56db\u751f\uff0c \u987b\u9aef\u4e92\u7ed3\u3002\u53ea\u6709\u4e24\u53ea\u773c\u775b\uff0c\u5f00\u9616\u4e4b\u95f4\uff0c\u5c04\u51fa\u7cbe\u5149\u3002 \u3000\u3000\u671d\u897f\u7684\u90a3\u4eba\uff0c\u67af\u7626\u5982\u67f4\uff0c\u4e24\u816e\u5185\u9677\uff0c\u98a7\u9aa8\u9ad8\u8038\uff0c\u80e1\u987b\u867d\u8f7b\uff0c\u4f46\u4e5f\u7559\u5f97\u5f88\u957f\uff0c\u8eab\u4e0a\u7a7f\u7740\u4e00 \u4ef6\u5df2\u7ecf\u6d17\u5f97\u53d1\u767d\u7684\u84dd\u5e03\u957f\u886b\uff0c\u5782\u76ee\u76d8\u819d\uff0c\u50cf\u5c0a\u77f3\u50cf\u76d8\u5750\u7740\u3002 \u3000\u3000\u8fd9\u79cd\u8be1\u5f02\u7684\u666f\u8c61\uff0c\u81ea\u7136\u96be\u602a\u4f0a\u98ce\u5403\u60ca\u3002\u4ed6\u5077\u671b\u4e86\u4e00\u4f1a\uff0c\u7b2c\u4e00\u4e2a\u5f97\u5230\u7684\u6982\u5ff5\u4fbf\u662f\uff1a\u8fd9\u4e24\u4eba \u5df2\u5728\u8fd9\u77f3\u5ba4\u4e2d\u4f4f\u4e86\u5f88\u4e45\u5f88\u4e45\u3002 \u3000\u3000\u5176\u6b21\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e24\u4eba\uff0c\u5fc5\u5b9a\u8eab\u6000\u7edd\u9876\u529f\u529b\u3002 \u3000\u3000\u4f46\u4ed6\u7591\u60d1\u7684\u662f\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u4f1a\u5728\u6b64\u6df1\u5c71\u77f3\u5ba4\u4e2d\u9759\u5750\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5f88\u6e05\u695a\u5730\u77e5\u9053\uff0c\u81ea\u5df1\u8fd9\u95ee\u9898\u5f88\u96be\u5f97\u5230\u7b54\u6848\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a \u3000\u3000\u201c\u6700\u597d\u6211\u80fd\u591f\u5077\u5077\u6e9c\u8fdb\u90a3\u6d1e\u7a74\u91cc\uff0c\u800c\u4e0d\u8ba9\u4ed6\u4eec\u77e5\u9053\uff0c\u518d\u5077\u5077\u6e9c\u51fa\u53bb\u3002\u201d \u3000\u3000\u5fc3\u91cc\u867d\u662f\u5982\u6b64\u60f3\uff0c\u5176\u5b9e\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u8fd9\u79cd\u60f3\u6cd5\u7684\u8352\u8c2c\u548c\u4e0d\u53ef\u80fd\uff0c\u4eba\u5bb6\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u4f1a\u5168 \u662f\u804b\u5b50\u7684\u5427\uff01 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7740\u6025\uff0c\u5374\u53c8\u65e0\u8ba1\u53ef\u65bd\u3002 \u3000\u3000\u76ee\u5149\u518d\u5411\u91cc\u671b\uff0c\u53c8\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c \u3000\u3000\u539f\u6765\u90a3\u866c\u987b\u5927\u6c49\u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u7b11\u4e86\u4e24\u58f0\uff0c\u58f0\u97f3\u76f4\u53ef\u7a7f\u91d1\u88c2\u77f3\uff0c\u9707\u5f97\u4f0a\u98ce\u7684\u8033\u6735\u55e1 \u55e1\u4f5c\u54cd\u3002\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0a\u534a\u8eab\u5411\u5de6\u4e00\u65cb\uff0c\u4f60\u4e0a\u6708\u90a3\u62db\u7684\u53f3\u624b\u4fbf\u521a\u597d\u8d34\u7740\u6211\u7684\u5de6\u4fa7\u64e6\u8fc7\uff0c\u4e0b\u534a\u8eab\u5411\u53f3\u65cb\uff0c\u662f \u8eb2\u5f00\u4f60\u659c\u51fb\u800c\u4e0b\u7684\u5de6\u624b\uff0c\u6211\u518d\u7528\u5de6\u624b\u56de\u52fe\uff0c\u6765\u70b9\u4f60\u53f3\u8033\u540e\u7684\u201c\u85cf\u8840\u7a74\u201d\uff0c\u53f3\u638c\u7528\u201c\u5c0f\u5929\u661f\u201d \u7684\u638c\u65b9\u5916\u51fb\uff0c\u4f60\u82e5\u5411\u5de6\u53bb\u907f\uff0c\u6211\u5de6\u624b\u6b63\u5c01\u4f4f\u4f60\u7684\u9000\u8def\uff0c\u4f60\u82e5\u5411\u53f3\u53bb\u907f\uff0c\u6211\u53f3\u817f\u8fd9\u4e00\u5708\u3001\u4e00 \u52fe\uff0c\u811a\u8ddf\u6b63\u597d\u649e\u5411\u4f60\u811a\u8ddf\u7684\u201c\u767e\u6d8c\u7a74\u201d\uff0c\u4f60\u53ea\u6709\u540e\u9000\uff0c\u4f46\u90a3\u65f6\u6211\u201c\u5c0f\u5929\u661f\u201d\u7684\u638c\u529b\uff0c\u6b63\u597d\u7528 \u4e0a\u3002\u201d\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u54c8\u54c8\u5927\u7b11\u4e86\u51e0\u58f0\uff0c\u53c8\u63a5\u53e3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u82e5\u975e\u6211\u4e60\u5f97\u201c\u62c6\u9aa8\u9501\u9aa8\u201d\u4e4b\u672f\uff0c\u6211\u5c31\u8981\u683d\u5728\u4f60\u4e0a\u6708\u90a3\u62db\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u7a97\u5916\u7684\u4f0a\u98ce\uff0c\u542c\u5f97\u51b7\u6c57\u6d94\u6d94\u800c\u843d\uff0c\u8fd9\u4e2a\u866c\u987b\u5927\u6c49\u7684\u6b66\u529f\u62db\u5f0f\uff0c\u7b80\u76f4\u7cbe\u5999\u5f97\u9a87\u4eba\u542c\u95fb\uff01 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6570\u8f6c\uff0c\u6697\u81ea\u601d\u5fd6\u9053\uff1a \u3000\u3000\u201c\u82e5\u6709\u4eba\u5bf9\u6211\u53d1\u51fa\u6b64\u62db\uff0c\u800c\u624b\u6cd5\u548c\u8fd9\u866c\u987b\u5927\u6c49\u4e00\u6837\u5feb\u7684\u8bdd\uff0c\u90a3\u6211\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u95ed\u76ee\u518d\u671d\u91cc\u671b\uff0c\u90a3\u67af\u7684\u7626\u7684\u8001\u8005\uff0c\u4ecd\u50cf\u8001\u50e7\u5165\u5b9a\u822c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5750\u5728\u90a3\u91cc\uff0c\u751f\u50cf\u662f\u6beb\u65e0\u6240 \u52a8\u7684\u6837\u5b50\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u5341\u4e5d\u7ae0\u3000\u5357\u5077\u5317\u76d7>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u5341\u4e5d\u7ae0\u3000\u5357\u5077\u5317\u76d7 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u4ef0\u5929\u800c\u7b11\u4e86\u4e00\u9635\uff0c\u8dd1\u5230\u540e\u9762\u53d6\u4e86\u4e00\u5757\u5df2\u7ecf\u5e72\u5f97\u50cf\u77f3\u5934\u4e00\u6837\u7684\u5364\u725b\u8089\uff0c\u53c8\u5750\u5230 \u4ed6\u539f\u5148\u7684\u90a3\u5757\u84b2\u56e2\u4e0a\uff0c\u5403\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u5fc3\u4e2d\u5df2\u6a21\u7cca\u5730\u6709\u4e86\u4e2a\u6982\u5ff5\uff0c\u5fc3\u4e2d\u6697\u6697\u731c\u6d4b\u7740 \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u5fc5\u5b9a\u662f\u5728\u8f83\u91cf\u7740\u6b66\u529f.\u201d \u3000\u3000\u4f46\u662f\u7591\u95ee\u53c8\u968f\u5373\u800c\u6765 \u3000\u3000\u201c\u4ed6\u4e24\u4eba\u8f83\u91cf\u6b66\u529f\uff0c\u4e3a\u4f55\u9009\u4e86\u8fd9\u79cd\u6240\u5728\uff1f\u800c\u4e14\u7167\u8fd9\u79cd\u6e05\u51b5\u770b\u6765\uff0c\u4ed6\u4e24\u4eba\u5728\u6b64\u5df2\u4e0d\u6b62\u4e00 \u5e74\uff0c\u96be\u9053\u4ed6\u4eec\u4e00\u76f4\u5728\u8fd9\u91cc\u8f83\u6280\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u6b63\u5728\u52a8\u5ff5\uff0c\u5374\u89c1\u90a3\u866c\u987b\u5927\u6c49\u53c8\u8df3\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8352\u5c71\u4e4b\u4e2d\uff0c\u4e5f\u6709\u5ba2\u6765\u3002\u7a97\u5916\u7684\u670b\u53cb\uff0c\u5feb\u8bf7\u8fdb\u6765\uff01\u201d \u3000\u3000\u7b11\u58f0\u7a7f\u91d1\u88c2\u77f3\uff0c\u8bed\u58f0\u66f4\u662f\u4f5c\u91d1\u77f3\u9e23\uff0c\u9707\u5f97\u56db\u5c71\u90fd\u5f77\u4f5b\u8d77\u4e86\u56de\u58f0\u3002 \u3000\u3000\u4f0a\u98ce\u8fd9\u4e00\u60ca\uff0c\u66f4\u662f\u975e\u540c\u5c0f\u9119\uff01\u4e0d\u7981\u66f4\u60ca\u5f02\u4e8e\u8fd9\u866c\u987b\u5927\u6c49\u7684\u529f\u529b\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\u6765\uff0c\u4ed6\u600e\u4f1a\u77e5\u9053\u6709\u4eba\u54e9\uff1f\u201d \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u81ea\u5df1\u7d27\u5f20\u8fc7\u5ea6\uff0c\u7adf\u53d1\u51fa\u6c89\u91cd\u7684\u547c\u5438\u58f0\u6765\u4e86\uff0c\u8d77\u5148\u4eba\u5bb6\u6b63\u5728\u6c89\u601d\uff0c\u6240\u4ee5\u6ca1\u6709\u542c \u5230\uff1b\u6b64\u523b\u8bf4\u51fa\u89e3\u62db\uff0c\u6ce8\u610f\u529b\u624d\u53ca\u81f3\u6b64\u5904\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u53c8\u9053\uff1a \u3000\u3000\u201c\u7a97\u5916\u7684\u5ba2\u4eba\uff0c\u518d\u4e0d\u8fdb\u6765\uff0c\u4e3b\u4eba\u5c31\u8981\u4eb2\u81ea\u51fa\u7a97\u53bb\u8bf7\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5df2\u53d8\u5f97\u9887\u4e3a\u4e25\u5389\u3002 \u3000\u3000\u4f0a\u98ce\u770b\u8fc7\u4eba\u5bb6\u7684\u8eab\u624b\uff0c\u77e5\u9053\u9003\u662f\u9003\u4e0d\u6389\uff1a\u800c\u4e14\u81ea\u5df1\u4e5f\u6ca1\u6709\u9003\u7684\u5fc5\u8981\u3002\u4f55\u51b5\u7537\u5b50\u6c49\u5927\u4e08 \u592b\uff0c\u5c31\u662f\u80fd\u9003\uff0c\u4e5f\u4e0d\u53ef\u9003\u7684\u3002 \u3000\u3000\u4ed6\u80c6\u6c14\u4e00\u58ee\uff0c\u7d22\u6027\u5927\u65b9\u7684\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e3b\u4eba\u76f8\u9080\uff0c\u6562\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u76ee\u5149\u56db\u5c04\uff0c\u5374\u53d1\u73b0\u8fd9\u77f3\u5ba4\u7adf\u6709\u7a97\u65e0\u95e8\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u53c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u5f53\u5e74\u76d6\u8fd9\u623f\u5b50\u7684\u65f6\u5019\uff0c\u5fd8\u8bb0\u76d6\u95e8\uff0c\u670b\u53cb\u5c31\u4ece\u7a97\u4e2d\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u4f0a\u98ce\u542c\u4ed6\u81ea\u79f0\u201c\u8001\u592b\u201d\uff0c\u4f46\u662f\u58f0\u82e5\u6d2a\u949f\uff0c\u8eab\u5f3a\u4f53\u5065\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\uff0c\u77eb\u6377\uff0e\u7075\u6d3b\uff0c\u65e0\u53ef\u6bd4 \u62df\uff0c\u53c8\u4f55\u5c1d\u6709\u4e00\u661f\u534a\u70b9\u8001\u6001\uff1f \u3000\u3000\u4f0a\u98ce\u5728\u9ed1\u6697\u4e2d\u4e00\u8038\u80a9\u8180\uff0c\u65e0\u53ef\u5948\u4f55\u7684\u82e6\u7b11\u4e00\u58f0\uff0c\u53cc\u624b\u642d\u4e0a\u7a97\u53e3\uff0c\u5934\u5f80\u91cc\u4e00\u94bb\uff0c\u8eab\u8eaf\u5c31\u50cf \u86c7\u4e00\u6837\u7684\uff0c\u4ece\u7a97\u53e3\u6ed1\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4e00\u8fdb\u623f\uff0c\u4ed6\u5c31\u53cc\u624b\u62b1\u62f3\u3002 \u3000\u3000\u987b\u77e5\u4f0a\u98ce\u5f31\u51a0\u6e38\u4fa0\uff0c\u5373\u540d\u626c\u56db\u6d77\uff0c\u4e5f\u6b63\u662f\u6761\u6ca1\u5962\u906e\u7684\u597d\u6c49\uff0c\u771f\u9047\u4e0a\u4e8b\uff0c\u6001\u5ea6\u53cd\u800c\u66f4\u4e3a\u4ece \u5bb9\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u4ed6\u957f\u8eab\u7389\u7acb\uff0c\u9762\u76ee\u82f1\u4fca\uff0c\u52a8\u4f5c\u4e4b\u95f4\uff0c\u81ea\u7136\u6d41\u9732\u51fa\u4e00\u79cd\u6f47\u6d12\uff0c\u98d8\u9038\u4e4b\u6001\u3002 \u3000\u3000\u53cc\u624b\u62b1\u62f3\u4e00\u62f1\uff0c\u53e3\u4e2d\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u65e0\u77e5\uff0c\u6597\u80c6\u95ef\u5165\u524d\u8f88\u5c45\u5904\uff0c\u8fdc\u671b\u524d\u8f88\u6055\u7f6a\u5219\u4e2a\uff01\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u4e0a\u4e0b\u6253\u91cf\u4e86\u4ed6\u51e0\u773c\uff0c\u7a81\u53c8\u8fde\u58f0\u54c8\u54c8\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u8352\u5c71\u6765\u5ba2\uff0c\u5df2\u662f\u5f02\u6570\uff0c\u800c\u6765\u5ba2\u5374\u53c8\u662f\u8fd9\u7b49\u4fca\u54c1\u4eba\u7269\uff0c\u771f\u6559\u8001\u592b\u559c\u4e0d\u81ea\u80dc\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8f6c\u5934\u53c8\u5411\u90a3\u59cb\u7ec8\u52a8\u4e5f\u4e0d\u52a8\u7684\u7626\u8001\u8005\u9053\uff1a \u3000\u3000\u201c\u5b64\u8001\u5934\uff01\u4f60\u5148\u522b\u52a8\u8111\u7b4b\uff0c\u770b\u770b\u6211\u4eec\u8fd9\u4f4d\u6f02\u4eae\u7684\u5ba2\u4eba\uff01\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u4e00\u8f6c\uff0c\u89c1\u90a3\u67af\u7626\u8001\u4eba\uff0c\u500f\u5730\u7741\u5f00\u773c\u6765\uff0c\u7adf\u4f3c\u7535\u5149\u4e00\u95ea\uff0c\u7981\u4e0d\u4f4f\u6084\u6084\u79fb\u5f00\u76ee\u5149\uff0c \u4e0d\u6562\u548c\u4eba\u5bb6\u90a3\u5229\u5203\u822c\u7684\u76ee\u5149\u63a5\u89e6\u3002 \u3000\u3000\u90a3\u67af\u7626\u8001\u4eba\u9762\u76ee\u6beb\u65e0\u8868\u60c5\uff0c\u4e5f\u6253\u91cf\u4e86\u4ed6\u51e0\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b69\u5b50\uff01\u8dd1\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff01\u201d \u3000\u3000\u968f\u5373\u53c8\u95ed\u4e0a\u773c\u775b\uff0c\u8001\u50e7\u4eba\u5b9a\u822c\u5730\u5750\u7740\uff0c\u5f77\u4f5b\u5bf9\u4e16\u95f4\u7684\u4e00\u5207\u4e8b\uff0c\u90fd\u6f20\u4e0d\u5173\u5fc3\u4f3c\u7684\u3002 \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u6709\u4e9b\u4e0d\u60a6\uff0c\u6697\u5fd6\uff1a \u3000\u3000\u201c\u8fd9\u8001\u5934\u5b50\u600e\u7684\u5982\u6b64\u6ca1\u6709\u4eba\u6027\uff1f\u201d \u3000\u3000\u4e8e\u662f\u6697\u4e2d\u5bf9\u8fd9\u866c\u987b\u5927\u6c49\u8d77\u4e86\u597d\u611f\uff0c\u53c8\u671d\u90a3\u5927\u6c49\u62b1\u62f3\u4e00\u6302\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u60ca\u6270\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u6e05\u4fee\uff0c\u6df1\u611f\u4e0d\u5b89\uff01\u53ea\u662f\u5c0f\u9119\u2026\u2026\u2026\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u4e00\u6446\u624b\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u53c8\u54c8\u54c8\u7b11\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff01\u4e0d\u5fc5\u5ba2\u6c14\uff01\u8001\u592b\u548c\u8fd9\u8001\u5934\u5b50\u5728\u8fd9\u91cc\u6253\u4e86\u5c06\u8fd1\u5341\u5e74\u7684\u67b6\uff0c\u5929\u5929\u770b\u7740\u8fd9\u8001\u5934\u5b50 \u7684\u9762\u76ee\uff0c\u5fc3\u91cc\u60f9\u5f97\u8d77\u817b\u3002\u5982\u4eca\u4f60\u8fd9\u6f02\u4eae\u5c0f\u4f19\u5b50\u6765\u4e86\uff0c\u6b63\u597d\u966a\u8001\u592b\u6211\u8c08\u8c08\uff0c\u8001\u592b\u5b9e\u5728\u9ad8\u5174\u5f97 \u5f88\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u201c\u8fd9\u4e24\u4eba\u5df2\u5728\u6b64\u8f83\u6280\u5341\u5e74\u4e86\u3002\u201d\u4ed6\u60ca\u5f02\u5730\u6697\u5fd6\u7740\u3002\u4e0d\u77e5\u9053\u8fd9\u4e24\u4eba\u662f \u4ec0\u4e48\u4e1c\u897f\u652f\u6301\u7740\u4ed6\u4eec\u5982\u6b64\u7684\uff1f \u3000\u3000\u4ed6\u671b\u7740\u8fd9\u5927\u6c49\u7684\u9e51\u8863\u6c61\u9762\uff0c\u5fc3\u4e2d\u60f3\u5230\u8fd9\u6df1\u5c71\u4e2d\u7684\u5341\u5e74\u5c81\u6708\uff0c\u4f1a\u662f\u5982\u4f55\u7684\u5bc2\u5bde\uff1f\u4ed6\u66f4\u4e0d\u77e5 \u9053\uff0c\u8fd9\u4e24\u4eba\u5982\u4f55\u5fcd\u53d7\u4e86\u8fc7\u6765\uff1f \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u88ab\u90a3\u4e9b\u73e0\u5b9d\u5149\u8292\u6620\u5f97\u8000\u76ee\u751f\u82b1\u3002\u5fc3\u4e2d\u5bf9\u8fd9\u4e24\u4eba\u7684\u6765\u5386\uff0c\u66f4\u662f\u5927\u60d1\uff01 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u4e3e\u638c\u4e00\u5207\uff0c\u4ed6\u624b\u4e2d\u90a3\u5757\u5e72\u786c\u5982\u77f3\u7684\u725b\u8089\uff0c\u7adf\u50cf\u8c46\u8150\u822c\u5730\u88ab\u4e00\u5207\u4e3a\u4e8c\u3002\u4ed6\u5c06\u4e00 \u5757\u9012\u7ed9\u4f0a\u98ce\uff0c\u53c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff0c\u5148\u5403\u4e9b\u725b\u8089\uff0c\u6b47\u606f\u6b47\u606f\uff0c\u8ba9\u90a3\u8001\u5bb6\u4f19\u53bb\u52a8\u8111\u7b4b\u53bb\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u7b11\uff0c\u63a5\u8fc7\u725b\u8089\uff0c\u5374\u4ece\u80cc\u540e\u89e3\u4e0b\u884c\u56ca\uff0c\u90a3\u91cc\u9762\u8fd8\u6709\u4eca\u5929\u65e9\u4e0a\u624d\u4e70\u6765\u7684\u98ce\u9e21\u8089\u812f\uff0c\u8fd8 \u6709\u4e00\u5c0f\u74f6\u4ed6\u5907\u6765\u5fa1\u5bd2\u7684\u70e7\u9152\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u4e00\u89c1\u4e86\u8fd9\u4e9b\uff0c\u53c8\u54c8\u54c8\u5927\u7b11\u4e86\u8d77\u6765\u3002\u4f0a\u98ce\u8fde\u5fd9\u5c06\u8fd9\u4e9b\u4e1c\u897f\u9012\u8fc7\u53bb\uff0c\u90a3\u5927\u6c49\u4e5f\u8001 \u5b9e\u4e0d\u5ba2\u6c14\u7684\u5403\u4e86\u8d77\u6765\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8fd9\u4e9b\u4e1c\u897f\u5c31\u88ab\u4e00\u626b\u800c\u7a7a\uff1b\u90a3\u4e00\u5c0f\u74f6\u9152\uff0c\u4e5f\u662f\u6d93\u6ef4\u4e0d\u5269\u4e86\u3002 \u3000\u3000\u90a3\u67af\u7626\u8001\u8005\u5374\u59cb\u7ec8\u6709\u5982\u4e0d\u95fb\u4e0d\u89c1\uff0c\u77f3\u50cf\u822c\u5730\u76d8\u819d\u5782\u76ee\u5750\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u77e5\u9053\u4ed6\u6b63\u4ee5\u81ea\u5df1\u6570\u5341\u5e74\u7684\u4fee\u4e3a\u529f\u529b\uff0c\u82e6\u601d\u65b9\u624d\u8fd9\u866c\u987b\u5927\u6c49\u6240\u8bf4\u90a3\u4e00\u62db\u7684\u7834\u89e3\u4e4b\u6cd5\u3002 \u3000\u3000\u518d\u770b\u5230\u8fd9\u866c\u987b\u5927\u6c49\u7684\u653e\u6000\u5403\u559d\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a \u3000\u3000\u201c\u65b9\u624d\u8fd9\u6c49\u5b50\u8bf4\u7684\u90a3\u62db\uff0c\u662f\u4e3a\u4e86\u7834\u89e3\u8fd9\u7626\u8001\u4eba\u4e0a\u6708\u6240\u521b\u7684\u4e00\u62db\uff0c\u90a3\u4e48\u5c82\u4e0d\u662f\u8fd9\u5927\u6c49\u7adf\u60f3 \u4e86\u4e00\u4e2a\u6708\uff0c\u624d\u60f3\u51fa\u4e00\u62db\u7684\u7834\u89e3\u4e4b\u6cd5\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u53c8\u5413\u7136\u3002 \u3000\u3000\u4ed6\u8fd8\u4e0d\u77e5\u9053\uff0c\u8fd9\u4e24\u4eba\u6709\u65f6\u4f1a\u5316\u66f4\u591a\u7684\u65f6\u95f4\uff0c\u53bb\u601d\u7d22\u4e00\u62db\u54e9\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u6240\u5b66\u5230\u7684\u62db\u5f0f\uff0c\u90fd\u5df2\u7528\u5c3d\uff0c\u800c\u6b64\u523b\u4ed6\u4eec\u6240\u7528\u7684\u62db\u5f0f\uff0c\u5374\u662f\u4ed6\u4eec\u4ee5\u81ea\u8eab\u7684\u529f\u529b\u548c \u8111\u529b\uff0c\u518d\u52a0\u4e0a\u65e0\u6570\u6b21\u7684\u5bf9\u654c\u7ecf\u9a8c\uff0c\u7ecf\u8fc7\u82e6\u601d\u800c\u81ea\u521b\u51fa\u6765\u7684\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u98ce\u5377\u6b8b\u4e91\u822c\u5403\u559d\u5b8c\u4e86\uff0c\u624d\u629a\u7740\u809a\u5b50\u6717\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u5df4\u5df4\u5730\u8dd1\u5230\u8fd9\u4e48\u9ad8\u7684\u5c71\u4e0a\u6765\uff0c\u662f\u4e3a\u7740\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u7acb\u523b\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u751f\u5e73\u6700\u7231\u767b\u5c71\uff0c\u662f\u4ee5\u624d\u66fe\u7531\u6c5f\u5357\u800c\u81f3\u6ec7\u4e2d\uff0c\u4e3a\u7684\u5c31\u662f\u4e45\u95fb\u6b64\u95f4\u540d\u5c71\uff0c\u60f3\u5230\u6b64\u95f4\u6765 \u4e00\u4e00\u767b\u4e34\u7684\u54e9\u3002\u201d \u3000\u3000\u4ed6\u65e9\u5c31\u60f3\u5230\u4eba\u5bb6\u4f1a\u6709\u6b64\u95ee\uff0c\u662f\u4ee5\u65e9\u5c31\u60f3\u597d\u8bf4\u8bcd\uff0c\u6b64\u523b\u624d\u80fd\u6beb\u65e0\u72b9\u7591\u5730\u56de\u7b54\u51fa\u6765\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u8fd9\u756a\u8bf4\u8bcd\uff0c\u9020\u7684\u5e76\u4e0d\u751a\u9ad8\u660e\u800c\u5df2\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u5374\u50cf\u5df2\u76f8\u4fe1\u4e86\uff0c\u8fde\u8fde\u70b9\u5934\u9053\uff1a \u3000\u3000\u201c\u767b\u5c71\u6700\u597d\uff0c\u767b\u5c71\u6700\u597d\uff0c\u5bf9\u4e8e\u8eab\u4f53\uff0c\u662f\u5f88\u6709\u76ca\u5904\u7684\u3002\u201d \u3000\u3000\u8bf4\u7f62\u53c8\u8fde\u58f0\u5927\u7b11\u3002\u4f4e\u5934\u5bfb\u627e\u7740\u5730\u4e0a\u6389\u4e0b\u7684\u9e21\u5c51\u8089\u78b4\uff0c\u6361\u8d77\u6765\u5f80\u5634\u91cc\u9001\u3002 \u3000\u3000\u4f0a\u98ce\u770b\u7740\u4ed6\u7684\u998b\u76f8\uff0c\u6697\u6697\u89c9\u5f97\u597d\u7b11\uff0c\u5374\u4e0d\u6562\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u7a81\u7136\u62ac\u5934\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u95ee\uff0c\u6211\u4eec\u8fd9\u4e24\u4e2a\u8001\u602a\u7269\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5728\u8fd9\u5c71\u4e0a\u6253\u4e86\u5341\u5e74\u7684\u67b6\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u8fde\u5fd9\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u5b9e\u6709\u6b64\u60f3\uff0c\u53ea\u662f\u4e0d\u6562\u5f00\u53e3\u800c\u5df2\u3002\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u53c8\u7b11\u9053\uff1a\u201c\u544a\u8bc9\u4f60\u4e5f\u65e0\u59a8\uff0c\u53cd\u6b63\u2014\u2014\u201d \u3000\u3000\u4ed6\u5374\u53c8\u7a81\u7136\u4e00\u987f\uff0c\u624d\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff01\u4f60\u53ef\u66fe\u542c\u5230\u8fc7\u4e09\u5341\u5e74\u524d\uff0c\u6c5f\u6e56\u4e0a\u6709\u4e24\u4e2a\u89c1\u94b1\u773c\u5f00\u7684\u89d2\u8272\uff01\u4ed6\u4e24\u4eba\uff0c\u4e00\u4e2a\u5077\uff0c \u4e00\u4e2a\u62a2\uff0c\u7528\u7684\u65b9\u6cd5\u867d\u7136\u4e0d\u540c\uff0c\u8def\u9053\u5374\u4e00\u6837\u3002\u65e0\u8bba\u9ed1\u9053\uff0c\u767d\u9053\uff0c\u4ed6\u4e24\u4eba\u90fd\u89c1\u94b1\u5c31\u62ff\uff0c\u516d\u4eb2\u4e0d \u8ba4\uff0c\u53ea\u662fX\u54c8\uff01\u6b66\u6797\u4e2d\u7684\u90a3\u4e9b\u996d\u6876\uff0c\u4e5f\u5948\u4f55\u4ed6\u4eec\u4e0d\u5f97\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u524d\u8f88\u6240\u8bf4\u7684\uff0c\u53ef\u5c31\u662f\u4e09\u5341\u5e74\u524d\u540d\u58f0\u9707\u52a8\u6c5f\u6e56\u7684\u201c\u5357\u5077\u5317\u76d7\u201d\uff0c\u5343\u91cc\u8ffd\u98ce\u795e\u884c\u65e0\u5f71\u5999\u624b \u8bb8\u767d\uff0c\u548c\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\uff0c\u4e24\u4f4d\u524d\u8f88\u5417\uff01\u53ea\u662f\u540e\u6765\u8fd9\u4e24\u4f4d\u524d\u8f88\uff0c\u4e0d\u77e5\u4ec0\u4e48\u539f\u56e0\uff0c\u4e00\u9f50\u5931\u8e2a \u4e86\u3002\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5bf9\u4e86\uff01\u201c\u5357\u5077\u5317\u76d7\u201d\uff0c\u5c31\u662f\u6211\u548c\u8fd9\u7626\u8001\u5934\u5b50\u3002\u6211\u4eec\u4e00\u4e2a\u5728\u5357\uff0c\u4e00\u4e2a\u5728\u5317\uff0c\u4e00\u4e2a\u5077\uff0c\u4e00 \u4e2a\u62a2\uff0c\u672c\u6765\u53ef\u8bf4\u662f\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u90a3\u77e5\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u7269\uff0c\u53c8\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5374\u4e3a\u4e86\u8fd9\u4ef6\u4e1c\u897f\uff0c\u6211\u4eec\u4e24\u4e2a\u5374\u78b0\u5230\u4e00\u8d77\uff1b\u4e0d\u4f46\u78b0\u5230\u4e00\u8d77\uff0c\u8fd8\u6253\u4e86\u8d77\u6765\uff1b\u4e0d\u4f46\u6253\u4e86\u8d77 \u6765\uff0c\u8fd9\u4e00\u6253\u7adf\u6253\u4e86\u5c06\u8fd1\u5341\u5e74\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5b9a\u6674\u671b\u53bb\uff0c\u5374\u89c1\u4ed6\u624b\u4e2d\u6240\u6301\u7684\uff0c\u53ea\u662f\u4e00\u5757\u4e00\u5c3a\u89c1\u65b9\u7684\u94c1\u5757\uff0c\u867d\u7136\u8fd9\u94c1\u5757\u91cc\u6709\u597d\u4e9b\u7487 \u5149\u6697\u8f6c\uff0c\u4f46\u4ed6\u5374\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u597d\u5904\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u5947\u602a\uff1a \u3000\u3000\u201c\u6309\u7406\u8bf4\uff01\u201c\u5357\u5077\u5317\u76d7\u201d\uff0c\u6210\u540d\u591a\u5e74\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u89c1\u8fc7\u7684\u5b9d\u7269\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u5374\u600e\u66fe\u4e3a \u4e86\u8fd9\u4e48\u5757\u9ed1\u9edd\u9edd\u7684\u94c1\u5757\uff0c\u95f9\u5f97\u5982\u6b64\u5730\u6b65.\uff1f\u201d \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u7ae0\u3000\u7487\u5149\u5b9d\u4eea>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u7ae0\u3000\u7487\u5149\u5b9d\u4eea \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u628a\u5f04\u4e86\u534a\u664c\uff0c\u624d\u62ac\u8d77\u5934\u6765\u671b\u7740\u4f0a\u98ce\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e1c\u897f\u53eb\u505a\u201c\u7487\u5149\u4eea\u201d\uff0c\u4f60\u83ab\u770b\u5b83\u4e0d\u8d77\u773c\uff0c\u53ef\u662f\u8fd9\u4e1c\u897f\u771f\u6b63\u7684\u597d\u5904\uff0c\u5374\u8bf4\u4e5f\u8bf4\u4e0d \u5c3d\uff01\u201d \u3000\u3000\u4ed6\u54bd\u4e86\u53e3\u553e\u6cab\uff0c\u53c8\u63a5\u7740\u8bf4\u9053 \u3000\u3000\u201c\u5b83\u4e0d\u4f46\u80fd\u9884\u6d4b\u7b2c\u4e8c\u5929\u7684\u6674\u9634\uff0c\u53c8\u80fd\u89e3\u6bd2\uff0c\u8fd8\u80fd\u907f\u86c7\uff0c\u866b\u4e00\u7c7b\u7684\u4e1c\u897f\u3002\u8fd9\u4e9b\u90fd\u4e0d\u8bf4\uff0c\u6700 \u5947\u7684\u662f\u5b83\u7adf\u80fd\u6d4b\u51fa\u90a3\u91cc\u6709\u5b9d\u7269\uff0c\u4e0d\u7ba1\u662f\u4eba\u7684\u8eab\u4e0a\uff0e\u623f\u5b50\u91cc\uff0c\u751a\u81f3\u662f\u57cb\u5728\u5730\u4e0b\u7684\u73cd\u5b9d\uff0c\u8fd9\u4e1c\u897f \u90fd\u53ef\u6d4b\u51fa\u6765\u3002\u54c8\uff0c\u8fd9\u624d\u53eb\u7cbe\u91c7\u5462\uff01\u201d \u3000\u3000\u4ed6\u4e00\u62cd\u5927\u817f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ef\u60dc\u7684\u662f\u2014\u2014\u8fd9\u4e1c\u897f\u6211\u53ea\u6709\u4e00\u534a\u3002\u4e8e\u662f\u6211\u5c31\u5343\u65b9\u767e\u8ba1\u5730\u53bb\u627e\u53e6\u4e00\u534a\uff0c\u627e\u6765\u627e\u53bb\uff0c\u624d\u77e5 \u9053\u8fd9\u4e1c\u897f\u7684\u53e6\u4e00\u534a\uff0c\u5374\u5728\u8fd9\u7626\u8001\u5934\u5b50\u8eab\u4e0a\uff01\u800c\u8fd9\u7626\u8001\u5934\u5b50\uff0c\u4e5f\u6b63\u5728\u5343\u65b9\u767e\u8ba1\u7684\u60f3\u627e\u7740\u5728\u6211\u624b \u91cc\u7684\u8fd9\u4e00\u534a\u3002\u201d \u3000\u3000\u4f0a\u98ce\u542c\u5f97\u51fa\u795e\uff0c\u4ed6\u81ea\u5c0f\u5230\u5927\uff0c\u8fd8\u771f\u6ca1\u6709\u542c\u8bf4\u8fc7\u4e16\u95f4\u6709\u8fd9\u79cd\u5e0c\u5947\u7684\u7269\u4e8b\uff0c\u4e0d\u7981\u66f4\u4ed4\u7ec6\u5730\u53bb \u671b\u90a3\u201c\u7487\u5149\u4eea\u201d\uff0c\u60f3\u770b\u770b\u8fd9\u4e1c\u897f\u5012\u5e95\u6709\u4f55\u5f02\u5904\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u53c8\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u4e24\u4eba\u8fd9\u4e00\u78b0\u9762\uff0c\u624d\u77e5\u9053\u81ea\u5df1\u8981\u620a\u7684\u4e1c\u897f\uff1a\u5c31\u5728\u5bf9\u65b9\u8eab\u4e0a\u3002\u6211\u4eec\u4e24\u4eba\u5fc3\u4e2d\u5c31\u5168\u6709 \u6570\uff0c\u77e5\u9053\u8981\u5f97\u5230\u5bf9\u65b9\u7684\u4e1c\u897f\uff0c\u53ef\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff01 \u3000\u3000\u4e8e\u662f\u6211\u4eec\u5c31\u7ea6\u8ba2\u597d\u4e86\u65f6\u95f4\uff0e\u5730\u70b9\uff0c\u4f5c\u4e00\u62da\u6597\u3002\u8c01\u8981\u662f\u8d62\u4e86\uff0c\u4e0d\u4f46\u80fd\u5f97\u5230\u8fd9\u201c\u7487\u5149\u4eea\u201d\uff01 \u3000\u3000\u4ed6\u4e00\u6307\u623f\u4e2d\u90a3\u4e0d\u8ba1\u5176\u6570\uff0c\u65e0\u6cd5\u4f30\u8ba1\u7684\u73e0\u5b9d\uff0c\u53c8\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u800c\u4e14\u8fd8\u53ef\u4ee5\u5f97\u5230\u5bf9\u65b9\u5386\u5e74\u6765\u7684\u79ef\u84c4\u3002\u504c\uff01\u5df4\u662f\u8fd9\u4e9b\u73a9\u610f\u3002\u201d \u3000\u3000\u4f0a\u98ce\u604d\u7136\u800c\u609f\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u5728\u8fd9\u79cd\u8352\u5c71\u4e4b\u4e2d\uff0c\u5fcd\u53d7\u5341\u5e74\u7684\u75db\u82e6\u548c\u5bc2\u5bde\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u53c8\u4e0d\u7981\u95ee\u7740\u81ea\u5df1\uff1a \u3000\u3000\u201c\u5316\u4e86\u5341\u5e74\u7684\u5149\u9634\uff0c\u800c\u4ec5\u662f\u4e3a\u7740\u8fd9\u4e9b\u8eab\u5916\u4e4b\u7269\uff0c\u53ef\u7b97\u503c\u5f97\u5417\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7981\u6697\u6697\u6447\u5934\uff0c\u4e3a\u7740\u8fd9\u4e24\u4f4d\u6b66\u6797\u524d\u8f88\u6240\u6d6a\u8d39\u7684\u5341\u8f7d\u65f6\u5149\u800c\u60cb\u60dc\uff01 \u3000\u3000\u866c\u987b\u5927\u6c49\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u6240\u7ea6\u6bd4\u6597\u4e4b\u5904\uff0c\u672c\u662f\u5728\u8fd9\u65e0\u91cf\u5c71\u4e0b\uff0c\u5230\u65f6\u53cc\u65b9\u679c\u7136\u90fd\u5982\u7ea6\u800c\u81f3\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u5728\u5c71\u4e0b\u8fde\u7eed\u6597\u4e86\u4e03\u5929\u4e03\u591c\uff0c\u6211\u548c\u8fd9\u7626\u8001\u5934\u5b50\u867d\u7136\u6240\u5b66\u7684\u529f\u592b\u5b8c\u5168\u4e0d\u540c\uff0c\u4f46\u529f \u529b\u6df1\u6d45\u5374\u5b8c\u5168\u4e00\u6837\u3002\u6253\u4e86\u4e03\u5929\u4e03\u591c\uff0c\u7adf\u4e5f\u6ca1\u6709\u6253\u51fa\u4e00\u70b9\u70b9\u7ed3\u679c\u6765\uff0c\u4ecd\u7136\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6697\u5fd6\uff1a \u3000\u3000\u201c\u4f60\u4eec\u4e00\u4e2a\u5077\uff0c\u4e00\u4e2a\u62a2\uff0c\u6240\u5b66\u7684\u529f\u592b\uff0c\u81ea\u7136\u5b8c\u5168\u4e0d\u76f8\u540c\u4e86\u3002\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u5374\u53c8\u4e0d\u7518\u5c31\u6b64\u5584\u7f62\u7518\u4f11\uff0c\u56e0\u4e3a\u90a3\u4e48\u4e00\u6765\uff0c\u6211\u4eec\u6c38\u8fdc\u5c31\u53ea\u80fd\u62ff\u7740\u534a\u4e2a\u7487\u5149\u4eea\uff0c \u90a3\u5c31\u5b8c\u5168\u7b49\u4e8e\u5e9f\u7269\u4e00\u6837\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6697\u6697\u53f9\u606f\uff1a \u3000\u3000\u201c\u4eba\u7c7b\u771f\u662f\u5947\u602a\uff1a\u4ed6\u4eec\u4e0d\u613f\u5f7c\u6b64\u5408\u4f5c\uff0c\u5374\u60c5\u613f\u6d6a\u8d39\u5341\u5e74\u4e00\u53bb\u4e0d\u8fd4\u7684\u65f6\u5149\uff0c\u6765\u4e3a\u7740\u4e00\u5757\u987d \u94c1\u62da\u6597\uff0c\u8fd9\u4e5f\u7b97\u4eba\u7c7b\u7684\u667a\u6167\u5417\uff1f\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u4f0a\u98ce\u5fc3\u4e2d\u7684\u60f3\u6cd5\uff0c\u5fae\u4e00\u505c\u987f\u540e\uff0c\u5446\u9053\uff1a \u3000\u3000\u201c\u4e8e\u662f\u6211\u4eec\u5c31\u5728\u8fd9\u5c71\u5dc5\u4e4b\u5904\uff0c\u5bfb\u5f97\u8fd9\u6240\u5728\uff0c\u642d\u8d77\u77f3\u5c4b\uff0c\u5c31\u5728\u8fd9\u77f3\u5c4b\u91cc\u5404\u81ea\u7814\u8ba8\uff0c\u60f3\u521b\u51fa \u4e00\u62db\u4f7f\u5bf9\u65b9\u65e0\u6cd5\u62db\u67b6\u7684\u7edd\u62db\u6765\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u6697\u9a82\uff1a \u3000\u3000\u201c\u4f60\u4eec\u4ec0\u4e48\u5730\u65b9\u4e0d\u597d\u9009\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u9009\u4e2d\u8fd9\u5730\u65b9\uff01\u201d \u3000\u3000\u53e3\u4e2d\u5374\u63a5\u53e3\u95ee\u9053\uff1a \u3000\u3000\u201c\u8981\u662f\u6709\u4eba\u4e00\u60f3\u5341\u5e74\uff0c\u90a3\u5bf9\u65b9\u4e0d\u662f\u5c31\u8981\u7b49\u4e0a\u4e2a\u5341\u5e74\u5417\uff01\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5f53\u7136\u6709\u4e2a\u671f\u9650\uff0c\u6211\u4eec\u4ee5\u56db\u5341\u5929\u4e3a\u671f\uff0c\u56db\u5341\u5929\u4e2d\uff0c\u82e5\u8fd8\u4e0d\u80fd\u60f3\u51fa\u4e00\u62db\u5316\u89e3\u5bf9\u65b9\u62db\u5f0f\u7684 \u7740\u6570\uff0c\u90a3\u4e48\u4fbf\u7b97\u8f93\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u4e00\u505c\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u5341\u5e74\u6765\uff0c\u5f7c\u6b64\u5374\u90fd\u672a\u8d25\u3002\u6709\u4e00\u6b21\uff0c\u8fc7\u4e86\u4e09\u5341\u4e5d\u5929\uff0c\u8fd9\u7626\u8001\u5934\u5b50\u8fd8\u6ca1\u6709\u60f3\u51fa\u7834\u89e3\u6211 \u4e00\u62db\u81ea\u521b\u7684\u201c\u62c2\u4e91\u624b\u201d\u7684\u62db\u6570\u6765\uff0c\u6211\u539f\u4ee5\u4e3a\u4ed6\u8f93\u5b9a\u4e86\uff0c\u90a3\u77e5\u5230\u4e86\u7b2c\u56db\u5341\u5929\u7684\u665a\u4e0a\uff0c\u8fd8\u662f\u8ba9\u4ed6 \u60f3\u51fa\u4e86\u8fd9\u4e00\u62db\u7684\u7834\u6cd5\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u4ed6\u4eec\u8fd9\u5341\u5e74\u7684\u5149\u9634\uff0c\u8fd8\u662f\u6709\u7740\u4ee3\u4ef7\u7684\u3002\u5341\u5e74\u6765\u4ed6\u4eec\u4e00\u5b9a\u521b\u51fa\u8bb8\u591a\u5999\u7edd\u4eba\u5bf0\u7684\u62db\u6570 \u6765\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u795e\u5f80\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a \u3000\u3000\u201c\u8001\u524d\u8f88\u7684\u90a3\u4e00\u62db\u201c\u62c2\u4e91\u624b\u201d\uff0c\u662f\u600e\u4e48\u6837\u7684\u4e00\u4e2a\u62db\u6570\u5462\uff1f\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u4f3c\u4e4e\u8c08\u5f97\u5174\u8d77\uff0c\u7a81\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u53cc\u624b\u7b95\u5f20\uff0c\u7531\u5185\u5411\u5916\u62c2\u51fa\uff0c\u6700\u5999\u7684\u811a\u4e0b\u5728\u8fd9 \u4e00\u62c2\u4e4b\u95f4\uff0c\u5df2\u6362\u4e86\u4e09\u4e2a\u65b9\u5411\uff0c\u800c\u4ed6\u7684\u8fd9\u4e00\u62c2\u4e4b\u52bf\uff0c\u5728\u811a\u4e0b\u7684\u8fd9\u4e00\u52a8\u4e4b\u95f4\uff0c\u4e5f\u53d8\u4e86\u56db\u4e2a\u65b9\u5411\u3002 \u3000\u3000\u4f0a\u98ce\u53ea\u89c9\u5f97\u4ed6\u8fd9\u4e00\u62db\uff0c\u638c\u5f71\u7f24\u7eb7\uff0c\u5982\u5929\u5973\u6240\u6563\u4e4b\u82b1\u96e8\uff1b\u800c\u4ed6\u90a3\u9b41\u4f1f\u5de8\u5927\u7684\u8eab\u5f62\uff0c\u5728\u4f7f\u7528 \u8fd9\u4e00\u62db\u65f6\uff0c\u7adf\u4e5f\u597d\u50cf\u6563\u82b1\u7684\u5929\u5973\u90a3\u6837\u7f8e\u5999\u3002\u4e0d\u7981\u5bf9\u8fd9\u866c\u987b\u5927\u6c49\u7684\u6b66\u529f\uff0c\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u3002 \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u8eab\u5f62\u4e00\u987f\uff0c\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u5f97\u610f\u5730\u5927\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u8fd9\u4e00\u62db\u201c\u62c2\u4e91\u624b\u201d\uff0c\u540d\u867d\u662f\u4e00\u62db\uff0c\u4f46\u4f7f\u7528\u8d77\u6765\uff0c\u5374\u6709\u5341\u4e8c\u4e2a\u9ad8\u624b\u540c\u65f6\u8fdb\u653b\u4e00\u4eba\u65f6\u7684 \u90a3\u79cd\u5a01\u529b\uff0c\u4e5f\u4e8f\u5f97\u8fd9\u7626\u8001\u5934\u5b50\uff0c\u80fd\u60f3\u5f97\u51fa\u7834\u6cd5\u6765\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5927\u6709\u5929\u4e0b\u9664\u4e86\u90a3\u7626\u8001\u5934\u4e00\u4eba \u4e4b\u5916\uff0c\u5c31\u518d\u65e0\u522b\u4eba\u80fd\u7834\u5f97\u4ed6\u8fd9\u4e00\u62db\u4e86\u3002\u5f97\u610f\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u5c31\u8fd9\u6837\u5728\u8fd9\u77f3\u5ba4\u4e2d\uff0c\u8fc7\u4e86\u5341\u5e74\u3002\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u8c01\u4e5f\u65e0\u6cd5\u9884\u6d4b\u80fd\u5b34\u5f97\u5bf9\u65b9\u3002\u5728\u82e6\u601d \u7834\u6cd5\u65f6\u8fd8\u597d\uff0c\u6700\u96be\u582a\u7684\uff0c\u5c31\u662f\u5728\u5bf9\u65b9\u6c89\u601d\u65f6\uff0c\u90a3\u79cd\u5bc2\u5bde\u7684\u611f\u89c9\uff0c\u53ef\u771f\u6559\u4eba\u96be\u4ee5\u5fcd\u53d7\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e4b\u4e2d.\u4e5f\u4e0d\u7981\u6d41\u9732\u51fa\u51c4\u51c9\u7684\u5473\u9053\u3002 \u3000\u3000\u4f0a\u98ce\u6b63\u81ea\u6697\u5730\u611f\u53f9\uff0c\u5374\u542c\u5f97\u8fd9\u866c\u987b\u5927\u6c49\u53c8\u5927\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u4ee5\u540e\u6709\u4f60\u966a\u7740\uff0c\u6211\u4eec\u8c08\u8c08\u8bf4\u8bf4\uff0c\u5bc2\u5bde\u5c31\u53ef\u4ee5\u89e3\u9664\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u60ca\uff0c\u8fde\u5fd9\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u867d\u60f3\u5728\u6b64\u5e38\u8046\u8001\u524d\u8f88\u7684\u6559\u76ca\uff0c\u53ea\u662f\u5c0f\u9119\u8fd8\u53e6\u6709\u2026\u2026\u2026\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u53cc\u76ee\u4e00\u5f20\uff0c\u76ee\u5149\u9510\u5229\u5982\u5200\u5730\u77aa\u7740\u4f0a\u98ce\uff0c\u7c97\u58f0\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u770b\u5f97\u8d77\u4f60\uff0c\u4f60\u8fd8\u6562\u4e0d\u8bc6\u62ac\u4e3e\u5417\uff1f\u96be\u9053\u4f60\u8fdb\u4e86\u8fd9\u95f4\u623f\u5b50\uff0c\u8fd8\u60f3\u4e00\u4e2a\u4eba\u5148\u8d70\u51fa \u53bb.\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u53c8\u662f\u5927\u9a87\u3002 \u3000\u3000\u5374\u542c\u8fd9\u866c\u987b\u5927\u6c49\u653e\u7f13\u4e86\u53e3\u6c14\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff01\u4f60\u4e5f\u662f\u5b66\u6b66\u4e4b\u4eba\uff0c\u5728\u8fd9\u91cc\u966a\u7740\u8001\u592b\uff0c\u7ba1\u4fdd\u6709\u4f60\u7684\u597d\u5904\uff0c\u4e0d\u4f46\u53ef\u4ee5\u5f97\u5230\u8bb8\u591a\u7cbe \u5999\u7684\u6b66\u529f\uff0c\u4e34\u8d70\u65f6\u8fd8\u53ef\u4ee5\u5f04\u4e00\u888b\u73cd\u5b9d\u56de\u53bb\u3002\u201d \u3000\u3000\u8fd9\u866c\u987b\u5927\u6c49\u6570\u5341\u5e74\u524d\u5c31\u4ee5\u751f\u6027\u4e4b\u5947\u50fb\uff0c\u4f20\u904d\u6b66\u6797\u3002\u6b64\u523b\u5b9e\u5728\u56e0\u4e3a\u8fd9\u4e48\u591a\u5e74\u6765\u96be\u582a\u7684\u5bc2 \u5bde\uff0c\u624d\u4f1a\u5bf9\u4f0a\u98ce\u8fd9\u4e48\u5ba2\u6c14\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u4e00\u79cd\u66f4\u5927\u7684\u529b\u91cf\uff0c\u5374\u4f7f\u4ed6\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u524d\u8f88\u7684\u76db\u60c5\uff0c\u5c0f\u9119\u5fc3\u9886\u2026\u2026\u2026\u201d \u3000\u3000\u90a3\u866c\u987b\u5927\u6c49\u4e00\u6446\u624b\uff0c\u62a2\u7740\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff01\u6211\u5148\u544a\u8bc9\u4f60\uff0c\u6211\u53ef\u4e0d\u59d3\u4e07\uff0c\u90a3\u7626\u8001\u5934\u5b50\u624d\u59d3\u4e07\u3002\u6211\u59d3\u8bb8\uff0c\u53eb\u8bb8\u767d\uff0c\u4f60\u542c\u6e05\u695a \u4e86\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u53c8\u4e00\u6014\u3002 \u3000\u3000\u4ed6\u53ef\u60f3\u4e0d\u5230\u8fd9\u9b41\u68a7\u7684\u5927\u6c49\uff0c\u7adf\u662f\u4ee5\u4e00\u8eab\u8f7b\u8eab\u5c0f\u5de7\u7684\u8f6f\u529f\u592b\u79f0\u8a89\u6b66\u6797\u7684\u5357\u5077\u2014\u2014\u5343\u91cc\u8ffd \u98ce\uff0c\u795e\u884c\u65e0\u5f71\uff0c\u5999\u624b\u8bb8\u767d\u3002 \u3000\u3000\u800c\u90a3\u7626\u5c0f\u965b\u5e72\u7684\u8001\u5934\u5b50\uff0c\u5374\u662f\u6614\u5e74\u4ee5\u5927\u9e70\u722a\u624b\u52a0\u6742\u7740\u5341\u4e8c\u8def\u91d1\u521a\u6454\u7891\u638c\uff0c\u4ee5\u53ca\u4e00\u8eab\u7ae5\u5b50 \u6df7\u5143\u4e00\u6c14\u529f\uff0c\u8d70\u901a\u5927\u6cb3\u5357\u5317\u7684\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u3002 \u3000\u3000\u4ed6\u671b\u7740\u8fd9\u4e24\u4eba\u7684\u8eab\u5f62\u9762\u8c8c\uff0c\u53c8\u60f3\u5230\u90a3\u4f4d\u201c\u5929\u5a9a\u201d\u6559\u4e3b\u7684\u5947\u4e11\u5987\u4eba\uff0c\u5fc3\u4e2d\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\u7684 \u611f\u89c9\uff01 \u3000\u3000\u53e3\u4e2d\u5374\u53ea\u5f97\u552f\u552f\u8bf4\u9053\uff1a \u3000\u3000\u201c\u662f\uff01\u8bb8\u8001\u524d\u8f88\u7684\u76db\u60c5\uff0c\u5c0f\u9119\u5fc3\u9886\u4e86\u3002\u7686\u56e0\u5c0f\u9119\u5b9e\u5728\u53e6\u6709\u4ed6\u4e8b\u2026\u2026\u2026\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u7a81\u7136\u655e\u58f0\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u4f0a\u98ce\u4e00\u60ca\uff0c\u81ea\u7136\u987f\u4f4f\u4e86\u8bdd\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u7b11\u58f0\u4e00\u4f4f\uff0c\u53cc\u76ee\u53c8\u7535\u4e5f\u4f3c\u7684\u5c04\u51fa\u7cbe\u5149\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u662f\u5b9e\u5728\u4e0d\u5356\u8001\u592b\u7684\u8d26\uff0c\u4e5f\u6ca1\u6709\u5173\u7cfb\uff1b\u53ea\u662f\u4f60\u5374\u8981\u8bf4\u7ed9\u8001\u592b\u542c\u542c\uff0c\u6709\u4ec0\u4e48\u4e8b\u503c\u5f97\u4f60 \u63a8\u5374\u8001\u592b\u8fd9\u79cd\u522b\u4eba\u68a6\u60f3\u4e0d\u5f97\u7684\u5947\u9047\uff01 \u3000\u3000\u201c\u82e5\u662f\u8001\u592b\u4e5f\u8ba4\u4e3a\u503c\u5f97\u7684\uff0c\u90a3\u8fd8\u7f62\u4e86\uff1a\u5982\u82e5\u4e0d\u7136\u2014\u2014\u54fc\uff01\u201d \u3000\u3000\u4f0a\u98ce\u73b0\u5728\u53ef\u53d1\u89c9\u4e86\u8fd9\u5999\u624b\u8bb8\u767d\u7684\u4e0d\u53ef\u7406\u55bb\u3002\u4e5f\u77e5\u9053\uff0c\u81ea\u5df1\u867d\u7136\u529f\u529b\u7cbe\u8fdb\uff0c\u4f46\u5012\u5e95\u4fee\u4e3a\u592a \u6d45\uff0c\u548c\u8fd9\u79cd\u9ad8\u624b\u4e00\u6bd4\uff0c\u8fd8\u5dee\u5f97\u8fdc\uff01 \u3000\u3000\u90a3\u5c31\u662f\u8bf4\uff01\u9664\u4e86\u4f9d\u7167\u4ed6\u8bf4\u7684\u8def\u8d70\u4e4b\u5916\uff0c\u522b\u65e0\u5176\u4ed6\u9009\u62e9\u7684\u4f59\u5730\uff01 \u3000\u3000\u4ed6\u56de\u5934\u4e00\u671b\uff0c\u90a3\u94c1\u9762\u5b64\u884c\u5ba2\u4ecd\u7136\u4e0d\u95fb\u4e0d\u95ee\u5730\u5446\u5750\u7740\uff0c\u751f\u50cf\u5c31\u7b97\u5929\u584c\u4e0b\u6765\uff0c\u4ed6\u4e5f\u7ba1\u4e0d\u7740\u4f3c \u7684\u3002 \u3000\u3000\u4f0a\u98ce\u957f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u600e\u7684\u8fd9\u4e24\u4eba\u7adf\u5982\u6b64\u4e0d\u901a\u60c5\u7406\uff01\u201d \u3000\u3000\u4ed6\u53ef\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u4e24\u4eba\u82e5\u975e\u751f\u6027\u5947\u50fb\u5f97\u4e0d\u8fd1\u60c5\u7406\uff0c\u53c8\u600e\u4f1a\u5728\u8fd9\u6df1\u5c71\u4e2d\u4e00\u803d\u5341\u5e74\uff1f\u4ed6\u5fc3\u5ff5 \u4e00\u52a8\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u770b\u6765\u6211\u53ea\u6709\u6682\u65f6\u5728\u8fd9\u91cc\u966a\u7740\u4ed6\u4eec\uff0c\u53cd\u6b63\u4ed6\u4eec\u603b\u6709\u4e00\u5929\uff0c\u6709\u5206\u51fa\u80dc\u8d1f\u7684\u3002\u5230\u4e86\u90a3\u4e00\u5929\uff0c \u6211\u4e00\u6837\u5730\u53ef\u4ee5\u53bb\u5bfb\u5f97\u90a3\u6b66\u66f2\u661f\u541b\u7684\u79d8\u85cf\u3002 \u3000\u3000\u201c\u5230\u4e86\u90a3\u65f6\u5019\uff0c\u6211\u8eab\u517c\u5404\u5bb6\u4e4b\u957f\uff0c\u518d\u52a0\u4e0a\u529f\u593a\u9020\u5316\u7684\u201c\u6bd2\u9f99\u4e38\u201d\uff0c\u6211\u4f55\u6101\u5927\u4ec7\u4e0d\u62a5\uff0c\u6b66 \u529f\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed6\u9ad8\u5174\u5730\u601d\u91cf\u7740\u3002 \u3000\u3000\u53ef\u662f\u5ff5\u5934\u518d\u4e00\u8f6c\u65f6\uff0c\u60f3\u5230\u7ec8\u5357\u5c71\u4e0a\u7684\u6570\u767e\u4eba\u547d\uff0c\u5374\u53c8\u9ad8\u5174\u4e0d\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u5ffd\u9752\uff0c\u5ffd\u767d\uff0c\u6b63\u662f\u4ed6\u5fc3\u4e2d\u5929\u4eba\u4ea4\u6218\u4e4b\u9645\u3002 \u3000\u3000\u987b\u77e5\u51e1\u662f\u4eba\u7c7b\uff0c\u5c31\u4e0d\u514d\u591a\u591a\u5c11\u5c11\u5730\u6709\u4e9b\u81ea\u79c1\u7684\u6b32\u5ff5\uff0c\u8fd9\u672c\u65e0\u53ef\u539a\u975e\uff1b\u53ea\u662f\u8fd9\u81ea\u79c1\u82e5\u635f\u5bb3 \u5230\u522b\u4eba\uff0c\u800c\u5c06\u522b\u4eba\u635f\u5bb3\u5f97\u5f88\u91cd\u7684\u8bdd\uff0c\u5c31\u5e94\u514b\u5236\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\uff0c\u6b63\u662f\u9677\u4e8e\u6781\u5ea6\u7684\u77db\u76fe\u4e4b\u4e2d\u3002\u4ed6\u77e5\u9053\u82e5\u4e00\u8bf4\u51fa\u6b64\u884c\u7684\u771f\u5b9e\u76ee\u7684\uff0c\u90a3\u4e48\u90a3\u672c\u6b66\u6797 \u7470\u5b9d\u201c\u5929\u661f\u79d8\u7b08\u201d\u548c\u90a3\u7c92\u529f\u80fd\u593a\u5929\u5730\u9020\u5316\u7684\u6bd2\u9f99\u4e38\uff0c\u5c31\u7edd\u5bf9\u4e0d\u4f1a\u518d\u662f\u81ea\u5df1\u4e4b\u7269\u4e86c \u3000\u3000\u800c\u4ed6\u5982\u4e0d\u8bf4\u5462\uff1f \u3000\u3000\u7ec8\u5357\u5c71\u91cc\u7684\u6570\u767e\u4e2a\u4e2d\u6bd2\u5782\u5371\u7684\u7ec8\u5357\u5f1f\u5b50\uff0c\u90fd\u5728\u7b49\u7740\u4ed6\u7684\u89e3\u836f\uff0c\u59d1\u65e0\u8bba\u4ed6\u8d76\u56de\u53bb\u65f6\u8fd8\u80fd\u6551 \u5f97\u591a\u5c11\u4eba\u7684\u6027\u547d\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4e00\u5411\u5ac9\u6076\u5982\u4ec7\uff0c\u4ee5\u4fa0\u4e49\u81ea\u8bb8\u7684\u4ed6\uff0c\u603b\u4e0d\u80fd\u89c1\u6b7b\u800c\u4e0d\u6551\u5440\uff01 \u3000\u3000\u7a97\u5916\u591c\u8272\u66f4\u6d53\u3002\u5e26\u7740\u51db\u51bd\u5bd2\u610f\u7684\u665a\u98ce\uff0c\u4ece\u7a97\u4e2d\u5c04\u5165\uff0c\u5439\u5230\u4f0a\u98ce\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u7136\u800c\u4ed6\u5374\u50cf\u662f\u6beb\u65e0\u6240\u89c9\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u8eab\u53d7\u5947\u8fb1\uff0c\u5fd7\u5207\u590d\u4ec7\uff0c\u82e5\u6b64\u523b\u8bf4\u51fa\u90a3\u79d8\u85cf\uff0c\u8fd9\u201c\u5357\u5077\u5317\u76d7\u201d\uff0c\u8fd8\u4f1a\u8ba9\u4ed6\u53d6\u51fa\u201c\u5929\u661f\u79d8 \u7b08\u201d\u548c\u201c\u6bd2\u9f99\u4e38\u201d\u5417\uff1f \u3000\u3000\u90a3\u4e48\uff0c\u4ed6\u590d\u4ec7\u7684\u5e0c\u671b\uff0c\u5c82\u975e\u53c8\u5b8c\u5168\u5f52\u4e8e\u6ce1\u5f71\uff01 \u3000\u3000\u5728\u4ed6\u60f3\u6765\uff0c\u4efb\u4f55\u4e00\u4e2a\u95ee\u9898\uff0c\u90fd\u662f\u975e\u5e38\u5bb9\u6613\u7b54\u8986\u7684\uff0c\u5c24\u5176\u662f\u6709\u5173\u4e8e\u81ea\u5df1\u5207\u8eab\u5229\u5bb3\u7684\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u53ea\u987b\u672c\u7740\u81ea\u5df1\u5229\u76ca\u8f83\u591a\u7684\u4e00\u65b9\u53bb\u505a\uff0c\u5728\u4ed6\u8ba4\u4e3a\u5c31\u662f\u6b63\u786e\u7684\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u72ec\u81ea\u9759\u5750\u5982\u6ce5\u5851\uff0c\u4e0d\u77e5\u9053\u4ed6\u662f\u5426\u542c\u5230\u4ed6\u4eec\u7684\u5bf9\u8bdd\u3002\u4f0a\u98ce\u84e6\u5730\u54ac\u7259\uff0c\u4e0b\u4e86\u4e2a\u51b3 \u5b9a\u2014\u2014 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u5404\u6000\u673a\u5fc3>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u5404\u6000\u673a\u5fc3 \u3000\u3000\u4ed6\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u524d\u8f88\u65e2\u7136\u5982\u6b64\u76f8\u903c\uff0c\u665a\u8f88\u81ea\u7136\u4e0d\u5f97\u4e0d\u8bf4\u51fa\u3002\u201d \u3000\u3000\u4ed6\u5251\u7709\u4e00\u626c\uff0c\u6b63\u6c14\u51db\u7136\uff01\u63a5\u7740\u53c8\u8bf4\u9053 \u3000\u3000\u201c\u53ea\u662f\u665a\u8f88\u5374\u4e0d\u662f\u4e3a\u4e86\u7231\u60dc\u81ea\u5df1\u7684\u65f6\u5149\uff0c\u751a\u6216\u751f\u547d\uff0c\u800c\u662f\u4e3a\u7740\u53e6\u5916\u6570\u767e\u6761\u4eba\u547d\uff0c\u4e0d\u5f97\u4e0d \u5c06\u6b64\u4e8b\u8bf4\u51fa\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u5fae\u4e00\u76b1\u7709\uff0c\u4f3c\u4e4e\u89c9\u5f97\u4e0d\u8010\u70e6\uff0c\u4e5f\u4f3c\u4e4e\u5bf9\u4f0a\u98ce\u7684\u8bdd\uff0c\u9887\u4e0d\u76f8\u4fe1\u3002\u56e0\u4e3a\u5728\u4ed6\u60f3\u6cd5\uff0c \u4e16\u4e0a\u7b80\u76f4\u4e0d\u53ef\u80fd\u6709\u4f0a\u98ce\u53e3\u4e2d\u6240\u8bf4\u4e4b\u4e8b\u3002 \u3000\u3000\u4f0a\u98ce\u6717\u6717\u8bf4\u4e0b\u53bb\u9053 \u3000\u3000\u201c\u5c0f\u9119\u6b64\u6765\u6ec7\u4e2d\u65e0\u91cf\u5c71\uff0c\u662f\u5173\u7cfb\u7740\u6b66\u6797\u4e2d\u4e00\u4e2a\u7edd\u5927\u7684\u79d8\u5bc6\uff0c\u90a3\u5c31\u662f\u767e\u5341\u5e74\u524d\uff0c\u6b66\u6797\u5f02\u4eba \u6b66\u66f2\u661f\u541b\u6240\u9057\u7559\u4e0b\u6765\u7684\u79d8\u85cf\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u90a3\u4e00\u76f4\u5782\u76ee\u800c\u5750\u7684\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u4e5f\u4e0d\u7981\u7741\u5f00\u773c\u775b\u6765\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u66f4\u662f\u9732\u51fa\u6025\u5207\u7684\u795e\u8272\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u4e00\u626b\uff0c\u770b\u5230\u4ed6\u4eec\u7684\u795e\u60c5\uff0c\u6697\u53f9\u4e00\u58f0\u3002\u89c9\u5f97\u8fd9\u4e24\u4eba\u6b66\u529f\u867d\u9ad8\uff0c\u4eba\u54c1\u5374\u6781\u4e3a\u4f4e\u4e0b\uff01 \u3000\u3000\u6697\u6697\u62c5\u5fc3\u90a3\u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\u82e5\u843d\u5728\u4ed6\u4eec\u624b\u4e0a\uff0c\u90a3\u81ea\u5df1\u5c82\u4e0d\u662f\u53d8\u6210\u4e86\u4e3a\u864e\u4f5c\u4f25.\uff0c \u3000\u3000\u4f46\u662f\u82e5\u975e\u5982\u6b64\uff0c\u53c8\u600e\u80fd\u6551\u5f97\u7ec8\u5357\u5c71\u91cc\u7684\u6570\u767e\u6761\u4eba\u547d\uff1f \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u7740\u8bf4\u4e0b\u53bb\u9053\uff1a \u3000\u3000\u201c\u6b66\u66f2\u661f\u541b\u6b7b\u524d\uff0c\u66fe\u5c06\u4ed6\u751f\u5e73\u6b66\u5b66\u4e4b\u7cbe\u534e\u201c\u5929\u661f\u79d8\u7b08\u201d\u548c\u4e00\u7c92\u201c\u6bd2\u9f99\u4e38\u201d\uff0c\u57cb\u85cf\u5728\u8fd9\u65e0 \u91cf\u5c71\u91cc\uff0c\u4e5f\u5c31\u662f\u4e24\u4f4d\u7684\u8eab\u4fa7\u2026\u2026\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u548c\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u90fd\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\uff01 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\uff0c\u81ea\u5df1\u82e5\u5f97\u4e86\u8fd9\u672c\u6614\u5e74\u7eb5\u6a2a\u5929\u4e0b\u7684\u6b66\u6797\u5f02\u4eba\u6240\u9057\u7559\u4e0b\u7684\u6b66\u5b66\u79d8\u7b08\uff0c\u518d\u52a0\u4e0a \u81ea\u8eab\u7684\u6570\u5341\u5e74\u4fee\u4e3a\uff0c\u90a3\u4e48\u81ea\u5df1\u77ac\u606f\u5c31\u53ef\u53d8\u6210\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u3002\u4e8e\u662f\u4ed6\u4eec\u773c\u4e2d\uff0c\u90fd\u53d1\u51fa\u4e86\u8d2a\u5a6a\u7684 \u5149\u5f69\uff0c\u66f4\u662f\u5c4f\u606f\u503e\u542c\u4e0b\u53bb\uff0c\u751f\u6015\u8fd9\u5e74\u8f7b\u4eba\u4e0d\u80af\u8bf4\u51fa\u85cf\u5b9d\u4e4b\u5730\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\uff0c\u66f4\u4e0d\u4f4f\u5927\u58f0\u50ac\u4fc3\u7740\uff01 \u3000\u3000\u201c\u5feb\u8bb2\u4e0b\u53bb\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5374\u6545\u610f\u505c\u987f\u4e86\u534a\u664c\uff0c\u4f7f\u5f97\u4ed6\u4e8c\u4eba\u6025\u4e4b\u4e0d\u80dc\uff0c\u624d\u63a5\u53e3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u6837\u4e1c\u897f\uff0c\u867d\u662f\u5929\u4e0b\u6b66\u6797\u4eba\u58eb\u6240\u6e34\u6c42\u4e4b\u7269\uff0c\u4f46\u60c5\u52bf\u5982\u6b64\uff0c\u665a\u8f88\u5374\u60c5\u613f\u653e\u5f03\u8fd9\u4e24\u6837\u4e1c \u897f\uff0c\u800c\u8f6c\u9001\u4e0e\u4e24\u4f4d\u524d\u8f88\u3002\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u6545\u610f\u4e00\u987f\uff0c\u518d\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f46\u662f\uff0c\u665a\u8f88\u5374\u5b9a\u8981\u5f97\u5230\u6b66\u66f2\u661f\u541b\u6240\u9057\u7559\u7684\u53e6\u5916\u4e00\u7269\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u548c\u94c1\u9762\u5b64\u884c\u5ba2\u51e0\u4e4e\u540c\u65f6\u95ee\u9053\uff1a \u3000\u3000\u201c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u66f4\u4e3a\u6e05\u695a\u5730\u4e86\u89e3\u4e86\u8fd9\u4e24\u4eba\u7684\u8d2a\u5a6a\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u90a3\u5c31\u662f\u5929\u4e0b\u81f3\u6bd2\u4e4b\u836f\u201c\u8680\u9aa8\u5723\u6c34\u201d\u7684\u552f\u4e00\u89e3\u836f\u3002\u6211\u4e4b\u6240\u4ee5\u6e34\u6c42\u6b64\u7269\uff0c\u5c31\u662f\u4e3a\u4e86\u89e3\u6551\u7ec8 \u5357\u5c71\u4e2d\u4e86\u6b64\u6bd2\u7684\u6570\u767e\u4eba\u547d\u3002\u201d \u3000\u3000\u4ed6\u89c9\u5f97\u5728\u8fd9\u4e24\u4eba\u9762\u524d\uff0c\u5df2\u65e0\u987b\u81ea\u79f0\u665a\u8f88\u3002\u800c\u8fd9\u4e24\u4eba\u4e5f\u4e0d\u4f1a\u6ce8\u610f\u79f0\u547c\u4e0a\u7684\u6539\u53d8\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u53ea\u662f\u89c9\u5f97\u8fd9\u5e74\u8f7b\u4eba\uff0c\u653e\u5f03\u4e86\u6b66\u6797\u79d8\u5b9d\uff0c\u800c\u5df4\u5df4\u5730\u8981\u90a3\u4e0e\u5df2\u65e0\u5173\u7684\u89e3\u836f\uff0c\u6709\u4e9b\u5947 \u602a\u3002\u4ed6\u4eec\u751a\u81f3\u60f3\u5230\u8fd9\u5176\u4e2d\u6709\u4ec0\u4e48\u8be1\u8ba1\uff0c\u4f46\u4ed6\u4eec\u81ea\u6043\u81ea\u5bb6\u7684\u80fd\u529b\uff0c\u5374\u4e5f\u672a\u5c06\u4efb\u4f55\u8be1\u8ba1\uff0c\u653e\u5728\u5fc3 \u4e0a\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u82e5\u653e\u4e86\u6211\uff0c\u6211\u5c31\u5c06\u4e24\u4f4d\u5e26\u5230\u90a3\u85cf\u5b9d\u4e4b\u5730\uff0c\u53ea\u8981\u5f97\u5230\u89e3\u836f\uff0c\u6211\u4fbf\u7acb\u5373\u56de\u53bb\u3002\u81f3\u4e8e\u90a3 \u4e24\u4ef6\u5f02\u5b9d\u7684\u5206\u914d\uff0c\u5168\u51ed\u4e24\u4f4d\u4f5c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u548c\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\uff0c\u5fc3\u4e2d\u5404\u5404\u4e00\u8f6c\uff0c\u53c8\u540c\u65f6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u884c\u5f97\uff01\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u76ee\u5149\u4e00\u671b\u7a97\u5916\uff0c\u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\u5929\u5149\u5df2\u6e10\u767d\uff0c\u6b63\u597d\u884c\u4e8b\u3002\u201d \u3000\u3000\u8f6c\u5934\u4e00\u671b\u4e07\u5929\u840d\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u4e4b\u4e8b\uff0c\u7b49\u5230\u6b64\u4e8b\u8fc7\u540e\uff0c\u518d\u4f5c\u4e86\u65ad\u597d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5176\u5b9e\u5df2\u53e6\u6709\u8ba1\u8f83\u3002\u4f46\u94c1\u9762\u5b64\u884c\u5ba2\u53c8\u4f55\u5c1d\u4e0d\u662f\u5982\u6b64\uff0c\u5f53\u7136\u4e5f\u6beb\u65e0\u5f02\u8bae\u7684\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u8eab\u5f62\u4e00\u52a8\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u500f\u7136\u4e4b\u95f4\uff0c\u5df2\u94bb\u51fa\u4e86\u7a97\u5b50\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u53f9\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a\u8fd9\u5343\u91cc\u8ffd\u98ce\u795e\u884c\u65e0\u5f71\u7684\u8f7b\u529f\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u53ea\u662f\u5176\u827a\u6108\u9ad8\uff0c\u5176 \u884c\u5374\u6108\u5351\uff0c\u4ee4\u4eba\u60cb\u60dc\u3002 \u3000\u3000\u4ed6\u601d\u5fd6\u4e4b\u95f4\uff0c\u773c\u524d\u53c8\u4e00\u82b1\uff0c\u90a3\u94c1\u9762\u5b64\u884c\u5ba2\u4e5f\u63a0\u4e86\u51fa\u53bb\u3002\u4ed6\u4e5f\u4e00\u63a0\u800c\u51fa\u3002 \u3000\u3000\u5929\u5149\u867d\u672a\u5927\u4eae\uff0c\u4f46\u4e1c\u65b9\u5df2\u6cdb\u51fa\u9c7c\u809a\u822c\u7684\u767d\u8272\uff0c\u5c71\u5773\u4e4b\u4e2d\uff0c\u4e5f\u660e\u4eae\u5f97\u8db3\u591f\u4ed6\u5bfb\u627e\u85cf\u5b9d\u4e4b\u5730 \u4e86\u3002 \u3000\u3000\u4ef0\u671b\u5929\u8272\uff0c\u4ed6\u5ffd\u671b\u60f3\u5230\u81ea\u5df1\u5982\u6b64\u505a\uff0c\u662f\u5426\u5bf9\u5f97\u8d77\u6614\u5e74\u5ac9\u6076\u5982\u4ec7\u7684\u6b66\u66f2\u661f\u541b\uff01 \u3000\u3000\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u53c8\u600e\u6709\u5176\u4ed6\u4e4b\u8def\u53ef\u8d70\uff01 \u3000\u3000\u4ed6\u6697\u5730\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u8001\u4eba\u5bb6\u7684\u5728\u5929\u4e4b\u7075\uff0c\u80fd\u591f\u539f\u8c05\u6211\u8fd9\u4e0d\u5f97\u5df2\u7684\u505a\u6cd5\u5427\uff01\u201d \u3000\u3000\u5c71\u58c1\u4e4b\u4e0a\uff0c\u6ee1\u751f\u9752\u82d4\uff0c\u4ed6\u6cbf\u7740\u7011\u5e03\u4e4b\u4fa7\u524d\u884c\uff0c\u76ee\u5149\u4ed4\u7ec6\u5730\u641c\u7d22\u7740\uff0c\u679c\u7136\u53d1\u73b0\u5728\u90a3\u6ee1\u751f\u9752 \u82d4\u7684\u5c71\u58c1\u4e0a\uff0c\u6709\u7740\u4e03\u5904\u75d5\u8ff9\u3002 \u3000\u3000\u90a3\u662f\u4ee5\u5185\u5bb6\u91d1\u521a\u6307\u4e00\u7c7b\u7684\u529f\u529b\uff0c\u5728\u5c71\u58c1\u4e0a\u5212\u51fa\u7684\u4e03\u4e2a\u5c0f\u4e09\u89d2\uff0c\u4f9d\u5317\u6597\u4e03\u661f\u4e4b\u4f4d\u800c\u6392\u5217\u3002 \u82e5\u975e\u6781\u4e3a\u7559\u610f\uff0c\u4e5f\u65e0\u6cd5\u770b\u5230\u3002 \u3000\u3000\u4ed6\u4f4e\u5524\u4e00\u58f0\uff1a \u3000\u3000\u201c\u5728\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u8ddf\u5728\u4ed6\u540e\u9762\u7684\u5999\u624b\u8bb8\u767d\u548c\u4e07\u5929\u840d\uff0c\u4e5f\u7acb\u523b\u7d27\u5f20\u5730\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u4ed6\u627e\u5230\u4e03\u661f\u4e2d\u7684\u4e3b\u661f\u65b9\u4f4d\uff0c\u7528\u624b\u4e00\u63a8\uff0c\u5c71\u58c1\u5374\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u6115\uff0c\u7acb\u523b\u771f\u6c14\u8d2f\u8fbe\u56db\u68a2\uff0c\u5410\u6c14\u95fb\u58f0\uff0c\u671d\u7740\u90a3\u4f4d\u7f6e\u53cc\u638c\u7f13\u7f13\u63a8\u53bb \u3000\u3000\u7acb\u523b\u8d77\u4e86\u4e00\u9635\u65e0\u6cd5\u5f62\u5bb9\u7684\u58f0\u54cd\uff0c\u800c\u90a3\u4e00\u7247\u6d51\u5982\u6574\u4f53\u7684\u5c71\u58c1\uff0c\u53f3\u4fa7\u5374\u7f13\u7f13\u5e94\u624b\u5411\u5185\u79fb\u53bb\uff0c \u5de6\u4fa7\u5374\u5411\u5916\u9762\u65cb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5c71\u58c1\u4e0a\u7acb\u523b\u73b0\u51fa\u4e00\u5904\u6d1e\u7a74\u3002 \u3000\u3000\u4ed6\u72c2\u559c\u4e4b\u4e0b\uff0c\u6697\u81ea\u4f69\u670d\u90a3\u4f4d\u524d\u8f88\u5f02\u4eba\u5fc3\u610f\u4e4b\u7075\u5de7\u3002 \u3000\u3000\u7a81\u5730\uff0c\u8eab\u4fa7\u201c\u55d6\u201d\uff0c\u201c\u55d6\u201d\u4e24\u58f0\uff0c\u539f\u6765\u5999\u624b\u8bb8\u767d\u548c\u4e07\u5929\u840d\uff0c\u5df2\u62a2\u7740\u51c9\u4e86\u8fdb\u53bb\uff0c\u4ed6\u55e4\u4e4b\u4ee5 \u9f3b\u5730\u8f7b\u7b11\u4e00\u58f0\uff0c\u4e5f\u8ddf\u7740\u8d70\u8fdb\u8fd9\u85cf\u5b9d\u4e4b\u7a9f\u3002 \u3000\u3000\u6709\u5929\u5149\u81ea\u5165\u53e3\u4e4b\u5904\u5c04\u5165\uff0c\u662f\u4ee5\u6d1e\u7a9f\u4e4b\u4e2d\uff0c\u5e76\u4e0d\u5341\u5206\u9ed1\u6697\uff1b\u4f46\u6d1e\u7684\u5185\u7aef\uff0c\u5374\u662f\u9ed1\u9edd\u9edd\u5730\uff0c \u5f77\u4f5b\u6df1\u4e0d\u53ef\u6d4b\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u671d\u4f0a\u98ce\u4e00\u626c\u624b\uff0c\u4f0a\u98ce\u773c\u795e\u5fae\u5206\uff01\u518d\u5b9a\u775b\u770b\u53bb\uff0c\u81ea\u5bb6\u8eab\u4e0a\u7684\u706b\u647a\u5b50\uff0c\u5df2\u88ab\u8fd9\u4f4d\u795e \u5077\u5999\u624b\uff0c\u5728\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u5077\u4e86\u53bb\u3002 \u3000\u3000\u4ed6\u65e0\u53ef\u5948\u4f55\u5730\u4e00\u7b11\uff0c\u5fc3\u60f3\uff1a\u81ea\u5df1\u603b\u7b97\u5c1d\u5230\u4e86\u8fd9\u4f4d\u795e\u5077\u5999\u624b\u7684\u6ecb\u5473\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u604d\u5f00\u706b\u77f3\uff0c\u5f53\u5148\u5411\u5185\u8d70\u53bb\uff0c\u4e07\u5929\u840d\u5f53\u7136\u4ea6\u6b65\u4ea6\u8d8b\u5730\u8ddf\u5728\u540e\u9762\uff0c\u4f0a\u98ce\u53cd\u800c\u8d70\u5728\u6700 \u540e\uff0c\u53ea\u662f\u4ed6\u4e5f\u5e76\u4e0d\u5728\u610f\u800c\u5df2\u3002 \u3000\u3000\u524d\u884c\u6570\u5341\u4e08\uff0c\u6d1e\u7a9f\u8d8a\u6765\u8d8a\u7a84\uff0c\u524d\u9762\u5ffd\u7136\u6709\u4e00\u5f20\u77f3\u684c\u6321\u4f4f\u53bb\u8def\u3002 \u3000\u3000\u4e09\u4eba\u76ee\u5149\u52a8\u5904\uff0c\u90fd\u770b\u5230\u4e86\u90a3\u77f3\u684c\u4e0a\u653e\u7740\u4e00\u4e2a\u94c1\u5323\uff0c\u5999\u624b\u8bb8\u767d\u548c\u94c1\u9762\u5b64\u884c\u5ba2\u8eab\u5f62\u75be\u52a8\uff0c\u51e0 \u4e4e\u5728\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u90fd\u6293\u5230\u4e86\u90a3\u94c1\u5323\u3002\u4ed6\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u5404\u6000\u6212\u5907\u3002 \u3000\u3000\u4e07\u5929\u840d\u4f38\u624b\u4e00\u626d\uff0c\u90a3\u5323\u4e0a\u7684\u94c1\u9501\u4fbf\u4e5f\u5e94\u624b\u800c\u6bc1\u3002 \u3000\u3000\u4f0a\u98ce\u4e5f\u63a0\u4e86\u4e0a\u6765\uff0c\u76ee\u5149\u6ce8\u89c6\u7740\u3002 \u3000\u3000\u94c1\u5323\u7684\u5323\u858f\uff0c\u88ab\u4e24\u4eba\u540c\u65f6\u63ed\u5f00\uff0c\u9996\u5148\u5165\u76ee\u7684\uff0c\u5374\u662f\u4e00\u5f20\u6773\u9ec4\u7eb8\u67ec\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u548c\u94c1\u9762\u5b64\u884c\u5ba2\u53c8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5404\u81ea\u7f13\u7f13\u7f29\u56de\u624b\u3002 \u3000\u3000\u85c9\u7740\u706b\u647a\u5b50\u6240\u53d1\u51fa\u7684\u5149\u7ebf\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5f20\u6773\u9ec4\u7eb8\u67ec\u4e0a\u5199\u7740\uff1a \u3000\u3000\u201c\u5165\u6b64\u95e8\u8005\uff0c\u65e2\u5c5e\u5df2\u62b1\u51b3\u6b7b\u4e4b\u5fc3\u4e4b\u4eba\uff0c\u542f\u6b64\u5323\u540e\uff0c\u7acb\u670d\u6b64\u4e38\uff0c\u65b9\u5177\u65e0\u7a77\u795e\u529b\uff0c\u542f\u6211\u540e \u6d1e\uff0c\u5f97\u6211\u79d8\u7b08\u2026\u2026\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u548c\u4e07\u5929\u840d\u770b\u5230\u8fd9\u91cc\uff0c\u540c\u65f6\u500f\u7136\u4f38\u624b\uff0c\u201c\u62cd\u201d\u5730\u4e00\u58f0\uff0c\u4e24\u4eba\u624b\u638c\u76f8\u51fb\uff0c\u5404\u81ea\u540e\u9000 \u4e00\u6b65\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u52a8\u5904\uff0c\u5374\u63a5\u7740\u5ff5\u4e0b\u53bb\uff1a \u3000\u3000\u201c\u2026\u2026\u5f97\u6211\u79d8\u7b08\uff0c\u6b64\u4e38\u201c\u9634\u9704\u201d\uff0c\u867d\u5177\u65e0\u7a77\u5999\u7528\uff0c\u4f46\u5374\u5185\u542b\u5267\u6bd2\u3002\u670d\u6b64\u4e38\u8005\uff0c\u4e09\u5e74\u4e4b \u540e\uff0c\u5fc5\u55b7\u8840\u4e0d\u6cbb\u800c\u6b7b\u3002\u6b64\u4e09\u5e74\u4e2d\uff0c\u6c5d\u53ef\u4eab\u53d7\u4eba\u751f\uff0c\u4efb\u610f\u884c\u4e8b\uff0c\u56e0\u6c5d\u4e4b\u795e\u529b\uff0c\u5df2\u53ef\u65e0\u654c\u4e8e\u4e16 \u77e3\u3002\u201d \u3000\u3000\u4ed6\u6717\u58f0\u5ff5\u5b8c\uff0c\u5999\u624b\u8bb8\u767d\u548c\u4e07\u5929\u840d\u90fd\u7f29\u56de\u624b\uff0c\u6115\u6115\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u8c01\u90fd\u4e0d\u613f\u610f\u5c31\u53ea\u518d\u6d3b\u4e09\u5e74\uff0c\u5f53\u7136\u4e0d\u613f\u670d\u4e0b\u6b64\u4e38\u3002\u4f0a\u98ce\u62a2\u524d\u4e00\u6b65\uff0c\u4f38\u624b\u5411\u90a3\u5323\u4e2d\uff0c\u8bf4 \u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u65e2\u7136\u90fd\u4e0d\u613f\u670d\uff0c\u6211\u5c31\u670d\u4e86\u5427\uff01\u201d \u3000\u3000\u90a3\u77e5\u98ce\u58f0\u55d6\u7136\uff0c\u4e00\u53ea\u624b\u64d2\u5411\u4ed6\u7684\u8109\u95e8\uff0c\u53e6\u4e00\u53ea\u624b\u5374\u5206\u6beb\u4e0d\u5dee\u5730\u6307\u5411\u4ed6\u8098\u95f4\u7684\u7a74\u3002 \u3000\u3000\u4ed6\u53ea\u5f97\u8fde\u5fd9\u7f29\u56de\u624b\u81c2\u3002 \u3000\u3000\u5374\u542c\u5f97\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e5f\u670d\u4e0d\u5f97\uff01\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u6115\uff01\u987b\u77e5\u4ed6\u6700\u6700\u6e34\u6c42\u4e4b\u4e8b\uff0c\u4fbf\u662f\u80fd\u591f\u96ea\u803b\u590d\u4ec7\u3002\u6b64\u4e38\u670d\u4e0b\u540e\uff0c\u7eb5\u7136\u53ea\u80fd\u518d\u6d3b\u4e09 \u5e74\uff1b\u4f46\u4ed6\u82e5\u80fd\u85c9\u7740\u8fd9\u795e\u529b\u5b8c\u6210\u5fc3\u613f\uff0c\u90a3\u6b7b\u4ea6\u4e0d\u60dc\u3002\u662f\u4ee5\u4ed6\u624d\u6709\u670d\u4e0b\u6b64\u4e38\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u4ed6\u6115\u4e86\u534a\u664c\uff0c\u624d\u4f53\u4f1a\u51fa\u6765\u3002\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u4e0d\u613f\u77ed\u547d\uff0c\u5f53\u7136\u4e0d\u613f\u670d\u4e0b\u6b64\u4e38\u3002\u53ef\u662f\u5374\u53c8\u6015\u6211\u670d\u4e0b\u6b64\u4e38\u540e\uff0c\u6709\u4e86\u201c\u65e0\u654c\u4e8e\u4e16\u201d \u7684\u795e\u529b\uff0c\u800c\u5bf9\u4ed6\u4eec\u4e0d\u5229\uff0c\u662f\u4ee5\u4ed6\u4eec\u624d\u4e5f\u4e0d\u613f\u6211\u670d\u6b64\u4e38\u3002\u201d \u3000\u3000\u51b7\u7b11\u4e00\u58f0\uff0c\u4e5f\u540e\u9000\u4e00\u6b65\uff0c\u675f\u624b\u800c\u89c2\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u548c\u4e07\u5929\u840d\uff0c\u679c\u7136\u662f\u8fd9\u79cd\u5fc3\u601d\uff0c\u4ed6\u4eec\u8111\u6d77\u4e2d\u6781\u5feb\u5730\u601d\u7d22\u4e86\u7247\u523b\uff0c\u4ecd\u7136\u6ca1\u6709\u89e3\u51b3\u7684 \u65b9\u6cd5\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u7b49\u5148\u62ff\u4e86\u6b64\u4e38\uff0c\u518d\u5f80\u524d\u884c\uff0c\u4e5f\u8bb8\u5408\u4f60\u6211\u4e09\u4eba\u4e4b\u529b\uff0c\u80fd\u591f\u5f00\u655e\u90a3\u6b66\u66f2\u661f\u541b\u7684\u540e\u6d1e\uff0c\u4e5f \u672a\u53ef\u77e5\uff0c\u90a3\u4e48\u6b64\u4e38\u4fbf\u53ef\u5f03\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e07\u5929\u840d\u5fae\u5fae\u9894\u9996\uff0c\u4e00\u58f0\u4e0d\u54cd\u5730\u62ff\u8d77\u90a3\u94c1\u5323\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u6697\u4e2d\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e00\u624b\u62ff\u7740\u8fd9\u94c1\u5323\uff0c\u7b49\u4f1a\u4fbf\u5c11\u4e86\u4e00\u53ea\u624b\u548c\u6211\u62a2\u4e1c\u897f\u4e86\u3002\u201d \u3000\u3000\u5fc3\u91cc\u597d\u751f\u5f97\u610f\uff0c\u9762\u4e0a\u5374\u4e00\u4e1d\u4e5f\u4e0d\u9732\u51fa\u6765\u3002 \u3000\u3000\u4e8e\u662f\u4e09\u4eba\u63a0\u8fc7\u77f3\u684c\uff0c\u53c8\u5f80\u524d\u8d70\u53bb\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u6b66\u66f2\u661f\u541b>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u6b66\u66f2\u661f\u541b \u3000\u3000\u518d\u5f80\u524d\u884c\uff0c\u6d1e\u7a9f\u4e5f\u5c31\u66f4\u7a84\u3002 \u3000\u3000\u4f46\u4e09\u4eba\u4ecd\u53ef\u5e76\u80a9\u800c\u884c\uff0c\u53ea\u662f\u4f0a\u98ce\u5374\u6545\u610f\u8d70\u5728\u540e\u9762\u800c\u5df2\u3002 \u3000\u3000\u524d\u884c\u6570\u5341\u6b65\uff0c\u524d\u9762\u8d6b\u7136\u4e00\u5757\u5de8\u77f3\uff0c\u6b63\u597d\u5d4c\u5728\u6d1e\u7a9f\u91cc\u3002 \u3000\u3000\u8fd9\u5757\u5de8\u77f3\uff0c\u5e9e\u5927\u65e0\u670b\uff0c\u6015\u4e0d\u5728\u5343\u65a4\u4e4b\u4e0a\uff1b\u666e\u5929\u4e4b\u4e0b\uff0c\u6050\u6015\u518d\u96be\u6709\u4eba\u80fd\u72ec\u529b\u79fb\u53bb\u6b64\u77f3\u7684\u3002 \u3000\u3000\u4e07\u5929\u840d\u4f30\u91cf\u4e00\u4e0b\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u4e09\u4eba\u4e00\u9f50\u7528\u529b\uff0c\u82e5\u80fd\u79fb\u53bb\u6b64\u77f3\uff0c\u8fdb\u5165\u540e\u6d1e\uff0c\u90a3\u201c\u8680\u9aa8\u5723\u6c34\u201d\uff0c\u81ea\u662f\u5f52\u8fd9\u8001\u5f1f\u6240 \u6709\uff0c\u81f3\u4e8e\u201c\u5929\u661f\u79d8\u7b08\u201d\u548c\u201c\u6bd2\u9f99\u4e38\u201d\uff0c\u5374\u600e\u7684\u5206\u914d\u6cd5.\u201d \u3000\u3000\u8bf4\u65f6\uff0c\u4ed6\u773c\u775b\u7785\u7740\u8bb8\u767d\uff0c\u8bb8\u767d\u5374\u54c8\u54c8\u5927\u7b11\u51e0\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u65e0\u751a\u610f\u89c1\uff0c\u4e0d\u8fc7\u603b\u4ee5\u731c\u679a\u4e4b\u6cd5\uff0c\u6700\u4e3a\u5408\u9002\u3002\u4f60\u8bf4\u5982\u4f55.\u201d \u3000\u3000\u4e07\u5929\u840d\u53c8\u5fae\u5fae\u9894\u9996\u3002 \u3000\u3000\u5999\u624b\u8bb8\u81ea\u4fbf\u53c8\u671d\u4f0a\u98ce\u4e00\u626c\u624b\uff0c\u4f0a\u98ce\u8fd9\u6b21\u5b66\u4e56\u4e86\uff0c\u773c\u795e\u4e00\u4e1d\u4e0d\u5206\u3002 \u3000\u3000\u8bb8\u767d\u54c8\u54c8\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff01\u771f\u6709\u4f60\u7684\uff01\u201d \u3000\u3000\u4f38\u51fa\u5927\u624b\u671d\u4f0a\u98ce\u80a9\u4e0a\u4e00\u62cd\uff0c\u4f0a\u98ce\u5374\u4e00\u76f4\u8b66\u89c9\u7740\u3002\u90a3\u77e5\u8bb8\u767d\u4f38\u5f00\u53e6\u4e00\u53ea\u624b\uff0c\u91cc\u9762\u5df2\u6709\u5341\u51e0 \u679a\u5236\u94b1\uff0c\u800c\u8fd9\u4e9b\u5236\u94b1\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u6709\u6570\uff0c\u53c8\u662f\u4ece\u81ea\u5df1\u8eab\u4e0a\u53d6\u53bb\u7684\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u54c8\u54c8\u800c\u7b11\uff0c\u53c8\u5411\u4e07\u5929\u840d\u9053\uff1a \u3000\u3000\u201c\u6211\u624b\u4e2d\u62ff\u7740\u51e0\u679a\u5236\u94b1\uff0c\u4f60\u6765\u731c\u5355\u53cc\uff0c\u82e5\u731c\u4e2d\u4e86\uff0c\u201c\u5929\u661f\u79d8\u7b08\u201d\u5c31\u5f52\u4f60\uff1a\u82e5\u731c\u4e0d\u4e2d\uff0c \u201c\u5929\u661f\u79d8\u7b08\u201d\u5c31\u5f52\u6211\uff0c\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4e07\u5929\u840d\u4e00\u58f0\u4e0d\u54cd\u3002 \u3000\u3000\u8bb8\u767d\u5c06\u624b\u653e\u5728\u80cc\u540e\uff0c\u4e00\u4f1a\u513f\u53c8\u4f38\u51fa\u6765\uff0c\u7d27\u7d27\u63e1\u7740\u62f3\uff0c\u671d\u4e07\u5929\u840d\u9053\uff1a \u3000\u3000\u201c\u4f60\u731c\uff01\u201d \u3000\u3000\u201c\u53cc\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\u4e00\u53e3\u7b54\u9053\u3002 \u3000\u3000\u8bb8\u767d\u4f38\u5f00\u624b\u638c\uff0c\u91cc\u9762\u6709\u516d\u679a\u5236\u94b1\uff0c\u6b63\u662f\u53cc\u6570\uff0c\u4e07\u5929\u840d\u731c\u4e2d\u4e86\u3002 \u3000\u3000\u8bb8\u767d\u4e00\u526f\u61ca\u607c\u7684\u6837\u5b50\uff0c\u9053\uff1a \u3000\u3000\u201c\u5929\u661f\u79d8\u7b08\u662f\u4f60\u7684\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\u9762\u4e0a\u867d\u4e0d\u9732\u58f0\u8272\uff0c\u4f46\u5fc3\u4e2d\u5374\u751a\u559c\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u6b66\u66f2\u661f\u541b\u7684\u4e00\u751f\u6b66\u5b66\uff0c\u6e0a\u535a\u5982\u6d77\uff0c\u81f3\u4eca\u6b66\u6797\u5c1a\u65e0\u4e00\u4eba\u80fd\u53ca\u3002\u8fd9\u79cd\u5185\u5bb6\u79d8\u7b08\u81ea\u7136\u53c8 \u6bd4\u201c\u6bd2\u9f99\u4e38\u201d\u9ad8\u4e0a\u4e00\u7b79\u3002 \u3000\u3000\u90a3\u77e5\u8bb8\u767d\u9762\u4e0a\u867d\u61ca\u607c\uff0c\u5fc3\u4e2d\u5374\u5f97\u610f\uff0c\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u5934\u5b50\uff0c\u4f60\u53c8\u4e0a\u5f53\u4e86\u3002\u6211\u670d\u4e0b\u6bd2\u9f99\u4e38\u540e\uff0c\u529f\u529b\u7acb\u523b\u5c31\u80dc\u8fc7\u4f60\uff0c\u4f60\u603b\u4e0d\u80fd\u7acb\u523b\u5b66\u4f1a \u201c\u5929\u661f\u79d8\u7b08\u201d\u4e0a\u7684\u6b66\u529f\u5440\uff0c\u6211\u96be\u9053\u4e0d\u80fd\u4ece\u4f60\u624b\u4e0a\u5c06\u201c\u5929\u661f\u79d8\u7b08\u201d\u62a2\u8fc7\u6765\uff01\u4f60\u806a\u660e\u4e00\u4e16\uff0c\u5374\u7cca \u6d82\u4e00\u65f6\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u5fc3\u4e2d\u65e9\u5c31\u6709\u4e86\u8ba1\u8f83\uff0c\u662f\u4ee5\u624d\u4f1a\u63d0\u51fa\u731c\u679a\u4e4b\u8bae\u3002 \u3000\u3000\u987b\u77e5\u5999\u624b\u8bb8\u767d\u4ee5\u201c\u5999\u624b\u201d\u540d\u6ee1\u5929\u4e0b\uff0c\u624b\u4e0a\u7684\u529f\u592b\uff0c\u5df2\u7ecf\u5999\u5230\u6beb\u98a0\uff0c\u5c06\u624b\u91cc\u5236\u94b1\u7684\u6570\u76ee\uff0c \u968f\u610f\u53d8\u5316\u4e00\u4e0b\uff0c\u90a3\u8fd8\u4e0d\u662f\u7b80\u5355\u5df2\u6781\u7684\u4e8b\uff01 \u3000\u3000\u4e07\u5929\u840d\u679c\u7136\u806a\u660e\u4e00\u4e16\uff0c\u7cca\u6d82\u4e00\u65f6\uff0c\u5fc3\u4e2d\u5f97\u610f\uff0c\u53e3\u4e2d\u9053\uff1a \u3000\u3000\u201c\u5206\u914d\u5df2\u5b9a\uff0c\u4f60\u6211\u5c31\u4e00\u540c\u7528\u529b\uff0c\u5c06\u6b64\u77f3\u63a8\u5f00\u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\u4e3e\u624b\u5148\u5411\u90a3\u5927\u77f3\u63a8\u53bb\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u4e00\u9f50\u7528\u529b\uff0c\u5a01\u529b\u5c82\u540c\u5c0f\u9119\uff01\u90a3\u5757\u5de8\u77f3\u77ac\u95f4\u5411\u540e\u79fb\u53bb\uff0c\u4e24\u65c1\u9732\u51fa\u4e24\u4e2a\u5c3a\u8bb8\u5bbd\u7684\u901a\u9053 \u540e\uff0c\u8bb8\u767d\u548c\u4e07\u5929\u840d\u5c31\u4e00\u9f50\u4f4f\u624b\uff0c\u5411\u5185\u63a0\u53bb\u3002 \u3000\u3000\u91cc\u9762\u8c41\u7136\u5f00\u6717\uff0c\u53c8\u662f\u4e00\u4e2a\u6781\u5927\u7684\u6d1e\u7a9f\uff0c\u5374\u662f\u8fd9\u5c71\u7a9f\u7684\u9876\u7aef\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u624b\u4e2d\u706b\u647a\u4e00\u7167\uff0c\u53d1\u73b0\u6d1e\u4e2d\u4e5f\u6709\u4e00\u5f20\u77f3\u684c\uff0c\u684c\u4e0a\u4e5f\u653e\u7740\u4e24\u4e2a\u94c1\u5323\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u8fde\u5fd9\u5411\u524d\u63a0\u53bb\uff0c\u4e00\u4eba\u652b\u53d6\u4e86\u4e00\u5323\uff0c\u626d\u9501\u4e00\u770b\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u90a3\u5999\u624b\u8bb8\u767d\u624b\u4e2d\u4e4b\u5323 \u91cc\uff0c\u679c\u6709\u4e00\u4e2a\u7389\u74f6\uff0c\u4e0a\u9762\u8d6b\u7136\u5199\u7740\u201c\u6bd2\u9f99\u4e38\u201d\u4e09\u5b57\u3002 \u3000\u3000\u4ed6\u7b49\u4e0d\u53ca\u53bb\u987e\u4e07\u5929\u840d\u7684\u53cd\u5e94\uff0c\u5306\u5fd9\u5730\u4ece\u74f6\u4e2d\u5012\u51fa\u4e00\u7c92\u9f99\u773c\u5927\u7684\u4e39\u4e38\uff0c\u5c31\u5f80\u53e3\u4e2d\u9001\u53bb\uff0c\u679c \u7136\u5165\u53e3\u9999\u6c14\u51db\u51bd\uff0c\u4ed6\u8fde\u5fd9\u54bd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u63a0\u8fdb\u8fd9\u6d1e\u7a9f\u65f6\uff0c\u77f3\u684c\u4e0a\u5df2\u7a7a\u65e0\u6240\u6709\u3002\u4e07\u5929\u840d\u6b63\u72c2\u559c\u7740\u68c0\u89c6\u5323\u4e2d\u7684\u4e00\u672c\u9ec4\u7eeb\u5c0f\u518c\uff0c\u800c \u90a3\u5999\u624b\u8bb8\u767d\u4e5f\u6b63\u5728\u5403\u7740\u4e39\u836f\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u60ca\uff1a\u201c\u96be\u9053\u90a3\u89e3\u836f\u7adf\u4e0d\u5728\u6b64\u6d1e\u4e2d\uff01\u201d \u3000\u3000\u4ed6\u6e38\u4fa0\u6c5f\u6e56\uff0c\u867d\u975e\u5927\u6148\u5927\u6167\u4e4b\u4eba\uff0c\u7136\u800c\u6b64\u523b\u5012\u662f\u5168\u4e3a\u7740\u522b\u4eba\uff0c\u4e00\u70b9\u4e3a\u5df1\u79c1\u5fc3\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u56db\u63a0\uff0c\u624d\u53d1\u73b0\u6d1e\u7a9f\u4e0a\u7aef\uff0c\u7a81\u51fa\u4e00\u77f3\uff0c\u77f3\u4e0a\u653e\u7740\u4e00\u4e2a\u7389\u74f6\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8fde\u5fd9\u63d0\u6c14\u7eb5\u8eab\uff0c\u5411\u90a3\u4e0a\u9762\u63a0\u53bb\uff0c\u521a\u521a\u591f\u7740\u5730\u4f4d\uff0c\u53f3\u624b\u75be\u4f38\uff0c\u6273\u7740\u90a3\u5c71\u77f3\u3002\u76ee\u5149\u52a8 \u5904\uff0c\u7adf\u53d1\u73b0\u8fd9\u5757\u7a81\u51fa\u7684\u5c71\u77f3\u4e0a\u9762\uff0c\u9664\u4e86\u90a3\u7389\u74f6\u4e4b\u5916\uff0c\u7adf\u8fd8\u6709\u4e00\u65b9\u4e0a\u9762\u5199\u6ee1\u5b57\u8ff9\u7684\u9ec4\u7eeb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u5fd6\uff1a\u90a3\u6b66\u66f2\u661f\u541b\u5c06\u89e3\u836f\u8fdc\u8fdc\u653e\u5728\u6b64\u5904\uff0c\u5fc5\u6709\u7528\u610f\u3002 \u3000\u3000\u4e8e\u662f\u5148\u4e0d\u98d8\u8eab\u4e0b\u5760\uff0c\u5de6\u624b\u62ff\u4e86\u90a3\u65b9\u9ec4\u7eeb\uff0c\u5c31\u7740\u5fae\u5149\u4e00\u770b\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e0a\u9762\u5199\u7740\uff1a \u3000\u3000\u201c\u4f58\u4e00\u751f\u884c\u4fa0\uff0c\u7136\u5374\u6b7b\u4e8e\u6d41\u8a00\uff0c\u82cd\u5929\uff01\u82cd\u5929\uff01\u5948\u4f55\u5948\u4f55\uff01 \u3000\u3000\u201c\u4e16\u4eba\u5bf9\u4f59\u4e0d\u516c\uff0c\u4f58\u4ea6\u53ef\u5bf9\u4e16\u4eba\u4e0d\u516c\u3002 \u3000\u3000\u201c\u7136\u4f59\u4e0d\u5fcd\u5c06\u4e00\u751f\u5fc3\u8840\u6240\u805a\uff0c\u968f\u4f58\u4e4b\u6b7b\u800c\u6c38\u8fdc\u6e6e\u6ca1\uff0c\u662f\u4ee5\u5c06\u4f59\u6b66\u529f\u4e4b\u7cbe\u7cb9\u201c\u5929\u661f\u79d8\u7b08\u201d \u53ca\u7075\u4e39\u5999\u836f\uff0c\u85cf\u4e8e\u6b64\u95f4\u3002 \u3000\u3000\u201c\u7136\u975e\u5177\u5fc5\u6b7b\u4e4b\u5fc3\u4e4b\u4eba\uff0c\u867d\u5165\u6b64\u6d1e\uff0c\u4ea6\u4e0d\u80fd\u5f97\u6211\u79d8\u85cf\uff0c\u4f20\u6211\u79d8\u6280\uff0c\u541b\u4e34\u5929\u4e0b\u3002 \u3000\u3000\u201c\u6b64\u6d1e\u6240\u85cf\u4e4b\u6bd2\u9f99\u4e39\uff0c\u4ecd\u6614\u5e74\u5c60\u9f99\u5927\u5e08\u91c7\u5929\u4e0b\u7075\u836f\u800c\u6210\uff0c\u529f\u80fd\u593a\u5929\u5730\u9020\u5316\uff0c\u4f46\u5176\u6027\u81f3 \u9633\uff0c\u82e5\u672a\u5148\u670d\u524d\u6d1e\u4e4b\u81f3\u9634\u4e39\u836f\u201c\u9634\u9704\u4e39\u201d\uff0c\u518d\u4e8e\u7528\u529b\u63a8\u77f3\u65f6\u5f15\u53d1\u836f\u6027\uff0c\u800c\u5192\u7136\u7528\u6b64\uff0c\u5219\u534a\u65f6 \u4e4b\u5185\uff0c\u5fc5\u55b7\u8840\u800c\u6b7b\u2026\u2026\u201d \u3000\u3000\u770b\u5230\u8fd9\u91cc\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u51db\uff01 \u3000\u3000\u79fb\u76ee\u4e0b\u671b\uff0c\u90a3\u4e07\u5929\u840d\u6b63\u8d2a\u5a6a\u5730\u770b\u7740\u90a3\u672c\u79d8\u7b08\uff0c\u800c\u5999\u624b\u8bb8\u767d\u5374\u53cc\u624b\u7d27\u6293\u7740\u77f3\u684c\uff0c\u5168\u8eab\u8d77\u4e86 \u4e00\u9635\u9635\u626d\u66f2\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\uff0c\u84e6\u7136\u8d77\u4e86\u4e00\u79cd\u96be\u8a00\u7684\u611f\u89c9\u3002 \u3000\u3000\u518d\u5f80\u4e0b\u9762\u770b\u9053\uff1a \u3000\u3000\u201c\u662f\u4ee5\u5165\u6211\u6d1e\u8005\uff0c\u65e0\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u5f03\u9634\u9704\u4e4b\u4e39\uff0c\u5219\u7eb5\u80fd\u4ee5\u5176\u4ed6\u65b9\u6cd5\u8fdb\u5165\u6b64\u6d1e\uff0c\u4ecd\u4e0d\u514d\u4e00 \u6b7b\u3002 \u3000\u3000\u201c\u6709\u7f18\u4e4b\u4eba\uff0c\u5f97\u6211\u79d8\u85cf\uff1a\u65e0\u7f18\u4e4b\u4eba\uff0c\u5fc5\u906d\u6a2a\u7978\u3002\u4f58\u6b7b\u975e\u9065\uff0c\u4e34\u4e66\u611f\u6000\u826f\u591a\u77e3\uff01\u201d \u3000\u3000\u4e66\u6cd5\u8d8a\u6765\u8d8a\u4e71\u3002\u4e0b\u9762\u6f66\u8349\u5730\u5199\u7740\uff1a \u3000\u3000\u201c\u6b66\u66f2\u661f\u541b\u4e34\u884c\u7edd\u7b14\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5306\u5306\u770b\u5b8c\uff0c\u5fcd\u4e0d\u4f4f\u957f\u53f9\u4e00\u58f0\uff0c\u98d8\u843d\u5730\u4e0a\u3002 \u3000\u3000\u4e07\u5929\u840d\u6b64\u523b\u624d\u6ce8\u610f\u5230\u4ed6\uff0c\u4e5f\u770b\u5230\u4ed6\u624b\u4e2d\u7684\u9ec4\u7eeb\uff0c\u7eb5\u8eab\u4e00\u63a0\uff0c\u4e00\u628a\u62a2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u4e0e\u4ed6\u4e89\uff0c\u9000\u540e\u4e00\u6b65\u3002 \u3000\u3000\u4e07\u5929\u840d\u6781\u5feb\u770b\u5b8c\uff0c\u7a81\u5730\u53d1\u72c2\u5730\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u5934\u53c8\u4e00\u51db\u3002\u76ee\u5149\u8f6c\u5230\u5999\u624b\u8bb8\u767d\u8eab\u4e0a\uff0c\u5374\u89c1\u4ed6\u5168\u8eab\u75c9\u631b\u4e0d\u5df2\uff0c\u989d\u4e0a\u4e5f\u5df2\u5f00\u59cb\u6d41\u4e0b\u9ec4 \u8c46\u822c\u5927\u7684\u6c57\u73e0\u3002 \u3000\u3000\u4e07\u5929\u840d\u7b11\u58f0\u8d8a\u5389\uff0c\u9707\u5f97\u5c71\u7a9f\u4e4b\u5185\uff0c\u56de\u58f0\u56db\u8d77\uff0c\u50cf\u662f\u6709\u7740\u65e0\u6570\u4e2a\u4e07\u5929\u840d\u5728\u8fd9\u5c71\u7a9f\u4e4b\u4e2d\u72c2\u7b11 \u4e00\u6837\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u7d27\u54ac\u7259\u5173\uff0c\u5389\u58f0\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e07\u5929\u840d\u72c2\u7b11\u9053\uff1a \u3000\u3000\u201c\u8bb8\u767d\u5440\uff01\u8bb8\u767d\uff01\u4ece\u6b64\u4ee5\u540e\uff0c\u4f60\u518d\u4e5f\u4e0d\u80fd\u548c\u6211\u4e89\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e3e\u8d77\u90a3\u65b9\u9ec4\u7eeb\uff0c\u4e00\u53e5\u4e00\u53e5\u5730\uff0c\u5c06\u4e0a\u9762\u7684\u5b57\u8ff9\u5ff5\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5ff5\u5230\u4e00\u534a\uff0c\u8bb8\u767d\u5c31\u72c2\u543c\u4e00\u58f0\u6251\u4e86\u4e0a\u53bb\u3002\u4ed6\u6b64\u523b\u4f53\u5185\u4e07\u706b\u711a\u5fc3\uff0c\u5065\u58ee\u7684\u808c\u8089\u626d\u66f2\uff0c\u5c06\u90a3\u4ef6 \u672c\u5df2\u7834\u70c2\u4e0d\u582a\u7684\u9e51\u8863\uff0c\u6323\u5f97\u7247\u7247\u96f6\u843d\uff01 \u3000\u3000\u4e07\u5929\u840d\u77e5\u9053\u81ea\u5df1\u7684\u529f\u529b\u548c\u8bb8\u767d\u76f8\u82e5\uff0c\u89c1\u4e86\u4ed6\u6251\u4e0a\u6765\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u610f\u3002\u51b7\u7b11\u4e00\u58f0\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4e34\u6b7b\u72c2\u5f92\uff01\u8fd8\u6323\u4ec0\u4e48\u547d\uff1f\u201d \u3000\u3000\u5de6\u80c1\u631f\u7740\u90a3\u88c5\u7740\u201c\u5929\u661f\u79d8\u7b08\u201d\u7684\u94c1\u5323\uff0c\u5de6\u624b\u7d27\u7d27\u63e1\u7740\uff0c\u539f\u6765\u4ed6\u65e9\u5df2\u5c06\u90a3\u7c92\u201c\u9634\u9704\u4e39\u201d\u6293 \u5728\u638c\u5fc3\uff0c\u53f3\u638c\u4e00\u6325\uff0c\u76f4\u53d6\u90a3\u50cf\u75c5\u864e\u4e00\u6837\u6251\u6765\u7684\u5999\u624b\u8bb8\u767d\u7684\u524d\u5fc3\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u867d\u7136\u5df2\u8fd1\u75af\u72c2\uff0c\u4f46\u662f\u4ed6\u6570\u5341\u5e74\u7684\u575a\u82e6\u4fee\u4e3a\uff0c\u4ecd\u4f7f\u4ed6\u5728\u8fd9\u79cd\u60c5\u51b5\u91cc\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u5e94 \u654c\u7684\u62db\u672f\uff0c\u5de6\u638c\u5411\u524d\u72c2\u51fb\uff0c\u53f3\u624b\u7b95\u5f20\uff0c\u5411\u4e07\u5929\u840d\u5f53\u80f8\u6293\u53bb\uff0c\u8fd9\u5df2\u662f\u62da\u547d\u7684\u7740\u6570\u4e86\u3002 \u3000\u3000\u4e07\u5929\u840d\u51b7\u7b11\u5fd6\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u627e\u6b7b\uff01\u201d\u53f3\u638c\u52a0\u4e86\u5341\u6210\u771f\u529b\uff0c\u5411\u524d\u51fb\u51fa\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u6b66\u529f\u867d\u548c\u5999\u624b\u8bb8\u767d\u76f8\u82e5\uff0c\u4f46\u4ed6\u6240\u4e60\u7684\u662f\u91d1\u521a\u638c\u529b\uff0c\u82e5\u786c\u78b0\u786c\u5730\u4e92\u5bf9\u638c\u529b\uff0c\u4ee5\u8f7b\u8f6f \u4e4b\u529f\u79f0\u8a89\u7684\u8bb8\u767d\uff0c\u4fbf\u4e07\u4e07\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4f55\u51b5\u4ed6\u4ee5\u53f3\u638c\u51fa\u51fb\uff0c\u800c\u8bb8\u767d\u5374\u4ec5\u4ee5\u5de6\u638c\u76f8\u8fce\u5462\uff1f \u3000\u3000\u90a3\u77e5\u53cc\u638c\u4e00\u63a5\uff0c\u5374\u5927\u51fa\u4e07\u5929\u840d\u610f\u5916\uff0c\u81ea\u5df1\u8eab\u5f62\u500f\u5730\u4e00\u9707\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u8f6c\u7b2c\u4e8c\u4e2a\u5ff5\u5934\uff0c\u5999 \u624b\u8bb8\u767d\u7684\u53f3\u638c\uff0c\u5df2\u7740\u7740\u5b9e\u5b9e\u5730\u6293\u5411\u4ed6\u524d\u80f8\u3002\u4e07\u5929\u840d\u60e8\u543c\u4e00\u58f0\uff0c\u5999\u624b\u8bb8\u767d\u7684\u53f3\u624b\u4e94\u6307\uff0c\u7adf\u6df1\u6df1 \u6325\u5165\u4ed6\u80f8\u5185\u3002 \u3000\u3000\u539f\u6765\u90a3\u6bd2\u9f99\u4e38\u81f3\u9633\u81f3\u521a\uff0c\u5929\u4e0b\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u5355\u72ec\u670d\u7528\uff0c\u5426\u5219\uff0c\u4fbf\u5f15\u53d1\u5fc3\u706b\uff0c\u55b7\u8840\u800c\u6b7b\uff1b \u5999\u624b\u8bb8\u767d\uff0c\u4e5f\u4e0d\u4f8b\u5916\u3002\u4f46\u662f\u4ed6\u5728\u670d\u4e0b\u6b64\u4e38\u540e\uff0c\u4f53\u5185\u7684\u771f\u529b\u4fbf\u500f\u7136\u589e\u957f\u6570\u500d\uff0c\u8fd9\u79cd\u529f\u529b\u7684\u66b4 \u589e\uff0c\u4e5f\u662f\u4efb\u4f55\u4eba\u6240\u4e0d\u80fd\u62b5\u53d7\u7684\u75db\u82e6\uff0c\u662f\u4ee5\u5bf9\u638c\u4e4b\u4e0b\uff0c\u4e07\u5929\u840d\u4fbf\u4e07\u975e\u4ed6\u7684\u654c\u624b\u3002 \u3000\u3000\u4f0a\u98ce\u8fdc\u8fdc\u7ad9\u7740\uff0c\u770b\u5230\u8fd9\u4e00\u5e55\u60e8\u7edd\u4eba\u5bf0\u7684\u666f\u50cf\uff0c\u7eb5\u7136\u6df1\u6076\u6b64\u4e8c\u4eba\uff0c\u4f46\u4e5f\u4e0d\u7981\u607b\u7136\uff01 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u80f8\u524d\u5267\u75db\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u62da\u7740\u6700\u540e\u4e00\u4e1d\u4f59\u529b\uff0c\u53f3\u638c\u524d\u51fb\uff0c\u7830\u7136\u4e00\u58f0\uff0c\u4e5f\u7740\u7740\u5b9e \u5b9e\u7684\u51fb\u5728\u5999\u624b\u8bb8\u767d\u7684\u80f8\u524d\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u53cc\u775b\u8840\u8d64\uff0c\u94c1\u9762\u5b64\u884c\u5ba2\u8fd9\u52bf\u631f\u5343\u94a7\u7684\u4e00\u638c\uff0c\u5e76\u672a\u80fd\u4f7f\u4ed6\u6454\u51fa\u53bb\uff0c\u53ea\u662f\u5374\u5df2\u5c06\u4ed6 \u524d\u80f8\u7684\u80c1\u9aa8\uff0c\u5c3d\u6570\u6253\u65ad\u4e86\u3002 \u3000\u3000\u7136\u800c\u5374\u6709\u53e6\u4e00\u79cd\u5947\u5f02\u7684\u529b\u9053\uff0c\u7adf\u652f\u6301\u4ed6\u6b8b\u5b58\u7684\u751f\u547d\u529b\uff0c\u4ed6\u5de8\u7075\u822c\u7684\u5de6\u638c\uff0c\u75be\u5730\u524d\u4f38\uff0c\u4e94 \u6307\u5982\u5200\uff0c\u7adf\u53c8\u6325\u5728\u4e07\u5929\u840d\u7684\u54bd\u5589\u91cc\u3002 \u3000\u3000\u4e07\u5929\u840d\u7684\u9c9c\u8840\uff0c\u6e85\u5f97\u4ed6\u4e00\u8eab\u4e00\u8138\uff0c\u4f7f\u5f97\u866c\u987b\u5de8\u76ee\u7684\u4ed6\uff0c\u66f4\u4e3a\u72f0\u72de\u53ef\u6016\uff01\u4ece\u8bb8\u767d\u53e3\u4e2d\u6ccc\u51fa \u7684\u8840\uff0c\u5df2\u4e00\u6458\u6ef4\u843d\u5728\u4e07\u5929\u840d\u7684\u8138\u4e0a\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5929\u8d44\u90fd\u7edd\u9ad8\uff0c\u6b66\u529f\u4ea6\u5947\u6df1\uff0c\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db\u540d\u7684\u602a\u6770\uff0c\u7adf\u4e3a\u4e86\u4e00\u4e9b\u8d2a\u5fc3\uff0c\u800c\u843d\u5f97 \u5982\u6b64\u4e0b\u573a\uff0c\u662f\u503c\u5f97\u60cb\u60dc\u7684\uff0c\u6291\u6216\u662f\u4e0d\u503c\u7684\u5462\uff1f \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4f0a\u98ce\u6bd5\u7adf\u4f5c\u4e86\u8fd9\u5e55\u60e8\u5267\u7684\u552f\u4e00\u770b\u5ba2\uff1b\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u5bf9\u8fd9\u4e24\u4eba\u7684\u6b7b\uff0c\u4e5f\u6000\u6709 \u8bb8\u591a\u60b2\u6006\u548c\u8bb8\u591a\u611f\u89e6\uff01 \u3000\u3000\u706b\u647a\u5b50\u5148\u524d\u88ab\u5999\u624b\u8bb8\u767d\u653e\u5728\u684c\u8fb9\uff0c\u6b64\u523b\u70e7\u5230\u4e86\u77f3\u684c\uff0c\u5c31\u7184\u4e86\u3002 \u3000\u3000\u5c71\u7a9f\u91cc\u987f\u65f6\u53d8\u5f97\u575f\u5893\u4e00\u822c\u7684\u9759\u5bc2\uff0c\u575f\u5893\u4e00\u822c\u7684\u9ed1\u6697\u2014\u2014 \u3000\u3000\u4f0a\u98ce\u6014\u6014\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u6084\u7136\u95ed\u8d77\u4e86\u773c\u775b\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u666f\u8c61\u5374\u4ecd\u6df1\u9083\u5730\uff0c\u7559\u5728\u4ed6\u8111\u6d77\u91cc\uff0c\u8fd9\u4e5f\u8bb8\u5bf9\u4ed6\u4ee5\u540e\u4f5c\u4eba\uff0c\u4f1a\u6709\u7740\u5f88\u5927\u7684\u5f71\u54cd\u5427. \u3000\u3000\u826f\u4e45\uff0c\u4ed6\u832b\u7136\u7741\u5f00\u773c\u775b\uff0c\u4f46\u56db\u5468\u5374\u4ecd\u50cf\u4ed6\u95ed\u7740\u773c\u775b\uff0c\u4e00\u6837\u9ed1\u6697\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6478\u7d22\u7740\uff0c\u8d70\u5230\u77f3\u684c\u8fb9\uff0c\u6478\u7d22\u7740\uff0c\u62ff\u5230\u90a3\u706b\u647a\u5b50\uff0c\u604d\u5f00\u706b\u7130\uff0c\u5730\u4e0a\u7684\u201c\u5357\u5077\u5317 \u76d7\u201d\uff0c\u8840\u6db2\u4e98\u6d41\uff0c\u7d27\u7d27\u538b\u5728\u4e00\u5904\u3002\u4ed6\u4eec\u751f\u524d\u7684\u6069\uff0c\u6028\uff0c\u4ee5\u53ca\u4ed6\u4eec\u751f\u524d\u7684\u8d2a\u5a6a\uff0c\u6b64\u523b\u5df2\u968f\u7740\u6b7b \u4ea1\uff0c\u6c38\u8fdc\u6d88\u5931\u4e86\uff01 \u3000\u3000\u6ca1\u6709\u4efb\u4f55\u58f0\u97f3\uff1b\u5373\u4f7f\u8fde\u6700\u8f7b\u5fae\u7684\u98ce\u58f0\uff0c\u866b\u9e23\uff0c\u90fd\u6ca1\u6709\u3002\u4f0a\u98ce\u9664\u4e86\u4ed6\u81ea\u5df1\u7684\u547c\u5438\u4e4b\u58f0\u5916\uff0c \u4ec0\u4e48\u90fd\u542c\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u53c8\u6115\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u8d70\u5230\u90a3\u4e24\u4e2a\u602a\u6770\u7684\u5c38\u8eab\u4e4b\u4fa7\u2014\u2014 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c06\u8fd9\u4e24\u5177\u5c38\u8eab\uff0c\u79fb\u5230\u77f3\u684c\u4e0a\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u4e07\u5929\u840d\u4ecd\u7d27\u631f\u7740\u90a3\u5185\u653e\u201c\u5929\u661f\u79d8\u7b08\u201d\u7684\u94c1\u5323\u3002\u4f0a\u98ce\u957f\u53f9\u4e00\u58f0\uff0c\u8d39\u529b\u5730\u5c06\u90a3\u94c1 \u5323\uff0c\u4ece\u4ed6\u51b0\u51c9\u7684\u80c1\u4e0b\uff0c\u53d6\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5148\u524d\uff0c\u4ed6\u867d\u5bf9\u8fd9\u4e24\u4eba\u6df1\u4e3a\u8f7b\u89c6\u548c\u75db\u6076\uff0c\u4f46\u6b64\u523b\uff0c\u8fd9\u4efd\u8f7b\u89c6\u548c\u75db\u6076\uff0c\u4e5f\u968f\u7740\u8fd9\u4e24\u4eba\u7684\u79bb\u5f00 \u4eba\u4e16\uff0c\u800c\u79bb\u5f00\u4e86\u4f0a\u98ce\u7684\u5fc3\u623f\u3002 \u3000\u3000\u4ed6\u9eef\u7136\u638f\u51fa\u4e00\u5757\u767d\u5dfe\uff0c\u4e3a\u8fd9\u4e24\u4f4d\u602a\u6770\uff0c\u62ed\u51c0\u4e86\u8138\u4e0a\u7684\u8840\u8ff9\uff0c\u518d\u7eb5\u8eab\u63a0\u8d77\uff0c\u4ece\u90a3\u5757\u5c71\u77f3 \u4e0a\uff0c\u62ff\u4e0b\u4e86\u90a3\u91cc\u9762\u653e\u7740\u89e3\u836f\u7684\u7389\u74f6\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u8111\u6d77\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\uff0c\u9664\u4e86\u90a3\u4e00\u5e55\u60e8\u70c8\u7684\u666f\u8c61\u5916\uff0c\u4ed6\u60f3\u4e0d\u5230\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u867d\u7136\u4ed6\u9f3b\u7aef\u55c5\u5230\u4e00\u80a1\u5f02\u9999\uff0c\u4ed6\u4e5f\u6ca1\u6709\u53bb\u63a2\u67e5\u90a3\u5f02\u9999\u7684\u6765\u6e90\u3002\u53ea\u89c9\u5f97\u8fd9\u6d1e\u7a9f\u91cc\uff0c\u6709\u7740\u4e00\u80a1 \u4ee4\u4eba\u5ba4\u606f\u7684\u610f\u5473\uff0c\u538b\u5728\u4ed6\u7684\u5fc3\u4e0a\u3002 \u3000\u3000\u4ed6\u6709\u6025\u5207\u79bb\u5f00\u8fd9\u91cc\u7684\u6b32\u671b\uff0c\u5306\u5306\u654c\u5f00\u94c1\u5323\uff0c\u5c06\u90a3\u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u63e3\u5728\u8eab\u4e0a\uff1b\u624b\u91cc\u8c28\u614e \u5730\u62ff\u7740\u90a3\u7389\u74f6\uff0c\u56e0\u4e3a\u8fd9\u5173\u7cfb\u7740\u8bb8\u591a\u4eba\u7684\u751f\u547d\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u56de\u8f6c\u8eab\uff0c\u5411\u6d1e\u5916\u8d70\u53bb\u3002 \u3000\u3000\u53ea\u9057\u7559\u4e0b\u8fd9\u4e24\u4e2a\u6b66\u6797\u602a\u6770\u7684\u5c38\u8eab\uff0c\u7ea0\u7f20\u5730\u5012\u5367\u5728\u77f3\u684c\u4e0a\u3002\u4e5f\u8fd8\u7559\u4e0b\u4e24\u4ef6\u4ed6\u5782\u624b\u53ef\u5f97\u7684\u6b66 \u6797\u5f02\u5b9d\uff0c\u6e6e\u6ca1\u5728\u8fd9\u6d1e\u7a9f\u91cc\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e24\u4ef6\u6b66\u6797\u5f02\u5b9d\uff0c\u662f\u4e0d\u4f1a\u6c38\u8fdc\u6e6e\u6ca1\u7684\u3002 \u3000\u3000\u90a3\u4e48\u53c8\u662f\u8c01\u80fd\u6709\u7f18\u5f97\u5230\u5b83\u5462\uff1f \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u76f8\u601c\u540c\u75c5>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u76f8\u601c\u540c\u75c5 \u3000\u3000\u4f0a\u98ce\u4ee5\u5c3d\u9119\u80fd\u7684\u901f\u5ea6\uff0c\u8d76\u51fa\u4e86\u8fd9\u4e2a\u6d1e\u7a9f\u3002\u5916\u9762\u65e5\u8272\u6ee1\u5929\uff0c\u5df2\u662f\u664c\u5348\u65f6\u5206\u4e86\u3002 \u3000\u3000\u4ed6\u6e38\u76ee\u56db\u987e\uff0c\u5c71\u5773\u91cc\u666f\u8272\u4f9d\u7136\uff0c\u90a3\u53e4\u62d9\u7684\u77f3\u5c4b\uff0c\u4e5f\u4ecd\u7136\u65e0\u6059\u5730\u8e72\u8e1e\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u77f3\u5c4b\u7684\u4e3b\u4eba\u5462\uff1f \u3000\u3000\u4ed6\u4e0d\u7981\u957f\u53f9\u7740\u3002 \u3000\u3000\u81ea\u5df1\u4e5f\u89c9\u5f97\u81ea\u5df1\u7684\u5fc3\u60c5\uff0c\u50cf\u662f\u4ece\u575f\u5893\u4e2d\u590d\u6d3b\u4e00\u6837\uff01 \u3000\u3000\u4ed6\u7684\u5fc3\u60c5\uff0c\u6b64\u523b\u662f\u8427\u7d22\u800c\u843d\u5bde\u7684\uff0c\u4e0b\u610f\u8bc6\u5730\u79fb\u52a8\u8eab\u5f62\uff0c\u5411\u5c71\u5773\u5916\u8d70\u53bb\u3002 \u3000\u3000\u6cbf\u7740\u5c71\u6da7\uff0c\u4ed6\u6781\u51b3\u5730\u5f80\u5c71\u4e0b\u7eb5\u53bb\u3002\u76f4\u5230\u5df2\u8fd1\u5c71\u9e93\u4e4b\u5904\uff0c\u4ed6\u624d\u60f3\u8d77\u5728\u90a3\u5c71\u5773\u4e2d\u8fd8\u6709\u4e00\u5806\u4ef7 \u503c\u65e0\u53ef\u6bd4\u62df\u7684\u73cd\u5b9d\uff0c\u4ed6\u51ed\u7740\u90a3\u5806\u73cd\u5b9d\uff0c\u53ef\u4ee5\u5728\u8fd9\u4e16\u4e0a\u4efb\u610f\u505a\u8bb8\u591a\u53ea\u8981\u81ea\u5bb6\u613f\u610f\u505a\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u8fd8\u60f3\u8d77\uff0c\u5728\u201c\u5357\u5077\u5317\u76d7\u201d\u7684\u8eab\u4e0a\uff0c\u8fd8\u6709\u7740\u4e00\u4e2a\u4ef7\u503c\u6bd4\u90a3\u5806\u73cd\u5b9d\u66f4\u9ad8\u7684\u5b9d\u7269\u7487\u5149\u4eea\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u4e0d\u7981\u52a8\u4e86\u4e00\u4e0b\uff0c\u51e0\u4e4e\u60f3\u7acb\u523b\u6298\u56de\u53bb\uff0c\u53d6\u5f97\u90a3\u4e9b\u4e1c\u897f\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5728\u4ed6\u5fc3\u5e95\u6df1\u5904\uff0c\u5374\u6709\u4e00\u79cd\u66f4\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u7981\u6b62\u4ed6\u5982\u6b64\u505a\uff01 \u3000\u3000\u5999\u624b\u8bb8\u767d\u548c\u94c1\u9762\u5b64\u884c\u5ba2\u7684\u60e8\u6b7b\uff0c\u7ec8\u5357\u5f1f\u5b50\u7684\u547b\u541f\u2026\u2026\u8fd9\u4e9b\uff0c\u4e5f\u90fd\u771f\u5b9e\u800c\u6df1\u523b\u7684\uff0c\u5728\u4ed6\u8111 \u6d77\u4e2d\u63a0\u8fc7\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u6beb\u4e0d\u8003\u8651\u5730\uff0c\u52a0\u901f\u4e86\u8eab\u5f62\uff0c\u63a0\u5411\u5c71\u4e0b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff1a\u552f\u6709\u8fd9\u6837\uff0c\u4ed6\u7684\u5fc3\u624d\u80fd\u5e73\u9759\u3002 \u3000\u3000\u7eb5\u7136\u4f60\u62e5\u6709\u5929\u4e0b\u6240\u6709\u7684\u73cd\u5b9d\uff0c\u4f46\u5fc3\u82e5\u4e0d\u5b89\uff0c\u4f60\u4e5f\u7b97\u662f\u4e0d\u5feb\u4e50\u7684\u4eba\uff0c\u662f\u5417\uff1f\u2014\u2014\u81f3\u5c11\uff0c\u4e00 \u90e8\u4efd\u4eba\u662f\u5982\u6b64\u3002 \u3000\u3000\u7f2d\u7ed5\u7684\u767d\u4e91\uff0c\u672c\u6765\u662f\u5728\u4ed6\u811a\u4e0b\u7684\uff0c\u6b64\u523b\u5df2\u53d8\u4e3a\u5728\u4ed6\u5934\u4e0a\u3002 \u3000\u3000\u524d\u9762\u5c71\u8def\u4e00\u8f6c\uff0c\u4ed6\u77e5\u9053\u8981\u518d\u8d8a\u8fc7\u4e24\u5904\u5c71\u5cf0\uff0c\u624d\u80fd\u56de\u5230\u5165\u5c71\u4e4b\u5904\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8eab\u5f62\u66f4\u5feb\uff0c\u6068\u4e0d\u5f97\u63d2\u7fc5\u98de\u56de\u7ec8\u5357\u3002 \u3000\u3000\u8f6c\u8fc7\u4e00\u5904\u5c71\u5cf0\uff0c\u5ffd\u7136\u6709\u4e00\u58f0\u957f\u53f9\u4e4b\u58f0\uff0c\u4ece\u5c71\u8170\u65c1\u7684\u6797\u6728\u4e2d\u4f20\u51fa\uff0c\u58f0\u97f3\u4e2d\uff0c\u5145\u6ee1\u4e86\u5e7d\u6028\uff0c \u6124\u6168\uff0c\u548c\u4e0d\u5e73\u3002 \u3000\u3000\u5728\u9759\u5bc2\u7684\u7fa4\u5c71\u4e2d\uff0c\u663e\u5f97\u5206\u5916\u6e05\u6670\u3002 \u3000\u3000\u5728\u665a\u51ac\u5bd2\u98ce\u4e2d\uff0c\u98d8\u51fa\u53bb\u8001\u8fdc\uff0c\u8001\u8fdc\u2014\u2014 \u3000\u3000\u4f0a\u98ce\u8eab\u5f62\u4e0d\u7981\u7565\u4e3a\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u6697\u5fd6\uff1a \u3000\u3000\u201c\u8fd9\u4e16\u4e0a\u7684\u4f24\u5fc3\u4eba\uff0c\u4f55\u5176\u5982\u6b64\u4e4b\u591a\uff01\u201d \u3000\u3000\u601d\u8def\u672a\u7ec8\uff0c\u90a3\u6797\u6728\u4e2d\u53c8\u4f20\u6765\u4e00\u4e2a\u60b2\u6124\u7684\u58f0\u97f3\uff0c\u4f3c\u4e4e\u662f\u5583\u5583\u81ea\u8bed\u7740\uff1a \u3000\u3000\u4f0a\u98ce\u5e76\u4e0d\u80fd\u542c\u5f97\u5341\u5206\u771f\u786e\uff0c\u4f46\u4ed6\u81ea\u5e7c\u7ec3\u529f\uff0c\u8033\u76ee\u81ea\u7136\u8981\u6bd4\u5e38\u4eba\u7075\u654f\u5f97\u591a\uff0c\u9690\u7ea6\u4e2d\u4ed6\u4ecd\u53ef \u542c\u51fa\u8bed\u58f0\u4e2d\u4f3c\u4e4e\u6709\uff1a\u201c\u7f62\u4e86\u2026\u2026\u518d\u89c1\u2026\u2026\u201d\u8fd9\u6837\u7684\u8bcd\u53e5\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u81ea\u601d\u5fd6\u7740\uff1a \u3000\u3000\u201c\u83ab\u975e\u6709\u4eba\u8981\u5728\u8fd9\u6df1\u5c71\u8352\u6797\u4e2d\u81ea\u5c3d\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u8111\u4e2d\u518d\u65e0\u8003\u8651\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u5411\u90a3\u53f9\u606f\u58f0\u7684\u6765\u5904\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u8fdb\u6811\u6797\uff0c\u4f0a\u98ce\u76ee\u5149\u77ac\u5904\uff0c\u679c\u7136\u53d1\u73b0\u5728\u6797\u4e2d\u4e00\u682a\u67af\u6728\u4e0a\uff0c\u60ac\u7740\u4e00\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u731c\u6d4b\u679c\u7136\u4e0d\u9519\uff0c\u8fd9\u8352\u6797\u4e4b\u4e2d\uff0c\u679c\u7136\u6709\u4eba\u81ea\u5c3d\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5f62\uff0c\u7acb\u523b\u98de\u63a0\u4e86\u8fc7\u53bb\uff0c\u901f\u5ea6\u4e4b\u5feb\uff0c\u51e0\u4e4e\u662f\u5728\u4ed6\u76ee\u5149\u6240\u53ca\u7684\u90a3\u540c\u4e00\u5239\u90a3\u3002 \u3000\u3000\u4ed6\u53f3\u638c\u671d\u60ac\u5728\u6811\u679d\u4e0a\u7684\u7ef3\u7d22\u4e00\u6325\uff0c\u624b\u6307\u822c\u7c97\u7ec6\u7684\u7ef3\u7d22\uff0c\u5e94\u624b\u800c\u65ad\uff0c\u60ac\u5728\u7ef3\u7d22\u4e0a\u7684\u8eaf\u5e72\uff0c \u81ea\u7136\u4e5f\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u5de6\u624b\u4e00\u63fd\uff0c\u7f13\u4f4f\u4e86\u90a3\u4eba\u4e0b\u843d\u7684\u52bf\u9053\uff0c\u968f\u7740\u81ea\u5df1\u8eab\u5f62\u7684\u4e0b\u843d\uff0c\u8f7b\u8f7b\u5c06\u90a3\u4eba\u653e\u5230\u5730\u4e0a\u3002 \u4ed6\u63a2\u624b\u4e00\u6478\u90a3\u4eba\u7684\u9f3b\u606f\uff0c\u5c1a\u672a\u6c14\u7edd\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5728\u90a3\u4e2a\u4eba\u7684\u4e09\u5341\u516d\u5904\u5927\u7a74\u4e0a\uff0c\u7565\u4e3a\u63a8\u62ff\u4e00\u4e0b\u3002\u90a3\u4eba\u60a0\u60a0\u957f\u53f9\u4e00\u58f0\uff0c\u4fbf\u81ea\u9192\u8f6c\uff0c\u76ee \u5149\u65e0\u52a9\u5730\u843d\u5728\u4f0a\u98ce\u8eab\u4e0a\u3002 \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u597d\u6b7b\u4e0d\u5982\u6b79\u6d3b\u3002\u670b\u53cb\uff01\u4f60\u6b63\u503c\u76db\u5e74\uff0c\u53c8\u4f55\u5fc5\u81ea\u5bfb\u6b7b\u8def\u54e9\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7a7f\u7740\u7834\u65e7\u7684\u886b\u88e4\uff0c\u9762\u76ee\u4e5f\u5341\u5206\u6194\u60b4\u3002 \u3000\u3000\u4f46\u662f\u4ece\u4ed6\u6194\u60b4\u4e4b\u8272\u4e2d\uff0c\u4ecd\u53ef\u4ee5\u53d1\u73b0\u4ed6\u662f\u4e00\u4e2a\u6781\u4e3a\u6e05\u79c0\u7684\u4eba\uff0c\u5e74\u9f84\u4e5f\u4e0d\u8fc7\u624d\u4e8c\u5341\u591a\u5c81\u3002 \u3000\u3000\u8fd9\u4f7f\u5f97\u4f0a\u98ce\u5bf9\u4ed6\u8d77\u4e86\u597d\u611f\u3002 \u3000\u3000\u90a3\u4eba\u76ee\u5149\u5446\u6ede\u5730\u8f6c\u4e86\u51e0\u8f6c\uff0c\u4f3c\u4e4e\u5728\u8bd5\u7740\u8bc1\u660e\u81ea\u5df1\u867d\u5df2\u65e0\u610f\u7559\u604b\u4eba\u4e16\uff0c\u4f46\u5374\u4ecd\u7136\u6d3b\u5728\u4eba\u4e16 \u4e0a\u3002 \u3000\u3000\u542c\u4e86\u4f0a\u98ce\u7684\u8bdd\uff0c\u957f\u53f9\u4e00\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u53c8\u4f55\u5fc5\u7ba1\u627e\uff1f\u6211\u5fc3\u5df2\u6b7b\uff0c\u7eb5\u7136\u4eba\u6d3b\u5728\u4e16\u4e0a\uff0c\u53c8\u6709\u4ec0\u4e48\u751f\u8da3\uff1f\u201d \u3000\u3000\u4ed6\u5fae\u4e00\u505c\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u975e\u4f24\u5fc3\u4eba\uff0c\u5f53\u7136\u4e0d\u77e5\u4f24\u5fc3\u4eba\u7684\u60b2\u54c0\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u5ddd\u9ed4\u53e3\u97f3\uff0c\u8bcd\u53e5\u4e4b\u95f4\uff0c\u7adf\u975e\u5e38\u4ece\u5bb9\u5f97\u4f53\u3002 \u3000\u3000\u90a3\u548c\u4ed6\u7684\u5916\u8868\uff0c\u6781\u4e3a\u4e0d\u76f8\u79f0\uff0c\u663e\u89c1\u662f\u843d\u9b44\u4e4b\u4eba\u3002 \u3000\u3000\u4f0a\u98ce\u81ea\u601c\u5730\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4f60\u53c8\u600e\u77e5\u6211\u4e0d\u662f\u4f24\u5fc3\u4eba\u5462\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u6709\u4f55\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u4e0d\u59a8\u8bf4\u6765\u542c\u542c\uff0c\u6216\u8bb8\u5728\u4e0b\u80fd\u6548\u5fae\u52b3\uff0c\u4e5f\u672a\u53ef\u77e5\uff1f\u201d \u3000\u3000\u4ed6\u7684\u8bed\u6c14\u975e\u5e38\u8c26\u548c\uff0c\u7edd\u672a\u56e0\u5bf9\u65b9\u7684\u843d\u9b44\uff0c\u800c\u7a0d\u6709\u8f7b\u89c6\u3002 \u3000\u3000\u90a3\u4eba\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u81ea\u8bc9\u4e86\u8eab\u4e16\u2014\u2014 \u3000\u3000\u539f\u6765\u4ed6\u662f\u5ddd\u8fb9\u5c4f\u5c71\u9547\u4e0a\u7684\u4e00\u4e2a\u4e66\u9999\u5b50\u5f1f\uff0c\u59d3\u6e29\u540d\u534e\uff0c\u867d\u975e\u5929\u8d44\u7edd\u9876\u4e4b\u4eba\uff0c\u4f46\u8bfb\u4e66\u5012\u4e5f\u975e \u5e38\u901a\u987a\u3002\u53ea\u662f\u547d\u8fd0\u4e0d\u4f73\uff0c\u4e00\u76f4\u8e49\u8dce\u6f66\u5012\uff0c\u6210\u4e86\u4e2a\u767e\u65e0\u4e00\u7528\u7684\u65e0\u7528\u4e66\u751f\u3002 \u3000\u3000\u4ed6\u5bb6\u4e1a\u4e00\u5149\uff0c\u7ef4\u751f\u4fbf\u65e0\u529b\u3002\u4e8e\u662f\u53ea\u5f97\u643a\u5e26\u7740\u5a07\u59bb\uff0c\u7531\u5ddd\u4eba\u6ec7\uff0c\u5728\u8fd9\u65e0\u91cf\u5c71\u91cc\u91c7\u6a35\u4e3a\u751f\u3002 \u6587\u4eba\u65e0\u547d\uff0c\u5c31\u662f\u4e16\u4e0a\u6700\u53ef\u601c\u7684\u4eba\u4e86\uff01 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u59bb\u5b50\uff0c\u5374\u8010\u4e0d\u4f4f\u8fd9\u5c71\u4e2d\u5bc2\u5bde\uff0c\u7adf\u548c\u53e6\u5916\u4e00\u4e2a\u5076\u7136\u7ed3\u8bc6\u7684\u5546\u4eba\u79c1\u5954\u4e86\u3002 \u3000\u3000\u6e29\u534e\u7b80\u7565\u5730\u8bf4\u51fa\u4e86\u81ea\u5df1\u60b2\u60e8\u7684\u8eab\u4e16\u3002 \u3000\u3000\u771f\u6b63\u662f\u4eba\u6d77\u4e2d\u8bb8\u591a\u503c\u5f97\u60b2\u54c0\u7684\u5c0f\u4eba\u7269\uff0c\u6240\u901a\u5e38\u80fd\u53d1\u751f\u7684\u6545\u4e8b\u3002\u7136\u800c\u4f0a\u98ce\u542c\u4e86\uff0c\u5374\u611f\u89e6\u751a \u6df1\u3002 \u3000\u3000\u4ed6\u6014\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u7ffb\u6d8c\u7740\u767e\u822c\u6ecb\u5473\u3002\u8fd9\u6e29\u534e\u7684\u8eab\u4e16\uff0c\u4e0d\u4e5f\u6709\u51e0\u5206\u548c\u81ea\u5df1\u76f8\u540c\u5417\uff01\u201c\u76f8\u601c \u6700\u662f\u540c\u75c5\u4eba\uff1f\u201d\u4ed6\u4e5f\u9677\u5165\u60b2\u54c0\u4e86\uff01 \u3000\u3000\u6e29\u534e\u53c8\u53f9\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u840d\u6c34\u76f8\u9022\uff0c\u627f\u9601\u4e0b\u597d\u610f\u6551\u4e86\u6211\u3002\u4f46\u662f\u9601\u4e0b\u53ea\u80fd\u6551\u6211\u4e4b\u8eab\uff0c\u53c8\u600e\u80fd\u6551\u6211\u4e4b\u5fc3\u5462\uff01\u201d \u3000\u3000\u201c\u5509\uff01\u91d1\u94b1\u4e07\u6076\uff0c\u5374\u4e5f\u662f\u4e07\u80fd\u7684\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7a81\u7136\u60f3\u5230\u5728\u5c71\u98a0\u5904\u77f3\u5ba4\u4e2d\u90a3\u4e00\u5806\u73e0\u5b9d\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fae\u7b11\u95ee\u6e29\u534e\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u65e2\u7136\u76f8\u9022\uff0c\u5c31\u662f\u6709\u7f18\u3002\u6211\u5728\u6b64\u5c71\u4e2d\u5b58\u6709\u4e9b\u8bb8\u94b1\u8d22\uff0c\u4e8e\u6211\u867d\u65e0\u7528\uff0c\u5bf9\u4f60\u5374\u6216\u6709\u5e2e \u52a9\u2026\u2026\u201d \u3000\u3000\u4ed6\u770b\u89c1\u6e29\u534e\u5f20\u53e3\u6b32\u8a00\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e07\u52ff\u63a8\u8f9e\uff01\u82e5\u4f60\u5f97\u5230\u90a3\u4e9b\u94b1\u8d22\u540e\uff0c\u8fd8\u60f3\u81ea\u5c3d\uff0c\u6211\u4e5f\u4e0d\u518d\u62e6\u963b\u4f60\u3002\u5509\uff01\u5176\u5b9e\u5929\u4e0b\u5c3d\u591a \u5973\u5b50\uff0c\u4f60\u59bb\u5b50\u65e2\u7136\u65e0\u60c5\uff0c\u4f60\u53c8\u4f55\u5fc5\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u5374\u4e0d\u7981\u81ea\u5df1\u987f\u4f4f\u8bdd\u3002\u4ed6\u5728\u8fd9\u6837\u529d\u7740\u4eba\u5bb6\uff0c\u800c\u4ed6\u81ea\u5df1\u5462\uff1f \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u5cf0\u56de\u8def\u8f6c>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u5cf0\u56de\u8def\u8f6c \u3000\u3000\u7559\u604b\u4eba\u751f\uff0c\u672c\u662f\u4eba\u7c7b\u7684\u901a\u6027\u3002 \u3000\u3000\u6e29\u534e\u7ec8\u4e8e\u8ddf\u7740\u4f0a\u98ce\u4e0a\u5c71\u3002 \u3000\u3000\u4ed6\u53f3\u81c2\u88ab\u4f0a\u98ce\u6240\u6301\uff0c\u53ea\u89c9\u8eab\u8eaf\u50cf\u662f\u817e\u4e91\u822c\uff0c\u76f4\u5f80\u4e0a\u98d8\u3002\u5fc3\u4e2d\u5bf9\u4f0a\u98ce\u4e4b\u7fa1\u6155\uff0c\u65e0\u4ee5\u590d\u52a0\uff01 \u3000\u3000\u800c\u4f0a\u98ce\u5462\uff0c\u4ed6\u811a\u4e0b\u867d\u4e0d\u505c\u5730\u8d70\u7740\uff0c\u7136\u800c\u5fc3\u4e2d\u5374\u52a8\u4e5f\u4e0d\u52a8\u5730\uff0c\u505c\u7559\u5728\u4e00\u5904\u2014\u2014 \u3000\u3000\u90a3\u662f\u5728\u6c5f\u5357\u7684\u4e00\u9053\u5c0f\u6728\u6865\u4e0a\u8fdc\u5904\u7684\u665a\u971e\uff0c\u591a\u5f69\u800c\u7eda\u4e3d\uff0c\u8fd1\u5904\u7684\u706f\u70df\uff0c\u5a40\u5a1c\u800c\u751f\u59ff\uff0c\u5915\u9633 \u6240\u7167\uff0c\u6cb3\u5cb8\u8fb9\u7684\u9752\u8349\uff0c\u8f6c\u6362\u6210\u68a6\u4e00\u6837\u7684\u989c\u8272\uff0c\u518d\u52a0\u4e0a\u6865\u4e0b\u6d41\u6c34\u7684\u4f4e\u8bed\u4eba\u95f4\u5c82\u975e\u80dc\u4e8e\u4ed9\u5883.\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u5730\u65b9\uff0c\u4f0a\u98ce\u7b2c\u4e00\u773c\u89c1\u5230\u4ed6\u7684\u59bb\u5b50\u2014\u2014\u81ea\u7136\uff0c\u5f53\u65f6\u5979\u8fd8\u4e0d\u662f\u4ed6\u7684\u59bb\u5b50\u3002 \u3000\u3000\u5979\u9a91\u7740\u767d\u8272\u9a6c\uff0c\u7f13\u7f13\u5730\uff0c\u7531\u6865\u7684\u90a3\u8fb9\u7b56\u9a6c\u8fc7\u6765\uff0c\u5915\u9633\u7167\u7740\u5979\u7684\u8138\uff0c\u53d1\u4e1d\u968f\u7740\u6625\u65e5\u7684\u5fae \u98ce\uff0c\u5728\u5979\u5a07\u7f8e\u5982\u82b1\u7684\u9762\u988a\u4e0a\u98d8\u821e\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u9677\u5165\u4e86\u56de\u5fc6\u2014\u2014 \u3000\u3000\u201c\u5979\u7389\u4e5f\u4f3c\u5730\u53f3\u624b\uff0c\u8f7b\u8f7b\u6325\u821e\u7740\u9a6c\u97ad\u3002\u201d \u3000\u3000\u201c\u671d\u6211\u751c\u751c\u4e00\u7b11\uff1a\u5c31\u662f\u8fd9\u4e00\u7b11\uff0c\u4f7f\u6211\u5fd8\u8bb0\u4e86\u4e00\u5207\uff01\u7531\u6c5f\u5357\u5fd8\u60c5\u5730\u8ddf\u7740\u5979\uff0c\u8ddf\u5230\u6c5f\u5317\u3002\u4e00 \u8def\u4e0a\uff0c\u5979\u5bf9\u6211\u4f3c\u4e4e\u6709\u610f\uff0c\u53c8\u4f3c\u4e4e\u65e0\u610f\u3002\u201d \u3000\u3000\u201c\u6211\u78b0\u5230\u6211\u7684\u597d\u53cb\u94f6\u67aa\u9676\u695a\u65f6\uff0c\u624d\u77e5\u9053\u5979\u5c31\u662f\u6c5f\u6e56\u4e0a\u7684\u7b2c\u4e00\u7f8e\u4eba\uff0c\u9500\u9b42\u7f57\u5239\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e0d\u81ea\u89c9\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5979\u8fd9\u4e2a\u540d\u5b57\u5728\u5ac1\u7ed9\u6211\u540e\uff0c\u5c31\u53d8\u6210\u4e86\u9500\u9b42\u592b\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u867d\u7136\u8ffd\u968f\u4e07\u91cc\uff0c\u53ef\u662f\u59cb\u7ec8\u6ca1\u6709\u673a\u4f1a\u8ba4\u8bc6\u5979\u3002\u201d \u3000\u3000\u201c\u76f4\u5230\u4e00\u5929\uff0c\u5979\u5728\u5251\u95e8\u9053\u4e0a\uff0c\u906d\u9047\u4e86\u201c\u5251\u95e8\u4e94\u9738\u201d\u3002\u5979\u7684\u4e00\u6761\u4eae\u94f6\u97ad\uff0c\u600e\u62b5\u654c\u5f97\u7740\u90a3\u51f6 \u540d\u56db\u64ad\u7684\u201c\u5251\u95e8\u4e94\u9738\u201d\u624b\u4e2d\u7684\u4e94\u6837\u5175\u5203\uff1f\u773c\u770b\u5c31\u8981\u4e0d\u654c\uff0c\u5979\u82e5\u88ab\u201c\u5251\u95e8\u4e94\u9738\u201d\u6240\u64d2\uff0c\u90a3\u540e\u679c \u662f\u4e0d\u582a\u8bbe\u60f3\u7684\uff01\u201d \u3000\u3000\u201c\u6211\u81ea\u7136\u51fa\u624b\u6551\u4e86\u5979\uff0c\u4e5f\u85c9\u7740\u8fd9\u673a\u7f18\u8ba4\u5f97\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u6211\u90a3\u65f6\u5e74\u8f7b\u6c14\u76db\uff0c\u81ea\u6043\u6b66\u529f\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e0d\u5982\u4e3a\u5979\u7ed3\u4e86\u591a\u5c11\u51a4\u5bb6\u3002\u201d \u3000\u3000\u201c\u76f4\u5230\u6709\u4e00\u5929\uff0c\u6211\u4e3a\u5979\u5f97\u7f6a\u4e86\u4ee5\u6bd2\u836f\u6697\u5668\u9a70\u540d\u5929\u4e0b\u7684\u56db\u5ddd\u5510\u5bb6\uff0c\u8eab\u53d7\u4e09\u4ef6\u5510\u5bb6\u7236\u5b50\u7684\u7edd \u6bd2\u6697\u5668\uff0c\u5979\u624d\u5bf9\u6211\u7a0d\u4e3a\u597d\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u90a3\u6b21\u4e5f\u771f\u662f\u4e5d\u6b7b\u4e00\u751f\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u6211\u771f\u6709\u4e9b\u6000\u7591\u662f\u5426\u503c\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u81ea\u4ece\u90a3\u6b21\u4e4b\u540e\uff0c\u5979\u5bf9\u6211\u53ef\u7b97\u597d\u5230\u6781\u70b9\u3002\u6211\u4eec\u5e76\u80a9\u9a70\u9a8b\uff0c\u6e38\u904d\u4e86\u6c5f\u5357\u6c5f\u5317\uff0c\u5927\u6cb3\u4e1c\u897f\uff0c \u751a\u81f3\u8fde\u585e\u5916\uff0c\u6211\u4eec\u90fd\u8dd1\u53bb\u8fc7\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u6bb5\u65f6\u65e5\uff0c\u771f\u662f\u751c\u871c\u871c\u7684\uff01\u201d \u3000\u3000\u201c\u6709\u4e00\u5929\uff0c\u6211\u4eec\u9759\u9759\u5750\u5728\u661f\u7a7a\u4e0b\uff0c\u5979\u6307\u7740\u5929\u7a7a\u4e0a\u7684\u7ec7\u5973\u661f\u8bf4\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u3002\u201d\u53c8\u6307\u7740\u725b \u90ce\u661f\u8bf4\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u5c31\u8bf4\uff01\u201c\u4e00\u5e74\u53ea\u89c1\u4e00\u6b21\uff0c\u672a\u514d\u592a\u5c11\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u8fd8\u8bb0\u5f97\u5979\u90a3\u65f6\u7684\u751c\u7b11\u3002 \u3000\u3000\u201c\u5c24\u5176\u5979\u8bf4\u7740\uff1a\u201c\u91d1\u98ce\u7389\u9732\u4e00\u76f8\u9022\uff0c\u4fbf\u80dc\u5374\u4eba\u95f4\u65e0\u6570\u3002\u4e24\u4eba\u60c5\u6df1\uff0c\u53c8\u4f55\u987b\u591a\u89c1\uff01\u53ea\u8981\u6211 \u4eec\u80fd\u751f\u751f\u4e16\u4e16\u5728\u4e00\u8d77\uff0c\u4e00\u5e74\u53ea\u89c1\u4e00\u6b21\uff0c\u6211\u4e5f\u7518\u5fc3\u3002\u201d \u3000\u3000\u201c\u8bf4\u90a3\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u5979\u82e5\u53eb\u6211\u7acb\u65f6\u6b7b\u5728\u5979\u9762\u524d\uff0c\u6211\u4e5f\u4f1a\u6beb\u4e0d\u72b9\u7591\u5730\u53bb\u6b7b\u7684\uff01\u201d \u3000\u3000\u4f0a\u98ce\u56e0\u7740\u8fd9\u4e9b\u751c\u871c\u7684\u56de\u5fc6\u800c\u5fae\u7b11\u4e86 \u3000\u3000\u201c\u540e\u6765\u6211\u4eec\u5b9a\u5c45\u4e86\u4e0b\u6765\uff0c\u90a3\u867d\u7136\u662f\u4e00\u95f4\u5e76\u4e0d\u534e\u4e3d\u7684\u623f\u5b50\uff0c\u7136\u800c\u5728\u6211\u770b\u6765\uff0c\u5374\u50cf\u662f\u4ed9\u5883\u4e00 \u6837\uff01\u201d \u3000\u3000\u201c\u65e0\u8bba\u522e\u98ce\u4e0b\u96e8\uff0c\u51ac\u5929\u590f\u5929\uff0c\u6211\u4eec\u4e24\u4eba\u90fd\u662f\u5feb\u4e50\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u65f6\uff0c\u6211\u4eec\u7eb5\u7136\u5bf9\u5750\u7740\u542c\u4e86\u4e00\u5915\u7684\u96e8\u58f0\uff0c\u4f46\u5374\u6b64\u505a\u4efb\u4f55\u4e8b\u90fd\u5feb\u4e50\u3002\u201d \u3000\u3000\u201c\u5728\u90a3\u6bb5\u65e5\u5b50\u91cc\uff0c\u6211\u4ec0\u4e48\u90fd\u4e0d\u60f3\u505a\uff0c\u751a\u81f3\u8fde\u5bb6\u95e8\u90fd\u4e0d\u613f\u8e0f\u51fa\u53bb\u4e00\u6b65\u3002\u6c5f\u6e56\u4e2d\u7684\u58f0\u540d\uff0c\u6b66 \u6797\u4e2d\u7684\u6069\u6028\uff0c\u6211\u90fd\u4e0d\u518d\u5728\u610f\u3002\u5f53\u65f6\u6211\u5c31\u60f3\uff1a\u82e5\u662f\u5979\u79bb\u5f00\u4e86\u6211\uff0c\u6211\u5c31\u7b97\u6210\u4e3a\u6b66\u6797\u4e2d\u7b2c\u4e00\u4eba\uff0c\u53c8 \u6709\u4f55\u4e50\u8da3.\uff1f\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4f46\u662f\uff0c\u6211\u60f3\u4e0d\u5230\u5979\u540e\u6765\u771f\u7684\u79bb\u5f00\u4e86\u6211\uff0c\u505a\u4e86\u90a3\u5929\u4e89\u6559\u4e3b\u7684\u60c5\u5987\u3002\u201d \u3000\u3000\u201c\u6211\u8d77\u5148\u4e0d\u61c2\u5979\u662f\u4e3a\u7740\u4ec0\u4e48.\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u90a3\u5929\u4e89\u6559\u4e3b\u6b66\u529f\u6bd4\u6211\u9ad8\uff0c\u6743\u529b\u6bd4\u6211\u5927\uff0c\u5979 \u5728\u4ed6\u90a3\u91cc\uff0c\u53ef\u4ee5\u4eab\u53d7\u8bb8\u591a\u5728\u6211\u8fd9\u91cc\u4eab\u53d7\u4e0d\u5230\u7684\u4e1c\u897f\uff0c\u6240\u4ee5\u5979\u624d\u4f1a\u80cc\u53db\u4e86\u6211\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u53c8\u5f00\u59cb\u5835\u585e\u8d77\u6765\uff0c\u81ea\u601c\uff0c\u81ea\u8d23\uff0c\u81ea\u5c0a\u5fc3\u7684\u5c48\u8fb1\uff0c\u4f7f\u5f97\u4ed6\u51e0\u4e4e\u8fde\u53f9\u606f\u90fd\u4e0d\u80fd\u591f\uff01\u6124 \u6012\u548c\u590d\u4ec7\u7684\u706b\u7130\uff0c\u71c3\u70e7\u7740\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u4ed6\u671b\u4e86\u671b\u65c1\u8fb9\u7684\u6e29\u534e\u4e00\u773c\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6211\u8981\u5c06\u90a3\u77f3\u5ba4\u4e2d\u7684\u73cd\u8d35\uff0c\u5168\u90e8\u7ed9\u4ed6\uff0c\u8ba9\u4ed6\u80fd\u4eab\u53d7\u4e00\u4e9b\u4eba\u4e16\u95f4\u7684\u5feb\u4e50\uff1b\u800c\u8ba9\u4ed6\u90a3\u6deb\u8361\u65e0 \u803b\u7684\u59bb\u5b50\uff0c\u540e\u6094\u81ea\u5df1\u4e3a\u4ec0\u4e48\u8981\u79bb\u5f00\u4ed6\uff01\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7a81\u7136\u5411\u6e29\u534e\u9053\uff1a \u3000\u3000\u201c\u4ee5\u540e\uff0c\u4f60\u7684\u59bb\u5b50\u82e5\u518d\u6765\u54c0\u6c42\u4f60\u7684\u5bbd\u6055\uff0c\u4f60\u5927\u53ef\u4ee5\u5c06\u4f60\u6b64\u523b\u5fc3\u4e2d\u6240\u611f\u5230\u7684\u5c48\u8fb1\u548c\u60b2\u54c0\uff0c \u52a0\u500d\u5730\u8fd8\u7ed9\u5979\u7684\u8eab\u4e0a\uff0c\u7136\u540e\u518d\u8d76\u5979\u51fa\u53bb\u3002\u201d \u3000\u3000\u6e29\u534e\u832b\u7136\u5730\u4e00\u70b9\u5934\uff0c\u89c9\u5f97\u8fd9\u5947\u602a\u7684\u5e74\u8f7b\u4eba\uff0c\u60f3\u6cd5\u548c\u81ea\u5df1\u6709\u5f88\u591a\u5730\u65b9\u5b8c\u5168\u76f8\u540c\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u4f0a\u98ce\u7684\u906d\u9047\uff0c\u4e5f\u6b63\u548c\u4ed6\u4e00\u6837\u54e9\uff01 \u3000\u3000\u6c34\u58f0\u6f7a\u6f7a\uff0c\u53c8\u5230\u4e86\u5c71\u6da7\u4e4b\u5904\u3002 \u3000\u3000\u4f0a\u98ce\u7cbe\u795e\u4e00\u632f\uff0c\u98de\u4e5f\u4f3c\u5730\u5411\u4e0a\u9762\u63a0\u53bb\u3002\u53ea\u662f\u4ed6\u81ea\u5df1\u6709\u4e9b\u611f\u89c9\u5230\uff1a\u81ea\u5df1\u7684\u4f53\u529b\uff0c\u65e5\u6e10\u4e0d\u652f \u4e86\u3002 \u3000\u3000\u7a7f\u8fc7\u5939\u58c1\uff0c\u5c71\u5773\u4e2d\u4e00\u5207\u4ecd\u5982\u6545\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u56db\u626b\uff0c\u53d1\u73b0\u90a3\u5c71\u58c1\u79d8\u7a9f\u5165\u53e3\u5904\u7684\u90a3\u5858\u5927\u77f3\uff0c\u4e5f\u4ecd\u662f\u5f00\u7740\u7684\uff0c\u9732\u51fa\u91cc\u9762\u9edd\u9ed1\u7684\u6d1e \u7a74\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u505c\u987f\u4e0b\u6765\uff0c\u6307\u7740\u90a3\u95ee\u77f3\u5c4b\u9053\uff1a \u3000\u3000\u201c\u90a3\u91cc\u9762\u7684\u5b9d\u7269\uff0c\u8db3\u591f\u4f60\u505a\u4efb\u4f55\u4e8b\uff01\u201d \u3000\u3000\u4ed6\u968f\u5373\u53c8\u8865\u5145\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u5b9d\u7269\uff0c\u867d\u975e\u6211\u6240\u6709\uff0c\u4f46\u6211\u5374\u6709\u6743\u6765\u52a8\u7528\u5b83\u3002\u201d \u3000\u3000\u6e29\u534e\u6b64\u523b\u5bf9\u4f0a\u98ce\u5df2\u662f\u53e3\u670d\uff0c\u5fc3\u670d\uff0c\u5f53\u7136\u53ea\u662f\u552f\u552f\u79f0\u662f\u3002 \u3000\u3000\u5230\u4e86\u90a3\u77f3\u5c4b\u65c1\uff0c\u4f0a\u98ce\u548c\u6e29\u534e\u4e00\u9f50\u5411\u7a97\u5185\u671b\u53bb\uff0c\u4e24\u4eba\u90fd\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u6e29\u534e\u60ca\u5f02\u7684\u662f\uff1a \u3000\u3000\u8fd9\u77f3\u5ba4\u4e2d\u653e\u7740\u7684\u73cd\u5b9d\uff0c\u8fdc\u51fa\u4ed6\u7684\u610f\u6599\uff0c\u7adf\u6bd4\u4ed6\u505a\u68a6\u68a6\u5230\u7684\u8fd8\u8981\u591a\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u8fd9\u4e9b\u5c31\u8981\u5f52\u4e3a\u81ea\u5df1\u6240\u6709\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u9635\u9635\u5730\u5267\u8df3\uff0c\u53c8\u6709\u4e9b\u4e0d\u76f8\u4fe1\u8fd9\u4f1a\u662f\u771f\u5b9e\u7684\u4e8b \u60c5\uff0c\u56e0\u4e3a\u8fd9\u6bd4\u68a6\u5883\u8fd8\u8981\u79bb\u5947\u3002 \u3000\u3000\u800c\u4f0a\u98ce\u60ca\u5f02\u7684\u5374\u662f\uff1a \u3000\u3000\u8fd9\u77f3\u5ba4\u4e2d\u7684\u73cd\u5b9d\uff0c\u7adf\u6bd4\u4ed6\u6e05\u6668\u6240\u89c1\u5c11\u4e86\u4e0d\u77e5\u591a\u5c11\uff0c\u5269\u4e0b\u7684\u4e0d\u8fc7\u4ec5\u662f\u5168\u90e8\u7684\u5341\u5206\u4e4b\u4e00\u4e86\u3002 \u3000\u3000\u201c\u662f\u8c01\u62ff\u4e86\u53bb.\uff1f\u201d\u4f0a\u98ce\u5403\u60ca\u5730\u95ee\u7740\u81ea\u5df1\u3002 \u3000\u3000\u76ee\u5149\u53c8\u56db\u626b\uff0c\u60f3\u4ece\u5468\u56f4\u7684\u7269\u4e8b\u4e0a\uff0c\u5bfb\u627e\u51fa\u81ea\u5df1\u8fd9\u4e2a\u95ee\u9898\u7684\u7b54\u6848\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5931\u671b\u4e86\uff01 \u3000\u3000\u8fd9\u5c71\u5773\u91cc\u7684\u6bcf\u4e00\u4ef6\u4e1c\u897f\uff0c\u4f3c\u4e4e\u90fd\u5b8c\u5168\u6ca1\u6709\u53d8\u52a8\u3002 \u3000\u3000\u4ed6\u60f3\u5bfb\u5f97\u4e00\u7247\u8db3\u8ff9\uff0c\u6216\u8005\u662f\u4efb\u4f55\u6709\u4eba\u6765\u8fc7\u7684\u8ff9\u8c61\u3002 \u3000\u3000\u7136\u800c\u4ed6\u4e5f\u5931\u671b\u4e86\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u5728\u5730\u4e0a\u53d1\u73b0\u4e86\u4e00\u6ef4\u8840\u6e0d\uff0c\u8fde\u5fd9\u8e72\u4e0b\u53bb\u770b\uff0c\u8840\u6e0e\u867d\u5df2\u5e72\uff0c\u4f46\u4ed6\u51ed\u7740\u591a\u5e74\u6c5f\u6e56\u7684\u7ecf \u9a8c\uff0c\u5224\u65ad\u8fd9\u8840\u6e0d\uff0c\u7edd\u5bf9\u662f\u65b0\u9c9c\u7684\u3002 \u3000\u3000\u201c\u8fd9\u5b64\u96f6\u96f6\u7684\u4e00\u6458\u8840\u6e0d\uff0c\u4ee3\u8868\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u518d\u6b21\u8be2\u95ee\u7740\u81ea\u5df1\uff0c\u50cf\u662f\u4e00\u6761\u730e\u72ac\u5728\u641c\u5bfb\u7740\u4ed6\u7684\u730e\u7269\u4f3c\u7684\uff0c\u4e25\u5bc6\u5730\u6253\u91cf\u7740\u56db\u5468\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u5728\u8fd1\u6d1e\u53e3\u4e4b\u5904\uff0c\u53c8\u53d1\u73b0\u4e86\u7b2c\u4e8c\u6ef4\u8840\u6e0d\u3002 \u3000\u3000\u4ed6\u8fde\u5fd9\u63a0\u4e86\u8fc7\u53bb\uff0c\u53d1\u73b0\u8fd9\u7b2c\u4e8c\u6ef4\u8840\u6e0d\uff0c\u548c\u7b2c\u4e00\u6458\u8840\u6e0d\u4e00\u6837\uff0c\u4e5f\u662f\u65b0\u843d\u4e0d\u4e45\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6beb\u4e0d\u72b9\u8c6b\u5730\u4e00\u63a0\u8fdb\u6d1e\uff0c\u6781\u5feb\u5730\u5411\u6d1e\u53e3\u8d70\u53bb\u3002 \u3000\u3000\u4ed6\u518d\u638f\u51fa\u706b\u6298\u5b50\uff0c\u4e00\u8def\u4e0a\u4ed4\u7ec6\u5730\u641c\u7d22\u7740\uff0c\u4e00\u76f4\u5230\u4e86\u540e\u6d1e\uff0c\u90a3\u5757\u5de8\u5927\u7684\u5c71\u77f3\uff0c\u4ecd\u9759\u5367\u5728\u90a3 \u91cc\u672a\u52a8\u3002 \u3000\u3000\u4ed6\u8c28\u614e\u5730\u63a0\u4e86\u8fdb\u53bb\uff0c\u706b\u647a\u4e0a\u7684\u706b\u7130\uff0c\u56e0\u7740\u4ed6\u4e0e\u5f62\u7684\u7a81\u4e00\u8f6c\u6298\uff0c\u7a0d\u7a0d\u6697\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u7b49\u5230\u706b\u7130\u518d\u660e\u7684\u65f6\u5019\uff0c\u4f0a\u98ce\u4e0d\u7981\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eb2\u624b\u653e\u5728\u77f3\u684c\u4e0a\u7684\u4e24\u5177\u5c38\u8eab\uff0c\u6b64\u523b\u53ea\u5269\u4e0b\u4e86\u5999\u624b\u8bb8\u767d\u7684\u4e00\u5177\uff1a\u800c\u5999\u624b\u8bb8\u767d\u7684\u5c38 \u8eab\uff0c\u4e5f\u6539\u53d8\u4e86\u539f\u6765\u7684\u59ff\u52bf\u3002 \u3000\u3000\u4ed6\u7981\u4e0d\u4f4f\u5168\u8eab\u751f\u51fa\u5bd2\u610f\uff01 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u7684\u5c38\u4f53\u5230\u90a3\u91cc\u53bb\u4e86.\u90a3\u4eba\u62ff\u53bb\u4ed6\u7684\u5c38\u4f53\uff0c\u6709\u4f55\u7528\u610f\uff01\u82e5\u8bf4\u4ed6\u7684\u5c38\u4f53\u4e0d\u662f\u88ab \u4eba\u62ff\u8d70\uff0c\u90a3\u4e48\u2014\u2014\u201d \u3000\u3000\u4ed6\u53c8\u8d77\u4e86\u4e00\u9635\u609a\u6817\uff0c\u4e0d\u60f3\u518d\u5f80\u4e0b\u60f3\u3002 \u3000\u3000\u6447\u66f3\u800c\u5fae\u5f31\u7684\u706b\u7130\u4e4b\u5149\uff0c\u7167\u7740\u5999\u624b\u8bb8\u767d\u7684\u5c38\u4f53\uff0c\u548c\u5730\u4e0a\u7684\u8840\u6e0e\uff0c\u7ed9\u8fd9\u672c\u5c31\u9634\u68ee\u7684\u6d1e\u7a9f\uff0c \u66f4\u6dfb\u4e86\u51e0\u5206\u9634\u68ee\u548c\u6050\u6016\uff01 \u3000\u3000\u4f0a\u98ce\u671b\u7740\u5730\u4e0a\u7684\u8840\uff0c\u518d\u60f3\u5230\u65b9\u624d\u6240\u89c1\u7684\u90a3\u4e24\u6ef4\u8840\u6e0d\uff0c\u518d\u4e5f\u4e0d\u6562\u5728\u8fd9\u6d1e\u7a9f\u91cc\u803d\u4e0b\u53bb\u4e86\u3002\u4e00 \u8f6c\u8eab\uff0c\u98de\u4e00\u6837\u5730\u63a0\u51fa\u6d1e\u53bb\u3002\u6d1e\u5916\u7684\u5929\u8272\uff0c\u6bd4\u4ed6\u5165\u6d1e\u65f6\u5f77\u4f5b\u9eef\u5f97\u591a\u4e86\u3002\u5fae\u98ce\u5439\u8fc7\uff0c\u98d2\u7136\u4f5c\u54cd\uff0c \u5439\u7740\u4f0a\u98ce\u7684\u8863\u8882\uff0c\u4ed6\u6253\u4e86\u4e2a\u5bd2\u6218\u3002\u76ee\u5149\u52a8\u5904\uff01\u5fc3\u4e2d\u4e0d\u7981\u53c8\u5403\u4e86\u4e00\u60ca\uff01 \u3000\u3000\u548c\u4ed6\u4e00\u9f50\u6765\u7684\u6e29\u534e\uff0c\u6b64\u65f6\u7adf\u7a81\u5730\u4e0d\u77e5\u53bb\u5411\u3002\u4ed6\u5fc3\u4e2d\u4e00\u51db\uff0c\u5230\u77f3\u5ba4\u7a97\u65c1\uff0c\u5411\u5185\u4e00\u770b\uff0c\u8d76\u7d27 \u56de\u8eab\u63a9\u76ee\uff0c\u4e0d\u5fcd\u518d\u770b\u3002 \u3000\u3000\u6e29\u6bd5\u7adf\u50f5\u5367\u5728\u77f3\u5ba4\u91cc\uff0c\u800c\u4ed6\u8eab\u7554\uff0c\u7adf\u6709\u4e00\u6ee9\u8840\u6e0d\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u5fc3\u4e2d\uff0c\u6ee1\u88ab\u6050\u6016\u6240\u636e\uff0c\u5df2\u8fde\u51b7\u9759\u601d\u8003\u7684\u80fd\u529b\uff0c\u90fd\u6ca1\u6709\u4e86\uff01 \u3000\u3000\u8fd9\u5374\u4e5f\u96be\u602a\u4ed6\uff0c\u4efb\u4f55\u4eba\u5904\u4e8e\u6b64\u60c5\u6b64\u666f\uff0c\u4e5f\u4f1a\u5413\u715e\uff01\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u6697\u60b8\uff0c\u7a81\u5730\u8eab\u540e\u4f20\u51fa\u4e00\u58f0 \u9634\u68ee\u4e4b\u6781\u7684\u51b7\u7b11\u3002 \u3000\u3000\u4ed6\u56de\u5934\u4e00\u770b\uff0c\u53cc\u76ee\u4e00\u9635\u6655\u7729\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u9a87\u6781\u800c\u547c \u3000\u3000\u539f\u6765\u4ed6\u7684\u8eab\u540e\uff0c\u50f5\u7acb\u7740\u4e00\u4e2a\u5168\u8eab\u8840\u6e0d\u7684\u4eba\uff0c\u76ee\u4e2d\u795e\u5149\u70af\u7136\u5374\u6b63\u662f\u4f0a\u98ce\u4eb2\u773c\u770b\u7740\u8eab\u53d7\u4e24\u5904 \u4e0d\u6cbb\u4e4b\u4f24\uff0c\u5df2\u7ecf\u6b7b\u53bb\u7684\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u6b7b\u4eba\u590d\u6d3b>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u6b7b\u4eba\u590d\u6d3b \u3000\u3000\u4f0a\u98ce\u56de\u5934\u4e00\u770b\uff0c\u987f\u65f6\u4ed6\u7684\u8840\u6db2\u548c\u9aa8\u9ad3\uff0c\u90fd\u50cf\u662f\u51dd\u7ed3\u4f4f\u4e86 \u3000\u3000\u5728\u4ed6\u540e\u9762\u53d1\u51fa\u9634\u68ee\u7684\u7b11\u58f0\u7684\uff0c\u6b63\u662f\u4ed6\u81ea\u5df1\u4eb2\u773c\u76ee\u7779\uff0c\u90a3\u5df2\u5728\u201c\u6b66\u66f2\u661f\u541b\u201d\u79d8\u85cf\u7684\u6d1e\u7a9d \u91cc\uff0c\u88ab\u5999\u624b\u8bb8\u767d\u4ee5\u91cd\u624b\u6cd5\u529b\u521b\u524d\u80f8\u548c\u54bd\u5589\uff0c\u5df2\u7ecf\u6beb\u65e0\u7591\u95ee\u5730\u6b7b\u53bb\u4e86\u7684\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u3002\u4f0a \u98ce\u7528\u529b\u631f\u4e86\u631f\u81ea\u5df1\u7684\u773c\u775b\uff0c\u66ae\u8272\u867d\u5df2\u4e34\uff0c\u4f46\u5927\u5730\u4ecd\u4e0d\u66fe\u5b8c\u5168\u9ed1\u6697\uff0c\u800c\u4ed6\u81ea\u4fe1\u81ea\u5bb6\u7684\u76ee\u529b\uff0c\u4e5f \u7edd\u4e0d\u81f4\u53d1\u751f\u773c\u82b1\u7684\u73b0\u8c61\u3002 \u3000\u3000\u90a3\u4e48\u8fd9\u5df2\u7ecf\u6b7b\u53bb\u4e86\u7684\u4e07\u5929\u840d\uff0c\u6b64\u523b\u53c8\u600e\u4f1a\u7ad9\u5728\u4ed6\u773c\u524d\u5462\uff1f \u3000\u3000\u4e07\u5929\u840d\u6ee1\u8eab\u3002\u90fd\u6cbe\u67d3\u7740\u9c9c\u660e\u7684\u8840\u8ff9\uff0c\u4ed6\u90a3\u67af\u7626\u7684\u9762\u5b54\uff0c\u5728\u8840\u8ff9\u4e4b\u540e\u5448\u73b0\u7740\u4e00\u79cd\u5f02\u6837\u7684\u9634 \u68ee\uff0c \u3000\u3000\u4ed6\u7684\u7b11\u58f0\uff0c\u5728\u6e05\u5bd2\u7684\u591c\u98ce\u4e2d\u6269\u6563\u7740\uff0c\u58f0\u6ce2\u8fdc\u8fdc\u5730\u4f20\u5230\u8fd9\u5c71\u5773\u7684\u56db\u58c1\uff0c\u53c8\u53cd\u9707\u4e86\u56de\u6765\uff0c\u9707 \u8361\u7740\u4e00\u9635\u9635\u4ee4\u4eba\u60ca\u51bb\u7684\u4f59\u97f3\u3002 \u3000\u3000\u8fd9\u672c\u5df2\u9634\u51b7\u68ee\u5bd2\u7684\u5c71\u5773\uff0c\u66f4\u50cf\u662f\u66f4\u62b9\u4e86\u4e0a\u96be\u4ee5\u5f62\u5bb9\u7684\u6050\u6016\u8272\u5f69\uff0c\u4ece\u4e0a\u9762\u5954\u6d41\u800c\u4e0b\u7684\u6c34 \u58f0\uff0c\u6b64\u65f6\u4e5f\u50cf\u662f\u53d8\u6210\u4e86\u7785\u7785\u9b3c\u54bd\u3002 \u3000\u3000\u5c31\u5728\u4f0a\u98ce\u76ee\u5149\u63a5\u89e6\u5230\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4f0a\u98ce\u7684\u4e07\u5343\u611f\u89c9\uff0c\u5374\u500f\u7136\u505c\u987f\u4f4f \u4e86\uff0c\u65e0\u52a9\u5730\u56de\u590d\u5230\u5343\u4e07\u5e74\u4ee5\u524d\uff0c\u4eba\u7c7b\u5728\u539f\u59cb\u65f6\u4ee3\u6240\u5177\u6709\u7684\u90a3\u79cd\u6050\u6016\u7684\u611f\u89c9\u91cc\u53bb\u3002 \u3000\u3000\u4e07\u5929\u840d\u7684\u7b11\u58f0\u672a\u7edd\uff01 \u3000\u3000\u5e26\u7740\u8fd9\u79cd\u9707\u4eba\u5fc3\u8151\u7684\u7b11\u58f0\uff0c\u4ed6\u7f13\u7f13\u5730\uff0c\u4e00\u6b65\u6b65\u5730\u5411\u4f0a\u98ce\u8d70\u4e86\u8fc7\u53bb\uff0c\u76ee\u4e2d\u6151\u4eba\u7684\u5149\u8292\uff0c\u4e5f \u50cf\u662f\u9b3c\u9b45\u822c\u90a3\u4e48\u5c16\u9510\u548c\u65e0\u60c5\u3002 \u3000\u3000\u4ed6\u9634\u68ee\u5730\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u4f60\u53c8\u56de\u6765\u5566\uff01\u597d\u6781\u4e86\u2026\u2026\u201d \u3000\u3000\u4f0a\u98ce\u5df2\u65e0\u6cd5\u5206\u8fa8\u4ed6\u7684\u8bed\u58f0\u662f\u50cf\u4eba\u7c7b\u822c\u5730\u53d1\u81ea\u4e39\u7530\uff0c\u6291\u6216\u662f\u90a3\u79cd\u51c4\u9634\u7684\u9b3c\u8bed\u3002\u4ed6\u7684\u8eab\u5f62\uff0c \u4e0d\u81ea\u89c9\u5730\u968f\u7740\u4e07\u5929\u840d\u7684\u6765\u52bf\uff0c\u800c\u4e00\u6b65\u6b65\u5411\u540e\u9762\u9000\u7740\u2014\u2014 \u3000\u3000\u4ed6\u7684\u76ee\u5149\uff0c\u751f\u50cf\u662f\u88ab\u4e00\u79cd\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\u6240\u5438\u5f15\u7740\u4f3c\u7684\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u77aa\u5728\u94c1\u9762\u5b64\u884c\u5ba2\u7684 \u8eab\u4e0a\uff0c\u76ee\u5149\u4e2d\u6240\u5448\u73b0\u7684\u90a3\u79cd\u60ca\u60b8\u4e4b\u6001\uff0c\u4f7f\u5f97\u4e07\u5929\u840d\u90a3\u79cd\u9634\u68ee\u51c4\u5389\u7684\u7b11\u58f0\uff0c\u8d8a\u53d1\u663e\u7740\u4e86\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4ed6\u611f\u89c9\u5230\u8eab\u540e\u5c31\u662f\u90a3\u77f3\u5c4b\u7684\u77f3\u58c1\uff0c\u4ed6\u77e5\u9053\u5df2\u65e0\u6cd5\u518d\u5411\u540e\u9762\u9000\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u90a3\u79cd\u548c\u8fd9\u9b3c\u9b45\u4f3c\u7684\u4e07\u5929\u840d\uff0c\u5c06\u8981\u9010\u6e10\u63a5\u8fd1\u7684\u6050\u6016\u4e4b\u610f\uff0c\u66f4\u50cf\u56db\u5468\u5c71\u5cb3\u7684\u9634\u5f71\u822c\uff0c\u7d27 \u7d27\u538b\u5728\u4ed6\u672c\u5df2\u609a\u6807\u7684\u5fc3\u623f\u4e0a\u3002 \u3000\u3000\u8fd9\u79cd\u6050\u6016\u7684\u611f\u89c9\uff0c\u4e0d\u53ef\u601d\u8bae\u5730\u4f7f\u5f97\u8fd9\u8eab\u6000\u7edd\u6280\uff0c\u800c\u6c5f\u6e56\u5386\u7ec3\u4e5f\u5f02\u5e38\u4e30\u5bcc\u7684\u4f0a\u98ce\uff0c\u7adf\u5931\u53bb \u4e86\u62b5\u6297\uff0c\u751a\u6216\u662f\u9003\u907f\u7684\u529b\u91cf\uff0c\u800c\u53ea\u662f\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u9759\u5f85\u7740\u4e07\u5929\u840d\u4e00\u6b65\u6b65\u5411\u4ed6\u884c\u8fd1 \u3000\u3000\u968f\u7740\u4e07\u5929\u840d\u7684\u811a\u6b65\uff0c\u7a7a\u6c14\u4e2d\u7684\u6bcf\u4e00\u77ac\u606f\uff0c\u90fd\u50cf\u662f\u94c1\u69cc\u822c\u5730\u6572\u5728\u4f0a\u98ce\u8eab\u4e0a\uff0c\u4ed6\u60ca\u6050\u5730\u53d1\u89c9 \u81ea\u5df1\u7684\u56db\u80a2\u6709\u9ebb\u75f9\u7684\u611f\u89c9\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u5df2\u7f29\u77ed\u5f97\u53ea\u5269\u4e0b\u5e38\u4eba\u7684\u4e03\uff0c\u516b\u6b65\u4e86\uff0c\u800c\u50cf\u4ed6\u4eec\u8fd9\u6837\u6b66\u6797\u9ad8\u624b\uff0c \u81ea\u7136\u8f7b\u8f7b\u4e00\u63a0\uff0c\u4fbf\u4f38\u624b\u53ef\u53ca\u3002 \u3000\u3000\u4e07\u5929\u840d\u679c\u7136\u7f13\u7f13\u4f38\u51fa\u624b\u6765\uff0c\u4ed6\u7684\u624b\u4e0a\u4e5f\u6cbe\u6ee1\u4e86\u8840\u8ff9\u3002 \u3000\u3000\u8fd9\u66fe\u4ee5\u9e70\u722a\u529f\u9707\u70c1\u6b66\u6797\u7684\u8c6a\u5ba2\uff0c\u6b64\u523b\u5374\u662f\u4ee5\u624b\u4e0a\u7684\u8840\u8ff9\u9707\u60b8\u7740\u4f0a\u98ce\u7684\u5fc3\u3002 \u3000\u3000\u4ed6\u90a3\u67af\u7626\u7684\u624b\u638c\uff0c\u4e00\u88ab\u8840\u8ff9\u6cbe\u6ee1\uff0c\u66f4\u4e0e\u9b3c\u722a\u4f55\u5f02\uff01 \u3000\u3000\u7a81\u5730\uff0c\u4e07\u5929\u840d\u7684\u7b11\u58f0\u621b\u7136\u800c\u6b62\u3002 \u3000\u3000\u4e8e\u662f\u7eb5\u7136\u6709\u5954\u6d41\u7684\u6c34\u58f0\uff0c\u56db\u5468\u4e5f\u987f\u65f6\u53d8\u5f97\u6b7b\u6837\u7684\u9759\u5bc2\u3002 \u3000\u3000\u4f0a\u98ce\u52aa\u529b\u5730\u652f\u6301\u81ea\u5df1\u7684\u8eab\u8eaf\uff0c\u7136\u800c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5168\u8eab\u90fd\u83ab\u540d\u5176\u5999\u5730\u50f5\u786c\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u53ea\u8981\u4e07\u5929\u840d\u8f7b\u8f7b\u4e00\u63a0\uff0c\u4ed6\u4fbf\u5f97\u7acb\u65f6\u4f24\u5728\u5782\u540d\u6b66\u6797\u7684\u94c1\u9762\u5b64\u884c\u5ba2\u90a3\u53cc\u6467\u91d1\u94c1\u5982\u67af\u673d\u7684 \u94c1\u638c\u4e4b\u4e0b\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u662f\u4e00\u77ac\u95f4\u4fbf\u53ef\u89e3\u51b3\u7684\u4e8b\uff0c\u53ea\u662f\u8fd9\u4e00\u77ac\u95f4\u5728\u4f0a\u98ce\u770b\u6765\uff0c\u5374\u6709\u5982\u65e0\u5c3d\u671f\u7684\u6f2b\u957f\u7f62\u4e86\u3002 \u3000\u3000\u4eba\u4e16\u95f4\u7684\u4e8b\uff0c\u6709\u65f6\u662f\u96be\u4ee5\u89e3\u91ca\u7684\u3002 \u3000\u3000\u4f46\u5c31\u5728\u4f0a\u98ce\u4e3a\u7ec8\u5357\u5f1f\u5b50\u6c42\u547d\uff0c\u8fdc\u8d74\u6ec7\u4e2d\u65e0\u91cf\u5c71\uff0c\u800c\u9047\u7740\u8fd9\u7b49\u5947\u4e8b\uff0c\u662f\u4ee5\u9677\u5165\u6b7b\u4ea1\u7684\u6050\u6016 \u4e2d\u7684\u540c\u4e00\u65f6\u95f4\u5185\uff0c\u7ec8\u5357\u5c71\u4e00\u606f\u5782\u5371\u7684\u6570\u767e\u5f1f\u5b50\uff0c\u5374\u4ece\u6b7b\u4ea1\u7684\u6050\u60e7\u4e2d\uff0c\u500f\u7136\u9003\u9038\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u79bb\u5f00\u4e86\u7ec8\u5357\u5c71\u540e\uff0c\u7ec8\u5357\u9053\u9662\u4e2d\u7684\u6bcf\u4e00\u4e2a\u4eba\uff0c\u9664\u4e86\u7b49\u5f85\u4e4b\u5916\uff0c\u5c31\u522b\u65e0\u9009\u62e9\u3002 \u3000\u3000\u7b49\u5f85\uff0c\u8fd9\u5728\u522b\u4eba\u6765\u8bf4\uff0c\u4e5f\u8bb8\u662f\u7ecf\u5e38\u80fd\u6709\u7684\u7ecf\u9a8c\uff1b\u7136\u800c\u5728\u5251\u5148\u751f\u548c\u4e09\u5fc3\u795e\u541b\u6765\u8bf4\uff0c\u8fd9\u5c31\u662f \u4e00\u79cd\u65b0\u5947\u7684\u4f53\u9a8c\u4e86\u3002 \u3000\u3000\u4e07\u5251\u4e4b\u5c0a\u548c\u4e09\u5fc3\u795e\u541b\uff0c\u6570\u5341\u5e74\u524d\u4fbf\u4ee5\u7edd\u4e16\u795e\u529f\u540d\u6ee1\u5929\u4e0b\uff0c\u81f3\u4eca\u66f4\u5df2\u8fd1\u4e0d\u574f\u4e4b\u8eab\u3002\u4ee5\u4ed6\u4eec \u7684\u81ea\u8eab\u529f\u529b\u800c\u8a00\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u7edd\u5c11\u6709\u4ed6\u4eec\u4e0d\u80fd\u505a\u5230\u7684\u4e8b\uff0c\u662f\u4ee5\u4ed6\u4eec\u4fbf\u6839\u672c\u4e0d\u9700\u7b49\u5f85\u3002\u800c\u6b64 \u523b\uff0c\u8fd9\u4e24\u4e2a\u6b66\u6797\u5947\u4eba\uff0c\u5374\u906d\u9047\u5230\u524d\u6240\u672a\u9047\u7684\u56f0\u96be\u4e86\uff01 \u3000\u3000\u8fd9\u5e9e\u5927\u7684\u9053\u89c2\u6bcf\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u90fd\u8ff7\u6162\u7740\u51c4\u51c9\u7684\u6c14\u606f\u3002 \u3000\u3000\u51e0\u4e4e\u6bcf\u4e00\u5929\uff0c\u8fd9\u9053\u89c2\u91cc\uff0c\u4fbf\u5f97\u6dfb\u4e0a\u51e0\u6761\u51a4\u5c48\u800c\u6b7b\u7684\u4eba\u547d\u3002\u800c\u675f\u624b\u65e0\u7b56\u7684\u7ec8\u5357\u638c\u95e8\u7384\u95e8\u4e00 \u9e64\uff0c\u5374\u53ea\u5f97\u4efb\u51ed\u8fd9\u4e9b\u5c38\u4f53\u505c\u7559\u5728\u4e39\u623f\u91cc\u3002 \u3000\u3000\u4e8e\u662f\u6bcf\u5468\u4e00\u5929\uff0c\u8fd9\u6b66\u6797\u540d\u6d3e\u4e4b\u4e00\u7684\u7ec8\u5357\u6d3e\u7684\u53d1\u7965\u5730\uff0c\u4fbf\u66f4\u589e\u52a0\u4e86\u51e0\u5206\u51c4\u51c9\u548c\u60b2\u54c0\u7684\u6c14 \u606f\u3002 \u3000\u3000\u5251\u5148\u751f\u548c\u4e09\u5fc3\u795e\u541b\u5728\u540e\u56ed\u4e2d\u7684\u4e00\u4e2a\u5c71\u4ead\u91cc\uff0c\u5782\u9996\u5bf9\u5955\u3002\u4f46\u662f\u4e0d\u53ef\u5426\u8ba4\u7684\uff0c\u4ed6\u4eec\u7684\u5fc3\u601d\uff0c \u8c01\u4e5f\u4e0d\u80fd\u4e13\u6ce8\u5728\u68cb\u76d8\u4e4b\u4e0a\u3002 \u3000\u3000\u51cc\u7433\u7684\u4f24\u52bf\uff0c\u4e5f\u5728\u6e10\u6e10\u75ca\u6108\u4e4b\u4e2d\uff0c\u5979\u9192\u6765\u540e\u6240\u89c1\u7684\u4e8b\uff0c\u81ea\u7136\u4ee4\u5979\u975e\u5e38\u60ca\u5f02\u548c\u5947\u602a\uff0c\u4e8e\u662f \u5979\u7684\u6bcd\u4eb2\u5c31\u6e05\u695a\u5730\u544a\u8bc9\u4e86\u5979\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u5e74\u5e7c\u800c\u806a\u660e\u7684\u5973\u5b69\u5b50\uff0c\u5374\u4e1d\u6beb\u4e0d\u611f\u6fc0\u4f0a\u98ce\u3002\u5979\u7684\u60f3\u6cd5\u662f\uff1a\u82e5\u6ca1\u6709\u4f0a\u98ce\uff0c\u90a3\u201c\u593a\u547d \u53cc\u96c4\u201d\u600e\u4f1a\u9047\u7740\u81ea\u5df1\uff01 \u3000\u3000\u4e8e\u662f\u5b59\u654f\u65e0\u8a00\u4e86\uff0c\u5979\u5bf9\u5979\u8fd9\u7cbe\u7075\u53e4\u602a\u7684\u5973\u513f\uff0c\u9664\u4e86\u7231\u62a4\u4e4b\u5916\uff0c\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\uff01 \u3000\u3000\u51cc\u7433\u5f53\u7136\u4e5f\u5e86\u5e78\u81ea\u5df1\u80fd\u9047\u7740\u8fd9\u4e24\u4f4d\u5947\u4eba\uff0c\u4e5f\u5bf9\u4eba\u5bb6\u6df1\u4e3a\u75bc\u6fc0\u3002 \u3000\u3000\u5979\u4f24\u52bf\u867d\u6e10\u6108\uff0c\u5374\u4ecd\u7136\u884c\u52a8\u4e0d\u5f97\uff0c\u53ea\u5f97\u7559\u5728\u90a3\u95f4\u4e39\u623f\u7684\u4e91\u5e8a\u4e0a\u3002 \u3000\u3000\u5979\u5e74\u7eaa\u867d\u5e7c\uff0c\u53ef\u662f\u5df2\u9971\u7ecf\u5fe7\u60a3\u3002\u5728\u5979\u90a3\u5df2\u63a5\u8fd1\u6210\u719f\u7684\u5934\u8111\u91cc\uff0c\u7ec8\u65e5\u65cb\u8f6c\u7740\u4e00\u4e9b\u5728\u5979\u8fd9\u79cd \u5e74\u7eaa\u91cc\u7684\u522b\u7684\u5973\u5b69\u5b50\u6240\u65e0\u6cd5\u60f3\u5230\u7684\u4e8b\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff1a\u5979\u5bf9\u90a3\u6c89\u9ed8\u5be1\u8a00\u7684\u7384\u95e8\u9053\u8005\u2014\u2014\u7ec8\u5357\u638c\u95e8\u5999\u7075\u9053\u4eba\uff0c\u4ece\u7b2c\u4e00\u773c\u89c1\u5230\u9762\u65f6\uff0c\u5c31 \u8d77\u4e86\u4e00\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u6076\u611f\u3002\u8fd9\u79cd\u6076\u611f\u7684\u6765\u6e90\uff0c\u662f\u65e0\u6cd5\u89e3\u91ca\u7684\uff0c\u53ea\u662f\u51fa\u4e8e\u5979\u7684\u672c\u80fd\u800c\u5df2\u3002 \u3000\u3000\u5b59\u654f\u9664\u4e86\u5230\u90a3\u5c0f\u4ead\u4e2d\u7167\u5e94\u5251\u5148\u751f\u548c\u4e09\u5fc3\u795e\u541b\u4e4b\u5916\uff0c\u5c31\u5728\u90a3\u95f4\u4e39\u623f\u91cc\u966a\u4f34\u7740\u5979\u7684\u7231\u5973\uff1b\u5979 \u7684\u5fc3\uff0c\u5374\u53ef\u601c\u5730\u88ab\u5272\u6210\u4e09\u4e2a\uff01 \u3000\u3000\u9664\u4e86\u5bf9\u7231\u5973\u7684\u7231\u62a4\u548c\u5bf9\u5f80\u4e8b\u7684\u601d\u5ff5\u4e4b\u5916\uff0c\u8fd9\u547d\u8fd0\u591a\u8e47\u7684\u5987\u4eba\uff0c\u6b64\u523b\u66f4\u591a\u4e86\u4e00\u5206\u7b49\u5f85\u548c\u7126 \u6025\uff0c\u4e5f\u591a\u4e86\u4e00\u4efd\u96be\u8a00\u7684\u60c5\u611f\u3002 \u3000\u3000\u5979\u7684\u7b49\u5f85\u548c\u7126\u6025\uff0c\u5f53\u7136\u662f\u4e3a\u7740\u4f0a\u98ce\u3002\u5979\u83ab\u540d\u5176\u5999\u5730\u5bf9\u90a3\u5e74\u8f7b\u4eba\u6709\u4e86\u597d\u611f\uff0c\u7126\u8651\u4ed6\u6b64\u884c\u80fd \u5426\u6210\u529f\uff0c\u7b49\u5f85\u4ed6\u65e9\u4e9b\u56de\u6765\u3002 \u3000\u3000\u4f46\u662f\u5979\u7684\u8fd9\u4efd\u7b49\u5f85\u548c\u7126\u6025\uff0c\u662f\u53ef\u4ee5\u89e3\u91ca\u7684\uff0c\u56e0\u4e3a\u5979\u5728\u7167\u6599\u7740\u4f0a\u98ce\u4f24\u91cd\u7684\u90a3\u4e00\u6bb5\u65f6\u95f4\u65f6\uff0c \u5979\u7684\u5fc3\u4e2d\uff0c\u5df2\u5c06\u4f0a\u98ce\u548c\u5979\u7684\u7231\u5973\uff0c\u653e\u5728\u540c\u4e00\u4f4d\u7f6e\u3002 \u3000\u3000\u4f46\u662f\u5979\u5bf9\u5251\u5148\u751f\u7684\u90a3\u4e00\u4efd\u60c5\u611f\uff0c\u5374\u662f\u4e0d\u80fd\u89e3\u91ca\u7684\u4e86\u3002\u5979\u5f53\u7136\u4e5f\u77e5\u9053\uff1a\u81ea\u5df1\u65e0\u8bba\u5728\u90a3\u4e00\u65b9 \u9762\uff0c\u90fd\u548c\u4eba\u5bb6\u76f8\u5dee\u5f97\u592a\u8fdc\uff1b\u5979\u4e5f\u77e5\u9053\uff1a\u8fd9\u770b\u6765\u867d\u4f3c\u4e2d\u5e74\u4eba\u7684\u5251\u5148\u751f\uff0c\u5b9e\u9645\u7684\u5e74\u9f84\u6050\u6015\u5df2\u8fdc\u5728 \u53e4\u7a00\u4e4b\u4e0a\u3002 \u3000\u3000\u53ef\u662f\u5979\u90a3\u4e00\u9897\u4e45\u65e0\u5bc4\u6258\u7684\u82b3\u5fc3\uff0c\u6b64\u523b\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u653e\u5728\u4eba\u5bb6\u8eab\u4e0a\u3002\u53ea\u8981\u80fd\u5f97\u5230\u4eba\u5bb6\u7684\u8f7b \u8f7b\u4e00\u987e\uff0c\u5979\u5c31\u6709\u65e0\u6bd4\u7684\u751c\u871c\uff01 \u3000\u3000\u8fd9\u4e9b\uff0c\u5f53\u7136\u90fd\u662f\u5979\u5fc3\u5e95\u7684\u79d8\u5bc6\u3002\u5979\u5c06\u8fd9\u4efd\u79d8\u5bc6\uff0c\u6df1\u6df1\u9690\u85cf\u8d77\u6765\uff0c\u5728\u5979\u9762\u5bf9\u7740\u7231\u5973\u7eaf\u771f\u800c \u7f8e\u4e3d\u7684\u9762\u5b54\u65f6\uff0c\u5979\u5374\u53c8\u4f1a\u4e3a\u4e86\u81ea\u5df1\u7684\u8fd9\u4efd\u79d8\u5bc6\uff0c\u89c9\u5230\u60ed\u6127\u3002 \u3000\u3000\u53ef\u662f\u51cc\u7433\u5728\u542c\u4e86\u5979\u6bcd\u4eb2\u6240\u8bf4\u7684\u201c\u5929\u6bd2\u6559\u201d\u65bd\u6bd2\u4e4b\u4e8b\u4ee5\u540e\uff0c\u5374\u8001\u662f\u4e0d\u505c\u5730\u95ee\u7740\u4e9b\u95ee\u9898\uff0c\u800c \u8fd9\u4e9b\u95ee\u9898\uff0c\u5374\u4f7f\u5f97\u5b59\u654f\u7adf\u4e5f\u5fd8\u8bb0\u4e86\u5979\u5fc3\u4e2d\u60c5\u611f\u7684\u7eb7\u6270\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u91cd\u91cd\u7591\u7aa6>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u91cd\u91cd\u7591\u7aa6 \u3000\u3000\u51cc\u7433\u7b2c\u4e00\u4e2a\u63d0\u51fa\u7684\u95ee\u9898\u662f \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u7ec8\u5357\u5c71\u4e0a\u7684\u9053\u58eb\uff0c\u5168\u662f\u5403\u4e86\u91cc\u9762\u542b\u6709\u201c\u8680\u9aa8\u5723\u6c34\u201d\u7684\u6cc9\u6c34\u800c\u4e2d\u6bd2\u7684\u4e86\u3002\u90a3\u4e48\u6211 \u4eec\u5403\u7684\uff0c\u662f\u4e0d\u662f\u4e5f\u662f\u90a3\u6cc9\u6c34\u5462\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u5b59\u654f\u53ef\u4ee5\u7b54\u8986\u5728\u4ed6\u4eec\u6765\u6b64\u4e4b\u540e\uff0c\u5251\u5148\u751f\u5c31\u53eb\u5999\u7075\uff0c\u8fdc\u5230\u540e\u5c71\u7684\u53e6\u4e00\u4e2a\u6c34\u6cc9\u5904\u53d6\u6765 \u98df\u6c34\uff0c\u4e3a\u7684\u81ea\u7136\u662f\u907f\u514d\u4e2d\u6bd2\u4e86\u3002 \u3000\u3000\u53ef\u662f\u51cc\u7433\u53c8\u95ee \u3000\u3000\u201c\u7ec8\u5357\u5c71\u9053\u4eba\u4eec\u5e73\u65e5\u98df\u7528\u7684\u6c34\uff0c\u82e5\u662f\u4ece\u5c71\u6cc9\u4e2d\u53d6\u6765\u7684\uff0c\u90a3\u4ed6\u4eec\u5c31\u4e0d\u53ef\u80fd\u5168\u90e8\u4e2d\u6bd2\u4e86\uff0c\u56e0 \u4e3a\u5c71\u6cc9\u662f\u5f80\u4e0b\u6d41\u7684\u5440\uff0c\u90a3\u4e48\u6709\u6bd2\u7684\u6c34\uff0c\u5c31\u4e0d\u53ef\u80fd\u6c38\u8fdc\u505c\u7559\u5728\u4ed6\u4eec\u53d6\u6c34\u7684\u5730\u65b9\u4e0d\u52a8\uff0c\u6240\u4ee5\u82e5\u662f \u8bf4\u201c\u5929\u6bd2\u6559\u201d\u6240\u4e0b\u7684\u6bd2\uff0c\u662f\u4e0b\u5728\u5c71\u6cc9\u91cc\uff0c\u90a3\u5c31\u7edd\u4e0d\u53ef\u80fd\uff0c\u9664\u975e\u662f\u7ec8\u5357\u9053\u4eba\u4eec\u5df2\u5c06\u5c71\u6cc9\u6c72\u6765\u9053 \u89c2\u540e\u518d\u4e0b\u7684\u6bd2\uff0c\u624d\u50cf\u8bdd\u4e9b\u3002\u201d \u3000\u3000\u5b59\u654f\u5fae\u4e00\u6c89\u541f\uff0c\u53ea\u5f97\u540c\u610f\u5979\u5973\u513f\u7684\u8bf4\u6cd5\uff0c\u5fae\u5fae\u70b9\u7740\u5934\u3002 \u3000\u3000\u51cc\u7433\u4e24\u53ea\u660e\u5a9a\u7684\u773c\u73e0\u4e00\u8f6c\uff0c\u7406\u4e86\u7406\u9b13\u8fb9\u7684\u4e71\u53d1\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u7ec8\u5357\u5c71\u7684\u90a3\u4e48\u591a\u9053\u4eba\u662f\u98df\u7528\u540c\u4e00\u79cd\u6c34\uff0c\u4e2d\u6bd2\u6709\u5148\u540e\uff0c\u90a3\u8fd8\u53ef\u4ee5\u8bf4\u662f\u56e0\u4e3a\u529f\u529b\u6709\u6df1\u6d45\u4e0d \u540c\uff1b\u53ef\u662f\u90a3\u7ec8\u5357\u638c\u95e8\u5374\u672a\u4e2d\u6bd2\uff0c\u5374\u6709\u4e9b\u4e0d\u901a\u4e86\u3002\u96be\u9053\u5929\u6bd2\u6559\u91cc\u7684\u4eba\u4f1a\u9690\u8eab\u6cd5\uff0c\u80fd\u795e\u4e0d\u77e5\u9b3c\u4e0d \u89c9\u5730\u5728\u4ed6\u5403\u7684\u6c34\u91cc\u5148\u653e\u4e0b\u4e9b\u89e3\u836f\uff0c\u8fd9\u6709\u70b9\u4e0d\u5927\u53ef\u80fd\u5427\uff01\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u5979\u7a81\u7136\u505c\u4f4f\u8bdd\uff0c\u773c\u775b\u77aa\u7740\u95e8\uff1b\u5b59\u654f\u5374\u6ca1\u6709\u6ce8\u610f\u5230\uff0c\u5fc3\u4e2d\u5728\u601d\u5fd6\u7740\u5979\u5973\u513f\u7684\u89c1\u89e3\uff0c\u4e5f\u8ba4\u4e3a \u6b64\u4e8b\u5176\u4e2d\u6709\u8bb8\u591a\u53ef\u7591\u4e4b\u5904\u3002 \u3000\u3000\u51cc\u7433\u7a81\u7136\u9053\uff1a \u3000\u3000\u201c\u5988\uff01\u4f60\u51fa\u53bb\u770b\u770b\uff0c\u95e8\u5916\u9762\u50cf\u662f\u6709\u4eba\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u5b59\u654f\u4e00\u6014\uff0c\u968f\u5373\u8eab\u5f62\u4e00\u52a8\uff0c\u63a8\u95e8\u800c\u671b\uff0c\u95e8\u5916\u53ea\u6709\u98ce\u58f0\u98d2\u7136\uff0c\u5374\u65e0\u4eba\u5f71\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5fae\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u773c\u775b\u82b1\u4e86\u7f62\uff0c\u5916\u9762\u90a3\u91cc\u6709\u4eba\uff1f\u201d \u3000\u3000\u51cc\u7433\u5374\u6447\u4e86\u6447\u5934\uff0c\u82e5\u6709\u6240\u601d\u5730\u671b\u7740\u4e39\u623f\u7684\u5c4b\u9876\uff0c\u50cf\u662f\u5728\u601d\u7d22\u4ec0\u4e48\u96be\u89e3\u7684\u95ee\u9898\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u6700\u82e6\u7684\u5374\u662f\u7384\u95e8\u4e00\u9e64\uff0c\u4ed6\u4ee5\u4e00\u6d3e\u638c\u95e8\u7684\u8eab\u4efd\uff0c\u6b64\u523b\u7adf\u505a\u8d77\u4f19\u5de5\u9053\u4eba\u6765\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u4ed6\u4f2a\u51cc\u7433\u716e\u4e86\u76c5\u53c2\u6c64\uff0c\u5b59\u654f\u611f\u6fc0\u5730\u8c22\u7740\u4ed6\u3002 \u3000\u3000\u51cc\u7433\u4e5f\u5a07\u7b11\u7740\uff0c\u5c06\u53c2\u6c64\u62ff\u4e86\u8fc7\u6765\uff0c\u53c8\u4e00\u7f29\u624b\uff0c\u53e3\u4e2d\u8bf4\uff1a\u201c\u597d\u70eb\u5440\uff01\u201d\u5c06\u90a3\u7897\u53c2\u6c64\u653e\u5728\u684c \u8fb9\u3002 \u3000\u3000\u5999\u7075\u9053\u4eba\u8138\u4e0a\u7684\u808c\u8089\u4e00\u95ea\uff0c\u7f13\u7f13\u8d70\u51fa\u95e8\u53bb\uff0c\u7709\u5934\u7d27\u7d27\u76b1\u5728\u4e00\u8d77\u3002\u8fd9\u4e24\u5929\u6765\uff0c\u8fd9\u5fe7\u90c1\u7684\u7384 \u95e8\u4e00\u9e64\u7684\u53cc\u7709\uff0c\u5c31\u672a\u66fe\u5f00\u6717\u8fc7\u3002 \u3000\u3000\u5728\u4ed6\u53d6\u53bb\u51cc\u7433\u684c\u8fb9\u7684\u7a7a\u7897\u65f6\uff0c\u51cc\u7433\u7684\u4f24\u52bf\uff0c\u5f77\u4f5b\u53c8\u8f6c\u5267\u4e86\uff0c\u4e0d\u4f4f\u5730\u547b\u541f\u7740\u3002\u4ed6\u524a\u8584\u7684\u53cc \u5507\u4e00\u52a8\uff0c\u5306\u5306\u5730\u5c06\u7a7a\u7897\u62ff\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5b59\u654f\u7acb\u523b\u4ece\u5c0f\u4ead\u4e2d\u8d76\u4e86\u8fc7\u6765\uff0c\u53c8\u6025\u5fd9\u8d76\u5230\u5c0f\u4ead\u4e2d\u5c06\u4e09\u5fc3\u795e\u541b\u8bf7\u4e86\u6765\u3002\u53ef\u662f\u7b49\u5230\u4e09\u5fc3\u795e\u541b \u4e3a\u51cc\u7433\u8bca\u65ad\u8fc7\u540e\uff0c\u5979\u5411\u4e09\u5fc3\u795e\u541b\u95ee\u7740\u51cc\u7433\u7684\u4f24\u52bf\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4f1a\u7a81\u7136\u52a0\u5267\u7684\u539f\u56e0\u65f6\uff0c\u4e09\u5fc3\u795e\u541b \u53ea\u662f\u6447\u5934\u4e0d\u8bed\uff0c\u8138\u4e0a\u5374\u5e26\u7740\u51b0\u5c71\u822c\u7684\u51b7\u68ee\u4e4b\u8272\u3002 \u3000\u3000\u5b59\u654f\u7684\u5fc3\u5f80\u4e0b\u6c89\uff0c\u51cc\u7433\u5374\u4f3c\u4e4e\u53c8\u9677\u5165\u660f\u8ff7\u4e4b\u4e2d\uff0c\u4e0d\u505c\u5730\u5453\u8bed\u7740\u3002\u4e09\u5fc3\u795e\u541b\u5374\u4ecd\u548c\u5251\u5148\u751f \u795e\u8272\u4e0d\u52a8\u5730\uff0c\u5c31\u7740\u591c\u8272\u5955\u7740\u68cb\u3002 \u3000\u3000\u5929\u8272\u66f4\u665a\u4e86\u3002\u867d\u7136\u6ca1\u6709\u66f4\u9f13\uff0c\u4f46\u63a8\u65ad\u65f6\u5019\uff0c\u5df2\u662f\u4e09\u66f4\u2014\u2014 \u3000\u3000\u4e00\u6761\u4eba\u5f71\u5728\u9053\u89c2\u7684\u7b2c\u4e09\u6392\u4e39\u623f\u7684\u540e\u9762\u884c\u8d70\u7740\uff0c\u4ed6\u85c9\u7740\u9634\u5f71\u85cf\u7740\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u884c\u52a8\u751a\u5feb\uff0c \u77ac\u606f\u4e4b\u95f4\uff0c\u5c31\u63a0\u5230\u4e86\u5899\u4e0b\u3002 \u3000\u3000\u5728\u4ed6\u4ece\u4e39\u623f\u540e\u7684\u9634\u5f71\uff0c\u63a0\u5230\u5899\u4e0b\u7684\u9634\u5f71\u95f4\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5c31\u7740\u5fae\u5f31\u7684\u5929\u5149\uff0c\u4f9d\u7a00\u9119\u4ee5\u770b \u51fa\uff0c\u8fd9\u4eba\u5f71\u7adf\u7136\u5c31\u662f\u7ec8\u5357\u638c\u95e8\u5999\u7075\u9053\u4eba\uff01 \u3000\u3000\u4ed6\u76ee\u5149\u56db\u987e\uff0c\u786e\u5b9a\u518d\u65e0\u4eba\u53d1\u73b0\u4ed6\u7684\u884c\u8e2a\uff0c\u5c31\u4f38\u51fa\u53f3\u624b\u4e24\u6307\uff0c\u5728\u5899\u4e0a\u8f7b\u8f7b\u5730\u5f39\u4e86\u4e09\u4e0b\uff0c\u7136 \u540e\u5c31\u5c06\u8033\u6735\u7d27\u7d27\u8d34\u5728\u5899\u4e0a\uff0c\u7559\u610f\u503e\u542c\u7740\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u5899\u7684\u90a3\u8fb9\u4e5f\u4f20\u6765\u4e09\u4e0b\u6781\u8f7b\u5fae\u7684\u5f39\u6307\u4e4b\u58f0\uff0c\u4ed6\u8138\u4e0a\u5fae\u5fae\u9732\u51fa\u559c\u8272\uff0c\u4f46\u662f\u8fd9\u4efd\u559c\u60a6 \u4e4b\u8272\uff0c\u4ecd\u4e0d\u80fd\u63a9\u9970\u4f4f\u4ed6\u7684\u60ca\u60e7\u548c\u4e0d\u5b89\u3002 \u3000\u3000\u8fdc\u5904\u7684\u623f\u9876\u4e0a\uff0c\u6709\u4e00\u6761\u8f7b\u6de1\u7684\u4eba\u5f71\u4e00\u95ea\uff0c\u90a3\u662f\u56e0\u4e3a\u8fd9\u4eba\u5f71\u901f\u5ea6\u592a\u5feb\uff0c\u5728\u591c\u8272\u4e2d\uff0c\u51e0\u4e4e\u4e0d \u662f\u4eba\u4eec\u7684\u8089\u773c\u53ef\u4ee5\u53d1\u89c9\u7684\u3002 \u3000\u3000\u5999\u7075\u9053\u4eba\u53c8\u8f6c\u5934\u56db\u987e\uff0c\u56db\u4e0b\u6c89\u5bc2\u5982\u6b7b\uff0c\u53ea\u6709\u98ce\u58f0\u5439\u5f97\u4ed6\u5bbd\u5927\u7684\u9053\u888d\u730e\u730e\u4f5c\u54cd\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u5c06\u9053\u888d\u7684\u4e0b\u6446\u6396\u5728\u8170\u95f4\u7684\u4e1d\u6761\u4e0a\uff0c\u624b\u638c\u4e0b\u538b\uff0c\u8eab\u5f62\u4fbf\u7b14\u76f4\u7684\u5411\u4e0a\u62d4\u53bb\uff0c\u4ece\u8fd9\u4e00\u624b \u201c\u65f1\u5730\u62d4\u8471\u201d\u7684\u8f7b\u529f\uff0c\u5c31\u53ef\u77e5\u8fd9\u7ec8\u5357\u5251\u5ba2\uff0c\u7384\u95e8\u4e00\u9e64\u7684\u8eab\u4e0a\uff0c\u679c\u7136\u6709\u7740\u6781\u4e3a\u7cbe\u7eaf\u7684\u529f\u592b\u3002 \u3000\u3000\u8eab\u5f62\u4e0a\u62d4\u4e08\u4f59\uff0c\u4ed6\u53cc\u624b\u4e00\u642d\uff0c\u642d\u5728\u5899\u5934\uff0c\u8eab\u5f62\u7075\u5de7\u5730\u4e00\u7ffb\uff0c\u4fbf\u63a0\u4e86\u51fa\u53bb\uff0c\u7edd\u5bf9\u6ca1\u6709\u5e26\u7740 \u4efb\u4f55\u4e00\u4e1d\u58f0\u97f3\u6765\u3002 \u3000\u3000\u4ed6\u65b9\u843d\u5728\u5899\u5916\uff0c\u7acb\u523b\u6709\u4e00\u6761\u4eba\u5f71\u8fce\u4e86\u4e0a\u6765\uff0c\u8fd9\u4eba\u5f71\u8eab\u5f62\u5a40\u5a1c\uff0c\u6d53\u91cd\u7684\u591c\u8272\u4e2d\uff0c\u4f7f\u4eba\u4ecd\u53ef \u4ee5\u611f\u89c9\u5230\u5979\u8eab\u4e0a\u6240\u6563\u53d1\u7684\u5a9a\u610f\u3002 \u3000\u3000\u5979\u4e00\u63a0\u5230\u5999\u7075\u8eab\u4fa7\uff0c\u4e24\u4eba\u7acb\u523b\u7d27\u7d27\u63e1\u7740\u624b\uff0c\u5999\u7075\u7684\u5589\u7ed3\u4e0a\u4e0b\u79fb\u52a8\u7740\uff0c\u5c06\u5979\u62d6\u5230\u5899\u4e0b\u7684\u9634 \u5f71\u91cc\uff0c\u63a5\u7740\u662f\u4e00\u8fde\u4e32\u53d1\u81ea\u5589\u95f4\u7684\u201c\u5514\u5514\u201d\u4e4b\u58f0 \u3000\u3000\u7136\u540e\u662f\u4e00\u4e2a\u6781\u4e3a\u5a07\u67d4\u7684\u58f0\u97f3\u9053\uff1a \u3000\u3000\u201c\u4f60\u77a7\u4f60\uff0c\u6025\u5f97\u50cf\u8fd9\u4e2a\u6837\u5b50\uff0c\u5374\u504f\u504f\u53c8\u6015\u5f97\u50cf\u8017\u5b50\u4f3c\u7684\uff01\u6211\u5c31\u4e0d\u76f8\u4fe1\uff0c\u90a3\u4e24\u4e2a\u7626\u9b3c\uff0c\u5c31 \u6709\u90a3\u4e48\u5389\u5bb3\uff1f\u8fde\u4f60\u90fd\u4e0d\u6210\u2026\u2026\u201d \u3000\u3000\u5999\u7075\u7684\u58f0\u97f3\u7acb\u523b\u50cf\u8033\u8bed\u822c\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5a9a\u5a18\uff01\u4f60\u8fc7\u6765\u4e00\u70b9\u2026\u2026\u201d\u4e0b\u9762\u53c8\u662f\u4e00\u8fde\u4e32\u68a6\u5453\u822c\u7684\u4f4e\u8bed\u3002 \u3000\u3000\u201c\u5a9a\u5a18\u201d\u5624\u549b\u7740\uff0c\u53c8\u4fcf\u8bed\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u4eba\u771f\u662f\u7684\uff0c\u4eba\u5bb6\u8ddf\u4f60\u8bf4\u6b63\u7ecf\u7684\uff0c\u4f60\u8fd8\u8981\u8fd9\u6837\u2026\u2026\u201d\u8bed\u58f0\u88ab\u4e00\u58f0\u7a81\u6765\u7684\u201c\u5514\u201d\u58f0\u6240 \u65ad\uff0c\u63a5\u7740\u53c8\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7b49\u4e00\u4e0b\u561b\u2026\u2026\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u4e8b\u60c5\u5df2\u7ecf\u4e0d\u80fd\u591f\u518d\u62d6\u4e0b\u53bb\u4e86\u5440\uff01\u6211\u4eec\u8fd9\u91cc\u4eba\u624b\u53c8\u4e0d\u591f\uff0c \u4f60\u2026\u2026\u4f60\u603b\u5f97\u60f3\u4e2a\u529e\u6cd5\u5440\uff01\u201d \u3000\u3000\u5999\u7075\u4f4e\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5a9a\u5a18\uff01\u6211\u4e3a\u4e86\u4f60\uff0c\u6211\u2026\u2026\u5509\uff01\u5a9a\u5a18\uff01\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u4e24\u4eba\u2026\u2026\u5509\uff01\u4e8b\u60c5\u5df2\u6210\u4e86\u4e5d\u5206\uff0c\u90a3 \u77e5\u9053\u8fd9\u4e24\u4eba\u504f\u504f\u649e\u4e86\u6765\u3002\u73b0\u5728\u6211\u4e5f\u6ca1\u6709\u4e3b\u610f\uff0c\u5a9a\u5a18\uff01\u53ea\u8981\u4f60\u8bf4\uff0c\u6211\u4ec0\u4e48\u4e8b\u90fd\u53ef\u4ee5\u4e3a\u4f60\u505a \u7684\u3002\u201d \u3000\u3000\u201c\u5a9a\u5a18\u201d\u8f7b\u8f7b\u4e00\u7b11\uff0c\u4fcf\u8bed\u9053\uff1a \u3000\u3000\u201c\u4f60\u770b\u4f60\uff0c\u5802\u5802\u4e00\u6d3e\u638c\u95e8\uff0c\u8fd8\u50cf\u4e2a\u5b69\u5b50\u4f3c\u7684\uff01\u53ea\u8981\u4f60\u5728\u4ed6\u4eec\u5403\u7684\u4e1c\u897f\u91cc\uff0c\u7a0d\u7a0d\u518d\u653e\u4e0b\u4e00 \u70b9\uff0c\u90a3\u4e0d\u4ec0\u4e48\u4e8b\u90fd\u89e3\u51b3\u4e86\u5417\uff01\u201d \u3000\u3000\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u5999\u7075\u4f3c\u4e4e\u5728\u8003\u8651\u7740\u3002\u4f46\u662f\u8fd9\u6c89\u9ed8\u7740\u7684\u4e24\u4e2a\u4eba\u5e76\u4e0d\u5b89\u9759\uff0c\u4ed6\u4eec\u4ecd\u7136\u5728\u8f7b\u5fae\u5730 \u52a8\u7740\u3002\u4e24\u4eba\u7684\u8eab\u4e0a\uff0c\u5374\u5728\u9707\u52a8\u7740\u4e00\u79cd\u867d\u65e0\u89c4\u5219\uff0c\u4f46\u5374\u662f\u4eba\u7c7b\u4e98\u53e4\u4ee5\u6765\u5c31\u672a\u66fe\u6539\u53d8\u7684\u97f5\u5f8b\u3002 \u3000\u3000\u98ce\u58f0\u4f9d\u7136\uff0c\u5927\u5730\u4f3c\u4e4e\u53ea\u5269\u4e0b\u4e86\u4ed6\u4eec\u4e24\u4eba\u3002 \u3000\u3000\u7136\u800c \u3000\u3000\u5899\u7684\u90a3\u8fb9\uff0c\u5374\u5353\u7136\u7acb\u7740\u4e00\u4e2a\u7626\u957f\u7684\u4eba\u5f71\uff0c\u4ed6\u542c\u5230\u4ed6\u4eec\u7684\u8bdd\uff0c\u8138\u4e0a\u6400\u5408\u7740\u4e00\u79cd\u8fd1\u4e8e\u201c\u60cb \u60dc\u201d\u7684\u60b2\u54c0\uff0c\u548c\u4e00\u79cd\u201c\u88ab\u6b3a\u9a97\u4e86\u201d\u7684\u6124\u6012\uff01 \u3000\u3000\u201c\u60f3\u4e0d\u5230\uff0c\u4ed6\u7adf\u4f1a\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff0c\u60f3\u4e0d\u5230\u2026\u2026\u4ed6\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u542c\u5230\u5899\u90a3\u8fb9\u9500\u9b42\u7684\u201c\u4f0a\u5514\u201d\u4e4b\u58f0\uff0c\u4ed6\u604d\u7136\u5f97\u5230\u4e86\u7b54\u6848\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u957f\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u5899\u7684\u53e6\u4e00\u8fb9\u7684\u5999\u7075\u548c\u5a9a\u5a18\uff0c\u867d\u7136\u5728\u6c89\u9189\u4e4b\u4e2d\uff0c\u53ef\u4e5f\u542c\u5230\u4e86\u8fd9\u4e00\u58f0\u957f\u53f9\u3002\u4e24\u4eba\u500f\u7136\u5927\u60ca\uff0c \u76ee\u5149\u540c\u65f6\u56db\u4e0b\u4e00\u8f6c\u3002 \u3000\u3000\u4e24\u4eba\u773c\u524d\u4e00\u82b1\u3002\u76ee\u5149\u4fbf\u7a81\u7136\u51dd\u7ed3\u4f4f\u4e86\u3002 \u3000\u3000\u4e00\u6761\u8f7b\u70df\u822c\u5730\u4eba\u5f71\uff0c\u4ece\u5899\u7684\u90a3\u8fb9\u63a0\u4e86\u8fc7\u6765\uff0c\u51b7\u9177\u5730\u7ad9\u5728\u4ed6\u4eec\u8eab\u4fa7\u4e09\u6b65\u4e4b\u5904\u3002 \u3000\u3000\u5999\u7075\u5931\u8272\u5730\u60ca\u547c\u4e00\u58f0\uff0c\u8eab\u5f62\u60f6\u7136\u5411\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u5374\u4e0d\u6562\u9003\u53bb\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5bb6\u975e\u5e38\u6e05\u695a\u5730\u77e5 \u9053\uff0c\u4ed6\u65e0\u6cd5\u9003\u51fa\u4eba\u5bb6\u7684\u638c\u63e1\u3002 \u3000\u3000\u5a9a\u5a18\u5374\u5a07\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u7ea4\u624b\u626c\u5904\uff0c\u5411\u90a3\u4eba\u5f71\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u8f7b\u8511\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u59ab\u5a18\u8eab\u5f62\u5982\u98de\u71d5\uff0c\u638c\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u4e00\u8f6c\uff0c\u6539\u5288\u4e3a \u6325\uff0c\u4e94\u53ea\u7ea4\u7ea4\u7389\u6307\uff0c\u53cd\u624b\u6325\u5411\u90a3\u4eba\u7ed3\u5589\u4e0b\u4e00\u5bf8\u7684\u201c\u5929\u7a81\u201d\uff0c\u65e0\u540d\u6307\u4e00\u52fe\uff0c\u70b9\u5411\u4ed6\u201c\u5929\u7a81\u201d\u7a74 \u4e0b\u4e00\u5bf8\u516d\u5206\u7684\u201c\u7487\u673a\u201d\u7a74\uff0c\u5de6\u638c\u5374\u5e26\u7740\u98ce\u58f0\u5288\u5411\u90a3\u4eba\u7684\u5de6\u80a9\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e24\u5f0f\uff0c\u53ef\u8bf4\u662f\uff1a\u72e0\uff0c\u51c6\uff0c\u5feb\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u8c01\u4e5f\u6599\u60f3\u4e0d\u5230\u8fd9\u4e00\u53cc\u8202\u8471\u822c\u7684\u624b\u638c\uff0c\u7adf \u80fd\u591f\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u53d6\u4eba\u6b7b\u547d\uff01 \u3000\u3000\u90a3\u4eba\u5f71\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\uff0c\u7b49\u5230\u8fd9\u4e00\u53cc\u624b\u638c\u582a\u582a\u63a5\u89e6\u5230\u4ed6\u7684\u8eab\u4f53\u65f6\uff0c\u4ed6\u5374\u5df2\u4e0d\u77e5\u600e\u7684\u5411\u53f3\u6ed1 \u5f00\u6570\u5bf8\uff0c\u867d\u7136\u53ea\u662f\u6570\u5bf8\uff0c\u7136\u800c\u5374\u4f7f\u5f97\u201c\u5a9a\u5a18\u201d\u8fd9\u72e0\uff0c\u51c6\uff0c\u5feb\u7684\u4e00\u62db\u4e24\u5f0f\uff0c\u521a\u597d\u591f\u4e0d\u7740\u90e8\u4f4d\u3002 \u3000\u3000\u5999\u7075\u5728\u8fd9\u4eba\u5f71\u4e00\u51fa\u73b0\u65f6\uff0c\u4ed6\u5fc3\u4e2d\u7535\u4e5f\u4f3c\u5730\u8f6c\u52a8\u7740\uff0c\u500f\u7136\u4e00\u54ac\u7259\uff0c\u8eab\u5f62\u6cbf\u7740\u5899\u6839\uff0c\u4ea1\u547d\u5730 \u98de\u63a0\u4e86\u53bb\uff0c\u542c\u5230\u8eab\u540e\u7684\u5a9a\u5a18\uff0c\u5a07\u5524\u4e86\u4e00\u58f0\uff0c\u4ed6\u77e5\u9053\u90a3\u66fe\u4f7f\u5f97\u81ea\u5df1\u5fc3\u9189\u795e\u8ff7\u7684\u7f8e\u4eba\uff0c\u6b64\u523b\u6015\u5df2 \u9999\u6d88\u7389\u6b92\u4e86\uff01 \u3000\u3000\u4f46\u662f\u4ed6\u4e0d\u6562\u56de\u5934\uff0c\u6c42\u751f\u7684\u6b32\u671b\u4f7f\u5f97\u4ed6\u7684\u8f7b\u529f\uff0c\u5f77\u4f5b\u6bd4\u5e73\u65f6\u66f4\u5feb\u901f\u4e86\u4e9b\u3002\u8fd9\u65f6\u4ed6\u5fc3\u4e2d\u518d\u65e0 \u522b\u7684\u5ff5\u5934\uff0c\u53ea\u60f3\u81ea\u5df1\u80fd\u591f\u9003\u8131\u4eba\u5bb6\u7684\u638c\u63e1\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4ed6\u773c\u524d\u53c8\u4e00\u82b1\uff0c\u89c9\u5f97\u6709\u4eba\u62e6\u5728\u524d\u9762\uff0c\u4ed6\u773c\u89d2\u52a8\u5904\uff0c\u53c8\u4e0d\u7981\u60e8\u55e5\u4e86\u4e00\u58f0\uff0c\u5728\u6df1\u591c\u4e2d \u4ee4\u4eba\u89c9\u5f97\u5206\u5916\u5730\u523a\u8033\u800c\u51c4\u9634\u3002 \u3000\u3000\u5728\u4ed6\u773c\u524d\u7684\uff0c\u8d6b\u7136\u7ad9\u7740\u201c\u5a9a\u5a18\u201d\u5a40\u5a1c\u7684\u8eab\u8eaf\uff0c\u591c\u8272\u4e2d\uff0c\u4ed6\u53ef\u4ee5\u770b\u5230\u6709\u9c9c\u8840\u81ea\u5a9a\u5a18\u90a3\u66fe\u7ecf \u53d1\u51fa\u4e0d\u77e5\u51e0\u8bb8\u4ee4\u4eba\u9b42\u6d88\u7684\u201c\u5514\u5514\u201d\u4e4b\u58f0\u7684\u5634\u4e2d\uff0c\u6d41\u4e86\u4e0b\u6765\uff0c\u5979\u90a3\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u5a9a\u773c\uff0c\u6b64\u523b \u4e5f\u662f\u7d27\u95ed\u7740\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e0d\u987e\u4e00\u5207\u5730\u6251\u4e86\u4e0a\u53bb\u2014\u2014 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u771f\u76f8\u5927\u767d>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u771f\u76f8\u5927\u767d \u3000\u3000\u4ed6\u5f20\u81c2\u6b32\u62b1\uff0c\u90a3\u77e5\u5374\u62b1\u4e86\u4e2a\u7a7a\uff0c\u518d\u4e00\u62ac\u5934\uff0c\u9762\u524d\u8d6b\u7136\u7adf\u662f\u4e09\u5fc3\u795e\u541b\u51b7\u6f20\u65e0\u60c5\u7684\u9762\u5bb9\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u795e\u667a\u65e9\u5df2\u72c2\u4e71\uff0c\u5389\u543c\u4e86\u4e00\u58f0\uff0c\u811a\u5c16\u4e00\u987f\uff0c\u201c\u6392\u5c71\u8fd0\u638c\u201d\uff0c\u4e24\u638c\u5e26\u7740\u864e\u864e\u7684\u638c\u98ce\uff0c \u5411\u4e09\u5fc3\u795e\u541b\u95ea\u7535\u822c\u5730\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u7830\u7136\u201d\u4e00\u58f0\uff0c\u4ed6\u53cc\u638c\u90fd\u7740\u7740\u5b9e\u5b9e\u51fb\u5728\u4e00\u4eba\u7684\u8eaf\u4f53\u4e0a\uff0c\u4f46\u662f\uff0c\u90a3\u5374\u4e0d\u662f\u4e09\u5fc3\u795e\u541b\u7684\u3002 \u3000\u3000\u539f\u6765\u4e09\u5fc3\u795e\u541b\u5728\u4ed6\u7684\u53cc\u638c\u51fb\u51fa\u65f6\uff0c\u8eab\u5f62\u5fae\u9000\uff0c\u5374\u5c06\u4ed6\u624b\u4e2d\u6293\u7740\u7684\u90a3\u201c\u5a9a\u5a18\u201d\u7684\u8eab\uff0c\u6321\u5728 \u524d\u9762\uff0c\u63a5\u4f4f\u4e86\u8fd9\u5999\u7075\u7684\u5168\u529b\u4e00\u638c\u3002 \u3000\u3000\u5999\u7075\u53c8\u4e00\u58f0\u5389\u543c\uff0c\u4e24\u6761\u94c1\u81c2\uff0c\u75af\u4e86\u4f3c\u7684\u62a1\u4e86\u5f00\u6765\u3002\u591a\u65e5\u6765\u7684\u6127\u600d\uff0c\u4e0d\u5b89\uff0c\u60ca\u60e7\uff0c\u90fd\u5728\u8fd9 \u4e00\u523b\u91cc\u5b8c\u5168\u53d1\u6cc4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u81ea\u5e7c\u5165\u5c71\uff0c\u6570\u5341\u5e74\u6765\uff0c\u90fd\u5728\u8fd9\u6df1\u5c71\u4e2d\u8fc7\u7740\u6e05\u51c0\u7edd\u4fd7\u7684\u751f\u6d3b\u3002\u5bf9\u4e8e\u4e16\u95f4\u7684\u4e00\u5207\u60c5\u4e8b\uff0c\u4ed6 \u90fd\u51e0\u4e4e\u5168\u7136\u4e0d\u4e86\u89e3\u3002\u5bf9\u4e8e\u4eba\u7c7b\u90a3\u4e9b\u60c5\u611f\u548c\u6b32\u5ff5\uff0c\u4ed6\u867d\u7136\u9690\u9690\u7ea6\u7ea6\u5730\u611f\u89c9\u5230\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u4f53 \u9a8c\u8fc7\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u7981\u4e0d\u8d77\u8bf1\u611f\u3002 \u3000\u3000\u90d1\u5a9a\u5a18\u5949\u4e86\u5bc6\u4ee4\uff0c\u5343\u65b9\u767e\u8ba1\u5730\u63a5\u8fd1\u4e86\u4ed6\uff0c\u4f7f\u5f97\u8fd9\u751f\u5e73\u672a\u66fe\u7ecf\u5386\u8fc7\u5973\u8272\u7684\u5999\u7075\uff0c\u4e3a\u4e86\u5979\u4e30 \u6ee1\u7684\u80f4\u4f53\uff0c\u7518\u5192\u5927\u4e0d\u8bb3\uff0c\u7adf\u5c06\u81ea\u5df1\u95e8\u4e0b\u7684\u6570\u767e\u5f1f\u5b50\uff0c\u90fd\u9001\u7ed9\u522b\u4eba\u505a\u4e86\u521b\u7acb\u6559\u6d3e\u7684\u727a\u7272\u54c1\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u65bd\u6bd2\uff0c\u6bd2\u4e86\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u7136\u540e\u518d\u51c6\u5907\u4f2a\u88c5\u7740\u51fa\u4e8e\u65e0\u5948\uff0c\u5c06\u7ec8\u5357\u5c71\u6570\u767e\u5e74\u6765\u521b\u7acb\u4e0b \u7684\u57fa\u4e1a\uff0c\u53cc\u624b\u9001\u4e8e\u522b\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u7406\u667a\uff0c\u5df2\u5168\u7136\u88ab\u201c\u6b32\u5ff5\u201d\u6240\u8ff7\u9189\uff0c\u53ea\u8981\u80fd\u4e00\u4eb2\u90d1\u5a9a\u5a18\u7684\u82b3\u6cfd\uff0c\u4ed6\u751a\u81f3\u53ef\u80fd\u6627\u7740 \u826f\u5fc3\u800c\u51fa\u5356\u81ea\u5df1\u7684\u7956\u5148\uff01 \u3000\u3000\u4f46\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u7684\u662f\uff1a\u5251\u5148\u751f\u548c\u4e09\u5fc3\u795e\u541b\u7adf\u7a81\u7136\u6765\u5230\u7ec8\u5357\u5c71\uff0c\u8fd9\u4f7f\u5f97\u4ed6\u80c6\u5bd2\u800c\u5fc3\u602f\u4e86\uff01 \u3000\u3000\u4f46\u4ed6\u53c8\u81ea\u6043\u81ea\u5df1\u7684\u8c0e\u8a00\u8bf4\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u56e0\u4e3a\u4efb\u662f\u8c01\uff0c\u4e5f\u4e0d\u4f1a\u6000\u7591\u5230\u65bd\u6bd2\u4e8e\u7ec8\u5357\u95e8\u4e0b\u6570\u767e \u5f1f\u5b50\u201c\u51f6\u624b\u201d\uff0c\u7adf\u662f\u7ec8\u5357\u6d3e\u672c\u6d3e\u7684\u638c\u95e8\u4eba\u5999\u7075\u9053\u4eba\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u4ecd\u7136\u662f\u5fc3\u865a\u7684\uff0c\u7ec8\u65e5\u7684\u795e\u7ecf\u90fd\u5728\u7d27\u5f20\u7740\uff0c\u751f\u6015\u522b\u4eba\u4f1a\u53d1\u73b0\u4ed6\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u6bcf\u4e00\u4e2a\u8fdd\u80cc\u4e86\u81ea\u5df1\u826f\u5fc3\u7684\u4eba\uff0c\u5374\u90fd\u53c8\u4f1a\u88ab\u81ea\u5df1\u7684\u826f\u5fc3\u91cd\u538b\u7740\uff1b\u800c\u5728\u65e0\u610f\u4e4b\u4e2d\uff0c\u81ea\u5df1\u9732\u51fa \u4e86\u79d8\u5bc6\u3002 \u3000\u3000\u4ed6\u5728\u4e39\u623f\u7684\u95e8\u5916\uff0c\u542c\u5230\u4e86\u51cc\u7433\u548c\u5979\u6bcd\u4eb2\u7684\u5bf9\u8bdd\uff0c\u5fc3\u91cc\u7acb\u523b\u4e0d\u5b89\u8d77\u6765\uff0c\u4ee5\u4e3a\u51cc\u7433\u5df2\u7ecf\u77e5\u9053 \u4e86\u4ed6\u7684\u79d8\u5bc6\u3002\u5176\u5b9e\u8fd9\u5f53\u7136\u662f\u4ed6\u81ea\u5df1\u7684\u7591\u5fc3\uff1b\u800c\u8fd9\u79cd\u7591\u5fc3\uff0c\u5374\u4f7f\u5f97\u5343\u767e\u5e74\u6765\u7684\u65e0\u6570\u201c\u51f6\u624b\u201d\uff0c \u81ea\u5df1\u51fa\u5356\u4e86\u81ea\u5df1\uff01 \u3000\u3000\u4ed6\u5fc3\u751f\u6697\u9b3c\u4e4b\u540e\uff0c\u5c31\u7279\u5730\u505a\u4e86\u76c5\u4e0b\u8fc7\u6bd2\u7684\u53c2\u6c64\uff0c\u60f3\u5c06\u51cc\u7433\u6740\u4e86\u706d\u53e3\u3002\u90a3\u77e5\u51cc\u7433\u73b2\u73d1\u5254 \u900f\uff0c\u7adf\u5c06\u90a3\u76c5\u53c2\u6c64\uff0c\u5012\u5728\u53e6\u4e00\u4e2a\u7897\u91cc\uff0c\u4fbf\u5f97\u5999\u7075\u5728\u53d6\u53bb\u7a7a\u7897\u65f6\uff0c\u4ee5\u4e3a\u5979\u5df2\u5c06\u90a3\u76c5\u53c2\u6c64\u559d\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u51cc\u7433\u53c8\u88c5\u7740\u75c5\u52bf\u8f6c\u5267\uff1a\u7b49\u5230\u4e09\u5fc3\u795e\u541b\u6765\u770b\u7684\u65f6\u5019\uff0c\u5979\u5374\u5c06\u5fc3\u4e2d\u7684\u6000\u7591\u548c\u90a3\u7897\u53c2\u6c64\uff0c \u90fd\u544a\u8bc9\u4e86\u4e09\u5fc3\u795e\u541b\u3002\u4e09\u5fc3\u795e\u541b\u533b\u9053\u5999\u7edd\u5929\u4e0b\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u5c31\u77e5\u9053\u90a3\u7897\u53c2\u6c64\u91cc\u679c\u7136\u6709\u7740\u5267\u6bd2\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e0d\u9732\u58f0\u8272\uff0c\u53ea\u662f\u5728\u6697\u4e2d\u7559\u610f\u7740\u3002 \u3000\u3000\u4e8e\u662f\u5999\u7075\u5c31\u5728\u4e00\u5ff5\u4e4b\u5dee\u4e0b\uff0c\u6bc1\u5374\u4e86\u81ea\u5df1\u7684\u524d\u9014\uff0c\u58f0\u8a89\uff0c\u751a\u81f3\u751f\u547d\uff01 \u3000\u3000\u5999\u7075\u6b64\u523b\u5fc3\u795e\u5d29\u6e83\uff0c\u5df2\u7ecf\u8fd1\u4e8e\u75af\u72c2\u4e86\uff01 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u51b7\u7b11\u559d\u9053\uff1a \u3000\u3000\u201c\u5b7d\u969c\uff01\u8fd8\u4e0d\u7ed9\u6211\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u8eab\u5f62\u52a8\u5904\uff0c\u56f4\u7740\u5999\u7075\u4e00\u8f6c\uff0c\u888d\u8896\u4e00\u62c2\uff0c\u62c2\u5411\u5999\u7075\u5927\u6a2a\u808b\u5916\uff0c\u5b63\u80c1\u4e4b\u7aef\u7684\u201c\u7ae0\u95e8\u201d\u7a74\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u51fa\u624b\uff0c\u6b63\u662f\u6b66\u6797\u4e2d\u5df2\u8fd1\u7edd\u4f20\u7684\u201c\u62c2\u7a74\u201d\u4e4b\u6cd5\uff0c\u70b9\u7684\u53c8\u662f\u4eba\u8eab\u8db3\u53a5\u9634\u809d\u7ecf\u4e2d\u7684\u91cd \u7a74\u3002 \u3000\u3000\u5999\u7075\u867d\u662f\u4e00\u6d3e\u5b97\u4e3b\uff0c\u8eab\u624b\u81ea\u7136\u4e0d\u51e1\uff1a\u4f46\u662f\u6b64\u523b\u5fc3\u795e\u75af\u4e71\uff0c\u9047\u7740\u7684\u53c8\u662f\u8fd9\u79cd\u7edd\u4e16\u5947\u4eba\uff0c\u90a3 \u6709\u8fd8\u624b\u4e4b\u5730\uff1f \u3000\u3000\u4e09\u5fc3\u795e\u541b\u4e00\u62c2\u4e4b\u4e0b\uff0c\u5374\u53ea\u7528\u4e86\u4e8c\u6210\u771f\u529b\uff0c\u624b\u81c2\u968f\u7740\u888d\u8896\u4e4b\u52bf\u4e00\u6284\uff0c\u5c06\u5999\u7075\u6284\u5728\u8eab\u540e\uff0c\u8db3 \u8ddf\u4e00\u65cb\uff0c\u8eab\u5f62\u5982\u7ecf\u5929\u4e4b\u8679\uff0c\u5411\u98a7\u5185\u63a0\u53bb\u3002 \u3000\u3000\u5251\u5148\u751f\u53cc\u7709\u6df1\u76b1\uff0c\u5b59\u654f\u4e5f\u5728\u5947\u602a\u8fd9\u7d20\u6709\u6e05\u8a89\u7684\u201c\u7ec8\u5357\u5251\u5ba2\u201d\uff0c\u600e\u4e48\u4f1a\u505a\u51fa\u8fd9\u79cd\u4e8b\u60c5\u6765\uff1f \u3000\u3000\u4e09\u5fc3\u795e\u541b\u51b7\u6f20\u7684\u9762\u4e0a\uff0c\u73b0\u51fa\u7b11\u5bb9\uff0c\u5411\u51cc\u7433\u9053\uff1a \u3000\u3000\u201c\u8fd8\u662f\u4f60\u884c\uff01\u6211\u4eec\u8fd9\u4e24\u4e2a\u8001\u5934\u5b50\uff0c\u90fd\u4e0d\u53ca\u4f60\uff01\u201d \u3000\u3000\u51cc\u7433\u4e00\u7b11\uff0c\u5f53\u7136\u4e5f\u6709\u4e9b\u5f97\u610f\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u7136\u4ece\u5e8a\u4e0a\u652f\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u7239\u7239\uff01\u4f60\u5c06\u8fd9\u4e2a\u5999\u7075\u9053\u4eba\u7684\u7a74\u9053\u89e3\u5f00\uff0c\u95ee\u95ee\u4ed6\u770b\uff0c\u4e5f\u8bb8\u4ed6\u65bd\u7684\u6bd2\uff0c\u5e76\u4e0d\u662f\u4ec0\u4e48\u201c\u8680 \u9aa8\u5723\u6c34\u201d\u5462\uff1f\u56e0\u4e3a\u6211\u60f3\u2026\u2026\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u731b\u5730\u4e00\u51fb\u638c\uff0c\u9053\uff1a \u3000\u3000\u201c\u5bf9\u4e86\uff01\u65e2\u7136\u662f\u4ed6\u65bd\u7684\u6bd2\uff0c\u90a3\u4e48\u8fd9\u80fd\u4f7f\u5168\u89c2\u6570\u767e\u4eba\uff0c\u4e00\u9f50\u5728\u65e0\u5f71\u65e0\u5f62\u4e2d\u4e2d\u6bd2\u7684\u6bd2\u836f\uff0c\u5c31 \u4e0d\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u54c8\u54c8\u4e00\u7b11\uff0c\u5411\u5251\u5148\u751f\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u771f\u662f\u8d8a\u6765\u8d8a\u7cca\u6d82\uff0c\u5c3d\u5c06\u8fd9\u4e8b\u5f80\u90a3\u9762\u53bb\u60f3\uff0c\u5374\u4e0d\u4eb2\u81ea\u53bb\u68c0\u67e5\u68c0\u67e5\u90a3\u4e9b\u9053\u4eba\u7684\u6bd2\u52bf\uff0c \u60f3\u4e0d\u5230\u4f60\u4e5f\u6709\u5931\u7b97\u7684\u4e00\u5929\uff01\u201d \u3000\u3000\u5251\u5148\u751f\u5fae\u559f\u4e86\u4e00\u58f0\uff0c\u4ed6\u7edd\u5bf9\u60f3\u4e0d\u5230\u5999\u7075\u4f1a\u6709\u8c0e\u8a00\uff0c\u5b8c\u5168\u76f8\u4fe1\u4e86\u4ed6\u7684\u8bdd\uff0c\u662f\u4ee5\u624d\u65ad\u5b9a\u8fd9\u4f7f \u7ec8\u5357\u95e8\u4e0b\u4e00\u9f50\u4e2d\u6bd2\u7684\u6bd2\u836f\uff0c\u4e00\u5b9a\u662f\u201c\u8680\u9aa8\u5723\u6c34\u201d\u3002\u56e0\u4e3a\u666e\u5929\u4e4b\u4e0b\uff0c\u518d\u65e0\u4efb\u4f55\u4e00\u79cd\u6bd2\u836f\uff0c\u6709\u5982 \u6b64\u5a01\u529b\u3002 \u3000\u3000\u800c\u6b64\u523b\u771f\u8c61\u5927\u767d\uff0c\u4ee5\u5999\u7075\u5728\u89c2\u4e2d\u7684\u5730\u4f4d\uff0c\u7eb5\u7136\u4ee5\u6700\u666e\u901a\u7684\u6bd2\u836f\uff0c\u4e5f\u53ef\u80fd\u4f7f\u7ec8\u5357\u5168\u6d3e\u7684\u5f1f \u5b50\uff0c\u4e00\u9f50\u4e2d\u6bd2\u7684\u3002 \u3000\u3000\u4ed6\u5fae\u559f\u7740\uff0c\u671d\u51cc\u7433\u770b\u4e86\u4e00\u773c\uff0c\u5979\u90a3\u660e\u4eae\u53cc\u77b3\u4e2d\uff0c\u6b63\u663e\u793a\u7740\u667a\u6167\u7684\u5149\u8292\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fae\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5973\u5b69\u5b50\u5929\u8d44\u4e4b\u9ad8\uff0c\u5fc3\u601d\u4e4b\u7075\u5de7\uff0c\u5b9e\u5728\u767e\u5e74\u7f55\u7779\uff01\u53ea\u8981\u7a0d\u52a0\u7422\u78e8\uff0c\u6210\u5c31\u6015\u4e0d\u96be\u8d85\u8fc8\u53e4 \u4eba\uff0c\u4e3a\u6b66\u6797\u653e\u4e00\u5f02\u5f69\uff01\u201d \u3000\u3000\u5b59\u654f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u7136\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u671d\u5251\u5148\u751f\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5251\u5148\u751f\u65b9\u81ea\u5fae\u6115\uff0c\u5374\u542c\u5b59\u654f\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\u81ea\u5e7c\u4e27\u7236\uff0c\u8eab\u8499\u6df1\u4ec7\uff0c\u5374\u65e0\u80fd\u4ee5\u62a5\uff0c\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u5979\u7adf\u63d0\u51fa\u4e86\u8981\u5251\u5148\u751f\u5c06\u81ea\u5df1\u7684\u5973\u513f\u6536\u4e3a\u5f1f\u5b50\u7684\u8981\u6c42\u3002 \u3000\u3000\u51cc\u7433\u5fc3\u601d\u7075\u5de7\uff0c\u5f53\u7136\u4e5f\u77e5\u9053\u5979\u77e5\u80fd\u505a\u5251\u5148\u751f\u7684\u5f1f\u5b50\uff0c\u662f\u4f55\u79cd\u7684\u5e78\u8fd0\uff01\u4e5f\u5728\u5e8a\u4e0a\u8dea\u4e86\u4e0b \u53bb\uff0c\u4e0d\u505c\u5730\u54c0\u6c42\u7740\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\u6697\u6697\u6447\u5934\uff0c\u4ed6\u77e5\u9053\uff1a\u5251\u5148\u751f\u767e\u5341\u5e74\u6765\uff0c\u4ece\u672a\u6536\u8fc7\u5f1f\u5b50\uff0c\u4ee5\u4e3a\u8fd9\u6bcd\u5973\u4e24\u4eba\u7684\u8981 \u6c42\uff0c\u5b9a\u7136\u8981\u906d\u5230\u5251\u5148\u751f\u7684\u62d2\u7edd\u3002 \u3000\u3000\u90a3\u77e5\u5251\u5148\u751f\u5fae\u4e00\u6c88\u541f\uff0c\u5374\u9053\uff1a \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u5feb\u8d77\u6765\uff0c\u6211\u5c31\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u4e09\u5fc3\u795e\u541b\u4e00\u6014\uff0c\u4ed6\u518d\u4e5f\u6599\u60f3\u4e0d\u5230\u5251\u5148\u751f\u4f1a\u6536\u5f92\u5f1f\u7684\u3002 \u3000\u3000\u7136\u800c\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u5251\u5148\u751f\uff0c\u8fd9\u4e9b\u5929\u6765\uff0c\u5185\u5fc3\u7684\u60c5\u7eea\uff0c\u4e5f\u6709\u7740\u6781\u5267\u70c8\u7684\u53d8\u52a8\u3002\u800c\u4ed6\u8fd9\u79cd\u53d8 \u52a8\uff0c\u4e00\u90e8\u4efd\u662f\u7531\u4e8e\u5f80\u4e8b\uff1b\u4e00\u90e8\u4efd\u5374\u662f\u56e0\u4e3a\u5b59\u654f\u5462\uff01 \u3000\u3000\u4eba\u7c7b\u5fc3\u4e8b\u7684\u590d\u6742\u5fae\u5999\uff0c\u7edd\u5bf9\u4e0d\u662f\u7b2c\u4e09\u8005\u53ef\u4ee5\u731c\u5f97\u900f\u7684\u3002\u4e09\u5fc3\u795e\u541b\u5f53\u7136\u4e0d\u4f1a\u60f3\u5230\u5728\u5251\u5148\u751f \u548c\u5b59\u654f\u4e4b\u95f4\uff0c\u4f1a\u6709\u7740\u60c5\u611f\u7684\u8fde\u7cfb\u3002 \u3000\u3000\u800c\u5251\u5148\u751f\u81ea\u5df1\uff0c\u53c8\u4f55\u5c1d\u4e0d\u5728\u4e3a\u4e86\u81ea\u5df1\u8fd9\u79cd\u60c5\u611f\u800c\u5947\u602a\uff0c\u4e0d\u5b89\u3002\u4ed6\u52aa\u529b\u5730\u5411\u81ea\u5df1\u89e3\u91ca\u7740 \u8bf4\uff1a\u8fd9\u4e0d\u670b\u4ec5\u662f\u4e00\u79cd\u666e\u901a\u7684\u597d\u611f\u800c\u5df2\u3002\u4f46\u8fd9\u79cd\u7684\u597d\u611f\uff0c\u662f\u5426\u662f\u666e\u901a\u7684\uff0c\u5374\u8fdd\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u5341\u5206 \u6e05\u695a\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u6b64\u523b\u7adf\u4e0d\u80fd\u62d2\u7edd\u5b59\u654f\u7684\u8981\u6c42\uff0c\u800c\u51fa\u4e8e\u4e09\u5fc3\u795e\u541b\u610f\u6599\u4e4b\u5916\u5730\uff0c\u5c06\u51cc\u7433\u7834\u683c \u6536\u4e3a\u95e8\u4e0b\uff0c\u8fd9\u5176\u4e2d\u5173\u7cfb\u7740\u4ed6\u5185\u5fc3\u60c5\u611f\u7684\u7eb7\u4e89\u3002 \u3000\u3000\u4f46\u4e0d\u53ef\u5426\u8ba4\u7684\uff0c\u51cc\u7433\u672c\u8eab\u4e5f\u6709\u8db3\u591f\u7684\u6761\u4ef6\uff0c\u4f7f\u5979\u914d\u505a\u8fd9\u7edd\u4e16\u5947\u4eba\u7684\u552f\u4e00\u5f1f\u5b50\u3002\u56e0\u4e3a\u5979\u4ee5 \u81ea\u8eab\u7684\u667a\u6167\uff0c\u4f7f\u5f97\u201c\u5929\u6bd2\u6559\u201d\u4e25\u5bc6\u7684\u8ba1\u5212\uff0c\u5b8c\u5168\u7834\u706d\u4e86\u3002 \u3000\u3000\u4e09\u5fc3\u795e\u541b\uff0c\u53d1\u73b0\u7ec8\u5357\u5f1f\u5b50\u6240\u4e2d\u4e4b\u6bd2\uff0c\u679c\u7136\u4e0d\u662f\u201c\u8680\u9aa8\u5723\u6c34\u201d\uff0c\u800c\u8fd9\u79cd\u6bd2\u836f\u4e5f\u662f\u975e\u5e38\u5389\u5bb3 \u7684\u3002\u4f46\u5374\u96be\u4e0d\u5012\uff0c\u8eab\u5177\u533b\u9053\u4e2d\u4e0d\u4f20\u4e4b\u79d8\uff0c\u5c06\u5929\u4e0b\u5343\u767e\u79cd\u6bd2\u6027\uff0c\u90fd\u4e86\u5982\u6307\u638c\u7684\u4e09\u5fc3\u795e\u541b\uff01 \u3000\u3000\u4e8e\u662f\u7ec8\u5357\u5c71\u7684\u6570\u767e\u9053\u4eba\uff0c\u5c31\u5728\u4f0a\u98ce\u56de\u6765\u4e4b\u524d\uff0c\u83b7\u5f97\u4e86\u89e3\u6551\u3002 \u3000\u3000\u800c\u5728\u6b66\u6797\u9887\u6709\u6e05\u8a89\u7684\u7384\u95e8\u4e00\u9e64\uff0c\u5374\u5728\u65e0\u6570\u4eba\u7684\u60cb\u60dc\uff0e\u4e0d\u9f7f\uff0c\u8d23\u9a82\uff0c\u6124\u6012\u4e4b\u4e2d\uff0c\u4e3a\u7740\u81ea\u5df1 \u7684\u6b32\u5ff5\uff0c\u4e27\u5931\u4e86\u4ed6\u672c\u6765\u6781\u6709\u524d\u9014\u7684\u751f\u547d\u3002 \u3000\u3000\u4eba\u4e16\u4e4b\u96be\u6d4b\uff0c\u6bcf\u591a\u5982\u6b64\uff01\u8fd9\u4ef6\u4e8b\u5728\u6ca1\u6709\u5f97\u77e5\u771f\u8c61\u4e4b\u524d\uff0c\u53c8\u6709\u8c01\u80fd\u731c\u5f97\u5230\u5176\u4e2d\u7684\u7a76\u7adf\u5462\uff1f \u3000\u3000\u5251\u5148\u751f\u7b49\u4eba\uff0c\u4ecd\u7136\u505c\u7559\u5728\u7ec8\u5357\u5c71\u4e0a\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8fd8\u8981\u7b49\u5f85\u4f0a\u98ce\u3002\u53ea\u662f\u4ed6\u4eec\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u6b64 \u523b\u4f0a\u98ce\u7684\u751f\u6b7b\uff0c\u6b63\u60ac\u4e8e\u4e00\u53d1\u4e4b\u95f4\u54e9\uff01 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u751f\u6b7b\u4e00\u53d1>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u751f\u6b7b\u4e00\u53d1 \u3000\u3000\u4f0a\u98ce\u7684\u5168\u90e8\u601d\u60f3\uff0c\u5168\u8eab\u7cbe\u529b\uff0c\u90fd\u56e0\u7740\u6050\u60e7\u800c\u50cf\u662f\u51bb\u7ed3\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u53cc\u76ee\u671b\u7740\u4e07\u5929\u840d\u4f38\u51fa\u6765\u7684\u90a3\u4e00\u53cc\u67af\u7626\u800c\u6ee1\u6cbe\u7740\u8840\u8ff9\u7684\u624b\u638c\uff0c\u5fc3\u4e2d\u98d8\u98d8\u6c64\u6c64\uff0c\u604d\u604d\u60da \u60da\uff0c\u4e5f\u9690\u9690\u7ea6\u7ea6\u5730\u89c9\u51fa\u4e86\u6b7b\u4ea1\u7684\u610f\u5473\u3002 \u3000\u3000\u4e07\u5929\u840d\u7684\u53cc\u775b\uff0c\u4e5f\u5728\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u4ed6\uff0c\u5374\u4ecd\u7136\u8fdf\u8fdf\u672a\u66fe\u51fa\u624b\uff0c\u8fd9\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u7f18\u6545 \u5462\uff01\u800c\u5df2\u7ecf\u8eab\u53d7\u4e24\u5904\u91cd\u521b\uff0c\u6beb\u65e0\u7591\u4e49\u5730\u6b7b\u53bb\u4e86\u7684\u4ed6\uff0c\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u800c\u80fd\u7a81\u7136\u590d\u751f\u4e86\u5462\uff1f \u3000\u3000\u4ed6\u7a81\u7136\u5e72\u6da9\u5730\u4e00\u7b11\uff0c\u88c2\u5f00\u4ed6\u90a3\u5634\u65c1\u4e5f\u6ee1\u6cbe\u8840\u6e0d\u7684\u5634\uff0c\u51b7\u786c\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b69\u5b50\uff01\u4f60\u8d76\u5feb\u5c06\u90a3\u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\u62ff\u51fa\u6765\uff01\u4e0d\u7136\u2026\u2026\u201d \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u987b\u8981\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4efb\u4f55\u4eba\u90fd\u80fd\u731c\u5230\u4ed6\u8bed\u4e2d\u7684\u542b\u610f\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u5374\u731b\u5730\u52a8\u4e86\u4e00\u4e0b\uff0c\u9b3c\u9b45\u4f3c\u7684\u4e07\u5929\u840d\uff0c\u5728\u4ed6\u773c\u4e2d\uff0c\u56e0\u7740\u8fd9\u4e00\u53e5\u8bdd\u800c\u7a81\u7136\u53d8\u56de\u4e86\u6d3b \u4eba\u3002\u56e0\u4e3a\u53ea\u6709\u751f\u5b58\u7684\u4eba\uff0c\u624d\u4f1a\u6709\u5bf9\u4e8b\u7269\u7684\u6b32\u671b\u3002\u82e5\u5df2\u6b7b\u4e86\u800c\u53d8\u6210\u4e86\u9b3c\uff0c\u53c8\u8981\u90a3\u201c\u5929\u661f\u79d8\u7b08\u201d \u4f55\u7528\uff1f \u3000\u3000\u4ed6\u6697\u6697\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u773c\u5149\u653e\u80c6\u5730\u5728\u4e07\u5929\u840d\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5374\u89c1\u4ed6\u524d\u80f8\u548c\u5589\u5934\u7684\u4f24\u75d5\u5b9b\u7136\uff0c\u9732 \u51fa\u4e00\u4e2a\u4e2a\u9edd\u9ed1\u800c\u60ca\u4eba\u7684\u7a7a\u6d1e\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5c31\u662f\u5999\u624b\u8bb8\u767d\u7684\u94c1\u94a9\u822c\u7684\u5341\u6307\uff0c\u5728\u4ed6\u8eab\u4e0a\u7559\u4e0b\u7684\u75d5\u8ff9\u3002\u800c\u8fd9\u79cd\u4f24\u75d5\uff0c\u53ea\u8981\u4e2d\u4e0a \u4e00\u5904\uff0c\u4fbf\u8db3\u4ee5\u7f6e\u4efb\u4f55\u4eba\u4e8e\u6b7b\u547d\u3002 \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u4e3a\u4ec0\u4e48\u53c8\u80fd\u590d\u751f\u5462\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6050\u60e7\u4e4b\u5ff5\u4e00\u6d88\uff0c\u60ca\u5f02\u4e4b\u5fc3\u5374\u5927\u4f5c\u3002\u4e24\u773c\u4ecd\u7136\u77aa\u7740\u4e07\u5929\u840d\uff0c\u5e76\u6ca1\u6709\u53bb\u56de\u7b54\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u4e07\u5929\u840d\u53c8\u524d\u8fc8\u4e00\u6b65\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u62ff\u4e0d\u62ff\u51fa\u6765.\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u53c8\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4e4b\u529f\u529b\u9ad8\u8fc7\u4e8e\u6211\uff0c\u53c8\u660e\u77e5\u9053\u201c\u5929\u661f\u79d8\u7b08\u201d\u5fc5\u5b9a\u8fd8\u653e\u8301\u627e\u8eab\u4e0a\uff0c\u5927\u53ef\u52a8\u624b\u5236\u4f4f\u4e86\u6211\uff0c \u62a2\u53bb\u8fd9\u672c\u79d8\u7b08\uff0c\u4e3a\u4ec0\u4e48\u5374\u8981\u6211\u81ea\u5df1\u62ff\u51fa\u6765\uff1f\u4ed6\u53f7\u79f0\u201c\u5317\u76d7\u201d\uff0c\u672c\u4e0d\u5e94\u662f\u8fd9\u79cd\u4f5c\u98ce\u5440\uff01\u201d \u3000\u3000\u987b\u77e5\u4f0a\u98ce\u672c\u662f\u7edd\u9876\u806a\u660e\u4e4b\u4eba\uff0c\u5fc3\u601d\u7075\u5de7\u5df2\u6781\uff0c\u662f\u4ee5\u4ed6\u624d\u80fd\u4ee5\u201c\u8bc8\u6b7b\u201d\u7792\u8fc7\u5929\u4e0b\u6b66\u6797\u8033 \u76ee\u3002\u6b64\u523b\u5fc3\u4e2d\u4e00\u52a8\u5ff5\uff0c\u63a5\u7740\u53c8\u5fd6\u9053\uff1a \u3000\u3000\u201c\u83ab\u975e\u4ed6\u8eab\u53d7\u81f4\u547d\u4e4b\u4f24\uff0c\u540e\u6765\u867d\u56e0\u7740\u4e00\u4ef6\u5947\u9047\u800c\u80fd\u590d\u751f\uff1b\u4f46\u4ed6\u5e73\u8eab\u7684\u529f\u529b\uff0c\u5374\u4e0d\u80fd\u5728\u8fd9 \u6781\u77ed\u7684\u4e00\u6bb5\u65f6\u95f4\u91cc\u6062\u590d\uff01\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u81f3\u6b64\uff0c\u9042\u4e5f\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e0d\u62ff\u51fa\u6765\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u731b\u7136\u4e00\u633a\u8170\uff0c\u7adf\u5f80\u524d\u9762\u8fc8\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u4e07\u5929\u840d\u9762\u8272\u4e00\u53d8\uff0c\u76ee\u5149\u4e2d\u6ee1\u542b\u6012\u6c14\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u524d\u89c6\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u731c\u6d4b\u82e5\u662f\u4e0d\u5bf9\uff0c\u90a3\u4e48\u4e07\u5929\u840d\u4e00\u52a8\u624b\uff0c\u81ea\u5df1\u4fbf\u8ba8\u4e0d\u4e86\u597d\u53bb\u3002\u4f46 \u4e8b\u5df2\u81f3\u6b64\uff0c\u4ed6\u53ea\u80fd\u5c06\u5fc3\u4e2d\u7684\u7d27\u5f20\uff0c\u6781\u529b\u63a7\u5236\u7740\u4e0d\u6d41\u9732\u51fa\u6765\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5404\u81ea\u90fd\u5728\u5fc3\u4e2d\u8f6c\u7740\u5ff5\u5934\uff1b\u4e5f\u5404\u81ea\u731c\u900f\u7740\u5bf9\u65b9\u5fc3\u4e2d\u7684\u6253\u7b97\u3002 \u3000\u3000\u4e07\u5929\u840d\u7a81\u5730\u53c8\u5e72\u6da9\u5730\u7b11\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u529d\u4f60\u8fd8\u662f\u5c06\u5b83\u62ff\u51fa\u6765\uff0c\u8fd9\u6837\u5bf9\u4f60\u6211\u90fd\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u53e3\u6c14\u679c\u7136\u7f13\u548c\u4e0b\u6765\uff1a\u5148\u524d\u8bdd\u4e2d\u7684\u5a01\u80c1\u610f\u5473\uff0c\u6b64\u523b\u51cf\u53bb\u4e0d\u5c11\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u53c8\u677e\u4e86\u53e3\u6c14\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u6240\u6599\u60f3\uff0c\u5df2\u79bb\u4e8b\u5b9e\u4e0d\u8fdc\uff0c\u5fc3\u4e2d\u53c8\u6781\u5feb\u5730\u8f6c\u4e86\u51e0\u8f6c\uff0c\u51b7 \u7b11\u9053\uff1a \u3000\u3000\u201c\u544a\u8bc9\u4f60\uff0c\u59d3\u4e07\u7684\uff01\u5929\u661f\u79d8\u7b08\u4e4b\u4e8b\uff0c\u4f60\u518d\u4e5f\u4f11\u63d0\uff01\u4f60\u82e5\u60f3\u751f\u51fa\u6b64\u8c37\uff0c\u54fc\uff01\u90a3\u8fd8\u5f97\u770b\u6211\u9ad8 \u4e0d\u9ad8\u5174\u5462\uff1f\u201d \u3000\u3000\u8bed\u950b\u4e00\u8f6c\uff0c\u7adf\u5b8c\u5168\u626d\u8f6c\u4e86\u5c40\u52bf\uff0c\u7531\u88ab\u5a01\u80c1\u7684\u5730\u4f4d\uff0c\u800c\u53d8\u6210\u5728\u5a01\u80c1\u4eba\u5bb6\u4e86\uff01 \u3000\u3000\u4e07\u5929\u840d\u4e00\u60ca\uff0c\u4ed6\u679c\u5982\u4f0a\u98ce\u6240\u6599\uff0c\u867d\u7136\u5e78\u5f97\u6b7b\u91cc\u9003\u751f\uff0c\u4f46\u529f\u529b\u6731\u590d\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6545\u610f\u4e0d\u5c51 \u5730\u72c2\u7b11\u51e0\u58f0\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u6211\u4e07\u5929\u840d\u95ef\u8361\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u8fd8\u6ca1\u6709\u4eba\u6562\u5728\u6211\u9762\u524d\u8bf4\u8fc7\u8fd9\u79cd\u72c2\u8bdd\u7684\uff01\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u5728\u8bf4\u7740\u8bdd\uff0c\u773c\u5149\u5374\u5728\u4e25\u5bc6\u5730\u6ce8\u89c6\u7740\u4f0a\u98ce\u7684\u53cd\u5e94\uff0c\u6b63\u662f\u8272\u5389\u800c\u5185\u834f\u3002\u4e24\u4eba\u4e92\u6597\u5fc3\u667a \u4e4b\u4e0b\uff0c\u4ed6\u5df2\u8d25\u4e86\u7b2c\u4e00\u9635\u3002 \u3000\u3000\u4f0a\u98ce\u58f0\u968f\u5ff5\u52a8\uff0c\u7a81\u5730\u4e5f\u4f38\u51fa\u624b\u6765\uff0c\u8bed\u6c14\u5f02\u5e38\u4e4b\u51b7\u6f20\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u62ff\u6765\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\u4e00\u6115\uff0c\u5374\u542c\u4f0a\u98ce\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u82e5\u4e0d\u5c06\u90a3\u201c\u7487\u5149\u4eea\u201d\u62ff\u51fa\u6765\uff0c\u4eca\u65e5\u518d\u4e5f\u4f11\u60f3\u751f\u51fa\u6b64\u8c37\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e2d\u7684\u72c2\u50b2\uff0c\u66f4\u8fdc\u5728\u4e07\u5929\u840d\u5411\u4ed6\u7d22\u53d6\u5929\u661f\u79d8\u7b08\u4e4b\u4e0a\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u4e3b\u5ba2\u6613\u52bf\uff0c\u4e07\u5929\u840d\u8138\u8272\u60e8\u767d\uff0c\u540e\u9000\u4e00\u6b65\uff0c\u6697\u4e2d\u5374\u5728\u8c03\u606f\u7740\u771f\u6c14\u3002 \u3000\u3000\u4f0a\u98ce\u53cc\u76ee\u51dd\u89c6\uff0c\u5374\u4e5f\u4e0d\u6562\u5192\u7136\u5411\u4ed6\u52a8\u624b\u3002 \u3000\u3000\u5c71\u98ce\u66f4\u5389\uff0c\u591c\u8272\u6e10\u6d53\u3002 \u3000\u3000\u4f0a\u98ce\u82e5\u5728\u6b64\u65f6\u4e00\u8d70\uff0c\u4e07\u5929\u840d\u65ad\u7136\u4e0d\u4f1a\u62e6\u4ed6\uff0c\u4e5f\u62e6\u4e0d\u4f4f\u4ed6\u3002\u53ef\u662f\u5f53\u5c40\u7740\u8ff7\uff0c\u4f0a\u98ce\u5374\u672a\u8f6c\u5230 \u8fd9\u5ff5\u5934\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u867d\u6ca1\u6709\u8981\u5f97\u5230\u201c\u7487\u5149\u4eea\u201d\u7684\u91ce\u5fc3\uff0c\u7136\u800c\u4ed6\u5374\u60f3\u85c9\u6b64\u6765\u6298\u8fb1\u4e07\u5929\u840d\u4e00\u756a\uff0c\u51fa\u4e00\u51fa\u5fc3\u4e2d\u7684 \u95f7\u6c14\u3002 \u3000\u3000\u4f55\u51b5\u90a3\u81ea\u5c3d\u88ab\u6551\u7684\u4e66\u751f\uff0c\u4ecd\u5012\u5367\u5728\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u751f\u6b7b\u672a\u77e5\uff0c\u4ed6\u4e5f\u4e0d\u613f\u5c31\u6b64\u4e00\u8d70\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u4ed6\u5fc3\u4e2d\u7591\u56e2\u91cd\u91cd\uff0c\u6068\u4e0d\u5f97\u4e07\u5929\u840d\u5c06\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u6b7b\u53bb\u91cd\u751f\u7684\u539f\u56e0\uff0c\u8bf4\u51fa\u6765\u624d\u5bf9\u5fc3 \u601d\u3002 \u3000\u3000\u662f\u4ee5\u5728\u4ed6\u5fc3\u4e2d\uff0c\u6839\u672c\u6ca1\u6709\u60f3\u5230\u4e58\u6b64\u673a\u4f1a\u6e9c\u8d70\u7684\u6253\u7b97\u3002 \u3000\u3000\u4e07\u5929\u840d\u50f5\u7acb\u4e0d\u8bed\uff0c\u4f0a\u98ce\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u6253\u5f00\u8fd9\u50f5\u5c40\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4e07\u5929\u840d\u53cc\u76ee\u4e00\u7ffb\uff0c\u5f3a\u70c8\u7684\u76ee\u5149\u5728\u4f0a\u98ce\u8eab\u4e0a\u4e00\u8f6c\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u51db\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u53ae\u7684\u76ee\u5149\u7a81\u7136\u5f3a\u9510\u4e86\u8d77\u6765\uff0c\u83ab\u975e\u5c31\u5728\u8fd9\u4e00\u523b\u91cc\uff0c\u4ed6\u5df2\u6062\u590d\u4e86\u529f\u529b\u5417\uff0c\u8fd9\u7b80\u76f4\u662f\u4e0d\u53ef \u80fd\u7684\u5440\uff01\u201d \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u4e16\u4e8b\u4e4b\u5947\uff0c\u7109\u662f\u4ed6\u80fd\u60f3\u50cf\u7684\u3002\u8fd9\u4e07\u5929\u840d\u4e0d\u4f46\u529f\u529b\u5df2\u590d\uff0c\u6050\u6015\u6b64\u523b\u4ed6\u7684\u529f \u529b\uff0c\u8fd8\u5728\u4ed6\u672a\u66fe\u53d7\u4f24\u7684\u65f6\u5019\u4e4b\u4e0a\u54e9\uff01 \u3000\u3000\u539f\u6765\u4e07\u5929\u840d\u8eab\u53d7\u91cd\u4f24\u540e\uff0c\u539f\u5df2\u662f\u4e0d\u6cbb\uff0c\u88ab\u4f0a\u98ce\u5c06\u4ed6\u548c\u5999\u624b\u8bb8\u767d\u7684\u5c38\u4f53\uff0c\u642c\u5230\u77f3\u5e8a\u4e0a\uff0c\u4e24 \u4eba\u8eab\u4f53\u7ea0\u7f20\uff0c\u5999\u624b\u8bb8\u767d\uff1a\u4f53\u5185\u6d41\u51fa\u4e4b\u8840\uff0c\u5374\u65e0\u5de7\u4e0d\u5de7\u5730\uff0c\u6d41\u5165\u90a3\u5c1a\u5b58\u4e00\u606f\u7684\u94c1\u9762\u5b64\u884c\u5ba2\u7684\u5634 \u91cc\u3002 \u3000\u3000\u987b\u77e5\u5999\u624b\u8bb8\u81ea\u4f53\u5185\u4e4b\u8840\u6db2\uff0c\u5df2\u6ee1\u542b\u201c\u6bd2\u9f99\u4e39\u201d\u4e4b\u7075\u6548\uff0c\u5374\u65e0\u201c\u6bd2\u9f99\u4e39\u201d\u90a3\u79cd\u81f3\u9633\u81f3\u521a\u7684 \u836f\u529b\uff0c\u6b63\u662f\u5df2\u53d8\u6210\u7edd\u9876\u7075\u4e39\uff0c\u90a3\u5c31\u662f\u8bf4\uff1a\u4efb\u4f55\u4eba\u82e5\u670d\u4e86\u5999\u624b\u8bb8\u767d\u4e4b\u8840\uff0c\u4fbf\u65e0\u6b8a\u4e8e\u670d\u4e86\u5929\u4e0b\u7684 \u5404\u79cd\u7075\u836f\u3002 \u3000\u3000\u4e07\u5929\u840d\u6655\u8ff7\u4e2d\uff0c\u53ea\u89c9\u6709\u4e00\u80a1\u70ed\u529b\uff0c\u7531\u5589\u95f4\u7f13\u7f13\u6ce8\u5165\u4e39\u7530\uff0c\u7adf\u82cf\u9192\u4e86\u8fc7\u6765\u3002\u7a0d\u4e00\u601d\u8003\uff0c\u4ee5 \u4ed6\u7684\u5b66\u8bc6\u5386\u7ec3\uff0c\u4ed6\u7acb\u523b\u5c31\u5224\u65ad\u51fa\u81ea\u5bb6\u4e4b\u6240\u4ee5\u80fd\u591f\u8d77\u6b7b\u56de\u751f\u7684\u539f\u56e0\u3002\u4e8e\u662f\u4ed6\u5c31\u5c06\u5999\u624b\u8bb8\u81ea\u4f53\u5185 \u7684\u8840\u6db2\uff0c\u542e\u5438\u4e00\u5c3d\u3002 \u3000\u3000\u987f\u65f6\uff0c\u4ed6\u53c8\u56de\u590d\u4e86\u751f\u5b58\u7684\u6d3b\u529b\u3002\u4e8e\u662f\u4ed6\u4ece\u8bb8\u767d\u6000\u4e2d\u641c\u51fa\u4e86\u7487\u5149\u4eea\u7684\u4e00\u534a\uff0c\u79bb\u5f00\u4e86\u79d8\u7a9f\uff0c \u5c06\u77f3\u5ba4\u4e2d\u7684\u73cd\u5b9d\uff0c\u5c3d\u9119\u80fd\u6346\u4e86\u4e00\u5305\u3002\u56e0\u4e3a\u5999\u624b\u8bb8\u767d\u4e00\u6b7b\uff0c\u4ed6\u5df2\u65e0\u9700\u5728\u8fd9\u6df1\u5c71\u4e2d\u7559\u4e0b\u7684\u5fc5\u8981\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7684\u786e\u662f\u56e0\u7978\u5f97\u798f\uff1a\u53ea\u662f\u201c\u5929\u661f\u79d8\u7b08\u201d\u5f97\u800c\u590d\u5931\uff0c\u662f\u552f\u4e00\u7f8e\u4e2d\u4e0d\u8db3\u4e4b\u5904\u3002\u4ed6\u9887\u4e3a\u540e \u6094\uff0c\u4e0d\u77e5\u9053\u90a3\u5e74\u8f7b\u4eba\u7684\u6765\u5386\u4e0b\u843d\uff1b\u56e0\u4e3a\u4ed6\u77e5\u9053\u5728\u4ed6\u548c\u5999\u624b\u8bb8\u767d\u76f8\u4e89\u7684\u65f6\u5019\uff0c\u90a3\u5e74\u8f7b\u4eba\u4e00\u5b9a\u6e14 \u7fc1\u5f97\u5229\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u5c31\u5728\u6b64\u65f6\uff0c\u4f0a\u98ce\u7adf\u7136\u53c8\u56de\u5230\u8fd9\u5c71\u5773\u91cc\u6765\uff0c\u4e07\u5929\u840d\u4e00\u89c1\u5927\u559c\uff0c\u4f46\u4ed6\u6b64\u523b\u751f\u529b\u867d\u590d\uff0c\u7136 \u800c\u56db\u80a2\u5374\u8f6f\u8f6f\u7684\uff0c\u90a3\u6b63\u662f\u56e0\u4e3a\u201c\u6bd2\u9f99\u4e39\u201d\u7684\u6548\u529b\u5df2\u5728\u4ed6\u4f53\u5185\u884c\u5f00\uff0c\u82e5\u4ed6\u6b64\u523b\u80fd\u7acb\u523b\u4ee5\u672c\u8eab\u7684 \u529f\u529b\u4e0e\u4e4b\u76f8\u5408\uff0c\u90a3\u4e48\u4ed6\u7684\u529f\u529b\u4fbf\u53ef\u500d\u957f\u6570\u500d\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u5374\u5c06\u8fd9\u5343\u8f7d\u96be\u9022\u7684\u5947\u7f18\u6d6a\u8d39\u4e86\uff0c\u201c\u6bd2\u9f99\u4e39\u201d\u672c\u53ef\u53d1\u6325\u5341\u6210\u7684\u836f\u529b\uff0c\u5728\u4ed6\u4f53\u5185\u53ea\u53d1 \u6325\u4e86\u4e24\u6210\uff0c\u7136\u800c\u5c31\u53ea\u8fd9\u4e24\u6210\uff0c\u5df2\u8db3\u591f\u4f7f\u4ed6\u7684\u529f\u529b\u589e\u957f\uff0c\u5c06\u4ed6\u7684\u751f\u547d\u4ece\u6b7b\u4ea1\u4e4b\u4e2d\u593a\u4e86\u56de\u6765\u3002 \u3000\u3000\u4ed6\u56db\u80a2\u8f6f\u800c\u65e0\u529b\uff0c\u81ea\u7136\u6ca1\u6709\u7acb\u523b\u73b0\u8eab\u3002\u4f0a\u98ce\u5165\u4e86\u77f3\u7a9f\u540e\uff0c\u90a3\u4e66\u751f\u773c\u8ff7\u4e8e\u73cd\u5b9d\uff0c\u7adf\u4ece\u7a97\u53e3 \u4e2d\u722c\u4e86\u8fdb\u53bb\u3002\u4e07\u5929\u840d\u4e00\u770b\u4ed6\u7684\u8eab\u6cd5\uff0c\u5c31\u77e5\u9053\u4ed6\u5b8c\u5168\u4e0d\u4f1a\u6b66\u529f\uff0c\u4e8e\u662f\u5c31\u4ee5\u4e00\u7c92\u4e09 \u3000\u3000\u77f3\u5b50\uff0c\u9694\u7a97\u51fb\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6cd5\u662f\u4f55\u7b49\u529b\u9053\uff1b\u867d\u7136\u53ea\u662f\u4e00\u7c92\u77f3\u5b50\uff0c\u7136\u800c\u5df2\u4f7f\u5f97\u90a3\u4e66\u751f\u53f3\u81c2\u6298\u65ad\uff0c\u5f53\u65f6\u91cf\u8ff7\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u540e\u6765\u4f0a\u98ce\u81ea\u77f3\u7a9f\u4e2d\u8dd1\u51fa\u6765\uff0c\u4e07\u5929\u840d\u7a81\u7136\u73b0\u8eab\uff0c\u679c\u7136\u5c06\u4f0a\u98ce\u5413\u5f97\u9762\u65e0\u4eba\u8272\u3002 \u3000\u3000\u4f46\u8bed\u950b\u4e00\u53d8\u4e4b\u4e0b\uff0c\u4e07\u5929\u840d\u5374\u843d\u4e86\u4e0b\u98ce\uff0c\u662f\u4ee5\u4ed6\u53ea\u5e0c\u671b\u81ea\u5df1\u7684\u529f\u529b\u80fd\u591f\u8d76\u7d27\u6062\u590d\u3002 \u3000\u3000\u7565\u4e00\u8c03\u606f\u4e4b\u4e0b\uff0c\u6bd2\u9f99\u4e39\u5df2\u89c1\u529f\u6548\uff0c\u4e07\u5929\u840d\u771f\u6c14\u8fd0\u884c\u4e00\u5468\u540e\uff0c\u81ea\u5df1\u5df2\u89c9\u51fa\u4e86\u81ea\u5df1\u7684\u529b\u91cf\uff0c \u53cc\u76ee\u4e00\u7ffb\uff0c\u4fbf\u8981\u5c06\u4f0a\u98ce\u4f24\u5728\u638c\u4e0b\u3002 \u3000\u3000\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u731b\u4e00\u9519\u6b65\uff0c\u8eab\u5f62\u5982\u884c\u4e91\u6d41\u6c34\uff0c\u500f\u7136\u63a0\u4e0a\u524d\u6765\uff0c\u53cc\u638c\u5fae\u4e00\u4ea4\u9519\uff0c\u5728\u4e2d\u95f4\u5212\u4e86 \u4e2a\u5706\u5708\uff0c\u5374\u53c8\u7535\u4e5f\u4f3c\u7684\u4e0a\u4e0b\u4ea4\u51fb\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62db\u638c\u5f71\u7f24\u7eb7\uff0c\u6b63\u662f\u5148\u8981\u4e71\u4e86\u5bf9\u65b9\u7684\u773c\u795e\uff0c\u518d\u731b\u529b\u4e00\u51fb\u3002 \u3000\u3000\u4f0a\u98ce\u5927\u60ca\u4e4b\u4e0b\uff0c\u8d76\u7d27\u4e00\u584c\u8170\uff0c\u8eab\u5f62\u53f3\u65cb\uff0c\u5de6\u638c\u55d6\u7136\u51fb\u51fa\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u6b64\u65f6\u7684\u529f\u529b\uff0c\u867d\u7136\u5df2\u65e0\u6b8a\u4e8e\u4e00\u6d41\u9ad8\u624b\uff0c\u7136\u800c\u4ed6\u52a8\u624b\u7684\u62db\u5f0f\uff0c\u5374\u4ecd\u7136\u4e0d\u89c1\u5f97\u5947\u5999\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u201c\u51e4\u51f0\u5355\u5c55\u7fc5\u201d\uff0c\u867d\u7136\u795e\u5b8c\u6c14\u8db3\uff0c\u52b2\u529b\uff0c\u90e8\u4f4d\u4e5f\u6070\u5230\u597d\u5904\uff0c\u5728\u6b66\u6797\u4e2d\u5df2\u53ef\u7b97\u5f97\u4e0a \u662f\u7edd\u5999\u9ad8\u62db\u3002 \u3000\u3000\u7136\u800c\u5728\u94c1\u9762\u5b64\u884c\u5ba2\u8fd9\u79cd\u4eba\u7684\u773c\u4e2d\uff0c\u5374\u662f\u666e\u901a\u5df2\u6781\u3002 \u3000\u3000\u4e07\u5929\u840d\u518d\u6b21\u51b7\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u626d\uff0c\u53cc\u638c\u539f\u5f0f\u51fb\u51fa\uff0c\u53ea\u662f\u6539\u62cd\u4e3a\u6293\uff0c\u5341\u6307\u7b95\u5f20\uff0c\u7528\u7684\u6b63\u662f \u4ed6\u540d\u9707\u6b66\u6797\u7684\u5927\u529b\u9e70\u722a\u795e\u529f\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62db\u7701\u53bb\u4e86\u53d8\u62db\u7684\u65f6\u95f4\uff0c\u81ea\u7136\u5feb\u8fc5\u5df2\u6781\u3002\u4f0a\u98ce\u7684\u5de6\u638c\u521a\u521a\u9012\u51fa\uff0c\u5c31\u5df2\u89c9\u5f97\u4eba\u5bb6\u7684\u53cc \u624b\uff0c\u5df2\u5206\u5411\u81ea\u5df1\u7684\u5589\u5934\u548c\u8179\u4e0b\u6293\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff01\u4ed6\u51fa\u9053\u6c5f\u6e56\uff0c\u52a8\u624b\u7684\u6b21\u6570\u5df2\u4e0d\u4e0b\u6570\u767e\u6b21\uff0c\u7136\u800c\u50cf\u8fd9\u6837\u5feb\u7684\u62db \u5f0f\uff0c\u4ed6\u8fd8\u662f\u7b2c\u4e00\u6b21\u9047\u5230\u7684\u3002 \u3000\u3000\u4ed6\u6765\u4e0d\u53ca\u518d\u8f6c\u522b\u7684\u5ff5\u5934\uff0c\u957f\u8170\u4e00\u626d\uff0c\u8e6c\uff0c\u8e6c\uff0c\u8e6c\uff0c\u8fde\u7740\u5012\u9000\u4e09\u6b65\uff0c\u4f46\u4e07\u5929\u840d\u5982\u5f62\u9644\u5f71\uff0c \u4e5f\u8ddf\u4e86\u4e0a\u6765\uff0c\u53cc\u638c\u5404\u5404\u5212\u4e86\u4e2a\u534a\u5f27\uff0c\u638c\u5c16\u5fae\u66f2\uff0c\u51fb\u5411\u4f0a\u98ce\u7684\u524d\u80f8\uff0c\u62db\u5f0f\u867d\u53d8\uff0c\u4f46\u8155\u8098\u672a\u5f2f\uff0c \u6839\u672c\u4e0d\u50cf\u666e\u901a\u6b66\u6797\u4e2d\u4eba\u5728\u64a4\u62db\u53d8\u62db\u4e4b\u95f4\uff0c\u8fd8\u5f97\u8d39\u53bb\u4e00\u4e9b\u529f\u592b\u3002 \u3000\u3000\u4f0a\u98ce\u77e5\u9053\uff1a\u53ea\u8981\u81ea\u5bb6\u8ba9\u4eba\u5bb6\u7684\u6307\u5c16\u642d\u4e0a\u4e00\u70b9\uff0c\u90a3\u4e48\u4eba\u5bb6\u5185\u5bb6\u201c\u5c0f\u5929\u661f\u201d\u7684\u638c\u529b\uff0c\u4fbf\u5f97\u63a5 \u8e35\u800c\u6765\u3002\u800c\u4e14\u4ed6\u77e5\u9053\uff1a\u8fd9\u4e07\u5929\u840d\u4eba\u867d\u7626\u5c0f\uff0c\u529f\u529b\u5374\u662f\u6700\u4ee5\u90a3\u79cd\u81f3\u521a\u81f3\u5f3a\u7684\u5185\u5bb6\u638c\u529b\u89c1\u957f\uff0c\u90a3 \u6562\u548c\u4eba\u5bb6\u786c\u78b0\u786c\u5730\u5bf9\u638c\uff0c\u811a\u6b65\u4e00\u9519\uff0c\u53c8\u5411\u540e\u9762\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u5b58\u602f\u654c\u4e4b\u610f\uff0c\u8d8a\u53d1\u5730\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u800c\u65e0\u8fd8\u624b\u4e4b\u529b\uff01 \u3000\u3000\u5176\u5b9e\u4ed6\u82e5\u80fd\u9759\u4e0b\u5fc3\u6765\uff0c\u4ee5\u4ed6\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\u5df2\u901a\u540e\u7684\u5185\u5bb6\u771f\u529b\uff0c\u6765\u548c\u4e07\u5929\u840d\u4e00\u62da\uff0c\u867d \u7136\u4e0d\u80fd\u53d6\u80dc\uff0c\u4f46\u4e5f\u4e0d\u81f4\u4e8e\u5982\u6b64\u72fc\u72c8\u3002 \u3000\u3000\u4e07\u5929\u840d\u51b7\u7b11\u8fde\u8fde\uff0c\u53e3\u4e2d\u8ba5\u8bbd\u9053\uff1a \u3000\u3000\u201c\u5c31\u51ed\u4f60\u8fd9\u6837\u7684\u8eab\u624b\uff0c\u8fd8\u6562\u5411\u6211\u8001\u4eba\u5bb6\u8bf4\u90a3\u79cd\u72c2\u8bdd\uff1f\u201d \u3000\u3000\u53cc\u638c\u5374\u8fd0\u638c\u5982\u98ce\uff0c\u5e26\u7740\u864e\u864e\u98ce\u58f0\u548c\u6f2b\u5929\u638c\u5f71\uff0c\u4e0a\u4e0b\u5de6\u53f3\u5730\u5411\u4f0a\u98ce\u5288\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u867d\u7136\u52c9\u529b\u652f\u6301\uff0c\u4f46\u6280\u4e0d\u5982\u4eba\uff0c\u53ea\u6709\u4e00\u6b65\u6b65\u5730\u540e\u9000\u3002 \u3000\u3000\u5341\u4f59\u62db\u4e00\u8fc7\uff0c\u4f0a\u98ce\u66f4\u4e0d\u652f\u3002\u4e07\u5929\u840d\u638c\u5f0f\u5374\u500f\u7136\u4e00\u53d8\uff0c\u7531\u731b\u653b\u800c\u53d8\u4e3a\u6e38\u6597\uff0c\u4ed6\u7adf\u60f3\u5c06\u8fd9\u66fe \u7ecf\u6298\u8fb1\u8fc7\u81ea\u5df1\u7684\u5e74\u8f7b\u4eba\u5148\u51cc\u8fb1\u4e00\u756a\uff0c\u518d\u7f6e\u4e4b\u6b7b\u5730\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u51fa\u62db\u7684\u624b\u6cd5\uff0c\u5c31\u4e0d\u4f3c\u65b9\u624d\u7684\u5a01\u731b\u6c89\u91cd\uff1b\u51fa\u624b\u7684\u90e8\u4f4d\uff0c\u4e5f\u4e0d\u518d\u51fb\u5411\u4f0a\u98ce\u7684\u8981\u5bb3\u3002\u53e3 \u4e2d\u5374\u51b7\u8bbd\u70ed\u9a82\uff0c\u5c06\u4f0a\u98ce\u9a82\u5f97\u4e2a\u4e0d\u4ea6\u4e50\u4e4e\u3002 \u3000\u3000\u4f0a\u98ce\u8fd9\u4e00\u4e0b\u5fc3\u91cc\u7684\u96be\u53d7\uff0c\u53ef\u66f4\u5728\u5148\u524d\u4e4b\u4e0a\uff01 \u3000\u3000\u53ea\u662f\u4ed6\u529f\u529b\u4e0d\u902e\uff0c\u6b64\u523b\u5c31\u662f\u518d\u60f3\u9003\u8d70\uff0c\u6050\u6015\u4e5f\u4e0d\u80fd\u591f\u4e86\u2014\u2014"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u6df1\u5bb5\u5f02\u4e8b",content:(0,t.jsx)(t.Fragment,{children:"\u62cd\u5730\uff0c\u4f0a\u98ce\u80a9\u5934\u7adf\u4e2d\u4e00\u4e86\u638c\uff0c\u867d\u7136\u9690\u9690\u4f5c\u75db\uff0c\u4f46\u5374\u672a\u4f24\u53ca\u7b4b\u9aa8\u3002 \u3000\u3000\u4f0a\u98ce\u77e5\u9053\u5bf9\u65b9\u7684\u7528\u610f\u3002\u53cc\u638c\u201c\u6cfc\u98ce\u516b\u6253\u201d\uff0c\u638c\u98ce\u864e\u864e\uff0c\u4f46\u5374\u4f24\u4e0d\u5230\u5bf9\u65b9\u7684\u6beb\u53d1\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u6e10\u9000\uff0c\u8f6c\u8eab\u4e4b\u95f4\uff0c\u5ffd\u7136\u770b\u5230\u90a3\u201c\u6b66\u66f2\u661f\u541b\u201d\u85cf\u8d38\u7684\u79d8\u7a9f\uff0c\u90a3\u5c01\u95e8\u7684\u5de8\u77f3\uff0c\u539f\u662f\u7531 \u4e2d\u95f4\u65cb\u5f00\uff0c\u6b64\u523b\u90a3\u5757\u5de8\u77f3\u4fbf\u6a2a\u4e98\u5728\u79d8\u7a9f\u6d1e\u53e3\u7684\u4e2d\u95f4\uff0c\u4e24\u8fb9\u9732\u51fa\u91cc\u9762\u9ed1\u9edd\u6df1\u9083\u7684\u6d1e\u7a9f\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u811a\u4e0b\u9519\u6b65\u95f4\uff0c\u4fbf\u6e10\u6e10\u5411\u90a3\u6d1e\u7a9f\u91cc\u79fb\u53bb\u3002 \u3000\u3000\u4e07\u5929\u840d\u638c\u5f71\u4ea4\u9519\uff0c\u53cc\u638c\u50cf\u662f\u4e24\u53ea\u8774\u8776\u4f3c\u7684\uff0c\u5728\u4f0a\u98ce\u8eab\u4fa7\u56db\u821e\u3002\u4ed6\u540d\u5782\u6b66\u6797\uff0c\u62db\u5f0f\u4e0a\u679c\u6709 \u72ec\u5f97\u4e4b\u79d8\uff0c\u4e0d\u662f\u4e00\u822c\u6b66\u6797\u638c\u6cd5\u3002 \u3000\u3000\u4ed6\u5de6\u638c\u4e00\u5708\uff0c\u500f\u5730\u53cd\u638c\u6325\u51fa\uff0c\u53e3\u4e2d\u5374\u51b7\u6f20\u800c\u8ba5\u5632\u5730\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b69\u5b50\uff01\u4f60\u5c06\u201c\u5929\u661f\u79d8\u7b08\u201d\u62ff\u51fa\uff0c\u518d\u4e56\u4e56\u5411\u6211\u8001\u4eba\u5bb6\u53e9\u4e09\u4e2a\u5934\uff0c\u6211\u8001\u4eba\u5bb6\u4e00\u9ad8\u5174\uff0c\u8bf4 \u4e0d\u5b9a\u4e0d\u4f46\u653e\u4e86\u4f60\uff0c\u8fd8\u6536\u4f60\u505a\u5f92\u5f1f\uff0c\u4e5f\u672a\u53ef\u77e5\u2026\u2026\u201d \u3000\u3000\u4f0a\u98ce\u66b4\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u5c3d\u4e86\u5341\u6210\u5927\u529b\u5411\u524d\u731b\u51fb\u3002\u4e07\u5929\u840d\u8bed\u58f0\u4e00\u987f\uff0c\u8eab\u5f62\u5fae\u5fae\u540e\u632b\u3002\u90a3\u77e5\u4f0a \u98ce\u8fd9\u4e00\u62db\uff0c\u5374\u662f\u4ee5\u8fdb\u4e3a\u9000\uff0c\u638c\u624d\u5230\u4e2d\u9014\uff0c\u5c31\u731b\u5730\u540e\u64a4\uff0c\u8eab\u5f62\u540e\u6291\uff0c\u201c\u91d1\u9ca4\u5012\u7a7f\u6ce2\u201d\uff0c\u5411\u540e\u9762 \u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u8ba1\u7b97\u597d\u90a3\u79d8\u7a9f\u7684\u4f4d\u7f6e\uff0c\u8eab\u5f62\u5728\u7a7a\u4e2d\u731b\u65cb\uff0c\u811a\u5c16\u4e00\u70b9\u5730\uff0c\u5237\u5730\uff0c\u5411\u79d8\u7a9f\u4e2d\u7a9c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4e07\u5929\u840d\u5fae\u60ca\u4e4b\u4e0b\uff0c\u8eab\u5f62\u7acb\u523b\u66b4\u8d77\uff0c\u4e5f\u76f4\u63a0\u5165\u6d1e\u3002\u90a3\u77e5\u8eab\u540e\u98ce\u58f0\u98d2\u7136\uff0c\u4ed6\u7981\u4e0d\u4f4f\u56de\u5934\u4e00 \u770b\uff0c\u539f\u6765\u90a3\u5c01\u6d1e\u7684\u5de8\u77f3\u4e5f\u968f\u7740\u4ed6\u7684\u6765\u52bf\u800c\u65cb\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u56de\u5934\u4e00\u6115\u4e4b\u95f4\uff0c\u62cd\u5730\u4e00\u58f0\uff0c\u90a3\u5757\u5de8\u77f3\u53c8\u5d4c\u56de\u6d1e\u53e3\u5c71\u58c1\u4e4b\u4e0a\uff0c\u4e07\u5929\u840d\u5927\u60ca\u56db\u987e\uff0c\u6d1e \u4e2d\u9ed1\u6697\u5f97\u8fde\u4e00\u4e1d\u5fae\u5149\u90fd\u6ca1\u6709\uff0c\u4ed6\u8d76\u7d27\u5c4f\u4f4f\u547c\u5438\uff0c\u53cc\u638c\u5f53\u80f8\uff0c\u751f\u6015\u4f0a\u98ce\u4f1a\u5728\u9ed1\u6697\u4e2d\u5411\u81ea\u5bb6\u6697 \u7b97\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u4f0a\u98ce\u65e9\u6709\u8ba1\u7b97\u8ba1\uff0c\u4e00\u5165\u6d1e\u540e\uff0c\u5c31\u6273\u7740\u90a3\u5757\u5de8\u77f3\u5728\u6d1e\u5185\u7684\u4e00\u7aef\u5411\u5916\u4e00\u65cb\u3002\u4ed6 \u81ea\u5df1\u5374\u5728\u90a3\u5757\u5de8\u77f3\u5c06\u5408\u672a\u5408\u4e4b\u9645\uff0c\u63a0\u51fa\u6d1e\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u65f6\u95f4\uff0c\u90e8\u4f4d\uff0c\u8981\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\uff1b\u800c\u4e14\u5fc5\u987b\u5fc3\u601d\u8fc7\u4eba\uff0c\u624d\u80fd\u5c06\u4eba\u5bb6\u5173\u8fdb\u6d1e\u7a9f\uff0c\u800c \u81ea\u5df1\u5374\u63a0\u51fa\u5916\u9762\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u5927\u610f\u4e4b\u4e0b\uff0c\u7adf\u88ab\u4f0a\u98ce\u5c01\u4e8e\u8fd9\u9edd\u9ed1\uff0c\u9634\u68ee\uff0c\u800c\u6df1\u9083\u7684\u6d1e\u7a9f\u4e4b\u5185\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u8ba1\u5f97\u6210\uff0c\u60ca\u9b42\u521d\u5b9a\uff0c\u5c71\u98ce\u5439\u5230\u4ed6\u8eab\u4e0a\uff0c\u867d\u7136\u5bd2\u51b7\uff0c\u4ed6\u5374\u89c9\u5f97\u975e\u5e38\u53ef\u7231\u3002 \u3000\u3000\u4ed6\u7565\u7565\u5598\u4e86\u4e24\u53e3\u6c14\uff0c\u8ba9\u6fc0\u6218\u4e4b\u540e\u7684\u5fc3\u60c5\uff0c\u5e73\u590d\uff0c\u677e\u5f1b\u4e0b\u6765\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8f7b\u63a0\u81f3\u77f3\u5c4b\u65c1\uff0c\u7ffb\u8eab\u5165\u7a97\uff0c\u8499\u80e7\u4e4b\u5149\u4e0b\uff0c\u4ed6\u770b\u5230\u90a3\u4e66\u751f\u4ecd\u4fef\u5367\u5728\u5730\u4e0a\u3002\u4ed6\u6697\u53f9\u4e00 \u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4ed6\u82e5\u662f\u6b7b\u4e86\uff0c\u90a3\u6211\u6551\u4ed6\u53cd\u6210\u4e86\u5bb3\u4ed6\u4e86\uff01\u201d \u3000\u3000\u8e72\u4e0b\u8eab\u5b50\uff0c\u63a2\u4e86\u63a2\u4ed6\u7684\u9f3b\u606f\uff0c\u5374\u53d1\u73b0\u4ed6\u4ecd\u662f\u6d3b\u7740\u7684\uff0c\u53ea\u662f\u6655\u5374\u4e86\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u5c06\u5269\u4e0b\u7684\u73e0\u5b9d\uff0c\u5377\u505a\u4e00\u5305\uff1b\u81f3\u4e8e\u5176\u4ed6\u73e0\u5b9d\u7684\u53bb\u5411\uff0c\u4ed6\u5df2\u518d\u65e0\u8fd9\u5fc3\u60c5\u53bb\u8ffd\u7a76\u4e86\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c06\u53d7\u4f24\u6655\u8ff7\u7684\u7a77\u4e66\u751f\uff0c\u642d\u5728\u80a9\u4e0a\uff0c\u51fa\u4e86\u77f3\u5ba4\uff0c\u63a0\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u8fd9\u7a77\u4e66\u751f\u4f24\u6108\u4e4b\u540e\uff0c\u4fbf\u5e26\u4e86\u4f0a\u98ce\u7ed9\u4ed6\u7684\u73e0\u5b9d\uff0c\u56de\u5230\u5c18\u4e16\uff0c\u800c\u5c18\u4e16\u4e5f\u591a\u4e86\u4e2a\u6325\u91d1\u5982\u571f\u7684\u9614 \u5c11\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u81ea\u59cb\u81f3\u7ec8\uff0c\u4e5f\u5f04\u4e0d\u6e05\u90a3\u4f7f\u4ed6\u7531\u8d64\u8d2b\u53d8\u4e3a\u8c6a\u5bcc\u7684\u4fa0\u58eb\uff0c\u5012\u5e95\u662f\u600e\u4e48\u4e2a\u4eba\u54e9\uff01 \u3000\u3000\u81f3\u4e8e\u4f0a\u98ce\uff0c\u4ed6\u51ed\u7740\u81ea\u8eab\u7684\u667a\u6167\uff0c\u6218\u80dc\u4e86\u5f3a\u4e8e\u81ea\u8eab\u7684\u5bf9\u624b\uff0c\u5f97\u5230\u4e86\u8db3\u4ee5\u50b2\u89c6\u6b66\u6797\u7684\u79d8\u7b08\uff0c \u4e5f\u5f97\u4e86\u4e16\u95f4\u4ec5\u6709\u7684\u89e3\u836f\uff0c\u5fc3\u60c5\u81ea\u7136\u662f\u5077\u5feb\u7684\u3002 \u3000\u3000\u4ed6\u8eab\u5fc3\u677e\u5f1b\u4e4b\u4e0b\uff0c\u89c9\u5f97\u6709\u96be\u4ee5\u5f62\u5bb9\u7684\u75b2\u5026\u3002\u7eb5\u7136\u4ed6\u662f\u94c1\u6253\u7684\u8eab\u8eaf\uff0c\u4f46\u7ecf\u8fc7\u8fd9\u4e48\u591a\u7684\u4e0d\u7720 \u4e0d\u4f11\uff0c\u518d\u52a0\u4e0a\u5fc3\u60c5\u7684\u7d27\u5f20\u548c\u4e00\u756a\u6fc0\u6218\uff0c\u6b64\u523b\u4ed6\u5f53\u7136\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff01\u5230\u666f\u4e1c\uff0c\u4ed6\u5c31\u6b47\u4e0b\u4e86\u3002 \u3000\u3000\u4ed6\u7761\u5f97\u81ea\u662f\u6781\u6c89\uff1a\u56e0\u4e3a\u8fd9\u4e9b\u5929\u6765\uff0c\u7761\u89c9\u5bf9\u4ed6\u800c\u8a00\uff0c\u5df2\u662f\u4e00\u79cd\u5962\u4f88\u7684\u4eab\u53d7\u4e86\u3002 \u3000\u3000\u4ed6\u68a6\u5230\u4ed6\u7684\u59bb\u5b50\u53c8\u56de\u5230\u4ed6\u7684\u8eab\u65c1\u3002\u9192\u6765\u7684\u65f6\u5019\uff0c\u5374\u66f4\u4e3a\u6005\u60d8\uff01\u51fa\u795e\u5730\u671b\u7740\u7a97\u5916\uff0c\u7a97\u5916\u4e00 \u7247\u8499\u80e7\uff0c\u539f\u6765\u6b64\u523b\u53c8\u662f\u6df1\u591c\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u8d77\u6765\uff0c\u53ea\u662f\u9759\u9759\u5367\u5728\u5e8a\u4e0a\uff0c\u542c\u7740\u7a97\u5916\u7684\u98ce\u58f0\u3002\u5bf9\u4eba\u4e16\u95f4\u7684\u8bb8\u591a\u4e8b\uff0c\u7a81\u7136\u8d77\u4e86\u53e6\u5916 \u4e00\u79cd\u60f3\u6cd5\u3002 \u3000\u3000\u4ed6\u59bb\u5b50\u7f8e\u4e3d\u7684\u9762\u5e9e\uff0c\u5728\u4ed6\u8111\u6d77\u4e2d\u6cdb\u6d8c\u7740\uff0c\u4e00\u4f1a\u513f\u90a3\u4e48\u6df1\uff0c\u4e00\u4f1a\u513f\u53c8\u6de1\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u542c\u5230\u7a97\u5916\u7684\u98ce\u58f0\u4e2d\uff0c\u5939\u6742\u6709\u591c\u884c\u4eba\u8863\u8882\u5e26\u98ce\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u82e5\u662f\u5728\u4ee5\u524d\uff0c\u4ed6\u4f1a\u6beb\u4e0d\u8fdf\u7591\u5730\u63a0\u51fa\u53bb\uff0c\u8ffd\u67e5\u8fd9\u591c\u884c\u4eba\u5728\u6df1\u591c\u4e4b\u4e2d\u8d70\u52a8\uff0c\u662f\u4e3a\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u6b64\u523b\uff0c\u4ed6\u5374\u4ecd\u7136\u610f\u5174\u8427\u7d22\u5730\u8eba\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u201c\u522b\u4eba\u7684\u4e8b\uff0c\u6211\u53c8\u4f55\u5fc5\u53bb\u7ba1\uff1f\u201d \u3000\u3000\u4ed6\u6697\u5fd6\u7740\uff01 \u3000\u3000\u201c\u6211\u7684\u4e8b\uff0c\u4e0d\u4e5f\u6ca1\u6709\u522b\u4eba\u7ba1\u5417\uff1f\u6211\u5728\u82cf\u4e1c\uff0c\u88ab\u5929\u4e89\u6559\u7684\u4e09\u4e2a\u91d1\u8863\u9999\u4e3b\u6240\u56f0\uff0c\u9669\u4e9b\u906d\u4e86\u6bd2 \u624b\uff0c\u90a3\u65f6\u53c8\u6709\u8c01\u6765\u7ba1\u6211\uff1f\u6211\u5931\u59bb\u4e4b\u540e\uff0c\u53c8\u88ab\u903c\u547d\uff0c\u82b8\u82b8\u6b66\u6797\u4e2d\uff0c\u53c8\u6709\u51e0\u4eba\u80af\u7ad9\u51fa\u6765\u4e3a\u6211\u8bf4\u4e24 \u53e5\u8bdd\u7684\uff1f\u201d \u3000\u3000\u4ed6\u843d\u5bde\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ee5\u524d\uff0c\u4ed6\u7684\u601d\u60f3\u662f\u7b14\u76f4\u7684\u3002\u6b64\u523b\u5374\u968f\u7740\u4eba\u95f4\u4e8b\u800c\u6709\u4e86\u8bb8\u591a\u5f2f\u66f2\uff0c\u800c\u4ed6\u4e5f\u8fdc\u4e0d\u5982\u4ee5\u524d\u5e78\u798f \u4e86\uff01 \u3000\u3000\u6df1\u591c\u7eee\u601d\uff0c\u4ed6\u53c8\u60f3\u8bb8\u591a\u4eba\uff1b\u4ed6\u751a\u81f3\u60f3\u8d77\u90a3\u5a07\u5c0f\u660e\u5a9a\u7684\u7a1a\u51e4\u9ea6\u6167\u2014\u2014 \u3000\u3000\u84e6\u5730\uff0c\u7a97\u5916\u7684\u9ed1\u6697\u4e2d\uff0c\u4f20\u6765\u4e00\u58f0\u9510\u5229\u7684\u5c16\u53eb\uff0c\u5c06\u4ed6\u7684\u601d\u8def\u6253\u65ad\u4e86\u3002 \u3000\u3000\u867d\u7136\u4ed6\u8ba4\u4e3a\u81ea\u5df1\u5df2\u7ecf\u5f88\u591f\u81ea\u79c1\uff1b\u4f46\u662f\u542c\u5230\u8fd9\u79cd\u60e8\u5389\u7684\u53eb\u58f0\uff0c\u4ed6\u5374\u518d\u4e5f\u65e0\u6cd5\u5728\u5e8a\u4e0a\u9759\u5367\u4e0b \u53bb\u3002 \u3000\u3000\u867d\u7136\u4ed6\u8b66\u544a\u81ea\u5df1\u4e0d\u8981\u591a\u7ba1\u95f2\u4e8b\uff0c\u5148\u8d76\u7d27\u5c06\u89e3\u836f\u9001\u5230\u7ec8\u5357\u5c71\u53bb\uff1b\u7136\u800c\u4e00\u79cd\u5929\u751f\u7684\u4fa0\u4e49\u4e4b \u5fc3\uff0c\u5374\u5728\u4ed6\u8840\u6db2\u4e4b\u4e2d\u5954\u6cb8\u7740\uff0c\u800c\u4ed6\u5374\u65e0\u6cd5\u6297\u62d2\u8fd9\u79cd\u529b\u91cf\u3002 \u3000\u3000\u201c\u53bb\u770b\u770b\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4e5f\u8d39\u4e0d\u4e86\u591a\u5c11\u65f6\u5019\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u5306\u5306\u7a7f\u4e0a\u9774\u5b50\uff0c\u4e00\u9762\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u8fd9\u4f1a\u53c8\u662f\u4ec0\u4e48\u5947\u4eba\u5947\u4e8b\uff01\u4ee5\u524d\u6211\u884c\u8d70\u6c5f\u6e56\u6240\u9047\u4e4b\u4e8b\uff0c\u4e0d\u5c31\u90fd\u662f\u7247\u523b\u4e4b\u95f4\u5c31\u53ef\u89e3\u51b3 \u7684\u5417\uff1f\u201d \u3000\u3000\u4ed6\u66ff\u81ea\u5df1\u627e\u5230\u4e86\u7406\u7531\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7528\u4e00\u6761\u4e1d\u5dfe\u624e\u4f4f\u8863\u895f\uff0c\u5c06\u89e3\u836f\u548c\u79d8\u7b08\uff0c\u90fd\u8c28\u614e\u5730\u63e3\u5230\u6000\u91cc\u3002 \u3000\u3000\u4ed6\u4e45\u8d70\u6c5f\u6e56\uff0c\u884c\u4e8b\u5df2\u6781\u4e3a\u5c0f\u5fc3\u4e86\u3002 \u3000\u3000\u7136\u540e\u4ed6\u8eab\u5f62\u4e00\u52a8\uff0c\u55d6\u7136\u4ece\u7a97\u4e2d\u63a0\u4e86\u51fa\u53bb\uff0c\u5411\u90a3\u60e8\u53eb\u58f0\u7684\u6765\u5904\u7a9c\u53bb\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u811a\u4e0b\u7684\u623f\u5c4b\u90fd\u662f\u9ed1\u6697\u800c\u6c89\u5bc2\u7684\uff1b\u800c\u90a3\u58f0\u60e8\u53eb\u4e5f\u662f\u90a3\u4e48\u7a81\u7136\uff0c\u4e00\u58f0\u8fc7\u540e\uff0c\u5c31\u518d\u65e0\u5176 \u4ed6\u7684\u58f0\u54cd\u3002\u56db\u4e0b\u5c31\u662f\u4e00\u7247\u9759\u5bc2\uff0c\u6839\u672c\u6ca1\u6709\u4efb\u4f55\u5f02\u6837\u4e4b\u5904\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u81ea\u7126\u6025\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u5feb\u70b9\u51fa\u6765\uff1f\u201d\u5730\u56db\u4e0b\u5de1\u89c6\uff0c\u8fd9\u79cd\u591c\u884c\u5c4b\u9762\u7684\u52fe\u5f53\uff0c\u4ed6\u5df2\u6709 \u8bb8\u4e45\u4e0d\u66fe\u8bd5\u8fc7\u4e86\u3002\u6b64\u65f6\u9ac0\u8089\u590d\u751f\uff0c\u5fc3\u80f8\u4e4b\u95f4\uff0c\u4f46\u89c9\u70ed\u8840\u6cb8\u817e\uff0c\u6614\u65e5\u7684\u8c6a\u6c14\uff0c\u53c8\u91cd\u65b0\u751f\u51fa\uff01 \u3000\u3000\u4ed6\u7a0d\u4e3a\u4f2b\u7acb\u7247\u523b\uff0c\u7559\u610f\u503e\u542c\u7740\u56db\u4e0b\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5c06\u8981\u5931\u671b\u7684\u65f6\u5019\uff0c\u84e6\u5730\u542c\u5230\u4e00\u79cd\u4f4e\u4f4e\u7684\u54c0\u6c42\u4e4b\u58f0\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6beb\u4e0d\u8fdf\u7591\u5730\u5411\u90a3\u65b9\u5411\u63a0\u53bb\uff0c\u8eab\u5f62\u4e4b\u8f7b\u5feb\uff0c\u50cf\u662f\u4e00\u53ea\u521d\u6625\u7684\u71d5\u5b50\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u770b\u5230\u6709\u4e00\u4e2a\u7a97\u53e3\u4e2d\u4ecd\u6709\u5fae\u5149\uff0c\u4e8e\u65f6\u4ed6\u7acb\u523b\u987f\u4f4f\u8eab\u5f62\uff0c\u7075\u5de7\u5730\u5728\u5c4b\u9762\u4e0a\u4e00\u7ffb\uff0c \u201c\u91d1\u94a9\u5012\u6302\u201d\uff0c\u8db3\u5c16\u94a9\u5728\u5c4b\u539d\u4e0a\uff0c\u5782\u9996\u4e0b\u671b\u3002 \u3000\u3000\u5c4b\u5185\u6709\u4e00\u76cf\u6cb9\u706f\uff0c\u4eae\u7740\u660f\u9ec4\u7684\u706f\u5149\uff0c\u4e00\u4eba\u6b63\u7aef\u5750\u6905\u4e0a\uff0c\u53f3\u624b\u6301\u7740\u957f\u5251\uff0c\u5de6\u624b\u7684\u4e2d\u6307\uff0c\u5fae \u5f39\u5251\u8eab\uff0c\u53d1\u51fa\u58f0\u58f0\u55e1\u7136\u4e4b\u9e23\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u5219\u76f4\u633a\u633a\u5730\u8dea\u5728\u4ed6\u9762\u524d\uff0c\u6ee1\u8138\u8840\u8ff9\u3002\u65b9\u624d\u90a3\u4e00\u58f0\u60e8\u53eb\uff0c\u60f3\u5fc5\u5c31\u662f\u6b64\u4eba\u53d1\u51fa\u7684\u3002 \u3000\u3000\u4f0a\u98ce\u95ed\u76ee\u5185\u671b\uff0c\u89c1\u5230\u8fd9\u5e45\u666f\u8c61\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u52fe\u5f53\uff01\u201d \u3000\u3000\u65b9\u81ea\u52a8\u5ff5\u4e4b\u95f4\uff0c\u5374\u89c1\u90a3\u6301\u5251\u4e4b\u4eba\uff0c\u624b\u4e2d\u4e4b\u5251\u4e00\u98a4\uff0c\u6296\u8d77\u4e00\u6e9c\u5bd2\u5149\uff0c\u5237\u5730\uff0c\u7adf\u5c06\u90a3\u8dea\u7740\u7684 \u4eba\u7684\u5de6\u8033\uff0c\u524a\u4e86\u4e0b\u6765\uff0c\u8840\u6c34\u56db\u6e85\u3002\u90a3\u4eba\u8fd0\u5251\u4e00\u8f6c\uff0c\u7adf\u5c06\u90a3\u53ea\u8033\u6735\u6311\u5728\u5251\u4e0a\u3002\u800c\u8dea\u7740\u7684\u4eba\uff0c\u5f53 \u7136\u53c8\u53d1\u51fa\u4e00\u58f0\u60e8\u53eb\uff01 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u51db\uff0c\u7adf\u7136\u53d1\u73b0\u90a3\u6301\u5251\u4e4b\u4eba\u7684\u957f\u5251\u4e0a\uff0c\u6311\u7740\u4e24\u53ea\u8033\u6735\uff0c\u4e0d\u7981\u5927\u6012\uff01\u6697\u5fd6\u9053\uff1a \u201c\u8fd9\u53ae\u600e\u5730\u5982\u6b64\u624b\u8fa3\uff1f\u201d \u3000\u3000\u9042\u5728\u9f3b\u5b54\u91cc\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u5012\u6302\u7740\u7684\u8eab\u5f62\uff0c\u4e5f\u968f\u7740\u8fd9\u4e00\u54fc\uff0c\u98d8\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u539f\u4ee5\u4e3a\u90a3\u6301\u5251\u4e4b\u4eba\u4e00\u5b9a\u4f1a\u63a0\u51fa\u6765\u3002 \u3000\u3000\u90a3\u77e5\u4eba\u5bb6\u53ea\u51b7\u51b7\u779f\u4e86\u7a97\u5916\u4e00\u773c\uff0c\u5374\u4ecd\u7136\u7aef\u5750\u5728\u6905\u4e0a\u4e0d\u52a8\uff0c\u55e1\u7136\u4e00\u58f0\uff0c\u53c8\u53d1\u51fa\u4e00\u58f0\u4f4e\u541f\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u6014\uff01\u5374\u89c1\u90a3\u4eba\u60a0\u95f2\u5730\u7aef\u8d77\u684c\u4e0a\u7684\u8336\uff0c\u559d\u4e86\u4e00\u53e3\uff0c\u7136\u540e\u4fa7\u8138\u671d\u7740\u7a97\u53e3\uff0c\u5fae\u5fae\u4e00\u7b11\u3002 \u4ee5\u4e00\u79cd\u975e\u5e38\u6e05\u8d8a\uff0c\u975e\u5e38\u60a6\u8033\u7684\u58f0\u97f3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7a97\u5916\u7ba1\u95f2\u4e8b\u7684\u670b\u53cb\uff01\u5916\u9762\u98ce\u5bd2\uff0c\u8bf7\u79fb\u9a7e\u8fdb\u6765\u4e00\u5750\u5982\u4f55\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u770b\u5230\u4ed6\u7684\u8138\uff0c\u82cd\u767d\u800c\u6e05\u79c0\uff0c\u5634\u4e0a\u5fae\u5fae\u7559\u6709\u77ed\u9aed\uff0c\u7136\u800c\u5374\u4f7f\u4ed6\u66f4\u6dfb\u4e86\u51e0\u5206\u7537\u6027\u6210\u719f\u7684 \u98ce\u97f5\uff0c\u770b\u8d77\u6765\u9192\u76ee\u5f97\u5f88\uff0c\u5374\u53c8\u6ca1\u6709\u7537\u4eba\u7684\u7c97\u8c6a\u4e4b\u6c14\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u7b11\u81ea\u5df1\uff0c\u600e\u7684\u81ea\u5df1\u6240\u9047\u7684\uff0c\u5c3d\u662f\u4e0d\u5408\u5e38\u8f68\u7684\u5947\u4e8b\uff01\u8fd9\u4eba\u5251\u524a\u4eba\u8033\uff0c\u5374\u4ecd\u5927\u524c\u548c\u5730 \u5750\u5728\u6905\u4e0a\uff0c\u5f77\u4f5b\u5fc3\u5b89\u7406\u5f97\u7684\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u770b\u5230\u7a97\u5b50\u662f\u5f00\u7740\u7684\u3002\u4e8e\u662f\u4ed6\u601d\u5fd6\u4e4b\u4e0b\uff0c\u74e2\u8eab\u8fdb\u53bb\uff0c\u843d\u5728\u90a3\u8dea \u7740\u7684\u4eba\u8eab\u4fa7\u3002 \u3000\u3000\u5374\u542c\u90a3\u6301\u5251\u4e4b\u4eba\u7b11\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\u679c\u7136\u597d\u8eab\u624b\uff01\u679c\u7136\u4e0d\u6127\u4e3a\u884c\u4fa0\u4ed7\u4e49\uff0c\u6253\u62b1\u4e0d\u5e73\u7684\u4fa0\u5ba2\uff01\u54c8\uff01\u54c8\uff01\u201d \u3000\u3000\u4ed6\u54c8\u54c8\u7b11\u4e86\u4e24\u58f0\uff0c\u50cf\u662f\u8d5e\u7f8e\uff0c\u5374\u53c8\u50cf\u662f\u5632\u5f04\u3002 \u3000\u3000\u4f0a\u98ce\u53cc\u76ee\u4e00\u77aa\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u548c\u8fd9\u4f4d\u6709\u4ec0\u4e48\u6881\u5b50\uff01\u4eba\u5bb6\u65e2\u7136\u8dea\u4e0b\u670d\u8f93\uff0c\u9601\u4e0b\u53c8\u4f55\u5fc5\u5982\u6b64\u76f8\u903c\uff01\u4e0d\u662f\u5c0f\u9119\u591a\u7ba1\u95f2 \u4e8b\uff0c\u53ea\u662f\u9601\u4e0b\u4e5f\u672a\u514d\u624b\u8fa3\u4e86\u4e00\u70b9\uff01\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u4f4f\uff0c\u90a3\u6301\u5251\u4e4b\u4eba\u53c8\u54c8\u54c8\u4e00\u7b11\u3002 \u3000\u3000\u90a3\u77e5\u90a3\u8dea\u7740\u7684\u6c49\u5b50\uff0c\u5374\u7a81\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u811a\u8e0f\u4e2d\u95e8\uff0c\u55d6\u5730\u4e00\u62f3\uff0c\u671d\u4f0a\u98ce\u5f53\u80f8\u51fb\u53bb\uff0c\u53e3\u4e2d\u9a82 \u9053\uff1a \u3000\u3000\u201c\u8001\u5b50\u7684\u4e8b\uff0c\u8981\u4f60\u7ba1\u4ec0\u4e48\u9e1f\uff1f\u201d \u3000\u3000\u62f3\u98ce\u6c64\u7136\uff0c\u7adf\u662f\u5c11\u6797\u4f0f\u864e\u795e\u62f3\u91cc\u7684\u5999\u7740\uff1b\u800c\u4e14\u4ed6\u5728\u8fd9\u79cd\u62f3\u6cd5\u4e0a\uff0c\u81f3\u5c11\u5df2\u6709\u4e09\u4e03\u5e74\u7684\u529f \u529b\u3002 \u3000\u3000\u4e8b\u51fa\u610f\u5916\uff0c\u8fd9\u4e00\u62f3\u9669\u4e9b\u6253\u5728\u4f0a\u98ce\u8eab\u4e0a\u3002\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u90a3\u6301\u5251\u4e4b\u4eba\u5e76\u672a\u51fa\u624b\uff0c\u5411\u81ea\u5df1\u62db\u547c \u7684\uff0c\u5374\u662f\u81ea\u5df1\u633a\u8eab\u51fa\u6765\u76f8\u52a9\u4e4b\u4eba\u3002 \u3000\u3000\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u9519\u6b65\u62d7\u8eab\uff0c\u90a3\u4e2a\u6c49\u5b50\u4e0d\u4f46\u529f\u529b\u9887\u6df1\uff0c\u62db\u5f0f\u4e5f\u6781\u4e3a\u7cbe\u7eaf\u5feb\u6377\uff0c\u624b\u8098\u4e00\u6c89\uff0c\u53cc \u62f3\u540c\u65f6\u62a2\u51fa\uff0c\u201c\u8fdb\u6b65\u6492\u62e6\u53cc\u649e\u624b\u201d\uff0c\u55d6\u55d6\uff0c\u4e24\u62f3\uff0c\u5212\u4e86\u4e2a\u534a\u5f27\uff0c\u51fb\u5411\u809b\u98ce\u7684\u5de6\u53f3\u592a\u9633\u7a74\u3002 \u3000\u3000\u4f0a\u98ce\u5fae\u4e00\u584c\u8170\uff0c\u53f3\u638c\u5237\u5730\u51fb\u51fa\u3002\u90a3\u4eba\u9a6c\u6b65\u4e00\u6c89\uff0c\u8155\u8098\u4f38\u7f29\u4e4b\u540c\u65f6\uff0c\u55d6\uff0c\u55d6\uff0c\u53c8\u662f\u4e24\u62f3\uff0c \u5e26\u7740\u62f3\u98ce\uff0c\u6781\u5feb\u5730\u51fb\u5411\u4f0a\u98ce\u7684\u524d\u80a9\u4e0b\u80f8\u3002 \u3000\u3000\u4f0a\u98ce\u5927\u6012\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u75af\u4e86\u5440.\uff1f\u201d \u3000\u3000\u8eab\u5f62\u4e00\u53d8\uff0c\u638c\u4e0a\u518d\u4e0d\u7559\u60c5\uff0c\u90a3\u79cd\u6df1\u539a\u7684\u529f\u529b\uff0c\u679c\u7136\u4e0d\u662f\u90a3\u6c49\u5b50\u62b5\u6321\u5f97\u4f4f\u7684\u3002 \u3000\u3000\u4f46\u90a3\u6c49\u5b50\u62f3\u6c89\u529b\u731b\uff0c\u62db\u5f0f\u7cbe\u7eaf\uff0c\u7adf\u4e5f\u662f\u4e00\u6d41\u8eab\u624b\uff0c\u4e00\u65f6\u534a\u523b\u4e4b\u95f4\uff0c\u7adf\u548c\u4f0a\u98ce\u62c6\u4e86\u5341\u6570 \u62db\uff0c\u6253\u5f97\u623f\u4e2d\u7684\u684c\u6905\u4ff1\u6bc1\uff0c\u676f\u76cf\u4e71\u98de\u3002 \u3000\u3000\u90a3\u6301\u5251\u4e4b\u4eba\uff0c\u4ecd\u7aef\u5750\u5728\u6905\u4e0a\uff0c\u5fae\u5fae\u53d1\u7740\u51b7\u7b11\uff0c\u76ee\u5149\u5374\u6781\u4e3a\u7559\u795e\u4f0a\u98ce\u7684\u6b65\u6cd5\uff1b\u53f3\u624b\u4e0d\u65f6\u5f39 \u7740\u5251\u810a\uff0c\u53d1\u51fa\u4e00\u58f0\u58f0\u4f4e\u541f\u3002 \u3000\u3000\u4f0a\u98ce\u5374\u6709\u4e9b\uff0e\u54ed\u7b11\u4e0d\u5f97\uff0c\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\u7684\u591a\u4e8b\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4e00\u9762\u6253\u4e00\u9762\u9a82\u7740\uff1a \u3000\u3000\u201c\u5140\u90a3\u4f60\u8fd9\u53ae\uff01\u597d\u6ca1\u6765\u7531\uff01\u8001\u5b50\u60c5\u613f\u671d\u4ed6\u8dea\uff0c\u60c5\u613f\u88ab\u4ed6\u524a\u8033\u6735\uff0c\u8981\u4f60\u8fd9\u738b\u516b\u6765\u7ba1\u4ec0\u4e48 \u9e1f\uff01\u8001\u5b50\u88ab\u4ed6\u780d\u4e0b\u8111\u888b\u4e5f\u60c5\u613f\uff0c\u83ab\u8bf4\u524a\u8033\u6735\uff01\u201d \u3000\u3000\u4f0a\u98ce\u88ab\u4ed6\u9a82\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u638c\u5f71\u5982\u98ce\uff0c\u5c06\u8fd9\u6ee1\u53e3\u7c97\u8bdd\u7684\u6c49\u5b50\u56f4\u4f4f\u3002 \u3000\u3000\u90a3\u6301\u5251\u4e4b\u4eba\u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u53e4\u4eba\u6709\u4e91\uff1a\u201c\u591a\u4e00\u4e8b\u4e0d\u5982\u5c11\u4e00\u4e8b\u3002\u201d\u670b\u53cb\uff01\u4f60\u591a\u7ba1\u95f2\u4e8b\uff0c\u53c8\u4f55\u82e6\u6765\u54c9\uff01\u53e4\u4eba\u4e4b\u8a00\uff0c\u5b9e \u662f\u6df1\u5f97\u6211\u5fc3\uff01\u6df1\u5f97\u6211\u5fc3\uff01\u201d \u3000\u3000\u4f0a\u98ce\u51e0\u4e4e\u6c14\u5f97\u5410\u8840\uff0c\u5fae\u4e00\u9519\u6b65\uff0c\u5237\u5730\u540e\u9000\u4e09\u5c3a\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u6211\u4e0d\u7ba1\u5c31\u4e0d\u7ba1\u201d \u3000\u3000\u90a3\u77e5\u8bdd\u8fd8\u8bf4\u672a\u5b8c\uff0c\u90a3\u6c49\u5b50\u5374\u53c8\u7a9c\u8fc7\u6765\uff0c\u5288\u9762\u4e00\u62f3\uff0c\u671d\u4f0a\u98ce\u6253\u53bb\uff0c\u53e3\u4e2d\u4ecd\u5728\u4e0d\u5e72\u4e0d\u51c0\u5730\u9a82 \u9053\uff1a \u3000\u3000\u201c\u5140\u90a3\u4f60\u8fd9\u53ae\uff01\u649e\u7834\u4e86\u8001\u5b50\u7684\u597d\u4e8b\uff0c\u8001\u5b50\u975e\u6253\u715e\u4f60\u4e0d\u53ef\uff01\u201d \u3000\u3000\u51fa\u62f3\u5982\u96e8\u70b9\u822c\u671d\u4f0a\u98ce\u6253\u53bb\uff0c\u7adf\u771f\u7684\u6709\u4e9b\u8981\u548c\u4f0a\u98ce\u62da\u547d\u7684\u6837\u5b50\u3002 \u3000\u3000\u6301\u5251\u4e4b\u4eba\u4ecd\u5728\u563b\u563b\u7b11\u7740\uff0c\u4f0a\u98ce\u5374\u4e00\u5934\u96fe\u6c34\uff0c\u6697\u81ea\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u6c49\u5b50\u53cc\u8033\u88ab\u524a\uff0c\u6211\u6765\u6551\u4ed6\uff0c\u4ed6\u5374\u8bf4\u6211\u649e\u7834\u4e86\u4ed6\u7684\u201c\u597d\u4e8b\u201d\uff0c\u96be\u9053\u4ed6\u8111\u5b50\u6709\u6bdb\u75c5\uff1f\u96be \u9053\u4ed6\u662f\u4e2a\u75af\u5b50\uff1f\u5509\uff01\u6211\u771f\u5012\u9709\uff01\u201d \u3000\u3000\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u60f3\u4e0d\u51fa\u6b64\u4e8b\u7684\u7a76\u7adf\uff0c\u53ea\u5f97\u6697\u53f9\u81ea\u5df1\u7684\u5012\u9709\u4e86\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e09\u5341\u7ae0\u3000\u6b66\u6797\u56db\u7f8e>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u7ae0\u3000\u6b66\u6797\u56db\u7f8e \u3000\u3000\u4ed6\u5fc3\u601d\u4e00\u5206\uff0c\u90a3\u6c49\u5b50\u7acb\u523b\u53c8\u7740\u7740\u62a2\u653b\uff0c\u53e3\u4e2d\u5374\u53c8\u559d\u9053\uff1a \u3000\u3000\u201c\u8001\u5b50\u4eca\u5929\u4e0d\u6253\u6b7b\u4f60\u8fd9\u738b\u516b\uff0c\u8001\u5b50\u5c31\u4e0d\u53eb\u4f0f\u864e\u91d1\u521a\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5440\u4e86\u4e00\u58f0\uff0c\u201c\u539f\u6765\u8fd9\u6c49\u5b50\u5c31\u662f\u4f0f\u864e\u91d1\u521a\u3002\u201d \u3000\u3000\u4ed6\u6697\u6697\u5fd6\u9053\u201c\u90a3\u4e48\uff0c\u4ed6\u5374\u53c8\u600e\u4f1a\u8fd9\u6837\u50cf\u4e2a\u75af\u5b50\u4f3c\u7684\u5462\uff1f\u201d \u3000\u3000\u987b\u77e5\u4f0f\u864e\u91d1\u521a\u962e\u5927\u6210\uff0c\u5728\u8700\u4e2d\u9887\u6709\u76db\u540d\uff0c\u662f\u6761\u6ca1\u5962\u906e\u7684\u6c49\u5b50\uff0c\u5e73\u65e5\u4e5f\u9887\u5f97\u4eba\u671b\uff0c\u662f\u4ee5\u4f0a \u98ce\u4e00\u542c\u5230\u4ed6\u7684\u540d\u5b57\uff0c\u5c31\u66f4\u4e3a\u5947\u602a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u962e\u5927\u6210\u7edd\u5bf9\u4e0d\u662f\u75af\u5b50\uff0c\u4f46\u4ed6\u4e0d\u662f\u75af\u5b50\uff0c\u5374\u53c8\u600e\u4f1a\u5982\u6b64\u5462\uff1f \u3000\u3000\u6301\u5251\u7684\u90a3\u4eba\uff0c\u59cb\u7ec8\u7aef\u5750\u5728\u90a3\u91cc\uff0c\u671b\u7740\u4f0a\u98ce\u4e0d\u65ad\u563b\u563b\u5730\u7b11\u7740\uff0c\u770b\u7740\u8fd9\u4e24\u4eba\u83ab\u540d\u5176\u5999\u5730\u6253\u5728 \u4e00\u5904\uff0c\u7adf\u50cf\u662f\u89c9\u5f97\u975e\u5e38\u5f00\u5fc3\u7684\u6837\u5b50\u3002 \u3000\u3000\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4e24\u4eba\u53c8\u62c6\u4e86\u6570\u62db\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u66f4\u4e0d\u8010\u3002\u987b\u77e5\u4ed6\u6b64\u523b\u7684\u529f\u529b\uff0c\u8fdc\u5728\u962e\u5927\u6210\u4e4b\u4e0a\u3002 \u53ea\u662f\u4ed6\u548c\u962e\u5927\u6210\u7d20\u65e0\u4ec7\u6028\uff0c\u800c\u4e14\u4ed6\u7684\u672c\u610f\u53c8\u662f\u4e3a\u4e86\u6551\u4eba\u800c\u6765\uff0c\u5f53\u7136\u4e0d\u613f\u4ee5\u5185\u5bb6\u529f\u529b\u4f24\u4eba\u3002 \u3000\u3000\u4f0f\u864e\u91d1\u521a\u962e\u5927\u6210\u53f3\u8db3\u671d\u524d\u4e00\u8e0f\uff0c\u53f3\u62f3\u7b14\u76f4\u5730\u51fb\u51fa\u3002\u4f0a\u98ce\u8eab\u968f\u610f\u52a8\uff0c\u6350\u5f03\u4ee5\u5f80\u7684\u62db\u5f0f\u4e0d \u7528\uff0c\u53cc\u638c\u5fae\u4e00\u4ea4\u9519\uff0c\u5404\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u95ea\u7535\u822c\u5730\u4e0a\u4e0b\u4ea4\u51fb\u800c\u51fa\uff0c\u51fb\u5411\u4f0f\u864e\u91d1\u521a\u7684\u5589\u95f4\uff0c\u80f8\u4e0b\u3002 \u3000\u3000\u4f0f\u864e\u91d1\u521a\u773c\u524d\u4e00\u82b1\uff0c\u8d76\u7d27\u5f80\u4e0b\u584c\u8170\uff0c\u521a\u521a\u6781\u529b\u907f\u5f00\u6b64\u62db\u3002 \u3000\u3000\u90a3\u77e5\u4f0a\u98ce\u8eab\u5f62\u4e00\u626d\uff0c\u53cc\u638c\u539f\u5f0f\u62cd\u51fa\uff0c\u7830\u7136\u4e24\u58f0\uff0c\u8fd9\u4e24\u638c\u7adf\u90fd\u7740\u7740\u5b9e\u5b9e\u51fb\u5728\u962e\u5927\u6210\u8eab\u4e0a\u3002 \u4ed6\u867d\u672a\u4f7f\u5168\u529b\uff0c\u4f46\u5df2\u5c06\u962e\u5927\u6210\u51fb\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u8fd9\u4e24\u62db\u8f7b\u7075\u66fc\u5999\uff0c\u5374\u6b63\u662f\u4ed6\u548c\u94c1\u9762\u5b64\u884c\u5ba2\u52a8\u624b\u65f6\u5077\u5b66\u6765\u7684\u3002\u8fd9\u4e24\u62db\u770b\u6765\u8f7b\u63cf\u6de1\u5199\uff0c\u4f46 \u8f6c\u62db\u4e4b\u95f4\uff0c\u5374\u6b64\u522b\u4eba\u5feb\u4e86\u4e00\u500d\u3002 \u3000\u3000\u662f\u4ee5\u962e\u5927\u6210\u5c1a\u672a\u53d8\u62db\uff0c\u5c31\u88ab\u51fb\u4e2d\uff0c\u5657\u5730\uff0c\u4e00\u8de4\u8dcc\u5728\u5730\u4e0a\u3002\u4e24\u773c\u53d1\u6014\u5730\u770b\u7740\u4f0a\u98ce\uff0c\u5fc3\u4e2d\u5947 \u602a\uff0c\u8fd9\u4e24\u62db\u4e2d\u6709\u4ec0\u4e48\u53e4\u602a\uff1f \u3000\u3000\u90a3\u6301\u5251\u4e4b\u4eba\u5374\u5f39\u5251\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\u6781\u4e86\uff01\u597d\u6781\u4e86\uff01\u679c\u7136\u9ad8\u660e\u5f97\u5f88\uff01\u5c0f\u5f1f\u4f69\u670d\u4e4b\u81f3\u3002\u201d \u3000\u3000\u4f0a\u98ce\u7684\u773c\u775b\uff0c\u5374\u5728\u8fd9\u4e24\u4eba\u8eab\u4e0a\u6253\u7740\u8f6c\uff0c\u4e0d\u660e\u767d\u8fd9\u4e24\u4eba\u4e4b\u95f4\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201c\u96be\u9053\u8fd9 \u4e24\u4eba\u662f\u4e00\u4e3b\u4e00\u5974\uff1f\u201d\u4f46\u662f\u4ed6\u7acb\u523b\u81ea\u5df1\u63a8\u7ffb\u4e86\u81ea\u5df1\u7684\u60f3\u6cd5\uff1a\u201c\u4f0f\u864e\u91d1\u521a\uff0c\u90a3\u6709\u505a\u4eba\u5bb6\u5974\u624d\u7684\u9053 \u7406\uff1f\u201d \u3000\u3000\u962e\u5927\u6210\u6c14\u543c\u543c\u5730\u722c\u8d77\u6765\uff0c\u867d\u7136\u88ab\u6253\uff0c\u5374\u4ecd\u7136\u662f\u6781\u4e3a\u4e0d\u670d\u6c14\u7684\u6837\u5b50\uff0c\u5927\u6709\u518d\u548c\u4f0a\u98ce\u4e00\u62da\u4e4b \u610f\u3002 \u3000\u3000\u90a3\u6301\u5251\u4e4b\u4eba\u5374\u7b11\u9053\uff1a \u3000\u3000\u201c\u962e\u8001\u5927\uff01\u7b97\u4e86\u5427\uff01\u4f60\u518d\u6253\u4e5f\u4e0d\u662f\u4eba\u5bb6\u7684\u5bf9\u624b\uff0c\u4f55\u51b5\u4f60\u4eca\u5929\u53ea\u4e3a\u6211\u727a\u7272\u4e86\u4e24\u53ea\u8033\u6735\uff0c\u53c8 \u7b97\u5f97\u4e86\u4ec0\u4e48\uff01\u4ee5\u540e\u6709\u673a\u4f1a\uff0c\u4f60\u8fd8\u662f\u53ef\u4ee5\u518d\u8bd5\u4e00\u8bd5\u7684\uff0c\u53cd\u6b63\u6211\u2026\u2026\u53cd\u6b63\u4f60\u4e5f\u77e5\u9053\u6211\u7684\u3002\u201d \u3000\u3000\u672c\u6765\u4e00\u5934\u96fe\u6c34\u7684\u4f0a\u98ce\uff0c\u5728\u542c\u4e86\u8fd9\u8bdd\u4e4b\u540e\uff0c\u8d8a\u53d1\u5730\u83ab\u540d\u5176\u5999\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u6709\u4e9b\u597d\u7b11\uff0c\u5f04\u5230\u73b0\u5728\uff0c\u8fd9\u6301\u5251\u4e4b\u4eba\uff0c\u5012\u6210\u4e86\u529d\u67b6\u7684\u4e86\u3002\u81ea\u5df1\u4e0d\u660e\u4e0d\u767d\u5730\u6253\u4e86\u8fd9\u573a\u51a4 \u6789\u67b6\uff0c\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\uff01 \u3000\u3000\u4ed6\u5fc3\u4e2d\u597d\u751f\u4e0d\u81ea\u5728\uff0c\u5fc3\u4e2d\u4e00\u5927\u5806\u95f7\u6c14\uff0c\u4e0d\u77e5\u8be5\u51fa\u5728\u8c01\u8eab\u4e0a\u597d\u3002 \u3000\u3000\u90a3\u6301\u5251\u4e4b\u4eba\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u671d\u7740\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\u9ad8\u59d3\u5927\u540d\uff01\u6df1\u5bb5\u76f8\u9022\uff0c\u603b\u662f\u6709\u7f18\u3002\u5982\u679c\u670b\u53cb\u4e0d\u5f03\uff0c\u4e0d\u59a8\u7559\u6b64\u548c\u5c0f\u5f1f\u4f5c\u4e00\u6e05\u8c08\u3002\u201d \u3000\u3000\u4ed6\u4e3e\u8d77\u8336\u58f6\uff0c\u5012\u4e86\u676f\u8336\uff0c\u53c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u5bd2\u591c\u5ba2\u6765\uff0c\u53ea\u5f97\u4ee5\u8336\u4f5c\u9152\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e24\u773c\u53d1\u6014\uff0c\u4ed6\u867d\u662f\u673a\u53d8\u767e\u51fa\uff0c\u4e5f\u731c\u4e0d\u51fa\u8fd9\u6301\u5251\u4e4b\u4eba\u662f\u4f55\u6765\u8def\u3002\u800c\u4e14\u8fd9\u4eba\u5bf9\u81ea\u5df1\u5ffd\u800c \u8ba5\u8bbd\uff0c\u5ffd\u800c\u53c8\u8c26\u606d\u6709\u793c\u8d77\u6765\uff1b\u4f0a\u98ce\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u6b64\u523b\u8be5\u5bf9\u4ed6\u5982\u4f55\u6001\u5ea6\uff0c\u662f\u76f8\u5e94\u4e0d\u7406\u5462\uff1f\u8fd8\u662f \u4e0d\u987e\u800c\u53bb\uff1f\u52a0\u6216\u5c31\u5ba2\u5ba2\u6c14\u6c14\u5730\u5750\u4e0b\u6765\uff0c\u548c\u8fd9\u5947\u4eba\u505a\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u72b9\u7591\u4e0d\u51b3\uff1a\u90a3\u4f0f\u864e\u91d1\u521a\u5374\u6c14\u543c\u543c\u5730\u51b2\u8fc7\u6765\uff0c\u5927\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u522b\u770b\u4ed6\u8138\u5b50\u767d\uff0c\u4ed6\u5fc3\u53ef\u6ca1\u6709\u6211\u962e\u5927\u6210\u597d\u3002\u6211\u962e\u5927\u6210\u4e3a\u4f60\u5403\u5c3d\u4e86\u82e6\uff1a\u73b0\u5728\u53c8\u88ab\u4f60\u524a\u4e0b \u4e24\u53ea\u8033\u6735\uff0c\u96be\u9053\u4f60\u4e00\u70b9\u4e5f\u4e0d\u53ef\u601c\u6211\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u95fb\u8a00\u53c8\u5927\u6115\uff0c\u4e0d\u77e5\u9053\u8fd9\u962e\u5927\u6210\u662f\u5426\u53d8\u6210\u4e86\u75af\u5b50\uff0c\u8fd9\u79cd\u637b\u9178\u5403\u918b\u7684\u8bdd\uff0c\u600e\u4f1a\u7528\u5728\u6b64\u65f6 \u6b64\u523b\uff1f\u4ed6\u662f\u5b9e\u5728\u6709\u4e9b\u8ff7\u60d8\u4e86\uff01 \u3000\u3000\u6301\u5251\u7684\u90a3\u4eba\uff0c\u8033\u6839\u5374\u50cf\u662f\u7ea2\u4e86\u4e00\u4e0b\uff0c\u7a81\u5730\u5c06\u5251\u8eab\u4e00\u6296\uff0c\u53c8\u6e9c\u8d77\u4e86\u4e00\u9053\u9752\u84dd\u8272\u7684\u5149\u534e\uff0c\u559d \u9053\uff1a \u3000\u3000\u201c\u962e\u8001\u5927\uff01\u4f60\u53ef\u5f97\u653e\u6e05\u695a\u4e9b\uff01\u4f60\u4e00\u5929\u5230\u665a\u8ddf\u7740\u6211\uff0c\u6211\u82e5\u4e0d\u770b\u4f60\u662f\u6761\u6c49\u5b50\uff0c\u65e9\u5c31\u780d\u4e0b\u4f60\u7684 \u8111\u888b\u4e86\uff0c\u4f60\u8fd8\u565c\u55e6\u4ec0\u4e48\uff1f\u4f55\u51b5\u4f60\u8033\u6735\u88ab\u524a\uff0c\u662f\u4f60\u5fc3\u7518\u60c5\u613f\uff0c\u8fd8\u54c0\u6c42\u7740\u6211\uff0c\u6211\u624d\u52a8\u624b\u7684\uff0c\u96be\u9053 \u53c8\u602a\u5f97\u4e86\u8c01\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u542c\u4e86\u8fd9\u4e9b\u8bdd\uff0c\u8d8a\u6765\u8d8a\u7cca\u6d82\u3002 \u3000\u3000\u90a3\u962e\u5927\u6210\u5374\u54ed\u4e27\u7740\u8138\uff0c\u50cf\u662f\u6b7b\u4e86\u7238\u7238\u4f3c\u7684\uff0c\u7ad9\u5728\u90a3\u91cc\u3002\u8138\u7684\u4e24\u8fb9\u672c\u6765\u957f\u7740\u8033\u6735\u7684\u5730\u65b9\uff0c \u4e0d\u505c\u5730\u5f80\u4e0b\u6ef4\u7740\u8840\u3002\u4f0a\u98ce\u770b\u7740\u4ed6\u8fd9\u5e45\u6837\u5b50\uff0c\u65e2\u50cf\u53ef\u7b11\uff0c\u4ea6\u590d\u53ef\u601c\uff0c\u53ef\u5374\u4e5f\u6709\u4e9b\u5947\u602a\u3002\u5fc3\u4e2d\u4e0d \u7981\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f0f\u864e\u91d1\u521a\u5728\u6b66\u6797\u4e2d\u4e5f\u7b97\u5f97\u4e0a\u662f\u4e2a\u4eba\u7269\uff0c\u5982\u4eca\u5374\u600e\u5730\u53d8\u6210\u4e86\u5982\u6b64\u6a21\u6837\uff1f\u201d \u3000\u3000\u4ed6\u671b\u4e86\u90a3\u6301\u5251\u4e4b\u4eba\u4e00\u773c\uff0c\u53c8\u63a5\u7740\u5fd6\u9053\uff1a \u3000\u3000\u201c\u82e5\u6b64\u4eba\u662f\u4e2a\u5973\u7684\uff0c\u90a3\u962e\u5927\u6210\u8fd8\u53ef\u8bf4\u662f\u5355\u604b\u6210\u75be\u3002\u4f46\u6b64\u4eba\u4ece\u5934\u5230\u811a\uff0c\u770b\u6765\u770b\u53bb\uff0c\u4e5f\u770b\u4e0d \u51fa\u8eab\u4e0a\u6709\u4e00\u4e1d\u5973\u4eba\u7684\u6837\u5b50\u5440\uff01\u201d \u3000\u3000\u6c5f\u6e56\u4e0a\u5973\u626e\u7537\u88c5\u4e4b\u4eba\uff0c\u6bd4\u6bd4\u7686\u662f\uff0c\u4f0a\u98ce\u89c1\u5f97\u591a\u4e86\uff1b\u65e0\u8bba\u662f\u8c01\uff0c\u626e\u6210\u7537\u88c5\u540e\uff0c\u603b\u8131\u4e0d\u4e86\u90a3 \u79cd\u5973\u4eba\u6c14\u606f\uff0c\u4f0a\u98ce\u53ef\u7b97\u89c1\u5f97\u591a\u4e86\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u6301\u5251\u4e4b\u4eba\uff0c\u867d\u7136\u767d\u9759\u6587\u79c0\uff0c\u4f46\u5634\u4e0a\u7684\u77ed\u9aed\uff0c\u6839\u6839\u89c1\u8089\uff0c\u8fd9\u662f\u4efb\u4f55\u5973\u5b50\u4e5f\u5316\u88c5\u4e0d\u6765 \u7684\u3002\u56e0\u4e3a\u8d34\u4e0a\u53bb\u7684\u5047\u987b\uff0c\u548c\u4ece\u76ae\u8089\u4e2d\u751f\u51fa\u7684\uff0c\u5916\u884c\u4eba\u867d\u96be\u4ee5\u5206\u8fa8\uff0c\u4f46\u50cf\u4f0a\u98ce\u8fd9\u79cd\u6c5f\u6e56\u8001\u624b\uff0c \u5374\u4e00\u671b\u800c\u77e5\u3002 \u3000\u3000\u4e00\u77ac\u4e4b\u95f4\uff0c\u4ed6\u53c8\u89c9\u5f97\u5bf9\u962e\u5927\u6210\u975e\u5e38\u540c\u60c5\uff0c\u4e5f\u6709\u4e9b\u601c\u60af\u3002 \u3000\u3000\u56e0\u4e3a\u962e\u5927\u6210\u4ecd\u7136\u5782\u5934\u4e27\u6c14\u5730\u5750\u5728\u90a3\u91cc\uff0c\u90a3\u4e48\u4e2a\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\uff0c\u5982\u4eca\u7adf\u843d\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u8fd9 \u51e0\u4e4e\u662f\u4ee4\u4eba\u65e0\u6cd5\u76f8\u4fe1\u7684\u4e8b\uff01 \u3000\u3000\u90a3\u6301\u5251\u4e4b\u5165\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u4e00\u8a00\u4e0d\u53d1\uff0c\u96be\u9053\u662f\u5c0f\u5f1f\u9ad8\u6500\u4e0d\u4e0a\u5417\uff1f\u201d \u3000\u3000\u8bed\u97f3\u843d\u5230\u201c\u5417\u201d\u5b57\u4e0a\uff0c\u5df2\u53d8\u5f97\u975e\u5e38\u51b7\u6f20\u3002 \u3000\u3000\u4f0a\u98ce\u5fae\u6014\u4e86\u4e00\u4e0b\uff0c\u8fde\u58f0\u9053\uff1a \u3000\u3000\u201c\u90a3\u91cc\uff01\u90a3\u91cc\uff01\u201d \u3000\u3000\u4e3e\u5934\u4e00\u671b\uff0c\u5df2\u6709\u65e5\u5149\u659c\u659c\u4ece\u7a97\u4e2d\u7167\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u65e0\u610f\u8bc6\u5730\u8d70\u5230\u7a97\u524d\uff0c\u7a97\u5916\u662f\u4e2a\u975e\u5e38\u7cbe\u81f4\u7684\u56ed\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u624d\u77e5\u9053\u81ea\u5df1\u5904\u8eab\u4e4b\u6240\uff0c\u662f\u4e00\u5bb6\u5927\u6237\u4eba\u5bb6\u540e\u9662\u4e2d\u7684\u4e24\u95f4\u7cbe\u820d\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5bf9\u8fd9\u6301\u5251\u4e4b\u4eba\u7684\u8eab\u5206\uff0c\u66f4\u8d77\u4e86\u6781\u5927\u7684\u597d\u5947\u5fc3\uff0c\u8f6c\u8eab\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5f1f\u4f0a\u98ce\uff0c\u53ea\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u540d\u5c0f\u5352\uff0c\u627f\u8499\u9601\u4e0b\u4e0d\u803b\u4e0b\u4ea4\uff0c\u5b9e\u5728\u60f6\u6050\u5f97\u5f88\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u60f3\u95ee\u4eba\u5bb6\u7684\u59d3\u540d\u8eab\u5206\uff0c\u53c8\u4e0d\u4fbf\u51fa\u53e3\u3002 \u3000\u3000\u90a3\u6301\u5251\u4e4b\u4eba\u53c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4ee5\u9601\u4e0b\u7684\u8fd9\u79cd\u8eab\u624b\uff0c\u82e5\u8bf4\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u540d\u5c0f\u5352\uff0c\u90a3\u9601\u4e0b\u672a\u514d\u592a\u8c26\u4e86\u5427\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u7f13\u7f13\u8e31\u5230\u7a97\u524d\u3002\u4f0a\u98ce\u624d\u53d1\u89c9\u4ed6\u8eab\u6750\u4e0d\u9ad8\uff0c\u53ea\u9f50\u81ea\u5df1\u7684\u9f3b\u4e0b\uff0c\u5fc3\u4e2d\u52a8\u4e86\u52a8\uff0c\u5374\u542c\u4ed6\u53c8 \u7b11\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5f1f\u8427\u5357\uff0c\u624d\u662f\u6c5f\u6e56\u4e0a\u7684\u65e0\u540d\u5c0f\u5352\u54e9\uff01\u201d \u3000\u3000\u4ed6\u9732\u9f7f\u4e00\u7b11\uff1a \u3000\u3000\u201c\u4eca\u591c\u4e4b\u4e8b\uff0c\u9601\u4e0b\u5fc5\u5b9a\u6709\u4e9b\u5947\u602a\uff1b\u4f46\u5c0f\u5f1f\u4e00\u89e3\u91ca\uff0c\u9601\u4e0b\u5c31\u4f1a\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u7559\u610f\u503e\u542c\u7740\uff0c\u4f46\u90a3\u81ea\u79f0\u201c\u8427\u5357\u201d\u4e4b\u4eba\uff0c\u8bdd\u5374\u5230\u6b64\u4e3a\u6b62\uff0c\u518d\u6ca1\u6709\u4e0b\u6587\uff0c\u6839\u672c\u6ca1\u6709\u89e3 \u91ca\uff0c\u4f0a\u98ce\u4e5f\u4ecd\u7136\u4e00\u5934\u96fe\u6c34\u3002 \u3000\u3000\u8427\u5357\u4e00\u56de\u8eab\uff0c\u62cd\u4e86\u62cd\u962e\u5927\u6210\u7684\u80a9\u5934\uff0c\u6362\u4e86\u53e6\u5916\u4e00\u79cd\u53e3\u6c14\u9053\uff1a \u3000\u3000\u201c\u962e\u8001\u5927\uff01\u4f60\u8fd8\u7ad9\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u5929\u5df2\u7ecf\u4eae\u4e86\u5440\uff01\u201d \u3000\u3000\u4f0f\u864e\u91d1\u521a\u6d53\u7709\u4e00\u7ad6\uff0c\u5927\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u53eb\u8fd9\u59d3\u4f0a\u7684\u5c0f\u5b50\u8d70\uff0c\u5374\u504f\u504f\u53eb\u6211\u8d70\uff0c\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u8427\u5357\u53cc\u76ee\u4e00\u5f20\uff0c\u660e\u4eae\u7684\u53cc\u775b\u91cc\uff0c\u7acb\u523b\u5c04\u51fa\u4e24\u9053\u5229\u5200\u822c\u7684\u5149\u8292\u3002 \u3000\u3000\u962e\u5927\u6210\u7adf\u5782\u4e0b\u5934\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u53f9\u4e00\u58f0\uff0c\u81ea\u89c9\u6b64\u884c\u5f04\u5f97\u7070\u5934\u571f\u8138\u3002\u8fd9\u4f0f\u864e\u91d1\u521a\u8bd8\u867d\u8bf4\u5f97\u4e0d\u5ba2\u6c14\uff0c\u4f46\u4f0a\u98ce\u89c9\u5f97\u4ed6\u6709 \u4e9b\u53ef\u601c\uff0c\u4e5f\u72af\u4e0d\u4e0a\u548c\u4ed6\u4e89\u5435\uff0c\u4ec5\u4ec5\u5fae\u7b11\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u52a8\u5904\uff0c\u770b\u5230\u90a3\u201c\u8427\u5357\u201d\u624b\u6301\u4e4b\u5251\u7684\u5251\u5c16\u4e0a\uff0c\u4ecd\u6311\u7740\u4e24\u53ea\u9c9c\u8840\u6dcb\u6f13\u7684\u8033\u6735\u3002 \u3000\u3000\u4ed6\u611f\u89c9\u5230\u4e00\u79cd\u8bf4\u4e0d\u51fa\u6765\u6076\u5fc3\uff0c\u5bf9\u8fd9\u201c\u8427\u5357\u201d\u7684\u4e3a\u4eba\uff0c\u4e5f\u6709\u7740\u8bf4\u4e0d\u51fa\u6765\u7684\u538c\u6076\u3002 \u3000\u3000\u4f46\u4eba\u5bb6\u4e00\u4e2a\u613f\u6253\uff0c\u4e00\u4e2a\u613f\u6371\uff0c\u88ab\u524a\u4e86\u8033\u6735\u7684\u4eba\u5fc3\u7518\u60c5\u613f\uff0c\u90a3\u4e48\u81ea\u5df1\u8fd9\u5c40\u5916\u4eba\u53c8\u80fd\u8bf4\u4e9b\u4ec0 \u4e48\u8bdd\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u5411\u201c\u8427\u5357\u201d\u4e00\u62f1\u624b\uff0c\u9053\uff1a \u3000\u3000\u201c\u5929\u5df2\u5927\u4eae\uff0c\u5c0f\u5f1f\u672c\u4e5f\u8be5\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u962e\u5927\u6210\u4e00\u77aa\u773c\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u8d70\u6211\u4e5f\u8d70\uff0c\u4f60\u8981\u662f\u4e0d\u8d70\uff0c\u6211\u53ef\u4e5f\u8981\u5728\u8fd9\u91cc\u591a\u803d\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u672c\u6765\u6ee1\u53e3\u56db\u5ddd\u571f\u97f3\uff0c\u6b64\u523b\u7adf\u5b66\u7740\u201c\u8427\u5357\u201d\u8bf4\u8d77\u5b98\u8bdd\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u6709\u4e9b\u597d\u7b11\uff0c\u4f46\u770b\u4e86\u4ed6\u90a3\u79cd\u72fc\u72c8\u7684\u6837\u5b50\uff0c\u5374\u53c8\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u521a\u4e00\u8fc8\u6b65\uff0c\u5374\u542c\u56ed\u4e2d\u4e00\u4e2a\u6781\u4e3a\u5a07\u5ae9\u7684\u53e3\u97f3\u7b11\u58f0\uff1a \u3000\u3000\u201c\u54ce\u54df\uff01\u600e\u4e48\u6211\u624d\u521a\u6765\uff0c\u5c31\u542c\u5230\u91cc\u9762\u6709\u4eba\u8bf4\u8981\u8d70\u8981\u8d70\u7684\uff0c\u96be\u9053\u4f60\u4eec\u90fd\u4e0d\u6b22\u8fce\u6211\u6765\u5417\uff1f\u201d \u3000\u3000\u8bed\u58f0\u529b\u843d\uff0c\u95e8\u5916\u5df2\u5a77\u5a77\u8d70\u8fdb\u4e00\u4eba\u6765\uff0c\u4e91\u9b13\u9ad8\u633d\uff0c\u8273\u5149\u56db\u7167\uff0c\u4e00\u8d70\u8fdb\u95e8\uff0c\u79cb\u6ce2\u5c31\u56db\u4e0b\u4e00 \u8f6c\uff0c\u7ed9\u5ba4\u4e2d\u5e73\u6dfb\u4e86\u51e0\u5206\u6625\u8272\uff01 \u3000\u3000\u5979\u5a07\u58f0\u4e00\u7b11\uff0c\u5411\u201c\u8427\u5357\u201d\u9053\uff1a \u3000\u3000\u201c\u8fd8\u662f\u4f60\u6709\u529e\u6cd5\uff0c\u5934\u5929\u521a\u6765\uff0c\u665a\u4e0a\u5c31\u6709\u4e24\u4f4d\u5ba2\u4eba\u6765\u627e\u4f60\u3002\u4f60\u59d0\u59d0\u6211\u5728\u8fd9\u91cc\u4f4f\u4e86\u5feb\u4e09\u5e74 \u5566\uff0c\u4e5f\u6ca1\u6709\u534a\u4e2a\u4eba\u6765\u627e\u6211\u3002\u201d\u8427\u5357\u4e5f\u7b11\u8bf4\uff1a \u3000\u3000\u201c\u8c01\u5403\u4e86\u718a\u5fc3\u8c79\u80c6\u6562\u6765\u627e\u4f60\u5440\uff1f\u4e0d\u6015\u70e7\u5f97\u6d51\u8eab\u8d77\u7a9f\u7abf\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u8a00\u7b11\u65e0\u5fcc\uff0c\u5f77\u4f5b\u751a\u719f\u3002 \u3000\u3000\u962e\u5927\u6210\u76ee\u5b9a\u53e3\u5446\u5730\u7ad9\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u7684\u4e24\u773c\u5374\u77aa\u5728\u201c\u8427\u5357\u201d\u8138\u4e0a\u3002 \u3000\u3000\u65b9\u624d\u90a3\u7edd\u8273\u5973\u5b50\u4e00\u8fdb\u6765\uff0c\u4f0a\u98ce\u5c31\u89c9\u5f97\u6709\u4e9b\u773c\u719f\uff0c\u73b0\u5728\u542c\u4e86\u201c\u8427\u5357\u201d\u7684\u8bdd\uff0c\u5fc3\u4e2d\u5df2\u731c\u51fa\u6b64 \u4eba\u662f\u8c01\u3002\u518d\u770b\u89c1\u201c\u8427\u5357\u201d\u7b11\u58f0\u660e\u6717\uff0c\u53cc\u76ee\u4e2d\u4e5f\u6ee1\u542b\u7b11\u610f\uff0c\u53ea\u662f\u9762\u4e0a\u4ecd\u6ca1\u6709\u4e00\u4e1d\u8868\u60c5\u3002\u60f3\u5230\u90a3 \u962e\u5927\u6210\u6240\u8bf4\u6ee1\u542b\u201c\u918b\u610f\u201d\u7684\u8bdd\uff0c\u5fc3\u4e0b\u7acb\u65f6\u604d\u7136\u5927\u609f\uff1a \u3000\u3000\u201c\u539f\u6765\u8fd9\u201c\u8427\u5357\u201d\u5374\u662f\u6f47\u6e58\u5983\u5b50\u8427\u5357\u82f9\uff0c\u602a\u4e0d\u5f97\u962e\u5927\u6210\u4e00\u5e45\u795e\u9b42\u98a0\u5012\u7684\u6837\u5b50\uff0c\u4e5f\u96be\u602a\u5979 \u6613\u9497\u800c\u5f01\uff0c\u6211\u7adf\u7136\u770b\u4e0d\u51fa\u6765\u3002\u82e5\u6362\u662f\u522b\u4eba\uff0c\u5f53\u7136\u5947\u602a\uff1b\u53ef\u662f\u8fd9\u8427\u4e09\u7237\u7684\u7231\u5973\u5316\u4e86\u88c5\uff0c\u522b\u8bf4\u6211 \u770b\u4e0d\u51fa\u6765\uff0c\u6050\u6015\u8c01\u4e5f\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u773c\u775b\u4e00\u671b\u90a3\u8273\u88c5\u5973\u5b50\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u4e00\u5b9a\u5c31\u662f\u6b66\u6797\u7b2c\u4e00\u706b\u5668\u540d\u5bb6\u706b\u795e\u7237\u7684\u7231\u59bb\u201c\u8fa3\u624b\u897f\u65bd\u201d\u8c37\u6653\u9759\u4e86\uff0c\u6211\u548c\u5979\u5012\u89c1\u8fc7 \u4e00\u9762\uff0c\u4e0d\u77e5\u5979\u8fd8\u8ba4\u4e0d\u8ba4\u5f97\u51fa\u6211\u6765\uff01\u5947\u602a\u7684\u662f\uff1a\u8fd9\u666f\u4e1c\u4e00\u4e2a\u5c0f\u5730\u65b9\uff0c\u600e\u4f1a\u4f4f\u7740\u9f0e\u9f0e\u5927\u540d\u7684\u201c\u6b66 \u6797\u56db\u7f8e\u201d\u4e2d\u7684\u540e\u4e24\u4f4d\uff0c\u53c8\u504f\u504f\u8ba9\u6211\u78b0\u7740\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8111\u4e2d\u4e00\u9635\u6df7\u4e71\uff0c\u53c8\u60f3\u5230\u4ed6\u7684\u59bb\u5b50\u201c\u9500\u9b42\u592b\u4eba\u201d\u3002\u539f\u6765\u90a3\u8427\u5357\uff0c\u679c\u7136\u5c31\u662f\u6614\u5e74\u4ee5\u6613\u5bb9\u4e4b \u672f\uff0c\u53ca\u72ec\u95e8\u6697\u5668\u626c\u540d\u5929\u4e0b\u7684\u8427\u65ed\u8427\u4e09\u7237\u7684\u7231\u5973\u6f47\u6e58\u5983\u5b50\u3002\u800c\u90a3\u8273\u88c5\u5973\u5b50\u4e5f\u4e0d\u51fa\u4f0a\u98ce\u6240\u6599\uff0c\u662f \u706b\u795e\u7237\u59da\u6e05\u5b87\u7684\u7231\u59bb\u8fa3\u624b\u897f\u65bd\u8c37\u6653\u9759\u3002 \u3000\u3000\u6614\u5e74\u201c\u6b66\u6797\u56db\u7f8e\u201d\u540d\u566a\u5929\u4e0b\uff1a\u8fd9\u201c\u6b66\u6797\u56db\u7f8e\u201d\u4e2d\u7684\u5934\u4e00\u4f4d\uff0c\u5c31\u662f\u4f0a\u98ce\u7684\u59bb\u5b50\u201c\u9500\u9b42\u592b \u4eba\u201d\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u6f47\u6e58\u5983\u5b50\u8427\u5357\u82f9\uff0c\u8fa3\u624b\u897f\u65bd\u8c37\u6653\u9759\u548c\u6606\u4ed1\u638c\u95e8\u7684\u7231\u5973\u2014\u2014\u6606\u4ed1\u7389\u5973\u5d14\u4f69\uff0c\u5c31\u662f\u88ab \u6c5f\u6e56\u4e2d\u4eba\u8273\u79f0\u7684\u201c\u6b66\u6797\u56db\u7f8e\u201d\u3002 \u3000\u3000\u540e\u6765\u9500\u9b42\u592b\u4eba\u5ac1\u7ed9\u4e86\u94c1\u621f\u6e29\u4faf\uff0c\u9690\u5c45\u6c5f\u5357\uff1a\u8fa3\u624b\u897f\u65bd\u8c37\u65bd\u8c37\u6653\u9759\u5ac1\u7ed9\u4e86\u6b66\u6797\u4e2d\u4f7f\u706b\u5668\u7684 \u7b2c\u4e00\u540d\u5bb6\u59da\u6e05\u5b87\uff1b\u6f47\u6e58\u5983\u5b50\u5374\u56e0\u4e3a\u8ffd\u6c42\u4e4b\u4eba\u592a\u591a\uff0c\u800c\u5979\u5374\u51b7\u82e5\u51b0\u971c\uff0c\u5c06\u4e0d\u5c11\u52a8\u5979\u8111\u7b4b\u7684\u6c5f\u6e56 \u8c6a\u5ba2\uff0c\u4f24\u5728\u5979\u4e0b\u201c\u56de\u98ce\u821e\u67f3\u201d\u5251\u4e0b\uff0c\u800c\u5f15\u8d77\u6b66\u6797\u4e2d\u7684\u4e0d\u6ee1\u540e\uff0c\u4e5f\u6e10\u9500\u58f0\u706d\u8ff9\uff1b\u6606\u4ed1\u7389\u5973\u5d14 \u4f69\uff0c\u5374\u4e5f\u7a81\u7136\u5728\u6b66\u6797\u4e2d\u5931\u53bb\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u4e8e\u662f\u8d6b\u8d6b\u4e00\u65f6\u7684\u201c\u6b66\u6797\u56db\u7f8e\u201d\uff0c\u5c31\u6e10\u6e10\u5728\u6b66\u6797\u4e2d\u6781\u5c11\u88ab\u4eba\u63d0\u8d77\u3002 \u3000\u3000\u90a3\u77e5\u4f0a\u98ce\u6b64\u756a\u8fdc\u8d74\u6ec7\u4e2d\uff0c\u5374\u5728\u8fd9\u5c71\u57ce\u91cc\u9047\u7740\u4e86\u201c\u6b66\u6797\u56db\u7f8e\u201d\u4e2d\u7684\u4e24\u4f4d\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u6f47\u6e58\u5983\u5b50>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u6f47\u6e58\u5983\u5b50 \u3000\u3000\u8fa3\u624b\u897f\u65bd\u548c\u9500\u9b42\u592b\u4eba\uff0c\u539f\u662f\u7d20\u8bc6\u3002\u4f0a\u98ce\u6614\u4eba\u548c\u4ed6\u7684\u59bb\u5b50\u7545\u6e38\u4e94\u5cb3\u65f6\uff0c\u5728\u6cf0\u5c71\u7389\u7687\u9876\u4e0a\uff0c \u66fe\u548c\u5979\u4eec\u592b\u5987\u89c1\u8fc7\u4e00\u9762\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u4e2d\u5fd0\u5fd1\uff0c\u751f\u6015\u8c37\u6653\u9759\u8ba4\u51fa\u4e86\u4ed6\uff0c\u6084\u6084\u8f6c\u8fc7\u8138\u53bb\u3002\u56e0\u4e3a\u4ed6\u8bc8\u6b7b\u4e4b\u540e\uff0c\u5728\u6c5f\u6e56\u4e0a\u5df2 \u6210\u4e86\u4e2a\u89c1\u4e0d\u5f97\u4eba\u7684\u201c\u9ed1\u4eba\u201d\u4e86\u3002 \u3000\u3000\u8c37\u6653\u9759\u5a07\u7b11\u4e0d\u4f11\uff0c\u773c\u6ce2\u4ecd\u8f6c\uff0c\u89c1\u5230\u962e\u5927\u6210\uff0c\u53c8\u8f7b\u5524\u4e86\u4e00\u58f0\uff0c\u5411\u8427\u5357\u82f9\u9053 \u3000\u3000\u201c\u8fd9\u53c8\u662f\u4f60\u7684\u6770\u4f5c\u5427\uff01\u4eba\u5bb6\u90fd\u8bf4\u6211\u201c\u8fa3\u624b\u201d\uff0c\u53ef\u662f\u6211\u770b\u5440\uff0c\u6211\u8fd9\u201c\u8fa3\u624b\u201d\u4e24\u4e2a\u5b57\u7684\u5916 \u53f7\uff0c\u5012\u4e0d\u5982\u8f6c\u9001\u7ed9\u4f60\u8fd8\u597d\u4e9b\u3002\u201d\u5a07\u58f0\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5feb\u628a\u4f60\u5c0f\u5b9d\u5251\u4e0a\u7684\u4e24\u53ea\u8033\u6735\u62ff\u4e0b\u6765\uff0c\u9c9c\u8840\u6dcb\u6dcb\u7684\u6015\u6b7b\u4eba\u4e86\uff01\u201d \u3000\u3000\u8427\u5357\u82f9\u4e00\u62bf\u5634\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u522b\u5ba2\u6c14\u4e86\u5427\uff0c\u60f3\u5f53\u5e74\u4f60\u628a\u4eba\u5bb6\u7684\u8111\u888b\u6311\u5728\u5b9d\u5251\u4e0a\uff0c\u4e5f\u6ca1\u6709\u8bf4\u4ec0\u4e48\u6015\u6b7b\u4eba\u4e86\uff0c\u73b0\u5728\u600e \u4e48\u5566\uff1f\u7a81\u7136\u5927\u6148\u5927\u60b2\u4e86\u5440.\u201d \u3000\u3000\u4f0a\u98ce\u7ad9\u5728\u7a97\u53e3\uff0c\u7559\u53c8\u4e0d\u662f\uff0c\u8d70\u53c8\u4e0d\u662f\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u6837\u597d\u3002 \u3000\u3000\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\u7684\u591a\u4e8b\uff0c\u597d\u751f\u751f\u5730\u4ece\u5e8a\u4e0a\u722c\u8d77\u6765\uff0c\u6dcc\u8fd9\u8d9f\u6d51\u6c34\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u8c37\u6653\u9759\u5374\u8d70\u5230\u4ed6\u8eab\u4fa7\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u5582\uff0c\u5c0f\u5144\u5f1f\uff01\u4f60\u8d35\u59d3\u5440\uff01\u600e\u4e48\u6211\u770b\u4f60\u50cf\u662f\u9762\u719f\u5f97\u5f88\u3002\u201d \u3000\u3000\u4f0a\u98ce\u552f\u552f\u800c\u5e94\uff0c\u4e0d\u6562\u7b54\u8154\u3002 \u3000\u3000\u962e\u5927\u6210\u4e5f\u4e0d\u662f\u767d\u75f4\uff0c\u53d7\u5230\u5982\u6b64\u51b7\u843d\uff0c\u5fc3\u91cc\u81ea\u7136\u5927\u5927\u4e0d\u662f\u6ecb\u5473\uff0c\u770b\u4e86\u8427\u5357\u82f9\u4e00\u773c\uff0c\u7c97\u58f0\u7c97 \u6c14\u5730\u9053\uff1a \u3000\u3000\u201c\u8427\u59d1\u5a18\uff01\u6211\u8fd9\u6837\u5bf9\u4f60\uff0c\u4f60\u8fd9\u6837\u5bf9\u6211\uff0c\u5509\uff01\u6211\u5565\u5b50\u90fd\u6ca1\u5f97\u8bf4\u7684\uff01\u4f60\u8bf4\u8981\u8bd5\u8bd5\u6211\u7684\u5fc3\uff0c \u597d\uff01\u6211\u7684\u8033\u6735\u90fd\u88ab\u4f60\u524a\u6389\u4e86\uff0c\u4f60\u8fd8\u662f\u2026\u2026\u5509\uff01\u53ea\u602a\u6211\u962e\u5927\u6210\u751f\u5f97\u4e11\u964b\uff0c\u6211\u2014\u2014\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8d8a\u8bf4\u8d8a\u4e0d\u662f\u5473\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u58f0\u97f3\u91cc\u7adf\u5e26\u7740\u54ed\u8154\uff0c\u4e00\u8f6c\u8eab\uff0c\u8e6c\uff0c\u8e6c\uff0c\u8e6c\uff0c\u671d\u95e8\u5916\u5927\u6b65\u8d70 \u4e86\u51fa\u53bb\uff0c\u8427\u5357\u82f9\u52a8\u4e5f\u4e0d\u52a8\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4f0a\u98ce\u89c1\u4ed6\u9b41\u4f1f\u7684\u80cc\u5f71\u6d88\u5931\u5927\u95e8\u5916\uff0c\u5374\u542c\u8427\u5357\u82f9\u5550\u9053\uff1a \u3000\u3000\u201c\u765e\u86e4\u87c6\uff01\u201d \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u4e0d\u5c51\u5730\u671b\u4e86\u5979\u4e00\u773c\uff0c\u89c9\u5f97\u962e\u5927\u6210\u867d\u7136\u53ef\u601c\uff0c\u5374\u4e5f\u66ff\u7537\u4eba\u4e22\u5c3d\u4e86\u8138\uff0c\u4e24\u9053\u5251\u7709\uff0c \u76b1\u5230\u4e00\u5904\uff0c\u4e0d\u6ee1\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u8c37\u6653\u9759\u773c\u73e0\u4e00\u8f6c\uff0c\u770b\u5230\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u4fcf\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e5f\u4e0d\u80fd\u602a\u8427\u5bb6\u59b9\u5b50\uff0c\u8fd9\u5e74\u5934\u6709\u4e9b\u7537\u5b50\uff0c\u4f60\u4e0d\u8fd9\u6837\u5bf9\u5fd6\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5c31\u81ea\u4ee5\u4e3a\u86ee\u4e0d\u9519 \u7684\uff0c\u50cf\u82cd\u8747\u4f3c\u7684\u53ee\u5728\u4f60\u540e\u9762\uff0c\u786e\u5b9e\u8ba8\u538c\uff01\u201d \u3000\u3000\u5979\u5a07\u7b11\u4e00\u4e0b\uff1a \u3000\u3000\u201c\u8981\u662f\u5929\u4e0b\u7684\u7537\u4eba\u90fd\u50cf\u4f60\uff0c\u90a3\u5c31\u6ca1\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u8138\u4e00\u7ea2\uff0c\u60f3\u5230\u81ea\u5df1\u4ee5\u524d\u4e0d\u4e5f\u662f\u6574\u5929\u8ddf\u5728\u9500\u9b42\u592b\u4eba\u540e\u9762\uff0c\u5fc3\u91cc\u6709\u4e9b\u4e0d\u81ea\u5728\uff0c\u5927\u6709\u540e\u6094 \u81ea\u5df1\u4ee5\u524d\u4e5f\u4e22\u4e86\u4eba\u7684\u610f\u601d\u3002 \u3000\u3000\u8427\u5357\u82f9\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e00\u4e2a\u59d1\u5a18\u4e8c\u4e2a\u59b9\u5b50\u7684\uff0c\u628a\u6211\u53eb\u5f97\u4e5f\u88c5\u4e0d\u6210\u7537\u4eba\u4e86\u3002\u201d \u3000\u3000\u4f38\u624b\u5728\u8138\u4e0a\u4e00\u62b9\uff0c\u4e00\u4e2a\u7edd\u7f8e\u7684\u9762\u5bb9\uff0c\u4fbf\u5947\u8ff9\u822c\u5730\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u773c\u524d\u53c8\u4e00\u4eae\uff0c\u5927\u4e3a\u8d5e\u670d\u90a3\u201c\u8427\u4e09\u7237\u201d\u7684\u6613\u5bb9\u4e4b\u672f\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u96be\u602a\u8427\u4e09\u7237\u4ee5\u524d\u66fe\u4ee5\u5341\u4e00\u4e2a\u540d\u5b57\u51fa\u73b0\u6c5f\u6e56\uff1a\u800c\u4e14\u82e5\u4e0d\u662f\u4ed6\u81ea\u5df1\u5728\u6b66\u6797\u5927\u4f1a\u4e0a\uff0c\u81ea\u5df1\u5ba3 \u6016\u4e86\u51fa\u6765\uff0c\u6c5f\u6e56\u4e0a\u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u5341\u4e00\u4e2a\u4eba\uff0c\u5176\u5b9e\u53ea\u662f\u4e00\u4e2a\u4eba\u3002\u73b0\u5728\u4ece\u4ed6\u5973\u513f\u8eab\u4e0a\uff0c\u5c31\u53ef\u4ee5\u770b \u51fa\u4ed6\u6613\u5bb9\u672f\u7684\u795e\u5999\u4e86\u3002\u201d \u3000\u3000\u773c\u5149\u5374\u4e0d\u81ea\u89c9\u5730\uff0c\u53c8\u779f\u5230\u8427\u5357\u82f9\u8eab\u4e0a\u3002 \u3000\u3000\u8c37\u6653\u9759\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u5750\u4e00\u4e0b\uff0c\u6211\u53bb\u66ff\u4f60\u4eec\u5f04\u4e9b\u7ca5\u6765\u3002\u201d \u3000\u3000\u5979\u8f7b\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u59da\u8001\u4e8c\u8fd9\u4e9b\u5e74\u6765\u8eab\u4f53\u8d8a\u53d1\u574f\u4e86\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u8d77\u6765\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c0f\u59d0\u592b\u8fd8\u5728\u7761\u5440\uff0c\u4ed6\u8ddf\u4f60\u5728\u4e00\u8d77\u8fd9\u4e48\u4e9b\u5e74\uff0c\u8eab\u4f53\u8981\u662f\u8fd8\u4e0d\u574f\uff0c\u90a3\u624d\u662f\u6ca1\u6709\u9053\u7406\u4e86 \u54e9\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u7684\u8138\u4e5f\u4e0d\u7981\u7ea2\u4e86\u8d77\u6765\uff0c\u8c37\u6653\u9759\u7b11\u7740\u8dd1\u8fc7\u53bb\u6253\u5979\uff0c\u4e00\u9762\u4fcf\u9a82\u9053\uff1a \u3000\u3000\u201c\u770b\u4f60\u8fd9\u5f20\u7f3a\u5fb7\u5634\uff0c\u5c06\u6765\u8c01\u8981\u662f\u5a36\u4e86\u4f60\uff0c\u51c6\u4fdd\u6bd4\u94c1\u621f\u6e29\u4faf\u5415\u5357\u4eba\u8fd8\u8981\u5012\u9709\uff01\u201d \u3000\u3000\u4f0a\u98ce\u6697\u6697\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u6c5f\u6e56\u4e2d\u4eba\u7adf\u5c06\u4ed6\u6b64\u505a\u5230\u9709\u7684\u5bf9\u8c61\uff0c\u4ed6\u4e0d\u7981\u6709\u4e9b\u81ea\u601c\uff0c\u4e5f\u6709\u4e9b\u81ea \u8d23\uff0c\u89c9\u5f97\u5728\u8fd9\u91cc\u518d\u4e5f\u803d\u4e0d\u4e0b\u53bb\u4e86\uff0c\u62f1\u624b\u9053\uff1a \u3000\u3000\u201c\u8c37\u59d1\u5a18\uff01\u4e0d\u7528\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u5374\u88ab\u8c37\u6653\u9759\u6253\u65ad\u4e86\u8bdd\u5934\uff0c\u7528\u90a3\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u4e0a\u4e0b\u6253\u91cf\u7740\u4ed6\uff0c\u4e00 \u9762\u7b11\u9053\uff1a \u3000\u3000\u201c\u54a6\uff01\u4f60\u600e\u4e48\u77e5\u9053\u6211\u59d3\u8c37\uff01\u201d \u3000\u3000\u773c\u775b\u4e00\u631f\uff1a\u201c\u5582\uff01\u6211\u770b\u4f60\u8d8a\u53d1\u9762\u719f\uff0c\u6211\u4eec\u4ee5\u524d\u662f\u66fe\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u9762\u5427\uff1f\u6211\u60f3\u60f3\u770b\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u60ca\uff0c\u8fde\u5fd9\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u7684\u786e\u6ca1\u6709\u8fd9\u4efd\u8363\u5e78\u89c1\u8fc7\u59d1\u5a18\uff0c\u53ea\u662f\u201c\u8fa3\u624b\u897f\u65bd\u201d\u540d\u6ee1\u5929\u4e0b\uff0c\u5c0f\u9119\u4e5f\u66fe\u5e38\u5e38\u542c\u5230\u8fc7 \u59d1\u5a18\u7684\u540d\u5b57\uff0c\u6240\u4ee5\u624d\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8c37\u6653\u9759\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4ecd\u7136\u6709\u4e9b\u4e0d\u76f8\u4fe1\u7684\u610f\u601d\uff0c\u4f4e\u7740\u5934\u4e0d\u77e5\u5728\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u5fd6\uff1a \u3000\u3000\u201c\u6211\u65e9\u8be5\u8d70\u4e86\u7684\uff0c\u7b49\u4e00\u4e0b\u90a3\u706b\u795e\u7237\u82e5\u4e5f\u5230\u6b64\u95f4\u6765\uff0c\u90a3\u5c31\u4e00\u5b9a\u8ba4\u5f97\u6211\u4e86\u3002\u6211\u8bc8\u6b7b\u4e4b\u4e8b\u82e5\u4f20 \u51fa\u6c5f\u6e56\uff0c\u975e\u4f46\u662f\u4e2a\u7b11\u8bdd\uff0c\u5929\u4e89\u6559\u52bf\u5fc5\u53c8\u8981\u518d\u6765\u641c\u5bfb\u6211\uff0c\u90a3\u6211\u8fde\u5b89\u5fc3\u9759\u7ec3\u6b66\u529f\u90fd\u4e0d\u80fd\u591f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8d8a\u60f3\u8d8a\u89c9\u6b64\u884c\u5b9e\u5728\u51a4\u6789\uff0c\u8eab\u5b50\u4e00\u8f6c\uff0c\u5148\u8d70\u5230\u95e8\u53e3\uff0c\u624d\u62f1\u624b\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u65e0\u72b6\uff0c\u6253\u6270\u4e86\u4e24\u4f4d\u8bb8\u4e45\uff0c\u5b9e\u5728\u8be5\u6b7b\uff0c\u65e5\u540e\u518d\u6765\u8c22\u7f6a\u5427\uff01\u201d \u3000\u3000\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u4e0d\u7b49\u4eba\u5bb6\u7684\u7b54\u8986\uff0c\u8f6c\u5934\u6025\u6025\u5411\u5916\u8d70\u53bb\u3002 \u3000\u3000\u4ed6\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u8fd9\u4e48\u4e00\u6765\uff0c\u662f\u5426\u4f1a\u66f4\u5f15\u8d77\u4eba\u5bb6\u7684\u6000\u7591\uff1f \u3000\u3000\u8d70\u5230\u56ed\u4e2d\uff0c\u6ee1\u56ed\u7684\u82b1\u6728\uff0c\u6b64\u523b\u592a\u534a\u51cb\u96f6\uff1b\u56ed\u4fa7\u7684\u534a\u6c60\u82b0\u8377\uff0c\u4f46\u4e86\u65ad\u6897\u6b8b\u679d\u3002\u79ef\u96ea\u672a\u6eb6\uff0c \u65b0\u971c\u8ff9\u5728\uff1b\u5bd2\u98ce\u5439\u8fc7\uff0c\u5bd2\u98d9\u88ad\u4eba\u3002 \u3000\u3000\u4ed6\u5927\u6b65\u800c\u884c\uff0c\u5f53\u7136\u4e0d\u4f1a\u6709\u5fc3\u60c5\u6765\u9886\u7565\u8fd9\u6b8b\u51ac\u7684\u5c0f\u56ed\u666f\u8272\u3002 \u3000\u3000\u773c\u89d2\u52a8\u5904\uff0c\u770b\u5230\u5899\u89d2\u6709\u4e2a\u6731\u7ea2\u7684\u5c0f\u95e8\uff0c\u8fde\u5fd9\u8d70\u4e86\u8fc7\u53bb\u3002 \u7b2c\u4e00\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u6cc4\u9732\u884c\u85cf>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u6cc4\u9732\u884c\u85cf \u3000\u3000\u4ed6\u6025\u6b65\u800c\u884c\uff0c\u90a3\u77e5\u5728\u4ed6\u8ddd\u79bb\u90a3\u5c0f\u95e8\u8fd8\u6709\u51e0\u6b65\u7684\u65f6\u5019\uff0c\u7a81\u7136\u8eab\u4fa7\u201c\u55d6\uff0c\u55d6\u201d\u4e24\u9053\u98ce\u58f0\u63a0\u4e86 \u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u5b9a\u773c\u4e00\u770b\uff0c\u90a3\u8fa3\u624b\u897f\u65bd\u548c\u6f47\u6e58\u5983\u5b50\u7adf\u65bd\u5c55\u8eab\u6cd5\uff0c\u63a0\u5230\u4e86\u4ed6\u7684\u524d\u9762\uff0c\u5835\u5728\u90a3\u5c0f\u95e8\u7684\u95e8 \u53e3\uff0c\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u53c8\u4e00\u60ca\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e24\u4eba\u662f\u4f55\u7528\u610f\u3002\u90a3\u77e5\u8c37\u6653\u9759\u5374\u6307\u7740\u4ed6\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u522b\u8d70\uff01\u6211\u60f3\u8d77\u4f60\u662f\u8c01\u4e86\uff0c\u4f60\u5c31\u662f\u94c1\u621f\u6e29\u4faf\u5415\u5357\u4eba\u3002\u201d \u3000\u3000\u4f0a\u98ce\u8fde\u5fd9\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\u8ba4\u9519\u4e86\u4eba\u5427\uff1f\u201d \u3000\u3000\u8c37\u6653\u9759\u54af\u54af\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u522b\u6025\uff01\u6211\u624d\u4e0d\u4f1a\u8ba4\u9519\u4eba\u5462\u3002\u90a3\u5e74\u5728\u6cf0\u5c71\u7389\u7687\u9876\u4e0a\uff0c\u6211\u770b\u89c1\u8fc7\u4f60\uff0c\u73b0\u5728\u624d\u60f3\u8d77\u6765\u2014 \u2014\u201d \u3000\u3000\u4f0a\u98ce\u60f6\u6025\u4e4b\u4e0b\uff0c\u4e00\u584c\u8170\uff0c\u5411\u4e0a\u63a0\u53bb\uff0c\u60f3\u4e00\u6e9c\u4e86\u4e4b\u3002 \u3000\u3000\u8c37\u6653\u9759\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u8dd1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u67f3\u8170\u4e00\u626d\uff0c\u4e5f\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u5728\u7a7a\u4e2d\u4e00\u8f6c\u52bf\uff0c\u53f3\u638c\u7adf\u5411\u8c37\u6653\u9759\u5288\u53bb\uff0c\u8eab\u5f62\u5374\u52aa\u529b\u5411\u5de6\u4e00\u626d\uff0c\u60f3\u8d8a\u5899\u800c\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u53c8\u662f\u4e00\u58f0\u5389\u559d\uff01 \u3000\u3000\u201c\u4ec0\u4e48\u4eba\u5728\u6b64\u6492\u91ce\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6765\u4e0d\u53ca\u56de\u5934\u53bb\u770b\uff0c\u53ea\u89c9\u6709\u4e00\u7f15\u52b2\u98ce\uff0c\u51fb\u5411\u81ea\u5df1\u7684\u5de6\u80c1\u3002\u98ce\u58f0\u9510\u5229\uff0c\u663e\u89c1\u5f97\u8fd9\u53d1\u6697\u5668 \u4e4b\u4eba\u624b\u52b2\u6781\u5927\u3002 \u3000\u3000\u4f0a\u98ce\u5728\u7a7a\u4e2d\u5df2\u8f6c\u8fc7\u4e00\u52bf\uff0c\u6b64\u523b\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u518d\u4e5f\u65e0\u6cd5\u5728\u7a7a\u4e2d\u85c9\u529b\u8f6c\u6298\uff0c\u800c\u90a3\u6697\u5668\u4e5f\u773c \u770b\u5c31\u8981\u6253\u5728\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u53ea\u542c\u5230\u201c\u6ce2\u201d\u4e00\u58f0\uff0c\u5de6\u4fa7\u6e9c\u8d77\u4e00\u6e9c\u84dd\u8272\u7684\u706b\u7130\uff0c\u539f\u6765\u6709 \u4eba\u4e5f\u7528\u6697\u5668\u5c06\u51fb\u5411\u4ed6\u7684\u6697\u5668\u51fb\u843d\u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u51db\uff0c\u77e5\u9053\u51fb\u5411\u81ea\u5df1\u7684\u6697\u5668\uff0c\u6b63\u662f\u6c5f\u6e56\u4e0a\u95fb\u540d\u4e27\u80c6\u7684\u201c\u706b\u795e\u73e0\u201d\u3002 \u3000\u3000\u5fc3\u795e\u4e00\u5206\u4e4b\u4e0b\uff0c\u51fb\u5411\u8c37\u6653\u9759\u7684\u53f3\u638c\u5f53\u7136\u843d\u7a7a\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u5df2\u65e0\u6cd5\u6e9c\u51fa\u6b64\u95f4\uff0c\u53ea\u5f97\u63d0\u7740\u6c14\u8f7b\u98d8\u98d8\u5730\u843d\u5230\u5730\u4e0b\u3002 \u3000\u3000\u4e00\u4e2a\u4e94\u77ed\u8eab\u6750\u7684\u6c49\u5b50\u98de\u5feb\u5730\u63a0\u4e86\u8fc7\u6765\uff0c\u53e3\u4e2d\u5927\u559d\u7740\u9053\uff1a \u3000\u3000\u201c\u8427\u5927\u59b9\u5b50\uff01\u4f60\u600e\u7684\u5c06\u6211\u7684\u6697\u5668\u51fb\u843d\u4e86\uff1f\u201d \u3000\u3000\u8eab\u5f62\u4e00\u987f\uff0c\u505c\u5728\u4f0a\u98ce\u5bf9\u9762\uff0c\u6b63\u81ea\u626c\u638c\u5f85\u51fb\uff0c\u770b\u5230\u4f0a\u98ce\u7684\u9762\u5bb9\uff0c\u5ffd\u5730\u201c\u5440\u201d\u5730\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u8eab\u6750\u77ee\u80d6\u7684\u6c49\u5b50\uff0c\u81ea\u7136\u5c31\u662f\u706b\u795e\u7237\u59da\u6e05\u5b87\u4e86\uff0c\u4ed6\u60ca\u5524\u4e4b\u540e\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5415\u5357\u4eba\u5415\u8001 \u5f1f\u5417\uff1f\u600e\u4f1a\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u597d\u6781\uff01\u597d\u6781\uff01\u201d \u3000\u3000\u4ed6\u5927\u7b11\u51e0\u58f0\uff0c\u8d70\u8fc7\u53bb\u62c9\u7740\u4f0a\u98ce\u7684\u81c2\u8180\uff0c\u4e00\u9762\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6b66\u6797\u4e2d\u90fd\u4f20\u8bf4\u4f60\u6b7b\u4e86\uff0c\u6211\u53ef\u4e0d\u76f8\u4fe1\uff0c\u5c31\u51ed\u4f60\u5bd2\u94c1\u53cc\u621f\u4e0a\u7684\u529f\u592b\uff0c\u96be\u9053\u8fd8\u4f1a\u8ba9\u522b\u4eba\u5360\u4e86 \u4fbf\u5b9c\uff01\u6211\u5c31\u60f3\u4f60\u4e00\u5b9a\u662f\u5728\u73a9\u82b1\u6837\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u6781\u4e3a\u8c6a\u723d\u7684\u5927\u7b11\u4e86\u4e24\u58f0\uff0c\u62cd\u7740\u4f0a\u98ce\u7684\u80a9\u5934\u6717\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u5feb\u8fdb\u53bb\u5750\uff01\u8198\u8fdb\u53bb\u5750\uff01\u6211\u4eec\u8001\u54e5\u513f\u4fe9\u4eba\u5012\u5f97\u597d\u597d\u8c08\u8c08\u3002\u201d \u3000\u3000\u4f0a\u98ce\u552f\u552f\u5e94\u7740\uff0c\u5fc3\u4e2d\u8001\u5927\u4e0d\u662f\u6ecb\u5473\u3002\u4ed6\u548c\u8fd9\u706b\u795e\u7237\u59da\u6e05\u5b87\u867d\u89c1\u8fc7\u6570\u9762\uff0c\u4f46\u5374\u4e0d\u662f\u6df1\u4ea4\uff0c \u6b64\u523b\u4eba\u5bb6\u8fd9\u4e48\u70ed\u60c5\u5730\u62db\u547c\u4ed6\uff0c\u4ed6\u5f53\u7136\u9ad8\u5174\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u884c\u85cf\u4e00\u9732\uff0c\u540e\u60a3\u65e0\u7a77\uff0c\u53c8\u4ee4\u4ed6\u9887\u4e0d\u81ea\u5728\u3002 \u3000\u3000\u8c37\u6653\u9759\u4e5f\u8d70\u8fc7\u6765\u7b11\u9053\uff1a \u3000\u3000\u201c\u521a\u624d\u4ed6\u8fd8\u85cf\u5934\u9732\u5c3e\u7684\uff0c\u751f\u6015\u522b\u4eba\u77e5\u9053\u4ed6\u6ca1\u6b7b\u3002\u5582\uff01\u6211\u8bf4\u5415\u8001\u5f1f\u5440\uff01\u4f60\u5802\u5802\u4e00\u4e2a\u6210\u540d\u9732 \u8138\u7684\u82f1\u96c4\uff0c\u53ef\u4e0d\u80fd\u8fd9\u4e48\u7740\uff01\u6709\u4ec0\u4e48\u597d\u6015\u7684\uff1f\u4f60\u8001\u5a46\u4e22\u4e86\u4f60\u7684\u4eba\uff0c\u4f60\u53ef\u4e0d\u80fd\u518d\u66ff\u81ea\u5df1\u4e22\u4eba \u5566\uff01\u201d \u3000\u3000\u4f0a\u98ce\u2014\u2014\u4ed6\u81ea\u8a93\u4e0d\u80fd\u96ea\u803b\uff0c\u5c31\u4e0d\u518d\u4ee5\u5415\u5357\u4eba\u7684\u540d\u5b57\u51fa\u73b0\u4eba\u4e16\uff0c\u662f\u4ee5\u6211\u4eec\u6b64\u523b\u4e5f\u53ea\u5f97\u8fd8\u79f0 \u547c\u4ed6\u8fd9\u4e2a\u540d\u5b57\u2014\u2014\u6b64\u523b\u4ed6\u7684\u5fc3\u4e2d\uff0c\u50cf\u662f\u6253\u7ffb\u4e86\u4e94\u5473\u74f6\uff0c\u4e71\u4e03\u516b\u7cdf\u5730\u4ec0\u4e48\u6ecb\u5473\u90fd\u6709\u3002 \u3000\u3000\u867d\u7136\u4ed6\u77e5\u9053\u8fd9\u59da\u6e05\u5b87\u592b\u5987\u90fd\u662f\u6027\u60c5\u4eba\u7269\uff0c\u4f46\u81ea\u5df1\u7684\u884c\u8e2a\u6cc4\u9732\uff0c\u4ecd\u4f7f\u4ed6\u4e0d\u5b89\uff1b\u800c\u8fd9\u79cd\u4e0d\u5b89 \u4e2d\uff0c\u53c8\u6709\u5bf9\u4ed6\u4eec\u592b\u5987\u8fd9\u79cd\u70ed\u60c5\u7684\u611f\u6fc0\u3002\u542c\u4e86\u8c37\u6653\u9759\u7684\u8bdd\uff0c\u5374\u53c8\u6709\u4e9b\u60ed\u6127\uff1b\u60f3\u5230\u81ea\u5df1\u7684\u59bb\u5b50\uff0c \u53c8\u6709\u4e9b\u7f9e\u6012\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5728\u6e05\u6668\u51db\u51bd\u7684\u5bd2\u98ce\u91cc\u6115\u4f4f\u4e86\uff0c\u8111\u4e2d\u6df7\u6df7\u6c8c\u6c8c\u7684\u3002 \u3000\u3000\u76f4\u5230\u59da\u6e05\u5b87\u5c06\u4ed6\u62c9\u5165\u4e86\u524d\u623f\u7684\u5ba2\u5385\uff0c\u5b89\u6392\u4ed6\u5750\u5728\u4e00\u5f20\u5bbd\u5927\u7684\u7d2b\u6a80\u6728\u6905\u4e0a\uff0c\u4ed6\u8111\u4e2d\u7684\u90a3\u79cd \u6df7\u6c8c\u7684\u611f\u89c9\uff0c\u4ecd\u7136\u672a\u66fe\u5b8c\u5168\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u968f\u53e3\u5e94\u7740\u4ed6\u4eec\u5411\u4ed6\u95ee\u7740\u7684\u8bdd\u3002\u9aa4\u7136\u63a5\u89e6\u5230\u8fd9\u4e9b\u548c\u4ed6\u4ee5\u524d\u7684\u90a3\u4e00\u6bb5\u65e5\u5b50\u6709\u7740\u5bc6\u5207\u5173\u7cfb\u7684 \u4eba\uff0c\u4ed6\u89c9\u5f97\u5947\u602a\u7684\u4e0d\u5b89\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e24\u5e74\u6765\uff0c\u4ed6\u51e0\u4e4e\u5df2\u5c06\u5df2\u5f80\u7684\u90a3\u4e00\u6bb5\u65e5\u5b50\uff0c\u5b8c\u5168\u5fd8\u5374\u4e86\u3002 \u3000\u3000\u4ed6\u968f\u65f6\u544a\u8bc9\u81ea\u5df1\uff1a\u81ea\u5df1\u53ea\u662f\u4f0a\u98ce\uff0c\u53ea\u662f\u6c5f\u6e56\u4e0a\u4e00\u4e2a\u65e0\u540d\u65e0\u59d3\u7684\u4eba\uff1b\u800c\u7edd\u4e0d\u662f\u66fe\u5728\u6c5f\u6e56\u4e0a \u663e\u8d6b\u8fc7\u4e00\u65f6\u7684\u94c1\u621f\u6e29\u4faf\u5415\u5357\u4eba\u3002 \u3000\u3000\u800c\u4ed6\u4e5f\u786e\u4e4e\u5fd8\u8bb0\u4e86\u81ea\u5df1\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u9aa4\u7136\u53c8\u88ab\u4eba\u5bb6\u62c9\u56de\u5230\u4ee5\u5f80\u7684\u65f6\u65e5\u4e2d\u53bb\uff0c\u56e0\u4e3a\u8fd9\u4e9b \u4eba\u53ea\u77e5\u9053\u4ed6\u662f\u5415\u5357\u4eba\uff0c\u4e5f\u90fd\u53ea\u628a\u4ed6\u5f53\u505a\u5415\u5357\u4eba\u770b\u3002 \u3000\u3000\u4ed6\u81ea\u601c\u5730\u4e00\u7b11\uff0c\u6697\u5fd6\u7740\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u628a\u6211\u770b\u505a\u4ec0\u4e48\uff1f\u770b\u505a\u4e00\u4e2a\u8fde\u81ea\u5df1\u59bb\u5b50\u90fd\u770b\u4e0d\u4f4f\u7684\u53ef\u601c\u866b\uff1f\u201d \u3000\u3000\u5728\u59da\u6e05\u5b87\u90a3\u4e9b\u4eba\u95ee\u7740\u4ed6\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u4f7f\u5f97\u59da\u6e05\u5b87\u7b49\u4e09\u4eba\uff0c\u8868\u9762\u4e0a\u867d\u5728 \u7b11\u7740\uff0c\u5fc3\u4e2d\u4e5f\u5728\u4e3a\u4ed6\u53f9\u606f\u3002 \u3000\u3000\u5c24\u5176\u662f\u8427\u5357\u82f9\uff0c\u5979\u7684\u4e00\u53cc\u660e\u7738\uff0c\u81ea\u59cb\u81f3\u7ec8\uff0c\u5c31\u59cb\u7ec8\u671b\u7740\u4ed6\u7684\u8138\uff0c\u4ed6\u867d\u7136\u5bf9\u5979\u5f88\u51b7\u6f20\uff0c\u751a \u81f3\u53ef\u4ee5\u8bf4\u662f\u5f88\u8f7b\u8511\uff1b\u4f46\u5979\u5374\u83ab\u540d\u5176\u5999\u5730\u5bf9\u4ed6\u8d77\u4e86\u597d\u611f\uff0c\u800c\u4e14\u7adf\u662f\u5979\u4ece\u672a\u6709\u8fc7\u7684\u597d\u611f\u3002 \u3000\u3000\u59da\u6e05\u5b87\u8c6a\u723d\u5730\u7b11\u9053\uff1a \u3000\u3000\u201c\u5415\u8001\u5f1f\uff01\u4f60\u5148\u5728\u8fd9\u91cc\u4f4f\u51e0\u5929\uff0c\u8ba9\u6211\u5e26\u4f60\u6563\u6563\u5fc3\u3002\u4f60\u653e\u5fc3\u597d\u4e86\uff0c\u4f60\u7684\u884c\u8e2a\u4e0d\u613f\u88ab\u522b\u4eba\u77e5 \u9053\uff0c\u6211\u4eec\u4e5f\u7edd\u4e0d\u4f1a\u5bf9\u522b\u4eba\u8bf4\u7684\u3002\u201d \u3000\u3000\u4f0a\u98ce\u611f\u6fc0\u5730\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u59da\u5927\u54e5\u7684\u76db\u610f\uff0c\u53ea\u662f\u5c0f\u5f1f\u5b9e\u5728\u56e0\u7740\u6025\u4e8b\uff0c\u8981\u8d76\u5230\u7ec8\u5357\u5c71\u53bb\u3002\u201d \u3000\u3000\u59da\u6e05\u5b87\u54a6\u4e86\u4e00\u58f0\uff0c\u6709\u4e9b\u60ca\u8bb6\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e5f\u8981\u5230\u7ec8\u5357\u5c71\uff1f\u201d \u3000\u3000\u624b\u4e00\u629a\u989d\uff0c\u53c8\u6c88\u541f\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u7ec8\u5357\u5c71\u7684\u4f1a\u671f\uff0c\u79bb\u73b0\u5728\u8fd8\u6709\u534a\u4e2a\u6708\u5440\u3002\u6211\u51c6\u5907\u8fc7\u51e0\u5929\u624d\u52a8\u8eab\uff0c\u4f60\u90a3\u4e48\u6025\u5e72\u4ec0\u4e48\uff1f \u96be\u9053\u4f60\u5148\u8d76\u5230\u7ec8\u5357\u5c71\u53bb\uff0c\u8fd8\u6709\u7740\u4ec0\u4e48\u522b\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u5374\u4e00\u60ca\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u4f1a\u671f\uff1f\u201d \u3000\u3000\u542c\u4e86\u201c\u4f1a\u671f\u201d\u4e24\u5b57\uff0c\u4ed6\u5927\u60ca\uff0c\u4ee5\u4e3a\u662f\u201c\u8d85\u6e21\u4ea1\u9b42\u201d\u90a3\u4e00\u7c7b\u7684\u4f1a\u671f\uff1a \u3000\u3000\u201c\u96be\u9053\u7ec8\u5357\u5f1f\u5b50\u5df2\u7b49\u4e0d\u53ca\u6211\uff0c\u5168\u6b7b\u4e86\u3002\u201d \u3000\u3000\u59da\u6e05\u5b87\u5fae\u6014\u9053\uff1a \u3000\u3000\u201c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4ed6\u5fae\u987f\u53c8\u9053\uff1a \u3000\u3000\u201c\u7ec8\u5357\u5c71\u4e0d\u77e5\u9053\u51fa\u4e86\u4ec0\u4e48\u53d8\u6545\uff0c\u638c\u95e8\u4eba\u7384\u95e8\u4e00\u9e64\u7a81\u7136\u6b7b\u4e86\uff0c\u7ec8\u5357\u5f1f\u5b50\u67ec\u9080\u5929\u4e0b\u6b66\u6750\uff0c\u5728 \u4e8c\u6708\u5eff\u56db\u65e5\u82b1\u671d\u8282\u90a3\u4e00\u5929\uff0c\u91cd\u9009\u7ec8\u5357\u638c\u95e8\u3002\u6211\u4e5f\u63a5\u5230\u8bf7\u67ec\u4e86\uff0c\u662f\u6628\u5929\u665a\u4e0a\u7531\u7ec8\u5357\u5f1f\u5b50\u9a91\u7740\u5feb \u9a6c\u9001\u5230\u7684\u3002\u201d \u3000\u3000\u4ed6\u5fae\u559f\u53c8\u9053\uff1a \u3000\u3000\u201c\u6700\u5947\u602a\u7684\u662f\uff1a\u6211\u95ee\u90a3\u4e2a\u7ec8\u5357\u5f1f\u5b50\u201c\u638c\u95e8\u4eba\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u4ed6\u5374\u652f\u652f\u5514\u5514\u5730\u4e0d\u80af\u8bf4\u3002\u6211 \u95ee\u4ed6\u201c\u6b7b\u4e86\u591a\u4e45.\u201d\u4ed6\u5374\u8bf4\u624d\u6b7b\u4e86\u4e24\u5929\u3002\u638c\u95e8\u624d\u6b7b\u4e86\u4e24\u5929\uff0c\u5c31\u6025\u7740\u53e6\u9009\u638c\u95e8\uff0c\u800c\u4e14\u8fd9\u7ec8\u5357\u5f1f \u5b50\u65e2\u672a\u5e26\u9ed1\uff0c\u4e5f\u6ca1\u6709\u534a\u70b9\u60b2\u621a\u4e4b\u5bb9\uff0c\u6211\u5c31\u89c9\u5f97\u4e8b\u60c5\u5927\u6709\u8e4a\u8df7\u5462\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u542c\u5b8c\uff0c\u53c8\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u5f04\u4e0d\u61a7\u8eab\u4e2d\u4e0d\u6cbb\u4e4b\u6bd2\u7684\u7ec8\u5357\u5b50\u5f1f\uff0c\u4e3a\u4ec0\u4e48\u90fd\u6ca1\u6709\u6b7b\uff01\u6b7b\u7684\u5374\u662f\u6ca1\u6709\u4e2d\u6bd2\u7684\u7ec8\u5357\u638c\u95e8\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5728\u81ea\u5df1\u79bb\u5f00\u7ec8\u5357\u5c71\u7684\u8fd9\u4e00\u6bb5\u65f6\u95f4\u91cc\uff0c\u7ec8\u5357\u5c71\u4e00\u5b9a\u53c8\u751f\u51fa\u5de8\u53d8\u3002\u201c\u4f46\u662f\u4ec0\u4e48\u53d8\u6545 \u5462\uff1f\u201d\u4ed6\u5374\u53c8\u832b\u7136\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u5b59\u654f\u6bcd\u5973\uff1a\u201c\u4e0d\u77e5\u9053\u5979\u4eec\u8fd8\u5728\u4e0d\u5728\u90a3\u91cc\u4e86\uff1f\u201d\u5fc3\u4e2d\u7adf\u7136\u975e\u5e38\u5173\u5fc3\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u660e \u4e86\u81ea\u5df1\u8fd9\u79cd\u5173\u5fc3\u7684\u7531\u6765\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u8111\u6d77\u4e2d\u8f6c\u5440\u8f6c\u7684\uff0c\u7adf\u7136\u90fd\u662f\u5b59\u654f\u90a3\u4eb2\u5207\u7684\u76ee\u5149\uff0c\u4eb2\u5207\u7684\u7b11\u5bb9\u3002\u4e8e\u662f\u4ed6\u8fde\u5fd9 \u5f3a\u5236\u7740\u81ea\u5df1\uff0c\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u62ac\u5934\uff0c\u5374\u548c\u8427\u5357\u82f9\u7684\u76ee\u5149\u78b0\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u4ed6\u4e45\u7ecf\u4e16\u6545\uff0c\u5f53\u7136\u77e5\u9053\u8427\u5357\u82f9\u76ee\u5149\u4e2d\u7684\u542b\u610f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5347\u8d77\u4e86\u4e00\u79cd\u5947\u602a\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u4ed6\u6697\u7b11\u81ea\u5df1\uff0c\u4ed6\u4e4b\u4e00\u751f\uff0c\u8bb8\u591a\u91cd\u8981\u7684\u8f6c\u53d8\uff0c\u90fd\u662f\u56e0\u7740\u5973\u4eba\u3002 \u3000\u3000\u201c\u5973\u4eba\u2026\u2026\u2026\u201d\u4ed6\u832b\u7136\u5730\u7b11\u4e86\u3002 \u3000\u3000\u542b\u7740\u7b11\u610f\u7684\u76ee\u5149\uff0c\u5374\u5e73\u89c6\u7740\u4ecd\u5728\u5411\u4ed6\u6ce8\u89c6\u7740\u7684\u8427\u5357\u82f9\u3002 \u3000\u3000\u201c\u6211\u8be5\u7559\u4e0b\u6765\u5462\uff01\u6291\u6216\u662f\u79bb\u53bb\uff01\u201d\u4ed6\u53cd\u8986\u5730\u95ee\u7740\u81ea\u5df1\u3002 \u3000\u3000\u6709\u8bb8\u591a\u79cd\u7406\u7531\u8ba4\u4e3a\u4ed6\u8be5\u7559\u4e0b\u6765\u3002 \u3000\u3000\u53c8\u6709\u8bb8\u591a\u7406\u7531\uff0c\u8ba4\u4e3a\u4ed6\u8be5\u79bb\u53bb\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u662f\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u786e\u4fe1\u7ec8\u5357\u4e2d\u6bd2\u5f1f\u5b50\uff0c\u90fd\u5df2\u83b7\u5f97\u89e3\u6551\uff0c\u800c\u5e76\u672a\u7b49\u5f85\u4ed6\u7684\u89e3\u836f\u4e4b\u6545\u3002 \u3000\u3000\u201c\u4f46\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u4ed6\u53c8\u6709\u63a2\u7d22\u7ec8\u5357\u5c71\uff0c\u5012\u5e95\u53d1\u751f\u4e86\u4f55\u79cd\u53d8\u5316\u7684\u597d\u5947\u5fc3\uff1a\u4ee5\u53ca\u5bf9\u67d0\u4e9b\u4eba\u6e34 \u6b32\u4e00\u89c1\u7684\u5fc3\u60c5\uff0c\u8fd9\u662f\u4ed6\u4e9f\u6b32\u79bb\u6b64\u7684\u7406\u7531\u3002 \u3000\u3000\u4ed6\u53cd\u8986\u63a2\u7d22\u7740\uff0c\u5f77\u4f5b\u5df2\u77e5\u9053\uff1a\u65e0\u8bba\u4ed6\u51b3\u5b9a\u79bb\u53bb\u6216\u7559\u4e0b\uff0c\u90fd\u5bf9\u4ed6\u8fd9\u4e00\u751f\uff0c\u6709\u7740\u6781\u91cd\u5927\u7684\u5173 \u952e\u4f3c\u7684\uff01 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u6e29\u67d4\u4e4b\u4e61>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u6e29\u67d4\u4e4b\u4e61 \u3000\u3000\u4f0a\u98ce\u6b63\u6df1\u9677\u4e8e\u4ed6\u201c\u53bb\u201d\u201c\u7559\u201d\u4e4b\u95f4\u7684\u77db\u76fe\u4e2d\uff0c\u8fa3\u624b\u897f\u65bd\u779f\u4e86\u8427\u5357\u82f9\u4e00\u773c\uff0c\u8f6c\u5411\u4ed6\u201c\u5657 \u54e7\u201d\u7b11\u9053\uff1a \u3000\u3000\u201c\u8981\u4e48\u4f60\u5c31\u75db\u75db\u5feb\u5feb\u5730\u7559\u5728\u8fd9\u91cc\uff0c\u8981\u4e48\u4f60\u5c31\u75db\u75db\u5feb\u5feb\u5730\u8bf4\u8d70\uff0c\u4e00\u4e2a\u7537\u5b50\u6c49\u5927\u4e08\u592b\u505a\u4e8b\u600e \u4e48\u5a46\u5a46\u5988\u5988\u7684\uff1f\u201d \u3000\u3000\u706b\u795e\u7237\u59da\u6e05\u5b87\u4e5f\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u5f1f\uff01\u4f60\u6211\u4e00\u89c1\u5982\u6545\uff0c\u54b1\u4eec\u8fd9\u4e24\u5929\uff0c\u53ef\u8981\u597d\u597d\u76d8\u6853\u76d8\u6853\uff0c\u8981\u662f\u4f60\u8001\u5f1f\u518d\u63a8\u8f9e\u7684\u8bdd\uff0c\u53ef \u5c31\u663e\u5f97\u77a7\u4e0d\u8d77\u6211\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u723d\u6717\uff1a \u3000\u3000\u201c\u8fc7\u51e0\u5929\uff0c\u4f60\u6211\u4e00\u8d77\u53bb\u7ec8\u5357\u5c71\u3002\u54c8\u54c8\uff01\u5927\u7ea6\u53c8\u662f\u573a\u70ed\u95f9\uff0c\u542c\u8bf4\u6709\u8bb8\u591a\u4eba\u90fd\u8981\u85c9\u7740\u8fd9\u673a\u4f1a \u53bb\u9732\u9732\u9762\u54e9\uff01\u201d \u3000\u3000\u987b\u77e5\u4e00\u95e8\u4e00\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u5927\u591a\u662f\u627f\u7ee7\u7684\uff0c\u8fd9\u79cd\u63a8\u4e3e\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u5b9a\u662f\u6709\u7740\u7279\u522b\u7f18\u6545\uff0c\u5728 \u6b66\u6797\u4e2d\u5e76\u4e0d\u591a\u89c1\uff0c\u800c\u8fd9\u79cd\u9f99\u86c7\u6df7\u96c6\u7684\u573a\u5408\uff0c\u4e5f\u5e76\u4e0d\u53ea\u662f\u9009\u9009\u638c\u95e8\u4eba\u90a3\u4e48\u5355\u7eaf\uff0c\u5b9a\u6709\u8bb8\u591a\u4e8b\u6545 \u53d1\u751f\u3002 \u3000\u3000\u662f\u4ee5\u706b\u795e\u7237\u7b11\u9053\uff1a\u201c\u5b9a\u6709\u70ed\u95f9\u597d\u770b\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5374\u6c88\u541f\u534a\u664c\uff0c\u53f9\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5f1f\u539f\u60f3\u5728\u4f1a\u671f\u4e4b\u524d\uff0c\u8d76\u5230\u7ec8\u5357\uff0c\u56e0\u4e3a\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u957f\u53f9\u4e00\u58f0\uff1a \u3000\u3000\u201c\u5c0f\u5f1f\u66fe\u8a93\u8a00\uff0c\u5982\u4e0d\u96ea\u803b\uff0c\u518d\u4e5f\u4e0d\u4ee5\u201c\u5415\u5357\u4eba\u201d\u4e4b\u8eab\u4efd\u51fa\u73b0\u2026\u2026\u201d \u3000\u3000\u8c37\u6653\u9759\u5374\u53c8\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u6015\u4eba\u5bb6\u8ba4\u51fa\u4f60\u7684\u771f\u9762\u76ee\uff0c\u5947\u602a\u4f60\u8fd9\u6b7b\u4e86\u7684\u4eba\u600e\u4e48\u53c8\u7a81\u7136\u590d\u6d3b\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5979\u5a07\u7b11\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f60\u8fd9\u771f\u662f\u591a\u8651\u4e86\uff0c\u8fd9\u8fd8\u4e0d\u597d\u529e\u2014\u2014\u201d \u3000\u3000\u5979\u6307\u4e86\u6307\u59cb\u7ec8\u51dd\u89c6\u4f0a\u98ce\u7684\u8427\u5357\u82f9\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u73b0\u6210\u5730\u653e\u7740\u8fd9\u4f4d\u8427\u4e09\u7237\u7684\u5343\u91d1\u5728\u8fd9\u91cc\uff0c\u53ea\u8981\u5979\u5728\u4f60\u8138\u4e0a\u52a8\u52a8\u624b\uff0c\u6211\u6015\u8fde\u4f60\u81ea\u5df1\u90fd\u4e0d\u89c1 \u5f97\u8ba4\u5f97\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u53c8\u662f\u4e00\u8fde\u4e32\u7684\u5a07\u7b11\u3002 \u3000\u3000\u706b\u795e\u7237\u4e00\u62cd\u5927\u817f\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd8\u662f\u4f60\u60f3\u5f97\u51fa\u6765\u3002\u201d\u8a00\u4e0b\u9887\u4e3a\u6fc0\u8d4f\u3002 \u3000\u3000\u4f0a\u98ce\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u53ef\u4e5f\u4e0d\u80fd\u518d\u8bf4\u63a8\u8f9e\u7684\u8bdd\uff0c\u9042\u9053\uff1a \u3000\u3000\u201c\u5982\u6b64\u53ea\u662f\u9ebb\u70e6\u8427\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u6b63\u548c\u8427\u5357\u82f9\u7684\u773c\u775b\u4e00\u89e6\uff0c\u53ea\u89c9\u5979\u660e\u5982\u79cb\u6c34\u7684\u53cc\u77b3\u91cc\uff0c\u60c5\u610f\u8109\u8109\uff0c\u5fc3\u5934\u4e0d\u7981\u4e00 \u70ed\u3002\u4f46\u4e07\u5343\u601d\u6f6e\uff0c\u77ac\u5373\u7ffb\u6d8c\u800c\u8d77\uff0c\u7adf\u5fd8\u4e86\u5c06\u76ee\u5149\u79fb\u5f00\u4e86\u3002 \u3000\u3000\u8427\u5357\u82f9\u7c89\u988a\u4e0a\u4f3c\u4e4e\u5fae\u5fae\u4e00\u7ea2\uff0c\u4f4e\u4e0b\u5934\u53bb\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e0d\u7b97\u4ec0\u4e48\u3002\u201d \u3000\u3000\u706b\u795e\u7237\u653e\u58f0\u4e00\u7b11\uff0c\u539f\u6765\u8427\u5357\u82f9\u6b64\u523b\u4ecd\u662f\u7537\u88c5\uff0c\u505a\u51fa\u8fd9\u79cd\u5c0f\u513f\u5973\u7f9e\u7b54\u7b54\u7684\u6837\u5b50\u6765\uff0c\u5b9e\u5728\u6709 \u4e9b\u6ed1\u7a3d\u3002 \u3000\u3000\u8c37\u6653\u9759\u4e5f\u5a07\u7b11\u7740\u7ad9\u8d77\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u624d\u50cf\u7537\u5b50\u6c49\uff0c\u4f60\u6298\u817e\u4e86\u534a\u591c\uff0c\u6211\u53bb\u66ff\u4f60\u4eec\u6574\u6cbb\u4e9b\u5403\u98df\u53bb\u3002\u201d \u3000\u3000\u8202\u8471\u822c\u7684\u7ea4\u6307\u4e00\u6307\u59da\u6e05\u5b87\uff0c\u4f6f\u55d4\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u5750\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u5feb\u8ddf\u7740\u6211\u53bb\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u59da\u6e05\u5b87\u5148\u662f\u4e00\u6115\uff0c\u4f46\u968f\u7740\u4ed6\u5a07\u59bb\u7684\u773c\u775b\uff0c\u671d\u8427\u5357\u82f9\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9042\u4e5f\u77ac\u5373\u4e86\u89e3\u4e86\u4ed6\u5a07\u59bb\u7684 \u7528\u610f\uff0c\u54e6\u54e6\u8fde\u58f0\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u4e00\u9762\u6447\u5934\u4f5c\u82e6\u7b11\u72b6\u9053\uff1a \u3000\u3000\u201c\u4f60\u603b\u662f\u653e\u4e0d\u8fc7\u6211\u3002\u201d \u3000\u3000\u8f6c\u9996\u5411\u7740\u4f0a\u98ce\uff1a \u3000\u3000\u201c\u8001\u5f1f\u7a0d\u5750\uff0c\u6211\u9a6c\u4e0a\u6765\u3002\u201d \u3000\u3000\u4f0a\u98ce\u671b\u7740\u8fd9\u4e00\u5bf9\u592b\u59bb\u7684\u80cc\u5f71\u51fa\u795e\uff0c\u601d\u6f6e\u53c8\u4e0d\u80fd\u81ea\u7981\u5730\u56de\u5230\u6c5f\u5357\uff0c\u4ed6\u81ea\u5df1\u90a3\u5728\u82cf\u5dde\u57ce\u91cc\uff0c \u66fe\u7ecf\u548c\u8fd9\u4e2a\u5bb6\u4e00\u6837\u5b89\u9002\uff0c\u606c\u9759\u7684\u5bb6\uff0c\u60f3\u8d77\u4e86\u90a3\u4e00\u6bb5\uff0c\u548c\u8fd9\u5bf9\u592b\u5987\u4e00\u6837\u6e29\u6696\u800c\u6109\u5feb\u7684\u751f\u6d3b\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u957f\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u76ee\u5149\u8f6c\u5230\u7a97\u5916\uff0c\u7a97\u5916\u662f\u4e2a\u5e76\u4e0d\u592a\u5927\u7684\u9662\u5b50\uff0c\u9662\u5b50\u91cc\u4e00\u5ea7\u82b1\u53f0\uff0c\u4e2d\u95f4\u690d\u7740\u4e9b\u828d\u836f\uff0c\u4e24\u65c1\u662f \u5929\u7afa\u814a\u6885\uff0c\u548c\u4e00\u4e9b\u6d77\u68e0\uff0c\u8349\u82b1\uff0c\u56e0\u8010\u4e0d\u7740\u4e25\u51ac\u800c\u51cb\u96f6\u5f97\u53ea\u5269\u67af\u679d\u7684\u679d\u5e72\u3002 \u3000\u3000\u4f46\u662f\u90a3\u5929\u7afa\u5b50\uff0c\u9876\u4e0a\u4ecd\u6709XX\u7684\u7ed3\u5b9e\uff0c\u989c\u8272\u90a3\u4e48\u7ea2\uff0c\u914d\u7740\u7fe0\u8272\u7684\u53f6\u5b50\uff0c\u66f4\u663e\u5f97\u90a3\u4e48\u9c9c \u8273\uff0c\u5728\u8fd9\u9c9c\u82b1\u51cb\u96f6\u5df2\u5c3d\u7684\u5b63\u8282\u91cc\uff0c\u53ea\u6709\u8fd9\u5929\u7afa\u5b50\u4ecd\u50b2\u7136\u4e8e\u897f\u98ce\u91cc\uff0c\u4e00\u679d\u72ec\u76db\u3002 \u3000\u3000\u4eba\u6c38\u8fdc\u65e0\u6cd5\u8131\u79bb\u4ed6\u65e7\u65f6\u7684\u56de\u5fc6\u7684\uff0c\u5373\u4f7f\u4ed6\u80fd\u5b8c\u5168\u65a9\u65ad\u8fc7\u53bb\uff0c\u4f46\u201c\u8fc7\u53bb\u201d\u4ecd\u4f1a\u50cf\u5f71\u5b50\u4f3c\u5730 \u4f9d\u9644\u5728\u4ed6\u540e\u9762\uff0c\u4e00\u6709\u673a\u4f1a\uff0c\u5c31\u4fb5\u5411\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u4f0a\u98ce\u843d\u5bde\u5730\u56de\u8fc7\u5934\uff0c\u4ed6\u51e0\u4e4e\u5df2\u5fd8\u8bb0\u4e86\u8fd9\u5ba4\u4e2d\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8fd8\u53e6\u4e00\u4eba\u5b58\u5728\uff0c\u4f46\u4ed6\u7ec8\u7a76\u56de\u5230 \u73b0\u5b9e\u4e2d\u6765\uff0c\u7ec8\u7a76\u770b\u5230\u4e86\u5979\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5f20\u6ee1\u542b\u7740\u540c\u60c5\u4e0e\u4e86\u89e3\u7684\u7f8e\u4e3d\u7684\u8138\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4f0a\u98ce\u7a81\u7136\u53d1\u89c9\u81ea\u5df1\u975e\u5e38\u9700\u8981\u8fd9\u4efd \u4e86\u89e3\u4e0e\u540c\u60c5\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e00\u52a8\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u4e45\u7ecf\u5fe7\u60a3\uff0c\u5fc3\u4e2d\u7684\u7ffb\u6d8c\uff0c\u5e76\u672a\u5728\u4ed6\u7684\u8138\u5bb9\u4e0a\u8868\u9732\u51fa\u6765\u3002 \u3000\u3000\u9759\u5bc2\uff0c\u4fbf\u5f97\u98ce\u5439\u8fc7\u7684\u58f0\u97f3\uff0c\u90fd\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u98ce\u4e2d\uff0c\u6709\u9662\u4e2d\u814a\u6885\u7684\u6e05\u9999\u6c14\u606f\uff0c\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8427\u59d1\u5a18\u53ef\u559c\u6b22\u6885\u82b1\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u5374\u53c8\u5c55\u989c\u4e00\u7b11\uff0c\u5782\u4e0b\u9888\u53bb\u3002\u6b64\u65f6\u7684\u65e0\u58f0\uff0c\u5df2\u80dc\u5374\u5343\u8a00\u4e07\u8bed\uff01 \u3000\u3000\u4eba\u4eec\u5728\u5bc2\u5bde\u7684\u65f6\u5019\uff0c\u6700\u5bb9\u6613\u63a5\u53d7\u522b\u4eba\u7684\u60c5\u611f\uff0c\u800c\u4f0a\u98ce\u6b64\u523b\u6b63\u662f\u5bc2\u5bde\u7684\u3002 \u3000\u3000\u7a81\u7136\uff0c\u53c8\u6709\u4e00\u8fde\u4e32\u94f6\u73b2\u822c\u7684\u5a07\u7b11\uff0c\u6253\u7834\u4e86\u8fd9\u9759\u5bc2\u3002\u8c37\u6653\u9759\u624b\u4e2d\u6258\u7740\u4e2a\u5927\u5927\u7684\u7ea2\u6728\u76d8\u5b50\u8d70 \u4e86\u8fc7\u6765\uff0c\u4e00\u9762\u7b11\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u4fe9\u4eba\u522b\u5728\u8fd9\u91cc\u53d1\u5446\u4e86\uff0c\u5feb\u5403\u4e9b\u70ed\u7ca5\u6321\u6321\u5bd2\u6c14\u3002\u201d \u3000\u3000\u773c\u6ce2\u4e00\u77ac\uff0c\u5374\u53c8\u201c\u5537\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u8fd9\u4f4d\u5973\u9b54\u5934\uff0c\u600e\u4e48\u8138\u90fd\u7ea2\u4e86\uff0c\u662f\u4ed6\u6b3a\u8d1f\u4e86\u4f60\u662f\u4e0d\u662f\uff01\u201d \u3000\u3000\u8427\u5357\u82f9\u7ad9\u8d77\u6765\u4e00\u987f\u811a\uff0c\u4e0d\u4f9d\u9053\uff1a \u3000\u3000\u201c\u4f60\u518d\u8bf4\uff0c\u770b\u6211\u4e0d\u6495\u70c2\u4f60\u7684\u5634\u3002\u201d \u3000\u3000\u8138\u5374\u8d8a\u53d1\u7ea2\u4e86\uff0c\u76ee\u5149\u7adf\u4e0d\u6562\u53bb\u770b\u4f0a\u98ce\u3002 \u3000\u3000\u7136\u800c\u773c\u89d2\u5374\u53c8\u5728\u6709\u610f\u65e0\u610f\u95f4\uff0c\u779f\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u4f0a\u98ce\u53ea\u89c9\u5f97\u6709\u8ff7\u60d8\uff0c\u5fc3\u91cc\u53c8\u6709\u4e9b\u751c\u751c\u7684\uff0c\u5728\u6b64\u523b\uff0c\u4ed6\u51e0\u4e4e\u5df2\u5168\u7136\u5fd8\u8bb0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u5df2\u5c06\u751f\u547d\u5207\u6210\u4e24\u6bb5\uff0c\u50cf\u86af\u8693\u4e00\u6837\uff0c\u7957\u4fdd\u7559\u7740\u4e00\u6bb5\u5728\u751f\u6d3b\u7740\uff0c\u5728\u8ffd\u9010\u7740\u4e00\u4e16\u4e00\u53ef\u4ee5 \u6cbb\u6108\u81ea\u5df1\u521b\u53e3\u7684\u4e8b\u7269\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u5728\u8fd9\u606c\u9002\u7684\u5bb6\u5ead\u4e2d\u803d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4eab\u53d7\u7740\u4ed6\u5df2\u4e45\u4e45\u672a\u66fe\u4eab\u53d7\u8fc7\u7684\u606c\u9759\u3002 \u3000\u3000\u4e5f\u9886\u7565\u7740\u4ed6\u4e45\u4e45\u672a\u66fe\u9886\u7565\u4e86\u7684\u5c11\u5973\u7684\u773c\u6ce2\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e24\u5929\uff0c\u706b\u795e\u7237\u5bb6\u91cc\u7a81\u7136\u70ed\u95f9\u8d77\u6765\u3002 \u3000\u3000\u8427\u5357\u82f9\u4fbf\u4ece\u56ca\u4e2d\u53d6\u51fa\u4e00\u4e2a\u9762\u5177\u6765\uff0c\u8584\u8584\u7684\u7adf\u662f\u4eba\u76ae\u6240\u5236\u3002\u8fd9\u79cd\u201c\u4eba\u76ae\u9762\u5177\u201d\u5728\u6c5f\u6e56\u4f20\u95fb \u5df2\u4e45\uff0c\u4f46\u4f0a\u98ce\u53ef\u4ece\u6765\u672a\u770b\u513f\u8fc7\uff0c\u6b64\u523b\u4e00\u770b\uff0c\u6bdb\u9aa8\u4e0d\u7981\u609a\u7136\u3002 \u3000\u3000\u90a3\u9762\u5177\u4e0a\u6709\u51e0\u4e2a\u5c0f\u6d1e\uff0c\u60f3\u5fc5\u662f\u7559\u4e0b\u8033\uff0c\u9f3b\uff0c\u76ee\uff0c\u53e3\u7b49\u51e0\u4e2a\u6c14\u5b54\u7684\u5730\u65b9\uff0c\u4f0a\u98ce\u867d\u7136\u5fc5\u987b\u6234 \u4e0a\u8fd9\u79cd\u4eba\u76ae\u6240\u5236\u6210\u7684\u4e1c\u897f\uff0c\u5fc3\u4e2d\u96be\u514d\u6709\u4e9b\u6076\u5fc3\u3002 \u3000\u3000\u4f46\u8427\u5357\u82f9\u4e3a\u4ed6\u6234\u4e0a\u540e\uff0c\u53c8\u82b1\u4e86\u4e9b\u5de5\u592b\uff0c\u5728\u4ed6\u9762\u988a\u548c\u9762\u5177\u4e4b\u95f4\uff0c\u52a0\u4e86\u4e9b\u4e1c\u897f\uff0c\u4ed6\u81ea\u5df1\u5bf9\u955c \u4e00\u7167\uff0c\u679c\u7136\u4e0d\u8ba4\u5f97\u81ea\u5df1\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u5766\u7136\u8d70\u51fa\u5927\u5385\uff0c\u53bb\u548c\u90a3\u4e9b\u5230\u201c\u706b\u795e\u7237\u201d\u5bb6\u4e2d\u6765\u62dc\u8bbf\u7684\u6b66\u6797\u8c6a\u58eb\u89c1\u9762\uff0c\u90a3\u5176\u4e2d\u81ea \u7136\u4e5f\u6709\u4f0a\u98ce\u7684\u7d20\u8bc6\uff0c\u4f46\u8c01\u4e5f\u8ba4\u4e0d\u51fa\u4ed6\u6765\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e48\u591a\u5929\u7684\u76f8\u5904\uff0c\u4f0a\u98ce\u548c\u8427\u5357\u82f9\u4e4b\u95f4\uff0c\u81ea\u7136\u4eb2\u5bc6\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u90fd\u5728\u5947\u602a\uff0c\u8fd9\u7d20\u6765\u51b7\u82e5\u51b0\u971c\u7684\u6f47\u6e58\u5983\u5b50\uff0c\u600e\u5730\u6b64\u523b\u5374\u4f1a\u5bf9\u4e00\u4e2a\u5728\u6b66\u6797\u4e2d\u65e0 \u85c9\u85c9\u4e4b\u540d\u7684\u4eba\u5982\u6b64\u9752\u7750\uff1f \u3000\u3000\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u7edc\u7ece\u4e0d\u7edd\uff0c\u4e00\u5929\u603b\u6709\u5341\u4f59\u4e2a\u5230\u8fd9\u201c\u706b\u795e\u7237\u201d\u5bb6\u91cc\u6765\uff0c\u539f\u6765\u90fd\u662f\u7ecf\u8fc7\u6b64\u95f4\uff0c \u5f80\u7ec8\u5357\u5c71\u53bb\u53c2\u52a0\u90a3\u63a8\u9009\u7ec8\u5357\u638c\u95e8\u7684\u76db\u4f1a\u3002 \u3000\u3000\u6709\u51e0\u4e2a\u548c\u59da\u6e05\u5b87\u53cb\u60c5\u8f83\u6df1\u7684\uff0c\u5c31\u7559\u4e86\u4e0b\u6765\uff0c\u51c6\u5907\u548c\u59da\u6e05\u5b87\u4e00\u9f50\u4e0a\u8def\u3002 \u3000\u3000\u4f46\u6765\u7684\u4eba\u867d\u7136\u591a\uff0c\u5374\u90fd\u662f\u4e9b\u8349\u83bd\u8c6a\u58eb\uff0c\u6b66\u6797\u4e2d\u4e5d\u5927\u5b97\u6d3e\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u5374\u4e00\u4e2a\u4e5f\u672a\u89c1\u3002 \u3000\u3000\u4f0a\u98ce\u5fae\u89c9\u5947\u602a\uff0c\u4f46\u4e5f\u5e76\u672a\u5728\u610f\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u7adf\u4e0d\u518d\u6025\u7740\u4e0a\u7ec8\u5357\u5c71\u53bb\uff0c\u4f46\u4f1a\u671f\u65e5\u8fd1\uff0c\u706b\u795e\u7237\u5374\u5df2\u5728\u68c0\u70b9\u884c\u88c5\uff0c\u51c6\u5907\u52a8\u8eab\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4f0a\u98ce\u4e5f\u53ea\u5f97\u6536\u62fe\u7cbe\u795e\uff0c\u79bb\u5f00\u8fd9\u6e29\u67d4\u4e4b\u4e61\u3002 \u3000\u3000\u4f46\u662f\u8427\u5357\u82f9\u7684\u5029\u5029\u4eba\u5f71\uff0c\u4e5f\u968f\u7740\u8fd9\u6bb5\u65f6\u65e5\u7684\u901d\u53bb\uff0c\u5728\u4f0a\u98ce\u5fc3\u4e2d\u7559\u4e0b\u4e00\u62b9\u6d45\u75d5\uff0c\u75d5\u8ff9\u867d \u6d45\uff0c\u5374\u662f\u6c38\u96be\u78e8\u706d\u7684\u54e9\uff01 \u3000\u3000\u8fd9\u4e00\u6bb5\u65f6\u65e5\u7684\u9017\u7559\uff0c\u867d\u7136\u662f\u6e29\u99a8\u7684\uff0c\u4f46\u4f0a\u98ce\u5374\u987b\u4e3a\u6b64\u4ed8\u51fa\u4ee3\u4ef7\uff1b\u53ea\u662f\u4ed6\u5e94\u5f97\u62a5\u507f\u7684\u65e5 \u5b50\uff0c\u6b64\u523b\u8fd8\u672a\u66fe\u5230\u6765\u5c31\u662f\u4e86\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u98de\u8679\u4e03\u5251>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u98de\u8679\u4e03\u5251 \u3000\u3000\u5929\u8272\u4ecd\u7136\u5f88\u51b7\uff0c\u6ee1\u5730\u4ecd\u6709\u971c\u8ff9\uff01 \u3000\u3000\u4f0a\u98ce\u653e\u773c\u671b\u53bb\uff0c\u524d\u9762\u5c31\u662f\u91cd\u91cd\u53e0\u53e0\u7684\u5c71\u5ce6\uff0c\u4e00\u76f4\u5806\u5230\u4e91\u9704\u3002\u7070\u8272\u7684\u5929\u7a7a\u5f88\u4f4e\uff0c\u91cd\u91cd\u53e0\u53e0 \u7684\u4e91\u5c42\uff0c\u4e00\u76f4\u5782\u5230\u5c71\u8170\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5c31\u662f\u7ec8\u5357\u5c71\u4e86\u3002 \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u5230\u540c\u884c\u7684\u706b\u795e\u7237\u59da\u6e05\u5b87\u592b\u5987\uff0c\u8d70\u5728\u81ea\u5df1\u8eab\u4fa7\u7684\u8427\u5357\u82f9\uff0c\u4ee5\u53ca\u53e6\u5916\u51e0\u4e2a\u540c\u884c \u7684\u6c5f\u6e56\u9053\uff0c\u90fd\u4f3c\u4e4e\u56e0\u4e3a\u76ee\u7684\u5730\u5df2\u8fbe\uff0c\u800c\u7cbe\u795e\u7a81\u7136\u5f00\u59cb\u7115\u53d1\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u65e9\u5c31\u5c06\u9a6c\u5339\u653e\u5728\u957f\u5b89\u57ce\u91cc\uff0c\u6b64\u523b\u65bd\u7136\u884c\u6765\uff0c\u770b\u89c1\u9053\u4e0a\u9887\u591a\u6b66\u6797\u4e2d\u4eba\uff0c\u5927\u591a\u548c\u706b\u795e\u7237 \u8ba4\u5f97\u3002\u770b\u5230\u6f47\u6e58\u5983\u5b50\u548c\u8fa3\u624b\u897f\u65bd\u65f6\uff0c\u5374\u4e0d\u7981\u7741\u5927\u4e86\u773c\u775b\u671d\u5979\u4eec\u6253\u91cf\u7740\u3002 \u3000\u3000\u8427\u5357\u82f9\u8f7b\u8f7b\u5550\u4e86\u4e00\u53e3\uff0c\u5374\u8f6c\u8fc7\u5934\u53bb\u671d\u4f0a\u98ce\u5ae3\u7136\u4e00\u7b11\uff0c\u7b11\u58f0\u672a\u6b47\uff0c\u7a81\u7136\u4e00\u9635\u9a6c\u8e44\u58f0\u6025\u9aa4\u5954 \u6765\uff0c\u7adf\u662f\u7b14\u76f4\u5730\u5bf9\u7740\u4ed6\u4eec\u8fd9\u4e2a\u65b9\u5411\u3002 \u3000\u3000\u4f0a\u98ce\u53cc\u7709\u4e00\u76b1\uff0c\u5fae\u5fae\u4fa7\u8eab\uff0c\u5df2\u6709\u51e0\u5339\u9a6c\u7bad\u4e5f\u4f3c\u5730\u4ece\u4ed6\u4eec\u8eab\u4fa7\u5954\u8fc7\u53bb\uff0c\u98de\u626c\u8d77\u65b0\u6eb6\u7684\u96ea \u6c34\u3002 \u3000\u3000\u8c37\u6653\u9759\u5a07\u9a82\u4e00\u58f0\u3002\u706b\u795e\u7237\u500f\u5730\u62a2\u524d\u4e00\u6b65\uff0c\u5237\u5730\u4e00\u638c\uff0c\u6b63\u5288\u5728\u90a3\u6700\u540e\u4e00\u5339\u9a6c\u7684\u9a6c\u80a1\u4e0a\uff0c\u90a3 \u9a6c\u600e\u7981\u53d7\u5f97\u8d77\uff0c\u60ca\u5636\u4e00\u58f0\uff0c\u4eba\u7acb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9a6c\u4e0a\u4eba\u8eab\u624b\u5374\u4e0d\u5f31\uff0c\u4e00\u5e26\u9a6c\u978d\uff0c\u5c06\u53d7\u60ca\u7684\u9a6c\u8f6c\u4e86\u4e2a\u5708\u5b50\uff0c\u4e24\u6761\u817f\u751f\u4e86\u6839\u4f3c\u5730\u631f\u5728\u9a6c\u978d \u4e0a\uff0c\u76ae\u97ad\u4e00\u6325\uff0c\u53e3\u4e2d\u6012\u53f1\u9053\uff1a \u3000\u3000\u201c\u6740\u80da\uff01\u201d \u3000\u3000\u97ad\u68a2\u4e00\u8f6c\uff0c\u5237\u5730\uff0c\u671d\u59da\u6e05\u5b87\u6253\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u706b\u795e\u7237\u6d53\u7709\u4e00\u7acb\uff0c\u51b7\u7b11\u58f0\u4e2d\uff0c\u811a\u6b65\u4e00\u8f6c\uff0c\u7adf\u4ece\u97ad\u5f71\u4e2d\u62a2\u524d\u4e24\u6b65\uff0c\u94c1\u638c\u4e00\u626c\uff0c\u53c8\u5207\u5728\u90a3\u5339 \u9a6c\u7684\u8116\u5b50\u4e0a\uff0c\u8fd9\u4e00\u638c\u66f4\u662f\u7528\u4e86\u516b\u6210\u771f\u529b\uff0c\u8fd9\u5339\u755c\u7272\u518d\u4e5f\u7981\u53d7\u4e0d\u4f4f\uff0c\u4e00\u4e2a\u98a0\u6c9b\uff0c\u88ab\u9a6c\u4e0a\u4eba\u7684\u5927 \u529b\u4e00\u538b\uff0c\u7adf\u201c\u5657\u201d\u5730\u5012\u5728\u5730\u4e0a\uff0c\u9a6c\u5634\u55b7\u51fa\u767d\u6cab\u6765\u3002 \u3000\u3000\u90a3\u9a6c\u4e0a\u4eba\u8eab\u624b\u6781\u4e3a\u77eb\u5065\uff0c\u6b64\u523b\u5df2\u817e\u8eab\u800c\u8d77\uff0c\u53e3\u4e2d\u6012\u559d\u9053\uff1a \u3000\u3000\u201c\u4e0d\u957f\u773c\u775b\u7684\u6740\u80da\uff01\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u5417\uff1f\u201d \u3000\u3000\u811a\u5c16\u4e00\u70b9\u9a6c\u978d\uff0c\u5237\uff0c\u5237\uff0c\u53c8\u662f\u4e24\u9a6c\u97ad\uff0c\u5e26\u7740\u547c\u54e8\u4e4b\u58f0\uff0c\u6325\u5411\u706b\u795e\u7237\u59da\u6e05\u5b87\u3002 \u3000\u3000\u59da\u6e05\u5b87\u4e3a\u7740\u5a07\u59bb\u7684\u4e00\u58f0\u8f7b\u55d4\uff0c\u5c31\u52a8\u624b\u62e6\u4eba\u6253\u9a6c\uff0c\u5df2\u662f\u6781\u4e3a\u9c81\u83bd\uff1b\u8fd9\u4eba\u5374\u6bd4\u4ed6\u66f4\u83bd\u649e\uff0c\u6839 \u672c\u4e0d\u8003\u8651\u5bf9\u65b9\u662f\u4ec0\u4e48\u4eba\u7269\uff0c\u5c31\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u6325\u97ad\uff0c\u8ddf\u59da\u6e05\u5b87\u540c\u6765\u7684\uff0c\u4e5f\u90fd\u662f\u5728\u6b66\u6797\u4e2d\u6210\u540d\u7acb\u4e07\u7684\u8c6a\u58eb\uff0c\u4e5f\u7eb7\u7eb7\u559d\u9a82\u7740\u6d8c\u4e86\u4e0a \u6765\uff0c\u800c\u548c\u8fd9\u6c49\u5b50\u540c\u884c\u7684\u53e6\u51e0\u5339\u9a6c\uff0c\u6b64\u523b\u4e5f\u515c\u4e86\u56de\u5934\u3002 \u3000\u3000\u4f0a\u98ce\u51b7\u773c\u65c1\u89c2\uff0c\u77e5\u9053\u773c\u4e0b\u5c31\u662f\u4e00\u573a\u6df7\u6218\u3002 \u3000\u3000\u90a3\u4eba\u9a6c\u97ad\u6325\u4e0b\uff0c\u4e00\u8fde\u4e24\u97ad\uff0c\u5feb\uff0c\u51c6\uff0c\u7a33\uff0c\u72e0\uff0c\u62bd\u5411\u59da\u6e05\u5b87\u7684\u5934\u9762\u3002 \u3000\u3000\u59da\u6e05\u5b87\u4e5f\u81ea\u5927\u6012\uff0c\u4e0d\u907f\u53cd\u8fce\uff0c\u864e\u8170\u4e00\u632b\uff0c\u53cd\u8155\u4e0b\u6284\uff0c\u53bb\u6284\u90a3\u4eba\u7684\u97ad\u68a2\uff0c\u65f6\u95f4\uff0c\u90e8\u4f4d\uff0c\u4ea6 \u662f\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\u3002 \u3000\u3000\u90a3\u4eba\u4f3c\u4e4e\u4e5f\u5fae\u5fae\u5730\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u601d\u4e00\u52a8\u4e4b\u4e0b\uff0c\u97ad\u68a2\u5df2\u88ab\u59da\u6e05\u5b87\u6284\u5728\u638c\u4e2d\uff0c\u66b4\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u7ed9\u6211\u8eba\u4e0b\uff01\u201d \u3000\u3000\u638c\u4e2d\u4e00\u8f83\u52b2\uff0c\u4e24\u4eba\u7adf\u90fd\u9a6c\u6b65\u6c88\u5b9e\uff0c\u672a\u88ab\u5bf9\u65b9\u7275\u52a8\u534a\u6b65\u3002 \u3000\u3000\u4f0a\u98ce\u4e0b\u7981\u5947\u602a\uff1a\u201c\u90a3\u91cc\u6765\u7684\u5982\u6b64\u9ad8\u624b\uff01\u201d\u53ea\u56e0\u201c\u706b\u795e\u7237\u201d\u59da\u6e05\u5b87\u5728\u6b66\u6797\u4e2d\u5df2\u4eab\u76db\u540d\uff0c\u90a3 \u4eba\u5374\u9762\u751f\u5f97\u5f88\uff0c\u800c\u6b64\u523b\u4e24\u4eba\u4e00\u8f83\u52b2\uff0c\u7adf\u662f\u4e0d\u5206\u5e73\u624b\uff0c\u662f\u4ee5\u4f0a\u98ce\u5fc3\u4e2d\u6697\u5947\u3002 \u3000\u3000\u6b64\u523b\u53e6\u51e0\u5339\u9a6c\u4e0a\uff0c\u5df2\u63a0\u4e0b\u4e24\u4eba\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u8eab\u5f62\u5982\u71d5\uff0c\u5feb\u5982\u7535\u706b\u4e00\u95ea\uff0c\u5df2\u81ea\u63a0\u5230\u8fd1\u524d\uff0c \u4e3e\u638c\u4e00\u5207\uff0c\u90a3\u88ab\u59da\u6e05\u5b87\u7b49\u4e24\u4eba\u626f\u76f4\u4e86\u7684\u9a6c\u97ad\uff0c\u88ab\u4ed6\u8fd9\u4e00\u5207\uff0c\u7adf\u5e94\u624b\u4e2d\u5206\u4e3a\u4e8c\uff0c\u5b9b\u5982\u5229\u5203\u6240 \u65ad\u3002 \u3000\u3000\u8fa3\u624b\u897f\u65bd\u51b7\u7b11\u4e00\u58f0\uff0c\u500f\u7136\u7ea4\u624b\u5fae\u626c\uff0c\u98d8\u7136\u51e0\u7f15\u5c16\u98ce\uff0c\u88ad\u5411\u8fd9\u4e24\u4e2a\u9a91\u9a6c\u7684\u6c49\u5b50\uff0c\u53e3\u4e2d\u5a07\u559d \u9053\uff1a \u3000\u3000\u201c\u8eba\u4e0b\uff01\u201d \u3000\u3000\u90a3\u77e5\u7acb\u638c\u5207\u97ad\u90a3\u6c49\u5b50\u624b\u638c\u4e00\u7ffb\uff0c\u55d6\u5730\uff0c\u5288\u51fa\u4e00\u80a1\u638c\u98ce\uff0c\u7adf\u5c06\u8c37\u6653\u9759\u53d1\u51fa\u7684\u516d\u70b9\u5bd2\u5149\uff0c\u626b \u843d\u4e86\u56db\u70b9\uff0c\u53e6\u5916\u90a3\u6c49\u5b50\u4e34\u5371\u4e0d\u4e71\uff0c\u638c\u4e2d\u534a\u622a\u9a6c\u97ad\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u4e5f\u81ea\u5c06\u88ad\u5411\u4ed6\u7684\u6697\u5668\u626b\u843d\u3002 \u3000\u3000\u8bf4\u6765\u8bdd\u957f\uff0c\u7136\u800c\u8fd9\u51e0\u4e2a\u4eba\u51fa\u624b\uff0c\u90fd\u5728\u6781\u5feb\u7684\u4e00\u77ac\u95f4\u5b8c\u6210\uff0c\u800c\u6b64\u523b\u5f7c\u6b64\u5fc3\u4e2d\u4e5f\u90fd\u6709\u6570\uff0c\u77e5 \u9053\u81ea\u5df1\u9047\u7740\u7684\u4e0d\u662f\u6cdb\u6cdb\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u53cc\u65b9\u53cd\u800c\u4e0d\u6562\u5192\u7136\u51fa\u624b\u3002 \u3000\u3000\u90a3\u638c\u4e0a\u7adf\u6709\u5288\u7a7a\u638c\u529b\u7684\u7626\u957f\u6c49\u5b50\uff0c\u76ee\u5149\u70af\u7136\u56db\u626b\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u6211\u5144\u53f0\u548c\u670b\u53cb\u4eec\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u5404\u8d70\u5404\u7684\u9053\uff0c\u670b\u53cb\u9aa4\u4e0b\u6bd2\u624b\uff0c\u662f\u51b2\u7740\u4ec0\u4e48\uff1f\u6211\u6bdb\u6587\u5947 \u5012\u8981\u9886\u6559\u9886\u6559\u3002\u201d \u3000\u3000\u8c37\u6653\u9759\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u4f60\u8d70\u8def\u96be\u9053\u6ca1\u957f\u7740\u773c\u775b\uff0c\u8981\u662f\u8d70\u8def\u8be5\u50cf\u4f60\u4eec\u8fd9\u6837\u6a2a\u51b2\u76f4\u649e\u7684\uff0c\u90a3\u5e72\u8106\u522b\u4eba\u5168\u90fd\u522b\u8d70 \u4e86\uff0c\u4f60\u4eec\u662f\u4ec0\u4e48\u89d2\u8272\uff1f\u4ed7\u6043\u7740\u4ec0\u4e48\uff0c\u8fd9\u4e48\u53d1\u6a2a\uff1f\u201d \u3000\u3000\u6bdb\u6587\u5947\u6765\u81ea\u957f\u767d\uff0c\u7ec8\u65e5\u9a70\u9a8b\u767d\u5c71\u9ed1\u6c34\u95f4\uff0c\u6839\u672c\u4e0d\u77e5\u653e\u9a6c\u7f13\u884c\u8fd9\u56de\u4e8b\u3002 \u3000\u3000\u6b64\u523b\u6115\u4e86\u4e00\u4e0b\uff0c\u4f46\u770b\u5230\u8c37\u6653\u9759\u8138\u4e0a\u7684\u795e\u6001\uff0c\u4ef0\u5929\u957f\u7b11\u4e00\u58f0\uff0c\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u6211\u6bdb\u6587\u5947\u521d\u6765\u4e2d\u539f\uff0c\u8fd9\u6b21\u5012\u8ba9\u6211\u5f00\u4e86\u773c\u754c\uff0c\u6765\u4e2d\u539f\u7684\u6b66\u6797\u9053\uff0c\u5168\u662f\u5a18\u513f\u4eec\u5728 \u53d1\u6a2a\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7adf\u662f\u6ee1\u53e3\u4e1c\u5317\u53e3\u97f3\u3002 \u3000\u3000\u4ed6\u8fd9\u8bdd\u4e00\u51fa\uff0c\u7adf\u628a\u4e2d\u539f\u6b66\u6797\u9053\u5168\u9a82\u4e0a\u4e86\uff0c\u53ef\u72af\u4e86\u4f17\u6012\uff0c\u7acb\u523b\u8fde\u8eab\u4e0d\u5173\u5df1\u7684\u4eba\uff0c\u90fd\u7eb7\u7eb7\u53f1 \u9a82\u8d77\u6765\u3002 \u3000\u3000\u6bdb\u6587\u5947\u51b7\u7b11\u8fde\u8fde\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\u6781\uff01\u597d\u6781\uff01\u6211\u6bdb\u6587\u5947\u9525\u7136\u53ea\u662f\u56db\u4eba\uff0c\u4f46\u5374\u6709\u5174\u8da3\u63a5\u63a5\u4e2d\u539f\u6b66\u6797\u9053\u7684\u9ad8\u62db\uff0c\u6765\u6765\uff01\u5404 \u4f4d\u662f\u8981\u4f17\u6bb4\uff0c\u662f\u8981\u72ec\u6597\uff1f\u53ea\u7ba1\u62db\u547c\u4e00\u58f0\uff0c\u6211\u4eec\u54e5\u513f\u56db\u4e2a\u7ecd\u63a5\u7740\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u8bf4\u7f62\u6697\u81ea\u51b7\u7b11\uff0c\u5927\u6709\u76ee\u4e2d\u65e0\u4eba\u4e4b\u610f\u3002 \u3000\u3000\u706b\u795e\u7237\u59da\u6e05\u5b87\u53cc\u7709\u4e00\u7acb\uff0c\u65b9\u81ea\u53d1\u8bdd\uff0c\u8c37\u6653\u9759\u5374\u53c8\u62a2\u7740\u9053\uff1a \u3000\u3000\u201c\u5537\uff0c\u8fd9\u662f\u4ece\u90a3\u91cc\u94bb\u51fa\u6765\u7684\u56db\u4e2a\u91ce\u79cd\uff0c\u6211\u59d3\u8c37\u7684\u8d70\u5357\u5230\u5317\uff0c\u8fd8\u6ca1\u770b\u5230\u8fd9\u4e48\u6a2a\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u53e3\u89d2\u4e4b\u4e0b\uff0c\u8a00\u8bcd\u76ca\u6781\u950b\u5229\u3002 \u3000\u3000\u4f0a\u98ce\u81ea\u6043\u81ea\u5df1\u7684\u8eab\u5206\uff0c\u662f\u4ee5\u53ea\u662f\u65c1\u89c2\u7740\uff0c\u65e2\u672a\u51fa\u6765\uff0c\u4e5f\u672a\u591a\u5634\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u770b\u5230\u8fd9\u98de\u9a6c\u800c\u6765\u7684\u56db\u4eba\uff0c\u4ff1\u662f\u4e24\u773c\u795e\u5149\u6ee1\u8db3\uff0c\u8eab\u624b\u77eb\u5065\uff0c\u5c24\u5176\u8fd9\u81ea\u79f0\u201c\u6bdb\u6587 \u5947\u201d\u7684\u4e00\u4eba\uff0c\u5185\u529f\u706b\u5019\u66f4\u662f\u6781\u5176\u6df1\u6e5b\uff0c\u638c\u4e0a\u7684\u529f\u529b\uff0c\u6bd4\u4e4b\u201c\u6731\u7802\u638c\u201d\u5c24\u5927\u541b\uff0c\u8fd8\u8981\u9ad8\u51fa\u751a \u591a\u3002 \u3000\u3000\u4ed6\u5fc3\u77e5\u8fd9\u56db\u4eba\u5fc5\u5b9a\u4e0d\u662f\u7b49\u95f2\u4e4b\u8f88\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6211\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u4e3a\u7740\u8fd9\u4e9b\u6ca1\u6765\u7531\u7684\u4e8b\u52a8\u624b\u3002\u201d \u3000\u3000\u9042\u8d70\u524d\u51e0\u6b65\uff0c\u671d\u7740\u90a3\u81ea\u79f0\u6bdb\u6587\u5947\u7684\u6c49\u5b50\u4e00\u62f1\u624b\uff0c\u65b9\u60f3\u529d\u89e3\u51e0\u53e5\u3002 \u3000\u3000\u90a3\u77e5\u6bdb\u6587\u5947\u4e00\u773c\u77a5\u89c1\u4ed6\uff0c\u8138\u4e0a\u795e\u8272\u7a81\u5730\u5927\u53d8\uff0c\u624b\u6307\u6307\u7740\u4ed6\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u4e3a\u4ed6\u8fd9\u79cd\u795e\u8272\u6240\u60ca\uff0c\u832b\u7136\u540e\u9000\u4e00\u6b65\uff0c\u773c\u89d2\u5fae\u626b\uff0c\u770b\u5230\u53e6\u5916\u90a3\u4e24\u4eba\uff0c\u4e5f\u662f\u5e26\u7740\u4e00 \u8138\u6fc0\u52a8\u7684\u795e\u8272\uff0c\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u5c31\u8fde\u90a3\u672c\u6765\u5750\u5728\u9a6c\u4e0a\u672a\u52a8\u7684\u4e00\u4eba\uff0c\u4e5f\u63a0\u4e86\u4e0b\u6765\uff0c\u773c\u775b\u76f4\u6115\u6115\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u975e\u4f46\u4f0a\u98ce\u5fc3\u4e2d\u8be7\u6115\u4e0d\u89e3\uff0c\u59da\u6e05\u5b87\uff0c\u8c37\u6653\u6094\uff0c\u8427\u5357\u82f9\u4e5f\u662f\u4e8b\u51fa\u610f\u5916\uff0c\u4e0d\u77e5\u9053\u8fd9\u56db \u4e2a\u9a91\u58eb\uff0c\u7a76\u7adf\u5728\u51fa\u4ec0\u4e48\u82b1\u6837\u3002 \u3000\u3000\u826f\u4e45\uff0c\u6bdb\u6587\u5947\u624d\u50cf\u4ece\u6781\u5927\u7684\u6fc0\u52a8\u4e0b\uff0c\u56de\u590d\u4e86\u8fc7\u6765\uff0c\u98a4\u6296\u7684\u58f0\u97f3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u4f60\u8fd9\u53ef\u662f\u4e0d\u5bf9\uff0c\u65e2\u7136\u597d\u597d\u5730\u6d3b\u7740\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u8ba9\u5927\u5925\u5144\u5f1f\uff0c\u4e3a\u4f60\u7740\u6025.\u4e09\u5f1f\uff0c \u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4eec\u591a\u60f3\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u603b\u662f\u56de\u907f\u7740\u6211\u4eec\uff0c\u4e5f\u4e0d\u6084\u4e2a\u53e3\u8baf\u6765\uff1f\uff0e\u4e09\u5f1f\uff01 \u4f60\u6211\u5144\u5f1f\u5728\u4e00\u5757\u957f\u5927\uff0c\u5728\u4e00\u5757\u513f\u5b66\u529f\u592b\uff0c\u96be\u9053\u4e0d\u6bd4\u4eb2\u751f\u7684\u9aa8\u8089\u8fd8\u4eb2\u8fd1\uff0c\u6709\u4ec0\u4e48\u8bdd\u4e0d\u80fd\u660e\u8bf4 \u7684\uff1f\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u4ed6\u7adf\u6fc0\u52a8\u5f97\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u8fde\u8fde\u957f\u53f9\u7740\uff0c\u76ee\u4e2d\u7adf\u4f3c\u6709\u6676\u83b9\u7684\u6cea\u5149\u3002 \u3000\u3000\u6700\u540e\u4ece\u9a6c\u4e0a\u63a0\u4e0b\u7684\u8001\u8005\uff0c\u4e5f\u5e9f\u7136\u53f9\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u4f60\u867d\u7136\u5e9f\u4e86\u4f60\u5927\u54e5\u6211\u4e00\u6761\u817f\uff0c\u53ef\u662f\u4f60\u662f\u6211\u4ece\u5c0f\u5e26\u5927\u7684\u2026\u2026\u6211\u5c31\u8ddf\u4f60\u7684\u4eb2\u5144\u5f1f\u4e00 \u6837\uff0c\u522b\u8bf4\u4f60\u65e0\u610f\u95f4\u4f24\u4e86\u6211\u7684\u817f\uff0c\u5c31\u7b97\u4f60\u628a\u6211\u7684\u4e24\u6761\u817f\u90fd\u5207\u4e0b\u6765\uff0c\u6211\u4e5f\u4e0d\u4f1a\u602a\u4f60\uff0c\u4f60\u4e3a\u4ec0 \u4e48\u2026\u2026\u201d \u3000\u3000\u8fd9\u5728\u56db\u4eba\u4e2d\u5e74\u7eaa\u6700\u957f\u7684\u8001\u8005\uff0c\u7adf\u4e5f\u6fc0\u52a8\u5f97\u8bf4\u4e0d\u4e0b\u8bdd\u6765\uff0c\u7f13\u7f13\u8d70\u5411\u4f0a\u98ce\uff0c\u4e24\u817f\u679c\u7136\u4e00\u8ddb\u4e00 \u8ddb\u5730\uff0c\u662f\u4e2a\u8ddb\u5b50\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8fd9\u51e0\u58f0\u201c\u4e09\u5f1f\u201d\u4e00\u558a\uff0c\u8fd9\u51e0\u53e5\u5145\u6ee1\u4e86\u60c5\u611f\u7684\u8bdd\u4e00\u8bf4\uff0c\u4f0a\u98ce\u53ef\u66f4\u6115\u4f4f\u4e86\uff0c\u770b\u7740\u8fd9\u8ddb \u8db3\u8001\u4eba\u5411\u81ea\u5df1\u884c\u6765\uff0c\u7adf\u4e0d\u77e5\u600e\u751f\u662f\u597d\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u76ee\u5149\u8f7b\u8f7b\u5730\u5728\u4f0a\u98ce\u8138\u4e0a\u6ed1\u52a8\u7740\uff0c\u4e00\u9762\u4ee5\u60b2\u6006\u7684\u58f0\u8c03\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff0c\u8fd9\u4e9b\u5e74\u6765\u4f60\u8dd1\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u600e\u4e48\u53d8\u5f97\u8fd9\u4e48\u9ed1\u7626\uff1f\u5509\uff01\u4e09\u5f1f\uff01\u4f60\u2026\u2026\u4f60\u54e5\u54e5\u6211\u5df2 \u7ecf\u8001\u4e86\uff0c\u817f\u4e5f\u4e0d\u7ba1\u7528\u4e86\uff0c\u8981\u4e0d\u662f\u62b1\u7740\u4e00\u70b9\u5e0c\u671b\u6765\u627e\u4f60\uff0c\u6211\u53ef\u771f\u4e0d\u60f3\u518d\u4e0b\u957f\u767d\u5c71\u4e00\u6b65\u3002\u4e09\u5f1f\uff01 \u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u5148\u8ddf\u6211\u4eec\u56de\u53bb\uff0c\u4f60\u8981\u4ec0\u4e48\uff0c\u4f60\u5927\u54e5\u6211\u8d1f\u8d23\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u53f9\u606f\u7740\uff0c\u4e00\u9762\u8bf4\u7740\uff0c\u58f0\u8c03\u6ee1\u542b\u60c5\u611f\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u77e5\u6240\u63aa\uff0c\u5450\u5450\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u8c37\u6653\u9759\u4e5f\u95f7\u5f97\u5934\u76ae\u53d1\u70b8\uff0c\u6b64\u523b\u4e00\u63a0\u800c\u6765\uff0c\u6321\u5728\u8fd9\u8ddb\u8db3\u8001\u4eba\u7684\u524d\u9762\uff0c\u5a07\u53f1\u9053\uff1a \u3000\u3000\u201c\u5582\uff01\u4f60\u75af\u4e86\u5440\uff01\u8c01\u662f\u4f60\u7684\u4e09\u5f1f\uff0c\u4f60\u770b\u6e05\u695a\u70b9\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8fd9\u8ddb\u8db3\u8001\u4eba\u672c\u6765\u59d4\u987f\u7684\u8eab\u5f62\uff0c\u6b64\u523b\u500f\u7136\u66b4\u957f\uff0c\u76ee\u4e2d\u4e5f\u5c04\u51fa\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u7684\u7cbe\u5149\uff0c\u72e0\u72e0\u77aa \u5728\u8c37\u6653\u9759\u7684\u8138\u4e0a\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u5a46\u5a18\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u4f60\u6562\u6765\u7ba1\u8001\u592b\u6211\u7684\u4e8b\uff01\u201d \u3000\u3000\u4ed6\u7a0d\u5fae\u505c\u987f\u4e00\u4e0b\uff0c\u5f77\u4f5b\u60f3\u8d77\u4ec0\u4e48\uff0c\u7a81\u7136\u53c8\u5927\u6012\u8d77\u6765\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u5c31\u662f\u4f60\uff0c\u5c31\u662f\u4f60\u8fd9\u53ea\u5c0f\u72d0\u72f8\uff0c\u628a\u6211\u4e09\u5f1f\u5f15\u4e0b\u5c71\u7684\uff01\u201d \u3000\u3000\u4ed6\u56de\u5934\u4e00\u58f0\u66b4\u559d\uff1a \u3000\u3000\u201c\u8001\u4e8c\uff01\u8001\u56db\uff01\u8ddf\u6211\u628a\u8fd9\u5a18\u513f\u4eec\u6293\u4e0b\u6765\uff01\u201d \u3000\u3000\u8bdd\u58f0\u4e00\u843d\uff0c\u6bdb\u6587\u5947\u53ca\u53e6\u5916\u4e24\u6761\u6c49\u5b50\u8eab\u5f62\u4e00\u52a8\uff0c\u5df2\u63a0\u4e86\u4e0a\u6765\uff0c\u671d\u8c37\u6653\u9759\u56db\u65b9\u4e00\u7ad9\uff0c\u624b\u8155\u4e00 \u7ffb\uff0c\u81ea\u8170\u95f4\u4f38\u51fa\u4e00\u7269\uff0c\u8fce\u98ce\u4e00\u6296\uff0c\u4f38\u5f97\u7b14\u76f4\uff0c\u7adf\u662f\u4e00\u67c4\u767e\u7ec3\u7cbe\u94a2\u6240\u5236\uff0c\u53ef\u67d4\u53ef\u521a\u7684\u957f\u5251\u3002 \u3000\u3000\u706b\u795e\u7237\u770b\u5230\u5a07\u59bb\u53d7\u8fb1\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u63a2\u56ca\u53d6\u51fa\u4e00\u7269\uff0c\u626c\u624b\u5411\u6bdb\u6587\u5947\u6253\u53bb\uff0c\u8eab\u5f62\u4e5f\u968f\u5373\u63a0\u4e86 \u4e0a\u6765\uff0c\u638c\u51fa\u5982\u98ce\uff0c\u76f4\u53d6\u90a3\u8ddb\u8db3\u8001\u4eba\u3002 \u3000\u3000\u6bdb\u6587\u5947\u542c\u5230\u80cc\u540e\u98ce\u58f0\uff0c\u77e5\u9053\u6709\u6697\u5668\u88ad\u6765\uff0c\u8eab\u5f62\u4e00\u626d\uff0c\u957f\u5251\u6392\u51fa\u4e00\u9053\u5251\u5f71\uff0c\u62a4\u4f4f\u5168\u8eab\u3002 \u3000\u3000\u90a3\u77e5\u706b\u795e\u7237\u59da\u6e05\u5b87\u7684\u706b\u836f\u6697\u5668\u72ec\u6b65\u6b66\u6797\uff0c\u65b9\u624d\u53d1\u51fa\u7684\uff0c\u6b63\u662f\u4ed6\u4ed7\u4ee5\u6210\u540d\u7684\u6697\u5668\u4e4b\u4e00\uff0c \u201c\u4e94\u96f7\u73e0\u201d\uff0c\u7a0d\u4e00\u6cbe\u7740\u5251\u5c16\uff0c\u4fbf\u81ea\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u70b8\u4e86\u5f00\u6765\uff0c\u9752\u84dd\u8272\u7684\u706b\u7130\uff0c\u987a\u7740\u5251\u8eab\u70e7\u4e86 \u4e0b\u53bb\u3002 \u3000\u3000\u6bdb\u6587\u5947\u8fd9\u4e0b\u53ef\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u731b\u6325\u957f\u5251\uff0c\u60f3\u5c06\u706b\u7130\u7529\u843d\uff0c\u90a3\u77e5\u90a3\u706b\u7130\u5374\u8d8a\u70e7\u8d8a\u65fa\uff0c\u773c\u770b\u5c31 \u8981\u70e7\u4e0a\u4ed6\u7684\u624b\u80cc\uff0c\u4ed6\u60c5\u6025\u4e4b\u4e0b\uff0c\u6765\u4e0d\u53ca\u591a\u601d\u7d22\uff0c\u5237\u5730\uff0c\u5c06\u638c\u4e2d\u5251\u671d\u5730\u4e0a\u76f4\u7529\u51fa\u53bb\uff0c\u4e09\u5c3a\u591a\u957f \u7684\u5251\u957f\uff0c\u7adf\u5b8c\u5168\u6ca1\u5165\u65b0\u6eb6\u7684\u96ea\u5730\u91cc\uff0c\u53ea\u7559\u4e0b\u4e09\u5bf8\u5251\u67c4\uff0c\u9732\u4e8e\u5730\u9762\u3002 \u3000\u3000\u90a3\u8fb9\u59da\u6e05\u5b87\u5374\u60ca\u547c\u4e00\u58f0\uff0c\u8eab\u5f62\u671d\u540e\u5012\u7eb5\u516b\u5c3a\uff0c\u7830\u5730\uff0c\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u539f\u6765\u4ed6\u65b9\u624d\u4e24\u638c\u62a2\u51fa\uff0c\u90a3\u8ddb\u8db3\u8001\u4eba\u7adf\u4e0d\u907f\u4e0d\u95ea\uff0c\u786c\u751f\u751f\u63a5\u4e86\u4ed6\u8fd9\u4e00\u638c\uff0c\u4e24\u4eba\u5bf9\u638c\u4e4b\u4e0b\uff0c \u59da\u6e05\u5b87\u7adf\u88ab\u9707\u5f97\u76f4\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8c37\u6653\u9759\u5a07\u547c\u4e00\u58f0\uff0c\u60f3\u63a0\u8fc7\u53bb\uff0c\u4f46\u9762\u524d\u5bd2\u5149\u4e71\u98a4\uff0c\u5df2\u6709\u4e00\u4eba\u6321\u7740\u5979\u7684\u53bb\u8def\uff0c\u53e6\u5916\u4e00\u4e9b\u6b66\u6797 \u8c6a\u58eb\uff0c\u4ff1\u90fd\u5927\u54d7\uff0c\u6709\u7684\u8dd1\u8fc7\u53bb\u67e5\u770b\u201c\u706b\u795e\u7237\u201d\u7684\u4f24\u52bf\uff0c\u6709\u4e9b\u4eba\u5219\u5728\u53f1\u9a82\u7740\uff0c\u4f46\u5927\u5bb6\u5374\u5168\u4e0d\u77e5 \u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u8bc6\u8d27\u7684\u4eba\u770b\u5230\u8fd9\u8ddb\u8db3\u8001\u4eba\u7684\u529f\u529b\uff0c\u5374\u5728\u6697\u6697\u6628\u820c\u3002 \u3000\u3000\u8427\u5357\u82f9\u59cb\u7ec8\u672a\u53d1\u4e00\u8a00\uff0c\u6b64\u523b\u770b\u5230\u60c5\u51b5\u6df7\u4e71\uff0c\u65b9\u8981\u63a0\u4e0a\u53bb\uff0c\u90a3\u8ddb\u8db3\u8001\u4eba\uff0c\u5374\u84e6\u5730\u66b4\u559d\u4e00 \u58f0\uff0c\u96c4\u6d51\u9ad8\u4ea2\u7684\u58f0\u97f3\uff0c\u538b\u4e0b\u4e86\u6df7\u4e71\u53f1\u9a82\u7684\u58f0\u97f3\uff0c\u9707\u5f97\u6bcf\u4e2a\u4eba\u7684\u8033\u6735\uff0c\u4e0d\u4f4f\u5730\u55e1\u7136\u4f5c\u54cd\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u70af\u7136\u4e00\u626b\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u98de\u8679\u5251\u534e\u54c1\u5947\uff0c\u5728\u6b64\u4e86\u65ad\u5bb6\u52a1\u4e8b\uff0c\u5404\u4f4d\u670b\u53cb\u6b64\u65f6\u82e5\u4e00\u4f38\u624b\uff0c\u4fbf\u662f\u4e0e\u6211\u957f\u767d\u6d3e\u4e3a \u654c\uff0c\u5949\u529d\u5404\u4f4d\uff0c\u8fd8\u662f\u8896\u624b\u4e3a\u5999\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8bdd\u8bf4\u5f97\u53ef\u7b97\u662f\u72c2\u50b2\u5df2\u6781\uff01\u4f46\u4ed6\u8fd9\u201c\u98de\u8679\u5251\u534e\u54c1\u5947\u201d\u516d\u5b57\u4e00\u51fa\uff0c\u7adf\u65e0\u4eba\u518d\u5bf9\u4ed6\u8fd9\u79cd\u72c2 \u50b2\u7684\u8bed\u6c14\uff0c\u9732\u51fa\u4e0d\u5fff\u4e4b\u8272\u3002 \u3000\u3000\u539f\u6765\u8fd9\u98de\u8679\u5251\u534e\u54c1\u5947\uff0c\u5374\u6b63\u662f\u6b66\u6797\u4e5d\u5927\u95e8\u6d3e\u4e4b\u4e00\u2014\u2014\u957f\u767d\u6d3e\u7684\u638c\u95e8\uff0c\u957f\u767d\u98de\u8679\u4e03\u5251\u4e4b \u9996\u3002\u6614\u5e74\u4ed6\u4e5f\u66fe\u5728\u4e2d\u539f\u6570\u73b0\u4fa0\u8e2a\uff0c\u5a01\u540d\u9887\u76db\uff0c\u53ea\u662f\u8fd1\u5e74\u4e45\u672a\u9732\u9762\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u8ddb\u8db3\u8001\u4eba\u7adf \u662f\u957f\u767d\u638c\u95e8\u3002 \u3000\u3000\u65c1\u89c2\u7684\u591a\u662f\u8349\u83bd\u8c6a\u58eb\uff0c\u867d\u4e5f\u6709\u4e9b\u6210\u540d\u7acb\u4e07\u7684\u4eba\u7269\uff0c\u4f46\u548c\u4ed6\u8fd9\u79cd\u4e00\u6d3e\u638c\u95e8\u4eba\u7684\u8eab\u4efd\u4e00\u6bd4\uff0c \u53ef\u5c31\u90fd\u5dee\u5f97\u592a\u8fdc\u3002 \u3000\u3000\u662f\u4ee5\u5927\u5bb6\u5168\u90fd\u9707\u4f4f\u4e86\uff0c\u7eb7\u4e71\u7684\u53f1\u9a82\u58f0\uff0c\u4e5f\u7acb\u523b\u9759\u4e86\u4e0b\u6765\uff0c\u518d\u65e0\u4e00\u4eba\u559d\u9a82\u3002 \u3000\u3000\u534e\u54c1\u5947\u76ee\u5149\u56db\u626b\uff0c\u518d\u8f6c\u56de\u8138\u6765\uff0c\u770b\u5230\u4ed6\u516d\u5f1f\u9f9a\u5929\u5947\u6b63\u5728\u7f20\u6597\uff0c\u4f46\u4ed6\u5374\u4e0d\u53bb\u7ba1\u4ed6\uff0c\u76ee\u5149\u4e00 \u8f6c\uff0c\u8ff3\u81ea\u8f6c\u5230\u4ed6\u81ea\u8ba4\u4e3a\u518d\u4e5f\u4e0d\u4f1a\u8ba4\u9519\u7684\uff0c\u90a3\u4e00\u522b\u7ecf\u5e74\uff0c\u97f3\u8baf\u5168\u65e0\uff0c\u98de\u8679\u4e03\u5251\u4e2d\u8001\u4e09\u953a\u82f1\u5947\u7684 \u8eab\u4e0a\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u5f20\u51a0\u674e\u6234>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u5f20\u51a0\u674e\u6234 \u3000\u3000\u539f\u6765\u4f0a\u98ce\u5728\u6613\u5bb9\u4e4b\u4e0b\uff0c\u9762\u8c8c\u7adf\u53d8\u5f97\u548c\u201c\u98de\u8679\u4e03\u5251\u201d\u4e2d\u4e09\u4fa0\u953a\u82f1\u5947\u7684\u9762\u8c8c\uff0c\u5b8c\u5168\u4e00\u6837\uff0c\u8fde \u81ea\u5e7c\u548c\u953a\u82f1\u5947\u4e00\u9f50\u76f8\u5904\u7684\u5e08\u5144\u5f1f\uff0c\u90fd\u5206\u8fa8\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u534e\u54c1\u5947\u770b\u5230\u4f0a\u98ce\u59cb\u7ec8\u672a\u52a8\uff0c\u5fc3\u91cc\u66f4\u8ba4\u5b9a\u4e86\u5c31\u662f\u81ea\u5df1\u7684\u4e09\u5f1f\uff0c\u5c31\u662f\u90a3\u81ea\u5e7c\u88ab\u81ea\u5df1\u6536\u517b\uff0c\u540e \u6765\u5374\u4e3a\u7740\u4e00\u4e8b\uff0c\u201c\u65e0\u610f\u201d\u4f24\u4e86\u81ea\u5df1\u7684\u53f3\u817f\uff0c\u4e00\u9003\u65e0\u8e2a\u7684\u953a\u82f1\u5947\uff0c\u5fc3\u4e0b\u4e0d\u7981\u53c8\u662f\u4e00\u9635\u607b\u7136\uff0c\u558a \u9053 \u3000\u3000\u201c\u4e09\u5f1f\uff01\u4f60\u5230\u8fd9\u8fb9\u6765\uff0c\u8ba9\u5927\u54e5\u6211\u770b\u770b\u4f60\u3002\u201d \u3000\u3000\u8c37\u6653\u9759\u867d\u7136\u540d\u5217\u201c\u6b66\u6797\u56db\u7f8e\u201d\uff0c\u4f46\u6b66\u529f\u5374\u5e76\u4e0d\u751a\u9ad8\uff0c\u6b64\u523b\u62b5\u654c\u9f9a\u5929\u5947\u638c\u4e2d\u7684\u201c\u98de\u8679 \u5251\u201d\uff0c\u4e8c\u5341\u4e2a\u7167\u9762\u4e0b\u6765\uff0c\u5df2\u662f\u9999\u6c57\u6dcb\u6f13\uff0c\u5927\u611f\u4e0d\u652f\u3002 \u3000\u3000\u4f55\u51b5\u5979\u8fd8\u6e05\u6025\u81ea\u5df1\u4e08\u592b\u7684\u5b89\u5371\uff0c\u4e0d\u7981\u5a07\u5524\u9053\uff1a \u3000\u3000\u201c\u59d3\u534e\u7684\uff0c\u4f60\u5f04\u5f04\u6e05\u695a\u597d\u4e0d\u597d\uff0c\u59d1\u5a18\u6211\u662f\u8fa3\u624b\u897f\u65bd\u8c37\u6653\u9759\uff0c\u4f60\u522b\u548c\u4f60\u7684\u5b9d\u8d1d\u5e08\u5144\u5f1f\u7275\u6d89 \u5230\u4e00\u5904\u53bb\u3002\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u5237\u5730\u4e00\u5251\uff0c\u81ea\u5979\u53f3\u81c2\u5212\u8fc7\uff0c\u5c06\u5979\u7684\u72d0\u76ae\u5c0f\u8884\uff0c\u5212\u4e86\u9053\u957f\u957f\u7684\u53e3\u5b50\u3002 \u3000\u3000\u5979\u66f4\u60ca\u5f97\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u5374\u542c\u534e\u54c1\u5947\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fa3\u624b\u897f\u65bd\uff0c\u54fc\uff01\u5df4\u51b2\u8fd9\u540d\u5b57\uff0c\u5c31\u4e0d\u662f\u597d\u4e1c\u897f\u3002\u4e09\u5f1f\uff01\u7ed9\u6211\u6293\u4e0b\u6765\u3002\u201d \u3000\u3000\u4f0a\u98ce\u59cb\u7ec8\u5728\u53d1\u7740\u6115\uff0c\u6b64\u523b\u521a\u521a\u6709\u4e9b\u4f1a\u8fc7\u610f\u6765\uff0c\u77e5\u9053\u81ea\u5df1\u65e0\u6cd5\u4e4b\u4e2d\u7684\u4e54\u88c5\uff0c\u521a\u597d\u548c\u4eba\u5bb6\u7684 \u4e09\u5e08\u5f1f\u7684\u9762\u8c8c\uff0c\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\uff0c\u4f46\u6b64\u523b\u7684\u60c5\u666f\uff0c\u5df2\u4e0d\u5bb9\u4ed6\u518d\u4e0d\u51fa\u624b\uff0c\u5fc3\u4e2d\u65b9\u81ea\u52a8\u5ff5\uff0c\u5374\u89c1\u8427\u5357\u840d \u5df2\u63a0\u4e86\u8fc7\u6765\uff0c\u4f4e\u8bed\u9053\uff1a \u3000\u3000\u201c\u5357\u54e5\uff01\u8d1d\u8fd9\u6837\u5b50\u662f\u8bef\u66fe\uff0c\u975e\u8981\u4f60\u81ea\u5df1\u51fa\u624b\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u5410\u6c14\u5982\u5170\uff0c\u5439\u8fdb\u4f0a\u98ce\u7684\u9f3b\u7aef\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5973\u4eba\u5bb6\u8bf4\u7684\u8bdd\uff0c\u548c\u6ca1\u6709\u8bf4\u7adf\u5b8c\u5168\u4e00\u6837\uff0c\u6211\u96be\u9053\u4e0d\u77e5\u9053\u8fd9\u662f\u8bef\u4f1a\u3002\u201d \u3000\u3000\u53c8\u770b\u4e86\u8427\u5357\u840d\u4e00\u773c\uff0c\u5374\u548c\u5979\u6ee1\u542b\u5173\u6000\u7684\u773c\u5149\uff0c\u78b0\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u4ed6\u518d\u4e00\u7b11\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u811a\u6b65\u5fae\u9519\u95f4\uff0c\u5df2\u5feb\u5982\u95ea\u7535\u5730\uff0c\u63a0\u5230\u8c37\u6653\u9759\u52a8\u624b\u4e4b\u5904\uff0c\u4f4e\u559d\u9053\uff1a \u201c\u8bf7\u6682\u4f4f\u624b\uff01\u201d\u8c37\u6653\u9759\u5a07\u58f0\u9053\uff0c \u3000\u3000\u201c\u4f60\u518d\u4e0d\u6765\u6211\u53ef\u8981\u6025\u75af\u4e86\u3002\u201d \u3000\u3000\u8eab\u5f62\u5411\u4ed6\u8eab\u540e\u8eb2\u53bb\u3002 \u3000\u3000\u662f\u4ee5\u9f9a\u5929\u5947\u55d6\u7136\u4e00\u5251\uff0c\u5374\u6b63\u597d\u662f\u523a\u5411\u4f0a\u98ce\u8eab\u524d\uff0c\u5bd2\u5149\u4e00\u6e9c\uff0c\u77ac\u5373\u6325\u81f3\u3002\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\u3002 \u6b64\u523b\u9f9a\u5929\u5947\u4e5f\u770b\u6e05\u9762\u524d\u4e4b\u4eba\uff0c\u53e3\u4e2d\u60ca\u559d\u9053\uff1a\u201c\u4e09\u54e5\u2014\u2014\u201d \u3000\u3000\u624b\u4e2d\u5251\u5f0f\uff0c\u5374\u56e0\u5df2\u8fd1\u5c3e\u52bf\uff0c\u524d\u529b\u5df2\u53d1\uff0c\u540e\u529b\u672a\u81f3\uff0c\u4ecd\u7136\u7b14\u76f4\u5730\u5241\u5411\u4f0a\u98ce\u3002 \u3000\u3000\u534e\u54c1\u5947\u4e5f\u60ca\u5524\u4e00\u58f0\u3002 \u3000\u3000\u5374\u89c1\u4f0a\u98ce\u5fae\u7b11\u58f0\u4e2d\uff0c\u80a9\u5934\u4e0d\u52a8\uff0c\u8eab\u5f62\u4e0d\u66f2\uff0c\u4eba\u5df2\u500f\u7136\u6e9c\u5f00\u4e09\u5c3a\u3002 \u3000\u3000\u4ed6\u8eab\u4e3a\u4e00\u6d3e\u638c\u95e8\uff0c\u89c1\u5230\u8fd9\u79cd\u5168\u51ed\u4e00\u53e3\u771f\u6c14\u7684\u8fd0\u884c\uff0c\u800c\u65bd\u51fa\u8f7b\u529f\u8eab\u6cd5\uff0c\u81ea\u662f\u8bc6\u8d27\uff0c\u4e0d\u7981\u60ca \u5524\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff0c\u4f60\u529f\u592b\u600e\u5730\u8fdb\u5883\u5982\u6b64\u4e4b\u901f\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u53c8\u5fae\u7b11\u4e00\u4e0b\uff0c\u77e5\u9053\u81ea\u5df1\u81ea\u4ece\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\u901a\u540e\uff0c\u529f\u529b\u65b9\u9762\u7684\u8fdb\u5883\uff0c\u786e\u662f\u975e\u540c\u5c0f \u9119\uff0c\u8fde\u8fd9\u957f\u767d\u638c\u95e8\u90fd\u4e3a\u4e4b\u52a8\u5bb9\u3002 \u3000\u3000\u4ed6\u5fae\u4e00\u62b1\u62f3\uff0c\u5411\u534e\u54c1\u5947\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u4f0a\u98ce\uff0c\u867d\u4e45\u95fb\u534e\u8001\u524d\u8f88\u4e4b\u5927\u540d\uff0c\u786e\u59cb\u7ec8\u65e0\u7f18\u62dc\u8bc6\uff0c\u4eca\u65e5\u5f97\u89c1\u4fa0\u8e2a\uff0c\u5b9e\u5728\u662f\u5c0f\u9119\u4e4b \u5e78\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u534e\u54c1\u5947\u5df2\u62a2\u7740\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u4f60\u8fd9\u662f\u8bf4\u7684\u4ec0\u4e48\u8bdd\uff1f\u96be\u9053\uff0c\u96be\u9053\u4f60\u8fd9\u51e0\u5e74\u6765\u5df2\u53e6\u6295\u540d\u5e08\uff0c\u5df2\u7ecf\u4e0d\u8ba4\u4f60\u7684\u5e08\u5144\u5f1f \u4e86\uff1f\u4f60\u2014\u2014\u4f60\u8fd9\u771f\u2014\u2014\u771f\u592a\u4e0d\u5bf9\u4e86\uff01\u201d \u3000\u3000\u8bf4\u5230\u540e\u6765\uff0c\u4ed6\u8bed\u58f0\u53c8\u56e0\u6fc0\u52a8\u800c\u98a4\u6296\u4e86\u3002 \u3000\u3000\u5728\u573a\u7fa4\u8c6a\uff0c\u600e\u4f1a\u77e5\u9053\u8fd9\u5176\u4e2d\u66f2\u6298\uff0c\u90fd\u4ee5\u60ca\u8be7\u800c\u4e0d\u5c51\u7684\u76ee\u5149\uff0c\u671b\u7740\u4f0a\u98ce\uff0c\u7686\u56e0\u80cc\u53db\u5e08\u95e8\uff0c \u6b63\u662f\u72af\u4e86\u6b66\u6797\u5927\u5fcc\uff1b\u4f55\u51b5\u8fd9\u534e\u54c1\u5947\u6b64\u523b\u795e\u6001\uff0c\u66f4\u6781\u6006\u7136\uff01 \u3000\u3000\u4f0a\u98ce\u65b9\u6b32\u7b54\u8bdd\uff0c\u90a3\u6bdb\u6587\u5947\u4e5f\u63a0\u4e86\u4e0a\u6765\uff0c\u9762\u4e25\u5982\u6c34\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u4f60\u4e5f\u672a\u514d\u592a\u65e0\u60c5\u4e86\uff01\u4f60\u548c\u5927\u5e08\u5144\u867d\u7136\u540d\u662f\u5e08\u5144\u5f1f\uff0c\u4f46\u81ea\u4ece\u5e08\u5085\u6b7b\u540e\uff0c\u4f60\u90a3\u4e00\u624b \u529f\u592b\u4e0d\u662f\u5927\u5e08\u5144\u6559\u4f60\u7684\uff0c\u73b0\u5728\u4f60\u5c31\u7b97\u4e0d\u8ba4\u5f97\u6211\u4eec\uff0c\u53ef\u662f\u4f60\u600e\u4e48\u80fd\u4e0d\u8ba4\u5927\u5e08\u5144\uff1f\u4f60\u2014\u2014\u4f60\u7b80\u76f4 \u2014\u2014\u592a\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u4f0a\u98ce\u6697\u53f9\u4e00\u58f0\uff0c\u77e5\u9053\u6b64\u4e8b\u4e0d\u662f\u5bb9\u6613\u8bf4\u5f97\u6e05\u695a\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u5f53\u7740\u5982\u8bb8\u591a\u6b66\u6797\u4e2d\u4eba\uff0c\u52bf\u53c8\u4e0d\u80fd\u63ed\u5f00\u81ea\u5df1\u7684\u9762\u5177\uff0c\u8bf4\u51fa\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u6c89\u541f\u534a\u664c\uff0c\u4ed6\u53ea\u5f97\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u4f0a\u98ce\uff0c\u5927\u7ea6\u662f\u548c\u534e\u8001\u524d\u8f88\u7684\u4e09\u5f1f\u751f\u5f97\u6781\u4e3a\u76f8\u50cf\uff0c\u662f\u4ee5\u534e\u8001\u524d\u8f88\u624d\u4f1a\u751f\u6b64\u8bef\u4f1a\u3002 \u5509\uff01\u5c0f\u9119\u5b9e\u5728\u4e5f\u65e0\u6cd5\u89e3\u91ca\u2014\u2014\u201d \u3000\u3000\u8427\u5357\u840d\u7a81\u7136\u63a0\u8fc7\u6765\uff0c\u62a2\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u534e\u8001\u524d\u8f88\uff01\u4f60\u542c\u4ed6\u8bf4\u8bdd\u7684\u53e3\u97f3\uff0c\u5b8c\u5168\u548c\u4f60\u4eec\u4e0d\u540c\uff0c\u96be\u9053\u751f\u957f\u5728\u957f\u767d\u5c71\u4e0a\u7684\u4eba\uff0c\u4f1a\u8bf4\u51fa \u8fd9\u79cd\u7eaf\u7cb9\u7684\u6c5f\u5357\u53e3\u97f3\u6765\u4e48\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6697\u8d5e\u4e00\u58f0\uff0c\u89c9\u5f97\u8427\u5357\u82f9\u7684\u806a\u6167\uff0c\u5b9e\u6709\u8fc7\u4eba\u4e4b\u5904\uff01 \u3000\u3000\u53c8\u89c9\u5f97\u5973\u4eba\u5bb6\u5012\u5e95\u5fc3\u7ec6\u4e9b\uff0c\u80fd\u6ce8\u610f\u5230\u8fd9\u4e9b\u5927\u5bb6\u90fd\u6ca1\u6709\u6ce8\u610f\u5230\u7684\u5730\u65b9\u3002 \u3000\u3000\u534e\u54c1\u5947\uff0c\u6bdb\u6587\u5947\uff0c\u9f9a\u5929\u5947\uff0c\u4ee5\u53ca\u90a3\u59cb\u7ec8\u672a\u51fa\u624b\u7684\u9ec4\u5fd7\u5947\uff0c\u8fd9\u201c\u98de\u8679\u4e03\u5251\u201d\u4e2d\u7684\u56db\u4eba\uff0c\u679c \u7136\u90fd\u6014\u4e86\u4e00\u4e0b\uff0c\u66f4\u4e3a\u4ed4\u7ec6\u5730\u671b\u7740\u4f0a\u98ce\u3002 \u3000\u3000\u90a3\u8fb9\u8c37\u6653\u9759\u5df2\u6276\u7740\u53d7\u5185\u529b\u9707\u4f24\u7684\u59da\u6e05\u5b87\u8d70\u4e86\u8fc7\u6765\uff0c\u671d\u7740\u201c\u98de\u8679\u4e03\u5251\u201d\u6068\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u59d3\u534e\u7684\uff01\u4f60\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\u5c31\u51fa\u624b\u4f24\u4eba\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u6211\u592b\u5987\u4e24\u4eba\u603b\u6709\u62a5\u590d\u4f60 \u7684\u4e00\u5929\u3002\u201d \u3000\u3000\u5979\u72e0\u72e0\u4e00\u8dfa\u811a\uff0c\u773c\u671b\u56db\u65b9\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\uff01\u4f60\u4eec\u770b\u770b\u8fd9\u4f4d\u957f\u767d\u5c71\u7684\u5927\u638c\u95e8\u4eba\uff0c\u81ea\u5df1\u7ba1\u4e0d \u4f4f\u81ea\u5df1\u7684\u5e08\u5f1f\uff0c\u8ba9\u5e08\u5f1f\u8dd1\u4e86\uff0c\u5374\u8dd1\u5230\u8def\u4e0a\u6765\uff0c\u968f\u4fbf\u8ba4\u4eba\u505a\u5e08\u5f1f\u3002\u54fc\uff01\u53ea\u53ef\u60dc\u4f60\u4eec\u201c\u98de\u8679\u4e03 \u5251\u201d\u7684\u540d\u5934\u867d\u5927\uff0c\u4eba\u5bb6\u4e5f\u4e0d\u5e0c\u7f55\u2014\u2014\u201d \u3000\u3000\u534e\u54c1\u5947\u6c14\u5f97\u6d51\u8eab\u98a4\u6296\uff0c\u6012\u559d\u9053\uff1a \u3000\u3000\u201c\u4f4f\u58c1\uff01\u201d \u3000\u3000\u8c37\u6653\u9759\u5374\u53c8\u8fde\u8fde\u8dfa\u811a\uff0c\u51d1\u4e0a\u524d\u53bb\uff0c\u5a07\u53f1\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u600e\u7684\uff1f\u4f60\u8981\u600e\u7684\uff1f\u96be\u9053\u4f60\u4ed7\u7740\u6b66\u529f\u6bd4\u4eba\u5bb6\u9ad8\uff0c\u5c31\u53ef\u4ee5\u968f\u4fbf\u6b3a\u8d1f\u4eba\u5417\uff01\u4f60\u518d\u4ed4\u7ec6\u770b \u770b\u6e05\u695a\uff0c\u4eba\u5bb6\u662f\u4e0d\u662f\u4f60\u7684\u5e08\u5f1f\uff1f\u54fc\uff01\u5929\u4e0b\u90a3\u6709\u8fd9\u79cd\u4e8b\uff0c\u786c\u62c9\u7740\u522b\u4eba\u8ba4\u505a\u662f\u81ea\u5df1\u7684\u5e08\u5f1f1\u201d \u3000\u3000\u5979\u8bed\u58f0\u6e05\u8106\uff0c\u8bf4\u5f97\u53c8\u5feb\uff0c\u534e\u54c1\u5947\u7a7a\u81ea\u6c14\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u5374\u65e0\u6cd5\u56de\u53e3\u3002 \u3000\u3000\u5979\u7a0d\u4e3a\u5598\u4e86\u53e3\u6c14\uff0c\u671d\u7740\u8427\u5357\u82f9\u548c\u4f0a\u98ce\u9053\uff1a \u3000\u3000\u201c\u4f0a\u8001\u5f1f\uff01\u8427\u4e09\u59b9\uff01\u6211\u4eec\u5148\u8d70\u4e86\u3002\u4ed6\u53d7\u4e86\u4f24\uff0c\u7ec8\u5357\u5c71\u4e5f\u53bb\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u4e00\u9762\u53c8\u8dfa\u7740\u811a\uff01 \u3000\u3000\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u5e73\u767d\u65e0\u6545\u5730\u60f9\u6765\u8fd9\u4e9b\u4e8b\u3002\u5582\uff01\u6211\u8bf4\u4e09\u59b9\uff01\u4f60\u8d76\u5feb\u5e26\u7740\u4f0a\u8001\u5f1f\u8d70\u8fdc\u70b9\u513f\uff0c\u522b \u8ba9\u75af\u72d7\u7ed9\u54ac\u4e00\u53e3\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u4e4b\u95f4\uff0c\u53d1\u51fa\u4e00\u4e9b\u5fcd\u4fca\u4e0d\u4f4f\u7684\u7b11\u58f0\u3002\u534e\u54c1\u5947\u9762\u8272\u94c1\u9752\uff0c\u4e25\u559d\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u82e5\u4e0d\u662f\u770b\u4f60\u662f\u4e2a\u65e0\u77e5\u7684\u5987\u4eba\uff0c\u4eca\u65e5\u5c31\u53eb\u4f60\u6bd9\u4e8e\u638c\u4e0b\u3002\u201d \u3000\u3000\u8c37\u6653\u9759\u5374\u4e5f\u4e00\u4e9b\u4e5f\u4e0d\u542b\u7cca\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u671d\u7740\u4ed6\u6068\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u59d3\u534e\u7684\uff01\u4f60\u5c11\u8bf4\u8fd9\u79cd\u5e9f\u8bdd\uff01\u6211\u65e0\u77e5\uff0c\u4f60\u624d\u65e0\u77e5\u5462\uff1f\u786c\u8bf4\u522b\u4eba\u662f\u4f60\u5e08\u5f1f\u3002\u5582\uff01\u6211\u8bf4\u4f0a\u8001 \u5f1f\uff01\u4f60\u2014\u2014\u201d \u3000\u3000\u4f0a\u98ce\u6015\u5979\u8bf4\u51fa\u81ea\u5df1\u6613\u5bb9\u7684\u4e8b\u6765\uff0c\u8d76\u7d27\u62a2\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u534e\u8001\u524d\u8f88\uff01\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5b9e\u662f\u51fa\u4e8e\u8bef\u4f1a\uff0c\u4e5f\u602a\u4e0d\u4e86\u4ec0\u4e48\u4eba\u3002\u4e0d\u8fc7\u5c0f\u9119\u53ef\u4ee5\u6307\u5929\u7acb\u8a93\uff0c\u5b9e \u5728\u751f\u5e73\u672a\u66fe\u89c1\u8fc7\u9601\u4e0b\u4e00\u9762\uff0c\u66f4\u4e0d\u662f\u8001\u524d\u8f88\u53e3\u4e2d\u7684\u201c\u4e09\u5f1f\u201d\uff0c\u5929\u4e0b\u50cf\u8c8c\u76f8\u540c\u4e4b\u4eba\u751a\u591a\u3002\u65e5\u540e\u5c0f \u9119\u82e5\u89c1\u7740\u534e\u8001\u524d\u8f88\u7684\u5e08\u5f1f\uff0c\u5fc5\u5b9a\u4ee3\u4e3a\u8f6c\u544a\u8001\u524d\u8f88\u7684\u610f\u601d\uff0c\u6211\u60f3\u90a3\u4f4d\u5144\u53f0\u53e6\u6709\u82e6\u8877\uff0c\u662f\u4ee5\u672a\u56de \u5c71\u53bb\u2014\u2014\u201d \u3000\u3000\u534e\u54c1\u5947\u5389\u58f0\u4e00\u53f1\uff0c\u963b\u4f4f\u4e86\u4ed6\u7684\u8bdd\u9053\uff1a \u3000\u3000\u201c\u4f60\u771f\u7684\u4e0d\u662f\u953a\u82f1\u5947\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u7b11\u6447\u5934\u9053\uff1a \u3000\u3000\u201c\u953a\u82f1\u5947\u8fd9\u540d\u5b57\uff0c\u5c0f\u9119\u6709\u751f\u4ee5\u6765\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u54e9\uff01\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u4e86\uff0c\u5374\u89c1\u534e\u54c1\u5947\u7684\u8eab\u5f62\u500f\u7136\u4e00\u52a8\uff0c\u77ac\u76ee\u4e4b\u95f4\uff0c\u6f2b\u5929\u5149\u534e\u4e71\u95ea\uff0c\u4f0a\u98ce\u5927\u51fa\u610f\u5916\uff0c\u53ea \u89c9\u5f97\u56db\u9762\u516b\u65b9\uff0c\u4ff1\u662f\u5251\u5f71\uff0c\u5411\u81ea\u5df1\u5f53\u5934\u538b\u4e0b\u3002 \u3000\u3000\u5728\u8fd9\u51e0\u4e4e\u662f\u751f\u6b7b\u7cfb\u4e8e\u4e00\u53d1\u7684\u5f53\u513f\uff0c\u4ed6\u76ee\u5149\u52a8\u5904\uff0c\u53d1\u73b0\u8fd9\u4e00\u62db\u7684\u5de6\u65b9\u4e0b\u7aef\uff0c\u4f3c\u4e4e\u5fae\u5fae\u6709\u4e00 \u4e1d\u7a7a\u9699\uff0c\u4ed6\u539f\u672c\u4e45\u8d70\u6c5f\u6e56\uff0c\u4e0e\u654c\u4eba\u52a8\u624b\u7684\u7ecf\u9a8c\u6781\u591a\uff0c\u6b64\u523b\u4fbf\u8eab\u968f\u610f\u52a8\uff0c\u811a\u6b65\u4e00\u8f6c\uff0c\u500f\u7136\u5411\u5de6 \u65b9\u6e9c\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u8eab\u5f62\u65b9\u81ea\u4e00\u52a8\uff0c\u90a3\u6709\u5982\u6f2b\u5929\u98de\u82b1\u7684\u5251\u5f71\uff0c\u7adf\u50cf\u662f\u65e9\u5c31\u77e5\u9053\u4ed6\u8eab\u5f62\u4e4b\u6240\u8d8b\u4f3c\u7684\uff0c\u5149 \u534e\u4e00\u95ea\uff0c\u6f2b\u5929\u5251\u5f71\u84e6\u5730\u53d8\u4e3a\u4e00\u6e9c\u9752\u84dd\u8272\u7684\u5149\u534e\uff0c\u5e26\u7740\u4e00\u7f15\u5c16\u9510\u7684\u98ce\u58f0\uff0c\u968f\u7740\u4f0a\u98ce\u7684\u53bb\u52bf\u6325\u5411 \u5de6\u65b9\u3002 \u3000\u3000\u4f0a\u98ce\u53f3\u811a\u65b9\u81ea\u6ed1\u5f00\uff0c\u773c\u89d2\u77a5\u5904\uff0c\u4e00\u70b9\u5251\u5149\u5df2\u523a\u5411\u4ed6\u524d\u80f8\uff0c\u751f\u50cf\u662f\u8fd9\u70b9\u5251\u5149\u65e9\u5df2\u5728\u90a3\u91cc\u7b49 \u7740\u4ed6\u4f3c\u7684\uff0c\u4ed6\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u5f97\u6084\u7136\u95ed\u4e0a\u773c\u6674\uff0c\u4f3c\u4e4e\u5df2\u5728\u9759\u5019\u7740\u8fd9\u4e00\u5251\u7684\u523a\u4e0b\u3002\u8fd9\u4e00\u53d8\u6545\uff0c\u7a81 \u7136\u800c\u6765\uff0c\u7b49\u5230\u5927\u5bb6\u53d1\u73b0\u65f6\uff0c\u90a3\u4e00\u6e9c\u84dd\u5149\uff0c\u5df2\u523a\u5411\u4f0a\u98ce\u4e86\u3002\u7fa4\u8c6a\u4e0d\u81ea\u89c9\u5730\u60ca\u547c\u4e00\u58f0\u3002\u8427\u5357\u82f9\u60c5 \u6025\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u6655\u4e86\u8fc7\u53bb\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u513f\u5973\u60c5\u6000>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u513f\u5973\u60c5\u6000 \u3000\u3000\u7136\u800c\uff0c\u8fd9\u4e00\u6e9c\u9752\u84dd\u8272\u7684\u5251\u5149\uff0c\u5728\u7a0d\u7a0d\u63a5\u89e6\u5230\u4f0a\u98ce\u80f8\u524d\u65f6\uff0c\u4fbf\u500f\u7136\u800c\u6b62\u3002 \u3000\u3000\u4f0a\u98ce\u7741\u5f00\u773c\u6765\uff0c\u770b\u5230\u534e\u54c1\u5947\u90a3\u4e00\u53cc\u70af\u7136\u6709\u5149\u7684\u773c\u775b\uff0c\u4e5f\u6b63\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u53c8\u611f\u6168\u4e07\u751f\uff0c\u4eba\u5bb6\u8fd9\u4e00\u5251\uff0c\u867d\u662f\u5728\u81ea\u5df1\u731d\u4e0d\u53ca\u9632\u7684\u6e05\u51b5\u4e0b\u523a\u6765\uff0c \u4f46\u7ec8\u7a76\u4e5f\u662f\u56e0\u4e3a\u81ea\u5df1\u5185\u529f\u867d\u6709\u6210\uff0c\u4f46\u62db\u5f0f\u5374\u8fd8\u662f\u672a\u767b\u5802\u5965\uff0c\u5426\u5219\u4e5f\u4e0d\u4f1a\u88ab\u4eba\u5bb6\u903c\u5f97\u5982\u6b64\u3002 \u3000\u3000\u4ed6\u53c8\u4e0d\u7981\u540e\u6094\uff0c\u81ea\u5df1\u5728\u59da\u6e05\u5b87\u5bb6\u4e2d\u90a3\u4e00\u6bb5\u65e5\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5c06\u201c\u5929\u661f\u79d8\u7b08\u201d\u4e0a\u7684\u6b66\u5b66\u53c2\u8be6 \u4e00\u4e0b\uff0c\u800c\u53ea\u987e\u5f97\u4eab\u53d7\u90a3\u4e9b\u81ea\u5df1\u5e76\u4e0d\u8be5\u4eab\u53d7\u7684\u6e29\u99a8\u3002 \u3000\u3000\u8fd9\u6837\uff0c\u6211\u8fd8\u80fd\u8c08\u4ec0\u4e48\u590d\u4ec7\uff0c\u96ea\u803b\u5462\uff1f \u3000\u3000\u4ed6\u6697\u6068\u7740\u81ea\u5df1\uff0c\u51e0\u4e4e\u8981\u5c06\u81ea\u5df1\u7684\u80f8\u819b\uff0c\u51d1\u5230\u90a3\u53d1\u4eae\u7684\u5251\u5c16\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u4e9b\u5ff5\u5934\uff0c\u5728\u4ed6\u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\u3002 \u3000\u3000\u90a3\u77e5\u534e\u54c1\u5947\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u6536\u56de\u5251\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4f3c\u4e4e\u53c8\u53d8\u5f97\u82cd\u8001\u4e86\u8bb8\u591a\uff0c\u671d\u7740\u6bdb\u6587\u5947\u957f\u53f9\u9053\uff1a \u3000\u3000\u201c\u4ed6\u679c\u7136\u4e0d\u662f\u8001\u4e09\uff0c\u5509\u600e\u5730\u5929\u4e0b\u7adf\u6709\u5982\u6b64\u76f8\u50cf\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u6bdb\u6587\u5947\u4e5f\u5782\u4e0b\u5934\uff0c\u548c\u9f9a\u5929\u5947\u7b49\u53c8\u63a0\u53bb\u9a6c\u4fa7\uff0c\u817e\u8eab\u4e0a\u4e86\u9a6c\u3002 \u3000\u3000\u534e\u54c1\u5947\u770b\u4e86\u90a3\u5012\u5728\u5730\u4e0a\u5df2\u5944\u5944\u5782\u606f\u7684\u9a6c\u4e00\u773c\uff0c\u957f\u5251\u4e00\u6296\uff0c\u5239\u90a3\u95f4\u5728\u9a6c\u8eab\u4e0a\u523a\u4e86\u4e09\u5251\uff0c\u90a3 \u5339\u585e\u5916\u7684\u826f\u9a79\uff0c\u4fbf\u4f4e\u5636\u7740\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u53f9\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u63a0\uff0c\u63a0\u5230\u6bdb\u6587\u5947\u6240\u4e58\u7684\u9a6c\u4e0a\uff0c\u4e09\u9a91\u56db\u4eba\uff0c\u4fbf\u53c8\u50cf\u6765\u65f6\u4e00\u6837\uff0c\u98ce\u9a70\u7535 \u63a3\u822c\u671d\u53e6\u4e00\u65b9\u9762\u5954\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u6115\u4e86\u8bb8\u4e45\uff0c\u624d\u62ac\u8d77\u5934\u6765\uff0c\u5374\u89c1\u8427\u5357\u82f9\u6b63\u7ad9\u5728\u9762\u524d\uff0c\u5fae\u5fae\u542b\u7b11\u5730\u671b\u7740\u81ea\u5df1\uff0c\u6e29\u67d4\u5730\u8bf4 \u5230\uff1a \u3000\u3000\u201c\u4f60\u522b\u96be\u53d7\uff01\u90a3\u4e2a\u8001\u5934\uff0c\u53ef\u771f\u5389\u5bb3\u5f97\u5f88\u2014\u2014\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u9886\u53d7\u4e86\u5979\u8bdd\u4e2d\u7684\u65e0\u5c3d\u5173\u5fc3\u548c\u5b89\u6170\u3002 \u3000\u3000\u800c\u5979\u4e5f\u77e5\u9053\u81ea\u5df1\u65e0\u987b\u518d\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\u5979\u4ece\u4ed6\u7684\u4e00\u7b11\u4e2d\uff0c\u5df2\u77e5\u9053\u4ed6\u5df2\u9886\u53d7\u4e86\u81ea\u5df1\u7684\u6e05\u610f\u3002 \u3000\u3000\u8c37\u6653\u9759\u6400\u6276\u7740\u9762\u8272\u60e8\u767d\u7684\u59da\u6e05\u5b87\uff0c\u7f13\u7f13\u8d70\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u8001\u5934\u5b50\u771f\u50cf\u795e\u7ecf\u75c5\u4f3c\u7684\uff0c\u4f60\u770b\uff01\u4ed6\u4e0d\u77e5\u600e\u7684\uff0c\u5c31\u8fd9\u4e48\u8d70\u4e86\u3002\u201d \u3000\u3000\u5979\u76ee\u5149\u5411\u4f0a\u98ce\u53eb\u8427\u5357\u82f9\u4e00\u8f6c\uff0c\u5634\u89d2\u4f3c\u4e4e\u53c8\u6709\u4e86\u4e9b\u7b11\u610f\uff0c\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4f24\u5f97\u867d\u7136\u4e0d\u592a\u91cd\uff0c\u53ef\u4e5f\u4e0d\u592a\u8f7b\uff0c\u6211\u5148\u9001\u4ed6\u56de\u5bb6\u53bb\u3002\u5582\uff01\u4e09\u59b9\uff01\u4f60\u662f\u4e0d\u662f\u8ddf\u6211\u4e00\u5757\u513f \u8d70\uff1f\u201d \u3000\u3000\u5979\u4e00\u6307\u4f0a\u98ce\uff1a\u201c\u8fd8\u662f\u8ddf\u4ed6\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u8138\u53c8\u7ea2\u4e86\u7ea2\uff0c\u8c37\u6653\u9759\u53c8\u5df2\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd8\u662f\u8ddf\u4ed6\u8d70\u5427\uff0c\u6211\u53ef\u4e0d\u6562\u786c\u628a\u4f60\u8fd9\u4f4d\u5973\u9b54\u5934\u62c9\u6765\u3002\u201d \u3000\u3000\u5979\u53c8\u671d\u4f0a\u98ce\u4e00\u626c\u624b\uff1a\u201c\u5582\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u628a\u6211\u7684\u4e09\u59b9\u4ea4\u7ed9\u4f60\u4e86\uff0c\u4f60\u53ef\u8981\u628a\u5979\u597d\u597d\u5730\u8fd8\u7ed9\u6211\uff0c\u8981\u662f\u4f60\u4e0d\u597d\u597d\u5f85\u5979\uff0c\u6b3a\u8d1f\u4e86\u5979\uff0c \u54fc\uff01\u8d1d\u6211\u4f1a\u4e0d\u4f1a\u9976\u4f60\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u65e0\u53ef\u5948\u4f55\u5730\u7b11\u4e86\u4e00\u4e0b\uff0c\u8427\u5357\u82f9\u7684\u8138\u5374\u53c8\u7ea2\u4e86\uff0c\u8fd9\u6614\u65e5\u4ee5\u624b\u6bb5\u4e4b\u8fa3\uff0c\u95fb\u540d\u6c5f\u6e56\u7684\u5973\u715e \u661f\uff0c\u8fd1\u65e5\u6765\u7a81\u7136\u53d8\u5f97\u50cf\u95fa\u5973\u822c\u6e29\u67d4\uff0c\u82e5\u4f60\u662f\u806a\u660e\u7684\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\uff0c\u80fd\u4f7f\u4e00\u4e2a\u521a\u5f3a\u7684\u5973\u5b50\uff0c\u7a81 \u7136\u53d8\u5f97\u6e29\u67d4\u7684\uff0c\u552f\u4e00\u7684\u529b\u91cf\uff0c\u5c31\u662f\u7231\u60c5\uff0c\u8fd9\u662f\u4e98\u53e4\u4e0d\u53d8\u7684\u3002 \u3000\u3000\u8427\u5357\u82f9\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u600e\u4e48\u6709\u4e86\u8fd9\u4efd\u60c5\u611f\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u8fde\u5979\u81ea\u5df1\u4e5f\u4e0d\u80fd\u4e5f\u4e0d\u613f\u5426\u8ba4\uff0c\u8fd9\u6b63\u662f\u7231\u60c5\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e03\u7ae0\u3000\u7ec8\u5357\u76db\u4f1a>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u4e03\u7ae0\u3000\u7ec8\u5357\u76db\u4f1a \u3000\u3000\u4f0a\u98ce\u50cf\u662f\u75f4\u8ff7\u4e86\u4f3c\u7684\u6115\u4e86\u8bb8\u4e45\u3002\u65b9\u624d\u90a3\u534e\u54c1\u5947\u7684\u4e00\u5251\uff0c\u867d\u7136\u5e76\u6ca1\u6709\u4f24\u5bb3\u5230\u4ed6\u7684\u8eab\u4f53\uff0c\u7136 \u5374\u50cf\u662f\u5df2\u4f24\u4e86\u4ed6\u7684\u5fc3\u3002\u4ed6\u77e5\u9053\u65b9\u624d\u5728\u8fdc\u8fdc\u56f4\u98a7\u7740\u7684\u6b66\u6797\u4eba\u58eb\uff0c\u6b64\u523b\u867d\u5df2\u6e10\u6e10\u8d70\u5f00\uff0c\u4f46\u662f\u4ed6\u4eec \u90a3\u79cd\u6df7\u5408\u7740\u60ca\u8be7\uff0c\u597d\u5947\uff0c\u548c\u53e6\u4e00\u79cd\u8bf4\u4e0d\u51fa\u610f\u5473\u6765\u7684\u76ee\u5149\uff0c\u5374\u5f77\u4f5b\u4ecd\u5728\u4f0a\u98ce\u56db\u4fa7\u51dd\u6ce8\u7740\uff0c\u4f7f\u5f97 \u4ed6\u51e0\u4e4e\u8fde\u5934\u90fd\u4e0d\u6562\u62ac\u8d77\u6765\u3002 \u3000\u3000\u59da\u6e05\u5b87\u548c\u8c37\u6653\u9759\u5df2\u7ecf\u8d70\u4e86\u3002\u4f0a\u98ce\u62ac\u8d77\u5934\uff0c\u671b\u89c1\u7684\u662f\u8427\u5357\u82f9\u90a3\u4e00\u53cc\u6e29\u67d4\u800c\u542b\u60c5\u7684\u773c\u775b\uff0c\u76ee \u5149\u4e2d\u7684\u5173\u6ce8\uff0c\u4f7f\u5f97\u4ed6\u4e0d\u7981\u5fae\u7b11\u4e00\u4e0b\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u5c71\u8170\u5904\u98d8\u4e0b\u51e0\u54cd\u949f\u58f0\uff0c\u8427\u5357\u82f9\u6084\u7136\u8d70\u524d\u4e00\u6b65\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u8be5\u4e0a\u5c71\u4e86\u5427\uff1f\u201d \u3000\u3000\u5ffd\u53c8\u653e\u4f4e\u4e86\u58f0\u8c03\uff1a \u3000\u3000\u201c\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u8ba9\u4f60\u65e0\u7f18\u65e0\u6545\u60f9\u4e0a\u8fd9\u573a\u9ebb\u70e6\u3002\u53ef\u662f\u771f\u662f\u5947\u602a\uff0c\u5929\u4e0b\u7adf\u6709\u8fd9\u4e48\u5de7\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u600e\u4e48\u80fd\u602a\u5f97\u4e86\u4f60\u2014\u2014\u201d\u4f0a\u98ce\u53c8\u5fae\u7b11\u4e00\u4e0b\uff0c\u5583\u5583\u5730\u8bf4\u9053\u3002 \u3000\u3000\u773c\u89d2\u52a8\u5904\uff0c\u5374\u89c1\u56db\u5468\u7684\u4eba\u5168\u90fd\u5df2\u6563\u5149\u4e86\u3002 \u3000\u3000\u8fdc\u8fdc\u4e00\u4e2a\u8eab\u7a7f\u84dd\u8272\u9053\u888d\u7684\u5e74\u8f7b\u9053\u4eba\uff0c\u6b63\u7f13\u6b65\u5411\u4ed6\u4eec\u884c\u6765\uff0c\u4e00\u9762\u62db\u624b\u9065\u547c\u9053\uff1a \u3000\u3000\u201c\u655d\u6d3e\u63a8\u9009\u638c\u95e8\u4eba\u4e4b\u4f1a\uff0c\u5df2\u7ecf\u5f00\u59cb\u4e86\uff0c\u4e24\u4f4d\u82e5\u4e5f\u662f\u6765\u53c2\u52a0\u6b64\u4f1a\u7684\uff0c\u5c31\u8bf7\u5feb\u4e9b\u4e0a\u5c71\u5427\u3002\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u5c71\u4e0a\u53c8\u4f20\u4e0b\u51e0\u54cd\u949f\u58f0\uff0c\u8885\u8885\u5a1c\u5a1c\uff0c\u4f59\u97f3\u4e0d\u6563\u3002 \u3000\u3000\u4f0a\u98ce\u8fde\u5fd9\u8c22\u8fc7\u4e86\u90a3\u5e74\u8f7b\u9053\u4eba\uff0c\u548c\u8427\u5357\u82f9\u5e76\u80a9\u4e0a\u5c71\u3002\u53ea\u56e0\u8427\u5357\u82f9\u6b64\u523b\u4ecd\u662f\u7537\u88c5\uff0c\u662f\u4ee5\u4ed6\u4eec \u4e5f\u4e0d\u987b\u52a0\u4ee5\u987e\u5fcc\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u6bb5\uff0c\u53c8\u770b\u89c1\u4e00\u4e2a\u9053\u4eba\u8fce\u9762\u800c\u6765\uff0c\u5411\u7740\u4ed6\u4eec\u5f2f\u8170\u4e3a\u793c\uff0c\u4e00\u9762\u5355\u638c\u6253\u7740\u95ee\u8baf\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u65bd\u4e3b\u662f\u90a3\u91cc\u6765\u7684\uff1f\u8981\u4e0d\u8981\u8d2b\u9053\u63a5\u5f15\u4e24\u4f4d\u4e0a\u5c71\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u89c1\u8fd9\u9053\u4eba\u5e74\u7eaa\u4e5f\u4e0d\u5927\uff0c\u5fc3\u4e2d\u5fae\u52a8\u4e86\u52a8\uff0c\u53e3\u4e2d\u5374\u8fde\u5fd9\u7b54\u9053\uff1a \u3000\u3000\u201c\u4e0d\u6562\u6709\u52b3\u9053\u957f\uff0c\u5c0f\u9119\u81ea\u4f1a\u4e0a\u53bb\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u671b\u4e86\u4ed6\u4e24\u773c\uff0c\u773c\u4e2d\u4f3c\u4e4e\u9732\u51fa\u4e00\u79cd\u8ff7\u60d8\u7684\u795e\u8272\uff0c\u53e3\u4e2d\u8bfa\u8bfa\u8fde\u58f0\uff0c\u8ff3\u81ea\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u524d\u9762\u662f\u4e00\u5904\u5c71\u6e7e\uff0c\u5c71\u58c1\u4e0b\u653e\u7740\u4e00\u4e2a\u67b6\u5b50\uff0c\u67b6\u5b50\u4e0a\u653e\u7740\u4e00\u4e2a\u8336\u6876\uff0c\u6b63\u6709\u4e00\u4e2a\u5e74\u8f7b\u7684\u9053\u4eba\uff0c \u624b\u5fd9\u811a\u4e71\u5730\u5f80\u91cc\u9762\u5012\u7740\u8336\uff0c\u770b\u89c1\u4f0a\u98ce\u548c\u8427\u5357\u82f9\u4e24\u4eba\u8d70\u6765\uff0c\u8138\u4e0a\u542b\u7740\u7b11\u5bb9\uff0c\u6253\u7740\u62db\u547c\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u9119\u8981\u559d\u676f\u8336\u518d\u4e0a\u5c71\uff1f\u201d \u3000\u3000\u4f0a\u7b11\u7740\u8c22\u4e86\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u52a8\u3002 \u3000\u3000\u5374\u89c1\u53c8\u6709\u4e24\u4e2a\u5e74\u8f7b\u7684\u9053\u4eba\uff0c\u81ea\u5c71\u4e0a\u75be\u6b65\u8d70\u4e86\u4e0b\u6765.\u8eab\u4e0a\u7a7f\u7740\u5d2d\u65b0\u7684\u84dd\u8272\u9053\u888d\uff0c\u5411\u4f0a\u98ce\u7b11 \u7740\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u8198\u4e0a\u5c71\u5427\uff0c\u5927\u4f1a\u6b64\u523b\u5df2\u5f00\u59cb\u4e86\u5462\uff01\u201d \u3000\u3000\u4f0a\u98ce\u518d\u5f80\u5c71\u4e0a\u8d70\u7684\u65f6\u5019\uff0c\u5fc3\u4e2d\u7591\u5ff5\u987f\u751f\uff0c\u6697\u5730\u601d\u5fd6\u7740\u9053\uff1a \u3000\u3000\u201c\u4ee5\u8fd9\u51e0\u4e2a\u9053\u4eba\u7684\u5e74\u9f84\uff0c\u548c\u4ed6\u4eec\u811a\u4e0b\u6240\u663e\u793a\u7684\u6b66\u529f\u6765\u8bf4\uff0c\u4ed6\u4eec\u6700\u591a\u4e0d\u8fc7\u662f\u638c\u95e8\u4eba\u4e0b\u7684\u7b2c \u4e09\u4ee3\u5f1f\u5b50\u3002\u4f46\u90a3\u5999\u7075\u9053\u4eba\u5374\u5f77\u4f5b\u8bf4\u8fc7\uff0c\u4ed6\u95e8\u4e0b\u7684\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u5168\u56e0\u529f\u529b\u4e0d\u6df1\uff0c\u4e2d\u6bd2\u4e4b\u540e\uff0c\u5927 \u591a\u906d\u4e86\u6bd2\u624b\uff0c\u90a3\u4e48\u4e3a\u4ec0\u4e48\u53c8\u4f1a\u6709\u5982\u6b64\u591a\u5e74\u8f7b\u7684\u9053\u4eba\u2014\u2014\u201d \u3000\u3000\u6b63\u601d\u5fd6\u95f4\uff0c\u53c8\u6709\u4e24\u4e2a\u5e74\u8f7b\u9053\u4eba\u5e76\u80a9\u800c\u6765\uff0c\u671d\u7740\u4f0a\u98ce\u542b\u7b11\u800c\u8fc7\u3002 \u3000\u3000\u8427\u5357\u82f9\u671b\u4e86\u4ed6\u4eec\u51e0\u773c\uff0c\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u9053\u4eba\u600e\u7684\u5168\u7a7f\u7740\u65b0\u9053\u888d\uff1f\u800c\u4e14\u4e00\u4e2a\u4e2a\u559c\u6c14\u6d0b\u6d0b\u7684\uff0c\u90a3\u50cf\u662f\u521a\u521a\u6b7b\u4e86\u638c\u95e8\u4eba\u7684\u6837 \u5b50\uff1f\u770b\u6765\u8fd9\u7ec8\u5357\u9053\u58eb\uff0c\u50cf\u662f\u4e0d\u5927\u5b88\u6e05\u89c4\u54e9\uff01\u201d \u3000\u3000\u5973\u4eba\u5bb6\u5bf9\u522b\u4eba\u7684\u8863\u7740\u7684\u65b0\u65e7\uff0c\u6c38\u8fdc\u662f\u6bd4\u7537\u5b50\u7559\u610f\u7684\u3002 \u3000\u3000\u4f0a\u98ce\u542c\u4e86\uff0c\u5fc3\u4e2d\u53c8\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u9053\u4eba\u770b\u6765\uff0c\u771f\u6709\u4e9b\u53ef\u7591\u3002\u201d\u5ff5\u5934\u4e00\u8f6c\uff0c\u7a81\u7136\u5411\u8427\u5357\u82f9\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u521a\u624d\u90a3\u4e24\u4e2a\u9053\u4eba\u79f0\u547c\u6211\u4eec\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u6c89\u541f\u534a\u664c\uff0c\u4e5f\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u8fd9\u771f\u6709\u4e9b\u900f\u7740\u5947\u602a\uff0c\u521a\u624d\u90a3\u51e0\u4e2a\u9053\u4eba\u5e76\u6ca1\u6709\u53eb\u6211\u4eec\u201c\u65bd\u4e3b\u201d\uff0c\u800c\u662f\u5c06\u6211\u4eec\u79f0\u505a \u201c\u670b\u53cb\u201d\uff0c\u96be\u9053\u8fd9\u4e9b\u9053\u58eb\u7a7f\u5728\u8eab\u4e0a\u7684\u9053\u888d\uff0c\u53ea\u662f\u88c5\u88c5\u6837\u5b50\u7684\uff1f\u201d\u7a0d\u5fae\u987f\u4e86\u4e00\u4e0b\uff0c\u5979\u53c8\u63a5\u7740 \u9053\uff1a \u3000\u3000\u201c\u5982\u679c\u8fd9\u7ec8\u5357\u6d3e\u4e0d\u662f\u6b66\u6797\u7d20\u8d1f\u6e05\u8a89\u7684\u95e8\u6d3e\u7684\u8bdd\uff0c\u90a3\u4e48\u6211\u771f\u8981\u7591\u5fc3\u8fd9\u4e9b\u5c0f\u9053\u58eb\u7684\u9053\u888d\uff0c\u662f \u4eca\u5929\u624d\u7a7f\u4e0a\u8eab\u7684\uff0c\u6628\u5929\u4ed6\u4eec\u8fd8\u662f\u7eff\u6797\u4e2d\u7684\u5c0f\u55bd\u7f57\uff1f\u201d \u3000\u3000\u5657\u54e7\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u771f\u4e0d\u662f\u9a82\u4ed6\u4eec\uff0c\u4f60\u770b\u4ed6\u4eec\u9664\u4e86\u90a3\u8eab\u9053\u888d\u4e4b\u5916\uff0c\u4ece\u5934\u5230\u811a\uff0c\u90a3\u91cc\u8fd8\u6709\u4e00\u70b9\u513f\u7384\u95e8\u4e2d\u4eba \u7684\u6837\u5b50\uff1f\u201d\u4f0a\u98ce\u76b1\u7740\u53cc\u7709\uff0c\u5fc3\u91cc\u65e2\u7591\u60d1\uff0c\u53c8\u62c5\u5fc3\uff0c\u4e0d\u77e5\u9053\u5728\u4ed6\u81ea\u5df1\u8fdc\u8d74\u6ec7\u4e2d\uff0c\u4e3a\u90a3\u4e9b\u7ec8\u5357\u95e8 \u4e0b\u4e2d\u6bd2\u7684\u5f1f\u5b50\u6c42\u53d6\u89e3\u836f\u7684\u65f6\u5019\uff0c\u7ec8\u5357\u5c71\u4e0a\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u600e\u5730\u90a3\u638c\u95e8\u4eba\u5999\u7075\u53c8\u7a81\u7136\u6b7b\u4e86\uff1f\u53c8 \u4e0d\u77e5\u9053\u5251\u5148\u751f\u548c\u5b59\u654f\u6bcd\u5973\u7b49\u4eba\uff0c\u6b64\u523b\u8fd8\u5728\u4e0d\u5728\u5c71\u4e0a\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u52a0\u5feb\u4e86\u6b65\u5b50\uff0c\u53c8\u8f6c\u8fc7\u51e0\u5904\u5c71\u6e7e\uff0c\u6bcf\u4e00\u5904\u5c71\u6e7e\u7684\u5c71\u58c1\u4e0b\uff0c\u90fd\u653e\u6709\u8336\u6c34\u67b6\u5b50\uff0c\u4e5f\u90fd\u6709 \u4e00\uff0c\u4e24\u4e2a\u5e74\u8f7b\u7684\u9053\u4eba\uff0c\u5728\u65c1\u8fb9\u5b88\u671b\u7740\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u7591\u60d1\uff0c\u5374\u4e5f\u6ca1\u6709\u5411\u8fd9\u51e0\u4e2a\u9053\u4eba\u8be2\u95ee\uff0c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u6b64\u4e8b\u770b\u6765\u6709\u4e9b\u8e4a\u8df7\u3002 \u3000\u3000\u4ed6\u5e0c\u671b\u5251\u5148\u751f\u7b49\u4eba\uff0c\u6b64\u523b\u4ecd\u5728\u5c71\u4e0a\uff0c\u90a3\u4e48\u81ea\u5df1\u5fc3\u4e2d\u7684\u7591\u56e2\uff0c\u4fbf\u53ef\u8fce\u5203\u800c\u89e3\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u6b65\u5c65\u4e4b\u95f4\uff0c\u4e5f\u5c31\u8d8a\u53d1\u52a0\u5feb\u3002 \u3000\u3000\u8427\u5357\u82f9\u7d27\u8d70\u5728\u4ed6\u65c1\u8fb9\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u5fc3\u4e2d\u6240\u5fd6\u4e4b\u4e8b\uff0c\u4e5f\u65e0\u6cd5\u4ece\u4ed6\u7684\u9762\u5b54\u4e0a\u7684\u8868\u60c5\u6765\u63a8\u6d4b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u81ea\u4ece\u6234\u4e0a\u4e86\u90a3\u4eba\u76ae\u7684\u9762\u5177\u4e4b\u540e\uff0c\u4ed6\u8138\u4e0a\u7684\u53d8\u5316\uff0c\u522b\u4eba\u5c31\u6839\u672c\u65e0\u6cd5\u518d\u770b\u51fa\u6765\u4e86c \u3000\u3000\u518d\u8f6c\u8fc7\u4e00\u5904\u5c71\u6e7e\uff0c\u524d\u9762\u5c31\u662f\u90a3\u53bb\u9053\u89c2\u524d\u4e1b\u6797\u4e86\uff0c\u4f0a\u98ce\u5306\u5306\u8d70\u4e86\u8fdb\u53bb\u3002\u4e00\u8fdb\u4e1b\u6797\uff0c\u4fbf\u89c1\u9053 \u89c2\uff0c\u9053\u89c2\u524d\u4e24\u6247\u6731\u7ea2\u7684\u5927\u95e8\uff0c\u6b64\u523b\u6d1e\u5f00\u7740\uff0c\u89c2\u95e8\u524d\u5782\u624b\u800c\u7acb\u7684\uff0c\u5374\u662f\u4e00\u4e2a\u9894\u4e0b\u5fae\u9aed\u7684\u4e2d\u5e74\u9053 \u4eba\u3002 \u3000\u3000\u4f0a\u98ce\u601d\u5fd6\u4e86\u4e00\u4e0b\uff0c\u7b14\u76f4\u5730\u671d\u4ed6\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u9053\u4eba\u5355\u638c\u6253\u7740\u95ee\u8baf\uff0c\u795e\u6001\u4e4b\u95f4\uff0c\u8fdc\u6bd4\u90a3\u91cc\u5e74\u8f7b\u7684\u9053\u4eba\u8083\u7a46\uff0c\u770b\u5230\u4f0a\u98ce\u884c\u6765\uff0c\u606d\u58f0\u9053\uff1a \u3000\u3000\u201c\u65bd\u4e3b\u8bf7\u81f3\u5415\u7956\u6bbf\u53bb\uff0c\u6b64\u523b\u5927\u4f1a\u65b9\u5f00\uff0c\u65bd\u4e3b\u8fd8\u8d76\u5f97\u53ca\u3002\u201d \u3000\u3000\u4f0a\u98ce\u8fde\u5fd9\u8fd8\u793c\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u53ef\u66fe\u77e5\u9053\u8d35\u89c2\u4e2d\u539f\u5148\u6709\u56db\u4e2a\u501f\u5bbf\u4e4b\u4eba\uff0c\u4e24\u7537\u4e24\u5973\uff0c\u6b64\u523b\u8fd8\u5728\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4ecd\u6709\u987e\u8651\uff0c\u56e0\u6b64\u6ca1\u6709\u8bf4\u51fa\u201c\u5251\u5148\u751f\u201d\u7b49\u4eba\u7684\u540d\u53f7\u3002 \u3000\u3000\u8fd9\u4e2d\u5e74\u9053\u4eba\u4e0a\u4e0b\u6253\u91cf\u4e86\u4f0a\u98ce\u51e0\u773c\uff0c\u6001\u5ea6\u53d8\u5f97\u66f4\u4e3a\u606d\u8c28\uff0c\u9053\uff1a \u3000\u3000\u201c\u65bd\u4e3b\u53ef\u5c31\u662f\u5c06\u655d\u6d3e\u6570\u767e\u5f1f\u5b50\u6551\u51fa\u751f\u5929\u7684\u90a3\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u5730\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u90a3\u4e24\u4f4d\u8001\u524d\u8f88\u591a\u65e5\u524d\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u7684\u5fc3\u5f80\u4e0b\u4e00\u6c89\uff0c\u6025\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u53ef\u77e5\u9053\u4ed6\u4eec\u8d70\u4e86\u591a\u4e45\uff1f\u5f80\u90a3\u91cc\u53bb\u4e86\uff1f\u53ef\u66fe\u7559\u4e0b\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u8fd9\u9053\u4eba\u6447\u4e86\u6447\u5934\uff0c\u53f9\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u82e5\u77e5\u9053\u4ed6\u4eec\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u53bb\u5904\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u5ffd\u5730\u5c06\u4f0a\u98ce\u62d6\u5230\u89c2\u95e8\u524d\u7684\u9634\u5f71\u4e0b\uff0c\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u65bd\u4e3b\u65e2\u7136\u662f\u90a3\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u670b\u53cb\uff0c\u4e5f\u8bb8\u5c31\u77e5\u9053\u655d\u6d3e\u7684\u638c\u95e8\u4eba\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u5bf9\u4e8e\u8fd9\u4ef6 \u4e8b\uff0c\u655d\u6d3e\u7684\u4e0a\u4e0b\u51e0\u4ee3\u5f1f\u5b50\u90fd\u4f24\u5fc3\u5f97\u5f88\uff01\u503c\u6b64\u975e\u5e38\u4e4b\u9645\uff0c\u662f\u4ee5\u655d\u6d3e\u624d\u4e00\u53cd\u591a\u5e74\u7684\u4f20\u7edf\uff0c\u800c\u4e3e\u884c \u8fd9\u516c\u63a8\u638c\u95e8\u4eba\u4e4b\u4f1a\uff0c\u53ea\u8981\u662f\u655d\u6d3e\u5f1f\u5b50\uff0c\u65e0\u8bba\u662f\u7b2c\u51e0\u4ee3\u7684\uff0c\u90fd\u53ef\u4ee5\u51ed\u7740\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u6765\u4e89\u53d6\u8fd9 \u638c\u95e8\u4eba\u4e4b\u4f4d\uff0c\u90a3\u77e5\u2014\u2014\u201d \u3000\u3000\u4ed6\u5306\u5306\u8bf4\u5230\u6b64\u5904\uff0c\u7adf\u7a81\u5730\u987f\u4f4f\u4e86\u3002\u4f0a\u98ce\u773c\u89d2\u5fae\u779f\uff0c\u671b\u89c1\u6709\u4e24\u4e2a\u9053\u4eba\u6b63\u5927\u6b65\u884c\u6765\uff0c\u671d\u7740\u81ea \u5df1\u8eac\u8eab\u65bd\u7740\u793c\uff0c\u4e00\u9762\u7b11\u9053\uff1a \u3000\u3000\u201c\u5927\u4f1a\u5df2\u5f00\u59cb\u4e86\uff0c\u91cc\u9762\u70ed\u95f9\u5f97\u5f88\uff0c\u65bd\u4e3b\u4eec\u600e\u7684\u8fd8\u4e0d\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u7ad9\u5728\u4f0a\u98ce\u65c1\u8fb9\uff0c\u7adf\u4e0d\u8d70\u4e86\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u9053\u4eba\u4e5f\u4e0d\u518d\u8bf4\u8bdd\uff0c\u8eac\u8eab\u5411\u5185\u8083\u5bb9\uff0c\u8138\u4e0a\u7adf\u4f3c\u9690\u6cdb\u6101\u5bb9\u3002 \u3000\u3000\u4f0a\u98ce\u53ea\u5f97\u9886\u7740\u8427\u5357\u82f9\u8d70\u8fdb\u53bb\uff0c\u5fc3\u4e2d\u66f4\u662f\u5927\u60d1\u4e0d\u89e3\uff1a\u201c\u4e3a\u4ec0\u4e48\u542c\u8fd9\u9053\u4eba\u7684\u53e3\u6c14\uff0c\u4ed6\u4eec\u638c\u95e8 \u4e4b\u6b7b\uff0c\u4f3c\u4e4e\u53e6\u6709\u6587\u7ae0\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u7684\u8bdd\u8bf4\u81f3\u4e00\u534a\uff0c\u770b\u5230\u6709\u522b\u7684\u9053\u4eba\u8d70\u6765\uff0c\u4fbf\u500f\u7136\u987f\u4f4f\uff1f\u5509\uff01\u53ea \u602a\u6211\u4e3a\u4ec0\u4e48\u8981\u5728\u90a3\u59da\u6e05\u5b87\u5bb6\u505c\u7559\u8fd9\u4e9b\u5929\uff0c\u4e0d\u4f46\u89c1\u4e0d\u7740\u5251\u5148\u751f\u548c\u5b59\u654f\u6bcd\u5973\uff0c\u53c8\u591a\u51fa\u8fd9\u4e9b\u4e8b \u6545\u3002\u201d \u3000\u3000\u4ed6\u6697\u81ea\u8c34\u8d23\u7740\u81ea\u5df1\uff0c\u5fc3\u91cc\u53c8\u7740\u6025\uff0c\u4e0d\u77e5\u9053\u5251\u5148\u751f\u7b49\u4eba\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u5fc3\u4e2d\u6c89\u5fd6\u95f4\uff0c\u5df2\u8d70\u5230 \u5927\u6bbf\u95e8\u53e3\uff0c\u63a2\u76ee\u5411\u5185\u4e00\u671b\uff0c\u53ea\u89c1\u8fd9\u65b9\u5706\u5341\u4f59\u4e08\u7684\u5927\u6bbf\uff0c\u56db\u4fa7\u90fd\u5750\u6ee1\u4e86\u4eba\uff0c\u9ed1\u538b\u538b\u5730\u4e00\u7247\uff0c\u4ed6 \u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e5f\u4e0d\u53bb\u6ce8\u610f\u8fd9\u4e9b\u4eba\u7684\u9762\u8c8c\uff0c\u6084\u7136\u7ed5\u8fc7\u6b63\u95e8\uff0c\u4ece\u6bbf\u4fa7\u7684\u4e00\u6247\u5c0f\u95e8\u4e2d\u8d70\u4e86\u8fdb\u53bb\uff0c\u6084\u7136 \u5750\u5728\u9760\u5899\u4e4b\u5904\u3002 \u3000\u3000\u6b64\u523b\u6bbf\u4e2d\u8bf8\u4eba\uff0c\u773c\u5149\u90fd\u5728\u6ce8\u610f\u7740\u7ad9\u5728\u5927\u6bbf\u795e\u9f9b\u524d\u7684\u4e00\u4e2a\u8001\u8005\u8eab\u4e0a\uff0c\u90fd\u6ca1\u6709\u7559\u610f\u4f0a\u98ce\u7684\u8fdb \u6765\uff0c\u5374\u542c\u90a3\u8001\u8005\u6b63\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u591a\u5e74\u6765\u672a\u66fe\u6d89\u8db3\u6c5f\u6f6e\uff0c\u60f3\u4e0d\u5230\u5404\u4f4d\u670b\u53cb\u4ecd\u672a\u5fd8\u8bb0\u6211\u3002\u201d \u3000\u3000\u4ed6\u6717\u58f0\u4e00\u7b11\uff1a \u3000\u3000\u201c\u5404\u4f4d\u65e2\u7136\u8981\u63a8\u6211\u8001\u5934\u5b50\u6765\u505a\u6b64\u4f1a\u4e4b\u4e3b\u6301\uff0c\u8001\u592b\u5374\u4e4b\u4e0d\u606d\uff0c\u53ea\u5f97\u539a\u7740\u8001\u8138\u51fa\u6765\u505a\u4e86\u3002\u53ea \u662f\u5404\u4f4d\u90fd\u77e5\u9053\u6b64\u4f1a\u5e76\u975e\u5bfb\u5e38\uff0c\u8001\u592b\u4e00\u4e2a\u4eba\u6050\u6015\u62c5\u5f53\u4e0d\u4e0b\u6765\uff0c\u5404\u4f4d\u6700\u597d\u518d\u63a8\u51fa\u51e0\u4eba\uff0c\u4e0d\u7136\u8001\u592b \u8001\u773c\u660f\u82b1\uff0c\u5bf9\u7ec8\u5357\u9053\u4eba\u7684\u8eab\u624b\uff0c\u672a\u5fc5\u770b\u5f97\u6e05\u695a\u54e9\u3002\u201d \u3000\u3000\u8bf4\u7f62\u53c8\u6717\u58f0\u4e00\u7b11\uff0c\u610f\u6c14\u4e4b\u95f4\uff0c\u751a\u662f\u81ea\u8c6a\u3002 \u3000\u3000\u4f0a\u98ce\u770b\u5230\u8fd9\u8001\u8005\uff0c\u5374\u4e0d\u8ba4\u5f97\uff0c\u5fc3\u4e2d\u5374\u5df2\u731c\u5230\u8fd9\u8001\u8005\u5927\u7ea6\u662f\u88ab\u8bf8\u4eba\u63a8\u4e3e\u51fa\u6765\uff0c\u4f5c\u8fd9\u4ee5\u6b66\u529f \u4e89\u638c\u95e8\u7684\u5927\u4f1a\u4e0a\uff0c\u7ec8\u5357\u5f1f\u5b50\u4eec\u8f83\u6280\u65f6\u7684\u516c\u6b63\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u8001\u8005\u4e00\u8bf4\u5b8c\u8bdd\uff0c\u5927\u6bbf\u4e0a\u7684\u8bf8\u4eba\u7acb\u523b\u8d77\u4e86\u4e00\u9635\u9a9a\u52a8\uff0c\u60f3\u5fc5\u662f\u5728\u63a8\u4e3e\u53e6\u4e8c\u4eba\u3002 \u3000\u3000\u4f0a\u98ce\u653e\u773c\u56db\u671b\uff0c\u770b\u5230\u8fd9\u5927\u6bbf\u4e0a\u5de6\uff0c\u53f3\u4e24\u4fa7\uff0c\u53ca\u6b63\u9762\u90fd\u5750\u6ee1\u4e86\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u6b63\u81ea\u4ea4\u5934\u4f4e\u8bed\uff0c \u795e\u9f9b\u7684\u540e\u9762\u4e24\u4fa7\uff0c\u5374\u7ad9\u6ee1\u4e86\u7a7f\u7740\u84dd\u888d\u7684\u9053\u4eba\uff0c\u60f3\u5fc5\u5c31\u662f\u7ec8\u5357\u6d3e\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u4f0a\u98ce\u6b63\u81ea\u89c2\u671b\u95f4\uff0c\u5374\u89c1\u8427\u5357\u82f9\u4e00\u62c9\u81ea\u5df1\u7684\u8896\u5b50\uff0c\u5728\u8033\u7554\u8f7b\u58f0\u9053\uff1a \u3000\u3000\u201c\u5357\u54e5\uff01\u8fd9\u8001\u5934\u5b50\u5c31\u662f\u5f62\u610f\u6d3e\u7684\u540d\u5bbf\uff0c\u516b\u5366\u795e\u638c\u8303\u5fe1\u5e73\uff0c\u60f3\u4e0d\u5230\u4ed6\u4e5f\u4f1a\u5728\u7ec8\u5357\u5c71\u4e0a\u51fa \u73b0\u3002\u5357\u54e5\uff01\u4f60\u8ba4\u5f97\u4ed6\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6447\u4e86\u6447\u5934\uff0c\u968f\u53e3\u7b54\u9053\uff1a \u3000\u3000\u201c\u6211\u867d\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4ed6\u7684\u540d\u5b57\u6211\u5012\u95fb\u540d\u5df2\u4e45\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u5374\u4ecd\u5728\u56db\u4e0b\u626b\u52a8\u7740\uff0c\u5374\u89c1\u5927\u6bbf\u4e0a\u7684\u7fa4\u8c6a\uff0c\u867d\u7136\u8bae\u8bba\u7eb7\u7eb7\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u518d\u63a8\u51fa\u4e00\u4eba \u6765\uff0c\u60f3\u5fc5\u662f\u8fd9\u4e9b\u4eba\u91cc\uff0c\u518d\u65e0\u4e00\u4eba\u7684\u58f0\u671b\uff0c\u80fd\u4ee5\u670d\u4f17\u7684\u3002 \u3000\u3000\u90a3\u8303\u4ef2\u5e73\u7ad9\u5728\u795e\u9f9b\u524d\uff0c\u9762\u542b\u5fae\u7b11\uff0c\u795e\u6001\u9887\u4e3a\u81ea\u5f97\u3002\u4f0a\u98ce\u77e5\u9053\uff0c\u6b64\u8001\u6709\u540d\u7684\u81ea\u8d1f\uff0e\u597d\u540d\uff0c \u4f46\u624b\u4e0b\u4e5f\u9887\u6709\u51e0\u5206\u529f\u592b\uff0c\u786e\u975e\u5f92\u62e5\u865a\u540d\u4e4b\u8f88\u3002 \u3000\u3000\u534a\u664c\uff0c\u5927\u6bbf\u5de6\u4fa7\u7fa4\u8c6a\u4e2d\u7a81\u6709\u4e00\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u56db\u5468\u4e00\u62f1\u624b\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u63a8\u4e3e\u4e00\u4eba\uff0c\u6b64\u4eba\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u65e0\u8bba\u58f0\u671b\uff0c\u6b66\u529f\uff0c\u90fd\u8db3\u4ee5\u62c5\u6b64\u91cd\u4efb\u3002\u201d \u3000\u3000\u4ed6\u624b\u671d\u5927\u6bbf\u53f3\u4fa7\u7684\u77f3\u67f1\u4e0b\u4e00\u6307\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u8981\u63a8\u4e3e\u7684\uff0c\u5c31\u662f\u6b64\u523b\u7ad9\u5728\u90a3\u8fb9\u77f3\u67f1\u4e0b\u7684\u6885\u82b1\u5251\u675c\u957f\u537f\u675c\u5927\u4fa0\u3002\u201d \u3000\u3000\u4ed6\u54c8\u54c8\u4e00\u7b11\uff1a \u3000\u3000\u201c\u81ea\u4ece\u94c1\u621f\u6e29\u4faf\u5415\u5357\u4eba\u4fdd\u5b9a\u57ce\u5916\u6b7b\u540e\uff0c\u82b8\u82b8\u6b66\u6797\u4e2d\uff0c\u8fd8\u6709\u8c01\u6bd4\u5f97\u4e0a\u675c\u5927\u4fa0\u7684\u5e74\u5c11\u633a\u9038\uff0c \u6b66\u529f\u9ad8\u5f3a\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u8bf4\u5b8c\uff0c\u4f17\u4eba\u4e4b\u95f4\uff0c\u7acb\u523b\u6709\u4eba\u54c4\u7136\u79f0\u597d\u3002 \u3000\u3000\u4f0a\u98ce\u5374\u542c\u5f97\u8eab\u7554\u7684\u8427\u5357\u82f9\u8f7b\u58f0\u4e00\u7b11\uff0c\u81ea\u5df1\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u559f\u7136\uff01\u8fd9\u6885\u82b1\u5251\u675c\u957f\u537f\u4e43\u5ce8\u5d4b\u95e8 \u4e0b\uff0c\u540e\u8d77\u4e00\u4ee3\u5251\u5ba2\u4e2d\u7684\u4f7c\u4f7c\u8005\u3002\u6614\u5e74\u4e0e\u6b66\u5f53\u7684\u540e\u8d77\u9ad8\u624b\u5165\u4e91\u9e64\u53e4\u5b50\u6602\uff0c\u548c\u4f0a\u98ce\u81ea\u5df1\u2014\u2014\u94c1\u621f \u6e29\u4faf\u5415\u5357\u4eba\u2014\u2014\u540c\u8d1f\u65f6\u540d\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e09\u4eba\u4e0d\u4f46\u5e74\u9f84\u76f8\u82e5\uff0c\u6b66\u529f\u90fd\u5f97\u81ea\u771f\u4f20\uff0c\u800c\u4e14\u8fd8\u90fd\u662f\u6d4a\u4e16\u4e2d\u7684\u4f73\u516c\u5b50\uff0c\u751f\u5f97\u4e00\u8868\u4eba \u6750\u3002 \u3000\u3000\u6b64\u523b\u4f0a\u98ce\u9aa4\u7136\u542c\u5230\u81ea\u5df1\u7684\u540d\u5b57\u53c8\u88ab\u63d0\u8d77\uff0c\u5fc3\u4e2d\u81ea\u7136\u96be\u514d\u611f\u6168\uff01 \u3000\u3000\u53ea\u662f\u6b64\u523b\u8c01\u4e5f\u4e0d\u4f1a\u77e5\u9053\uff0c\u5750\u5728\u8fd9\u9634\u6697\u89d2\u843d\u91cc\u7684\u6c49\u5b50\uff0c\u5c31\u662f\u94c1\u621f\u6e29\u4faf\u3002 \u3000\u3000\u7fa4\u8c6a\u4e00\u9635\u9a9a\u52a8\u540e\uff0c\u679c\u771f\u5c31\u628a\u6885\u82b1\u5251\u675c\u957f\u537f\u63a8\u4e3e\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u6885\u82b1\u5251\u675c\u957f\u537f\u957f\u8eab\u7389\u7acb\uff0c\u9762\u5982\u51a0\u7389\uff0c\u957f\u5251\u6302\u5728\u8170\u7554\uff0c\u6b64\u523b\u8fde\u58f0\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u5e74\u8f7b\u8bc6\u6d45\uff0c\u600e\u62c5\u5f53\u5f97\u8d77\u5982\u6b64\u91cd\u4efb\uff01\u201d \u3000\u3000\u4f46\u8fd8\u662f\u88ab\u4f17\u8c6a\u54c4\u4e86\u4e0a\u53bb\uff0c\u7ad9\u5728\u90a3\u516b\u5366\u795e\u638c\u8303\u4ef2\u5e73\u7684\u8eab\u4fa7\uff0c\u795e\u6001\u6f47\u6d12\u4ece\u5bb9\uff0c\u4e1d\u6beb\u6ca1\u6709\u4e0d\u5b89 \u7684\u6837\u5b50\u3002 \u3000\u3000\u516b\u5366\u795e\u638c\u8303\u5fe1\u5e73\u53c8\u6717\u58f0\u5927\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u597d\u6781\uff01\u597d\u6781\uff01\u6c5f\u5c71\u4ee3\u6709\u624d\u5165\u51fa\uff0c\u8001\u592b\u773c\u770b\u7740\u540e\u8d77\u9ad8\u624b\u6210\u540d\u7acb\u4e07\uff0c\u6700\u662f\u9ad8\u5174\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\uff0c\u53c8\u5411\u6885\u82b1\u5251\u675c\u957f\u537f\u9053\uff1a \u3000\u3000\u201c\u4ee4\u5e08\u96ea\u56e0\u5927\u5e08\uff0c\u548c\u8001\u592b\u6614\u5e74\u672c\u662f\u65b9\u5916\u4e4b\u81f3\u4ea4\u3002\u5982\u4eca\u675c\u5c11\u4fa0\u4e5f\u5df2\u957f\u6210\uff0c\u5802\u5802\u4e00\u8868\u4eba\u624d\uff0c \u5353\u7136\u4e0d\u51e1\uff0c\u6545\u4eba\u6709\u540e\uff0c\u8001\u592b\u771f\u662f\u9ad8\u5174\u5f97\u5f88\uff01\u201d \u3000\u3000\u675c\u957f\u537f\u4e00\u542c\u4eba\u5bb6\u63d0\u5230\u81ea\u5df1\u7684\u5e08\u5085\uff0c\u8d76\u7d27\u5f2f\u4e0b\u8170\u53bb\u65bd\u793c\u3002 \u3000\u3000\u516b\u5366\u795e\u638c\u53f3\u624b\u634b\u7740\u82b1\u767d\u7684\u957f\u987b\uff0c\u8fde\u8fde\u5730\u70b9\u7740\u5934\uff0c\u6717\u58f0\u5730\u5927\u7b11\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u65b9\u81ea\u6168\u7136\uff0c\u5374\u89c1\u8fd9\u8001\u5f53\u76ca\u58ee\u7684\u8303\u4ef2\u5e73\u53c8\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\u5df2\u6709\u6211\u4eec\u8fd9\u8001\u5c11\u4e24\u4eba\uff0c\u5404\u4f4d\u53ea\u8981\u518d\u63a8\u4e3e\u4e00\u4eba\u51fa\u6765\uff0c\u5c31\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u5fae\u9a9a\u52a8\u95f4\uff0c\u5927\u6bbf\u53f3\u4fa7\uff0c\u53c8\u500f\u7136\u7ad9\u8d77\u4e00\u4eba\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u8981\u63a8\u4e3e\u7684\u662f\u4e00\u4f4d\u5fb7\u9ad8\u671b\u91cd\u7684\u8001\u524d\u8f88\uff0c\u5c31\u662f\u6b64\u523b\u5750\u5728\u5728\u4e0b\u8eab\u65c1\u7684\u4e07\u80dc\u5200\u9ec4\u9547\u56fd\u9ec4\u8001 \u82f1\u96c4\u3002\u9ec4\u8001\u82f1\u96c4\u5728\u6d59\u4e1c\u8bbe\u573a\u6388\u5f92\uff0c\u95e8\u4e0b\u53ef\u8c13\u6843\u674e\u6ee1\u95e8\uff0c\u51fa\u6765\u62c5\u6b64\u91cd\u4efb\uff0c\u5b9e\u5728\u518d\u597d\u4e5f\u6ca1\u6709 \u4e86\u3002\u201d \u3000\u3000\u8bdd\u65b9\u8bf4\u5b8c\uff0c\u4ed6\u8eab\u4fa7\u5c31\u7ad9\u8d77\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u8001\u4eba\uff0c\u53cc\u624b\u671d\u56db\u4fa7\u62b1\u7740\u62f3\uff0c\u4f46\u662f\u7fa4\u8c6a\u53cd\u5e94\uff0c\u5374 \u4e0d\u89c1\u70ed\u70c8\uff0c\u53ea\u56e0\u8fd9\u4e07\u80dc\u5200\u9ec4\u9547\u56fd\u867d\u7136\u662f\u4e2a\u8001\u6b66\u5e08\uff0c\u4f46\u5728\u6c5f\u6e56\u95f4\u7684\u201c\u4e07\u513f\u201d\uff0c\u5374\u5e76\u4e0d\u5341\u5206\u54cd \u4eae\u3002 \u3000\u3000\u8fd9\u4e07\u80dc\u5200\u5e74\u7eaa\u867d\u5927\uff0c\u4f46\u5374\u50cf\u662f\u5341\u5206\u597d\u540d\uff0c\u6b64\u523b\u4e0d\u7b49\u522b\u4eba\u518d\u8ba9\uff0c\u5c31\u60f3\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u8427\u5357\u82f9\u65b9\u81ea\u5931\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u8001\u5934\u5b50\u5012\u6709\u8da3\uff0c\u4eba\u5bb6\u8fd8\u4e0d\u600e\u4e48\u6b22\u8fce\u4ed6\uff0c\u4ed6\u5c45\u7136\u81ea\u5df1\u8dd1\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u77e5\u6b63\u9762\u8bf8\u8c6a\u4e2d\uff0c\u7a81\u7136\u6709\u4eba\u51b7\u54fc\u4e86\u4e00\u4e0b\uff0c\u4e00\u4eba\u7b14\u76f4\u8d70\u4e86\u51fa\u6765\uff0c\u773c\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a \u3000\u3000\u201c\u655d\u4eba\u94b1\u7fca\u3002\u655d\u4eba\u8981\u63a8\u4e3e\u7684\uff0c\u5c31\u662f\u533a\u533a\u5728\u4e0b\u81ea\u5df1\uff01\u201d \u3000\u3000\u6b64\u4eba\u8fd9\u4e00\u51fa\u6765\uff0c\u8bf4\u51fa\u8fd9\u756a\u8bdd\uff0c\u8bf8\u8c6a\u4e0d\u7981\u54c4\u7136\u3002\u518d\u52a0\u4e0a\u6b64\u4eba\u770b\u8d77\u6765\u5e74\u7eaa\u4e5f\u751a\u8f7b\uff1b\u4f46\u4e3e\u6b62\u4e4b \u95f4\uff0c\u5374\u5927\u6709\u76ee\u4e2d\u65e0\u4eba\u4e4b\u52bf\u3002 \u3000\u3000\u5148\u524d\u63a8\u4e3e\u4e07\u80dc\u5200\u7684\u90a3\u4e2a\u6c49\u5b50\uff0c\u6b64\u523b\u4e5f\u8dd1\u51fa\u6765\uff0c\u6307\u7740\u8fd9\u5c11\u5e74\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\u662f\u4f55\u65b9\u9ad8\u4eba\uff1f\uff1f\u6211\u5c0f\u9738\u738b\u8d70\u5357\u95ef\u5317\uff0c\u8fd8\u6ca1\u6709\u770b\u89c1\u6709\u50cf\u9601\u4e0b\u8fd9\u6837\u4e00\u53f7\u4eba\u7269\uff0c\u670b\u53cb\u81ea \u5df1\u4ee5\u4e3a\u81ea\u5df1\u662f\u8c01\uff0c\u96be\u9053\u6ca1\u6709\u5c06\u9ec4\u8001\u82f1\u96c4\u770b\u5728\u773c\u91cc\uff01\u201d\u90a3\u81ea\u79f0\u201c\u94b1\u7fca\u201d\u7684\u5c11\u5e74\uff0c\u4ecd\u7136\u5353\u7acb\uff0c\u6839 \u672c\u6ca1\u6709\u770b\u8fd9\u201c\u5c0f\u9738\u738b\u201d\u4e00\u773c\uff0c\u4e24\u773c\u5fae\u5fae\u4e0a\u7ffb\uff0c\u51b7\u7136\u9053\uff1a\u201c\u5404\u4f4d\u63a8\u4e3e\u51fa\u6765\u4e4b\u4eba\uff0c\u987b\u5f97\u81ea\u8eab\u6b66\u529f \u9ad8\u5f3a\uff0c\u773c\u5149\u654f\u9510\u8005\uff0c\u65b9\u80fd\u4f5c\u8fd9\u9ad8\u4eba\u8f83\u6280\u7684\u516c\u6b63\u3002\u655d\u4eba\u867d\u4e0d\u624d\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u8981\u6bd4\u8fd9\u7cdf\u8001\u5934 \u5b50\u5f3a\u5f97\u591a\u3002\u56e0\u6b64\uff0c\u655d\u4eba\u5c31\u5fcd\u4e0d\u4f4f\u8981\u6bdb\u9042\u81ea\u8350\u4e86\u3002\u201d\u8bed\u58f0\u4e00\u843d\uff0c\u7fa4\u8c6a\u53c8\u5927\u54d7\u3002\u90a3\u4e07\u80dc\u5200\u9ec4\u9547\u56fd \u66f4\u662f\u6c14\u5f97\u54bb\u54bb\u5598\u6c14\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u6211\u9ec4\u9547\u56fd\u662f\u7cdf\u8001\u5934\u5b50\uff0c\u6211\u8fd9\u4e2a\u7cdf\u8001\u5934\u5b50\uff0c\u5012\u8981\u8bd5\u8bd5 \u4f60\u8fd9\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u5b50\uff0c\u6709\u4ec0\u4e48\u51fa\u5947\u5236\u80dc\u7684\u529f\u592b\uff01\u201d\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5c31\u51c6\u5907\u548c\u8fd9\u5c11\u5e74\u52a8 \u624b\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e09\u5341\u516b\u7ae0\u3000\u9752\u6d77\u6765\u5ba2>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u516b\u7ae0\u3000\u9752\u6d77\u6765\u5ba2 \u3000\u3000\u94b1\u7fca\u773c\u89d2\u779f\u4e86\u4ed6\u4e00\u773c\uff0c\u53cc\u76ee\u53c8\u5fae\u5fae\u4e0a\u7ffb\uff0c\u6839\u672c\u7406\u4e5f\u6ca1\u6709\u7406\u4ed6\uff0c\u4e5f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u5c06\u4ed6\u653e\u5728 \u773c\u91cc\u3002\u6001\u5ea6\u4e4b\u72c2\u50b2\uff0c\u4ee4\u5f97\u7fa4\u8c6a\u53c8\u4e3a\u4e4b\u54d7\u7136\u3002 \u3000\u3000\u9ec4\u9547\u56fd\u6c14\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u6c89\u8170\u5750\u9a6c\uff0c\u55d6\u7136\u4e00\u62f3\uff0c\u671d\u4ed6\u540e\u80cc\u6253\u53bb\uff0c\u8fd9\u81ea\u79f0\u201c\u94b1\u7fca\u201d\uff0c\u5728\u6c5f\u6e56 \u4e0a\u85c9\u85c9\u65e0\u540d\u7684\u5c11\u5e74\uff0c\u5374\u6839\u672c\u52a8\u4e5f\u4e0d\u52a8\u3002\u9ec4\u9547\u56fd\u7684\u8fd9\u4e00\u62f3\uff0c\u7adf\u7740\u7740\u5b9e\u5b9e\u5730\u6253\u5728\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u7fa4\u8c6a\u773c\u770b\u4e07\u80dc\u5200\u4e00\u62f3\u6253\u5728\u8fd9\u5c11\u5e74\u8eab\u4e0a\u3002\u90a3\u77e5\u9ec4\u9547\u56fd\u4e00\u62f3\uff0c\u65b9\u81ea\u6cbe\u7740\u4eba\u5bb6\u7684\u8863\u670d\uff0c\u81ea\u5df1\u7684 \u8eab\u5b50\uff0c\u5374\u7a81\u7136\u50cf\u662f\u4e2d\u4e86\u90aa\u4e00\u6837\uff0c\u5e73\u767d\u98de\u4e86\u8d77\u6765\uff0c\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u8dcc\u5230\u5730\u4e0a\u3002 \u3000\u3000\u7fa4\u8c6a\u53c8\u590d\u5927\u54d7\u3002 \u3000\u3000\u6709\u4e9b\u8bc6\u8d27\u7684\uff0c\u4e0d\u7981\u8131\u53e3\u800c\u547c\uff1a \u3000\u3000\u201c\u6cbe\u8863\u5341\u516b\u8dcc\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c11\u5e74\uff0c\u6240\u4f7f\u7684\u624b\u6cd5\uff0c\u7adf\u662f\u6b66\u6797\u5185\u5bb6\u7684\u767b\u5cf0\u9020\u6781\u7684\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\uff0c \u4e0d\u4f46\u7fa4\u8c6a\u54d7\u7136\uff0c\u4f0a\u98ce\u4e5f\u5927\u4e3a\u52a8\u5bb9\uff0c\u6697\u5730\u60ca\u5f02\u8fd9\u5c11\u5e74\u600e\u5730\u6709\u5982\u6b64\u7684\u8eab\u624b.\u5374\u53c8\u600e\u7684\u672a\u5728\u6c5f\u6e56\u4e0a \u9732\u8fc7\u9762\uff1f \u3000\u3000\u516b\u5366\u795e\u638c\u4e5f\u81ea\u9762\u76ee\u53d8\u8272\uff0c\u7f13\u7f13\u8d70\u5230\u8fd9\u5c11\u5e74\u201c\u94b1\u7fca\u201d\u8eab\u4fa7\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u5c11\u5e74\u670b\u53cb\u597d\u4fca\u7684\u529f\u592b\uff0c\u5c0a\u5e08\u4f55\u4eba\uff1f\u53ef\u662f\u6b66\u5f53\u5c71\u7684\u5b5f\u9053\u957f\uff1f\u201d \u3000\u3000\u94b1\u7fca\u5fae\u5fae\u4e00\u7b11\uff0c\u4f46\u7b11\u5bb9\u4e2d\u4ecd\u6ee1\u542b\u50b2\u6c14\uff0c\u5fae\u5fae\u62b1\u62f3\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u6765\u81ea\u9752\u6d77\u7a46\u9c81\u4e4c\u82cf\u6cb3\uff0c\u5bb6\u5e08\u66fe\u5bf9\u5c0f\u9119\u8bf4\u8d77\u8fc7\u8303\u8001\u82f1\u96c4\u7684\u4fa0\u540d\uff0c\u60f3\u8303\u8001\u82f1\u96c4\u5fc5\u4e5f\u8bb0 \u5f97\u5bb6\u5e08\u5427\uff1f\u201d \u3000\u3000\u516b\u5366\u795e\u638c\u679c\u7136\u9762\u8272\u500f\u53d8\uff0c\u201c\u501a\u8001\u201d\u4e4b\u6001\uff0c\u987f\u65f6\u6e3a\u7136\uff0c\u7adf\u62f1\u624b\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u94b1\u5c11\u4fa0\u6765\u81ea\u5e03\u514b\u9a6c\u56e0\u5c71\u53e3\uff0c\u5c0a\u5e08\u6b66\u6797\u5f02\u4eba\uff0c\u8001\u592b\u6614\u5e74\u4e5f\u66fe\u6709\u7f18\u62dc\u8bc6\u8fc7\u3002\u5982\u4eca\u94b1\u5c11 \u4fa0\u884c\u9053\u6c5f\u6e56\uff0c\u90a3\u597d\u6781\u4e86\uff01\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u5148\u524d\u5df2\u7ecf\u88ab\u8fd9\u5c11\u5e74\u7684\u529f\u592b\u6240\u9707\uff0c\u6b64\u523b\u53c8\u89c1\u4e00\u5411\u81ea\u8d1f\u7684\u516b\u5366\u795e\u638c\uff0c\u7adf\u4e5f\u524d\u5028\u540e\u606d\uff0c\u5bf9 \u8fd9\u5c11\u5e74\u5982\u6b64\u606d\u656c\uff0c\u4e0d\u7981\u76f8\u987e\u8be7\u7136\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u201c\u94b1\u7fca\u201d\u53c8\u5fae\u5fae\u4e00\u7b11\uff0c\u50b2\u7136\u9053\uff1a \u3000\u3000\u201c\u8303\u8001\u524d\u8f88\uff01\u8d1d\u5c0f\u9119\u51fa\u6765\u505a\u6b64\u4f1a\u7684\u516c\u6b63\u4eba\uff0c\u53ef\u8fd8\u4f7f\u5f97\uff1f\u201d \u3000\u3000\u516b\u5366\u795e\u638c\u8fde\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f7f\u5f97\uff01\u4f7f\u5f97\uff01\u201d\u4e00\u9762\u5411\u56db\u5ea7\u7fa4\u8c6a\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u94b1\u5c11\u4fa0\uff0c\u5c31\u662f\u9690\u5c45\u9752\u6d77\u5e03\u514b\u9a6c\u56e0\u5c71\u53e3\u7684\u6b66\u6797\u524d\u8f88\u5f02\u4eba\u2014\u2014\u65e0\u540d\u53df\u7684\u9ad8\u5f1f\u3002\u5404\u4f4d\u8d70 \u52a8\u6c5f\u6e56\uff0c\u60f3\u5fc5\u4e5f\u66fe\u542c\u8d77\u8fc7\u9752\u6d77\u65e0\u540d\u8001\u4eba\u7684\u540d\u58f0\u5427\uff01\u201d \u3000\u3000\u201c\u65e0\u540d\u8001\u4eba\u201d\u56db\u5b57\u4e00\u51fa\uff0c\u7fa4\u8c6a\u53c8\u590d\u6654\u7136\u3002 \u3000\u3000\u90a3\u4f4d\u201c\u4e07\u80dc\u5200\u201d\u9ec4\u9547\u56fd\uff0c\u4e00\u542c\u8fd9\u56db\u5b57\uff0c\u8d76\u7d27\u548c\u201c\u5c0f\u9738\u738b\u201d\u4ece\u4fa7\u95e8\u6e9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u542c\u6b64\u4eba\u4e4b\u540d\uff0c\u4e5f\u590d\u5927\u60ca\uff0c\u4e0d\u7981\u66f4\u7559\u610f\u6253\u91cf\u4e86\u8fd9\u201c\u94b1\u7fca\u201d\u51e0\u773c\u3002 \u3000\u3000\u539f\u6765\u6b66\u6797\u76f8\u4f20\uff0c\u9752\u6d77\u5e03\u514b\u9a6c\u56e0\u5c71\u53e3\u91cc\uff0c\u9690\u5c45\u7740\u4e00\u4f4d\u6b66\u6797\u5f02\u4eba\uff0c\u6570\u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u4eba\u90fd\u77e5 \u9053\u8fd9\u4f4d\u5f02\u4eba\uff0c\u529f\u884c\u5df2\u53c2\u9020\u5316\uff0c\u5374\u90fd\u672a\u66fe\u89c1\u5230\u8fd9\u4f4d\u5f02\u4eba\u7684\u771f\u9762\u76ee\uff0c\u53ea\u662f\u4ee5\u201c\u65e0\u540d\u8001\u4eba\u201d\u540d\u4e4b\u3002 \u3000\u3000\u8fd9\u201c\u94b1\u7fca\u201d\u867d\u662f\u65e0\u540d\u4e4b\u4eba\uff0c\u4f46\u4ed6\u7684\u5e08\u627f\u6765\u5386\u4e00\u8bf4\uff0c\u7fa4\u8c6a\u5374\u90fd\u4e0d\u7981\u52a8\u5bb9\u3002\u5c31\u8fde\u516b\u5366\u795e\u638c\u8fd9 \u79cd\u6b66\u6797\u524d\u8f88\uff0c\u90fd\u4e0d\u514d\u53d8\u8272\u3002 \u3000\u3000\u94b1\u7fca\u50b2\u7136\u56db\u987e\uff0c\u8d70\u5230\u795e\u9f9b\u524d\u3002\u516b\u5366\u795e\u638c\u671d\u795e\u9f9b\u540e\u7684\u7ec8\u5357\u9053\u4eba\u62f1\u624b\u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\u6b66\u6797\u7fa4\u8c6a\u5df2\u63a8\u51fa\u6211\u7b49\u4e09\u4eba\uff0c\u4f5c\u4e3a\u8d35\u6d3e\u6280\u4e89\u638c\u95e8\u4e4b\u89c1\u8bc1\uff0c\u5c31\u8bf7\u8d35\u6d3e\uff0c\u5f00\u59cb\u4e86\u5374\u8fd9\u4ef6 \u6b66\u6797\u5927\u4e8b\u3002\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u8f6c\u5230\u795e\u9f9b\u540e\u9762\uff0c\u5374\u89c1\u65b9\u624d\u5728\u89c2\u95e8\u524d\u6240\u9047\u7684\u90a3\u4e2d\u5e74\u9053\u4eba\uff0c\u6b64\u523b\u6b63\u548c\u53e6\u4e24\u4e2a\u9053\u4eba\uff0c \u5728\u4f4e\u58f0\u8bf4\u7740\u8bdd\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u9053\u4eba\u5e74\u7eaa\u90fd\u751a\u5927\uff0c\u4e00\u9762\u503e\u542c\u7740\uff0c\u76ee\u5149\u4e00\u9762\u5728\u56db\u4e0b\u641c\u7d20\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u662f\u627e\u6211\uff1f\u201d \u3000\u3000\u5374\u89c1\u5176\u4e2d\u4e00\u4e2a\u5934\u53d1\u5df2\u7ecf\u82b1\u767d\u7684\u9053\u4eba\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u5411\u56db\u5ea7\u6253\u4e86\u4e2a\u95ee\u8baf\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u655d\u6d3e\u6b64\u6b21\u56e0\u638c\u95e8\u4eba\u5999\u7075\u9053\u957f\uff0c\u56e0\u75c5\u4ed9\u53bb\uff0c\u4e34\u53bb\u5306\u5306\uff0c\u672a\u66fe\u4f20\u4f4d\u4e0e\u4ed6\u4eba\u3002\u662f\u4ee5\u655d\u6d3e\u6570\u767e \u5f1f\u5b50\u516c\u8bae\uff0c\u8981\u4ee5\u6b66\u6280\u7684\u9ad8\u4f4e\uff0c\u9009\u51fa\u655d\u7ec8\u5357\u6d3e\u7684\u7b2c\u516d\u4ee3\u638c\u95e8\u4eba\u6765\uff0c\u662f\u4ee5\u52b3\u52a8\u5404\u4f4d\u8c6a\u6770\uff0c\u5171\u8944\u6b64 \u4e3e\u3002\u201d \u3000\u3000\u4ed6\u6c89\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5404\u4f4d\u63a8\u51fa\u7684\u8fd9\u4e09\u4f4d\uff0c\u90fd\u662f\u6b66\u6797\u4e2d\u540d\u91cd\u4e00\u65f6\u7684\u8c6a\u58eb\uff0c\u80af\u4e3a\u655d\u6d3e\u6b64\u4f1a\u4f5c\u4e3a\u89c1\u8bc1\uff0c\u8d2b\u9053\u8c28\u4e3a \u655d\u6d3e\u5168\u4f53\u5f1f\u5b50\uff0c\u5411\u5404\u4f4d\u81f4\u8c22\u3002\u201d \u3000\u3000\u4ed6\u53cc\u7709\u5fae\u76b1\uff0c\u8138\u4e0a\u7adf\u9690\u542b\u5fe7\u8272\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u655d\u6d3e\u5f1f\u5b50\u4e2d\uff0c\u7ecf\u8d2b\u9053\u6240\u8be2\uff0c\u6709\u610f\u4e89\u6b64\u201c\u638c\u95e8\u4eba\u201d\u4e4b\u4f4d\u7684\uff0c\u5171\u6709\u4e03\u4eba\uff0c\u6b64\u4e03\u4f4d\u540c\u95e8\uff0c\u591a \u662f\u655d\u6d3e\u4e2d\u7684\u82f1\u9510\uff0c\u8d2b\u9053\u81ea\u4ea6\u6df1\u671b\u655d\u6d3e\u4ecd\u5f97\u4e00\u80fd\u8005\uff0c\u62c5\u5f53\u5927\u4efb\u3002\u6b64\u523b\u8d2b\u9053\u5148\u8bf7\u8fd9\u4e03\u4f4d\u540c\u95e8\u51fa \u6765\uff0c\u5411\u5404\u4f4d\u89c1\u793c\u3002\u201d \u3000\u3000\u516b\u5366\u795e\u638c\u7a81\u5730\u6717\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u5999\u6cd5\u9053\u957f\uff01\u96be\u9053\u65e0\u610f\u4e8e\u6b64\u5417\uff1f\u201d \u3000\u3000\u8fd9\u53d1\u987b\u82b1\u767d\u7684\u9053\u4eba\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u8001\u4e86\uff0c\u7b4b\u9aa8\u4e5f\u8870\u9000\u4e86\uff1b\u600e\u6bd4\u5f97\u4e0a\u8303\u65bd\u4e3b\uff0c\u4ecd\u7136\u7cbe\u795e\u77cd\u94c4\u3002\u201d \u3000\u3000\u8303\u4ef2\u5e73\u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u4e5f\u77e5\u9053\u9053\u957f\u6709\u5982\u95f2\u4e91\u91ce\u9e64\uff0c\u4f55\u7b49\u900d\u9065\u81ea\u5728\uff01\u65e2\u662f\u5982\u6b64\uff0c\u5feb\u8bf7\u8d35\u6d3e\u90a3\u4e03\u4f4d\u9053\u957f\u51fa \u6765\uff0c\u6211\u60f3\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\uff0c\u90fd\u662f\u6e34\u6b32\u4e00\u89c1\u7ec8\u5357\u6d3e\u672a\u6765\u638c\u95e8\u4eba\u7684\u9762\u76ee\u7684\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u81ea\u662f\u54c4\u7136\u9644\u610f\u3002 \u3000\u3000\u8fd9\u5999\u6cd5\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u8f6c\u8eab\u5411\u540e\uff0c\u795e\u9f9b\u4e24\u4fa7\u5c31\u9646\u7eed\u8d70\u51fa\u4e03\u4e2a\u84dd\u888d\u9053\u4eba\u6765\uff0c\u7fa4\u8c6a\u53ea\u89c1\u8fd9\u4e03 \u4eba\uff0c\u9ad8\u77ee\u8001\u5e7c\u90fd\u4e0d\u7b49\uff0c\u4f46\u5374\u90fd\u662f\u795e\u5b8c\u6c14\u8db3\uff0c\u6b65\u5c65\u5b89\u8be6\uff0c\u76ee\u5149\u70af\u7136\u903c\u4eba\uff0c\u60f3\u5fc5\u90fd\u662f\u5185\u5bb6\u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u4e03\u4e2a\u9053\u4eba\u4e00\u51fa\u6765\uff0c\u5c31\u53cc\u638c\u53f0\u5341\uff0c\u5411\u7740\u56db\u5ea7\u8eac\u8eab\u65bd\u793c\uff0c\u7fa4\u8c6a\u4e5f\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u7eb7\u7eb7\u8fd8\u793c\u3002 \u987b\u77e5\u8fd9\u4e03\u4eba\u4e2d\uff0c\u5c31\u6709\u4e00\u4eba\uff0c\u662f\u672a\u6765\u7ec8\u5357\u4e00\u6d3e\u7684\u638c\u95e8\u3002\u6b66\u6797\u7fa4\u8c6a\u5bf9\u6b64\u4e03\u4eba\uff0c\u5f53\u7136\u4e5f\u90fd\u4e0d\u6562\u5931 \u793c\u3002 \u3000\u3000\u4f0a\u98ce\u7ad9\u5728\u6700\u540e\uff0c\u773c\u4e2d\u6ce8\u89c6\u7740\u8fd9\u4e03\u4e2a\u9053\u4eba\u3002\u5fc3\u4e2d\u603b\u89c9\u5f97\u4eca\u65e5\u4e4b\u4f1a\uff0c\u5176\u4e2d\u5927\u6709\u8e4a\u8df7\uff1b\u53ea\u662f\u5230 \u6b64\u523b\u4e3a\u6b62\uff0c\u8fd8\u672a\u73b0\u51fa\u7aef\u502a\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u201c\u5415\u7956\u6bbf\u201d\u751a\u662f\u5bbd\u5927\uff0c\u9664\u4e86\u56db\u4fa7\u88ab\u6b66\u6797\u7fa4\u8c6a\u5360\u5750\u7684\u5730\u65b9\u5916\uff0c\u5f53\u4e2d\u8fd8\u6709\u4e00\u5757\u4e09\u4e08\u89c1\u65b9\u7684 \u7a7a\u5730\u3002\u6b64\u523b\u4e00\u4e2a\u5e74\u7ea6\u4e09\u5341\u8bb8\u7684\u9053\u4eba\u7ad9\u4e86\u51fa\u6765\uff0c\u53cc\u638c\u5408\u5341\uff0c\u5411\u56db\u5ea7\u5fae\u4e00\u884c\u793c\uff0c\u8f6c\u5411\u795e\u9f9b\uff0c\u64a9\u8d77 \u9053\u888d\uff0c\u5411\u795e\u9f9b\u91cc\u7684\u5415\u7956\u795e\u50cf\uff0c\u7aef\u7aef\u6b63\u6b63\u53e9\u4e86\u4e09\u4e2a\u5934\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u7ec8\u5357\u7b2c\u4eba\u4ee3\u5f1f\u5b50\u7384\u5316\uff0c\u606d\u8bf7\u5404\u4f4d\u5e08\u4f2f\uff0c\u5e08\u5144\u5f1f\u6307\u6559\u3002\u201d \u3000\u3000\u6492\u8d77\u9053\u888d\u4e00\u89d2\uff0c\u6396\u5728\u8170\u4e2d\u7684\u4e1d\u5e26\u4e0a\uff0c\u53cc\u624b\u5782\u4e0b\uff0c\u53cc\u76ee\u5fae\u7fd5\uff0c\u811a\u4e0b\u4e0d\u4e01\u4e0d\u516b\uff0c\u51dd\u7136\u5353\u7acb\uff0c \u610f\u5728\u62f3\u5148\uff0c\u679c\u7136\u8eab\u624b\u4e0d\u4fd7\u3002 \u3000\u3000\u5728\u5ea7\u4f17\u8c6a\uff0c\u5c31\u51b2\u8fd9\u7384\u5316\u9053\u4eba\u7684\u8fd9\u4e00\u4f2b\u7acb\uff0c\u5c31\u77e5\u9053\u8fd9\u9053\u4eba\u6b66\u529f\uff0c\u81f3\u5c11\u5df2\u6709\u4e8c\u5341\u5e74\u7684\u706b\u5019\uff0c \u4e0d\u7981\u6697\u5fd6\uff1a\u201c\u7ec8\u5357\u5f1f\u5b50\uff0c\u679c\u6709\u597d\u624b\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7ad9\u5728\u4e0b\u7aef\u7684\u53e6\u4e00\u4e2a\u9053\u4eba\uff0c\u4e5f\u8d70\u4e86\u51fa\u6765\uff0c\u4e5f\u671d\u7740\u5415\u7956\u795e\u50cf\u53ca\u4f17\u8c6a\u884c\u8fc7\u793c\uff0c\u64a9\u8d77\u9053 \u888d\uff0c\u5411\u51dd\u795e\u5353\u7acb\u7684\u7384\u5316\u5408\u5341\u9053\uff1a \u3000\u3000\u201c\u7384\u673a\u606d\u8bf7\u5e08\u5144\u8d50\u62db\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4e5f\u81ea\u5353\u7136\u800c\u7acb\uff0c\u51dd\u795e\u5f85\u654c\u3002 \u3000\u3000\u7384\u5316\u9053\u4eba\u4f4e\u559d\u4e00\u58f0\uff0c\u5de6\u81c2\u5e73\u8d77\uff0c\u53f3\u638c\u4e2d\u5207\uff0c\u811a\u4e0b\u5fae\u4e00\u8e0f\u6b65\u95f4\uff0c\u5df2\u5230\u7384\u673a\u8eab\u524d\u3002\u53cc\u638c\u500f\u7136 \u5916\u626c\uff0c\u4e00\u51fb\u9762\u95e8\uff0c\u4e00\u626b\u4e0b\u8179\u3002 \u3000\u3000\u7384\u673a\u811a\u6b65\u4e00\u9519\uff0c\u8eab\u5f62\u534a\u8f6c\uff0c\u8fde\u6d88\u5e26\u6253\uff0c\u53f3\u81c2\u4e5f\u7a7f\u51fa\u4e00\u51fb\u3002 \u3000\u3000\u987f\u65f6\u4e4b\u95f4\uff0c\u8fd9\u4e09\u4e08\u65b9\u5706\u7684\u7a7a\u5730\u4e0a\uff0c\u638c\u5f71\u98de\u821e\uff0c\u8eab\u5f62\u7535\u95ea\u3002\u8fd9\u7384\u5316\uff0c\u7384\u673a\u4e24\u4eba\uff0c\u7528\u7684\u5168\u662f \u672c\u95e8\u62f3\u672f\uff0c\u8f7b\u7075\u4e4b\u4e2d\uff0c\u4e0d\u5931\u7a33\u5065\uff1b\u7a33\u5065\u4e4b\u4e2d\uff0c\u5374\u53c8\u6709\u5982\u884c\u4e91\u6d41\u6c34\uff0c\u62db\u62db\u751f\u751f\u4e0d\u606f\u3002\u53d8\u5e7b\u6d41 \u52a8\uff0c\u6ce2\u8c32\u4e91\u8be1\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u65bd\u5c55\u51fa\u638c\u6cd5\uff0c\u4f17\u8c6a\u624d\u77e5\u9053\u7ec8\u5357\u638c\u6cd5\uff0c\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff01 \u3000\u3000\u8bf8\u8c6a\u6b63\u81ea\u795e\u9a70\u95f4\uff0c\u7a81\u89c1\u4eba\u5f71\u4e00\u5206\uff0c\u7384\u673a\u9053\u4eba\u8fdc\u8fdc\u9000\u81f3\u4e00\u65c1\uff0c\u8eac\u8eab\u9053\uff1a \u3000\u3000\u201c\u5e08\u5144\u5999\u7740\uff0c\u7384\u673a\u4e0d\u654c\u3002\u201d \u3000\u3000\u518d\u4e00\u5408\u5341\uff0c\u7f13\u7f13\u8d70\u56de\u795e\u9f9b\u540e\u3002 \u3000\u3000\u516b\u5366\u795e\u638c\u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u624d\u662f\u9ad8\u624b\u8f83\u6280\uff0c\u8fd9\u624d\u53eb\u505a\u9ad8\u4eba\uff01\u201d \u3000\u3000\u5fae\u4e00\u56db\u987e\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u65b9\u624d\u90a3\u4f4d\u7384\u673a\u9053\u957f\u53ea\u8f93\u4e86\u534a\u7740\u4fbf\u81ea\u627f\u5df2\u8d25\u3002\u8fd9\u79cd\u540d\u5bb6\u98ce\u5ea6\uff0c\u5927\u5bb6\u771f\u8be5\u5b66\u5b66\uff01\u201d \u3000\u3000\u8bf4\u7740\u53c8\u4f38\u51fa\u5927\u59c6\u6307\uff0c\u8fde\u58f0\u5927\u7b11\u4e0d\u5df2\u3002 \u3000\u3000\u4f17\u8c6a\u5df2\u81ea\u4f69\u670d\uff1b\u6709\u4e9b\u4eba\u6839\u672c\u8fde\u7384\u673a\u5982\u4f55\u8d25\u7684\uff0c\u90fd\u4e0d\u66fe\u770b\u6e05\u3002\u6b64\u523b\u8303\u4ef2\u5e73\u4e00\u8bf4\uff0c\u5404\u4eba\u90fd\u4f38 \u5927\u59c6\u6307\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u4eec\u8fd9\u4e00\u6bd4\u6597\uff0c\u6709\u5173\u4e00\u6d3e\u638c\u95e8\u4eba\u4e4b\u4f4d\uff1b\u800c\u8fd9\u7384\u673a\u9053\u4eba\uff0c\u7adf\u80fd\u5c06\u80dc\u8d1f\u5982\u6b64\u6de1\u7136\u89c6\u4e4b\uff0c \u80f8\u895f\u81ea\u975e\u5e38\u4eba\u80fd\u53ca\u3002 \u3000\u3000\u77ac\u606f\u4e4b\u95f4\uff0c\u7ec8\u5357\u9053\u4eba\u53c8\u8d25\u4e0b\u4e24\u4f4d\u3002\u5728\u573a\u4e2d\u51dd\u795e\u5353\u7acb\u7684\uff0c\u4ecd\u662f\u90a3\u6700\u5148\u51fa\u573a\u7684\u7384\u5316\u9053\u4eba\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u6697\u81ea\u611f\u53f9\uff0c\u8fd9\u7ec8\u5357\u4e00\u6d3e\u786e\u975e\u51e1\u95e8\u3002\u4e00\u9762\u5374\u53c8\u6697\u8d5e\u8fd9\u7384\u5316\u9053\u4eba\u7684\u8eab\u624b\uff0c\u8fde\u63a5\u4e09\u573a \u4e4b\u4e0b\uff0c\u4ed6\u4ecd\u7136\u610f\u6001\u5b89\u8be6\uff0c\u4ece\u5bb9\u5f97\u5f88\u3002 \u3000\u3000\u6885\u82b1\u5251\u675c\u957f\u537f\u6b64\u65f6\u5ffd\u7136\u8d70\u5230\u8303\u4ef2\u5e73\u8eab\u4fa7\uff0c\u4f4e\u8bed\u51e0\u53e5\uff0c\u8303\u5fe1\u5e73\u8fde\u8fde\u70b9\u5934\uff0c\u5bf9\u675c\u957f\u537f\u7684\u8bdd\uff0c \u5927\u6709\u9887\u8868\u8d5e\u540c\u4e4b\u610f\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e5d\u7ae0\u3000\u9010\u9e7f\u638c\u95e8>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e09\u5341\u4e5d\u7ae0\u3000\u9010\u9e7f\u638c\u95e8 \u3000\u3000\u6b64\u523b\u90a3\u53e6\u4e09\u4e2a\u9053\u4eba\u53c8\u8d70\u51fa\u4e00\u4e2a\u4e94\u7efa\u957f\u987b\u7684\u9053\u8005\uff0c\u6b64\u4eba\u672c\u662f\u5999\u7075\u9053\u4eba\u7684\u5e08\u5f1f\uff0c\u6bd4\u7384\u5316\u5c1a\u957f \u51fa\u4e00\u8f88\u3002\u7384\u5316\u4e00\u89c1\u6b64\u4eba\u8d70\u51fa\uff0c\u5fd9\u8eac\u8eab\u9053\uff1a \u3000\u3000\u201c\u4e94\u5e08\u53d4\u4e5f\u6765\u8d50\u6559\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e94\u7efa\u957f\u987b\u9053\u4eba\u4e43\u6614\u5e74\u7ec8\u5357\u638c\u6559\u7389\u673a\u771f\u4eba\u7684\u4e94\u5f1f\u5b50\u5999\u5143\uff0c\u6b64\u523b\u5fae\u5fae\u4e00\u7b11\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u8f83\u6280\uff0c\u5404\u65bd\u6240\u957f\uff0c\u4f60\u5207\u5207\u4e0d\u53ef\u5fc3\u5b58\u793c\u8ba9\u987e\u5fcc\uff0c\u5426\u5219\u5c31\u5931\u4e86\u4ee5\u8f83\u6280\u6765\u4e89\u638c\u95e8\u7684\u539f\u610f \u4e86\u3002\u201d \u3000\u3000\u7384\u5316\u5fd9\u8eac\u8eab\u552f\u552f\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5b50\u9075\u4ece\u5e08\u53d4\u7684\u6559\u8bf2\u3002\u201d \u3000\u3000\u53cc\u624b\u4e0b\u5782\uff0c\u51dd\u795e\u800c\u7acb\uff0c\u6b63\u5f85\u51fa\u624b\u3002\u516b\u5366\u795e\u638c\u5374\u7a81\u5730\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u5c06\u624b\u4e00\u62e6\uff0c\u6717\u58f0\u7b11 \u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u4e14\u6162\u52a8\u624b\uff01\u65b9\u624d\u675c\u5c11\u4fa0\u4e4b\u610f\uff0c\u7384\u5316\u9053\u957f\uff0c\u5df2\u8fc7\u4e86\u4e09\u573a\uff0c\u6b64\u523b\u4e0d\u59a8\u7a0d\u4e3a\u6b47\u606f\u4e00\u4e0b\uff0c \u7531\u53e6\u4e09\u4f4d\u9053\u957f\u5148\u8fc7\u8fc7\u624b\uff0c\u5176\u4e2d\u6700\u80dc\u4e4b\u4e00\u4f4d\uff0c\u518d\u51fa\u6765\u548c\u7384\u5316\u9053\u957f\u52a8\u624b\uff0c\u5404\u4f4d\u770b\u6b64\u4e3e\u53ef\u59a5\u5f53 \u5426\uff1f\u201d \u3000\u3000\u7384\u5316\u5782\u624b\u9000\u6b65\u3002 \u3000\u3000\u5999\u5143\u8eac\u8eab\u9053\uff1a \u3000\u3000\u201c\u5168\u51ed\u8303\u8001\u5e08\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u8fd9\u4e24\u9635\u8f83\u6280\u4e0b\u6765\uff0c\u5999\u5143\u9053\u4eba\u4ee5\u4e00\u62db\u201c\u91d1\u86df\u526a\u201d\u80dc\u4e86\u7b2c\u4e00\u9635\uff0c\u6700\u540e\u4e0a\u6765\u7684\u662f\u201c\u7389\u673a\u771f\u4eba\u201d \u7684\u56db\u5f1f\u5b50\u5999\u901a\uff0c\u4ea4\u624b\u65b9\u5341\u6570\u4e2a\u7167\u9762\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u7adf\u88ab\u5999\u5143\u62a2\u5165\u4e2d\u5bab\uff0c\u4ee5\u638c\u7f18\u5728\u4ed6\u524d\u80f8\u62c2\u4e86\u4e00 \u4e0b\u3002 \u3000\u3000\u4e8e\u662f\u5999\u901a\u9053\u4eba\uff0c\u4e5f\u7acb\u523b\u9000\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u773c\u770b\u8fd9\u51e0\u4f4d\u7ec8\u5357\u9ad8\u624b\u8fc7\u62db\uff0c\u6280\u4e89\u638c\u95e8\uff0c\u7adf\u50cf\u662f\u5e73\u65f6\u5e08\u5144\u5f1f\u8003\u8f83\u8eab\u624b\u4e00\u6837\uff0c\u5b8c\u5168\u6ca1\u6709 \u60ca\u9669\uff0c\u524c\u6fc0\u7684\u573a\u9762\u3002\u4e00\u9762\u6697\u8d5e\u8fd9\u4e9b\u7ec8\u5357\u5f1f\u5b50\u7684\u5bbd\u5b8f\u6c14\u5ea6\uff1b\u4e00\u9762\u5374\u53c8\u6697\u6697\u60cb\u60dc\u81ea\u5df1\u7684\u773c\u798f\uff0c\u6ca1 \u6709\u770b\u5230\u4ec0\u4e48\u70ed\u95f9\u3002 \u3000\u3000\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\uff0c\u5927\u591a\u662f\u8fdc\u9053\u800c\u6765\uff01\u5fc3\u91cc\u591a\u591a\u5c11\u5c11\u603b\u662f\u5b58\u6709\u4e00\u4e9b\u5e78\u707e\u4e50\u7978\u7684\u4eba\u7c7b\u901a\u75c5\uff0c\u6068 \u4e0d\u5f97\u8fd9\u4e9b\u7ec8\u5357\u9053\u4eba\uff0c\u6253\u4e2a\u8840\u6dcb\u6dcb\u7684\u706b\u7206\u573a\u9762\u3002\u6b64\u523b\u89c1\u4ed6\u4eec\u8f7b\u63cf\u6de1\u5199\uff0c\u5c31\u5df2\u8fc7\u4e86\u4e94\u9635\uff0c\u5012\u6709\u4e9b \u6005\u7136\u3002 \u3000\u3000\u6b64\u523b\u552f\u4e00\u672a\u51b3\u80dc\u8d1f\u7684\uff0c\u53ea\u5269\u4e0b\u5999\u5143\u9053\u4eba\u548c\u7384\u5316\u9053\u4eba\u4e24\u4eba\uff0c\u7fa4\u8c6a\u4e0d\u7981\u5c06\u6ce8\u610f\u529b\u90fd\u96c6\u4e2d\u5230\u8fd9 \u4e24\u4eba\u8eab\u4e0a\u3002\u56e0\u4e3a\u6b64\u4e24\u4eba\u7684\u80dc\u8d1f\uff0c\u5c31\u5173\u7cfb\u7740\u7ec8\u5357\u4e00\u6d3e\u7684\u638c\u95e8\u3002\u8fd9\u5728\u6b66\u6797\u4e2d\u6765\u8bf4\uff0c\u53ef\u7b97\u5f97\u662f\u4ef6\u5927 \u4e8b\u3002 \u3000\u3000\u516b\u5366\u795e\u638c\u6717\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u9053\u957f\u7a0d\u4e3a\u6b47\u606f\u4e00\u4e0b\uff0c\u518d\u52a8\u624b\u4e89\u8fd9\u638c\u95e8\u4e4b\u5ea7\u3002\u8001\u592b\u4e5f\u7b97\u773c\u798f\u4e0d\u6d45\uff0c\u80fd\u773c\u89c1\u5982\u6b64\u9ad8\u624b \u7684\u8fc7\u62db\u3002\u201d\u4ed6\u8f6c\u8eab\u5411\u675c\u957f\u537f\uff0c\u94b1\u7fca\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u60f3\u5fc5\u4e5f\u6709\u540c\u611f\u5427\u3002\u8fd9\u539f\u662f\u767e\u5e74\u7f55\u7779\u7684\u54e9\uff01\u201d \u3000\u3000\u94b1\u7fca\u659c\u501a\u5728\u4e00\u5f20\u4ea4\u6905\u4e0a\uff0c\u59cb\u7ec8\u52a8\u4e5f\u672a\u52a8\u3002\u6b64\u523b\u5fae\u5fae\u9894\u9996\uff0c\u50cf\u662f\u8981\u8bf4\u8bdd\u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u77e5\u90a3\u5999\u6cd5\u771f\u4eba\u7a81\u7136\u8d70\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u5999\u5143\u5e08\u5f1f\u548c\u7384\u5316\u5e08\u4f84\uff0c\u8fd8\u662f\u6b64\u523b\u5c31\u52a8\u624b\u5427\uff01\u5f97\u80dc\u8005\u5c31\u5728\u6b64\u95f4\u5f53\u7740\u5929\u4e0b\u82f1\u96c4\u548c\u5415\u7956\u795e \u50cf\uff0c\u5c31\u4e3a\u7ec8\u5357\u638c\u95e8\uff0c\u4e5f\u7528\u4e0d\u7740\u518d\u7acb\u4eea\u5f0f\u4e86\u3002\u201d \u3000\u3000\u8303\u4ef2\u5e73\u53cc\u7709\u5fae\u76b1\uff0c\u6697\u6697\u5947\u602a\u8fd9\u5999\u6cd5\u9053\u4eba\uff0c\u4e00\u5411\u8001\u6210\u6301\u91cd\uff0c\u6b64\u523b\u5374\u600e\u7684\u7adf\u5c06\u8fd9\u7b49\u5927\u4e8b\uff0c\u5904 \u7f6e\u5f97\u5982\u6b64\u8349\u7387\uff1f\u8fde\u8ba9\u4ed6\u4eec\u6b47\u606f\u4e00\u4e0b\u90fd\u7b49\u4e0d\u53ca\u3002 \u3000\u3000\u4f0a\u98ce\u51b7\u773c\u65c1\u89c2\uff0c\u5374\u8d1d\u8fd9\u5999\u6cd5\u9053\u4eba\u8138\u4e0a\u7684\u5fe7\u8272\uff0c\u66f4\u52a0\u6d53\u91cd\uff0c\u773c\u5149\u4e0d\u65f6\u626b\u5411\u95e8\u5916\uff0c\u5f77\u4f5b\u751f\u6015 \u6709\u4ec0\u4e48\u4eba\u4f1a\u7a81\u7136\u95ef\u6765\uff0c\u6270\u4e71\u6b64\u4e00\u76db\u4e3e\u4f3c\u7684\uff0c\u662f\u4f3c\u8feb\u4e0d\u53ca\u5f85\u5730\u5c31\u8ba9\u5999\u5143\uff0c\u7384\u5316\u4e24\u4eba\uff0c\u52a8\u624b\u8fc7 \u62db\uff0c\u51b3\u4e00\u80dc\u8d1f\u3002 \u3000\u3000\u8427\u5357\u82f9\u5374\u5168\u90fd\u4e0d\u7ba1\u8fd9\u4e9b\uff0c\u53ea\u662f\u5e78\u798f\u5730\u501a\u5728\u4f0a\u98ce\u8eab\u4fa7\u3002\u56e0\u4e3a\u56db\u5ea7\u7fa4\u8c6a\uff0c\u5750\u5f97\u90fd\u751a\u4e3a\u903c\u6324\uff0c \u662f\u4ee5\u5979\u5168\u8eab\u90fd\u4f9d\u504e\u5728\u4f0a\u98ce\u8eab\u4e0a\uff0c\u5374\u4e5f\u4e0d\u89c9\u60f9\u773c\u3002 \u3000\u3000\u6b64\u523b\u5927\u5385\u8083\u7136\uff0c\u90fd\u5728\u51dd\u795e\u89c2\u671b\u7740\u8fd9\u7ec8\u5357\u6d3e\u7684\u4e24\u4f4d\u6700\u9ad8\u624b\u7684\u6bd4\u6597\u3002 \u3000\u3000\u5999\u5143\u548c\u7384\u5316\u4e24\u4eba\uff0c\u6b63\u662f\u5168\u795e\u51dd\u6ce8\u3002 \u3000\u3000\u8fd9\u4e9b\u9053\u4eba\u4eec\u5728\u52a8\u624b\u4e4b\u5148\uff0c\u5168\u90fd\u5168\u795e\u51dd\u6ce8\uff0c\u7edd\u4e0d\u5927\u610f\u3002\u4f46\u5728\u4e00\u5206\u80dc\u8d1f\u4e4b\u540e\uff0c\u7acb\u523b\u544a\u9000\uff0c\u786e \u662f\u540d\u5bb6\u98ce\u8303\uff01 \u3000\u3000\u90a3\u65f6\u5c31\u5728\u8fd9\u5927\u5385\u4e2d\u9759\u5f97\u8fde\u8bf8\u4eba\u547c\u5438\u4e4b\u58f0\uff0c\u90fd\u53ef\u4ee5\u542c\u5230\u7684\u65f6\u5019\uff0c\u6b63\u9762\u5750\u7740\u7684\u7fa4\u8c6a\uff0c\u7a81\u7136\u8d77 \u4e86\u9635\u9a9a\u52a8\uff0c\u7eb7\u7eb7\u5411\u4e24\u65c1\u79fb\u5f00\u3002 \u3000\u3000\u5999\u6cd5\u9053\u4eba\u9762\u8272\u5927\u53d8\u3002\u4f0a\u98ce\u4e5f\u4e00\u60ca\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u731c\u60f3\u672a\u9519\uff0c\u679c\u7136\u6b64\u4e8b\u5e76\u4e0d\u7b80\u5355\u3002 \u3000\u3000\u516b\u5366\u795e\u638c\uff0c\u6885\u82b1\u5251\u7b49\u4eba\uff0c\u6b63\u81ea\u60ca\u8be7\uff0c\u5374\u89c1\u8fd9\u5415\u7956\u6b63\u6bbf\u7684\u6b63\u95e8\uff0c\u8d70\u5165\u4e00\u884c\u4eba\u6765\uff0c\u7adf\u4e5f\u5168\u90e8 \u662f\u8eab\u7740\u84dd\u8272\u9053\u888d\u7684\u9053\u4eba\u3002 \u3000\u3000\u56db\u5ea7\u7fa4\u8c6a\uff0c\u90fd\u4e0d\u77e5\u9053\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002\u53ea\u89c1\u5f53\u5934\u800c\u884c\u7684\u4e00\u4e2a\u9053\u4eba\uff0c\u5f62\u5bb9\u67af\u7626\uff0c\u80cc\u540e\u80cc \u7740\u4e00\u67c4\u957f\u5251\uff0c\u51e0\u4e4e\u62d6\u5230\u5730\u4e0a\u3002\u4f46\u6b65\u5c65\u4e4b\u95f4\u7684\u6c88\u5065\uff0c\u7709\u76ee\u4e4b\u95f4\u7684\u9510\u5149\uff0c\u5374\u4ee4\u4eba\u4e00\u671b\u800c\u77e5\u662f\u6b66\u5bb6 \u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u884c\u5341\u4f59\u9053\u4eba\uff0c\u4e2a\u4e2a\u8eab\u540e\u90fd\u80cc\u7740\u957f\u5251\uff0c\u6700\u4ee4\u4f0a\u98ce\u89e6\u76ee\u7684\uff0c\u5374\u662f\u8fd9\u4e9b\u9053\u4eba\u6240\u7a7f\u7684\u9053\u888d\uff0c \u7adf\u5168\u90fd\u662f\u5d2d\u65b0\u7684\uff0c\u4f46\u53c8\u4e0d\u662f\u65b9\u624d\u5728\u5c71\u4e0b\u6240\u89c1\u7684\u90a3\u4e9b\u5e74\u8f7b\u9053\u4eba\u3002 \u3000\u3000\u5f53\u5148\u800c\u8d70\u7684\u90a3\u67af\u7626\u9053\u4eba\uff0c\u9e70\u76ee\u5fae\u4e00\u987e\u76fc\uff0c\u7adf\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5999\u6cd5\u5e08\u5144\uff01\u4f60\u8fd9\u5374\u4e0d\u5bf9\u4e86\uff01\u5c0f\u5f1f\u65e9\u5df2\u4ee4\u95e8\u5f92\u5148\u6765\u7980\u62a5\u5e08\u5144\uff0c\u8bf4\u662f\u6211\u8fd9\u4e2a\u4e0d\u6210\u6750\u7684\u5e08 \u5f1f\uff0c\u4e5f\u8981\u6765\u51d1\u51d1\u70ed\u95f9\u3002\u600e\u5730\u5e08\u5144\u5374\u8ff3\u81ea\u5c31\u884c\u8d77\u4f1a\u6765\uff1f\u96be\u9053\u4e00\u522b\u5341\u4f59\u5e74\uff0c\u5e08\u5144\u4f60\u7adf\u5fd8\u4e86\u7ec8\u5357\u95e8 \u4e0b\uff0c\u8fd8\u6709\u5c0f\u5f1f\u6211\u8fd9\u4e48\u4e00\u4e2a\u4e0d\u6210\u6750\u7684\u5e08\u5f1f\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e00\u9762\u53c8\u56db\u987e\u7fa4\u8c6a\uff0c\u5927\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u5999\u96e8\uff0c\u4ea6\u662f\u7ec8\u5357\u5f1f\u5b50\uff0c\u6b64\u6b21\u6709\u52b3\u5404\u4f4d\u8fdc\u6765\uff0c\u65e9\u4ee5\u547d\u5c0f\u5f92\u4eec\uff0c\u5728\u5c71\u4e0b\u4e3a\u5404\u4f4d\u6446\u8336\u63a5 \u98ce\u3002\u655d\u5e08\u5144\u63a5\u5f85\u4e0d\u5468\u4e4b\u7f6a\uff0c\u8d2b\u9053\u5148\u5728\u6b64\u8c22\u8fc7\u3002\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u7fa4\u8c6a\u5168\u90fd\u6115\u7136\uff0c\u5947\u602a\u534a\u8def\u4e0a\u600e\u5730\u53c8\u591a\u4e86\u6b64\u4eba\u51fa\u6765\uff01 \u3000\u3000\u4f0a\u98ce\u4e5f\u604d\u7136\u800c\u609f\uff0c\u6697\u5fd6\uff1a \u3000\u3000\u201c\u539f\u6765\u5148\u524d\u5728\u5c71\u4e0b\u7684\u90a3\u4e9b\u9053\u4eba\uff0c\u5168\u90fd\u662f\u8fd9\u5999\u96e8\u9053\u4eba\u7684\u5f92\u4f17\u3002\u4f46\u8fd9\u5999\u96e8\u9053\u4eba\u867d\u81ea\u79f0\u662f\u7ec8\u5357 \u5f1f\u5b50\uff0c\u90a3\u5999\u6cd5\u9053\u4eba\u5374\u4e3a\u4ec0\u4e48\u4f5c\u5982\u6b64\u5f62\u72b6\uff1f\u201d \u3000\u3000\u5148\u524d\u5728\u5c71\u4e0b\u90a3\u4e9b\u5e74\u8f7b\u9053\u4eba\u7684\u4e3e\u6b62\uff0c\u89c2\u95e8\u524d\u90a3\u4e2d\u5e74\u9053\u4eba\u7684\u795e\u6001\uff0c\u90a3\u4e9b\u6b32\u8a00\u53c8\u6b62\u7684\u8a00\u8bed\uff0c\u6b64 \u523b\u90fd\u4e00\u4e00\u95ea\u8fc7\u4f0a\u98ce\u5fc3\u5934\u3002 \u3000\u3000\u4f0a\u98ce\u77e5\u9053\u8fd9\u5999\u96e8\u9053\u4eba\u6b64\u6765\uff0c\u5176\u4e2d\u5fc5\u5b9a\u6709\u7740\u4e9b\u8e4a\u8df7\u3002\u4f46\u5176\u4e2d\u7a76\u7adf\u5982\u4f55\uff0c\u4ed6\u5374\u4e5f\u6478\u4e0d\u6e05\u695a\uff0c \u53ea\u5f97\u9759\u5f85\u6b64\u4e8b\u53d1\u5c55\u4e0b\u53bb\u3002 \u3000\u3000\u56db\u5ea7\u7fa4\u8c6a\uff0c\u6115\u7136\u76f8\u987e\uff0c\u6240\u62b1\u7684\u5fc3\u91cc\uff0c\u6b63\u4e5f\u548c\u4f0a\u98ce\u76f8\u540c\u3002 \u3000\u3000\u5999\u6cd5\u9053\u4eba\u9762\u8272\u9aa4\u53d8\u4e4b\u540e\uff0c\u76ee\u5149\u4e00\u76f4\u77aa\u5728\u90a3\u5999\u96e8\u9053\u4eba\u9762\u4e0a\uff0c\u6b64\u523b\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5999\u6cd5\u4e0d\u624d\uff0c\u53ef\u4e0d\u6562\u505a\u9601\u4e0b\u7684\u5e08\u5144\uff0c\u6b7b\u53bb\u7684\u5e08\u5c0a\uff0c\u6b64\u523b\u82e5\u6709\u77e5\uff0c\u4e5f\u65ad\u65ad\u4e0d\u6562\u627f\u8ba4\u6709\u9601\u4e0b \u5982\u6b64\u9ad8\u4eba\u7684\u5f1f\u5b50\u201d \u3000\u3000\u5999\u96e8\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u5e08\u5144\uff01\u4f60\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u5c0f\u5f1f\u867d\u7136\u4e00\u522b\u7ec8\u5357\u5341\u4f59\u5e74\uff0c\u4f46\u5fc3\u4e2d\u5374\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u60e6\u8bb0\u7740\u5e08 \u95e8\u3002\u800c\u4e14\u5c0f\u5f1f\u867d\u7136\u8fdc\u6e38\u5728\u5916\uff0c\u5374\u4e5f\u59cb\u7ec8\u6ca1\u6709\u88ab\u9010\u51fa\u95e8\u5899\u5440\uff01\u96be\u9053\u5e08\u5144\u4f60\u4eca\u65e5\u5374\u8981\u628a\u5c0f\u5f1f\u9010\u51fa \u95e8\u5916\u5417\uff1f\u201d \u3000\u3000\u4ed6\u9634\u68ee\u4e4b\u6781\u5730\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u5e08\u5085\u5728\u751f\u524d\uff0c\u6ca1\u6709\u9010\u51fa\u5c0f\u5f1f\uff0c\u5c0f\u5f1f\u5c31\u4ecd\u7136\u662f\u7ec8\u5357\u5f1f\u5b50\u3002\u5e08\u5144\u4f60\u7eb5\u7136\u5bf9\u5c0f\u5f1f\u4e0d\u6ee1\uff0c \u53ef\u4e5f\u4e0d\u80fd\u516c\u62a5\u79c1\u4ec7\uff0c\u786c\u6307\u5c0f\u5f1f\u4e0d\u662f\u7ec8\u5357\u5f1f\u5b50\u54e9\uff01\u201d \u3000\u3000\u5999\u6cd5\u9053\u4eba\u9762\u76ee\u66f4\u662f\u53d8\u8272\u3002\u90a3\u77e5\u5999\u5143\u9053\u4eba\u5374\u4e00\u6b65\u62a2\u4e0a\u524d\u6765\uff0c\u671d\u5999\u96e8\u8eac\u8eab\u65bd\u4e86\u4e00\u793c\u3002\u5999\u96e8\u9053 \u4eba\u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u4e94\u5e08\u5f1f\uff01\u4f60\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u6709\u6211\u8fd9\u4e48\u4e00\u4e2a\u5e08\u5144\u3002\u201d\u5999\u5143\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5f1f\u867d\u672a\u5fd8\u8bb0\u5e08\u5144\uff0c\u5374\u53ea\u6015\u5e08\u5144\u65e9\u5df2\u5fd8\u8bb0\u5c0f\u5f1f\u4eec\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u4e00\u5f20\uff0c\u58f0\u8272\u8f6c\u5389\uff0c\u9053\uff1a \u3000\u3000\u201c\u8bf7\u95ee\u5e08\u5144\uff01\u82e5\u4f60\u8fd8\u672a\u5fd8\u8bb0\u5e08\u95e8\uff0c\u5e08\u5085\u4ed9\u53bb\u65f6\u5e08\u5144\u600e\u5730\u4e0d\u6765\uff1f\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u540d\u6ee1\u5929 \u4e0b\uff0c\u53ef\u662f\u53c8\u6709\u8c01\u77e5\u9053\u8fd9\u4f4d\u591a\u624b\u771f\u4eba\u5c31\u662f\u7ec8\u5357\u5f1f\u5b50\uff1f\u600e\u5730\u5e08\u5144\u65e9\u4e0d\u60f3\u8d77\u5e08\u95e8\u665a\u4e0d\u60f3\u8d77\u5e08\u95e8\uff0c\u5374 \u504f\u504f\u5728\u6b64\u65f6\u60f3\u8d77\u5e08\u95e8\uff1f\u96be\u9053\u8fd9\u533a\u533a\u7ec8\u5357\u638c\u95e8\u4e4b\u5e2d\uff0c\u8fd8\u653e\u5728\u4f60\u591a\u624b\u771f\u4eba\u7684\u773c\u91cc\u5417\uff1f\u201d \u3000\u3000\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u66f4\u52a0\u6fc0\u6602\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6614\u5e74\u4f60\u6211\u5e08\u5144\u5f1f\u516d\u4eba\uff0c\u5e08\u5085\u5f85\u4f60\u6700\u539a\u3002\u53ef\u662f\u5e08\u5144\u4f60\u5374\u7f6e\u5e08\u95e8\u58f0\u540d\u4e0d\u987e\uff0c\u5728\u6c5f\u6e56\u4e0a\u505a\u51fa \u8bb8\u591a\u8d25\u574f\u5e08\u95e8\u7684\u4e8b\uff0c\u53ef\u53f9\u5e08\u5c0a\u4e34\u53bb\u65f6\uff0c\u5374\u4ecd\u6302\u5ff5\u7740\u4f60\uff0c\u4e0d\u80af\u5c06\u4f60\u9010\u51fa\u95e8\u5916\u3002\u5e08\u5144\uff01\u4f60\u5982\u7a0d\u6709 \u826f\u5fc3\uff0c\u5c31\u8be5\u8ff7\u9014\u77e5\u8fd4\u3002\u90a3\u77e5\u5e08\u5144\u4f60\u2026\u2026\u4f60\u5374\u53c8\u6295\u5165\u2026\u2026\u201d \u3000\u3000\u5999\u96e8\u9053\u4eba\u59cb\u7ec8\u51b7\u7b11\u503e\u542c\u7740\uff0c\u6b64\u523b\u7a81\u5730\u4e00\u58f0\u5389\u53f1\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u5999\u5143\uff01\u4f60\u518d\u8981\u80e1\u8a00\uff0c\u6211\u505a\u5e08\u5144\u7684\u53ef\u8981\u5f53\u7740\u6b66\u6797\u7fa4\u8c6a\uff0c\u6559\u8bad\u6559\u8bad\u4f60\u8fd9\u4e2a\u76ee\u65e0\u5c0a\u957f\u7684\u72c2 \u5f92\uff01\u201d \u3000\u3000\u5999\u5143\u51b7\u8511\u4e4b\u6781\u5730\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5929\u4e0b\u6b66\u6797\uff0c\uff0e\u8c01\u4e0d\u77e5\u9053\u4f60\u591a\u624b\u771f\u4eba\u7684\u90a3\u4e9b\u201c\u5584\u884c\u4e49\u4e3e\u201d\uff1f\u6211\u8bf4\u4e0d\u8bf4\u53c8\u6709\u4f55\u59a8\uff1f\u53ea\u662f\u8fd9 \u4e9b\u8bdd\u6211\u5374\u6709\u5982\u9aa8\u54fd\u5728\u5589\uff0c\u4e0d\u5410\u4e0d\u5feb\u3002\u201d \u3000\u3000\u56db\u5ea7\u7fa4\u8c6a\u6b64\u523b\u624d\u90fd\u8038\u7136\u52a8\u5bb9\u3002\u4ed6\u4eec\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e2a\u67af\u7626\u7684\u8001\u9053\uff0c\u5c31\u662f\u6a2a\u884c\u5ddd\u9ed4\u4e00\u5e26\uff0c\u6076 \u884c\u65e0\u6570\uff0c\u5374\u53c8\u6781\u5c11\u6709\u4eba\u89c1\u5230\u771f\u9762\u76ee\u7684\u9b54\u5934\u2014\u2014\u591a\u624b\u771f\u4eba\u3002 \u3000\u3000\u66f4\u60f3\u4e0d\u5230\u8fd9\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\uff0c\u7adf\u4f1a\u662f\u7ec8\u5357\u95e8\u4e0b\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u8fd9\u5999\u96e8\u9053\u4eba\u548c\u7ec8\u5357\u6d3e\u5176\u4e2d\u7684\u7ea0\u845b\uff0c\u7fa4\u8c6a\u6b64\u523b\u4ea6\u90fd\u4ece\u5999\u5143\u9053\u4eba\u4e49\u6b63\u8bcd\u4e25\u7684\u8fd9\u4e00\u5e2d\u8bdd\u4e2d\uff0c\u604d \u7136\u5f97\u77e5\u4e86\u771f\u76f8\uff0c\u4e0d\u7981\u7eb7\u7eb7\u8bae\u8bba\u7740\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u8bae\u8bba\u4e4b\u58f0\uff0c\u5374\u662f\u6781\u4e3a\u8f7b\u5fae\u7684\uff0c\u66f4\u6ca1\u6709\u4e00\u4eba\u633a\u8eab\u51fa\u6765\u8bf4\u8bdd\u3002 \u3000\u3000\u5999\u6cd5\u771f\u4eba\u6b64\u523b\u4e5f\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u4f55\u51b5\u4f60\u53c8\u52a0\u5165\u4e86\u5929\u4e89\u6559\u4e0b\uff01\u6b64\u523b\u4f60\u7109\u6562\u518d\u65e0\u803b\u5730\u56de\u6765\u4e89\u8fd9\u638c\u95e8\u4eba\u4e4b\u4f4d\uff0c\u96be\u9053\u4f60\u4ee5\u4e3a\u4f60 \u7684\u6240\u4e3a\uff0c\u522b\u4eba\u90fd\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u4f0a\u98ce\u4e0d\u7981\u66f4\u60ca\uff0c\u8fd9\u591a\u624b\u771f\u4eba\u65e2\u5df2\u5165\u4e86\u5929\u4e89\u6559\uff0c\u6b64\u523b\u5374\u53c8\u6765\u9010\u9e7f\u8fd9\u7ec8\u5357\u638c\u95e8 \u4f4d\uff0c\u5176\u7528\u5fc3\u4e0d\u96be\u60f3\u89c1\u3002 \u3000\u3000\u201c\u770b\u6765\u8fd9\u5929\u4e89\u6559\u4e0d\u4f46\u60f3\u79f0\u5c0a\u6b66\u6797\uff0c\u7adf\u8fd8\u60f3\u5c06\u5404\u95e8\u5404\u6d3e\u4e00\u7f51\u6253\u5c3d\u3002\u82e5\u771f\u8ba9\u8fd9\u5929\u4e89\u6559\u5f92\u4f5c\u4e86 \u7ec8\u5357\u638c\u6559\uff0c\u90a3\u5929\u4e0b\u6b66\u6797\uff0c\u773c\u89c1\u5c31\u5c06\u518d\u65e0\u77a7\u7c7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u81f3\u6b64\uff0c\u5fc3\u4e2d\u70ed\u8840\u7ffb\u6d8c\uff0c\u51e0\u4e4e\u8981\u633a\u8eab\u800c\u51fa\u3002 \u3000\u3000\u516b\u5366\u795e\u638c\u6b64\u523b\u4e5f\u4e00\u634b\u957f\u987b\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u6309\u7406\u8bf4\uff1a\u5999\u96e8\u9053\u957f\u65e2\u672a\u88ab\u9010\u51fa\u95e8\u5899\uff0c\u81ea\u5e94\u4ecd\u7b97\u7ec8\u5357\u95e8\u5f92\u3002\u4f46\u82e5\u5999\u96e8\u9053\u4eba\u771f\u7684\u5165\u4e86\u201c\u5929 \u4e89\u6559\u201d\uff0c\u90a3\u4e48\u518d\u4e89\u7ec8\u5357\u638c\u6559\uff0c\u5c31\u6709\u4e9b\u4e0d\u4fbf\u4e86\u3002\u201d \u3000\u3000\u5999\u96e8\u9053\u4eba\u5374\u7a81\u5730\u4ef0\u5929\u4e00\u9635\u957f\u7b11\uff0c\u7b11\u58f0\u7adf\u5982\u91d1\u77f3\uff0c\u9707\u5f97\u56db\u95f4\u55e1\u7136\u4f5c\u54cd\u3002\u8fde\u5927\u6bbf\u627f\u5c18\u4e0a\u7684\u79ef \u5c18\uff0c\u6b64\u523b\u7adf\u90fd\u7c0c\u7c0c\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7fa4\u8c6a\u76f8\u987e\u53d8\u8272\u4e4b\u95f4\uff0c\u7b11\u58f0\u621b\u7136\u800c\u6b62\uff0c\u4f59\u97f3\u867d\u4ecd\u7ed5\u6881\uff0c\u4f46\u5927\u5bb6\u8033\u7554\u5374\u90fd\u500f\u7136\u4e00\u8f7b\u3002 \u3000\u3000\u5999\u96e8\u771f\u4eba\u53cc\u76ee\u4e00\u5f20\uff0c\u51b7\u7136\u9053\uff1a \u3000\u3000\u201c\u6709\u8c01\u8bf4\u7ec8\u5357\u5f1f\u5b50\u5165\u4e0d\u5f97\u201c\u5929\u4e89\u6559\u201d\uff1f\u6709\u8c01\u8bf4\u201c\u5929\u4e89\u6559\u201d\u5f92\u505a\u4e0d\u5f97\u7ec8\u5357\u5f1f\u5b50.\u6211\u5999\u96e8\u867d \u5165\u201c\u5929\u4e89\u6559\u201d\uff0c\u5374\u4ecd\u7136\u662f\u7ec8\u5357\u95e8\u5f92\uff0c\u6709\u8c01\u8bf4\u6211\u4e89\u4e0d\u5f97\u7ec8\u5357\u638c\u6559\uff1f\u201d \u3000\u3000\u4ed6\u50b2\u7136\u56db\u987e\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u597d\u6559\u5404\u4f4d\u5e08\u5144\u5f1f\u5f97\u77e5\uff0c\u4e0d\u4f46\u6211\u5999\u96e8\u91cd\u5f52\u5e08\u95e8\uff0c\u957f\u6c5f\u5357\u5317\uff0c\u5927\u6cb3\u4e24\u5cb8\u7684\u6240\u6709\u540d\u5251\u624b\uff0c\u6b64 \u523b\u4e5f\u90fd\u5165\u4e86\u6211\u7ec8\u5357\u95e8\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u53f3\u624b\u671d\u968f\u4ed6\u8fdb\u6765\u7684\u5341\u4f59\u4e2a\u84dd\u888d\u9053\u4eba\u9010\u4e00\u6307\u70b9\u7740\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u52b3\u5c71\u4e09\u5251\u6c6a\u6c0f\u5144\u5f1f\uff0c\u4e00\u5251\u9707\u91d1\u9675\u80e1\u5927\u4fa0\uff0c\u5357\u5bab\u53cc\u5251\u674e\u6c0f\u6606\u4ef2\uff0c\u71d5\u5c71\u4e09\u5251\uff0c\u592a\u6e56\u4e00\u5251\uff0c \u8fd9\u51e0\u4f4d\u5251\u5ba2\u7684\u5927\u540d\uff0c\u60f3\u5404\u4f4d\u4e5f\u90fd\u542c\u5230\u8fc7\u5427\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u4ef0\u5929\u4e00\u9635\u957f\u7b11\uff0c\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\u8fd9\u4e9b\u58f0\u540d\u663e\u8d6b\u7684\u540d\u5251\u5ba2\uff0c\u5168\u90fd\u5165\u4e86\u6211\u7ec8\u5357\u95e8\u4e0b\uff0c\u773c\u770b\u7ec8\u5357\u4e00\u6d3e\uff0c\u884c\u5373\u5149\u8000\u6b66\u6797\uff0c \u5e08\u5085\u5728\u5929\u4e4b\u7075\u6709\u77e5\uff0c\u4e5f\u8be5\u542b\u7b11\u4e5d\u6cc9\u4e86\u3002\u201d \u3000\u3000\u516b\u5366\u795e\u638c\u8138\u4e0a\u5374\u6709\u4e9b\u4e0d\u60a6\uff0c\u4ed6\u672c\u662f\u8001\u800c\u76ca\u8fa3\u7684\u59dc\u6842\u4e4b\u6027\uff0c\u6b64\u523b\u4e24\u9053\u7070\u767d\u7684\u957f\u7709\u4e00\u7acb\uff0c\u6b63 \u5f85\u53d1\u8bdd\uff0c \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u7ae0\u3000\u949f\u6572\u5341\u54cd>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u7ae0\u3000\u949f\u6572\u5341\u54cd \u3000\u3000\u90a3\u77e5\u8eab\u4fa7\u7a81\u7136\u54cd\u8d77\u5999\u5143\u9053\u4eba\u6e05\u6717\u7684\u53e3\u97f3\u3002 \u3000\u3000\u201c\u4f60\u80dc\u5f97\u4e86\u6211\uff0c\u518d\u4e89\u7ec8\u5357\u638c\u95e8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u8eab\u968f\u8bdd\u5230\uff0c\u638c\u98ce\u55d6\u7136\uff0c\u5df2\u81ea\u88ad\u5411\u5999\u96e8\u9053\u4eba\u7684\u524d\u80f8\u3002 \u3000\u3000\u5999\u96e8\u9053\u4eba\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\u6781\uff01\u8ba9\u5e08\u5144\u6211\u770b\u770b\u4f60\u8fd9\u4e9b\u5e74\u6765\uff0c\u529f\u529b\u8fdb\u6b65\u4e86\u51e0\u8bb8\uff1f\u201d \u3000\u3000\u8eab\u5f62\u8f6c\u6298\u4e4b\u95f4\uff0c\u5999\u5143\u9053\u4eba\u5feb\u5982\u95ea\u7535\u7684\u4e00\u638c\uff0c\u5df2\u81ea\u9012\u7a7a\u3002 \u3000\u3000\u5999\u5143\u632b\u6b65\u584c\u8170\uff0c\u53f3\u638c\u56de\u6536\uff0c\u5237\u5730\u4e00\u638c\u5f27\u5f62\u5207\u4e0b\uff1b\u5de6\u638c\u5374\u5e76\u6307\u5982\u621f\uff0c\u5e26\u7740\u4e00\u7f15\u9510\u98ce\uff0c\u76f4\u70b9 \u5999\u96e8\u9053\u4eba\u524d\u80f8\u7684\u201c\u671f\u95e8\u201d\u7a74\u3002 \u3000\u3000\u5999\u96e8\u51b7\u7b11\u58f0\u4e2d\uff0c\u811a\u6b65\u518d\u4e00\u9519\uff0c\u53e3\u4e2d\u9053\uff1a \u3000\u3000\u201c\u505a\u5e08\u5144\u7684\u5148\u8ba9\u4f60\u4e09\u62db\u3002\u201d \u3000\u3000\u5999\u5143\u7684\u53cc\u638c\uff0c\u53c8\u582a\u582a\u843d\u7a7a\u3002\u4ed6\u5389\u53f1\u4e00\u58f0\uff0c\u53cc\u638c\u500f\u7136\u56de\u6536\u3002\u4e00\u541e\u4e00\u5410\uff0c\u7adf\u4ee5\u201c\u6392\u5c71\u638c\u201d\u51fb \u5411\u5999\u96e8\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u5df2\u4f7f\u51fa\u5168\u529b\uff0c\u638c\u98ce\u864e\u864e\uff0c\u9707\u5f97\u5999\u96e8\u771f\u4eba\u7684\u8863\u8882\u5fae\u626c\uff0c\u8fd9\u65f6\u5019\u53ef\u770b\u51fa\u8fd9\u591a\u624b\u771f\u4eba\u7684 \u771f\u529f\u592b\u6765\uff0c\u4ed6\u7adf\u5927\u4ef0\u8eab\uff0c\u7626\u5c0f\u7684\u8eab\u8eaf\u7b14\u76f4\u5730\u5012\u4e86\u4e0b\u53bb\uff0c\u7adf\u4ee5\u201c\u94c1\u677f\u6865\u201d\u8fd9\u79cd\u9669\u4e4b\u53c8\u9669\u7684\u529f \u592b\uff0c\u8eb2\u5f00\u6b64\u62db\u3002 \u3000\u3000\u987b\u77e5\u201c\u94c1\u677f\u6865\u201d\u8fd9\u7c7b\u529f\u592b\uff0c\u9ad8\u624b\u6bd4\u6597\u65f6\uff0c\u9664\u975e\u4e07\u4e0d\u5f97\u5df2\uff0c\u90fd\u4e0d\u6562\u8f7b\u4f7f\u3002\u7686\u56e0\u8eab\u5f62\u4e00\u540e \u4ef0\uff0c\u4e0a\uff0c\u4e2d\uff0c\u4e0b\u4e09\u5904\u7a7a\u95e8\u90fd\u5927\u9732\uff0c\u7b49\u4e8e\u5c06\u81ea\u5df1\u5168\u8eab\u5356\u7ed9\u4e86\u4eba\u5bb6\uff0c\u5bf9\u65b9\u53ea\u8981\u51cc\u7a7a\u518d\u65bd\u4e00\u51fb\uff0c\u90a3 \u4e48\u81ea\u5df1\u5c31\u7b97\u4e0d\u88ab\u51fb\u4e2d\uff0c\u4f46\u52bf\u5fc5\u8981\u88ab\u522b\u4eba\u62a2\u5f97\u5148\u673a\u3002 \u3000\u3000\u8fd9\u5999\u96e8\u9053\u4eba\u6b64\u62db\u8f7b\u6613\u4e00\u4f7f\uff0c\u7fa4\u8c6a\u5374\u5fae\u54a6\u4e86\u4e00\u58f0\u3002\u5999\u5143\u9053\u4eba\u95f7\u542d\u4e00\u58f0\uff0c\u786c\u751f\u751f\u5c06\u524d\u51fb\u7684\u529b \u9053\u62c9\u56de\uff0c\u53cc\u638c\u500f\u7136\u4e0b\u5207\u3002 \u3000\u3000\u90a3\u77e5\u5999\u96e8\u9053\u4eba\u8fd9\u79cd\u8eab\u5f62\u4e0b\uff0c\u811a\u8ddf\u4ecd\u80fd\u4e00\u65cb\uff0c\u5012\u5367\u7740\u7684\u8eab\u8eaf\uff0c\u4fbf\u500f\u7136\u53d8\u4e86\u4e2a\u4f4d\u7f6e\u3002\u5999\u5143\u52bf \u631f\u96f7\u9706\u7684\u53cc\u638c\uff0c\u4fbf\u53c8\u518d\u6b21\u843d\u7a7a\u3002 \u3000\u3000\u5c31\u5728\u5999\u5143\u9053\u4eba\u65e7\u529b\u5df2\u5c3d\uff0c\u65b0\u529b\u672a\u751f\uff0c\u8fd9\u79cd\u9752\u9ec4\u4e0d\u63a5\u7684\u5f53\u821f\uff0c\u5999\u96e8\u9053\u4eba\u8eab\u5f62\u5fae\u5fae\u5411\u4e0a\u4e00 \u62ac\uff0c\u53f3\u638c\u659c\u6325\uff0c\u5237\u5730\u4e00\u638c\uff0c\u5df2\u51fb\u5728\u5999\u5143\u7684\u5de6\u80c1\u4e0a\u3002 \u3000\u3000\u5999\u5143\u9053\u4eba\u8eab\u5f62\u4e00\u6447\uff0c\u5e76\u672a\u5012\u4e0b\uff0c\u539f\u6765\u8fd9\u5999\u96e8\u9053\u4eba\u6b64\u638c\uff0c\u53ea\u4f7f\u51fa\u534a\u6210\u771f\u529b\u800c\u5df2\u3002\u6b64\u523b\u4ed6\u671b \u7740\u5999\u5143\u9053\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5e08\u5f1f\uff01\u4f60\u8fd8\u5f97\u518d\u8ddf\u5e08\u5144\u6211\u5b66\u51e0\u5e74\u5462\u3002\u201d \u3000\u3000\u8bed\u6c14\u4e4b\u4e2d\uff0c\u6ee1\u542b\u8ba5\u5632\u3002 \u3000\u3000\u5999\u5143\u9053\u4eba\u4e09\u62db\u843d\u7a7a\uff0c\u5374\u88ab\u4eba\u5bb6\u4e00\u51fa\u624b\u4fbf\u51fb\u4e2d\u81ea\u5df1\uff0c\u6b64\u523b\u4ed6\u7adf\u50cf\u6115\u4f4f\u4e86\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd \u6765\u3002 \u3000\u3000\u7fa4\u8c6a\u4e5f\u90fd\u5927\u60ca\uff0c\u8fd9\u5999\u5143\u9053\u4eba\u7684\u529f\u592b\uff0c\u65b9\u624d\u4ed6\u4eec\u662f\u4eb2\u773c\u89c1\u5230\u7684\uff0c\u6b64\u523b\u5728\u5999\u96e8\u4e00\u62db\u4e4b\u4e0b\uff0c\u4fbf \u544a\u843d\u8d25\uff0c\u5927\u5bb6\u4e0d\u7981\u90fd\u88ab\u90a3\u5999\u96e8\u7684\u6b66\u529f\u9707\u4f4f\u3002 \u3000\u3000\u5999\u6cd5\u9053\u4eba\u6b64\u523b\u9762\u5982\u9752\u94c1\uff0c\u4e00\u6b65\u63a0\u4e86\u4e0a\u6765\uff0c\u5c06\u5999\u5143\u9053\u4eba\u5fae\u5fae\u4e00\u63a8\uff0c\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u4e94\u5e08\u5f1f\uff01\u4f60\u5148\u9000\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53cc\u76ee\u4e00\u5f20\uff0c\u7d27\u7d27\u77aa\u5728\u5999\u96e8\u9053\u4eba\u7684\u8138\u4e0a\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u6b66\u529f\u679c\u7136\u7cbe\u8fdb\uff0c\u53ea\u662f\u4f60\u6b66\u529f\u7eb5\u7136\u518d\u9ad8\uff0c\u6211\u7ec8\u5357\u95e8\u4e0b\u6240\u6709\u7684\u5f1f\u5b50\uff0c\u4e5f\u4e0d\u4f1a \u627f\u8ba4\u4f60\u8fd9\u8d25\u7c7b\u662f\u638c\u95e8\u4eba\u3002\u201d \u3000\u3000\u5999\u96e8\u9053\u4eba\u53c8\u4ef0\u5929\u957f\u7b11\u8d77\u6765 \u3000\u3000\u90a3\u77e5\u7a81\u5730\u53c8\u6709\u4e00\u9635\u66f4\u4e3a\u5639\u4eae\u7684\u7b11\u58f0\uff0c\u54cd\u81ea\u795e\u9f9b\u524d\u4fa7\u3002 \u3000\u3000\u7fa4\u8c6a\u9669\u4e9b\u63a9\u4f4f\u8033\u6735\uff0c\u8be7\u7136\u671b\u53bb\uff0c\u5374\u89c1\u5230\u90a3\u59cb\u7ec8\u4e0d\u8a00\u4e0d\u52a8\u7684\u9752\u6d77\u6765\u5ba2\u94b1\u7fca\uff0c\u6b64\u523b\u5927\u7b11\u7740\u7f13 \u6b65\u8d70\u4e86\u51fa\u6765\uff0c\u9510\u5229\u7684\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u6717\u58f0\u7684\u5927\u7b11\uff0c\u4e5f\u500f\u7136\u8f6c\u53d8\u6210\u51b7\u68ee\u7684\u51b7\u7b11\uff0c\u671b\u7740\u5999\u6cd5\uff0c\u7f13 \u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5374\u8ba9\u533a\u533a\u5728\u4e0b\u6709\u4e9b\u4e0d\u61a7\u4e86\uff0c\u8d35\u6d3e\u6b64\u6b21\u5927\u9009\u638c\u95e8\uff0c\u53c8\u52b3\u52a8\u4e86\u5929\u4e0b\u6b66\u6797\u8c6a\u58eb\uff0c\u4e3a\u7684\u60f3\u5fc5 \u5c31\u662f\u201c\u516c\u6b63\u201d\u4e24\u5b57\u800c\u5df2\u3002\u8fd9\u5999\u96e8\u9053\u957f\uff0c\u65e2\u662f\u7ec8\u5357\u95e8\u4e0b\uff0c\u53c8\u6280\u538b\u5f53\u573a\uff0c\u81ea\u7136\u5c31\u662f\u7ec8\u5357\u638c\u6559\u3002\u96be \u9053\u9601\u4e0b\u5f53\u7740\u5929\u4e0b\u82f1\u96c4\u4e4b\u9762\uff0c\u8fd8\u60f3\u81ea\u98df\u5176\u8a00\uff0c\u51fa\u5c14\u53cd\u5c14\u5417\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u51b7\u68ee\u4e4b\u6781\u5730\u4e00\u7b11\u3002 \u3000\u3000\u5999\u6cd5\u9053\u4eba\u5df2\u81ea\u9762\u76ee\u53d8\u8272\u5730\u53f1\u9053\uff1a \u3000\u3000\u201c\u655d\u6d3e\u4e4b\u4e8b\uff0c\u655d\u6d3e\u5f1f\u5b50\u81ea\u4f1a\u6599\u7406\uff0c\u4e0d\u52b3\u9601\u4e0b\u4e3a\u655d\u6d3e\u64cd\u5fc3\u3002\u201d \u3000\u3000\u867d\u662f\u6c14\u5fff\u586b\u80f8\uff0c\u4f46\u8fd9\u8001\u6210\u6301\u91cd\u7684\u9053\u4eba\uff0c\u6b64\u523b\u4ecd\u5f3a\u81ea\u5fcd\u7740\u3002 \u3000\u3000\u94b1\u7fca\u5374\u53c8\u4ef0\u5929\u6253\u4e86\u4e2a\u201c\u54c8\u54c8\u201d\uff0c\u51b7\u7136\u9053\uff1a \u3000\u3000\u201c\u5929\u4e0b\u4e8b\u5929\u4e0b\u4eba\u5c3d\u90fd\u5f97\u7ba1\uff0c\u4f60\u7ec8\u5357\u6d3e\u4e2d\u7684\u4e8b\uff0c\u82e5\u662f\u4e0d\u5bb9\u522b\u4eba\u8fc7\u95ee\uff0c\u53c8\u4e3a\u4f55\u8981\u8ba9\u5929\u4e0b\u6b66\u6797 \u82f1\u96c4\uff0c\u5954\u6ce2\u800c\u6765\uff1f\u96be\u9053\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\uff0c\u90fd\u8be5\u53d7\u9601\u4e0b\u7684\u652f\u4f7f\uff1f\u4efb\u9601\u4e0b\u547c\u4e4b\u5373\u6765\uff0c\u6325\u4e4b\u5373\u53bb \u5417\uff1f\u201d \u3000\u3000\u5999\u6cd5\u9053\u4eba\u672c\u4e0d\u5584\u8a00\u8bcd\uff0c\u6b64\u523b\u88ab\u8fd9\u79cd\u950b\u5229\u7684\u8bcd\u950b\u8fd9\uff0c\u8d8a\u53d1\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5999\u96e8\u9053\u4eba\u5374\u5411\u94b1\u7fca\u4e00\u62f1\u624b\uff0c\u6717\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u65e2\u4e3a\u82b8\u82b8\u6b66\u6797\uff0c\u4e3b\u6301\u516c\u9053\uff0c\u8d2b\u9053\u611f\u5fbc\u4e4b\u4f59\uff0c\u53ea\u5f97\u8eab\u53d7\u4e86\u3002\u201d \u3000\u3000\u4ed6\u54b3\u55fd\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u81ea\u4eca\u65e5\u8d77\uff0c\u8d2b\u9053\u5999\u96e8\u4fbf\u662f\u7ec8\u5357\u638c\u95e8\uff0c\u6709\u52b3\u5404\u4f4d\u8c6a\u6770\u4e4b\u5904\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u672a\u4e86\uff0c\u5999\u6cd5\u5df2\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u53db\u5f92\uff01\u4f60\u7ed9\u6211\u4e0b\u6765\uff01\u201d \u3000\u3000\u968f\u7740\u8bed\u58f0\uff0c\u8eab\u5f62\u5411\u5999\u96e8\u731b\u6251\u4e86\u8fc7\u53bb\uff0c\u5341\u6307\u7b95\u5f20\uff0c\u6293\u5411\u5999\u96e8\u7684\u5589\u5934\uff0c\u4ed6\u548c\u8eab\u800c\u6251\uff0c\u7adf\u662f\u4e0d \u8981\u547d\u7684\u7740\u6570\u3002 \u3000\u3000\u5999\u96e8\u4e00\u770b\u4ed6\u8fd9\u79cd\u6253\u6cd5\uff0c\u53ef\u4e5f\u6709\u4e9b\u5403\u60ca\uff0c\u8eab\u5f62\u4e00\u626d\uff0c\u5411\u65c1\u8fb9\u8ba9\u5f00\u4e09\u5c3a\uff0c\u5374\u89c9\u5f97\u81ea\u5df1\u8eab\u65c1\uff0c \u98ce\u58f0\u4e00\u51db\uff0c\u63a5\u7740\u545c\u7136\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u4ed6\u5b9a\u775b\u4e00\u770b\uff0c\u5999\u6cd5\u9053\u4eba\u5df2\u8fdc\u8fdc\u843d\u5728\u5730\u4e0a\u3002\u90a3\u94b1\u7fca\u5374\u5fae\u5fae\u51b7\u7b11\u7ad9\u5728\u4ed6\u8eab\u4fa7\uff0c\u53f3\u624b\u4ecd\u4e0d\u4f4f\u73a9 \u5f04\u7740\u8170\u95f4\u7684\u4e1d\u5e26\uff0c\u5fae\u5fae\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u94b1\u7fca\u5012\u8981\u4e3a\u6b66\u6797\u4e3b\u6301\u516c\u9053\uff0c\u8fd9\u5999\u96e8\u9053\u957f\u51ed\u4ec0\u4e48\u4e0d\u80fd\u505a\u7ec8\u5357\u7684\u638c\u95e8\uff1f\u201d \u3000\u3000\u539f\u6765\u65b9\u624d\u5999\u6cd5\u548c\u8eab\u4e4b\u4e00\u6251\uff0c\u524d\u80f8\u7a7a\u95e8\u5927\u9732\uff0c\u6b63\u662f\u72af\u4e86\u6b66\u5bb6\u5927\u5fcc\uff0c\u88ab\u94b1\u7fca\u4ee5\u6781\u5feb\u7684\u8eab\u6cd5\uff0c \u63a0\u4e86\u8fc7\u6765\uff0c\u4e58\u9699\u5f53\u80f8\u4e00\u638c\uff0c\u51fb\u5728\u4ed6\u524d\u80f8\u4e0a\u3002 \u3000\u3000\u8fd9\u4e24\u4e0b\u8eab\u624b\u90fd\u5feb\uff0c\u7fa4\u8c6a\u53ea\u89c9\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u5999\u6cd5\u5df2\u8dcc\u5728\u5730\u4e0a\uff0c\u7adf\u4e5f\u662f\u4e00\u62db\u4e4b\u4e0b\uff0c\u4fbf\u5206\u51fa \u80dc\u8d1f\uff0c\u4f17\u4eba\u4e0d\u7981\u90fd\u60ca\u547c\u51fa\u58f0\u6765\u3002 \u3000\u3000\u94b1\u7fca\u53cc\u773c\u671b\u5929\uff0c\u624b\u91cc\u73a9\u5f04\u7740\u4e1d\u5e26\uff0c\u5fae\u5fae\u51b7\u7b11\u7740\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7ec8\u5357\u5f1f\u5b50\u4e2d\uff0c\u82e5\u8fd8\u6709\u4e0d\u670d\u5999\u96e8\u9053\u957f\u7684\uff0c\u81ea\u53ef\u4e0e\u4ed6\u4e00\u8f83\u8eab\u624b\uff0c\u4e89\u90a3\u638c\u6559\u4e4b\u5e2d\uff0c\u6b66\u6797\u7fa4\u8c6a \u4e2d\u82e5\u8fd8\u6709\u8ba4\u4e3a\u533a\u533a\u5728\u4e0b\u6b64\u4e3e\u4e0d\u5f53\u7684\uff0c\u4e5f\u5927\u53ef\u51fa\u6765\u8d50\u6559\u6211\u94b1\u67d0\u4eba\u51e0\u624b\u9ad8\u62db\u3002\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u4e00\u5f20\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u5230\u5728\u8fd9\u5927\u6bbf\u7684\u53f3\u540e\u4fa7\uff0c\u79bb\u4ed6\u7ea6\u83ab\u4e09\uff0c\u56db\u4e08\u8fdc\u8fd1\uff0c\u653e\u7740\u4e00\u4e2a\u67b6 \u5b50\uff0c\u4e0a\u60ac\u4e00\u4e2a\u7d2b\u94dc\u94f8\u5c31\u7684\u5927\u949f\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u4e00\u4e0b\uff0c\u53f3\u624b\u7a81\u5730\u653e\u4e0b\u4e1d\u5e26\uff0c\u671d\u90a3\u5de8\u949f\u865a\u7a7a\u4e00\u6307\uff0c\u53ea\u542c\u201c\u5f53\u201d\u5730\u4e00\u58f0\uff0c\u90a3\u5de8\u949f\u7adf\u88ab \u4ed6\u6307\u4e0a\u7684\u771f\u529b\u6572\u5f97\u4e00\u54cd\u3002 \u3000\u3000\u7fa4\u8c6a\u53c8\u590d\u88ab\u4ed6\u8fd9\u79cd\u5df2\u5165\u5316\u5883\u7684\u201c\u5f39\u6307\u795e\u9053\u201d\u7684\u6307\u4e0a\u529f\u529b\uff0c\u9707\u5f97\u5664\u82e5\u5bd2\u8749\u3002 \u3000\u3000\u4ed6\u6717\u58f0\u4e00\u7b11\uff0c\u53c8\u590d\u50b2\u7136\u9053\uff1a \u3000\u3000\u201c\u6b64\u523b\u949f\u6572\u4e00\u54cd\uff0c\u82e5\u949f\u54cd\u5341\u54cd\u4e4b\u540e\uff0c\u82e5\u5404\u4f4d\u4ecd\u65e0\u5f02\u8bae\uff0c\u5999\u96e8\u9053\u957f\u4ece\u6b64\u4fbf\u662f\u7ec8\u5357\u638c\u6559\u3002\u201d \u3000\u3000\u8bf4\u7f62\u624b\u6307\u5fae\u626c\uff0c\u90a3\u5de8\u949f\u53c8\u201c\u5f53\u201d\u5730\u4e00\u58f0\u5de8\u54cd\u3002 \u3000\u3000\u516b\u5366\u795e\u638c\u5e9f\u7136\u4e00\u58f0\u957f\u53f9\uff0c\u4ed6\u81ea\u95ee\u4ee5\u4ed6\u81ea\u8eab\u6570\u5341\u5e74\u7684\u529f\u529b\uff0c\u4ecd\u4e0d\u662f\u8fd9\u5c11\u5e74\u7684\u654c\u624b\u3002\u957f\u53f9\u58f0 \u4e2d\uff0c\u888d\u8896\u4e00\u62c2\uff0c\u65e0\u989c\u518d\u7559\u6b64\u5730\uff0c\u7adf\u8ff3\u81ea\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u94db\u201d\u5730\uff0c\u949f\u53c8\u4e00\u54cd\u3002 \u3000\u3000\u6885\u82b1\u5251\u6b32\u524d\u53c8\u6b62\u3002\u7ec8\u5357\u5f1f\u5b50\u4e00\u4e2a\u4e2a\u9762\u5982\u6b7b\u7070\uff0c\u4e0d\u77e5\u6240\u6b62\u3002 \u3000\u3000\u949f\u518d\u4e00\u54cd\u3002 \u3000\u3000\u7384\u5316\u9053\u4eba\u524d\u8de8\u4e00\u6b65\uff0c\u5374\u89c1\u84e6\u5730\u6ee1\u6bbf\u5bd2\u5149\u66b4\u957f\uff0c\u90a3\u4e0e\u5999\u96e8\u9053\u4eba\u540c\u6765\u7684\u5341\u4f59\u84dd\u888d\u201c\u9053\u4eba\u201d\uff0c \u6b64\u65f6\u957f\u5251\u4ff1\u90fd\u51fa\u5323\uff0c\u53ea\u8981\u7384\u5316\u7a0d\u6709\u4e3e\u52a8\uff0c\u4fbf\u662f\u4e00\u573a\u8840\u6218\u3002 \u3000\u3000\u949f\u6572\u4e94\u54cd\uff0c\u516d\u54cd\u2014\u2014 \u3000\u3000\u7384\u5316\u9053\u4eba\u5fc3\u4e2d\u7d0a\u4e71\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u4ed6\u81ea\u77e5\u81ea\u5df1\u4e07\u4e07\u4e0d\u662f\u4eba\u5bb6\u654c\u624b\uff0c\u4f46\u5374\u4e5f\u4e07\u4e07\u4e0d\u80fd \u8ba9\u8fd9\u5999\u96e8\u9053\u4eba\u505a\u4e86\u7ec8\u5357\u638c\u95e8\u3002 \u3000\u3000\u949f\u54cd\u4e03\u4e0b\u2014\u2014 \u3000\u3000\u5927\u6bbf\u7684\u5de6\u4fa7\u7a81\u7136\u54cd\u8d77\u4e00\u4e2a\u6e05\u6717\u7684\u53e3\u97f3\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4e14\u6162\u201d\u4e00\u6761\u7070\u8272\u7684\u4eba\u5f71\uff0c\u968f\u7740\u559d\u58f0\uff0c\u5982\u7070\u9e64\u884c\u7a7a\uff0c\u4e00\u63a0\u6570\u4e08\uff0c\u4ece\u4f17\u8c6a\u5934\u4e0a\u98de\u63a0\u800c\u51fa\uff0c \u98d8\u7136\u843d\u5728\u5730\u4e0a\uff0c\u5374\u6b63\u662f\u4e45\u4e45\u672a\u4f5c\u8868\u793a\u7684\u4f0a\u98ce\u3002 \u3000\u3000\u8bf8\u8c6a\u4ff1\u90fd\u5927\u60ca\u3002\u94b1\u7fca\u4e5f\u51b7\u7b11\u4e00\u58f0\uff0c\u76ee\u5149\u5728\u4f0a\u98ce\u9762\u4e0a\u4e00\u8f6c\uff0c\u5374\u84e6\u5730\u540e\u9000\u4e00\u6b65\uff0c\u8fde\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u539f\u6765\u4f60\u4e5f\u6765\u4e86\u3002\u7b97\u6211\u591a\u4e8b\uff01\u7b97\u6211\u591a\u4e8b\uff01\u201d \u3000\u3000\u888d\u8896\u4e00\u5c55\uff0c\u7adf\u5728\u7fa4\u8c6a\u65e0\u6bd4\u7684\u60ca\u8be7\u4e4b\u4e2d\uff0c\u8eab\u5f62\u5982\u7535\u5149\u4e00\u95ea\uff0c\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u4e00\u6115\uff0c\u8111\u6d77\u4e2d\u987f\u65f6\u4e00\u4e71\uff0c\u4e0d\u77e5\u9053\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u5fae\u4e00\u6c89\u541f\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5999\u96e8\u9053\u4eba\u867d\u662f\u7ec8\u5357\u5f1f\u5b50\uff0c\u4f46\u5374\u4e0d\u5b5a\u4f17\u671b\uff0c\u600e\u80fd\u505a\u7ec8\u5357\u4e00\u6d3e\u4e4b\u638c\u95e8\uff1f\u5728\u4e0b\u6709\u9274\u4e8e\u6b64\uff0c\u4e0d \u77e5\u5404\u4f4d\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u60ca\u7591\u5927\u8d77\uff0c\u662f\u4ee5\u53e3\u4e2d\u6240\u8bf4\u4e5f\u662f\u63a2\u8be2\u4e00\u7c7b\u7684\u8bdd\u3002\u4f17\u8c6a\u51e0\u767e\u53cc\u773c\u775b\uff0c\u5728\u4f0a\u98ce\uff0e\u5999\u96e8\u9053 \u4eba\uff0c\u548c\u90a3\u5341\u4f59\u4e2a\u6301\u5251\u9053\u4eba\u8eab\u4e0a\u6e9c\u6765\u6e9c\u53bb\uff0c\u4e0d\u77e5\u9053\u5373\u5c06\u53d1\u751f\u4ec0\u4e48\u4e8b\u3002\u7ec8\u5357\u5f1f\u5b50\u66f4\u662f\u5404\u5404\u6ee1\u9762\u559c \u5bb9\u5730\u671b\u7740\u4f0a\u98ce\u3002 \u3000\u3000\u5999\u96e8\u9053\u4eba\u4ee5\u60ca\u7591\u800c\u8ff7\u60d8\u7684\u76ee\u5149\uff0c\u671b\u4e86\u4f0a\u98ce\u51e0\u773c\uff0c\u84e6\u5730\u4e00\u6325\u624b\uff0c\u90a3\u5341\u4f59\u9053\u4eba\uff0c\u7adf\u4e5f\u500f\u7136\u6536 \u56de\u957f\u5251\uff0c\u4e5f\u4ee5\u60ca\u7591\u800c\u8ff7\u60d8\u7684\u76ee\u5149\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u7adf\u4e0d\u7ea6\u800c\u540c\u5730\u8f6c\u8eab\u5411\u6bbf\u5916\u8d70\u53bb\u3002 \u3000\u3000\u5999\u96e8\u9053\u4eba\u7684\u76ee\u5149\u518d\u5411\u4f0a\u98ce\u4e00\u779f\uff0c\u548c\u4f0a\u98ce\u7684\u76ee\u5149\uff0c\u5fae\u4e00\u63a5\u89e6\uff0c\u5374\u7acb\u523b\u5782\u4e0b\u5934\u53bb\uff0c\u50cf\u662f\u6c89\u541f \u4e86\u534a\u664c\uff0c\u7adf\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u65e2\u7136\u5404\u4f4d\u610f\u89c1\u5982\u6b64\uff0c\u8d2b\u9053\u5c31\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u8eab\u5f62\u66b4\u8d77\uff0c\u7adf\u4e5f\u63a0\u51fa\u6bbf\u53bb\u3002 \u3000\u3000\u4f17\u8c6a\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u60ca\u8be7\uff0c\u4e0d\u7981\u4e00\u9f50\u7ad9\u4e86\u8d77\u6765\uff0c\u671b\u7740\u8fd9\u7a0d\u4e00\u73b0\u8eab\uff0c\u5c31\u5c06\u90a3\u72c2\u50b2\u7684\u94b1\u7fca \u548c\u5999\u96e8\u9053\u4eba\u60ca\u9000\u4e86\u7684\u5c11\u5e74\u3002 \u3000\u3000\u6709\u7684\u63a2\u9996\u6bbf\u5916\uff0c\u5915\u9633\u5c06\u4e0b\uff0c\u6f2b\u5929\u5f69\u971e\u4e2d\uff0c\u5df2\u5931\u53bb\u4e86\u90a3\u4e9b\u631f\u65e0\u6bd4\u58f0\u52bf\u800c\u6765\u7684\u84dd\u888d\u201c\u9053\u4eba\u201d \u7684\u5f71\u5b50\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u4e00\u7ae0\u3000\u53f9\u606f\u58f0\u4e2d>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u4e00\u7ae0\u3000\u53f9\u606f\u58f0\u4e2d \u3000\u3000\u4f0a\u98ce\u548c\u8427\u5357\u82f9\u4e00\u5165\u4e86\u7ec8\u5357\u5c71\u7684\u4e0a\u5c71\u8def\u5f84\u540e\uff0c\u5c31\u53d1\u89c9\u4e86\u4e8b\u6709\u8e4a\u8df7\uff0c\u7b49\u5230\u4ed6\u5728\u7384\u5999\u89c2\u7684\u89c2\u95e8 \u524d\uff0c\u770b\u5230\u4e86\u90a3\u4e2d\u5e74\u9053\u4eba\u6b32\u8a00\u53c8\u6b62\u7684\u795e\u8272\uff0c\u66f4\u52a0\u65ad\u5b9a\u4e86\u5728\u8fd9\u7ec8\u5357\u5251\u6d3e\u91cc\uff0c\u53c8\u53d1\u751f\u4e86\u4e00\u4e9b\u4e8b\u6545\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u5728\u90a3\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\uff0c\u4e5f\u5c31\u662f\u7ec8\u5357\u5f1f\u5b50\u5999\u96e8\u5c1a\u672a\u73b0\u8eab\u4e4b\u524d\uff0c\u4ed6\u5e76\u4e0d\u80fd\u786e\u5207\u5730\u77e5\u9053 \u8fd9\u4e9b\u4e8b\u6545\u7a76\u7adf\u662f\u4ec0\u4e48\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u9759\u89c2\u5f85\u53d8\u4e4b\u4e0b\uff0c\u679c\u7136\u53d1\u73b0\u8fd9\u5176\u4e2d\u7684\u9634\u8c0b\uff0c\u5929\u4e89\u6559\u7adf\u7136\u60f3\u5229\u7528\u7ec8\u5357\u6d3e\u4e2d\u7684\u4e00\u4e2a\u53db\u5f92\uff0c\u800c \u5c06\u6b66\u6797\u4e2d\u7d20\u8d1f\u6e05\u8a89\u7684\u7ec8\u5357\u5251\u6d3e\u6536\u5f52\u5230\u4ed6\u5929\u4e89\u6559\u7684\u7ec4\u7ec7\u4e0b\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5929\u4e89\u6559\u5728\u6b66\u6797\u4e2d\u7684\u6c14 \u7130\uff0c\u4e5f\u5c06\u66f4\u76db\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u5bf9\u5929\u4e89\u6559\uff0c\u9664\u4e86\u4ed6\u4e2a\u4eba\u7684\u79c1\u4ec7\u4e4b\u5916\uff0c\u8fd8\u6709\u7740\u4e00\u4efd\u4f38\u5f20\u6b63\u4e49\u3001\u6291\u5236\u5f3a\u6743\u7684\u6b63\u4e49\u4e4b\u5fc3\uff0c \u5f53\u7136\u4e0d\u4f1a\u773c\u770b\u5929\u4e89\u6559\u5f97\u624b\u3002 \u3000\u3000\u4f46\u5929\u4e89\u6559\u7684\u624b\u6bb5\u7adf\u5982\u6b64\u72e0\u6bd2\uff0c\u65b9\u6cd5\u7adf\u5982\u6b64\u4e25\u5bc6\uff0c\u7adf\u5728\u7ec8\u5357\u6d3e\u7fa4\u805a\u6b66\u6797\u7fa4\u8c6a\uff0c\u516c\u5f00\u9009\u62d4\u638c \u95e8\u7684\u65f6\u5019\uff0c\u62ac\u51fa\u4e86\u4e00\u4e2a\u5999\u96e8\u3002 \u3000\u3000\u56e0\u4e3a\u5999\u96e8\u65e2\u662f\u7ec8\u5357\u5f1f\u5b50\uff0c\u53c8\u672a\u88ab\u9010\u51fa\u95e8\u5899\uff0c\u90a3\u4e48\u4ed6\u4e5f\u53c2\u52a0\u8fd9\u9009\u62d4\u638c\u95e8\u4eba\u7684\u5927\u4f1a\uff0c\u770b\u8d77\u6765 \u81ea\u662f\u5149\u660e\u6b63\u5927\u4e4b\u4e8b\u3002 \u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u5929\u4e89\u6559\u5374\u53c8\u4ee5\u5927\u6cb3\u4e24\u5cb8\uff0c\u957f\u6c5f\u5357\u5317\u7684\u5341\u4f59\u4e2a\u540d\u5251\u624b\uff0c\u4f5c\u4e3a\u4ed6\u6b64\u4e00\u8ba1\u5212\u6b66\u529b\u7684 \u540e\u76fe\uff0c\u518d\u52a0\u4e0a\u90a3\u9752\u6d77\u7a81\u6765\u4e4b\u5ba2\u2014\u2014\u94b1\u7fca\uff0c\u795e\u5947\u83ab\u6d4b\u7684\u6b66\u529f\uff0c\u4f7f\u5f97\u5728\u573a\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u6ca1\u6709\u4e00\u4eba \u80fd\u633a\u8eab\u51fa\u6765\u4e3a\u7ec8\u5357\u6d3e\u8bf4\u51e0\u53e5\u516c\u9053\u8bdd\u3002 \u3000\u3000\u5c31\u8fde\u516b\u5366\u795e\u638c\u8303\u4ef2\u5e73\u90a3\u79cd\u6027\u60c5\u8c6a\u731b\uff0c\u800c\u53c8\u9887\u5177\u58f0\u5a01\u7684\u6b66\u6797\u524d\u8f88\uff0c\u5728\u5fd6\u91cf\u60c5\u52bf\u4e0b\uff0c\u4e5f\u53ea\u6709 \u4e00\u8d70\u4e86\u4e4b\u3002\u5176\u4ed6\u7684\u4eba\uff0c\u66f4\u662f\u4e0d\u613f\u6765\u6dcc\u8fd9\u8d9f\u6d51\u6c34\u4e86\u3002 \u3000\u3000\u94b1\u7fca\u865a\u7a7a\u4e00\u6307\uff0c\u5de8\u949f\u4e00\u54cd\uff0c\u4f0a\u98ce\u5df2\u51b3\u5b9a\u633a\u8eab\u800c\u51fa\uff0c\u51b3\u5b9a\u4e0d\u8ba9\u5999\u96e8\u5728\u5982\u8bb8\u591a\u6b66\u6797\u8c6a\u58eb\u7684\u9762 \u524d\uff0c\u63a5\u638c\u7ec8\u5357\u95e8\u6237\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e5f\u77e5\u9053\u4ee5\u81ea\u5df1\u4e00\u8eab\u4e4b\u529b\uff0c\u6765\u548c\u4eba\u5bb6\u8fd9\u79cd\u5468\u5bc6\u8ba1\u5212\u4e0b\u7684\u529b\u91cf\u76f8\u6297\uff0c\u663e\u7136\u592a\u8fc7\u5fae\u5f31\u3002 \u3000\u3000\u56e0\u6b64\u4ed6\u60f3\u5728\u8fd9\u4e00\u6781\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u627e\u51fa\u4e00\u4e2a\u8f83\u4e3a\u59a5\u5f53\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u4f46\u8f6c\u77ac\u95f4\u949f\u6572\u4e03\u54cd\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u65f6\u95f4\u5df2\u4e0d\u5141\u8bb8\u4ed6\u518d\u591a\u52a0\u601d\u7d22\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u4e0e\u751f\u4ff1\u6765\u7684\u4fa0\u4e49\u5929\u6027\uff0c\u8fdc\u8fdc\u80dc\u8fc7 \u4e86\u4ed6\u7684\u7406\u667a\u3002 \u3000\u3000\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u5373\u4f7f\u6211\u81ea\u8eab\u5316\u9aa8\u626c\u7070\uff0c\u4e5f\u4e07\u4e07\u5bb9\u4e0d\u5f97\u8fd9\u53ae\u5f97\u624b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u54ac\u94a2\u7259\uff0c\u65ad\u7136\u4e0b\u4e86\u51b3\u5b9a\uff0c\u731b\u5730\u4e00\u957f\u8eab\uff0c\u98de\u8eab\u800c\u51fa\u3002 \u3000\u3000\u987b\u77e5\u5728\u8fd9\u79cd\u60c5\u52bf\u4e0b\uff0c\u4f0a\u98ce\u81ea\u5bb6\u4e5f\u77e5\u9053\u81ea\u5df1\u7684\u8fd9\u4e00\u51fa\u624b\uff0c\u5b9a\u662f\u51f6\u591a\u5409\u5c11\uff0c\u800c\u4e14\u4e8e\u4e8b\u4e5f\u4e0d\u89c1 \u5f97\u6709\u8865\u3002 \u3000\u3000\u4f46\u8def\u89c1\u4e0d\u5e73\uff0c\u5c1a\u8981\u62d4\u5200\u76f8\u52a9\uff0c\u4e3a\u6b63\u4e49\u4e24\u808b\u6325\u5200\uff0c\u4ea6\u5728\u6240\u4e0d\u60dc\u3002\u4f0a\u98ce\u7684\u8fd9\u79cd\u4fa0\u4e49\u4e4b\u5fc3\uff0c\u6bcf \u5728\u4e00\u4e2a\u5229\u5bb3\u5206\u660e\u7684\u7d27\u8981\u5173\u5934\uff0c\u4fbf\u663e\u9732\u51fa\u6765\u3002 \u3000\u3000\u81f3\u4e8e\u4e00\u4e9b\u5c0f\u8282\uff0c\u4ed6\u5e76\u4e0d\u53bb\u62d8\u62d8\u8ba1\u8f83\uff0c\u8fd9\u4e5f\u6b63\u662f\u4ed6\u8840\u6027\u7537\u513f\u7684\u672c\u8272\uff01 \u3000\u3000\u90a3\u77e5\u4e8b\u60c5\u5927\u51fa\u4ed6\u7adf\u6599\u4e4b\u5916\uff0c\u4ed6\u73b0\u8eab\u4e4b\u540e\uff0c\u94b1\u7fca\u7adf\u9996\u5148\u9038\u53bb\uff1b\u63a5\u7740\uff0c\u5999\u96e8\u9053\u4eba\u548c\u90a3\u5341\u51e0\u4e2a \u62d4\u5251\u800c\u7acb\u7684\u5251\u624b\uff0c\u4e5f\u83ab\u540d\u5176\u5999\u5730\u8d70\u4e86\u3002\u5374\u7ed9\u4f0a\u98ce\u548c\u6ee1\u5802\u6b66\u6797\u8c6a\u5ba2\uff0c\u7559\u4e0b\u4e86\u65e0\u6bd4\u7684\u6000\u7591\u548c\u60ca \u8be7\u3002 \u3000\u3000\u6b63\u6bbf\u91cc\u6709\u7247\u523b\u7684\u9759\u9ed8\uff0c\u63a5\u7740\u800c\u6765\u7684\u5c31\u662f\u4e00\u7247\u54c4\u7136\u7684\u8bae\u8bba\u58f0\u3002 \u3000\u3000\u7a81\u7136\u4eba\u4e1b\u91cc\u53c8\u98de\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u500f\u7136\u843d\u5728\u4f0a\u98ce\u8eab\u4fa7\u3002 \u3000\u3000\u8fd9\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u7136\u5c31\u662f\u4e5f\u6ee1\u6000\u60ca\u8be7\u7684\u8427\u5357\u82f9\u4e86\u3002 \u3000\u3000\u7ec8\u5357\u5f1f\u5b50\u4eec\uff0c\u6b64\u523b\u4e5f\u4ece\u60ca\u6115\u4e2d\u6062\u590d\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u5bf9\u4f0a\u98ce\uff0c\u81ea\u7136\u662f\u4e07\u5206\u611f\u6fc0\uff0c\u7136\u800c\u5728\u611f\u6fc0\u4e2d\uff0c\u5374\u53e6\u6709\u4e00\u79cd\u65e2\u60ca\u4e14\u60e7\u7684\u611f\u89c9\uff1a \u3000\u3000\u4e0d\u77e5\u9053\u8fd9\u5728\u6c5f\u6e56\u4e0a\u4e1d\u6beb\u6ca1\u6709\u540d\u58f0\u7684\u5e74\u8f7b\u4eba\uff0c\u600e\u6709\u8fd9\u79cd\u5a01\u529b\uff1f\u7a0d\u4e00\u73b0\u8eab\uff0c\u4fbf\u60ca\u9000\u4e86\u90a3\u4e48\u591a \u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u81ea\u7136\u4e0d\u80fd\u5c06\u5fc3\u4e2d\u7684\u611f\u89c9\uff0c\u5f53\u9762\u5411\u4f0a\u98ce\u95ee\u51fa\u6765\u3002 \u3000\u3000\u7384\u5316\u9053\u4eba\u524d\u884c\u4e24\u6b65\uff0c\u5f53\u5934\u5411\u4f0a\u98ce\u6df1\u6df1\u4e00\u63d6\uff0c\u606d\u58f0\u9053\uff1a \u3000\u3000\u201c\u58ee\u58eb\u4ed7\u4e49\u63f4\u624b\uff0c\u6b64\u6069\u6b64\u5fb7\uff0c\u6211\u7ec8\u5357\u5f1f\u5b50\u4e0d\u6562\u8a00\u62a5\u3002\u4f46\u613f\u9601\u4e0b\u80fd\u7a0d\u4f5c\u6b47\u606f\uff0c\u7b49\u655d\u6d3e\u5f1f\u5b50 \u4e00\u9f50\u5411\u9601\u4e0b\u53e9\u8c22\u3002\u201d \u3000\u3000\u4f0a\u98ce\u8d76\u7d27\u56de\u793c\uff0c\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\uff01\u5207\u83ab\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u8fd9\u53ea\u662f\u5c0f\u9119\u4efd\u5185\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u505c\u987f\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u8eab\u53d7\u8d35\u6d3e\u6258\u5e87\u4e4b\u6069\uff0c\u6b64\u523b\u80fd\u4e3a\u8d35\u6d3e\u7a0d\u6548\u5fae\u52b3\uff0c\u6b63\u662f\u5c0f\u9119\u4e4b\u5e78\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u4e07\u5206\u7d0a\u4e71\uff0c\u60f3\u5728\u5343\u4e1d\u4e07\u7ebf\u4e2d\u627e\u51fa\u4e00\u4e2a\u5934\u7eea\u6765\uff0c\u4f46\u5374\u4e0d\u5f97\u4e0d\u5148\u632f\u8d77\u7cbe\u795e\u6765\u56de\u7b54\u4eba \u5bb6\u7684\u8bdd\u3002 \u3000\u3000\u7384\u5316\u9053\u4eba\u5374\u6115\u4e86\u4e00\u4e0b\uff0c\u4ed6\u4e0d\u77e5\u9053\u4f0a\u98ce\u6240\u8bf4\u7684\u201c\u6258\u5e87\u4e4b\u6069\u201d\u662f\u6307\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u6b64\u523b\u5999\u6cd5\u9053\u4eba\u5df2\u6323\u624e\u7740\uff0c\u88ab\u5999\u901a\u548c\u5999\u5143\u4e24\u4eba\u6400\u6276\u4e86\u8d77\u6765\uff0c\u4ed6\u867d\u5f53\u80f8\u88ab\u94b1\u7fca\u6325\u4e86\u4e00\u638c\uff0c\u4f46 \u4f24\u52bf\u5374\u4e0d\u751a\u91cd\uff0c\u6b64\u65f6\u8d70\u8fc7\u6765\uff0c\u5598\u7740\u6c14\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u53ef\u5c31\u662f\u65b9\u624d\u8be2\u53ca\u5251\u8001\u524d\u8f88\u7684\u90a3\u4f4d\uff1f\u65b9\u624d\u6211\u542c\u7384\u4e39\u5e08\u4f84\u4e00\u8bf4\uff0c\u5c31\u77e5\u9053\u6765\u4e86\u6551\u661f\u3002 \u5509\uff01\u679c\u7136\u82cd\u5929\u6709\u773c\uff0c\u4e0d\u6559\u9b51\u9b45\u6a2a\u884c\u3002\u9601\u4e0b\u4e0d\u4f46\u662f\u655d\u6d3e\u4e0a\u4e0b\u6570\u767e\u5f1f\u5b50\u7684\u6069\u4eba\uff0c\u4e5f\u662f\u6b66\u6797\u7684\u6551 \u661f\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u7adf\u6323\u624e\u7740\u8981\u62dc\u4f0f\u4e0b\u53bb\uff0c\u53e3\u4e2d\u8fde\u8fde\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8bf7\u5148\u53d7\u6211\u4e00\u62dc\uff01\u201d \u3000\u3000\u4f0a\u98ce\u53ef\u4e0d\u6562\u62c5\u53d7\u4eba\u5bb6\u6b64\u793c\uff0c\u8fde\u5fd9\u963b\u62e6\u7740\uff0c\u53e3\u4e2d\u6025\u5207\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u5207\u5207\u4e0d\u53ef\u5982\u6b64\uff01\u522b\u8bf4\u8d35\u6d3e\u5bf9\u5c0f\u9119\u6709\u7740\u5927\u6069\uff0c\u5c31\u662f\u83ab\u4e0d\u76f8\u5e72\u7684\u4eba\uff0c\u65e2\u7136\u773c\u89c1\u6b64\u4e8b\uff0c \u4e5f\u4e07\u4e07\u4e0d\u80fd\u5750\u89c6\u7684\uff0c\u8fd9\u6b63\u662f\u5c0f\u9119\u4efd\u5185\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u6885\u82b1\u5251\u675c\u957f\u537f\u4e5f\u5728\u65c1\u8fb9\uff0c\u6b64\u523b\u8138\u4e0a\u4e0d\u7981\u7ea2\u4e86\u4e00\u4e0b\uff0c\u3001\u5fc3\u91cc\u60ed\u6127\u5f97\u5f88\u3002\u5999\u6cd5\uff0c\u5999\u5143\u7b49\u9053 \u4eba\uff0c\u5374\u4e0d\u7981\u53c8\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u4eec\u90fd\u4e0d\u77e5\u9053\u4f0a\u98ce\u5728\u8eab\u53d7\u91cd\u4f24\uff0c\u5944\u5944\u5782\u606f\u65f6\uff0c\u5c31\u662f\u5728\u8fd9\u7384\u5999\u89c2\u4e2d\u83b7\u6cbb\uff0c\u800c\u4e14\u8fd8\u56e0\u6b64 \u5f97\u4e86\u8bb8\u591a\u4e0d\u4e16\u5947\u7f18\u3002 \u3000\u3000\u5f53\u7136\u4e5f\u5c31\u4e0d\u77e5\u9053\u4f0a\u98ce\u6240\u8bf4\u7684\uff1a\u201c\u6211\u66fe\u53d7\u8fc7\u8d35\u6559\u5927\u6069\u201d\u8fd9\u53e5\u8bdd\uff0c\u5176\u4e2d\u6240\u542b\u7684\u610f\u601d\u3002 \u3000\u3000\u4f55\u51b5\u5c31\u7b97\u4ed6\u4eec\u77e5\u9053\u4e86\u6b64\u4e8b\uff0c\u53ef\u4e5f\u4e0d\u80fd\u8ba4\u4e3a\u4eba\u5bb6\u771f\u662f\u53d7\u8fc7\u81ea\u5df1\u7684\u5927\u6069\uff0c\u56e0\u4e3a\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9 \u79cd\u4e8b\u603b\u4e0d\u80fd\u7b97\u505a\u65bd\u6069\u4e8e\u4eba\u5440\uff01 \u3000\u3000\u4f46\u4f0a\u98ce\u7684\u5fc3\u91cc\u5374\u4e0d\u540c\uff0c\u4ed6\u5728\u7ec8\u5357\u5c71\u4e0a\u6240\u9047\uff0c\u6b63\u662f\u4ed6\u751f\u547d\u7684\u4e00\u4e2a\u8f6c\u6369\u70b9\u3002\u4ed6\u5bf9\u672a\u6765\u8bb8\u591a\u6781 \u4e3a\u6e3a\u832b\u7684\u5e0c\u671b\uff0c\u4e5f\u56e0\u6b64\u800c\u6709\u4e86\u7740\u843d\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u53e3\u53e3\u58f0\u58f0\u8bf4\u81ea\u5df1\u66fe\u53d7\u7ec8\u5357\u6d3e\u7684\u5927\u6069\uff0c\u5374\u4e0d\u77e5\u5374\u5c06\u4eba\u5bb6\u5f04\u5f97\u6709\u4e9b\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u4f0a\u98ce\u770b\u5230\u4ed6\u4eec\u8138\u4e0a\u7684\u832b\u7136\u795e\u8272\uff0c\u4e5f\u77e5\u9053\u4ed6\u4eec\u9519\u6115\u7684\u539f\u56e0\uff0c\u5374\u4e5f\u6682\u4e14\u4e0d\u53bb\u8bf4\u7834\uff0c\u53ea\u662f\u5fae\u5fae \u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u4eec\u4e14\u83ab\u7406\u4f1a\u5c0f\u9119\uff0c\u5c0f\u9119\u81ea\u4f1a\u6b47\u606f\uff0c\u8fd8\u662f\u5148\u53bb\u6599\u7406\u8d35\u6d3e\u4e2d\u7684\u4e8b\u60c5\u4e3a\u8981\uff0c\u514d\u5f97\u6559\u5982\u8bb8 \u591a\u6b66\u6797\u8c6a\u6770\uff0c\u5728\u6b64\u4e45\u7b49\u3002\u201d \u3000\u3000\u5999\u6cd5\u9053\u4eba\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u771f\u662f\u7cca\u6d82\uff0c\u7adf\u5fd8\u4e86\u8fd8\u6709\u8bb8\u591a\u8d35\u5ba2\u5728\u6b64\uff01\u201d \u3000\u3000\u4ed6\u7a0d\u4e3a\u4e00\u987f\uff0c\u53c8\u8d76\u7d27\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u9601\u4e0b\u5343\u4e07\u5148\u8bf7\u6b47\u5750\u4e00\u4e0b\uff0c\u7b49\u655d\u6d3e\u5148\u6599\u7406\u4e00\u4e0b\uff0c\u518d\u62dc\u8c22\u5927\u6069\u3002\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4eca\u65e5\u4e5f\u5f97\u5148\u5c06\u638c\u95e8\u4eba\u63a8\u9009\u51fa\u6765\uff0c\u514d\u5f97\u65e5\u5b50\u4e00\u957f\uff0c\u53c8\u751f\u53d8\u5316\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u606f\u7740\u3002 \u3000\u3000\u5176\u5b9e\u8fd1\u5e74\u6765\u6b66\u6797\u4eba\u6750\uff0c\u6e10\u6e10\u51cb\u843d\uff0c\u7ec8\u5357\u4e00\u6d3e\u66f4\u662f\u5982\u6b64\uff0c\u8fd9\u8001\u9053\u4eba\u5fc3\u4e2d\u611f\u6168\u957f\u591a\uff0c\u600e\u4e0d\u8fde \u8fde\u53f9\u606f\u3002 \u3000\u3000\u8fd9\u4e2a\u5999\u6cd5\u9053\u4eba\u6614\u5e74\u672c\u662f\u7ec8\u5357\u7684\u4e2d\u5174\u638c\u6559\uff0c\u53f7\u79f0\u6b66\u6797\u4e03\u5927\u5251\u5e08\u4e4b\u4e00\u7684\u7389\u673a\u9053\u4eba\u7684\u9996\u5f92\uff0c\u53ea \u56e0\u6027\u60c5\u606c\u6de1\uff0c\u53c8\u597d\u7384\u7406\uff0c\u6b63\u662f\u4e2a\u6e05\u51c0\u65e0\u4e3a\u7684\u7384\u95e8\u7fbd\u58eb\uff0c\u5bf9\u6b66\u529f\u4e00\u9053\uff0c\u5e76\u65e0\u6df1\u6e5b\u7684\u9020\u8be3\uff0c\u5bf9\u6b66 \u6797\u4e2d\u4e8b\uff0c\u66f4\u4e0d\u611f\u5174\u8da3\u3002 \u3000\u3000\u662f\u4ee5\u7389\u673a\u9053\u4eba\u6b7b\u540e\uff0c\u624d\u8ba9\u4ed6\u7684\u4e8c\u5e08\u5f1f\u5999\u7075\u9053\u4eba\u63a5\u638c\u4e86\u95e8\u6237\uff0c\u81ea\u5df1\u5374\u5c06\u751f\u547d\u6d88\u78e8\u5728\u9752\u706f\u9ec4 \u5377\u4e4b\u7554\u3002 \u3000\u3000\u90a3\u77e5\u5999\u7075\u9053\u4eba\u5374\u9053\u5fc3\u4e0d\u575a\uff0c\u4e3a\u8272\u6240\u8bf1\uff0c\u7ec8\u4e8e\u8eab\u4e27\u540d\u88c2\uff0c\u4ed6\u81ea\u7136\u75db\u5fc3\u3002 \u3000\u3000\u518d\u773c\u770b\u7ec8\u5357\u5f1f\u5b50\u4eba\u6750\u51cb\u843d\uff0c\u800c\u522b\u6d3e\u95e8\u4e0b\uff0c\u5374\u6709\u4e9b\u5947\u624d\u4fca\u5f66\u51fa\u73b0\u3002\u8fd9\u4e00\u73b0\u8eab\u4fbf\u60ca\u9000\u7fa4\u5c0f\u7684 \u4f0a\u98ce\u4e0d\u8c08\uff0c\u5c31\u8fde\u90a3\u6765\u81ea\u9752\u6d77\u5e03\u514b\u9a6c\u56e0\u5c71\u53e3\u7684\u7684\u72c2\u50b2\u5c11\u5e74\u94b1\u7fca\uff0c\u4f55\u5c1d\u4e0d\u662f\u8eab\u6000\u7edd\u5b66\u3002 \u3000\u3000\u81ea\u5df1\u867d\u4e0d\u597d\u6b66\uff0c\u4f46\u5012\u5e95\u662f\u6570\u5341\u5e74\u4fee\u4e3a\uff0c\u5374\u88ab\u4eba\u5bb6\u4e00\u62db\u4e4b\u4e0b\uff0c\u5c31\u6325\u8dcc\u51fa\u53bb\uff0c\u867d\u4e5f\u662f\u56e0\u7740\u81ea \u5df1\u5927\u610f\uff0c\u4f46\u4e0d\u53ef\u8bb3\u8a00\u7684\uff0c\u4eba\u5bb6\u90a3\u4efd\u8eab\u624b\uff0c\u672c\u6765\u4e5f\u9ad8\u4e8e\u81ea\u5df1\uff0c\u66f4\u9051\u8bba\u6559\u4e2d\u7684\u540e\u8f88\u5f1f\u5b50\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u5fc3\u4e2d\u7684\u60b2\u6006\u611f\u6000\uff0c\u7136\u800c\u5f53\u7740\u6b66\u6797\u7fa4\u8c6a\uff0c\u4ed6\u5374\u4e0d\u80fd\u9732\u5728\u8138\u4e0a\u3002\u5728\u5999\u5143\uff0c\u5999\u901a\u4e24\u4e2a\u9053 \u4eba\u7684\u6400\u6276\u4e0b\uff0c\u53c8\u5f80\u524d\u8d70\u4e86\u4e00\u6b65\uff0c\u52c9\u5f3a\u63d0\u9ad8\u58f0\u8c03\u9053\uff1a \u3000\u3000\u201c\u655d\u6d3e\u4e0d\u5e78\uff0c\u51fa\u4e86\u90a3\u79cd\u52a3\u5f92\uff0c\u800c\u8d2b\u9053\u53c8\u65e0\u80fd\uff0c\u4e0d\u80fd\u4e3a\u5148\u5e08\u6e05\u7406\u95e8\u6237\uff0c\u4e3a\u6b66\u6797\u9664\u6b64\u8d25\u7c7b\uff0c \u53c8\u52b3\u5404\u4f4d\u5728\u6b64\u7a7a\u5019\uff0c\u8d2b\u9053\u5b9e\u5728\u8be5\u6b7b\uff01\u201d \u3000\u3000\u5927\u6bbf\u7fa4\u8c6a\u987f\u65f6\u54c4\u7136\u8c26\u8c22\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u5999\u6cd5\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fd1\u5e74\u6b66\u6797\u5f02\u9053\u6a2a\u884c\uff0c\u8fd9\u60f3\u5fc5\u4e5f\u662f\u4ee4\u5404\u4f4d\u60b2\u5fc3\u4e4b\u4e8b\uff0c\u655d\u6d3e\u6b64\u6b21\u4e4b\u4e00\u53cd\u5f80\u4f8b\uff0c\u516c\u9009\u638c\u95e8\uff0c \u4e5f\u662f\u5e0c\u671b\u655d\u6d3e\u80fd\u4ece\u6b64\u6574\u987f\uff0c\u4e3a\u6b66\u6797\u62c5\u5f53\u4e00\u4efd\u8d23\u4efb\u3002\u90a3\u77e5\u2014\u2014\u5509\uff01\u82e5\u4e0d\u662f\u5e78\u5f97\u9ad8\u4eba\u89e3\u5371\uff0c\u8fd8\u4e0d \u77e5\u843d\u5f97\u4ec0\u4e48\u4e0b\u573a\u3002\u201d \u3000\u3000\u58f0\u8c03\u66f4\u4e3a\u6006\u75db\uff0c\u505c\u987f\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u4f46\u613f\u6b64\u6b21\u5f53\u7740\u5404\u4f4d\uff0c\u655d\u6d3e\u80fd\u9009\u51fa\u4e00\u4f4d\u4e0d\u8d1f\u5404\u4f4d\u7231\u62a4\u655d\u6d3e\u4e4b\u610f\u7684\u638c\u95e8\u6765\uff0c\u4e5f\u4e0d\u8d1f\u5404 \u4f4d\u8fdc\u6765\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u5f53\u7136\uff0c\u7b11\u5bb9\u5e76\u4e0d\u662f\u5077\u5feb\u7684\uff0c\u63a5\u7740\u53c8\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u603b\u4e4b\uff0c\u8bf7\u5404\u4f4d\u518d\u7a0d\u5f85\u7247\u523b\uff0c\u655d\u6d3e\u656c\u5907\u4e86\u4e9b\u8bb8\u7d20\u9152\uff0c\u4e3a\u5404\u4f4d\u6d17\u5c18\uff1b\u4e5f\u662f\u2014\u2014\u4e5f\u662f\u4e3a\u5404\u4f4d \u996f\u884c\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u8fd9\u987b\u53d1\u51e0\u4e4e\u5168\u767d\u7684\u9053\u4eba\uff0c\u4e0d\u4f4f\u5730\u5598\u7740\u6c14\uff0c\u4e0d\u77e5\u662f\u56e0\u7740\u8eab\u4e0a\u6240\u53d7\u7684\u4f24\uff0c\u6291\u6216\u662f\u56e0 \u7740\u5fc3\u4e2d\u7684\u611f\u6168\uff0c\u6006\u75db\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5f77\u4f5b\u53c8\u82cd\u8001\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u7fa4\u8c6a\u542c\u4e86\u4ed6\u8fd9\u4e00\u756a\u8bdd\uff0c\u4e5f\u90fd\u4ff1\u4e3a\u9ed8\u7136\uff0c\u4e5f\u8bb8\u662f\u5fc3\u91cc\u4e5f\u6709\u4e9b\u60ed\u6127\u5427\uff1f \u3000\u3000\u7ec8\u5357\u5f1f\u5b50\u4eec\uff0c\u66f4\u662f\u4ff1\u90fd\u5782\u9996\u9ed8\u7acb\uff0c\u6b32\u8bed\u65e0\u8a00\u3002\u8fde\u6b64\u65f6\u5fc3\u60c5\u672c\u6765\u5df2\u88ab\u7231\u60c5\u6c89\u6d78\u5f97\u6781\u4e3a\u5e78 \u798f\uff0c\u5077\u5feb\u7684\u8427\u5357\u82f9\uff0c\u89c1\u4e86\u6b64\u60c5\u6b64\u666f\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u4e00\u53f9\u3002 \u3000\u3000\u5999\u6cd5\u9053\u4eba\u5598\u606f\u4e86\u534a\u664c\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6b64\u523b\u5c31\u8bf7\u5999\u5143\u5e08\u5f1f\uff0c\u548c\u7384\u5316\u5e08\u4f84\u4e24\u4eba\uff0c\u518d\u4e00\u4e89\u638c\u95e8\u4e4b\u4f4d\u3002\u201d \u3000\u3000\u4ed6\u5fae\u559f\u4e00\u4e0b\uff1a \u3000\u3000\u201c\u4e0d\u8bba\u4f60\u4eec\u8c01\u80dc\uff0c\u8c01\u8d1f\uff0c\u4f60\u4eec\u603b\u662f\u7ec8\u5357\u5f1f\u5b50\u4e2d\u7684\u4f7c\u4f7c\u8005\uff0c\u65e0\u8bba\u662f\u8c01\u63a5\u638c\u4e86\u7ec8\u5357\u95e8\u6237\uff0c\u6211 \u2014\u2014\u6211\u4e5f\u9ad8\u5174\u3002\u201d \u3000\u3000\u5999\u5143\u9053\u4eba\u59cb\u7ec8\u5782\u9996\u65e0\u8a00\uff0c\u8138\u4e0a\u7684\u795e\u8272\u4e5f\u662f\u96be\u770b\u5df2\u6781\uff01 \u3000\u3000\u6b64\u523b\u7a81\u5730\u653e\u4e0b\u6400\u6276\u7740\u5999\u6cd5\u9053\u4eba\u7684\u624b\uff0c\u62a2\u5148\u51e0\u6b65\uff0c\u5728\u6b63\u6bbf\u4e2d\u7684\u5415\u7956\u795e\u50cf\u524d\uff0c\u7aef\u7aef\u6b63\u6b63\u53e9\u4e86 \u51e0\u4e2a\u5934\uff0c\u7136\u540e\u8f6c\u8fc7\u8eab\u6765\uff0c\u60b2\u6006\u5730\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5999\u5143\u65e0\u80fd\uff0c\u4e0d\u80fd\u4e3a\u672c\u6d3e\u5fa1\u654c\uff0c\u66f4\u4e0d\u6562\u51fa\u4efb\u638c\u95e8\u3002\u7384\u5316\u5e08\u4f84\uff0c\u58ee\u5e74\u82f1\u53d1\uff0c\u65e0\u8bba\u662f\u6b66\u529f\u3001 \u4eba\u54c1\uff0c\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u6b63\u662f\u62c5\u5f53\u638c\u95e8\u7684\u6700\u7406\u60f3\u4e4b\u4eba\uff0c\u4f46\u671b\u4ed6\u80fd\u62c5\u5f53\u8d77\u8fd9\u4ed8\u62c5\u5b50\u6765\u2026\u2026\u201d \u3000\u3000\u4ed6\u53f9\u606f\u4e00\u58f0\uff0c\u5782\u9996\u53c8\u9053\uff1a \u3000\u3000\u201c\u81f3\u4e8e\u5999\u5143\u2014\u2014\u5df2\u5411\u5415\u7956\u53ca\u5148\u7956\u8a93\u8a00\uff1a\u6b64\u540e\u95ed\u5173\u5341\u5e74\uff0c\u91cd\u7814\u7ec8\u5357\u7edd\u827a\u3002\u6765\u65e5\u82e5\u80fd\u5e78\u800c\u6709 \u6210\uff0c\u5999\u5143\u624d\u7b97\u4e0d\u8f9c\u8d1f\u5148\u5e08\u7684\u683d\u57f9\uff1b\u4e0d\u7136\u7684\u8bdd\uff0c\u5999\u5143\u4ece\u6b64\u57cb\u9996\u6df1\u5c71\uff0c\u518d\u4e5f\u65e0\u989c\u8fc7\u95ee\u4e16\u4e8b\u4e86\u3002\u201d \u3000\u3000\u65b9\u624d\u4ed6\u4e00\u62db\u4e4b\u4e0b\uff0c\u4fbf\u8d25\u5728\u672c\u662f\u4ed6\u540c\u95e8\uff0c\u540c\u8f88\uff0c\u540c\u5e08\u6388\u827a\u7684\u5e08\u5144\u5999\u96e8\u624b\u4e0a\uff0c\u5fc3\u91cc\u81ea\u7136\u60b2 \u75db\uff0c\u60ed\u6127\u3002\u6b64\u523b\u4e00\u6c14\u8bf4\u5b8c\uff0c\u624d\u7565\u4e3a\u89c9\u5f97\u8212\u7545\u4e86\u4e9b\u3002 \u3000\u3000\u5999\u6cd5\u9053\u4eba\u5fae\u9732\u7b11\u5bb9\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e94\u5e08\u5f1f\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5b9e\u5728\u9ad8\u5174\u5f97\u5f88\uff01\u201d \u3000\u3000\u4ed6\u7565\u4e00\u505c\u987f\uff0c\u7384\u5316\u4e5f\u62a2\u5148\u51e0\u6b65\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u65e0\u80fd\uff0c\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u5999\u6cd5\u4e00\u6446\u624b\uff0c\u963b\u4f4f\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u518d\u4e5f\u4e0d\u8981\u63a8\u8ba9\uff0c\u503c\u6b64\u65f6\u671f\uff0c\u62c5\u8d1f\u8d77\u6b64\u91cd\u4efb\uff0c\u6b63\u662f\u4f60\u4e4b\u5e78\u8fd0\uff0c\u5374\u4e5f\u6b63\u662f\u4f60\u7684\u4e0d\u5e78\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u4e2d\u7684\u6c89\u75db\uff0c\u4f7f\u5f97\u7384\u5316\u5657\u5730\uff0c\u8dea\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5999\u6cd5\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u4ef0\u9996\u671b\u5929\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u4f46\u613f\u4f60\u5162\u5162\u4e1a\u4e1a\uff0c\u597d\u597d\u505a\u53bb\uff0c\u4e0d\u8981\u8fdd\u80cc\u4e86\u7956\u5e08\u7237\u7684\u6559\u8bad\uff0c\u4e5f\u4e0d\u8981\u50cf\u4f60\u6b7b\u53bb\u7684\u5e08 \u7236\u2026\u2026\u201d \u3000\u3000\u5f53\u7740\u6b66\u6797\u7fa4\u8c6a\uff0c\u4ed6\u600e\u80fd\u8bf4\u51fa\u7384\u5316\u7684\u5e08\u7236\uff0c\u4ed6\u81ea\u5df1\u7684\u5e08\u5f1f\uff0c\u7ec8\u5357\u7684\u638c\u95e8\uff0c\u56e0\u8272\u60d1\u5fd7\u7684\u8bdd \u6765\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u987f\u4f4f\u8bdd\u5934\uff0c\u5fae\u559f\u4e00\u58f0\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u4ed6\u2014\u2014\u4ed6\u6b7b\u5f97\u592a\u65e9\u4e86\u3002\u201d \u3000\u3000\u6b66\u6797\u7fa4\u8c6a\u600e\u80fd\u4e86\u89e3\u4ed6\u8bdd\u4e2d\u8fd9\u5c0f\u5c0f\u7684\u6f0f\u6d1e\u4e2d\uff0c\u6240\u5305\u542b\u7684\u4e00\u4e2a\u5de8\u5927\u7684\u6545\u4e8b\uff01 \u3000\u3000\u4f0a\u98ce\u542c\u4e86\u867d\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u4ed6\u6b64\u523b\u5fc3\u4e2d\u5168\u90fd\u88ab\u81ea\u8eab\u6240\u9047\u5230\u7684\u5947\u4e8b\uff0c\u5360\u5f97\u6ee1\u6ee1\u7684\uff0c\u90a3\u6709\u4f59 \u9699\u6765\u601d\u8003\u522b\u7684\u4e8b\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5728\u65e0\u6570\u58f0\u53f9\u606f\u58f0\u4e2d\uff0c\u7ec8\u5357\u5251\u6d3e\u65b0\u7684\u4e00\u4ee3\u638c\u95e8\uff0c\u4e8e\u5179\u9009\u51fa\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u4e8c\u7ae0\u3000\u5c42\u5c42\u63a8\u7a76>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u4e8c\u7ae0\u3000\u5c42\u5c42\u63a8\u7a76 \u3000\u3000\u4f0a\u98ce\u5c06\u81ea\u5df1\u5fc3\u4e2d\u4e07\u5343\u6761\u7d0a\u4e71\u7684\u601d\u8def\uff0c\u614e\u91cd\u800c\u7f13\u6162\u5730\u6574\u7406\u7740\uff0c\u5e0c\u671b\u80fd\u5bf9\u65b9\u624d\u6240\u53d1\u751f\u7684\u5947 \u4e8b\uff0c\u4f5c\u4e00\u4e2a\u5468\u5bc6\u800c\u5408\u7406\u7684\u89e3\u91ca\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u5728\u89c1\u5230\u6211\u4e4b\u540e\uff0c\u4e3a\u4ec0\u4e48\u7a81\u7136\u653e\u5f03\u4e86\u4ed6\u4eec\u7684\u8ba1\u5212\u800c\u9038\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u662f\u6b66\u6797\u4e2d\u6709\u540d\u5fc3\u72e0\u624b\u8fa3\u7684\u4eba\u7269\uff0c\u4ed6\u7684\u51f6\u540d\uff0c\u6211\u53ef\u542c\u5230\u5f97\u4e45\u4e86\uff0c\u5728\u60c5\u5728 \u7406\uff0c\u4ed6\u65ad\u7136\u4e0d\u4f1a\u56e0\u7740\u754f\u60e7\u6211\u800c\u9003\u8d70\u3002\u94c1\u621f\u6e29\u4faf\u5728\u6b66\u6797\u4e2d\u867d\u7136\u540d\u58f0\u4e5f\u9887\u4e3a\u54cd\u4eae\uff0c\u4f46\u5374\u4e5f\u4e07\u4e07\u5413 \u4e0d\u5012\u6a2a\u884c\u5ddd\u6ec7\u7684\u9b54\u5934\u591a\u624b\u771f\u4eba\u5440\uff1f\u201d \u3000\u3000\u201c\u4f55\u51b5\uff0c\u6b64\u523b\u6211\u5df2\u7ecf\u8fc7\u6613\u5bb9\uff0c\u5929\u4e0b\u518d\u4e5f\u65e0\u4eba\u8ba4\u5f97\u6211\u5c31\u662f\u94c1\u621f\u6e29\u4faf\u5415\u5357\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\uff0c\u5f88\u663e\u7136\u5730\uff0c\u4ed6\u4eec\u6240\u754f\u60e7\u7684\uff0c\u662f\u53e6\u4e00\u4eba\u3002\u800c\u6211\u6613\u5bb9\u540e\u7684\u9762\u8c8c\uff0c\u53c8\u51b6\u5de7\u548c\u8fd9\u4eba\u6781\u4e3a \u76f8\u50cf\uff0c\u662f\u4ee5\u4ed6\u4eec\u8bef\u8ba4\u4e86\u3002\u201d \u3000\u3000\u601d\u8def\u81f3\u6b64\uff0c\u4ed6\u60f3\u8d77\u65b9\u624d\u5728\u5c71\u811a\u4e0b\u6240\u9047\u7684\u98de\u8679\u4e03\u5251\uff0c\u60f3\u8d77\u98de\u8679\u5251\u5ba2\u4eec\u5728\u770b\u5230\u4ed6\u65f6\u7684\u8868\u6e05\uff0c \u4ee5\u53ca\u4ed6\u4eec\u5bf9\u81ea\u5df1\u6240\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5c06\u8fd9\u4e24\u4ef6\u4e8b\u53f0\u800c\u4e3a\u4e00\uff0c\u63a5\u7740\u5f80\u4e0b\u9762\u7ee7\u7eed\u63a8\u7a76\u7740\u2014\u2014 \u3000\u3000\u201c\u6211\u7edd\u5bf9\u4e0d\u53ef\u80fd\u548c\u4e24\u4e2a\u4eba\u7684\u9762\u8c8c\u90fd\u5b8c\u5168\u76f8\u540c\uff0c\u662f\u4ee5\uff0c\u8fd9\u591a\u624b\u771f\u4eba\u548c\u90a3\u4e9b\u957f\u767d\u6d3e\u7684\u5251\u624b\u90fd \u5c06\u6211\u8ba4\u6210\u53e6\u5916\u4e00\u4eba\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u5c31\u662f\u591a\u624b\u771f\u4eba\u5c06\u6211\u8bef\u8ba4\u4e3a\u4ee5\u524d\u5728\u957f\u767d\u5251\u6d3e\u4e2d\u90a3\u4e2a\u59d3\u953a\u7684\u5251 \u5ba2\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\uff0c\u4ed6\u4eec\u53c8\u4e3a\u4ec0\u4e48\u8981\u754f\u60e7\uff0c\u8fdc\u5728\u5173\u4e1c\u7684\u957f\u767d\u5251\u6d3e\u4e2d\u7684\u4e00\u4e2a\u5251\u5ba2\u5462\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u5df1\u5411\u81ea\u5df1\u63d0\u51fa\u4e86\u8fd9\u95ee\u9898\uff0c\u968f\u5373\u53c8\u66ff\u81ea\u5df1\u5bfb\u627e\u7740\u7b54\u6848\uff1a \u3000\u3000\u201c\u4e00\u5b9a\u662f\u8fd9\u59d3\u953a\u7684\u5251\u624b\uff0c\u5728\u79bb\u5f00\u4f25\u767d\u5c71\u540e\uff0c\u6295\u5165\u53e6\u4e00\u4eba\u7684\u95e8\u4e0b\u3002\u4e0d\u4f46\u5982\u6b64\uff0c\u4ed6\u4e00\u5b9a\u8fd8\u53e6 \u5916\u6362\u4e86\u4e2a\u540d\u5b57\uff0c\u800c\u8fd9\u4e2a\u540d\u5b57\uff0c\u5fc5\u5b9a\u662f\u5728\u8fd1\u5e74\u6c5f\u6e56\u4e2d\u975e\u5e38\u54cd\u4eae\u7684\uff0c\u4e5f\u662f\u8db3\u4ee5\u4f7f\u5f97\u8fde\u591a\u624b\u771f\u4eba\u8fd9 \u79cd\u4eba\u90fd\u5f02\u5e38\u754f\u60e7\u7684\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u53c8\u5f88\u5feb\u5730\u53c8\u8054\u60f3\u5230\u90a3\u72c2\u50b2\u7684\u94b1\u7fca\uff0c\u4ee5\u53ca\u94b1\u7fca\u5728\u89c1\u5230\u4ed6\u65f6\u7684\u90a3\u79cd\u5947\u602a\u7684\u6001\u5ea6\uff0c\u5f88 \u5feb\u5730\u518d\u60f3\u4e0b\u53bb\uff01 \u3000\u3000\u201c\u94b1\u7fca\u4e00\u5b9a\u8ba4\u5f97\u90a3\u4eba\uff0c\u4e5f\u5c31\u662f\u8bf4\u94b1\u7fca\u4e00\u5b9a\u8ba4\u5f97\u548c\u6211\u6613\u5bb9\u540e\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u7684\u90a3\u4eba\uff0c\u800c\u94b1\u7fca \u5374\u662f\u9752\u6d77\u65e0\u540d\u8001\u4eba\u7684\u5f1f\u5b50\uff0c\u4ed6\u4ee5\u524d\u5728\u6c5f\u6e56\u4e4b\u4e2d\uff0c\u6ca1\u6709\u4e1d\u6beb\u540d\u58f0\uff0c\u4ee5\u4ed6\u7684\u6b66\u529f\u6765\u8bf4\uff0c\u90a3\u81ea\u7136\u662f \u56e0\u4e3a\u4ed6\u4ee5\u524d\u6839\u672c\u6ca1\u6709\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\u8fc7\uff0c\u4ed6\u65e2\u672a\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\u8fc7\uff0c\u5374\u8ba4\u5f97\u90a3\u4eba\uff0c\u800c\u53c8\u5f77\u4f5b\u5f88 \u719f\u201d \u3000\u3000\u4ed6\u601d\u8def\u4e0d\u6562\u5206\u5c90\uff0c\u6781\u5feb\u5730\u60f3\u4e0b\u53bb\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u4eec\u4e00\u5b9a\u662f\u65e9\u5c31\u8ba4\u5f97\u7684\uff0c\u4f46\u636e\u90a3\u98de\u8679\u5251\u5ba2\u6240\u8bf4\uff0c\u90a3\u59d3\u953a\u7684\u5374\u662f\u81ea\u5e7c\u5373\u5728\u957f\u767d\u4e60 \u827a\uff0c\u90a3\u4e48\u552f\u4e00\u7684\u53ef\u80fd\u4fbf\u662f\u8fd9\u59d3\u953a\u7684\u5251\u624b\uff0c\u79bb\u5f00\u957f\u767d\u4e4b\u540e\uff0c\u5c31\u6295\u5165\u4e86\u9752\u6d77\u65e0\u540d\u8001\u4eba\u7684\u95e8\u4e0b\uff0c\u662f \u4ee5\u94b1\u7fce\u624d\u8ba4\u5f97\u4ed6\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u94b1\u7fca\u5982\u679c\u548c\u4ed6\u662f\u540c\u95e8\uff0c\u89c1\u4e86\u6211\u4e5f\u4f1a\u8bef\u8ba4\uff0c\u90a3\u4e48\u53ef\u89c1\u6211\u6613\u5bb9\u540e\u7684\u9762\u8c8c\uff0c\u662f\u7edd\u5bf9\u548c\u90a3\u59d3\u953a\u7684 \u5b8c\u5168\u76f8\u4f3c\u4e86\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u65e9\u8be5\u60f3\u5230\u8fd9\u70b9\uff0c\u56e0\u7740\u8fde\u90a3\u4e9b\u548c\u201c\u953a\u82f1\u5947\u201d\u81ea\u5e7c\u76f8\u5904\u7684\u201c\u98de\u8679\u4e03\u5251\u201d\u4e5f\u4f1a\u8bef\u8ba4\uff0c\u90a3 \u4e48\u4ed6\u4eec\u9762\u8c8c\u7684\u76f8\u540c\uff0c\u5c31\u53ef\u89c1\u4e00\u6591\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u65e0\u540d\u8001\u4eba\u867d\u7136\u540d\u5782\u6b66\u6797\uff0c\u4ed6\u7684\u5f1f\u5b50\u5374\u4e5f\u4e0d\u89c1\u5f97\u80fd\u4f7f\u591a\u624b\u771f\u4eba\u548c\u6b66\u6797\u4e2d\u7684\u90a3\u4e48\u591a\u540d \u5251\u5ba2\u7779\u9762\u4e4b\u4e0b\uff0c\u4fbf\u7acb\u523b\u9038\u53bb\u5440\uff01 \u3000\u3000\u4f55\u51b5\u5728\u591a\u624b\u771f\u4eba\u548c\u90a3\u4e9b\u5251\u624b\u7684\u8eab\u4e0a\uff0c\u4e00\u5b9a\u8fd8\u62c5\u5f53\u7740\u5929\u4e89\u6559\u7f1c\u5bc6\u8ba1\u5212\u4e0b\u6240\u6d3e\u9063\u7684\u4f7f\u547d\uff0c\u800c \u4ee5\u201c\u5929\u4e89\u6559\u201d\u6b64\u523b\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u8bf4\u6765\uff0c\u4e5f\u65ad\u7136\u4e0d\u4f1a\u56e0\u7740\u4efb\u4f55\u4e00\u4e2a\u4eba\u7684\u51fa\u73b0\u800c\u6539\u53d8\u81ea\u5df1\u7684\u8ba1 \u5212\uff0c\u5373\u4f7f\u51fa\u73b0\u7684\u8fd9\u4eba\u662f\u540d\u5782\u6b66\u6797\u7684\u524d\u8f88\u5f02\u4eba\u65e0\u540d\u8001\u4eba\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u4ecd\u5728\u4f0a\u98ce\u8111\u6d77\u4e2d\u76d8\u65cb\u7740\uff0c\u4ed6\u6709\u65f6\u50cf\u662f\u6293\u7740\u4e86\u4e00\u4e9b\u7aef\u502a\uff0c\u4f46\u77ac\u5373\u53c8\u832b\u65e0\u5934\u7eea\uff0c\u5782 \u7740\u5934\uff0c\u4ed6\u5168\u7136\u9677\u5165\u6df1\u601d\u91cc\u3002 \u3000\u3000\u8427\u5357\u82f9\u7ad9\u5728\u4ed6\u7684\u8eab\u4fa7\uff0c\u672c\u6765\u88ab\u7ec8\u5357\u9053\u4eba\u7684\u90a3\u79cd\u60b2\u6006\u6c14\u6c1b\u6240\u611f\uff0c\u5fc3\u91cc\u4e5f\u9887\u6709\u4e00\u4e9b\u6c89\u91cd\u7684\u610f \u5473\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u90a3\u5e74\u8f7b\u7684\u7384\u5316\u9053\u4eba\uff0c\u5df2\u6b63\u5f0f\u63a5\u957f\u4e86\u7ec8\u5357\u95e8\u6237\uff0c\u5f53\u7740\u6b66\u6797\u7fa4\u8c6a\uff0c\u5728\u7b80\u5355\u4f46\u5374\u8083\u7a46\u7684 \u4eea\u5f0f\u4e0b\uff0c\u53c2\u62dc\u4e86\u5415\u7956\uff0c\u548c\u7ec8\u5357\u5217\u7956\u7684\u795e\u50cf\uff0c\u6210\u4e3a\u7ec8\u5357\u4e00\u6d3e\u6709\u53f2\u4ee5\u6765\uff0c\u6700\u5e74\u8f7b\u7684\u4e00\u4e2a\u638c\u95e8\u4eba\u3002 \u3000\u3000\u4e8e\u662f\u6c14\u6c1b\u4e5f\u50cf\u662f\u53d8\u5f97\u8f7b\u677e\u5f97\u591a\uff0c\u6b66\u6797\u7fa4\u8c6a\uff0c\u5206\u6210\u4e00\u6279\u4e00\u6279\u7684\uff0c\u5411\u8fd9\u7ec8\u5357\u5251\u6d3e\u65b0\u4efb\u7684\u638c\u95e8 \u4eba\u9053\u8d3a\u3002 \u3000\u3000\u8427\u5357\u82f9\u4e5f\u56de\u8fc7\u5934\uff0c\u53bb\u671b\u4f0a\u98ce\u3002 \u3000\u3000\u5979\u770b\u5230\u4f0a\u98ce\u6b63\u76b1\u7740\u7709\uff0c\u6c89\u601d\u7740\uff0c\u8f7b\u8f7b\u4e00\u7b11\uff0c\u63a8\u4e86\u63a8\u4ed6\u7684\u80a9\u5934\uff0c\u4fcf\u8bed\u9053\uff1a \u3000\u3000\u201c\u4f60\u60f3\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u832b\u7136\u62ac\u5934\uff0c\u671b\u4e86\u5979\u4e00\u773c\uff0c\u5374\u53c8\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u8427\u5357\u82f9\u4e45\u4f5c\u7537\u88c5\uff0c\u4e54\u88c5\u5df2\u60ef\uff0c\u4f46\u6b64\u523b\u5374\u53c8\u5fd8\u8bb0\u4e86\u81ea\u5df1\u662f\u201c\u7537\u4eba\u201d\uff0c\u561f\u8d77\u5c0f\u5634\uff0c\u4e0d\u4f9d\u9053\uff1a \u3000\u3000\u201c\u4f60\u77a7\u4f60\uff01\u60f3\u4ec0\u4e48\u60f3\u5f97\u90a3\u4e48\u51fa\u795e\uff1f\u4eba\u5bb6\u8ddf\u4f60\u8bb2\u8bdd\uff0c\u4f60\u90fd\u4e0d\u7406\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u6b63\u662f\u5bc6\u7ed3\u6ee1\u8179\uff0c\u90a3\u6709\u5fc3\u60c5\u56de\u7b54\u5979\u7684\u8bdd\uff0c\u6f2b\u5e94\u4e86\u4e00\u53e5\uff0c\u7136\u800c\u5374\u53ea\u8981\u8fd9\u4e00\u58f0\u6f2b\u5e94\uff0c \u5374\u5df2\u8db3\u591f\u4f7f\u8fd9\u6c89\u5165\u7231\u60c5\u4e2d\u7684\u5c11\u5973\uff0c\u56de\u55d4\u4f5c\u559c\u4e86\u3002 \u3000\u3000\u5979\u5a07\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u5728\u60f3\u7740\u4ec0\u4e48\uff0c\u4f60\u5728\u60f3\u90a3\u4ef6\u4e8b\u771f\u5947\u602a\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5979\u505c\u987f\u4e00\u4e0b\uff0c\u50cf\u662f\u81ea\u8bed\u4f3c\u7684\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e0d\u8fc7\u4e5f\u662f\u771f\u7684\u5947\u602a\uff0c\u90a3\u4e9b\u4eba\u4e3a\u4ec0\u4e48\u4e00\u770b\u5230\u4f60\u5c31\u8d70\u4e86\u5462\uff1f\u4f60\u53c8\u4e0d\u662f\u4ed6\u4eec\u7684\u2014\u2014\u4ed6\u4eec\u7684\u6559 \u4e3b\uff01\u201d \u3000\u3000\u5979\u672c\u60f3\u8bf4\uff1a\u4f60\u53c8\u4e0d\u662f\u4ed6\u4eec\u7684\u7238\u7238\uff0c\u4f46\u662f\u4e00\u4e2a\u5973\u5b69\u5b50\u5bb6\uff0c\u201c\u7238\u7238\u201d\u4e24\u5b57\u5012\u5e95\u4e0d\u597d\u51fa\u53e3\u3002 \u3000\u3000\u5979\u7684\u8138\u4e5f\u56e0\u5fc3\u91cc\u6709\u4e86\u8fd9\u60f3\u6cd5\u800c\u7ea2\u4e86\u8d77\u6765\uff0c\u7f9e\u6025\u4e4b\u4e0b\uff0c\u5c31\u968f\u610f\u8bf4\u51fa\u4e24\u4e2a\u5b57\uff0c\u5c06\u81ea\u5df1\u7684\u8bdd\u63a5 \u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7136\u800c\u201c\u6559\u4e3b\u201d\u4e24\u5b57\u4e00\u5165\u4f0a\u98ce\u4e4b\u8033\uff0c\u4f0a\u98ce\u5374\u9669\u4e9b\u8df3\u4e86\u8d77\u6765\uff0c\u56de\u8eab\u6293\u4f4f\u5979\u7684\u624b\uff0c\u8131\u53e3\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48.\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u4e00\u6115\uff0c\u4f0a\u98ce\u5374\u6839\u672c\u6ca1\u6709\u8981\u5f97\u5230\u5979\u56de\u7b54\u7684\u610f\u601d\uff0c\u53e3\u4e2d\u4e0d\u4f4f\u5583\u5583\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u5bf9\u4e86\u3002\u201d\u4e00\u53ea\u624b\u4ecd\u634f\u7740\u8427\u5357\u82f9\u7684\u624b\u4e0d\u653e\u3002 \u3000\u3000\u8427\u5357\u82f9\u8138\u4e0a\u7f9e\u7ea2\uff0c\u5fc3\u91cc\u5374\u751c\u751c\u7684\uff0c\u4e00\u6323\uff0c\u6ca1\u6709\u6323\u8131\uff0c\u773c\u89d2\u4e00\u779f\u5927\u6bbf\u4e2d\u7684\u7fa4\u8c6a\uff0c\u4eba\u5bb6\u6839\u672c \u6ca1\u6709\u770b\u4ed6\u4eec\uff0c\u5979\u4e5f\u5c31\u4efb\u4ed6\u63e1\u7740\u3002\u67d4\u60c5\u871c\u610f\uff0c\u6ee1\u5145\u5fc3\u6000\uff0c\u53ea\u6068\u4e0d\u5f97\u6b64\u523b\u5929\u5730\u95f4\u53ea\u5269\u4e0b\u4ed6\u4eec\u4e24 \u4eba\u3002 \u3000\u3000\u90a3\u77e5\u5979\u5fc3\u4e2d\u7684\u8fd9\u4efd\u67d4\u60c5\u871c\u610f\uff0c\u4f0a\u98ce\u53ef\u5374\u4e00\u4e1d\u4e00\u6beb\u4e5f\u6ca1\u6709\u5206\u4eab\u5230\u3002 \u3000\u3000\u4f0a\u98ce\u5728\u542c\u5230\u8427\u5357\u82f9\u65e0\u610f\u4e2d\u8bf4\u51fa\u7684\u201c\u6559\u4e3b\u201d\u4e24\u5b57\u4e4b\u540e\uff0c\u5fc3\u91cc\u84e6\u5730\u840c\u51fa\u4e86\u4e00\u79cd\u60f3\u6cd5\uff0c\u8fd9\u60f3\u6cd5 \u867d\u7136\u602a\u8bde\uff0c\u751a\u81f3\u8fde\u4ed6\u81ea\u5df1\u5bf9\u81ea\u5df1\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\u90fd\u6709\u4e9b\u5403\u60ca\uff0c\u4f46\u4ed6\u4ecd\u7136\u63a5\u7740\u60f3\u4e0b\u53bb\uff0c\u56e0\u4e3a\u8fd9\u60f3 \u6cd5\u867d\u7136\u602a\u8bde\uff0c\u4f46\u5374\u5408\u7406\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u4e3a\u4ec0\u4e48\u4e00\u89c1\u6211\u5c31\u9038\u53bb\uff0c\u8fd9\u672c\u6765\u4e0d\u53ef\u89e3\u91ca\uff0c\u9664\u975e\u2026\u2026\u9664\u975e\u548c\u6211\u6b64\u523b\u7684\u9762\u5bb9\u5b8c\u5168\u76f8 \u4f3c\u7684\u4e00\u4eba\uff0c\u5c31\u662f\u5929\u4e89\u6559\u4e3b\u8427\u65e0\uff1b\u800c\u8427\u65e0\u4e5f\u5c31\u662f\u90a3\u957f\u767d\u5251\u6d3e\uff0c\u98de\u8679\u4e03\u5251\u7b49\u4eba\u53e3\u4e2d\u7684\u201c\u4e09\u5f1f\u201d\u3002 \u3000\u3000\u201c\u662f\u4ee5\u90a3\u591a\u624b\u771f\u4eba\u89c1\u4e86\u6211\uff0c\u4ee5\u4e3a\u662f\u4ed6\u4eec\u7684\u6559\u4e3b\u6765\u4e86\uff0c\u800c\u6559\u4e3b\u65e2\u5982\u6b64\u8bf4\uff0c\u5f53\u7136\u662f\u8ba1\u5212\u6709\u6240 \u66f4\u52a8\u3002\u591a\u624b\u771f\u4eba\u867d\u4e45\u7740\u51f6\u540d\uff0c\u4f46\u4ed6\u5df2\u5c5e\u201c\u5929\u4e89\u6559\u201d\u4e0b\uff0c\u81ea\u7136\u4e0d\u6562\u8fdd\u6297\u6559\u4e3b\uff0c\u662f\u4ee5\u4ed6\u5fc3\u91cc\u867d\u7136 \u5947\u602a\uff0c\u800c\u5374\u4e0d\u5f97\u4e0d\u4e00\u8a00\u4e0d\u53d1\u5730\u8d70\u53bb\u3002 \u3000\u3000\u201c\u800c\u90a3\u94b1\u7fca\uff0c\u60f3\u662f\u56e0\u4e3a\u521d\u5165\u6c5f\u6e56\uff0c\u77e5\u9053\u4ed6\u7684\u540c\u95e8\u662f\u201c\u5929\u4e89\u6559\u201d\u6559\u4e3b\uff0c\u542c\u5230\u591a\u624b\u771f\u4eba\u662f\u5929 \u4e89\u6559\u4e0b\uff0c\u5c31\u51fa\u6765\u5e2e\u591a\u624b\u771f\u4eba\u4e00\u4e2a\u5fd9\u3002\u53ef\u662f\u4ed6\u540e\u6765\u770b\u5230\u6211\u73b0\u8eab\uff0c\u4e5f\u4ee5\u4e3a\u6211\u5c31\u662f\u8427\u65e0\uff0c\u53c8\u89c1\u6211\u8bf4 \u90a3\u79cd\u8bdd\uff0c\u662f\u4ee5\u4fbf\u5728\u81ea\u8ba4\u591a\u4e8b\u4e4b\u4e0b\uff0c\u62c2\u8896\u800c\u53bb\u3002 \u3000\u3000\u201c\u5f53\u7136\uff0c\u8fd9\u4e5f\u6709\u53ef\u80fd\u662f\u4ed6\u4eec\u4e4b\u95f4\u65e9\u5df2\u6709\u9884\u8c0b\uff0c\u90a3\u94b1\u7fca\u5e76\u975e\u51d1\u5de7\uff0c\u800c\u662f\u7279\u610f\u5730\u8d76\u5230\u6b64 \u95f4\u3002\u201d \u3000\u3000\u4f46\u8fd9\u4e9b\u7ec6\u8282\uff0c\u4f0a\u98ce\u5df2\u4e0d\u53bb\u6df1\u7a76\uff0c\u56e0\u4e3a\u4ed6\u5df2\u4ece\u5343\u4e07\u6761\u601d\u8def\u4e2d\uff0c\u627e\u51fa\u4e86\u6700\u8352\u8c2c\uff0c\u5374\u4e5f\u662f\u6700\u4e3a \u5408\u7406\u7684\u4e00\u6761\u3002 \u3000\u3000\u56e0\u4e3a\u5929\u4e89\u6559\u521b\u6559\u4ee5\u6765\uff0c\u5929\u4e89\u6559\u4e3b\u8427\u65e0\u867d\u540d\u6ee1\u5929\u4e0b\uff0c\u4f46\u8427\u65e0\u7684\u771f\u9762\u76ee\uff0c\u5374\u59cb\u7ec8\u65e0\u4eba\u89c1\u8fc7\u3002 \u3000\u3000\u5c31\u8fde\u5929\u4e89\u6559\u603b\u575b\u6240\u5728\u5730\uff0c\u6c5f\u6e56\u4e2d\u4eba\u4e5f\u53ea\u77e5\u662f\u5728\u6c5f\u5357\uff0c\u7a76\u7adf\u5728\u4ec0\u4e48\u5730\u65b9\uff01\u5374\u4e5f\u65e0\u4eba\u77e5\u9053 \u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u867d\u88ab\u8427\u65e0\u593a\u53bb\u4e86\u59bb\u5b50\uff0c\u907f\u5f97\u65e0\u5904\u5bb9\u8eab\uff0c\u4f46\u8427\u65e0\u7684\u5e90\u5c71\u771f\u9762\u76ee\uff0c\u4ed6\u5374\u4e5f\u6ca1\u6709\u89c1\u5230\u8fc7\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u81ea\u5fd6\uff0c\u4ed6\u6b64\u523b\u7684\u9762\u8c8c\uff0c\u65e2\u88ab\u591a\u624b\u771f\u4eba\u7b49\u5982\u6b64\u754f\u60e7\uff0c\u4f46\u6ee1\u5802\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u5374\u65e0\u4e00 \u4eba\u8ba4\u8bc6\uff0c\u90a3\u4e48\u81ea\u5df1\u6b64\u523b\u6b63\u548c\u9664\u4e86\u5929\u4e89\u6559\u4e0b\u7684\u91d1\u8863\u9999\u4e3b\u4eec\u5916\uff0c\u518d\u65e0\u4e00\u4eba\u89c1\u5230\u8fc7\u5e90\u5c71\u771f\u9762\u76ee\u7684\u5929 \u4e89\u6559\u4e3b\u8427\u65e0\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u3002\u8fd9\u4e0d\u662f\u6781\u4e3a\u5408\u7406\uff0c\u800c\u53c8\u51e0\u4e4e\u662f\u552f\u4e00\u5408\u7406\u7684\u63a8\u6d4b\u5417\uff1f \u3000\u3000\u7136\u800c\u8fd9\u60f3\u6cd5\u5374\u4f7f\u5f97\u4f0a\u98ce\u81ea\u5df1\u4e5f\u4e3a\u4e4b\u9707\u60ca\u4e0d\u5df2\uff0c\u4ed6\u751a\u81f3\u6709\u4e9b\u557c\u7b11\u7686\u975e\u7684\u611f\u89c9\u3002\u4f46\u4ece\u5934\u5230 \u5c3e\uff0c\u4ed6\u518d\u5c06\u81ea\u5df1\u5148\u524d\u6240\u4f5c\u7684\u63a8\u7a76\uff0c\u7ec6\u7ec6\u60f3\u4e86\u4e00\u904d\uff0c\u89c9\u5f97\u81ea\u5df1\u6b64\u523b\u6240\u4f5c\u7684\u63a8\u65ad\uff0c\u5176\u4e2d\u867d\u7136\u8fd8\u6709 \u4e9b\u5fae\u7ec6\u8282\uff0c\u81ea\u5df1\u5c1a\u4e0d\u80fd\u660e\u4e86\uff1b\u4f46\u6574\u4f53\u8bf4\u6765\uff0c\u5374\u663e\u7136\u662f\u5408\u7406\u7684\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u81ea\u5df1\u8be5\u7acb\u523b\u6495\u4e0b\u8fd9\u5f20\u548c\u4ed6\u751f\u5e73\u6700\u5927\u7684\u4ec7\u654c\u9762\u5bb9\u5b8c\u5168\u76f8\u540c\u7684\u9762\u5177\uff0c\u6291\u6216\u662f\u7559\u4e0b \u5b83\uff0c\u751a\u81f3\u5229\u7528\u5b83\u505a\u4e00\u4e9b\u4e8b\u3002 \u3000\u3000\u4ed6\u867d\u4e86\u89e3\u8fd9\u5f20\u9762\u5177\u5bf9\u4ed6\u81ea\u5df1\u6781\u53ef\u80fd\u6709\u7740\u5f88\u5927\u7684\u5229\u7528\u4ef7\u503c\uff0c\u7136\u800c\u5f53\u4e00\u4e2a\u4eba\u5bf9\u955c\u81ea\u7167\u65f6\uff0c\u77e5 \u9053\u81ea\u5df1\u7684\u9762\u8c8c\u7adf\u548c\u90a3\u593a\u53bb\u81ea\u5df1\u59bb\u5b50\uff0c\u4f7f\u5f97\u81ea\u5df1\u4ee5\u201c\u8bc8\u6b7b\u201d\u6765\u8eb2\u8fc7\u8ffd\u51fb\u7684\u4eba\u4e00\u6837\u65f6\uff0c\u90a3\u4e48\u4ed6\u5fc3 \u4e2d\u53c8\u8be5\u662f\u4ec0\u4e48\u6ecb\u5473\u5462\uff1f \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u4e09\u7ae0\u3000\u53bb\u800c\u590d\u8fd4>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u4e09\u7ae0\u3000\u53bb\u800c\u590d\u8fd4 \u3000\u3000\u7a81\u5730\uff0c\u4e00\u58f0\u8f7b\u8f7b\u7684\u54b3\u55fd\uff0c\u60ca\u7834\u4e86\u8427\u5357\u82f9\u7684\u67d4\u60c5\u871c\u610f\uff0c\u548c\u4f0a\u98ce\u7684\u5c42\u5c42\u601d\u8651\u3002\u65b0\u4efb\u7684\u7ec8\u5357\u638c \u95e8\u2014\u2014\u7384\u5316\u9053\u4eba\uff0c\u7ad9\u5728\u4f0a\u98ce\u9762\u524d\uff0c\u606d\u8eab\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u8c28\u4e3a\u7ec8\u5357\u95e8\u4e0b\u5168\u4f53\u5f1f\u5b50\uff0c\u5411\u9601\u4e0b\u53e9\u8c22\u5927\u6069\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8fd9\u7ec8\u5357\u5251\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u4e00\u64a9\u9053\u888d\uff0c\u7adf\u7aef\u7aef\u6b63\u6b63\u5730\u8dea\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u84e6\u7136\u60ca\u89c9\uff0c\u62ac\u773c\u4e00\u770b\uff0c\u5927\u6bbf\u4e2d\u7684\u51e0\u767e\u5bf9\u773c\u775b\uff0c\u6b64\u523b\u6b63\u90fd\u6ce8\u89c6\u7740\u81ea\u5df1\uff0c\u800c\u90a3\u5df2\u6210\u4e3a\u638c \u95e8\u4eba\u7684\u7384\u5316\u9053\u4eba\uff0c\u6b63\u8dea\u5728\u81ea\u5df1\u9762\u524d\u3002 \u3000\u3000\u4ed6\u53c8\u4e00\u60ca\uff0c\u8fde\u5fd9\u4e5f\u8dea\u4e86\u4e0b\u53bb\u3002\u7384\u5316\u9053\u4eba\u53c8\u4f38\u8fc7\u624b\u53bb\u6400\u4ed6\uff0c\u53e3\u4e2d\u9053\uff1a \u3000\u3000\u201c\u6069\u4eba\u82e5\u4e0d\u80af\u53d7\u8d2b\u9053\u4e00\u62dc\uff0c\u90a3\u4e48\u8d2b\u9053\u5fc3\u4e2d\u8d8a\u53d1\u4e0d\u5b89\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u81ea\u7136\u8dea\u5728\u5730\u4e0a\u4e0d\u80af\u8d77\u6765\uff0c\u5374\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u3002\u53e3\u4e2d\u5450\u5450\u5730\uff0c\u6b63\u60f3\u627e\u51e0\u53e5\u8bdd\u6765\u8bf4\uff0c\u7a81 \u542c\u5927\u6bbf\u6b63\u95e8\u90a3\u91cc\u53c8\u662f\u4e00\u9635\u9a9a\u52a8\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u77ac\u773c\u53bb\u671b\uff0c\u4f46\u4ed6\u8dea\u5728\u5730\u4e0a\uff0c\u5374\u4e5f\u770b\u4e0d\u5230\u4ec0\u4e48\u3002\u5374\u542c\u8427\u5357\u82f9\u9053\uff1a \u3000\u3000\u201c\u54a6\uff01\u90a3\u201c\u98de\u8679\u4e03\u5251\u201d\u600e\u5730\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u8fde\u5fd9\u56de\u624b\u53bb\u6400\u6276\u7384\u5316\uff0c\u53e3\u4e2d\u8fde\u8fde\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u5207\u83ab\u5982\u6b64\uff0c\u6298\u715e\u5c0f\u9119\u4e86\uff01\u201d \u3000\u3000\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u4ea6\u53d7\u4e86\u8d35\u6d3e\u4e4b\u6069\u3002\u201d \u3000\u3000\u53c8\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u8d76\u5feb\u8d77\u6765\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u672c\u5df2\u7d0a\u4e71\uff0c\u542c\u5230\u201c\u98de\u8679\u4e03\u5251\u201d\u53bb\u800c\u590d\u8fd4\uff0c\u5fc3\u4e2d\u66f4\u662f\u5927\u52a8\uff0c\u8bf4\u8bdd\u7adf\u90fd\u6709\u4e9b\u8bed\u65e0\u4f26\u6b21 \u4e86\u3002 \u3000\u3000\u6b64\u523b\u201c\u98de\u8679\u4e03\u5251\u201d\u4e2d\u7684\u6bdb\u6587\u5947\uff0c\u534e\u54c1\u5947\uff0c\u60f3\u662f\u56e0\u4e3a\u770b\u89c1\u4e86\u8dea\u5728\u5927\u6bbf\u6b63\u524d\u65b9\uff0c\u6781\u4e3a\u89e6\u76ee\u7684 \u4f0a\u98ce\u6765\uff0c\u6392\u5f00\u7fa4\u8c6a\uff0c\u4e5f\u6324\u5230\u6bbf\u4e2d\uff0c\u5bf9\u7740\u4f0a\u98ce\u8fdc\u8fdc\u559d\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u4e14\u8fc7\u6765\uff0c\u6211\u5f1f\u5144\u8fd8\u6709\u8bdd\u8981\u95ee\u95ee\u4f60\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e9b\u957f\u767d\u5251\u624b\uff0c\u5728\u534e\u54c1\u5947\u4ee5\u4e00\u62db\u957f\u767d\u5251\u6d3e\u4e2d\u7684\u7edd\u5b66\u201c\u98a0\u5012\u5e72\u5764\u201d\uff0c\u8bd5\u51fa\u4f0a\u98ce\u679c\u7136\u4e0d \u662f\u957f\u767d\u95e8\u4e0b\uff0c\u8f6c\u8eab\u79bb\u53bb\u540e\uff0c\u6b64\u6b21\u53c8\u91cd\u65b0\u6298\u4e86\u56de\u5934\uff0c\u6b63\u662f\u4e3a\u4e86\u5bfb\u627e\u8fd9\u548c\u201c\u98de\u8679\u4e03\u5251\u201d\u4e2d\u7684\u953a\u82f1 \u5947\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u7684\u4eba\u3002\u6b64\u523b\u89c1\u4e86\u4f0a\u98ce\uff0c\u5c31\u559d\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4e45\u5c45\u5173\u4e1c\uff0c\u6027\u6ca1\u906e\u5962\uff0c\u7adf\u6ca1\u6709\u60f3\u5230\u8fd9\u79cd\u5730\u65b9\uff0c\u5c82\u5bb9\u5f97\u4ed6\u4eec\u5927\u8086\u5406\u559d\uff1f\u5999\u6cd5\u9053\u4eba\u8138\u81ea \u4e00\u6c89\uff0c\u90a3\u5999\u901a\u9053\u4eba\u5374\u5df2\u55d4\u9053\uff1a \u3000\u3000\u201c\u65bd\u4e3b\u4eec\u90a3\u91cc\u6765\uff1f\u8981\u627e\u4ec0\u4e48\u4eba\uff1f\u795e\u6bbf\u4e4b\u4e2d\uff0c\u65bd\u4e3b\u4eec\u4e5f\u8be5\u5b89\u9759\u4e9b\uff01\u201d \u3000\u3000\u534e\u54c1\u5947\u8138\u4e5f\u4e00\u6c89\u3002\u4f0a\u98ce\u5374\u5df2\u62a2\u6b65\u8fc7\u6765\uff0c\u62e6\u5728\u5999\u901a\u524d\u9762\uff0c\u671d\u534e\u54c1\u5947\u5fae\u4e00\u62b1\u62f3\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u524d\u8f88\u53bb\u800c\u590d\u8fd4\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5999\u901a\u9053\u4eba\u89c1\u8fd9\u4e9b\u9c81\u83bd\u6c49\u5b50\uff0c\u662f\u81ea\u5df1\u5168\u95e8\u6069\u4eba\u7684\u76f8\u8bc6\uff0c\u4fbf\u4e5f\u65e0\u53ef\u5982\u4f55\u3002 \u3000\u3000\u90a3\u77e5\u534e\u54c1\u5947\u51b7\u7b11\u4e00\u58f0\uff0c\u5389\u559d\u9053\uff1a \u3000\u3000\u201c\u6211\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u4f0a\u98ce\u65b9\u81ea\u4e00\u6115\uff0c\u5374\u89c1\u6f2b\u5929\u5149\u534e\u4e71\u95ea\u3002\u539f\u6765\u534e\u54c1\u5947\u5df2\u5728\u8fd9\u5389\u559d\u58f0\u4e2d\uff0c\u62d4\u51fa\u957f\u5251\uff0c\u7adf\u4ee5\u65b9\u624d \u5b8c\u5168\u76f8\u540c\u7684\u4e00\u62db\uff0c\u201c\u98a0\u5012\u5e72\u5764\u201d\uff0c\u523a\u5411\u4f0a\u98ce\u3002 \u3000\u3000\u4f0a\u98ce\u60ca\u6115\u4e4b\u4e0b\uff0c\u773c\u5149\u77ac\u5904\uff0c\u53c8\u77a5\u89c1\u90a3\u5251\u5149\u4e2d\u7684\u7a7a\u9699\u4e4b\u5904\uff0c\u8fd9\u65f6\u4ed6\u672c\u5df2\u7d0a\u4e71\u4e0d\u582a\u4e4b\u8111\u6d77\uff0c \u5df2\u6d51\u7136\u5fd8\u5374\u4e86\u65b9\u624d\u81ea\u5df1\u6240\u53d7\u5230\u7684\u6559\u8bad\uff0c\u51e0\u4e4e\u662f\u51fa\u4e4e\u672c\u80fd\u7684\uff0c\u53c8\u5f80\u90a3\u5251\u5149\u7684\u7a7a\u9699\u5904\u4e00\u95ea\u3002 \u3000\u3000\u5f53\u7136\uff0c\u50cf\u4e0a\u4e00\u6b21\u4e00\u6837\uff0c\u6f2b\u5929\u5149\u534e\u53c8\u8f6c\u53d8\u4e3a\u9752\u5149\u4e00\u7f15\uff0c\u5411\u4ed6\u95ea\u907f\u7684\u65b9\u5411\u523a\u53bb\u3002\u4f46\u548c\u4e0a\u6b21\u4e0d \u540c\u7684\uff0c\u5728\u534e\u54c1\u5947\u624b\u4e2d\u7684\u957f\u5251\u5241\u5411\u4f0a\u98ce\u65f6\uff0c\u4fa7\u9762\u7a81\u7136\u5bd2\u5149\u66b4\u957f\uff0c\u201c\u62d0\u4e00\u67c4\u5251\u5df2\u523a\u5411\u4ed6\u814b\u4e0b\u4e09\u5bf8 \u7684\u201c\u5929\u6c60\u7a74\u201d\u3002 \u3000\u3000\u8fd9\u201c\u5929\u6c60\u7a74\u201d\u5c5e\u624b\u53a5\u9634\u7ecf\uff0c\u5728\u814b\u4e0b\u4e09\u5bf8\uff0c\u4e73\u540e\u4e00\u5bf8\uff0c\u7740\u80c1\u76f4\u814b\uff0c\u6485\u80c1\u95f4\uff0c\u4e43\u4eba\u8eab\u5927\u7a74\u4e4b \u4e00\uff0c\u8fd9\u4e00\u62db\u6b63\u662f\u653b\u534e\u54c1\u5947\u4e4b\u5fc5\u6551\u3002 \u3000\u3000\u534e\u54c1\u5947\u51b7\u7b11\u4e00\u58f0\uff0c\u811a\u6b65\u5fae\u9519\u95f4\uff0c\u6e9c\u5f00\u4e09\u5c3a\uff0c\u5374\u6839\u672c\u4e0d\u7406\u4f1a\u90a3\u62d4\u5251\u523a\u5411\u4ed6\u7684\u6885\u82b1\u5251\u675c\u957f \u537f\uff0c\u53cd\u5374\u5411\u7740\u6bdb\u6587\u5947\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e8c\u5f1f\uff01\u679c\u7136\u4e0d\u51fa\u4f60\u6240\u6599\uff0c\u679c\u7136\u4e0d\u51fa\u4f60\u6240\u6599\u3002\u201d \u3000\u3000\u8f6c\u9996\u5411\u4f0a\u98ce\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u4f60\u4e5f\u4e0d\u5fc5\u518d\u7792\u7740\u6211\u4eec\uff0c\u6709\u4ec0\u4e48\u4e8b\u5c3d\u9119\u8bf4\u51fa\u6765\uff0c\u96be\u9053\u4f60\u6211\u5144\u5f1f\u4e4b\u95f4\u90a3\u4e48\u591a\u5e74\u76f8 \u5904\uff0c\u7adf\u8fde\u4e00\u70b9\u513f\u60c5\u4efd\u90fd\u6ca1\u6709\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u5168\u7136\u6115\u4f4f\u4e86\uff0c\u4ed6\u96be\u4ee5\u4e86\u89e3\u8fd9\u201c\u98de\u8679\u4e03\u5251\u201d\u660e\u660e\u5df2\u5728\u5224\u522b\u81ea\u5df1\u4e0d\u662f\u4ed6\u4eec\u7684\u5e08\u5f1f\u540e\u79bb \u53bb\uff0c\u6b64\u523b\u5374\u53c8\u6298\u56de\u6765\uff0c\u53c8\u8bf4\u8fd9\u4e9b\u8bdd\u5462\uff1f \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u534e\u54c1\u5947\u7b49\u4eba\u98de\u9a6c\u9a70\u53bb\u540e\uff0c\u6bdb\u6587\u5947\u5c31\u57cb\u6028\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff01\u4f60\u4e5f\u592a\u5fe0\u539a\u4e86\uff01\u4e09\u5f1f\u82e5\u4e0d\u80af\u8ba4\u6211\u4eec\uff0c\u4ed6\u5927\u53ef\u4ee5\u88c5\u505a\u4e0d\u61a7\u8fd9\u4e00\u62db\u201c\u98a0\u5012\u5e72\u5764\u201d\u7684 \u5965\u5999\u3002\u56e0\u4e3a\u4ed6\u660e\u77e5\u5927\u54e5\u4f60\u4e0d\u4f1a\u4f24\u4ed6\u7684\u3002\u201d \u3000\u3000\u662f\u4ee5\u8fd9\u201c\u98de\u8679\u4e03\u5251\u201d\u4e2d\u7684\u56db\u4eba\uff0c\u53c8\u6298\u4e86\u56de\u6765\uff0c\u800c\u534e\u54c1\u5947\u518d\u4ee5\u201c\u98a0\u5012\u5e72\u5764\u201d\u4e00\u62db\u76f8\u8bd5\u3002\u6b64 \u523b\u4f0a\u98ce\u82e5\u5fc3\u5883\u6f84\u5e73\uff0c\u5728\u51e0\u4e2a\u65f6\u8fb0\u524d\u624d\u5403\u8fc7\u6b64\u62db\u7684\u82e6\uff0c\u6b64\u523b\u5c31\u7b97\u8eb2\u4e0d\u8fc7\u6b64\u62db\uff0c\u81f3\u5c11\u4e5f\u4e0d\u4f1a\u91cd\u8e48 \u8986\u8f99\uff0c\u518d\u50cf\u4e0a\u4e00\u6b21\u90a3\u6837\u53bb\u8eb2\u3002\u987b\u77e5\u7eb5\u4f7f\u7b28\u5230\u6781\u70b9\u4e4b\u4eba\uff0c\u4e5f\u65ad\u7136\u6ca1\u6709\u4eba\u4f1a\u5728\u4e00\u4e2a\u6781\u77ed\u7684\u65f6\u95f4 \u91cc\uff0c\u540c\u4e0a\u4e24\u6b21\u7edd\u5bf9\u76f8\u540c\u7684\u5f53\u7684\u9053\u7406\u3002 \u3000\u3000\u662f\u4ee5\u534e\u54c1\u5947\u4fbf\u63a8\u65ad\u4f0a\u98ce\u662f\u6545\u610f\u5982\u6b64\u7684\uff0c\u5426\u5219\u4ed6\u600e\u4f1a\u7b28\u5230\u5982\u6b64\u7530\u5730\uff01\u800c\u56e0\u6b64\uff0c\u4ed6\u4eec\u7adf\u4e5f\u4e3b \u89c2\u5730\u65ad\u5b9a\u4f0a\u98ce\u5c31\u662f\u4ed6\u4eec\u5931\u8e2a\u7684\u5e08\u5f1f\u953a\u82f1\u5947\u3002 \u3000\u3000\u6b64\u65f6\u5927\u6bbf\u4e2d\u7684\u7fa4\u8c6a\uff0c\u53c8\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u6301\u5251\u800c\u7acb\u7684\u6885\u82b1\u5251\u675c\u957f\u537f\u548c\u7ec8\u5357\u5f1f\u5b50\u4eec\uff0c\u5728\u542c\u5230\u534e\u54c1\u5947\u79f0\u547c\u4f0a\u98ce\u201c\u4e09\u5f1f\u201d\uff0c\u800c\u4f0a\u98ce\u7adf\u50cf \u4e5f\u9ed8\u8ba4\u4e86\u7684\u65f6\u5019\uff0c\u66f4\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u4ed6\u4eec\u5bf9\u4f0a\u98ce\u7684\u6765\u5386\uff0c\u672c\u5c31\u4e00\u65e0\u6240\u77e5\uff0c\u6b64\u523b\u5f53\u7136\u66f4\u4e3a\u8ff7\u60d8\u3002 \u3000\u3000\u5927\u6bbf\u4e2d\u7684\u6570\u767e\u53cc\u773c\u775b\uff0c\u6b64\u523b\u5f53\u7136\u53c8\u90fd\u843d\u5728\u4f0a\u98ce\u8eab\u4e0a\u3002 \u3000\u3000\u5c31\u8fde\u8427\u5357\u82f9\uff0c\u90fd\u4e5f\u88ab\u4eca\u65e5\u6240\u53d1\u751f\u7684\u4e00\u8fde\u4e32\u5947\u602a\u7684\u4e8b\uff0c\u5f04\u5f97\u6df7\u6c8c\u4e00\u7247\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\uff0c\u8111\u6d77\u4e2d\u6781\u5feb\u5730\u95ea\u8fc7\u51e0\u4e2a\u5ff5\u5934\uff0c\u4ed6\u77e5\u9053\u6b64\u4e8b\uff0c\u6b64\u523b\u5df2\u4e0d\u662f\u4e09\u8a00\u4e24\u8bed\u6240\u80fd\u89e3\u91ca\uff0c \u5fc3\u4e2d\u65b9\u4e0b\u4e86\u4e2a\u51b3\u5b9a\uff0c\u534e\u54c1\u5947\u5374\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u4f60\u6211\u5f1f\u5144\u4e4b\u4e8b\uff0c\u5927\u53ef\u4e0d\u5fc5\u5f53\u7740\u8fd9\u4e48\u591a\u5916\u4eba\u6765\u8bb2\uff0c\u4f60\u8fd8\u662f\u8ddf\u7740\u5927\u54e5\u6211\u4e0b\u5c71\u53bb\u5427\uff01 \u5509\u2014\u2014\u201d \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e3a\u7740\u4e9b\u8bb8\u5c0f\u4e8b\uff0c\u4f60\u53c8\u4f55\u82e6\u5982\u6b64\u5462\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a \u3000\u3000\u201c\u59d3\u534e\u7684\uff01\u4f60\u600e\u5730\u8fd9\u4e48\u565c\u82cf\uff01\u6211\u544a\u8bc9\u4f60\u2026\u2026\u201d \u3000\u3000\u90a3\u77e5\u4f0a\u98ce\u5374\u4e00\u62c9\u4ed6\u7684\u8896\u5b50\uff0c\u963b\u6b62\u4f4f\u4e86\u5979\u7684\u8bdd\uff0c\u4fa7\u8eab\u5bf9\u5979\u8f7b\u58f0\u9053\uff1a \u3000\u3000\u201c\u6211\u4e14\u968f\u8fd9\u201c\u98de\u8679\u4e03\u5251\u201d\u4e00\u884c\uff0c\u4f60\u4e0d\u59a8\u5728\u59da\u6e05\u5b87\u5927\u54e5\u5904\u7b49\u6211\u3002\u201d \u3000\u3000\u4e0d\u7b49\u8427\u5357\u82f9\u7b54\u8bdd\uff0c\u53c8\u8f6c\u8eab\u5411\u90a3\u4e9b\u60ca\u8be7\u7684\u7ec8\u5357\u5f1f\u5b50\u62f1\u624b\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u4fd7\u52a1\u7f20\u8eab\uff0c\u4eca\u65e5\u6682\u4e14\u522b\u8fc7\uff0c\u4ed6\u65e5\u6709\u7f18\uff0c\u5c0f\u9119\u81ea\u5f53\u518d\u6765\u62dc\u5019\u3002\u201d \u3000\u3000\u5999\u6cd5\u9053\u4eba\u6839\u672c\u5c31\u5168\u7136\u4e0d\u77e5\u9053\u6b64\u4e8b\u7684\u7a76\u7adf\uff0c\u6b64\u523b\u53ea\u5f97\u4e5f\u5408\u5341\u9053\uff1a \u3000\u3000\u201c\u65bd\u4e3b\u5929\u9645\u795e\u9f99\uff0c\u6765\u53bb\u5306\u5306\uff0c\u8d2b\u9053\u4eec\u867d\u826f\u4e45\u8046\u6559\u76ca\u4e4b\u5fc3\uff0c\u5374\u4e5f\u77e5\u9053\u65e0\u6cd5\u7559\u5f97\u4f4f\u4fa0\u9a7e\uff0c\u53ea \u662f\u5306\u5306\u4e00\u4f1a\uff0c\u9601\u4e0b\u7684\u5927\u6069\u5927\u5fb7\uff0c\u8db3\u4ee5\u4f7f\u6211\u7ec8\u5357\u6d3e\u6570\u767e\u5f1f\u5b50\uff0c\u6c38\u94ed\u4e0d\u5fd8\u4e86\uff01\u201d \u3000\u3000\u534e\u54c1\u5947\u8138\u4e0a\u5fae\u9732\u559c\u8272\uff0c\u4ed6\u4ee5\u4e3a\u81ea\u5df1\u7684\u5e08\u5f1f\u5df2\u8ff7\u9014\u77e5\u8fd4\u3002\u90a3\u77e5\u9053\u4f0a\u98ce\u6b64\u4e3e\uff0c\u53ea\u662f\u60f3\u4ece\u8fd9 \u201c\u98de\u8679\u4e03\u5251\u201d\u8eab\u4e0a\uff0c\u591a\u5f97\u5230\u4e00\u70b9\u8427\u65e0\u7684\u6d88\u606f\u800c\u5df2\u3002 \u3000\u3000\u56e0\u81f3\u6b64\u4e3a\u6b62\uff0c\u4ed6\u9664\u4e86\u77e5\u9053\u8427\u65e0\u548c\u81ea\u5df1\u6b64\u523b\u7684\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u4e4b\u5916\uff0c\u5176\u4f59\u7684\uff0c\u5374\u4ecd\u7136\u662f\u4e00\u65e0 \u6240\u77e5\u7684\u3002 \u3000\u3000\u6700\u96be\u53d7\u7684\uff0c\u5374\u662f\u8427\u5357\u82f9\uff0c\u5979\u672c\u60f3\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5374\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u8bf4\u3002\u5979\u672c\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u4f46 \u6b64\u523b\u60c5\u611f\u5374\u4f7f\u5979\u53d8\u5f97\u75f4\u4e86\uff01 \u3000\u3000\u4eba\u4eec\u7684\u7b2c\u4e00\u6b21\u604b\u60c5\uff0c\u6c38\u8fdc\u662f\u5982\u6b64\u6fc0\u70c8\u7684\uff01\u6b66\u6797\u7fa4\u8c6a\uff0c\u6709\u7684\u5728\u5c71\u811a\u66fe\u7ecf\u76ee\u7779\u6b64\u4e8b\u7684\u524d\u4e00 \u534a\uff1b\u6709\u7684\u6839\u672c\u6ca1\u6709\uff0c\u4f46\u5374\u5168\u4e0d\u77e5\u9053\u6b64\u4e8b\u7684\u7a76\u7adf\u3002\u76f4\u5230\u5f88\u4e45\u4ee5\u540e\uff0c\u8fd9\u4ef6\u4e8b\u5728\u6b66\u6797\u4e2d\u7684\u4e00\u90e8\u4efd\u4eba \u53e3\u4e2d\uff0c\u4ecd\u662f\u4e00\u4e2a\u4e0d\u53ef\u89e3\u91ca\u7684\u8c1c\u54e9\uff01 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u56db\u7ae0\u3000\u6005\u6005\u79bb\u60c5>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u56db\u7ae0\u3000\u6005\u6005\u79bb\u60c5 \u3000\u3000\u6b64\u523b\u66ae\u8272\u5df2\u5408\uff0c\u665a\u971e\u521d\u843d\u3002\u897f\u8fb9\u5929\u672b\uff0c\u5c1a\u7559\u5f97\u51e0\u75d5\u6de1\u6de1\u7684\u4e91\u971e\uff0c\u5f71\u6620\u5f97\u6ee1\u5929\u67af\u6728\u758f\u6797\uff0c \u5e73\u6dfb\u4e86\u591a\u5c11\u5e7d\u6e05\u7684\u753b\u610f\u3002 \u3000\u3000\u4f0a\u98ce\u968f\u7740\u201c\u98de\u8679\u4e03\u5251\u201d\u51fa\u89c2\u4e0b\u5c71\uff0c\u5404\u5404\u5fc3\u91cc\u90fd\u6709\u7740\u5fc3\u4e8b\uff0c\u662f\u4ee5\u4e00\u8def\u9ed8\u7136\u3002\u53ea\u6709\u534e\u54c1\u5947\u53d1 \u51fa\u7684\u53f9\u606f\u58f0\uff0c\u5076\u800c\u6253\u7834\u6c89\u5bc2\u3002 \u3000\u3000\u6b64\u523b\u5929\u5df2\u5165\u66ae\uff0c\u518d\u52a0\u4e0a\u4ed6\u4eec\u90fd\u77e5\u9053\u6b64\u5c71\u6b64\u523b\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u662f\u4ee5\u4fbf\u90fd\u5c55\u5f00\u8eab\u6cd5\uff0c\u5bc2\u5bc2\u5c71 \u8def\u4e0a\uff0c\u53ea\u89c1\u51e0\u6761\u6781\u6de1\u4eba\u5f71\u4e00\u95ea\u800c\u8fc7\u3002 \u3000\u3000\u5230\u4e86\u5c71\u811a\u4e0b\uff0c\u98de\u8679\u5251\u5ba2\u4eec\u65b9\u624d\u9a91\u6765\u7684\u4e09\u5339\u5065\u9a6c\uff0c\u6b63\u88ab\u7cfb\u5728\u4e00\u6bb5\u67af\u5e72\u4e4b\u4e0a\u3002 \u3000\u3000\u534e\u54c1\u5947\u4fa7\u987e\u4f0a\u98ce\u4e00\u773c\uff0c\u559f\u7136\u8bf4\u9053 \u3000\u3000\u201c\u4e09\u5f1f\uff0c\u4f60\u5148\u548c\u6211\u540c\u4e58\u4e00\u9a91\u5427\u3002\u201d \u3000\u3000\u4ed6\u53f9\u7682\u4e00\u58f0\uff0c\u53c8\u9053 \u3000\u3000\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\uff0c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u90a3\u5929\u4e0d\u62b1\u7740\u4f60\u9a91\u9a6c\u515c\u4e00\u4e2a\u5708\u5b50\uff1f\u5509\uff0c\u5c81\u6708\u50ac\u4eba\uff0c\u5982\u4eca\u4f60 \u5df2\u957f\u5927\u6210\u4eba\uff0c\u800c\u6211\u2014\u2014\u4e5f\u8001\u4e86\u3002\u201d \u3000\u3000\u53f9\u606f\u7684\u5c3e\u97f3\uff0c\u4e45\u4e45\u4e0d\u843d\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u540c\u60c5\u5730\u770b\u4e86\u8fd9\u5782\u66ae\u7684\u6b66\u6797\u5065\u8005\u4e00\u773c\uff0c\u5fc3\u91cc\u5bf9\u8427\u65e0\uff0c\u66f4\u8d77\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u6765\u7684\u538c \u6076\u3002\u60f3\u89c1\u90a3\u8427\u65e0\uff0c\u5fc5\u5b9a\u662f\u5929\u6027\u6781\u4e3a\u51c9\u8584\u65e0\u60c5\u4e4b\u4eba\uff0c\u5426\u5219\u53c8\u600e\u4f1a\u5982\u6b64\uff01 \u3000\u3000\u4ed6\u6b63\u81ea\u611f\u53f9\u95f4\uff0c\u5ffd\u7136\u5c71\u7554\u4f20\u6765\u4e00\u58f0\u58f0\u5c16\u9510\u800c\u6025\u5207\u7684\u547c\u58f0\uff0c\u4f0a\u98ce\u4e00\u542c\uff0c\u5c31\u77e5\u9053\u662f\u8427\u5357\u82f9\u5728 \u547c\u5524\u7740\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u6025\u5207\u7684\u547c\u58f0\uff0c\u4f7f\u5f97\u4ed6\u7a81\u7136\u5347\u8d77\u4e86\u4e00\u79cd\u6b49\u610f\uff0c\u4f4e\u53f9\u4e00\u58f0\uff0c\u4ed6\u6084\u7136\u56de\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u4e0a\u679c\u7136\u6781\u5feb\u5730\u7a9c\u4e0b\u4e00\u4eba\uff0c\u7b14\u76f4\u5730\u63a0\u5230\u4ed6\u8eab\u524d\uff0c\u4f9d\u4f9d\u5a07\u5598\u7740\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u8fc7\u6025\u7684\u5954 \u9a70\uff0c\u6b64\u523b\u989d\u4e0a\u751a\u81f3\u5df2\u73b0\u6c57\u73e0\u4e86\u3002 \u3000\u3000\u201c\u5357\u54e5\uff01\u6211\u2026\u2026\u6211\u8981\u548c\u4f60\u4e00\u9f50\u8d70\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u6e29\u67d4\u7684\u76ee\u5149\uff0c\u4e5e\u601c\u5730\u671b\u7740\u4f0a\u98ce\u3002 \u3000\u3000\u665a\u98ce\u98d2\u7136\uff0c\u85c9\u7740\u5c06\u9eef\u7684\u5929\u8272\uff0c\u4f0a\u98ce\u770b\u5230\u4e86\u5979\u53cc\u988a\u7684\u7ea2\u6655\uff0c\u4e24\u9b13\u7684\u4e71\u53d1\uff0c\u867d\u7136\u662f\u7537\u88c5\uff0c\u4f46 \u5979\u4ecd\u663e\u5f97\u90a3\u6837\u59a9\u5a9a\u52a8\u4eba\u3002\u5373\u4f7f\u6700\u4e11\u7684\u5973\u5b50\uff0c\u5728\u771f\u60c5\u6d41\u9732\u65f6\uff0c\u4e5f\u4f1a\u53d8\u5f97\u7f8e\u4e86\uff0c\u4f55\u51b5\u8427\u5357\u82f9\u8fd9\u7f8e \u82e5\u6625\u82b1\u7684\u5973\u5b50\u3002 \u3000\u3000\u4f0a\u98ce\u867d\u7136\u5bf9\u8427\u5357\u82f9\u4e5f\u6709\u7740\u4e00\u4e9b\u60c5\u611f\uff1b\u4f46\u4ed6\u4e5f\u81ea\u77e5\uff0c\u81ea\u5df1\u5bf9\u4eba\u5bb6\u7684\u60c5\u611f\uff0c\u8fdc\u4e0d\u5982\u4eba\u5bb6\u5bf9\u81ea \u5df1\u7684\u6d53\u539a\u3002\u4ed6\u5148\u524d\u867d\u7136\u53eb\u8427\u5357\u82f9\u5728\u59da\u6e05\u5b87\u5904\u7b49\u4ed6\uff0c\u4f46\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u786e\u77e5\u81ea\u5df1\u662f\u5426\u4f1a\u56de\u5230\u59da\u6e05 \u5b87\u5904\uff0c\u53bb\u5bfb\u627e\u8fd9\u7b49\u5f85\u7740\u81ea\u5df1\u7684\u75f4\u60c5\u800c\u7f8e\u4e3d\u7684\u5c11\u5973\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u4e2d\u6709\u7740\u6127\u610f\uff0c\u53e3\u4e2d\u4e5f\u5c31\u5450\u5450\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u534a\u664c\uff0c\u534e\u54c1\u5947\u5df2\u5fae\u5fae\u76b1\u7709\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u8198\u4e9b\u4e0a\u8def\u5427\uff01\u201d \u3000\u3000\u8427\u5357\u82f9\u6ee1\u542b\u55d4\u610f\u5730\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u54c0\u6028\u5730\u8f6c\u5411\u4f0a\u98ce\u3002 \u3000\u3000\u5979\u4e5f\u660e\u77e5\u81ea\u5df1\u73cd\u85cf\u4e86\u591a\u5e74\u7684\u60c5\u611f\uff0c\u6b64\u523b\u867d\u5df2\u627e\u5230\u4e86\u5f52\u4f9d\u4e4b\u5904\uff0c\u4f46\u8fd9\u5f52\u4f9d\u4e4b\u5904\uff0c\u504f\u53c8\u662f\u8fd9 \u4e48\u6e3a\u832b\uff0c\u6e3a\u832b\u5f97\u5c31\u50cf\u90a3\u5929\u672b\u7684\u4e91\u96fe\u4f3c\u7684\uff01 \u3000\u3000\u826f\u4e45\u2014\u2014 \u3000\u3000\u4ed6\u89c1\u4f0a\u98ce\u4ecd\u7136\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\uff0c\u5c11\u5973\u7684\u81ea\u5c0a\uff0c\u4f7f\u5f97\u5979\u7684\u5fc3\uff0c\u6bd4\u88ab\u4eba\u6233\u4e86\u5343\u4e07\u5200\u8fd8\u8981\u96be\u53d7\u3002 \u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u53ea\u89c9\u5f97\u8840\u6db2\u4e0a\u6d8c\uff0c\u773c\u524d\u4e5f\u53d8\u5f97\u6df7\u6df7\u6c8c\u6c8c\u7684\uff0c\u51e0\u4e4e\u8fde\u4f0a\u98ce\u7684\u5f71\u5b50\uff0c\u90fd\u5206\u8fa8\u4e0d\u51fa \u6765\u3002 \u3000\u3000\u4f0a\u98ce\u671b\u7740\u4ed6\u9762\u524d\u8fd9\u51c4\u695a\u7684\u5c11\u5973\uff0c\u4e5f\u88ab\u8fd9\u4efd\u771f\u60c5\u6240\u52a8\uff0c\u51e0\u4e4e\u613f\u610f\u653e\u4e0b\u4e00\u5207\uff0c\u548c\u8fd9\u7eaf\u60c5\u7684\u5c11 \u5973\uff0c\u8fdc\u8fdc\u8eb2\u5230\u5929\u6daf\u6d77\u89d2\uff0c\u8ba9\u4e16\u4eba\u518d\u4e5f\u5bfb\u627e\u4e0d\u7740\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u611f\u5230\u8fd9\u5c11\u5973\u7684\u771f\u60c5\uff0c\u662f\u8fd9\u4e48\u6c89\u91cd\uff0c\u6c89\u91cd\u5f97\u4f7f\u81ea\u5df1\u7684\u5fc3\uff0c\u90fd\u88ab\u538b\u7f29\u5f97\u6ca1\u6709\u4f59\u9699\u6765\u5bb9 \u7eb3\u522b\u7684\u611f\u89c9\u4e86\u3002 \u3000\u3000\u4ed6\u541e\u5410\u7740\uff0c\u6b63\u60f3\u8bf4\u8bdd\u3002 \u3000\u3000\u90a3\u77e5\u8427\u5357\u82f9\u7a81\u7136\u60b2\u9e23\u4e00\u58f0\uff0c\u53cc\u624b\u63a9\u9762\uff0c\u7ea4\u8170\u4e00\u8f6c\uff0c\u98de\u4e5f\u4f3c\u7684\u63a0\u4e86\u53bb\u3002 \u3000\u3000\u591c\u98ce\u5439\u5f97\u5979\u5bbd\u5927\u7684\u6587\u58eb\u8863\u895f\uff0c\u50cf\u662f\u4e00\u53ea\u8774\u8776\u7684\u5f69\u7ffc\u822c\uff0c\u5728\u4f0a\u98ce\u7684\u5fc3\u5e95\u9707\u52a8\u7740\u4e00\u79cd\u65e0\u6bd4\u548c \u8c10\uff0c\u4e5f\u5374\u662f\u65e0\u6b64\u51c4\u695a\u7684\u65cb\u5f8b\uff01 \u3000\u3000\u5979\u7ea4\u7ec6\u7684\u8eab\u5f71\uff0c\u7ec8\u4e8e\u5728\u82cd\u832b\u7684\u66ae\u8272\u4e2d\uff0c\u5189\u5189\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u5374\u50cf\u662f\u5c0a\u77f3\u50cf\u4f3c\u7684\uff0c\u7ad9\u5728\u4ed6\u5148\u524d\u6240\u7ad9\u7740\u7684\u5730\u65b9\uff0c\u52a8\u4e5f\u52a8\u5f39\u4e0d\u4e86\u4e00\u4e0b\u3002\u4ed6\u4e0d\u77e5\u4ed6\u81ea\u5df1 \u6b64\u65f6\u7684\u60c5\u611f\uff0c\u662f\u81ea\u8d23\uff0c\u6291\u6216\u662f\u81ea\u601c\uff01\u53ea\u662f\u4ed6\u5374\u89c9\u5f97\uff0c\u5929\u5730\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u7adf\u7a81\u7136\u5bc2\u5bde\u4e86\u8d77\u6765\uff01 \u3000\u3000\u4eba\u4eec\uff0c\u6709\u65f6\u662f\u6700\u611a\u8822\u7684\u52a8\u7269\uff0c\u5e38\u5e38\u4f1a\u4e3a\u7740\u4e00\u4e9b\u4e0d\u503c\u5f97\u73cd\u8d35\u7684\u4e8b\uff0c\u800c\u820d\u5f03\u4e86\u4e00\u4e9b\u6700\u6700\u73cd\u8d35 \u7684\u4e1c\u897f\u3002\u56e0\u4e3a\u5728\u4ed6\u4eab\u6709\u8fd9\u4e9b\u73cd\u8d35\u4e4b\u7269\u7684\u65f6\u5019\uff0c\u5e76\u6ca1\u6709\u611f\u89c9\u5230\u8fd9\u4e9b\u4e1c\u897f\u7684\u53ef\u8d35\u4e4b\u5904\uff0c\u4e5f\u4e0d\u53bb\u73cd \u60dc\u3002 \u3000\u3000\u800c\u7b49\u5230\u4ed6\u89c9\u5f97\u8fd9\u4e9b\u4e8b\u7269\u53ef\u8d35\uff0c\u518d\u60f3\u73cd\u60dc\u7684\u65f6\u5019\uff0c\u90a3\u4e9b\u4e8b\u7269\uff0c\u5374\u5df2\u79bb\u4ed6\u8fdc\u53bb\uff0c\u4ed6\u518d\u60f3\u53bb\u5bfb \u627e\uff0c\u4e5f\u5c06\u662f\u975e\u5e38\u56f0\u96be\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4f0a\u98ce\u611f\u89c9\u5230\u6709\u4eba\u8f7b\u8f7b\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u5934\uff0c\u4ed6\u56de\u5934\u53bb\u671b\uff0c\u534e\u54c1\u5947\u6b63\u5e26\u7740\u4e00\u79cd\u559f\u7136\u7684\u8868 \u60c5\u5728\u770b\u7740\u4ed6\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\uff01\u6211\u4eec\u8d70\u5427\uff01\u5e0c\u671b\u4eca\u665a\u80fd\u8d76\u5230\u957f\u5b89\uff0c\u6211\u6709\u8bb8\u591a\u8bdd\u8981\u95ee\u95ee\u4f60\u3002\u201d \u3000\u3000\u4f0a\u98ce\u9eef\u7136\u5730\u968f\u7740\u4ed6\u4eec\u4e0a\u4e86\u9a6c\uff0c\u5fc3\u91cc\u50cf\u662f\u503e\u5012\u4e86\u7684\u4e94\u5473\u74f6\uff0c\u9178\uff0c\u751c\uff0c\u82e6\uff0c\u8fa3\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5206 \u8fa8\u4e0d\u51fa\u5012\u5e95\u662f\u90a3\u4e00\u79cd\u60c5\u611f\uff01 \u3000\u3000\u9a6c\u8e44\u5954\u9a70\u7740\uff0c\u5728\u5d0e\u5c96\u7684\u9053\u8def\u4e0a\uff0c\u54cd\u8d77\u4e00\u8fde\u4e32\u4e86\u4eae\u7684\u8e44\u58f0\u3002 \u3000\u3000\u66ae\u8272\u6108\u91cd\u3002 \u3000\u3000\u4f0a\u98ce\u5750\u5728\u9a6c\u540e\uff0c\u4e24\u773c\u76f4\u89c6\u7740\uff0c\u8def\u65c1\u7684\u67af\u6728\uff0c\u50cf\u662f\u4e00\u6839\u6839\u8054\u63a5\u7740\u671d\u4ed6\u5934\u4e0a\u6253\u6765\u3002\u4ed6\u751a\u81f3\u4e5f \u613f\u610f\u4f38\u957f\u8116\u5b50\uff0c\u8ba9\u81ea\u5df1\u6df7\u4e71\u7684\u5934\u8111\uff0c\u91cd\u91cd\u6371\u4e0a\u4e00\u4e0b\u3002\u56e0\u4e3a\uff0c\u90a3\u81f3\u5c11\u53ef\u4ee5\u6362\u5f97\u7247\u523b\u7684\u5b89\u5b81\uff0c\u6c89 \u9189\u3002 \u3000\u3000\u4f46\u662f\uff0c\u90a3\u4e9b\u67af\u6728\u5374\u4e00\u6839\u6839\u5728\u4ed6\u8eab\u65c1\u64e6\u8fc7\u4e86\uff0c\u751a\u81f3\u8fde\u4ed6\u7684\u8863\u8882\u90fd\u6ca1\u6709\u6cbe\u4e0a\u4e00\u70b9\u3002\u8fd9\u4e00\u77ac \u95f4\uff0c\u4ed6\u4f3c\u4e4e\u53d1\u73b0\u4e86\u4e00\u4e9b\u54f2\u7406\u3002 \u3000\u3000\u90a3\u5c31\u662f\u4e16\u95f4\u6709\u8bb8\u591a\u4e8b\uff0c\u660e\u660e\u50cf\u662f\u5df2\u7ecf\u964d\u4e34\u5230\u4f60\u5934\u4e0a\uff0c\u4f46\u5374\u4ec5\u662f\u64e6\u8eab\u800c\u8fc7\uff1a\u800c\u53e6\u4e00\u4e9b\u4e8b\uff0c \u5374\u5728\u4f60\u6beb\u65e0\u6240\u89c9\u4e4b\u95f4\uff0c\u964d\u4e34\u5728\u4f60\u7684\u8eab\u4e0a\u3002\u800c\u8fd9\u4e9b\u90fd\u662f\u4f60\u6240\u65e0\u6cd5\u9884\u6d4b\u7684\u3002\u4eba\uff0c\u53c8\u6709\u8c01\u80fd\u591f\u771f\u7684 \u5148\u77e5\u5462\uff1f \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u81ea\u5df1\u6240\u60f3\u7684\uff0c\u662f\u5426\u5408\u4e8e\u5929\u7406\u7684\u8f68\u8ff9\uff01\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u5374\u56e0\u6b64\u800c\u5fae\u7b11\u4e86\u4e00\u4e0b\u3002\u62ac \u5934\u4e00\u671b\uff0c\u524d\u9762\u706f\u706b\u83b9\u83b9\uff0c\u50cf\u662f\u5df2\u5230\u4e86\u957f\u5b89\u4e86\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u4e94\u7ae0\u3000\u6f2b\u5929\u82b1\u5e02>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u4e94\u7ae0\u3000\u6f2b\u5929\u82b1\u5e02 \u3000\u3000\u8427\u5357\u82f9\u8fd9\u75f4\u60c5\u7684\u5c11\u5973\uff0c\u5df2\u5b8c\u5168\u5931\u843d\u5728\u60c5\u611f\u7684\u8ff7\u96fe\u91cc\u4e86\u3002 \u3000\u3000\u5979\u662f\u90a3\u4e48\u51c4\u695a\u800c\u4f24\u5fc3\uff0c\u56e0\u4e3a\u5979\u53d1\u73b0\u5979\u81ea\u5df1\u6240\u6df1\u7231\u7740\u7684\u90a3\u4eba\uff0c\u5bf9\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u8fdc\u4e0d\u5982\u81ea\u5df1 \u5bf9\u4ed6\u7684\u5343\u4e07\u5206\u4e4b\u4e00\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u540e\u6094\u81ea\u5df1\u5bf9\u4ed6\u4ed8\u51fa\u90a3\u4e48\u6d53\u539a\u7684\u60c5\u611f\u3002\u8fd9\u662f\u5979\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u4ed8\u51fa\u7684\u60c5\u611f\uff0c\u7136\u800c\uff0c \u5979\u5374\u4e0d\u5f97\u4e0d\u4f24\u5fc3\u4ed6\u5bf9\u81ea\u5df1\u7684\u65e0\u60c5\u3002 \u3000\u3000\u5728\u7ecf\u8fc7\u4e00\u9635\u75af\u72c2\u7684\u5954\u9a70\u4e4b\u540e\uff0c\u6b64\u523b\u5979\u89c9\u5f97\u81ea\u5df1\u5fc3\u80f8\u95f4\uff0c\u6709\u4e00\u79cd\u8981\u5455\u5410\u7684\u611f\u89c9\uff1b\u56e0\u4e3a\u65b9\u624d \u90a3\u9635\u5954\u9a70\uff0c\u5df2\u8d85\u8d8a\u5979\u81ea\u8eab\u529f\u529b\u6240\u80fd\u8fbe\u5230\u7684\u9650\u5ea6\u4e4b\u5916\u3002\u8fd9\u5f53\u7136\u662f\u5979\u60f3\u85c9\u6b64\u6765\u5fd8\u5374\u5fc3\u7075\u7684\u75db\u82e6\u3002 \u3000\u3000\u7136\u800c\uff0c\u5979\u6b64\u523b\u5374\u5931\u671b\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u79cd\u5176\u6df1\u5165\u9aa8\u7684\u75db\u82e6\u4e0e\u81ea\u601c\uff0c\u5e76\u6ca1\u6709\u56e0\u4e3a\u8fd9\u8089\u4f53\u7684\u6298\u78e8\uff0c\u800c\u6709\u6240\u51cf\u8f7b\uff0c\u751a\u81f3\u66f4\u52a0\u91cd \u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u5979\u53ea\u5f97\u653e\u7f13\u4e86\u811a\u6b65\uff0c\u8ff7\u60d8\u800c\u65e0\u52a9\u5730\uff0c\u8e2f\u8e85\u5728\u65e0\u4eba\u7684\u8352\u5f84\u91cc\u3002 \u3000\u3000\u5979\uff0c\u4e0d\u4f46\u5df2\u8ff7\u5931\u4e86\u81ea\u5df1\uff1b\u800c\u4e14\uff0c\u4e5f\u5df2\u8ff7\u5931\u4e86\u9053\u8def\u7684\u65b9\u5411\u4e86\u3002 \u3000\u3000\u201c\u8be5\u5230\u90a3\u91cc\u53bb\u5462\uff1f\u201d\u5979\u832b\u7136\u73af\u987e\u56db\u5468\uff0c\u56db\u5468\u662f\u5df2\u6ca6\u4e8e\u591c\u8272\u4e4b\u4e2d\u7684\u6797\u91ce\u548c\u5c71\u9e93\u3002 \u3000\u3000\u5979\u7684\u5fc3\uff0c\u4e5f\u6b63\u5982\u56db\u5468\u822c\u5730\u9edd\u9ed1\u800c\u5bc2\u5bde\u3002 \u3000\u3000\u5bc2\u5bde\u7684\u56db\u5468\uff0c\u5bf9\u4e8e\u4e00\u4e2a\u4f24\u5fc3\u7684\u4eba\u6765\u8bf4\uff0c\u4e0d\u662f\u500d\u89c9\u51c4\u51c9\u5417\uff01 \u3000\u3000\u5979\u4e0d\u662f\u4e00\u4e2a\u8f6f\u5f31\u7684\u5973\u5b50\uff0c\u4e5f\u4e0d\u60ef\u4e8e\u5411\u522b\u4eba\u4e5e\u6c42\u60c5\u611f\u3002\u8fd9\u4ece\u5979\u4ee5\u5f80\u7684\u4e8b\u60c5\u4e0a\uff0c\u5c31\u53ef\u4ee5\u5f88\u663e \u7136\u5730\u770b\u51fa\u6765\u3002 \u3000\u3000\u5979\u66fe\u7ecf\u6298\u78e8\u8fc7\u65e0\u6570\u6df1\u7231\u7740\u5979\u7684\u7537\u4eba\u7684\u5fc3\uff1b\u800c\u6b64\u523b\uff0c\u5f53\u5979\u4e5f\u6b63\u6df1\u7231\u7740\u4e00\u4e2a\u7537\u4eba\u7684\u65f6\u5019\uff0c\u5979 \u7684\u5fc3\uff0c\u5374\u88ab\u8fd9\u7537\u4eba\u6298\u78e8\u4e86\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u6000\u6068\u4f0a\u98ce\uff0c\u53ea\u662f\u4e3a\u81ea\u5df1\u4f24\u5fc3\u3002\u4f24\u5fc3\u4e4b\u4e2d\uff0c\u53c8\u6709\u4e9b\u540e\u6094\uff0c\u540e\u6094\u5979\u4ee5\u524d\u4e3a\u4ec0\u4e48\u8981\u90a3\u6837 \u5bf9\u4ed8\u90a3\u4e9b\u6df1\u7231\u7740\u81ea\u5df1\u7684\u4eba\u4eec\uff01 \u3000\u3000\u591c\u8272\u82cd\u832b\u3002 \u3000\u3000\u82cd\u832b\u7684\u591c\u8272\u91cc\uff0c\u5979\u542c\u5230\u6709\u4e00\u8fde\u4e32\u4f4e\u6c89\u7684\u4eba\u8bed\u58f0\uff0c\u50cf\u662f\u5728\u4e3a\u67d0\u4e00\u4ef6\u4e8b\u4e89\u6267\u7740\u3002\u4e8e\u662f\u5979\u7acb\u523b \u5c06\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4eba\u8bed\u4e4b\u58f0\uff0c\u8d8a\u6765\u8d8a\u5927\uff0c\u90a3\u662f\u4ece\u5979\u8eab\u4fa7\u7684\u4e00\u4e2a\u8352\u6797\u91cc\u4f20\u51fa\u7684 \u3000\u3000\u201c\u8c22\u9999\u4e3b\uff01\u4e0d\u662f\u5c0f\u5f1f\u4e0d\u4fe1\u4efb\u4f60\uff0c\u4f46\u662f\u6559\u4e3b\u660e\u660e\u5df2\u53bb\u6ec7\u4e2d\uff0c\u4e34\u884c\u4e4b\u9645\uff0c\u8fd8\u544a\u8bc9\u8fc7\u5c0f\u5f1f\uff0c\u8bf4 \u662f\u636e\u95fb\u6614\u5e74\u7684\u201c\u5357\u5077\u5317\u76d7\u201d\uff0c\u5e76\u6ca1\u6709\u5f52\u9690\u6216\u662f\u6b7b\u53bb\uff0c\u800c\u662f\u5728\u6ec7\u4e2d\u65e0\u91cf\u5c71\u91cc\uff0c\u4e89\u593a\u7740\u4e00\u4ef6\u7a00\u4e16 \u7684\u73cd\u5b9d\u3002\u6559\u4e3b\u6b64\u53bb\uff0c\u4e5f\u5c31\u662f\u4e3a\u7740\u8fd9\u4ef6\u4e8b\u7684\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u97e6\u9999\u4e3b\uff01\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u96be\u9053\u6211\u8c22\u96e8\u4ed9\u8fd8\u4e0d\u60f3\u5f53\u7ec8\u5357\u638c\u6559\uff1f\u96be\u9053\u6211\u8fd8\u4f1a\u6545\u610f\u634f\u9020 \u8fd9\u4e9b\u4e8b\u6765\u9a97\u4f60\uff1f\u6559\u4e3b\u5728\u7384\u5999\u89c2\u91cc\u73b0\u8eab\uff0c\u80e1\u9999\u4e3b\u4ed6\u4eec\u90fd\u662f\u4eb2\u773c\u76ee\u7779\u7684\uff0c\u53c8\u4e0d\u662f\u53ea\u6709\u6211\u4e00\u4e2a\u4eba\u770b \u5230\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u5bf9\u8bdd\uff0c\u65ad\u65ad\u7eed\u7eed\u4f20\u5165\u8427\u5357\u82f9\u8033\u91cc\uff0c\u5979\u5fc3\u91cc\u867d\u7136\u8ff7\u4e71\uff0c\u53ef\u4e5f\u4e0d\u7531\u84e6\u5730\u4e00\u60ca\u3002\u77e5\u9053\u5728\u8fd9 \u6811\u6797\u91cc\u8bb2\u8bdd\uff0c\u6b63\u662f\u5148\u524d\u5728\u7ec8\u5357\u5c71\u4e0a\uff0c\u4e89\u593a\u7ec8\u5357\u638c\u6559\u7684\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\uff1b\u53e6\u5916\u4e00\u4eba\uff0c\u60f3\u5fc5\u4e5f\u662f \u5929\u4e89\u6559\u4e0b\u7684\u9999\u4e3b\u3002 \u3000\u3000\u5979\u5403\u60ca\u7684\u5012\u4e0d\u662f\u8fd9\u4e9b\uff0c\u800c\u662f\u4ece\u4ed6\u4eec\u6240\u8bf4\u7684\u8bdd\u4e2d\uff0c\u53ef\u4ee5\u542c\u51fa\u4f0a\u98ce\u6613\u5bb9\u4e4b\u540e\uff0c\u9762\u8c8c\u7adf\u7136\u662f\u548c \u5929\u4e89\u6559\u4e3b\u8427\u65e0\u76f8\u540c\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u7684\u5de7\u5408\u4e4b\u5947\uff0c\u8fde\u5979\u81ea\u5df1\u90fd\u4e0d\u80fd\u76f8\u4fe1\u3002\u4f46\u6b64\u523b\u8a00\u8bc1\u786e\u51ff\uff0c\u4f3c\u4e4e\u5df2\u662f\u5343\u771f\u4e07\u786e\u7684\u4e86\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u6781\u5feb\u5730\u8f6c\u4e86\u51e0\u4e0b\uff0c\u4e0d\u77e5\u9053\u81ea\u5df1\u6b64\u523b\u7a76\u7adf\u8be5\u5c06\u91c7\u53d6\u4ec0\u4e48\u6b65\u9aa4\uff01 \u3000\u3000\u6811\u6797\u91cc\u7684\u4e24\u4eba\uff0c\u50cf\u662f\u8bdd\u4e0d\u6295\u673a\uff0c\u6b64\u523b\u5df2\u4e0d\u518d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5979\u9edb\u7709\u5fae\u76b1\uff0c\u7ea4\u8170\u4e00\u626d\uff0c\u60f3\u5148\u907f\u5f00\u6b64\u5730\uff0c\u514d\u5f97\u751f\u4e9b\u9ebb\u70e6\u3002 \u3000\u3000\u90a3\u77e5\u5979\u65b9\u4e00\u5c55\u52a8\u8eab\u5f62\uff0c\u6811\u6797\u91cc\u5df2\u84e6\u7136\u4f20\u51fa\u4e24\u58f0\u66b4\u559d\uff1a \u3000\u3000\u201c\u662f\u8c01.\uff1f\u201d \u3000\u3000\u4e24\u6761\u4eba\u5f71\uff0c\u4e5f\u968f\u7740\u8fd9\u66b4\u559d\u4e4b\u58f0\uff0c\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u8427\u5357\u82f9\u65b9\u624d\u5954\u9a70\u8fc7\u5ea6\uff0c\u6b64\u523b\u771f\u529b\u4ecd\u672a\u56de\u590d\uff01\u773c\u89d2\u77ac\u5904\uff0c\u671b\u89c1\u90a3\u4e24\u4eba\u7684\u8eab\u6cd5\uff0c\u8f7b\u7075\u6025\u5feb\uff0c \u8f7b\u529f\u5728\u6b66\u6797\u4e2d\uff0c\u5df2\u662f\u4e00\u6d41\u9ad8\u624b\u3002 \u3000\u3000\u4f55\u51b5\u5979\u6b64\u523b\u5fc3\u4e2d\u52a8\u5ff5\uff0c\u81ea\u5df1\u548c\u5929\u4e89\u6559\u7d20\u65e0\u4ec7\u6028\uff0c\u4e5f\u72af\u4e0d\u4e0a\u53bb\u9003\u907f\u4eba\u5bb6\u3002\u5229\u5bb3\u7684\u6743\u8861\u4e4b \u4e0b\uff0c\u5979\u65b9\u60f3\u505c\u4f4f\u81ea\u5df1\u7684\u8eab\u5f62\u3002 \u3000\u3000\u90a3\u77e5\u8eab\u540e\u53c8\u5df2\u559d\u9053\uff1a \u3000\u3000\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u518d\u4e0d\u505c\u4f4f\u8eab\u5f62\uff0c\u6211\u8c22\u771f\u4eba\uff0c\u5c31\u771f\u8981\u6559\u8bad\u6559\u8bad\u4f60\u4e86\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u51b7\u7b11\u4e00\u58f0\u3002 \u3000\u3000\u6f47\u6e58\u5983\u5b50\u5728\u6b66\u6797\u4e2d\u6709\u540d\u7684\u5fc3\u9ad8\u6c14\u50b2\uff0c\u6b64\u523b\u5fc3\u60c5\u672c\u574f\uff0c\u5728\u8fd9\u79cd\u5389\u53f1\u4e4b\u4e0b\uff0c\u4e0d\u7981\u6c14\u5f80\u4e0a\u51b2\u3002 \u3000\u3000\u5979\u53cc\u81c2\u5fae\u5f20\uff0c\u5728\u7a7a\u4e2d\u5fae\u626d\u8f6c\u8170\uff0c\u786c\u751f\u751f\u5c06\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u8f6c\u53d8\u4e86\u4e00\u4e2a\u65b9\u5411\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5c31\u5728\u5979\u8fd9\u5fae\u4e00\u8f6c\u6298\u4e4b\u95f4\uff0c\u5df2\u6709\u51e0\u7f15\u5c16\u98ce\uff0c\u5411\u5979\u88ad\u6765\u3002 \u3000\u3000\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u8fd9\u51e0\u7f15\u5c16\u98ce\u95ea\u7740\u4e4c\u5149\uff0c\u98ce\u58f0\u51cc\u5389\uff0c\u6765\u52bf\u6781\u901f\uff0c\u800c\u4e14\u53d1\u6697\u5668\u7684\u90e8\u4f4d\uff0c\u6781\u4e3a\u5201 \u94bb\u9738\u9053\uff0c\u4e24\u88ad\u524d\u80f8\uff0c\u4e00\u51fb\u9762\u95e8\uff0c\u5374\u53c8\u6709\u4e24\u70b9\u5bd2\u5149\uff0c\u662f\u6253\u5411\u5979\u8eab\u4fa7\u4e24\u8fb9\u7684\u7a7a\u95f4\u3002\u8fd9\u4e00\u6765\uff0c\u8427\u5357 \u82f9\u65e0\u8bba\u4e0a\u62d4\uff0c\u659c\u63a0\uff0c\u53ef\u90fd\u5728\u4ed6\u7684\u6697\u5668\u63a7\u5236\u4e4b\u4e0b\u3002 \u3000\u3000\u4e00\u8fd9\u79cd\u53d1\u6697\u5668\u7684\u624b\u6cd5\u663e\u89c1\u5f97\u662f\u6781\u4e3a\u9ad8\u660e\u3002\u800c\u4e14\u8fd9\u6697\u5668\u53d1\u7740\u4e4c\u5149\uff0c\u65e0\u7591\u5730\u4e0a\u9762\u5df2\u6709\u6781\u5389\u5bb3 \u7684\u6bd2\u836f\u3002\u90a3\u53d1\u6697\u5668\u7684\u4eba\uff0c\u5728\u52a8\u624b\u4e4b\u5148\uff0c\u7adf\u6ca1\u6709\u5148\u559d\u58f0\u201c\u6253\u201d\uff0c\u53ef\u89c1\u4ed6\u5fc3\u72e0\u624b\u8fa3\uff0c\u5bf9\u4e00\u4e2a\u672a\u5206 \u654c\u53cb\u7684\u4eba\uff0c\u5c31\u65bd\u51fa\u8fd9\u79cd\u8fa3\u624b\u6765\uff0c\u8fde\u6c5f\u6e56\u89c4\u77e9\uff0c\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ee5\u6697\u5668\u4e00\u9053\u6765\u8bb2\uff0c\u6614\u5e74\u201c\u8427\u4e09\u7237\u201d\uff0c\u53ef\u8bf4\u5f97\u4e0a\u662f\u9876\u513f\u5c16\u513f\u7684\u9ad8\u624b\u3002\u8427\u5357\u82f9\u5bb6\u5b66\u6e0a \u6e90\uff0c\u6697\u5668\u4e00\u95e8\u529f\u592b\uff0c\u4e5f\u662f\u65e9\u5c31\u95fb\u540d\u6c5f\u6e56\u7684\u3002 \u3000\u3000\u6b64\u523b\u5979\u867d\u7136\u8eab\u5f62\u521a\u521a\u8f6c\u56de\u6765\uff0c\u53ef\u662f\u5149\u4ece\u8fd9\u6697\u5668\u7684\u98ce\u58f0\uff0c\u5979\u5df2\u7ecf\u77e5\u9053\u4e86\u8fd9\u4e9b\u6697\u5668\u88ad\u6765\u7684\u90e8 \u4f4d\u3002 \u3000\u3000\u5f53\u4e0b\u5979\u518d\u4e00\u63d0\u6c14\uff0c\u8eab\u5f62\u201c\u5237\u201d\u5730\uff0c\u671d\u540e\u9762\u5012\u7eb5\u56de\u53bb\u3002\u7b49\u5230\u8fd9\u51e0\u70b9\u6697\u5668\uff0c\u5df2\u6210\u5f3a\u5f29\u4e4b\u672b\uff0c \u5979\u518d\u5fae\u9519\u811a\u6b65\uff0c\u53cc\u638c\u53cd\u6325\uff0c\u88ad\u5411\u5979\u8eab\u4e0a\u7684\u4e09\u9053\u4e4c\u5149\uff0c\u5c31\u5168\u90fd\u88ab\u8f7b\u63cf\u6de1\u5199\u7684\u51fb\u843d\u4e86\u3002\u53e6\u5916\u4e24\u70b9 \u6697\u5668\uff0c\u672c\u6765\u5c31\u4e0d\u662f\u671d\u5979\u8eab\u4e0a\u62db\u547c\uff0c\u5979\u8eab\u5b50\u6ca1\u6709\u5de6\u53f3\u63a0\u52a8\uff0c\u6b64\u523b\u81ea\u7136\u4e5f\u5168\u843d\u4e86\u7a7a\u3002 \u3000\u3000\u53d1\u6697\u5668\u7684\u4eba\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u7136\u5c31\u662f\u90a3\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u4e86\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u5389\u559d\u9053\uff1a\u201c\u597d\u670b\u53cb\uff01\u6709\u4e24\u4e0b\u5b50\uff0c\u518d\u63a5\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u53cc\u624b\u8fde\u626c\uff0c\u55d6\uff0c\u55d6\uff0c\u7adf\u53c8\u662f\u5341\u51e0\u9053\u4e4c\u5149\uff0c\u4ece\u4ed6\u638c\u4e2d\u6325\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8c22\u96e8\u4ed9\u638c\u4e2d\u6240\u53d1\u51fa\u53bb\u7684\u6697\u5668\uff0c\u6b63\u662f\u6c5f\u6e56\u95fb\u540d\u800c\u8272\u53d8\u7684\u201c\u4e94\u86c7\u9aa8\u9488\u201d\u3002 \u3000\u3000\u8fd9\u79cd\u6697\u5668\uff0c\u5168\u662f\u4ee5\u6bd2\u86c7\u7684\u9aa8\u9abc\uff0c\u518d\u6d78\u4ee5\u6781\u5389\u5bb3\u7684\u6bd2\u836f\u5236\u6210\u7684\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u5b50\u4e0d\u89c1\u5348\uff0c\u5348 \u4e0d\u89c1\u5b50\uff0c\u53ea\u8981\u88ab\u8fd9\u6697\u5668\u7a0d\u4e3a\u5212\u7834\u4e00\u70b9\u76ae\u8089\uff0c\u4e0d\u5230\u4e00\u4e2a\u5bf9\u65f6\uff0c\u4fbf\u5f97\u545c\u547c\uff0c\u53ef\u8c13\u9738\u9053\u5df2\u6781\uff01 \u3000\u3000\u800c\u4ed6\u53d1\u6697\u5668\u7684\u624b\u6cd5\uff0c\u7adf\u662f\u53cc\u624b\u201c\u6f2b\u5929\u82b1\u96e8\u201d\u3002\u8fd9\u79cd\u624b\u6cd5\uff0c\u5728\u6b66\u6797\u4e2d\u53ef\u79f0\u5f97\u4e0a\u662f\u4e00\u7edd\uff0c\u4e0d \u7136\uff0c\u8c22\u96e8\u4ed9\u600e\u4f1a\u4ee5\u201c\u591a\u624b\u771f\u4eba\u201d\u540d\u6ee1\u5929\u4e0b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u5374\u60f3\u4e0d\u5230\uff0c\u81ea\u5df1\u6b64\u523b\u6240\u9047\u7740\u7684\uff0c\u4e5f\u662f\u6697\u5668\u4e2d\u7684\u5927\u884c\u5bb6\u3002 \u3000\u3000\u8427\u5357\u82f9\u5728\u7a0d\u4e00\u5598\u6c14\u4e4b\u540e\uff0c\u638c\u4e2d\u4e5f\u5df2\u51c6\u5907\u597d\u4e86\u4e00\u638c\u201c\u4e94\u832b\u73e0\u201d\u3002 \u3000\u3000\u6697\u5668\u4e4b\u4e2d\uff0c\u201c\u4e94\u832b\u73e0\u201d\u53ef\u7b97\u5f97\u4e0a\u662f\u6781\u4e3a\u5149\u660e\u6b63\u5927\u7684\u4e00\u79cd\u3002 \u3000\u3000\u53ef\u8d8a\u662f\u8fd9\u79cd\u5149\u660e\u6b63\u5927\u7684\u6697\u5668\uff0c\u5728\u540d\u5bb6\u624b\u4e2d\uff0c\u5a01\u529b\u4e5f\u662f\u8d8a\u4e3a\u60ca\u4eba\u3002 \u3000\u3000\u6b64\u523b\u5979\u7ea4\u638c\u5fae\u626c\uff0c\u4e03\u9053\u94f6\u5149\uff0c\uff0e\u4fbf\u5e26\u7740\u8f7b\u5fae\u7684\u5578\u58f0\uff0c\u5411\u8c22\u96e8\u4ed9\u6240\u53d1\u51fa\u7684\u5341\u51e0\u9053\u4e4c\u5149\u8fce \u53bb\u3002\u800c\u5979\u7684\u8eab\u5f62\uff0c\u4e5f\u5728\u8fd9\u4e00\u626c\u624b\u4e4b\u95f4\uff0c\u500f\u7136\u6ed1\u51fa\u516d\u5c3a\u3002 \u3000\u3000\u201c\u53ee\u5f53\u201d\u51e0\u58f0\u5fae\u54cd\uff0c\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u7684\u4e4c\u5149\uff0c\u4fbf\u5df2\u88ab\u51fb\u843d\u4e86\u4e00\u534a\u3002\u53ef\u662f\u90a3\u4e03\u9053\u94f6\u5149\uff0c\u52bf \u5b50\u4ecd\u672a\u51cf\u5f31\uff0c\u4ecd\u7136\u5e26\u7740\u5578\u58f0\u51fb\u5411\u8c22\u96e8\u4ed9\u3002\u663e\u7136\u53ef\u89c1\uff0c\u53d1\u51fa\u8fd9\u4e03\u9053\u94f6\u5149\u7684\u529b\u9053\uff0c\u662f\u6781\u4e3a\u60ca\u4eba \u7684\uff01 \u3000\u3000\u7b14\u4e0b\u5199\u6765\u81ea\u6162\uff0c\u7136\u800c\u8fd9\u4e9b\u4e8b\u5374\u53ea\u4e0d\u8fc7\u662f\u4e00\u77ac\u95f4\u7684\u529f\u592b\u3002 \u3000\u3000\u8c22\u96e8\u4ed9\u7709\u5934\u5fae\u76b1\uff0c\u5de6\u53f3\u817e\u632a\uff0c\u907f\u5f00\u8fd9\u51e0\u9053\u94f6\u5149\u3002\u94c1\u638c\u5fae\u6284\uff0c\u53c8\u5c06\u4e00\u7c92\u201c\u4e94\u832b\u73e0\u201d\u6284\u5728\u624b \u91cc\uff0c\u76ee\u5149\u5fae\u95ea\uff0c\u4e0d\u7981\u5389\u559d\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\u4e14\u4f4f\u624b\uff01\u4eae\u4e2a\u4e07\u513f\uff0c\u82e5\u662f\u201c\u8427\u4e09\u7237\u201d\u4ec0\u4e48\u4eba\uff0c\u6211\u59d3\u8c22\u7684\u53ef\u5f97\u5356\u4e2a\u4ea4\u60c5\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u51b7\u7b11\u4e00\u58f0\uff0c\u77e5\u9053\u8fd9\u8c22\u96e8\u4ed9\u5df2\u8ba4\u51fa\u81ea\u5df1\u7684\u7239\u7239\u6614\u5e74\u540d\u9707\u6b66\u6797\u7684\u6697\u5668\u3002\u4e24\u9053\u7ec6\u957f\u7684\u67f3 \u7709\u4e00\u5c55\uff0c\u51b7\u7b11\u7740\u5389\u58f0\u559d\u9053\uff1a \u3000\u3000\u201c\u8c01\u8981\u4f60\u5356\u4ea4\u60c5\uff1f\u201d \u3000\u3000\u53cc\u624b\u518d\u626c\uff0c\u5de6\u53f3\u53cc\u638c\uff0c\u7adf\u4e5f\u662f\u4f7f\u51fa\u6697\u5668\u4e2d\u7684\u7edd\u5b66\u201c\u6f2b\u5929\u82b1\u96e8\u201d\uff0c\u5fae\u5578\u58f0\u4e2d\uff0c\u53c8\u662f\u5341\u4f59\u9053 \u94f6\u5149\u7535\u5c04\u800c\u51fa\uff0c\u671d\u591a\u624b\u771f\u4eba\u88ad\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u5c31\u5728\u8fd9\u5341\u4f59\u7c92\u201c\u4e94\u832b\u73e0\u201d\u5df2\u5c06\u5230\u8fbe\u8c22\u96e8\u4ed9\u8eab\u524d\u7684\u65f6\u5019\uff0c\u7a81\u5730\u53c8\u6709\u4e00\u58f0\u8f7b\u559d\uff0c\u8c22\u96e8\u4ed9 \u8eab\u4e0a\uff0c\u7adf\u751f\u662f\u7a81\u7136\u98de\u6765\u4e00\u7247\u91d1\u5899\uff0c\u8fce\u7740\u90a3\u5341\u4f59\u7c92\u201c\u4e94\u832b\u73e0\u201d\u4e00\u6321\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u53ee\u5f53\u201d\u51e0\u58f0 \u8f7b\u54cd\u3002 \u3000\u3000\u63a5\u7740\uff0c\u90a3\u9053\u91d1\u5899\u5374\u53c8\u53cd\u5377\u4e86\u56de\u53bb\uff0c\u800c\u90a3\u5341\u51e0\u7c92\u201c\u4e94\u832b\u73e0\u201d\uff0c\u5374\u4e5f\u5c31\u65e0\u5f71\u65e0\u8e2a\u4e86\u3002 \u3000\u3000\u8427\u5357\u82f9\u4e0d\u7981\u5fae\u53d8\u8138\u8272\uff0c\u76ee\u5149\u77ac\u5904\uff0c\u539f\u6765\u5728\u90a3\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u8eab\u4fa7\uff0c\u7ad9\u7740\u4e00\u4e2a\u77ee\u80d6\u7684\u91d1\u8863 \u4eba\uff0c\u624b\u91cc\u5782\u7740\u4e00\u7247\u7f51\u72b6\u7684\u4e1c\u897f\uff0c\u800c\u90a3\u5341\u4f59\u7c92\u529b\u9053\u5f3a\u52b2\u7684\u201c\u4e94\u832b\u73e0\u201d\uff0c\u4fbf\u662f\u88ab\u8fd9\u7f51\u888b\u7684\u4e1c\u897f\u6536 \u4e86\u53bb\u3002 \u3000\u3000\u8427\u5357\u82f9\u6697\u4e2d\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff01\u5979\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u5374\u662f\u4e2a\u8001\u6c5f\u6e56\u4e86\u3002\u6b64\u65f6\u5979\u4ece\u90a3\u77ee\u80d6\u7684\u91d1\u8863\u4eba \u624b\u91cc\u62ff\u7740\u7684\u90a3\u4e1c\u897f\u4e0a\uff0c\u4fbf\u5df2\u731c\u51fa\u6b64\u4eba\u7684\u6765\u5386\u3002 \u3000\u3000\u201c\u6b64\u4eba\u83ab\u975e\u662f\u97e6\u50b2\u7269\u3002\u201d \u3000\u3000\u539f\u6765\u6b66\u6797\u4e4b\u4e2d\uff0c\u51e1\u662f\u4f7f\u6697\u5668\u7684\u4eba\uff0c\u83ab\u4e0d\u6015\u9047\u7740\u201c\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u201d\u3002\u8fd9\u56e0\u4e3a\u6b64\u4eba\u6240\u4f7f\u7684 \u5175\u5668\uff0c\u602a\u9053\u5df2\u6781\uff0c\u7adf\u662f\u4e00\u9762\u6e14\u7f51\u3002 \u3000\u3000\u8fd9\u9762\u6e14\u7f51\uff0c\u53ef\u4e0d\u662f\u666e\u901a\u7684\u6e14\u7f51\uff0c\u800c\u662f\u4ee5\u4e00\u79cd\u5947\u5f02\u7684\u91d1\u5c5e\u6400\u5408\u7740\u4e4c\u91d1\u6253\u9020\u7684\u91d1\u4e1d\u7f16\u6210\u7684\u3002 \u4e0d\u4f46\u4e13\u7834\u5929\u4e0b\u5404\u95e8\u5404\u6d3e\u7684\u6697\u5668\uff0c\u800c\u4e14\u62db\u5f0f\u81ea\u6210\u4e00\u5bb6\u3002\u8fd9\u4e03\u6d77\u6e14\u5b50\u7684\u201c\u4e07\u513f\u201d\uff0c\u4e5f\u56e0\u4e4b\u5728\u6b66\u6797 \u4e2d\u53eb\u5f97\u6781\u54cd\u3002 \u3000\u3000\u666e\u5929\u4e4b\u4e0b\uff0c\u4f7f\u8fd9\u79cd\u602a\u5f02\u7684\u5175\u5668\uff0c\u53ea\u6709\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u4e00\u4eba\uff1b\u800c\u666e\u5929\u4e4b\u4e0b\uff0c\u4f7f\u6697\u5668\u7684\u4eba\uff0c \u4e5f\u83ab\u4e0d\u77e5\u9053\u6709\u7740\u8fd9\u4e48\u4e00\u4f4d\u4eba\u7269\u3002 \u3000\u3000\u8427\u5357\u82f9\u4e00\u89c1\u6b64\u4eba\u624b\u4e2d\u7684\u91d1\u7f51\uff0c\u518d\u52a0\u4e0a\u4eba\u5bb6\u65b9\u624d\u7834\u53bb\u81ea\u5df1\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5fc3\u91cc\u518d\u65e0\u7591\u95f4\uff0c\u8fd9 \u4e2a\u77ee\u80d6\u7684\u91d1\u8863\u6c49\u5b50\uff0c\u4fbf\u662f\u540d\u9707\u6b66\u6797\u7684\u4eba\u7269\u4e4b\u4e00\u2014\u2014\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\uff0c\u5fc3\u4e2d\u5403\u60ca\u4e4b\u4e0b\uff0c\u53c8\u4e0d\u7981 \u5947\u602a\uff01 \u3000\u3000\u201c\u8fd9\u97e6\u50b2\u7269\u4e00\u5411\u72ec\u5f80\u72ec\u6765\uff0c\u6b64\u523b\u600e\u7684\u4e5f\u5165\u4e86\u5929\u4e89\u6559\u4e0b\uff1f\u201d \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u516d\u7ae0\u3000\u4e03\u6d77\u6e14\u5b50>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u516d\u7ae0\u3000\u4e03\u6d77\u6e14\u5b50 \u3000\u3000\u8fd9\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u97e6\u50b2\u7269\u53f3\u624b\u4e00\u6296\uff0c\u5c06\u7f51\u91cc\u7684\u201c\u4e94\u832b\u73e0\u201d\u5168\u90fd\u6296\u843d\u5728\u5730\u4e0a\uff0c\u54c8\u54c8\u4e00\u7b11\u9053 \u3000\u3000\u201c\u670b\u53cb\u662f\u9ed1\u9053\u8fd8\u662f\u767d\u9053\u7684\uff1f\u662f\u4e0d\u662f\u201c\u8427\u4e09\u7237\u201d\u7684\u95e8\u4e0b.\u4e0d\u59a8\u5148\u4eae\u4e2a\u201c\u4e07\u513f\u201d\u3002\u670b\u53cb\uff0c\u9ed1 \u591c\u91cc\u7a83\u542c\u6211\u5144\u5f1f\u4eec\u7684\u8c08\u8bdd\uff0c\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u51b2\u7740\u4ec0\u4e48\u6765\u7684\uff0c\u4e5f\u8bf7\u544a\u8bc9\u6211\u59d3\u97e6\u7684\u4e00\u58f0\uff0c\u97e6\u67d0\u867d\u4e0d \u624d\uff0c\u4f46\u597d\u6b79\u4e5f\u5f97\u7ed9\u670b\u53cb\u4e00\u4e2a\u4ea4\u5f85\u3002\u4f46\u670b\u53cb\u82e5\u8fd9\u4e48\u85cf\u5934\u9732\u5c3e\u7684\uff0c\u53ef\u5c31\u663e\u5f97\u6709\u70b9\u4e0d\u591f\u4ea4\u60c5\u5566\uff0c\u90a3 \u5c31\u522b\u602a\u97e6\u67d0\u4e5f\u4e0d\u591f\u670b\u53cb\u3002\u201d \u3000\u3000\u8fd9\u97e6\u50b2\u7269\u7b11\u5bb9\u6ee1\u9762\uff0c\u4f46\u8bb2\u51fa\u6765\u7684\u8bdd\uff0c\u53ef\u662f\u53e5\u53e5\u90fd\u5e26\u7740\u6781\u91cd\u7684\u4efd\u91cf\uff01 \u3000\u3000\u8427\u5357\u82f9\u5fc3\u91cc\u867d\u5df2\u6709\u4e86\u602f\u610f\uff0c\u4f46\u53e3\u5934\u4e0a\u4ecd\u4e0d\u80af\u793a\u5f31\uff0c\u4e5f\u51b7\u7b11\u4e00\u58f0\u9053 \u3000\u3000\u201c\u5929\u4e0b\u8def\u5929\u4e0b\u4eba\u8d70\u5f97\uff0c\u8fd9\u6761\u9053\u53c8\u4e0d\u662f\u4f60\u4eec\u4e70\u4e0b\u6765\u7684\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8d70\u3002\u201d \u3000\u3000\u5979\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u662f\u8d70\u9053\u7684\uff0c\u8c01\u8981\u5077\u542c\u4f60\u4eec\u8c08\u8bdd.\u4ec0\u4e48\u4ea4\u6e05\u4e0d\u4ea4\u60c5\uff0c\u6211\u4e0d\u61c2\uff01\u201d \u3000\u3000\u5979\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5fc3\u91cc\u66f4\u8352\uff0c\u56e0\u4e3a\u8fd9\u65f6\u8fdc\u8fdc\u53c8\u6709\u4e24\u4e2a\u4eba\u5954\u6765\u3002\u81ea\u5df1\u5b64\u8eab\u4e00\u4eba\uff0c\u5149\u662f\u8fd9 \u4e24\u4eba\uff0c\u81ea\u5df1\u5df2\u7ecf\u4e0d\u80fd\u5e94\u4ed8\u4e86\uff0c\u6b64\u523b\u4eba\u5bb6\u53c8\u6765\u4e86\u5e2e\u624b\uff0c\u4e07\u4e00\u8a00\u8bed\u4e00\u4e2a\u5f04\u50f5\uff0c\u52a8\u8d77\u624b\u6765\uff0c\u81ea\u5df1\u53ef \u5c31\u5f97\u5403\u4e8f\u3002 \u3000\u3000\u4f46\u662f\u5979\u81ea\u5e7c\u5a07\u7eb5\u6210\u6027\uff0c\u884c\u8d70\u6c5f\u6e56\u65f6\uff0c\u4eba\u5bb6\u5c31\u662f\u4e0d\u754f\u60e7\u5979\u7684\u6b66\u529f\uff0c\u5c31\u51b2\u7740\u5979\u8fd9\u4efd\u6f02\u4eae\uff0c\u518d \u52a0\u4e0a\u5979\u7239\u7239\u201c\u8427\u4e09\u7237\u201d\u7684\u540d\u5934\uff0c\u4e5f\u5f97\u8ba9\u5979\u4e09\u5206\uff0c\u662f\u4ee5\u4e5f\u5c31\u66f4\u517b\u6210\u4e86\u5979\u8fd9\u79cd\u5a07\u7eb5\u7684\u813e\u6c14\u3002 \u3000\u3000\u6b64\u523b\u5979\u5fc3\u91cc\u867d\u5df2\u8f6f\u4e86\u4e0b\u6765\uff0c\u4f46\u8a00\u8bcd\u4e0a\uff0c\u5374\u4ecd\u7136\u786c\u5f97\u5f88\uff0c\u4e0d\u80af\u9976\u4eba\u3002 \u3000\u3000\u90a3\u591a\u624b\u771f\u4eba\u548c\u4e03\u6d77\u6e14\u5b50\uff0c\u540c\u65f6\u9634\u607b\u607b\u4e00\u58f0\u51b7\u7b11\u3002 \u3000\u3000\u8c22\u96e8\u4ed9\u62a2\u5148\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e48\u9601\u4e0b\u5c31\u8bf7\u5c06\u542c\u8fc7\u6211\u5144\u5f1f\u8c08\u8bdd\u7684\u4e24\u53ea\u8033\u7559\u4e0b\u6765\uff0c\u4e0d\u7136\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u51b7\u7b11\u4e00\u58f0\u3002 \u3000\u3000\u8fd9\u65f6\u540e\u6765\u63a0\u6765\u7684\u4eba\u5f71\uff0c\u5df2\u7ad9\u5230\u97e6\u50b2\u7269\u8eab\u540e\uff0c\u5728\u591c\u8272\u4e2d\u770b\u4e86\u8427\u5357\u82f9\u4e00\u773c\uff0c\u5ffd\u5730\u9644\u8033\u671d\u97e6\u50b2 \u7269\u4f4e\u8bed\u4e86\u51e0\u53e5\u3002 \u3000\u3000\u8427\u5357\u82f9\u6b64\u65f6\u5df2\u81ea\u5168\u795e\u6212\u5907\uff0c\u76ee\u5149\u77ac\u5904\uff0c\u5979\u770b\u5230\u63a0\u6765\u7684\u662f\u4e24\u4e2a\u7a7f\u7740\u957f\u9053\u888d\u7684\u5e74\u8f7b\u6c49\u5b50\uff0c\u60f3 \u5fc5\u662f\u5148\u524d\u5728\u7ec8\u5357\u5c71\u4e0a\u4e54\u88c5\u9053\u58eb\u7684\u201c\u5929\u4e89\u201d\u5f92\u4f17\u3002 \u3000\u3000\u5979\u4e00\u5411\u4e13\u95e8\u524a\u4eba\u5bb6\u7684\u8033\u6735\uff0c\u6b64\u65f6\u5374\u88ab\u4eba\u5bb6\u8981\u81ea\u5df1\u524a\u53bb\u8033\u6735\uff0c\u5fc3\u91cc\u4e0d\u7981\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\u7684\u6216 \u89c9\uff0c\u773c\u775b\u671b\u7740\u8c22\u96e8\u4ed9\uff0c\u770b\u770b\u4ed6\u51b7\u7b11\u8fc7\u540e\uff0c\u8fd8\u4f1a\u8bf4\u51fa\u4ec0\u4e48\u8bdd\u6765\uff0c\u8fd8\u662f\u4e00\u8a00\u4e0d\u53d1\uff0c\u5c31\u5411\u81ea\u5df1\u52a8 \u624b\u3002 \u3000\u3000\u90a3\u77e5\u8c22\u96e8\u4ed9\u51b7\u7b11\u4e86\u51e0\u58f0\uff0c\u8fd8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u90a3\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u5374\u5df2\u7ecf\u5927\u6b65\u5411\u524d\u8de8\u4e86\u4e00\u6b65\uff0c\u8fde \u58f0\u5927\u7b11\u7740\uff0c\u7adf\u671d\u8427\u5357\u82f9\u5f53\u5934\u4e00\u63d6\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e0d\u4f46\u8427\u5357\u82f9\u4e3a\u4e4b\u6115\u4f4f\uff0c\u90a3\u591a\u624b\u771f\u4eba\u4e5f\u4e0d\u7981\u8272\u53d8\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e03\u6d77\u6e14\u5b50\u5ffd\u7136\u5bf9\u4eba\u5bb6\u4f5c \u8d77\u63d6\u6765\uff0c\u7a76\u7adf\u662f\u4e3a\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u90a3\u91cc\u77e5\u9053\u90a3\u4e24\u4e2a\u8eab\u7a7f\u9053\u888d\u7684\u5929\u4e89\u6559\u5f92\uff0c\u5148\u524d\u5728\u7ec8\u5357\u5c71\u5165\u5c71\u7684\u8def\u4e0a\uff0c\u66fe\u7ecf\u89c1\u8fc7\u4f0a\u98ce\u548c\u5979 \u4e4b\u9762\uff0c\u540e\u6765\u4f0a\u98ce\u7a81\u7136\u73b0\u8eab\uff0c\u60ca\u8d70\u4e86\u6765\u81ea\u9752\u6d77\u7684\u94b1\u7fca\u548c\u591a\u624b\u771f\u4eba\u7b49\u5341\u4f59\u4e2a\u540d\u5251\u624b\u65f6\uff0c\u4ed6\u4eec\u4e5f\u66fe \u76ee\u7779\u3002 \u3000\u3000\u4ed6\u4eec\u540e\u6765\u542c\u5230\u4e86\u591a\u624b\u771f\u4eba\u7b49\u4eba\u7684\u8bdd\uff0c\u81ea\u7136\u4ee5\u4e3a\u4f0a\u98ce\u5c31\u662f\u4ed6\u4eec\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u9762\u7684\u6559\u4e3b\uff0c\u6b64 \u523b\u4e5f\u81ea\u7136\u4ee5\u4e3a\u8427\u5357\u82f9\u662f\u6559\u4e3b\u7684\u670b\u53cb\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u4eec\u5bf9\u4e03\u6d77\u6e14\u5b50\u4e00\u8bf4\uff0c\u4e03\u6d77\u6e14\u5b50\u4fbf\u7acb\u65f6\u524d\u5028\u800c\u540e\u606d\u8d77\u6765\u3002 \u3000\u3000\u97e6\u50b2\u7269\u957f\u7b11\u8fc7\u540e\uff0c\u7a81\u5730\u4e00\u6574\u8138\u8272\uff0c\u5e84\u5bb9\u5411\u5357\u82f9\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5148\u524d\u5192\u72af\u4e4b\u5904\uff0c\u8bf7\u9601\u4e0b\u6055\u7f6a\u3002\u53ea\u662f\u97e6\u67d0\u5374\u6709\u4e00\u4e8b\u8bf7\u6559\uff1a\u4eca\u6668\u4e0e\u9601\u4e0b\u540c\u884c\u4e4b\u4eba\uff0c\u4e0e\u9601\u4e0b \u53ef\u662f\u7d20\u8bc6\uff0c\u6b64\u523b\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u97e6\u50b2\u7269\u542c\u4e86\u4ed6\u95e8\u4e0b\u7684\u5f1f\u5b50\u7684\u8bdd\uff0c\u6b64\u523b\u8a00\u8bcd\u4e4b\u4e2d\uff0c\u7adf\u8fd8\u4fdd\u7559\u7740\u4e09\u5206\uff0c\u679c\u7136\u4e0d\u6127\u662f\u8001\u6c5f \u6e56\uff01 \u3000\u3000\u8427\u5357\u82f9\u4f55\u5c1d\u53c8\u662f\u7b28\u4eba.\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u4e5f\u77e5\u9053\u4e86\u4eba\u5bb6\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5fc3\u5ff5\u6570\u8f6c\u4e4b\u4e0b\uff0c\u5374\u6545\u610f\u94c1\u9752 \u7740\u8138\uff0c\u51b7\u7b11\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e0e\u6211\u540c\u884c\uff0c\u81ea\u662f\u6211\u53cb\uff0c\u4e0d\u8fc7\u6211\u5374\u4e0d\u4f1a\u53bb\u7ba1\u4eba\u5bb6\u7684\u884c\u52a8\uff0c\u4ed6\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002 \u670b\u53cb\u4eec\u5982\u662f\u90a3\u4eba\u7684\u670b\u53cb\uff0c\u81ea\u7136\u65e0\u8bdd\u53ef\u8bf4\uff0c\u670b\u53cb\u4eec\u82e5\u548c\u90a3\u4eba\u6709\u7740\u6881\u5b50\uff0c\u533a\u533a\u867d\u7136\u4e0d\u624d\uff0c\u5374\u4e5f\u53ef \u4ee5\u4ee3\u90a3\u4eba\u63a5\u7740\u3002\u201d \u3000\u3000\u5979\u73b2\u73d1\u5254\u900f\uff0c\u6545\u610f\u88c5\u7740\u4e0d\u77e5\u9053\u6b64\u4e8b\u7684\u7a76\u7adf\uff0c\u5148\u5c06\u5bf9\u65b9\u5957\u4f4f\u3002 \u3000\u3000\u97e6\u50b2\u7269\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u660e\u4eba\u9762\u524d\u4e0d\u8bf4\u6697\u8bdd\uff0c\u6211\u5144\u5f1f\u662f\u4ec0\u4e48\u4eba\uff0c\u670b\u53cb\u96be\u9053\u8fd8\u4f1a\u4e0d\u77e5\u9053\uff1f\u9601\u4e0b\u65e2\u7136\u4e0d\u80af\u76f8\u544a\uff0c\u97e6 \u67d0\u53ea\u5f97\u5148\u5c06\u670b\u53cb\u7559\u4f4f\u3002\u201d \u3000\u3000\u8fd9\u4e03\u6d77\u6e14\u5b50\u4e0d\u4f46\u6b66\u529f\u81ea\u6210\u4e00\u6d3e\uff0c\u800c\u4e14\u4e3a\u4eba\u673a\u667a\u6df1\u6c89\uff0c\u5728\u5929\u4e89\u6559\u4e0b\uff0c\u4ed6\u662f\u6559\u4e3b\u7684\u667a\u56ca\uff0c\u6b64 \u6b21\u7ec8\u5357\u5c71\u4e4b\u53d8\uff0c\u4e5f\u662f\u8fd9\u4f4d\u4eba\u7269\u4e00\u624b\u7b56\u5212\u3002 \u3000\u3000\u4ed6\u5bf9\u6b64\u4e8b\uff0c\u672c\u5c31\u6709\u7740\u6000\u7591\uff0c\u662f\u4ee5\u5148\u524d\u624d\u4f1a\u548c\u8c22\u96e8\u4ed9\u53d1\u751f\u4e89\u6267\u3002\u8427\u5357\u82f9\u6b64\u523b\u82e5\u7f16\u4e2a\u8c0e\u8bdd\uff0c \u5012\u4e5f\u597d\u4e86\uff0c\u5979\u5374\u504f\u504f\u4e5f\u5356\u5f04\u673a\u667a\uff0c\u90a3\u77e5\u806a\u660e\u5374\u88ab\u806a\u660e\u8bef\uff0c\u8bd5\u60f3\u5979\u82e5\u771f\u662f\u5929\u4e89\u6559\u4e3b\u7684\u670b\u53cb\uff0c\u6b64 \u523b\u90a3\u4f1a\u4e0d\u77e5\u9053\u5bf9\u65b9\u662f\u4ec0\u4e48\u4eba\uff0c\u800c\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff1f \u3000\u3000\u97e6\u50b2\u7269\u7591\u5ff5\u4e00\u751f\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8eab\u5f62\u5df2\u52a8\uff0c\u624b\u91cc\u7684\u91d1\u4e1d\u6e14\u7f51\u5fae\u6296\uff0c\u5982\u4f7f\u4e00\u5835\u91d1\u5899\uff0c\u5411\u8427\u5357 \u82f9\u5f53\u5934\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u53d8\u53d8\u5f97\u53c8\u6781\u5176\u7a81\u7136\uff0c\u8427\u5357\u82f9\u5927\u60ca\u4e4b\u4e0b\uff0c\u5a07\u8eaf\u4e00\u8f6c\uff0c\u8eab\u5b50\u65b9\u6e9c\u5f00\u51e0\u6b65\uff0c\u90a3\u77e5\u90a3\u7247\u91d1\u4e1d \u6e14\u7f51\uff0c\u65b9\u5411\u4e00\u8f6c\u7adf\u6a2a\u7740\u5411\u5979\u5377\u53bb\u3002 \u3000\u3000\u8427\u5357\u82f9\u52a8\u624b\u7684\u7ecf\u9a8c\uff0c\u867d\u5df2\u53ef\u7b97\u4e0d\u5c11\uff0c\u4f46\u8fd9\u79cd\u9738\u9053\u7684\u5916\u95e8\u5175\u5668\uff0c\u5979\u5012\u8fd8\u662f\u7b2c\u4e00\u6b21\u9047\u4e0a\uff0c\u811a \u6b65\u4e00\u9519\uff0c\u53ea\u5f97\u518d\u907f\u5f00\uff0c\u8fde\u8fd8\u624b\u4e4b\u529b\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u624b\u8155\u4e00\u6296\uff0c\u90a3\u5f20\u91d1\u4e1d\u6e14\u7f51\u867d\u7136\u539f\u5c01\u4e0d\u52a8\u5730\u5411\u8427\u5357\u82f9\u88ad\u53bb\uff0c\u4f46\u5374\u5df2\u53d8 \u6210\u4e00\u6761\u957f\u7ea6\u4e94\u5c3a\u7684\u91d1\u8272\u8f6f\u68cd\u3002 \u3000\u3000\u8fd9\u91d1\u4e1d\u6e14\u7f51\uff0c\u88ab\u4ed6\u7684\u771f\u529b\u6240\u6536\uff0c\u7adf\u4ee5\u8f6f\u68cd\u7684\u62db\u5f0f\uff0c\u5411\u8427\u5357\u82f9\u80c1\u4e0b\u7684\u201c\u7ae0\u95e8\u201d\u5927\u7a74\u70b9\u53bb\u3002 \u3000\u3000\u8fd9\u79cd\u4ee5\u68cd\u70b9\u7a74\u7684\u62db\u5f0f\uff0c\u8427\u5357\u82f9\u5374\u8f83\u4e3a\u719f\u6089\u4e9b\u3002\u5979\u867d\u7136\u60ca\u5f02\u4e8e\u8fd9\u4e03\u6d77\u6e14\u5b50\u62db\u5f0f\u7684\u7384\u5f02\uff0c\u4f46 \u672c\u80fd\u4e4b\u4e0b\uff0c\u8eab\u8eaf\u5411\u4e00\u8f6c\uff0c\u5de6\u638c\u5237\u5730\uff0c\u97e6\u50b2\u7269\u53f3\u8155\u731b\u5207\uff0c\u53f3\u624b\u5374\u81ea\u53cd\u8155\u64a4\u5251\u3002 \u3000\u3000\u5979\u4ee5\u653b\u4e3a\u5b88\uff0c\u6b3a\u8eab\u8fdb\u62db\uff0c\u672c\u662f\u5999\u7740\uff0c\u90a3\u77e5\u4e03\u6d77\u6e14\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u7b11\u58f0\u4e2d\u624b\u8155\u4e00\u62bd\u4e00\u5e26\uff0c\u90a3 \u6761\u91d1\u8272\u8f6f\u68cd\uff0c\u4fbf\u53c8\u5ffd\u5730\u5f20\u5f00\u3002 \u3000\u3000\u8427\u5357\u82f9\u53ea\u89c9\u773c\u524d\u91d1\u5149\u53c8\u66b4\u957f\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u4f46\u5979\u6b64\u65f6\u5168\u8eab\u7684\u529b\u9053\uff0c\u5df2\u7528\u4f5c\u653b\u654c\uff0c\u6b64\u523b\u8fd9\u7247 \u91d1\u4e1d\u6e14\u7f51\u4e00\u5f20\u5f00\uff0c\u5bf9\u624b\u5c31\u5b8c\u5168\u88ab\u4fdd\u62a4\u7740\u4e86\uff0c\u8fde\u4e00\u4e1d\u7a7a\u9699\u90fd\u6ca1\u6709\uff0c\u800c\u81ea\u5df1\u5374\u5168\u8eab\u90fd\u5728\u4eba\u5bb6\u7684\u5a01 \u529b\u6240\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u867d\u7136\u62bd\u8eab\u540e\u9000\uff0c\u4f46\u5374\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u5979\u53ea\u89c9\u5f97\u90a3\u7247\u6e14\u7f51\u6f2b\u5929\u5411\u81ea\u5df1\u7f69\u4e86\u4e0b\u6765\uff0c\u53f3\u624b\u53cd\u6325\uff0c\u867d\u4e00\u5251\u6325\u51fa\uff0c\u4f46\u5374\u8f6f\u8f6f\u5730\u4e00\u4e1d\u7740\u529b \u4e4b\u5904\u90fd\u6ca1\u6709\uff0c\u81ea\u5df1\u8fde\u4eba\u5e26\u5251\uff0c\u7adf\u90fd\u88ab\u8fd9\u5f20\u6e14\u7f51\u7f69\u4f4f\u3002 \u3000\u3000\u591a\u624b\u771f\u4eba\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u97e6\u9999\u4e3b\u679c\u7136\u597d\u529f\u592b\uff0c\u4eca\u65e5\u8c22\u67d0\u4eba\u5012\u771f\u662f\u5f00\u4e86\u773c\u754c\u3002\u201d \u3000\u3000\u867d\u662f\u606d\u7ef4\u4e4b\u8bdd\uff0c\u4f46\u8bed\u6c14\u91cc\u5374\u6ca1\u6709\u534a\u70b9\u606d\u7ef4\u7684\u610f\u601d\u3002 \u3000\u3000\u539f\u6765\u5929\u4e89\u6559\u4e0b\u7684\u6559\u4f17\uff0c\u5171\u5206\u4e94\u7ea7\uff0c\u91d1\u8863\u9999\u4e3b\u5728\u6559\u4e2d\u662f\u4e00\u6d41\u7684\u8eab\u4efd\uff0c\u80fd\u591f\u6709\u8d44\u683c\u5728\u5929\u4e89\u6559 \u91cc\u7740\u4e0a\u4e00\u88ad\u91d1\u886b\u7684\uff0c\u5728\u6c5f\u6e56\u4e0a\u81ea\u7136\u4e5f\u4e0d\u4f1a\u662f\u65e0\u540d\u4e4b\u8f88\uff0c\u4f46\u5728\u91d1\u8272\u9999\u4e3b\u4e4b\u4e2d\uff0c\u6b66\u529f\uff0c\u8eab\u4efd\uff0c\u5374 \u4ecd\u7136\u6709\u9ad8\u4e0b\u4e4b\u5206\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u540c\u5728\u5929\u4e89\u6559\u4e0b\uff0c\u4f46\u8fd9\u4e9b\u672c\u5df2\u5728\u6b66\u6797\u4e2d\u6210\u540d\u7acb\u4e07\uff0c\u5404\u4eab\u76db\u540d\uff0c\u5404\u6709\u5730\u76d8\u7684\u6c5f\u6e56\u9ad8 \u624b\u4eec\uff0c\u5374\u4ecd\u7136\u4e0d\u514d\u4e92\u76f8\u731c\u5fcc\uff0c\u6392\u8f67\u3002 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\uff0c\u4ee5\u81ea\u8eab\u7684\u540d\u671b\uff0e\u6b66\u529f\u548c\u673a\u667a\uff0c\u5728\u6b66\u6797\u4e2d\u672c\u5df2\u662f\u9876\u5c42\u4eba\u7269\uff0c\u5165\u4e86\u5929\u4e89 \u6559\uff0c\u66f4\u6210\u4e86\u7b2c\u4e00\u6d41\u7684\u7ea2\u4eba\u3002 \u3000\u3000\u4f46\u591a\u624b\u771f\u4eba\u6a2a\u884c\u5ddd\u9ed4\u591a\u5e74\uff0c\u4e07\u513f\u4e5f\u6781\u54cd\u4eae\uff0c\u672c\u5df2\u4e0d\u5356\u8fd9\u4e03\u6d77\u6e14\u5b50\u7684\u8d26\uff0c\u518d\u52a0\u4e0a\u8fd9\u7ec8\u5357\u5c71 \u4e00\u4e8b\uff0c\u5f7c\u6b64\u53c8\u65b0\u751f\u82a5\u8482\uff0c\u662f\u4ee5\u8c22\u96e8\u4ed9\u770b\u5230\u4e03\u6d77\u6e14\u5b50\u751f\u64d2\u4e86\u8427\u5357\u82f9\uff0c\u5374\u4ee5\u4e3a\u4ed6\u662f\u62a2\u529f\u3002\u8a00\u8bed\u4e4b \u4e2d\uff0c\u81ea\u7136\u4e0d\u5feb\u3002 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u5fc3\u91cc\u6697\u54fc\u4e00\u58f0\uff0c\u8868\u9762\u4e0a\u5374\u4e1d\u6beb\u4e0d\u9732\u51fa\u6765\uff0c\u4ecd\u7136\u7b11\u9053\uff1a \u3000\u3000\u201c\u8c22\u9999\u4e3b\u8fc7\u8a89\u4e86\uff0c\u6c5f\u6e56\u4e4b\u95f4\uff0c\u8c01\u4e0d\u77e5\u9053\u591a\u624b\u771f\u4eba\u5728\u6697\u5668\u4e0a\uff0c\u6709\u7740\u72ec\u5230\u7684\u529f\u592b\uff0c\u53cc\u624b\u201c\u6f2b \u5929\u82b1\u96e8\u201d\u4e4b\u5916\uff0c\u8fd8\u6709\u7740\u201c\u67f3\u7d6e\u56de\u98ce\u201d\u7684\u7edd\u6280\u3002\u201d \u3000\u3000\u591a\u624b\u771f\u4eba\u4ef0\u5929\u4e00\u7b11\uff0c\u5374\u9053\uff1a \u3000\u3000\u201c\u97e6\u9999\u4e3b\u60f3\u662f\u6210\u5fc3\u8981\u6211\u59d3\u8c22\u7684\u597d\u770b\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u8c01\u4e0d\u77e5\u9053\u4e03\u6d77\u6e14\u5b50\u7684\u91d1\u4e1d\u795e\u7f51\uff0c\u662f\u5929\u4e0b \u5404\u95e8\u6d3e\u6697\u5668\u7684\u514b\u661f\u3002\u201d \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u77e5\u9053\u4ed6\u5403\u4e86\u5473\u4e86\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5374\u4e5f\u5e76\u4e0d\u89e3\u91ca\u3002 \u3000\u3000\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u671d\u90a3\u4ecd\u5728\u91d1\u4e1d\u6e14\u7f51\u91cc\u6323\u624e\u7740\u7684\u8427\u5357\u82f9\uff0c\u671b\u4e86\u4e00\u773c\uff0c\u51b7\u51b7\u5730\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6b64\u4eba\u65e2\u7136\u88ab\u97e6\u9999\u4e3b\u64d2\u5f97\uff0c\u81ea\u7136\u5168\u51ed\u97e6\u9999\u4e3b\u5904\u7f6e\u3002\u65e5\u540e\u6559\u4e3b\u82e5\u602a\u7f6a\u4e0b\u6765\uff0c\u51ed\u97e6\u9999\u4e3b\u7684\u8eab \u4efd\u5730\u4f4d\uff0c\u81ea\u7136\u4e5f\u62c5\u5f53\u5f97\u8d77\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u626b\uff0c\u53c8\u51b7\u7b11\u4e00\u4e0b\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u81f3\u4e8e\u5728\u4e0b\u4e48\u2026\u2026\u5374\u4e07\u4e07\u62c5\u5f53\u4e0d\u8d77\uff0c\u6b64\u523b\u53ea\u6709\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5148\u524d\u4e5f\u7ecf\u90a3\u4e24\u4e2a\u5929\u4e89\u6559\u5f92\u544a\u77e5\u4e86\u6b64\u523b\u88ab\u4e03\u6d77\u6e14\u5b50\u64d2\u4f4f\u7684\u4eba\u662f\u8c01\uff0c\u662f\u4ee5\u6b64\u523b\u624d\u8bf4\u51fa\u8fd9\u79cd \u8bdd\u6765\uff0c\u5148\u63a8\u53bb\u4e86\u81ea\u5df1\u7684\u8d23\u4efb\u3002 \u3000\u3000\u97e6\u50b2\u7269\u5fc3\u91cc\u5374\u53e6\u6709\u6253\u7b97\uff0c\u4ecd\u7136\u9634\u607b\u607b\u5730\u7b11\u7740\u3002\u8c22\u96e8\u4ed9\u9762\u8272\u53d8\u5f97\u66f4\u52a0\u96be\u770b\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u4e00 \u8dfa\u811a\uff0c\u8eab\u5f62\u5012\u7eb5\u800c\u8d77\uff0c\u7adf\u5982\u98de\u63a0\u8d70\u3002 \u3000\u3000\u97e6\u50b2\u7269\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u51b7\u7b11\u4e86\u51e0\u58f0\uff0c\u6b64\u523b\u867d\u65e0\u4e3e\u52a8\uff0c\u5fc3\u91cc\u5374\u79cd\u4e0b\u4e86\u65e5\u540e\u85c9\u6545\u9664\u53bb\u8fd9\u4e2a\u548c \u81ea\u5df1\u4e0d\u5bf9\u4e4b\u4eba\u7684\u6740\u673a\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4fef\u8eab\u5411\u8427\u5357\u82f9\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u653e\u5b89\u9759\u4e9b\u5427\uff01\u201d\u968f\u7740\u8bdd\u58f0\uff0c\u5de6\u624b\u5e76\u6307\u5982\u5200\uff0c\u5200\u53bb\u5982\u98ce\uff0c\u201c\u55d6\u201d\u5730\uff0c\u7adf\u4ece\u91d1\u4e1d\u6e14 \u7f51\u7684\u7f51\u773c\u4e2d\uff0c\u70b9\u4e2d\u4e86\u8427\u5357\u82f9\u5934\u9876\u6b63\u4e2d\u7684\u201c\u6606\u4ed1\u7a74\u201d\u3002 \u3000\u3000\u6b64\u7a74\u4e43\u4eba\u4e4b\u4e00\u8eab\u767e\u8109\u4f1a\u805a\u4e4b\u5904\uff0c\u672c\u5df2\u7f9e\u6124\uff0c\u6025\u6012\u4ea4\u52a0\u7684\u8427\u5357\u82f9\uff0c\u5728\u4ed6\u624b\u6307\u7684\u8f7b\u8f7b\u4e00\u70b9\u4e4b \u4e0b\uff0c\u7adf\u5168\u7136\u5931\u53bb\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u97e6\u50b2\u7269\u53f3\u624b\u4e00\u6296\uff0c\u5c06\u7f69\u4f4f\u8427\u5357\u82f9\u7684\u91d1\u4e1d\u6e14\u7f51\u64a4\u4e86\u4e0b\u6765\uff0c\u8f6c\u8eab\u56de\u987e\u59cb\u7ec8\u7ad9\u5728\u4ed6\u8eab\u540e\u7684\u90a3\u4e24 \u4e2a\u5929\u4e89\u6559\u5f92\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u5c06\u8fd9\u4eba\u625b\u8d77\u6765\uff0c\u5f04\u8f86\u5927\u8f66\uff0c\u6b64\u95f4\u4e8b\u60c5\u5df2\u4e86\uff0c\u6211\u4eec\u8fde\u591c\u8d76\u56de\u6c5f\u5357\u603b\u8235\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u58f0\u53c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u76f8\u4e0d\u76f8\u4fe1\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u4e24\u5929\u6211\u4eec\u6559\u91cc\uff0c\u5df2\u51fa\u4e86\u8bb8\u591a\u83ab\u540d\u5176\u5999\u7684\u4e8b\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u5fc3\u91cc\u867d\u4e0d\u660e\u767d\u97e6\u9999\u4e3b\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u505a\uff0c\u4f46\u77e5\u9053\u8fd9\u7d20\u4ee5\u673a\u667a\u89c1\u79f0\u7684\u4e03\u6d77\u6e14\u5b50\u6b64\u4e3e\u5fc5\u6709 \u6df1\u610f\uff0c\u662f\u4ee5\u7b54\u5e94\u4e86\u4e4b\u540e\uff0c\u4fbf\u4e00\u4e2a\u7bad\u6b65\u63a0\u5230\u8427\u5357\u82f9\u8eab\u524d\uff0c\u4f38\u624b\u4ece\u5979\u7684\u80c1\u4e0b\u6284\u4e86\u8fc7\u53bb\uff0c\u4f46\u4e00\u89e6\u5979 \u524d\u80f8\uff0c\u4ed6\u4e0d\u7981\u5fae\u5fae\u60ca\u547c\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6b64\u4eba\u539f\u6765\u662f\u4e2a\u5973\u5b50\uff01\u201d \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u4e03\u7ae0\u3000\u4f0a\u4eba\u6709\u8baf>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u4e03\u7ae0\u3000\u4f0a\u4eba\u6709\u8baf \u3000\u3000\u8427\u5357\u82f9\u518d\u6b21\u56de\u590d\u77e5\u89c9\u7684\u65f6\u5019\uff0c\u6ee1\u8033\u8f66\u58f0\u8f9a\u8f9a\uff0c\u5979\u77e5\u9053\u81ea\u5df1\u662f\u5728\u8f66\u4e0a\u3002\u4f46\u662f\u76ee\u5149\u4e00\u8f6c\uff0c\u8fd9 \u8f86\u8f66\u5b50\u91cc\uff0c\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u7adf\u518d\u65e0\u4ed6\u4eba\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u5012\u5e95\u5c06\u6211\u600e\u4e48\u6837\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u5fc3\u91cc\u6b63\u601d\u7d22\uff0c\u7a97\u53e3\u5df2\u63a2\u8fdb\u4e00\u4e2a\u5934\u6765\uff0c\u5374\u662f\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\uff0c\u671b\u7740\u5979\u5fae\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u5df2\u77e5\u9053\u4f60\u662f\u4e2a\u5973\u5b50\uff0c\u51b3\u4e0d\u4f1a\u96be\u4e3a\u4f60\u7684\uff0c\u4f55\u51b5\u6211\u4ece\u4f60\u968f\u8eab\u5e26\u7740\u7684\u6697\u5668\u4e0a\u9762\uff0c\u4e5f\u731c\u51fa\u4f60 \u5927\u6982\u5c31\u662f\u201c\u8427\u4e09\u7237\u201d\u7684\u5973\u513f\uff0c\u4ed6\u8001\u4eba\u5bb6\u5728\u4e16\u7684\u65f6\u5019\uff0c\u548c\u6b66\u6797\u4e2d\u7684\u670b\u53cb\uff0c\u90fd\u76f8\u5904\u5f97\u5f88\u597d\uff0c\u6211\u770b \u5728\u4ed6\u7684\u9762\u5b50\u4e0a\uff0c\u66f4\u4e0d\u4f1a\u5bf9\u4f60\u600e\u4e48\u6837\uff0c\u53ea\u8981\u4e8b\u60c5\u5f04\u6e05\u695a\u4e86\uff0c\u5c31\u9a6c\u4e0a\u653e\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5bb9\u5ffd\u655b\uff0c\u53c8\uff1a \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e5f\u4e0d\u8981\u5984\u52a8\uff0c\u6b64\u65f6\u4f60\u6c14\u8840\u76f8\u4ea4\u4e4b\u5904\u7684\u201c\u8179\u7ed3\u7a74\u201d\uff0c\u5df2\u88ab\u6211\u70b9\u4f4f\uff0c\u4e5f\u7528\u4e0d\u5f97 \u529b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u4e00\u7b11\uff1a \u3000\u3000\u201c\u4f55\u51b5\u4f60\u5750\u5728\u8f66\u4e0a\uff0c\u4e5f\u86ee\u8212\u670d\u7684\uff0c\u8fd9\u4e48\u51b7\u7684\u5929\u6c14\uff0c\u4e0d\u6bd4\u6211\u9a91\u5728\u7109\u4e0a\uff0c\u8981\u8212\u670d\u591a\u4e86\u5417\uff1f\u201d \u8bf4\u7740\uff0c\u4ed6\u53c8\u7f29\u56de\u5934\u3002\u8427\u5357\u82f9\u5fc3\u4e2d\u6697\u6c14\uff0c\u4f46\u8bd5\u4e00\u8fd0\u6c14\uff0c\u4fbf\u7acb\u5373\u53d7\u963b\uff0c\u77e5\u9053\u8fd9\u4e03\u6d77\u6e14\u5b50\u6240\u8a00\u975e \u865a\uff0c\u5fc3\u91cc\u867d\u6709\u6c14\uff0c\u53ef\u4e5f\u6ca1\u6709\u6cd5\u5b50\u3002 \u3000\u3000\u8f66\u5b50\u767d\u5929\u8d70\u7740\uff0c\u665a\u4e0a\u6b47\u4e0b\uff0c\u53ef\u5374\u4e5f\u4e0d\u5c06\u8427\u5357\u82f9\u642c\u4e0b\u8f66\uff0c\u5979\u5012\u4e5f\u843d\u4e2a\u6e05\u9759\u3002 \u3000\u3000\u8fd9\u4e03\u6d77\u6e14\u5b50\u867d\u9634\u51f6\u72e1\u72e0\uff0c\u4f46\u5374\u4e0d\u662f\u597d\u8272\u7684\u6deb\u5f92\uff0c\u6bcf\u5929\u4e5f\u6309\u65f6\u7ed9\u8427\u5357\u82f9\u9001\u4e9b\u5403\u98df\uff0c\u4e0d\u5979\u997f \u7740\u3002 \u3000\u3000\u8f66\u5b50\u8d70\u4e86\u597d\u591a\u5929\uff0c\u5fc3\u50b2\u6c14\u9ad8\u7684\u6f47\u6e58\u5983\u5b50\uff0c\u5728\u8fd9\u4e24\u5929\u91cc\uff0c\u53ef\u88ab\u6298\u78e8\u5f97\u591f\u4e86\u3002\u5979\u6068\u4e0d\u5f97\u4f0f\u5728 \u8f66\u5b50\u91cc\u5927\u54ed\u4e00\u573a\uff0c\u5374\u53c8\u6015\u88ab\u8f66\u5b50\u5916\u9762\u7684\u97e6\u50b2\u7269\u542c\u5230\uff0c\u53ea\u6709\u5c06\u6ee1\u8179\u7684\u59d4\u66f2\uff0c\u6df1\u6df1\u85cf\u8d77\u6765\u3002 \u3000\u3000\u5979\u5c3d\u91cf\u4e0d\u53bb\u60f3\u4f0a\u98ce\uff0c\u4f46\u662f\u4f0a\u98ce\u7684\u5f71\u5b50\uff0c\u5374\u504f\u504f\u65e0\u65f6\u65e0\u523b\u4e0d\u95ef\u8fdb\u5979\u5fc3\u91cc\u3002 \u3000\u3000\u5979\u67d4\u80a0\u767e\u7ed3\uff0c\u6ee1\u8179\u8f9b\u9178\uff0c\u53ef\u5374\u80fd\u5411\u8c01\u53bb\u8bc9\u8bf4\u5462\uff1f \u3000\u3000\u5979\u5750\u5728\u8f66\u5b50\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u5230\u5e95\u5230\u4e86\u90a3\u91cc\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e00\u5929\uff0c\u5979\u5ffd\u7136\u542c\u5230\u8f66\u5b50\u540e\u9762\uff0c\u6709\u4e00\u4e2a\u4eba\u5927\u58f0\u53eb\u7740\uff1a \u3000\u3000\u201c\u97e6\u9999\u4e3b\uff01\u97e6\u9999\u4e3b\uff01\u201d \u3000\u3000\u8f66\u5b50\u4fbf\u7f13\u7f13\u505c\u4e86\u4e0b\u6765\uff0c\u4e00\u9635\u6025\u907d\u7684\u9a6c\u8e44\u58f0\uff0c\u7136\u540e\u5728\u8f66\u65c1\u505c\u4e0b\uff0c\u4e00\u4e2a\u4e2d\u6c14\u9887\u8db3\u7684\u58f0\u97f3\u5728\u8f66 \u7a97\u5916\u54cd\u4e86\u8d77\u6765\uff0c\u8bf4\u7740\uff1a \u3000\u3000\u201c\u97e6\u9999\u4e3b\uff01\u9047\u7740\u4f60\u771f\u597d\u6781\u4e86\uff01\u4f60\u4e0d\u77e5\u9053\uff0c\u5c0f\u5f1f\u8fd9\u4e24\u5929\u771f\u5947\u602a\u7684\u7d27\uff0c\u82e5\u4e0d\u662f\u53c8\u78b0\u7740\u8001\u5144\uff0c \u53ef\u771f\u8981\u5c06\u5c0f\u5f1f\u95f7\u6b7b\u4e86\uff01\u201d \u3000\u3000\u53c8\u542c\u97e6\u50b2\u7269\u7b11\u7740\u95ee\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u80fd\u8ba9\u4f60\u76d8\u9f99\u68cd\u848b\u4f2f\u9633\u6025\u6210\u8fd9\u4ed8\u6837\u5b50\u7684\uff1f\u5c0f\u5f1f\u5012\u4e5f\u5947\u602a\u7684\u5f88\u3002\u201d \u3000\u3000\u8f66\u53a2\u91cc\u7684\u8427\u5357\u82f9\u4e0d\u7981\u53c8\u76b1\u4e86\u4e00\u4e0b\u7709\uff0c\u5fd6\u7740\uff1a \u3000\u3000\u201c\u600e\u7684\u5c11\u6797\u95e8\u5f92\u4e2d\u4e5f\u6709\u4eba\u5165\u4e86\u5929\u4e89\u6559\uff01\u8d1d\u6765\u8fd9\u5929\u4e89\u6559\u7684\u52bf\u529b\uff0c\u771f\u7684\u65e5\u76ca\u58ee\u5927\uff0c\u8fde\u76d8\u9f99\u68cd \u848b\u4f2f\u9633\u7adf\u4e5f\u88ab\u4ed6\u4eec\u6536\u7f57\u4e86\u53bb\u3002\u201d \u3000\u3000\u5979\u4e0d\u7981\u6697\u6697\u5730\u7740\u6025\uff0c\u5979\u7684\u201c\u5357\u54e5\u54e5\u201d\u7684\u4ec7\u96be\u62a5\u3002 \u3000\u3000\u5374\u542c\u90a3\u4ee5\u5c11\u6797\u201c\u4e00\u767e\u53e6\u516b\u5357\u4f0f\u864e\u68cd\u6cd5\u201d\u53ca\u638c\u4e2d\u4eae\u94f6\u76d8\u9f99\u68cd\u540d\u9707\u6cb3\u6714\u7684\u848b\u4f2f\u9633\u9053\uff1a \u3000\u3000\u201c\u97e6\u5144\uff01\u4f60\u77e5\u4e0d\u77e5\u9053\u6559\u4e3b\u8fd9\u4e24\u5929\u4e3a\u4ec0\u4e48\u5230\u4e86\u6cb3\u5357\u6765\uff0c\u6211\u5728\u5f00\u5c01\u9047\u7740\u6559\u4e3b\uff0c\u6559\u4e3b\u5c31\u53eb\u6211\u53ec \u96c6\u6ee1\u57ce\u7684\u5f1f\u5144\uff0c\u5f53\u665a\u5728\u57ce\u5916\u5f00\u575b\uff0c\u8fd9\u5df2\u662f\u7834\u5929\u8352\u7684\u4e8b\u4e86\u3002\u5230\u4e86\u665a\u4e0a\uff0c\u5927\u5925\u513f\u5c31\u90fd\u5728\u606d\u5019\u6559\u4e3b \u7684\u5927\u9a7e\uff0c\u90a3\u77e5\u6559\u4e3b\u5374\u6ca1\u5927\u6765\uff0c\u8fd9\u8fd8\u4e0d\u8bf4\uff0c\u5374\u4e0d\u77e5\u4ece\u90a3\u91cc\u6765\u4e86\u51e0\u4e2a\u8499\u7740\u9762\u7684\u5bb6\u4f19\uff0c\u7adf\u5c06\u6211\u4eec\u5728 \u5f00\u5c01\u57ce\u91cc\u7684\u8235\u7ed9\u6311\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e03\u6d77\u6e14\u5b50\u867d\u7136\u60ca\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u542c\u848b\u4f2f\u9633\u53c8\u8865\u5145\u7740\u8bf4\u7740\uff1a \u3000\u3000\u201c\u90a3\u51e0\u4e2a\u8499\u9762\u6c49\u5b50\u6b66\u529f\u7adf\u90fd\u6781\u9ad8\uff0c\u4f7f\u7684\u5374\u662f\u5173\u5185\u7edd\u672a\u89c1\u8fc7\u7684\u5251\u6cd5\u3002\u97e6\u5144\uff01\u4f60\u662f\u77e5\u9053\u7684\uff0c \u5f00\u5c01\u8235\u4e0b\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u597d\u624b\u3002\u81f3\u4e8e\u5c0f\u5f1f\uff0c\u5509\u2014\u2014\u53cc\u62f3\u96be\u654c\u56db\u624b\uff0c\u52c9\u5f3a\u62b5\u654c\u4f4f\u4e00\u9635\u5b50\uff0c\u8eab\u5b50\u4e5f \u6302\u4e86\u5f69\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u60f3\u5fc5\u662f\u5f53\u65f6\u4ed6\u89c1\u673a\u4e0d\u5bf9\uff0c\u5c31\u5148\u6e9c\u4e86\uff0c\u662f\u4ee5\u6b64\u523b\u7565\u7565\u5e26\u8fc7\u4e00\u53e5\uff0c\u5c31\u53c8\u8bf4\u7740\uff1a \u3000\u3000\u201c\u6b64\u4e8b\u592a\u8fc7\u8e4a\u8df7\uff0c\u5c0f\u5f1f\u6b63\u60f3\u8d76\u5230\u603b\u8235\u53bb\u95ee\u95ee\uff0c\u90a3\u77e5\u5374\u5728\u6b64\u5730\u9047\u7740\u8001\u5144\u2014\u2014\u97e6\u5144\uff01\u4f9d\u4f60\u4e4b \u89c1\uff0c\u8fd9\u7a76\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u8f66\u53a2\u91cc\u7684\u8427\u5357\u82f9\u5fc3\u91cc\u4e0d\u7981\u6026\u6026\u8df3\u52a8\u7740\uff0c\u4ece\u8fd9\u848b\u4f2f\u9633\u7684\u8bdd\u4e2d\uff0c\u5979\u77e5\u9053\u8fd9\u4e8b\u5fc5\u5b9a\u5c31\u662f\u4f0a\u98ce\u548c \u90a3\u201c\u98de\u8679\u4e03\u5251\u201d\u5e72\u51fa\u6765\u7684\u3002 \u3000\u3000\u201c\u60f3\u5fc5\u662f\u5357\u54e5\u54e5\u5bf9\u201c\u98de\u8679\u4e03\u5251\u201d\u4e5f\u8bf4\u51fa\u4e86\u771f\u76f8\uff0c\u662f\u4ee5\u4fbf\u6311\u4e86\u5929\u4e89\u6559\u7684\u5206\u8235\u3002\u4f46\u662f\u5357\u54e5\u54e5 \u73b0\u5728\u5728\u90a3\u91cc\u5462\uff1f\u4ed6\u77e5\u4e0d\u77e5\u9053\u6211\u73b0\u5728\u6b63\u5728\u53d7\u7740\u7f6a\uff1f\u4ed6\u82e5\u77e5\u9053\uff0c\u4f1a\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u6551\u6211\u5462\uff1f\u201d \u3000\u3000\u5979\u4e0d\u7981\u53c8\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4f46\u5374\u53c8\u8d76\u7d27\u5c06\u53f9\u6c14\u58f0\u6536\u4f4f\uff0c\u751f\u6015\u88ab\u90a3\u673a\u667a\u6df1\u6c89\u7684\u4e03\u6d77\u6e14\u5b50\u542c \u5230\u3002 \u3000\u3000\u8f66\u53a2\u5916\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u60f3\u5728\u90a3\u97e6\u50b2\u7269\u4e5f\u4e3a\u7740\u6b64\u4e8b\u800c\u6c89\u601d\u7740\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u5374\u542c\u4ed6\u6717\u58f0\u8bf4\u7740\uff1a \u3000\u3000\u201c\u6b64\u4e8b\u5b9e\u5728\u900f\u7740\u53e4\u602a\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u77e5\u9053\u3002\u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u848b\u9999\u4e3b\u6700\u597d\u8fd8\u662f\u5148\u56de\u5f00\u5c01\u57ce\u53bb\uff0c\u5c06 \u5269\u4e0b\u7684\u5144\u5f1f\u6574\u987f\u4e00\u4e0b\uff0c\u5148\u5c06\u5f00\u5c01\u5206\u8235\u518d\u6574\u7406\u8d77\u6765\u3002\u522b\u7684\u4e8b\uff0c\u7b49\u5c0f\u5f1f\u56de\u5230\u603b\u8235\uff0c\u67e5\u6e05\u4e86\u771f\u76f8\uff0c \u518d\u6765\u901a\u77e5\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u4e5f\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u90a3\u76d8\u9f99\u68cd\u848b\u4f2f\u9633\u6c89\u541f\u4e86\u534a\u664c\uff0c\u4e5f\u9053\uff1a \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c0f\u5f1f\u5c31\u5148\u56de\u53bb\u4e86\u3002\u5509\uff01\u771f\u60f3\u4e0d\u5230\uff0c\u5728\u5f00\u5c01\u57ce\u91cc\u8f9b\u8f9b\u82e6\u82e6\u521b\u7acb\u4e0b\u6765\u7684\u57fa\u4e1a\uff0c \u5374\u8fd9\u4e48\u6837\u7cca\u91cc\u7cca\u6d82\u5730\u65ad\u9001\u4e86\u5927\u534a\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u50cf\u662f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u53c8\u6c89\u9ed8\u4e86\u534a\u664c\u3002\u8427\u5357\u82f9\u53c8\u542c\u4e86\u4e00\u9635\u9a6c\u8e44\u58f0\uff0c\u6e10\u884c\u6e10\u8fdc\uff0c\u5979\u77e5\u9053\u90a3 \u76d8\u9f99\u68cd\u848b\u4f2f\u9633\u5df2\u7ecf\u8d70\u4e86\u3002 \u3000\u3000\u63a5\u7740\uff0c\u9a6c\u8f66\u53c8\u590d\u8d77\u884c\uff0c\u8427\u5357\u82f9\u7684\u5fc3\u91cc\uff0c\u4e0d\u7981\u53c8\u559c\uff0c\u53c8\u6012\uff0c\u601d\u6f6e\u53c8\u7d0a\u4e71\u4e86\u8d77\u6765\uff0c\u8fd9\u5f53\u7136\u662f \u56e0\u7740\u5979\u9aa4\u7136\u542c\u5230\u4f0a\u98ce\u7684\u6d88\u606f\u3002 \u3000\u3000\u8f66\u5b50\u8d70\u4e86\u4e00\u9635\uff0c\u5374\u975e\u5e38\u4f8b\u5916\u5730\u5728\u767d\u5929\u5c31\u505c\u4e0b\u4e86\uff0c\u8427\u5357\u82f9\u4ece\u5916\u9762\u55a7\u95f9\u7684\u5e02\u58f0\u91cc\u542c\u51fa\u6765\uff0c\u505c \u8f66\u7684\u5730\u65b9\u662f\u5728\u4e00\u5904\u4eba\u70df\u9887\u7a20\u7684\u57ce\u91cc\u3002 \u3000\u3000\u66f4\u4f8b\u5916\u7684\u662f\uff1a\u7adf\u6709\u4e24\u4eba\u4ece\u8f66\u5b50\u91cc\u5c06\u8427\u5357\u82f9\u6276\u4e86\u51fa\u6765\uff0c\u642d\u8fdb\u4e00\u5bb6\u5ba2\u6808\u91cc\uff0c\u800c\u90a3\u4e03\u6d77\u6e14\u5b50\u97e6 \u50b2\u7269\uff0c\u5374\u4e0d\u77e5\u8dd1\u5230\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u8427\u5357\u82f9\u5728\u5fc3\u91cc\u6697\u4e2d\u731c\u6d4b\uff0c\u8fd9\u97e6\u50b2\u7269\u5fc5\u5b9a\u662f\u53bb\u6253\u63a2\u6d88\u606f\u53bb\u4e86\uff0c\u6b64\u65f6\u5b88\u5728\u5979\u65c1\u8fb9\u7684\uff0c\u662f\u4e24\u4e2a \u5e74\u8f7b\u7684\u6c49\u5b50\uff0c\u4ed6\u4eec\u867d\u7136\u8131\u4e0b\u4e86\u9053\u888d\uff0c\u4f46\u662f\u8427\u5357\u82f9\u5374\u77e5\u9053\uff0c\u4ed6\u4eec\u5c31\u662f\u90a3\u4e24\u4e2a\u66fe\u4e54\u5145\u9053\u58eb\u7684\u5929\u4e89 \u6559\u4e0b\u7684\u5c0f\u55bd\u7f57\u3002 \u3000\u3000\u5979\u88ab\u642d\u8fdb\u4e00\u95f4\u9887\u4e3a\u5bbd\u655e\u7684\u623f\u95f4\u91cc\uff0c\u90a3\u4e24\u4e2a\u5e74\u8f7b\u7684\u6c49\u5b50\u5374\u5b88\u5728\u65c1\u8fb9\uff0c\u5979\u77e5\u9053\u51ed\u81ea\u5df1\u7684\u4e00\u8eab \u6b66\u529f\uff0c\u4e0d\u96be\u5c06\u8fd9\u4e24\u4e2a\u6c49\u5b50\u6536\u62fe\u4e0b\u6765\uff0c\u4f46\u81ea\u5df1\u201c\u6c14\u8840\u4e4b\u56ca\u201d\u2014\u2014\u8179\u7ed3\u7a74\u5df2\u7ecf\u88ab\u70b9\u4f4f\uff0c\u6d51\u8eab\u8fde\u4e00 \u4e1d\u529b\u6c14\u90fd\u7528\u4e0d\u4e0a\u6765\uff0c\u53ea\u6709\u773c\u7741\u7741\u5730\u8eba\u5728\u5e8a\u4e0a\uff0c\u53c8\u6709\u4ec0\u4e48\u522b\u7684\u6cd5\u5b50\uff1f \u3000\u3000\u8fd9\u4e24\u4e2a\u6c49\u5b50\u563b\u563b\u54c8\u54c8\u5730\u626f\u7740\u95f2\u7bc7\uff0c\u6709\u8bb8\u591a\u8bdd\u6559\u8427\u5357\u82f9\u542c\u4e86\uff0c\u6068\u4e0d\u80fd\u5c06\u8fd9\u4e24\u4eba\u7684\u820c\u5934\u9f50\u6839 \u5207\u53bb\uff0c\u4f46\u8fd9\u4e24\u4e2a\u5e74\u8f7b\u800c\u8f7b\u8584\u7684\u6c49\u5b50\u5f53\u7136\u77e5\u9053\uff0c\u8fd9\u6c5f\u6e56\u4e0a\u7d20\u79f0\u62db\u60f9\u4e0d\u5f97\u7684\u6f47\u6e58\u5983\u5b50\uff0c\u6b64\u65f6\u6839\u672c \u65e0\u80fd\u4e3a\u529b\uff0c\u662f\u4ee5\u8bdd\u8d8a\u8bf4\u8d8a\u4e0d\u50cf\u8bdd\uff0c\u7b11\u7684\u58f0\u97f3\u4e5f\u8d8a\u6765\u8d8a\u5927\u3002 \u3000\u3000\u800c\u8427\u5357\u82f9\u5462\uff0c\u6b64\u65f6\u53ea\u8981\u8fd9\u4e24\u4e2a\u6c49\u5b50\u4e0d\u5411\u81ea\u5df1\u52a8\u624b\u52a8\u811a\uff0c\u5979\u5df2\u8c22\u5929\u8c22\u5730\u4e86\uff0c\u6b64\u5916\uff0c\u5979\u60f3\u4e0d \u542c\u4eba\u5bb6\u7684\u8bdd\uff0c\u5374\u4e5f\u6ca1\u6709\u529e\u6cd5\u3002 \u3000\u3000\u5979\u53ea\u6709\u53bb\u60f3\u4f0a\u98ce\uff0c\u56e0\u4e3a\u53ea\u6709\u60f3\u5230\u4ed6\u65f6\uff0c\u624d\u80fd\u5fd8\u8bb0\u4e00\u4e9b\u70e6\u607c\u3002\u7136\u800c\uff0c\u53e6\u4e00\u4e9b\u70e6\u607c\uff0c\u5374\u53c8\u968f \u7740\u4f0a\u98ce\u7684\u5f71\u5b50\uff0c\u6d8c\u8fd1\u5979\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u5149\u7ebf\u6108\u6765\u6108\u6697\uff0c\u5979\u77e5\u9053\u5929\u5df2\u7ecf\u9ed1\u4e86\u3002 \u3000\u3000\u5c11\u65f6\uff0c\u623f\u91cc\u638c\u4e0a\u706f\uff0c\u4f46\u4e03\u6d77\u6e14\u5b50\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u4ecd\u7136\u6ca1\u6709\u56de\u6765\u3002\u5df4\u7ed3\u5730\u5e97\u5c0f\u4e8c\uff0c\u53c8\u9001\u6765\u4e9b \u9152\u83dc\uff0c\u8427\u5357\u82f9\u95ed\u8d77\u773c\u775b\uff0c\u5fc3\u91cc\u66f4\u4e71\u4e86\u3002 \u3000\u3000\u7a81\u5730\uff0c\u5979\u80a9\u5934\u88ab\u4eba\u63a8\u4e86\u4e00\u4e0b\uff0c\u7741\u773c\u5904\uff0c\u4e00\u4e2a\u6c49\u5b50\u6b63\u563b\u76ae\u7b11\u8138\u5730\u671b\u7740\u5979\u7b11\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u5403\u4e0d\u5403\u996d\u5440\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u6447\u4e86\u6447\u5934\uff0c\u53c8\u95ed\u8d77\u773c\u775b\u3002\u90a3\u6c49\u5b50\u563b\u563b\u54c8\u54c8\u5730\u7b11\u7740\uff0c\u8d70\u4e86\u56de\u53bb\u3002\u63a5\u7740\u8427\u5357\u82f9\u542c\u5230\u4ed6 \u4eec\u731c\u62f3\u7684\u58f0\u97f3\uff0c\u60f3\u5fc5\u662f\u8fd9\u4e24\u4e2a\u6c49\u5b50\uff0c\u5df2\u5728\u559d\u7740\u9152\u4e86\u3002 \u3000\u3000\u4e00\u4f1a\u513f\uff0c\u8fd9\u4e2a\u6c49\u5b50\u53c8\u5531\u8d77\u5c0f\u8c03\u6765\uff0c\u53ea\u542c\u90a3\u6c49\u5b50\u6015\u7740\u684c\u5b50\u5531\u9053\uff1a \u3000\u3000\u201c\u78a7\u7eb1\u7a97\u5916\u9759\u65e0\u4eba\uff0c\u8dea\u4e0b\u8eab\u6765\u5fd9\u8981\u4eb2\uff0c\u9a82\u4e86\u8366\u8d1f\u5fc3\u56de\u6301\u8eab\uff0c\u54ce\u54df\u54df\uff0c\u5176\u5b9e\u5440\uff0c\u662f\u534a\u513f\u63a8 \u8f9e\u4e00\u534a\u513f\u80af\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u5fc3\u91cc\u4e71\u5f97\u50cf\u662f\u5979\u81ea\u5df1\u6b64\u523b\u7684\u5934\u53d1\u4f3c\u7684\u3002\u5ffd\u5730\uff0c\u5979\u55c5\u5230\u4e00\u9635\u6251\u9f3b\u7684\u9152\u6c14\uff0c\u4e00\u9897\u5fc3\u7acb \u523b\u8df3\u5230\u8154\u53e3\uff0c\u7741\u773c\u4e00\u770b\uff1a \u3000\u3000\u4e00\u5f20\u7ea2\u5f97\u5192\u6c57\u7684\u8138\uff0c\u6b63\u5e26\u7740\u91ba\u4eba\u7684\u9152\u6c14\uff0c\u671d\u81ea\u5df1\u8138\u513f\u51d1\u4e86\u4e0a\u6765\uff0c\u5634\u91cc\u4ecd\u7136\u5728\u54fc\u54fc\u54c8\u54c8\uff0c \u80e1\u8a00\u4e71\u8bed\u7740\uff1a \u3000\u3000\u201c\u6211\u770b\u4f60\u5440\uff0c\u5c0f\u59b9\u5b50\uff01\u4f60\u4e5f\u662f\u4e00\u534a\u513f\u63a8\u8f9e\u4e00\u534a\u513f\u80af\u54df\uff01\u201d \u3000\u3000\u53e6\u4e00\u4eba\u54c8\u54c8\u602a\u7b11\u7740\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff01\u4f60\u6709\u79cd\uff01\u4e0d\u6015\u7b49\u4f1a\u97e6\u9999\u4e3b\u5207\u4e0b\u4f60\u7684\u8111\u888b\uff0c\u6211\u5440\u2026\u2026\u201d \u3000\u3000\u4ed6\u54c8\u54c8\u602a\u7b11\u4e00\u58f0\uff01 \u3000\u3000\u201c\u6211\u5440\uff01\u9119\u4e5f\u6709\u70b9\u71ac\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u6b64\u523b\u6b63\u50cf\u662f\u4e07\u4e08\u6d2a\u6d41\u7684\u6eba\u8005\uff0c\u773c\u770b\u90a3\u5f20\u8138\u6108\u51d1\u6108\u8fd1\uff0c\u5979\u60f3\u4f38\u624b\u53bb\u63a8\uff0c\u53c8\u60f3\u4f38\u811a\u53bb \u8e22\uff0c\u4f46\u8fd9\u5f20\u8138\uff0c\u5374\u5df2\u5c06\u51d1\u5230\u5979\u8138\u4e0a\u4e86\u3002 \u3000\u3000\u8fd9\u65e0\u52a9\u7684\u5c11\u5973\uff0c\u53c8\u6709\u8c01\u6765\u6551\u5979\u5462\uff1f \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u56db\u5341\u516b\u7ae0\u3000\u60c5\u601d\u9036\u8fe4>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u56db\u5341\u516b\u7ae0\u3000\u60c5\u601d\u9036\u8fe4 \u3000\u3000\u72b9\u6709\u8202\u5bd2\u3002 \u3000\u3000\u662f\u4ee5\u8427\u5357\u82f9\u6b64\u523b\u7a7f\u7740\u7684\uff0c\u4ecd\u662f\u539a\u91cd\u7684\u8863\u88f3\uff0c\u4f46\u2014\u2014 \u3000\u3000\u201c\u5636\u2014\u2014\u201d\u5730\u4e00\u58f0\uff0c\u5979\u7684\u524d\u895f\uff0c\u4ecd\u7136\u88ab\u6495\u5f00\u4e86\u3002\u5728\u8fd9\u4e00\u77ac\u606f\uff0c\u5979\u7684\u5fc3\u50cf\u662f\u88ab\u4eba\u523a\u4e00\u4e86\u5251 \u4f3c\u7684\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u5c06\u8981\u53d1\u751f\u7684\u4e8b\u3002 \u3000\u3000\u602a\u7b11\u58f0\uff0c\u50cf\u662f\u67ad\u9e1f\u7684\u591c\u557c\uff0c\u53c8\u50cf\u662f\u72c2\u72ac\u7684\u6625\u5420\uff0c\u5728\u5979\u8033\u4e2d\uff0c\u6df7\u6742\u6210\u4e00\u79cd\u96be\u4ee5\u5fcd\u53d7\u7684\u58f0 \u97f3\u3002 \u3000\u3000\u7136\u800c\uff0c\u5c31\u5728\u8fd9\u53ef\u6015\u7684\u4e8b\u60c5\u5c06\u8981\u53d1\u751f\uff0c\u5374\u6ca1\u6709\u53d1\u751f\u7684\u4e00\u5239\u90a3\u91cc\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u6df7\u4e71\u7684\u7b11\u58f0\uff0c\u50cf\u51b0\u4e00\u6837\u5730\u51dd\u7ed3\u4f4f\u4e86\uff0c\u63a5\u7740\u662f\u4e00\u58f0\u60e8\u55e5\u3002 \u3000\u3000\u8427\u5357\u82f9\u4e3a\u8fd9\u7a81\u751f\u7684\u53d8\u6545\uff0c\u7741\u5f00\u773c\u775b\u6765\uff0c\u773c\u524d\u90a3\u7ea2\u5f97\u5192\u6c57\u7684\u8138\uff0c\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u5979\u76ee\u5149\u4e00 \u77ac\uff0c\u4e00\u6761\u82f1\u633a\u7684\u4eba\u5f71\uff0c\u6b63\u4e00\u638c\u5288\u5728\u53e6\u4e00\u6761\u6c49\u5b50\u7684\u5934\u4e0a\u3002\u90a3\u5e74\u8f7b\u800c\u8f7b\u8584\u7684\u6c49\u5b50\uff0c\u4e5f\u60e8\u55e5\u4e86\u4e00 \u58f0\uff0c\u968f\u7740\u4ed6\u7684\u540c\u4f34\u6b7b\u4e86\u3002 \u3000\u3000\u8427\u5357\u82f9\u72c2\u559c\u7740\uff0c\u90a3\u82f1\u633a\u7684\u4eba\u5f71\u4e00\u56de\u5934\uff0c\u4e00\u5f20\u5979\u6240\u719f\u6089\u7684\u9762\u5b54\uff0c\u4fbf\u7acb\u523b\u6d8c\u73b0\u5728\u5979\u773c\u91cc\u3002\u5979 \u6b64\u523b\u82e5\u4e0d\u662f\u7a74\u9053\u88ab\u70b9\uff0c\u6015\u4e0d\u7acb\u523b\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u5979\u6b64\u523b\u8fde\u4e00\u4e1d\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u5979\u53ea\u80fd\u8f7b\u5fae\uff0c\u4f46\u5374\u72c2\u559c\u7684\u558a\u4e86\u58f0\uff1a \u3000\u3000\u201c\u5357\u54e5\u54e5\uff01\u201d \u3000\u3000\u8fd9\u4e09\u4e2a\u5b57\u50cf\u662f\u4e00\u7ae0\u6781\u5176\u7f8e\u4e3d\u7684\u66f2\u8bcd\uff0c\u60a0\u7136\u800c\u6f3e\uff0c\u7136\u800c\u53c8\u6536\u675f\u5728\u201c\u5357\u54e5\u54e5\u201d\u4e09\u4e2a\u5b57\u4e0a\u3002 \u3000\u3000\u5979\u770b\u5230\u201c\u5357\u54e5\u54e5\u201d\u5e26\u7740\u4e00\u8138\u7b11\u5bb9\u63a0\u5230\u5979\u5e8a\u524d\uff0c\u5979\u770b\u5230\u201c\u5357\u54e5\u54e5\u201d\u7684\u773c\u775b\uff0c\u770b\u7740\u81ea\u5df1\u7684\u80f8 \u524d\u3002 \u3000\u3000\u5f53\u7136\uff0c\u5979\u77e5\u9053\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff0c\u5979\u867d\u7136\u4e5f\u6709\u4e9b\u7f9e\u6da9\uff0c\u4f46\u662f\u5979\u5374\u6beb\u4e0d\u6124\u6012\u3002\u5973\u5b50\u88ab\u5979\u6240\u7231\u7684\u4eba \u770b\u7740\u81ea\u5df1\u7684\u8eab\u5b50\uff0c\u7eb5\u7136\u90a3\u662f\u5728\u4e00\u4e2a\u5e76\u4e0d\u9002\u5f53\u7684\u60c5\u51b5\u4e0b\uff0c\u53ef\u4e5f\u662f\u4ec5\u6709\u7f9e\u6da9\u800c\u65e0\u4e0d\u5feb\u7684\u3002 \u3000\u3000\u7f9e\u6da9\u4e4b\u4e2d\uff0c\u5979\u7684\u5fc3\u8df3\u52a0\u5feb\u4e86\uff0c\u56e0\u4e3a\u201c\u5357\u54e5\u54e5\u201d\u5df2\u4f38\u51fa\u624b\uff0c\u4e3a\u81ea\u5df1\u62c9\u4e0a\u80f8\u524d\u655e\u5f00\u7684\u8863\u895f\uff0c \u90a3\u53ef\u7231\u53c8\u53ef\u6068\u7684\u7b11\u5bb9\u5440\u2014\u2014 \u3000\u3000\u5979\u7684\u8138\u7ea2\u4e86\uff0c\u6b63\u60f3\u95ee\u201c\u5357\u54e5\u54e5\u201d\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff0c\u4f46\u662f\u201c\u5357\u54e5\u54e5\u201d\u7684\u8138\u2014\u2014\u4ed6\u8fd8\u6ca1\u6709\u5c06\u81ea\u5df1 \u4e3a\u4ed6\u6613\u5bb9\u7684\u5316\u88c5\u62ff\u6389\uff0c\u2014\u2014\u5374\u7a81\u7136\u53d8\u4e86\u3002 \u3000\u3000\u5979\u5f53\u7136\u4e5f\u968f\u7740\u4e00\u60ca\uff0c\u51dd\u795e\u542c\u5904\uff0c\u539f\u6765\u95e8\u5916\u5df2\u54cd\u8d77\u90a3\u4e03\u6d77\u6e14\u5b50\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4e8e\u662f\u5979\u53c8\u60f6\u6050 \u7684\u4f4e\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u5357\u54e5\u54e5\u201d \u3000\u3000\u4f46\u662f\u5979\u8fd9\u4e09\u4e2a\u5b57\u8fd8\u6ca1\u6709\u5b8c\u5168\u5524\u51fa\u6765\uff0c\u201c\u5357\u54e5\u54e5\u201d\u7684\u624b\uff0c\u5df2\u63a9\u4f4f\u5979\u7684\u5634\u5df4\uff0c\u53e6\u4e00\u53ea\u624b\u5374\u6284 \u8d77\u5979\u7684\u8170\u80a2\uff0c\u5c06\u5979\u62e6\u8170\u62b1\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7136\u540e\uff0c\u6538\u731b\u4e00\u957f\u8eab\uff0c\u811a\u5c16\u987f\u5904\uff0c\u500f\u7136\u4ece\u7a97\u4e2d\u7a7f\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8427\u5357\u82f9\u53ea\u89c9\u5f97\u81ea\u5df1\u5728\u5979\u7684\u201c\u5357\u54e5\u54e5\u201d\u90a3\u5f3a\u800c\u6709\u529b\u7684\u81c2\u5f2f\u91cc\uff0c\u90a3\u79cd\u611f\u89c9\u662f\u65e0\u4e0e\u4f26\u6bd4\u7684\u7f8e \u5999\uff01 \u3000\u3000\u867d\u7136\u4ed6\u6b63\u4ee5\u4e00\u79cd\u8d77\u4e8e\u5bfb\u5e38\u7684\u901f\u5ea6\uff0c\u5411\u524d\u98de\u63a0\u7740\uff0c\u800c\u4f7f\u631f\u5728\u4ed6\u81c2\u5f2f\u91cc\u7684\u8427\u5357\u82f9\uff0c\u6709\u4e00\u79cd\u6655 \u7729\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5728\u8427\u5357\u82f9\u5fc3\u91cc\uff0c\u8fd9\u79cd\u6655\u7729\u7684\u611f\u89c9\uff0c\u5374\u50cf\u662f\u81ea\u5df1\u8eba\u5728\u5929\u9e45\u7ed2\u7684\u90a3\u4e48\u67d4\u8f6f\u7684\u5e8a\u4e0a\u4f3c \u7684\uff0c\u53ea\u662f\u5076\u5c14\u53d1\u51fa\u4e00\u4e24\u58f0\u5e78\u798f\u7684\u547b\u541f\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u9053\u4ed6\u98de\u63a0\u4e86\u591a\u4e45\uff0c\u8427\u5357\u82f9\u611f\u89c9\u5230\u81ea\u5df1\u5df2\u4e0a\u4e86\u4e00\u5ea7\u5c71\uff0c\u53c8\u8fdb\u4e86\u4e00\u4e2a\u6811\u6797\u5b50\uff0c\u5979\u770b\u5230 \u4e86\u5730\u4e0a\u7684\u79ef\u96ea\uff0c\u96ea\u4e0a\u7684\u6b8b\u679d\u3002 \u3000\u3000\u201c\u5357\u54e5\u54e5\u4e3a\u4ec0\u4e48\u8981\u8dd1\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u5440\uff01\u201d \u3000\u3000\u5979\u8be2\u95ee\u7740\u81ea\u5df1\uff0c\u4f46\u968f\u5373\u53c8\u4e3a\u81ea\u5df1\u5bfb\u6c42\u7740\u89e3\u7b54\uff0c\u5728\u6b64\u65f6\uff0c\u65e0\u8bba\u662f\u4ec0\u4e48\u89e3\u7b54\uff0c\u4e5f\u90fd\u80fd\u4f7f\u8fd9\u75f4 \u60c5\u7684\u5c11\u5973\u6ee1\u610f\u7684\uff0c\u56e0\u4e3a\u5979\u6b63\u8eba\u5728\u5979\u7231\u7740\u7684\u4eba\u7684\u81c2\u5f2f\u91cc\uff0c\u8fd9\u4e0d\u662f\u6bd4\u4efb\u4f55\u89e3\u7b54\uff0c\u90fd\u8981\u7f8e\u5999\u4e9b\u7684\u4e8b \u5b9e\u5417\uff1f \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u505c\u4e0b\u6765\u4e86\u3002\u8427\u5357\u82f9\u5f20\u5f00\u521a\u521a\u95ed\u4e0a\u7684\u773c\u775b\uff0c\u770b\u5230\u81ea\u5df1\u5df2\u7ecf\u7f6e\u8eab\u5728\u4e00\u4e2a\u6d1e\u7a9d\u91cc\uff0c\u4e8e \u662f\uff0c\u5979\u4e0d\u7981\u53c8\u6709\u4e9b\u5947\u602a\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u5947\u602a\u7684\u611f\u89c9\uff0c\u662f\u90a3\u4e48\u5fae\u5f31\uff0c\u6bd4\u4e0d\u4e0a\u5979\u5fc3\u4e2d\u559c\u60a6\u662f\u4e07\u5206\u4e4b\u201c\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5979\u88ab\u5b89\u5b89\u7a33\u7a33\u5730\u653e\u5728\u5730\u4e0a\uff0c\u5440\uff0c\u4e0d\u662f\u5730\u4e0a\uff0c\u800c\u662f\u5e8a\u4e0a\uff0c\u5e8a\u4e0a\u8fd8\u6709\u6e29\u8f6f\u7684\u68c9\u8925\uff0c\u57ab \u5728\u4e0b\u9762\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b.\u2026\u2026\u201d \u3000\u3000\u4f46\u662f\u201c\u5357\u54e5\u54e5\u201d\u6ee1\u5e26\u7b11\u5bb9\u7684\u8138\uff0c\u53c8\u6d6e\u73b0\u5728\u5979\u9762\u524d\u4e86\uff0c\u5149\u7ebf\u867d\u9eef\u5f97\u4fbf\u5979\u770b\u4e0d\u6e05\u4ed6\u8138\u4e0a\u7684\u7b11 \u5bb9\uff0c\u4f46\u662f\u90a3\u6e29\u6696\u7684\u7b11\u610f\uff0c\u5979\u5374\u611f\u89c9\u5f97\u5230\u3002 \u3000\u3000\u60f3\u4e0d\u5230\uff0c\u5979\u7ec8\u65e5\u6240\u4f01\u6c42\u7684\u4e8b\uff0c\u5374\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\u8fbe\u5230\u4e86\u3002 \u3000\u3000\u5979\u5e78\u798f\u5730\u53c8\u4f4e\u5524\u7740\uff1a \u3000\u3000\u201c\u5357\u54e5\u54e5\u201d\u8170\u95f4\u4e00\u677e\uff0c\u5979\u7684\u7a74\u9053\u867d\u7136\u88ab\u89e3\u5f00\u4e86\uff0c\u7136\u800c\u5979\u66f4\u8f6f\u8f6f\u5730\u6ca1\u6709\u529b\u6c14\uff0c\u6b64\u60c5\u6b64\u666f\uff0c \u5979\u53c8\u80fd\u8bf4\u4ec0\u4e48\u8bdd\u5462\uff1f\u4e8e\u662f\uff0c\u5e78\u798f\u53d8\u4e3a\u75db\u82e6\uff0c\u75db\u82e6\u53d8\u4e3a\u5e78\u798f\uff0c\u5e78\u798f\u7740\u7684\u75db\u82e6\uff0c\u75db\u82e6\u7740\u7684\u5e78\u798f\uff0c \u4e16\u4e8b\u9065\u8fdc\u4e86\uff0c\u4e16\u4e8b\u6df7\u6c8c\u4e86\uff0c\u8ff7\u4e71\u4e86 \u3000\u3000\u5929\u4e5f\u4eae\u4e86\u3002 \u3000\u3000\u8427\u5357\u82f9\u5a07\u6175\u5730\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5440\uff01\u5979\u90a3\u8eab\u65c1\u7684\u4eba\u89c1\u5374\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u5979\u63c9\u4e00\u63c9\u773c\u775b\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u8fd9\u662f\u4e00\u4e2a\u52a0\u8fc7\u4eba\u5de5\u7684\u5c71\u6d1e\uff0c\u4f46\u662f\uff0c\u5c71\u6d1e\u91cc\u5374\u662f\u7a7a\u6d1e\u6d1e\u7684\uff0c\u8fde \u534a\u4e2a\u4eba\u7684\u5f71\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u96be\u9053\u662f\u4e2a\u68a6\uff1f\u201d \u3000\u3000\u5979\u8df3\u4e86\u8d77\u6765\uff0c\u53c8\u75db\u82e6\u5730\u8f7b\u8f7b\u76b1\u4e86\u76b1\u7709\uff0c\u66ff\u81ea\u5df1\u4e0b\u4e86\u4e2a\u51b3\u5b9a\uff1a \u3000\u3000\u201c\u4e0d\u662f\u68a6\u5440\u3002\u201d \u3000\u3000\u56e0\u4e3a\u6628\u591c\u7684\u8ff7\u4e71\u6e29\u99a8\u7684\u8ff7\u4e71\uff0c\u6b64\u523b\u4ecd\u7559\u5728\u5979\u7684\u5fc3\u5e95\uff0c\u5979\u8bb0\u5f97\uff0c\u975e\u5e38\u6e05\u695a\u7684\u8bb0\u65e5\u5bf8\u3002 \u3000\u3000\u53ea\u662f\u5728\u8fd9\u79cd\u8ff7\u4e71\u4e4b\u4e2d\uff0c\u5357\u54e5\u54e5\u66fe\u7ecf\u95ee\u8fc7\u5979\u4ec0\u4e48\u8bdd\uff0c\u548c\u5979\u81ea\u5df1\u56de\u7b54\u4e86\u4ec0\u4e48\uff0c\u5979\u5374\u5df2\u5fd8\u8bb0 \u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u662f\u65e0\u8db3\u8f7b\u91cd\u7684\uff0c\u56e0\u4e3a\u522b\u7684\u4e8b\uff0c\u8fdc\u6bd4\u8fd9\u4e9b\u8bdd\u91cd\u8981\u5f97\u591a\u3002 \u3000\u3000\u201c\u6216\u8005\u4ed6\u51fa\u53bb\u4e86\uff0c\u6216\u8005\u4ed6\u53bb\u4e3a\u6211\u627e\u5bfb\u98df\u7269\u53bb\u4e86\uff0c\u4ed6\u7acb\u523b\u5c31\u4f1a\u56de\u6765\u7684\u3002\u5440\uff01\u591a\u4e48\u5947\u5999\uff01\u539f \u6765\u4eba\u95f4\u6b22\u4e50\uff0c\u662f\u6bd4\u75db\u82e6\u591a\u4e9b\u3002\u201d \u3000\u3000\u5979\u5b89\u6170\u7740\u81ea\u5df1\uff0c\u53c8\u5a07\u6175\u5730\u5012\u5728\u5e8a\u4e0a\uff0c\u90a3\u662f\u4e00\u5f20\u77f3\u5e8a\u3002\u8fd9\u5c71\u6d1e\u91cc\u9664\u4e86\u8fd9\u77f3\u5e8a\u4e4b\u5916\uff0c\u8fd8\u6709\u7740 \u4e00\u5f20\u77f3\u684c\u5b50\uff0c\u8fd8\u6709\u7740\u4e00\u4e9b\u96f6\u4e71\u7684\u4ec0\u7269\u3002 \u3000\u3000\u201c\u8fd9\u4e5f\u8bb8\u662f\u4ed6\u5728\u907f\u4ec7\u65f6\u4e3a\u81ea\u5df1\u5e03\u7f6e\u7684\u5c71\u6d1e\u5427\uff01\u4ed6\u662f\u4e2a\u591a\u4e48\u5947\u5999\u7684\u4eba\uff0c\u6211\u53ea\u8981\u80fd\u548c\u4ed6\u5728\u4e00 \u8d77\uff0c\u7eb5\u7136\u7ec8\u65e5\u4f4f\u5728\u8fd9\u5c71\u6d1e\u91cc\uff0c\u6211\u4e5f\u9ad8\u5174\u3002\u201d \u3000\u3000\u5979\u60c5\u601d\u5982\u6d41\u6c34\uff0c\u56de\u8f6c\u66f2\u6298\uff0c\u65f6\u95f4\u4f7f\u4e5f\u5728\u8fd9\u9036\u8fe4\u7684\u60c5\u601d\u91cc\uff0c\u6d88\u78e8\u4e86\u8fc7\u53bbu \u3000\u3000\u65f6\u95f4\u5728\u7b49\u5f85\u4e2d\u867d\u7136\u7f13\u6162\uff0c\u4f46\u5374\u7ec8\u4e8e\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u6e10\u6e10\u5730\u8427\u5357\u82f9\u7684\u5fc3\uff0c\u7531\u6e29\u99a8\u800c\u53d8\u4e3a\u7126\u6025\uff0c\u7531\u7126\u6025\u800c\u53d8\u4e3a\u56f0\u60d1\uff0c\u518d\u7531\u56f0\u60d1\u800c\u53d8\u4e3a\u60f6\u6050\uff0c\u7136 \u540e\uff0c\u8fd9\u4efd\u60f6\u6050\u53c8\u53d8\u4e3a\u60ca\u60e7\u4e86\uff01 \u3000\u3000\u4e00\u4e9b\u5979\u5728\u72c2\u559c\u4e2d\u6ca1\u6709\u60f3\u5230\u7684\u4e8b\uff0c\u6b64\u523b\u5374\u6765\u5230\u5979\u8111\u6d77\u91cc\u3002\u201c\u4ed6\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u5728\u5ba2\u6808\u91cc\uff01\u4ed6 \u600e\u4e48\u4f1a\u5728\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u8bf4\u7684\u60c5\u51b5\u4e0b\uff0c\u5bf9\u6211\u2026\u2026\u5bf9\u6211\u8fd9\u4e48\u597d\uff1f\u4ed6\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\u5440\uff01\u201d \u3000\u3000\u8427\u5357\u82f9\u7684\u8138\uff0c\u7531\u5ae3\u7ea2\u800c\u53d8\u4e3a\u82cd\u767d\u4e86\uff0c\u751a\u81f3\u5168\u8eab\u8d77\u4e86\u60ca\u6050\u7684\u609a\u6817\uff01 \u3000\u3000\u201c\u5982\u679c\u4ed6\u4e0d\u662f\u5357\u54e5\u54e5\uff0c\u4f1a\u662f\u8c01\u5462\uff1f\u96be\u9053\uff0c\u96be\u9053\u662f\u4ed6\uff01\u201d \u3000\u3000\u201c\u5929\u4e89\u6559\u4e3b\u8427\u65e0\u201d\u8fd9\u51e0\u4e2a\u5b57\uff0c\u5728\u8fd9\u53ef\u601c\u800c\u75f4\u60c5\u7684\u5c11\u5973\u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\uff0c\u5979\u8111\u4e2d\u4e00\u9635\u6655\u7729\uff0c \u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\u81ea\u5df1\u7684\u795e\u667a\u4e86\uff01 \u3000\u3000\u4e00\u7247\u6df7\u6c8c\u4e4b\u4e2d\uff0c\u5979\u597d\u50cf\u770b\u5230\u90a3\u5f20\u8138\uff0c\u98de\u65cb\u7740\uff0c\u5e26\u7740\u6ee1\u8138\u7684\u72de\u7b11\uff0c\u671d\u5979\u538b\u4e86\u4e0b\u6765\uff0c\u90a3\u5f20 \u8138\uff0c\u672c\u662f\u5979\u4eb2\u624b\u5728\u53e6\u4e00\u5f20\u4e0d\u540c\u7684\u8138\u4e0a\u9020\u6210\u7684\u3002 \u3000\u3000\u90a3\u65f6\u5019\uff0c\u53ea\u8981\u5979\u5728\u4e3a\u7740\u4e00\u4e2a\u5979\u6240\u7231\u7740\u7684\u4eba\u6613\u5bb9\u7684\u65f6\u5019\uff0c\u7a0d\u4e3a\u53d8\u52a8\u4e00\u4e0b\u624b\u6cd5\uff0c\u90a3\u4e48\u5bf9\u5979\u6765 \u8bf4\uff0c\u8fd9\u4e16\u754c\u6b64\u523b\u5c31\u662f\u4f1a\u53e6\u4e00\u4e2a\u5b8c\u5168\u4e0d\u540c\u7684\u4e16\u754c\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u4f1a\u60f3\u5230\uff0c\u5728\u8fd9\u53cc\u7ea4\u7ea4\u7389\u624b\u4e4b\u4e0b\uff0c\u4e0d\u4f46\u6539\u53d8\u4e86\u5979\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u6539\u53d8\u4e86\u53e6\u4e00\u4e16\u4e00\u4eba\u7684 \u547d\u8fd0\u3002\u4e5f\u6539\u53d8\u8fd9\u6b66\u6797\u7684\u547d\u8fd0\u3002 \u3000\u3000\u8fd9\u5f20\u8138\uff0c\u5728\u5979\u8111\u6d77\u4e2d\u649e\u51fb\u7740\uff0c\u98de\u65cb\u7740\u3002 \u3000\u3000\u5979\u8ddf\u8dc4\u5730\u722c\u4e86\u8d77\u6765\uff0c\u8ddf\u8dc4\u5730\u7a7f\u4e0a\u8863\u670d\uff0c\u5728\u8fd9\u5df2\u6539\u53d8\u5979\u4e00\u751f\u547d\u8fd0\u7684\u5c71\u6d1e\u91cc\uff0c\u5bfb\u89c6\u4e86\u4e00\u4e0b\uff0c \u7136\u800c\uff0c\u8fd9\u91cc\u5374\u6ca1\u6709\u7559\u4e0b\u4efb\u4f55\u80fd\u4f7f\u5979\u8fa8\u660e\u81ea\u5df1\u6b64\u523b\u6240\u5904\u5730\u4f4d\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5979\u53c8\u8ddf\u8dc4\u7740\u8d70\u4e86\u51fa\u53bb\uff0c\u6d1e\u5916\u8fd8\u6709\u4e00\u6761\u6570\u6587\u957f\u7684\u96a7\u9053\uff0c\u5979\u8ddf\u8dc4\u5730\u8d70\u51fa\u8fd9\u6761\u96a7\u9053\uff0c\u8e52 \u8dda\u5730\u4ece\u88c2\u9699\u4e2d\u722c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6d1e\u5916\u7684\u4e00\u5207\uff0c\u5e76\u6ca1\u6709\u56e0\u5979\u7684\u6539\u53d8\uff0c\u800c\u6709\u4e1d\u6beb\u7684\u6539\u53d8\u3002 \u3000\u3000\u5979\u5728\u79ef\u96ea\u7684\u5c71\u9053\u4e0a\u8ddf\u8dc4\u5730\u8d70\u7740\uff0c\u8eab\u540e\u7559\u4e0b\u4e00\u8fde\u4e32\u51cc\u4e71\u7684\u811a\u5370\u3002 \u3000\u3000\u5979\u6355\u6349\u7740\u8111\u6d77\u4e2d\uff0c\u4e00\u4e9b\u65ad\u7eed\u7684\u6784\u601d\uff1a \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u51fa\u53bb\u627e\u7740\u4e86\u8427\u65e0\u8427\u65e0\u77e5\u9053\u4e86\u6709\u4eba\u548c\u4ed6\u9762\u8c8c\u76f8\u540c\u2014\u2014\u53c8\u77e5\u9053\u6211\u662f\u8fd9\u4eba\u7684\u670b\u53cb\u4e8e\u662f \u4ed6\u4eec\u5c31\u505a\u4e0b\u4e86\u5708\u5957\u3002 \u3000\u3000\u4e00\u4e2a\u4e2a\u7247\u65ad\u51d1\u8d77\u6765\uff0c\u5c31\u53d8\u6210\u4e86\u8fd9\u6b8b\u9177\u7684\u4e8b\u5b9e\uff0c\u8fd9\u6b8b\u9177\u7684\u4e8b\u5b9e\u538b\u5728\u5979\u5fc3\u4e0a\uff0c\u751a\u81f3\u628a\u5979\u7684\u7075 \u9b42\u90fd\u538b\u5f97\u5df2\u69a8\u51fa\u82e6\u6c41\u6765\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5979\u4ecd\u7136\u4f01\u6c42\u7740\uff0c\u76fc\u671b\u7740\uff0c\u5e0c\u671b\u8fd9\u4ec5\u4e0d\u8fc7\u662f\u5979\u7684\u72c2\u60f3\uff0c\u5e0c\u671b\u6628\u591c\u7684\u201c\u4ed6\u201d\u771f\u7684\u662f \u201c\u5357\u54e5\u54e5\u201d\u3002 \u3000\u3000\u8fd9\u4f3c\u4e4e\u5df2\u7ecf\u7edd\u671b\u4e2d\u7684\u5e0c\u671b\uff0c\u6b64\u523b\u5c31\u652f\u6301\u7740\u5979\u7684\u811a\u6b65\uff0c\u4f7f\u8fd9\u672c\u6765\u5a07\u7eb5\u800c\u72e0\u5fc3\uff0c\u8fd9\u53ef\u601c\u800c\u75f4 \u60c5\u7684\u5c11\u5973\uff0c\u80fd\u7ee7\u7eed\u5411\u524d\u9762\u8d70\u7740\u3002\u652f\u6301\u7740\u5979\u865a\u5f31\u7684\u8eab\u8eaf\uff0c\u8fd8\u6ca1\u6709\u5012\u4e0b\u6765\u3002 \u3000\u3000\u4e0a\u5c71\u7684\u65f6\u5019\uff0c\u5979\u662f\u88ab\u80c1\u6301\u5728\u201c\u4ed6\u201d\u7684\u81c2\u5f2f\u91cc\uff0c\u8ff7\u60d8\u800c\u99a8\u6655\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5979\u5728\u5bfb\u89c5\u7740\u4e0b\u5c71\u9014\u5f84\u7684\u65f6\u5019\uff0c\u624d\u77e5\u9053\u8fd9\u5ea7\u5c71\uff0c\u8fdc\u6bd4\u5979\u60f3\u50cf\u4e4b\u4e2d\u8981\u9ad8\u5f97\u591a\uff0c\u79ef\u96ea\u7684 \u5c71\u8def\u5c24\u5176\u96be\u884c\u3002\u5979\u4e0d\u5f97\u4e0d\u6536\u6444\u4e00\u90e8\u4efd\u795e\u667a\uff0c\u63d0\u7740\u6c14\u5411\u4e0b\u9762\u8d70\u7740\uff0c\u6e10\u6e10\uff0c\u5979\u7684\u8eab\u6cd5\u4e0d\u77e5\u4e0d\u77e5\u4e0d \u89c9\u5730\u52a0\u5feb\u4e86\u3002 \u3000\u3000\u4f46\u8d70\u4e86\u4e00\u9635\uff0c\u5979\u5374\u4e0d\u7981\u53c8\u505c\u4f4f\u811a\u6b65\uff0c\u56e0\u4e3a\u6b64\u523b\u5979\u7adf\u53d1\u73b0\u5979\u6240\u91c7\u53d6\u7684\u8fd9\u6761\u5c71\u8def\uff0c\u7adf\u7136\u53c8\u7531 \u4f4e\u800c\u9ad8\uff0c\u524d\u9762\u7adf\u662f\u4e00\u5904\u5c71\u5cf0\u3002 \u3000\u3000\u6709\u4e00\u6761\u5f88\u7a84\u7684\u5c71\u8def\uff0c\u6cbf\u7740\u5cf0\u4fa7\u5411\u540e\u9762\u4f38\u5ef6\u4e86\u8fc7\u53bb\u3002\u4f46\u662f\u56e0\u4e3a\u5979\u770b\u5230\u7684\u4e00\u90e8\u4efd\uff0c\u5e76\u4e0d\u592a \u957f\uff0c\u662f\u4ee5\u5979\u4e0d\u80fd\u4ee5\u6b64\u63a8\u65ad\u8fd9\u6761\u8def\u5411\u4e0a\u884c\uff0c\u6291\u6216\u662f\u5411\u4e0b\u7684\uff0c\u4e8e\u662f\u7ad9\u5728\u8fd9\u5c71\u5cf0\u524d\uff0c\u5979\u6014\u4e86\u534a\u664c\u3002 \u3000\u3000\u5979\u6b64\u523b\u82e5\u662f\u5fc3\u795e\u5b89\u5b9a\u800c\u4f53\u529b\u5145\u6c9b\u7684\uff0c\u90a3\u4e48\uff0c\u5979\u4e00\u5b9a\u5c31\u4f1a\u4ece\u524d\u9762\u7684\u90a3\u6761\u8def\u8d70\u8fc7\u53bb\uff0c\u5373\u4f7f\u90a3 \u6761\u8def\u662f\u4e0a\u884c\u7684\uff0c\u5979\u4e5f\u4f1a\u63a2\u6d4b\u4e00\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u5979\u6b64\u523b\u5374\u662f\u5fc3\u795e\u8ff7\u60d8\uff0c\u4f53\u529b\u52b3\u7601\u3002 \u3000\u3000\u4e8e\u662f\u5979\u53ea\u6709\u53f9\u606f\u4e00\u58f0\uff0c\u5f80\u56de\u5934\u8d70\u53bb\u3002\u4f46\u5979\u672c\u8eab\u662f\u201c\u4e0b\u5c71\u201d\u7684\uff0c\u6b64\u523b\u4e00\u56de\u5934\uff0c\u5374\u53c8\u662f\u6e10\u884c \u6e10\u4e0a\u3002 \u3000\u3000\u8fd9\u5176\u4e2d\u4f3c\u4e4e\u53c8\u5305\u6db5\u7740\u4ec0\u4e48\u54f2\u7406\uff0c\u4f46\u662f\uff0c\u5979\u5374\u6ca1\u6709\u8fd9\u4efd\u5fc3\u60c5\u53bb\u63a8\u7a76\u5b83\uff0c\u56e0\u4e3a\u4f53\u529b\u7684\u4e0d\u652f\uff0c \u4f7f\u5979\u7684\u811a\u6b65\u53c8\u653e\u7f13\u4e86\uff1a\u4f46\u6628\u591c\u6240\u53d1\u751f\u7684\u90a3\u4e9b\u201c\u4ee4\u5979\u5fc3\u788e\u201d\u7684\u4e8b\uff0c\u53c8\u5982\u6f6e\u5730\u6d8c\u56de\u5979\u7834\u788e\u7684\u5fc3 \u91cc\u3002 \u3000\u3000\u54d7\u2026\u2026 \u3000\u3000\u5ffd\u5730\u4e00\u4e2a\u58f0\u97f3\uff0c\u4f7f\u5979\u7684\u5fc3\u60c5\uff0c\u84e6\u7136\u4ece\u8ff7\u7f51\u4e2d\u60ca\u9192\u4e86\uff0c\u8fd9\u58f0\u97f3\u662f\u8fd9\u4e48\u719f\u6089\uff0c\u5979\u8fde\u5fd9\u505c\u4e0b\u811a \u53bb\u6355\u6349\u5b83\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8fd9\u58f0\u97f3\u672c\u5c31\u6765\u5f97\u975e\u5e38\u9065\u8fdc\uff0c\u6b64\u523b\u66f4\u5df2\u6e3a\u7136\uff0c\u5979\u51dd\u795e\u503e\u542c\u4e86\u534a\u664c\uff0c\u6700\u540e\uff0c\u7ec8\u4e8e\u4e00\u54ac \u7259\uff0c\u671d\u90a3\u58f0\u97f3\u7684\u6765\u5904\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5979\u7684\u7cbe\u529b\u4f3c\u4e4e\u5df2\u6062\u590d\u4e86\uff0c\u539f\u6765\u65b9\u624d\u5979\u6240\u542c\u5230\u7684\u90a3\u58f0\u97f3\uff0c\u4f3c\u4e4e\u662f\u5c5e\u4e8e\u201c\u5357\u54e5\u54e5\u201d \u7684\uff0c\u800c\u5047\u5982\u201c\u5357\u54e5\u54e5\u201d\u771f\u7684\u5728\u8fd9\u5c71\u91cc\uff0c\u90a3\u4e48\u4e0d\u5c31\u53ef\u4ee5\u8bc1\u660e\u6628\u591c\u7684\u201c\u4ed6\u201d\uff0c\u201d\u8d1d\u662f\u201c\u5357\u54e5\u54e5\u201d \u4e86\u5417\uff1f \u3000\u3000\u90a3\u4e48\uff0c\u5979\u81ea\u5df1\u65b9\u624d\u6709\u5173\u6b64\u4e8b\u7684\u90a3\u4e9b\u4e0d\u5e78\u7684\u63a8\u6d4b\uff0c\u5c31\u53d8\u5f97\u6781\u5176\u53ef\u7b11\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u591a\u4e48\u503c\u5f97\u5979\u72c2\u559c\u7684\u4e8b\uff01\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u7eb5\u7136\u8fd9\u58f0\u97f3\u662f\u6765\u81ea\u5929\u8fb9\uff0c\u5979\u4e5f\u4f1a\u53bb\u8ffd\u5bfb \u7684\uff1a\u7eb5\u7136\u5979\u53cc\u811a\u5df2\u4e0d\u80fd\u884c\u52a8\uff0c\u90a3\u4e48\u5979\u5373\u4f7f\u722c\u7740\u4e5f\u4f1a\u722c\u4e86\u53bb\u7684\u3002\u4f55\u51b5\u5979\u6b64\u523b\u8fd8\u80fd\u98de\u63a0\u5462\uff1f \u3000\u3000\u5c71\u8def\u7684\u4e24\u65c1\uff0c\u662f\u5df2\u67af\u51cb\u7684\u6811\u6797\uff0c\u4f46\u6797\u6728\u5374\u6781\u5bc6\uff0c\u4e0b\u9762\u662f\u6e17\u5408\u7740\u5df2\u6eb6\u7684\u96ea\u6c34\uff0c\u6b8b\u8d25\u7684\u67af \u679d\uff0c\u548c\u4e00\u4e9b\u672a\u6eb6\u7684\u51b0\u96ea\u7684\u6ce5\u5730\u3002\u5979\u8270\u96be\u5730\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u63a0\u884c\u7740\uff0c\u641c\u5bfb\u7740\uff0c\u5728\u7ecf\u8fc7\u4e00\u8fde\u4e32\u56f0\u82e6 \u7684\u6512\u884c\u540e\uff0c\u7ec8\u4e8e\uff0c\u5979\u53d1\u73b0\u4e86\u4e00\u4ef6\u5979\u5b81\u53ef\u727a\u7272\u4e00\u751f\u7684\u5e78\u798f\uff0c\u751a\u81f3\u5979\u7684\u751f\u547d\u6765\u6362\u53d6\u7684\u4e8b\u4ed9\u201c\u54ce\u7a7a \u5345\u95f7 \u3000\u3000\u8427\u5357\u82f9\u5728\u7edd\u671b\u4e2d\u6355\u6349\u4e86\u4e00\u4e1d\u5e0c\u671b\uff0c\u5979\u5c31\u4e0d\u987e\u4e00\u5207\u5730\u671d\u8fd9\u5e0c\u671b\u8ffd\u5bfb\u4e86\u53bb\u3002 \u3000\u3000\u67af\u6797\u7684\u5149\u7ebf\uff0c\u968f\u7740\u811a\u6b65\u7684\u5f80\u5185\u884c\u4e00\u6b65\uff0c\u800c\u53d8\u5f97\u8d8a\u53d1\u91cc\u6697\u3002\u5230\u4e86\u540e\u6765\uff0c\u6797\u4e2d\u7adf\u7136\u624e\u679d\u76d8 \u7ea0\uff0c\u65e5\u5149\u60f3\u5fc5\u5df2\u88ab\u5c71\u5cf0\u6321\u4f4f\uff0c\u5979\u867d\u7136\u81ea\u5e7c\u7ec3\u6b66\uff0c\u76ee\u529b\u81ea\u7136\u5f02\u4e8e\u5e38\u4eba\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u653e\u7f13\u4e86\u6b65 \u5b50\u3002 \u3000\u3000\u4e00\u79cd\u9634\u6697\u6f6e\u6e7f\u7684\u9709\u5473\uff0c\u4f7f\u5f97\u5fc3\u91cc\u5927\u7ffb\uff0c\u6d8c\u8d77\u4e00\u9635\u60f3\u5410\u7684\u611f\u89c9\u3002 \u3000\u3000\u5979\u8270\u96be\u5730\u5728\u8fd9\u9634\u6666\u7684\u68ee\u6797\u91cc\u6512\u884c\u7740\uff0c\u7eb5\u7136\u5979\u77e5\u9053\u5728\u8fd9\u79cd\u7ec8\u5e74\u4e0d\u89c1\u884c\u4eba\u7684\u5bc6\u6797\u91cc\uff0c\u86c7\u65ed\u6bd2 \u866b\uff0cI\u758b\u7136\u5f88\u591a\uff0c\u8bf4\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\uff0c\u5c31\u4f1a\u7a9c\u51fa\u6765\u54ac\u81ea\u5df1\u4e00\u53e3\uff0c\u4f46\u662f\uff0c\u5979\u4ecd\u7136\u6ca1\u6709\u540e\u6094\u7684\u610f \u601d\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u6709\u5173\u5979\u4e00\u751f\u7684\u5e78\u798f\uff0c\u8fd9\u5bc6\u6797\u4e2d\u867d\u7136\u662f\u9634\u6666\u7684\uff0c\u4f46\u662f\u5979\u5fc3\u91cc\uff0c\u5374\u5df2\u73b0\u51fa\u4e00\u5e45\u6781\u5176 \u5149\u660e\u7684\u56fe\u753b\u3002 \u3000\u3000\u201c\u4eca\u5929\u65e9\u4e0a\uff0c\u5357\u54e5\u54e5\u4e3a\u6211\u51fa\u6765\u627e\u98df\u7269\uff0c\u90a3\u77e5\u5374\u88ab\u9677\u5728\u8fd9\u5bc6\u6797\u91cc\u4e86\uff0c\u5bfb\u4e0d\u7740\u51fa\u8def\uff0c\u65b9\u624d\u6211 \u542c\u5230\u7684\u58f0\u97f3\uff0c\u5c31\u662f\u4ed6\u5728\u8fd9\u5bc6\u6797\u91cc\u7684\u547c\u5524\u3002\u201d \u3000\u3000\u5979\u5e78\u798f\u7684\u601d\u7d22\u7740\uff0c\u867d\u7136\u53c8\u4e0d\u514d\u4e3a\u201c\u5357\u54e5\u54e5\u201d\u62c5\u5fc3\u8d77\u6765\uff01 \u3000\u3000\u201c\u5047\u5982\u6211\u627e\u5230\u4e86\u4ed6\uff0c\u4ed6\u8be5\u591a\u4e48\u9ad8\u5174\u5440\uff01\u6628\u5929\u665a\u4e0a\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u8fd9\u75f4\u60c5\u7684\u5c11\u5973\u8138\u7ea2\u4e86\uff0c\u66f4\u52a0\u52aa\u529b\u5730\u671d\u524d\u9762\u8d70\u4e86\u8fc7\u53bb\uff0c\u5bc6\u6797\u91cc\u7684\u56f0\u963b\u867d\u591a\uff0c\u7136\u800c\uff0c\u5374\u963b\u6b62 \u4e0d\u4e86\u8fd9\u5c11\u5973\u5bfb\u6c42\u5e78\u798f\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u5979\u4f3c\u4e4e\u53c8\u542c\u5230\u4e00\u8fde\u4e32\u9690\u7ea6\u7684\u4eba\u58f0\uff0c\u4ece\u53f3\u9762\u98d8\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u4e0d\u7981\u6697\u81ea\u5e86\u5e78\uff0c\u81ea\u5e7c\u81f3\u4eca\u7684\u8bad\u7ec3\uff0c\u4f7f\u5979\u6709\u8fd9\u5f02\u4e8e\u5e38\u4eba\u7684\u542c\u89c9\uff0c\u624d\u80fd\u4f7f\u5979\u542c\u5230\u8fd9\u4e9b\uff0c\u4e8e \u662f\u5979\u6beb\u4e0d\u72b9\u8c6b\u5730\u671d\u53f3\u9762\u7ed5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u867d\u7136\u6ca1\u6709\u542c\u6e05\u8fd9\u4eba\u58f0\u662f\u5c5e\u4e8e\u8c01\u7684\uff0c\u4f46\u662f\uff0c\u5728\u8fd9\u79cd\u5bc6\u6797\u4e4b\u4e2d\uff0c\u96be\u9053\u8fd8\u4f1a\u6709\u522b\u4eba\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u524d\u9762\u7684\u4e69\u679d\u7ea0\u7ed3\u66f4\u591a\uff0c\u5979\u53cd\u5f04\u80cc\u540e\uff0c\u60f3\u62bd\u51fa\u80cc\u540e\u80cc\u7740\u7684\u5251\uff0c\u4f46\u4f38\u624b\u53bb\u62bd\u4e86\u4e2a\u7a7a\uff0c\u5979\u4e0d\u7981 \u54d1\u7136\u5931\u7b11\uff0c\u5728\u7ecf\u8fc7\u8fd9\u8bb8\u591a\u5929\u7684\u6ce2\u6298\uff0c\u548c\u6628\u591c\u7684\u90a3\u4ef6\u4e8b\u540e\uff0c\u81ea\u5df1\u80cc\u540e\u7684\u957f\u5251\uff0c\u600e\u4f1a\u8fd8\u5728\u539f\u5904 \u5462\u3002 \u3000\u3000\u4e8e\u662f\u5979\u53ea\u5f97\u7528\u624b\u53bb\u5206\u5f00\u524d\u9762\u7ea0\u7ed3\u7740\u7684\u6811\u679d\uff0c\u8d70\u6ca1\u591a\u8fdc\uff0c\u5ffd\u7136\u53d1\u73b0\u6797\u4e2d\uff0c\u7adf\u6709\u4e00\u6761\u4e0a\u884c\u4e4b \u8def\uff0c\u5bbd\u7ea6\u56db\u5c3a\uff0c\u873f\u8712\u524d\u884c\u3002 \u3000\u3000\u5979\u5728\u8fd9\u8def\u53e3\u8003\u8651\u4e86\u4e00\u4e0b\uff0c\u76ee\u5149\u56db\u626b\uff0c\u770b\u5230\u7acb\u8eab\u4e4b\u5904\uff0c\u524d\u540e\u5de6\u53f3\u90fd\u662f\u5bc6\u6797\u3002\u53ea\u6709\u8fd9\u6761\u8def\uff0c \u4e0a\u9762\u867d\u4ecd\u6728\u679d\u5bc6\u8986\uff0c\u4e24\u65c1\u4e5f\u6709\u6797\u6728\uff0c\u4f46\u8def\u5374\u662f\u5bbd\u4ec4\u5982\u4e00\uff0c\u5730\u4e0a\u8fde\u91ce\u751f\u7684\u6742\u8349\u90fd\u6ca1\u6709\u4ec0\u4e48\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u4e0d\u7981\u4e00\u52a8\uff1a \u3000\u3000\u201c\u8fd9\u6761\u8def\u96be\u9053\u662f\u4eba\u5de5\u5f00\u51fa\u6765\u7684\uff01\u201d \u3000\u3000\u5728\u8fd9\u79cd\u5730\u65b9\u4f1a\u6709\u4eba\u5de5\u5f00\u51fa\u6765\u7684\u8def\uff0c\u4e0d\u662f\u592a\u503c\u5f97\u5947\u602a\u7684\u4e8b\u4e86\u5417\uff01 \u3000\u3000\u4e8e\u662f\u5728\u5979\u5fc3\u91cc\u672c\u5c31\u7d0a\u4e71\u7ea0\u7ed3\u7684\u5404\u79cd\u60c5\u611f\u91cc\uff0c\u6b64\u523b\u53c8\u52a0\u4e86\u4e00\u4efd\u60ca\u5f02\u548c\u5947\u602a\uff0c\u5374\u53c8\u7981\u4e0d\u4f4f\u52a0 \u4e86\u4e00\u4efd\u4eba\u7c7b\u4e0e\u751f\u4ff1\u6765\u7684\u597d\u5947\u4e4b\u5fc3\u3002 \u3000\u3000\u4e8e\u662f\u5979\u8003\u8651\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u5faa\u5f84\u76d8\u5347\u3002 \u3000\u3000\u5979\u8d70\u5f97\u5f88\u5feb\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u4e0a\u63a0\u4e86\u6570\u5341\u4e08\u3002\u4f46\u5728\u8fd9\u79cd\u5730\u65b9\u884c\u8def\uff0c\u5979\u4ecd\u662f\u6781\u4e3a\u5c0f\u5fc3\u7684\uff0c\u76ee \u5149\u6781\u4e3a\u7559\u610f\u5730\u671d\u524d\u9762\u770b\u7740\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u5979\u6781\u5feb\u5730\u987f\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u539f\u6765\u5730\u52bf\u5ffd\u7136\u4e2d\u65ad\uff0c\u524d\u9762\u7edd\u671b\u6df1\u6c89\uff0c\u7adf\u7136\u6df1\u4e0d\u89c1\u5e95\uff0c\u5f62\u52bf\u4e4b\u9669\u6076\uff0c\u4f7f\u5f97\u5979\u4e0d\u7981\u4e3a\u4e4b\u5012\u62bd \u4e00\u53e3\u51c9\u6c14\uff01 \u3000\u3000\u5979\u7684\u5fc3\u53c8\u5f80\u4e0b\u6c89\u4e86\u4e0b\u53bb\uff0c\u6b63\u81ea\u6697\u53f9\u7740\u81ea\u5df1\u7684\u8fd9\u4e00\u756a\u8dcb\u6d89\uff0c\u81f3\u6b64\u5df2\u5168\u90e8\u6210\u7a7a\uff0c\u5e7d\u5e7d\u5730\u957f\u53f9 \u4e86\u4e00\u58f0\uff0c\u4f38\u624b\u53bb\u62ed\u989d\u4e0a\u7684\u6c57\u73e0\u3002 \u3000\u3000\u4f46\u662f\u624b\u4e00\u89e6\u5230\u9762\u989d\uff0c\u5979\u53c8\u500f\u7136\u7f29\u4e86\u56de\u6765\u3002\u539f\u6765\u5979\u6b64\u523b\u624d\u53d1\u89c9\u81ea\u5df1\u90a3\u4e00\u53cc\u624b\u638c\uff0c\u6b64\u523b\u5df2\u662f \u9c9c\u8840\u6dcb\u6f13\uff0c\u663e\u7136\u662f\u65b9\u624d\u81ea\u5df1\u7528\u624b\u53bb\u5206\u5f00\u7ea0\u7ed3\u7684\u6728\u679d\u65f6\uff0c\u6240\u53d7\u7684\u4f24\uff0c\u6b64\u523b\u624d\u89c9\u51fa\u75bc\u75db\u3002 \u3000\u3000\u8fd9\u75f4\u60c5\uff0e\u53ef\u601c\u800c\u65e0\u52a9\u7684\u5c11\u5973\uff0c\u7ad9\u5728\u8fd9\u9634\u5cfb\u51a5\u6c89\u7684\u7edd\u58d1\u4e4b\u524d\uff0c\u4e0d\u81ea\u89c9\u5730\uff0c\u5df2\u6d41\u4e0b\u6cea\u73e0\u4e86\uff01 \u3000\u3000\u6cea\u73e0\uff0c\u6cbf\u7740\u5979\u7684\u9762\u988a\u6d41\u4e0b\u6765\uff0c\u5979\u53cd\u624b\u7528\u624b\u80cc\u53bb\u64e6\u62ed\u4e00\u4e0b\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5979\u53d1\u89c9\u5de6\u4fa7\u4f3c\u6709\u4e00\u6761\u8def\uff0c\u901a\u5f80\u7edd\u58d1\u7684\u90a3\u9762\u3002 \u3000\u3000\u4e8e\u662f\u5979\u7cbe\u795e\u53c8\u81ea\u4e00\u632f\uff0c\u8fde\u5fd9\u7ed5\u4e86\u8fc7\u53bb\uff0c\u524d\u884c\u529b\u4e00\u4e08\uff0c\u76ee\u5149\u524d\u671b\u65f6\uff0c\u5979\u4e0d\u7981\u60ca\u559c\u5f97\u9669\u4e9b\u6655 \u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u5979\u8fd9\u624d\u770b\u51fa\uff0c\u8fd9\u7edd\u58d1\u672c\u662f\u6a2a\u4e98\u534a\u7a7a\u4e2d\uff0c\u5bf9\u9762\u5374\u6709\u4e00\u4e2a\u6781\u5e7f\u5927\u7684\u77f3\u6881\uff0c\u6070\u597d\u5c06\u7edd\u58d1 \u7684\u4e24\u8fb9\u8fde\u4f4f\uff0c\u77f3\u6881\u7684\u4e09\u9762\uff0c\u867d\u7136\u8fd8\u662f\u5bc6\u6797\u73af\u7ed5\uff0c\u4f46\u51b2\u7740\u81ea\u5df1\u8fd9\u4e00\u9762\uff0c\u5374\u662f\u7a7a\u7a7a\u7684\u6ca1\u6709\u6811\u6728\u3002 \u3000\u3000\u5728\u8fd9\u7247\u77f3\u6881\u4e0a\uff0c\u7adf\u6709\u4e00\u5b87\u697c\u9601\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u7adf\u50cf\u662f\u51cc\u7a7a\u800c\u5efa\u3002\u6700\u5999\u7684\u662f\uff1a\u5728\u8fd9\u5b87\u697c\u9601\u4e4b \u4fa7\uff0c\u8fd8\u6709\u4e00\u5904\u98de\u4ead\uff0c\u800c\u5728\u8fd9\u98de\u4ead\u91cc\uff0c\u501a\u7740\u680f\u6746\u4fef\u9996\u6df1\u601d\u7684\uff0c\u5374\u7adf\u662f\u5979\u671d\u5915\u76f8\u601d\u7684\u201c\u5357\u54e5 \u54e5\u201d\uff01 \u3000\u3000\u6b64\u65f6\uff0c\u5979\u7684\u7406\u667a\u5b8c\u5168\u88ab\u72c2\u559c\u6df9\u6ca1\u4e86\uff0c\u6839\u672c\u6ca1\u6709\u60f3\u5230\uff0c\u5728\u8fd9\u79cd\u8352\u5c71\uff0c\u5bc6\u6797\uff0c\u8fd9\u4e48\u5947\u9669\u7684\u5730 \u52bf\uff0c\u600e\u4e48\u6709\u8fd9\u79cd\u697c\u9601\uff01 \u3000\u3000\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u6628\u591c\u7684\u201c\u4ed6\u201d\u82e5\u662f\u5357\u54e5\u54e5\uff0c\u6b64\u65f6\u600e\u66fe\u5728\u8fd9\u91cc\uff01\u53ea\u8ba4\u4e3a\u6628\u591c\u7684\u4e8b\uff0c\u65e2\u662f\u5728\u8fd9 \u5c71\u4e2d\u53d1\u751f\u7684\uff0c\u800c\u8fd9\u91cc\u65e2\u6709\u4e2a\u201c\u5357\u54e5\u54e5\u201d\uff0c\u4fbf\u662f\u503c\u5f97\u72c2\u559c\u7684\u4e8b\u3002\u5374\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u6b64\u523b\u7ad9\u5728\u8fd9\u98de \u4ead\u4e4b\u4e0a\u7684\uff0c\u4e0d\u4e5f\u53ef\u80fd\u5c31\u662f\u90a3\u201c\u5929\u4e89\u6559\u4e3b\u201d\u8427\u65e0\u5417\uff01 \u3000\u3000\u4e16\u4e0a\u82e5\u6709\u4e24\u4eba\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\uff0c\u6709\u65f6\u4fbf\u4f1a\u751f\u51fa\u4e00\u4e9b\u6781\u5176\u79bb\u5947\u7684\u4e8b\u6765\u3002\u82e5\u8fd9\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u7684 \u4e24\u4eba\uff0c\u8eab\u4e16\uff0c\u6027\u683c\u56de\u5f02\uff0c\u8eab\u5fc3\uff0c\u884c\u4e8b\u4e5f\u4e0d\u540c\uff0c\u800c\u53c8\u5904\u5728\u6781\u7aef\u654c\u5bf9\u7684\u5730\u4f4d\u4e2d\uff0c\u90a3\u4e48\uff0c\u6240\u53d1\u751f\u7684 \u4e8b\uff0c\u81ea\u7136\u5c31\u66f4\u52a0\u8be1\u5f02\u3002 \u3000\u3000\u4f55\u51b5\u8fd9\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u7684\u4e24\u4eba\u4e4b\u4e2d\uff0c\u8fd8\u6709\u7740\u4e00\u4eba\uff0c\u4ed6\u7684\u9762\u8c8c\uff0c\u662f\u7ecf\u6613\u5bb9\u4e4b\u540e\u800c\u5982\u6b64\u7684\u5462\uff1f \u3000\u3000\u90a3\u4e48\uff0c\u6b64\u523b\u5728\u8fd9\u98de\u4ead\u4e4b\u4e0a\uff0c\u4fef\u9996\u6c89\u601d\u7684\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u4f0a\u98ce\uff01\u8427\u65e0\uff01 \u3000\u3000\u6628\u591c\u5728\u90a3\u5c71\u7a9f\u4e4b\u4e2d\uff0c\u548c\u6b64\u523b\u5728\u8fd9\u98de\u4ead\u4e4b\u4e0a\u7684\uff0c\u662f\u4e0d\u662f\u540c\u4e00\u4eba\u5462\uff01\u82e5\u662f\uff0c\u90a3\u4ed6\u662f\u4f0a\u98ce\u8fd8\u662f \u8427\u65e0\u5462\uff1f \u3000\u3000\u82e5\u4e0d\u662f\uff0c\u90a3\u4e48\u8c01\u662f\u4f0a\u98ce\uff01\u8c01\u662f\u8427\u65e0\uff0c\u8fd9\u4e24\u4eba\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u4e48\u51d1\u5de7\uff0c\u540c\u6765\u4e00\u5c71\u4e4b\u4e2d\uff01\u800c\u8fd9\u4e2a \u8be1\u5f02\u7684\u98de\u9601\uff0c\u53c8\u662f\u5c5e\u4e8e\u4f55\u4eba\u7684\u5462\uff01\u65e6\u8bf4\u4f0a\u5919\u5eff\u4ec3 \u3000\u3000\u5165\u4e86\u957f\u5b89\u57ce\uff0c\u5df2\u662f\u4e07\u5bb6\u706f\u706b\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u5728\u504f\u50fb\u4e4b\u5904\uff0c\u5bfb\u4e86\u4e2a\u9152\u697c\uff0c\u548c\u90a3\u59cb\u7ec8\u4ed6\u8ba4\u505a\u662f\u201c\u4e09\u5f1f\u201d\u7684\u201c\u98de\u8679\u5251\u5ba2\u201d\u4eec\uff0c\u627e\u4e86\u95f4 \u96c5\u5ea7\u5750\u4e0b\uff0c\u4e09\u8a00\u4e24\u8c19\uff0c\u5c31\u5c06\u4e8b\u60c5\u89e3\u91ca\u6e05\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u53ea\u8981\u5c06\u9762\u4e0a\u7684\u4eba\u76ae\u9762\u5177\uff0c\u63ed\u5f00\u5c11\u8bb8\uff0c\u90a3\u4e48\u4e00\u4e9b\u7591\u60d1\uff0c\u4fbf\u53ef\u4e0d\u653b\u81ea\u7834\u3002 \u3000\u3000\u98de\u8679\u5251\u5ba2\u4eec\uff0c\u4e00\u770b\u8fd9\u4eba\u662f\u7ecf\u8fc7\u6613\u5bb9\u4e4b\u540e\uff0c\u624d\u548c\u81ea\u5df1\u7684\u201c\u4e09\u5f1f\u201d\u76f8\u50cf\u7684\uff0c\u90a3\u4e48\u8fd9\u4eba\u672c\u6765\u7684 \u9762\u76ee\uff0c\u81ea\u7136\u662f\u53e6\u6709\u5176\u4eba\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u4e3e\uff0c\u662f\u7ecf\u8fc7\u4e00\u9635\u5468\u8be6\u7684\u8003\u8651\u7684\uff0c\u56e0\u4e3a\u8fd9\u201c\u98de\u8679\u4e03\u5251\u201d\uff0c\u4e45\u5c45\u5173\u5916\uff0c\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053 \u81ea\u5df1\u7684\u672c\u6765\u9762\u76ee\uff0c\u7a76\u7adf\u662f\u8c01\u3002 \u3000\u3000\u518d\u8005\uff0c\u4e5f\u662f\u56e0\u4e3a\u6b64\u4e8b\u8bef\u4f1a\u5df2\u6df1\uff0c\u9664\u4e86\u8fd9\u4e48\u505a\u4e4b\u5916\uff0c\u4e5f\u786e\u5b9e\u6ca1\u6709\u5176\u4ed6\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u5c06\u8fd9\u9762\u76ee\u5b8c\u5168\u63ed\u5f00\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u8981\u7559\u7740\u8fd9\u5f62\u72b6\u53bb\u53e6\u5916\u505a\u4e9b\u4e8b\uff0c\u8fd9\u662f\u4e00\u4e2a\u6781\u4e3a\u5947\u8be1 \u7684\u201c\u5de7\u5408\u201d\uff0c\u5374\u662f\u4ed6\u503c\u5f97\u5229\u7528\u7684\u3002 \u3000\u3000\u201c\u98de\u8679\u4e03\u5251\u201d\u89c1\u4e86\uff0c\u81ea\u662f\u60d8\u7136\u82e5\u5931\u3002\u4ed6\u4eec\u8d70\u904d\u5929\u6daf\uff0c\u539f\u4ee5\u4e3a\u5df2\u662f\u5bfb\u7740\u81ea\u5df1\u7684\u201c\u4e09\u5f1f\u201d\uff0c \u90a3\u77e5\u81ea\u5df1\u8ba4\u4e3a\u5343\u771f\u4e07\u786e\u7684\u4e8b\u5b9e\uff0c\u6b64\u523b\u5374\u53d1\u5c55\u5230\u8fd9\u79cd\u5730\u6b65\u3002 \u3000\u3000\u534e\u54c1\u5947\u5e9f\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u7ad9\u4e86\u8d77\u6765\u3002\u5ffd\u5730\u5c06\u684c\u524d\u7684\u9152\u676f\u62ff\u8d77\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u5411\u4f0a\u98ce\u5f53\u5934\u4e00 \u63d6\uff0c\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u8fd9\u6b21\u79cd\u79cd\u8bef\u4f1a\uff0c\u7d2f\u5f97\u670b\u53cb\u4e5f\u591a\u51fa\u8bb8\u591a\u9ebb\u70e6\uff0c\u6211\u9664\u4e86\u6df1\u81f4\u6b49\u610f\u4e4b\u5916\uff0c\u522b\u65e0\u8bdd\u53ef \u8bf4\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u65e5\u540e\u670b\u53cb\u82e5\u6709\u7528\u5f97\u7740\u6211\u5144\u5f1f\u7684\u5730\u65b9\uff0c\u53ea\u8981\u901a\u77e5\u4e00\u58f0\uff0c\u6211\u5144\u5f1f\u5fc5\u5b9a \u4e3a\u670b\u53cb\u6548\u52b3\uff0c\u4e5f\u7b97\u662f\u6211\u5144\u5f1f\u5bf9\u670b\u53cb\u7684\u8865\u62a5\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bdd\uff0c\u8fd9\u8ddb\u8db3\u7684\u8001\u4eba\uff0c\u8eab\u5f62\u7adf\u50cf\u662f\u7ad9\u4e0d\u4f4f\u4e86\uff0c\u6447\u6447\u6b32\u5012\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u7a81\u7136\u5bf9\u8fd9\u8001\u4eba\uff0c\u8d77\u4e86\u6781\u5927\u7684\u540c\u60c5\uff0c\u5374\u8d1d\u4ed6\u53c8\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a \u3000\u3000\u201c\u6b64\u4e8b\u65e2\u662f\u6211\u5144\u5f1f\u9c81\u83bd\u4e4b\u9519\uff0c\u670b\u53cb\u5982\u6709\u4e8b\uff0c\u81ea\u7ba1\u8bf7\u4fbf\u3002\u201d \u3000\u3000\u4ed6\u53c8\u957f\u53f9\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u4e00\u7b11\uff0c\u77e5\u9053\u4ed6\u8bf4\u7684\u8bdd\uff0c\u7edd\u975e\u9010\u5ba2\u4e4b\u4ee4\uff0c\u53ea\u662f\u8fd9\u751f\u957f\u5728\u5173\u5916\u767d\u5c71\u9ed1\u6c34\u95f4\u7684\u5251\u624b\uff0c \u4e0d\u5584\u8a00\u8f9e\u800c\u5df2\u3002 \u3000\u3000\u5fc3\u4e2d\u6781\u5feb\u5730\u4e00\u8f6c\uff0c\u7a81\u7136\u7b11\u9053\uff1a \u3000\u3000\u201c\u6b64\u4e8b\u65e2\u5c5e\u5de7\u5408\uff0c\u53c8\u600e\u602a\u5f97\u4e86\u5404\u4f4d\u3002\u81f3\u4e8e\u6055\u7f6a\u8865\u62a5\u7684\u8bdd\uff0c\u8bf7\u534e\u8001\u524d\u8f88\u518d\u4e5f\u6728\u63d0\uff0c\u53ea \u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u5fae\u7b11\u4e00\u4e0b\uff0c\u76ee\u5149\u5728\u98de\u8679\u5251\u5ba2\u4eec\u7684\u8eab\u4e0a\u4e00\u8f6c\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u534e\u8001\u524d\u8f88\u5982\u679c\u4e0d\u5acc\u665a\u8f88\u5192\u6627\u7684\u8bdd\uff0c\u53ef\u5426\u5c06\u6709\u5173\u4ee4\u5e08\u5f1f\u7684\u4e8b\uff0c\u5bf9\u665a\u8f88\u4e00\u53d9\uff01\u56e0\u4e3a\u6709\u5173\u4ee4 \u5e08\u5f1f\u7684\u4e0b\u843d\uff0c\u665a\u8f88\u6216\u8bb8\u7565\u77e5\u4e00\u4e8c\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u4ed6\u65b9\u624d\u4e00\u756a\u6781\u4e3a\u5468\u5bc6\u7684\u63a8\u7a76\uff0c\u4ed6\u5df2\u786e\u4fe1\u90a3\u548c\u81ea\u5df1\u9762\u8c8c\u5b8c\u5168\u76f8\u540c\u7684\u4eba\uff0c\u4fbf\u662f\u540d\u9707\u5929\u4e0b \u7684\u201c\u5929\u4e89\u6559\u4e3b\u201d\u8427\u65e0\uff0c\u662f\u4ee5\u4ed6\u6b64\u523b\u624d\u5982\u6b64\u8bf4\u3002 \u3000\u3000\u98de\u8679\u4e03\u5251\u4e2d\u7684\u6bdb\u6587\u5947\uff0c\u9f9a\u5929\u5947\u7b49\u4eba;\u672c\u6765\u5404\u81ea\u5782\u5934\u65e0\u8a00\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5374\u4e0d\u7981\u4e00\u9f50\u62ac\u8d77\u5934 \u6765\uff0c\u76ee\u5149\u5728\u4f0a\u98ce\u8eab\u4e0a\u4e00\u626b\u3002 \u3000\u3000\u987b\u77e5\u4f0a\u98ce\u6b64\u523b\u7684\u8eab\u4e16\u6765\u5386\uff1a\u4e3a\u4f55\u51fa\u73b0\u6c5f\u6e56\u65f6\u4ed6\u8981\u65bd\u4ee5\u6613\u5bb9\uff1a\u8fd9\u4e9b\u5728\u201c\u98de\u8679\u4e03\u5251\u201d\u4e2d\uff0c\u4e5f \u6210\u4e86\u4e00\u4e2a\u8c1c\u3002\u5f53\u542c\u4e86\u8fd9\u8bdd\u4ee5\u540e\uff0c\u4ed6\u626a\u5fc3\u4e2d\u81ea\u7136\u66f4\u8d77\u4e86\u7591\u60d1\u3002\u534e\u54c1\u5947\u4fef\u9996\u6c89\u541f\u4e00\u4e0b\uff0c\u624d\u5fae\u5fae\uff0c \u53f9\u9053\uff1a \u3000\u3000\u201c\u6b64\u4e8b\u672c\u662f\u5bb6\u4e11\uff0c\u8bf4\u6765\u5df2\u6781\u4e3a\u4f24\u5fc3\u3002\u4f46\u9601\u4e0b\u65e2\u7136\u5982\u6b64\u8bf4\uff0c\u5509\uff01\u2026\u2026\u201d \u3000\u3000\u8fd9\u957f\u767d\u6d3e\u7684\u540d\u5251\u624b\uff0c\u6b64\u65f6\u867d\u7136\u5df2\u8fc7\u77e5\u547d\u4e4b\u5e74\uff0c\u53c8\u5728\u611f\u6168\u4e4b\u4e2d\uff0c\u4f46\u8c6a\u8fc8\u4e4b\u6c14\uff0c\u5374\u5e76\u672a\u56e0\u4e4b \u800c\u6709\u4e1d\u6beb\u7684\u51cf\u9000\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fae\u559f\u4e00\u58f0\uff0c\u53c8\u6ee1\u4e86\u4e00\u676f\u9152\uff0c\u53e9\u9996\u800c\u5e72\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u5148\u5e08\u5e7c\u5e74\uff0c\u672c\u662f\u4e2a\u5b64\u513f\uff0c\u540e\u6765\u56e0\u4e3a\u673a\u7f18\u51d1\u5de7\uff0c\u6210\u4e86\u957f\u767d\u6d3e\u7684\u4e00\u4ee3\u5251\u8c6a\uff0c\u6211\u957f\u767d\u6d3e\u4e5f\u56e0 \u4e4b\u5f97\u4ee5\u5217\u540d\u6b66\u6797\u4e5d\u5927\u5b97\u6d3e\u3002\u4f46\u957f\u767d\u6d3e\u59cb\u7ec8\u672a\u66fe\u4f20\u5165\u4e2d\u539f\uff0c\u5c31\u662f\u56e0\u4e3a\u5148\u5e08\u6536\u5f92\u4e4b\u9645\uff0c\u5c31\u5148\u58f0 \u8a00\uff1a\u95e8\u4e0b\u5f1f\u5b50\u82e5\u60f3\u5f97\u957f\u767d\u6d3e\u7684\u7edd\u827a\uff0c\u5c31\u5f97\u7ec8\u8001\u662f\u5c71\uff0c\u6bd5\u751f\u4e0d\u8fc7\u95ee\u6b66\u6797\u4e2d\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u606f\u4e00\u58f0\u3002\u4f0a\u98ce\u77e5\u9053\u8fd9\u5176\u4e2d\u5fc5\u5b9a\u53c8\u6709\u4e00\u4ef6\u5173\u4e8e\u6b66\u6797\u7684\u638c\u6545\uff0c\u4f46\u4eba\u5bb6\u4e0d\u8bf4\uff0c\u81ea\u5df1\u4e5f\u4e0d \u4fbf\u591a\u95ee\u3002\u5374\u542c\u8fd9\u957f\u767d\u5251\u6d3e\u7684\u638c\u95e8\u4eba\u53c8\u9053\uff1a \u3000\u3000\u201c\u800c\u4e14\u5148\u5e08\u7ec8\u751f\uff0c\u53ea\u6536\u4e86\u6211\u5e08\u5144\u5f1f\u4e03\u4eba\uff0c\u5374\u4e5f\u90fd\u662f\u5b64\u513f\uff1a\u800c\u6211\u5e08\u5144\u5f1f\u4e03\u4eba\uff0c\u4e5f\u59cb\u7ec8\u9075\u5b88 \u7740\u5148\u5e08\u9057\u547d\uff0c\u4ece\u672a\u6d89\u8db3\u6c5f\u6e56\u3002\u201d \u3000\u3000\u8fd9\u8ddb\u8db3\u8001\u4eba\uff0c\u76ee\u4e2d\u7684\u795e\u5149\uff0c\u53d8\u5f97\u6781\u4e3a\u9eef\u6de1\u8d77\u6765\u3002\u4f0a\u98ce\u4e5f\u4e0d\u7981\u6697\u53f9\uff0c\u8ba9\u4e00\u4e2a\u8eab\u6000\u7edd\u6280\u7684\u5251 \u5ba2\uff0c\u7ec8\u8001\u6df1\u5c71\uff0c\u8fd9\u662f\u4e00\u4ef6\u591a\u4e48\u6b8b\u9177\u7684\u4e8b\uff0c\u8fd9\u534e\u54c1\u5947\u5c81\u6708\u8e49\u8dce\uff0c\u4e24\u9b13\u5df2\u6591\uff0c\u5927\u597d\u5e74\u534e\uff0c\u5168\u90fd\u5728 \u9762\u5bf9\u7740\u5bd2\u51b0\u767d\u4e91\u95f4\u6e21\u8fc7\uff0c\u82f4\u5165\u6b64\u523b\u5fc3\u60c5\uff0c\u81ea\u4e0d\u96be\u60f3\u89c1\u3002 \u3000\u3000\u534e\u54c1\u5947\u53f9\u606f\u7740\u5446\u9053\uff1a \u3000\u3000\u201c\u6211\u957f\u767d\u4e00\u6d3e\uff0c\u5f97\u4ee5\u5217\u540d\u4e5d\u5927\u5b97\u6d3e\uff0c\u662f\u5148\u5e08\u6614\u5e74\u5728\u6b66\u6797\u5927\u4f1a\u4e0a\uff0c\u4ee5\u81ea\u521b\u7684\u201c\u98ce\u96f7\u5251 \u6cd5\u201d\uff0c\u786c\u78b0\u786c\u6253\u4e0b\u6765\u7684\u58f0\u540d\uff0c\u8fd9\u201c\u98ce\u96f7\u5251\u6cd5\u201d\uff0c\u4e5f\u81ea\u7136\u4e5f\u6210\u4e86\u6211\u957f\u767d\u4e00\u6d3e\u9547\u5c71\u7684\u5251\u6cd5\u3002\u5148\u5e08 \u6614\u5e74\u8ba9\u6211\u4eec\u7acb\u4e0b\u7684\u8a93\u8a00\uff0c\u5c31\u662f\u95e8\u4e0b\u5f1f\u5b50\u82e5\u6709\u4e0d\u8010\u5bc2\u5bde\uff0c\u60f3\u6d89\u8db3\u6b66\u6797\u7684\uff0c\u4e5f\u5e76\u975e\u4e0d\u53ef\uff1a\u53ea\u662f\u5374 \u4e0d\u80fd\u7ec3\u8fd9\u201c\u98ce\u96f7\u5251\u6cd5\u201d\u800c\u5df2\u3002 \u3000\u3000\u201c\u6211\u5e08\u5144\u5f1f\u90fd\u662f\u8eab\u4e16\u5b64\u82e6\u7684\u5b64\u513f\uff0c\u6ca1\u6709\u5148\u5e08\u7684\u6536\u7559\u6559\u517b\uff0c\u53ea\u6015\u65e9\u5df2\u90fd\u51bb\u997f\u800c\u6b7b\u3002\u5165\u662f\u4ee5 \u5148\u5e08\u4e0d\u53ea\u662f\u6211\u5e08\u5144\u5f1f\u7684\u5e08\u7236\uff0c\u4e5f\u662f\u6069\u4eba\u3002\u6211\u5e08\u5144\u5f1f\u4e5f\u5c31\u90fd\u613f\u610f\u5728\u957f\u767d\u5c71\u4e0a\uff0c\u4f34\u7740\u5148\u5e08\u7684\u7075 \u9aa8\uff0c\u4f55\u51b5\u6b66\u6797\u4e2d\u662f\u662f\u975e\u975e\uff0c\u6069\u6069\u6028\u6028\uff0c\u6211\u4eec\u5b9e\u5728\u4e0d\u613f\u610f\u8fc7\u95ee\u3002 \u3000\u3000\u201c\u591a\u5e74\u4ee5\u524d\uff0c\u6211\u5e08\u5144\u5f1f\u4e2d\u5374\u6709\u4e00\u4eba\u4e00\u5b9a\u8981\u4e0b\u5c71\uff0c\u6211\u529d\u4e5f\u65e0\u7528\uff0c\u4f46\u90a3\u65f6\u4ed6\u8fd8\u6ca1\u6709\u7ec3\u6210\u201c\u98ce \u96f7\u5251\u6cd5\u201d\uff0c\u56e0\u4e3a\u8fd9\u5251\u6cd5\u5185\u529f\u4e0d\u6210\uff0c\u6839\u672c\u65e0\u6cd5\u7ec3\u5f97\u2026\u2026\u5509\uff01\u4ed6\u662f\u6211\u4eb2\u624b\u5e26\u5927\u7684\u3002\u4ed6\u8981\u8d70\uff0c\u6211\u867d \u7136\u4f24\u5fc3\uff0c\u5374\u4e5f\u65e0\u6cd5\uff0c\u4e5f\u53ea\u5f97\u8ba9\u4ed6\u8d70\u4e86\u3002\u201d \u3000\u3000\u957f\u767d\u5251\u5ba2\u60f3\u662f\u56e0\u4e3a\u5fc3\u4e2d\u7684\u611f\u6000\u7d0a\u4e71\uff0c\u6b64\u523b\u8bf4\u8d77\u8bdd\u6765\uff0c\u5df2\u6709\u4e9b\u96f6\u4e71\u4e86\uff01 \u3000\u3000\u201c\u4f46\u8fc7\u4e86\u4e0d\u4e45\uff0c\u4ed6\u53c8\u8dd1\u56de\u5c71\u4e0a\u4e86\uff0c\u8eab\u4e0a\u5374\u53d7\u4e86\u4e09\u5904\u4f24\uff0c\u4eba\u4e5f\u6194\u60b4\u5f97\u4e0d\u6210\u6837\u5b50\u3002\u539f\u6765\u4ed6\u4e00 \u4e0b\u5c71\u4e4b\u540e\uff0c\u5c31\u7ed3\u4e86\u4e0d\u5c11\u4ec7\u5bb6\u3002\u4ed6\u90a3\u65f6\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u6b66\u529f\u8fd8\u6ca1\u6709\u7ec3\u6210\uff0c\u51e0\u4e2a\u6708\u91cc\uff0c\u5c31\u5403\u4e86\u4eba\u5bb6\u4e0d \u5c11\u4e8f\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e2d\u7684\u90a3\u79cd\u795e\u8272\uff0c\u4f7f\u4f0a\u98ce\u7acb\u523b\u77e5\u9053\uff1a\u8fd9\u8001\u4eba\u5bf9\u4ed6\u7684\u201c\u4e09\u5f1f\u201d\uff0c\u5fc5\u5b9a\u6709\u7740\u5f88\u6df1\u7684\u60c5 \u611f\uff0c\u4e5f\u77e5\u9053\u8fd9\u957f\u767d\u5251\u624b\uff0c\u5b9e\u662f\u6027\u60c5\u4e2d\u4eba\u3002 \u3000\u3000\u5374\u542c\u4ed6\u53c8\u9053\uff1a \u3000\u3000\u201c\u4ed6\u8fd9\u6837\u56de\u6765\uff0c\u6211\u5fc3\u91cc\u81ea\u7136\u96be\u53d7\uff0c\u7adf\u79c1\u4e0b\u4f20\u7ed9\u4e86\u4ed6\u201c\u98ce\u96f7\u5251\u6cd5\u201d\u3002\u5509\uff01\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u606f\u7740\uff0c\u73af\u987e\u4e86\u4ed6\u7684\u5e08\u5f1f\u4eec\u4e00\u773c\uff0c\u50cf\u662f\u5bf9\u4f0a\u98ce\u8bf4\uff0c\u53c8\u50cf\u662f\u5bf9\u4ed6\u7684\u5e08\u5f1f\u4eec\u8bf4\uff0c\u53c8\u50cf\u662f \u5bf9\u81ea\u5df1\u8bf4\uff0c\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u548c\u4ed6\u867d\u7136\u662f\u5e08\u5144\u5f1f\uff0c\u4f46\u662f\u53ea\u6709\u4ed6\u662f\u6211\u4eb2\u624b\u517b\u5927\u7684\uff0c\u4ed6\u2026\u2026\u4ed6\u4eba\u53c8\u806a\u660e\uff0c\u6211\u5bf9\u4ed6\u5b9e\u5728 \u6709\u7740\u7236\u5b50\u5144\u5f1f\u822c\u7684\u9aa8\u8089\u4e4b\u60c5\u3002 \u3000\u3000\u201c\u4ed6\u5b66\u6210\u201c\u98ce\u96f7\u5251\u6cd5\u201d\u4e4b\u540e\uff0c\u4fbf\u53c8\u8dd1\u4e86\u4e0b\u5c71\u3002\u6211\u5fc3\u91cc\u66f4\u96be\u53d7\uff0c\u4ee5\u4e3a\u4ed6\u8fd9\u6b21\u518d\u4e5f\u4e0d\u4f1a\u56de\u6765 \u4e86\uff0c\u90a3\u77e5\u9053\u4e0d\u5230\u534a\u5e74\uff0c\u4ed6\u53c8\u8dd1\u4e86\u56de\u6765\uff0c\u800c\u4e14\u53d7\u7684\u4f24\u66f4\u91cd\uff0c\u51e0\u4e4e\u8fde\u817f\u90fd\u9669\u4e9b\u88ab\u4eba\u5bb6\u6253\u65ad\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u770b\u4e4b\u4e0b\uff0c\u5fc3\u91cc\u4e5f\u6709\u4e9b\u751f\u6c14\uff0c\u53c8\u6709\u4e9b\u96be\u53d7\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u9ad8\u5174\uff0c\u6b66\u6797\u4e2d\u80fd\u4eba\u592a\u591a\uff0c\u4ed6 \u60f3\u51ed\u7740\u8fd9\u201c\u98ce\u96f7\u5251\u6cd5\u201d\uff0c\u6a2a\u884c\u6c5f\u6e56\uff0c\u90a3\u91cc\u80fd\u505a\u5f97\u5230\u203b\uff0e\u8ba9\u4ed6\u53d7\u4e86\u8fd9\u6b21\u6559\u8bad\uff0c\u4e5f\u8bb8\u4ed6\u5c31\u4f1a\u8001\u8001 \u5b9e\u5b9e\u5728\u5c71\u4e0a\u4f4f\u4e0b\u6765\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6697\u53f9\u4e86\u4e00\u58f0\uff0c\u77e5\u9053\u8fd9\u534e\u54c1\u5947\u53f0\u7136\u5c06\u4ed6\u201c\u4e09\u5f1f\u201d\u4e00\u624b\u517b\u6210\uff0c\u4f46\u5374\u4e0d\u4e86\u89e3\u4ed6\u201c\u4e09\u5f1f\u201d\uff0c \u5c31\u51ed\u4ed6\u201c\u4e09\u5f1f\u201d\u7684\u8fd9\u79cd\u813e\u6c14\uff0c\u600e\u4e48\u4f1a\u5728\u5403\u4e86\u4eba\u5bb6\u7684\u5927\u4e8f\u4e4b\u540e\uff0c\u4e0d\u60f3\u62a5\u4ec7\uff0c\u53cd\u800c\u8001\u8001\u5b9e\u5b9e\u5728\u5c71 \u4e0a\u4f4f\u4e0b\u6765\u5462\uff1f \u3000\u3000\u679c\u7136\u534e\u54c1\u5947\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u90a3\u77e5\u4ed6\u4f24\u4e00\u517b\u597d\uff0c\u5c31\u6c42\u6211\u4e0b\u5c71\u53bb\u4e3a\u4ed6\u590d\u4ec7\uff0c\u6211\u867d\u75bc\u7231\u4ed6\uff0c\u4e0d\u60dc\u4f20\u7ed9\u4ed6\u201c\u98ce\u96f7\u5251\u6cd5\u201d\uff0c \u4f46\u4e5f\u4e0d\u80fd\u5e26\u7740\u522b\u7684\u5144\u5f1f\u53bb\u8fdd\u80cc\u5148\u5e08\u7684\u9057\u547d\uff0c\u81ea\u7136\u5c31\u62d2\u7edd\u4e86\u4ed6\uff0c\u53c8\u53eb\u4ed6\u5b89\u5fc3\u4f4f\u4e0b\u6765\uff0c\u4e0d\u8981\u80e1\u4e71 \u60f9\u7978\u3002 \u3000\u3000\u201c\u4ed6\u5374\u4e5f\u4e00\u58f0\u4e0d\u54cd\uff0c\u90a3\u77e5\u9053\u53c8\u8fc7\u4e86\u51e0\u5929\uff0c\u5c31\u6709\u8bb8\u591a\u6b66\u6797\u4e2d\u4eba\uff0c\u8dd1\u5230\u957f\u767d\u5c71\u4e0a\u6765\u5bfb\u4ec7\u4e86\u3002 \u5f53\u7136\u90fd\u662f\u4ed6\u60f9\u4e0b\u7684\u7978\uff0c\u800c\u4e14\u6211\u4e00\u95f4\u4e4b\u4e0b\uff0c\u7adf\u7136\u90fd\u662f\u4ed6\u7684\u9519\u3002\u4e8e\u662f\u6211\u5c31\u5f53\u7740\u90a3\u4e16\u4e00\u4eba\uff0c\u5c06\u4ed6\u75db \u8d3e\u4e86\u4e00\u987f\u3002\u201d \u3000\u3000\u4ed6\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u8fd9\u4e48\u505a\uff0c\u4e00\u65b9\u9762\u81ea\u7136\u662f\u56e0\u4e3a\u5148\u5e08\u7684\u9057\u547d\uff0c\u4e5f\u56e0\u4e3a\u4e0d\u8ba9\u5929\u4e0b\u6b66\u6797\u8bf4\u6211\u957f\u767d\u6d3e\u7eb5\u5bb9\u5f1f \u5b50\uff1a\u53e6\u4e00\u65b9\u9762\u5374\u4e5f\u4e3a\u7740\u4ed6\u597d\uff0c\u5e0c\u671b\u4ed6\u81ea\u6b64\u4ee5\u540e\uff0c\u597d\u597d\u505a\u4eba\uff0c\u4e5f\u4e0d\u6789\u6211\u6559\u5bfc\u4ed6\u7684\u4e00\u756a\u5fc3\u8840\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u6697\u6697\u8d5e\u4f69\uff0c\u8fd9\u534e\u54c1\u5947\u679c\u7136\u662f\u5b88\u6b63\u4e0d\u73c2\u7684\u540d\u5bb6\u98ce\u5ea6\uff0c\u4e0d\u6127\u4e3a\u6b66\u6797\u4e5d\u5927\u5b97\u6d3e\u4e4b\u4e00\u957f \u767d\u5251\u6d3e\u7684\u4e00\u4ee3\u638c\u95e8\u4eba\uff01 \u3000\u3000\u6b64\u523b\u8fd9\u957f\u767d\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u53c8\u6ee1\u996e\u4e86\u4e00\u676f\u9152\uff0c\u201c\u7830\u201d\u5730\uff0c\u5c06\u9152\u676f\u91cd\u91cd\u653e\u5230\u684c\u4e0a\uff0c\u63a5\u7740\u8bf4 \u9053\uff1a \u3000\u3000\u201c\u5374\u4e0d\u77e5\u4ed6\u5374\u5df2\u6068\u4e0a\u4e86\u6211\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u518d\u4e5f\u4e0d\u548c\u6211\u8bf4\u4e00\u53e5\u8bdd\u3002\u6211\u5fc3\u91cc\u53c8\u6c14\uff0c\u53c8\u96be\u53d7\uff0c\u4f46 \u53ea\u8981\u4ed6\u597d\u597d\u7684\uff0c\u5bf9\u6211\u600e\u4e48\u6837\u6211\u90fd\u65e0\u6240\u8c13\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u4e9b\uff0c\u90a3\u6bdb\u6587\u5947\u7a81\u7136\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u62a2\u5728\u534e\u54c1\u5947\u7684\u524d\u9762\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff01\u4f60\u6b47\u6b47\uff01\u8ba9\u5144\u5f1f\u6211\u4ee3\u5927\u54e5\u63a5\u4e0b\u53bb\u5427\u3002\u201d \u3000\u3000\u7adf\u6ca1\u6709\u7b49\u5230\u534e\u54c1\u5947\u7684\u540c\u610f\uff0c\u5c31\u63a5\u7740\u4ed6\u7684\u8bdd\u5f80\u4e0b\u9762\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u65f6\u5019\u6211\u4eec\u51e0\u4e2a\u5f1f\u5144\u770b\u4e86\u5c31\u90fd\u6709\u4e9b\u751f\u6c14\uff0c\u4f46\u65e2\u7136\u5927\u54e5\u4e0d\u8bf4\uff0c\u6211\u4eec\u81ea\u7136\u4e5f\u66f4\u65e0\u8bdd\u53ef\u8bf4\u3002 \u90a3\u77e5\u9053\u4ed6\u5c45\u7136\u5728\u5927\u54e5\u7ec3\u529f\u6700\u5403\u7d27\u7684\u65f6\u5019\uff0c\u95ef\u8fdb\u5927\u54e5\u90a3\u91cc\uff0c\u8ba9\u5927\u54e5\u6c14\u8840\u963b\u585e\u5728\u5de6\u9762\u201c\u6d8c\u6cc9\u7a74\u201d \u4e0a\uff0c\u81ea\u6b64\u2026\u2026\u201d \u3000\u3000\u534e\u54c1\u5947\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u5f3a\u7740\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5012\u4e0d\u80fd\u602a\u4ed6\uff0c\u4ed6\u662f\u65e0\u610f\u7684\u3002\u201d \u3000\u3000\u6bdb\u6587\u5947\u5251\u7709\u4e00\u7acb\uff0c\u5fae\u5fae\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u7565\u6709\u4e0d\u5e73\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff01\u60a8\u522b\u8fd9\u4e48\u8bf4\uff01\u96be\u9053\u4ed6\u8ddf\u5927\u54e5\u60a8\u8fd9\u4e48\u4e45\uff0c\u8fd8\u4e0d\u77e5\u9053\u5927\u54e5\u60a8\u7ec3\u529f\u7684\u65f6\u8fb0\uff1f\u90a3\u5929\u82e5\u4e0d \u662f\u6211\u6070\u597d\u8d76\u6765\uff0c\u66ff\u5927\u54e5\u60a8\u8d76\u7d27\u6551\u6cbb\uff0c\u60a8\u4e0d\u4f46\u817f\u5e9f\u4e86\uff0c\u6050\u6015\u8fde\u6027\u547d\u90fd\u4fdd\u4e0d\u4f4f\uff01\u63a0i\u73b0\u5728\u8fd8\u5728\u8fd9 \u6837\u5e2e\u4ed6\u8bf4\u8bdd\uff1f\u4f60i\u2026\u2026\u201d \u3000\u3000\u4ed6\u500f\u7136\u987f\u4f4f\u4e86\u8bdd\uff0c\u50cf\u662f\u77e5\u9053\u4ed6\u81ea\u5df1\u6b64\u523b\u5bf9\u4ed6\u5927\u54e5\u6240\u8bf4\u7684\u8bdd\uff0c\u4efd\u91cf\u5df2\u5acc\u592a\u91cd\u3002 \u3000\u3000\u4f0a\u98ce\u5374\u4e0d\u7981\u53c8\u6697\u6697\u611f\u53f9\u7740\uff0c\u4e00\u9762\u611f\u53f9\u7740\u8fd9\u534e\u54c1\u5947\u7684\u201c\u5584\u826f\u201d\uff0c\u53e6\u4e00\u9762\u76f8\u5f62\u4e4b\u4e0b\uff0c\u4ed6\u90a3 \u201c\u4e09\u5f1f\u201d\u7684\u51b7\u8840\u65e0\u60c5\uff0c\u4e5f\u5c31\u66f4\u53ef\u6068\u4e86\uff01 \u3000\u3000\u201c\u96be\u602a\u8fd9\u201c\u5929\u4e89\u6559\u4e3b\u201d\u8427\u65e0\uff0c\u9634\u72e0\uff0c\u5351\u8d31\uff0c\u4ed6\u5bf9\u90a3\u4e48\u7231\u62a4\u4ed6\u7684\u5e08\u5144\uff0c\u90fd\u4f1a\u5982\u6b64\uff1a\u5bf9\u522b\u4eba \u7684\u624b\u6bb5\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u601d\u5fd6\u4e2d\uff0c\u5374\u542c\u90a3\u6bdb\u6587\u5947\u5728\u9759\u9ed8\u534a\u664c\u540e\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u4e3a\u5927\u54e5\u63a8\u62ff\u4e00\u9635\u4e4b\u540e\uff0c\u518d\u53bb\u627e\u4ed6\uff0c\u4ed6\u5374\u5df2\u4e0d\u77e5\u6240\u8e2a\u4e86\u3002\u90a3\u65f6\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u81ea\u77e5\u72af\u4e86\u5927 \u9519\uff0c\u754f\u7f6a\u800c\u9003\u5462\u3002\u201d \u3000\u3000\u4ed6\u53cc\u7709\u53c8\u4e00\u7acb\uff0c\u9053\uff1a \u3000\u3000\u201c\u90a3\u77e5\u9053\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u4e8b\u60c5\u5e76\u4e0d\u5355\u7eaf\u5982\u6b64\u3002\u201d \u3000\u3000\u8fd9\u6bdb\u6587\u5947\u60f3\u662f\u5bf9\u4ed6\u90a3\u4f4d\u201c\u4e09\u5f1f\u201d\uff0c\u6781\u4e3a\u4e0d\u6ee1\uff0c\u662f\u4ee5\u6b64\u523b\u6beb\u4e0d\u7559\u60c5\u5730\u8bf4\u7740\u3002 \u3000\u3000\u4f46\u4f0a\u98ce\u60f3\u5230\u8fd9\u6bdb\u6587\u5947\u4eca\u6668\u5728\u7ec8\u5357\u5c71\u4e0b\uff0c\u6001\uff0c\u77e5\u9053\u8fd9\u6bdb\u6587\u5947\u5bf9\u4ed6\u7684\u201c\u4e09\u5f1f\u201d\u867d\u4e0d\u6ee1\uff0c\u8bf4\u4e0b \u53bb\u9053\uff1a \u3000\u3000\u201c\u51e0\u4e2a\u6708\u524d\uff0c\u6211\u4eec\u624d\u53d1\u73b0\u5148\u5e08\u7684\u9057\u7269\u653e\u5728\u6781\u4e25\u5bc6\u7684\u6240\u5728\uff0c\u5916\u4eba\u7edd\u4e0d\u4f1a\u77e5\u9053\u3002\u4f55\u51b5\u4e0b\uff0c\u9664 \u4e86\u4ed6\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u4eba\u4f1a\u62ff\u8fd9\u4e1c\u897f\u3002\u800c\u9669\u4e9b\u8d70\u706b\u5165\u9b54\uff0c\u6211\u4eec\u5927\u5bb6\u90fd\u4e3a\u5927\u54e5\u60ca\u614c\u65f6\uff0c\u5c71\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4f4d\u201c\u4e09\u5f1f\u201d\u7684\u884c\u4e3a\uff0c\u5b9e\u5728\u662f\u4ee4\u4eba\u9f7f\u51b7\u5c06\u81ea\u5df1\u8bef\u4e3a\u4ed6\u201c\u4e09\u5f1f\u201d\u65f6\uff0c\u8bf4\u8bdd\u65f6\u5019\u7684\u795e\u5374\u4ecd\u6709 \u7740\u624b\u8db3\u4e4b\u60c5\uff0c\u4e0d\u7981\u6697\u4e2d\u4e00\u53f9\uff0c\u542c\u4ed6\uff0c\u5c11\u4e86\u6781\u91cd\u8981\u7684\u4e00\u4ef6\u3002\u5148\u5e08\u7684\u9057\u7269\uff0c\u672c\u662f\u957f\u767d\u5c71\u8fd9\u4e9b\u5e74 \u6765\uff0c\u4e5f\u7edd\u65e0\u5916\u6765\u5ba2\u3002\u63a8\u7a76\u4e4b\u4e14\u6211\u518d\u4e00\u7422\u78e8\uff0c\u60f3\u5fc5\u662f\u4ed6\u6545\u610f\u5c06\u5927\u54e5\u5f04\u5f97\u4ed6\u5374\u6084\u6084\u5c06\u5148\u5e08\u7684\u90a3\u4ef6 \u9057\u7269\u5077\u4e86\u53bb\uff0c\u9003\u4e0b\uff01\u4f0a\u98ce\u5fc3\u4e2d\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u6ee1\u6000\u5bf9\u6b64\u4eba\u7684\u6124\u8fb0\u52fa \u3000\u3000\u6bdb\u6587\u5947\u5598\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u5144\u5f1f\u8fd9\u624d\u4e00\u9f50\u4e0b\u5c71\uff0c\u60f3\u627e\u4ed6\u8981\u56de\u8fd9\u4ef6\u9057\u7269\uff1a\u4f46\u5929\u4e0b\u4e4b\u5927\uff0c\u4eba\u6d77\u832b\u832b\uff0c\u4ed6\u4e0b\u5c71\u4e4b\u540e\uff0c \u4fbf\u65e0\u97f3\u8baf\uff0c\u53c8\u53eb\u6211\u4eec\u5230\u90a3\u91cc\u627e\u4ed6\u53bb\uff1f\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u98de\u8679\u5251\u5ba2\u4eec\u90fd\u4e0d\u7981\u4e3a\u4e4b\u53f9\u606f\uff01 \u3000\u3000\u90a3\u534e\u54c1\u5947\u9762\u4e0a\u7684\u795e\u8272\uff0c\u66f4\u52a0\u9eef\u7136\uff01\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5f77\u4f5b\u53c8\u53d8\u5f97\u82cd\u8001\u4e86\u8bb8\u591a\u3002\u5f15\u543c\u5176\u8033\u76ee \u3000\u3000\u4f0a\u98ce\u5374\u5728\u6697\u81ea\u611f\u53f9\u7740\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u6b66\u6797\u4e2d\u65e0\u4eba\u80fd\u77e5\u7684\u90a3\u201c\u5929\u4e89\u6559\u4e3b\u201d\u8427\u65e0\u7684\u8eab\u4e16\uff0c\u6b64\u523b\u5374\u88ab\u6211\u77e5\u9053\u4e86\u3002\u5509\u859b\u82e5\u58c1 \u5440\uff0c\u859b\u82e5\u58c1\uff01\u4f60\u600e\u4f1a\u8ddf\u4e86\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7981\u81ea\u601c\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u76ee\u5149\u5728\u534e\u54c1\u5947\u60b2\u6006\u7684\u9762\u4e0a\u4e00\u63a0\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5929\u4e0b\u867d\u5927\uff0c\u4ee4\u5e08\u5f1f\u7684\u53bb\u5411\uff0c\u672c\u5982\u6d77\u5e95\u4e4b\u9488\uff0c\u65e0\u5904\u53ef\u5bfb\uff1a\u4f46\u665a\u8f88\u5374\u56e0\u673a\u7f18\u51d1\u5de7\uff0c\u4ed6\u7684\u53bb \u5411\uff0c\u665a\u8f88\u5374\u7565\u77e5\u4e00\u4e8c\u5462\u3002\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u98de\u8679\u5251\u5ba2\u4eec\u4e0d\u7981\u90fd\u4e3a\u4e4b\u6115\u7136\u800c\u5927\u5403\u4e00\u60ca\uff01\u534e\u54c1\u5947\u66f4\u662f\u60ca\u5947\u5730\u51e0\u4e4e\u4e00\u628a\u62c9\u7740\u4f0a \u98ce\u7684\u8863\u895f\uff0c\u6025\u5207\u5730\u95ee\u9053\uff1a \u3000\u3000\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u7b11\uff0c\u9042\u5c06\u7ec8\u5357\u5c71\u4e0a\u6240\u53d1\u751f\u7684\u90a3\u4ef6\u5947\u4e8b\uff0c\u548c\u81ea\u5df1\u5fc3\u91cc\u7684\u63a8\u7a76\uff0c\u8bf4\u4e86\u51fa\u6765\u3002\u56e0\u4e3a\u8fd9\u4ef6 \u4e8b\u662f\u8fd9\u4e48\u79bb\u5947\u548c\u8be1\u5f02\uff0c\u4ed6\u9700\u8981\u8bf4\u5f88\u4e45\uff0c\u624d\u80fd\u5c06\u5b83\u8bf4\u5f97\u80fd\u4f7f\u522b\u4eba\u660e\u4e86\u3002\u7b49\u4ed6\u8bf4\u5b8c\u4e86\uff0c\u5374\u5df2\u591c\u6df1 \u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9152\u697c\u65e9\u5df2\u8be5\u5c31\u6253\u70ca\uff0c\u4f46\u8fde\u638c\u67dc\u7684\u5e26\u8dd1\u5802\u7684\uff0c\u53ef\u90fd\u65e9\u5c31\u770b\u51fa\u6765\u8fd9\u6279\u5927\u7237\u4eec\u4e0d\u5927\u597d \u60f9\uff0c\u80cc\u540e\u90fd\u80cc\u7740\u5251\uff0c\u800c\u4e14\u795e\u8272\u4e4b\u95f4\uff0c\u50cf\u662f\u5fc3\u91cc\u90fd\u5b58\u7740\u51e0\u5206\u706b\u6c14\u3002\u662f\u4ee5\u9152\u697c\u867d\u5df2\u6253\u70ca\uff0c\u53ef\u5374\u4e0d \u6562\u53bb\u8d76\u4eba\u5bb6\u8d70\u3002 \u3000\u3000\u53ef\u662f\uff0c\u592a\u665a\u4e86\u4e5f\u4e0d\u884c\uff0c\u8dd1\u5802\u7684\u5230\u540e\u6765\uff0c\u53ea\u5f97\u966a\u7740\u5c0f\u5fc3\uff0c\u7b11\u7740\u5bf9\u4ed6\u4eec\u9053\uff1a \u3000\u3000\u201c\u7237\u4eec\u8bf7\u5305\u6db5\uff0c\u73b0\u5728\u5df2\u7ecf\u8fc7\u4e86\u5b50\u65f6\u4e86\uff0c\u7237\u4eec\u8981\u662f\u8fd8\u60f3\u559d\u9152\u2026\u2026\u201d \u3000\u3000\u98de\u8679\u4e03\u5251\u53ef\u4e0d\u662f\u4e0d\u8bb2\u7406\u7684\u4eba\uff0c\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5c31\u7ed3\u7b97\u4e86\u9152\u8d26\uff0c\u8d70\u4e86\u51fa\u53bb\u3002\u6b64\u65f6\u679c\u5df2\u591c\u6df1\uff0c \u6599\u5ced\u7684\u8202\u5bd2\uff0c\u50cf\u6c34\u4e00\u6837\u5730\u6d78\u4eba\u3002 \u3000\u3000\u534e\u54c1\u5947\u8ba9\u4ed6\u7684\u5e08\u5f1f\u7275\u7740\u9a6c\uff0c\u81ea\u5df1\u5374\u548c\u4f0a\u98ce\u5e76\u80a9\u800c\u884c\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u5bf9\u4f0a\u98ce\u7684\u8bdd\uff0c\u867d\u7136\u4ecd\u6709\u4e9b\u6000\u7591\uff0c\u4f46\u5374\u5927\u90e8\u5df2\u7ecf\u76f8\u4fe1\u4e86\u3002 \u3000\u3000\u53ea\u662f\uff0c\u6b64\u523b\u4ed6\u201c\u4e09\u5f1f\u201d\u7684\u884c\u8e2a\uff0c\u867d\u5df2\u6709\u4e0b\u843d\uff0c\u60f3\u4e0d\u5230\u7684\uff0c\u5374\u662f\u4ed6\u7684\u201c\u4e09\u5f1f\u201d\u6b64\u65f6\u5df2\u6210\u4e86 \u540d\u9707\u6b66\u6797\u7684\u4eba\u7269\uff0c\u800c\u4e14\u8fd8\u662f\u6c5f\u6e56\u4e2d\u6700\u5927\u4e00\u4e2a\u5e2e\u4f1a\u7684\u201c\u6559\u4e3b\u201d\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u4ed6\u867d\u5df2\u5f97\u5230\u4ed6\u201c\u4e09\u5f1f\u201d\u7684\u4e0b\u843d\uff0c\u4f46\u4ed6\u201c\u4e09\u5f1f\u201d\u6b64\u523b\u7a76\u7adf\u5728\u90a3\u91cc\uff1f\u5374\u4ecd\u7136\u65e0\u4eba\u77e5 \u9053\u3002\u56e0\u4e3a\u201c\u5929\u4e89\u6559\u4e3b\u201d\u7684\u884c\u8e2a\uff0c\u5728\u6b66\u6797\u4e2d\u672c\u662f\u4e2a\u8c1c\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u5c31\u5546\u91cf\u7740\uff0c\u7531\u4f0a\u98ce\u6545\u610f\u5728\u8fd9\u4e00\u5e26\uff0c\u4ee5\u201c\u5929\u4e89\u6559\u4e3b\u201d\u7684\u8eab\u4efd\u73b0\u8eab\uff0c\u4f7f\u5f97\u8fd9\u6d88\u606f\u5728 \u6b66\u6797\u4e2d\u4f20\u51fa\uff0c\u90a3\u4e48\uff0c\u771f\u7684\u201c\u5929\u4e89\u6559\u4e3b\u201d\u5c31\u6781\u53ef\u80fd\u7b71\u5f15\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u5728\u4ed6\u4eec\u53cc\u65b9\uff0c\u90fd\u6781\u4e3a\u6709\u5229\uff0c\u4f0a\u98ce\u81ea\u7136\u4e5f\u6781\u4e3a\u8d5e\u540c\u3002 \u3000\u3000\u5f00\u5c01\u5e9c\uff0c\u4f4d\u4e8e\u9ec4\u6cb3\u5357\u5cb8\uff0c\u4e0d\u4f46\u4e43\u8c6b\u4e2d\u540d\u57ce\uff0c\u4e14\u662f\u4e2d\u539f\u4e00\u5927\u5360\u90fd\u3002 \u3000\u3000\u4f0a\u98ce\u8fdb\u4e86\u5f00\u5c01\uff0c\u98de\u8679\u4e03\u5251\u5374\u5728\u57ce\u5916\u7684\u4e00\u5bb6\u5ba2\u6808\u91cc\u7b49\u7740\u3002 \u3000\u3000\u8fd9\u5f00\u5c01\u5e9c\u4eba\u7269\u98ce\u534e\uff0c\u5e02\u9762\u679c\u7136\u6781\u5176\u7e41\u76db\u3002\u4f0a\u98ce\u65bd\u7136\u800c\u884c\uff0c\u76ee\u5149\u5374\u5728\u50cf\u730e\u72ac\u822c\u5730\u641c\u5bfb\u7740\uff0c \u5e0c\u671b\u80fd\u627e\u56de\u5230\u51e0\u4e2a\u5929\u4e89\u6559\u4f17\u3002 \u3000\u3000\u4ed6\u4e00\u6d3e\u4ece\u5bb9\u6f47\u6d12\u7684\u6837\u5b50\uff0c\u901b\u4e86\u534a\u664c\uff0c\u4f46\u662f\u5929\u4e89\u6559\u4e0b\u9664\u4e86\u91d1\u886b\u9999\u4e3b\u7684\u8863\u886b\u8f83\u4e3a\u597d\u8ba4\u5916\uff0c\u522b \u7684\u6559\u4f17\u8eab\u4e0a\uff0c\u81ea\u7136\u4e0d\u4f1a\u6302\u7740\u201c\u5929\u4e89\u6559\u201d\u7684\u62db\u724c\u3002 \u3000\u3000\u53ea\u662f\u91d1\u886b\u9999\u4e3b\uff0c\u5728\u201c\u5929\u4e89\u6559\u201d\u4e2d\u672c\u5c31\u4e0d\u591a\u3002\u4ed6\u4e13\u95e8\u5230\u5f00\u5c01\u6765\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u6697\u81ea\u5fd6\u5ea6\uff0c\u8fd9 \u5f00\u5c01\u57ce\u91cc\uff0c\u6781\u53ef\u80fd\u6709\u7740\u91d1\u886b\u9999\u4e3b\u2026\u56e0\u4e3a\uff0c\u201c\u5929\u4e89\u6559\u201d\u4e2d\uff0c\u9664\u4e86\u91d1\u886b\u9999\u4e3b\u5916\uff0c\u4fbf\u5f88\u5c11\u6709\u4eba\u770b\u5230 \u8fc7\u6559\u4e3b\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u4f0a\u98ce\u901b\u4e86\u8bb8\u4e45\uff0c\u4ecd\u6ca1\u6709\u770b\u5230\u91d1\u886b\u9999\u4e3b\u7684\u5f71\u5b50\uff0c\u6b63\u81ea\u6709\u4e9b\u7740\u6025\uff1a\u4ed6\u5fc3\u5ff5\u8f6c\u5904\uff0c\u4e0d\u7981\u731b\u5730\u4e00 \u52a8\uff0c\u4ed6\u5fae\u629a\u4e0a\u989d\uff0c\u6697\u7b11\u81ea\u5df1\uff01 \u3000\u3000\u201c\u6211\u600e\u7684\u53d8\u5f97\u8fd9\u4e48\u7b28\uff01\u5c71\u4e0d\u4f1a\u6765\u627e\u6211\uff0c\u6211\u96be\u9053\u4e5f\u4e0d\u4f1a\u53bb\u627e\u5c71\u5417\uff01\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u8d70\u8fdb\u4e86\u4e00\u5bb6\u5f88\u70ed\u95f9\u7684\u8336\u9986\u3002 \u3000\u3000\u8fd9\u56e0\u4e3a\u4ed6\u4e45\u8d70\u6c5f\u6e56\uff0c\u77e5\u9053\u8fd9\u8336\u9986\u4e4b\u4e2d\uff0c\u4e5d\u6d41\u4e09\u6559\uff0c\u4eba\u54c1\u6700\u662f\u590d\u6742\uff0c\u6b63\u9002\u5408\u81ea\u5df1\u6b64\u523b\u6240 \u7528\u3002 \u3000\u3000\u4ed6\u4e00\u8d70\u8fdb\u8336\u9986\uff0c\u76ee\u5149\u56db\u626b\uff0c\u5c31\u770b\u5230\u5ea7\u4e2d\u5927\u90fd\u76f4\u7709\u6123\u773c\u7684\u6c49\u5b50\uff0c\u6697\u4e2d\u6ee1\u610f\u5730\u4e00\u7b11\uff0c\u7b14\u76f4\u5730 \u8d70\u5230\u4e00\u5f20\u5750\u7740\u56db\u4e2a\u5f6a\u5f62\u5927\u6c49\u7684\u684c\u5b50\u65c1\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\uff0c\u671d\u684c\u65c1\u90a3\u5f20\u957f\u677f\u51f3\u4e0a\u7684\u7a7a\u5904\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5f6a\u5f62\u5927\u6c49\u672c\u5728\u8c08\u7740\u8bdd\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u53ef\u90fd\u6115\u4f4f\u4e86\uff0c\u4f46\u671b\u4e86\u4f0a\u98ce\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u8863\u5c65\u4e4b \u95f4\uff0c\u6c14\u6d3e\u4e0d\u51e1\uff0c\u5fc3\u91cc\u867d\u5947\u602a\uff0c\u4ecd\u6ca1\u6709\u53d1\u4f5c\u3002 \u3000\u3000\u90a3\u77e5\u4f0a\u98ce\u7a81\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u5c06\u684c\u4e0a\u8336\u676f\u90fd\u9707\u5f97\u98de\u4e86\u8d77\u6765\u3002\u8fd9\u56db\u4e2a\u6c49\u5b50\u5374\u90fd\u4e0d\u7981\u52c3\u7136\u8272\u53d8\uff0c \u4e00\u4e2a\u6ee1\u5934\u765e\u75e2\u7684\u6c49\u5b50\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u77aa\u7740\u4e00\u53cc\u6ee1\u5e03\u7ea2\u4e1d\u7684\u91d1\u9c7c\u773c\uff0c\u6307\u7740\u4f0a\u98ce\uff0c\u7834\u53e3\u9a82\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u662f\u6d3b\u7684\u4e0d\u8010\u70e6\u4e86\uff0c\u662f\u600e\u4e48\u7740\uff1f\u4e5f\u4e0d\u6253\u542c\u6253\u542c\u4ffa\u767d\u6591\u864e\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u4f60\u8981\u662f\u6d3b \u7684\u4e0d\u8010\u70e6\u4e86\uff0c\u5c31\u5230\u522b\u7684\u5730\u65b9\u53bb\u6b7b\uff0c\u4e0d\u8981\u8dd1\u5230\u8fd9\u91cc\u6765\u6b7b\uff01\u201d \u3000\u3000\u6115\u91cc\u6115\u6c14\u7684\uff0c\u6b63\u662f\u7eaf\u7cb9\u7684\u6cb3\u5357\u8bdd\u3002 \u3000\u3000\u4f0a\u98ce\u6545\u610f\u51b7\u7b11\u4e00\u58f0\uff0c\u500f\u5730\u4ece\u684c\u4e0a\u6284\u8d77\u4e00\u4e2a\u8336\u58f6\u6765\uff0c\u55d6\u5730\u671d\u8fd9\u201c\u767d\u6591\u864e\u201d\u5934\u4e0a\u62a1\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u8eab\u624b\uff0c\u8981\u62a1\u4e2d\u201c\u767d\u6591\u864e\u201d\u90a3\u9897\u957f\u6ee1\u4e86\u765e\u75e2\u7684\u8111\u888b\uff0c\u8fd8\u4e0d\u5bb9\u6613\uff1f\u53ea\u662f\u4ed6\u5374\u6545\u610f\u5c06\u8fd9 \u8336\u58f6\u62a1\u5f97\u8fdc\u8fdc\u7684\uff0c\u4e00\u9762\u5927\u9a82\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u8fd9\u6279\u5929\u4e89\u6559\u7684\u72d7\u817f\u5b50\uff0c\u770b\u5230\u5927\u7237\u6765\uff0c\u8fd8\u4e0d\u5feb\u7ed9\u6211\u8dea\u4e0b\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u9a82\uff0c\u8fd8\u771f\u9a82\u5bf9\u4e86\u3002\u539f\u6765\u5929\u4e89\u6559\u5728\u8fd9\u5f00\u5c01\u5730\u9762\u4e0a\u7684\u52bf\u529b\u9887\u5927\uff0c\u8fd9\u4e9b\u6ce1\u8336\u9986\u7684\u95f2\u6c49\uff0c \u5012\u6709\u4e00\u534a\u662f\u5c5e\u5929\u4e89\u6559\u7684\u5f00\u5c01\u5206\u8235\u4e4b\u4e0b\u3002 \u3000\u3000\u56e0\u662f\u8336\u9986\u91cc\u767b\u65f6\u5927\u4e71\uff0c\u55d6\u5730\u7ad9\u8d77\u4e86\u4e00\u5927\u534a\u4eba\u6765\uff0c\u6709\u7684\u5f80\u5916\u9762\u8dd1\uff0c\u6709\u7684\u5c31\u5927\u58f0\u559d\u9a82\u7740\uff0c\u767d \u6591\u864e\u5374\u5288\u9762\u4e00\u62f3\uff0c\u671d\u4f0a\u98ce\u9762\u95e8\u6253\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u51b7\u7b11\u4e00\u58f0\uff0c\u624b\u8155\u500f\u7136\u7a7f\u51fa\uff0c\u53ea\u7528\u4e86\u4e09\u6210\u65b9\uff0c\u5201\u4f4f\u8fd9\u7c97\u6c49\u7684\u624b\u8155\uff0c\u53cd\u624b\u4e00\u62e7\uff0c\u90a3\u201c\u767d \u6591\u864e\u201d\u7acb\u523b\u50cf\u53ea\u88ab\u5bb0\u7684\u732a\u4e00\u6837\u5730\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u7565\u5c55\u8eab\u624b\uff0c\u6253\u5f97\u8fd9\u6279\u7c97\u6c49\u53eb\u82e6\u8fde\u5929\uff01\u8336\u9986\u7684\u684c\u5b50\uff0c\u6905\u5b50\uff0c\u90fd\u98de\u5230\u8def\u4e0a\uff1a\u8def\u4e0a\u7684\u7816 \u5934\uff0c\u77f3\u5757\uff0c\u5374\u98de\u5230\u8336\u9986\u91cc\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u4e3e\uff0c\u5f53\u7136\u662f\u60f3\u5c06\u90a3\u5f00\u5c01\u57ce\u91cc\u7684\u91d1\u886b\u9999\u4e3b\u5f15\u6765\uff0c\u4ee5\u671f\u6270\u4e71\u5929\u4e89\u6559\u7684\u8033\u76ee\u3002\u53e6\u4e00\u65b9 \u9762\uff0c\u5374\u662f\u4ed6\u5bf9\u5929\u4e89\u6559\u79ef\u6028\u5df2\u6df1\uff0c\u60f3\u85c9\u6b64\u51fa\u51fa\u6c14\u3002 \u3000\u3000\u4f46\u4ed6\u81ea\u5df1\u77e5\u9053\uff1a\u81ea\u5df1\u6b64\u523b\u5185\u529b\u7684\u4fee\u4e3a\uff0c\u51fa\u624b\u4e0d\u8fc7\u53ea\u4f7f\u4e86\u4e24\uff0c\u4e09\u6210\u529b\u9053\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u7528\u6765\u5bf9\u4ed8\u8fd9\u6279\u7c97\u6c49\uff0c\u5374\u5df2\u8db3\u591f\u4e86\u3002 \u3000\u3000\u4f46\u6253\u4e86\u534a\u5929\uff0c\u91d1\u886b\u9999\u4e3b\u7684\u5f71\u5b50\u90fd\u6ca1\u6709\u770b\u5230\uff0c\u4f0a\u98ce\u4e0d\u7981\u5728\u5fc3\u91cc\u6697\u9a82\uff1a\u201c\u8fd9\u6279\u5c0f\u5b50\u7684\u67b6\u5b50\u5012 \u4e0d\u5c0f\uff01\u201d \u3000\u3000\u4f46\u5fc3\u91cc\u53ef\u53c8\u6709\u4e9b\u7740\u6025\uff0c\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u603b\u4e0d\u662f\u4e8b\u3002 \u3000\u3000\u90a3\u77e5\u5fc3\u5ff5\u529b\u52a8\u95f4\uff0c\u5ffd\u7136\u542c\u5230\u4e00\u58f0\u66b4\u559d\uff1a \u3000\u3000\u201c\u90fd\u7ed9\u6211\u7ad9\u7740\uff01\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u559c\uff1a\u90a3\u8bdd\u513f\u6765\u4e86\u3002 \u3000\u3000\u76ee\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u8336\u9986\u91cc\u52a8\u7740\u624b\u7684\u6c49\u5b50\uff0c\u679c\u7136\u542c\u8bdd\uff0c\u4e00\u4e2a\u4e2a\u5168\u90fd\u4f4f\u624b\u3002 \u3000\u3000\u518d\u671d\u53d1\u8bdd\u4e4b\u5904\u4e00\u671b\uff0c\u5374\u4e0d\u7981\u5927\u5931\u6240\u671b\u3002 \u3000\u3000\u539f\u6765\u6765\u7684\u53ea\u662f\u4e00\u4e2a\u7a7f\u84dd\u8863\u7684\uff0c\u548c\u4e00\u4e2a\u7a7f\u7d2b\u8863\u7684\u6c49\u5b50\u3002\u4f0a\u98ce\u77e5\u9053\uff0c\u8fd9\u4e2a\u7a7f\u7d2b\u8863\u7684\u6c49\u5b50\uff0c\u5927 \u7ea6\u5c31\u662f\u5929\u4e89\u6559\u7684\u201c\u7d2b\u8863\u9999\u4e3b\u201d\uff0c\u800c\u201c\u7d2b\u8863\u9999\u4e3b\u201d\u5728\u5929\u4e89\u6559\u4e2d\u7684\u5730\u4f4d\u867d\u4e0d\u4f4e\uff0c\u5374\u4e0d\u89c1\u5f97\u89c1\u8fc7\u6559 \u4e3b\u7684\u9762\u76ee\u3002 \u3000\u3000\u679c\u7136\uff0c\u8fd9\u7d2b\u8863\u9999\u4e3b\u5927\u524c\u524c\u5730\u8d70\u5230\u4f0a\u98ce\u8eab\u524d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\u662f\u90a3\u6761\u9053\u4e0a\u7684\uff1f\u8eab\u624b\u8fd8\u4e0d\u5f31\uff0c\u4f46\u51ed\u7740\u4efd\u8eab\u624b\uff0c\u5c31\u60f3\u5728\u5f00\u5c01\u5730\u9762\u4e0a\u6492\u91ce\uff0c\u670b\u53cb\uff01\u4f60 \u7684\u62db\u5b50\u4e5f\u5c31\u592a\u4e0d\u4eae\u5566\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u7136\u7a9c\u5730\u4e00\u4e2a\u7bad\u6b65\uff0c\u5de6\u624b\u4e00\u9886\u8fd9\u7d2b\u8863\u9999\u4e3b\u7684\u773c\u795e\uff0c\u53f3\u817f\u4e00\u52fe\uff0c\u4e00\u4e2a\u201c\u626b \u5802\u817f\u201d\uff0c\u671d\u4ed6\u4e0b\u4e09\u8def\u626b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u7d2b\u8863\u4eba\u5728\u6cb3\u5357\u7701\u5185\u4e5f\u6709\u7740\u4e0d\u5c0f\u7684\u201c\u4e07\u513f\u201d\uff0c\u6b66\u529f\u4e5f\u8fd8\u4e0d\u5f31\uff0c\u600e\u4f1a\u5c06\u201c\u626b\u5802\u817f\u201d\u8fd9\u79cd\u5e84 \u7a3c\u628a\u5f0f\u653e\u5728\u773c\u91cc\uff1f\u51b7\u7b11\u4e00\u58f0\uff0c\u53f3\u62f3\u51fa\u62f3\u5982\u98ce\uff0c\u51fb\u5411\u4f0a\u98ce\u80f8\u819b\uff0c\u5de6\u638c\u5374\u55d6\u5730\u5f80\u4f0a\u98ce\u90a3\u6761\u626b\u6765\u7684 \u817f\u4e0a\uff0c\u5207\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u53e3\u91cc\u60ca\u5524\u4e00\u58f0\uff0c\u8e22\u51fa\u53bb\u7684\u8fd9\u4e00\u817f\uff0c\u751f\u50cf\u662f\u5df2\u7ecf\u51fa\u4e86\u5168\u529b\uff0c\u6536\u4e0d\u56de\u6765\u4e86\u4f3c\u7684\uff0c\u6781\u529b\u5411 \u540e\u7f29\u3002\u90a3\u7d2b\u8863\u4eba\u53e3\u7483\u51b7\u7b11\uff0c\u624b\u638c\u4e00\u7ffb\uff0c\u53ea\u89c1\u4f0a\u98ce\u811a\u4e0b\u4e00\u4e2a\u8dc4\u8ddf\uff0c\u201c\u5657\u201d\u5730\u7adf\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u521a\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u8d77\u5148\u88ab\u4f0a\u98ce\u63cd\u5f97\u6655\u5934\u8f6c\u5411\u7684\u5929\u4e89\u6559\u5f92\uff0c\u6b64\u523b\u4e0d\u7981\u90fd\u559d\u8d77\u91c7\u6765\u3002 \u3000\u3000\u90a3\u7d2b\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u53f1\u9053\uff1a\u201c\u670b\u53cb\uff01\u4f60\u8fd8\u662f\u8001\u8001\u5b9e\u5b9e\u5730\u7ed9\u5927\u7237\u722c\u5728\u90a3\u513f\u5427\uff01\u4f60\u8981\u901e\u80fd\uff0c \u4e5f\u5f97\u6361\u6361\u5730\u65b9\u5440\uff01\u201d \u3000\u3000\u5f97\u610f\u4e4b\u8272\uff0c\u6ea2\u4e8e\u8a00\u8868\uff0c\u4fa7\u76ee\u53c8\u559d\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5144\u4eec\uff01\u58eb\u8fdf\u4e0d\u628a\u8fd9\u602f\u8d27\u56f0\u8d77\u6765\uff0c\u9001\u56de\u603b\u8235\u53bb\uff0c\u8ba9\u5c06\u8235\u4e3b\u53d1\u843d\uff01\u201d \u3000\u3000\u4f0a\u98ce\u505a\u51fa\u4e00\u5fd6\u5782\u9996\u4e27\u6c14\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u5374\u5728\u6697\u6697\u9ad8\u5174\uff0c\u6697\u5fd6\u81ea\u5df1\u4e00\u8de4\uff0c\u603b\u7b97\u8dcc\u5f97\u4e0d\u9519\uff0c\u603b \u7b97\u80fd\u89c1\u7740\u8fd9\u5f00\u5c01\u5e9c\u91cc\u7684\u91d1\u8863\u9999\u4e3b\u4e86\u3002 \u3000\u3000\u4f46\u7b49\u5230\u90a3\u4e9b\u5929\u4e89\u6559\u5f92\u53e3\u91cc\u9a82\u7740\u7c97\u8bdd\uff0c\u4e03\u624b\u516b\u811a\u6765\u56f0\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u5728\u5fc3\u91cc\u53c8\u4e0d\u7981\u6697\u9a82\uff0c\u6068 \u4e0d\u5f97\u4e00\u62f3\u4e00\u811a\uff0c\u518d\u5c06\u8fd9\u6279\u7c97\u6c49\uff0c\u6253\u4e2a\u75db\u5feb\u3002 \u3000\u3000\u90a3\u7d2b\u8863\u9999\u4e3b\u4e24\u773c\u4e0a\u7ffb\uff0c\u80cc\u8d1f\u7740\u624b\uff0c\u9886\u5934\u524d\u8d70\uff0c\u90a3\u79cd\u4e0d\u53ef\u4e00\u4e16\u7684\u6837\u5b50\uff0c\u7684\u7f79\u4ee4\u4eba\u96be\u4ee5\u5fcd \u53d7\uff01 \u3000\u3000\u4e24\u4e2a\u76f4\u7709\u6123\u773c\u7684\u6c49\u5b50\uff0c\u5c06\u4f0a\u98ce\u4e94\u82b1\u5927\u7ed1\u4e86\u8d77\u6765\uff0c\u62d6\u62d6\u62c9\u62c9\u5730\uff0c\u5c06\u4ed6\u62fd\u5230\u8857\u53e3\uff0c\u5f04\u4e86\u8f86\u5927 \u8f66\uff0c\u5c06\u4ed6\u201c\u7830\u201d\u5730\u629b\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u5fcd\u4f4f\u6c14\uff0c\u5374\u89c1\u90a3\u8dbe\u9ad8\u6c14\u626c\u7684\u7d2b\u8863\u9999\u4e3b\u4e5f\u5750\u4e0a\u4e86\u8f66\uff0c\u9a6c\u8f66\u5c31\u8f9a\u8f9a\u524d\u884c\u3002 \u3000\u3000\u90a3\u7d2b\u8863\u9999\u4e3b\u6a2a\u7740\u773c\u775b\u671b\u7740\u4ed6\uff0c\u51b7\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u59d3\u4ec0\u4e48\uff01\u53eb\u4ec0\u4e48\u4e8c\u758b\u53d7\u8c01\u7684\u4e3b\u4f7f\u5230\u8fd9\u91cc\u6765\u6492\u91ce\uff01\u4f60\u8981\u662f\u8001\u8001\u5b9e\u5b9e\u62db\u51fa\u6765\uff0c\u8fd8 \u53ef\u4ee5\u5c11\u53d7\u70b9\u82e6\uff1a\u4e0d\u7136\u2026\uff1a\u7761=\uff01\u90a3\u4f60\u5403\u4e0d\u4e86\uff0c\u515c\u7740\u8d70\uff0c\u90a3\u4f60\u7684\u4e50\u5b50\u53ef\u5c31\u5927\u4e86\uff01\u201d \u3000\u3000\u4f0a\u98ce\u95ed\u7740\u773c\uff0c\u4e5f\u4e0d\u56de\u7b54\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u90a3\u7d2b\u8863\u9999\u4e3b\u53cc\u7709\u4e00\u8f69\uff0c\u6012\u9a82\u9053\uff1a \u3000\u3000\u201c\u6740\u80da\uff01\u4f60\u73b0\u5728\u8981\u662f\u4e0d\u8bf4\u8bdd\uff0c\u7b49\u4f1a\u513f\u5927\u7237\u4e0d\u53eb\u4f60\u6367\u4f4f\u8116\u5b50\u53eb\u5976\u5976\uff0c\u5927\u7237\u5c31\u4e0d\u53eb\u5c0f\u4e27 \u95e8\u3002\u201d \u3000\u3000\u8fd9\u7d2b\u8863\u9999\u4e3b\u5c0f\u4e27\u95e8\u9648\u656c\u4ec1\uff0c\u4e00\u8def\u53f1\u9a82\u7740\uff0c\u4f0a\u98ce\u5374\u50cf\u662f\u5b8c\u5168\u6ca1\u6709\u542c\u5230\u4f3c\u7684\u3002 \u3000\u3000\u8f66\u5b50\u8d70\u4e86\u7ea6\u6478\u4e24\u76cf\u8336\u529f\u592b\uff0c\u5c31\u505c\u4e86\u4e0b\u6765\u3002\u8fd9\u5c0f\u4e27\u95e8\u51b7\u7b11\u7740\u7ad9\u4e86\u8d77\u6765\uff0c\u201c\u7830\u201d\u5730\u91cd\u91cd\u8e22\u4e86 \u4f0a\u98ce\u4e00\u811a\uff0c\u53c8\u9a82\u9053\uff1a \u3000\u3000\u201c\u6b7b\u56da\uff01\u4f60\u7684\u5730\u5934\u5230\u4e86\u3002\u201d \u3000\u3000\u5927\u524c\u524c\u5730\u8d70\u4e86\u4e0b\u8f66\uff0c\u53c8\u53eb\u4e24\u4e2a\u6c49\u5b50\u5c06\u4f0a\u98ce\u62ac\u4e0b\u6765\uff0c\u81ea\u5df1\u5374\u62c2\u4e86\u62c2\u8863\u88f3\uff0c\u671d\u5927\u95e8\u91cc\u8d70\u4e86\u8fdb \u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u4e0b\u8f66\uff0c\u5c31\u770b\u5230\u9a6c\u8f66\u6240\u505c\u7684\u5730\u65b9\u662f\u4e00\u5e62\u5de8\u5b85\u7684\u95e8\u53e3\uff0c\u6731\u6f06\u7684\u5927\u95e8\uff0c\u53d1\u4eae\u7684\u95e8\u73af\uff0c\u95e8 \u7684\u4e24\u8fb9\uff0c\u4e00\u6392\u5341\u51e0\u4e2a\u7cfb\u9a6c\u7684\u77f3\u693f\u5b50\u3002\u6c14\u6d3e\u4e4b\u5927\uff0c\u5c31\u50cf\u662f\u4ec0\u4e48\u8fbe\u5b98\u8d35\u4eba\u7684\u5e9c\u90b8\u4f3c\u7684\uff0c\u751a\u81f3\u5c24\u6709 \u8fc7\u4e4b\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6c49\u5b50\uff0c\u9752\u8863\u9ed1\u5e3d\uff0c\u6253\u626e\u5f97\u50cf\u4e2a\u5bb6\u4ec6\uff0c\u751f\u50cf\u5374\u4ecd\u7136\u8131\u4e0d\u51f6\u6a2a\u4e4b\u6c14\uff0c\u4e5f\u662f\u4e00\u8def\u5406\u559d \u7740\uff0c\u5c06\u4f0a\u98ce\u5f04\u4e86\u8fdb\u53bb\uff0c\u7b80\u76f4\u6bd4\u8859\u95e8\u91cc\u6293\u5c0f\u5077\u7684\u5dee\u5f79\uff0c\u8fd8\u8981\u6a2a\u5f97\u591a\uff0c\u7adf\u6ca1\u6709\u5c06\u4f0a\u98ce\u5f53\u505a\u4eba\u770b \u5f85\uff01 \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u65e2\u6012\u53c8\u6c14\uff0c\u8fd9\u201c\u5929\u4e89\u6559\u201d\u7684\u51f6\u6a2a\uff0c\u770b\u6765\u7adf\u8fd8\u5728\u4f20\u95fb\u4e4b\u4e0a\uff01\u5c0f\u5c0f\u4e00\u4e2a\u5f00\u5c01\u5206\u8235\uff0c \u5904\u7f6e\u4e00\u4e2a\u53ea\u4e0d\u8fc7\u6f2b\u9a82\u4e86\u51e0\u53e5\u7684\u201c\u72af\u4eba\u201d\uff0c\u5c31\u6709\u8fd9\u4e48\u5389\u5bb3\uff01\u58eb;\u4f59\u7684\uff0c\u81ea\u7136\u66f4\u4e0d\u95ee\u53ef\u77e5\u4e86\u3002 \u3000\u3000\u5230\u4e86\u5927\u5385\u95e8\u53e3\uff0c\u90a3\u4e24\u4e2a\u6c49\u5b50\u5c06\u4f0a\u98ce\u5f80\u77f3\u9636\u4e0a\u4e00\u63a8\uff0c\u671d\u91cc\u9762\u8eac\u8eab\u9053\uff1a \u3000\u3000\u201c\u5916\u9762\u7684\u72af\u4eba\uff0c\u5df2\u7ecf\u5e26\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u7adf\u771f\u7684\u5c06\u4f0a\u98ce\u53eb\u505a\u201c\u72af\u4eba\u201d\u3002\u4f0a\u98ce\u5251\u7709\u5fae\u8f69\uff0c\u7709\u5fc3\u4e2d\u5df2\u9690\u9690\u9732\u51fa\u6740\u673a\uff01 \u3000\u3000\u5927\u5385\u6709\u4eba\u5e72\u54b3\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c06\u4ed6\u5e26\u4e0a\u6765\u3002\u201d\u4e00\u9762\u53c8\u9053\uff1a \u3000\u3000\u201c\u9648\u9999\u4e3b\uff01\u4f60\u4e5f\u672a\u514d\u592a\u4ed4\u7ec6\u4e86\uff0c\u8fd9\u79cd\u9e21\u6bdb\u849c\u76ae\u7684\u5c0f\u5b50\uff0c\u4f60\u81ea\u5df1\u5c06\u4ed6\u6253\u53d1\u4e86\uff0c\u4e0d\u5c31\u5b8c\u4e86\uff0c \u53c8\u4f55\u5fc5\u5e26\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u53ea\u542c\u65b9\u624d\u90a3\u5f20\u72c2\u53ef\u4e00\u4e16\u7684\u201c\u5c0f\u4e27\u95e8\u201d\u6b64\u523b\u4f4e\u58f0\u4e0b\u6c14\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8235\u4e3b\u8bf4\u7684\u662f\uff1a\u4e0d\u8fc7\u8fd9\u5c0f\u5b50\u624b\u5e95\u4e0b\u4f3c\u4e4e\u8fd8\u6709\u4e24\u4e0b\u5b50\uff0c\u57ce\u91cc\u5f1f\u5144\uff0c\u6709\u597d\u591a\u4e2a\u90fd\u683d\u5728\u4ed6\u624b\u91cc \u4e86\uff0c\u6240\u4ee5\u5728\u4e0b\u624d\u5c06\u4ed6\u9001\u5230\u8235\u4e3b\u8fd9\u91cc\u6765\u53d1\u843d\u3002\u201d \u3000\u3000\u8fd9\u5f00\u5c01\u5206\u8235\u7684\u8235\u4e3b\uff0c\u6b63\u662f\u201c\u76d8\u9f99\u94f6\u68cd\u201d\u848b\u4f2f\u9633\uff0c\u6b64\u523b\u4ed6\u6b63\u4e00\u624b\u7aef\u7740\u76d6\u7897\uff0c\u4e24\u773c\u671b\u5929\u7aef\u5750 \u5728\u5927\u5385\u6b63\u4e2d\u7684\u7ea2\u6728\u4ea4\u6905\u4e0a\uff0c\u90a3\u5c0f\u4e27\u95e8\u5374\u5782\u624b\u7ad9\u5728\u65c1\u8fb9\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u8fdb\u5927\u5385\uff0c\u5c31\u770b\u51fa\u8fd9\u5929\u4e89\u6559\u5f00\u5c01\u57ce\u91cc\u7684\u91d1\u8863\u9999\u4e3b\uff0c\u7adf\u662f\u5c11\u6797\u5f1f\u5b50\u848b\u4f2f\u9633\u6765\u3002 \u3000\u3000\u987b\u77e5\u4f0a\u98ce\u6614\u5e74\u904d\u5386\u6c5f\u6e56\uff0c\u8fd9\u201c\u76d8\u9f99\u94f6\u68cd\u201d\u848b\u4f2f\u9633\uff0c\u5728\u6b66\u6797\u4e2d\u7684\u540d\u58f0\u9887\u54cd\uff0c\u624b\u9762\u5f88\u9614\uff0c\u662f \u4ee5\u4f0a\u98ce\u4e5f\u81ea\u8ba4\u5f97\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6781\u5feb\u5730\u4e00\u8f6c\uff0c\u786e\u5b9a\u8fd9\u201c\u76d8\u9f99\u94f6\u68cd\u201d\u848b\u4f2f\u9633\uff0c\u5728\u5929\u4e89\u6559\u4e2d\u7684\u5730\u4f4d\uff0c\u662f\u7edd\u5bf9\u591f\u5f97\u4e0a\u89c1 \u8fc7\u6559\u4e3b\u7684\u771f\u9762\u76ee\u7684\uff0c\u90a3\u4e48\u6362\u53e5\u8bdd\u8bf4\uff0c\u5c31\u662f\u81ea\u5df1\u6b64\u523b\u9762\u5bb9\uff0c\u8fd9\u201c\u76d8\u9f99\u94f6\u68cd\u201d\u848b\u4f2f\u9633\u4e5f\u4e00\u5b9a\u8ba4 \u5f97\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u6545\u610f\u8f6c\u8fc7\u4e86\u5934\uff0c\u51b2\u7740\u5385\u5916\u3002 \u3000\u3000\u90a3\u5c0f\u4e27\u95e8\u5df2\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u6740\u80da\uff01\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u8fd9\u662f\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\uff01\u4f60\u8fd8\u6562\u8fd9\u4e48\u5f20\u72c2\uff01\u201d \u3000\u3000\u90a3\u201c\u76d8\u9f99\u6839\u68cd\u201d\u848b\u4f2f\u9633\u6380\u8d77\u7897\u76d6\uff0c\u559d\u4e86\u8336\uff0c\u4e5f\u81ea\u6c89\u58f0\u53f1\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u4e3a\u7740\u4ec0\u4e48\u539f\u56e0\uff0c\u5230\u5f00\u5c01\u5e9c\u57ce\u91cc\u6765\uff1f\u4f60\u8d76\u7d27\u597d\u751f\u544a\u8bc9\u6211\uff01\u53ea\u8981\u4f60\u5b57\u5b57\u4e0d\u865a\uff0c\u6211 \u4e5f\u4e0d\u4f1a\u600e\u4e48\u96be\u4e3a\u4f60\uff1a\u4e0d\u7136\u7684\u8bdd\uff0c\u4f60\u53ef\u77e5\u8981\u77e5\u9053\uff0c\u201c\u5929\u4e89\u6559\u201d\u4e09\u4e2a\u5b57\uff0c\u53ef\u5bb9\u4e0d\u5f97\u4f60\u5728\u8857\u4e0a\u6f2b\u9a82 \u7684\u54e9\u3002\u201d \u3000\u3000\u8fd9\u201c\u76d8\u9f99\u94f6\u68cd\u201d\u848b\u4f2f\u9633\uff0c\u679c\u7136\u4e0d\u6127\u4e3a\u6b63\u6d3e\u51fa\u8eab\uff0c\u53e3\u4e2d\u5012\u4e5f\u4e0d\u5e26\u79fd\u5b57\uff0c\u6bd4\u8d77\u90a3\u4e9b\u8349\u83bd\u51fa\u8eab \u7684\u89d2\u8272\uff0c\u786e\u662f\u8981\u9ad8\u660e\u4e00\u4e9b\u3002 \u3000\u3000\u4f0a\u98ce\u5374\u4ecd\u5bd2\u7740\u8138\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u6211\u5230\u5f00\u5c01\u57ce\u6765\uff0c\u5c31\u4e3a\u7684\u662f\u627e\u4f60\uff0c\u96be\u9053\u4f60\u8fd9\u7b97\u662f\u5f85\u5ba2\u4e4b\u9053\u5417\uff01\u201d \u3000\u3000\u848b\u4f2f\u9633\u201c\u7830\u201d\u5730\u5c06\u76d6\u7897\u653e\u5230\u684c\u4e0a\uff0c\u7897\u91cc\u7684\u70ed\u8336\uff0c\u6e85\u5f97\u4e00\u684c\u90fd\u662f\u3002\u4ed6\u53cc\u7709\u5012\u7ad6\uff0c\u5df2\u542b\u6012 \u610f\uff0c\u76ee\u5149\u5982\u70ac\uff0c\u5389\u58f0\u53f1\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u58c1\u6761\u5b50\u653e\u6e05\u695a\u4e9b\uff01\u4f60\u8981\u4e70\u5c06\u201c\u5929\u4e89\u6559\u201d\u770b\u5f97\u592a\u9a6c\u864e\uff0c\u90a3\u662f\u81ea\u8ba8\u82e6\u5403\uff01\u201d \u3000\u3000\u4f0a\u98ce\u84e6\u5730\u653e\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5c06\u56f0\u5728\u8eab\u4e0a\u7684\u7c97\u7d20\uff0c\u9707\u5f97\u5bf8\u5bf8\u65ad\u843d\u3002 \u3000\u3000\u4ed6\u957f\u7b11\u7740\u56de\u8fc7\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u848b\u4f2f\u9633\uff01\u4f60\u96be\u9053\u4e0d\u8ba4\u5f97\u6211\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u201c\u76d8\u9f99\u6839\u68cd\u201d\u770b\u5230\u8fd9\u201c\u72c2\u4eba\u201d\u5c45\u7136\u971e\u65ad\u7ef3\u7d22\uff0c\u529b\u81ea\u5927\u60ca\uff1a\u90a3\u5c0f\u4e27\u95e8\u5df2\u6012\u53f1\u7740\u671d\u4f0a\u98ce\u6251 \u4e86\u4e0a\u53bb\uff0c\u55d6\u55d6\u4e24\u638c\uff0c\u5288\u5411\u4f0a\u98ce\u3002 \u3000\u3000\u53ef\u662f\uff0c\u848b\u4f2f\u9633\u5b9a\u775b\u4e4b\u4e0b\uff0c\u5df2\u770b\u51fa\u8fd9\u201c\u72af\u4eba\u201d\u662f\u8c01\u6765\u4e86\u3002 \u3000\u3000\u5c0f\u4e27\u95e8\u9648\u656c\u4ec1\u5de6\u638c\u6a2a\u5207\u4f0a\u98ce\u7684\u80f8\u819b\uff0c\u53f3\u638c\u659c\u659c\u4e0b\u5288\uff0c\u8fde\u80a9\u5e26\u9888\u5288\u4e0b\uff0c\u5374\u89c1\u8fd9\u4eba\u7adf\u7136\u8fd8\u5e26 \u7740\u7b11\u7ad9\u7740\uff0c\u65e2\u4e0d\u907f\uff0c\u4e5f\u4e0d\u95ea\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u6b63\u81ea\u5947\u602a\uff0c\u90a3\u77e5\u8eab\u540e\u7a81\u5730\u98ce\u58f0\u55d6\u7136\uff0c\u4f3c\u4e4e\u6709\u4eba\u91cd\u91cd\u4e00\u62f3\uff0c\u6b63\u6253\u5411\u81ea\u5df1\u7684\u540e\u80cc\uff0c\u4ed6 \u81ea\u6551\u4e3a\u5148\uff0c\u987e\u4e0d\u5f97\u653b\u654c\uff0c\u8155\u8098\u5fae\u6c89\uff0c\u811a\u8ddf\u7acb\u65cb\u3002 \u3000\u3000\u90a3\u77e5\u8eab\u540e\u5df2\u53f1\u9053\uff1a \u3000\u3000\u201c\u9648\u656c\u4ec1\uff01\u8198\u7ed9\u6211\u4f4f\u624b\uff01\u201d \u3000\u3000\u7adf\u662f\u90a3\u201c\u76d8\u9f99\u94f6\u68cd\u201d\u848b\u4f2f\u9633\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5c0f\u4e27\u95e8\u66f4\u662f\u5927\u4e3a\u60ca\u9a87\u8be7\u5f02\uff0c\u5ff5\u5934\u8fd8\u4e0d\u53ca\u8f6c\u5b8c\uff0c\u90a3\u76d8\u9f99\u94f6\u68cd\u5df2\u7830\u5730\u4e00\u638c\uff0c\u5c06\u4ed6\u8e6c\uff0c\u8e6c\uff0c \u8e6c\uff0c\u6253\u5f97\u5411\u65c1\u8fb9\u51b2\u51fa\u4e94\uff0c\u516d\u6b65\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f2f\u9633\u5144\u8fd8\u8ba4\u5f97\u6211\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u8179\u4e2d\u4e5f\u5728\u597d\u7b11\uff0c\u770b\u7740\u8fd9\u848b\u4f2f\u9633\u9762\u8272\u5982\u571f\u5730\uff0c\u671d\u81ea\u5df1\u6df1\u6df1\u8eac\u8170\u53bb\uff0c\u4e00\u9762\u8bda\u60f6\u8bda\u6050\u5730 \u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f2f\u9633\u4e0d\u77e5\u9053\u662f\u6559\u4e3b\u6765\u4e86\uff0c\u672a\u66fe\u8fdc\u8fce\uff0c\u53c8\u6559\u90a3\u73ed\u8822\u624d\u6709\u773c\u65e0\u73e0\uff0c\u5192\u72af\u4e86\u6559\u4e3b\uff0c\u5b9e\u662f\u6b7b \u7f6a\uff0c\u8fd8\u8bf7\u6559\u4e3b\u4ece\u4e25\u60e9\u5904\u3002\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u6b63\u81ea\u4e00\u5934\u9732\u6c34\uff0c\u542c\u5230\u848b\u4f2f\u9633\u8fd9\u4e00\u8bf4\uff0c\u6ee1\u5934\u7684\u96fe\uff0c\u5374\u90fd\u5316\u4e3a\u51b7\u6c34\uff0c\u4e00\u76f4\u6d47\u5230\u80cc\u810a \u91cc\uff0c\u7531\u80cc\u810a\u900f\u51fa\u4e00\u80a1\u5bd2\u6c14\u3002 \u3000\u3000\u4ed6\u7528\u624b\u6478\u4e86\u6478\u81ea\u5df1\u7684\u989d\u89d2\uff0c\u4e24\u817f\u865a\u98d8\u98d8\u7684\uff0c\u751f\u50cf\u662f\u5df2\u8f6f\u4e86\u534a\u622a\uff0c\u5f80\u524d\u9762\u8d70\u4e24\u6b65\uff0c\u5b9a\u4e86\u5b9a \u795e\uff0c\u5657\u5730\u4e00\u58f0\uff0c\u7adf\u8dea\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u8f6c\u52a8\uff0c\u4ef0\u9996\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u624b\u4e0a\u7528\u4e86\u4e03\u6210\u771f\u529b\uff0c\u671d\u5c0f\u4e27\u95e8\u4e00\u63a8\uff0c\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u7684\u6b66\u529f\u4fca\u5f97\u5f88\uff01\u638c\u4e0a\u4f3c\u4e4e\u6709\u5317\u6d3e\u6768\u5bb6\u638c\u7684\u5473\u9053\u2026\u2026\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u53ea\u89c9\u8fde\u8dea\u90fd\u8dea\u4e0d\u4f4f\u4e86\uff0c\u8eab\u5b50\u6643\u4e86\u6643\uff0c\u5fc3\u91cc\u66f4\u60ca\u60f6\uff0c\u4e0d\u7b49\u8fd9\u4e2a\u5192\u724c\u6559\u4e3b\u7684\u8bdd\u8bf4\u5b8c\uff0c \u5c31\u62a2\u7740\u9053\uff1a \u3000\u3000\u201c\u5c0f\u7684\u4e0d\u77e5\u9053\u662f\u6559\u4e3b\u5927\u9a7e\uff0c\u5192\u72af\u4e86\u6559\u4e3b\uff0c\u4f46\u671b\u6559\u4e3b\u6055\u7f6a\u3002\u201d \u3000\u3000\u8fd9\u201c\u5c0f\u4e27\u95e8\u201d\u4f0f\u5728\u5730\u4e0a\u5374\u50cf\u53ea\u4e27\u5bb6\u4e4b\u72ac\u4f3c\u7684\uff0c\u4f0a\u98ce\u60f3\u5230\u4ed6\u65b9\u624d\u90a3\u79cd\u9a84\u6a2a\u7684\u6837\u5b50\uff0c\u548c\u73b0\u5728 \u4e00\u6bd4\uff0c\u4ed6\u7684\u7b11\u58f0\uff0c\u4e0d\u7981\u8d8a\u53d1\u9ad8\u4ea2\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u653e\u773c\u5929\u4e0b\uff0c\u50cf\u201c\u5c0f\u4e27\u95e8\u201d\u8fd9\u6837\u7684\u4eba\uff0c\u6b63\u662f\u591a\u5f97\u4e0d\u53ef\u80dc\u6570\u54e9\uff01\uff0e\u4e00\u8304\u201c\u4e94\u65ec*\u53cd \u3000\u3000\u4f0a\u98ce\u7b11\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u76ee\u5149\u51db\u6b8a\u626b\u5728\u8fd9\u201c\u5c0f\u4e27\u95e8\u201d\u8eab\u4e0a\uff0c\u9053 \u3000\u3000\u201c\u5f00\u5c01\u57ce\u91cc\u7684\u5f1f\u5144\u4eec\uff0c\u4e5f\u8d8a\u6765\u8d8a\u4e0d\u50cf\u8bdd\u4e86\uff0c\u8981\u77e5\u9053\u6211\u521b\u7acb\u8fd9\u5929\u4e89\u6559\uff0c\u662f\u8981\u505a\u4e00\u756a\u5927\u4e8b\u4e1a \u7684\uff0c\u73b0\u5728\u4ed6\u4eec\u5374\u7528\u6765\u505a\u4ed7\u52bf\u6b3a\u4eba\u7684\u62db\u724c\u3002\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u98a4\u6296\u7740\u4f0f\u5728\u5730\u4e0a\uff0c\u8fde\u8fde\u79f0\u662f\uff0c\u76d8\u9f99\u94f6\u68cd\u4e5f\u9a87\u5f97\u9762\u76ee\u53d8\u8272\u3002\u4f0a\u98ce\u770b\u5728\u773c\u91cc\uff0c\u89c9\u5f97\u8fd9 \u201c\u5929\u4e89\u6559\u4e3b\u201d\u7684\u5a01\u52bf\uff0c\u5b9e\u5728\u4e0d\u5c0f\u3002\u81ea\u5df1\u95ef\u6c64\u6c5f\u6e56\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u5374\u626e\u6f14\u4e86\u5982\u6b64\u8fd9\u4e48\u4e00\u4e2a\u89d2\u8272\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u4ed6\u7684\u5fc3\u91cc\u5ffd\u6b8a\u63a0\u8fc7\u4e00\u79cd\u5fae\u5999\u7684\u611f\u89c9\u3002 \u3000\u3000\u987b\u77e5\u201c\u6743\u52bf\u201d\u4e24\u5b57\uff0c\u6b63\u662f\u81ea\u53e4\u4ee5\u6765\u4eba\u4eba\u60f3\u5f97\u5230\u7684\u4e1c\u897f\u3002\u53e4\u5f80\u4eca\u6765\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u82f1\u96c4\u8c6a\u6770 \u7684\u5343\u79cb\u4e8b\u4e1a\uff0c\u4fbf\u662f\u5efa\u7acb\u5728\u8fd9\u201c\u6743\u52bf\u201d\u4e24\u5b57\u4e4b\u4e0a\u3002\u53ea\u4e0d\u8fc7\u8981\u770b\u8fd9\u638c\u63e1\u201c\u6743\u52bf\u201d\u7684\u4eba\uff0c\u662f\u5426\u8fd0\u7528 \u5f97\u5f53\u7f62\u4e86\u3002 \u3000\u3000\u201c\u82e5\u4f60\u5c06\u201c\u6743\u52bf\u201d\u505a\u4e3a\u4f60\u7684\u5974\u96b6\uff0c\u800c\u8fd0\u7528\u5b83\u505a\u6210\u4e00\u756a\u4e8b\u4e1a\uff0c\u90a3\u4f60\u4fbf\u662f\u6210\u529f\u7684\uff0c\u4f46\u662f\u4f60\u82e5 \u53d8\u4e3a\u201c\u6743\u52bf\u201d\u7684\u5974\u96b6\uff0c\u90a3\u4f60\u5c31\u503c\u5f97\u60b2\u54c0\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u611f\u6168\u7740\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5ffd\u5730\u770b\u5230\u201c\u5c0f\u4e27\u95e8\u201d\u548c\u201c\u76d8\u9f99\u94f6\u68cd\u201d\u7684\u56db\u53ea\u773c\u775b\uff0c\u6b63\u5728\u671b \u7740\u81ea\u5df1\uff0c\u5fc3\u5ff5\u6570\u8f6c\uff0c\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u848b\u5e08\u5085\uff01\u57ce\u5916\u4e8c\u5341\u91cc\u94fa\uff0c\u6709\u4e00\u95f4\u5305\u6c0f\u5bb6\u7960\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u5e76\u6ca1\u6709\u7b49\u5f85\u8fd9\u848b\u4f2f\u9633\u7684\u56de\u7b54\uff0c\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u4eca\u591c\u4e09\u66f4\uff0c\u848b\u5e08\u5085\u5c31\u8bf7\u5c06\u5f00\u5c01\u57ce\u91cc\u5929\u4e89\u6559\u4e0b\u6709\u804c\u53ef\u7684\u5f1f\u5b50\uff0c\u5168\u805a\u5230\u90a3\u5305\u6c0f\u5bb6\u7960\u91cc \u53bb\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u51db\uff1a \u3000\u3000\u201c\u848b\u5e08\u5085\uff01\u534a\u65e5\u4e4b\u95f4\uff0c\u4f60\u80fd\u5c06\u5f1f\u5b50\u90fd\u62db\u9f50\u5417\uff1f\u201d \u3000\u3000\u76d8\u9f99\u94f6\u68cd\u6b64\u523b\u4e5f\u5782\u7740\u5934\uff0c\u95fb\u8a00\u7acb\u523b\u5e94\u9053\uff1a \u3000\u3000\u201c\u8bf7\u6559\u4e3b\u653e\u5fc3\u597d\u4e86\uff0c\u4eca\u591c\u4e09\u66f4\uff0c\u4f2f\u9633\u5c31\u5728\u5305\u6c0f\u5bb6\u7960\u91cc\u5f00\u575b\uff0c\u7b49\u5019\u6559\u4e3b\u7684\u5927\u9a7e\u3002\u4e0d\u8fc7\uff0c\u4e0d \u8fc7\u82e5\u5c06\u6ee1\u57ce\u5f1f\u5b50\u90fd\u62db\u9f50\uff0c\u90a3\u4eba\u5c31\u2026\u2026\u201d \u3000\u3000\u4f0a\u98ce\u51b7\u54fc\u4e00\u58f0\uff0c\u622a\u4f4f\u4ed6\u7684\u8bdd\u9053\uff1a \u3000\u3000\u201c\u6211\u8bf4\u7684\u662f\u6709\u804c\u53ef\u7684\u5f1f\u5b50\uff0c\u4f60\u53ef\u542c\u6e05\u4e86\u3002\u201d \u3000\u3000\u848b\u4f2f\u9633\u7acb\u523b\u53c8\u5782\u9996\u79f0\u662f\u3002 \u3000\u3000\u4f0a\u98ce\u51b7\u7b11\u4e00\u58f0\uff0c\u5fae\u62c2\u8863\u8896\uff0c\u8ff3\u81ea\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u76d8\u9f99\u94f6\u68cd\u6025\u884c\u4e09\u6b65\uff0c\u8ddf\u5728\u4ed6\u540e\u9762\uff0c\u606d\u58f0\u9053\uff1a \u3000\u3000\u201c\u6559\u4e3b\u600e\u5730\u8fd9\u5c31\u8d70\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u966a\u8d77\u7b11\u8138\uff1a \u3000\u3000\u201c\u4f2f\u9633\u8fd9\u91cc\u6709\u4e24\u74f6\u4e0a\u597d\u7af9\u53f6\u9752\uff0c\u6559\u4e3b\u53ef\u8981\u559d\u4e24\u676f\u518d\u8d70\uff0c\u4e5f\u8ba9\u4f2f\u9633\u8868\u793a\u4e9b\u656c\u610f\u3002\u201d \u3000\u3000\u4f0a\u98ce\u8db3\u672a\u505c\u6b65\uff0c\u4eba\u5df2\u8d70\u5230\u9662\u5b50\u91cc\uff0c\u95fb\u8a00\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u848b\u5e08\u5085\u7684\u597d\u610f\uff0c\u6211\u5fc3\u9886\u4e86\u3002\u7b49\u660e\u5929\u529e\u5b8c\u6b63\u4e8b\uff0c\u518d\u6765\u6270\u4f60\u5427\u3002\u201d \u3000\u3000\u76d8\u9f99\u94f6\u68cd\u5f2f\u8170\u8eac\u8eab\u5730\u8ddf\u5728\u8eab\u540e\uff0c\u90a3\u7acb\u5728\u95e8\u524d\u7684\u4e24\u4e2a\u6c49\u5b50\uff0c\u6b64\u523b\u4e5f\u662f\u9762\u8272\u5982\u571f\uff0c\u609a\u7acb\u5728\u65c1 \u8fb9\uff0c\u8fde\u58f0\u5927\u6c14\u90fd\u4e0d\u6562\u5598\u51fa\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u8d70\u51fa\u4e86\u95e8\uff0c\u6325\u624b\u6b62\u4f4f\u4e86\u90a3\u76d8\u9f99\u94f6\u68cd\u7684\u606d\u9001\uff0c\u4e00\u8def\u65bd\u7136\u800c\u53bb\uff0c\u5fc3\u91cc\u5374\u4e0d\u7981\u6709\u7247\u4e00\u597d \u7b11\u3002 \u3000\u3000\u4ed6\u4e00\u8def\u8d70\u51fa\u57ce\u5916\uff0c\u57ce\u5916\u7409\u7483\u5854\u7684\u5c16\u9876\uff0c\u6b63\u5728\u5915\u9633\u4e2d\u707f\u7740\u91d1\u5149\u3002\u5f00\u5c01\u5360\u57ce\u7684\u5f71\u5b50\uff0c\u88ab\u5915\u9633 \u4e00\u6620\uff0c\u4e5f\u957f\u957f\u5730\u62d6\u4e86\u4e0b\u6765\uff0c\u538b\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u7cbe\u795e\u6781\u4e3a\u632f\u83d6\uff01 \u3000\u3000\u90a3\u6b66\u66f2\u661f\u541b\u7684\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u4ed6\u5df2\u4ed4\u7ec6\u770b\u8fc7\u4e00\u904d\uff0c\u867d\u7136\u8fd8\u672a\u80fd\u5c3d\u5f97\u5176\u4e2d\u7684\u5965\u79d8\uff0c\u4f46\u50cf\u4ed6 \u8fd9\u6837\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u53ea\u8981\u7a0d\u4e3a\u9886\u609f\u5230\u4e00\u4e9b\u8bc0\u8981\uff0c\u529f\u529b\u4fbf\u53ef\u7cbe\u8fdb\u4e0d\u5c11\u3002 \u3000\u3000\u8fd9\u4e24\u5e74\u6765\uff0c\u4ed6\u867d\u7136\u7ecf\u8fc7\u4e0d\u5c11\u6298\u78e8\u5371\u96be\uff0c\u4f46\u8fd9\u4e9b\u6298\u78e8\u5371\u96be\uff0c\u975e\u4f46\u6ca1\u6709\u51fb\u5012\u4ed6\uff0c\u53cd\u5374\u4f7f\u4ed6\u53d8 \u5f97\u66f4\u4e3a\u575a\u5f3a\u4e86\u3002 \u3000\u3000\u672c\u6765\u4e00\u4e9b\u5e0c\u671b\u9887\u4e3a\u6e3a\u832b\u7684\u4e8b\uff0c\u6b64\u523b\u5374\u4e5f\u5df2\u9732\u51fa\u66d9\u5149\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fbe\u6210\u8fd9\u4e9b\u5e0c\u671b\uff0c\u5df2\u53ea\u662f\u65f6\u95f4\u7684\u95ee\u9898\u4e86\u3002 \u3000\u3000\u8427\u5357\u82f9\u7684\u5f71\u5b50\uff0c\u867d\u7136\u5728\u4ed6\u5fc3\u91cc\u7559\u4e0b\u51e0\u8bb8\u51c4\u60cb\u7684\u6e29\u99a8\uff0c\u4f46\u4ed6\u5374\u5c06\u8fd9\u4e9b\u6df1\u6df1\u5730\u57cb\u85cf\u5728\u5fc3\u5e95\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff1a\u82e5\u662f\u4e00\u4e2a\u7537\u4eba\uff0c\u5f53\u4ed6\u6709\u8bb8\u591a\u4e8b\u60c5\u8981\u505a\u7684\u65f6\u5019\uff0c\u5374\u5c06\u81ea\u5df1\u7684\u5927\u534a\u7cbe\u795e\uff0c\u60c5\u611f\uff0c\u5316 \u5728\u5973\u4eba\u8eab\u4e0a\uff0c\u90a3\u5c31\u662f\u4e00\u79cd\u611a\u8822\u7684\u9519\u8bef\uff0c\u7eb5\u7136\u8fd9\u79cd\u9519\u8bef\uff0c\u4e5f\u662f\u751c\u871c\u800c\u6e29\u99a8\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u627e\u7740\u4e86\u98de\u8679\u5251\u5ba2\u4eec\uff0c\u544a\u8bc9\u4e86\u4ed6\u4eec\u81ea\u5df1\u6b64\u884c\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u8def\u4e0a\uff0c\u201c\u98de\u8679\u5251\u5ba2\u201d\u4eec\u5df2\u4e86\u89e3\u5230\u201c\u5929\u4e89\u6559\u201d\u5728\u6b66\u6797\u4e2d\u6240\u5360\u7684\u5730\u4f4d\u3002 \u3000\u3000\u5f53\u534e\u54c1\u5947\u77e5\u9053\u90a3\u88ab\u81ea\u5df1\u4ece\u5c0f\u5e26\u5927\u7684\u201c\u4e09\u5f1f\u201d\uff0c\u6b64\u523b\u7adf\u4e3b\u5bb0\u7740\u6b66\u6797\u4e2d\u5982\u6b64\u5e9e\u5927\u7684\u4e00\u4e2a\u52bf\u529b \u65f6\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u89c9\u4e5f\u6709\u4e9b\u96be\u8a00\u7684\u6ecb\u5473\u3002 \u3000\u3000\u6709\u4e9b\u5351\u5fae\u7684\u611f\u89c9\u4e59\u758b\u65e0\u8bba\u82f1\u96c4\u8c6a\u6770\uff0c\u6291\u6216\u662f\u5351\u5fae\u5c0f\u4eba\uff0c\u90fd\u80fd\u5171\u540c\u611f\u89c9\u5230\u7684\uff1a\u53ea\u662f\u82f1\u96c4\u8c6a \u6770\u4eec\uff0c\u5374\u80fd\u5c06\u8fd9\u4e9b\u611f\u89c9\u538b\u5236\uff0c\u662f\u4ee5\u4ed6\u4eec\u4fbf\u80fd\u80dc\u8fc7\u522b\u4eba\u3002 \u3000\u3000\u4f0a\u98ce\u548c\u957f\u767d\u5251\u5ba2\u4eec\u7684\u5c45\u6240\uff0c\u662f\u5728\u5f00\u5c01\u57ce\u4e1c\uff0c\u7409\u7483\u5854\u4e0b\u7684\u4e00\u5bb6\u5ba2\u6808\u91cc\uff0c\u800c\u90a3\u4e8c\u5341\u91cc\u94fa\uff0c\u5374 \u662f\u5f00\u5c01\u57ce\u897f\u7684\u4e00\u4e2a\u5c0f\u9547\u3002 \u3000\u3000\u5305\u6c0f\u5bb6\u7960\uff0c\u662f\u4e8c\u5341\u91cc\u94fa\u7684\u4e00\u4e2a\u6700\u597d\u53bb\u5904\uff0c\u7960\u5802\u5916\u53e4\u6728\u53c2\u5929\uff0c\u82cd\u90c1\u6ef4\u7fe0\uff0c\u7960\u5802\u91cc\u4e5f\u6253\u626b\u5f97 \u6781\u4e3a\u6e05\u6d01\u51c0\u723d\u3002\u6625\u79cb\u4f73\u53e3\uff0c\u4e5f\u6709\u4e0d\u5c11\u4eba\u5230\u8fd9\u91cc\u6765\u8e0f\u9752\u7684\u3002\u7960\u5802\u7684\u51f9\u5904\uff0c\u81ea\u4e5f\u7559\u4e0b\u4e0d\u5c11\u9a9a\u4eba\u58a8 \u5ba2\u7684\u9898\u6cf3\u3002 \u3000\u3000\u4f46\u8fd9\u5929\u665a\u4e0a\uff0c\u5929\u4e00\u5165\u9ed1\uff0c\u5305\u6c0f\u7960\u5802\u7684\u56db\u5468\uff0c\u7a81\u7136\u51fa\u73b0\u4e86\u4e09\u4e94\u6210\u7fa4\u7684\u9ed1\u8863\u58ee\u6c49\uff0c\u963b\u6b62\u7740\u4efb \u4f55\u4eba\u518d\u5f80\u524d\u884c\u4e00\u6b65\u3002 \u3000\u3000\u5305\u6c0f\u7960\u5802\u91cc\u7684\u4e00\u4e9b\u9999\u706b\u9053\u4eba\uff0c\u4e5f\u90fd\u83ab\u540d\u5176\u5999\u7684\uff0c\u88ab\u8d76\u5230\u53e6\u5916\u4e00\u95f4\u7834\u571f\u5730\u5e99\u53bb\u3002 \u3000\u3000\u4e8c\u5341\u91cc\u94fa\u7684\u4eba\uff0c\u53ea\u89c1\u8fd9\u95f4\u7960\u5802\u91cc\u706f\u706b\u7a81\u5730\u5927\u76db\uff0c\u91cc\u9762\u4eba\u5f71\u5e62\u5e62\uff0c\u800c\u4e14\u5929\u8d8a\u665a\uff0c\u5230\u7684\u4eba\u4e5f \u5c31\u8d8a\u591a\uff0c\u8fd9\u4e48\u591a\u4eba\u4e3a\u4ec0\u4e48\u7a81\u7136\u90fd\u805a\u5230\u5305\u6c0f\u7960\u5802\u91cc\u6765\uff01\u5df4\u6210\u4e86\u4e8c\u5341\u91cc\u94fa\u4e0a\u7684\u4e00\u4e2a\u8c1c\u3002 \u3000\u3000\u6572\u8fc7\u4e09\u66f4\uff0c\u6709\u4e9b\u4e2a\u5e72\u665a\u6d3b\u7684\u4eba\uff0c\u542c\u5230\u8fd9\u5305\u6c0f\u7960\u5802\u91cc\uff0c\u7a81\u7136\u4f20\u51fa\u4e00\u58f0\u58f0\u51c4\u5389\u7684\u60e8\u53eb\u58f0\uff1a\u4e5f \u6709\u4e0d\u5c11\u6ee1\u8eab\u8840\u8ff9\u7684\u5927\u6c49\uff0c\u4ece\u91cc\u9762\u7a9c\u51fa\u6765\uff0c\u56db\u4e0b\u5954\u9003\u7740\u3002\u8fd9\u5728\u4e00\u5411\u5b81\u9759\u7684\u4e8c\u5341\u91cc\u94fa\uff0c\u7acb\u523b\u9020\u6210 \u4e00\u9635\u9a9a\u52a8\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u5b89\u4efd\u957f\u6c11\u4eec\uff0c\u90fd\u4e5f\u6ca1\u6709\u63a2\u7a76\u6b64\u4e8b\u771f\u76f8\u7684\u52c7\u6c14\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u6709\u4eba\u58ee\u7740\u80c6\u5b50\u524d\u53bb\u4e00\u770b\uff0c\u8fd9\u95f4\u539f\u672c\u5e72\u51c0\u6e05\u723d\u7684\u5305\u6c0f\u5bb6\u7960\uff0c\u7adf\u7136\u6ee1\u5730\u90fd\u662f\u8840\u8ff9\u3002 \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u4e5f\u731c\u5f97\u5230\u8fd9\u4e00\u5b9a\u662f\u8349\u83bd\u4eba\u7269\u7684\u51f6\u6740\uff0c\u53ea\u662f\u6740\u4eba\u7684\u662f\u8c01\uff01\u88ab\u6740\u7684\u662f\u8c01\uff01\u5df4\u4e0d\u662f\u8fd9 \u91cc\u6b66\u6797\u4ee5\u5916\u7684\u826f\u6c11\uff0c\u6240\u80fd\u63e3\u6d4b\u7684\u4e86\u3002 \u3000\u3000\u539f\u6765\u5f00\u5c01\u8235\u4e0b\u7684\u6570\u5341\u4e2a\u5929\u4e89\u5f92\u4f17\uff0c\u6b63\u5728\u8fd9\u5305\u6c0f\u7960\u5802\u91cc\u7b49\u5019\u6559\u4e3b\u5927\u9a7e\u7684\u65f6\u5019 \u3000\u3000\u5305\u6c0f\u7960\u5802\u91cc\uff0c\u91cc\u91cc\u5916\u5916\u4e00\u7247\u9759\u5bc2\uff0c\u5927\u58f0\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4e00\u53e5\u4e5f\u542c\u4e0d\u89c1\u3002\u76d8\u9f99\u94f6\u68cd\u848b\u4f2f\u9633\uff0c \u4e00\u88ad\u91d1\u8272\u957f\u886b\uff0c\u8d1f\u624b\u7acb\u5728\u7960\u5802\u7684\u5927\u5385\u524d\uff1a\u5c0f\u4e27\u95e8\u9648\u656c\u4ec1\uff0c\u7d27\u7d27\u7ad9\u5728\u65c1\u8fb9\uff0c\u5fc3\u91cc\u5374\u662f\u5fd0\u5fd1\u6014 \u5fe1\uff0c\u751f\u50cf\u7b49\u4f1a\u513f\u6559\u4e3b\u6765\u4e86\uff0c\u8981\u62ff\u81ea\u5df1\u4e0b\u624b\u5f00\u5200\u3002 \u3000\u3000\u8fdc\u8fdc\u4f20\u6765\u201c\u7b03\uff0c\u7b03\uff0c\u7b03\u201d\u4e09\u58f0\u6572\u6886\u58f0\uff0c\u76d8\u9f99\u94f6\u68cd\u56db\u987e\u4e00\u773c\uff0c\u671b\u56db\u4e0b\u7ad9\u7740\u7684\u5929\u4e89\u5f92\u4f17\u559d \u9053\uff1a \u3000\u3000\u201c\u5f1f\u5144\u4eec\uff01\u90fd\u4f9d\u987a\u5e8f\u7ad9\u597d\uff0c\u6559\u4e3b\u8fd9\u5c31\u5feb\u6765\u4e86\u3002\u4eca\u5929\u665a\u4e0a\uff0c\u4f60\u4eec\u80fd\u89c1\u5f97\u6559\u4e3b\u7684\u771f\u9762\u76ee\uff0c\u8fd9 \u4e5f\u7b97\u662f\u4f60\u4eec\u7684\u9020\u5316\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u7136\u56db\u65b9\u516b\u9762\u90fd\u4f20\u6765\u4e00\u9635\u523a\u8033\u7684\u7b11\u58f0 \u3000\u3000\u4e94\u6761\u9ed1\u8863\u8499\u9762\u7684\u4eba\u5f71\uff0c\u4ece\u5927\u5385\u7684\u56db\u9762\u98ce\u4e00\u6837\u5730\u63a0\u4e86\u8fdb\u6765\u3002\u8fd9\u5305\u6c0f\u7960\u5802\u7684\u56db\u5468\uff0c\u90fd\u4f0f\u7740\u5929 \u4e89\u6559\u7684\u6697\u5361\uff0c\u53ef\u662f\u8fd9\u4e94\u4e2a\u9ed1\u8863\u4eba\uff0c\u7adf\u4e0d\u77e5\u662f\u600e\u4e48\u6765\u7684\u3002 \u3000\u3000\u76d8\u9f99\u94f6\u68cd\u9762\u8272\u5927\u5b6a\uff0c\u6012\u53f1\u4e00\u58f0\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u662f\u90a3\u513f\u6765\u7684\uff1f\u201d \u3000\u3000\u53f1\u58f0\u529b\u4f4f\uff0c\u4e00\u6761\u9ed1\u8863\u4eba\u5f71\uff0c\u5df2\u6765\u5230\u4ed6\u9762\u524d\uff0c\u4ed6\u4f46\u89c9\u773c\u524d\u5bd2\u5149\u66b4\u957f\uff0c\u4e00\u6e9c\u9752\u84dd\u8272\u7684\u5149\u534e\uff0c \u5df2\u5e26\u80a9\u5e26\u81c2\u5730\u671d\u4ed6\u524a\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u848b\u4f2f\u9633\u827a\u51fa\u5d69\u5c71\uff0c\u6b66\u529f\u4ea6\u975e\u7b49\u95f2\uff0c\u6012\u53f1\u4e00\u58f0\uff0c\u5927\u62e7\u8eab\uff0c\u5f80\u65c1\u4e00\u95ea\uff1a\u4f46\u8fd9\u9ed1\u8863\u4eba\u8eab\u6cd5\u5feb \u8fc5\uff0c\u5251\u5149\u5982\u6d9b\uff0c\u5237\uff0c\u5237\uff0e\u5237\uff0c\u53c8\u662f\u4e09\u5251\u3002\u848b\u4f2f\u9633\u4f46\u89c9\u6ee1\u773c\u5bd2\u5149\uff0c\u8fd9\u4e00\u5251\u4e09\u62db\uff0c\u7adf\u62db\u62db\u4e0d\u79bb\u4ed6 \u7684\u8981\u5bb3\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6781\u529b\u62db\u67b6\uff0c\u4f46\u638c\u4e2d\u6ca1\u6709\u5e26\u7740\u5175\u5203\uff0c\u624b\u5e95\u4e0b\u5c31\u81ea\u7136\u6253\u4e86\u6298\u6263\u3002\u4ed6\u867d\u7136\u5927\u58f0\u53f1\u95ee\uff0c\u4f46 \u8fd9\u9ed1\u8863\u4eba\u7adf\u95f7\u58f0\u4e0d\u54cd\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u8033\u7554\u4e00\u58f0\u60e8\u53eb\uff0c\u4ed6\u542c\u51fa\u90a3\u662f\u5c5e\u4e8e\u5c0f\u4e27\u95e8\u9648\u656c\u4ec1\u7684\uff0c\u76ee\u5149\u4e00\u779f\uff0c\u90a3\u5c0f\u4e27\u95e8\u53cc\u624b\u63a9\u7740\u80f8\uff0c\u9c9c \u8840\u6c68\u7136\u5916\u5192\uff0c\u8eab\u5f62\u6643\u4e86\u4e24\u6643\uff0c\u5c31\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5927\u5385\u4e2d\u60e8\u53eb\u4e4b\u58f0\u56db\u8d77\uff0c\u5939\u6742\u7740\u8fd9\u4e9b\u9ed1\u8863\u4eba\u7684\u51b7\u7b11\u53f1\u58f0\u3002 \u3000\u3000\u76d8\u9f99\u94f6\u68cd\u848b\u4f2f\u9633\u5fc3\u91cc\u8d8a\u6765\u8d8a\u4e71\uff0c\u5bf9\u65b9\u7684\u5251\u62db\u5374\u8d8a\u6765\u8d8a\u5389\uff0c\u5251\u8def\u4e4b\u72e0\u8fa3\u8be1\u5f02\uff0c\u7adf\u662f\u4f1a\u904d\u5929 \u4e0b\u5404\u6d3e\u540d\u5bb6\u7684\u848b\u4f2f\u9633\u524d\u6240\u672a\u89c1\u7684\uff01 \u3000\u3000\u4ed6\u60c5\u6025\u5fc3\u4e71\u4e4b\u4e0b\uff0c\u53cc\u638c\u5fae\u4e00\u758f\u795e\uff0c\u53ea\u89c1\u9752\u5149\u4e00\u7f15\uff0c\u4ece\u81ea\u5df1\u7684\u638c\u5f71\u4e2d\u76f4\u5241\u4e86\u8fdb\u6765\uff0c\u63a5\u7740\u81ea \u5df1\u5de6\u81c2\u4e00\u51c9\uff0c\u7adf\u88ab\u5212\u4e86\u957f\u51e0\u8fbe\u5c3a\u7684\u4e00\u9053\u53e3\u5b50\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u6570\u8f6c\uff0c\u77e5\u9053\u5927\u52bf\u5df2\u53bb\uff0c\u7a81\u7136\u51fa\u62f3\u5982\u98ce\uff0c\u864e\u864e\u4e24\u62f3\uff0c\u5c06\u201c\u5c11\u6797\u4f0f\u864e\u62f3\u201d\u91cc\u6700\u7cbe\u5999\u7684 \u4e24\u7740\uff0c\u65bd\u4e86\u51fa\u6765\uff0c\u8fd9\u79cd\u540d\u5bb6\u7684\u7edd\u6280\uff0c\u679c\u81ea\u4e0d\u540c\u51e1\u54cd\uff0c\u90a3\u9ed1\u8863\u4eba\u8eab\u624b\u867d\u9ad8\uff0c\u5374\u4e5f\u4e0d\u7981\u540e\u9000\u4e00 \u6b65\u3002 \u3000\u3000\u800c\u76d8\u9f99\u94f6\u68cd\u848b\u4f2f\u9633\uff0c\u5c31\u5728\u81ea\u5df1\u7684\u62f3\u5df2\u51fa\uff0c\u5bf9\u65b9\u8eab\u5f62\u5fae\u9000\u7684\u5f53\u513f\uff0c\u731b\u4e00\u957f\u8eab\uff0c\u811a\u8ddf\u7528\u529b\uff0c \u55d6\u5730\u5012\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u91cf\u597d\u5730\u5f62\uff0c\u811a\u5c16\u5728\u8eab\u540e\u7684\u4f9b\u684c\u4e0a\u4e00\u70b9\uff0c\u8eab\u5f62\u5fae\u4e00\u8f6c\u6298\uff0c\u5c31\u50cf\u7bad\u4e5f\u4f3c\u5730\u4ece\u7a97\u4e2d\u63a0\u4e86 \u51fa\u53bb\u3002\u6b64\u523b\u4ed6\u4fdd\u547d\u4e3a\u5148\uff0c\u5927\u5385\u4e2d\u7684\u5929\u4e89\u6559\u5f92\u4eec\u60e8\u547c\u4e4b\u58f0\u518d\u5389\uff0c\u4ed6\u867d\u542c\u5230\u8033\u91cc\uff0c\u5374\u4e5f\u987e\u4e0d\u5f97 \u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u8def\u9000\u51fa\u53bb\uff0c\u624d\u77e5\u9053\u4f0f\u5728\u7960\u5802\u5916\u7684\u6697\u5361\uff0c\u7adf\u90fd\u88ab\u4eba\u5bb6\u5236\u4f4f\u4e86\uff0c\u4e8e\u662f\u8fd9\u4e9b\u9ed1\u8863\u8499\u9762\u4eba\u7684 \u8eab\u624b\u4e4b\u9ad8\uff0c\u5c31\u66f4\u4ee4\u4ed6\u60ca\u5f02\u3002 \u3000\u3000\u4f46\u662f\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u5bf9\u8fd9\u4e9b\u8be1\u5f02\u7684\u9ed1\u8863\u4eba\u7684\u6765\u8def\uff0c\u4ed6\u4ecd\u7136\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0c\u534a\u70b9\u4e5f\u4e0d\u77e5 \u9053\u3002 \u3000\u3000\u4e8e\u662f\u5929\u4e89\u6559\u5c31\u8fd9\u6837\u83ab\u540d\u5176\u5999\u7684\uff0c\u5728\u5f00\u5c01\u57ce\u91cc\u53d7\u4e86\u8fd9\u4e48\u4e00\u4e2a\u4ece\u672a\u53d7\u8fc7\u7684\u91cd\u5927\u632b\u6298\u3002\u800c\u8fd9\u4e9b \u81ea\u7136\u4e5f\u5c31\u662f\u4f0a\u98ce\u7684\u6770\u4f5c\u4e86\u3002\u6263\u201c\u6d35\u57ae\u4e4b\u54ce \u3000\u3000\u4f0a\u98ce\u548c\u201c\u98de\u8679\u5251\u5ba2\u201d\u4eec\uff0c\u91cc\u8863\u8499\u9762\uff0c\u4e58\u591c\u6311\u4e86\u5929\u4e89\u6559\u5f00\u5c01\u57ce\u7684\u5206\u8235\uff0c\u5374\u4e5f\u77e5\u9053\u4e0d\u80fd\u5728\u5f00 \u5c01\u4e45\u7559\uff0c\u4e8e\u662f\u4fbf\u7531\u4e8c\u5341\u91cc\u94fa\u7ed5\u57ce\u800c\u53bb\u3002 \u3000\u3000\u9a6c\u7fa4\u5728\u91cc\u6697\u4e2d\u5954\u9a70\u4e00\u591c\uff0c\u201c\u98de\u8679\u5251\u5ba2\u201d\u4eec\u4e45\u9690\u5173\u5916\uff0c\u76f4\u5230\u4eca\u591c\uff0c\u624d\u7b97\u5927\u5feb\u8eab\u624b\uff0c\u5fc3\u91cc\u90fd \u89c9\u5f97\u6267\u8840\u5954\u817e\uff0c\u4e0d\u80fd\u81ea\u5df2\uff0c \u3000\u3000\u5c31\u8fde\u5e74\u5df2\u77e5\u547d\u7684\u534e\u54c1\u5947\uff0c\u6b64\u523b\u9a91\u5728\u9a6c\u4e0a\uff0c\u4e5f\u662f\u4e0d\u505c\u5730\u9ad8\u8c08\u9614\u8bba\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u5634\u8fb9\uff0c\u5e26\u7740\u4e00\u4e1d\u5fae\u7b11\uff0c\u4ed6\u80fd\u4e86\u89e3\u5230\u8fd9\u4e9b\u6765\u81ea\u5173\u5916\u7684\u5251\u624b\u4eec\u7684\u5fc3\u6e05\uff0c\u4ed6\u4eec\u5404\u5404\u8eab\u5a18\u7edd \u6280\uff0c\u90fd\u59cb\u7ec8\u6ca1\u6709\u5728\u6b66\u6797\u4e2d\u9a70\u9a8b\u8fc7\uff0c\u5c31\u8fde\u201c\u98de\u8679\u4e03\u5251\u201d\u8fd9\u4efd\u4e07\u513f\uff0c\u90fd\u662f\u56e0\u4e3a\u4ed6\u4eec\u7684\u6388\u4e1a\u5e08\u7684\u540d \u5934\u800c\u4f20\u51fa\u7684\u3002 \u3000\u3000\u8fd9\u6b63\u5982\u4e00\u4e2a\u5bb6\u8d22\u949c\u4e07\u7684\u5bcc\u5bb6\u516c\u5b50\uff0c\u867d\u6b8a\u62e5\u8d44\u65e0\u6570\uff0c\u4f46\u5374\u59cb\u7ec8\u95f7\u5728\u5bb6\u91cc\uff0c\u867d\u6b8a\u77e5\u9053\u91d1\u94b1\u4e07 \u80fd\uff0c\u5374\u4e5f\u59cb\u7ec8\u6ca1\u6709\u81ea\u5df1\u4eb2\u8eab\u4f53\u9a8c\u8fc7\u3002\u7b49\u5230\u4ed6\u4e00\u65e6\u4e86\u89e3\u5230\u91d1\u94b1\u7684\u771f\u6b63\u4ef7\u503c\uff0c\u81ea\u5df1\u4eb2\u624b\u82b1\u8fc7\u94b1\u7684 \u65f6\u5019\uff0c\u90a3\u4e48\u4ed6\u5bb6\u91cc\u7684\u949c\u4e07\u5bb6\u8d22\uff0c\u5728\u4ed6\u773c\u4e2d\u4fbf\u7acb\u523b\u6362\u4e86\u53e6\u4e00\u79cd\u610f\u4e49\uff0c\u800c\u4ed6\u5fc3\u60c5\u4e4b\u6b22\u5a31\uff0c\u81ea\u662f\u53ef \u60f3\u800c\u77e5\u3002 \u3000\u3000\u800c\u4f0a\u98ce\u81ea\u5df1\u5462\uff1f\u4ed6\u81ea\u7136\u65e0\u6cd5\u5206\u4eab\u8fd9\u4efd\u6b22\u5a31\u3002\u591c\u8272\u5982\u58a8\uff0c\u4ed6\u7eb5\u9a6c\u72c2\u5954\uff0c\u5fc3\u91cc\u5374\u4e5f\u89c9\u5f97\u5341\u5206 \u75db\u5feb\uff1a\u8fd9\u4e24\u5e74\u6765\u7684\u79ef\u90c1\uff0c\u4eca\u591c\u4e5f\u7b97\u6d88\u53bb\u4e0d\u5c11\u3002 \u3000\u3000\u5929\u8272\u5fae\u660e\uff0c\u6b8b\u51ac\u7684\u6e05\u6668\uff0c\u5bd2\u610f\u957f\u4eba\u523a\u9aa8\uff1a\u4f46\u4ed6\u4eec\u7684\u4eba\u548c\u9a6c\uff0c\u5374\u90fd\u662f\u6ee1\u5934\u5927\u6c57\uff0c\u4e00\u70b9\u4e5f\u6ca1 \u6709\u5bd2\u610f\u3002 \u3000\u3000\u4e1c\u65b9\u5c04\u51fa\u7b2c\u4e00\u7ebf\u5149\u8292\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5230\u4e86\u6d35\u9633\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u9a6c\u5f53\u5148\uff0c\u51b2\u5230\u57ce\u811a\uff0c\u4f46\u8fd9\u65f6\u65f6\u5149\u592a\u65e9\uff0c\u57ce\u95e8\u5c1a\u4e14\u672a\u5f00\uff0c\u4f0a\u98ce\u56de\u8fc7\u5934\u53bb\uff0c\u4f4e\u9053\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u57ce\u95e8\u867d\u7136\u672a\u5f00\uff0c\u4f46\u8fc7\u4e86\u6d35\u9633\uff0c\u524d\u9762\u5c31\u518d\u4e5f\u6ca1\u6709\u5927\u9547\uff0c\u6211\u4eec\u4e0d\u5982\u7b49\u8fd9\u91cc\u57ce\u95e8\u5f00\u4e86\uff0c \u5148\u5728\u8fd9\u91cc\u6253\u4e2a\u5c16\uff0c\u518d\u5f80\u524d\u8d76\u8def\u5427\uff01\u201d \u3000\u3000\u4ed6\u4e45\u5386\u6c5f\u6e56\uff0c\u201c\u98de\u8679\u4e03\u5251\u201d\u5374\u662f\u521d\u5165\u4e2d\u539f\uff0c\u81ea\u7136\u4e00\u5207\u4e8b\u90fd\u552f\u4ed6\u9a6c\u9996\u662f\u77bb\u3002\u4e8e\u662f\u8fd9\u4e00\u884c\u4eba \u9a6c\uff0c\u5c31\u5728\u57ce\u95e8\u5916\u9a7b\u4e86\u8db3\uff0c\u638f\u51fa\u5e03\u5dfe\u6765\u64e6\u6c57\u3002 \u3000\u3000\u4e16\u95f4\u5e38\u6709\u8bb8\u591a\u5de7\u5408\uff0c\u4f7f\u5f97\u4e00\u5207\u4e8b\u90fd\u4e3a\u4e4b\u6539\u89c2\u3002\u4ed6\u4eec\u82e5\u662f\u7ed5\u57ce\u800c\u53bb\uff0c\u4e8b\u60c5\u7684\u53d8\u5316\uff0c\u4e5f\u8bb8\u5c31 \u4e0d\u4f1a\u6709\u5982\u4ee5\u540e\u7684\u90a3\u4e48\u590d\u6742\uff1b\u4f46\u4ed6\u4eec\u5374\u504f\u504f\u7b49\u5230\u57ce\u95e8\u5916\u9762\uff0c\u751f\u50cf\u662f\u8fd9\u4e00\u5207\u4e8b\uff0c\u65e9\u5df2\u88ab\u4e0a\u82cd\u5b89\u6392 \u597d\u4e86\u4f3c\u7684\u3002 \u3000\u3000\u5929\u5149\u5927\u4eae\uff0c\u201c\u5440\u201d\u5730\u4e00\u58f0\uff0c\u57ce\u95e8\u5148\u5f00\u4e86\u4e00\u7ebf\uff0c\u4f0a\u98ce\u5708\u8fc7\u9a6c\u5934\uff0c\u90a3\u77e5\u57ce\u95e8\u5f00\u5904\uff0c\u91cc\u9762\u5374\u5148 \u9a70\u51fa\u4e00\u5339\u9a6c\u6765\uff0c\u4ece\u4f0a\u98ce\u8eab\u4fa7\u64e6\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u672c\u672a\u6ce8\u610f\uff0c\u76ee\u5149\u8f6c\u52a8\u5904\uff0c\u53ea\u770b\u5230\u9a70\u51fa\u7684\u90a3\u4eba\uff0c\u4e00\u8eab\u9526\u7ee3\uff0c\u5728\u64e6\u8fc7\u81ea\u5df1\u8eab\u4fa7\u7684\u65f6\u5019\uff0c \u4f3c\u4e4e\u8fd8\u8f7b\u8f7b\u53d1\u51fa\u201c\u54a6\u201d\u5730\u4e00\u58f0\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e5f\u5e76\u672a\u5728\u610f\uff0c\u7a0d\u4e3a\u626d\u5934\u4e00\u671b\uff0c\u534e\u54c1\u5947\u7b49\u4eba\u5df2\u7531\u540e\u8d76\u6765\uff0c\u548c\u4ed6\u5e76\u9a91\u9a70\u5165\u57ce\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u4eec\u65b9\u81ea\u5165\u57ce\uff0c\u80cc\u540e\u7a81\u5730\u4f20\u6765\u4e00\u58f0\u54cd\u4eae\u7684\u558a\u559d\u58f0\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u58f0\u97f3\u4e4b\u6d2a\u4eae\u9ad8\u4ea2\uff0c\u4f7f\u4eba\u542c\u4e86\uff0c\u751f\u50cf\u662f\u6709\u94c1\u69cc\u5728\u8033\u7554\u91cd\u51fb\u4e00\u4e0b\uff0c\u5165\u8033\u9535\u7136\u3002 \u3000\u3000\u4f0a\u98ce\u548c\u534e\u54c1\u5947\u7b49\uff0c\u90fd\u4e0d\u7981\u6115\u7136\u56de\u987e\uff0c\u540e\u9762\u5df2\u6709\u4e00\u9a91\u5954\u9a70\u800c\u6765\uff0c\u4f0a\u98ce\u76ee\u5149\u52a8\u5904\uff0c\u8fd9\u4e00\u9a91\u7adf \u7136\u5c31\u662f\u5148\u524d\u51fa\u57ce\u800c\u53bb\u7684\u90a3\u4e2a\u6ee1\u8eab\u9526\u7ee3\u7684\u9a91\u58eb\u3002 \u3000\u3000\u534e\u54c1\u5947\u9f3b\u4e2d\u4e0d\u60a6\u5730\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u7b49\u5230\u8fd9\u9a91\u5954\u4e86\u4e0a\u6765\uff0c\u4e5f\u4ea6\u51b7\u53f1\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u8fd9\u662f\u671d\u8c01\u5728\u558a\uff1f\u201d \u3000\u3000\u90a3\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u7a7f\u7740\u4e00\u8eab\u6df1\u7d2b\u8272\u7684\u8863\u886b\uff0c\u4e0a\u9762\u8fd8\u6ee1\u5e03\u91d1\u82b1\uff0c\u8de8\u5728\u9a6c\u8e6c\u4e0a\u7684\u4e24\u53ea\u9774\u5b50\uff0c\u5149 \u534e\u95ea\u707f\uff0c\u539f\u6765\u4e0a\u9762\u7adf\u90fd\u9576\u7740\u660e\u73e0\u3002 \u3000\u3000\u4ed6\u4e00\u9a6c\u9a70\u6765\uff0c\u773c\u89d2\u779f\u4e5f\u672a\u779f\u534e\u54c1\u5947\u4e00\u773c\uff0c\u5374\u77aa\u5728\u4f0a\u98ce\u8eab\u4e0a\uff0c\u6c88\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u600e\u4e48\u8dd1\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u8fd9\u65f6\u4e5f\u5df2\u770b\u6e05\u4ed6\u7684\u8138\uff0c\u4f53\u5185\u7684\u8840\u6db2\uff0c\u51e0\u4e4e\u53c8\u4e3a\u4e4b\u51dd\u56fa\u8d77\u6765\uff01\u8fd9\u4eba\u867d\u7136\u6ee1\u8eab\u9526\u8863\uff0c\u4f46 \u5374\u67af\u7626\u5982\u67f4\uff0c\u4e24\u816e\u5185\u9677\uff0c\u89c2\u9aa8\u9ad8\u8038\uff0c\u9894\u4e0b\u7559\u5f97\u7a00\u7a00\u7684\u51e0\u7f15\u5c71\u7f8a\u80e1\u5b50\uff0c\u76ee\u4e2d\u795e\u5149\u5982\u526a\uff0c\u4e0d\u662f\u90a3 \u4e2a\u5df2\u88ab\u81ea\u5df1\u7528\u667a\u8ba1\u5173\u5728\u65e0\u91cf\u5c71\u5dc5\u7684\u79d8\u7a9f\u91cc\u7684\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u662f\u8c01. \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u4f0a\u98ce\u7acb\u523b\u4e3a\u4e4b\u9762\u8272\u5927\u53d8\uff0c\u4ed6\u8eab\u4fa7\u7684\u534e\u54c1\u5947\u5df2\u6012\u53f1\u53c8\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u8fd9\u662f\u51b2\u7740\u8c01\u8bf4\u8bdd\uff1f\u4f60\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u94c1\u9762\u5b64\u884c\u5ba2\u4e5f\u6a2a\u76ee\u6012\u626b\u4ed6\u4e00\u773c\uff0c\u67af\u7626\u7684\u8138\u4e0a\uff0c\u8868\u60c5\u66f4\u52a0\u4e25\u5cfb\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u5728\u534e\u54c1\u5947\u9762\u4e0a\u51db\u7136\u4e00\u626b\uff0c\u51b7\u51b7\u5730\u622a\u4f4f\u4ed6\u7684\u8bdd\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u662f\u5728\u51b2\u7740\u8c01\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u4ed6\u76ee\u5149\u8f6c\u5411\u4f0a\u98ce\uff1a \u3000\u3000\u201c\u5582\uff0c\u8fd9\u8001\u5934\u5b50\u662f\u8c01\uff1f\u82e5\u662f\u4f60\u7684\u670b\u53cb\uff0c\u8001\u592b\u8fd8\u53ef\u9976\u4ed6\u4e00\u547d\uff0c\u5426\u5219\u7684\u8bdd\u2026\u2026\u54fc\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5927\u9a87\u4e4b\u4e0b\uff0c\u95fb\u8a00\u5374\u4e0d\u7981\u53c8\u8be7\u5f02\u8d77\u6765\uff0c\u5728\u5fc3\u91cc\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u600e\u5730\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u7a81\u7136\u5bf9\u6211\u8fd9\u4e48\u5ba2\u6c14\uff1f\u5728\u65e0\u91cf\u5c71\u5dc5\u4e0a\u4ed6\u4e0d\u662f\u8981\u7f6e\u6211\u4e8e\u6b7b\u5730\u5417\uff1f\u4f55\u51b5\u6211 \u53c8\u5c06\u4ed6\u5173\u5728\u90a3\u77f3\u7a9f\u91cc\uff0c\u4ed6\u53c8\u662f\u600e\u4e48\u51fa\u6765\u7684\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7a81\u5730\u53c8\u60f3\u8d77\u4e00\u4ef6\u4e8b\u6765\uff1a \u3000\u3000\u201c\u4f46\u662f\u6211\u6b64\u523b\u5df2\u7ecf\u4e0d\u662f\u539f\u6765\u7684\u9762\u76ee\u4e86\u5440\uff01\u96be\u9053\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u4e5f\u548c\u6211\u6b64\u523b\u8fd9\u5fd6\u9762\u76ee\u2014\u2014 \u8427\u65e0\uff0c\u6709\u7740\u4ec0\u4e48\u5173\u7cfb\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u6781\u5feb\u7684\u95ea\u52a8\u51e0\u4e0b\uff0c\u90a3\u534e\u54c1\u5947\u5374\u5df2\u51b7\u51b7\u53f1\u9053\uff1a \u3000\u3000\u201c\u5582\uff0c\u8fd9\u8001\u5934\u5b50\u53ef\u662f\u8001\u5f1f\u7684\u670b\u53cb\uff0c\u82e5\u662f\u7684\u8bdd\u8001\u592b\u4e5f\u53ef\u9976\u4ed6\u4e00\u547d\uff0c\u5426\u5219\u2026\u2026\u54fc\uff01\u201d \u3000\u3000\u4ed6\u7167\u65b9\u6293\u836f\uff0c\u628a\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u65b9\u624d\u8bf4\u7684\u8bdd\uff0c\u7acb\u523b\u53c8\u56de\u656c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e07\u5929\u840d\u67af\u7626\u7684\u8138\u4e0a\uff0c\u4ecd\u7136\u50cf\u7384\u51b0\u4f3c\u7684\u6beb\u65e0\u53d8\u5316\uff0c\u786e\u5b9e\u4e0d\u6127\u201c\u94c1\u9762\u201d\u4e24\u5b57\u3002\u4f46\u4f0a\u98ce\u5374\u5df2\u4ece \u4ed6\u90a3\u8d8a\u6765\u8d8a\u51db\u51bd\u7684\u76ee\u5149\u4e2d\uff0c\u770b\u51fa\u6740\u673a\u3002 \u3000\u3000\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u5c06\u7f30\u7ef3\u5fae\u5fae\u4e00\u5e26\uff0c\u8f6c\u5411\u534e\u54c1\u5947\uff0c\u7a81\u5730\u51fa\u638c\u5982\u98ce\uff0c\u201c\u5427\u201d\u5730\uff0c\u5728\u534e\u54c1\u5947\u7684\u5750 \u9a91\u5934\u4e0a\u62cd\u4e86\u4e00\u4e0b\uff0c\u90a3\u5339\u9a6c\u7acb\u523b\u4e00\u58f0\u60e8\u5636\uff0c\u8fde\u6323\u624e\u90fd\u6ca1\u6709\u6323\u624e\uff0c\u5c31\u762b\u8f6f\u5730\u5012\u5728\u5730\u4e0a\uff0c\u7adf\u5df2\u6c14\u7edd \u4e86\u3002 \u3000\u3000\u534e\u54c1\u5947\u81ea\u5df1\u65e9\u5c31\u4ece\u9a6c\u4e0a\u63a0\u4e86\u4e0b\u6765\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u770b\u5230\u8fd9\u5339\u9a6c\u7684\u9a6c\u9996\uff0c\u7adf\u88ab\u8fd9\u5176\u8c8c\u4e0d\u626c\u7684\u67af \u7626\u8001\u8005\uff0c\u4e00\u638c\u51fb\u5f97\u7a00\u70c2\uff01 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u4e5f\u81ea\u5927\u9a87\uff0c\u8fd9\u79cd\u638c\u4e0a\u7684\u529b\u9053\uff0c\u4e0d\u4f46\u60ca\u4e16\u9a87\u4fd7\uff0c\u7b80\u76f4\u532a\u5937\u6240\u601d\u4e86\uff01 \u3000\u3000\u800c\u8fd9\u65f6\u53e6\u4e09\u5339\u9a6c\u4e0a\u5389\u53f1\u8fde\u58f0\uff0c\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u5251\u5149\u66b4\u957f\uff0c\u6bdb\u6587\u5947\u548c\u4ed6\u90a3\u4e24\u4e2a\u5e08\u5f1f\uff0c \u5df2\u8dc4\u7405\u62d4\u51fa\u5251\u6765\u3002 \u3000\u3000\u4e07\u5929\u840d\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u500f\u7136\u4ece\u9a6c\u978d\u4e0a\u63a0\u4e86\u8d77\u6765\uff0c\u7b14\u76f4\u5730\u5411\u6bdb\u6587\u5947\u63a0\u53bb\uff0c\u53cc\u638c\u4f38\u51fa\uff0c \u5341\u6307\u5982\u949d\uff0c\u8fd9\u4ee5\u91d1\u521a\u638c\u529b\u548c\u5927\u9e70\u722a\u624b\u540d\u9707\u6b66\u6797\u7684\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u50cf\u662f\u5df2\u7ecf\u52a8\u4e86\u771f\u6012\uff0c\u7adf\u65bd\u51fa\u715e \u624b\u6765\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u5c06\u8fd9\u4e8b\u6781\u8be6\u7ec6\uff0c\u8c28\u614e\u5730\u601d\u7d22\u4e86\u4e00\u904d\uff0c\u7136\u540e\u817f\u5f2f\u4e00\u76f4\uff0c\u5728\u9a6c\u8e6c\u4e0a\u7ad9 \u4e86\u8d77\u6765\uff0c\u6447\u624b\u5927\u559d\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u524d\u8f88\u8bf7\u4f4f\u624b\uff01\u201d \u3000\u3000\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u7adf\u771f\u7684\u88ab\u8fd9\u559d\u58f0\u6240\u963b\uff0c\u67af\u7626\u7684\u8eab\u8eaf\uff0c\u5728\u7a7a\u4e2d\u5fae\u4e00\u8f6c\u6298\uff0c\u7adf\u53c8\u98d8\u7136\u843d\u5230\u9a6c \u978d\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u8eaf\uff0c\u7adf\u50cf\u6e38\u9c7c\u5728\u6c34\u91cc\u4f3c\u7684\uff0c\u5728\u7a7a\u4e2d\u4ea6\u80fd\u6765\u53bb\u81ea\u5982\u3002 \u3000\u3000\u98de\u8679\u5251\u5ba2\u4eec\u4e0d\u7981\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u629b\u4eec\u8c01\u4e5f\u6ca1\u6709\u770b\u51fa\u8fd9\u4e00\u70b9\u4e5f\u4e0d\u8d77\u773c\uff0c\u50cf\u4e2a\u4e61\u4e0b\u571f\u8d22\u4e3b\u4f3c \u7684\u8001\u5934\uff0c\u7adf\u6709\u8fd9\u79cd\u8d85\u51e1\u5165\u5723\u7684\u6b66\u529f\u3002 \u3000\u3000\u50cf\u662f\u4efb\u4f55\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u4f3c\u7684\uff0c\u94c1\u9762\u5b64\u884c\u5ba2\u53c8\u5bd2\u7740\u8138\uff0c\u5750\u5728\u9a6c\u978d\u4e0a\uff0c\u9762\u5411\u4f0a\u98ce\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u4f60\u53eb\u8fd9\u6279\u5bb6\u4f19\u8d76\u5feb\u5148\u6eda\uff0c\u8001\u592b\u8fd8\u6709\u8bdd\u8981\u95ee\u4f60\u3002\u201d \u3000\u3000\u4f0a\u98ce\u8bfa\u8bfa\u8fde\u58f0\uff0c\u4e00\u9762\u53c8\u671d\u534e\u54c1\u5947\u7b49\u4eba\u505a\u7740\u773c\u8272\u3002 \u3000\u3000\u201c\u98de\u8679\u5251\u5ba2\u201d\u4eec\uff0c\u6b64\u523b\u662f\u65e2\u60ca\u4e14\u6012\uff0c\u4f46\u4eba\u5bb6\u6b66\u529f\u65e2\u9ad8\uff0c\u518d\u52a0\u4e0a\u4f0a\u98ce\u90a3\u79cd\u4f3c\u6709\u6df1\u610f\u7684\u6697 \u793a\uff0c\u4ed6\u4eec\u53c8\u4e0d\u5f97\u4e0d\u6682\u5fcd\u7740\u6c14\u3002 \u3000\u3000\u6bdb\u6587\u5947\u624b\u8155\u4e00\u7ffb\uff0c\u957f\u5251\u91cd\u53c8\u5165\u9798\u3002\u534e\u54c1\u5947\u7ad9\u5728\u5730\u4e0a\uff0c\u9762\u8272\u6570\u53d8\uff0c\u7ec8\u4e8e\u4e00\u8dc3\u5230\u6bdb\u6587\u5947\u7684\u9a6c \u4e0a\uff0c\u4e00\u9762\u5411\u90a3\u4e07\u5929\u840d\u53f1\u9053\uff1a \u3000\u3000\u201c\u4eca\u65e5\u6211\u662f\u770b\u5728\u6211\u8fd9\u8001\u5f1f\u7684\u4efd\u4e0a\uff0c\u6682\u4e14\u4e0d\u4e0e\u4f60\u8ba1\u8f83\uff0c\u5341\u65e5\u4e4b\u5185\uff0c\u6211\u4eec\u90fd\u5728\u8944\u9633\u57ce\u91cc\uff0c\u606d \u5019\u5927\u9a7e\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u4e00\u534a\u81ea\u662f\u573a\u9762\u8bdd\uff0c\u8bf4\u7ed9\u8fd9\u4e07\u5929\u840d\u542c\u7684\uff1b\u53e6\u4e00\u534a\u5374\u662f\u544a\u8bc9\u4f0a\u98ce\uff0c\u81ea\u5df1\u5148\u53bb\u8944\u9633\uff0c\u4f60 \u8981\u9a6c\u4e0a\u5c31\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u4f1a\u610f\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u91cc\u601d\u7d22\u7684\u5374\u662f\uff1b\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u548c\u90a3\u8427\u65e0\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u79cd\u5173 \u7cfb\uff1f\u514d\u5f97\u7b49\u4f1a\u4e00\u8bf4\u8bdd\uff0c\u4fbf\u5f97\u9732\u51fa\u9a6c\u811a\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5bb9\u52a8\u4e5f\u4e0d\u52a8\u5730\u5750\u5728\u9a6c\u4e0a\uff0c\u5bf9\u8fd9\u534e\u54c1\u5947\u7684\u573a\u9762\u8bdd\uff0c\u4e1d\u6beb\u90fd\u4e0d\u7b54\u7406\uff0c\u50cf\u662f\u8fd9\u79cd\u8bdd\u4ed6 \u6b63\u542c\u5f97\u591a\u4e86\uff0c\u6839\u672c\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u7b49\u534e\u54c1\u5947\u7b49\u56db\u4eba\u4e09\u9a91\uff0c\u626c\u97ad\u800c\u53bb\uff0c\u4ed6\u624d\u5728\u9f3b\u5b54\u91cc\u51b7\u54fc\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u770b\u5728\u4f60\u7684\u9762\u4e0a\uff0c\u6682\u4e14\u653e\u8fc7\u4ed6\uff0c\u5341\u5929\u4e4b\u540e\u2026\u2026\u54fc\uff01\u201d \u3000\u3000\u8fd9\u5728\u6c5f\u6e56\u4e0a\u7d20\u4ee5\u5fc3\u72e0\u624b\u8fa3\u95fb\u540d\u7684\u4eba\u7269\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u4e5f\u662f\u51b7\u68ee\u68ee\u7684\uff01 \u3000\u3000\u800c\u4e14\u6700\u5947\u5999\u7684\u662f\uff1a\u4ed6\u8bf4\u7684\u8bdd\u90fd\u50cf\u672a\u66fe\u8bf4\u5b8c\uff0c\u800c\u53ea\u7528\u4e00\u4e2a\u201c\u54fc\u201d\u5b57\uff0c\u4ee3\u8868\u5176\u4ed6\u7684\u610f\u6076\u3002 \u3000\u3000\u4ed6\u5c06\u624b\u4e2d\u7684\u9a6c\u97ad\u671d\u57ce\u5916\u4e00\u6307\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u8ddf\u6211\u51fa\u57ce\u53bb\uff0c\u5148\u5e2e\u6211\u529e\u4ef6\u4e8b\uff0c\u7136\u540e\u518d\u4e00\u9f50\u5230\u897f\u6881\u5c71\u53bb\u2026\u2026\u54fc\uff01\u4f60\u4eec\u5e74\u8f7b\u4eba\u90fd\u662f\u8fd9\u4e48 \u8352\u5510\uff01\u4f60\u4e0d\u662f\u8bf4\u5148\u5230\u8c6b\u6eaa\u53e3\u53bb\u7b49\u6211\u7684\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6839\u672c\u5c31\u4e0d\u660e\u4e86\u4ed6\u8bdd\u4e2d\u7684\u610f\u601d\uff0c\u4f46\u5374\u552f\u552f\u7b54\u5e94\u7740\uff0c\u968f\u7740\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u7684\u9a6c\uff0c\u53c8\u8d70\u51fa\u57ce \u5916\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e94\u5341\u4e94\u7ae0\u3000\u94c1\u9762\u5b64\u884c>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e94\u5341\u4e94\u7ae0\u3000\u94c1\u9762\u5b64\u884c \u3000\u3000\u4e24\u4eba\u5e76\u80a9\u800c\u9a70\uff0c\u4f0a\u98ce\u773c\u89d2\u659c\u779f\uff0c\u53ea\u89c1\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u4e25\u5cfb\u7684\u9762\u5b54\u4e0b\u9762\uff0c\u8116\u5b50\u4e0a\u8d6b\u7136\u6709\u51e0\u4e2a \u7d2b\u9ed1\u8272\u7684\u75a4\u8ff9\uff0c\u4f0a\u98ce\u77e5\u9053\u8fd9\u662f\u5999\u624b\u8bb8\u767d\u7684\u94c1\u6307\u5728\u4ed6\u8eab\u4e0a\u7559\u4e0b\u7684\uff0c\u4ed6\u4e0d\u7981\u6697\u4e2d\u611f\u53f9\uff1a \u3000\u3000\u201c\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u771f\u6b63\u662f\u4e2a\u5947\u4eba\uff0c\u8fde\u7ecf\u8fd9\u4e24\u6b21\u6211\u773c\u770b\u4ed6\u518d\u65e0\u6d3b\u8def\u7684\u5927\u96be\uff0c\u4ed6\u8fd8\u662f\u597d\u751f\u751f\u6d3b \u5728\u8fd9\u91cc\u3002\u5c24\u5176\u5947\u602a\u7684\u662f\uff1a\u4ed6\u600e\u4f1a\u4ece\u90a3\u79d8\u7a9f\u4e2d\u9003\u51fa\u6765\u7684\u5462\uff1f\u5509\uff01\u4ed6\u82e5\u77e5\u9053\u6211\u5e76\u975e\u4ed6\u5fc3\u4e2d\u6240\u5fd6\u4e4b \u4eba\uff0c\u53ea\u6015\u6b64\u523b\u53c8\u5c06\u662f\u4e00\u756a\u5267\u70c8\u7684\u751f\u6b7b\u640f\u6597\u3002\u201d \u3000\u3000\u4e00\u51fa\u4e86\u57ce\uff0c\u4e07\u5929\u840d\u5c31\u5c06\u9a6c\u9a70\u5feb\uff0c\u4f0a\u98ce\u7d27\u7d27\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u4e00\u5fc3\u60f3\u8981\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u662f\u600e\u4e48\u9003\u51fa\u79d8\u7a9f\u7684\uff0c\u53c8\u60f3\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u548c \u90a3\u5929\u4e89\u6559\u4e3b\u8427\u65e0\uff0c\u662f\u600e\u6837\u7684\u4e00\u79cd\u5173\u7cfb\u3002 \u3000\u3000\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u4f3c\u4e4e\u5bf9\u8def\u5f84\u751a\u4e3a\u5b70\u6089\uff0c\u4e0d\u7531\u5b98\u9053\uff0c\u6539\u884c\u5c0f\u5cb8\u3002\u8def\u4e0a\u79ef\u96ea\u672a\u6eb6\uff0c\u51b0\u96ea\u6ee1\u9053\uff0c \u50cf\u662f\u5df2\u6709\u8bb8\u4e45\u6ca1\u6709\u4eba\u8d70\u8fc7\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u8d8a\u53d1\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u5728\u5f04\u4ec0\u4e48\u7384\u865a\u3002 \u3000\u3000\u770b\u5230\u4e07\u5929\u840d\u67af\u7626\u7684\u8138\u4e0a\uff0c\u534a\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u7d27\u95ed\u7740\u5634\uff0c\u4e5f\u4e0d\u8bf4\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u867d\u5947\u602a\uff0c\u53ef\u4e5f\u4e0d\u6562\u95ee\u51fa\u6765\u3002 \u3000\u3000\u4e07\u5929\u840d\u4e09\u8f6c\u4e24\u8f6c\uff0c\u8fd9\u6761\u5c0f\u5cb8\u4e5f\u8d8a\u6765\u8d8a\u8352\u50fb\uff0c\u6d35\u9633\u57ce\u5730\u5f53\u6c49\u6c34\u4e4b\u5317\uff0c\u5e72\u4f51\u6cb3\u4e4b\u4e1c\uff0c\u4ed6\u4eec\u51fa \u57ce\u4e4b\u540e\uff0c\u5374\u662f\u5954\u5411\u4e1c\u5317\u65b9\u800c\u53bb\u3002 \u3000\u3000\u662f\u4ee5\u5730\u52bf\u8d8a\u884c\u8d8a\u662f\u9ad8\u5cfb\uff0c\u5e78\u597d\u4f0a\u98ce\u6240\u9a91\u7684\u4e5f\u662f\u4e00\u5339\u957f\u7a0b\u5065\u9a6c\uff0c\u662f\u6545\u8fd8\u80fd\u8ddf\u5f97\u4e0a\u3002\u4f46\u4ed6\u8fd9 \u5339\u9a6c\u5df2\u7ecf\u9a70\u9a8b\u4e86\u5f88\u957f\u4e00\u6bb5\u8def\uff0c\u6b64\u523b\u53e3\u55b7\u7740\u767d\u6cab\uff0c\u56db\u8e44\u7ffb\u52a8\u95f4\uff0c\u5df2\u6e10\u6e10\u900f\u7740\u6709\u4e9b\u4e0d\u652f\u4e86\u3002 \u3000\u3000\u5230\u4e86\u4e00\u5ea7\u67af\u6797\u65c1\u8fb9\uff0c\u4e07\u5929\u840d\u7a81\u5730\u5c06\u9a6c\u52d2\u4f4f\uff0c\u56de\u8eab\u4ece\u9a6c\u540e\u62ff\u4e86\u4e2a\u6781\u5927\u7684\u9769\u56ca\u4e0b\u6765\uff0c\u968f\u624b\u4e00 \u62db\u4f0a\u98ce\uff0c\u4fbf\u81ea\u98d8\u7136\u4e0b\u4e86\u9a6c\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u95ea\u52a8\uff0c\u53ea\u89c1\u8fd9\u7247\u67af\u6797\u6ee1\u88ab\u96ea\u5c01\uff0c\u4f3c\u5df2\u662f\u4e45\u65e0\u4eba\u8ff9\u3002\u4e07\u5929\u840d\u624b\u4e0a\u7684\u8fd9\u4e2a\u9769\u56ca\uff0c\u50cf \u662f\u6781\u4e3a\u6c89\u91cd\uff0c\u4ed6\u66f4\u4e0d\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u6765\u6b64\u505a\u4ec0\u4e48\u3002 \u3000\u3000\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u867d\u4ee5\u786c\u529f\u638c\u529b\u6210\u540d\uff0c\u4f46\u8f7b\u529f\u4ea6\u6781\u9ad8\u7edd\u3002\u624b\u91cc\u62ff\u7740\u90a3\u4e48\u6c89\u91cd\u7684\u4e00\u5305\u4e1c\u897f\uff0c \u8d70\u5728\u8fd9\u79ef\u96ea\u7684\u6ce5\u5730\u4e0a\uff0c\u4ecd\u7136\u662f\u8f7b\u7075\u5de7\u5feb\uff0c\u811a\u4e0b\u672a\u7559\u534a\u70b9\u811a\u5370\uff0c\u8eab\u5f62\u5fae\u4e00\u8d77\u843d\uff0c\u4fbf\u5df2\u7eb5\u5165\u67af \u6797\u3002 \u3000\u3000\u4e00\u8fdb\u4e86\u6797\u5b50\uff0c\u5149\u7ebf\u5c31\u500f\u7136\u9eef\u4e86\u4e0b\u6765\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u5fd0\u5fd1\u6697\u5fd6\uff1a \u3000\u3000\u201c\u83ab\u975e\u4ed6\u65e9\u5df2\u770b\u51fa\u6211\u7684\u672c\u6765\u9762\u76ee\uff0c\u662f\u4ee5\u628a\u6211\u8bf1\u5230\u8fd9\u91cc\u6765\u6536\u62fe\u6211\u2026\u2026\u201d \u3000\u3000\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u6709\u8fdb\u65e0\u9000\uff0c\u4f0a\u98ce\u4e5f\u53ea\u5f97\u968f\u4ed6\u524d\u884c\u3002 \u3000\u3000\u5165\u6797\u5df2\u6df1\uff0c\u4e07\u5929\u840d\u7a81\u5730\u56de\u8fc7\u5934\u6765\uff0c\u5c06\u624b\u4e2d\u7684\u9769\u56ca\u4ea4\u7ed9\u4f0a\u98ce\uff0c\u4ecd\u7136\u662f\u4e00\u8a00\u4e0d\u53d1\u3002\u4f0a\u98ce\u5c06\u8fd9 \u9769\u56ca\u653e\u5728\u624b\u91cc\u5fae\u5fae\u4e00\u6382\uff0c\u8fd9\u9769\u56ca\u4e0d\u4f46\u6c89\u91cd\uff0c\u800c\u4e14\u968f\u7740\u4f0a\u98ce\u7684\u624b\u52bf\u5fae\u52a8\uff0c\u91cc\u9762\u5c31\u53d1\u51fa\u4e00\u9635\u91d1\u94c1 \u4ea4\u51fb\u7684\u58f0\u97f3\u6765\uff0c\u8fd9\u9769\u56ca\u91cc\u9762\u88c5\u7684\uff0c\u7adf\u50cf\u662f\u97ad\u88e5\u4e00\u7c7b\u7684\u5175\u5203\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u8f6c\u4e86\u51e0\u8f6c\uff0c\u62ac\u5934\u53bb\u671b\u8fd9\u884c\u8ff9\u8be1\u5f02\u7684\u4e07\u5929\u840d\uff0c\u53ea\u89c1\u4ed6\u4e00\u9762\u524d\u884c\uff0c\u4e00\u9762\u4f38\u624b\u5165\u6000\uff0c \u638f\u51fa\u4e00\u6837\u4e1c\u897f\u6765\uff0c\u800c\u8fd9\u6837\u4e1c\u897f\uff0c\u4e00\u5165\u4f0a\u98ce\u4e4b\u76ee\uff0c\u4f0a\u98ce\u5fc3\u4e0b\u4fbf\u7acb\u65f6\u604d\u7136\u5927\u609f\uff1a \u3000\u3000\u201c\u539f\u6765\u4ed6\u662f\u6765\u6b64\u5bfb\u5b9d\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u4e07\u5929\u840d\u81ea\u6000\u4e2d\u53d6\u51fa\u7684\u4e00\u7269\uff0c\u662f\u4e24\u7247\u4e00\u5c3a\u89c1\u65b9\u7684\u9ed1\u94c1\u5757\uff0c\u4e5f\u6b63\u662f\u5999\u624b\u8bb8\u767d\u5728\u65e0\u91cf\u5c71 \u5dc5\uff0c\u66fe\u7ecf\u62ff\u7ed9\u4f0a\u98ce\u770b\u8fc7\u7684\u201c\u7487\u5149\u5b9d\u4eea\u201d\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u4e00\u6b7b\uff0c\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u5c31\u5c06\u8fd9\u7487\u5149\u4eea\u7684\u4e00\u534a\uff0c\u51d1\u6210\u4e86\u53cc\u3002 \u3000\u3000\u4f0a\u98ce\u66fe\u7ecf\u542c\u90a3\u5999\u624b\u8bb8\u767d\u8bf4\u8fc7\u8fd9\u4e1c\u897f\u7684\u5999\u5904\uff0c\u6b64\u523b\u4e0d\u7981\u5f20\u5927\u4e86\u773c\u775b\uff0c\u77aa\u5728\u94c1\u9762\u5b64\u884c\u5ba2\u624b\u4e2d \u7684\u8fd9\u5757\u770b\u53bb\u6beb\u4e0d\u8d77\u773c\u7684\u9ed1\u94c1\u5757\u4e0a\u3002 \u3000\u3000\u8fd9\u4e07\u5929\u840d\u811a\u6b65\u5df2\u7f13\uff0c\u5f2f\u7740\u8170\u5c06\u624b\u4e2d\u7684\u8fd9\u201c\u7487\u5149\u5b9d\u4eea\u201d\u8d34\u8fd1\u5730\u9762\uff0c\u4e00\u8def\u63a2\u6d4b\u7740\uff0c\u7a81\u5730\u731b\u4e00 \u957f\u8eab\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u4e25\u5cfb\u7684\u8138\u4e0a\uff0c\u9732\u51fa\u7b11\u5bb9\uff0c\u9053\uff1a \u3000\u3000\u201c\u563f\uff01\u5df4\u5728\u8fd9\u91cc\u3002\u4f60\u628a\u56ca\u4e2d\u7684\u94c1\u9539\u62ff\u51fa\u6765\uff0c\u5e2e\u6211\u671d\u4e0b\u9762\u6398\u3002\u8001\u5b9e\u8bf4\uff1a\u6211\u4e00\u5411\u72ec\u6765\u72ec\u5f80\uff0c \u4eca\u5929\u627e\u4f60\u8fd9\u5e2e\u624b\uff0c\u8fd8\u771f\u662f\u751f\u5e73\u7b2c\u4e00\u6b21\u5462\uff01\u201d \u3000\u3000\u4f0a\u98ce\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u65e2\u7136\u540d\u66f0\u201c\u94c1\u9762\u5b64\u884c\u201d\uff0c\u751f\u5e73\u6ca1\u6709\u627e\u8fc7\u5e2e\u624b\uff0c\u81ea\u662f\u5b9e\u8bdd\u3002\u4f46\u4ed6\u6b64\u523b\u7adf \u627e\u7740\u81ea\u5df1\u6765\u53c2\u4e0e\u8fd9\u79cd\u6781\u4e3a\u79d8\u5bc6\u7684\u884c\u52a8\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u4ed6\u4e0e\u81ea\u5df1\u6b64\u523b\u7684\u8fd9\u4ed8\u9762\u76ee\u2014\u2014\u4e5f\u5c31\u662f\u8427\u65e0 \u7684\u9762\u76ee\u2014\u2014\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5fc5\u4e0d\u5bfb\u5e38\uff0c\u5426\u5219\u4ed6\u7109\u80af\u8ba9\u81ea\u5df1\u4e00\u9f50\u6398\u5b9d\uff01 \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u731c\u6d4b\u4e0d\u5df2\uff0c\u9762\u4e0a\u53ef\u4e00\u4e1d\u4e5f\u4e0d\u9732\u51fa\u6765\uff0c\u5c06\u8fd9\u9769\u56ca\u6253\u5f00\uff0c\u91cc\u9762\u679c\u7136\u662f\u94c1\u9539\uff0c\u94c1\u94f2\u4e00 \u7c7b\u7684\u6398\u571f\u94c1\u5668\uff0c\u4ed6\u4e0d\u7981\u5bf9\u81ea\u5df1\u65b9\u624d\u7684\u731c\u6d4b\uff0c\u6697\u89c9\u597d\u7b11\u3002 \u3000\u3000\u6797\u4e2d\u7684\u6ce5\u5730\u4e0a\u79ef\u96ea\uff0c\u5df2\u51dd\u6210\u575a\u51b0\uff0c\u662f\u4ee5\u6781\u4e3a\u575a\u786c\u3002\u4f46\u5728\u8fd9\u4e24\u4e2a\u6b66\u6797\u9ad8\u624b\u7684\u624b\u4e0b\uff0c\u8fd9\u79cd\u79ef \u96ea\u575a\u51b0\uff0c\u4e5f\u50cf\u662f\u677e\u8f6f\u6ce5\u6c99\u4e00\u6837\u3002\u94c1\u9539\u7ffb\u98de\u5904\uff0c\u4f55\u6d88\u7247\u523b\uff0c\u5c31\u88ab\u6398\u4e86\u6df1\u51e0\u8fbe\u4e08\u7684\u4e00\u4e2a\u5927\u5751\u3002 \u3000\u3000\u4f0a\u98ce\u94c1\u9539\u518d\u6b21\u843d\u4e0b\uff0c\u5ffd\u7136\u542c\u5230\u201c\u94db\u201d\u5730\u4e00\u58f0\uff0c\u4f0a\u98ce\u624b\u4e2d\u7684\u94c1\u9539\uff0c\u7acb\u523b\u6298\u4e86\u4e00\u534a\uff1a\u4ed6\u8fd9\u4e00 \u9539\uff0c\u7adf\u662f\u6398\u5728\u4e00\u5757\u50cf\u662f\u91d1\u94c1\u4e4b\u5c5e\u7684\u4e0a\u9762\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u559c\u52a8\u989c\u8272\uff0c\u4e00\u63a0\u4e0a\u5751\uff0c\u6362\u4e86\u628a\u94c1\u94f2\uff0c\u53c8\u8dc3\u4e0b\u6765\uff0c\u63a5\u8fde\u51e0\u94f2\uff0c\u8fd9\u571f\u5751\u4e2d\u7a81\u5730\u94f6\u5149 \u5927\u73b0\uff0c\u4e0b\u9762\u7adf\u662f\u4e00\u7247\u767d\u956a\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u7981\u4e3a\u4e4b\u6115\u4f4f\uff0c\u5730\u4e0b\u7684\u8fd9\u4e00\u7247\u767d\u956a\uff0c\u5df2\u51dd\u6210\u4e00\u7247\uff0c\u5c11\u8bf4\u4e5f\u6709\u6570\u5341\u4e07\u4e24\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5fc3\u80f8\u78ca\u843d\uff0c\u4f46\u9aa4\u7136\u89c1\u7740\u8fd9\u949c\u4e07\u767d\u94f6\uff0c\u4e5f\u96be\u514d\u5fc3\u52a8\u795e\u9a70\u3002 \u3000\u3000\u90a3\u77e5\u4e07\u5929\u840d\u5374\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u5c06\u624b\u4e0a\u7684\u94c1\u94f2\u5f80\u4e0a\u4e00\u629b\uff0c\u4f3c\u4e4e\u610f\u5174\u7d22\u7136\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u53c8\u662f\u94f6\u5b50\uff01\u201d \u3000\u3000\u4e00\u6709\u4e0b\u4e4b\u610f\uff0c\u8fd9\u6570\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u5728\u4ed6\u773c\u4e2d\uff0c\u7adf\u6709\u5982\u5e9f\u94c1\u3002\u4f0a\u98ce\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6115\uff01 \u3000\u3000\u5374\u542c\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u63a5\u7740\u53c8\u53f9\u9053\uff1a \u3000\u3000\u201c\u6211\u4ece\u65e0\u91cf\u5c71\u4e0b\u6765\uff0c\u8d39\u4e86\u597d\u591a\u4e8b\uff0c\u624d\u6398\u4e86\u4e09\u5904\uff0c\u90a3\u77e5\u5374\u90fd\u662f\u94f6\u5b50\uff01\u5047\u82e5\u5929\u4e0b\u4eba\u7684\u6240\u8c13 \u201c\u85cf\u5b9d\u201d\uff0c\u90fd\u662f\u94f6\u5b50\uff0c\u90a3\u53ef\u771f\u6559\u4eba\u626b\u5174\uff01\u201d \u3000\u3000\u987b\u77e5\u4e00\u79cd\u540c\u6837\u7684\u4e1c\u897f\uff0c\u5728\u4e24\u4e2a\u4e0d\u540c\u7684\u4eba\u7684\u773c\u91cc\uff0c\u4fbf\u6709\u622a\u7136\u76f8\u5f02\u7684\u4ef7\u503c\u3002 \u3000\u3000\u8fd9\u949c\u4e07\u767d\u94f6\uff0c\u5728\u8fd9\u4e2a\u6b66\u6797\u4e2d\u53f1\u5412\u6a2a\u884c\u7684\u5de8\u76d7\u773c\u91cc\uff0c\u672c\u5df2\u76f4\u5982\u5e9f\u94dc\uff1b\u4f55\u51b5\u4ed6\u6709\u7487\u5149\u4eea\u8fd9\u79cd \u5f02\u5b9d\u5728\u63e1\uff0c\u5fc3\u4e2d\u6240\u5180\u6c42\u4e4b\u7269\u7684\u4ef7\u503c\uff0c\u66f4\u8981\u6bd4\u9ec4\u91d1\u767d\u94f6\u8fd9\u79cd\u4fd7\u4e16\u8d22\u7269\uff0c\u9ad8\u8fc7\u8bb8\u591a\u500d\u3002 \u3000\u3000\u5929\u5149\u4ece\u79ef\u96ea\u7684\u6797\u68a2\u6f0f\u4e0b\u6765\uff0c\u6210\u4e86\u51e0\u8bb8\u591a\u89d2\u800c\u53d8\u5e7b\u7684\u5149\u5f71\u3002 \u3000\u3000\u4f0a\u98ce\u7eb5\u8eab\u51fa\u5751\uff0c\u4f46\u89c9\u6ee1\u5751\u7684\u767d\u956a\uff0c\u88ab\u8fd9\u6563\u788e\u7684\u5149\u5f71\u4e00\u7167\uff0c\u94f6\u5149\u6d41\u52a8\uff0c\u66f4\u663e\u5f97\u5149\u91c7\u593a\u76ee\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u76ee\u5149\u4e00\u8f6c\uff0c\u5ffd\u5730\u7b11\u9053\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\uff01\u4f60\u82e5\u5bf9\u6b64\u6709\u610f\uff0c\u8fd9\u4e9b\u4e1c\u897f\uff0c\u5c31\u7b97\u6211\u9001\u7ed9\u4f60\u7684\u5427\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u7a81\u5730\u4e00\u6c89\uff1a \u3000\u3000\u201c\u8001\u592b\u7eb5\u6a2a\u591a\u5e74\uff0c\u6562\u8bf4\u662f\u6069\u4ec7\u4e86\u4e86\u3002\u8fd9\u6b21\u5728\u65e0\u91cf\u5c71\u5dc5\uff0c\u5374\u53d7\u4e86\u4f60\u7684\u5927\u6069\u2026\u2026\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u4f0a\u98ce\u5fc3\u5934\u7acb\u5373\u4e3a\u4e4b\u4e00\u4eae\uff0c\u79ef\u5b58\u5728\u4ed6\u5fc3\u91cc\u7684\u7591\u56e2\uff0c\u968f\u4e4b\u8c41\u7136\u5f00\u6717\uff1a \u3000\u3000\u201c\u539f\u6765\u8fd9\u88ab\u6211\u5173\u5728\u79d8\u7a9f\u4e2d\u7684\u4e07\u5929\u840d\uff0c\u662f\u88ab\u8427\u65e0\u8fd9\u53ae\u6551\u51fa\u6765\u7684\u3002\u8fd9\u5c31\u662f\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u9003\u51fa\u79d8 \u7a9f\uff0c\u800c\u53c8\u548c\u8427\u65e0\u6709\u7740\u5173\u7cfb\u7684\u539f\u56e0\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u867d\u5df2\u604d\u7136\uff0c\u4f46\u968f\u5373\u53c8\u8d77\u4e86\u4e00\u4e9b\u7591\u95ee\uff1a \u3000\u3000\u201c\u8fd9\u8427\u65e0\u600e\u4f1a\u8dd1\u5230\u65e0\u91cf\u5c71\u5dc5\uff1f\u53c8\u600e\u4f1a\u77e5\u9053\u8fd9\u79d8\u7a9f\u7684\u5f00\u654c\u4e4b\u6cd5\u7684\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u5982\u6d8c\uff0c\u5374\u5fd8\u4e86\u53bb\u56de\u7b54\u8fd9\u4e07\u5929\u840d\u7684\u8bdd\u3002 \u3000\u3000\u4e07\u5929\u840d\u5374\u53c8\u4e00\u63a0\u51fa\u5751\uff0c\u5728\u4e0a\u9762\u558a\u9053\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\uff01\u4f60\u4e14\u4e0a\u6765\uff0c\u518d\u628a\u8fd9\u571f\u5751\u586b\u5e73\uff0c\u8fd9\u4e48\u591a\u94f6\u5b50\uff0c\u4e5f\u4e0d\u662f\u4f60\u6211\u4e24\u4eba\u4e4b\u529b\u6240\u80fd\u642c\u5f97\u8d70 \u7684\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6f2b\u5e94\u4e00\u58f0\uff0c\u65b9\u81ea\u63a0\u4e0a\uff0c\u4e00\u56e2\u7802\u571f\uff0c\u5df2\u5728\u4e07\u5929\u840d\u94c1\u94f2\u4e00\u6311\u4e4b\u4e0b\uff0c\u843d\u4e0b\u5751\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u968f\u610f\u4e00\u8dc3\uff0c\u521a\u597d\u843d\u5728\u4e07\u5929\u840d\u8eab\u4fa7\uff0c\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u8fde\u6311\u94c1\u94f2\uff0c\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u4ed6\u7684\u884c \u52a8\u3002\u4f0a\u98ce\u773c\u89d2\u5fae\u52a8\uff0c\u8111\u6d77\u4e2d\u5ffd\u5730\u5347\u8d77\u4e00\u4e2a\u5ff5\u5934\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u53ea\u8981\u81ea\u5df1\u53f3\u638c\u5fae\u6325\uff0c\u4fbf\u53ef\u76f4\u51fb\u4e07\u5929\u840d\u7684\u80c1\u4e0b\uff0c\u800c\u4e07\u5929\u840d\u4e5f\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u5982 \u6b64\u505a\u3002\u4ed6\u731d\u4e0d\u53ca\u9632\uff0c\u5fc5\u5b9a\u8eb2\u4e0d\u5f00\u8fd9\u4e00\u51fb\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u6ca1\u6709\u5982\u6b64\u505a\uff0c\u5373\u4f7f\u4ee5\u540e\u4ed6\u4ee5\u672c\u6765\u9762\u76ee\u9047\u7740\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u65f6\uff0c\u5c11\u4e0d\u5f97\u4f1a\u6709\u6076 \u6597\uff0c\u751a\u81f3\u4ed6\u4e0d\u662f\u8fd9\u4e07\u5929\u840d\u7684\u654c\u624b\uff0c\u4f46\u8fd9\u79cd\u6709\u6b20\u5149\u660e\u78ca\u843d\u7684\u4e8b\uff0c\u4ed6\u5374\u4e07\u4e07\u7684\u505a\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u81ea\u5fd6\u4e4b\u4e0b\uff0c\u8fd9\u4e07\u5929\u840d\u548c\u81ea\u5df1\u8bf4\u4e0d\u4e0a\u6709\u4ec0\u4e48\u51a4\u4ec7\uff0c\u4ed6\u53c8\u600e\u80fd\u5728\u80cc\u540e\u5411\u4e00\u4e2a\u548c\u81ea\u5df1\u65e0 \u751a\u51a4\u4ec7\u7684\u4eba\uff0c\u9aa4\u4e0b\u6bd2\u624b\u54e9\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u4e3e\u8d77\u94c1\u9539\uff0c\u5e2e\u7740\u4e07\u5929\u840d\u5c06\u7802\u571f\u91cd\u65b0\u586b\u5165\u571f\u5751\u3002 \u3000\u3000\u4ed6\u5e76\u672a\u62d2\u7edd\u4e07\u5929\u840d\u7684\u8d60\u9001\u4ed6\u8fd9\u949c\u4e07\u767d\u94f6\uff0c\u5374\u4e5f\u5e76\u672a\u63a5\u53d7\u3002\u53ea\u56e0\u4e3a\u4ed6\u89c9\u5f97\u8fd9\u949c\u4e07\u767d\u94f6\uff0c\u672c \u975e\u4e07\u5929\u840d\u6240\u6709\u4e4b\u7269\uff0c\u662f\u4ee5\u4ed6\u6839\u672c\u65e0\u6743\u5c06\u4e4b\u8d60\u9001\u7ed9\u81ea\u5df1\uff0c\u90a3\u4e48\u81ea\u5df1\u53c8\u4f55\u5fc5\u8bf4\u51fa\u62d2\u7edd\uff0c\u6216\u662f\u63a5\u53d7 \u7684\u8bdd\u5462\uff1f \u3000\u3000\u800c\u4e14\u91d1\u94f6\u4e00\u7269\uff0c\u53ea\u8981\u7528\u4e4b\u5f97\u5b9c\uff0c\u5927\u53ef\u9020\u798f\u4eba\u7fa4\uff0c\u505a\u8bb8\u591a\u4e8b\u4e1a\uff0c\u81ea\u5df1\u65e5\u540e\u6216\u6709\u7528\u5f97\u7740\u5b83\u7684 \u5730\u65b9\uff0c\u4e5f\u672a\u53ef\u77e5\u3002 \u3000\u3000\u4ed6\u81ea\u4fe1\u8fd9\u949c\u4e07\u767d\u956a\uff0c\u843d\u4eba\u81ea\u5df1\u624b\u4e0a\uff0c\u7528\u4e4b\u4e8e\u4eba\uff0c\u603b\u6bd4\u57cb\u6ca1\u5728\u8fd9\u67af\u6797\u7684\u6ce5\u5730\u4e0b\uff0c\u597d\u5f97\u591a\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4fbf\u53c8\u51ed\u7a7a\u5f97\u4e86\u949c\u4e07\u94b1\u8d22\u3002 \u3000\u3000\u8fd9\u534a\u5e74\u6765\uff0c\u4ed6\u5c61\u5f97\u5947\u7f18\uff0c\u8fd9\u662f\u4e0d\u662f\u51a5\u51a5\u4e0a\u82cd\uff0c\u5728\u5bf9\u4ed6\u4f5c\u4e86\u4e00\u4e9b\u4e0d\u516c\u5e73\u7684\u5904\u7f6e\u540e\u7684\u4e00\u4e9b\u8865 \u507f\u5462\uff1f\u90a3\u5c31\u8981\u770b\u4ed6\u662f\u5426\u80fd\u5584\u4e8e\u8fd0\u7528\u8fd9\u4e9b\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u585e\u7fc1\u5931\u9a6c\uff0c\u7109\u77e5\u975e\u798f\u201d\uff0c\u800c\u4e00\u4e2a\u4eba\u5728\u9aa4\u7136\u83b7\u5f97\u592a\u591a\u7684\u5e78\u8fd0\u4e4b\u540e\uff0c\u4e5f\u672a\u5fc5\u662f\u597d\u4e8b \u54e9\uff01 \u3000\u3000\u65e9\u5148\u6398\u51fa\u53bb\u7684\u571f\uff0c\u867d\u53c8\u91cd\u65b0\u586b\u56de\u571f\u5751\uff0c\u4f46\u6bd5\u7adf\u662f\u548c\u522b\u5904\u4e0d\u4e00\u6837\u4e86\u3002\u4e00\u4e2a\u5fc3\u7075\u4e2d\u7684\u60c5\u611f\uff0c \u5df2\u5168\u90fd\u6298\u78e8\u6b86\u5c3d\u7684\u4eba\uff0c\u7eb5\u7136\u522b\u7684\u60c5\u611f\u6765\u5145\u5b9e\uff0c\u662f\u4e0d\u662f\u4e5f\u4f1a\u7559\u4e0b\u4e00\u4e9b\u4e0d\u53ef\u78e8\u706d\u7684\u521b\u75d5\u5462\uff1f \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e94\u5341\u516d\u7ae0\u3000\u8282\u5916\u751f\u679d>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e94\u5341\u516d\u7ae0\u3000\u8282\u5916\u751f\u679d \u3000\u3000\u63a0\u51fa\u6797\u5916\uff0c\u4e07\u5929\u840d\u7a81\u5730\u56de\u9996\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u53ef\u8981\u5728\u8fd9\u91cc\u505a\u4e2a\u8bb0\u53f7\u3002\u4ee5\u540e\u6765\u62ff\u65f6\u4e5f\u65b9\u4fbf\u4e9b\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u7b11\u7740\u6447\u4e86\u6447\u5934\u3002\u653e\u773c\u56db\u89c2\uff0c\u53ea\u89c1\u539f\u5148\u7559\u5728\u6797\u5916\u7684\u4e24\u5339\u9a6c\uff0c\u5df2\u88ab\u5bd2\u98ce\u5439\u5f97\u53d1\u6296\u3002 \u3000\u3000\u662f\u4ee5\u4e24\u4eba\u4e00\u4e0a\u4e86\u9a6c\uff0c\u8fd9\u4e24\u5339\u5750\u9a91\uff0c\u5c31\u7eb5\u8e44\u72c2\u5954\uff0c\u4f3c\u4e4e\u4e5f\u50cf\u4eba\u4e00\u6837\uff0c\u61a7\u5f97\u5982\u6b64\u4fbf\u80fd\u9a71\u9664\u5bd2 \u6c14\u3002 \u3000\u3000\u4f0a\u98ce\u867d\u7136\u4e00\u591c\u672a\u7720\uff0c\u4f46\u6b64\u523b\u5750\u5728\u6025\u9a70\u7684\u9a6c\u4e0a\uff0c\u8fce\u7740\u6251\u9762\u800c\u6765\u7684\u5bd2\u98ce\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u5026\u610f\u3002 \u3000\u3000\u4f46\u518d\u6b21\u56de\u5230\u6d35\u9633\u65f6\uff0c\u4ed6\u5374\u6709\u4e9b\u997f\u4e86\u3002 \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u548c\u8427\u65e0\u7ea6\u5728\u8c6b\u6eaa\u53e3\uff0c\u5230\u5e95\u662f\u6709\u4ec0\u4e48\u4e8b.\u4f46\u4ed6\u6b64\u523b\u81ea\u7136\u4e5f\u4e0d\u80fd\u95ee\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u4e5f\u4e0d\u613f\u610f\u548c\u4e07\u5929\u840d\u540c\u5230\u8c6b\u6eaa\u53e3\u53bb\uff0c\u8bd5\u60f3\u90a3\u65f6\u82e5\u6709\u4e24\u4e2a\u8427\u65e0\u51fa\u73b0\uff0c\u90a3\u8be5\u662f\u600e\u6837\u4e00 \u79cd\u573a\u9762\uff1f \u3000\u3000\u4e8e\u662f\u5728\u6d35\u9633\u57df\u5916\uff0c\u4ed6\u5c31\u505c\u4f4f\u9a6c\uff0c\u4fa7\u9996\u5411\u4e07\u5929\u840d\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u524d\u8f88\uff01\u5c0f\u9119\u53e6\u5916\u8fd8\u6709\u670b\u53cb\u4e4b\u7ea6\uff0c\u4e07\u8001\u524d\u8f88\u5982\u679c\u65e0\u4e8b\u5429\u5490\uff0c\u5c0f\u9119\u5c31\u60f3\u5728\u6b64\u544a\u8f9e \u4e86\u3002\u201d \u3000\u3000\u4e07\u5929\u840d\u7a81\u5730\u53cc\u76ee\u4e00\u5f20\uff0c\u5728\u4ed6\u8138\u4e0a\u6253\u4e86\u4e2a\u8f6c\u3002 \u3000\u3000\u4f0a\u98ce\u751f\u6015\u4ed6\u5728\u81ea\u5df1\u8138\u4e0a\u770b\u51fa\u4ec0\u4e48\u7834\u7efd\u6765\uff0c\u90a3\u77e5\u9053\u4e07\u5929\u6d25\u795e\u8272\u53c8\u8f6c\u548c\u7f13\uff0c\u4e25\u5cfb\u7684\u8138\u4e0a\uff0c\u7adf \u5fae\u5fae\u6cdb\u51fa\u7b11\u5bb9\u6765\uff0c\u548c\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\uff01\u4f60\u8fd9\u5c31\u4e0d\u5bf9\u4e86\uff0c\u4f60\u4e0d\u662f\u66fe\u7ecf\u7b54\u5e94\u548c\u6211\u540c\u4e0a\u897f\u6881\u5c71\u7684\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u6709\u4e9b\u53d1\u6bdb\uff0c\u5634\u91cc\u4e5f\u5450\u5450\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5374\u542c\u4e07\u5929\u6d25\u53c8\u542b\u7b11\u9053\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\uff01\u4f60\u653e\u5fc3\uff01\u4f60\u4e8e\u6211\u6709\u6069\uff0c\u8001\u592b\u4e00\u751f\u884c\u4e8b\uff0c\u867d\u7136\u7a0d\u5acc\u72e0\u8fa3\uff0c\u4f46\u5bf9\u4e8e\u4f60\u54c8\uff01\u8427\u8001 \u5f1f\uff0c\u4f60\u653e\u5fc3\uff01\u8ddf\u8001\u592b\u4e00\u9f50\u53bb\uff0c\u7edd\u5bf9\u6709\u4f60\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e45\u7ecf\u4e16\u6545\uff0c\u5fc3\u601d\u53c8\u6781\u7075\u654f\uff0c\u6b63\u662f\u4e00\u70b9\u5c31\u900f\u7684\u89d2\u8272\u3002\u4ed6\u4e00\u542c\u4e07\u5929\u840d\u5982\u6b64\u8bf4\uff0c\u5c31\u77e5\u9053\u5373 \u4f7f\u662f\u8427\u65e0\u672c\u4eba\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u897f\u6881\u5c71\u4e4b\u7ea6\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002\u5fc3\u4e2d\u4e00\u5b9a\uff0c\u9042\u4e5f\u542b\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u524d\u8f88\u5bf9\u5c0f\u9119\u7684\u76db\u60c5\uff0c\u5c0f\u9119\u81ea\u662f\u611f\u6fc0\uff1b\u4f46\u5c0f\u9119\u5b9e\u5728\u8fd8\u53e6\u6709\u7ea6\u4f1a\uff0c\u53cd\u6b63\u9752\u5c71\u4e0d\u6539\uff0c\u7eff \u6c34\u957f\u6d41\uff0c\u5c0f\u9119\u53e3\u540e\u81ea\u591a\u9ebb\u70e6\u4e07\u8001\u524d\u8f88\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4e07\u5929\u840d\u7a81\u5730\u7eb5\u58f0\u957f\u7b11\u8d77\u6765\u3002\u4f0a\u98ce\u548c\u4e07\u5929\u840d\u89c1\u9762\u591a\u6b21\uff0c\u8fd9\u5012\u8fd8\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u8fd9\u201c\u94c1\u9762\u5b64\u884c \u5ba2\u201d\u8138\u4e0a\u9732\u51fa\u7b11\u5bb9\u6765\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u7b11\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u67af\u7626\u7684\u8138\u4e0a\uff0c\u7acb\u523b\u53c8\u50cf\u662f\u7ed3\u4e86\u4e00\u5c42\u7384\u51b0\uff0c\u6c89\u7740\u58f0\u97f3\u9053\uff1a \u3000\u3000\u201c\u6211\u95ee\u4f60\uff0c\u4f60\u662f\u548c\u90a3\u51e0\u4eba\u4e4b\u7ea6\u5728\u5148\u5462\uff1f\u8fd8\u662f\u548c\u8001\u592b\u4e4b\u7ea6\u5728\u5148\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u6115\uff0c\u53c8\u5450\u5450\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u53ea\u542c\u4e07\u5929\u840d\u6c89\u58f0\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u82e5\u662f\u548c\u8001\u592b\u4e4b\u7ea6\u5728\u5148\uff0c\u4f60\u5c31\u5f97\u548c\u8001\u592b\u4e00\u540c\u4e0a\u897f\u6881\u5c71\u53bb\uff1b\u4f60\u82e5\u662f\u548c\u522b\u4eba\u4e4b\u7ea6\u5728\u5148\uff0c\u90a3 \u4e48\u4f60\u53c8\u4e3a\u4ec0\u4e48\u8981\u548c\u8001\u592b\u8ba2\u4e0b\u6b64\u7ea6\u5462\uff1f\u96be\u9053\u4f60\u662f\u5b58\u5fc3\u620f\u5f04\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u201c\u987b\u77e5\u4f60\u5728\u65e0\u91cf\u5c71\u5dc5\uff0c\u5c06\u8001\u592b\u6551\u51fa\u5c71\u7a9f\uff0c\u90a3\u4e0d\u8fc7\u662f\u4f60\u9002\u9022\u5176\u4f1a\u800c\u5df2\uff1b\u4f60\u82e5\u662f\u4ed7\u7740\u8fd9\u4e8b\uff0c \u5c31\u5728\u8001\u592b\u9762\u524d\u5f04\u9b3c\uff0c\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u54fc\uff01\u90a3\u4e48\u8001\u592b\u4e00\u6837\u53ef\u4ee5\u5236\u4f60\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u6697\u6697\u53eb\u82e6\uff0c\u77e5\u9053\u81ea\u5df1\u8fd9\u6b21\u53c8\u9047\u7740\u9ebb\u70e6\u4e86\uff0c\u50cf\u4e07\u5929\u840d\u8fd9\u79cd\u4eba\uff0c\u6b63\u662f\u51e1\u4e8b\u90fd\u4e0d\u80fd\u7406 \u55bb\u7684\u89d2\u8272\uff01\u81ea\u5df1\u4e8b\u60c5\u5df2\u7ecf\u591f\u591a\u4e86\uff0c\u672c\u6765\u5c31\u50cf\u4e00\u56e2\u4e71\u9ebb\uff0c\u7406\u4e5f\u7406\u4e0d\u6e05\u695a\uff0c\u4f46\u5929\u9053\u5f04\u4eba\uff0c\u81ea\u5df1\u4e9f \u6b32\u4e00\u89c1\u7684\u4eba\uff0c\u8b6c\u5982\u5251\u5148\u751f\uff0c\u51cc\u5b59\u654f\uff0c\u751a\u81f3\u8427\u5357\u82f9\uff0c\u81ea\u5df1\u4e00\u4e2a\u4e5f\u9047\u4e0d\u4e0a\uff0c\u5374\u504f\u504f\u8ba9\u81ea\u5df1\u9047\u7740\u8fd9 \u4e9b\u4e0d\u613f\u610f\u89c1\u7684\u89d2\u8272\u3002 \u3000\u3000\u4e00\u9762\uff0c\u4ed6\u5374\u53c8\u5947\u602a\uff1a\u8fd9\u4e07\u5929\u840d\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u81ea\u5df1\u540c\u8d74\u897f\u6881\u5c71\u5462\uff1f\u90a3\u897f\u6881\u5c71\u4e0a\uff0c\u53c8\u6709\u4ec0\u4e48 \u4e8b\u8981\u53d1\u751f\u5462\uff1f \u3000\u3000\u81ea\u4ece\u4ed6\u5728\u534e\u5c71\u4e4b\u9634\uff0c\u9047\u7740\u5b59\u654f\u6bcd\u5973\u4e4b\u540e\uff0c\u4e00\u5207\u4e8b\u7684\u53d1\u5c55\uff0c\u5c31\u4f3c\u4e4e\u4e0d\u662f\u4ed6\u81ea\u5df1\u6240\u80fd\u63a7\u5236\u5f97 \u4e86\u7684\u3002\u8fd9\u4e9b\u4e8b\u867d\u7136\u90fd\u6709\u7740\u5173\u8fde\uff0c\u4f46\u5374\u90fd\u662f\u8282\u5916\u4e4b\u8282\uff0c\u679d\u5916\u4e4b\u679d\uff0c\u8fde\u4ed6\u81ea\u5df1\uff0c\u90fd\u51e0\u4e4e\u4e0d\u77e5\u9053\u90a3 \u4e00\u6761\u662f\u4e3b\u5e72\u4e86\u3002 \u3000\u3000\u4ed6\u4fef\u9996\u6c89\u541f\u4e86\u534a\u664c\uff0c\u7136\u540e\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u4e00\u53cc\u5bd2\u5149\u5982\u5251\u7684\u7738\u5b50\uff0c\u6b63\u5728\u671b\u7740 \u81ea\u5df1\uff0c\u9759\u5f85\u7740\u81ea\u5df1\u7684\u7b54\u8986\u3002 \u3000\u3000\u201c\u5509\uff01\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u6211\u5c31\u4e00\u5207\u7d22\u6027\u987a\u4e4e\u81ea\u7136\u597d\u4e86\uff0c\u53cd\u6b63\u5251\u5148\u751f\u548c\u5b59\u654f\u6bcd\u5973\u4e24\u7684\u884c\u8e2a\uff0c\u6211 \u662f\u65e0\u5904\u53ef\u5bfb\u80ae\u82f9\u59b9\u4e00\u6012\u800c\u53bb\u4e4b\u540e\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u5230\u90a3\u91cc\u53bb\u4e86\uff1b\u5929\u4e89\u6559\u5728\u6b66\u6797\u4e2d\u65e9\u5df2\u6839\u6df1\u8482 \u56fa\uff0c\u6211\u8981\u590d\u4ec7\uff0c\u4e5f\u4e0d\u662f\u4e00\u671d\u4e00\u5915\u53ef\u4ee5\u505a\u5230\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u81f3\u6b64\uff0c\u89c9\u5f97\u81ea\u5df1\u867d\u7136\u50cf\u662f\u6709\u8ba6\u591a\u4e8b\u8981\u505a\uff0c\u4f46\u8fd9\u4e9b\u4e8b\u5374\u53c8\u90fd\u662f\u832b\u65e0\u5934\u7eea\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u62ac\u8d77\u5934\u6765\uff0c\u65e0\u53ef\u5948\u4f55\u5730\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u524d\u8f88\u65e2\u7136\u6267\u610f\u5982\u6b64\uff0c\u90a3\u4e48\u5c0f\u9119\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u4e07\u5929\u840d\u53c8\u5fae\u9732\u7b11\u5bb9\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff01\u8fd9\u624d\u50cf\u8bdd\u3002\u4f60\u653e\u5fc3\uff01\u8001\u592b\u603b\u6709\u751c\u5934\u7ed9\u4f60\u5403\uff0c\u53ea\u6015\u4e00\u5230\u897f\u6881\u5c71\uff0c\u8001\u592b\u518d\u8d76\u4f60\u4e0b \u6765\uff0c\u4f60\u90fd\u4e0d\u80af\u4e0b\u6765\u4e86\u54e9\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\uff0c\u5374\u53c8\u4f7f\u4f0a\u98ce\u5982\u5815\u4e94\u91cc\u96fe\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u8def\u4e0a\uff0c\u4ed6\u4e0d\u65ad\u5730\u5728\u601d\u7d22\u7740\uff1a\u4e07\u5929\u840d\u4e3a\u4ec0\u4e48\u8981\u81ea\u5df1\u540c\u4e0a\u897f\u6881\u5c71\u5462\uff1f \u3000\u3000\u8fd9\u95ee\u9898\uff0c\u9976\u662f\u4ed6\u7528\u5c3d\u5fc3\u673a\uff0c\u5374\u4e5f\u5f97\u4e0d\u5230\u7b54\u6848\u3002\u4f46\u662f\u5728\u8fd9\u4e00\u8def\u4e0a\uff0c\u4ed6\u5374\u77e5\u9053\u4e86\u4e00\u4ef6\u4e8b\uff0c\u90a3 \u5c31\u662f\u8fd9\u4e07\u5929\u840d\u5bf9\u4ed6\u7edd\u65e0\u6076\u610f\u3002 \u3000\u3000\u53ea\u662f\u7b49\u5230\u4ed6\u4e00\u95ee\u8d77\u8fd9\u95ee\u9898\u7684\u65f6\u5019\uff0c\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u5c31\u4f1a\u5fae\u5fae\u542b\u7740\u7b11\u9053\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\uff01\u4f60\u4e0d\u7528\u591a\u95ee\uff0c\u4e00\u5230\u4e86\u5c71\u4e0a\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\u4e86\uff0c\u53cd\u6b63\u8fd9\u6b21\u6211\u8ba9\u4f60\u4e00\u9f50\u4e0a\u897f\u6881\u5c71\uff0c \u603b\u662f\u5bf9\u4f60\u6709\u76ca\u65e0\u635f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e07\u5929\u840d\u7adf\u7136\u5b88\u53e3\u5982\u74f6\uff0c\u4e00\u4e9b\u53e3\u98ce\u4e5f\u4e0d\u80af\u9732\u51fa\u6765\u5fc3\u800c\u4e14\u8a00\u4e0b\u4e4b\u610f\uff0c\u9887\u6709\u8981\u8ba9\u8fd9\u5192\u724c\u7684\u8427 \u65e0\u4f0a\u98ce\uff0c\u60ca\u559c\u4e00\u4e0b\u7684\u6837\u5b50\u3002 \u3000\u3000\u6700\u4f7f\u4f0a\u98ce\u611f\u5230\u96be\u4ee5\u5e94\u4ed8\u7684\uff0c\u8fd8\u662f\u8fd9\u4e07\u5929\u840d\u4e00\u8def\u4e0a\u4e0d\u65ad\u5730\u8be2\u95ee\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\uff01\u6211\u770b\u4f60\u7684\u6b66\u529f\u4e0d\u5f31\uff0c\u5185\u529f\u4e5f\u9887\u6709\u6839\u57fa\uff0c\u4f60\u7684\u5e08\u627f\u662f\u90a3\u4e00\u95e8\uff01\u662f\u90a3\u4e00\u6d3e\u5440\uff01\u201d \u3000\u3000\u53c8\u95ee\u9053\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\uff01\u6211\u770b\u4f60\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u6587\u624d\u4e5f\u4e0d\u574f\uff0c\u4f60\u7684\u5bb6\uff0c\u60f3\u5fc5\u662f\u4e66\u9999\u4e4b\u5bb6\u5427\uff1f\u4ee4\u5c0a\u4ee4\u5802 \u90fd\u8fd8\u5065\u5728\u5417\uff1f\u4f60\u7684\u5bb6\u4e61\u662f\u5c5e\u90a3\u91cc\u5440\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\uff0c\u4f0a\u98ce\u90fd\u968f\u53e3\u7b54\u8986\u4e86\uff0c\u4e00\u9762\u53c8\u6697\u81ea\u5e86\u5e78\uff0c\u90a3\u8427\u65e0\u4ee5\u524d\u6ca1\u6709\u544a\u8bc9\u8fc7\u4ed6\u3002 \u3000\u3000\u90a3\u77e5\u8fd9\u4e07\u5929\u840d\u5728\u8fc7\u4e86\u4fe1\u9633\u7684\u65f6\u5019\u95ee\u4e86\u4ed6\u4e00\u6b21\uff0c\u5230\u4e86\u53f0\u80a5\uff0c\u5374\u53c8\u628a\u540c\u6837\u7684\u95ee\u9898\uff0c\u95ee\u4e86\u4ed6\u4e00 \u6b21\uff0c\u4f0a\u98ce\u4e0d\u7981\u6697\u81ea\u5e86\u5e78\u81ea\u5df1\u7684\u8bb0\u5fc6\u529b\uff0c\u53c8\u628a\u540c\u6837\u7684\u8bdd\uff0c\u56de\u7b54\u4e86\u4e00\u6b21\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u5374\u4e0d\u7981\u5947\u602a\uff0c\u8fd9\u4e07\u5929\u6d25\u4e3a\u4ec0\u4e48\u9891\u9891\u67e5\u95ee\u201c\u8427\u65e0\u201d\u7684\u5bb6\u4e16\u5462\uff1f \u3000\u3000\u987b\u77e5\u4e07\u5929\u840d\u5728\u65e0\u91cf\u5c71\u5dc5\u4e00\u803d\u5341\u5e74\uff0c\u5929\u4e89\u6559\u7684\u5174\u8d77\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\uff1b\u8427\u65e0\u7684\u540d\u5b57\uff0c\u4ed6\u4e5f\u672a\u66fe \u542c\u8fc7\uff1b\u4ed6\u4e4b\u6240\u4ee5\u9891\u9891\u95ee\u6b64\uff0c\u81ea\u7136\u662f\u6709\u7740\u539f\u56e0\u7684\uff0c\u53ea\u662f\u8fd9\u539f\u56e0\uff0c\u4f0a\u98ce\u518d\u4e5f\u65e0\u6cd5\u731c\u5f97\u51fa\u6765\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u5728\u65e0\u91cf\u5c71\u5dc5\uff0c\u88ab\u4f0a\u98ce\u4ee5\u673a\u667a\u5173\u4eba\u79d8\u7a9f\uff0c\u4ed6\u7eb5\u7136\u6b66\u529f\u7edd\u9876\uff0c\u5374\u4e5f\u65e0\u6cd5\u4ece\u90a3\u539a\u8fbe\u8fd1\u4e08\u7684\u5c71 \u58c1\u4e2d\u7a7f\u51fa\u6765\uff0c\u800c\u8fd9\u5c71\u7a9f\u53c8\u522b\u65e0\u9000\u8def\u3002 \u3000\u3000\u8d77\u5148\uff0c\u4ed6\u8fd8\u5e0c\u5180\u8fd9\u79d8\u7a9f\u7684\u6d1e\u95e8\uff0c\u6216\u8bb8\u80fd\u591f\u5728\u91cc\u9762\u5f00\u542f\u4e5f\u672a\u53ef\u77e5\uff0c\u53ef\u662f\u4e24\uff0c\u4e09\u5929\u4e4b\u540e\uff0c\u4ed6 \u77e5\u9053\u81ea\u5df1\u8fd9\u5e0c\u671b\u662f\u843d\u7a7a\u4e86\uff0c\u9965\u997f\u548c\u75b2\u52b3\uff0c\u4f7f\u5f97\u4ed6\u5df2\u8fdb\u5165\u5944\u5944\u4e00\u606f\u7684\u72b6\u6001\uff0c\u4ed6\u51e0\u4e4e\u6ca1\u6709\u52c7\u6c14\u8d70 \u5230\u8fd9\u79d8\u7a9f\u6700\u91cc\u9762\u4e00\u5c42\u7684\u5c71\u6d1e\u91cc\u53bb\uff0c\u56e0\u4e3a\u90a3\u91cc\u6709\u7740\u5999\u624b\u8bb8\u767d\u7684\u5c38\u8eab\u3002 \u3000\u3000\u4e00\u4e2a\u4e2a\u5e0c\u671b\uff0c\u968f\u7740\u65f6\u5149\u4e4b\u6d41\u53bb\u800c\u7834\u706d\uff0c\u4f46\u662f\u8fd9\u6b66\u6797\u4e4b\u602a\u6770\uff0c\u4ecd\u4e0d\u7518\u5fc3\u5c31\u6b64\u6b7b\u53bb\u3002\u800c\u4ed6\u6240 \u996e\u4e0b\u7684\u5999\u624b\u8bb8\u767d\u4f53\u4e2d\u542b\u6709\u7075\u836f\u7684\u8840\uff0c\u4e5f\u5947\u8ff9\u822c\u5730\u652f\u6301\u4e86\u4ed6\u597d\u51e0\u5929\u7684\u751f\u547d\u3002\u4ed6\u76d8\u5750\u5728\u8fd9\u79d8\u7a9f\u7684 \u6d1e\u95e8\u540e\u9762\uff0c\u7528\u4ed6\u9a70\u8a89\u6b66\u6797\u7684\u91d1\u521a\u638c\u529b\uff0c\u4e0d\u65ad\u5730\u51fb\u7740\u5c71\u58c1\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u4e5f\u81ea\u77e5\uff0c\u81ea\u5bb6\u638c\u529b\u867d\u662f\u60ca\u4eba\uff0c\u4f46\u82e5\u60f3\u51fb\u7a7f\u8fd9\u5c71\u58c1\uff0c\u4ecd\u662f\u7edd\u65e0\u53ef\u80fd\uff1b\u4f55\u51b5\u81ea\u5df1\u4e5f\u5c06 \u8981\u7981\u4e0d\u4f4f\u9965\u6e34\u548c\u75b2\u52b3\u7684\u4fb5\u8680\u4e86\u54e9\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u81ea\u5df1\u638c\u51fb\u5c71\u58c1\u7684\u58f0\u97f3\uff0c\u5374\u4f1a\u88ab\u98ce\u95fb\u201c\u5357\u5077\u5317\u76d7\u201d\u5728\u6b64\u5c71\u4e2d\uff0c\u957f \u9014\u8dcb\u6d89\u800c\u6765\u5bfb\u5b9d\u7684\u8427\u65e0\u542c\u5230\u4e86\uff0c\u4e8e\u662f\u4ed6\u4ee5\u7edd\u9876\u5185\u529b\u9694\u7740\u5c71\u58c1\u4e00\u95ee\uff0c\u77e5\u9053\u5173\u5728\u91cc\u9762\u7684\u5c31\u662f\u201c\u5317 \u76d7\u201d\u4e07\u5929\u840d\u3002 \u3000\u3000\u4e07\u5929\u840d\u72c2\u559c\u4e4b\u4e0b\uff0c\u4e5f\u9694\u7740\u5c71\u58c1\u544a\u8bc9\u4e86\u201c\u8427\u65e0\u201d\u8fd9\u79d8\u7a9f\u5f00\u654c\u7684\u65b9\u6cd5\u2014\u2014\u4ed6\u5728\u4f0a\u98ce\u5f00\u655e\u5c71\u58c1 \u7684\u65f6\u5019\uff0c\u65e9\u5df2\u5c31\u8bb0\u4e0b\u4e86\u65b9\u6cd5\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u5c31\u518d\u6b21\u5947\u8ff9\u4f3c\u7684\u4fdd\u5b58\u4e86\u751f\u547d\u3002 \u3000\u3000\u662f\u4ee5\u4f0a\u98ce\u6b64\u523b\u7684\u731c\u6d4b\u4e0d\u9519\uff0c\u4ed6\u5bf9\u8fd9\u201c\u8427\u65e0\u201d\uff0c\u7684\u786e\u662f\u6ca1\u6709\u534a\u70b9\u6076\u610f\u7684\uff0c\u800c\u4e14\u6b64\u4eba\u867d\u7136\u884c \u4e8b\u5fc3\u72e0\u624b\u8fa3\uff0c\u559c\u6012\u65e0\u5e38\uff0c\u4f46\u5374\u7684\u786e\u662f\u6709\u6069\u5fc5\u62a5\u7684\u89d2\u8272\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u9a91\u7684\uff0c\u90fd\u662f\u957f\u7a0b\u5065\u9a6c\uff0c\u662f\u4ee5\u5728\u8def\u4e0a\u5e76\u6ca1\u6709\u803d\u8bef\u4ec0\u4e48\u65f6\u5019\uff0c\u4fbf\u5df2\u5230\u4e86\u897f\u6881\u5c71\u4e4b\u5357\u7684 \u8c6b\u6eaa\u53e3\uff0c\u4f0a\u98ce\u5fc3\u91cc\u6709\u4e9b\u5fd0\u5fd1\uff1a \u3000\u3000\u201c\u4e07\u4e00\u53c8\u8dd1\u51fa\u6765\u4e86\u4e2a\u8427\u65e0\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4f46\u662f\u4e0a\u5929\u5374\u5c06\u8fd9\u4ef6\u4e8b\u5b89\u6392\u5f97\u5982\u6b64\u5de7\u5999\uff1b\u4ed6\u4eec\u82e5\u5728\u8c6b\u6eaa\u53e3\u803d\u8bef\u4e00\u5929\uff0c\u4ed6\u4eec\u5c31\u4f1a\u9047\u7740\u88ab\u4e03\u6d77 \u6e14\u5b50\u62bc\u6765\u7684\u8427\u5357\u82f9\uff0c\u4e5f\u5c31\u4f1a\u9047\u7740\u65f6\u523b\u4e0d\u5fd8\u201c\u5357\u5077\u5317\u76d7\u201d\u7684\u85cf\u5b9d\u7684\uff0c\u771f\u6b63\u5929\u4e89\u6559\u4e3b\u8427\u65e0\u3002 \u3000\u3000\u90a3\u4e48\u8fd9\u4ef6\u4e8b\uff0c\u5f53\u65f6\u4e5f\u8bb8\u4f1a\u4ee4\u4f0a\u98ce\u611f\u5230\u96be\u4ee5\u5e94\u5fd6\uff0c\u4e8b\u540e\u5374\u6ca1\u6709\u90a3\u4e48\u591a\u66f2\u6298\u4e86\uff1b\u53ea\u662f\u4e8b\u60c5\u504f \u504f\u5982\u6b64\u9634\u9519\u9633\u5dee\uff01 \u3000\u3000\u4f46\u8fd9\u5728\u5f53\u65f6\uff0c\u5374\u53c8\u6709\u8c01\u80fd\u9884\u6599\u5f97\u5230\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4f0a\u98ce\u5c31\u6709\u4e86\u4e00\u4e2a\u81f3\u6b64\u8fd8\u672a\u4f59\u89e3\u7b54\u7684\u95ee\u9898\uff1a \u3000\u3000\u201c\u5728\u897f\u6881\u5c71\u4e0a\uff0c\u6709\u4ec0\u4e48\u4e8b\u8981\u53d1\u751f\u5462\uff1f\u4e07\u5929\u840d\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6211\u540c\u4e0a\u897f\u6881\u5c71\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u5c31\u5e26\u7740\u8fd9\u4e2a\u95ee\u9898\uff0c\u4e0a\u4e86\u897f\u6881\u5c71\u3002 \u3000\u3000\u4ed6\u82e5\u662f\u77e5\u9053\u5728\u897f\u6881\u5c71\u91cc\uff0c\u7adf\u6709\u7740\u90a3\u4e48\u591a\u4e8b\u5c06\u8981\u53d1\u751f\u7684\u8bdd\uff0c\u53ea\u6015\u4ed6\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u4f1a\u968f\u7740 \u4e07\u5929\u840d\u4e0a\u5c71\u4e86\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e94\u5341\u4e03\u7ae0\u3000\u7fe0\u88c5\u4e3d\u4eba>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e94\u5341\u4e03\u7ae0\u3000\u7fe0\u88c5\u4e3d\u4eba \u3000\u3000\u5927\u5730\u660f\u669d\uff0c\u6b63\u662f\u9ec4\u660f\u2014\u2014\u5e26\u7740\u4e9b\u8bb8\u7591\u60d1\u7684\u4f0a\u98ce\uff0c\u4fbf\u8e0f\u7740\u82cd\u82cd\u66ae\u972d\uff0c\u968f\u7740\u90a3\u9ed1\u9053\u4e2d\u7684\u5de8 \u5b50\uff0c\u201c\u5317\u76d7\u201d\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\uff0c\u4e0a\u4e86\u8c6b\u6eaa\u53e3\u5317\u7684\u897f\u6881\u5c71\u3002 \u3000\u3000\u5c71\u8def\u9036\u8fe4\uff0c\u524d\u884c\u6570\u91cc\uff0c\u591c\u8272\u4fbf\u6df1\uff0c\u591c\u5bd2\u4e5f\u8d8a\u91cd\u3002\u94c1\u9762\u5b64\u884c\u5ba2\u672c\u5728\u524d\u9762\u7f13\u7f13\u800c\u884c\uff0c\u4e00\u9762\u56de \u5934\u548c\u4f0a\u98ce\u8bb2\u4e9b\u4e0d\u7740\u8fb9\u9645\u7684\u8bdd\uff0c\u5e76\u672a\u65bd\u5c55\u51fa\u8f7b\u529f\u6765\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7adf\u4e00\u64a9\u957f\u886b\uff0c\u4fa7\u9996\u559d\u9053\uff1a \u3000\u3000\u201c\u8ddf\u7740\u6211\uff0c\u5c0f\u5fc3\u4e9b\uff01\u201d \u3000\u3000\u8dfa\u811a\u5411\u8def\u4fa7\u63a0\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u56db\u95ea\uff0c\u89c1\u5230\u8fd9\u6761\u5c71\u8def\u65c1\u8fb9\u7adf\u662f\u6839\u679d\u866c\u7ed3\u7684\u68ee\u6797\u3002\u6b64\u523b\u591c\u8272\u672c\u9eef\uff0c\u7531\u5916\u671b\u53bb\uff0c\u8fd9 \u7247\u4e1b\u6797\uff0c\u66f4\u662f\u9ed1\u9edd\u9edd\u5730\u6df1\u4e0d\u53ef\u6d4b\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u5e26\u81ea\u5df1\u8d70\u8fdb\u8fd9\u79cd\u68ee\u6797\u505a\u4ec0\u4e48\uff0c\u5fc3\u4e0b\u65b9\u81ea\u6709\u4e9b\u6e4d\u7136\uff0c\u524d\u9762\u7a81\u5730\u706b\u5149\u4e00 \u95ea\uff0c\u94c1\u9762\u5b64\u884c\u5ba2\u5df2\u4ece\u6000\u4e2d\u53d6\u4e86\u4e2a\u706b\u647a\u5b50\u51fa\u6765\uff0c\u4eae\u8d77\u4e00\u70b9\u867d\u5f88\u5fae\u5f31\uff0c\u4f46\u5728\u6b64\u523b\u5374\u663e\u5f97\u9887\u4e3a\u660e\u4eae \u7684\u706b\u5149\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u81ea\u8e0c\u8e87\u4e86\u4e00\u4e0b\uff0c\u4e07\u5929\u840d\u5df2\u5728\u524d\u9762\u6325\u624b\u62db\u547c\uff0c\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4f0a\u98ce\u4f3c\u4e4e\u4e5f\u65e0\u6cd5\u9000\u5374\uff0c \u4e8e\u662f\u4ed6\u5fae\u63d0\u771f\u6c14\uff0c\u4e5f\u968f\u7740\u5165\u6797\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u8d70\u7684\u9053\u8def\uff0c\u4e5f\u6b63\u662f\u8427\u5357\u82f9\u5728\u7b2c\u4e09\u65e5\u6e05\u6668\u6240\u8d70\u7684\uff1b\u53ea\u662f\u8427\u5357\u82f9\u90a3\u65f6\u662f\u832b\u65e0\u76ee\u7684\u5730\u63a2 \u7d22\uff0c\u800c\u94c1\u9762\u5b64\u884c\u5ba2\u5374\u662f\u8f7b\u8f66\u719f\u8def\uff0c\u5f77\u4f5b\u5bf9\u8fd9\u9eef\u9ed1\uff0c\u6d53\u5bc6\u7684\u68ee\u6797\uff0c\u751a\u4e3a\u719f\u6089\uff0c\u5df2\u4e0d\u77e5\u6765\u8fc7\u591a\u5c11 \u904d\u4f3c\u7684\u3002 \u3000\u3000\u8fd9\u53ef\u53c8\u6559\u4f0a\u98ce\u5fc3\u4e2d\u4e3a\u4e4b\u7591\u60d1\u4e0b\u5df2\uff0c\u5165\u6797\u6108\u6df1\uff0c\u4ed6\u5fc3\u4e2d\u7684\u8b66\u89c9\uff0c\u4e5f\u5c31\u63d0\u5f97\u6108\u9ad8\u3002 \u3000\u3000\u9ed1\u6697\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e07\u5929\u840d\u5e26\u7740\u624b\u4e2d\u7684\u4e00\u70b9\u706b\u5149\uff0c\u873f\u8712\u524d\u884c\uff0c\u5212\u7834\u8fd9\u79cd\u6df1\u6c89\u7684\u9ed1\u9eef\u3002\u4ed6\u4eec\u811a \u6b65\u8e0f\u5728\u79ef\u96ea\uff0c\u67af\u679d\uff0c\u6df7\u5408\u7740\u8d25\u53f6\uff0c\u6de4\u6ce5\u7684\u58f0\u97f3\uff0c\u4e5f\u7ed9\u8fd9\u79cd\u6df1\u6c89\u7684\u9759\u5bc2\uff0c\u5e26\u6765\u751f\u673a\u3002 \u3000\u3000\u4e09\u8f6c\u4e24\u8f6c\uff0c\u4ed6\u4eec\u4fbf\u4e5f\u5230\u4e86\u90a3\u7247\u65ad\u5d16\u524d\u9762\uff0c\u6b64\u523b\u5bc6\u6797\u5df2\u5c3d\uff0c\u5df2\u6709\u5929\u5149\u5c04\u4e0b\uff0c\u4f46\u4e07\u5929\u840d\u624b\u4e2d \u7684\u706b\u5149\uff0c\u5374\u663e\u5f97\u5fae\u5f31\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u95ea\u52a8\uff0c\u4f46\u662f\u65ad\u5d16\u4e4b\u4e0b\uff0c\u6da7\u58d1\u6df1\u6c89\uff0c\u51e0\u4e0d\u89c1\u5e95\u3002\u5728\u5bf9\u9762\u5c71\u6881\u4e4b\u4e0a\uff0c\u5c4b\u5f71\u5e62\u5e62\uff0c\u4f9d \u7a00\u9119\u4ee5\u770b\u5230\u4e00\u7247\u4ead\u9601\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u81ea\u53c8\u7591\u4e91\u5927\u8d77\uff0c\u4ed6\u867d\u7136\u4e45\u5386\u6c5f\u6e56\uff0c\u9605\u5386\u9887\u591a\uff0c\u5374\u4e5f\u4ece\u672a\u89c1\u8fc7\u5728\u8fd9\u79cd\u7edd\u9669\u7684\u5730\u52bf \u4e2d\uff0c\u8fd8\u6709\u7740\u4ead\u9601\u7684\u3002\u800c\u4e07\u5929\u840d\u5c06\u4ed6\u5e26\u5230\u6b64\u5904\u6765\u7684\u7528\u610f\uff0c\u4ed6\u66f4\u662f\u65e0\u6cd5\u63e3\u6d4b\u3002 \u3000\u3000\u987b\u77e5\u4e07\u5929\u840d\u81f3\u6b64\uff0c\u8fd8\u672a\u5411\u4ed6\u900f\u51fa\u534a\u70b9\u53e3\u98ce\uff0c\u82e5\u662f\u4e0d\u660e\u4e0d\u767d\u7740\u4e86\u4eba\u5bb6\u9053\u513f\uff0c\u90a3\u5c82\u975e\u51a4\u6789\uff1f \u3000\u3000\u90a3\u77e5\u4e07\u5929\u840d\u7a81\u7136\u4fa7\u76ee\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u5f1f\uff01\u8fd9\u91cc\u5c31\u662f\u5730\u5934\u4e86\u3002\u8001\u592b\u6614\u5e74\u82b1\u4e86\u65e0\u6570\u5fc3\u8840\uff0c\u624d\u5728\u8fd9\u91cc\u5efa\u4e86\u8fd9\u4e48\u4e2a\u6240\u5728\uff0c\u6c5f\u6e56\u4e2d \u4eba\uff0c\u80fd\u591f\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u6050\u6015\u6700\u591a\u4e5f\u4e0d\u8fc7\u4e94\u4eba\u54e9\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u81ea\u662f\u8ba4\u4e3a\u4f0a\u98ce\u80fd\u6765\u6b64\u5730\uff0c\u5df2\u662f\u5f02\u6570\u3002 \u3000\u3000\u4f0a\u98ce\u53ea\u5f97\u4e00\u7b11\uff0c\u5fc3\u4e0b\u65b9\u81ea\u6697\u5fd6\uff01 \u3000\u3000\u201c\u539f\u6765\u8fd9\u6d53\u6797\u5bc6\u9601\uff0c\u662f\u4e07\u5929\u840d\u6240\u5efa\u7684\u3002\u201d \u3000\u3000\u518d\u4e00\u8f6c\u5ff5\uff1a \u3000\u3000\u201c\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u5efa\u4e0b\u8fd9\u79cd\u6240\u5728\uff0c\u60f3\u5fc5\u662f\u4e3a\u4e86\u6536\u85cf\u4ed6\u4e00\u751f\u4e2d\u5f97\u6765\u7684\u73cd\u5b9d\u3002\u4f46 \u2014\u2014\u201d \u3000\u3000\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u5374\u662f\u90a3\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u7a81\u5730\u64ae\u53e3\u957f\u5578\u8d77\u6765\u3002 \u3000\u3000\u5578\u58f0\u5982\u957f\u7a7a\u9e64\u5533\uff0c\u9ad8\u4ea2\u5165\u4e91\uff0c\u5728\u8fd9\u9759\u5bc2\u7684\u591c\u8272\u4e2d\uff0c\u4e45\u4e45\u4e0d\u6563\u3002 \u3000\u3000\u4f0a\u98ce\u81ea\u4e5f\u88ab\u8fd9\u7a81\u6765\u7684\u5578\u58f0\u6240\u60ca\uff1b\u706b\u5149\u4e4b\u4e2d\uff0c\u4f46\u89c1\u94c1\u9762\u5b64\u884c\u5ba2\u4e25\u5cfb\u7684\u8138\u4e0a\uff0c\u6b64\u523b\u7adf\u5fae\u5fae\u9732 \u51fa\u7126\u6025\u7684\u795e\u8272\uff0c\u76ee\u5149\u70af\u70af\uff0c\u671b\u7740\u5bf9\u5d16\u7684\u9601\u5f71\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e0d\u7981\u53c8\u662f\u4e00\u52a8\u3002\u987b\u77e5\u4ed6\u672c\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u77e5\u9053\u4ee5\u94c1\u9762\u5b64\u884c\u5ba2\u8fd9\u79cd\u4eba\uff0c\u5018\u82e5\u5bf9 \u5d16\u7684\u697c\u9601\uff0c\u4ec5\u662f\u4ed6\u7684\u85cf\u73cd\u4e4b\u5730\uff0c\u90a3\u4e48\u4ed6\u6b64\u523b\u7edd\u4e0d\u4f1a\u9732\u51fa\u8fd9\u79cd\u795e\u8272\u6765\uff0c\u9664\u975e\u90a3\u91cc\u6709\u7740\u503c\u5f97\u8fd9\u8349 \u83bd\u5de8\u8c6a\u7126\u6025\u7684\u4e1c\u897f\u3002 \u3000\u3000\u201c\u4f46\u90a3\u8fb9\u53c8\u662f\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u7684\u76ee\u5149\uff0c\u4e0d\u7981\u4e5f\u968f\u4e4b\u5411\u5bf9\u5d16\u671b\u53bb\u3002\u4f46\u5578\u58f0\u8fc7\u540e\uff0c\u56db\u4e0b\u53c8\u7acb\u523b\u6062\u590d\u6b7b\u5bc2\u3002 \u3000\u3000\u90a3\u5982\u58a8\u591c\u8272\u4e2d\u7684\u9601\u5f71\uff0c\u4e5f\u4f9d\u7136\u662f\u9759\u5bc2\u5730\u8e72\u8e1e\u5728\u90a3\u91cc\uff0c\u5e76\u6ca1\u6709\u534a\u4e1d\u52a8\u9759\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u9762\u4e0a\u7126\u6025\u7684\u795e\u8272\uff0c\u66f4\u4e3a\u663e\u9732\uff0c\u4f3c\u4e4e\u5728\u6697\u4e2d\u4f4e\u8bed\u4e00\u53e5\uff1a \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u4ece\u5730\u4e0a\u6361\u8d77\u4e00\u5757\u77f3\u5b50\uff0c\u6296\u624b\u5411\u5bf9\u5d16\u6253\u53bb\u3002 \u3000\u3000\u4e24\u5d16\u4e4b\u95f4\uff0c\u76f8\u9694\u6570\u4e08\uff0c\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5c24\u5176\u73b0\u5f97\u9065\u8fdc\u3002 \u3000\u3000\u4f0a\u98ce\u4f46\u89c1\u8fd9\u5757\u77f3\u5b50\uff0c\u50cf\u6d41\u661f\u4f3c\u7684\u63a0\u8fc7\u6df1\u58d1\uff0c\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u51fb\u5728\u697c\u9601\u4e0a\u3002 \u3000\u3000\u8fd9\u79cd\u60ca\u4eba\u7684\u8155\u529b\uff0c\u4f7f\u5f97\u4f0a\u98ce\u4e0d\u895f\u53c8\u4e3a\u4e4b\u4e00\u60ca\uff01 \u3000\u3000\u800c\u6b64\u523b\u4ed6\u8eab\u4fa7\u7684\u4e07\u5929\u840d\u5578\u58f0\u53c8\u8d77\uff0c\u4f3c\u4e4e\u6bd4\u4e0a\u6b21\u66f4\u4e3a\u9ad8\u4ea2\uff0c\u7126\u6025\u7684\u610f\u5473\uff0c\u4e5f\u90fd\u4ece\u8fd9\u9ad8\u4ea2\u7684 \u5578\u58f0\u4e2d\uff0c\u900f\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5ffd\u89c1\u5bf9\u5d16\u6c89\u6c89\u9601\u5f71\u4e2d\uff0c\u6311\u8d77\u4e00\u76cf\u7ea2\u706f\u6765\uff0c\u8fce\u98ce\u6643\u4e86\u4e24\u6643\uff0c\u8fd9\u8fb9\u94c1\u9762\u5b64\u884c\u5ba2\u8138\u4e0a\uff0c\u4e5f\u968f\u5373 \u9732\u51fa\u559c\u8272\uff0c\u624b\u81c2\u4e00\u62a1\uff0c\u5c06\u624b\u4e2d\u7684\u706b\u6298\u5b50\uff0c\u659c\u659c\u663c\u4e86\u4e2a\u534a\u5f27\uff0c\u53c8\u53cd\u5411\u4e00\u62a1\uff0c\u663c\u4e86\u4e2a\u534a\u5f27\u3002 \u3000\u3000\u90a3\u8fb9\u7ea2\u706f\u4e00\u6c89\uff0c\u9690\u9690\u542c\u5230\u4e00\u58f0\u6b22\u547c\uff0c\u63a5\u7740\u706f\u5149\u5927\u660e\uff0c\u90a3\u5e62\u5e62\u5c4b\u5f71\u7684\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u7adf\u90fd\u70b9\u8d77 \u706f\u6765\uff0c\u5bf9\u5d16\u671b\u53bb\uff0c\u771f\u5982\u795e\u4ed9\u697c\u9601\u3002 \u3000\u3000\u4e07\u5929\u840d\u5728\u6c5f\u6e56\u4e2d\u7d20\u6765\u9762\u51b7\u5fc3\u8fa3\uff0c\u6b64\u523b\u5374\u7adf\u7136\u559c\u52a8\u989c\u8272\uff0c\u7b11\u8bed\u4f0a\u98ce\u9053\uff1a \u3000\u3000\u201c\u8001\u5f1f\uff01\u5148\u6c89\u4f4f\u6c14\uff01\u7b49\u4f1a\u4e00\u4e0b\u8ba9\u4f60\u5927\u5403\u4e00\u60ca\u3002\u5509\uff01\u2014\u2014\u5341\u5e74\u4ee5\u6765\uff0c\u6211\u4e3a\u7740\u4e00\u4e9b\u610f\u6c14\u4e4b \u4e89\uff0c\u7adf\u6559\u4ed6\u4eec\u5728\u8fd9\u91cc\u5b64\u5b64\u5355\u5355\u5730\u8fc7\u4e86\u5341\u5e74\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u7adf\u90fd\u8fd8\u5728\u8fd9\u91cc\u7b49\u6211\u2014\u2014\u201d \u3000\u3000\u8a00\u4e0b\u7adf\u9887\u611f\u6000\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u81ea\u4e00\u7b11\u3002 \u3000\u3000\u4f46\u89c1\u5bf9\u5d16\u697c\u9601\u706f\u5149\u5927\u660e\u4e4b\u540e\uff0c\u501a\u7740\u697c\u5b87\u6240\u5efa\u7684\u4e00\u5ea7\u98de\u9601\uff0c\u7a81\u5730\u706f\u5149\u66f4\u662f\u5927\u4eae\u3002\u98de\u9601\u56db \u89d2\uff0c\u6311\u8d77\u56db\u76cf\u5bab\u706f\uff0c\u4e00\u4e2a\u7fe0\u886b\u4e3d\u4eba\uff0c\u6b63\u501a\u7740\u6731\u680f\uff0c\u9891\u9891\u5411\u8fd9\u8fb9\u62db\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4f0a\u98ce\u4e0d\u7981\u53c8\u4e3a\u4e4b\u5927\u5403\u4e00\u60ca\uff0c\u52a8\u5ff5\u4e4b\u4e2d\uff0c\u65b9\u81ea\u731c\u51fa\u4e00\u4e9b\u4e8b\uff0c\u90a3\u77e5\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u7a81 \u5730\u54c8\u54c8\u5927\u7b11\uff0c\u5927\u7b11\u58f0\u4e2d\uff0c\u4e00\u62cd\u4ed6\u7684\u80a9\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u5f1f\uff01\u4f60\u770b\u770b\uff01\u5bf9\u5d16\u9601\u4e2d\u7684\uff0c\u5c31\u662f\u5c0f\u5973\u3002\u60f3\u4e0d\u5230\u5427\uff01\u6211\u94c1\u9762\u5b64\u884c\u5ba2\u7d20\u6765\u72ec\u6765\u72ec\u5f80\uff0c\u6c5f \u6e56\u4e2d\u4eba\uff0c\u6709\u8c01\u77e5\u9053\u6211\u8fd8\u6709\u4e2a\u5973\u513f\u3002\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u4e86\uff0c\u5bf9\u5d16\u9601\u4e2d\uff0c\u53c8\u8d70\u4e86\u4e00\u4e2a\u9ad8\u633d\u6309\u53d1\u7684\u5987\u4eba\uff0c\u6276\u7740\u4e00\u4e2a\u5782\u9aeb\u5973\u73af\u7684\u80a9\u5934\uff0c\u7acb\u5728\u680f \u8fb9\uff0c\u5411\u7740\u8fd9\u8fb9\u6325\u4e00\u65b9\u7c89\u5e15\u3002 \u3000\u3000\u4f0a\u98ce\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u72ec\u884c\u6c5f\u6e56\uff0c\u6ee1\u624b\u8840\u8165\uff0c\u5374\u5728\u8fd9\u79cd\u7edd\u9876\u9690\u5bc6\u7684\u6240\u5728\uff0c \u5b89\u6392\u4e0b\u4ed6\u7684\u59bb\u5b50\u5973\u513f\u3002 \u3000\u3000\u8fd9\u4e07\u5929\u840d\u6b64\u523b\u4e5f\u4e0d\u505c\u5730\u6325\u7740\u624b\u4e2d\u7684\u706b\u647a\u5b50\u3002\u7a81\u89c1\u5bf9\u5d16\u9601\u4e2d\u7684\u7fe0\u88c5\u4e3d\u4eba\u5a07\u8eaf\u4e00\u626d\uff0c\u5de6\u624b\u63d0 \u7740\u4e00\u76cf\u5bab\u706f\uff0c\u4ece\u9601\u4e2d\u98de\u63a0\u4e0b\u6765\uff0c\u8eab\u6cd5\u4e4b\u8f7b\u7075\u66fc\u5999\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u671b\u4e4b\u6709\u5982\u4e5d\u5929\u4e0b\u964d\u7684\u4ed9\u5b50\uff01 \u3000\u3000\u4e07\u5929\u840d\u5589\u95f4\u5e72\u9654\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u8679\u513f\uff01\u600e\u5730\u8fd9\u4e48\u5927\u80c6\uff01\u201d \u3000\u3000\u76ee\u5149\u5982\u70ac\uff0c\u671b\u7740\u5728\u5bf9\u5d16\u7684\u4e3d\u4eba\u8eab\u4e0a\uff0c\u5173\u5207\u4e4b\u5bb9\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u8fd9\u201c\u94c1\u9762\u201d\u5927\u8c6a\uff0c\u6b64\u523b\u89c1\u7740\u81ea\u5df1\u7684\u5973\u513f\uff0c\u4e5f\u50cf\u4e16\u95f4\u6240\u6709\u7684\u7236\u4eb2\u4e00\u6837\uff0c\u9732\u51fa\u90a3\u79cd\u975e\u5e38\u5e73 \u51e1\uff0c\u4f46\u5374\u73cd\u8d35\u7684\u60c5\u611f\u6765\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u6697\u6697\u53f9\u606f\uff0c\u4ed6\u548c\u8fd9\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e00\u8def\u884c\u6765\uff0c\u81f3\u4eca\u624d\u89c1\u4ed6\u9732\u51fa\u4e86\u4eba\u5473\u3002 \u3000\u3000\u5176\u5b9e\u5929\u4e0b\u6b66\u6797\u4e2d\uff0c\u6240\u6709\u7d20\u79f0\u5fc3\u72e0\u624b\u8fa3\u7684\u9b54\u5934\uff0c\u53c8\u6709\u51e0\u4e2a\u5728\u81ea\u5df1\u7684\u4eb2\u4eba\u9762\u524d\uff0c\u4e0d\u662f\u548c\u51e1\u4eba \u4e00\u6837\u5730\u6709\u7740\u4eba\u6027\u5462\uff1f \u3000\u3000\u53ea\u662f\u4ed6\u4eec\u7684\u8fd9\u79cd\u201c\u4eba\u6027\u201d\uff0c\u9664\u4e86\u4ed6\u4eec\u7684\u4eb2\u4eba\uff0c\u5c31\u4e0d\u6613\u770b\u5230\u7f62\u4e86\uff01 \u3000\u3000\u697c\u9601\u4e4b\u4e0b\uff0c\u5c71\u6881\u5bbd\u4ec5\u5c3a\u4f59\u3002\u90a3\u7fe0\u88c5\u5c11\u5973\u4fbf\u7ad9\u5728\u8fd9\u4ec5\u5bb9\u7acb\u8db3\u7684\u5c71\u6881\u4e0a\uff0c\u6df1\u591c\u5bd2\u98ce\uff0c\u5439\u5f97\u5979 \u7fe0\u7eff\u7684\u8863\u886b\uff0c\u98d8\u98d8\u800c\u821e\u3002\u800c\u5979\u90a3\u5a40\u5a1c\u5a07\u5c0f\u7684\u8eab\u8eaf\uff0c\u4fbf\u4e5f\u751f\u50cf\u662f\u8981\u968f\u7740\u8fd9\u98de\u821e\u4e4b\u52bf\uff0c\u4e58\u98ce\u800c \u53bb\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e94\u5341\u516b\u7ae0\u3000\u886b\u5e26\u8fce\u5bbe>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e94\u5341\u516b\u7ae0\u3000\u886b\u5e26\u8fce\u5bbe \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u51dd\u89c6\u7740\u5bf9\u5d16\uff0c\u7a81\u89c1\u5bf9\u5d16\u98de\u9601\u4e4b\u4e0a\uff0c\u5339\u7ec3\u4f3c\u7684\u5782\u4e0b\u4e00\u6761\u5f69\u5e26\u6765\uff0c\u4e24\u4e2a\u5782\u9aeb\u5973\u73af\uff0c \u53cc\u624b\u6267\u7740\u5f69\u5e26\u7684\u4e0a\u7aef\uff0c\u8fce\u98ce\u4e00\u6296\uff0c\u8fd9\u6761\u957f\u8fbe\u6570\u4e08\u7684\u5f69\u5e26\uff0c\u4fbf\u201c\u547c\u201d\u5730\u62a1\u821e\u4e86\u8d77\u6765\uff0c\u663e\u89c1\u8fd9\u4e24 \u4e2a\u5782\u9aeb\u5973\u73af\uff0c\u624b\u4e0b\u4e5f\u6709\u7740\u8fe5\u5f02\u5e38\u4eba\u7684\u529f\u529b\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u957f\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u6709\u5982\u98de\u9e64\u63a0\u8d77\uff0c\u51cc\u7a7a\u98de\u5411\u8fd9\u5df2\u5411\u8fd9\u8fb9\u629b\u6765\u7684\u5f69\u5e26\u4e0a\uff0c\u94c1\u638c\u5fae \u4f38\uff0c\u5f69\u5e26\u518d\u6b21\u56de\u5377\uff0c\u8fd9\u6b66\u529f\u9ad8\u7edd\u7684\u6b66\u6797\u5de8\u76d7\uff0c\u7adf\u5c31\u85c9\u7740\u8fd9\u5f69\u5e26\u7684\u56de\u65cb\u4e4b\u52bf\uff0c\u98de\u63a0\u6570\u4e08\uff0c\u63a0\u5230 \u5bf9\u5d16\u4e0a\u3002 \u3000\u3000\u4f0a\u98ce\u9065\u9065\u671b\u53bb\uff0c\u90a3\u7fe0\u88c5\u4e3d\u4eba\u5df2\u6251\u5230\u5979\u7238\u7238\u8eab\u4e0a\u3002\u51ed\u680f\u4f4e\u89c6\u7684\u4e2d\u5e74\u5987\u5973\uff0c\u4fa7\u9996\u4f4e\u8bed\u4e24\u53e5\uff0c \u90a3\u4e24\u4e2a\u5782\u9aeb\u5973\u73af\uff0c\u4fbf\u53c8\u5fae\u62ac\u7ea4\u624b\uff0c\u90a3\u6761\u5f69\u5e26\uff0c\u4fbf\u53c8\u5339\u7ec3\u822c\u5730\u629b\u8d77\uff0c\u5f69\u8679\u822c\u5730\u98de\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f46\u4f0a\u98ce\u53ef\u6ca1\u6709\u7acb\u523b\u7eb5\u8eab\u8fce\u53bb\u3002\u6709\u8bb8\u591a\u4e8b\uff0c\u5e76\u4e0d\u662f\u4eba\u4eec\u5728\u52a8\u5ff5\u4e4b\u4e2d\u5c31\u53ef\u51b3\u5b9a\u7684\uff0c\u5c24\u5176\u662f\u8fd9 \u79cd\u6709\u5173\u751f\u6b7b\u4e4b\u4e8b\u3002\u4f0a\u98ce\u7eb5\u662f\u8fbe\u4eba\uff0c\u4f46\u6b64\u523b\u5bf9\u5d16\u76f8\u8ddd\u975e\u8fd1\uff0c\u4e0b\u9762\u7edd\u58d1\u6df1\u6c89\uff0c\u4ed6\u5148\u5c06\u81ea\u5df1\u7684\u751f \u547d\uff0c\u5192\u7136\u4ea4\u6258\u4e8e\u4e24\u4e2a\u5782\u9aeb\u5973\u73af\u7684\u624b\u91cc\uff0c\u90a3\u5c82\u975e\u83bd\u649e\uff1f \u3000\u3000\u8e0c\u8e87\u4e4b\u95f4\uff0c\u5374\u89c1\u94c1\u9762\u5b64\u884c\u5ba2\u5df2\u9694\u5d16\u5927\u547c\uff1a \u3000\u3000\u201c\u8001\u5f1f\uff01\u4f60\u5feb\u8fc7\u6765\uff01\u201d \u3000\u3000\u547c\u5730\u4e00\u638c\uff0c\u5c06\u90a3\u52bf\u9053\u5df2\u8870\u7684\u5f69\u5e26\uff0c\u91cd\u53c8\u9707\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u50cf\u662f\u4e00\u6761\u592d\u77eb\u800c\u6765\u7684\u795e\u9f99\u4f3c\u7684\u3002 \u3000\u3000\u4f0a\u98ce\u4f46\u89c9\u5bab\u706f\u5149\u5f71\u4e4b\u4e0b\uff0c\u8fd9\u6761\u5f69\u5e26\u8000\u76ee\u751f\u5149\uff0c\u7adf\u4e0d\u662f\u4e1d\u5e1b\u4e4b\u7c7b\u4e1c\u897f\u505a\u7684\u3002 \u3000\u3000\u4e07\u5929\u840d\u547c\u58f0\u65b9\u4f4f\uff0c\u5bf9\u5d16\u5374\u53c8\u4f20\u6765\u4e00\u58f0\u5a07\u547c\uff1a \u3000\u3000\u201c\u4f60\u8981\u4e0d\u8981\u6211\u8fc7\u6765\u63a5\u4f60\uff0c\u8fd9\u91cc\u2026\u2026\u201d \u3000\u3000\u547c\u58f0\u672a\u4e86\uff0c\u4f0a\u98ce\u5df2\u81ea\u957f\u7b11\u63a0\u8d77\uff0c\u5bbd\u5927\u7684\u8863\u886b\uff0c\u5e76\u672a\u6396\u8d77\uff0c\u662f\u4ee5\u886b\u89d2\u98de\u821e\uff0c\u4ed6\u5982\u4e58\u98ce\u4e00 \u822c\u3002 \u3000\u3000\u4ed6\u53cc\u624b\u4e00\u642d\u4e0a\u8fd9\u6761\u5f69\u5e26\uff0c\u679c\u7136\u5165\u624b\u6e05\u51c9\uff0c\u4f3c\u91d1\u4f3c\u94c1\u3002\u9601\u4e0a\u7684\u4e24\u4e2a\u5973\u73af\uff0c\u53e3\u4e2d\u4fcf\u559d\u4e00\u58f0\uff0c \u56db\u53ea\u767d\u751f\u751f\u7684\u624b\u8155\uff0c\u5411\u4e0a\u4e00\u62ac\uff0c\u8fd9\u6761\u5f69\u5e26\u4fbf\u53c8\u731b\u5730\u56de\u5377\u800c\u53bb\u3002\u4f0a\u98ce\u771f\u6c14\u731b\u63d0\uff0c\u4e0d\u7b49\u8fd9\u6761\u5f69\u5e26 \u7684\u56de\u5377\u4e4b\u52bf\u53d1\u6ee1\uff0c\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u4fbf\u81ea\u51cc\u7a7a\u76f4\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u672c\u81ea\u534a\u5f13\uff0c\u6b64\u523b\u957f\u8eab\u5f20\u81c2\uff0c\u8eab\u5f62\u4fbf\u53c8\u500f\u7136\u4e0a\u5347\u4e94\u5c3a\uff0c\u7136\u540e\u5934\u4e0b\u811a\u4e0b\uff0c\u7bad\u4e5f\u4f3c\u7684\u7a9c \u5411\u90a3\u706f\u5149\u5982\u663c\u7684\u98de\u9601\u4e0a\u3002 \u3000\u3000\u7fe0\u88c5\u5c11\u5973\u6d45\u7b11\u5a07\u547c\uff01 \u3000\u3000\u201c\u597d\u8eab\u624b\uff01\u201d \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u4e5f\u81ea\u957f\u7b11\u63a0\u8d77\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u7684\u8eab\u5f62\uff0c\u4fbf\u51e0\u4e4e\u5728\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u843d\u5728\u90a3\u98de\u9601\u4e0a\u9762\u3002 \u3000\u3000\u501a\u680f\u800c\u7acb\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u53f3\u624b\u4ecd\u7136\u501a\u5728\u90a3\u5782\u9aeb\u5973\u73af\u7684\u80a9\u4e0a\uff0c\u4f4e\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5929\u840d\uff01\u4f60\u624d\u56de\u6765\u5440\uff1f\u201d \u3000\u3000\u65e0\u9650\u60c6\u6005\uff0c\u65e0\u9650\u76f8\u601d\uff0c\u4e5f\u4e0d\u9700\u592a\u591a\u7684\u8a00\u8bcd\u8868\u9732\uff0c\u5c31\u662f\u8fd9\u5be5\u5be5\u6570\u5b57\uff0c\u5c31\u8fde\u4f0a\u98ce\u5fc3\u4e2d\u4e5f\u4e0d\u7981 \u4e3a\u4e4b\u9eef\u7136\uff01 \u3000\u3000\u4ed6\u4fa7\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u4e07\u5929\u840d\u7684\u4e00\u5f20\u201c\u94c1\u9762\u201d\u4e0a\uff0c\u60c5\u611f\u6fc0\u52a8\u4e0d\u5df2\u3002\u5f80\u524d\u5927\u8fc8\u4e00\u6b65\uff0c\u8f7b\u8f7b\u63e1\u7740 \u90a3\u4e2d\u5e74\u5987\u4eba\u7684\u53f3\u624b\uff0c\u6014\u6014\u5730\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5343\u8a00\u4e07\u8bed\uff0c\u4fbf\u5728\u4ed6\u4eec\u8fd9\u51dd\u76ee\u4e00\u89c6\u4e2d\uff0c\u8868\u9732\u65e0\u9057\uff01 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u5973\u7f57\u8896\u5fae\u626c\uff0c\u8f7b\u8f7b\u62c2\u4e86\u62c2\u773c\u89d2\uff0c\u5f3a\u7b11\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u6b21\u56de\u6765\uff0c\u8fd8\u5e26\u6765\u4e00\u4f4d\u5ba2\u4eba\u3002\u5509\uff01\u5341\u5e74\u6765\uff0c\u6211\u4eec\u51e0\u4e4e\u5df2\u7ecf\u5fd8\u4e86\u8fd9\u4e16\u4e0a\u9664\u4e86\u6211 \u4eec\u51e0\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u522b\u4eba\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u611f\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u76ee\u5149\u95ea\u5904\uff0c\u53ea\u89c1\u8fd9\u4e2d\u5e74\u5987\u4eba\u9ad8\u633d\u9b13\u53d1\uff0c\u5f62\u5bb9\u6194\u60b4\uff0c\u672c\u662f\u6e05\u6f88\u7684\u53cc\u7738\uff0c\u6b64\u523b\u773c\u89d2\u5df2\u6ee1\u5e03\u9c7c \u5c3e\uff0c\u5c81\u6708\u50ac\u4eba\uff0c\u5e74\u534e\u4e0d\u518d\uff0c\u8fd9\u5987\u4eba\u7684\u5927\u597d\u5e74\u534e\uff0c\u5c31\u5168\u5728\u8fd9\u79cd\u5bc2\u5bde\u7684\u5c81\u6708\u4e2d\u6d88\u8680\u4e86\uff01 \u3000\u3000\u4e07\u5929\u840d\u5fae\u53f9\u4e00\u58f0\uff0c\u4ea6\u81ea\u5f3a\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u662f\u62d9\u8346\uff0c\u8fd9\u4f4d\u662f\u8427\u65e0\u8427\u8001\u5f1f\u3002\u5509\uff01\u2014\u2014\u6167\u742a\uff01\u4f60\u6211\u4eca\u756a\u80fd\u5f97\u518d\u89c1\uff0c\u82e5\u4e0d\u662f\u8fd9\u4f4d\u8427\u8001 \u5f1f\uff0c\u53ea\u6015\u6211\u65e9\u5df2\u4e27\u547d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u7684\u59bb\u5b50\uff0c\u4fbf\u6df1\u6df1\u5411\u4f0a\u98ce\u798f\u4e86\u4e0b\u53bb\uff0c\u4f0a\u98ce\u8fde\u5fd9\u8c26\u8c22\uff0c\u8fd8\u793c\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697 \u5fd6\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\uff1a\u94c1\u9762\u5b64\u884c\u5ba2\u8fd9\u79cd\u9b54\u5934\uff0c\u5374\u6709\u59bb\u5b50\u5982\u6b64\uff01\u8fd9\u8981\u5bf9\u522b\u4eba\u53bb\u8bf4\uff0c\u53c8\u6709\u8c01\u80fd\u76f8\u4fe1 \u5462\uff1f\u201d \u3000\u3000\u4ed6\u76ee\u5149\u518d\u4e00\u8f6c\uff0c\u8f6c\u5230\u90a3\u51e0\u4e2a\u201c\u5782\u9aeb\u5973\u73af\u201d\u8eab\u4e0a\u3002\u53ea\u89c1\u8fd9\u51e0\u4e2a\u8fdc\u8fdc\u671b\u6765\uff0c\u4ff1\u4f3c\u7a1a\u5c81\u7684\u5973 \u5b50\uff0c\u7adf\u5df2\u4ff1\u90fd\u9762\u6709\u9c7c\u7eb9\uff0c\u5e74\u7eaa\u90fd\u6709\u4e09\u5341\u5c81\u4e86\uff0c\u7709\u68a2\u773c\u89d2\uff0c\u5fe7\u8272\u91cd\u91cd\u3002\u539f\u6765\u8fd9\u4e9b\u5c11\u5973\uff0c\u81ea\u5782\u9aeb \u7a1a\u5c81\u800c\u6765\uff0c\u5230\u73b0\u5728\u5df2\u6709\u5341\u591a\u5e74\u4e86\uff0c\u867d\u7136\u88c5\u675f\u672a\u6539\uff0c\u4f46\u5fc3\u5883\u4e4b\u51c4\u51c9\u82cd\u8001\uff0c\u53c8\u6709\u8c01\u80fd\u4f53\u5473\u5f97\u5230\u7684 \u54e9\uff01 \u3000\u3000\u4e00\u6761\u873f\u8712\u7684\u77f3\u9636\uff0c\u76f4\u8fbe\u5730\u9762\u3002\u94c1\u9762\u5b64\u884c\u5ba2\u592b\u5987\uff0c\u62f1\u624b\u8fce\u5bbe\uff1b\u90a3\u51e0\u4e2a\u5df2\u662f\u534a\u8001\u5f90\u5a18\u7684\u201c\u5782 \u9aeb\u5973\u73af\u201d\uff0c\u624b\u91cc\u6311\u7740\u5bab\u706f\uff0c\u6b3e\u6b3e\u884c\u4e0b\u3002 \u3000\u3000\u4f0a\u98ce\u8d70\u5728\u524d\u9762\uff0c\u8033\u4e2d\u53ea\u542c\u89c1\u90a3\u7fe0\u88c5\u5c11\u5973\uff0c\u4e0d\u505c\u5730\u5a07\u7b11\u800c\u8bed\uff1a \u3000\u3000\u201c\u6211\u548c\u5a18\u5148\u524d\u542c\u5230\u60a8\u7684\u5578\u58f0\uff0c\u8fd8\u4e0d\u76f8\u4fe1\u662f\u7239\u60a8\u771f\u7684\u56de\u6765\u4e86\u5462\u3002\u7239\uff01\u60a8\u4e0d\u77e5\u9053\uff0c\u4e8c\u5e74\u591a \u524d\uff0c\u6709\u4e00\u6b21\u732b\u5934\u9e70\u5728\u5916\u9762\u591c\u557c\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u60a8\u56de\u6765\u4e86\u5462\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u4e00\u7b11\u3002\u4f46\u4e5f\u4e0d\u7981\u89c9\u5230\u8fd9\u7b11\u58f0\uff0c\u662f\u542b\u7740\u60b2\u54c0\u800c\u51c4\u51c9\u7684\u610f\u5473\u7684\uff1b\u5c31\u8fde\u81ea\u5df1\u8fd9\u5c40\u5916 \u4eba\uff0c\u4e5f\u4e3a\u4e4b\u9eef\u7136\u3002 \u3000\u3000\u4f46\u4ed6\u518d\u4e00\u60f3\u5230\u81ea\u5df1\uff0c\u8fd8\u662f\u4e0d\u77e5\u9053\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u5c06\u81ea\u5df1\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u5230\u5e95\u662f\u4e3a\u7740\u4ec0\u4e48\uff1f \u4ed6\u4e0d\u7981\u6697\u6697\u611f\u53f9\u7740\u9020\u5316\u7684\u5f04\u4eba\uff0c\u4e3a\u4ec0\u4e48\u7adf\u5c06\u81ea\u5df1\u6613\u5bb9\u540e\u7684\u9762\u8c8c\uff0c\u504f\u504f\u5f04\u5f97\u548c\u90a3\u8427\u65e0\u4e00\u6837\uff01\u4e16 \u95f4\u5de7\u5408\u867d\u591a\uff0c\u53c8\u518d\u6709\u4ec0\u4e48\u80fd\u548c\u6b64\u4e8b\u76f8\u6bd4\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u7684\u601d\u6f6e\uff0c\u53c8\u4e0d\u7981\u8f6c\u5230\u90a3\u4e00\u53cc\u66fe\u66ff\u81ea\u5df1\u5e26\u6765\u8fd9\u79cd\u65e0\u6bd4\u5947\u5999\u906d\u9047\u7684\u7ea4\u624b\u4e0a\u3002\u5f53\u65f6\u53c8\u6709 \u8c01\u80fd\u60f3\u5230\uff0c\u90a3\u53cc\u7ea4\u624b\u7684\u5fae\u4e00\u64ad\u5f04\uff0c\u5c31\u5728\u81ea\u5df1\u7684\u751f\u547d\u4e2d\uff0c\u79cd\u4e0b\u4e86\u5982\u6b64\u5de8\u5927\u6539\u53d8\u7684\u79cd\u5b50\u5462\uff1f \u3000\u3000\u4ed6\u550f\u5618\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u89c9\u80a9\u4e0a\u6709\u4eba\u8f7b\u8f7b\u4e00\u62cd\uff0c\u4e00\u4e2a\u5a07\u67d4\u7684\u53e3\u97f3\u9053\uff1a \u3000\u3000\u201c\u5582\uff01\u4f60\u8d70\u9519\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u56de\u9996\uff0c\u4f46\u89c1\u90a3\u7fe0\u88c5\u5c11\u5973\u7684\u4e00\u5f20\u5a07\u9762\uff0c\u6b63\u81ea\u5fae\u5fae\u542b\u7b11\uff1b\u4e00\u53cc\u79cb\u6c34\u4e3a\u795e\u7684\u4fcf\u76ee\uff0c\u4e5f\u6b63\u542b \u7b11\u51dd\u7747\u7740\u81ea\u5df1\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8427\u8001\u5f1f\u8fdc\u9053\u800c\u6765\uff0c\u8679\u513f\uff01\u4f60\u5f97\u597d\u597d\u7167\u987e\u7167\u987e\u4eba\u5bb6\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u8f7b\u8f7b\u4f38\u51fa\u7ea4\u624b\uff0c\u63a9\u53e3\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u8ddf\u7740\u6211\u6765\uff01\u201d \u3000\u3000\u5a07\u8eaf\u4e00\u626d\uff0c\u5a40\u5a1c\u884c\u53bb\u3002\u4f0a\u98ce\u671b\u7740\u5979\u7684\u80cc\u5f71\uff0c\u5fc3\u91cc\u5374\u4e0d\u7981\u6cdb\u8d77\u53e6\u4e00\u4eba\u7684\u5f71\u5b50\u3002\u4f46\u5929\u6daf\u832b \u832b\uff0c\u4f0a\u4eba\u65e0\u8baf\uff0c\u5979\u6b64\u523b\u7a76\u7adf\u5728\u90a3\u91cc\u5462\uff1f \u3000\u3000\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u679c\u7136\u4e0d\u6127\u4e3a\u4e00\u4ee3\u67ad\u96c4\uff0c\u4ed6\u4e0d\u4f46\u5728\u8fd9\u5e38\u4eba\u8fde\u767b\u4e34\u90fd\u6781\u4e3a\u56f0\u96be\u7684\u5730\u65b9\uff0c\u5efa\u4e0b\u8fd9 \u79cd\u697c\u9601\uff1b\u800c\u4e14\u697c\u5185\u88c5\u9970\u4e4b\u534e\u4e3d\uff0c\u4ea6\u8db3\u60ca\u4eba\u3002 \u3000\u3000\u90a3\u7fe0\u88c5\u5c11\u5973\u5a40\u5a1c\u884c\u5230\u697c\u5b87\u4e0b\uff0c\u7ea4\u624b\u5fae\u63a8\uff0c\u5ffd\u5730\u5440\u7684\u4e00\u58f0\uff0c\u63a8\u5f00\u4e00\u91cd\u95e8\u6237\uff0c\u7acb\u523b\u6709\u6d45\u7eff\u7684 \u706f\u5149\uff0c\u7531\u91cc\u9762\u6620\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e07\u5929\u840d\u5fae\u7b11\u8083\u5bb9\uff0c\u4f0a\u98ce\u7f13\u6b65\u800c\u5165\uff0c\u4f46\u89c1\u5c4b\u5185\u6ee1\u773c\u4ff1\u662f\u5dcd\u5dcd\u7684\u7ec0\u78a7\u8272\uff0c\u9648\u8bbe\u867d\u7136\u4e0d\u591a\uff0c\u4f46 \u534e\u4e3d\u8000\u773c\uff0c\u96be\u4ee5\u60f3\u50cf\u3002\u4f0a\u98ce\u7a81\u7136\u53d1\u73b0\u7fe0\u88c5\u5c11\u5973\u6b63\u5728\u51dd\u89c6\u81ea\u5df2\uff0c\u4e0d\u7531\u5fc3\u5934\u4e00\u98a4\uff0c\u8fd8\u597d\u9762\u4e0a\u6234\u7740 \u9762\u5177\u5c06\u5fc3\u60c5\u63a9\u9970\u8d77\u6765\u3002\u81ea\u5df1\u5373\u4f7f\u9762\u9732\u5fae\u7b11\uff0c\u7136\u800c\u5728\u522b\u4eba\u770b\u8d77\u6765\uff0c\u5374\u4ecd\u7136\u662f\u5168\u7136\u65e0\u52a8\u4e8e\u8877\u7684\u3002 \u81f3\u4e8e\u5176\u4ed6\u7684\u4efb\u4f55\u4e00\u79cd\u8868\u60c5\uff0c\u522b\u4eba\u81ea\u7136\u66f4\u65e0\u6cd5\u770b\u5f97\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u653e\u773c\u5929\u4e0b\uff0c\u9762\u4e0a\u6234\u7740\u9762\u5177\u7684\uff0c\u53c8\u4f55\u6b62\u4ed6\u4e00\u4e2a\u54e9\uff1f \u3000\u3000\u90a3\u4e9b\u4eba\u9762\u4e0a\u6240\u6234\u7684\u9762\u76ee\uff0c\u8d28\u6599\u867d\u7136\u548c\u4ed6\u800c\u4e0a\u7684\u8fd9\u5f20\u7edd\u4e0d\u76f8\u540c\u2014\u2014\u90a3\u4e9b\u662f\u7528\u4e16\u6545\uff0c\u865a\u4f2a\uff0c \u751a\u6216\u662f\u77eb\u60c5\u8fd9\u4e00\u7c7b\u4e1c\u897f\u505a\u6210\u7684\u3002 \u3000\u3000\u7136\u800c\u5b83\u4eec\u7684\u6027\u8d28\uff0c\u5374\u662f\u5b8c\u5168\u4e00\u6837\u7684\u2014\u2014\u6b3a\u9a97\u522b\u4eba\uff0c\u63a9\u9970\u81ea\u5df1\u3002 \u3000\u3000\u6b63\u5f53\u4f0a\u98ce\u7684\u8111\u6d77\u91cc\uff0c\u6df7\u6dc6\u7740\u8fd9\u4e9b\u9887\u96be\u7406\u89e3\u7684\u95ee\u9898\u65f6\u2014\u2014 \u3000\u3000\u4ed6\u53d1\u89c9\u4e00\u676f\u70ed\u6c14\u817e\u817e\u7684\u8336\uff0c\u88ab\u7aef\u5230\u4ed6\u9762\u524d\u3002\u9752\u7389\u7684\u8336\u676f\uff0c\u7fe0\u7eff\u7684\u8336\u6c34\uff0c\u518d\u52a0\u4e0a\u90a3\u53ea\u7aef\u7740 \u8336\u676f\u7684\u8202\u8471\u822c\u7684\u67d4\u8351\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u51fa\u795e\u5730\u671b\u5728\u8fd9\u5e45\u7edd\u7f8e\u7684\u56fe\u753b\u4e0a\u4e86\uff0c\u5374\u542c\u4e00\u4e2a\u5a07\u67d4\u7684\u58f0\u97f3\u7b11\u9053\uff1a \u3000\u3000\u201c\u5582\uff01\u559d\u8336\u561b\uff01\u6211\u53eb\u4e07\u8679\uff0c\u662f\u6211\u7239\u7239\u7684\u5973\u513f\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u8fd9\u5a07\u7f8e\u7684\u5c11\u5973\uff0c\u4e0d\u7981\u201c\u5657\u54e7\u201d\u4e00\u7b11\u3002\u4f46\u968f\u5373\u53c8\u4e00\u672c\u6b63\u7ecf\u5730\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u4f60\u5bf9\u7239\u7239\u90a3\u4e48\u597d\uff0c\u6211\u5f88\u611f\u6fc0\u4f60\uff01\u4ee5\u540e\u4f60\u6709\u4ec0\u4e48\u4e8b\uff0c\u6211\u4e5f\u4f1a\u5e2e\u4f60\u7684\u5fd9\u7684\u3002\u201d \u3000\u3000\u4e24\u53ea\u660e\u4eae\u7684\u773c\u775b\uff0c\u95ea\u52a8\u5f97\u6709\u5982\u6625\u591c\u7684\u665a\u661f\uff1b\u9762\u9765\u4e0a\u7684\u4e00\u53cc\u9152\u6da1\uff0c\u53c8\u7981\u4e0d\u4f4f\u50cf\u662f\u6625\u6c34\u4e2d\u7684 \u6d9f\u6f2a\u4f3c\u5730\uff0c\u6c64\u6f3e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u63a5\u7740\u8336\u676f\uff0c\u5450\u5450\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u8033\u4e2d\u4f46\u542c\u89c1\u4e07\u5929\u840d\u5f97\u610f\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u77e5\u9053\uff1a\u6b64\u6765\u897f\u6881\u5c71\uff0c\u672c\u662f\u597d\u5947\uff0c\u4f46\u8fd9\u4efd\u597d\u5947\uff0c\u5374\u53c8\u4e3a\u81ea\u5df1\u5e26\u6765\u9ebb\u70e6\u4e86\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u4e94\u5341\u4e5d\u7ae0\u3000\u54ab\u5c3a\u5929\u6daf>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e94\u5341\u4e5d\u7ae0\u3000\u54ab\u5c3a\u5929\u6daf \u3000\u3000\u4f0a\u98ce\u5728\u8fd9\u7fe0\u8272\u7684\u534e\u5ba4\u4e2d\uff0c\u555c\u7740\u7fe0\u7eff\u8272\u7684\u70ed\u8336\u7684\u65f6\u5019\uff0c\u4e5f\u6b63\u662f\u8427\u5357\u82f9\u5728\u5c71\u7a9f\u91cc\u60e8\u906d\u8e42\u8e8f\u7684 \u65f6\u5019\uff01 \u3000\u3000\u6b64\u523b\u4f0a\u98ce\u53c8\u600e\u4f1a\u77e5\u9053\uff0c\u4e00\u4e2a\u7eaf\u771f\u591a\u60c5\u7684\u5c11\u5973\uff0c\u5df2\u4e3a\u4e86\u81ea\u5df1\uff0c\u4e27\u5931\u4e86\u5979\u4e00\u751f\u4e2d\u6700\u503c\u5f97\u73cd\u8d35 \u7684\u4e1c\u897f\u5462\uff01 \u3000\u3000\u8fd9\u5929\u665a\u4e0a\uff0c\u4f0a\u98ce\u6210\u4e86\u4e07\u5929\u840d\u592b\u5987\u6bb7\u6bb7\u5782\u8be2\u7684\u5bf9\u8c61\uff0c\u4ed6\u4e5f\u53ea\u6709\u5450\u5450\u5730\u5e94\u5fd6\u7740\uff0c\u76f4\u5230\u6e05\u6668\uff0c \u4ed6\u624d\u88ab\u5b89\u6392\u5728\u4e00\u95f4\u540c\u6837\u7fe0\u7eff\uff0c\u540c\u6837\u534e\u4e3d\u7684\u5367\u5ba4\u91cc\uff0c\u83b7\u5f97\u4e86\u4ed6\u6781\u4e3a\u76fc\u671b\u7684\u6b47\u606f\u673a\u4f1a\u3002 \u3000\u3000\u53ef\u662f\uff0c\u7b49\u5230\u4ed6\u53d1\u73b0\u8fd9\u95f4\u5367\u5ba4\uff0c\u5c31\u662f\u90a3\u7fe0\u88c5\u4e3d\u4eba\u4e07\u8679\u7684\u95fa\u623f\u65f6\uff0c\u4ed6\u7684\u601d\u6f6e\uff0c\u4e0d\u7981\u53c8\u5f00\u59cb\u7d0a \u4e71\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u8bb8\u591a\u91cd\u5927\u7684\u6539\u53d8\uff0c\u51e0\u4e4e\u90fd\u662f\u4e3a\u4e86\u5973\u5b50\u3002 \u3000\u3000\u5728\u4ed6\u6ca1\u6709\u8ba4\u8bc6\u859b\u82e5\u74a7\u4ee5\u524d\uff0c\u4ed6\u539f\u662f\u4e00\u4e2a\u5728\u60c5\u611f\u4e0a\u5b8c\u5168\u7a7a\u767d\u7684\u7537\u5b50\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u4ed6\u5728\u90a3\u6c5f\u5357\u5982\u663c\u7684\u5c0f\u6865\u4e0a\uff0c\u9082\u9005\u4e86\u859b\u82e5\u74a7\u4e4b\u540e\uff0c\u4ed6\u7684\u751f\u547d\uff0c\u4fbf\u56e0\u4e4b\u800c\u5b8c\u5168\u6539\u53d8 \u4e86\uff0c\u53d8\u5f97\u5145\u5b9e\u800c\u591a\u91c7\u8d77\u6765\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u4e00\u6bb5\u5145\u5b9e\u800c\u591a\u91c7\u7684\u751f\u547d\uff0c\u5ef6\u7eed\u5f97\u5e76\u4e0d\u957f\u4e45\uff0c\u4e8e\u662f\u4ed6\u5931\u671b\uff0e\u7a7a\u865a\uff0c\u9893\u5e9f\uff0c\u75db\u82e6\u4e86\uff01 \u3000\u3000\u4ed6\u4e5f\u5f00\u59cb\u77e5\u9053\uff0c\u60c5\u611f\u4e0a\u7684\u6298\u78e8\uff0c\u8fdc\u975e\u4efb\u4f55\u5176\u4ed6\u7684\u75db\u82e6\uff0c\u80fd\u591f\u6bd4\u62df\u7684\uff01 \u3000\u3000\u5f53\u4e00\u4e2a\u7537\u4eba\u53d1\u73b0\u81ea\u5df1\u6df1\u7231\u7740\u7684\u4eba\uff0c\u5e76\u4e0d\u503c\u5f97\u81ea\u5df1\u6df1\u7231\uff0c\u4e5f\u6839\u672c\u6ca1\u6709\u7231\u7740\u81ea\u5df1\u7684\u65f6\u5019\uff0c\u90a3 \u79cd\u5931\u671b\uff0c\u751a\u81f3\u6bd4\u7edd\u671b\u8fd8\u6765\u5f97\u66f4\u8981\u5f3a\u70c8\u4e9b\uff01 \u3000\u3000\u4ee5\u524d\u4e00\u5207\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u7f8e\u4e3d\u7684\u4e8b\uff0c\u4e8e\u5179\u4fbf\u5b8c\u5168\u53d8\u4e3a\u4e11\u6076\uff1b\u5c71\u76df\u6d77\u8a93\u7684\u771f\u60c5\uff0c\u4e5f\u53d8\u6210\u4e86\u865a\u60c5 \u5047\u610f\u7684\u6b3a\u9a97\u3002 \u3000\u3000\u8fd9\u5176\u95f4\u7684\u8ddd\u79bb\uff0c\u65e5\u5b50\u76f8\u8ddd\u5f97\u6f2b\u957f\u4e9b\uff0c\u4e5f\u8f83\u4e3a\u597d\u4e9b\uff1b\u82e5\u662f\u53d8\u5316\u6765\u5f97\u5982\u6b64\u7a81\u7136\uff0c\u90a3\u4e48\u8fd9\u79cd\u75db \u82e6\uff0c\u5c31\u4e0d\u662f\u4efb\u4f55\u4eba\u80fd\u591f\u5fcd\u53d7\u7684\u4e86\uff01 \u3000\u3000\u4f0a\u98ce\uff0c\u4ed6\u5374\u5fcd\u53d7\u8fc7\u8fd9\u79cd\u75db\u82e6\u3002\u5f53\u7136\uff0c\u4ed6\u4e5f\u66fe\u7ed9\u8fc7\u522b\u4eba\u75db\u82e6\uff0c\u7136\u800c\u90a3\u5374\u5168\u90fd\u4e0d\u662f\u53d1\u4e8e\u4ed6\u672c \u5fc3\u7684\u3002 \u3000\u3000\u5c24\u5176\u662f\u8427\u5357\u82f9\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u77e5\u9053\u8fd9\u9a84\u7eb5\u7684\u5c11\u5973\uff0c\u4e00\u65e6\u53d8\u4e3a\u6e29\u67d4\uff0c\u5c31\u5b8c\u5168\u662f\u56e0\u4e3a\u5979\u5df2\u6df1\u7231\u4e86 \u81ea\u5df1\uff1b\u4f46\u662f\u8fd9\u4efd\u6df1\u60c5\uff0c\u4ed6\u5374\u96be\u4ee5\u63a5\u53d7\u3002 \u3000\u3000\u800c\u6b64\u523b\uff0e\u4ed6\u4ece\u90a3\u7fe0\u88c5\u5c11\u5973\u4e07\u8679\u7684\u773c\u6ce2\u4e2d\uff0c\u53d1\u73b0\u4e86\u53c8\u6709\u4e00\u4e2a\u5c11\u5973\uff0c\u7231\u4e0a\u4e86\u81ea\u5df1\uff0c\u800c\u8fd9\u4efd\u60c5 \u611f\uff0c\u751a\u81f3\u8fd8\u53ef\u4ee5\u8bf4\u662f\u8fd9\u5c11\u5973\u7684\u7236\u4eb2\u4fc3\u6210\u7684\uff0c\u4e8e\u662f\u8fd9\u79cd\u60c5\u5f62\uff0c\u5f53\u7136\u4e5f\u5c31\u66f4\u4e3a\u660e\u663e\u4e9b\u3002 \u3000\u3000\u6700\u7cdf\u7684\u662f\uff1a\u4ed6\u77e5\u9053\u6b64\u523b\u81ea\u5df1\u5df2\u4e0d\u662f\u81ea\u5df1\uff01 \u3000\u3000\u81ea\u5df1\u6b64\u523b\u6240\u4ee3\u8868\u7684\uff0c\u5b8c\u5168\u662f\u53e6\u4e00\u4e2a\u4eba\u2014\u2014\u4e00\u4e2a\u81ea\u5df1\u5bdd\u98df\u96be\u5fd8\u7684\u4ec7\u4eba\u3002\u8fd9\u79cd\u590d\u6742\u7684\u60c5\u51b5\uff0c \u4fbf\u4f7f\u5f97\u4ed6\u5b8c\u5168\u56f0\u611f\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8be5\u600e\u6837\u6765\u5904\u7406\u8fd9\u4ef6\u4ee4\u4ed6\u56f0\u611f\u7684\u4e8b\uff0c\u501a\u5728\u9752\u94dc\u5e8a\u4e0a\u7684\u7fe0\u7eff\u4e1d\u887e\u4e2d\uff0c\u4ed6\u843d\u5165\u5fe7\u90c1\u7684 \u6c89\u601d\u91cc\u3002 \u3000\u3000\u7167\u8fdb\u7a97\u53e3\u7684\u9633\u5149\uff0c\u6e10\u6e10\u5730\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u592a\u9633\u8d8a\u5347\u8d8a\u9ad8\uff0c\u6b64\u523b\u5df2\u5c06\u662f\u6b63\u5348\u4e86\u3002 \u3000\u3000\u4e25\u51ac\u7684\u65e9\u4e0a\u5c45\u7136\u6709\u9633\u5149\u51fa\u73b0\uff0c\u672c\u662f\u4e00\u4ef6\u503c\u5f97\u6b23\u559c\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u5374\u4e00\u4e1d\u4e5f\u6ca1\u6709\u5206\u4eab\u5230\u8fd9\u79cd\u6b23\u559c\u3002 \u3000\u3000\u4ed6\u4fcf\u4fcf\u8d70\u4e0b\u5e8a\uff0c\u7a7f\u4e0a\u8863\u88f3\uff0c\u6084\u6084\u5730\u8d70\u51fa\u4e86\u8fd9\u95f4\u7fe0\u7eff\u800c\u534e\u4e3d\uff0c\u751a\u81f3\u8fd8\u6de1\u6de1\u5730\u6563\u53d1\u7740\u4e00\u79cd\u5904 \u5b50\u5e7d\u9999\u7684\u95fa\u623f\uff0c\u8d70\u5230\u90a3\u95f4\u5385\u623f\u91cc\u3002 \u3000\u3000\u5385\u623f\u91cc\u4e5f\u5bc2\u65e0\u4eba\u5f71\uff0c\u6628\u591c\u5269\u4f59\u7684\u9152\u9994\uff0c\u6b64\u523b\u90fd\u65e9\u5c31\u6536\u8d70\u4e86\u3002 \u3000\u3000\u7fe0\u7eff\u4e1d\u7ef8\u7684\u7a97\u5e54\uff0c\u5fae\u5fae\u98de\u626c\u7740\uff0c\u4eca\u65e5\u867d\u662f\u6674\u5929\uff0c\u5374\u4ecd\u8fd8\u662f\u6709\u98ce\u3002 \u3000\u3000\u4ed6\u6396\u597d\u6563\u843d\u7740\u7684\u8863\u895f\uff0c\u8d70\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u5916\u9762\u679c\u7136\u662f\u65e0\u6bd4\u6674\u6717\u7684\u5929\u6c14\uff0c\u5bf9\u9762\u7684\u98de\u9601\uff0c\u4e5f\u5b8c\u5168\u6d78\u6d74\u5728\u665a\u5bd2\u6e29\u6696\u7684\u9633\u5149\u91cc\u3002 \u3000\u3000\u4e00\u6761\u788e\u77f3\u780c\u6210\u7684\u77f3\u9636\uff0c\u873f\u8713\u901a\u5230\u98de\u9601\u4e0a\u3002 \u3000\u3000\u501a\u7740\u6731\u7ea2\u7684\u680f\u6746\uff0c\u671b\u7740\u4e0b\u9762\u7684\u6c89\u6c89\u7edd\u58d1\uff0c\u60f3\u53ca\u5f80\u4e8b\uff0c\u4ed6\u53c8\u843d\u4eba\u7d0a\u4e71\u7684\u601d\u6f6e\u91cc\u3002 \u3000\u3000\u8eab\u4fa7\u7a81\u7136\u54cd\u8d77\u4e00\u4e32\u5a07\u67d4\u7684\u7b11\u58f0\uff0c\u4e00\u9635\u65b9\u624d\u4ed6\u5728\u90a3\u95f4\u7fe0\u7eff\u7684\u95fa\u623f\u91cc\u55c5\u5230\u7684\u5e7d\u9999\uff0c\u53c8\u518d\u6b21\u51b2 \u5165\u4ed6\u7684\u9f3b\u7aef\u3002 \u3000\u3000\u4e07\u8679\u5e26\u7740\u6e29\u67d4\u7684\u7b11\u9765\uff0c\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u4f60\u665a\u4e0a\u7761\u5f97\u597d\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u7b11\uff0c\u8f7b\u8f7b\u5c06\u81ea\u5df1\u90a3\u5df2\u89e6\u53ca\u90a3\u6e29\u6696\u8eaf\u4f53\u7684\u8eab\u5b50\uff0c\u632a\u5f00\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u4ed6\u62ac\u8d77\u76ee\u5149\u6765\u2014\u2014 \u3000\u3000\u4e00\u4e2a\u4ee4\u4ed6\u51e0\u4e4e\u505c\u6b62\u5fc3\u8109\u8df3\u52a8\u7684\u666f\u8c61\uff0c\u4fbf\u84e6\u5730\u6d8c\u73b0\u5230\u4ed6\u773c\u524d\u3002 \u3000\u3000\u6b64\u523b\u9633\u5149\u666e\u7167\uff0c\u5bf9\u5d16\u666f\u7269\u5386\u5386\u53ef\u89c1\uff0c\u800c\u7ad9\u5728\u90a3\u65ad\u5d16\u4e4b\u8fb9\uff0c\u9762\u8272\u82cd\u767d\uff0c\u4e91\u9b13\u84ec\u4e71\uff0c\u4e00\u53cc\u79c0 \u76ee\u4e4b\u4e2d\uff0c\u6cea\u5149\u9690\u73b0\uff0c\u6ee1\u9762\u51c4\u695a\u4e4b\u8272\u7684 \u3000\u3000\u6b63\u662f\u90a3\u4e00\u522b\u65e0\u97f3\u8baf\u7684\u8427\u5357\u82f9\u3002 \u3000\u3000\u8427\u5357\u82f9\u6a2a\u906d\u56f0\u8fb1\uff0c\u88ab\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u4e00\u8def\u62bc\u5230\u8c6b\u6eaa\u53e3\uff0c\u53c8\u9669\u88ab\u4ed3\u592b\u6240\u8fb1\uff0c\u4e00\u53d1\u5343\u94a7\u65f6\uff0c \u5374\u5230\u4e86\u6551\u661f \u3000\u3000\u897f\u6881\u5c71\u4e0a\u5e7d\u79d8\u7684\u4ed9\u7a9f\u91cc\uff0c\u4e00\u5915\u72c2\u6b22\u7684\u6e29\u99a8\uff0c\u5979\u5931\u53bb\u4e86\u751f\u547d\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u6837\u4e1c\u897f\uff0c\u5374\u53c8\u50cf \u662f\u5f97\u5230\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u5c31\u5979\u5fc3\u60c5\u6700\u8ff7\u4e71\u7684\u65f6\u5019\uff0c\u5979\u5374\u53d1\u73b0\u5df2\u4f7f\u5979\u751f\u547d\u5b8c\u5168\u6539\u53d8\u7684\u201c\u4ed6\u201d\uff0c\u5df2\u7ecf\u8d70\u4e86\u3002 \u3000\u3000\u597d\u5bb9\u6613\uff0c\u5386\u5c3d\u5343\u8f9b\u4e07\u82e6\uff0c\u5979\u53c8\u627e\u5230\u4e86\u201c\u4ed6\u201d\uff0c\u5374\u770b\u4e86\u201c\u4ed6\u201d\u7684\u8eab\u4fa7\uff0c\u7ad9\u7740\u7684\u7adf\u662f\u4e00\u4e2a\u7edd \u7f8e\u800c\u6e29\u67d4\u7684\u7fe0\u88f3\u5c11\u5973\u3002 \u3000\u3000\u5979\u5f53\u7136\u4e0d\u77e5\u9053\u6628\u591c\u7684\u201c\u4ed6\u201d\uff0c\u5e76\u4e0d\u662f\u6b64\u523b\u7684\u201c\u4ed6\u201d\uff0c\u90a3\u4e48\u5979\u6b64\u523b\u7684\u5fc3\u5883\uff0c\u5c31\u53ef\u60f3\u800c\u77e5 \u4e86\u3002 \u3000\u3000\u9694\u7740\u90a3\u4e00\u9053\u6c89\u6c89\u7edd\u671b\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u51dd\u89c6\u65e0\u8bed\uff01\u5fc3\u91cc\u5374\u5404\u4e2a\u6709\u4e00\u79cd\u65e0\u6cd5\u89e3\u91ca\u7684\u611f\u89c9 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u4eec\u7684\u611f\u89c9\u662f\u622a\u7136\u4e0d\u540c\u7684\u3002 \u3000\u3000\u4e07\u8679\u53d1\u73b0\u8eab\u4fa7\u7684\u4eba\uff0c\u795e\u8272\u7a81\u5730\u53d8\u4e86\u3002 \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u65f6\u5149\u90fd\u5b8c\u5168\u662f\u5728\u8fd9\u6d53\u6797\u5bc6\u9601\u91cc\u6e21\u8fc7\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5979\u5df2\u5c06\u81ea\u5df1\u7684\u5c11\u5973\u82b3\u5fc3\uff0c\u4f9d\u4f9d\u5730\u4ea4\u7ed9\u4e86\u6b64\u523b\u6b63\u7ad9\u5728\u5979\u8eab\u4fa7\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u662f\u90a3\u4e48\u6f47\u6d12\uff0c\u90a3\u4e48\u542b\u84c4\uff0c\u867d\u7136\u4f60\u4e0d\u80fd\u5728\u4ed6\u8138\u4e0a\u5bfb\u627e\u5230\u4e00\u4e1d\u7b11\u5bb9\uff0c\u7136\u800c\u4f60\u5374\u53ef\u4ee5\u4ece \u4ed6\u90a3\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\u4e2d\uff0c\u627e\u51fa\u7b11\u610f\u3002 \u3000\u3000\u6c89\u9ed8\uff0c\u542b\u84c4\uff0c\u800c\u4e0d\u8f7b\u6613\u53d1\u7b11\u7684\u7537\u5b50\uff0c\u5728\u591a\u60c5\u3001\u5e7b\u60f3\uff0c\u800c\u53c8\u559c\u6b22\u53d1\u7b11\u7684\u5c11\u5973\u773c\u4e2d\uff0c\u6c38\u8fdc\u662f \u4e16\u4e0a\u6700\u6700\u53ef\u7231\u7684\u4eba\u3002 \u3000\u3000\u4f55\u51b5\u8fd9\u4eba\u53c8\u662f\u5979\u7239\u7239\u7684\u201c\u6551\u547d\u6069\u4eba\u201d\u54e9\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5979\u7684\u4e00\u53cc\u660e\u7738\uff0c\u4e00\u4f1a\u513f\u671b\u7740\u8eab\u4fa7\u7684\u201c\u4ed6\u201d\uff0c\u4e00\u4f1a\u513f\u671b\u7740\u5bf9\u5d16\u7684\u201c\u5979\u201d\u3002 \u3000\u3000\u201c\u5979\u201d\u662f\u8c01\u5462\uff1f\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u671b\u7740\u201c\u4ed6\u201d\uff1f \u3000\u3000\u867d\u7136\u662f\u6781\u77ed\u7684\u4e00\u5239\u90a3\uff0c\u7136\u800c\u5728\u8fd9\u4e09\u4eba\u770b\u6765\uff0c\u5374\u6709\u5982\u65e0\u6cd5\u63cf\u8ff0\u7684\u6f2b\u957f\u3002 \u3000\u3000\u8427\u5357\u82f9\u987f\u89c9\u5929\u5730\u4e4b\u5927\uff0c\u518d\u4e5f\u6ca1\u6709\u4e00\u5904\u53ef\u5bb9\u5f97\u4e0b\u81ea\u5df1\u3002 \u3000\u3000\u5979\u811a\u4e0b\u865a\u98d8\u98d8\u7684\uff0c\u8fd9\u4e16\u754c\u5df2\u4e0d\u518d\u5c5e\u4e8e\u5979\uff0c\u5979\u4e5f\u4e0d\u518d\u5c5e\u4e8e\u8fd9\u4e16\u754c\u3002 \u3000\u3000\u4f0a\u98ce\u5462\uff1f \u3000\u3000\u4ed6\u5947\u602a\uff1a\u4e3a\u4ec0\u4e48\u8427\u5357\u82f9\u6b64\u523b\u7adf\u7136\u8dd1\u5230\u6b64\u5730\u6765\uff01 \u3000\u3000\u8fc7\u5ea6\u7684\u60ca\u6115\uff0c\u4fbf\u5f97\u4ed6\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u8bdd\u624d\u597d\u3002 \u3000\u3000\u8eab\u4fa7\u7684\u4e07\u8679\u53c8\u6084\u8bed\u9053\uff1a \u3000\u3000\u201c\u5979\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u53e3\u4e2d\u4f0a\u5514\u4e86\u534a\u53e5\uff0c\u671b\u4e86\u8fd9\u5a07\u67d4\u7684\u5c11\u5973\u4e00\u773c\uff0c\u76ee\u5149\u7acb\u523b\u53c8\u56de\u5230\u5bf9\u5d16\u3002 \u3000\u3000\u90a3\u77e5 \u3000\u3000\u84e6\u5730\u4e00\u58f0\u60ca\u5524\uff0c\u5bf9\u5d16\u7684\u8427\u5357\u82f9\uff0c\u7adf\u50cf\u662f\u7acb\u8db3\u4e0d\u7a33\u4f3c\u7684\uff0c\u7adf\u5411\u90a3\u6c89\u6c89\u7684\u7edd\u58d1\uff0c\u5815\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u4f0a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u6293\u7740\u680f\u6746\u7684\u53cc\u624b\uff0c\u7adf\u90fd\u6df1\u6df1\u9677\u5165\u680f\u6728\u91cc\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u8427\u5357\u82f9\u7684\u53cc\u624b\uff0c\u51fa\u4e8e\u672c\u80fd\u5730\u5728\u65ad\u5d16\u7684\u5c71\u58c1\u4e0a\u4e71\u6293\uff0c\u5374\u4ec0\u4e48\u4e5f\u6293\u4e0d\u5230\u3002 \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u5979\u5df2\u5815\u4e0b\u6570\u4e08\uff0c\u4e0b\u9762\u7684\u6c89\u6c89\u7edd\u58d1\uff0c\u4e5f\u5982\u4e00\u4e2a\u731b\u517d\u7684\u5de8\u53e3\u4f3c\u7684\uff0c\u5df2\u5c06\u8981\u5b8c\u5168\u541e \u566c\u4e86\u5979\u3002 \u3000\u3000\u4f0a\u98ce\u6765\u4e0d\u53ca\u518d\u8f6c\u7b2c\u4e8c\u4e2a\u5ff5\u5934\uff0c\u76ee\u5149\u5fae\u8f6c\uff0c\u5df2\u7136\u671b\u89c1\u8fd9\u98de\u9601\u7684\u89d2\u91cc\uff0c\u6b63\u76d8\u7740\u4e00\u6761\u5f69\u5e26\uff0c\u5374 \u6b63\u662f\u6628\u591c\u7528\u4ee5\u8fce\u5bbe\u7684\u3002 \u3000\u3000\u4ed6\u7684\u624b\uff0c\u4e5f\u7acb\u5373\u968f\u7740\u4ed6\u7684\u76ee\u5149\uff0c\u6293\u5230\u90a3\u76d8\u5f69\u5e26\u4e0a\uff0c\u5fae\u5fae\u4e00\u6296\uff0c\u5c06\u5f69\u5e26\u7684\u4e00\u7aef\u4ea4\u7ed9\u4e07\u8679\uff0c \u81ea\u5df1\u5374\u7d27\u63e1\u7740\u53e6\u4e00\u7aef\uff0c\u63a0\u51fa\u9601\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u53d8\u5316\uff0c\u5728\u5f53\u65f6\u771f\u662f\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u4e07\u8679\u832b\u7136\u63a5\u8fc7\u5f69\u5e26\uff0c\u7adf\u672a\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u5374\u89c1\u201c\u4ed6\u201d\u5df2\u50cf\u71d5\u5b50\u4f3c\u7684\uff0c\u98de\u63a0\u4e86\u51fa\u53bb\uff0c\u4e24\u5d16\u76f8 \u9694\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e94\uff0c\u516d\u4e08\uff0c\u4f0a\u98ce\u594b\u529b\u4e00\u63a0\uff0c\u79bb\u7740\u5bf9\u5d16\uff0c\u5374\u8fd8\u6709\u4e24\u4e08\u8fdc\u8fd1\u3002\u4f46\u6b64\u523b\u4ed6\u5df2\u5168\u7136\u5c06\u751f \u6b7b\u7f6e\u4e4b\u5ea6\u5916\u2014\u2014\u4eba\u4eec\u5728\u60c5\u611f\u7684\u6fc0\u52a8\u4e4b\u4e2d\uff0c\u4e0d\u662f\u5e38\u5e38\u5982\u6b64\u7684\u5417\uff1f \u3000\u3000\u4ed6\u731b\u63d0\u771f\u6c14\uff0c\u53cc\u8db3\u987f\u5904\uff0c\u98d8\u98d8\u7684\u8eab\u5f62\uff0c\u4fbf\u53c8\u518d\u6b21\u524d\u63a0\uff0c\u4f46\u8fd9\u65f6\u4ed6\u8eab\u5728\u7a7a\u4e2d\uff0c\u4e00\u65e0\u4f9d\u636e\uff0c \u8eab\u5f62\u867d\u53c8\u524d\u63a0\u4e08\u8bb8\uff0c\u4f46\u5374\u5df2\u529b\u7aed\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u671b\u7740\u5bf9\u5d16\uff0c\u867d\u7136\u53ea\u5269\u4e0b\u4e0d\u5230\u4e00\u4e08\u7684\u8ddd\u79bb\u4e86\uff0c\u4f46\u8fd9\u4e00\u6bb5\u8ddd\u79bb\uff0c\u5374\u751f\u50cf\u662f\u65e0\u6cd5\u4f01\u53ca\u7684 \u9065\u8fdc\u3002 \u3000\u3000\u201c\u8ddd\u79bb\u201d\uff0c\u8fd9\u4e24\u5b57\u5e76\u4e0d\u662f\u7edd\u5bf9\u7684\u540d\u8bcd\uff0c\u6709\u65f6\u4e07\u4e08\u6709\u5982\u54ab\u5c3a\uff0c\u6709\u65f6\u54ab\u5c3a\u5374\u5982\u5929\u6daf\u3002\u4eba\u4e0e\u4eba \u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u4e0d\u4e5f\u662f\u5982\u6b64\u5417\uff1f \u3000\u3000\u4f0a\u98ce\u81ea\u5e7c\u4e60\u6b66\uff0c\u5341\u4f59\u5e74\u6027\u547d\u4ea4\u4fee\u7684\u6b66\u529f\uff0c\u6b64\u523b\u5df2\u5168\u90e8\u65bd\u5c55\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u5c31\u5728\u4ed6\u6362\u6c14\u4e4b\u95f4\uff0c\u4ed6\u7684\u8eab\u5f62\uff0c\u5374\u4e5f\u6709\u5982\u9668\u77f3\u822c\u5730\uff0c\u671d\u7edd\u671b\u4e2d\u843d\u4e86\u4e0b\u53bb\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u516d\u5341\u7ae0\u3000\u7edd\u58d1\u6df1\u60c5>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u7ae0\u3000\u7edd\u58d1\u6df1\u60c5 \u3000\u3000\u7acb\u5728\u680f\u8fb9\u7684\u4e07\u8679\uff0c\u4e0d\u7981\u4e3a\u4e4b\u60ca\u547c\u51fa\u58f0\uff0c\u4e00\u53cc\u7ea4\u624b\uff0c\u6293\u4f4f\u5f69\u5e26\uff0c\u518d\u4e5f\u4e0d\u80af\u653e\u677e\u3002\u5fc3\u4e2d\u4e4b\u60c5 \u601d\uff0c\u5374\u6709\u5982\u6012\u6d9b\u822c\u6c79\u6d8c\u8d77\u6765\u3002 \u3000\u3000\u201c\u5979\u662f\u8c01\u5462\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u4e48\u820d\u547d\u5730\u53bb\u6551\u5979\uff1f\u201d \u3000\u3000\u90a3\u77e5\u53cc\u624b\u7a81\u5730\u4e00\u677e\uff0c\u5f69\u5e26\u7684\u90a3\u4e00\u7aef\u5df2\u7a7a\u65e0\u4e00\u4eba\uff0c\u4f0a\u98ce\u7684\u8eab\u5f62\uff0c\u5df2\u5982\u6d41\u661f\u822c\u843d\u4e86\u4e0b\u53bb\uff0c\u4e0b \u9762\u7edd\u58d1\u6c89\u6c89\uff0c\u6df1\u4e0d\u89c1\u5e95\u3002 \u3000\u3000\u8fd9\u521d\u6b21\u52a8\u60c5\u7684\u5c11\u5973\uff0c\u8111\u4e2d\u4e00\u9635\u91cf\u7729\uff0c\u5589\u95f4\u50cf\u662f\u7a81\u7136\u5835\u585e\u4f4f\u4e86\uff0c\u8fde\u60ca\u547c\u7684\u58f0\u97f3\u90fd\u53d1\u4e0d\u51fa \u6765\u3002 \u3000\u3000\u7b49\u5230\u5979\u5fae\u5fae\u5b9a\u4e86\u5b9a\u795e\uff0c\u76ee\u5149\u518d\u5f80\u4e0b\u641c\u7d22\u65f6\uff0c\u5979\u4f9d\u7a00\u5728\u5bf9\u9762\u7684\u5c71\u58c1\u4e0a\uff0c\u770b\u5230\u4e00\u70b9\u4eba\u5f71\uff0c\u6b63 \u7f13\u7f13\u5730\u5411\u4e0b\u79fb\u52a8\u7740\u3002 \u3000\u3000\u53ea\u662f\u6b64\u523b\u65e5\u5149\u5df2\u9690\uff0c\u90a3\u4eba\u5f71\u6240\u5728\u7684\u5730\u4f4d\uff0c\u8ddd\u79bb\u5d16\u5934\u5df2\u6709\u4e8c\uff0c\u4e09\u5341\u4e08\uff0c\u5979\u867d\u7528\u5c3d\u4e86\u76ee\u529b\uff0c \u5374\u4ecd\u7136\u65e0\u6cd5\u5206\u8fa8\u5f97\u51fa\uff0c\u8fd9\u6761\u4eba\u5f71\u7a76\u7adf\u662f\u8c01\u6765\u3002 \u3000\u3000\u8fd9\u51e0\u58f0\u60ca\u5524\u58f0\uff0c\u5f53\u7136\u5df2\u60ca\u52a8\u4e86\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929\u840d\uff0c\u4ed6\u4e00\u63a0\u4e0a\u9601\uff0c\u6c89\u58f0\u559d\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e07\u8679\u67f3\u8170\u4e00\u67e0\uff0c\u6251\u8fdb\u5979\u7239\u7239\u7684\u6000\u91cc\uff0c\u542b\u7740\u6cea\u8bf4\u51fa\u4e86\u65b9\u624d\u7684\u4e8b\u3002 \u3000\u3000\u4e07\u5929\u840d\u4e0d\u7981\u4e5f\u4e3a\u4e4b\u9762\u8272\u5927\u53d8\uff0c\u5374\u4ecd\u7136\u5b89\u6170\u7740\u81ea\u5df1\u7684\u5973\u513f\uff1a \u3000\u3000\u201c\u4e0d\u6253\u7d27\u7684\uff01\u4ed6\u867d\u7136\u5df2\u843d\u4e86\u4e0b\u53bb\uff0c\u4f46\u51ed\u4ed6\u7684\u8eab\u624b\uff0c\u7edd\u5bf9\u6b7b\u4e0d\u4e86\u2014\u2014\u7b49\u4f1a\u513f\u7239\u7239\u4e5f\u60f3\u6cd5\u5b50 \u4e0b\u53bb\u627e\u627e\u770b\u3002\u8fd9\u4e48\u5927\u7684\u4eba\uff0c\u8fd8\u54ed\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8f7b\u629a\u7740\u81ea\u5df1\u7231\u5973\u7684\u79c0\u53d1\uff0c\u5634\u91cc\u867d\u662f\u8fd9\u4e48\u8bf4\uff0c\u5176\u5b9e\u5fc3\u91cc\u5374\u6ca1\u6709\u534a\u70b9\u628a\u63e1\u3002\u8eab\u624b\u518d\u9ad8\u7684 \u4eba\uff0c\u843d\u4eba\u8fd9\u79cd\u7edd\u58d1\u91cc\uff0c\u82e5\u8bf4\u662f\u7edd\u65e0\u5371\u9669\uff0c\u90a3\u5c31\u662f\u6b3a\u4eba\u4e4b\u8c08\u4e86\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u6b64\u523b\u4f0a\u98ce\u548c\u8427\u5357\u82f9\u7684\u547d\u8fd0\uff0c\u53c8\u5df2\u662f\u843d\u5230\u4ec0\u4e48\u5730\u6b65\u4e86\u5462\uff1f \u3000\u3000\u65b9\u624d\u4ed6\u5fae\u6563\u771f\u6c14\uff0c\u8eab\u5f62\u4fbf\u4e0d\u7531\u81ea\u4e3b\u5730\u843d\u4e86\u4e0b\u53bb\u3002\u4f46\u7a81\u5730\u624b\u4e2d\u53c8\u4e00\u7d27\uff0c\u539f\u6765\u662f\u5f69\u5e26\u5df2\u5230\u5c3d \u5934\u3002 \u3000\u3000\u4ed6\u4e34\u5371\u4e4b\u4e0b\uff0c\u795e\u5fd7\u672a\u4e71\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5f53\u7136\u4e5f\u5bb9\u4e0d\u5f97\u4ed6\u6765\u505a\u4e2a\u8be6\u7ec6\u7684\u5206\u6790\uff0c\u5230\u4e86\u8fd9\u79cd\u65f6 \u5019\uff0c\u4eba\u4eec\u6709\u65f6\u4fbf\u5f97\u51ed\u672c\u80fd\u51b3\u5b9a\u4e00\u5207\u4e86\u3002 \u3000\u3000\u8fd9\u6761\u5f69\u5e26\uff0c\u53bb\u52bf\u5df2\u5f31\uff0c\u81ea\u7136\u5c31\u53c8\u7f13\u7f13\u5411\u98de\u9601\u90a3\u8fb9\u6c64\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4e8e\u662f\u4f0a\u98ce\u548c\u5bf9\u9762\u5c71\u5d16\u7684\u8ddd\u79bb\uff0c\u81ea\u7136\u4e5f\u8d8a\u6765\u8d8a\u8fdc\u3002\u4ed6\u5fae\u4e00\u601d\u5fd6\u4e4b\u4e0b\uff0c\u53cc\u811a\u7a81\u53c8\u5411\u524d\u4e00\u8e74\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5f62\uff0c\u4fbf\u7acb\u523b\u53c8\u5411\u524d\u6c64\uff0c\u8fd9\u79cd\u6837\u5b50\u867d\u6709\u5982\u5782\u9aeb\u5e7c\u7ae5\u7684\u6c64\u79cb\u5343\uff0c\u4f46\u5374\u662f\u751f\u6b7b\u7cfb\u4e8e\u4e00 \u53d1\uff0c\u5371\u9669\u5f97\u65e0\u4ee5\u590d\u52a0\u7684\u60c5\u51b5\u4e86\u3002 \u3000\u3000\u5f69\u5e26\u7684\u957f\u5ea6\u5df2\u5c3d\uff0c\u4ed6\u518d\u4e5f\u4e0d\u53bb\u601d\u8003\u4fbf\u6293\u7740\u81ea\u5df1\u7684\u8eab\u5f62\u548c\u5c71\u58c1\u6700\u8fd1\u65f6\u90a3\u4e00\u5239\u90a3\uff0c\u7eb5\u8eab\u5411\u5c71 \u58c1\u98de\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u58c1\u95f4\u867d\u7136\u5bf8\u8349\u4e0d\u751f\uff0c\u4f46\u5374\u51f8\u51f9\u751a\u591a\uff0c\u4e5f\u5076\u6709\u4e9b\u88c2\u9699\u3002\u987b\u77e5\u8427\u5357\u82f9\u65b9\u624d\u795e\u667a\u5df2\u4e3a\u60c5\u6240\u4e71\uff0c \u843d\u4e0b\u53bb\u65f6\uff0c\u81ea\u7136\u4ec0\u4e48\u4e5f\u6293\u4e0d\u7740\u3002 \u3000\u3000\u7136\u800c\u6b64\u523b\u7684\u4f0a\u98ce\uff0c\u5374\u7edd\u672a\u56e0\u81ea\u5df1\u5904\u5883\u7684\u5371\u9669\uff0c\u800c\u4e1d\u6beb\u614c\u4e71\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u552f\u4e00\u7684\u4e00\u4e2a\u5ff5\u5934\uff0c\u5c31\u662f\u627e\u7740\u8427\u5357\u82f9\uff0c\u751a\u81f3\u662f\u5979\u7684\u5c38\u8eab\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u4eba\u4eec\u7684\u771f\u6027\u60c5\uff0c\u4fbf\u4f1a\u6beb\u65e0\u4fdd\u7559\u5730\u663e\u9732\u4e86\u51fa\u6765\uff0c\u5c24\u5176\u662f\u50cf\u4f0a\u98ce\u8fd9\u79cd\u6027\u60c5\u7537 \u5b50\uff0c\u6709\u65f6\u5e38\u4f1a\u5c06\u201c\u751f\u6b7b\u201d\u4e24\u5b57\uff0c\u629b\u5728\u4e00\u8fb9\u3002 \u3000\u3000\u4ed6\u4e00\u53cc\u94c1\u638c\uff0c\u7d27\u7d27\u6500\u5728\u5c71\u58c1\u4e0a\uff0c\u51ed\u7740\u4e00\u53e3\u771f\u6c14\uff0c\u7f13\u7f13\u5411\u4e0b\u79fb\u52a8\u7740\u3002 \u3000\u3000\u8fd9\u5c71\u58c1\u58c1\u7acb\u5343\u4ede\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u80fd\u4e0d\u80fd\u5230\u8fbe\u5c3d\u5934\uff1a\u4f46\u4ed6\u5374\u77e5\u9053\u53ea\u8981\u81ea\u5df1\u4e00\u5931\u624b\uff0c \u90a3\u4e48\u81ea\u5df1\u4fbf\u8981\u5230\u8fbe\u751f\u547d\u7684\u5c3d\u5934\u4e86\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4e00\u9635\u82e5\u65ad\u82e5\u7eed\u7684\u547b\u541f\u4e4b\u58f0\uff0c\u4f20\u5165\u4ed6\u7684\u8033\u91cc\uff0c\u4ed6\u7cbe\u795e\u53cd\u5012\u4e00\u632f\u3002 \u3000\u3000\u987b\u77e5\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u5f53\u7136\u4e0d\u4f1a\u6709\u522b\u7684\u4eba\u7c7b\u3002\u90a3\u4e48\u8fd9\u547b\u541f\u4e4b\u58f0\uff0c\u81ea\u4e5f\u5fc5\u7136\u5c31\u662f\u8427\u5357\u82f9\u53d1\u51fa\u6765 \u7684\u3002 \u3000\u3000\u8fd9\u547b\u541f\u4e4b\u58f0\uff0c\u4e5f\u65e0\u5f02\u544a\u8bc9\u4e86\u4ed6\u3002\u8427\u5357\u82f9\u4e5f\u5e76\u672a\u6b7b\u53bb\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5fc3\u4e2d\u8fd9\u4e00\u559c\uff0c\u624b\u95f4\u4e00\u6ed1\uff0c\u4e00\u5757\u5c0f\u5c0f\u7684\u5c71\u77f3\uff0c\u4ece\u4ed6\u8eab\u4fa7\u843d\u4e86\u4e0b\u53bb\uff0c\u5e26\u8d77\u4e00\u8fde\u4e32\u8f7b\u5fae \u7684\u54cd\u52a8\uff0c\u5374\u542c\u4e0d\u5230\u843d\u5230\u5730\u4e0a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u4e00\u9635\u51b7\u610f\uff0c\u76f4\u900f\u80cc\u810a\uff0c\u5168\u8eab\u4e5f\u7981\u4e0d\u4f4f\u5192\u51fa\u4e00\u9635\u51b7\u6c57\uff0c\u5fd9\u81ea\u6536\u6444\u795e\u667a\uff0c\u518d\u4e5f\u4e0d\u6562\u6709 \u534a\u70b9\u758f\u5ffd\u3002 \u3000\u3000\u53c8\u5f80\u4e0b\u6ed1\u4e86\u7ea6\u6478\u4e8c\u5341\u4f59\u4e08\uff0c\u65ad\u7eed\u7684\u547b\u541f\u58f0\uff0c\u5165\u8033\u4e5f\u8d8a\u53d1\u6e05\u6670\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u5947\u602a\uff0c\u8fd9\u5c71\u58c1\u4e00\u4e0b\u5343\u4e08\uff0c\u4e2d\u95f4\u7edd\u65e0\u4e00\u5757\u53ef\u4ee5\u5bb9\u8eab\u7684\u5730\u65b9\uff0c\u8427\u5357\u82f9\u50cf\u9668\u77f3\u822c\u5815\u4e0b\u53bb \u7684\u8eab\u5b50\uff0c\u600e\u4f1a\u5728\u534a\u9014\u505c\u4f4f\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u5de6\u624b\u4e94\u6307\u5982\u94a9\uff0c\u6df1\u6df1\u6325\u5165\u4e00\u9053\u6a2a\u751f\u7684\u88c2\u9699\u91cc\uff0c\u518d\u504f\u8d77\u53f3\u9762\u7684\u8eab\u5b50\uff0c\u4fef\u9996\u4e0b\u671b\uff0c\u53ea \u89c1\u8ddd\u79bb\u81ea\u5df1\u811a\u6b65\uff0c\u4e0d\u8fc7\u6570\u4e08\u4e4b\u5904\uff0c\u7adf\u662f\u4e00\u7247\u8346\u68d8\u3002 \u3000\u3000\u800c\u8427\u5357\u82f9\u90a3\u65ad\u7eed\u7684\u547b\u541f\u58f0\uff0c\u4fbf\u5c31\u662f\u4ece\u8fd9\u7247\u8346\u68d8\u95f4\u53d1\u51fa\u7684\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u518d\u4e0b\u964d\u6570\u4e08\uff0c\u4ed6\u4e0d\u7981\u8131\u53e3\u60ca\u547c\u51fa\u6765\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e00\u7247\u4e1b\u751f\u7684\u8346\u68d8\u3002\u4e2d\u95f4\u5df2\u6709\u4e00\u5904\u88ab\u538b\u4e86\u4e0b\u53bb\uff0c\u4e00\u53cc\u8840\u8ff9\u6dcb\u6f13\u7684\u624b\u638c\uff0c\u7d27\u7d27\u6293\u7740\u8346 \u68d8\uff0c\u6700\u5148\u8fdb\u5165\u4f0a\u98ce\u7684\u773c\u5e18\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u770b\u89c1\u8427\u5357\u82f9\u90a3\u5f20\u672c\u662f\u65e0\u6b64\u79c0\u7f8e\u7684\u9762\u5e9e\uff0c\u6b64\u523b\u7adf\u4e5f\u6ee1\u662f\u8840\u8ff9\uff0c\u9c9c\u8840\u5df2\u67d3\u5f97\u5979\u7684 \u8138\uff0c\u6839\u672c\u5df2\u5206\u4e0d\u51fa\u539f\u6765\u7684\u80a4\u8272\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u53ea\u89c9\u5168\u8eab\u4e00\u8f6f\uff0c\u53cc\u624b\u9669\u4e9b\u53c8\u628a\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u773c\u4e2d\u987f\u65f6\u4e5f\u8ff7\u8499\u4e86\u8d77\u6765\uff0c\u4e0d\u77e5\u662f\u7edd\u58d1\u6df1\u5904\u7684\u96fe\u6c14\uff0c\u6291\u6216\u662f\u773c\u4e2d\u6d8c\u51fa\u7684\u6cea\u73e0\u3002 \u3000\u3000\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u76ee\u5149\u56db\u626b\uff0c\u53e3\u4e2d\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u522b\u6015\uff01\u6211\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u770b\u5230\u8427\u5357\u82f9\u5931\u795e\u7684\u773c\u775b\uff0c\u7531\u4e0b\u9762\u671b\u4e86\u4e0a\u6765\uff0c\u671b\u5230\u4e86\u81ea\u5df1\uff0c\u4e5f\u542c\u5230\u8fd9\u75f4\u60c5\u7684\u5c11\u5973\u5fae\u5f31\u7684 \u58f0\u97f3\uff0c\u5728\u65ad\u7eed\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5357\u2026\u2026\u54e5\u2026\u2026\u521a\u624d\uff0c\u521a\u624d\u90a3\u4e2a\u5973\u5b69\u5b50\u2026\u2026\u662f\u8c01\u5440\uff1f\u201d\u4f0a\u98ce\u53ea\u89c9\u5fc3\u5e95\u7684\u60c5\u611f\uff0c\u7ffb\u6c5f\u5012\u6d77 \u822c\u6d8c\u4e86\u4e0a\u6765\uff0c\u5728\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u4ed6\u6d51\u5fd8\u4e86\u4e00\u5207\uff0c\u5fc3\u4e2d\u6240\u611f\u53d7\u5230\u7684\uff0c\u552f\u4e00\u53ea\u6709\u8427\u5357\u82f9\u5bf9\u81ea\u5df1\u7684\u6df1 \u5029\uff01 \u3000\u3000\u4e8e\u662f\u4ed6\u5f3a\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u4e0d\u8981\u50bb\uff01\u90a3\u662f\u6211\u4e00\u4e2a\u670b\u53cb\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5b89\u6170\u7684\u7b11\u9765\uff0c\u6d6e\u4e0a\u4e86\u8427\u5357\u82f9\u7684\u8138\uff1b\u5979\u6ee1\u9762\u7684\u8840\u8ff9\uff0c\u90fd\u751f\u50cf\u662f\u56e0\u7740\u8fd9\u4e2a\u7b11\u9765\uff0c\u800c\u53d8\u5f97 \u6709\u5982\u73ab\u7470\u82b1\u6c41\u822c\u7684\u9c9c\u8273\u3002 \u3000\u3000\u5979\u6084\u7136\u95ed\u4e0a\u773c\u775b\uff0c\u4f4e\u4f4e\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u90a3\u2026\u2026\u6211\u5c31\u2026\u2026\u653e\u5fc3\u4e86\uff0c\u6211\u2026\u2026\u8fd8\u4ee5\u4e3a\u4f60\u2026\u2026\u559c\u6b22\u5979\u54e9\u3002\u201d \u3000\u3000\u4f0a\u98ce\u773c\u4e2d\u7684\u8ff7\u8499\uff0c\u66f4\u52a0\u6df1\u91cd\u4e86\uff01 \u3000\u3000\u4ed6\u51e0\u4e4e\u8981\u4e0d\u987e\u4e00\u5207\u5730\u8df3\u4e0b\u53bb\uff0c\u548c\u8fd9\u4e2a\u6df1\u7231\u81ea\u5df1\u7684\u5973\u5b50\uff0c\u62e5\u62b1\u5728\u8fd9\u4e00\u7247\u4e1b\u751f\u7684\u8346\u68d8\u91cc\u3002 \u3000\u3000\u81ea\u53e4\u4ee5\u6765\uff0c\u53c8\u6709\u4ec0\u4e48\u4e1c\u897f\uff0c\u6bd4\u771f\u7eaf\u7684\u60c5\u611f\uff0c\u66f4\u4e3a\u53ef\u8d35\u5462\uff1f \u3000\u3000\u4ed6\u7684\u5589\u5934\u54fd\u54bd\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u4e3a\u4e86\u8fd9\u4e00\u4efd\u771f\u7eaf\u7684\u60c5\u611f\uff0c\u66f4\u8981\u73cd\u60dc\u81ea\u5df1\u548c\u5979\u7684\u6027\u547d\u3002 \u3000\u3000\u6b64\u523b\u5df2\u662f\u6b8b\u51ac\u3002 \u3000\u3000\u6625\u5929\u5c31\u8981\u5230\u4e86\u3002\u4ed6\u8981\u548c\u5979\u4e00\u9f50\u4eab\u53d7\u90a3\u5149\u8f89\u707f\u70c2\u7684\u6625\u65e5\uff0c\u4eab\u53d7\u751f\u547d\u7684\u5927\u597d\u5e74\u534e\uff0c\u4eab\u53d7\u8fd9\u4e00 \u4efd\u771f\u7eaf\u7684\u60c5\u611f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u54fd\u54bd\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u82f9\u59b9\uff01\u632f\u4f5c\u4e9b\uff0c\u4e0d\u8981\u4e71\u60f3\uff01\u7b49\u6211\u628a\u4f60\u62c9\u8d77\u6765\u3002\u201d \u3000\u3000\u5979\u5012\u63e1\u7740\u53cc\u624b\uff0c\u5f80\u8346\u68d8\u4e2d\u6ed1\u53bb\u3002 \u3000\u3000\u4ed6\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u6d41\u4e0b\u6cea\u6765\uff0c\u6e05\u6f88\uff0c\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u6cbf\u7740\u4ed6\u7684\u9762\u988a\uff0c\u8f7b\u8f7b\u6ed1\u843d\u4e0b\u53bb\uff0c\u4e00\u6ef4\uff0c \u4e24\u6ef4\u3002\u6ef4\u5728\u4ed6\u7684\u8863\u886b\u4e0a\u3002 \u3000\u3000\u201c\u4e08\u592b\u6709\u6cea\u4e0d\u8f7b\u5f39\uff0c\u53ea\u56e0\u672a\u5230\u4f24\u5fc3\u5904\u3002\u201d\u7136\u800c\u4ed6\u6b64\u523b\u5e76\u975e\u4f24\u5fc3\uff0c\u800c\u662f\u6df1\u6df1\u5730\u88ab\u8fd9\u79cd\u771f\u60c5 \u6240\u611f\u52a8\uff0c\u4eba\u4eec\u4e4b\u6240\u4ee5\u6d41\u6cea\uff0c\u539f\u975e\u4e00\u5b9a\u662f\u4e3a\u7740\u60b2\u54c0\u5440\uff01 \u3000\u3000\u4ed6\u627e\u7740\u53e6\u4e00\u6761\u6a2a\u751f\u7684\u88c2\u9699\uff0c\u5c06\u81ea\u5df1\u7684\u624b\u638c\u63d2\u4e86\u8fdb\u53bb\u3002\u6570\u5341\u5e74\u4ece\u672a\u95f4\u65ad\u7684\u8bad\u7ec3\uff0c\u867d\u7136\u4f7f\u5f97 \u4ed6\u624b\u638c\u6709\u5982\u94a2\u94c1\u4e00\u822c\u575a\u786c\uff0c\u4f46\u6b64\u523b\uff0c\u4ed6\u4ecd\u7136\u611f\u5230\u4e00\u9635\u9635\u6df1\u5165\u9aa8\u9ad3\u7684\u75db\u82e6\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u79cd\u75db\u82e6\u5f3a\u70c8\u7a0b\u5ea6\uff0c\u5374\u8fd8\u6bd4\u4e0d\u4e0a\u4ed6\u5fc3\u4e2d\u6240\u611f\u53d7\u5230\u7684\uff0c\u90a3\u79cd\u6e17\u542b\u7740\u60b2\u54c0\u7684\u559c\u60a6\u7684\u5343\u4e07 \u5206\u4e4b\u4e00\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7f13\u7f13\u5f13\u4e0b\u8eab\u5b50\uff0c\u4e00\u53ea\u624b\u7d27\u6293\u7740\u5c71\u58c1\uff0c\u4e00\u53ea\u624b\u63a2\u5165\u8346\u68d8\uff0c\u5fae\u4e00\u54ac\u7259\uff0c\u72e0\u4e86\u72e0\u5fc3\uff0c\u6293 \u7740\u8427\u5357\u82f9\u7684\u5934\u53d1\uff0c\u63d0\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8427\u5357\u82f9\u4f4e\u4f4e\u547b\u541f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5357\u54e5\u54e5\uff01\u4f60\u653e\u5fc3\uff01\u53ea\u8981\u4f60\u6765\u4e86\u3002\u6211\u5c31\u4e0d\u8981\u7d27\u4e86\u3002\u6211\u2026\u2026\u6839\u672c\u6ca1\u6709\u53d7\u4ec0\u4e48\u4f24\u54e9\u3002\u201d \u3000\u3000\u8fd9\u75f4\u60c5\u7684\u5c11\u5973\uff0c\u6b64\u523b\u679c\u7136\u5df2\u7ecf\u6062\u590d\u4e86\u751f\u5b58\u7684\u52c7\u6c14\uff0c\u4e5f\u6062\u590d\u4e86\u5bf9\u201c\u6b7b\u4ea1\u201d\u640f\u6597\u7684\u7cbe\u529b\uff0c\u5c31 \u7b97\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u4e5f\u6bd4\u65b9\u624d\u632f\u594b\u5f97\u591a\uff0c\u5df2\u4e0d\u518d\u662f\u65ad\u7eed\u7684\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u4f46\u89c9\u624b\u63d0\u5904\u5b9b\u5982\u65e0\u7269\uff0c\u4e0d\u7981\u5b89\u6170\u5730\u5fae\u7b11\u8d77\u6765\u3002\u4ed6\u77e5\u9053\u5979\u7684\u8f7b\u529f\uff0c\u5e76\u672a\u5931\u53bb\uff0c\u6ee1\u9762\u6ee1 \u624b\u7684\u8840\u8ff9\uff0c\u4e0d\u8fc7\u53ea\u662f\u8868\u76ae\u7684\u64e6\u4f24\u7f62\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u4fbf\u53c8\u7f13\u7f13\u5730\uff0c\u6323\u624e\u7740\uff0c\u5411\u4e0a\u9762\u722c\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u4ef0\u76ee\u800c\u89c6\uff0c\u4ed6\u4eec\u8ddd\u79bb\u5d16\u8fb9\uff0c\u867d\u7136\u6709\u7740\u6570\u5341\u4e08\u7684\u8ddd\u79bb\uff0c\u4f46\u4ed6\u76f8\u4fe1\uff1a\u51ed\u7740\u81ea\u5df1\u548c\u8427\u5357\u82f9 \u7684\u529f\u529b\uff0c\u5c31\u7b97\u518d\u8fdc\u4e9b\uff0c\u4e5f\u53ef\u4ee5\u722c\u5f97\u4e0a\u53bb\u7684\u3002 \u3000\u3000\u65b9\u624d\u63a9\u4f4f\u65e5\u5149\u7684\u90a3\u5757\u4e4c\u4e91\uff0c\u6b64\u523b\u5df2\u8d70\u5230\u4e0d\u77e5\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u4f46\u89c9\u5929\u5730\u4e4b\u95f4\uff0c\u53c8\u5145\u6ee1\u751f\u673a\uff0c\u81ea\u5df1\u6bcf\u5411\u4e0a\u79fb\u52a8\u4e00\u5c3a\uff0c\u90a3\u4e48\u81ea\u5df1\u8ddd\u79bb\u5e78\u798f\u4e5f\u5c31\u8fd1\u4e86\u4e00 \u5c3a\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u516d\u5341\u4e00\u7ae0\u3000\u6628\u591c\u6cea\u75d5>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u4e00\u7ae0\u3000\u6628\u591c\u6cea\u75d5 \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4f0a\u98ce\u4e5f\u77e5\u9053\uff0c\u4ece\u8fd9\u9762\u5230\u5d16\u8fb9\uff0c\u662f\u4e00\u6bb5\u975e\u5e38\u8270\u82e6\u7684\u884c\u7a0b\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4fa7\u89c6\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u662f\u4e00\u9635\u9eef\u7136\uff01\u4ed6\u8eab\u4fa7\u7684\u8427\u5357\u82f9\uff0c\u6b64\u523b\u4e0d\u4f46\u624b\u4e0a\uff0c\u8138\u4e0a\uff0c\u5c31\u8fde\u8eab \u4e0a\uff0c\u90fd\u5230\u5904\u67d3\u6ee1\u4e86\u8840\u8ff9\u3002\u672c\u5df2\u84ec\u4e71\u7684\u9752\u4e1d\uff0c\u6b64\u523b\u81ea\u7136\u66f4\u662f\u84ec\u4e71\u3002\u4e00\u8eab\u8863\u886b\uff0c\u4e5f\u662f\u4e03\u96f6\u516b\u843d\u7684 \u4e86\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u75f4\u60c5\u7684\u5973\u5b50\uff0c\u5fc3\u4e2d\u5374\u6709\u65e0\u6bd4\u7684\u5feb\u4e50\uff0c\u8fd9\u79cd\u5feb\u4e50\uff0c\u4f7f\u5f97\u5979\u5c06\u4efb\u4f55\u8089\u4f53\u4e0a\u7684\u75db\u82e6\uff0c\u90fd \u4e0d\u518d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u201c\u6628\u591c\u7684\u201c\u4ed6\u201d\uff0c\u679c\u7136\u5c31\u662f\u201c\u5357\u54e5\u54e5\u201d\u3002\u201d \u3000\u3000\u5979\u5fc3\u5e95\u7ffb\u6d8c\u8d77\u7684\u5feb\u4e50\u548c\u6e29\u99a8\uff0c\u5373\u4f7f\u7528\u5c3d\u4e16\u95f4\u6240\u6709\u7684\u8a00\u8bcd\uff0c\u4e5f\u65e0\u6cd5\u5f62\u5bb9\u5f97\u51fa\u6765\u7684\u3002 \u3000\u3000\u4f55\u51b5\u5979\u6b64\u523b\u4e5f\u77e5\u9053\uff0c\u5357\u54e5\u54e5\u662f\u5bf9\u5979\u6709\u7740\u771f\u60c5\uff0c\u4e0d\u7136\uff0c\u4ed6\u600e\u4e48\u4f1a\u5192\u7740\u6b7b\u4ea1\u4e0b\u6765\u6551\u81ea\u5df1\u5462\uff1f \u3000\u3000\u4e8e\u662f\u5979\u53c8\u7b11\u4e86\uff0c\u4fa7\u8f6c\u5934\uff0c\u8f7b\u58f0\u9053\uff1a \u3000\u3000\u201c\u5357\u54e5\u54e5\uff01\u4f60\u7d2f\u4e0d\u7d2f\uff1f\u8981\u4e0d\u8981\u6211\u6276\u4f60\u4e00\u628a\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u7b11\u7740\u6447\u4e86\u6447\u5934\uff0c\u8f7b\u8f7b\u4f38\u51fa\u4e00\u53ea\u624b\uff0c\u6276\u7740\u5979\u7684\u8170\u80a2\u3002\u4ed6\u77e5\u9053\u6b64\u523b\u9700\u8981\u5e2e\u52a9\u7684\uff0c\u7edd\u4e0d\u662f \u81ea\u5df1\uff0c\u800c\u662f\u81ea\u5df1\u8eab\u4fa7\u7684\u5979\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u867d\u7136\u59cb\u7ec8\u6ca1\u6709\u7ec3\u8fc7\u201c\u58c1\u864e\u6e38\u5899\u201d\u8fd9\u4e00\u7c7b\u529f\u592b\uff1b\u4f46\u6b64\u523b\uff0c\u5374\u6709\u4e00\u79cd\u65e0\u6bd4\u5f3a\u5927 \u7684\u529b\u91cf\uff0c\u4f7f\u5f97\u4ed6\u80fd\u5c06\u8fd9\u79cd\u9887\u4e3a\u9ad8\u6df1\u7684\u8f7b\u529f\uff0c\u8fd0\u7528\u5f97\u65e0\u6bd4\u66fc\u5999\uff01\u8fd9\u5f53\u7136\u4e5f\u57fa\u4e8e\u4ed6\u672c\u8eab\u6df1\u6e5b\u7684\u529f \u529b\u3002 \u3000\u3000\u5c31\u7b49\u4e8e\u4e00\u4e2a\u7cbe\u4e8e\u201c\u6977\u4e66\u201d\u7684\u4eba\uff0c\u5373\u4f7f\u672a\u4e60\u201c\u884c\u4e66\u201d\uff0c\u4f46\u5374\u4ecd\u7136\u4e00\u6837\u5730\u53ef\u4ee5\u5f88\u7cbe\u7ec3\u5730\u5199\u51fa \u201c\u884c\u4e66\u201d\u6765\u3002 \u3000\u3000\u8def\u7a0b\u867d\u7136\u8270\u8f9b\uff0c\u4f46\u65e0\u8bba\u4efb\u4f55\u4e00\u6761\u8def\uff0c\u5374\u603b\u6709\u5230\u8fbe\u7684\u65f6\u5019\u3002 \u3000\u3000\u5f53\u4f0a\u98ce\u6258\u7740\u8427\u5357\u82f9\u7684\u8170\u80a2\uff0c\u5c06\u5979\u6258\u4e0a\u4e86\u5d16\u8fb9\uff0c\u81ea\u5df1\u4e5f\u7ffb\u8eab\u800c\u4e0a\u65f6\uff0c\u4ed6\u81ea\u8ba4\u5df2\u662f\u4e16\u4e0a\u6700\u75b2 \u52b3\u7684\u4eba\u4e86\u3002 \u3000\u3000\u4ef0\u5367\u5728\u5d16\u8fb9\uff0c\u4ed6\u6df1\u6df1\u5730\u5598\u53e3\u6c14\uff0c\u5c06\u4f53\u5185\u7684\u771f\u6c14\uff0c\u7f13\u7f13\u8c03\u606f\u4e00\u904d\uff0c\u7136\u540e\u7741\u5f00\u773c\u6765\u3002 \u3000\u3000\u8427\u5357\u82f9\u4ecd\u7136\u9759\u5367\u5728\u4ed6\u8eab\u4fa7\uff0c\u5929\u4e0a\u767d\u4e91\u82cd\u7a79\uff0c\u9633\u5149\u4f9d\u65e7\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e0d\u662f\u68a6\u5883\uff0c\u4e8e\u662f\u4e00\u9635\u5e78 \u798f\u7684\u611f\u89c9\uff0c\u4fbf\u7acb\u523b\u5f25\u6f2b\u4e86\u4ed6\u5168\u8eab\u3002 \u3000\u3000\u4ed6\u5c06\u8eab\u8eaf\u8f6c\u4e86\u4e00\u4e9b\uff0c\u76ee\u5149\u6e29\u67d4\u5730\u6295\u5728\u8427\u5357\u82f9\u8eab\u4e0a\uff0c\u5979\u4e00\u4ef6\u6d45\u7d2b\u7684\u8863\u88f3\uff0c\u6b64\u523b\u5df2\u7ecf\u53d8\u5f97\u51e0 \u4e4e\u6210\u4e86\u7070\u9ed1\u8272\u4e86\u3002 \u3000\u3000\u524d\u80f8\u7684\u8863\u88f3\u5df2\u5b8c\u5168\u7834\u70c2\uff0c\u9732\u51fa\u91cc\u9762\u8f7b\u7ea2\u7684\u4eb5\u8863\u6765\uff0c\u6210\u719f\u7684\u80f8\u819b\uff0c\u4ecd\u5728\u5267\u70c8\u7684\u8d77\u4f0f\u7740\uff0c\u8863 \u88f3\u4e0a\u9c9c\u7ea2\u7684\u8840\u8ff9\uff0c\u5728\u9633\u5149\u4e0b\u66f4\u5206\u5916\u593a\u76ee\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4f0a\u98ce\u7684\u76ee\u5149\uff0c\u4fbf\u4f9d\u5faa\u7740\u5979\u8eab\u8eaf\u7684\u5f27\u7ebf\uff0c\u843d\u5728\u5979\u7684\u8138\u4e0a\u3002 \u3000\u3000\u8fd9\u5f20\u8138\u7684\u8f6e\u5ed3\u662f\u90a3\u4e48\u7f8e\u79c0\uff0c\u4f46\u662f\u5f53\u4f0a\u98ce\u7684\u76ee\u5149\u51dd\u6ce8\u5728\u8fd9\u5f20\u7f8e\u79c0\u7684\u8138\u4e0a\u7684\u65f6\u5019\uff0c\u4ed6\u4fbf\u518d\u4e5f \u63a7\u5236\u4e0d\u4f4f\u81ea\u5df1\uff0c\u731b\u5730\u7ffb\u8eab\u63a0\u4e86\u8d77\u6765\uff01 \u3000\u3000\u8fd9\u5f20\u7f8e\u79c0\u7684\u8138\u4e0a\uff0c\u6b64\u523b\u7adf\u5e03\u7740\u4f24\u75d5\uff0c\u4e00\u6761\u4e00\u6761\uff0c\u7eb5\u6a2a\u9519\u843d\uff01\u7ec6\u81f4\u7684\u76ae\u80a4\uff0c\u5411\u4e24\u8fb9\u7ffb\u8d77\uff0c \u9732\u51fa\u91cc\u9762\u9c9c\u8840\u7684\u8089\u6765\u3002 \u3000\u3000\u6709\u7684\u8840\u5757\u5df2\u7ecf\u51dd\u7ed3\u4e86\uff0c\u51dd\u7ed3\u5728\u7ffb\u5f00\u7684\u76ae\u8089\u4e0a\u9762\u3002\u6709\u4e9b\u4f24\u75d5\u8f83\u6df1\uff0c\u91cc\u9762\u4ecd\u5728\u6ccc\u7740\u8840\u73e0\u3002\u8fd9 \u5f20\u7f8e\u79c0\u7684\u9762\u5b54\uff0c\u6b64\u523b\u7adf\u6709\u65e0\u6bd4\u7684\u4e11\u6076\uff01 \u3000\u3000\u8427\u5357\u82f9\u6084\u7136\u5f20\u5f00\u773c\u6765\uff0c\u770b\u5230\u610f\u4e2d\u4eba\u6b63\u5728\u4fef\u89c6\u7740\u81ea\u5df1\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u75f4\u60c5\u5c11\u5973\u4fbf\u6e29\u67d4\u5730\u7b11\u4e86\u8d77\u6765\uff0c\u5fae\u7b11\u7275\u52a8\u4e86\u5979\u9762\u4e0a\u7684\u4f24\u75d5\uff0c\u4f7f\u5f97\u5979\u611f\u5230\u4e00\u9635\u75db\u695a\uff0c \u4f46\u6b64\u523b\u8fd9\u79cd\u75db\u695a\uff0c\u5728\u5979\u770b\u6765\uff0c\u53c8\u662f\u591a\u4e48\u8f7b\u5fae\u5462\uff01 \u3000\u3000\u5979\u4f38\u51fa\u624b\uff0c\u6625\u8471\u822c\u7684\u7389\u624b\uff0c\u6b64\u523b\u66f4\u662f\u6ee1\u5e03\u521b\u75d5\u3002\u6709\u7684\u5730\u65b9\uff0c\u751a\u81f3\u5df2\u9732\u51fa\u9aa8\u6765\u3002\u5979\u5c31\u7528\u8fd9 \u53cc\u624b\uff0c\u6e29\u67d4\u5730\u63e1\u7740\u4e86\u4f0a\u98ce\u7684\u624b\u638c\uff0c\u67d4\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u591a\u6b47\u606f\u4e00\u4e0b\u5462\uff01\u4f60\u770b\uff01\u4f60\u7684\u773c\u795e\uff0c\u591a\u96be\u770b\u2026\u2026\u201d \u3000\u3000\u5979\u5fae\u5fae\u5598\u606f\u4e00\u4e0b\uff01\u5fc3\u80f8\u95f4\u4f46\u89c9\u6ee1\u662f\u67d4\u60c5\uff0c\u5fae\u7b11\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u4eca\u5929\u65e9\u4e0a\u6211\u4e00\u9192\u6765\uff0c\u770b\u4e0d\u89c1\u4f60\uff0c\u4f60\u4e0d\u77e5\u9053\u6211\u5fc3\u91cc\u6709\u591a\u96be\u53d7\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u7f9e\u6da9\u5730\u7b11\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u8fd8\u4ee5\u4e3a\u6628\u5929\u665a\u4e0a\u7684\u4e0d\u662f\u4f60\u5462\uff0c\u8fd8\u4ee5\u4e3a\u662f\u90a3\u4e2a\u8be5\u6b7b\u7684\u8427\u65e0\u3002\u5357\u54e5\u54e5\uff01\u628a\u4f60\u8138\u4e0a\u90a3\u4e2a\u9b3c \u4e1c\u897f\u63ed\u53bb\u597d\u4e0d\u597d\uff1f\u8ba9\u6211\u770b\u770b\u4f60\u672c\u6765\u7684\u6837\u5b50\u3002\u5509\u2014\u2014\u6211\u771f\u6068\u4f60\u8138\u4e0a\u90a3\u9b3c\u4e1c\u897f\uff0c\u5bb3\u6211\u62c5\u4e86\u597d\u534a\u5929 \u7684\u5fc3\u3002\u201d \u3000\u3000\u8fd9\u591a\u60c5\u800c\u6e29\u67d4\u51e0\u53e5\u8bdd\uff0c\u88ab\u8fd9\u75f4\u60c5\u7684\u5c11\u5973\u5a07\u5f31\u5730\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u662f\u5bf9\u4f0a\u98ce\u6765\u8bf4\uff0c\u8fd9\u51e0\u53e5\u8bdd\u5374\u6bd4\u6674\u5929\u9739\u96f3\uff0c\u8fd8\u8981\u60ca\u4eba\uff01\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u601d\u6f6e\uff0c\u53c8\u5168\u7136 \u53d8\u4e3a\u6df7\u6c8c\uff0c\u7406\u4e0d\u51fa\u4e00\u4e1d\u5934\u7eea\u6765\uff01 \u3000\u3000\u800c\u8427\u5357\u82f9\u5462\uff0c\u8fd9\u4e00\u65e0\u6240\u77e5\uff0c\u5df2\u5f00\u59cb\u61a7\u61ac\u7740\u672a\u6765\u5e78\u798f\u7684\u5c11\u5973\uff0c\u5374\u4ecd\u6e29\u67d4\u7684\u7b11\u7740\uff0c\u8f7b\u8f7b\u5730\u8bf4 \u7740\uff1a \u3000\u3000\u201c\u6628\u5929\u665a\u4e0a\u8981\u662f\u4f60\u4e0d\u6765\uff0c\u6211\u2026\u2026\u6211\u771f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u597d\u4e86\u3002\u201d \u3000\u3000\u5979\u53c8\u7f9e\u6da9\u5730\u5a07\u7b11\u4e00\u4e0b\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u4f60\u6765\u4e86\uff0c\u6211\u2026\u2026\u5b9e\u60f3\u4e0d\u5230\u4f60\u8fd9\u4e48\u2026\u2026\u574f\uff01\u5357\u54e5\u54e5\uff01\u4ece\u6b64\u4ee5\u540e\uff0c\u4e0d\u8981\u79bb\u5f00\u6211\u597d\u4e0d \u597d\uff01\u6211\u2026\u2026\u6211\u5df2\u7ecf\u662f\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5df2\u4ece\u6df7\u6c8c\u7684\u601d\u6f6e\u91cc\uff0c\u6574\u7406\u51fa\u4e00\u4e2a\u5934\u7eea\u6765\uff1b\u4ed6\u5df2\u4ece\u5979\u7684\u8bdd\u4e2d\uff0c\u731c\u51fa\u4e86\u6628\u591c\u7a76\u7adf\u662f\u600e\u4e48 \u56de\u4e8b\uff0c\u4f46\u662f\u4ed6\u5374\u4e0d\u5fcd\u76f8\u4fe1\u8fd9\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e00\u5207\u5bf9\u8fd9\u591a\u60c5\u5c11\u5973\u8bf4\u6765\uff0c\u662f\u591a\u4e48\u6b8b\u9177\uff01 \u3000\u3000\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u4ed6\u8dea\u4e86\u4e0b\u6765\uff0c\u8dea\u5728\u8fd9\u591a\u60c5\u7684\u5c11\u5973\u9762\u524d\uff0c\u5589\u5934\u4e5f\u54fd\u54bd\u7740\uff0c\u8bf4\u4e0d\u51fa\u4e86\u8bdd\u6765\u3002 \u3000\u3000\u8427\u5357\u82f9\u5a07\u8eaf\u8f7b\u8f7b\u626d\u52a8\u4e00\u4e0b\uff0c\u4e0d\u4f9d\u7740\u9053\uff1a \u3000\u3000\u201c\u4f60\u770b\u4f60\uff01\u6211\u53eb\u4f60\u505a\u7684\u4e8b\u4f60\u90fd\u4e0d\u4f9d\u6211\uff0c\u628a\u8138\u4e0a\u90a3\u9b3c\u4e1c\u897f\u62ff\u4e0b\u6765\u561b\uff01\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u5728\u5979\u90a3\u4f24\u75d5\u6ee1\u5e03\u7684\u8138\u4e0a\uff0c\u8f6c\u52a8\u4e86\u4e00\u4e0b\uff0c\u5fc3\u4e2d\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u832b\u7136\u5c06\u9762\u4e0a\u8fd9\u5f20\u9020\u6210 \u65e0\u6570\u4e8b\u7aef\u7684\u9762\u76ee\uff0c\u63ed\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e8e\u662f\u4e00\u5f20\u75db\u82e6\u800c\u626d\u66f2\u7684\u8138\uff0c\u4fbf\u5448\u73b0\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6b64\u523b\u5728\u4ed6\u5fc3\u4e2d\u6df7\u6dc6\u7740\u4e00\u79cd\u96be\u8a00\u7684\u60c5\u611f\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u5206\u6790\u4e0d\u51fa\u662f\u60b2\u75db\uff0c\u601c\u60dc\uff0c\u6291\u6216\u662f\u6124 \u6068\uff01 \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u53c8\u600e\u5fcd\u5fc3\u8bf4\u51fa\u6628\u665a\u7684\u201c\u4ed6\u201d\uff0c\u5e76\u4e0d\u662f\u81ea\u5df1\u3002 \u3000\u3000\u53c8\u600e\u5fcd\u5fc3\u8ba9\u8fd9\u591a\u60c5\u800c\u53ef\u601c\u7684\u5c11\u5973\uff0c\u5728\u6628\u591c\u672a\u5e72\u7684\u6cea\u75d5\u4e0a\uff0c\u53c8\u6dfb\u4e0a\u4e00\u9053\u65b0\u7684\u3002 \u3000\u3000\u4f55\u51b5\u4ee5\u4ed6\u591a\u5e74\u95ef\u6c64\u6c5f\u6e56\u7684\u7ecf\u9a8c\uff0c\u4ed6\u77e5\u9053\u5979\u9762\u4e0a\u7684\u8fd9\u4e9b\u4f24\u75d5\uff0c\u7eb5\u7136\u75ca\u6108\uff0c\u5374\u4e5f\u4e0d\u4f1a\u5e73\u590d\u7684 \u4e86\u3002 \u3000\u3000\u5f53\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u53d1\u73b0\u81ea\u5df1\u7684\u5bb9\u8c8c\uff0c\u5df2\u4e0d\u518d\u7f8e\u4e3d\u7684\u65f6\u5019\uff0c\u90a3\u4e48\u5979\u5185\u5fc3\u7684\u60b2\u75db\uff0c\u5df2\u662f\u8db3 \u591f\u4ee4\u5979\u61be\u6068\u7ec8\u751f\u7684\u4e86\uff0c\u4ed6\u53c8\u4f55\u5fcd\u518d\u4e3a\u5979\u52a0\u4e0a\u4e00\u5206\u66f4\u5f3a\u70c8\u7684\u75db\u82e6\uff01 \u3000\u3000\u5728\u4ed6\u63ed\u53bb\u81ea\u5df1\u9762\u76ee\u7684\u8fd9\u4e00\u5239\u90a3\uff0c\u4ed6\u5df2\u81ea\u51b3\u5b9a\uff0c\u5b81\u53ef\u81ea\u5df1\u5fcd\u53d7\u4e00\u5207\uff0c\u5374\u7edd\u4e0d\u8ba9\u8fd9\u591a\u60c5\u7684\u5c11 \u5973\uff0c\u518d\u53d7\u5c48\u8fb1\u4e86\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u8ba4\u4e3a\u81ea\u5df1\u8fd9\u51b3\u5b9a\uff0c\u662f\u5168\u7136\u6b63\u786e\uff0c\u800c\u522b\u65e0\u9009\u62e9\u7684\u3002\u81ea\u5df1\u7eb5\u7136\u75db\u82e6\uff0c\u8fd9\u5c11\u5973\u5bf9\u81ea\u5df1\u7684 \u8fd9\u4e00\u4efd\u8db3\u4ee5\u611f\u52a8\u5929\u5730\u7684\u771f\u60c5\uff0c\u5374\u5df2\u591f\u5f25\u8865\u4e00\u5207\u4e86\uff01 \u3000\u3000\u4e8e\u662f\u4ed6\u66f4\u6df1\u6df1\u5f2f\u4e0b\u8170\uff0c\u5e26\u7740\u4e00\u4efd\u542b\u6cea\u7684\u7b11\u5bb9\uff0c\u4fef\u89c6\u7740\u5979\uff0c\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u4ee5\u540e\u4e0d\u8981\u80e1\u601d\u4e71\u60f3\u4e86\uff0c\u6628\u5929\u665a\u4e0a\u4e0d\u662f\u6211\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4ed6\u770b\u5230\u5979\u9762\u4e0a\u6cdb\u8d77\u82b1\u822c\u7684\u7b11\uff0c\u8fd9\u7b11\u4f7f\u5f97\u5979\u9762\u4e0a\u4e11\u6076\u7684\u4f24\u75d5\uff0c\u90fd\u4f3c\u4e4e\u53d8\u5f97\u65e0\u6bd4\u7684\u7f8e\u4e3d\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u63a5\u7740\u5f80\u4e0b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u5728\u8fd9\u91cc\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u95ed\u8d77\u773c\u775b\u6765\uff0c\u7b49\u4e00\u4f1a\u6211\u5c31\u628a\u4f60\u5e26\u4e0b\u5c71\u3002\u5509\u2014\u2014\u4eca\u5929\u65e9\u4e0a\u2026\u2026 \u4eca\u5929\u65e9\u4e0a\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u90a3\u4e48\u65e9\u9192\u6765\uff0c\u6240\u4ee5\u6211\u624d\u6765\u8fd9\u91cc\u627e\u4e2a\u670b\u53cb\uff0c\u5374\u60f3\u4e0d\u5230\u53d1\u751f\u4e86\u8fd9\u4e9b \u4e8b\u2026\u2026\u201d \u3000\u3000\u4ed6\u627f\u53d7\u4e86\u65e0\u6bd4\u7684\u75db\u82e6\uff0c\u5c06\u4e00\u4efd\u5e76\u975e\u81ea\u5df1\u5e94\u8be5\u627f\u62c5\u7684\u7f6a\u5b7d\uff0c\u627f\u5f53\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u6b64\u523b\u4ed6\u53ea\u8981\u80fd\u770b\u5230\u5979\u800c\u4e0a\u6cdb\u51fa\u7b11\u5bb9\uff0c\u90a3\u4e48\u4e5f\u5c31\u662f\u4ed6\u81ea\u5df1\u5728\u7b11\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e3a\u4e00\u4e2a\u81ea\u5df1\u6240\u6df1\u75db\u6076\u7edd\u7684\u4ec7\u4eba\uff0c\u627f\u5f53\u4e86\u8fd9\u4efd\u672c\u5df2\u4f7f\u4ed6\u4e07\u5206\u75db\u82e6\u7684\u7f6a\u5b7d\uff0c\u8fd9\u53c8\u662f\u4e00 \u79cd\u591a\u4e48\u6df1\u9083\u7684\u75db\u82e6\u54e9\uff01 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u516d\u5341\u4e8c\u7ae0\u3000\u6069\u6028\u96be\u5206>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u4e8c\u7ae0\u3000\u6069\u6028\u96be\u5206 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u4ed6\u80cc\u540e\u7a81\u5730\u4f20\u6765\u4e00\u58f0\u9634\u68ee\u5165\u9aa8\u7684\u51b7\u7b11\u3002 \u3000\u3000\u4f0a\u98ce\u7729\u7136\u56db\u987e\uff0c\u4e00\u5f20\u6beb\u65e0\u8868\u60c5\u7684\u201c\u94c1\u9762\u201d\uff0c\u6b63\u4ee5\u65e0\u6bd4\u68ee\u51b7\u7684\u76ee\u5149\uff0c\u5728\u51dd\u89c6\u7740\u4ed6\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u53ea\u89c1\u8fd9\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u5634\u89d2\u7275\u52a8\u4e00\u4e0b\uff0c\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u662f\u4f60\uff0c\u771f\u60f3\u4e0d\u5230\uff0c\u8001\u592b\u4e00\u751f\u95ef\u8361\uff0c\u5374\u6559\u4f60\u9a97\u4e86\u4e0d\u5c11\u65f6\u5019\u3002\u201d \u3000\u3000\u4f0a\u98ce\u53f3\u624b\u7d27\u63e1\u7740\u65b9\u81ea\u9762\u4e0a\u63ed\u4e0b\u7684\u9762\u76ee\uff0c\u5168\u795e\u8b66\u6212\u7740\u3002 \u3000\u3000\u90a3\u8427\u5357\u82f9\u6115\u7136\u7741\u5f00\u773c\u6765\uff0c\u89c1\u5230\u8fd9\u9762\u5e26\u5bd2\u610f\u7684\u4e07\u5929\u840d\uff0c\u5fc3\u4e2d\u4ea6\u4e3a\u4e4b\u5927\u60ca\u3002 \u3000\u3000\u867d\u7136\u5979\u4e0d\u8ba4\u8bc6\u4e07\u5929\u840d\uff0c\u4f46\u89c1\u4e86\u8fd9\u79cd\u60c5\u72b6\uff0c\u5374\u4e5f\u77e5\u9053\u8fd9\u4eba\u5fc5\u5b9a\u5bf9\u4f0a\u98ce\u6709\u7740\u654c\u610f\uff0c\u56e0\u4e4b\u5979\u4e00 \u6491\u53cc\u8098\uff0c\u5f3a\u81ea\u6323\u624e\u7740\u722c\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u5fae\u4e00\u633a\u8170\uff0c\u8eab\u8eaf\u5df2\u7b14\u76f4\u5730\u7ad9\u5728\u5730\u4e0a\u3002\u4ed6\u867d\u5df2\u77e5\u9053\u2014\u2014\u6b64\u523b\u8fd9\u4e07\u5929\u840d\u5df2\u8ba4\u51fa\u81ea\u5df1\u7684\u672c \u6765\u9762\u76ee\uff0c\u5fc5\u5b9a\u4f1a\u6709\u9ebb\u70e6\uff0c\u4f46\u4ed6\u4ecd\u5b89\u6170\u7740\u5979\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u6ca1\u5173\u7cfb\uff0c\u4f60\u6b47\u7740\u597d\u4e86\u3002\u201d \u3000\u3000\u8bed\u72b9\u672a\u7adf\uff0c\u90a3\u4e07\u5929\u840d\u5df2\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e0d\u8fc7\u8001\u592b\u4e5f\u7684\u786e\u6709\u4e9b\u5947\u602a\uff0c\u4f60\u8fd9\u5c0f\u5b50\u96be\u9053\u662f\u732a\u6cb9\u6726\u4e86\u5fc3\uff0c\u5374\u5c06\u8001\u592b\u4ece\u5c71\u7a9f\u91cc\u6551\u51fa\u6765\u505a \u4ec0\u4e48\uff01\u201d \u3000\u3000\u4f0a\u98ce\u540e\u9000\u534a\u6b65\uff0c\u6321\u5728\u8427\u5357\u82f9\u8eab\u524d\uff0c\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\u5730\u77aa\u5728\u4e07\u5929\u840d\u7684\u4e00\u53cc\u624b\u4e0a\uff0c\u7a81\u5730\u4ef0\u5929\u957f \u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u7b11\uff0c\u5374\u4e0d\u7981\u4f7f\u5f97\u90a3\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u9762\u4e0a\uff0c\u4e5f\u5fae\u5fae\u53d8\u8272\u3002 \u3000\u3000\u4f0a\u98ce\u7b11\u58f0\u4e00\u987f\uff0c\u795e\u8272\u53c8\u590d\u61d4\u7136\u3002\u4ed6\u5728\u8fd9\u7a81\u6765\u7684\u957f\u7b11\u4e4b\u540e\uff0c\u7adf\u8fd8\u662f\u4e00\u8a00\u4e0d\u53d1\uff0c\u751f\u50cf\u662f\u4ed6\u65b9 \u624d\u7684\u8fd9\u6b21\u957f\u7b11\uff0c\u6839\u672c\u662f\u6beb\u65e0\u610f\u4e49\u4f3c\u7684\u3002 \u3000\u3000\u4e07\u5929\u840d\u76ee\u5149\u4e00\u51db\uff0c\u4f0a\u98ce\u76ee\u5149\u51dd\u4f4f\u3002 \u3000\u3000\u90a3\u77e5\u5c31\u5728\u6b64\u523b\uff0c\u7edd\u5d16\u8fb9\u7a81\u5730\u4e00\u58f0\u5a07\u547c\uff0c\u4e00\u4e2a\u7fe0\u7eff\u8863\u88f3\u7684\u4eba\u5f71\uff0c\u7fe9\u7136\u63a0\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u7fe0\u8272\u4eba\u5f71\uff0c\u811a\u5c16\u4e00\u6cbe\u5730\u9762\uff0c\u7acb\u523b\u6ed1\u5230\u5979\u7239\u7239\u8eab\u4fa7\uff0c\u5f77\u4f5b\u662f\u751f\u6015\u5979\u7239\u7239\u731d\u7136\u51fa\u624b\u4f3c\u7684\u3002 \u3000\u3000\u4f46\u662f\u7b49\u5230\u5979\u4e00\u53cc\u4fcf\u76ee\uff0c\u8f6c\u5230\u4f0a\u98ce\u8138\u4e0a\u65f6\uff0c\u5979\u5374\u53c8\u4e0d\u7981\u4e3a\u4e4b\u60ca\u547c\u51fa\u58f0\u6765\uff0c\u4f38\u51fa\u4e00\u53ea\u6625\u8471\u7389 \u6307\uff0c\u6307\u7740\u4f0a\u98ce\uff0c\u60ca\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u8fd9\u662f\u600e\u4e48\u56de\u4e8b.\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u5de6\u624b\u5fae\u626c\uff0c\u5c06\u624b\u4e2d\u7684\u4eba\u76ae\u9762\u76ee\uff0c\u8fce\u98ce\u62db\u5c55\u4e86\u4e00\u4e0b\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u524d\u8f88\uff01\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8001\u524d\u8f88\u5fc3\u4e2d\u60f3\u4e5f\u77e5\u9053\u4e86\u3002\u5c0f\u9119\u4e0e\u8001\u524d\u8f88\u672c\u65e0\u6069\u6028\uff0c\u6628\u2026\u2026 \u4eca\u6668\u6253\u6270\u4e86\u8001\u524d\u8f88\uff0c\u65e5\u540e\u5c0f\u9119\u5fc5\u5b9a\u6709\u8865\u62a5\u4e4b\u5904\u3002\u81f3\u4e8e\u5c0f\u9119\u4e3a\u4ec0\u4e48\u8981\u6234\u4e0a\u8fd9\u5f20\u9762\u76ee\uff0c\u60f3\u4eba\u751f\u672c \u5982\u6e38\u620f\uff0c\u8001\u524d\u8f88\u4ea6\u662f\u8fbe\u4eba\uff0c\u5c0f\u9119\u53c8\u4f55\u987b\u89e3\u91ca\u3002\u53ea\u662f\u5c0f\u9119\u5fc5\u987b\u58f0\u8a00\u7684\uff0c\u5c31\u662f\u5c0f\u9119\u5bf9\u8001\u524d\u8f88\u7edd\u65e0 \u620f\u5f04\u4e4b\u610f\u2026\u2026\u201d \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u51b7\u53f1\u4e00\u58f0\uff0c\u4e00\u53cc\u9e70\u76ee\uff0c\u76ef\u5728\u4f0a\u98ce\u800c\u4e0a\uff0c\u50cf\u662f\u8981\u770b\u900f\u8fd9\u5c11\u5e74\u5fc3\u4e2d\u7a76\u7adf\u6709\u4ec0 \u4e48\u79d8\u5bc6\u4f3c\u7684\u3002 \u3000\u3000\u76f4\u81f3\u6b64\u523b\uff0c\u4ed6\u8fd8\u4e0d\u77e5\u9053\uff0c\u6b64\u523b\u7ad9\u5728\u4ed6\u5bf9\u9762\u7684\u5c11\u5e74\uff0c\u5e76\u4e0d\u662f\u5728\u65e0\u91cf\u5c71\u5dc5\u4ece\u201c\u6b66\u66f2\u201d\u79d8\u7a9f\u91cc \u6551\u51fa\u81ea\u5df1\u7684\u4eba\u2014\u2014\u8fd9\u539f\u662f\u4ef6\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u5947\u602a\uff0c\u4f46\u9762\u4e0a\u5374\u4ecd\u68ee\u51b7\u5982\u5e38\uff0c\u51b7\u53f1\u7740\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u4e00\u751f\u4e4b\u4e2d\uff0c\u5feb\u610f\u6069\u4ec7\uff0c\u4ece\u672a\u6709\u8fc7\u4e00\u4ef6\u5f53\u673a\u4e0d\u65ad\u7684\u4e8b\u3002\u4f46\u8001\u592b\u4e0e\u4f60\uff0c\u5374\u662f\u6069\u6028\u96be \u5206\uff0c\u6309\u7406\u6211\u82e5\u65e0\u4f60\u4e4b\u76f8\u6551\uff0c\u6211\u65e9\u5df2\u846c\u8eab\u65e0\u91cf\u5c71\u5dc5\u90a3\u79d8\u7a9f\u91cc\uff1b\u4f46\u8001\u592b\u4e4b\u6240\u4ee5\u88ab\u5173\u5165\u90a3\u91cc\uff0c\u5374\u4e5f \u662f\u88ab\u4f60\u8fd9\u5c0f\u5b50\u5bb3\u7684\u3002\u201d \u3000\u3000\u7fe0\u88f3\u5c11\u5973\u4e07\u8679\uff0c\u77aa\u7740\u5927\u773c\u775b\uff0c\u5728\u5979\u7239\u7239\u8eab\u4fa7\uff0c\u672c\u5df2\u6115\u4e86\u8bb8\u4e45\uff1a\u6b64\u523b\u542c\u4e86\u5979\u7239\u7239\u7684\u8bdd\uff0c\u5fc3 \u91cc\u5374\u8d8a\u53d1\u7cca\u6d82\u4e86\uff0c\u4e0d\u77e5\u9053\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u4f0a\u98ce\u9762\u4e0a\u5fae\u7b11\u4e00\u4e0b\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u90a3\u77e5\u90a3\u4e07\u5929\u840d\u5374\u53c8\u4e00\u6446\u624b\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u6709\u6069\u62a5\u6069\uff0c\u6709\u4ec7\u62a5\u4ec7\uff0c\u672c\u662f\u8001\u592b\u7ec8\u751f\u5949\u884c\u7684\u516b\u4e2a\u5b57\uff0c\u4f46\u6b64\u523b\u6211\u82e5\u62a5\u4f60\u7684\u4ec7\uff0c\u5c31\u65e0\u6cd5\u62a5 \u4f60\u7684\u6069\uff0c\u82e5\u8001\u592b\u5148\u62a5\u4f60\u7684\u6069\uff0c\u518d\u5c06\u4f60\u6740\u4e86\uff0c\u5374\u53c8\u600e\u4e48\u80fd\u7b97\u5df2\u62a5\u8fc7\u4f60\u7684\u6069\u5462\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u4e00\u4f38\u5927\u59c6\u6307\uff0c\u6697\u8d5e\u8fd9\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\uff0c\u867d\u7136\u4e00\u751f\u884c\u4e8b\uff0c\u5e76\u4e0d\u5149\u660e\u78ca\u843d\uff1a\u4f46\u82e5\u4ee5 \u8fd9\u201c\u6069\u201d\u201c\u4ec7\u201d\u4e24\u5b57\u800c\u8a00\uff0c\u4ed6\u5374\u4ecd\u7136\u4e0d\u5931\u662f\u4e2a\u4e08\u592b\u3002 \u3000\u3000\u987b\u77e5\u6b66\u6797\u4e2d\u4eba\uff0c\u8861\u91cf\u4eba\u6027\u7684\u5c3a\u5ea6\uff0c\u672c\u5c31\u548c\u666e\u901a\u4eba\u7edd\u4e0d\u76f8\u540c\uff0c\u5c24\u5176\u8fd9\u201c\u6069\u6028\u5206\u660e\u201d\u56db\u5b57\uff0c \u66f4\u662f\u88ab\u6b66\u6797\u4e2d\u4eba\u6700\u770b\u5f97\u91cd\u7684\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u6b64\u523b\u7adf\u771f\u7684\u50cf\u662f\u5341\u5206\u56f0\u6270\u3002 \u3000\u3000\u4f0a\u98ce\u51b7\u51b7\u5730\u6ce8\u89c6\u7740\u4ed6\uff0c\u5fc3\u91cc\u5374\u4e5f\u4ea4\u6218\u7740\uff0c\u4e0d\u77e5\u9053\u8be5\u4e0d\u8be5\u5c06\u5728\u65e0\u91cf\u5c71\u5dc5\u6551\u4ed6\u51fa\u7a9f\uff0c\u662f\u53e6\u6709 \u5176\u4eba\u8fd9\u4ef6\u4e8b\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e00\u9635\u5c71\u98ce\u5439\u6765\uff0c\u8427\u5357\u82f9\u66f4\u9760\u8fd1\u4e86\u4ed6\u4e9b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u82e5\u4e00\u8bf4\u51fa\u6b64\u4e8b\uff0c\u8fd9\u4e07\u5929\u840d\u60f3\u5fc5\u4e00\u5b9a\u7acb\u523b\u4f1a\u5411\u81ea\u5df1\u52a8\u624b\uff0c\u800c\u81ea\u5df1\u81ea\u5fd6\u529f\u529b\uff0c\u5374 \u975e\u6b64\u4eba\u4e4b\u654c\uff0c\u90a3\u4e48\u4e0d\u4f46\u81ea\u5df1\u6b64\u523b\u4fbf\u7acb\u523b\u547d\u6bd5\u4e8e\u6b64\uff0c\u7ad9\u5728\u81ea\u5df1\u8eab\u540e\u7684\u8427\u5357\u82f9\uff0c\u5374\u4e5f\u4e07\u4e07\u53d7\u4e0d\u4f4f \u8fd9\u6253\u51fb\u7684\u3002 \u3000\u3000\u4f46\u662f\u4e00\u4e2a\u9876\u5929\u7acb\u5730\u7684\u7537\u6c49\u5b50\uff0c\u5374\u53c8\u600e\u80fd\u5047\u5192\u522b\u4eba\uff0c\u6765\u627f\u53d7\u6069\u60e0\u5462\uff1f\u4f55\u51b5\u8fd9\u4eba\u66fe\u7ecf\u7ed9\u8fc7\u81ea \u5df1\u90a3\u4e48\u6df1\u523b\u800c\u5f3a\u70c8\u7684\u5c48\u8fb1\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u53cd\u624b\u63e1\u4f4f\u8427\u5357\u82f9\u7684\u624b\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e07\u5929\u840d\uff01\u6211\u4e0d\u59a8\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u4ece\u65e0\u91cf\u5c71\u5dc5\u7684\u79d8\u7a9f\u4e2d\u6551\u51fa\u4f60\u7684\uff0c\u5e76\u4e0d\u662f\u6211\u3002\u4f60\u6211\u4e4b\u95f4\uff0c \u867d\u7136\u672c\u65e0\u6069\u6028\uff0c\u4f46\u7ec6\u8bf4\u8d77\u6765\uff0c\u5374\u662f\u6709\u6028\u65e0\u6069\uff0c\u4f60\u82e5\u60f3\u5bf9\u6211\u590d\u4ec7\uff0c\u53ea\u7ba1\u52a8\u624b\u5c31\u662f\u4e86\uff0c\u7528\u4e0d \u7740\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u5374\u5df2\u88ab\u4e07\u5929\u840d\u7684\u957f\u7b11\u4e4b\u58f0\u6253\u65ad\u4e86\u3002 \u3000\u3000\u201c\u6709\u9aa8\u6c14\uff01\u6709\u9aa8\u6c14\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\u957f\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u4f60\u4e5f\u672a\u514d\u5c06\u8001\u592b\u770b\u5f97\u592a\u6613\u611a\u5f04\u4e86\uff0c\u8001\u592b\u96be\u9053\u8fd8\u4f1a\u76f8\u4fe1\u4f60\u8fd9\u9b3c\u8bdd\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u7565\u4e3a\u4e00\u987f\uff0c\u4e07\u8679\u5df2\u6084\u6084\u501a\u5230\u4ed6\u8eab\u4e0a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7239\u7239\uff01\u4f60\u65e2\u7136\u53c8\u4e0d\u80fd\u62a5\u4ec7\uff0c\u53c8\u4e0d\u62a5\u80fd\u6069\uff0c\u90a3\u4f60\u4ec0\u4e48\u90fd\u4e0d\u62a5\uff0c\u4e0d\u5c31\u662f\u7ed3\u4e86\u5417\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\u76ee\u5149\u51db\u7136\u5730\u5728\u5979\u5973\u513f\u9762\u4e0a\u4e00\u8f6c\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u6697\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u77e5\u5973\u83ab\u82e5\u7236\u201d\uff0c\u4ed6\u5df2\u770b\u51fa\u81ea\u5df1\u7684\u5973\u513f\uff0c\u7adf\u5bf9\u4eba\u5bb6\u751f\u4e86\u60c5\u612b\u3002 \u3000\u3000\u8fd9\u867d\u662f\u81ea\u5df1\u672c\u6765\u6240\u76fc\u671b\uff0c\u751a\u81f3\u662f\u81ea\u5df1\u6240\u8ba1\u5212\u7684\u4e8b\uff0c\u4f46\u6b64\u523b\u5374\u53c8\u6210\u4e86\u81ea\u5df1\u7684\u56f0\u607c\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u6570\u8f6c\uff0c\u6b63\u81ea\u59d4\u51b3\u4e0d\u4e0b\u4e2d\uff0c\u7a81\u5730\u4e00\u4e2a\u5ff5\u5934\u95ea\u8fc7\uff0c\u4e8e\u662f\u4ed6\u53c8\u4e00\u6446\u624b\uff0c\u963b\u4f4f\u4e86\u4f0a\u98ce\u5f20\u5634 \u8981\u8bf4\u7684\u8bdd\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u5fc5\u518d\u8bf4\u8bdd\u4e86\uff0c\u6b64\u523b\u6211\u5fc3\u610f\u5df2\u51b3\u2026\u2026\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u4f38\u51fa\u98df\u4e2d\u4e8c\u6839\u624b\u6307\u6765\uff0c\u63a5\u7740\u5f80\u4e0b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8001\u5929\u4e00\u751f\u6069\u6028\u5206\u660e\uff0c\u5bf9\u4f60\u4e5f\u7edd\u4e0d\u4f1a\u505a\u51fa\u5fd8\u6069\u8d1f\u4e49\u7684\u4e8b\u6765\uff0c\u53ef\u4e5f\u4e0d\u80fd\u6709\u4ec7\u4e0d\u62a5\uff0c\u6b64\u523b\u8001 \u592b\u653e\u4e0b\u4e24\u6761\u8def\u7ed9\u4f60\u8d70\uff0c\u4f60\u53ef\u969c\u4fbf\u9009\u62e9\u4e00\u6837\u3002\u201d \u3000\u3000\u4f0a\u98ce\u50b2\u7136\u4e00\u7b11\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u82e5\u662f\u6211\u4e24\u6761\u8def\u5168\u4e0d\u8d70\u5462\uff1f\u201d \u3000\u3000\u90a3\u77e5\u4e07\u5929\u840d\u6839\u672c\u50cf\u662f\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u81ea\u987e\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u7b2c\u4e00\u6761\u8def\uff0c\u8001\u592b\u601c\u4f60\u8fd8\u662f\u4e2a\u6c49\u5b50\uff0c\u4f60\u82e5\u62dc\u6211\u4e3a\u5e08\uff0c\u90a3\u4e48\u4f60\u6211\u4ee5\u524d\u7684\u6069\u6028\uff0c\u4fbf\u4e00\u7b14\u52fe \u6d88\uff0c\u4f60\u8fd8\u53ef\u4ee5\u4ece\u8001\u592b\u5904\u5b66\u5f97\u8bb8\u591a\u7edd\u827a\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\uff1a \u3000\u3000\u201c\u81f3\u4e8e\u90a3\u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u8001\u592b\u4e5f\u53ef\u548c\u4f60\u4e00\u9f50\u53c2\u7814\u3002\u201d \u3000\u3000\u4e07\u8679\u5fc3\u91cc\u6697\u6697\u611f\u5fbc\uff0c\u77e5\u9053\u5979\u7236\u4eb2\u8fd9\u6761\u8def\uff0c\u662f\u5b8c\u5168\u4e3a\u7740\u81ea\u5df1\u8bf4\u7684\u3002 \u3000\u3000\u5979\u4e00\u53cc\u5999\u76ee\uff0c\u4fbf\u5173\u5207\u5730\u843d\u5230\u4f0a\u98ce\u8eab\u4e0a\uff0c\u53ea\u671b\u4ed6\u5634\u91cc\u8bf4\u51fa\u4e00\u4e2a\u201c\u597d\u201d\u5b57\u6765\u3002 \u3000\u3000\u90a3\u77e5\u4f0a\u98ce\u51b7\u54fc\u4e00\u58f0\uff0c\u60f3\u4e5f\u4e0d\u60f3\u5c31\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e14\u8bf4\u51fa\u7b2c\u4e8c\u6761\u8def\u6765\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u624b\u638c\u4e0a\u7684\u4f24\u75d5\uff0c\u867d\u662f\u5176\u75db\u6f88\u9aa8\uff0c\u4f46\u5979\u4ecd\u6e29\u67d4\u5730\u63e1\u4e86\u63e1\u4ed6\u7684\u624b\uff0c\u82b3\u5fc3\u4e4b\u4e2d\uff0c\u5927\u4e3a\u8d5e \u8bc4\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929\u840d\uff0c\u5374\u4e0d\u7981\u9762\u76ee\u7acb\u53d8\uff0c\u5389\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u7b2c\u4e8c\u6761\u8def\u4e48\uff01\u2014\u2014\u8001\u592b\u6614\u5e74\u4e3a\u4e86\u5efa\u6b64\u5bc6\u9601\uff0c\u66fe\u5c06\u8fd9\u897f\u6881\u5c71\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u5168\u90e8\u63a2\u67e5\u4e86 \u4e00\u904d\uff0c\u624d\u5bfb\u7740\u8fd9\u4e2a\u6240\u5728\u3002\u201d \u3000\u3000\u4ed6\u8bed\u5fae\u987f\uff0c\u4f0a\u98ce\u5fc3\u91cc\u5374\u4e0d\u7981\u5947\u602a\uff0c\u8fd9\u4e07\u5929\u840d\u600e\u5730\u5728\u6b64\u523b\u7adf\u8bf4\u8d77\u95f2\u7bc7\u6765\u4e86\uff01 \u3000\u3000\u5374\u542c\u4e07\u5929\u840d\u5df2\u51b7\u7b11\u63a5\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u5728\u6211\u53d1\u73b0\u8fd9\u5904\u6240\u5728\u4e4b\u524d\uff0c\u6211\u5374\u5df2\u5230\u5c71\u9634\u5904\u5bfb\u5f97\u4e00\u5904\u5c71\u6d1e\uff0c\u8fd9\u5904\u5c71\u6d1e\uff0c\u4e5f\u548c\u65e0\u91cf\u5c71 \u5dc5\u7684\u79d8\u7a9f\u4e00\u6837\uff0c\u53ea\u6709\u4e00\u6761\u901a\u8def\u3002\u6b64\u523b\u8001\u592b\u5c31\u5c06\u4f60\u9001\u5230\u8fd9\u5c71\u6d1e\u91cc\uff0c\u5916\u9762\u7528\u5de8\u77f3\u5c06\u4f60\u9501\u5728\u91cc\u9762\uff0c \u4e00\u4e2a\u6708\u5185\uff0c\u4f60\u82e5\u80fd\u9003\u51fa\u8fd9\u5c71\u6d1e\uff0c\u90a3\u4f60\u6211\u4e4b\u95f4\uff0c\u6069\u6028\u4ea6\u53ef\u4e00\u7b14\u52fe\u9500\uff0c\u5426\u5219\u4e00\u6708\u4e4b\u540e\uff0c\u4f60\u5728\u90a3\u5c71 \u6d1e\u4e2d\u82e5\u8fd8\u672a\u6b7b\uff0c\u8001\u592b\u4e5f\u4f1a\u5c06\u4f60\u653e\u51fa\u6765\uff0c\u4e0d\u8fc7\u6b64\u540e\u4f60\u5bf9\u8001\u592b\u7684\u8bdd\uff0c\u5374\u534a\u53e5\u4e5f\u4e0d\u80fd\u8fdd\u80cc\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5634\u89d2\u8f7b\u8511\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u5374\u89c1\u8fd9\u4e07\u5929\u840d\u76ee\u5149\u5982\u5200\uff0c\u51dd\u89c6\u81ea\u5df1\uff0c\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u6761\u8def\u4f60\u82e5\u5168\u4e0d\u63a5\u53d7\u7684\u8bdd\uff0c\u90a3\u4e48\u4f60\u5c31\u4f11\u602a\u8001\u592b\u624b\u8fa3\u4e86\u3002\u201d \u3000\u3000\u4e07\u8679\u8f7b\u8f7b\u4e00\u626f\u4ed6\u7239\u7239\u7684\u8863\u8896\uff0c\u5a07\u58f0\u9053\uff1a \u3000\u3000\u201c\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u592a\u957f\u4e86\u5427\uff01\u7239\u7239\uff0c\u4f60\u8001\u4eba\u5bb6\u7b49\u5f97\u53ca\u5417\uff1f\u201d \u3000\u3000\u4e07\u5929\u840d\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5341\u5e74\u4e4b\u957f\uff0c\u5728\u4f60\u7239\u7239\u773c\u4e2d\uff0c\u4e5f\u4e0d\u8fc7\u5f39\u6307\u95ee\u8fc7\uff0c\u4f55\u51b5\u77ed\u77ed\u7684\u4e00\u4e2a\u6708\u54e9\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u8f6c\u5411\u4f0a\u98ce\uff1a \u3000\u3000\u201c\u8fd9\u4e00\u4e2a\u6708\u4e4b\u5185\uff0c\u8001\u592b\u4e00\u5b9a\u66ff\u4f60\u5b88\u4f4f\u6d1e\u95e8\uff0c\u9664\u975e\u8001\u592b\u6b7b\u4e86\uff0c\u5426\u5219\u666e\u5929\u4e4b\u4e0b\uff0c\u4e0d\u8981\u6709\u4e00\u4eba \u60f3\u8fdb\u6b64\u6d1e\uff0c\u4e5f\u4e0d\u8981\u6709\u4e00\u4eba\u60f3\u5f97\u5230\u6b64\u523b\u5728\u4f60\u8eab\u4e0a\u7684\u201c\u5929\u661f\u79d8\u7b08\u201d\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6697\u4e2d\u5fae\u54c2\uff0c\u77e5\u9053\u8fd9\u4e07\u5929\u840d\u867d\u7136\u8868\u9762\u88c5\u5f97\u5927\u65b9\uff0c\u5176\u5b9e\u5fc3\u4e2d\u8fd8\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u8fd9\u672c\u5929\u661f\u79d8 \u7b08\u3002 \u3000\u3000\u81ea\u5df1\u4e00\u6708\u4e4b\u540e\uff0c\u82e5\u662f\u6b7b\u4e86\uff0c\u90a3\u4e48\u8fd9\u672c\u5929\u661f\u79d8\u7b08\u81ea\u7136\u5c31\u5f52\u4ed6\u6240\u6709\uff1b\u81ea\u5df1\u82e5\u662f\u4e0d\u6b7b\uff0c\u90a3\u4e48\u81ea \u5df1\u4e00\u751f\u4e4b\u4e2d\uff0c\u5c31\u5f97\u542c\u4ed6\u7684\u5dee\u9063\uff0c\u8fd9\u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u8fd8\u4e0d\u662f\u7b49\u4e8e\u4ed6\u7684\u4e00\u6837\uff1f \u3000\u3000\u4ed6\u65e2\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u90a3\u4e48\u4ed6\u53e3\u4e2d\u7684\u5c71\u6d1e\uff0c\u5fc5\u5b9a\u5341\u5206\u5e7d\u79d8\uff0c\u662f\u81ea\u5df1\u4e07\u4e07\u9003\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u4f46\u662f\u81ea\u5df1\u82e5\u4e0d\u63a5\u53d7\u4ed6\u7684\u6761\u4ef6\uff0c\u90a3\u4e48\u8bf4\u4e0d\u5b9a\u81ea\u5df1\u7acb\u65f6\u4fbf\u5f97\u8840\u6e85\u6b64\u5904\uff0c\u800c\u4e14\u6e85\u7684\u8fd8\u4e0d\u6b62\u662f\u4ed6 \u4e00\u4eba\u7684\u8840\uff0c\u8fd8\u5305\u62ec\u4e86\u8427\u5357\u82f9\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u72b9\u7591\u96be\u5b9a\uff0c\u90a3\u77e5\u8427\u5357\u82f9\u7a81\u5730\u4e00\u626f\u4ed6\u7684\u8863\u88f3\uff0c\u6781\u8f7b\u58f0\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7b54\u5e94\u4ed6\u8fd9\u6761\u8def\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u77e5\u9053\u5979\u6b64\u8bdd\u4e2d\u5fc5\u6709\u7528\u610f\uff0c\u4e8e\u662f\u4ed6\u4fbf\u54c2\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5c71\u6d1e\u662f\u5728\u90a3\u91cc\u5462\uff1f\u201d \u3000\u3000\u4e07\u5929\u840d\u888d\u8896\u4e00\u62c2\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u5927\u6b65\u5411\u5d16\u4e0b\u8d70\u53bb\uff0c\u800c\u90a3\u7fe0\u88f3\u5c11\u5973\u4e07\u8679\uff0c\u5374\u8f6c\u5411\u5bf9\u5d16\u7684\u98de\u9601\uff0c\u64ae\u53e3\u4f4e\u5578\u4e86\u4e00\u58f0\u3002\u6b64\u523b\u4f0a \u98ce\uff0c\u8427\u5357\u82f9\uff0c\u5374\u5df2\u968f\u7740\u4e07\u5929\u840d\u8d70\u5f97\u8fdc\u4e86\u3002 \u7b2c\u4e8c\u5377\u2014\u2014\u7b2c\u516d\u5341\u4e09\u7ae0\u3000\u83f1\u82b1\u94dc\u955c>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u4e09\u7ae0\u3000\u83f1\u82b1\u94dc\u955c \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929\u840d\u5934\u4e5f\u4e0d\u56de\uff0c\u5927\u6b65\u8d70\u5728\u524d\u9762\uff0c\u9047\u7740\u963b\u8def\u7684\u6839\u679d\uff0c\u4ed6\u5c31\u94c1\u638c\u4e00\u6325\uff0c\u90a3 \u4e9b\u6839\u679d\uff0c\u4fbf\u7acb\u5373\u98de\u51fa\u53bb\u8001\u8fdc\u3002 \u3000\u3000\u8427\u5357\u82f9\u633d\u7740\u4f0a\u98ce\u7684\u94c1\u81c2\uff0c\u7d27\u7d27\u5730\u8ddf\u5728\u4e07\u5929\u840d\u540e\u9762\uff0c\u6b64\u523b\u5979\u9762\u4e0a\u7684\u8840\u8ff9\u5df2\u5e72\uff0c\u521b\u75d5\u66f4\u662f\u660e \u663e\uff0c\u53ea\u662f\u5979\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u9762\u4e0a\u7684\u8840\u8ff9\uff0c\u53ea\u4e0d\u8fc7\u662f\u53d7\u4e86\u4e9b\u8f7b\u4f24\u800c\u5df2\uff0c\u800c\u5979\u6b64\u523b \u7684\u82b3\u5fc3\uff0c\u53ea\u56e0\u4e3a\u5b8c\u5168\u8d2f\u6ce8\u5728\u4f0a\u98ce\u8eab\u4e0a\uff0c\u800c\u65e0\u6687\u65c1\u987e\u3002 \u3000\u3000\u4e24\uff0c\u4e09\u76cf\u6267\u8336\u65f6\u5019\uff0c\u4ed6\u4eec\u4fbf\u5df2\u8d70\u51fa\u4e1b\u6797\u3002 \u3000\u3000\u4e07\u5929\u840d\u56de\u5934\u51b7\u77a5\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u8ddf\u6211\u8d70\uff1f\u201d \u3000\u3000\u8eab\u8eaf\u5411\u5de6\u4e00\u8f6c\uff0c\u5927\u6b65\u5411\u5de6\u8d70\u53bb\u3002 \u3000\u3000\u8427\u5357\u82f9\u5fc3\u91cc\u7acb\u523b\u72c2\u559c\u5730\u8df3\u52a8\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u4ed6\u6240\u8bf4\u7684\u5c71\u6d1e\uff0c\u771f\u7684\u5982\u6211\u6240\u731c\uff0c\u5c31\u662f\u6628\u591c\u7684\u5c71\u6d1e\u5417\uff1f\u90a3\u8be5\u662f\u5357\u54e5\u54e5\u719f\u6089\u7684\u5440\uff01\u201d \u3000\u3000\u5979\u4fa7\u76ee\u4e00\u671b\u4f0a\u98ce\uff0c\u53ea\u89c1\u4f0a\u98ce\u5251\u7709\u6df1\u76b1\uff0c\u9762\u4e0a\u5fe7\u8272\u91cd\u91cd\uff0c\u5979\u4e0d\u7981\u53c8\u5947\u602a\uff1a \u3000\u3000\u201c\u96be\u9053\u4ed6\u6ca1\u6709\u60f3\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u4e00\u634f\u4ed6\u7684\u80f3\u818a\uff0c\u4ed6\u4fa7\u76ee\u8f7b\u7b11\u4e00\u4e0b\uff0c\u5374\u4ecd\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u793a\u3002 \u3000\u3000\u201c\u5927\u6982\u4ed6\u4e0d\u613f\u9732\u5728\u9762\u4e0a\uff0c\u6050\u6015\u88ab\u90a3\u59d3\u4e07\u7684\u8001\u5934\u5b50\u77e5\u9053\u5427\u3002\u201d \u3000\u3000\u5979\u66ff\u81ea\u5df1\u5982\u6b64\u89e3\u91ca\u7740\uff0c\u5fc3\u4e0b\u4e0d\u7981\u53c8\u4e3a\u4e4b\u91ca\u7136\u3002 \u3000\u3000\u6b64\u523b\u5df2\u8fc7\u5348\u65f6\uff0c\u4f46\u65e5\u5149\u4ecd\u76db\uff0c\u6b8b\u51ac\u5df2\u5c06\u5168\u901d\uff0c\u521d\u6625\u5df2\u73b0\u8e2a\u8ff9\uff0c\u4e07\u5929\u840d\u5728\u8fd9\u9887\u6709\u6625\u610f\u7684\u9633 \u5149\u4e0b\uff0c\u5e76\u672a\u65bd\u5c55\u51fa\u8f7b\u8eab\u7684\u529f\u592b\u6765\uff0c\u4f46\u662f\u4ed6\u5927\u6b65\u800c\u884c\uff0c\u884c\u8def\u7684\u901f\u5ea6\uff0c\u4ecd\u4e0d\u662f\u5e38\u4eba\u6240\u80fd\u4f01\u53ca\u7684\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u7ea6\u83ab\u987f\u996d\u529f\u529b\uff0c\u8427\u5357\u82f9\u6c14\u529b\u5df2\u53c8\u4e0d\u652f\u4e86\uff0c\u4f0a\u98ce\u601c\u60dc\u5730\u6276\u7740\u5979\uff0c\u5979\u6021\u7136\u95ed\u4e0a\u773c\u775b\uff0c \u5c06\u5168\u8eab\u7684\u5927\u534a\u91cd\u91cf\uff0c\u90fd\u4ea4\u6258\u5728\u4ed6\u90a3\u5f3a\u800c\u6709\u529b\u7684\u81c2\u8180\u4e0a\u3002 \u3000\u3000\u53ea\u8981\u80fd\u591f\u4f9d\u9644\u5728\u4ed6\u7684\u81c2\u8180\u4e0a\uff0c\u8fd9\u6761\u8def\u5373\u4f7f\u901a\u5411\u6b7b\u4ea1\uff0c\u5979\u4e5f\u4f1a\u4e50\u4e8e\u5c31\u9053\u7684\u3002 \u3000\u3000\u4e07\u5929\u840d\u7a81\u5730\u51b7\u53f1\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5230\u4e86\uff01\u5df4\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u8427\u5357\u82f9\u5f20\u5f00\u773c\u6765\uff0c\u5fc3\u91cc\u4e0d\u7981\u53c8\u201c\u5657\u901a\u201d\u4e00\u8df3\uff01\u4e07\u5929\u840d\u624b\u6307\u7740\u7684\u8fd9\u6761\u5c71\u9699\uff0c\u4e0d\u5c31\u662f\u901a\u5411\u6628 \u591c\u90a3\u4ee4\u81ea\u5df1\u6c38\u751f\u4e0d\u80fd\u76f8\u5fd8\u7684\u5730\u65b9\u5417\uff1f \u3000\u3000\u5374\u542c\u4e07\u5929\u840d\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u6761\u5c71\u9699\uff0c\u957f\u8fbe\u5341\u4e08\uff0c\u4e00\u76f4\u8d70\u91cc\u53bb\uff0c\u5c31\u6709\u4e00\u5904\u6d1e\u7a9f\uff0c\u8001\u592b\u77e5\u9053\u91cc\u9762\u7edd\u65e0\u6bd2\u86c7\u731b\u517d\uff1b\u5c31 \u662f\u6709\u6bd2\u86c7\u731b\u517d\uff0c\u51ed\u4f60\u7684\u8eab\u624b\uff0c\u4e5f\u53ef\u6253\u53d1\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u56db\u626b\uff0c\u51b7\u51b7\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fdb\u53bb\u4e4b\u540e\uff0c\u8001\u592b\u5c31\u7528\u5de8\u77f3\u5c06\u8fd9\u88c2\u9699\u5c01\u8d77\u6765\uff0c\u800c\u8001\u592b\u5c31\u5bf9\u9762\u5750\u5728\u8fd9\u91cc\u3002\u662f\u4ee5\u4e00\u6708\u4e4b \u5185\uff0c\u4f60\u5c31\u7b97\u80fd\u5f04\u5f00\u4e00\u5757\u5de8\u77f3\uff0c\u4f46\u8001\u592b\u4f1a\u7acb\u523b\u52a0\u4e00\u5757\u4e0a\u53bb\u3002\u662f\u4ee5\u4f60\u6839\u672c\u7edd\u5c11\u6709\u5e0c\u671b\u80fd\u81ea\u884c\u51fa \u6d1e\u3002\u4f55\u51b5\u6570\u65e5\u4e4b\u540e\uff0c\u53ea\u6015\u4f60\u997f\u5f97\u8fde\u4e3e\u624b\u7684\u529b\u6c14\u90fd\u6ca1\u6709\u4e86\uff01\u201d \u3000\u3000\u4f0a\u98ce\u9762\u4e0d\u6539\u8272\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u6709\u5c06\u4ed6\u8fd9\u5a01\u80c1\u7684\u8bdd\uff0c\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u662f\u51b7\u7136\u8bf4\u9053\uff1a \u3000\u3000\u201c\u591a\u627f\u76f8\u544a\uff0c\u4e0d\u8fc7\u90a3\u7b2c\u4e00\u6761\u8def\uff0c\u6211\u5374\u662f\u4e07\u4e07\u4e0d\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u4e07\u5929\u840d\u957f\u7709\u8f69\u5904\uff0c\u53f1\u9053\uff1a \u3000\u3000\u201c\u90a3\u4f60\u5c31\u5feb\u6eda\u8fdb\u53bb\u2026\u2026\u201d \u3000\u3000\u53f1\u58f0\u672a\u4e86\uff0c\u5c71\u9053\u4e0a\u5df2\u5f69\u8776\u4f3c\u7684\u63a0\u6765\u4e00\u6761\u7fe0\u8272\u4eba\u5f71\uff0c\u8fdc\u8fdc\u5a07\u5524\u9053\uff1a \u3000\u3000\u201c\u7b49\u6211\u4e00\u7b49\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u4e00\u4fa7\u987e\uff0c\u5df2\u770b\u5230\u90a3\u4e07\u8679\u5df2\u5982\u98de\u63a0\u6765\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u4e2a\u7bee\u5b50\uff0c\u7fe0\u7eff\u8272\u7684\u8863\u886b\uff0c\u5728\u5c71\u98ce \u4e2d\u4e00\u98d8\u4e00\u98d8\u5730\uff0c\u715e\u662f\u597d\u770b\u3002 \u3000\u3000\u8fd9\u4e07\u8679\u4e00\u63a0\u8fc7\u6765\uff0c\u5c31\u5c06\u624b\u4e2d\u7684\u7bee\u5b50\uff0c\u653e\u5728\u5730\u4e0a\uff0c\u91cc\u9762\u5374\u88c5\u7740\u4e24\u76d8\u83dc\uff0c\u4e00\u7897\u7ec6\u9762\uff0c\u8fd8\u6709\u4e00 \u58f6\u9152\u3002 \u3000\u3000\u65b9\u624d\u5979\u867d\u6765\u52bf\u5982\u98ce\uff0c\u4f46\u7bee\u4e2d\u7684\u83dc\uff0c\u76d8\u4e2d\u7684\u9762\uff0c\u58f6\u4e2d\u7684\u9152\uff0c\u5374\u6ca1\u6709\u4e00\u4e1d\u6cfc\u5728\u5916\u9762\u3002 \u3000\u3000\u4e07\u5929\u840d\u9f3b\u4e2d\u51b7\u54fc\u4e00\u58f0\uff0c\u8d1f\u624b\u8f6c\u8fc7\u8eab\u53bb\u3002 \u3000\u3000\u8427\u5357\u82f9\u5fc3\u4e2d\u4e00\u52a8\uff1a \u3000\u3000\u201c\u8fd9\u5973\u5b50\u600e\u5730\u5bf9\u5357\u54e5\u54e5\u8fd9\u6837\u597d\uff1f\u201d \u3000\u3000\u918b\u610f\u4e0d\u7981\u5927\u4f5c\uff0c\u5374\u4e5f\u4e0d\u597d\u8bf4\u51fa\u8bdd\u6765\uff0c\u53ea\u662f\u95f7\u5728\u5fc3\u91cc\u800c\u5df2\u3002 \u3000\u3000\u5343\u767e\u5e74\u6765\uff0c\u4e0d\u5403\u918b\u7684\u5973\u5b50\uff0c\u6050\u6015\u8fd8\u6ca1\u6709\u54e9\u3002 \u3000\u3000\u4e07\u8679\u5fae\u626d\u7ea4\u8170\uff0c\u8d70\u5230\u5c71\u58c1\u8fb9\uff0c\u4f38\u51fa\u4e24\u53ea\u8202\u8471\u822c\u7684\u7389\u624b\uff0c\u5374\u5c06\u4e00\u5757\u78e8\u76d8\u5927\u7684\u77f3\u5757\uff0c\u4e3e\u4e86\u8d77 \u6765\uff0c\u8f7b\u79fb\u83b2\u6b65\uff0c\u8d70\u5230\u4f0a\u98ce\u8eab\u4fa7\uff0c\u653e\u4e0b\u77f3\u5757\uff0c\u5c06\u7bee\u4e2d\u7684\u9152\u83dc\uff0c\u4e00\u6837\u4e00\u6837\u5730\u62ff\u4e86\u51fa\u6765\uff0c\u653e\u5728\u77f3\u5757 \u4e0a\uff0c\u5a07\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u4e00\u8fdb\u53bb\uff0c\u6050\u6015\u8981\u597d\u4e45\u624d\u80fd\u51fa\u6765\uff0c\u5728\u91cc\u9762\u53c8\u6ca1\u6709\u4e1c\u897f\u5403\uff0c\u5148\u628a\u8fd9\u4e9b\u5403\u4e86\u518d\u8fdb\u53bb\u5427\uff01 \u5509\u65f6\u95f4\u8fd9\u4e48\u5306\u5fd9\uff0c\u4e0d\u7136\u6211\u5c31\u4eb2\u624b\u7ed9\u4f60\u505a\u4e86\u3002\u201d \u3000\u3000\u62ff\u8d77\u4e00\u53cc\u94f6\u7b77\uff0c\u9012\u5230\u4f0a\u98ce\u7684\u624b\u4e0a\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u51c9\u4e86\u5c31\u4e0d\u597d\u5403\u4e86\uff0c\u5feb\u5440\uff01\u201d \u3000\u3000\u4f0a\u98ce\u671b\u7740\u8fd9\u7eaf\u771f\u65e0\u90aa\u7684\u5c11\u5973\uff0c\u832b\u7136\u63a5\u8fc7\u94f6\u7b77\u6765\uff0c\u5fc3\u5934\u6d8c\u8d77\u4e00\u80a1\u96be\u8a00\u7684\u6ecb\u5473\u6765\uff0c\u5374\u4e0d\u5fcd\u62d2 \u7edd\u3002 \u3000\u3000\u8fd9\u53cc\u7b77\u5b50\uff0c\u5728\u4ed6\u624b\u91cc\u7adf\u50cf\u662f\u6709\u5343\u94a7\u822c\u91cd\u4f3c\u7684\uff0c\u4ed6\u5450\u5450\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u59d1\u5a18\uff01\u201d \u3000\u3000\u7136\u540e\u8f6c\u8fc7\u5934\uff0c\u5c06\u624b\u4e2d\u7684\u94f6\u7b77\uff0c\u9012\u7ed9\u8427\u5357\u82f9\uff0c\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u4f60\u5403\u4e00\u4e9b\uff01\u201d \u3000\u3000\u90a3\u77e5\u8427\u5357\u82f9\u7a81\u5730\u4e00\u8f6c\u8eab\uff0c\u5c06\u8138\u8f6c\u4e86\u8fc7\u53bb\uff0c\u4f0a\u98ce\u65b9\u81ea\u4e00\u6115\uff0c\u5de6\u81c2\u5df2\u88ab\u4eba\u62c9\u4f4f\uff0c\u4e00\u4e2a\u5a07\u607c\u7740 \u7684\u58f0\u97f3\u9053\uff1a \u3000\u3000\u201c\u6211\u662f\u9001\u7ed9\u4f60\u5403\u7684\uff0c\u4f60\u5ba2\u6c14\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u80cc\u7740\u8138\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u8c01\u5e0c\u7f55\uff01\u6211\u6839\u672c\u5c31\u4e0d\u8981\u5403\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e0d\u7981\u6697\u81ea\u4e00\u7b11\uff0c\u4f46\u6b64\u60c5\u6b64\u666f\uff0c\u4ed6\u5fc3\u4e2d\u7684\u6697\u7b11\uff0c\u53c8\u600e\u4f1a\u5ef6\u7eed\u5f97\u957f\u4e45\u5462\uff01 \u3000\u3000\u4ed6\u4e3e\u7740\u94f6\u7b77\uff0c\u671b\u7740\u8fd9\u4e24\u4e2a\u5403\u7740\u918b\u7684\u5c11\u5973\uff0c\u671b\u7740\u9762\u524d\u7684\u9152\u83dc\uff0c\u624b\u4e2d\u7684\u94f6\u7b77\uff0c\u5728\u9633\u5149\u4e0b\u6b63\u95ea \u7740\u5149\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5e45\u591a\u4e48\u7f8e\u7684\u60c5\u666f\uff01\u4f46\u662f\u8fd9\u60c5\u666f\u53c8\u80fd\u5ef6\u7eed\u591a\u4e45\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u5c06\u624b\u4e2d\u7684\u94f6\u7b77\uff0c\u653e\u5728\u90a3\u5757\u9752\u77f3\u4e0a\uff0c\u5fae\u559f\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u59d1\u5a18\uff01\u4e0d\u8fc7\u5c0f\u9119\u5b9e\u5728\u5403\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e07\u8679\u773c\u5708\u4e00\u7ea2\uff0c\u89c9\u5f97\u59d4\u66f2\u5f97\u5f88\uff0c\u8fd8\u60f3\u518d\u8bf4\u53e5\u8bdd\uff0c\u90a3\u77e5\u4e07\u5929\u840d\u5df2\u8f6c\u8fc7\u8eab\u6765\uff0c\u53f1\u9053\uff1a \u3000\u3000\u201c\u4e0d\u5403\u5c31\u7b97\u4e86\uff01\u201d \u3000\u3000\u94c1\u638c\u4e00\u6325\uff0c\u5c06\u9752\u77f3\u4e0a\u7684\u9152\u83dc\uff0c\u6c64\u9762\uff0c\u90fd\u6325\u5728\u5730\u4e0a\u3002\u4f38\u51fa\u4e00\u53ea\u98df\u6307\u6765\uff0c\u6307\u7740\u90a3\u5bbd\u624d\u53ca\u5c3a\u7684 \u5c71\u9699\uff0c\u53c8\u51b7\u53f1\u9053\uff1a \u3000\u3000\u201c\u5feb\u8fdb\u53bb\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5251\u7709\u4e00\u8f69\uff0c\u65b9\u60f3\u53d1\u4f5c\uff0c\u8427\u5357\u82f9\u5374\u5df2\u63e1\u4f4f\u4ed6\u7684\u624b\u638c\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u8fdb\u53bb\u5c31\u8fdb\u53bb\u3002\u201d \u3000\u3000\u8fc8\u5f00\u811a\u6b65\uff0c\u5c31\u5f80\u91cc\u8d70\u3002\u90a3\u77e5\u773c\u524d\u7a81\u5730\u4e00\u82b1\uff0c\u4e00\u6761\u7fe0\u8272\u7684\u4eba\u5f71\uff0c\u5f20\u7740\u53cc\u624b\uff0c\u6321\u5728\u5c71\u9699\u524d \u9762\uff0c\u5a07\u53f1\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u7239\u7239\u8981\u4ed6\u8fdb\u53bb\uff0c\u4f60\u4e5f\u8fdb\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8427\u5357\u82f9\u6773\u773c\u5706\u7741\uff0c\u4ea6\u5a07\u53f1\u9053\uff1a \u3000\u3000\u201c\u4f60\u7ba1\u4e0d\u7740\uff01\u201d \u3000\u3000\u8f6c\u5411\u4f0a\u98ce\uff1a \u3000\u3000\u201c\u8d70\uff01\u6211\u4eec\u4e00\u9f50\u8fdb\u53bb\uff0c\u8981\u6b7b\u4e5f\u6b7b\u5728\u4e00\u9f50\u3002\u201d \u3000\u3000\u4e07\u8679\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4ece\u6765\u6ca1\u6709\u770b\u8fc7\u50cf\u8fd9\u6837\u7684\u4eba\uff0c\u8138\u4e0a\u957f\u5f97\u8ddf\u4e11\u516b\u602a\u4f3c\u7684\uff0c\u8fd8\u62c9\u4f4f\u4eba\u5bb6\u7684\u624b\uff0c\u4e5f\u4e0d\u6015\u4eba\u5bb6 \u8ba8\u538c\u4f60\u3002\u201d\u201c\u4f60\u8bf4\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u5c31\u662f\u4f60\uff01\u201d \u3000\u3000\u8427\u5357\u82f9\u7a81\u7136\u201c\u54af\u54af\u201d\u5730\u5a07\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u79cd\u8bdd\u6211\u5012\u662f\u7b2c\u4e00\u6b21\u542c\u8fc7\uff0c\u60f3\u4e0d\u5230\u4e16\u4e0a\u8fd8\u6709\u4eba\u8bf4\u6211\u6f47\u6e58\u5983\u5b50\u4e11\uff0c\u5357\u54e5\u54e5\uff01\u4f60\u8bf4\u53ef\u7b11\u4e0d \u53ef\u7b11\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u53cc\u7709\u6df1\u76b1\uff0c\u90a3\u77e5\u4e07\u8679\u5374\u5df2\u5a07\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u4e11\uff0c\u4f60\u4e0d\u4e11\uff0c\u4f60\u7f8e\u6781\u4e86\u3002\u201d \u3000\u3000\u4e00\u800c\u4f38\u624b\u5165\u6000\uff0c\u638f\u51fa\u4e00\u9762\u83f1\u82b1\u94dc\u955c\u6765\uff0c\u653e\u5728\u8427\u5357\u82f9\u773c\u524d\uff0c\u53c8\u5a07\u7b11\u7740\uff0c\u8ba5\u5632\u7740\u9053\uff1a \u3000\u3000\u201c\u4f60\u81ea\u5df1\u770b\u770b\uff0c\u662f\u7f8e\u662f\u4e11\uff01\u201d \u3000\u3000\u4f0a\u98ce\u51fa\u624b\u5982\u98ce\uff0c\u75be\u5730\u53bb\u62a2\u8fd9\u9762\u94dc\u955c\uff0c\u4f46\u4e07\u8679\u624b\u8155\u4e00\u66f2\u4e00\u6298\uff0c\u5374\u53c8\u5c06\u8fd9\u9762\u94dc\u955c\uff0c\u9001\u5230\u8427\u5357 \u82f9\u773c\u524d\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516d\u5341\u56db\u7ae0\u3000\u82b3\u5fc3\u5bf8\u788e>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u56db\u7ae0\u3000\u82b3\u5fc3\u5bf8\u788e \u3000\u3000\u4f0a\u98ce\u8eab\u5f62\u4e00\u52a8\uff0c\u8fc5\u6025\u63a0\u5230\u8427\u5357\u82f9\u8eab\u524d\uff0c\u53cc\u624b\u75be\u51fa\u5982\u98ce\uff0c\u4e0a\u4e0b\u4ea4\u9519\u800c\u53bb\uff0c\u201c\u53cc\u9f99\u593a\u73e0\u201d\uff0c \u4e24\u53ea\u94c1\u638c\uff0c\u540c\u65f6\u593a\u5411\u8fd9\u9762\u94dc\u955c\u3002 \u3000\u3000\u4e07\u8679\u201c\u54af\u54af\u201d\u5a07\u7b11\u4e00\u58f0\uff0c\u67f3\u8170\u8f7b\u6298\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u8eab\u5f62\u4fbf\u5df2\u500f\u7136\u6ed1\u5f00\u4e09\u5c3a\uff0c\u7389\u624b\u4e00\u626c\uff0c\u5c06 \u624b\u4e2d\u7684\u94dc\u955c\u7b14\u76f4\u629b\u5411\u8427\u5357\u82f9\uff0c\u4e00\u9762\u8f7b\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u81ea\u5df1\u770b\u770b\u5427\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5927\u62e7\u8eab\uff0c\u4f38\u624b\u593a\u955c\uff0c\u4f46\u80c1\u4e0b\u7a81\u5730\u88ad\u6765\u4e00\u7f15\u98ce\u58f0\uff0c\u4e07\u8679\u7684\u4e00\u53ea\u7389\u624b\uff0c\u5df2\u500f\u7136\u88ad\u6765\uff0c\u4e24 \u53ea\u6625\u8471\u822c\u7684\u624b\u6307\uff0c\u5fae\u5fae\u5e76\u8d77\uff0c\u6307\u7532\u4e0a\u6d82\u7740\u9c9c\u8840\u7684\u82b1\u6c41\uff0c\u8d8a\u53d1\u886c\u5f97\u8fd9\u53ea\u624b\u7684\u80a4\u8272\u5982\u7389\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u53ea\u7389\u624b\uff0c\u5374\u662f\u75be\u5730\u70b9\u5411\u4f0a\u98ce\u80c1\u4e0b\u7684\u201c\u85cf\u8840\u201d\u5927\u7a74\u3002 \u3000\u3000\u4f0a\u98ce\u5927\u60ca\u4e4b\u4e0b\uff0c\u63d0\u53f3\u811a\uff0c\u6c88\u5de6\u8098\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u5212\u5411\u4e07\u8679\u7684\u624b\u8155\uff0c\u90a3\u77e5\u4e07\u8679\u5374\u7a81\u5730\u6536\u56de\u7389 \u638c\uff0c\u5fae\u6298\u7ea4\u8170\uff0c\u53c8\u6ed1\u5f00\u4e09\u5c3a\uff0c\u8f7b\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u624d\u4e0d\u8ddf\u4f60\u6253\u54e9\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u5fae\u6115\u4e86\u4e00\u4e0b\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u53ea\u89c1\u8427\u5357\u82f9\u6b63\u5728\u6367\u7740\u8fd9\u9762\u955c\u5b50\uff0c\u76ee\u5149\u5446\u6ede\uff0c\u770b\u4e2a\u4e0d\u5df2\u3002 \u3000\u3000\u800c\u90a3\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929\u840d\uff0c\u5374\u662f\u8d1f\u624b\u51b7\u7b11\uff0c\u5bf9\u65b9\u624d\u6240\u53d1\u751f\u7684\u8fd9\u4e9b\u4e8b\uff0c\u7adf\u7136\u5b8c\u5168\u4e0d\u95fb\u4e0d \u95ee\u3002 \u3000\u3000\u505a\u7236\u6bcd\u7684\u5fc3\u60c5\uff0c\u5c24\u5176\u662f\u505a\u4e00\u4e2a\u5e74\u65b9\u53ca\u7b04\u7684\u6000\u6625\u5c11\u5973\u7684\u7236\u6bcd\uff0c\u5176\u5fc3\u60c5\uff0c\u4f0a\u98ce\u5f53\u7136\u65e0\u6cd5\u4e86 \u89e3\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6709\u4e9b\u5947\u602a\u4e07\u5929\u840d\u7684\u6001\u5ea6\uff0c\u4f46\u662f\u6b64\u60c5\u6b64\u666f\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u5374\u53c8\u600e\u5bb9\u5f97\u4ed6\u6765\u601d\u7d22\u8fd9\u4e9b\uff1f \u3000\u3000\u4ed6\u5e72\u54b3\u4e00\u58f0\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u8427\u5357\u82f9\u8eab\u4fa7\uff0c\u67d4\u58f0\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u522b\u770b\u4e86\uff01\u4f60\u8138\u4e0a\u7684\u8fd9\u4e9b\uff0c\u4e0d\u8fc7\u662f\u76ae\u8089\u64e6\u4f24\u800c\u5df2\uff0c\u9a6c\u4e0a\u5c31\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u8f7b\u8f7b\u4f38\u51fa\u624b\uff0c\u53bb\u62ff\u8427\u5357\u82f9\u624b\u4e0a\u7684\u90a3\u9762\u955c\u5b50\u3002 \u3000\u3000\u4f46\u662f\u8427\u5357\u82f9\u634f\u7740\u955c\u5b50\u7684\u624b\uff0c\u7adf\u751f\u50cf\u662f\u94c1\u94f8\u7684\u4f3c\u7684\uff0c\u534a\u70b9\u4e5f\u4e0d\u653e\u677e\u3002 \u3000\u3000\u4e07\u8679\u5728\u5c71\u58c1\u95f4\u6298\u4e86\u4e00\u6bb5\u67af\uff0c\u62ff\u5728\u624b\u4e0a\uff0c\u4e00\u6bb5\u4e00\u6bb5\u5730\u6298\u65ad\uff0c\u53e3\u4e2d\u7b11\u9053\uff1a \u3000\u3000\u201c\u5357\u54e5\u54e5\uff01\u4f60\u53c8\u4f55\u5fc5\u9a97\u5979\u5462\uff1f\u5979\u5c31\u7b97\u8138\u4e0a\u7684\u4f24\u597d\u4e86\uff0c\u4e5f\u8981\u53d8\u6210\u4e00\u4e2a\u5927\u9ebb\u5b50\u4e86\u3002\u201d \u3000\u3000\u5979\u65b9\u624d\u542c\u5230\u8427\u5357\u82f9\u53eb\u4f0a\u98ce\u201c\u5357\u54e5\u54e5\u201d\uff0c\u6b64\u523b\u81ea\u5df1\u4fbf\u4e5f\u53eb\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u53eb\u7684\u58f0\u97f3\u5a07\u67d4\u5b9b \u8f6c\uff0c\u5165\u8033\u5982\u871c\uff01 \u3000\u3000\u4f0a\u98ce\u56de\u5934\u6012\u89c6\u4e00\u773c\uff0c\u90a3\u77e5\u8427\u5357\u82f9\u7a81\u5730\u4ef0\u5929\u72c2\u7b11\u8d77\u6765\uff0c\u4e00\u62ac\u624b\uff0c\u5c06\u624b\u4e2d\u7684\u94dc\u955c\uff0c\u201c\u94db\u201d\u5730 \u629b\u5728\u5c71\u58c1\u4e0a\u3002 \u3000\u3000\u4f0a\u98ce\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e00\u628a\u6293\u4f4f\u5979\u7684\u624b\uff0c\u8fde\u8fde\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u5357\u82f9\uff01\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u8427\u5357\u72c2\u7b11\u7740\uff0c\u773c\u4e2d\u7684\u6cea\u73e0\uff0c\u65ad\u4e86\u7ebf\u4f3c\u7684\u6d41\u4e86\u4e0b\u6765\uff0c\u6d41\u8fc7\u5979\u6ee1\u662f\u8840\u8ff9\u7684\u9762\u9765\uff0c\u843d\u4e0b\u6765\u65f6\uff0c \u4fbf\u4e5f\u53d8\u5f97\u6709\u5982\u8840\u822c\u9c9c\u7ea2\u3002 \u3000\u3000\u5979\u72c2\u7b11\u7740\uff0c\u6454\u7740\u4e86\u4f0a\u98ce\u7684\u624b\uff0c\u7b11\u58f0\u5df2\u53d8\u4e3a\u54ed\u6ce3\uff0c\u54ed\u6ce3\u5374\u4ecd\u4f3c\u72c2\u7b11\uff0c\u8fd9\u72c2\u7b11\u58f0\u4e0e\u54ed\u6ce3\u58f0\uff0c \u4fbf\u6df7\u5408\u6210\u4e00\u79cd\u94c1\u77f3\u4eba\u542c\u4e86\u90fd\u8981\u80a0\u65ad\u7684\u58f0\u97f3\uff01 \u3000\u3000\u6f47\u6e58\u5983\u5b50\uff0c\u7f8e\u540d\u904d\u53ca\u6b66\u6797\uff0c\u53ea\u8981\u662f\u884c\u8d70\u6c5f\u6e56\u7684\u4eba\uff0c\u867d\u672a\u89c1\u8fc7\u6f47\u6e58\u5983\u5b50\uff0c\u5374\u4e5f\u77e5\u9053\u5979\u662f\u7f8e \u5982\u5929\u4ed9\u7684\u4e3d\u4eba\uff0c\u7136\u800c\u6b64\u523b\u2026\u2026 \u3000\u3000\u8427\u5357\u82f9\u7684\u82b3\u5fc3\uff0c\u4fbf\u6709\u5982\u4e07\u8679\u624b\u4e0a\u7684\u67af\uff0c\u4e00\u5bf8\u4e00\u5bf8\u5730\u65ad\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5979\u77e5\u9053\u6b64\u523b\u81ea\u5df1\u5df2\u4e0d\u914d\u4f0a\u98ce\uff0c\u4f46\u662f\u6628\u591c\u72c2\u4e71\u7684\u6e29\u99a8\uff0c\u5374\u4ecd\u5b9b\u7136\u5728\u76ee\u3002 \u3000\u3000\u5979\u4e0d\u77e5\u81ea\u5df1\u8be5\u600e\u4e48\u529e\uff0c\u773c\u524d\u832b\u7136\u4e00\u7247\uff0c\u5929\u4e0b\u867d\u5927\uff0c\u5374\u50cf\u662f\u518d\u4e5f\u6ca1\u6709\u4e00\u6761\u81ea\u5df1\u80fd\u8d70\u7684\u8def\uff01 \u3000\u3000\u8ff7\u832b\u7684\u773c\u4e2d\uff0c\u5979\u4f3c\u4e4e\u770b\u5230\u4f0f\u864e\u91d1\u521a\u962e\u5927\u6210\uff0c\u4ee5\u53ca\u4e00\u4e9b\u66fe\u7ecf\u88ab\u81ea\u5df1\u6298\u8fb1\u8fc7\u7684\u75f4\u60c5\u7537\u5b50\uff0c\u4e00 \u4e2a\u4e2a\u90fd\u4f38\u51fa\u624b\u6765\uff0c\u6307\u7740\u81ea\u5df1\u7b11\u9a82\u3002 \u3000\u3000\u7136\u540e\uff0c\u8fd9\u4e9b\u4eba\u7684\u5f71\u5b50\uff0c\u4fbf\u5728\u5979\u8111\u6d77\u4e2d\u5f00\u59cb\u65cb\u8f6c\u8d77\u6765\uff0c\u50cf\u98ce\u8f66\u4f3c\u7684\uff0c\u8d8a\u8f6c\u8d8a\u5feb\uff0c\u7ec8\u4e8e\u53d8\u6210 \u4e00\u7247\u6df7\u6c8c\u3002 \u3000\u3000\u4f0a\u98ce\u5403\u60ca\u5730\u671b\u7740\u5979\uff0c\u624b\u8db3\u4e5f\u4e3a\u4e4b\u5931\u63aa\u3002 \u3000\u3000\u4e07\u8679\u7ad9\u5728\u5c71\u58c1\u524d\uff0c\u4e5f\u4e0d\u7981\u6014\u4f4f\uff0c\u5fae\u5fae\u6709\u4e9b\u540e\u6094\u5979\u7ec8\u7a76\u8fd8\u662f\u4e2a\u7eaf\u771f\u7684\u5c11\u5973\u5440\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u5374\u51b7\u54fc\u4e00\u58f0\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u65f6\u5149\u5df2\u7ecf\u4e0d\u65e9\u4e86\uff0c\u4f60\u53ef\u4ee5\u8fdb\u53bb\u4e86\u5427\uff01\u6709\u4ec0\u4e48\u8bdd\uff0c\u4e00\u4e2a\u6708\u540e\uff0c\u53ea\u8981\u4f60\u4e0d\u6b7b\uff0c\u518d\u8bf4\u4e5f\u4e0d \u8fdf\u3002\u201d \u3000\u3000\u8427\u5357\u82f9\u7a81\u5730\u4f38\u51fa\u90a3\u53cc\u5e26\u8840\u7684\u7389\u624b\uff0c\u63a9\u5728\u81ea\u5df1\u8138\u4e0a\uff0c\u5a07\u557c\u7740\uff0c\u98de\u4e5f\u4f3c\u7684\u72c2\u5954\u51fa\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u5927\u53eb\u4e00\u58f0\uff0c\u5c55\u52a8\u8eab\u5f62\uff0c\u62e6\u5728\u5979\u7684\u524d\u9762\uff0c\u60b2\u5636\u7740\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u4e0d\u7ba1\u4f60\u7684\u8138\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u6211\u2026\u2026\u6211\u8fd8\u662f\u559c\u6b22\u4f60\u7684\u3002\u201d \u3000\u3000\u7136\u800c\u8427\u5357\u82f9\u7684\u557c\u58f0\u5374\u66f4\u60b2\u54c0\u4e86\uff01\u6b64\u523b\u5979\u867d\u6709\u5343\u8a00\u4e07\u8bed\uff0c\u54fd\u5728\u5589\u95f4\uff0c\u5374\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa \u6765\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u5979\u6697\u4e2d\u4e00\u54ac\u94f6\u7259\uff0c\u60b2\u5207\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5357\u54e5\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u8fdb\u53bb\u5427\uff01\u53ea\u8981\u4f60\u4e0d\u6b7b\u2026\u2026\u6211\u2026\u2026\u59cb\u7ec8\u662f\u4f60\u7684\uff0c\u6628\u5929\u665a\u4e0a\u2026\u2026\u6211\u2026\u2026 \u6211\u4e0d\u662f\u628a\u4e00\u5207\u90fd\u4ea4\u7ed9\u4f60\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e07\u5929\u840d\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u63a0\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u5728\u505a\u68a6\u5427\uff01\u6628\u5929\u665a\u4e0a\uff0c\u8fd9\u5c0f\u5b50\u660e\u660e\u2026\u2026\u201d \u3000\u3000\u8bdd\u65b9\u8bf4\u81f3\u6b64\u5904\uff0c\u4f0a\u98ce\u5df2\u5927\u543c\u4e00\u58f0\uff0c\u548c\u8eab\u6251\u4e86\u4e0a\u53bb\uff0c\u53f3\u624b\u4e94\u6307\u7b95\u5f20\uff0c\u6293\u5411\u4e07\u5929\u840d\u7684\u9762\u95e8\uff0c \u5de6\u624b\u638c\u7f18\u5982\u5200\uff0c\u6a2a\u5207\u4e07\u5929\u840d\u7684\u80f8\u8179\u3002 \u3000\u3000\u638c\u98ce\u51cc\u5389\uff0c\u52bf\u5982\u75af\u864e\uff01\u8fd9\u4e00\u653b\uff0c\u6b63\u662f\u4f0a\u98ce\u6bd5\u751f\u529f\u529b\u6240\u805a\u3002\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u867d\u7136\u6b66\u529f\u7edd \u9ad8\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u505c\u4f4f\u58c1\uff0c\u4fa7\u8eab\u907f\u62db\u3002 \u3000\u3000\u4f0a\u98ce\u4e00\u62db\u843d\u7a7a\uff0c\u7edd\u4e0d\u5bb9\u4e07\u5929\u840d\u518d\u6709\u5598\u606f\u7684\u673a\u4f1a\uff0c\u638c\u5f71\u7ffb\u98de\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff0c\u4e00\u8fde\u6570\u638c\uff0c\u75be \u5982\u98d8\u98ce\u5730\u653b\u5411\u4e07\u5929\u840d\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u5634\u89d2\u5fae\u5659\u51b7\u7b11\uff0c\u811a\u4e0b\u5fae\u8e29\u8ff7\u8e2a\uff0c\u888d\u8896\u62c2\u5904\uff0c\u8f7b\u6613\u5c31\u5c06\u4f0a\u98ce\u7684\u6570\u62db\u907f\u8fc7\u3002 \u3000\u3000\u987b\u77e5\u4f0a\u98ce\u6b66\u529f\u672c\u5c31\u4e0d\u662f\u4e07\u5929\u840d\u7684\u654c\u624b\uff0c\u5728\u65e0\u91cf\u5c71\u5dc5\uff0c\u4ed6\u867d\u66fe\u5c06\u4e07\u5929\u840d\u903c\u5728\u4e0b\u98ce\uff0c\u4f46\u90a3\u65f6 \u5374\u662f\u4e07\u5929\u840d\u5927\u4f24\u672a\u6108\uff0c\u771f\u529b\u6731\u590d\u7684\u65f6\u5019\u3002 \u3000\u3000\u800c\u6b64\u523b\u4e07\u5929\u840d\u4e0d\u4f46\u529f\u529b\u5df2\u5b8c\u5168\u6062\u590d\uff0c\u800c\u4e14\u81ea\u4ece\u4ed6\u559d\u4e86\u5999\u624b\u8bb8\u767d\u4f53\u5185\u542b\u6709\u7075\u836f\u7684\u8840\u540e\uff0c\u529f \u529b\u66f4\u662f\u5927\u589e\uff0c\u81ea\u7136\u672a\u5c06\u4f0a\u98ce\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u800c\u4f0a\u98ce\u6b64\u523b\u672c\u5df2\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u6570\u62db\u62a2\u653b\u8fc7\u540e\uff0c\u4ed6\u771f\u529b\u66f4\u662f\u4e0d\u7ee7\u3002\u5374\u89c1\u4e07\u5929\u840d\u888d\u8896\u62c2\u52a8 \u5904\uff0c\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u90a3\u5973\u5b50\u5df2\u7ecf\u8d70\u4e86\uff0c\u4f60\u8fd8\u62da\u4ec0\u4e48\u547d\uff1f\u6211\u771f\u4e0d\u61a7\uff0c\u4f60\u597d\u597d\u4e00\u4e2a\u6c49\u5b50\uff0c\u770b\u6765\u4e5f\u86ee\u806a\u660e\u7684\uff0c\u600e \u5730\u5982\u6b64\u7b28\u6cd5\uff0c\u8fde\u4e2a\u597d\u6b79\u90fd\u4e0d\u61c2\uff01\u201d \u3000\u3000\u4f0a\u98ce\u624b\u8098\u4e00\u6c89\uff0c\u53cc\u638c\u4fbf\u53c8\u201c\u7830\u201d\u5730\u51fb\u51fa\uff0c\u76ee\u5149\u8f6c\u52a8\u5904\uff0c\u56db\u4e0b\u679c\u7136\u5df2\u5931\u53bb\u4e86\u8427\u5357\u82f9\u7684\u5f71 \u5b50\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u53c8\u5927\u559d\u4e00\u58f0\uff0c\u8f6c\u8eab\u6251\u4e86\u8fc7\u53bb\uff0c\u4f46\u9762\u524d\u7a81\u5730\u5288\u6765\u4e00\u80a1\u52b2\u98ce\uff0c\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u5df2\u5e26\u7740 \u51b7\u7b11\u6321\u5728\u4ed6\u9762\u524d\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u4f60\u60f3\u8d70\u53ef\u4e0d\u6210\uff01\u201d \u3000\u3000\u888d\u8896\u8fde\u5c55\uff0c\u96c4\u6d51\u7684\u638c\u98ce\uff0c\u903c\u5f97\u4f0a\u98ce\u811a\u6b65\u8e09\u8dc4\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u6b64\u523b\u4ed6\u7adf\u8fde\u8fd8\u624b\u4e4b\u529b\u90fd\u6ca1\u6709 \u4e86\u3002 \u3000\u3000\u4e07\u5929\u840d\u76ee\u5149\u51db\u4e8e\u5bd2\u51b0\uff0c\u51b7\u53f1\u9053\uff1a \u3000\u3000\u201c\u4f60\u60f3\u6b7b\uff0c\u8fd8\u662f\u60f3\u6d3b\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u72c2\u543c\u4e00\u58f0\uff0c\u53c8\u6251\u4e86\u4e0a\u53bb\uff0c\u4f46\u624b\u8155\u5374\u7a81\u5730\u4e00\u7d27\uff0c\u4ed6\u7684\u53f3\u624b\uff0c\u7adf\u88ab\u4e07\u8679\u7684\u4e00\u53cc\u7389\u638c\u7262\u7262 \u6293\u7740\u4e86\u3002 \u3000\u3000\u6b64\u523b\u4f0a\u98ce\u7684\u773c\u4e2d\uff0c\u751f\u50cf\u662f\u8981\u55b7\u51fa\u8840\u6765\uff0c\u706b\u8d64\u7684\u773c\u775b\uff0c\u77aa\u5728\u4e07\u8679\u8eab\u4e0a\uff0c\u53f3\u624b\u731b\u5730\u4e00\u7529\uff0c\u6068 \u58f0\u9053\uff1a \u3000\u3000\u201c\u90fd\u662f\u4f60\uff01\u201d \u3000\u3000\u4f46\u4ed6\u53f3\u8155\u65b9\u81ea\u6323\u8131\uff0c\u5de6\u8155\u5374\u50cf\u662f\u7a81\u5730\u52a0\u4e86\u4e00\u9053\u94dc\u531d\u4f3c\u7684\uff0c\u8109\u95e8\u4e00\u9ebb\uff0c\u4ed6\u5168\u8eab\u7684\u52b2\u529b\uff0c\u7adf \u5728\u4e00\u5239\u90a3\u4e2d\u6d88\u5931\u4e86\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929\u840d\uff0c\u4ee5\u638c\u65b9\u540d\u6ee1\u5929\u4e0b\uff0c\u624b\u4e0a\u7684\u529b\u9053\uff0c\u662f\u4f55\u7b49\u60ca\u4eba\uff01\u6b64\u523b\u4f0a\u98ce\u88ab\u4ed6\u64d2 \u4f4f\u4e86\u8109\u95e8\uff0c\u7eb5\u7136\u4ed6\u6b66\u529f\u518d\u9ad8\uff0c\u5374\u518d\u4e5f\u65e0\u6cd5\u6323\u8131\u3002 \u3000\u3000\u53ea\u89c1\u4e07\u5929\u840d\u5201\u7740\u4ed6\u5de6\u8155\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u4f60\u60f3\u6b7b\uff0c\u8fd8\u662f\u60f3\u6d3b\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u5982\u706b\uff0c\u77aa\u5728\u4ed6\u8138\u4e0a\uff0c\u5634\u5507\u7d27\u7d27\u95ed\u7740\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929\u840d\u867d\u7136\u4e00\u751f\u6740\u4eba\u65e0\u6570\uff0c\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u4e3a\u4ed6\u8fd9\u79cd\u76ee\u5149\u6240\u6151\u3002 \u3000\u3000\u201c\u6b64\u4eba\u6027\u60c5\u5014\u5f3a\uff0c\u4eca\u65e5\u6211\u82e5\u653e\u8fc7\u4e86\u4ed6\uff0c\u65e5\u540e\u4ed6\u5fc5\u5b9a\u5343\u65b9\u767e\u8ba1\u5730\u62a5\u590d\u3002\u201d \u3000\u3000\u4e07\u5929\u840d\u4e00\u5ff5\u81f3\u6b64\uff0c\u773c\u4e2d\u6740\u673a\u5df2\u73b0\uff0c\u7f13\u7f13\u4e3e\u8d77\u5de6\u638c\u6765\uff0c\u4fbf\u5411\u4f0a\u98ce\u9762\u95e8\u62cd\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u638c\u52bf\u65b9\u81ea\u62cd\u81f3\u4e2d\u9014\uff0c\u4e07\u8679\u5374\u5df2\u63a0\u4e86\u8fc7\u6765\uff0c\u5c06\u81ea\u5df1\u7684\u8eab\u5b50\uff0c\u6321\u5728\u5979\u7239\u7239\u94c1\u638c\u62cd\u51fa\u7684 \u65b9\u5411\u524d\u9762\uff0c\u5a07\u58f0\u9053\uff1a \u3000\u3000\u201c\u7239\u7239\uff01\u4f60\u8fd8\u662f\u628a\u4ed6\u5173\u5728\u90a3\u5c71\u6d1e\u91cc\u53bb\u5427\uff01\u8ba9\u4ed6\u51b7\u9759\u5730\u60f3\u4e24\u5929\uff0c\u4e5f\u8bb8\u2026\u2026\u4e5f\u8bb8\u4ed6\u4f1a\u56de\u5fc3\u8f6c \u610f\uff0c\u62dc\u5728\u4f60\u8001\u4eba\u5bb6\u7684\u95e8\u4e0b\u5462\u3002\u201d \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u6697\u53f9\u4e00\u58f0\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u5973\u513f\u5df2\u52a8\u4e86\u771f\u60c5\u3002\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u867d\u7136\u4e0d\u77e5\u4f24\u8fc7\u591a\u5c11\u4eba \u7684\u5fc3\uff0c\u53ef\u662f\u4ed6\u5374\u4e0d\u5fcd\u8ba9\u81ea\u5df1\u7684\u5973\u513f\u4f24\u5fc3\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7f13\u7f13\u4f38\u56de\u624b\u638c\uff0c\u5374\u89c1\u4f0a\u98ce\u7d27\u7d27\u95ed\u7740\u53cc\u76ee\uff0c\u4e00\u4ed8\u5df2\u5c06\u751f\u6b7b\u7f6e\u4e4b\u4e0d\u987e\u7684\u6837\u5b50\uff0c\u4f3c\u4e4e\u4e16 \u95f4\u7684\u4e00\u5207\u4e8b\uff0c\u90fd\u5df2\u4e0d\u653e\u5728\u4ed6\u5fc3\u4e0a\u3002 \u3000\u3000\u4e07\u5929\u840d\u5fae\u5fae\u559f\u4e00\u58f0\uff0c\u5de6\u624b\u4ea6\u81ea\u62a2\u51fa\uff0c\u6263\u4f4f\u4e86\u4f0a\u98ce\u7684\u53f3\u8155\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u4ed6\u7adf\u5c06\u4f0a\u98ce\u62d6\u5230\u5c71 \u9699\u524d\u9762\uff0c\u53f3\u624b\u4e00\u677e\uff0c\u4f38\u6307\u5728\u4ed6\u201c\u7b11\u8170\u201d\u7a74\u4e0a\u70b9\u4e86\u4e00\u4e0b\uff0c\u5de6\u624b\u6325\u5904\uff0c\u5c31\u5c06\u4f0a\u98ce\u63a8\u8fdb\u4e86\u5c71\u9699\u3002 \u3000\u3000\u4e07\u8679\u5446\u5446\u5730\u770b\u7740\u5979\u7239\u7239\uff0c\u5c06\u5979\u4e00\u751f\u4e2d\u7b2c\u4e00\u4e2a\u953a\u60c5\u7684\u7537\u5b50\uff0c\u63a8\u8fdb\u4e86\u90a3\u6761\u5c71\u9699\uff0c\u53c8\u4ece\u5c71\u58c1\u8fb9 \u642c\u6765\u4e24\u5757\u5de8\u77f3\uff0c\u585e\u7740\u5c71\u9699\u7684\u51fa\u53e3\u3002 \u3000\u3000\u8fd9\u4e24\u5757\u5de8\u77f3\uff0c\u60f3\u5fc5\u672c\u5c31\u662f\u7528\u4ee5\u5835\u585e\u8fd9\u6761\u88c2\u9699\u7684\uff0c\u662f\u4ee5\u5927\u5c0f\u80ae\u6070\u5230\u597d\u5904\u3002 \u3000\u3000\u800c\u4e14\u8fd9\u4e24\u5757\u5de8\u77f3\uff0c\u91cd\u903e\u5343\u65a4\uff0c\u8fde\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u8fd9\u79cd\u4ee5\u201c\u6df7\u5143\u4e00\u6c14\u529f\u201d\uff0c\u540d\u9707\u6c5f\u6e56\u7684\u4eba \u7269\uff0c\u642c\u52a8\u65f6\u5c1a\u4e14\u5c3d\u4e86\u5168\u529b\uff1b\u90a3\u4e48\u52b2\u529b\u5df2\u6210\u5f3a\u5f29\u4e4b\u672b\u7684\u4f0a\u98ce\uff0c\u53c8\u600e\u4e48\u80fd\u5728\u5c71\u9699\u91cc\u5c06\u5b83\u5f04\u5f00\u5462\uff1f \u4f55\u51b5\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u8fd8\u5728\u5916\u9762\u53c8\u52a0\u4e86\u4e24\u5757\u5de8\u77f3\u3002 \u3000\u3000\u4e07\u8679\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5782\u4e0b\u4e86\u5934\uff0c\u5446\u5446\u5730\u60f3\u7740\u5fc3\u4e8b\u3002 \u3000\u3000\u51ac\u65e5\u672c\u77ed\uff0c\u6b64\u523b\u65e5\u5df2\u897f\u5760\uff0c\u843d\u5230\u5c71\u540e\uff0c\u5c71\u98ce\u66f4\u52b2\uff0c\u5439\u5230\u5979\u8eab\u4e0a\uff0c\u5df2\u6709\u5bd2\u610f\u3002 \u3000\u3000\u5979\u6b63\u81ea\u82b3\u5fc3\u6697\u4e2d\u51c4\u695a\uff0c\u5374\u542c\u5979\u7239\u7239\u5df2\u6697\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8679\u513f\uff01\u4e0d\u8981\u96be\u53d7\uff01\u518d\u8fc7\u4e2a\u4e94\uff0c\u516d\u5929\uff0c\u7b49\u4ed6\u997f\u5f97\u5dee\u4e0d\u591a\u65f6\u5019\uff0c\u6211\u5c31\u5c06\u4ed6\u653e\u51fa\u6765\u3002\u5509\u2014\u2014 \u50bb\u5b69\u5b50\uff01\u4f60\u8fd8\u6015\u7239\u4e0d\u77e5\u9053\u4f60\u7684\u5fc3\u5417\uff1f\u201d \u3000\u3000\u4e07\u8679\u867d\u4ecd\u7136\u5782\u7740\u5934\uff0c\u7c89\u9762\u5374\u5df2\u7f9e\u6da9\u5730\u5ae3\u7ea2\u4e86\u8d77\u6765\u3002\u53e3\u4e2d\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u504e\u8fdb\u5979\u7239\u7239\u7684\u6000 \u91cc\uff0c\u4e0d\u4f9d\u9053\uff1a \u3000\u3000\u201c\u4f60\u8001\u4eba\u5bb6\u77e5\u9053\u4ec0\u4e48\uff1f\u6211\u7684\u5fc3\u53c8\u600e\u4e48\u4e86\u2014\u2014\u201d \u3000\u3000\u5374\u53c8\u5fcd\u4e0d\u4f4f\u9053\uff1a \u3000\u3000\u201c\u7239\u7239\uff01\u4f60\u521a\u624d\u662f\u4e0d\u662f\u70b9\u5728\u4ed6\u7684\u201c\u7b11\u8170\u201d\u7a74\u4e0a\uff0c\u65f6\u5019\u4e00\u4e45\u4e86\uff0c\u6050\u6015\u8981\u53d7\u4f24\u5427\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u50bb\u5b69\u5b50\uff01\u4f60\u653e\u5fc3\uff01\u7239\u7239\u624b\u5e95\u4e0b\uff0c\uff0e\u81ea\u7136\u6709\u5206\u5bf8\u7684\uff0c\u7528\u4e0d\u7740\u4e00\u4e2a\u5bf9\u65f6\uff0c\u4ed6\u7684\u7a74\u9053\u81ea\u7136\u5c31 \u4f1a\u89e3\u5f00\u7684\u3002\u201d \u3000\u3000\u8fd9\u540d\u6ee1\u6c5f\u6e56\u7684\u8fa3\u624b\u5de8\u76d7\uff0c\u6b64\u523b\u5f97\u610f\u5730\u5927\u7b11\u7740\u3002\u56e0\u4e3a\u4ed6\u53e3\u4e2d\u867d\u7136\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u91cc\u5374\u77e5\u9053\uff0c\u81ea \u5df1\u70b9\u7684\u7a74\u9053\uff0c\u867d\u7136\u4e00\u4e2a\u5bf9\u65f6\u4e4b\u540e\uff0c\u4fbf\u80fd\u81ea\u89e3\uff0c\u4f46\u662f\u88ab\u70b9\u4e2d\u7a74\u9053\u7684\u4eba\uff0c\u5374\u81f3\u5c11\u6709\u4e00\u4e2a\u6708\u771f\u6c14\u4e0d \u80fd\u901a\u7545\u3002 \u3000\u3000\u90a3\u4e48\u4f0a\u98ce\u7eb5\u7136\u8eab\u4e0a\u6000\u6709\u6b66\u6797\u81f3\u5b9d\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u5374\u4e5f\u65e0\u6cd5\u5728\u8fd9\u4e9b\u5929\u91cc\uff0c\u5b66\u4f1a\u4e0a\u9762\u7684\u6b66 \u529f\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u95ef\u6c64\u6c5f\u6e56\uff0c\u5fc3\u601d\u4e4b\u7f1c\u5bc6\uff0c\u81ea\u975e\u5e38\u4eba\u6240\u80fd\u53ca\uff1b\u800c\u4e14\u4ed6\u4ee5\u638c\u529b\u6210\u540d\uff0c\u81ea\u4fe1\u81ea\u5df1\u5bf9\u201c\u70b9 \u7a74\u201d\u4e00\u9053\uff0c\u5df2\u7ecf\u7089\u706b\u7eaf\u9752\uff0c\u53ef\u4ee5\u4e0d\u6210\u95ee\u9898\uff0c\u968f\u610f\u63a7\u5236\u81ea\u5df1\u70b9\u7a74\u7684\u529b\u9053\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u5fc3\u601d\u7f1c\u5bc6\u7684\u8001\u6c5f\u6e56\uff0c\u5374\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2a\u88ab\u70b9\u4e2d\u7a74\u9053\u7684\u4eba\uff0c\u4e0d\u5230\u4e24\u4e2a\u65f6\u8fb0\uff0c\u7a74 \u9053\u5c31\u88ab\u4eba\u89e3\u5f00\u4e86\u3002\u53ea\u662f\u89e3\u5f00\u4f0a\u98ce\u7a74\u9053\u7684\u8fd9\u4eba\uff0c\u5374\u662f\u4f0a\u98ce\u4e00\u751f\u4e4b\u4e2d\uff0c\u6700\u6700\u4e0d\u613f\u610f\u89c1\u5230\u7684\u4eba\u54e9\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516d\u5341\u4e94\u7ae0\u3000\u91cd\u9022\u5982\u68a6>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u4e94\u7ae0\u3000\u91cd\u9022\u5982\u68a6 \u3000\u3000\u4f0a\u98ce\u8109\u95e8\u88ab\u6263\uff0c\u8170\u7554\u53c8\u88ab\u94c1\u9762\u5b64\u884c\u5ba2\u7684\u5185\u5bb6\u91cd\u624b\uff0c\u70b9\u4e2d\u7a74\u9053\uff0c\u6beb\u65e0\u53cd\u6297\u5730\u88ab\u63a8\u5165\u4e86\u5c71 \u9699\uff0c\u8033\u7554\u53ea\u542c\u5f97\u8f70\u7136\u8fde\u54cd\uff0c\u5c71\u7684\u51fa\u53e3\uff0c\u5c31\u88ab\u5de8\u77f3\u5835\u6b7b\u3002 \u3000\u3000\u672c\u5c31\u53ea\u6709\u4e00\u7ebf\u5929\u5149\u5c04\u5165\u7684\u5c71\u9699\uff0c\u6b64\u523b\u81ea\u7136\u4e5f\u5c31\u53d8\u5f97\u575f\u5893\u822c\u7684\u9edd\u9eef\uff0c\u751a\u81f3\u8fde\u81ea\u5df2\u7684\u624b\u6307\uff0c \u90fd\u65e0\u6cd5\u5206\u8fa8\u3002 \u3000\u3000\u4ed6\u867d\u7136\u7a74\u9053\u88ab\u70b9\uff0c\u4f46\u53ea\u662f\u5168\u8eab\u65e0\u6cd5\u52a8\u5f39\uff0c\u6c14\u8840\u4e5f\u65e0\u6cd5\u6d41\u7545\u800c\u5df2\uff0c\u77e5\u89c9\u5374\u672a\u5b8c\u5168\u5931\u53bb\uff0c\u5fc3 \u4e2d\u7684\u601d\u6f6e\uff0c\u53cd\u800c\u4e71\u5f97\u66f4\u5389\u5bb3\u4e86\u3002 \u3000\u3000\u9ed1\u6697\u4e4b\u4e2d\uff0c\u4ed6\u53ea\u89c9\u8427\u5357\u82f9\u7684\u9762\u5bb9\uff0c\u4ece\u56db\u9762\u516b\u529b\u5730\u671d\u4ed6\u538b\u4e86\u8fc7\u6765\uff0c\u5176\u4e2d\u6709\u7684\u5de7\u7b11\u5029\u7136\uff0c\u8273 \u4e3d\u5982\u82b1\uff1b\u6709\u7684\u5374\u662f\u6ee1\u9762\u8840\u8ff9\uff0c\u60e8\u4e0d\u5fcd\u7779\u3002 \u3000\u3000\u7136\u800c\u8fd9\u4e9b\u9762\u5bb9\u91cc\uff0c\u5374\u6709\u4e00\u70b9\u68fa\u540c\u7684\u5730\u65b9\uff0c\u90a3\u5c31\u662f\u5979\u90a3\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u53cc\u77b3\uff0c\u5374\u662f\u59cb\u7ec8\u6e29 \u67d4\u800c\u5e7d\u6028\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u81ea\u5df1\u4e5f\u4e0d\u80fd\u5206\u6790\u81ea\u5df1\u5bf9\u8427\u5357\u82f9\u7a76\u7adf\u662f\u90a3\u4e00\u79cd\u60c5\u611f\uff0c\u4f46\u662f\u4ed6\u5374\u80fd\u975e\u5e38\u6e05\u695a\u5730\u4e86 \u89e3\uff0c\u8427\u5357\u82f9\u5bf9\u4ed6\u662f\u90a3\u4e00\u79cd\u60c5\u611f\u3002 \u3000\u3000\u8fd1\u5e74\u6765\uff0c\u4ed6\u7684\u5fc3\u60c5\uff0c\u867d\u6709\u5982\u67af\u6728\u822c\u7684\u67af\u5bd2\uff0c\u4f46\u8fd9\u4efd\u60c5\u611f\uff0c\u5374\u5e26\u7ed9\u4ed6\u4e00\u5206\u6e29\u6696\uff0c\u53ea\u662f\u6b64\u523b \u8fd9\u79cd\u60c5\u611f\uff0c\u5374\u5df2\u6210\u4e86\u4e00\u79cd\u8fc7\u91cd\u7684\u8d1f\u62c5\uff0c\u5c31\u50cf\u4e00\u4ed8\u91cd\u62c5\u4f3c\u7684\uff0c\u538b\u5728\u4ed6\u5fc3\u4e0a\uff0c\u4f7f\u5f97\u4ed6\u7684\u5fc3\uff0c\u90fd\u5feb \u8981\u7206\u70b8\u4e86\u3002 \u3000\u3000\u8427\u5357\u82f9\u4e34\u53bb\u524d\u542b\u6cea\u7684\u72c2\u7b11\uff0c\u6b64\u523b\u8fd8\u4e0d\u53ef\u904f\u6b62\u5730\u5728\u4ed6\u8033\u65c1\u6fc0\u6c64\u7740\uff1a \u3000\u3000\u201c\u5357\u82f9\uff01\u4f60\u8dd1\u5230\u90a3\u91cc\u53bb\u4e86\u5462\uff1f\u201d\u8fd9\u95ee\u9898\u50cf\u6bd2\u86c7\u822c\u5728\u5543\u556e\u7740\u4ed6\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u6b64\u523b\u4ed6\u770b\u6765\u5374\u751a\u6de1\u7136\uff0c\u56e0\u4e3a\u4ed6\u81ea\u77e5\u5df2\u843d\u4eba\u4e00\u4e2a\u60b2\u60e8\u800c\u65e0\u52a9\u7684\u5883\u51b5 \u4e2d\u3002 \u3000\u3000\u6700\u4e25\u91cd\u7684\uff0c\u662f\u4ed6\u81ea\u5df1\u6b64\u523b\u8fde\u52a8\u5f39\u90fd\u65e0\u6cd5\u52a8\u5f39\u4e00\u4e0b\uff0c\u8eba\u5728\u8fd9\u6697\u9ed1\u800c\u9634\u68ee\u7684\u5c71\u7a9f\u91cc\uff0c\u6f6e\u6e7f\u800c \u5bd2\u51b7\u7684\u6ce5\u5730\u4e0a\uff0c\u8bf4\u4e0d\u4e00\u5b9a\u4ec0\u4e48\u65f6\u5019\uff0c\u9ed1\u6697\u4e2d\u4f1a\u6709\u6bd2\u86c7\u7a9c\u51fa\u6765\uff0c\u5728\u81ea\u5df1\u8eab\u4e0a\u54ac\u4e0a\u4e00\u53e3\u2014\u2014 \u3000\u3000\u4f55\u51b5\u4ed6\u7eb5\u4f7f\u80fd\u8eb2\u8fc7\u86c7\u866b\u7684\u6bd2\u543b\uff0c\u4e5f\u65e0\u6cd5\u9003\u51fa\u8fd9\u6697\u9ed1\u7684\u5c71\u7a9f\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5df2\u5f00\u59cb\u5e7b\u60f3\uff0c\u5728\u81ea\u5df1\u5df2\u88ab\u997f\u56f0\u82e6\uff0c\u6298\u78e8\u5f97\u4e0d\u6210\u4eba\u5f62\u7684\u65f6\uff0c\u90a3\u94c1\u9762\u5b64\u884c\u5ba2\u5c31\u4f1a\u5e26\u7740 \u72de\u7b11\u8d70\u8fdb\u6765\uff0c\u7ad9\u5728\u81ea\u5df1\u9762\u524d\uff0c\u53eb\u81ea\u5df1\u7b54\u5e94\u4ed6\u4e00\u5207\u547d\u4ee4\uff0c\u800c\u4ed6\u4e5f\u6df1\u77e5\u81ea\u5df1\u5b81\u53ef\u6b7b\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u63a5 \u53d7\u7684\u3002 \u3000\u3000\u5f53\u4eba\u4eec\u5df2\u5c06\u81ea\u5df1\u7684\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u7684\u65f6\u5019\uff0c\u90a3\u4e48\u4ed6\u5bf9\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u4e0d\u662f\u5c31\u4f1a\u770b\u5f97\u6781\u4e3a\u6de1\u7136 \u5417\uff1f \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u7d22\u6027\u95ed\u4e0a\u773c\u775b\uff0c\u9759\u9759\u542c\u7740\u81ea\u5df1\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u5728\u8fd9\u5bc2\u9759\u7684\u5c71\u7a9f\u91cc\uff0c\u4e00\u58f0\u63a5\u4e00\u58f0\u5730 \u8df3\u52a8\u7740\u3002 \u3000\u3000\u201c\u8fd9\u58f0\u97f3\u4ec0\u4e48\u65f6\u5019\u4f1a\u7a81\u7136\u505c\u6b62\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6697\u4e2d\u81ea\u5632\u5730\u5fae\u7b11\u4e00\u4e0b\u3002 \u3000\u3000\u7a81\u5730\u60f3\u8d77\u4e00\u4e2a\u513f\u65f6\u542c\u5230\u7684\u6545\u4e8b\uff0c\u90a3\u5927\u610f\u662f\u8bf4\uff1b\u4e00\u4e2a\u5bb6\u8d22\u4e07\u771f\u7684\u949c\u5bcc\uff0c\u5e26\u7740\u4ed6\u6240\u6709\u7684\u8d22 \u4ea7\uff0c\u65c5\u884c\u5230\u6c99\u6f20\u4e2d\u53bb\uff0c\u51c6\u5907\u4ee5\u4ed6\u6240\u6709\u91d1\u94b1\u7684\u529b\u91cf\uff0c\u5efa\u9020\u4e00\u4e2a\u81ea\u5df1\u7406\u60f3\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u5728\u4eba\u7c7b\u4e2d\u95f4\uff0c\u672c\u662f\u4e00\u4e2a\u5f3a\u8005\uff0c\u56e0\u4e3a\u4ed6\u6709\u7740\u6bd4\u522b\u4eba\u591a\u4e0a\u65e0\u6570\u500d\u7684\u94b1\u8d22\uff0c\u800c\u4ed6\u81ea\u5df1\u4e5f\u5e38\u4ee5 \u5f3a\u8005\u81ea\u5c45\u3002 \u3000\u3000\u4f46\u662f\uff0c\u7ec8\u6709\u4e00\u5929\uff0c\u91d1\u94b1\u53d8\u5f97\u65e0\u7528\u4e86\uff0c\u6c99\u6f20\u4e2d\u65e2\u65e0\u98df\u6c34\uff0c\u66f4\u65e0\u98df\u7cae\uff0c\u4e8e\u662f\u8fd9\u4e2a\u81ea\u4ee5\u4e3a\u91d1\u94b1 \u4e07\u80fd\u7684\u5f3a\u8005\uff0c\u4fbf\u5728\u6c99\u6f20\u91cc\uff0c\u4f34\u7740\u65e0\u6570\u94b1\u8d22\uff0c\u56e0\u6e34\u800c\u6b7b\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u4e0d\u77e5\u9053\u81ea\u5df1\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u600e\u4f1a\u7a81\u7136\u60f3\u5230\u8fd9\u4e2a\u6545\u4e8b\u6765\u7684\u3002 \u3000\u3000\u90a3\u5f77\u4f5b\u8fd8\u662f\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u4e00\u4e2a\u6ee1\u6708\u7684\u590f\u591c\u91cc\uff0c\u81ea\u5df1\u5750\u5728\u4e00\u5f20\u9752\u7af9\u5236\u6210\u7684\u5c0f\u6905\u5b50\u4e0a\uff0c \u542c\u4e00\u4e2a\u5438\u7740\u65f1\u70df\u7684\u8001\u8005\uff0c\u5bf9\u81ea\u5df1\u8bf4\u7684\u3002 \u3000\u3000\u8fd9\u6545\u4e8b\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u5df2\u5fd8\u53bb\u4e86\u5f88\u591a\uff0c\u4f46\u4ed6\u5374\u89c9\u5f97\u81ea\u5df1\u7684\u60c5\u51b5\uff0c\u6b64\u523b\u7adf\u6709\u4e00\u4e9b\u548c\u8fd9\u6545\u4e8b\u76f8 \u50cf\u3002 \u3000\u3000\u4ed6\u81ea\u5e7c\u597d\u6b66\uff0c\u81ea\u4ee5\u4e3a\u53ea\u8981\u6b66\u529f\u8d85\u4eba\uff0c\u5929\u4e0b\u95f4\u6240\u6709\u4e0d\u5e73\u7684\u4e8b\uff0c\u5c31\u4e0d\u4f46\u4e0d\u4f1a\u843d\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c \u81ea\u5df1\u53cd\u53ef\u4f7f\u7740\u4e00\u8eab\u6b66\u529f\u4efb\u610f\u5c06\u5b83\u9664\u53bb\u3002 \u3000\u3000\u4f46\u6765\uff0c\u4ed6\u5374\u77e5\u9053\u4e16\u754c\u4e0a\u6709\u8bb8\u591a\u4e8b\uff0c\u7edd\u4e0d\u662f\u51ed\u7740\u6b66\u529f\u53ef\u4ee5\u89e3\u51b3\u5f97\u4e86\u7684\uff0c\u4e5f\u6b63\u5982\u5e76\u975e\u91d1\u94b1\u80fd \u591f\u89e3\u51b3\u4e00\u6837\u3002 \u3000\u3000\u6b64\u523b\u81ea\u5df1\u88ab\u56f0\u5728\u8fd9\u5c71\u7a9f\u91cc\uff0c\u8eab\u4e0a\u5c31\u6000\u6709\u6b66\u6797\u4e2d\u68a6\u5bd0\u4ee5\u6c42\u7684\u81f3\u5b9d\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u4f46\u81ea\u5df1\u5374 \u8fde\u770b\u4e0a\u4e00\u773c\uff0c\u90fd\u4e0d\u80fd\u591f\u3002 \u3000\u3000\u201c\u5929\u661f\u79d8\u7b08\u201d\u4e0a\u7eb5\u6709\u89e3\u7a74\u9053\u7684\u65b9\u6cd5\uff0c\u4f46\u6b64\u523b\u5bf9\u81ea\u5df1\u5374\u534a\u70b9\u7528\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u8d8a\u60f3\u8d8a\u591a\uff0c\u5fc3\u4e2d\u601d\u6f6e\u4e5f\u5c31\u8d8a\u4e71\u3002 \u3000\u3000\u5ffd\u7136\u53c8\u89c9\u5f97\u8fd9\u6545\u4e8b\u548c\u81ea\u5df1\u7684\u60c5\u51b5\uff0c\u4e00\u70b9\u90fd\u4e0d\u50cf\uff0c\u5ffd\u7136\u53c8\u60f3\u5230\u53e6\u4e00\u4e2a\u6545\u4e8b\u3002 \u3000\u3000\u4f46\u5ffd\u7136\u53c8\u89c9\u5f97\u9762\u524d\u5c31\u662f\u8427\u5357\u82f9\u7684\u5f71\u5b50\uff0c\u5ffd\u7136\u53c8\u770b\u5230\u4e07\u5929\u840d\u72de\u7b11\u7684\u9762\u5b54\u2026\u2026 \u3000\u3000\u4e16\u95f4\u6700\u96be\u63a7\u5236\u7684\u4e8b\uff0c\u6050\u6015\u5c31\u662f\u4eba\u4eec\u5fc3\u4e2d\u7684\u601d\u6f6e\u4e86\u3002\u4e16\u95f4\u4e4b\u6240\u4ee5\u6709\u5982\u6b64\u591a\u7684\u70e6\u607c\uff0c\u90a3\u4e5f\u5c31 \u662f\u4eba\u4eec\u5e38\u5e38\u4f1a\u60f3\u5230\u81ea\u5df1\u4e0d\u8be5\u60f3\u7684\u4e8b\u3002 \u3000\u3000\u4f0a\u98ce\u4e5f\u6b63\u662f\u5982\u6b64\uff0c\u4ed6\u8d8a\u60f3\u5c06\u601d\u6f6e\u5e73\u9759\u4e0b\u6765\uff0c\u5fc3\u91cc\u60f3\u7684\u4e8b\u5374\u53cd\u800c\u66f4\u591a\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u6b63\u81ea\u5fc3\u795e\u7d0a\u4e71\u4e4b\u9645\uff0c\u5c71\u7a9f\u6df1\u5904\uff0c\u4e5f\u4f20\u6765\u4e00\u9635\u7ec6\u788e\u7684\u811a\u6b65\u58f0\uff0c\u4ed6\u5168\u8eab\u8eba\u5728\u5730\u4e0a\uff0c\u662f \u4ee5\u542c\u5f97\u5206\u5916\u771f\u5207\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u811a\u6b65\u58f0\u6765\u52bf\u867d\u7f13\uff0c\u4f46\u58f0\u97f3\u5374\u8d8a\u6765\u8d8a\u663e\u7740\uff0c\u663e\u89c1\u5f97\u5df2\u6765\u5230\u8fd1\u524d\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u7d0a\u4e71\u7684\u601d\u6f6e\uff0c\u6b64\u65f6\u4e0d\u7981\u4e00\u626b\u800c\u7a7a\uff0c\u5374\u6362\u4e0a\u91ca\u91cd\u7684\u7591\u60d1\uff1a \u3000\u3000\u201c\u8fd9\u5c71\u7a9f\u4e2d\u600e\u4f1a\u6709\u811a\u6b65\u58f0\uff0c\u83ab\u975e\u662f\u91cc\u9762\u6f5c\u4f0f\u7740\u4ec0\u4e48\u731b\u517d\uff0c\u95fb\u5230\u751f\u4eba\u6c14\u5473\u2014\u2014\u5509\uff01\u60f3\u5fc5\u662f \u90a3\u4e07\u5929\u840d\u65e9\u5c31\u77e5\u9053\uff0c\u662f\u4ee5\u628a\u6211\u5173\u5728\u8fd9\u91cc\uff0c\u53c8\u70b9\u4e0a\u7a74\u9053\uff0c\u597d\u6559\u731b\u517d\u5403\u4e86\uff0c\u4ed6\u81ea\u5df1\u624b\u4e0a\u5374\u4e0d\u6cbe\u8840 \u8165\uff0c\u4e5f\u514d\u5f97\u8ba9\u4ed6\u5973\u513f\u770b\u5230\u4ed6\u4eb2\u8eab\u6740\u6211\uff0c\u5fc3\u91cc\u96be\u53d7\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u5347\u8d77\u4e00\u80a1\u5bd2\u610f\uff0c\u8d34\u5728\u5730\u4e0a\u7684\u80cc\u810a\uff0c\u4e5f\u5c31\u66f4\u52a0\u51b0\u51b7\u3002 \u3000\u3000\u5f20\u5f00\u773c\uff0c\u5374\u89c1\u8fd9\u672c\u6765\u9edd\u9ed1\u65e0\u6bd4\u7684\u5c71\u7a9f\uff0c\u5374\u7a81\u5730\u6709\u4e86\u4e9b\u4eae\u5149\uff0c\u800c\u4e14\u968f\u7740\u811a\u6b65\u58f0\u7684\u524d\u8fd1\uff0c\u800c \u8d8a\u6765\u8d8a\u4eae\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e0d\u7981\u53c8\u81ea\u5632\u5730\u6697\u7b11\u4e00\u4e0b\uff0c\u77e5\u9053\u8fd9\u811a\u6b65\u58f0\u7edd\u975e\u731b\u517d\u53d1\u51fa\u7684\uff0c\u56e0\u4e3a\u91ce\u517d\u624b\u91cc\uff0c\u4e00\u5b9a\u4e0d \u4f1a\u5e26\u7740\u706f\u706b\u3002 \u3000\u3000\u201c\u4f46\u4ece\u8fd9\u91cc\u9762\u51fa\u6765\u7684\u4eba\uff0c\u53c8\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4ed6\u867d\u60f3\u56de\u5934\u770b\u770b\uff0c\u4f46\u5374\u505a\u4e0d\u5230\uff0c\u53ea\u5f97\u5c06\u773c\u775b\u5c3d\u91cf\u4e0a\u7ffb\uff0c\u679c\u7136\u770b\u5230\u8fd9\u6761\u7a84\u957f\u7684\u5c71\u7a9f\u91cc\u9762\uff0c \u7f13\u7f13\u884c\u6765\u4e00\u6761\u4eba\u5f71\uff0c\u624b\u91cc\u6367\u7740\u4e00\u76cf\u6cb9\u706f\uff0c\u5728\u8fd9\u79cd\u9edd\u9ed1\u65e0\u6bd4\u7684\u5730\u65b9\uff0c\u4fbf\u663e\u5f97\u5206\u5916\u660e\u4eae\u3002 \u3000\u3000\u4ed6\u7a0d\u4e00\u95ed\u773c\u775b\uff0c\u518d\u5f20\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u8fd9\u6761\u4eba\u5f71\uff0c\u5df2\u6765\u81f3\u8fd1\u524d\u4e86\u3002 \u3000\u3000\u85c9\u7740\u6cb9\u706f\u4e4b\u5149\uff0c\u4ed6\u770b\u51fa\u8fd9\u4eba\u5f71\u7adf\u662f\u5973\u5b50\uff0c\u83b2\u8db3\u7ea4\u7ea4\uff0c\u7a7f\u7740\u4e00\u53cc\u7ee3\u91d1\u7684\u7d2b\u7ea2\u86ee\u9774\uff0c\u9774\u5b50\u4e0a \u662f\u6761\u6d45\u7d2b\u7684\u6563\u811a\u957f\u88e4\u3002\u518d\u5f80\u4e0a\u770b\uff0c\u53ea\u662f\u4e00\u53ea\u6625\u8471\u822c\u7684\u7389\u624b\u91cc\uff0c\u6367\u7740\u4e00\u76cf\u9752\u94dc\u94f8\u6210\u7684\u6cb9\u706f\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u7591\u4e91\u5927\u8d77\uff0c\u5e0c\u671b\u8fd9\u5c71\u7a9f\u4e2d\u795e\u79d8\u7684\u5973\u5b50\uff0c\u518d\u5f80\u524d\u8d70\u4e00\u4e9b\uff0c\u597d\u770b\u6e05\u695a\u5979\u957f\u5f97\u4ec0\u4e48\u6837 \u5b50\u3002 \u3000\u3000\u90a3\u77e5\u8fd9\u5973\u5b50\u8fc8\u6b65\u73ca\u73ca\uff0c\u5a1c\u884c\u81f3\u6b64\u5904\uff0c\u5c31\u505c\u4e0b\u811a\u6b65\uff0c\u4e0d\u5f80\u524d\u8d70\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u867d\u5c3d\u529b\u7ffb\u7740\u773c\u775b\uff0c\u5374\u4e5f\u65e0\u6cd5\u770b\u6e05\u8fd9\u5973\u5b50\u7684\u9762\u5bb9\u3002 \u3000\u3000\u5374\u542c\u90a3\u5973\u5b50\u8f7b\u8f7b\u60ca\u547c\u4e86\u4e00\u58f0\uff0c\u83b2\u8db3\u5fae\u62ac\uff0c\u50cf\u4e00\u9635\u98ce\u4f3c\u63a0\u8fc7\u4f0a\u98ce\uff0c\u63a0\u5230\u6d1e\u53e3\uff0c\u4f38\u624b\u63a8\u4e86\u63a8 \u5835\u5728\u6d1e\u53e3\u7684\u5de8\u77f3\uff0c\u50cf\u662f\u4e5f\u5927\u51fa\u610f\u5916\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u867d\u7136\u770b\u5230\u5979\u7684\u5168\u8eab\uff0c\u4f46\u5374\u53ea\u662f\u4e2a\u80cc\u5f71\uff0c\u53ea\u89c1\u8fd9\u5973\u5b50\u5934\u4e0a\u4e91\u9b13\u9ad8\u633d\uff0c\u5305\u7740\u4e00\u65b9\u7d2b \u7ee2\uff0c\u8eab\u4e0a\u4e5f\u7a7f\u7740\u4e00\u88ad\u7d2b\u8272\u888d\u5b50\uff0c\u4f46\u5374\u5bbd\u5927\u5df2\u6781\uff0c\u548c\u5979\u5a40\u5a1c\u7684\u8eab\u6750\uff0c\u5927\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u8d8a\u6765\u8d8a\u5947\u602a\uff0c\u53ea\u89c1\u8fd9\u5973\u5b50\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab\u5b50\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u51db\uff0c\u4e0d\u81ea\u89c9 \u5730\u95ed\u4e0a\u773c\u775b\uff0c\u4ed6\u672c\u6765\u4e9f\u6b32\u4e00\u7aa5\u8fd9\u5973\u5b50\u7684\u9762\u5bb9\uff0c\u4f46\u6b64\u523b\u5374\u7adf\u53c8\u4e0d\u6562\u770b\uff0c\u751f\u6015\u8fd9\u5973\u5b50\u8f6c\u8fc7\u8138\u6765\uff0c \u8138\u4e0a\u53ea\u662f\u4e00\u4ed8\u9ab7\u9ac5\u3002 \u3000\u3000\u90a3\u77e5\u5374\u542c\u8fd9\u5973\u5b50\u7a81\u5730\u4e00\u58f0\u60ca\u547c\uff0c\u63a5\u7740\u201c\u94db\u201d\u5730\u4e00\u58f0\uff0c\u50cf\u662f\u5979\u624b\u4e2d\u7684\u6cb9\u706f\uff0c\u4e5f\u843d\u5230\u5730\u4e0a\u3002 \u4f0a\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u8d76\u5fd9\u5f20\u5f00\u773c\u6765\uff0c\u5374\u89c1\u6d1e\u4e2d\u53c8\u662f\u6f06\u9ed1\u4e00\u7247\uff0c\u8fde\u8fd9\u5973\u7684\u8eab\u5f62\u90fd\u770b\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u7591\u56e2\u767e\u7ed3\uff0c\u5374\u82e6\u4e8e\u8fde\u5f00\u53e3\u95ee\u95ee\u90fd\u4e0d\u80fd\u591f\uff0c\u6697\u81ea\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5973\u5b50\u60f3\u662f\u770b\u8fc7\u6d1e\u4e2d\u6709\u4eba\uff0c\u56e0\u6b64\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u5979\u7684\u8eab\u6cd5\uff0c\u8f7b\u529f\u5df2\u53ef\u7b97\u662f\u9ad8\u624b\uff0c\u5979\u82e5\u5f53 \u6211\u662f\u4e2a\u6b79\u5f92\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u5148\u628a\u6211\u5236\u6b7b\uff0c\u5509\u2014\u2014\u6211\u5728\u6c5f\u6e56\u95ef\u6c64\uff0c\u51fa\u751f\u5165\u6b7b\u591a\u6b21\uff0c\u5982\u679c\u6b64\u523b \u4e0d\u660e\u4e0d\u767d\uff0c\u6b7b\u5728\u8fd9\u5973\u5b50\u624b\u4e0a\uff0c\u5c82\u975e\u51a4\u6789\u3002\u201d \u3000\u3000\u987b\u77e5\u4f0a\u98ce\u867d\u7136\u5df2\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4f46\u771f\u5982\u6fd2\u4e34\u7edd\u5883\uff0c\u4ecd\u4f1a\u4e0d\u81ea\u7981\u5730\u5347\u8d77\u8bb8\u591a\u5947\u602a\u7684\u60f3 \u6cd5\uff0c\u8fd9\u672c\u662f\u4eba\u7c7b\u901a\u6709\u7684\u5f31\u70b9\uff0c\u4ed6\u867d\u662f\u8fbe\u4eba\uff0c\u4f46\u7ec8\u7a76\u4e5f\u662f\u4eba\u7c7b\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u4f8b\u5916\u3002 \u3000\u3000\u9ed1\u6697\u4e4b\u4e2d\uff0c\u53ea\u542c\u5230\u8fd9\u5973\u5b50\u7684\u547c\u5438\u4e4b\u58f0\uff0c\u6781\u4e3a\u7c97\u91cd\uff0c\u663e\u89c1\u5979\u5fc3\u4e2d\u6b63\u81ea\u6fc0\u52a8\u65e0\u6bd4\uff0c\u4f0a\u98ce\u4e0d\u7981 \u53c8\u6697\u81ea\u5947\u602a\uff0c\u5979\u4e3a\u7740\u4ec0\u4e48\u5982\u6b64\u5462\uff1f \u3000\u3000\u90a3\u77e5\u8033\u7554\uff0c\u4e00\u9635\u98ce\u58f0\u55d6\u7136\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u8fd9\u5973\u5b50\u7adf\u53c8\u98de\u4e5f\u4f3c\u7684\u63a0\u5165\u6d1e\u91cc\uff0c\u8863\u8882\u98d8\u98ce\u58f0\u4e2d\uff0c \u4f3c\u4e4e\u8fd8\u9690\u9690\u542c\u5230\u8fd9\u5973\u5b50\u7684\u5598\u606f\u4e4b\u58f0\uff0c\u6bd4\u5148\u524d\u66f4\u52a0\u7c97\u91cd\uff0c\u4f46\u77ac\u606f\u4e4b\u95f4\uff0c\u53c8\u5168\u6ca1\u5165\u6d1e\u7a9f\u6df1\u5904\u3002 \u3000\u3000\u6b64\u4e3e\u5012\u662f\u5927\u51fa\u4f0a\u98ce\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5973\u5b50\u4f1a\u7a81\u7136\u79bb\u53bb\uff0c\u65e2\u672a\u5bf9\u81ea\u5df1\u6709\u6240\u4e3e\u52a8\uff0c \u751a\u81f3\u8fde\u8bdd\u90fd\u6ca1\u6709\u95ee\u4e00\u53e5\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8fd9\u5973\u5b50\u5982\u6b64\u4e3e\u52a8\uff0c\u786e\u662f\u5927\u51fa\u5e38\u7406\u4e4b\u5916\uff0c\u4f0a\u98ce\u5de6\u60f3\u53f3\u60f3\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u4e00\u4e2a \u7406\u7531\u6765\u3002\u5fc3\u4e2d\u6b63\u81ea\u7591\u60d1\u4e4b\u9645\uff0c\u90a3\u77e5\u6d1e\u7a9f\u6df1\u5904\uff0c\u5374\u53c8\u4f20\u51fa\u4e00\u9635\u7ec6\u788e\u7684\u811a\u6b65\uff0c\u53ea\u662f\u6bd4\u4e0a\u6b21\u6765\u5f97\u8fdc \u4e3a\u5feb\u901f\u3002 \u3000\u3000\u4f0a\u98ce\u51dd\u795e\u800c\u542c\uff0c\u5ffd\u5730\u542c\u5f97\u8fd9\u811a\u6b65\u58f0\u4e2d\uff0c\u8fd8\u5939\u6742\u7740\u4f0a\u5440\u513f\u8bed\u4e4b\u58f0\uff0c\u50cf\u662f\u4e00\u4e2a\u5c1a\u672a\u5b66\u8bed\u7684\u5e7c \u7ae5\u53d1\u51fa\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u5c1a\u672a\u6765\u53ca\u601d\u7d22\u4e4b\u524d\uff0c\u90a3\u811a\u6b65\u58f0\u5df2\u6765\u5230\u8033\u7554\uff0c\u98ce\u58f0\u54cd\u52a8\u5904\uff0c\u4ed6\u53ea\u89c9\u90a3\u5973\u5b50\u5df2\u6765\u5230\u8eab \u4fa7\uff0c\u4e00\u9635\u9635\u751c\u67d4\u7684\u9999\u6c14\uff0c\u6563\u4eba\u9f3b\u4e2d\u3002 \u3000\u3000\u4ed6\u4fa7\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u4e00\u56e2\u9ed1\u5f71\uff0c\u7acb\u5728\u8eab\u4fa7\uff0c\u624b\u91cc\u4f3c\u4e4e\u8fd8\u62b1\u7740\u4e00\u4e2a\u7a1a\u9f84\u5e7c\u7ae5\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u9ed8\u9ed8\u4f2b\u7acb\u4e86\u534a\u664c\uff0c\u7a81\u5730\u4fef\u4e0b\u8eab\u6765\uff0c\u4f38\u51fa\u4e00\u53ea\u624b\uff0c\u5728\u4f0a\u98ce\u8eab\u4e0a\u629a\u6478\u4e00\u904d\uff0c\u7136\u540e\u624b\u8155 \u4e00\u7ffb\uff0c\u5c06\u4f0a\u98ce\u7684\u8eab\u5b50\u53cd\u8f6c\u4e86\u8fc7\u53bb\uff0c\u201c\u62cd\uff0c\u62cd\u201d\u51e0\u58f0\uff0c\u6781\u5feb\u5730\u5728\u4f0a\u98ce\u80cc\u540e\u8170\u7554\uff0c\u62cd\u4e86\u4e94\u638c\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u65b9\u81ea\u6697\u53eb\u201c\u4e0d\u5999\u201d\uff0c\u90a3\u77e5\u5589\u95f4\u4e00\u677e\uff0c\u201c\u54b3\u201d\u5730\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\u6765\uff0c\u5168\u8eab\u6c14\u8840\uff0c\u7adf \u7acb\u523b\u901a\u884c\u65e0\u963b\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u6115\uff0c\u7f13\u4e86\u53e3\u6c14\uff0c\u633a\u8170\u7ad9\u4e86\u8d77\u6765\uff0c\u53ea\u89c1\u90a3\u4eba\u5f71\u4ecd\u9ed8\u9ed8\u5730\u7ad9\u5728\u5bf9\u9762\u3002 \u3000\u3000\u5c71\u7a9f\u91cc\u5bc2\u7136\u65e0\u58f0\uff0c\u53ea\u6709\u88ab\u62b1\u5728\u8fd9\u795e\u79d8\u5973\u5b50\u624b\u4e2d\u7684\u5a74\u7ae5\uff0c\u5728\u201c\u5440\uff0c\u5440\u201d\u5730\u5b66\u4eba\u8bed\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u773c\u524d\u4e00\u4eae\uff0c\u8fd9\u5973\u5b50\u624b\u4e2d\uff0c\u5df2\u591a\u4e86\u4e00\u53ea\u717d\u7740\u706b\u7684\u706b\u647a\u5b50\uff0c\u4f0a\u98ce\u9000\u540e\u4e00\u6b65\uff0c\u76ee\u5149\u7535\u4e5f\u4f3c\u7684\u671b \u5411\u8fd9\u5973\u5b50\u7684\u9762\u4e0a\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u4ed6\u53ea\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u8111\u4e2d\u4e00\u7247\u6df7\u6c8c\uff0c\u51e0\u4e4e\u518d\u4e5f\u65e0\u6cd5\u652f\u6301\u81ea\u5df1\u7684\u8eab \u8eaf\uff0c\u800c\u6447\u6447\u6b32\u5012\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6b64\u523b\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u624b\u91cc\u62b1\u7740\u4e00\u4e2a\u80a5\u80d6\u7684\u5a74\u513f\u7684\u7d2b\u886b\u5973\u5b50\uff0c\u7adf\u662f\u9500\u9b42\u592b\u4eba\u859b\u82e5\u74a7\uff01 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516d\u5341\u516d\u7ae0\u3000\u5982\u6b64\u4eba\u751f>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u516d\u7ae0\u3000\u5982\u6b64\u4eba\u751f \u3000\u3000\u706b\u647a\u4e0a\u7684\u706b\u7130\uff0c\u867d\u7136\u4e0d\u4eae\uff0c\u4f46\u5df2\u8db3\u591f\u4f7f\u5f97\u4ed6\u4eec\u770b\u6e05\u5f7c\u6b64\u7684\u9762\u5bb9\u3002 \u3000\u3000\u83b9\u83b9\u7684\u706b\u5149\uff0c\u7167\u5230\u5c71\u58c1\u4e0a\uff0c\u4f7f\u5f97\u957f\u6ee1\u82d4\u85d3\u7684\u5c71\u58c1\uff0c\u53d1\u51fa\u4e00\u79cd\u78a7\u7eff\u800c\u9634\u68ee\u989c\u8272\uff0c\u8fd9\u5374\u4e5f\u6b63 \u5982\u4f0a\u98ce\u6b64\u523b\u7684\u9762\u8272\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u5730\uff0c\u77aa\u5728\u8fd9\u66fe\u7ecf\u4ee4\u4ed6\u51e0\u4e4e\u5931\u53bb\u4e86\u751f\u5b58\u52c7\u6c14\u7684\u5973\u5b50\u8eab\u4e0a\uff0c\u7d27\u63e1\u7740\u7684\u53cc\u624b\uff0c \u4e5f\u5f00\u59cb\u98a4\u6296\u4e86\u8d77\u6765\u3002 \u3000\u3000\u62b1\u5728\u859b\u82e5\u74a7\u624b\u4e0a\u7684\u5a74\u513f\uff0c\u6eda\u52a8\u7740\u5927\u773c\u775b\uff0c\u770b\u5230\u4ed6\u7684\u6837\u5b50\uff0c\u201c\u54c7\u201d\u5730\u4e00\u58f0\u54ed\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u53cc\u76ee\u706b\u8d64\uff0c\u4ece\u859b\u82e5\u74a7\u8138\u4e0a\uff0c\u7f13\u7f13\u6ed1\u4e86\u4e0b\u53bb\uff0c\u53ea\u89c1\u5979\u6614\u5e74\u65e0\u6bd4\u5a40\u5a1c\u7684\u8eab\u8eaf\uff0c\u6b64\u523b\u7adf\u81c3 \u80bf\u4e0d\u582a\uff0c\u51dd\u76ee\u4e00\u671b\uff0c\u539f\u6765\u662f\u5df2\u6000\u6709\u8eab\u5b55\u3002 \u3000\u3000\u8fd9\u4f7f\u5f97\u4f0a\u98ce\u5fc3\u4e2d\uff0c\u7ede\u75db\u5f97\u4f3c\u5df2\u6ef4\u51fa\u8840\u6765\uff0c\u90a3\u77e5\u859b\u82e5\u74a7\u5e7d\u5e7d\u4e00\u7b11\uff0c\u5374\u9053\uff1a \u3000\u3000\u201c\u5357\u4eba\uff01\u4f60\u60f3\u4e0d\u5230\u662f\u6211\u5427\uff01\u522b\u8fd9\u4ed8\u6837\u5b50\u770b\uff0c\u597d\u4e0d\u597d    \u2014\u2014\u201d \u3000\u3000\u4f0a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u8e42\u811a\u7a9c\u4e86\u8fc7\u53bb\uff0c\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u4f60\u7adf\u8fd8\u6709\u8138\u6765\u89c1\u6211\uff1f\u201d \u3000\u3000\u5fc3\u60c5\u7684\u8fc7\u5ea6\u75db\u82e6\u548c\u6fc0\u52a8\uff0c\u4f7f\u5f97\u4ed6\u5931\u53bb\u4e86\u7406\u667a\u7684\u63a7\u5236\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u53c8\u6709\u4ec0\u4e48\u4eba\u80fd\u63a7\u5236 \u5f97\u4f4f\u81ea\u5df1\u54e9\uff01 \u3000\u3000\u859b\u82e5\u74a7\u5de6\u624b\u73af\u62b1\u7740\u5a74\u513f\uff0c\u53f3\u624b\u5b66\u7740\u706b\u647a\u5b50\uff0c\u5fae\u4e00\u6298\u8170\uff0c\u8eab\u5f62\u7fe9\u7136\u6ed1\u4e86\u5f00\u53bb\uff0c\u53e3\u4e2d\u5374\u9053\uff1a \u3000\u3000\u201c\u5357\u4eba\uff01\u4f60\u813e\u6c14\u600e\u5730\u53d8\u5f97\u8fd9\u4e48\u706b\u66b4\uff0c\u4f60\u770b\uff01\u628a\u4f60\u7684\u513f\u5b50\u90fd\u5413\u54ed\u4e86\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\uff0c\u50cf\u652f\u7bad\u4f3c\u7684\uff0c\u76f4\u5c04\u5165\u4f0a\u98ce\u5fc3\u91cc\uff0c\u4e00\u77ac\u95f4\uff0c\u4ed6\u5168\u8eab\u7684\u8840\u6db2\uff0c\u4e0d\u7981\u90fd\u7acb\u523b\u4e3a\u4e3a\u51dd \u7ed3\uff0c\u7f13\u7f13\u4fa7\u8fc7\u8eab\u6765\uff0c\u5389\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u859b\u82e5\u74a7\u5de6\u624b\u6447\u52a8\u7740\u6000\u91cc\u7684\u5a74\u513f\uff0c\u6e29\u67d4\u5730\u8bf4\u7740\uff1a \u3000\u3000\u201c\u5c0f\u5357\uff01\u522b\u54ed\uff0c\u8fd9\u662f\u4f60\u7684\u7239\u7239\u3002\u6765\uff01\u7b11\u4e00\u4e2a\uff0c\u7b11\u7ed9\u4f60\u7239\u7239\u770b\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5927\u559d\uff1a \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u811a\u6b65\u52a8\u5904\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u5230\u859b\u82e5\u74a7\u9762\u524d\u3002 \u3000\u3000\u859b\u541b\u74a7\u5374\u8f7b\u8f7b\u4e00\u7b11\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u5c31\u662f\u4f60\u7684\u513f\u5b50\uff0c\u4eca\u5e74\u5df2\u7ecf\u4e09\u5c81\u4e86\uff0c\u5374\u8fd8\u6ca1\u6709\u89c1\u8fc7\u7238\u7238\u54e9\uff01\u201d \u3000\u3000\u5de6\u624b\u4e00\u62ac\uff0c\u7adf\u5c06\u624b\u4e0a\u7684\u5a74\u513f\uff0c\u9001\u5230\u4f0a\u98ce\u9762\u524d\u3002\u8fd9\u5a74\u513f\u5c0f\u624b\u4e00\u5f20\uff0c\u7adf\u4e0d\u54ed\u4e86\uff0c\u5f20\u7740\u624b\u6251\u5230 \u4f0a\u98ce\u8eab\u4e0a\u3002 \u3000\u3000\u5df2\u7ecf\u5168\u7136\u6115\u4f4f\u4e86\u7684\u4f0a\u98ce\uff0c\u4f46\u89c9\u81ea\u5df1\u5fc3\u91cc\u7a7a\u7a7a\u6d1e\u6d1e\u5730\uff0c\u4e0d\u77e5\u9053\u81ea\u5df1\u8be5\u600e\u4e48\u597d\uff0c\u4e0b\u610f\u8bc6\uff0c\u5730 \u4f38\u624b\u63a5\u8fc7\u8fd9\u5b69\u5b50\uff0c\u5374\u542c\u859b\u82e5\u74a7\u53c8\u81ea\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u770b\uff01\u5c0f\u5357\u591a\u4e56\uff01\u4ed6\u8fd8\u8ba4\u5f97\u7238\u7238\u54e9\u3002\u201d \u3000\u3000\u5de6\u624b\u8f7b\u8f7b\u4e00\u62e2\u9b13\u53d1\uff0c\u56de\u8fc7\u8eab\u5b50\uff0c\u7f13\u6b65\u671d\u6d1e\u7a9f\u6df1\u5904\u8d70\u4e86\u8fc7\u53bb\uff0c\u4e00\u9762\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u9ed1\u5f97\u5f88\uff0c\u5feb\u8ddf\u6211\u4e00\u9f50\u8fdb\u53bb\uff0c\u522b\u8ba9\u5c0f\u5357\u5413\u7740\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6014\u6014\u5730\u62b1\u7740\u624b\u4e2d\u7684\u5a74\u513f\uff0c\u4f46\u89c1\u8fd9\u5b69\u5b50\u7adf\u5e26\u7740\u4e00\u8138\u65e0\u90aa\u7684\u7b11\u5bb9\uff0c\u5728\u671b\u7740\u81ea\u5df1\uff0c\u4e00\u53cc\u5c0f \u624b\uff0c\u4e0d\u4f4f\u5730\u5728\u81ea\u5df1\u773c\u524d\u6643\u52a8\u7740\uff0c\u7adf\u771f\u7684\u50cf\u662f\u8ba4\u8bc6\u81ea\u5df1\u4f3c\u7684\uff0c\u4ed6\u4e0d\u7981\u5fc3\u4e2d\u5927\u52a8\uff0c\u62a2\u6b65\u8ddf\u4e86\u4e0a \u53bb\uff0c\u4e00\u9762\u559d\u9053\uff1a \u3000\u3000\u201c\u82e5\u2014\u2014\u859b\u82e5\u74a7\uff0c\u4f60\u8fd9\u662f\u4e0d\u662f\u53c8\u5728\u9a97\u6211\uff01\u201d \u3000\u3000\u859b\u82e5\u74a7\u5934\u4e5f\u4e0d\u56de\uff0c\u6781\u5feb\u5730\u5728\u524d\u9762\u8d70\u7740\uff0c\u9f3b\u5b54\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u7b97\u7b97\u770b\uff0c\u6211\u79bb\u5f00\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\uff0c\u8fd9\u5b69\u5b50\u6709\u591a\u5927\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u7d27\u4e86\u7d27\u624b\u4e2d\u7684\u5b69\u5b50\uff0c\u4ed6\u51e0\u4e4e\u6ca1\u6709\u52c7\u6c14\u63a5\u53d7\u8fd9\u4e2a\u4e8b\u5b9e\u3002 \u3000\u3000\u7136\u800c\uff0c\u4e00\u79cd\u7236\u5b50\u7531\u751f\u4ff1\u6765\uff0c\u65e0\u6cd5\u78e8\u706d\u7684\u5929\u6027\uff0c\u5374\u4f7f\u4ed6\u6b64\u523b\u5c06\u4efb\u4f55\u4e8b\u90fd\u5fd8\u4e86\u3002\u811a\u4e0b\u52a0\u52b2\uff0c \u671b\u524d\u62a2\u51fa\u51e0\u6b65\uff0c\u5374\u89c1\u859b\u82e5\u74a7\u8eab\u5f62\u4e00\u8f6c\uff0c\u5df2\u8f6c\u5165\u4e00\u4e2a\u6570\u4e08\u65b9\u5706\u7684\u6d1e\u7a9f\u91cc\u3002 \u3000\u3000\u4eba\u751f\u7684\u9645\u9047\uff0c\u53c8\u662f\u591a\u4e48\u5947\u5999\uff0c\u8fd9\u6d1e\u7a9f\u6628\u591c\u66fe\u6539\u53d8\u4e86\u8427\u5357\u82f9\u4e00\u751f\u7684\u547d\u8fd0\uff0c\u5982\u4eca\u5374\u53c8\u6765\u6349\u5f04 \u4f0a\u98ce\u4e86\u3002 \u3000\u3000\u4ed6\u6014\u6014\u5730\u671b\u7740\u81ea\u5df1\u624b\u4e2d\u7684\u5b69\u5b50\uff0c\u8fd9\u5b69\u5b50\u662f\u4ed6\u7684\u8089\u4e2d\u4e4b\u8089\uff0c\u9aa8\u4e2d\u4e4b\u9aa8\u3002 \u3000\u3000\u7136\u800c\u8fd9\u5b69\u5b50\u5374\u53c8\u662f\u4ece\u4e00\u4e2a\u88ab\u81ea\u5df1\u6df1\u75db\u6076\u7edd\u7684\u6deb\u8d31\u5973\u5b50\u809a\u4e2d\u751f\u51fa\u6765\u7684\uff0c\u800c\u8fd9\u5973\u5b50\u6b64\u523b\u6000\u7740 \u7684\u53e6\u4e00\u8eab\u5b55\uff0c\u5374\u662f\u81ea\u5df1\u6df1\u4ec7\u5927\u6068\u7684\u9aa8\u8840\u3002 \u3000\u3000\u8fd9\u79cd\u5fae\u5999\u800c\u590d\u6742\u7684\u5173\u7cfb\uff0c\u53c8\u6709\u4ec0\u4e48\u4eba\u80fd\u591f\u6574\u7406\u5f97\u51fa\u5934\u7eea\u6765\u5462\uff1f \u3000\u3000\u53c8\u6709\u4ec0\u4e48\u4eba\u80fd\u544a\u8bc9\u4f0a\u98ce\uff0c\u4ed6\u6b64\u523b\u7a76\u7adf\u5e94\u8be5\u5982\u4f55\u505a\u5462\uff1f \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u7684\u4f0a\u98ce\uff0c\u81ea\u7136\u662f\u6df7\u4e71\u800c\u8ff7\u5931\u7684\uff0c\u4ed6\u5446\u5446\u5730\u7ad9\u5728\u8fd9\u6d1e\u7a9f\u7684\u4e2d\u592e\uff0c\u770b\u5230\u859b\u82e5\u74a7 \u70b9\u8d77\u67b6\u5728\u5c71\u58c1\u4e0a\u7684\u4e00\u76cf\u94dc\u706f\uff0c\u5377\u706d\u4e86\u624b\u4e2d\u7684\u706b\u647a\u5b50\uff0c\u7f13\u7f13\u8d70\u5230\u5e8a\u8fb9\uff0c\u548c\u8eab\u5012\u5367\u4e86\u53bb\uff0c\u4e00\u9762\u7b11 \u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\u4f60\u603b\u8be5\u76f8\u4fe1\u8fd9\u5b69\u5b50\u662f\u4f60\u7684\u4e86\u5427\uff1f\u4e0d\u8fc7\u2014\u2014\u5582\uff01\u8fd9\u4e5f\u771f\u6709\u70b9\u5947\u602a\uff0c\u4f60\u600e\u4e48\u4f1a\u8dd1\u5230\u8fd9 \u91cc\u6765\u7684\uff1f\u53c8\u88ab\u4eba\u70b9\u4e2d\u7a74\u9053\uff0c\u800c\u4e14\u8fd8\u88ab\u4eba\u4ece\u6d1e\u53e3\u5916\u9762\u5835\u6b7b\u4e86\uff1f\u521a\u624d\u6211\u4e00\u770b\u89c1\u5012\u5728\u5730\u4e0a\u7684\u662f\u4f60\uff0c \u53ef\u771f\u628a\u6211\u5413\u4e86\u4e00\u8df3\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5207\u9f7f\u6697\u9a82\uff0c\u81ea\u5df1\u5f53\u5e74\u771f\u662f\u778e\u4e86\u773c\u775b\uff0c\u5343\u6311\u4e07\u9009\uff0c\u5374\u9009\u4e2d\u4e86\u5982\u6b64\u4e00\u4e2a\u5973\u5b50\u505a\u59bb\u5b50\uff0c\u5982 \u4eca\u4ed6\u867d\u5df2\u5f97\u5230\u4e86\u6559\u8bad\uff0c\u77e5\u9053\u4e00\u4e2a\u4eba\u5185\u5fc3\u7684\u7f8e\u4e3d\uff0c\u8fdc\u6bd4\u5916\u8868\u7684\u7f8e\u4e3d\u91cd\u8981\u5f97\u591a\uff0c\u4f46\u662f\u8fd9\u6559\u8bad\u5374\u662f \u591a\u4e48\u6b8b\u9177\uff01 \u3000\u3000\u4ed6\u671b\u7740\u5012\u5367\u5728\u77f3\u5e8a\u4e0a\uff0c\u8fd9\u66fe\u7ecf\u88ab\u81ea\u5df1\u5168\u5fc3\u7231\u8fc7\u7684\u5973\u5b50\uff0c\u5fc3\u4e2d\u5207\u9f7f\u6697\u5fd6\uff1a \u3000\u3000\u201c\u65b9\u624d\u5979\u770b\u5230\u6211\uff0c\u5374\u4e0d\u6562\u89c1\u6211\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u6211\u7edd\u4e0d\u4f1a\u653e\u8fc7\u5979\uff0c\u662f\u4ee5\u53c8\u628a\u8fd9\u5b69\u5b50\u5e26\u51fa\u6765\uff0c \u5509\u2014\u2014\u6211\u867d\u7136\u6068\u5979\u5165\u9aa8\uff0c\u5374\u53c8\u600e\u80fd\u5bf9\u4ed8\u6211\u4eb2\u751f\u9aa8\u8089\u7684\u5988\u5988\u5462\uff1f \u3000\u3000\u201c\u859b\u82e5\u74a7\uff01\u4f60\u5916\u8868\u867d\u7136\u7f8e\u4e3d\u5982\u6614\uff0c\u5185\u5fc3\u5374\u6bd4\u4ee5\u524d\u66f4\u4e3a\u4e11\u6076\u4e86\uff01\u5509\u2014\u2014\u5929\u5440\uff01\u4e3a\u4ec0\u4e48\u53c8\u504f \u504f\u8ba9\u6211\u9047\u7740\u8fd9\u4e9b\u4e8b\uff0c\u8fd9\u4e0d\u662f\u592a\u4e0d\u516c\u5e73\u4e86\u5417\uff1f\u201d \u3000\u3000\u859b\u82e5\u74a7\u5728\u5e8a\u4e0a\u5a07\u6175\u7684\u7ffb\u4e86\u4e2a\u8eab\uff0c\u9762\u4e0a\u53c8\u6cdb\u8d77\u4e86\u6843\u82b1\u822c\u7684\u7b11\u5bb9\uff0c\u5a07\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u5582\uff01\u4f60\u600e\u5730\u4e0d\u8bf4\u8bdd\uff0c\u522b\u5fd8\u4e86\u521a\u624d\u662f\u6211\u628a\u4f60\u6551\u56de\u6765\u7684\u5440\uff01\u90a3\u65f6\u5019\u53ea\u8981\u6211\u4e00\u4f38\u624b\uff0c\u4f60\u5c31\u5b8c \u4e86\uff0c\u4f55\u51b5\u5c31\u662f\u6211\u4e0d\u4f38\u624b\uff0c\u4f60\u53c8\u6371\u5f97\u8fc7\u591a\u5c11\u65f6\u5019\u5462\uff01\u5509\u2014\u2014\u4f60\u8fd9\u4eba\u771f\u6ca1\u826f\u5fc3\uff0c\u4e5f\u4e0d\u6765\u8c22\u8c22 \u6211\u3002\u201d \u3000\u3000\u4f0a\u98ce\u51b7\u54fc\u4e00\u58f0\uff0c\u52c9\u5f3a\u538b\u7740\u5fc3\u91cc\u7684\u6124\u6068\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u90a3\u8427\u65e0\u5462\uff1f\u4f60\u4e0d\u8ddf\u7740\u4ed6\uff0c\u5374\u8dd1\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u859b\u82e5\u74a7\u624b\u8098\u4e00\u7528\u529b\uff0c\u4ece\u5e8a\u4e0a\u7ffb\u8eab\u5750\u4e86\u8d77\u6765\uff0c\u6ee1\u542b\u7b11\u5bb9\u7684\u9762\u5e9e\uff0c\u6b64\u523b\u7a81\u5730\u7b3c\u4e0a\u4e86\u4e00\u5c42\u79cb \u971c\uff0c\u72e0\u72e0\u5730\u671b\u7740\u4f0a\u98ce\uff0c\u6068\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u95ee\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u95ee\u4ed6\uff0c\u8c01\u95ee\u4ed6\uff1f\u4ed6\u867d\u7136\u6bc1\u4e86\u6211\u7684\u5bb6\u5ead\uff0c\u593a\u53bb\u6211\u7684\u59bb\u5b50\uff0c\u4f46\u6211\u5374\u8981\u8c22\u8c22\u4ed6\uff0c\u56e0\u4e3a\u4ed6 \u8ba9\u6211\u770b\u5230\u4f60\u90a3\u6deb\u8d31\uff0c\u5351\u9119\u7684\u5fc3\uff0c\u82e5\u4e0d\u662f\u4ed6\uff0c\u6211\u5c31\u8981\u548c\u4f60\u8fd9\u79cd\u4eba\u5b88\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u58c1\u95f4\u7684\u706f\u5149\uff0c\u7167\u5728\u859b\u82e5\u74a7\u5a07\u7f8e\u5982\u82b1\u7684\u8138\u4e0a\uff0c\u53ea\u89c1\u5979\u8299\u84c9\u4e3a\u9762\uff0c\u6625\u5c71\u4e3a\u7709\uff0c\u4e00\u53cc\u526a\u6c34\u53cc\u77b3 \u4e0a\u8986\u76d6\u7740\u957f\u957f\u7684\u776b\u6bdb\uff0c\u7ea2\u5982\u6a31\u6843\u7684\u6a31\u5507\u4e0a\uff0c\u662f\u79c0\u4e3d\u800c\u633a\u76f4\u7684\u9f3b\u5b50\uff0c\u8fd9\u9500\u9b42\u592b\u4eba\u859b\u82e5\u74a7\uff0c\u679c\u7136 \u7f8e\u5165\u9aa8\u9ad3\uff0c\u4f46\u662f\u5979\u76ee\u5149\u6d41\u8f6c\u4e0d\u6b47\uff0c\u9762\u8272\u9634\u6697\u4e0d\u5b9a\uff0c\u5374\u663e\u89c1\u5f97\u662f\u4e2a\u96be\u4ee5\u6349\u6478\u7684\u5973\u5b50\u3002 \u3000\u3000\u6b64\u523b\u5979\u7adf\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u4f38\u51fa\u90a3\u53ea\u6b3a\u971c\u8d5b\u96ea\u7684\u9752\u8471\u7389\u624b\uff0c\u5728\u773c\u7736\u65c1\u8fb9\u8f7b\u8f7b\u62b9\u4e86\u4e00\u4e0b\uff0c\u7f13 \u7f13\u9053\uff1a \u3000\u3000\u201c\u5357\u4eba\uff01\u6211\u77e5\u9053\u4f60\u6068\u6211\uff0c\u4f46\u662f\u4f60\u4e5f\u5f97\u539f\u8c05\u6211\uff0c\u6211\u53ea\u662f\u4e00\u4e2a\u67d4\u5f31\u7684\u5973\u5b50\uff0c\u867d\u7136\u4e5f\u4f1a\u4e9b\u6b66 \u529f\uff0c\u4f46\u600e\u80fd\u6297\u62d2\u5f97\u4e86\u8427\u65e0\uff0c\u4f55\u51b5\u2014\u2014\u4f60\u90a3\u65f6\u53c8\u4e0d\u5728\u5bb6\u3002\u5357\u4eba\uff01\u6211\u4eec\u662f\u90a3\u4e48\u591a\u5e74\u7684\u592b\u59bb\u4e86\uff0c\u6709 \u4ec0\u4e48\u8bdd\u4e0d\u80fd\u8bf4\u5f00\u7684\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u2026\u2026\u6211\u5fc3\u91cc\u2026\u2026\u8fd8\u662f\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u8fd9\u72e1\u9ee0\u800c\u7f8e\u8c8c\u7684\u5973\u5b50\uff0c\u7adf\u201c\u54c7\u201d\u5730\u4e00\u58f0\uff0c\u54ed\u4e86\u8d77\u6765\uff0c\u53cd\u8eab\u6251\u5230\u5e8a\u4e0a\uff0c\u9999\u80a9\u4e0d \u4f4f\u8d77\u4f0f\u7740\uff0c\u50cf\u662f\u54ed\u5f97\u6781\u4e3a\u60b2\u75db\u3002 \u3000\u3000\u4f0a\u98ce\u671b\u7740\u5979\u8d77\u4f0f\u7740\u7684\u80a9\u5934\uff0c\u5fc3\u91cc\u867d\u7136\u6709\u65e0\u6bd4\u7684\u538c\u6076\uff0c\u4f46\u5374\u53c8\u4e0d\u7981\u53d1\u51fa\u4e00\u79cd\u96be\u8a00\u7684\u60c5\u611f\u3002 \u3000\u3000\u62b1\u5728\u4ed6\u624b\u4e2d\u7684\u5a74\u513f\uff0c\u5c0f\u624b\u5f20\u4e86\u4e24\u5f20\uff0c\u4e5f\u201c\u54c7\u201d\u5730\u4e00\u58f0\uff0c\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u7eb5\u7136\u5fc3\u80a0\u5982\u94c1\uff0c\u7eb5\u7136\u4ed6\u4e5f\u77e5\u9053\u4f0f\u5728\u5e8a\u4e0a\u50cf\u662f\u5728\u75db\u54ed\u7740\u7684\u5973\u4eba\uff0c\u8868\u9762\u867d\u5728\u75db\u54ed\u7740\uff0c\u5fc3 \u91cc\u5374\u4e0d\u77e5\u53c8\u5728\u8f6c\u7740\u4ec0\u4e48\u5ff5\u5934\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e24\u4eba\u7684\u54ed\u58f0\uff0c\u5374\u4f7f\u5f97\u4ed6\u7684\u5fc3\u53c8\u5f00\u59cb\u4e71\u4e86\uff0c\u4e71\u5f97\u50cf\u66ae\u6625\u65f6\u8282\uff0c\u6c5f\u5357\u6cb3\u5cb8\u8fb9\u7684\u6625\u8349\uff0c \u4ef3\u4e0d\u7981\u6697\u6697\u4f69\u670d\u201c\u6731\u4e70\u81e3\u201d\uff0c\u6709\u201c\u9a6c\u524d\u6cfc\u6c34\u201d\u7684\u51b3\u65ad\uff0c\u4eba\u4eec\u62d2\u7edd\u4e00\u4e2a\u66fe\u7ecf\u505a\u8fc7\u81ea\u5df1\u59bb\u5b50\u7684\u4eba \u7684\u8981\u6c42\uff0c\u8be5\u662f\u591a\u4e48\u56f0\u96be\uff0c\u56f0\u96be\u5f97\u51e0\u4e4e\u4e0d\u80fd\u505a\u5230\u7684\u4e8b\u5440\uff01 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u4f38\u51fa\u90a3\u53ea\u66fe\u7ecf\u632b\u8d25\u8fc7\u4e0d\u77e5\u51e0\u8bb8\u6b66\u6797\u9ad8\u624b\u7684\u94c1\u638c\uff0c\u5728\u4ed6\u6000\u4e2d\u90a3\u5929\u771f\u800c\u65e0 \u90aa\u7684\u5b69\u5b50\u8eab\u4e0a\uff0c\u8f7b\u8f7b\u62cd\u52a8\u7740\uff0c\u5f20\u53e3\u60f3\u8bf4\u8bdd\uff0c\u5374\u53c8\u4e0d\u77e5\u8be5\u600e\u4e48\u8bf4\u624d\u597d\uff0c\u7f13\u7f13\u5730\u8d70\u5230\u5e8a\u524d\u3002 \u3000\u3000\u90a3\u77e5\u859b\u82e5\u74a7\u7a81\u5730\u7ffb\u8eab\u5750\u4e86\u8d77\u6765\uff0c\u4f38\u51fa\u7ea4\u624b\uff0c\u4e00\u62b9\u6cea\u75d5\uff0c\u54fd\u54bd\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u7ba1\u4f60\u8fd8\u8981\u4e0d\u8981\u6211\uff0c\u53cd\u6b63\u6211\u4eec\u6b64\u523b\u88ab\u56f0\u5728\u8fd9\u91cc\uff0c\u6d1e\u53e3\u90a3\u5757\u5927\u77f3\uff0c\u91cd\u903e\u5343\u65a4\uff0c\u6211\u4eec\u4e24 \u4eba\u4e5f\u63a8\u4e0d\u5f00\u5b83\uff0c\u800c\u4e14\u2026\u2026\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u4e5f\u4e0d\u60f3\u6d3b\u4e86\uff0c\u53ef\u662f\u6211\u4eec\u73b0\u5728\u603b\u7b97\u53c8\u5728\u4e00\u8d77\uff0c\u8fd9\u4e5f\u8bb8 \u662f\u8001\u5929\u53ef\u601c\u6211\uff0c\u8ba9\u6211\u80fd\u518d\u89c1\u7740\u4f60\uff0c\u6211\u2026\u2026\u6211\u4e0d\u8981\u542c\u4f60\u90a3\u4e9b\u96be\u542c\u7684\u8bdd\uff0c\u4f60\u8981\u662f\u8fd8\u6068\u6211\uff0c\u4f60\u5c31\u4e00 \u5200\u628a\u6211\u6740\u4e86\u4e5f\u597d\u3002\u201d \u3000\u3000\u4f0a\u98ce\u671b\u4e86\u624b\u4e2d\u7684\u5b69\u5b50\u4e00\u773c\uff0c\u4e0d\u7981\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\u3002\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u9047\u7740\u7684\u56f0\u5883\uff0c\u867d\u6709\u4e0d\u5c11\uff0c \u4f46\u53d6\u820d\u4e4b\u95f4\uff0c\u5374\u4ece\u672a\u6709\u66f4\u56f0\u96be\u4e8e\u6b64\u523b\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u5982\u6d8c\uff0c\u4fef\u9996\u51dd\u601d\u4e86\u534a\u664c\uff0c\u62b1\u5728\u4ed6\u624b\u4e0a\u7684\u5b69\u5b50\uff0c\u53c8\u6b62\u4f4f\u4e86\u54ed\u58f0\uff0c\u4f38\u51fa\u5c0f\u624b\uff0c\u5728 \u4ed6\u5df2\u7565\u4e3a\u663e\u5f97\u6709\u4e9b\u6194\u60b4\uff0c\u4f46\u4ecd\u4e0d\u5931\u82f1\u4fca\u7684\u9762\u5b54\u4e0a\uff0c\u8f7b\u8f7b\u629a\u6478\u7740\u3002 \u3000\u3000\u8fd9\u53ea\u5c0f\u624b\uff0c\u50cf\u662f\u5e26\u7ed9\u4ed6\u4e00\u79cd\u65e0\u6bd4\u5de8\u5927\u7684\u529b\u91cf\uff0c\u4f7f\u5f97\u4ed6\u500f\u7136\u6062\u590d\u4e86\u751f\u547d\u7684\u52c7\u6c14\u3002\u4e8e\u662f\u4ed6\u62ac \u8d77\u5934\u6765\uff0c\u6c89\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u53ef\u6709\u98df\u7cae\uff1f\u201d \u3000\u3000\u859b\u82e5\u74a7\u70b9\u4e86\u70b9\u5934\uff0c\u9762\u4e0a\u5374\u53c8\u63a0\u8fc7\u4e00\u4e1d\u5bd2\u610f\uff0c\u6068\u58f0\u53c8\u9053\uff1a \u3000\u3000\u201c\u5357\u4eba\uff01\u4f60\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u54fc\uff0c\u8fd9\u91cc\u5c31\u662f\u90a3\u8427\u65e0\u6deb\u4e50\u7684\u5730\u65b9\uff0c\u4ed6\u5728\u5916\u9762\u5f04\u5230\u5973 \u4eba\uff0c\u5c31\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u4ed6\u8fd8\u4ee5\u4e3a\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u4e00\u987f\uff0c\u4f0a\u98ce\u671b\u7740\u9762\u524d\u8fd9\u5916\u8868\u7684\u7f8e\u8c8c\uff0c\u5df2\u63a9\u4e0d\u4f4f\u5185\u5fc3\u7684\u4e11\u6076\u7684\u5973\u5b50\uff0c\u4e0d\u7981\u5347\u8d77\u4e00\u9635 \u6076\u5fc3\u7684\u611f\u89c9\uff0c\u5374\u542c\u5979\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u6211\u5374\u60f3\u4e0d\u5230\uff0c\u6628\u5929\u665a\u4e0a\u4ed6\u642d\u4e0a\u7684\u5973\u5b50\uff01\u201d \u3000\u3000\u5979\u6068\u6076\u5730\u8bf4\u7740\uff0c\u4e00\u9762\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u65b9\u624b\u5e15\u6765\uff0c\u626c\u4e86\u626c\uff0c\u63a5\u7740\u9053\uff1a \u201c\u5c45\u7136\u662f\u6f47\u6e58\u5983\u5b50\u8427\u5357\u82f9\uff01\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516d\u5341\u4e03\u7ae0\u3000\u4e8c\u5341\u5929\u4e2d>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u4e03\u7ae0\u3000\u4e8c\u5341\u5929\u4e2d \u3000\u3000\u4f0a\u98ce\u4f46\u89c9\u8033\u7554\u8f70\u7136\u4e00\u58f0\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u8fc7\u53bb\uff0c\u62a2\u8fc7\u90a3\u6761\u624b\u5e15\uff0c\u63d0\u8d77\u4e00\u770b\uff0c\u53ea\u89c1\u8fd9\u6761\u6de1\u9752 \u8272\u7684\u624b\u5e15\u89d2\u4e0a\uff0c\u679c\u7136\u7ee3\u7740\u6df1\u84dd\u8272\u7684\u201c\u5357\u82f9\u201d\u4e8c\u5b57\u3002 \u3000\u3000\u859b\u82e5\u74a7\u4e00\u624b\u63a5\u8fc7\u90a3\u5df2\u54ed\u4e86\u8d77\u6765\u7684\u5b69\u5b50\uff0c\u4e00\u9762\u53c8\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4eca\u5929\u6211\u5230\u8fd9\u91cc\u6765\u7684\u65f6\u5019\uff0c\u563f\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u5f20\u5e8a\u4e0a\u4e71\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u5730\u4e0a\u8fd8\u6709\u8fd9\u5757\u624b \u5e15\uff0c\u6211\u4e00\u770b\u5c31\u77e5\u9053\u662f\u8427\u5357\u82f9\u90a3\u59ae\u5b50\u7684\u2014\u2014\u201d \u3000\u3000\u4f0a\u98ce\u5389\u53f1\u4e00\u58f0\uff1a \u3000\u3000\u201c\u4f4f\u58c1\uff01\u201d \u3000\u3000\u5374\u89c1\u859b\u82e5\u74a7\u5403\u60ca\u5730\u671b\u7740\u81ea\u5df1\uff0c\u4e8e\u662f\u6697\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fd9\u79cd\u65e0\u803b\u4e4b\u4e8b\uff0c\u8bf7\u4f60\u518d\u83ab\u5728\u6211\u9762\u524d\u63d0\u8d77\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u53c8\u600e\u80fd\u4e0d\u63a9\u9970\u4f4f\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u4ed6\u9762\u4e0a\u808c\u8089\uff0c\u65e0\u6cd5\u63a7\u5236\u5730\u626d\u66f2\u8d77\u6765\u3002 \u3000\u3000\u4e16\u95f4\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u8a00\u8bcd\uff0c\u80fd\u5f62\u5bb9\u4ed6\u6b64\u523b\u7684\u5fc3\u5883\uff01 \u3000\u3000\u4e5f\u66f4\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u8a00\u8bcd\uff0c\u80fd\u5f62\u5bb9\u4ed6\u5bf9\u90a3\u8427\u65e0\u7684\u4ec7\u6068\uff01 \u3000\u3000\u4f46\u859b\u82e5\u74a7\uff0c\u5374\u4e1d\u6beb\u4e0d\u4e86\u89e3\u4ed6\u6b64\u523b\u7684\u5fc3\u5883\uff0c\u5979\u6b63\u5de7\u5730\u5728\u7f16\u7ec7\u7740\u4e00\u5f20\u7c89\u7ea2\u8272\u7684\u7f51\u5b50\uff0c\u60f3\u8ba9\u8fd9 \u66fe\u7ecf\u7231\u8fc7\u81ea\u5df1\u7684\u4eba\uff0c\u518d\u4e00\u6b21\u8dcc\u5165\u81ea\u5df1\u60c5\u611f\u7684\u5708\u5957\u3002 \u3000\u3000\u8fd9\u5e7d\u79d8\u7684\u77f3\u7a9f\uff0c\u663e\u7136\u662f\u7ecf\u8fc7\u5de7\u5999\u7684\u5b89\u6392\u7684\uff0c\u51e1\u662f\u751f\u6d3b\u4e0a\u4e00\u5207\u5fc5\u987b\u7684\u4e1c\u897f\uff0c\u4f60\u90fd\u53ef\u4ee5\u5728\u8fd9 \u5f20\u77f3\u5e8a\u4e0b\u9762\u7684\u7a7a\u6d1e\u91cc\u627e\u5230\u3002 \u3000\u3000\u4e00\u7bd3\u6cf0\u5b89\u7684\u540d\u4ea7\u9171\u6e0d\u5305\u74dc\uff0c\u4e00\u53ea\u5df2\u7ecf\u84b8\u719f\u7684\u7f8a\u817f\uff0c\u4e00\u65b9\u9e7f\u812f\uff0c\u4e24\u53ea\u98ce\u9e21\uff0c\u56db\u53ea\u677f\u9e2d\uff0c\u4e00 \u7bd3\u5173\u5916\u9752\u7a1e\u5236\u6210\u7684\u7a1e\u5df4\uff0c\u4e00\u6ce5\u5c01\u672a\u5f00\u7684\u7ecd\u5174\u5973\u513f\u7ea2\u548c\u4e00\u6f84\u6e05\u7684\u98df\u6c34\uff0c\u8fd9\u5929\u4e89\u6559\u4e3b\u7684\u5b89\u6392\uff0c\u7684 \u786e\u662f\u7f1c\u5bc6\u7684\u3002 \u3000\u3000\u859b\u82e5\u74a7\u5730\u6574\u6cbb\u7740\u98df\u7269\uff0c\u4f3c\u4e4e\u60f3\u5c06\u4f0a\u98ce\u5e26\u56de\u9065\u8fdc\u7684\u56de\u5fc6\u91cc\u3002 \u3000\u3000\u4f0a\u98ce\u65e0\u52a8\u4e8e\u8877\u5730\u671b\u7740\u8fd9\u4e9b\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u5fd6\uff1a \u3000\u3000\u201c\u9760\u7740\u8fd9\u4e9b\u98df\u7269\uff0c\u6211\u652f\u6301\u4e2a\u4e00\uff0c\u4e8c\u5341\u5929\uff0c\u662f\u4e0d\u6210\u95ee\u9898\u7684\u3002\u4e58\u6b64\u65f6\u5019\uff0c\u6211\u8981\u628a\u201c\u5929\u661f\u79d8 \u7b08\u201d\u4e0a\u7684\u5947\u529f\u79d8\u6280\uff0c\u5c3d\u91cf\u5b66\u5f97\u4e00\u70b9\uff0c\u4e8c\u5341\u5929\u540e\uff0c\u90a3\u4e07\u5929\u840d\u5982\u4e0d\u98df\u8a00\u2014\u2014\u201d \u3000\u3000\u4ed6\u5634\u89d2\u4e0d\u7981\u6cdb\u8d77\u4e00\u4e1d\u5fae\u7b11\uff0c\u4f46\u662f\u8fd9\u7b11\u5bb9\uff0c\u5374\u4e5f\u662f\u6781\u4e3a\u9eef\u6de1\u7684\u3002 \u3000\u3000\u8fd9\u77f3\u7a9f\u4e2d\u7684\u4e24\u4eba\uff0c\u5404\u81ea\u90fd\u5728\u8f6c\u4e2a\u5fc3\u601d\u3002 \u3000\u3000\u53ea\u6709\u90a3\u65e0\u90aa\u7684\u5a74\u513f\uff0c\u77aa\u7740\u4e00\u53cc\u65e0\u90aa\u7684\u773c\u775b\uff0c\u671b\u7740\u4ed6\u7684\u7236\u6bcd\uff0c\u4eba\u4e16\u95f4\u7684\u60c5\u4ec7\u6069\u6028\uff0c\u4ed6\u4e00\u4e1d \u4e5f\u6ca1\u6709\u611f\u89c9\u5230\uff0c\u4ed6\uff0c\u4e0d\u662f\u4eba\u4e16\u95f4\u6700\u6700\u5e78\u798f\u7684\u5417\uff1f \u3000\u3000\u4f0a\u98ce\u9664\u4e86\u4e0d\u65f6\u548c\u4ed6\u7684\u5e7c\u5b50\u6148\u853c\u7684\u7b11\u7b11\u4e4b\u5916\uff0c\u5c31\u518d\u4e5f\u4e0d\u53d1\u4e00\u8a00\uff0c\u751a\u81f3\u8fde\u671b\u90fd\u4e0d\u671b\u859b\u82e5\u74a7\u4e00 \u773c\u3002 \u3000\u3000\u7b49\u5230\u859b\u82e5\u74a7\u548c\u5a74\u513f\u90fd\u7761\u4e86\uff0c\u4ed6\u5c31\u5750\u5728\u706f\u4e0b\uff0c\u638f\u51fa\u5929\u661f\u79d8\u7b08\u6765\uff0c\u4ed4\u7ec6\u5730\u7ffb\u9605\u7740\uff0c\u4e0d\u65f6\u4f1a\u7a81 \u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u6bd4\u4e2a\u62db\u5f0f\uff0c\u53c8\u72c2\u559c\u5730\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e09\u5929\u4e4b\u4e2d\uff0c\u4ed6\u5b66\u4f1a\u4e86\u4e00\u4e9b\u4ee5\u524d\u4ed6\u8fde\u505a\u68a6\u90fd\u6ca1\u6709\u60f3\u8d77\u7684\u6b66\u529f\u62db\u5f0f\u3002 \u3000\u3000\u5728\u8fd9\u4e09\u5929\u4e2d\uff0c\u4ed6\u8fde\u8ddf\u775b\u90fd\u672a\u66fe\u5408\u8fc7\u4e00\u4e0b\uff0c\u859b\u82e5\u74a7\u50cf\u662f\u4e5f\u8d4c\u8d77\u6c14\u6765\uff0c\u4e0d\u548c\u4ed6\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4ed6 \u81ea\u7136\u66f4\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4eba\u603b\u6709\u75b2\u5026\u7684\u65f6\u5019\uff0c\u4e8e\u662f\u4ed6\u501a\u5728\u5899\u8fb9\uff0c\u80e1\u4e71\u5730\u7761\u7740\u4e86\u3002 \u3000\u3000\u7761\u68a6\u4e4b\u4e2d\uff0c\u4ed6\u53ea\u89c1\u94c1\u9762\u5b64\u884c\u5ba2\u6b63\u94c1\u9752\u7740\u8138\uff0c\u6765\u62a2\u4ed6\u6000\u4e2d\u7684\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c \u72c2\u543c\u4e00\u58f0\uff0c\u4fbf\u81ea\u60ca\u9192\u3002 \u3000\u3000\u7741\u773c\u4e00\u770b\uff0c\u5374\u89c1\u859b\u82e5\u74a7\u6b63\u8d64\u7740\u4e00\u53cc\u811a\uff0c\u7ad9\u5728\u81ea\u5df1\u9762\u524d\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u77e5\u9053\u5979\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u4e8e\u662f\u81ea\u6b64\u4ed6\u751a\u81f3\u4e0d\u6562\u7761\u89c9\uff0c\u53ea\u662f\u5076\u7136\u6253\u4e2a\u76f9\uff0c\u4f46\u4e5f\u968f\u65f6\u60ca\u89c9 \u7740\u3002 \u3000\u3000\u4e00\u5929\uff0c\u4e24\u5929\uff0c\u2026\u2026 \u3000\u3000\u8bb8\u591a\u65e5\u5b50\u8fc7\u53bb\u4e86\uff0c\u4e00\u4e2a\u4e25\u91cd\u7684\u95ee\u9898\uff0c\u5374\u968f\u7740\u65f6\u65e5\u7684\u901d\u53bb\u53d1\u751f\u3002 \u3000\u3000\u98df\u6c34\u6ca1\u6709\u4e86\uff0c\u4e8e\u662f\u4ed6\u4eec\u6253\u5f00\u9152\uff0c\u4ee5\u9152\u4f5c\u6c34\u3002 \u3000\u3000\u4f46\u662f\u5b69\u5b50\u5462\uff01\u5b69\u5b50\u4e5f\u53ea\u5f97\u559d\u9152\u3002\u4f0a\u98ce\u7528\u7b77\u5b50\u91ae\u9152\uff0c\u653e\u5728\u4ed6\u53e3\u91cc\uff0c\u8ba9\u4ed6\u6162\u6162\u542e\u7740\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u8fd9\u5b69\u5b50\u5df2\u4e60\u60ef\u4e86\u9152\u5473\uff0c\u4e5f\u80fd\u4e00\u53e3\u53e3\u5730\u559d\u9152\u4e86\u3002 \u3000\u3000\u7ecd\u5174\u5973\u513f\u7ea2\uff0c\u9152\u5473\u867d\u9187\uff0c\u540e\u52b2\u5374\u5927\uff0c\u5b69\u5b50\u81ea\u7136\u6700\u5148\u9189\u4e86\uff0c\u859b\u82e5\u74a7\u4e5f\u8ddf\u7740\u9189\u5012\u3002 \u3000\u3000\u4f0a\u98ce\u671b\u4e86\u671b\u5979\u633a\u8d77\u7684\u809a\u5b50\uff0c\u5fc3\u4e2d\u7a81\u53c8\u6d8c\u8fc7\u4e00\u969c\u96be\u8a00\u7684\u6ecb\u5473\uff0c\u8d70\u5230\u5899\u8fb9\u8eba\u4e0b\uff0c\u653e\u5fc3\u5730\u547c\u547c \u5927\u7761\u8d77\u6765\u3002 \u3000\u3000\u6839\u672c\u6ca1\u6709\u65e5\u5149\u900f\u5165\uff0c\u56e0\u6b64\u4ed6\u4eec\u4e5f\u6839\u672c\u4e0d\u77e5\u65e5\u5b50\u5012\u5e95\u8fc7\u4e86\u8bb8\u591a\uff0c\u859b\u82e5\u74a7\u9189\u4e86\u53c8\u9192\uff0c\u9192\u4e86\u53e3 \u66f4\u6e34\uff0c\u4e8e\u662f\u518d\u559d\u53c8\u9189 \u3000\u3000\u4e0d\u53ef\u907f\u514d\u7684\uff0c\u4f0a\u98ce\u7684\u795e\u601d\uff0c\u4e5f\u56e0\u7ec8\u65e5\u996e\u9152\uff0c\u800c\u53d8\u5f97\u6709\u4e9b\u6655\u7729\uff0c\u53ea\u662f\u4ed6\u7a76\u7adf\u662f\u4e2a\u7537\u5b50\uff0c\u9152 \u91cf\u8f83\u5b8f\uff0c\u662f\u4ee5\u8fd8\u6ca1\u6709\u9189\u5012\u7f62\u4e86\u2014\u2014 \u3000\u3000\u65e5\u5b50\u98de\u65cb\u7740\u6e9c\u8d70\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u5df2\u5c06\u90a3\u672c\u201c\u5929\u661f\u79d8\u7b08\u201d\uff0c\u4ece\u5934\u5230\u5c3e\u770b\u4e86\u4e00\u904d\uff0c\u4ed6\u6b66\u5b66\u5df2\u6709\u6839\u57fa\uff0c\u5929\u8d44\u672c\u5c31\u6781\u9ad8\uff0c\u6b64 \u523b\u5b66\u8d77\u6765\uff0c\u81ea\u7136\u662f\u4e8b\u534a\u529f\u500d\u3002 \u3000\u3000\u5176\u4e2d\u867d\u6709\u4e9b\u5965\u5999\u4e4b\u5904\uff0c\u4ed6\u8fd8\u4e0d\u80fd\u5b8c\u5168\u9886\u7565\uff0c\u4f46\u90a3\u53ea\u4e0d\u8fc7\u662f\u65f6\u65e5\u95f4\u9898\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u81ea\u89c9\u81ea\u5bb6\u7684\u6b66\u529f\uff0c\u6bd4\u8d77\u8fdb\u6d1e\u4e4b\u524d\uff0c\u5df2\u6709\u9704\u58e4\u4e4b\u522b\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u81ea\u4fe1\u5730\u8ba4\u4e3a\uff1a\u4ee5\u81ea\u5bb6\u6b64\u65f6\u7684\u529f\u529b\uff0c\u4e0d\u96be\u548c\u4e07\u5929\u840d\u4e00\u8f83\u77ed\u957f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6b23\u559c\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u5728\u684c\u4e0a\u62c8\u8d77\u4e00\u7247\u706b\u817f\uff0c\u653e\u5728\u53e3\u4e2d\u6162\u6162\u5480\u56bc\u7740\uff0c\u671b\u7740\u5e8a\u4e0a\u7761\u5f97\u6b63 \u6d53\u7684\u7231\u5b50\uff0c\u4ed6\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4fef\u9996\u6c89\u601d\u957f\u4e45 \u3000\u3000\u7a81\u5730\uff0c\u4e00\u58f0\u8f70\u7136\u5de8\u54cd\uff0c\u4ece\u8fd9\u6d1e\u7a9f\u5916\u9762\u7684\u96a7\u9053\u5c3d\u5934\u4f20\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff01\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\uff0c\u53c8\u98de\u4e5f\u4f3c\u7684\u63a0\u4e86\u56de\u6765\uff0c\u63a0\u5230\u5e8a\u524d\uff0c\u4f38\u51fa\u53cc\u624b\uff0c\u60f3\u62b1\u90a3\u4ecd \u5728\u719f\u7761\u4e2d\u7684\u5b69\u5b50\u3002 \u3000\u3000\u8fd9\u4e9b\u5929\u6765\uff0c\u4ed6\u548c\u8fd9\u5b69\u5b50\u4e4b\u95f4\u7684\u60c5\u611f\uff0c\u8d8a\u6765\u8d8a\u6d53\u7236\u5b50\u4e4b\u60c5\uff0c\u6709\u65f6\u662f\u6bd4\u4e16\u95f4\u4efb\u4f55\u4e00\u79cd\u60c5\u611f\u90fd \u8981\u6d53\u539a\u7684\uff0c\u8fd9\u672c\u51fa\u4e8e\u5929\u6027\uff0c\u65e0\u6cd5\u52c9\u5f3a\u3002 \u3000\u3000\u90a3\u77e5\u859b\u82e5\u74a7\u7a81\u5730\u4e00\u4e2a\u7ffb\u8eab\uff0c\u4f0f\u5728\u8fd9\u5b69\u5b50\u8eab\u4e0a\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u51b7\u54fc\u4e00\u58f0\uff0c\u53f1\u58f0\uff1a \u3000\u3000\u201c\u8fd9\u662f\u6211\u7684\u5b69\u5b50\uff0c\u6211\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u518d\u8ddf\u7740\u4f60\u3002\u201d \u3000\u3000\u859b\u82e5\u74a7\u5c06\u8eab\u5b50\uff0c\u6574\u4e2a\u538b\u5728\u8fd9\u5b69\u5b50\u8eab\u4e0a\uff0c\u5fae\u5fae\u4fa7\u8fc7\u8138\uff0c\u5706\u7741\u7740\u674f\u76ee\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u51ed\u4ec0\u4e48\u8981\u8fd9\u5b69\u5b50\uff01\u5c0f\u5357\u662f\u6211\u751f\u7684\uff0c\u53c8\u662f\u6211\u517b\u7684\uff0c\u4f60\u51ed\u4ec0\u4e48\u8981\u628a\u4ed6\u4ece\u6211\u8eab\u8fb9\u62a2\u8d70\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u51b7\u54fc\u4e00\u58f0\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u75be\u4f38\u53cc\u638c\uff0c\u53f3\u624b\u53bb\u6273\u859b\u82e5\u74a7\u7684\u8eab\u5b50\uff0c\u5de6\u624b\u5374\u53bb\u62a2\u90a3\u5b69\u5b50\uff0c\u90a3 \u5b69\u5b50\u4ece\u7761\u68a6\u4e2d\u9192\u6765\uff0c\u201c\u54c7\u201d\u5730\u4e00\u58f0\u54ed\u4e86\u3002 \u3000\u3000\u859b\u82e5\u74a7\u5de6\u624b\u53cd\u6325\uff0c\u53bb\u5212\u4f0a\u98ce\u7684\u624b\u8155\uff0c\u53e3\u4e2d\u53d1\u72c2\u4f3c\u7684\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u662f\u518d\u6562\u78b0\u8fd9\u5b69\u5b50\uff0c\u6211\u5c31\u5f04\u6b7b\u4ed6\uff0c\u6211\u4e5f\u6b7b\uff0c\u6211\u4eec\u6bcd\u5b50\u4e24\u4eba\u4e00\u9f50\u6b7b\u7ed9\u4f60\u770b\u3002\u201d \u3000\u3000\u4f0a\u98ce\u75be\u4f38\u51fa\u53bb\u7684\u94c1\u638c\uff0c\u505c\u7559\u5728\u859b\u82e5\u74a7\u8eab\u4e0a\u5fae\u5fae\u98a4\u6296\u4e86\u4e00\u4e0b\uff0c\u7ec8\u4e8e\u7f29\u56de\u624b\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u6c88 \u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u8fd9\u5b69\u5b50\u5e72\u4ec0\u4e48\uff1f\u96be\u9053\u4f60\u8981\u4ed6\u8ddf\u4f60\u548c\u2026\u2026\u2026\u548c\u8427\u65e0\u4e00\u9f50\uff0c\u8ba9\u4ed6\u53d7\u90a3\u59d3\u8427\u7684\u6298\u78e8\uff1f \u5509\uff01\u4f60\u82e5\u8fd8\u6709\u592b\u5987\u4e4b\u60c5\uff0c\u5c31\u5c06\u8fd9\u5b69\u5b50\u8fd8\u6211\uff0c\u6211\u2014\u2014\u6211\u611f\u6fc0\u4f60\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u859b\u82e5\u74a7\u7a81\u5730\u7eb5\u58f0\u72c2\u7b11\u4e86\u8d77\u6765\uff0c\u4f38\u51fa\u7ea4\u638c\uff0c\u4e00\u63a0\u4e71\u53d1\uff0c\u72c2\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u592b\u59bb\u4e4b\u60c5\uff1f\u2014\u2014\u54c8\uff01\u4f60\u4e5f\u77e5\u9053\u592b\u59bb\u4e4b\u60c5\uff0c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u53ea\u8981\u5b69\u5b50\uff1f\u5415\u5357\u4eba\uff01\u6211\u867d\u7136\u4e5f\u6709 \u5bf9\u4f60\u4e0d\u8d77\u7684\u5730\u65b9\uff0c\u53ef\u662f\u2014\u2014\u201d \u3000\u3000\u5979\u72c2\u7b11\u987f\u4f4f\uff0c\u58f0\u97f3\u7a81\u7136\u53d8\u5f97\u54fd\u54bd\u8d77\u6765\uff0c\u5fae\u5fae\u62ac\u8d77\u4e9b\u8eab\u5b50\uff0c\u4f38\u624b\u6478\u4e86\u6478\u90a3\u5b69\u5b50\u7684\u9762\u988a\uff0c\u63a5 \u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u73b0\u5728\u5df2\u7ecf\u77e5\u9053\u9519\u4e86\uff0c\u4f60\u96be\u9053\u2014\u2014\u201d \u3000\u3000\u5979\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u518d\u5f80\u4e0b\u8bf4\u4e0b\u53bb\uff0c\u4f46\u5c31\u7b97\u5979\u4e0d\u8bf4\uff0c\u4f0a\u98ce\u4e5f\u5df2\u77e5\u9053\uff0c\u8fd9\u806a\u660e\u7684\u5973\u5b50\uff0c \u6b64\u523b\u5df2\u60f3\u8131\u79bb\u8427\u65e0\uff0c\u56de\u5230\u81ea\u5df1\u8eab\u4fa7\u6765\uff0c\u800c\u7528\u8fd9\u5b69\u5b50\uff0c\u4f5c\u4e3a\u8981\u80c1\u7684\u6b66\u5668\u3002 \u3000\u3000\u53ea\u662f\u5979\u592a\u806a\u660e\u4e86\u4e9b\uff0c\u7adf\u5c06\u522b\u4eba\uff0c\u90fd\u5f53\u6210\u767d\u75f4\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u859b\u82e5\u74a7\uff01\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4f60\u8be5\u77e5\u9053\u2014\u2014\u201d \u8bed\u72b9\u672a\u4e86\uff0c\u90a3\u77e5\u2014\u2014 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516d\u5341\u516b\u7ae0\u3000\u8bb8\u767d\u66f4\u751f>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u516b\u7ae0\u3000\u8bb8\u767d\u66f4\u751f \u3000\u3000\u6d1e\u53e3\u7a81\u5730\u54cd\u8d77\u4e00\u9635\u72c2\u7b11\uff0c\u4e00\u4e2a\u6709\u5982\u6d2a\u949f\u822c\u7684\u58f0\u97f3\uff0c\u72c2\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u6b63\u5947\u602a\uff01\u4e07\u5929\u840d\u8fd9\u53ea\u8001\u819c\u5b50\uff0c\u4e3a\u4ec0\u4e48\u50cf\u5446\u5b50\u4f3c\u7684\uff0c\u5750\u5728\u8fd9\u5c71\u6d1e\u7684\u6d1e\u53e3\uff0c\u6d1e\u53e3\u53c8\u5835\u7740 \u5927\u77f3\u5934\uff1b\u5374\u4e0d\u77e5\u9053\u539f\u6765\u662f\u4f60\u8fd9\u5a03\u5a03\u5728\u6d1e\u91cc\u9762\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5927\u60ca\u8f6c\u8eab\uff0c\u76ee\u5149\u65b9\u81ea\u4e00\u8f6c\uff0c\u5374\u53c8\u9a87\u5f97\u51e0\u4e4e\u8981\u5931\u58f0\u60ca\u547c\u8d77\u6765\u3002 \u3000\u3000\u58c1\u95f4\u6cb9\u706f\u5149\u4eae\u5df2\u5f31\uff0c\u660f\u9ec4\u7684\u706f\u5149\uff0c\u7167\u5728\u6d1e\u53e3\u8fd9\u4eba\u8eab\u4e0a\uff0c\u53ea\u89c1\u6b64\u4eba\u8eab\u8eaf\u5f6a\u58ee\uff0c\u5149\u7740\u5934\u9876\uff0c \u84ec\u4e71\u7684\u5934\u53d1\uff0c\u80e1\u4e71\u6253\u6210\u4e00\u4e2a\u53d1\u9afb\uff0c\u76d8\u5728\u5934\u4e0a\u3002\u8eab\u4e0a\u7a7f\u7684\u4e00\u88ad\u7edd\u597d\u6e58\u7f0e\u5236\u6210\u7684\u957f\u886b\uff0c\u4e0a\u895f\u7684\u65e0 \u5b50\u5374\u5b8c\u5168\u655e\u5f00\u7740\uff0c\u9732\u51fa\u80f8\u819b\u4e0a\u8338\u8338\u7684\u9ed1\u6bdb\uff0c\u548c\u51e0\u4e2a\u9ed1\u8272\u7684\u4f24\u75a4\u3002\u6d53\u7709\u73af\u773c\uff0c\u76ee\u5149\u5982\u7535\uff0c\u9894\u4e0b \u866c\u987b\u5982\u94c1\uff0c\u6839\u6839\u89c1\u8089\uff0c\u5374\u6b63\u662f\u90a3\u5343\u91cc\u8ffd\u98ce\u795e\u884c\u65e0\u5f71\uff0c\u5999\u624b\u8bb8\u767d\u3002 \u3000\u3000\u4f0a\u98ce\u4f46\u89c9\u81ea\u5df1\u638c\u5fc3\u5c3d\u6e7f\uff0c\u5168\u8eab\u4e0d\u7531\u81ea\u4e3b\u5730\u5192\u7740\u51b7\u6c57\u3002 \u3000\u3000\u4ed6\u5728\u65e0\u91cf\u5c71\u5dc5\uff0c\u4eb2\u773c\u89c1\u5230\u8fd9\u201c\u5357\u5077\u201d\u548c\u201c\u5317\u76d7\u201d\u4e24\u4eba\uff0c\u4e92\u51fb\u800c\u6b7b\uff0c\u4f46\u90a3\u201c\u5317\u76d7\u201d\u94c1\u9762\u5b64 \u884c\u5ba2\u4e07\u5929\u840d\uff0c\u5374\u5148\u5c31\u590d\u6d3b\u3002 \u3000\u3000\u53ea\u662f\u90a3\u65f6\u5012\u5e95\u9694\u65f6\u672a\u4e45\uff0c\u5c1a\u4e14\u8fd8\u6709\u4e9b\u9053\u7406\u53ef\u8bf4\uff0c\u4f46\u6b64\u523b\u8fd9\u5343\u91cc\u8ffd\u98ce\u5999\u624b\u8bb8\u767d\uff0c\u7adf\u7a81\u5730\u51fa \u73b0\u5728\u81ea\u5df1\u773c\u524d\uff0c\u8fd9\u5374\u4ee4\u4eba\u532a\u5937\u6240\u601d\u4e86\u3002 \u3000\u3000\u4ed6\u811a\u4e0b\u50cf\u662f\u751f\u4e86\u6839\u4f3c\u7684\uff0c\u518d\u4e5f\u65e0\u6cd5\u79fb\u52a8\u534a\u6b65\u3002 \u3000\u3000\u90a3\u859b\u82e5\u74a7\u89c1\u4e86\u8fd9\u79cd\u60c5\u51b5\uff0c\u4e5f\u4e0d\u7981\u60ca\u5f97\u5446\u4e86\uff0c\u751a\u81f3\u8fde\u90a3\u5b69\u5b50\uff0c\u90fd\u6b62\u4f4f\u4e86\u557c\u54ed\u3002 \u3000\u3000\u5374\u89c1\u5999\u624b\u8bb8\u767d\u54c8\u54c8\u72c2\u7b11\u7740\uff0c\u5927\u6b65\u8d70\u5165\u6d1e\u7a9f\uff0c\u76ee\u5149\u95ea\u7535\u822c\u56db\u4e0b\u4e00\u626b\uff0c\u770b\u5230\u77f3\u684c\u4e0a\u8fd8\u672a\u5403\u5b8c \u7684\u7f8a\u817f\u98ce\u9e21\uff0c\u548c\u77f3\u684c\u8fb9\u4e0d\u8fc7\u4ec5\u4ec5\u5269\u4e0b\u5c11\u8bb8\u7684\u7ecd\u5174\u201c\u5973\u513f\u7ea2\u201d\uff0c\u4e0d\u7981\u53c8\u81ea\u7b11\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u8fd9\u5c71\u6d1e\u91cc\u7adf\u662f\u6041\u5730\u597d\u53bb\u5904\uff0c\u5c45\u7136\u6709\u9152\u6709\u8089\uff01\u201d \u3000\u3000\u4ed6\u4e00\u624b\u6293\u8d77\u534a\u53ea\u98ce\u9e21\uff0c\u4e00\u624b\u63d0\u8d77\u90a3\u53ea\u9152\uff0c\u5927\u53e3\u559d\u4e86\u53e3\u9152\uff0c\u563f\u5730\u4e00\u7b11\uff0c\u8fde\u58f0\u9053\uff1a \u3000\u3000\u201c\u597d\u9152\uff01\u597d\u9152\uff01\u201d \u3000\u3000\u5403\u4e86\u53e3\u9e21\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u597d\u9e21\uff01\u597d\u9e21\uff01\u201d \u3000\u3000\u56de\u8fc7\u5934\u6765\uff0c\u770b\u5230\u4f0a\u98ce\u7684\u6837\u5b50\uff0c\u72c2\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5a03\u5a03\uff01\u4f60\u5bb3\u5f97\u6211\u8fd9\u5b64\u9b42\u91ce\u9b3c\u597d\u82e6\uff0c\u4e0a\u5230\u5357\u5929\u95e8\uff0c\u8fde\u5b59\u609f\u7a7a\u90fd\u5acc\u6211\u592a\u4e11\uff0c\u4e00\u68cd\u5b50\u5c06\u6211 \u6253\u4e0b\u6765\uff0c\u8dd1\u5230\u5730\u72f1\uff0c\u5374\u53c8\u88ab\u725b\u5934\u9a6c\u9762\u6321\u4e86\u9a7e\uff0c\u6211\u4e0a\u5929\u5165\u5730\uff0c\u624d\u5bfb\u5f97\u8fd9\u597d\u5730\u65b9\uff0c\u6709\u9152\u6709\u8089\uff0c\u4e00 \u9ad8\u5174\uff0c\u8bf4\u4e0d\u5b9a\u4e0d\u5411\u4f60\u7d22\u547d\u4e86\uff0c\u4f60\u6101\u7709\u82e6\u8138\u7684\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u4ed6\u867d\u7136\u4ece\u6765\u4e0d\u4fe1\u4eba\u4e16\u4e4b\u95f4\uff0c\u6709\u9b3c\u9b42\u51fa\u73b0\uff0c\u4f46\u6b64\u523b\u8fd9\u660e\u660e\u5df2\u6b7b\u4e86 \u597d\u4e45\u7684\u5999\u624b\u8bb8\u767d\uff0c\u5374\u662f\u771f\u771f\u5207\u5207\u5730\u7ad9\u5728\u81ea\u5df1\u9762\u524d\uff0c\u5374\u53c8\u6709\u4ec0\u4e48\u5176\u4ed6\u7684\u89e3\u91ca\u5462\uff01 \u3000\u3000\u859b\u82e5\u74a7\u4f38\u51fa\u7ea4\u624b\uff0c\u62a4\u4f4f\u90a3\u5df2\u9a87\u5f97\u76f4\u6487\u5634\uff0c\u5374\u4e0d\u6562\u54ed\u51fa\u6765\u7684\u5b69\u5b50\u8eab\u524d\uff0c\u5a07\u58f0\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u201c\u5478\u201d\u5730\u4e00\u58f0\uff0c\u5c06\u9e21\u9aa8\u5410\u5728\u5730\u4e0a\uff0c\u76ee\u5149\u5728\u5979\u9762\u4e0a\u4e00\u8f6c\uff0c\u53c8\u4ef0\u53e3\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u547c \u5730\u547c\u51fa\u53e3\u957f\u6c14\uff0c\u5927\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u5c0f\u5a03\u5a03\uff0c\u5012\u5a36\u4e86\u4e2a\u8fd9\u4e48\u6f02\u4eae\u7684\u592a\u592a\u3002\u201d \u3000\u3000\u5927\u6b65\u8d70\u5230\u4f0a\u98ce\u8eab\u4fa7\uff0c\u4f38\u51fa\u81ea\u6293\u7740\u98ce\u9e21\u7684\u5de8\u638c\uff0c\u201c\u5427\u201d\u5730\u4f0a\u98ce\u80a9\u4e0a\u62cd\u4e86\u4e00\u4e0b\uff0c\u53c8\u81ea\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5a03\u5a03\uff01\u4e0d\u8981\u6015\u3002\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u8001\u592b\u8fd8\u672a\u6b7b\u3002\u8001\u592b\u8981\u662f\u6b7b\u4e86\uff0c\u51a4\u9b3c\u4e5f\u4e0d\u4f1a\u627e\u5230\u4f60\u8eab \u4e0a\uff0c\u4f60\u6015\u4e2a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e3e\u8d77\u9152\uff0c\u4ef0\u9996\u5f85\u996e\uff0c\u4f46\u4e2d\u7684\u9152\uff0c\u5374\u5df2\u6ca1\u6709\u4e86\uff0c\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u9152\u867d\u4e0d\u9519\uff0c\u53ef\u60dc\u592a\u5c11\u4e86\u4e9b\uff01\u201d \u3000\u3000\u968f\u624b\u4e00\u6325\uff0c\u5c06\u9152\u629b\u5728\u5c71\u58c1\u4e0a\uff0c\u53d1\u51fa\u201c\u7830\u201d\u5730\u4e00\u58f0\u5de8\u54cd\uff0c\u8eba\u5728\u5e8a\u4e0a\u7684\u5b69\u5b50\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c \u653e\u58f0\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u6115\u4e86\u534a\u664c\uff0c\u52c9\u5f3a\u5728\u8138\u4e0a\u6324\u51fa\u4e00\u70b9\u7b11\u5bb9\uff0c\u5450\u5450\u8bf4\u9053\uff1a \u3000\u3000\u201c\u591a\u65e5\u672a\u89c1\uff0c\u8bb8\u8001\u524d\u8f88\u98ce\u91c7\u5374\u4ecd\u4f9d\u65e7\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u65e0\u91cf\u5c71\u5dc5\u4e00\u522b\uff0c\u81f3\u6b64\u6050\u6015\u5df2\u6709\u6708\u4f59\u4e86\u5427\uff01\u8bb8\u8001\u524d\u8f88\u600e\u4e5f\u6709\u5174\u4e0a\u8fd9\u897f\u6881\u5c71\u6765\uff1f\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u5c0f\u5a03\u5a03\uff0c\u4e0d\u8981\u7ed5\u7740\u5708\u5b50\u8bf4\u8bdd\uff0c\u4f60\u5728\u5947\u602a\u8001\u592b\u600e\u5730\u672a\u6b7b\uff1f\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4ed6\u5927\u6b65\u8d70\u5230\u684c\u65c1\uff0c\u53c8\u62ff\u8d77\u4e00\u53ea\u9e2d\u817f\uff0c\u5750\u5230\u6905\u4e0a\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5a03\u5a03\uff01\u4f60\u4e5f\u5750\u4e0b\u6765\u3002\u201d \u3000\u3000\u4ed6\u7528\u9e2d\u817f\u6307\u4e86\u6307\u77f3\u5e8a\uff01 \u3000\u3000\u201c\u8ddf\u4f60\u5ab3\u5987\u513f\u5750\u5728\u4e00\u8d77\uff0c\u542c\u8001\u592b\u6162\u6162\u544a\u8bc9\u4f60\u2014\u2014\u201d \u3000\u3000\u4e00\u773c\u77a5\u89c1\u5730\u4e0a\u8fd8\u6709\u53ea\u9152\u676f\uff0c\u676f\u91cc\u8fd8\u6709\u4e9b\u5269\u9152\uff0c\u62ff\u6765\u4e00\u53e3\u559d\u4e86\uff0c\u53c8\u81ea\u7b11\u9053\uff1a \u3000\u3000\u201c\u5341\u5e74\u4ee5\u524d\uff0c\u6211\u548c\u4e07\u5929\u840d\u90a3\u8001\u7334\u5b50\u4e0a\u4e86\u65e0\u91cf\u5c71\uff0c\u539f\u672c\u4ee5\u4e3a\u6700\u591a\u5341\u5929\u534a\u4e2a\u6708\u5c31\u80fd\u89e3\u51b3\uff0c\u90a3 \u77e5\u9053\u8fd9\u8001\u7334\u5b50\u7684\u786e\u6709\u4e00\u624b\uff0c\u6211\u4eec\u8fd9\u4e00\u6bd4\u5212\uff0c\u7adf\u6bd4\u5212\u4e86\u5341\u5e74\uff01\u201d \u3000\u3000\u4ed6\u5c06\u624b\u4e2d\u7684\u9e21\u817f\uff0c\u653e\u5230\u53e3\u91cc\u5480\u56bc\u7740\uff0c\u662f\u4ee5\u8bdd\u58f0\u4e5f\u53d8\u5f97\u542b\u7cca\u4e0d\u6e05\uff0c\u4f46\u4ed6\u5374\u4ecd\u6307\u624b\u5212\u811a\u5730\u8bf4 \u9053\uff1a \u3000\u3000\u201c\u90a3\u5341\u5e74\u91cc\u2014\u2014\u563f\uff0c\u65e5\u5b50\u53ef\u771f\u4e0d\u597d\u8fc7\u3002\u76f4\u5230\u4f60\u8fd9\u5c0f\u5a03\u5a03\u6765\u4e86\uff0c\u53c8\u8bf4\u51fa\u5929\u661f\u79d8\u7b08\u7684\u4e8b\uff0c\u6211 \u5c31\u77e5\u9053\u6211\u548c\u90a3\u8001\u7334\u5b50\u7684\u6bd4\u5212\uff0c\u53c8\u5f97\u4e0d\u4e86\u4e86\u4e4b\u5566\u3002\u56e0\u4e3a\u90a3\u4e9b\u5929\u661f\u79d8\u85cf\uff0c\u53ef\u6bd4\u6211\u548c\u90a3\u8001\u7334\u5b50\u4e89\u7684 \u201c\u7487\u5149\u5b9d\u4eea\u201d\uff0c\u8981\u8d35\u91cd\u5f97\u591a\uff0c\u6211\u53ef\u4e5f\u52a8\u4e86\u5fc3\u4e86\u3002 \u3000\u3000\u201c\u540e\u6765\u90a3\u4e9b\u4e8b\u4f60\u5168\u77e5\u9053\uff0c\u53ef\u662f\u6709\u4ef6\u4e8b\u4f60\u4e0d\u77e5\u9053\uff0c\u5c31\u662f\u5728\u731c\u679a\u9009\u5b9d\u7684\u65f6\u5019\uff0c\u6211\u5f04\u4e86\u9b3c\uff0c\u8ba9 \u90a3\u8001\u7334\u5b50\u5148\u62ff\u5f97\u5929\u661f\u79d8\u7b08\uff0c\u7b49\u6211\u5403\u4e86\u6bd2\u9f99\u4e38\uff0c\u529f\u529b\u6df1\u8fc7\u4ed6\u65f6\uff0c\u518d\u628a\u5929\u661f\u79d8\u7b08\u62a2\u6765\uff0c\u8ba9\u90a3\u8001\u7334 \u5b50\u7a7a\u6b22\u559c\u4e00\u573a\u3002\u90a3\u77e5\u2014\u2014\u5509\uff01\u4eba\u7b97\u4e0d\u5982\u5929\u7b97\uff0c\u6211\u662f\u806a\u660e\u53cd\u88ab\u806a\u660e\u8bef\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5e72\u54b3\u4e00\u4e0b\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u65f6\u6211\u8fd8\u5947\u602a\uff1a\u8fd9\u8bb8\u767d\u65e2\u4ee5\u201c\u5999\u624b\u201d\u9a70\u8a89\u5929\u4e0b\uff0c\u600e\u5730\u4e0d\u5728\u201c\u731c\u679a\u201d\u65f6\u5f04\u4e0b \u9b3c\uff0c\u539f\u6765\u4ed6\u53e6\u6709\u7b97\u76d8\u3002\u201d \u3000\u3000\u5374\u542c\u5999\u624b\u8bb8\u767d\u5927\u7b11\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5a03\u5a03\uff01\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u5728\u9a82\u6211\u4e0d\u591f\u78ca\u843d\uff0c\u4f60\u5374\u4e0d\u77e5\u9053\u6211\u5999\u624b\u8bb8\u767d\u4e00\u751f\u884c\u4e8b\uff0c\u53ea\u8981\u6211\u81ea \u95ee\u8bf4\u5f97\u8fc7\u53bb\u5c31\u884c\u4e86\u3002\u90a3\u4e07\u5929\u840d\u6709\u540d\u7684\u5978\u72e1\u8d3c\u6ed1\uff0c\u6211\u53c8\u4f55\u82e6\u5bf9\u4ed6\u5149\u660e\u78ca\u843d\u2014\u2014\u201d \u3000\u3000\u4f0a\u98ce\u5251\u7709\u4e00\u8f69\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u5374\u53c8\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u8bb8\u767d\u4f38\u51fa\u5de8\u638c\uff0c\u4ece\u5634\u91cc\u638f\u51fa\u4e00\u6839\u9e21\u9aa8\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u6211\u73b0\u5728\u5374\u77e5\u9053\u505a\u4eba\u592a\u7cbe\u660e\u4e86\uff0c\u4e5f\u4e0d\u662f\u798f\u6c14\u3002\u5f53\u65f6\u6211\u4e00\u53e3\u541e\u4e0b\u6bd2\u9f99\u4e38\uff0c\u5148\u65f6\u8fd8\u597d\uff0c \u5230\u540e\u6765\u53ef\u5c31\u89c9\u5f97\u4e0d\u5bf9\u4e86\uff0c\u53ea\u89c9\u5f97\u8111\u5b50\u53d1\u6da8\uff0c\u8eab\u5b50\u4e5f\u53d1\u6da8\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\uff0c\u4e5f\u4e0d\u77e5\u751f\u51fa\u4ec0\u4e48\u4e8b\uff0c \u5c31\u5b8c\u5168\u6ca1\u6709\u77e5\u89c9\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u8fd9\u6614\u5e74\u7eb5\u6a2a\u5929\u4e0b\u7684\u89d2\u8272\uff0c\u9762\u4e0a\u7684\u808c\u8089\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u62bd\u6410\u8d77\u6765\uff0c\u50cf\u662f\u5bf9\u5f53\u65f6\u7684 \u60c5\u51b5\uff0c\u601d\u4e4b\u72b9\u6709\u4f59\u60b8\u3002 \u3000\u3000\u4ed6\u4f38\u624b\u4e00\u62b9\u5634\u4e0a\u7684\u6cb9\u6e0d\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u7b49\u5230\u6211\u7a0d\u4e3a\u6062\u590d\u4e00\u4e9b\u77e5\u89c9\u7684\u65f6\u5019\uff0c\u6211\u53ea\u89c9\u6709\u4e2a\u4eba\u4f0f\u5728\u6211\u8eab\u4e0a\uff0c\u50cf\u662f\u5728\u5438\u7740\u6211\u7684\u8840\uff0c\u5f53 \u65f6\u6211\u9a87\u5f97\u5fc3\u9b42\u4ff1\u5931\uff0c\u4f46\u53ef\u4e5f\u6ca1\u6709\u529b\u6c14\u53cd\u6297\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e0d\u89c9\u53c8\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u5012\u9000\u4e24\u6b65\uff0c\u201c\u5657\u201d\u5730\u5750\u5728\u5e8a\u4e0a\u3002\u4fa7\u76ee\u4e00\u671b\u859b\u82e5\u74a7\uff0c\u53ea\u89c1\u5979\u90a3\u5a07 \u7f8e\u5982\u82b1\u7684\u9762\u5b54\uff0c\u6b64\u523b\u4e5f\u53d8\u5f97\u50cf\u7eb8\u4e00\u6837\u5730\u82cd\u767d\u3002 \u3000\u3000\u53ea\u542c\u90a3\u5999\u624b\u8bb8\u767d\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u5947\u602a\u7684\u662f\uff1a\u4ed6\u8d8a\u5438\u6211\u7684\u8840\uff0c\u6211\u53cd\u800c\u8d8a\u89c9\u5f97\u8212\u670d\uff0c\u6e10\u6e10\u5934\u4e5f\u4e0d\u6da8\u4e86\uff0c\u8eab\u5b50\u4e5f\u4e0d\u6da8 \u4e86\uff0c\u53ea\u662f\u5168\u8eab\u865a\u98d8\u98d8\u5730\uff0c\u6574\u4e2a\u4eba\u50cf\u662f\u8981\u98de\u8d77\u6765\u3002\u4e8e\u662f\u6211\u8ff7\u8ff7\u7cca\u7cca\u5730\u53c8\u7761\u89c9\u4e86\u3002\u201d \u3000\u3000\u201c\u7b49\u6211\u518d\u9192\u6765\u7684\u65f6\u5019\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u7741\u5f00\u773c\u775b\u4e00\u770b\uff0c\u90a3\u5c71\u7a9f\u91cc\u7a7a\u7a7a\u5730\uff0c\u8fde\u534a\u4e2a\u4eba\u5f71 \u90fd\u6ca1\u6709\uff0c\u6211\u5374\u662f\u8eba\u5728\u90a3\u5f20\u77f3\u684c\u4e0a\u2014\u2014\u5582\uff01\u5c0f\u5a03\u5a03\uff0c\u662f\u4e0d\u662f\u4f60\u628a\u6211\u653e\u4e0a\u53bb\u7684\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u4e00\u70b9\u5934\uff0c\u5fc3\u4e2d\u53ea\u89c9\u8df3\u52a8\u751a\u5de8\uff0c\u4ee5\u524d\u4ed6\u6709\u4e9b\u4e0d\u660e\u767d\u7684\u5730\u65b9\uff0c\u73b0\u5728\u867d\u7136\u5168\u90fd\u77e5\u9053\u4e86\uff0c \u4f46\u662f\u8fd9\u79cd\u8840\u6dcb\u6dcb\u7684\u6545\u4e8b\uff0c\u5374\u4f7f\u4ed6\u6709\u4e9b\u96be\u53d7\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u76ee\u5149\u4e00\u51db\uff0c\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u559d\u6211\u8840\u7684\uff0c\u60f3\u5fc5\u662f\u4e07\u5929\u840d\u90a3\u8001\u7334\u5b50\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5450\u5450\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5374\u542c\u4ed6\u53c8\u9053\uff1a \u3000\u3000\u201c\u5f53\u65f6\u6211\u867d\u5df2\u9192\u8f6c\uff0c\u4f46\u662f\u5168\u8eab\u4e0a\u4e0b\u7684\u9aa8\u5934\uff0c\u5374\u50cf\u662f\u5df2\u7ecf\u62c6\u6563\u4e86\u4f3c\u7684\uff0c\u53c8\u9178\u53c8\u75db\uff0c\u53c8\u6ca1\u6709 \u4e00\u4e1d\u529b\u91cf\uff0c\u5e78\u597d\u6211\u81ea\u5e7c\u7ec3\u529f\uff0c\u8fd8\u662f\u7ae5\u8eab\uff0c\u8fd9\u70b9\u53ef\u662f\u90a3\u8001\u7334\u5b50\u6bd4\u4e0d\u4e0a\u7684\u3002\u2014\u2014\u201d \u3000\u3000\u4ed6\u5f97\u610f\u5730\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u6697\u4e2d\u8c03\u606f\u4e86\u8bb8\u591a\uff0c\u53ea\u542c\u5f97\u6d1e\u5916\u4e0d\u65f6\u6709\u53ee\u53ee\u549a\u549a\u7684\u804b\u97f3\u4f20\u8fdb\u6765\uff0c\u6709\u65f6\u505c\u4e0b\uff0c\u8fc7\u4e00\u4f1a\u53c8 \u6572\u6253\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u5fc3\u91cc\u5947\u602a\uff0c\u6323\u624e\u7740\u722c\u8d77\u6765\uff1a\u53ea\u770b\u89c1\u684c\u4e0a\u5730\u4e0b\uff0c\u90fd\u662f\u5df2\u7ecf\u5e72\u5f97\u53d1\u9ed1\u7684\u8840\u8ff9\uff0c\u6211\u5934\u4e00 \u6655\uff0c\u53c8\u5012\u5728\u5730\u4e0a\uff0c\u6211\u77e5\u9053\u6211\u5931\u8840\u592a\u591a\uff0c\u6b64\u523b\u5c31\u662f\u4e00\u4e2a\u4e09\u5c81\u7684\u5c0f\u5b69\u5b50\u8fdb\u6765\uff0c\u4e00\u62f3\u4e5f\u80fd\u628a\u6211\u6253 \u6b7b\u3002\u4e8e\u662f\u6211\u53c8\u722c\u56de\u77f3\u684c\uff0c\u52a8\u4e5f\u4e0d\u6562\u52a8\uff0c\u6697\u4e2d\u6162\u6162\u8c03\u606f\u7740\u3002\u201d \u3000\u3000\u859b\u82e5\u74a7\u7d27\u7d27\u62b1\u7740\u5979\u7684\u5b69\u5b50\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5999\u624b\u8bb8\u767d\u7f13\u7f13\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u58c1\u8fb9\uff0c\u5c06\u58c1\u95f4\u7684\u6cb9\u706f\uff0c\u706f\u854a\u62c9\u957f\u4e86\u4e00\u4e9b\uff0c\u4e8e\u662f\u8fd9\u6d1e\u7a9f \u4e2d\u4fbf\u660e\u4eae\u8bb8\u591a\u3002 \u3000\u3000\u8f6c\u8fc7\u8eab\u6765\uff0c\u706f\u5149\u7167\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u53ea\u89c1\u4ed6\u7684\u9762\u8272\uff0c\u5176\u9752\u5982\u94c1\u3002 \u3000\u3000\u859b\u82e5\u74a7\u4f38\u624b\u63e1\u4f4f\u5979\u5b69\u5b50\u7684\u5c0f\u624b\uff0c\u4f46\u89c9\u6e7f\u6f09\u6f09\u5730\uff0c\u539f\u6765\u5979\u638c\u5fc3\u65e9\u5df2\u6dcc\u51fa\u51b7\u6c57\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u76ee\u5149\u6d41\u8f6c\uff0c\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u5728\u684c\u4e0a\u8eba\u4e86\u8bb8\u4e45\uff0c\u90a3\u53ee\u53ee\u549a\u549a\u7684\u58f0\u97f3\uff0c\u867d\u7136\u65ad\u65ad\u7eed\u7eed\uff0c\u5374\u59cb\u7ec8\u5728\u6572\u6253\u7740\u3002 \u3000\u3000\u201c\u6211\u5168\u8eab\u4ecd\u662f\u8f6f\u7ef5\u7ef5\u5730\uff0c\u4e00\u60f3\u8fd9\u4e5f\u4e0d\u662f\u529e\u6cd5\uff0c\u4e8e\u662f\u6211\u5c31\u53c8\u722c\u4e86\u8d77\u6765\uff0c\u4e00\u8def\u722c\u4e86\u51fa\u6765\uff0c\u53ea \u542c\u90a3\u53ee\u53ee\u549a\u549a\u7684\u58f0\u97f3\uff0c\u5c31\u662f\u5728\u6d1e\u53e3\u53d1\u51fa\u6765\uff0c\u4e8e\u662f\u6211\u66f4\u52a0\u5c0f\u5fc3\uff0c\u4e0d\u6562\u5f04\u51fa\u4e00\u70b9\u58f0\u97f3\u6765\uff0c\u8eb2\u5728\u5c71 \u58c1\u7684\u647a\u7f1d\u91cc\u5f80\u5916\u4e00\u770b\u2014\u2014\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u5927\u7b11\u4e00\u58f0\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u4e07\u5929\u840d\u90a3\u8001\u7334\u5b50\u4e5f\u88ab\u56f0\u5728\u91cc\u9762\u4e86\uff0c\u6b64\u523b\u6b63\u5728\u5c71\u6d1e\u95e8\u53e3\u5904\uff0c\u53d1\u72c2\u5730\u6572\u6253\u7740\u5c71\u6d1e\uff0c\u50cf \u662f\u60f3\u628a\u5c71\u58c1\u5f04\u4e2a\u6d1e\uff0c\u4f46\u662f\u2014\u2014\u201d \u3000\u3000\u4ed6\u53c8\u653e\u58f0\u4e00\u7b11\uff1a \u3000\u3000\u201c\u4f60\u60f3\u60f3\u770b\uff0c\u8fd9\u600e\u4e48\u529e\u5f97\u5230\uff1f\u201d \u3000\u3000\u201c\u6211\u518d\u4ed4\u7ec6\u4e00\u770b\uff0c\u539f\u6765\u4ed6\u8fd9\u7334\u5b50\uff0c\u4e5f\u662f\u4e0d\u5927\u83c5\u7528\u4e86\uff0c\u4e00\u4e3e\u4e00\u52a8\uff0c\u90fd\u900f\u7740\u6709\u6c14\u65e0\u529b\u7684\u6837 \u5b50\uff0c\u800c\u4e14\u6572\u4e0d\u4e86\u4e24\u4e0b\uff0c\u5c31\u5f97\u505c\u4e0b\u6765\u6b47\u4e00\u6b47\uff0c\u7c97\u7740\u8116\u5b50\u76f4\u5598\u6c14\u3002 \u3000\u3000\u201c\u90a3\u65f6\u5019\u6211\u53ea\u8981\u6709\u539f\u6709\u529f\u529b\u7684\u5341\u5206\u4e4b\u4e00\uff0c\u5c31\u53ef\u4ee5\u628a\u4ed6\u5f04\u6b7b\uff0c\u53ea\u53ef\u60dc\u6211\u90a3\u65f6\u5374\u6bd4\u4ed6\u66f4\u4e0d\u7ba1 \u7528\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u95ee\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5a03\u5a03\uff01\u4f60\u8d70\u7684\u65f6\u5019\uff0c\u662f\u4e0d\u662f\u5148\u5c06\u6d1e\u95e8\u53c8\u5173\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u900f\u51fa\u4e00\u53e3\u957f\u6c14\uff0c\u6447\u4e86\u6447\u5934\uff0c\u5c06\u81ea\u5df1\u5982\u4f55\u5c06\u4e07\u5929\u840d\u9a97\u5165\u5c71\u7a9f\uff0c\u5173\u4e0a\u77f3\u95e8\u7684\u4e8b\uff0c\u8bf4\u4e86\u4e00 \u904d\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u629a\u638c\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u60f3\u4e0d\u5230\u8fd9\u53ea\u8001\u72d0\u72f8\uff0c\u4e5f\u6709\u4e0a\u4eba\u5f53\u7684\u4e00\u5929\uff0c\u771f\u6559\u8001\u592b\u9ad8\u5174\u5f97\u5f88\uff01\u201d \u4ef0\u5929\u8fde\u58f0\u5927\u7b11\uff0c\u663e\u89c1\u5f97\u5fc3\u4e2d\u9ad8\u5174\u5df2\u6781\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516d\u5341\u4e5d\u7ae0\u3000\u62bd\u4e1d\u5265\u8327>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516d\u5341\u4e5d\u7ae0\u3000\u62bd\u4e1d\u5265\u8327 \u3000\u3000\u4f0a\u98ce\u6014\u6014\u5730\u671b\u7740\u8bb8\u767d\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a \u3000\u3000\u201c\u8fd9\u5999\u624b\u8bb8\u767d\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u58f0\u5982\u6d2a\u949f\uff0c\u5fc3\u91cc\u6709\u4ec0\u4e48\u4e8b\uff0c\u5927\u534a\u90fd\u663e\u9732\u5728\u9762\u4e0a\u3002\u90a3\u4e07\u5929\u840d\u5374 \u662f\u7626\u5c0f\u965b\u5e72\uff0c\u4e0d\u4f46\u9762\u4e0a\u6c38\u8fdc\u4e0d\u52a8\u58f0\u8272\uff0c\u8bf4\u8bdd\u4e5f\u662f\u5c16\u58f0\u5c16\u6c14\u3002\u8fd9\u4e24\u4eba\u4e00\u9634\u4e00\u9633\uff0c\u4e00\u6b63\u4e00\u53cd\uff0c\u50cf \u662f\u5929\u751f\u51fa\u6765\uff0c\u5c31\u662f\u5bf9\u5934\uff0c\u5012\u4e0d\u77e5\u5c06\u6765\u662f\u4f55\u4e86\u5c40\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u601d\u5fd6\uff0c\u5374\u542c\u8bb8\u767d\u53c8\u9053\uff1a \u3000\u3000\u201c\u662f\u4ee5\u54ea\u65f6\u6211\u8fde\u5927\u6c14\u4e5f\u4e0d\u6562\u5598\uff01\u5fc3\u91cc\u6b63\u5728\u60f3\uff1a\u201c\u6d1e\u95e8\u6253\u4e0d\u5f00\u6700\u597d\uff0c\u53cd\u6b63\u6211\u65e2\u6d3b\u4e0d\u6210\uff0c\u4f60 \u4e5f\u6b7b\u5b9a\u4e86\u3002\u90a3\u77e5\u6d1e\u5916\u5374\u7a81\u5730\u54cd\u8d77\u4e00\u4e2a\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u6211\u867d\u542c\u4e0d\u6e05\u695a\uff0c\u5374\u89c1\u90a3\u8001\u7334\u5b50\u542c\u4e86\uff0c\u9ad8\u5174 \u5f97\u4e0a\u4e0b\u4e71\u8df3\u2014\u2014\u54c8\uff01\u4f60\u6ca1\u6709\u770b\u5230\uff0c\u90a3\u624d\u771f\u50cf\u53ea\u6d3b\u7334\u5b50\u54e9\uff01\u201d \u3000\u3000\u201c\u4ed6\u8df3\u4e86\u534a\u664c\u540e\uff0c\u5c31\u7528\u5634\u5427\u8d34\u7740\u77f3\u58c1\uff0c\u5bf9\u5916\u9762\u5927\u58f0\u547c\u558a\uff0c\u544a\u8bc9\u90a3\u4eba\u5f00\u542f\u6d1e\u95e8\u7684\u65b9\u6cd5\uff0c\u53ea \u662f\u4ed6\u6b64\u523b\u4e2d\u6c14\u5df2\u7ecf\u4e0d\u8db3\uff0c\u53eb\u4e86\u4e24\u4e09\u904d\uff0c\u90a3\u4eba\u624d\u542c\u6e05\u695a\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u201c\u5440\u201d\u5730\u4e00\u58f0\uff0c\u90a3\u77f3 \u95e8\u679c\u7136\u5f00\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u900f\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u95e8\u5916\u7acb\u523b\u6709\u5929\u5149\u5c04\u8fdb\u6765\uff0c\u4e5f\u6709\u98ce\u5439\u8fdb\u6765\uff0c\u98ce\u5439\u5230\u6211\u8eab\u4e0a\uff0c\u6211\u771f\u662f\u9ad8\u5174\u6781\u4e86\uff0c\u8fd9\u65f6\u5019\u6d1e \u5916\u63a0\u5165\u4e00\u4e2a\u4eba\u6765\uff0c\u8eab\u6750\u4e5f\u5c31\u548c\u4f60\u8fd9\u4e48\u9ad8\u5927\uff0c\u7a7f\u7740\u4e00\u88ad\u6781\u534e\u8d35\u7684\u888d\u5b50\uff0c\u770b\u8d77\u6765\u5012\u662f\u98ce\u5ea6\u7fe9\u7fe9\u7684 \u6837\u5b50\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5251\u7709\u4e00\u8f69\uff0c\u8131\u53e3\u9053\uff1a \u3000\u3000\u201c\u8fd9\u60f3\u5fc5\u5c31\u662f\u8427\u65e0\u4e86\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u53cc\u76ee\u4e00\u5f20\uff0c\u60ca\u8bb6\u5730\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u600e\u5730\u77e5\u9053\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u54fc\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u8f6c\u8fc7\u859b\u82e5\u74a7\u8eab\u4e0a\uff0c\u53c8\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u524d\u51e0\u5929\u5df2\u89c1\u8fc7\u59d3\u4e07\u7684\u4e86\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u6b64\u4eba\u679c\u7136\u81ea\u79f0\u59d3\u8427\u540d\u65e0\uff0c\u5f53\u65f6\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u53ea\u662f\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u4ed6\u7adf\u662f\u8fd1 \u5e74\u540d\u9707\u6c5f\u6e56\u7684\u201c\u5929\u4e89\u6559\u201d\u6559\u4e3b\u3002\u201d \u3000\u3000\u4f0a\u98ce\u9f3b\u5b54\u91cc\u53c8\u8f7b\u54fc\u4e00\u58f0\uff0c\u5374\u542c\u4ed6\u53c8\u9053\uff1a \u3000\u3000\u201c\u5f53\u65f6\u6211\u4f0f\u5728\u6697\u5904\uff0c\u89c1\u8fd9\u8427\u65e0\u4e0e\u90a3\u8001\u7334\u5b50\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u76ee\u5149\u4f3c\u6709\u610f\u4f3c\u65e0\u610f\u5730\uff0c\u671d\u6211\u8fd9\u8fb9 \u671b\u4e86\u4e24\u773c\uff0c\u6211\u4e5f\u672a\u66fe\u5728\u610f\u3002\u53ea\u89c1\u90a3\u8001\u7334\u5b50\u8ddf\u7740\u4ed6\u8d70\u51fa\u4e86\u6d1e\uff0c\u6211\u5374\u4e0d\u6562\u51fa\u53bb\uff0c\u5750\u5728\u5f53\u98ce\u4e4b\u5904\uff0c \u5438\u4e86\u4e24\u53e3\u6c14\uff0c\u53c8\u6015\u90a3\u8001\u7334\u5b50\u7a81\u7136\u8f6c\u56de\u6765\uff0c\u53ea\u5f97\u53c8\u722c\u56de\u89d2\u843d\u91cc\u3002 \u3000\u3000\u201c\u90a3\u77e5\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u8427\u65e0\u5374\u53c8\u8f6c\u4e86\u56de\u6765\uff0c\u7b14\u76f4\u5730\u8d70\u5411\u6211\u85cf\u8eab\u7684\u5730\u65b9\uff0c\u671d\u6211\u5f53\u5934\u4e00\u63d6\uff0c \u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u5c31\u662f\u5343\u91cc\u8ffd\u98ce\uff0c\u795e\u884c\u65e0\u5f71\uff0c\u8bb8\u5927\u4fa0\uff01\u201d\u6211\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u91cc\u4e0d\u7981\u6697\u6697\u4f69\u670d\uff0c\u8fd9 \u4e2a\u8427\u65e0\u5e74\u7eaa\u867d\u8f7b\uff0c\u5012\u7684\u786e\u662f\u4e2a\u89d2\u3002\u5c31\u51ed\u8fd9\u4efd\u5fc3\u601d\uff0c\u5c31\u4e0d\u662f\u666e\u901a\u4eba\u80fd\u591f\u4f01\u53ca\u7684\u3002\u201d \u3000\u3000\u4f0a\u98ce\u518d\u6b21\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u8f6c\u5230\u522b\u7684\u5730\u65b9\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u7792\u4ed6\u4e0d\u8fc7\uff0c\u5c31\u5c06\u4e8b\u60c5\u6e90\u6e90\u672c\u672c\u544a\u8bc9\u4e86\u4ed6\u3002\u4ed6\u4e00\u542c\u90a3\u201c\u7487\u5149\u5b9d\u4eea\u201d\u53ef\u80fd\u5c31\u5728\u4e07\u5929 \u840d\u8eab\u4e0a\uff0c\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u60cb\u60dc\u7684\u795e\u8272\u6765\u3002\u6211\u5fc3\u91cc\u6697\u60f3\uff1a\u201c\u53ea\u6015\u4f60\u8001\u65e9\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u8981\u4e58\u90a3\u8001\u7334 \u5b50\u529b\u5f31\u7684\u65f6\u5019\uff0c\u62a2\u4e86\u8fc7\u6765\u3002\u201d\u4e8e\u662f\u6211\u5c31\u77e5\u9053\uff0c\u8fd9\u4e5f\u4e0d\u662f\u597d\u4e1c\u897f\u3002\u201d \u3000\u3000\u4f0a\u98ce\u201c\u54c8\u201d\u5730\u4e00\u7b11\uff0c\u62cd\u4e86\u62cd\u5927\u817f\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u524d\u8f88\u7684\u89c1\u8bc6\uff0c\u679c\u7136\u8d85\u4eba\u4e00\u7b49\uff01\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u6293\u4e86\u628a\u706b\u817f\uff0c\u653e\u5165\u53e3\u4e2d\uff0c\u53c8\u81ea\u54c8\u54c8\u5927\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u95ef\u6c64\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u90a3\u867d\u7136\u7cbe\u7075\uff0c\u5374\u53c8\u600e\u7cbe\u7075\u5f97\u8fc7\u6211\uff01\u4f46\u5f53\u65f6\u6211\u4e5f\u4e0d\u52a8\u58f0\u8272\uff0c\u53cd\u800c \u8fde\u8fde\u5938\u5956\u7740\u4ed6\uff0c\u4ed6\u4e5f\u5c3d\u5bf9\u6211\u8bf4\u4e9b\u4ef0\u6155\u7684\u8bdd\uff0c\u53c8\u6276\u7740\u6211\u8d70\u4e0b\u5c71\uff0c\u5728\u8def\u4e0a\u8bbe\u6cd5\u627e\u7740\u53ea\u6d3b\u9e7f\uff0c\u6253\u6b7b \u4e86\uff0c\u6211\u8d81\u70ed\u5c06\u9e7f\u8840\u90fd\u559d\u4e86\u4e0b\u53bb\uff0c\u7cbe\u795e\u624d\u89c9\u5f97\u7565\u4e3a\u597d\u4e9b\u3002 \u3000\u3000\u201c\u4e0d\u8fc7\u6211\u6709\u4e9b\u5947\u602a\uff1a\u8fd9\u59d3\u8427\u7684\u600e\u4f1a\u5982\u6b64\u5bf9\u6211\uff1f\u201d \u3000\u3000\u201c\u540e\u6765\u4ed6\u53c8\u544a\u8bc9\u6211\uff0c\u548c\u59d3\u4e07\u7684\u90a3\u53ea\u8001\u7334\u5b50\uff0c\u7ea6\u5728\u8fd9\u897f\u6881\u5c71\u4e0a\u89c1\u9762\uff0c\u8bf4\u4e86\u534a\u5929\uff0c\u8a00\u4e0b\u4e4b \u610f\uff0c\u5374\u662f\u53eb\u6211\u5e2e\u4ed6\u4e00\u9f50\u5f04\u6b7b\u4e07\u5929\u840d\u3002\u6211\u5fc3\u91cc\u4e00\u5bd2\uff0c\u6697\u6697\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb6\u4f19\u5fc3\u80a0\u771f\u6bd2\uff0c\u5fc3\u91cc\u60f3 \u5f04\u6b7b\u4e07\u5929\u840d\uff0c\u81ea\u5df1\u53c8\u4e0d\u613f\u4e0b\u624b\uff0c\u5374\u53eb\u8001\u592b\u6765\u66ff\u4ed6\u9876\u7f38\u3002\u201d\u5f53\u65f6\u6211\u8fd9\u6837\u60f3\uff0c\u5df2\u7ecf\u662f\u5f80\u6700\u574f\u7684\u5730 \u65b9\u60f3\u4e86\uff0c\u90a3\u77e5\u8fd9\u5c0f\u5b50\u5374\u8fd8\u8981\u574f\u4e0a\u5341\u500d\uff01\u201d \u3000\u3000\u201c\u539f\u6765\u4ed6\u77e5\u9053\u6211\u548c\u90a3\u8001\u7334\u5b50\uff0c\u4e00\u4e2a\u5f3a\u76d7\uff0c\u4e00\u4e2a\u5c0f\u5077\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u4e00\u5b9a\u5f04\u4e86\u4e0d\u5c11\u94b1\uff0c\u4ed6 \u4e5f\u60f3\u5206\u70b9\u8d3c\u8d43\u3002\u540e\u6765\u542c\u6211\u8bf4\u8d77\u201c\u7487\u5149\u5b9d\u4eea\u201d\u7684\u597d\u5904\uff0c\u4ed6\u53c8\u52a8\u4e86\u5fc3\uff0c\u6240\u4ee5\u624d\u8fd9\u4e48\u505a\uff0c\u4e00\u9762\u8ba9\u6c5f \u6e56\u4e2d\u4eba\u90fd\u77e5\u9053\u4ed6\u662f\u4e2a\u5927\u4ec1\u5927\u4e49\u7684\u541b\u5b50\uff0c\u201c\u5357\u5077\u5317\u76d7\u201d\u90fd\u662f\u4ece\u4ed6\u624b\u4e0a\u6551\u51fa\u6765\u7684\uff1b\u4e00\u9762\u8ba9\u6211\u548c\u90a3 \u7334\u5b50\u62da\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff0c\u4ed6\u5374\u5728\u65c1\u8fb9\u6361\u73b0\u6210\u7684\u3002\u5c31\u7b97\u4e8b\u60c5\u4e0d\u5982\u7406\u60f3\uff0c\u6211\u548c\u90a3\u8001\u7334\u5b50\u603b\u4f1a\u611f\u6fc0\u4ed6\u4e00 \u8f88\u5b50\uff0c\u5c06\u6765\u4ed6\u9047\u7740\u4ec0\u4e48\u4e8b\uff0c\u6211\u4eec\u77e5\u9053\u4e86\u4e5f\u4e0d\u4f1a\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u4f0a\u98ce\u6697\u53f9\u4e00\u58f0\uff0c\u89c9\u5f97\u4eba\u4e16\u4e4b\u95f4\u7684\u673a\u8bc8\uff0c\u6709\u8bb8\u591a\u771f\u4e0d\u662f\u81ea\u5df1\u80fd\u591f\u4e86\u89e3\u7684\u3002 \u3000\u3000\u53c8\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u90a3\u8427\u65e0\u7684\u786e\u4e0d\u6127\u4e3a\u67ad\u96c4\u4e4b\u624d\uff0c\u884c\u4e8b\u4e4b\u9634\u68ee\u72e1\u8bc8\uff0c\u786e\u975e\u5e38\u4eba\u6240\u80fd\u591f\u5fd6\u91cf\u5f97\u51fa\u7684\u3002\u5509\uff01\u2014 \u2014\u6b64\u4eba\u57ce\u5e9c\u5982\u6b64\u4e4b\u6df1\uff0c\u5c06\u6765\u8981\u9664\u53bb\u4ed6\uff0c\u53ea\u6015\u4e0d\u5bb9\u6613\u54e9\uff01\u201d \u3000\u3000\u8bb8\u767d\u4e00\u6447\u866c\u987b\uff0c\u5927\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u8fd9\u60f3\u5f97\u867d\u5999\uff0c\u8001\u592b\u5374\u4e5f\u4e0d\u662f\u5446\u5b50\u3002\u8001\u592b\u548c\u4ed6\u5206\u4e86\u624b\u540e\uff0c\u5c31\u627e\u4e86\u4e2a\u5730\u65b9\uff0c\u5f04\u4e86\u4e2a\u8865 \u8840\u8865\u6c14\u7684\u4e1c\u897f\u6765\uff0c\u5927\u5403\u5927\u559d\u5341\u51e0\u5929\u3002\u7b49\u5230\u6c14\u529b\u6062\u590d\u4e86\uff0c\u5c31\u8dd1\u5230\u8fd9\u897f\u6881\u5c71\u6765\uff0e\u5374\u770b\u5230\u4e07\u5929\u840d\u90a3 \u8001\u7334\u5b50\uff0c\u5446\u5446\u5730\u5750\u5728\u8fd9\u4e2a\u5c71\u6d1e\u7684\u524d\u9762\uff0c\u4ed6\u65c1\u8fb9\u8fd8\u6709\u4e2a\u5973\u5b69\u5b50\uff0c\u4e0d\u4f4f\u5730\u592e\u6c42\u4ed6\u5c06\u5835\u5728\u6d1e\u53e3\u7684\u5927 \u77f3\u642c\u5f00\u3002 \u3000\u3000\u201c\u6211\u4e00\u89c1\u8fd9\u8001\u7334\u5b50\u4e4b\u9762\uff0c\u5c31\u89c9\u5f97\u6c14\u4f4f\u4e0a\u649e\uff0c\u672c\u6765\u60f3\u7b49\u5230\u90a3\u59d3\u8427\u7684\u5c0f\u5b50\u4e5f\u6765\u4e86\uff0c\u5f04\u5f97\u4ed6\u4eec \u5148\u6253\u4e00\u67b6\u7684\u8ba1\u5212\uff0c\u4e5f\u629b\u5230\u4e5d\u9704\u4e91\u5916\u4e86\u3002\u201d \u3000\u3000\u4ed6\u987b\u53d1\u5f20\uff0c\u4e00\u77ac\u4e4b\u95f4\uff0c\u4f46\u89c9\u4ed6\u8c6a\u6c14\u9044\u98de\u3002 \u3000\u3000\u4f0a\u98ce\u6697\u5fd6\uff1a \u3000\u3000\u201c\u8fd9\u5999\u624b\u8bb8\u767d\u867d\u7136\u4e5f\u72e1\u8bc8\u5f97\u5f88\uff0c\u4f46\u5374\u662f\u4e2a\u6027\u60c5\u4e2d\u4eba\uff0c\u8a00\u8bcd\u4e3e\u6b62\uff0c\u4ecd\u4e0d\u5931\u4e3a\u70ed\u8840\u6c49\u5b50\uff0c\u5012 \u8981\u6bd4\u90a3\u4e9b\u201c\u4f2a\u541b\u5b50\u201d\u8981\u5f3a\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u987b\u77e5\u4eba\u4e16\u4e4b\u95f4\uff0c\u201c\u771f\u5c0f\u4eba\u201d\u82e5\u591a\u4e8e\u201c\u4f2a\u541b\u5b50\u201d\u7684\u8bdd\uff0c\u90a3\u4e48\u4e16\u95f4\u5c31\u8981\u592a\u5e73\u591a\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u5999\u624b\u8bb8\u767d\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u8c6a\u6c14\u987f\uff0c\u957f\u53f9\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u4e00\u751f\u884c\u4e8b\uff0c\u5c31\u662f\u5403\u5c3d\u8fd9\u201c\u4e0d\u80fd\u5fcd\u201d\u7684\u4e8f\uff0c\u5c0f\u5a03\u5a03\uff01\u4f60\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u6b63\u662f\u5982\u65e5\u65b9\u4e2d\uff0c \u5b9a\u8981\u5728\u8fd9\u201c\u5fcd\u201d\u5b57\u4e0a\uff0c\u591a\u4e0b\u4e9b\u529f\u592b\uff0c\u65b9\u80fd\u6210\u5f97\u5927\u5668\uff0c\u8fd9\u4e0d\u662f\u8001\u592b\u4ee5\u8001\u5356\u8001\uff0c\u5374\u662f\u7531\u8877\u4e4b \u8a00\u3002\u201d \u3000\u3000\u4f0a\u98ce\u552f\u552f\u53d7\u6559\uff0c\u5fc3\u4e0b\u4e0d\u89c9\u5bf9\u8fd9\u8c6a\u8fc8\u7684\u8001\u4eba\uff0c\u8d77\u4e86\u597d\u611f\u3002 \u3000\u3000\u5374\u89c1\u8fd9\u5999\u624b\u8bb8\u767d\u201c\u5427\u201d\u5730\u4e00\u62cd\u77f3\u684c\uff0c\u9707\u5f97\u77f3\u684c\u4e0a\u7684\u4e66\u518c\u9e21\u9aa8\uff0c\u90fd\u76f4\u8df3\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u987a\u624b\u53c8\u63a5\u8fc7\u4e00\u5757\u9e21\u812f\uff0c\u63a5\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u76db\u6012\u4e4b\u4e0b\uff0c\u5c31\u8df3\u4e86\u51fa\u53bb\uff0c\u6307\u7740\u4e07\u5929\u840d\u6b32\u5927\u9a82\uff0c\u90a3\u77e5\u90a3\u8001\u7334\u5b50\u4e00\u89c1\u6211\u7684\u9762\uff0c\u5413\u5f97\u8138 \u90fd\u767d\u4e86\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u6389\u5934\u5c31\u8dd1\u3002 \u3000\u3000\u201c\u672c\u6765\u7ad9\u5728\u4ed6\u65c1\u8fb9\u7684\u5973\u5b50\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u58f0\u53eb\u7740\u201c\u7239\u7239\u201d\uff0c\u4e5f\u8ddf\u7740\u63a0\u53bb\u3002 \u3000\u3000\u201c\u6211\u5fc3\u91cc\u8f6c\u4e86\u51e0\u8f6c\uff0c\u89c1\u90a3\u8001\u7334\u5b50\u65bd\u5c55\u8f7b\u529f\u4e4b\u95f4\uff0c\u529f\u529b\u5f77\u4f5b\u53c8\u6bd4\u4ee5\u524d\u7cbe\u8fdb\u4e86\u4e9b\uff0c\u7eb5\u7136\u6211\u80fd \u8ffd\u4e0a\u4ed6\uff0c\u4e5f\u672a\u5fc5\u662f\u4ed6\u7684\u654c\u624b\uff0c\u4f55\u51b5\u6211\u53c8\u5728\u5947\u602a\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5b88\u5728\u6d1e\u53e3\uff0c\u662f\u4ee5\u6211\u5c31\u8bbe\u6cd5\u5f04\u5f00\u4e86 \u5835\u5728\u95e8\u53e3\u7684\u5927\u77f3\u5757\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fae\u987f\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u90a3\u53ef\u771f\u8d39\u4e86\u6211\u4e0d\u5c11\u529f\u592b\uff0c\u8fd8\u627e\u5230\u6839\u94c1\u68cd\u624d\u628a\u5b83\u5f04\u5f00\uff0c\u4e5f\u771f\u96be\u4e3a\u90a3\u8001\u7334\u5b50\uff0c\u600e\u4e48\u628a\u4ed6\u642c \u6765\u7684\uff0c\u8fd9\u79cd\u81c2\u529b\uff0c\u53ef\u771f\u60ca\u4eba\u5f97\u5f88\uff01\u201d \u3000\u3000\u8fd9\u5999\u624b\u8bb8\u767d\u5a13\u5a13\u8a00\u6765\uff0c\u5c06\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u4e9b\u672a\u89e3\u4e4b\u8c1c\uff0c\u90fd\u5982\u62bd\u4e1d\u5265\u8327\u822c\uff0c\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u859b\u82e5\u74a7\u66f4\u662f\u542c\u5f97\u5fc3\u4e2d\u6fc0\u52a8\u4e0d\u5df2\uff0c\u7d27\u7d27\u63e1\u7740\u5979\u5b69\u5b50\u7684\u5c0f\u624b\uff0c\u5374\u8fde\u52a8\u5f39\u90fd\u6ca1\u6709\u52a8\u5f39\u4e00\u4e0b\u3002 \u3000\u3000\u58c1\u95f4\u706f\u706b\u7684\u5149\u5f71\uff0c\u7a81\u5730\u4e00\u6447\uff0c\u8fd9\u76cf\u94dc\u706f\u50a8\u6cb9\u867d\u591a\uff0c\u4f46\u70b9\u4e86\u8fd9\u4e48\u4e9b\u5929\uff0c\u5374\u5df2\u5c06\u8fd1\u6cb9\u7aed\u706f\u67af \u4e86\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u7ae0\u3000\u60c5\u6028\u7f20\u7ed3>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u7ae0\u3000\u60c5\u6028\u7f20\u7ed3 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u5c31\u5728\u706f\u5149\u98d8\u6447\u4e4b\u95f4\uff0c\u6d1e\u5916\u7a81\u5730\u98de\u4e5f\u4f3c\u5730\uff0c\u63a0\u8fdb\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u9762\u8272\u5fae\u53d8\u3002 \u3000\u3000\u5374\u89c1\u63a0\u8fdb\u6d1e\u6765\u7684\uff0c\u7adf\u662f\u90a3\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u7684\u5973\u513f\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u6b64\u523b\u867d\u4ecd\u662f\u4e00\u8eab\u7fe0\u88f3\uff0c\u4f46\u4e91\u9b13\u84ec\u4e71\uff0c\u7389\u5bb9\u6194\u60b4\uff0c\u8863\u886b\u4e5f\u51cc\u4e71\u5f97\u5f88\u3002\u63a0\u8fdb\u6d1e\u91cc\uff0c\u79cb \u6ce2\u56db\u8f6c\uff0c\u4e00\u773c\u671b\u5230\u5999\u624b\u8bb8\u767d\uff0c\u9762\u8272\u5fae\u5fae\u53d8\u4e86\u4e00\u4e0b\u3002\u53c8\u5728\u859b\u82e5\u74a7\u9762\u4e0a\u72e0\u72e0\u76ef\u4e86\u51e0\u773c\uff0c\u201c\u5624\u549b\u201d \u4e00\u58f0\uff0c\u63a0\u5230\u4f0a\u98ce\u8eab\u4fa7\uff0c\u5fae\u5f20\u6a31\u53e3\uff0c\u5374\u53c8\u5450\u5450\u5730\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u6d1e\u7a9f\u4e4b\u4e2d\uff0c\u9664\u4e86\u4f0a\u98ce\u4e4b\u5916\uff0c\u5c45\u7136\u8fd8\u6709\u522b\u7684\u4eba\u5728\uff0c\u8fd9\u663e\u7136\u5927\u5927\u51fa\u4e4e\u5979\u7684\u610f\u6599\u4e4b\u5916\u3002\u800c \u4e14\u8fd9\u6d1e\u7a9f\u4e4b\u4e2d\u5c45\u7136\u6709\u5e8a\u6709\u51e0\uff0c\u66f4\u662f\u4ee4\u4eba\u60ca\u6115\uff01\u53ea\u662f\u5728\u60ca\u6115\u4e4b\u4e2d\uff0c\u5979\u5374\u53c8\u6709\u4e9b\u6b23\u559c\uff0c\u56e0\u4e3a\u5979\u7684 \u610f\u4e2d\u4e4b\u4eba\u4f0a\u98ce\uff0c\u6b64\u523b\u795e\u91c7\u5955\u5955\uff0c\u5b8c\u5168\u4e0d\u662f\u5979\u60f3\u50cf\u4e2d\u8870\u5f31\u6194\u60b4\u7684\u6837\u5b50\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u5927\u6b65\u8fc8\u524d\u4e00\u6b65\uff0c\u5389\u58f0\u53f1\u9053\uff1a \u3000\u3000\u201c\u5c0f\u59d1\u5a18\uff01\u4f60\u7684\u7239\u7239\u5462\uff1f\u201d \u3000\u3000\u4e07\u8679\u79cb\u6ce2\u4e00\u6a2a\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u8f6c\u5411\u4f0a\u98ce\uff0c\u6084\u8bed\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u5929\u6765\uff0c\u4f60\u53ef\u8fd8\u597d\u5417\uff1f\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\uff0c\u53cc\u76ee\u7535\u5f20\u3002\u4f0a\u98ce\u8fde\u5fd9\u957f\u8eab\u7ad9\u8d77\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u6709\u4ec0\u4e48\u8bdd\uff0c\u4e0d\u59a8\u51fa\u53bb\u518d\u8bf4\uff0c\u6211\u5728\u8fd9\u91cc\u56f0\u4e86\u5341\u591a\u5929\uff0c\u5b9e\u5728\u6709\u4e9b\u817b\u4e86\uff01\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\uff0c\u51b7\u51b7\u5730\u671b\u7740\u859b\u82e5\u74a7\uff0c\u53f1\u9053\uff1a \u3000\u3000\u201c\u81f3\u4e8e\u4f60\u51fa\u4e0d\u51fa\u53bb\uff0c\u6211\u662f\u6089\u542c\u5c0a\u610f\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u53cc\u624b\u75be\u4f38\uff0c\u53bb\u62a2\u859b\u82e5\u74a7\u8eab\u4fa7\u7684\u5b69\u5b50\uff0c\u4e00\u9762\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u53ef\u5f97\u4ea4\u7ed9\u6211\u3002\u201d \u3000\u3000\u859b\u82e5\u74a7\u5a07\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53cc\u624b\u62a4\u4f4f\u5b69\u5b50\uff0c\u5de6\u817f\u84e6\u5730\u51fa\u3002\u4f0a\u98ce\u5fae\u4e00\u62e7\u8170\uff0c\u53f3\u638c\u4e0b\u5207\uff0c\u5de6\u638c\u4ecd\u539f\u5f0f\u53bb\u62a2\u90a3\u5b69\u5b50\u3002\u90a3\u77e5 \u859b\u82e5\u74a7\u5de6\u817f\u5fae\u7f29\uff0c\u53f3\u817f\u5df2\u7535\u4e5f\u4f3c\u7684\u4e86\u51fa\u6765\uff0c\u5979\u867d\u7136\u5927\u8179\u4fbf\u4fbf\uff0c\u4f46\u8fd9\u8fde\u73af\u4e24\u817f\uff0c\u4ecd\u7136\u662f\u75be\u5982\u98d8 \u98ce\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u4e0d\u7075\u4fbf\u7684\u6837\u5b50\u3002 \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u7684\u6b66\u529f\uff0c\u867d\u5df2\u5927\u5f02\u4e8e\u5f80\u6614\uff0c\u4f46\u6b64\u523b\u5374\u4e0d\u5f97\u4e0d\u64a4\u6b65\u62e7\u8eab\uff0c\u5148\u6c42\u81ea\u4fdd\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u610f\u5728\u62a2\u5f97\u81ea\u5df1\u7684\u5b69\u5b50\uff0c\u5e76\u4e0d\u60f3\u4f24\u53ca\u859b\u82e5\u74a7\uff0c\u662f\u4ee5\u51fa\u624b\u4fbf\u6709\u8bb8\u591a\u987e\u5fcc\uff0c\u8bb8\u591a\u7cbe\u5999\u800c \u72e0\u8fa3\u7684\u62db\u5f0f\uff0c\u6839\u672c\u7528\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u65b9\u81ea\u6e9c\u5f00\uff0c\u811a\u6b65\u4e00\u9519\uff0c\u5374\u53c8\u63a0\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u6d53\u7709\u4e00\u8f69\uff0c\u55d6\u5730\u6321\u5230\u4f0a\u98ce\u8eab\u524d\uff0c\u7a81\u5730\u54c8\u54c8\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8001\u592b\u867d\u7136\u4e00\u4e16\u72ec\u8eab\u3002\u5374\u6700\u6068\u522b\u4eba\u592b\u59bb\u5435\u67b6\u3002\u5582\uff01\u6211\u8bf4\u5c0f\u5a03\u5a03\uff0c\u4f60\u548c\u4f60\u8001\u5a46\u5435\u4e9b\u4ec0\u4e48\uff1f \u8bf4\u7ed9\u8001\u592b\u542c\u542c\u770b\uff0c\u8ba9\u8001\u592b\u6765\u8bc4\u8bc4\u7406\u3002\u201d \u3000\u3000\u4e07\u8679\u201c\u5440\u201d\u5730\u4e00\u58f0\uff0c\u5f80\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u7c89\u9762\u7acb\u523b\u53d8\u5f97\u715e\u767d\uff0c\u5446\u5446\u5730\u671b\u7740\u4f0a\u98ce\uff0c\u5374\u89c1\u4f0a\u98ce\u4ea6 \u662f\u6ee1\u9762\u6012\u8272\uff0c\u53cc\u76ee\u6012\u5f20\uff0c\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u8c01\u8ba4\u5f97\u5979\u8fd9\u8d31\u4eba\uff01\u8bb8\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u5411\u859b\u82e5\u74a7\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u859b\u82e5\u74a7\u633a\u8eab\u4ece\u5e8a\u4e0a\u5750\u4e86\u8d77\u6765\uff0c\u5a07\u559d\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u662f\u6211\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u4f38\u51fa\u4e00\u53ea\u6625\u8471\u7389\u624b\uff0c\u6307\u7740\u4f0a\u98ce\uff1a \u3000\u3000\u201c\u4f60\u8bf4\uff01\u4f60\u8bf4\uff01\u4ed6\u662f\u4e0d\u662f\u6211\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u53c8\u9053\uff1a \u3000\u3000\u201c\u8001\u524d\u8f88\uff01\u4f60\u53ef\u5f97\u4e3a\u6211\u8fd9\u82e6\u547d\u7684\u5973\u5b50\uff0c\u4e3b\u6301\u516c\u9053\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u7adf\u53c8\u63a9\u9762\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u53cc\u76ee\u706b\u8d64\uff0c\u6c14\u5f97\u8fde\u58f0\u97f3\u90fd\u53d8\u4e86\uff0c\u987f\u8db3\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u8d31\u4eba\u2026\u2026\u6211\u5b69\u5b50\u53ef\u6ca1\u6709\u4f60\u8fd9\u79cd\u6bcd\u4eb2\uff01\u8bb8\u8001\u524d\u8f88\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u5973\u5b50\u628a\u201c\u4e03\u51fa\u201d\u4e4b \u6761\uff0c\u90fd\u72af\u5c3d\u4e86\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u53c8\u600e\u80fd\u5c06\u4e8b\u5b9e\u7684\u771f\u76f8\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4f46\u5999\u624b\u8bb8\u767d\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5bdf\u8a00\u8fa8\u8272\uff0c\u5df2\u4e5f\u731c\u51fa\u4e2a\u7a76\u7adf\u3002\u76ee\u5149\u6570\u8f6c\uff0c\u4e5f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u597d\u3002 \u3000\u3000\u90a3\u77e5\u4e07\u8679\u7a81\u5730\u201c\u54af\u54af\u201d\u4e00\u7b11\uff0c\u5a40\u5a1c\u8d70\u4e86\u8fc7\u6765\uff0c\u5a07\u58f0\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u6562\u60c5\u5c31\u662f\u4ec0\u4e48\u201c\u5929\u4e89\u6559\u4e3b\u201d\u7684\u592b\u4eba\u5427\uff1f\u602a\u4e0d\u5f97\u6211\u548c\u7239\u7239\u524d\u51e0\u5929\u5b88\u5728\u5916\u9762\u7684\u65f6\u5019\uff0c \u6709\u597d\u51e0\u62e8\u7a7f\u7740\u4e94\u989c\u516d\u8272\u8863\u670d\u7684\u6c49\u5b50\u4eec\uff0c\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u8bf4\u662f\u8981\u627e\u201c\u6559\u4e3b\u592b\u4eba\u201d\uff0c\u53c8\u8bf4\uff1a\u4ed6\u4eec\u90fd \u662f\u201c\u5929\u4e89\u6559\u201d\u4e0b\u7684\u5f1f\u5b50\u3002\u6211\u7239\u7239\u4e0d\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff0c\u5168\u7ed9\u6253\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u5979\u79cb\u6ce2\u8f6c\u5411\u4f0a\u98ce\uff0c\u5a07\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u8bf4\u4f60\u5440\uff01\u4f60\u8fd9\u4eba\u771f\u662f\u2026\u2026\u8981\u4eba\u5bb6\u7684\u5b69\u5b50\u5e72\u4ec0\u4e48\uff1f\u4f60\u8981\u5b69\u2026\u2026\u201d \u3000\u3000\u5979\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u7c89\u9762\u5fae\u7ea2\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4f0a\u98ce\u5251\u7709\u6012\u8f69\uff0c\u540e\u9762\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u542c\u89c1\uff0c\u5374\u95ee\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e9b\u4eba\u53eb\u4ec0\u4e48\u540d\u5b57\u3002\u201d \u3000\u3000\u4e07\u8679\u5a07\u58f0\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u540d\u5b57\u6211\u53ef\u8bb0\u4e0d\u5f97\u4e86\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u4e2a\u76f4\u7709\u6115\u773c\u7684\uff0c\u5374\u5168\u4e0d\u50cf\u662f\u597d\u4eba\u3002\u5176\u4e2d\u4e00\u4e2a\u4f7f\u7684\u5175\u5668\u6700 \u602a\uff0c\u7adf\u662f\u4e00\u9762\u6e14\u7f51\uff0c\u6b66\u529f\u4e5f\u6570\u4ed6\u6700\u597d\uff0c\u7239\u7239\u8d39\u4e86\u534a\u5929\u4e8b\uff0c\u624d\u5c06\u4ed6\u6253\u53d1\u56de\u53bb\uff1b\u5176\u4f59\u7684\u4eba\u6b66\u529f\u5374 \u90fd\u5e73\u5e38\u5f97\u5f88\u3002\u201d \u3000\u3000\u4f0a\u98ce\u51b7\u51b7\u4e00\u54fc\uff0c\u9053\uff1a \u3000\u3000\u201c\u6559\u4e3b\u592b\u4eba\uff01\u4f60\u4eec\u6559\u4e3b\u5df2\u7ecf\u6d3e\u4eba\u6765\u63a5\u4f60\u4e86\uff0c\u4f60\u8fd8\u4e0d\u5feb\u70b9\u6eda\u56de\u53bb\uff0c\u4e0d\u8fc7\u2026\u2026\u4f60\u8981\u662f\u4e0d\u628a\u5b69 \u5b50\u7559\u4e0b\u6765\uff0c\u4f60\u5c31\u4f11\u60f3\u2026\u2026\u201d \u3000\u3000\u90a3\u77e5\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u859b\u82e5\u74a7\u7a81\u4ece\u5e8a\u4e0a\u7a9c\u8d77\u6765\uff0c\u626c\u624b\u4e00\u7247\u91d1\u5149\uff0c\u88ad\u5411\u4f0a\u98ce\uff0c\u81ea\u5df1\u4e00\u624b\u62b1\u7740\u5b69 \u5b50\uff0c\u5374\u85c9\u7740\u5999\u624b\u8bb8\u767d\u548c\u4e07\u8679\u7684\u8eab\u5f62\u63a9\u62a4\uff0c\u55d6\u5730\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u65e2\u60ca\u4e14\u6012\uff0c\u888d\u8896\u4e00\u5c55\uff0c\u5c06\u859b\u82e5\u74a7\u626c\u624b\u53d1\u51fa\u7684\u201c\u7f57\u5239\u91d1\u4e1d\u201d\uff0c\u547c\u5730\u6325\u4e86\u5f00\u53bb\uff0c\u4f46\u81ea\u5df1 \u5374\u4e5f\u4e0d\u7981\u60ca\u5f97\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u539f\u6765\u8fd9\u201c\u7f57\u5239\u91d1\u9488\u201d\uff0c\u6b63\u662f\u859b\u82e5\u74a7\u4e91\u82f1\u672a\u5ac1\uff0c\u884c\u8d70\u6c5f\u6e56\u65f6\uff0c\u4ed7\u4ee5\u6210\u540d\u7684\u6697\u5668\u3002\u8fd9\u6697\u9488\u7ec6 \u82e5\u725b\u6bdb\uff0c\u5374\u662f\u6839\u6839\u6709\u6bd2\uff0c\u53ea\u8981\u4e2d\u4e0a\u4e00\u9488\uff0c\u808c\u80a4\u4fbf\u7acb\u65f6\u6e83\u6f9c\uff0c\u7eb5\u4e0d\u4f24\u547d\uff0c\u5374\u4e5f\u5dee\u4e0d\u591a\u4e86\uff0c\u7aef\u7684 \u9738\u9053\u5df2\u6781\uff01 \u3000\u3000\u4f0a\u98ce\u548c\u859b\u82e5\u74a7\u592b\u59bb\u6570\u5e74\uff0c\u5f53\u7136\u6df1\u77e5\u8fd9\u79cd\u6697\u5668\u7684\u5389\u5bb3\uff0c\u888d\u8896\u5916\u6325\uff0c\u8eab\u5f62\u540e\u9000\uff0c\u6321\u8fc7\u8fd9\u9635\u9488 \u96e8\u3002\u5374\u89c1\u859b\u82e5\u74a7\u5df2\u62b1\u7740\u5b69\u5b50\uff0c\u63a0\u51fa\u6d1e\u53bb\u3002 \u3000\u3000\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u63a0\u5230\u6d1e\u53e3\uff0c\u90a3\u77e5\u5916\u9762\u53c8\u662f\u4e00\u84ec\u9488\u96e8\uff0c\u6251\u9762\u5411\u4ed6\u88ad\u6765\u3002\u4ed6\u65b9\u6b32\u540e \u9000\uff0c\u8eab\u4fa7\u7a81\u5730\u4e00\u9635\u52b2\u98ce\u63a0\u8fc7\uff0c\u90a3\u84ec\u9488\u96e8\uff0c\u7adf\u90fd\u88ab\u6325\u843d\u4e00\u8fb9\uff0c\u8033\u7554\u542c\u5f97\u5999\u624b\u8bb8\u767d\u7684\u58f0\u97f3\u9053\uff1a \u3000\u3000\u201c\u8ffd\u51fa\u53bb\uff01\u201d \u3000\u3000\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u5999\u624b\u8bb8\u767d\u7684\u8eab\u5f62\uff0c\u5df2\u5982\u8f7b\u70df\u822c\u9038\u51fa\u3002 \u3000\u3000\u4f0a\u98ce\u518d\u4e0d\u8fdf\u7591\uff0c\u8ddf\u7740\u63a0\u5165\u96a7\u9053\uff0c\u53ea\u89c1\u524d\u9762\u6697\u5f71\u4e4b\u4e2d\uff0c\u5999\u624b\u8bb8\u767d\u7684\u8863\u8882\u98d8\u98d8\uff0c\u5df2\u7ecf\u63a0\u51fa\u6570 \u4e08\u5f00\u5916\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u5fc3\u4e2d\u6697\u53f9\uff1a \u3000\u3000\u201c\u8fd9\u5999\u624b\u8bb8\u767d\uff0c\u4eba\u79f0\u201c\u5343\u91cc\u8ffd\u98ce\uff0c\u795e\u884c\u65e0\u5f71\u201d\uff0c\u5982\u4eca\u4e00\u89c1\uff0c\u8f7b\u529f\u679c\u662f\u5999\u7edd\u4eba\u5bf0\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4e0d\u77e5\u9053\u90a3\u5251\u5148\u751f\u548c\u4e09\u5fc3\u795e\u541b\u7684\u8f7b\u529f\uff0c\u53ef\u6709\u4ed6\u5982\u6b64\u9ad8\u5999\u2026\u2026\u201d \u3000\u3000\u4ed6\u601d\u6f6e\u8f6c\u53ca\u6b64\u5904\uff0c\u4e0d\u7981\u53c8\u60f3\u8d77\u90a3\u4e09\u6e58\u5927\u4fa0\u7684\u672a\u4ea1\u4eba\u6bcd\u5973\u4e24\u4eba\uff0c\u5374\u4e0d\u77e5\u5979\u4eec\u73b0\u5728\u5230\u90a3\u91cc\u53bb \u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u8f6c\u5ff5\u95f4\uff0c\u5374\u542c\u5f97\u90a3\u4e07\u8679\u5728\u540e\u9762\u53eb\u7740\uff1a \u3000\u3000\u201c\u7b49\u6211\u4e00\u7b49\u3002\u201d \u3000\u3000\u56de\u5934\u671b\u5904\uff0c\u5374\u89c1\u8fd9\u5c11\u5973\u8f7b\u529f\u4ea6\u81ea\u4e0d\u51e1\uff0c\u6b64\u523b\u4e5f\u5df2\u8ffd\u4e86\u4e0a\u6765\uff0c\u800c\u81ea\u5df1\u5fc3\u4e2d\u8fd9\u4e00\u751f\u5c94\u5ff5\uff0c\u811a \u4e0b\u5fae\u6162\uff0c\u5374\u5c06\u524d\u9762\u7684\u4eba\u7ed9\u8ffd\u4e22\u4e86\u3002 \u3000\u3000\u4ef3\u811a\u4e0b\u52a0\u628a\u52b2\uff0c\u8eab\u5f62\u7075\u6d3b\u800c\u66fc\u5999\u5730\uff0c\u5728\u8fd9\u72ed\u7a84\u7684\u96a7\u9053\u91cc\u98de\u63a0\u7740\uff0c\u55d6\u55d6\u55d6\u4e09\u4e24\u4e2a\u8d77\u843d\uff0c\u4ed6 \u5df2\u5230\u51fa\u53e3\u4e4b\u5904\u3002\u5374\u89c1\u6d1e\u53e3\u7adf\u7136\u88ab\u5927\u77f3\u5835\u6b7b\u4e86\u3002\u5999\u624b\u8bb8\u767d\uff0c\u4e00\u624b\u62ff\u7740\u4e2a\u706b\u647a\u5b50\uff0c\u6b63\u4e00\u624b\u5728\u63a8\u90a3 \u5de8\u77f3\u3002 \u3000\u3000\u859b\u82e5\u74a7\u624b\u4e2d\u62b1\u7740\u5b69\u5b50\uff0c\u754f\u7f29\u5730\u7ad9\u5728\u89d2\u843d\u91cc\uff0c\u90a3\u5b69\u5b50\u8fde\u65e5\u559d\u9152\uff0c\u6b64\u523b\u7adf\u50cf\u8fd8\u662f\u5bbf\u9189\u672a\u9192\uff0c \u6eda\u52a8\u7740\u5927\u773c\u775b\uff0c\u671b\u6765\u671b\u53bb\uff0c\u7adf\u672a\u54ed\u51fa\u6765\uff0c\u4f46\u4e00\u5f20\u80a5\u80d6\u7684\u8138\uff0c\u5374\u5df2\u7626\u524a\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u9635\u601c\u60dc\uff0c\u4e00\u773c\u671b\u5230\u859b\u82e5\u74a7\uff0c\u53ea\u89c1\u8fd9\u6b66\u6797\u4e2d\u7684\u7b2c\u4e00\u7f8e\u4eba\uff0c\u6b64\u523b\u4e5f\u6194\u60b4\u4e0d\u5c11\uff0c\u660e \u63ae\u7684\u79cb\u6ce2\u4e2d\uff0c\u4e0d\u65f6\u9732\u51fa\u6050\u60e7\u7126\u6025\u7684\u795e\u8272\u6765\uff0c\u4ed6\u4e0d\u7981\u53c8\u4e3a\u4e4b\u6697\u4e2d\u957f\u53f9\u3002 \u3000\u3000\u201c\u4f46\u8fd9\u662f\u5979\u548e\u7531\u81ea\u53d6\uff0c\u53c8\u602a\u5f97\u8c01\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u7acb\u523b\u538b\u4e0b\u8fd9\u4efd\u601c\u60af\u7684\u611f\u89c9\uff0c\u5982\u6b64\u544a\u8bc9\u81ea\u5df1\u3002 \u3000\u3000\u5927\u6b65\u8d70\u8fc7\u53bb\uff0c\u8d70\u5230\u5999\u624b\u8bb8\u767d\u8eab\u4fa7\uff0c\u6c89\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u8bb8\u8001\u524d\u8f88\uff01\u8fd9\u53c8\u662f\u8c01\u5e72\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u51b7\u54fc\u4e00\u58f0\uff0c\u76ee\u5149\u56db\u8f6c\uff0c\u5c06\u624b\u4e2d\u7684\u706b\u647a\u5b50\uff0c\u4ea4\u7ed9\u5df2\u7ecf\u968f\u540e\u8d76\u6765\u7684\u4e07\u8679\uff0c\u4e00\u9762\u9053\uff1a \u3000\u3000\u201c\u9664\u4e86\u90a3\u8001\u7334\u5b50\u8fd8\u6709\u8c01\uff0c\u6c14\u8d77\u6765\u6211\u771f\u60f3\u5927\u5bb6\u5c31\u5168\u5728\u8fd9\u91cc\u9762\u8017\u7740\uff0c\u770b\u770b\u6700\u5148\u997f\u6b7b\u7684\u662f \u8c01\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u770b\u4e86\u4e07\u8679\u4e00\u773c\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u7239\u4e0d\u77e5\u9053\u4f60\u8fdb\u6765\u5427\uff1f\u201d \u3000\u3000\u4e07\u8679\u6447\u4e86\u6447\u5934\uff0c\u5999\u624b\u8bb8\u767d\u5df2\u559d\u9053\uff1a \u3000\u3000\u201c\u6765\uff01\u5c0f\u5a03\u5a03\uff01\u5e2e\u6211\u628a\u8fd9\u5757\u77f3\u5757\u5f04\u5f00\u3002\u54fc\uff01\u4e07\u5929\u840d\u5440\u4e07\u5929\u840d\uff01\u4f60\u4e5f\u672a\u514d\u592a\u5c0f\u89d1\u4e8e\u6211\u4e86\uff0c \u96be\u9053\u8fd9\u533a\u533a\u4e00\u5757\u77f3\u7855\uff0c\u5c31\u80fd\u628a\u6211\u56f0\u6b7b\u3002\u201d \u3000\u3000\u7a81\u5730\u5750\u9a6c\u6c89\u8170\uff0c\u75be\u63a8\u53cc\u638c\uff0c\u5999\u624b\u8bb8\u767d\u53e3\u4e2d\u95f7\u201c\u5543\u201d\u4e00\u58f0\uff0c\u6ee1\u53d1\u771f\u529b\uff0c\u671d\u8fd9\u5757\u5927\u77f3\u63a8\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u53cc\u81c2\uff0c\u65e9\u4ea6\u6ee1\u84c4\u771f\u6c14\uff0c\u6b64\u523b\u518d\u4e0d\u8fdf\u7591\uff0c\u4ea6\u81ea\u4e3e\u638c\u63a8\u51fa\u3002\u8fd9\u4e24\u4eba\u662f\u4f55\u7b49\u529f\u529b\uff0c\u8f6c\u773c\u4e4b \u95f4\uff0c\u5927\u77f3\u5c31\u88ab\u63a8\u5f00\u4e00\u7ebf\u3002 \u5916\u9762\u5374\u4f20\u5165\u4e00\u9635\u9635\u53f1\u5412\u7684\u58f0\u97f3\u6765\uff0c\u50cf\u662f\u6709\u4eba\u6253\u6597\u6b63\u5267\u7684\u6837\u5b50\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u4e00\u7ae0\u3000\u5251\u6c14\u51b2\u5bb5>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u4e00\u7ae0\u3000\u5251\u6c14\u51b2\u5bb5 \u3000\u3000\u4f0a\u98ce\u548c\u8bb8\u767d\u4e92\u89c6\u4e00\u773c\uff0c\u53e3\u4e2d\u540c\u65f6\u66b4\u559d\u4e00\u58f0\uff0c\u56db\u638c\u75be\u63a8\uff0c\u53ea\u542c\u8f70\u7136\u4e00\u58f0\uff0c\u90a3\u5757\u91cd\u903e\u5343\u65a4\u7684 \u5de8\u77f3\uff0c\u7adf\u88ab\u4ed6\u4e24\u4eba\u63a8\u5f97\u76f4\u6eda\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u987b\u77e5\u8fd9\u5999\u624b\u8bb8\u767d\uff0c\u6614\u5e74\u540d\u9707\u5929\u4e0b\uff0c\u867d\u4ee5\u8f7b\u529f\u89c1\u957f\uff0c\u4f46\u771f\u529b\u4e4b\u5f3a\uff0c\u5f53\u4e16\u4e4b\u4e2d\uff0c\u5374\u4e5f\u5f88\u5c11\u6709 \u4eba\u80fd\u4e0e\u4e4b\u9889\u9883\u7684\u3002 \u3000\u3000\u4f0a\u98ce\u5e74\u7eaa\u867d\u8f7b\uff0c\u5374\u672c\u5df2\u53ef\u7b97\u6b66\u6797\u9ad8\u624b\uff0c\u81ea\u4ece\u201c\u7763\u201d\u201c\u4efb\u201d\u4e24\u8109\uff0c\u88ab\u6b66\u6797\u7edd\u4ee3\u5947\u4eba\u5251\u5148\u751f \u6253\u901a\u540e\uff0c\u4ed6\u771f\u529b\u4e4b\u731b\u8fdb\uff0c\u4f55\u6b62\u500d\u84f0\uff0c\u4f55\u51b5\u8fd9\u5341\u51e0\u5929\u91cc\uff0c\u4ed6\u53c8\u4e60\u5f97\u201c\u5929\u661f\u79d8\u7b08\u201d\u91cc\u7684\u65e0\u4e0a\u5fc3 \u6cd5\u3002 \u3000\u3000\u662f\u4ee5\u8fd9\u4e24\u4eba\u9f50\u4e00\u7528\u529b\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u81ea\u53ef\u60f3\u89c1\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u53ea\u6015\u63a8\u4e0a\u5341\u5e74\uff0c\u4e5f\u672a\u5fc5\u80fd\u5c06\u8fd9\u5757\u5de8\u77f3\u79fb\u52a8\u5206\u6beb\u3002 \u3000\u3000\u5de8\u77f3\u4e00\u5f00\uff0c\u5929\u5149\u7acb\u523b\u5c04\u5165\uff0c\u5999\u624b\u8bb8\u767d\u56de\u987e\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u5a03\u5a03\u529f\u592b\u5012\u4e0d\u9519\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u8001\u524d\u8f88\u5938\u5956\u2026\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u843d\uff0c\u5374\u89c1\u90a3\u859b\u82e5\u74a7\u5df2\u63a0\u4e86\u51fa\u53bb\uff0c\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u811a\u5c16\u987f\u70b9\uff0c\u55d6\u5730\uff0c\u50cf\u7bad\u4e5f\u4f3c\u5730\u7a9c\u4e86 \u51fa\u53bb\u3002\u5374\u89c1\u859b\u82e5\u74a7\u5df2\u5c06\u7ed5\u8fc7\u5de8\u77f3\uff0c\u4ed6\u7acb\u523b\u5927\u62e7\u8eab\uff0c\u53cc\u81c2\u5206\u98de\uff0c\u5982\u5f71\u968f\u5f62\u5730\u6251\u4e86\u8fc7\u53bb\uff0c\u4e00\u628a\u6293 \u4f4f\u5979\u7684\u81c2\u8180\uff0c\u5389\u559d\u9053\uff1a \u3000\u3000\u201c\u628a\u5b69\u5b50\u7ed9\u6211\uff01\u201d \u3000\u3000\u90a3\u77e5\u773c\u524d\u7a81\u5730\u5149\u534e\u9519\u843d\uff0c\u56db\u53e3\u5e26\u7740\u5bd2\u5149\u7684\u957f\u5251\uff0c\u5251\u5c16\u6b63\u5bf9\u7740\u81ea\u5df2\uff0c\u4ecd\u5728\u4e0d\u4f4f\u98a4\u6296\u7740\uff0c\u4e00 \u4e2a\u68ee\u51b7\u7684\u53e3\u97f3\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\uff01\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u9a87\u7136\u56db\u987e\uff0c\u53ea\u89c1\u8fd9\u7247\u5c71\u5730\u4e0a\uff0c\u7adf\u7ad9\u7740\u5341\u4f59\u4e2a\u624b\u6301\u957f\u5251\u7684\u6c49\u5b50\uff0c\u800c\u6b64\u523b\u8fd9\u51b7\u7136\u5411\u81ea\u5df1\u53d1 \u8bdd\u7684\uff0c\u7adf\u5c31\u662f\u90a3\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u5728\u7ec8\u5357\u5c71\u4e0a\uff0c\u66fe\u89c1\u8fc7\u8fd9\u8c22\u96e8\u4ed9\u4e00\u9762\uff0c\u53ea\u662f\u90a3\u65f6\u4ed6\u5df2\u7ecf\u6613\u5bb9\uff0c\u662f\u4ee5\u4ed6\u8ba4\u5f97\u8c22\u96e8\u4ed9\uff0c \u8c22\u96e8\u4ed9\u5374\u4e0d\u8ba4\u5f97\u4ed6\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5fae\u6014\u4e4b\u95f4\uff0c\u90a3\u8c22\u96e8\u4ed9\u5df2\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u8fd8\u4e0d\u653e\u624b\uff1f\u201d \u3000\u3000\u5237\u5730\u4e00\u5251\uff0c\u5411\u4ed6\u5241\u6765\u3002\u53e6\u5916\u4e09\u4eba\uff0c\u6b63\u662f\u6b66\u6797\u4e00\u6d41\u5251\u624b\uff0c\u4e5f\u66fe\u968f\u4ed6\u540c\u4e0a\u7ec8\u5357\u5c71\u7684\u5c71\u4e1c\u5927\u8c6a \u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u6b64\u523b\u5404\u81ea\u5212\u52a8\u5251\u8eab\uff0c\u540c\u65f6\u5411\u4f0a\u98ce\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u56db\u9053\u5bd2\u5149\uff0c\u4ea4\u526a\u800c\u81f3\uff0c\u4f0a\u98ce\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u6492\u624b\uff0c\u62e7\u8eab\uff0c\u9519\u6b65\uff0c\u8eab\u5f62\u500f\u7136\u6ed1 \u5f00\u4e94\u5c3a\u3002\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u5999\u624b\u8bb8\u767d\u6b63\u548c\u90a3\u94c1\u9762\u5b64\u884c\u5ba2\u76f8\u5bf9\u800c\u7acb\uff0c\u4e07\u8679\u7d27\u7d27\u7ad9\u5728\u5979\u7236\u4eb2\u8eab\u4fa7\uff0c \u53e6\u5916\u51e0\u4e2a\u52b2\u88c5\u6c49\u5b50\uff0c\u5404\u6301\u957f\u5251\uff0c\u56f4\u5728\u4e00\u5468\uff0c\u76ee\u5149\u4ff1\u90fd\u51b7\u51b7\u5730\u77aa\u5728\u4e07\u5929\u840d\u8eab\u4e0a\uff0c\u663e\u7136\u65b9\u624d\u66fe\u7ecf \u5de8\u6597\u3002 \u3000\u3000\u4f0a\u98ce\u8fd9\u4e00\u5c55\u52a8\u8eab\u5f62\uff0c\u90a3\u79cd\u60ca\u4eba\u7684\u901f\u5ea6\uff0c\u4ed6\u81ea\u5df1\u867d\u672a\u611f\u89c9\u5230\uff0c\u5374\u4f7f\u5f97\u591a\u624b\u771f\u4eba\u6697\u5403\u4e00\u60ca\uff0c \u5251\u5c16\u659c\u6311\uff0c\u6536\u4f4f\u5251\u5f0f\uff0c\u7ffb\u8eab\u5411\u859b\u82e5\u74a7\u8eac\u8eab\u65bd\u4e86\u4e00\u793c\uff0c\u548c\u52b3\u5c71\u4e09\u5251\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u5374 \u7a9c\u5230\u7ad9\u5728\u5c71\u5730\u8fb9\u4e00\u682a\u67af\u6811\u4e0b\u7684\u4e24\u4e2a\u957f\u886b\u6c49\u5b50\u8eab\u4fa7\uff0c\u4f4e\u4f4e\u8bf4\u4e86\u4e24\u53e5\u8bdd\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u76ee\u5149\u968f\u7740\u4ed6\u7684\u8eab\u5f62\u671b\u53bb\uff0c\u5374\u89c1\u90a3\u8fb9\u6811\u4e0b\uff0c\u7ad9\u7740\u7684\u4e24\u4eba\uff0c\u5176\u4e2d\u4e00\u4e2a\u9762\u4e0a\u5fae \u5fae\u542b\u7740\u51b7\u7b11\uff0c\u4e24\u76ee\u4e0a\u7ffb\uff0c\u4e00\u624b\u637b\u7740\u8170\u95f4\u7684\u4e1d\u5e26\uff0c\u4e0d\u4f4f\u628a\u73a9\u7740\uff0c\u5374\u7adf\u662f\u90a3\u6765\u81ea\u9752\u6d77\u7684\u72c2\u50b2\u5c11\u5e74 \u94b1\u7fca\u3002\u53e6\u4e00\u4e2a\u867d\u4e0d\u8ba4\u5f97\uff0c\u4f46\u4e00\u773c\u671b\u53bb\uff0c\u6c14\u5ea6\u4ea6\u81ea\u4e0d\u51e1\uff0c\u663e\u89c1\u7edd\u975e\u5eb8\u624b\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u8f6c\u52a8\u5904\uff0c\u5c31\u7acb\u523b\u5224\u65ad\u51fa\u6b64\u523b\u7684\u60c5\u52bf\uff0c\u4e8e\u81ea\u5df1\u5927\u5927\u4e0d\u5229\uff0c\u81ea\u5df1\u4eca\u65e5\u82e5\u60f3\u4ece\u859b\u82e5\u74a7\u624b \u4e0a\u593a\u56de\u81ea\u5df1\u7684\u5b69\u5b50\uff0c\u4e5f\u7edd\u975e\u6613\u4e8b\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8d70\u5230\u5999\u624b\u8bb8\u767d\u8eab\u4fa7\uff0c\u53ea\u89c1\u8fd9\u201c\u5357\u5077\u5317\u76d7\u201d\u4e24\u4eba\uff0c\u6b64\u523b\u56db\u53ea\u773c\u775b\uff0c\u5404\u5404\u76f8\u5bf9\u51dd\u89c6\uff0c \u4e07\u5929\u840d\u7a81\u5730\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u59d3\u8bb8\u7684\uff01\u60f3\u4e0d\u5230\u4f60\u8fd8\u6ca1\u6b7b\u3002\u8fd9\u91cc\u4e5f\u4e0d\u662f\u4f60\u6211\u8c08\u8bdd\u4e4b\u5904\uff0c\u4f60\u82e5\u6709\u5174\uff0c\u6211\u4eec\u4e0d\u59a8\u53e6\u5916\u627e\u4e2a \u5730\u65b9\u8c08\u8c08\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u4ef0\u5929\u4e00\u7b11\uff0c\u5927\u58f0\u9053\uff1a \u3000\u3000\u201c\u518d\u597d\u6ca1\u6709\uff0c\u8fd9\u91cc\u4f60\u6bd4\u6211\u719f\uff0c\u4f60\u5c31\u5728\u524d\u9762\u5f15\u8def\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u8d70\uff0c\u4f0a\u98ce\u66f4\u662f\u52bf\u5b64\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u6025\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a \u3000\u3000\u201c\u8bb8\u8001\u524d\u8f88\u2026\u2026\u2026\u2026\u201d \u3000\u3000\u4e07\u5929\u840d\u51b7\u54fc\u4e00\u58f0\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u7684\u5e10\u8fd8\u6ca1\u6709\u7b97\u5b8c\uff0c\u4f60\u73b0\u5728\u5c11\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u8eab\u5f62\u4e00\u8f6c\uff0c\u6b63\u5f85\u5927\u6b65\u79bb\u53bb\uff0c\u90a3\u77e5\u8eab\u540e\u7a81\u53c8\u54cd\u8d77\u4e00\u9635\u957f\u7b11\uff0c\u4f0a\u98ce\u95ed\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u94b1\u7fca\u4e00 \u624b\u637b\u7740\u4e1d\u5e26\uff0c\u957f\u7b11\u7740\u7f13\u6b65\u800c\u6765\uff0c\u8d70\u5230\u4e07\u5929\u840d\u548c\u4f0a\u98ce\u4e2d\u95f4\uff0c\u5353\u7136\u4e00\u7ad9\uff0c\u4e24\u773c\u671d\u5929\u4e0a\u7ffb\u4e86\u7ffb\uff0c\u7b11 \u58f0\u987f\u4f4f\uff0c\u51b7\u7136\u9053\uff1a \u3000\u3000\u201c\u5404\u4f4d\u5148\u8bf7\u6682\u7559\u8d35\u6b65\uff0c\u5c0f\u9119\u8fd8\u6709\u8bdd\u8bf7\u6559\u5404\u4f4d\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u6d53\u7709\u4e00\u8f69\uff0c\u76ee\u5149\u7535\u5f20\u3002\u90a3\u4e07\u5929\u840d\u9762\u4e0a\u5374\u4ecd\u7136\u5168\u65e0\u8868\u60c5\uff0c\u63a5\u53e3\u95ee\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u94b1\u7fca\u51b7\u7136\u4e00\u7b11\uff0c\u5c06\u53f3\u624b\u7684\u4e1d\u5e26\uff0c\u6362\u5230\u5de6\u624b\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u65b9\u624d\u4ee5\u4e00\u53cc\u94c1\u638c\uff0c\u529b\u654c\u516d\u5251\uff0c\u6b66\u529f\u53ef\u7b97\u4e0d\u5f31\uff0c\u60f3\u5fc5\u662f\u4f4d\u6b66\u6797\u524d\u8f88\u3002\u53ea\u662f\u5728\u4e0b\u5374\u8981 \u8bf7\u6559\uff0c\u8fd9\u5c71\u7a9f\u65e2\u975e\u9601\u4e0b\u6240\u5efa\uff0c\u4ea6\u975e\u9601\u4e0b\u6240\u4e70\uff0c\u9601\u4e0b\u5374\u4e3a\u4f55\u8981\u4e09\u756a\u56db\u6b21\u5730\u963b\u62e6\u6211\u4eec\u5f1f\u5144\u8fdb\u53bb\uff0c \u96be\u9053\u9601\u4e0b\u5728\u91cc\u9762\u505a\u4e86\u4ec0\u4e48\u89c1\u4e0d\u5f97\u4eba\u7684\u52fe\u5f53\uff0c\u4e0d\u60f3\u8ba9\u522b\u4eba\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5730\u6ed4\u6ed4\u800c\u8a00\uff0c\u8bf4\u5b8c\u4e86\u5c31\u8f6c\u8fc7\u8eab\u5b50\uff0c\u6839\u672c\u4e0d\u7b49\u4e07\u5929\u840d\u56de\u7b54\uff0c\u51b7\u7136\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fd8\u6709\u2026\u2026\u8fd9\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u7528\u624b\u4e0a\u7684\u4e1d\u5e26\uff0c\u6307\u4e86\u6307\u4f0a\u98ce\uff1a \u3000\u3000\u201c\u9601\u4e0b\u4e00\u8868\u5802\u5802\uff0c\u6b66\u529f\u4ea6\u662f\u5353\u5353\u4e0d\u51e1\uff0c\u53ea\u662f\u5728\u4e0b\u4e5f\u6709\u8bf7\u6559\uff0c\u9601\u4e0b\u4e0e\u6211\u90a3\u8427\u5927\u5ac2\uff0c\u662f\u5426\u6cbe \u4eb2\uff1f\u6709\u65e0\u5e26\u6545\u2026\u2026\u201d \u3000\u3000\u4ed6\u9762\u8272\u7a81\u5730\u4e00\u6c89\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5982\u679c\u975e\u4eb2\u975e\u6545\uff0c\u9601\u4e0b\u96be\u9053\u4e0d\u77e5\u201c\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\u201d\uff0c\u5374\u62c9\u4eba\u5bb6\u5973\u5b50\u81c2\u8180\u4f5c\u4ec0\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u6307\u4e86\u6307\u90a3\u5904\u6d1e\u7a9f\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u6d1e\u7a9f\u4e2d\uff0c\u5e7d\u6df1\u9edd\u9ed1\uff0c\u4f60\u4eec\u51e0\u4eba\uff0c\u5728\u91cc\u9762\u662f\u5728\u4f5c\u4e2a\u4ec0\u4e48\u52fe\u5f53\uff1f\u8fd8\u8981\u7528\u77f3\u5934\u5c06\u6d1e\u53e3\u5c01\u8d77 \u6765\uff0c\u5f04\u4e2a\u8001\u5934\u5b50\u5728\u5916\u9762\u770b\u95e8\u2026\u2026\u54c8\u2026\u2026\u201d \u3000\u3000\u4ed6\u72c2\u7b11\u4e00\u58f0\uff0c\u653e\u4e0b\u624b\u4e2d\u7684\u4e1d\u5e26\u3002 \u3000\u3000\u201c\u8fd9\u90fd\u6559\u5728\u4e0b\u5f04\u4e0d\u61a7\u4e86\u3002\u201d \u3000\u3000\u4e07\u5929\u840d\u9762\u5982\u9752\u94c1\uff0c\u5999\u624b\u8bb8\u767d\u987b\u53d1\u7686\u5f20\uff0c\u4e07\u8679\u5706\u7741\u674f\u773c\uff0c\u4f0a\u98ce\u5251\u7709\u6012\u8f69\uff0c\u8fd9\u56db\u4eba\u5404\u5404\u6012\u53f1 \u4e00\u58f0\uff0c\u65b9\u5f85\u7b54\u8bdd\u3002\u90a3\u77e5\u90a3\u859b\u82e5\u74a7\u7adf\u4e00\u9635\u98ce\u4f3c\u7684\u63a0\u4e86\u8fc7\u6765\uff0c\u660e\u7738\u4e4b\u5185\u6cea\u5149\u95ea\u52a8\uff0c\u7adf\u54fd\u54bd\u7740\u9053\uff1a \u201c\u4e8c\u5f1f\uff01\u4f60\u2026\u2026\u4f60\u600e\u5730\u73b0\u5728\u624d\u6765\uff0c\u4f60\u5e08\u54e5\u5462\uff1f\u4ed6\u53c8\u8dd1\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u94b1\u7fca\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u76ee\u4e2d\u795e\u5149\u95ea\u52a8\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u5e08\u5144\u4e0d\u5728\u8fd9\u91cc\uff0c\u6709\u5c0f\u5f1f\u5728\u8fd8\u4e0d\u662f\u4e00\u6837\uff1f\u6709\u4eba\u6b3a\u8d1f\u5ac2\u5b50\uff0c\u5c0f\u5f1f\u867d\u4e0d\u624d\uff0c\u4e5f\u8981\u5c3d\u529b\u548c\u4ed6\u5468 \u65cb\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\uff0c\u4e00\u634b\u866c\u987b\uff0c\u66b4\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u5c0f\u5b50\uff01\u8bf4\u8bdd\u53ef\u5f97\u653e\u6e05\u695a\u4e9b\uff01\u4f60\u82e5\u5728\u8001\u592b\u9762\u524d\u80e1\u8a00\u4e71\u8bed\u2026\u2026\u201d \u3000\u3000\u94b1\u7fca\u51b7\u7136\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u53c8\u5f53\u600e\u7684\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u72b9\u81ea\u672a\u843d\uff0c\u6f2b\u5929\u7684\u638c\u5f71\uff0c\u5df2\u5f53\u5934\u538b\u4e0b\uff0c\u62db\u5f0f\u5947\u8be1\u98d8\u5ffd\uff0c\u751f\u50cf\u662f\u81ea\u5df1\u7684\u524d\u540e\u5de6\u53f3\uff0c\u7adf \u540c\u65f6\u5728\u4eba\u5bb6\u7684\u638c\u98ce\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u53c8\u50cf\u662f\u6709\u597d\u51e0\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u540c\u65f6\u51fa\u638c\u88ad\u5411\u81ea\u5df1\u3002 \u3000\u3000\u859b\u82e5\u74a7\u6000\u4e2d\u7684\u5b69\u5b50\uff0c\u54c7\u5730\u54ed\u4e86\uff0c\u4f0a\u98ce\u5251\u7709\u8f69\u5904\uff0c\u7bad\u6b65\u7a9c\u4e86\u4e0a\u53bb\uff0c\u5de6\u624b\u5fae\u6325\uff0c\u4e00\u9886\u859b\u82e5\u74a7 \u7684\u773c\u795e\uff0c\u53f3\u624b\u75be\u51fa\u5982\u98ce\uff0c\u4ecd\u662f\u53bb\u62a2\u5b69\u5b50\u3002 \u3000\u3000\u9500\u9b42\u592b\u4eba\u5a07\u547c\u4e00\u58f0\uff0c\u811a\u4e0b\u98ce\u5feb\u5730\u9000\u51fa\u4e09\u6b65\uff0c\u4f0a\u98ce\u5389\u53f1\u6251\u4e0a\uff0c\u4f46\u662f\u773c\u524d\u5374\u53c8\u5251\u5149\u66b4\u957f\uff0c\u5237 \u5237\u51e0\u5251\uff0c\u5e26\u7740\u9752\u84dd\u7684\u5251\u5149\uff0c\u5241\u5411\u81ea\u5df1\uff0c\u6b63\u662f\u90a3\u201c\u52b3\u5c71\u4e09\u5251\u201d\u51fb\u51fa\u7684\u3002 \u3000\u3000\u90a3\u8fb9\u94b1\u7fca\u7a81\u5730\u957f\u5578\u4e00\u58f0\uff0c\u9880\u957f\u7684\u8eab\u5f62\uff0c\u5982\u98ce\u4e2d\u4e4b\u67f3\uff0c\u6447\u66f3\u8f6c\u6298\uff0c\u6beb\u65e0\u5b9a\u5411\uff0c\u4e00\u773c\u671b\u53bb\uff0c \u7adf\u50cf\u662f\u4ed6\u5df2\u7ad9\u4e0d\u4f4f\u8eab\u5b50\u4f3c\u7684\uff0c\u4f46\u5999\u624b\u8bb8\u767d\u7684\u6f2b\u5929\u638c\u5f71\uff0c\u5374\u5728\u4ed6\u8fd9\u79cd\u5947\u5999\u7684\u8eab\u5f62\u4e0b\uff0c\u5168\u90e8\u843d \u7a7a\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e24\u4eba\u5404\u5404\u5927\u5403\u4e00\u60ca\uff0c\u94b1\u7fca\u56fa\u7136\u60f3\u4e0d\u5230\uff0c\u81ea\u5df1\u7684\u5bf9\u624b\u662f\u5982\u6b64\u5389\u5bb3\u7684\u89d2\u8272\uff1b\u90a3\u5999\u624b\u8bb8 \u767d\u5374\u66f4\u60f3\u4e0d\u5230\uff0c\u81ea\u5df1\u76db\u6012\u4e4b\u4e0b\uff0c\u51fb\u51fa\u5728\u65e0\u91cf\u5c71\u5dc5\u82e6\u5fc3\u7814\u6210\u7684\u4e00\u638c\uff0c\u7adf\u88ab\u8fd9\u5e74\u9f84\u5e76\u4e0d\u751a\u5927\u7684\u5c11 \u5e74\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8eab\u6cd5\u7a0d\u7a0d\u4e00\u987f\uff0c\u5404\u53c8\u9519\u6b65\u8fdb\u51fb\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u53ea\u89c1\u638c\u98ce\u864e\u864e\uff0c\u5df2\u5206\u4e0d\u51fa\u8fd9\u4e24\u4eba\u7684\u8eab\u5f71 \u6765\u3002 \u3000\u3000\u4e07\u5929\u840d\u56db\u987e\u4e00\u773c\uff0c\u4f4e\u53f1\u9053\uff1a \u3000\u3000\u201c\u8679\u513f\uff01\u4f60\u5148\u56de\u53bb\u3002\u201d \u3000\u3000\u731b\u4e00\u53cd\u8eab\uff0c\u6251\u5411\u73af\u4f3a\u8eab\u540e\u7684\u51e0\u4e2a\u5251\u624b\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\uff0c\u5df2\u5bf9\u8fd9\u51e0\u4eba\u5404\u5404\u51fb\u51fa\u4e00\u638c\u3002 \u3000\u3000\u4e07\u8679\u79cb\u6ce2\u6d41\u8f6c\uff0c\u811a\u4e0b\u7f13\u7f13\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u7a81\u5730\u7a9c\u5230\u859b\u82e5\u74a7\u8eab\u4fa7\uff0c\u7ea4\u638c\u9f50\u5730\u6325\u51fa\uff0c\u5de6\u624b\u7389\u6307 \u5e76\u8d77\u5982\u5251\uff0c\u75be\u5730\u70b9\u5411\u859b\u82e5\u74a7\u53f3\u8170\u4e0b\u7684\u201c\u7b11\u8170\u7a74\u201d\uff0c\u53f3\u624b\u5212\u4e2a\u534a\u5708\uff0c\u5237\u5730\u4e00\u638c\uff0c\u5207\u5411\u5979\u7684\u5de6 \u80f8\u3002 \u3000\u3000\u859b\u82e5\u74a7\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5c11\u5973\u4f1a\u7a81\u7136\u5411\u81ea\u5df1\u51fa\u624b\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u5de6\u638c\u75be\u8d77\u8fce\u654c\uff0c\u90a3\u77e5\u6000\u4e2d\u4e00 \u677e\uff0c\u53f3\u624b\u62b1\u7740\u7684\u5b69\u5b50\uff0c\u5374\u88ab\u4e07\u8679\u4e00\u628a\u62a2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u53d8\u5316\uff0c\u51e0\u4e4e\u5728\u540c\u4e00\u5239\u90a3\u4e2d\u53d1\u751f\uff0c\u8fdc\u8fdc\u7ad9\u5728\u6811\u4e0b\u7684\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u548c\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2 \u7269\uff0c\u53ea\u542c\u5f97\u859b\u82e5\u74a7\u60ca\u547c\u4e00\u58f0\uff0c\u4e00\u6761\u7fe0\u7eff\u8272\u7684\u4eba\u5f71\uff0c\u7535\u4e5f\u4f3c\u5730\u63a0\u4e86\u5f00\u53bb\uff0c\u55d6\u55d6\u51e0\u4e2a\u8d77\u843d\uff0c\u5df2\u81ea \u6d88\u5931\u5728\u8def\u65c1\u7684\u6797\u6728\u4e2d\uff0c\u800c\u5df2\u8eab\u6000\u516d\u7532\u7684\u859b\u82e5\u74a7\uff0c\u4e5f\u5a07\u547c\u7740\u8ffd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u8c22\u5144\u96be\u9053\u4e5f\u53d7\u4e86\u4f24\u5417\uff1f\u201d \u3000\u3000\u4ed6\u80a9\u80db\u4e4b\u5904\uff0c\u65e5\u524d\u4e2d\u4e86\u4e07\u5929\u840d\u4e00\u638c\uff0c\u51e0\u4e4e\u9aa8\u5934\u90fd\u88ab\u6253\u788e\uff0c\u662f\u4ee5\u8fdc\u8fdc\u7ad9\u5f00\uff0c\u6b64\u523b\u51b7\u51b7\u4e00 \u95ee\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u81ea\u7136\u662f\u4f60\u5e76\u6ca1\u6709\u53d7\u4f24\uff0c\u5374\u4e3a\u4f55\u548c\u6211\u7ad9\u5728\u4e00\u9f50\uff0c\u4ee5\u81f4\u51fa\u4e86\u53d8\u6545\uff0c\u90fd\u6765\u4e0d\u53ca\u51fa \u624b\u3002 \u3000\u3000\u90a3\u591a\u624b\u771f\u4eba\u5c82\u6709\u542c\u4e0d\u51fa\u4ed6\u8bdd\u4e2d\u542b\u610f\u7684\u9053\u7406\u3002\u51b7\u7b11\u4e00\u58f0\uff0c\u4ea6\u81ea\u5c55\u52a8\u8eab\u5f62\u8ffd\u4e86\u8fc7\u53bb\uff0c\u97e6\u50b2\u7269 \u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5583\u5583\u4f4e\u8bed\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u88ab\u4eba\u62a2\u8d70\u4e5f\u597d\uff0c\u7559\u4e0b\u8fd9\u5a03\uff0c\u8fdf\u65e9\u603b\u662f\u4e2a\u7978\u80ce\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u548c\u8427\u65e0\u6700\u662f\u63a5\u8fd1\uff0c\u662f\u4ee5\u8fd9\u4e9b\u79c1\u4e8b\uff0c\u4ed6\u5168\u77e5\u9053\u3002\u4fef\u9996\u6c89\u541f\u4e86\u534a\u664c\uff0c \u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u773c\u524d\u4eba\u5f71\u7ffb\u98de\uff0c\u5251\u5149\u9519\u843d\uff0c\u6253\u5f97\u81ea\u6fc0\u70c8\u65e0\u6bd4\uff0c\u5e76\u672a\u56e0\u8fd9\u4e00\u7a81\u6765\u7684\u53d8\u6545\u4f4f\u624b\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4f38\u51fa\u5de6\u624b\uff0c\u5fae\u5fae\u629a\u4e86\u629a\u53f3\u80a9\u7684\u4f24\u5904\uff0c\u7f13\u6b65\u8d70\u5411\u6fc0\u6597\u7740\u7684\u4eba\u7fa4\uff0c\u50cf\u662f\u8981\u5c06\u4ed6\u4eec\u7684\u52a8 \u624b\u60c5\u5f62\uff0c\u770b\u5f97\u66f4\u6e05\u695a\u4e9b\u3002 \u3000\u3000\u6b64\u523b\u5c71\u98ce\u5f3a\u52b2\uff0c\u65e5\u5df2\u897f\u9690\uff0c\u5929\u8272\u8d8a\u6765\u8d8a\u9eef\u3002\u8fd9\u897f\u6881\u5c71\u7554\uff0c\u51b2\u9704\u7684\u5251\u6c14\uff0c\u5149\u534e\u5374\u8d8a\u6765\u8d8a \u76db\uff0c\u7ed9\u8fd9\u6599\u5ced\u7684\u6625\u65e5\u8584\u66ae\uff0c\u66f4\u52a0\u6d53\u4e86\u51e0\u5206\u5bd2\u610f\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u4e8c\u7ae0\u3000\u5404\u901e\u8eab\u624b>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u4e8c\u7ae0\u3000\u5404\u901e\u8eab\u624b \u3000\u3000\u8fd9\u6f2b\u5929\u7684\u5251\u6c14\u638c\u5f71\uff0c\u8fdc\u770b\u867d\u662f\u4e00\u56e2\uff0c\u751a\u81f3\u8fde\u4eba\u5f71\u90fd\u5206\u8fa8\u4e0d\u51fa\uff1b\u4ed4\u7ec6\u4e00\u770b\uff0c\u5374\u662f\u5206\u505a\u4e09 \u5904\u3002 \u3000\u3000\u94c1\u9762\u5b64\u884c\u5ba2\u4ee5\u4e00\u654c\u4e94\uff0c\u53ea\u89c1\u8fd9\u7eb5\u6a2a\u6cb3\u6714\u7684\u5de8\u76d7\uff0c\u8eab\u624b\u679c\u81ea\u60ca\u4eba\uff0c\u638c\u98ce\u864e\u864e\uff0c\u88c2\u77f3\u5f00\u5c71\uff0c \u5728\u4e94\u67c4\u7cbe\u94a2\u957f\u5251\u7ec7\u6210\u7684\u5251\u7f51\u4e2d\uff0c\u81ea\u653b\u591a\u5b88\u5c11\u3002\u53ea\u662f\u548c\u4ed6\u5bf9\u654c\u7684\uff0c\u5374\u4e5f\u4e0d\u662f\u5eb8\u624b\uff0c\u800c\u4e14\u6700\u5389\u5bb3 \u7684\u662f\uff1b\u8fd9\u4e94\u4eba\u5251\u6cd5\u914d\u5408\u4e4b\u4e25\u5bc6\uff0c\u751f\u50cf\u662f\u6d51\u5982\u4e00\u4f53\u3002 \u3000\u3000\u4e07\u5929\u840d\u6697\u6697\u76b1\u7709\uff0c\u4ed6\u6599\u4e0d\u5230\u8fd9\u51e0\u4e2a\u5929\u4e89\u6559\u4f17\uff0c\u7adf\u6709\u5982\u6b64\u8eab\u624b\uff01 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u8fd9\u51e0\u4e2a\u4eba\u5728\u6c5f\u6e56\u4e0a\u4ea6\u662f\u5927\u6709\u76db\u540d\uff0c\u6b64\u523b\u4ed6\u4eec\u5fc3\u91cc\u7684\u6025\u8e81\uff0c\u66f4\u5728\u4ed6\u4e4b\u4e0a\u54e9\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e94\u4eba\uff0c\u5176\u4e2d\u4e09\u4e2a\u8eab\u4f53\u8f83\u77ee\uff0c\u8170\u95f4\u5404\u81ea\u4f69\u7740\u4e00\u4e2a\u9769\u56ca\uff0c\u5251\u6cd5\u4ee5\u96c4\u6d51\u89c1\u957f\u7684\uff0c\u7adf\u662f\u6b66 \u6797\u4e2d\u58f0\u540d\u8d6b\u8d6b\u7684\u5251\u624b\u201c\u71d5\u5c71\u4e09\u5251\u201d\u3002 \u3000\u3000\u8fd9\u5144\u5f1f\u4e09\u4eba\u7eb5\u6a2a\u6c5f\u5357\uff0c\u884c\u8d70\u65f6\u4ece\u4e0d\u843d\u5355\uff0c\u52a8\u624b\u65f6\u4ea6\u662f\u4e09\u5251\u9f50\u4e0a\uff0c\u51fa\u9053\u6c5f\u6e56\u4ee5\u6765\uff0c\u4ece\u672a\u78b0 \u8fc7\u4ec0\u4e48\u9489\u5b50\u3002\u6b64\u523b\u4ee5\u4e09\u654c\u4e00\uff0c\u8fd8\u6709\u201c\u5357\u5bab\u53cc\u5251\u201d\u76f8\u52a9\uff0c\u7adf\u4ecd\u4e45\u6218\u4e0d\u4e0b\uff0c\u4e0d\u7981\u5fc3\u4e2d\u90fd\u5728\u6697\u6697\u63e3 \u6d4b\uff0c\u548c\u81ea\u5df1\u52a8\u624b\u7684\u8fd9\u7626\u8001\u5934\u5b50\uff0c\u7a76\u7adf\u662f\u4f55\u4eba\u7269\u3002 \u3000\u3000\u53e6\u4e24\u4e2a\u8eab\u6750\u9880\u957f\u7626\u524a\uff0c\u9762\u76ee\u957f\u5f97\u51e0\u4e4e\u5b8c\u5168\u4e00\u6837\u7684\uff0c\u5374\u6b63\u662f\u6614\u5e74\u201c\u5357\u5bab\u5927\u4fa0\u201d\u7684\u552f\u4e00\u4f20 \u4eba\uff0c\u6c5f\u5357\u201c\u4e09\u624d\u5251\u201d\u7684\u540d\u5bb6\u201c\u5357\u5bab\u53cc\u5251\u201d\u3002 \u3000\u3000\u201c\u4e09\u624d\u5251\u201d\u4ee5\u8f7b\u7075\u89c1\u957f\uff0c\u8fd9\u5357\u5bab\u53cc\u5251\uff0c\u8eab\u6cd5\u4e4b\u8f7b\u7075\uff0c\u66f4\u662f\u6b64\u4e2d\u7fd8\u695a\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e24\u4eba\u8eab\u968f\u5251\u8d70\uff0c\u5251\u968f\u8eab\u6e38\uff0c\u8eab\u4e0d\u79bb\u5251\uff0c\u5251\u4e0d\u79bb\u8eab\uff0c\u4e24\u9053\u9752\u84dd\u7684\u5251\u5149\uff0c\u76d8\u65cb\u6325\u523a\uff0c \u7740\u7740\u4e0d\u79bb\u4e07\u5929\u840d\u7684\u8981\u5bb3\u3002\u8fd9\u94c1\u9762\u5b64\u884c\u5ba2\u6b66\u529f\u867d\u5df2\u51e0\u8fbe\u5dc5\u5cf0\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u65e0\u6cd5\u5360\u5f97\u4e0a\u98ce\u3002 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u6697\u4e2d\u70b9\u5934\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5357\u5bab\u53cc\u674e\uff0c\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff0c\u51e0\u65f6\u5012\u8981\u8bbe\u6cd5\u62c9\u62e2\u8fc7\u6765\uff0c\u8fd9\u4e9b\u4eba\u82e5\u548c\u90a3\u8c22\u96e8\u4ed9\u7ed3\u6210\u6b7b \u515a\uff0c\u5c06\u6765\u786e\u6709\u4e9b\u4e0d\u597d\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u97e6\u50b2\u7269\u4e0e\u8c22\u96e8\u4ed9\u4e4b\u95f4\u7684\u5acc\u9699\uff0c\u5df2\u8d8a\u6765\u8d8a\u6df1\uff0c\u662f\u4ee5\u624d\u8f6c\u7740\u8fd9\u79cd\u5ff5\u5934\u3002 \u3000\u3000\u4e00\u9762\u5c06\u76ee\u5149\u79fb\u5230\u90a3\u6765\u81ea\u9752\u6d77\u7684\u5c11\u5e74\u94b1\u7fca\u8eab\u4e0a\u53bb\u3002 \u3000\u3000\u4ed6\u7a0d\u4e00\u51dd\u89c6\uff0c\u53cc\u7709\u4e0d\u89c9\u7d27\u7d27\u76b1\u5230\u4e00\u5904\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6df1\u77e5\u8fd9\u94b1\u7fca\u7684\u6b66\u529f\uff0c\u539f\u4ee5\u4e3a\u4ed6\u4e00\u5b9a\u53ef\u4ee5\u5360\u5f97\u4e0a\u98ce\uff0c\u90a3\u77e5\u6b64\u523b\u4e00\u770b\uff0c\u5374\u89c1\u90a3\u8eab\u7a7f\u9526 \u8863\u7684\u7c97\u8c6a\u6c49\u5b50\u7684\u8eab\u6cd5\uff0c\u6709\u5982\u72c2\u98d9\u9f99\u5377\uff0c\u7adf\u5c06\u94b1\u7fca\u56db\u9762\u516b\u65b9\u5730\u56f0\u4f4f\u4e86\u3002 \u3000\u3000\u94b1\u7fca\u5fc3\u4e2d\uff0c\u53c8\u4f55\u5c1d\u6ca1\u6709\u5982\u6b64\u60f3\u6cd5\u3002\u4ed6\u5728\u9752\u6d77\u90a3\u79cd\u5947\u5bd2\u9177\u70ed\u4e4b\u5730\uff0c\u803d\u4e86\u5341\u6570\u5e74\uff0c\u5c06\u90a3\u201c\u65e0 \u540d\u8001\u4eba\u201d\u7684\u4e00\u8eab\u7edd\u6280\uff0c\u51e0\u4e4e\u5b66\u5f97\u4e5d\u6210\uff0c\u6b64\u756a\u631f\u6280\u51fa\u5c71\uff0c\u81ea\u4ee5\u4e3a\u51ed\u6043\u7740\u81ea\u5df1\u7684\u8eab\u624b\uff0c\u4f55\u96be\u5728\u6b66 \u6797\u4e2d\u538b\u5012\u7fa4\u8c6a\uff0c\u90a3\u77e5\u6b64\u523b\u8fd9\u7c97\u8c6a\u7684\u8001\u8005\uff0c\u8eab\u6cd5\u4e4b\u98d8\u5ffd\u5947\u8be1\uff0c\u7adf\u4f7f\u5f97\u81ea\u5df1\u5904\u5904\u7f1a\u624b\u7f1a\u811a\uff0c\u51e0\u4e4e \u8fde\u8eab\u624b\u90fd\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u4e24\u4eba\u4ee5\u5feb\u6253\u5feb\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u62c6\u4e86\u767e\u4f59\u62db\uff0c\u5fc3\u4e2d\u4ea6\u662f\u6025\u8e81\u4e0d\u582a\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u4eec\u52a8\u624b\u4e4b\u524d\uff0c\u4ff1\u5404\u4e0d\u77e5\u9053\u5bf9\u65b9\u7684\u6765\u5386\uff0c\u662f\u4ee5\u4e0d\u514d\u90fd\u4f4e\u4f30\u4e86\u5bf9\u624b\u3002\u6b64\u523b\u4e00\u89c1\u5bf9\u65b9\u7684 \u8eab\u624b\uff0c\u8fdc\u51fa\u4e8e\u81ea\u5df1\u610f\u6599\u4e4b\u5916\uff0c\u81ea\u7136\u96be\u514d\u4ff1\u90fd\u4e3a\u4e4b\u60ca\u5947\u9519\u6115\u4e86\u3002 \u3000\u3000\u8fd9\u8fb9\u6fc0\u6597\u7740\u7684\u4f17\u4eba\uff0c\u4ff1\u662f\u4e00\u5473\u54d1\u6597\uff0c\u4f46\u95fb\u864e\u864e\u638c\u98ce\u4e0e\u98d5\u98d5\u7684\u5251\u6c14\uff0c\u54cd\u6210\u4e00\u7247\u3002 \u3000\u3000\u5f7c\u6b64\u5fc3\u4e2d\u867d\u5728\u5947\u602a\uff0c\u5374\u8c01\u4e5f\u6ca1\u6709\u559d\u95ee\u5bf9\u65b9\u7684\u6765\u5386\u3002 \u3000\u3000\u5c24\u5176\u662f\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\uff0c\u4ed6\u5fc3\u4e2d\u7126\u8651\u7740\u81ea\u5df1\u7231\u5973\u7684\u53bb\u5411\uff0c\u4e00\u9762\u53c8\u5728\u5947\u602a\u5979\u4e3a\u4ec0\u4e48\u8981\u62a2 \u53bb\u4eba\u5bb6\u6000\u4e2d\u7684\u5b69\u5b50\uff0c\u662f\u4ee5\u4e0b\u624b\u8d8a\u89c1\u72e0\u8fa3\uff0c\u6068\u4e0d\u5f97\u4e00\u62db\u51fb\u6bd9\u5bf9\u624b\uff0c\u524d\u53bb\u770b\u770b\u81ea\u5df1\u7231\u5973\u7684\u4e0b\u843d\u3002 \u3000\u3000\u800c\u90a3\u8fb9\u548c\u4f0a\u98ce\u52a8\u7740\u624b\u7684\u201c\u52b3\u5c71\u4e09\u5251\u201d\u53e3\u4e2d\u867d\u662f\u53f1\u5412\u8fde\u58f0\uff0c\u624b\u4e0b\u5374\u672a\u4f7f\u51fa\u5168\u529b\u3002 \u3000\u3000\u8fd9\u201c\u52b3\u5c71\u4e09\u5251\u201d\u7684\u5251\u6cd5\uff0c\u4f20\u81ea\u9c81\u4e1c\u201c\u9739\u96f3\u5251\u6d3e\u201d\u3002\u4f46\u4ed6\u4eec\u6b64\u523b\u5251\u5149\u867d\u6709\u5982\u9739\u96f3\u9706\u96f7\uff0c\u4f46 \u6536\u62db\u51fa\u62db\u95f4\uff0c\u5374\u663e\u7136\u7559\u4e0b\u4e09\u5206\u4f59\u5730\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u60f3\u5c06\u5bf9\u624b\u4f24\u5728\u5251\u4e0b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e09\u4eba\u751f\u6027\u61a8\u76f4\uff0c\u867d\u7136\u5165\u4e86\u201c\u5929\u4e89\u6559\u201d\uff0c\u4f46\u5374\u5bf9\u201c\u5929\u4e89\u6559\u201d\u6709\u4e9b\u4e0d\u6ee1\u3002\u5c24\u5176\u4ee4\u4ed6\u4eec \u770b\u4e0d\u987a\u773c\u7684\uff0c\u5c31\u662f\u90a3\u4e9b\u201c\u5929\u4e89\u6559\u201d\u7684\u91d1\u8863\u9999\u4e3b\u4eec\uff0c\u4e00\u4e2a\u4e2a\u773c\u775b\u90fd\u50cf\u662f\u957f\u5728\u5934\u9876\u4e0a\u3002 \u3000\u3000\u8fd9\u5144\u5f1f\u738b\u4eba\u6697\u4e2d\u4e00\u5546\u91cf\uff0c\u89c9\u5f97\u4e0e\u5176\u5728\u8fd9\u201c\u5929\u4e89\u6559\u201d\u91cc\uff0c\u770b\u522b\u4eba\u7684\u773c\u8272\uff0c\u5012\u4e0d\u5982\u81ea\u5df1\u505a\u756a \u4e8b\u4e1a\uff0c\u6765\u5f97\u7545\u5feb\u3002\u662f\u4ee5\u65e9\u5c31\u840c\u4e86\u9000\u5fd7\uff0c\u53ea\u662f\u5374\u53c8\u754f\u60e7\u7740\u201c\u5929\u4e89\u6559\u201d\u7684\u52bf\u529b\uff0c\u4e0d\u6562\u968f\u610f\u79bb\u53bb\u800c \u5df2\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8fd9\u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u81ea\u5c31\u4e0d\u80af\u4e3a\u201c\u5929\u4e89\u6559\u201d\u5356\u547d\u3002\u800c\u4e14\u5bf9\u624b\u6b66\u529f\u9887\u9ad8\uff0c\u62db \u5f0f\u5c24\u5176\u5947\u8be1\uff0c\u7adf\u4e0d\u662f\u6b66\u6797\u4e2d\u4efb\u4f55\u4e00\u95e8\u4e00\u6d3e\u7684\u8def\u6570\u3002\u81ea\u5df1\u672c\u975e\u4eba\u5bb6\u7684\u654c\u624b\uff0c\u53ea\u662f\u4eba\u5bb6\u624b\u4e0b\u4f3c\u4e4e \u4e5f\u5728\u7559\u60c5\uff0c\u5728\u4f7f\u5f97\u4e00\u62db\u5df2\u4fbf\u81ea\u5df1\u65e0\u6cd5\u62b5\u6321\u7684\u62db\u6570\u540e\uff0c\uff0c\u5374\u5e76\u4e0d\u8ddf\u7740\u8fdb\u51fb\uff0c\u662f\u4ee5\u8fd9\u4e09\u4eba\u5fc3\u4e2d\u6697 \u6697\u611f\u6fc0\uff0c\u5c31\u66f4\u4e0d\u5c3d\u529b\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5374\u4e0d\u77e5\u9053\uff0c\u4f0a\u98ce\u7684\u5fc3\u60c5\u5374\u548c\u4ed6\u4eec\u7edd\u4e0d\u4e00\u6837\uff0c\u4ed6\u6b63\u5fc3\u6025\u4e8e\u81ea\u5df1\u7684\u4eb2\u751f\u9aa8\u8840\u7684\u5b89\u5371\uff0c\u53c8 \u5947\u602a\u4e8e\u90a3\u4e07\u8679\u6b64\u4e3e\u7684\u7528\u610f\uff0c\u672c\u60f3\u65e9\u4e9b\u6253\u53d1\u4e86\u5bf9\u624b\uff0c\u5374\u53c8\u82e6\u4e8e\u4eba\u5bb6\u5e76\u975e\u5eb8\u624b\uff0c\u662f\u4ee5\u4fbf\u65bd\u5c55\u51fa\u5b66 \u81ea\u5929\u661f\u79d8\u7b08\u4e0a\u7684\u62db\u5f0f\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u62db\u5f0f\uff0c\u4ed6\u53ea\u7ec3\u4e86\u77ed\u77ed\u5341\u4f59\u5929\uff0c\u867d\u7136\u4ed7\u7740\u6b66\u529f\u7684\u6839\u57fa\u548c\u5929\u8d44\u7684\u8d85\u4eba\uff0c\u5df2\u80fd\u5c06\u8fd9\u4e9b\u4ece \u672a\u4f7f\u7528\u8fc7\u7684\u62db\u5f0f\uff0c\u4f7f\u7528\u51fa\u6765\u3002 \u3000\u3000\u4f46\u6b66\u529f\u4e00\u9053\uff0c\u987b\u7ecf\u82e6\u7ec3\uff0c\u7edd\u4e0d\u662f\u4e00\u671d\u4e00\u5915\u53ef\u4ee5\u4fa5\u5e78\u5f97\u6765\u7684\u3002\u4ed6\u5728\u65bd\u5c55\u8fd9\u4e9b\u62db\u5f0f\u7684\u65f6\u5019\uff0c \u624b\u6cd5\u81ea\u7136\u96be\u514d\u751f\u758f\uff0c\u4e5f\u5e38\u5e38\u5c06\u4e00\u4e9b\u53ef\u4ee5\u5236\u80dc\u7684\u673a\u4f1a\u5931\u53bb\u3002 \u3000\u3000\u662f\u4ee5\u8fd9\u6b63\u5728\u6fc0\u6597\u7740\u7684\u4e09\u62e8\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u6839\u672c\u5206\u4e0d\u51fa\u9ad8\u4e0b\u3002 \u3000\u3000\u201c\u71d5\u5c71\u4e09\u5251\u201d\u672c\u662f\u9f0e\u8db3\u800c\u7acb\uff0c\u6b64\u523b\u7a81\u5730\u4e00\u58f0\u66b4\u559d\uff0c\u4e09\u4eba\u7adf\u9f50\u5730\u62bd\u8eab\u64a4\u6b65\uff0c\u7ea6\u9000\u4e94\u5c3a\uff0c\u540c \u65f6\u5c06\u53f3\u624b\u7684\u5251\u4ea4\u5230\u5de6\u624b\uff0c\u53f3\u624b\u5374\u9f50\u5730\u4e00\u626c \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u7a81\u5730\u5bd2\u661f\u6ee1\u5929\uff0c\u6570\u5341\u9053\u5e26\u7740\u547c\u5578\u4e4b\u58f0\u7684\u5bd2\u5149\uff0c\u7535\u4e5f\u4f3c\u5730\u671d\u94c1\u9762\u5b64\u884c\u5ba2\u51fb\u53bb\u3002 \u3000\u3000\u4e07\u5929\u840d\u51b7\u7b11\u4e00\u58f0\uff0c\u8fd9\u6b66\u6797\u4e2d\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u600e\u4f1a\u5c06\u4e00\u4e9b\u6697\u5668\u653e\u5728\u773c\u91cc\u3002\u51b7\u7b11\u58f0\u4e2d\uff0c\u8eab\u5f62\u7684 \u6e9c\u6e9c\u7684\u4e00\u8f6c\uff0c\u888d\u8896\u8fde\u62c2\uff0c\u65b9\u81ea\u5c06\u8fd9\u4e9b\u6697\u5668\u6325\u5374\u3002 \u3000\u3000\u90a3\u77e5\u8fd9\u4e9b\u6697\u5668\u7adf\u6709\u5982\u6f2b\u5929\u800c\u6765\u7684\u98de\u8757\uff0c\u7adf\u6ca1\u6709\u4e00\u4e1d\u505c\u6b47\u7684\u65f6\u5019\u3002 \u3000\u3000\u6700\u5389\u5bb3\u7684\u662f\uff1a\u8fd9\u4e9b\u770b\u4f3c\u7b14\u76f4\u51fb\u6765\u7684\u5bd2\u5149\uff0c\u6709\u65f6\u5374\u4f1a\u5728\u7a7a\u4e2d\u7a81\u7136\u8f6c\u6298\u4e00\u4e0b\uff0c\u7a81\u53c8\u6362\u4e86\u4e2a\u65b9 \u5411\u51fb\u6765\uff0c\u6709\u7684\u660e\u660e\u5df2\u6389\u5728\u5730\u4e0a\uff0c\u5374\u53c8\u7a81\u5730\u53cd\u5f39\u800c\u4e0a\u3002 \u3000\u3000\u4e07\u5929\u840d\u4e00\u751f\u95ef\u6c64\u6c5f\u6e56\uff0c\u5374\u4e5f\u672a\u66fe\u9047\u8fc7\u5982\u6b64\u5947\u8be1\u7684\u6697\u5668\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u88ab\u8fd9\u4e9b\u6f2b\u5929\u98de\u6765\u7684 \u5bd2\u661f\u903c\u4f4f\uff0c\u62bd\u64a4\u4e0d\u51fa\u8eab\u5f62\u6765\u3002 \u3000\u3000\u5357\u5bab\u53cc\u674e\u4e92\u6253\u4e86\u4e2a\u624b\u5f0f\uff0c\u8fdc\u8fdc\u9000\u5230\u4e00\u8fb9\u3002 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u9762\u4e0a\u559c\u52a8\u989c\u8272\uff0c\u6697\u4e2d\u4f4e\u8bed\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8fd9\u71d5\u5c71\u4e09\u5251\u7adf\u5df2\u5b66\u5f97\u4e86\u201c\u5e38\u6c0f\u795e\u4e38\u201d\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u6709\u5426\u4f20\u5f97\u90a3\u201c\u4e5d\u5251\u4ea4\u51fb\u201d\u7684 \u7edd\u6280\uff01\u5509\uff01\u2014\u2014\u60f3\u4e0d\u5230\u8fd9\u5728\u6b66\u6797\u4e2d\u6e6e\u6ca1\u5df2\u4e45\u7684\u7edd\u6280\uff0c\u4eca\u65e5\u5374\u5728\u8fd9\u4e09\u4e2a\u4eba\u8eab\u4e0a\u89c1\u5230\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u65b9\u81ea\u6697\u559c\uff0c\u90a3\u77e5\u8fdc\u8fdc\u7a81\u5730\u4f20\u6765\u4e00\u9635\u7126\u6025\u7684\u547c\u58f0\uff0c\u4e00\u4eba\u60f6\u7136\u5982\u98de\u63a0\u6765\uff0c\u6ee1\u9762\u4ff1\u662f\u7126 \u6025\u7684\u795e\u8272\u3002 \u8eab\u5f62\u65b9\u987f\u5c31\u7528\u624b\u6307\u9065\u9065\u6307\u7740\u8def\u65c1\u7684\u6797\u6728\uff0c\u5374\u53c8\u56e0\u4e3a\u5598\u606f\u8fc7\u5267\uff0c\u7adf\u7126\u6025\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u4e09\u7ae0\u3000\u592b\u4eba\u6709\u96be>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u4e09\u7ae0\u3000\u592b\u4eba\u6709\u96be \u3000\u3000\u6614\u5e74\u70b9\u82cd\u6d3e\u7684\u4e00\u4ee3\u5b97\u5e08\u5e38\u6f2b\u5929\u592b\u5987\uff0c\u4e3a\u7740\u4e00\u4ef6\u6781\u4e3a\u79bb\u5947\u7684\u53d8\u6545\uff0c\u5728\u6df1\u5c71\u4e2d\uff0c\u82e6\u9690\u5341\u6570 \u5e74\uff0c\u8fd9\u5341\u6570\u5e74\u4e2d\u4ed6\u592b\u5987\u4e24\u4eba\uff0c\u867d\u7136\u5c1d\u5c3d\u4e86\u4e16\u95f4\u7684\u8f9b\u9178\u56f0\u82e6\uff0c\u4f46\u5374\u4e5f\u7ec3\u6210\u4e86\u4e00\u79cd\u626c\u540d\u5929\u4e0b\uff0c\u50b2 \u5578\u6b66\u6797\u7684\u7edd\u6280\uff0c\u90a3\u4fbf\u662f\u6b64\u523b\u8fd9\u201c\u71d5\u5c71\u4e09\u5251\u201d\u6240\u65bd\u5c55\u7684\u201c\u5e38\u6c0f\u795e\u4e38\u201d\uff01 \u3000\u3000\u8fd9\u201c\u5e38\u6c0f\u795e\u4e38\u201d\uff0c\u81ea\u4ece\u5e38\u6f2b\u5929\u592b\u5987\u518d\u6b21\u5f52\u9690\u4e4b\u540e\uff0c\u5728\u6b66\u6797\u4fbf\u7edd\u8ff9\u5df2\u4e45\uff0c\u6b64\u523b\u7adf\u53c8\u5728\u201c\u71d5 \u5c71\u4e09\u5251\u201d\u8eab\u4e0a\u91cd\u73b0\uff0c\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u81ea\u662f\u5927\u611f\u60ca\u559c\u3002 \u3000\u3000\u8981\u77e5\u9053\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u97e6\u50b2\u7269\u624b\u4e2d\u7684\u4e00\u9762\u5bd2\u91d1\u6e14\u7f51\uff0c\u4e43\u662f\u666e\u5929\u4e4b\u4e0b\u5404\u95e8\u5404\u6d3e\u6697\u5668\u514b\u661f\uff0c\u4ed6 \u5bf9\u4e8e\u5f53\u4eca\u5929\u4e0b\u5404\u79cd\u6697\u5668\u7684\u6765\u5386\u7528\u6cd5\uff0c\u81ea\u4e5f\u77e5\u4e4b\u751a\u8be6\uff0c\u662f\u4ee5\u6b64\u523b\u52a8\u624b\u7684\u5404\u4eba\uff0c\u53ea\u6709\u4ed6\u624d\u8ba4\u5f97\u8fd9 \u79cd\u7edd\u8ff9\u6b66\u6797\u7684\u6697\u5668\uff0c\u65b9\u81ea\u6697\u4e2d\u6b23\u559c\uff0c\u90a3\u77e5\u8fdc\u8fdc\u6797\u6728\u6df1\u5904\uff0c\u7a81\u5730\u4f20\u6765\u4e00\u9635\u7126\u6025\u7684\u547c\u58f0\uff0c\u4e00\u4eba\u60f6 \u7136\u63a0\u6765\uff0c\u6c14\u5598\u54bb\u54bb\uff0c\u751f\u50cf\u662f\u7a81\u7136\u9047\u7740\u4e86\u4e00\u4ef6\u8db3\u4ee5\u4ee4\u4ed6\u5341\u5206\u60ca\u60e7\u4e4b\u4e8b\u4f3c\u7684\u3002 \u3000\u3000\u97e6\u50b2\u7269\u4e00\u60ca\u4e4b\u4e0b\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4eba\u7adf\u662f\u65b9\u624d\u8ffd\u8e2a\u859b\u82e5\u74a7\u800c\u53bb\u7684\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\uff0c\u4ed6 \u4e0e\u8c22\u96e8\u4ed9\u867d\u7136\u6781\u4e3a\u4e0d\u7766\uff0c\u4f46\u5374\u4e5f\u77e5\u9053\u8fd9\u4e2a\u53cd\u7ec8\u5357\u5c71\uff0c\u80cc\u53db\u6c5f\u6e56\u9053\uff0c\u6b66\u6797\u4e2d\u4eba\u95fb\u800c\u8e59\u7709\u7684\u201c\u591a \u624b\u771f\u4eba\u201d\uff0c\u662f\u4e2a\u6781\u5176\u9634\u72e0\u6df1\u6c88\u7684\u4eba\u7269\uff0c\u82e5\u975e\u9047\u7740\u5341\u5206\u4e25\u91cd\u7684\u4e8b\uff0c\u7edd\u4e0d\u4f1a\u663e\u9732\u51fa\u8fd9\u79cd\u7126\u6025\u614c\u4e71 \u7684\u795e\u6001\u6765\u3002 \u3000\u3000\u5f53\u4e0b\u8fd9\u7d20\u4ee5\u673a\u667a\u8c28\u614e\u540d\u95fb\u6c5f\u6e56\u7684\u201c\u4e03\u6d77\u6e14\u5b50\u201d\uff0c\u4e0d\u7981\u4e3a\u4e4b\u6115\u7136\u95ee\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8c22\u96e8\u4ed9\u5982\u98de\u63a0\u6765\u7684\u8eab\u5f62\uff0c\u500f\u7136\u505c\u5728\u97e6\u50b2\u7269\u8eab\u4fa7\uff0c\u91cd\u91cd\u5598\u4e86\u4e24\u53e3\u6c14\uff0c\u8f6c\u76ee\u56db\u671b\uff0c\u53ea\u89c1\u773c\u524d \u5149\u534e\u95ea\u52a8\uff0c\u53ee\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u5728\u6f2b\u5929\u4ea4\u4e92\u51b2\u51fb\u7684\u94a2\u4e38\u4e4b\u4e0b\uff0c\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u8eab\u5f62\u65cb\u8f6c\u5982\u98ce\uff0c\u53cc\u624b\u888d\u8896\u8fde\u62c2\uff0c\u731b\u7136\u5f3a\u52b2 \u7684\u638c\u98ce\uff0c\u867d\u5c06\u51fb\u5411\u4ed6\u7684\u201c\u5e38\u6c0f\u795e\u4e38\u201d\u90fd\u4e00\u4e00\u6325\u843d\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u79cd\u50b2\u89c6\u7fa4\u4f26\u7684\u6697\u5668\uff0c\u7684\u786e\u6709\u7740\u975e\u540c\u51e1\u54cd\u7684\u9738\u9053\uff0c\u9000\u800c\u540e\u6765\uff0c\u53bb\u800c\u540e\u56de\uff0c\u660e\u660e\u5411 \u53f3\uff0c\u7a81\u5730\u4e00\u8f6c\u5411\u5de6\uff0c\u660e\u660e\u5411\u4e0a\uff0c\u7a81\u5730\u4e00\u8f6c\u5411\u4e0b\uff0c\u7adf\u6ca1\u6709\u4e00\u4e1d\u505c\u6b47\u7684\u65f6\u5019\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u6b66\u529f\u867d\u9ad8\uff0c\u638c\u529b\u867d\u5f3a\uff0c\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u663e\u5f97\u6709\u4e9b\u614c\u4e71\uff0c\u53ea\u662f\u4e00\u65f6\u534a\u523b\u95f4\uff0c \u5374\u4e5f\u4e0d\u81f4\u843d\u8d25\u800c\u5df2\u3002 \u3000\u3000\u8d64\u624b\u4e0e\u9c81\u4e1c\u201c\u9739\u96f3\u5251\u6d3e\u201d\u957f\u95e8\u5f1f\u5b50\u201c\u52b3\u5c71\u4e09\u5251\u201d\u76f8\u6597\u7684\u4f0a\u98ce\uff0c\u6b64\u523b\u62db\u5f0f\u6108\u6253\u6108\u89c1\u7cbe\u5999\uff0c \u7adf\u5c06\u8fd9\u6210\u540d\u5df2\u4e45\u7684\u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u903c\u7684\u4e0d\u5f97\u4e0d\u4f7f\u51fa\u5168\u529b\u65a1\u65cb\uff0c\u4e8e\u662f\u8fd9\u4e09\u9053\u6709\u5982\u9739\u96f3\u96f7\u9706\u7684\u5251 \u5149\uff0c\u6b64\u523b\u58f0\u52bf\u4fbf\u66f4\u60ca\u4eba\uff01 \u3000\u3000\u6765\u81ea\u9752\u6d77\u7684\u94b1\u7fca\uff0c\u5fc3\u6000\u60ca\u7591\u9519\u6115\uff0c\u4e0e\u5782\u540d\u6b66\u6797\u6570\u5341\u5e74\u7684\u5343\u91cc\u8ffd\u98ce\uff0c\u795e\u884c\u65e0\u5f71\uff0c\u5999\u624b\u8bb8\u767d \u7684\u6076\u6597\uff0c\u6b64\u523b\u5374\u5df2\u5230\u4e86\u80dc\u8d1f\u7acb\u5236\u7684\u9636\u6bb5\uff0c\u4e24\u4eba\u98d8\u5ffd\u7684\u8eab\u6cd5\uff0c\u8be1\u5f02\u7684\u62db\u5f0f\uff0c\u4f7f\u5f97\u5f7c\u6b64\u5404\u5404\u611f\u5230 \u4e0d\u80fd\u6709\u4e00\u4e1d\u677e\u61c8\uff0c\u8fd9\u79cd\u4ee5\u52a8\u5236\u52a8\uff0c\u4ee5\u5feb\u6253\u5feb\u7684\u52a8\u624b\u65b9\u5f0f\uff0c\u5728\u6b66\u6797\u9ad8\u624b\u8eab\u4e0a\u672c\u4e0d\u5e38\u89c1\uff0c\u56e0\u4e3a\u5f7c \u6b64\u4e4b\u95f4\uff0c\u8c01\u90fd\u77e5\u9053\uff0c\u81ea\u5df1\u7684\u62db\u5f0f\u53ea\u8981\u7a0d\u6709\u758f\u5ffd\uff0c\u7acb\u65f6\u4fbf\u4f1a\u6709\u8840\u8089\u6a2a\u98de\u4e4b\u7978\uff0c\u8fd9\u4e24\u4eba\u867d\u65e0\u4e0d\u5171 \u6234\u5929\uff0c\u6027\u547d\u76f8\u640f\u4e4b\u4ec7\uff0c\u4f46\u6b64\u523b\u5927\u5bb6\u5168\u5df2\u52bf\u6210\u9a91\u864e\uff0c\u8981\u60f3\u7f62\u624b\uff0c\u5374\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u591a\u624b\u771f\u4eba\u8c22\u96e8\u4ed9\u76ee\u5149\u626b\u52a8\u95f4\uff0c\u5fae\u4e00\u5b9a\u795e\uff0c\u5ffd\u5730\u9644\u5728\u97e6\u50b2\u7269\u7684\u8033\u8fb9\uff0c\u4f4e\u4f4e\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u97e6 \u50b2\u7269\u9762\u8272\u7acb\u65f6\u4e3a\u4e4b\u4e00\u53d8\uff0c\u8131\u53e3\u95ee\u9053\uff1a \u3000\u3000\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u8c22\u96e8\u4ed9\u4f4e\u7b11\u4e00\u58f0\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u6c88\u58f0\u9053\uff1a \u3000\u3000\u201c\u6b64\u4e8b\u4e07\u4e07\u4e0d\u53ef\u8fdf\u7591\uff0c\u97e6\u5144\u5b9a\u8981\u5feb\u4e9b\u8d76\u53bb\u624d\u662f\uff0c\u5509\u2026\u2026\u5c0f\u5f1f\u867d\u7136\u2026\u2026\u5509\uff0c\u53ef\u662f\u9047\u7740\u8fd9\u79cd \u4e8b\uff0c\u5c0f\u5f1f\u5374\u7684\u786e\u662f\u4e00\u7b79\u83ab\u5c55\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u957f\u53f9\u4e86\u4e24\u58f0\uff0c\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u4ea6\u662f\u6ee1\u9762\u6c88\u601d\u96be\u51b3\u4e4b\u8272\uff0c\u4fef\u9996\u6c88\u541f\u957f\u4e45\uff0c\u7a81\u5730\u5927\u559d \u4e00\u58f0\uff1a \u3000\u3000\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8fd9\u77f3\u7834\u5929\u60ca\uff0c\u58f0\u5982\u96f7\u9706\u7684\u201c\u4f4f\u624b\u201d\u4e24\u5b57\uff0c\u65b9\u4e00\u51fa\u53e3\uff0c\u793a\u4eba\u9f50\u90fd\u4e3a\u4e4b\u4e00\u60ca\u3002 \u3000\u3000\u672c\u5df2\u8fdc\u8fdc\u9000\u5230\u4e00\u65c1\u7684\u201c\u5357\u5bab\u53cc\u5251\u201d\u674e\u6c0f\u5144\u5f1f\uff0c\u6115\u7136\u9762\u987e\uff0c\u9f50\u5730\u4e00\u4e2a\u7bad\u6b65\uff0c\u7eb5\u8eab\u81f3\u97e6\u50b2\u7269 \u8eab\u524d\uff0c\u63a2\u9996\u6c88\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u52b3\u5c71\u4e09\u5251\u52a8\u624b\u4e4b\u95f4\uff0c\u53ea\u89c1\u5bf9\u624b\u62db\u5f0f\u6108\u6765\u6108\u5999\uff0c\u5a01\u529b\u6108\u6765\u6108\u5f3a\uff0c\u4e09\u4eba\u5fc3\u4e2d\u4e0d\u7981\u53c8\u662f\u60ca\u5f02\uff0c \u53c8\u662f\u5947\u602a\uff0c\u4ed6\u4e09\u4eba\u672c\u5c31\u4e0d\u613f\u4e3a\u201c\u5929\u4e89\u6559\u201d\u5356\u547d\uff0c\u6b64\u523b\u542c\u89c1\u8fd9\u4e00\u58f0\u547c\u559d\uff0c\u5de6\u624b\u5251\u8bc0\uff0c\u9f50\u5730\u4e00 \u626c\uff0c\u53f3\u624b\u957f\u5251\uff0c\u7531\u5de6\u800c\u53f3\uff0c\u201c\u547c\u201d\u5730\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u8fd9\u4e09\u4eba\u7adf\u540c\u65f6\u65bd\u51fa\u4e00\u62db\u4e0e\u201c\u592a\u6781\u5251\u6cd5\u201d\u4e2d \u7684\u5999\u7740\uff01\u201c\u5982\u5c01\u4f3c\u95ed\u201d\uff0c\u529f\u6548\u76f8\u540c\uff0c\u51fa\u624b\u76f8\u4f3c\uff0c\u4f46\u5a01\u529b\u72b9\u6709\u8fc7\u4e4b\u7684\u201c\u957f\u8679\u8d2f\u65e5\u201d\u6765\u3002 \u3000\u3000\u4e09\u9053\u5251\u5149\uff0c\u679c\u7136\u6709\u5982\u957f\u8679\u8d3e\u65e5\u4e00\u822c\uff0c\u4ea4\u526a\u800c\u6765\uff0c\u201c\u52b3\u5c71\u4e09\u5251\u201d\u53e3\u4e2d\u4fbf\u4e5f\u9f50\u5730\u4f4e\u53f1\u4e00\u58f0\uff0c \u62e7\u8170\u9519\u6b65\uff0c\u540e\u9000\u4e94\u5c3a\u3002 \u3000\u3000\u4f0a\u98ce\u5148\u524d\u4f55\u5c1d\u4e0d\u77e5\u8fd9\u4e09\u4eba\u624b\u4e0b\u6709\u5bb9\u60c5\u4e4b\u5904\uff0c\u6b64\u523b\u5fae\u4e00\u9519\u6115\uff0c\u4fbf\u4e5f\u4e0d\u4e3a\u5df1\u751a\uff0c\u53ea\u89c1\u8fd9\u4e8c\u4e2a \u9c81\u4e1c\u5251\u624b\uff0c\u62e7\u8170\u9519\u6b65\u95f4\uff0c\u63a0\u5230\u4e03\u6d77\u6e14\u5b50\u8eab\u4fa7\uff0c\u4ea6\u63a2\u9996\u6c88\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u6709\u4f55\u5429\u5490\uff01\u201d \u3000\u3000\u71d5\u5c71\u4e09\u5251\uff0c\u867d\u7136\u662f\u6614\u5e74\u4e00\u4ee3\u5251\u8c6a\u5e38\u6f2b\u5929\u7684\u8fdc\u623f\u4eb2\u5c5e\uff0c\u4f46\u5b66\u5f97\u8fd9\u6b66\u6797\u79d8\u6280\u201c\u5e38\u6c0f\u795e\u4e38\u201d\uff0c \u5374\u662f\u53e6\u6709\u673a\u7f18\uff0c\u4e09\u4eba\u4e43\u662f\u59d1\u8868\u5144\u5f1f\uff0c\u81ea\u5e7c\u751f\u957f\u4e00\u5904\uff0c\u4e60\u6b66\u65f6\u7247\u523b\u4e0d\u79bb\uff0c\u8fd9\u4ea6\u662f\u4ed6\u4eec\u80fd\u4e60\u5f97 \u201c\u5e38\u6c0f\u795e\u4e38\u201d\u7684\u4e00\u4e2a\u4e3b\u8981\u539f\u56e0\u3002 \u3000\u3000\u6b64\u523b\uff0c\u8fd9\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u4e00\u58f0\u66b4\u559d\uff0c\u4f7f\u5f97\u201c\u71d5\u5c71\u4e09\u5251\u201d\u4ea6\u4e3a\u4e4b\u4e00\u6115\uff0c\u5de6\u8155\u4e00\u53cd\uff0c\u5c06\u624b\u4e2d\u957f \u5251\u9690\u5230\u8098\u540e\uff0c\u4e0d\u4f4f\u5f80\u8170\u7554\u9769\u56ca\u4e2d\u53d6\u53d1\u6697\u5668\u7684\u53f3\u624b\uff0c\u500f\u7136\u505c\u4f4f\uff0c\u8f6c\u8eab\u63a0\u5230\u4e03\u6d77\u6e14\u5b50\u8eab\u4fa7\uff0c\u6c88\u58f0 \u95ee\u9053\uff1a \u3000\u3000\u201c\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u8fd9\u201c\u5357\u5bab\u53cc\u674e\u201d\uff0c\u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u201c\u71d5\u5c71\u4e09\u5251\u201d.\u5374\u51e0\u4e4e\u662f\u540c\u65f6\u4f4f\u624b\u6492\u8eab\uff0c\u9000\u5230\u97e6\u50b2\u7269 \u8eab\u4fa7\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u53cc\u7709\u6df1\u76b1\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u591a\u624b\u771f\u4eba\u5374\u5df2\u6c88\u58f0\u9053\uff1a \u3000\u3000\u201c\u6559\u4e3b\u592b\u4eba\u6709\u96be\uff0c\u5c31\u5728\u90a3\u8fb9\u5c71\u6797\u6df1\u5904\u3002\u201d \u3000\u3000\u201c\u5357\u5bab\u53cc\u674e\u201d\uff0c\u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u201c\u71d5\u5c71\u4e09\u5251\u201d\uff0c\u9f50\u5730\u4e00\u60ca\uff0c\u9762\u5bb9\u5927\u53d8\uff01 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u5e72\u54b3\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6b64\u95f4\u4e4b\u4e8b\uff0c\u53ef\u6682\u6401\u7f6e\uff0c\u4f60\u4eec\u2014\u2014\u4f60\u4eec\u8fd8\u662f\u4e00\u9f50\u53bb\u770b\u770b\u5427\u2026\u2026\u201d \u3000\u3000\u8f6c\u9996\u5411\u8c22\u96e8\u4ed9\u4e00\u7b11\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u591a\u52b3\u8c22\u5144\u4e86\u3002\u201d \u3000\u3000\u8c22\u96e8\u4ed9\u53cc\u7709\u4e00\u76b1\uff0c\u9053\uff1a \u3000\u3000\u201c\u97e6\u5144\uff0c\u96be\u9053\u4f60\u4e0d\u53bb\u5417\uff1f\u201d \u3000\u3000\u97e6\u50b2\u7269\u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u5bf9\u6b64\u4e8b\u4e00\u65e0\u6240\u77e5\uff0c\u53bb\u4e86\u4ea6\u662f\u65e0\u7528\uff0c\u8c22\u5144\u2014\u2014\u563f\u563f\uff0c\u8c22\u5144\u60f3\u5fc5\u8981\u6bd4\u5c0f\u5f1f\u719f\u6089\u591a\u4e86\uff01\u201d \u3000\u3000\u8c22\u96e8\u4ed9\u76ee\u5149\u4e00\u51db\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u5728\u4ed6\u8138\u4e0a\uff0c\u7ec8\u4e8e\u72e0\u72e0\u4e00\u8dfa\u811a\uff0c\u9053\uff1a \u3000\u3000\u201c\u8ddf\u6211\u6765\u3002\u201d\u5c55\u52a8\u8eab\u5f62\uff0c\u5411\u5c71\u6797\u5904\u63a0\u53bb\u3002 \u3000\u3000\u201c\u5357\u5bab\u53cc\u674e\u201d\uff0c\u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u201c\u71d5\u5c71\u4e09\u5251\u201d\uff0c\u89c1\u4e86\u8fd9\u4e03\u6d77\u6e14\u5b50\u4e0e\u591a\u624b\u771f\u4eba\u4e24\u4eba\u7684\u795e \u6001\uff0c\u5fc3\u4e2d\u4ff1\u662f\u5927\u60d1\u4e0d\u89e3\u3002 \u3000\u3000\u4ed6\u4eec\u518d\u4e5f\u60f3\u4e0d\u9053\uff0c\u6559\u4e3b\u592b\u4eba\u6709\u96be\uff0c\u600e\u5730\u8fd9\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u5374\u754f\u7f29\u4e0d\u524d\uff0c\u800c\u4e14\u82e6\u7b11\u8fde\u8fde\uff0c\u50cf \u662f\u9047\u7740\u4ec0\u4e48\u5341\u5206\u5c34\u5c2c\u4e4b\u4e8b\u4f3c\u7684\u3002 \u3000\u3000\u65b9\u624d\u9669\u4e3a\u201c\u5e38\u6c0f\u795e\u4e38\u201d\u6240\u56f0\u7684\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\uff0c\u201c\u547c\uff0c\u547c\u201d\u6570\u58f0\uff0c\u638c\u98ce\u8fde\u626c\uff0c\u5c06\u56db\u4e0b \u7684\u94a2\u4e38\u5168\u90fd\u6325\u53bb\uff0c\u5fae\u4e00\u5b9a\u795e\uff0c\u89c1\u5230\u8fd9\u4e9b\u5929\u4e89\u6559\u4f17\uff0c\u7adf\u7a81\u5730\u8d70\u7684\u5e72\u5e72\u51c0\u51c0\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff0c\u8f6c \u8eab\u548c\u4f0a\u98ce\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4f0a\u98ce\u9762\u4e0a\uff0c\u4ea6\u6ee1\u662f\u60ca\u5947\u4e4b\u8272\uff0c\u4e24\u4eba\u5fc3\u4e2d\u5404\u5728\u6697\u95ee\u81ea\u5df1\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u53ef\u662f\u5728\u5f04\u4ec0\u4e48\u7384\u865a\uff01\u201d \u3000\u3000\u84e6\u5730\u2014\u2014 \u3000\u3000\u53ea\u542c\u201c\u5999\u624b\u201d\u8bb8\u767d\u7126\u96f7\u822c\u5927\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u8eba\u4e0b\uff01\u201d \u3000\u3000\u63a5\u7740\u4fbf\u662f\u60ca\u5927\u52a8\u5730\u822c\u51e0\u58f0\u5de8\u54cd\uff0c\u4fbf\u53c8\u54cd\u8d77\u94b1\u7fca\u90a3\u9634\u68ee\u5c16\u7ec6\u7684\u58f0\u97f3\uff0c\u51b7\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u53ea\u6015\u672a\u5fc5\u89c1\u5f97\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\uff0c\u4f0a\u98ce\u53c8\u4e0d\u7981\u4e00\u9f50\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u94b1\u7fca\uff0e\u8bb8\u767d\u4e24\u4eba\uff0c\u6b64\u523b\u8eab\u5f62\u5df2\u9f50\u5730\u987f\u4f4f\uff0c\u9762 \u9762\u76f8\u5bf9\uff0c\u4e92\u76f8\u51dd\u89c6\uff0c\u4e07\u5929\u840d\u8170\u8eab\u5fae\u5f13\uff0c\u4e00\u53cc\u864e\u76ee\u4e4b\u4e2d\uff0c\u7cbe\u5149\u66b4\u5c04\uff0c\u95ea\u7535\u822c\u51dd\u89c6\u7740\u94b1\u7fca\uff0c\u4e24\u53ea \u7b4b\u7ed3\u9634\u73b0\uff0c\u84b2\u6247\u822c\u7684\u5de8\u638c\uff0c\u6216\u62f3\u6216\u638c\uff0c\u7f13\u7f13\u4f38\u66f2\uff0c\u5728\u8fd9\u4e00\u4f38\u4e00\u66f2\u95f4\uff0c\u4e0d\u65f6\u53d1\u51fa\u201c\u683c\u683c\u201d\u5730\u9aa8 \u8282\u58f0\u54cd\uff0c\u751f\u50cf\u6068\u4e0d\u5f97\u7acb\u65f6\u5c06\u5bf9\u9762\u8fd9\u5c11\u5e74\uff0c\u4e00\u4e0b\u51fb\u6b7b\u5728\u81ea\u5df1\u638c\u4e0b\u3002 \u3000\u3000\u7ad9\u5728\u4ed6\u5bf9\u9762\u7684\u94b1\u7fca\uff0c\u4ecd\u662f\u6ee1\u9762\u51b7\u50b2\u4e4b\u8272\uff0c\u50b2\u7136\u5353\u7acb\uff0c\u4f46\u662f\u76ee\u5149\u4e4b\u4e2d\uff0c\u5374\u4f3c\u4e4e\u5df2\u5fae\u5fae\u9732\u51fa \u4e00\u4e9b\u754f\u602f\u4e4b\u610f\uff0c\u4ea6\u72e0\u72e0\u5730\u51dd\u6ce8\u7740\u4ed6\u7684\u654c\u624b\uff0c\u4e24\u8fb9\u5782\u4e0b\u7684\u624b\u638c\uff0c\u867d\u4e0d\u65f6\u53d1\u51fa\u4e00\u9635\u9635\u4e0d\u52a0\u6ce8\u610f\u4fbf \u96be\u770b\u51fa\u7684\u8f7b\u5fae\u98a4\u6296\uff0c\u4f46\u662f\u4ed6\u5353\u7acb\u7740\u7684\u8eab\u8eaf\uff0c\u5374\u4ecd\u7136\u662f\u575a\u5b9a\u7684\u6709\u5982\u5c71\u5cb3\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u8c01\u4e5f\u4e0d\u518d\u8bf4\u8bdd\uff0c\u8c01\u4e5f\u6ca1\u6709\u9000\u7f29\u534a\u6b65\uff0c\u65b9\u624d\u4ed6\u4eec\u5bf9\u4e86\u4e24\u638c\uff0c\u5999\u624b\u8bb8\u767d\u867d \u60f3\u4ee5\u6d78\u6deb\u4e8e\u4e2d\u6570\u5341\u5e74\u7684\u5185\u529b\u4fee\u4e3a\uff0c\u51fb\u5012\u8fd9\u770b\u6765\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u7eb5\u7136\u6b66\u529f\u9ad8\u5999\uff0c\u4f46\u5185\u529b\u5b9a\u4e0d\u4f1a\u6df1\u7684 \u5c11\u5e74\u3002\u90a3\u77e5\u94b1\u7fca\u5341\u6570\u5e74\u7684\u82e6\u7ec3\uff0c\u5c45\u7136\u4ee5\u201c\u65e0\u540d\u8001\u4eba\u201d\u5ae1\u4f20\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u5c06\u4e4b\u6321\u5374\uff0c\u8fd9\u4e0d\u4f46\u5927 \u5927\u51fa\u4e4e\u8bb8\u767d\u610f\u6599\u4e4b\u5916\uff0c\u5c31\u8fde\u4e00\u65c1\u89c2\u671b\u7684\u4e07\u5929\u840d\uff0c\u4ea6\u4e3a\u4e4b\u6697\u4e2d\u76b1\u7709\u81ea\u95ee\uff1a \u201c\u8fd9\u5c11\u5e74\u662f\u8c01\uff0c\u600e\u5730\u6709\u5982\u6b64\u6b66\u529f\uff1f\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u56db\u7ae0\u3000\u4e0d\u6b7b\u4e0d\u4f11>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u56db\u7ae0\u3000\u4e0d\u6b7b\u4e0d\u4f11 \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u76b1\u7740\u53cc\u7709\uff0c\u51dd\u89c6\u591a\u624b\u771f\u4eba\u4ee5\u53ca\u71d5\u5c71\u4e09\u5251\u7b49\u4eba\u7684\u8eab\u5f62\uff0c\u9010\u6e10\u6d88\u5931\u5728\u82cd\u83bd\u7684 \u5c71\u6797\u6df1\u5904\uff0c\u6b64\u523b\u4ea6\u4e3a\u5999\u624b\u8bb8\u767d\u7684\u8fd9\u4e00\u58f0\u66b4\u559d\u6240\u60ca\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u76ee\u5149\u56db\u626b\uff0c\u7a81\u5730\u5927\u6b65\u8d70\u4e86\u8fc7 \u6765\uff0c\u94c1\u9762\u5b64\u884c\u5ba2\u51b7\u54fc\u4e00\u58f0\uff0c\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u7b14\u76f4\u5730\u77aa\u5728\u4ed6\u7684\u9762\u4e0a\uff0c\u4ed6\u5374\u6beb\u4e0d\u5728\u610f\u5730\u5411\u4e07\u5929\u840d\u4e00 \u63d6\u53cc\u624b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u655d\u6559\u4e0e\u9601\u4e0b\u539f\u6765\u7d20\u65e0\u4ec7\u6028\uff0c\u4e8e\u4eca\u867d\u56e0\u6559\u4e3b\u592b\u4eba\u4e4b\u6545\uff0c\u4ee5\u81f4\u751f\u51fa\u8bb8\u591a\u4e8b\u7aef\uff0c\u4f46\u6b64\u523b\u655d\u6559 \u4e2d\u53c8\u751f\u51fa\u975e\u5e38\u4e4b\u53d8\uff0c\u5728\u4e0b\u7b49\u53ea\u6709\u6682\u4e14\u544a\u8f9e\uff0c\u65e5\u540e\u662f\u53cb\u662f\u654c\uff0c\u4e5f\u53ea\u6709\u4efb\u51ed\u9601\u4e0b\u81ea\u62e9\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5df2\u6084\u6084\u79fb\u81f3\u94b1\u7fca\u8eab\u4fa7\uff0c\u8bdd\u58f0\u4e00\u4e86\uff0c\u7a81\u5730\u4f38\u5934\u8fc7\u53bb\uff0c\u5728\u94b1\u7fca\u8033\u8fb9\u4f4e\u8bed \u4e24\u53e5\uff0c\u90a3\u77e5\u94b1\u7fca\u7684\u4e00\u53cc\u773c\u775b\uff0c\u5374\u4ecd\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u5999\u624b\u8bb8\u767d\u7684\u8eab\u4e0a\uff0c\u751f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u89c1\u4ed6 \u7684\u8bdd\u4f3c\u7684\u3002 \u3000\u3000\u6b64\u523b\u5728\u573a\u4f17\u4eba\uff0c\u5404\u6709\u6069\u6028\u7f20\u7ed3\uff0c\u60c5\u51b5\u4e4b\u590d\u6742\u5fae\u5999\uff0c\u7edd\u975e\u5c40\u5916\u4eba\u80fd\u591f\u4e86\u89e3\uff0c\u4f46\u5176\u4e2d\u5374\u53ea\u6709 \u4f0a\u98ce\u4e0e\u201c\u5929\u4e89\u6559\u201d\u4ec7\u6028\u6700\u6df1\uff0c\u6b64\u523b\u4ed6\u51b7\u773c\u770b\u7740\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u7684\u4e3e\u52a8\uff0c\u518d\u8f6c\u5411\u4e07\u5929\u840d\u51b7\u5cfb\u7684\u9762 \u5bb9\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u7a81\u5730 \u3000\u3000\u5999\u624b\u8bb8\u767d\u53c8\u662f\u4e00\u58f0\u66b4\u559d\uff0c\u811a\u6b65\u5fae\u9519\u95f4\uff0c\u8eab\u5f62\u5c55\u52a8\u5982\u864e\u8c79\uff0c\u5de6\u6307\u7b95\u5f20\u5982\u9e70\u722a\uff0c\u5de6\u624b\u4e00\u987a\uff0c \u624b\u638c\u4e00\u53cd\uff0c\u505c\u7559\u5728\u94b1\u7fca\u53f3\u4fa7\u7684\u7a7a\u95f4\uff0c\u53f3\u624b\u5374\u201c\u547c\u201d\u5730\u4e00\u638c\uff0c\u51fb\u5411\u94b1\u7fca\u5de6\u80f8\u7684\u201c\u671f\u95e8\u201d\u5927\u7a74\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u770b\u6765\u5e73\u5e73\u65e0\u5947\uff0c\u5374\u6b63\u662f\u201c\u5999\u624b\u201d\u8bb8\u767d\u4e0e\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\u5728\u65e0\u91cf\u5c71\u5dc5\u5341\u8f7d\u8f83\u6280\uff0c \u82e6\u5fc3\u7814\u521b\u7684\u4e00\u8bb0\u7edd\u62db\u3002 \u3000\u3000\u94b1\u7fca\u65b9\u624d\u4e0e\u5999\u624b\u8bb8\u767d\u7684\u786c\u5bf9\u4e24\u638c\uff0c\u5916\u8868\u770b\u6765\uff0c\u867d\u65e0\u53d8\u5316\uff0c\u5176\u5b9e\u4f53\u5185\u7684\u771f\u6c14\uff0c\u5374\u5df2\u7a0d\u6709\u6e83 \u6563\u4e4b\u8c61\uff0c\u6b64\u523b\u8bb8\u767d\u4e00\u62db\u653b\u6765\uff0c\u4ed6\u6765\u4e0d\u53ca\u8fd0\u7528\u601d\u5fd6\uff0c\u65b9\u5f85\u7529\u5de6\u80a9\uff0c\u66f2\u53f3\u819d\uff0c\u5288\u5f00\u8fd9\u4e00\u638c\u3002 \u3000\u3000\u90a3\u77e5\u4e07\u5929\u840d\u5374\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u5de6\u638c\u8d64\u624b\u64d2\u9f99\uff0c\u53f3\u638c\u51e4\u51f0\u5c55\u7fc5\uff0c\u8fdb\u53f3\u8db3\uff0c\u8e0f\u4e2d\u5bab\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u8bf4\u5f97\u6781\u5feb\uff0c\u51e0\u4e4e\u6709\u5982\u73e0\u843d\u7389\u76d8\uff0c\u94b1\u7fca\u5fc3\u5ff5\u52a8\u5904\uff0c\u53e3\u4e2d\u5410\u6c14\u5f00\u58f0\uff0c\u5de6\u638c\u4e00\u66f2\u4e00\u4f38\uff0c \u66f2\u4f38\u4e4b\u95f4\uff0c\u679c\u7136\u51fb\u51fa\u4e00\u62db\u201c\u8d64\u624b\u64d2\u9f99\u201d\uff0c\u4f46\u53f3\u638c\u5374\u672a\u6709\u4e3e\u52a8\uff0c\u539f\u6765\u4e07\u5929\u840d\u6240\u8bf4\u7684\u8bdd\uff0c\u4ed6\u53ea\u542c \u6e05\u695a\u4e86\u524d\u9762\u4e00\u53e5\u3002 \u3000\u3000\u4e07\u5929\u840d\u53cc\u7709\u5fae\u76b1\uff0c\u4f4e\u53f1\u9053\uff1a \u3000\u3000\u201c\u8822\u624d\uff01\u201d \u3000\u3000\u4e03\u6d77\u6e14\u5b50\u97e6\u50b2\u7269\u540e\u9000\u4e09\u6b65\uff0c\u89c1\u5230\u94b1\u7fca\u679c\u7136\u65bd\u51fa\u4e00\u62db\u201c\u8d64\u624b\u64d2\u9f99\u201d\uff0c\u5fc3\u4e2d\u5927\u60ca\uff0c\u8981\u77e5\u9053\u4ee5 \u6b66\u5b66\u5e38\u89c4\u800c\u8a00\uff0c\u5bf9\u6297\u201c\u5999\u624b\u201d\u8bb8\u767d\u8fd9\u4e00\u62db\uff0c\u53ea\u8981\u7529\u5de6\u80a9\uff0c\u66f2\u5de6\u819d\uff0c\u7136\u540e\u53f3\u638c\u8fdb\u51fb\u624d\u662f\u6b63\u7406\uff0c \u4ed6\u4e0d\u7981\u6697\u602a\u94b1\u7fca\uff0c\u600e\u5730\u542c\u4ece\u8d77\u81ea\u5df1\u654c\u624b\u7684\u670b\u53cb\u7684\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u5176\u4e2d\u53ea\u6709\u4f0a\u98ce\u77e5\u9053\u201c\u5999\u624b\u201d\u8bb8\u767d\u4e0e\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929\u840d\u4e4b\u95f4\u7684\u4ec7\u6028\uff0c\u4e5f\u77e5\u9053\u4ed6\u4eec\u5728 \u65e0\u91cf\u5c71\u5dc5\u82e6\u6597\u5341\u5e74\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u4e5f\u77e5\u9053\u4e07\u5929\u840d\u53e3\u4e2d\u8bf4\u51fa\u7684\u62db\u5f0f\uff0c\u5fc5\u5b9a\u662f\u4e13\u7834\u201c\u5999\u624b\u201d\u8bb8\u767d\u65bd\u51fa\u7684\u8fd9\u4e00\u62db\u5999\u7740\u3002 \u3000\u3000\u53ea\u89c1\u201c\u5999\u624b\u201d\u8bb8\u767d\u95f7\u54fc\u4e00\u58f0\uff0c\u679c\u7136\u6536\u638c\u65cb\u8eab\uff0c\u64a4\u56de\u8fdb\u51fb\u4e4b\u52bf\uff0c\u8fd9\u65f6\u94b1\u7fca\u5982\u679c\u5b8c\u5168\u4f9d\u7167\u4e07 \u5929\u840d\u6240\u8bf4\u7684\u62db\u5f0f\uff0c\u201c\u8fdb\u53f3\u8db3\uff0c\u8e0f\u4e2d\u5bab\u201d\uff0c\u4fbf\u5fc5\u5b9a\u80fd\u5728\u8fd9\u4e00\u573a\u4e89\u6597\u4e2d\u62a2\u5f97\u5148\u673a\u3002 \u3000\u3000\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u5927\u611f\u60ca\u5f02\u5730\u4fa7\u987e\u4e07\u5929\u840d\u4e00\u773c\uff0c\u53ea\u89c1\u8fd9\u51b7\u5cfb\u7626\u524a\u7684\u8001\u4eba\u53c8\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5de6\u624b\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\uff0c\u53f3\u624b\u201c\u8d64\u624b\u64d2\u9f99\u201d\uff0c\u8fdb\u5de6\u8db3\uff0c\u8e0f\u4e2d\u5bab\u3002\u201d \u3000\u3000\u94b1\u7fca\u6697\u4e2d\u4e00\u54ac\u7259\uff0c\u5de6\u638c\u659c\u6325\uff0c\u53f3\u638c\u66f2\u4f38\uff0c\u679c\u7136\u662f\u5de6\u624b\u64d2\u9f99\uff0c\u53f3\u638c\u5c55\u7fc5\uff0c\u5999\u624b\u8bb8\u767d\u6d53\u7709\u6012 \u8f69\uff0c\u76ee\u5149\u7cbe\u5149\u66b4\u5c04\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u731b\u65cb\u8eab\u8eaf\uff0c\u5f6a\u4f1f\u7684\u8eab\u5f62\uff0c\u7a81\u5730\u6251\u5411\u4e07\u5929\u840d\u3002 \u3000\u3000\u8fd9\u9661\u7136\u53d1\u751f\u4e4b\u53d8\uff0c\u4f7f\u5f97\u97e6\u50b2\u7269\uff0c\u94b1\u7fca\u9f50\u5730\u4e00\u6115\uff0c\u5374\u89c1\u8bb8\u767d\u5df2\u81ea\u5389\u559d\u9053\uff1a \u3000\u3000\u201c\u59d3\u4e07\u7684\uff0c\u8fd8\u6211\u7684\u8840\u6765\uff01\u201d \u3000\u3000\u559d\u58f0\u51c4\u5389\u5c16\u9510\uff0c\u4e16\u95f4\u51e0\u4e4e\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u8bed\u8a00\u6587\u5b57\u80fd\u591f\u5f62\u5bb9\u8fd9\u79cd\u58f0\u97f3\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u58f0 \u97f3\uff0c\u53ef\u4e0e\u8fd9\u79cd\u58f0\u97f3\u6bd4\u62df\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4f0a\u98ce\uff0c\u94b1\u7fca\uff0c\u97e6\u50b2\u7269\uff0c\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4f0a\u98ce\u8111\u6d77\u4e4b \u4e2d\uff0c\u4f3c\u53c8\u6cdb\u8d77\u65e0\u91cf\u5c71\u5dc5\uff0c\u90a3\u201c\u5929\u661f\u79d8\u7a9f\u201d\u4e2d\u51c4\u60e8\u9634\u68ee\u7684\u666f\u8c61\uff0c\u4e00\u4e2a\u6ee1\u8eab\u8840\u8ff9\uff0c\u9762\u76ee\u72f0\u72de\u7684\u67af \u7626\u8001\u4eba\uff0c\u6b63\u4f0f\u5728\u53e6\u4e00\u4e2a\u6ee1\u8eab\u8840\u8ff9\u7684\u8001\u4eba\u8eab\u4e0a\u72c2\u542e\u9c9c\u8840\uff01 \u3000\u3000\u4f0a\u98ce\u7d20\u6765\u80c6\u6c14\u751a\u8c6a\uff0c\u4f46\u6b64\u523b\u4e00\u634f\u638c\u5fc3\uff0c\u6ee1\u638c\u4ff1\u662f\u51b7\u6c57\u3002 \u3000\u3000\u94b1\u7fce\uff0c\u97e6\u50b2\u7269\u867d\u7136\u5bf9\u6b64\u4e8b\u7684\u771f\u76f8\u4e1d\u6beb\u4e0d\u77e5\uff0c\u4f46\u542c\u4e86\u8bb8\u767d\u7684\u8fd9\u79cd\u5389\u559d\u4e4b\u58f0\uff0c\u4ea6\u4e0d\u7981\u611f\u5230\u4e00 \u9635\u5bd2\u610f\uff0c\u81ea\u80cc\u810a\u5347\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u5999\u624b\u8bb8\u767d\u6b64\u523b\u8eab\u5f62\u6709\u5982\u75af\u864e\uff0c\u6325\u624b\u62ac\u8db3\u95f4\uff0c\u4ff1\u662f\u7acb\u53ef\u5236\u4eba\u6b7b\u547d\u7684\u7edd\u62db\uff0c\u8fd9\u4e24\u4eba\u52a8\u624b \u4e4b\u6fc0\u70c8\uff0c\u548c\u65b9\u624d\u4e0e\u94b1\u7fca\u52a8\u4e3b\u65f6\u53c8\u4e0d\u77e5\u8981\u6d53\u70c8\u591a\u5c11\uff0c\u4f0a\u98ce\u6df1\u77e5\u4ed6\u4e8c\u4eba\u4e4b\u95f4\u7684\u4e0d\u53ef\u5316\u89e3\u7684\u6028\u4ec7\uff0c \u77e5\u9053\u82e5\u60f3\u5c06\u8fd9\u4e24\u4eba\u5316\u89e3\u5f00\u6765\uff0c\u771f\u662f\u96be\u5982\u767b\u5929\u3002 \u3000\u3000\u97e6\u50b2\u7269\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u94b1\u7fca\u4ecd\u5446\u5446\u5730\u671b\u7740\u8fd9\u4e24\u4eba\u7684\u8eab\u5f62\uff0c\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u53c8\u81ea\u4f38\u5934\u8fc7 \u53bb\uff0c\u5728\u4ed6\u8033\u7554\u4f4e\u8bed\u4e24\u53e5\u3002 \u3000\u3000\u6b64\u6b21\u94b1\u7fca\u9762\u5bb9\u65b9\u81ea\u4e3a\u4e4b\u4e00\u53d8\uff0c\u76ee\u5149\u8f6c\u52a8\u534a\u664c\uff0c\u7a81\u5730\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u4e09\u5e74\u4e4b\u540e\uff0c\u94b1\u7fca\u518d\u5728\u6b64\u5730\u606d\u5019\u9601\u4e0b\u7684\u5927\u9a7e\u3002\u201d \u3000\u3000\u6b63\u81ea\u52a8\u7740\u624b\u7684\u5999\u624b\u8bb8\u767d\u5927\u7b11\u51e0\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u4e09\u5e74\u4e4b\u540e\u2026\u2026\u201d\u4e00\u65cb\u8eab\u8eaf\uff0c\u500f\u7136\u51fb\u51fa\u56db\u638c\uff0c\u8fd9\u56db\u638c\u653b\u53bb\uff0c\u4e0d\u4f46\u6709\u5982\u96f7\u8f70\u7535 \u51fb\uff0c\u58f0\u5a01\u7edd\u4f26\uff0c\u800c\u4e14\u51fa\u62db\u90e8\u4f4d\uff0c\u66f4\u662f\u5927\u5927\u51fa\u4e4e\u5e38\u89c4\u4e4b\u5916\uff0c\u94b1\u7fca\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6b64\u4eba\u7684\u6b66\u529f\uff0c\u600e\u5730\u5982\u6b64\u5947\u5999\uff1a\u201d\u773c\u4e00\u5782\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u5411\u97e6\u50b2\u7269\u6c88\u58f0\u9053\uff1a \u3000\u3000\u201c\u90a3\u8fb9\u7684\u4e8b\uff0c\u4f60\u4eec\u81ea\u53bb\u6599\u7406\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u500f\u7136\u4f4f\u58c1\uff0c\u731b\u4e00\u957f\u8eab\uff0c\u62c2\u8896\u800c \u53bb\u3002 \u3000\u3000\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u9762\u5bb9\u4e00\u53d8\uff0c\u6115\u7136\u9053\uff1a \u3000\u3000\u201c\u94b1\u5927\u4fa0\uff0c\u4f60\u2014\u2014\u201d\u4f46\u4ed6\u8bdd\u58f0\u5c1a\u672a\u8bf4\u51fa\uff0c\u94b1\u7fca\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u5df2\u63a0\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u970e\u773c\u4e4b \u95f4\uff0c\u4fbf\u5df2\u6d88\u5931\u5728\u5c71\u8def\u6df1\u5904\u3002 \u3000\u3000\u4ed6\u5446\u7acb\u4e86\u534a\u664c\uff0c\u7a81\u5730\u4e00\u65cb\u8eab\u5f62\uff0c\u5411\u65b9\u624d\u8c22\u96e8\u4ed9\u63a0\u53bb\u7684\u5c71\u6797\u5954\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u65b9\u5f85\u8ddf\u53bb\uff0c\u4f46\u8f6c\u76ee\u671b\u5904\uff0c\u8bb8\u767d\uff0c\u4e07\u5929\u840d\u4e24\u4eba\uff0c\u6b64\u523b\u76f8\u640f\u4e4b\u70c8\uff0c\u7adf\u6bd4\u65b9\u624d \u66f4\u5f62\u9669\u6076\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u82e5\u4e5f\u4e00\u8d70\uff0c\u8fd9\u4e24\u4eba\u6b64\u756a\u4e4b\u6597\uff0c\u4fbf\u662f\u4e0d\u6b7b\u4e0d\u4f11\uff0c\u6b64\u523b\u8c01\u4e5f\u4e0d\u77e5\u9053\u662f\u8c01\u4f1a \u4f24\u5728\u8c01\u7684\u638c\u4e0b\uff0c\u751a\u81f3\u4ed6\u4e8c\u4eba\u4f1a\u4e00\u9f50\u6b7b\u5728\u6b64\u5730\u4ea6\u672a\u53ef\u77e5\u3002 \u3000\u3000\u4ed6\u4e0e\u8fd9\u4e24\u4eba\u4e4b\u95f4\uff0c\u867d\u65e0\u6069\u4e49\u53ef\u8a00\uff0c\u4f46\u4ed6\u751f\u5177\u81f3\u6027\uff0c\u5374\u4e5f\u4e0d\u5fcd\u5c31\u6b64\u4e00\u8d70\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u80f8\u4e2d\uff0c\u601d\u6f6e\u51b2\u51fb\uff0c\u867d\u7136\u660e\u77e5\u4ee5\u81ea\u5df1\u7684\u6b66\u529f\u6839\u57fa\uff0c\u82e5\u5bf9\u8fd9\u4e24\u4eba\u5bf9\u624b\u7684\u62db\u5f0f\u52a0\u4ee5\u7422 \u78e8\uff0c\u5fc5\u5b9a\u83b7\u76ca\u975e\u6d45\uff0c\u4f46\u4ed6\u5374\u8fde\u770b\u4e5f\u672a\u770b\u4e00\u773c\uff0c\u4fef\u9996\u6c88\u601d\u534a\u664c\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u4e07\u8679\uff0c\u4f60\u600e\u4e48\u6837\u4e86\uff01\u201d\u8dc3\u8d77\u8eab\u5f62\uff0c\u95ea\u7535\u822c\u5411\u90a3\u8fb9\u5c71\u6797\u63a0\u53bb\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u867d\u7136\u4e00\u751f\u51b7\u9177\uff0c\u4f46\u4e00\u542c\u201c\u4e07\u8679\u201d\u4e24\u5b57\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u5927\u52a8\uff0c\u4e00\u638c\u6325\u53bb\u201c\u5999 \u624b\u201d\u8bb8\u767d\u7684\u638c\u5f0f\uff0c\u5927\u559d\u9053\uff1a \u3000\u3000\u201c\u8679\u513f\uff0c\u4f60\u600e\u7684\u4e86\uff1f\u201d\u8eab\u5f62\u540e\u63a0\u4e94\u5c3a\uff0c\u518d\u4e5f\u4e0d\u7406\u8bb8\u767d\uff0c\u8ddf\u8e2a\u4f0a\u98ce\u800c\u53bb\u3002 \u3000\u3000\u4e16\u4eba\u4e00\u751f\u4e4b\u4e2d\uff0c\u7eb5\u7136\u65e0\u4f55\u52a8\u5fc3\uff0c\u5374\u603b\u6709\u4e00\u4ef6\u8db3\u4ee5\u4ee4\u4ed6\u52a8\u5fc3\u4e4b\u4e8b\uff0c\u4f0a\u98ce\u77e5\u9053\u82e5\u60f3\u8fd9\u4e24\u4eba\u6b64 \u523b\u4e4b\u6597\u6682\u65f6\u89e3\u5f00\uff0c\u4fbf\u53ea\u6709\u4ee5\u4e00\u4e8b\u4ee5\u6253\u52a8\u4ed6\u4e24\u4eba\u5176\u4e2d\u4efb\u4f55\u4e00\u4eba\u4e4b\u5fc3\uff0c\u4f46\u8fd9\u4e24\u4eba\u5374\u53c8\u90fd\u662f\u6027\u60c5\u5927 \u5f02\u5e38\u4eba\u7684\u6b66\u6797\u5f02\u4eba\uff0c\u8981\u60f3\u51fa\u4e00\u4ef6\u8db3\u4ee5\u4ee4\u4ed6\u4eec\u52a8\u5fc3\u4e4b\u4e8b\uff0c\u5b9e\u662f\u5927\u4e3a\u56f0\u96be\uff0c\u4f46\u60f3\u6765\u60f3\u53bb\uff0c\u9664\u4e86\u7236 \u5973\u4e4b\u60c5\u6216\u53ef\u6709\u7528\u4e4b\u5916\uff0c\u7b80\u76f4\u522b\u65e0\u4ed6\u6cd5\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u867d\u672a\u770b\u5230\u4e07\u8679\uff0c\u5374\u90a3\u822c\u5927\u559d\u4e00\u58f0\uff0c\u6b64\u523b\u56de\u76ee\u671b\u53bb\uff0c\u89c1\u5230\u4e07\u5929\u840d\u679c\u5df2\u968f\u540e\u8ddf\u6765\uff01\u5fc3 \u4e2d\u6697\u559c\uff0c\u811a\u6b65\u5374\u66f4\u52a0\u5feb\u4e86\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u5fae\u5fae\u4e00\u6014\uff0c\u6697\u54fc\u4e00\u58f0\uff0c\u51b7\u7b11\u4f4e\u8bed\u9053\uff1a \u3000\u3000\u201c\u4f60\u8d70\u5230\u5929\u8fb9\uff0c\u6211\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u4f60\uff01\u201d \u3000\u3000\u5f53\u4e0b\u4ed6\u4ea6\u81ea\u5c55\u52a8\u8eab\u5f62\uff0c\u968f\u540e\u63a0\u53bb\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u4ff1\u6709\u7740\u4e00\u8eab\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\u7684\u6b66\u529f\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u90fd\u5df2\u63a0\u5165\u5c71\u6797\uff0c\u6797\u4e2d\u6811\u53f6\u5fae\u98a4\uff0c \u9e1f\u8bed\u5541\u557e\uff0c\u4f3c\u662f\u5b89\u9759\u5df2\u6781\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u53f6\u52a8\u9e1f\u8bed\u4e4b\u58f0\u4e2d\uff0c\u7a81\u5730\u9690\u9690\u4f20\u51fa\u4e00\u9635\u9635\u75db\u82e6\u7684\u547b\u541f\u4e4b\u58f0\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8f6c\u800c\u5411\u8fd9\u547b\u541f\u58f0\u4f20\u51fa\u7684\u65b9\u5411\u63a0\u53bb\uff0c\u4e07\u5929\u840d\u542c\u4e86\u8fd9\u547b\u541f\u4e4b\u58f0\uff0c\u5fc3\u4e2d\u5927\u4e3a\u60f6 \u6025\uff0c\u8131\u53e3\u558a\u9053\uff1a \u3000\u3000\u201c\u8679\u513f\uff0c\u662f\u4f60\u4e48\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u55d6\u7136\u4e8c\u4e2a\u8d77\u843d\uff0c\u7a7f\u8fc7\u6d53\u5bc6\u7684\u6811\u5e72\uff0c\u63a0\u81f3\u4f0a\u98ce\u8eab\u4fa7\uff0c\u53c8 \u9053\uff1a \u3000\u3000\u201c\u90a3\u91cc\u662f\u8679\u513f\u4e48\uff0c\u4f60\u600e\u7684\u4e86\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u5fae\u4e00\u6447\u5934\uff0c\u811a\u4e0b\u4e0d\u505c\uff0c\u63a0\u4e86\u8fc7\u53bb\uff0c\u6b64\u5904\u5c71\u6797\u751f\u5bc6\uff0c\u5730\u52bf\u5374\u6108\u8d70\u6108\u4f4e\uff0c\u524d\u884c\u5341\u4f59\u4e08\uff0c \u6797\u6728\u63a9\u6620\u5904\uff0c\u7a81\u5730\u6709\u4eba\u5f71\u6643\u52a8\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u65b9\u624d\u7a81\u7136\u8d70\u4e86\u7684\u201c\u71d5\u5c71\u4e09\u5251\u201d\u7b49\u4eba\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u6b64\u523b\u5fc3\u4e71\u5982\u9ebb\uff0c\u63d0\u6c14\u7eb5\u8eab\uff0c\u95ea\u7535\u822c\u63a0\u53bb\uff0c\u53ea\u89c1\u201c\u71d5\u5c71\u4e09\u5251\u201d\uff0c\u52b3\u5c71\u4e09 \u5251\u201d\uff0c\u201c\u5357\u5bab\u53cc\u674e\u201d\uff0c\u4ee5\u53ca\u8c22\u96e8\u4ed9\uff0c\u97e6\u50b2\u7269\u7b49\u4eba\uff0c\u90fd\u5446\u7acb\u5728\u4e00\u4e1b\u6742\u6811\u4e4b\u524d\uff0c\u800c\u4e0d\u65ad\u7684\u547b\u541f \u58f0\uff0c\u4fbf\u4e5f\u662f\u4ece\u90a3\u6742\u6811\u4e1b\u4e2d\u4f20\u51fa\uff01 \u3000\u3000\u8fd9\u4e9b\u4eba\u89c1\u4e86\u4e07\u5929\u840d\u5165\u6797\uff0c\u5374\u53ea\u56de\u5934\u671b\u4e86\u4e00\u773c\uff0c\u968f\u5373\u76ee\u6ce8\u6811\u4e1b\uff0c\u6ee1\u9762\u4ff1\u662f\u7126\u6025\uff0c\u60f6\u4e71\u4e4b \u8272\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e07\u5929\u840d\u6c88\u58f0\u9053\uff1a \u3000\u3000\u201c\u91cc\u9762\u662f\u8c01\uff1f\u201d \u3000\u3000\u6b64\u523b\u4ed6\u5df2\u542c\u51fa\u8fd9\u547b\u541f\u58f0\u4e0d\u662f\u4ed6\u5973\u513f\u7684\u58f0\u97f3\uff0c\u662f\u4ee5\u5fc3\u4e2d\u4e00\u5b9a\uff0c\u5374\u89c1\u8fd9\u4e9b\u4eba\u5404\u5404\u5bf9\u671b\u4e00\u773c\uff0c \u8fd8\u662f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4e1b\u6811\u4e2d\u7684\u547b\u541f\u58f0\u66f4\u89c1\u6fc0\u70c8\uff0c\u8c22\u96e8\u4ed9\uff0c\u97e6\u50b2\u7269\u7b49\u4eba\u9762\u4e0a\u4ff1\u90fd\u73b0\u51fa\u7d27\u5f20\u4e4b\u8272\uff0c\u5404\u4eba\u9762\u9762\u76f8 \u89d1\uff0c\u8c01\u90fd\u6ca1\u6709\u5411\u90a3\u6811\u4e1b\u4e2d\u770b\u4e0a\u4e00\u773c\uff0c\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u867d\u7136\u4e45\u8d70\u6c5f\u6e56\uff0c\u6c5f\u6e56\u9605\u5386\u751a\u4e30\uff0c\u4f46\u5374\u4e5f \u4ece\u672a\u89c1\u8fc7\u8fd9\u7b49\u5927\u51fa\u5e38\u60c5\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u5927\u611f\u60ca\u5f02\uff1a \u3000\u3000\u201c\u5728\u8fd9\u91cc\u9762\u547b\u541f\u7684\u4eba\u662f\u8c01\u5462\uff1f\u770b\u6765\u4e0e\u8fd9\u73ed\u5929\u4e89\u6559\u5f92\u90fd\u5927\u6709\u5173\u7cfb\uff0c\u4f46\u4ed6\u4eec\u600e\u5730\u90fd\u4e0d\u8fdb\u53bb\u5e2e \u52a9\u4e8e\u5979\uff1f\u96be\u9053\u8fd9\u91cc\u9762\u6709\u4ec0\u80fd\u8db3\u4ee5\u4ee4\u4ed6\u4eec\u754f\u60e7\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5728\u5bf9\u8fd9\u5973\u5b50\u65bd\u4ee5\u9177\u5211\u5417\uff1f\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u4e94\u7ae0\u3000\u74dc\u719f\u8482\u843d>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u4e94\u7ae0\u3000\u74dc\u719f\u8482\u843d \u3000\u3000\u6b64\u523b\u4f0a\u98ce\u4e5f\u5df2\u63a0\u4e86\u8fc7\u6765\uff0c\u811a\u6b65\u987f\u5904\uff0c\u51dd\u795e\u4e00\u542c\u8fd9\u6742\u6811\u4e1b\u4e2d\u7684\u547b\u541f\u4e4b\u58f0\uff0c\u9762\u8272\u7a81\u5730\u5927\u53d8\uff01 \u55d6\u5730\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u6811\u4e1b\u65c1\u8fb9\uff0c\u4f3c\u4e4e\u7a81\u53c8\u60f3\u8d77\u4ec0\u4e48\uff0c\u500f\u7136\u6b62\u4f4f\uff0c\u5012\u9000\u4e24\u6b65\uff0c\u76ee\u5149\u8f6c\u5411\u201c\u52b3\u5c71 \u4e09\u5251\u201d\u8eab\u4e0a\uff0c\u52b3\u5c71\u4e09\u5251\u5144\u5f1f\u4e09\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u5411\u4f0a\u98ce\u5fae\u4e00\u9894\u9996\uff0c\u5927\u5bb6\u5fc3\u91cc\u4fbf\u4ff1\u90fd\u6709\u6570\uff0c\u4f46\u5374\u4ecd \u90fd\u6ca1\u6709\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u6797\u68a2\u6811\u6728\u54d7\u5730\u4e00\u58f0\uff0c\u6728\u53f6\u7eb7\u98de\u5904\uff0c\u843d\u4e0b\u4e00\u6761\u4eba\u5f71\uff0c\u4e07\u5929\u840d\u4fa7\u76ee\u4e00\u770b\uff0c\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u6e9c\u7684\u3002\u201d \u3000\u3000\u8fd9\u4eba\u4ea6\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u6e9c\u4e5f\u6e9c\u4e0d\u6389\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4eba\u4fbf\u662f\u201c\u5999\u624b\u201d\u8bb8\u767d\uff0c\u65b9\u624d\u4ed6\u8d77\u6b65\u8f83\u8fdf\uff0c\u662f\u4ee5\u8eab\u5f62\u843d\u540e\uff0c\u5230\u4e86\u6797\u4e2d\uff0c\u4e07\u5929\u840d\uff0c\u4f0a \u98ce\u4e24\u4eba\u4ff1\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u4ed6\u542c\u5230\u8fd9\u547b\u541f\u58f0\uff0c\u4fbf\u4e5f\u8ffd\u6765\uff0c\u4f46\u6797\u4e2d\u6811\u6728\u751a\u5bc6\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u8010\uff0c\u662f\u4ee5\u4fbf \u8dc3\u5230\u6797\u68a2\uff0c\u65bd\u5c55\u4ed6\u72ec\u6b65\u5929\u4e0b\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u81ea\u6797\u68a2\u63a0\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u547b\u541f\u4e4b\u58f0\u7a81\u6b62\uff0c\u4f46\u5927\u5bb6\u5374\u66f4\u90fd\u4f3c\u7d27\u5f20\u7684\u900f\u4e0d\u51fa\u6c14\u6765\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u8f6c\u76ee\u56db\u671b\uff0c\u89c1\u5230\u8fd9\u4e9b\u4eba\u7684\u795e\u8272\uff0c\u4ea6\u662f\u5927\u611f\u60ca\u5947\uff0c\u53e3\u4e2d\u4f4e\u9a82\u4e00\u53e5\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u4e5f\u4e0d\u8fdb\u53bb\u770b\u770b\uff1f\u201d\u8bf4\u5b8c\uff0c\u7adf\u5927\u6b65\u5411\u6797\u4e2d\u8d70\u53bb\uff0c\u201c\u591a\u624b\u771f\u4eba\u201d\uff0c\u201c\u4e03\u6d77\u6e14 \u5b50\u201d\uff0c\u201c\u71d5\u5c71\u4e09\u5251\u201d\u4e00\u9f50\u6a2a\u8eab\u6321\u5728\u4ed6\u9762\u524d\uff0c\u5999\u624b\u8bb8\u767d\u53c8\u5927\u9a82\u4e00\u58f0\uff0c\u72e0\u72e0\u77aa\u4e86\u4ed6\u4eec\u51e0\u773c\u65b9\u5f85\u518d \u6b21\u559d\u95ee\uff0c\u5374\u89c1\u4f0a\u98ce\u8eab\u5f62\u4e00\u98d8\uff0c\u63a0\u5230\u4ed6\u8eab\u4fa7\uff0c\u5728\u4ed6\u8033\u4f34\u4f4e\u8bed\u4e24\u53e5\u3002 \u3000\u3000\u201c\u5999\u624b\u201d\u8bb8\u767d\u4e5f\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u5730\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u6709\u4eba\u5728\u91cc\u9762\u751f\u5b69\u5b50\u3002\u201d\u4f4e\u58f0\u5411\u4f0a\u98ce\u95ee\u9053\uff1a \u3000\u3000\u201c\u5c31\u662f\u4f60\u90a3\u5a18\u513f\u4eec\u5417\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u6b64\u523b\u5fc3\u4e2d\u6b63\u662f\u7f9e\u60ed\uff0c\u607c\u6012\uff0c\u4ea4\u76f8\u7eb7\u81f3\uff0c\u95fb\u8a00\u4e5f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u56de\u7b54\uff0c\u201c\u591a\u624b\u771f\u4eba\u201d\u8c22 \u96e8\u4ed9\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\u9053\uff1a \u3000\u3000\u201c\u8981\u662f\u6709\u4eba\u5bf9\u6211\u4eec\u6559\u4e3b\u592b\u4eba\u4e0d\u656c\uff0c\u90a3\u4ed6\u4e0d\u7ba1\u662f\u8c01\uff1f\u90fd\u8981\u5012\u9709\u4e86\u3002\u201d \u3000\u3000\u5999\u624b\u8bb8\u767d\u6a2a\u767e\u4e00\u671b\uff0c\u5374\u542c\u4e07\u5929\u840d\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u81ea\u5df1\u7684\u4e8b\u8fd8\u672a\u4e86\uff0c\u5374\u53c8\u7ba1\u8d77\u4eba\u5bb6\u7684\u95f2\u4e8b\u6765\u4e86\uff0c\u771f\u662f\u6df7\u5e10\u3002\u201d \u3000\u3000\u8bb8\u767d\u53cc\u62f3\u4e00\u63e1\uff0c\u7f13\u7f13\u8d70\u5230\u4e07\u5929\u840d\u8eab\u524d\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u53c8\u662f\u5251\u62d4\u5f29\u5f20\u8d77\u6765\uff0c\u201c\u4e03\u6d77\u6e14 \u5b50\u201d\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5728\u5987\u4eba\u751f\u4ea7\u4e4b\u5730\u77aa\u773c\u53d1\u5a01\uff0c\u54fc\uff0c\u6211\u771f\u4e0d\u77e5\u9053\u8fd9\u7b97\u662f\u90a3\u95e8\u5b50\u597d\u6c49\uff01\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u6811\u6797\u4e2d\u547b\u541f\u4e4b\u58f0\u53c8\u8d77\uff0c\u4f17\u4eba\u5fc3\u4e2d\u867d\u662f\u5927\u4e3a\u7126\u6025\uff0c\u4f46\u8c01\u4e5f\u4e0d\u4fbf\u5f80\u91cc\u518d\u8d70\u4e00\u6b65\uff0c \u201c\u5999\u624b\u201d\u8bb8\u767d\u80f8\u819b\u8d77\u4f0f\u4e24\u4e0b\uff0c\u4f3c\u4e4e\u5728\u5f3a\u5fcd\u7740\u5fc3\u80f8\u4e2d\u7684\u6012\u706b\uff0c\u7136\u540e\u5bf9\u4e07\u5929\u840d\u51b7\u54fc\u4e00\u70b9\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u6211\u4e4b\u95f4\uff0c\u5e10\u8fd8\u672a\u6e05\uff0c\u4f60\u7ad9\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u51fa\u53bb\u518d\u5e72\u4e00\u573a\u3002\u201d \u3000\u3000\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u6b64\u5730\u6839\u672c\u4e0d\u5173\u5404\u4f4d\u7684\u4e8b\uff0c\u5404\u4f4d\u8fd8\u662f\u51fa\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u8fd9\u51e0\u4eba\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u90fd\u6781\u4e3a\u4f4e\u6c89\uff0c\u56e0\u4e3a\u8c01\u4e5f\u4e0d\u613f\u60ca\u52a8\u91cc\u9762\u7684\u4ea7\u5987\u3002 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u6811\u6797\u6df1\u5904\uff0c\u7a81\u5730\u4f20\u6765\u4e00\u9635\u9635\u201c\u54af\u54af\u201d\u7684\u7b11\u58f0\uff0c\u4ee5\u53ca\u4e00\u9635\u9635\u5a07\u8106\u7684\u559d\u9a82\u58f0\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4e00\u6761\u5a40\u5a1c\u7684\u4eba\u5f71\uff0c\u6781\u5feb\u5730\u7531\u6797\u5916\u63a0\u6765\uff0c\u89c1\u7740\u8fd9\u4e48\u591a\u4eba\uff0c\u4f3c\u4e4e\u4e3a\u4e4b\u4e00\u60ca\uff0c\u505c\u4e0b\u811a \u6b65\uff0c\u671b\u4e86\u4e24\u773c\uff0c\u4f46\u968f\u5373\u81ea\u987e\u8c03\u5f04\u6000\u4e2d\u6240\u62b1\u7684\u4e00\u4e2a\u5a74\u513f\uff0c\u518d\u4e5f\u4e0d\u671b\u4f17\u4eba\u4e00\u773c\uff0c\u4e00\u9762\u4e0d\u4f4f\u7684\u5a07\u7b11 \u7740\u3002 \u3000\u3000\u4f46\u4f17\u4eba\u4e00\u89c1\u8fd9\u4eba\u5f71\u4e4b\u9762\uff0c\u5374\u90fd\u4e0d\u7981\u6697\u4e2d\u4e00\u60ca\uff0c\u53ea\u89c1\u8fd9\u4eba\u8eab\u5f62\u5a40\u5a1c\uff0c\u4f53\u6001\u66fc\u5999\uff0c\u4f46\u5374\u53d1\u9afb \u677e\u4e71\uff0c\u8eab\u4e0a\u7a7f\u7740\u7684\u8863\u670d\uff0c\u66f4\u662f\u7834\u70c2\u4e0d\u582a\uff0c\u671d\u5979\u8138\u4e0a\u4e00\u671b\uff0c\u4f17\u4eba\u66f4\u4e0d\u7981\u4ff1\u90fd\u81ea\u5fc3\u4e2d\u5012\u5438\u4e00\u53e3\u51c9 \u6c14\uff0c\u8fd9\u4e9b\u4eba\u4e00\u751f\u4e4b\u4e2d\uff0c\u6240\u89c1\u6240\u95fb\u867d\u90fd\u6781\u5e7f\uff0c\u4f46\u5374\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u96be\u770b\u7684\u9762\u5b54\u3002 \u3000\u3000\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u89c1\u4e86\u6b64\u4eba\uff0c\u9762\u5bb9\u4ea6\u4e0d\u7981\u4e00\u53d8\uff0c\u540e\u9000\u4e24\u6b65\u3002 \u3000\u3000\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u97e6\u50b2\u7269\u5fae\u4e00\u5b9a\u795e\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\u662f\u8c01\uff1f\u62b1\u7740\u655d\u6d3e\u6559\u4e3b\u7684\u5b69\u5b50\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u77e5\u8fd9\u5973\u5b50\u5374\u4f3c\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u4f0a\u98ce\u4e00\u89c1\u8fd9\u5973\u5b50\u4e4b\u9762\uff0c\u5468\u8eab\u6709\u5982\u88ab\u96f7\u7535\u6240\u51fb\uff0c\u51e0 \u4e4e\u518d\u4e5f\u52a8\u5f39\u4e0d\u5f97\uff0c\u76f4\u5230\u6b64\u523b\u65b9\u81ea\u5b9a\u8fc7\u795e\u6765\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u8fc7\u53bb\uff0c\u4e00\u628a\u6293\u4f4f\u5979\u7684\u80a9\u8180\uff0c\u60f6\u58f0\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff0c\u4f60\u600e\u7684\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u53d1\u9afb\u677e\u84ec\uff0c\u8863\u886b\u51cc\u4e71\uff0c\u9762\u4e0a\u4f24\u75d5\u7d2f\u7d2f\uff0c\u663e\u5f97\u6781\u5176\u4e11\u602a\u7684\u5973\u5b50\uff0c\u4fbf\u662f\u6614\u65e5\u5149\u53ef\u9274 \u4eba\uff0c\u8863\u886b\u4fee\u6d01\uff0c\u7f8e\u4fcf\u4e3d\uff0c\u9ad8\u50b2\u4f46\u5374\u591a\u60c5\u7684\u201c\u8427\u6e58\u5983\u5b50\u201d\u8427\u5357\u82f9\u3002 \u3000\u3000\u6b64\u523b\u5979\u5446\u5446\u671b\u4e86\u4f0a\u98ce\u4e24\u773c\uff0c\u76ee\u5149\u4e2d\u4f3c\u4e4e\u95ea\u8fc7\u4e00\u4e1d\u6df7\u5408\u7740\u559c\u60a6\u4e0e\u60b2\u54c0\u7684\u5149\u91c7\uff0c\u4f46\u77ac\u5373\u53c8\u53d8 \u7684\u832b\u7136\u4e00\u7247\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5de6\u624b\u62b1\u7d27\u5a74\u513f\uff0c\u53f3\u638c\u4e00\u6325\uff0c\u5c06\u4f0a\u98ce\u6325\u5230\u8001\u8fdc\u3002 \u3000\u3000\u4f0a\u98ce\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u4e2d\u65e2\u662f\u4f24\u5fc3\uff0c\u53c8\u89c9\u60ed\u6127\uff0c\u6797\u5916\u7a81\u53c8\u63a0\u5165\u4e00\u6761\u5973\u5b50\u4eba\u5f71\uff0c\u4e00\u9762\u5a07\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u4e11\u4e2b\u5934\uff0c\u62a2\u6211\u7684\u5b69\u5b50\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e07\u5929\u840d\u76ee\u5149\u4e00\u52a8\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u7a81\u5730\u8eab\u5f62\u63a0\u8d77\u5982\u98ce\uff0c\u63a0\u5230\u540e\u6765\u8fd9\u5973\u5b50\u8eab\u4fa7\uff0c\u4e00\u628a\u6293\u4f4f\u5979\u7684 \u624b\u8155\uff0c\u8fd9\u5973\u5b50\u60ca\u547c\u4e00\u58f0\u9053\uff1a \u3000\u3000\u201c\u7239\u7239\uff01\u201d \u3000\u3000\u4e07\u5929\u840d\u6012\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u4e2b\u5934\uff0c\u96be\u9053\u4f60\u4e5f\u75af\u4e86\u5417\uff1f\u201d\u62c9\u7740\u4e07\u8679\u7684\u624b\u8155\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5999\u624b\u8bb8\u767d\u4ea6\u81ea\u5927\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u5f80\u90a3\u91cc\u53bb\uff01\u201d\u968f\u6b65\u62a2\u51fa\u3002 \u3000\u3000\u4f0a\u98ce\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\u9053\uff1a \u3000\u3000\u201c\u5357\u82f9\uff0c\u4f60\u597d\u597d\u5c06\u606f\u5c06\u606f\uff0c\u624b\u4e2d\u7684\u5b69\u513f\uff0c\u8fd8\u662f\u8ba9\u6211\u62b1\u7740\u5427\uff01\u201d \u3000\u3000\u8427\u5357\u82f9\u5931\u9b42\u843d\u9b44\u4f3c\u5730\u8f6c\u8fc7\u76ee\u5149\uff0c\u7a81\u5730\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u5927\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u6211\u7684\u5b69\u5b50\uff0c\u6211\u624d\u4e0d\u7ed9\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u591a\u624b\u771f\u4eba\u201d\u8c22\u96e8\u4ed9\uff0c\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u97e6\u50b2\u7269\uff0c\u201c\u71d5\u5c71\u4e09\u5251\u201d\uff0c\u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u201c\u5357\u5bab\u53cc \u674e\u201d\u4e5d\u5bf9\u773c\u775b\uff0c\u4e00\u9f50\u671b\u5411\u4f0a\u98ce\uff0c\u4e00\u4f1a\u513f\u671b\u5411\u8fd9\u6709\u5982\u75af\u5b50\u4e00\u822c\u7684\u4e11\u5987\uff0c\u5fc3\u4e2d\u4ea6\u5404\u81ea\u5927\u5947\uff0c\u90a3\u77e5 \u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u6742\u6811\u4e1b\u4e2d\uff0c\u7a81\u5730\u4f20\u51fa\u4e00\u9635\u6d2a\u4eae\u7684\u5a74\u513f\u557c\u58f0\u3002 \u3000\u3000\u4f17\u4eba\u9762\u8272\u9f50\u90fd\u4e00\u53d8\uff0c\u8427\u5357\u82f9\u5374\u53c8\u5c55\u989c\u4e00\u7b11\uff0c\u6b22\u559c\u5730\u53eb\u9053\uff1a \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4e2a\u5b69\u5b50\u3002\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u500f\u7136\u63a0\u5165\u6742\u6811\u4e1b\u4e2d\uff0c\u5979\u8eab\u906d\u5de8\u53d8\uff0c\u7cbe\u795e\u4e0a\u53d7\u4e86\u83ab\u5927\u7684\u523a \u6fc0\uff0c\u662f\u4ee5\u7cbe\u795e\u53d8\u7684\u6709\u4e9b\u604d\u60da\u3002 \u3000\u3000\u65e5\u524d\u5979\u5c06\u90a3\u9762\u201c\u83f1\u82b1\u94dc\u955c\u201d\u5f80\u5c71\u77f3\u4e0a\u4e00\u6454\uff0c\u4fbf\u8dd1\u5230\u6df1\u5c71\u91cc\uff0c\u7ec8\u65e5\u653e\u58f0\u75db\u54ed\uff0c\u54ed\u8fc7\u4e86\uff0c\u4fbf \u5728\u6df1\u5c71\u4e2d\u6e38\u8361\uff0c\u4e5f\u4e0d\u77e5\u8981\u505a\u4ec0\u4e48\uff0c\u9762\u5bf9\u7740\u7a7a\u5c71\u6d41\u6c34\uff0c\u5979\u60f3\u5230\u81ea\u5df1\u7684\u4f3c\u6c34\u5e74\u534e\uff0c\u4e8e\u662f\u5979\u75af\u7684\u66f4 \u5389\u5bb3\u4e86\u3002 \u3000\u3000\u5979\u7ec8\u65e5\u968f\u610f\u800c\u884c\uff0c\u8fd9\u65e5\u7a81\u7136\u9047\u7740\u4e07\u8679\u624b\u91cc\u62b1\u7740\u4e00\u4e2a\u5a74\u513f\u72c2\u5954\uff0c\u5979\u7cbe\u795e\u867d\u7136\u604d\u60da\uff0c\u5374\u8fd8\u8ba4 \u5f97\u4e07\u8679\uff0c\u5f53\u4e0b\u4fbf\u62e6\u4f4f\u4e07\u8679\u7684\u53bb\u8def\uff0c\u4e07\u8679\u5927\u60ca\u4e4b\u4e0b\uff0c\u5fae\u4e00\u758f\u795e\uff0c\u624b\u4e2d\u7684\u5a74\u513f\u4fbf\u88ab\u5979\u62a2\u53bb\u3002 \u3000\u3000\u8981\u77e5\u9053\u4e00\u4e2a\u7cbe\u795e\u4e0a\u53d7\u5230\u6781\u5927\u523a\u6fc0\u7684\u4eba\uff0c\u4ed6\u5fc5\u5b9a\u8981\u5bfb\u627e\u4e00\u4e2a\u6170\u85c9\uff0c\u800c\u6b64\u523b\u8427\u5357\u82f9\u5374\u611f\u5230\u5a74 \u513f\u662f\u5979\u6700\u5927\u7684\u6170\u85c9\uff0c\u662f\u4ee5\u5979\u4e00\u542c\u5230\u6742\u6811\u4e1b\u4e2d\u7684\u5a74\u513f\u54ed\u58f0\uff0c\u4fbf\u7acb\u523b\u63a0\u4e86\u8fdb\u53bb\uff0c\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u4f17 \u4eba\u9f50\u5730\u5927\u559d\u4e00\u58f0\uff0c\u60f3\u963b\u4f4f\u5979\u7684\u53bb\u8def\uff0c\u5374\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u516d\u7ae0\u3000\u9999\u6d88\u7389\u6b92>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u516d\u7ae0\u3000\u9999\u6d88\u7389\u6b92 \u3000\u3000\u4f0a\u98ce\u671b\u7740\u8427\u5357\u82f9\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u5f53\u7136\u662f\u767e\u611f\u4ea4\u96c6\uff0c\u957f\u53f9\u7740\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e9b\u201c\u5929\u4e89\u5f1f \u5b50\u201d\u4ff1\u90fd\u805a\u5728\u6811\u4e1b\u4e4b\u7554\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u4eba\u6562\u518d\u5f80\u524d\u8d70\u4e00\u6b65\u3002 \u3000\u3000\u53ea\u89c1\u6811\u4e1b\u4e4b\u4e2d\u4f20\u51fa\u8427\u5357\u82f9\u7684\u7b11\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u53c8\u767d\u53c8\u80d6\uff0c\u771f\u53ef\u7231\uff0c\u771f\u597d\u73a9\u2014\u2014\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u58f0\u97f3\u8fd8\u5728\u8fd1\u4fa7\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u58f0\u97f3\u5374\u5df2\u53bb\u5230\u5f88\u8fdc\uff0c\u663e\u89c1\u662f\u5979\u8fde\u8fd9\u5b69\u5b50\u4e5f\u62b1\u8d70 \u4e86\uff0c\u4f0a\u98ce\u4e0d\u7981\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u7591\u60d1\uff0c\u6697\u5fd6\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u5929\u4e89\u6559\u5f92\u773c\u770b\u4ed6\u4eec\u6559\u4e3b\u7684\u5b69\u5b50\u88ab\u4eba\u62a2\u8d70\uff0c\u5374\u4e5f\u4e0d\u524d\u8d70\u4e00\u6b65\uff0c\u867d\u662f\u907f\u5acc\uff0c\u5374\u4e5f\u4e0d\u5fc5 \u907f\u5230\u5982\u6b64\u5730\u4f4d\u5440\uff01\u201d \u3000\u3000\u8fd9\u5ff5\u5934\u5728\u4ed6\u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\uff0c\u4f46\u4ed6\u8f6c\u5ff5\u53c8\u60f3\u5230\u81ea\u5df1\u7684\u5b69\u5b50\uff0c\u6b64\u523b\u4e5f\u5df2\u88ab\u534a\u8fd1\u75af\u72c2\u7684\u8427\u5357\u82f9 \u62b1\u8d70\uff0c\u5fc3\u4e2d\u7126\u6025\u4e07\u72b6\uff0c\u90a3\u91cc\u8fd8\u6709\u5fc3\u601d\u53bb\u60f3\u522b\u7684\u3002 \u3000\u3000\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u9762\u4e0a\u808c\u8089\u5fae\u5fae\u4e00\u52a8\uff0c\u76ee\u5149\u4e00\u95ea\uff0c\u7a81\u5730\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\u4f60\u8fd8\u6ca1\u4e8b\u5427\uff0c\u5f1f\u5b50\u4eec\u90fd\u5728\u5916\u9762\u4f3a\u5019\uff0c\u592b\u4eba\u4e0d\u8981\u7740\u6025\uff0c\u7b49\u4f1a\u592b\u4eba\u6536\u62fe\u597d\u4e86\uff0c\u5f1f\u5b50\u4eec \u518d\u8fdb\u53bb\u7167\u6599\u3002\u201d \u3000\u3000\u4ed6\u6c89\u58f0\u8bf4\u5b8c\u8bdd\uff0c\u4fbf\u9000\u5230\u4e00\u682a\u6811\u4e0b\uff0c\u95ed\u76ee\u517b\u795e\uff0c\u4f17\u4eba\u4e00\u89c1\uff0c\u4e5f\u90fd\u9000\u5230\u4e00\u65c1\uff0c\u8981\u77e5\u9053\u201c\u4e03\u6d77 \u6e14\u5b50\u201d\u5728\u201c\u5929\u4e89\u6559\u201d\u4e2d\uff0c\u5730\u4f4d\u6781\u9ad8\uff0c\u662f\u4ee5\u4ed6\u9ed8\u7136\u5982\u6b64\uff0c\u522b\u4eba\u4e5f\u4e0d\u80fd\u518d\u6709\u4e3e\u52a8\u3002 \u3000\u3000\u800c\u6b64\u523b\u7684\u4f0a\u98ce\u5462\uff0c\u5fc3\u4e2d\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u4ed6\u60f3\u8ffd\u8e2a\u8427\u5357\u82f9\u800c\u53bb\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u53c8\u65e0 \u6cd5\u4e3e\u6b65\uff0c\u4ea6\u81ea\u7ad9\u5728\u6811\u4e0b\uff0c\u5446\u7acb\u4e86\u957f\u4e45\u3002 \u3000\u3000\u98ce\u7a7f\u5165\u6797\uff0c\u6728\u53f6\u6447\u66f3\u751f\u54cd\uff0c\u7136\u800c\u5728\u8fd9\u65b9\u6797\u4e2d\u95f4\uff0c\u4f17\u4eba\u7684\u547c\u5438\u4e4b\u58f0\uff0c\u5374\u5f7c\u6b64\u53ef\u95fb\u3002 \u3000\u3000\u6811\u4e1b\u4e4b\u4e2d\u547b\u541f\u4e4b\u58f0\u672a\u6b62\uff0c\u53c8\u662f\u4e00\u9635\u9635\u8863\u886b\u7684\u6089\u7c0c\u4e4b\u58f0\uff0c\u60f3\u662f\u859b\u82e5\u58c1\u6b63\u5728\u5f3a\u5fcd\u7740\u4ea7\u540e\u7684\u75db \u82e6\uff0c\u6536\u62fe\u7740\u81ea\u5df1\u7684\u8863\u886b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4f17\u4eba\u5fc3\u60c5\u90fd\u6781\u4e3a\u6c89\u91cd\u4e4b\u9645\uff0c\u6811\u4e1b\u4e4b\u4e2d\uff0c\u7a81\u5730\u4f20\u51fa\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u60e8\u547c\u4e4b\u58f0\u4e00\u7ecf\u5165\u8033\uff0c\u4f17\u4eba\u4fbf\u7acb\u523b\u53ef\u4ee5\u8fa8\u51fa\uff0c\u662f\u9500\u9b42\u592b\u4eba\u859b\u82e5\u74a7\u53d1\u51fa\u7684\u3002 \u3000\u3000\u63a5\u7740\u8fd9\u4e00\u58f0\u60e8\u547c\u7684\uff0c\u662f\u859b\u82e5\u74a7\u5fae\u5f31\u7684\u8bed\u58f0\uff0c\u65ad\u7ee7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u2026\u4f60\u9976\u4e86\u6211\u5427\u2026\u2026\u2026\u6211\u4e0d\u6562\u2026\u2026\u2026\u2026\u2026\u201d\u8bed\u58f0\u500f\u987f\uff0c\u53c8\u662f\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u4f17\u4eba\u4ff1\u90fd\u9762\u5bb9\u5927\u53d8\uff0c\u4f0a\u98ce\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u201c\u547c\u201d\u5730\u4e00\u638c\uff0c\u5288\u5f00\u679d\u53f6\uff0c\u63a0\u4e86\u8fdb\u53bb\uff0c\u201c\u71d5\u5c71\u4e09 \u5251\u201d\uff0c\u201c\u591a\u624b\u771f\u4eba\u201d\uff0c\u4e5f\u4e00\u9f50\u8ddf\u5165\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e00\u4e1b\u6742\u6811\u4e4b\u4e2d\uff0c\u6709\u4e00\u5757\u4e94\u5c3a\u89c1\u65b9\u9699\u5730\uff0c\u5730\u4e0a\u8840\u6c61\u72fc\u85c9\uff0c\u859b\u82e5\u74a7\u66f2\u5730\u4e0a\uff0c\u800c\u4e00\u6761\u6de1 \u9752\u4eba\u5f71\uff0c\u4ea6\u65b9\u81ea\u4ece\u6797\u6084\u63a0\u8d70\u3002 \u3000\u3000\u8fd9\u4eba\u5f71\u8eab\u5f62\u4e4b\u5feb\uff0c\u65e0\u4e0e\u4f26\u6bd4\uff0c\u4f0a\u98ce\u76ee\u5149\u65b9\u52a8\uff0c\u4ed6\u5df2\u6d88\u5931\u65e0\u5f71\u3002 \u3000\u3000\u201c\u71d5\u5c71\u4e09\u5251\u201d\uff0c\u201c\u591a\u624b\u771f\u4eba\u201d\u4e00\u9f50\u63a0\u5230\u859b\u82e5\u74a7\u8eab\u524d\uff0c\u4fef\u8eab\u4e00\u770b\uff0c\u4e0d\u7531\u9f50\u5730\u9762\u76ee\u53d8\u8272\uff0c\u60ca \u547c\u4e00\u58f0\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u9000\u540e\u4e09\u6b65\u3002 \u3000\u3000\u4f0a\u98ce\u867d\u60ca\u5f02\u4e8e\u8fd9\u6761\u4eba\u5f71\u7684\u6765\u5386\u53bb\u8def\uff0c\u4f46\u542c\u5f97\u8fd9\u6570\u58f0\u60ca\u547c\uff0c\u4ea6\u81ea\u56de\u8fc7\u5934\u6765\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u4ea6 \u4e0d\u7981\u4e3a\u4e4b\u9762\u8272\u5927\u53d8\uff0c\u60ca\u547c\u4e00\u58f0\uff0c\u9000\u540e\u4e09\u6b65\uff01 \u3000\u3000\u539f\u6765\uff0c\u4f0f\u5728\u8840\u6c61\u72fc\u85c9\u7684\u6ce5\u5730\u4e0a\u7684\u201c\u9500\u9b42\u592b\u4eba\u201d\u859b\u82e5\u74a7\uff0c\u6b64\u523b\u7adf\u662f\u53cc\u76ee\u7d27\u95ed\uff0c\u9762\u5982\u91d1\u7eb8\uff0c \u6beb\u65e0\u751f\u606f\uff0c\u5728\u5979\u90a3\u5fae\u5fae\u9686\u8d77\u7684\u4e30\u6ee1\u7684\u80f8\u819b\u4e0a\uff0c\u7adf\u8d6b\u7136\u63d2\u7740\u4e00\u67c4\u91d1\u8272\u5f2f\u5200\u3002 \u3000\u3000\u9ec4\u91d1\u7684\u5200\u67c4\uff0c\u91d1\u9ec4\u7684\u5200\u7a57\uff0c\u5728\u5fae\u98ce\u4e2d\u6447\u66f3\u7740\uff0c\u9c9c\u7ea2\u7684\u8840\u8ff9\uff0c\u81ea\u5200\u67c4\u4e0b\u7f13\u7f13\u6ea2\u51fa\u3002 \u3000\u3000\u201c\u52b3\u5c71\u4e09\u5251\u201d\uff0c\u201c\u5357\u5bab\u53cc\u674e\u201d\uff0c\u201c\u4e03\u6d77\u6e14\u5b50\u201d\uff0c\u4e5f\u63a0\u4e86\u8fdb\u6765\uff0c\u4e00\u9f50\u60ca\u547c\u4e00\u58f0\uff01 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u60ca\u547c\u4e4b\u58f0\u5374\u662f\u6781\u4e3a\u77ed\u4fc3\u7684\uff0c\u5f53\u4ed6\u4eec\u7684\u76ee\u5149\u63a5\u89e6\u5230\u8fd9\u9ec4\u91d1\u5200\u67c4\u4e4b\u9645\uff0c\u4ed6\u4eec\u9762\u4e0a\u7684 \u60ca\u6050\u4e4b\u8272\uff0c\u4fbf\u9f50\u90fd\u51dd\u7ed3\u4f4f\u4e86\uff01 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5927\u5730\u4e0a\u7684\u4e00\u523b\uff0c\u4e5f\u90fd\u50cf\u662f\u7a81\u7136\u51dd\u7ed3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u7a81\u5730\u4e00\u6325\u624b\u638c\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u6389\u5934\u800c\u53bb\u3002 \u3000\u3000\u201c\u71d5\u5c71\u4e09\u5251\u201d\uff0e\u201c\u591a\u624b\u771f\u4eba\u201d\uff0c\u201c\u5357\u5bab\u53cc\u674e\u201d\uff0c\u9f50\u5730\u5bf9\u671b\u4e00\u773c\uff0c\u4f3c\u4e4e\u4e5f\u4ff1\u9131\u6697\u4e2d\u53f9\u606f\u4e00 \u58f0\uff0c\u9ed8\u7136\u8d70\u51fa\u6811\u4e1b\u3002 \u3000\u3000\u201c\u52b3\u5c71\u4e09\u5251\u201d\u7684\u76ee\u5149\uff0c\u601c\u60dc\u5730\u843d\u5728\u859b\u82e5\u74a7\u8eab\u4e0a\uff0c\u7136\u540e\u53c8\u4e00\u9f50\u779f\u5411\u4f0a\u98ce\u3002 \u3000\u3000\u4ed6\u4eec\u4f3c\u4e4e\u6709\u4ec0\u4e48\u8bdd\u8981\u5bf9\u4f0a\u98ce\u53bb\u8bf4\uff0c\u4f46\u7ec8\u4e8e\u5fcd\u4f4f\u4e86\uff0c\u5404\u81ea\u53f9\u606f\u4e00\u58f0\uff0c\u63a0\u51fa\u6811\u4e1b\uff0c\u7136\u800c\u4ed6\u4eec \u53f9\u606f\u7684\u58f0\u97f3\uff0c\u5374\u4f3c\u8fd8\u5728\u6797\u7a0d\u98d8\u6563\u7740\u3002 \u3000\u3000\u4f0a\u98ce\u5446\u671b\u7740\u859b\u82e5\u74a7\u7684\u8eab\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u89c1\u5230\u8fd9\u4e9b\u4eba\u7a81\u7136\u4e00\u9f50\u79bb \u53bb\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5947\u602a\uff0c\u5947\u602a\u8fd9\u4e9b\u5929\u4e89\u5f1f\u5b50\uff0c\u89c1\u5230\u4ed6\u4eec\u7684\u6559\u4e3b\u592b\u4eba\u60e8\u6b7b\uff0c\u600e\u5730\u4e0d\u4f46\u5168\u65e0\u8868\u793a\uff0c\u800c \u4e14\u4ff1\u90fd\u79bb\u53bb\uff0c\u4efb\u51ed\u8fd9\u5177\u6614\u65e5\u66fe\u7ecf\u98a0\u5012\u4f17\u751f\u7684\u7f8e\u4eba\u8eaf\uff0c\u66b4\u4e8e\u5929\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4f46\u662f\uff0c\u53e6\u4e00\u79cd\u96be\u8a00\u7684\u60b2\u54c0\uff0c\u5374\u4f7f\u5f97\u4ed6\u4e2d\u6b62\u4e86\u5fc3\u4e2d\u7684\u7591\u60d1\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u5f80\u6614\u90a3\u4e00\u6bb5\u7f8e\u4e3d\u7684\u65f6\u5149\uff0c\u4ed6\u60f3\u8d77\u5c0f\u6865\u4e0a\u7684\u9082\u9005\uff0c\u661f\u5149\u4e0b\u7684\u76df\u8a93\uff0c\u5c0f\u5c4b\u4e2d\u7684\u5bc6 \u8bed\uff0c\u51dd\u89c6\u65f6\u7684\u751c\u5bc6 \u3000\u3000\u8fd9\u4e00\u4e9b\uff0c\u5bf9\u4ed6\u8bf4\u6765\uff0c\u4f3c\u4e4e\u662f\u90a3\u4e48\u771f\u5b9e\uff0c\u5374\u53c8\u4f3c\u4e4e\u662f\u90a3\u4e48\u9065\u8fdc\u3002 \u3000\u3000\u671b\u7740\u5730\u4e0a\u8fd9\u5177\u51b0\u51b7\u7684\u8eab\uff0c\u4ed6\u7a81\u7136\u611f\u5230\u4e00\u9635\u65e0\u6bd4\u7684\u5bd2\u610f\u3002 \u3000\u3000\u62ac\u8d77\u5934\u6765\uff0c\u66ae\u8272\u679c\u7136\u5df2\u6084\u7136\u7b3c\u7f69\u7740\u5927\u5730\uff0c\u6797\u95f4\u7684\u665a\u98ce\uff0c\u5f77\u4f5b\u6709\u7740\u6bd4\u5e73\u65e5\u66f4\u6d53\u539a\u7684\u8427\u7d22\u4e4b \u610f\u3002\u4ed6\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u601d\u6f6e\uff0c\u53c8\u56de\u5230\u6614\u65e5\u7f8e\u4e3d\u7684\u68a6\u5883\u4e2d\u53bb\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4fef\u4e0b\u8eab\u53bb\uff0c\u7f13\u7f13\u4f38\u51fa\u624b\u638c\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u90a3\u53ea\u7f8e\u4e3d\uff0c\u82cd\u767d\uff0c\u4f46\u5374\u51b0\u51b7\u7684\u7ea4\u624b\uff0c\u4e00\u6ef4\u773c \u6cea\u593a\u7736\u800c\u51fa\u6cbf\u988a\u6d41\u4e0b\uff0c\u6ef4\u5728\u8fd9\u53ea\u7f8e\u4e3d\u800c\u82cd\u767d\u7684\u624b\u638c\u4e0a\uff0c\u50cf\u662f\u4e00\u7c92\u6676\u83b9\u7684\u660e\u73e0\u3002 \u3000\u3000\u859b\u82e5\u58c1\u82e5\u662f\u8fd8\u6709\u77e5\u89c9\uff0c\u8fd8\u80fd\u611f\u89c9\u5230\u8fd9\u6cea\u73e0\u7684\u51b0\u51c9\uff0c\u5979\u4fbf\u4e5f\u8be5\u8db3\u4ee5\u5b89\u6170\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e00\u751f\u4e4b\u4e2d\uff0c\u867d\u7136\u4e00\u65e0\u6240\u5f97\uff0c\u7136\u800c\u5979\u5374\u5df2\u5bfb\u5f97\u4e00\u4e2a\u5982\u6b64\u591a\u60c5\uff0c\u5982\u6b64\u6602\u85cf\u7684\u7537\u5b50\uff0c\u5728 \u5979\u4e34\u6b7b\u7684\u65f6\u5019\uff0c\u8fd8\u5b88\u5728\u5979\u8eab\u65c1\uff01 \u3000\u3000\u592a\u9633\uff0c\u7ec8\u4e8e\u5b8c\u5168\u88ab\u5c71\u5dc5\u541e\u6ca1\u4e86\u3002 \u3000\u3000\u6d53\u91cd\u7684\u591c\u8272\uff0c\u50cf\u68a6\u4e00\u6837\uff0c\u7a81\u7136\u4fbf\u964d\u4e34\u5230\u8fd9\u5c71\u6797\u4e2d\uff0c\u8fd9\u5927\u5730\u4e0a\u3002 \u3000\u3000\u4f0a\u98ce\u8f7b\u63e1\u7740\u8fd9\u66fe\u7ecf\u6df1\u7231\u7740\u4ed6\uff0c\u4e5f\u6df1\u6df1\u88ab\u4ed6\u6240\u7231\u7684\u5973\u5b50\u7684\u624b\uff01\u5fc3\u80f8\u4e4b\u95f4\uff0c\u9664\u4e86\u90a3\u9065\u8fdc\u800c\u7f8e \u4e3d\u7684\u56de\u5fc6\u4e4b\u5916\uff0c\u4f3c\u4e4e\u4ec0\u4e48\u4e5f\u4e0d\u91cd\u60f3\u4e86\u3002 \u3000\u3000\u4eba\u4eec\uff0c\u662f\u591a\u4e48\u5947\u602a\u5440\uff0c\u4ed6\u5e38\u5e38\u4f1a\u5fd8\u8bb0\u4e00\u4e2a\u6b7b\u8005\u7684\u8fc7\u5931\uff0c\u800c\u53ea\u8bb0\u5f97\u4ed6\u7684\u597d\u5904\uff0c\u8fd9\u4e5f\u8bb8\u5c31\u662f \u4eba\u7c7b\u4e3a\u4ec0\u4e48\u4f1a\u88ab\u79f0\u4e3a\u201c\u4e07\u7269\u4e4b\u7075\u201d\u7684\u539f\u56e0\u5427\uff1a\u56e0\u4e3a\u201c\u4ec1\u6148\u201d\u548c\u201c\u5bbd\u6055\u201d\uff0c\u4e0d\u5c31\u662f\u6240\u6709\u201c\u7075 \u6027\u201d\u4e2d\u6700\u4f1f\u5927\u7684\u201c\u7075\u6027\u201d\u5417\uff1f \u3000\u3000\u65f6\u5149\uff0c\u7f13\u6162\u800c\u65e0\u60c5\u7684\u6e9c\u8d70\u3002\u591c\u8272\uff0c\u66f4\u91cd\u4e86\u3002 \u3000\u3000\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u5728\u8fd9\u6811\u4e1b\u7684\u65c1\u8fb9\uff0c\u6398\u4e86\u4e00\u4e2a\u6df1\u6df1\u7684\u571f\u5751\uff0c\u8fd9\u4ef6\u5de5\u4f5c\uff0c\u4fbf\u5f97\u4ed6\u53cc\u624b\u90fd\u4e3a\u4e4b\u9ebb \u6728\u8d77\u6765\uff0c\u6307\u7532\u4e5f\u7f1d\u6ee1\u4e86\u6ce5\u571f\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u4e1d\u6beb\u4e5f\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u4ed6\u5c0f\u5fc3\u5730\u6367\u8d77\u5979\u7684\u8eab\uff0c\u8f7b\u8f7b\u653e\u5728\u8fd9\u571f\u5751\u91cc\uff0c\u7136\u540e\u518d\u6367 \u8d77\u4e00\u628a\u6ce5\u571f\uff0c\u5728\u5979\u8eab\u4e0a\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u52a8\uff0c\u770b\u89c1\u4e86\u5979\u80f8\u819b\u4e0a\u7684\u90a3\u67c4\u9ec4\u91d1\u5f2f\u5200\uff0c\u4e8e\u662f\u4ed6\u4fef\u4e0b\u8eab\uff0c\u5c06\u8fd9\u67c4\u5f2f\u5200\u62d4\u8d77\u6765\uff0c\u8c28 \u614e\u5730\u653e\u5728\u6000\u4e2d\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u5e76\u6ca1\u6709\u4ed4\u7ec6\u5730\u6765\u770b\u8fd9\u67c4\u5f2f\u5200\uff0c\u56e0\u4e3a\u5f53\u4eba\u4eec\u6ee1\u5fc3\u4ff1\u4e3a\u60b2\u54c0\u5145\u6ee1\u7684\u65f6\u5019\uff0c\u4fbf\u4e0d\u4f1a\u518d\u6709 \u5fc3\u60c5\u53bb\u89c2\u5bdf\u4efb\u4f55\u4e00\u4ef6\u4e8b\u7269\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u4e0d\u4f4f\u5730\u7740\u6ce5\u571f\uff0c\u8ba9\u4e0d\u53d8\u7684\u6ce5\u571f\uff0c\u5c06\u5e38\u53d8\u7684\u4eba\u8eab\u8986\u84c4\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u571f\u5751\u5e73\u4e86\u3002 \u3000\u3000\u6614\u53e3\u5a07\u4e3d\u7edd\u4f26\uff0c\u98a0\u5012\u4f17\u751f\u7684\u7f8e\u4eba\uff0c\u6b64\u523b\u4fbf\u53d8\u4e3a\u4e00\u576f\u9ec4\u571f\u3002 \u3000\u3000\u4ed6\u6df1\u957f\u5730\u53f9\u606f\u7740\uff0c\u8d70\u5230\u4e00\u8fb9\uff0c\u9009\u4e86\u4e00\u5757\u8f83\u4e3a\u5e73\u6574\u7684\u5c71\u77f3\uff0c\u638f\u51fa\u6000\u4e2d\u7684\u5f2f\u5200\uff0c\u6781\u4e3a\u4ed4\u7ec6\u800c \u7f13\u6162\u5730\u5728\u5c71\u77f3\u4e0a\u523b\u4e86\u4e03\u4e2a\u5b57\u201c\u4ea1\u5984\u859b\u82e6\u58c1\u4e4b\u5893\u201d\u3002 \u3000\u3000\u8fd9\u4e03\u4e2a\u5b57\u867d\u7136\u548c\u4efb\u4f55\u5b57\u4e00\u6837\u5e73\u51e1\uff0c\u4f46\u5176\u4e2d\u6240\u5305\u6db5\u7740\u7684\u5bbd\u6055\uff0c\u4ec1\u6148\uff0c\u548c\u60c5\u611f\uff0c\u5374\u662f\u65e0\u53ef\u6bd4 \u62df\u7684\uff0c\u5bf9\u542b\u6068\u6b7b\u53bb\u7684\u859b\u82e5\u58c1\u6765\u8bf4\uff0c\u4e16\u95f4\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u4ef6\u4e8b\u7269\u80fd\u548c\u8fd9\u5e73\u51e1\u7684\u4e03\u4e2a\u5b57\u76f8\u6bd4\uff0c\u4f60\u770b \u5230\u4e86\u5417\uff0c\u5728\u8fd9\u4e00\u576a\u9ec4\u571f\u4e2d\u7684\u7075\u9b42\uff0c\u4e0d\u662f\u5df2\u7ecf\u5b89\u6170\u5730\u7efd\u5f00\u4e00\u4e1d\u5fae\u7b11\u4e86\u5417\uff1f \u3000\u3000\u7136\u540e\uff0c\u4f0a\u98ce\u5c06\u8fd9\u5757\u5c71\u77f3\uff0c\u4e5f\u57cb\u5728\u9ec4\u571f\u4e2d\uff0c\u53ea\u7559\u4e0b\u4e00\u65b9\u5c0f\u5c0f\u7684\u77f3\u89d2\uff0c\u7559\u505a\u8868\u5fd7\uff0c\u4ed6\u4e0d\u613f\u5979 \u7684\u9057\u4f53\u88ab\u4efb\u4f55\u4eba\u9a9a\u6270\uff0c\u5c24\u5176\u5728\u8fd9\u6708\u5149\u5982\u94f6\u7684\u665a\u4e0a\uff0c\u4e8e\u662f\uff0c\u4ed6\u53c8\u9759\u9759\u5730\u5750\u4e0b\u6765\uff0c\u7b49\u5f85\u65e5\u5149\u7684\u91cd \u4eae\u3002 \u3000\u3000\u6708\u5149\uff0c\u4ece\u6797\u6084\u6620\u5165\uff0c\u5c06\u4ed6\u7684\u5f71\u5b50\u62d6\u5f97\u957f\u957f\u5730\uff0c\u8986\u76d6\u5728\u8fd9\u4e00\u576a\u65b0\u6398\u7684\u9ec4\u571f\u4e0a\uff0c\u5c31\u50cf\u662f\u591a\u5e74 \u4ee5\u524d\uff0c\u201c\u94c1\u621f\u6e29\u5019\u201d\u5415\u5357\u4eba\uff0c\u7528\u4ed6\u90a3\u53ea\u5f3a\u5065\u6709\u529b\u7684\u81c2\u8180\uff0c\u8f7b\u8f7b\u5730\u62e5\u62b1\u7740\u4ed6\u7684\u7231\u59bb\u4e00\u6837\u3002\u6709\u98ce \u7684\u65f6\u5019\uff0c\u6728\u53f6\u5dc5\u7136\uff0c\u4f3c\u4e4e\u4e5f\u5728\u4e3a\u8fd9\u591a\u60c5\u800c\u6602\u85cf\u7684\u7537\u5b50\u4f5c\u65e0\u8a00\u7684\u53f9\u606f\uff01 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u4e03\u7ae0\u3000\u8c46\u853b\u68a2\u5934>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u4e03\u7ae0\u3000\u8c46\u853b\u68a2\u5934 \u3000\u3000\u9633\u5149\uff0c\u50cf\u662f\u4e3a\u4e86\u6628\u591c\u592a\u591a\u7684\u60b2\u54c0\uff0c\u4eca\u6668\u7adf\u5347\u8d77\u7684\u7279\u522b\u65e9\u3002 \u3000\u3000\u521d\u5347\u7684\u7b2c\u4e00\u9053\u9633\u5149\uff0c\u5212\u7834\u4e86\u6c88\u91cd\u7684\u9ed1\u6697\uff0c\u6495\u88c2\u4e86\u6d78\u6668\u7684\u6d53\u96fe\uff0c\u4e5f\u6652\u5e72\u4e86\u65b0\u751f\u6811\u53f6\u4e0a\u7684\u671d \u9732\u2014\u2014 \u3000\u3000\u7136\u540e\uff0c\u5145\u6c9b\u800c\u65fa\u76db\u5730\u9752\u6625\u7684\u671d\u6c14\uff0c\u4fbf\u5728\u8fd9\u4e00\u7247\u9752\u78a7\u7684\u5c71\u91ce\u95f4\uff0c\u968f\u7740\u88ab\u6495\u88c2\u4e86\u7684\u6d53\u96fe\u98de\u626c \u8d77\u6765\u3002 \u3000\u3000\u873f\u8712\u8fe4\u8ffd\u7684\u5c71\u9053\u4e0a\u7070\u9ec4\u7684\u7802\u77f3\uff0c\u4e5f\u88ab\u8fd9\u521d\u5347\u7684\u9633\u5149\uff0c\u5f71\u6620\u53d8\u4e3a\u4e00\u7247\u707f\u70c2\u7684\u91d1\u9ec4\uff0c\u50cf\u662f\u6f2b \u5c71\u7fe1\u7fe0\u6811\u95f4\u7684\u4e00\u6761\u9ec4\u91d1\u9053\u8def\uff0c\u751f\u547d\uff0c\u5728\u8fd9\u521d\u6625\u7684\u6e05\u6668\u91cc\uff0c\u5bf9\u4eba\u4eec\u6765\u8bf4\uff0c\u7684\u786e\u662f\u592a\u4f18\u6e25\u4e86\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u8fd9\u6709\u5982\u9ec4\u91d1\u6210\u7684\u5c71\u9053\u4e0a\uff0c\u7adf\u968f\u98ce\u98d8\u4e0a\u4e86\u4e00\u9635\u9635\u60a0\u626c\u7684\u6b4c\u58f0\uff0c\u58f0\u97f3\u662f\u5a07\u67d4\u800c\u66fc\u5999\u7684\uff0c\u4f46\u5374 \u542c\u4e0d\u751a\u6e05\uff0c\u5f77\u4f5b\u662f\u4e2a\u8c46\u853b\u5e74\u534e\u7684\u6000\u6625\u5c11\u5973\uff0c\u5728\u66fc\u58f0\u4f4e\u6627\u7740\uff1a \u3000\u3000\u8bb8\u591a\u66f0\u672a\u5230\u5c71\u91ce\uff0c\u5c71\u8def\u987f\u89c9\u6625\u6df1\uff0c\u7eff\u53f6\u76d6\u6ee1\u67af\u6811\uff0c\u65b0\u6c34\u4e89\u5b66\u7434\u97f3\uff0c\u8fd8\u6709\u53cc\u53cc\u72c2\u8776\u98de\u6765\u98de \u53bb\uff0c\u4f3c\u6709\u610f\u6253\u52a8\u4eba\u5fc3\uff0c\u2026\u2026\u2026\u2026\u2026\u2026\u2026\u2026 \u3000\u3000\u6b4c\u58f0\u8fd1\u4e86\uff0c\u968f\u7740\u8fd9\u66fc\u5999\u7684\u6b4c\u58f0\uff0c\u5c71\u8def\u4e0a\u8f7b\u5feb\u5730\u8d70\u4e0a\u4e00\u4e2a\u50cf\u662f\u53ea\u6709\u5341\u4e09\uff0c\u56db\u5c81\u7684\u660e\u5a9a\u7684\u5c11 \u5973\uff0c\u5979\u4e00\u624b\u8f7b\u8f7b\u629a\u7740\u88ab\u6625\u98ce\u5439\u4e71\u4e86\u7684\u79c0\u53d1\uff0c\u4e00\u624b\u8f7b\u62c8\u7740\u4e00\u7247\u6625\u8349\uff0c\u50cf\u662f\u53ea\u5feb\u4e50\u7684\u9ec4\u83ba\u4f3c\u7684\uff0c \u8f7b\u5feb\u5730\u8d70\u7740\uff0c\u8f7b\u5feb\u5730\u5531\u7740\uff01 \u3000\u3000\u4e16\u95f4\u56fe\u753b\u591a\u5c11\uff0c \u3000\u3000\u53ef\u66fe\u753b\u8fd9\u822c\u5c71\u6797\u6c60\u6cbc\uff0c \u3000\u3000\u4e16\u95f4\u8bd7\u8bcd\u591a\u5c11\uff0c \u3000\u3000\u53ef\u66fe\u548f\u8fd9\u822c\u73b2\u73d1\u7a88\u7a95\uff0c \u3000\u3000\u5929\u7136\u7f8e\u666f\u753b\u4e0d\u6210\uff01 \u3000\u3000\u5f85\u6b4c\u548f\uff0c \u3000\u3000\u4e5f\u8f93\u4e0e\u679d\u5934\u597d\u9e1f\u2026\u2026\u2026 \u3000\u3000\u679d\u5934\u597d\u9e1f\u3002 \u3000\u3000\u95ee\u2014\u2014\u201c\u4e16\u95f4\u8bd7\u8bcd\u591a\u5c11\uff01\u201d\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4e16\u95f4\u7684\u8bd7\u8bcd\uff0c\u6709\u8fc7\u591a\u5c11\u662f\u8d5e\uff0c\u548f\u8fd9 \u521d\u6625\u6e05\u6668\u7684\u5c71\u91ce\uff0c\u4f46\u662f\u6211\u77e5\u9053\uff0c\u53e4\u5f80\u4eca\u6765\u6240\u6709\u7684\u8d5e\uff0c\u548f\u4e5f\u53ca\u4e0d\u4e0a\u8fd9\u5c11\u5973\u6b64\u523b\u5728\u66fc\u58f0\u4f4e\u548f\u7740\u7684 \u6b4c\u66f2\uff0c\u56e0\u4e3a\u5b83\u662f\u90a3\u4e48\u81ea\u7136\uff0c\u81ea\u7136\u5f97\u6ca1\u6709\u4efb\u4f55\u62d8\u675f\uff0c\u5c31\u50cf\u662f\u6625\u591c\u4e2d\u7684\u8f7b\u98ce\uff0c\u6d41\u6c34\uff0c\u866b\u8bed\u2026\u2026\u4e00 \u6837\uff0c\u7528\u6700\u81ea\u7136\u7684\u6b4c\u66f2\u6765\u8d5e\u548f\u81ea\u7136\u7684\u7f8e\u5999\uff0c\u90a3\u4e0d\u6c38\u8fdc\u662f\u6700\u6700\u4ee4\u4eba\u5fc3\u52a8\u7684\u5417\uff1f \u3000\u3000\u5440\uff01\u201c\u4e16\u95f4\u56fe\u753b\u591a\u5c11\uff01\u201d\u6211\u4e0d\u77e5\u9053\uff1a\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e16\u95f4\u6709\u591a\u5c11\u4e39\u9752\u5999\u624b\uff0c\u66fe\u7ecf\u7528\u9c9c\u7684\u5f69 \u8272\uff0c\u6765\u63cf\u7ed8\u4e00\u4e2a\u5c11\u5973\u7684\u7f8e\u8273\uff01 \u3000\u3000\u4f46\u662f\uff0c\u6211\u5374\u77e5\u9053\uff0c\u4e16\u95f4\u6c38\u8fdc\u4e0d\u4f1a\u6709\u4e00\u4e2a\u4e39\u9752\u7684\u5999\u624b\uff0c\u80fd\u5c06\u8fd9\u5c11\u5973\u63cf\u7ed8\u51fa\u6765\uff0c\u56e0\u4e3a\u7eb5\u7136\u6709 \u4eba\u80fd\u63cf\u7ed8\u51fa\u5979\u660e\u5a9a\u7684\u773c\u775b\uff0c\u5374\u65e0\u6cd5\u63cf\u7ed8\u51fa\u5979\u773c\u6ce2\u4e2d\u7684\u5149\u91c7\uff0c\u7eb5\u7136\u6709\u4eba\u80fd\u63cf\u7ed8\u51fa\u5979\u5a07\u7f8e\u7684\u7b11 \u9765\uff0c\u5374\u65e0\u6cd5\u63cf\u7ed8\u51fa\u5979\u7b11\u9765\u4e2d\u7684\u751c\u610f\uff0c\u7eb5\u7136\u6709\u4eba\u80fd\u63cf\u7ed8\u51fa\u5979\u7a88\u7a95\u7684\u4f53\u6001\uff0c\u5374\u4e5f\u6c38\u8fdc\u65e0\u6cd5\u63cf\u7ed8\u51fa \u5979\u8eab\u4f53\u5185\u53f0\u8574\u7740\u7684\u9752\u6625\u6d3b\u529b\uff1a \u3000\u3000\u5979\u8f7b\u5feb\u5730\uff0c\u6b22\u8dc3\u5730\uff0c\u4ece\u5c71\u4e0b\u8d70\u4e86\u4e0a\u6765\uff0c\u8f7b\u7ea2\u7684\u8863\u886b\uff0c\u5728\u9752\u7eff\u7684\u5927\u5730\u95f4\uff0c\u50cf\u662f\u4e00\u6735\u8f7b\u67d4\u7684 \u665a\u4e91\uff0c\u5728\u851a\u84dd\u4e07\u91cc\u7684\u82cd\u7a79\u95f4\u5189\u5189\u98de\u6765\uff0c\u4e16\u95f4\u7684\u4e00\u5207\u5fe7\u90c1\u4e0e\u4e0d\u5e78\uff0c\u4f3c\u4e4e\u90fd\u56e0\u5979\u7684\u5230\u6765\u800c\u8fdc\u53bb\u3002 \u3000\u3000\u6b4c\u58f0\u505c\u4e86\uff0c \u3000\u3000\u5979\u660e\u5a9a\u7684\u76ee\u5149\uff0c\u8d5e\u8d4f\u7684\u779f\u8fc7\u6bcf\u4e00\u4ef6\u6625\u98ce\u4e2d\u7684\u666f\u7269\uff0c\u811a\u6b65\u4ecd\u7136\u8f7b\u5feb\u5730\u79fb\u52a8\u7740\uff0c\u79c0\u53d1\u98d8\u5728\u8eab \u540e\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u5728\u8fd9\u5982\u6b64\u660e\u5a9a\uff0c\u5982\u6b64\u6109\u5feb\u7684\u6625\u4e4b\u6668\u4e2d\uff0c\u5728\u8fd9\u5982\u6b64\u79c0\u4e3d\uff0c\u5982\u6b64\u6e05\u5e7d\u7684\u9752\u78a7\u5c71\u91ce\u91cc\uff0c\u7adf\u4f1a\u8fd8 \u6709\u4eba\u53d1\u51fa\u5982\u6b64\u5fe7\u90c1\uff0c\u5982\u6b64\u6c89\u91cd\u7684\u53f9\u606f\uff01\u5979\u505c\u4e0b\u811a\u6b65\uff0c\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u8fd9\u53f9\u606f\u50cf\u662f\u5c71\u8def\u90a3\u8fb9\uff0c\u4e00\u7247\u5c71\u5761\u4e0a\uff0c\u4e00\u7247\u5c0f\u6797\u4e2d\u7684\u4e00\u4e2a\u7ea2\u9876\u5c71\u4ead\u4e2d\u53d1\u51fa\u6765\u7684\uff0c\u800c\u4e14\uff0c\u8fd8 \u50cf\u662f\u4e0d\u6b62\u4e00\u4eba\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u76b1\u4e86\u76b1\u7709\uff0c\u4f46\u662f\u5634\u89d2\u7684\u7b11\u97f3\u4ecd\u672a\u6d88\u5931\uff0c\u811a\u6b65\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u5c31\u5f00\u59cb\u5411\u5c71\u4ead\u90a3\u8fb9\u8d70 \u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u50cf\u662f\u4e24\u638c\u4e92\u51fb\uff0c\u53c8\u50cf\u662f\u4ee5\u638c\u51fb\u684c\u3002 \u3000\u3000\u7136\u540e\uff0c\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8001\u4e8c\uff0c\u4f60\u8bf4\u8fd9\u5947\u4e0d\u5947\u602a\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6765\uff0c\u5509\u2014\u2014\u201d \u3000\u3000\u4ed6\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4e09\u5f1f\u6c38\u8fdc\u662f\u8fd9\u79cd\u4e0d\u987e\u4eba\u7684\u813e\u6c14\uff0c\u4e5f\u4e0d\u7ba1\u522b\u4eba\u5fc3\u91cc\u662f\u5426\u7740\u6025\uff0c\u8001\u4e8c\uff0c\u4f60\u542c\u6e05\u695a\u6ca1\u6709\uff0c\u4e09 \u5f1f\u8bf4\u7684\uff0c\u53ef\u662f\u4e0d\u662f\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u53e6\u4e00\u58f0\u53f9\u606f\uff0c\u53e6\u4e00\u4e2a\u5fe7\u90c1\u6c89\u91cd\u7684\u58f0\u97f3\uff0c\u4ea6\u81ea\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff0c\u4e09\u5f1f\u4f1a\u6765\u7684\uff01\u4ed6\u2026\u2026\u2026\u5509\uff01\u201d \u3000\u3000\u4ed6\u5f77\u4f5b\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u7ec8\u4e8e\u7528\u4e00\u58f0\u53f9\u606f\u7ed3\u675f\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u5148\u524d\u90a3\u82cd\u8001\u800c\u6c89\u91cd\u7684\u58f0\u97f3\u53c8\u8bf4 \u9053\uff1a \u3000\u3000\u201c\u4f1a\u6765\u7684\u2026\u2026\u4f1a\u6765\u7684\uff0c\u4f46\u613f\u4ed6\u4f1a\u6765\uff0c\u5509\u2026\u2026\u4e09\u5f1f\uff0c\u4f60\u77e5\u9053\uff0c\u5927\u54e5\u662f\u6c38\u8fdc\u4e0d\u4f1a\u5bf9\u4f60\u6709\u6076\u610f \u7684\u5440\uff0c\u5509\u4e09\u5f1f\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8fd9\u82cd\u8001\u3001\u5fe7\u90c1\uff0c\u6c89\u91cd\uff0c\u800c\u53c8\u5145\u6ee1\u60c5\u611f\u7684\u58f0\u97f3\uff0c\u4ece\u8fdc\u5904\u4f20\u6765\uff0c\u4f20\u5165\u8fd9\u5c11\u5973\u7684\u8033\u91cc\uff0c\u5979\u6084\u6084 \u7728\u52a8\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u8d70\u4e0a\u5c71\u5761\u3002 \u3000\u3000\u73b2\u73d1\u7684\u5c71\u4ead\u4e2d\u6709\u4e00\u5f20\u77f3\u684c\uff0c\u56db\u6761\u77f3\u58a9\uff0c\u77f3\u58a9\u4e0a\u5750\u7740\u4e24\u4e2a\u8eab\u7a7f\u84dd\u886b\u7684\u4e2d\u5e74\u4eba\uff0c\u9894\u4e0b\u5fae\u5fae\u6709 \u4e9b\u77ed\u987b\uff0c\u4ed6\u4eec\u4ee5\u624b\u652f\u9890\uff0c\u4f4e\u5782\u7740\u53cc\u76ee\uff0c\u9ed8\u9ed8\u5730\u5750\u5728\u684c\u8fb9\uff0c\u50cf\u662f\u975e\u5e38\u5fe7\u90c1\uff0c\u53c8\u50cf\u662f\u975e\u5e38\u75b2\u5026\u3002 \u3000\u3000\u5c71\u4ead\u8fb9\u6709\u7fe0\u7eff\u7684\u680f\u6746\uff0c\u4e00\u4e2a\u5fe7\u90c1\u800c\u75b2\u5026\u7684\u8001\u4eba\uff0c\u53e6\u4e00\u4e2a\u84dd\u886b\u7684\u8001\u8005\uff0c\u5e74\u7eaa\u867d\u7136\u8f83\u4ed6\u8f7b \u4e9b\uff0c\u4f46\u8138\u4e0a\u7684\u5fe7\u90c1\u548c\u75b2\u5026\u7684\u795e\u8272\uff0c\u5374\u548c\u4ed6\u5b8c\u5168\u4e00\u6837\uff0c\u4ed6\u4eec\u9ed8\u9ed8\u5730\u501a\u5728\u680f\u8fb9\uff0c\u51fa\u795e\u7684\u671b\u5411\u8fdc \u65b9\uff0c\u50cf\u662f\u5728\u773a\u671b\u7740\u4ec0\u4e48\uff0c\u53c8\u50cf\u662f\u5728\u671f\u5f85\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u8f7b\u76c8\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u548c\u4ed6\u4eec\u7684\u76ee\u5149\u9047\u5728\u4e00\u5904\uff0c\u5979\u5fc3\u4e2d\u8f7b\u8f7b\u4e00\u8df3\uff0c\u53ea\u89c9\u5f97 \u8fd9\u56db\u4eba\u7684\u76ee\u5149\u7adf\u662f\u5982\u6b64\u9510\u5229\uff0c\u90a3\u4e48\u6837\u7684\u5fe7\u90c1\u548c\u75b2\u5026\uff0c\u7adf\u4e5f\u4e0d\u80fd\u5c06\u4ed6\u4eec\u773c\u775b\u4e2d\u9510\u5229\u7684\u5149\u91c7\u6d88\u53bb \u534a\u5206\u3002 \u3000\u3000\u5979\u7728\u4e86\u7728\u773c\u775b\uff0c\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u5634\u89d2\u5f00\u59cb\u6cdb\u8d77\u4e00\u4e2a\u751c\u7f8e\u7684\u7b11\u5bb9\uff0c\u5979\u5a07\u7b11\u7740\uff0c\u5411\u8fd9\u7d20\u4e0d\u76f8 \u8bc6\u7684\u56db\u4e2a\u7537\u5b50\u8f7b\u5feb\u7684\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4eca\u5929\u5929\u6c14\u771f\u597d\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8fd9\u56db\u4e2a\u84dd\u886b\u4eba\u9f50\u90fd\u4e00\u6115\uff0c\u8fc5\u5feb\u5730\u4ea4\u6362\u4e86\u4e2a\u773c\u8272\uff0c\u4e8e\u662f\u4ed6\u4eec\u90fd\u77e5\u9053\uff0c\u81ea\u5df1\u8fd9\u4e9b\u4eba\u91cc\u6ca1\u6709\u4e00 \u4e2a\u4eba\u548c\u8fd9\u5c11\u5973\u662f\u7d20\u8bc6\u7684\uff0c\u4ed6\u4eec\u53c8\u5411\u540e\u671b\u4e86\u4e00\u773c\uff0c\u56db\u91ce\u7a7a\u7a7a\uff0c\u9664\u4e86\u4ed6\u4eec\u4e4b\u5916\uff0c\u5c31\u518d\u65e0\u4eba\u8e2a\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u53c8\u77e5\u9053\uff0c\u8fd9\u5c11\u5973\u662f\u5728\u5bf9\u81ea\u5df1\u8bf4\u8bdd\uff0c\u4f46\u662f\u4ed6\u4eec\u90fd\u4e0d\u8ba4\u5f97\u5979\uff0c\u4e5f\u4e0d\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u8981 \u5bf9\u81ea\u5df1\u8bf4\u8bdd\uff0c\u56db\u5bf9\u773c\u775b\uff0c\u53c8\u81ea\u95ea\u7535\u822c\u671b\u4e86\u8fd9\u5c11\u5973\u4e00\u773c\uff0c\u53ea\u89c9\u5f97\u5979\u7b11\u5bb9\u662f\u90a3\u6837\u751c\u7f8e\uff0c\u76ee\u5149\u4e2d\u53c8 \u90fd\u662f\u5584\u610f\uff0c\u53eb\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u62d2\u7edd\u56de\u7b54\u5979\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u90a3\u5fe7\u90c1\u7684\u8001\u8005\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u52c9\u5f3a\u5728\u81ea\u5df1\u8138\u4e0a\u6324\u51fa\u4e00\u4e2a\u5fae\u7b11\uff0c\u70b9\u5934\u9053\uff1a \u3000\u3000\u201c\u662f\u5440\uff0c\u5c0f\u59d1\u5a18\uff0c\u4eca\u5929\u5929\u6c14\u771f\u597d\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u773c\u775b\u7728\u4e5f\u4e0d\u7728\u5730\u671b\u7740\u4ed6\uff0c\u770b\u5230\u4ed6\u7b11\uff0c\u5979\u5c31\u7b11\u5f97\u66f4\u751c\u4e86\uff0c\u5979\u9ad8\u5174\u5730\u62cd\u7740\u624b\uff0c\u5927\u58f0\u7b11 \u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u4f60\u7b11\u4e86\uff0c\u6211\u539f\u5148\u4ee5\u4e3a\u4f60\u4e0d\u4f1a\u7b11\u7684\u5462\uff01\u201d \u3000\u3000\u8fd9\u8001\u8005\u53c8\u81ea\u5e72\u54b3\u4e00\u58f0\uff0c\u56de\u5934\u671b\u4e86\u53e6\u4e09\u4eba\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u4eec\u773c\u91cc\uff0c\u4e5f\u90fd\u50cf\u662f\u6709\u4e86\u4e9b\u7b11\u610f\uff0c\u53ea \u662f\u53c8\u90fd\u5728\u5fcd\u8010\u7740\uff0c\u6ca1\u6709\u7b11\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u7a33\u91cd\u4e25\u5cfb\uff0c\u522b\u4eba\u90fd\u5c06\u4ed6\u5f53\u505a\u957f\u5144\u4e25\u7236\uff0c\u4ece\u6ca1\u6709\u4eba\u5728\u4ed6\u9762\u524d\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff0c\u6b64\u523b\u4ed6 \u671b\u7740\u8fd9\u5c11\u5973\u751c\u751c\u7684\u7b11\u5bb9\uff0c\u5fe7\u90c1\u800c\u82cd\u8001\u7684\u5fc3\u5883\uff0c\u4e5f\u50cf\u662f\u5f00\u59cb\u6709\u4e86\u4e9b\u6696\u610f\uff0c\u6e29\u67d4\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u8981\u5230\u90a3\u91cc\u53bb\u5440\uff0c\u8fd9\u91cc\u5c71\u5f88\u6df1\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u8ff7\u8def\uff1f\u201d \u3000\u3000\u53e6\u4e09\u4e2a\u84dd\u886b\u4eba\u5947\u602a\u5730\u4ea4\u6362\u4e86\u4e2a\u773c\u8272\uff0c\u4ed6\u4eec\u4ece\u672a\u770b\u8fc7\u4ed6\u5982\u6b64\u795e\u6001\u8bf4\u8bdd\uff0c\u5c24\u5176\u4ed6\u8bf4\u8bdd\u7684\u5bf9 \u8c61\uff0c\u7adf\u662f\u4e2a\u5341\u4e09\uff0c\u56db\u5c81\u7684\u5c11\u5973\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4eec\u5374\u4e5f\u6ca1\u6709\u5c06\u5fc3\u91cc\u7684\u5947\u602a\u8bf4\u51fa\u6765\uff0c\u53ea\u89c1\u8fd9\u5c11\u5973\u7728\u4e86\u7728\u660e\u5a9a\u7684\u773c\u775b\uff0c\u542b\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u8ff7\u8def\uff0c\u6211\u8ddf\u5988\u5988\u5728\u4e00\u8d77\uff0c\u8001\u4f2f\u4f2f\uff0c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u662f\u5e0c\u671b\u4f60\u4e0d\u4f1a\u53f9\u6c14\uff0c\u4f60\u770b\uff0c \u5929\u662f\u8fd9\u4e48\u84dd\uff0c\u6811\u662f\u8fd9\u4e48\u7eff\uff0c\u51ac\u5929\u597d\u4e0d\u5bb9\u6613\u8fc7\u4e86\uff0c\u73b0\u5728\u662f\u8fd9\u4e48\u7f8e\u4e3d\u7684\u6625\u5929\uff0c\u4e16\u4e0a\u6709\u4ec0\u4e48\u4e8b\u662f\u4e0d \u80fd\u89e3\u51b3\u7684\uff0c\u8001\u4f2f\u4f2f\uff0c\u4f60\u53c8\u4f55\u5fc5\u53f9\u6c14\u5462\uff1f\u201d \u3000\u3000\u5979\u5a07\u67d4\u7684\u58f0\u97f3\uff0c\u751c\u7f8e\u7684\u7b11\u9765\uff0c\u4ee5\u53ca\u8a00\u8bed\u4e2d\u6e29\u67d4\u7684\u529d\u6170\uff0c\u4fbf\u5f97\u5c0f\u4ead\u4e2d\u56db\u4e2a\u84dd\u8863\u4eba\u9762\u4e0a\u7684\u5fe7 \u90c1\uff0c\u5f88\u5feb\u5730\u5c31\u88ab\u4e00\u9635\u5fae\u7b11\u66ff\u4ee3\u3002 \u3000\u3000\u4e8e\u662f\u5979\u6ee1\u610f\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5a07\u7b11\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u8d70\u4e86\uff0c\u6211\u8fd8\u8981\u966a\u5988\u5988\u53bb\u627e\u4eba\uff0c\u5e0c\u671b\u4f60\u4eec\u7b49\u7684\u4eba\uff0c\u5f88\u5feb\u5c31\u6765\u3002\u201d\u8bf4\u7740\uff0c\u5979\u5fae\u7b11\u7740\u62db\u4e86 \u62db\u624b\uff0c\u50cf\u4e00\u53ea\u8774\u8776\u4f3c\u7684\uff0c\u518d\u6b21\u8f7b\u5feb\u5730\u5411\u5c71\u4e0a\u8d70\u53bb \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u516b\u7ae0\u3000\u8776\u5a92\u82b1\u8baf>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u516b\u7ae0\u3000\u8776\u5a92\u82b1\u8baf \u3000\u3000\u4e8e\u662f\uff0c\u6b64\u523b\u5979\u7684\u811a\u6b65\u5c31\u66f4\u8f7b\u76c8\u4e86\uff0c\u5fc3\u60c5\u4e5f\u66f4\u6109\u5feb\u4e86\uff0c\u56e0\u4e3a\u5979\u89c9\u5f97\u5df2\u5e2e\u52a9\u4e86\u522b\u4eba\uff0c\u89e3\u5f00\u4e86 \u522b\u4eba\u5fc3\u4e2d\u7684\u5fe7\u90c1\uff0c\u5979\u5feb\u4e50\u5730\u4f4e\u8bed\uff1a \u3000\u3000\u201c\u5e2e\u5fd9\u522b\u4eba\uff0c\u539f\u6765\u662f\u8fd9\u6837\u4ee4\u4eba\u5feb\u4e50\u7684\u4e8b\u5440\u2026\u2026\u201d\u7a81\u5730\uff0c\u4e00\u53ea\u8774\u8776\u81ea\u9762\u524d\u98de\u8fc7\uff0c\u5979\u7b11\u4e86 \u7b11\uff1a\u201c\u2026\u2026\u98de\u6765\u98de\u53bb\uff0c\u4f60\u4e5f\u60f3\u6253\u52a8\u522b\u4eba\u7684\u5fc3\u5417?\u201d\u8f7b\u4f38\u7389\u624b\u62c2\u53bb\uff0c\u4f46\u970e\u773c\u95f4\uff0c\u8fd9\u53ea\u8774\u8776\uff0c\u7adf \u53c8\u98de\u8fc7\u6765\u4e86\uff0c\u5979\u76b1\u4e86\u76b1\u9f3b\u5b50\uff0c\u7a81\u5730\u75be\u4f38\u53cc\u638c\uff0c\u60f3\u6349\u4f4f\u5b83\uff0c\u90a3\u77e5\u8fd9\u8774\u8776\u5f69\u7fc5\u4e00\u5c55\uff0c\u7adf\u53c8\u8fdc\u8fdc\u98de \u4e86\u5f00\u53bb\u3002 \u3000\u3000\u9633\u5149\u4e0b\uff0c\u5979\u53ea\u89c9\u8fd9\u53cc\u5c55\u52a8\u7740\u7684\u5f69\u7fc5\uff0c\u7adf\u6709\u65e0\u6bd4\u7684\u7f8e\u4e3d\u3002 \u3000\u3000\u5979\u5de6\u53f3\u56db\u987e\u4e00\u773c\uff0c\u786e\u5b9a\u5df2\u518d\u65e0\u4eba\u8ff9\uff0c\u7a81\u5730\u8fd0\u8db3\u8f7b\u70b9\uff0c\u55d6\u5730\u63a0\u8d77\u4e00\u4e08\uff0c\u6251\u5411\u90a3\u53ea\u5f69\u8776\uff0c\u75be \u4f38\u7389\u638c\uff0c\u53cc\u624b\u4e00\u62cd \u3000\u3000\u62cd\u5730\uff0c\u5979\u53c8\u843d\u7a7a\u4e86\u3002 \u3000\u3000\u5979\u8f7b\u53f1\u4e00\u58f0\uff0c\u811a\u5c16\u5728\u704c\u6728\u679d\u4e0a\u4e00\u70b9\uff0c\u7a88\u7a95\u7684\u8eab\u5f62\uff0c\u518d\u5ea6\u817e\u8eab\u800c\u8d77\uff0c\u8fd9\u6b21\u5979\u8fde\u4e0b\u4e00\u6b21\u843d\u811a \u5904\u90fd\u770b\u51c6\u4e86\uff0c\u975e\u5c06\u8fd9\u53ea\u5f69\u8776\u6349\u4f4f\u4e0d\u53ef\uff0c\u5176\u5b9e\uff0c\u5f53\u5979\u8f7b\u76c8\u7684\u8eab\u5f62\uff0c\u6de1\u7ea2\u7684\u8863\u88f3\uff0c\u98d8\u98d8\u5730\u51cc\u7a7a\u98de \u63a0\u7684\u65f6\u5019\uff0c\u8fd8\u4e0d\u662f\u548c\u4e00\u53ea\u5f69\u8776\u4e00\u6837\uff01 \u3000\u3000\u5c31\u53ea\u662f\u811a\u5c16\u5728\u67d4\u8f6f\u7684\u679d\u53f6\u4e0a\u8f7b\u8f7b\u90a3\u4e00\u70b9\uff0c\u5979\u5df2\u66fc\u5999\u5730\u524d\u63a0\u4e08\u4f59\uff0c\u773c\u770b\u7740\u90a3\u53ea\u5f69\u8776\u7eda\u4e3d\u7684 \u7fc5\u8180\uff0c\u5979\u624b\u638c\u518d\u6b21\u8f7b\u8f7b\u4e00\u62cd\uff0c\u7adf\u62cd\u51fa\u4e00\u80a1\u67d4\u548c\u7684\u638c\u98ce\uff0c\u90a3\u5f69\u8776\u5411\u524d\u4e00\u51b2\uff0c\u7136\u540e\u6162\u6162\u5730\u843d\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u5979\u5f97\u610f\u7684\u5a07\u7b11\u4e00\u4e0b\uff0c\u5a07\u8eaf\u5fae\u626d\uff0c\u7b14\u76f4\u5730\u63a0\u5411\u90a3\u5f69\u8776\u843d\u4e0b\u7684\u5730\u65b9\uff0c\u90a3\u662f\u5728\u4e00\u4e1b\u6d53\u5bc6\u7684\u6797\u6728 \u540e\u9762\uff0c\u5979\u60f3\uff0c\u8fd9\u53ea\u8774\u8776\u843d\u4e0b\u53bb\u7684\u5730\u65b9\uff0c\u5012\u771f\u50cf\u4e2a\u5c4f\u98ce\u4f3c\u7684\uff0c\u6697\u4e2d\u4e00\u8c03\u771f\u6c14\uff0c\u6b63\u5f85\u98d8\u8eab\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u5979\u76ee\u5149\u52a8\u5904\uff0c\u5374\u4e0d\u7981\u4e3a\u4e4b\u60ca\u547c\u4e00\u58f0\uff0c\u53cc\u81c2\u731b\u5f20\uff0c\u8eab\u5f62\u63d0\u8d77\u4e09\u5c3a\u3002 \u3000\u3000\u539f\u6765\uff0c\u5728\u5979\u5c06\u8981\u98d8\u8eab\u843d\u4e0b\u7684\u5730\u65b9\uff0c\u7adf\u7aef\u7aef\u6b63\u6b63\u5730\u5750\u7740\u4e00\u4e2a\u4eba\uff0c\u50cf\u662f\u5c0a\u77f3\u50cf\u4f3c\u7684\uff0c\u542c\u5230\u8fd9 \u4e00\u58f0\u60ca\u547c\uff0c\u624d\u6162\u6162\u8f6c\u8fc7\u5934\u6765\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u6963\u5bf9\u2026\u2026 \u3000\u3000\u5979\u4e0d\u7981\u53c8\u60ca\u547c\u4e00\u58f0\uff0c\u8eab\u5f62\u843d\u5230\u8fd9\u4eba\u8eab\u65c1\u7684\u5730\u4e0a\uff0c\u8f7b\u4f38\u7389\u6307\uff0c\u6307\u7740\u7aef\u5750\u5730\u4e0a\u7684\u4eba\uff0c\u60ca\u8bb6\u5730 \u8131\u53e3\u547c\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u662f\u5415\u5357\u4eba\uff01\u201d \u3000\u3000\u5728\u6599\u7684\u6625\u5bd2\u4e2d\uff0c\u9762\u5bf9\u7740\u57cb\u846c\u4e86\u6614\u65e5\u7231\u59bb\u7684\u4e00\u576f\u65b0\u571f\uff0c\u67af\u5750\u4e86\u4e00\u591c\u7684\u4f0a\u98ce\uff0c\u6b64\u523b\u56de\u8fc7\u5934\uff0c \u7adf\u89c1\u5230\u4e00\u4e2a\u5999\u9f84\u5c11\u5973\uff0c\u8131\u53e3\u547c\u51fa\u8fde\u81ea\u5df1\u90fd\u5df2\u51e0\u4e4e\u9057\u5fd8\u4e86\u7684\u540d\u5b57\uff0c\u5fc3\u5934\u4e0d\u89c9\u4e00\u9707\uff0c\u5b9a\u795e\u671b\u53bb\uff0c \u7a81\u4e5f\u8131\u53e3\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u662f\u4e0d\u662f\u51cc\u5927\u4fa0\u7684\u5973\u516c\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u5c55\u989c\u4e00\u7b11\uff1a \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u6211\u5c31\u662f\u51cc\u7433\uff0c\u5440\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u8ba4\u5f97\u6211\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u77a5\u89c1\u4f0a\u98ce\u9762\u524d\u7684\u4e00\u576f \u65b0\u571f\uff0c\u518d\u671b\u4e86\u671b\u4f0a\u98ce\u7684\u9762\u8272\uff0c\u7728\u4e86\u7728\u773c\u775b\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u867d\u53c8\u5fcd\u4f4f\uff0c\u4f46\u7ec8\u4e8e\u55eb\u5685\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5415\u2026\u2026\u5415\u5927\u53d4\uff0c\u4f60\u5750\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff0c\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u4f0a\u98ce\u957f\u53f9\u4e00\u58f0\uff0c\u622a\u4f4f\u4e86\u5979\u7684\u8bdd\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u8bb8\u4e45\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u957f\u5927\u4e86\u4e0d\u5c11\uff0c\u6211\u2026\u2026\u6211\u4e5f\u8001\u4e86\u2026\u2026\u8001\u4e86\u3002\u201d\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u5446 \u6ede\u5730\u8f6c\u52a8\u4e86\u4e00\u4e0b\u76ee\u5149\uff01 \u3000\u3000\u201c\u4f60\u600e\u5730\u4e5f\u6765\u4e86\uff0c\u4f60\u5988\u5988\u5462\u3002\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4f60\u4eec\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u201d\u4ed6\u8bed\u58f0\u987f\u4e86\u987f\uff0c\u7a81\u5730\u60f3 \u8d77\u5979\u4eec\u6bcd\u5973\u3002\u5df2\u8ddf\u201c\u4e09\u5fc3\u795e\u541b\u201d\u4e60\u827a\u4e4b\u4e8b\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u5728\u4e09\u5fc3\u524d\u8f88\u5904\u4e60\u6b66\uff0c\u5374\u8dd1\u5230\u8fd9\u91cc \u6765\uff1f\u201d \u3000\u3000\u51cc\u7433\u660e\u5a9a\u7684\u76ee\u5149\uff0c\u5728\u4f0a\u98ce\u82cd\u767d\u51c4\u6e05\u7684\u9762\u76ee\u4e0a\u4e00\u8f6c\uff0c\u7a81\u5730\u201c\u5657\u54e7\u201d\u7b11\u9053\uff1a \u3000\u3000\u201c\u624d\u4e0d\u8fc7\u4e00\u5e74\u591a\u561b\uff01\u5415\u5927\u53d4\uff0c\u4f60\u600e\u4e48\u5c31\u8bf4\u81ea\u5df1\u8001\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u82e6\u7b11\u4e00\u4e0b\uff1a \u3000\u3000\u201c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\uff0c\u6709\u4e9b\u4eba\u5728\u4e00\u591c\u4e4b\u95f4\u5c31\u4f1a\u82cd\u8001\u8bb8\u591a\uff0c\u5509\u2014\u2014\u5c31\u50cf\u662f\u5341\u5e74\u4e00 \u6837\uff0c\u800c\u6709\u4e9b\u4eba\u6e21\u8fc7\u5341\u5e74\uff0c\u5374\u50cf\u662f\u5f39\u6307\u95f4\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4f4e\u6c89\u800c\u7f13\u6162\uff0c\u50cf\u662f\u5728\u56de\u7b54\u51cc\u7433\u7684\u8bdd\uff0c\u53c8\u50cf\u662f\u5728\u6697\u81ea\u4f4e\u8bed\u3002 \u3000\u3000\u51cc\u7433\u79cb\u6ce2\u8f6c\u5904\uff0c\u518d\u6b21\u671b\u4e86\u671b\u5730\u4e0a\u7684\u4e00\u576f\u65b0\u571f\uff0c\u5979\u77e5\u9053\u8fd9\u5176\u4e2d\u5fc5\u5b9a\u6709\u7740\u4ec0\u4e48\u662f\u4ee5\u4ee4\u5979\u7684 \u201c\u5415\u5927\u53d4\u201d\u4f24\u5fc3\u7684\u4e8b\uff0c\u4f46\u662f\u5979\u4e0d\u6562\u95ee\u3002 \u3000\u3000\u5979\u53ea\u662f\u8f7b\u8f7b\u7b11\u7740\u8bf4\uff1a \u3000\u3000\u201c\u6211\u548c\u5988\u5988\u672c\u6765\u662f\u8ddf\u7740\u5e08\u5085\u7ec3\u6b66\u7684\uff0c\u53ea\u662f\u5e08\u5085\u4ed6\u8001\u4eba\u5bb6\u4e8b\u60c5\u597d\u50cf\u5f88\u591a\u7684\u6837\u5b50\uff0c\u6559\u4e86\u6211\u51e0 \u4e2a\u6708\uff0c\u5c31\u8bf4\u8981\u91c7\u836f\u53bb\u4e86\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u53eb\u6211\u597d\u597d\u628a\u90a3\u4e9b\u529f\u592b\u7ec3\u534a\u5e74\uff0c\u7136\u540e\u5c31\u968f\u4fbf\u6211 \u5230\u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u4f0a\u98ce\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5446\u6ede\u5730\u8f6c\u52a8\u7740\u76ee\u5149\uff0c\u6700\u540e\u7ec8\u4e8e\u505c\u7559\u5728\u51cc\u7433\u8eab\u4e0a\uff0c\u4ed6\u89c9\u5f97\u65f6\u95f4\u867d\u53ea\u8fc7 \u4e86\u5e74\u4f59\uff0c\u4f46\u662f\u5728\u8fd9\u6bb5\u65e5\u5b50\u91cc\uff0c\u53d8\u5316\u5374\u53c8\u662f\u591a\u4e48\u5927\u5440\uff0c\u6614\u65e5\u8fd8\u662f\u4e2a\u5782\u9aeb\u5c11\u5973\uff0c\u5982\u4eca\u7adf\u5df2\u8fd9\u4e48\u5927 \u4e86\u3002 \u3000\u3000\u671b\u7740\u5979\uff0c\u4f0a\u98ce\u5fc3\u91cc\u4e0d\u81ea\u89c9\u5730\u8d77\u4e86\u4e00\u9635\u6e29\u6696\u7684\u611f\u89c9\uff0c\u4ed6\u548c\u5979\u4eec\u6bcd\u5b50\u4e24\u4eba\uff0c\u867d\u7136\u89c1\u9762\u7684\u65e5\u5b50 \u6ca1\u6709\u591a\u5c11\uff0c\u7136\u800c\u5374\u7ecf\u8fc7\u4e00\u6bb5\u751f\u6b7b\u60a3\u96be\u7684\u65e5\u5b50\uff0c\u8fd9\u6bb5\u65e5\u5b50\u5728\u4f0a\u98ce\u5fc3\u4e2d\uff0c\u662f\u6c38\u751f\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u7684\uff0c \u6b64\u523b\u4ed6\u770b\u5230\u5979\uff0c\u5c31\u50cf\u662f\u770b\u5230\u591a\u5e74\u524d\u7684\u6545\u4ea4\u4e00\u6837\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5634\u89d2\u4e0d\u7981\u6cdb\u8d77\u4e00\u4e1d\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u4ed6\u6c89\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6240\u4ee5\u4f60\u7ec3\u4e86\u534a\u5e74\u6b66\uff0c\u5c31\u5077\u5077\u8dd1\u51fa\u6765\u73a9\u4e86\uff0c\u4f60\u5988\u5988\u653e\u5fc3\u5417\uff1f\u201d \u3000\u3000\u51cc\u7433\u4e5f\u5728\u671b\u7740\u4ed6\uff0c\u4ed6\u7559\u5728\u5979\u5fc3\u91cc\u7684\u5370\u8c61\uff0c\u672c\u6765\u662f\u6781\u4e3a\u6a21\u7cca\u7684\uff0c\u5979\u53ea\u662f\u5e38\u542c\u5979\u5988\u5988\u544a\u8bc9 \u5979\uff0c\u66fe\u7ecf\u6709\u7740\u8fd9\u4e48\u4e00\u4e2a\u52c7\u6562\u800c\u6b63\u76f4\u7684\u4eba\uff0c\u4ece\u201c\u593a\u547d\u53cc\u201d\u7684\u9b54\u638c\u4e0b\uff0c\u6551\u51fa\u4e86\u5979\u7684\u6027\u547d\u3002 \u3000\u3000\u4f46\u5230\u4e86\u6b64\u523b\uff0c\u5979\u624d\u77e5\u9053\uff0c\u867d\u53ea\u5306\u5306\u4e00\u9762\uff0c\u4f46\u4ed6\u7559\u5728\u81ea\u5df1\u5fc3\u91cc\u7684\u5370\u8c61\uff0c\u5374\u5df2\u975e\u5e38\u6df1\u523b\uff0c\u6df1 \u523b\u5f97\u8db3\u4f7f\u5979\u5728\u7b2c\u4e00\u773c\u89c1\u7740\u4ed6\u7684\u65f6\u5019\uff0c\u5c31\u7acb\u523b\u8ba4\u51fa\u4ed6\u662f\u8c01\u6765\u3002 \u3000\u3000\u5979\u5446\u671b\u7740\u4ed6\uff0c\u53ea\u89c9\u4ed6\u662f\u8fd9\u6837\u82f1\u4fca\u800c\u6210\u719f\uff0c\u70af\u70af\u7684\u76ee\u5149\uff0c\u50cf\u662f\u80fd\u770b\u900f\u4f60\u7684\u5fc3\u4e8b\uff0c\u633a\u76f4\u7684\u9f3b \u6881\uff0c\u80fd\u591f\u7ed9\u4efb\u4f55\u4eba\u4e00\u4efd\u575a\u6bc5\u7684\u611f\u89c9\uff0c\u4f46\u662f\u5f53\u4ed6\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u6d45\u7b11\u7684\u65f6\u5019\uff0c\u4ed6\u777f\u667a\u800c\u575a\u6bc5\u7684\u9762 \u76ee\uff0c\u7acb\u523b\u5c31\u53d8\u7684\u90a3\u4e48\u6e29\u67d4\u3002 \u3000\u3000\u62ac\u8d77\u5934\uff0c\u9047\u7740\u4ed6\u7684\u76ee\u5149\uff0c\u4ed6\u4f3c\u4e4e\u8fd8\u5728\u7b49\u7740\u5979\u7684\u7b54\u8986\uff0c\u5979\u8f7b\u8f7b\u7b11\u4e86\uff01 \u3000\u3000\u201c\u6211\u4e0d\u662f\u5077\u5077\u6e9c\u51fa\u6765\u7684\uff0c\u5988\u5988\u4e5f\u6765\u4e86\uff0c\u5979\u8981\u5230\u8fd9\u91cc\u6765\u627e\u4e00\u4e2a\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u8ddf\u7740\u4e00\u8d77\u6765 \u7684\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u4e00\u63a0\u9b13\u53d1\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5415\u5927\u53d4\uff0c\u4f60\u5fc3\u91cc\u597d\u50cf\u6709\u7740\u4ec0\u4e48\u5fc3\u4e8b\u4f3c\u7684\uff0c\u53ef\u4e0d\u53ef\u4ee5\u544a\u8bc9\u6211\uff0c\u8ba9\u6211\u2026\u2026\u8ba9\u6211\u4e5f\u66ff\u4f60\u5206\u62c5 \u4e00\u4e9b\uff0c\u5988\u5988\u8bf4\u628a\u70e6\u607c\u7684\u4e8b\u95f7\u5728\u809a\u91cc\uff0c\u6700\u4e0d\u597d\u4e86\uff0c\u5415\u5927\u53d4\uff0c\u4f60\u8bf4\u5988\u5988\u8bf4\u7684\u8bdd\u662f\u4e0d\u662f\u5bf9\u7684\u5440\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u53c8\u6de1\u6de1\u5730\u7b11\u4e86\uff0c\u4ed6\u7a81\u7136\u53d1\u73b0\uff0c\u8fd9\u5c11\u5973\u7adf\u662f\u5982\u6b64\u53ef\u7231\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8d70\u8fc7\u53bb\uff0c\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5979\u7684\u80a9\u8180\uff0c\u7b11\u5bb9\u867d\u4e0d\u80fd\u63a9\u76d6\u4ed6\u9762\u4e0a\u7684\u82cd\u767d\uff0c\u66f4\u4e0d\u80fd\u63a9\u76d6\u4ed6\u76ee \u5149\u4e2d\u7684\u5fe7\u90c1\uff0c\u4f46\u662f\u4ed6\u6bd5\u7adf\u7b11\u4e86\u3002 \u3000\u3000\u51cc\u7433\u4e5f\u7b11\u4e86\uff0c\u53ea\u89c9\u4ed6\u62cd\u5728\u81ea\u5df1\u80a9\u4e0a\u7684\u624b\u638c\uff0c\u662f\u90a3\u4e48\u5bbd\u5927\u800c\u6e29\u6696\uff0c\u50cf\u662f\u80fd\u4f7f\u4efb\u4f55\u4eba\u90fd\u613f\u610f \u5c06\u81ea\u5df1\u7684\u4e00\u5207\u4ea4\u6258\u7ed9\u8fd9\u53cc\u624b\u638c\u3002 \u3000\u3000\u4f0a\u98ce\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u4f60\u5988\u5988\u8bf4\u7684\u8bdd\uff0c\u81ea\u7136\u662f\u5bf9\u7684\uff0c\u4ee5\u540e\u2026\u2026\u4ee5\u540e\u6211\u81ea\u7136\u4f1a\u628a\u6211\u5fc3\u91cc\u7684\u70e6\u607c\u5168\u90e8\u8bf4\u7ed9\u4f60 \u542c\u3002\u201d \u3000\u3000\u51cc\u7433\u62ac\u8d77\u5934\uff1a \u3000\u3000\u201c\u771f\u7684\uff0c\u5415\u5927\u53d4\uff0c\u4f60\u4e0d\u8981\u9a97\u6211\u5440\uff01\u201d \u3000\u3000\u4f0a\u98ce\u6697\u81ea\u53f9\u606f\u7740\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6211\u5fc3\u91cc\u7684\u70e6\u607c\uff0c\u53c8\u6709\u8c01\u80fd\u8d1f\u62c5\uff0c\u5509\u2014\u2014\u201d\u76ee\u5149\u4e00\u5782\uff0c\u671b\u89c1\u51cc\u7433\u771f\u631a\u7684\u76ee\u5149\uff0c\u4ed6\u5fc3\u91cc\u53f9 \u606f\u7740\uff0c\u53e3\u4e2d\u5374\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u600e\u4e48\u4f1a\u9a97\u4f60\uff0c\u73b0\u5728\uff0c\u4f60\u8981\u4e0d\u8981\u6211\u53bb\u627e\u4f60\u7684\u5988\u5988\u53bb\uff1f\u201d \u3000\u3000\u51cc\u7433\u7b11\u4e86\uff0c\u771f\u5fc3\u5730\u7b11\u4e86\uff0c\u5ae3\u7ea2\u7684\u7b11\u9765\u4e24\u8fb9\uff0c\u9732\u51fa\u4e24\u4e2a\u6df1\u6df1\u7684\u7b11\u6da1\uff0c\u5979\u5f00\u5fc3\u5730\u62c9\u4f4f\u4e86\u4ed6\u5bbd \u5927\u7684\u624b\u638c\uff0c\u5206\u5f00\u679d\u53f6\uff0c\u5411\u5916\u9762\u8d70\u53bb\uff0c\u4e00\u9762\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u6211\u5e26\u4f60\u53bb\u627e\u5988\u5988\u53bb\uff0c\u5979\u89c1\u4e86\u4f60\uff0c\u4e0d\u77e5\u9053\u8981\u9ad8\u5174\u6210\u4ec0\u4e48\u6837\u5b50\u5462\uff0c\u5415\u5927\u53d4\uff0c\u4f60\u77e5\u4e0d\u77e5 \u9053\uff0c\u5988\u5988\u603b\u662f\u63d0\u8d77\u4f60\uff0c\u8bf4\u4f60\u591a\u4e48\u52c7\u6562\uff0c\u591a\u4e48\u597d\uff0c\u53ea\u53ef\u60dc\u4e0d\u77e5\u9053\u4f60\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u54c8\u2014\u2014\u5979\u770b\u89c1 \u6211\u7a81\u7136\u548c\u4f60\u4e00\u540c\u51fa\u73b0\uff0c\u4f60\u731c\u731c\u5979\u4f1a\u73b0\u51fa\u4ec0\u4e48\u6837\u5b50\uff01\u201d \u3000\u3000\u4f0a\u98ce\u968f\u7740\u5979\u8d70\u4e86\u51fa\u53bb\uff0c\u4ed6\u4e0d\u7528\u56de\u5934\uff0c\u4e5f\u77e5\u9053\u7559\u5728\u4ed6\u8eab\u540e\u7684\u662f\u4ec0\u4e48\uff0c\u4f46\u662f\u4ed6\u4ecd\u7136\u5fcd\u4e0d\u4f4f\u8981 \u56de\u5934\u770b\u4e0a\u4e00\u773c\uff0c\u770b\u4e00\u773c\u90a3\u4e00\u576f\u65b0\u6398\u7684\u9ec4\u571f\uff0c\u56e0\u4e3a\u5728\u8fd9\u576f\u9ec4\u571f\u91cc\u6c38\u4e45\u5b89\u606f\u7740\u7684\uff0c\u662f\u66fe\u7ecf\u88ab\u4ed6\u6df1 \u7231\u7740\u7684\u4eba\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7ec8\u4e8e\u56de\u8fc7\u5934\u6765\uff0c\u5728\u4ed6\u773c\u524d\u7684\uff0c\u662f\u7eda\u4e3d\u7684\u9633\u5149\uff0c\u78a7\u7eff\u7684\u6811\u53f6\uff0c\u5145\u6ee1\u751f\u547d\u6d3b\u529b\u7684\u5927 \u5730\uff0c\u548c\u6ee1\u542b\u6e29\u67d4\u751c\u610f\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u89c9\u5f97\u751f\u547d\u4ecd\u7136\u662f\u7f8e\u597d\u7684\uff0c\u4e16\u4e0a\u4ecd\u7136\u5145\u6ee1\u4e86\u4eba\u7c7b\u7684\u7231\u5fc3\uff0c\u4ed6\u53c8\u4f55\u82e6\u628a\u81ea \u5df1\u6df1\u6df1\u57cb\u846c\u5728\u8fc7\u53bb\u7684\u5fe7\u90c1\u91cc\u3002 \u4e8e\u662f\u4ed6\u633a\u4e86\u633a\u80f8\u819b\uff0c\u7d27\u63e1\u7740\u51cc\u7433\u6e29\u6696\u7684\u5c0f\u624b\uff0c\u5927\u6b65\u5411\u524d\u8d70\u53bb\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e03\u5341\u4e5d\u7ae0\u3000\u8fdf\u66ae\u4f24\u6625>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e03\u5341\u4e5d\u7ae0\u3000\u8fdf\u66ae\u4f24\u6625 \u3000\u3000\u5b59\u654f\uff0c\u672c\u6765\u662f\u548c\u51cc\u7433\u4e00\u8d77\u5230\u8fd9\u897f\u6881\u5c71\u6765\u7684\uff0c\u4f46\u662f\u4e0a\u4e86\u897f\u6881\u5c71\uff0c\u9762\u5bf9\u7740\u6ee1\u5c71\u6625\u8272\uff0c\u5979\u7a81\u7136 \u6709\u4e86\u4e00\u79cd\u65e0\u6cd5\u627f\u53d7\u7684\u611f\u89c9\u3002 \u3000\u3000\u5979\u65e0\u6cd5\u77e5\u9053\u8fd9\u4efd\u611f\u89c9\u7684\u7531\u6765\uff0c\u4e5f\u4e0d\u6562\u53bb\u5bfb\u6c42\u89e3\u7b54\uff0c\u5979\u53ea\u662f\u89c9\u5f97\u81ea\u5df1\u5fc3\u91cc\u6709\u4e00\u4efd\u6de1\u6de1\u7684\u5fe7 \u90c1\uff0c\u800c\u5979\u751a\u81f3\u8fde\u8fd9\u4efd\u5fe7\u90c1\u662f\u4e3a\u4e86\u81ea\u5df1\uff01\u8fd8\u662f\u4e3a\u4e86\u6625\u5929\uff0c\u5374\u4e0d\u77e5\u9053\uff0c\u4e8e\u662f\u5979\u624d\u4f1a\u8ba9\u5e74\u8f7b\u7684\u5973\u513f \u5148\u4e0a\u5c71\uff0c\u800c\u5979\u81ea\u5df1\uff0c\u5374\u613f\u610f\u72ec\u81ea\u6765\u6d88\u53d7\u8fd9\u4efd\u521d\u6625\u7684\u5fe7\u90c1\u3002 \u3000\u3000\u671b\u7740\u5979\u5973\u513f\u5145\u6ee1\u9752\u6625\u6d3b\u529b\u7684\u80cc\u5f71\uff01\u5979\u5fc3\u4e2d\u53c8\u89c9\u5f97\u5f88\u6ee1\u8db3\uff0c\u8fd9\u6de1\u7ea2\u7684\u8eab\u5f71\uff0c\u53c8\u6d3b\u751f\u751f\u5c31\u662f \u81ea\u5df1\u4e8c\u5341\u5e74\u524d\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5728\u8fd9\u8fe4\u9026\u7684\u5c0f\u9053\u4e0a\uff0c\u5979\u7f13\u7f13\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u5f80\u4e8b\uff0c\u53c8\u50cf\u6f6e\u6c34\u4e00\u6837\u5730\u5f00\u59cb\u5728\u5979\u5fc3\u91cc\u7ffb\u6d8c\u8d77 \u6765\u3002 \u3000\u3000\u5f80\u4e8b\uff0c\u5f80\u4e8b\u2014\u2014\u5509\uff0c\u526a\u4e0d\u65ad\uff0c\u7406\u8fd8\u4e71\u7684\u5f80\u4e8b\uff0c\u4eba\u4eec\u4e3a\u4ec0\u4e48\u8981\u6709\u5f80\u4e8b\u7684\u56de\u5fc6\uff0c\u82e5\u4eba\u4eec\u5355\u5355 \u53ea\u4f1a\u61a7\u61ac\u672a\u6765\uff0c\u4e0d\u8981\u6bd4\u73b0\u5728\u5e78\u798f\u5f97\u591a\u5417\uff1f \u3000\u3000\u9752\u6625\u7684\u65e5\u5b50\uff0c\u5c31\u50cf\u6cb3\u91cc\u7684\u6d41\u6c34\uff0c\u4e00\u53bb\uff0c\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6765\u4e86\u3002 \u3000\u3000\u6c5f\u4e2d\u7684\u6696\u6d41\uff0c\u679d\u5934\u7684\u7ea2\u53f6\uff0c\u4eba\u9762\u7684\u5806\u7b11\uff0c\u6d53\u60c5\u7684\u5bc6\u8bed\u2026\u2026\u3002 \u3000\u3000\u867d\u7136\u5904\u5904\u90fd\u6709\u6625\u610f\uff0c\u4f46\u8fdf\u66ae\u7684\u5987\u4eba\u5fc3\u4e2d\uff0c\u5374\u6c38\u8fdc\u4e0d\u4f1a\u611f\u89c9\u5230\uff0c\u5979\u5e74\u7eaa\u867d\u4e0d\u751a\u5927\uff0c\u770b\u6765\u4e5f \u4e0d\u89c9\u82cd\u8001\uff0c\u4f46\u662f\u5979\u7684\u5fc3\u5883\uff0c\u7eb5\u7136\u5728\u8fd9\u521d\u6625\u7684\u5929\u6c14\u91cc\uff0c\u4e5f\u6a61\u662f\u6709\u4e86\u665a\u79cb\u7684\u8427\u7d22\uff0c\u5979\u4e0d\u77e5\u9053\u4ec0\u4e48 \u662f\u81ea\u5df1\u8981\u8ffd\u5bfb\u7684\uff0c\u4eba\u751f\uff0c\u4f3c\u4e4e\u5df2\u5b8c\u5168\u6ca1\u6709\u4e00\u6837\u503c\u5f97\u5979\u8ffd\u5bfb\u7684\u4e1c\u897f\uff0c\u9664\u4e86\u90a3\u7c89\u7ea2\u8272\u7684\u8eab\u5f71\u3002 \u3000\u3000\u5979\u7ec8\u4e8e\u6709\u4e86\u4e00\u4e2a\u53ef\u4ee5\u5bc4\u6258\u5979\u7684\u5730\u65b9\uff0c\u867d\u7136\u4eba\u751f\u4e0d\u8fc7\u767e\u5e74\uff0c\u662f\u90a3\u4e48\u5306\u4fc3\uff0c\u4f46\u5979\u7684\u751f\u547d\uff0c\u5374 \u5df2\u6709\u4e86\u5ef6\u7eed\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5979\u7684\u811a\u6b65\u5feb\u4e86\u4e9b\uff0c\u5979\u6781\u529b\u96c6\u4e2d\u601d\u6f6e\uff0c\u5728\u524d\u9762\u7684\u9053\u8def\u4e0a\uff0c\u4ec0\u4e48\u4e5f\u4e0b\u770b\uff0c\u4ec0\u4e48\u4e5f\u4e0d \u60f3\u3002 \u3000\u3000\u7ec8\u4e8e\u2014\u2014 \u3000\u3000\u5979\u542c\u5230\u4e86\u5979\u5973\u513f\u7684\u7b11\u58f0\uff0c\u542c\u5230\u5979\u5973\u513f\u5728\u5feb\u4e50\u5730\u547c\u558a\u7740\uff1a \u3000\u3000\u201c\u5988\u5988\uff01\u201d \u3000\u3000\u4f38\u51fa\u7389\u638c\uff0c\u5979\u62b9\u4e86\u62b9\u9762\u988a\uff0c\u62b9\u53bb\u4e86\u9762\u4e0a\u7684\u8f7b\u5c18\uff0c\u4e5f\u62b9\u53bb\u4e86\u800c\u4e0a\u7684\u8f7b\u6101\uff0c\u7136\u540e\uff0c\u5979\u62ac\u8d77 \u5934\uff0c\u5806\u8d77\u7b11\u5bb9\uff0c\u56de\u7b54\u7740\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u6211\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u5c0f\u8def\u4e0a\u98de\u5feb\u5730\u63a0\u51fa\u4e24\u6761\u4eba\u5f71\u6765\uff0c\u90a3\u662f\u5979\u5973\u513f\uff0c\u4f46\u662f\u2014\u2014 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u662f\u8c01\uff1f \u3000\u3000\u5979\u5b9a\u4e86\u5b9a\u795e\uff0c\u51dd\u76ee\u671b\u53bb\u3002 \u3000\u3000\u201c\u5440\u2014\u2014\u201d \u3000\u3000\u5979\u4e0d\u7981\u5931\u58f0\u9ad8\u547c\u4e86\u8d77\u6765\uff01 \u3000\u3000\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u5415\u2026\u2026\u5357\u4eba\uff0c\u5357\u4eba\uff0c\u4f60\u7adf\u7136\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4e09\u5341\u4e94\u5c81\u5987\u4eba\u60ef\u6709\u7684\u77dc\u6301\uff0c\u5374\u4e5f\u63a9\u4e0d\u4f4f\u5979\u6b64\u523b\u7684\u4e0e\u594b\uff0c\u4e8e\u662f\uff0c\u77dc\u6301\u6d88\u5931\u4e86\uff0c\u5979\u64a9\u8d77 \u88d9\u811a\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u8fc7\u53bb\u3002 \u3000\u3000\u8eab\u6cd5\u662f\u8fc5\u5feb\u800c\u60ca\u4eba\u7684\uff0c\u4f46\u662f\u4f0a\u98ce\u5374\u7b11\u4e86\uff0c\u591a\u65e5\u6765\u7b2c\u4e00\u6b21\u771f\u6b63\u7684\u7b11\u4e86\uff0c\u4e00\u4e2a\u9ad8\u633d\u4e91\u5b89\uff0c\u7a7f \u7740\u767e\u8936\u6e58\u88d9\u7684\u9ad8\u8d35\u5987\u4eba\uff0c\u7adf\u7136\u4f1a\u64a9\u8d77\u81ea\u5df1\u7684\u88d9\u811a\uff0c\u50cf\u4e2a\u7537\u5b50\u6c49\u4f3c\u7684\uff0c\u7a9c\u8d77\u7bad\u6b65\u6765\uff0c\u4ed6\u4ece\u672a\u60f3 \u5230\u81ea\u5df1\u4e00\u751f\u4e4b\u4e2d\uff0c\u4f1a\u770b\u5230\u7c7b\u4f3c\u6b64\u523b\u7684\u60c5\u666f\u3002 \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u4e2d\uff0c\u4e0d\u77e5\u5305\u6db5\u4e86\u591a\u5c11\u5b89\u6170\uff0c\u4ed6\u8fce\u4e0a\u53bb\uff0c\u7b11\u7740\u8bf4\uff1a \u3000\u3000\u201c\u5b59\u2026\u2026\u51cc\u592b\u4eba\uff0c\u6211\u2026\u2026\u5c0f\u9119\u4e5f\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u91cc\u9047\u7740\u60a8\u3002\u201d \u3000\u3000\u4e16\u6545\uff0c\u4f7f\u5f97\u4ed6\u4e00\u8fde\u53d8\u4e86\u4e24\u4e2a\u79f0\u547c\uff0c\u5b59\u654f\u76ee\u5149\u4e00\u8f6c\uff0c\u8f7b\u7b11\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\u2026\u2026\u5440\uff0c\u4f60\u8fd8\u662f\u53eb\u6211\u5927\u59d0\u597d\u4e86\u3002\u201d\u5c31\u5728\u5979\u76ee\u5149\u4e00\u8f6c\u4e2d\uff0c\u5979\u5df2\u53d1\u73b0\u4e86\u4f0a\u98ce\u7b11\u5bb9\u540e\u7684 \u82cd\u767d\u4e0e\u5fe7\u90c1\uff0c\u5979\u8f6c\u5411\u51cc\u7433\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u4f60\u662f\u600e\u6837\u9047\u7740\u5415\u5927\u53d4\u7684\u3002\u201d \u3000\u3000\u51cc\u7433\u62a2\u7740\u8bf4\u4e86\uff0c\u7b49\u5230\u51cc\u7433\u8bf4\u5b8c\uff0c\u5b59\u654f\u7684\u53cc\u7709\u8f7b\u8f7b\u76b1\u4e86\u8d77\u6765\uff0c\u5979\u76ee\u5149\u518d\u6b21\u51dd\u89c6\u7740\u4f0a\u98ce\uff0c\u76ee \u5149\u6ee1\u542b\u7740\u8be2\u95ee\uff0c\u5979\u60f3\u95ee\u4ed6\uff1a \u3000\u3000\u201c\u4f60\u4e00\u4e2a\u4eba\u5728\u90a3\u91cc\u5750\u7740\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4f60\u5fc3\u91cc\u6709\u4ec0\u4e48\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u5979\u6ca1\u6709\u95ee\u51fa\u6765\u3002 \u3000\u3000\u53ea\u662f\u5979\u867d\u7136\u6ca1\u6709\u95ee\u51fa\u6765\uff0c\u4f0a\u98ce\u5374\u5df2\u77e5\u9053\u5979\u60f3\u95ee\u7684\u662f\u4ec0\u4e48\uff0c\u4ed6\u5782\u4e0b\u5934\uff0c\u5f3a\u7b11\u7740\uff0c\u88c5\u6210\u6109\u5feb \u7684\u58f0\u97f3\u8bf4\uff1a \u3000\u3000\u201c\u5927\u59d0\uff0c\u4f60\u53eb\u4f60\u5973\u513f\u4e5f\u4e0d\u8981\u53eb\u6211\u5415\u5927\u53d4\u597d\u4e0d\u597d\uff0c\u6211\u2026\u2026\u6211\u65e9\u5df2\u4e0d\u59d3\u5415\u4e86\uff0c\u53eb\u6211\u4f0a\u98ce\u597d \u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u540e\u6765\uff0c\u4ed6\u8bed\u6c14\u4e2d\u4f6f\u88c5\u7684\u6109\u5feb\u4e5f\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u5b59\u654f\u66f4\u6e05\u695a\u5730\u77e5\u9053\u4ed6\u5fc3\u91cc\uff0c\u5fc5\u5b9a\u6709\u7740\u6c89\u91cd\u7684\u5fc3\u4e8b\uff0c\u4e5f\u77e5\u9053\u4ed6\u4e0d\u613f\u610f\u5c06\u8fd9\u5fc3\u4e8b\u8bf4\u51fa \u6765\uff0c\u5979\u5c31\u4e0d\u518d\u95ee\uff0c\u592a\u591a\u7684\u60a3\u96be\uff0c\u592a\u591a\u7684\u5fe7\u90c1\uff0c\u4f7f\u5f97\u5979\u5bf9\u522b\u4eba\u5fc3\u91cc\u7684\u70e6\u607c\uff0c\u4e5f\u6709\u7740\u4e00\u4efd\u6df1\u9083\u7684 \u4e86\u89e3\u548c\u540c\u60c5\u3002 \u3000\u3000\u5979\u53ea\u662f\u8f6c\u5f00\u8bdd\u9898\uff0c\u7b11\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u53eb\u4f60\u4f0a\u98ce\uff0c\u5f53\u7136\u53ef\u4ee5\uff0c\u53ef\u662f\u96be\u9053\u4f60\u8981\u8ba9\u7433\u513f\u4e5f\u53eb\u4f60\u4f0a\u98ce\u5417\uff1f\u201d \u3000\u3000\u51cc\u7433\u5a07\u7b11\u7740\uff0c\u671b\u5411\u4ed6\uff0c\u4ed6\u6b63\u4e5f\u671b\u5411\u51cc\u7433\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u4ed6\u53c8\u5f3a\u81ea\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u5f53\u7136\uff0c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u53ef\u4ee5\u3002\u201d\u51cc\u7433\u5a07\u7f8e\u7684\u7b11\u5bb9\u6269\u6563\u5f97\u66f4\u5927\u4e86\uff0c\u5979\u671b\u7740\u5979\u5988\u5988\uff0c\u50cf\u662f\u81ea\u5df1 \u5df2\u53d8\u6210\u4e86\u5927\u4eba\u4f3c\u7684\u8bf4\uff1a \u3000\u3000\u201c\u4f0a\u98ce\u6ca1\u6709\u4e8b\uff0c\u6211\u4eec\u628a\u4ed6\u4e5f\u5e26\u5230\u5927\u963f\u59e8\u90a3\u91cc\u53bb\uff0c\u597d\u4e0d\u597d\uff01\u201d \u3000\u3000\u5979\u6545\u610f\u5c06\u201c\u4f0a\u98ce\u201d\u4e24\u5b57\u558a\u5f97\u5f85\u522b\u6e05\u695a\uff0c\u5b59\u654f\u8d23\u5907\u7684\u671b\u4e86\u5979\u4e00\u773c\uff0c\u53ef\u662f\u7b49\u5230\u5b59\u654f\u7684\u76ee\u5149\u7ecf \u8fc7\u4f0a\u98ce\u7684\u9762\u988a\uff0c\u518d\u671b\u5230\u51cc\u7433\u8eab\u4e0a\u7684\u65f6\u5019\uff0c\u5b59\u654f\u76ee\u5149\u4e2d\u7684\u8d23\u5907\u4e4b\u610f\uff0c\u4fbf\u50cf\u662f\u56e0\u4e3a\u7a81\u7136\u770b\u5230\u4e86\u4ec0 \u4e48\uff0c\u548c\u7a81\u7136\u60f3\u8d77\u4e86\u4ec0\u4e48\uff0c\u800c\u53d8\u4e3a\u4e00\u79cd\u6e29\u548c\u7684\u7b11\u610f\u3002 \u3000\u3000\u4e8e\u662f\u5979\u544a\u8bc9\u4f0a\u98ce\uff0c\u5979\u8fd9\u6b21\u5230\u897f\u6881\u5c71\u6765\uff0c\u662f\u4e3a\u4e86\u8981\u63a2\u671b\u4e00\u4e2a\u5df2\u6709\u591a\u5e74\u4e0d\u89c1\u7684\u5802\u59d0\uff0c\u5979\u8bf4\uff01 \u3000\u3000\u201c\u6211\u5df2\u7ecf\u6709\u8bb8\u4e45\u6ca1\u770b\u5230\u5979\u4e86\uff0c\u6211\u751a\u81f3\u4ece\u6765\u90fd\u6ca1\u6709\u60f3\u5230\u8981\u6765\u627e\u5979\uff0c\u53ef\u662f\u6700\u8fd1\u2026\u2026\u201d\u5979\u7b11\u4e86 \u7b11\uff1a\u201c\u5927\u6982\u662f\u56e0\u4e3a\u5e74\u7eaa\u5927\u4e86\u4e9b\u5427\uff0c\u6211\u7a81\u7136\u60f3\u5230\u6211\u5728\u4e16\u4e0a\u8fd8\u6709\u8fd9\u6837\u4e00\u4e2a\u4eb2\u4eba\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u8981\u6765\u627e \u5979\u3002\u201d \u3000\u3000\u5979\u7b11\u5bb9\u4e2d\u52a0\u4e86\u4e9b\u8f7b\u5fae\u7684\u53f9\u606f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f60\u5047\u5982\u6ca1\u6709\u4e8b\uff0c\u5c31\u4e00\u9f50\u53bb\u597d\u5417\uff1f\u5440\u2014\u2014\u201d\u5979\u7a81\u7136\u53c8\u9ad8\u5174\u8d77\u6765\uff1a\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u90a3\u5802 \u59d0\uff0c\u662f\u4e2a\u5f88\u5947\u602a\u7684\u5973\u4eba\uff0c\u800c\u4e14\u5ac1\u4e86\u4e2a\u5f88\u5947\u602a\u7684\u4e08\u592b\uff0c\u4f4f\u5728\u4e00\u4e2a\u5f88\u5947\u602a\u7684\u5730\u65b9\uff0c\u4f60\u53bb\u4e86\uff0c\u6211\u62c5 \u4fdd\u4f60\u4f1a\u89c9\u5f97\u53bb\u8fd9\u4e00\u8d9f\u4e0d\u662f\u6ca1\u6709\u4ef7\u503c\u7684\u4e8b\uff0c\u4e5f\u62c5\u4fdd\u4f60\u4e0d\u4f1a\u5931\u671b\u3002\u201d \u3000\u3000\u4f0a\u98ce\u60f3\u4e86\u60f3\uff1a \u3000\u3000\u201c\u6b64\u523b\uff0c\u6211\u8be5\u5230\u90a3\u91cc\u53bb\u5462\uff1f\u201d\u4ed6\u867d\u7136\u81ea\u89c9\u6709\u8bb8\u591a\u4e8b\u8981\u505a\uff0c\u53ef\u662f\u73b0\u5728\u5374\u4e0d\u77e5\u8be5\u5148\u505a\u90a3\u4e00 \u6837\uff0c\u66f4\u4e0d\u77e5\u9053\u5230\u90a3\u91cc\u53bb\uff0c\u4e8e\u662f\u4ed6\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6781\u5feb\u5730\u4e0a\u5c71\uff0c\u5b59\u654f\u62c9\u6742\u5730\u8bf4\u7740\u4e9b\u95f2\u8bdd\uff0c\u51cc\u7433\u5374\u5728\u65c1\u8fb9\u51fa\u795e\u5730\u671b\u7740\u4f0a\u98ce\uff0c\u5b59\u654f\u8bf4\uff1a \u3000\u3000\u201c\u6211\u867d\u7136\u597d\u4e45\u6ca1\u6709\u6765\u4e86\uff0c\u4e0a\u6b21\u6211\u6765\u4e86\u4e00\u6b21\uff0c\u90a3\u8fd8\u662f\u548c\u2026\u2026\u5509\uff0c\u90a3\u662f\u548c\u5317\u4fee\u4e00\u9f50\u6765\u7684\u3002\u201d \u5979\u773c\u5708\u7ea2\u4e86\u7ea2\uff0c\u4f46\u968f\u5373\u4e00\u7b11\uff1a\u201c\u53ef\u662f\u6211\u76f4\u5230\u73b0\u5728\uff0c\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u5230\u4ed6\u4eec\u5bb6\u91cc\u53bb\u7684\u8def\uff0c\u56e0\u4e3a\u4ed6\u4eec \u4f4f\u7684\u5730\u65b9\u592a\u7279\u522b\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff01 \u3000\u3000\u201c\u83ab\u975e\u662f\u4ed6\uff1f\u201d \u3000\u3000\u5374\u542c\u5979\u53c8\u9053\uff1a \u3000\u3000\u201c\u5251\u5148\u751f\u5462\uff1f\u4f60\u770b\u5230\u4ed6\u6ca1\u6709\uff1f\u201d\u4f0a\u98ce\u6447\u4e86\u6447\u5934\uff0c\u5b59\u654f\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u6709\u8bb8\u4e45\u6ca1\u6709\u89c1\u5230\u4ed6\u4e86\u3002\u201d\u5979\u5782\u4e86\u5782\u773c\uff1a \u3000\u3000\u201c\u4f60\u5230\u65e0\u91cf\u5c71\u53bb\uff0c\u600e\u4e48\u53bb\u4e86\u90a3\u4e48\u4e45\uff0c\u662f\u4e0d\u662f\u9047\u7740\u4ec0\u4e48\u4e8b\uff0c\u5509\u2014\u2014\u5bf9\u4e86\uff0c\u4f60\u53bb\u90a3\u91cc\u627e\u7684\u4e1c \u897f\uff0c\u5012\u5e95\u627e\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u5f85\u8bf4\u51fa\u81ea\u5df1\u8fd9\u4e00\u5e74\u591a\u6765\u79bb\u5947\u7684\u906d\u9047\uff0c\u5b59\u654f\u5374\u53c8\u559c\u547c\u4e00\u58f0\uff1a\u201c\u5230\u4e86\uff01\u201d \u5979\u8bf4\uff1a \u3000\u3000\u201c\u6211\u4eec\u5230\u4e86\u90a3\u91cc\uff0c\u4f60\u518d\u8bf4\u7ed9\u6211\u542c\u5427\uff0c\u6211\u77e5\u9053\u90a3\u4e00\u5b9a\u662f\u6bb5\u5f88\u957f\u7684\u6545\u4e8b\u3002\u201d\u5979\u811a\u6b65\u5fae\u987f\uff0c\u770b \u4e86\u770b\u77d7\u7acb\u5728\u524d\u9762\u5c71\u8def\u4e0a\u7684\u5c71\u5cf0\uff0c\u81ea\u8bed\u7740\u9053\uff1a \u3000\u3000\u201c\u4e00\u5b9a\u662f\u8fd9\u91cc\u3002\u201d\u8eab\u5f62\u4e00\u6298\u5411\u8def\u65c1\u7684\u5c71\u6797\u4e2d\u63a0\u53bb\u3002 \u3000\u3000\u521d\u6625\u7684\u5c71\u4e2d\uff0c\u67af\u6728\u4ecd\u591a\uff0c\u4f0a\u98ce\u63a0\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u5fc3\u5934\u5728\u5267\u70c8\u7684\u8df3\u52a8\u7740\uff0c\u56e0\u4e3a\u4ed6\u6b64\u523b\u5df2\u8bc1\u5b9e \u4e86\u81ea\u5df1\u65b9\u624d\u7684\u731c\u6d4b\uff01 \u3000\u3000\u201c\u5979\u7684\u5802\u59d0\uff0c\u679c\u7136\u5c31\u662f\u5ac1\u7ed9\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u7684\u90a3\u4e2a\u5973\u5b50\u3002\u5440\uff01\u679c\u7136\u662f\u4e2a\u5947\u602a\u7684\u5973\u5b50\uff0c\u5ac1 \u4e86\u4e2a\u5947\u602a\u7684\u4e08\u592b\uff0c\u4f4f\u5728\u4e2a\u5947\u602a\u7684\u5730\u65b9\uff0c\u5509\u2014\u2014\u53ef\u662f\u6211\u53c8\u600e\u80fd\u548c\u5979\u4e00\u9f50\u5230\u4e07\u5929\u840d\u90a3\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5176\u4e2d\u53ea\u6709\u51cc\u7433\u662f\u5174\u594b\u7684\uff0c\u4e00\u63a0\u5165\u5c71\u6797\uff0c\u5979\u5c31\u7d27\u7d27\u6293\u4f4f\u4f0a\u98ce\u7684\u624b\u638c\uff0c\u5979\u662f\u90a3\u4e48\u5174\u594b\uff0c\u6240 \u4ee5\u5979\u7adf\u6ca1\u6709\u53d1\u73b0\u4f0a\u98ce\u624b\u638c\u7684\u98a4\u6296\uff0c\u53ea\u542c\u5b59\u654f\u9053\uff1a \u3000\u3000\u201c\u4e0a\u6b21\u6211\u6765\u7684\u65f6\u5019\uff0c\u59d0\u592b\u4e0d\u5728\uff0c\u8fd9\u6b21\uff0c\u4ed6\u8be5\u56de\u6765\u4e86\u5427\uff01\u201d \u3000\u3000\u4f0a\u98ce\u6f2b\u5e94\u7740\uff0c\u4ed6\u6b64\u523b\u5df2\u9677\u5165\u65e0\u9650\u7684\u77db\u76fe\u4e2d\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u662f\u4e0d\u8be5\u548c\u5979\u4eec\u6bcd\u5973\u4e00\u9f50\u53bb\u7684\uff0c\u4f46 \u662f\u4ed6\u770b\u5230\u5979\u4eec\u6bcd\u5973\u7684\u7b11\u5bb9\uff0c\u5c24\u5176\u662f\u9762\u5bf9\u7740\u51cc\u7433\u8fd9\u4e2a\u7eaf\u771f\u7684\u5c11\u5973\uff0c\u4ed6\u53c8\u5b9e\u5728\u4e0d\u77e5\u8be5\u5982\u4f55\u6765\u62d2\u7edd \u5979\u4eec\u3002 \u3000\u3000\u4e00\u5165\u5c71\u6797\uff0c\u4ed6\u4eec\u7684\u811a\u6b65\u5c31\u5feb\u4e86\u8d77\u6765\uff0c\u5411\u53f3\u4e00\u6298\u4e4b\u540e\uff0c\u4fbf\u662f\u90a3\u6761\u5bbd\u7ea6\u56db\u5c3a\uff0c\u873f\u8712\u4e0a\u884c\u7684\u5c71 \u8def\uff0c\u671b\u7740\u8fd9\u6761\u5c71\u8def\uff0c\u4ed6\u6697\u95ee\u81ea\u5df1\uff1a \u201c\u6211\u7a76\u7adf\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u7ae0\u3000\u508d\u58d1\u60ca\u53d8>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u7ae0\u3000\u508d\u58d1\u60ca\u53d8 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u5c71\u8def\u4e0a\u7adf\u4f20\u4e0b\u4e00\u9635\u6d2a\u949f\u822c\u7684\u559d\u9a82\u4e4b\u58f0\uff1a \u3000\u3000\u201c\u8001\u7334\u5b50\uff0c\u4f60\u8eb2\u5728\u91cc\u9762\u4e0d\u51fa\u6765\uff0c\u7b97\u5f97\u662f\u4ec0\u4e48\u82f1\u96c4\uff0c\u54c8\u54c8\u2014\u2014\u6211\u53ea\u5f53\u94c1\u9762\u5b64\u884c\u5ba2\u662f\u4e2a\u82f1 \u96c4\uff0c\u90a3\u77e5\u5374\u662f\u4e2a\u72d7\u718a\u3002\u201d \u3000\u3000\u4f0a\u98ce\u4e00\u60ca\uff0c\u4ed6\u7acb\u523b\u542c\u51fa\u8fd9\u662f\u201c\u5999\u624b\u201d\u8bb8\u767d\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5b59\u654f\uff0c\u51cc\u7433\uff0c\u7adf\u81ea\u4e00\u9f50\u90fd\u9762\u8272\u4e00\u53d8\uff0c\u5b59\u654f\u4f3c\u4e5f\u77e5\u9053\u201c\u5357\u5077\u5317\u76d7\u201d\u95f4\u81f3\u6b7b\u65b9\u4f11\u7684\u4e89\u6597\uff0c\u95fb \u58f0\u53d8\u8272\u9053\uff1a \u3000\u3000\u201c\u662f\u4e0d\u662f\u201c\u5999\u624b\u201d\u8bb8\u767d\u4e5f\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u51cc\u7433\u79c0\u7709\u4e00\u8f69\uff0c\u55d4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5bb6\u4f19\u662f\u4ec0\u4e48\u4eba\uff0c\u600e\u5730\u5982\u6b64\u72c2\u5984\uff0c\u8d70\uff0c\u6211\u4eec\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u5979\u4e00\u62c9\u4f0a\u98ce\u7684\u624b\uff0c\u98de\u4e5f\u4f3c\u7684\u4e0a\u4e86\u8fd9\u6761\u5c71\u8def\uff0c\u4f0a\u98ce\u5fc3\u4e2d\u867d\u7136\u72b9\u7591\u4e0d\u5b9a\uff0c\u5374\u4e5f\u8ddf\u7740\u5979\u63a0\u4e86\u4e0a \u53bb\uff0c\u4f46\u89c9\u5979\u8eab\u5f62\u4e4b\u8f7b\u76c8\u66fc\u5999\uff0c\u5df2\u4e0d\u77e5\u8d85\u51fa\u5e74\u524d\u82e5\u5e72\u500d\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u7981\u6697\u6697\u8d5e\u4f69\uff0c\u90a3\u201c\u4e09\u5fc3\u795e \u541b\u201d\u679c\u7136\u6559\u5bfc\u6709\u65b9\uff0c\u5728\u8fd9\u77ed\u77ed\u4e00\u5e74\u6765\uff0c\u4fbf\u5df2\u8c03\u6559\u51fa\u8fd9\u822c\u51fa\u8272\u7684\u5f92\u5f1f\u3002 \u3000\u3000\u4e0a\u63a0\u6570\u5341\u4e08\uff0c\u4f7f\u5230\u4e86\u5c71\u8def\u7684\u5c3d\u5934\uff0c\u4f0a\u98ce\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u4e00\u6761\u9b41\u4f1f\u7684\u4eba\u5f71\uff0c\u500f\u7136\u4ece\u5c71\u8def\u5c3d \u5934\u5904\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u9b41\u4f1f\u7684\u4eba\u5f71\u81ea\u7136\u5c31\u662f\u201c\u5999\u624b\u201d\u8bb8\u767d\uff0c\u4ed6\u559d\u58f0\u8fc7\u540e\uff0c\u9510\u5229\u7684\u76ee\u5149\u7acb\u523b\u8fa8\u51fa\u63a0\u4e0a\u7684\u4eba\u5f71\u662f \u8c01\uff0c\u4e00\u6447\u866c\u987b\uff0c\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u662f\u4f60\uff0c\u54c8\u2014\u2014\u600e\u5730\u53c8\u5e26\u4e86\u4e2a\u5c0f\u59d1\u5a18\u6765\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u77a5\u89c1\u5b59\u654f\uff1a \u3000\u3000\u201c\u54c8\u4e24\u4e2a\uff01\u201d \u3000\u3000\u672c\u81ea\u6ee1\u5fc3\u55d4\u6012\u7684\u51cc\u7433\uff0c\u89c1\u5230\u81ea\u5df1\u55d4\u6012\u7684\u5bf9\u8c61\u7adf\u662f\u8ba4\u5f97\u4f0a\u98ce\u7684\uff0c\u4e0d\u7981\u6115\u4e86\u4e00\u6115\uff0c\u628a\u53e3\u8fb9\u8981 \u9a82\u7684\u8bdd\uff0c\u5fcd\u4e86\u56de\u6765\u3002 \u3000\u3000\u5b59\u654f\u4ea6\u4e3a\u4e4b\u4e00\u6115\uff0c\u8131\u53e3\u9053\uff1a \u3000\u3000\u201c\u4f0a\u98ce\uff0c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u5999\u624b\u201d\u8bb8\u767d\u53c8\u81ea\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u4e00\u6b65\u5411\u524d\uff0c\u63e1\u4f4f\u4f0a\u98ce\u7684\u81c2\u8180\uff0c\u5927 \u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u4f60\u6765\u4e86\uff0c\u597d\u6781\u4e86\uff0c\u8ba9\u4f60\u4e5f\u770b\u7740\u4e07\u5929\u840d\u90a3\u8001\u7334\u513f\u7684\u666f\u50cf\uff0c\u6211\u548c\u4ed6\u4e00\u8def\u6253\u5230\u8fd9\u91cc\uff0c\u90a3\u4ead\u5b50 \u91cc\u7a81\u7136\u8dd1\u51fa\u4e00\u4e2a\u5973\u4eba\u6765\uff0c\u5bf9\u4e07\u5929\u840d\u53c8\u558a\u53c8\u53eb\u7684\uff0c\u54c8\u54c8\u2014\u2014\u4f60\u77e5\u9053\u6211\u662f\u751f\u5e73\u6700\u4e0d\u613f\u548c\u5973\u4eba\u565c\u55e6 \u7684\uff0c\u521a\u505c\u4e0b\u624b\uff0c\u8ba9\u4ed6\u8bb2\u4e2a\u75db\u5feb\uff0c\u90a3\u77e5\u90a3\u8fb9\u7a81\u5730\u98de\u6765\u4e00\u6761\u5e26\u5b50\uff0c\u4e07\u5929\u840d\u8fd9\u8001\u7334\u513f\u7adf\u62c9\u7740\u8fd9\u6761\u5e26 \u5b50\u8dd1\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4f38\u51fa\u5de8\u5927\u7684\u624b\u638c\uff0c\u5728\u81ea\u5df1\u53e6\u4e00\u53ea\u624b\u638c\u4e0a\u91cd\u91cd\u4e00\u51fb\uff0c\u72c2\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u4ed6\u8fd9\u4e00\u53bb\uff0c\u7adf\u51fa\u4e0d\u6765\u4e86\uff0c\u6211\u5728\u8fd9\u91cc\u9a82\u4e86\u534a\u5929\uff0c\u4ed6\u5374\u50cf\u4e2a\u7f29\u5934\u2026\u2026\u201d \u3000\u3000\u51cc\u7433\u79c0\u7709\u8f69\u5904\uff0c\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u5a07\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u600e\u4e48\u9a82\u8d77\u6211\u59e8\u7236\u6765\u4e86\uff0c\u8fd9\u4e48\u5927\u5e74\u7eaa\u4e86\uff0c\u8fd8\u50cf\u4e2a\u5c0f\u5b69\u5b50\u4f3c\u7684\u9a82\u4eba\uff0c\u54fc\uff0c\u771f\u4e8f\u4f60 \u4e5f\u4e0d\u77e5\u9053\u4ec0\u4e48\u53eb\u5bb3\u81ca\uff01\u201d \u3000\u3000\u201c\u5999\u624b\u201d\u8bb8\u767d\u4e00\u6014\uff1a \u3000\u3000\u201c\u4f60\u7684\u59e8\u7236\uff01\u201d\u76ee\u5149\u5411\u53f3\u4e00\u779f\uff0c\u779f\u4e86\u4f0a\u98ce\u4e00\u773c\uff0c\u5411\u5de6\u4e00\u779f\uff0c\u671b\u4e86\u51cc\u7433\u4e00\u773c\uff0c\u5de6\u53f3\u8f6c\u4e86\u4e24 \u8f6c\uff0c\u7a81\u53c8\u7eb5\u58f0\u72c2\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u5c0f\u59d1\u5a18\uff0c\u6709\u5fd7\u6c14\uff0c\u6570\u5341\u5e74\u6765\uff0c\u771f\u8fd8\u6ca1\u6709\u4eba\u6562\u9a82\u8fc7\u8001\u592b\u7684\uff0c\u73b0\u5728\u4f60\u5c45\u7136\u8bf4\u8001\u592b \u50cf\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4e0d\u5bb3\u81ca\uff0c\u54c8\u54c8\u2014\u2014\u201d\u4ed6\u6307\u4e86\u6307\u4f0a\u98ce\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u4ea4\u7684\u5c0f\u59d1\u5a18\uff0c\u771f\u662f\u4e00\u4e2a\u6bd4\u4e00\u4e2a\u5389\u5bb3\u3002\u201d \u3000\u3000\u4f0a\u98ce\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u5b59\u654f\u5df2\u81ea\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u60f3\u5fc5\u5c31\u662f\u201c\u5999\u624b\u201d\u8bb8\u767d\u5927\u4fa0\u5427\uff1f\u201d \u3000\u3000\u201c\u5999\u624b\u201d\u8bb8\u767d\u53c8\u4e00\u6014\uff0c\u70b9\u4e86\u70b9\u5934\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8001\u592b\u6b63\u662f\u8bb8\u767d\u3002\u201d \u3000\u3000\u5b59\u654f\u51b7\u51b7\u4e00\u7b11\uff1a \u3000\u3000\u201c\u8bb8\u5927\u4fa0\u82f1\u540d\u4e45\u5df2\u9707\u52a8\u6b66\u6797\uff0c\u8bf4\u8bdd\u4e5f\u8be5\u653e\u5c0a\u91cd\u4e9b\uff0c\u4e5f\u597d\u53eb\u5c0f\u8f88\u4eec\u5b66\u5b66\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u5999\u624b\u201d\u8bb8\u767d\u76ee\u5149\u4e00\u51db\uff0c\u987b\u53d1\u7686\u5f20\uff0c\u672c\u5df2\u9b41\u4f1f\u65e0\u6bd4\u7684\u8eab\u5f62\uff0c\u500f\u7136\u4e4b\u95f4\uff0c\u50cf\u662f\u53c8\u9ad8\u5927\u4e86 \u4e9b\u3002 \u3000\u3000\u4f46\u5b59\u654f\u5374\u4ecd\u7136\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\uff0c\u50cf\u662f\u4e16\u4e0a\u4efb\u4f55\u4e8b\u90fd\u4e0d\u8db3\u4ee5\u4ee4\u8fd9\u4e2a\u575a\u5f3a\u7684\u5973\u5b50\u60e7\u6015\u4f3c\u7684\uff0c\u5374 \u89c1\u201c\u5999\u624b\u201d\u8bb8\u767d\u76ee\u5149\u4e00\u8f6c\uff0c\u7adf\u7a81\u53c8\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u54c8\uff0c\u4f60\u9519\u4e86\uff0c\u4f60\u9519\u4e86\uff0c\u6211\u53c8\u4e0d\u662f\u5927\u4fa0\uff0c\u6211\u53ea\u662f\u4e2a\u5c0f\u5077\uff01\u201d\u7b11\u58f0\u4e00\uff0c\u76ee\u4e2d\u5a01\u5149\u53c8\u73b0\uff0c\u63a5 \u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u6211\u5012\u8981\u95ee\u95ee\u4f60\uff0c\u4f60\u662f\u8c01\uff0c\u51ed\u4ec0\u4e48\u8981\u7ba1\u8001\u592b\u7684\u95f2\u4e8b\uff1f\u201d\u8f6c\u5411\u4f0a\u98ce\uff1a \u3000\u3000\u201c\u8001\u592b\u82e5\u4e0d\u662f\u4e3a\u4e86\u548c\u4f60\u8fd9\u5a03\u5a03\u8fd8\u6709\u4e9b\u4ea4\u60c5\uff0c\u65e9\u5c31\u2026\u2026\u201d \u3000\u3000\u4f0a\u98ce\u5e72\u54b3\u4e00\u58f0\uff0c\u62a2\u53e3\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u51cc\u592b\u4eba\uff0c\u4fbf\u662f\u6614\u5e74\u7684\u4e09\u6e58\u5927\u4fa0\u51cc\u5317\u4fee\u7684\u592b\u4eba\uff0c\u8fd9\u4f4d\u5c31\u662f\u4e09\u6e58\u5927\u4fa0\u7684\u7231\u5973\uff0c\u54b3\u2014\u2014 \u51cc\u592b\u4eba\u7684\u4ee4\u59d0\uff0c\u5c31\u662f\u4e07\u8001\u524d\u8f88\u7684\u592b\u4eba\u3002\u201d\u4ed6\u50cf\u662f\u8d39\u4e86\u504c\u5927\u6c14\u529b\uff0c\u624d\u5c06\u8fd9\u5176\u4e2d\u7684\u5173\u7cfb\u5f04\u6e05\u3002 \u3000\u3000\u201c\u5999\u624b\u201d\u8bb8\u767d\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u8981\u77e5\u9053\u6614\u5e74\u201c\u4e09\u6e58\u5927\u4fa0\u201d\u4fa0\u540d\u751a\u7740\uff0c\u800c\u4e14\u5728\u6b66\u6797\u4e2d\u66f4\u4e45\u6709 \u4e49\u58f0\uff0c\u662f\u4ee5\u201c\u5999\u624b\u201d\u8bb8\u767d\u8fd9\u79cd\u6b66\u6797\u9ad8\u4eba\uff0c\u542c\u4e86\u8fd9\u540d\u5b57\u4e5f\u4e0d\u7981\u6709\u4e9b\u656c\u610f\uff0c\u8fd9\u6b63\u662f\u6b66\u6797\u8c6a\u5ba2\u5f7c\u6b64 \u95f4\u7684\u76f8\u60dc\u4e4b\u5fc3\uff0c\u5374\u4e0d\u662f\u8bf4\u201c\u5999\u624b\u201d\u8bb8\u767d\u5bf9\u90a3\u51cc\u5317\u4fee\u6709\u6240\u754f\u60e7\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u4fbf\u53c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u51b2\u7740\u4f60\u8fd9\u5a03\u5a03\u548c\u51cc\u5317\u4fee\u7684\u9762\u5b50\uff0c\u6211\u4e0d\u518d\u9a82\u90a3\u8001\u7334\u513f\u5c31\u662f\uff0c\u53ef\u662f\uff0c\u6211\u5374\u8981\u5728\u8fd9\u91cc\u7b49\u4ed6\uff0c \u770b\u4ed6\u662f\u4e0d\u662f\u6c38\u8fdc\u90fd\u7f29\u5728\u2026\u2026\u54c8\u54c8\uff0c\u6c38\u8fdc\u90fd\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u4e0d\u9a82\uff0c\u5012\u5e95\u8fd8\u662f\u9a82\u4e86\u53e5\u201c\u8001\u7334\u5b50\u201d\uff0c\u800c\u4e14\u82e5\u4e0d\u662f\u4f4f\u58c1\u5f97\u5feb\uff0c\u540e\u6765\u90a3\u53e5\u201c\u7f29\u5728\u58f3 \u91cc\u201d\u4e5f\u51e0\u51e0\u4e4e\u9a82\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u91cc\u6697\u7b11\uff0c\u51cc\u7433\u4e5f\u89c9\u5f97\u8fd9\u8001\u4eba\u751a\u662f\u8c6a\u723d\u6709\u8da3\uff0c\u671b\u4e86\u4ed6\u51e0\u773c\uff0c\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u201c\u5657\u54e7\u201d\u4e00 \u58f0\uff0c\u7b11\u4e86\u51fa\u6765\uff0c\u5979\u5929\u6027\u5584\u826f\uff0c\u5bf9\u4efb\u4f55\u4eba\u90fd\u6ca1\u6709\u6000\u6068\u4e4b\u5fc3\uff0c\u4f55\u51b5\u8fd9\u8001\u4eba\u53c8\u662f\u548c\u4f0a\u98ce\u8ba4\u8bc6\u7684\u5462\uff01 \u3000\u3000\u53ea\u6709\u5b59\u654f\uff0c\u5979\u9762\u4e0a\u5374\u4ecd\u7136\u4e0d\u52a8\u58f0\u8272\uff0c\u5979\u60f3\u547c\u559d\u4e24\u58f0\uff0c\u8ba9\u4e07\u5929\u840d\u77e5\u9053\u81ea\u5df1\u6765\u4e86\uff0c\u53c8\u6015\u4e07\u5929 \u840d\u51fa\u6765\u540e\uff0c\u89c1\u7740\u8fd9\u201c\u5999\u624b\u201d\u8bb8\u767d\uff0c\u90a3\u65f6\u60c5\u51b5\u5c82\u975e\u5341\u5206\u5c34\u5c2c\uff0c\u5979\u60f3\u4e86\u60f3\uff0c\u5374\u89c1\u201c\u5999\u624b\u201d\u8bb8\u767d\u5927 \u7b11\u4e86\u4e24\u58f0\uff0c\u53c8\u9762\u5bf9\u7740\u5bf9\u5d16\u7684\u51cc\u7a7a\u98de\u9601\uff0c\u76d8\u819d\u5750\u4e86\u4e0b\u6765\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u56e0\u4e3a\u8c01\u90fd \u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u51cc\u7433\u79cb\u6ce2\u6d41\u8f6c\uff0c\u671b\u4e1c\u671b\u897f\uff0c\u957f\u4e45\u826f\u4e45\uff0c\u7a81\u5730\u5e7d\u5e7d\u4e00\u53f9\uff1a \u3000\u3000\u201c\u4f60\u4eec\u5012\u5e95\u5728\u7b49\u4ec0\u4e48\uff0c\u5509\u2014\u2014\u7b49\u5f85\u53ef\u771f\u662f\u96be\u53d7\u7684\u4e8b\uff0c\u5148\u524d\u6211\u4e0a\u5c71\u7684\u65f6\u5019\uff0c\u770b\u5230\u6709\u4e2a\u8001\u5934 \u5b50\u5728\u4ead\u5b50\u91cc\u7b49\u4ed6\u7684\u4e09\u5f1f\uff0c\u7adf\u597d\u50cf\u7b49\u4e86\u4e00\u591c\u2014\u2014\u201d \u3000\u3000\u4f0a\u98ce\u5fc3\u5934\u4e00\u9707\uff0c\u8131\u53e3\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u8bf4\u8c01\u5728\u7b49\u4ed6\u7684\u4e09\u5f1f\uff0c\u90a3\u4eba\u662f\u4e0d\u662f\u8eab\u6750\u7626\u524a\uff0c\u6ee1\u9762\u5fe7\u90c1\u4e4b\u8272\u7684\u8001\u4eba\u3002\u201d \u3000\u3000\u51cc\u7433\u7741\u5927\u773c\u775b\uff0c\u70b9\u5934\u9053\uff1a \u3000\u3000\u201c\u662f\u5440\uff0c\u9664\u4e86\u90a3\u8001\u5934\u5b50\uff0c\u53e6\u5916\u8fd8\u6709\u4e09\u4e2a\u4eba\uff0c\u4ed6\u4eec\u90fd\u7a7f\u7740\u84dd\u8863\u670d\uff0c\u600e\u4e48\uff0c\u4f60\u53c8\u8ba4\u5f97\u4ed6 \u4eec\uff1f\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4f0a\u98ce\u53ea\u89c9\u7531\u5fc3\u5e95\u5347\u8d77\u4e00\u9635\u98a4\u6296\uff0c\u95ea\u7535\u822c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u56db\u4eba\u96be\u9053\u5c31\u662f\u534e\u54c1\u5947\u4ed6\u4eec\uff0c\u5728\u7b49\u201c\u4e09\u5f1f\u201d\uff01\u5440\u2014\u2014\u201d\u5251\u7709\u4e00\u8f69\uff0c\u4e00\u628a\u6293\u4f4f\u51cc\u7433\u7684 \u624b\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u51cc\u7433\u6ee1\u5fc3\u7591\u60d1\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u5c31\u5728\u5c71\u8170\u4e0b\u7684\u4e00\u4e2a\u7ea2\u9876\u5c0f\u4ead\u91cc\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5168\u8eab\u98a4\u6296\u7740\uff1a\u201c\u4e09\u5f1f\uff0c\u96be\u9053\u4ed6\u4eec\u5728\u7b49\u8427\u65e0\uff1f\u96be\u9053\u4ed6\u4eec\u5df2\u627e\u5230\u4e86\u8427\u65e0\uff1f\u201d\u4e00\u54ac\u94a2\u7259\uff0c \u7a81\u5730\u626d\u8f6c\u8eab\u8eaf\uff0c\u7535\u4e5f\u4f3c\u5730\u671d\u6765\u8def\u63a0\u53bb\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u4e2d\u4f3c\u5df2\u6d51\u5fd8\u4e00\u5207\uff0c\u53ea\u8bb0\u5f97\u201c\u8427\u65e0\u201d\u4e8c\u5b57\uff0c\u4ed6\u6beb\u4e0d\u8003\u8651\u81ea\u5df1\u89c1\u7740\u201c\u8427\u65e0\u201d\u65f6\u8be5\u600e \u4e48\u505a\uff0c\u66f4\u4e0d\u8003\u8651\u81ea\u5df1\u662f\u5426\u662f\u8fd9\u201c\u5929\u4e89\u6559\u4e3b\u201d\u7684\u654c\u624b\uff0c\u4ed6\u53ea\u6e34\u671b\u7740\u80fd\u89c1\u5230\u8fd9\u9634\u9669\u6bd2\u8fa3\u7684\u5bf9\u624b\u4e00 \u9762\uff0c\u56e0\u4e3a\u4e45\u5df2\u8574\u96c6\u5728\u4ed6\u5fc3\u91cc\u7684\u4ec7\u6068\uff0c\u6b64\u523b\u5df2\u50cf\u706b\u5c71\u822c\u7206\u53d1\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u53d1\u72c2\u4e86\u4f3c\u7684\u63a0\u4e0b\u5c71\u5761\uff0c\u201c\u5999\u624b\u201d\u8bb8\u767d\uff0c\u5b59\u654f\uff0c\u51cc\u7433\u5374\u4e0d\u7981\u4e3a\u4e4b\u4e00\u9707\uff0c\u4e09\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c \u51cc\u7433\u7a81\u5730\u5a07\u5524\u4e00\u58f0\uff1a \u3000\u3000\u201c\u5988\u5988\uff0c\u6211\u53bb\u770b\u770b\u4ed6\u5e72\u4ec0\u4e48\uff1f\u201d\u5a07\u8eaf\u8f7b\u8f6c\uff0c\u4ea6\u81ea\u968f\u540e\u8ddf\u53bb\u3002 \u3000\u3000\u5979\u7528\u8db3\u5168\u529b\uff0c\u53ea\u89c1\u4f0a\u98ce\u4fee\u957f\u7684\u8eab\u8eaf\uff0c\u50cf\u71d5\u5b50\u4f3c\u7684\u5728\u5c71\u6797\u4e2d\u98de\u63a0\u7740\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u63a0\u51fa\u5c71 \u6797\uff0c\u51cc\u7433\u60f3\u4e0d\u5230\u4ed6\u7684\u8eab\u5f62\u7adf\u662f\u5982\u6b64\u60ca\u4eba\u7684\u8fc5\u6025\uff0c\u5979\u7eb5\u7136\u4f7f\u5c3d\u5168\u529b\uff0c\u5374\u4e5f\u65e0\u6cd5\u8ffd\u4e0a\uff0c\u5979\u7740\u6025\u5730 \u5927\u558a\uff1a \u3000\u3000\u201c\u4f0a\u98ce\uff0c\u7b49\u7b49\u6211\u2014\u2014\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u867d\u5927\uff0c\u4f0a\u98ce\u5374\u6839\u672c\u6ca1\u6709\u542c\u5230\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u6cd5\uff0c\u4f3c\u4e4e\u5df2\u6bd4\u6614\u65e5\u5feb\u4e86\u4e0d\u77e5\u591a\u5c11\uff0c\u8981\u77e5\u9053\u6b66\u529f\u4e00\u9053\uff0c\u6700\u662f\u7384\u5999\uff0c \u4ed6\u81ea\u5e7c\u53ca\u957f\uff0c\u591a\u5e74\u82e6\u7ec3\uff0c\u672c\u5df2\u624e\u4e0b\u4e86\u6781\u597d\u7684\u6839\u57fa\uff0c\u518d\u7ecf\u90a3\u6b66\u6797\u4e00\u4ee3\u5947\u4eba\u201c\u5251\u5148\u751f\u201d\u4e3a\u4ed6\u6253\u901a \u201c\u4efb\u7763\u4e24\u8109\u201d\uff0c\u4ed6\u5185\u529b\u4f55\u6b62\u589e\u8fdb\u4e00\u501f\uff0c\u5230\u540e\u6765\u4ed6\u5728\u79d8\u7a9f\u4e2d\u53c8\u82e6\u7814\u90a3\u6b66\u6797\u4e2d\u7684\u81f3\u5b9d\u201c\u5929\u661f\u79d8 \u7b08\u201d\u4e0a\u6240\u8f7d\u7684\u6b66\u5b66\u4e2d\u6700\u6df1\u5965\u7684\u5185\u529f\uff0c\u529f\u529b\u53c8\u4e0d\u77e5\u589e\u8fdb\u82e5\u5e72\uff0c\u53ea\u662f\u4ed6\u81ea\u5df1\u5374\u8fd8\u4e0d\u77e5\u9053\u800c\u5df2\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u4f7f\u51fa\u5168\u529b\uff0c\u4ed6\u624d\u77e5\u9053\u4e86\u81ea\u5df1\u529f\u529b\u7684\u589e\u957f\uff0c\u4ed6\u53d1\u72c2\u5730\u98de\u63a0\u7740\uff0c\u53ea\u89c9\u9053\u5c71\u4e24\u65c1 \u7684\u6811\u6728\uff0c\u50cf\u98de\u4e5f\u4f3c\u7684\u4ece\u8eab\u7554\u5012\u9000\u8fc7\u53bb\u3002\u4ed6\u5fc3\u4e2d\u7684\u70ed\u8840\uff0c\u4e5f\u5f00\u59cb\u6cb8\u817e\u8d77\u6765\uff0c\u4ed6\u5174\u594b\u5730\u6697\u95ee\u7740\u81ea \u5df1\uff1a\u201c\u4e0d\u77e5\u9053\u201c\u8427\u65e0\u201d\u53ef\u662f\u771f\u8981\u5230\u90a3\u5c0f\u4ead\u4e2d\u4f1a\u89c1\u534e\u54c1\u5947\uff1f\u4e0d\u77e5\u9053\u6b64\u523b\u4ed6\u53ef\u66fe\u8d70\u4e86\uff1f\u201d\u5fc3\u610f\u4e00 \u8f6c\uff1a\u201c\u6211\u6b64\u523b\u529f\u529b\u4f3c\u5df2\u589e\u8fdb\u4e0d\u5c11\uff0c\u4e0d\u77e5\u9053\u662f\u5426\u662f\u90a3\u4e07\u6076\u7684\u8d3c\u5b50\u8427\u65e0\u7684\u654c\u624b\uff1f\u201d\u4ed6\u6025\u5207\u5730\u6e34\u671b \u7740\u8981\u77e5\u9053\u8fd9\u4e9b\u95ee\u9898\u7684\u7b54\u6848\u3002\u4e8e\u662f\uff0c\u4ed6\u7684\u8eab\u5f62\u5c31\u98de\u63a0\u5f97\u66f4\u5feb\u4e86\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u4e00\u7ae0\u3000\u6b7b\u4e0b\u7791\u76ee>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u4e00\u7ae0\u3000\u6b7b\u4e0b\u7791\u76ee \u3000\u3000\u6f2b\u5c71\u6625\u9633\uff0c\u6f2b\u5c71\u91d1\u9ec4\u3002 \u3000\u3000\u4f0a\u98ce\u7728\u52a8\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u53ea\u89c9\u9762\u4e0a\u7684\u808c\u8089\uff0c\u5f77\u4f5b\u50cf\u662f\u6709\u79cd\u5e72\u88c2\u4e86\u7684\u75db\u82e6\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\u81ea\u5df1 \u7684\u9762\u5bb9\uff0c\u4e0d\u4f46\u591a\u65e5\u672a\u89c1\u9633\u5149\uff0c\u800c\u4e14\u4e5f\u5df2\u7ecf\u6709\u591a\u65e5\u672a\u66fe\u6d17\u6da4\u4e86\uff0c\u6613\u5bb9\u540e\u7684\u4eba\u76ae\u9762\u5177\uff0c\u4e00\u76f4\u91cc\u7740 \u4ed6\u7684\u8138\uff0c\u5c31\u50cf\u662f\u98ce\u5e72\u4e86\u7684\u9c7c\u76ae\u4f3c\u7684\uff0c\u4ed6\u4e0d\u7981\u6697\u5730\u5632\u7b11\u81ea\u5df1\u3002 \u3000\u3000\u201c\u539f\u6765\u6613\u5bb9\u662f\u4ef6\u8fd9\u4e48\u96be\u53d7\u7684\u4e8b\uff0c\u6211\u53ea\u4e0d\u8fc7\u624d\u5fcd\u53d7\u4e86\u77ed\u77ed\u6570\u6708\u7684\u65f6\u5019\u800c\u5df2\uff0c\u60f3\u5f53\u5e74\u90a3\u4f4d\u8427 \u4e09\u7237\uff0c\u53ef\u771f\u4e0d\u77e5\u4ed6\u65e5\u5b50\u662f\u600e\u4e48\u8fc7\u7684\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5f62\u4e0d\u505c\uff0c\u4f38\u624b\u6478\u4e86\u5e72\u71e5\u7684\u9762\u9765\uff1a \u3000\u3000\u201c\u5509\uff0c\u6211\u8be5\u627e\u4e9b\u6c34\u4e86\u3002\u201d\u4ed6\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u4f46\u662f\uff0c\u8fd9\u4e5f\u8981\u7b49\u6211\u627e\u7740\u8427\u65e0\u4e4b\u540e\u3002\u201d \u3000\u3000\u4ed6\u4ece\u4e0d\u77e5\u9053\uff0c\u4ec7\u6068\u53ef\u4ee5\u4f7f\u4eba\u5fcd\u53d7\u8fd9\u4e48\u591a\u51e0\u4e4e\u4e0d\u80fd\u5fcd\u53d7\u7684\u4e8b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6b64\u523b\uff0c\u4ed6\u5374\u77e5\u9053\u4e86\uff0c\u975e\u5e38\u6df1\u5207\u5730\u77e5\u9053\u4e86\u3002\u4ed6\u5ffd\u7136\u60f3\u5230\u5982\u679c\u4ed6\u81ea\u5df1\u662f\u52fe\u8df5\u7684\u8bdd\uff0c\u4ed6 \u4e5f\u4e00\u6837\u5730\u4f1a\u505a\u51fa\u5367\u85aa\u5c1d\u80c6\u8fd9\u4e00\u7c7b\u4e8b\u7684\u3002 \u3000\u3000\u7eb7\u4e71\u7684\u601d\u6f6e\uff0c\u6cb8\u817e\u7684\u70ed\u8840\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u7a76\u7adf\u5954\u63a0\u4e86\u591a\u4e45\uff0c\u4e5f\u8bb8\uff0c\u53ea\u662f\u970e\u773c\u4e4b\u95f4\u5427\u3002 \u3000\u3000\u76ee\u5149\u62ac\u5904\uff0c\u6ee1\u5c71\u65b9\u62bd\u65b0\u7eff\u7684\u6797\u6728\u63a9\u6620\u4e2d\uff0c\u679c\u81ea\u9732\u51fa\u7ea2\u4ead\u4e00\u89d2\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u4e0d\u7981\u4e3a\u4e4b\u72c2\u559c\u5730\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u6ee1\u5f15\u4e00\u53e3\u771f\u6c14\uff0c\u500f\u7136\u6570\u4e2a\u8d77\u843d\uff0c\u90a3\u9065\u8fdc\u7684\u7ea2\u4ead\uff0c\u4fbf \u7a81\u5730\u50cf\u9b54\u672f\u822c\u8df3\u5230\u4ed6\u773c\u524d\u5440\uff0c\u8fd9\u662f\u4e00\u79cd\u591a\u4e48\u5947\u5999\u7684\u4eab\u53d7\u5440\uff0c\u53ea\u662f\u4f60\u82e5\u6ca1\u6709\u7edd\u9876\u7684\u8f7b\u529f\uff0c\u4fbf\u4e07 \u4e07\u68a6\u60f3\u4e0d\u5230\u8fd9\u79cd\u4eab\u53d7\u7684\u5883\u754c\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u66fc\u5999\u5730\u5c06\u771f\u6c14\u4e00\u8f6c\uff0c\u8eab\u5f62\u518d\u6b21\u4e00\u63a0\uff0c\u7eb5\u8eab\u5411\u8fd9\u5c71\u4ead\u6251\u4e0b\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\u2014\u2014 \u3000\u3000\u4ed6\u4f46\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u201c\u5440\u201d\u5730\u4e00\u58f0\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u8e6c\uff0c\u8e6c\uff0c\u8e6c\uff0c\u5411\u524d\u51b2\u51fa\u6570\u6b65\uff0c\u4e00\u628a\u6293\u4f4f\u8fd9 \u5c71\u4ead\u7fe0\u7eff\u7684\u680f\u6746\uff0c\u53ea\u542c\u2014\u2014 \u3000\u3000\u201c\u6015\u201d\u5730\u4e00\u58f0\u2014\u2014 \u3000\u3000\u7fe0\u7af9\u7684\u680f\u6746\uff0c\u5e94\u624b\u800c\u88c2\uff0c\u7834\u88c2\u7684\u7af9\u7247\uff0c\u88ab\u4f0a\u98ce\u94c1\u638c\u634f\u5f97\u7c89\u788e\uff0c\u7136\u540e\u518d\u7f13\u7f13\u4ece\u4ed6\u638c\u7f1d\u4e2d\u6d41 \u51fa\u3002 \u3000\u3000\u4ed6\u7d27\u54ac\u7740\u7259\u5173\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53e3\u5149\u4e2d\u4f3c\u4e4e\u55b7\u51fa\u706b\u6765\uff0c\u72e0\u72e0\u76ef\u5728\u2014\u2014 \u3000\u3000\u8fd9\u5c71\u4ead\u4e2d\u7684\u56db\u5177\u8eab\u4e0a\u3002 \u3000\u3000\u521d\u6625\u7684\u6e05\u6668\uff0c\u6ee1\u5c71\u98de\u626c\u7740\u751f\u547d\u7684\u7f8e\u5999\uff0c\u5927\u5730\u5c31\u6b63\u548c\u7231\u7f8e\u7684\u5c11\u5973\u4e00\u6837\uff0c\u53ca\u65e9\u6536\u8d77\u4e86\u539a\u91cd\u7684 \u68c9\u8884\uff0c\u6362\u4e0a\u4e86\u65b0\u7eff\u7684\u8f7b\u886b\uff0c\u591a\u60c5\u7684\u5c11\u5e74\uff0c\u6b63\u671b\u7740\u8fd9\u65b0\u7eff\u7f57\u886b\u7684\u7a88\u7a95\u8eab\u5f71\uff0c\u4f4e\u548f\u7740\u6df1\u60c5\u7684\u53e5 \u5b50\uff0c\u5c31\u7b97\u662f\u5df2\u5728\u98ce\u70db\u6b8b\u5e74\u4e2d\u7684\u8001\u4eba\uff0c\u4e5f\u4f1a\u642c\u8d77\u4e00\u5f20\u7af9\u6905\u5b50\uff0c\u642c\u5230\u9662\u5b50\u7684\u524d\u9762\uff0c\u9616\u4e0a\u773c\uff0c\u9759\u9759 \u5730\u4eab\u53d7\u8fd9\u521d\u6625\u7684\u9633\u5149\u3002 \u3000\u3000\u7136\u800c\uff0c \u3000\u3000\u201c\u98de\u8679\u4e03\u5251\u201d\uff01 \u3000\u3000\u6765\u81ea\u9065\u8fdc\u7684\u5173\u5916\u7684\u201c\u98de\u8679\u4e03\u5251\u201d\uff01 \u3000\u3000\u4e3a\u4e86\u624b\u8db3\u7684\u6df1\u60c5\u6765\u81ea\u9065\u8fdc\u7684\u5173\u5916\u7684\u201c\u98de\u8679\u4e03\u5251\u201d\uff01 \u3000\u3000\u53d7\u5c3d\u5343\u8f9b\u4e07\u82e6\uff0c\u53ea\u4e3a\u4e86\u624b\u8db3\u6df1\u60c5\uff0c\u6765\u81ea\u9065\u8fdc\u7684\u5173\u5916\u7684\u201c\u98de\u8679\u4e03\u5251\u201d\uff01 \u3000\u3000\u6b64\u523b\u5374\u5728\u8fd9\u521d\u6625\u98de\u626c\u7684\u6c14\u606f\u4e2d\uff0c\u5012\u6bd9\u5728\u8fd9\u7fe0\u7eff\u7684\u5c71\u4ead\u91cc\uff0c\u5012\u6bd9\u5728\u4ed6\u4eec\u81ea\u5df1\u6d41\u51fa\u7684\u9c9c\u8840\u8840 \u8ff9\u4e0a\u2026 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\u2014\u2014 \u3000\u3000\u4f0a\u98ce\u6ee1\u8eab\u575a\u5f3a\u7684\u808c\u8089\uff0c\u90fd\u4e0d\u7981\u8d77\u4e86\u4e00\u9635\u9635\u75c9\u631b\u5730\u98a4\u6296\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u4ed6\u65b9\u81ea\u957f\u957f\u5410\u51fa\u80f8 \u4e2d\u4e00\u53e3\u6028\u6bd2\u4e4b\u6c14\uff0c\u6012\u53f1\u9053\uff1a \u3000\u3000\u201c\u597d\u72e0\uff01\u201d \u3000\u3000\u4ed6\u9880\u957f\u7684\u8eab\u5f62\uff0c\u4e5f\u968f\u7740\u8fd9\u77ed\u77ed\u7684\u53f1\u58f0\uff0c\u63a0\u5165\u8fd9\u5c0f\u5de7\u7684\u5c71\u4ead\u4e2d\u3002 \u3000\u3000\u4ed6\u987e\u4e0d\u5f97\u68c0\u89c6\u522b\u4eba\uff0c\u9996\u5148\u5c31\u63a0\u5230\u90a3\u8ddb\u8db3\u7684\u8001\u4eba\uff0c\u4e5f\u5c31\u662f\u201c\u98de\u8679\u4e03\u5251\u201d\u4e4b\u9996\u2014\u2014\u534e\u54c1\u5947\u8eab \u4fa7\uff0c\u6b64\u523b\u8fd9\u8c6a\u723d\u6b63\u76f4\u800c\u4e49\u6c14\u7684\u8001\u4eba\uff0c\u6b63\u65e0\u52a9\u5730\u4fef\u5367\u5728\u5730\u4e0a\uff0c\u4f0a\u98ce\u60f6\u6025\u5730\u4f38\u51fa\u98a4\u6296\u7740\u7684\u624b\u638c\uff0c \u5c06\u4ed6\u67af\u7626\u82cd\u8001\uff0c\u800c\u5df2\u51b0\u51c9\u4e86\u7684\u8eab\u8eaf\u7ffb\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff1a \u3000\u3000\u4ed6\u5c31\u770b\u5230\u4e86\u8fd9\u8001\u4eba\u4e00\u53cc\u7a7a\u6d1e\u800c\u5446\u6ede\u7684\u773c\u775b\uff0c\u4e00\u53cc\u4f0a\u98ce\u6709\u751f\u4ee5\u6765\u4ece\u672a\u89c1\u8fc7\uff0c\u6b64\u540e\u4e5f\u6c38\u4e0d\u4f1a \u5fd8\u8bb0\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6240\u6709\u7684\u751f\u673a\uff0c\u90fd\u5df2\u968f\u7740\u4ed6\u7684\u9c9c\u8840\u4ece\u521b\u53e3\u4e2d\u6d41\u51fa\u3002 \u3000\u3000\u4f46\u662f\uff01\u4ed6\u7684\u4e00\u53cc\u773c\u775b\u2014\u2014 \u3000\u3000\u591a\u5c11\u7684\u5931\u671b\uff0c\u591a\u5c11\u7684\u75db\u82e6\uff0c\u591a\u5c11\u7684\u6028\u6bd2\uff01\u6b64\u523b\u5374\u4ecd\u6df1\u6df1\u5730\u542b\u8574\u5728\u8fd9\u4e00\u53cc\u773c\u775b\u91cc\uff0c\u4f0a\u98ce\u7528 \u4e0d\u7740\u770b\u4ed6\u800c\u4e0a\u626d\u66f2\u7684\u808c\u8089\uff0c\u4e5f\u7528\u4e0d\u7740\u770b\u4ed6\u7d27\u63e1\u7740\u7684\u624b\u638c\uff0c\u5c31\u53ef\u4ee5\u4f53\u4f1a\u51fa\u8fd9\u8001\u4eba\u5fc3\u91cc\u7684\u5931\u671b\uff0c \u6028\u6bd2\uff0c\u4e0e\u75db\u82e6\u3002 \u3000\u3000\u201c\u4ed6\u662f\u6b7b\u4e0d\u7791\u76ee\u7684\u2026\u2026\u6b7b\u4e0d\u7791\u76ee\u7684\u2026\u2026\uff01\u201d \u3000\u3000\u4f0a\u98ce\u8f7b\u8f7b\u653e\u4e0b\u4ed6\u7684\u8eab\uff0c\u6c89\u91cd\u5730\u5728\u4ed6\u9762\u524d\u8dea\u4e86\u4e0b\u6765\uff1a \u3000\u3000\u7136\u540e\uff0c\u4ed6\u4fbf\u53c8\u770b\u5230\u4e86\u8001\u4eba\u80c1\u4e0b\u8981\u5bb3\u4e0a\u6367\u7740\u7684\u4e00\u67c4\u5c16\u5200\uff01 \u3000\u3000\u4e00\u67c4\u9ec4\u91d1\u7684\u5f2f\u5200\uff1a\u5200\u5203\u5374\u5df2\u53ef\u6015\u5730\u63d2\u5165\u80c1\u4e0b\uff0c\u53ea\u6709\u5200\u67c4\u4ecd\u5728\u9633\u5149\u4e0b\u95ea\u7740\u91d1\u5149\u5e26\u7740\u8840\u7ea2\u8272 \u7684\u91d1\u5149\uff01 \u3000\u3000\u62d4\u8d77\u4e86\u8fd9\u67c4\u5200\uff01\u98a4\u6296\u7740\uff1a\u4f0a\u98ce\u7684\u624b\u638c\u5267\u70c8\u5730\u98a4\u6296\u7740\uff0c\u9c9c\u8840\u7684\u8840\u73e0\uff0c\u6cbf\u7740\u5200\u810a\u4e0a\u7684\u8840\u7cdf\uff0c \u4e00\u6ef4\u4e00\u6ef4\u5730\u843d\u4e0b\u6765\u3002 \u3000\u3000\u5bf9\u6b7b\u8005\u7684\u601c\u60af\u4e0e\u5c0a\u656c\uff0c\u5bf9\u751f\u8005\u7684\u60ef\u6068\u4e0e\u6028\u6bd2\uff0c\u4f7f\u5f97\u4f0a\u98ce\u7684\u5fc3\u80f8\u4e2d\uff0c\u50cf\u94c5\u4e00\u6837\u7684\u6c89\u91cd\uff0c\u5239 \u90a3\u4e4b\u95f4\uff0c\u4ed6\u77e5\u9053\u4e86\uff0c\u8fd9\u51f6\u624b\u7684\u59d3\u540d\u2014\u2014 \u3000\u3000\u8427\u65e0\uff01 \u3000\u3000\u6628\u591c\u6740\u6b7b\u4e00\u4e2a\u4ea7\u5987\uff0c\u4e00\u4e2a\u53ef\u601c\u7684\u4ea7\u5987\uff0c\u4e00\u4e2a\u521a\u521a\u4e3a\u4ed6\u81ea\u5df1\u751f\u51fa\u4e00\u4e2a\u5b69\u5b50\u6765\u7684\u4ea7\u5987\u7684\u8427 \u65e0\uff01 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u624b\u8155\u4e00\u53cd\u2014\u2014 \u3000\u3000\u53ea\u89c1\u8840\u5149\u4e00\u82b1\uff0c\u4f0a\u98ce\u7684\u7259\u6839\u54ac\u5f97\u66f4\u7d27\u4e86\uff0c\u4ed6\u7adf\u65ad\u53bb\u4e86\u81ea\u5df1\u5de6\u638c\u7684\u4e00\u6839\u5c0f\u6307\uff0c\u4ed6\u98a4\u6296\u7740\u62fe \u8d77\u8fd9\u6839\u65ad\u6307\uff0c\u8f7b\u8f7b\u653e\u5728\u6b7b\u53bb\u7684\u8001\u4eba\u51b0\u51c9\u7684\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u4ed6\u7f13\u6162\uff0c\u4f4e\u6c89\uff0c\u4f46\u5374\u65e0\u6bd4\u575a\u5f3a\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u8bf4\u51fa\u516b\u4e2a\u5b57\uff1a \u3000\u3000\u201c\u4e0d\u6740\u6b64\u4eba\uff0c\u6709\u5982\u6b64\u6307\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u50cf\u5947\u8ff9\u4e00\u6837\u2026\u2026 \u3000\u3000\u8fd9\u8001\u4eba\u5f20\u5f00\u7684\u773c\uff0c\u7adf\u500f\u7136\u53c8\u5408\u8d77\u6765\u4e86\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5439\u5728\u4f0a\u98ce\u7684\u80cc\u810a\u4e0a\uff0c\u4f0a\u98ce\u53ea\u89c9\u6d51\u8eab \u4e00\u9707\uff0c\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u4e00\u9635\u96be\u8a00\u7684\u609a\u61d4\uff0c\u50cf\u68a6\u9b54\u4e00\u6837\u5e03\u6ee1\u4e86\u4ed6\u5168\u8eab\uff0c\u4ec7\u6068\uff01\u4ec7\u6068\uff01\u4ec7 \u6068\u2026\u2026\u3002 \u3000\u3000\u4ed6\u5e73\u751f\u4ece\u672a\u6709\u8fc7\u4efb\u4f55\u4e00\u523b\u6709\u6b64\u523b\u8fd9\u822c\u63a5\u8fd1\u4ec7\u6068\uff01\u5373\u4f7f\u4ed6\u7684\u7231\u59bb\u80cc\u53db\u4ed6\u7684\u65f6\u5019\uff1a\u56e0\u4e3a\uff0c\u4ed6 \u6df1\u523b\u7684\u611f\u89c9\u5230\u8fd9\u8001\u4eba\u7684\u4e00\u8eab\u90fd\u5145\u6ee1\u4e86\u4ec7\u6068\uff0c\u800c\u6b64\u523b\u8fd9\u8001\u4eba\u5374\u5df2\u5c06\u590d\u4ec7\u7684\u4f7f\u547d\u7559\u7ed9\u4e86\u4f0a\u98ce\u2014\u2014 \u867d\u7136\u6ca1\u6709\u4e00\u53e5\u8bdd\uff0c\u6ca1\u6709\u4e00\u4e2a\u5b57\uff0c\u4f46\u5374\u6bd4\u4e16\u4e0a\u6240\u6709\u8a00\u8bed\u7684\u603b\u548c\u8fd8\u8981\u660e\u663e\uff01 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u4f3c\u4e4e\u518d\u4e5f\u4e0d\u66fe\u52a8\u5f39\u4e00\u4e0b\uff0c\u4ed6\u5446\u5446\u5730\u671b\u7740\u9762\u524d\u8fd9\u8001\u4eba\u7684\u9762\u5bb9\uff0c\u4e16\u4e0a\u6240\u6709\u5176\u4ed6 \u7684\u60c5\u611f\u90fd\u5df2\u79bb\u4ed6\u8fdc\u53bb\uff0c\u53ea\u6709\u4ec7\u6068\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u4e00\u9635\u8f7b\u8f7b\u7684\u555c\u6ce3\u58f0\uff0c\u4ece\u4ed6\u8eab\u540e\u4f20\u6765\uff0c\u4e00\u53ea\u83b9\u767d\u5982\u7389\u7684\u7ea4\u624b\uff0c\u6e29\u67d4\u5730\u62ac\u8d77\u4e86\u4ed6\u7684\u5de6\u8155\u2026\u2026 \u3000\u3000\u5979\u8f7b\u76c8\u7a88\u7a95\u7684\u8eab\u8eaf\uff0c\u4e5f\u6e29\u67d4\u5730\u5728\u4f0a\u98ce\u8eab\u65c1\u8dea\u4e86\u4e0b\u6765\uff0c\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u6e05\u6f88\u7684\u6cea\u73e0\uff0c\u6d41\u8fc7\u5979 \u5ae3\u7ea2\uff0c\u6e29\u67d4\u7684\u5a07\u9765\uff0c\u6ef4\u5728\u4f0a\u98ce\u9c9c\u8840\u6dcb\u6f13\u7684\u624b\u638c\uff0c\u5979\u770b\u5230\u4f0a\u98ce\u7f13\u7f13\u56de\u8fc7\u5934\uff0c\u832b\u7136\u671b\u7740\u5979\uff0c\u50cf\u662f \u4e00\u4e2a\u964c\u751f\u4eba\u4e00\u6837\uff0c\u5979\u7684\u5fc3\uff0c\u4e5f\u7834\u788e\u5f97\u6709\u5982\u65b9\u624d\u90a3\u7fe0\u7af9\u7684\u680f\u6746\u4e00\u6837\u3002 \u3000\u3000\u5979\u4ece\u672a\u60f3\u5230\u4e16\u95f4\u7adf\u6709\u5982\u6b64\u6b8b\u9177\u7684\u4e8b\uff0c\u4e5f\u4ece\u672a\u60f3\u8d77\u4e16\u95f4\u6709\u5982\u6b64\u51c4\u60e8\u7684\u666f\u8c61\u3002 \u3000\u3000\u5979\u8bb0\u5f97\u7247\u523b\u4e4b\u95f4\uff0c\u5979\u6240\u542c\u5230\u7684\u8fd9\u8001\u4eba\u82cd\u8001\uff0c\u5fe7\u90c1\uff0c\u800c\u5145\u6ee1\u60c5\u611f\u7684\u58f0\u97f3\u5728\u8bf4\u7740\uff1a\u201c\u2026\u2026\u4e09 \u5f1f\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5979\u53c8\u8bb0\u5f97\uff0c\u8fd9\u8001\u4eba\u501a\u5728\u7fe0\u7eff\u7684\u680f\u6746\u8fb9\uff0c\u90a3\u79cd\u5fe7\u90c1\u800c\u75b2\u5026\u7684\u795e\u60c5\u3002 \u3000\u3000\u5979\u66f4\u8bb0\u5f97\uff0c\u8fd9\u8001\u4eba\u66fe\u6e29\u67d4\u5730\u5bf9\u5979\u8bf4\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u8981\u5230\u90a3\u91cc\u53bb\u5440\uff0c\u8fd9\u91cc\u5c71\u5f88\u6df1\uff0c\u4f60\u4f1a\u4e0d \u4f1a\u8ff7\u8def\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\uff0c\u6b64\u523b\u4fbf\u90fd\u50cf\u56fe\u753b\u4e00\u6837\u5730\uff0c\u53c8\u5f00\u59cb\u5728\u5979\u8111\u6d77\u4e2d\u6d6e\u8d77\uff0c\u4f46\u662f\uff1a\u8fd9\u8001\u4eba\u6b64\u523b\u5374\u5df2\u7ecf\u6b7b \u4e86\u3002 \u3000\u3000\u5979\u53c8\u60f3\u8d77\u81ea\u5df1\u66fe\u7ecf\u5bf9\u8fd9\u8001\u4eba\u8bf4\u7684\u8bdd\uff1a\u201c\u2026\u2026\u5929\u662f\u8fd9\u4e48\u84dd\uff0c\u6811\u662f\u8fd9\u4e48\u7eff\uff0c\u51ac\u5929\u597d\u4e0d\u5bb9\u6613\u8fc7 \u4e86\uff0c\u73b0\u5728\u662f\u8fd9\u4e48\u7f8e\u4e3d\u7684\u6625\u5929\uff0c\u4e16\u4e0a\u6709\u4ec0\u4e48\u4e8b\u662f\u4e0d\u80fd\u89e3\u51b3\u7684\uff0c\u8001\u4f2f\u4f2f\uff0c\u4f60\u53c8\u4f55\u5fc5\u53f9\u6c14\u5462\uff1f\u201d \u3000\u3000\u4e8e\u662f\uff0c\u5979\u5fcd\u4e0d\u4f4f\u653e\u58f0\u75db\u54ed\u4e86\uff0c\u75db\u54ed\u7740\u9053\uff1a \u3000\u3000\u201c\u8001\u4f2f\u4f2f\u2026\u2026\u6211\u2026\u2026\u6211\u9519\u4e86\u2026\u2026\u4e16\u4e0a\u662f\u6709\u4e9b\u4e8b\u4e0d\u80fd\u89e3\u51b3\u7684\u2026\u2026\u6b7b\u2026\u2026\u6b7b\u662f\u4e0d\u80fd\u89e3\u51b3 \u7684\u2026\u2026\u6b7b\u662f\u4e0d\u80fd\u89e3\u51b3\u7684\uff01\u201d \u3000\u3000\u51c4\u5b9b\u7684\u54ed\u58f0\uff0c\u518d\u52a0\u4e0a\u4f0a\u98ce\u65e0\u58f0\u7684\u54ed\u6ce3\uff0c\u7834\u788e\u4e86\u7684\u680f\u6746\u5f71\u5b50\uff0c\u6c89\u91cd\u5730\u6295\u843d\u5728\u9c9c\u8840\u4e2d\u7684\u9996 \u4e0a\uff0c\u51cc\u7433\u5782\u4e0b\u5934\uff0c\u7528\u555c\u6ce3\u7740\uff0c\u98a4\u6296\u7740\u7684\u6a31\u5507\uff0c\u542e\u5438\u7740\u4f0a\u98ce\u65ad\u6307\u4e0a\u7684\u9c9c\u8840\uff0c\u4f0a\u98ce\u542b\u6cea\u7684\u53cc\u7738\uff0c \u60b2\u54c0\u5730\u51dd\u89c6\u7740\u8fd9\u6e29\u67d4\u7684\u5c11\u5973\uff0c\u6625\u98ce\u4ecd\u5728\u5439\u52a8\uff0c\u6625\u9633\u4f9d\u65e7\u707f\u70c2\u3002 \u4f46\u662f\uff0c\u8fd9\u521d\u6625\u7684\u5c71\u91ce\uff0c\u5374\u5df2\u6709\u4e86\u665a\u79cb\u7684\u8427\u7d22\uff01 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u4e8c\u7ae0\u3000\u5982\u6b64\u5934\u9885>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u4e8c\u7ae0\u3000\u5982\u6b64\u5934\u9885 \u3000\u3000\u201c\u5636\u201d\u5730\u4e00\u58f0\uff0c \u3000\u3000\u51cc\u7433\u6495\u4e0b\u4e86\u4e00\u6761\u6de1\u7ea2\u7684\u8863\u895f\uff0c\u65e0\u8a00\u5730\u4e3a\u4f0a\u98ce\u5305\u91cc\u5de6\u638c\u7684\u4f24\u53e3\uff0c\u4f0a\u98ce\u662f\u9ebb\u6728\u7684\uff0c\u662f\u4ec7\u6068\u4f7f \u5f97\u4ed6\u9ebb\u6728\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u9ebb\u6728\u7684\u5fc3\u5f26\uff0c\u6b64\u523b\u5374\u53c8\u4e0d\u7981\u8d77\u4e86\u4e00\u9635\u8f7b\u5fae\u7684\u98a4\u6296\uff0c\u4ed6\u6323\u624e\u7740\uff0c\u60f3\u5c06\u81ea\u5df1\u7684\u5fc3\uff0c\u4ece \u8fd9\u79cd\u5fae\u5999\u7684\u98a4\u6296\u4e2d\u62bd\u51fa\u6765\uff0c\u4e5f\u60f3\u5c06\u81ea\u5df1\u7684\u624b\u638c\uff0c\u4ece\u5979\u90a3\u4e00\u53cc\u5c0f\u5de7\u800c\u83b9\u767d\u7684\u624b\u638c\u4e2d\u62bd\u51fa\u6765\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u671b\u7740\u5979\u54ed\u6ce3\u7740\u7684\u773c\u775b\uff0c\u4ed6\u671b\u7740\u5979\u5782\u843d\u7684\u79c0\u53d1\uff0c\u4ed6\u7a81\u7136\u53d1\u73b0\u8fd9\u6837\u505a\u4f1a\u662f\u4e00\u79cd\u591a\u4e48 \u6b8b\u9177\u7684\u4e8b\uff0c\u4e24\u4eba\u5e76\u80a9\u8dea\u5728\u8840\u6cca\u91cc\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5509\u2014\u2014\u7eb7\u4e71\u7684\u601d\u6f6e\uff0c\u7eb7\u4e71\u7684\u60c5\u4e1d\u2014\u2014 \u3000\u3000\u8fd9\u7eb7\u4e71\u7684\u601d\u6f6e\u4e0e\u7eb7\u4e71\u7684\u60c5\u601d\uff0c\u4f7f\u5f97\u4ed6\u4eec\u8c01\u90fd\u6ca1\u6709\u56de\u5934\u671b\u4e00\u773c\u3002 \u3000\u3000\u4ed6\u4eec\u5374\u4e0d\u77e5\u9053\uff0c\u6b64\u523b\u2014\u2014 \u3000\u3000\u5c31\u5728\u6b64\u523b\uff0c\u5c71\u4ead\u5916\u7684\u6797\u836b\u4e2d\uff0c\u7a81\u5730\u6f2b\u65e0\u58f0\u97f3\u5730\u8d70\u51fa\u4e00\u4e2a\u5c11\u5e74\u6765\uff0c\u7626\u5f31\u4f46\u5374\u575a\u5f3a\u7684\u8eab\u8eaf \u4e0a\uff0c\u7a7f\u7740\u4e00\u8eab\u6de1\u9ec4\u8272\u51e0\u4e4e\u50cf\u662f\u91d1\u6bdb\u7684\u8863\u886b\uff0c\u7ea4\u957f\u7684\u53cc\u624b\uff0c\u6367\u7740\u4e00\u4e2a\u4e00\u5c3a\u89c1\u65b9\u7684\u6a80\u6728\u5323\u5b50\u3002 \u3000\u3000\u4ed6\u8eab\u8eaf\u662f\u90a3\u4e48\u8f7b\u5de7\uff0c\u8f7b\u5de7\u5f97\u79fb\u52a8\u65f6\u7adf\u6ca1\u6709\u53d1\u751f\u4efb\u4f55\u4e00\u4e1d\u78ec\u97f3\uff0c\u4f46\u662f\u4ed6\u7684\u76ee\u5149\u5374\u662f\u6c89\u91cd \u7684\uff0c\u6c89\u91cd\u5730\u843d\u5728\u51cc\u7433\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u5446\u671b\u7740\u51cc\u7433\uff0c\u76ee\u5149\u4e2d\u50cf\u662f\u8981\u55b7\u51fa\u72c2\u70ed\u7684\u706b\u82b1\uff0c\u7136\u540e\uff0c\u4ed6\u7ec8\u4e8e\u8f7b\u54b3\u4e00\u58f0 \u3000\u3000\u4f0a\u98ce\uff0c\u51cc\u7433\u84e6\u5730\u4e00\u60ca\uff0c\u95ea\u7535\u822c\u56de\u8f6c\u8eab\u6765\uff0c\u9f50\u5730\u559d\u9053\uff1a \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u53cc\u7709\u4e00\u626c\uff0c\u4e00\u6b65\u63a0\u5230\u4ead\u4fa7\uff0c\u53cc\u624b\u9ad8\u4e3e\u7740\u90a3\u6a80\u6728\u5323\u5b50\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5b50\u5949\u5bb6\u5e08\u4e4b\u547d\uff0c\u524d\u6765\u62dc\u89c1\u201c\u94c1\u621f\u6e29\u4faf\u201d\u5415\u5927\u4fa0\uff01\u201d \u3000\u3000\u4f0a\u98ce\u5168\u8eab\u4e00\u9707\uff0c\u76ee\u4e2d\u5c04\u51fa\u7cbe\u5149\uff0c\u5389\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u8c01\u4eba\uff1f\u4ee4\u5e08\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u81ea\u5df1\u5c4f\u5f03\u5df2\u4e45\u7684\u540d\u5b57\uff0c\u6b64\u65f6\u6b64\u523b\u7adf\u7a81\u5730\u88ab\u4e00\u4e2a\u7d20\u4e0d\u76f8\u8bc6\u7684\u5c11\u5e74\u63ed\u7834\uff0c\u8fd9 \u7a81\u6765\u7684\u523a\u6fc0\uff0c\u50cf\u5c16\u9488\u4e00\u6837\u5728\u4ed6\u9ebb\u6728\u7684\u5fc3\u623f\u4e0a\u72e0\u72e0\u523a\u4e86\u4e00\u9488\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u4f46\u89c9\u5168\u8eab\u53c8\u5f00\u59cb\u6025 \u907d\u5730\u6d41\u52a8\u8d77\u65b9\u624d\u4f3c\u4e4e\u5df2\u5168\u90e8\u51dd\u7ed3\u4e86\u7684\u9c9c\u8840\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u50cf\u95ea\u7535\u4e00\u6837\u671b\u5728\u8fd9\u5c11\u5e74\u8eab\u4e0a\uff0c\u4f46\u662f\u8fd9\u5c11\u5e74\u5374\u4ecd\u7136\u50b2\u7136\u5353\u7acb\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5b50\u953a\u9759\uff0c\u5949\u5bb6\u5e08\u4e4b\u547d\uff0c\u5c06\u8fd9\u62dc\u76d2\u9001\u4ea4\u5415\u5927\u4fa0\uff0c\u9601\u4e0b\u5982\u679c\u662f\u5415\u5927\u4fa0\u7684\u8bdd\uff0c\u5c06\u8fd9\u62dc\u76d2\u6536 \u4e0b\uff0c\u4fbf\u53ef\u77e5\u9053\uff0c\u9601\u4e0b\u5982\u4e0d\u662f\u5415\u5927\u4fa0\uff0c\u5f1f\u5b50\u4fbf\u8981\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53cc\u624b\u7b14\u76f4\u5730\u4f38\u4e86\u51fa\u6765\uff0c\u4e1d\u7eb9\u4e0d\u52a8\u5730\u6367\u7740\u90a3\u96d5\u523b\u5f97\u6781\u4e3a\u7cbe\u81f4\u7684\u6a80\u6728\u5323\u5b50\uff0c\u8bed\u58f0\u6e05\u6717\uff0c\u6001 \u5ea6\u6c89\u9759\uff0c\u4f0a\u98ce\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5e74\u8f7b\u7684\u5c11\u5e74\u6709\u5982\u6b64\u6c89\u9759\u7684\u795e\u6001\uff0c\u751f\u50cf\u662f\u4e00\u5207\u53d8\u5316\u90fd\u4e0d\u80fd\u4f7f\u4ed6\u60ca\u614c \u4e00\u6837\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u76ee\u5149\u63a0\u8fc7\u51cc\u7433\u65f6\uff0c\u6c89\u9759\u7684\u76ee\u5149\uff0c\u4fbf\u7acb\u523b\u55b7\u51fa\u4e86\u72c2\u70ed\u7684\u706b\u7130\uff0c\u8fd9\u79cd\u76ee\u5149\u4e0e\u4ed6\u9762\u4e0a \u795e\u6001\u4e4b\u4e0d\u76f8\u79f0\uff0c\u5c31\u50cf\u662f\u4e25\u51ac\u7684\u96ea\u5730\u4e0a\u7adf\u7a81\u7136\u6709\u8774\u8776\u98de\u7fd4\u4e00\u6837\uff0c\u4f0a\u98ce\u5251\u7709\u4e00\u8f69\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u4f38 \u624b\u63a5\u8fc7\u4e86\u8fd9\u7cbe\u81f4\u7684\u6a80\u6728\u62dc\u76d2\u3002 \u3000\u3000\u51cc\u7433\u7741\u5927\u773c\u671b\u7740\u4ed6\u4eec\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u201c\u953a\u9759\u201d\uff0c\u5c06\u624b\u4e2d\u7684\u76d2\u5b50\u4e00\u4ea4\u5230\u5415\u5357\u4eba\u624b\u4e0a\uff0c\u4fbf\u8f6c\u8eab \u6b32\u53bb\uff0c\u5979\u5fc3\u5ff5\u52a8\u5904\uff0c\u7a81\u5730\u5a07\u559d\u9053\uff1a \u3000\u3000\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u5c11\u5e74\u953a\u9759\u6115\u4e86\u4e00\u6115\uff0c\u4fbf\u505c\u4f4f\u811a\u6b65\uff0c\u4ed6\u9762\u4e0a\u867d\u4ecd\u4e00\u65e0\u8868\u60c5\uff0c\u4f46\u4f60\u82e5\u4ed4\u7ec6\u4e00\u770b\uff0c\u4fbf\u77e5\u9053\u4ed6\u9762 \u4e0a\u7684\u808c\u8089\u50cf\u662f\u5df2\u5168\u90e8\u50f5\u786c\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5b50\u5dee\u4f7f\u5df2\u4e86\uff0c\u4e0d\u77e5\u5415\u5927\u4fa0\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\u3002\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u51dd\u6ce8\u7740\u6a80\u6728\u5323\u4e0a\u7684\u82b1\u7eb9\uff0c\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u9ebb\u70e6\u4f60\u5c06\u8fd9\u5323\u5b50\u66ff\u6211\u5f00\u5f00\u3002\u201d\u6b64\u523b\u4ed6\u5fc3\u4e2d\u5df2\u81ea\u7591\u4e91\u5927\u8d77\uff0c\u751f\u6015\u8fd9\u5323\u5b50\u4e2d\u88c5\u6709\u4ec0\u4e48\u6b79\u6bd2 \u7684\u52fe\u5f53\uff0c\u662f\u4ee5\u624d\u5982\u6b64\u8bf4\u6cd5\u3002 \u3000\u3000\u5c11\u5e74\u201c\u953a\u9759\u201d\u51b7\u51b7\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5bb6\u5e08\u53ea\u53eb\u5f1f\u5b50\u5c06\u6b64\u5323\u9001\u7ed9\u5415\u5927\u4fa0\uff0c\u5374\u672a\u66fe\u53eb\u5f1f\u5b50\u5f00\u654c\uff0c\u800c\u5415\u5927\u4fa0\u5982\u679c\u4e0d\u613f\u5f00\u542f\u6b64\u5323\u7684 \u8bdd\uff0c\u4e5f\u4e0e\u5f1f\u5b50\u6beb\u65e0\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u867d\u7f13\u6162\uff0c\u8a00\u8bcd\u5374\u7280\u5229\u5df2\u6781\uff0c\u53ea\u542c\u5f97\u4f0a\u98ce\u53cc\u7709\u4e00\u8f69\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u51cc\u7433\u5374\u5df2\u5a07\u53f1\u7740 \u9053\uff1a \u3000\u3000\u201c\u53eb\u4f60\u5f00\u5f00\uff0c\u4f60\u5c31\u5f00\u5f00\uff0c\u565c\u82cf\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u201c\u953a\u9759\u201d\u76ee\u5149\u4e00\u6c89\uff0c\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u50cf\u662f\u7a81\u7136\u8981\u4f5c\u4ec0\u4e48\u91cd\u5927\u7684\u51b3\u5b9a\u4e00\u6837\uff0c\u9ed8\u7136\u826f\u4e45\uff0c\u7a81 \u5730\u4e00\u8a00\u4e0d\u53d1\u5730\u4ece\u4f0a\u98ce\u624b\u4e2d\u63a5\u8fc7\u6a80\u6728\u5323\u5b50\u3002 \u3000\u3000\u4f0a\u98ce\u671b\u7740\u8fd9\u5c11\u5e74\u6c89\u9759\u7684\u795e\u60c5\uff0c\u660e\u4eae\u7684\u53cc\u76ee\uff0c\u548c\u4fca\u79c0\u7684\u9762\u5bb9\uff0c\u518d\u56de\u9996\u4e00\u671b\u51cc\u7433\uff0c\u53ea\u89c1\u5979\u660e \u4eae\u7684\u79cb\u6ce2\u4e2d\uff0c\u4f3c\u4e4e\u95ea\u8fc7\u4e00\u4e1d\u559c\u8272\uff0c\u50cf\u662f\u5728\u6697\u4e2d\u8d5e\u8d4f\u8fd9\u5c11\u5e74\u542c\u8bdd\u4e00\u6837\uff0c\u5fc3\u4e2d\u7a81\u5730\u4e00\u6c89\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eca\u5e74\u591a\u5927\u4e86\uff1f\u201d \u3000\u3000\u5c11\u5e74\u201c\u953a\u9759\u201d\u4f3c\u4e4e\u4e5f\u88ab\u4ed6\u8fd9\u7a81\u7136\u7684\u95ee\u8bdd\u6115\u4e86\u4e00\u6115\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u4ecd\u7136\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5b50\u4eca\u5e74\u65b9\u6ee1\u5341\u4e03\u3002\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u8bed\u6c14\u7a81\u5730\u53d8\u5f97\u51b0\u51b7\uff1a \u3000\u3000\u201c\u8fd9\u95ee\u9898\u539f\u4e0e\u5415\u5927\u4fa0\u65e0\u5173\uff0c\u5f1f\u5b50\u4e5f\u5e76\u975e\u4e00\u5b9a\u8981\u7b54\u8986\uff0c\u4f46\u662f\u5415\u5927\u4fa0\u8fd9\u662f\u7b2c\u4e00\u6b21\u76f8\u8be2\u4e8e\u5f1f \u5b50\uff0c\u4e0b\u6b21\u4e48\u2026\u2026\u201d \u3000\u3000\u4ed6\u500f\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u53f3\u638c\u4e00\u626c\uff0c\u5c06\u5323\u76d2\u6380\u8d77\uff0c\u5415\u5357\u4eba\u529b\u81ea\u6697\u53f9\uff01 \u3000\u3000\u201c\u8fd9\u5c11\u5e74\u4e0d\u4f46\u795e\u6001\u6c89\u9ed8\uff0c\u8a00\u8bed\u950b\u5229\uff0c\u800c\u4e14\u5f85\u4eba\u63a5\u7269\uff0c\u6781\u4e3a\u5f97\u4f53\uff0c\u867d\u7136\u7a0d\u5acc\u72c2\u50b2\uff0c\u4f46\u50b2\u9aa8 \u94ee\u94ee\uff0c\u4e0d\u5351\u4e0d\u4ea2\uff0c\u6b63\u662f\u5c11\u5e74\u4eba\u672c\u8272\uff0c\u5509\uff0c\u4e0d\u77e5\u9053\u662f\u8c01\u80fd\u8c03\u6559\u51fa\u8fd9\u79cd\u5f1f\u5b50\u2026\u2026\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u7a81\u5730\u4e00\u52a8\uff0c\u5374\u542c\u51cc\u7433\u5df2\u81ea\u5a07\u5524\u4e00\u58f0\uff0c\u63a9\u9762\u56de\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u5934\u4e00\u51db\uff0c\u5b9a\u775b\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e2a\u5c11\u5e74\u6728\u7136\u6367\u7740\u62dc\u76d2\uff0c\u7b14\u76f4\u5730\u7ad9\u5728\u4ead\u524d\u7684\u77f3\u7ea7\u4e0a\uff0c\u8fde \u76ee\u5149\u90fd\u6ca1\u6709\u8f6c\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u800c\u8fd9\u96d5\u5236\u5f97\u6781\u4e3a\u7cbe\u81f4\u7684\u6a80\u6728\u5323\u4e2d\uff0c\u4e00\u5f20\u6de1\u9ec4\u7684\u7eb8\u67ec\u4e4b\u4e0b\uff0c\u7adf\u8d6b\u7136\u653e\u7740\u4e00\u9897\u53d1\u9afb\u84ec\u677e\uff0c\u5374 \u65e0\u4e1d\u6beb\u8840\u8ff9\u7684\u4eba\u5934\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4f0a\u98ce\u53ea\u89c9\u5168\u8eab\u53c8\u81ea\u4e00\u9707\uff0c\u63a2\u624b\u593a\u8fc7\u8fd9\u7d2b\u6a80\u62dc\u5323\u6765\uff0c\u63ed\u5f00\u7eb8\u67ec\uff0c\u51dd\u76ee\u4e00\u671b\uff0c\u53ea \u89c1\u8fd9\u9897\u4eba\u5934\u9762\u5bb9\u8870\u8001\u82cd\u767d\uff0c\u4e0d\u4f46\u6ca1\u6709\u4e00\u4e1d\u8840\u8ff9\uff0c\u66f4\u65e0\u4e00\u4e1d\u8840\u8272\uff0c\u751f\u50cf\u662f\u8721\u5236\u7684\u4eba\u5934\u4e00\u6837\u3002 \u3000\u3000\u4f46\u8fd9\u9762\u5bb9\u4e00\u5165\u4f0a\u98ce\u4e4b\u76ee\uff0c\u4ed6\u5374\u4e0d\u7981\u60ca\u559d\u4e00\u58f0\uff0c\u98a4\u58f0\u9053\uff1a \u3000\u3000\u201c\u6731\u7802\u638c\u5c24\u5927\u541b\uff01\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u7d2b\u6a80\u5323\u4e2d\u7684\u5934\u9885\uff0c\u7adf\u662f\u5929\u4e89\u6559\u4e24\u6cb3\u603b\u8235\u4e2d\u7684\u91d1\u886b\u9999\u4e3b\uff0c\u4e5f\u5c31\u662f\u4ed6\u4ee5\u8bc8\u6b7b \u611a\u4e4b\u7684\uff0c\u6731\u7802\u638c\u5c24\u5927\u541b\uff01 \u3000\u3000\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u76ee\u5149\u62ac\u8d77\uff0c\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u7ad9\u4f4f\uff01\u662f\u8c01\u53eb\u4f60\u6765\u7684\u3002\u201d \u3000\u3000\u953a\u9759\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u65b9\u624d\u5f1f\u5b50\u65e2\u7136\u672a\u8d70\uff0c\u6b64\u523b\u4fbf\u4e5f\u4e0d\u4f1a\u8d70\uff0c\u5415\u5927\u4fa0\u53ea\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u51b7\u51b7\u53c8 \u9053\uff1a \u3000\u3000\u201c\u81f3\u4e8e\u662f\u8c01\u547d\u5f1f\u5b50\u6765\u7684\uff0c\u5f1f\u5b50\u539f\u4ee5\u4e3a\u5415\u5927\u4fa0\u65e9\u5df2\u731c\u5230\u4e86\uff0c\u4e0d\u8fc7\u5415\u5927\u4fa0\u65e2\u672a\u731c\u5230\uff0c\u53ea\u8981\u4e00 \u770b\u5bb6\u5e08\u968f\u5323\u5949\u4e0a\u7684\u62dc\u7b3a\uff0c\u4e5f\u53ef\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u7b14\u76f4\u5730\u671b\u5728\u524d\u9762\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u50cf\u662f\u751f\u6015\u81ea\u5df1\u53c8\u4f1a\u671b\u5230\u90a3\u7a7f\u7740\u4e00\u8eab\u8f7b\u7ea2\u8863\u886b\u7684\u5c11\u5973 \u8eab\u4e0a\u4f3c\u7684\u3002 \u3000\u3000\u4f0a\u98ce\u95fb\u8a00\u5fc3\u4e2d\u5374\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u51db\uff0c\u4e00\u624b\u5c55\u5f00\u7eb8\u7b3a\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a \u3000\u3000\u201c\u94c1\u621f\u6e29\u5019\u5415\u5357\u4eba\u9601\u4e0b\u52cb\u542f\uff1a \u3000\u3000\u9601\u4e0b\u5a01\u9707\u5f0f\u6797\uff0c\u540d\u503e\u5929\u4e0b\uff0c\u65e0\u65e0\u7f18\u8bc6\u8346\uff0c\u5e38\u4ee5\u4e3a\u6068\uff0c\u5e74\u524d\u5ffd\u4f20\u9601\u4e0b\u6b7b\u8baf\uff0c\u65e0\u5b9e\u60ca\u60bc\u83ab \u540d\uff0c\u81f3\u4eca\u65b9\u77e5\u6b64\u8baf\u5b9e\u4e43\u8bef\u4f20\u3002 \u3000\u3000\u9601\u4e0b\u54af\u65bd\u5c0f\u8ba1\uff0c\u4fbf\u5df2\u611a\u5c3d\u5929\u4e0b\u4eba\u8033\u76ee\uff0c\u56e0\u662f\u65e0\u66f4\u5bf9\u9601\u4e0b\u4e4b\u5fc3\u667a\u666f\u4ef0\u77e3\uff0c\u56e0\u65e0\u4e0e\u9601\u4e0b\uff0c\u5b9e \u4e43\u4e00\u65f6\u4e4b\u745c\u4eae\uff0c\u60fa\u60fa\u76f8\u60dc\u4e4b\u5fc3\uff0c\u5b9e\u6240\u96be\u5141\uff0c\u662f\u4ee5\u65e0\u5148\u4e3a\u9601\u4e0b\u62a5\u5374\u4fdd\u5b9a\u5e9c\u57ce\u5916\u4e00\u638c\u4e4b\u4ec7\uff0c\u5e76\u5c06 \u6b64\u611a\u4eba\u4e4b\u5934\u9885\uff0c\u9001\u7ed9\u9601\u4e0b\uff0c\u590d\u4e3a\u9601\u4e0b\u62a5\u7ec8\u5357\u5c71\u4e0b\u4e00\u5251\u4e4b\u6068\uff0c\u5c06\u6765\u81ea\u957f\u767d\u4e4b\u65e0\u77e5\u8001\u513f\uff0c\u6bd9\u4e8e\u9601 \u4e0b\u4e4b\u524d\uff0c\u66f4\u4e3a\u9601\u4e0b\u9664\u5374\u6deb\u5954\u4e4b\u59bb\u2014\u2014 \u3000\u3000\u770b\u5230\u8fd9\u91cc\uff0c\u4f0a\u98ce\u4e0d\u7981\u5927\u559d\u4e00\u58f0\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u51e0\u4e4e\u55b7\u51fa\u706b\u6765\uff0c \u3000\u3000\u53ea\u89c1\u4e0b\u9762\u5199\u9053\uff1a \u3000\u3000\u201c\u7531\u6b64\u53ef\u89c1\uff0c\u65e0\u5bf9\u9601\u4e0b\uff0c\u5b9e\u5df2\u4ec1\u81f3\u4e49\u5c3d\uff0c\u600e\u5948\u9601\u4e0b\u5374\u504f\u504f\u4e0e\u65e0\u4e3a\u656c\uff0c\u5c82\u975e\u4ee4\u65e0\u4f24\u65b0\u3002\u201d \u3000\u3000\u4f0a\u98ce\u77a0\u76ee\u5927\u9a82\u9053\uff1a \u3000\u3000\u201c\u65e0\u803b\uff0c\u5351\u8d31\uff0c\u65e0\u803b\u4e4b\u6781\uff0c\u5351\u8d31\u4e4b\u6781\uff01\u201d \u3000\u3000\u5374\u89c1\u4e0b\u9762\u5199\u9053\uff1a \u3000\u3000\u201c\u4eca\u65e0\u6709\u4e8b\u8d74\u6c5f\u5357\uff0c\u53c8\u590d\u4e0d\u514b\u4e0e\u9601\u4e0b\u76f8\u89c1\uff0c\u65e0\u66f4\u4ee5\u4e3a\u61be\uff01\u201d \u3000\u3000\u4f0a\u98ce\u51b7\u7b11\uff1a \u3000\u3000\u201c\u6211\u66f4\u9057\u61be\u3002\u201d\u4ed6\u76f4\u6068\u4e0d\u5f97\u98df\u6b64\u4eba\u4e4b\u8089\uff0c\u5bdd\u6b64\u4eba\u4e4b\u76ae\u3002 \u3000\u3000\u4e0b\u9762\u5199\u9053\uff1a \u3000\u3000\u201c\u4eca\u5c81\u4e94\u6708\u7aef\u9633\uff0c\u65e0\u656c\u6cbb\u9ec4\u9e21\u827e\u9152\u4e8e\u5357\u6e56\u70df\u96e8\u697c\u4e0a\uff0c\u4f46\u671b\u9601\u4e0b\u80fd\u6765\u4e00\u9189\uff0c\u65e0\u4e0e\u9601\u4e0b\u7f18\u60ad \u4e00\u9762\uff0c\u81f3\u65f6\u60f3\u5fc5\u80fd\u5c3d\u6b22\u4e5f\uff0c\u4e13\u6b64\u5949\u8fbe\uff0c\u5e76\u95ee\u91d1\u5b89\u3002\u201d \u3000\u3000\u4e0b\u9762\u5177\u540d\uff0c\u81ea\u7136\u662f\uff1a \u3000\u3000\u201c\u5929\u4e89\u6559\u5357\u4e03\u5317\u516d\u5341\u4e09\u8235\u603b\u8235\u4e3b\u8427\u65e0\u62dc\u4e0a\u3002\u201d \u3000\u3000\u4f0a\u98ce\u7684\u624b\u638c\uff0c\u5df2\u56e0\u6fc0\u6012\u800c\u98a4\u6296\u8d77\u6765\uff0c\u4ed6\u76f4\u6068\u4e0d\u5f97\u80fd\u5c06\u8fd9\u4e00\u5f20\u51b7\u8840\u7684\u4e66\u67ec\uff0c\u4e00\u628a\u6495\u6210\u4e24 \u534a\u3002 \u3000\u3000\u4f46\u662f\uff0c\u540e\u9762\u5374\u4ecd\u6709\u5b57\u8ff9\uff1a \u3000\u3000\u201c\u53c8\u53ca\uff1a \u3000\u3000\u5c1a\u6709\u4e24\u4e8b\uff0c\u65e0\u5fc5\u987b\u5bf9\u9601\u4e0b\u4e00\u8c22\uff0c\u4e00\u4e3a\u9601\u4e0b\u7adf\u7136\u6177\u6168\u6bc1\u53bb\u9762\u4e0a\u4e4b\u9762\u5176\uff0c\u4f7f\u65e0\u4ece\u6b64\u5fc3\u5b89\uff0c\u4e8c \u4e3a\u9601\u4e0b\u4e4b\u5b9d\u9a6c\u786e\u4e43\u795e\u9a79\uff0c\u4e88\u65e0\u65b9\u4fbf\u4e0d\u5c11\uff0c\u800c\u9601\u4e0b\u7adf\u4ee5\u6b64\u9a6c\u76f8\u8d60\uff0c\u65e0\u602a\u9601\u4e0b\u6177\u6168\u4e4b\u540d\u4f20\u904d\u5929\u4e0b \u4e5f\u3002\u201d \u3000\u3000\u201c\u518d\u53ca\uff1a \u3000\u3000\u4eca\u5f0f\u6797\u4e2d\u4eba\u5747\u5df2\u77e5\u9601\u4e0b\u672a\u6b7b\uff0c\u9601\u4e0b\u5f03\u7956\u5b97\u4e4b\u540d\u4e0d\u7528\uff0c\u5c82\u975e\u53ef\u60dc\uff1f\u4e00\u7b11\u3002\u201d \u3000\u3000\u51cc\u7433\u6b64\u523b\u5df2\u6084\u6084\u8f6c\u8fc7\u5934\u6765\uff0c\u5979\u867d\u7136\u6ca1\u6709\u770b\u4f0a\u98ce\u624b\u4e0a\u7684\u4fe1\u7b3a\uff0c\u5374\u770b\u5230\u4f0a\u98ce\u9762\u4e0a\u6124\u6012\u7684\u795e \u60c5\uff0c\u5979\u77e5\u9053\u8fd9\u5c01\u4fe1\u91cc\uff0c\u5fc5\u5b9a\u6709\u7740\u8bb8\u591a\u4e0d\u582a\u4eba\u76ee\u7684\u8bdd\u3002 \u3000\u3000\u4e8e\u662f\u5979\u9759\u9759\u6b62\u4f4f\u555c\u6ce3\uff0c\u6084\u6084\u4f38\u51fa\u624b\u638c\uff0c\u634f\u4f4f\u4ed6\u7684\u81c2\u8180\u3002 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u4f0a\u98ce\u7a81\u5730\u624b\u8155\u4e00\u7ffb\uff0c\u624b\u4e2d\u7684\u7d2b\u6a80\u5323\u5b50\uff0c\u4fbf\u8131\u624b\u98de\u51fa\uff0c\u624b\u4e2d\u7684\u6de1\u9ec4\u5b57\u67ec\uff0c\u4e5f\u6495\u4e3a\u4e24\u534a\uff0c\u4f46 \u9759\u7acb\u5728\u4ed6\u9762\u524d\u7684\u5c11\u5e74\u953a\u9759\uff0c\u5374\u4ecd\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u53ea\u662f\u671b\u5728\u51cc\u7433\u7684\u7389\u9762\u4e0a\u800c\u773c\u775b\u5374\u53c8\u50cf \u662f\u8981\u55b7\u51fa\u706b\u6765\u2014\u2014 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u4e09\u7ae0\u3000\u6b63\u540d\u632f\u540d>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u4e09\u7ae0\u3000\u6b63\u540d\u632f\u540d \u3000\u3000\u7d2b\u6a80\u62dc\u5323\uff0c\u8fdc\u8fdc\u98de\u53bb\uff0c\u5323\u4e2d\u7684\u4eba\u5934\uff0c\u4e5f\u5df2\u5c06\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u4f0a\u98ce\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u95ea\u7535\u822c\u63a0\u8d77\uff0c\u6709\u5982\u79bb\u5f26\u4e4b\u7bad\u822c\uff0c\u659c\u98de\u4e00\u4e08\uff0c\u624b\u638c\u75be\u6284\uff0c\u7adf\u5c06\u8fd9\u5df2\u5c06\u843d \u5730\u7684\u6728\u5323\u4eba\u5934\uff0c\u6284\u5728\u624b\u4e2d\uff0c\u8eab\u5f62\u4e00\u6298\uff0c\u811a\u5c16\u8f7b\u70b9\uff0c\u53c8\u98d8\u98d8\u843d\u5728\u539f\u5904\uff0c\u8f7b\u8f7b\u5c06\u62dc\u76d2\u4eba\u5934\u653e\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u6fc0\u6012\u4e4b\u4e0b\uff0c\u867d\u5df2\u5c06\u4eba\u5934\u629b\u51fa\uff0c\u4f46\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5374\u53c8\u89c9\u5f97\u4e0d\u8be5\u5bf9\u4e00\u4e2a\u6b7b\u53bb\u7684\u4eba\u5982\u6b64\u6b8b \u5fcd\uff0c\u51cc\u7433\u76ee\u5149\u52a8\u5904\uff0c\u8f7b\u8f7b\u4e00\u53f9\uff0c\u5c11\u5e74\u953a\u9759\u65e0\u8868\u60c5\u7684\u9762\u76ee\u4e0a\uff0c\u4f3c\u4e4e\u4e5f\u95ea\u8fc7\u4e00\u4e1d\u5bf9\u4f0a\u98ce\u6b66\u529f\u60ca\u5947 \u7684\u795e\u8272\u3002 \u3000\u3000\u53ea\u542c\u4f0a\u98ce\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u4f60\u5c31\u662f\u8427\u65e0\u90a3\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u953a\u9759\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u6b63\u662f\uff0c\u9601\u4e0b\u5982\u65e0\u5429\u5490\uff0c\u5f1f\u5b50\u5c31\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u5251\u7709\u8f69\u5904\uff0c\u7a81\u5730\u4ef0\u5929\u957f\u7b11\u4e86\u8d77\u6765\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u5982\u662f\u8fd9\u6076\u5f92\u7684\u5f1f\u5b50\uff0c\u800c\u7adf\u6562\u4e0d\u8d70\uff0c\u80c6\u5b50\u5012\u4e5f\u5927\u5f97\u5f88\u3002\u201d\u7b11\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u9762\u4e0a\u6e10\u6e10\u7b3c \u4e0a\u4e00\u5c42\u715e\u6c14\uff0c\u5389\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u96be\u9053\u4e0d\u6015\u6211\u5c06\u4f60\u6740\u6b7b\u3002\u201d \u3000\u3000\u5c11\u5e74\u953a\u9759\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e24\u56fd\u4ea4\u5175\uff0c\u4e0d\u65a9\u6765\u4f7f\uff0c\u5f1f\u5b50\u77e5\u9053\u5415\u5927\u4fa0\u7edd\u65e0\u52a0\u5bb3\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f55\u51b5\uff0c\u5373\u4f7f\u5415\u5927\u4fa0\u6709\u52a0\u5bb3\u4e4b\u5fc3\uff0c\u5f1f\u5b50\u5374\u4e5f\u672a\u89c1\u754f\u60e7\u54e9\uff01\u201d \u3000\u3000\u4f0a\u98ce\u9762\u8272\u4e00\u5bd2\uff0c\u5389\u53f1\u9053\uff1a \u3000\u3000\u201c\u6709\u5176\u5e08\u5fc5\u6709\u5176\u5f92\uff0c\u7559\u4f60\u5728\u4e16\uff0c\u4e5f\u662f\u5bb3\u4eba\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u6740\u4f60\uff1f\u201d \u3000\u3000\u5389\u53f1\u58f0\u4e2d\uff0c\u624b\u638c\u4e00\u626c\uff0c\u5237\u5730\u4e00\u638c\uff0c\u5411\u8fd9\u5c11\u5e74\u953a\u9759\u5288\u9762\u51fb\u53bb\uff01 \u3000\u3000\u51cc\u7433\u6697\u4e2d\u4e00\u60ca\uff0c\u53ea\u89c1\u8fd9\u4e00\u638c\u773c\u89c1\u8981\u5288\u4e0a\u8fd9\u5c11\u5e74\u7684\u9f3b\u6881\uff0c\u8fd9\u5c11\u5e74\u8f69\u7709\u77a0\u9f3b\uff0c\u5374\u4ecd\u4e0d\u907f\u4e0d \u95ea\uff0c\u9762\u4e0a\u4e5f\u4ecd\u662f\u6728\u65e0\u8868\u60c5\uff0c\u5c31\u751f\u50cf\u662f\u8fd9\u4e00\u638c\u5e76\u975e\u662f\u8981\u6253\u5230\u4ed6\u8eab\u4e0a\u4f3c\u7684\u3002 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u4f0a\u98ce\u638c\u52bf\u7adf\u4e5f\u7a81\u5730\u4e00\u987f\uff0c\u786c\u751f\u751f\u505c\u7559\u5728\u8fd9\u5c11\u5e74\u9762\u524d\u5206\u6beb\u4e4b\u95f4\u3002 \u3000\u3000\u51cc\u7433\u6697\u4e2d\u53c8\u81ea\u53f9\u4e86\u53e3\u957f\u6c14\uff0c\u5374\u542c\u4f0a\u98ce\u51b7\u51b7\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u600e\u5730\u4e0d\u52a8\u624b\u76f8\u62d2\uff1f\u201d \u3000\u3000\u953a\u9759\u53cc\u7709\u4e00\u626c\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u5415\u5927\u4fa0\u65e0\u8bba\u4e0e\u5bb6\u5e08\u662f\u53cb\u662f\u654c\uff0c\u4f46\u6b64\u523b\u5bb6\u5e08\u5374\u4ecd\u4e0e\u5415\u5927\u4fa0\u5e73\u8f88\u8bba\u4ea4\uff0c\u5f1f\u5b50\u4e0d\u6562\u4ee5\u4e0b\u72af \u4e0a\uff01\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u4e00\u8f6c\uff0c\u9762\u8272\u7adf\u4e5f\u7acb\u523b\u7f13\u548c\u4e0b\u6765\uff0c\u82e6\u53f9\u4e00\u58f0\uff0c\u6536\u56de\u624b\u638c\uff0c\u548c\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u524d\u9014\u5927\u6709\u53ef\u4e3a\uff0c\u600e\u5730\u6ca1\u6709\u5584\u6076\u4e4b\u5206\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u90a3\u8427\u65e0\u7684\u884c\u4e8b\u662f\u5584\u6291 \u6216\u662f\u6076\u5417\uff1f\u201d \u3000\u3000\u953a\u9759\u76ee\u5149\u4e00\u5782\uff0c\u5446\u5446\u5730\u671b\u7740\u77f3\u9636\uff0c\u9ed8\u7136\u534a\u664c\uff0c\u65b9\u81ea\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u6614\u5e74\u8c6b\u8ba9\u7eb9\u8eab\u541e\u70ad\uff0c\u53c8\u4f55\u5c1d\u6709\u5584\u6076\u4e4b\u5206\uff0c\u53ea\u4e0d\u8fc7\u662f\u62a5\u77e5\u5df1\u4e4b\u6069\u800c\u5df2\uff0c\u5f1f\u5b50\u7236\u6bcd\u53cc\u4ea1\uff0c \u4e00\u751f\u5b64\u82e6\uff0c\u5e78\u8499\u5bb6\u5e08\u6536\u7559\uff0c\u6b64\u6069\u6b64\u5fb7\uff0c\u6709\u5982\u5929\u9ad8\u6d77\u6df1\uff0c\u7eb5\u7136\u662f\u7eb9\u8eab\u541e\u70ad\uff0c\u4e5f\u96be\u62a5\u5176\u6069\u5fb7\u4e07 \u4e00\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u6602\u7136\u53c8\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5b50\u5bf9\u5415\u5927\u4fa0\u867d\u7136\u4ea6\u6781\u656c\u6155\uff0c\u5f1f\u5b50\u5bf9\u5415\u5927\u4fa0\u867d\u7136\u4e0d\u6562\u4ee5\u4e0b\u72af\u4e0a\uff0c\u4f46\u5415\u5927\u4fa0\u51fa\u8a00\u5982\u518d\u8fb1 \u53ca\u5bb6\u5e08\uff0c\u5f1f\u5b50\u4e5f\u8bf4\u4e0d\u5f97\u8981\u5192\u72af\u5415\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u76ee\u5149\u4e00\u6c88\uff0c\u5446\u5446\u51dd\u89c6\u5728\u8fd9\u5c11\u5e74\u8eab\u4e0a\uff0c\u7a81\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u6325\u624b\u9053\uff1a \u3000\u3000\u201c\u53bb\uff0c\u53bb\uff01\u201d \u3000\u3000\u5c11\u5e74\u953a\u9759\u606d\u8eab\u4e00\u63d6\uff0c\u7f13\u7f13\u56de\u8fc7\u5934\uff0c\u5927\u6b65\u8d70\u53bb\uff0c\u4ed6\u4e00\u76f4\u6728\u65e0\u8868\u60c5\u7684\u9762\u5bb9\u4e0a\uff0c\u6b64\u523b\u4e0d\u77e5\u600e \u5730\uff0c\u5374\u5df2\u6709\u4e86\u4e00\u9635\u96be\u8a00\u7684\u626d\u66f2\u3002 \u3000\u3000\u4f0a\u98ce\u76ee\u9001\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u6d88\u5931\u5728\u6797\u6728\u6df1\u5904\uff0c\u65b9\u81ea\u957f\u53f9\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8427\u65e0\u8fd9\u4e07\u6076\u7684\u9b54\u5934\uff0c\u7adf\u6709\u4e2a\u8fd9\u4e48\u597d\u7684\u5f92\u5f1f\u3002\u201d \u3000\u3000\u51cc\u7433\u4ea6\u81ea\u8f7b\u8f7b\u53f9\u9053\uff1a \u3000\u3000\u201c\u521a\u624d\u6211\u53ea\u6015\u4f60\u628a\u4ed6\u6740\u6b7b\u3002\u201d\u4f0a\u98ce\u76ee\u5149\u4e00\u5782\uff0c\u5374\u542c\u5979\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f46\u6211\u90a3\u65f6\u53c8\u60f3\uff0c\u4f60\u4e0d\u4f1a\u662f\u90a3\u79cd\u4eba\u7684\uff0c\u5230\u540e\u6765\u2026\u2026\u201d\u5979\u7adf\u81ea\u7f13\u7f13\u5782\u4e0b\u773c\uff1a\u201c\u540e\u6765\uff0c\u4f60\u679c \u7136\u6ca1\u6709\u8ba9\u6211\u5931\u671b\u3002\u201d \u3000\u3000\u4f0a\u98ce\u52c9\u5f3a\u538b\u5236\u7740\u5fc3\u4e2d\u7684\u6fc0\u52a8\uff0c\u7f13\u7f13\u56de\u8fc7\u5934\u53bb\uff0c\u5374\u89c1\u51cc\u7433\u5df2\u8d70\u5230\u4ed6\u524d\u9762\uff0c\u8f7b\u8f7b\u4ece\u90a3\u8001\u4eba\u7684 \u8eab\u4e0a\uff0c\u62fe\u8d77\u90a3\u8282\u65ad\u6307\uff0c\u5446\u5446\u5730\u51dd\u6ce8\u4e86\u534a\u664c\uff0c\u8f7b\u8f7b\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u6495\u4e0b\u4e86\u4e00\u6761\u8863\u895f\uff0c\u4ed4\u7ec6\u5730\u5c06\u5b83 \u5305\u4e86\u8d77\u6765\uff0c\u7a81\u5730\u62ac\u8d77\u5934\uff0c\u7b14\u76f4\u5730\u671b\u5411\u4f0a\u98ce\uff0c\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u6211\u66ff\u4f60\u6536\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u4f0a\u98ce\u7f13\u7f13\u62ac\u8d77\u5934\uff0c\u5374\u53c8\u7f13\u7f13\u5782\u4e0b\u5934\uff0c\u4ed6\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u5374\u542c\u51cc\u7433\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u6709\u4e00\u6837\u4e1c\u897f\u8981\u9001\u4f60\u3002\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u5979\u7f13\u7f13\u4f38\u8fc7\u7389\u638c\uff0c\u638c\u4e2d\u653e\u7684\u662f\u9762\u8c61\u7259\u7684\u724c\u5b50\uff0c\u4e0a\u9762\u6781\u7cbe\u81f4\u5730\u96d5\u7740\u4e09 \u9897\u5fc3\u3002 \u3000\u3000\u4f0a\u98ce\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u4f46\u89c9\u5343\u767e\u79cd\u6ecb\u5473\uff0c\u4e00\u9f50\u6d8c\u4e0a\u5fc3\u5934\uff0c\u4ea6\u5206\u4e0d\u6e05\u662f\u60b2\uff01\u662f\u6101\uff01\u662f\u6068\uff01\u4ed6 \u52c9\u5f3a\u6324\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u6447\u5934\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u8fd9\u4e2a\u2014\u2014\u4f60\u8fd8\u662f\u81ea\u5df1\u6536\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u51cc\u7433\u79c0\u76ee\u4e00\u5f20\uff0c \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u5446\u4e86\u4e00\u5446\uff0c\u5f3a\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e71\u9001\u522b\u4eba\u4e1c\u897f\uff0c\u4f60\u5988\u5988\u4f1a\u9a82\u4f60\u7684\u3002\u201d \u3000\u3000\u51cc\u7433\u6625\u8471\u822c\u7684\u624b\u638c\uff0c\u4ecd\u7b14\u76f4\u5730\u4f38\u5728\u4f0a\u98ce\u9762\u524d\uff1a \u3000\u3000\u201c\u8fd9\u662f\u5e08\u5085\u9001\u7ed9\u6211\u7684\uff0c\u6211\u5988\u5988\u600e\u4f1a\u9a82\u6211\u3002\u201d\u5979\u79cb\u6ce2\u4e00\u8f6c\uff1a \u3000\u3000\u201c\u4f60\u5728\u6c5f\u6e56\u4e2d\u95ef\u8361\uff0c\u6709\u4e86\u8fd9\u724c\u5b50\uff0c\u4e5f\u8bb8\u6709\u7528\uff0c\u4f60\u770b\uff0c\u8fd9\u4e0a\u9762\u8fd8\u6709\u201c\u4e09\u5fc3\u795e\u541b\u201d\u7684\u8868\u8bb0 \u5462\uff1f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8981\uff0c\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u5730\u8bf4\u7740\uff0c\u8bed\u58f0\u4e4b\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e00\u79cd\u4e0d\u53ef\u63cf\u8ff0\u7684\u5fe7\u90c1\uff0c\u5c31\u50cf\u662f\u4e0d\u77e5\u9053\u591a\u4e48\u6015\u4f0a\u98ce\u62d2\u7edd \u5979\u4e00\u6837\u3002 \u3000\u3000\u4f0a\u98ce\u53c8\u5446\u4e86\u5446\uff0c\u7ec8\u4e8e\u7f13\u7f13\u4f38\u8fc7\u624b\uff0c\u63a5\u8fc7\u4e86\u8fd9\u9762\u7389\u724c\uff0c\u53c8\u81ea\u5f3a\u7b11\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\u4e00\u5b9a\u8981\u9001\u7ed9\u6211\uff0c\u6211\u600e\u4f1a\u4e0d\u8981\u5462\uff1f\u201d \u3000\u3000\u51cc\u7433\u79c0\u7709\u4e00\u626c\uff1a \u3000\u3000\u201c\u4f60\u8981\u4e86\u5c31\u597d\u2014\u2014\u5582\uff0c\u6211\u95ee\u4f60\uff0c\u600e\u4e48\u7a81\u7136\u53eb\u6211\u7433\u513f\u4e86\u2014\u2014\u4e0d\u8fc7\uff0c\u7433\u513f\u4e5f\u5f88\u597d\u542c\uff0c\u4f0a\u98ce\uff0c \u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u7a81\u5730\u53cc\u7709\u4e00\u76b1\uff01 \u3000\u3000\u201c\u4e0d\u8fc7\u4f60\u4ee5\u540e\u4e0d\u8981\u518d\u53eb\u6211\u4f0a\u98ce\u4e86\u3002\u201d \u3000\u3000\u51cc\u7433\u65b9\u81ea\u626c\u8d77\u7684\u79c0\u7709\uff0c\u6b64\u523b\u7a81\u53c8\u98a6\u5728\u4e00\u8d77\uff0c\u60f6\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff0c\u4e3a\u4ec0\u4e48\u2026\u2026\u4f60\u4e0d\u559c\u6b22\u6211\u53eb\u4f60\u7684\u540d\u5b57\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4f0a\u98ce\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u5979\u5a07\u8273\u5929\u771f\u7684\u9762\u9765\u4e0a\uff0c\u6b64\u523b\u7adf\u5145\u6ee1\u4e86\u60b2\u82e6\u60f6\u6025\u4e4b\u8272\uff0c\u4e00\u53cc\u660e\u5a9a\u7684\u79cb \u6ce2\u4e2d\uff0c\u518d\u50cf\u662f\u8981\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u53ea\u89c9\u5fc3\u4e2d\u60c5\u601d\u53c8\u81ea\u5927\u4e71\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u53e3\u4e2d\u5374\u4ecd\u5f3a\u7b11\u9053\uff1a \u3000\u3000\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u2014\u2014\u6211\u4ee5\u540e\u518d\u4e5f\u4e0d\u7528\u4f0a\u98ce\u8fd9\u4e2a\u540d\u5b57\u4e86\uff0c\u4f60\u2026\u2026\u4f60\u8fd8\u662f\u53eb\u6211\u5357\u4eba\u597d \u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u5929\u771f\u800c\u7eaf\u7f8e\u7684\u51cc\u7433\uff0c\u7acb\u523b\u5927\u6b22\u60a6\u8d77\u6765\uff0c\u5979\u5a07\u7f8e\u7684\u9762\u9765\u4e0a\uff0c\u5fcd\u4e0d\u4f4f\u6cdb\u51fa\u4e00\u4e1d\u559c\u8272\uff0c\u6084 \u6084\u5730\u7728\u52a8\u7740\u5979\u4e00\u53cc\u660e\u7738\uff0c\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u5357\u4eba\u2026\u2026\u5357\u4eba\uff0c\u8fd9\u771f\u662f\u4e00\u4e2a\u591a\u4e48\u54cd\u4eae\u7684\u540d\u5b57\u5440\uff01\u201d\u5979\u82b3\u5fc3\u4e2d\u5374\u5728\u6697\u5fd6\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u540d \u5b57\u4ee5\u540e\u4e00\u5b9a\u4f1a\u9707\u52a8\u6b66\u6797\u7684\uff01\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u56db\u7ae0\u3000\u82ad\u70bc\u6210\u94a2>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u56db\u7ae0\u3000\u82ad\u70bc\u6210\u94a2 \u3000\u3000\u5979\u79cb\u6ce2\u4e00\u62ac\uff0c\u53ea\u89c1\u201c\u5415\u5357\u4eba\u201d\u6b63\u671b\u7740\u624b\u4e2d\u7684\u4e09\u5fc3\u7259\u724c\u51fa\u795e\uff0c\u4f3c\u4e4e\u5728\u60f3\u7740\u4ec0\u4e48\uff0c\u5979\u8f7b\u8f7b\u8bf4 \u9053\uff1a \u3000\u3000\u201c\u4f0a\u2014\u2014\u5357\u4eba\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u5357\u4eba\u4e00\u6115\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u5728\u60f3\uff0c\u4f60\u80fd\u627e\u5230\u4e09\u5fc3\u8001\u524d\u8f88\u8fd9\u79cd\u5e08\u5085\uff0c\u771f\u662f\u5e78\u8fd0\u7684\u5f88\u3002\u201d \u3000\u3000\u51cc\u7433\u7728\u4e86\u7728\u773c\u775b\uff1a \u3000\u3000\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u8fd8\u6709\u4e2a\u5e08\u5085\u5462\uff01\u5df4\u662f\u5251\u5148\u751f\uff0c\u672c\u6765\u5728\u7ec8\u5357\u5c71\uff0c\u6211\u662f\u62dc\u4ed6\u8001\u4eba\u5bb6\u4e3a\u5e08\u7684\uff0c \u90a3\u77e5\u9053\u4e00\u4e0b\u5c71\u540e\uff0c\u4e00\u5929\u665a\u4e0a\uff0c\u4ed6\u8001\u4eba\u5bb6\u7a81\u7136\u8d70\u4e86\uff0c\u7559\u4e00\u4e0b\u5f20\u6761\u5b50\uff0c\u624d\u8981\u5e08\u5085\u5148\u4f20\u6211\u4eec\u529f \u592b\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u9053\uff1a \u3000\u3000\u201c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u91cc\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u7adf\u90fd\u50cf\u662f\u987a\u53e3\u9053\u51fa\uff0c\u4f46\u51cc\u7433\u6b64\u523b\u5fc3\u4e2d\u6b63\u6709\u7740\u65e0 \u7a77\u7f8e\u4e3d\u7684\u61a7\u61ac\uff0c\u7adf\u4ec0\u4e48\u4e5f\u6ca1\u6709\u770b\u5230\uff01 \u3000\u3000\u7b49\u5230\u5415\u5357\u4eba\u7684\u76ee\u5149\u7f13\u7f13\u79fb\u5230\u5730\u4e0a\u7684\u9996\uff0c\u4ed6\u832b\u7136\u7684\u795e\u8272\uff0c\u624d\u4e3a\u4e4b\u4e00\u53d8\uff0c\u4e8e\u662f\u4ed6\u957f\u53f9\u7740\u5c06\u8fd9 \u56db\u5177\u8eab\uff0c\u8f7b\u8f7b\u5730\u6392\u5728\u4e00\u5904\uff0c\u53ea\u89c1\u4ed6\u4eec\u8eab\u4e0a\uff0c\u7adf\u5404\u5404\u63d2\u7740\u4e00\u67c4\u9ec4\u91d1\u5f2f\u5200\uff0c\u6709\u7684\u5728\u80c1\u4e0b\uff0c\u6709\u7684\u5728 \u8170\u7554\uff0c\u4f46\u5374\u4ff1\u5728\u8981\u5bb3\u4e4b\u5904\uff0c\u4ed6\u4e0d\u7981\u6697\u53f9\uff01 \u3000\u3000\u201c\u8fd9\u8427\u65e0\u7684\u6b66\u529f\u7684\u786e\u4e0d\u5f31\uff0c\u7adf\u80fd\u540c\u65f6\u51fb\u4e2d\u8fd9\u56db\u4e2a\u4eba\u7684\u8981\u5bb3\uff0c\u53ea\u662f\u4ed6\u624b\u6bb5\u4e5f\u592a\u8fa3\u4e86\u4e9b\uff0c\u5509 \u2014\u2014\u6211\u4e0d\u77e5\u9053\u4ed6\u5bf9\u5982\u6b64\u4eb2\u8fd1\u7684\u4eba\u600e\u4e0b\u5f97\u4e86\u5982\u6b64\u8fa3\u624b\u3002\u201d \u3000\u3000\u4ed6\u5c06\u8fd9\u56db\u67c4\u5f2f\u5200\uff0c\u4e00\u9f50\u8c28\u614e\u5730\u653e\u5165\u6000\u91cc\u3002 \u3000\u3000\u201c\u4e94\u6708\u7aef\u9633\u2026\u2026\u4e94\u6708\u7aef\u9633\u2026\u2026\u201d\uff0c\u4ed6\u6697\u4e2d\u81ea\u8a93\uff0c\u5c31\u5728\u4e94\u6708\u7aef\u9633\u8fd9\u4e00\u5929\uff0c\u4ed6\u8981\u5c06\u8fd9\u4e94\u67c4\u5f2f \u5200\uff0c\u4e00\u9f50\u63d2\u56de\u8427\u65e0\u8eab\u4e0a\u3002 \u3000\u3000\u897f\u6881\u5c71\u4e0a\uff0c\u53c8\u591a\u4e86\u4e94\u5904\u65b0\u8d77\u7684\u575f\u5893\u3002 \u3000\u3000\u8fd9\u4e94\u5904\u575f\u5893\uff0c\u662f\u5415\u5357\u4eba\u548c\u51cc\u7433\u5c3d\u4e86\u6700\u5927\u7684\u52aa\u529b\uff0c\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u6398\u597d\u7684\uff0c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u5728\u62c5 \u5fc3\u7740\u5c71\u6df1\u5904\u7684\u5b59\u654f\u548c\u8bb8\u767d\u3002 \u3000\u3000\u201c\u5988\u5988\u600e\u5730\u4e0d\u4e0b\u6765\uff0c\u96be\u9053\u90a3\u8fb9\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u51cc\u7433\u60f6\u6025\u5730\u4f4e\u8bed\u7740\uff0c\u4e00\u9762\u53c8\u8f7b\u8f7b\u957f\u53f9\uff0c\u5979\u81ea\u89c9\u81ea\u5df1\u5df2\u6210\u719f\u4e86\u4e0d\u5c11\uff0c\u56e0\u4e3a\u5979\u5df2\u7ecf\u5386\u8fc7\u60b2\u54c0 \u4e0e\u6b7b\u4ea1\uff0c\u5979\u5df2\u4e3a\u4eba\u6398\u8fc7\u575f\u5893\u3002 \u3000\u3000\u800c\u5415\u5357\u4eba\u7684\u5fc3\u60c5\u5462\uff01\u81ea\u7136\u66f4\u662f\u60b2\u54c0\u800c\u6c88\u91cd\u7684\uff0c\u5728\u8fd9\u534a\u65e5\u4e4b\u4e2d\uff0c\u4ed6\u4eb2\u624b\u57cb\u846c\u4e86\u8bb8\u591a\u4eba\uff0c\u4ed6 \u9661\u7136\u4e86\u89e3\u4e86\u751f\u4e0e\u6b7b\u4e4b\u95f4\u7684\u5206\u9694\uff0c\u53ea\u662f\u4e00\u6bb5\u591a\u4e48\u77ed\u6682\u7684\u8ddd\u79bb\uff0c\u5c24\u5176\u4ee4\u4ed6\u5fc3\u4e2d\u60b2\u54c0\u4e0e\u60ef\u6168\u7684\u662f\uff01 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8be5\u6b7b\u7684\u4eba\u4e0d\u6b7b\uff0c\u800c\u4e0d\u8be5\u6b7b\u7684\u4eba\u5374\u504f\u504f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u98ce\u58f0\u5439\u52a8\u7740\u6797\u6728\uff0c\u4ed6\u7b14\u76f4\u5730\u8dea\u5728\u8fd9\u65b0\u8d77\u7684\u575f\u5893\u524d\uff0c\u9ed8\u8bf5\u7740\u795d\u7977\u7684\u8bcd\u53e5\uff0c\u4ed6\u867d\u7136\u4ece\u4e0d\u76f8\u4fe1 \u9b3c\u795e\uff0c\u4f46\u6b64\u523b\u4ed6\u5374\u4ecd\u4e0d\u7981\u4e3a\u8fd9\u4e9b\u6b7b\u53bb\u7684\u82f1\u9b42\u7948\u7977\uff0c\u4ed6\u795d\u7977\u8fd9\u4e9b\u4e3a\u4e86\u7231\u548c\u4e49\u800c\u6b7b\u7684\u4eba\uff0c\u6b7b\u540e\u80fd \u591f\u98de\u5347\u6781\u4e50\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u4eec\u518d\u6b21\u5411\u5c71\u4e0a\u63a0\u53bb\uff0c\u8fd9\u65f6\u5415\u5357\u4eba\u5fc3\u4e2d\u6124\u6012\uff0c\u60b2\u54c0\uff0c\u4ff1\u5df2\u8fc7\u53bb\uff0c\u53ea\u89c9\u5fc3\u4e2d\u7a7a\u7a7a\u8361 \u8361\u5730\uff0c\u8be5\u60f3\u7684\u4e8b\u60c5\u5f88\u591a\uff0c\u5374\u4e00\u4ef6\u90fd\u60f3\u4e0d\u8d77\u6765\uff0c\u4e0d\u8be5\u60f3\u7684\u4e8b\u60c5\u867d\u4e0d\u591a\uff0c\u5374\u4ef6\u4ef6\u90fd\u5728\u5fc3\u91cc\u98d8\u6765\u98d8 \u53bb\u3002 \u3000\u3000\u4ed6\u6697\u53f9\u4e00\u58f0\uff0c\u504f\u8fc7\u5934\u53bb\uff0c\u8fd9\u624d\u53d1\u73b0\u51cc\u7433\u8ddf\u5728\u4ed6\u8eab\u65c1\uff0c\u8d70\u5f97\u4f3c\u4e4e\u6781\u4e3a\u5403\u529b\uff0c\u51cc\u7433\u89c1\u4ed6\u671b\u7740 \u81ea\u5df1\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u529f\u592b\u53ef\u771f\u597d\uff0c\u6211\u77e5\u9053\u4f60\u8bb8\u4e45\u90fd\u6ca1\u6709\u4f11\u606f\u8fc7\uff0c\u4e5f\u6ca1\u6709\u8fdb\u4ec0\u4e48\u996e\u98df\uff0c\u53ef\u662f\u73b0\u5728\u5374\u4e00\u70b9\u513f \u4e5f\u4e0d\u7d2f\uff0c\u6211\u2026\u2026\u6211\u53ef\u771f\u7d2f\u4e86\u3002\u201d \u3000\u3000\u5415\u5357\u5165\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u6709\u5982\u6b64\u660e\u5e08\uff0c\u5c06\u6765\u6b66\u529f\u8fd8\u6015\u4e0d\u80dc\u6211\u5341\u500d\u3002\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u5730\u60f3\u8d77\u5979\u5728\u201c\u4e09\u5fc3\u795e\u541b\u201d \u5904\u5b66\u6b66\u5df2\u6709\u4e0d\u5c11\u65f6\u65e5\uff0c\u600e\u5730\u6b66\u529f\u5374\u672a\u89c1\u5f97\u5982\u4f55\u7cbe\u8fdb\uff01\u800c\u81ea\u5df1\u53ea\u4e0d\u8fc7\u5c06\u201c\u5929\u661f\u79d8\u7b08\u201d\u4e2d\u7684\u6b66\u529f \u8bc0\u8981\uff0c\u7c97\u7565\u5730\u7ec3\u4e86\u4e00\u904d\uff0c\u8fdb\u6b65\u5374\u8fdc\u975e\u6614\u65e5\u53ef\u6bd4\u3002 \u3000\u3000\u201c\u5509\u5982\u6b64\u770b\u6765\uff0c\u8fd9\u201c\u5929\u661f\u79d8\u7b08\u201d\u679c\u7136\u65e0\u6127\u4e3a\u6b66\u6797\u79d8\u5b9d\u4e86\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5339\u592b\u65e0\u7f6a\uff0c\u6000\u74a7\u5176\u7f6a\uff0c\u6211\u8eab\u4e0a\u65e2\u6709\u5982\u6b64\u6b66\u6797\u79d8\u5b9d\uff0c\u82e5\u88ab\u4eba\u77e5\u9053\uff0c\u5c11\u4e0d\u4e86\u53c8\u8981\u60f9\u51fa\u591a\u5c11 \u9ebb\u70e6\uff0c\u5e78\u597d\u77e5\u9053\u6b64\u4e8b\u7684\u4eba\uff0c\u5be5\u5be5\u65e0\u51e0\uff0c\u5c31\u7b97\u5251\u5148\u751f\uff0c\u4e09\u5fc3\u795e\u541b\uff0c\u4ee5\u53ca\u51cc\u592b\u4eba\u7b49\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u77e5 \u9053\u6211\u7a76\u7adf\u5f97\u5230\u6b64\u7269\u6ca1\u6709\u4f46\u6b64\u7269\u5374\u5e94\u662f\u5251\u5148\u751f\u6240\u6709\uff0c\u65e5\u540e\u6211\u82e5\u89c1\u7740\u4ed6\u8001\u4eba\u5bb6\uff0c\u5b9a\u5f97\u5c06\u8fd9\u672c\u79d8\u7b08 \u4ea4\u8fd8\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u53c8\u4e00\u8f6c\uff1a \u3000\u3000\u201c\u5440\u2014\u2014\u90a3\u4e07\u5929\u840d\u662f\u77e5\u9053\u6b64\u4e66\u843d\u5728\u6211\u624b\u7684\uff0c\u65b9\u624d\u4e5f\u8bb8\u56e0\u4e3a\u4e8b\u6545\u592a\u591a\uff0c\u662f\u4ee5\u672a\u66fe\u52a8\u624b\uff0c\u6b64 \u523b\u6211\u82e5\u8d70\u5230\u90a3\u91cc\uff0c\u4ed6\u5c11\u4e0d\u5f97\u4f1a\u6709\u5f3a\u593a\uff0c\u6b64\u523b\u6211\u6b66\u529f\u8fd8\u4e0d\u662f\u4ed6\u7684\u654c\u624b\uff0c\u8fd9\u53c8\u8be5\u5982\u4f55\u662f\u597d\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u811a\u6b65\u7a81\u987f\uff0c\u51cc\u7433\u5f80\u524d\u51b2\u51fa\u6570\u6b65\uff0c\u60ca\u8be7\u5730\u56de\u8eab\u95ee\u9053\uff1a \u3000\u3000\u201c\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u5415\u5357\u4eba\u5f3a\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u3000\u3000\u201c\u7a81\u7136\u60f3\u8d77\u4e86\u4e00\u4ef6\u4e8b\uff0c\u662f\u4ee5\u2014\u2014\u201d \u3000\u3000\u51cc\u7433\u79c0\u7709\u4e00\u76b1\uff0c\u60f6\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u4e0d\u60f3\u548c\u6211\u4e00\u9f50\u8d70\u4e48\uff1f\u201d \u3000\u3000\u5415\u5357\u4eba\u5fc3\u5ff5\u6570\u8f6c\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u5f53\u884c\u5219\u884c\uff0c\u5f53\u6b62\u5219\u6b62\uff0c\u5415\u5357\u4eba\u5440\u5415\u5357\u4eba\uff0c\u4f60\u4e00\u751f\u884c\u4e8b\uff0c\u673a\u654f\u6709\u4f59\uff0c\u4e5f \u8fd8\u6b63\u76f4\uff0c\u4f46\u662f\u52c7\u6562\u5374\u4e0d\u8db3\uff0c\u662f\u4ee5\u65b9\u4f1a\u6709\u8bc8\u6b7b\u4e4b\u4e8b\uff0c\u5982\u4eca\u7adf\u5728\u8427\u65e0\u53e3\u4e2d\u843d\u4e0b\u4e2a\u8bdd\u67c4\u3002\u7537\u5b50\u6c49\u80dc \u5219\u80dc\uff0c\u8d25\u5219\u8d25\uff0c\u751f\u5219\u751f\uff0c\u6b7b\u5219\u6b7b\uff0c\u5f97\u5931\u4e4b\u95f4\uff0c\u672c\u5bfb\u5e38\u4e8b\u8033\uff0c\u5982\u4eca\u4f60\u867d\u65e0\u5fc3\u8d1f\u759a\uff0c\u4f46\u5374\u6ee1\u8eab\u5b7d \u503a\uff0c\u60f3\u90a3\u8427\u5357\u82f9\u82e5\u975e\u4e3a\u4e86\u4f60\uff0c\u53c8\u4f55\u81f3\u843d\u5230\u8fd9\u822c\u72b6\u51b5\uff0c\u4ee5\u540e\u4f60\u5904\u4e16\u884c\u4e8b\uff0c\u82e5\u518d\u5982\u6b64\u754f\u9996\u754f\u5c3e\uff0c \u4f11\u8bf4\u4e0d\u80fd\u7b97\u662f\u4e2a\u4e0a\u65e0\u6127\u4e8e\u5929\uff0c\u4e0b\u65e0\u600d\u4e8e\u4eba\u7684\u5927\u4e08\u592b\uff0c\u4f60\u7b80\u76f4\u4e0d\u80fd\u7b97\u662f\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u51cc\u7433\u89c1\u4ed6\u7a81\u7136\u5782\u9996\u6c88\u601d\u8d77\u6765\uff0c\u7adf\u6ca1\u6709\u56de\u7b54\u81ea\u5df1\u7684\u8bdd\uff0c\u5a07\u9765\u4e4b\u4e0a\uff0c\u7a81\u53c8\u6ee1\u73b0\u51c4\u82e6\u4e4b\u8272\uff0c\u5e7d \u5e7d\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u662f\u4e0d\u613f\u548c\u6211\u2014\u2014\u201d \u3000\u3000\u90a3\u77e5\u5979\u8bed\u58f0\u65b9\u4e86\uff0c\u5415\u5357\u4eba\u7a81\u5730\u4e00\u633a\u80f8\u819b\uff0c\u8f69\u7709\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u6211\u81ea\u7136\u8981\u966a\u4f60\u53bb\u7684\uff0c\u53ea\u4e0d\u8fc7\u6709\u4ef6\u4e8b\uff0c\u6211\u65b9\u624d\u5076\u7136\u8981\u60f3\u60f3\u7f62\u4e86\u3002\u201d \u3000\u3000\u51cc\u7433\u5c55\u989c\u4e00\u7b11\uff0c\u5782\u4e0b\u5934\u53bb\uff0c\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u90a3\u5c31\u597d\u4e86\uff0c\u6211\u53ea\u6015\u2026\u2026\u201d\u81fb\u9996\u4e00\u62ac\uff0c\u5fae\u63a0\u9b13\u53d1\uff0c\u5411\u524d\u5954\u8d70\uff0c\u4f0a\u98ce\u51dd\u6ce8\u7740\u5979\u7a88\u7a95\u7684\u8eab \u5f71\uff0c\u5446\u5446\u5730\u671b\u4e86\u8bb8\u4e45\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u5ffd\u800c\u6ee1\u73b0\u5fe7\u90c1\uff0c\u5ffd\u800c\u53c8\u63a0\u8fc7\u4e00\u4e1d\u559c\u8272\uff0c\u53ea\u89c1\u5230\u51cc\u7433\u53c8\u5df2\u5954 \u51fa\u6570\u4e08\uff0c\u56de\u9996\u547c\u9053\uff1a \u3000\u3000\u201c\u5357\u4eba\uff0c\u5feb\u4e9b\u561b\u3002\u201d \u3000\u3000\u4ed6\u65b9\u81ea\u5b9a\u4e86\u5b9a\u795e\uff0c\u968f\u540e\u63a0\u53bb\u3002 \u3000\u3000\u8981\u77e5\u9053\u5415\u5357\u4eba\u672c\u662f\u5929\u8d44\u7edd\u9876\u7684\u4e0d\u4e16\u4e4b\u624d\uff0c\u800c\u4e14\u751f\u5177\u81f3\u6027\uff0c\u53ea\u662f\u4ed6\u81ea\u5e7c\u53ca\u957f\uff0c\u4e00\u5e06\u98ce\u987a\uff0c \u5c11\u5e74\u626c\u540d\uff0c\u7f8e\u7737\u5982\u82b1\uff0c\u65e0\u8bba\u4e8b\u4e1a\uff0c\u5bb6\u5ead\uff0c\u90fd\u6210\u529f\u5df2\u6781\uff0c\u662f\u4ee5\u5728\u5982\u6b64\u60c5\u51b5\u4e2d\u957f\u6210\u7684\u4ed6\uff0c\u4fbf\u96be\u514d \u5c11\u4e86\u4e9b\u521a\u5f3a\u52c7\u6562\u4e4b\u6c14\u3002 \u3000\u3000\u76f4\u5230\u5e74\u524d\u4ed6\u5a07\u59bb\u80cc\u53db\uff0c\u53c8\u88ab\u4eba\u82e6\u82e6\u6b3a\u51cc\uff0c\u4ed6\u8fd9\u624d\u906d\u53d7\u5e73\u751f\u7b2c\u4e00\u6b21\u91cd\u5927\u7684\u6253\u51fb\uff0c\u800c\u5728\u8fd9\u79cd \u6253\u51fb\u4e4b\u4e0b\uff0c\u4ed6\u51e0\u4e4e\u832b\u7136\u4e0d\u77e5\u6240\u63aa\uff0c\u82e6\u601d\u826f\u4e45\uff0c\u4ed6\u8fd9\u624d\u5728\u4fdd\u5b9a\u57ce\u5916\u8bc8\u6b7b\uff0c\u4ee5\u56fe\u7792\u8fc7\u5929\u4e89\u6559\u7684\u8033 \u76ee\uff0c\u65e5\u540e\u624d\u56fe\u590d\u4ec7\uff0c\u8fd9\u79cd\u884c\u4e8b\uff0c\u4e5f\u6b63\u5982\u4ed6\u81ea\u5df1\u6240\u8bf4\uff0c\u6b63\u662f\u673a\u654f\u6709\u4f59\uff0c\u52c7\u6c14\u5374\u4e0d\u8db3\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u53ef\u7b97\u662f\u9971\u7ecf\u5fe7\u60a3\uff0c\u6b63\u662f\u6240\u8c13\u201c\u7389\u4e0d\u7422\uff0c\u4e0d\u6210\u5668\u201d\uff0c\u8270\u82e6\u7684\u78e8 \u6298\uff0c\u5404\u79cd\u7684\u6253\u51fb\uff0c\u7ec8\u4e8e\u4f7f\u5f97\u8fd9\u4e00\u5757\u672c\u8d28\u6781\u4f73\u7684\u67d4\u94c1\uff0c\u953b\u6210\u4e86\u575a\u94a2\uff0c\u6b64\u523b\u4ed6\u7a81\u6709\u5982\u6b64\u6539\u53d8\uff0c\u867d \u4e5f\u662f\u56e0\u4e3a\u8fd9\u4e00\u65e5\u591c\u6765\uff0c\u4ed6\u6240\u89c1\u5230\u7684\u666f\u51b5\u592a\u60e8\uff0c\u6240\u906d\u53d7\u7684\u523a\u6fc0\u592a\u6df1\uff0c\u4f46\u201c\u9ec4\u6cb3\u51b0\u51bb\uff0c\u975e\u4e00\u65e5\u4e4b \u5bd2\u201d\uff0c\u4ed6\u4e4b\u80fd\u6709\u8fd9\u79cd\u6539\u53d8\uff0c\u5374\u6b63\u4e5f\u662f\u4e00\u70b9\u4e00\u6ef4\u6162\u6162\u5f62\u6210\u7684\u54e9\u3002 \u3000\u3000\u751f\u6b7b\u4e4b\u5ff5\u770b\u5f97\u4e00\u6de1\uff0c\u5fc3\u4e2d\u4fbf\u5766\u5766\u8361\u8361\uff0c\u5f97\u5931\u4e4b\u5ff5\u770b\u505c\u4e00\u6de1\uff0c\u4e3a\u4eba\u4fbf\u4e00\u4e1d\u4e0d\u82df\uff0c\u4f46\u8981\u505a\u6210 \u8fd9\u201c\u65e0\u754f\u201d\u4e24\u5b57\uff0c\u53c8\u4f55\u5c1d\u5bb9\u6613\uff01 \u3000\u3000\u5c71\u98ce\u6251\u9762\u800c\u6765\uff0c\u751a\u662f\u5f3a\u52b2\uff0c\u51cc\u7433\u5fae\u98a6\u67f3\u7709\uff0c\u57cb\u6028\u7740\u9053\uff1a \u3000\u3000\u201c\u5440\uff0c\u6211\u4eec\u662f\u9006\u98ce\u800c\u4e0a\uff0c\u96be\u602a\u8fd9\u4e48\u5403\u529b\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u6709\u9006\u98ce\u5c31\u6709\u987a\u98ce\uff0c\u6ca1\u6709\u9006\u98ce\u600e\u4f1a\u6709\u987a\u98ce\u54e9\u3002\u201d \u3000\u3000\u51cc\u7433\u5446\u4e86\u4e00\u5446\uff0c\u53ea\u89c9\u8fd9\u4e24\u53e5\u8bdd\u9053\u7406\u771f\u662f\u518d\u7b80\u5355\u4e5f\u6ca1\u6709\u4e86\uff0c\u4f46\u5374\u53c8\u662f\u90a3\u4e48\u771f\u5b9e\u548c\u51c6\u786e\uff0c\u4ed6 \u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e48\u7b80\u5355\u7684\u9053\u7406\uff0c\u4e3a\u4ec0\u4e48\u4eba\u4eec\u6709\u65f6\u5c31\u504f\u504f\u4e0d\u80fd\u4e86\u89e3\u54e9\u3002\u201d \u3000\u3000\u8f6c\u5934\u671b\u5904\uff0c\u53ea\u89c1\u5415\u5357\u4eba\u82f1\u633a\u9038\u4fca\u7684\u9762\u76ee\u4e4b\u4e0a\uff0c\u5bb9\u5149\u7115\u53d1\uff0c\u6ee1\u73b0\u6b63\u76f4\u575a\u5f3a\u7684\u5149\u8f89\uff0c\u90a3\u91cc\u6709 \u4e00\u4e1d\u4e00\u6beb\u61ca\u4e27\u4e4b\u8272\uff0c\u5979\u5ffd\u7136\u4e86\u61c8\u8fd9\u79cd\u575a\u5f3a\u78ca\u843d\u7684\u7537\u5b50\uff0c\u6b63\u662f\u4e16\u4e0a\u6240\u6709\u7684\u5973\u5b50\u90fd\u7518\u5fc3\u4f9d\u9760\u7684\uff0c \u90a3\u8fdc\u6bd4\u4efb\u4f55\u4f9d\u9760\u90fd\u8981\u5b89\u5168\uff0c\u4e8e\u662f\u5979\u53c8\u4e0d\u7981\u8f7b\u8f7b\u4e00\u7b11\uff0c\u6251\u9762\u800c\u6765\u7684\u5c71\u98ce\u518d\u5f3a\u52b2\uff0c\u5979\u5374\u4e5f\u5168\u90e8\u4e0d \u518d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002 \u201c\u867d\u7136\u6709\u98ce\uff0c\u9633\u5149\u4e0d\u4e5f\u662f\u7b14\u76f4\u5730\u7167\u5728\u6211\u8eab\u4e0a\u5417\uff1f\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u4e94\u7ae0\u3000\u60b2\u559c\u4ea4\u96c6>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u4e94\u7ae0\u3000\u60b2\u559c\u4ea4\u96c6 \u3000\u3000\u884c\u4eba\u5bc6\u6797\uff0c\u8e0f\u4e0a\u5bc6\u9053\u3002 \u3000\u3000\u56db\u4e0b\u7adf\u9759\u5f97\u51fa\u5947\uff0c\u65b9\u624d\u5999\u624b\u8bb8\u767d\u5927\u7b11\u8c29\u9a82\u7684\u58f0\u97f3\uff0c\u6b64\u523b\u5df2\u5168\u90fd\u6ca1\u6709\u4e86\uff0c\u5415\u5357\u4eba\u548c\u51cc\u7433\u5bf9 \u671b\u4e00\u773c\uff0c\u4e24\u4eba\u76ee\u5149\u4e2d\u90fd\u4e0d\u7981\u73b0\u51fa\u60ca\u7591\u4e4b\u8272\u3002 \u3000\u3000\u518d\u5347\u5341\u6570\u4e08\uff0c\u5415\u5357\u4eba\u76ee\u5149\u4e0a\u671b\uff0c\u5fc3\u5374\u7a81\u5730\u5411\u4e0b\u4e00\u6c88\u3002 \u3000\u3000\u539f\u6765\u4ed6\u53ea\u89c1\u90a3\u7edd\u58d1\u4e4b\u8fb9\uff0c\u6b64\u523b\u7adf\u6e3a\u65e0\u4eba\u8ff9\uff0c\u5999\u624b\u8bb8\u767d\u548c\u5b59\u654f\u90fd\u4e0d\u77e5\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u53ea\u542c\u51cc \u7433\u60ca\u547c\u9053\uff1a \u3000\u3000\u201c\u5988\u5440\uff01\u201d \u3000\u3000\u7a88\u7a95\u7684\u8eab\u8eaf\uff0c\u53d1\u72c2\u4f3c\u5730\u63a0\u4e86\u4e0a\u53bb\uff0c\u5415\u5357\u4eba\u5fc3\u4e2d\u4ea6\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u4f46\u7ec8\u7a76\u5b9a\u529b\u7a0d\u4f73\uff0c\u53ea\u542c\u4e0a \u9762\u4f3c\u4e4e\u9690\u9690\u6709\u5973\u5b50\u54ed\u6ce3\u4e0e\u529d\u6170\u4e4b\u58f0\u4f20\u6765\uff0c\u4ed6\u5fc3\u4e2d\u5374\u53c8\u4e00\u51db\uff0c\u6697\u5730\u5bfb\u601d\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u771f\u5e94\u4e86\u4e24\u864e\u76f8\u4e89\uff0c\u5fc5\u6709\u4e00\u4f24\u90a3\u53e5\u8bdd\uff0c\u4ed6\u4e24\u4eba\u7adf\u6709\u4e00\u4eba\u6b7b\u4e86\u4e0d\u6210\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u8eab\u5f62\u4fbf\u53c8\u52a0\u5feb\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u63a0\u81f3\u7edd\u9876\uff0c\u53ea\u89c1\u51cc\u7433\u53d1\u5446\u5730\u7ad9\u5728\u7edd\u58d1\u4e4b\u8fb9\uff0c\u79cb \u6ce2\u51dd\u6ce8\u5728\u7edd\u58d1\u7684\u5bf9\u5cb8\u3002 \u3000\u3000\u800c\u5bf9\u5cb8\u90a3\u8fb9\uff0c\u90a3\u5efa\u5f97\u5de7\u593a\u5929\u5de5\u7684\u51cc\u7a7a\u98de\u4ead\u4e4b\u4e2d\uff0c\u4e07\u8679\u6b63\u4f0f\u5728\u5979\u5988\u5988\u8eab\u4e0a\uff0c\u4e24\u4eba\u76f8\u62e5\u75db \u54ed\uff0c\u4ed6\u4eec\u8eab\u4fa7\u4f2b\u7acb\u7740\u4e24\u4e2a\u5782\u9aeb\u4e2b\u73af\uff0c\u548c\u4e0d\u4f4f\u67d4\u58f0\u529d\u6170\u7684\u5b59\u654f\uff0c\u4ead\u7554\u4f3c\u4e4e\u5782\u7740\u4e24\u6761\u957f\u7d22\uff0c\u5176\u957f \u65e0\u6bd4\uff0c\u76f4\u4e0b\u7edd\u58d1\uff0c\u800c\u90a3\u5999\u624b\u8bb8\u767d\u4e0e\u94c1\u9762\u5b64\u884c\u5ba2\u4e07\u5929\u840d\uff0c\u6b64\u523b\u5374\u90fd\u4e0d\u77e5\u8d70\u5230\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u51cc\u7433\u4e00\u773c\u770b\u5230\u6bcd\u4eb2\uff0c\u82b3\u5fc3\u5df2\u81ea\u5927\u5b9a\uff0c\u4f46\u5979\u89c1\u4e86\u5bf9\u5cb8\u98de\u4ead\u4e2d\u7684\u60c5\u51b5\uff0c\u5374\u53c8\u60ca\u5f97\u4e0d\u77e5\u8be5\u5982\u4f55 \u662f\u597d\uff0c\u5446\u7acb\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u602f\u602f\u5730\u558a\u4e86\u58f0\uff1a \u3000\u3000\u201c\u5988\u5988\uff0c\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5b59\u654f\u8fd9\u624d\u56de\u8fc7\u5934\u6765\uff0c\u5415\u5357\u4eba\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u5979\u9762\u4e0a\u4ea6\u6ee1\u542b\u60b2\u621a\u4e4b\u8272\uff0c\u518d\u89c1\u4e86\u4e07\u6c0f\u6bcd\u5973\u75db \u54ed\u7684\u6837\u5b50\uff0c\u4fbf\u77e5\u9053\u94c1\u9762\u5b64\u884c\u5ba2\u5fc5\u6709\u4e0d\u6d4b\uff0c\u53ea\u89c1\u5b59\u654f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u4f3c\u662f\u653e\u5fc3\uff0c\u53c8\u4f3c\u662f\u57cb\u6028\uff1a \u3000\u3000\u201c\u4f60\u4eec\u73b0\u5728\u624d\u56de\u6765\u5440\uff1f\u201d \u3000\u3000\u4e07\u6c0f\u6bcd\u5973\u6b64\u523b\u4e5f\u4e0d\u9f50\u62ac\u8d77\u5934\u6765\uff0c\u4e07\u8679\u89c1\u7740\u5415\u5357\u4eba\uff0c\u79c0\u76ee\u4e00\u5f20\uff0c\u6cea\u73e0\u66f4\u6709\u5982\u6d8c\u6cc9\u822c\u593a\u7736\u800c \u51fa\uff0c\u5954\u5411\u4ead\u8fb9\uff0c\u4f38\u51fa\u53f3\u624b\uff0c\u6307\u5411\u90a3\u9634\u5cfb\u51a5\u6c88\uff0c\u6df1\u4e0d\u89c1\u5e95\u7684\u7edd\u58d1\u4e0b\u9762\uff0c\u653e\u58f0\u75db\u54ed\u7740\u9053\uff1a \u3000\u3000\u201c\u7239\u7239\u2026\u2026\u548c\u90a3\u2026\u2026\u59d3\u8bb8\u7684\u2026\u2026\u90fd\u2026\u2026\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u5fc3\u5934\u4e00\u9707\uff0c\u4fef\u9996\u4e00\u671b\uff0c\u9633\u5149\u867d\u7136\u5f3a\u70c8\uff0c\u4f46\u8fd9\u6df1\u6c88\u7684\u7edd\u58d1\uff0c\u6570\u5341\u4e08\u4e0b\uff0c\u4fbf\u51a5\u6c88\u96be \u89c1\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8fd9\u4e24\u4e2a\u6b66\u6797\u5947\u4eba\u4e4b\u4e89\uff0c\u679c\u771f\u662f\u4e0d\u6b7b\u4e0d\u4f11\uff0c\u4f46\u662f\u2014\u2014\u5509\uff0c\u4ed6\u4eec\u8fd9\u5374\u53c8\u662f\u4e3a\u4ec0\u4e48 \u5462\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u65e9\u5df2\u60f3\u5230\u8fd9\u4e00\u9634\u4e00\u9633\uff0c\u4e00\u67d4\u4e00\u521a\uff0c\u4e00\u6b63\u4e00\u53cd\u4e24\u4e2a\u6b66\u6797\u5947\u4eba\uff0c\u5c06\u6765\u4e86\u5c40\u5b9a\u5fc5\u751a\u60e8\uff0c \u4f46\u4ed6\u6b64\u523b\u81ea\u5df1\u4eb2\u773c\u89c1\u5230\u8fd9\u79cd\u60c5\u51b5\uff0c\u5fc3\u4e2d\u5374\u4ecd\u4e0d\u7981\u9887\u4e3a\u611f\u4f24\uff0c\u957f\u53f9\u4f4e\u8bed\u9053\uff1a \u3000\u3000\u201c\u5509\u2014\u2014\u8fd9\u4e24\u4eba\u5929\u751f\u4fbf\u662f\u5bf9\u5934\uff0c\u6b64\u523b\u679c\u7136\u843d\u5f97\u8fd9\u822c\u4e0b\u573a\uff0c\u4e0d\u77e5\u9053\u6211\u4e0e\u90a3\u8427\u65e0\u8d3c\u5b50\uff0c\u5c06\u6765 \u53c8\u5c06\u600e\u5730\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u4ed6\u81ea\u5fd6\u672c\u8eab\u5b9e\u529b\uff0c\u975e\u4f46\u6ca1\u6709\u5fc5\u80dc\u8427\u65e0\u7684\u628a\u63e1\uff0c\u800c\u4e14\u8fd8\u4f3c\u4e4e\u5c45\u4e8e\u4e0b\u98ce\uff0c\u4f46\u5fc3\u4e2d\u53c8\u4e0d \u60f3\u9976\u8fc7\u8fd9\u7b49\u4e07\u6076\u4e4b\u4eba\uff0c\u4ed6\u4e0e\u8427\u65e0\u672c\u5df2\u6068\u6df1\u4f3c\u6d77\uff0c\u5c31\u7b97\u4ed6\u4e0e\u6b64\u4eba\u7d20\u65e0\u4ec7\u6028\uff0c\u4ed6\u53c8\u600e\u80fd\u754f\u7f29\u4e0d \u524d\uff1f \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u771f\u662f\u60b2\u4eba\u53f9\u5df1\uff0c\u611f\u6168\u4e07\u5343\u3002 \u3000\u3000\u53ea\u542c\u51cc\u7433\u5728\u8eab\u4fa7\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u4e5f\u8fc7\u53bb\u5427\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u5bf9\u9762\u98de\u9601\u4e4b\u4e2d\uff0c\u53c8\u5df2\u629b\u51fa\u4e24\u6761\u5f69\u5e26\u6765\uff0c\u8fd9\u79cd\u8fce\u5bbe\u7684\u65b9\u6cd5\uff0c\u4ed6\u4ee5\u524d \u5df2\u7ecf\u5386\u8fc7\u4e00\u6b21\uff0c\u662f\u4ee5\u4e1d\u6beb\u4e0d\u89c9\u60ca\u5f02\uff0c\u4f46\u5fc3\u5ff5\u52a8\u5904\uff0c\u7a81\u5730\u60f3\u5230\u51cc\u7433\u65b9\u624d\u75b2\u5026\u7684\u6837\u5b50\uff0c\u4e0d\u7981\u4fa7\u9996 \u9053\uff1a \u3000\u3000\u201c\u4f60\u8fc7\u5f97\u53bb\u5417\uff1f\u201d \u3000\u3000\u8bed\u6c14\u4e4b\u4e2d\uff0c\u6ee1\u542b\u5173\u5207\u4e4b\u60c5\uff0c\u51cc\u7433\u4f46\u89c9\u5fc3\u5934\u4e00\u6696\uff0c\u90a3\u91cc\u8fd8\u4f1a\u5c06\u4efb\u4f55\u5371\u9669\u56f0\u96be\u653e\u5728\u5fc3\u4e0a\uff0c\u5a07 \u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u7a81\u5730\u63a0\u8d77\u2014\u2014 \u3000\u3000\u5415\u5357\u4eba\u5fc3\u5934\u4e00\u60ca\uff0c\u5fc3\u5ff5\u52a8\u5904\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u522b\u7684\uff0c\u8eab\u5f62\u4ea6\u81ea\u63a0\u8d77\u3002 \u3000\u3000\u4ed6\u53ea\u5168\u529b\u4e00\u63a0\uff0c\u5f53\u771f\u662f\u5feb\u5982\u79bb\u5f26\u4e4b\u7bad\uff0c\u8033\u4e2d\u53ea\u542c\u5f97\u5bf9\u5cb8\u5b59\u654f\u60ca\u547c\u4e4b\u58f0\uff0c\u4ed6\u5df2\u4e00\u624b\u6284\u7740\u51cc \u7433\u7684\u7ea4\u8170\uff0c\u4e00\u624b\u6284\u8d77\u90a3\u6761\u5f69\u5e26\uff0c\u4f46\u89c9\u5f69\u5e26\u4e00\u6c64\uff0c\u4ed6\u8eab\u5f71\u5df2\u662f\u63a0\u5165\u98de\u4ead\uff0c\u8f69\u76ee\u671b\u53bb\uff0c\u5bf9\u5cb8\u9065\u9677 \u6570\u4e08\uff0c\u4e0b\u4e34\u65e0\u5e95\u7edd\u58d1\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u65b9\u624d\u90a3\u91cc\u6709\u8fd9\u7b49\u52c7\u6c14\uff0c\u505a\u51fa\u8fd9\u7b49\u5371\u9669\u4e4b\u4e8b\uff0c\u8981\u77e5\u9053 \u8fd9\u79cd\u98de\u6e21\u7684\u65b9\u6cd5\uff0c\u5168\u51ed\u4e00\u70b9\u5de7\u52b2\uff0c\u4e00\u4eba\u5df2\u662f\u4e0d\u6613\uff0c\u4e24\u4eba\u81ea\u7136\u66f4\u96be\uff0c\u4e00\u4e2a\u4e0d\u5999\uff0c\u90a3\u91cc\u8fd8\u6709\u547d \u5728\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4ecd\u89c9\u5fc3\u5934\u7830\u7136\u8df3\u52a8\uff0c\u6084\u7136\u5408\u8d77\u773c\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u53ea\u89c9\u51cc\u7433\u8fd8\u6b63\u4f0f\u5728\u4ed6\u7684\u6000\u4e2d\uff0c\u4e0d\u4f4f \u5598\u606f\uff0c\u4e00\u53cc\u7ea4\u624b\uff0c\u7adf\u7d27\u7d27\u56f4\u7740\u81ea\u5df1\u7684\u80a9\u5934\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u8361\uff0c\u5f20\u5f00\u773c\u6765\uff0c\u5374\u7adf\u6b63\u89e6\u7740\u4e07\u8679\u7684\u4e00\u53cc \u773c\u775b\uff0c\u53ea\u89c1\u5979\u79cb\u6ce2\u4e4b\u4e2d\uff0c\u4f3c\u6028\u4f3c\u6068\uff0c\u4f3c\u60b2\u4f3c\u82e6\uff0c\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u5b59\u654f\u5fc3\u6b63\u5728\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u81ea \u5df1\u3002 \u3000\u3000\u51cc\u7433\u6b64\u523b\u4e5f\u6b63\u662f\u60ca\u9b42\u521d\u5b9a\uff0c\u4f46\u5979\u4f0f\u5728\u8fd9\u5bbd\u9614\u800c\u575a\u5b9e\u7684\u80f8\u819b\u4e0a\uff0c\u5fc3\u4e2d\u5374\u6709\u8bf4\u4e0d\u51fa\u7684\u751c\u871c\u548c \u5b89\u6170\u3002 \u3000\u3000\u5979\u8ff7\u8499\u5730\u5408\u7740\u773c\u775b\uff0c\u51e0\u4e4e\u518d\u4e5f\u4e0d\u613f\u7741\u5f00\uff0c\u5979\u7d27\u7d27\u62b1\u7740\u4ed6\u7684\u80a9\u5934\uff0c\u51e0\u4e4e\u518d\u4e5f\u4e0d\u613f\u653e\u624b\u3002 \u3000\u3000\u4f46\u5415\u5357\u4eba\u6b64\u523b\u5df2\u8f7b\u8f7b\u62cd\u7740\u5979\u7684\u9999\u80a9\uff0c\u67d4\u58f0\u4f4e\u8bed\u9053\uff1a \u3000\u3000\u201c\u51cc\u513f\uff0c\u5230\u4e86\u3002\u201d \u3000\u3000\u51cc\u7433\u7f13\u7f13\u62ac\u8d77\u5934\u6765\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u7ea2\u751f\u53cc\u988a\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u8f6c\u8eab\u6251\u8fdb\u5979\u5988\u5988\u6000\u91cc\uff0c\u5b59\u654f\u7684 \u76ee\u5149\uff0c\u6148\u7231\u5730\u843d\u5230\u5979\u5982\u4e91\u7684\u67d4\u53d1\u4e0a\uff0c\u5fc3\u91cc\u987f\u89c9\u653e\u4e0b\u4e86\u4e00\u6837\u5fc3\u4e8b\uff0c\u4f46\u5374\u53c8\u4f3c\u4e4e\u89c9\u5f97\uff0c\u50cf\u662f\u5931\u843d \u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u5415\u5357\u4eba\u65e2\u4e0d\u6562\u63a5\u89e6\u5230\u5b59\u654f\u7684\u76ee\u5149\uff0c\u66f4\u4e0d\u6562\u89c1\u5230\u4e07\u8679\u7684\u76ee\u5149\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u6c88\u58f0\u9053\uff1a \u3000\u3000\u201c\u8bb8\u2026\u2026\u4e07\u4e24\u4f4d\u8001\u524d\u8f88\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u201c\u8bb8\u201d\u5b57\u5df2\u81ea\u51fa\u53e3\uff0c\u624d\u60f3\u5230\u5728\u8fd9\u51c4\u82e6\u75db\u54ed\u7740\u7684\u4e07\u6c0f\u6bcd\u5973\u524d\u9762\uff0c\u53c8\u600e\u5fcd\u51fa\u95ee\u8d77\u201c\u5999\u624b\u201d \u8bb8\u767d\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4e07\u592b\u4eba\u832b\u7136\u6447\u4e86\u6447\u5934\uff0c\u53c8\u81ea\u653e\u58f0\u75db\u54ed\u8d77\u6765\uff0c\u4e07\u8679\u5931\u9b42\u843d\u9b44\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u66f4\u50cf\u662f\u4ec0\u4e48 \u8bdd\u4e5f\u6ca1\u6709\u542c\u5230\u3002 \u3000\u3000\u4ed6\u5e72\u54b3\u4e00\u58f0\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u671b\u5411\u5b59\u654f\uff01 \u3000\u3000\u201c\u90a3\u4e24\u4f4d\u524d\u8f88\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u5b59\u654f\u957f\u53f9\u4e00\u58f0\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u5374\u542c\u51cc\u7433\u5df2\u81ea\u5728\u5979\u6000\u4e2d\u4fcf\u8bed\u9053\uff1a \u3000\u3000\u201c\u5988\uff0c\u4eba\u5bb6\u5728\u95ee\u4f60\u8bdd\u5462\u3002\u201d \u3000\u3000\u5b59\u654f\u76ee\u5149\u518d\u6b21\u6e29\u67d4\u5730\u843d\u5728\u5979\u7231\u5973\u8eab\u4e0a\uff01\u5fc3\u4e2d\u771f\u662f\u60b2\u559c\u4ea4\u96c6\u3002 \u3000\u3000\u5979\u89c1\u5230\u4e86\u8fd9\u79cd\u60c5\u5f62\uff0c\u81ea\u7136\u77e5\u9053\u5979\u7231\u5973\u5df2\u5bf9\u5415\u5357\u4eba\u6709\u4e86\u6781\u6df1\u7684\u60c5\u611f\uff0c\u8fd9\u5979\u975e\u4f46\u4e0d\u53cd\u5bf9\uff0c\u800c \u4e14\u8fd8\u9ad8\u5174\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u9762\u524d\u8fd9\u5e74\u8f7b\u4eba\uff0c\u662f\u53ef\u4ee5\u4ed8\u6258\u7ec8\u8eab\u7684\uff0c\u4f46\u5979\u53c8\u6015\u8fd9\u4ec5\u662f\u5979\u7231\u5973\u7684\u7247\u9762\u76f8 \u601d\uff0c\u5979\u6df1\u77e5\u7433\u513f\u7684\u813e\u6c14\uff0c\u5982\u679c\u771f\u662f\u8fd9\u6837\uff0c\u5b9a\u5fc5\u9020\u6210\u60b2\u5267\u3002 \u3000\u3000\u5979\u53c8\u6115\u4e86\u4e00\u4f1a\uff0c\u65b9\u53f9\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u65e9\u6765\u4e00\u6b65\uff0c\u5509\u2014\u2014\u771f\u60f3\u4e0d\u5230\u4e16\u4e0a\u4f1a\u6709\u8fd9\u79cd\u51a4\u5bb6\u3002\u201d \u3000\u3000\u5979\u60b2\u54c0\u5730\u53f9\u606f\u4e86\u6570\u58f0\uff0c\u65b9\u9053\uff1a \u3000\u3000\u201c\u521a\u521a\u4f60\u4eec\u8d70\u4e86\uff0c\u6211\u672c\u6765\u4e5f\u60f3\u8ddf\u53bb\u7684\uff0c\u90a3\u77e5\u6211\u521a\u4e00\u8f6c\u8eab\uff0c\u90a3\u8fb9\u6211\u59d0\u2014\u2014\u4e07\u5927\u54e5\u5df2\u8d70\u4e86\u51fa \u6765\uff0c\u4ed6\u770b\u5230\u6211\uff0c\u50cf\u662f\u4e00\u6115\uff0c\u6211\u5927\u59d0\u4e5f\u51fa\u6765\u4e86\uff0c\u770b\u89c1\u6211\uff0c\u7acb\u523b\u5c31\u547c\u51fa\u58f0\u6765\uff0c\u6211\u548c\u5927\u59d0\u5df2\u6709\u8bb8\u591a \u5e74\u4e0d\u89c1\u4e86\uff0c\u4e0a\u6b21\u6211\u6765\u7684\u65f6\u5019\uff0c\u5317\u4fee\u2014\u2014\u201d \u3000\u3000\u5979\u773c\u7736\u4e00\u7ea2\uff0c\u4f38\u624b\u5fae\u62ed\uff0c\u65b9\u81ea\u63a5\u9053\uff1a \u3000\u3000\u201c\u5509\u2014\u2014\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u90a3\u59d3\u8bb8\u7684\u53c8\u5927\u9a82\u4e86\u8d77\u6765\uff0c\u6211\u770b\u89c1\u4e07\u5927\u54e5\u7684\u9762\u8272\uff0c\u94c1\u9752\u5f97\u6015\u4eba\uff0c\u5927 \u59d0\u4e0d\u4f4f\u5730\u8bf4\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u6709\u4ec0\u4e48\u51a4\u4ec7\u62da\u4e86\u8fd9\u4e48\u591a\u5e74\u547d\u8fd8\u4e0d\u591f\u5417\uff1f\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u62fc\u4e2a\u4f60\u6b7b\u6211 \u6d3b\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u4e07\u5927\u54e5\u5374\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u5927\u59d0\u7684\u8bdd\uff0c\u6211\u770b\u5230\u59d3\u8bb8\u7684\u548c\u4e07\u5927\u54e5\u4f60\u77aa\u7740\u6211\uff0c\u6211\u77aa\u7740 \u4f60\uff0c\u76f4\u50cf\u662f\u6709\u6740\u7236\u6df1\u4ec7\u7684\uff0c\u5c31\u4e5f\u529d\u9053\uff1a \u3000\u3000\u201c\u8bb8\u5927\u4fa0\uff0c\u4e16\u4e0a\u6ca1\u6709\u89e3\u4e0d\u5f00\u7684\u51a4\u4ec7\uff0c\u4f60\u53c8\u4f55\u82e6\u5982\u6b64\uff0c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981 \u8fd9\u6837\u770b\u4e0d\u5f00\u5462\uff0c\u800c\u4e14\u4e24\u864e\u76f8\u4e89\uff0c\u5fc5\u6709\u4e00\u4f24\u5440\u3002\u201d \u3000\u3000\u4f46\u662f\u2014\u2014\uff0c\u5509\uff0c\u8fd9\u59d3\u8bb8\u7684\u773c\u775b\u77aa\u5f97\u5c31\u50cf\u94dc\u94c3\u4e00\u6837\uff0c\u7adf\u4e5f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u6211\u8bf4\u7684\u8bdd\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u6697\u53f9\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u770b\u6765\u81ea\u4ece\u65e0\u91cf\u5c71\u5dc5\u4e4b\u4e8b\u53d1\u751f\u4e4b\u540e\uff0c\u8fd9\u4e24\u4eba\u7684\u4ec7\u6068\u679c\u771f\u8d8a\u7ed3\u8d8a\u6df1\u4e86\u3002\u201d\u4ed6\u7a81\u7136\u60f3\u8d77\u201c\u5999 \u624b\u201d\u8bb8\u767d\u5927\u559d\u4e00\u58f0\uff1a\u201c\u8fd8\u6211\u8840\u6765\uff01\u201d\u7684\u6837\u5b50\uff0c\u5fcd\u4e0d\u4f4f\u5fc3\u5934\u4e00\u51db\uff0c\u53ea\u542c\u5b59\u654f\u6c89\u91cd\u5730\u53f9\u606f\u7740\u63a5 \u9053\uff1a \u3000\u3000\u201c\u4e8e\u662f\u6211\u5411\u5bf9\u9762\u7684\u4e07\u5927\u54e5\u9ad8\u547c\uff01\u201c\u4e07\u5927\u54e5\uff01\u4f60\u96be\u9053\u4e0d\u66ff\u5927\u59d0\u548c\u4f84\u5973\u513f\u60f3\u60f3\u5417\uff0c\u4f60\u8fd9\u6837\u2014 \u2014\u201d\u90a3\u77e5\u6211\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4e07\u5927\u54e5\u7a81\u5730\u4e00\u62ac\u624b\uff0c\u629b\u51fa\u4e00\u6761\u5f69\u5e26\u6765\uff0c\u90a3\u59d3\u8bb8\u7684\u54c8\u54c8\u5927\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u8001\u7334\u5b50\uff0c\u679c\u7136\u8fd8\u6709\u79cd\u3002\u201d\u7b11\u58f0\u672a\u4e86\uff0c\u4ed6\u4eba\u5df2\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u4e00\u53f9\uff0c\u5fc3\u91cc\u50cf\u662f\u5728\u6697\u6697\u8d5e\u4f69\u7740\u8fd9\u201c\u59d3\u8bb8\u7684\u201d\u6b66\u529f\uff0c\u4f46\u5979\u53e3\u4e2d\u81ea\u7136\u4e0d\u4f1a\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5979\u53ea\u662f\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u53ea\u5f53\u90a3\u59d3\u8bb8\u7684\u4e00\u8fc7\u53bb\u5c31\u8981\u52a8\u624b\uff0c\u90a3\u77e5\u4ed6\u63a0\u8fc7\u53bb\u540e\uff0c\u5374\u5148\u5411\u5df2\u5fcd\u4e0d\u4f4f\u75db\u54ed\u8d77\u6765\u7684\u5927\u59d0 \u5f53\u5934\u4e00\u63d6\uff0c\u8bf4\u4ec0\u4e48\u4ed6\u548c\u4e07\u5927\u54e5\u5b9e\u5728\u6709\u4e0d\u80fd\u89e3\u7684\u51a4\u4ec7\uff0c\u4eca\u65e5\u65e0\u8bba\u662f\u8c01\u6740\u4e86\u8c01\uff0c\u4ed6\u5bf9\u5927\u59d0\u90fd\u5f88\u62b1 \u6b49\uff0c\u201c\u56e0\u4e3a\u201d\u4ed6\u8bf4\uff1a\u201c\u56e0\u4e3a\u8ba9\u4e00\u4e2a\u6ca1\u6709\u72af\u8fc7\u4ec0\u4e48\u9519\u8bef\u7684\u4eba\u53d7\u7f6a\uff0c\u7684\u786e\u4e0d\u5bf9\uff0c\u4f46\u8fd9\u53ea\u80fd\u602a\u59d3\u4e07 \u7684\uff0c\u4e0d\u80fd\u602a\u6211\u8bb8\u767d\u3002\u201d\u5927\u59d0\u5c31\u95ee\u4ed6\u662f\u4ec0\u4e48\u4ec7\u6068\uff0c\u8fd9\u4e48\u6df1\uff0c\u4ed6\u770b\u4e86\u770b\u8679\u513f\uff0c\u53c8\u770b\u4e86\u770b\u5927\u59d0\uff0c\u6447 \u6447\u5934\uff0c\u72c2\u7b11\u8d77\u6765\uff0c\u5374\u6ca1\u6709\u8bf4\u51fa\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u201c\u5999\u624b\u201d\u8bb8\u767d\u5012\u771f\u662f\u4e2a\u5802\u5802\u6c49\u5b50\uff0c\u4e0d\u613f\u5c06\u8fd9\u79cd\u4e8b\u5728\u4eba\u5bb6\u59bb\u5973\u7684\u9762\u524d\u8bf4\u51fa\uff0c\u5509\u4ed6\u867d\u6709 \u67d4\u80a0\u50b2\u9aa8\uff0c\u4f46\u5374\u5c11\u4e86\u51e0\u5206\u4ec1\u5fc3\uff0c\u662f\u4ee5\u7ec8\u7a76\u4f1a\u843d\u5f97\u5982\u6b64\u4e0b\u573a\u201d\u4ed6\u5fc3\u5ff5\u81f3\u6b64\uff0c\u53e3\u4e2d\u7adf\u8131\u53e3\u4f4e\u8bed \u9053\uff1a \u3000\u3000\u201c\u5509\u2014\u2014\u4ed6\u4eec\u7684\u786e\u6709\u7740\u4e9b\u4e0d\u53ef\u89e3\u7684\u4ec7\u6068\u2014\u2014\u201d \u3000\u3000\u5b59\u654f\u4e00\u6115\uff0c\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5415\u5357\u4eba\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u4eba\u4eba\u90fd\u5728\u671b\u7740\u81ea\u5df1\uff0c\u4ed6\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\u7cca\u6d82\uff0c\u600e\u5730\u5c06\u8fd9\u7b49\u4e8b\u6f0f\u53e3\u51fa \u6765\uff0c\u6c88\u541f\u534a\u664c\uff0c\u6447\u5934\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u8fd9\u4e0d\u8fc7\u662f\u662f\u731c\u60f3\u800c\u5df2\u2014\u2014\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5de7\u5999\u5730\u7528\u201c\u540e\u6765\u5462\u201d\u4e09\u4e2a\u5b57\uff0c\u5c06\u8bdd\u9898\u8f6c\u5f00\u3002 \u3000\u3000\u5b59\u654f\u4fbf\u53c8\u63a5\u9053\uff1a \u3000\u3000\u201c\u4e07\u5927\u54e5\u9762\u8272\u94c1\u9752\uff0c\u4e00\u58f0\u4e0d\u54cd\u5730\u671b\u7740\u4ed6\uff0c\u53ea\u5230\u4ed6\u8bf4\u8bdd\u5b8c\u4e86\uff0c\u4e07\u5927\u54e5\u624d\u8bf4\uff1a\u201c\u4f60\u4e0d\u5fc5\u5e9f \u8bdd\uff0c\u6211\u65e2\u7136\u5c06\u4f60\u63a5\u8fc7\uff0c\u81ea\u7136\u8981\u4e00\u62fc\u751f\u6b7b\u3002\u201d\u90a3\u59d3\u8bb8\u7684\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u53ea\u662f\u6211\u4e24\u4eba\u8981\u5206\u51fa\u80dc \u8d1f\uff0c\u8fd8\u4e0d\u5927\u5bb9\u6613\uff0c\u8001\u7334\u2014\u2014\u201d\u201d \u3000\u3000\u5979\u7a81\u7136\u60f3\u8d77\u81ea\u5df1\u600e\u4e48\u80fd\u5c06\u4eba\u5bb6\u9a82\u81ea\u5df1\u59d0\u592b\u7684\u8bdd\uff0c\u539f\u5c01\u4e0d\u52a8\u5730\u8bf4\u51fa\u6765\uff0c\u8bed\u58f0\u500f\u7136\u4e00\u987f\uff0c\u51cc \u7433\u542c\u5f97\u6b63\u662f\u51fa\u795e\uff0c\u89c1\u5979\u7a81\u7136\u505c\u4f4f\uff0c\u4ef0\u9996\u9053\uff1a \u201c\u4ed6\u4eec\u540e\u6765\u5012\u5e95\u662f\u600e\u6837\u62da\u6597\u7684\uff0c\u96be\u9053\u4ed6\u4eec\u4e00\u9f50\u8df3\u4e0b\u53bb\u4e86\u4e48\uff1f\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u516d\u7ae0\u3000\u9b42\u60ca\u7edd\u58d1>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u516d\u7ae0\u3000\u9b42\u60ca\u7edd\u58d1 \u3000\u3000\u5415\u5357\u4eba\u542c\u4e86\uff0c\u4e0d\u7981\u6697\u53f9\u8fd9\u5c11\u5973\u7684\u5929\u771f\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u4fdd\u62a4\u8fd9\u5929\u771f\u7684\u5c11\u5973\u7684\u8d23\u4efb\uff0c\u6b64\u523b\u5df2\u843d \u5230\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u5b59\u654f\u8d23\u5907\u5730\u62cd\u4e86\u62cd\u5979\u7231\u5973\u7684\u80a9\u5934\uff0c\u4e5f\u5728\u6697\u602a\u5979\u7231\u5973\u8bf4\u8bdd\u7684\u83bd\u649e\uff0c\u800c\u4e07\u8679\u7684\u4e00\u5bf9\u76ee \u5149\uff0c\u5374\u4e5f\u6b63\u51b0\u51b7\u5730\u671b\u5728\u51cc\u7433\u8eab\u4e0a\u3002 \u3000\u3000\u5415\u5357\u4eba\u76ee\u5149\u52a8\u5904\uff0c\u7a81\u5730\u5fc3\u5934\u4e00\u52a8\uff0c\u53ea\u89c9\u5f97\u8fd9\u4e07\u8679\u7684\u76ee\u5149\uff0c\u6b64\u523b\u7adf\u548c\u5979\u7239\u7239\u4e00\u6a21\u4e00\u6837\uff0c\u4e00 \u65f6\u4e4b\u95f4\uff0c\u94c1\u9762\u5b64\u884c\u5ba2\uff0c\u90a3\u51b0\u51b7\u7684\u9762\u5bb9\uff0c\u72e0\u6bd2\u7684\u795e\u8272\uff0c\u4f3c\u4e4e\u53c8\u4ece\u4ed6\u5fc3\u4e2d\u95ea\u8fc7\uff0c\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u6697\u4e2d \u4e00\u51db\uff0c\u5bf9\u8fd9\u5c11\u5973\uff0c\u7adf\u4e0d\u77e5\u4e0d\u89c9\u8d77\u4e86\u4e09\u5206\u754f\u60e7\u9632\u8303\u4e4b\u5fc3\uff0c\u56e0\u4e3a\u4ed6\u6df1\u523b\u7684\u4e86\u89e3\uff0c\u8fd9\u79cd\u76ee\u5149\u7684\u542b\u610f \u662f\u8868\u793a\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u5374\u542c\u5b59\u654f\u8bed\u58f0\u5fae\u987f\u540e\uff0c\u53c8\u81ea\u63a5\u9053\uff1a \u3000\u3000\u201c\u90a3\u59d3\u8bb8\u7684\u8fd8\u8bf4\u201c\u6211\u4e24\u4eba\u6570\u5341\u5e74\u6765\uff0c\u867d\u7136\u603b\u60f3\u4e00\u51b3\u751f\u6b7b\uff0c\u4f46\u603b\u662f\u534a\u9014\u800c\u5e9f\uff0c\u4eca\u65e5\u6211\u770b\u5012 \u4e0d\u5982\u5927\u5bb6\u90fd\u7ad9\u7740\u4e0d\u52a8\uff0c\u5404\u5404\u8ba9\u5bf9\u65b9\u6253\u4e09\u62f3\uff0c\u90a3\u4e48\u2014\u2014\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4e07\u5927\u54e5\u5c31\u51b7\u51b7\u95ee\u4ed6\uff1a \u201c\u8c01\u5148\u6253\u3002\u201d\u4ed6\u6115\u4e86\u4e00\u6115\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u529f\u529b\u76f8\u5f53\uff0c\u65e0\u8bba\u8c01\u5148\u52a8\u624b\uff0c\u5bf9\u65b9\u90fd\u65e0\u6cd5\u62db\u67b6\u5f97\u4f4f\uff0c\u82e5\u662f\u8ba9\u4e07\u5929\u840d\u5148\u51fb\u4e00\u638c\uff0c\u8bb8 \u767d\u7eb5\u7136\u4e0d\u6b7b\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u51fa\u624b\u8fd8\u51fb\u4e86\u3002\u5509\u2014\u2014\u4e07\u5929\u840d\u8fd9\u4e00\u95ee\uff0c\u5f53\u771f\u662f\u95ee\u5f97\u53eb\u4eba\u65e0\u8a00\u53ef\u5bf9\uff1f\u201d \u3000\u3000\u5b59\u654f\u53c8\u81ea\u63a5\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4e24\u4eba\u5bf9\u671b\u4e86\u534a\u664c\uff0c\u6211\u89c1\u5230\u4e07\u5927\u54e5\u9762\u4e0a\u7684\u795e\u60c5\u8d8a\u53d1\u96be\u770b\uff0c\u5fc3\u91cc\u771f\u5bb3\u6015\u6781\u4e86\uff0c\u5fcd\u4e0d\u4f4f \u53c8\u529d\u4ed6\u4eec\uff0c\u90a3\u77e5\u9053\u4e07\u5927\u54e5\u7a81\u5730\u4e00\u63a0\u56de\u8eab\uff0c\u8dd1\u5230\u540e\u9762\u53bb\uff0c\u59d3\u8bb8\u7684\u5f20\u53e3\u50cf\u662f\u53c8\u60f3\u559d\u9a82\uff0c\u4f46\u5374\u53c8\u5fcd \u4f4f\u3002 \u3000\u3000\u201c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4e07\u5927\u54e5\u679c\u7136\u5df2\u8dd1\u4e86\u56de\u6765\uff0c\u53cc\u624b\u6367\u7740\u4e00\u5927\u6346\u7c97\u7d22\uff0c\u51e0\u4e4e\u628a\u4ed6\u6574\u4e2a\u4eba\u90fd\u6321\u4f4f \u4e86\uff0c\u6211\u4e00\u751f\u4e4b\u4e2d\u90fd\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e48\u591a\u7ef3\u5b50\uff0c\u59d3\u8bb8\u7684\u4e5f\u5f88\u5947\u602a\uff0c\u5c31\u95ee\u9053\uff1a\u201c\u8001\u2026\u2026\uff01\u4f60\u5e72\u4ec0 \u4e48\uff1f\u201d\u4e07\u5927\u54e5\u4e00\u8a00\u4e0d\u53d1\uff0c\u5c06\u90a3\u5806\u7ef3\u5b50\u201c\u7830\u201d\u5730\u653e\u5728\u5730\u4e0a\uff0c\u7a81\u5730\u4ece\u6000\u4e2d\u62ff\u4e86\u4e00\u5757\u9ed1\u94c1\u51fa\u6765\uff0c\u5728 \u59d3\u8bb8\u7684\u9762\u524d\u4e00\u626c\u2014\u2014\u201d \u3000\u3000\u5415\u5357\u4eba\u5fc3\u5934\u4e00\u9707\uff0c\u5fcd\u4e0d\u4f4f\u8131\u53e3\u9053\uff1a \u3000\u3000\u201c\u7487\u5149\u5b9d\u4eea\uff01\u201d \u3000\u3000\u5b59\u654f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u600e\u4e5f\u77e5\u9053\uff01\u201d \u3000\u3000\u5415\u5357\u4eba\u4e0d\u7981\u53c8\u65e0\u6cd5\u56de\u7b54\uff0c\u51cc\u7433\u79cb\u6ce2\u4e00\u8f6c\uff0c\u5077\u5077\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5988\uff0c\u4f60\u8bf4\u4e0b\u53bb \u561b\u3002\u201d \u3000\u3000\u5b59\u654f\u4fef\u9996\u6c88\u541f\u534a\u664c\uff0c\u8f7b\u8f7b\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u62ac\u5904\uff0c\u671b\u5411\u5415\u5357\u4eba\uff0c\u4f46\u7ec8\u4e8e\u53c8\u81ea\u7f13\u7f13\u63a5\u7740\u8bf4 \u7740\uff1a \u3000\u3000\u201c\u90a3\u679c\u7136\u662f\u201c\u7487\u5149\u5b9d\u4eea\u201d\uff0c\u59d3\u8bb8\u7684\u89c1\u4e86\uff0c\u4e5f\u8131\u53e3\u5927\u547c\u8d77\u6765\uff0c\u4e07\u5927\u54e5\u8138\u4e0a\u5374\u6ca1\u6709\u4e00\u4e1d\u4e00\u6beb \u8868\u60c5\uff0c\u7a81\u5730\u4f38\u624b\u4e00\u629b\uff0c\u7adf\u5c06\u8fd9\u5e0c\u4e16\u5947\u73cd\u629b\u5230\u8fd9\u6df1\u4e0d\u89c1\u5e95\u7684\u7edd\u58d1\u91cc\u53bb\u4e86\uff0c\u59d3\u8bb8\u7684\u53c8\u5927\u5403\u4e86\u4e00 \u60ca\uff0c\u8fd8\u4ee5\u4e3a\u4e07\u5927\u54e5\u75af\u4e86\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\u3002\u201d\u8dd1\u5230\u680f\u6746\u8fb9\uff0c\u4fef\u9996\u4e0b\u671b\uff0c\u90a3\u201c\u7487\u5149\u5b9d \u4eea\u201d\u4e00\u843d\u5343\u4e08\uff0c\u90a3\u91cc\u8fd8\u6709\u5f71\u5b50\uff0c\u800c\u4e14\u4ed6\u4fef\u9996\u671b\u4e86\u8bb8\u4e45\uff0c\u7adf\u8fde\u4e00\u70b9\u843d\u5730\u7684\u58f0\u97f3\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u5979\u53f9\u606f\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fc7\u4e86\u534a\u664c\uff0c\u4e07\u5927\u54e5\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u6211\u4e24\u4eba\uff0c\u5230\u8fd9\u4e0b\u9762\u53bb\uff0c\u8c01\u627e\u7740\u6b64\u7269\uff0c\u4fbf\u662f\u8c01\u80dc\uff0c\u4e07 \u5927\u54e5\u8bf4\u8bdd\u603b\u662f\u8fd9\u4e48\u7b80\u77ed\uff0c\u4f46\u6211\u4eec\u542c\u4e86\uff0c\u5374\u4e0d\u7981\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u90a3\u59d3\u8bb8\u7684\u4e5f\u50cf\u662f\u4e3a\u4e4b\u4e00\u60ca\uff0c\u4f46\u7acb \u523b\u53c8\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u597d\u529e\u6cd5\uff0c\u6211\u4eec\u8fd9\u4e00\u6b21\uff0c\u5927\u7ea6\u603b\u6709\u4e00\u4eba\u4f1a\u6b7b\u4e86\u3002\u201d\u4e07\u5927\u54e5\u5374\u51b7 \u51b7\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4f60\u6211\u4e24\u4eba\uff0c\u8c01\u4e5f\u4e0d\u7528\u60f3\u6d3b\u7740\u56de\u6765\u4e86\u3002\u201d\u201d \u3000\u3000\u5415\u5357\u4eba\u4e0d\u7981\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u51cc\u7433\u5374\u5a07\u5524\u4e00\u58f0\uff0c\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u8fd9\u53c8\u4f55\u82e6\uff01\u201d \u3000\u3000\u5b59\u654f\u53f9\u9053\uff1a \u3000\u3000\u201c\u90a3\u65f6\u5019\u6211\u4eec\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u90fd\u5413\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5927\u59d0\u66f4\u662f\u54ed\u5f97\u4f24\u5fc3\uff0c\u90a3\u59d3\u8bb8\u7684\u5c31\u8bf4\uff1a \u201c\u7ef3\u5b50\u5012\u4e0d\u5c11\u3002\u201d\u4e07\u5927\u54e5\u8bf4\uff01\u201c\u4e00\u4eba\u4e00\u534a\uff0c\u63f4\u7ef3\u4e0b\u53bb\u3002\u201d\u62ff\u8d77\u7ef3\u5b50\uff0c\u5206\u6210\u4e24\u534a\uff0c\u9053\uff1a\u201c\u4f60\u5148 \u6311\u3002\u201d\u59d3\u8bb8\u7684\u770b\u4e5f\u4e0d\u770b\uff0c\u5c31\u62ff\u4e86\u4e00\u6bb5\uff0c\u9053\uff1a\u201c\u957f\u5012\u5f88\u957f\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u5230\u5e95\u3002\u201d\u4e07\u5927\u54e5 \u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u8bf4\u4e0d\u5b9a\u79bb\u5e95\u8fd8\u6709\u5343\u767e\u4e08\u3002\u201d\u90a3\u59d3\u8bb8\u7684\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5982\u662f\u8fd9\u6837\uff0c\u90a3 \u4f60\u6211\u4e24\u4eba\uff0c\u5f53\u771f\u662f\u8c01\u4e5f\u4e0d\u8981\u60f3\u6d3b\u7740\u56de\u6765\u4e86\u3002\u201d\u201d \u3000\u3000\u51cc\u7433\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u53f9\u9053\uff1a \u3000\u3000\u201c\u771f\u5947\u602a\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u90fd\u4e0d\u6015\u6b7b\u3002\u201d \u3000\u3000\u5979\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5c1a\u4e0d\u77e5\u9053\u4eba\u4eec\u4e3a\u4e86\u8bb8\u591a\u79cd\u539f\u56e0\uff0c\u90fd\u4f1a\u5c06\u751f\u6b7b\u4e4b\u4e8b\u770b\u5f88\u6de1\u2014\u2014\u90a3\u5c31\u662f\u6df1\u5207\u7684 \u7231\u548c\u6068\uff0c\u4ec1\u548c\u4e49\uff0c\u4ee5\u53ca\u4e89\u53d6\u81ea\u7531\u7684\u529b\u91cf\u3002 \u3000\u3000\u5b59\u654f\u76ee\u5149\u4e00\u8f6c\uff0c\u50cf\u662f\u60f3\u8d23\u5907\u5979\u7231\u5973\u7684\u63d2\u53e3\uff0c\u4f46\u5374\u53c8\u8f7b\u53f9\u4e00\u58f0\uff0c\u4ecd\u7136\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u90a3\u65f6\u5019\u59d3\u8bb8\u7684\u72c2\u7b11\u4e4b\u58f0\uff0c\u548c\u5927\u59d0\u7684\u75db\u54ed\u4e4b\u58f0\uff0c\u4fbf\u6211\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u90a3\u59d3\u8bb8 \u7684\u5374\u5927\u7b11\u9053\uff1a\u201c\u8981\u8d70\u73b0\u5728\u5c31\u8d70\u3002\u201d\u4e07\u5927\u54e5\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u4e24\u4eba\u4e00\u9f50\u5c06\u7ef3\u5934\u629b\u4e86\u4e0b\u53bb\uff0c\u5c06\u53e6\u4e00 \u5934\u7262\u7262\u7ed3\u4f4f\u5728\u4e00\u68f5\u5927\u6811\u4e0a\uff0c\u90a3\u65f6\u5927\u59d0\u548c\u8679\u513f\u5fcd\u4e0d\u4f4f\u8df3\u8d77\u6765\uff0c\u62b1\u7740\u5927\u54e5\uff0c\u5927\u54e5\u5fc3\u91cc\u60f3\u5fc5\u4e5f\u96be\u53d7 \u5f97\u5f88\uff0c\u4f46\u5374\u51b7\u51b7\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u4e00\u5b9a\u4f1a\u6b7b\uff0c\u54ed\u4ec0\u4e48\u3002\u201d\u4e00\u628a\u5c06\u5927\u59d0\u63a8\u5f00\uff0c\u5927\u59d0\u7adf\u88ab\u4ed6\u63a8\u5012\u5728\u5730 \u4e0a\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u82e6\u53f9\u4e00\u58f0\uff0c\u5fcd\u4e0d\u4f4f\u529d\u6170\u9053\uff1a \u3000\u3000\u201c\u8fd9\u7edd\u58d1\u867d\u6df1\uff0c\u4f46\u6709\u8fd9\u4e48\u957f\u7684\u7ef3\u7d22\u53ef\u4ee5\u6500\u63f4\uff0c\u518d\u52a0\u4e0a\u4ed6\u4e24\u4eba\u6709\u5982\u6b64\u6b66\u529f\uff0c\u4f9d\u5c0f\u9119\u4e4b\u89c1\uff0c \u8bf4\u4e0d\u5b9a\u4ed6\u4e24\u4eba\u6b64\u756a\u90fd\u80fd\u751f\u8fd8\u4e5f\u672a\u53ef\u77e5\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u867d\u5927\u534a\u662f\u529d\u6170\u4e4b\u8a00\uff0c\u5176\u5b9e\u5374\u4e5f\u6709\u51e0\u5206\u9053\u7406\u3002 \u3000\u3000\u4f46\u5b59\u654f\u5374\u957f\u53f9\u9053\uff1a \u3000\u3000\u201c\u6309\u7167\u5e38\u7406\u6765\u8bf4\uff0c\u8fd9\u5f53\u7136\u53ef\u80fd\uff0c\u4f46\u4ed6\u4eec\u4e24\u4eba\u5982\u6b64\u4ec7\u6068\uff0c\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u5f53\u7136\u4f1a\u5f7c\u6b64\u5404\u4e0b\u6bd2 \u624b\uff0c\u53c8\u600e\u4f1a\u8ba9\u5bf9\u65b9\u5b89\u7a33\u5730\u63f4\u7ef3\u800c\u4e0b\u5462\uff1f\u201d \u3000\u3000\u5415\u5357\u4eba\u957f\u53f9\u7740\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u5b59\u654f\u53c8\u9053\uff1a \u3000\u3000\u201c\u5728\u90a3\u79cd\u60c5\u51b5\u4e0b\uff0c\u8c01\u4e5f\u65e0\u6cd5\u963b\u6b62\u4ed6\u4eec\uff0c\u59d3\u8bb8\u7684\u8d70\u5230\u680f\u6746\u8fb9\uff0c\u5ffd\u7136\u53c8\u9000\u4e86\u56de\u6765\uff0c\u8fde\u58f0\u9053\uff1a \u201c\u4e0d\u884c\uff0c\u4e0d\u884c\u3002\u201d\u6211\u5fc3\u91cc\u4e00\u559c\uff0c\u8fd8\u5f53\u4ed6\u4e0d\u613f\u548c\u4e07\u5927\u54e5\u771f\u7684\u4e00\u62da\u751f\u6b7b\uff0c\u5927\u59d0\u4e5f\u75db\u54ed\u7740\u6c42\u4ed6\uff0c\u90a3 \u77e5\u4ed6\u5374\u9053\uff1a\u201c\u4f60\u6211\u4e24\u4eba\uff0c\u4e00\u9f50\u4e0b\u53bb\u4e86\u5427\uff0c\u4e0a\u9762\u7684\u4eba\uff0c\u82e5\u662f\u5c06\u6211\u7684\u7ef3\u7d22\u5272\u65ad\uff0c\u90a3\u6211\u5c82\u975e\u662f\u81ea\u767d \u9001\u4e86\u6027\u547d\u3002\u201d\u201d \u3000\u3000\u5415\u5357\u4eba\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5509\u2014\u2014\u8fd9\u4e24\u4eba\u4e0d\u4f46\u6b66\u529f\u76f8\u82e5\uff0c\u5fc3\u8ba1\u5374\u4e5f\u76f8\u5f53\uff0c\u5509\u2014\u2014\u4e0a\u5929\u65e2\u751f\u4e86\u201c\u94c1\u9762\u5b64\u884c\u5ba2\u201d\u4e07\u5929 \u840d\uff0c\u4e3a\u4f55\u53c8\u504f\u504f\u8981\u518d\u751f\u4e2a\u201c\u5999\u624b\u201d\u8bb8\u767d\u5462\uff1f\u201d \u3000\u3000\u5b59\u654f\u63a5\u9053\uff1a \u3000\u3000\u201c\u6211\u542c\u4e86\u5c31\u8d76\u7d27\u8fde\u58f0\u79f0\u662f\uff0c\u529d\u4ed6\u4eec\u53e6\u5916\u60f3\u529e\u6cd5\uff0c\u6211\u867d\u7136\u77e5\u9053\u529d\u4ed6\u4eec\u4e0d\u4f4f\uff0c\u53ea\u671b\u4ed6\u4eec\u80fd\u62d6 \u4e9b\u65f6\u95f4\uff0c\u90a3\u77e5\u4e07\u5927\u54e5\u5374\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e48\u53e9\u4ed6\u4eec\u5168\u5230\u91cc\u9762\u53bb\u597d\u4e86\uff0c\u4ed6\u4eec\u4fbf\u65e0\u6cd5\u77e5\u9053\u90a3\u6761\u7ef3\u7d22\u662f \u4f60\u7684\u3002\u201d\u8fd9\u65f6\u5019\u53eb\u2014\u2014\u5509\uff0c\u5979\u672c\u60f3\u8bf4\uff1a\u201c\u8fd9\u65f6\u5019\u6211\u770b\u5230\u5927\u59d0\u9762\u4e0a\u9732\u51fa\u4e00\u4e1d\u559c\u8272\uff0c\u6211\u5fc3\u91cc\u4e5f \u60f3\uff0c\u6211\u4eec\u8fdb\u53bb\u4e86\uff0c\u96be\u9053\u4e0d\u80fd\u51fa\u6765\u5417\uff1f\u96be\u9053\u6211\u4eec\u5c31\u4e0d\u80fd\u5077\u770b\u4f60\u662f\u4ece\u90a3\u6761\u7ef3\u7d22\u6500\u63f4\u4e0b\u53bb\u7684 \u5417\uff1f\u201d\u201d \u3000\u3000\u4f46\u662f\u8fd9\u540e\u9762\u534a\u6bb5\u8bdd\uff0c\u5979\u5374\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u5979\u53ea\u662f\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u90a3\u59d3\u8bb8\u7684\u542c\u4e86\u5927\u54e5\u7684\u8bdd\uff0c\u7a81\u5730\u53c8\u5927\u7b11\u8d77\u6765\uff0c\u5927\u7b11\u58f0\u4e2d\uff0c\u8eab\u5f62\u7a81\u5730\u7684\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u6211\u65b9\u81ea \u4e00\u6115\uff0c\u53ea\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u5df2\u88ab\u4ed6\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u5927\u59d0\uff0c\u8679\u513f\uff0c\u548c\u4e2b\u73af\u4eec\u4e5f\u90fd\u5168\u88ab\u4ed6\u70b9\u4e86\u7a74\u9053\uff0c\u4e07 \u5927\u54e5\u5374\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u66f4\u4e0d\u963b\u6b62\uff0c\u90a3\u65f6\u6211\u5fc3\u91cc\u771f\u5947\u602a\uff0c\u5374\u53c8\u4e0d\u7981\u5bf9\u90a3\u59d3\u8bb8\u7684\u8fd9\u4e48\u5feb\u7684 \u8eab\u624b\u6240\u60ca\u2014\u2014\u201d \u3000\u3000\u5979\u8bed\u58f0\u672a\u4e86\uff0c\u5374\u542c\u5415\u5357\u4eba\u53f9\u9053\uff1a \u3000\u3000\u201c\u4e07\u8001\u524d\u8f88\u6ca1\u6709\u963b\u6b62\uff0c\u90a3\u53ea\u662f\u56e0\u4e3a\u4ed6\u65e9\u5df2\u6599\u5230\u8bb8\u767d\u6b64\u4e3e\u7684\u7528\u610f\u800c\u5df2\u3002\u201d\u5176\u5b9e\u6b64\u523b\u4ed6\u81ea\u5df1 \u5fc3\u4e2d\uff0c\u53c8\u4f55\u5c1d\u6ca1\u6709\u731c\u51fa\u8bb8\u767d\u7684\u7528\u610f\u5462\uff1f \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u4e03\u7ae0\u3000\u751f\u6b7b\u8c01\u77e5>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u4e03\u7ae0\u3000\u751f\u6b7b\u8c01\u77e5 \u3000\u3000\u5b59\u654f\u773c\u4e00\u5782\uff0c\u9894\u9996\u9053\uff1a \u3000\u3000\u201c\u5509\uff0c\u4f60\u731c\u7684\u4e5f\u4e0d\u9519\u90a3\u59d3\u8bb8\u7684\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u70b9\u4f4f\u4e86\u6570\u4eba\u7684\u7a74\u9053\uff0c\u65b9\u81ea\u9053\uff1a\u201c\u6211\u624b\u4e0b\u81ea\u6709 \u5206\u5bf8\uff0c\u8fd9\u4e9b\u4eba\u7a74\u9053\u867d\u88ab\u6211\u70b9\u4f4f\uff0c\u4f46\u6211\u62c5\u4fdd\u4ed6\u4eec\u8eab\u4f53\u4e0d\u81f3\u53d7\u635f\uff0c\u800c\u4e14\u4e00\u4e2a\u65f6\u8fb0\u540e\uff0c\u7a74\u9053\u81ea \u89e3\u3002\u201d\u4e07\u5927\u54e5\u53ea\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u59d3\u8bb8\u7684\u5927\u58f0\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u4f60\u82e5\u662f\u4e0d\u77e5\u9053\uff0c \u53c8\u600e\u4f1a\u5bb9\u5f97\u6211\u52a8\u624b\u3002\u201d \u3000\u3000\u201c\u5509\u2014\u2014\u8fd9\u4e24\u4eba\u5f53\u771f\u662f\u68cb\u9022\u654c\u624b\uff0c\u53ea\u53ef\u60dc\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u751f\u6b7b\u76f8\u4e89\u5462\uff1f\u201d \u3000\u3000\u5979\u832b\u7136\u8f6c\u52a8\u7740\u76ee\u5149\uff0c\u773c\u4e2d\u50cf\u662f\u53c8\u770b\u5230\u4e86\u5f53\u65f6\u7684\u60c5\u666f\u3002 \u3000\u3000\u201c\u6211\u773c\u7741\u7741\u5730\u770b\u7740\u4ed6\u4eec\u4e00\u4eba\u4e00\u8fb9\uff0c\u63a0\u4e86\u4e0b\u6765\uff0c\u4e0d\u4f46\u65e0\u6cd5\u963b\u6b62\uff0c\u7adf\u8fde\u58f0\u97f3\u90fd\u65e0\u6cd5\u53d1\u51fa\u201d \u3000\u3000\u51cc\u7433\u7a81\u7136\u63d2\u8a00\u9053\uff1a \u3000\u3000\u201c\u6709\u671d\u4e00\u65e5\uff0c\u6211\u82e5\u9047\u7740\u90a3\u59d3\u8bb8\u7684\uff0c\u4e00\u5b9a\u8981\u70b9\u4e0a\u4ed6\u7684\u7a74\u9053\uff0c\u8ba9\u4ed6\u4e5f\u5c1d\u5c1d\u6ecb\u5473\u3002\u201d \u3000\u3000\u5b59\u654f\u67f3\u7709\u4e00\u76b1\uff0c\u6b63\u5f85\u5475\u8d23\uff0c\u5374\u542c\u4e07\u8679\u7a81\u5730\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u610f\u793a\u4e0d\u5c51\uff0c\u5b59\u654f\u4e0d\u7981\u5927\u5947\u5fd6 \u9053\uff1a \u3000\u3000\u201c\u7433\u513f\u8fd9\u662f\u5e2e\u4f60\u4eec\u8bf4\u8bdd\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u5bf9\u5f85\u4e8e\u5979\u3002\u201d \u3000\u3000\u4f46\u662f\u5979\u76ee\u5149\u4e00\u8f6c\uff0c\u89c1\u5230\u5415\u5357\u4eba\u7684\u53cc\u7709\u7d27\u76b1\uff0c\u4f3c\u662f\u9887\u4e3a\u62c5\u5fc3\uff0c\u5979\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u5730\u604d\u7136\u5927 \u609f\uff0c\u6697\u4e2d\u5bfb\u601d\uff1a \u3000\u3000\u201c\u539f\u6765\u8679\u513f\u4e5f\u7231\u4e0a\u4e86\u4ed6\uff0c\u8fd9\u5374\u600e\u751f\u662f\u597d\uff1f\u201d\u53e3\u4e2d\u5374\u8f7b\u53f1\u9053\uff1a \u3000\u3000\u201c\u5c11\u63d2\u53e3\uff0c\u83ab\u8981\u60f9\u4eba\u7b11\u8bdd\u3002\u201d \u3000\u3000\u51cc\u7433\u660e\u7738\u4e00\u8f6c\uff0c\u50cf\u662f\u53c8\u60f3\u8bf4\u4ec0\u70b9\uff0c\u4f46\u5415\u5357\u4eba\u5374\u5df2\u9053\uff1a \u3000\u3000\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u51cc\u7433\u8f7b\u8f7b\u4e00\u54ac\u6a31\u5507\uff0c\u7adf\u5c06\u53e3\u8fb9\u7684\u8bdd\u5fcd\u4e86\u56de\u53bb\uff0c\u5b59\u654f\u8f7b\u53f9\u4e00\u58f0\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4e24\u4eba\u8eab\u5f62\u90fd\u5feb\uff0c\u6211\u773c\u524d\u4e00\u82b1\uff0c\u4ed6\u4eec\u5df2\u90fd\u6ca1\u4e86\u5f71\u5b50\uff0c\u6211\u5fc3\u91cc\u7740\u6025\u5f97\u7b80\u76f4\u65e0\u6cd5\u5f62\u5bb9\uff0c \u53ea\u542c\u5f97\u5230\u5904\u90fd\u662f\u201c\u7830\u7830\u201d\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u6211\u77e5\u9053\u5927\u5bb6\u90fd\u5728\u7740\u6025\uff0c\u90a3\u77e5\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u59d3\u8bb8\u7684\u7a81\u7136 \u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u597d\u2026\u2026\u201d\u58f0\u97f3\u9690\u9690\u4ece\u4e0b\u9762\u4f20\u4e0a\u4e4b\u540e\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u58f0\u97f3\uff0c\u6211\u7684\u5fc3\u5374\u8df3\u5f97\u66f4\u5389\u5bb3 \u4e86\uff0c\u3001\u5fc3\u60f3\uff1a\u83ab\u975e\u4ed6\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u6697\u53f9\uff1a \u3000\u3000\u201c\u60f3\u6765\u53c8\u662f\u90a3\u4e07\u5929\u840d\u51fa\u624b\u6697\u7b97\uff0c\u5509\u2014\u2014\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8bb8\u767d\u5f53\u771f\u662f\u96be\u4ee5\u8eb2\u907f\u3002\u201d\u5fc3\u5ff5\u4e00 \u8f6c\uff0c\u76ee\u5149\u4e00\u626b\u4e07\u6c0f\u6bcd\u5973\uff0c\u53c8\u5947\u9053\uff1a \u3000\u3000\u201c\u65e2\u662f\u5982\u6b64\uff0c\u90a3\u4e07\u5929\u840d\u60f3\u5fc5\u65e0\u4e8b\uff0c\u5979\u4e24\u4eba\u90fd\u53c8\u54ed\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5374\u542c\u5b59\u654f\u53c8\u81ea\u53f9\u9053\uff1a \u3000\u3000\u201c\u90a3\u77e5\u6211\u5ff5\u5934\u8fd8\u6ca1\u6709\u8f6c\u5b8c\uff0c\u4e0b\u9762\u5374\u53c8\u9690\u9690\u4f20\u6765\u4e07\u5927\u54e5\u547c\u559d\u58f0\u97f3\uff0c\u63a5\u7740\u53c8\u662f\u90a3\u59d3\u8bb8\u7684\u7b11\u58f0 \u548c\u53f1\u58f0\uff0c\u6ca1\u6709\u591a\u4e45\uff0c\u4e24\u4eba\u7a81\u7136\u540c\u65f6\u5927\u559d\u4e00\u58f0\u2014\u2014\u5509\uff0c\u4ee5\u540e\u5c31\u771f\u7684\u518d\u4e5f\u6ca1\u6709\u4efb\u4f55\u58f0\u97f3\u4e86\u3002\u201d \u3000\u3000\u5415\u5357\u4eba\u5fc3\u5934\u4e00\u5bd2\uff0c\u53ea\u542c\u5b59\u654f\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u98de\u9601\u4e4b\u4e2d\uff0c\u7adf\u4e5f\u662f\u518d\u65e0\u5176\u4ed6\u58f0\u97f3\uff0c\u53ea\u542c\u5f97\u547c\u5438\u4e4b \u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\u3002 \u3000\u3000\u4ed6\u9ed8\u7136\u53f9\u606f\u534a\u664c\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u81ea\u529d\u6170\u9053\uff1a \u3000\u3000\u201c\u751f\u6b7b\u7531\u547d\u2014\u2014\u5509\uff0c\u4ed6\u4e24\u4eba\u4ff1\u662f\u4e00\u8eab\u7edd\u6280\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u90fd\u4e0d\u77e5\u7ecf\u8fc7\u591a\u5c11\u4ef6\u51f6\u9669\u4e4b\u4e8b\uff0c\u6b64 \u6b21\u8bf4\u4e0d\u5b9a\u4ea6\u80fd\u5316\u9669\u4e3a\u5937\u4ea6\u672a\u53ef\u77e5\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u672a\u4e86\uff0c\u672c\u5df2\u505c\u4f4f\u97ad\u58f0\u7684\u4e07\u6c0f\u6bcd\u5973\uff0c\u7a81\u53c8\u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u5415\u5357\u4eba\u5450\u5450\u5730\u65b9\u5f85\u518d\u8bf4\u4e24 \u53e5\u529d\u6170\u4e4b\u8a00\uff0c\u90a3\u77e5\u4e07\u592b\u4eba\u7a81\u5730\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u5411\u4ed6\u8dea\u4e86\u4e0b\u6765\uff0c\u4e00\u628a\u62c9\u7740\u4ed6\u7684\u8863\u895f\uff0c\u5415\u5357\u4eba\u4e00 \u60ca\u5fd9\u9053\uff0c \u3000\u3000\u201c\u592b\u4eba\u2026\u2026\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e07\u592b\u4eba\u7adf\u54c0\u54ed\u9053\uff1a \u3000\u3000\u201c\u4f60\u6551\u6551\u4ed6\u4eec\u2026\u2026\u4f60\u6551\u6551\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u5415\u5357\u4eba\u832b\u7136\u4e0d\u77e5\u6240\u63aa\uff0c\u60f6\u58f0\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u53ea\u8981\u80fd\u4e4b\u6240\u53ca\uff0c\u5509\u2026\u2026\u201d \u3000\u3000\u4e07\u592b\u4eba\u54c0\u54ed\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u6b66\u529f\u5f88\u9ad8\uff0c\u4f60\u4e0b\u53bb\u770b\u770b\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u6b7b\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5415\u5357\u4eba\u4e00\u6115\uff0c\u5374\u542c\u5979\u53c8\u9053\uff1a \u3000\u3000\u201c\u8679\u513f\uff0c\u4f60\u4e5f\u8dea\u4e0b\u6765\u2026\u2026\u59b9\u5b50\uff0c\u4f60\u4e5f\u5e2e\u6211\u6c42\u6c42\u4ed6\u2026\u2026\u6211\u2026\u2026\u8fd9\u4e48\u591a\u5e74\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5657\u5730\u4e00\u58f0\uff0c\u4e07\u8679\u4e5f\u8dea\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5415\u5357\u4eba\u6ee1\u8033\u90fd\u662f\u54c0\u6c42\u4e4b\u58f0\uff0c\u6ee1\u5fc3\u4ff1\u662f\u60f6\u7136\u4e4b\u60c5\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u4ed6\u600e\u80fd\u65ad\u7136\u62d2\u7edd\u8fd9\u653e\u58f0\u54c0\u54ed \u7740\u7684\u5987\u4eba\uff0c\u4f46\u4ed6\u53c8\u600e\u80fd\u7b54\u5e94\uff1f \u3000\u3000\u53ea\u542c\u4e07\u592b\u4eba\u75db\u54ed\u7740\u53c8\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e48\u591a\u5e74\u2026\u2026\u6211\u548c\u5929\u840d\u2026\u2026\u4e00\u5171\u624d\u53ea\u6709\u77ed\u77ed\u51e0\u4e2a\u6708\u7684\u65f6\u95f4\u5728\u4e00\u8d77.\u5982\u4eca\u2026\u2026\u5c31\u7b97\u4ed6\u6b7b \u4e86\uff0c\u4f60\u2026\u2026\u4f60\u4e5f\u8981\u628a\u4ed6\u7684\u9996\u2026\u2026\u7ed9\u6211\uff0c\u4f60\u2026\u2026\u59b9\u5b50\uff0c\u4f60\u6c42\u6c42\u4ed6\u2026\u2026\u8679\u513f\u2026\u2026\u4f60\u6c42\u6c42\u4ed6\u2026\u2026\u4f60 \u4e3a\u4ec0\u4e48\u8fd9\u4e48\u72e0\u5fc3\u3002\u201d \u3000\u3000\u5979\u7a81\u7136\u8f6c\u5411\u5b59\u654f\uff0c\u53c8\u9053\uff0c \u3000\u3000\u201c\u59b9\u5b50\uff0c\u4f60\u6c42\u6c42\u4ed6\u2026\u2026\u201d\u653e\u58f0\u75db\u54ed\u4e0d\u5df2\u3002 \u3000\u3000\u5b59\u654f\u6b64\u523b\u5374\u5df2\u4e3a\u4e4b\u60ca\u6115\u4f4f\u4e86\uff0c\u5450\u5450\u9053\uff1a \u3000\u3000\u201c\u4f0a\u98ce\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u548c\u4e07\u592b\u4eba\u867d\u7136\u59d0\u59b9\u5173\u5fc3\uff0c\u4f46\u5979\u5374\u53c8\u600e\u80fd\u5411\u522b\u4eba\u63d0\u51fa\u5982\u6b64\u4e4b\u4e0d\u8fd1\u60c5\u7406\u7684\u8981\u6c42\uff0c\u4f55\u51b5\u6b64\u4eba \u8fd8\u662f\u5979\u7231\u5973\u7684\u5fc3\u4e0a\u4e4b\u4eba\uff0c\u4e07\u8679\u4ea6\u81ea\u54c0\u54c0\u5730\u75db\u54ed\u7740\uff0c\u819d\u884c\u5230\u5415\u5357\u4eba\u9762\u524d\uff0c\u75db\u54ed\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u5bf9\u4f60\uff0c\u6211\u5bf9\u4f60\u2026\u2026\u4f60\u77e5\u4e0d\u77e5\u9053\u2026\u2026\u6211\u77e5\u9053\u4f60\u7684\u529f\u592b\u2026\u2026\u201d \u3000\u3000\u4e07\u592b\u4eba\u7acb\u523b\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e00\u4e2a\u4eba\u4e0b\u53bb\u7edd\u5bf9\u4e0d\u4f1a\u6709\u4e8b\u7684\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u5415\u5357\u4eba\u6084\u7136\u5408\u4e0a\u773c\uff0c\u7a81\u53c8\u5f20\u5f00\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u63a0\u5230\u680f\u65c1\uff0c\u6293\u8d77\u4e00\u6761\u7c97\u7ef3\uff0c\u624b\u638c\u5207\u4e0b\uff0c\u65a9\u4e3a \u4e24\u65ad\uff0c\u53cc\u624b\u4ea4\u4f20\uff0c\u5c06\u8fd9\u6761\u7c97\u7ef3\u5782\u5728\u4e0b\u9762\u7684\u90e8\u4efd\uff0c\u63d0\u4e86\u4e0a\u6765\uff0c\u5b59\u654f\u5706\u7741\u53cc\u7738\uff0c\u5403\u60ca\u5730\u671b\u7740\u4ed6\u3002 \u51cc\u7433\u5374\u5df2\u82b1\u5bb9\u5931\u8272\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u4ed6\u8eab\u65c1\uff0c\u60f6\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u5357\u4eba\u76ee\u5149\u51dd\u6ce8\u7740\u81ea\u5df1\u7684\u624b\u638c\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u6211\u5c06\u8fd9\u6761\u7ef3\u5b50\u62ff\u4e0a\u6765\uff0c\u5e26\u5728\u8eab\u65c1\uff0c\u82e5\u662f\u90a3\u6761\u7ef3\u5b50\u4e0d\u591f\u957f\uff0c\u6211\u5c31\u628a\u5b83\u63a5\u4e0a\u53bb\uff0c\u77e5\u9053\u4e86 \u5417\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u51cc\u7433\u7684\u4e00\u53cc\u773c\u775b\uff0c\u5df2\u83b9\u7136\u6709\u4e86\u6cea\u5149\u3002 \u3000\u3000\u201c\u4f60\u2026\u2026\u8981\u2026\u2026\u4e0b\u2026\u2026\u53bb\uff01\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\uff0c\u5df2\u5f00\u59cb\u98a4\u6296\u8d77\u6765\u3002 \u3000\u3000\u5415\u5357\u4eba\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\uff0c\u4ecd\u81ea\u5708\u7740\u7ef3\u7d22\uff0c\u53e3\u4e2d\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0b\u53bb\u770b\u770b\uff0c\u4e0d\u4f1a\u6709\u4e8b\u7684\u3002\u201d \u3000\u3000\u51cc\u7433\u4e00\u628a\u62c9\u7740\u4ed6\u81c2\u8180\uff0c\u60f6\u58f0\u5927\u53eb\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff0c\u4f60\u4f60\u2026\u2026\u4eba\u5bb6\u5bf9\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u4e86\uff0c\u4e07\u592b\u4eba\u5df2\u548c\u58f0\u6251\u4e86\u4e0a\u53bb\uff0c\u4e00\u628a\u62b1\u4f4f\u5979\u7684\u7ea4\u8170\uff0c\u54ed\u9a82\u9053\uff1a \u3000\u3000\u201c\u4f60\u771f\u6ca1\u6709\u826f\u5fc3\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u5929\u840d\u662f\u4f60\u59e8\u7236\uff0c\u4ed6\u2026\u2026\u4ed6\u662f\u2026\u2026\u201d \u3000\u3000\u5b59\u654f\u5446\u5446\u5730\u7ad9\u5728\u65c1\u8fb9\uff01\u5fc3\u4e2d\u7d0a\u4e71\u5982\u9ebb\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5979\u6b63\u662f\u65b9\u5bf8\u5927\u4e71\uff0c\u5de6\u53f3\u4e3a\u96be\uff0c\u4e0d\u77e5\u600e \u751f\u662f\u597d\u3002 \u3000\u3000\u51cc\u7433\u4ea6\u81ea\u54ed\u53eb\u7740\uff1a \u3000\u3000\u201c\u4f60\u653e\u624b\uff0c\u4f60\u5bf9\u4eba\u5bb6\u600e\u4e48\u6837\uff0c\u4e3a\u4ec0\u4e48\u8981\u53eb\u4eba\u5bb6\u4e5f\u966a\u4f60\u4e08\u592b\u4e00\u9f50\u6b7b\u2026\u2026\u201d \u3000\u3000\u5b59\u654f\u8f7b\u53f9\u9053\uff1a\u201c\u7433\u513f\uff0c\u4f4f\u58c1\u3002\u201d\u4f46\u5979\u58f0\u97f3\u8bf4\u5f97\u4e0d\u5927\uff0c\u4f55\u51b5\u5979\u5373\u4f7f\u58f0\u97f3\u771f\u5927\uff0c\u51cc\u7433\u5374\u4e5f\u4e0d \u4f1a\u542c\u5230\u3002 \u3000\u3000\u90a3\u77e5\u5415\u5357\u4eba\u7a81\u5730\u5389\u53f1\u4e00\u58f0\uff1a \u3000\u3000\u201c\u4f4f\u58c1\uff01\u201d \u3000\u3000\u8fd9\u8f7b\u8f7b\u4e00\u58f0\u547c\u53f1\uff0c\u5374\u50cf\u662f\u6709\u7740\u4ec0\u4e48\u9b54\u529b\u4f3c\u7684\uff0c\u4f7f\u5f97\u54ed\u58f0\u90fd\u5fae\u5f31\u4e86\u4e0b\u6765\uff0c\u4ed6\u7f13\u7f13\u8f6c\u8fc7\u8eab \u8eaf\uff0c\u9762\u5411\u4e07\u592b\u4eba\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8bf7\u4f60\u653e\u5f00\u624b\u3002\u201d \u3000\u3000\u4e07\u592b\u4eba\u53ea\u89c9\u4ed6\u76ee\u5149\u4e4b\u4e2d\uff0c\u50cf\u662f\u6709\u7740\u4ec0\u4e48\u4ee4\u4eba\u4e0d\u80fd\u4e0d\u6151\u670d\u7684\u529b\u91cf\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u677e\u5f00\u624b\u638c\u9000 \u540e\u4e00\u6b65\uff0c\u5782\u9996\u800c\u7acb\u3002 \u3000\u3000\u5415\u5357\u4eba\u7f13\u7f13\u4f38\u51fa\u624b\u6765\uff0c\u8f7b\u8f7b\u629a\u6478\u7740\u51cc\u7433\u7684\u5982\u4e91\u79c0\u53d1\uff0c\u67d4\u58f0\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u6211\u662f\u4e00\u4e2a\u52c7\u6562\u7684\u4eba\uff1f\u201d \u3000\u3000\u51cc\u7433\u65e0\u8a00\u5730\u7559\u7740\u6cea\u73e0\uff0c\u65e0\u8a00\u5730\u70b9\u7740\u5934\u3002 \u3000\u3000\u5415\u5357\u4eba\u7f13\u7f13\u53c8\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e48\uff0c\u4f60\u603b\u4e0d\u4f1a\u613f\u610f\u6211\u4e3a\u4e86\u5371\u9669\u548c\u56f0\u96be\uff0c\u5c31\u4e0d\u53bb\u5e2e\u52a9\u522b\u4eba\u5427\u3125\u4f60\u8981\u77e5\u9053\uff0c\u52a9\u4eba\u662f\u4e0d\u8bba \u4eb2\u758f\u7684\uff0c\u8def\u89c1\u4e0d\u5e73\uff0c\u5c1a\u4e14\u8981\u62d4\u5200\u76f8\u52a9\uff0c\u5373\u4f7f\u90a3\u4eba\u662f\u4f60\u7684\u654c\u4eba\uff0c\u4f46\u662f\u4ed6\u82e5\u771f\u7684\u9700\u8981\u4f60\u7684\u5e2e\u52a9\uff0c \u4f60\u5c31\u8be5\u4f38\u624b\uff0c\u4f55\u51b5\u662f\u6551\u4eba\uff0c\u90a3\u4f60\u5c31\u66f4\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\uff0c\u73b0\u5728\u4e07\u5927\u4fa0\u548c\u8bb8\u5927\u4fa0\u7684\u751f\u6b7b\u8c01\u4e5f\u4e0d\u77e5 \u9053\uff0c\u6211\u4e0b\u53bb\u4e86\uff0c\u53ef\u80fd\u4f1a\u5c06\u4ed6\u4eec\u6551\u6d3b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\uff0c\u51cc\u7433\u4e00\u9762\u7559\u7740\u773c\u6cea\uff0c\u5b59\u654f\u4e5f\u4e0d\u7981\u9eef\u7136\u6d41\u6cea\uff0c \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u51cc\u7433\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u53c8\u653e\u58f0\u75db\u54ed\u4e86\u8d77\u6765\uff0c\u75db\u54ed\u7740\u9053\uff1a \u3000\u3000\u201c\u4f46\u662f\u2026\u2026\u4f60\u81ea\u5df1\u5462\u2026\u2026\u4f60\u96be\u9053\u4e0d\u60f3\u60f3\u4f60\u81ea\u5df1\uff0c\u4f60\u2026\u2026\u5230\u5e95\u4e3a\u4e86\u4ec0\u4e48\uff0c\u96be\u9053\u2026\u2026\u96be\u9053\u4f60 \u662f\u4e3a\u4e86\u90a3\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u5979\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u56de\u8fc7\u5934\uff0c\u98a4\u6296\u7740\u4f38\u51fa\u7389\u638c\uff0c\u6307\u7740\u90a3\u4ecd\u7136\u8dea\u5728\u5730\u4e0a\u7684\u4e07\u8679\uff0c\u4e07\u8679\u76ee\u5149\u4e00 \u62ac\uff0c\u9762\u4e0a\u7a81\u53c8\u63a0\u8fc7\u4e00\u4e1d\u6124\u6068\u6028\u6bd2\u4e4b\u8272\uff0c\u72e0\u72e0\u77aa\u4e86\u51cc\u7433\u4e24\u773c\uff0c\u4fbf\u53c8\u5782\u4e0b\u5934\u53bb\uff0c\u4f46\u6b64\u523b\u4eba\u4eba\u5fc3\u4e2d \u4ff1\u662f\u7d0a\u4e71\u5982\u9ebb\uff0c\u81ea\u7136\u8c01\u4e5f\u6ca1\u6709\u7559\u610f\u5230\u5979\u8fd9\u4e00\u77a5\u4e2d\u7684\u6068\u610f\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u516b\u7ae0\u3000\u82cd\u5929\u65e0\u8bed>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u516b\u7ae0\u3000\u82cd\u5929\u65e0\u8bed \u3000\u3000\u5415\u5357\u4eba\u5251\u7709\u4e00\u8f69\uff0c\u5fae\u6709\u6012\u610f\uff0c\u4f46\u77ac\u5373\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u600e\u4e48\u80fd\u8fd9\u6837\u8bf4\uff0c\u6211\u600e\u4e48\u4e3a\u4e86\u5979\u505a\u8fd9\u79cd\u4e8b\uff0c\u4f60\u77e5\u9053\uff0c\u9664\u4e86\u53c8\u7eaf\u6d01\uff0c\u53c8\u5929\u771f\uff0c \u53c8\u6e29\u67d4\u2026\u2026\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u8bf4\u7740\uff0c\u76ee\u5149\u4e2d\u4f3c\u53c8\u6cdb\u8fc7\u8427\u5357\u82f9\u7684\u8eab\u5f71\uff0c\u4e8e\u662f\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u81ea\u63a5\u9053\uff1a \u3000\u3000\u201c\u53c8\u4ec1\u6148\u2026\u2026\u50cf\u4f60\u8fd9\u6837\u5b69\u5b50\uff0c\u6211\u4f1a\u4e3a\u5979\u4eec\u5192\u9669\u4e4b\u5916\uff0c\u6211\u4e0d\u4f1a\uff01\u5509\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u6211\u8fd9\u4e48 \u505a\uff0c\u4e0d\u662f\u4e3a\u4eba\uff0c\u662f\u4e3a\u4e86\u4e8b\uff0c\u6211\u89c9\u5f97\u8be5\u505a\u8fd9\u6837\u4e8b\uff0c\u6240\u4ee5\u6211\u5c31\u505a\u4e86\uff0c\u5047\u5982\u6211\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u662f\u4e0d\u8be5\u505a \u7684\uff0c\u90a3\u4e48\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u5f3a\u8feb\u6211\uff0c\u50bb\u5b69\u5b50\uff0c\u77e5\u9053\u5417\uff0c\u6765\uff0c\u70b9\u70b9\u5934\uff0c\u8ba9\u6211\u4e0b\u53bb\uff0c\u54ce\uff01\u5bf9\u4e86\uff0c\u70b9 \u70b9\u5934\uff0c\u8ba9\u6211\u4e0b\u53bb\uff0c\u7136\u540e\u518d\u4e56\u4e56\u5730\u7b49\u7740\u6211\u56de\u6765\uff0c\u6211\u4e00\u5b9a\u4f1a\u56de\u6765\u7684\uff0c\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u51cc\u7433\u4e0d\u65ad\u5730\u70b9\u7740\u5934\uff0c\u4f46\u662f\u5979\u7684\u6cea\u73e0\u5df2\u6ee1\u4e86\u5979\u81ea\u5df1\u8863\u88f3\uff0c\u4e5f\u5df2\u6ee1\u4e86\u5415\u5357\u4eba\u7684\u8863\u88f3\u3002 \u3000\u3000\u5b59\u654f\u6162\u6162\u8d70\u8fc7\u6765\uff0c\u8fd9\u575a\u5f3a\u7684\u5987\u4eba\uff0c\u6b64\u523b\u4ea6\u81ea\u6cea\u6d41\u6ee1\u9762\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u555c\u6ce3\u7740\u9053\uff1a \u3000\u3000\u201c\u4f0a\u98ce\uff0c\u4f60\u2026\u2026\u4f60\u591a\u73cd\u91cd\uff0c\u5c0f\u5fc3\u4e9b\u2026\u2026\u201d \u3000\u3000\u4f0a\u98ce\u70b9\u4e86\u70b9\u7855\uff0c\u5c06\u90a3\u4e00\u5708\u5df2\u7ecf\u5708\u597d\u4e86\u7684\u7ef3\u7d22\uff0c\u5c0f\u5fc3\u5730\u7dae\u5728\u8170\u4e0a\uff0c\u7136\u540e\u8f6c\u52a8\u4e00\u4e0b\u8eab\u8eaf\uff0c\u8bd5 \u8bd5\u8eab\u624b\u662f\u5426\u4ecd\u81ea\u7075\u4fbf\uff0c\u7136\u540e\u4ed6\u7a81\u7136\u9053\uff1a \u3000\u3000\u201c\u53eb\u6211\u5357\u4eba\uff0c\u6211\u53eb\u5415\u5357\u4eba\uff0c\u4ece\u6b64\uff0c\u4e16\u4e0a\u518d\u4e5f\u6ca1\u6709\u4f0a\u98ce\u8fd9\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u4e86\uff0c\u4ed6\u500f\u7136\u8f6c\u8eab\uff0c\u95ea\u7535\u822c\u63f4\u7d22\u800c\u4e0b\uff0c\u5f3a\u5fcd\u7740\u4e0d\u518d\u5411\u4e0a\u671b\u4e00\u773c\uff0c\u4f46\uff0e\u4ed6\u65e0\u6cd5\u4f7f\u4ed6\u8033 \u4e2d\u542c\u4e0d\u5230\u4e0a\u9762\u4f20\u4e0b\u7684\u53ee\u549b\u548c\u75db\u54ed\u58f0\uff0c\u4ed6\u81ea\u5632\u5730\u82e6\u7b11\uff1a \u3000\u3000\u201c\u5012\u5e95\u662f\u5973\u5b50\u3002\u201d\u53c8\u575a\u5f3a\u5730\u544a\u8bc9\u81ea\u5df1\uff1a \u3000\u3000\u201c\u6211\u53c8\u600e\u4f1a\u6b7b\u54e9\uff0c\u4e0b\u9762\u518d\u5371\u9669\uff0c\u4f46\u53ea\u8981\u6709\u8fd9\u6761\u7ef3\u7d22\u53ef\u4ee5\u4f9d\u9644\uff0c\u6211\u8fd8\u6015\u4ec0\u4e48\uff0c\u6211\u4e00\u5b9a\u4f1a\u518d \u4e0a\u6765\u7684\uff0c\u90a3\u65f6\u5979\u4eec\u5c31\u90fd\u4f1a\u7b11\u4e86\u3002\u201d \u3000\u3000\u6e10\u6e10\u2026\u2026\u2026 \u3000\u3000\u4e0a\u9762\u7684\u54ed\u6ce3\u58f0\u8d8a\u6765\u8d8a\u5fae\u5f31\uff0c\u751a\u81f3\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6e10\u6e10\u2026\u2026\u2026 \u3000\u3000\u5c71\u52bf\u66f4\u9669\u5cfb\u4e86\u8d77\u6765\uff0c\u8fd9\u58c1\u7acb\u5343\u5bfb\u7684\u58c1\u4e0a\uff0c\u77f3\u7259\u602a\u7acb\uff0c\u53c8\u6ee1\u751f\u7740\u9752\u82d4\uff0c\u5076\u7136\u4e5f\u6709\u4e00\u4e9b\u4e0d\u77e5 \u540d\u7684\u6811\u6728\uff0c\u4ece\u77f3\u7f1d\u4e2d\u751f\u51fa\uff0c\u800c\u4e14\u8d8a\u5f80\u4e0b\u9762\uff0c\u8d8a\u4e3a\u9669\u5cfb\uff0c\u4ed6\u751a\u81f3\u4e0d\u6562\u518d\u5f80\u4e0b\u770b\u4e00\u773c\uff0c\u53ea\u662f\u8c28\u614e \u800c\u7f13\u6162\u5730\u5f80\u4e0b\u9762\u79fb\u52a8\u7740\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u4e86\u4e00\u4ef6\u4e8b\u2026\u2026 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u4ed6\u8fd9\u5ff5\u5934\u65b9\u81ea\u5347\u8d77\uff0c\u624b\u638c\u7a81\u5730\u4e00\u8f7b\uff0c\u5168\u8eab\u663e\u7136\u5931\u5374\u4e86\u53ef\u4ee5\u4f9d\u9644\u4e4b\u7269\uff0c\u65e0\u52a9\u5730\u5411\u90a3\u6df1\u4e0d\u89c1 \u5e95\uff0c\u9634\u6c89\u5e7d\u6697\u7684\u7edd\u671b\u4e2d\u843d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u7531\u751f\u5730\u60ca\u547c\u4e00\u58f0\uff0c\u5fc3\u4e2d\u95ea\u7535\u822c\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff1a \u3000\u3000\u201c\u8fd9\u7ef3\u7d22\u600e\u4f1a\u65ad\u7684\uff1f\u201d\u76ee\u5149\u52a8\u5904\uff0c\u89c1\u5230\u5c71\u58c1\u4e0a\u4f3c\u4e4e\u6709\u4e2a\u6d1e\u7a74\uff0c\u4ed6\u60f3\u4f38\u624b\u6500\u4f4f\uff0c\u4f46\u662f\uff0c\u4ed6 \u7684\u8eab\u5f62\u5374\u5df2\u4e00\u65e0\u51ed\u85c9\u5730\u843d\u4e86\u4e0b\u53bb\u2026\u2026 \u3000\u3000\u843d\u4e86\u4e0b\u53bb\u2026\u2026\u2026\u2026 \u3000\u3000\u2026\u2026\u2026\u2026\u2026\u843d\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u5947\u602a\uff0c\u8fd9\u7ef3\u7d22\u600e\u4f1a\u65ad\u7684\uff01 \u3000\u3000\u51cc\u7433\u60b2\u5207\u5730\u4f0f\u5728\u680f\u6746\u4e0a\uff0c\u671b\u7740\u5415\u5357\u4eba\u8d8a\u6765\u8d8a\u5c0f\u7684\u8eab\u5f71\uff0c\u5979\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u7ffb\u8eab\u6251\u5728\u5979\u5988\u5988 \u8eab\u4e0a\uff0c\u53c8\u81ea\u653e\u58f0\u75db\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5b59\u654f\u601c\u7231\u5730\u629a\u7740\u5979\u67d4\u8f6f\u7684\u80cc\u810a\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u67d4\u58f0\u53f9\u9053\uff1a \u3000\u3000\u201c\u4e56\u5b69\u5b50\uff0c\u4e0d\u8981\u54ed\uff0c\u4ed6\u4f1a\u56de\u6765\u7684\uff0c\u4ed6\u4e0d\u662f\u5bf9\u4f60\u8bf4\u8fc7\u4e86\u5417\uff1f\u201d \u3000\u3000\u5979\u52c9\u5f3a\u5728\u81ea\u5df1\u4ea6\u662f\u6cea\u6d41\u6ee1\u9762\u7684\u8138\u4e0a\uff0c\u6324\u51fa\u4e00\u4e2a\u7b11\u5bb9\uff1a \u3000\u3000\u201c\u4f60\u96be\u9053\u4e0d\u76f8\u4fe1\u4ed6\u5417\uff1f\u4ed6\u4f1a\u5e73\u5b89\u7684\u3002\u201d \u3000\u3000\u51cc\u7433\u62ac\u8d77\u5934\uff0c\u62bd\u6ce3\u7740\u9053\uff1a \u3000\u3000\u201c\u4ed6\u771f\u7684\u4f1a\u5e73\u5b89\u5417\uff1f\u201d \u3000\u3000\u5b59\u654f\u5fcd\u4f4f\u6cea\uff0c\u5fae\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4e0d\u4f46\u4f1a\u5e73\u5b89\u5730\u56de\u6765\uff0c\u800c\u4e14\u8fd8\u4f1a\u5e26\u56de\u4f60\u7684\u59e8\u7236\uff0c\u800c\u4e14\u2014\u2014\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u4f60\u75af\u4e86\u5417\uff1f\u201d \u3000\u3000\u51cc\u7433\u6b63\u6c89\u9189\u5728\u5979\u5988\u5988\u7684\u751c\u871c\u5730\u8a00\u8bed\u4e4b\u4e2d\uff0c\u7a81\u5730\u542c\u5230\u5979\u5988\u5988\u5389\u58f0\u5927\u559d\u8d77\u6765\uff0c\u5979\u65b9\u81ea\u4e00\u6115\uff0c \u63a5\u7740 \u3000\u3000\u53c8\u662f\u5415\u5357\u4eba\u7684\u4e00\u58f0\u60e8\u547c\uff0c\u81ea\u58d1\u4e0b\u4f20\u6765\u3002 \u3000\u3000\u5979\u60ca\u614c\u5931\u63aa\u5730\u62ac\u8d77\u5934\uff0c\u53ea\u89c1\u5979\u5988\u5988\u6728\u7136\u800c\u7acb\uff0c\u9762\u8272\u60e8\u53d8\uff0c\u671b\u7740\u8eab\u540e\u2014\u2014 \u3000\u3000\u5979\u5927\u559d\u4e00\u58f0\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u4e07\u8679\u6b63\u6ee1\u9762\u5e26\u7740\u72e0\u6bd2\u7684\u7b11\u5bb9\uff0c\u7ad9\u5728\u680f\u6746\u65c1\u8fb9\uff0c\u800c\u680f\u6746\u4e4b\u4e0a\u7684\u7ef3 \u7d22\uff0c\u5374\u5df2\u5269\u4e0b\u77ed\u77ed\u4e00\u5c3a\uff01 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u5979\u53ea\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u4ec0\u4e48\u4e8b\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u4e0d\u4f1a\u8bf4\u2026\u2026 \u3000\u3000\u4e07\u8679\u7a81\u7136\u51c4\u5389\u5730\u72c2\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u8981\u4ed6\u6b7b\uff0c\u5927\u5bb6\u90fd\u5f97\u4e0d\u5230\u2026\u2026\u54c8\u54c8\uff0c\u5927\u5bb6\u90fd\u5f97\u4e0d\u5230\u3002\u201d \u3000\u3000\u5979\u51c4\u5389\u5730\u72c2\u7b11\uff0c\u51c4\u5389\u5730\u72c2\u558a\u7740\uff0c\u5c31\u8fde\u5979\u5988\u5988\uff0c\u4e5f\u88ab\u5979\u60ca\u5f97\u5706\u7741\u53cc\u76ee\uff0c\u75f4\u75f4\u5730\u671b\u7740\u5979\uff0c\u53e3 \u4e2d\u5583\u5583\u8bf4\u9053\uff1a \u3000\u3000\u201c\u75af\u4e86\u2026\u2026\u75af\u4e86\u2026\u2026\u201d \u3000\u3000\u4e07\u8679\u51c4\u5389\u5730\u547c\u558a\uff1a \u3000\u3000\u201c\u75af\u4e86\u2026\u2026\u75af\u4e86\u2026\u2026\u201d \u3000\u3000\u6e10\u6e10\u2014\u2014 \u3000\u3000\u72c2\u7b11\u53d8\u6210\u72c2\u54ed\uff0c\u5979\u7a81\u7136\u4f38\u51fa\u624b\u638c\uff0c\u6293\u626f\u7740\u81ea\u5df1\u7684\u9762\u9765\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u51cc\u7433\u5927\u559d\u4e00\u58f0\uff0c\u5411\u5979\u6251\u4e86\u8fc7\u53bb\uff1a \u3000\u3000\u201c\u4f60\u597d\u72e0\uff0c\u4f60\u597d\u72e0\uff0c\u6211\u8981\u6740\u6b7b\u4f60\uff0c\u627e\u8981\u6740\u6b7b\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u7adf\u4e5f\u53d1\u72c2\u5730\u547c\u558a\u7740\uff0c\u53d1\u72c2\u5730\u5728\u4e07\u8679\u8eab\u4e0a\uff0c\u5934\u4e0a\u2026\u2026\u51fb\u6253\u7740\uff0c\u53ea\u662f\u5979\u6b64\u523b\u5fc3\u75db\u5982\u7ede\uff0c\u5fc3 \u4e71\u5982\u9ebb\uff0c\u7adf\u4f3c\u5df2\u5fd8\u4e86\u4f7f\u51fa\u5185\u5bb6\u771f\u529b\uff0c\u800c\u4f7f\u51fa\u5973\u6027\u6700\u539f\u59cb\u7684\u6b66\u5668\uff0c\u5979\u7adf\u4e5f\u7528\u6307\u7532\u5728\u4e07\u8679\u8eab\u4e0a\uff0c \u5934\u4e0a\u6293\u626f\u7740\uff0c \u3000\u3000\u5b59\u654f\uff0c\u8fd9\u575a\u5f3a\u7684\u5987\u4eba\uff0c\u6b64\u523b\u53c8\u518d\u4e00\u6b21\u53d1\u6325\u4e86\u5979\u575a\u5f3a\u7684\u795e\u667a\u3002 \u3000\u3000\u56e0\u4e3a\u6b64\u523b\u8fd9\u5176\u95f4\u53ea\u6709\u5979\u4e00\u4eba\u7684\u795e\u667a\u8f83\u4e3a\u6e05\u9192\u4e9b\uff0c\u5979\u4e00\u6b65\u7a9c\u4e86\u8fc7\u53bb\uff0c\u62b1\u7740\u5979\u7231\u5973\u7684\u53cc\u81c2\uff0c \u8fde\u58f0\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u6e05\u9192\u4e9b\u2026\u2026\u7433\u513f\uff0c\u6e05\u9192\u4e9b\u2026\u2026\u201d \u3000\u3000\u4e07\u8679\u75af\u72c2\u4e86\u4f3c\u7684\u8dd1\u5230\u98de\u9601\u4e0a\u53bb\uff0c\u51cc\u7433\u4e5f\u53d1\u72c2\u4e86\u4f3c\u7684\u8981\u8ffd\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u662f\u5979\u5988\u5988\u5374\u5168\u529b\u62b1\u7740\u5979\uff0c\u5979\u5fc3\u6d3b\u50cf\u4e2d\u4e86\u4e71\u7bad\u4f3c\u7684\uff0c\u70b9\u70b9\u6ef4\u6ef4\u5730\u6ef4\u51fa\u8840\u6765\uff0c\u5979\u72c2\u558a\u7740\uff1a \u3000\u3000\u201c\u4f60\u4eec\u597d\u72e0\u2026\u2026\u4ed6\u4e3a\u4f60\u4eec\u4e0b\u53bb\u4e86\u2026\u2026\u4f60\u4eec\u5374\u5bb3\u6b7b\u4e86\u4ed6\uff01\u201d \u3000\u3000\u6e10\u6e10\u2014\u2014 \u3000\u3000\u5979\u547c\u58f0\u4e5f\u5fae\u5f31\u4e86\uff0c\u5979\u53ea\u89c9\u8033\u65c1\u4ec0\u4e48\u58f0\u97f3\u90fd\u5fae\u5f31\u4e86\u4e0b\u6765\u2014\u2014\u5305\u62ec\u5979\u81ea\u5df1\u7684\u72c2\u547c\uff0c\u7ec8\u4e8e\uff0c\u5979 \u4ec0\u4e48\u58f0\u97f3\u90fd\u4e0d\u518d\u80fd\u542c\u5230\u3002 \u3000\u3000\u5979\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u592a\u7a81\u7136\u7684\u523a\u6fc0\uff0c\u592a\u6df1\u5207\u7684\u75db\u82e6\uff0c\u4f7f\u5f97\u8fd9\u7eaf\u771f\u7684\u5973\u5b69\u5b50\uff0c\u7ec8\u4e8e\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7b49\u5230\u5979\u9192\u6765\u7684\u65f6\u5019\uff0c\u592a\u9633\u5df2\u7ecf\u4e0b\u5c71\u4e86\u3002 \u3000\u3000\u5979\u7f13\u7f13\u5c55\u5f00\u773c\uff0c\u6f2b\u5c71\u7684\u5915\u9633\uff0c\u6b63\u707f\u70c2\u5730\u6620\u7167\u5728\u5979\u8138\u4e0a\uff0c\u56db\u9762\u98ce\u5439\u6797\u6728\uff0c\u8349\u6620\u5915\u9633\uff0c\u5979\u6b64 \u523b\u7adf\u662f\u7f6e\u8eab\u5728\u4e00\u5904\u6811\u6797\u4e2d\u7684\u4e00\u65b9\u9752\u77f3\u4e0a\u3002 \u3000\u3000\u201c\u6211\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5979\u8111\u6d77\u4e2d\u662f\u4e00\u7247\u7a7a\u767d\uff0c\u5979\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u5979\u5988\u5988\u600e\u6837\u79bb\u5f00\u4e86\u90a3\u51cc\u7a7a\u98de\u9601\uff0c \u600e\u4e48\u8c28\u614e\u5730\u5e26\u7740\u5979\u4ece\u4e00\u6761\u7279\u5236\u7684\u4e91\u68af\u4e0a\uff0c\u6e21\u8fc7\u90a3\u6df1\u6c89\u7684\u7edd\u671b\uff0c\u7a7f\u8fc7\u90a3\u6d53\u5bc6\u7684\u4e1b\u6797\uff0c\u6765\u5230\u8fd9 \u91cc\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5979\u751a\u81f3\u4e5f\u4e0d\u8bb0\u5f97\u5979\u6655\u53a5\u4e4b\u524d\u6240\u53d1\u751f\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u6240\u6709\u7684\u4e00\u5207\u4e8b\uff0c\u5374\u90fd\u50cf\u6012\u6f6e\u4f3c\u5730\u6d8c\u5230\u5979\u5fc3\u623f\uff0c\u5979\u75db\u82e6\u5730\u547b\u541f\u4e00\u58f0\uff0c\u60f3 \u6323\u624e\u7740\u7ad9\u8d77\u6765\uff0c\u4f46\u662f\u4e00\u53cc\u81c2\u8180\u7acb\u523b\u6e29\u67d4\u5730\u62e5\u4f4f\u5979\uff0c\u4e8e\u662f\uff0c\u5979\u53d1\u73b0\u81ea\u5df1\u6b64\u523b\u4ecd\u7136\u662f\u8eba\u5728\u5979\u6148\u6bcd \u6000\u91cc\uff01 \u3000\u3000\u4e8e\u662f\uff0c\u5979\u5fcd\u4e0d\u4f4f\u53c8\u6251\u5411\u8fd9\u6e29\u6696\u7684\u6000\u62b1\uff0c\u653e\u58f0\u75db\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u5988\u5988\uff0c\u662f\u5979\u4eec\u5bb3\u6b7b\u4e86\u4ed6\uff0c\u5979\u4eec\u5bb3\u6b7b\u4e86\u4ed6\u2026\u2026\u6211\u8981\u4e3a\u4ed6\u62a5\u4ec7\uff0c\u6211\u4e00\u5b9a\u8981\u4e3a\u4ed6\u62a5\u4ec7\u7684\uff01\u201d \u3000\u3000\u5979\u75db\u54ed\u7740\uff0c\u547c\u558a\u7740\uff0c\u8f9b\u82e6\u4e86\uff0c\u75b2\u5026\u4e86\uff0c\u4e5f\u4f24\u5fc3\u4e86\u7684\u5b59\u654f\uff0c\u65e0\u8a00\u5730\u62e5\u62b1\u7740\u5979\uff0c\u6b64\u65f6\u6b64\u523b\uff0c \u5979\u53c8\u80fd\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u5415\u5357\u4eba\uff0c\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u4e5f\u662f\u5979\u6df1\u6df1\u559c\u6b22\u7740\u7684\uff0c\u8fd9\u5e74\u8f7b\u4eba\u82e5\u662f\u6b7b\u4e86\uff0c\u5979\u4e5f\u4f1a\u4f24\u5fc3\uff0c\u96be\u53d7\uff0c\u5979 \u8bb0\u5f97\u4e0a\u6b21\u8fd9\u5e74\u8f7b\u4eba\u4e3a\u4e86\u81ea\u5df1\u7684\u5973\u513f\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u5979\u662f\u5982\u4f55\u5730\u62c5\u5fc3\uff0c\u662f\u5982\u6b64\u5730\u7167\u987e\u4ed6\uff0c\u751a\u81f3\u6bd4 \u62c5\u5fc3\u5979\u5973\u513f\uff0c\u7167\u987e\u5979\u5973\u513f\u8fd8\u8981\u6df1\u539a\u591a\u4e86\u3002\u540e\u6765\uff0c\u4fa5\u5e78\u4ed6\u80fd\u9047\u7740\u5947\u4eba\uff0c\u4e0d\u4f46\u4f24\u52bf\u597d\u8f6c\uff0c\u8fd8\u6709\u5947 \u9047\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u4ed6\u7ec8\u4e8e\u6b7b\u4e86\uff0c\u662f\u4e3a\u4e86\u5979\u59ca\u59b9\u6b7b\u7684\uff0c\u5979\u5fc3\u91cc\u80fd\u4e0d\u96be\u53d7\uff1f\u5979\u53e3\u4e2d\u5583\u5583\u5730\u5b89\u6170\u7740\u5979\u7684 \u7231\u5973\uff0c\u5979\u7684\u5fc3\uff0c\u5374\u5728\u7ede\u75db\u7740\u3002 \u3000\u3000\u5979\u60f3\u95f4\u82cd\u5929\uff0c\u5bf9\u8fd9\u52c7\u6562\u800c\u6b63\u76f4\u7684\u70ed\u8840\u5c11\u5e74\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u6837\u6b8b\u9177\u3002 \u3000\u3000\u4f46\u662f\u5915\u9633\u867d\u4ecd\u8f89\u714c\uff0c\u82cd\u5929\u5374\u6c38\u65e0\u8bed\uff0c\u53ea\u6709\u5979\u7684\u7231\u5973\u7684\u60b2\u6ce3\uff0c\u6df7\u5408\u5728\u545c\u54bd\u7740\u7684\u665a\u98ce\u91cc\uff0c\u5927 \u5730\uff0c\u5df2\u53c8\u5c06\u88ab\u9ed1\u8863\u7b3c\u7f69\uff01\u4eba\u4eec\uff0c\u4e5f\u53c8\u5c06\u5728\u9ed1\u6697\u4e2d\u5b89\u606f\uff0c\u4f46\u662f\uff0c\u5979\u5fc3\u4e2d\u6697\u60f3\uff0c\u5415\u5357\u4eba\uff0c\u662f\u6c38\u8fdc \u4f1a\u6d3b\u5728\u5979\u5fc3\u91cc\u7684\uff0c\u4e0d\u4f46\u6d3b\u5728\u5979\u5fc3\u91cc\uff0c\u8fd8\u4f1a\u6d3b\u5728\u8bb8\u591a\u4eba\u5fc3\u91cc\uff0c\u4f60\u8bf4\u662f\u5417\uff1f \u90a3\u4e48\u3002\u8ba9\u6211\u6084\u6084\u5730\u544a\u8bc9\u4f60\u2026\u2026 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u516b\u5341\u4e5d\u7ae0\u3000\u7384\u51b0\u70c8\u706b>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u516b\u5341\u4e5d\u7ae0\u3000\u7384\u51b0\u70c8\u706b \u3000\u3000\u90a3\u4e48\uff0c\u8ba9\u6211\u6084\u6084\u5730\u544a\u8bc9\u4f60\u2026\u2026 \u3000\u3000\u5c31\u5728\u8fd9\u66b4\u98ce\u4f9d\u4f9d\uff0c\u5915\u9633\u5982\u706b\uff0c\u9759\u9759\u7684\u521d\u6625\u9ec4\u660f\uff0c\u5c31\u5728\u5b59\u654f\u4e0e\u51cc\u7433\u8fd9\u4e00\u53cc\u5386\u5c3d\u6ca7\u6851\u7684\u6bcd \u5973\uff0c\u6b63\u81ea\u65e0\u8a00\u5730\u76f8\u5bf9\u62e5\u6ce3\u7684\u65f6\u5019\u3002 \u3000\u3000\u6811\u6797\u5916\uff0c\u5d0e\u5c96\u7684\u5c71\u9053\u4e0a\uff0c\u4e00\u4e2a\u6c88\u9ed8\u800c\u5b89\u8be6\u7684\u5c11\u5e74\uff0c\u6b63\u7528\u4ed6\u90a3\u4e00\u53cc\u6e05\u6f88\u800c\u660e\u4eae\u7684\u76ee\u5149\uff0c\u9759 \u9759\u5730\u81ea\u63a9\u6620\u7684\u6797\u6728\u4e2d\uff0c\u671b\u7740\u5979\u4eec\uff0c\u72b9\u5e26\u6599\u4e4b\u610f\u7684\u521d\u6625\u66ae\u98ce\uff0c\u5377\u8d77\u4e86\u7070\u7802\u4e0e\u5c18\u571f\uff0c\u5377\u5728\u4ed6\u5728\u90a3 \u8eab\u6de1\u9ec4\u8272\u7684\u8863\u886b\u4e0a\uff0c\u4ed6\u7684\u76ee\u5149\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u8f6c\u52a8\u4e00\u4e0b\uff01 \u3000\u3000\u6e10\u6e10\u5730\u2014\u2014 \u3000\u3000\u8fd9\u6e05\u6f88\u800c\u660e\u4eae\u7684\u76ee\u5149\uff0c\u8f7b\u8f7b\u5730\u8499\u4e0a\u4e86\u4e00\u5c42\u6726\u80e7\u7684\u8ff7\u60d8\uff0c\u7a7f\u8fc7\u8fd9\u5c42\u8ff7\u60d8\uff0c\u7fe0\u7eff\u7684\u5c0f\u6797\uff0c\u6de1 \u9ec4\u7684\u5c18\u571f\uff0c\u4f3c\u4e4e\u5168\u90fd\u53d8\u6210\u4e86\u4e00\u7247\u8f7b\u76c8\u7684\u7c89\u7ea2\uff0c\u800c\u8fd9\u4e00\u7247\u7c89\u7ea2\u4e2d\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u5c04\u51fa\u4e86\u5723\u6d01\u7684\u5149 \u8292\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u832b\u7136\u5f00\u59cb\u79fb\u52a8\u7740\u81ea\u5df1\u7684\u811a\u6b65\uff0c\u8f7b\u5fae\u800c\u7f13\u6162\u5730\u5411\u5979\u4eec\u8d70\u53bb\uff0c\u54ed\u6ce3\u7684\u58f0\u97f3\u9010\u6e10\u5fae\u5f31\uff0c \u800c\u4ed6\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u5374\u9010\u6e10\u52a0\u54cd\u3002 \u3000\u3000\uff0c\u5b59\u654f\u67f3\u7709\u8f7b\u98a6\uff0c\u7a81\u5730\u8f6c\u8eab\u4f4e\u53f1\uff1a \u3000\u3000\u201c\u662f\u8c01\uff01\u201d \u3000\u3000\u79fb\u52a8\u7740\u7684\u5c11\u5e74\u500f\u7136\u987f\u4f4f\u811a\u6b65\uff0c\u4ed6\u7684\u5fc3\u623f\u867d\u7136\u8df3\u52a8\u5f97\u90a3\u4e48\u6025\u907d\uff0c\u4ed6\u7684\u76ee\u5149\u4e2d\u867d\u5df2\u6d41\u9732\u51fa\u592a \u591a\u7684\u70ed\u60c5\uff01 \u3000\u3000\u4f46\u662f\u2026\u2026 \u3000\u3000\u4ed6\u7684\u9762\u5bb9\u5374\u4ecd\u7136\u662f\u5b89\u7965\u800c\u6c88\u9759\u7684\uff0c\u6e05\u6670\u5206\u660e\u7684\u8f6e\u5ed3\u4e0e\u7ebf\u6761\uff0c\u5c31\u50cf\u662f\u4e0a\u53e4\u7684\u667a\u8005\uff0c\u5728\u575a\u786c \u7684\u82b1\u5188\u77f3\u4e0a\u96d5\u6210\u7684\u77f3\u50cf\uff01 \u3000\u3000\u5728\u6ee1\u5929\u5ae3\u7ea2\u7684\u5915\u9633\u4e0b\uff0c\u51cc\u7433\u62bd\u6ce3\u7740\u62ac\u8d77\u5934\u6765\uff0c\u79cb\u6ce2\u4e00\u8f6c\u3002 \u3000\u3000\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u5979\u62b9\u53bb\u4e86\u9762\u4e0a\u7684\u6cea\u75d5\uff0c\u8131\u53e3\u60ca\u547c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u660e\u4eae\u7684\u76ee\u5149\u4e2d\uff0c\u7a81\u5730\u53c8\u95ea\u8fc7\u4e00\u4e1d\u66f4\u660e\u4eae\u7684\u5149\u8292\uff0c\u6c88\u91cd\u7684\u5fc3\u623f\u8df3\u52a8\u4f3c\u4e4e\u4e5f\u56e0\u7740\u5979\u4ecd \u7136\u6ca1\u6709\u5fd8\u53bb\u81ea\u5df1\uff0c\u800c\u8f7b\u76c8\u5730\u98de\u626c\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u5f2f\u4e0b\u8170\uff0c\u8eac\u8eab\u4e00\u793c\uff1a \u3000\u3000\u201c\u5c0f\u9119\u953a\u9759\uff0c\u65e0\u610f\u95ef\u6765\u6b64\u95f4\uff0c\u5982\u592b\u4eba\u4e0d\u5acc\u5192\u6627\uff0c\u5c0f\u9119\u4e0d\u6562\u8bf7\u95ee\u592b\u4eba\uff0c\u662f\u5426\u53ef\u6709\u5bb9\u5c0f\u9119\u6548 \u52b3\u4e4b\u5904\uff1f\u201d \u3000\u3000\u4ed6\u867d\u662f\u5728\u5411\u5b59\u654f\u8bf4\u8bdd\uff0c\u4f46\u76ee\u5149\u5374\u4ecd\u505c\u7559\u5728\u51cc\u7433\u8eab\u4e0a\u3002 \u3000\u3000\u5b59\u654f\u5446\u5446\u5730\u671b\u7740\u8fd9\u5c11\u5e74\uff0c\u5979\u6b64\u523b\u5df2\u77e5\u9053\u4ed6\u4e0e\u81ea\u5df1\u7684\u7231\u5973\u662f\u76f8\u8bc6\u7684\uff0c\u4f46\u4f55\u65f6\u76f8\u8bc6\uff1f\u5982\u4f55\u76f8 \u8bc6\uff1f\u5979\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e8e\u662f\u8fd9\u9971\u7ecf\u5fe7\u60a3\u7684\u6bcd\u4eb2\uff0c\u4fbf\u96be\u514d\u4e3a\u81ea\u5df1\u5929\u771f\u7684\u5973\u513f\u62c5\u5fc3\uff0c\u62c5\u5fc3\u4e4b \u5916\uff0c\u53c8\u6709\u4e9b\u5947\u602a\uff0c\u5bf9\u8fd9\u5c11\u5e74\u5b89\u7965\u7684\u4e3e\u6b62\uff0c\u6c88\u9759\u7684\u9762\u5bb9\uff0c\u5979\u5e76\u65e0\u4e1d\u6beb\u62c5\u5fc3\uff0c\u5947\u602a\u4e4b\u5904\uff0c\u4f46\u662f\u4ed6 \u8fd9\u4e00\u53cc\u773c\u775b\u4e2d\u707c\u4eba\u7684\u706b\u7130\uff0c\u5373\u4f7f\u5979\u62c5\u5fc3\u800c\u5947\u602a\u3002 \u3000\u3000\u5df2\u7ecf\u6e21\u8fc7\u4e86\u751f\u547d\u4e2d\u5927\u534a\u7eda\u70c2\u5c81\u6708\u7684\u5b59\u654f\uff0c\u53ef\u8bf4\u771f\u7684\u662f\u6d89\u4e16\u5df2\u6df1\u4e86\uff0c\u800c\u4e14\u5929\u751f\u5979\u5c31\u6709\u4e00\u79cd \u8d85\u4e8e\u5e38\u4eba\u7684\u9547\u9759\uff0c\u4e5f\u6709\u4e00\u53cc\u6d1e\u6089\u4e16\u4eba\u7684\u76ee\u5149\uff0c\u53ef\u662f\u5979\u5374\u4ece\u672a\u60f3\u5230\u8fc7\u4e00\u4e2a\u5982\u6b64\u5b89\u7965\u6c88\u9ed8\u7684\u5c11 \u5e74\uff0c\u7adf\u4f1a\u6709\u6b64\u707c\u4eba\u7684\u76ee\u5149\uff0c\u8fd9\u6b63\u5982\u7ec8\u5e74\u4e07\u6234\u7384\u51b0\u4e0b\u63a9\u8986\u7684\u706b\u5c71\uff0c\u6b64\u523b\u5df2\u56e0\u4e00\u4e2a\u7a81\u5982\u5176\u6765\u7684\u53d8 \u6545\u4e0e\u6fc0\u52a8\u800c\u88c2\u5f00\u4e86\u4e00\u4e1d\u7f3a\u53e3\uff0c\u4e8e\u662f\u88ab\u6291\u5236\u5f97\u592a\u4e45\u4e86\u7684\u706b\u7130\uff0c\u4fbf\u4e0d\u80fd\u81ea\u7981\u5730\u4ece\u8fd9\u7f3a\u53e3\u4e2d\u55b7\u51fa\u4e86 \u706b\u82b1\uff01 \u3000\u3000\u867d\u7136\u5979\u77e5\u9053\u5411\u4e24\u4e2a\u5728\u6df1\u5c71\u6797\u6728\u4e2d\u54ed\u6ce3\u7684\u5987\u5973\u4f38\u51fa\u63f4\u624b\uff0c\u6b63\u662f\u884c\u4fa0\u6c5f\u6e56\uff0c\u4ed7\u4e49\u4eba\u95f4\u7684\u6e38\u4fa0 \u8c6a\u6770\u6240\u5e94\u6709\u7684\u672c\u4efd\uff0c\u4f46\u662f\u8fd9\u5c11\u5e74\u4e00\u53cc\u707c\u4eba\u7684\u76ee\u5149\uff0c\u5374\u4f7f\u5979\u6123\u4e86\u534a\u664c\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\u4ed6\u8fd9\u4efd \u5584\u610f\u7684\u8be2\u95ee\u3002 \u3000\u3000\u953a\u9759\u7b14\u76f4\u5730\u4f2b\u7acb\u7740\uff0c\u5374\u4e1d\u6beb\u672a\u56e0\u5979\u6ca1\u6709\u56de\u7b54\u81ea\u5df1\u7684\u8bdd\u800c\u4e0d\u5b89\uff0c\u4ed6\u7d27\u95ed\u7740\u5634\u5507\uff0c\u95ea\u52a8\u7740\u76ee \u5149\u3002 \u3000\u3000\u90a3\u77e5\u51cc\u7433\u5374\u7a81\u5730\u8f7b\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u4f60\u6765\u4e86\u6b63\u597d\uff0c\u6211\u6b63\u8981\u627e\u4f60\uff01\u201d \u3000\u3000\u5b59\u654f\u5fc3\u5934\u4e00\u8df3\uff0c\u5f00\u59cb\u60ca\u5f02\uff0c\u4e0d\u77e5\u9053\u5979\u7684\u7231\u5973\u600e\u66fe\u7a81\u5730\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u5374\u89c1\u953a\u9759\u5b89\u7965\u6c88\u9759\u7684\u9762\u5bb9\uff0c\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u8f7b\u5fae\u7684\u626d\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u201c\u59d1\u5a18\u6709\u4f55\u5429\u5490\uff1f\u5c0f\u9119\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u8bed\u58f0\u7f13\u6162\u4f4e\u6c88\uff0c\u5374\u663e\u7136\u662f\u5728\u6781\u56f0\u96be\u7684\u514b\u5236\u7740\u3002\u5b59\u654f\u4f38\u51fa\u624b\u638c\uff0c\u63e1\u4f4f\u4e86\u5979\u7231\u5973\u7684\u67d4\u8351\uff0c\u4ed6 \u4e0d\u613f\u7231\u5973\u518d\u8bf4\u51fa\u4efb\u4f55\u4e00\u53e5\u8db3\u4ee5\u4ee4\u5979\u60ca\u5f02\u7684\u8bdd\u6765\uff0c\u5c31\u50cf\u65b9\u624d\u6240\u8bf4\u7684\u90a3\u53e5\u8bdd\u4e00\u6837\u3002 \u3000\u3000\u5374\u542c\u51cc\u7433\u53c8\u81ea\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u65b9\u624d\u4ea4\u7ed9\u5357\u2026\u2026\u201c\u94c1\u621f\u6e29\u4faf\u201d\u5415\u5927\u4fa0\u90a3\u5f20\u5b57\u67ec\uff0c\u4e0a\u9762\u5199\u7684\u662f\u4ec0\u4e48\uff0c\u4f60\u53ef\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u953a\u9759\u94a2\u7259\u5fae\u54ac\uff0c\u6c88\u58f0\u9053\uff1a \u3000\u3000\u201c\u5bb6\u5e08\u867d\u547d\u5c0f\u9119\u5c06\u5b57\u67ec\u4ea4\u7ed9\u5415\u5927\u4fa0\uff0c\u4e0a\u9762\u7684\u5b57\u8ff9\uff0c\u5c0f\u9119\u5374\u672a\u5c1d\u5f97\u89c1\uff01\u201d \u3000\u3000\u51cc\u7433\u773c\u4e00\u5408\uff0c\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u4fbf\u53c8\u593a\u7736\u800c\u51fa\uff0c\u5374\u542c\u953a\u9759\u7f13\u7f13\u53c8\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\u5982\u6b64\u4f24\u5fc3\uff0c\u96be\u9053\u662f\u5415\u5927\u4fa0\u5df2\u4e0d\u8f9e\u800c\u522b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u51cc\u7433\u555c\u6ce3\u7740\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u953a\u9759\u7f13\u7f13\u8f6c\u8fc7\u76ee\u5149\uff0c\u51fa\u795e\u5730\u51dd\u89c6\u7740\u4ece\u6797\u6f0f\u4e0b\u7684\u4e00\u7247\u6563\u788e\u7684\u5915\u9633 \u5f71\u5b50\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\u82e5\u662f\u60f3\u5bfb\u8bbf\u5415\u5927\u4fa0\uff0c\u5728\u4e94\u6708\u7aef\u9633\uff0c\u81f3\u5609\u5174\u5357\u6e56\u70df\u96e8\u697c\u5934\u4e00\u884c\uff0c\u4fbf\u53ef\u5bfb\u5f97\u5415\u5927\u4fa0\u7684 \u4fa0\u8e2a\u3002\u201d \u3000\u3000\u51cc\u7433\u500f\u7136\u5f20\u5f00\u773c\u6765\uff1a \u3000\u3000\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u5915\u9633\u7684\u5149\u5f71\uff0c\u6620\u4e86\u953a\u9759\u773c\u4e2d\u8f7b\u7ea2\u8272\u7684\u8ff7\u60d8\uff0c\u4f3c\u4e4e\u5df2\u8f6c\u53d8\u6210\u4e00\u7247\u6de1\u7070\u7684\u6726\u80e7\uff0c\u4f46\u662f\u4ed6\u7684\u76ee \u5149\uff0c\u5374\u4ecd\u672a\u8f6c\u52a8\uff0c\u53ea\u662f\u7f13\u7f13\u63a5\u9053\uff1a \u3000\u3000\u201c\u4e94\u6708\u7aef\u9633\uff0c\u4e43\u662f\u5bb6\u5e08\u4e0e\u5415\u5927\u4fa0\u7ea6\u89c1\u6664\u4f1a\u4e4b\u65f6\uff0c\u5415\u5927\u4fa0\u4e07\u65e0\u4e0d\u53bb\u4e4b\u7406\uff0c\u59d1\u5a18\u4f46\u8bf7\u653e\u5fc3\u597d \u4e86\u3002\u201d \u3000\u3000\u51cc\u7433\u6084\u7136\u95ed\u8d77\u773c\u775b\uff0c\u5583\u5583\u9053\uff1a \u3000\u3000\u201c\u4e94\u6708\u7aef\u9633\u2026\u2026\u5357\u6e56\u70df\u96e8\u697c\u5934\u2026\u2026\u4ed6\u4e00\u5b9a\u4f1a\u53bb\u7684\uff0c\u4e00\u5b9a\u4f1a\u53bb\u7684\u2026\u2026\u5988\u2026\u2026\u6211\u4e5f\u4e00\u5b9a\u8981 \u53bb\u3002\u201d \u3000\u3000\u5b59\u654f\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u5979\u6df1\u5207\u5730\u4e86\u89e3\u5979\u5973\u513f\uff0c\u6b63\u5982\u5979\u6df1\u5207\u5730\u4e86\u89e3\u5979\u81ea\u5df1\u8863\u4e0a\u7684\u647a\u75d5\u4e00\u6837\uff0c \u5979\u77e5\u9053\u5979\u5973\u513f\u6b64\u523b\u867d\u7136\u4f24\u5fc3\uff0c\u5374\u672a\u7edd\u671b\u3002 \u3000\u3000\u76f8\u7231\u7740\u7684\u4eba\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u76f8\u4fe1\u88ab\u81ea\u5df1\u6240\u7231\u7684\u4eba\u771f\u7684\u6b7b\u4e86\uff0c\u9664\u5979\u80fd\u4eb2\u773c\u770b\u5230\u4ed6\u5df2\u65e0\u751f\u606f\u7684\u8eaf \u4f53\uff0c\u4eb2\u624b\u629a\u6478\u5230\u4ed6\u51b0\u51c9\u7684\u808c\u80a4\u2026\u2026 \u3000\u3000\u800c\u51cc\u7433\uff0c\u6b63\u662f\u8fd9\u6837\uff0c\u5979\u6df1\u4fe1\u5415\u5357\u4eba\u4f1a\u5947\u8ff9\u822c\u5730\u4ece\u90a3\u7edd\u58d1\u4e2d\u9003\u51fa\u6765\uff0c\u5947\u8ff9\u822c\u5730\u51fa\u73b0\u5728\u5979\u773c \u524d\u3002 \u3000\u3000\u5b59\u654f\u5fcd\u4e0d\u4f4f\u6c88\u91cd\u5730\u53f9\u606f\u7740\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u4ed6\u4e0d\u4f1a\u53bb\u7684\uff01\u201d \u3000\u3000\u8fd9\u77ed\u77ed\u4e94\u4e2a\u5b57\uff0c\u4ece\u4e0d\u5fcd\u4f7f\u7231\u5973\u4f24\u5fc3\u7684\u6bcd\u4eb2\u53e3\u4e2d\u8bf4\u51fa\uff0c\u771f\u662f\u4ef6\u56f0\u96be\u7684\u4e8b\uff0c\u953a\u9759\u76ee\u5149\u4e00\u8f6c\uff0c \u95ea\u7535\u822c\u56de\u5230\u51cc\u7433\u8eab\u4e0a\uff0c\u50cf\u662f\u60f3\u95ee\uff1a \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5374\u89c1\u51cc\u7433\u53ea\u662f\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4f1a\u53bb\u7684\u2026\u2026\u4ed6\u4e0d\u4f1a\u6b7b\uff0c\u50cf\u4ed6\u90a3\u6837\u7684\u4eba\u82e5\u662f\u6b7b\u4e86\uff0c\u8001\u5929\u7237\u4e0d\u662f\u592a\u4e0d\u516c\u5e73\u4e86\u5417\uff1f\u4f60\u8bf4\u662f \u5417\uff1f\u2026\u2026\uff0e\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u5979\u7b2c\u4e00\u53e5\u201c\u4f60\u8bf4\u662f\u5417\uff1f\u201d\u662f\u95ee\u5979\u7684\u6bcd\u4eb2\uff0c\u7b2c\u4e8c\u53e5\u201c\u4f60\u8bf4\u662f\u5417\uff1f\u201d\u5374\u662f\u95ee\u5411\u953a\u9759\u3002 \u3000\u3000\u5f53\u5979\u90a3\u53cc\u6cea\u75d5\u672a\u5e72\u7684\u79cb\u6ce2\u8f6c\u5411\u953a\u9759\u7684\u65f6\u5019\uff0c\u4ed6\u7acb\u523b\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u907f\u5f00\u4e86\u5979\u7684\u76ee\u5149\uff0c\u56e0\u4e3a\u6b64 \u523b\u4ed6\u7684\u773c\u4e2d\uff0c\u6709\u7740\u592a\u591a\u5979\u6c38\u8fdc\u4e0d\u8be5\u770b\u5230\uff0c\u4ed6\u4e5f\u6c38\u8fdc\u4e0d\u613f\u8ba9\u5979\u770b\u5230\u7684\u4e8b\uff0c\u4f46\u662f\u4ed6\u4ecd\u5fcd\u4e0d\u4f4f\u8131\u53e3 \u95ee\u9053\uff1a \u3000\u3000\u201c\u4e8c\u4f4d\u5982\u6b64\u8bf4\u6765\uff0c\u96be\u9053\u5415\u5927\u4fa0\u5df2\u9047\u7740\u4ec0\u4e48\u4e0d\u6d4b\u4e4b\u7978\u4e48\uff1f\u201d \u3000\u3000\u51cc\u7433\u53c8\u81ea\u4e0d\u53ef\u6291\u6b62\u5730\u555c\u6ce3\u8d77\u6765\uff0c\u5b59\u654f\u5374\u60b2\u4f24\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u5979\u8fd8\u4e0d\u77e5\u9053\u8fd9 \u5c11\u5e74\u662f\u8c01\uff0c\u66f4\u4e0d\u77e5\u9053\u4ed6\u5c31\u662f\u81ea\u5df1\u4ec7\u4eba\u8427\u65e0\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u5979\u53ea\u662f\u8f7b\u53f9\u7740\u9053\uff1a \u3000\u3000\u201c\u5357\u4eba\u786e\u5df2\u9047\u7740\u4e86\u4e0d\u5e78\u4e4b\u4e8b\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u5509\uff01\u80fd\u591f\u6d3b\u547d\u7684\u5e0c\u671b\u4e0d\u591a\uff0c\u5e0c\u671b\u4f60\u56de\u53bb \u8f6c\u544a\u4ee4\u5e08\uff0c\u7aef\u9633\u4e4b\u4f1a\uff0c\u4ed6\u53ea\u6015\u2026\u2026\u5509\uff01\u5df2\u7ecf\u4e0d\u80fd\u8d74\u7ea6\u4e86\uff01\u201d \u3000\u3000\u953a\u9759\u76ee\u5149\u4e00\u8f6c\uff0c\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u5415\u5927\u4fa0\u4eca\u751f\u7adf\u7136\u65e0\u6cd5\u89c1\u5230\u5bb6\u5e08\u4e86\uff01\u5509\uff01\u60f3\u6765\u5415\u5927\u4fa0\u867d\u6b7b\u4ea6\u96be\u669d\u76ee\uff0c\u8fd9\u771f\u7684\u662f\u5929 \u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u7978\u798f\u3002\u4eca\u65e5\u6e05\u6668\uff0c\u5f1f\u5b50\u65b9\u81ea\u89c1\u5230\u5415\u5927\u4fa0\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u6b64\u523b\u5df2\u7136\u2026\u2026\u201d \u3000\u3000\u8bed\u672a\u4e86\uff0c\u51cc\u7433\u7a81\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u4e00\u628a\u6293\u7740\u5979\u6bcd\u4eb2\u7684\u8863\u895f\uff0c\u75db\u54ed\u770b\u9053\uff1a \u3000\u3000\u201c\u5988\uff01\u6211\u4eec\u5230\u2026\u2026\u5357\u6e56\u70df\u96e8\u697c\u53bb\u2026\u2026\u201d \u3000\u3000\u5b59\u654f\u53f9\u606f\u7740\uff0c\u6148\u7965\u5730\u62cd\u7740\u5979\u7231\u5973\u7684\u624b\u638c\uff0c\u5979\u4e0d\u5fcd\u518d\u8bf4\u4ee4\u5979\u7231\u5973\u7edd\u671b\u7684\u8bdd\uff0c\u4f46\u662f\u5979\u5374\u53c8\u4e0d \u80fd\u4e0d\u8bf4\uff0c\u4efb\u4f55\u4e00\u4e2a\u4eba\uff0c\u65e0\u8bba\u4ed6\u7684\u6b66\u529f\u591a\u9ad8\uff0c\u82e5\u662f\u5760\u5165\u90a3\u6df1\u4e0d\u89c1\u5e95\u7684\u7edd\u58d1\u4e2d\u53bb\uff0c\u6d3b\u547d\u7684\u5e0c\u671b\uff0c \u5f53\u771f\u6bd4\u6cf0\u5c71\u77f3\u70c2\uff0c\u5317\u6d77\u6c34\u67af\u7684\u673a\u4f1a\u8fd8\u8981\u5c11\u4e9b\u3002 \u3000\u3000\u4e8e\u662f\u5979\u6c88\u91cd\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u50bb\u5b69\u5b50\uff0c\u4eba\u751f\u4e0d\u662f\u795e\u8bdd\uff0c\u6545\u4e8b\uff0c\u4e5f\u6ca1\u6709\u795e\u8bdd\u6545\u4e8b\u90a3\u4e48\u7f8e\u4e3d\u3002\u4eba\u751f\u662f\u6b8b\u9177\u7684\u3002\u4e8b\u5b9e\u66f4\u6b8b \u9177\uff0c\u5047\u5982\u6211\u4eec\u90fd\u662f\u6d3b\u5728\u865a\u5e7b\u7684\u795e\u8bdd\u6545\u4e8b\u4e2d\uff0c\u6211\u4e00\u5b9a\u966a\u4f60\u5230\u5357\u6e56\u53bb\uff0c\u56e0\u4e3a\u53ea\u6709\u5728\u795e\u8bdd\u6545\u4e8b\u91cc\uff0c \u6b7b\u53bb\u7684\u4eba\uff0c\u624d\u80fd\u590d\u751f\u3002\u50bb\u5b69\u5b50\uff0c\u73b0\u5728\u4f60\u96be\u9053\u8fd8\u60f3\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u953a\u9759\u51fa\u795e\u5730\u542c\u7740\uff0c\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u542c\u8fc7\u5982\u6b64\u6e29\u67d4\u7684\u8bed\u53e5\uff0c\u66f4\u672a\u60f3\u5230\uff0c\u5728\u5982\u6b64\u6e29\u67d4\u7684\u8bed \u53e5\u4e2d\uff0c\u7adf\u4f1a\u5305\u6db5\u8fd9\u4e48\u591a\u6df1\u9083\u7684\u4eba\u751f\u54f2\u7406\u3002 \u3000\u3000\u201c\u4eba\u751f\u662f\u6b8b\u9177\u7684\uff0c\u4e8b\u5b9e\u66f4\u6b8b\u9177\uff0c\u5509\u2026\u2026\u4e3a\u4ec0\u4e48\u4eba\u751f\u8fd9\u4e48\u6b8b\u9177\uff0c\u8ba9\u6211\u504f\u504f\u4f1a\u2026\u2026\u201d \u3000\u3000\u4ed6\u7384\u601d\u672a\u7edd\uff0c\u5374\u542c\u51cc\u7433\u53c8\u81ea\u54ed\u558a\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u4f1a\u53bb\u7684\uff0c\u4ed6\u5c31\u662f\u6b7b\u4e86\uff0c\u4ed6\u7684\u9b3c\u9b42\u4e5f\u4f1a\u53bb\uff0c\u6211\u77e5\u9053\uff0c\u4ed6\u7684\u9b3c\u9b42\u4e5f\u4e00\u5b9a\u4f1a\u5230\u70df\u96e8\u697c \u53bb\uff0c\u5c06\u90a3\u4e07\u6076\u7684\u8427\u65e0\u6740\u6b7b\uff01\u201d \u3000\u3000\u5b59\u654f\u5168\u8eab\u4e00\u51db\uff0c\u8131\u53e3\u9053\uff1a \u3000\u3000\u201c\u8427\u65e0\uff01\u201d \u3000\u3000\u5979\u624b\u638c\u7d27\u7d27\u63e1\u4e86\u8d77\u6765\uff0c\u6e29\u67d4\u6148\u7965\u7684\u773c\u6ce2\uff0c\u7a81\u5730\u6ee1\u73b0\u6028\u6bd2\u4e4b\u8272\u3002 \u3000\u3000\u5979\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u671b\u5411\u953a\u9759\uff0c\u8fd9\u6ee1\u542b\u6028\u6bd2\u7684\u76ee\u5149\uff0c\u50cf\u662f\u4e00\u67c4\u5229\u5203\uff0c\u7b14\u76f4\u5730\u6233\u8fdb\u953a\u9759 \u7684\u5fc3\u623f\u91cc\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u4e00\u9635\u6f88\u9aa8\u7684\u5bd2\u610f\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u4fbf\u5df2\u5e03\u6ee1\u4ed6\u7684\u5168\u8eab\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5782\u4e0b\u76ee\u5149\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff01\u5bb6\u5e08\u6b63\u662f\u5929\u4e89\u6559\u4e3b\u8427\u65e0\u3002\u201d \u3000\u3000\u6bcf\u8bf4\u4e00\u5b57\uff0c\u4ed6\u53ea\u611f\u89c9\u5230\u90a3\u51b0\u51b7\u6028\u6bd2\u7684\u76ee\u5149\uff0c\u4fbf\u50cf\u662f\u53c8\u5728\u4ed6\u5fc3\u623f\u4e2d\u6233\u4e86\u4e00\u5200\u3002\u4ed6\u5f00\u59cb\u77e5\u9053 \u8fd9\u4e00\u53cc\u6bcd\u5973\uff0c\u5fc5\u5b9a\u4e5f\u548c\u81ea\u5df1\u7684\u5e08\u5085\u6709\u7740\u4ec7\u6068\uff0c\u800c\u4e14\u662f\u975e\u5e38\u6df1\u523b\u7684\u4ec7\u6068\uff01 \u3000\u3000\u4ed6\u75db\u82e6\u5730\u5728\u5fc3\u91cc\u547c\u558a\uff1a \u3000\u3000\u201c\u4eba\u751f\u4e3a\u4ec0\u4e48\u90a3\u4e48\u6b8b\u9177\uff1f\u4e3a\u4ec0\u4e48\u504f\u504f\u4f1a\u8ba9\u6211\u9047\u7740\u4e86\u5979\uff1f\u201d \u3000\u3000\u5b59\u654f\u7684\u76ee\u5149\uff0c\u50cf\u662f\u8981\u770b\u7a7f\u8fd9\u5c11\u5e74\u7684\u5fc3\u5e95\u6df1\u5904\u4f3c\u7684\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u5374\u52a8\u4e5f\u4e0d\u52a8\u3002\u5915\u9633\u7684\u5f71\u5b50\u6de1\u4e86\uff0c\u6f2b\u5929\u665a\u971e\uff0c\u4e5f\u7531\u7eda\u70c2\u5f52\u4e8e\u5e73\u6de1\uff0c\u6c88\u91cd\u7684\u66ae\u8272\uff0c\u6084\u6084\u5730 \u6ed1\u8fdb\u4e86\u5c71\u6797\uff0c\u722c\u4e0a\u4ed6\u7684\u9762\u988a\uff0c\u82cd\u767d\u7684\u9762\u8272\uff0c\u5728\u9ed1\u6697\u4e2d\u66f4\u89c1\u82cd\u767d\uff0c\u7070\u9eef\u7684\u76ee\u5149\uff0c\u5728\u9ed1\u6697\u4e2d\u81ea\u4e5f \u66f4\u52a0\u7070\u9eef\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u3002 \u3000\u3000\u5b59\u654f\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u602a\u4f60\uff0c\u6211\u4e0d\u602a\u4f60\u2026\u2026\u4efb\u4f55\u4e00\u4e2a\u4eba\u7684\u4e8b\uff0c\u90fd\u548c\u5176\u4ed6\u7684\u4eba\u65e0\u5173\uff0c\u4f60\u867d\u7136\u662f\u8427\u65e0\u7684\u5f1f \u5b50\uff0c\u4f46\u4e00\u5207\u5374\u548c\u4f60\u6ca1\u6709\u5173\u7cfb\uff0c\u4f60\u2026\u2026\u4f60\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u953a\u9759\u5fae\u5fae\u8fdf\u7591\u4e00\u4e0b\uff0c\u7ec8\u4e8e\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e0a\u4ee3\u6069\u4ec7\uff0c\u4e0d\u6d89\u4e0b\u4ee3\uff0c\u592b\u4eba\u4e4b\u5fc3\u80f8\uff0c\u5f53\u771f\u662f\u5c0f\u9119\u751f\u5e73\u4ec5\u89c1\uff0c\u65e0\u8bba\u5bb6\u5e08\u4e0e\u592b\u4eba\u6069\u4ec7\u5982\u4f55 \u4e86\u7ed3\uff0c\u4e5f\u65e0\u8bba\u5c0f\u9119\u8eab\u5728\u4f55\u65b9\uff0c\u5c0f\u9119\u6c38\u8fdc\u4f1a\u4ee5\u5fc3\u9999\u4e00\u74e3\uff0c\u9065\u795d\u592b\u4eba\u5065\u94a1\u3002\u5415\u5927\u4fa0\u4e4b\u4e0d\u5e78\uff0c\u5c0f\u9119 \u4ea6\u662f\u60b2\u61be\u826f\u6df1\uff0c\u5415\u5927\u4fa0\u5728\u5929\u4e4b\u7075\uff0c\u60f3\u5fc5\u80fd\u6df1\u77e5\u5c0f\u9119\u5fc3\u610f\uff0c\u53ea\u6068\u5c0f\u9119\u4eca\u751f\u5df2\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u8eac\u8eab\u4e00\u63d6\uff0c\u8f6c\u8eab\u800c\u53bb\uff0c\u4ec5\u5b58\u4e00\u7ebf\u7684\u6b8b\u971e\uff0c\u5c06\u4ed6\u7684\u8eab\u5f71\u957f\u957f\u7684 \u5370\u5728\u5730\u4e0a\uff0c\u5c31\u50cf\u662f\u4ed6\u5fc3\u91cc\u7684\u60b2\u54c0\u4e00\u6837\u6c88\u91cd\uff01 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e5d\u5341\u7ae0\u3000\u5faa\u5faa\u5584\u8bf1>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u5341\u7ae0\u3000\u5faa\u5faa\u5584\u8bf1 \u3000\u3000\u5b59\u654f\u7684\u76ee\u5149\uff0c\u8ddf\u968f\u7740\u8fd9\u9880\u957f\u7684\u8eab\u5f71\uff0c\u5979\u5fc3\u91cc\u7a81\u5730\u52a0\u4e86\u4e00\u4efd\u65b0\u8d77\u7684\u60b2\u54c0\uff0c\u800c\u5979\u6df1\u77e5\u8fd9\u4efd\u60b2 \u54c0\u5e76\u975e\u4e3a\u4e86\u81ea\u5df1\uff0c\u4ea6\u975e\u4e3a\u4e86\u522b\u4eba\uff0c\u5374\u662f\u4e3a\u4e86\u8fd9\u5df2\u88ab\u547d\u8fd0\u7684\u957f\u7ebf\u7d27\u7f1a\u4f4f\u4e0d\u80fd\u52a8\u5f39\u7684\u5c11\u5e74\u3002 \u3000\u3000\u56de\u8fc7\u5934\uff0c\u5979\u53d1\u89c9\u51cc\u7433\u5e26\u6cea\u7684\u773c\u775b\uff0c\u4e5f\u671b\u5728\u8fd9\u5c11\u5e74\u6c88\u91cd\u7684\u80cc\u5f71\u4e0a\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u5ffd\u7136\u89c9\u5f97\u5979\u6709\u4e00\u79cd\u5c06\u8fd9\u5c11\u5e74\u81ea\u90aa\u6076\u4e4b\u4e2d\u62ef\u6551\u51fa\u6765\u7684\u5fc5\u8981\uff0c\u5bf9\u4e8e\u751f\u547d\uff0c\u5979 \u4e00\u76f4\u4e86\u89e3\u5f97\u6700\u6df1\u523b\uff0c\u4e3a\u4e86\u5979\u7684\u7231\u5973\uff0c\u4e5f\u4e3a\u4e86\u590d\u4ec7\uff0c\u5979\u6ca1\u6709\u88ab\u60b2\u54c0\u846c\u9001\uff0c\u53cd\u800c\u575a\u5f3a\u5730\u6d3b\u5230\u73b0 \u5728\u3002 \u3000\u3000\u800c\u73b0\u5728\uff0c\u5979\u53c8\u53d1\u89c9\uff0c\u751f\u547d\u7684\u610f\u4e49\u867d\u6709\u8bb8\u591a\uff0c\u4f46\u521b\u9020\u5b87\u5b99\u95f4\u7ee7\u8d77\u7684\u751f\u547d\uff0c\u5374\u662f\u8fd9\u8bb8\u591a\u610f\u4e49 \u4e2d\u6700\u6700\u91cd\u8981\u7684\u4e00\u4e2a\uff01 \u3000\u3000\u201c\u5bf9\u4eba\u7c7b\u6765\u8bf4\uff0c\u62ef\u6551\u4e00\u4e2a\u5584\u826f\u7684\u7075\u9b42\uff0c\u4e00\u5b9a\u8981\u6bd4\u8bdb\u6740\u4e00\u4e2a\u90aa\u6076\u7684\u751f\u547d\u8fd8\u8981\u610f\u4e49\u91cd\u5927\u7684 \u591a\uff01\u201d \u3000\u3000\u5979\u5583\u5583\u5730\u4f4e\u8bed\u7740\uff0c\u7a81\u5730\u62ac\u5934\u558a\u9053\uff1a \u3000\u3000\u201c\u4f60\u2014\u2014\u56de\u6765\uff01\u201d \u3000\u3000\u953a\u9759\u811a\u6b65\u4e00\u987f\uff0c\u7f13\u7f13\u56de\u8fc7\u5934\u6765\uff0c\u9762\u8272\u4f9d\u7136\u662f\u6c88\u9759\u7684\uff0c\u56e0\u4e3a\u6ca1\u6709\u4eba\u80fd\u4ece\u4ed6\u9762\u4e0a\u770b\u51fa\u4ed6\u5fc3\u91cc \u7684\u559c\u60a6\u3002 \u3000\u3000\u4ed6\u6123\u4e86\u534a\u664c\uff0c\u786e\u5b9a\u4e86\u8fd9\u53e5\u8bdd\u7684\u786e\u662f\u5bf9\u81ea\u5df1\u8bf4\u7684\uff0c\u4e8e\u662f\u4fbf\u8d70\u56de\u5b59\u654f\u7684\u8eab\u524d\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u56e0 \u4e3a\u4ed6\u77e5\u9053\u6c88\u9ed8\u6709\u65f6\u4e5f\u6709\u4f1a\u548c\u8be2\u95ee\u4e00\u6837\u3002 \u3000\u3000\u5b59\u654f\u76ee\u5149\u4e00\u8f6c\uff0c\u6c88\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u8ddf\u7740\u8427\u65e0\u6709\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u953a\u9759\u5782\u9996\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u5e7c\u906d\u5b64\u9732\uff0c\u5373\u8499\u5bb6\u5e08\u6536\u7559\uff0c\u6027\u547d\u8840\u9aa8\uff0c\u7686\u662f\u5bb6\u5e08\u6240\u8d50\u3002\u201d \u3000\u3000\u4ed6\u81ea\u7136\u77e5\u9053\u8fd9\u6148\u7965\u7684\u592b\u4eba\u5411\u81ea\u5df1\u95ee\u8fd9\u53e5\u8bdd\u7684\u542b\u610f\uff0c\u800c\u5b59\u654f\u4f55\u5c1d\u542c\u4e0d\u51fa\u4ed6\u56de\u7b54\u81ea\u5df1\u4e00\u6709\u8bed \u4e2d\u7684\u542b\u610f\u3002 \u3000\u3000\u5979\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6709\u8bb8\u591a\u4eba\u4e5f\u548c\u4f60\u4e00\u6837\uff0c\u5e7c\u906d\u5b64\u9732\uff0c\u800c\u4ed6\u4eec\u7684\u7236\u6bcd\uff0c\u5374\u662f\u88ab\u8427\u65e0\u6740\u6b7b\u7684\uff1f\u201d \u3000\u3000\u953a\u9759\u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u5b59\u654f\u53c8\u81ea\u7f13\u7f13\u53f9\u9053\uff1a \u3000\u3000\u201c\u4eba\u4eec\u7acb\u4e16\u5904\u8eab\uff0c\u5bf9\u4e8e\u5584\u6076\u4e4b\u5206\uff0c\u603b\u5e94\u8be5\u8981\u6bd4\u6069\u4ec7\u4e4b\u522b\u770b\u7684\u91cd\u4e9b\uff01\u6211\u77e5\u9053\u4f60\u5f88\u5584\u826f\uff0c\u4e5f \u5f88\u806a\u660e\uff0c\u5e94\u8be5\u542c\u5f97\u51fa\u6211\u8bed\u4e2d\u7684\u610f\u601d\uff01\u201d \u3000\u3000\u953a\u9759\u7684\u5934\u5782\u5f97\u66f4\u4f4e\u4e86\u3002 \u3000\u3000\u5b59\u654f\u76ee\u5149\u518d\u4e00\u8f6c\uff0c\u773c\u775b\u4e2d\u5df2\u6709\u4e86\u6676\u83b9\u7684\u6cea\u5149\uff0c\u5979\u6c88\u58f0\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u5148\u592b\u51cc\u5317\u4fee\uff0c\u4e00\u751f\u6025\u4eba\u4e4b\u96be\uff0c\u800c\u4e14\u53ea\u8981\u542c\u5230\u4eba\u95f4\u6709\u4e0d\u5e73\u7684\u4e8b\uff0c\u4ed6\u7acb\u523b\u4f1a\u632f\u81c2\u800c\u8d77\uff0c\u4f46 \u662f\u2026\u2026\u4ed6\u4e5f\u88ab\u8427\u65e0\u5bb3\u6b7b\u4e86\uff0c\u5bb3\u4ed6\u7684\u4eba\uff0c\u82e5\u662f\u4e3a\u4e86\u6b63\u4e49\uff0c\u4e3a\u4e86\u9053\u5fb7\uff0c\u6211\u5fc3\u91cc\u867d\u7136\u96be\u53d7\uff0c\u4f46\u662f\u7edd \u4e0d\u4f1a\u4e3a\u4ed6\u590d\u4ec7\u3002\u4ed6\u8fd9\u6837\u88ab\u6076\u4eba\u5bb3\u6b7b\uff0c\u6211\u5fc3\u91cc\u9664\u4e86\u96be\u53d7\u4e4b\u5916\uff0c\u8fd8\u6709\u6124\u6068\uff0c\u6211\u8981\u5411\u8427\u65e0\u590d\u4ec7\uff0c\u5e76 \u4e0d\u662f\u4e3a\u4e86\u5148\u592b\u4e00\u4eba\uff0c\u800c\u662f\u4e3a\u4e86\u4e16\u4e0a\u6240\u6709\u5584\u826f\u7684\u4eba\uff0c\u8fd9\u4e9b\uff0c\u6211\u60f3\u4f60\u4e5f\u8be5\u77e5\u9053\uff01\u201d \u3000\u3000\u953a\u9759\u53f0\u4e0a\u773c\uff0c\u957f\u4e45\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\u547d\u5c0f\u9119\u56de\u8f6c\uff0c\u82e5\u53ea\u662f\u4e3a\u4e86\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u5c0f\u9119\u4fbf\u8981\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u53c8\u81ea\u5f00\u59cb\u555c\u6ce3\u7684\u51cc\u7433\uff0c\u76ee\u5149\u500f\u7136\u4e00\u62ac\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u88ab\u5b59\u654f\u963b\u6b62\u4e86\uff0c\u5979\u53ea\u662f\u7f13\u7f13\u95ee \u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u953a\u9759\u76f4\u5230\u6b64\u523b\uff0c\u8fd8\u6ca1\u6709\u62ac\u8d77\u76ee\u5149\uff0c\u56e0\u4e3a\u4e0d\u6562\u9762\u5bf9\u8fd9\u6b63\u76f4\u800c\u6e29\u67d4\uff0c\u4e25\u5cfb\u800c\u6148\u7965\u7684\u5987\u4eba\uff0c\u4ed6 \u5782\u7740\u5934\u6c88\u58f0\u7b54\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9119\u8ff3\u8d74\u5609\u5174\uff0c\u5411\u5bb6\u5e08\u8986\u547d\uff01\u201d \u3000\u3000\u5b59\u654f\u9ed8\u7136\u534a\u664c\uff0c\u7a81\u5730\u8f7b\u8f7b\u62cd\u7740\u51cc\u7433\u7684\u624b\u638c\uff0c\u7f13\u6162\u4f46\u5374\u575a\u5b9a\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u4e5f\u5230\u5609\u5174\u53bb\uff01\u201d \u3000\u3000\u51cc\u7433\u53cd\u8eab\u6349\u4f4f\u5979\u6bcd\u4eb2\u7684\u624b\u638c\uff0c\u50cf\u662f\u5728\u8868\u793a\u5bf9\u5979\u6bcd\u4eb2\u7684\u611f\u6fc0\uff0c\u800c\u5979\u5fc3\u91cc\u5374\u5728\u6697\u4e2d\u547c\u558a\uff1a \u3000\u3000\u201c\u4ed6\u4e0d\u4f1a\u6b7b\u7684\u2026\u2026\u4ed6\u4f1a\u5230\u5357\u6e56\u70df\u96e8\u697c\u53bb\u7684\u3002\u201d \u3000\u3000\u8fd9\u5e0c\u671b\u4f7f\u5979\u62ac\u8d77\u5934\u6765\uff0c\u4ef0\u671b\u82cd\u7a79\uff0c\u4f46\u5929\u8fb9\u5374\u8fde\u6700\u540e\u4e00\u4e1d\u5f69\u971e\u4e5f\u9690\u6ca1\u5728\u9ed1\u6697\u91cc\u4e86\u3002 \u3000\u3000\u4ece\u897f\u6881\u5c71\u5230\u5609\u5174\uff0c\u8def\u7a0b\u5e76\u4e0d\u7b97\u77ed\uff0c\u4f46\u4efb\u4f55\u8def\u90fd\u6709\u8d70\u5b8c\u7684\u65f6\u5019\u3002 \u3000\u3000\u5979\u4eec\uff0c\u5230\u4e86\u5609\u5174\u3002 \u3000\u3000\u8fd9\u4e00\u6bb5\u8def\u9014\u5bf9\u953a\u9759\u8bf4\u6765\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u68a6\uff0c\u4e00\u4e2a\u6df7\u5408\u7740\u6e29\u99a8\u4e0e\u5bd2\u51b7\uff0c\u8f7b\u76c8\u4e0e\u6c88\u91cd\uff0c\u5feb\u4e50\u4e0e \u60b2\u4f24\uff0c\u5b89\u6170\u4e0e\u75db\u82e6\u7684\u68a6\uff0c\u662f\u90a3\u4e48\u6f2b\u957f\u800c\u9065\u8fdc\uff0c\u5374\u53c8\u662f\u5982\u6b64\u5306\u907d\u548c\u77ed\u4fc3\u3002 \u3000\u3000\u4ed6\u662f\u90a3\u4e48\u6e05\u6670\u5730\u77e5\u9053\uff0c\u4e0e\u90a3\u4e48\u6df1\u5207\u7684\u4e86\u89e3\uff0c\u5728\u8fd9\u4e00\u6bb5\u8def\u9014\u4e0a\uff0c\u6148\u7965\u7684\u5b59\u654f\u6240\u5bf9\u4ed6\u8bf4\u7684\u6bcf \u4e00\u53e5\u8a00\u8bed\u4e2d\u7684\u542b\u610f\u3002\u4f46\u662f\u4ed6\u5374\u4e0d\u60f3\u77e5\u9053\uff0c\u66f4\u4e0d\u60f3\u4e86\u89e3\uff0c\u56e0\u4e3a\u8fd9\u4efd\u4e86\u89e3\u6240\u5e26\u7ed9\u4ed6\u7684\uff0c\u53ea\u6709\u51fa\u81ea \u826f\u77e5\u7684\u75db\u82e6\u3002 \u3000\u3000\u201c\u9ebb\u6728\uff01\u201d\u5b59\u654f\u6709\u65f6\u4f1a\u8fd9\u6837\u6697\u4e2d\u601d\u5fd6\uff1a \u3000\u3000\u201c\u96be\u9053\u8fd9\u5b69\u5b50\u5df2\u7ecf\u88ab\u90a3\u51b7\u9177\u7684\u9b54\u5934\u6559\u8bad\u5f97\u53d8\u4e3a\u9ebb\u6728\uff1f\u201d \u3000\u3000\u5bf9\u4e8e\u5979\u4efb\u4f55\u4e00\u79cd\u5584\u610f\u7684\u8bf1\u5bfc\uff0c\u4ed6\u53ea\u662f\u4e1d\u6beb\u65e0\u52a8\u4e8e\u8877\u5730\u503e\u542c\u7740\uff0c\u4ed6\u6df1\u6c89\u7684\u9762\u5bb9\u4e0a\uff0c\u4f3c\u4e4e\u6c38 \u8fdc\u4e0d\u4f1a\u73b0\u51fa\u4efb\u4f55\u4e00\u4e1d\u60c5\u611f\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u5f53\u7136\u2014\u2014 \u3000\u3000\u9664\u4e86\u4ed6\u7684\u76ee\u5149\uff0c\u50cf\u662f\u4e0d\u7ecf\u610f\u5730\u6295\u5411\u51cc\u7433\u7684\u65f6\u5019\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff01\u90a3\u5145\u6ee1\u4e16\u95f4\u6700\u6700\u9ad8\u8d35\u7684\u60c5\u64cd\u2014\u2014\u540c\u60c5\uff0c\u7eaf\u771f\u4e0e\u5584\u826f\u7684\u51cc\u7433\uff0c\u7adf\u4f1a\u5bf9\u8fd9\u8db3\u4ee5\u71c3 \u70e7\u5230\u4efb\u4f55\u4e00\u4e2a\u4eba\u5fc3\u7075\u6df1\u5904\u7684\u76ee\u5149\uff0c\u7adf\u4e5f\u4f1a\u50cf\u953a\u9759\u5bf9\u5f85\u522b\u4eba\u65f6\u4e00\u6837\u5730\u6f20\u7136\u800c\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000\u5979\u50cf\u662f\u4e5f\u5b8c\u5168\u9ebb\u6728\u4e86\uff0c\u800c\u5979\u7684\u8fd9\u4efd\u9ebb\u6728\uff0c\u5374\u662f\u4e3a\u4e86\u60b2\u54c0\uff0c\u5bf9\u5979\u8fd9\u4e00\u751f\u4e2d\u552f\u4e00\u631a\u7231\u7684\u4eba\u7684 \u60b2\u54c0\u3002 \u3000\u3000\u4e5f\u8bb8\u5979\u8fd8\u5e74\u8f7b\uff0c\u4e5f\u8bb8\u6709\u4eba\u4f1a\u8bf4\uff0c\u5979\u5e74\u8f7b\u5f97\u8fd8\u4e0d\u591f\u80fd\u4e86\u89e3\u7231\u7684\u610f\u4e49\uff0c\u4e5f\u4e0d\u591f\u4f53\u9a8c\u5230\u7231\u7684\u771f \u5473\u3002 \u3000\u3000\u4f3c\u662f\u5979\u8fd9\u4e00\u4efd\u7231\u5fc3\uff0c\u5374\u771f\u7684\u662f\u90a3\u4e48\u7eaf\u771f\uff0c\u90a3\u4e48\u6df1\u631a\uff0c\u5979\u6bcb\u5eb8\u4e86\u89e3\uff0c\u4e5f\u4e0d\u60f3\u4e86\u89e3\u3002\u5979\u53ea\u77e5 \u9053\u7231\u548c\u88ab\u7231\uff0c\u8fd9\u4e5f\u8bb8\u662f\u4e0a\u5929\u4e3a\u4e86\u916c\u7b54\u5979\u5bf9\u4e16\u4eba\u7684\u5584\u826f\u800c\u7ed9\u5979\u7684\u6069\u8d50\u2014\u2014\u56e0\u4e3a\uff0c\u5979\u6240\u77e5\u9053\u7684\uff0c \u5df2\u7ecf\u662f\u5168\u90e8\u7231\u7684\u771f\u610f\u3002 \u3000\u3000\u84b9\u846d\u6768\u67f3\uff0c\u56db\u5904\u98de\u82b1\uff0c\u66ae\u6625\u7684\u4e94\u6708\uff0c\u4e94\u6708\u7684\u521d\u56db\uff0c\u6625\u9633\u5c06\u6de1\u9752\u8272\u7684\u77f3\u677f\u9053\u8def\uff0c\u6620\u5f97\u50cf\u662f \u65b9\u6d78\u4e86\u6625\u96e8\u4f3c\u7684\u6e05\u65b0\uff0c\u7530\u79e7\u78a7\u6cb9\u6cb9\u5730\u95ea\u7740\u751f\u547d\u7684\u5149\u91c7\u3002\u953a\u9759\u4f9d\u604b\u5730\u56de\u5934\uff0c\u518d\u6b21\u77a5\u4e86\u4ecd\u7136\u7ad9\u5728 \u90a3\u95f4\u50fb\u9759\u5ba2\u6808\u95e8\u8fb9\u7684\u5b59\u654f\u4e0e\u51cc\u7433\u4e00\u773c\uff0c\u5634\u89d2\u6cdb\u4e86\u4e00\u4e1d\u5fae\u7b11\uff0c\u7136\u540e\u8fc8\u7740\u575a\u5b9a\u7684\u6b65\u5b50\uff0c\u5411\u8857\u7684\u5c3d \u5934\u5904\u8d70\u53bb\u3002 \u3000\u3000\u5fae\u7b11\u2014\u2014 \u3000\u3000\u5b59\u654f\u4e0e\u51cc\u7433\uff0c\u5374\u662f\u975e\u5e38\u6e05\u6670\u5730\u770b\u5230\u4e86\u4ed6\u7684\u5fae\u7b11\uff0c\u8fd9\u4e00\u8fde\u4e32\u65e5\u5b50\u4e2d\uff0c\u8fd9\u6df1\u6c88\u7684\u5c11\u5e74\u6240\u9732\u51fa \u7684\u7b2c\u4e00\u4e1d\u5fae\u7b11\uff0c\u867d\u7136\u8fd9\u5fae\u7b11\u4e2d\u5305\u542b\u662f\u90a3\u4e48\u591a\u5fe7\u90c1\u4e0e\u79bb\u6101\uff0c\u4f46\u8fd9\u5c31\u50cf\u662f\u6ee1\u5e03\u9634\u973e\u7684\u82cd\u7a79\u6240\u9732\u51fa \u7684\u4e00\u4e1d\u9633\u5149\uff0c\u8db3\u4ee5\u4f7f\u5f97\u6148\u7965\u7684\u5b59\u654f\u5fc3\u4e2d\u611f\u89c9\u6e29\u6696\u4e0e\u5b89\u6170\u3002 \u3000\u3000\u5979\u81ea\u89c9\u5df2\u7528\u4e86\u5979\u6240\u6709\u7684\u529b\u91cf\u6765\u4f7f\u8fd9\u5c11\u5e74\u8e0f\u4e0a\u6b63\u9014\uff0c\u4f46\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u5979\u5374\u4ecd\u7136\u4e0d\u77e5\u9053\u81ea \u5df1\u7684\u52aa\u529b\u662f\u5426\u6709\u6548\u3002 \u3000\u3000\u56e0\u4e3a\u6b64\u523b\uff0c\u4ed6\u8fd8\u662f\u6beb\u4e0d\u72b9\u7591\u5730\u56de\u5230\u4ed6\u5e08\u7236\u90a3\u91cc\u53bb\uff0c\u867d\u7136\u5728\u8fd9\u4e00\u8def\u4e0a\uff0c\u4ed6\u4ece\u672a\u4e0e\u4efb\u4f55\u4e00\u4e2a \u4e0e\u201c\u5929\u4e89\u6559\u201d\u6709\u5173\u7684\u4eba\u6216\u4e8b\u7269\u63a5\u89e6\uff0c\u4f46\u6b64\u523b\uff0c\u4e16\u4e0a\u4ecd\u7136\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u529b\u91cf\u80fd\u5c06\u4ed6\u633d\u7559\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u8d70\u4e86\uff0c\u5915\u9633\u4e0b\u5c71\uff0c\u591c\u5e55\u6df1\u5782\u2026\u2026 \u3000\u3000\u6e10\u6e10\u2026\u2026 \u3000\u3000\u5b59\u654f\u4e0e\u51cc\u7433\uff0c\u7a81\u7136\u611f\u5230\u4e00\u79cd\u832b\u7136\u7684\u6050\u60e7\uff0c\u5c24\u5176\u662f\u5b59\u654f\uff0c\u5979\u5f00\u59cb\u60f3\u5230\u8bb8\u591a\u4e2a\u4ee4\u5979\u6050\u60e7\u7684\u95ee \u9898\u3002 \u3000\u3000\u201c\u8427\u65e0\uff0c\u8fd9\u6b8b\u9177\uff0c\u5978\u6076\uff0c\u4f46\u5374\u53c8\u662f\u90a3\u4e48\u673a\u667a\u7684\u9b54\u5934\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u65e9\u5df2\u77e5\u9053\u4ed6\u7684\u7231\u5f92\u5df2\u548c\u81ea \u5df1\u4ec7\u4eba\u7684\u59bb\u5973\uff0c\u751f\u51fa\u4e86\u6df1\u539a\u7684\u60c5\u611f\uff1f\u201d \u3000\u3000\u201c\u82e5\u662f\u4ed6\u5df2\u77e5\u9053\u4e86\uff0c\u90a3\u4e48\u4ed6\u5c06\u4f1a\u5bf9\u4ed6\u7684\u7231\u5f92\u2014\u2014\u953a\u9759\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u5979\u5fc3\u5934\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u51db\uff01 \u3000\u3000\u201c\u5929\u4e89\u6559\u515a\u7fbd\u904d\u5e03\u6c5f\u6e56\uff0c\u6211\u4eec\u548c\u953a\u9759\u4e00\u8def\u884c\u6765\uff0c\u4ed6\u4eec\u96be\u9053\u4e0d\u77e5\u4e48\uff1f\u201d \u3000\u3000\u5979\u6447\u6447\u5934\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u5583\u5583\u81eaWE\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u77e5\u9053\u7684\uff0c\u53ea\u662f\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5411\u6211\u4eec\u52a8\u624b\uff1f\u96be\u9053\u662f\u4e3a\u4e86\u953a\u9759\u4e4b\u6545\uff0c\u662f\u4ee5\u6295\u9f20 \u5fcc\u5668\uff01\u6291\u6216\u662f\u8427\u65e0\u90a3\u9b54\u5934\u53e6\u5b58\u66f4\u6bd2\u8fa3\u7684\u6253\u7b97\uff01\u201d \u3000\u3000\u51cc\u7433\u4e00\u76f4\u5782\u9996\u51dd\u601d\uff0c\u6b64\u523b\u5ffd\u7136\u62ac\u8d77\u5934\u6765\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u5988\uff01\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u654f\u5fae\u5fae\u4e00\u7b11\uff0c\u67d4\u58f0\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51cc\u7433\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u5728\u60f3\u2026\u2026\u201d \u3000\u3000\u5979\u79cb\u6ce2\u4e4b\u5185\uff0c\u83b9\u7136\u53c8\u73b0\u6cea\u5149\uff1a \u3000\u3000\u201c\u6211\u5728\u60f3\uff0c\u660e\u5929\u5c31\u662f\u4e94\u6708\u7aef\u9633\u4e86\uff0c\u4e0d\u77e5\u9053\u2026\u2026\u4e0d\u77e5\u9053\u2026\u2026\u5509\uff01\u4ed6\u4f1a\u4e0d\u4f1a\u6765\uff1f\u201d \u3000\u3000\u5b59\u654f\u5fc3\u4e2d\u7a81\u5730\u6d8c\u8d77\u4e00\u9635\u96be\u8a00\u7684\u60b2\u54c0\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u5979\u624d\u4e86\u89e3\u81ea\u5df1\u7684\u5973\u513f\u5bf9\u5415\u5357\u4eba\u7528\u60c5\u4e4b \u6df1\uff0c\u56e0\u4e3a\u8fd9\u7eaf\u771f\u7684\u5c11\u5973\u7adf\u4ec0\u4e48\u90fd\u4e0d\u518d\u6302\u5ff5\uff0c\u5c31\u8fde\u81ea\u8eab\u7684\u5b89\u5371\uff0c\u4e5f\u5168\u90fd\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u5979\u5fc3\u91cc \u6240\u60f3\u7684\uff0c\u53ea\u6709\u8fd9\u4e94\u4e2a\u5b57\uff01 \u3000\u3000\u201c\u4ed6\u4f1a\u4e0d\u4f1a\u6765\uff1f\u201d \u3000\u3000\u58c1\u95f4\u660f\u9ec4\u7684\u706f\u5149\uff0c\u6620\u5728\u51cc\u7433\u90a3\u5ae3\u7ea2\u7684\u9762\u9765\u4e0a\uff0c\u5b59\u654f\u5446\u5446\u5730\u51dd\u89c6\u7740\u5979\u7684\u7231\u5973\uff0c\u592a\u591a\u7684\u60b2 \u54c0\uff0c\u592a\u591a\u7684\u5173\u6000\uff0c\u4f7f\u5f97\u5979\u826f\u4e45\u826f\u4e45\uff0c\u90fd\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\uff0c\u56e0\u4e3a\u5979\u80fd\u786e\u8ba4\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u4e00\u5b9a \u662f\uff1a \u201c\u4ed6\u4e0d\u4f1a\u6765\u7684\uff01\u201d \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e5d\u5341\u4e00\u7ae0\u3000\u76f8\u4f9d\u4e3a\u547d>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u5341\u4e00\u7ae0\u3000\u76f8\u4f9d\u4e3a\u547d \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u8fd9\u6bcd\u5973\u4e24\u4eba\u76f8\u5bf9\u9ed8\u7136\uff0c\u90fd\u4e5f\u6ca1\u6709\u5206\u6beb\u7761\u610f\uff0c\u5916\u9762\u591c\u9611\u4eba\u9759\uff0c\u4e07\u7c41\u4ff1 \u5bc2\uff0c\u53ea\u6709\u665a\u98ce\u5439\u5f97\u7a97\u7eb8\u7c0c\u7c0c\u4f5c\u54cd\uff0c\u6709\u4e00\u53e5\u8bdd\u5374\u5728\u5b59\u654f\u5589\u5934\u6253\u8f6c\uff0c\u5979\u60f3\u95ee\uff1a \u3000\u3000\u201c\u82e5\u662f\u4ed6\u4e0d\u6765\u5462\uff1f\u201d \u3000\u3000\u4f46\u662f\u8fd9\u516d\u4e2a\u5b57\u5374\u751f\u50cf\u6709\u7740\u5343\u94a7\u91cd\u91cf\uff0c\u5979\u7eb5\u7136\u9f13\u8d77\u52c7\u6c14\uff0c\u5374\u4e5f\u4e0d\u6562\u95ee\u51fa\u53e3\u6765\uff0c\u56e0\u4e3a\u5979\u6015\u8fd9 \u95ee\u9898\u7684\u7b54\u6848\uff0c\u4f1a\u523a\u4f24\u5979\u7231\u5973\u7684\u5fc3\u3002 \u3000\u3000\u5979\u53ea\u662f\u8f7b\u8f7b\u8bf4\u4e86\u53e5\uff1a \u3000\u3000\u201c\u5509\u2014\u2014\u6709\u98ce\u7684\u5929\u6c14\u2026\u2026\u201d \u3000\u3000\u6de1\u6de1\u7684\u4e00\u53e5\u8bdd\uff0c\u6de1\u6de1\u7684\u8bed\u610f\uff0c\u4f46\u65e0\u9650\u7684\u6148\u6bcd\u5fe7\u601d\u5173\u6000\uff0c\u5374\u5df2\u90fd\u6df1\u6df1\u5730\u5305\u542b\u5728\u8fd9\u516d\u4e2a\u5b57 \u91cc\u3002 \u3000\u3000\u53c8\u662f\u4e00\u9635\u98ce\u5439\u8fc7\u3002 \u3000\u3000\u7a81\u5730\uff0c\u7d27\u95ed\u7740\u7684\u7a97\u6237\uff0c\u4f3c\u4e4e\u56e0\u98ce\u800c\u5f00\uff0c\u665a\u98ce\uff0c\u7ec8\u4e8e\u5439\u5165\u4e86\u8fd9\u65e0\u98ce\u7684\u5c0f\u5c4b\uff0c\u5b59\u654f\uff0c\u51cc\u7433\u4e00 \u9f50\u62ac\u8d77\u6765\uff0c\u76ee\u5149\u52a8\u5904\u2014\u2014 \u3000\u3000\u201c\u5440\uff01\u662f\u4f60\uff01\u201d \u3000\u3000\u4e24\u4eba\u7adf\u4e0d\u80fd\u81ea\u4e3b\u5730\u60ca\u5524\u51fa\u58f0\u6765\uff01 \u3000\u3000\u591c\u8272\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e00\u4e2a\u904d\u4f53\u91d1\u8272\u52b2\u88c5\u7684\u5c11\u5e74\uff0c\u4e00\u811a\u8e29\u7740\u7a97\u69db\uff0c\u5f53\u7a97\u800c\u7acb\uff0c\u665a\u98ce\u867d\u7136\u5c06\u4ed6\u7684 \u8863\u8882\u5439\u5f97\u98d8\u98d8\u98de\u821e\uff0c\u4f46\u4ed6\u7684\u8eab\u8eaf\uff0c\u5374\u6709\u5982\u77f3\u50cf\u822c\u5730\u6728\u7136\u4e0d\u52a8\u3002 \u3000\u3000\u5b59\u654f\u4e00\u58f0\u60ca\u5524\uff0c\u4e00\u4e1d\u7b11\u5bb9\uff0c\u81ea\u5634\u89d2\u6cdb\u8d77\uff0c\u5979\u67d4\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u953a\u9759\uff01\u4f60\u7ec8\u4e8e\u6765\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e2d\u5305\u542b\u7740\u90a3\u4e48\u591a\u5b89\u6170\u4e0e\u6148\u7965\uff0c\u4f7f\u5f97\u6728\u7acb\u7a97\u53f0\u4e4b\u4e0a\u7684\u953a\u9759\uff0c\u65e0\u8a00\u5730\u5408\u8d77\u773c\u775b\uff0c\u50cf\u662f\u5728 \u5fc3\u5e95\u6df1\u5904\uff0c\u6c89\u91cd\u5730\u53f9\u606f\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u4f46\u662f\u7b49\u5230\u4ed6\u518d\u6b21\u5f20\u5f00\u773c\u775b\u7684\u65f6\u5019\uff0c\u4ed6\u9762\u4e0a\u5374\u53c8\u6062\u590d\u4e86\u51b7\u9759\uff0c\u90a3\u79cd\u5168\u7136\u4e0d\u5e26\u4efb\u4f55\u4e00\u4e1d\u4eba\u7c7b \u60c5\u611f\u7684\u51b7\u9759\u3002 \u3000\u3000\u5b59\u654f\u5fae\u5fae\u4e00\u6123\uff0c\u67d4\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u7ad9\u5728\u90a3\u91cc\u5e72\u4ec0\u4e48\uff1f\u5916\u9762\u98ce\u5927\uff0c\u8fd8\u662f\u4e0b\u6765\u5427\uff0c\u8fd9\u91cc\u5927\u6982\u8fd8\u6709\u4e9b\u70ed\u8336\uff0c\u4f60\u5148\u559d\u4e00\u676f\uff0c\u89e3 \u89e3\u5bd2\u6c14\uff0c\u7136\u540e\u518d\u544a\u8bc9\u6211\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u5730\u201c\u5527\u201d\u4e00\u58f0\uff0c\u5b59\u654f\uff0c\u51cc\u7433\u9f50\u5730\u4e00\u60ca \u3000\u3000\u953a\u9759\u7adf\u5df2\u53cd\u8155\u62d4\u51fa\u5251\u6765\u3002 \u3000\u3000\u68ee\u5bd2\uff0c\u78a7\u7eff\u7684\u5251\u5149\uff0c\u6620\u7740\u4ed6\u91d1\u8272\u7684\u52b2\u88c5\uff0c\u6620\u7740\u4ed6\u82cd\u767d\u7684\u9762\u5bb9\uff0c\u5b59\u654f\u7a81\u5730\u89c9\u5f97\u4e00\u4e1d\u5bd2\u610f\uff0c \u81ea\u5fc3\u5e95\u6cdb\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u98a4\u58f0\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u8fd9\u662f\u2026\u2026\u201d \u3000\u3000\u953a\u9759\u76ee\u5149\u6728\u7136\u51dd\u89c6\u7740\u81ea\u5df1\u638c\u4e2d\u7684\u957f\u5251\uff0c\u98ce\u50cf\u662f\u66f4\u5927\u4e86\uff0c\u4ed6\u7684\u8863\u8882\uff0c\u98de\u821e\u66f4\u6025\uff0c\u7136\u800c\u4ed6\u7684 \u76ee\u5149\uff0c\u5374\u77ac\u4e5f\u4e0d\u77ac \u3000\u3000\u65e0\u8a00\u5730\u6c89\u9ed8\u4e2d\uff0c\u4f3c\u4e4e\u5df2\u6709\u4e86\u4ee4\u4eba\u7a92\u606f\u7684\u610f\u5473\uff0c\u65e0\u8a00\u7684\u953a\u9759\uff0c\u7a81\u5730\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5929\u4e89\u6559\u4e0b\u7b2c\u4e8c\u4ee3\u638c\u95e8\u5f1f\u5b50\u953a\u9759\uff0c\u5949\u5929\u4e89\u6559\u4e3b\u4eb2\u4f20\u6cd5\u8c15\uff0c\u524d\u6765\u53d6\u51cc\u5317\u4fee\u9057\u5b7d\u59bb\u5973\u9996 \u7ea7\u3002\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u5b59\u654f\u53ea\u89c9\u8033\u7554\u8f70\u7136\u4e00\u58f0\u5de8\u54cd\uff0c\u518d\u4e5f\u7ad9\u4e0d\u7a33\u8eab\u5f62\uff0c\u8eab\u5f62\u6447\u6447\u3002\u8dc4\u8e09\u9000\u540e\u6570\u6b65\uff0c \u201c\u7830\u201d\u7136\u4e00\u7247\u58f0\u54cd\uff0c\u684c\u4e0a\u676f\u58f6\uff0c\u5168\u88ab\u8863\u8896\u5e26\u843d\u5730\u4e0a\u3002 \u3000\u3000\u5b59\u654f\u5706\u7741\u79c0\u76ee\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u8131\u53e3\u60ca\u547c\u51fa\u6765\u3002 \u3000\u3000\u90a3\u77e5\u953a\u9759\u7684\u76ee\u5149\uff0c\u5374\u4ecd\u7136\u5446\u6728\u5730\u51dd\u89c6\u7740\u81ea\u5df1\u638c\u4e2d\u7684\u957f\u5251\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u53c8\u9053\uff1a \u3000\u3000\u201c\u5929\u4e89\u6559\u4e0b\u953a\u9759\u5949\u547d\u6765\u53d6\u4e24\u4f4d\u9996\u7ea7\uff0c\u662f\u5426\u8fd8\u8981\u5728\u4e0b\u81ea\u5df1\u52a8\u624b\uff0c\u5168\u51ed\u4e24\u4f4d\u4e4b\u610f\uff01\u201d\u80a9\u5934\u5fae \u52a8\uff0c\u98d8\u7136\u843d\u4e0b\u3002 \u3000\u3000\u51cc\u7433\u6123\u4e86\u534a\u664c\uff0c\u7a81\u5730\u201c\u54af\u54af\u201d\u5927\u7b11\u8d77\u6765\uff0c\u5979\u7adf\u5927\u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u662f\u4f60\u2026\u2026\u3002\u6211\u4eec\u5f53\u7136\u8981\u4f60\u4eb2\u81ea\u52a8\u624b\uff0c\u96be\u9053\u4f60\u4ee5\u4e3a\u6211\u4eec\u8fd8\u4f1a\u81ea\u6740\u4e48\uff1f\u4e0d\u8fc7\uff0c\u6211 \u53ea\u6015\u4f60\u8fd9\u4f4d\u523d\u5b50\u624b\uff0c\u8fd8\u672a\u5fc5\u662f\u6211\u6bcd\u5973\u4e24\u4eba\u7684\u654c\u624b\u5462\uff1f\u201d \u3000\u3000\u5979\u8fb9\u7b11\u8fb9\u8bf4\uff0c\u76f4\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff0c\u5c31\u50cf\u662f\u7a81\u7136\u9047\u7740\u4e16\u4e0a\u6700\u6700\u597d\u7b11\u7684\u4e8b\u4e00\u6837\uff0c\u4f46\u662f\u5979\u7684\u7b11 \u58f0\uff0c\u5374\u662f\u51c4\u5389\u7684\uff0c\u8fd9\u51c4\u5389\u7684\u7b11\u58f0\u4e2d\u5305\u542b\u7740\u4ec0\u4e48\uff0c\u9664\u4e86\u953a\u9759\u4e4b\u5916\uff0c\u8c01\u4e5f\u65e0\u6cd5\u9886\u53d7\u5f97\u51fa\uff0c\u8c01\u4e5f\u65e0 \u6cd5\u4f53\u4f1a\u5f97\u5230\u3002 \u3000\u3000\u6570\u7c92\u6cea\u73e0\uff0c\u96f6\u4e71\u5730\u843d\u5230\u5730\u4e0a\uff0c\u662f\u8c01\uff1f\u662f\u8c01\u54ed\u4e86\uff1f\u5440\uff01\u762a\u7b11\u7740\u7684\u51cc\u7433\u7684\u53cc\u76ee\u4e4b\u4e2d\uff0c\u4e0d\u53c8\u5df2 \u6709\u4e24\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u5c06\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4f46\u662f\uff0c\u953a\u9756\u7684\u76ee\u5149\uff0c\u5374\u4f9d\u65e7\u6728\u7136\u51dd\u89c6\u5728\u81ea\u5df1\u638c\u4e2d\u7684\u5251\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u51cc\u7433\u51c4\u5389\u7684\u7b11\u58f0\uff0c\u500f\u7136\u987f\u4f4f\uff0c\u5979\u7ea4\u8170\u5fae\u626d\uff0c\u4f3c\u4e4e\u5df2\u8981\u4e0a\u524d\u52a8\u624b\uff0c\u53ea\u89c9\u8863\u8896\u4e00\u7d27\uff0c\u5979 \u6bcd\u4eb2\u5df2\u7acb\u5728\u5979\u8eab\u65c1\uff0c\u51cc\u7433\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u5e7d\u5e7d\u53f9\u9053\uff1a \u3000\u3000\u201c\u5988\u2026\u2026\u201d \u3000\u3000\u5b59\u654f\u7684\u4e00\u53cc\u6148\u7965\u800c\u53c8\u4e25\u5cfb\uff0c\u6e29\u67d4\u800c\u53c8\u6c89\u91cd\u7684\u76ee\u5149\uff0c\u5374\u5e76\u672a\u4fa7\u76ee\u671b\u5979\u7231\u5973\u4e00\u773c\uff0c\u5979\u53ea\u662f\u9759 \u9759\u5730\u671b\u7740\u953a\u9759\uff0c\u8f7b\u8f7b\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u867d\u7136\u5bf9\u6211\u5982\u6b64\uff0c\u4f46\u5e08\u547d\u96be\u8fdd\uff0c\u6211\u4e86\u89e3\u4f60\u7684\u82e6\u8877\uff0c\u6211\u4e00\u4e1d\u4e00\u6beb\u4e5f\u6ca1\u6709\u6068\u4f60\uff0c\u6211\u539f\u5148\u8fd8 \u5728\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4e00\u8def\u4e0a\u8427\u65e0\u90fd\u4e0d\u5411\u6211\u6bcd\u5973\u4e0b\u624b\uff0c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u539f\u6765\u4ed6\u8981\u8ba9\u4f60\u6765\u5f53\u8fd9\u4e00\u4efd \u7f6a\u6076\u3002\u201d \u3000\u3000\u5979\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u5e73\u751f\u4e0d\u4f1a\u9a82\u4eba\uff0c\u4f46\u662f\u50cf\u8427\u65e0\u8fd9\u79cd\u4eba\uff0c\u6211\u7eb5\u7136\u7528\u5c3d\u4e16\u4e0a\u6240\u6709\u6076\u6bd2\u7684\u8bdd\u6765\u9a82\u4ed6\uff0c\u4e5f\u8fd8\u5acc \u4e0d\u591f\u3002\u6211\u4e0d\u4e3a\u6211\u4eec\u6bcd\u5973\u96be\u53d7\uff0c\u6211\u53ea\u4e3a\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u96be\u53d7\uff0c\u56e0\u4e3a\u6b66\u6797\u4e4b\u4e2d\uff0c\u7adf\u51fa\u4e86\u8fd9\u6837\u4e00\u4e2a\u4e07 \u6076\u7684\u9b54\u5934\uff01\u201d \u3000\u3000\u5979\u6084\u7136\u5408\u4e0a\u773c\u3002 \u3000\u3000\u201c\u6211\u6bcd\u5973\u81ea\u77e5\uff0c\u4ee5\u6211\u4eec\u7684\u529b\u91cf\uff0c\u7edd\u5bf9\u65e0\u6cd5\u9003\u51fa\u4ed6\u7684\u6bd2\u624b\uff0c\u4f60\u7eb5\u7136\u4e0d\u52a8\u624b\uff0c\u4eca\u591c\u6211\u4eec\u8fd8\u662f \u4f1a\u6b7b\u5728\u522b\u4eba\u624b\u4e0a\uff0c\u6240\u4ee5\u6211\u5f88\u9ad8\u5174\uff0c\u56e0\u4e3a\u6211\u6bcd\u5973\u88ab\u4f60\u6740\u6b7b\uff0c\u603b\u8981\u6bd4\u5929\u4e89\u6559\u522b\u7684\u8d3c\u5b50\u6740\u6b7b\u597d\u7684 \u591a\uff0c\u4f60\u53ea\u7ba1\u52a8\u624b\u597d\u4e86\uff0c\u65e0\u8bba\u4f60\u6b66\u529f\u600e\u6837\uff0c\u6211\u6bcd\u5973\u7edd\u4e0d\u8fd8\u624b\uff01\u201d \u3000\u3000\u5979\u8f7b\u67d4\uff0c\u7f13\u6162\u800c\u6c89\u91cd\u5730\u8bf4\u5230\u8fd9\u91cc\uff0c\u773c\u6df1\u5782\uff0c\u7adf\u771f\u7684\u7b49\u5f85\u953a\u9759\u5411\u81ea\u5df1\u4e0b\u624b\uff0c\u51cc\u7433\u5446\u671b\u7740\u5979 \u7684\u6bcd\u4eb2\u534a\u664c\uff0c\u4ea6\u81ea\u5408\u4e0a\u773c\u3002 \u3000\u3000\u5927\u5730\u6c89\u9ed8\u77e5\u6b7b\uff0c\u5c31\u8fde\u98ce\u58f0\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4f3c\u4e4e\u90fd\u5df2\u505c\u987f\u4e86\u3002 \u3000\u3000\u953a\u9759\uff0c\u5374\u4ecd\u6728\u7136\u671b\u7740\u81ea\u5df1\u638c\u4e2d\u957f\u5251\u2026\u2026 \u3000\u3000\u5b59\u654f\u53cc\u76ee\u4e00\u5f20\uff0c\u8f7b\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u5feb\u4e9b\u52a8\u624b\uff0c\u6211\u7edd\u4e0d\u602a\u4f60\uff0c\u4f60\u82e5\u89c9\u5f97\u6709\u4e00\u4e9b\u96be\u53d7\uff0c\u5c31\u8bf7\u4f60\u5728\u6211\u6bcd\u5973\u6b7b\u540e\uff0c\u5c06\u6211\u6bcd\u5973\u846c \u5728\u4e00\u8d77\uff0c\u7136\u540e\u2026\u2026\u201d \u3000\u3000\u51cc\u7433\u7a81\u5730\u5f20\u76ee\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u7b49\u5230\u540e\u6765\uff0c\u5e0c\u671b\u4f60\u80fd\u5230\u6211\u7684\u68fa\u6750\u6216\u8005\u6b7b\u524d\uff0c\u544a\u8bc9\u6211\u2026\u2026\u544a\u8bc9\u6211\uff0c\u660e\u5929\u4ed6\u7a76\u7adf\u6765\u2026\u2026 \u4e86\u2026\u2026\u6ca1\u2026\u2026\u6709\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u9010\u6e10\u4f4e\u5fae\uff0c\u4e8e\u662f\u56db\u4e0b\u53c8\u5f52\u4e8e\u6b7b\u4e00\u822c\u7684\u5bc2\u9759\u3002 \u3000\u3000\u7a81\u5730\u2026\u2026 \u3000\u3000\u201c\u55e1\u201d\u7136\u4e00\u58f0\uff0c\u953a\u9759\u638c\u4e2d\u957f\u5251\u4e00\u6296\uff0c\u4f46\u89c1\u6735\u6735\u5251\u82b1\uff0c\u6f2b\u5929\u800c\u8d77\uff0c\u68ee\u68ee\u5251\u6c14\uff0c\u7728\u4eba\u808c\u80a4\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u529f\u529b\u4e4b\u6df1\uff0c\u4f7f\u5f97\u51cc\u7433\u79c0\u76ee\u4e00\u5f20\uff0c\u5374\u6709\u4e00\u4e1d\u5fae\u7b11\uff0c\u51c4\u51c9\u7684\u5fae\u7b11\uff0c\u6084\u6084\u6ed1\u4e0a\u5b59\u654f\u7684\u9762 \u9765\uff0c\u5979\u65b9\u5f85\u5408\u8d77\u773c\u775b\uff0c\u63a5\u53d7\u6b7b\u4ea1\u3002 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u953a\u9759\u6296\u624b\u4e00\u5251\uff0c\u7a81\u5730\u957f\u957f\uff0c\u957f\u957f\uff0c\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u8981\u5c06\u751f\u5e73\u5fe7\u90c1\u90fd\u5728\u8fd9\u4e00\u53f9\u4e2d \u5410\u5c3d\uff0c\u7136\u540e\u53cd\u8155\u53c8\u662f\u4e00\u5251\uff0c\u5411\u81ea\u5df1\u5589\u95f4\u520e\u53bb\uff01 \u3000\u3000\u5b59\u654f\uff0c\u51cc\u7433\uff0c\u9f50\u5730\u60ca\u547c\u4e00\u58f0\uff0c\u773c\u89c1\u8fd9\u67c4\u957f\u5251\uff0c\u5df2\u5c06\u5212\u5728\u8fd9\u5c11\u5e74\u7684\u5589\u5934\u4e0a\uff0c\u5979\u4e24\u4eba\u5927\u60ca\u4e4b \u4e0b\uff0c\u7adf\u4e0d\u77e5\u51fa\u624b\u63f4\u6551\u3002 \u3000\u3000\u53c8\u662f\u4e00\u9635\u98ce\u5439\u6765\u2014\u2014 \u3000\u3000\u4e00\u58f0\u9634\u68ee\uff0c\u51b7\u5ced\u7684\u8f7b\u7b11\uff0c\u968f\u98ce\u98d8\u5165\u3002 \u3000\u3000\u4e00\u9635\u5c16\u9510\uff0c\u51cc\u5389\u7684\u98ce\u58f0\uff0c\u631f\u7740\u4e00\u4e1d\u4e4c\u5149\uff0c\u4e5f\u968f\u7740\u98ce\u58f0\uff0c\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u201c\u94db\u201d\u5730\u4e00\u58f0\uff0c\u91d1\u9523\u6e05\u54cd\uff01 \u3000\u3000\u201c\u545b\u201d\u5730\u4e00\u58f0\uff0c\u957f\u5251\u843d\u5730\uff01 \u3000\u3000\u5b59\u654f\uff0c\u51cc\u7433\uff0c\u9a87\u7136\u540e\u9000\u3002 \u3000\u3000\u953a\u9759\u4e00\u624b\u6367\u8155\uff0c\u5927\u60ca\u8f6c\u8eab\u3002 \u3000\u3000\u53ea\u89c1\u2014\u2014 \u3000\u3000\u6df1\u6c89\u6d53\u91cd\u7684\u591c\u8272\u4e2d\uff0c\u4e0d\u77e5\u4f55\u65f6\uff0c\u5df2\u591a\u4e86\u4e00\u6761\u9880\u957f\u9edd\u9ed1\u7684\u8eab\u5f71\uff0c\u8fdc\u8fdc\u7acb\u5728\u7a97\u5916\uff0c\u5b59\u654f\uff0c\u51cc \u7433\uff0c\u7eb5\u7136\u7528\u5c3d\u76ee\u529b\uff0c\u4e5f\u770b\u4e0d\u89c1\u4ed6\u7684\u9762\u5bb9\uff0c\u53ea\u89c1\u4ed6\u4e00\u53cc\u76ee\u5149\uff0c\u6709\u5982\u4e24\u70b9\u5bd2\u661f\uff0c\u5728\u591c\u8272\u4e2d\u95ea\u95ea\u751f \u5149\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5b59\u654f\u6bcd\u5973\u4e24\u4eba\uff0c\u53ea\u89c9\u5929\u5730\u4e07\u7269\uff0c\u5f77\u4f5b\u9f50\u90fd\u53d8\u4e86\u989c\u8272\uff0c\u56e0\u4e3a\u5979\u4eec\u6b64\u523b\u5df2\u77e5 \u9053\uff0c\u7acb\u5728\u7a97\u5916\u7684\u4eba\u5f71\uff0c\u4fbf\u662f\u201c\u5929\u4e89\u6559\u4e3b\u201d\u8427\u65e0\u3002 \u3000\u3000\u53c8\u662f\u4e00\u58f0\u5176\u5bd2\u5165\u9aa8\u7684\u51b7\u7b11\uff0c\u968f\u98ce\u98d8\u5165\uff0c\u53ea\u89c1\u7a97\u5916\u4eba\u5f71\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u953a\u9759\uff0c\u51fa\u6765\uff01\u201d \u3000\u3000\u953a\u9759\u5934\u4e5f\u4e0d\u56de\uff0c\u7f13\u7f13\u8d70\u5230\u7a97\u5916\uff0c\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u63a0\u51fa\u7a97\u5916\uff0c\u7f13\u7f13\u8d70\u5230\u7a97\u5916\u4eba\u5f71\u8eab\u524d\uff0c\u4f0f\u5230\u5730 \u4e0a\uff0c\u606d\u606d\u656c\u656c\u5730\u78d5\u4e86\u5934\uff0c\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u9759\u7acb\u4e00\u65c1\uff0c\u4ed6\u4e00\u53e5\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4e00\u4e1d\u58f0\u97f3\u4e5f\u6ca1\u6709\u53d1 \u51fa\u3002 \u3000\u3000\u5b59\u654f\uff0e\u51cc\u7433\uff0c\u5446\u5446\u5730\u671b\u7740\u4ed6\u7684\u8eab\u5f71\uff0c\u63a0\u51fa\u7a97\u524d\uff0c\u53ea\u89c9\u623f\u4e2d\u7684\u5bd2\u610f\u8d8a\u6765\u8d8a\u91cd\uff0c\u51cc\u7433\u6084\u7136\u79fb \u52a8\u811a\u6b65\uff0c\u9760\u5230\u4ed6\u6bcd\u4eb2\u8eab\u4fa7\uff0c\u8fd9\u4e00\u53cc\u5b64\u82e6\u4f36\u4ec3\uff0c\u76f8\u4f9d\u4e3a\u547d\u7684\u6bcd\u5973\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u66f4\u662f\u8c01\u4e5f\u4e0d\u613f\u79bb \u5f00\u8c01\u4e00\u6b65\u3002 \u56e0\u4e3a\uff0c\u5979\u4eec\u7eb5\u7136\u8981\u6b7b\uff0c\u4e5f\u8981\u6b7b\u5728\u4e00\u5904\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e5d\u5341\u4e8c\u7ae0\u3000\u4ed6\u6765\u4e0d\u6765>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u5341\u4e8c\u7ae0\u3000\u4ed6\u6765\u4e0d\u6765 \u3000\u3000\u5b59\u654f\u8f7b\u8f7b\u4f38\u51fa\u624b\u638c\uff0c\u63e1\u4f4f\u5979\u5973\u513f\u7684\u5c0f\u624b\uff0c\u53c8\u51b0\u53c8\u51c9\u7684\u5c0f\u624b\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u52c7\u6c14\u5ffd\u751f\uff0c \u7eb5\u7136\u7a97\u5916\u7ad9\u7740\u7684\u662f\u4e2a\u4eba\u529b\u4e0d\u80fd\u76f8\u6297\u7684\u6076\u9b54\uff0c\u4e3a\u7740\u5979\u7684\u5973\u513f\uff0c\u5979\u4e5f\u8981\u62da\u4e0a\u4e00\u62da\u3002 \u3000\u3000\u5979\u7d27\u4e86\u7d27\u624b\u638c\uff0c\u8f7b\u8f7b\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u4e0d\u8981\u6015\uff01\u201d \u3000\u3000\u51cc\u7433\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u51c4\u7136\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u6015\uff0c\u6211\u53ea\u662f\u2026\u2026\u53ea\u662f\u6709\u4e9b\u96be\u53d7\uff0c\u4ed6\u7a76\u7adf\u662f\u751f\u662f\u6b7b\uff0c\u90fd\u4e0d\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5b59\u654f\u94f6\u7259\u4e00\u54ac\uff0c\u76ee\u5149\u95ea\u7535\u822c\u671b\u5411\u7a97\u5916\u2014\u2014 \u3000\u3000\u90a3\u77e5\u2014\u2014 \u3000\u3000\u7a97\u5916\u4eba\u5f71\u5374\u7a81\u5730\u8f7b\u8f7b\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u4e0d\u7528\u5bb3\u6015\uff0c\u6211\u6b64\u6765\u5374\u65e0\u52a0\u5bb3\u4e4b\u610f\uff0c\u4f60\u4eec\u53ea\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u5b59\u654f\u60ca\u6123\u4ea4\u96c6\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u89c1\u4ed6\u624b\u638c\u4e00\u626c\uff0c\u53c8\u662f\u4e00\u7247\u91d1\u5149\uff0c\u7a7f\u7a97\u800c\u5165\uff0c\u201c\u94db\u201d\u5730\u4e00 \u8038\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u7adf\u662f\u4e00\u9762\u5c0f\u5c0f\u91d1\u9523\u3002 \u3000\u3000\u53ea\u542c\u7a97\u5916\u4eba\u5f71\u7f13\u7f13\u53c8\u9053\uff1a \u3000\u3000\u201c\u6b64\u9762\u91d1\u9523\uff0c\u4e43\u662f\u672c\u6559\u9632\u8eab\u5bc6\u7269\uff0c\u4f60\u6bcd\u5973\u4e24\u4eba\uff0c\u6b64\u540e\u884c\u8d70\u6c5f\u6e56\uff0c\u82e5\u9047\u65e0\u6cd5\u89e3\u51b3\u4e4b\u4e8b\uff0c\u6301 \u6b64\u91d1\u9523\uff0c\u4e8e\u95f9\u4e4b\u4e2d\u8f7b\u6572\u4e09\u54cd\uff0c\u81ea\u6709\u5929\u4e89\u6559\u5f92\uff0c\u524d\u6765\u4e3a\u4e24\u4f4d\u6548\u529b\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u843d\uff0c\u957f\u8896\u5fae\u62c2\uff0c\u8f7b\u53f1\uff01 \u3000\u3000\u201c\u8d70\uff01\u201d \u3000\u3000\u8eab\u5f62\u52a8\u5904\uff0c\u5df2\u5728\u6570\u4e08\u5f00\u5916\uff0c\u8fd9\u4e00\u58f0\u201c\u8d70\u201d\u5b57\uff0c\u7adf\u65e0\u6cd5\u5206\u8fa8\u4ed6\u662f\u7acb\u5728\u4f55\u5904\u8bf4\u7684\u3002 \u3000\u3000\u5b59\u654f\uff0c\u51cc\u7433\u53c8\u81ea\u4e00\u60ca\uff0c\u4e00\u6123\uff0c\u53ea\u89c1\u953a\u9759\u4f3c\u4e4e\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u7acb\u523b\u4e5f\u4e00\u63a0\u800c\u53bb\uff0c\u6df1\u6c89\u6d53\u91cd\u7684 \u591c\u8272\uff0c\u77ac\u606f\u4fbf\u5c06\u8fd9\u4e24\u6761\u4eba\u5f71\u541e\u6ca1\u3002 \u3000\u3000\u5b59\u654f\u6bcd\u5973\u5e76\u80a9\u76f8\u4f9d\uff0c\u5fc3\u4e2d\u4f3c\u4e4e\u9aa4\u7136\u8f7b\u677e\u8bb8\u591a\uff0c\u53c8\u4f3c\u4e4e\u9aa4\u7136\u6c89\u91cd\u8bb8\u591a\uff0c\u82e5\u4e0d\u662f\u90a3\u9762\u5c0f\u5c0f\u91d1 \u9523\u4ecd\u5728\u706f\u5149\u4e0b\u4e00\u95ea\u4e00\u95ea\u5730\u53d1\u5149\uff0c\u8fd9\u4e00\u5207\u771f\u7684\u5c31\u6709\u5982\u4e00\u573a\u6076\u68a6\uff0c\u4e00\u573a\u51e0\u4e4e\u4ee4\u4eba\u96be\u4ee5\u7f6e\u4fe1\u7684\u6076 \u68a6\u3002 \u3000\u3000\u201c\u8427\u65e0\u8fd9\u6076\u9b54\u4e3a\u4ec0\u4e48\u4f1a\u653e\u8fc7\u6211\u4eec\uff1f\u4e0d\u4f46\u653e\u8fc7\u6211\u4eec\uff0c\u8fd8\u7559\u4e0b\u8fd9\u4e00\u9762\u53ef\u4ee5\u9632\u8eab\u6551\u547d\u7684\u91d1 \u9523\uff01\u201d \u3000\u3000\u8fd9\u95ee\u9898\u5b59\u654f\u7eb5\u7136\u60f3\u4e0a\u5341\u5e74\uff0c\u5374\u4e5f\u65e0\u6cd5\u60f3\u51fa\u6b64\u4e2d\u7684\u56e0\u679c\u3002 \u3000\u3000\u4f60\u8bf4\u662f\u4e48\uff1f \u3000\u3000\u5609\u5174\u53bf\u5357\u4e8c\u91cc\uff0c\u9e33\u6e56\u4e4b\u6c34\u4e0e\u5176\u652f\u6d41\uff0c\u81f3\u57ce\u4e1c\u5357\u4f1a\u4e8e\u4e00\u5904\uff0c\u4f9d\u4f9d\u6768\u67f3\uff0c\u70b9\u70b9\u8377\u82b1\uff0c\u7eff\u6d78\u6ce2 \u5149\uff0c\u78a7\u5f00\u5929\u5f71\uff0c\u96d5\u8237\u7b19\u745f\uff0c\u9761\u95f4\u51c9\u71e0\u2026\u2026\u2026\u8fd9\u4fbf\u662f\u5929\u4e0b\u95fb\u540d\u7684\u201c\u5357\u6e56\u201d\uff01 \u3000\u3000\u5357\u6e56\u6e56\u5fc3\uff0c\u6ce2\u5149\u6c34\u8272\u4e2d\uff0c\u6709\u4e00\u7247\u5c0f\u5c0f\u7684\u201c\u5c9b\u5c7f\u201d\uff0c\u6bd4\u5357\u6e56\u66f4\u6709\u540d\u7684\u201c\u70df\u96e8\u697c\u201d\uff0c\u5c31\u662f\u5728 \u8fd9\u7247\u5c0f\u5c0f\u7684\u9ec4\u8272\u6ce5\u571f\u4e0a\u3002 \u3000\u3000\u4e94\u6708\u7aef\u9633\uff0c\u5357\u6e56\u6e56\u8fb9\uff0c\u4e07\u5934\u94bb\u52a8\uff0c\u6e38\u4eba\u5982\u7ec7\uff0c\u5357\u6e56\u6e56\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u6761\u5c0f\u5c0f\u7684\u663c\u822b\uff0c \u8f7d\u7740\u4e0d\u77e5\u591a\u5c11\u4e2a\u6e38\u4eba\uff0c\u6c64\u6f3e\u5176\u95f4\uff0c\u4f46\u89c1\u6ce2\u5149\u6c34\u8272\u4e4b\u95f4\uff0c\u5ae3\u7ea2\u7eff\uff0c\u4e07\u7d2b\u5343\u7ea2\uff0c\u5440\uff01\u867d\u7136\u5df2\u662f\u4e94 \u6708\uff0c\u4f46\u8fd9\u5357\u6e56\u6e56\u7554\uff0c\u5374\u4ecd\u662f\u6625\u5929\u3002 \u3000\u3000\u70df\u96e8\u697c\u5934\uff0c\u4e00\u53cc\u4eba\u5f71\uff0c\u51ed\u680f\u800c\u7acb\uff0c\u4e00\u4e2a\u6e05\u6717\u7684\u53e3\u97f3\uff0c\u5728\u5979\u4eec\u8eab\u540e\u66fc\u58f0\u6717\u541f\u7740\u70df\u96e8\u697c\u5934\u7684 \u540d\u8054\uff1a \u3000\u3000\u201c\u6816\u53f0\u56f4\u5341\u4e07\u4eba\u5bb6\uff0c\u770b\u69db\u5916\u6ce2\u5149\uff0c\u90ed\u5916\u5c71\u5149\uff0c\u5982\u6b64\u5929\u6c34\uff0c\u8981\u6709\u674e\u5317\u6d77\u8c6a\u60c5\uff0c\u65b9\u8bb8\u5230\u4ead\u4e2d \u996e\u9152\uff1b \u3000\u3000\u9c7c\u9e1f\u62d3\u4e09\u5343\u4e16\u754c\uff0c\u6b63\u82a6\u82b1\u79cb\u65e5\uff0c\u8377\u82b1\u590f\u65e5\uff0c\u662f\u4f55\u666f\u7269\uff1f\u5018\u65e0\u675c\u5c11\u9675\u7edd\u5531\uff0c\u5207\u83ab\u6765\u6e56\u4e0a\u9898 \u8bd7\u3002\u201d \u3000\u3000\u8bed\u6c14\u6e05\u6717\uff0c\u4e2d\u6c14\u4ea6\u8db3\u3002 \u3000\u3000\u51ed\u680f\u800c\u7acb\u7684\u4e00\u53cc\u4eba\u5f71\uff0c\u9aa4\u95fb\u8bd7\u58f0\uff0c\u500f\u7136\u56de\u8fc7\u5934\u6765\uff0c\u5374\u89c1\u6717\u541f\u4e4b\u4eba\uff0c\u53ea\u662f\u4e00\u4e2a\u4e2d\u5e74\u84dd\u886b\u9053 \u4eba\uff0c\u4e0d\u7981\u8f7b\u53f9\u4e00\u58f0\uff0c\u4f3c\u4e4e\u9887\u4e3a\u5931\u671b\u3002 \u3000\u3000\u5979\u4eec\u5931\u671b\u7684\u662f\u56e0\u4e3a\u76f4\u5230\u6b64\u523b\uff0c\u8fd8\u6ca1\u6709\u89c1\u7740\u5979\u4eec\u671f\u5f85\u7684\u4eba\u2014\u2014\u5415\u5357\u4eba\uff0c\u800c\u5979\u4eec\u81ea\u7136\u4fbf\u662f\u5b59 \u654f\u4e0e\u5979\u7684\u7231\u5973\u51cc\u7433\u4e86\u3002 \u3000\u3000\u5929\u8272\u8fd8\u6ca1\u6709\u4eae\u7684\u65f6\u5019\uff0c\u5979\u4eec\u5df2\u5230\u4e86\u5357\u6e56\uff0c\u7528\u5c3d\u4e16\u95f4\u6240\u6709\u7684\u529b\u91cf\uff0c\u4e5f\u4e0d\u80fd\u4f7f\u5fc3\u6025\u7684\u51cc\u7433\u665a \u6765\u4e00\u6b65\uff0c\u5979\u4ec0\u4e48\u4e5f\u4e0d\u60f3\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u6015\uff0c\u60f3\u7684\u53ea\u662f\uff1a\u201c\u4ed6\u6765\u4e0d\u6765\uff1f\u201d\u6015\u7684\u53ea\u662f\uff1a\u201c\u4ed6\u4e0d\u6765 \u4e86\uff01\u201d \u3000\u3000\u968f\u7740\u5929\u5149\u7684\u5927\u4eae\uff0c\u5357\u6e56\u4e0a\u7684\u6e38\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u751a\u81f3\u8fde\u70df\u96e8\u697c\u56db\u5468\u7684\u80dc\u8ff9\uff1a\u9274\u4ead\uff0c\u83f1\u9999\u6c34 \u69ad\uff0c\u5927\u58eb\u9601\uff0c\u83f0\u4e91\uff0c\u9c7c\u4e50\u56ed\uff0c\u9493\u9ccc\u77f6\uff0c\u6d6e\u7389\u4ead\u2026\u2026\uff0c\u90fd\u62e5\u6ee1\u4e86\u6e38\u5ba2\uff0c\u4f46\u662f\u5415\u5357\u4eba\uff0c\u5374\u4ecd\u6773\u5982 \u9ec4\u9e64\uff1f \u3000\u3000\u5979\u4eec\u52a8\u4e5f\u4e0d\u52a8\u5730\u7acb\u5728\u70df\u96e8\u697c\u5934\uff0c\u7eb5\u6709\u8f7b\u8584\u7684\u5c11\u5e74\uff0c\u5728\u80cc\u540e\u8baa\u7b11\uff0c\u5979\u4eec\u4e5f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c \u5230\u3002 \u3000\u3000\u4eba\u8d8a\u591a\uff0c\u51cc\u7433\u5fc3\u4e2d\u7684\u7126\u6025\uff0c\u4e5f\u5c31\u66f4\u91cd\uff0c\u5979\u4e00\u53cc\u6e05\u6f88\u7684\u79cb\u6ce2\uff0c\u6b64\u523b\u5df2\u7b3c\u4e0a\u4e86\u4e00\u5c42\u6de1\u6de1\u7684\u7ea2 \u4e1d\uff0c\u5979\u5947\u602a\uff0c\u8427\u65e0\u7684\u7ea6\u4f1a\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5b9a\u5728\u8fd9\u79cd\u5730\u65b9\u3002 \u3000\u3000\u201c\u4ed6\u4e0d\u6765\uff1f\u600e\u5730\u8427\u65e0\u90a3\u6076\u9b54\u4e5f\u4e0d\u6765\uff1f\u201d \u3000\u3000\u51cc\u7433\u8f7b\u8f7b\u5730\u95ee\u7740\u5979\u6bcd\u4eb2\uff0c\u800c\u5b59\u654f\u7684\u56de\u7b54\uff0c\u53ea\u662f\u5fe7\u90c1\u5730\u6447\u5934\uff0c\u7eb5\u7136\u8427\u65e0\u6765\u4e86\uff0c\u5979\u5374\u4e5f\u65e0\u6cd5 \u8ba4\u51fa\u3002 \u3000\u3000\u201c\u4e0d\u4f1a\u6765\u4e86\u5427\uff1f\u4e0d\u4f1a\u6765\u4e86\u5427\uff1f\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\uff0c\u4e00\u6b21\u53c8\u4e00\u6b21\u5730\u5728\u51cc\u7433\u5fc3\u4e2d\u6253\u8f6c\uff0c\u6bcf\u8f6c\u4e00\u6b21\uff0c\u5c31\u50cf\u662f\u6709\u4e00\u67c4\u5343\u94a7\u94c1\uff0c\u5728\u5979\u5fc3\u623f\u4e0a \u91cd\u91cd\u5730\u6253\u51fb\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u66ae\u8272\u6e10\u751f\u2026\u2026\u2026\u2026 \u3000\u3000\u591c\u66ae\u5df2\u5782\u2026\u2026\u2026\u2026 \u3000\u3000\u5357\u6e56\u56db\u4fa7\uff0c\u4eae\u8d77\u5343\u70b9\u706f\u706b\uff0c\u665a\u4e0a\u7684\u5357\u6e56\uff0c\u6bd4\u767d\u5929\u66f4\u7f8e\u4e86\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u201c\u4ed6\u6765\u4e0d\u6765\uff1f\u4ed6\u6765\u4e0d\u6765\uff1f\u4ed6\u6765\u4e0d\u6765\uff1f\u2026\u2026\u201d \u3000\u3000\u591c\u8272\u6e10\u6df1\uff0c\u6e10\u6d53\uff0c\u6e10\u91cd\u2026\u2026\u2026\u2026 \u3000\u3000\u6e38\u4eba\u6e10\u53bb\uff0c\u6e10\u7a00\uff0c\u6e10\u65e0\u2026\u2026\u2026\u2026 \u3000\u3000\u706f\u5149\u70b9\u4e86\uff0c\u661f\u5149\u4eae\u4e86\uff0c\u5f52\u53bb\u7684\u753b\u822b\uff0c\u53cc\u6d46\u8f7b\u62cd\u6e56\u6c34\uff0c\u58f0\u58f0\u4e43\uff0c\u7ec8\u4e5f\u6d88\u65e0\uff0c\u672a\u53bb\u672a\u53d8\u7684\u53ea \u5269\u5343\u7f15\u67f3\u4e1d\uff0c\u4e07\u70b9\u8377\u82b1\uff0c\u6e05\u6e05\u6e56\u6c34\uff0c\u5dcd\u5dcd\u697c\u9601\u2026\u2026\u8fd8\u6709\u697c\u9601\u4e0a\u7684\u4e00\u53cc\u4eba\u5f71\u3002 \u3000\u3000\u201c\u5509\uff01\u2026\u2026\u4ed6\u53ea\u6015\u4e0d\u4f1a\u6765\u4e86\uff01\u201d \u3000\u3000\u5b59\u654f\u7ec8\u4e8e\u957f\u53f9\u7740\uff0c\u8bf4\u51fa\u4e86\u8fd9\u53e5\u5979\u4e0d\u77e5\u82b1\u4e86\u591a\u5c11\u6c14\u529b\u624d\u8bf4\u5f97\u51fa\u7684\u8bdd\uff0c\u5979\u7d27\u7d27\u63e1\u7740\u51cc\u7433\u7684 \u624b\uff0c\u518d\u4e5f\u4e0d\u6562\u653e\u677e\u4e00\u4e0b\u3002 \u3000\u3000\u4f46\u662f\uff0c\u51cc\u7433\u5374\u50cf\u662f\u5df2\u5168\u7136\u9ebb\u6728\u4e86\u4e00\u6837\uff0c\u671b\u7740\u680f\u5916\u6ee1\u6016\u82cd\u7a79\u7684\u70b9\u70b9\u661f\u5149\uff0c\u7a81\u5730\u5e7d\u5e7d\u957f\u53f9\u4e00 \u58f0\uff1a \u3000\u3000\u201c\u4ed6\u2026\u2026\u771f\u2026\u2026\u7684\u2026\u2026\u4e0d\u6765\u4e86\u2026\u2026\u201d\u5a07\u8eaf\u4e00\u8f6f\uff0c\u7f13\u7f13\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5b59\u654f\u60ca\u547c\u4e00\u58f0\uff0c\u4e00\u628a\u62e6\u4f4f\u5979\u7231\u5973\u7684\u7ea4\u8170\uff0c\u5931\u8272\u60ca\u547c\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u4f60\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u6ca1\u6709\u58f0\u606f\uff0c\u661f\u5149\u4e0b\uff0c\u79c0\u4e3d\u7684\u9762\u5bb9\uff0c\u82cd\u767d\u5982\u7eb8\uff0c\u665a\u98ce\u4e2d\uff0c\u7ea4\u67d4\u7684\u624b\u638c\uff0c\u5bd2\u51b7\u5982 \u51b0\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u4e00\u65b9\u6de1\u9ec4\u5b57\u67ec\uff0c\u81ea\u680f\u5916\u98d8\u98d8\u843d\u4e0b\uff0c\u5b59\u654f\u76ee\u5149\u52a8\u5904\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u4f38\u624b\u4e00\u6284\uff0c\u8fd9\u5b57\u67ec\u7adf\u50cf\u662f \u6709\u7740\u7075\u6027\u4f3c\u7684\u5df2\u81ea\u98d8\u843d\u5728\u5979\u624b\u4e0a\u3002 \u3000\u3000\u867d\u7136\u662f\u9ed1\u591c\uff0c\u4f46\u5b57\u7b3a\u4e0a\u7684\u5b57\u8ff9\uff0c\u5374\u4ecd\u5341\u5206\u663e\u76ee\u3002 \u3000\u3000\u201c\u4e45\u5019\u4e0d\u81f3\uff0c\u6211\u5148\u53bb\u4e86\uff01\u201d \u3000\u3000\u5b59\u654f\u4f4e\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u8427\u65e0\uff01\u201d \u3000\u3000\u957f\u8eab\u800c\u8d77\uff0c\u55d6\u5730\u63a0\u4e0a\u697c\u5934\uff0c\u665a\u98ce\u4e60\u4e60\uff0c\u56db\u4e0b\u5bc2\u7136\uff0c\u53ea\u6709\u6e56\u4e2d\u53cd\u6620\u7684\u4e07\u70b9\u661f\u5149\uff0c\u660e\u706d\u500f \u5ffd\uff0c\u95ea\u52a8\u4e0d\u5df2\uff0c\u90a3\u6709\u534a\u6761\u4eba\u5f71\uff0c\u5979\u6123\u4e86\u4e00\u6123\uff0c\u63a0\u5165\u680f\u4e2d\uff0c\u660f\u8ff7\u5728\u6c60\u4e0a\u7684\u51cc\u7433\uff0c\u5374\u5df2\u6709\u4e86\u4e00\u58f0 \u53f9\u606f\u3002 \u4e00\u58f0\u8f7b\u5fae\uff0c\u5e7d\u6028\uff0e\u60b2\u54c0\uff0c\u6c89\u75db\u7684\u53f9\u606f\uff0c\u968f\u7740\u665a\u98ce\uff0c\u4e00\u4e1d\u4e00\u4e1d\u5730\u98d8\u9001\u51fa\u53bb\u2026\u2026 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e5d\u5341\u4e09\u7ae0\u3000\u8fa3\u624b\u86c7\u5fc3>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u5341\u4e09\u7ae0\u3000\u8fa3\u624b\u86c7\u5fc3 \u3000\u3000\u9759\u5bc2\u7684\u6625\u591c\uff0c\u9759\u5bc2\u7684\u8857\u9053\uff0c\u7a81\u5730\u51e0\u58f0\u7830\u7136\u62cd\u95e8\u7684\u58f0\u54cd\uff0c\u5212\u7834\u8fd9\u873f\u8712\u4e8e\u6625\u591c\u4e2d\u8857\u9053\u7684\u9759 \u5bc2\u3002 \u3000\u3000\u7761\u610f\u6726\u80e7\u7684\u5e97\u5c0f\u4e8c\uff0c\u7761\u610f\u6726\u80e7\u5730\u6253\u5f00\u5e97\u95e8\uff0c\u7761\u610f\u6726\u80e7\u5730\u5f15\u7740\u8fdf\u5f52\u7684\u5ba2\u4eba\u2014\u2014\u5b59\u654f\u6bcd\u5973\uff0c \u7a7f\u8fc7\u8d70\u5eca\uff0c\u5f15\u81f3\u623f\u95f4\uff0c\u7761\u610f\u6726\u80e7\u5730\u5f00\u5f00\u623f\u95e8\u2026\u2026 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u4e00\u58f0\u60ca\u547c\uff0c\u8fde\u9000\u4e09\u6b65\uff01 \u3000\u3000\u7761\u610f\u6726\u80e7\u7684\u5e97\u5c0f\u4e8c\u7761\u610f\u4e0d\u518d\u6726\u80e7\uff0c\u4ed6\u98a4\u6296\u7740\u4f38\u51fa\u624b\u6307\uff0c\u98a4\u6296\u7740\u6307\u5411\u5df2\u7ecf\u655e\u5f00\u7684\u623f\u95f4\u98a4\u6296 \u5730\u60ca\u547c\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b59\u654f\u5fc3\u5934\u4e00\u51db\uff0c\u9762\u5bb9\u7a81\u53d8\uff0c\u522b\u5730\uff0c\u63a0\u81f3\u95e8\u53e3\uff0c\u63a2\u76ee\u5185\u671b\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u5979\u7adf\u4e5f\u4e00\u58f0\u60ca\u547c\uff0c\u98a4\u6296\u7740\u4f38\u51fa\u7389\u6307\uff0c\u98a4\u6296\u7740\u6307\u5411\u95e8\u5185\uff0c\u98a4\u6296\u7740\u547c\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u662f\u4f60\uff01\u201d \u3000\u3000\u51cc\u7433\u53cc\u76ee\u4e00\u5f20\uff0c\u8131\u53e3\u95ee\u9053\uff1a \u3000\u3000\u201c\u662f\u8c01\uff1f\u662f\u5357\u4eba\uff1f\u201d \u3000\u3000\u5237\u5730\uff0c\u5979\u4ea6\u81ea\u63a0\u81f3\u5b59\u654f\u8eab\u4fa7\uff0c\u63a2\u76ee\u5185\u671b\u2026\u2026 \u3000\u3000\u7a81\u5730\u2026\u2026 \u3000\u3000\u5979\u7adf\u4e5f\u4e00\u58f0\u60ca\u547c\uff0c\u98a4\u6296\u7740\u4f38\u51fa\u7389\u6307\uff0c\u98a4\u6296\u7740\u6307\u5411\u95e8\u5185\uff0c\u98a4\u6296\u7740\u547c\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u600e\u5730\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4e09\u58f0\u60ca\u547c\uff0c\u867d\u6709\u5148\u540e\uff0c\u5374\u51e0\u4e4e\u53d1\u751f\u5728\u540c\u4e00\u5239\u90a3\u4e4b\u95f4\uff01 \u3000\u3000\u4e09\u4eba\u516d\u9053\u76ee\u5149\uff0c\u9f50\u5730\u5446\u5446\u5730\u671b\u5411\u95e8\u5185\uff0c\u53ea\u89c1\u5f53\u95e8\u7684\u4e00\u5f20\u7ea2\u6905\u4e0a\uff0c\u7adf\u5982\u75f4\u5982\u5446\u5730\u7aef\u5750\u7740\u4e00 \u4e2a\u6ee1\u8eab\u6d74\u8840\uff0c\u9762\u5bb9\u82cd\u767d\uff0c\u795e\u60c5\u6728\u7136\uff0c\u76ee\u5149\u7a7a\u6d1e\uff0c\u53f3\u81c2\u5df2\u81ea\u9f50\u6839\u65ad\u53bb\uff0c\u4f24\u5904\u7adf\u672a\u5305\u624e\u7684\u5c11\u5e74\uff01 \u3000\u3000\u4ed6\u5446\u5446\u5730\u671b\u7740\u5b59\u654f\u6bcd\u5973\uff0c\u5c31\u50cf\u662f\u4ed6\u4e00\u751f\u4e4b\u4e2d\u4ece\u672a\u89c1\u8fc7\u5979\u4e24\u4eba\u4f3c\u7684\uff0c\u66f4\u4e0d\u77e5\u56de\u7b54\u51cc\u7433\u7684\u95ee \u8bdd\u3002 \u3000\u3000\u5b59\u654f\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u4ed6\u8eab\u4fa7\uff0c\u7126\u6025\uff0c\u60ca\u60f6\u7684\u6cea\u73e0\uff0c\u5df2\u6d41\u4e0b\u5979\u7684\u5a07\u9765\uff0c\u5979\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea \u5df1\u7684\u773c\u775b\uff0c\u5979\u7126\u6025\u800c\u60ca\u60f6\u5730\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u600e\u5730\u4e86\uff1f\u4f60\u2026\u2026\u600e\u5730\u4e0d\u56de\u7b54\u6211\u7684\u8bdd\uff0c\u4f60\u2026\u2026\u5509\uff01\u4f60\u5230\u5e95\u600e\u4e48\u4e86\u5440\uff1f\u201d \u3000\u3000\u5750\u7740\u7684\u4eba\uff0c\u4f9d\u7136\u5750\u7740\u4e0d\u52a8\uff0c\u4e0d\u52a8\u2026\u2026 \u3000\u3000\u51cc\u7433\u7a81\u5730\u4e00\u58f0\u5927\u559d\uff1a \u3000\u3000\u201c\u953a\u9759\uff01\u4f60\u96be\u9053\u4e0d\u8ba4\u5f97\u6211\u4eec\u4e86\u4e48\uff1f\u201d \u3000\u3000\u953a\u9759\u7684\u76ee\u5149\u7f13\u7f13\u4e00\u8f6c\uff0c\u7ec8\u4e8e\u6295\u843d\u5728\u51cc\u7433\u7684\u9762\u4e0a\uff0c\u4e8e\u662f\u4ed6\u7a7a\u6d1e\u800c\u5446\u6ede\u7684\u76ee\u5149\uff0c\u6e10\u6e10\u5f00\u59cb\u6cdb \u8d77\u4e00\u4e1d\u706b\u82b1\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u4ecd\u662f\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8bf4\u4e5f\u4e0d\u8bf4\uff0c\u5b59\u654f\u8c28\u614e\u5730\u66ff\u4ed6\u5305\u624e\u4f24\u53e3\uff0c\u6e29\u67d4\u5730\u95ee\u9053\uff1a \u3000\u3000\u201c\u544a\u8bc9\u6211\u2026\u2026\u662f\u8c01\uff1f\u662f\u8c01\u6709\u8fd9\u4e48\u6b8b\u5fcd\u7684\u5fc3\u80a0\uff0c\u6bd2\u8fa3\u7684\u624b\u6bb5\uff1f\u201d \u3000\u3000\u953a\u9759\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u953a\u9759\u65e0\u987b\u56de\u7b54\u3002 \u3000\u3000\u56e0\u4e3a\u5b59\u654f\u6bcd\u5973\uff0c\u6b64\u523b\u5df2\u77e5\u9053\u4e86\u7b54\u6848\u3002 \u3000\u3000\u201c\u8fdd\u547d\u80cc\u5e08\uff0c\u5176\u7f6a\u5f53\u8bdb\uff0c\u5374\u56e0\u5fc3\u6148\uff0c\u4ec5\u6b8b\u5176\u8eab\uff0c\u4e8b\u7531\u5c14\u8d77\uff0c\u7f6a\u7531\u5c14\u53d1\uff0c\u662f\u8be5\u5c14\u7b49\u517b\u5176\u7ec8 \u751f\uff01\u201d \u3000\u3000\u6de1\u9ec4\u7684\u7eb8\u67ec\uff0c\u9edd\u9ed1\u7684\u5b57\u8ff9\uff0c\u5c31\u50cf\u662f\u5b59\u654f\u65b9\u624d\u5728\u70df\u96e8\u697c\u5934\u63a5\u5230\u7684\u4e00\u6837\uff0c\u6b64\u523b\u6b63\u88ab\u538b\u5728\u953a\u9759 \u8eab\u540e\u684c\u4e0a\u7684\u8336\u676f\u4e0b\u3002 \u3000\u3000\u5b59\u654f\u5288\u624b\u62ff\u6765\uff0c\u6495\u6210\u4e24\u534a\uff0c\u5979\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u8427\u65e0\u7adf\u4f1a\u5c06\u81ea\u5df1\u7684\u7231\u5f92\uff0c\u6467\u6b8b\u6210\u8fd9\u822c\u6a21\u6837\uff01 \u3000\u3000\u5979\u6e29\u67d4\u5730\u6276\u8d77\u953a\u9759\uff0c\u89e6\u624b\u4e4b\u5904\uff0c\u53ea\u89c9\u4ed6\u8eab\u4e0a\u7684\u808c\u8089\uff0c\u6709\u5982\u68c9\u7d6e\u4e00\u822c\uff0c\u67d4\u8f6f\u8150\u5f31\uff0c\u5979\u77e5\u9053 \u8fd9\u5c11\u5e74\u7684\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u5df2\u88ab\u4ed6\u90a3\u6709\u6bd2\u86c7\u4e00\u822c\u7684\u5fc3\u80a0\u548c\u6bd2\u624b\u7684\u5e08\u5085\u6bc1\u53bb\uff0c\u4e8e\u662f\u5979\u6697\u4e2d\u6c89\u91cd\u5730\u53f9 \u606f\u7740\uff0c\u5c06\u4ed6\u8f7b\u8f7b\u653e\u5012\u5e8a\u4e0a\u3002 \u3000\u3000\u5979\u4e0d\u6562\u66f4\u4e0d\u5fcd\u53bb\u601d\u5fd6\u8fd9\u5c11\u5e74\u6b64\u523b\u7684\u5fc3\u5883\uff0c\u4e00\u4e2a\u575a\u6bc5\uff0c\u6c89\u7a33\uff0e\u77eb\u5065\uff0c\u654f\u6377\uff0e\u82f1\u4fca\uff0e\u633a\u9038\u7684 \u5c11\u5e74\uff0c\u7adf\u53d8\u6210\u4e86\u4e00\u4e2a\u75f4\u5446\uff0c\u9ebb\u6728\uff0c\u8fdf\u949d\uff0c\u9893\u9761\uff0c\u82cd\u767d\uff0c\u5b71\u5f31\u7684\u6b8b\u5e9f\uff0c\u800c\u8fd9\u5176\u95f4\u7684\u53d8\u5316\uff0c\u5374\u53ea \u662f\u4e00\u5929\u4e2d\u7684\u4e8b\uff0c\u5979\u6084\u6084\u5730\u8f6c\u8fc7\u8138\uff0c\u53c8\u6709\u4e24\u7c92\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u593a\u7736\u800c\u51fa\uff0c\u7a97\u5916\uff0c\u4e1c\u65b9\uff0c\u5df2\u5fae\u5fae\u6709 \u4e86\u9c7c\u809a\u822c\u7684\u767d\u8272\u3002 \u3000\u3000\u53c8\u662f\u4e00\u5929\u2014\u2014 \u3000\u3000\u4ee5\u540e\u7684\u8bb8\u591a\u5929\u5462\uff1f \u3000\u3000\u5979\u5f00\u59cb\u540e\u6094\uff0c\u4e0d\u8be5\u5230\u897f\u6881\u5c71\u53bb\uff0c\u5979\u4eec\u4e0d\u53bb\u897f\u6881\u5c71\uff0c\u6709\u8bb8\u591a\u4e8b\uff0c\u5c31\u4e0d\u4f1a\u53d1\u751f\u4e86\uff0c\u6700\u5c11\uff0c\u5415 \u5357\u4eba\u4e0d\u4f1a\u4e27\u5931\u5728\u90a3\u65e0\u5e95\u7684\u7edd\u671b\u4e2d\u2026\u2026 \u3000\u3000\u4f46\u662f\u5979\u4e0d\u6562\u8bf4\u51fa\u6765\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u8bf4\u51fa\u6765\u540e\uff0c\u53ea\u4f1a\u66f4\u589e\u52a0\u5979\u7231\u5973\u7684\u60b2\u4f24\u3002 \u3000\u3000\u5979\u53ea\u662f\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u7684\u4f24\uff0c\u5251\u5148\u751f\u548c\u4f60\u5e08\u5085\u5927\u6982\u80fd\u6cbb\u5f97\u597d\uff0c\u4f46\u662f\u2026\u2026\u8981\u5230\u4ec0\u4e48\u5730\u65b9\u624d\u80fd\u627e\u5230\u4ed6\u4eec\u4e24 \u4f4d\u8001\u524d\u8f88\u5462\uff1f\u201d \u3000\u3000\u51cc\u7433\u5931\u795e\u5730\u751f\u5728\u9760\u7a97\u7684\u6905\u5b50\u4e0a\uff0c\u5446\u5446\u5730\u51dd\u89c6\u7740\u7a97\u5916\u7684\u82cd\u7a79\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u6216\u8bb8\u80fd\u6cbb\u5f97\u953a\u9759\u7684\u4f24\uff0c\u4f46\u662f\u2026\u2026\u5357\u4eba\u5462\uff01\u96be\u9053\u4ed6\u4eec\u4e5f\u80fd\u5c06\u5357\u4eba\u6551\u51fa\u90a3\u7edd\u58d1\u5417\uff1f\u201d \u3000\u3000\u5979\u4e00\u5b57\u4e00\u5b57\u5730\u5c06\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u8bf4\u5f97\u90a3\u4e48\u7f13\u6162\uff0c\u5c31\u751f\u50cf\u6bcf\u4e2a\u5b57\u540e\u9762\u90fd\u62d6\u7740\u4e00\u4ed8\u5343\u94a7\u94c1\u4f3c \u7684\u3002 \u3000\u3000\u5b59\u654f\u53ea\u5f97\u53c8\u65e0\u8a00\u5730\u53f9\u606f\u4e86\uff0c\u5979\u5f00\u59cb\u8f7b\u8f7b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u4f24\u5f97\u771f\u91cd\uff01\u4ed6\u6b66\u529f\u5df2\u88ab\u5e9f\uff0c\u53ea\u6015\u518d\u4e5f\u53d7\u4e0d\u5f97\u8f66\u9a6c\u98a0\u7c38\u4e86\uff0c\u6211\u4eec\u53ea\u6709\u5728\u8fd9\u91cc\u7b49\u4ed6 \u4f24\u52bf\u75ca\u6108\uff0c\u5509\u2026\u2026\u4f24\u52bf\u75ca\u6108\u2026\u2026\u4ed6\u53c8\u600e\u4e48\u4f1a\u75ca\u6108\u5462\uff01\u4ed6\u80a2\u4f53\u5df2\u6b8b\uff0c\u4ed6\u5fc3\u91cc\u7684\u521b\u75d5\u53ea\u6015\u518d\u4e5f\u4e0d \u4f1a\u75ca\u6108\u4e86\uff01\u201d \u3000\u3000\u51cc\u7433\u5374\u4ecd\u5446\u5446\u5730\u51dd\u89c6\u7740\u7a97\u5916\u3002 \u3000\u3000\u201c\u53ef\u662f\u4ed6\u8fd8\u6d3b\u7740\uff0c\u5988\uff01\u4e0d\u662f\u5417\uff1f\u6d3b\u7740\uff0c\u603b\u6bd4\u6b7b\u4e86\u8981\u597d\u7684\u591a\u4e86\uff01\u201d \u3000\u3000\u5979\u8bdd\u5934\u5374\u4ecd\u53c8\u56de\u5230\u5415\u5357\u4eba\u8eab\u4e0a\uff0c\u5979\u613f\u610f\u727a\u7272\u81ea\u5df1\u4e00\u5207\u5e78\u798f\u548c\u6b22\u7b11\uff0c\u53bb\u6362\u53d6\u5415\u5357\u4eba\u7684\u6027 \u547d\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6b7b\u53bb\u4e86\u7684\u751f\u547d\uff0c\u53c8\u5c82\u662f\u4efb\u4f55\u4ee3\u4ef7\u6240\u80fd\u6362\u56de\u7684\u5462\uff1f \u3000\u3000\u953a\u9759\u7ec8\u4e8e\u6e10\u6e10\u75ca\u6108\u4e86\u2014\u2014\u6b63\u5982\u5b59\u654f\u6240\u8bf4\uff0c\u65ad\u53bb\u7684\u81c2\u8180\u4e0d\u4f1a\u91cd\u751f\uff0c\u5fc3\u91cc\u7684\u521b\u4f24\uff0c\u66f4\u4e0d\u5bb9\u6613 \u75ca\u6108\u3002 \u3000\u3000\u4ece\u6e05\u6668\u5230\u767d\u663c\uff0c\u4ece\u767d\u663c\u5230\u9ec4\u660f\uff0c\u4ece\u9ec4\u660f\u5230\u9ed1\u591c\uff0c\u4ece\u9ed1\u6697\u53c8\u5230\u6e05\u6668\u2026\u2026 \u3000\u3000\u4ed6\u53ea\u662f\u75f4\u75f4\u5730\u5446\u5750\u7740\uff0c\u9762\u5bb9\u82cd\u767d\uff0c\u795e\u7cbe\u5446\u6728\uff0c\u76ee\u5149\u7a7a\u6d1e\u2014\u2014\u9664\u4e86\u5728\u671b\u5411\u51cc\u7433\u7684\u65f6\u5019\uff0c\u4f46 \u662f\uff0c\u51cc\u7433\u5374\u53c8\u50cf\u4ed6\u4e00\u6837\u9ebb\u6728\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u5929\uff0c\u4ed6\u4eec\u4ece\u672a\u8e0f\u51fa\u8fc7\u8fd9\u5ba2\u6808\u4e00\u6b65\uff0c\u4e16\u4e0a\u7684\u6240\u6709\u4e00\u5207\uff0c\u5728\u8fd9\u8bb8\u591a\u5929\u4e2d\uff0c\u4f3c \u4e4e\u5df2\u548c\u4ed6\u4eec\u5b8c\u5168\u65ad\u7edd\u4e86\u5173\u7cfb\u3002 \u3000\u3000\u953a\u9759\u60f3\u7740\u7684\u4f3c\u4e4e\u53ea\u6709\u51cc\u7433\u3002 \u3000\u3000\u51cc\u7433\u60f3\u7740\u7684\u81ea\u7136\u53ea\u6709\u5415\u5357\u4eba\u4e86\u3002 \u3000\u3000\u800c\u5b59\u654f\u7684\u4e00\u7f15\u5e7d\u601d\uff0c\u6ee1\u8154\u70ed\u7231\uff0c\u5374\u5316\u505a\u8bb8\u591a\u4efd\uff0c\u5206\u8d60\u7ed9\u8bb8\u591a\u4eba\uff01 \u3000\u3000\u5415\u5357\u4eba\uff0c\u51cc\u7433\uff0c\u953a\u9759\uff0c\u751a\u81f3\u90a3\u65e9\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u6709\u5982\u5929\u9645\u795e\u9f99\u7684\u6b66\u6797\u5f02\u4eba\u201c\u4e09\u5fc3\u795e\u541b\u201d\u4e0e \u5251\u5148\u751f\uff01 \u3000\u3000\u7ec8\u4e8e\u2014\u2014 \u3000\u3000\u953a\u9759\u7684\u4f24\u53e3\u5df2\u5408\uff0c\u5df2\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u5b59\u654f\u603b\u7b97\u653e\u4e0b\u4e00\u534a\u5fc3\u4e8b\uff0c\u800c\u51cc\u7433\u5374\u53c8\u5f00\u59cb\u903c\u7740\u5979\u6bcd \u4eb2\uff0c\u518d\u5230\u897f\u6881\u5c71\u53bb\u3002 \u3000\u3000\u201c\u6211\u4eca\u751f\u7eb5\u7136\u518d\u4e5f\u89c1\u4e0d\u7740\u5357\u4eba\uff0c\u53ef\u662f\u6211\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u518d\u89c1\u4ed6\u7684\u9aa8\u4e00\u9762\uff01\u201d \u8fd9\u5c31\u662f\u51cc\u7433\u7684\u8bdd\uff0c\u8fd9\u5c31\u662f\u51cc\u7433\u7684\u5fc3\u610f\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e5d\u5341\u56db\u7ae0\u3000\u4e00\u8baf\u51b2\u5929>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u5341\u56db\u7ae0\u3000\u4e00\u8baf\u51b2\u5929 \u3000\u3000\u623f\u95e8\u7a81\u5730\u54cd\u4e86\u3002 \u3000\u3000\u51cc\u7433\u76b1\u7740\u7709\u6253\u5f00\u623f\u95e8\uff0c\u79cb\u6ce2\u8f6c\u5904\uff0c\u9762\u5bb9\u5fae\u53d8\uff0c\u8f7b\u53f1\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u662f\u8c01\uff1f\u6765\u6b64\u4f55\u5e72\uff1f\u201d \u3000\u3000\u95e8\u5916\u7b14\u76f4\u5730\u5e76\u80a9\u7acb\u7740\u56db\u4e2a\u6ee1\u8eab\u94f6\u886b\u7684\u5927\u6c49\uff0c\u94f6\u5dfe\u5305\u5934\uff0c\u94f6\u5e26\u624e\u8170\uff0c\u624b\u4e2d\u5374\u5404\u5404\u6367\u7740\u4e00\u4e2a \u94f6\u8272\u62dc\u76d2\uff0c\u5f53\u5148\u4e00\u4e2a\u6c49\u5b50\u8eac\u8eab\u9053\uff1a \u3000\u3000\u201c\u5c0f\u7684\u4eec\u5949\u655d\u6559\u6559\u4e3b\u4e4b\u547d\uff0c\u9001\u4e0a\u56db\u8272\u6c34\u793c\uff0c\u671b\u8bf7\u7b11\u7eb3\uff01\u201d \u3000\u3000\u5b59\u654f\u5fc3\u5934\u4e00\u51db\uff0c\u6c89\u58f0\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\u4eec\u662f\u90a3\u4e00\u6d3e\u9ad8\u4eba\uff1f\u8d35\u6559\u6559\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u4f3c\u4e4e\u4ed6\u5df2\u770b\u51fa\u623f\u4e2d\u8fd9\u4e24\u4e2a\u5973\u5b50\u4ea6\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u5148\u524d\u90a3\u79cd\u62d8\u8c28\u7684\u795e\u6001\uff0c \u4fbf\u8f83\u4e3a\u8f7b\u677e\u4e86\u4e9b\uff0c\u542b\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u655d\u6d3e\u5d1b\u8d77\u6c5f\u6e56\uff0c\u624d\u4e0d\u8fc7\u6708\u4f59\uff0c\u60f3\u5fc5\u4e24\u4f4d\u672a\u66fe\u542c\u8d77\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u4e00\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u5c0f\u7684\u4eec\u53ef\u5411\u4e24\u4f4d\u4fdd\u8bc1\uff0c\u4e0d\u51fa\u4e09\u6708\uff0c\u6c5f\u6e56\u4e2d\u5c31\u5168\u90fd\u4f1a\u77e5\u9053\u655d\u6d3e\u7684\u58f0\u540d\uff0c\u6709\u5982\u6b64\u523b\u4eba \u4eec\u5168\u90fd\u77e5\u9053\u2014\u2014\u201c\u5929\u4e89\u6559\u201d\u4e00\u6837\uff01\u201d \u3000\u3000\u5b59\u654f\u9762\u8272\u5fae\u9701\uff0c\u4e00\u53cc\u67f3\u7709\uff0c\u5374\u76b1\u5f97\u66f4\u7d27\u4e86\uff0c\u6c89\u58f0\u53c8\u9053\uff1a \u3000\u3000\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u670b\u53cb\u60f3\u5fc5\u4e0d\u662f\u5929\u4e89\u6559\u6d3e\u6765\u7684\u4e86\uff0c\u4e0d\u77e5\u8d35\u6d3e\u4e0e\u5929\u4e89\u6559\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9762\u5bb9\u4e00\u6574\uff0c\u6b63\u8272\u9053\uff1a \u3000\u3000\u201c\u655d\u6d3e\u975e\u4f46\u4e0e\u5929\u4e89\u6559\u6beb\u65e0\u5e72\u7cfb\uff0c\u800c\u4e14\u2026\u2026\u65e5\u540e\u4e24\u4f4d\u81ea\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u8083\u5bb9\u6b65\u5165\uff0c\u5c06\u90a3\u56db\u4e2a\u94f6\u8272\u62dc\u76d2\uff0c\u4e00\u9f50\u653e\u5230\u684c\u4e0a\uff0c\u76ee\u5149\u5411\u50f5\u5750\u684c\u65c1\u7684\u953a \u9759\u4e00\u8f6c\uff0c\u9762\u4e0a\u4f3c\u4e4e\u5fae\u9732\u60ca\u8be7\u4e4b\u8272\u3002 \u3000\u3000\u5374\u542c\u5b59\u654f\u53c8\u9053\uff1a \u3000\u3000\u201c\u8d35\u6559\u6559\u4e3b\u662f\u8c01\uff1f\u6211\u7b49\u7d20\u4e0d\u76f8\u8bc6\uff0c\u600e\u53ef\u65e0\u7aef\u53d7\u793c\uff0c\u8fd8\u8bf7\u56db\u4f4d\u670b\u53cb\u5e26\u56de\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u5979\u8001\u4e8e\u4e16\u6545\uff0c\u6b64\u523b\u5fc3\u4e2d\u81ea\u7136\u60ca\u7591\u4ea4\u96c6\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u7a81\u5730\u9001\u6765\u8fd9\u56db\u8272\u793c\u7269\uff0c\u7a76\u7adf\u6709\u4f55\u7528 \u610f\uff1f \u3000\u3000\u90a3\u6c49\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u5609\u5174\u57ce\u4e2d\u5bb6\u5bb6\u6237\u6237\uff0c\u90fd\u6536\u4e0b\u4e86\u655d\u6d3e\u4e4b\u793c\uff0c\u4e24\u4f4d\u5982\u4e0d\u6536\u4e0b\uff0c\u5374\u6559\u5c0f\u7684\u5982\u4f55\u56de\u53bb\u4ea4\u5f85\uff1f\u201d \u3000\u3000\u5b59\u654f\uff0c\u51cc\u7433\u9f50\u90fd\u4e00\u6123\uff01\u5927\u5947\u9053\uff1a \u3000\u3000\u201c\u5bb6\u5bb6\u6237\u6237\uff0c\u90fd\u6536\u4e0b\u4e86\u8d35\u6d3e\u4e4b\u793c\uff01\u96be\u9053\u8d35\u6d3e\u7adf\u5907\u4e0b\u6570\u5341\u4e07\u4efd\u793c\u7269\uff0c\u5728\u5609\u5174\u57ce\u6328\u5bb6\u6328\u6237\u5730 \u9001\u4e86\u4e00\u904d\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u53c8\u81ea\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u8eac\u8eab\u4e00\u793c\uff0c\u9000\u51fa\u95e8\u5916\uff0c\u8f7b\u8f7b\u5e26\u4e0a\u623f\u95e8\uff0c\u5b59\u654f\u6115\u4e86\u4e00\u6115\uff0c\u9001\u5c06\u51fa\u53bb\uff0c\u5374\u89c1\u8fd9\u56db\u4e2a\u795e\u79d8\u7684\u94f6 \u886b\u6c49\u5b50\uff0c\u65e9\u5df2\u8d70\u51fa\u8fd9\u5c0f\u5c0f\u7684\u8de8\u9662\u4e86 \u3000\u3000\u56db\u4e2a\u94f6\u8272\u62dc\u76d2\uff0c\u6574\u9f50\u5730\u653e\u5728\u684c\u4e0a\uff0c\u4e00\u65b9\u94f6\u8272\u7684\u62dc\u5e16\uff0c\u5e73\u6574\u5730\u538b\u5728\u76d2\u89d2\uff1b\u5341\u516d\u4e2a\u79c0\u9038\u7684\u5b57 \u8ff9\uff0c\u6574\u9f50\u5730\u5199\u5728\u62dc\u5e16\u4e0a\uff01 \u3000\u3000\u201c\u5f3a\u6743\u5fc5\u706d\uff0c\u6b63\u4e49\u5fc5\u5f20\uff0c\u56db\u8272\u83f2\u793c\uff0c\u656c\u8bf7\u7b11\u7eb3\uff01\u201d \u3000\u3000\u4e0b\u9762\u7f72\u540d\uff0c\u7adf\u662f\uff1a\u201c\u6b63\u4e49\u5e2e\u4e3b\u8c28\u62dc\u201d\u3002 \u3000\u3000\u8fd9\u201c\u6b63\u4e49\u5e2e\u4e3b\u201d\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u4ed6\u8981\u82b1\u8d39\u8fd9\u4e48\u591a\u4eba\u529b\u7269\u529b\uff0c\u5728\u5609\u5174\u57ce\u4e2d\u6328\u6237\u9001\u4e0b\u8fd9\u4e00\u4efd\u539a \u793c\uff0c\u6700\u5947\u602a\u7684\u662f\uff1b\u4ed6\u600e\u4f1a\u6709\u5982\u6b64\u4e30\u5bcc\u7684\u4eba\u529b\u7269\u529b\uff0c\u83ab\u662f\u8bf4\u76d2\u4e2d\u793c\u7269\uff0c\u5355\u7957\u8fd9\u6570\u5341\u4e07\u4e2a\u76d2\u5b50\uff0c \u5df2\u4e0d\u662f\u5e38\u4eba\u68a6\u60f3\u80fd\u505a\u5230\u4e4b\u4e8b\u3002 \u3000\u3000\u5b59\u654f\u867d\u7136\u8001\u4e8e\u4e16\u6545\uff0c\u9605\u5386\u6781\u4e30\uff0c\u6b64\u523b\u5374\u4ecd\u4e0d\u7981\u4e3a\u4e4b\u8ff7\u60d1\uff0c\u5979\u4ece\u672a\u60f3\u5230\u8fc7\u4e16\u4e0a\u7adf\u4f1a\u6709\u5982\u6b64 \u4eba\u7269\uff01\u505a\u51fa\u5982\u6b64\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\uff01 \u3000\u3000\u5979\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u7a81\u53c8\u5fae\u62e7\u7ea4\u8170\uff0c\u8f6c\u8eab\u5954\u4e86\u51fa\u53bb\uff0c\u5979\u4e00\u5fc3\u63a2\u7a76\u51fa\u8fd9\u4e9b\u51e0\u4e4e\u4e0d\u53ef\u89e3\u91ca\u7684 \u95ee\u9898\u7684\u7b54\u6848\uff0c\u4f46\u662f\u90a3\u56db\u4e2a\u795e\u79d8\u7684\u94f6\u886b\u6c49\u5b50\uff0c\u6b64\u523b\u5374\u5df2\u4e0d\u77e5\u8d70\u5230\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u4e00\u9635\u60a0\u626c\u7684\u4e50\u58f0\uff0c\u968f\u98ce\u81ea\u6237\u5916\u98d8\u6765\uff0c\u5979\u67f3\u7709\u5fae\u76b1\uff0c\u8ffd\u5bfb\u7740\u8fd9\u4e50\u58f0\u7684\u65b9\u5411\uff0c\u8d70\u4e86\u51fa\u53bb\uff0c\u5374 \u89c1\u8fd9\u5bb6\u5ba2\u6808\u95e8\u524d\uff0c\u5df2\u62e5\u6ee1\u4e86\u7a83\u7a83\u79c1\u8bae\uff0c\u4e0d\u4f4f\u853c\u53f9\u7684\u4eba\u7fa4\uff0c\u5979\u8fdf\u7591\u534a\u664c\uff0c\u4ea6\u81ea\u4ece\u8ba9\u5f00\u7684\u4eba\u7fa4\u4e2d \u8d70\u5230\u8857\u5934\uff0c\u79cb\u6ce2\u5fae\u8f6c\uff0c\u76ee\u5149\u671b\u5904\uff0c\u5374\u4e5f\u4e0d\u7981\u53d1\u51fa\u4e00\u58f0\u8f7b\u5fae\u7684\u60ca\u53f9\uff0c\u5979\u53ea\u89c1\u2026\u2026\u8857\u7684\u5c3d\u5934\u4e0a\uff0c \u6b64\u523b\u6b63\u6709\u4e00\u884c\u4eba\u9a6c\uff0c\u7f13\u7f13\u884c\u6765\u3002\u4e09\u5341\u516d\u5bf9\u94f6\u886b\u66f3\u5730\uff0c\u79c0\u53d1\u5982\u4e91\u7684\u5999\u9f84\u5c11\u5973\uff0c\u4e00\u9762\u5439\u594f\u7740\u624b\u4e2d \u7684\u7eaf\u94f6\u7bab\u7b1b\u7b14\u7436\uff0c\u4e00\u9762\u5f53\u5148\u884c\u6765\uff0c\u540e\u9762\u7d27\u8ddf\u7740\u4e09\u5341\u516d\u5bf9\u663e\u89c1\u7ecf\u8fc7\u4e25\u5bc6\u6311\u9009\u7684\u7eaf\u767d\u826f\u9a79\uff0c\u7eaf\u94f6 \u978d\u8f94\uff0c\u94f6\u4e1d\u7ef3\uff0c\u4e09\u5341\u516d\u5bf9\u7709\u6e05\u76ee\u79c0\u7684\u5c11\u5e74\uff0c\u7275\u7740\u94f6\u4e1d\u7ef3\uff0c\u968f\u7740\u4e50\u58f0\uff0c\u7f13\u6b65\u800c\u884c\uff01 \u3000\u3000\u7136\u540e\u662f\u4e00\u9876\u94f6\u5149\u95ea\u95ea\u7684\u5927\u8f7f\uff0c\u7eaf\u94f6\u8f7f\u9876\u94f6\u4e1d\u5782\uff0c\u5341\u516d\u5bf9\u94f6\u8272\u52b2\u88c5\u5927\u6c49\uff0c\u624b\u6258\u8f7f\u7aff\u7f13\u6b65\u800c \u884c\uff0c\u7136\u540e\u53c8\u662f\u4e03\u5341\u4e8c\u5bf9\u5c11\u5e74\u7537\u5973\uff0c\u638c\u4e2d\u5404\u5404\u6258\u7740\u4e00\u65b9\u94f6\u8272\u62dc\u76d2\uff0c\u968f\u5728\u8f7f\u540e\u3002 \u3000\u3000\u65e5\u6e10\u897f\u6c89\uff0c\u5374\u672a\u897f\u6c89\u3002 \u3000\u3000\u6f2b\u5929\u7684\u9633\u5149\uff0c\u5c06\u8fd9\u795e\u79d8\u8be1\u5f02\uff0c\u4ece\u6765\u672a\u89c1\u7684\u884c\u5217\uff0c\u6620\u5f97\u4ee4\u4eba\u8000\u76ee\u751f\u82b1\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u60f3\u5fc5\u5c31\u662f\u90a3\u201c\u6b63\u4e49\u5e2e\u201d\u4e2d\u4eba\u4e86\uff0c\u8f7f\u4e2d\u5750\u7684\uff0c\u60f3\u5fc5\u5c31\u662f\u90a3\u6b63\u4e49\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u5b59\u654f\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u60f3\u8981\u63a0\u4e0a\u524d\u53bb\uff0c\u6380\u5f00\u6df1\u6df1\u5782\u4e0b\u7684\u8f7f\uff0c\u770b\u770b\u8f7f\u4e2d\u5750\u7684\uff0c\u8fd9\u5bcc\u53ef\u654c\u56fd\uff0c\u795e\u79d8 \u8be1\u5f02\uff0c\u6709\u5982\u5929\u9645\u795e\u9f99\uff0c\u500f\u7136\u964d\u4e34\u4eba\u95f4\uff0c\u53c8\u6709\u5982\u5341\u5f69\u83b2\u82b1\uff0c\u5e73\u5730\u6d8c\u8d77\u6b66\u6797\u7684\u201c\u6b63\u4e49\u5e2e\u4e3b\u201d\uff0c\u7a76 \u7adf\u662f\u600e\u6837\u7684\u4eba\u7269\uff1f \u3000\u3000\u81ea\u53e4\u4ee5\u6765\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u85c9\u85c9\u65e0\u540d\u7684\u4eba\u7269\uff0c\u7a81\u7136\u7531\u5e73\u6de1\u8d8b\u4e8e\u7eda\u70c2\u3002\u50cf\u5947\u8ff9\u822c\u626c\u540d\u4e8e\u6c5f\u6e56\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5374\u4ece\u672a\u6709\u4e00\u4eba\uff0c\u50cf\u8fd9\u201c\u6b63\u4e49\u5e2e\u4e3b\u201d\u5982\u6b64\u795e\u5947\uff0c\u5982\u6b64\u58f0\u52bf\uff0c\u5982\u6b64\u8be1\u79d8\u2026\u2026 \u3000\u3000\u5b59\u654f\u5fc3\u5ff5\u6570\u8f6c\uff0c\u6697\u81ea\u5bfb\u601d\uff1a \u3000\u3000\u201c\u4ed6\u6216\u8005\u539f\u672c\u5c31\u662f\u4e2a\u540d\u58f0\u751a\u7740\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u4f46\u662f\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5f04\u8fd9\u4e9b\u7384\u865a\u5462\uff1f\u96be \u9053\u2026\u2026\u201d \u3000\u3000\u90a3\u77e5\u5979\u5fc3\u5ff5\u5c1a\u672a\u8f6c\u5b8c\uff0c\u5374\u542c\u90a3\u500f\u626c\u8f7b\u67d4\u7684\u4e50\u58f0\uff0c\u7a81\u5730\u53d8\u7684\u70ed\u70c8\u6fc0\u6602\uff0c\u88c2\u77f3\u7a7f\u4e91\uff0c\u4e50\u58f0\u65b9 \u53d8\uff0c\u90a3\u4e03\u5341\u4e8c\u5bf9\u624b\u6367\u94f6\u76d2\u7684\u5c11\u5e74\u7537\u5973\uff0c\u7a81\u5730\u811a\u6b65\u5fae\u987f\uff0c\u624b\u638c\u5fae\u626c\uff0c\u4e00\u624b\u5c06\u94f6\u76d2\u76d2\u76d6\u6380\u5f00\u2026\u2026 \u3000\u3000\u53ea\u542c\u4e00\u9635\u632f\u8033\u7684\u94f6\u94c3\u4e4b\u58f0\uff0c\u968f\u7740\u6570\u767e\u53ea\u9888\u7cfb\u94f6\u94c3\u7684\u5065\u7ffc\u94f6\u9e3d\uff0c\u51b2\u5929\u98de\u8d77\u3002 \u3000\u3000\u6bcf\u65b9\u94f6\u76d2\u4e4b\u4e2d\uff0c\u7adf\u5404\u98de\u51fa\u56db\u53ea\u94f6\u9e3d\uff0c\u800c\u6bcf\u56db\u53ea\u94f6\u9e3d\u8db3\u4e0a\uff0c\u7adf\u4ff1\u90fd\u7f1a\u7740\u4e00\u9762\u7384\u4e4c\u4e1d\u5dfe\uff0c\u94f6 \u9e3d\u98de\u8d77\uff0c\u9e1f\u5dfe\u5782\u4e0b\uff0c\u56db\u9e3d\u4e00\u5dfe\uff0c\u4e00\u5dfe\u4e8c\u4e08\u3002 \u3000\u3000\u970e\u773c\u4e4b\u95f4\u2026\u2026 \u3000\u3000\u53ea\u542c\u94c3\u58f0\u6f2b\u5929\uff0c\u53ee\u5f53\u4e0d\u7edd\u3002 \u3000\u3000\u53ea\u89c1\u94f6\u7ffc\u7ff1\u7fd4\uff0c\u4f4e\u56de\u98de\u821e\u3002 \u3000\u3000\u800c\u90a3\u767e\u5341\u9762\u7384\u4e4c\u4e1d\u5dfe\u4e4b\u4e0a\uff0c\u6bcf\u9762\u5404\u6709\u5341\u6570\u4e2a\u5f84\u5c3a\u94f6\u5b57\uff0c\u51cc\u7a7a\u95ea\u95ea\u751f\u5149\u3002 \u3000\u3000\u5b59\u654f\u60ca\u53f9\u4e4b\u4e2d\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u8fd9\u5341\u6570\u94f6\u5b57\uff0c\u6709\u7684\u8d6b\u7136\u7adf\u662f\uff01 \u3000\u3000\u201c\u6b63\u4e49\u5e2e\u4e3b\u8c28\u5411\u5929\u4e89\u6559\u4e3b\u8427\u65e0\u6311\u6218\uff01\u201d \u3000\u3000\u6709\u7684\u5374\u662f\uff1a \u3000\u3000\u201c\u516b\u6708\u4e2d\u79cb\uff0c\u70df\u96e8\u697c\u5934\uff0c\u656c\u5019\u5927\u9a7e\u3002\u201d \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u7eb5\u662f\u76ee\u529b\u7a0d\u5dee\u4e4b\u4eba\uff0c\u4e5f\u5c06\u8fd9\u4e9b\u94f6\u5149\u95ea\u95ea\u7684\u5b57\u8ff9\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4e50\u58f0\u518d\u53d8\uff0c \u7a81\u5730\u53d8\u4e3a\u4e00\u957f\u58f0\u5c16\u9510\u7684\u54e8\u58f0\u3002 \u3000\u3000\u90a3\u6570\u5341\u53ea\u5065\u7ffc\u94f6\u9e3d\uff0c\u56db\u4e2a\u4e00\u7fa4\uff0c\u6709\u7684\u98de\u5411\u4e1c\u65b9\uff0c\u6709\u7684\u98de\u5411\u897f\u65b9\uff0c\u6709\u7684\u98de\u5411\u5357\u65b9\uff0c\u6709\u7684\u98de \u5411\u5317\u65b9\uff0c\u5239\u90a3\u95f4\u4f7f\u5df2\u53bb\u8fdc\uff0c\u53ea\u5269\u4e0b\u8fdc\u5904\u5929\u9645\uff0c\u4e0d\u65f6\u8fd8\u89c1\u4e4c\u5dfe\u98d8\u821e\uff0c\u94f6\u7ffc\u7ff1\u7fd4\uff0c\u94c3\u58f0\u2026\u2026 \u3000\u3000\u4e50\u58f0\u518d\u6b21\u4e00\u53d8\uff0c\u884c\u5217\u4f9d\u7136\u524d\u884c\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u201c\u6b63\u4e49\u5e2e\u4e3b\uff0c\u8c28\u5411\u5929\u4e89\u6559\u4e3b\u8427\u65e0\u6311\u6218\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e2a\u8db3\u4ee5\u60ca\u5929\u52a8\u5730\uff0c\u9707\u64bc\u6c5f\u6e56\u7684\u8baf\u606f\uff0c\u5374\u5df2\u968f\u7740\u8fd9\u6f2b\u5929\u7684\u94f6\u9e3d\u94f6\u7fce\uff0c\u4f20\u9001\u5230\u4e1c\u65b9\uff0c\u4f20 \u9001\u5230\u897f\u65b9\uff0c\u4f20\u9001\u5230\u5357\u65b9\uff0c\u4f20\u9001\u5230\u5317\u65b9\u2026\u2026 \u4f20\u9001\u5230\u666e\u5929\u4e4b\u4e0b\u6b66\u6797\u6c5f\u6e56\uff0c\u6bcf\u4e00\u4e2a\u5730\u65b9\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e5d\u5341\u4e94\u7ae0\u3000\u7a97\u4e2d\u4eba\u8bed>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u5341\u4e94\u7ae0\u3000\u7a97\u4e2d\u4eba\u8bed \u3000\u3000\u76f4\u5230\u884c\u5217\u5df2\u7ecf\u53bb\u5f97\u5f88\u8fdc\uff0c\u5b59\u654f\u5374\u4ecd\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5979\u542c\u5230\u6ee1\u8033\u6742\u4e71\u7684\u4f4e\u8bed\u548c\u60ca\u53f9\uff0c\u5979 \u4e5f\u770b\u5230\u8857\u4e0a\u4eba\u7fa4\u4e2d\uff0c\u6709\u6570\u5341\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u6084\u6084\u5730\u5c3e\u968f\u7740\u8fd9\u4e00\u884c\u8be1\u5f02\uff0c\u4f46\u5374\u7729\u76ee\u7684\u884c\u5217\u8d70\u53bb\u3002 \u3000\u3000\u5979\u7565\u4e3a\u8fdf\u7591\u534a\u664c\uff0c\u5374\u89c1\u5bf9\u8857\u7adf\u6709\u4e24\u6761\u9ed1\u8863\u5927\u6c49\uff0c\u76ee\u5149\u707c\u707c\u5730\u671b\u7740\u81ea\u5df1\uff0c\u5979\u4e00\u62e2\u9b13\u53d1\uff0c\u6084 \u7136\u8d70\u56de\u5e97\u4e2d\uff0c\u5728\u5979\u5185\u5fc3\u7684\u6df1\u5904\uff0c\u867d\u7136\u4e0d\u6b62\u4e00\u6b21\uff0c\u6709\u7740\u4e5f\u60f3\u5c3e\u968f\u8fd9\u795e\u79d8\u7684\u884c\u5217\uff0c\u53bb\u4e00\u63a2\u7a76\u7adf\u7684 \u51b2\u52a8\uff0c\u4f46\u662f\u751f\u6d3b\u7684\u78e8\u7ec3\uff0c\u5374\u4f7f\u5f97\u5979\u53ea\u662f\u5c06\u8fd9\u4efd\u51b2\u52a8\uff0c\u6df1\u6df1\u5730\u9690\u85cf\uff0c\u538b\u5236\u4e86\u4e0b\u6765\uff0c\u56e0\u4e3a\u5979\u77e5\u9053 \u5979\u81ea\u5df1\u5df2\u6709\u4e86\u592a\u591a\u8981\u505a\u7684\u4e8b\uff0c\u800c\u4e00\u4e2a\u50cf\u5979\u8fd9\u6837\u6709\u7740\u592a\u591a\u4e8b\u8981\u505a\u7684\u4eba\uff0c\u662f\u4e0d\u8be5\u518d\u53bb\u7406\u4f1a\u8fd9\u4e9b\u4e0e \u5df1\u65e0\u5173\u7684\u4e8b\u4e86\uff0c\u7eb5\u7136\u8fd9\u4e9b\u4e8b\u662f\u90a3\u4e48\u591a\u5f69\u548c\u7729\u76ee\u3002 \u3000\u3000\u90a3\u56db\u65b9\u94f6\u8272\u7684\u793c\u76d2.\u4ecd\u5b89\u9759\u5730\u6392\u5217\u5728\u684c\u4e0a\uff0c\u5979\u4f4e\u5ff5\u7740\u76d2\u8fb9\u5b57\u7b3a\u4e0a\u7684\u5b57\u8ff9\uff1a\u201c\u5f3a\u6743\u5fc5\u8d25\uff0c \u6b63\u4e49\u5fc5\u5f20\u2026\u2026\u201d\u5979\u5634\u89d2\u5f00\u59cb\u6cdb\u51fa\u4e00\u4e1d\u6de1\u6de1\u7684\u5fae\u7b11\uff0c\u800c\u5979\u7684\u5973\u513f\u51cc\u7433\u548c\u953a\u9759\uff0c\u5374\u4ecd\u7136\u5446\u5446\u5730\u5750 \u5728\u6905\u4e0a\u3002 \u3000\u3000\u5979\u76ee\u5149\u8f6c\u5411\u8fd9\u4e00\u53cc\u5fe7\u90c1\u7684\u5c11\u5e74\uff0c\u5fc3\u4e8b\u6d8c\u8d77\uff0c\u5fae\u7b11\u6d88\u5931\uff0c\u6709\u4e00\u4e9b\u8bdd\uff0c\u5979\u5728\u5fc3\u4e2d\u5df2\u9690\u85cf\u4e86\u8bb8 \u591a\u65e5\u5b50\uff0c\u5979\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6b64\u523b\uff0c\u5f53\u5979\u7684\u76ee\u5149\u8f6c\u5411\u8fd9\u4e00\u4e2a\u5c11\u5e74\u65f6\uff0c\u5979\u5fcd\u4e0d\u4f4f\u5728\u5fc3\u91cc\u4e0b\u4e86\u4e2a\u51b3\u5b9a\uff1a \u3000\u3000\u201c\u6211\u4e00\u5b9a\u8981\u544a\u8bc9\u4ed6\uff0c\u4e5f\u8bb8\u8fd9\u4e00\u4efd\u5feb\u4e50\uff0c\u80fd\u591f\u51b2\u6de1\u4ed6\u5fc3\u4e2d\u7684\u75db\u82e6\u4e0e\u6050\u60e7\uff0c\u5509\u2026\u2026\u201d \u3000\u3000\u957f\u53f9\u4e00\u58f0\uff0c\u5979\u51dd\u89c6\u7740\u7a97\u65c1\u7684\u5c11\u5e74\uff0c\u5979\u4f46\u613f\u80fd\u4ee5\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u91cd\u65b0\u71c3\u70e7\u8d77\u8fd9\u5c11\u5e74\u751f\u547d\u4e2d\u5df2 \u5c06\u7184\u706d\u7684\u706b\u82b1\u3002 \u3000\u3000\u53c8\u662f\u4e00\u5929\u65f6\u5149\u6d41\u53bb\uff0c\u591c\u6df1\u4e86\u3002 \u3000\u3000\u5609\u5174\u57ce\u4e2d\uff0c\u7a81\u5730\u8f7b\u70df\u822c\u968f\u98ce\u98d8\u5165\u4e00\u6761\u4eba\u5f71\uff0c\u4ed6\u6765\u5f97\u5c31\u50cf\u665a\u98ce\u822c\u90a3\u4e48\u8f7b\u7075\uff0c\u90a3\u4e48\u81ea\u7136\u6ed1\u8fc7 \u4e00\u91cd\u53c8\u4e00\u91cd\u7684\u5c4b\u810a\uff0c\u98d8\u8fc7\u4e00\u6761\u53c8\u4e00\u6761\u7684\u8857\u9053\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u7684\u76ee\u529b\uff0c\u80fd\u8fa8\u6e05\u4ed6\u7684\u8eab\u5f62\uff0c\u4e5f \u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u7684\u8111\u6d77\uff0c\u80fd\u68a6\u60f3\u5230\u4ed6\u7684\u8eab\u624b\u3002 \u3000\u3000\u4e94\u6708\u7684\u7a79\u82cd\uff0c\u661f\u7fa4\u95ea\u70c1\uff0c\u4ed6\u5728\u4e00\u5e62\u9ad8\u5927\u7684\u5c4b\u810a\u540e\uff0c\u7565\u4e00\u505c\u987f\uff0c\u503e\u9996\u8f7b\u8f7b\u4e00\u53f9\uff0c\u53f9\u606f\u4e2d\u867d \u6709\u5fe7\u90c1\u548c\u60b2\u75db\uff0c\u4f46\u5374\u5df2\u6709\u7740\u5e78\u798f\u548c\u6b22\u6109\uff0c\u5c31\u50cf\u662f\u6c99\u6f20\u4e2d\u8270\u8f9b\u7684\u65c5\u4eba\uff0c\u7ec8\u4e8e\u671b\u89c1\u4ed6\u7684\u76ee\u7684\u5730\u65f6 \u4e00\u6837\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u76ee\u5149\u95ea\u7535\u822c\u4e00\u8f6c\uff0c\u8fa8\u4e86\u8fa8\u5730\u52bf\u548c\u65b9\u5411\uff0c\u4fbf\u6beb\u4e0d\u72b9\u8c6b\u5730\u63a0\u5411\u5b59\u654f\u6bcd\u5973\u6295\u5bbf\u7684\u5ba2 \u6808\u2026\u2026 \u3000\u3000\u5ba2\u6808\u4e2d\u4eba\u58f0\u5df2\u5bc2\uff0c\u53ea\u6709\u897f\u9762\u7684\u4e00\u95f4\u5c0f\u5c0f\u7684\u8de8\u9662\uff0c\u8fd8\u6709\u5fae\u5f31\u7684\u706f\u706b\uff0c\u4ed6\u76ee\u5149\u518d\u6b21\u8f6c\u52a8\u95f4\uff0c \u4f3c\u5df2\u6d41\u9732\u51fa\u8bb8\u591a\u6b22\u4e50\u7684\u5149\u8f89\uff0c\u811a\u4e0b\u5fae\u52a8\uff0c\u4e00\u63a0\u6570\u4e08\uff0c\u4ed6\u5df2\u7b14\u76f4\u5730\u63a0\u5165\u8fd9\u95f4\u5c0f\u5c0f\u8fa9\u9662\u7684\u7a97\u524d\u3002 \u3000\u3000\u7a81\u5730\uff0c\u660f\u9ec4\u7684\u7a97\u4e2d\uff0c\u98d8\u51fa\u4e00\u4e1d\u5e7d\u6028\uff0c\u6df1\u6c89\uff0c\u5374\u53c8\u5a07\u5f31\u7684\u53f9\u606f\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u53f9\u606f\uff0c\u4f7f\u5f97\u8fd9\u8eab\u5177\u6b66\u6797\u4e2d\u7edd\u9876\u8f7b\u529f\u7684\u4eba\u5f71\uff0c\u50cf\u662f\u7a81\u7136\u88ab\u9b54\u6cd5\u50ac\u7720\u4e86\u4f3c\u7684\uff0c\u500f\u7136\u987f \u4f4f\u8eab\u5f62\uff0c\u5446\u5446\u4f2b\u7acb\u5728\u660f\u9ec4\u7684\u7a97\u69db\u524d\u3002 \u3000\u3000\u53ea\u542c\u7a97\u5185\u53c8\u81ea\u4f20\u51fa\u4e00\u58f0\u53f9\u606f\uff0c\u4e00\u4e2a\u4f4e\u6c89\uff0c\u7f13\u6162\uff0c\u6148\u7965\uff0c\u5a07\u7f8e\u7684\u6210\u719f\u5987\u4eba\u53e3\u97f3\uff0c\u5e26\u7740\u65e0\u9650 \u7684\u5173\u5207\u548c\u7231\u62a4\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff01\u4f60\u8be5\u7761\u4e86\uff0c\u6211\u6709\u51e0\u53e5\u8bdd\uff0c\u60f3\u5bf9\u4f60\u9759\u54e5\u54e5\u8bf4\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u60f3\u7761\uff0c\u6211\u4e0d\u60f3\u2026\u2026\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff0c\u96be\u9053\u6211\u4e0d\u80fd\u542c\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u5a07\u67d4\u7684\u8bdd\u58f0\uff0c\u867d\u7136\u4f4e\u5fae\uff0c\u7136\u800c\u5728\u5982\u6b64\u5b89\u9759\u7684\u6df1\u591c\u91cc\uff0c\u6bcf\u4e2a\u5b57\u5374\u90fd\u6e05\u6670\u5730\u4f20\u5165\u4f2b\u7acb\u5728\u7a97 \u5916\u7684\u4eba\u5f71\u8033\u4e2d\u3002 \u3000\u3000\u4ed6\u811a\u6b65\u7f13\u6162\u79fb\u52a8\u4e86\u4e00\u6b65\uff0c\u5374\u542c\u90a3\u6148\u7965\u7684\u58f0\u97f3\u53c8\u81ea\u54cd\u8d77\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u8bdd\uff0c\u6211\u672c\u6765\u65e9\u5c31\u60f3\u8bf4\u7684\uff0c\u4f46\u662f\u2026\u2026\u4f46\u662f\u2026\u2026\u5509\uff01\u7433\u513f\uff01\u5988\u7684\u5fc3\u610f\uff0c\u6211\u60f3\u4f60\u4e5f\u8be5\u77e5 \u9053\uff0c\u5bf9\u4e8e\u5357\u4eba\u7684\u6b7b\uff0c\u4f60\u867d\u7136\u60b2\u54c0\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u96be\u53d7\u4e48\uff1f\u4f46\u662f\u4f60\u8fd8\u5e74\u8f7b\uff0c\u4f60\u8fd8\u6709\u4e00\u6bb5\u751f\u547d\u4e2d\u6700 \u7f8e\u7684\u65e5\u5b50\u8981\u8fc7\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u500f\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u7a97\u5916\u7684\u4eba\uff0c\u5374\u8d77\u4e86\u4e00\u9635\u8f7b\u5fae\u7684\u98a4\u6296\uff01 \u3000\u3000\u662f\u4e3a\u4e86\u591c\u98ce\u592a\u6025\uff1f\u591c\u5bd2\u592a\u91cd\uff1f\u6291\u6216\u662f\u4e3a\u4e86\u5176\u4ed6\u7684\u539f\u56e0\uff1f \u3000\u3000\u7a97\u5185\u4e5f\u6709\u534a\u664c\u96be\u582a\u7684\u6c89\u5bc2\uff0c\u7a81\u5730\u53c8\u4f20\u51fa\u4e00\u58f0\u5e7d\u5e7d\u7684\u957f\u53f9\uff01 \u3000\u3000\u201c\u5988\uff01\u76f4\u5230\u73b0\u5728\uff0c\u6211\u624d\u77e5\u9053\u60b2\u54c0\u662f\u4ec0\u4e48\u6ecb\u5473\u2026\u2026\u2026\u6211\u80fd\u591f\u6709\u8fd9\u4efd\u60b2\u54c0\u4f34\u6211\u6e21\u8fc7\u4e00\u751f\uff0c\u6211 \u5df2\u7ecf\u5f88\u6ee1\u8db3\u4e86\uff0c\u56e0\u4e3a\u548c\u60b2\u54c0\u4e00\u8d77\u6765\u7684\uff0c\u6211\u8fd8\u6709\u4e00\u4efd\u6b22\u6109\u751c\u7f8e\u7684\u56de\u5fc6\uff0c\u8fd9\u4e0d\u6bd4\u4ec0\u4e48\u90fd\u6ca1\u6709\u7684\u4eba \u8981\u597d\u5f97\u591a\u4e86\u4e48\uff1f\u5988\uff01\u4f60\u653e\u5fc3\uff0c\u4f60\u81ea\u5df1\u53bb\u7761\u5427\uff01\u201d \u3000\u3000\u60b2\u54c0\u7684\u8a00\u8bed\uff0c\u5c31\u50cf\u662f\u4f18\u7f8e\u7684\u6b4c\u66f2\uff0c\u98d8\u51fa\u7a97\u5916\uff0c\u98d8\u5165\u4f2b\u7acb\u7740\u7684\u4eba\u5f71\u8033\u91cc\u3002 \u3000\u3000\u4ed6\u660e\u4eae\u7684\u76ee\u5149\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e86\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u624b\u638c\u4e00\u9635\u75c9\u631b\u4f3c\u5730\u575a\u63e1\uff0c\u7f13\u7f13\u4e3e\u8d77\uff0c\u65b9\u5f85\u62cd\u5411 \u7a97\u69db\u3002 \u3000\u3000\u5374\u542c\u7a97\u4e2d\u53c8\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff01\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6709\u4e9b\u4eba\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u751a\u81f3\u8fde\u56de\u5fc6\u4e5f\u662f\u9eef\u6de1\u800c\u60b2\u60e8\u7684\uff0c\u8fd9\u4e9b\u4eba\u6700\u503c\u5f97 \u6211\u4eec\u53bb\u601c\u60af\u548c\u53f9\u606f\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u660f\u9ec4\u7684\u7a97\u7eb8\u4e2d\uff0c\u6620\u73b0\u51fa\u4e00\u6761\u79c0\u4e3d\u7684\u4eba\u5f71\uff0c\u8fd9\u4eba\u5f71\u7f13\u7f13\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6148\u7965\u7684\u58f0\u97f3\u53c8\u9053\uff1a \u3000\u3000\u201c\u9759\u513f\uff0c\u4ed6\u4e3a\u4e86\u6211\u4eec\uff0c\u727a\u7272\u4e86\u4ec0\u4e48\uff1f\u6211\u4e0d\u8bf4\u4f60\u4e5f\u77e5\u9053\uff0c\u4ed6\u5bf9\u4f60\u7684\u60c5\u611f\uff0c\u4f60\u4e5f\u4f1a\u77e5\u9053\u5f97\u6bd4 \u6211\u6e05\u695a\uff0c\u4ed6\u4e00\u751f\u5b64\u82e6\uff0c\u73b0\u5728\u771f\u7684\u662f\u4ec0\u4e48\u4e5f\u6ca1\u6709\u4e86\uff0c\u751a\u81f3\u8fde\u6b66\u529f\u90fd\u5b8c\u5168\u5931\u53bb\uff0c\u4ed6\u8fd9\u4e9b\u8eab\u4f53\u4e0a\u7684 \u6b8b\u4f24\u59d1\u4e14\u4e0d\u53bb\u8bf4\u4ed6\uff0c\u7136\u800c\u4ed6\u7684\u5fc3\u5374\u5df2\u6b7b\u4e86\uff0c\u54c0\u83ab\u5927\u4e8e\u5fc3\u6b7b\uff0c\u4e16\u4e0a\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u75db\u82e6\uff0c\u80fd\u6bd4\u5f97 \u4ed6\u6b64\u523b\u6240\u627f\u53d7\u7684\u2026\u2026\u201d \u3000\u3000\u4e00\u58f0\u53f9\u606f\uff1a \u3000\u3000\u201c\u5988\uff01\u4f60\u5bf9\u6211\u8bf4\u8fd9\u4e9b\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6148\u7965\u7684\u58f0\u97f3\uff0c\u5f00\u59cb\u6709\u4e86\u4e00\u4e9b\u4e25\u5cfb\u7684\u610f\u5473\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u6211\u4e0d\u8bb8\u4f60\u8bf4\u8bdd\u8fd9\u4e48\u51b7\u9177\uff0c\u4ed6\u548c\u4f60\u4e00\u6837\uff0c\u751f\u547d\u4e2d\u672c\u8be5\u8fd8\u6709\u7740\u4e00\u8fde\u4e32\u6700\u6700\u7f8e\u597d\u7684\u65e5 \u5b50\uff0c\u4f46\u662f\u5374\u4e3a\u4e86\u6211\u4eec\uff0c\u628a\u4e00\u5207\u5e78\u798f\u90fd\u727a\u7272\u4e86\uff0c\u96be\u9053\u6211\u4eec\u5c31\u4e0d\u5e94\u8be5\u5bf9\u4ed6\u62a5\u7b54\u4e00\u4e9b\u4e48\uff1f\u4f60\u7239 \u7239\u2026\u2026\u5509\uff01\u4ed6\u5728\u4e16\u7684\u65f6\u5019\uff0c\u4e0d\u662f\u5e38\u5bf9\u4f60\u8bf4\uff0c\u4e0d\u77e5\u62a5\u4ec7\u7684\u4eba\u662f\u61e6\u592b\uff0c\u7136\u800c\u4e0d\u77e5\u62a5\u6069\u7684\u4eba\uff0c\u5374\u8fde \u732a\u72d7\u90fd\u4e0d\u5982\uff0c\u96be\u9053\u4f60\u5df2\u7ecf\u5fd8\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7a97\u4e2d\u7684\u4eba\u5f71\uff0c\u5782\u4e0b\u5934\u53bb\u2026\u2026 \u3000\u3000\u7a97\u5916\u7684\u4eba\u5f71\uff0c\u4e5f\u5782\u4e0b\u5934\u53bb\uff0c\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u5927\u5730\u4e00\u7247\u6f06\u9ed1\u3002 \u3000\u3000\u957f\u4e45\uff0c\u90a3\u58f0\u97f3\u53c8\u6062\u590d\u6148\u7965\uff1a \u3000\u3000\u201c\u4f60\u53bb\u91cc\u95f4\u628a\u9759\u513f\u53eb\u5230\u8fd9\u91cc\u6765\uff0c\u5509\u2026\u2026\u8fd9\u5b69\u5b50\uff0c\u6574\u6574\u7684\u51e0\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u5750\u5728\u90a3\u91cc\uff0c\u751a\u81f3\u8fde \u534a\u70b9\u90fd\u6ca1\u6709\u52a8\u5f39\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u7a97\u4e2d\u7684\u4eba\u5f71\uff0c\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u8d70\u52a8\uff0c\u7a81\u5730\u56de\u5934\u9053\uff1a \u3000\u3000\u201c\u5988\uff01\u4f60\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u77e5\u9053\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u90fd\u8981\u5148\u5230\u897f\u6881\u5c71\u53bb\uff0c\u770b\u5230\u4ed6\u7684\u8eab\uff0c\u800c\u4e14 \u4e3a\u4ed6\u2026\u2026\u4e3a\u4ed6\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u5730\u51b2\u51fa\u623f\u53bb\u3002 \u3000\u3000\u7a97\u5185\u6709\u6c88\u91cd\u7684\u53f9\u606f\uff0c\u7a97\u5916\u5374\u6709\u65e0\u58f0\u7684\u53f9\u606f\uff0c\u53c8\u662f\u4e00\u9635\u96be\u582a\u7684\u6c89\u9ed8\uff0c\u7a97\u7eb8\u4e0a\u7f13\u7f13\u6cdb\u8d77\u4e00\u4e2a \u9edd\u9ed1\uff0c\u7626\u524a\u7684\u4eba\u5f71\uff0c\u8fd9\u4eba\u5f71\u9762\u4e0a\u660e\u663e\u800c\u6e05\u6670\u7684\u8f6e\u5ed3\uff0c\u6620\u5728\u660f\u9ec4\u7684\u7a97\u7eb8\u4e0a\uff0c\u66f4\u663e\u5f97\u575a\u5f3a\u800c\u89e6 \u76ee\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u5750\u4e86\u4e0b\u53bb\uff0c\u5374\u6ca1\u6709\u8bf4\u4e00\u4e2a\u5b57\uff0c\u50cf\u662f\u4ed6\u5df2\u4e0d\u613f\u8fd0\u7528\u4e16\u4e0a\u7684\u4efb\u4f55\u4e00\u79cd\u8a00\u8bed\uff0c\u6765\u8868\u8fbe\u4ed6 \u5fc3\u4e2d\u7684\u601d\u60f3\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u7957\u6709\u6c89\u9ed8\uff0c\u65e0\u9650\u7684\u6c89\u9ed8\u2026\u2026 \u3000\u3000\u7136\u540e\u53c8\u662f\u90a3\u6148\u7965\u7684\u8bed\u58f0\uff1a \u3000\u3000\u201c\u9759\u513f\uff0c\u4f60\u867d\u7136\u4e0d\u8bf4\u8bdd\uff0c\u4f46\u662f\u6211\u4ece\u4f60\u7684\u76ee\u5149\u4e2d\uff0c\u8fd8\u662f\u53ef\u4ee5\u770b\u5f97\u51fa\uff0c\u4f60\u662f\u542c\u5f97\u51fa\u6211\u7684\u8bdd \u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u751a\u81f3\u8fde\u6447\u5934\u6216\u70b9\u5934\u7684\u52a8\u4f5c\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6148\u7965\u7684\u8bed\u58f0\u4e00\u58f0\u957f\u53f9\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u8981\u544a\u8bc9\u4f60\uff0c\u4f60\u5bf9\u7433\u513f\u7684\u70ed\u7231\uff0c\u4e0d\u4f46\u7433\u513f\u77e5\u9053\uff0c\u6211\u4e5f\u77e5\u9053\uff0c\u800c\u4e14\u6211\u4eec\u90fd\u5df2\u7528\u5341\u4e07\u5206\u5730 \u611f\u6fc0\u6765\u73cd\u60dc\u8fd9\u4efd\u70ed\u7231\uff0c\u56e0\u4e3a\u4e16\u4e0a\u4efb\u4f55\u4e1c\u897f\uff0c\u6bd4\u8d77\u4f60\u7684\u70ed\u7231\u6765\uff0c\u90fd\u4f1a\u53d8\u5f97\u6e3a\u5c0f\u800c\u9119\u4fd7\uff01\u201d \u3000\u3000\u5979\u505c\u987f\u4e86\u8bed\u58f0\uff0c\u50cf\u662f\u5728\u7559\u610f\u89c2\u5bdf\u7740\u8fd9\u5c11\u5e74\u9762\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u7136\u540e\u53c8\u662f\u4e00\u58f0\u53f9\u606f\uff1a \u3000\u3000\u201c\u4e3a\u4e86\u4f60\u7684\u70ed\u7231\u2014\u2014\u7edd\u4e0d\u662f\u4e3a\u4e86\u522b\u7684\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u5c31\u662f\u8fd9\u4e00\u4efd\u70ed\u7231\uff0c\u5df2\u7ecf\u8db3\u591f\uff0c\u8db3\u591f\u8ba9 \u4e16\u4e0a\u4efb\u4f55\u4e00\u4e2a\u5973\u5b69\uff0c\u4e5f\u7528\u540c\u6837\u7684\u70ed\u7231\u6765\u5bf9\u4f60\uff0c\u4f60\u2026\u2026\u4f60\u597d\u597d\u62a4\u4f24\uff0c\u7b49\u5230\u4f60\u5fc3\u91cc\u7684\u548c\u8eab\u4e0a\u7684\u4f24 \u5b8c\u5168\u597d\u4e86\uff0c\u6211\u2026\u2026\u6211\u5c31\u66ff\u4f60\u548c\u7433\u513f\u5b8c\u5a5a\uff0c\u5728\u8fd9\u6bb5\u65f6\u5019\uff0c\u4f60\u4ec0\u4e48\u4e5f\u4e0d\u8981\u62c5\u5fc3\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u7a97\u4e2d\u7684\u4eba\u5f71\uff0c\u4e00\u9635\u98a4\u6296\u4ed6\u662f\u4e3a\u4e86\u7a81\u6765\u7684\u60ca\u559c\u3002 \u3000\u3000\u7a97\u5916\u7684\u4eba\u5f71\uff0c\u4e5f\u4e00\u9635\u98a4\u6296\u4ed6\u5374\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u5f00\u59cb\u7f13\u7f13\u8f6c\u56de\u8eab\uff0c\u90a3\u822c\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u6b64\u523b\u7adf\u50cf\u662f\u5df2\u6709\u4e86\u5343\u4e07\u94a7\u7684\u6c89\u91cd\uff0c\u4ed6\u6781\u529b\u5c0f\u5fc3\u4e0d \u8ba9\u81ea\u5df1\u53d1\u751f\u4efb\u4f55\u58f0\u97f3\uff0c\u7136\u800c\u4ed6\u5fc3\u4e2d\u7684\u53f9\u606f\uff0c\u5374\u4e0d\u77e5\u6709\u591a\u4e48\u6c89\u91cd\u3002 \u3000\u3000\u7a97\u4e2d\u4ecd\u6709\u4eba\u8bed\u3002 \u3000\u3000\u4ed6\u5374\u518d\u4e5f\u4e0d\u613f\u53bb\u542c\u4e86\uff0c\u9661\u7136\u4e00\u65cb\u8eab\u8eaf\uff0c\u9880\u957f\u7684\u8eab\u5f62\uff0c\u7a81\u5730\u51b2\u5929\u800c\u8d77\uff0c\u7136\u540e\u53d1\u72c2\u4f3c\u7684\u63a0\u5411 \u8fdc\u65b9\u3002 \u3000\u3000\u6b63\u6708\u7684\u7a79\u82cd\uff0c\u661f\u7fa4\u4f9d\u7136\u95ea\u707f\uff0c\u7136\u800c\u7a79\u82cd\u4e0b\u7684\u53f9\u606f\u2026\u2026\u5509\uff0c\u7a79\u82cd\u4e0b\u7684\u53f9\u606f\u4e2d\uff0c\u5374\u5df2\u5c11\u4e86\u5e78 \u798f\u548c\u6b22\u6109\uff0c\u53f9\u606f\u7740\u7684\u4eba\u5f71\uff0c\u4e5f\u50cf\u4ed6\u6765\u65f6\u4e00\u822c\u8f7b\u7075\u800c\u66fc\u5999\u5730\uff0c\u50cf\u665a\u98ce\u4e00\u6837\uff0c\u6d88\u5931\u5728\u591c\u8272\u4e2d\u3002 \u7b2c\u4e09\u5377\u2014\u2014\u7b2c\u4e5d\u5341\u516d\u7ae0\u3000\u8d60\u541b\u660e\u73e0>> \u53e4\u9f99\u300a\u98d8\u9999\u5251\u96e8\u7eed\u300b \u7b2c\u4e5d\u5341\u516d\u7ae0\u3000\u8d60\u541b\u660e\u73e0 \u3000\u3000\u7d27\u95ed\u7740\u7684\u7a97\u6237\uff0c\u7a81\u5730\u63a8\u5f00\u2014\u2014 \u3000\u3000\u4e00\u5f20\u6df7\u5408\u7740\u60ca\u5947\uff0c\u9519\u6115\uff0c\u9ebb\u6728\uff0e\u5446\u677f\uff0c\u4f46\u5374\u53c8\u662f\u6781\u5ea6\u6b23\u559c\uff0c\u6b22\u6109\u7684\u82cd\u767d\u9762\u5bb9\uff0c\u4ef0\u89c6\u661f \u5149\uff0c\u5583\u5583\u9053\uff1a \u3000\u3000\u201c\u5929\u662f\u4e0d\u662f\u5feb\u4eae\u4e86\u2026\u2026\u5929\u662f\u4e0d\u662f\u5feb\u4eae\u2026\u2026\u201d \u3000\u3000\u5979\u8eab\u540e\u54cd\u8d77\u4e00\u4e2a\u6148\u7965\u7684\u58f0\u97f3\uff1a \u3000\u3000\u201c\u5929\u662f\u4e0d\u662f\u5feb\u4eae\u4e86\uff0c\u8be5\u7528\u4f60\u5fc3\u91cc\u7684\u773c\u775b\u53bb\u770b\uff0c\u77e5\u9053\u4e48\uff1f\u4f60\u82e5\u60f3\u5f97\u5230\u5e78\u798f\uff0c\u4f60\u5c31\u8be5\u81ea\u5df1\u5148 \u5feb\u6d3b\u8d77\u6765\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u63a9\u4e0a\u7a97\u6237\uff1a\u201c\u5916\u9762\u98ce\u5927\uff0c\u4f60\u7684\u4f24\u8fd8\u6ca1\u6709\u597d\u3002\u201d\u7136\u540e\u56de\u8f6c\u8eab\uff1a \u3000\u3000\u201c\u7433\u513f\uff01\u6211\u65b9\u624d\u548c\u4f60\u9759\u54e5\u54e5\u8c08\u4e86\u8bb8\u4e45\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u9759\u591c\u4e4b\u4e2d\uff0c\u7a81\u7136\u6709\u4e00\u9635\u6025\u907d\u7684\u9a6c\u8e44\u58f0\uff0c\u968f\u98ce\u4f20\u6765\uff0c\u621b\u7136\u505c\u987f\u5728\u5ba2\u6808\u95e8\u524d\uff0c\u63a5 \u7740\u4fbf\u662f\u6572\u95e8\u58f0\uff0c\u4eba\u8bed\u58f0\u2026\u2026\uff0c\u7136\u540e\u9a6c\u8e44\u58f0\u53c8\u81ea\u8fdc\u53bb\u3002 \u3000\u3000\u5b59\u654f\u7709\u5cf0\u5fae\u76b1\uff0c\u65b9\u81ea\u5728\u5947\u602a\u7740\u8fd9\u9635\u9a6c\u8e44\u58f0\u6765\u53bb\u4e4b\u5306\u907d\u3002 \u3000\u3000\u90a3\u77e5\u2026\u2026 \u3000\u3000\u5374\u542c\u4e00\u9635\u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u8d70\u5165\u8de8\u9662\uff0c\u4e00\u4e2a\u5636\u54d1\u7684\u58f0\u97f3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\u8fd8\u6ca1\u6709\u7761\u4e48\uff1f\u201d \u3000\u3000\u5b59\u654f\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u6253\u5f00\u623f\u95e8\uff0c\u5374\u89c1\u7761\u610f\u65b9\u6d53\u7684\u5e97\u4f19\uff0c\u6b63\u81ea\u624b\u6367\u4e00\u65b9\u7d2b\u6a80\u6728\u5323\uff0c\u5446\u5446\u5730 \u7ad9\u5728\u95e8\u53e3\uff0c\u966a\u7740\u7b11\u9053\uff1a \u3000\u3000\u201c\u65b9\u624d\u6709\u4eba\u5c06\u8fd9\u5323\u4e1c\u897f\u9001\u6765\uff0c\u53eb\u5c0f\u7684\u4ea4\u7ed9\u592b\u4eba\uff0c\u8bf4\u91cc\u9762\u5168\u662f\u73cd\u8d35\u4e4b\u7269\uff0c\u5c0f\u7684\u4e0d\u6562\u803d\u8bef\u56e0 \u6b64\u5373\u523b\u5c31\u9001\u6765\u4e86\uff0c\u6b63\u597d\u592b\u4eba\u6ca1\u6709\u7761\u2026\u2026\u201d \u3000\u3000\u5b59\u654f\u5fc3\u4e2d\u5927\u4e3a\u60ca\u5947\uff0c\u53e3\u4e2d\u5374\u662f\u6de1\u6de1\u8bf4\u4e86\u58f0\uff1a \u3000\u3000\u201c\u77e5\u9053\u4e86\uff01\u201d\u987a\u624b\u63a5\u8fc7\u90a3\u65b9\u7d2b\u6a80\u6728\u5323\uff1a\u201c\u534a\u591c\u628a\u4f60\u60ca\u52a8\uff0c\u771f\u4e0d\u597d\u610f\u601d\uff01\u201d \u3000\u3000\u9012\u51fa\u534a\u952d\u94f6\u5b50\uff0c\u5e97\u4f19\u5343\u6069\u4e07\u8c22\u5730\u8d70\u4e86\uff0c\u5b59\u654f\u624b\u6367\u6728\u5323\uff0c\u5374\u4ecd\u5728\u5446\u5446\u5730\u51fa\u7740\u795e\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u65b9\u5236\u4f5c\u5f97\u6781\u5176\u7cbe\u81f4\u7684\u7d2b\u6a80\u6728\u5323\uff0c\u706f\u5149\u4ece\u8eab\u540e\u6620\u51fa\uff01\u5979\u53ef\u4ee5\u6781\u5176\u6e05\u6670\u5730\u770b\u6e05\u5323\u4e0a\u7684 \u82b1\u7eb9\u3002 \u3000\u3000\u90a3\u662f\u5bcc\u8d35\u4eba\u5bb6\u5e38\u89c1\u7684\u5409\u7965\u96d5\u523b\uff01\u201c\u9e3e\u51e4\u5408\u9e23\u201d\u3002\u5979\u8fdf\u7591\u7740\u8f6c\u56de\u8eab\uff0c\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u8fd9\u91cc\u9762 \u662f\u4ec0\u4e48\uff1f\u8c01\u9001\u6765\u7684\uff1f\u201d \u3000\u3000\u51cc\u7433\u5446\u5446\u5730\u51dd\u89c6\u7740\u5979\u6bcd\u4eb2\uff0c\u53ea\u89c1\u5979\u7f13\u7f13\u6253\u5f00\u6728\u5323\uff0c\u7a81\u5730\uff01\u4e00\u9635\u5f3a\u70c8\u7684\u73e0\u5149\uff0c\u81ea\u5323\u4e2d\u817e \u8d77\uff0c\u51cc\u7433\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u77e5\u5979\u8bdd\u8fd8\u6ca1\u6709\u95ee\u51fa\uff0c\u5b59\u654f\u8eab\u4e0a\uff0c\u7adf\u7a81\u5730\u8d77\u4e86\u4e00\u9635\u98a4\u6296\uff0c\u9762\u5bb9\u4e5f\u53d8\u5f97\u5f02\u6837\u82cd\u767d\u3002 \u3000\u3000\u5657\u5730\u4e00\u58f0\u2014\u2014 \u3000\u3000\u7d2b\u6a80\u6728\u5323\uff0c\u843d\u5230\u5730\u4e0a\uff0c\u7adf\u6563\u51fa\u6570\u5341\u7c92\u660e\u73e0\uff0c\u968f\u5730\u6d41\u8f6c\uff0c\u51cc\u7433\u8f7b\u547c\u4e00\u58f0\uff0c\u5374\u89c1\u5979\u6bcd\u4eb2\u98a4\u6296 \u7740\u7684\u624b\u638c\u4e2d\uff0c\u81ea\u62ff\u7740\u4e00\u65b9\u7eb8\u67ec\u3002 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u8dd1\u4e86\u8fc7\u53bb\uff0c\u4ece\u5979\u6bcd\u4eb2\u98a4\u6296\u7740\u7684\u624b\u638c\u4e2d\uff0c\u63a5\u8fc7\u8fd9\u65b9\u7eb8\u67ec\uff0c\u660f\u9ec4\u7684\u706f\u5149\uff0c\u6620\u7740\u4fca\u79c0 \u7684\u5b57\u8ff9\uff1a \u3000\u3000\u201c\u6b23\u95fb\u559c\u8baf\uff0c\u8d60\u541b\u660e\u5473\uff0c\u73e0\u6620\u74a7\u4eba\uff0c\u767e\u5e74\u597d\u5408\uff01\u201d \u3000\u3000\u5e73\u51e1\u7684\u5b57\u8ff9\uff0c\u5e73\u51e1\u7684\u8bed\u53e5\uff0c\u65e2\u65e0\u4e0a\u6b3e\uff0c\u4ea6\u65e0\u7f72\u540d\uff0c\u8fd9\u539f\u8be5\u6ca1\u6709\u4e1d\u6beb\u503c\u5f97\u5b59\u654f\u60ca\u5f02\u4e4b\u5904 \u5440\uff01 \u3000\u3000\u51cc\u7433\u6115\u4e86\u6115\uff0c\u76ee\u5149\u8f6c\u5411\u5979\u6bcd\u4eb2\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5979\u5fc3\u91cc\u7a81\u4e5f\u95ea\u7535\u822c\u63a0\u8fc7\u4e00\u4e2a\u5fc3\u5ff5\uff0c\u5a07\u8eaf\u4e00 \u8f6f\uff0c\u540e\u9000\u4e09\u6b65\uff0c\u60ca\u547c\u7740\u9053\uff1a \u3000\u3000\u201c\u662f\u4ed6\uff01\u662f\u4ed6\uff01\u96be\u9053\u662f\u4ed6\uff1f\u201d \u3000\u3000\u5b59\u654f\u76ee\u5149\u4f4e\u5782\uff0c\u5730\u4e0a\u7684\u73e0\u5149\uff0c\u4ecd\u5728\u6ee1\u5730\u6d41\u8f6c\uff0c\u5979\u6697\u4e2d\u60ca\u5fd6\uff1a \u3000\u3000\u201c\u662f\u4e0d\u662f\u4ed6\uff1f\u5927\u7ea6\u662f\u4ed6\uff1f\u4ed6\u96be\u9053\u6ca1\u6709\u6b7b\uff1f\u9664\u4e86\u4ed6\u8fd8\u6709\u8c01\uff01\u201d \u3000\u3000\u5979\u5728\u5fc3\u5e95\u6df1\u5904\uff0c\u65e0\u6cd5\u89e3\u91ca\u5730\u76f4\u89c9\u611f\u5230\uff0c\u8d60\u73e0\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u4ed6\uff1f \u3000\u3000\u4f46\u662f\u5979\u53e3\u4e2d\u5374\u4ecd\u5f3a\u81ea\u7f13\u7f13\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u662f\u4ed6\uff1f\u201d \u3000\u3000\u51cc\u7433\u5706\u7741\u660e\u7738\uff1a \u3000\u3000\u201c\u5988\uff01\u4f60\u4e00\u5b9a\u4e5f\u77e5\u9053\u662f\u4ed6\uff0c\u4e0d\u7136\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u5403\u60ca\u5462\uff1f\u5988\uff01\u4f60\u8bf4\u662f\u5417\uff1f\u4f60\u8bf4\u662f\u5417\uff1f \u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u5979\u4e00\u8fde\u8bf4\u4e86\u4e09\u58f0\uff0c\u201c\u4f60\u8bf4\u662f\u5417\uff1f\u201d\u8bf4\u5230\u6700\u540e\u4e00\u58f0\uff0c\u5979\u5df2\u7d27\u7d27\u6293\u7740\u5979\u5988\u5988\u7684\u80a9\u5934\uff0c\u50cf\u662f\u8981 \u4ece\u5979\u5988\u5988\u8eab\u4e0a\uff0c\u8bc1\u5b9e\u5979\u81ea\u5df1\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u201c\u6211\u4eec\u65b9\u624d\u8bf4\u7684\u8bdd\uff0c\u4ed6\u5168\u90fd\u542c\u5230\u4e86\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8fdb\u6765\u5462\uff1f\u96be\u9053\u2026\u2026\u96be \u9053\u2026\u2026\u201d \u3000\u3000\u5979\u4e00\u904d\u53c8\u4e00\u904d\u5730\u4f4e\u8bed\u7740\uff0c\u6bcf\u8bf4\u4e00\u904d\uff0c\u5979\u7684\u4e00\u53cc\u660e\u7738\u4e4b\u4e2d\uff0c\u5c31\u4e0d\u77e5\u8981\u6d41\u51fa\u591a\u5c11\u7c92\u6cea\u73e0\uff0c\u6bd4 \u5730\u4e0a\u6d41\u8f6c\u7740\u7684\u660e\u73e0\u66f4\u73cd\u8d35\uff0c\u66f4\u6676\u83b9\u7684\u6cea\u73e0\uff01 \u3000\u3000\u5b59\u654f\u6c89\u91cd\u5730\u53f9\u606f\u7740\uff0c\u8f7b\u62cd\u7740\u5979\u5973\u513f\u7684\u79c0\u53d1\uff0c\u5374\u53ea\u4f1a\u53cd\u8986\u7740\u8bf4\uff1a \u3000\u3000\u201c\u50bb\u5b69\u5b50\uff01\u4f60\u600e\u4e48\u77e5\u9053\u662f\u4ed6\uff1f\u50bb\u5b69\u5b50\uff01\u4f60\u600e\u4e48\u77e5\u9053\u662f\u4ed6\uff1f\u201d \u3000\u3000\u7a97\u5916\u98ce\u58f0\u7c0c\u7136\uff0c\u51cc\u7433\u7a81\u5730\u4e00\u58f0\u5927\u547c\uff1a \u3000\u3000\u201c\u4ed6\u8fd8\u6ca1\u6709\u8d70\uff0c\u4ed6\u8fd8\u5728\u5916\u9762\uff01\u201d \u3000\u3000\u4e00\u6b65\u63a0\u5230\u7a97\u524d\uff0c\u5288\u624b\u4e00\u638c\uff0c\u51fb\u5f00\u7a97\u95e8\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u7a81\u53c8\u4e00\u58f0\u60ca\u547c\uff0c\u8fde\u9000\u4e09\u6b65\uff0c\u5389\u9053 \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f1\u58f0\u672a\u4e86\uff0c\u4e00\u9635\u5927\u7b11\u4e4b\u58f0\uff0c\u5df2\u7531\u7a97\u5916\u4f20\u5165\uff0c\u661f\u5149\u4e0b\uff0c\u4e00\u6761\u77ee\u80d6\u4eba\u5f71\uff0c\u5f53\u7a97\u800c\u7acb\uff0c\u5b59\u654f\u53ea \u89c9\u5fc3\u5934\u4e00\u5bd2\uff0c\u5237\u5730\u63a0\u5411\u5e8a\u5934\uff0c\u62bd\u51fa\u5e8a\u5934\u7684\u96ea\u5203\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u65b9\u5f85\u53bb\u706f\u706b\uff0c\u5374\u542c\u7a97\u5916\u4eba\u5f71\u5df2\u81ea \u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\u65e6\u83ab\u60ca\u60f6\uff0c\u5728\u4e0b\u6b64\u6765\u5b9e\u65e0\u6076\u610f\u3002\u201d \u3000\u3000\u706f\u706b\u5fae\u82b1\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u5df2\u81ea\u7a7f\u7a97\u800c\u5165\uff0c\u4e00\u8eab\u95ea\u4eae\u7684\u91d1\u886b\uff0c\u867d\u886c\u5f97\u4ed6\u7684\u8eab\u6750\u6781\u4e3a\u81c3\u80bf\uff0c\u4f46 \u662f\u4ed6\u8eab\u624b\u7684\u7075\u654f\uff0c\u77eb\u5065\uff0c\u5374\u53c8\u4e0d\u7981\u4f7f\u5f97\u5b59\u654f\u5fc3\u5934\u4e00\u9707\uff0c\u6c89\u58f0\u53f1\u9053\uff1a \u3000\u3000\u201c\u670b\u53cb\u662f\u8c01\uff1f\u65e2\u65e0\u6076\u610f\uff0c\u6df1\u591c\u4e4b\u4e2d\uff0c\u95ef\u4eba\u79c1\u5ba4\uff0c\u5374\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u5f71\u8eab\u5f62\u65b9\u5b9a\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u8f7b\u8f7b\u779f\u8fc7\u6728\u7acb\u5899\u8fb9\u7684\u953a\u9759\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u97e6\u50b2\u7269\uff0c\u4e0e\u51cc\u5927\u4fa0\u6614\u5e74\u4ea6\u6709\u6570\u9762\u4e4b\u7f18\uff0c\u4e0d\u77e5\u9053\u592b\u4eba\u8fd8\u8bb0\u5f97\u5728\u4e0b\u4e48\uff1f\u201d \u3000\u3000\u5b59\u654f\u7f13\u7f13\u653e\u4e0b\u624b\u4e2d\u5229\u5203\uff0c\u76ee\u5149\u4e2d\u4f3c\u4e4e\u5728\u60ca\u5f02\u7740\u8fd9\u77ee\u80d6\u81c3\u80bf\u7684\u6c49\u5b50\uff0c\u7adf\u4f1a\u5c31\u662f\u540d\u9707\u6c5f\u6e56\u7684 \u201c\u4e03\u6d77\u6e14\u5b50\u201d\u97e6\u50b2\u7269\u3002 \u3000\u3000\u5374\u542c\u97e6\u50b2\u7269\u53c8\u662f\u4e00\u9635\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u51cc\u59d1\u5a18\u597d\u5389\u5bb3\u7684\u8033\u529b\uff0c\u5728\u4e0b\u65b9\u5230\u539d\u4e0b\uff0c\u5c31\u88ab\u53d1\u89c9\uff0c\u82e5\u662f\u6709\u90a3\u4e2a\u4e0d\u5f00\u773c\u7684\u5c0f\u8d3c\uff0c\u8f6c\u5ff5\u5934 \u8f6c\u5230\u51cc\u59d1\u5a18\u5934\u4e0a\uff0c\u90a3\u624d\u771f\u662f\u778e\u4e86\u773c\u775b\u54e9\uff01\u201d \u3000\u3000\u51cc\u7433\u79cb\u6ce2\u8f6c\u5904\uff0c\u9762\u5bd2\u5982\u6c34\uff0c\u6839\u672c\u5c31\u672a\u5c06\u4ed6\u8fd9\u756a\u606d\u7ef4\u4e4b\u8a00\uff0c\u542c\u5165\u8033\u4e2d\uff0c\u97e6\u50b2\u7269\u54c8\u54c8\u5e72\u7b11\u6570 \u58f0\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u6df1\u591c\u6253\u6270\uff0c\u5b9e\u5728\u5192\u6627\u7684\u5f88\uff0c\u4f46\u5374\u662f\u4e3a\u4e86\u592b\u4eba\uff0c\u65b9\u6562\u6597\u80c6\u6765\u6b64\u3002\u201d \u3000\u3000\u5b59\u654f\u79c0\u7709\u5fae\u8f69\uff0c\u8be7\u58f0\u9053\uff1a \u3000\u3000\u201c\u9601\u4e0b\u4e0e\u6211\u6bcd\u5973\u7d20\u6627\u5e73\u751f\uff0c\u9601\u4e0b\u6b64\u8a00\uff0c\u5b9e\u5728\u6559\u6211\u83ab\u6d4b\u9ad8\u6df1\uff0c\u96be\u9053\u6df1\u591c\u4e2d\u95ef\u5165\u4eba\u5bb6\u5973\u5b50\u79c1 \u5ba4\uff0c\u8fd8\u662f\u4e3a\u4e86\u2014\u2014\u201d \u3000\u3000\u5979\u6b64\u523b\u5df2\u77e5\u9053\u8fd9\u201c\u4e03\u6d77\u6e14\u5b50\u201d\u97e6\u50b2\u7269\u5b9a\u4ea6\u662f\u5929\u4e89\u6559\u4e0b\u4e4b\u4eba\uff0c\u662f\u4ee5\u8a00\u8bed\u4e4b\u4e2d\uff0c\u950b\u8292\u6bd5\u9732\uff0c \u4e0d\u518d\u66ff\u4ed6\u7559\u4e1d\u6beb\u60c5\u9762\u3002 \u3000\u3000\u90a3\u77e5\u5979\u8bdd\u58f0\u672a\u4e86\uff0c\u97e6\u50b2\u7269\u5374\u53c8\u5df2\u5927\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u6ca1\u5934\u6ca1\u8111\u5730\u5c31\u8bf4\u51fa\u8fd9\u4e9b\u8bdd\uff0c\u81ea\u7136\u96be\u602a\u592b\u4eba\u4e0d\u61c2\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7adf\u7136\u5927\u523a\u523a\u5728\u684c\u65c1\u6728\u6905\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u63a5\u53e3\u53c8\u9053\uff1a \u3000\u3000\u201c\u4f46\u592b\u4eba\u4e00\u542c\u5728\u4e0b\u89e3\u91ca\uff0c\u5b9a\u5fc5\u5c31\u53ef\u4ee5\u4e86\u89e3\u5728\u4e0b\u7684\u82e6\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5b59\u654f\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u97e6\u50b2\u7269\u53c8\u9053\uff1a \u3000\u3000\u201c\u4eca\u65e5\u5728\u4e0b\u542c\u5f97\u6211\u6559\u4e0b\u95e8\u5f92\u6765\u62a5\uff0c\u8bf4\u662f\u592b\u4eba\u4f3c\u4e4e\u5bf9\u90a3\u4ec0\u4e48\u201c\u6b63\u4e49\u5e2e\u201d\u6709\u4e9b\u5174\u8da3\uff0c\u662f\u4ee5\u5728 \u4e0b\u4fbf\u8d76\u7d27\u63a2\u51fa\u90a3\u5e2e\u4eba\u7684\u843d\u811a\u4e4b\u5904\uff0c\u524d\u6765\u62a5\u77e5\u592b\u4eba\uff0c\u592b\u4eba\u5174\u8da3\u5982\u4f55\uff0c\u5728\u4e0b\u4e0d\u63e3\u5192\u6627\uff0c\u81ea\u613f\u4e3a\u592b \u4eba\u9886\u8def\u3002\u201d \u3000\u3000\u5b59\u654f\u79cb\u6ce2\u4e00\u8f6c\uff0c\u6697\u4e2d\u5fd6\u9053\uff1a \u3000\u3000\u201c\u770b\u6765\u5929\u4e89\u6559\u5f53\u771f\u662f\u4eba\u6750\u6d4e\u6d4e\uff0c\u4eca\u65e5\u6211\u5728\u5ba2\u6808\u95e8\u5916\uff0c\u5e76\u65e0\u663e\u660e\u8868\u793a\uff0c\u5fc3\u610f\u5374\u5df2\u88ab\u5bf9\u9762\u90a3 \u4e24\u6761\u6c49\u5b50\u770b\u51fa\uff0c\u8fd9\u59d3\u97e6\u7684\u6b64\u756a\u524d\u6765\uff0c\u60f3\u5fc5\u662f\u60f3\u5229\u7528\u6211\u505a\u5757\u95ee\u8def\u4e4b\u77f3\u3002\u201d \u3000\u3000\u5979\u6697\u4e2d\u51b7\u7b11\u4e00\u58f0\uff0c\u5fc3\u5ff5\u7a7a\u5730\u4e00\u8f6c\uff0c\u95ea\u7535\u822c\u63a0\u8fc7\u51e0\u4e2a\u5ff5\u5934\uff0c\u7acb\u523b\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u6b63\u4e49\u5e2e\u4e3b\u7684\u843d\u811a\u4e4b\u5904\uff0c\u97e6\u9999\u4e3b\u771f\u7684\u5df2\u7ecf\u77e5\u9053\u4e86\u4e48\uff1f\u201d \u3000\u3000\u97e6\u50b2\u7269\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u5df2\u5f97\u6559\u4e3b\u4f20\u8c15\uff0c\u8bf4\u592b\u4eba\u6b64\u540e\u5df2\u662f\u655d\u6559\u4e00\u5bb6\u4eba\u4e86\uff0c\u96be\u9053\u5728\u4e0b\u8fd8\u6562\u5bf9\u592b\u4eba\u8bf4\u51fa\u6b3a\u7792\u4e4b \u8a00\u4e48\uff1f\u201d \u3000\u3000\u5b59\u654f\u660e\u7738\u5fae\u5f20\uff0c\u4f46\u5374\u5fcd\u4e0b\u4e86\u5fc3\u4e2d\u7684\u6012\u6c14\uff0c\u56e0\u4e3a\u5979\u6b64\u523b\u5fc3\u91cc\u5df2\u6709\u4e00\u4e2a\u79d8\u5bc6\u7684\u731c\u6d4b\uff0c\u5979\u5fc3\u60f3 \u8bc1\u5b9e\u8fd9\u731c\u6d4b\u662f\u5426\u6b63\u69b7\uff0c\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a \u3000\u3000\u201c\u97e6\u9999\u4e3b\u53ef\u662f\u6b64\u523b\u5c31\u8981\u8d70\u4e48\uff1f\u201d \u3000\u3000\u97e6\u50b2\u7269\u9894\u9996\u7b11\u9053\uff1a \u3000\u3000\u201c\u53ea\u8981\u592b\u4eba\u613f\u610f\uff0c\u5728\u4e0b\u4e00\u5b9a\u5949\u966a\u3002\u201d\u76ee\u5149\u8f6c\u52a8\u4e4b\u95f4\uff0c\u8d2a\u5a6a\u5730\u5728\u6ee1\u5730\u660e\u73e0\u4e0a\u671b\u4e86\u51e0\u773c\uff0c\u5374 \u89c1\u5b59\u654f\u7f13\u7f13\u5c06\u638c\u4e2d\u5229\u5203\uff0c\u653e\u56de\u5e8a\u5934\uff0c\u8f6c\u9996\u9053\uff1a \u3000\u3000\u201c\u7433\u513f\uff01\u4f60\u5728\u8fd9\u91cc\u966a\u2026\u2026\u5750\u5750\uff0c\u6211\u9a6c\u4e0a\u5c31\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u51cc\u7433\u867d\u7136\u806a\u6167\uff0c\u5374\u5df2\u731c\u6d4b\u4e0d\u51fa\u5979\u6bcd\u4eb2\u7684\u5fc3\u610f\uff0c\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u5b59\u654f\u5374\u5df2\u7ecf\u53f1\u4e00\u58f0 \u201c\u8d70\uff01\u201d\u7ea4\u8170\u5fae\u62e7\uff0c\u7a7f\u7a97\u800c\u51fa\u3002 \u3000\u3000\u97e6\u50b2\u7269\u54c8\u54c8\u4e00\u7b11\uff0c\u62b1\u62f3\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\u7a0d\u5019\uff01\u201d\u7a81\u5730\u8f6c\u5411\u953a\u9759\uff0c\u5728\u953a\u9759\u8033\u7554\u4f4e\u4f4e\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u8eab\u5f62\u8f6c\u4fa7\u4e4b\u95f4\uff0c\u4fbf\u4e5f\u7a7f\u7a97 \u800c\u51fa\uff0c\u51cc\u7433\u4f9d\u7a00\u542c\u89c1\u4ed6\u8bf4\u7684\u662f\uff1a \u3000\u3000\u201c\u2026\u2026\u4f60\u53ea\u8981\u2026\u2026\u6559\u4e3b\u4e4b\u5429\u5490\uff0c\u7acb\u523b\u5c31\u53ef\u4ee5\u2026\u2026\u6211\u529d\u4f60\u2026\u2026\u201d \u3000\u3000\u4f46\u953a\u9759\u5374\u53ea\u662f\u832b\u7136\u7741\u7740\u773c\u775b\uff0c\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\u4f3c\u7684\uff0c\u7a97\u5916\u661f\u5149\u70b9\u70b9\uff0c\u98ce\u58f0\u4f9d \u4f9d\uff0c\u5b59\u654f\u548c\u97e6\u50b2\u7269\u90fd\u5df2\u8d70\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u6df1\u591c\u4e2d\u7684\u5609\u5174\u8857\u9053\uff0c\u5c31\u50cf\u662f\u6c34\u94f6\u94fa\u6210\u7684\u9053\u8def\uff0c\u5e73\u6ed1\u800c\u5b89\u9759\u3002 \u3000\u3000\u5355\u8c03\u800c\u523b\u677f\u7684\u66f4\u58f0\u9f13\u70b9\uff0c\u4e00\u58f0\u4e00\u58f0\u5730\u5212\u7834\u56db\u5468\u7684\u9759\u5bc2\u3002 \u3000\u3000\u5b59\u654f\u65e0\u8a00\u5730\u5728\u8fd9\u9759\u5bc2\u4e2d\u98de\u63a0\u7740\uff0c\u5979\u8f7b\u529f\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u5728\u6b66\u6797\u4e2d\u5374\u5df2\u7b97\u4e0d\u5f97\u5eb8\u4fd7\u8eab\u624b\uff0c\u6ca1 \u6709\u591a\u65f6\uff0c\u5979\u4fbf\u5df2\u63a0\u51fa\u57ce\u5916\uff0c\u63a0\u51fa\u4e86\u90a3\u6761\u6a2a\u8de8\u5728\u9759\u9759\u5730\u6cb3\u6c34\u4e0a\u7684\u9759\u9759\u5730\u5c0f\u6865\uff0c\u70df\u96e8\u5357\u6e56\uff0c\u5728\u6df1 \u591c\u4e2d\u66f4\u89c1\u82cd\u832b\u7edd\u7f8e\uff0c\u5979\u6df1\u957f\u5730\u900f\u4e86\u53e3\u6c14\uff0c\u4fa7\u9996\u8f7b\u95ee\uff1a \u3000\u3000\u201c\u53ef\u5230\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e00\u76f4\u4e0d\u6025\u4e0d\u5f90\u8ddf\u5728\u5979\u8eab\u4fa7\u7684\u97e6\u50b2\u7269\u5fae\u7b11\u5e94\u9053\uff1a \u3000\u3000\u201c\u4e0d\u8fdc\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e2d\u811a\u6b65\u7a81\u5730\u52a0\u6025\uff0c\u591c\u98ce\u5439\u5f97\u4ed6\u8863\u886b\u6c99\u6c99\u4f5c\u54cd\uff0c\u7a7f\u8fc7\u4e00\u7247\u6811\u6797\uff0c\u4ed6\u5374\u7a81\u53c8\u987f\u4f4f\u8eab\u5f62\uff0c \u8f7b\u5de7\u5730\u5c06\u8eab\u4e0a\u91d1\u8272\u8863\u886b\u8131\u4e0b\uff0c\u9732\u51fa\u91cc\u9762\u7684\u9ed1\u8863\u52b2\u670d\uff0c\u9065\u6307\u524d\u65b9\uff0c\u542b\u7b11\u53c8\u9053\uff1a \u3000\u3000\u201c\u592b\u4eba\uff01\u524d\u9762\u90a3\u51e0\u91cd\u5c4b\u5f71\uff0c\u672c\u662f\u5f53\u671d\u4e00\u4f4d\u5927\u81e3\u7684\u5bb6\u5b85\uff0c\u5982\u4eca\u4e0d\u77e5\u600e\u5730\uff0c\u5374\u505a\u4e86\u90a3\u5e2e\u4eba\u7684 \u843d\u811a\u4e4b\u5904\uff0c\u5728\u4e0b\u867d\u7136\u672a\u66fe\u53bb\u8fc7\uff0c\u4f46\u95fb\u8bf4\u91cc\u9762\u56ed\u6797\u9887\u6df1\uff0c\u592b\u4eba\u8fdb\u53bb\uff0c\u5343\u4e07\u8981\u5c0f\u5fc3\u4e9b\uff0c\u4e0d\u8981\u548c\u5728 \u4e0b\u8d70\u5931\uff0c\u90a3\u91cc\u770b\u6765\u867d\u65e0\u52a8\u9759\uff0c\u5176\u5b9e\u5374\u4e0d\u557b\u9f99\u6f6d\u864e\u7a74\u2014\u2014\u201d \u3000\u3000\u4ed6\u54c8\u54c8\u8f7b\u7b11\u6570\u58f0\uff1a\u201c\u5728\u4e0b\u6b64\u523b\uff0c\u4e5f\u5b9e\u5728\u662f\u5728\u820d\u547d\u966a\u541b\u5b50\u54e9\uff01\u201d \u3000\u3000\u5b59\u654f\u6697\u4e2d\u51b7\u7b11\u4e00\u58f0\uff0c\u51dd\u76ee\u9065\u671b\uff0c\u524d\u9762\u6797\u6728\u6df1\u5904\uff0c\u679c\u6709\u4e00\u7247\u5c4b\u9876\uff0c\u6a2a\u5367\u5728\u6df1\u6c89\u7684\u591c\u8272\u95f4\uff0c \u5979\u5e73\u9759\u5730\u547c\u5438\u4e00\u4e0b\uff0c\u5f3a\u5236\u7740\u5fc3\u4e2d\u7684\u6fc0\u52a8\uff0c\u6697\u95ee\u81ea\u5df1\uff1a \u3000\u3000\u201c\u8fd9\u5c4b\u5b50\u91cc\u4f4f\u7740\u7684\u771f\u7684\u4f1a\u662f\u90a3\u201c\u6b63\u4e49\u5e2e\u4e3b\u201d\u4e48\uff1f\u800c\u8fd9\u201c\u6b63\u4e49\u5e2e\u4e3b\u201d\u7684\u771f\u5b9e\u8eab\u4efd\uff0c\u53c8\u4f1a\u4e0d \u4f1a\u771f\u7684\u5c31\u662f\u6211\u5fc3\u4e2d\u731c\u6d4b\u7684\u90a3\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u5979\u4f3c\u4e4e\u5df2\u542c\u5230\u81ea\u5df1\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u56e0\u4e3a\u5979\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u82e5\u662f\u80af\u5b9a\u7684\uff0c\u81ea\u7136\u597d\u4e86\uff0c\u82e5\u662f\u5426 \u5b9a\u7684\uff0c\u5979\u5982\u6b64\u5192\u7136\u5730\u95ef\u5165\u4e00\u4e2a\u65b0\u8d77\u5e2e\u6d3e\u7684\u79d8\u5bc6\u5de2\u7a74\uff0c\u90a3\u5c82\u975e\u771f\u7684\u662f\u53bb\u9001\u6b7b\u4e48\uff1f\u4f46\u662f\u5979\u4e3a\u4e86\u4e00 \u4e9b\u7279\u522b\u7684\u539f\u56e0\uff0c\u5374\u4e5f\u987e\u4e0d\u5f97\u8fd9\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u4e24\u6761\u9edd\u9ed1\u7684\u4eba\u5f71\uff0c\u6295\u5165\u9edd\u9ed1\u7684\u5c4b\u9876\u4e0a\u3002 \u3000\u3000\u5609\u5174\u57ce\u4e2d\u5ba2\u6808\u91cc\u897f\u8de8\u9662\u5ba4\u5185\u7684\u706f\u5149\uff0c\u7531\u660f\u9ec4\u53d8\u5f97\u60e8\u767d\u3002 \u3000\u3000\u5927\u5730\u6c38\u6052\u5730\u6ca1\u6709\u4e00\u4e1d\u53d8\u5316\uff0c\u4eba\u7c7b\u5374\u65f6\u523b\u5730\u5728\u53d8\u5316\u7740\uff0c\u53ea\u662f\u8fd9\u4e00\u5207\u53d8\u5316\u53ea\u4e0d\u8fc7\u662f\u4eba\u6d77\u4e2d\u4e00 \u8fde\u4e32\u5c0f\u5c0f\u7684\u6ce1\u6cab\uff0c\u5f00\u59cb\u548c\u7ed3\u675f\uff0c\u5728\u6c38\u6052\u7684\u5b87\u5b99\u4e2d\uff0c\u90fd\u4e0d\u8fc7\u662f\u5239\u90a3\u95f4\u7684\u4e8b\u60c5\u7f62\u4e86\uff01 \u3000\u3000\u6240\u4ee5\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u8fd9\u5c0f\u5c0f\u7684\u6545\u4e8b\u7684\u5f00\u59cb\u4e0e\u7ed3\u675f\uff0c\u4e0d\u66f4\u52a0\u6e3a\u5c0f\u548c\u53ef\u7b11\u4e86\u5417\uff1f \u3000\u3000\u6240\u4ee5\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u8981\u8bf4\uff01\u201c\u4e16\u4e0a\u4efb\u4f55\u4e00\u4ef6\u6ca1\u6709\u7ed3\u675f\u7684\u4e8b\uff0c\u5176\u5b9e\u4e5f\u53ef\u4ee5\u8bf4\u662f\u5df2\u7ecf\u7ed3\u675f\uff0c \u4e16\u4e0a\u4efb\u4f55\u4e00\u4ef6\u7ed3\u675f\u4e86\u7684\u4e8b\uff0c\u5176\u5b9e\u5374\u4e5f\u53ef\u4ee5\u8bf4\u662f\u6ca1\u6709\u7ed3\u675f\uff0c\u56e0\u4e3a\u7ed3\u675f\u4e0e\u4e0d\u7ed3\u675f\u3002\u8fd9\u5176\u95f4\u7684\u8ddd \u79bb\uff0c\u771f\u662f\u591a\u4e48\u53ef\u601c\u800c\u53ef\u7b11\u5730\u77ed\u6682\u5440\uff01\u201d \u3000\u3000\uff08\u8bf7\u770b\u7eed\u96c6\uff09"}),showOnHomepage:!0}]}}]);