"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[89595],{27446:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u51b0\u5ddd\u5929\u5973\u4f20",content:(0,s.jsx)(s.Fragment,{children:"\u795e\u7bad\u8fde\u98de\u3000\u7a7f\u4e91\u60ca\u5c0f\u4fa0 \u98de\u5200\u4e00\u63b7\u3000\u5288\u679c\u6551\u4f73\u4eba \u3000\u3000\u5723\u5cf0\u7684\u51b0\u5ddd\u8c61\u5927\u6cb3\u5012\u6302\uff0c \u3000\u3000\u4f60\u542c\u90a3\u6d41\u6c34\u6d6e\u52a8\u8f7b\u8f7b\u7684\u54cd\u2014\u2014 \u3000\u3000\u8c61\u662f\u59d1\u5a18\u7684\u5de7\u624b\u5f39\u8d77\u4e86\u4e1c\u4e0d\u62c9\u3002 \u3000\u3000\u5979\u5728\u95ee\u90a3\u6d41\u6d6a\u7684\u65c5\u4eba\uff1a \u3000\u3000\u4f60\u8fd8\u8981\u6500\u8fc7\u51e0\u5ea7\u51b0\u5c71\uff0c\u7ecf\u5386\u51e0\u8bb8\u98ce\u7802\uff1f \u3000\u3000\u5566\u5566\u2026\u2026 \u3000\u3000\u6d41\u6d6a\u7684\u65c5\u4eba\u5440\uff0c \u3000\u3000\u8349\u539f\u7684\u5140\u9e70\u4e5f\u4e0d\u80fd\u7ec8\u65e5\u76d8\u65cb\u4e0d\u4e0b\uff0c \u3000\u3000\u4f60\u4eec\u5c3d\u662f\u8d70\u5440\uff0c\u8d70\u5440\uff0c\u8d70\u5440\u2026\u2026 \u3000\u3000\u8981\u8d70\u5230\u90a3\u5e74\u90a3\u6708\uff0c\u624d\u80af\u505c\u4e0b\u4f60\u4eec\u7684\u9a6c\uff1f \u3000\u3000\u59d1\u5a18\u5440\uff0c\u591a\u8c22\u4f60\u7684\u597d\u5fc3\u597d\u610f\uff0c \u3000\u3000\u53ea\u662f\u6211\u4eec\u6ca1\u6709\u529e\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u4f60\u53ef\u66fe\u89c1\u8fc7\u8352\u6f20\u5f00\u82b1\uff0c \u3000\u3000\u4f60\u53ef\u66fe\u89c1\u8fc7\u51b0\u5ddd\u878d\u5316\u3002 \u3000\u3000\u4f60\u6ca1\u6709\u89c1\u8fc7\uff1f\u6ca1\u6709\u89c1\u8fc7\u5440\uff01 \u3000\u3000\u90a3\u4e48\u6d41\u6d6a\u7684\u65c5\u4eba\u54ea\uff0c \u3000\u3000\u4ed6\u4e5f\u6c38\u4e0d\u4f1a\u505c\u4e0b\uff01 \u3000\u3000\u6b4c\u58f0\u6742\u7740\u9a6c\u94c3\u98d8\u8361\u5728\u85cf\u8fb9\u7684\u8349\u539f\uff0c\u4e00\u7fa4\u5356\u5531\u7684\u6d41\u6d6a\u8005\u6b63\u5728\u8349\u539f\u7ecf\u8fc7\uff0e\u8349\u539f\u56db\u671b\u65e0\u8fb9\uff0c\u559c \u9a6c\u62c9\u96c5\u5c71\u7ef5\u5ef6\u5929\u9645\uff0c\u6676\u83b9\u7684\u96ea\u5cf0\u8c61\u4e00\u6392\u6392\u767d\u7389\u96d5\u6210\u7684\u64ce\u5929\u7389\u67f1\uff0c\u9ad8\u63d2\u4e91\u9704\uff0c\u9690\u9690\u9732\u51fa\u5934\u6765\uff0c \u4f3c\u662f\u6b63\u5728\u503e\u542c\u6d41\u6d6a\u8005\u7684\u54c0\u5f26\u51c4\u8bc9\u3002 \u3000\u3000\u8349\u539f\u4e0a\u4e00\u4e2a\u6c49\u65cf\u5c11\u5e74\u4e5f\u6b63\u5728\u503e\u542c\u8fd9\u7fa4\u6d41\u6d6a\u8005\u7684\u6b4c\u58f0\uff0c\u773c\u4e2d\u9690\u6709\u6cea\u73e0\uff0c\u6f78\u7136\u53f9\u9053\uff1a\u201c\u6211\u548c \u4f60\u4eec\u4e5f\u662f\u4e00\u6837\uff0c\u4f60\u4eec\u6d6a\u8ff9\u5929\u6daf\uff0c\u6211\u4e5f\u4e0d\u77e5\u4f55\u5e74\u4f55\u6708\u624d\u5f97\u91cd\u56de\u6545\u91cc\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u59d3\u9648\uff0c\u540d\u5524\u5929\u5b87\uff0c\u672c\u662f\u6c5f\u5357\u82cf\u5dde\u4eba\u6c0f\uff0c\u53ea\u56e0\u4ed6\u7236\u4eb2\u9648\u5b9a\u57fa\u5728\u671d\u4e3a\u5bab\uff0c\u4e0a\u7ae0\u5f39\u52be\u4e7e \u9686\u7687\u5e1d\u6700\u5e9e\u7231\u7684\u5978\u81e3\u548c\u5764\uff0c\u56e0\u800c\u88ab\u8d2c\u897f\u85cf\uff0c\u505a\u8428\u8fe6\u5b97\u7684\u5ba3\u6170\u4f7f\uff0c\u8fdc\u620c\u8fb9\u7586\uff0c\u7728\u773c\u516b\u5e74\uff0c\u4ed6\u968f \u7236\u4eb2\u6765\u65f6\u8fd8\u53ea\u6709\u5341\u5c81\uff0c\u73b0\u5728\u5df2\u662f\u5341\u516b\u5c81\u7684\u5c11\u5e74\u4e86\uff0c\u4ed6\u7236\u4eb2\u65e5\u65e5\u4e0e\u4ed6\u8c08\u8bf4\u6c5f\u5357\u98ce\u7269\uff0c\u56e0\u800c\u4ed6\u5c0f \u5c0f\u5e74\u7eaa\uff0c\u5fc3\u4e2d\u4e5f\u5145\u6ee1\u4e61\u601d\u3002 \u3000\u3000\u8fd9\u7fa4\u6d41\u6d6a\u8005\u7ea6\u6570\u5341\u4f59\uff0c\u5176\u4e2d\u6709\u85cf\u4eba\uff0c\u6709\u7ef4\u4eba\uff0c\u8fd8\u6709\u4e24\u4e2a\u6c49\u4eba\uff0c\u4f3c\u4e4e\u662f\u5728\u65c5\u9014\u4e2d\u62fc\u63cd\u800c \u6210\uff0c\u7ed3\u961f\u5356\u5531\u7684\u3002\u9648\u5929\u5b87\u76ee\u9001\u4ed6\u4eec\u7f13\u7f13\u7ecf\u8fc7\uff0c\u76ee\u5149\u5ffd\u7136\u505c\u7559\u5728\u4e00\u4e2a\u62ab\u7740\u81ea\u7eb1\u7684\u85cf\u65cf\u5c11\u5973\u8eab \u4e0a\uff0c\u8fd9\u5c11\u5973\u6742\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u6709\u5982\u9e64\u7acb\u9e21\u7fa4\uff0c\u4f17\u4eba\u53cd\u590d\u6b4c\u5531\uff0c\u53ea\u6709\u5979\u7d27\u7d27\u95ed\u7740\u5634\u513f\uff0c\u4e00\u53cc\u660e\u5982 \u79cb\u6c34\u7684\u773c\u775b\u51dd\u671b\u5929\u9645\u6d6e\u4e91\u3002\u663e\u51fa\u4e00\u6d3e\u832b\u7136\u7684\u795e\u8272\uff0c\u4efb\u7531\u9a6c\u513f\u9a6e\u7740\u5979\u8d70\uff0c\u5bf9\u540c\u4f34\u7684\u6b4c\u58f0\u542c\u800c\u4e0d \u95fb\uff0c\u4f3c\u662f\u5fc3\u4e2d\u6b63\u5728\u601d\u91cf\u4ec0\u4e48\uff0c\u597d\u4f3c\u662f\u5bf9\u4e00\u5207\u90fd\u6f20\u4e0d\u5173\u5fc3\uff0c\u8fde\u6574\u4e2a\u4e16\u754c\u90fd\u4e0d\u5b58\u5728\u4f3c\u7684\u3002\u8981\u4e0d\u662f \u5979\u7684\u773c\u73e0\u8fd8\u4f1a\u95ea\u52a8\uff0c\u9648\u5929\u5b87\u51e0\u4e4e\u6000\u7591\u9a6c\u80cc\u4e0a\u9a6e\u7684\u4e43\u662f\u4e00\u5c0a\u77f3\u50cf\u3002 \u3000\u3000\u9648\u5929\u5b87\u6b63\u5728\u51fa\u795e\uff0c\u5ffd\u542c\u5f97\u5934\u9876\u4e0a\u4e00\u58f0\u9e26\u53eb\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u731b\u5730\u91cc\u5f13\u5f26\u75be\u54cd\uff0c\u5176\u4e2d\u4e00\u4e2a\u6c49\u4eba \u9aa4\u7136\u4e00\u7bad\u5c04\u6765\uff0c\u542c\u90a3\u5229\u7bad\u7a7f\u7a7a\u7684\u523a\u8033\u4e4b\u58f0\uff0c\u7adf\u662f\u6025\u52b2\u4e4b\u6781\uff01 \u3000\u3000\u9648\u5929\u5b87\u98d8\u8eab\u4e00\u95ea\uff0c\u53cd\u624b\u4e00\u6325\uff0c\u6284\u7740\u7bad\u5c3e\uff0c\u6b63\u5f85\u559d\u53eb\uff0c\u53ea\u542c\u5f97\u50fb\u556a\u4e00\u58f0\uff0c\u5f13\u5f26\u518d\u54cd\uff0c\u8fd9\u4eba \u7528\u7684\u7adf\u662f\u8fde\u73e0\u7bad\u6cd5\uff0c\u524d\u7bad\u5c04\u51fa\uff0c\u540e\u7bad\u5373\u81f3\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u90a3\u4e4c\u9e26\u557c\u58f0\u987f\u6b62\uff0c\u4ece\u7a7a\u4e2d\u8dcc\u4e86\u4e0b\u6765\u3002 \u90a3\u6c49\u5b50\u62b1\u5f13\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5acc\u8fd9\u9e26\u58f0\u566a\u8033\uff0c\u6240\u4ee5\u628a\u5b83\u5c04\u4e0b\uff0c\u7bad\u6cd5\u4e0d\u7cbe\uff0c\u8bef\u60ca\u4e86\u516c\u5b50\u4e86\u3002\u201d \u9648\u5929\u5b87\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6c14\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u8fd8\u61c2\u5f97\u7a7a\u624b\u63a5\u7bad\u4e4b\u6cd5\uff0c\u73b0\u5728\u8fd8\u80fd\u548c\u4f60\u8bf4\u8bdd\u5417\uff1f\u8fd9\u7bad \u662f\u600e\u4e48\u5c04\u7684\uff1f\u201d\u90a3\u6c49\u5b50\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u4f60\u770b\u770b\u6211\u8fd9\u53ea\u7bad\uff0c\u5b83\u662f\u4e0d\u80fd\u4f24\u4eba\u7684\u5440\uff01\u6211\u672c\u6765\u662f \u5c04\u90a3\u4e4c\u9e26\u7684\uff0c\u602a\u53ea\u602a\u6211\u7684\u7bad\u6cd5\u4e0d\u7cbe\uff0c\u6559\u516c\u5b50\u8bef\u4f1a\u4e86\u3002\u201d\u9648\u5929\u5b87\u4e00\u770b\uff0c\u90a3\u652f\u7bad\u6ca1\u6709\u7bad\u7c07\uff0c\u679c\u7136 \u4e0d\u662f\u4f24\u4eba\u7684\u5229\u7bad\u3002\u90a3\u6c49\u5b50\u53c8\u62bd\u51fa\u4e00\u652f\u6709\u7bad\u7c07\u7684\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u624d\u662f\u4f24\u4eba\u7684\u5229\u7bad\u3002\u201d\u5f15\u5f26\u4e00\u5c04\uff0c \u76f4\u4e0a\u534a\u7a7a\uff0c\u5f85\u90a3\u7bad\u6389\u5934\u4e0b\u843d\uff0c\u94c1\u5f13\u4e00\u5f2f\uff0c\u970d\u7684\u53c8\u662f\u4e00\u7bad\uff0c\u4e24\u652f\u7bad\u521a\u597d\u5728\u7a7a\u4e2d\u78b0\u4e2a\u6b63\u7740\uff0c \u201c\u5693\u201d\u7684\u6fc0\u8d77\u4e00\u70b9\u706b\u661f\uff0c\u4e00\u95ea\u5373\u706d\u3002\u90a3\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u62b1\u5f13\u4e00\u63d6\uff0c\u8de8\u9a6c\u8d76\u4e0a\u5927\u961f\u53bb\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u6014\u6014\u51fa\u795e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u6c49\u5b50\u7bad\u6cd5\u60ca\u4eba\uff0c\u5b9e\u662f\u7f55\u89c1\uff0e\u4ed6\u521a\u624d\u90a3\u7bad\u660e\u660e\u662f\u5411\u6211\u5c04 \u6765\uff0c\u600e\u8bf4\u662f\u5931\u4e86\u51c6\u5934\u3002\u6211\u4e0e\u4ed6\u7d20\u4e0d\u4e0d\u76f8\u8bc6\uff0c\u4f55\u4f3c\u4ed6\u8981\u5c04\u6211\uff1f\u65e2\u7136\u5c04\u6211\uff0c\u53c8\u4f55\u4ee5\u7528\u7684\u662f\u6ca1\u6709\u7bad \u7c07\uff0c\u4e0d\u80fd\u4f24\u4eba\u7684\u7bad\uff0c\u5012\u5e95\u662f\u4f55\u7528\u610f\uff1f\u201d\u5b9e\u662f\u767e\u601d\u4e0d\u89e3\u3002\u6b63\u5728\u601d\u91cf\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5c11 \u7237\uff01\u201d\u4e00\u4e2a\u5e74\u7ea6\u5341\u516d\u4e03\u5c81\u7684\u4e66\u7ae5\u4e0d\u77e5\u4ece\u4ec0\u4e48\u5730\u65b9\u6084\u6084\u7684\u6e9c\u4e86\u51fa\u6765\uff0c\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a \u201c\u6c5f\u5357\uff0c\u4f60\u4e5f\u5728\u8fd9\u91cc\u5417\uff1f\u600e\u4e48\u6211\u6ca1\u77a7\u89c1\u4f60\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u7684\u7236\u4eb2\u56e0\u4e3a\u4e45\u79bb\u6c5f\u5357\uff0c\u6240\u4ee5\u7ed9\u4e66\u7ae5\u8d77\u4e86\u8fd9\u4e48\u4e00\u4e2a\u540d\u5b57\uff0c\u804a\u6170\u4e61\u601d\u3002\u8fd9\u4e66\u7ae5\u4e0e\u9648\u5929 \u5b87\u5e74\u7eaa\u76f8\u82e5\uff0c\u5e73\u7d20\u73a9\u5728\u4e00\u8d77\uff0c\u751a\u662f\u6dd8\u6c14\uff0c\u542c\u5f97\u9648\u5929\u5b87\u95ee\u4ed6\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u8001\u7237\u53eb\u6211\u51fa\u6765\u627e \u4f60\uff0c\u90a3\u9e1f\u6c49\u5c04\u4f60\uff0c\u6211\u8eb2\u5728\u8349\u91cc\u5462\u3002\u563b\uff0c\u5c11\u7237\uff0c\u6211\u8ddf\u4e86\u4f60\u8fd9\u8bb8\u591a\u5e74\uff0c\u7adf\u4e0d\u77e5\u9053\u4f60\u6709\u8fd9\u4e48\u5927\u7684\u672c \u4e8b\uff0c\u4e00\u4e0b\u5b50\u5c31\u628a\u90a3\u652f\u7bad\u63a5\u7740\u4e86\uff01\u5e73\u65f6\u4e5f\u6ca1\u89c1\u4f60\u7ec3\u8fc7\u5f13\u7bad\uff0c\u5582\uff0c\u4f60\u6559\u6211\u884c\u4e0d\u884c\uff1f\u201d\u9648\u5929\u5b87\u9762\u8272 \u4e00\u53d8\uff0c\u7aef\u5bb9\u8bf4\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4e0d\u51c6\u4f60\u8bf4\u4e0e\u8001\u7237\u77e5\u9053\uff01\u4f60\u82e5\u5c06\u6211\u4eca\u65e5\u63a5\u7bad\u4e4b\u4e8b\u5bf9\u4eba\u8bf4\u4e86\uff0c\u6211\u5c31\u6495 \u4f60\u7684\u76ae\uff01\u201d\u6c5f\u5357\u89c1\u5c11\u7237\u751a\u662f\u8ba4\u771f\uff0c\u4f38\u4f38\u820c\u5934\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u8bf4\uff0c\u4e0d\u8bf4\uff01\u201d\u5fc3\u4e2d\u6697\u6697\u5947\u60ca\uff1a\u201c\u5c11 \u7237\u6709\u90a3\u4e48\u5927\u7684\u672c\u4e8b\uff0c\u4e3a\u4f55\u5374\u8981\u7792\u7740\u8001\u7237\uff1f\u201d \u3000\u3000\u90a3\u4e66\u7ae5\u8e66\u8e66\u8df3\u8df3\uff0c\u8dd1\u53bb\u62e3\u90a3\u5730\u4e0a\u7684\u4e4c\u9e26\uff0c\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e4c\u9e26\u6ca1\u53d7\u534a\u70b9\u4f24\u7adf\u7136\u6b7b\u53bb\uff0c\u8fd9 \u662f\u600e\u4e48\u5c04\u7684\uff1f\u201d\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u90a3\u4e4c\u9e26\u679c\u7136\u7fbd\u6bdb\u5b8c\u64ce\u3001\u6ca1\u534a\u70b9\u4f24\uff0c\u90a3\u652f\u6ca1\u7c07\u7bad\u6389\u5728\u65c1 \u8fb9\uff0c\u7bad\u6746\u4e0a\u4e5f\u6ca1\u6cbe\u534a\u70b9\u8840\u3002\u5fc3\u77e5\u8fd9\u4e4c\u9e26\u4e4b\u6b7b\uff0c\u4e43\u662f\u53d7\u7bad\u6746\u7684\u6fc0\u8361\u4e4b\u529b\u9707\u4f24\u5185\u810f\u6240\u81f3\u3002\u5fc3\u4e2d\u60ca \u9053\uff1a\u201c\u8fd9\u4e4c\u9e26\u98de\u5728\u9ad8\u7a7a\uff0c\u7ed9\u5229\u7bad\u5c04\u6b7b\u4e0d\u8db3\u4e3a\u5947\uff0c\u7ed9\u7bad\u6746\u9707\u6b7b\uff0c\u90a3\u6c49\u5b50\u7684\u624b\u52b2\u5185\u529b\u53ef\u771f\u662f\u60ca \u4eba\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u95f7\u95f7\u4e0d\u4e50\u968f\u4e66\u91cd\u8fd4\u5bb6\uff0c\u56de\u5230\u5bb6\u4e2d\uff0c\u53ea\u89c1\u7236\u4eb2\u6b63\u5728\u5ba2\u5385\u4e0e\u4eba\u8c08\u8bdd\uff1a\u90a3\u4eba\u5e74\u7ea6\u4e94\u65ec\u3001 \u76f8\u8c8c\u6e05\u766f\uff0c\u4e09\u7efa\u957f\u987b\uff0c\u80cc\u5fae\u4f5d\u507b\uff0c\u6d3b\u50cf\u4e2a\u79d1\u573a\u5931\u610f\u7684\u8001\u5112\u3002 \u3000\u3000\u6b64\u4eba\u59d3\u8427\u540d\u9752\u5cf0\uff0c\u6b63\u662f\u9648\u5b9a\u57fa\u6240\u8bf7\u7684\u6559\u4e66\u5148\u751f\uff0c\u8bf4\u8d77\u6765\u8fd8\u6b63\u662f\u9648\u5b9a\u57fa\u88ab\u8d2c\u90a3\u5e74\u8bf7\u7684\u3002\u90a3 \u5e74\u9648\u5b9a\u57fa\u65b9\u4efb\u5fa1\u53f2\uff0c\u5b98\u573a\u5e94\u916c\u751a\u591a\uff0c\u65e0\u6687\u4eb2\u6559\u513f\u5b50\uff0c\u6709\u4f4d\u670b\u53cb\u4fbf\u8350\u4e86\u8fd9\u4f4d\u6559\u4e66\u5148\u751f\u6765\uff0c\u9648\u5b9a \u57fa\u63a5\u8c08\u4e4b\u4e0b\uff0c\u89c1\u8fd9\u4eba\u5b66\u95ee\u679c\u7136\u4e0d\u9519\uff0c\u4fbf\u8058\u7528\u4e86\u3002\u4e0d\u4e45\uff0c\u9648\u5b9a\u57fa\u5c31\u56e0\u4e0a\u7ae0\u5f39\u52be\u548c\u5764\u3002\u88ab\u8d2c\u897f \u85cf\uff0c\u9648\u5b9a\u57fa\u672c\u6765\u4e0d\u597d\u610f\u601d\u8981\u4ed6\u540c\u8d74\u8fb9\u7586\uff0c\u5374\u662f\u4ed6\u575a\u51b3\u540c\u5f80\uff0c\u8bf4\u662f\u5bbe\u4e3b\u76f8\u5f97\uff0c\u4e0e\u5176\u5728\u4e2d\u5dde\u843d \u9b44\uff0c\u4e0d\u5982\u540c\u8d74\u8fb9\u8352\uff0c\u9648\u5b9a\u57fa\u611f\u4ed6\u610f\u8bda\uff0c\u5f85\u4ed6\u6709\u5982\u5bb6\u4eba\u3002 \u3000\u3000\u9648\u5929\u5b87\u5411\u7236\u4eb2\u548c\u8001\u5e08\u8bf7\u5b89\u8fc7\u540e\uff0c\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u5b87\u513f\uff0c\u4f60\u5230\u54ea\u91cc\u4f1a\u8fd9\u4e48\u4e45\uff1f\u4ee5\u540e\u53ef\u4e0d\u51c6\u5355 \u72ec\u4e00\u4eba\u53bb\u73a9\u3002\u201d\u6c5f\u5357\u63d2\u5634\u9053\uff1a\u201c\u6709\u4e00\u961f\u5356\u5531\u7684\u6765\u4e86\uff0c\u4eca\u665a\u53ef\u80fd\u6709\u620f\u770b\u5462\u3002\u201d\u9648\u5929\u5b87\u6a2a\u4ed6\u4e00 \u773c\uff0c\u6c5f\u5357\u8bf4\u6e9c\u4e86\u5634\uff0c\u5ffd\u9053\uff1a\u201c\u6559\u4e66\u5148\u751f\uff0c\u4f60\u89c1\u591a\u8bc6\u5e7f\uff0c\u53ef\u89c1\u8fc7\u6709\u4eba\u7528\u6ca1\u6709\u7bad\u7c07\u7684\u7bad\u5c04\u4e4c\u9e26\u7684 \u4e48\uff1f\u8427\u9752\u5cf0\u795e\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u9762\u5982\u767d\u7eb8\uff0c\u6447\u6447\u6b32\u5815\u3002\u9648\u5b9a\u57fa\u614c\u9053\uff1a\u201c\u8427\u5148\u751f\u4f60\u600e\u4e48 \u5566\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5929\u65f6\u4e0d\u6b63\uff0c\u6562\u60c5\u662f\u611f\u5192\u4e86\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u6276\u5148\u751f\u8fdb\u623f\u6b47\u606f\u3002\u201d \u9648\u5929\u5b87\u9053\uff1a\u201c\u5148\u751f\u4e0d\u8212\u670d\uff0c\u4f60\u4e0d\u51c6\u591a\u8bdd\uff0c\u6270\u4ed6\u4e0d\u5b89\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u77e5\u9053\u5566\u3002\u201d\u5077\u5077\u5411\u9648\u5929\u5b87 \u529e\u4e00\u4e2a\u9b3c\u8138\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u8bf4\u4f60\u63a5\u7bad\u4e4b\u4e8b\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u5fc3\u4e2d\u6781\u4e3a\u5947\u602a\uff0c\u4e0d\u660e\u5148\u751f\u4f55\u4e3a\u5982\u6b64\u9a87\u6015\u3002\u53ea\u542c\u5f97\u7236\u4eb2\u8bf4\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u53ef\u4e0d\u8981\u5355\u72ec \u53bb\u73a9\uff0c\u6ca1\u4e8b\u6700\u597d\u7559\u5728\u5bb6\u4e2d\u3002\u4f60\u77e5\u9053\u5417\uff1f\u53bb\u5e74\u5c3c\u6cca\u5c14\u56fd\u7684\u5eca\u5c14\u54af\u65cf\u4fb5\u5165\u897f\u85cf\uff0c\u88ab\u6211\u4eec\u5929\u671d\u6d3e\u5175 \u6253\u9000\uff0c\u4ed6\u4eec\u5b9e\u4e0d\u7518\u5fc3\uff0c\u542c\u8bf4\u4ed6\u4eec\u6d3e\u9063\u523a\u5ba2\u6765\uff0c\u8981\u6740\u5c3d\u5927\u6e05\u7684\u5b98\u5458\uff0c\u73b0\u5728\u9a7b\u85cf\u7684\u5b98\u5458\uff0c\u6ca1\u6709\u62a4 \u536b\u966a\u7740\uff0c\u8c01\u90fd\u4e0d\u6562\u968f\u4fbf\u8d70\u52a8\u3002\u201d\u9648\u5929\u5b87\u6012\u9053\uff1a\u201c\u771f\u7684\uff1f\u4ed6\u4eec\u6562\u8fd9\u6837\u7684\u5927\u80c6\uff1f\u201d\u9648\u5b9a\u57fa\u9053\uff1a \u201c\u8fd9\u662f\u798f\u5927\u5e05\u603b\u90e8\u4f20\u51fa\u6765\u7684\u3002\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\u3002\u201d\u798f\u5927\u5e05\u5373\u798f\u5eb7\u5b89\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u4e7e \u9686\u7684\u79c1\u751f\u5b50\uff0c\u4e8b\u5c5e\u65e0\u7a3d\uff0c\u96be\u4ee5\u5165\u4fe1\uff0c\u4e0d\u8fc7\u4ed6\u662f\u4e7e\u9686\u7687\u5e1d\u6700\u5e9e\u7231\u7684\u5927\u5c06\uff0c\u5374\u662f\u4e8b\u5b9e\u3002\u4e7e\u9686\u91cd\u89c6 \u8fb9\u7586\uff0c\u6240\u4ee5\u6d3e\u798f\u5eb7\u5b89\u505a\u9a7b\u85cf\u5927\u81e3\uff0c\u603b\u90e8\u8bbe\u5728\u897f\u85cf\u9996\u5e9c\u62c9\u8428\u3002 \u3000\u3000\u9648\u5929\u5b87\u542c\u4e86\u867d\u89c9\u6124\u6012\uff0c\u5374\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u8fd9\u665a\u4ed6\u7236\u4eb2\u4e00\u65e9\u5c31\u53eb\u4ed6\u7761\u89c9\uff0c\u4ed6\u5374\u7ffb\u6765\u590d\u53bb\u7684 \u5c3d\u5728\u60f3\u90a3\u7fa4\u5356\u5531\u7684\u6d41\u6d6a\u8005\u3002\u90a3\u4e2a\u795e\u7bad\u60ca\u4eba\u7684\u5c04\u624b\u5df2\u53eb\u4ed6\u731c\u4e0d\u900f\uff0c\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\u7684\u5f71\u5b50\u66f4 \u5df1\u7559\u5728\u8111\u4e2d\uff0c\u6325\u4e4b\u4e0d\u53bb\u3002\u53ea\u8981\u4e00\u95ed\u4e0a\u773c\uff0c\u5c31\u4eff\u4f5b\u5982\u5728\u773c\u524d\uff0c\u90a3\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u90a3\u77f3\u50cf\u822c\u7684\u8138 \u5b54\uff0c\u7adf\u8c61\u662f\u9ed1\u6697\u4e2d\u5077\u5077\u7684\u77a7\u8005\u4ed6\u3002\u5ffd\u542c\u5f97\u8fdc\u8fdc\u4f20\u6765\u4e00\u9635\u549a\u549a\u7684\u9f13\u58f0\uff0c\u53c8\u662f\u4e00\u9635\u94dc\u94b5\u58f0\u548c\u5587\u53ed \u58f0\uff0c\u58f0\u97f3\u5355\u8c03\u4e4b\u6781\uff0c\u4e0d\u8bba\u662f\u6572\u3001\u6253\u3001\u5439\u3001\u62cd\uff0c\u603b\u662f\u4e0d\u7d27\u4e0d\u6162\uff0c\u97f3\u8c03\u8282\u594f\u51e0\u4e4e\u6beb\u65e0\u53d8\u5316\u3002\u9648\u5929 \u5b87\u77e5\u9053\uff0c\u8fd9\u4e00\u5b9a\u662f\u90a3\u7fa4\u5356\u5531\u8005\u5728\u8349\u539f\u591c\u6f14\uff0c\u4e00\u4e2a\u4eba\u5728\u9ed1\u591c\u4e4b\u4e2d\uff1b\u542c\u8fd9\u5355\u8c03\u7684\u6beb\u65e0\u53d8\u5316\u7684\u97f3 \u54cd\uff0c\u4e0d\u89c9\u6709\u4e9b\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u9648\u5929\u5b87\u521a\u521a\u7761\u9192\uff0c\u5ffd\u542c\u5f97\u6c5f\u5357\u5728\u5916\u9762\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff0c\u6211\u6628\u591c\u89c1\u4e86 \u4e00\u4e2a\u5973\u9b3c\u3002\u54c8\uff0c\u771f\u7684\uff0c\u4e0d\u9a97\u4f60\uff0c\u4e00\u4e2a\u5973\u9b3c\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u5403\u4e86\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u6c5f\u5357\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u90a3\u5973\u9b3c\u62ab\u7740\u4e24\u6761\u7ea2\u7ef8\uff0c\u5047\u53d1\u62d6\u5230\u8170 \u95f4\uff0c\u6234\u7740\u4e00\u4e2a\u4e09\u89d2\u5f62\u7684\u9762\u5177\uff0c\u53c8\u957f\u53c8\u5bbd\u7684\u820c\u5934\u4ece\u53e3\u4e2d\u642d\u62c9\u51fa\u6765\uff1b\u5979\u8fd8\u8df3\u821e\u5462\uff0c\u8f6c\u5440\u8f6c\u7684\u8f6c\u5f97 \u5feb\u6781\u4e86\uff0c\u6211\u77a7\u90fd\u77a7\u4e0d\u6e05\u695a\u3002\u54c8\uff0c\u5979\u814b\u4e0b\u8fd8\u63d2\u7740\u4e24\u67c4\u77ed\u5200\uff0c\u8df3\u5b8c\u4e86\u821e\u5c31\u5927\u7ffb\u7b4b\u6597\uff0c\u90a3\u4e24\u67c4\u5200\u660e \u6643\u6643\u7684\uff0c\u53eb\u4eba\u89c1\u4e86\u60ca\u5fc3\uff0c\u53ef\u5979\u5927\u7ffb\u7b4b\u6597\uff0c\u5374\u4e00\u70b9\u4e5f\u6ca1\u53d7\u4f24\u3002\u540e\u6765\u5979\u6f14\u5b8c\u4e86\uff0c\u628a\u5047\u53d1\u4e00\u9664\uff0c\u9762 \u5177\u4e00\u62c9\uff0c\u54c8\uff0c\u4f60\u731c\u600e\u4e48\u6837\uff1f\u7f8e\u6781\u5566\u3002\u6211\u6240\u89c1\u8fc7\u7684\u85cf\u65cf\u5c11\u5973\uff0c\u6ca1\u6709\u4e00\u4e2a\u6bd4\u5f97\u4e0a\uff0c\u53ea\u662f\u9762\u5b54\u51b0\u51b7 \u7684\uff0c\u54c8\uff0c\u8fd8\u662f\u50cf\u4e00\u4e2a\u5973\u9b3c\uff01\u201d\u539f\u6765\u4ed6\u662f\u548c\u770b\u95e8\u7684\u8001\u738b\u8bf4\u8bdd\uff0c\u8bf4\u7684\u662f\u6628\u665a\u6240\u770b\u7684\u620f\u9648\u5929\u5b87\u4e00 \u542c\uff0c\u5c31\u77e5\u4ed6\u51c6\u662f\u8bf4\u90a3\u4e2a\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\u3002 \u3000\u3000\u770b\u95e8\u7684\u8001\u738b\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u76ae\u75d2\u5566\uff0c\u8001\u7237\u5429\u5490\u6211\u4eec\u4e0d\u8981\u968f\u4fbf\u5916\u51fa\uff0c\u4f60\u5374 \u5077\u5077\u4e00\u4e2a\u4eba\u6e9c\u53bb\u770b\u620f\u3002\u201d\u6c5f\u5357\u54c8\u54c8\u4e00\u7b11\uff0c\u602a\u58f0\u602a\u6c14\u7684\u56de\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u4eba\u6e9c\u53bb\u770b\u620f\uff1f\u54c8\uff0c\u8001 \u738b\uff0c\u4f60\u53c8\u731c\u9519\u5566\uff01\u4f60\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\uff0c\u54b1\u4eec\u7684\u6559\u4e66\u5148\u751f\u4e5f\u6e9c\u53bb\u770b\u5566\uff0c\u54a6\uff0c\u8bf4\u8d77\u6765\u53ef\u6bd4\u90a3\u5973\u9b3c\u8fd8 \u602a\uff0c\u54b1\u4eec\u7684\u5148\u751f\u54ea\u2014\u2014\u201d\u521a\u8bf4\u5230\u8fd9\u91cc\u3001\u9648\u5929\u5b87\u5df2\u6025\u6025\u5f00\u95e8\u51fa\u6765\u4eba\u7acb\u5373\u559d\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u8fd9\u591a \u5634\u7684\u6bdb\u75c5\u51e0\u65f6\u624d\u6539\uff01\u5feb\u8fdb\u6765\u66ff\u6211\u6536\u62fe\u623f\u95f4\u3002\u201d\u8001\u738b\u89c1\u5c11\u7237\u751f\u6c14\uff0c\u4fcf\u6084\u8d70\u5f00\uff0c\u6c5f\u5357\u4f38\u4e86\u4f38\u820c \u5934\uff0c\u8d70\u5165\u9648\u5929\u5b87\u623f\u4e2d\uff0c\u4f5c\u51fa\u4e00\u526f\u53d7\u59d4\u5c48\u7684\u6a21\u6837\u9053\uff1a\u201c\u5c11\u7237\uff0c\u4f60\u8fd9\u4e24\u5929\u600e\u4e48\u8fd9\u6837\u51f6\u5475\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u63a9\u4e0a\u623f\u95e8\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u8427\u5148\u751f\u6628\u665a\u600e\u4e48\u6837\uff1f\u201d\u6c5f\u5357\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u5c11 \u7237\u60f3\u542c\u6545\u4e8b\uff0c\u636e\u6211\u770b\u5566\uff0c\u54b1\u4eec\u7684\u5148\u751f\u4e5f\u662f\u4e2a\u5927\u6709\u672c\u4e8b\u7684\u4eba\uff0c\u6628\u665a\u4eba\u6324\u5f97\u5f88\u3002\u6211\u6324\u4e86\u6ee1\u8eab\u81ed\u6c57 \u624d\u6324\u4e86\u8fdb\u53bb\uff0c\u7ed9\u540e\u9762\u7684\u4eba\u63a8\u5440\u78b0\u5440\uff0c\u5140\u662f\u7acb\u4e0d\u7740\u811a\u6b65\uff0c\u53ef\u54b1\u4eec\u90a3\u4f4d\u5148\u751f\u5440\uff0c\u4f60\u522b\u77a7\u4ed6\u90a3\u526f\u5f31 \u4e0d\u7981\u98ce\u7684\u6837\u4e86\uff0c\u4ed6\u53ef\u7ad9\u5f97\u5f88\u7a33\uff0c\u90a3\u4e9b\u4eba\u6324\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u5c31\u50cf\u6f6e\u6c34\u822c\u7684\u4e24\u8fb9\u5206\u5f00\uff0c\u78b0\u90fd\u6ca1\u6709\u78b0 \u7740\u4ed6\u3002\u4e5f\u4e0d\u77e5\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u513f\uff1f\u6211\u5947\u602a\u6781\u5566\uff0c\u60f3\u8fc7\u53bb\u95ee\u4ed6\uff0c\u4eba\u53c8\u6324\u3001\u90a3\u5973\u9b3c\u53c8\u4e0a\u573a\u4e86\uff0c\u6211 \u5c31\u6ca1\u6709\u8fc7\u53bb\u3002\u8c01\u77e5\u770b\u5b8c\u4e86\u90a3\u573a\u5973\u9b3c\u7684\u620f\u3001\u518d\u627e\u4e4b\u65f6\uff0c\u4ed6\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u6709\u5fc3\u6765\u770b\u620f\uff0c\u600e\u4e48\u53ea\u770b \u4e86\u4e00\u573a\u5c31\u8d70\uff1f\u5c11\u7237\uff0c\u4f60\u8bf4\u4ed6\u53ef\u662f\u4e0d\u662f\u4e00\u4e2a\u602a\u4eba\uff1f\u201d\u9648\u5929\u5b87\u9762\u5b54\u4e00\u677f\uff0c\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u8427\u5148\u751f\u7684 \u4e8b\uff0c\u53ea\u51c6\u4f60\u8bf4\u7ed9\u6211\u542c\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u4e0d\u8bba\u662f\u8001\u738b\uff0c\u751a\u81f3\u662f\u8001\u7237\uff0c\u90fd\u4e0d\u51c6\u4f60\u8bf4\uff0c\u4f60\u82e5\u8bf4\u4e86\uff0c\u6211\u5c31 \u6495\u4f60\u7684\u76ae\uff0c\u4e0d\uff0c\u6211\u5c31\u518d\u4e5f\u4e0d\u7406\u4f60\u3002\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7406\u6211\u6bd4\u6495\u6211\u7684\u76ae\u8fd8\u96be\u53d7\uff0c\u597d\u5c11\u7237\uff0c\u4f60 \u653e\u5fc3\uff0c\u8fd9\u56de\u6211\u4e0d\u518d\u591a\u5634\u5566\u3002\u201d\u9648\u5929\u5b87\u4e0e\u6c5f\u5357\u5e73\u7d20\u73a9\u5728\u4e00\u8d77\uff0c\u672c\u6765\u6ca1\u6709\u4ec0\u4e48\u4e3b\u4ec6\u4e4b\u5206\uff0c\u77e5\u9053\u4ed6 \u7684\u813e\u6c14\uff0c\u4e00\u8bf4\u4e0d\u7406\u4ed6\uff0c\u4ed6\u5c31\u4e0d\u6562\u518d\u4fcf\u76ae\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u6d17\u8fc7\u4e86\u8138\uff0c\u5403\u4e86\u65e9\u70b9\uff0c\u6c5f\u5357\u53c8\u8fdb\u6765\u9053\uff1a\u201c\u8001\u7237\u53eb\u4f60\u3002\u201d\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u53c8\u53eb\u6211\u505a \u4ec0\u4e48\uff1f\u201d\u51fa\u5230\u542c\u5802\uff0c\u53ea\u89c1\u7236\u4eb2\u9762\u8272\u6c89\u6697\uff0c\u9053\uff1a\u201c\u571f\u53f8\u4eca\u5929\u8981\u89c1\u4f60\uff0c\u53ef\u4e0d\u77e5\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u8fd9\u571f \u53f8\u813e\u6c14\u6781\u574f\uff0c\u8fde\u6211\u4eec\u671d\u5ead\u547d\u5b98\u90fd\u4e0d\u5927\u653e\u5728\u773c\u91cc\uff0c\u6211\u6765\u4e86\u516b\u5e74\uff0c\u4e5f\u53ea\u89c1\u8fc7\u4ed6\u51e0\u9762\uff0c\u4eca\u513f\u4ed6\u5374\u7279 \u522b\u6d3e\u4eba\u8bf7\u6211\u53bb\u5403\u996d\uff09\u8fd8\u6307\u540d\u8bf7\u4f60\u4e00\u9053\u53bb\uff0c\u4f60\u5feb\u6362\u8863\u670d\u5427\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u4e3a\u4f55\u4ed6\u6307\u540d\u8981\u6211\u540c\u53bb\uff0c\u6211\u4e0d\u53bb\uff01\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u6211\u5728\u4ed6 \u7684\u8f96\u5730\u4e3a\u5b98\uff0c\u4ed6\u662f\u4e3b\uff0c\u54b1\u4eec\u662f\u5bbe\uff0c\u5bbe\u4e3b\u7406\u5e94\u548c\u597d\uff0c\u4f55\u51b5\u54b1\u4eec\u6709\u8bb8\u591a\u4e8b\u60c5\u8fd8\u8981\u4ef0\u4ed7\u4e8e\u4ed6\uff0c\u5b98\u573a \u4e4b\u4e2d\uff0c\u5bb6\u4eba\u5b50\u5f1f\u4e92\u76f8\u6765\u5f80\u4e5f\u662f\u6b63\u5e38\uff0c\u4ed6\u65e2\u6709\u8bf7\uff0c\u600e\u80fd\u4e0d\u53bb\uff1f\u4f60\u5c11\u95f9\u5c11\u7237\u813e\u6c14\uff01\u201d\u9648\u5929\u5b87\u65e0 \u5948\uff0c\u53ea\u597d\u6362\u4e86\u8863\u670d\uff0c\u968f\u7236\u4eb2\u53bb\u62dc\u8bbf\u571f\u53f8\uff0c\u5ba3\u6170\u4f7f\u4e43\u662f\u6587\u5b98\uff0c\u53ea\u6709\u51e0\u5341\u540d\u62a4\u536b\u4eb2\u5175\uff0c\u9648\u5b9a\u57fa\u6311 \u6765\u6311\u53bb\uff0c\u597d\u534a\u5929\u624d\u9009\u51fa\u516b\u540d\u76f8\u8c8c\u9b41\u6342\u52c7\u6b66\u6709\u529b\u7684\u5175\u4e01\u4f5c\u81ea\u5df1\u7684\u968f\u884c\u536b\u58eb\u3002 \u3000\u3000\u6b63\u5f85\u51fa\u95e8\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u9a6c\u5636\uff0c\u5bb6\u4e01\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u6d85\u5df4\u6c42\u89c1\u5927\u4eba\u3002\u201d\u9648\u5b9a\u57fa\u53c8\u60ca\u53c8 \u559c\uff0c\u9053\uff1a\u201c\u771f\u662f\u4fc4\u9a6c\u767b\u6d85\u5df4\u5417\uff1f\u600e\u7684\u53ea\u4ed6\u4e00\u4eba\u524d\u6765\uff1f\u201d\u6d85\u5df4\u4e43\u662f\u897f\u85cf\u7684\u5b98\u8854\uff0c\u6bcf\u4e2a\u571f\u53f8\u4e0b\uff0c \u5206\u8bbe\u56db\u4e2a\u6d85\u5df4\uff0c\u638c\u7ba1\u519b\u653f\u3001\u6c11\u5211\uff0c\u6743\u529b\u751a\u5927\u3002\u6bcf\u4e00\u6d85\u5df4\u51fa\u95e8\u4e4b\u65f6\uff0c\u90fd\u662f\u4ec6\u4ece\u5982\u4e91\uff0c\u4ece\u65e0\u5355\u72ec \u4e00\u4eba\u51fa\u73b0\uff0c\u662f\u4ee5\u9648\u5b9a\u57fa\u6709\u6b64\u4e00\u95ee\u3002 \u3000\u3000\u9648\u5929\u5b87\u4f8d\u7acb\u4e00\u65c1\uff0c\u53ea\u89c1\u4fc4\u9a6c\u767b\u6d85\u5df4\u5b66\u7740\u671d\u5ead\u5b98\u5458\u7684\u8d70\u8def\u59ff\u52bf\uff0c\u53cc\u624b\u53cd\u526a\u80cc\u540e\uff0c\u8e31\u7740\u65b9\u6b65 \u8d70\u5230\u81ea\u5df1\u7684\u7236\u4eb2\u8ddf\u524d\uff0c\u606d\u606d\u656c\u656c\u7684\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u5e03\u53ef\u662f\u8d74\u571f\u53f8\u4e4b\u5bb4\u4e48\uff1f\u201d\uff08\u6ce8\uff1a \u201c\u672c\u5e03\u201d\u4e43\u662f\u85cf\u8bed\u7684\u5927\u5b98\u4e4b\u610f\uff0c\u4e5f\u662f\u5bf9\u5b98\u5458\u7684\u4e00\u79cd\u5c0a\u79f0\uff09\u9648\u5b9a\u57fa\u663e\u51fa\u53d7\u5ba0\u82e5\u60ca\u7684\u6a21\u6837\uff0c\u614c\u5fd9 \u8fd8\u793c\uff0c\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4e0d\u6562\u6709\u52b3\u6d85\u5df4\u6765\u63a5\u3002\u201d\u5fc3\u4e2d\u5927\u662f\u5947\u602a\uff1a\u8fd9\u4fc4\u9a6c\u767b\u6d85\u5df4\u5e73\u65e5\u6c14\u7130\u751a\u5927\uff0c\u4f55 \u4ee5\u4eca\u65e5\u5bf9\u81ea\u5df1\u5c0a\u656c\u5982\u65af\uff01 \u3000\u3000\u4fc4\u9a6c\u767b\u7728\u7728\u773c\u775b\uff0c\u7b11\u9053\u201c\u65e0\u4e8b\u4e0d\u767b\u4e09\u5b9d\u6bbf\uff0c\u4eca\u65e5\u5230\u6765\uff0c\u5b9e\u662f\u6c42\u672c\u5e03\u505a\u4e00\u4ef6\u597d\u4e8b\u3002\u201d\u9648\u5b9a \u57fa\u672c\u4ee5\u4e3a\u4ed6\u662f\u571f\u53f8\u6d3e\u6765\u8fce\u63a5\u81ea\u5df1\u7684\uff0c\u95fb\u8a00\u9887\u51fa\u610f\u5916\uff0c\u95f4\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u6628\u65e5\u8349 \u539f\u6765\u4e86\u4e00\u7fa4\u5356\u5531\u7684\u6d41\u6d6a\u6c49\uff0c\u672c\u5e9c\u53ef\u77e5\u9053\u4e48\uff1f\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u542c\u5bb6\u4eba\u8bf4\u8fc7\u3002\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u539f \u6765\u4ed6\u4eec\u4e43\u662f\u5077\u9a6c\u8d3c\uff0c\u672c\u9886\u4e5f\u771f\u4e0d\u9519\uff0c\u5c45\u7136\u5077\u4e86\u571f\u53f8\u7684\u4e94\u5339\u9a6c\uff0c\u7537\u7684\u90fd\u9003\u8dd1\u4e86\uff0c\u53ea\u6349\u5230\u4e00\u4e2a\u5c11 \u5973\u3002\u201d\u9648\u5929\u5b87\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5176\u4ed6\u7684\u4eba\u4e0d\u77e5\uff0c\u90a3\u4e2a\u7528\u7bad\u7c07\u5c04\u9e26\u7684\u6c49\u4eba\u53ef\u662f\u5927\u6709\u672c\u9886 \u4e4b\u4eba\uff0c\u600e\u4f1a\u505a\u5077\u9a6c\u8d3c\uff0c\u53ea\u6015\u5176\u4e2d\u8fd8\u6709\u5185\u60c5\u3002\u90a3\u5c11\u5973\u8be5\u4e0d\u4f1a\u662f\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5973\u90ce\u5427\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4fc4\u9a6c\u767b\u53c8\u9053\uff1a\u201c\u672c\u5e03\u5728\u6b64\u591a\u5e74\uff0c\u60f3\u5fc5\u77e5\u9053\u571f\u53f8\u60e9\u6cbb\u76d7\u8d3c\u7684\u89c4\u77e9\u3002\u201d\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00 \u6817\uff0c\u4ed6\u4e5f\u66fe\u542c\u7236\u4eb2\u8bf4\u8fc7\uff0c\u571f\u53f8\u60e9\u6cbb\u76d7\u8d3c\uff0c\u624b\u6bb5\u6700\u4e3a\u6b8b\u9177\uff0c\u5148\u525c\u773c\u73e0\uff0c\u540e\u5272\u53cc\u624b\u3002\u60f3\u8d77\u795e\u79d8\u5c11 \u5973\u90a3\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u4e0d\u89c9\u5168\u8eab\u98a4\u6296\u3002\u9648\u5b9a\u57fa\u4e5f\u53d8\u4e86\u9762\u8272\uff0c\u53ea\u662f\u571f\u53f8\u7684\u5211\u7f5a\uff0c\u81ea\u5df1\u53ef\u4e0d\u4fbf \u975e\u8bae\u3002\u90a3\u4fc4\u9a6c\u767b\u53c8\u9053\uff1a\u201c\u6211\u7d20\u6765\u5fc3\u6148\uff0c\u5b9e\u662f\u4e0d\u5fcd\u89c1\u90a3\u5973\u90ce\u53d7\u6b64\u5211\u7f5a\uff0c\u6c42\u672c\u5e02\u4eca\u65e5\u5f80\u89c1\u571f\u53f8\u4e4b \u65f6\uff0c\u4ee3\u90a3\u5c11\u5973\u8bf4\u6e05\u3002\u82e5\u7136\u8981\u8d4e\u91d1\u7684\u8bdd\u3002\u8bf7\u4f60\u5148\u4ed8\uff0c\u6211\u53ef\u4ee5\u6697\u4e2d\u8fd8\u4f60\u3002\u201d\u4fc4\u9a6c\u767b\u6b64\u8a00\u4e00\u51fa\uff0c\u9648 \u5b9a\u57fa\u66f4\u662f\u5947\u602a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4fc4\u9a6c\u767b\u7d20\u6765\u8d2a\u541d\u51fa\u540d\uff0c\u4ee5\u4f55\u4eca\u65e5\u5982\u6b64\u6177\u6168\uff1f\u96be\u9053\u548c\u90a3\u5c11\u5973\u6709 \u4ec0\u4e48\u76f8\u5e72\u4e0d\u6210\uff1f\u201d\u53ef\u662f\u82e5\u7136\u90a3\u5c11\u5973\u662f\u548c\u4fc4\u9a6c\u767b\u6709\u5173\u7cfb\u4e4b\u4eba\uff0c\u5979\u53c8\u600e\u4f1a\u5728\u8349\u539f\u5356\u5531\uff1f\u201d \u3000\u3000\u4fc4\u9a6c\u767b\u89c1\u9648\u5b9a\u57fa\u9690\u8e87\u4e0d\u51b3\uff0c\u5927\u662f\u7126\u6025\uff0c\u6413\u624b\u8bf4\u9053\uff1a\u201c\u672c\u5e03\u5927\u4eba\uff0c\u90a3\u4f4d\u59d1\u5a18\u7684\u6027\u547d\u5c31\u5168\u7cfb \u5728\u4f60\u7684\u624b\u4e0a\u4e86\u3002\u201d\u9648\u5b9a\u57fa\u6168\u7136\u8bf4\u9053\uff1a\u201c\u6551\u4eba\u4e00\u547d\uff0c\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\uff0c\u6211\u81ea\u5f53\u5c3d\u529b\u800c\u4e3a\uff0c\u82e5\u8981\u8d4e \u91d1\uff0c\u6211\u4e5f\u8fd8\u6709\u5c11\u8bb8\u5b98\u56ca\uff0c\u4e0d\u5fc5\u6d85\u5df4\u7834\u8d39\uff0c\u6015\u53ea\u6015\u571f\u53f8\u672a\u5fc5\u5141\u51c6\u3002\u201d\u4fc4\u9a6c\u767b\u559c\u9053\uff1a\u201c\u6709\u672c\u5e03\u6c42 \u60c5\uff0c\u571f\u53f8\u5b9a\u5fc5\u51c6\u5141\uff0c\u6211\u544a\u8f9e\u4e86\u3002\u4eca\u65e5\u4e4b\u4e8b\u60c5\u5343\u4e07\u4e0d\u8981\u5728\u571f\u53f8\u9762\u524d\u63d0\u8d77\u3002\u201d\u606d\u606d\u656c\u656c\u7684\u53c8\u884c\u4e86 \u4e00\u793c\uff0c\u51fa\u95e8\u4e4b\u65f6\uff0c\u5ffd\u7136\u5bf9\u9648\u5929\u5b87\u7b11\u4e86\u4e00\u7b11\uff0c\u795e\u60c5\u751a\u662f\u5947\u7279\u3002\u9648\u5929\u5b87\u4e00\u5f85\u6d85\u5df4\u51fa\u95e8\uff0c\u7acb\u523b\u8bf4 \u9053\uff1a\u201c\u7239\uff0c\u54b1\u4eec\u5feb\u53bb\u3002\u201d\u9648\u5b9a\u57fa\u4e0d\u89c9\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u521a\u624d\u4f60\u4e0d\u662f\u8fd8\u4e0d\u60f3\u53bb\u7684\u5417\uff1f\u201d\u9648\u5929\u5b87 \u9762\u4e0a\u4e00\u7ea2\uff0c\u53ea\u542c\u5f97\u7236\u4eb2\u5df2\u53eb\u5bb6\u4eba\u5907\u9a6c\u3002\u571f\u53f8\u7684\u5e84\u9662\u501a\u5c71\u5efa\u7b51\uff0c\u9ad8\u4e00\u5c42\u4f4e\u4e00\u5c42\uff0c\u4e00\u5c42\u53e0\u4e00\u5c42\uff0c \u4ece\u4e0b\u9762\u770b\u8d77\u6765\u5b9b\u5982\u4e00\u5ea7\u65b9\u5f62\u7684\u57ce\u5821\u3002\u9648\u5b9a\u57fa\u4e00\u884c\u4eba\u5feb\u9a6c\u8d76\u5230\uff0c\u65e5\u5934\u6b63\u5728\u5929\u4e2d\uff0c\u521a\u597d\u8d76\u4e0a\u4e2d\u5348 \u7684\u5bb4\u4f1a\u3002\uff08\u897f\u85cf\u571f\u53f8\u7684\u5bb4\u4e0a\uff0c\u60ef\u4e8e\u4e2d\u5348\u5f00\u59cb\uff0c\u996e\u81f3\u65e5\u843d\u5373\u6563\uff09\u9648\u5b9a\u57fa\u7236\u5b50\u88ab\u5f15\u5230\u82b1\u56ed\u7684\u4ead \u5b50\uff0c\u968f\u4ece\u6563\u5728\u56ed\u4e2d\u4f8d\u536b\u3002\u4ead\u4e2d\u5df2\u6446\u8bbe\u597d\u4e00\u5e2d\u9152\u5e2d\uff0c\u9648\u5b9a\u57fa\u7236\u5e72\u521a\u521a\u5750\u5b9a\uff0c\u53ea\u542c\u5f97\u7b5d\u5b50\u4e0b\u6446\u5217 \u4e24\u65c1\u7684\u85cf\u5175\u5927\u58f0\u62a5\u9053\uff1a\u201c\u571f\u53f8\u5230\uff01\u201d \u3000\u3000\u53ea\u89c1\u90a3\u571f\u53f8\u5e74\u7ea6\u4e94\u65ec\uff0c\u9e70\u9f3b\u864e\u989d\uff0c\u53cc\u773c\u95ea\u95ea\u6709\u5149\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u51db\uff0c\u9648\u5b9a\u57fa\u4f9d\u7167\u85cf\u65cf\u793c\u4eea \u732e\u8fc7\u201c\u54c8\u8fbe\u201d\uff08\u767d\u8272\u7684\uff0c\u5728\u897f\u85cf\u662f\u4e00\u79cd\u5d07\u9ad8\u5c0a\u8d35\u7684\u793c\u54c1\uff09\uff0c\u90a3\u571f\u53f8\u7b11\u54aa\u54aa\u7684\u6253\u91cf\u9648\u5929\u5b87\uff0c\u597d \u534a\u664c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4ee4\u90ce\u5417\uff1f\u771f\u597d\u76f8\u8c8c\uff01\u201d\u53cc\u638c\u4e00\u62cd\uff0c\u53eb\u9053\uff1a\u201c\u5e26\u72af\u4eba\u6765\uff01\u201d\u8f6c\u8fc7\u5934\u6765\uff0c\u53c8 \u5bf9\u9648\u5b9a\u57fa\u7b11\u9053\uff1a\u201c\u54b1\u8fd9\u662f\u4e2a\u7a77\u5730\u65b9\uff0c\u6ca1\u6709\u4ec0\u4e48\u4e1c\u897f\u53ef\u5a31\u8d35\u5bbe\uff0c\u8bf7\u4f60\u770b\u770b\u6211\u5ba1\u72af\u6d88\u9063\uff0c\u54c8\uff0c\u8fd9 \u4e2a\u72af\u4eba\u53ef\u8fd8\u771f\u6f02\u4eae\u5462\uff01\u201d \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u53ea\u89c9\u8840\u8109\u6124\u5f20\uff0c\u547c\u5438\u51e0\u4e4e\u7a92\u606f\u3002\u53ea\u89c1\u4e24\u540d\u85cf\u5175\u631f\u7740\u4e00\u540d\u5c11\u5973\uff0c\u7f13\u7f13\u8d70 \u6765\uff0c\u5728\u4ead\u5b50\u5916\u8fb9\u7ad9\u5b9a\uff0c\u8fd9\u5c11\u5973\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6628\u65e5\u6240\u89c1\u7684\u85cf\u65cf\u5c11\u5973\u3002\u4ead\u5b50\u4e0b\u9762\u5df2\u6446\u597d\u5211\u5177\uff0c \u5176\u4e2d\u5305\u62ec\u4e24\u628a\u5bbd\u5203\u7684\u85cf\u5200\u548c\u4e24\u652f\u53ef\u4ee5\u5229\u5229\u843d\u843d\u628a\u773c\u73e0\u63a2\u51fa\u6765\u7684\u5c0f\u7af9\u7ba1\uff0c\u8fd8\u6709\u4e00\u4e2a\u77f3\u5708\uff0c\u4e0a\u9762 \u6709\u4e24\u4e2a\u534a\u5b64\u5f62\u7684\uff0c\u4e0d\u76f8\u7c98\u8fde\u7684\u8584\u94c1\u7247\uff0c\u53ef\u4e0d\u77e5\u662f\u4f5c\u4ec0\u4e48\u7528\u7684\u3002\u90a3\u5c11\u5973\u5bf9\u9762\u524d\u7684\u5211\u5177\u77a7\u4e5f\u4e0d \u77a7\uff0c\u8138\u4e0a\u4ecd\u662f\u4e00\u6d3e\u6f20\u7136\u7684\u795e\u8272\uff0c\u773c\u775b\u4e2d\u8fd8\u9690\u9690\u5e26\u6709\u4e00\u79cd\u5632\u5f04\u7684\u773c\u5149\uff0c\u597d\u8c61\u88ab\u5ba1\u8baf\u7684\u4e0d\u662f\u5979\u800c \u662f\u90a3\u4e2a\u51f6\u6076\u7684\u571f\u53f8\u3002\u6b7b\u4ea1\u7684\u9b54\u5f71\u5f71\uff0c\u5bf9\u4e8e\u5979\u4e5f\u597d\u4f3c\u6beb\u4e0d\u8db3\u60e7\u3002\u4f46\u6b63\u662f\u7531\u4e8e\u8fd9\u79cd\u6f20\u7136\u7684\u795e\u8272\uff0c \u56ed\u4e2d\u6050\u6015\u53ea\u662f\u9664\u4e86\u571f\u53f8\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u611f\u5230\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u90a3\u571f\u53f8\u54c8\u54c8\u4e00\u7b11\uff0c\u6307\u7740\u5211\u5177\u8bf4\u9053\uff1a\u201c\u628a\u8fd9\u4e2a\u77f3\u5708\u5957\u5728\u72af\u4eba\u5934\u4e0a\uff0c\u7528\u5c0f\u94c1\u9524\u5728\u94c1\u7247\u4e0a\u8f7b\u8f7b \u4e00\u6572\uff0c\u72af\u4eba\u7684\u773c\u775b\u4fbf\u4f1a\u51f8\u4e86\u51fa\u6765\uff0c\u54c8\uff0c\u518d\u7528\u90a3\u4e24\u652f\u5c0f\u7af9\u7ba1\u8f7b\u8f7b\u4e00\u6316\uff0c\u8fd9\u6f02\u4eae\u7684\u72af\u4eba\u5c31\u53d8\u6210\u76f2 \u5973\u5566\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u6b63\u60f3\u559d\u4ee4\u884c\u5211\uff0c\u731b\u542c\u5f97\u9648\u5b9a\u57fa\u53eb\u9053\uff1a\u201c\u7b49\u7b49\uff0c\u8bf7\u7b49\u4e00\u7b49\uff01\u201d\u571f\u53f8\u6115\u7136\u8d77 \u7acb\uff0c\u9762\u5411\u9648\u5b9a\u57fa\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec\u6c49\u4eba\u80c6\u5c0f\uff0c\u4e0d\u6562\u770b\u884c\u5211\u5417\uff1f\u201d \u3000\u3000\u9648\u5b9a\u67d0\u5fcd\u7740\u6012\u6c14\uff0c\u9053\uff1a\u201c\u8bf7\u95ee\u571f\u53f8\uff0c\u4ed6\u4eec\u5077\u4f60\u51e0\u5339\u9a6c\uff1f\u201d\u571f\u53f8\u9053\uff1a\u201c\u4e94\u5339\u6700\u597d\u7684\u767d \u9a6c\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u6211\u66ff\u5979\u8d54\u4f60\u5341\u5339\uff01\u201d\u571f\u53f8\u9053\uff1a\u201c\u5979\u8fd8\u60f3\u70b9\u706b\u70e7\u6211\u7684\u9a6c\u53a9\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a \u201c\u70e7\u4e86\u6ca1\u6709\uff1f\u201d\u571f\u53f8\u9053\uff1a\u201c\u521a\u64e6\u71c3\u706b\u77f3\u5c31\u7ed9\u6211\u4eec\u6349\u4f4f\u4e86\u3002\u201d\u9648\u5b9a\u57fa\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u8eab\u4e0a\u6478\u51fa\u706b \u77f3\uff0c\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u8eab\u4e0a\u4e5f\u5e26\u6709\u8fd9\u4e2a\u4e1c\u897f\uff01\u201d\u571f\u53f8\u54c8\u54c8\u5927\u7b11\uff0c\u77e5\u9053\u9648\u5b9a\u57fa\u7684\u610f\u601d\u662f\u8bf4\uff1a\u65e2\u672a \u7eb5\u706b\uff0c\u53ea\u5e26\u6709\u706b\u77f3\uff0c\u7109\u80fd\u4fbf\u5165\u4eba\u4ee5\u7f6a\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u5e76\u4e0d\u56de\u907f\u571f\u53f8\u7684\u76ee\u5149\uff0c\u77aa\u7740\u571f\u53f8\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u571f\u53f8\u4f60\u662f\u4e0d\u662f\u53ef\u4ee5\u7f51\u5f00\u4e00\u9762\uff01\u201d \u9648\u5929\u5b87\u5c4f\u7740\u547c\u5438\uff0c\u671b\u7740\u571f\u53f8\uff0c\u4e5f\u671b\u7740\u7236\u4eb2\u3002\u8fd9\u970e\u90a3\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u5bf9\u7236\u4eb2\u5145\u6ee1\u656c\u4f69\u4e4b\u60c5\uff0c\u7236\u4eb2\u4e0d \u518d\u8c61\u5e73\u65e5\u90a3\u6837\u754f\u9996\u754f\u5c3e\u4e86\uff0c\u4ed6\u633a\u8170\u76f4\u7acb\uff0c\u5c45\u7136\u4e5f\u50cf\u90a3\u5c11\u5973\u4e00\u6837\uff0c\u9762\u65e0\u60e7\u8272\u3002\u6562\u60c5\u4ed6\u5f53\u5e74\u4fee\u672c \u53c2\u52be\u548c\u5764\u4e4b\u65f6\uff0c\u4e5f\u662f\u8fd9\u526f\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u60c5\u3002\u9648\u5929\u5b87\u5728\u7236\u4eb2\u7684\u6ee1\u5934\u767d\u53d1\u4e2d\u770b\u51fa\u4e86\u7236\u4eb2\u58ee\u5e74 \u7684\u8c6a\u6c14\u3002 \u3000\u3000\u571f\u53f8\u5fae\u5fae\u4e00\u51db\uff0c\u5fc3\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u8fd9\u4e2a\u8870\u5f31\u7684\u6c49\u65cf\u6587\u5b98\u3002\u5c45\u7136\u4e5f\u6709\u8fd9\u526f\u80c6\u8272\u3002\u201d\u7b11\u9053\uff1a \u201c\u672c\u5e03\u66ff\u5979\u6c42\u60c5\uff0c\u672c\u8be5\u5c0a\u7167\u3002\u65e0\u5948\u6211\u4eec\u7956\u5b97\u7684\u6210\u6cd5\uff0c\u5b9e\u662f\u96be\u4ee5\u66f4\u6539\u3002\u201d\u9648\u5929\u5b87\u6697\u6697\u634f\u7740\u85cf\u85cf \u5728\u8896\u4e2d\u7684\u5315\u9996\uff0c\u53ea\u8981\u571f\u53f8\u4e00\u559d\u4ee4\u884c\u5211\uff0c\u5c31\u5148\u628a\u4ed6\u523a\u4e2a\u900f\u660e\u7a9f\u7abf\u3002\u571f\u53f8\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u7956 \u5b97\u7684\u6210\u6cd5\u4e0d\u53ef\u6539\uff0c\u672c\u5e03\u7684\u9762\u5b50\u4e5f\u8be5\u987e\u5168\u3002\u597d\u5427\uff0c\u54b1\u4eec\u4e14\u8d4c\u4e00\u8d4c\u8fd9\u72af\u4eba\u7684\u8fd0\u6c14\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c \u4e00\u5458\u85cf\u5175\u5c06\u4e00\u679a\u91d1\u8272\u7684\u82f9\u679c\u653e\u5728\u5c11\u5973\u5934\u4e0a\uff0c\u571f\u53f8\u53c8\u54c8\u54c8\u5927\u7b11\uff0c\u56de\u987e\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u4f60\u4eec\u7684\u98de\u5200 \u4f7f\u5f97\u5982\u6cb3\uff1f\u201d\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u4e00\u67c4\u89e3\u8155\u5c16\u5200\u63d2\u5728\u684c\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u5200\u98de\u53bb\uff0c\u82e5\u7136\u5c06\u4e00\u679a \u82f9\u679c\u521a\u597d\u4ece\u5f53\u4e2d\u5288\u6210\u4e24\u534a\uff0c\u90a3\u4e48\u9a6c\u4e5f\u4e0d\u7528\u8d54\uff0c\u6211\u7acb\u523b\u51c6\u5979\u8d70\uff0c\u8fd9\u98de\u5200\u5288\u679c\u7684\u529e\u6cd5\uff0c\u4e5f\u662f\u6211\u4eec \u85cf\u65cf\u7684\u89c4\u77e9\u3002\u597d\uff0c\u73b0\u5728\u5e26\u8fd9\u72af\u4eba\u5728\u767e\u6b65\u4e4b\u5916\u7ad9\u597d\uff01\u201d\u85cf\u5175\u6276\u7740\u5973\u72af\uff0c\u8d70\u4e00\u6b65\uff0c\u5ff5\u4e00\u4e2a\u6570\u5b57\uff0c \u5ff5\u5230\u4e00\u767e\uff0c\u505c\u4e86\u4e0b\u6765\uff0c\u90a3\u679a\u91d1\u8272\u7684\u82f9\u679c\u770b\u8d77\u6765\u592a\u5c0f\u4e86\u3002\u571f\u53f8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u51c6\u4f60\u6216\u8005\u4f60\u7684\u968f \u4ece\uff0c\u968f\u4fbf\u6311\u4e00\u4e2a\u4eba\u6765\u98de\u5200\u5288\u679c\u5427\uff01\u201d \u3000\u3000\u9648\u5b9a\u57fa\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u968f\u4ece\u4e2d\u4e5f\u6ca1\u6709\u767e\u6b65\u7a7f\u6768\u7684\u4eba\u624d\uff0c\u571f\u53f8\u51fa\u8fd9\u96be\u9898\uff0c\u5206\u660e\u662f\u60f3\u6709\u610f\u7f9e \u8fb1\u6c49\u4eba\u3002\u9648\u5b9a\u57fa\u52c3\u7136\u6012\u9053\uff1a\u201d\u5c82\u53ef\u5c06\u4eba\u547d\u4f5c\u4e3a\u513f\u620f\uff1f\u201d\u571f\u53f8\u4f5c\u85d0\u89c6\u4e4b\u72b6\uff0c\u5472\u7259\u4e00\u7b11\uff0c\u9053\uff1a\u201c \u65e2\u7136\u4eec\u4e0d\u6562\u66ff\u5979\u8d4c\u8fd9\u8fd0\u6c14\u3002\u90a3\u4e48\u54b1\u4eec\u8fd8\u662f\u65e9\u65e9\u884c\u5211\uff01\u201d\u9648\u5929\u5b87\u53cc\u76ee\u70af\u70af\u653e\u5149\uff0c\u84e6\u7136\u8d77\u7acb\uff0c\u95ee \u9053\uff1a\u201c\u8981\u662f\u6211\u4e00\u5200\u5c06\u8fd9\u82f9\u679c\u5288\u4e3a\u4e24\u534a\u2026\u2026\u201d\u571f\u53f8\u622a\u7740\u9053\uff1a\u201c\u6211\u5c31\u7acb\u523b\u628a\u5979\u653e\u8d70\uff01\u201d\u9648\u5929\u5b87 \u9053\uff1a\u201c\u4e00\u8a00\u4e3a\u5b9a\uff01\u201d\u571f\u53f8\u9053\uff1a\u201c\u5c82\u6709\u865a\u8a00\uff1f\u201d\u9648\u5b9a\u57fa\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5b87\u513f\uff0c\u4f60\u505a\u4ec0 \u4e48\uff1f\u201d\u8bdd\u58f0\u6765\u4e86\uff0c\u53ea\u89c1\u9648\u5929\u5b87\u6293\u8d77\u5c16\u5200\uff0c\u95ea\u7535\u822c\u7684\u7529\u624b\u4e00\u63b7\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u5c11\u5973\u5934 \u4e0a\u91d1\u8272\u7684\u82f9\u679c\u5206\u6210\u4e24\u534a\uff0c\u98de\u5728\u534a\u7a7a\u3002\u85cf\u5175\u63a5\u5728\u624b\u4e2d\uff0c\u53eb\u9053\uff1a\u201d\u521a\u597d\u5728\u5f53\u4e2d\u5206\u5f00\uff0c\u4e24\u8fb9\u4e00\u822c\u5927 \u5c0f\uff01\u201d\u4e0a\u53f8\u9762\u8272\u500f\u53d8\uff0c\u968f\u5373\u5927\u7b11\uff0c\u7fd8\u8d77\u62c7\u6307\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u98de\u5200\u7edd\u6280\u5440\uff01\u201d \u3000\u3000\u9648\u5b9a\u57fa\u5140\u5982\u8eab\u7f6e\u68a6\u4e2d\uff0c\u5fc3\u4e2d\u60ca\u5947\u4e4b\u6781\uff0c\u201c\u513f\u5b50\u4ece\u6765\u6ca1\u6709\u4e60\u6280\uff0c\u5341\u516b\u5e74\u7236\u5b50\u76f8\u4f9d\uff0c\u7adf\u7136\u4e0d \u77e5\u9053\u4ed6\u6709\u8fd9\u6837\u7684\u672c\u9886\u3002\u85cf\u5175\u66ff\u90a3\u5c11\u5973\u89e3\u5f00\u4e86\u7f1a\u5728\u8eab\u4e0a\u7684\u725b\u7b4b\u7d22\uff0c\u90a3\u5c11\u5973\u77a5\u4e86\u9648\u5929\u5b87\u4e00\u773c\uff0c\u4fbf \u4ece\u4e24\u884c\u6392\u5217\u7740\u7684\u5200\u5251\u4e1b\u4e2d\u8d70\u51fa\u53bb\uff0c\u4ecd\u7136\u662f\u90a3\u526f\u6f20\u7136\u7684\u795e\u8272\uff0c\u4ecd\u7136\u662f\u90a3\u526f\u4ee4\u4eba\u5fc3\u5e95\u53d1\u5bd2\u7684\u3001\u51b7 \u68ee\u68ee\u7684\u76ee\u5149\u3002\u5979\u4e0d\u53d1\u4e00\u8a00\u4fbf\u8d70\u51fa\u53bb\u4e86\uff0c\u5e76\u6ca1\u6709\u5411\u9648\u5929\u5b87\u9053\u8c22\u3002 \u3000\u3000\u571f\u53f8\u6447\u6447\u5934\u9053\uff1a\u201c\u5567\uff0c\u8fd9\u6837\u6f02\u4eae\u7684\u5973\u72af\u4eba\uff0c\u771f\u662f\u4fbf\u5b9c\u5979\u4e86\u3002\u201d\u8c61\u662f\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\uff0c\u6c14\u7130 \u8fd9\u624d\u51cf\u4e86\u8bb8\u591a\u3002\u5bbe\u4e3b\u5750\u5b9a\uff0c\u9648\u5b9a\u57fa\u57fa\u6b63\u5f85\u5411\u571f\u53f8\u656c\u9152\uff0c\u571f\u53f8\u53c8\u77a7\u4e86\u9648\u5929\u5b87\u4e00\u773c\uff0c\u5ffd\u53c8\u5174\u9ad8\u91c7 \u70c8\u5730\u5429\u5490\u5f85\u4ece\u9053\uff1a\u201c\u8bf7\u6c5f\u739b\u53e4\u4fee\u51fa\u6765\u3002\u201d \u3000\u3000\u6c5f\u739b\u53e4\u4fee\u4e43\u662f\u85cf\u8bed\u4e2d\u7684\u5c0f\u59d0\u4e4b\u610f\uff0c\u9648\u5b9a\u57fa\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u53eb\u5973\u513f\u51fa\u6765\u966a\u5ba2\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u8fd9\u65f6\u624d\u89c9\u5f97\u624b\u6307\u53d1\u6296\uff0c\u60f3\u8d77\u521a\u624d\u90a3\u98de\u5200\u4e00\u63b7\uff0c\u5b9e\u662f\u5371\u9669\u4e4b\u6781\uff0c\u8fd9\u8fd8\u662f\u4ed6\u7b2c\u4e00\u6b21\u5728 \u4eba\u524d\u6296\u9732\u672c\u9886\uff0c\u60f3\u4e0d\u5230\u4e00\u4e3e\u594f\u529f\u3002\u201c\u90a3\u5c11\u5973\u662f\u4ec0\u4e48\u4eba\uff1f\u5979\u771f\u662f\u5077\u9a6c\u8d3c\u5417\uff1f\u5979\u61c2\u4e0d\u61c2\u6b66\u529f\uff1f\u4e3a \u4ec0\u4e48\u5979\u7684\u8138\u4e0a\u8001\u662f\u6302\u7740\u90a3\u4ed8\u5947\u7279\u7684\u795e\u8272\uff1f\u201d\u9648\u5929\u5b87\u5c3d\u5728\u60f3\u90a3\u795e\u79d8\u5c11\u5973\u7684\u4e8b\u60c5\uff0c\u4ee5\u81f3\u4e8e\u5e76\u4e0d\u77e5 \u9053\u571f\u53f8\u53eb\u4ed6\u7684\u5973\u513f\u51fa\u6765\u966a\u5ba2\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u9635\u73af\u4f69\u53ee\u5f53\u4e4b\u58f0\uff0c\u4e00\u4e2a\u85cf\u65cf\u5c11\u5973\uff0c\u6234\u7740\u6ee1\u8eab\u9970\u7269\uff0c\u7a7f\u7740\u4e00\u4ef6\u6e56\u6c34\u8272\u7684\u957f\u888d\uff0c\u4e0a \u8eab\u62ab\u4e86\u4ef6\u84dd\u7ed2\u8863\uff0c\u8170\u95f4\u8fd8\u7f20\u4e86\u4e00\u7f15\u8f7b\u7eb1\uff0c\u6253\u626e\u5f97\u534e\u8d35\u6781\u4e86\uff0c\u50cf\u76db\u5f00\u7684\u590f\u65e5\u73ab\u7470\uff0c\u53ef\u4e0d\u77e5\u600e \u7684\uff0c\u5374\u603b\u662f\u4ee4\u4eba\u89c9\u5f97\u6709\u4e00\u80a1\u5eb8\u4fd7\u7684\u5473\u9053\u3002 \u3000\u3000\u571f\u53f8\u7684\u5973\u513f\u8138\u4e0a\u5806\u7740\u7b11\u5bb9\uff0c\u8170\u80a2\u8f6f\u6446\uff0c\u4e00\u6b65\u6b65\u7684\u671d\u7740\u9648\u5929\u5b87\u8d70\u6765\uff0c\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u90a3 \u4e0a\u53f8\u7684\u5973\u513f\u8d70\u5230\u9648\u5929\u5b87\u9762\u524d\uff0c\u8170\u80a2\u4e00\u5f2f\uff0c\u563b\u563b\u4e00\u7b11\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u7684\u978b\u5e26\u677e\u5566\uff01\u201d\u53cc\u624b\u6478\u7740\u4ed6 \u7684\u725b\u76ae\u7edf\u978b\uff0c\u5c31\u66ff\u4ed6\u7ed3\u978b\u5e26\u3002\u8fd9\u4e3e\u52a8\u5927\u51fa\u9648\u5929\u5b87\u610f\u5916\uff0c\u7adf\u5f04\u4e0d\u6e05\u695a\u5979\u4ec0\u4e48\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u8be5 \u600e\u4e48\u505a\u624d\u597d\u3002\u90a3\u571f\u53f8\u7684\u5973\u513f\u66ff\u9648\u5929\u5b87\u7ed3\u597d\u978b\u5e26\u7b11\u563b\u563b\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u8138\u4e0a\u73b0\u51fa\u4e86\u4e00\u5708\u7ea2\u6655\uff0c\u5ff8 \u6029\u4f5c\u6001\uff0c\u628a\u5934\u522b\u8fc7\u4e00\u8fb9\uff0c\u907f\u5f00\u548c\u9648\u5929\u5b87\u7684\u76ee\u5149\u76f8\u78b0\uff0c\u9648\u5929\u5b87\u6014\u4e86\u4e00\u5f81\uff0c\u53ea\u89c1\u7236\u4eb2\u8138\u4e0a\u9732\u51fa\u4e86 \u4e00\u79cd\u5947\u7279\u7684\u8868\u60c5\uff0c\u8c61\u662f\u975e\u5e38\u7126\u6025\uff0c\u53c8\u8c61\u662f\u6709\u4e9b\u6b22\u559c\uff0c\u90a3\u571f\u53f8\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff0c\u201c\u5e72\u676f\uff0c\u4ece\u6b64 \u54b1\u4eec\u662f\u4e00\u5bb6\u4eba\u5566\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u731b\u7136\u4e00\u9192\uff0c\u4e0d\u89c9\u5927\u60ca\u5931\u8272\uff0c\u539f\u6765\u662f\u897f\u85cf\u7684\u98ce\u4fd7\uff0c\u5c11\u5973\u66ff\u7537\u5b50\u7ed9\u978b\u5e26\uff0c\u5c31\u662f\u8868\u793a\u6c42 \u5a5a\u7684\u610f\u601d\uff0c\u82e5\u7136\u90a3\u7537\u5b50\u4e0d\u52a0\u62d2\u7edd\uff0c\u8fd9\u4eb2\u4e8b\u5c31\u7b97\u7ed3\u6210\u4e86\u3002\u539f\u6765\u8fd9\u4e0a\u53f8\u7684\u5973\u513f\uff0c\u5e73\u65e5\u559c\u6b22\u5728\u8349\u539f \u4e0a\u9a91\u9a6c\u5c04\u7bad\uff0c\u89c1\u8fc7\u9648\u5929\u5b87\u51e0\u9762\uff0c\u9648\u5929\u5b87\u53ef\u6ca1\u7559\u610f\u5979\u3002\u571f\u53f8\u7684\u5973\u513f\u957f\u5927\u4e86\uff0c\u5e94\u8be5\u662f\u7ed3\u5a5a\u7684\u65f6\u5019 \u4e86\uff0c\u53ef\u662f\u5468\u56f4\u6ca1\u6709\u9002\u5408\u7684\u7537\u5b50\u3002\u571f\u53f8\u7684\u5973\u513f\u65e9\u5c31\u7231\u4e0a\u4e86\u9648\u5929\u5b87\u7684\u82f1\u4fca\uff0c\u6240\u4ee5\u8fd9\u6b21\u571f\u53f8\u4e4b\u5bb4\uff0c \u5176\u5b9e\u5c31\u662f\u5b9a\u4eb2\u4e4b\u5bb4\u3002 \u3000\u3000\u571f\u53f8\u4e3e\u8d77\u4e86\u4e00\u652f\u9ad8\u811a\u9152\u676f\uff0c\u5bf9\u9648\u5b9a\u57fa\u9053\uff0c\u201c\u8fd9\u5934\u4eb2\u4e8b\u6211\u6ee1\u610f\u6781\u5566\uff0c\u4eb2\u5bb6\uff0c\u54b1\u4eec\u5e72\u4e86\u6b64 \u676f\uff01\u201d\u9648\u5b9a\u57fa\u6413\u7740\u53cc\u624b\u4e0d\u77e5\u6240\u63aa\u3002\u9648\u5929\u5b87\u5ffd\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u6ee1\u610f\uff01\u201d\u571f\u53f8\u52c3\u7136\u4f5c\u8272\uff0c\u559d \u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u6211\u571f\u53f8\u7684\u5973\u513f\uff0c\u4f60\u4e0d\u6ee1\u610f\uff01\u201d\u571f\u53f8\u7684\u5973\u513f\u5624\u7136\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u6025\u9053\uff1a\u201c\u5c0f\u513f\u5e74\u5e7c\u65e0\u77e5\uff0c\u5364\u83bd\u5931\u4f53\uff0c\u571f\u53f8\u4f11\u602a\u3002\u201d\u571f\u53f8\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u624d\u8c61 \u53e5\u8bdd\uff0c\u5c0f\u4f19\u5b50\uff0c\u5feb\u4e0e\u4f60\u672a\u5a5a\u59bb\u5b50\u5e72\u4e86\u6b64\u676f\u3002\u201d\u571f\u53f8\u7684\u5973\u513f\u7834\u6d95\u4e3a\u7b11\uff0c\u5c06\u659f\u6ee1\u9152\u7684\u9152\u676f\u9012\u5230\u9648 \u5929\u5b87\u9762\u524d\uff0c\u9648\u5929\u5b87\u624b\u8db3\u65e0\u63aa\u3002\u82b1\u56ed\u5916\u4e00\u7247\u55b3\u54d7\uff0c\u5ffd\u7136\u4e00\u4eba\u62ab\u5934\u6563\u53d1\uff0c\u51b2\u4e86\u8fdb\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u201c\u4e0d\u597d\u4e86\uff0c\u9648\u5927\u4eba\uff0c\u7978\u4e8b\uff01\u7978\u4e8b\uff01\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u6709\u8bdd\u6162\u8bf4\uff0c\u4ec0\u4e48\u7978\u4e8b\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u8859\u95e8 \u88ab\u5f3a\u76d7\u653e\u706b\u70e7\u4e86\uff0c\u6b7b\u4f24\u4e86\u8bb8\u591a\u8bb8\u591a\u4eba\u3002\u201d\u4ed3\u7405\u4e00\u58f0\uff0c\u9648\u5b9a\u57fa\u9152\u676f\u843d\u5730\uff0c\u53ea\u89c1\u9648\u5929\u5b87\u5df1\u50cf\u65cb\u98ce \u4e00\u822c\u6251\u4e0b\u4ead\u5b50\uff0c\u62a2\u4e86\u4e00\u5339\u5feb\u9a6c\uff0c\u5982\u98de\u51fa\u95e8\u3002 \u3000\u3000\u571f\u53f8\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u5f3a\u76d7\uff0c\u4e5f\u503c\u5f97\u5927\u60ca\u5c0f\u602a\uff0c\u6c6a\u5408\u6d85\u5df4\uff0c\u66ff\u6211\u70b9\u4e00\u767e\u540d\u5175\u5352\u524d\u5f80\uff0c\u628a\u5f3a \u76d7\u90fd\u6349\u56de\u6765\uff0c\u54c8\uff0c\u4eb2\u5bb6\u672c\u5e03\uff0c\u4f60\u6709\u4e86\u6211\u8fd9\u4e2a\u9760\u5c71\uff0c\u4ec0\u4e48\u90fd\u4e0d\u7528\u5bb3\u6015\uff01\u201d\u9648\u5b9a\u57fa\u5fc3\u6025\u5982\u711a\uff0c\u597d \u5bb9\u6613\u7b49\u571f\u53f8\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4e5f\u6025\u5fd9\u5954\u4e0b\u4ead\u5b50\uff0c\u8de8\u4e0a\u5750\u9a91\uff0c\u6025\u6025\u5e26\u62a4\u536b\u5954\u56de\u3002\u80cc\u540e\u571f\u53f8\u4ecd\u5728\u54c8\u54c8\u5927 \u7b11\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4eb2\u5bb6\u672c\u5e03\uff0c\u8fd9\u91cc\u9152\u5e2d\u672a\u6563\uff0c\u6349\u4e86\u5f3a\u76d7\uff0c\u7acb\u523b\u5e26\u4f60\u7684\u513f\u5b50\u56de\u6765\uff01\u201d \u3000\u3000\u4e14\u8bf4\u662f\u9648\u5929\u5b87\u75be\u9a6c\u5954\u56de\uff0c\u672a\u5230\u5ba3\u6170\u4f7f\u8859\u95e8\uff0c\u5df2\u89c1\u4e00\u7247\u706b\u5149\uff0c\u5e78\u559c\u5929\u8272\u751a\u597d\uff0c\u5e76\u4e0d\u522e\u98ce\uff0c \u706b\u52bf\u5c1a\u672a\u5927\u76db\uff0c\u9648\u5929\u5b87\u6025\u6025\u4e0b\u9a6c\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u547b\u541f\u4e4b\u58f0\uff0c\u5f3a\u76d7\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u8131\u4e0b\u5927\u8863\uff0c\u906e\u5934\u6325\u821e\uff0c\u907f\u5f00\u706b\u820c\uff0c\u5954\u4eba\u8859\u4e2d\uff0c\u53ea\u89c1\u5c38\u6a2a\u904d\u5730\uff0c\u518d\u5b9a\u775b\u770b\u65f6\uff0c\u5730\u4e0a \u5e76\u65e0\u8840\u6d41\uff0c\u7adf\u50cf\u662f\u7ed9\u4eba\u7528\u91cd\u624b\u6cd5\u9707\u6b7b\u4eba\uff0c\u6709\u4e9b\u672a\u6b7b\u7684\uff0c\u5728\u5730\u4e0b\u8f97\u8f6c\u547b\u541f\uff0c\u60e8\u4e0d\u5fcd\u7779\uff0c\u9648\u5929\u5b87 \u5927\u4e3a\u5403\u60ca\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8427\u5148\u4e3b\uff0c\u8427\u5148\u751f\uff01\u201d\u4e71\u5c38\u5806\u4e2d\u5ffd\u542c\u5f97\u6709\u4eba\u5e94\u9053\uff1a\u201c\u8427\u5148\u751f\u548c\u5f3a\u76d7\u90fd \u8d70\u5566\uff01\u201d\u9648\u5929\u5b87\u6025\u6025\u4ece\u5c38\u5806\u4e2d\u5c06\u8bf4\u8bdd\u90a3\u4eba\u6293\u51fa\uff0c\u6b63\u662f\u6c5f\u5357\uff0c\u9648\u5929\u5b87\u9053\uff1a\u201c\u5440\uff0c\u8c22\u8c22\u5929\uff0c\u4f60\u8fd8 \u672a\u6b7b\u3002\u201d\u6c5f\u5357\u5410\u5410\u820c\u5934\uff1a\u201c\u90a3\u4e24\u4e2a\u5f3a\u76d7\u4e5f\u4ee5\u4e3a\u6211\u6b7b\u6b7b\u4e86\uff0c\u54c8\uff0c\u5176\u5b9e\u6211\u662f\u88c5\u6b7b\u9a97\u8fc7\u4ed6\u95e8\uff0c\u82e5\u4e0d \u662f\u8bc8\u6b7b\uff0c\u6211\u5c31\u4e0d\u80fd\u751f\u5566\uff01\u201d\u5728\u9669\u6b7b\u8fd8\u751f\u7684\u5371\u96be\u4e4b\u4e2d\uff0c\u6c5f\u5357\u591a\u5634\u7684\u813e\u6c14\u4ecd\u662f\u672a\u6539\u3002\u9648\u5929\u5b87\u6025\u5fd9 \u628a\u4ed6\u62d6\u51fa\u8859\u95e8\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u73b0\u5728\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u4e86\u4e0d\u4e45\uff0c\u90a3\u4e24\u4e2a\u5f3a\u76d7\u5c31\u6765\u5566\uff01\u5c31\u662f\u90a3\u4e24\u4e2a\u5356\u5531\u7684\u6c49\u4eba\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5c31 \u662f\u6628\u5929\u7528\u7bad\u5c04\u4f60\u7684\uff0c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u8bb0\u5f97\uff01\u4f60\u8bc0\u8bf4\u4e0b\u53bb\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u90a3\u4e24 \u4e2a\u5f3a\u76d7\uff0c\u4e00\u4e2a\u62ff\u7740\u4f1a\u55b7\u706b\u7684\u7b52\u5b50\uff0c\u706b\u5149\u5c04\u5230\u90a3\u91cc\uff0c\u90a3\u91cc\u5c31\u70e7\u8d77\u6765\uff0c\u5c11\u7237\uff0c\u4f60\u89c1\u8fc7\u8fd9\u79cd\u602a\u4e1c\u897f \u5417\uff1f\u201d\u9648\u5929\u5b87\u6025\u9053\uff1a\u201c\u672a\u89c1\u8fc7\u3001\u5feb\u8bf4\u4e0b\u53bb\uff0c\u4e0d\u8981\u591a\u8bf4\u95f2\u8bdd\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u53e6\u4e00\u4e2a\u5f3a\u76d7\u63d0\u7740\u4e00 \u628a\u5927\u5f13\uff0c\u5feb\u6781\u5566\uff0c\u4e00\u78b0\u5230\u54b1\u4eec\u62a4\u536b\u7684\u5175\u58eb\uff0c\u5c31\u662f\u90a3\u4e48\u8fce\u5934\u4e00\u4e0b\uff0c\u53ea\u662f\u90a3\u4e48\u4e00\u4e0b\uff0c\u5175\u58eb\u4eec\u5c31\u54fc \u4e5f\u4e0d\u54fc\u8eba\u4e0b\u4e86\uff0c\u6211\u4e0d\u7b49\u4ed6\u6253\u6211\uff0c\u5c31\u5148\u8eba\u4e0b\u53bb\u4f6f\u6b7b\u3002\u5475\uff0c\u8fd9\u65f6\u5019\u8427\u5148\u751f\u51fa\u6765\u4e86\uff0c\u6211\u8eba\u5728\u5730\u4e0a\u5077 \u5077\u770b\u4ed6\uff0c\u53ef\u5168\u4e0d\u50cf\u5e73\u65e5\u7684\u6837\u5b50\uff0c\u8170\u677f\u4e5f\u633a\u771f\u5566\uff0c\u9f13\u7740\u4e00\u53cc\u773c\u775b\uff0c\u53c8\u5927\u53c8\u5706\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8427 \u67d0\u5728\u6b64\uff0c\u4e0e\u8fd9\u91cc\u7684\u4e3b\u4eba\u65e0\u5173\uff0c\u54b1\u4eec\u5230\u540e\u5c71\u53bb\u4e00\u51b3\u6b7b\u751f\uff0c\u4eca\u65e5\u603b\u80fd\u5982\u4f60\u4eec\u6240\u613f\uff0c\u4e86\u8fd9\u5341\u5e74\u516c \u6848\uff01\u201d \u3000\u3000\u540e\u9762\u50f5\u5934\u5927\u8d77\uff0c\u9a6c\u58f0\u5636\u9e23\uff0c\u9648\u5b9a\u57fa\u7684\u536b\u58eb\u548c\u571f\u53f8\u7684\u5175\u5168\u8d76\u6765\u4e86\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u5230\u540e\u5c71 \u53bb\u627e\u5148\u751f\u3001\u53ea\u51c6\u4f60\u8bf4\u7ed9\u8001\u7237\u4e00\u4e2a\u4eba\u77e5\u9053\uff01\u201d\u7acb\u523b\u4e0a\u9a6c\uff0c\u9a70\u5165\u540e\u9762\u5c71\u8c37\u3002 \u3000\u3000\u5c71\u8c37\u9669\u5cfb\uff0c\u575a\u51b0\u79ef\u96ea\uff0c\u602a\u77f3\u9cde\u5ccb\uff0c\u9a6c\u4e5f\u96be\u884c\uff0c\u9648\u5929\u5b87\u5f03\u9a6c\u767b\u5c71\uff0c\u8f6c\u8fc7\u4e24\u8fb9\u5c71\u6c9f\uff0c\u5ffd\u542c\u5f97 \u4e00\u9635\u53ee\u5b9a\u5f53\u5f53\u4e4b\u58f0\uff0c\u5047\u5982\u594f\u4e50\uff0c\u4f46\u90a3\u4e50\u58f0\u6742\u4e71\u6beb\u65e0\u7ae0\u6cd5\uff0c\u6025\u4fc3\u5c16\u9510\uff0c\u4ee4\u4eba\u542c\u6765\u610f\u4e71\u5fc3\u70e6\u3002\u9648 \u5929\u5b87\u767b\u9ad8\u4e0b\u671b\uff0c\u53ea\u89c1\u8427\u5148\u751f\u6325\u7740\u4e00\u67c4\u62c2\u5c18\uff0c\u5728\u4e24\u4e2a\u654c\u4eba\u56f4\u653b\u4e4b\u4e0b\u7a9c\u6765\u7a9c\u53bb\uff0c\u90a3\u4e24\u4e2a\u654c\u4eba\u4e00\u4e2a \u63d0\u7740\u628a\u5927\u5f13\uff0c\u62c2\u5c18\u62c2\u5728\u5f13\u5f26\u4e4b\u4e0a\uff0c\u5c31\u662f\u4e00\u9635\u53ee\u5f53\u4f5c\u54cd\uff0c\u53e6\u4e00\u4e2a\u654c\u4eba\u624b\u4f7f\u4e03\u8282\u8f6f\u97ad\uff0c\u77e2\u77eb\u5982 \u9f99\uff0c\u770b\u6837\u5b50\u662f\u60f3\u593a\u53d6\u8427\u5148\u751f\u624b\u4e2d\u7684\u62c2\u5c18\uff0c\u4f46\u90a3\u62c2\u5c18\u5728\u97ad\u5f71\u4e4b\u4e2d\u6325\u821e\u81ea\u5982\uff0c\u4ecd\u7136\u662f\u4e0d\u65ad\u5730\u62c2\u5728 \u5f13\u5f26\u4e4b\u4e0a\u3002 \u3000\u3000\u9648\u5929\u5b87\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff01\u201d\u53ea\u542c\u5f97\u4e00\u9635\u4e01\u51ac\u58f0\u54cd\uff0c\u8427\u9752\u5cf0\u626c\u58f0\u8bf4\u9053\uff0c\u201c\u5b87\u513f\uff0c\u4e0d\u8981\u4e0b \u6765\uff01\u201d\u58f0\u97f3\u6025\u4fc3\uff0c\u4f3c\u662f\u663e\u5f97\u6709\u4e9b\u6c14\u5598\uff0c\u9648\u5929\u5b87\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u867d\u7136\u5bf9\u4e8e\u5185\u529f\u53ea\u662f\u6697\u7aa5\u95e8 \u5f84\uff0c\u4f46\u542c\u8fd9\u58f0\u97f3\uff0c\u5df2\u77e5\u5e08\u5085\u7684\u5185\u5bb6\u771f\u6c14\uff0c\u9887\u53d7\u635f\u4f24\u3002 \u3000\u3000\u539f\u6765\u8427\u9752\u5cf0\u4e43\u662f\u4e00\u4f4d\u9690\u540d\u5927\u4fa0\uff0c\u5177\u6709\u7edd\u9876\u6b66\u529f\uff0c\u9648\u5929\u5b87\u7684\u529f\u592b\u5c31\u662f\u4ed6\u6240\u4f20\u6388\u3002\u4ed6\u66fe\u4e00\u518d \u7684\u544a\u8beb\u9648\u5929\u5b87\u4e0d\u51c6\u6cc4\u6f0f\uff0c\u8bf4\u662f\u82e5\u4e00\u6cc4\u6f0f\uff0c\u5c31\u6050\u6709\u751f\u547d\u4e4b\u9669\uff0c\u6545\u6b64\u9648\u5929\u5b87\u535c\u65e5\u95f4\u4e60\u6587\uff0c\u665a\u4e0a\u4e60 \u6b66\uff0c\u5c31\u8fde\u9648\u5b9a\u57fa\u4e5f\u4e0d\u77e5\u9053\u3002\u9648\u5929\u5b87\u662f\u5728\u5e08\u5085\u6765\u7684\u7b2c\u4e8c\u5e74\u8ddf\u4ed6\u4e60\u6b66\u7684\uff0c\u524d\u540e\u4e03\u5e74\uff0c\u53ea\u77e5\u5e08\u5085\u662f \u9752\u57ce\u6d3e\u7684\u9ad8\u624b\uff0c\u81f3\u4e8e\u5e08\u5085\u7684\u8eab\u4e16\uff0c\u4ee5\u53ca\u4ed6\u4e3a\u4ec0\u4e48\u8981\u79bb\u5f00\u4e2d\u539f\uff0c\u968f\u81ea\u5df1\u4e00\u5bb6\u8fdc\u8d74\u85cf\u8fb9\uff0c\u7b49\u7b49\u60c5 \u7531\uff0c\u5e08\u5085\u90fd\u4e0d\u80af\u8bf4\uff0c\u4e5f\u4e0d\u51c6\u591a\u95ee\u3002\u53ea\u8bf4\u5e08\u5085\u9047\u5408\uff0c\u4e43\u662f\u7f18\u6cd5\uff0c\u82e5\u7136\u6211\u8eab\u4e16\u6cc4\u9732\uff0c\u8fd9\u7f18\u6cd5\u4e5f\u5c31 \u5c3d\u5566\u3002\u9648\u5929\u5b87\u4e3a\u4eba\u8bda\u6734\uff0c\u5bf9\u5e08\u5085\u656c\u7231\u4e4b\u6781\uff0c\u95ee\u8fc7\u4e00\u6b21\u4e4b\u540e\u5c31\u4e0d\u6562\u518d\u95ee\u3002 \u3000\u3000\u8fd9\u65f6\u51b0\u539f\u4e0a\u640f\u6597\u66f4\u70c8\uff0c\u4e09\u4e2a\u4eba\u8dd1\u9a6c\u706f\u4f3c\u7684\u98ce\u8f66\u65cb\u8f6c\uff0c\u811a\u5e95\u7684\u51b0\u51b3\u4e0d\u65f6\u53d1\u51fa\u788e\u88c2\u7684\u58f0\u54cd\uff0c \u82e5\u662f\u5e38\u4eba\uff0c\u7ad9\u7740\u884c\u8d70\u4e5f\u6050\u6709\u8dcc\u5012\u4e4b\u865e\uff0c\u66f4\u4e0d\u8981\u8bf4\u640f\u6597\u4e86\u3002\u9648\u5929\u5b87\u770b\u5f97\u5fc3\u513f\u535c\u535c\u4e71\u8df3\uff0c\u5fc3\u9053\uff1a \u201c\u8fd9\u4e00\u6b21\u6211\u62fc\u7740\u53d7\u5e08\u5085\u8d23\u602a\uff0c\u4e5f\u4e0d\u80fd\u542c\u4ed6\u7684\u8bdd\u4e86\u3002\u201d\u63d0\u4e86\u53e3\u6c14\uff0c\u8d70\u4e0b\u5c71\u5761\uff0c\u4ed6\u867d\u7136\u77e5\u9053\u8fd9\u4e24 \u4e2a\u4eba\u90fd\u662f\u5f3a\u654c\uff0c\u81ea\u5df1\u4e0b\u53bb\u4e5f\u53ea\u662f\u9001\u6b7b\uff0c\u4f46\u5374\u600e\u5fcd\u89c1\u5e08\u5085\u53d7\u56f4\u653b\u800c\u4e0d\u6551\uff1f \u3000\u3000\u731b\u7136\u95f4\uff0c\u5ffd\u89c1\u5e08\u5085\u8eab\u5f62\u4e00\u6643\uff0c\u63a5\u7740\u4e00\u58f0\u54d7\u5566\u7684\u51b0\u5757\u584c\u88c2\u4e4b\u58f0\uff0c\u5e08\u5085\u4f3c\u662f\u811a\u5e95\u4e00\u6ed1\uff0c\u8eab\u5411 \u524d\u503e\uff0c\u90a3\u4f7f\u97ad\u7684\u654c\u4eba\u970d\u7684\u4e00\u97ad\uff0c\u75be\u5982\u7535\u95ea\uff0c\u731b\u4e0b\u7edd\u62db\uff0c\u62e6\u8170\u4fbf\u626b\uff0c\u9648\u5929\u5b87\u9a87\u53eb\u4e4b\u58f0\u5c1a\u672a\u51fa\u5df2 \u6597\u89c1\u4e00\u6761\u9ed1\u5f71\u817e\u7a7a\u98de\u8d77\uff0c\u63a5\u7740\u662f\u4e00\u58f0\u51c4\u5389\u7684\u5c16\u53eb\uff0c\u53e6\u4e00\u4e2a\u4eba\u968f\u7740\u51b0\u5757\u6eda\u4e0b\u51b0\u8c37\uff0c\u90a3\u4f7f\u5f13\u7684\u6012 \u543c\u4e00\u58f0\uff0c\u5f13\u5f26\u75be\u5f39\uff0c\u53c8\u662f\u4e00\u9635\u53ee\u549a\u5bc6\u54cd\uff0c\u539f\u6765\u90a3\u6761\u817e\u7a7a\u98de\u8d77\u7684\u9ed1\u5f71\u4e43\u662f\u8427\u9752\u5cf0\uff0c\u4ed6\u6545\u610f\u5356\u4e86 \u4e00\u4e2a\u7834\u7efd\uff0c\u4e58\u7740\u90a3\u4f7f\u97ad\u7684\u6c49\u5b50\u8f7b\u8fdb\u4e4b\u9645\uff0c\u4e00\u4e2a\u201c\u7a9d\u5fc3\u811a\u201d\u5c06\u4ed6\u8e22\u4e0b\u51b0\u6e0a\u3002\u9648\u5929\u5b87\u5413\u51fa\u4e00\u8eab\u51b7 \u6c57\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u6025\u4fc3\u7684\u5f13\u5f26\u7684\u602a\u54cd\uff0c\u5e08\u5085\u7684\u62c2\u5c18\u98de\u6563\uff0c\u4e00\u7bf7\u8f7b\u67d4\u82e5\u4e1d\u7684\u5c18\u5c3e\uff0c\u7adf\u4f3c\u662f\u7ed9 \u654c\u4eba\u5f13\u5f26\u62c9\u65ad\uff0c\u4e71\u8349\u4e00\u822c\u7684\u98d8\u821e\u7a7a\u4e2d\uff01\u987b\u77e5\u8427\u9752\u5cf0\u8fd9\u652f\u62c2\u5c18\uff0c\u770b\u6765\u4f3c\u662f\u9a6c\u864e\uff0c\u5374\u662f\u4e4c\u91d1\u7cbe\u7ec3 \u7684\uff0c\u575a\u97e7\u4e4b\u6781\uff0c\u7b97\u5f97\u662f\u6b66\u6797\u4e00\u4ef6\u5947\u5b9d\uff0c\u800c\u4eca\u7adf\u88ab\u654c\u4eba\u7684\u5f13\u62c9\u65ad\uff0c\u8fd9\u4eba\u7684\u5185\u529f\uff0c\u5b9e\u5df2\u70bc\u5230\u4e86 \u201c\u6458\u53f6\u98de\u82b1\uff0c\u4f24\u4eba\u7acb\u6b7b\u201d\u7684\u901a\u7384\u5999\u5883\uff0c\u9648\u5929\u5b87\u89c1\u4e86\uff0c\u4e5f\u4e0d\u7981\u9a87\u7136\u5931\u8272\u3002\u6b63\u81ea\u98de\u5954\u800c\u4e0b\u3002\u9661\u7136 \u95f4\uff0c\u731b\u542c\u5f97\u53c8\u662f\u4e00\u9635\u53ee\u53ee\u7684\u7e41\u97f3\u5bc6\u54cd\uff0c\u63a5\u7740\u6025\u4fc3\u4e00\u58f0\uff0c\u58f0\u5982\u88c2\u5e1b\uff0c\u8bf8\u58f0\u4ff1\u5bc2\uff0c\u53ea\u89c1\u4e24\u4eba\u8eab \u5f71\uff0c\u970d\u7684\u5206\u5f00\uff0c\u8dcc\u5750\u5730\u4e0a\uff0c\u4e00\u4e2a\u865a\u4e3e\u62c2\u5c18\uff0c\u4f5c\u52bf\u9065\u51fb\uff0c\u4e00\u4e2a\u624b\u5f39\u5f13\u54cd\uff0c\u5f13\u5f26\u5374\u5df2\u54d1\u7136\u65e0\u58f0\u3002 \u9648\u5929\u5b87\u770b\u5f97\u83ab\u660e\u5176\u5999\u3002 \u3000\u3000\u8fd9\u65f6\u9648\u5929\u5b87\u5df2\u5954\u4e0b\u51b0\u539f\uff0c\u8ddd\u79bb\u4e8c\u4eba\u53ea\u6709\u767e\u6765\u6b65\u4e86\uff0c\u4ed4\u7ec6\u770b\u65f6\uff0c\u4f46\u89c1\u5e08\u5085\u8dcc\u5750\u5bd2\u51b0\u4e4b\u4e0a\uff0c \u5934\u4e0a\u7adf\u7136\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002\u90a3\u654c\u4eba\u4e5f\u662f\u4e00\u6837\uff0c\u4e24\u4eba\u5bf9\u9762\u8dcc\u5750\uff0c\u6012\u76ee\u800c\u89c6\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u5341\u6b65\u3002 \u53cc\u65b9\u8eab\u5b50\uff0c\u5374\u662f\u52a8\u4e5f\u4e0d\u52a8\u3002\u9648\u5929\u5b87\u65f6\u624d\u98de\u9a6c\u6765\u65f6\uff0c\u5e26\u6709\u8170\u5200\u5f13\u7bad\uff0c\u89c1\u6b64\u60c5\u4f0f\uff0c\u77e5\u9053\u5e08\u5085\u6b63\u4ee5 \u4e0a\u4e58\u5185\u529f\uff0c\u4e0e\u654c\u4eba\u5168\u529b\u5468\u65cb\u3002\u770b\u6837\u5b50\u529f\u529b\u6089\u654c\u3002\u9648\u5929\u5b87\u6025\u4e8e\u6b32\u52a9\u5e08\u5085\u4e00\u81c2\u4e4b\u529b\uff0c\u4e0d\u6687\u601d\u7d22\uff0c \u7acb\u523b\u5f20\u5f13\u642d\u7bad\uff0c\u5728\u767e\u6b65\u4e4b\u5916\uff0c\u7a81\u7684\u4e00\u7bad\uff0c\u4fbf\u5411\u654c\u4eba\u80cc\u5fc3\u5c04\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5e08\u5085\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5b87\u513f\uff0c\u5feb\u8d70\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u90a3\u4eba\u4e3e\u5f13\u4e00\u62e8\uff0c\u9648\u5929\u5b87 \u5c04\u53bb\u7684\u7bad\uff0c\u500f\u7684\u53c8\u98de\u4e86\u56de\u6765\uff0c\u5feb\u82e5\u6d41\u661f\u95ea\u7535\uff0c\u9648\u5929\u5b87\u5413\u5446\u4e86\uff0c\u767e\u5fd9\u4e2d\u4e3e\u5200\u4e00\u9694\uff0c\u4f46\u89c9\u81c2\u4e0a\u4e00 \u9635\u9178\u9ebb\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u90a3\u652f\u5229\u7bad\u7adf\u7136\u63d2\u5728\u5200\u4e0a\uff0c\u7bad\u7c07\u9677\u5165\u51e0\u5206\uff0c\u82e5\u4e0d\u662f\u8170\u5200\u8fd9\u4e00\u9694\u521a\u597d\u6321\u7740\uff0c \u8fd9\u4e00\u7bad\u4fbf\u6709\u7a7f\u5fc3\u88c2\u8179\u4e4b\u707e\uff0c\u9648\u5929\u5b87\u60ca\u9a87\u6b32\u7edd\uff0c\u795e\u667a\u672a\u6e05\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u731b\u542c\u5230\u4e00\u58f0\u5c16\u53eb\uff0c \u6597\u89c1\u5e08\u5085\u51cc\u7a7a\u98de\u8d77\uff0c\u62c2\u5c18\u4e00\u626b\uff0c\u654c\u4eba\u5728\u5730\u4e0a\u8fde\u7ffb\u4e86\u51e0\u4e2a\u7b4b\u6597\uff0c\u4e5f\u968f\u5728\u4ed6\u7684\u540c\u4f34\u4e4b\u540e\uff0c\u6eda\u4e0b\u4e86 \u767e\u4e08\u51b0\u6e0a\u3002 \u3000\u3000\u9648\u5929\u5b87\u6025\u5954\u4e0a\u524d\uff0c\u53ea\u8d1d\u5e08\u5085\u4ecd\u7136\u8dcc\u5750\u5730\u4e0a\uff0c\u95ed\u76ee\u4e0d\u8bed\uff0c\u9762\u5982\u6b7b\u7070\uff0c\u62c2\u5c18\u843d\u5728\u8eab\u8fb9\u3002 \u3000\u3000\u9648\u5929\u5b87\u81f3\u9996\u4f8d\u7acb\uff0c\u7ea6\u8fc7\u4e86\u4e00\u652f\u9999\u7684\u65f6\u523b\uff0c\u8427\u9752\u5cf0\u7684\u9762\u8272\u624d\u6e10\u6e10\u7ea2\u6da6\uff0c\u5f20\u5f00\u773c\u775b\uff0c\u6c14\u5401\u5401 \u7684\u9053\uff1a\u201c\u5b87\u513f\uff0c\u5c06\u90a3\u62c2\u5c18\u7ed9\u6211\u3002\u201d\u9648\u5929\u5b87\u62fe\u8d77\u62c2\u5c18\uff0c\u8427\u9752\u5cf0\u770b\u4e86\u4e00\u773c\uff0c\u53c8\u9053\uff0c\u201c\u5c06\u62c2\u5c18\u7ed9\u6211 \u6302\u5728\u8170\u95f4\u3002\u201d\u9648\u5929\u5b87\u8fd9\u624d\u53d1\u73b0\uff0c\u5e08\u5085\u7684\u4e24\u652f\u624b\u638c\u7ffb\u8d77\uff0c\u624b\u6307\u98a4\u6296\uff0c\u5e72\u81c2\u4e0b\u5782\uff0c\u8f6c\u52a8\u751a\u4e0d\u7075 \u4fbf\uff0c\u9648\u5929\u5b87\u60ca\u9053\uff1a\u201c\u5e08\u5085\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8427\u9752\u5cf0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5c18\u5c3e\u8fd8\u5269\u4e0b\u4e00\u534a\uff0c\u4ed6\u7684\u5f13\u5f26 \u5374\u5df2\u7ed9\u6211\u62c2\u65ad\uff0c\u8fd9\u4e00\u573a\u8f83\u91cf\uff0c\u6211\u603b\u7b97\u6ca1\u8f93\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u7684\u624b\uff0c\u4f60\u7684\u624b\u2026\u2026\u201d\u8427\u9752\u5cf0\u53c8 \u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5d14\u8001\u4e09\u662f\u5d06\u5cd2\u6d3e\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u6211\u628a\u4ed6\u786c\u751f\u751f\u5730\u62c2\u4e0b\u51b0\u6e0a\uff0c\u8eab\u4e0a\u81ea\u7136\u4e5f\u5f97 \u53d7\u4e9b\u4f24\u635f\uff0c\u6211\u8fd9\u4e24\u81c2\u53d7\u4ed6\u7684\u5f13\u68a2\u6240\u5f39\uff0c\u7ecf\u8109\u626d\u66f2\uff0c\u6240\u4ee5\u5982\u6b64\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4e5f\u6ca1\u672c\u4e8b\u5c06\u6211\u5f04\u6210\u6b8b \u5e9f\uff0c\u65e9\u5219\u4e94\u65e5\uff0c\u8fdf\u5219\u4e03\u65e5\uff0c\u6211\u81ea\u5df1\u4f1a\u628a\u4ed6\u51b6\u597d\uff0e\u5b87\u513f\uff0c\u6b64\u6b21\u5012\u5168\u4e8f\u4f60\u5c04\u8fd9\u4e00\u7bad\u3002\u201d\u9648\u5929\u5b87\u5341 \u5206\u60ed\u6127\uff0c\u9053\uff1a\u201c\u6211\u5c04\u8fd9\u7bad\uff0c\u7b80\u76f4\u5982\u5375\u51fb\u77f3\uff0c\u975e\u4f46\u5c04\u4e0d\u7740\u4ed6\uff0c\u53cd\u800c\u7ed9\u4ed6\u53cd\u5c04\uff0c\u8fd9\u90fd\u662f\u6b66\u529f\u6ca1\u6709 \u7ec3\u597d\uff0c\u4ee5\u81f3\u5e2e\u4e0d\u4e0a\u5e08\u5085\u7684\u5fd9\u3002\u201d\u8427\u9752\u5cf0\u7b11\u9053\uff0c\u201c\u5b87\u513f\uff0c\u4f60\u8fd8\u4e0d\u660e\u5176\u4e2d\u7684\u9053\u7406\u4e48\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u8bf7\u5e08\u5085\u6307\u70b9\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4ed6\u6b63\u5168\u529b\u4e0e\u6211\u5468\u65cb\uff0c\u4e3a\u4e86\u62d4\u4f60\u8fd9\u53ea\u7bad\uff0c\u5206\u4e86 \u5fc3\u795e\uff0c\u6211\u624d\u5f97\u4e58\u865a\u800c\u5165\uff0c\u8981\u4e0d\u7136\u6211\u867d\u4e0d\u81f3\u843d\u8d25\uff0c\u8981\u80dc\u4ed6\u53ef\u4e5f\u4e0d\u6613\u5462\u3002\u53ea\u662f\uff0c\u4f60\u4e5f\u5fd2\u5192\u9669\u4e86\uff0c \u8981\u4e0d\u662f\u76f8\u8ddd\u767e\u6b65\u4e4b\u5916\uff0c\u8fd9\u53cd\u5f39\u4e4b\u529b\uff0c\u4f60\u7109\u80fd\u7981\u53d7\u5f97\u4f4f\uff1f\u8bf4\u6765\u4e5f\u771f\u5de7\u5408\uff0c\u6211\u6388\u4f60\u7684\u7bad\u6cd5\u6cc4\u9732\u4e86 \u6211\u7684\u884c\u85cf\uff0c\u4f46\u53c8\u66ff\u6211\u6253\u8d25\u5f3a\u654c\u3002\u201d\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u90a3\u65e5\u4ed6\u7528\u6ca1\u7c07\u7bad\u5c04\u6211\uff0c\u83ab\u975e\u662f\u6709\u610f\u76f8\u8bd5 \u4e48\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u6296\u9732\u51fa\u7a7a\u624b\u63a5\u7bad\u7684\u672c\u4e8b\uff0c\u4ed6\u4fbf\u77e5\u9053\u662f\u6211\u7684\u4f20\u6388\uff0c\u5bfb\u4e86\u5341\u5e74\u7ec8\u4e8e \u7ed9\u4ed6\u5bfb\u7740\u4e86\u3002\u201d\u9648\u5929\u5b87\u60f3\u8d77\u4e00\u4e8b\uff0c\u5fc3\u751a\u4e0d\u5b89\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u90a3\u7fa4\u5356\u5531\u7684\u6d41\u6d6a\u8005\u90fd\u662f\u574f\u4eba \u4e48\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\uff0c\u6211\u67e5\u6e05\u695a\u4e86\uff0c\u9664\u4e86\u90a3\u4e2a\u85cf\u65cf\u5c11\u5973\u5916\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u786e\u5b9e\u90fd\u662f\u6d41 \u6d6a\u7684\u827a\u4eba\uff0c\u6211\u8fd9\u4e24\u4e2a\u5f3a\u654c\u4e0e\u90a3\u5c11\u5973\u90fd\u662f\u5404\u6709\u76ee\u7684\uff0c\u6df7\u5728\u90a3\u5806\u4eba\u4e2d\u7684\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u55ef\uff0c\u90a3 \u85cf\u65cf\u5c11\u5973\uff0c\u5979\uff0c\u5979\u53c8\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u201d\u8427\u80af\u5cf0\u7b11\u9053\uff1a\u201c\u8fd9\u6211\u53ef\u4e0d\u77e5\u9053\u4e86\uff0c\u6211\u672c\u8eab\u7684\u4e8b\u5df2\u591f\u5934 \u75bc\uff0c\u90a3\u8fd8\u6709\u95f2\u5fc3\u4ed4\u7ec6\u67e5\u5979\u3002\u5440\uff0c\u5b87\u513f\uff0c\u54b1\u4eec\u7684\u7f18\u6cd5\u5c3d\u4e86\uff0c\u201d\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u5e08\u5085\u7684\u4e24\u4e2a\u5f3a\u654c \u4e0d\u662f\u90fd\u6b7b\u4e86\u4e48\uff1f\u5c1a\u6709\u4f55\u60e7\uff1f\u201d\u8427\u7701\u5cf0\u82e6\u7b11\u9053\uff1a\u201c\u738b\u7624\u5b50\u4e2d\u4e86\u6211\u7684\u7a9d\u5fc3\u811a\u6599\u4ed6\u4e0d\u80fd\u6d3b\u547d\uff0c\u4f46\u795e \u5f13\u5d14\u8001\u4e09\u529f\u529b\u6df1\u539a\uff0c\u5927\u534a\u8dcc\u4e0d\u6b7b\u4ed6\uff0c\u800c\u4e14\u6211\u4e0d\u6b62\u662f\u6709\u8fd9\u4e24\u4e2a\u5f3a\u654c\uff0c\u8fd8\u6709\u7b2c\u4e09\u4e2a\u5f3a\u654c\uff0c\u8fd9\u4eba\u7684 \u6b66\u529f\u8fdc\u975e\u6211\u6240\u80fd\u53ca\uff0c\u5d14\u8001\u4e09\u4e0d\u6b7b\uff0c\u4e00\u5b9a\u5f15\u4ed6\u6765\u627e\u6211\uff0c\u53ea\u6101\u5929\u4e0b\u65e0\u4eba\u80fd\u6551\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u8fd9\u3001\u8fd9\u53ef\u600e\u751f\u662f\u597d\uff1f\u201d\u5fe7\u6124\u4e4b\u60c5\uff0c\u73b0\u4e8e\u989c\u8272\u3002\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u95fb\u8bf4\u6709\u4f4d\u5f02\u4eba\uff0c\u5c31\u4f4f\u5728\u85cf \u8fb9\uff0c\u4ed6\u4e5f\u8bb8\u80fd\u654c\u5f97\u4f4f\u6211\u7684\u5bf9\u5934\uff0c\u53ea\u4e0d\u77e5\u4ed6\u80af\u4e0d\u80af\u6551\u6211\uff0c\u5904\u6b64\u7edd\u5883\uff0c\u522b\u65e0\u4ed6\u6cd5\uff0c\u6211\u4eca\u65e5\u4fbf\u8981\u79bb \u5f00\u6b64\u5730\uff0c\u4e14\u8bd5\u4e00\u8bd5\u5bfb\u627e\u90a3\u4f4d\u5f02\u4eba\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u6b63\u6b32\u518d\u95ee\uff0c\u5ffd\u89c1\u4e0a\u5761\u4e4b\u4e0a\u4e00\u4e2a\u9ed1\u70b9\uff0c\u6e10\u8fd1\u6e10\u663e\uff0c\u722c\u4e86\u4e0b\u6765\uff0c\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u662f \u4f60\uff0c\u6c5f\u5357\uff01\u201d\u6c5f\u5357\u722c\u5f97\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u6b47\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7237\u53eb\u6211\u6765\u627e\u4f60\u4eec\uff0c\u4eca\u5929\u4e4b\u4e8b\uff0c \u6211\u5df2\u4f9d\u5c11\u7237\u7684\u5429\u5490\uff0c\u544a\u8bc9\u4e86\u8001\u7237\u5566\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u8001\u7237\u600e\u4e48\u5566\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u8001\u7237\u5e26\u4e86\u62a4 \u536b\u8d76\u56de\uff0c\u4e0d\u4e45\u571f\u53f8\u7684\u5175\u4e5f\u6765\u4e86\uff0c\u706b\u5df2\u6551\u7184\uff0c\u6b7b\u8005\u5df1\u57cb\u3002\u4f24\u8005\u4e5f\u90fd\u6551\u51fa\u6765\u4e86\u3002\u5440\uff0c\u54b1\u4eec\u8859\u95e8\u7684 \u5175\uff0c\u6b7b\u4f24\u516b\u4e5d\uff0c\u53ea\u5269\u4e0b\u5341\u6765\u4e2a\u5566\u3002\u8001\u7237\u8bf4\u8981\u5230\u62c9\u8428\u89c1\u798f\u5927\u5e05\u53bb\uff0c\u90a3\u5e26\u5175\u6d85\u5df4\uff0c\u5374\u53e3\u53e3\u58f0\u58f0\u8981 \u627e\u4f60\uff0c\u8bf4\u662f\u8981\u4f60\u4eca\u665a\u5230\u571f\u53f8\u5bb6\u53bb\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u4e0d\u53bb\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u662f\u5440\uff0c\u8001\u7237\u4e5f\u77e5\u9053 \u4f60\u5b9a\u7136\u4e0d\u53bb\uff0c\u4ed6\u53eb\u6211\u5bf9\u4f60\u8bf4\uff0c\u4ed6\u4e0d\u613f\u5f3a\u8feb\u4f60\u505a\u4e0d\u613f\u610f\u505a\u7684\u4e8b\uff0c\u4ed6\u73b0\u5728\u5df2\u77e5\u9053\u5148\u751f\u662f\u4e2a\u5927\u6709\u672c \u9886\u7684\u4eba\uff0c\u6240\u4ee5\u4ed6\u653e\u5fc3\u8ba9\u4f60\u8ddf\u5148\u751f\u53bb\u3002\u5c11\u7237\uff0c\u4f60\u4e0d\u613f\u505a\u4ec0\u4e48\u4e8b\u6e05\uff1f\u201d\u9648\u5929\u5b87\u4e0d\u7b54\u6c5f\u5357\u7684\u8bdd\uff0c \u9053\uff1a\u201c\u5e08\u5085\uff0c\u90a3\u4e48\u6211\u8ddf\u4f60\u53bb\u627e\u90a3\u4f4d\u5f02\u4eba\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u53bb\uff1f\u5440\uff0c\u8fd9\u53ef\u5371\u9669\u5f97\u5f88 \u54ea\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u7559\u5728\u8fd9\u91cc\uff0c\u66f4\u662f\u5371\u9669\uff0c\u5e08\u5085\uff0c\u8fd9\u4e8b\u4ee5\u540e\u6211\u518d\u5bf9\u4f60\u7ec6\u8bf4\u3002\u6c5f\u5357\uff0c\u4f60\u56de\u53bb \u544a\u8bc9\u8001\u7237\uff0c\u5c06\u6765\u6211\u5230\u62c9\u8428\u627e\u4ed6\u3002\u201d\u8427\u9752\u8702\u770b\u4e86\u4e00\u770b\u81ea\u5df1\u7684\u53cc\u624b\uff0c\u751a\u662f\u611f\u52a8\u3002\u9053\uff1a\u201c\u5f92\u513f\uff0c\u6211 \u77e5\u9053\u4f60\u7684\u597d\u610f\uff0c\u597d\uff0c\u4f60\u5c31\u968f\u6211\u53bb\u5427\uff0e\u201d\u8fd9\u4e00\u53bb\u4e5f\uff0c\u6709\u5206\u6559\uff1a \u3000\u3000\u864e\u95e8\u9f99\u4e89\u60ca\u585e\u5916\uff0c\u5f15\u51fa\u51b0\u5ddd\u5929\u5973\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u731c\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u5cfb\u5cad\u98de\u9a91\u3000\u4ec7\u5bb6\u7aa5\u5e10\u5e55\u3000\u91d1\u9488\u89e3\u7a74\u3000\u533b\u9053\u914d\u795e\u529f \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u4e8c \u56de \u5cfb\u5cad\u98de\u9a91\u3000\u4ec7\u5bb6\u7aa5\u5e10\u5e55 \u91d1\u9488\u89e3\u7a74\u3000\u533b\u9053\u914d\u795e\u529f \u3000\u3000\u65f6\u5e8f\u5df2\u662f\u66ae\u6625\uff0c\u4f46\u4ece\u85cf\u5357\u8428\u8ff9\u901a\u5f80\u85cf\u897f\u65e5\u5580\u5219\u7684\u5c71\u533a\uff0c\u51b0\u96ea\u5374\u5c1a\u672a\u5f00\u59cb\u878d\u5316\u3002\u6700\u5927\u80c6\u7684 \u7267\u4eba\uff0c\u4e5f\u8fd8\u8981\u7b49\u5230\u534a\u6708\u4e4b\u540e\uff0c\u5f85\u521d\u590f\u7684\u9633\u5149\u666e\u7167\uff0c\u5c01\u5c71\u7684\u96ea\u5757\u6d88\u878d\u4e4b\u540e\uff0c\u624d\u6562\u884c\u8d70\u3002\u4f46\u4ee4\u5927 \u80c6\u7684\u7267\u4eba\u4e5f\u610f\u6599\u4e0d\u5230\u7684\u662f\uff1a\u8fd9\u4e2a\u65f6\u8282\uff0c\u7adf\u7136\u6709\u4e24\u9a91\u5f6a\u9a6c\u5382\u5728\u76d8\u65cb\u66f2\u6298\u7684\u5c71\u9053\u4e0a\u7f13\u7f13\u524d\u884c\uff0c\u800c \u4e14\u8fd9\u4e24\u4f4d\u9a91\u5ba2\uff0c\u4e00\u8001\u4e00\u5c11\u4ece\u5916\u8c8c\u770b\u6765\uff0c\u8fd8\u90fd\u662f\u6587\u5f31\u7684\u4e66\u751f\uff0c\u8fd9\u4e24\u4f4d\u9a91\u5ba2\uff0c\u6b63\u662f\u5e08\u5f92\u4e8c\u4eba\uff0c\u8001 \u7684\u662f\u8427\u9752\u5cf0\uff0c\u5c11\u7684\u662f\u9648\u5929\u5b87\u3002 \u3000\u3000\u897f\u85cf\u9ad8\u539f\uff0c\u53f7\u79f0\u201c\u4e16\u754c\u5c4b\u810a\u201d\uff0c\u5c24\u5176\u662f\u4ece\u8428\u8fe6\u5230\u65e5\u5580\u5219\u3002\u8fd9\u6bb5\uff0c\u5357\u6709\u559c\u9a6c\u62c9\u96c5\u5c71\uff0c\u5317\u6709 \u5580\u5587\u6606\u4ed1\u5c71\uff0c\u5c71\u8109\u7ef5\u5ef6\uff0c\u5730\u52bf\u9ad8\u7ae3\uff0c\u66f4\u662f\u96be\u884c\uff0c\u9ad8\u539f\u7a7a\u6c14\u7a00\u8584\uff0c\u547c\u5438\u4e5f\u9887\u56f0\u96be\uff0c\u5e78\u800c\u8427\u9752\u5cf0 \u5185\u529f\u6df1\u6e5b\uff0c\u9648\u5929\u5b87\u7ec3\u6b66\u591a\u5e74\uff0c\u4e5f\u9887\u6709\u6839\u5e95\u3002\u517c\u4e4b\u80dc\u5728\u5e74\u9752\u529b\u58ee\uff0c\u4e5f\u8fd8\u4e0d\u89c9\u600e\u6837\u3002\u53ea\u662f\u4e24\u5339\u5065 \u9a6c\uff0c\u5374\u662f\u547c\u547c\u5598\u6c14\uff0c\u76f4\u6d41\u53e3\u6cab\u3002 \u3000\u3000\u9648\u5929\u5b87\u8f7b\u6276\u9a6c\u9b03\uff0c\u53f9\u9053\uff1a\u201c\u4eba\u672a\u7d2f\u6b7b\uff0c\u9a6c\u5374\u8981\u7d2f\u6b7b\u4e86\u3002\u201d\u897f\u85cf\u6c14\u5019\u6781\u602a\uff0c\u65e5\u95f4\u9a84\u9633\u5982 \u706b\uff0c\u5c24\u5176\u5c71\u533a\u7a7a\u6c14\u7a00\u8584\uff0c\u65e5\u5934\u76f4\u5c04\u4e0b\u6765\uff0c\u66f4\u662f\u70ed\u5f97\u6015\u4eba\uff0c\u4f46\u4e00\u5230\u592a\u9633\u5c04\u7167\u4e0d\u5230\u7684\u9634\u5f71\u4e4b\u5904\u3002 \u6216\u662f\u5230\u4e86\u665a\u95f4\uff0c\u5374\u53c8\u662f\u51b7\u6c14\u6c81\u4eba\uff0c\u4e25\u5bd2\u71ac\u9aa8\u3002\u5c71\u5cf0\u4e0a\u867d\u7136\u79ef\u96ea\u7691\u7691\uff0c\u5c71\u6c9f\u95f4\u867d\u6709\u51b0\u5ddd\u4ea4\u9519\uff0c \u5047\u82e5\u6e38\u9f99\uff0c\u4f46\u7eb5\u662f\u672c\u9886\u518d\u9ad8\u7684\u4eba\uff0c\u4e5f\u4e0d\u6562\u5192\u90a3\u5929\u5927\u7684\u5947\u9669\uff0c\u53bb\u767b\u90a3\u51b0\u96ea\u3002\u987b\u77e5\u51b0\u96ea\u4e00\u53d7\u9707 \u52a8\uff0c\u5c31\u53ef\u80fd\u5f15\u8d77\u96ea\u5d29\u4e4b\u707e\uff0c\u4eba\u755c\u4ff1\u53d7\u6d3b\u57cb\u3002\u6240\u4ee5\u5728\u5c71\u533a\u8d76\u8def\u7684\u65c5\u4eba\uff0c\u7a7a\u5bf9\u77d7\u7acb\u7684\u51b0\u5cad\uff0c\u5374\u662f \u96be\u6b62\u53e3\u4e2d\u7684\u5e72\u6e34\u3002 \u3000\u3000\u8427\u9752\u8702\u770b\u7740\u5750\u9a91\u547c\u547c\u5598\u6c14\uff0c\u602a\u662f\u96be\u53d7\uff0c\u8fd0\u51dd\u534a\u54cd\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u5269\u6709\u51e0\u56ca\u6c34\uff1f\u201d\u9648\u5929 \u5b87\u9053\uff1a\u201c\u8fd8\u6709\u4e09\u4e2a\u6c34\u56ca\uff0c\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u628a\u534a\u56ca\u6c34\u8ba9\u8fd9\u4e24\u5339\u9a6c\u559d\u4e86\uff0c\u54b1\u4eec\u8282\u7701\u4e00\u70b9\u3002\u9a6c \u5339\u559d\u4e86\u6c34\u624d\u6709\u529b\u6c14\u8d76\u8def\u3002\u201d\u8427\u9752\u5cf0\u7684\u4e00\u624b\u81c2\u88ab\u5f3a\u654c\u6240\u4f24\uff0c\u73b0\u5728\u5c1a\u672a\u80fd\u8f6c\u52a8\u81ea\u5982\uff0c\u6240\u4ee5\u53d6\u6c34\u5582 \u9a6c\u7b49\u7b49\u4e8b\u60c5\uff0c\u90fd\u987b\u9648\u5929\u5b87\u53bb\u529e\u3002 \u3000\u3000\u9648\u5929\u5b87\u8df3\u4e0b\u9a6c\u6765\uff0c\u6253\u5f00\u6c34\u56ca\uff0c\u631f\u7740\u9a6c\u5934\uff0c\u8ba9\u5b83\u559d\u6c34\u3002\u5ffd\u95fb\u5f97\u80cc\u540e\u9a6c\u94c3\u4e4b\u58f0\uff0c\u53ea\u89c1\u540e\u800c\u4e09 \u5339\u9a6c\u8d76\u4e86\u4e0a\u6765\uff0c\u9a6c\u4e0a\u7684\u4e58\u5ba2\u4e43\u662f\u4e09\u4f4d\u53cc\u4eba\uff0c\u6d53\u7709\u5927\u773c\uff0c\u4e2a\u4e2a\u76f8\u8c8c\u7956\u8c6a\uff0c\u89c1\u9648\u5929\u5b87\u4ee5\u6c34\u5582\u9a6c\uff0c \u8fde\u8fde\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff01\u53ef\u60dc\uff01\u201d \u3000\u3000\u4e3a\u9996\u7684\u4e00\u62c9\u9a6c\u7f30\uff0c\u5728\u9648\u5929\u5b87\u8eab\u65c1\u505c\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u4f4d\u5c0f\u54e5\u5e26\u7684\u6c34\u591a\uff0c\u54b1\u4eec\u7684\u5374\u559d \u5b8c\u4e86\uff0c\u4f60\u5206\u4e00\u56ca\u6c34\u7ed9\u6211\u5982\u4f55\uff1f\u201d\u8bf4\u5f97\u6ee1\u4e0d\u5728\u4e4e\uff0c\u6beb\u65e0\u793c\u8c8c\uff0c\u9648\u5929\u5b87\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u5728\u8fd9 \u6e3a\u65e0\u4eba\u8ff9\u7684\u5c71\u533a\uff0c\u6c34\u6bd4\u4e07\u91d1\u8fd8\u8981\u96be\u5f97\uff0c\u5982\u4f55\u53ef\u4ee5\u8f7b\u6613\u7ed9\u4eba\uff1f\u201d\u5ffd\u95fb\u5f97\u5e08\u5085\u8bf4\u9053\uff1a\u201c\u51fa\u95e8\u4e4b \u4eba\uff0c\u7406\u5e94\u60a3\u96be\u76f8\u52a9\uff0c\u5b87\u513f\uff0c\u7ed9\u4ed6\uff01\u201d\u9648\u5929\u5b87\u89c1\u662f\u5e08\u5085\u5429\u5490\uff0c\u53ea\u5f97\u89e3\u4e0b\u6c34\u56ca\uff0c\u9001\u7ed9\u90a3\u4eba\uff0c\u90a3\u4eba \u9aa8\u561f\u561f\u5730\u559d\u4e86\u53e3\u6c34\uff0c\u6b6a\u7740\u773c\u775b\u770b\u4e86\u8427\u9752\u5cf0\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5012\u662f\u4e2a\u597d\u4eba\uff0c\u5582\uff0c\u4f60\u53bb\u54ea\u91cc\uff1f\u201d\u8427 \u9752\u5cf0\u9053\uff1a\u201c\u5f80\u65e5\u5580\u5219\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u7b49\u51b0\u96ea\u878d\u5316\u5c31\u6025\u7740\u8d76\u8def\uff1f\u201d\u8427\u9752\u8702\u9053\uff1a\u201c\u655d\u621a\u5728 \u65e5\u5580\u5219\u75c5\u91cd\uff0c\u8981\u8d76\u53bb\u77a7\u4ed6\u3002\u201d\u90a3\u4eba\u4e0e\u540c\u4f34\u5bf9\u671b\u4e00\u773c\uff0c\u9762\u4e0a\u795e\u60c5\uff0c\u4f3c\u4fe1\u4f3c\u7591\u3002 \u3000\u3000\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5b87\u513f\uff0c\u90a3\u4e9b\u836f\u4f60\u53ef\u5f97\u5f53\u5fc3\uff0c\u836f\u56ca\u4e0d\u8981\u6302\u5728\u9a6c\u978d\u4e0a\uff0c\u6536\u8d77\u6765\u5427\uff0c\u5c71\u8def\u5d0e\u5c96\uff0c \u9a6c\u513f\u4e00\u4e2a\u5931\u8e44\uff0c\u8dcc\u4e86\u836f\u56ca\u53ef\u4e0d\u5f97\u4e86\u3002\u522b\u7684\u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u9f99\u6811\u679c\u5374\u662f\u6ca1\u5730\u65b9\u4e70\u7684\uff0e\u201d\u9648\u5929\u5b87\u4e00 \u6014\uff0c\u6302\u4e3b\u9a6c\u978d\u4e0a\u7684\u54ea\u662f\u4ec0\u4e48\u836f\u56ca\uff0c\u4e43\u65e9\u4ed6\u4eec\u6240\u7528\u7684\u6697\u5668\u56ca\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u5e08\u5085\u773c\u5149\u4e4b\u4e2d\u4f3c \u6709\u6df1\u610f\uff0c\u9648\u5929\u5b87\u731b\u7136\u9192\u9053\uff1a\u201c\u662f\u5475\uff0c\u8fd9\u4e0b\u4eba\u6562\u5728\u6b64\u65f6\u884c\u8d70\uff0c\u60f3\u6765\u4e5f\u662f\u5927\u6709\u672c\u9886\u4e4b\u4eba\u3002\u54b1\u4eec\u4e0d \u53ef\u9732\u76f8\u3002\u8fd9\u6697\u5668\u56ca\u8fd8\u662f\u6536\u4e86\u597d\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u90a3\u9f99\u6811\u679c\u867d\u662f\u5929\u7afa\u6765\u7684\uff0c\u8428\u8fe6\u5230\u5904\u6709\u5356\uff0c\u4e5f\u6ca1 \u6709\u4ec0\u4e48\u7a00\u5947\uff0c\u4e3a\u4f55\u5e08\u5085\u8bf4\u5f97\u5982\u6b64\u73cd\u91cd\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u5148\u5934\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4ee4\u4eb2\u60a3\u7684\u4e43\u662f\u8840\u5d29\u4e4b\u75c7\uff0c\u9f99\u6811\u679c\u867d\u662f\u5bf9\u75c7\u4e4b\u836f\uff0c\u5374\u4e5f\u672a\u5fc5 \u51c6\u80fd\u594f\u6548\uff0c\u5144\u5f1f\u4e0d\u624d\uff0c\u8fd8\u7a0d\u61c2\u4e00\u70b9\u533b\u9053\uff0c\u5144\u5f1f\u4e5f\u662f\u5230\u65e5\u5580\u5219\u7684\uff0c\u5c31\u6b64\u540c\u884c\u5982\u4f55\uff1f\u201d\u8427\u9752\u5cf0 \u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d\u8001\u673d\u867d\u4e5f\u7a0d\u8bfb\u8fc7\u51e0\u672c\u533b\u4e66\uff0c\u5bf9\u6cbb\u8840\u5d29\u4e4b\u75c7\u3001\u5374\u662f\u6beb\u65e0\u628a\u63e1\uff0c\u655d\u4eb2\u4e4b \u75c5\uff0c\u5c06\u6765\u5b9a\u8981\u4ef0\u4ed7\u7684\u4e86\u3002\u201d\u90a3\u4eba\u4e5f\u62f1\u62f1\u624b\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff01\u627f\u8499\u8d60\u6c34\uff1b\u5f53\u5f97\u6548\u52b3\uff0c\u201d\u7adf\u7136 \u7b56\u9a6c\u8ddf\u7740\u8427\u9752\u5cf0\uff0c\u4ed6\u7684\u4e24\u4e2a\u540c\u4f34\uff0c\u4e5f\u4e00\u524d\u4e00\u540e\uff0c\u628a\u9648\u5929\u5b87\u5939\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u9648\u5929\u5b87\u731c\u4e0d\u5230\u5e08\u5085\u8bf4\u8bdd\u7684\u7528\u610f\uff0c\u751a\u662f\u7eb3\u7f55\uff0c\u88ab\u90a3\u4e24\u4eba\u4f3c\u62bc\u89e3\u56da\u5f92\u4f3c\u7684\u5939\u5728\u4e2d\u95f4\uff0c\u66f4\u662f\u6c14 \u95f7\uff1a\u201c\u4ed6\u5374\u4e0d\u77e5\uff0c\u90a3\u9f99\u6811\u679c\u5728\u8428\u8fe6\u867d\u4e0d\u5e0c\u5947\uff0c\u4f46\u8981\u7b49\u6c34\u96ea\u878d\u6bd4\u4e4b\u540e\uff0c\u624d\u6709\u836f\u6750\u8d29\u5b50\u8fd0\u5230\u65e5\u5580 \u5219\uff0c\u6240\u4ee5\u5728\u65e5\u5580\u5219\u5374\u662f\u96be\u5f97\u4e4b\u7269\u3002\u8427\u9752\u5cf0\u5982\u6b64\u8bf4\u6cd5\uff0c\u5b9e\u662f\u6709\u610f\u5411\u90a3\u4e9b\u4eba\u89e3\u91ca\uff0c\u4e3a\u4f55\u81ea\u5df1\u8981\u5192 \u9669\u8d76\u5230\u65e5\u5580\u5219\u53bb\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u4eba\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u64a9\u8427\u9752\u5cf0\u8bf4\u8bdd\uff0c\u8427\u9752\u5cf0\u751a\u662f\u8c28\u614e\uff0c\u78b0\u7740\u4ed6\u4eec\u63d0\u5230\u6c5f\u6e56\u4e0a\u7684\u4e8b \u60c5\uff0c\u5c31\u4f6f\u50bb\u626e\u61f5\uff0c\u53ea\u548c\u4ed6\u4eec\u8c08\u4e00\u4e9b\u533b\u9053\uff0c\u90a3\u4e9b\u4eba\u5176\u5b9e\u5bf9\u533b\u9053\u4e5f\u5e76\u4e0d\u9ad8\u660e\uff0c\u53ea\u662f\u61c2\u5f97\u4e00\u4e9b\u6cbb\u8dcc \u6253\u548c\u5410\u8840\u7b49\u75c5\u75c7\uff0c\u8fd9\u4e9b\u75c5\u75c7\uff0c\u51e1\u662f\u666e\u904d\u7ec3\u6b66\u4e4b\u4eba\u90fd\u5fc5\u987b\u61c2\u5f97\u6cbb\u7684\u3002 \u3000\u3000\u884c\u4e86\u4e00\u9635\uff0c\u65e5\u5f71\u897f\u659c\uff0c\u524d\u884c\u7684\u90a3\u7c97\u8c6a\u6c49\u5b50\u9053\uff1a\u201c\u5e78\u559c\u6ca1\u78b0\u4e0a\u96ea\u5d29\u3002\u201d\u8bdd\u72b9\u6765\u4e86\uff0c\u5ffd\u542c\u524d \u9762\u201c\u5f97\u5f97\u201d\u58f0\u54cd\uff0c\u90a3\u4eba\u51db\u7136\u4e00\u60ca\uff0c\u5c71\u5773\u5904\u7a81\u7136\u5954\u51fa\u4e00\u9a91\u9a6c\u6765\uff0c\u9a6c\u4e0a\u5305\u7740\u9632\u5bd2\u7684\u539a\u7ed2\u3002\u6240\u4ee5\u5230 \u4e86\u4e34\u8fd1\u65b9\u624d\u77e5\u6653\uff0c\u51fa\u8def\u9669\u5cfb\uff0c\u4ec5\u5bb9\u4e00\u9a91\uff0c\u90a3\u5339\u9a6c\u9aa4\u7136\u5954\u6765\uff0c\u6536\u7f30\u4e0d\u4f4f\uff0c\u770b\u770b\u5c31\u8981\u649e\u4e2a\u6b63\u7740\uff0c \u524d\u884c\u90a3\u6c49\u5b50\u8c8c\u4f3c\u7c97\u8c6a\uff0c\u9a91\u672f\u7cbe\u7edd\uff0c\u9661\u7136\u53cc\u817f\u4e00\u5939\uff0c\u628a\u9a6c\u5b9a\u4f4f\uff0c\u547c\u7684\u4e00\u638c\u63a8\u51fa\uff0c\u8fd9\u4e00\u638c\u52b2\u9053\u5341 \u8db3\uff0c\u7adf\u662f\u610f\u6b32\u628a\u90a3\u4e0d\u901f\u4e4b\u5ba2\u786c\u751f\u751f\u63a8\u4e0b\u6df1\u8c37\uff01\u90a3\u4e0d\u901f\u4e4b\u5ba2\u9a87\u53eb\u4e00\u58f0\uff0c\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u4e0b\u9a6c\u6765\uff0c \u53f3\u624b\u4e00\u4f38\uff0c\u5374\u626f\u4f4f\u4e86\u7c97\u8c6a\u6c49\u5b50\u90a3\u5339\u9a6c\u978d\uff0c\u5411\u540e\u4e00\u8dcc\uff0c\u6070\u6070\u8dcc\u7ffb\u5728\u9648\u5929\u5b87\u7684\u9a6c\u524d\uff0c\u53ea\u542c\u5f97\u535c\u7684 \u4e00\u58f0\uff0c\u7c97\u8c6a\u6c49\u5b50\u9a6c\u978d\u4e0a\u6302\u7684\u90a3\u4e2a\u6c34\u56ca\uff0c\u7adf\u7ed9\u4ed6\u626f\u4e86\u4e0b\u53bb\uff0c\u8dcc\u4e0b\u6df1\u8c37\u53bb\u4e86\u3002\u9648\u5929\u5b87\u60ca\u9b42\u672a\u5b9a\uff0c \u53c8\u5403\u4e00\u60ca\uff0c\u5b9a\u773c\u770b\u65f6\uff0c\u8fd9\u4e0d\u901f\u4e4b\u5ba2\u4e43\u662f\u4e2a\u4e66\u751f\u6253\u626e\u7684\u5c11\u5e74\u4eba\uff0c\u602f\u751f\u751f\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u90a3\u7c97\u8c6a\u6c49 \u5b50\u8df3\u4e0b\u9a6c\u6765\uff0c\u6055\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u8d70\u8def\u4e0d\u5e26\u773c\u775b\u5417\uff1f\u201d\u5feb\u628a\u6c34\u56ca\u8d54\u6211\uff01\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u6211\u7684 \u6c34\u90fd\u559d\u5149\u4e86\uff0c\u4e5f\u6b63\u5728\u5bfb\u89c5\u5c71\u6cc9\uff0c\u90a3\u6709\u5f97\u8d54\u4f60\u3002\u201d\u90a3\u7c97\u8c6a\u6c49\u5b50\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u6ca1\u6709\u6c34\u8d54\uff1f\u6211\u5c31 \u62c6\u4f60\u7684\u76ae\uff0c\u559d\u4f60\u7684\u8840\uff01\u201d\u5582\u7684\u62e8\u51fa\u4f69\u5200\uff0c\u8fc8\u6b65\u4e0a\u524d\uff0c\u5c31\u8981\u6349\u90a3\u5c11\u5e74\u4e66\u751f\u3002\u9648\u5929\u5b87\u5fc3\u5934\u5927\u6124\uff0c \u60f3\u9053\uff1a\u201c\u8fd9\u4e66\u751f\u867d\u662f\u83bd\u649e\uff0c\u4f60\u8981\u53d6\u4ed6\u6027\u547d\uff0c\u53ef\u662f\u592a\u8fc7\u5f3a\u6a2a\uff01\u201d\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u66ff\u4ed6\u8d54\uff01\u201d\u90a3 \u7c97\u8c6a\u6c49\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u66ff\u4ed6\u8d54\uff1f\u62ff\u6765\u5427\uff01\u201d\u9648\u5929\u5b87\u53c8\u89e3\u4e0b\u4e00\u4e2a\u6c34\u56ca\uff0c\u4ed6\u5e08\u5f92 \u4e8c\u4eba\u672c\u6765\u5e26\u4e86\u4e09\u56ca\u6c34\uff0c\u9001\u4e86\u4e00\u4e2a\u6c34\u56ca\uff0c\u73b0\u5728\u53c8\u66ff\u8fd9\u5c11\u5e74\u8d54\u4e86\u4e00\u4e2a\uff0c\u9a6c\u5339\u559d\u4e86\u534a\u56ca\uff0c\u5269\u4e0b\u7684\u53ea \u6709\u534a\u56ca\u6c34\u4e86\u3002\u90a3\u7c97\u8c6a\u6c49\u5b50\u5c45\u7136\u6beb\u4e0d\u5ba2\u6c14\uff0c\u4f38\u624b\u5c31\u8981\u4e86\u9648\u5929\u5b87\u7684\u6c34\u56ca\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u5411\u9648\u5929\u5b87\u6df1\u6df1\u4e00\u63d6\uff0c\u5531\u4e86\u4e2a\u8bfa\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5144\u53f0\u6551\u547d\u4e4b\u6069\uff0c\u545c\u547c\uff0c\u541b\u5b50\u4e4b\u4e49 \u4e0e\u5c0f\u4eba\u4e4b\u5229\u5224\u7136\u660e\u77e3\uff01\u201d\u90a3\u7c97\u8c6a\u6c49\u5b50\u77aa\u773c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u6211\u5ff5\u5236\u827a \uff08\u516b\u80a1\u6587\u7ae0\uff09\uff0c\u4e0e\u4f60\u4f55\u5e72\uff1f\u201d\u9648\u5929\u5b87\u6025\u9053\uff1a\u201c\u540c\u662f\u51fa\u95e8\u4e4b\u4eba\uff0c\u76f8\u8ba9\u4e3a\u4e0a\uff0c\u9601\u4e0b\u6beb\u65e0\u635f\u5931\uff0c\u8bf7 \u7b97\u4e86\u5427\u3002\u201d\u8ddf\u5728\u8427\u9752\u5cf0\u80cc\u540e\u7684\u90a3\u4e2a\u6c49\u5b50\u4f3c\u4e4e\u662f\u4e09\u4eba\u4e2d\u7684\u5927\u54e5\uff0c\u4ed6\u51fa\u58f0\u529d\u9053\uff1a\u201c\u8001\u4e09\uff0c\u770b\u8fd9\u4f4d \u5c0f\u54e5\u9762\u4e0a\uff0c\u9976\u4e86\u8fd9\u53ae\u3002\u201d\u90a3\u7c97\u8c6a\u6c49\u5b50\u6124\u6124\u7136\u7684\u8de8\u4e0a\u9a6c\u80cc\u3002\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u9e1f\uff0c\u628a\u4f60\u7684\u9a6c\u9000\u540e\uff0c \u7275\u5230\u5c71\u52a9\u8f6c\u89d2\u5bbd\u9614\u7684\u5730\u65b9\u53bb\uff0c\u8ba9\u6211\u4eec\u5148\u8fc7\u3002\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u8bf7\u95ee\u4f60\u4eec\u4e0a\u7684\u90a3\u513f\uff1f\u201d\u90a3\u7c97 \u8c6a\u6c49\u5b50\u9053\uff1a\u201c\u6211\u4eec\u4e0a\u90a3\u513f\u5173\u4f60\u9e1f\u4e8b\uff01\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u5c82\u6562\u52a8\u95ee\u4f60\u8001\uff0c\u6211\u95ee\u7684\u662f\u8fd9\u4f4d\u5c0f \u54e5\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u53bb\u65e5\u5580\u5219\u3002\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u90a3\u54b1\u4eec\u90fd\u662f\u540c \u8def\u3002\u201d\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u4f60\u4ece\u90a3\u8fb9\u6765\uff0c\u600e\u4e48\u4e5f\u662f\u53bb\u65e5\u5580\u5219\uff1f\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u6211\u5bfb\u89c5\u5c71\u6cc9\uff0c \u5c71\u8def\u7eb7\u6b67\uff0c\u7ed5\u6765\u7ed5\u53bb\uff0c\u7ed5\u5230\u56de\u5934\u8def\u4e86\u3002\u5440\uff0c\u597d\u6e34\uff0c\u597d\u6e34\uff01\u5c0f\u54e5\uff0c\u4f60\u505a\u597d\u4eba\u505a\u5230\u5e95\uff0c\u518d\u8ba9\u6211\u559d \u4e24\u53e3\u6c34\u3002\u201d\u9648\u5929\u5b87\u65e0\u5948\u89e3\u4e0b\u6c34\u56ca\uff0c\u770b\u90a3\u5c11\u5e74\u5927\u53e3\u5927\u53e3\u7684\u51e0\u4e4e\u559d\u53bb\u4e00\u534a\uff0c\u5fc3\u4e2d\u751a\u662f\u75db\u60dc\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u559d\u9971\u4e86\u6c34\uff0c\u4e00\u4fa7\u8eab\u5c31\u4ece\u90a3\u7c97\u8c6a\u6c49\u5b50\u7684\u9a6c\u65c1\u7a9c\u8fc7\uff0c\u8eab\u6cd5\u7adf\u7136\u751a\u5feb\uff0c\u90a3\u6c49\u5b50\u4e00\u63d0 \u9a6c\u7f30\uff0c\u672c\u60f3\u628a\u9a6c\u5934\u62e8\u8f6c\uff0c\u5413\u4e00\u5413\u4ed6\uff0c\u5c82\u77e5\u4ed6\u5df2\u8c61\u6c34\u86c7\u822c\u7684\u6ed1\u8fc7\uff0c\u4e0d\u7531\u5f97\u5fae\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u5c11\u5e74 \u5df1\u98de\u8eab\u4e0a\u9a6c\uff0c\u5411\u9648\u5929\u5b87\u62f1\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u6211\u5e26\u8def\u5148\u8d70\u4e86\u3002\u201d\u90a3\u7c97\u8c6a\u6c49\u5b50\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u8c01\u8981\u4f60 \u5e26\u8def\uff1f\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u53ea\u5f53\u5e76\u4e0d\u542c\u95fb\uff0c\u62e8\u9a6c\u5f84\u884c\u3002 \u3000\u3000\u90a3\u7c97\u8c6a\u6c49\u5b50\u6124\u6124\u4e0d\u5e73\uff0c\u4e0d\u4f4f\u7684\u56de\u5934\u548c\u4ed6\u7684\u4e24\u4e2a\u540c\u4f34\u53fd\u54e9\u5495\u55dc\u7684\u5927\u8bf4\u6c5f\u6e56\u9ed1\u8bdd\uff0c\u9648\u5929\u5b87\u4e00 \u53e5\u4e5f\u542c\u4e0d\u61c2\uff0c\u5374\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u65e5\u5f71\u6c89\u897f\uff0c\u5c71\u98ce\u9661\u8d77\uff0c\u6b63\u89c9\u5bd2\u51b7\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u5636\u5636\u58f0\u54cd\uff0c\u8ddf \u5728\u8427\u9752\u5cf0\u9a6c\u540e\u7684\u90a3\u4eba\u559c\u9053\uff1a\u201d\u6211\u4eec\u6b63\u6101\u4eca\u665a\u627e\u4e0d\u5230\u6b47\u606f\u4e4b\u6240\uff0c\u5374\u559c\u9047\u7740\u6e29\u6cc9\u4e86\u3002\u8f6c\u8fc7\u4e00\u4e2a\u5c71 \u5773\uff0c\u524d\u9762\u5730\u5f62\u5bbd\u5766\uff0c\u5ca9\u77f3\u7f1d\u95f4\u55b7\u51fa\u4e00\u56e2\u56e2\u84b8\u6c14\uff0c\u707c\u70ed\u7684\u706b\u82b1\uff0c\u98de\u6e85\u7a7a\u4e2d\uff0c\u5728\u6de1\u6de1\u659c\u8f89\u6620\u5c04\u4e4b \u4e0b\uff0c\u5f62\u6210\u4e00\u5708\u5708\u6a59\u8272\u7684\u3001\u6de1\u7d2b\u548c\u6d45\u7ea2\u7684\u82b1\u6735\uff0c\u5047\u5982\u5143\u5bb5\u4f73\u8282\u6240\u653e\u7684\u70df\u82b1\uff0c\u5341\u5206\u7f8e\u4e3d\u3002 \u3000\u3000\u539f\u6765\u897f\u85cf\u9ad8\u539f\uff0c\u5730\u4e0b\u5230\u5904\u90fd\u6709\u706b\u5c71\uff0c\u6709\u4e9b\u55b7\u53d1\u51fa\u6765\uff0c\u6210\u4e3a\u55b7\u6cc9\uff0c\u4e43\u662f\u897f\u85cf\u7684\u4e00\u79cd\u5929\u7136\u5947 \u666f\uff0c\u6709\u4e9b\u55b7\u6cc9\u7684\u6e29\u5ea6\u53ef\u8fbe\u534e\u6c0f\u4e00\u767e\u4e94\u5341\u5ea6\uff0c\u897f\u85cf\u7684\u5c71\u8c37\u91cc\u71c3\u6599\u5f88\u5c11\uff0c\u5f53\u5730\u4eba\u975e\u5e38\u73cd\u60dc\u8fd9\u79cd\u70ed \u6c34\uff0c\u4ed6\u4eec\u5e38\u5e38\u628a\u98ce\u5e72\u7684\u8089\u5757\u6813\u5728\u7ef3\u5b50\u4e0a\uff0c\u653e\u5165\u55b7\u6cc9\u7684\u70ed\u6c34\u91cc\uff0c\u7ecf\u8fc7\u51e0\u5c0f\u65f6\u4e4b\u540e\uff0c\u8fd9\u5757\u8089\u4fbf\u716e \u719f\u4e86\u3002 \u3000\u3000\u55b7\u6cc9\u9644\u8fd1\uff0c\u548c\u6696\u5982\u6625\uff0c\u6b63\u662f\u65c5\u4eba\u6700\u597d\u7684\u6b47\u5bbf\u4e4b\u6240\uff0c\u800c\u4e14\u8fd9\u79cd\u70ed\u6c34\u7ecf\u8fc7\u6ee4\u51b7\u4e4b\u540e\uff0c\u53c8\u662f\u6700 \u597d\u7684\u996e\u6599\uff0c\u56e0\u6b64\u4e00\u884c\u4eba\u90fd\u6781\u559c\u6b22\uff0c\u4fbf\u5728\u55b7\u6cc9\u9644\u8fd1\u6b47\u4e0b\u9a6c\u6765\uff0c\u652f\u8d77\u5e10\u84ec\uff0c\u90a3\u4e09\u4e2a\u6c49\u5b50\u81ea\u505a\u4e00 \u9053\uff0c\u9648\u5929\u5b87\u89c1\u90a3\u5c11\u5e74\u4e66\u751f\u5b64\u8eab\u4e00\u4eba\uff0c\u6015\u4ed6\u53d7\u90a3\u4f19\u6b3a\u8d1f\uff0c\u4fbf\u6084\u6084\u5e08\u5085\u5546\u91cf\uff0c\u601d\u8bf7\u90a3\u5c11\u5e74\u8fdb\u4ed6\u4eec \u7684\u5e10\u84ec\u540c\u4f4f\uff0c\u5ffd\u89c1\u5e08\u5085\u800c\u8272\u6c89\u91cd\uff0c\u5fae\u5fae\u6447\u4e86\u6447\u5934\uff0c\u9648\u5929\u5b87\u53ea\u5f97\u7f62\u4e86\u3002 \u3000\u3000\u559d\u4e86\u70ed\u6c34\uff0c\u5403\u4e86\u5e72\u7cae\uff0c\u5404\u4eba\u8eb2\u8fdb\u5e10\u7bf7\uff0c\u9648\u5929\u5b87\u4f4e\u58f0\u95ee\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5e08\u5085\u53ef\u77a7\u51fa\u90a3\u5c11\u5e74\u6709 \u4ec0\u4e48\u4e0d\u5bf9\u4e48\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u4e66\u751f\u7684\u8def\u9053\u6211\u6ca1\u6709\u77a7\u51fa\uff0c\u90a3\u4e09\u4e2a\u6c49\u5b50\u5374\u662f\u6211\u7684\u5bf9\u5934\uff01\u201d \u9648\u5929\u5b87\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u8fd9\u53ef\u600e\u751f\u662f\u597d\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u6811\u4e0b\u4e09\u4e2a\u5f3a\u654c\uff0c\u524d\u65e5\u5230 \u8428\u8fe6\u627e\u6211\u5bfb\u4ec7\u7684\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u53eb\u738b\u7624\u5b50\uff0c\u4e00\u4e2a\u53eb\u5d14\u4e91\u5b50\uff0c\u738b\u7624\u5b50\u6b66\u529f\u8fdc\u900a\u4e8e\u6211\uff0c\u5d14\u4e91\u5b50 \u5374\u548c\u6211\u5dee\u4e0d\u591a\uff0c\u8fd9\u4e24\u4eba\u4e5f\u8fd8\u7f62\u4e86\uff0c\u53e6\u6709\u4e00\u4e2a\u5bf9\u5934\u5374\u662f\u5f53\u4eca\u6b66\u5f53\u6d3e\u7684\u7b2c\u4e00\u9ad8\u624b\u96f7\u9707\u5b50\uff0c\u6b66\u529f\u8fdc \u8fdc\u5728\u6211\u4e4b\u4e0a\uff0c\u6211\u4e3a\u4e86\u907f\u4ed6\uff0c\u8fd9\u624d\u8fdc\u9005\u8fb9\u8352\uff0c\u90a3\u77e5\u8fd8\u662f\u907f\u4ed6\u4e0d\u4e86\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u90a3\u4e09\u4e2a\u4eba\u4e2d\u6709 \u4e00\u662f\u4e2a\u96f7\u9707\u5b50\u5417\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u82e5\u662f\u96f7\u9707\u5b50\uff0c\u6211\u65e9\u5c31\u6ca1\u547d\u4e86\uff0c\u8fd9\u4e09\u4e2a\u4eba\u4e43\u662f\u96f7\u9707\u5b50\u7684\u5f92 \u5f1f\uff0c\u6211\u521a\u624d\u5728\u9014\u4e2d\u542c\u4ed6\u4eec\u7528\u6c5f\u6e56\u5207\u53e3\u4ea4\u8c08\uff0c\u539f\u6765\u4ed6\u4eec\u662f\u5949\u5e08\u5085\u4e4b\u547d\uff0c\u6765\u627e\u738b\u9ebb\u5b50\u4e0e\u5d14\u4e91\u5b50 \u7684\uff0c\u800c\u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\u6211\u5c31\u662f\u4ed6\u4eec\u5e08\u5085\u7684\u5bf9\u5934\uff0c\u4f46\u4ed6\u4eec\u5374\u6000\u7591\u90a3\u5c11\u5e74\u4e66\u751f\u662f\u6211\u7684\u5f92\u5f1f\uff0c\u6240\u4ee5\u4e5f \u6697\u6697\u628a\u4ed6\u76ef\u4e0a\u4e86\u3002\u90a3\u5c11\u5e74\u4e66\u751f\u60f3\u6765\u4e5f\u662f\u4e2a\u6709\u672c\u9886\u4e4b\u4eba\uff0c\u662f\u53cb\u662f\u654c\uff0c\u5374\u672a\u5206\u6653\uff0c\u603b\u4e4b\u4f60\u8981\u6b65\u6b65 \u5c0f\u5fc3\uff0c\u4e07\u4e0d\u53ef\u8ba9\u4ed6\u4eec\u77a7\u51fa\u7834\u9648\u5929\u5b87\u5fc3\u4e2d\u63e3\u63e3\uff0c\u8eba\u5728\u5e10\u7bf7\u4e4b\u4e2d\uff0c\u7ffb\u6765\u8986\u53bb\uff0c\u600e\u6837\u4e5f\u7761\u4e0d\u7740\uff0c\u4e5f \u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u4faf\uff0c\u8fdc\u5904\u9690\u9690\u4f20\u6765\u4e00\u9635\u54ed\u6ce3\u4e4b\u58f0\uff0c\u51c4\u51c4\u5207\u5207\uff0c\u60e8\u5389\u9a87\u4eba\uff0c\u8352\u8c37\u6df1\u5bb5\uff0c\u5982\u95fb\u9b3c \u54ed\u3002\u521d\u521d\u4e00\u542c\uff0c\u4e0d\u89c9\u6bdb\u9aa8\u609a\u7136\uff0c\u518d\u542c\u771f\u4e86\uff0c\u8fd9\u54ed\u58f0\u7adf\u4f3c\u66fe\u76f8\u8bc6\uff0c\u9648\u5929\u5b87\u7ffb\u8eab\u8df3\u8d77\uff0c\u8427\u9752\u5cf0 \u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u542c\u8fd9\u5973\u4eba\u7684\u54ed\u58f0\uff0c\u597d\u8c61\u662f\u9047\u5230\u751a\u4e48\u4e0d\u5e78\u4e4b\u4e8b\uff0c\u8c61 \u8fd8\u5728\u547c\u6551\u5462\u3002\u201d\u8427\u9752\u5cf0\u4e24\u773c\u53d1\u5149\uff0c\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u5b87\u513f\uff0c\u4f60\u53bb\u77a7\u77a7\u3002\u201d\u9648\u5929\u5b87\u4e00\u9707\uff0c\u9053\uff1a \u201c\u4e0d\uff0c\u6211\u966a\u5e08\u5085\u3002\u201d\u987b\u77e5\u8427\u9752\u5cf0\u6b66\u529f\u867d\u6781\u9ad8\u5f3a\uff0c\u4f46\u53cc\u624b\u4e0d\u80fd\u8f6c\u52a8\uff0c\u4e0e\u5e9f\u4eba\u4e5f\u5dee\u4e0d\u591a\uff0c\u82e5\u7136\u5bf9 \u5934\u6765\u88ad\u3002\u600e\u80fd\u5e94\u4ed8\uff0c\u6240\u4ee5\u9648\u5929\u5b87\u867d\u7136\u60e6\u5ff5\u90a3\u4e2a\u5973\u5b50\uff0c\u5374\u4e0d\u6562\u79bb\u5f00\u5e08\u5085\uff0c\u90a3\u77e5\u8427\u9752\u5cf0\u53cc\u773c\u4e00 \u7ffb\uff0c\u5374\u9053\uff1a\u201c\u6211\u8f88\u4fa0\u4e49\u4e2d\u4eba\u3001\u5c82\u6709\u89c1\u6b7b\u4e0d\u6551\u4e4b\u7406\uff1f\u4f60\u542c\u90a3\u5973\u5b50\u54ed\u5f97\u5982\u6b64\u51c4\u60e8\uff0c\u82e5\u975e\u9047\u7740\u5f3a \u4eba\uff0c\u5c31\u662f\u60f3\u5bfb\u81ea\u5c3d\uff0c\u4f60\u4ec5\u7ba1\u53bb\uff0c\u6211\u8fd8\u53ef\u4ee5\u81ea\u5df1\u7167\u6599\u81ea\u5df1\u3002\u53bb\uff0c\u5feb\u53bb\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u4e00\u9635\u8fdf\u7591\uff0c\u90a3\u5973\u5b50\u54ed\u58f0\u53c8\u8d77\uff0c\u8427\u9752\u5cf0\u6012\u9053\uff1a\u201c\u4e8b\u6709\u7f13\u6025\u8f7b\u91cd\uff0c\u73b0\u5728\u6551\u90a3\u5973\u5b50\u8981 \u7d27\uff0c\u4f60\u600e\u4e48\u4e0d\u542c\u6211\u7684\u8bdd\uff1f\u53bb\uff01\u5feb\u53bb\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u5085\uff0c\u90a3\u4f60\u597d\u751f\u4fdd\u91cd\uff0c\u5f1f\u5b50\u53bb\u53bb\u5c31 \u56de\u3002\u201d\u6084\u6084\u6e9c\u51fa\u5e10\u84ec\uff0c\u5e78\u5728\u90a3\u4f19\u4eba\u65e0\u4eba\u53d1\u89c9\uff0c\u9648\u5929\u5b87\u6025\u5fd9\u65bd\u5c55\u5e08\u5085\u6240\u6388\u7684\u8f7b\u529f\uff0c\u5bfb\u58f0\u89c5\u8ff9\uff0c \u627e\u90a3\u54ed\u6ce3\u7684\u5973\u4eba\u3002 \u3000\u3000\u9648\u5929\u5b87\u7684\u529f\u592b\u4e43\u662f\u6697\u4e2d\u6240\u5b66\uff0c\u62ff\u6765\u5b9e\u7528\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\uff0c\u5c71\u9053\u9669\u5cfb\uff0c\u602a\u77f3\u9cde\u5ccb\uff0c\u53c8\u66f4\u517c\u662f \u591c\u95f4\uff0c\u4ed6\u65bd\u5c55\u8f7b\u529f\u63d0\u7eb5\u4e4b\u672f\uff0c\u5438\u4e00\u53e3\u6c14\uff0c\u98de\u63a0\u6570\u4e08\uff0c\u5374\u4e0d\u6599\u53bb\u52bf\u592a\u6025\uff0c\u8db3\u5c16\u4e00\u6ed1\uff0c\u6454\u4e86\u4e00 \u8de4\uff0c\u5ffd\u542c\u5f97\u9759\u591c\u4e4b\u4e2d\uff0c\u4e0d\u8fdc\u4e4b\u5904\uff0c\u4f3c\u6709\u4eba\u53d1\u58f0\u51b7\u7b11\uff0c\u9648\u5929\u5b87\u6025\u5fd9\u722c\u8d77\uff0c\u5f20\u76ee\u56db\u987e\uff0c\u5374\u53ea\u89c1\u8fdc \u5904\u51b7\u5cf0\u95ea\u95ea\u53d1\u5149\uff0c\u8fdc\u5904\u55b7\u6cc9\u70ed\u96fe\u817e\u817e\uff0c\u90a3\u91cc\u6709\u4eba\u7684\u5f71\u5b50\uff1f\u9648\u5929\u5b87\u5b9a\u4e86\u5b9a\u795e\uff0c\u9f13\u8d77\u52c7\u6c14\uff0c\u518d\u5f80 \u524d\u8d70\uff0c\u8fd9\u56de\u4efd\u5916\u5c0f\u5fc3\uff0c\u8e0f\u5b9e\u4e86\u624d\u8ba9\u8eab\u5f62\u843d\u4e0b\uff0c\u867d\u7136\u4e0d\u4f3c\u9002\u624d\u4e4b\u5feb\uff0c\u5374\u4e0b\u518d\u8dcc\u8de4\u4e86\u3002\u90a3\u5c11\u5973\u7684 \u54ed\u58f0\u65f6\u65ad\u65f6\u7eed\uff0c\u9648\u5929\u5b87\u89c5\u58f0\u89c5\u8ff9\uff0c\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6765\u5230\u4e86\u4e00\u4e0a\u51b0\u53f0\u524d\u9762\u3002 \u3000\u3000\u53ea\u89c1\u51b0\u5ca9\u4e0a\u7acb\u7740\u4e00\u4e2a\u5c11\u5973\uff0c\u6b63\u662f\u795e\u79d8\u7684\u85cf\u65cf\u59d1\u5a18\uff0c\u53ea\u542c\u5979\u54ed\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\uff0c\u6211\u540e\u6094\u6ca1 \u6709\u8ddf\u4f60\u591a\u5b66\u51e0\u65e5\u6b66\u529f\uff0c\u800c\u4eca\u4ec7\u4e0d\u80fd\u62a5\uff0c\u53cd\u7ed9\u654c\u4eba\u8feb\u5f97\u65e0\u8def\u53ef\u9003\uff0c\u5440\uff0c\u7238\u7238\u5988\u5988\uff0c\u82e6\u547d\u7684\u5973\u513f \u8fd8\u662f\u8ddf\u4f60\u4eec\u53bb\u5427\uff01\u201d\u9648\u5929\u5b87\u5927\u9a87\uff0c\u5ffd\u89c1\u90a3\u5c11\u5973\u4f5c\u52bf\u6b32\u8df3\uff0c\u5374\u53c8\u4e0d\u8df3\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u6211\u62fc\u5f97\u4e00 \u4e2a\u662f\u4e00\u4e2a\uff0c\u597d\uff0c\u6765\u5427\uff0c\u6765\u5427\uff01\u201d\u9648\u5927\u5b57\u79bb\u51b0\u5ca9\u8fd8\u6709\u5341\u6765\u4e08\uff0c\u4e14\u6709\u5927\u77f3\u969c\u5f62\uff0c\u90a3\u5973\u5b50\u53c8\u4e0d\u662f\u9762 \u5bf9\u81ea\u5df1\u8fd9\u8fb9\uff0c\u770b\u6765\u53c8\u4e0d\u4f3c\u53d1\u73b0\u81ea\u5df1\u3002 \u3000\u3000\u9648\u5929\u5b87\u5fc3\u5934\u7a0d\u7a0d\u653e\u5bbd\uff0c\u77e5\u9053\u8fd9\u5c11\u5973\u8fd8\u65e0\u610f\u81ea\u5c3d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979\u8981\u62a5\u4ec0\u4e48\u4ec7\uff1f\u83ab\u975e\u5979\u7684 \u4ec7\u4eba\u5c31\u662f\u90a3\u4e2a\u571f\u53f8\uff0c\u82e5\u7136\u662f\u90a3\u571f\u53f8\uff0c\u90a3\u4e48\u571f\u53f8\u5c31\u7edd\u4e0d\u4f1a\u56e0\u6211\u7238\u7238\u6c42\u60c5\uff0c\u5c31\u9976\u5979\u4e00\u6b7b\u3002\u90a3\u65e5\uff0c \u571f\u53f8\u4e5f\u53ea\u662f\u8bf4\u5979\u60f3\u5077\u9a6c\uff0c\u53ef\u5e76\u6ca1\u6709\u5176\u4ed6\u7684\u7f6a\u540d\u5440\uff01\u201d \u3000\u3000\u800c\u4e14\u571f\u53f8\u867d\u7136\u6b8b\u66b4\uff0c\u8bf4\u8bdd\u5374\u662f\u8bf4\u4e00\u4e0d\u4e8c\uff0c\u90a3\u65e5\u6211\u98de\u5200\u5288\u679c\uff0c\u571f\u53f8\u5f53\u7740\u4f17\u4eba\u91ca\u653e\u4e86\u5979\uff0c\u96be \u9053\u53c8\u4f1a\u6697\u4e2d\u6d3e\u4eba\u53bb\u8ffd\u6355\u5979\uff1f\u82e5\u7136\u4e0d\u662f\uff0c\u4e3a\u4f55\u5979\u53c8\u8bf4\u7ed9\u654c\u4eba\u8feb\u5f97\u65e0\u8def\u53ef\u8d70\uff0c\u201d\u767e\u60f3\u4e0d\u5f97\u5176\u89e3\uff0c \u53c8\u60f3\u9053\uff1a\u201c\u90a3\u5929\u5973\u53c8\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u600e\u4e48\u540d\u5b57\u8d77\u5f97\u5982\u6b64\u4e4b\u602a\uff1f\u201d\u7591\u96fe\u91cd\u91cd\uff0c\u6b63\u60f3\u4ece\u77f3\u540e\u8d70\u51fa\uff0c \u722c\u4e0a\u51b0\u5ca9\u5ffd\u542c\u5f97\u513f\u5c11\u5973\u4e00\u58f0\u5389\u53eb\uff0c\u626c\u624b\u5c31\u662f\u4e00\u9053\u94f6\u5149\uff0c\u539f\u6765\u5979\u4e5f\u4f1a\u98de\u5200\uff0c\u9648\u5929\u5b87\u8fd8\u672a\u770b\u6e05\uff0c \u53ea\u89c1\u90a3\u5c11\u5973\u4f3c\u662f\u9aa4\u7136\u7528\u529b\uff0c\u4e00\u4e2a\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dcc\u4e86\u4e0b\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u8bc0\uff0c\u51b0\u5ca9\u7684\u8f6c\u89d2\u52a9\u5904\uff0c \u7a81\u7136\u7a9c\u4e0a\u4e00\u4eba\uff0c\u4e00\u628a\u5c06\u5979\u6293\u7740\uff0c\u518d\u770b\u771f\u65f6\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff1b\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u90a3\u65e5\u54c0\u6c42\u9648\u5929 \u5b87\u7684\u7236\u4eb2\u53bb\u6551\u90a3\u85cf\u65cf\u5c11\u5973\u7684\u4fc4\u9a6c\u767b\uff0c\u4e5f\u5c31\u662f\u571f\u53f8\u624b\u4e0b\u56db\u5927\u6d85\u5df4\u4e4b\u4e00\u7684\u4fc4\u9a6c\u767b\u3002\u60f3\u4e0d\u5230\u8fd9\u4e2a\u8d2a \u8d22\u7684\u6d85\u5df4\uff0c\u8eab\u8eaf\u80a5\u80d6\u3002\u5e73\u65e5\u8d70\u8def\u4e5f\u4e0d\u81ea\u7136\uff0c\u5982\u4eca\u7a9c\u4e0a\u60ac\u5ca9\uff0c\u8eab\u624b\u7adf\u7136\u662f\u5982\u6b64\u5229\u843d\uff01\u8fd9\u970e\u90a3 \u95f4\uff0c\u9648\u5929\u5b87\u60ca\u5947\u5f97\u53eb\u4e5f\u53eb\u4e0d\u51fa\u6765\uff0c\u624b\u4e2d\u634f\u7740\u4e00\u628a\u98de\u5200\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u8fd9\u6d85\u5df4\u6562\u4f24\u5bb3\u5979\uff0c\u6211\u5c31 \u4e00\u4e00\u5200\u6420\u4ed6\u5589\u5499\uff01\u201d \u3000\u3000\u9ad8\u539f\u4e4b\u4e0a\uff0c\u5bd2\u98ce\u523a\u9aa8\uff0c\u9648\u5929\u5b87\u5374\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u624b\u4e2d\u7d27\u7d27\u634f\u7740\u98de\u5200\uff0c\u4ed6\u5374\u4e0d\u60f3\uff0c\u90a3\u6d85\u5df4\u6b66 \u529f\u5728\u4ed6\u4e4b\u4e0a\uff0c\u82e5\u7136\u4e00\u63b7\u4e0d\u4e2d\uff0c\u5c82\u975e\u767d\u767d\u966a\u4e86\u6027\u547d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u653e\u5f00\u3002\u6211\u5b66\u827a\u4e0d\u7cbe\uff0c\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u6b64\u4ec7\u65e2\u4e0d\u53ef\u62a5\uff0c\u5c31\u8ba9\u6211\u81ea\u5df1 \u8df3\u4e0b\u60ac\u5ca9\uff0c\u4f60\u65e2\u53d7\u571f\u53f8\u4e4b\u547d\u6765\u8ffd\u6355\u6211\uff0c\u5c31\u8be5\u77e5\u9053\u6211\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u6211\u5c82\u80fd\u53d7\u4f60\u8fd9\u53ae\u4fae\u8fb1\uff1f\u201d\u90a3 \u4fc4\u9a6c\u767b\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u5047\u540d\u53eb\u505a\u6851\u9a6c\u3002\u771f\u540d\u53eb\u505a\u829d\u5a1c\uff0c\u4f60\u662f\u6c81\u5e03\u85e9\u738b\u7684\u5973 \u513f\uff01\u201d\u90a3\u5c11\u5973\u5389\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u8fd8\u80c6\u6562\u653e\u8086\u3002\u85e9\u738b\u7684\u5973\u513f\u53ea\u80fd\u81ea\u5c3d\uff0c\u4e0d\u80fd\u53d7\u4eba\u4fae \u8fb1\uff0c\u6211\u8df3\u4e0b\u60ac\u5d16\u4e4b\u540e\uff0c\u4f60\u518d\u7528\u5229\u5200\u5272\u4e0b\u6211\u7684\u5934\uff01\u201d\u4fc4\u9a6c\u767b\u4ecd\u662f\u6293\u7d27\u5979\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60 \u53c8\u77e5\u9053\u6211\u662f\u4f55\u7b49\u4eba\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u4f60\u662f\u8428\u8fe6\u571f\u53f8\u7684\u8d70\u72d7\uff01\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u8bf4\u9519\u4e86\u3002\u6211 \u4e5f\u662f\u571f\u53f8\u7684\u4ec7\u4eba\uff0c\u6211\u6b64\u6765\u662f\u6551\u4f60\u7684\u3002\u201d\u829d\u5a1c\u4f3c\u662f\u6014\u4e86\u4e00\u6014\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6765\u8ffd\u6355\u6211 \u7684\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u4e0a\u53f8\u5e76\u4e0d\u77e5\u9053\u4f60\u662f\u85e9\u738b\u7684\u5973\u513f\uff0c\u82e5\u7136\u4ed6\u77e5\u9053\uff0c\u4ed6\u81ea\u7136\u4f1a\u6d3e\u4eba\u6765\u8ffd\u6355 \u4f60\u3002\u201d\u5a1c\u829d\u7f13\u4e86\u53e3\u6c14\uff0c\u4fc4\u9a6c\u767b\u653e\u5f00\u4e86\u624b\u9053\uff1a\u201c\u4f60\u52c7\u6c14\u53ef\u5609\u3002\u5374\u662f\u592a\u50bb\u3002\u201d\u829d\u5a1c\u9053\uff1a\u201c\u600e \u4e48\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201d\u4f60\u4e5f\u4e0d\u60f3\u60f3\u571f\u53f8\u624b\u4e0b\u6709\u591a\u5c11\u80fd\u4eba\uff1f\u4f60\u5b64\u8eab\u4e00\u4eba\uff0c\u5c31\u6562\u8dd1\u6765\u62a5\u4ec7\uff0c\u6211\u81ea\u95ee \u6b66\u529f\u6bd4\u4f60\u9ad8\u5f3a\uff0c\u8fd9\u4e48\u591a\u5e74\uff0c\u4e5f\u53ea\u6709\u66f4\u540d\u6539\u59d3\uff0c\u5728\u571f\u53f8\u624b\u4e0b\u505a\u4e2a\u6d85\u5df4\uff0c\u542c\u4ed6\u4f7f\u5524\uff0c\u62a5\u4ec7\u8981\u7b49\u65f6 \u673a\uff0c\u6c49\u4eba\u6709\u9053\uff0c\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u4e0d\u665a\u3002\u8fd9\u53e5\u8bdd\u4f60\u6ca1\u542c\u8fc7\u5417\uff1f\u201d\u829d\u5a1c\u773c\u4e2d\u6ef4\u4e0b\u6cea\u73e0\uff0c\u4f3c\u662f\u5bf9\u8fd9 \u6d85\u5df4\u5df2\u7ecf\u5341\u5206\u76f8\u4fe1\uff0c\u4fc4\u9a6c\u767b\u5ffd\u9053\uff1a\u201c\u4f60\u8fd9\u6b66\u529f\u662f\u8c01\u6559\u7684\uff1f\u201d\u829d\u5a1c\u9053\uff0c\u201c\u51b0\u5ddd\u5929\u5973\uff01\u201d\u4fc4\u9a6c\u767b \u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u3002\u771f\u7684\u662f\u51b0\u5ddd\u5929\u5973\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u5979\u4e0d\u80af\u505a\u6211\u7684\u5e08\u5085\uff0c\u5979\u53ea\u6559\u4e86 \u6211\u4e09\u65e5\u6b66\u529f\u3002\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u6211\u5c31\u4fe1\u4e86\u201d\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u663e\u7136\u662f\u90a3\u51b0\u5ddd\u5929\u5973\u7684\u6b66\u529f\u9ad8 \u5f3a\u4e4b\u6781\uff0c\u82e5\u7136\u771f\u662f\u5979\u7684\u5f1f\u5b50\uff0c\u6b66\u529f\u7edd\u5bf9\u4e0d\u4f1a\u5bfb\u5e38\u3002\u53ea\u542c\u5f97\u4fc4\u9a6c\u767b\u53c8\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u4f4f\u5728\u4ec0\u4e48 \u5730\u65b9\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u4f4f\u5728\u5929\u6e56\u3002\u5979\u7684\u540d\u5b57\uff0c\u5916\u95f4\u5c11\u4eba\u77e5\u9053\u3002\u4f60\u600e\u4f1a\u8ba4\u8bc6\u5979\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a \u201c\u6211\u5e76\u4e0d\u8ba4\u8bc6\u5979\uff0c\u53ef\u662f\u6211\u77e5\u9053\u6709\u4eba\u8981\u627e\u5979\uff0c\u201d\u5ffd\u7136\u4f4e\u58f0\u5411\u829d\u5a1c\u8bf4\u4e86\u51e0\u53e5\uff0c\u9648\u5929\u5b87\u5728\u5ca9\u4e0b\u542c\u4e0d \u6e05\u695a\uff0c\u4f46\u89c1\u829d\u5a1c\u70b9\u4e86\u70b9\u5934\uff0c\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u4ece\u6c34\u8c37\u4e0b\u9762\u90a3\u6761\u8def\u9003\u51fa\u53bb\u5427\uff0c\u6211\u8fd9\u6709\u4e00\u652f\u571f \u53f8\u7684\u4ee4\u7bad\u3002\u4f60\u62ff\u4e86\uff0c\u5df2\u6ca1\u4eba\u6562\u9a9a\u6270\u4f60\uff0c\u54a6\uff0c\u8fdc\u5904\u4f3c\u6709\u4eba\u58f0\uff0c\u4f60\u8eb2\u8d77\u6765\uff0c\u6211\u5148\u8d70\u4e86\u3002\u201d\u9648\u5929\u5b87 \u7ad6\u8033\u7ec6\u542c\uff0c\u5374\u4e00\u70b9\u4e5f\u542c\u4e0d\u51fa\u6765\uff0c\u90a3\u6d85\u5df4\u53d6\u51fa\u4e00\u6839\u957f\u7ef3\uff0c\u5c31\u4ece\u51b0\u5ca9\u4e0a\u60ac\u5ca9\u800c\u4e0b\uff0c\u9648\u5929\u5b87\u5077\u773c\u4e00 \u77a5\uff0c\u5ffd\u89c1\u5728\u51b7\u6708\u5bd2\u51b0\u7684\u5f71\u7167\u4e4b\u4e0b\uff0c\u4fc4\u9a6c\u767b\u7684\u9762\u4e0a\u73b0\u51fa\u4e00\u79cd\u4ee4\u4eba\u6bdb\u9aa8\u8038\u7136\u7684\u5978\u6ed1\u7b11\u5bb9\uff0c\u9648\u5929\u5b87 \u624d\u542c\u4e86\u4ed6\u90a3\u5e2d\u8bdd\uff0c\u672c\u6765\u5bf9\u4ed6\u7684\u6076\u611f\u7a0d\u6d88\uff0c\u4ee5\u4e3a\u4ed6\u662f\u597d\u4eba\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u89c1\u4e86\u4ed6\u8fd9\u7b11\u5bb9\uff0c\u5fc3\u4e2d\u65e0 \u9650\u538c\u70e6\uff0c\u66f4\u589e\u7591\u8651\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7f13\u7f13\u8f6c\u8fc7\u4e86\u5934\uff0c\u5ffd\u7136\u5411\u9648\u5929\u5b87\u8eb2\u85cf\u4e4b\u5904\u62db\u624b\u9053\uff1a\u201c\u4f60\u51fa\u6765\u5427\uff0c\u6211\u77a7\u89c1\u4f60\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u8f7b\u8f7b\u8d70\u4e0b\u51b0\u5ca9\uff0c\u9648\u5929\u5b87\u5fc3\u5934\u535c\u535c\u5730\u8df3\uff0c\u4e0d\u77e5\u600e\u7684\u3002\u4ed6\u662f\u4e3a\u6551\u5979\u800c\u6765\uff0c\u800c\u4eca\u89c1\u4e86\uff0c \u5374\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002\u90a3\u5c11\u5973\u8d70\u5230\u9648\u5929\u5b87\u9762\u524d\uff0c\u5ffd\u5730\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6551\u6211\u8fd9\u82e6\u547d\u7684\u5973 \u4eba\u3002\u201d\u9648\u5929\u5b87\u6d3b\u5230\u5341\u516b\u5c81\uff0c\u4ece\u672a\u4e0e\u964c\u751f\u7684\u5973\u90ce\u8bf4\u8fc7\u8bdd\uff0c\u751a\u662f\u817c\u8146\u4e0d\u5b89\uff0c\u4f46\u770b\u8fd9\u5c11\u5973\u7684\u795e\u60c5\uff0c \u867d\u7136\u8fd8\u4f3c\u524d\u5728\u571f\u53f8\u5bb6\u4e2d\u6240\u89c1\u90a3\u6837\uff0c\u5e26\u7740\u51e0\u5206\u51b7\u50b2\uff0c\u4f46\u5634\u89d2\u6302\u7740\u7684\u90a3\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u5374\u4f3c\u51b0\u8c37\u4e2d \u7efd\u5f00\u7684\u82b1\u6735\uff0c\u51cf\u5c11\u4e86\u4e0d\u5c11\u5bd2\u610f\uff0c\u4ee4\u9648\u7530\u91ce\u6d88\u9664\u4e86\u602f\u4ff1\u3002\u9648\u5929\u5b87\u4e0d\u81ea\u89c9\u7684\u62a5\u4ee5\u4e00\u7b11\uff0c\u62bd\u51fa\u4e86\u4e00 \u6761\u767d\u8272\u4e1d\u4e2d\uff0c\u4f9d\u7740\u85cf\u65cf\u7684\u4eea\u793c\uff0c\u5448\u732e\u201c\u54c8\u8fbe\u201d\uff0c\u90a3\u5c11\u5973\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u53cc\u6307\u4e00\u62c8\uff0c\u628a\u4e1d\u5dfe\u63a5 \u4e86\u8fc7\u6765\uff0c\u653e\u5165\u6000\u4e2d\uff0c\u9053\uff0c\u201c\u591a\u8c22\u4f60\u7684\u793c\u7269\uff0c\u4f60\u6765\u4e86\u8bb8\u4e45\u5475\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u521a\u624d\u7684\u60c5\u666f\u627e\u90fd \u770b\u5230\u4e86\uff0c\u5b9e\u662f\u6599\u60f3\u4e0d\u5230\uff0c\u539f\u6765\u4f60\u662f\u6211\u4eec\u5c0a\u8d35\u7684\u6c5f\u739b\u4fee\uff08\u5c0f\u59d0\uff09\u3002\u201d\u90a3\u5c11\u5973\u622a\u7740\u9053\uff1a\u201c\u6211\u7684\u4e8b \u60c5\u4f60\u4e0d\u5fc5\u63d0\uff0c\u6211\u4eec\u85cf\u65cf\u6709\u53e5\u8c1a\u8bed\uff1a\u2018\u665a\u4e0a\u6240\u505a\u7684\u68a6\uff0c\u65e5\u5929\u4e0d\u8981\u8bf4\u5b83\u3002\u2019\u610f\u601d\u662f\u8bf4\uff0c\u8fc7\u53bb\u79cd \u79cd\uff0c\u6709\u5982\u68a6\u5883\uff0c\u8bf4\u8d77\u6765\u5f92\u589e\u4f24\u611f\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e00\u9635\u5c34\u5c2c\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5bf9\u8fd9\u5c11\u5973\uff0c\u50cf\u7279\u522b\u5173\u6000\u3002\u5fc3\u4e2d\u6709\u4e8b\uff0c\u5982\u54fd\u5728\u5589\uff0c\u4e0d\u5410\u4e0d \u5feb\uff0c\u9f13\u8d77\u52c7\u6c14\u8bf4\u9053\uff1a\u201c\u90a3\u4fc4\u9a6c\u767b\u6d85\u5df4\uff0c\u59d1\u5a18\u8fd8\u662f\u4e0d\u8981\u592a\u8fc7\u76f8\u4fe1\u7684\u597d\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u662f\u5417\uff1f \u6211\u7684\u4e8b\u60c5\u6211\u81ea\u5df2\u77e5\u9053\u6599\u7406\uff0c\u4f60\u653e\u5fc3\u5427\u3002\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u4f3c\u4e4e\u53d1\u89c9\u81ea\u5df1\u7684\u8bed\u6c14\u53ef\u80fd\u4f24\u4e86\u8fd9\u5c11\u5e74\u7684 \u5fc3\uff0c\u7d27\u8ddf\u7740\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u8fd8\u662f\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u5176\u5b9e\u6211\u4e5f\u5e76\u4e0d\u600e\u6837\u76f8\u4fe1\u4ed6\uff1f\u6211 \u65e9\u5df2\u77e5\u6653\u4f60\u6765\u4e86\uff0c\u4f46\u5728\u4ed6\u7684\u9762\u524d\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u8bf4\u7834\u3002\u201d\u9648\u5929\u5b87\u53c8\u4e0d\u81ea\u89c9\u7684\u62a5\u4ee5\u4e00\u7b11\uff0c\u6b63\u60f3\u8bf4 \u8bdd\uff0c\u90a3\u5c11\u5973\u5374\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u793c\u7269\uff0c\u6211\u6ca1\u6709\u4ec0\u4e48\u4e1c\u897f\u53ef\u4ee5\u62a5\u7b54\uff1b\u9001\u4f60\u4e00\u6735\u82b1\u5427\u3002\u201d\u9648 \u5929\u5b87\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5728\u9ad8\u539f\u4e4b\u4e0a\uff0c\u4e25\u5bd2\u672a\u8fc7\uff0c\u90a3\u6709\u82b1\u6735\uff1f\u201d\u53ea\u89c1\u90a3\u5c11\u5973\u53d6\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u7684\u94f6 \u74f6\uff0c\u74f6\u4e2d\u6709\u4e00\u6735\u767d\u82b1\uff0c\u82b1\u74e3\u4e0a\u8fd8\u6709\u9732\u73e0\u6eda\u52a8\uff0c\u597d\u50cf\u662f\u521a\u521a\u6458\u4e0b\u6765\u4f3c\u7684\uff0c\u90a3\u5c11\u5973\u9053\uff0c\u201c\u8fd9\u662f\u51b0 \u5ddd\u5929\u5973\u9001\u4e0e\u6211\u7684\uff0c\u6211\u85cf\u7740\u5b83\u5df2\u6709\u4e00\u5e74\u4e86\uff0c\u73b0\u5728\u5c31\u9001\u7ed9\u4f60\u5427\u3002\u201d\u9648\u5929\u5b87\u4e0d\u89c9\u5927\u4e3a\u8be7\u5f02\uff1a\u4e16\u4e0a\u90a3 \u6709\u8fd9\u6837\u7684\u82b1\u6735\uff0c\u6458\u4e86\u4e0b\u6765\u3002\u7ecf\u8fc7\u4e00\u5e74\uff0c\u5374\u8fd8\u4f3c\u679d\u5934\u4e0a\u7684\u9c9c\u82b1\uff1f\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u53c8\u9053\uff1a\u542c\u5929\u5973\u59d0 \u8bc5\u8bf4\uff0c\u8fd9\u662f\u5979\u4ece\u5929\u5c71\u79fb\u690d\u8fc7\u6765\u7684\u96ea\u83b2\uff0c\u4e0d\u8bba\u53d7\u4e86\u591a\u91cd\u7684\u5185\u4f24\uff0c\u5f53\u96ea\u83b2\u56bc\u4e0b\uff0c\u4fbf\u53ef\u65e0\u7591\uff0c\u4f60\u62ff \u53bb\u5427\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u6837\u5b9d\u8d35\u7684\u793c\u7269\uff0c\u6211\u4e0d\u6562\u53d7\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86\u4f60\u7684\u5e08\u5085\u5417\uff1f \u6211\u77e5\u9053\u90a3\u4e24\u4e2a\u6c49\u4eba\u5411\u4f60\u5e08\u5085\u5bfb\u4ec7\uff0c\u60f3\u4ed6\u5b9a\u53d7\u4e86\u4f24\uff0c\u4f60\u90a3\u65e5\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211\u65e0\u53ef\u62a5\u7b54\uff0c\u8fd9\u6735 \u96ea\u83b2\uff0c\u6b63\u5408\u4f60\u5e08\u5085\u7528\uff0c\u4f60\u62ff\u53bb\u5427\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u60f3\u8d77\u4e86\u5e08\u5085\u7684\u4f24\uff0c\u867d\u7136\u5e08\u5085\u8bf4\u8fc7\uff0c\u4ed6\u53ef\u4ee5\u5728\u4e03\u65e5\u4e4b\u5185\uff0c\u81ea\u8fd0\u7384\u529f\uff0c\u590d\u539f\u5982\u65e7\uff0c\u4f46 \u800c\u4eca\u5df2\u8fc7\u4e86\u56db\u65e5\uff0c\u53cc\u624b\u8fd8\u662f\u50f5\u786c\u4e0d\u80fd\u8f6c\u52a8\uff0c\u4ed6\u7684\u81ea\u7597\u662f\u5426\u6709\u6548\uff0c\u5c1a\u672a\u53ef\u77e5\u3002\u5982\u6b64\u4e00\u60f3\uff0c\u4fbf\u4e0d \u518d\u5ba2\u6c14\uff0c\u4f38\u624b\u6402\u8fc7\u90a3\u4e2a\u94f6\u74f6\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8138\u4e0a\u6cdb\u8d77\u4e00\u6735\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u5085\u7b49\u4f60\u8be5\u7b49\u5f97\u5fc3\u7126\u4e86\uff0c\u4f60\u5feb\u56de\u53bb\u5427\u3002\u201d\u6597\u7136\u4ece\u8170 \u95f4\u89e3\u4e0b\u4e00\u6761\u957f\u7d22\uff0c\u7d22\u7aef\u88c5\u7740\u98de\u6293\uff0c\u53ea\u89c1\u5979\u8f7b\u8f7b\u4e00\u6296\uff0c\u957f\u7d22\u6296\u7684\u6bd5\u76f4\uff0c\u98de\u6293\u52fe\u7740\u5c71\u77f3\u9699\u95f4\u957f\u51fa \u7684\u866c\u677e\uff0c\u624b\u6293\u7ef3\u7d22\u3002\u8eab\u5f62\u4e00\u6643\uff0c\u8361\u79cb\u5343\u822c\u7684\u8361\u4e86\u8fc7\u53bb\uff0c\u5982\u6b64\u8fd9\u822c\u7684\u51e0\u6b21\uff1a\u5df2\u8fc7\u4e86\u659c\u5bf9\u9762\u7684\u5c71 \u5761\uff0c\u6536\u8d77\u98de\u6293\uff0c\u8f6c\u8fc7\u5c0f\u6c9f\uff0c\u8eab\u5f62\u4e91\u5ffd\u4e0d\u89c1\u3002 \u3000\u3000\u9648\u5929\u5b87\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u6211\u6789\u5b66\u4e86\u8fd9\u4e48\u591a\u5e74\u7684\u6b66\u529f\uff0c\u5979\u53ea\u5b66\u4e86\u4e09\u5929\uff0c\u770b\u8fd9\u4efd\u8f7b\u529f\uff0c\u5374\u5df2\u8fdc\u80dc \u4e8e\u6211\u3002\u201d\u6536\u597d\u96ea\u83b2\uff0c\u8e0f\u7740\u6708\u5149\uff0c\u6298\u5411\u56de\u5934\u8def\u8d70\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u60f3\u8d77\u8fd9\u51e0\u65e5\u906d\u9047\u4e4b\u5947\uff0c\u8fd9\u85cf \u65cf\u5c11\u5973\u5df2\u662f\u795e\u79d8\u4e4b\u6781\uff0c\u800c\u542c\u4ed6\u548c\u4fc4\u9a6c\u767b\u6240\u8bf4\uff0c\u90a3\u51b0\u5ddd\u5929\u5973\u66f4\u662f\u795e\u79d8\u4e07\u5206\uff0c\u4e0d\u77e5\u662f\u4f55\u7b49\u6837\u4eba\uff0c \u4f55\u4ee5\u5728\u4e09\u65e5\u4e4b\u95f4\uff0c\u4fbf\u80fd\u6559\u5f97\u4e00\u4e2a\u67d4\u5f31\u7684\u85e9\u738b\u5973\u513f\uff0c\u98de\u6a90\u8d70\u58c1\u3002\u201d \u3000\u3000\u4e00\u8def\u6c89\u601d\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u8d70\u8fc7\u51e0\u5904\u5c71\u6c9f\uff0c\u8fdc\u8fdc\u5df2\u53ef\u770b\u89c1\u55b7\u6cc9\u84b8\u6c7d\u3001\u6d6e\u8361\u591c\u7a7a\uff0c\u597d\u50cf\u4e00\u56e2\u56e2 \u4e91\u7d6e\uff0c\u5189\u7565\u4e0a\u5347\uff0c\u5728\u9ad8\u539f\u4e4b\u4e0a\uff0c\u851a\u6210\u5947\u666f\uff0c\u5c71\u98ce\u5439\u9001\uff0c\u9648\u5929\u5b87\u9690\u9690\u542c\u5f97\u5728\u55b7\u6cc9\u55b7\u53d1\u7684\u4e1d\u4e1d\u58f0 \u54cd\u4e4b\u4e2d\uff0c\u597d\u50cf\u5939\u6742\u7740\u5175\u5203\u78b0\u51fb\u4e4b\u58f0\uff0c\u8d8a\u542c\u8d8a\u771f\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u52a0\u5feb\u811a\u5361\uff0c\u5ffd\u542c\u201c\u563f \u563f\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u8d77\u81ea\u8eab\u65c1\uff0c\u9648\u5929\u5b87\u8d76\u5fd9\u62e8\u5251\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u659c\u91cc\u7a9c\u51fa\u4e00\u6761 \u6c49\u5b50\uff0c\u6325\u52a8\u957f\u97ad\uff0c\u77ac\u556a\u4f5c\u54cd\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u7cca\u6d82\u7684\u5c0f\u5b50\uff0c\u60f3\u8d76\u56de\u53bb\u7ed9\u8427\u8001\u513f\u9001\u846c \u5417\uff1f\u201d\u9648\u5929\u5b87\u5927\u6012\uff0c\u5237\u7684\u53cd\u624b\u4e00\u5251\uff0c\u90a3\u6c49\u5b50\u8eab\u5f62\u4e00\u6643\uff0c\u957f\u97ad\u4e00\u63a0\u3002\u6296\u5f97\u7b14\u76f4\uff0c\u5411\u9648\u5929\u5b87\u62e6\u8170 \u75be\u626b\uff0c\u9648\u5929\u5b87\u4e00\u4e2a\u201c\u65f1\u5730\u62e8\u8471\u201d\uff0c\u5411\u4e0a\u4e00\u8df3\uff0c\u9669\u9669\u7ed9\u4ed6\u3002\u7684\u957f\u97ad\u626b\u4e2d\uff0c\u90a3\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u957f \u97ad\u50cf\u6bd2\u86c7\u80a1\u5012\u5377\u8f6c\u6765\uff0c\u5237\u5237\u53c8\u662f\u4e24\u97ad\uff0c\u9648\u5929\u5b87\u4e00\u62db\u201c\u63a8\u7a97\u671b\u6708\u201d\uff0c\u5251\u5203\u5e73\u524a\uff0c\u53cd\u627e\u654c\u4eba\u624b \u8155\uff0c\u90a3\u4eba\u7684\u957f\u97ad\u7adf\u4f7f\u5f97\u5341\u5206\u7075\u6d3b\uff0c\u62db\u5f0f\u4e00\u53d8\uff0c\u53c8\u6539\u626b\u4e0b\u76d8\uff0c\u9648\u5929\u5b87\u7ed9\u95f9\u966a\u5f97\u4e2a\u624b\u5fd9\u811a\u4e71\uff0c\u767e \u5fd9\u4e2d\u4e00\u5251\u659c\u6307\uff0c\u5192\u9669\u53cd\u653b\uff0c\u5ffd\u89c9\u624b\u8155\u4e00\u6c89\uff0c\u5251\u8eab\u5df2\u7ed9\u97ad\u68a2\u7f20\u4e0a\uff0c\u9648\u5929\u5b87\u5fc3\u91cc\u53d1\u614c\uff0c\u4e0d\u6687\u601d \u7d22\uff0c\u81ea\u7136\u800c\u7136\u7684\u4f7f\u51fa\u5e08\u95e8\u5fc3\u6cd5\uff0c\u6c89\u8170\u5750\u9a6c\u3002\u957f\u5251\u4e00\u63a2\uff0c\u5251\u950b\u4e00\u65cb\uff0c\u53ea\u542c\u5f97\u90a3\u6c49\u5b50\u201c\u566b\u201d\u4e86\u4e00 \u58f0\uff0c\u957f\u987a\u4e00\u64a4\uff0c\u538b\u529b\u987f\u677e\uff0c\u9648\u5929\u5b57\u5de6\u4e00\u5251\u201c\u5371\u8702\u7a7f\u4e91\u201d\uff0c\u53f3\u4e00\u5251\u5927\u6f20\u5b64\u70df\uff0c\u8fde\u73af\u4e24\u62db\uff0c\u5f0f\u4e2d \u5957\u5f0f\uff0c\u7adf\u628a\u90a3\u6c49\u5b50\u8feb\u5f97\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u539f\u6765\u9648\u5929\u5b87\u7684\u6b66\u529f\uff0c\u672c\u5728\u90a3\u6c49\u5b50\u4e4b\u4e0a\uff0c\u53ea\u56e0\u4eca\u756a\u8fd8\u662f\u7b2c\u4e00\u6b21\u4e34\u654c\u5e94\u7528\uff0c\u6545\u6b64\u5f00\u5934\u51e0\u62db\uff0c \u4e0d\u77e5\u5e94\u4ed8\u3002\u800c\u4eca\u89c1\u8fd9\u6c49\u5b50\u4e5f\u4e0d\u8fc7\u5982\u662f\uff0c\u80c6\u6c14\u987f\u58ee\uff0c\u628a\u9752\u57ce\u5251\u6cd5\u5c55\u5f00\uff0c\u5b9b\u5982\u7389\u9f99\u5929\u77eb\uff0c\u5f97\u5fc3\u5e94 \u624b\u3002\u97ad\u6765\u5251\u5f80\uff0c\u5251\u53bb\u97ad\u8d76\uff0c\u4e24\u4eba\u8f6c\u773c\u4e4b\u95f4\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u9648\u5929\u5b87\u80dc\u5728\u5251\u6cd5\u7cbe\u5999\uff0c\u90a3\u6c49\u5b50\u5374\u80dc \u5728\u7ecf\u9a8c\u8001\u5230\uff0c\u5404\u6709\u6240\u957f\uff0c\u4e0d\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u8f7b\u654c\u4e4b\u5ff5\u5df2\u6d88\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u540d\u5e08\u6240\u6388\uff0c\u679c\u662f\u4e0d\u540c\u3002\u201d\u5b9e\u65bd\u72e1\u8ba1\uff0c\u4e0d\u4f4f\u7684\u5411\u5de6\u53f3 \u79fb\u52a8\u811a\u6b65\uff0c\u5f15\u9648\u5929\u5b87\u8ddf\u7740\u4ed6\u8f6c\u3002 \u3000\u3000\u5c71\u9053\u672c\u5c31\u9669\u5cfb\uff0c\u52a0\u4e0a\u591c\u95f4\u9177\u5bd2\uff0c\u591c\u9732\u51dd\u51b0\uff0c\u811a\u5e95\u751a\u6ed1\uff0c\u9648\u5929\u5b87\u8fd8\u662f\u521d\u51fa\u9053\uff0c\u884c\u8d70\u5c71\u8def\u5df2 \u662f\u4e0d\u60ef\uff0c\u4f55\u51b5\u662f\u6fc0\u70c8\u640f\u6597\uff0c\u8ddf\u4ed6\u8f6c\u4e86\u51e0\u8f6c\uff0c\u53ea\u89c9\u811a\u6b65\u865a\u6d6e\uff0c\u597d\u51e0\u6b21\u9669\u9669\u8dcc\u5012\uff0c\u90a3\u6c49\u5b50\u5f15\u5230\u60ac \u5ca9\u524a\u58c1\u4e4b\u524d\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u770b\u770b\u5f97\u624b\uff0c\u9648\u5929\u5b87\u5ffd\u5730\u7ad9\u7740\uff0c\u51dd\u7acb\u4e0d\u52a8\uff0c\u4e00\u53e3\u5251\u4e0a\u4e0b\u7ffb\u98de\uff0c\u62a4\u7740\u8981 \u5bb3\uff0c\u53ea\u5f85\u654c\u4eba\u8feb\u8fd1\u4e4b\u65f6\u3002\u5c31\u662f\u5ffd\u5730\u4e00\u5251\u3002\u539f\u6765\u9648\u5929\u5b87\u4e5f\u751a\u673a\u7075\uff0c\u9047\u4e86\u51e0\u6b21\u9669\u62db\uff1b\u770b\u51fa\u60c5\u5f62\u4e0d \u5bf9\uff0c\u6025\u8fd0\u5e08\u95e8\u72ec\u5230\u7684\u5343\u65a4\u5760\u529f\u592b\uff0c\u53cc\u8db3\u9489\u7262\u5730\u4e0a\u3002\u6709\u5982\u9489\u6869\uff0c\u4e0d\u6c42\u6709\u529f\uff0c\u5148\u6c42\u65e0\u8fc7\uff0c\u90a3\u6c49\u5b50 \u4e00\u8fde\u4f7f\u4e86\u597d\u51e0\u6b21\u865a\u62db\u8bf1\u7740\uff0c\u9648\u5929\u5b87\u90fd\u4e0d\u4e3a\u6240\u52a8\u3002 \u3000\u3000\u8f6c\u773c\u53c8\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u90a3\u6c49\u5b50\u653b\u4e0d\u8fdb\u6765\uff0c\u9648\u5929\u5b87\u4e5f\u4e0d\u6562\u5192\u6627\u6740\u4e0a\uff0c\u53d8\u6210\u4e86\u4e2a\u50f5\u6301\u4e4b\u5c40\uff0c \u9648\u5929\u5b87\u6b63\u5728\u5fc3\u7126\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u563f\u563f\u7684\u51b7\u7b11\uff0c\u4e00\u4e2a\u5636\u54d1\u7684\u82cd\u8001\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fde\u4e00\u4e2a\u6d51\u5c0f\u5b50 \u964d\u4e0d\u4e86\uff0c\u522b\u7ed9\u6211\u4e22\u8138\u5566\u3002\u864e\u5b50\uff0c\u625b\u6211\u4e0a\u524d\u53bb\u770b\u770b\u3002\u201d\u9648\u5929\u5b87\u5b9a\u773c\u770b\u95ee\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u4e2a\u53ef\uff0c\u53ea \u89c1\u4e00\u4e2a\u9ed1\u8138\u5927\u6c49\uff0c\u6258\u7740\u4e00\u4e2a\u8fc7\u5c71\u7af9\u515c\uff0c\u515c\u4e0a\u5750\u7740\u4e00\u4e2a\u4eba\uff0c\u9762\u5982\u9ec4\u814a\uff0c\u5f62\u5bb9\u9a87\u4eba\uff0c\u53cc\u773c\u5706\u7741\uff0c \u563f\u563f\u51b7\u7b11\uff0c\u8fd9\u602a\u4eba\u6b63\u662f\u90a3\u65e5\u7ed9\u8427\u9752\u5cf0\u62c2\u5c18\u626b\u4e0b\u51b0\u6e0a\uff0c\u5e78\u672a\u8dcc\u6b7b\u7684\u5d14\u4e91\u5b50\u3002\u4ed6\u7ed9\u62c2\u5c18\u4e00\u626b\uff0c\u4e94 \u810f\u516d\u8151\u4ff1\u7ed9\u9707\u4f24\uff0c\u534a\u8eab\u762b\u75ea\uff0c\u4e0d\u80fd\u884c\u52a8\u3002\u56e0\u6b64\u53eb\u4e24\u4e2a\u5f92\u5f1f\u7528\u7af9\u51f3\u62ac\u4ed6\uff0c\u65e5\u591c\u517c\u7a0b\uff0c\u60f3\u8d76\u5230\u65e5 \u5580\u5219\u627e\u628a\u5144\u96f7\u9707\u5b50\u533b\u51b6\uff0c\u60f3\u4e0b\u5230\u9648\u5929\u5b87\u7adf\u7136\u5728\u8fd9\u4e2a\u65f6\u5019\u9047\u89c1\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u867d\u53d7\u4e86\u91cd\u4f24\uff0c\u5374\u8fd8\u4fdd\u6301\u8eab\u4efd\uff0c\u4e0d\u5c51\u4e0e\u5c0f\u8f88\u52a8\u624b\uff0c\u8d77\u5148\u53ea\u53eb\u4e00\u4e2a\u5f92\u5f1f\u51fa\u51fb\uff0c\u6ee1\u4ee5\u4e3a\u9648\u5929 \u5b87\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6b66\u529f\u6599\u6765\u5e73\u5eb8\uff0c\u81ea\u5df1\u7684\u5f92\u5f1f\u6709\u4e8c\u5341\u5e74\u529f\u592b\uff0c\u4e00\u51fa\u624b\u5fc5\u5b9a\u624b\u5230\u64d2\u6765\uff0c\u54ea\u77e5\u9648\u5929\u5b87 \u5b66\u7684\u662f\u9752\u57ce\u6d3e\u7684\u6b63\u5b97\u5185\u529f\uff0c\u81ea\u5e7c\u624e\u7a33\u6839\u57fa\uff0c\u52a0\u4e4b\u5251\u6cd5\u7cbe\u5999\uff0c\u82e5\u975e\u7ecf\u9a8c\u592a\u5dee\uff0c\u81ea\u5df1\u5f92\u5f1f\u8fd8\u771f\u4e0d \u662f\u4ed6\u7684\u5bf9\u624b\u3002\u5d14\u4e91\u5b50\u4e00\u770b\u4e0d\u5bf9\uff0c\u8feb\u5f97\u81ea\u5df1\u51fa\u9635\u3002 \u3000\u3000\u4e0e\u9648\u5929\u5b87\u5bf9\u654c\u7684\u90a3\u4e2a\u6c49\u5b50\uff0c\u542c\u5f97\u5e08\u7236\u51fa\u58f0\u65a5\u9a82\uff0c\u6ee1\u9762\u7f9e\u60ed\uff0c\u5782\u624b\u9000\u4e0b\uff0c\u7acb\u5230\u7af9\u51f3\u65c1\uff0c\u90a3 \u5d14\u4e91\u5b50\u867d\u7136\u534a\u8eab\u762b\u75ea\uff0c\u624b\u81c2\u5c1a\u53ef\u8f6c\u52a8\uff0c\u53ea\u89c1\u4ed6\u5728\u602a\u7b11\u58f0\u4e2d\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u4e00\u7c92\u94c1\u83b2\u5b50\u55e4\u7684\u4e00 \u58f0\uff0c\u7834\u7a7a\u98de\u51fa\uff0c\u9648\u5929\u5b87\u672a\u53ca\u95ea\u907f\uff0c\u80f8\u53e3\u5df2\u662f\u4e00\u9ebb\uff0c\u6251\u901a\u8dcc\u5012\uff0c\u8fd8\u5e78\u5d14\u4e91\u5b50\u53d7\u4e86\u91cd\u4f24\uff0c\u5185\u529f\u5df2 \u51cf\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u5f39\u4e4b\u529b\uff0c\u4fbf\u53ef\u5c06\u9648\u5929\u5b87\u6253\u6655\u3002 \u3000\u3000\u90a3\u9ed1\u8138\u819b\u7684\u6c49\u5b50\u653e\u4e0b\u7af9\u515c\uff0c\u4e0e\u5e08\u5144\u5939\u624b\u5939\u811a\uff0c\u5c06\u9648\u5929\u5b87\u7f1a\u4e2a\u7ed3\u5b9e\uff0c\u5d14\u4e91\u5b50\u9053\uff1a\u201c\u641c\u4ed6\u7684 \u8eab\uff01\u201d\u4e00\u641c\u641c\u51fa\u90a3\u4e2a\u94f6\u74f6\uff0c\u5d14\u4e91\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u6851\u739b\u5c45\u7136\u820d\u5f97\u628a\u5929\u5c71\u96ea\u83b2\u7ed9\u4f60\u3002\u5f92 \u513f\u628a\u94f6\u74f6\u62ff\u7ed9\u6211\u3002\u201d\u9648\u5929\u5b87\u6012\u6781\u6c14\u6781\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5e08\u5085\u7684\u4e1c\u897f\u3002\u201d\u5d14\u4e91\u5b50\u5927\u7b11\u9053\uff1a\u201c\u4f60 \u5e08\u5085\u7528\u4e0d\u7740\u5566\uff0c\u7b49\u4f1a\u513f\u6211\u5c31\u9001\u4f60\u53bb\u89c1\u5e08\u5085\u3002\u201d\u9648\u5929\u5b87\u7528\u529b\u6323\u624e\uff0c\u5d14\u4e91\u5b50\u9053\uff1a\u201c\u864e\u5b50\uff0c\u70b9\u4ed6\u7684 \u9ebb\u7a74\uff0c\u9001\u4ed6\u5230\u7af9\u515c\u4e0a\u6765\u3002\u201d\u9648\u5929\u5b87\u88ab\u7ed1\u5728\u5d14\u4e91\u5b50\u65c1\u8fb9\uff0c\u773c\u7741\u7741\u5730\u770b\u7740\u5e08\u5085\u7684\u5927\u4ec7\u4eba\u63ed\u5f00\u94f6 \u74f6\uff0c\u628a\u90a3\u6735\u5929\u5c71\u96ea\u83b2\uff0c\u672c\u6765\u662f\u51c6\u5907\u7ed9\u5e08\u5085\u6551\u547d\u7684\u5929\u5c71\u96ea\u83b2\uff0c\u9001\u8fdb\u4e86\u5634\u4e2d\uff0c\u4e00\u9635\u4e71\u56bc\uff0c\u54bd\u4e86\u4e0b \u53bb\uff0c\u9648\u5929\u5b87\u5fc3\u75db\u5982\u5272\uff0c\u5374\u662f\u51fa\u4e0d\u4e86\u58f0\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6c49\u5b50\u62ac\u7740\u7af9\u515c\uff0c\u5065\u6b65\u5982\u98de\uff0c\u6708\u5149\u4ece\u51b0\u5cf0\u4e0a\u6d12\u4e0b\u6765\uff0c\u5c71\u5934\u4e00\u7247\u94f6\u767d\uff0c\u9648\u5929\u5b87\u8eba\u5728\u5d14 \u4e91\u5b50\u65c1\u8fb9\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u90a3\u5d14\u4e91\u5b50\u672c\u662f\u9762\u8272\u5982\u814a\uff0c\u5f62\u5bb9\u9a87\u4eba\uff0c\u56bc\u4e0b\u96ea\u83b2\u4e4b\u540e\uff0c\u53ea\u89c1\u4ed6\u6df1\u6df1 \u5438\u6c14\uff0c\u6c14\u606f\u6e10\u79df\uff0c\u8138\u8272\u4e5f\u6e10\u7ea2\u6da6\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5929\u5c71\u96ea\u83b2\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u58f0 \u97f3\u6e05\u4eae\uff0c\u4e0e\u9002\u624d\u7684\u5636\u54d1\u5927\u4e0d\u76f8\u540c\u3002\u9648\u5929\u5b87\u53c8\u662f\u5fc3\u75db\uff0c\u53c8\u662f\u60ca\u8bb6\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5929\u5c71\u96ea\u83b2\u5982 \u6b64\u7075\u5f02\uff0c\u8fd9\u53ae\u5185\u4f24\u5df2\u6108\uff0c\u6211\u5e08\u5f92\u6027\u547d\uff0c\u4eca\u65e5\u4f11\u77e3\uff01\u201d \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u55b7\u6cc9\u7684\u5636\u5636\u58f0\u54cd\u6108\u6765\u6108\u5927\uff0c\u800c\u5175\u5203\u78b0\u51fb\uff0c\u53f1\u54a4\u8ffd\u9010\u4e4b\u58f0\u4ea6\u6108\u542c\u6108\u771f\uff0c\u5d14\u4e91\u5b50 \u9762\u4e0a\u73b0\u51fa\u60ca\u8bb6\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u8427\u8001\u513f\u7684\u624b\u81c2\u7ed9\u6211\u7684\u5f13\u5f26\u62c9\u65ad\u4e86\u7b4b\u8109\uff0c\u600e\u4e48\u8fd8\u80fd\u4e0e\u4eba\u640f \u6597\uff1f\u201d\u5ffd\u5730\u53cc\u6307\u4e00\u5939\uff0c\u628a\u9648\u5929\u5b87\u8eab\u4e0a\u7684\u7ef3\u7d22\u526a\u65ad\uff0c\u5c06\u9648\u5929\u5b87\u4e00\u628a\u63d0\u8d77\uff0c\u8df3\u4e0b\u7af9\u515c\uff0c\u9053\uff1a\u201c\u4e0d \u8981\u4f60\u4eec\u62ac\u5566\uff01\u5c0f\u5b50\u6211\u5d14\u8001\u4e09\u8bf4\u4e00\u4e0d\u4e8c\uff0c\u73b0\u5728\u5c31\u4eb2\u81ea\u9001\u4f60\u53bb\u89c1\u5e08\u5085\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u88ab\u5d14\u4e91\u5b50\u5939\u7740\uff0c\u52a8\u5f39\u4e0d\u4e86\uff0c\u5230\u4e86\u55b7\u6cc9\u65c1\u8fb9\uff0c\u53ea\u89c1\u81ea\u5df1\u90a3\u5f20\u84ec\u5e10\u56db\u9762\u88c2\u5f00\uff0c\u539a\u539a\u7684 \u5e06\u5e03\u7ed9\u5272\u6210\u4e00\u7247\u7247\u7684\u788e\u5e03\uff0c\u8fce\u98ce\u98d8\u821e\uff0c\u6628\u65e5\u8def\u4e0a\u6240\u89c1\u7684\u90a3\u4e09\u4e2a\u7c97\u8c6a\u6c49\u5b50\uff0c\u6301\u7740\u660e\u6643\u6643\u7684\u5229 \u5200\uff0c\u8d70\u9a6c\u706f\u4f3c\u7684\u5728\u7834\u88c2\u7684\u5e10\u84ec\u4e2d\u56f4\u7740\u81ea\u5df1\u7684\u5e08\u5085\u653b\u51fb\u3002 \u3000\u3000\u9648\u5929\u5b87\u5927\u5403\u4e00\u60ca\uff0c\u5b9a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u81ea\u5df1\u7684\u5e08\u5085\u4ecd\u7136\u7aef\u5750\u5730\u4e0a\uff0c\u8eab\u8eaf\u52a8\u4e5f\u4e0d\u52a8\u3002\u53e3\u4e2d\u5374\u54ac \u7740\u4e00\u67c4\u62c2\u5c18\uff0c\u654c\u4eba\u7684\u5229\u5200\u5288\u5230\u773c\u524d\uff0c\u7ed9\u4ed6\u7684\u62c2\u5c18\u4e00\u62c2\u5c31\u8361\u4e86\u5f00\u53bb\uff0c\u4e0d\u8bba\u654c\u4eba\u4ece\u524d\u9762\u3001\u4fa7\u9762\u751a \u81f3\u540e\u9762\u8fdb\u653b\uff0c\u4ed6\u7684\u5934\u53ea\u662f\u8f7b\u8f7b\u4e00\u6447\uff0c\u62c2\u5c18\u524d\u626b\u540e\u62c2\uff0c\u90fd\u662f\u6070\u597d\u628a\u5229\u5203\u6321\u7740\uff0c\u6bd4\u522b\u4eba\u7528\u624b\u8fd8\u8981 \u7075\u6d3b\u5f97\u591a\u3002\u654c\u4eba\u653b\u5f97\u8d8a\u7d27\uff0c\u9707\u8361\u53cd\u51fb\u4e4b\u529b\u5c31\u8d8a\u5f3a\uff0c\u90a3\u4e09\u4e2a\u6c49\u5b50\u7adf\u7136\u7ed9\u4ed6\u5e26\u5f97\u56e2\u56e2\u4e71\u8f6c\uff0c\u5175\u5203 \u4e92\u76f8\u78b0\u649e\uff0c\u5c31\u5982\u6709\u5341\u6570\u4eba\u5728\u5e10\u4e2d\u8ffd\u9010\u640f\u6597\u4e00\u80a1\u3002 \u3000\u3000\u5d14\u4e91\u5b50\u7709\u5934\u4e00\u76b1\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8427\u9752\u5cf0\uff0c\u6211\u518d\u6765\u4f1a\u4f1a\u4f60\u7684\u94c1\u62c2\u5c18\u3002\u201d\u90a3\u4e09\u4e2a\u6c49\u5b50\u500f \u7684\u8df3\u4e0b\uff0c\u53ea\u89c1\u5d14\u4e91\u5b50\u53cc\u81c2\u7b95\u5f20\uff0c\u4e00\u8dc3\u800c\u524d\uff0c\u5341\u6307\u9f50\u5f39\uff0c\u50fb\u556a\u4f5c\u54cd\uff0c\u8427\u9752\u5cf0\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00 \u58f0\uff0c\u5f20\u53e3\u4e00\u5410\uff0c\u62c2\u5c18\u5982\u77e2\uff0c\u75be\u5c04\u800c\u53bb\uff0c\u5d14\u4e91\u5b50\u4e00\u95ea\u95ea\u5f00\uff0c\u53ea\u542c\u5f97\u8427\u9752\u5cf0\u53f9\u9053\uff1a\u201c\u4e91\u5b50\uff0c\u4f60\u7684 \u5185\u529f\u679c\u7136\u6bd4\u6211\u9ad8\uff0c\u6211\u8fd0\u4e86\u56db\u65e5\u7384\u529f\uff0c\u53cc\u81c2\u5c1a\u672a\u80fd\u6062\u590d\u539f\u72b6\uff0c\u800c\u4f60\u5c45\u7136\u80fd\u884c\u52a8\u5982\u5e38\uff0c\u6211\u8427\u9752\u5cf0 \u670d\u8f93\u5566\uff01\u201d\u9648\u5929\u5b87\u5927\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u5e08\u5085\u4f60\u6ca1\u6709\u8f93\uff0c\u662f\u4ed6\uff0c\u4ed6\u62a2\u4e86\u6211\u7684\u5929\u5c71\u96ea\u83b2\uff0c\u201d\u8427\u9752\u5cf0\u53eb \u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u2026\u2026\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5d14\u4e91\u5b50\u5df2\u500f\u7684\u6b3a\u8eab\u76f4\u8fdb\uff0c\u9a88\u6307\u4e00\u70b9\uff0c\u70b9\u4e86\u4ed6\u7684\u9ebb\u7a74\uff0c\u8427\u9752 \u5cf0\u90a3\u53e5\u201c\u4f60\u54ea\u91cc\u6765\u7684\u5929\u5c71\u96ea\u83b2\uff1f\u201d\u7adf\u7136\u6765\u4e0d\u53ca\u95ee\u3002 \u3000\u3000\u9648\u5929\u5b87\u7684\u7a74\u9053\u672c\u6765\u89e3\u5f00\uff0c\u8fd9\u65f6\u4e5f\u7ed9\u5d14\u4e91\u5b50\u7684\u5f92\u5f1f\u63a8\u5230\u524d\u9762\uff0c\u5d14\u4e91\u5b50\u5565\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8427 \u9752\u5cf0\uff0c\u8bba\u5185\u529f\u662f\u4f60\u6bd4\u6211\u9ad8\u3002\u4f46\u5f97\u9053\u8005\u52a9\u591a\uff0c\u5929\u610f\u53eb\u6211\u6740\u4f60\uff0c\u6240\u4ee5\u501f\u4f60\u5f92\u513f\u7684\u624b\uff0c\u7ed9\u6211\u9001\u6765\u4e86 \u4e16\u95f4\u7f55\u5f97\u7684\u96ea\u83b2\u5566\uff01\u201d \u3000\u3000\u8427\u9752\u5cf0\u9762\u8272\u4e00\u53d8\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\u5a01\u98ce\u3002\u6211\u4eca\u65e5\u624d\u89c1\u5230\u5d06\u5cd2\u6d3e\u9ad8\u624b\u7684\u771f\u672c \u9886\uff01\u201d\u5d14\u4e91\u4e86\u7b11\u9053\uff1a\u201c\u8bba\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\uff0c\u6211\u672c\u8be5\u5f85\u4f60\u4f24\u597d\u4e4b\u540e\uff0c\u624d\u518d\u548c\u4f60\u8f83\u91cf\uff0c\u4f46\u53c8\u6015\u4f60\u4f24 \u597d\u4e4b\u540e\uff0c\u5939\u8005\u5c3e\u5df4\u9003\u8dd1\uff0c\u6211\u5230\u54ea\u513f\u627e\u4f60\uff1f\u4f55\u51b5\u4f60\u5f53\u5e74\u4e0e\u90a3\u5996\u5973\uff0c\u4e5f\u662f\u7528\u8be1\u8ba1\u4f24\u4e86\u6211\u4eec\u3002\u5454\uff0c \u4f60\u542c\u7740\uff0c\u6211\u5148\u66ff\u5927\u54e5\u62a5\u4ec7\uff0c\u5728\u4f60\u7684\u9762\u4e0a\u5212\u4e0a\u56db\u5200\uff1f\u201d\u500f\u7684\u4ece\u4e00\u4e2a\u5e08\u4f84\uff08\u90a3\u4e09\u4e2a\u6c49\u5b50\u662f\u96f7\u9707\u5b50 \u7684\u5f92\u5f1f\uff09\u624b\u4e0a\u593a\u8fc7\u4e00\u5f20\u660e\u6643\u6643\u7684\u5229\u5200\uff0c\u6267\u7740\u8427\u9752\u5cf0\u7684\u624b\u81c2\uff0c\u5c06\u4ed6\u62c9\u8fd1\uff0c\u51dd\u89c6\u7740\u4ed6\u7684\u9762\u95e8\u3001\u5634 \u4e2d\u53d1\u51fa\u72de\u7b11\u3002\u624b\u4e0a\u7684\u5229\u5203\u5c31\u8981\u5411\u8427\u9752\u8702\u7684\u9762\u95e8\u5212\u4e0b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u8f7b\u8f7b\u7684\u51b7\u7b11\uff0c\u4e00\u4e2a\u5cfb\u5ced\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\u5a01\u98ce\uff01\u201d\u9648\u5929\u5b87\u7a81\u89c9\u5fae\u98ce\u98d2 \u7136\uff0c\u4e00\u6761\u4eba\u5f71\u4ece\u8eab\u65c1\u7a9c\u8fc7\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u8eab\u4e0a\u4e00\u677e\uff0c\u7a74\u9053\u5ffd\u7136\u81ea\u89e3\uff0c\u53ea\u89c1\u6628\u65e5\u8def\u4e0a\u6240\u9047\u7684\u90a3\u5c11 \u5e74\u4e66\u751f\uff0c\u7b11\u541f\u541f\u4ed6\u7ad9\u5728\u573a\u4e2d\u3002 \u3000\u3000\u5d14\u4e91\u5b50\u77aa\u4e86\u90a3\u5c11\u5e74\u4e66\u751f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u77a7\u4e0d\u987a\u773c\u5417\uff1f\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u5c82\u6562\uff01\u6c5f\u6e56 \u9053\u4e0a\u5bfb\u4ec7\u62a5\u590d\u4e4b\u4e8b\u672c\u6781\u5bfb\u5e38\uff0c\u4f46\u8fd9\u8001\u513f\u5374\u4e0e\u6211\u6709\u70b9\u5173\u7cfb\u3002\u201d\u5d14\u4e91\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u9053\u4e0a\uff0c\u4e3a \u670b\u53cb\u4e24\u80c1\u63d2\u5200\uff0c\u4e8b\u60c5\u4e5f\u5c5e\u5bfb\u5e38\u3002\u597d\u5427\uff0c\u54b1\u4eec\u5c11\u8bf4\u95f2\u8bdd\uff0c\u4f60\u4eae\u51fa\u5175\u5668\u6765\uff0c\u4ffa\u5d14\u4e91\u5b50\u5c31\u7a7a\u624b\u63a5\u4f60 \u51e0\u62db\u3002\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u6211\u5c1a\u672a\u6ee1\u5e08\uff0c\u5e08\u7236\u6709\u547d\uff0c\u4e0d\u8bb8\u548c\u4eba\u52a8\u624b\u3002\u201d \u5d14\u4e91\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u51ed\u4f60\u8fd9\u8fd8\u672a\u51fa\u9053\u7684\u96cf\u513f\u7684\u4e00\u53e5\u8bdd\uff0c\u6211\u5c31\u8981\u7ed9\u4f60\u5356\u4ea4\u60c5\u3002\u9976\u4e86\u8fd9\u8001\u513f \u5417\uff0c\uff1f\u4f60\u662f\u8c01\uff1f\u5e08\u7236\u662f\u90a3\u4f4d\uff1f\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u4e00\u7b11\u9053\uff1a\u201c\u8c01\u8981\u4f60\u653e\u8fd9\u8001\u513f\uff1f\u8fd9\u8001\u513f\u4e5f\u662f\u6211\u7684\u4ec7 \u4eba\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5d14\u4e91\u5b50\u4e0d\u89c9\u4e00\u6014\uff0c\u9053\uff1a\u201c\u539f\u6765\u4ffa\u4f1a\u9519\u610f\u4e86\uff0c\u4f60\u4e5f\u662f\u4ed6\u7684\u4ec7\u4eba\uff1f\u201d\u5c11\u5e74\u4e66\u751f \u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u662f\u4ed6\u7684\u4ec7\u4eba\u3002\u5d14\u4e91\u5b50\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u7b97\u662f\u4f60\u7684\u9020\u5316\uff0c\u51ed\u7740\u4f60\u7684\u6b66\u529f\uff0c\u8427 \u8001\u513f\u4e00\u6307\u5c31\u53ef\u4ee5\u5c06\u4f60\u5f39\u5165\u51b0\u8c37\u3002\u5ff5\u5728\u540c\u4ec7\u7684\u9762\u4e0a\uff0c\u5f85\u6211\u5148\u5241\u4ed6\u56db\u5200\uff0c\u7136\u540e\u518d\u8ba9\u4f60\u4e5f\u5241\u4e00\u5200\u6d88 \u6d88\u6c14\u3002\u201d\u90a3\u5c11\u5e74\u5148\u751f\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0e\u4ed6\u4ec7\u6df1\u4f3c\u6d77\uff0c\u5f85\u6211\u5148\u62a5\u3002\u201d\u5d14\u4e91\u5b50\u5fc3\u4e2d\u751f\u6c14\uff0c\u60f3\u9053\uff1a \u201c\u8fd9\u5c11\u5e74\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u65f1\uff0c\u82e5\u975e\u6211\u5c06\u8427\u9752\u5cf0\u6349\u83b7\uff0c\u4f60\u7109\u80fd\u62a5\u4ec7\uff0c\u5c45\u7136\u8fd8\u6562\u4e0e\u6211\u4e89\u5148\u8bba \u540e\uff1f\u201d\u597d\u5947\u5fc3\u8d77\uff0c\u5fcd\u7740\u6c14\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4e0e\u4ed6\u6709\u4ec0\u4e48\u4ec7\uff1f\u8bf4\u6211\u542c\u542c\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u6628\u65e5\u5728 \u8def\u4e0a\u9047\u7740\u4ed6\u4eec\u5e08\u5f92\uff0c\u6211\u95ee\u4ed6\u7684\u5f92\u5f1f\u8ba8\u53e3\u6c34\u559d\uff0c\u8fd9\u8001\u513f\u9762\u4e0a\u5c45\u7136\u73b0\u51fa\u541d\u60dc\u4e4b\u8272\uff0c\u597d\u5728\u4ed6\u7684\u5f92\u5f1f \u7ed9\u6211\uff0c\u545c\u547c\uff0c\u53e3\u6e34\u80fd\u81f4\u4eba\u4e8e\u6b7b\uff0c\u89c1\u6b7b\u4e0d\u6551\uff0c\u6b64\u6df1\u4ec7\u4e4b\u4e00\u4e5f\u3002\u4eca\u665a\u665a\u95f4\uff0c\u8fd9\u5c0f\u54e5\u672c\u8981\u8bf7\u6211\u4e0e\u4ed6 \u540c\u4f4f\u84ec\u5e10\uff0c\u8fd9\u8001\u513f\u5374\u4e0d\u5e94\u5141\uff0c\u6211\u7684\u5e10\u84ec\u7834\u70c2\uff0c\u7ed9\u5bd2\u98ce\u62ec\u4e86\u8fdb\u6765\uff0c\u51e0\u4e4e\u51bb\u6b7b\uff0c\u545c\u547c\uff0c\u81f4\u4eba\u4e8e\u9965 \u5bd2\u4ea4\u8feb\u4e4b\u4e2d\uff0c\u6b64\u6df1\u4ec7\u4e4b\u4e8c\u4e5f\uff01\u201d \u3000\u3000\u8427\u9752\u5cf0\u4e0e\u8fd9\u5c11\u5e74\u7d20\u4e0d\u76f8\u8bc6\uff0c\u672c\u5df2\u5947\u602a\uff0c\u542c\u4ed6\u6447\u5934\u6446\u8111\u7684\u8bf4\u4e86\u4e00\u5927\u904d\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c\u5fc3\u9053\uff1a \u201c\u6211\u4e0e\u5b87\u513f\u8bf4\u7684\u8bf4\u8bdd\uff0c\u600e\u7684\u7ed9\u4ed6\u5077\u542c\u4e86\u53bb\uff1f\u201d \u3000\u3000\u5d14\u4e91\u5b50\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4f60\u8fd9\u53ae\u5c45\u7136\u6562\u62ff\u8001\u5b50\u6d88\u9063\uff01\u201d\u624b\u8d77\u4e00\u5200\uff0c\u4e0d\u65ab\u8427 \u9752\u5cf0\uff0c\u5374\u5411\u90a3\u5c11\u5e74\u4e66\u751f\u65ab\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u201c\u54ce\u5466\u201d\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u6b6a\uff0c\u5d14\u4e91\u5b50\u7adf\u7136\u6ca1\u6709\u65ab\u4e2d\uff0c\u53ea\u542c\u90a3\u5c11\u5e74\u4e66\u751f\u53c8\u53eb\u9053\uff1a \u201c\u4f60\u4e0d\u5411\u8fd9\u8001\u513f\u62a5\u4ec7\uff0c\u5374\u6765\u65ab\u6211\uff0c\u545c\u547c\uff0c\u6709\u4ec7\u4e0d\u62a5\uff0c\u53cd\u4f24\u540c\u4ec7\u4e4b\u4eba\uff0c\u4e16\u95f4\u5b81\u6709\u662f\u7406\u54c9\uff1f\u201d\u5d14 \u4e91\u5b50\u6c14\u6781\uff0c\u5237\u5237\u5237\u53c8\u662f\u4e00\u8fde\u4e09\u5200\uff0c\u522b\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u62a5\uff0c\u90a3\u5c31\u8ba9\u6211\u52a8\u624b\u5427\u3002\u6211\u672a\u6ee1 \u5e08\uff0c\u5e08\u7236\u4e0d\u51c6\u6211\u62ff\u5200\u5f04\u5251\uff0c\u7528\u6697\u5668\u5927\u7ea6\u8fd8\u53ef\u4ee5\u3002\u201d\u8eab\u8eaf\u4e71\u98a4\uff0c\u907f\u5f00\u5d14\u4e91\u5b50\u7684\u8fde\u73af\u5200\u65a9\uff0c\u9661\u7136 \u628a\u624b\u4e00\u626c\uff0c\u51e0\u9053\u7ec6\u82e5\u6e38\u4e1d\u7684\u91d1\u8272\u5149\u8292\uff0c\u5ffd\u5730\u5411\u8427\u9752\u5cf0\u98de\u53bb\uff0c\u8427\u9752\u5cf0\u7ed9\u70b9\u4e86\u7a74\u9053\uff0c\u4e0d\u80fd\u8f6c\u52a8\uff0c \u907f\u65e0\u53ef\u907f\uff0c\u5c11\u5e74\u4e66\u751f\u6240\u53d1\u7684\u91d1\u9488\u6697\u5668\uff0c\u5168\u90e8\u5c04\u5165\u4e86\u8427\u9752\u5cf0\u7684\u76ae\u8089\uff01 \u3000\u3000\u9648\u5929\u5b87\u5927\u9a87\uff0c\u4ed6\u542c\u4e86\u5c11\u5e74\u4e66\u751f\u620f\u5f04\u5d14\u4e91\u5b50\u7684\u90a3\u756a\u8bf4\u8bdd\uff0c\u672c\u4ee5\u4e3a\u4ed6\u662f\u53cb\u975e\u654c\uff0c\u4e0d\u6599\u4ed6\u7adf\u7136 \u771f\u7684\u7528\u6697\u5668\u6253\u4e86\u5e08\u5085\uff0c\u8fd9\u65f6\u4ed6\u7a74\u9053\u5df2\u89e3\uff0c\u4e0d\u6687\u601d\u7d22\uff0c\u4e00\u8dc3\u800c\u524d\uff0c\u5de6\u62f3\u53f3\u638c\uff0c\u4e00\u62db\u201c\u91d1\u9f13\u9f50 \u9e23\u201d\uff0c\u5c31\u6253\u90a3\u5c11\u5e74\u7684\u592a\u9633\u7a74\u3002\u90a3\u5c11\u5e74\u98d8\u8eab\u4e00\u95ea\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8499\u8d60\u6c34\uff0c\u4f60\u662f\u6211\u7684\u6069\u4eba\uff0c\u5927\u4e08\u592b \u6709\u6069\u62a5\u6069\uff0c\u6709\u4ec7\u62a5\u4ec7\uff0c\u6211\u7109\u80fd\u4e0e\u6069\u4eba\u52a8\u624b\uff1f\u201d\u8eab\u5f62\u5982\u7bad\uff0c\u7a9c\u51fa\u5e10\u7bf7\uff0c\u500f\u5ffd\u4e0d\u89c1\u3002 \u3000\u3000\u5d14\u4e91\u5b50\u8fde\u65a9\u90a3\u5c11\u5e74\u56db\u5200\uff0c\u8fde\u8863\u89d2\u4e5f\u6ca1\u6cbe\u7740\uff0c\u800c\u4eca\u53c8\u7a81\u89c1\u4ed6\u9732\u4e86\u8fd9\u624b\uff0c\u4ea6\u662f\u5927\u5927\u51fa\u4e4e\u610f\u6599 \u4e4b\u5916\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u771f\u662f\u90aa\u95e8\uff01\u201d\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u8427\u9752\u5cf0\u65f6\uff0c\u5ffd\u89c1\u8427\u9752\u8702\u53cc\u81c2\u62a1\u52a8\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5d14\u8001\u4e09\uff0c\u54b1\u4eec\u518d\u8f83\u91cf\u8f83\u91cf\uff01\u201d\u81c2\u4e0a\u80a9\u4e0a\uff0c\u6240\u4e2d\u7684\u91d1\u9488\u5c1a\u81ea\u9732\u51fa\u8863\u5916\uff0c\u53d1\u51fa\u707f\u7136\u91d1 \u5149\uff01 \u3000\u3000\u5374\u8bf4\u8427\u9752\u5cf0\u7ed9\u90a3\u5c11\u5e74\u4eba\u4e00\u628a\u91d1\u9488\u7a7f\u8863\u5165\u9aa8\uff0c\u5239\u90a3\u95f4\u4e5f\u662f\u60ca\u9a87\u4e4b\u6781\uff0c\u4e0d\u610f\u9aa4\u7136\u4e4b\u95f4\uff0c\u4f53\u5185 \u5ffd\u611f\u4e00\u9635\u6e05\u51c9\uff0c\u6c14\u8840\u6d41\u52a8\uff0c\u4e0d\u4f46\u7a74\u9053\u5df2\u89e3\uff0c\u800c\u4e14\u626d\u66f2\u7684\u7ecf\u8109\u4f3c\u4e4e\u4e5f\u5df2\u6062\u590d\u6b63\u5e38\uff0c\u9ebb\u75f9\u7684\u5173 \u8282\uff0c\u4ea6\u5df2\u80fd\u591f\u6d3b\u52a8\uff0c\u4e0d\u89c9\u53c8\u60ca\u53c8\u559c\u3002 \u3000\u3000\u5d14\u4e91\u5b50\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u89c1\u8427\u9752\u5cf0\u5c0f\u81c2\u4e00\u5f2f\uff0c\u547c\u7684\u4e00\u638c\u62cd\u51fa\uff0c\u5d14\u4e91\u5b50\u8fd0\u638c\u4e0a\u8fce\uff0c\u53ea\u89c9 \u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u9000\u4e86\u4e09\u6b65\uff0c\u5fc3\u4e2d\u5927\u5947\uff1a\u201c\u8fd9\u8001\u513f\u7684\u529f\u592b\u4e0d\u8fc7\u4ec5\u4ec5\u80dc\u6211\u4e00\u7b79\uff0c\u4f55\u4ee5 \u7a81\u7136\u4e4b\u95f4\uff0c\u5982\u6b64\u5389\u5bb3\uff1f\u201d\u4ed6\u53ef\u4e0d\u77e5\uff0c\u8427\u9752\u5cf0\u7684\u529f\u529b\u4e0d\u8fc7\u6062\u590d\u539f\u72b6\uff0c\u800c\u4ed6\u56e0\u6240\u53d7\u7684\u5185\u4f24\u6bd4\u8427\u9752 \u5cf0\u6c89\u91cd\uff0c\u867d\u4ed7\u96ea\u83b2\u6cbb\u597d\uff0c\u4f46\u5374\u6bd4\u5e73\u65e5\u6253\u4e86\u6298\u6263\uff0c\u6240\u4ee5\u4e00\u8f83\u4e4b\u4e0b\uff0c\u5c31\u663e\u5f97\u529f\u529b\u6bd4\u8427\u9752\u5cf0\u5f31\u4e86\u8bb8 \u591a\u3002 \u3000\u3000\u9648\u5929\u5b87\u89c1\u5e08\u5085\u7a81\u7136\u95f4\u6062\u590d\u6b63\u5e38\uff0c\u4e0d\u7981\u72c2\u559c\uff0c\u5ffd\u542c\u5f97\u5e08\u5085\u53eb\u9053\uff1a\u201c\u5b87\u513f\uff0c\u7559\u795e\uff01\u201d\u5d14\u4e91\u5b50 \u7684\u5f92\u5f1f\uff0c\u5de6\u53f3\u5939\u51fb\uff0c\u9648\u5929\u5b87\u4e00\u62db\u201c\u5f00\u5f13\u5c04\u96d5\u201d\u582a\u582a\u654c\u4f4f\uff0c\u6628\u65e5\u7d22\u6c34\u90a3\u7c97\u8c6a\u6c49\u5b50\uff0c\u500f\u7684\u4e00\u5200\u5288 \u6765\uff0c\u9648\u5929\u5b87\u90a3\u80fd\u529b\u654c\u4e09\u4eba\uff0c\u9669\u8c61\u7acb\u89c1\uff0c\u5200\u98ce\u659c\u5439\uff0c\u770b\u770b\u5288\u5230\uff0c\u5ffd\u542c\u5f97\u545b\u5577\u4e00\u58f0\uff0c\u90a3\u53e3\u5200\u6389\u5728 \u5730\u4e0a\uff0c\u90a3\u7c97\u8c6a\u6c49\u5b50\uff0c\u68d2\u7740\u53f3\u624b\uff0c\u5927\u58f0\u547c\u75db\u3002 \u3000\u3000\u8427\u9752\u5cf0\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u628a\u96f7\u9707\u5b50\u4e0e\u5d14\u4e91\u5b50\u7684\u4e94\u4e2a\u5f92\u5f1f\uff0c\u5175\u5203\u5168\u90e8\u6253\u98de\u3002\u8fd0\u638c\u5982\u98ce\uff0c\u7d27\u7d27 \u5411\u5d14\u4e91\u5b50\u8fdb\u8feb\u3002\u5d14\u4e91\u5b50\u89c1\u72b6\u4e0d\u5999\uff0c\u6025\u5fd9\u5927\u53eb\u201c\u626f\u547c\uff01\u201d\u4e00\u58f0\u80e1\u54e8\u3001\u7387\u9886\u5f92\u5f1f\u5e08\u4f84\uff0c\u6025\u6025\u9003 \u8dd1\u3002 \u3000\u3000\u9648\u5929\u5b87\u4ed7\u5251\u8d76\u53bb\uff0c\u8427\u9752\u5cf0\u53eb\u9053\uff1a\u201c\u7a77\u5bc7\u83ab\u8ffd\uff0c\u5b87\u513f\u56de\u6765\u3002\u201d\u9648\u5929\u5b87\u56de\u5230\u5e08\u7236\u8eab\u8fb9\uff1b\u6b63\u6b32 \u53d1\u95ee\uff0c\u53ea\u89c1\u5e08\u5085\u4e00\u53e3\u53e3\u7684\u5c06\u91d1\u9488\u62e8\u51fa\uff0c\u4e0d\u4f4f\u7684\u5567\u5567\u79f0\u5f02\uff0c\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u5085\uff0c\u8fd9\u662f\u600e\u4e48\u56de \u4e8b\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u533b\u672f\u4e4b\u4e2d\uff0c\u672c\u6709\u4e00\u79cd\u9488\u7078\u6cbb\u75c5\u4e4b\u6cd5\u3002\u4f46\u8fd9\u5c11\u5e74\u8fdc\u8fdc\u4e00\u63b7\uff0c\u4e03\u53e3\u91d1\u9488\uff0c\u90fd \u6b63\u6b63\u5c04\u4e2d\u6709\u5173\u7684\u7a74\u9053\uff0c\u628a\u7ecf\u8109\u5168\u90e8\u6253\u901a\uff0c\u4e0d\u4f46\u533b\u672f\u7cbe\u5999\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u66f4\u662f\u4e0d\u53ef\u601d\u8bae\uff01\u201d\u9648\u5929 \u5b87\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u6551\u5e08\u7236\u7684\uff0c\u521a\u624d\u6211\u51e0\u4e4e\u7ed9\u4ed6\u5413\u6b7b\uff01\u201d\u8427\u9752\u5cf0\u5ffd\u800c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u771f\u662f\u5929 \u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\uff0c\u8fd9\u4e66\u751f\u5e74\u7eaa\u8f7b\u7ecf\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5374\u8fdc\u5728\u6211\u4e4b\u4e0a\uff0c\u6211\u771f\u5982\u4e95\u86d9\u7aa5\u5929\uff0c\u4e0d\u77e5\u5929 \u5730\u4e4b\u5927\uff0c\u4ece\u4eca\u800c\u540e\uff0c\u6211\u771f\u4e0d\u6562\u518d\u4ee5\u6b66\u529f\u81ea\u70ab\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u5085\u5728\u6211\u5bb6\u5c06\u8fd1\u5341\u5e74\uff0c\u4e0a\u4e0b\u4eba\u7b49\uff0c\u4ece\u65e0\u4e00\u4eba\u77e5\u5e08\u5085\u662f\u5177\u6709\u7edd\u5927\u672c\u9886\u4e4b\u4eba\uff0c \u5e08\u5085\u7684\u6db5\u517b\u529f\u592b\uff0c\u4e16\u95f4\u7f55\u6709\u3002\u201d\u8427\u9752\u5cf0\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u77e5\u9053\uff0c\u6211\u5c11\u5e74\u4e4b\u65f6\uff0c\u5c31\u66fe\u56e0\u4e3a \u81ea\u70ab\u6b66\u529f\uff0c\u95ef\u4e0b\u5927\u7978\uff0c\u4e0e\u90a3\u51e0\u4e2a\u9b54\u5934\uff0c\u7ed3\u4e0b\u6df1\u4ec7\u3002\u201d\u9648\u5929\u5b87\u4ece\u672a\u542c\u8fc7\u5e08\u5085\u8bf4\u81ea\u5df1\u7684\u4e8b\uff0c\u4e0d\u89c9 \u7ad6\u8033\u7741\u542c\u3002 \u3000\u3000\u8427\u9752\u5cf0\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\uff0e\u5f53\u4eca\u5929\u4e0b\uff0c\u90a3\u4e00\u6d3e\u5251\u672f\u6700\u4e3a\u7cbe\u5999\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u5085\u4e0d\u662f \u8bf4\u8fc7\uff0c\u4ee5\u5929\u5c71\u6d3e\u7684\u5251\u672f\u6700\u4e3a\u7cbe\u5999\u5417\uff0c\u5929\u5c71\u4e00\u6d3e\uff0c\u81ea\u6666\u660e\u7985\u5e08\u624b\u5251\uff0c\u4f20\u51cc\u672a\u98ce\uff0c\u518d\u4f20\u81f3\u5510\u6653 \u6f9c\uff0c\u90fd\u662f\u4e00\u4ee3\u5927\u9655\uff0c\u60f3\u6765\u4e16\u95f4\u7f55\u6709\u5176\u5339\u4e86\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u5929\u5c71\u4e00\u6d3e\uff0c\u50fb\u5904\u585e\u5916\uff0c \u81ea\u5510\u6653\u6f9c\u5510\u5927\u4fa0\u4e4b\u540e\uff0c\u5373\u7f55\u81f3\u4e2d\u539f\u3002\u4e2d\u539f\u4e4b\u5185\uff0c\u5374\u4ee5\u5c11\u6797\uff0c\u6b66\u5f53\uff0c\u548c\u5ce8\u5d4b\u4e09\u6d3e\u88ab\u63a8\u4e3a\u6b66\u6797\u6b63 \u5b97\u3002\u6211\u9752\u57ce\u6d3e\uff0c\u8131\u80ce\u5ce8\u5d4b\uff0c\u4ea6\u81ea\u7acb\u95e8\u6237\uff0c\u4e2d\u539f\u4e09\u5927\u95e8\u6d3e\uff0c\u5404\u6709\u64c5\u957f\u3002\u201d\u9648\u5929\u5b87\u89c1\u5e08\u5085\u4e0e\u81ea\u5df1 \u8be6\u8bba\u6b66\u6797\u5251\u6d3e\uff0c\u751a\u662f\u51fa\u5947\u3002\u53ea\u542c\u5f97\u5e08\u5085\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u731c\u6211\u4eca\u5e74\u591a\u5c11\u5e74\u7eaa\uff1f\u201d\u9648\u5929\u5b87 \u770b\u4e86\u4e00\u770b\u5e08\u5085\u5934\u4e0a\u7684\u767d\u53d1\uff0c\u9053\uff1a\u201c\u5e08\u5085\u60f3\u6765\u4e0e\u6211\u7239\u76f8\u5dee\u4e0d\u8fdc\u5427\uff1f\u201d\u9648\u5929\u5b87\u7236\u4eb2\u5df2\u4e94\u5341\u6709\u4f59\u3002 \u8427\u9752\u5cf0\u9053\uff1a\u201c\u5fe7\u60a3\u4f59\u751f\uff0c\u53d1\u4e5f\u767d\u4e86\uff0c\u6211\u4eca\u5e74\u56db\u5341\u521a\u521a\u51fa\u5934\u3002\u201d\u9648\u5929\u5b87\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u8427\u9752\u5cf0\u7eed \u9053\uff1a\u201c\u5341\u4e8c\u5e74\u524d\uff0c\u6211\u5728\u56db\u5ddd\uff0c\u90a3\u5e74\u6070\u9047\u7740\u6b66\u5f53\u540d\u5bbf\u5192\u5ddd\u751f\u6bcf\u5341\u5e74\u4e00\u6b21\u7684\u5f00\u5c71\u7ed3\u7f18\u4e4b\u671f\u3002\u201d\u9648 \u5929\u5b87\u9053\uff1a\u201c\u5192\u5927\u4fa0\u548c\u5c1a\u5417\uff1f\u201d\u8427\u9752\u5cf0\u7b11\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u8bb2\u7ecf\u8bba\u9053\uff0c\u8c61\u548c\u5c1a\u90a3\u6837\u7684\u5e7f\u7ed3\u7f18\u5206\uff0c\u800c \u662f\u4e0e\u6b66\u6797\u540e\u8f88\u7ed3\u7f18\u3002\u542c\u8bf4\u5192\u5ddd\u751f\u662f\u524d\u8f88\u5251\u4fa0\uff0c\u6b66\u5f53\u5317\u6d3e\u8fbe\u6469\u5251\u6cd5\u5ae1\u7cfb\u4f20\u4eba\u6842\u4ef2\u660e\u4e4b\u5b50\uff0c\u53ea\u56e0 \u4ece\u6bcd\u4eb2\u4e4b\u59d3\uff0c\u627f\u7ee7\u5192\u6c0f\u9999\u706b\uff0c\u6240\u4ee5\u59d3\u5192\u3002\u4ed6\u662f\u4e2d\u539f\u6b66\u6797\u516c\u8ba4\u4e3a\u6b66\u529f\u6700\u9ad8\u4e4b\u4eba\u3002\u5192\u5927\u4fa0\u6700\u80af\u5609 \u60e0\u540e\u5b66\uff0c\u6bcf\u5341\u5e74\u5f00\u5c71\u4e00\u6b21\uff0c\u4e3b\u8bb2\u6b66\u529f\u5999\u7406\uff0c\u5e76\u56e0\u4eba\u800c\u65bd\uff0c\u6307\u70b9\u8bc0\u7a8d\u3002\u6240\u4ee5\u6bcf\u9022\u4ed6\u5f00\u5c71\u7ed3\u7f18\u4e4b \u671f\uff0c\u5404\u6d3e\u90fd\u6709\u9ad8\u8db3\u5165\u5c71\u4e8e\u8bb2\u3002\u90a3\u5e74\u6211\u4e5f\u6070\u9022\u5176\u4f1a\u3001\u96f7\u9707\u5b50\u3001\u5d14\u4e91\u5b50\u738b\u7624\u5b50\u4e09\u4eba\u5c31\u662f\u90a3\u5e74\u7ed3\u8bc6 \u7684\u90a3\u65f6\u738b\u7624\u5b50\u9888\u4e0a\u8fd8\u672a\u751f\u51fa\u7624\u5b50\uff0c\u53eb\u738b\u6d41\u5b50\u3002\u8fc7\u4e86\u90a3\u5e74\uff0c\u751f\u4e86\u7624\u540e\u6c5f\u6e56\u4e0a\u624d\u4ee5\u8bb9\u4f20\u8bb9\uff0c\u53eb\u4ed6 \u505a\u738b\u7624\u5b50\u7684\u3002\u5176\u65f6\u53c2\u52a0\u76db\u4f1a\u7684\uff0c\u8fd8\u6709\u5ce8\u5d4b\u6d3e\u7684\u4e00\u4f4d\u5973\u5f1f\u5b50\uff0c\u53eb\u505a\u5723\u624b\u4ed9\u5a18\u8c22\u4e91\u771f\uff0c\u542c\u8bf4\u662f\u5ce8 \u5d4b\u7b2c\u4e8c\u4ee3\u4e2d\u6b66\u529f\u6700\u9ad8\u7684\u4e00\u4f4d\uff0c\u201d\u8bf4\u5230\u8c22\u4e91\u771f\u7684\u540d\u5b57\u65f6\uff0c\u8427\u9752\u5cf0\u5fae\u5fae\u6218\u6296\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u9ad8\u539f\u7ec6\u8bf4\u5f53\u5e74\u60c5\uff0c\u5e73\u5730\u98ce\u6ce2\u6700\u607c\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u4e3a\u907f\u5f3a\u4ec7\u3000\u9003\u751f\u6765\u585e\u5916\u3000\u6b32\u5bfb\u4fa0\u58eb\u3000\u5192\u9669\u4e0a\u51b0\u5cf0 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u4e09 \u56de \u4e3a\u907f\u5f3a\u4ec7\u3000\u9003\u751f\u6765\u585e\u5916 \u6b32\u5bfb\u4fa0\u58eb\u3000\u5192\u9669\u4e0a\u51b0\u5cf0 \u3000\u3000\u8427\u9752\u5cf0\u5e73\u65e5\u559c\u6012\u4e0d\u5f62\u4e8e\u8272\uff0c\u8fd9\u65f6\u663e\u89c1\u5fc3\u60c5\u6fc0\u52a8\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8c22\u4e91\u771f\u4eba\u65e2\u7f8e\u8273\uff0c\u6b66\u529f\u53c8 \u9ad8\uff0c\u6027\u60c5\u4ea6\u751a\u548c\u853c\u3002\u6211\u4e0e\u5979\u5e08\u95e8\u672c\u6709\u4ea4\u60c5\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u53c8\u672c\u65e0\u7537\u5973\u4e4b\u89c1\uff0c\u662f\u4ee5\u5728\u5192\u5927\u4fa0\u5f00\u5c71 \u7ed3\u7f18\u4e4b\u671f\uff0c\u6211\u4fbf\u5e38\u4e0e\u5979\u4eb2\u8fd1\u3002\u201d\u9648\u5929\u5b87\u867d\u7136\u8fd8\u4e0d\u5927\u61c2\u7537\u5973\u60c5\u4e8b\uff0c\u89c1\u5e08\u7236\u8bf4\u8bdd\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u4e5f \u81ea\u660e\u767d\uff0c\u5e08\u7236\u60f3\u5fc5\u751a\u662f\u6b22\u559c\u90a3\u4e2a\u8c22\u4e91\u771f\u3002 \u3000\u3000\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e00\u65e5\uff0c\u6211\u4e0e\u5979\u8c08\u8bba\u5404\u6d3e\u6b66\u529f\u5251\u6cd5\uff0c\u5979\u8bf4\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u6b66\u5f53\u5251\u6cd5\uff0c\u867d\u7136\u540d\u95fb \u6d77\u5185\uff0c\u72ec\u6b65\u4e2d\u539f\uff0c\u4f46\u8bba\u5230\u5947\u529f\u5999\u6280\uff0c\u7384\u95e8\u6b63\u5b97\uff0c\u90a3\u5374\u8fd8\u8981\u6570\u5979\u5ce8\u5d4b\u8fd9\u6d3e\u3002\u81f3\u4e8e\u5176\u4ed6\u5404\u6d3e\uff0c\u90a3 \u662f\u81ea\u90fd\u4ee5\u4e0b\uff0c\u4e0d\u8db3\u8bba\u77e3\u3002\u6211\u6599\u4e0d\u5230\u5979\u7adf\u662f\u5982\u6b64\u81ea\u8d1f\uff0c\u5f53\u65f6\u5c11\u5e74\u610f\u76db\uff0c\u4fbf\u9053\uff1a\u2018\u6b64\u8bba\u4f3c\u4e0d\u6070 \u5f53\uff0c\u987b\u77e5\u5404\u6d3e\u90fd\u6709\u72ec\u7279\u7684\u6b66\u529f\uff0c\u6b66\u5b66\u4f3c\u65e0\u5929\u4e0b\u7b2c\u4e00\u4e4b\u7406\u3002\u2019\u5979\u542c\u4e86\u5fae\u5fae\u51b7\u7b11\uff0c\u4fbf\u4e0d\u518d\u8a00\u3002 \u3000\u3000\u8d74\u4f1a\u8bf8\u4eba\uff0c\u96f7\u9707\u5b50\u662f\u6b66\u5f53\u9ad8\u624b\uff0c\u5d14\u4e91\u5b50\u662f\u5025\u4f97\u9ad8\u624b\uff0c\u738b\u6d41\u5b50\u5219\u662f\u6c5d\u5357\u6b66\u5e08\u90d1\u5e73\u7684\u5f1f\u5b50\uff0c \u5d14\u4e91\u5b50\u8fd8\u6709\u4e00\u4e2a\u5f1f\u5f1f\u5d14\u96e8\u5b50\u4e5f\u662f\u5ce8\u5d4b\u6d3e\u95e8\u4eba\uff0c\u4e0d\u77e5\u56e0\u4f55\u7f18\u6545\uff0c\u88ab\u8d76\u51fa\u5e08\u95e8\uff0c\u8fd9\u6b21\u4e5f\u5230\u5c71\u4e2d\u542c \u8bb2\u3002\u8fd9\u56db\u4eba\u5e38\u5728\u4e00\u8d77\uff0c\u4e0e\u6211\u4ea6\u751a\u4e3a\u76f8\u5f97\u3002\u4e00\u4e00\u65e5\uff0c\u53c8\u662f\u8c08\u8bba\u5404\u6d3e\u6b66\u529f\uff0c\u96f7\u9707\u5b50\u9053\uff1a\u4ed6\u4eec\u7684\u638c \u95e8\u5192\u5927\u4fa0\u6b66\u529f\u76d6\u4e16\uff0c\u5f53\u7136\u662f\u6b66\u5f53\u6d3e\u7684\u6b66\u529f\u6700\u5f3a\u3002\u6211\u542c\u4e86\u4e0d\u670d\uff0c\u9a73\u4ed6\u9053\uff1a\u5404\u4eba\u8d44\u8d28\u4e0d\u540c\uff0c\u529f\u529b \u706b\u5019\u4e0d\u540c\uff0c\u5e08\u7236\u5929\u4e0b\u7b2c\u4e00\uff0c\u4e0d\u89c1\u5f97\u95e8\u4eba\u90fd\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u96f7\u9707\u5b50\u5f53\u573a\u4fbf\u8981\u548c\u6211\u6bd4\u5251\uff0c\u8bf4\u662f\u70b9\u5230 \u4e3a\u6b62\uff0c\u80dc\u8d25\u4e0d\u8bba\uff0c\u4e00\u6bd4\u4e4b\u4e0b\uff0c\u6211\u662f\u8f93\u4e86\uff0c\u4f46\u5176\u4e2d\u6211\u6709\u4e00\u62db\u2018\u661f\u843d\u9ad8\u539f\u2019\uff0c\u5374\u662f\u9752\u57ce\u6d3e\u72ec\u521b\u7684 \u62db\u6570\uff0c\u90a3\u4e00\u62db\u7a81\u7136\u4f7f\u51fa\uff0c\u4e5f\u628a\u96f7\u9707\u4e8e\u7684\u8863\u8896\u523a\u7a7f\uff0c\u6240\u4ee5\u8f93\u662f\u8f93\u4e86\uff0c\u5374\u4e5f\u4e0d\u7b97\u5f97\u5168\u8f93\u3002\u6bd4\u8bd5\u4e4b \u540e\uff0c\u96f7\u9707\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u5bf9\u6211\u518d\u4e09\u79f0\u8d5e\uff0c\u6211\u89c1\u4ed6\u80dc\u800c\u4e0d\u9a84\uff0c\u6beb\u65e0\u82a5\u8482\uff0c\u66f4\u662f\u8877\u5fc3\u548c\u4ed6\u7ed3\u7eb3\u3002 \u3000\u3000\u201c\u6211\u7ecf\u4e86\u6b64\u6b21\u4e4b\u540e\uff0c\u4fbf\u51b3\u5fc3\u4e0d\u518d\u4e0e\u4eba\u6bd4\u5251\uff0c\u8c01\u77e5\u4e16\u4e0a\u4e4b\u4e8b\uff0c\u66f4\u662f\u6599\u60f3\u4e0d\u5230\uff0c\u6211\u521a\u4e0b\u4e86\u51b3 \u5fc3\uff0c\u4e0d\u8fc7\u4e09\u65e5\uff0c\u53c8\u518d\u4e0e\u4eba\u6bd4\u5251\u5566\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u53c8\u662f\u54ea\u6d3e\u7684\u9ad8\u624b\u81ea\u5938\u6b66\u529f\uff0c\u4f60\u542c\u4e86\u4e0d\u670d\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u90a3 \u662f\u5192\u5927\u4fa0\u8bb2\u575b\u6563\u4f1a\u7684\u524d\u5915\uff0c\u738b\u6d41\u5b50\u5ffd\u7136\u4e00\u4e2a\u4eba\u8d70\u6765\uff0c\u6084\u6084\u5730\u62c9\u6211\u81f3\u201c\u50fb\u9759\u4e4b\u5904\u8bf4\u8bdd\uff0c\u8bf4\u5ce8\u5d4b \u5973\u4fa0\u8c22\u60f3\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u6b66\u529f\uff0c\u56e0\u6b64\u6697\u4e2d\u793a\u610f\u4e8e\u4ed6\uff0c\u8ba9\u4ed6\u4ee3\u7ea6\u6211\u53bb\u6bd4\u5251\u3002\u5e76\u7ea6\u5b9a\u5927\u5bb6\u90fd\u6234\u4e0a\u9762 \u5177\uff0c\u5728\u4e09\u66f4\u65f6\u5206\uff0c\u5230\u5c71\u540e\u6bd4\u8bd5\uff0c\u6bd4\u5b8c\u540e\uff0c\u5927\u5bb6\u4fbf\u8d70\uff0c\u5f53\u505a\u6ca1\u6709\u8fd9\u56de\u4e8b\uff0c\u8fd9\u6837\u8c01\u80dc\u8c01\u8d25\uff0c\u90e1\u4e0d \u4f1a\u4e0d\u597d\u610f\u601d\u3002\u6211\u672c\u6765\u4e0d\u5141\uff0c\u738b\u6d41\u5b50\u7b11\u9053\uff1a\u2018\u54fc\uff0c\u4f60\u8fd9\u50bb\u5b50\uff0c\u8c22\u4e91\u771f\u5bf9\u4f60\u751a\u6709\u610f\u601d\uff0c\u4f60\u7adf\u7136\u4e00 \u70b9\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u5979\u5bf9\u4f60\u7684\u4eba\u54c1\u4f69\u670d\u6781\u4e86\uff0c\u6709\u4e00\u6761\u5c31\u662f\u4e0d\u77e5\u4f60\u7684\u6b66\u529f\u6df1\u6d45\uff0c\u6240\u4ee5\u8fd8\u4e0d\u653e\u5fc3\u3002 \u5440\uff0c\u6211\u8bf4\u5f97\u5982\u6b64\uff0c\u81ea\u5df2\u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\u5979\u7684\u7528\u610f\u5417\uff1f\u2019\u6211\u542c\u4e86\u5fc3\u65cc\u6447\u6447\uff0c\u4e0d\u53ef\u6b62\u6b47\uff0c\u54ea\u91cc\u77e5 \u9053\uff0c\u8fd9\u5176\u4e2d\u85cf\u6709\u8be1\u8c0b\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8427\u9752\u5cf0\u51dd\u76ee\u591c\u7a7a\uff0c\u81ea\u987e\u81ea\u7684\u8bf4\u9053\uff1a\u201c\u987b\u77e5\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u7537\u5973\u76f8\u60a6\uff0c \u6700\u559c\u8f83\u91cf\u5bf9\u65b9\u7684\u6b66\u529f\uff0c\u5c31\u5982\u90a3\u4e9b\u535a\u8bfb\u8bd7\u4e66\u7684\u624d\u5973\uff0c\u9009\u62e9\u592b\u5a7f\uff0c\u4e5f\u8981\u5148\u770b\u5bf9\u65b9\u7684\u8bd7\u6587\u4e00\u6837\u3002\u6211 \u542c\u4e86\u81ea\u662f\u559c\u4e0d\u81ea\u80dc\uff0c\u4f46\u60f3\u5230\u8c22\u4e91\u771f\u6b66\u529f\uff0c\u53f7\u79f0\u5ce8\u5d4b\u7b2c\u4e8c\u4ee3\u7b2c\u4e00\u9ad8\u624b\uff0c\u76db\u540d\u4e4b\u4e0b\uff0c\u6599\u60f3\u65e0\u865a\uff0c \u5fc3\u4e2d\u53c8\u662f\u8e0c\u8e87\u96be\u51b3\u3002 \u3000\u3000\u738b\u6d41\u5b50\u4f3c\u662f\u77e5\u9053\u6211\u7684\u5fc3\u610f\uff0c\u7b11\u9053\uff1a\u2018\u8bba\u5230\u6b66\u529f\u5251\u6cd5\uff0c\u4f60\u4e5f\u7565\u900a\u4e8e\u5979\uff0c\u53ea\u662f\u6570\u5341\u62db\u5185\uff0c\u65ad \u4e4e\u4e0d\u4f1a\u843d\u8d25\u3002\u5979\u60ef\u4f7f\u201c\u7075\u79bd\u655b\u7fd8\u201d\u8fd9\u62db\uff0c\u6570\u5341\u62db\u5185\uff0c\u5fc5\u7136\u4f1a\u6709\u4e00\u6b21\u51fa\u73b0\u3002\u4f60\u90a3\u62db\u2018\u661f\u843d\u9ad8 \u539f\uff1a\u6b63\u662f\u5979\u8fd9\u62db\u7684\u514b\u661f\u3002\u9752\u57ce\u6d3e\u8131\u80ce\u5ce8\u5d4b\uff0c\u5176\u4e2d\u751a\u591a\u62db\u6570\uff0c\u4e43\u9488\u5bf9\u5ce8\u5d4b\u6d3e\u7684\u62db\u6570\u800c\u52a0\u4ee5\u53d8\u5316 \u7684\u3002\u6240\u4ee5\u738b\u6d41\u5b50\u4e4b\u8bf4\u5b9e\u662f\u4e0d\u5047\u3002 \u3000\u3000\u201c\u7b2c\u4e8c\u65e5\u591c\u95f4\uff0c\u6211\u4f9d\u7ea6\u5230\u540e\u5c71\u53bb\uff0c\u90a3\u665a\u6708\u9ed1\u98ce\u9ad8\uff0c\u5341\u6b65\u4e4b\u5916\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u6211\u5230\u4e86\u540e\u5c71\uff0c \u679c\u7136\u89c1\u7740\u4e00\u4e2a\u9ed1\u8863\u4eba\u5f71\uff0c\u6234\u7740\u9762\u5177\uff0c\u8eab\u6750\u4e0e\u8c22\u4e91\u771f\u76f8\u82e5\uff0c\u6211\u7d27\u5f20\u4e4b\u6781\uff0c\u4e0d\u6562\u8bf4\u8bdd\uff0c\u62d4\u5251\u51fa \u9798\uff0c\u6325\u52a8\u4e24\u4e0b\uff0c\u5c31\u5411\u5979\u8fdb\u62db\u3002 \u3000\u3000\u201c\u8fd9\u9ed1\u8863\u4eba\u5f71\u624b\u821e\u8db3\u8e48\uff0c\u542c\u5230\u6211\u7684\u5251\u73af\u4f5c\u54cd\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u524d\uff0c\u4e00\u53e3\u5251\u6cfc\u98ce\u4f3c\u7684\uff0c\u8fde\u8d70\u9669 \u62db\uff0c\u7740\u7740\u5411\u6211\u8981\u5bb3\u4e4b\u5904\u62db\u547c\uff0c\u7adf\u662f\u72b6\u82e5\u75af\u72c2\uff0c\u5982\u540c\u62fc\u547d\uff0c\u6211\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u96be\u9053\u8c22\u4e91\u771f\u8981 \u53d6\u6211\u7684\u6027\u547d\uff1f\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4e5f\u8bb8\u662f\u5979\u6545\u610f\u5982\u6b64\uff0c\u6765\u8feb\u6211\u732e\u51fa\u771f\u5b9e\u529f\u592b\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u60f3\u6cd5\uff0c\u5728\u5fc3\u4e2d\u4e00\u63a0\u5373\u8fc7\u3002\u5979\u7684\u5251\u52bf\u6765\u5f97\u5927\u731b\uff0c\u6211\u5df2\u7ecf\u65e0\u6687\u518d\u60f3\u5566\u3002\u6ca1\u5948\u4f55\u53ea\u5f97\u65bd \u5c55\u5168\u8eab\u672c\u9886\uff0c\u4e0e\u5979\u76f8\u6597\uff0c\u970e\u5ffd\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u975e\u4f46\u2018\u7075\u79bd\u655b\u7fc5\u2019\u8fd9\u4e00\u62db\u4e0d\u89c1\u51fa\u73b0\uff0c\u5373\u5979\u6240\u4f7f \u7684\u5251\u6cd5\u4e5f\u4e0d\u4f3c\u662f\u5ce8\u5d4b\u5251\u6cd5\uff0c\u5012\u50cf\u662f\u6b66\u5f53\u6d3e\u7684\uff0c\u6211\u60ca\u9a87\u83ab\u660e\uff0c\u6b63\u60f3\u51fa\u58f0\u76f8\u95ee\uff0c\u5ffd\u5730\u8df3\u51fa\u4e09\u6761\u9ed1 \u5f71\uff0c\u4e00\u9f50\u5411\u6211\u8fdb\u653b\u3002\u6211\u5bf9\u5979\u4e00\u4eba\u5df2\u662f\u5403\u529b\uff0c\u591a\u6dfb\u4e86\u4e09\u4e2a\u5f3a\u654c\uff0c\u7acb\u523b\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u201c\u6211\u5927\u53eb\u9053\uff1a\u2018\u5582\u5582\uff0c\u6211\u662f\u9752\u57ce\u6d3e\u7684\u8427\u9752\u5cf0\uff0c\u4f60\u4eec\u662f\u8c01\uff1f\u201d\u90a3\u4e09\u4eba\u4e00\u9f50\u51b7\u7b11\uff0c\u7b11\u58f0\u672a \u6b47\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u5a07\u7b11\uff0c\u4e00\u4e2a\u9752\u8863\u5c11\u5973\uff0c\u4ece\u6811\u68a2\u4e0a\u7a81\u7136\u98de\u4e0b\uff0c\u5979\u65e2\u4e0d\u6234\u9762\u5177\uff0c\u4e5f\u4e0d\u7a7f\u9ed1 \u8863\uff0c\u7adf\u4ee5\u672c\u6765\u9762\u76ee\u51fa\u73b0\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u5979\u662f\u8c22\u4e91\u771f\uff01\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u662f\u8c22\u4e91\u771f\uff0c\u6211\u60ca\u5f97\u5446\u4e86\uff0c\u5ffd\u542c\u5f97\u4fa7 \u9762\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u4e00\u6761\u9ed1\u5f71\u5411\u6211\u6251\u6765\uff0c\u4e00\u53e3\u660e\u6643\u6643\u7684\u5229\u5251\u5df2\u9012\u5230\u9762\u524d\uff0c\u4f7f\u7684\u6b63\u662f\u2018\u7075\u79bd\u655b \u7fc5\u2019\u7684\u62db\u6570\uff0c\u6211\u795e\u667a\u5df2\u4e71\uff0c\u6025\u4e8e\u6551\u547d\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u968f\u624b\u4e00\u62db\uff0c\u5251\u950b\u4e00\u843d\uff0c\u4f7f\u7684\u662f\u2018\u661f\u843d\u9ad8 \u539f\uff0c\uff0c\u90a3\u9ed1\u5f71\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u6761\u81c2\u818a\u7ed9\u6211\u524a\u4e86\u4e0b\u6765\uff0c\u8c22\u4e91\u771f\u8fd0\u5251\u5982\u98ce\uff0c\u6dae\u7684\u8865\u4e0a\u4e00\u5251\uff0c\u628a\u4ed6\u6740 \u6b7b\uff01 \u3000\u3000\u201c\u6211\u9a87\u5f97\u5927\u58f0\u547c\u53eb\uff0c\u4e0d\u77e5\u8bf4\u8bdd\u3002\u53ea\u89c1\u8c22\u4e91\u771f\u53c8\u662f\u4e24\u5251\uff0c\u5728\u5148\u524d\u548c\u6211\u5bf9\u654c\u7684\u90a3\u4eba\u8138\u4e0a\u5212\u4e86 \u4e24\u4e0b\uff0c\u50fb\u556a\u6709\u58f0\uff0c\u6562\u60c5\u662f\u8fd9\u4eba\u7684\u9762\u5177\u5df2\u7ed9\u5251\u950b\u5272\u7834\uff0c\u867d\u662f\u9ed1\u591c\uff0c\u4e5f\u89c1\u9c9c\u8840\u6cca\u65e7\u6d41\u4e0b\uff0c\u90a3\u4eba\u75db \u5f97\u53cc\u624b\u4e71\u6293\uff0c\u6293\u843d\u9762\u5177\u66f4\u662f\u60ca\u4eba\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u4ed6\u8138\u5b54\u4e00\u5b9a\u4f24\u5f97\u6781\u4e3a\u96be\u770b\uff0c\u6240\u4ee5\u5e08\u7236\u770b\u4e86\u5403\u60ca\u3002\u201d\u8427\u9752\u5cf0\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u4ed6 \u7684\u8138\u5b54\u7ed9\u5229\u5251\u5212\u6210\u4e00\u4e2a\u5341\u5b87\uff0c\u5de6\u8fb9\u773c\u73e0\uff0c\u4e5f\u7ed9\u5251\u5c16\u523a\u5f97\u51f8\u4e86\u51fa\u6765\uff0c\u9762\u76ee\u72f0\u72de\uff0c\u6709\u5982\u6076\u9b3c\u3002\u4f46 \u4ed6\u672c\u6765\u9762\u76ee\uff0c\u66f4\u662f\u60ca\u4eba\u3002\u4f60\u9053\u4ed6\u662f\u8c01\uff1f\u201d\u9648\u5929\u5b87\u542c\u5e08\u7236\u8bf4\u5f97\u6781\u4e3a\u53ef\u6015\uff0c\u867d\u7136\u672a\u7ecf\u76ee\u7779\uff0c\u4f46\u89c9 \u5fc3\u80c6\u7686\u5bd2\uff0c\u832b\u7136\u53cd\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u8427\u9752\u5cf0\u987f\u4e86\u4e00\u987f\uff0c\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u96f7\u9707\u5b50\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5475\uff0c\u600e\u4e48\u662f\u96f7 \u9707\u4e8e\uff1f\u201d\u8427\u9752\u5cf0\u7eed\u9053\uff1a\u201c\u8c22\u4e91\u771f\u51fa\u624b\u5feb\u6781\uff0c\u4f24\u4e86\u96f7\u9707\u5b50\u540e\uff0c\u4e00\u58f0\u5a07\u7b11\uff0c\u53f3\u624b\u957f\u5251\u4e00\u843d\uff0c\u5de6\u624b \u6697\u5668\u4e00\u626c\uff0c\u5587\u7684\u4e00\u58f0\uff0c\u2018\u55e4\u2019\u7684\u4e00\u54cd\uff0c\u4e24\u6761\u9ed1\u5f71\uff0c\u540c\u65f6\u4ec6\u5730\uff0c\u4e0e\u6211\u5bf9\u654c\u7684\u90a3\u56db\u4eba\uff0c\u4e00\u6b7b\u4e09 \u4f24\uff0c\u5168\u90fd\u57ae\u5566\u3002\u6211\u60ca\u9b42\u672a\u5b9a\uff0c\u53ea\u542c\u5f97\u8c22\u4e91\u771f\u7b11\u9053\uff1a\u2018\u4f60\u672c\u8be5\u4e5f\u53d7\u6211\u4e00\u5251\uff0c\u77a7\u4f60\u52a9\u6211\u7684\u4efd\u4e0a\uff0c \u9976\u4e86\u4f60\u5427\uff01\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u4fbf\u5373\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u6211\u64e6\u71c3\u706b\u77f3\uff0c\u89e3\u4e0b\u90a3\u4e09\u4eba\u7684\u9762\u5177\uff0c\u66f4\u662f\u5403\u60ca\uff0c\u6b7b\u7684\u5d14\u96e8\u5b50\uff0c\u7ed9\u6697\u5668\u6253\u4f24\u7684\u662f\u738b\u7624\u5b50\uff0c \u88ab\u5251\u523a\u4f24\u7684\u662f\u5d14\u4e91\u5b50\u3002\u96f7\u9707\u5b50\u5728\u5730\u4e0a\u6323\u624e\uff0c\u53cc\u624b\u6325\u821e\uff0c\u6211\u4e0a\u53bb\u60f3\u66ff\u4ed6\u88f9\u4f24\uff0c\u53ea\u542c\u5f97\u4ed6\u5389\u58f0\u559d \u9053\u3002\u2018\u6eda\u5f00\uff01\u738b\u7624\u5b50\u548c\u5d14\u4e91\u5b50\u4e5f\u90fd\u6012\u76ee\u800c\u89c6\uff0c\u4e09\u53cc\u773c\u775b\u5728\u9ed1\u591c\u4e4b\u4e2d\u95ea\u95ea\u53d1\u5149\uff0c\u597d\u50cf\u53d7\u4f24\u7684\u91ce \u72fc\u6012\u89c6\u730e\u4eba\u4e00\u6837\u3002\u6211\u7ed9\u4ed6\u4eec\u5413\u5f97\u6bdb\u9aa8\u60ca\u7136\uff0c\u7cca\u91cc\u7cca\u6d82\uff0c\u53cd\u8eab\u4fbf\u8dd1\uff0c\u8fde\u5192\u5927\u4fa0\u5904\uff0c\u4e5f\u4e0d\u53bb\u544a \u8f9e\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f3c\u662f\u90a3\u96f7\u9707\u5b50\u6709\u610f\u5bb3\u4f60\u3001\u4f46\u4e3a\u4f55\u5374\u626f\u4e86\u5ce8\u5d4b\u5973\u4fa0\u8c22\u4e91\u771f\uff1f\u201d\u8427 \u9752\u5cf0\u9053\uff1a\u201c\u4f60\u53ea\u731c\u5f97\u4e00\u534a\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u90a3\u96f7\u9707\u5b50\u548c\u5d14\u96e8\u5b50\u90fd\u66fe\u5411\u8c22\u4e91\u771f\u6c42\u5a5a\u4e0d\u9042\uff0c\u96f7\u9707 \u5b50\u7ed9\u7f9e\u8fb1\u4e86\u4e00\u756a\uff0c\u5d14\u96e8\u5b50\u56e0\u60f3\u7528\u5f3a\u4fae\u8fb1\u5e08\u59d0\uff0c\u56e0\u6b64\u88ab\u9010\u51fa\u5c71\u95e8\uff1b\u90a3\u665a\u662f\u96f7\u9707\u5b50\u7ea6\u8c22\u4e91\u771f\u6bd4 \u5251\uff0c\u96f7\u9707\u5b50\u4e0e\u5979\u7ea6\u5b9a\u5404\u6234\u9762\u5177\uff0c\u53c8\u6697\u4e2d\u57cb\u4f0f\u4e86\u5d14\u4e91\u5b50\u4e09\u4e2a\u9ad8\u624b\uff0c\u4ecd\u6015\u654c\u5979\u4e0d\u8fc7\uff0c\u4e8e\u662f\u53c8\u7528\u8ba1 \u53eb\u738b\u6d41\u5b50\u53eb\u6211\u51fa\u6765\uff0c\u60f3\u6211\u4e0e\u5979\u5148\u6597\u3001\u4ed6\u597d\u4ece\u4e2d\u53d6\u5229\u3002\u54ea\u77e5\u8c22\u4e91\u771f\u4e0d\u6653\u5f97\u7528\u4ec0\u4e48\u6cd5\u513f\uff0c\u672a\u5230\u65f6 \u5019\u5df2\u628a\u96f7\u9707\u5b50\u9a97\u4e86\u51fa\u6765\uff0c\u65bd\u7528\u6bd2\u624b\u628a\u4ed6\u9707\u7684\u7ecf\u8109\u9006\u884c\uff0c\u795e\u667a\u660f\u4e71\uff0c\u504f\u504f\u6211\u53c8\u5fc3\u6025\uff0c\u4e5f\u662f\u672a\u5230 \u4e09\u66f4\uff0c\u4fbf\u81f3\u5c71\u540e\uff0c\u98ce\u9ad8\u6708\u9ed1\uff0c\u96f7\u9707\u4e8e\u8eab\u6750\u53c8\u4e0e\u8c22\u4e91\u771f\u7565\u7565\u76f8\u4f3c\uff0c\u4e8e\u662f\u7cca\u5f04\u91cc\u7cca\u6d82\u52a8\u8d77\u624b\u6765\uff0c \u540e\u6765\u5d14\u4e91\u5b50\u4e09\u4eba\u4e00\u5230\uff0c\u4ee5\u4e3a\u6211\u5df2\u770b\u7834\uff0c\u53cd\u8fc7\u4e0e\u8c22\u4e91\u771f\u7ed3\u7eb3\uff0c\u4f24\u5bb3\u4ed6\u4eec\u7684\u5927\u54e5\uff0c\u4e8e\u662f\u4e00\u6d8c\u800c \u4e0a\u3002\u90a3\u5d14\u96e8\u5b50\u672c\u662f\u5ce8\u5d4b\u6d3e\u7684\uff0c\u795e\u5dee\u9b3c\u4f7f\uff0c\u6070\u6070\u53c8\u4f7f\u51fa\u4e86\u2018\u7075\u79bd\u655b\u7fc5\u2019\u90a3\u62db\uff0c\u4e27\u4e86\u6027\u547d\uff0c\u90a3\u665a \u82e5\u975e\u5982\u6b64\u9634\u5dee\u9633\u9519\uff0c\u8c22\u4e91\u771f\u6b66\u529f\u7eb5\u9ad8\uff0c\u6050\u6015\u4e5f\u4e0d\u662f\u4ed6\u4eec\u56db\u4eba\u4e4b\u654c\u3002 \u3000\u3000\u201c\u96f7\u9707\u5b50\u672c\u6765\u53f7\u79f0\u7389\u9762\u72d0\u72f8\uff0c\u7ed9\u8c22\u4e91\u771f\u5229\u5251\u6bc1\u5bb9\uff0c\u53c8\u7802\u4e00\u76ee\uff0c\u628a\u8c22\u4e91\u771f\u548c\u6211\u6068\u5230\u6781\u70b9\uff0c \u5d14\u4e91\u5b50\u6709\u6740\u5f1f\u4e4b\u4ec7\uff0c\u738b\u6d41\u5b50\u7ed9\u8c22\u4e91\u771f\u7684\u6bd2\u9488\u6240\u4f24\uff0c\u4f24\u597d\u4e4b\u540e\uff0c\u7ed3\u4e86\u4e2a\u7624\uff0c\u6b66\u529f\u4e5f\u518d\u7ec3\u4e0d\u5230\u539f \u6765\u5730\u6b65\u3002\u8c22\u4e91\u771f\u7ecf\u90a3\u665a\u4e4b\u540e\uff0c\u4fbf\u4e0d\u77e5\u8e2a\u8ff9\uff0c\u8fd9\u4e09\u4eba\u5c3d\u90fd\u8fc1\u6012\u4e8e\u6211\uff0c\u5341\u4f59\u5e74\u6765\uff0c\u5230\u5904\u8ffd\u8e2a\uff0c\u7acb \u8a93\u8981\u628a\u6211\u7f6e\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u542c\u5f97\u6bdb\u9aa8\u60ca\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5e08\u7236\u662f\u4e3a\u4e86\u9003\u907f\u4ed6\u4eec\uff0c\u624d\u5230\u6211\u5bb6\u6559\u4e66\uff0c\u4e0e\u6211\u4eec\u540c\u6765 \u897f\u85cf\u7684\u3002\u201d\u53ea\u542c\u5f97\u8427\u9752\u5cf0\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u662f\u65e0\u5984\u4e4b\u707e\uff0c\u90a3\u665a\u8fc7\u540e\uff0c\u6211\u5fe7\u6025\u4ea4\u714e\uff0c \u5c1a\u5728\u76db\u5e74\uff0c\u53d1\u5148\u767d\u4e86\u3002\u53ea\u662f\u6211\u8fd8\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u90a3\u738b\u6d41\u5b50\u4e0d\u77e5\u662f\u56e0\u4f55\u7f18\u6545\uff0c\u66ff\u4ed6\u4eec\u5e03\u4e0b\u8fd9\u6076\u6bd2 \u7684\u9690\u9636\uff1f\u201d\u9648\u5929\u5b87\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7ed9\u5e08\u7236\u4e00\u811a\u8e22\u4e0b\u51b0\u6e0a\u7684\u90a3\u4e2a\u4eba\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6b63\u662f\u90a3 \u4eba\u3002\u5440\uff0c\u6211\u8feb\u4e8e\u65e0\u5948\uff0c\u53c8\u6740\u4e86\u738b\u6d41\u5b50\uff0c\u8fd9\u51a4\u4ec7\u7ed3\u5f97\u66f4\u6df1\u4e86\u3002\u542c\u8bf4\u96f7\u9707\u5b50\u90a3\u6b21\u632b\u8d25\u4e4b\u540e\uff0c\u82e6\u5fc3 \u7ec3\u529f\uff0c\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u5f53\u5e74\u6211\u5df2\u4e0d\u662f\u4ed6\u7684\u654c\u624b\uff0c\u4eca\u540e\u76f8\u9022\uff0c\u53ea\u6015\u66f4\u96be\u5e78\u514d\uff01\u201d\u9648\u5929\u5b87 \u9053\uff1a\u201c\u542c\u4e86\u6b64\u4e8b\uff0c\u6211\u89c9\u5f97\u96f7\u9707\u5b50\u90a3\u51e0\u4eba\u56fa\u662f\u4e0d\u8be5\uff0c\u8c22\u4e91\u771f\u4e5f\u672a\u514d\u592a\u8fc7\u5fc3\u72e0\u624b\u8fa3\uff01\u201d \u3000\u3000\u8427\u9752\u5cf0\u5618\u4e86\u4e00\u58f0\uff0c\u5e10\u5916\u5bd2\u98ce\u6012\u53f7\uff0c\u5ffd\u542c\u5f97\u201c\u563f\u563f\u201d\u51b7\u7b11\u4e4b\u58f0\uff0c\u6df7\u6742\u5728\u98ce\u58f0\u4e4b\u4e2d\uff0c\u58f0\u97f3\u4e0d \u5927\uff0c\u5374\u662f\u6781\u5176\u6e05\u5cfb\uff0c\u8427\u9752\u5cf0\u4e00\u8dc3\u800c\u8d77\uff0c\u53ea\u89c1\u4e00\u7247\u4e1c\u897f\uff0c\u8f7b\u98d8\u98d8\u7684\u6251\u9762\u98de\u6765\uff0c\u8427\u9752\u5cf0\u65e0\u6687\u7406 \u4f1a\uff0c\u4e00\u95ea\u95ea\u8fc7\uff0c\u5954\u51fa\u5e10\u5916\uff0c\u53ea\u89c1\u55b7\u6cc9\u6e85\u73e0\uff0c\u51b0\u6cb3\u6620\u6708\uff0c\u5c71\u5934\u94f6\u767d\uff0c\u51b7\u51b7\u6e05\u6e05\uff0c\u8427\u9752\u5cf0\u5fc3\u5934\u4e00 \u9707\uff1a\u8fd9\u4eba\u7684\u8f7b\u529f\u600e\u7684\u5982\u6b64\u9ad8\u660e\uff0c\u7adf\u7136\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5c31\u9003\u5f97\u65e0\u8e2a\u65e0\u5f71\u3002 \u3000\u3000\u8427\u9752\u5cf0\u5fc3\u5934\u6014\u5fe1\uff0c\u8fd4\u8eab\u5165\u5e10\uff0c\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u7236\u4f60\u770b\uff01\u201d\u58f0\u97f3\u98a4\u6296\uff0c\u8427\u9752\u5cf0\u671d\u4ed6\u624b\u6307\u4e4b \u5904\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u7247\u725b\u76ae\uff0c\u4e0a\u7aef\u7262\u9644\u5728\u5e10\u5e55\u5e06\u5e03\u4e0a\uff0c\u4e0b\u8fb9\u4e24\u89d2\uff0c\u5374\u5377\u8d77\u6765\uff0c\u98d8\u98d8\u8361\u8361\u3002\u8427\u9752\u5cf0 \u5fc3\u4e2d\u4e00\u51db\uff0c\u8fd9\u7247\u725b\u76ae\u867d\u6bd4\u666e\u901a\u7684\u7eb8\u8d28\u4e3a\u539a\uff0c\u5230\u5e95\u662f\u4e0d\u53d7\u529b\u4e4b\u7269\uff0c\u6765\u4eba\u7adf\u7136\u7528\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5c06 \u5b83\u5f39\u4e86\u8fdb\u6765\uff0c\u9644\u5728\u5e10\u4e0a\uff0c\u5185\u52b2\u4e4b\u795e\u5999\uff0c\u5b9e\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u90a3\u7247\u725b\u76ae\u4e0a\u7aef\u7528\u4e24\u53e3\u5c0f\u9489\u9489\u4f4f\uff0c\u9648\u5929 \u5b87\u5c55\u4e86\u5f00\u6765\uff0c\u53ea\u89c1\u4e0a\u9762\u5212\u6709\u4e24\u884c\u5c0f\u5b87\uff0c\u5b87\u8ff9\u68f1\u89d2\u56db\u9732\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u7528\u6307\u7532\u5212\u7684\uff0c\u4e0d\u89c9\u53c8\u662f\u4e00 \u60ca\uff0c\u5ff5\u9053\uff1a\u201c\u6e56\u6d77\u98d8\u84ec\u5341\u6570\u5e74\uff0c\u6c5f\u5357\u6f20\u5317\u6bcf\u6d6a\u8fde\uff0c\u8bf7\u541b\u65e9\u5230\u5929\u6e56\u4f1a\uff0c\u95ee\u8baf\u5f53\u5e74\u94c1\u62d0\u4ed9\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u76ee\u5149\u95ea\u52a8\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u96f7\u9707\u5b50\uff0c\u8c01\u77e5\u5374\u662f\u94c1\u62d0\u4ed9\uff0c\u54a6\uff0c\u8fd9\u5012\u5947 \u4e86\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u8c01\u662f\u94c1\u62d0\u4ed9\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u94c1\u62d0\u4ed9\u662f\u4e8c\u5341\u5e74\u524d\u7eb5\u6a2a\u6e56\u5357\u7684\u4e00\u4f4d\u602a\u4fa0\uff0c \u542c\u8bf4\u662f\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u524d\u8f88\u7684\u5f92\u5f1f\uff0c\u7518\u98ce\u6c60\u628a\u4ed6\u5e08\u5144\u4e86\u56e0\u7684\u94c1\u62d0\uff0c\u5728\u5cb7\u5c71\u77f3\u58c1\u4e0a\u53d6\u4e0b\u6765\uff0c\u4f20 \u6388\u7ed9\u4ed6\u2026\u2026\u201d\u9648\u5929\u5b87\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u4e86\u56e0\u7684\u94c1\u6756\uff0c\u4f55\u4ee5\u4f1a\u63d2\u5728\u5cb7\u5c71\u77f3\u58c1\u4e0a\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e86 \u56e0\u5f53\u521d\u662f\u6c5f\u5357\u516b\u4fa0\u4e4b\u9996\uff0c\u4e0e\u7518\u51e4\u6c60\u6709\u534a\u5e08\u4e4b\u4efd\uff0c\u540e\u6765\u4e86\u56e0\u80cc\u53db\u5e08\u95e8\uff0c\u6c5f\u5357\u4e03\u4fa0\u5728\u5cb7\u5c71\u5e08\u7236\u5893 \u524d\uff0c\u8054\u5251\u8bdb\u51f6\uff0c\u7531\u5973\u4fa0\u5415\u56db\u5a18\u6740\u4e86\u4ed6\uff0c\u4e86\u56e0\u6597\u8d25\u4e4b\u540e\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u628a\u94c1\u62d0\u4e00\u63b7\uff0c\u63d2\u5165\u5cb7\u5c71\u77f3 \u58c1\uff0c\uff08\u6309\uff1a\u6b64\u6bb5\u60c5\u4e8b\u8be6\u89c1\u62d9\u8457\u300a\u6760\u6e56\u4e09\u5973\u4fa0\u300b\uff0c\u6b64\u5904\u4e0d\u8d58\u3002\uff09\u7518\u98ce\u6c60\u540e\u6765\u5c06\u5b83\u53d6\u4e0b\uff0c\u4f20\u4e0e\u7231 \u5f92\uff0c\u60f3\u662f\u4e3a\u4e86\u5ff5\u53ca\u5f53\u5e74\u4e86\u56e0\u4ee3\u5e08\u4f20\u6388\u4e4b\u60c5\uff0c\u6240\u4ee5\u8ba9\u4ed6\u7684\u7985\u6756\u4f20\u4f5c\u672c\u95e8\u4e4b\u5b9d\uff0c\u7518\u51e4\u6c60\u7684\u5f92\u5f1f\u672c \u540d\u53eb\u505a\u5415\u9752\uff0c\u5f97\u4e86\u5e08\u4f2f\u7684\u7985\u6756\u4e4b\u540e\uff0c\u6539\u4e3a\u94c1\u62d0\uff0c\u7531\u7518\u51e4\u6c60\u6388\u4ed6\u4e00\u767e\u96f6\u516b\u8def\u62ab\u98ce\u62d0\u6cd5\uff0c\u6545\u6b64\u53f7 \u79f0\u94c1\u62d0\u4ed9\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u94c1\u62d0\u4ed9\u548c\u5e08\u7236\u4ea4\u60c5\u600e\u6837\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u51fa\u9053\u4e4b\u65f6\uff0c\u4ed6\u5df2\u540d\u6ee1\u6c5f\u6e56\uff0c \u6211\u867d\u7136\u6155\u4ed6\u4e4b\u540d\uff0c\u5374\u662f\u65e0\u7f18\u62dc\u89c1\u3002\u201d\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5e08\u7236\u4e0e\u94c1\u62d0\u4ed9\u5e76\u65e0\u4e00\u9762\u4e4b \u7f18\uff0c\u4f55\u4ee5\u4ed6\u53c8\u7ea6\u4f60\u5230\u5929\u6e56\u76f8\u4f1a\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u662f\u5440\uff0c\u6b64\u4e8b\u6211\u4ea6\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u53cd\u6b63\u6211\u8981\u5230 \u5929\u6e56\u53bb\u627e\u4e00\u4f4d\u5f02\u4eba\uff0c\u82e5\u80fd\u5728\u90a3\u91cc\u9047\u89c1\u94c1\u62d0\u4ed9\uff0c\u5012\u662f\u4e00\u4ef6\u5e78\u4e8b\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u60f3\u8d77\u4e86\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\u4e4b\u8a00\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5e08\u7236\u627e\u7684\u5f02\u4eba\uff0c\u53ef\u662f\u51b0\u5ddd\u5929\u5973 \u4e48\uff1f\u201d\u8427\u9752\u5cf0\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u51b0\u5ddd\u5929\u5973\uff1f\u8fd9\u540d\u5b87\u597d\u602a\uff0c\u6211\u53ef\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u3002\u51b0\u5ddd\u5929\u5973\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ea\u542c\u5f97\u90a3\u85cf\u65cf\u5c11\u5973\u8bf4\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u4f4f\u5728\u5929\u6e56\u3002\u201d\u9042\u628a\u4e0a\u534a \u591c\u5728\u51b0\u5ca9\u4e0a\u9047\u89c1\u85cf\u65cf\u5c11\u5973\u7b49\u4e4b\u60c5\u4e8b\u8bf4\u4e86\u4e00\u904d\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u5e08\u7236\u6240\u8981\u627e\u7684\u5f02\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u542c\u8bf4\u5192\u5ddd\u751f\u5927\u4fa0\u7684\u5f1f\u5f1f\u6842\u534e\u751f\uff0c\u5c11\u5e74\u4e4b\u65f6\uff0c\u56e0\u4e0e\u5929\u5c71\u6d3e\u7684\u5510\u6653\u6f9c\u592b\u5987\u8f83 \u91cf\u5251\u6cd5\uff0c\u8f93\u4e86\u4e00\u62db\uff0c\u8d1f\u6c14\u8fdc\u8d70\u897f\u85cf\uff0c\u9690\u5c45\u5929\u6e56\uff0c\u6b64\u4e8b\u5f97\u4e8e\u4f20\u95fb\uff0c\u4e0d\u77e5\u662f\u5426\u5c5e\u5b9e\u3002\u4f46\u5982\u4eca\u6211\u53d7 \u5f3a\u4ec7\u8ffd\u9010\uff0c\u90a3\u96f7\u9707\u5b50\u7684\u6b66\u529f\u53c8\u662f\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u7b2c\u4e00\u9ad8\u624b\uff0c\u8fdc\u975e\u6211\u6240\u80fd\u654c\uff0c\u5728\u6b64\u50fb\u58e4\u7a77\u8fb9\uff0c\u53c8\u65e0 \u4eba\u53ef\u4ee5\u63f4\u624b\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u5e0c\u5180\u6842\u5927\u4fa0\u5c1a\u5728\u4eba\u95f4\uff0c\u53ef\u4ee5\u4e3a\u6211\u89e3\u6b64\u56f0\u5384\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u600e \u4e48\u5192\u5927\u4fa0\u7684\u5f1f\u5f1f\u5374\u53c8\u59d3\u6842\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6842\u4ef2\u660e\u524d\u8f88\u4e0e\u5192\u70f7\u83b2\u5973\u4fa0\u7ed3\u4e3a\u592b\u5987\uff0c\u5171\u751f\u4e09\u5b50\uff0c \u4e00\u4f9d\u7236\u59d3\uff0c\u4e00\u4f9d\u6bcd\u59d3\uff0c\u4e00\u4f9d\u4e49\u7236\u4e4b\u59d3\uff0c\u5404\u5404\u4e0d\u540c\uff0c\u5927\u54e5\u53eb\u5192\u5ddd\u751f\uff0c\u4e8c\u54e5\u53eb\u77f3\u5e7f\u751f\uff0c\u4e09\u5f1f\u53eb\u6842 \u534e\u751f\u3002\u4e09\u4eba\u4e4b\u4e2d\u5192\u5ddd\u751f\u5185\u529f\u6700\u9ad8\uff0c\u6842\u534e\u751f\u5251\u6cd5\u6700\u597d\u3002\u4ed6\u8f88\u4efd\u6781\u9ad8\uff0c\u82e5\u7136\u4ed6\u80af\u4f38\u624b\uff0c\u96f7\u9707\u5b50\u7edd \u5bf9\u4e0d\u6562\u901e\u5f3a\uff0c\u5440\uff0c\u53ea\u4e0d\u77e5\u9053\u4ed6\u662f\u5426\u5c1a\u5728\u4eba\u95f4\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u90a3\u94c1\u62d0\u4ed9\u7684\u6b66\u529f\u6bd4\u96f7\u9707\u5b50\u5982 \u4f55\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e00\u522b\u5341\u4f59\u5e74\uff0c\u6211\u4e5f\u4e0d\u77e5\u96f7\u9707\u5b50\u7684\u6b66\u529f\u53c8\u5230\u4e86\u5982\u4f55\u795e\u5999\u4e4b\u5883\uff1f\u53ea\u662f\u770b\u9002\u624d \u94c1\u62d0\u4ed9\u6240\u9732\u90a3\u624b\uff0c\u96f7\u9707\u5b50\u8c05\u4e5f\u4e0d\u80fd\u80dc\u4ed6\u3002\u201d\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u94c1\u62d0\u4ed9\u4e0e\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u7ea6\u6211\u5230 \u5929\u6e56\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff1f\u96f7\u9707\u5b50\u662f\u6b66\u5f53\u6d3e\u7684\u4eba\uff0c\u6b66\u5f53\u6d3e\u4ea4\u6e38\u5e7f\u9614\uff0c\u82e5\u7136\u94c1\u62d0\u4ed9\u662f\u96f7\u9707\u5b50\u7ea6\u6765\u7684 \u4eba\uff0c\u90a3\u6211\u5c31\u66f4\u7cdf\u4e86\u3002\u201d\u9648\u5929\u5b87\u672c\u60f3\u5efa\u8bae\u5e08\u7236\u8bf7\u94c1\u62d0\u4ed9\u76f8\u52a9\uff0c\u89c1\u4ed6\u5982\u6b64\u8bf4\u6cd5\uff0c\u5fc3\u4e2d\u66f4\u662f\u4e0d\u5b89\u3002 \u3000\u3000\u5e08\u5f92\u4e24\u4eba\u5728\u7834\u70c2\u7684\u7bf7\u5e10\u4e2d\u4f4f\u4e86\u534a\u665a\uff0c\u5bd2\u98ce\u900f\u9aa8\uff0c\u51b7\u5f97\u9648\u5929\u5b87\u7259\u5173\u6253\u6218\uff0c\u597d\u5bb9\u6613\u71ac\u5230\u5929 \u660e\uff0c\u6536\u62fe\u884c\u674e\uff0c\u5374\u89c1\u6628\u665a\u90a3\u4f19\u4eba\u7684\u7bf7\u5e10\uff0c\u4ecd\u7136\u7559\u5728\u5f53\u5730\uff0c\u60f3\u662f\u56e0\u4e3a\u9003\u8d70\u5306\u5fd9\uff0c\u6765\u4e0d\u53ca\u5e26\u8d70\u3002 \u9648\u5929\u5b87\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u4fbf\u5c06\u7bf7\u5e10\u5377\u4e86\uff0c\u8427\u9752\u5cf0\u77aa\u4ed6\u4e00\u773c\uff0c\u5ffd\u800c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5185\u529f\u672a\u5230\u706b \u5019\uff0c\u96be\u53d7\u4e25\u5bd2\uff0c\u597d\uff0c\u5c31\u8ba9\u4f60\u5c06\u8fd9\u7bf7\u5e10\u5e26\u8d70\u5427\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u628a\u55b7\u6cc9\u7684\u70ed\u6c34\uff0c\u7ecf\u8fc7\u8fc7\u6ee4\u51b7\u5374\uff0c\u53c8\u76db\u6ee1\u4e86\u4e09\u4e2a\u6c34\u56ca\u3002\u4e24\u5e08\u5f92\u8de8\u4e0a\u9a6c\u80cc\uff0c\u7eed\u5411\u524d \u884c\uff0c\u7b2c\u4e00\u65e5\u5929\u6c14\u5c1a\u597d\uff0c\u7b2c\u4e8c\u65e5\u5374\u4e0b\u8d77\u9761\u9761\u7684\u96ea\u96e8\u6765\uff0c\u51b7\u5f97\u9648\u5929\u5b87\u597d\u4e0d\u96be\u53d7\u3002 \u3000\u3000\u7b2c\u4e09\u65e5\u5929\u867d\u653e\u6674\uff0c\u79ef\u96ea\u878d\u5316\uff0c\u66f4\u662f\u5bd2\u51b7\u3002\u65e5\u5934\u8fc7\u5348\uff0c\u4e24\u4eba\u521a\u51fa\u5c71\u53e3\uff0c\u5730\u52bf\u5f00\u9614\uff0c\u65e5\u5580\u5219 \u57ce\u9690\u9690\u5728\u671b\uff0c\u8427\u9752\u5cf0\u559c\u9053\uff1a\u201c\u4eca\u65e5\u665a\u95f4\u53ef\u4ee5\u8d76\u5230\u65e5\u5580\u5219\u4e86\u3002\u201d\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9762\u6709\u5f02 \u8272\u3002\u9648\u5929\u5b87\u773c\u5229\uff0c\u53ea\u89c1\u5728\u5c71\u53e3\u659c\u5761\u4e4b\u4e0a\uff0c\u7761\u7740\u4e00\u4e2a\u4e5e\u4e10\uff0c\u90a3\u4e5e\u4e10\u53d1\u5982\u4e71\u8349\uff0c\u534a\u9762\u8138\u57cb\u5728\u79ef\u96ea \u4e4b\u4e2d\uff0c\u5934\u6795\u5728\u4e00\u6280\u94c1\u62d0\u4e4b\u4e0a\uff0c\u8eab\u4e0a\u8863\u670d\u7834\u7834\u70c2\u70c2\uff0c\u9732\u51fa\u6765\u7684\u808c\u8089\u51bb\u5f97\u901a\u7ea2\uff0c\u9648\u5929\u5b87\u751f\u4e86\u601c\u60af \u4e4b\u60c5\uff0c\u4e0a\u53bb\u5c06\u4ed6\u8f7b\u8f7b\u4e00\u63a8\uff0c\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4e0d\u8981\u7761\u5728\u8fd9\u513f\uff01\u201d\u90a3\u602a\u53eb\u5316\u4fa7\u4e86\u4fa7\u8eab\uff0c\u51e0\u4e4e\u6eda \u4e0b\uff0c\u9648\u5929\u5b87\u6025\u5fd9\u5c06\u4ed6\u6276\u4f4f\uff0c\u90a3\u602a\u53eb\u5316\u4e00\u4f38\u61d2\u8170\uff0c\u5ffd\u9053\uff0c\u201c\u4e0d\u8981\u78b0\u6211\u3002\u201d\u9648\u5929\u5b87\u8fd9\u624d\u53d1\u73b0\u4ed6\u5de6 \u8db3\u957f\u53f3\u8db3\u77ed\uff0c\u539f\u6765\u662f\u4e2a\u7638\u5b50\uff0c\u8fde\u5fd9\u9053\u6b49\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u8981\u4e1c\u897f\u5403\u4e48\uff1f\u201d\u90a3\u53eb\u5316\u7f13\u7f13\u62fe\u8d77\u5934 \u6765\uff0c\u9648\u5929\u5b87\u6708\u5149\u4e0e\u4ed6\u76f8\u63a5\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u4ed6\u9762\u5982\u9505\u5e95\uff0c\u914d\u4e0a\u6ee1\u5934\u4e71\u53d1\uff0c\u5947\u4e11\u65e0\u6bd4\uff0c\u773c \u5149\u51b0\u51b7\u51b7\u7684\u5c04\u4f4f\u9648\u592d\u5b87\uff0c\u9648\u5929\u5b87\u6253\u4e86\u4e2a\u5bd2\u6218\uff0c\u90a3\u4e5e\u4e10\u6709\u6c14\u6ca1\u529b\u7684\u9053\uff1a\u201c\u653e\u4e0b\u3002\u201d\u9648\u5929\u5b87\u653e\u4e0b \u4e00\u888b\u5e72\u7cae\uff0c\u4ed6\u6beb\u4e0d\u9053\u8c22\uff0c\u4fa7\u4e86\u4fa7\u8eab\uff0c\u8138\u5b54\u53c8\u57cb\u5165\u79ef\u96ea\u4e4b\u4e2d\u3002\u9648\u5929\u5b87\u5076\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u5e08\u7236\u76ee\u5149 \u5145\u6ee1\u5fe7\u8651\u4e4b\u8272\uff0c\u793a\u610f\u53eb\u4ed6\u5feb\u8d70\uff0c\u9648\u5929\u5b87\u89e3\u4e0b\u8eab\u4e0a\u7684\u9a7c\u7ed2\u5916\u5957\uff0c\u8f7b\u8f7b\u76d6\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u56de\u5230\u5e08\u7236 \u8eab\u65c1\u3002\u4e24\u5e08\u5f92\u9a70\u51fa\u4e86\u5c71\u53e3\u3002\u8d70\u4e0b\u5e73\u5730\uff0c\u8427\u9752\u5cf0\u8fd9\u624d\u957f\u957f\u5401\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u9648\u5929\u5b87\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u53ef\u6709\u4ec0\u4e48\u4e0d\u5bf9\u4e48\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u6ce8\u610f\u4ed6\u90a3\u679d\u94c1\u62d0\uff1f\u201d \u9648\u5929\u5b87\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u94c1\u62d0\u4ed9\u5417\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u6ca1\u89c1\u8fc7\u94c1\u62d0\u4ed9\uff0c\u6211\u4e5f\u672a\u542c\u8bf4\u8fc7\u94c1 \u62d0\u4ed9\u662f\u4e2a\u7638\u5b50\u3002\u8fd9\u602a\u53eb\u5316\u7684\u90a3\u652f\u94c1\u62d0\uff0c\u7c97\u5982\u7897\u53e3\uff0c\u770b\u4e0a\u53bb\u603b\u6709\u4e94\u4e03\u5341\u65a4\uff0c\u5bfb\u5e38\u7684\u53eb\u5316\u54ea\u80fd\u63d0 \u5f97\u5b83\u52a8\uff1f\u4f55\u51b5\u4ed6\u5c45\u7136\u7761\u5728\u659c\u5761\u4e4b\u4e0a\uff0c\u79ef\u96ea\u4e4b\u4e2d\uff0c\u4fbf\u53ef\u65ad\u5b9a\u4ed6\u4e0d\u662f\u5bfb\u5e38\u4e4b\u4eba\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u82e5\u7136\u4ed6\u662f\u94c1\u62d0\u4ed9\uff0c\u5e08\u7236\u548c\u4ed6\u5957\u4e2a\u4ea4\u60c5\uff0c\u5c82\u4e0d\u751a\u597d\uff1f\u201d\u8427\u9752\u5cf0\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u521d\u8d70\u6c5f\u6e56\uff0c\u4e0d \u77e5\u6c5f\u6e56\u7684\u89c4\u77e9\uff1f\u82e5\u7136\u4ed6\u662f\u94c1\u62d0\u4ed9\uff0c\u6211\u5c31\u66f4\u4e0d\u80fd\u5728\u6b64\u9645\u4e0e\u4ed6\u62db\u547c\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u662f\u4e3a \u4f55\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4ed6\u7ea6\u6211\u5230\u5929\u6e56\u76f8\u4f1a\uff0c\u662f\u53cb\u662f\u654c\uff0c\u5c1a\u672a\u5206\u660e\u3002\u4f9d\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\uff0c\u6211\u5c31\u5e94\u5230 \u5929\u6e56\u624d\u80fd\u4e0e\u4ed6\u76f8\u89c1\uff0c\u6211\u82e5\u9053\u7834\u4ed6\u7684\u884c\u85cf\uff0c\u4fbf\u662f\u6c5f\u6e56\u4e4b\u5fcc\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u82e5\u7136\u4e0d\u662f\u94c1\u62d0\u4ed9 \u5462\uff1f\u201d\u8427\u9752\u9053\uff1a\u201c\u4f3c\u6b64\u6c5f\u6e56\u5f02\u4eba\uff0c\u4e0d\u660e\u5e95\u7ec6\uff0c\u66f4\u662f\u4e0d\u5b9c\u62db\u60f9\uff0c\u4f60\u6ca1\u5fd8\u8bb0\u4e09\u65e5\u4e4b\u524d\uff0c\u4f60\u62db\u60f9\u6765 \u7684\u90a3\u4f19\u5f3a\u4eba\u5417\uff1f\u201d\u9648\u5929\u5b87\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u62db\u60f9\u4e86\u90a3\u4f19\u5f3a\u5f92\uff0c\u867d\u662f\u5f15\u72fc\u5165\u5ba4\uff0c\u96be\u8f9e\u5176 \u548e\uff0c\u4f46\u7ed3\u7eb3\u4e86\u90a3\u4e2a\u4e66\u751f\uff0c\u5374\u4e5f\u5f97\u4e86\u610f\u5916\u4e4b\u52a9\u3002\u5e08\u7236\u53ef\u662f\u592a\u8fc7\u8c28\u614e\u5c0f\u5fc3\u4e86\u3002\u201d\u867d\u6709\u6b64\u60f3\uff0c\u5374\u4e0d \u4fbf\u4e0e\u5e08\u7236\u8fa9\u9a73\uff0c\u53ea\u6709\u968f\u7740\u5e08\u7236\uff0c\u5feb\u9a6c\u52a0\u97ad\uff0c\u8d81\u7740\u65e5\u5934\u672a\u843d\uff0c\u5306\u5306\u8d76\u8def\u3002 \u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u679c\u7136\u8d76\u5230\u4e86\u65e5\u5580\u5219\u57ce\uff0c\u65e5\u5580\u5219\u867d\u662f\u897f\u85cf\u7684\u4e00\u4e2a\u540d\u57ce\uff0c\u4f46\u8fb9\u8352\u4e4b\u5730\uff0c\u65c5\u4eba\u6765\u5f80 \u4e0d\u591a\uff0c\u57ce\u4e2d\u53ea\u6709\u4e00\u95f4\u50cf\u6837\u7684\u5ba2\u5e97\uff0c\u4e24\u5e08\u5f92\u8d70\u5165\u5ba2\u5e97\uff0c\u5e97\u4fdd\u89c1\u4ed6\u4eec\u8863\u886b\u4e0d\u4fd7\uff0c\u6025\u5fd9\u5f15\u8fdb\uff0c\u521a\u521a \u6b65\u4e0a\u53f0\u9636\uff0c\u5ffd\u95fb\u5f97\u91cc\u9762\u4e00\u9635\u55a7\u95f9\u4e4b\u58f0\u3002 \u3000\u3000\u8427\u9752\u5cf0\u628a\u773c\u4e00\u770b\uff0c\u767b\u65f6\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u4e00\u4e2a\u9e51\u8863\u767e\u7ed3\u7684\u5316\u5b50\uff0c\u53f3\u8db3\u7fd8\u8d77\uff0c\u94c1\u62d0\u6491\u5730\uff0c\u652f \u6301\u8eab\u4f53\uff0c\u6c14\u547c\u547c\u5730\u9053\uff1a\u201c\u4f60\u4eec\u5f00\u5ba2\u5e97\u7684\u600e\u4e48\u4e0d\u8ba9\u6211\u8fdb\u6765\u4f4f\u5bbf\uff0c\u54fc\uff0c\u54fc\uff01\u4f60\u4eec\u72d7\u773c\u770b\u4eba\u4f4e\uff0c\u5148 \u656c\u7f57\u8863\u540e\u656c\u4eba\uff0c\u89c1\u5927\u7237\u8863\u88f3\u7834\u70c2\uff0c\u5c31\u4e0d\u62db\u5f85\u5417\uff1f\u201d\u94c1\u62d0\u4e00\u987f\uff0c\u4e00\u5757\u65b9\u7816\u767b\u65f6\u88c2\u4e86\u3002\u638c\u67dc\u7684\u5fc3 \u4e2d\u4e00\u614e\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u7237\u4f11\u8981\u52a8\u6012\uff0c\u5c0f\u5e97\u8d44\u91d1\u77ed\u5c11\uff0c\u5411\u6765\u89c4\u77e9\uff0c\u623f\u94b1\u996d\u94b1\uff0c\u8981\u8bf7\u5ba2\u4eba\u5148 \u60e0\u3002\u201d\u90a3\u5316\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff0c\u4f60\u6015\u5927\u7237\u6ca1\u94b1\u5417\uff1f\u201d\u4f38\u624b\u4e00\u6478\uff0c\u7adf\u7136\u5728\u8eab\u4e0a\u6478 \u51fa\u4e00\u952d\u5143\u5b9d\uff0c\u4ed6\u8863\u88f3\u7834\u70c2\uff0c\u4e5f\u4e0d\u77e5\u8fd9\u5143\u5b9d\u662f\u600e\u6837\u85cf\u7684\uff1f\u53ea\u89c1\u4ed6\u5c06\u5143\u5b9d\u556a\u7684\u4e00\u58f0\uff0c\u6401\u5728\u67dc\u4e0a\uff0c \u9053\uff1a\u201c\u7ed9\u6211\u4e00\u95ee\u4e0a\u623f\uff0c\u6253\u4e24\u65a4\u9152\uff0c\u5bb0\u4e00\u53ea\u80a5\u9e21\uff0c\u597d\u597d\u670d\u4f8d\u4f60\u7684\u5927\u7237\u3002\u600e\u4e48\uff1f\u4f60\u77aa\u5927\u773c\u775b\u770b\u6211 \u505a\u4ec0\u4e48\uff1f\u94b1\u4e0d\u591f\u5417\uff1f\u201d\u638c\u67dc\u7684\u54ea\u6599\u5f97\u5230\u8fd9\u53eb\u5316\u5b50\u5c45\u7136\u6709\u4e00\u952d\u5927\u65e0\u5b9d\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u623f \u94b1\u996d\u94b1\u4e8c\u4e24\u94f6\u5b50\u5df2\u7ecf\u591f\u4e86\uff0c\u5c0f\u4e8c\uff0c\u62ff\u628a\u79e4\u5b50\u6765\uff0c\u79e4\u4e00\u79e4\u8fd9\u4e2a\u5143\u5b9d\uff0c\u591a\u4f59\u7684\u627e\u56de\u8fd9\u4f4d\u5927\u7237\u3002\u201d \u90a3\u5316\u5b50\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u627e\u5566\uff0c\u591a\u4f59\u7684\u7ed9\u4f60\u3002\u4f60\u5927\u7237\u660e\u65e5\u4e00\u65e9\u4fbf\u8d70\uff0c\u4f60\u4eec\u4ee5 \u540e\u2018\u62db\u5b50\u2019\uff08\u773c\u73e0\uff09\u653e\u4eae\u4e00\u4e9b\uff0c\u522b\u89c1\u5230\u50cf\u5927\u7237\u4e00\u6837\u7684\u7a77\u670b\u53cb\uff0c\u5c31\u8d76\u5fd9\u7684\u8981\u63a8\u4ed6\u51fa\u53bb\u3002\u201d\u638c\u67dc \u7684\u5927\u559c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u5c0f\u5e97\u62db\u5f85\u4e0d\u5468\uff0c\u4f60\u5927\u7237\u591a\u591a\u5305\u6db5\uff01\u201d\u5fd9\u53eb\u5e97\u5c0f\u4e8c\u5f00\u4e86\u4e00\u95f4\u4e0a \u623f\u3002 \u3000\u3000\u8fd9\u5316\u5b50\u6b63\u662f\u4ed6\u4eec\u65e5\u95f4\u6240\u89c1\u7684\u602a\u4e10\uff0c\u8427\u9752\u5cf0\u5fc3\u5185\u6697\u6697\u5600\u5495\uff0c\u4ed6\u4eec\u9a91\u7684\u662f\u9a6c\uff0c\u8fd9\u5316\u5b50\u5c45\u7136\u6bd4 \u4ed6\u4eec\u5148\u5230\uff0c\u5c31\u7b97\u662f\u4ed6\u53e6\u6284\u6377\u5f84\uff0c\u8fd9\u901f\u5ea6\u4e5f\u662f\u5feb\u5f97\u9a87\u4eba\u3002\u8427\u9752\u5cf0\u672c\u5f85\u9000\u51fa\uff0c\u4f46\u5df2\u4e0a\u4e86\u53f0\u9636\uff0c\u9000 \u4e0b\u53bb\u592a\u9732\u75d5\u8ff9\uff0c\u5e78\u597d\u90a3\u5316\u5b50\u773c\u89d2\u4e5f\u4e0d\u779f\u4ed6\u4eec\u4e00\u4e0b\uff0c\u4fbf\u968f\u5e97\u5c0f\u4e8c\u8fdb\u623f\u53bb\u4e86\u3002 \u3000\u3000\u8427\u9752\u5cf0\u8981\u4e86\u4e00\u95f4\u5927\u623f\uff0c\u5173\u4e0a\u623f\u95e8\uff0c\u4e24\u5e08\u5f92\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u4e2d\u6b63\u5728\u53d1\u6101\uff0c\u8427\u9752\u5cf0\u8981\u4e86\u4e00\u4e9b\u996d \u83dc\uff0c\u80e1\u4e71\u5403\u4e86\u4e00\u987f\uff0c\u5ffd\u542c\u5f97\u9a6c\u58f0\u957f\u5636\uff0c\u53c8\u6765\u4e86\u4e24\u4e2a\u5ba2\u4eba\uff0c\u4e00\u8fdb\u95e8\u4fbf\u547c\u559d\u638c\u68fa\u7684\u7ed9\u4ed6\u4eec\u5f00\u623f\u5907 \u996d\uff0c\u4ece\u7a97\u53e3\u671b\u51fa\uff0c\u6765\u7684\u5374\u662f\u4e24\u4e2a\u519b\u5b98\u3001\u524d\u884c\u7684\u90a3\u4e2a\u80c1\u4e0b\u631f\u7740\u4e00\u4e2a\u7ea2\u6f06\u6728\u7bb1\uff0c\u4f3c\u4e4e\u5341\u5206\u5b9d\u91cd\uff0c \u4ed6\u4eec\u8981\u7684\u623f\u95f4\uff0c\u6070\u597d\u5728\u8427\u9752\u5cf0\u78b0\u9762\u3002 \u3000\u3000\u8427\u9752\u5cf0\u659c\u773c\u4e00\u77a5\uff0c\u5ffd\u89c1\u659c\u5bf9\u9762\u90a3\u95f4\u623f\u5b50\uff0c\u4e5f\u6709\u4e24\u4e2a\u4eba\u63a2\u51fa\u5934\u6765\uff0c\u5934\u4e0a\u7f20\u7740\u767d\u5e03\uff0c\u78a7\u773c\u7ea2 \u987b\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u897f\u57df\u4eba\u3002\u8fd9\u4e24\u4eba\u4e00\u63a2\u5934\u5c31\u7f29\u4e86\u8fdb\u53bb\uff0c\u9762\u4e0a\u73b0\u51fa\u8be1\u5f02\u7684\u7b11\u5bb9\uff0c\u8427\u9752\u5cf0\u53c8\u662f\u4e00 \u60ca\uff0c\u5f85\u5c0f\u4e8c\u6765\u6536\u62fe\u4e4b\u65f6\uff0c\u8427\u9752\u5cf0\u7ed9\u4e86\u4ed6\u4e00\u4e24\u94f6\u5b50\u8d4f\u94b1\u3002\u95ee\u659c\u5bf9\u9762\u623f\u7684\u90a3\u4e24\u4e2a\u756a\u5ba2\u662f\u4ec0\u4e48\u4eba\uff0c \u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4ed6\u4eec\u53fd\u54e9\u5495\u55dc\u7684\u8bf4\u8bdd\u6211\u542c\u4e0d\u61c2\uff0c\u542c\u638c\u67dc\u8bf4\uff0c\u4ed6\u61c2\u5f97\u8bb8\u591a\u79cd\u6d3b\uff0c\u4ed6\u8bf4\u8fd9\u4e24\u4eba\u662f\u4ece \u5c3c\u6cca\u5c14\u6765\u7684\u6b66\u58eb\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u53bb\u540e\uff0c\u9648\u5929\u5b87\u9053\uff1a\u201c\u53bb\u5e74\u5c3c\u6cca\u5c14\u56fd\u7684\u5eca\u5c14\u5580\u65cf\u4fb5\u5165\u897f\u85cf\uff0c\u6740\u4e86\u8bb8\u591a\u7267\u6c11\uff0c\u62a2\u4e86\u4e0d \u5c11\u725b\u7f8a\uff0c\u540e\u6765\u7ed9\u671d\u5ef7\u6d3e\u5175\u6253\u9000\u4e86\uff0c\u5dee\u4e0d\u591a\u4e00\u5e74\uff0c\u4ed6\u4eec\u7684\u4eba\u4e0d\u6562\u518d\u8fdb\u897f\u85cf\uff0c\u6700\u8fd1\u6211\u542c\u7238\u7238\u8bf4\uff0c \u4ed6\u4eec\u89c1\u4e8b\u60c5\u5df2\u6de1\uff0c\u53c8\u8822\u8822\u6b32\u52a8\u3002\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u53ea\u6015\u4e0d\u662f\u4ec0\u4e48\u597d\u8def\u9053\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a \u201c\u4e24\u56fd\u63a5\u58e4\uff0c\u672c\u6765\u4e0d\u5e94\u4e92\u76f8\u654c\u89c6\uff0c\u6062\u590d\u5f80\u6765\uff0c\u4e43\u5c5e\u6b63\u5e38\u3002\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\uff0c\u4e5f\u6709\u4fa0\u4e49\u4e4b\u4eba\uff0c\u5012 \u4e0d\u53ef\u4e00\u6982\u800c\u8bba\u3002\u201d\u9648\u5929\u5b87\u70b9\u4e86\u70b9\u5934\uff0c\u8427\u9752\u5cf0\u53c8\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u77a7\u51fa\u6709\u4ec0\u4e48\u8def\u9053\u4e0d\u5bf9\uff0c\u4e5f\u4e0d\u5b9c\u52a8 \u624b\u3002\u201d \u3000\u3000\u4e24\u5e08\u5f92\u6b63\u5728\u95f2\u8bdd\uff0c\u7a97\u5916\u4eba\u5f71\u4e00\u6643\uff0c\u9648\u5929\u5b87\u4ece\u7a97\u9699\u77a7\u51fa\uff0c\u53ea\u89c1\u4e00\u4e2a\u7ea2\u9762\u8001\u5934\uff0c\u866c\u9aef\u5982\u8f7d\uff0c \u5728\u5ead\u9662\u4e2d\u8e31\u6765\u8e31\u53bb\uff0c\u5ffd\u800c\u4ef0\u5929\u6b4c\u9053\uff1a\u201c\u8d3a\u5170\u5c71\u4e0b\u9635\u5982\u4e91\uff0c\u7fbd\u6a84\u4ea4\u9a70\u65e5\u5915\u95fb\uff0c\u8bd5\u62c2\u94c1\u8863\u5982\u96ea \u7ec3\uff0c\u804a\u5c06\u5b9d\u5251\u52a8\u661f\u6587\u3002\u613f\u5f97\u71d5\u5f13\u5c04\u5927\u5c06\uff0c\u803b\u4ee4\u8d8a\u7532\u9e23\u543e\u541b\u3002\u201d\u6b4c\u58f0\u672a\u4e86\uff0c\u5bf9\u9762\u623f\u7684\u519b\u5b98\u9a82 \u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5728\u5916\u9762\u4e71\u5531\uff0c\u5435\u5f97\u8001\u5b50\u4e0d\u80fd\u5b89\u7761\uff0c\u518d\u5531\u4ffa\u5c31\u51fa\u53bb\u63cd\u4f60\u4e00\u987f\uff0c\u8ba9\u4f60\u53eb\u4e2a\u75db \u5feb\uff01\uff1b\u2019\u90a3\u8001\u5934\u54c8\u54c8\u4e00\u7b11\uff0c\u5e76\u4e0d\u52a8\u6012\uff0c\u4e5f\u4e0d\u56de\u5634\uff0c\u8d70\u56de\u81ea\u5df1\u623f\u95f4\u53bb\u4e86\u3002\u4ed6\u7684\u623f\u95f4\u6b63\u5728\u8427\u9752\u5cf0 \u7684\u53f3\u624b\u8fb9\u3002 \u3000\u3000\u9648\u5929\u5b87\u56de\u8f6c\u5934\u6765\uff0c\u53ea\u89c1\u5e08\u7236\u53cc\u76ee\u95ea\u95ea\u653e\u5149\uff0c\u9732\u51fa\u53c8\u60ca\u53c8\u559c\u7684\u795e\u8272\uff0c\u9648\u5929\u5b87\u95ee\u9053\uff1a\u201c\u8fd9\u8001 \u5934\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u6709\u4e86\u6551\u661f\u4e86\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u8fd9\u4f4d\u8001 \u82f1\u96c4\u540d\u53eb\u9ea6\u6c38\u660e\uff0c\u662f\u9655\u7518\u4e24\u7701\u6700\u8d1f\u76db\u540d\u7684\u5927\u4fa0\uff0c\u6b66\u529f\u7cbe\u6df1\uff0c\u4eba\u83ab\u80fd\u6d4b\uff1b\u800c\u4e14\u53e4\u9053\u70ed\u80a0\uff0c\u559c\u6b22 \u66ff\u4eba\u6392\u96be\u89e3\u7eb7\uff0c\u548c\u6211\u5e08\u95e8\u9887\u6709\u6e0a\u6e90\uff0c\u53ea\u4e0d\u77e5\u4ed6\u4e3a\u4f55\u4e5f\u4f1a\u81f3\u6b64\uff1f\u201d\u6c89\u541f\u534a\u664c\uff0c\u6b63\u60f3\u5f00\u623f\u524d\u53bb\u62dc \u8bbf\uff0c\u5ffd\u89c1\u5de6\u624b\u8fb9\u90a3\u95ee\u623f\u95f4\uff0c\u90a3\u4e2a\u602a\u53eb\u5316\u9732\u51fa\u5934\u6765\uff0c\u671d\u7740\u8427\u9752\u5cf0\u7684\u623f\u95f4\u7b11\u4e86\u4e00\u7b11\uff0c\u8427\u9752\u5cf0\u51dd\u601d \u4e00\u9635\uff0c\u5ffd\u5730\u4e00\u53e3\u6c14\u5439\u7184\u706f\u706b\uff0c\u548c\u8863\u7761\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u8be7\u9053\uff1a\u201c\u5e08\u7236\u4e3a\u4f55\u4e0d\u53bb\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u8fd9\u95f4\u5ba2\u5e97\uff0c\u4eca\u665a\u6765\u4e86\u8fd9\u4e48\u591a\u80fd\u4eba\uff0c\u770b\u6765 \u5b9a\u4f1a\u95f9\u4e8b\u3002\u6211\u6682\u65f6\u4e14\u4e0d\u9732\u9762\uff0c\u770b\u770b\u518d\u8bf4\u3002\u201d\u9648\u5929\u5b87\u5fc3\u60c5\u7d27\u5f20\uff0c\u4f38\u624b\u5c06\u6401\u5728\u51e0\u4e0a\u7684\u6697\u5668\u56ca\u4e00 \u62c9\uff0c\u653e\u5728\u6795\u5934\u5e95\u4e0b\uff0c\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5b87\u513f\uff0c\u4eca\u665a\u4e0d\u8bba\u5916\u9762\u95f9\u5f97\u5730\u8986\u5929\u7ffb\uff0c\u90fd\u4e0d\u51c6\u4f60\u8d77\u8eab\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u542c\u5e08\u7236\u5982\u6b64\u8bf4\u6cd5\uff0c\u5fc3\u60c5\u66f4\u662f\u7d27\u5f20\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u5408\u4e0d\u4e0a\u773c\uff0c\u53ef\u662f\u5916\u9762\u9759\u6084\u6084\u7684\uff0c\u4ec0 \u4e48\u58f0\u97f3\u4e5f\u6ca1\u6709\uff0c\u8f6c\u77ac\u542c\u5f97\u6572\u4e86\u4e09\u66f4\u53c8\u6572\u4e86\u56db\u66f4\uff0c\u4ecd\u662f\u6beb\u65e0\u52a8\u9759\uff0c\u9648\u5929\u5b87\u71ac\u4e0d\u4f4f\u4e86\uff0c\u660f\u660f\u601d \u7761\uff0c\u5ffd\u89c1\u9ed1\u5f71\u4e00\u6643\uff0c\u539f\u6765\u662f\u5e08\u7236\u8d77\u8eab\uff0c\u9648\u5929\u5b87\u5413\u4e86\u4e00\u8df3\uff0c\u8427\u9752\u5cf0\u5728\u4ed6\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d \u8981\u52a8\uff0c\u6211\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u5e76\u4e0d\u77e5\u9053\uff0c\u5916\u9762\u5c4b\u9876\u4e0a\u6b63\u6709\u4eba\u63a0\u8fc7\uff0c\u53ea\u662f\u6b64\u4eba\u8f7b\u529f\u592a\u9ad8\uff0c\u8eab\u5f62\u8fc7\u5904\uff0c\u53ea\u662f\u5fae\u98ce\u98d2 \u7136\uff0c\u9648\u5929\u5b87\u542c\u4e0d\u51fa\u6765\uff0c\u8427\u9752\u5cf0\u5374\u5df2\u542c\u51fa\uff0c\u8fd9\u662f\u5f62\u610f\u95e8\u7684\u4e0a\u4e58\u8eab\u6cd5\uff0c\u9ea6\u6c38\u660e\u6b63\u662f\u5f62\u610f\u95e8\u7684\u540d \u5bbf\uff0c\u60f3\u6742\u9664\u4e86\u662f\u4ed6\uff0c\u66f4\u65e0\u4ed6\u4eba\u3002 \u3000\u3000\u8427\u9752\u5cf0\u65e9\u6362\u4e86\u4e00\u8eab\u9ed1\u8272\u7684\u591c\u884c\u8863\u670d\uff0c\u4e00\u7a9c\u8eab\u4ece\u7a97\u53e3\u98de\u51fa\uff0c\u53ea\u5200\u6761\u9ed1\u5f71\uff0c\u5df2\u9644\u5728\u5bf9\u9762\u623f\u95f4 \u7684\u5c4b\u6a90\uff0c\u63a2\u5934\u5185\u671b\u3002\u8427\u9752\u5cf0\u4e5f\u98de\u591a\u5c11\u5c4b\uff0c\u90a3\u9ed1\u5f71\u5ffd\u7136\u56de\u8fc7\u5934\u6765\uff0c\u6b63\u662f\u9655\u7518\u5927\u4fa0\u9ea6\u6c38\u660e\u3002 \u3000\u3000\u8427\u9752\u5cf0\u6025\u5fd9\u8fde\u6253\u624b\u52bf\uff0c\u793a\u610f\u662f\u540c\u9053\u4e2d\u4eba\u3002\u9ea6\u6c38\u660e\u5341\u4f59\u5e74\u524d\u89c1\u8fc7\u8427\u9752\u5cf0\uff0c\u6b64\u65f6\u4f9d\u7a00\u8bb0\u5f97\uff0c \u4e3e\u8d77\u53f3\u624b\u6447\u4e86\u4e24\u6447\uff0c\u793a\u610f\u53eb\u4ed6\u4e0d\u53ef\u591a\u7ba1\u95f2\u4e8b\u3002\u8427\u9752\u5cf0\u5728\u5c4b\u9876\u7684\u51f9\u5904\u4e00\u4f0f\uff0c\u5f20\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u90a3 \u4e24\u519b\u5b98\u6240\u4f4f\u7684\u623f\u95f4\uff0c\u623f\u4e2d\u70b9\u7740\u4e00\u652f\u7c97\u5982\u513f\u81c2\u7684\u5927\u725b\u6cb9\u70db\uff0c\u7a97\u95e8\u5927\u5f00\uff0c\u623f\u5185\u9f3e\u58f0\u5982\u96f7\u3001\u7adf\u4f3c\u662f \u5f00\u95e8\u63d6\u76d7\u3002\u8427\u9752\u5cf0\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5e03\u7f6e\uff0c\u975e\u6709\u5927\u672c\u9886\u4e4b\u4eba\u4e0d\u6562\u5982\u6b64\uff0c\u6c5f\u6e56\u4e0a\u7684\u591c\u884c\u4eba\uff0c\u82e5\u7136 \u4e0d\u77e5\u5bf9\u65b9\u865a\u5b9e\uff0c\u89c1\u4e86\u8fd9\u7b49\u5e03\u7f6e\uff0c\u5b9a\u7136\u6084\u6084\u6e9c\u8d70\uff0c\u4e0d\u6562\u4fb5\u6270\u3002\u60f3\u4e0d\u5230\u8fd9\u4e24\u4e2a\u519b\u5b98\uff0c\u7adf\u7136\u4e5f\u662f\u6c5f \u6e56\u4e0a\u7684\u5927\u884c\u5bb6\u3002\u201d \u3000\u3000\u9ea6\u6c38\u660e\u5927\u7ea6\u4e5f\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u5728\u7a97\u5916\u5f20\u671b\u597d\u4e45\uff0c\u8e0c\u8e87\u672a\u51b3\u3002\u623f\u5185\u9f3e\u58f0\u8d8a\u6765\u8d8a\u54cd\uff0c\u9ea6\u6c38\u660e\u5ffd \u4f3c\u7a81\u7136\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4e00\u62bd\u5b9d\u5251\uff0c\u5982\u71d5\u5b50\u7a7f\u5e18\uff0c\u98de\u8eab\u76f4\u5165\u3002 \u3000\u3000\u8427\u9752\u5cf0\u8eab\u5f62\u6025\u8d77\uff0c\u7a9c\u5230\u4e86\u9ea6\u6c38\u660e\u9002\u624d\u7684\u4f4d\u7f6e\uff0c\u8fd9\u53ea\u662f\u7535\u5149\u77f3\u706b\u822c\u7684\u77ac\u606f\u4e4b\u4e8b\uff0c\u53ea\u89c1\u9ea6\u6c38 \u660e\u4e00\u5165\u623f\u4e2d\uff1b\u4f38\u624b\u5c31\u53d6\u6401\u5728\u5e8a\u8fb9\u7ea2\u6f06\u6728\u7bb1\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u4e00\u8dc3\u800c\u8d77\u3001\u53cc\u5251\u9f50 \u523a\u9ea6\u6c38\u660e\u53cc\u80c1\u5927\u7a74\uff0c\u5251\u52bf\u8fc5\u6377\uff0c\u800c\u4e14\u662f\u4ee5\u6709\u5907\u653b\u5176\u65e0\u5907\uff0c\u4e0d\u5dee\u6beb\u5398\u3002 \u3000\u3000\u9ea6\u6c38\u660e\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002\u4ed6\u4e5f\u771f\u4e0d\u6127\u662f\u9655\u7518\u5927\u4fa0\uff0c\u53ea\u89c1\u4ed6\u5728\u7edd\u9669\u4e4b\u4e2d\uff0c\u8eab\u5f62\u7b14\u76f4\u7a9c\u8d77\uff0c\u957f \u5251\u6a2a\u7a7a\u4e00\u683c\uff0c\u53ee\u54e8\u4e24\u58f0\uff0c\u628a\u4e24\u67c4\u5251\u90fd\u8361\u4e86\u5f00\u53bb\u3002\u8eab\u5f62\u672a\u843d\uff0c\u5c31\u7adf\u800c\u4e00\u4e2a\u76d8\u65cb\uff0c\u5148\u8e22\u5de6\u8db3\uff0c\u540e \u53f3\u8db3\uff0c\u8fd9\u6b63\u662f\u5f62\u610f\u95e8\u4e2d\u7684\u201c\u8fde\u73af\u593a\u547d\u9e33\u9e2f\u811a\u201d\u4e0e\u201c\u6d41\u661f\u8d76\u6708\u8ffd\u98ce\u5251\u201d\u4e24\u4e2a\u7edd\u62db\u7684\u8054\u5408\u52a8\u7528\uff0c \u987f\u65f6\u4e4b\u95f4\uff0c\u628a\u90a3\u4e24\u4e2a\u519b\u5b98\u8feb\u5230\u5c4b\u89d2\u3002 \u3000\u3000\u9ea6\u6c38\u660e\u4e00\u8f6c\u8eab\u53c8\u5f85\u53d6\u90a3\u7ea2\u6f06\u6728\u7bb1\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u5b50\uff0c\u4eca\u665a\u54b1\u4eec\u662f\u5b89\u6392\u9999\u9975 \u9493\u91d1\u9ccc\uff0c\u4f60\u8fd8\u60f3\u52a8\u624b\u5417\uff1f\u201d\u9ea6\u6c38\u660e\u521a\u521a\u4f38\u624b\uff0c\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u53c8\u5df2\u5230\u4e86\u80cc\u540e\uff0c\u9ea6\u6c38\u660e\u817e\u7684\u4e00 \u811a\uff0c\u628a\u7ea2\u6f06\u6728\u7bb1\u8e22\u5230\u95e8\u8fb9\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u4e0e\u90a3\u4e24\u4e2a\u519b\u5b98\u76f8\u6597\u3002 \u3000\u3000\u9ea6\u6c38\u660e\u4e00\u5251\u6a2a\u62ab\uff0c\u500f\u4e0a\u500f\u4e0b\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u8fdb\u56db\u62db\uff0c\u62db\u62db\u90fd\u662f\u6740\u624b\u3002\u90a3\u4e24\u4e2a\u519b\u5b98\u4e5f\u597d\u751f \u4e86\u5f97\uff0c\u53cc\u5251\u4e00\u5206\u4e00\u5408\uff0c\u7adf\u7136\u628a\u95e8\u6237\u5c01\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u4e86\u56db\u62db\uff0c\u4e0e\u9ea6\u6c38\u660e\u6253\u5f97\u96be \u5206\u96be\u89e3\u3002 \u3000\u3000\u8427\u9752\u5cf0\u5fc3\u4e2d\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u8fd9\u7ea2\u6f06\u6728\u7bb1\u4e4b\u4e2d\u4e0d\u77e5\u85cf\u7684\u662f\u4ec0\u4e48\u7269\u4e8b\uff1f\u4f46\u65e2\u7136\u662f\u9ea6\u5927\u4fa0\u6240\u8981\u53d6 \u7684\uff0c\u6211\u5c31\u8be5\u66ff\u4ed6\u53d6\u4e86\u3002\u201d\u6b63\u60f3\u98d8\u8eab\u98de\u5165\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u623f\u95e8\u7ed9\u4eba\u4e00\u811a\u8e22\u5f00\uff0c\u53ea\u89c1\u90a3 \u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u51f6\u795e\u6076\u715e\u4e00\u822c\u7684\u76f4\u95ef\u8fdb\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u4e00\u5f2f\u8170\u5c31\u5c06\u90a3\u7ea2\u6f06\u6728\u7bb1\u62fe\u4e86\uff01 \u3000\u3000\u90a3\u6ce5\u6cca\u5c14\u6b66\u58eb\u6b63\u5f85\u593a\u95e8\u5954\u51fa\uff0c\u8427\u9752\u5cf0\u5ffd\u5730\u98d8\u8eab\u98de\u5165\uff0c\u62c2\u5c18\u4e00\u5c55\uff0c\u8fce\u9762\u4e00\u62c2\uff0c\u90a3\u5c3c\u6cca\u5c14\u6b66 \u58eb\u5587\u7684\u53cd\u624b\u4e00\u5200\uff0c\u4ed6\u7684\u5200\u5f62\u5982\u6708\u7259\uff0c\u5200\u950b\u5185\u5f2f\uff0c\u950b\u5229\u5f02\u5e38\uff0c\u4e0d\u4f46\u662f\u4e00\u4ef6\u4f24\u4eba\u7684\u5229\u5668\uff0c\u800c\u4e14\u53ef \u4ee5\u52fe\u62c9\u9501\u593a\u654c\u4eba\u7684\u5175\u5203\uff0c\u5374\u4e0d\u6599\u8427\u9752\u5cf0\u7684\u94c1\u62c2\u5c18\u66f4\u662f\u6b66\u6797\u7f55\u89c1\u7684\u5f02\u5b9d\uff0c\u53ef\u67d4\u53ef\u521a\uff0c\u90a3\u6ce5\u6cca\u5c14 \u6b66\u58eb\u4e00\u5200\u5288\u53bb\uff0c\u5ffd\u89c9\u8f6f\u7ef5\u7ef5\u3001\u677e\u6563\u6563\u7684\u5168\u4e0d\u53d7\u529b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u987a\u624b\u4e00\u62c9\uff0c\u8427\u9752\u5cf0\u7684\u62c2\u5c18\u5df2\u8d81 \u52bf\u7f20\u4e0a\uff0c\u90a3\u6b66\u58eb\u4e00\u62c9\uff0c\u622a\u4e4b\u4e0d\u65ad\uff0c\u5374\u7ed9\u8427\u9752\u5cf0\u501f\u529b\u4e00\u9001\uff0c\u559d\u58f0\uff1a\u201c\u8131\u624b\uff01\u201d\u90a3\u6b66\u58eb\u73cd\u60dc\u5b9d \u5200\uff0c\u628a\u52b2\u529b\u5168\u8fd0\u5230\u53f3\u81c2\u4e4b\u4e0a\uff0c\u4e0e\u8427\u9752\u5cf0\u76f8\u6301\uff0c\u54ea\u77e5\u8427\u9752\u5cf0\u6b63\u8981\u4ed6\u5982\u6b64\uff0c\u7a81\u7136\u6a2a\u8098\u4e00\u649e\uff0c\u5de6\u624b \u4e00\u63a2\uff0c\u628a\u90a3\u6b66\u58eb\u5de6\u624b\u62b1\u7740\u7684\u7ea2\u6f06\u6728\u7bb1\u593a\u4e86\u56de\u6765\u3002\u8fd9\u662f\u58f0\u4e1c\u51fb\u897f\u4e4b\u8ba1\uff0c\u90a3\u6b66\u58eb\u5168\u795e\u8d2f\u6ce8\u5b9d\u5200\uff0c \u5de6\u8fb9\u95e8\u6237\u5927\u5f00\uff0c\u4e00\u4e0b\u5b50\u5c31\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u731b\u7684\u9192\u8d77\uff1a\u8fd9\u6728\u7bb1\u4e2d\u6240\u85cf\u4e4b\u7269\uff0c\u6bd4\u4ed6\u7684\u5b9d\u5200\u4e0d\u77e5\u8d35\u91cd\u51e0\u5343\u4e07\u500d\uff0c\u8fd9\u4e00\u60ca\u975e \u540c\u5c0f\u53ef\uff0c\u8427\u9752\u5cf0\u8d81\u4ed6\u5fc3\u795e\u5927\u4e71\u4e4b\u9645\uff0c\u62c2\u5c18\u4e00\u6325\uff0c\u6708\u7259\u5200\u767b\u65f6\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u5f53\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u62fe\u8d77\u6728\u7bb1\u4e4b\u65f6\uff0c\u623f\u4e2d\u7684\u5f62\u52bf\u5df2\u662f\u7a81\u53d8\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u4e0e\u9ea6\u6c38\u660e\u7acb\u5373\u505c\u624b\uff0c \u4e09\u53e3\u957f\u5251\u540c\u65f6\u8f6c\u4e86\u8fc7\u6765\uff0c\u5411\u65b0\u7684\u654c\u4eba\u51b2\u523a\uff0c\u8fd9\u51e0\u4e0b\u5b50\u90fd\u662f\u5feb\u6377\u975e\u5e38\uff0c\u5f85\u4ed6\u4eec\u5251\u5c16\u523a\u5230\u4e4b\u65f6\uff0c \u8427\u9752\u5cf0\u5df2\u628a\u6728\u7bb1\u593a\u5230\u624b\u3002 \u3000\u3000\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u53ea\u89c1\u4ed6\u6a2a\u91cc\u4e00\u8dc3\uff0c\u628a\u624b\u4e00\u6284\uff0c\u53c8\u628a\u6708\u7259\u5200\u63a5\u5230\u624b\u4e2d\uff0c\u540c\u65f6\u53f3 \u8db3\u5377\u5730\u4e00\u626b\uff0c\u8e22\u8427\u9752\u5cf0\u7684\u4e0b\u76d8\uff0c\u4ed6\u7684\u540c\u4f34\uff0c\u53e6\u4e00\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u4e5f\u63c9\u8eab\u6025\u8fdb\uff1b\u5506\uff0c\u5506\uff0c\u5506\u5411 \u8427\u9752\u5cf0\u8fde\u5288\u4e09\u5200\u3002 \u3000\u3000\u8427\u9752\u5cf0\u62b1\u7740\u6728\u7bb1\uff0c\u8eab\u5f62\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u95ea\u5f00\u4e86\u7b2c\u4e00\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u7684\u7a81\u88ad\uff0c\u62c2\u5c18\u4e00\u6325\uff0c\u53c8\u628a \u7b2c\u4e8c\u4e2a\u6b66\u58eb\u7684\u5b9d\u5200\u8361\u5f00\uff0c\u731b\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u5ffd\u5730\u6539\u4e86\u76ee\u6807\uff0c\u53cc\u5251\u540c\u65f6\u5411 \u8427\u9752\u5cf0\u6025\u523a\u3002\u8427\u9752\u5cf0\u53cd\u624b\u4e00\u62db\uff0c\u4e00\u4e2a\u758f\u5ffd\uff0c\u7bb1\u5b50\u53c8\u7ed9\u7b2c\u4e8c\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u62a2\u4e86\u56de\u53bb\u3002 \u3000\u3000\u201c\u53ee\u5f53\u4e00\u58f0\uff0c\u9ea6\u6c38\u660e\u4f38\u5251\u5c06\u4e24\u4e2a\u519b\u5b98\u7684\u957f\u5251\u683c\u5f00\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u5c3c\u6cca\u5c14\u6b66\u58eb\u5df2\u593a\u95e8\u51fa\uff0c\u9ea6 \u6c38\u660e\u4e00\u6014\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u8ffd\uff01\u201d\u98de\u8eab\u5148\u51fa\uff0c\u8427\u9752\u5cf0\u548c\u90a3\u4e24\u4e2a\u519b\u5b98\uff0c\u505c\u6b62\u4e89\u6597\uff0c\u4e5f\u8d76\u7740\u8ffd\u51fa \u53bb\u3002 \u3000\u3000\u516d\u4e2a\u4eba\u7a7f\u623f\u8fc7\u5c4b\uff0c\u98ce\u9a70\u7535\u63a3\uff0c\u970e\u5ffd\u5230\u4e86\u57ce\u5916\uff0c\u516d\u4eba\u4e4b\u4e2d\uff0c\u9ea6\u6c38\u660e\u8f7b\u529f\u6700\u9ad8\uff0c\u9996\u5148\u8ffd\u53ca\uff0c \u4e0e\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u6253\u4e86\u8d77\u6765\uff0c\u8427\u9752\u5cf0\u6b21\u4e4b\uff0c\u4e0d\u4e45\uff0c\u4e5f\u63a5\u7740\u8ffd\u5230\u3002\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u53cc\u6218 \u9ea6\u6c38\u660e\u8fd8\u5dee\u4e0d\u591a\uff0c\u4e00\u52a0\u5165\u4e86\u8427\u9752\u5cf0\uff0c\u7acb\u611f\u5904\u5728\u4e0b\u98ce\uff0c\u9ea6\u6c38\u660e\u957f\u5251\u5de6\u843d\uff1b\u4e00\u8fde\u524a\u4e86\u56db\u4e0b\uff0c\u653b\u5f97 \u90a3\u4e24\u4e2a\u6b66\u58eb\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u8427\u9752\u5cf0\u62c2\u5c18\u76d8\u65cb\u4e00\u821e\uff0c\u62a4\u7740\u8eab\u8eaf\uff0c\u817e\u51fa\u624b\u6765\uff0c\u5c31\u8981\u593a\u90a3\u7ea2\u6f06\u6728\u7bb1\uff0c \u731b\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a\u201c\u628a\u6728\u7bb1\u7ed9\u6211\u7559\u4e0b\uff01\u201d\u539f\u6765\u662f\u90a3\u4e24\u4e2a\u519b\u5b98\u8d74\u4e86\u4e0a\u6765\uff0c\u4e24\u67c4\u957f\u5251\u5de6\u53f3\u5206\u8fdb\uff0c \u4e00\u9f50\u523a\u90a3\u62b1\u7740\u6728\u7bb1\u7684\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u60f3\u62a2\u5728\u8427\u9752\u5cf0\u4e4b\u524d\uff0c\u5148\u628a\u90a3\u6728\u7bb1\u593a\u4e0b\u3002 \u3000\u3000\u56db\u4e2a\u9ad8\u624b\u540c\u65f6\u8fdb\u62db\uff0c\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u770b\u6765\u4e07\u4e07\u9003\u907f\u4e0d\u4e86\uff0c\u5374\u542c\u4ed6\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u9661\u5730\u5c06\u7ea2 \u6f06\u6728\u7bb1\u5411\u9ea6\u6c38\u660e\u5288\u9762\u4e00\u6454\uff0c\u9ea6\u6c38\u660e\u614c\u5fd9\u4f38\u624b\u53bb\u63a5\uff0c\u8fd9\u4e00\u6765\uff0c\u519b\u5b98\u6b66\u58eb\uff0c\u53c8\u8054\u6210\u4e00\u7ebf\uff0c\u53cc\u5200\u53cc \u53c8\u6539\u4e86\u76ee\u6807\uff0c\u6539\u5411\u9ea6\u6c38\u660e\u8fdb\u88ad\u3002 \u3000\u3000\u5251\u4f3c\u6e38\u9f99\uff0c\u5200\u5982\u98de\u51e4\uff0c\u53ee\u53ee\u54e8\u5417\u7684\u6b64\u6765\u5f7c\u5f80\uff0c\u6740\u5f97\u4e2a\u96be\u89e3\u96be\u5206\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u4e0e\u90a3\u4e24\u4e2a\u6b66 \u4e0a\uff0c\u82e5\u7136\u4ee5\u4e00\u654c\u4e00\uff0c\u90fd\u4e0d\u662f\u9ea6\u6c38\u660e\u4e0e\u8427\u9752\u5cf0\u7684\u5bf9\u624b\uff0c\u4f46\u8054\u5408\u8d77\u6765\uff0c\u4ee5\u56db\u654c\u4e8c\uff0c\u5374\u662f\u5927\u5360\u4e0a \u98ce\uff0c\u66f4\u517c\u9ea6\u6c38\u660e\u4e00\u624b\u62b1\u7740\u6728\u7bb1\uff0c\u8981\u5206\u5fc3\u7167\u987e\uff0c\u5b9e\u529b\u66f4\u662f\u6253\u4e86\u6298\u6263\uff0c\u4e09\u4e94\u5341\u62db\u4e00\u8fc7\uff0c\u9ea6\u3001\u8427\u4e8c \u4eba\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u519b\u5b98\u4e0e\u6b66\u58eb\u8d8a\u653b\u8d8a\u6025\uff0c\u9ea6\u6c38\u660e\u5ffd\u5730\u4e5f\u5927\u559d\u4e00\u58f0\uff0c\u5c06\u7ea2\u6f06\u6728\u7bb1\u629b\u56de\u7ed9\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u90a3\u4e24\u4e2a \u519b\u5b98\u4e00\u6014\uff0c\u9ea6\u6c38\u660e\u957f\u5251\u4e00\u6325\uff0c\u6dae\u5587\u4e24\u5251\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5148\u628a\u8fd9\u4e24\u4eba\u6740\u4e86\u518d\u8bf4\u3002\u201d \u90a3\u4e24\u4e2a\u519b\u5b98\u4e5f\u8ddf\u7740\u5251\u950b\u4e00\u8f6c\uff0c\u5f85\u5411\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u8fdb\u62db\uff0c\u5374\u53c8\u4f3c\u72b9\u7591\u4e0d\u51b3\uff0c\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e00\u58f0 \u957f\u7b11\uff0c\u67b6\u4e86\u4e00\u5200\uff0c\u53c8\u628a\u7ea2\u6f06\u6728\u7bb1\u63b7\u51fa\uff0c\u8427\u9752\u5cf0\u7ad9\u5728\u9644\u8fd1\uff0c\u53ea\u5f97\u63a5\u8fc7\uff0c\u970e\u65f6\u95f4\u519b\u5b98\u7684\u957f\u5251\uff0c\u4e0e \u6b66\u58eb\u7684\u6708\u7259\u5200\uff0c\u53c8\u7eb7\u7eb7\u5411\u4ed6\u8eab\u4e0a\u62db\u547c\u3002\u8fd9\u7ea2\u6f06\u6728\u7bb1\u672c\u6765\u662f\u5404\u65b9\u4e89\u593a\u4e4b\u7269\uff0c\u800c\u4eca\u5374\u4f3c\u53d8\u6210\u4e00\u4e2a \u7978\u80ce\uff0c\u5230\u4e86\u8c01\u7684\u624b\u4e0a\uff0c\u8c01\u5c31\u906d\u6b83\u3002 \u3000\u3000\u8427\u9752\u5cf0\u6321\u4e86\u51e0\u62db\uff0c\u9669\u8c61\u73af\u751f\uff0c\u4e5f\u8ddf\u7740\u4f9d\u6837\u753b\u846b\u82a6\uff0c\u632f\u81c2\u4e00\u629b\uff0c\u5c06\u6728\u7bb1\u5411\u519b\u5b98\u63b7\u53bb\uff0c\u5374\u4e0d \u6599\u90a3\u519b\u5b98\u201c\u563f\u3001\u563f\u201d\u51b7\u7b11\uff0c\u5ffd\u5730\u62a2\u4e0a\u4e00\u6b65\uff0c\u547c\u7684\u4e00\u638c\uff0c\u7ade\u8fce\u7740\u6728\u7bb1\u5f84\u5288\u3002\u9ea6\u6c38\u660e\u5927\u5403\u4e00\u60ca\uff0c \u6025\u8feb\u4e4b\u9645\uff0c\u65e0\u6687\u601d\u8003\uff0c\u4e00\u4f38\u624b\u53c8\u5c06\u90a3\u6728\u7bb1\u63a5\u8fc7\uff0c\u4e0d\u6562\u518d\u629b\uff0c\u8fd9\u4e00\u6765\uff0c\u7acb\u523b\u53c8\u9690\u5165\u4e86\u519b\u5b98\u4e0e\u6b66 \u58eb\u7684\u8054\u5408\u5305\u56f4\u4e4b\u4e2d\u3002 \u3000\u3000\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u7b11\uff0c\u5c16\u9510\u4e4b\u6781\uff0c\u7b11\u58f0\u672a\u505c\uff0c\u4eba\u5f71\u500f\u5730\u51fa\u73b0\uff0c\u8427\u9752\u5cf0\u5b9a\u775b\u4e00\u770b\uff0c \u6b63\u662f\u90a3\u4e2a\u602a\u4e10\uff0c\u53ea\u89c1\u4ed6\u65cb\u98ce\u822c\u76f4\u5377\u8fdb\u6765\uff0c\u94c1\u62d0\u4e00\u62db\u201c\u529b\u5212\u9e3f\u6c9f\u201d\uff0c\u5c06\u8bf8\u822c\u5175\u5668\u4e00\u9f50\u6321\u4f4f\uff0c\u5ffd \u800c\u653b\u90a3\u6b66\u58eb\uff0c\u5ffd\u800c\u653b\u90a3\u519b\u5b98\uff0c\u53c8\u5ffd\u800c\u653b\u9ea6\u6c38\u660e\uff0c\u7adf\u4e0d\u77e5\u4ed6\u5230\u5e95\u662f\u53cb\u662f\u654c\u3002\u8fd9\u4e00\u6765\u66f4\u6210\u4e86\u6df7\u6218 \u4e4b\u52bf\uff0c\u90a3\u602a\u4e10\u7684\u94c1\u62d0\u547c\u547c\u631f\u98ce\uff0c\u626b\u5230\u8c01\u7684\u8ddf\u524d\uff0c\u8c01\u5c31\u8981\u88ab\u8feb\u5f97\u9000\u540e\u51e0\u6b65\u3002 \u3000\u3000\u8427\u9752\u5cf0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u5982\u6b64\u6253\u6cd5\uff0c\u5206\u660e\u662f\u60f3\u628a\u5404\u4eba\u90fd\u5f04\u5f97\u7d2f\u4e86\uff0c\u7136\u540e\u597d\u6536\u6e14\u7fc1\u4e4b \u5229\uff0c\u72ec\u5360\u8fd9\u6728\u7bb1\u3002\u201d\u6b63\u60f3\u559d\u7834\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u957f\u7b11\uff0c\u573a\u4e2d\u7a81\u7136\u591a\u4e86\u4e00\u4eba\uff0c\u8fd9\u4eba\u6765\u5f97\u66f4\u662f\u795e \u5947\uff0c\u521a\u624d\u90a3\u602a\u53eb\u5316\u6765\u65f6\uff0c\u8fd8\u662f\u5148\u95fb\u58f0\u800c\u540e\u89c1\u4eba\uff0c\u800c\u4eca\u6b64\u4eba\uff0c\u5374\u662f\u58f0\u5230\u4eba\u73b0\uff0c\u5c31\u5982\u98de\u5c06\u519b\u4ece\u5929 \u800c\u964d\uff0c\u6ee1\u573a\u9ad8\u624b\uff0c\u7adf\u65e0\u4e00\u4eba\u5728\u4e8b\u5148\u53d1\u73b0\u4ed6\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u51b7\u6708\u758f\u661f\u4e4b\u4e0b\uff0c\u8427\u9752\u5cf0\u770b\u5f97\u5206\u660e\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u524d\u51e0\u65e5\u7528\u4e00\u628a\u91d1\u8ba1\u6551\u4ed6\u6027\u547d\u7684\u90a3\u4e2a\u4e66 \u751f\uff0c\u53ea\u89c1\u4ed6\u4e00\u624b\u53c9\u8170\uff0c\u4e00\u624b\u6325\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c\u4e00\u526f\u61d2\u6d0b\u6d0b\u7684\u795e\u6c14\uff0c\u6162\u541e\u541e\u7684\u9053\uff1a\u201c\u4ec0\u4e48\u5e0c\u7f55\u4e1c \u897f\uff0c\u503c\u5f97\u4f60\u4e89\u6211\u593a\uff1f\u201d \u3000\u3000\u8fd9\u4e66\u751f\u7a81\u7136\u51fa\u73b0\uff0c\u6ee1\u573a\u9ad8\u624b\uff0c\u65e0\u4e0d\u6115\u7136\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u505c\u4e86\u6218\u6597\u3002\u602a\u53eb\u5316\u5634\u89d2\u5659\u7740\u51b7\u7b11\uff0c \u5012\u63d0\u94c1\u62d0\uff0c\u770b\u4f3c\u6beb\u4e0d\u5728\u4e4e\uff0c\u5176\u5b9e\u5374\u662f\u5168\u795e\u8d2f\u6ce8\uff0c\u6697\u4e2d\u51c6\u5907\uff0c\u84c4\u52b2\u5f85\u53d1\uff0c\u9ea6\u6c38\u660e\u89c1\u591a\u8bc6\u5e7f\uff0c\u77e5 \u8fd9\u4e66\u751f\u5fc5\u662f\u5927\u6709\u6765\u5934\uff0c\u5f53\u4e0b\u624b\u629a\u5251\u67c4\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4ffa\u5b9d\u9e21\u9ea6\u6c38\u660e\u8981\u5728\u8fd9\u4e24\u4e2a\u9e70 \u722a\u5b59\u624b\u4e2d\u53d6\u4e00\u4ef6\u4e1c\u897f\uff0c\u5929\u4e0b\u7ea2\u82b1\u7eff\u53f6\uff0c\u540c\u662f\u4e00\u5bb6\uff0c\u9601\u4e0b\u82e5\u662f\u6b66\u6797\u540c\u9053\uff0c\u4ffa\u4e0d\u6562\u6c42\u52a9\uff0c\u4f46\u8bf7\u7f6e \u8eab\u4e8b\u5916\uff0c\u5219\u4ed6\u65e5\u5c71\u6c34\u76f8\u9022\uff0c\u5b9a\u5f53\u62a5\u7b54\u3002\u201d\u8981\u77e5\u9ea6\u6c38\u660e\u4e43\u9655\u7518\u5927\u4fa0\uff0c\u5728\u56db\u5317\u6570\u7701\uff0c\u6b63\u662f\u54cd\u5f53\u5f53 \u7684\u811a\u8272\uff0c\u63d0\u8d77\u6765\u65e0\u4eba\u4e0d\u8bc6\uff0c\u8fd9\u4e00\u756a\u81ea\u62a5\u540d\u5934\uff0c\u8bf4\u8bdd\u53c8\u975e\u5e38\u6f02\u4eae\u5f97\u4f53\uff0c\u8fd9\u5c11\u5e74\u4e66\u751f\u770b\u6765\u4e0d\u8fc7\u4e8c \u5341\u591a\u5c81\uff0c\u8f88\u4efd\u65e0\u8bba\u5982\u4f55\u4e0d\u4f1a\u5728\u9ea6\u6c38\u660e\u4e4b\u4e0a\uff0c\u9ea6\u6c38\u660e\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e1d\u6beb\u4e0d\u4ee5\u524d\u8f88\u81ea\u5c45\uff0c\u4f46\u5374\u5728\u6697 \u4e2d\u8d23\u4ee5\u6c5f\u6e56\u5927\u4e49\uff0c\u4ee5\u4e3a\u8fd9\u5c11\u5e74\u4e66\u751f\u542c\u4e86\uff0c\u5b9a\u5fc5\u52a8\u5bb9\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u62d4\u5251\u76f8\u52a9\u7528\uff09\u77e5\u8fd9\u5c11\u5e74\u4e66\u751f\u53ea \u662f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u77e5\u9053\u4e86\uff01\u201d\u7adf\u597d\u50cf\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u9ea6\u6c38\u660e\u7684\u540d\u5b87\u4e00\u822c\uff0c\u8fde\u8427\u9752\u5cf0\u4e5f\u89c9\u5f97\u8fd9 \u5c11\u5e74\u4e66\u751f\u672a\u514d\u8fc7\u4efd\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u519b\u5b98\u89c1\u72b6\u5927\u559c\uff0c\u4e5f\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5728\u5fa1\u6797\u519b\u5f53\u5dee\uff0c\u5949\u4e07\u5c81\u7237\u4e4b\u547d\uff0c\u9001\u4e00\u4ef6\u4e1c\u897f \u5230\u62c9\u8428\uff0c\u5374\u7ed9\u8fd9\u8001\u6df7\u86cb\u52ab\u4e86\uff0c\u4e0d\u6562\u8bf7\u9601\u4e0b\u76f8\u52a9\u3002\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u5514\uff0c\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u602a\u53eb\u5316\u51b7\u7b11\u4e00\u58f0\uff0c\u5c31\u5f85\u53d1\u4f5c\uff0c\u90a3\u5c11\u5e74\u4e66\u751f\u8fc8\u524d\u4e24\u6b65\uff0c\u4e5f\u4e0d\u89c1\u600e\u6837\u4f5c\u52bf\uff0c\u5ffd\u7136\u4e00\u4f38\u624b\u5c31\u4ece \u9ea6\u6c38\u660e\u624b\u4e0a\u5c06\u7ea2\u6f06\u6728\u7bb1\u593a\u4e86\u8fc7\u6765\u3002\u8bd5\u60f3\u9ea6\u6c38\u660e\u662f\u4f55\u7b49\u672c\u4e8b\uff0c\u7adf\u7136\u8fde\u62db\u67b6\u4e5f\u6765\u4e0d\u53ca\uff0c\u5b9d\u7bb1\u4fbf\u544a \u6613\u624b\uff0c\u4e0d\u4f46\u8427\u9752\u5cf0\u89c9\u5f97\u60ca\u8be7\uff0c\u519b\u5b98\u3001\u6b66\u58eb\u4e5f\u90fd\u4e0d\u7ea6\u800c\u540c\u5730\u201c\u5475\u554a\uff01\u201d\u4e00\u58f0\uff0c\u5404\u9000\u51e0\u6b65\u3002 \u3000\u3000\u5c11\u5e74\u4e66\u751f\u7684\u624b\u6cd5\u5feb\u5230\u6781\u70b9\uff0c\u90a3\u602a\u53eb\u5316\u7684\u94c1\u62d0\u4e5f\u5feb\u5230\u6781\u70b9\uff0c\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u90a3\u602a\u53eb\u5316 \u624b\u8155\u4e00\u7ffb\uff0c\u94c1\u62d0\u547c\u7684\u4e00\u58f0\uff0c\u5df2\u7838\u5230\u4e66\u751f\u80cc\u810a\u3002\u8fd9\u5c11\u5e74\u4e66\u751f\u5bf9\u8427\u9752\u5cf0\u6709\u6551\u547d\u4e4b\u6069\uff0c\u8427\u9752\u5cf0\u89c1\u6b64 \u9669\u72b6\uff0c\u4e0d\u81ea\u7981\u7684\u201c\u5475\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5c11\u5e74\u4e66\u751f\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u5f39\uff0c\u8eab\u5f62\u7acb\u523b\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u8eab \u6cd5\u7f8e\u5999\u4e4b\u6781\uff0c\u602a\u53eb\u5316\u7684\u94c1\u62d0\u7fd8\u4e86\u8d77\u6765\uff0c\u672a\u53ca\u6536\u56de\uff0c\u5df2\u542c\u5f97\u90a3\u5c11\u5e74\u4e66\u751f\u6717\u58f0\u7b11\u9053\uff1a\u201c\u94c1\u62d0\u4ed9\u679c \u7136\u540d\u4e0d\u865a\u4f20\uff01 \u3000\u3000\u8427\u9752\u5cf0\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8fd9\u602a\u53eb\u5316\u679c\u7136\u662f\u94c1\u62d0\u4ed9\uff01\u5ffd\u542c\u5f97\u90a3\u5c11\u5e74\u4e66\u751f\u53c8\u662f\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u6211\u5012\u8981 \u770b\u770b\u662f\u4ec0\u4e48\u5e0c\u7f55\u7684\u4e1c\u897f\uff0c\u503c\u5f97\u4f60\u4e89\u6211\u593a\u3002\u201d\u4e00\u638c\u5288\u4e0b\uff0c\u5c06\u90a3\u7ea2\u6f06\u6728\u7bb1\u9707\u5f00\uff0c\u4f38\u624b\u4e00\u638f\uff0c\u5411\u5730 \u4e0b\u4e00\u6454\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u54cd\u58f0\uff0c\u6728\u7bb1\u91cc\u7684\u4e1c\u897f\u5df2\u7ed9\u4ed6\u6454\u6210\u516b\u7247\uff01 \u3000\u3000\u9ea6\u6c38\u660e\u4e00\u58f0\u60ca\u547c\uff0c\u53eb\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u4e0d\u662f\u91d1\u74f6\uff01\u201d\u602a\u53eb\u5316\u4e5f\u4f3c\u751a\u4e3a\u60ca\u8be7\uff0c\u63d0\u6756\u832b\u7136\uff0c\u505a\u58f0 \u4e0d\u5f97\uff0c\u8427\u9752\u5cf0\u4ed4\u7ec6\u770b\u65f6\uff0c\u88ab\u6454\u7834\u7684\u4e0d\u8fc7\u662f\u4e2a\u666e\u666e\u901a\u901a\u7684\u74f7\u74f6\uff0c\u4e0d\u77e5\u4ed6\u4eec\u4f55\u4ee5\u8981\u4f60\u4e89\u6211\u593a\uff0c\u4e5f \u662f\u832b\u7136\u4e0d\u89e3\uff01 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u6454\u88c2\u74f7\u74f6\uff0c\u4ef0\u5929\u4e00\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7978\u6839\u5df2\u706d\u5e72\u6208\u6b62\u3002\u7b11\u6740\u5f53\u4eca\u9c81\u4ef2\u8fde\u3002\u54c8 \u54c8\uff0c\u4e0d\u4ea6\u5feb\u54c9\uff0c\u4e0d\u4ea6\u5feb\u54c9\uff01\u4ffa\u5c11\u966a\u5566\uff01\u201d\u888d\u8896\u4e00\u62c2\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u7fe9\u5982\u5de8\u96c1\uff0c\u4fbf\u5411\u832b\u832b\u65e0\u9645\u7684 \u8349\u539f\u201c\u98de\u201d\u8d70\uff0c\u9ea6\u6c38\u660e\u5ffd\u7136\u5927\u543c\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u4f60\u9601\u4e0b\u65e2\u6765\u6cbe\u8fd9\u8d9f\u6d51\u6c34\uff0c\u54ea\u80fd\u5982\u6b64\u5bb9\u6613\u4fbf\u6b62 \u4e86\u5e72\u6208\uff1f\u201d\u58f0\u53d1\u4eba\u8d77\uff0c\u633a\u5251\u75be\u8ffd\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u548c\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e5f\u8ddf\u8e2a\u8ffd\u53bb\uff0c\u4e00\u7247\u545b\u559d\u4e4b \u58f0\uff0c\u9707\u8361\u8349\u539f\u3002 \u3000\u3000\u90a3\u602a\u53eb\u5316\u94c1\u62d0\u652f\u5730\uff0c\u6728\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u8427\u9752\u5cf0\u672c\u6765\u4e5f\u5f85\u8ffd\u53bb\uff0c\u89c1\u6b64\u60c5\u72b6\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u62c2\u5c18 \u4e00\u6302\uff0c\u6b63\u60f3\u62db\u547c\uff0c\u90a3\u53eb\u5316\u602a\u773c\u4e00\u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8ffd\u5f97\u4e0a\u5417\uff1f\u7559\u4e9b\u7cbe\u529b\uff0c\u4ee5\u5f85\u5929\u6e56\u4e4b \u4f1a\u5427\uff01\u201d\u6479\u7136\u4e00\u62d0\u631f\u98ce\uff0c\u5411\u8427\u9752\u5cf0\u62e6\u8170\u75be\u626b\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e8b\u5148\u6beb\u65e0\u6715\u5146\uff0c\u5b9e\u662f\u5927\u51fa\u8427\u9752\u5cf0\u610f\u6599\u4e4b\u5916\uff0c\u800c\u4e14\u602a\u52a0\u5316\u8fd9\u4e00\u62d0\u624b\u6cd5\u5999\u6781\uff0c\u7adf\u662f\u4ece \u4ed6\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u6253\u6765\uff0c\u7eb5K\u6b66\u529f\u518d\u9ad8\uff0c\u50cf\u8fd9\u7b49\u53d8\u8d77\u4ed3\u6dec\uff0c\u4e5f\u96be\u9003\u907f\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684 \u4e00\u58f0\uff0c\u602a\u53eb\u5316\u7684\u94c1\u62d0\uff0c\u5df2\u5728\u4ed6\u7684\u81c0\u90e8\u91cd\u91cd\u7684\u6572\u4e86\u4e00\u8bb0\uff0c \u3000\u3000\u8bd5\u60f3\u8fd9\u602a\u53eb\u5316\u662f\u4f55\u7b49\u529f\u529b\uff0c\u8427\u9752\u5cf0\u89c1\u94c1\u62d0\u4ee5\u6392\u5c71\u5012\u6d77\u4e4b\u52bf\u626b\u6765\uff0c\u5fc3\u4e2d\u4ee5\u4e3a\u51c6\u6b7b\u65e0\u7591\uff1a \u201c\u4e0d\u6599\u6211\u8427\u67d0\u4eba\u4e0d\u660e\u4e0d\u767d\u4e27\u751f\u4e8e\u6b64\u5904\uff01\u201d\u5c82\u77e5\u94c1\u62d0\u51fb\u6765\uff0c\u5374\u4f3c\u6709\u4e00\u80a1\u5f39\u529b\uff0c\u5ffd\u7684\u628a\u8427\u9752\u5cf0\u5f39 \u4e86\u8d77\u6765\uff0c\u5e73\u7a7a\u629b\u51fa\u6570\u4e08\uff0c\u6beb\u65e0\u635f\u4f24\uff01 \u3000\u3000\u628a\u773c\u770b\u65f6\uff0c\u90a3\u602a\u53eb\u5316\u5df2\u7ecf\u6ca1\u4e86\u8e2a\u8ff9\u3002\u8427\u9752\u5cf0\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff0c\u201c\u54b1\u4eec\u8fd9\u602a\u53eb\u5316\u4e0e\u81ea\u5df1\u6709 \u4ec7\uff0c\u4f55\u4ee5\u4ed6\u8fd9\u4e00\u62d0\u4e0d\u65bd\u6740\u624b\uff1f\u82e5\u8bf4\u65e0\u4ec7\uff0c\u53c8\u4f55\u5fc5\u8981\u5413\u552c\u81ea\u5df1\uff0c\u8ff9\u8fd1\u4fae\u5f04\uff1f\u201d\u8427\u9752\u5cf0\u867d\u662f\u4e45\u5386 \u6c5f\u6e56\uff0c\u4e5f\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u90a3\u5ba2\u5e97\u534a\u591c\u91cc\u4e00\u573a\u5927\u6597\uff0c\u4e52\u4e52\u4e53\u4e53\u7684\u4ece\u5e97\u5185\u6253\u5230\u5e97\u5916\uff0c\u5e97\u4e3b\u548c\u4f4f\u5ba2\u90fd\u5413\u5f97\u4e00\u4f5b\u51fa\u4e16\uff0c\u4e8c \u4f5b\u6d85\u6868\uff0c\u8499\u8d77\u5934\u6765\u4e0d\u6562\u51fa\u5916\uff0c\u5f85\u5f97\u6253\u6597\u7684\u58f0\u97f3\u5df2\u8fdc\u4e4b\u540e\uff0c\u518d\u8fc7\u4e86\u597d\u4e45\uff0c\u5e97\u4e3b\u4eba\u624d\u6562\u51fa\u6765\uff0c\u63d0 \u706f\u7b3c\u5bdf\u770b\uff0c\u53ea\u89c1\u9ea6\u6c38\u660e\u3001\u519b\u5b98\u6b66\u58eb\u4ee5\u53ca\u90a3\u602a\u53eb\u5316\u7684\u56db\u95f4\u623f\u95e8\u6253\u5f00\uff0c\u4eba\u5f71\u6e3a\u7136\u3002\u5e97\u4e3b\u4eba\u5012\u62bd\u4e00 \u53e3\u51b7\u6c14\uff0c\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u65e9\u77e5\u9053\u90a3\u53eb\u5316\u5b50\u4e0d\u662f\u5584\u7c7b\uff01\u201d\u4ed6\u4e0d\u6562\u9a82\u519b\u5b98\uff0c\u4e0d\u6562\u9a82\u6b66\u58eb\uff0c\u66f4 \u4e0d\u6562\u9a82\u9655\u7518\u5927\u4fa0\u9ea6\u6c38\u660e\uff0c\u4e00\u53e3\u54ac\u5b9a\u662f\u602a\u53eb\u5316\u95f9\u4e8b\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u5012\u6709\u70b9\u826f\u5fc3\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u7ed9\u90a3\u952d\u5143\u5b9d\uff0c\u8db3\u6709\u5341\u4e8c\u4e24\u3002\u6211\u79f0\u8fc7\u4e86\u3002\u201d\u5e97\u4e3b\u4eba\u542c\u4e86 \u6b64\u8a00\uff0c\u9762\u8272\u5927\u5f02\u3002\u5ffd\u7136\u8dd1\u56de\u53bb\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53c8\u8dd1\u56de\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u8fd9\u5929\u6740\u7684\u5316\u5b50\uff0c\u5077\u4e86\u6211\u7684 \u94f6\u5b50\u6765\u620f\u5f04\u6211\uff01\u201d\u5e97\u4e3b\u4eba\u54c0\u54c0\u5492\u9a82\uff0c\u751a\u662f\u4f24\u5fc3\u3002 \u3000\u3000\u9648\u5929\u5b87\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u602a\u53eb\u5316\u624b\u6bb5\u786e\u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u4f46\u8981\u5e97\u4e3b\u4eba\u8d34\u623f\u996d\u94b1\uff0c\u5374\u4e5f\u672a\u514d\u592a \u8fc7\u3002\u201d\u4ed6\u5c11\u5e74\u70ed\u60c5\uff0c\u51e1\u4e8b\u4e0d\u8ba1\u5229\u5bb3\uff0c\u4e8e\u662f\u8d70\u51fa\u623f\u6765\uff0c\u9053\uff1a\u201c\u5e97\u4e3b\u4eba\u4f60\u4e0d\u5fc5\u4f24\u5fc3\u9a82\u9a82\uff0c\u8fd9\u952d\u5143 \u5b9d\u6211\u8d54\u4e0e\u4f60\u5427\u3002\u90a3\u4f4d\u53eb\u5316\u5b50\u4f2f\u6015\u662f\u6211\u7684\u4e00\u4f4d\u957f\u8f88\uff0c\u4ed6\u751f\u6027\u6ed1\u7a3d\uff0c\u60f3\u662f\u6545\u610f\u4f5c\u5f04\u4f60\u7684\u3002\u201d\u5e97\u4e3b \u4eba\u867d\u7136\u5947\u602a\u50cf\u9648\u5929\u5b87\u8fd9\u822c\u8863\u670d\u534e\u4e3d\u7684\u8d35\u516c\u5b50\u7adf\u7136\u4f1a\u4e0e\u53eb\u5316\u5b50\u76f8\u8bc6\uff0c\u4f46\u542c\u5f97\u4ed6\u80af\u8d54\u94b1\uff0c\u559c\u51fa\u671b \u5916\uff0c\u5343\u6069\u4e07\u8c22\uff0c\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u9648\u5929\u5b87\u56de\u5230\u623f\u4e2d\uff0c\u89c1\u5929\u8272\u5df2\u5c06\u62c2\u6653\uff0c\u5e08\u7236\u5c1a\u672a\u56de\u6765\uff0c\u5fc3\u4e2d\u81ea\u662f\u7126\u6025\uff0c\u5ffd\u542c\u5f97\u7a97\u5916\u6709\u4eba\u7b11 \u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u513f\u5012\u597d\u5fc3\u80a0\uff01\u201d\u9648\u5929\u5b87\u4e00\u60ca\u95ee\u9053\uff1a\u201c\u54ea\u4f4d\u524d\u8f88\uff1f\u201d\u63a8\u7a97\u4e00\u671b\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u56de\u5934 \u770b\u65f6\uff0c\u53ea\u89c1\u5e8a\u8fb9\u5c0f\u51e0\uff0c\u5df2\u591a\u4e86\u4e00\u5305\u4e1c\u897f\uff0c\u6298\u5f00\u4e00\u770b\uff0c\u6b63\u662f\u81ea\u5df1\u9001\u4e0e\u602a\u53eb\u5316\u7684\u90a3\u4ef6\u9a7c\u7ed2\u5916\u8863\uff0c \u91cc\u9762\u8fd8\u6709\u4e00\u952d\u5143\u5b9d\u3002 \u3000\u3000\u5f85\u5f97\u5929\u660e\uff0c\u8427\u9752\u5cf0\u6084\u6084\u56de\u6765\uff0c\u4e24\u5e08\u5f92\u8bf4\u8d77\u6628\u665a\u4e4b\u4e8b\uff0c\u90fd\u611f\u602a\u5f02\uff0c\u90a3\u53eb\u5316\u5b50\u662f\u654c\u662f\u53cb\uff0c\u4ecd \u672a\u5206\u660e\uff0c\u5bf9\u9ea6\u6c38\u660e\u4e0e\u90a3\u519b\u5b98\u3001\u6b66\u58eb\u4f55\u4ee5\u8981\u4e89\u593a\u4e00\u4e2a\u666e\u666e\u901a\u901a\u7684\u74f7\u74f6\uff0c\u4e5f\u662f\u4e0d\u89e3\u3002\u4e24\u5e08\u5f92\u7591\u56e2 \u6ee1\u8179\uff0c\u5403\u8fc7\u65e9\u996d\uff0c\u53c8\u518d\u767b\u7a0b\u3002 \u3000\u3000\u4ece\u65e5\u5580\u5219\u51fa\u53d1\uff0c\u8d70\u4e86\u534a\u4e2a\u6708\uff0c\u6765\u5230\u62c9\u8428\u897f\u5317\uff0c\u53c8\u89c1\u4e00\u5ea7\u5927\u5c71\uff0c\u9ad8\u8038\u4e91\u8868\uff0c\u6321\u7740\u53bb\u8def\uff0c\u8fd9 \u662f\u897f\u85cf\u5883\u5185\u9ad8\u5ea6\u4ec5\u6b21\u4e8e\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u3002\u5176\u65f6\u5df2\u662f\u4ef2\u590f\uff0c\u5c71\u811a\u767e\u82b1\u7efd\u5f00\uff0c\u5c71\u8170\u6d41 \u6cc9\u545c\u54cd\uff0c\u6070\u4f3c\u6c5f\u5357\u521d\u6625\uff0c\u4f46\u5c71\u9876\u4ecd\u662f\u96ea\u82b1\u7eb7\u98de\uff0c\u6784\u6210\u4e86\u72ec\u7279\u7684\u666f\u8272\u3002\u8427\u9752\u5cf0\u9053\uff1a\u201c\u542c\u8bf4\u6842\u534e \u751f\u6842\u8001\u524d\u8f88\u5c31\u4f4f\u5728\u6b64\u5c71\u4e4b\u4e2d\uff0c\u4f46\u613f\u4ed6\u5c1a\u5728\u4eba\u95f4\uff0c\u4e3a\u6211\u89e3\u6b64\u56f0\u5883\u3002\u201d \u3000\u3000\u4e24\u5e08\u5f92\u65e9\u5df2\u51c6\u5907\u4e86\u767b\u5c71\u7528\u5177\uff0c\u6500\u85e4\u9644\u845b\uff0c\u8d70\u4e86\u4e09\u65e5\uff0c\u65b9\u5230\u5c71\u8170\uff0c\u7eb5\u76ee\u56db\u671b\uff0c\u4f46\u89c1\u51b0\u5ddd\u4ea4 \u9519\uff0c\u4e25\u82e5\u94f6\u9f99\uff0c\u53c8\u662f\u4e00\u756a\u5947\u666f\u3002\u51b0\u5ddd\u7684\u51b0\u5c42\uff0c\u867d\u56e0\u53d7\u5230\u521d\u590f\u7684\u9633\u5149\uff0c\u5df2\u6709\u90e8\u5206\u878d\u5316\uff0c\u4f46\u5c71\u9876 \u7684\u96ea\u82b1\uff0c\u4e00\u7247\u4e00\u7247\u8f7b\u98d8\u98d8\u5730\u4e0b\u7740\uff0c\u5c31\u597d\u50cf\u767d\u7eb8\u5c51\uff0c\u6c34\u6676\u672a\u4e00\u822c\uff0c\u843d\u5230\u51b0\u5ddd\u4e4b\u4e0a\uff0c\u9010\u6e10\u7ed3\u6676\u51bb \u7ed3\uff0c\u6700\u540e\u8f6c\u5316\u4e3a\u51b0\u5c42\u3002\u6240\u4ee5\u5c71\u4e0a\u7684\u51b0\u5ddd\uff0c\u4e98\u53e4\u4e0d\u5316\u3002\u7531\u4e8e\u592a\u9633\u5149\u7684\u6298\u5c04\u548c\u6563\u5c04\uff0c\u6574\u4e2a\u51b0\u5c42\u90fd \u53d8\u6210\u6d45\u84dd\u8272\u7684\u900f\u660e\u4f53\uff0c\u7aef\u7684\u662f\u5947\u4e3d\u4e07\u72b6\uff0c\u96be\u4ee5\u5f62\u5bb9\u3002\u66ae\u6625\u521d\u590f\u7684\u96ea\u6bd4\u8f83\u6da6\u6e7f\u3001\u89c2\u91cd\uff0c\u8fd9\u79cd\u96ea \u91cc\u9762\u6c34\u4efd\u8f83\u591a\uff0c\u843d\u5728\u51b0\u5ddd\u4e0a\uff0c\u672a\u51bb\u7ed3\u6210\u4e3a\u51b0\u5c42\u4e4b\u524d\uff0c\u5c31\u50cf\u4e00\u6735\u6735\u6885\u82b1\u3002\u6709\u8bd7\u4e3a\u8bc1\uff1a\u201c\u6625\u96ea\u6ee1 \u7a7a\u6765\uff0c\u89e6\u5904\u4f3c\u82b1\u5f00\uff0c\u4e0d\u77e5\u5c71\u91cc\u6811\uff0c\u82e5\u4e2a\u662f\u771f\u6885\uff1f\u201d\u6240\u548f\u53f9\u7684\u5c31\u662f\u8fd9\u79cd\u4eba\u95f4\u7f55\u89c1\u7684\u5947\u666f\u3002 \u3000\u3000\u4e24\u5e08\u5f92\u6b63\u5728\u7eb5\u76ee\u51bd\u89c8\u51b0\u5ddd\u5947\u666f\uff0c\u5ffd\u542c\u5c71\u8170\u5e95\u4e0b\uff0c\u5587\u5566\u5566\u7684\u4e00\u7247\u54cd\uff0c\u4e24\u4e2a\u7a7f\u7740\u4e00\u8eab\u7070\u8272\u7bad \u8863\u7684\u4eba\uff0c\u7a9c\u4e0a\u659c\u5bf9\u9762\u7684\u5c71\u5cf0\u3002\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\uff0c\u5c71\u5cf0\u9519\u6742\uff0c\u867d\u7136\u6240\u9694\u4e0d\u8fc7\u91cc\u8bb8\u4e4b\u9065\uff0c\u4f46\u90a3\u4e24\u6761 \u4eba\u5f71\uff0c\u4e00\u8f6c\u5165\u5c71\u53e3\uff0c\u5df2\u88ab\u5ca9\u77f3\u906e\u7740\uff0c\u4e0d\u53ef\u590d\u7779\u3002 \u3000\u3000\u4e24\u5e08\u5f92\u76f8\u7ee7\u6115\u7136\uff0c\u5ffd\u53c8\u542c\u5f97\u4e00\u9635\u7434\u58f0\u7f13\u7f13\u4f20\u6765\u3002 \u3000\u3000\u4e24\u5e08\u5f92\u5411\u7740\u7434\u58f0\u6765\u5904\u8ffd\u8e2a\uff0c\u9648\u5929\u5b87\u8d8a\u8d70\u8d8a\u89c9\u6c14\u5019\u6696\u548c\uff0c\u5947\u602a\u95ee\u9053\uff1a\u201c\u524d\u51e0\u65e5\u6211\u4eec\u4e00\u8def\u767b \u5c71\uff0c\u8d8a\u8d70\u8d8a\u89c9\u5bd2\u51b7\uff0c\u4f55\u4ee5\u5982\u4eca\u5230\u4e86\u5c71\u8170\uff0c\u53cd\u89c9\u6bd4\u4e0b\u9762\u6696\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u53ef\u80fd\u6211\u4eec\u6240\u7ad9\u4e4b \u5904\uff0c\u4fbf\u6709\u5730\u4e0b\u706b\u5c71\uff0c\u90a3\u9053\u7406\u5c31\u5982\u96ea\u5c71\u4e0a\u5e38\u6709\u6e29\u6cc9\u4e00\u6837\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u524d\u9762\u7684\u7434\u58f0\u66f4\u662f\u6e05\u6670\uff0c\u9648\u5929\u5b87\u77e5\u97f3\u5ba1\u5f8b\uff0c\u542c\u51fa\u90a3\u662f\u4e00\u79cd\u4e94\u5f26\u7684\u80e1\u7434\uff0c\u58f0 \u8c03\u82cd\u51c9\u4e4b\u6781\uff0c\u800c\u4e14\u8fd9\u7434\u97f3\u7adf\u4f3c\u4ee5\u524d\u66fe\u542c\u8fc7\u4e00\u822c\uff0c\u9648\u5929\u5b87\u65b9\u89c9\u5fc3\u5934\u4e00\u52a8\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u6709\u4eba\u6b4c \u9053\uff1a \u3000\u3000\u201c\u51b0\u5ddd\u4e0b\u9762\u6709\u53ea\u5c0f\u9ec4\u7f8a\uff0c \u3000\u3000\u5b83\u5931\u4e86\u7239\u53c8\u5931\u4e86\u5a18\uff0c \u3000\u3000\u5929\u4e0a\u7684\u5140\u9e70\u5728\u8ffd\u7740\u5b83\uff0c \u3000\u3000\u8981\u5c06\u5b83\u6293\u53bb\u5145\u98df\u7cae\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u2014\u2014\u6211\u7684\u597d\u59d0\u59d0\u5475\uff01 \u3000\u3000\u4f60\u542c\u4e0d\u542c\u89c1\u5b83\u7684\u54c0\u9e23\uff0c\u77e5\u4e0d\u77e5\u9053\u5b83\u7684\u5fe7\u4f24\uff1f \u3000\u3000\u4f60\u66ff\u5b83\u8d76\u6389\u51f6\u6076\u7684\u5140\u9e70\u5427\uff0c \u3000\u3000\u5b83\u7ec8\u751f\u4e0d\u4f1a\u5fd8\u4e86\u4f60\u7684\u6069\u5178\uff01\u201d \u3000\u3000\u8fd9\u6b4c\u58f0\u6b63\u662f\u90a3\u4e2a\u5047\u540d\u6851\u739b\uff0c\u771f\u540d\u829d\u5a1c\u7684\u85cf\u65cf\u5c11\u5973\u5531\u51fa\u6765\u7684\uff0c\u9648\u5929\u5b87\u53c8\u559c\u53c8\u60ca\uff0c\u9053\uff1a\u201c\u5e08 \u7236\uff0c\u4f60\u542c\uff0c\u8fd9\u6b4c\u58f0\u5206\u660e\u662f\u5411\u51b0\u5ddd\u5929\u5973\u6c42\u6551\u7684\uff0c\u539f\u6765\u51b0\u5ddd\u5929\u5973\u5c31\u4f4f\u5728\u8fd9\u91cc\uff01\u5440\uff0c\u8fd9\u85cf\u65cf\u5c11\u5973\u4e5f \u771f\u662f\u591a\u707e\u591a\u96be\uff0c\u4f60\u542c\u5979\u8fd9\u6b4c\u58f0\u793a\u610f\uff0c\u5206\u660e\u662f\u53c8\u6709\u6076\u4eba\u8ffd\u8d76\u5979\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u4e0d\u5f85\u5e08\u7236\u5429\u5490\uff0c\u7acb\u523b\u638c\u5fc3\u6697\u6263\u98de\u5200\uff0c\u8d76\u4e0a\u524d\u53bb\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u62d7\uff0c\u5ffd\u89c9\u773c\u775b\u4e00\u4eae\uff0c \u7fa4\u5cf0\u73af\u62b1\u4e4b\u4e2d\uff0c\u7adf\u7136\u662f\u767d\u832b\u832b\u7684\u4e00\u7247\u6e56\u6c34\u3002\u539f\u6765\u8fd9\u4e2a\u5927\u6e56\uff0c\u4fbf\u662f\u4e16\u754c\u7684\u7b2c\u4e00\u9ad8\u6e56\uff0c\u85cf\u540d\u53eb\u505a \u201c\u817e\u683c\u91cc\u6d77\u201d\uff0c\u5b83\u7684\u6e56\u9762\u6d77\u62d4\u5728\u56db\u5343\u516d\u767e\u4e03\u5341\u4e8c\u516c\u5c3a\u4ee5\u4e0a\uff0c\u6bd4\u4e16\u754c\u8457\u540d\u7684\u9ad8\u6e56\u2014\u2014\u201c\u7684\u7684\u5580 \u5580\u6e56\u201d\uff08\u5728\u5357\u7f8e\u6d32\u73bb\u91cc\u5229\u4e9a\u9ad8\u539f\uff09\u8fd8\u9ad8\u516b\u767e\u591a\u516c\u5c3a\uff0c\u4e5f\u5c31\u662f\u8bf4\u7ea6\u76f8\u5f53\u4e8e\u4e09\u4e2a\u6cf0\u5c71\u9ad8\uff0c\u771f\u662f\u4e16 \u754c\u552f\u4e00\u65e0\u4e8c\u7684\u5947\u8ff9\uff01 \u3000\u3000\u9648\u5929\u5b87\u4e00\u773c\u671b\u53bb\uff0c\u4f46\u89c1\u6e56\u6c34\u6e05\u6f88\uff0c\u78a7\u6ce2\u8361\u6f3e\uff0c\u6e56\u4e2d\u6709\u7247\u7247\u95ea\u5149\u7684\u6d6e\u51b0\uff0c\u6e56\u8fb9\u6c34\u8fde\u5929\uff0c\u5929 \u8fde\u6c34\uff0c\u604d\u5982\u6e56\u6cca\u5c31\u5728\u5929\u4e0a\u3002\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u85cf\u80de\u79f0\u5b83\u4e3a\u2018\u7eb3\u6728\u9519\u2019\uff08\u5373\u662f\u6c49\u4eba\u6240\u8bf4\u7684 \u2018\u5929\u6e56\u2019\uff0c\u4e0d\u77e5\u51b0\u5ddd\u5929\u5973\u662f\u4e0d\u662f\u4f4f\u5728\u8fd9\u513f\uff1f\u8fd9\u5012\u771f\u662f\u4e2a\u4e16\u5916\u6843\u6e90\u4e4b\u5883\u3002\u201d \u3000\u3000\u6e56\u8fb9\u7eff\u8349\u5982\u8335\uff0c\u6742\u82b1\u751f\u6811\uff0c\u6709\u767d\u7eb1\u5934\u5dfe\u8fce\u98ce\u98d8\u62c2\uff0c\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u829d\u5a1c\u6c5f\u739b\u53e4\u4fee\uff0c\u6211\u5728 \u8fd9\u513f\uff01\u201d\u90a3\u85cf\u65cf\u5c11\u5973\u8f6c\u8fc7\u5934\u6765\uff0c\u521a\u4e00\u7167\u9762\uff0c\u5ffd\u542c\u5f97\u6709\u58f0\u53eb\u9053\uff1a\u201c\u829d\u5a1c\u6c5f\u739b\u53e4\u4fee\uff0c\u54b1\u4eec\u4e5f\u5728\u8fd9 \u513f\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u6811\u836b\u4e0b\u7a81\u7136\u6251\u51fa\u4e24\u6761\u5927\u6c49\uff0c\u4e00\u8eab\u7070\u8272\u7bad\u8863\uff0c\u6ee1\u9762\u72de\u7b11\uff0c\u4f38\u624b\u671d\u829d\u5a1c\u5c31\u6293\u3002 \u3000\u3000\u9648\u5929\u5b87\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6076\u8d3c\u4f11\u5f97\u901e\u51f6\u4e86\uff01\u201d\u8131\u624b\u4e24\u67c4\u98de\u5200\uff0c\u90a3\u4e24\u4e2a\u7070\u8863\u4eba\u89e3\u4e0b\u8170\u5e26\uff0c\u8fce\u7740 \u98de\u5200\u4e00\u6296\uff0c\u7acb\u89c1\u4e24\u9053\u94f6\u5149\uff0c\u5c04\u5165\u6e56\u5fc3\uff0c\u9648\u5929\u5b87\u7684\u98de\u5200\uff0c\u7adf\u7136\u88ab\u4ed6\u4eec\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u5377\u98de\u4e86 \u53bb\u3002 \u3000\u3000\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u5ffd\u542c\u5f97\u90a3\u4e24\u4eba\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u4e00\u4e2a\u6eda\u5730\u846b\u82a6\uff0c\u4ece\u5c71\u5761\u76f4\u6eda\u4e0b\u53bb\uff0c\u539f\u6765 \u662f\u8427\u9752\u5cf0\u98de\u8eab\u8d76\u81f3\uff0c\u6298\u4e86\u4e24\u652f\u6811\u679d\uff0c\u6253\u4e2d\u4e86\u90a3\u4e24\u4eba\u7684\u7a74\u9053\u3002\u90a3\u4e24\u4eba\u672c\u6765\u4e5f\u975e\u5eb8\u624b\uff0c\u53ea\u56e0\u5168\u795e \u62e8\u5f00\u9648\u5929\u5b87\u7684\u98de\u5200\uff0c\u51b7\u4e0d\u9632\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u90a3\u85cf\u65cf\u5c11\u5973\u4ed3\u7687\u5954\u8d70\uff0c\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u6ca1\u4e8b\u5566\uff0c\u654c\u4eba\u5df2\u7ecf\u88ab\u6211\u7684\u5e08\u7236\u6253\u8d70\u4e86\u3002\u201d\u8427\u9752\u5cf0 \u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u5f92\u5f1f\u7684\u8a00\u8bed\u3001\u884c\u52a8\u3001\u795e\u60c5\uff0c\u4e0d\u7531\u5f97\u60f3\u8d77\u81ea\u5df1\u5f53\u5e74\u60c5\u7aa6\u521d\u5f00\u4e4b\u65f6\uff0c\u6697\u604b\u8c22\u4e91\u771f\u7684 \u5149\u666f\u3002\u5f53\u4e0b\u653e\u6162\u811a\u6b65\uff0c\u4e0d\u53bb\u6253\u6405\u4ed6\u4eec\u3002\u5ffd\u89c1\u82b1\u6811\u4e1b\u4e2d\u4eba\u5f71\u4e00\u95ea\uff0c\u6709\u4e2a\u6781\u5176\u51b7\u5ced\u7684\u58f0\u97f3\u8bf4\u9053\uff1a \u201c\u597d\u624b\u6cd5\uff0c\u597d\u624b\u6cd5\uff0c\u54b1\u4eec\u8001\u670b\u53cb\u53c8\u89c1\u9762\u5566\uff01\u201d\u8427\u9752\u5cf0\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u89c1\u524d\u9762\u73b0\u51fa\u4e24\u4eba\uff0c \u8d70\u5728\u524d\u9762\u7684\u90a3\u4eba\uff0c\u9762\u4e0a\u4ea4\u53c9\u4e24\u9053\u5200\u75d5\uff0c\u5706\u7741\u72ec\u773c\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u5728\u6e56\u5149\u5c71\u8272\u63a9\u6620\u4e4b\u4e0b\u3002\u66f4\u663e\u5f97 \u8be1\u79d8\u4e4b\u6781\uff0c\u53ef\u6016\u975e\u5e38\u3002\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u4ee4\u8427\u9752\u5cf0\u65e5\u591c\u62c5\u5fc3\uff0c\u9b42\u68a6\u4e0d\u5b89\u7684\u5f3a\u4ec7\u5927\u654c\uff0c\u6b66\u5f53\u6d3e\u7b2c \u4e8c\u4ee3\u7684\u7b2c\u4e00\u9ad8\u624b\u96f7\u9707\u5b50\u3002\u540e\u9762\u7684\u90a3\u4eba\u5219\u662f\u5d14\u4e91\u5b50\uff0c\u4ed6\u5403\u4e86\u96ea\u83b2\uff0c\u8fc7\u4e86\u591a\u65e5\uff0c\u8eab\u4f53\u5df2\u662f\u5b8c\u5168\u6062 \u590d\uff0c\u8fd9\u65f6\u63d0\u7740\u4e00\u5f20\u5927\u5f13\uff0c\u90a3\u88ab\u8427\u9752\u5cf0\u62c2\u5c18\u6bc1\u4e86\u7684\u5f13\u5f26\uff0c\u53c8\u5df2\u91cd\u65b0\u8865\u4e0a\u3002\u968f\u624b\u4e00\u5f39\uff0c\u6323\u6323\u4f5c \u54cd\uff0c\u4e5f\u5728\u51b7\u51b7\u7684\u76ef\u7740\u8427\u9752\u5cf0\u3002 \u3000\u3000\u9648\u5929\u5b87\u8854\u5c3e\u8ffd\u90a3\u85cf\u65cf\u5c11\u5973\uff0c\u53ea\u89c1\u90a3\u85cf\u65cf\u5c11\u5973\u4ece\u5d14\u4e91\u5b50\u7684\u8eab\u65c1\u5954\u8fc7\uff0c\u5d14\u4e91\u5b50\u88c2\u5634\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u6851\u739b\uff0c\u591a\u8c22\u4f60\u7684\u96ea\u83b2\u3002\u201d\u5e76\u4e0d\u62e6\u963b\uff0c\u5374\u628a\u5f13\u5f26\u4e00\u62e8\uff0c\u8f6c\u8fc7\u6765\u8fce\u7740\u9648\u5929\u5b87\uff0c\u8427\u9752\u5cf0\u6025\u58f0 \u53eb\u9053\uff1a\u201c\u5b87\u513f\uff0c\u56de\u6765\uff01\u201d\u9648\u5929\u5b87\u9000\u56de\u5e08\u7236\u8eab\u8fb9\uff0c\u53ea\u89c1\u90a3\u85cf\u65cf\u5c11\u5973\u7ed5\u7740\u6e56\u8fb9\u6025\u5954\uff0c\u5df2\u8dd1\u51fa\u534a\u91cc \u4e4b\u9065\u3002 \u3000\u3000\u96f7\u9707\u5b50\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u957f\u5251\uff0c\u5de6\u53f3\u6325\u52a8\uff0c\u54a7\u6dae\u6709\u58f0\uff0c\u4e00\u6b65\u4e00\u6b65\uff0c\u5411\u8427\u9752\u5cf0\u8feb\u8fd1\uff0c\u8427\u9752\u5cf0 \u9053\uff1a\u201c\u5f53\u5e74\u4e4b\u4e8b\uff0c\u5b9e\u662f\u51fa\u4e8e\u65e0\u610f\uff0c\u96f7\u5927\u54e5\u4f60\u4f55\u5fc5\u803f\u803f\u4e8e\u5fc3\u3002\u201d\u96f7\u9707\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8138\u4e0a\u808c \u8089\u626d\u66f2\uff0c\u66f4\u662f\u96be\u770b\uff0c\u53ea\u542c\u4ed6\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8981\u6211\u4e0d\u803f\u803f\u4e8e\u5fc3\uff0c\u90a3\u4e5f\u5bb9\u6613\uff0c\u4f60\u8d70\u8fc7\u6765\uff0c\u8ba9\u6211\u7167\u6837 \u7684\u5728\u4f60\u7684\u9762\u4e0a\u5212\u4e0a\u4e24\u5200\uff0c\u518d\u525c\u6389\u4f60\u7684\u773c\u775b\uff0c\u90a3\u5c31\u4e86\u7ed3\u5566\uff01\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u53c8\u4e0d\u662f\u6211\u5e72 \u7684\uff0c\u6211\u53ea\u662f\u65e0\u610f\u4e4b\u4e2d\u52a9\u4e86\u8c22\u4e91\u771f\u4e00\u81c2\u4e4b\u529b\u7f62\u4e86\u3002\u201d\u96f7\u9707\u5b50\u72ec\u773c\u4e00\u77aa\uff0c\u9762\u8272\u8d8a\u53d1\u96be\u770b\uff0c\u8427\u9752\u5cf0 \u4e0d\u63d0\u8c22\u4e91\u771f\u4e5f\u8fd8\u7f62\u4e86\uff0c\u63d0\u8d77\u4e86\u8c22\u4e91\u771f\u66f4\u662f\u4ee4\u4ed6\u60b2\u6124\u4e8e\u5fc3\uff0c\u4ed6\u672c\u662f\u4e2a\u7f8e\u7537\u5b50\uff0c\u800c\u4eca\u5374\u53d8\u4e86\u8fd9\u6837 \u7684\u4e00\u4e2a\u4e11\u516b\u602a\uff0c\u8ffd\u6e90\u7978\u59cb\uff0c\u4ed6\u5bfb\u4e0d\u7740\u8c22\u4e91\u771f\u3001\u6ee1\u8154\u6012\u6c14\u90fd\u53d1\u6cc4\u5728\u8427\u9752\u5cf0\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u96f7\u9707\u5b50\u4e00\u6b65\u4e00\u6b65\u7684\u8feb\u8fd1\uff0c\u957f\u5251\u4e00\u6307\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u670b\u53cb\uff0c\u4f60\u7684\u6280\u4e1a\u6ca1\u6709\u9000\u51cf\uff0c\u6211 \u96f7\u67d0\u4eba\u4e5f\u7ec3\u4e86\u51e0\u624b\u529f\u592b\uff0c\u54b1\u4eec\u5341\u51e0\u5e74\u524d\u66fe\u6bd4\u8fc7\u4e00\u573a\uff0c\u800c\u4eca\u6211\u53c8\u8981\u5411\u4f60\u732e\u4e11\u5566\uff01\u201d\u957f\u5251\u4e00\u6325\uff0c \u54a7\u7684\u4e00\u5251\uff0c\u7acb\u523b\u5411\u8427\u9752\u5cf0\u65bd\u5c55\u6740\u624b\uff01 \u3000\u3000\u8427\u9752\u5cf0\u82e6\u7b11\u9053\uff1a\u201c\u96f7\u5927\u54e5\uff0c\u4f60\u5b9e\u5728\u6324\u5f97\u5c0f\u5f1f\u6ca1\u6cd5\u5566\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8fde\u95ea\u4e09\u5251\uff0c\u96f7\u9707\u5b50\u4e00 \u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u7b2c\u56db\u5251\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u76f4\u53d6\u8427\u9752\u5cf0\u80f8\u819b\u7684\u201c\u671f\u95e8\u7a74\u201d\uff0c\u5251\u52bf\u96c4\u52b2\uff0c\u4e07\u96be\u95ea \u907f\uff0c\u8427\u9752\u5cf0\u5ffd\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u62c2\u5c18\u4e00\u6325\uff0c\u5343\u7f15\u7384\u4e1d\uff0c\u7acb\u523b\u7f20\u4f4f\u4e86\u96f7\u9707\u5b50\u7684\u957f\u5251\u3002\u539f\u6765\u8427\u9752\u5cf0\u5fc3 \u602f\u5f3a\u4ec7\uff0c\u5341\u6570\u5e74\u6765\uff0c\u82e6\u5fc3\u601d\u7d22\u7834\u654c\u4e4b\u6cd5\uff0c\u96f7\u9707\u5b50\u7684\u5251\u6cd5\u6b66\u529f\uff0c\u90fd\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff0c\u56e0\u6b64\u53ea\u80fd\u8ba1 \u53d6\uff0c\u4e0d\u80fd\u529b\u654c\uff0c\u4ed6\u9002\u624d\u8fde\u95ea\u4e09\u5251\uff0c\u6545\u793a\u602f\u6001\uff0c\u5f85\u96f7\u9707\u5b50\u5251\u52bf\u653e\u5c3d\uff0c\u8fd9\u624d\u4e00\u4e3e\u5c06\u4ed6\u957f\u5251\u7f20\u7740\uff0c \u987b\u77e5\u8427\u9752\u5cf0\u7684\u62c2\u5c18\uff0c\u4e43\u662f\u4e00\u4ef6\u6b66\u6797\u5f02\u5b9d\uff0c\u4f5b\u5c18\u770b\u6765\u4f3c\u662f\u5c18\u5c3e\uff0c\u5176\u5b9e\u5374\u662f\u4e4c\u91d1\u7cbe\u70bc\u7684\u7384\u4e1d\uff0c\u575a \u97e7\u4e4b\u6781\uff0c\u5200\u5251\u6240\u4e0d\u80fd\u65ad\uff0c\u4e00\u88ab\u7f20\u4e0a\uff0c\u5175\u5668\u7eb5\u4e0d\u8131\u624b\uff0c\u4e5f\u96be\u89e3\u8131\u3002\u8427\u9752\u5cf0\u89c1\u5341\u51e0\u5e74\u6765\u82e6\u5fc3\u601d\u7d22 \u7684\u7834\u654c\u4e4b\u6cd5\uff0c\u679c\u7136\u5f97\u5fc3\u5e94\u624b\uff0c\u4e0d\u7981\u5927\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u518d\u51f6\uff0c\u4e5f\u65bd\u5c55\u4e0d\u5f00\u5566\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u96f7\u9707\u5b50\u4e00\u58f0\u51b7\u7b11\uff0c\u5618\u6c14\u4e00\u5439\uff0c\u5251\u628a\u4e00\u98a4\uff0c\u94c1\u62c2\u5c18\u7684\u5343\u7f15\u7384\u4e1d\uff0c\u7adf\u5982\u98ce\u4e2d\u6e38\u4e1d\u98d8\u98d8 \u98de\u626c\uff0c\u8427\u9752\u5cf0\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u60f3\u4e0d\u5230\u96f7\u9707\u5b50\u7684\u6c14\u529f\u7adf\u7136\u70bc\u5230\u5982\u6b64\u5883\u754c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u96f7\u9707\u5b50\u957f\u5251\u4e00\u6296\uff0c\u6dae\u6dae\u6dae\u53c8\u5df2\u8fde\u8fdb\u4e09\u62db\uff0c\u8427\u9752\u5cf0\u62c2\u5c18\u6325\u821e\uff0c\u53ea\u80fd\u5c01\u95ed\u95e8\u6237\uff0c\u66f4\u65e0\u4f59\u529b\u8fdb\u62db\u3002 \u3000\u3000\u96f7\u9707\u5b50\u8d8a\u653b\u8d8a\u6025\uff0c\u4e00\u53e3\u5251\u4f7f\u5f97\u795e\u51fa\u9b3c\u6ca1\uff0c\u5251\u5251\u6307\u5411\u654c\u4eba\u8981\u5bb3\uff0c\u8427\u9752\u5cf0\u8fde\u8fde\u540e\u9000\uff0c\u5934\u4e0a\u5192 \u51fa\u817e\u817e\u70ed\u6c14\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002\u518d\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u53ea\u89c1\u96f7\u9707\u5b50\u53c8\u8fd0\u6c14\u4e00\u5439\uff0c\u6a2a\u5251\u4e00\u524a\uff0c\u8427\u9752\u5cf0 \u7684\u62c2\u5c18\u767b\u65f6\u65ad\u4e86\u4e00\u7f15\uff0c\u5982\u4e71\u8349\u822c\u98d8\u8361\u7a7a\u4e2d\u3002\u8427\u9752\u5cf0\u7684\u62c2\u5c18\uff0c\u5c18\u5c3e\u82e5\u7136\u805a\u5728\u4e00\u5904\uff0c\u90a3\u662f\u5929\u4e0b\u6700 \u5229\u7684\u5b9d\u5251\u4e5f\u4e0d\u80fd\u622a\uff0c\u4f46\u88ab\u96f7\u9707\u5b50\u8fd0\u6c14\u5439\u6563\uff0c\u518d\u628a\u5185\u5bb6\u771f\u529b\u8fd0\u5230\u5251\u4e0a\uff0c\u90a3\u5c31\u5982\u4e00\u675f\u7b77\u5b50\u62c6\u4e86\u5f00 \u6765\uff0c\u5bb9\u6613\u6298\u65ad\u4e00\u6837\u3002\u8427\u9752\u5cf0\u5fc3\u75db\u4e4b\u6781\uff0c\u4e0d\u6562\u518d\u6597\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u8ba4\u547d\u5566\uff01\u201d\u96f7\u9707\u5b50\u4e00 \u58f0\u72de\u7b11\uff0c\u8fc8\u524d\u4e24\u6b65\uff0c\u773c\u5149\u76ef\u7740\u8427\u9752\u5cf0\u7684\u9762\u5b54\uff0c\u5229\u5251\u4e00\u6643\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u8fd9\u4e24\u5251\u8981\u5728\u4f60\u9762\u4e0a \u5212\u51fa\u4ea4\u53c9\u4e24\u9053\u4f24\u75d5\uff0c\u4e0e\u6211\u9762\u4e0a\u7684\u4e00\u6a21\u4e00\u6837\u3002\u5d14\u8d24\u5f1f\uff0c\u4f60\u4e5f\u6765\u770b\u770b\uff0c\u770b\u770b\u4e3a\u5144\u7684\u624b\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u8427\u9752\u5cf0\u53ea\u611f\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u95ed\u4e86\u773c\u775b\uff0c\u4e0d\u6562\u770b\u96f7\u9707\u5b50\u624b\u4e2d\u5229\u5251\uff0c\u5ffd\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c \u96f7\u9707\u5b50\u5927\u559d\u9053\uff1a\u201c\u4f55\u65b9\u5c0f\u5b50\uff0c\u6562\u65bd\u6697\u7b97\uff1f\u201d\u8427\u9752\u5cf0\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u96f7\u9707\u5b50\u7684\u5251\u5c16\u6b6a\u8fc7\u4e00\u8fb9\uff0c \u98a4\u52a8\u4e0d\u5df2\uff0c\u55e1\u55e1\u4f5c\u54cd\uff0c\u663e\u662f\u88ab\u4ec0\u4e48\u6697\u5668\u6253\u4e2d\uff0c\u4e0d\u7981\u5927\u5947\uff1a\u8c01\u4eba\u6709\u6b64\u526a\u529b\uff0c\u7adf\u7136\u80fd\u628a\u96f7\u9707\u5b50\u7684 \u957f\u5251\u6253\u6b6a\uff1f \u3000\u3000\u96f7\u9707\u5b50\u8bdd\u72b9\u6765\u4e86\uff0c\u7acb\u523b\u6709\u4eba\u63a5\u58f0\u5e94\u9053\uff1a\u201c\u4f60\u8001\u5b50\u5c31\u5728\u8fd9\u513f\uff0c\u4f60\u773c\u775b\u778e\u4e86\u5417\uff1f\u201d\u96f7\u9707\u5b50\u626d \u5934\u4e00\u770b\uff0c\u53ea\u89c1\u53f3\u65b9\u8eab\u4fa7\uff0c\u7a81\u7136\u591a\u4e86\u4e00\u4eba\uff0c\u8138\u5982\u9505\u5e95\uff0c\u53d1\u5982\u4e71\u8349\uff0c\u9f3b\u5b54\u671d\u5929\uff0c\u8eab\u4e0a\u9e26\u8863\u767e\u7ed3\uff0c \u7adf\u7136\u662f\u4e2a\u53eb\u5316\u3002\u8427\u9752\u5cf0\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u94c1\u62d0\u4ed9\u6b64\u6765\uff0c\u4e0d\u77e5\u662f\u53cb\u662f\u654c\u3002\u201d\u4f46\u4ed6\u73b0\u5728\u5df2\u662f\u96f7 \u9707\u5b50\u7827\u4e0a\u4e4b\u8089\uff0c\u53cd\u6b63\u53ea\u6709\u7b49\u6b7b\u7684\u4efd\u513f\uff0c\u5373\u7b97\u94c1\u62d0\u4ed9\u662f\u654c\uff0c\u4e5f\u4e0d\u8fc7\u5982\u662f\u800c\u5df2\uff0c\u5e76\u4e0d\u589e\u52a0\u5fe7\u8651\uff1b \u96f7\u9707\u5b50\u5374\u5927\u662f\u60ca\u7591\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5929\u6e56\u6765\u602a\u5ba2\uff0c\u5251\u6c14\u6620\u51b0\u6cb3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u6e56\u7554\u5bc4\u60c5\u3000\u62d0\u4ed9\u65bd\u5999\u624b\u3000\u51b0\u6cb3\u602a\u5f71\u3000\u5929\u5973\u6151\u7fa4\u8c6a \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u56db \u56de \u6e56\u7554\u5bc4\u60c5\u3000\u62d0\u4ed9\u65bd\u5999\u624b \u51b0\u6cb3\u602a\u5f71\u3000\u5929\u5973\u6151\u7fa4\u8c6a \u3000\u3000\u90a3\u602a\u53eb\u5316\u6491\u7740\u94c1\u62d0\uff0c\u4e00\u8dcb\u4e00\u62d0\u5730\u8d70\u6765\uff0c\u96f7\u9707\u5b50\u867d\u77e5\u6765\u8005\u4e0d\u5584\uff0c\u4f46\u81ea\u6043\u5df2\u7ec3\u597d\u4e0a\u4e58\u5185\u529f\uff0c \u7edd\u5999\u5251\u6cd5\uff0c\u4e5f\u5e76\u4e0d\u600e\u6837\u653e\u5728\u5fc3\u4e0a\uff0c\u5f53\u4e0b\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8427\u9752\u5cf0\u4f60\u7684\u4eba\u9762\u5012\u771f\u4e0d\u9519\uff0c\u9884\u5148\u7ea6\u597d\u4e86 \u670b\u53cb\u6765\u5566\uff01\u201d\u4e0e\u5d14\u4e91\u5b50\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u53eb\u4ed6\u51c6\u5907\u5939\u51fb\uff0c\u90a3\u602a\u53eb\u5316\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eca\u65e5\u4e0d\u662f \u6765\u52a9\u62f3\uff0c\u662f\u51c6\u5907\u6765\u6328\u6253\u54e9\uff01\u5582\uff0c\u4f60\u662f\u60f3\u5728\u4ed6\u7684\u9762\u4e0a\u5212\u4e24\u5200\u4e48\uff1f\u201d\u96f7\u9707\u5b50\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u770b\u4e0d \u8fc7\u773c\uff0c\u8981\u66ff\u4f60\u7684\u670b\u53cb\u51fa\u5934\u6765\u4e86\uff1f\u201d\u90a3\u602a\u53eb\u5316\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u7a77\u5316\u5b50\u54ea\u6765\u7684\u8bb8\u591a\u670b \u53cb\uff1f\u4e0d\u8fc7\uff0c\u6211\u770b\u8fd9\u4f4d\u8427\u5148\u751f\u4e00\u8868\u65af\u6587\uff0c\u548c\u4f60\u5f53\u5e74\u4e00\u6837\u3002\u5f53\u5e74\u4f60\u4ece\u5c0f\u767d\u8138\u53d8\u6210\u4e86\u4e11\u516b\u602a\uff0c\u75db\u4e0d \u6b32\u751f\u3002\u5df1\u6240\u4e0d\u6b32\uff0c\u5c82\u53ef\u91cd\u65bd\u4e8e\u4eba\uff01\u54c8\uff0c\u6211\u5012\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u6211\u662f\u4e2a\u4e11\u516b\u602a\uff0c\u4e5f\u4e0d\u6562\u5984\u60f3\u6709\u4f73\u4eba \u5782\u9752\uff0c\u5c31\u5728\u9762\u4e0a\u518d\u6dfb\u591a\u4e24\u9053\u5200\u75d5\uff0c\u4e5f\u4e11\u4e0d\u5230\u54ea\u91cc\u53bb\u3002\u6211\u5c31\u66ff\u4ed6\u6328\u4e86\u8fd9\u4e24\u5200\u5427\uff0c\u4f60\u7684\u5229\u5251\u5c3d\u7ba1 \u5411\u6211\u7684\u9762\u4e0a\u62db\u547c\uff01\u5514\uff0c\u81f3\u4e8e\u8fd9\u4f4d\u8427\u5148\u751f\uff0c\u4f60\u77aa\u7740\u773c\u775b\u770b\u6211\u505a\u4ec0\u4e48\uff1f\u6211\u6253\u4e86\u4f60\u4e00\u62d0\u4f60\u4e0d\u670d\u6c14 \u4e48\uff1f\u4e0d\u670d\u6c14\u5c31\u4e5f\u4e0a\u6765\u52a8\u624b\u5427\uff01\u201d\u8427\u9752\u5cf0\u62c2\u5c18\u4e00\u6302\uff0c\u7b54\u58f0\uff1a\u201c\u4e0d\u6562\u3002\u201d\u9000\u8fc7\u4e00\u65c1\uff0c\u5fc3\u4e2d\u5947\u602a\u4e4b \u6781\u3002 \u3000\u3000\u96f7\u9707\u5b50\u542c\u90a3\u53eb\u5316\u5b50\u7684\u8bf4\u8bdd\uff0c\u53e5\u53e5\u6697\u5b58\u5632\u7b11\uff0c\u6b63\u6b63\u89e6\u53ca\u4ed6\u7684\u75ae\u75a4\uff0c\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a \u201c\u597d\uff0c\u8fd9\u53ef\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u770b\u5251\uff01\u201d\u51fa\u624b\u5982\u7535\uff0c\u5587\u7684\u4e00\u5251\uff0c\u90a3\u53eb\u5316\u62d0\u6756\u4e00\u7ad6\uff0c\u53ea\u542c\u5f97\u201c\u9677\u201d \u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u96f7\u9707\u5b50\u7684\u8eab\u8eaf\u5f39\u5230\u534a\u7a7a\uff0c\u5c31\u5728\u7a7a\u4e2d\u4e00\u62db\u201c\u9e4f\u640f\u4e5d\u9704\u201d\uff0c\u51cc\u7a7a\u4e0b\u523a\uff0c\u5251\u52bf \u4ecd\u662f\u51cc\u5389\u4e4b\u6781\uff0c\u602a\u53eb\u5316\u559d\u58f0\u597d\uff0c\u968f\u624b\u4e00\u6296\uff0c\u94c1\u62d0\u500f\u5730\u76f4\u5f39\u76f4\u6765\uff0c\u6756\u5c16\u6307\u5411\u4e39\u7530\u8981\u7a74\uff0c\u96f7\u9707\u5b50 \u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u4e0b\u6765\uff0c\u957f\u5251\u70b9\u5230\u602a\u53eb\u5316\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u602a\u53eb\u5316\u5fae\u4e00\u7f29\u80a9\uff0c\u6756\u5934\u7a0d\u504f\uff0c\u96f7\u9707\u5b50\u7684 \u957f\u5251\u4e0e\u602a\u53eb\u5316\u7684\u94c1\u62d0\u4ea4\u64e6\u800c\u8fc7\uff0c\u8fd9\u4e00\u62db\uff0c\u53cc\u65b9\u90fd\u662f\u9669\u6781\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u8427\u9752\u5cf0\u770b \u4e86\uff0c\u4e0d\u7981\u6697\u6697\u53f9\u670d\u3002\u53ea\u89c1\u602a\u53eb\u5316\u94c1\u62d0\u4e00\u62bd\uff0c\u987a\u52bf\u53cd\u5c55\uff0c\u75be\u5982\u9a87\u7535\u5954\u96f7\uff0c\u7838\u5251\u523a\u7a74\uff0c\u5484\u5484\u8feb \u4eba\u3002\u96f7\u9707\u5b50\u4e00\u5251\u523a\u51fa\uff0c\u5de6\u638c\u4e00\u62cd\uff0c\u501f\u7740\u94c1\u62d0\u5f39\u5251\u4e4b\u529b\uff0c\u8eab\u5f62\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u5de6\u638c\u62cd\u4e0b\uff0c\u6070\u597d\u62cd\u5230 \u602a\u53eb\u5316\u540e\u9888\u7684\u201c\u5929\u67f1\u7a74\u201d\u3002\u602a\u53eb\u5316\u53c8\u559d\u4e86\u58f0\uff1a\u201c\u597d\uff01\u201d\u7adf\u50cf\u80cc\u540e\u957f\u7740\u773c\u775b\u4e00\u6837\uff0c\u80a9\u5934\u4e00\u649e\uff0c \u53cd\u62d0\u4e00\u62bd\uff0c\u4ee5\u653b\u5bf9\u653b\uff0c\u5c06\u96f7\u9707\u5b50\u7684\u62db\u6570\u5316\u89e3\u5f00\u53bb\u3002 \u3000\u3000\u96f7\u9707\u5b50\u60ca\u9a87\u4e4b\u6781\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u94c1\u62d0\u4ed9\uff1f\u201d\u602a\u53eb\u5316\u77aa\u76ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4e0d\u6562\u5212\u82b1\u6211\u7684\u9762 \u5b54\uff0c\u6211\u5374\u8981\u5728\u4f60\u7684\u80cc\u810a\u6253\u4e0a\u4e09\u62d0\uff0c\u6559\u8bad\u6559\u8bad\u4f60\u8fd9\u5c0f\u5b50\u3002\u201d\u96f7\u9707\u5b50\u5927\u6012\u9053\uff1a\u201c\u4f60\u5c31\u662f\u94c1\u62d0\u4ed9\u6211 \u4e5f\u4e0d\u6015\u4f60\uff01\u201d\u4e00\u62db\u201c\u91ce\u706b\u6625\u98ce\u201d\uff0c\u5251\u5c16\u4e00\u6311\uff0c\u53c8\u523a\u8fc7\u53bb\u3002\u94c1\u62d0\u4ed9\u970d\u5730\u4e00\u8df3\uff0c\u94c1\u62d0\u4e00\u626b\uff0c\u8fc5\u5373 \u8fd8\u62db\uff0c\u8fd9\u4e00\u6765\u6597\u5f97\u66f4\u70c8\uff0c\u4f46\u89c1\u6756\u5f71\u5982\u5c71\uff0c\u5251\u5149\u4f3c\u7ec3\uff0c\u6740\u5f97\u4e2a\u96be\u89e3\u96be\u5206\uff0c\u94c1\u62d0\u4ed9\u8155\u529b\u60ca\u4eba\uff0c\u7897 \u53e3\u822c\u7684\u94c1\u62d0\u821e\u5f04\u8d77\u6765\u3001\u5982\u62c8\u706f\u8349\uff0c\u6325\u6d12\u81ea\u5982\uff0c\u6756\u98ce\u6240\u81f3\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u597d\u4e0d\u60ca\u4eba\u3002\u800c\u96f7\u9707\u5b50\u5251 \u8d70\u8f7b\u7075\uff0c\u5251\u52bf\u5982\u8679\uff0c\u4e5f\u662f\u53d8\u5316\u83ab\u6d4b\u3002 \u3000\u3000\u8427\u9752\u5cf0\u770b\u5f97\u76ee\u7729\u795e\u6447\uff0c\u53ea\u89c1\u5251\u6765\u6756\u5f80\uff0c\u53cc\u65b9\u90fd\u662f\u4e00\u6d3e\u8fdb\u624b\u62db\u6570\uff0c\u4efb\u4f55\u4e00\u65b9\uff0c\u53ea\u8981\u7a0d\u4e00\u4e0d \u614e\uff0c\u5c31\u8981\u8840\u6d12\u9ec4\u6c99\u3002\u8427\u9752\u5cf0\u624b\u634f\u62c2\u5c18\uff0c\u5d14\u4e91\u5b50\u6307\u6309\u5f13\u5f26\uff0c\u4e00\u9762\u6ce8\u76ee\u6597\u573a\uff0c\u4e00\u9762\u4e92\u76f8\u9632\u5907\uff0c\u90fd \u662f\u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\u3002\u5927\u7ea6\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f46\u89c1\u96f7\u9707\u5b50\u7684\u5934\u4e0a\u5df2\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u602a\u53eb\u5316\u811a\u8e0f \u516b\u5366\u65b9\u4f4d\uff0c\u653b\u52bf\u6e10\u6e10\u7f13\u6162\u4e0b\u6765\u3002\u8427\u8037\u5cf0\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u7a76\u7adf\u662f\u94c1\u62d0\u4ed9\u7a0d\u80dc\u4e00\u7b79\u3002\u201d\u94c1\u62d0 \u4ed9\u7684\u6756\u52bf\u867d\u7f13\uff0c\u529b\u9053\u5374\u662f\u6bd4\u524d\u6c89\u91cd\u5f97\u591a\uff0c\u96f7\u9707\u5b50\u7684\u5251\u52bf\u5df2\u6e10\u6e10\u7684\u88ab\u4ed6\u7684\u6756\u529b\u8feb\u4f4f\uff0c\u5708\u5b50\u8d8a\u7f29 \u8d8a\u5c0f\uff0c\u5f62\u52bf\u4e5f\u8d8a\u6765\u8d8a\u9669\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u5374\u5e76\u4e0d\u50cf\u4ed6\u7684\u5e08\u7236\u90a3\u6837\u5168\u795e\u8d2f\u6ce8\u6597\u573a\uff0c\u4ed6\u60e6\u6302\u90a3\u4e2a\u85cf\u65cf\u59d1\u5a18\uff0c\u4e0d\u4f4f\u7684\u6e38\u76ee\u56db\u770b\uff0c \u90a3\u85cf\u65cf\u5c11\u5973\u7684\u80cc\u5f71\u5728\u82b1\u6811\u4e1b\u4e2d\u9690\u53bb\u4e4b\u540e\uff0c\u5c31\u518d\u4e5f\u4e0d\u89c1\u51fa\u6765\uff0c\u4e0d\u77e5\u5979\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u5929\u6e56\u9762\u79ef\u6781\u5927\uff0c\u9648\u5929\u5b87\u53d1\u73b0\u5728\u6e56\u7684\u897f\u5317\u89d2\uff0c\u6709\u4e00\u6761\u51b0\u5ddd\uff0c\u6709\u5982\u5929\u6cb3\u5012\u6302\uff0c\u4ece\u5c71\u9876\u4e0a\u76f4\u6cfb \u4e0b\u6765\uff0c\u60f3\u662f\u56e0\u4e3a\u5730\u6c14\u6e29\u6696\u4e4b\u6545\uff0c\u51b0\u5c42\u5e76\u4e0d\u4f3c\u5176\u4ed6\u51b0\u5ddd\u7684\u51dd\u7ed3\u4e0d\u5316\uff0c\u51b0\u5c42\u7684\u4e0b\u9762\u867d\u7136\u4ecd\u4f3c\u4e00\u5ea7 \u5ea7\u7684\u5c0f\u51b0\u5c71\uff0c\u4e0a\u9762\u5374\u6709\u4e00\u5927\u534a\u788e\u88c2\u6210\u4e3a\u51b0\u5858\uff0c\u6709\u7684\u5982\u78e8\u76d8\uff0c\u6709\u7684\u5982\u4e91\u77f3\u7247\uff0c\u968f\u7740\u878d\u5316\u4e86\u7684\u96ea \u6c34\uff0c\u54d7\u5566\u5566\u7684\u51b2\u6cfb\u800c\u4e0b\uff0c\u6ce8\u5165\u5929\u6e56\uff0c\u6e56\u4e2d\u7684\u6d6e\u51b0\uff0c\u5c31\u662f\u8fd9\u6837\u6765\u7684\u3002\u9648\u5929\u5b87\u6781\u76ee\u9065\u671b\uff0c\u51b0\u5ddd\u7684 \u4e0a\u7aef\uff0c\u63a5\u8fd1\u5c71\u9876\u4e4b\u5904\uff0c\u7adf\u4f3c\u6709\u51e0\u5e62\u5bab\u6bbf\u5f0f\u7684\u5efa\u7b51\uff0c\u4f46\u56e0\u8ddd\u79bb\u9065\u8fdc\uff0c\u770b\u4e0d\u6e05\u695a\uff0c\u8fd8\u4e0d\u6562\u786e\u5b9a\uff0c \u90a3\u662f\u623f\u5c4b\u5bab\u6bbf\u8fd8\u662f\u5ca9\u77f3\u7684\u8096\u5f62\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u4e0e\u53e3\u54e8\u58f0\uff0c\u9648\u5929\u5b87\u4e00\u770b\uff0c\u53ea\u89c1\u5c31\u5728\u9002\u624d\u90a3\u85cf\u65cf\u5c11\u5973\u6240\u6765\u4e4b\u5904\uff0c\u6709\u4e00\u4f19\u4eba\u6500 \u767b\u4e0a\u6765\uff0c\u6700\u524d\u9762\u7684\u4e09\u4eba\uff0c\u4e00\u5217\u5e76\u884c\uff0c\u5de6\u53f3\u4e8c\u4eba\u6b63\u662f\u521a\u624d\u8ffd\u90a3\u85cf\u65cf\u5c11\u5973\u3001\u88ab\u81ea\u5df1\u5e08\u7236\u6253\u7ffb\u7684\u6c49 \u5b50\uff0c\u4e2d\u95f4\u90a3\u4eba\u5374\u662f\u4e2a\u62ab\u7740\u5927\u7ea2\u8888\u88df\u7684\u5587\u561b\uff0c\u8fd9\u4e09\u4e2a\u4eba\u4e00\u5230\u6e56\u8fb9\uff0c\u770b\u4e86\u6597\u573a\u4e00\u773c\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c \u76f4\u5411\u90a3\u6761\u51b0\u5ddd\u8d70\u53bb\u3002 \u3000\u3000\u8ddf\u7740\u5c31\u662f\u5728\u65e5\u5580\u5219\u6240\u89c1\u7684\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u8fd9\u4e24\u4eba\u624b\u6367\u85cf\u9999\uff0c\u4e00\u8138\u8654\u656c\u7684\u6837\u5b50\uff0c\u770b\u4e5f \u4e0d\u770b\u6597\u573a\uff0c\u5c31\u8d70\u5230\u51b0\u5ddd\u5165\u6e56\u4e4b\u5904\uff0c\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8f9e\uff0c\u71c3\u8d77\u85cf\u9999\uff0c\u7adf\u7136\u8dea\u4e86\u4e0b\u6765\uff0c\u597d\u50cf\u5728\u4f5c\u8654\u8bda \u7684\u7977\u544a\u3002 \u3000\u3000\u518d\u63a5\u7740\u4e0a\u6765\u7684\u4e00\u4f19\u4eba\uff0c\u4eba\u6570\u6700\u591a\u3002\u7ea6\u6709\u4e94\u516d\u4e2a\u4eba\uff0c\u6709\u7684\u662f\u6cb9\u5934\u7c89\u9762\u7684\u5c11\u5e74\uff0c\u6709\u7684\u662f\u72b6\u8c8c \u7c97\u8c6a\u7684\u6c49\u5b50\uff0c\u6709\u7684\u4f3c\u662f\u5929\u7afa\u50e7\u4eba\uff0c\u6709\u7684\u5374\u53c8\u88c5\u626e\u4e2d\u539f\u6b66\u58eb\u3002\u8fd9\u4f19\u4eba\u90aa\u5f62\u90aa\u76f8\uff0c\u4e00\u4e0a\u5230\u6765\uff0c\u89c1 \u96f7\u9707\u5b50\u4e0e\u94c1\u62d0\u4ed9\u9163\u6597\uff0c\u4f3c\u4e4e\u9887\u4e3a\u60ca\u5947\uff0c\u6709\u7684\u6307\u624b\u5212\u811a\u7684\u8bc4\u8bba\u62db\u6570\u5f97\u5931\uff0c\u6709\u7684\u5374\u5728\u98ce\u8a00\u98ce\u8bed\u7684 \u8c08\u7b11\u3002\u9648\u5929\u5b87\u542c\u5f97\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u5012\u4e5f\u4e0d\u77e5\u81ea\u91cf\uff0c\u766b\u86e4\u819c\u60f3\u5403\u5929\u9e45\u8089\u6765\u5566\uff0c\u4ed6 \u4eec\u7adf\u5148\u6211\u4eec\u800c\u6765\uff0c\u5728\u8fd9\u91cc\u4e89\u98ce\u5403\u918b\u4e86\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u94c1\u62d0\u4ed9\u4e00\u62d0\u6a2a\u6311\uff0c\u547c\u7684\u6311\u8d77\u4e00\u5757\u77f3\u5934\uff0c \u5411\u8bf4\u8bdd\u90a3\u4eba\u98de\u53bb\uff0c\u90a3\u4eba\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u5bb6\u4f19\uff01\u201d\u53cc\u638c\u4e00\u6258\uff0c\u5c06\u90a3\u5757\u77f3\u5934\u63b7\u4e0b\u5c71\u8c37\uff0c\u8f70\u7136\u6709 \u58f0\u3002 \u3000\u3000\u8bd5\u60f3\u94c1\u62d0\u4ed9\u662f\u4f55\u7b49\u529f\u529b\uff0c\u4ed6\u6311\u8d77\u8fd9\u5757\u77f3\u5934\uff0c\u91cd\u903e\u767e\u65a4\uff0c\u98de\u8fc7\u53bb\u53c8\u52b2\u53c8\u6025\uff0c\u90a3\u4eba\u7adf\u7136\u80fd\u8f7b \u63cf\u6de1\u5199\u5730\u4e00\u6258\u6258\u5f00\uff0c\u8db3\u89c1\u6b66\u529f\u4ea6\u7b79\u662f\u4e0d\u5f31\u3002\u8427\u9752\u5cf0\u5fc3\u5185\u6697\u6697\u54fa\u5495\uff1a\u600e\u4e48\u4e00\u4e0b\u5b50\u6765\u4e86\u8fd9\u4e48\u591a\u6b66 \u827a\u79b9\u6db5\u3001\u5947\u5f62\u602a\u76f8\u7684\u4eba\u7269\u3002 \u3000\u3000\u90a3\u4e00\u4f19\u4eba\u89c1\u94c1\u62d0\u4ed9\u663e\u4e86\u8fd9\u624b\u529f\u592b\uff0c\u4e0d\u6562\u518d\u62db\u60f9\u4ed6\uff0c\u4e00\u7a9d\u8702\u81ea\u90fd\u671d\u7740\u51b0\u5ddd\u6ce8\u5165\u5929\u6e56\u4e4b\u5904\u6d8c \u53bb\uff0c\u98ce\u4e2d\u9690\u9690\u7ea6\u7ea6\u9001\u6765\u8c08\u7b11\u4e4b\u58f0\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u4e0d\u77e5\u662f\u4ec0\u4e48\u6a21\u6837\uff1f\u201d\u201c\u540d\u5b57\u8fd9\u6837\u597d\u542c\u3002\u603b\u5e94\u8be5 \u662f\u4e2a\u7f8e\u4eba\uff1f\u201d\u201c\u54c8\uff0c\u5982\u679c\u662f\u4e2a\u4e11\u516b\u602a\u5c31\u8ba9\u7ed9\u4f60\u5427\u3002\u201d\u201c\u4f60\u4e0d\u7528\u6025\uff0c\u51b0\u5ddd\u5929\u5973\u54b1\u4eec\u6ca1\u6709\u89c1\u8fc7\uff0c \u829d\u5a1c\u6c5f\u739b\u53e4\u4fee\u603b\u7b97\u5f97\u662f\u4e2a\u6807\u81f4\u7684\u7f8e\u4eba\u3002\u201d\u4e03\u5634\u516b\u820c\uff0c\u8bf4\u4e2a\u4e0d\u6e05\uff0c\u6e10\u884c\u6e10\u8fdc\uff0c\u58f0\u97f3\u4e5f\u6e10\u6e10\u542c\u4e0d \u6e05\u695a\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4f19\u4eba\u7adf\u7136\u662f\u60f3\u6253\u51b0\u5ddd\u5929\u5973\u7684\u4e3b\u610f\uff0c\u8fd8\u60f3\u52ab\u90a3\u85cf\u65cf\u59d1\u5a18 \u7684\u3002\u201d\u9648\u5929\u5b87\u5bf9\u51b0\u5ddd\u5929\u5973\u53ea\u662f\u597d\u5947\uff0c\u5bf9\u90a3\u85cf\u65cf\u5c11\u5973\u5374\u6709\u4e00\u4efd\u83ab\u540d\u5947\u5999\u7684\u5173\u6000\uff0c\u6697\u81ea\u7740\u6025\u3002\u770b \u5e08\u7236\u65f6\uff0c\u5e08\u7236\u5bf9\u521a\u624d\u6240\u53d1\u751f\u7684\u79cd\u79cd\u4e4b\u4e8b\uff0c\u7adf\u597d\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u4e13\u5fc3\u81f4\u5fd7\u7684\u6ce8\u89c6\u7740\u573a \u4e2d\u7684\u6076\u6597\u3002\u8fd9\u65f6\u4f18\u52a3\u66f4\u660e\u663e\u4e86\uff0c\u94c1\u62d0\u4ed9\u8d8a\u6218\u8d8a\u52c7\u5165\u90a3\u7897\u53e3\u822c\u7c97\u5927\u7684\u62d0\u6756\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u5c31\u5982\u602a \u87d2\u6bd2\u9f99\uff0c\u51cc\u7a7a\u98de\u821e\uff0c\u6bcf\u4e00\u62d0\u90fd\u631f\u7740\u52b2\u98ce\uff0c\u547c\u547c\u8f70\u8f70\u7684\u4f5c\u54cd\uff0c\u4f7f\u5230\u75be\u65f6\uff0c\u4f46\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u94c1 \u62d0\u4ed9\u7684\u8eab\u5f71\uff0c\u4e00\u6839\u94c1\u62d0\u5c31\u5982\u540c\u5316\u4e86\u6570\u5341\u767e\u6839\uff0c\u62d0\u5f71\u5982\u5c71\uff0c\u628a\u96f7\u9707\u5b50\u7f69\u5728\u5f53\u4e2d\uff0c\u7aef\u7684\u662f\u98ce\u96e8\u4e0d \u900f\u3002\u4f46\u89c1\u96f7\u9707\u5b50\u6240\u65bd\u5251\u62db\uff0c\u5708\u5b50\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u5230\u4e86\u540e\u6765\uff0c\u5c31\u53ea\u89c1\u4e00\u56e2\u94f6\u5149\uff0c\u6709\u5982\u661f\u4e38\u8df3\u8dc3\uff0c\u8df3 \u8361\u4e0d\u4f11\uff0c\u4f46\u4ed6\u7684\u5251\u6cd5\u4e5f\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u867d\u7136\u5982\u6b64\uff0c\u94c1\u62d0\u4ed9\u5140\u662f\u4e0d\u80fd\u7a7f\u8fc7\u90a3\u56e2\u94f6\u5149\uff0c\u770b\u6765\u96f7\u9707 \u5b50\u867d\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u5374\u4ecd\u7136\u5b88\u5f97\u5341\u5206\u4e25\u5bc6\u3002 \u3000\u3000\u9648\u5929\u5b87\u65e0\u5fc3\u591a\u770b\uff0c\u805a\u62e2\u76ee\u5149\uff0c\u4ecd\u671d\u7740\u51b0\u5ddd\u5165\u6e56\u4e4b\u5904\u6ce8\u89c6\uff0c\u5ffd\u7136\u5f02\u58f0\u9aa4\u8d77\uff0c\u51b0\u6cb3\u4e0a\u6e38\u6709\u4e00 \u70b9\u9ed1\u70b9\u987a\u6d41\u800c\u4e0b\uff0c\u6e10\u89c1\u6269\u5927\uff0c\u539f\u662f\u4e00\u53f6\u5c0f\u821f\uff0c\u821f\u4e2d\u7acb\u7740\u4e09\u4eba\uff0c\u9762\u5bb9\u8fd8\u770b\u4e0d\u6e05\u695a\uff0c\u90a3\u4e00\u7fa4\u4eba\uff0c \u9664\u4e86\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u8fd8\u5728\u8dea\u7740\u819c\u62dc\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\u4e00\u9f50\u6b22\u547c\uff0c\u7eb7\u52ff\u6324\u5230\u51b0\u5ddd\u4eba\u53e3\u4e4b\u5904\u63a2\u671b\u3002 \u3000\u3000\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u51b0\u5ddd\u5929\u5973\u6765\u4e86\uff1f\u201d\u51dd\u795e\u770b\u65f6\uff0c\u4e4d\u89c1\u90a3\u4e00\u53f6\u8f7b\u821f\uff0c\u5728\u51b0\u6cb3 \u4e4b\u4e2d\u7f13\u7f13\u6d41\u4e0b\uff0c\u987b\u77e5\u90a3\u51b0\u6cb3\u662f\u4ece\u5c71\u9876\u5012\u6cfb\u4e0b\u6765\uff0c\u6c34\u52bf\u751a\u6025\uff0c\u800c\u4e14\u51b0\u6cb3\u4e4b\u4e0a\uff0c\u5230\u5904\u90fd\u662f\u51b0\u5757\uff0c \u51b0\u6cb3\u4e4b\u4e0b\uff0c\u53c8\u662f\u4e98\u53e4\u4e0d\u5316\u7684\u4e00\u5ea7\u5ea7\u5c0f\u5c71\u822c\u7684\u51b0\u5c42\uff0c\u83ab\u8bf4\u662f\u5c0f\u821f\uff0c\u5c31\u662f\u5927\u8239\uff0c\u78b0\u7740\u51b0\u5757\uff0c\u89e6\u7740 \u51b0\u5c42\uff0c\u4e5f\u4f1a\u88ab\u7838\u5f97\u7c89\u788e\uff1b\u90a3\u5c0f\u821f\u5374\u662f\u5947\u602a\u4e4b\u6781\uff0c\u5728\u6e4d\u6025\u7684\u51b0\u6cb3\u4e4b\u4e2d\u987a\u6d41\u800c\u4e0b\u3002\u7adf\u7136\u5982\u5728\u5e73\u9759 \u7684\u5c0f\u6cb3\u822a\u884c\u3001\u53c8\u5982\u6709\u65e0\u6570\u9690\u5f62\u7684\u529b\u58eb\u66ff\u5b83\u628a\u8235\u4e00\u6837\uff0c\u7adf\u7136\u5341\u5206\u5e73\u7a33\uff0c\u4e0d\u75be\u4e0d\u5f90\uff0c\u5728\u51b0\u5757\u6fc0 \u649e\u3001\u6c34\u6d41\u5486\u54ee\u4e4b\u4e2d\u7f13\u7f13\u6d41\u4e0b\uff0c\u5c0f\u821f\u5230\u5904\uff0c\u51b0\u5757\u5c31\u5411\u4e24\u8fb9\u6392\u5f00\uff0c\u7adf\u4f3c\u7ed9\u5b98\u8ba9\u8def\u4e00\u822c\u3002\u9648\u5929\u5b87\u6b66 \u529f\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u89c1\u6b64\u60c5\u5f62\uff0c\u4e5f\u77e5\u821f\u4e2d\u4e4b\u4eba\u5b9e\u5177\u6709\u4e0d\u53ef\u601d\u8bae\u7684\u672c\u9886\uff0c\u597d\u5947\u4e4b\u5fc3\uff0c\u8d8a\u53d1\u70bd\u76db\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u5c0f\u821f\u8d8a\u6765\u8d8a\u8fd1\uff0c\u821f\u4e2d\u4eba\u7684\u9762\u5bb9\u5df2\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u9648\u5929\u5b87\u4e00\u773c\u77a5\u53bb\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a \u5bd2\u6218\uff0c\u51b7\u610f\u76f4\u900f\u5fc3\u5934\u3002\u821f\u4e2d\u5171\u6709\u4e09\u4e2a\u5973\u5b50\uff0c\u5de6\u8fb9\u7684\u662f\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\u829d\u5a1c\uff0c\u5728\u5979\u4e00\u5411\u51b0\u51b7 \u7684\u9762\u5b54\u4e0a\uff0c\u7adf\u7136\u6302\u7740\u4e00\u6735\u7b11\u5bb9\uff0c\u5c31\u5982\u5728\u51b0\u8c37\u4e2d\u7efd\u5f00\u7684\u82b1\u6735\uff0c\u6b64\u4e8b\u5df2\u4ee4\u9648\u5929\u5b87\u5947\u602a\uff0c\u4f46\u4e5f\u8fd8\u4e0d \u89c9\u4ec0\u4e48\u3002\u53f3\u8fb9\u7684\u662f\u4e2a\u4e2d\u5e74\u5987\u4eba\uff0c\u989c\u5bb9\u7f8e\u8273\uff0c\u7acb\u5728\u821f\u4e2d\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8fd9\u4e5f\u6ca1\u6709\u4ec0\u4e48\u3002\u6700\u5947\u602a\u662f \u4e2d\u95f4\u90a3\u4e2a\u5973\u5b50\uff0c\u4f46\u89c1\u5979\u62ab\u7740\u4e00\u5934\u4e71\u53d1\uff0c\u5982\u68d8\u5982\u9488\uff0c\u4e00\u5f20\u9762\u5b54\uff0c\u82cd\u767d\u5f97\u6beb\u65e0\u534a\u70b9\u4eba\u8272\uff0c\u53cc\u624b\u4ea4 \u53c9\u80f8\u524d\uff0c\u5341\u6307\u6709\u5982\u9e21\u722a\uff0c\u4e4d\u773c\u671b\u53bb\uff0c\u5c31\u5982\u5728\u5e7d\u575f\u53e4\u5893\u4e4b\u4e2d\u8d70\u51fa\u6765\u7684\u50f5\u5c38\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\u3002\u90a3 \u4e9b\u4eba\u9aa4\u89c1\u602a\u76f8\u201c\u5475\u5440\u201d\u4e00\u58f0\u53eb\uff0c\u7eb7\u7eb7\u60ca\u8df3\u8d77\u6765\uff0c\u6709\u4e09\u4e24\u4e2a\u80c6\u5b50\u8f83\u5c0f\u800c\u53c8\u662f\u51c6\u5907\u5411\u51b0\u5ddd\u5929\u5973\u6c42 \u5a5a\u7684\u7adf\u7136\u5413\u5f97\u8499\u4e86\u9762\u5b54\uff0c\u8dcc\u8dcc\u649e\u649e\u7684\u6025\u5fd9\u98de\u8dd1\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u5954\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u9648\u5929\u5b87\u53c8\u60ca\u53c8\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u4e0d\u77e5\u662f\u5426\u5728\u8fd9\u5c0f\u821f\u4e4b\u5185\uff0c\u82e5\u7136\u5728\u8fd9\u5c0f\u821f\u4e4b\u5185\uff0c\u90a3\u4e48 \u82e5\u4e0d\u662f\u90a3\u4e2d\u5e74\u7f8e\u5987\uff0c\u5c31\u662f\u8fd9\u50f5\u5c38\u822c\u7684\u5973\u4eba\u4e86\u3002\u201c\u6b63\u81ea\u601d\u7591\uff0c\u5ffd\u542c\u5f97\u5e08\u7236\u4e5f\u60ca\u53eb\u4e86\u4e00\u58f0\uff0c\u56de\u9996 \u770b\u65f6\u3002\u53ea\u89c1\u5e08\u7236\u9762\u5982\u767d\u7eb8\uff0c\u624b\u811a\u98a4\u6296\uff0c\u7adf\u5982\u60a3\u4e86\u53d1\u51b7\u75c5\u4e00\u822c\uff0c\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u5e08\u7236\u6b64\u751f\uff0c\u7ecf \u8fc7\u65e0\u6570\u5927\u98ce\u5927\u6d6a\uff0c\u600e\u4e48\u6bd4\u6211\u8fd8\u8981\u80c6\u5c0f\uff1f\u201d\u4f46\u542c\u5f97\u5e08\u7236\u54fa\u54fa\u81ea\u8bed\u9053\uff1a\u201c\u5440\uff0c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u60f3\u4e0d \u5230\u7adf\u7136\u5728\u8fd9\u513f\u9047\u89c1\u4e86\u5979\uff1f\u771f\u662f\u51a4\u5b7d\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8bf4\u7684\u5979\u662f\u8c01\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a \u201c\u5ce8\u5d4b\u5973\u4fa0\uff0c\u593a\u547d\u4ed9\u5b50\u8c22\u4e91\u771f\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u662f\u4e2d\u95f4\u90a3\u4e2a\u5973\u4eba\u5417\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e0d\uff0c\u662f \u53f3\u8fb9\u90a3\u4f4d\u3002\u5979\u7684\u5bb9\u8c8c\u548c\u5341\u591a\u5e74\u524d\u8fd8\u4e00\u6a21\u4e00\u6837\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u53c8\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u96be\u9053\u4e2d\u95f4\u90a3\u4e2a\uff0c\u662f\u51b0\u5ddd\u5929\u5973\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u821f\u6765\u7684\u8fd1\u4e86\uff0c\u5ffd\u7136\u54ea\u4e2a\u5587\u561b\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u8c01\u662f\u51b0\u5ddd\u5929\u5973\uff1f\u201d\u98de\u8eab\u800c\u8d77\uff0c\u8dc3\u5165\u51b0 \u5ddd\u3002\u811a\u70b9\u6d6e\u51b0\uff0c\u5954\u5411\u5c0f\u8239\uff0c\u624b\u76f4\u5411\u8c22\u4e91\u9547\u6293\u53bb\u3002\u8fd9\u4e00\u624b\u767b\u840d\u5ea6\u6c34\u7684\u529f\u592b\uff0c\u771f\u662f\u8d85\u7fa4\u62d4\u7434\uff0c\u8427 \u9752\u5cf0\u8fd9\u65f6\uff0c\u76ee\u5149\u4e5f\u5168\u88ab\u90a3\u5c0f\u821f\u5438\u4f4f\uff0c\u89c1\u7ea2\u8863\u5587\u8d5a\u7684\u201c\u7075\u5c71\u638c\u201d\u75be\u5982\u98ce\u96f7\uff0c\u770b\u770b\u5c31\u8981\u6293\u5230\u8c22\u4e91 \u771f\u8eab\u4e0a\uff0c\u4e0d\u7981\u201c\u5475\u5440\u201d\u4e00\u58f0\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u53ea\u89c1\u8c22\u4e91\u771f\u51b7\u51b7\u4e00\u7b11\uff0c\u521a\u6b32\u51fa\u624b\uff0c\u4e2d\u95f4\u90a3\u4e2a\u5973\u5b50\uff0c\u5ffd\u7136\u624b\u6307\u4e00\u5f39\uff0c\u5feb\u6377\u5982\u7535\uff0c\u4e00\u5757\u6d6e\u51b0 \u6b63\u6b63\u5f39\u4e2d\u90a3\u7ea2\u8863\u5587\u561b\u7684\u5fc3\u7a9d\uff0c\u90a3\u5587\u561b\u60e8\u53eb\u4e00\u58f0\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u6251\u901a\u4e00\u58f0\uff0c\u4ece\u6d6e\u51b0\u4e0a\u8dcc\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u6c34\u6d41\u6e4d\u6025\uff0c\u4e00\u4e0b\u5b50\u5c31\u5377\u5230\u4e0b\u9762\uff0c\u60f3\u662f\u78b0\u7740\u4e0b\u9762\u7684\u51b0\u5c71\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8840\u6c34\u5c31\u5192\u4e86\u51fa\u6765\uff0c\u67d3 \u7ea2\u4e86\u51b0\u5ddd\u5165\u53e3\u4e4b\u5904\u7684\u7322\u9762\uff01\u6e56\u8fb9\u7fa4\u8c6a\u7eb7\u7eb7\u9a87\u53eb\uff01 \u3000\u3000\u8427\u9752\u5cf0\u66f4\u662f\u60ca\u9a87\u4e4b\u6781\uff0c\u987b\u77e5\u5b66\u201c\u7075\u5c71\u638c\u201d\u7684\u529f\u592b\uff0c\u5fc5\u7136\u8981\u5b66\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u4e4b\u7c7b\u7684\u5916\u529f\uff0c \u8eab\u8eaf\u603b\u80fd\u53d7\u5f97\u5343\u65a4\u538b\u529b\uff0c\u7ea2\u8863\u5587\u561b\u6240\u663e\u7684\u51e0\u624b\u529f\u592b\uff0c\u8db3\u89c1\u662f\u4e2a\u9ad8\u624b\uff0c\u7adf\u7136\u88ab\u4e00\u5757\u5c0f\u5c0f\u6d6e\u51b0\u4e00 \u5f39\u4e4b\u4e0b\uff0c\u4fbf\u4e27\u4e86\u6027\u547d\uff01 \u3000\u3000\u6e56\u8fb9\u7fa4\u8c6a\u672c\u6765\u5206\u6210\u4e09\u6279\uff0c\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e00\u6279\uff0c\u8fd9\u65f6\u6b63\u5728\u819c\u62dc\u3002\u6709\u4e9b\u4eba\u542c\u8bf4\u5929\u5973\u7684\u7f8e \u540d\uff0c\u60f3\u6765\u6c42\u5a5a\u987a\u4fbf\u60f3\u5e7c\u8d70\u829d\u5a1c\u7684\uff0c\u89c1\u90a3\u5973\u5b50\u51fa\u624b\u5982\u6b64\u5389\u5bb3\u3001\u90fd\u5413\u5f97\u614c\u4e86\u3002\u6709\u7684\u7259\u9f7f\u6253\u6218\uff0c\u624b \u9178\u811a\u8f6f\uff0c\u5413\u5f97\u4e0d\u80fd\u8d70\u52a8\uff0c\u6709\u7684\u8f83\u4e3a\u80c6\u5927\uff0c\u8fd8\u60f3\u7fa4\u6bb4\uff0c\u6709\u7684\u5219\u8f6c\u8fc7\u8eab\u6765\u3001\u4fbf\u60f3\u9003\u8d70\u3002\u53ea\u89c1\u90a3\u85cf \u65cf\u5c11\u5973\u4f38\u4e4e\u6307\u4e86\u4e24\u6307\uff0c\u9053\uff1a\u201c\u4e00\u8981\u6349\u6211\u7684\u662f\u8fd9\u4e24\u4e2a\u4eba\u3002\u201d\u5750\u5728\u5c0f\u821f\u4e2d\u95f4\u90a3\u9762\u65e0\u8840\u8272\u3001\u5f62\u4f3c\u50f5 \u5c38\u7684\u5973\u5b50\u5934\u4e5f\u4e0d\u62ac\uff0c\u968f\u624b\u5728\u6e56\u4e2d\u62fe\u8d77\u6d6e\u51b0\uff0c\u987a\u624b\u5f39\u51fa\u3001\u90a3\u4e24\u4e2a\u85cf\u4eba\u521a\u8d70\u51fa\u4e09\u6b65\uff0c\u5c31\u7ed9\u51b0\u5757\u5f39 \u4e2d\uff0c\u767b\u65f6\u53e3\u5410\u9c9c\u8840\uff0c\u6655\u6b7b\u5730\u4e0a\u3002\u8c22\u4e91\u771f\u9053\uff1a\u201c\u4e00\u8fd9\u4e9b\u4eba\u90fd\u4e0d\u662f\u597d\u4e1c\u897f\u3002\u201d\u90a3\u5973\u5b50\u53cc\u624b\u8fde\u5f39\uff0c \u6d6e\u51b0\u4e0d\u5f80\u7684\u5982\u5f39\u4e38\u98de\u4f1a\uff1b\u7247\u523b\u4e4b\u95f4\uff0c\u9664\u4e86\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e4b\u5916\uff0c\u5168\u90fd\u7ed9\u6d6e\u51b0\u6253\u4e2d\uff0c\u5176\u4e2d\u53ea \u6709\u4e24\u4e2a\u6b66\u529f\u6700\u9ad8\u7684\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u8fd8\u80fd\u9003\u8dd1\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u5168\u90fd\u7ed9\u51b0\u5757\u6253\u6b7b\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u60ca\u5fc3\u9a87\u76ee\uff0c\u4e0d\u4f46\u662f\u8427\u9752\u5cf0\u5e08\u5f92\u79fb\u76ee\u6ce8\u89c6\uff0c\u573a\u4e2d\u7684\u94c1\u62d0\u4ed9\u4e0e\u96f7\u9707\u5b50\u542c\u5f97\u58f0\u58f0\u5389\u53eb\uff0c \u4e5f\u4e0d\u89c9\u7684\u7f13\u4e86\u4e0b\u6765\u3002\u659c\u4e14\u7aa5\u89c6\uff0c\u4f46\u94c1\u62d0\u4ed9\u7684\u94c1\u62d0\u4ecd\u7136\u5c01\u95ed\u4e86\u96f7\u9707\u5b50\u8131\u8eab\u7684\u95e8\u6237\uff0c\u52bf\u9053\u867d\u662f\u7f13 \u548c\uff0c\u5371\u673a\u4ecd\u7136\u672a\u51cf\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u5973\u5b50\u820d\u821f\u767b\u9646\uff0c\u7f13\u7f13\u7684\u8d70\u4e0a\u5cb8\u6765\uff0c\u8427\u9752\u5cf0\u7684\u773c\u5149\u4e0e\u8c22\u4e91\u771f\u7684\u63a5\u89e6\uff0c\u53ea\u89c1\u5979\u4f3c\u7b11\u975e \u7b11\u7684\u770b\u7740\u81ea\u5df1\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u7231\u6068\u4ea4\u5e76\uff0c\u8427\u9752\u5cf0\u60f3\u51fa\u58f0\u62db\u547c\u3002\u5589\u5934\u560e\u54bd\uff0c\u7adf\u7136\u53eb\u4e0d\u51fa\u6765\u3002\u8c22\u4e91 \u771f\u5374\u6de1\u6de1\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u508d\u7740\u90a3\u4e2a\u5973\u5b50\uff0c\u76f4\u5411\u6597\u573a\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u5973\u5b50\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5168\u65e0\u8840\u8272\u7684\u9762\u5b54\u8d8a\u770b\u8d8a\u662f\u53ef\u6015\u3001\u9648\u5929\u5b87\u5413\u5f97\u6296\u6296\u7d22\u7d22\uff0c\u5ffd\u542c\u5f97\u8c22\u4e91\u771f \u7b11\u9053\uff1a\u201c\u8001\u4f34\u513f\uff0c\u51b0\u5ddd\u5929\u5973\u6765\u5566\uff0c\u4f60\u8fd8\u597d\u610f\u601d\u6b3a\u8d1f\u5979\u7684\u5c0f\u8f88\u5417\uff1f\u5feb\u5feb\u6536\u8d77\u4f60\u7684\u6253\u72d7\u62d0\u6756 \u5427\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u8427\u9752\u5cf0\u548c\u9648\u5929\u5b87\u90fd\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\u3002\u8427\u9752\u5cf0\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u5982\u6b64\u7f8e\u8c8c\u7684\u8c22\u4e91\u771f \u7adf\u7136\u505a\u4e86\u4e11\u4e5e\u4e10\u94c1\u62d0\u4ed9\u7684\u59bb\u5b50\uff0c\u9648\u5929\u5b87\u4e5f\u662f\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u4ed6\u5fc3\u76ee\u4e2d\u4ee5\u4e3a\u5b9a\u662f\u7f8e\u8c8c\u5c11\u5973\u7684\u51b0 \u5ddd\u5929\u5973\u7adf\u7136\u662f\u5982\u6b64\u53ef\u6015\u7684\u5973\u50f5\u5c38\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u85cf\u65cf\u5c11\u5973\u4e5f\u662f\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\uff0c\u90a3\u5c0f\u4f19\u5b50\u662f\u4e2a\u597d\u4eba\uff0c\u59d0\u59d0\uff0c\u4f60\u4e0d\u8981\u5413\u574f \u4e86\u4ed6\u3002\u201d\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u628a\u624b\u4e00\u62e8\u3002\u5c06\u90a3\u4e71\u8349\u822c\u7684\u5934\u53d1\u62e8\u843d\u5730\u4e0a\uff0c\u539f\u6765\u4e43\u662f\u5047\u53d1\u3002\u53c8\u564e\u7684\u4e00 \u58f0\uff0c\u6495\u5f00\u7f69\u8863\uff1b\u518d\u53cc\u624b\u4e00\u6296\uff0c\u6296\u843d\u4e24\u53ea\u624b\u5957\uff0c\u7136\u540e\u53c8\u62c9\u4e0b\u4e86\u9762\u5177\uff0c\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u7684\u773c\u775b\u90fd \u5b9a\u4f4f\u4e86\uff01 \u3000\u3000\u53ea\u89c1\u90a3\u5973\u5b50\u4e00\u8eab\u6e56\u6c34\u8272\u7684\u8863\u88f3\uff0c\u8138\u5982\u65b0\u6708\uff0c\u6d45\u753b\u53cc\u7709\u3002\u773c\u73e0\u5fae\u78a7\uff0c\u6a31\u6843\u5c0f\u53e3\uff0c\u4f3c\u559c\u8fd8 \u98a6\uff0c\u79c0\u53d1\u5782\u80a9\uff0c\u68b3\u6210\u4e24\u6761\u8fab\u5b50\uff0c\u675f\u4f3c\u7ea2\u7eeb\uff0c\u80a4\u8272\u6709\u5982\u7f8a\u8102\u767d\u7389\uff0c\u6620\u96ea\u751f\u8f89\uff0c\u7aef\u7684\u662f\u7edd\u4e16\u5bb9 \u989c\uff0c\u521a\u5065\u5a40\u5a1c\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u8d5b\u4f3c\u753b\u9601\u4ed9\u5973\uff0c\u6bd4\u9648\u5929\u5b87\u5fc3\u76ee\u4e2d\u6240\u60f3\u8c61\u7684\u8fd8\u8981\u7f8e\u4e3d\u5f97\u591a\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u65e9\u6536\u8d77\u94c1\u62d0\uff0c\u8df3\u8fc7\u4e00\u8fb9\u3002\u5782\u624b\u7acb\u5728\u8c22\u4e91\u771f\u7684\u53f3\u4fa7\uff0c\u96f7\u9707\u5b50\u4e5f\u6a2a\u5251\u5f53\u80f8\uff0c\u663e\u5f97\u751a\u662f \u8be7\u5f02\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u79c0\u7709\u4e00\u8e59\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96f7\u9707\u5b50\uff0c\u4f60\u653e\u4e0b\u5251\u6765\u7ed9\u8427\u5148\u751f\u53e9\u4e09\u4e2a\u54cd\u5934\uff0c\u4e0b\u5c71\u53bb \u5427\u3002\u201d\u8fd9\u8bed\u6c14\u5c31\u5982\u540c\u5429\u5490\u5c0f\u8f88\u4e00\u822c\u3002\u96f7\u9707\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u6012\u6781\u53cd\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u51ed\u4ec0\u4e48 \u8981\u6211\u5411\u4ed6\u78d5\u5934\uff1f\u201d\u987b\u77e5\u96f7\u9707\u5b50\u662f\u5f53\u4eca\u6b66\u5f53\u6d3e\u7684\u7b2c\u4e8c\u4ee3\u9ad8\u624b\u3001\u5e74\u7eaa\u56db\u5341\u6709\u591a\uff0c\u51b0\u5ddd\u5929\u5973\u770b\u8d77\u6765 \u6700\u591a\u4e0d\u8fc7\u4e8c\u5341\u5c81\u5de6\u53f3\uff0c\u66f4\u517c\u96f7\u9707\u5b50\u5728\u6c5f\u6e56\u4e0a\u4e45\u8d1f\u76db\u540d\uff0c\u5fc3\u9ad8\u6c14\u505a\u3001\u4f60\u53eb\u4ed6\u5982\u4f55\u80af\u5728\u4e00\u4e2a\u5c11\u5973 \u9762\u524d\u4f4e\u5934\u4fef\u9996\uff1f \u3000\u3000\u5374\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6b66\u5f53\u6d3e\u7684\u7b2c\u5341\u4e8c\u6761\u6212\u5f8b\u662f\u4ec0\u4e48\uff1f\u201d\u90a3\u6761\u6212\u5f8b\u662f\uff1a \u201c\u660e\u8fa8\u662f\u975e\uff0c\u9047\u4e8b\u5f53\u5148\u95ee\u81ea\u5df1\u53ef\u6709\u4e0d\u662f\uff1f\u4e0d\u53ef\u4ee5\u52bf\u51cc\u4eba\u3002\u201d\u96f7\u9707\u5b50\u4e0d\u7531\u5f97\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u9053\uff1a \u201c\u8fd9\u8fb9\u8352\u50fb\u5730\u3001\u72ec\u5904\u5cf0\u4e0a\u7684\u5c11\u5973\uff0c\u5982\u4f55\u4f1a\u77e5\u9053\u672c\u95e8\u6212\u5f8b\uff1f\u201d\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u53c8\u9053\uff1a\u201c\u4f60\u7684\u4e8b \u60c5\u6211\u90fd\u77e5\u9053\u4e86\uff0c\u8fd9\u4e8b\u8d77\u56e0\u786e\u662f\u4f60\u7684\u4e0d\u5bf9\uff0c\u59d1\u5ff5\u4f60\u5fc3\u672f\u867d\u7136\u4e0d\u6b63\uff0c\u4f46\u5c1a\u975e\u7f6a\u5927\u6076\u6781\uff0c\u800c\u4e14\u5176\u4e2d \u53c8\u6709\u5978\u4eba\u6311\u5f04\uff0c\u4e0d\u80fd\u5b8c\u5168\u602a\u7f6a\u4e8e\u4f60\uff0c\u6240\u4ee5\u9976\u4f60\u4e0d\u6b7b\uff0c\u4f60\u8fd8\u4e0d\u5feb\u53bb\u5411\u8427\u5148\u751f\u966a\u7f6a\u4e48\uff1f\u201d \u3000\u3000\u96f7\u9707\u5b50\u72ec\u773c\u5706\u7741\uff0c\u6012\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6211\u7684\u672c\u95e8\u957f\u8f88\uff0c\u4e5f\u7ba1\u4e0d\u5230\u6211\uff01\u6211\u4e3a\u4ec0\u4e48\u8981\u542c\u4f60\u8fd9\u9ec4\u6bdb \u4e2b\u5934\u7684\u8bf4\u8bdd\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\u7684\u5f1f\u5b50\uff1f\u8fd9\u4e48\u5f3a\u5634\uff01\u201d\u96f7\u9707\u5b50\u6a2a\u5251\u6012 \u89c6\uff0c\u95ed\u53e3\u4e0d\u7b54\uff0c\u94c1\u62d0\u4ed9\u5728\u65c1\u4ee3\u7b54\u9053\uff1a\u201c\u4ed6\u662f\u5f53\u4eca\u6b66\u5f53\u6d3e\u638c\u95e8\u95f2\u4e91\u9053\u4eba\u7684\u5f1f\u5b50\u3002\u201d\u95f2\u4e91\u9053\u4eba\u662f \u5192\u5ddd\u751f\u7684\u5e08\u4f84\uff0c\u867d\u4e3a\u638c\u95e8\uff0c\u7d20\u6027\u95f2\u6563\uff0c\u4e0d\u5927\u7231\u7406\u95e8\u4eba\u4e4b\u4e8b\uff0c\u6545\u6b64\u4ee4\u5230\u96f7\u9707\u5b50\u65e5\u6e10\u9a84\u6a2a\uff0c\u96be\u4ee5 \u5236\u6b62\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u6211\u4e45\u95fb\u6b66\u5f53\u6d3e\u6212\u5f8b\u8c28\u4e25\uff0c\u7d20\u91cd\u5c0a\u5351\u4e4b\u522b\uff0c\u96be\u9053\u5982\u4eca\u8fd9\u98ce\u6c14 \u7adf\u7136\u66f4\u6539\u4e86\u4e48\uff1f\u539f\u6765\u4f60\u672c\u95e8\u7684\u957f\u8f88\u4e5f\u7ba1\u4e0d\u4e86\u4f60\uff01\u53ef\u662f\u4f60\u672c\u95e8\u7684\u957f\u8f88\u7ba1\u4e0d\u4e86\u4f60\uff0c\u6211\u5374\u504f\u8981\u66ff\u4ed6 \u4eec\u7ba1\u4e00\u7ba1\u4f60\uff01\u201d \u3000\u3000\u96f7\u9707\u5b50\u6c14\u5f80\u4e0a\u51b2\uff0c\u4e0d\u53ef\u590d\u5fcd\u3002\u6a2a\u8dc3\u4e09\u6b65\uff0c\u957f\u5251\u4e00\u6325\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u5c31\u6765\u7ba1\u5427\uff01\u4ffa\u96f7\u9707 \u5b50\u5728\u8fd9\u91cc\u9886\u6559\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u8981\u4e0e\u6211\u6bd4\u5251\u3002\u201d\u5979\u53cc\u624b\u7a7a\u7a7a\uff0c\u968f\u8eab\u4ea6 \u65e0\u5175\u5203\uff0c\u8c22\u4e91\u771f\u62d4\u51fa\u4f69\u5251\uff0c\u60f3\u629b\u7ed9\u5979\uff0c\u53ea\u89c1\u5979\u6446\u4e86\u6446\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\uff01\u201d\u968f\u624b\u5728\u6e56\u8fb9\u62fe\u8d77 \u4e00\u5757\u6d6e\u51b0\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5757\u5f62\u5982\u957f\u68d2\u7684\u51b0\u5757\uff0c\u51b0\u5ddd\u5929\u5973\u62fe\u4e86\u8d77\u6765\uff0c\u55d6\u7684\u4e00\u638c\u524a\u4e0b\uff0c\u524a\u4e86\u51e0\u524a\uff0c\u524a\u5f97\u90a3\u5757\u957f \u5f62\u51b0\u5757\uff0c\u5f62\u5982\u4e00\u652f\u5229\u5251\u3002\u51b0\u5757\u867d\u7136\u5e76\u4e0d\u662f\u4ec0\u4e48\u575a\u786c\u7684\u4e1c\u897f\uff0c\u4f46\u8fd9\u6837\u968f\u5fc3\u6240\u6b32\uff0c\u968f\u624b\u524a\u6765\uff0c\u5374 \u4e5f\u5b9e\u662f\u9a87\u4eba\u542c\u95fb\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u201c\u51b0\u5251\u201d\u4e00\u626c\uff0c\u9053\uff1a\u201c\u96f7\u9707\u5b50\uff0c\u4f60\u82e5\u80fd\u5728\u5341\u62db\u4e4b\u5185\uff0c\u4e0e\u6211\u6253\u6210\u5e73 \u624b\uff0c\u6211\u5c31\u628a\u8427\u5148\u751f\u4efb\u4f60\u5904\u7f6e\u3002\u201d\u5176\u65f6\u6b63\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u65e5\u5149\u76f4\u5c04\u4e0b\u6765\uff0c\u5c31\u662f\u51b0\u5ddd\u91cc\u7684\u6d6e\u51b0\uff0c\u4e5f \u5728\u9010\u6e10\u878d\u5316\uff0c\u66f4\u4f55\u51b5\u662f\u63e1\u5728\u624b\u4e2d\uff0c\u53d7\u4eba\u4f53\u70ed\u529b\u6240\u84b8\u53d1\u7684\u51b0\u5757\uff1f\u8427\u9752\u5cf0\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5c31 \u7b97\u96f7\u9707\u5b50\u524a\u5b83\u4e0d\u65ad\uff0c\u5b83\u4e5f\u8fc7\u4e0d\u4e86\u534a\u4e2a\u65f6\u5206\uff0c\u5c31\u8981\u5316\u4e3a\u51b0\u6c34\uff01\u51b0\u5ddd\u5929\u5973\u8fd9\u5c82\u4e0d\u662f\u62ff\u6211\u7684\u6027\u547d\u5f00 \u73a9\u7b11\u5417\uff1f\u201d\u53ea\u542c\u5f97\u96f7\u9707\u5b50\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u53ef\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u6211\u82e5\u5728\u5341\u62db\u4e4b\u5185\uff0c\u4e0d\u80fd\u5c06\u4f60\u7684 \u51b0\u5251\u524a\u65ad\uff0c\u6211\u5c31\u5411\u4f60\u53e9\u5934\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u662f\u8981\u4f60\u5411\u8427\u5148\u751f\u53e9\u5934\u3002\u201d\u96f7\u9707\u5b50\u9053\uff1a\u201c\u4e0d\u5fc5 \u591a\u8a00\uff0c\u4e00\u5207\u4f9d\u4f60\u4fbf\u662f\uff0c\u770b\u5251\uff01\u201d\u5530\u7684\u4e00\u5251\uff0c\u7acb\u523b\u6a2a\u524a\u8fc7\u53bb\u3002\u94c1\u62d0\u4ed9\u5728\u65c1\u9ad8\u58f0\u6570\u9053\uff1a\u201c\u7b2c\u4e00 \u62db\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5251\u5feb\u6377\u4e4b\u6781\uff0c\u66f4\u52a0\u4e0a\u96f7\u9707\u5b50\u6f5c\u4fee\u4e86\u5341\u591a\u5e74\u7684\u5185\u529f\uff0c\u4f11\u8bf4\u662f\u51b0\uff0c\u5c31\u662f\u94a2\u5200\u94c1\u5251\uff0c\u7ed9\u4ed6 \u622a\u7740\uff0c\u53ea\u6015\u4e5f\u8981\u88ab\u524a\u4e3a\u4e24\u6bb5\u3002\u4f46\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u58f0\uff1a\u201c\u597d\uff01\u201d\u51b0\u5251\u4e00\u6307\uff0c\u7adf\u7136\u662f\u4ece\u4ed6 \u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\uff0c\u6307\u5230\u4e86\u4ed6\u80f8\u53e3\u7684\u201c\u7384\u673a\u7a74\u201d\uff0c\u8fd9\u4e43\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u96f7\u9707\u5b50\u5927\u5403\u4e00 \u60ca\uff0c\u6025\u5fd9\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\uff0c\u786c\u751f\u5723\u5730\u5c06\u8eab\u5f62\u626d\u66f2\u3002\u5c06\u653b\u51fa\u53bb\u7684\u52b2\u529b\u4e5f\u6536\u4e86\u56de\u6765\uff0c\u6a2a \u5251\u56de\u524a\uff0c\u8d39\u4e86\u6781\u5927\u7684\u529b\u6c14\uff0c\u624d\u5c06\u51b0\u5ddd\u5929\u5973\u8fd9\u4e00\u72e0\u62db\u5316\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u5374\u662f\u597d\u6574\u4ee5\u6687\uff0c\u5fae\u5fae\u4e00 \u7b11\uff0c\u4e00\u63a0\u800c\u8fc7\uff0c\u5c06\u51b0\u5251\u53c8\u6536\u4e86\u56de\u6765\u3002 \u3000\u3000\u96f7\u9707\u5b50\u91cd\u6574\u95e8\u6237\uff0c\u957f\u5251\u6a2a\u80f8\u4e00\u7acb\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4ee5\u4e00\u638c\u62a4\u80f8\uff0c\u4e00\u5251\u8fce\u654c\uff0c\u4e14\u6740\u4f60\u4e2a\u63aa\u624b\u4e0d \u53ca\uff0c\u53ea\u8981\u4f60\u7684\u51b0\u5251\u7ed9\u6211\u7684\u52b2\u529b\u5fae\u4e00\u6cbe\u4e0a\uff0c\u5c31\u5316\u4e3a\u51b0\u6c34\uff0c\u770b\u4f60\u5982\u4f55\u9632\u5907\u3002\u201d\u4e3b\u610f\u6253\u5b9a\u3001\u653b\u52bf\u7a81 \u53d1\uff0c\u4e00\u8fde\u4e09\u5251\uff0c\u8fd9\u662f\u6b66\u5f53\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u5b9e\u662f\u5341\u5206\u96be\u4ee5\u62b5\u654c\u3002\u53ea\u542c\u51b0\u5ddd\u5929 \u5973\u7b11\u9053\uff1a\u201c\u4f60\u7684\u672c\u95e8\u5251\u6cd5\u8fd8\u5dee\u5f97\u8fdc\u54ea\uff01\u201d\u4f46\u89c1\u5979\u8eab\u5f62\u8d77\u5904\uff0c\u8863\u8882\u8f7b\u98d8\u3001\u970e\u773c\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u4e09 \u5251\uff0c\u6bcf\u4e00\u5251\u90fd\u662f\u4e2d\u9014\u53d8\u62db\uff0c\u5947\u8be1\u4e4b\u6781\uff0c\u96f7\u9707\u5b50\u8fde\u5979\u7684\u8863\u88f3\u4e5f\u6cbe\u4e0d\u7740\uff0c\u53ea\u89c9\u5979\u7684\u51b0\u5251\u5bd2\u5149\u95ea \u95ea\uff0c\u5728\u81ea\u5df1\u7684\u9762\u95e8\u95ea\u6765\u95ea\u53bb\uff0c\u8000\u773c\u6b32\u82b1\uff0c\u88ab\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u53ea\u542c\u5f97\u94c1\u62d0\u4ed9\u5df2\u6570\u5230\u7b2c\u56db\u62db\u4e86\u3002 \u3000\u3000\u96f7\u9707\u5b50\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u602a\u5f02\u7edd\u4f26\uff0c\u7adf\u7136\u662f\u672c\u6d3e\u7684\u8fbe\u6469\u5251\u6cd5\uff01\u8fd9\u8fbe\u6469\u5251 \u6cd5\u5728\u5143\u4ee3\u4e2d\u53f6\u5931\u4f20\uff0c\u76f4\u81f3\u5eb7\u7199\u5e74\u95f4\uff0c\u624d\u7531\u8f9b\u9f99\u5b50\u590d\u5f97\uff0c\u4f20\u81f3\u6842\u4ef2\u660e\u3002\u4f46\u56e0\u8fbe\u6469\u5251\u6cd5\u7e41\u590d\u602a\u5f02 \u4e4b\u6781\uff0c\u5728\u6b66\u5f53\u6d3e\u590d\u4f20\u7684\u65f6\u65e5\u5c1a\u6d45\uff0c\u6570\u5341\u5e74\u6765\uff0c\u540e\u8f88\u5f1f\u5b50\u80fd\u7cbe\u901a\u8fbe\u6469\u5251\u6cd5\u7684\u5b9e\u5728\u8fd8\u6ca1\u6709\u51e0\u4eba\uff0c \u96f7\u9707\u5b50\u662f\u6b66\u5f53\u5357\u652f\u7684\u5f1f\u5b50\uff0c\u6b66\u5f53\u5357\u5317\u4e8c\u6d3e\u7684\u5251\u672f\uff0c\u540e\u6765\u867d\u7136\u4ea4\u6d41\uff0c\u4f46\u5357\u652f\u8fd8\u662f\u7565\u900a\u3002 \u3000\u3000\u201c\u8fd9\u4e2b\u5934\u83ab\u975e\u771f\u662f\u672c\u95e8\u957f\u8f88\uff1f\u201d\u9661\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u66f4\u662f\u5fc3\u614c\uff0c\u6b63\u6b32\u51fa\u58f0\u8be2\u95ee\uff0c\u659c\u773c\u4e00\u77a5\uff0c \u5ffd\u89c1\u94c1\u62d0\u4ed9\u5634\u89d2\u6302\u7740\u51b7\u7b11\uff0c\u6b6a\u7740\u773c\u775b\u5728\u770b\u7740\u81ea\u5df1\uff0c\u7981\u4e0d\u4f4f\u5fc3\u5934\u706b\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u662f\u62fc \u4e86\u6027\u547d\uff0c\u4e5f\u4e0d\u8ba4\u8f93\uff01\u201d\u770b\u51b0\u5ddd\u5929\u5973\u65f6\uff0c\u53ea\u89c1\u5979\u4ecd\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u5251\u5c16\u659c\u6307\u7740\u81ea\u5df1\uff0c\u5e76\u4e0d\u62a2\u5148\u51fa \u62db\uff0c\u5206\u660e\u662f\u4e00\u6d3e\u957f\u8f88\u5bf9\u5c0f\u8f88\u7684\u795e\u6c14\u3002 \u3000\u3000\u8fd9\u6837\u7684\u7f13\u4e86\u4e00\u7f13\uff0c\u51b0\u5ddd\u5929\u5973\u624b\u4e2d\u7684\u51b0\u5251\u5df2\u6e10\u6e10\u878d\u5316\uff0c\u51b0\u6c34\u4e00\u6ef4\u4e00\u6ef4\u7684\u6d12\u4e0b\u5730\u6765\uff0c\u51b0\u5251\u53d8 \u5f97\u66f4\u8584\u66f4\u900f\u660e\u4e86\uff0c\u96f7\u9707\u5b50\u7a81\u7136\u60f3\u51fa\u4e86\u4e00\u4e2a\u6b79\u6bd2\u7684\u4e3b\u610f\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u80af\u51fa\u62db\uff0c\u6211\u5c31\u548c\u4f60\u5bf9\u8017\uff0c \u53ea\u8981\u4f60\u7684\u51b0\u5251\u878d\u5316\uff0c\u6211\u5c31\u662f\u4e0d\u6218\u800c\u80dc\uff01\u201d\u4ed6\u4eec\u6709\u8bdd\u5728\u5148\uff0c\u8bf4\u660e\u662f\u6bd4\u5251\u6cd5\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u51b0\u5251\u82e5 \u771f\u7684\u662f\u5316\u4e3a\u4e4c\u6709\uff0c\u90a3\u53ef\u4e0d\u80fd\u8bf4\u96f7\u9707\u5b50\u72e1\u733e\u53d6\u5de7\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u9762\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u96f7\u9707\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u96f7\u9707\u5b50\u4e0d\u7406\u4e0d\u776c\uff0c\u6309\u5251\u51dd\u89c6\uff0c\u52a8\u4e5f\u4e0d \u52a8\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u6837\u7684\u5fc3\u672f\uff0c\u6211\u5c31\u5e94\u66ff\u95f2\u4e91\u9053\u957f\u6559\u8bad\u4f60\u5566\uff01\u201d \u7ea4\u6307\u8f7b\u8f7b\u4e00\u5f39\uff0c\u51b0\u6c34\u98de\u6e85\uff0c\u96f7\u9707\u5b50\u9661\u89c9\u773c\u775b\u4e00\u82b1\uff0c\u767d\u6aac\u6d5a\u7684\u6c34\u6c14\u906e\u7740\u773c\u775b\uff0c\u6709\u51e0\u6ef4\u51b0\u6c34\u5df2\u6d12 \u5230\u9762\u4e0a\uff0c\u5947\u5bd2\u5f7b\u9aa8\uff0c\u819d\u9647\u4e2d\u53ea\u9053\u51b0\u5ddd\u5929\u5973\u7a81\u51fa\u602a\u62db\uff0c\u4e0d\u81ea\u89c9\u7684\u4e00\u5251\u64a9\u53bb\uff0c\u8fd9\u4e5f\u662f\u5b66\u6b66\u4e4b\u4eba\uff0c \u9632\u8eab\u653b\u654c\u5df2\u6210\u4e60\u6027\uff0c\u6240\u4ee5\u4e00\u6709\u98ce\u5439\u8349\u52a8\uff0c\u51fa\u62db\u81ea\u662f\u5fc5\u7136\u3002 \u3000\u3000\u4e00\u5251\u65e2\u51fa\uff0c\u5ffd\u89c9\u4e2d\u8ba1\uff0c\u518d\u8981\u64a4\u56de\uff0c\u5df2\u662f\u4e0d\u53ca\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51b0\u5ddd\u5929\u5973\u5251\u5c16\u5df2\u81f3\u54bd \u5589\uff0c\u8fd9\u6b63\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u7b11\u9053\uff1a\u201c\u672c\u60f3\u770b\u4f60\u5341\u62db\uff0c\u770b\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u53ea\u56e0\u4f60\u5fc3\u672f\u4e0d\u6b63\uff0c\u53ea\u597d\u51cf\u534a\u3002\u4f60 \u670d\u8f93\u5417\uff1f\u4ee5\u540e\u770b\u4f60\u6562\u4e0d\u6562\u5bf9\u957f\u8f88\u65e0\u7406\uff1f\u201d\u96f7\u9707\u5b50\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u662f\u6842\u5e08\u53d4\u7956\u7684\u5973\u513f\uff1f\u201d\u51b0\u5ddd\u5929 \u5973\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u5566\uff01\u201d \u3000\u3000\u96f7\u9707\u5b50\u8bf4\u7684\u5373\u662f\u6842\u534e\u751f\uff0c\u4ed6\u5251\u672f\u6700\u7cbe\u3002\u96f7\u9707\u5b50\u542c\u957f\u8f88\u8bf4\u8fc7\u6842\u534e\u751f\u8d1f\u6c14\u8fdc\u8d70\u8fb9\u7586\uff0c\u4e00\u53bb\u4e0d \u77e5\u6240\u7ec8\u4e4b\u4e8b\uff0c\u4f46\u5374\u4e07\u4e07\u6599\u4e0d\u5230\u4ed6\u4f1a\u6709\u4e00\u4e2a\u5973\u513f\u5f80\u5728\u5929\u6e56\u4e4b\u4e0a\u3002 \u3000\u3000\u96f7\u9707\u5b50\u957f\u53f9\u4e00\u58f0\uff0c\u63b7\u5251\u4e8e\u5730\uff0c\u5411\u51b0\u5ddd\u5929\u5973\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u7684\u51b0\u5251\u5df2\u878d\u5316\u6b86 \u5c3d\uff0c\u53ea\u5269\u4e0b\u8584\u8584\u7684\u4e00\u7247\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u201c\u51b0\u5251\uff0c\u5728\u624b\u5fc3\u4e00\u6413\uff0c\u987f\u65f6\u5316\u4e3a\u4e4c\u6709\uff0c\u5ffd \u800c\u9762\u4e0a\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u53bb\u5411\u8427\u5148\u751f\u966a\u793c\u4e48\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u503e\u5c3d\u5929\u6e56\u6c34\uff0c\u96be\u6d88\u4eca\u65e5\u7f9e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u6d41\u6c34\u843d\u82b1\u3000\u6df1\u4ec7\u4f24\u5bc2\u5bde\u3000\u73e0\u5bab\u8d1d\u9619\u3000\u5f80\u4e8b\u8bc9\u8f9b\u9178 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u4e94 \u56de \u6d41\u6c34\u843d\u82b1\u3000\u6df1\u4ec7\u4f24\u5bc2\u5bde \u73e0\u5bab\u8d1d\u9619\u3000\u5f80\u4e8b\u8bc9\u8f9b\u9178 \u3000\u3000\u96f7\u9707\u5b50\u9762\u8272\u94c1\u9752\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u8dd1\u4e0a\u53bb\u5bf9\u8427\u9752\u5cf0\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u5ffd\u7136\u4e00\u5f2f\u8170\uff0c\u5c31\u624b\u6293\u8d77\u4e86 \u5730\u4e0a\u7684\u957f\u5251\uff0c\u53cd\u5251\u5411\u54bd\u5589\u4fbf\u5272\uff0c\u987b\u77e5\u96f7\u9707\u5b50\u5728\u60c5\u573a\u5931\u610f\u4e4b\u540e\uff0c\u53c8\u60e8\u88ab\u610f\u4e2d\u4eba\u8fa3\u624b\u6bc1\u5bb9\uff0c\u5929\u4e0b \u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u6bd4\u8fd9\u4e2a\u66f4\u4ee4\u4eba\u5fc3\u4f24\uff1a\u662f\u4ee5\u4ed6\u56e0\u7231\u6210\u4ec7\uff0c\u9664\u4e86\u6068\u5ce8\u5d4b\u5973\u4fa0\u8c22\u4e91\u771f\u4e4b\u5916\uff0c\u66f4\u8fc1\u6012\u5230 \u8427\u9752\u5cf0\u8eab\u4e0a\u3002\u5c82\u77e5\u542b\u6068\u534a\u751f\uff0c\u51fa\u624b\u62a5\u4ec7\uff0c\u7adf\u51fa\u5176\u610f\u5916\u7684\u9047\u5230\u4e86\u51b0\u5ddd\u5929\u5973\uff0c\u62db\u6765\u4e86\u5982\u65af\u7f9e\u8fb1\uff0c \u6545\u6b64\u4ed6\u628a\u5fc3\u4e00\u6a2a\uff0c\u4fbf\u60f3\u81ea\u520e\u3002 \u3000\u3000\u8427\u9752\u5cf0\u5931\u58f0\u60ca\u547c\uff0c\u96f7\u9707\u5b50\u52a8\u4f5c\u592a\u5feb\uff0c\u963b\u5df2\u4e0d\u53ca\uff0c\u5ffd\u542c\u5f97\u201c\u5484\u201d\u7684\u4e00\u58f0\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u96f7\u9707 \u5b50\u7684\u957f\u5251\u8131\u624b\u98de\u53bb\uff0c\u5815\u5728\u5730\u4e0a\uff0c\u539f\u6765\u662f\u51b0\u5ddd\u5929\u5973\u6253\u51fa\u4e00\u7247\u5bd2\u51b0\u3002 \u3000\u3000\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6ca1\u51fa\u606f\u7684\u4e1c\u897f\uff0c\u672c\u9886\u4e0d\u597d\u4e0d\u80fd\u518d\u7ec3\u5417\uff1f\u201d\u96f7\u9707\u5b50\u542c\u4e86\u6b64 \u8a00\uff0c\u53c8\u88ab\u6fc0\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6211\u82e5\u81ea\u6740\uff0c\u5979\u53ef\u771f\u5f53\u6211\u662f\u793a\u5f31\u4e86\u3002\u201d\u53ea\u542c\u5f97\u51b0 \u5ddd\u5929\u5973\u53c8\u9053\uff1a\u201c\u82e5\u7136\u4f60\u7f6a\u5b7d\u81f3\u6b7b\uff0c\u6211\u65e9\u5df2\u5c06\u4f60\u5904\u7f6e\uff0c\u8fd8\u987b\u4f60\u52a8\u624b\u5417\uff0c\u5f53\u5e74\u4e4b\u4e8b\uff0c\u94c1\u62d0\u4ed9\u592b\u5987 \u90fd\u5bf9\u6211\u8bf4\u4e86\uff0c\u8fd9\u56fa\u7136\u662f\u4f60\u7684\u5fc3\u672f\u4e0d\u6b63\uff0c\u4f46\u4f60\u53d7\u4e86\u5978\u4eba\u611a\u5f04\u5374\u4e0d\u81ea\u77e5\uff0c\u4ea6\u662f\u53ef\u601c\u53ef\u7b11\uff0c\u738b\u7624\u5b50 \u662f\u4ec0\u4e48\u7528\u5fc3\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u4f60\u82e5\u60f3\u77e5\u9053\u3002\u4eca\u5e74\u4e2d\u79cb\uff0c\u4f60\u81ea\u5df1\u53ef\u4ee5\u5230\u624e\u4f26\u53bb\u770b\u3002\u201d\u96f7\u9707\u5b50\u542c\u4e86\uff0c \u4e0d\u89c9\u4e00\u6014\uff0c\u5fc3\u9053\uff0c\u201c\u738b\u7624\u5b50\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8c01\u8fd8\u80fd\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\uff1f\u600e\u4e48\u5230\u624e\u6ca6\u4e00\u884c\uff0c\u53ef\u4ee5\u77e5\u9053\u6b7b \u4e86\u7684\u738b\u7624\u5b50\u7684\u7528\u5fc3\u5462\uff1f\u201d\u597d\u5947\u4e4b\u5fc3\u4e00\u8d77\uff0c\u81ea\u6740\u4e4b\u5ff5\u987f\u4fcf\uff0c\u5f53\u4e0b\u518d\u62fe\u8d77\u957f\u5251\uff0c\u5782\u5934\u4e27\u6c14\u5730\u4e0e\u5d14 \u4e91\u5b50\u4e00\u540c\u4e0b\u5c71\u3002 \u3000\u3000\u8427\u9752\u5cf0\u4e00\u6d3e\u832b\u7136\uff0c\u5982\u68a6\u5982\u5e7b\uff0c\u53ea\u89c1\u8c22\u4e91\u771f\u4e0e\u94c1\u62d0\u4ed9\u4f4e\u58f0\u8c08\u7b11\uff0c\u72b6\u6781\u4eb2\u70ed\uff0c\u8427\u9752\u5cf0\u5fc3\u4e2d\u4e00 \u9178\uff0c\u60f3\u9053\uff1a\u201c\u771f\u662f\u5404\u6709\u5404\u7684\u7f18\u4efd\uff0c\u52c9\u5f3a\u4e0d\u6765\u7684\u3002\u94c1\u62d0\u4ed9\u867d\u7136\u4e11\u602a\uff0c\u4f46\u5230\u5e95\u662f\u9a70\u540d\u4e00\u4ee3\u7684\u6c5f\u5357 \u5927\u4fa0\u7518\u51e4\u6c60\u8863\u94b5\u771f\u4f20\u7684\u5f1f\u5b50\uff0c\u4e0e\u8c22\u4e91\u771f\u5339\u914d\uff0c\u4e5f\u7b97\u4e0d\u5f97\u8fb1\u6ca1\u4e86\u5979\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u60f3\u5230\u81ea\u5df1\u5c11 \u5e74\u65f6\u5019\u7684\u610f\u4e2d\u4eba\u5df1\u5f97\u4f73\u5076\uff0c\u4e0d\u5fc5\u518d\u52b3\u81ea\u5df1\u7275\u6302\uff0c\u5fc3\u4e2d\u53cd\u89c9\u5766\u7136\u3002\u5ffd\u89c1\u94c1\u62d0\u4ed9\u6491\u7740\u94c1\u62d0\uff0c\u4e00\u80a2 \u4e00\u62d0\u5730\u5411\u81ea\u5df1\u8d70\u6765\uff0c\u5230\u4e86\u9762\u524d\u4e09\u5c3a\u4e4b\u5730\uff0c\u5ffd\u7136\u624b\u629a\u94c1\u62d0\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u8427\u9752\u5cf0\u614c\u4e0d\u8fed\u5730\u8fd8\u793c\uff0c \u8fde\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u94c1\u62d0\u4ed9\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8427\u8001\u5f1f\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4f55\u6253\u4f60\u4e00\u62d0\uff0c \u73b0\u5728\u53c8\u5411\u4f60\u966a\u793c\u5417\uff1f\u201d\u8427\u9752\u5cf0\u6115\u7136\u4e0d\u77e5\u6240\u7b54\uff0c\u53ea\u542c\u5f97\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u6211\u81ea\u77e5\u662f\u4e2a\u4e11\u516b\u602a\uff0c\u6240\u4ee5 \u561b\uff0c\u6240\u4ee5\u2026\u2026\u201d\u8c22\u4e91\u771f\u4e00\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u7f9e\u7684\u8001\u9b3c\uff0c\u8981\u60f9\u4eba\u7b11\u8bdd\u5417\uff0c\u5feb\u522b\u8bf4\u5566\uff01\u201d\u539f\u6765\u94c1\u62d0 \u4ed9\u56e0\u4e3a\u81ea\u5df1\u76f8\u8c8c\u4e11\u964b\uff0c\u59bb\u5b50\u5219\u8c8c\u7f8e\u5982\u82b1\uff0c\u4ed6\u6027\u60c5\u672c\u5c31\u602a\u50fb\uff0c\u7adf\u56e0\u6b64\u800c\u8d77\u4e86\u5947\u5992\uff0c\u51e1\u5bf9\u4ed6\u59bb\u5b50 \u8d77\u8fc7\u5ff5\u5934\uff0c\u7ea0\u7f20\u8fc7\u7684\uff0c\u4ed6\u90fd\u8981\u53bb\u6253\u90a3\u4eba\u4e00\u62d0\uff0c\u94c1\u62d0\u4ed9\u8fd9\u79cd\u5947\u602a\u7684\u5992\u5ff5\uff0c\u8427\u9752\u5cf0\u505a\u68a6\u4e5f\u60f3\u4e0d \u5230\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u7684\u8bf4\u8bdd\u88ab\u59bb\u5b50\u6253\u65ad\uff0c\u5f88\u4e0d\u81ea\u7136\u5730\u53c8\u52c9\u5f3a\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u6253\u4f60\u7684\u539f\u56e0\u6211 \u4e0d\u8bf4\u4e86\uff0c\u73b0\u5728\u6211\u8bf4\u5411\u4f60\u966a\u793c\u7684\u539f\u56e0\u5427\u3002\u5582\uff0c\u8427\u9752\u5cf0\uff0c\u4f60\u4eca\u5e74\u51e0\u5c81\uff1f\u201d \u3000\u3000\u8427\u9752\u5cf0\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u94c1\u62d0\u4ed9\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u7b54\u9053\uff1a\u201c\u5c0f\u5f1f\u4eca\u5e74\u56db\u5341\u521a\u521a\u51fa\u5934\u3002\u201d \u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u6bd4\u6211\u5e74\u8f7b\u591a\u5566\u3002\u53ef\u601c\u4f60\u989c\u5bb9\u82cd\u8001\uff0c\u53d1\u90fd\u767d\u4e86\uff0c\u542c\u8bf4\u5341\u591a\u5e74\u524d\uff0c\u4f60 \u8fd8\u662f\u4e2a\u86ee\u6f02\u4eae\u7684\u5c0f\u4f19\u5b50\u5462\uff01\u201d\u8427\u9752\u5cf0\u82cd\u767d\u7684\u8138\u4e0a\u6cdb\u8d77\u4e00\u7247\u5fae\u7ea2\uff0c\u5fc3\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u56e0\u4e3a\u4f60\u7684\u59bb \u5b50\uff0c\u5c06\u6211\u65e0\u7f18\u65e0\u6545\u5730\u7275\u5165\u4e86\u8fd9\u573a\u6f29\u6da1\uff0c\u4ee5\u81f3\u6211\u4e3a\u907f\u5f3a\u4ec7\uff0c\u8fdc\u8d70\u585e\u5916\uff0c\u7ec8\u65e5\u62c5\u5fc3\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b \u95f4\uff0c\u5c31\u767d\u4e86\u5c11\u5e74\u5934\u3002\u9752\u6625\u7684\u65f6\u5149\u90fd\u865a\u6e21\u4e86\uff0c\u53ea\u542c\u5f97\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u8427\u8001\u5f1f\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u4e2d\u57cb\u6028 \u4ec0\u4e48\uff0c\u6240\u4ee5\u62d9\u8346\u8981\u6211\u4ee3\u5979\u5411\u4f60\u8d54\u793c\u5566\uff0c\u5979\u8bf4\u7275\u7d2f\u4f60\u906d\u4e86\u4e00\u573a\u7978\u4e8b\uff0c\u5fc3\u4e2d\u5b9e\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c\u9664\u4e86 \u5411\u4f60\u966a\u7f6a\u4e4b\u5916\uff0c\u8fd8\u8981\u9001\u4f60\u4e00\u4ef6\u793c\u7269\u3002\u201d\u8bf4\u7f62\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u7389\u5323\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u4f60\u6253\u5f00\u770b \u770b\uff01\u201d\u8427\u9752\u5cf0\u6253\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u5323\u4e2d\u85cf\u7684\u4e43\u662f\u4e00\u6735\u7855\u5927\u65e0\u670b\u3001\u6709\u5982\u5de8\u7897\u3001\u9c9c\u7ea2\u5982\u8840\u7684\u5927\u7ea2\u82b1\u3002 \u8427\u9752\u5cf0\u5947\u602a\u4e4b\u6781\uff0c\u83ab\u540d\u5176\u5999\uff0c\u53ea\u542c\u5f97\u94c1\u62d0\u4ed9\u7eed\u9053\uff1a\u201c\u8fd9\u662f\u4f18\u6619\u4ed9\u82b1\uff0c\u5403\u4e86\u53ef\u4ee4\u4eba\u767d\u53d1\u53d8\u9ed1\uff0c \u8fd4\u8001\u8fd8\u7ae5\u3002\u6211\u8fd9\u4e2a\u4e11\u516b\u602a\u53cd\u6b63\u7528\u5b83\u4e0d\u7740\uff0c\u5c31\u9001\u7ed9\u4f60\u5427\u3002\u201d\u539f\u6765\u8c22\u4e91\u771f\u5c11\u5e74\u4e4b\u65f6\uff0c\u53f7\u79f0\u201c\u593a\u547d \u4ed9\u5b50\u201d\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u5389\u5bb3\u65e0\u6bd4\uff0c\u505a\u4e8b\u4e0d\u62e9\u624b\u6bb5\uff0c\u6240\u4ee5\u624d\u6709\u5f53\u5e74\u90a3\u4e00\u573a\u51f6\u6740\uff0c\u800c\u8427\u9752\u5cf0\u5374\u7cca\u91cc \u7cca\u6d82\u53d7\u4e86\u96f7\u9707\u5b50\u4e0e\u8c22\u4e91\u771f\u53cc\u65b9\u7684\u5229\u7528\u3002\u8c22\u4e91\u771f\u7ed3\u5a5a\u4e4b\u540e\uff0c\u6027\u60c5\u6e10\u53d8\uff0c\u751a\u4e3a\u540e\u6094\u3001\u6070\u597d\u4e0e\u94c1\u62d0 \u4ed9\u6f2b\u6e38\u897f\u5317\u4e4b\u65f6\uff0c\u5728\u5929\u5c71\u4e0a\u627e\u5230\u4e86\u4e00\u6735\u4f18\u6619\u4ed9\u82b1\uff0c\u4fbf\u51b3\u610f\u62ff\u5b83\u6765\u4e0e\u8427\u9752\u5cf0\u4f5c\u4e3a\u8d4e\u7f6a\u3002 \u3000\u3000\u8427\u9752\u5cf0\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u8fd9\u662f\u4f18\u6619\u4ed9\u82b1\uff01\u201d\u60f3\u8d77\u524d\u8f88\u7684\u4f20\u8bf4\uff0c\u8fd9\u4ed9\u82b1\u8981\u516d\u5341 \u5e74\u624d\u5f00\u4e00\u6b21\uff0c\u767e\u4f59\u5e74\u524d\uff0c\u6b66\u5f53\u6d3e\u7684\u8fdc\u7956\u5353\u4e00\u822a\u60f3\u91c7\u4f18\u6619\u4ed9\u82b1\u9001\u4e0e\u767d\u53d1\u9b54\u5973\uff0c\u5b88\u5019\u4e00\u751f\uff0c\u8fd8\u5b88 \u4e0d\u5230\u5f00\u82b1\u3002\u4e0d\u6599\u5982\u4eca\u5f97\u89c1\uff0c\u800c\u4e14\u94c1\u62d0\u4ed9\u8fd8\u9001\u7ed9\u81ea\u5df1\u3002\u8427\u9752\u5cf0\u6014\u6014\u5730\u770b\u7740\u90a3\u6735\u7ea2\u82b1\uff0c\u4e0d\u6562\u4f38\u624b \u53bb\u63a5\u3002\u8c22\u4e91\u771f\u7f13\u7f13\u884c\u8fd1\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u9752\u5cf0\uff0c\u4f60\u5403\u4e86\u5b83\u5427\u3002\u4e94\u5e74\u524d\u6211\u5728\u5ddd\u897f\u9047\u89c1\u4f60\u7684\u8868\u59b9\u5434 \u7edb\u4ed9\uff0c\u5979\u5728\u95ee\u5019\u4f60\u5462\u3002\u4f60\u6bcd\u4eb2\u4e5f\u8fd8\u5065\u5728\uff0c\u4f60\u4e0d\u60f3\u56de\u53bb\u770b\u770b\u5979\u4eec\u5417\uff1f\u201d\u8427\u9752\u5cf0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u731b\u5730 \u60f3\u8d77\u4e86\u6545\u4e61\u4eb2\u53cb\uff0c\u601d\u4e61\u4e4b\u5fc3\u9661\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u73b0\u5728\u51a4\u4ec7\u5df2\u7ecf\u89e3\u5f00\uff0c\u662f\u8be5\u56de\u4e61\u7684\u65f6\u5019\u4e86\u3002\u6211\u4e3a\u5979 \u906d\u4e86\u4e00\u573a\u5927\u7978\uff0c\u8981\u5979\u8fd9\u6735\u4ed9\u82b1\uff0c\u4e5f\u4e0d\u4e3a\u8fc7\u3002\u4e8e\u662f\u4f38\u624b\u63a5\u8fc7\u90a3\u6735\u7ea2\u82b1\uff0c\u4ef0\u5929\u53f9\u9053\uff1a\u201c\u98d8\u6cca\u6c5f\u6e56 \u6570\u5341\u79cb\uff0c\u76f8\u9022\u672a\u767d\u5c11\u5e74\u5934\u3002\u201d\u8c22\u4e91\u771f\u63a5\u9053\uff1a\u201c\u800c\u4eca\u597d\u81ea\u8fd8\u5bb6\u53bb\uff0c\u7af9\u9a6c\u9752\u6885\u89c5\u65e7\u6e38\uff01\u201d\u8427\u9752\u5cf0 \u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u8bf4\u5f97\u597d\uff01\u5b87\u513f\u5475\uff0c\u4e3a\u5e08\u7684\u8981\u548c\u4f60\u5206\u624b\u4e86\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u5728\u8fd9\u534a\u65e5\u4e4b\u95f4\uff0c\u76ee\u7779\u8bb8\u591a\u5947\u60c5\u602a\u4e8b\uff0c\u604d\u5982\u7f6e\u8eab\u68a6\u5883\u4e4b\u4e2d\uff0c\u5ffd\u7136\u542c\u8bf4\u5e08\u7236\u8981\u8fd4\u56de\u5bb6 \u4e61\uff0c\u4e0d\u7981\u6014\u4f4f\u4e86\uff0c\u597d\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u8427\u9752\u5cf0\u4e5f\u89c9\u5341\u5206\u96be\u820d\u3002\u94c1\u62d0\u4ed9\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5f92\u5f1f\u5fc3 \u80a0\u751a\u597d\uff0c\u6781\u5408\u6211\u7684\u5fc3\u610f\uff0c\u6211\u8fd9\u4e2a\u5316\u5b50\uff0c\u89c1\u4e86\u522b\u4eba\u7684\u597d\u4e1c\u897f\u5c31\u60f3\u4e5e\u8ba8\uff0c\u8427\u8001\u5f1f\uff0c\u4f60\u8fd9\u4e2a\u5f92\u5f1f\u5c31 \u8ba9\u4e86\u6211\u5427\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u559c\u9053\uff1a\u201c\u4f60\u80af\u6536\u5b87\u513f\u4e3a\u5f92\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u5b87\u513f\uff0c\u8fc7\u6765\u78d5\u5934\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08 \u7236\uff0c\u4f60\u771f\u7684\u8981\u56de\u53bb\u4e86\u4e48\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u4e0d\u56de\u53bb\uff0c\u8fd8\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u5b87\u513f\uff0c\u4e3a\u5e08\u7684\u4e5f\u820d\u4e0d \u5f97\u4f60\uff0c\u4f46\u4f60\u7684\u7236\u6bcd\u5bb6\u4eba\u90fd\u5728\u6b64\u5730\uff0c\u6211\u53c8\u600e\u80fd\u5e26\u4f60\u56de\u53bb\u3002\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201d\u54c8\uff0c\u4f60\u4e2a\u5c0f\u5a03\u5a03\u4e5f\u751f \u4e86\u4e00\u5bf9\u52bf\u5229\u7684\u773c\u775b\uff0c\u4e0d\u80af\u62dc\u6211\u8fd9\u4e2a\u81ed\u53eb\u5316\u505a\u5e08\u7236\u5417\uff1f\u201d\u9648\u5929\u5b87\u6025\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u3002\u201d\u8fde\u5fd9 \u78d5\u5934\uff0c\u94c1\u62d0\u4ed9\u54c8\u54c8\u5927\u7b11\u3002\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u4f60\u5e08\u7236\u7684\u548c\u6c14\uff0c\u4f60\u5728\u6211\u95e8\u4e0b\uff0c\u8981\u66ff\u6211\u8ba8\u996d\u4e5e\u98df\uff0c\u82e5 \u4e0d\u542c\u8bdd\uff0c\u6211\u5c31\u7528\u8fd9\u6839\u94c1\u62d0\u6253\u4f60\u7684\u5c41\u80a1\u3002\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u4f60\u522b\u5413\u552c\u8fd9\u597d\u5b69\u5b50\u5566\uff0c\u6211\u8bf4\u5440\uff0c\u4f60\u5c31 \u662f\u8e0f\u7834\u94c1\u978b\uff0c\u4e5f\u627e\u4e0d\u5230\u8fd9\u6837\u597d\u7684\u5f92\u5f1f\u3002\u201d\u8427\u9752\u5cf0\u54bd\u4e0b\u773c\u6cea\uff0c\u770b\u4e86\u9648\u5929\u5b87\u4e00\u773c\uff0c\u53c8\u770b\u4e86\u8c22\u4e91\u771f \u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u5566\uff0c\u5b87\u513f\uff0c\u4f60\u597d\u597d\u542c\u8fd9\u4f4d\u5e08\u7236\u7684\u8bdd\u3002\u82e5\u662f\u6709\u7f18\uff0c\u54b1\u4eec\u65e5\u540e\u8fd8\u80fd\u76f8 \u89c1\u3002\u201d\u63d0\u8d77\u62c2\u5c18\uff0c\u98d8\u7136\u4e0b\u5c71\u3002\u540e\u6765\u8427\u9752\u5cf0\u56de\u5230\u4e2d\u539f\uff0c\u4e0d\u4e45\u5c31\u5f97\u4e86\u4e00\u4f4d\u79f0\u5fc3\u5982\u610f\u7684\u4f34\u4fa3\uff0c\u800c\u4e14 \u7ec3\u6210\u4e86\u9752\u57ce\u6d3e\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u8fd9\u662f\u540e\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u7b11\u9053\uff1a\u201c\u8fd9\u8001\u513f\u53bb\u5c31\u53bb\u4e86\uff0c\u504f\u597d\u751f\u7f57\u55e6\u3002\u201d\u8c22\u4e91\u771f\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd8\u6709\u66f4\u7f57 \u55e6\u7684\u4eba\u5462\uff01\u201d\u94c1\u62d0\u4ed9\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u521a\u624d\u5728\u6e56\u8fb9\u711a\u9999\u793c\u62dc\u7684\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6 \u5019\u5df2\u56de\u5230\u8fd9\u513f\uff0c\u6b63\u5728\u51b0\u5ddd\u5929\u5973\u7684\u8ddf\u524d\u4f4e\u58f0\u8bf4\u8bdd\uff0c\u51b0\u5ddd\u5929\u5973\u4ef0\u9996\u671b\u5929\u3002\u795e\u60c5\u6de1\u6f20\u4e4b\u6781\uff0c\u7adf\u4e0d\u7406 \u776c\u4ed6\u4eec\u3002\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u6307\u624b\u5212\u811a\uff0c\u8bf4\u4e86\u53c8\u8bf4\uff0c\u8bf4\u4e2a\u4e0d\u4f11\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u6d3e\u7126\u6025\u7684\u795e\u60c5\uff0c \u4f3c\u662f\u671f\u5f85\uff0c\u53c8\u4f3c\u54c0\u6c42\u3002\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\u597d\u4f3c\u868a\u53eb\u4e00\u6837\uff0c\u800c\u4e14\u94c1\u62d0\u4ed9\u4e5f\u4e0d\u61c2\u5c3c\u6cca\u5c14\u8bdd\uff0c\u7559\u5fc3\u9759 \u542c\uff0c\u4e5f\u542c\u4e0d\u6e05\u695a\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5fc3\u4e2d\u597d\u751f\u5947\u602a\u3002\u9648\u5929\u5b87\u5728\u897f\u85cf\u957f\u5927\uff0c\u897f\u85cf\u5e38\u6709\u5c3c\u6cca\u5c14\u7684\u4eba\u6765 \u505a\u751f\u610f\uff0c\u6240\u4ee5\u4ed6\u7a0d\u61c2\u5f97\u51e0\u53e5\uff0c\u542c\u4e2a\u65ad\u7eed\u7684\u5982\u201c\u91d1\u74f6\u201d\u201c\u7236\u738b\u201d\u4e4b\u7c7b\uff0c\u610f\u601d\u5374\u8fde\u63a5\u4e0d\u8d77\u6765\uff0c\u731b \u5730\u60f3\u8d77\u4e86\u9ea6\u5927\u4fa0\u548c\u94c1\u62d0\u4ed9\u4ed6\u4eec\u5728\u65e5\u5580\u5219\u65c5\u5e97\u4e4b\u4e2d\u4e89\u593a\u74f7\u74f6\u7684\u4e8b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4e24\u4e2a\u5c3c \u6cca\u5c14\u6b66\u58eb\u6240\u8bf4\u4e4b\u4e8b\uff0c\u4e0e\u74f7\u74f6\u6709\u5173\u5417\uff1f\u4f46\u90a3\u53ef\u662f\u74f7\u74f6\uff0c\u5e76\u4e0d\u662f\u4ec0\u4e48\u91d1\u74f6\uff0c\u7236\u738b\u53c8\u6307\u7684\u662f\u8c01 \u5462\uff1f\u201d\u5fc3\u4e2d\u4e5f\u662f\u597d\u751f\u7eb3\u7f55\u3002\u51b0\u5ddd\u5929\u5973\u4f3c\u662f\u5f88\u4e0d\u8010\u70e6\uff0c\u5ffd\u800c\u9ad8\u58f0\u8bf4\u4e86\u4e00\u53e5\u5c3c\u6cca\u5c14\u8bdd\uff0c\u8fd9\u53e5\u6d3b\u9648 \u5929\u5b87\u5374\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5979\u662f\u8bf4\uff1a\u201c\u9664\u975e\u4e0a\u9762\u8fd9\u5ea7\u51b0\u5cf0\u5012\u4e86\uff0c\u5426\u5219\u6211\u6b64\u751f\u7edd\u4e0d\u4e0b\u5c71\u3002\u201d\u4e00\u6325\u7389 \u624b\uff0c\u6307\u4e00\u6307\u90a3\u5ea7\u51b0\u5cf0\uff0c\u51b3\u7136\u8bf4\u9053\uff1a\u201c\u53bb\uff0c\u53bb\uff0c\u4f60\u4eec\u81ea\u5df1\u56de\u53bb\u3002\u201d\u5979\u7684\u8bdd\u58f0\u5e76\u4e0d\u4e25\u5389\uff0c\u4f46\u5374\u4f3c \u4e4e\u662f\u4e00\u4e2a\u7edf\u5e05\u5728\u767e\u4e07\u519b\u4e2d\u4e0b\u4ee4\u4e00\u822c\uff0c\u6709\u4e00\u80a1\u51db\u7136\u4e0d\u53ef\u62c2\u9006\u7684\u795e\u60c5\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u53ea\u89c9\u5f97 \u5979\u4e0d\u4f46\u662f\u7f8e\u8273\u5982\u4ed9\uff0c\u800c\u4e14\u6c14\u5ea6\u9ad8\u534e\uff0c\u65e2\u50cf\u4e0d\u98df\u4eba\u95f4\u70df\u706b\u7684\u4ed9\u5b50\uff0c\u53c8\u50cf\u5c0a\u8d35\u4e4b\u6781\u7684\u5973\u738b\uff0c\u8fd9\u4e24 \u4e2a\u5370\u8c61\u672c\u6781\u77db\u76fe\uff0c\u4f46\u773c\u524d\u7684\u60c5\u666f\uff0c\u8fd9\u4e24\u4e2a\u77db\u76fe\u7684\u5370\u8c61\u5374\u63c9\u5408\u4e3a\u4e00\uff0c\u518d\u96be\u627e\u5230\u7b2c\u4e8c\u79cd\u9002\u5f53\u7684\u5f62 \u5bb9\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u9762\u9762\u76f8\u89d1\uff0c\u60ca\u7136\u800c\u9000\uff0c\u4e0d\u6562\u518d\u8bf4\uff0c\u9762\u4e0a\u5374\u90fd\u73b0\u51fa\u4e00\u526f\u6781\u5176\u5931\u671b\u7684\u795e \u60c5\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u968f\u624b\u6458\u4e86\u4e00\u6735\u91ce\u82b1\uff0c\u629b\u8fdb\u6e56\u4e2d\uff0c\u6b63\u5f53\u51b0\u6cb3\u5165\u53e3\u4e4b\u5904\uff0c\u6c34\u6da1\u4e00\u5377\uff0c\u4e00\u74e3\u4e00\u74e3\u7684\u82b1 \u74e3\u968f\u7740\u6c34\u6d41\u6f02\u53bb\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u6d3e\u6005\u7136\u7684\u795e\u60c5\uff0c\u4f3c\u662f\u5fc3\u6709\u6240\u611f\uff0c\u610f\u5174\u9611\u73ca\uff0c\u9648\u5929\u5b87\u7a81\u7136\u60f3\u8d77\u4e86 \u201c\u7269\u72b9\u5982\u6b64\uff0c\u4eba\u4f55\u4ee5\u582a\uff01\u201d\u8fd9\u4e24\u53e5\u8bf4\u8bdd\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff0c\u770b\u90a3\u96ea\u5c71\u51b0\u5cf0\uff0c\u9ad8\u8038\u5165\u4e91\uff0c\u4e0a \u9762\u5b9a\u662f\u5bd2\u51b7\u65e0\u6bd4\uff0c\u800c\u773c\u524d\u5374\u662f\u4e00\u6e56\u6625\u6c34\uff0c\u904d\u91ce\u82b1\u9999\uff0c\u6e56\u7554\u7389\u4eba\uff0c\u98ce\u534e\u7edd\u4ee3\uff0c\u4e00\u5c71\u4e4b\u4e0a\uff0c\u5883\u754c \u60ac\u6b8a\uff1b\u8fd9\u98ce\u534e\u7edd\u4ee3\u7684\u7389\u4eba\uff0c\u5374\u957f\u5e74\u7d2f\u6708\u5b64\u5355\u4e00\u4eba\u4f4f\u5728\u96ea\u5c71\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u9648\u5929\u5b87\u5ffd\u53d1\u5947\u60f3\uff0c\u60f3 \u9053\uff1a\u8fd9\u5c31\u597d\u6bd4\u51ac\u5929\u91cc\u7684\u6625\u5929\uff0c\u53ef\u60dc\u8fd9\u6625\u5929\u7684\u666f\u8272\uff0c\u5374\u6c38\u4e0d\u4e3a\u4e16\u4eba\u6240\u77e5\uff0c\u96ea\u5c71\u4e4b\u4e2d\uff0c\u5c45\u7136\u4f1a\u6709 \u4e00\u4e2a\u5929\u6e56\uff0c\u5df2\u662f\u5947\u5999\uff0c\u51b0\u5ddd\u4e4b\u4e0a\uff0c\u7adf\u6709\u4e00\u4e2a\u5929\u5973\uff0c\u66f4\u662f\u795e\u5947\uff1a\u96be\u9053\u8fd9\u51b0\u5ddd\u5929\u5973\uff0c\u5c06\u6765\u4e5f\u50cf\u8fd9 \u6e56\u7554\u7684\u6625\u82b1\uff0c\u81ea\u5f00\u81ea\u843d\uff0c\u82b1\u81ea\u98d8\u96f6\u6c34\u81ea\u6d41\uff01 \u3000\u3000\u9648\u5929\u5b87\u6b63\u5728\u9050\u601d\uff0c\u5ffd\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u60a0\u60a0\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u91cc\u672c\u4e0d\u62db\u5f85\u5916\u4eba\uff0c\u4f46\u7518\u5927\u4fa0\u4e43\u662f\u5bb6 \u7236\u81f3\u4ea4\uff0c\u94c1\u62d0\u4ed9\u4f60\u65e2\u5949\u7518\u5927\u4fa0\u7684\u9057\u547d\uff0c\u4e07\u6c34\u5343\u5c71\uff0c\u524d\u6765\u627e\u6211\uff0c\u90a3\u4e48\u6211\u4e5f\u5c31\u7834\u4e00\u6b21\u4f8b\uff0c\u8bf7\u4f60\u4eec \u592b\u5987\u5230\u6211\u7684\u5c71\u5c45\u5c0f\u4f4f\u51e0\u5929\u3002\u201d\u539f\u6765\u81ea\u6842\u534e\u751f\u5931\u8e2a\u4e4b\u540e\uff0c\u4ed6\u7684\u4e24\u4f4d\u54e5\u54e5\u904d\u6258\u9ad8\u4eba\u5bfb\u89c5\u3002\u7518\u51e4\u6c60 \u4e5f\u662f\u53d7\u6258\u8005\u4e4b\u4e00\uff0c\u4e09\u5341\u5e74\u6765\uff0c\u904d\u627e\u65e0\u8e2a\uff0c\u7518\u98ce\u6c60\u6700\u91cd\u7136\u8bfa\uff0c\u6240\u4ee5\u5728\u8eab\u6b7b\u4e4b\u540e\uff0c\u4ecd\u6709\u9057\u8a00\u8981\u5f92 \u5f1f\u5bfb\u627e\uff0c\u94c1\u62d0\u4ed9\u592b\u5987\u603b\u7b97\u4e0d\u8d1f\u6240\u6258\uff0c\u6253\u63a2\u51fa\u5929\u6e56\u4e4b\u4e0a\u6709\u4e00\u4f4d\u51b0\u5ddd\u5929\u5973\uff0c\u5341\u4e4b\u516b\u4e5d\uff0c\u4f1a\u662f\u6842\u534e \u751f\u7684\u5973\u513f\uff0c\u56e0\u800c\u5bfb\u5230\u6b64\u95ee\uff0c\u9002\u624d\u94c1\u62d0\u4ed9\u5728\u6e56\u8fb9\u4e0e\u96f7\u9707\u5b50\u6bd4\u6b66\u4e4b\u65f6\uff0c\u6b63\u662f\u8c22\u4e91\u771f\u4e0e\u51b0\u5ddd\u5929\u5973\u4f1a \u9762\u4e4b\u9645\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u7b11\u9053\uff1a\u201c\u6211\u7d20\u6155\u6b64\u95f4\u4ed9\u5883\uff0c\u5fc3\u6709\u6240\u613f\uff0c\u4e0d\u6562\u8bf7\u8033\u3002\u4f60\u80af\u7559\u6211\u4f4f\u51e0\u5929\uff0c\u90a3\u662f\u6700\u597d\u4e0d \u8fc7\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5927\u5bb6\u90fd\u8bf7\u4e0b\u8239\u5427\uff0c\u4f60\u662f\u94c1\u62d0\u4ed9\u7684\u5f92\u5f1f\uff0c\u53c8\u662f\u6211\u8fd9\u4f4d\u829d\u5a1c\u59b9\u59b9\u7684 \u670b\u53cb\uff0c\u4f60\u4e5f\u6765\u5427\u3002\u201d\u9648\u5929\u5b87\u7565\u4e00\u8e0c\u8e87\uff0c\u4e5f\u4fbf\u968f\u7740\u4ed6\u4eec\u540c\u4e0b\u5c0f\u8239\u3002\u8fd9\u65f6\u65e5\u5934\u8fc7\u5348\uff0c\u51b0\u5ddd\u4e2d\u7684\u51b0 \u5757\u878d\u5316\u66f4\u591a\uff0c\u6c34\u6d41\u66f4\u6025\uff0c\u631f\u7740\u6d6e\u51b0\uff0c\u81ea\u5c71\u9876\u5954\u6cfb\u800c\u4e0b\uff0c\u66f4\u662f\u4ee4\u4eba\u89e6\u76ee\u60ca\u5fc3\u3002\u9648\u5929\u5b87\u5fc3\u9053\uff1a \u201c\u9006\u6d41\u800c\u4e0a\uff0c\u6bd4\u9002\u624d\u987a\u6d41\u800c\u4e0b\uff0c\u66f4\u8981\u8270\u96be\u51e0\u500d\uff0c\u51b0\u5ddd\u5929\u5973\u7eb5\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u4e5f\u96be\u4ee5\u5c06\u8fd9\u5c0f\u821f\u5728 \u51b0\u5ddd\u4e4b\u4e2d\uff0c\u6491\u81f3\u5c71\u9876\uff0c\u96be\u9053\u5979\u4e0d\u662f\u8840\u8089\u6240\u9020\u7684\u5bfb\u5e38\u4e4b\u4eba\uff0c\u800c\u7adf\u662f\u540d\u7b26\u5176\u5b9e\u7684\u5929\u5973\uff1f\u5bf9\u51b0\u5ddd\u5929 \u5973\u9002\u624d\u5728\u51b0\u5ddd\u4e4b\u4e2d\u64cd\u821f\u5982\u5c65\u5e73\u5730\u7684\u529f\u592b\uff0c\u4e07\u5206\u4e0d\u89e3\u3002 \u3000\u3000\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u5750\u5b9a\u4e86\uff1f\u5f00\u8239\u5566\uff01\u201d\u53d6\u8d77\u4e00\u6280\u78a7\u7389\u8239\u7bd9\uff0c\u8f7b\u8f7b\u5728\u51b0\u5757\u4e4b\u4e0a \u4e00\u70b9\uff0c\u5c0f\u821f\u7acb\u523b\u9a76\u524d\u51e0\u4e08\uff0c\u5ffd\u7ed9\u6c34\u6d41\u4e00\u6d8c\uff0c\u6d6e\u51b0\u4e00\u6324\uff0c\u53c8\u9000\u540e\u4e08\u8bb8\uff0c\u51b0\u5ddd\u5929\u5973\u62e8\u5f00\u6d6e\u51b0\uff0c\u53c8 \u662f\u8f7b\u8f7b\u4e00\u70b9\uff0c\u5c0f\u821f\u53c8\u518d\u5411\u524d\uff0c\u9648\u5927\u5b57\u628a\u773c\u4e00\u671b\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u5168\u795e\u8d2f\u6ce8\uff0c\u4f3c\u662f\u9887\u4e3a\u5403\u529b\uff0c\u800c \u821f\u4e2d\u8bf8\u4eba\uff0c\u5374\u90fd\u5b89\u7136\u5750\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u8981\u5979\u4e00\u4eba\u7528\u529b\uff0c\u8fd9\u600e\u4e48\u8fc7\u610f\u5f97\u53bb\uff1f\u201d \u5ffd\u89c1\u53c8\u662f\u4e00\u80a1\u6025\u6d41\u5954\u6765\uff0c\u90a3\u5c0f\u8239\u56e2\u56e2\u4e71\u8f6c\uff0c\u7adf\u88ab\u5377\u5728\u6f29\u6da1\u4e4b\u4e2d\uff0c\u8fdb\u9000\u4e0d\u5f97\uff0c\u51b0\u5c51\u4e0e\u6d6a\u82b1\u9f50 \u98de\uff0c\u6e85\u4e86\u6ee1\u9762\u3002 \u3000\u3000\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u89c1\u5e08\u7236\u90a3\u652f\u94c1\u62d0\u501a\u5728\u8239\u8fb9\uff0c\u9648\u5929\u5b87\u5c11\u5e74\u70ed\u5fc3\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u62ff\u8d77\u5e08\u7236\u90a3 \u679d\u94c1\u62d0\uff0c\u610f\u6b32\u52a9\u5979\u4e00\u81c2\u4e4b\u529b\uff0c\u94c1\u62d0\u6c89\u91cd\u975e\u5e38\uff0c\u9648\u5929\u5b87\u52c9\u5f3a\u63d0\u4e86\u8d77\u6765\uff0c\u63d2\u5165\u6c34\u4e2d\uff0c\u7528\u529b\u4e00\u6491\uff0c \u4e0d\u6491\u72b9\u597d\uff0c\u4e00\u6491\u4e4b\u4e0b\uff0c\u90a3\u5c0f\u8239\u7a81\u7136\u6253\u6a2a\u4e00\u8f6c\uff0c\u7ed9\u6fc0\u6d41\u4e00\u51b2\u800c\u4e0b\uff0c\u4e00\u5c0f\u534a\u8239\u8eab\u5df2\u4fb5\u5165\u6c34\u4e2d\uff0c\u503e \u4fa7\u98a0\u7c38\u3002\u94c1\u62d0\u4ed9\u6025\u5c06\u94c1\u62d0\u4e00\u628a\u62a2\u8fc7\uff0c\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u53cc\u6307\u4e00\u5f39\uff0c\u53d1\u51fa\u4e00\u7247\u6d6e \u51b0\uff0c\u5c06\u94c1\u62d0\u5f39\u5f00\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u4e00\u7247\u597d\u5fc3\uff0c\u4e0d\u5fc5\u602a\u4ed6\u3002\u201d\u9648\u5929\u5b87\u9762\u4e0a\u70ed\u8fa3\u8fa3\u7684\u597d\u4e0d\u7f9e\u60ed\uff0c \u53ea\u89c1\u90a3\u5c0f\u8239\u4e0d\u77e5\u600e\u7684\uff0c\u53c8\u7a33\u4f4f\u4e86\u5728\u6c34\u6d41\u4e4b\u4e2d\u6253\u8f6c\uff0c\u9648\u5929\u5b87\u5fc3\u4e2d\u7a0d\u5bbd\uff0c\u5ffd\u89c1\u53c8\u662f\u4e00\u80a1\u6fc0\u6d41\uff0c\u81ea \u5de6\u8fb9\u5954\u6765\uff0c\u6bd4\u5148\u524d\u90a3\u80a1\u6fc0\u6d41\u66f4\u731b\u66f4\u6025\uff0c\u631f\u7740\u6d6e\u51b0\uff0c\u54d7\u5566\u5566\u7684\u75be\u51b2\u800c\u4e0b\uff0c\u9648\u5929\u5b87\u5413\u5f97\u9762\u9752\u5507 \u767d\uff0c\u6697\u9053\uff1a\u201c\u6b64\u547d\u4f11\u77e3\uff01\u201d\u5ffd\u5730\u91cc\uff0c\u90a3\u5c0f\u8239\u5411\u4e0a\u4e00\u629b\uff0c\u9648\u5929\u5b87\u987f\u611f\u8eab\u5b50\u4e00\u8f7b\uff0c\u5c31\u5982\u817e\u4e91\u9a7e\u96fe \u4e00\u822c\uff0c\u4f3c\u662f\u7ed9\u90a3\u80a1\u6f88\u6d41\u629b\u63b7\u5230\u4e5d\u5929\u4e4b\u4e0a\uff0c\u5ffd\u7136\u53c8\u6389\u4e0b\u6765\uff0c\u7741\u5f00\u773c\u65f6\uff0c\u53ea\u89c1\u90a3\u5c0f\u8239\u5df2\u5e73\u7a33\u7684\u6d6e \u5728\u6c34\u4e2d\u3001\u79bb\u5f00\u51b0\u5ddd\u5165\u6e56\u4e4b\u5904\u5f88\u8fdc\u4e86\u3002\u9648\u5929\u5b87\u5927\u611f\u795e\u5947\uff0c\u5ffd\u542c\u5f97\u90a3\u85cf\u65cf\u5c11\u5973\u829d\u5a1c\u7b11\u9053\uff1a\u201c\u6211\u521d \u6765\u65f6\u4e5f\u66fe\u7ed9\u6fc0\u6d41\u5413\u5f97\u8981\u6b7b\uff0c\u540e\u6765\u624d\u77e5\u9053\uff0c\u82e5\u7136\u8fd9\u51b0\u5ddd\u4e4b\u4e2d\u6ca1\u6709\u6fc0\u6d41\uff0c\u5c0f\u821f\u6839\u672c\u5c31\u4e0d\u80fd\u4e0a\u4e0b\u3002 \u539f\u6765\u51b0\u5ddd\u5929\u5973\u751f\u4e8e\u65af\uff0c\u957f\u4e8e\u65af\uff0c\u4e60\u77e5\u51b0\u5ddd\u7279\u6027\uff0c\u51b0\u5ddd\u7684\u6fc0\u6d41\u5c31\u5982\u9f99\u5377\u98ce\u4e00\u6837\u3001\u53ef\u4ee5\u56de\u65cb\u6253 \u8f6c\uff0c\u987a\u7740\u8fd9\u80a1\u6c34\u6d41\uff0c\u5c0f\u821f\u53ef\u4ee5\u81ea\u7136\u800c\u7136\u5730\u88ab\u5b83\u5012\u5377\u4e0a\u53bb\uff0c\u6240\u4ee5\u5728\u51b0\u5ddd\u4e4b\u4e2d\u884c\u821f\uff0c\u867d\u7136\u4e5f\u8981\u5177 \u6709\u4e0d\u5bfb\u5e38\u7684\u6b66\u529f\uff0c\u4f46\u5374\u5e76\u975e\u795e\u8ff9\u3002 \u3000\u3000\u4e0d\u7528\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c0f\u821f\u5df2\u5230\u4e86\u5c71\u9876\uff0c\u9648\u5929\u5b87\u9661\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u53ea\u89c1\u5c71\u4e0a\u5efa\u7b51\uff0c\u5982\u540c\u5bab\u6bbf\uff0c\u90a3 \u4e9b\u5c4b\u5b57\u90fd\u662f\u6c34\u6676\u3001\u4e91\u77f3\u3001\u6676\u76d0\uff0c\u6216\u8005\u575a\u51b0\u6240\u9020\uff0c\u901a\u4f53\u900f\u660e\u3002\u5728\u5915\u9633\u8fd4\u7167\u4e4b\u4e0b\uff0c\u53ea\u89c9\u971e\u5f69\u593a \u76ee\uff0c\u95ea\u95ea\u751f\u5149\uff0c\u7aef\u7684\u662f\u4eba\u95f4\u7f55\u89c1\u7684\u5947\u666f\uff0c\u80dc\u4f3c\u4f20\u8bf4\u4e2d\u7684\u8d1d\u95f9\u73e0\u5bab\u3002\u9648\u5929\u5b87\u672c\u5df1\u75b2\u5026\u975e\u5e38\uff0c\u89c1 \u6b64\u5947\u666f\uff0c\u4e5f\u89c9\u7cbe\u795e\u4e00\u632f\uff0c\u4f46\u5fc3\u4e2d\u5374\u81ea\u60f3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u4e00\u4eba\uff0c\u4f4f\u8fd9\u4e48\u5927\u7684\u5bab\u6bbf\uff0c\u4e0d\u5927\u5bc2\u5bde\u4e86 \u4e48\uff1f\u201d\u829d\u5a1c\u7b11\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\uff0c\u4f60\u82e5\u80af\u6536\u6211\u4f5c\u4f60\u7684\u4f8d\u5973\uff0c\u6211\u771f\u613f\u610f\u7ec8\u8001\u6b64\u95f4\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u8fd9\u5730\u65b9\u4f60\u600e\u4f4f\u5f97\u60ef\uff1f\u4f55\u51b5\u4f60\u4e0d\u662f\u65e5\u65e5\u591c\u591c\u90fd\u5728\u60f3\u62a5\u7236\u6bcd\u4e4b\u4ec7\u5417\uff1f\u201d\u829d\u5a1c\u9eef\u7136 \u4e0d\u8bed\uff0c\u51b0\u5ddd\u5929\u5973\u53c8\u9053\uff1a\u201c\u4f60\u8001\u662f\u53eb\u6211\u5929\u5973\u59d0\u59d0\uff0c\u4e0d\u6015\u5916\u4eba\u89c1\u7b11\u4e48\uff1f\u6211\u53ea\u4e0d\u8fc7\u4f4f\u5728\u51b0\u5ddd\u4e4b\u4e0a\u7f62 \u4e86\uff0c\u54ea\u91cc\u662f\u4ec0\u4e48\u5929\u5973\u5462\uff1f\u6211\u59d3\u6842\uff0c\u540d\u53eb\u6842\u51b0\u5a25\uff0c\u94c1\u62d0\u4ed9\u592b\u5987\uff0c\u4f60\u4eec\u5927\u7ea6\u8fd8\u4e0d\u77e5\u9053\u6211\u7684\u540d\u5b57 \u5427\u3002\u201d\u8c22\u4e91\u771f\u7b11\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u771f\u597d\uff0c\u4e0d\u8fc7\u4f60\u7f8e\u82e5\u5929\u4eba\uff0c\u6211\u8fd8\u662f\u53eb\u4f60\u505a\u5929\u5973\u59d0\u59d0\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5e26\u9886\u4f17\u4eba\uff0c\u8d70\u5165\u5bab\u6bbf\uff0c\u53cc\u638c\u4e00\u62cd\uff0c\u53ea\u89c1\u6bcf\u5e62\u5bab\u6bbf\u4e4b\u524d\uff0c\u90fd\u51fa\u73b0\u4e86\u4e00\u4f4d\u5bab\u88c5\u5c11 \u5973\uff0c\u56e0\u4e3a\u5bab\u6bbf\u900f\u660e\uff0c\u6240\u4ee5\u91cc\u9762\u867d\u7136\u662f\u91cd\u95e8\u53e0\u6237\uff0c\u90a3\u4e9b\u5b98\u88c5\u5c11\u5973\uff0c\u5374\u90fd\u9690\u7ea6\u53ef\u89c1\u3002\u5947\u602a\u7684\u662f\uff0c \u90a3\u4e9b\u5bab\u5973\u867d\u7136\u4e2a\u4e2a\u90fd\u662f\u5999\u66fc\u591a\u59ff\uff0c\u4f46\u88c5\u675f\u4f53\u6001\u3001\u975e\u85cf\u975e\u6c49\uff0c\u4e0d\u77e5\u662f\u6765\u81ea\u4f55\u65b9\uff1f \u3000\u3000\u9648\u5929\u5b87\u76ee\u7729\u795e\u8ff7\uff0c\u611f\u89c9\u4f3c\u4e4e\u662f\u8d70\u4eba\u4e86\u795e\u8bdd\u4e2d\u7684\u5883\u754c\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4eec\u8dcb\u6d89\u98ce\u5c18\uff0c\u65c5 \u9014\u52b3\u987f\uff0c\u5148\u6b47\u6b47\u5427\u3002\u201d\u53eb\u4f8d\u5973\u5f15\u4ed6\u4eec\u53bb\u4f11\u606f\uff0c\u94a6\u62d0\u4ed9\u592b\u5987\u3001\u9648\u5929\u5b87\u4e0e\u829d\u5a1c\u56db\u4eba\u90fd\u5f7c\u5206\u9694\u5f00 \u6765\uff0c\u6bcf\u4eba\u8fdb\u4e00\u95f4\u5bab\u6bbf\u3002 \u3000\u3000\u5bab\u4e2d\u9053\u8def\u5f2f\u5f2f\u66f2\u66f2\uff0c\u9648\u5929\u5b87\u968f\u7740\u4f8d\u5973\u8d70\u8fc7\u51e0\u9053\u56de\u5eca\uff0c\u5230\u4e86\u4e00\u5904\u82b1\u56ed\uff0c\u4f46\u89c1\u5947\u82b1\u5f02\u8349\u89e6\u76ee \u90fd\u662f\uff0c\u6709\u7684\u82b1\u5f00\u5982\u96ea\uff0c\u6709\u7684\u707f\u82e5\u4e91\u971e\uff0c\u6709\u7684\u9ed1\u5982\u58a8\u5170\uff0c\u6709\u7684\u7ea2\u82e5\u73ab\u7470\uff0c\u6709\u7684\u7275\u85e4\u9644\u845b\uff0c\u6709\u7684 \u77f3\u9699\u6a2a\u751f\uff0c\u90fd\u8bf4\u4e0d\u51fa\u540d\u5b57\u6765\u3002\u9648\u5929\u5b87\u76ee\u4e0d\u6687\u7ed9\uff0c\u53ea\u542c\u5f97\u90a3\u4f8d\u5973\u8bf4\u9053\uff1a\u201c\u76f8\u516c\u8bf7\u5165\u8fd9\u95f4\u5c4b\u5b50\u6b47 \u606f\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u53eb\u6211\uff0c\u53ef\u4ee5\u7275\u52a8\u5c4b\u91cc\u7684\u94dc\u7ebf\uff0c\u6211\u5c31\u77e5\u9053\u4e86\u3002\u8fd9\u91cc\u9053\u8def\u7eb7\u6b67\uff0c\u76f8\u516c\u82e5\u51fa\u56ed\u4e2d\u6e38 \u73a9\uff0c\u8bf7\u8bb0\u7740\u8fd9\u4e2a\u6807\u8bb0\uff0c\u4ee5\u514d\u8ff7\u5931\u3002\u201d\u7528\u624b\u6307\u7ed9\u9648\u5929\u5b87\u770b\uff0c\u9648\u5929\u5b87\u6240\u4f4f\u7684\u8fd9\u95f4\u5bab\u6bbf\uff0c\u5c4b\u9876\u96d5\u6709 \u4e00\u4e2a\u77f3\u72ee\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u5176\u4ed6\u5bab\u6bbf\uff0c\u6216\u8005\u662f\u96d5\u6709\u9a8f\u9a6c\uff0c\u6216\u8005\u662f\u8001\u864e\uff0c\u6216\u8005\u662f\u51e4\u51f0\uff0c\u90fd\u6709\u6807\u5fd7\u3002 \u8fd9\u86ee\u5973\u76f8\u8c8c\u867d\u6b8a\u4e2d\u4e0a\uff0c\u4f46\u5374\u8bf4\u5f97\u4e00\u53e3\u5f88\u597d\u7684\u5317\u4eac\u8bdd\u3002\u6e05\u751c\u5706\u6da6\uff0c\u542c\u8d77\u6765\u5f88\u662f\u8212\u670d\u3002 \u3000\u3000\u4f8d\u5973\u4ea4\u5f85\u6e05\u695a\uff0c\u4fbf\u81ea\u9000\u4e0b\u3002\u9648\u5929\u5b87\u63a8\u5f00\u623f\u95e8\uff0c\u5ffd\u89c1\u623f\u4e2d\u7a81\u7136\u73b0\u51fa\u51e0\u4e2a\u5c11\u5e74\uff0c\u90fd\u5e26\u7740\u60ca\u6115 \u7684\u8868\u60c5\uff0c\u8fce\u9762\u800c\u6765\u3002\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u4ed4\u7ec6\u770b\u65f6\uff0c\u5374\u539f\u6765\u662f\u81ea\u5df1\u7684\u5f71\u5b50\u3002\u8fd9\u95f4\u5bab\u6bbf\u662f\u4e91\u77f3\u6240 \u9020\uff0c\u56db\u9762\u5899\u58c1\u90fd\u5d4c\u6709\u73bb\u7483\u955c\u5b50\uff0c\u7ea4\u6beb\u6bd5\u73b0\uff0c\u5f53\u65f6\u8fd9\u79cd\u7422\u78e8\u7cbe\u7f8e\u7684\u7167\u8eab\u955c\u90fd\u662f\u4ece\u897f\u6d0b\u8fd0\u6765\u7684\uff0c \u9648\u5929\u5b87\u867d\u7136\u89c1\u8fc7\uff0c\u4f46\u5374\u6ca1\u6709\u8fd9\u4e48\u7cbe\u7f8e\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e48\u591a\uff0c\u662f\u4ee5\u611f\u5230\u60ca\u8bb6\u3002\u623f\u4e2d\u5e03\u7f6e\uff0c\u6e05\u96c5\u5bcc \u4e3d\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u4e1d\u7ec7\u9526\u88ab\u914d\u4ee5\u63cf\u91d1\u5e10\u5b50\uff0c\u6a80\u9999\u4e66\u684c\u4e0a\u4f9b\u4e00\u74f6\u4e0d\u77e5\u540d\u7684\u5f02\u82b1\uff0c\u53d1\u6563\u7740\u5e7d\u5e7d\u7684\u6e05 \u9999\uff0c\u5899\u58c1\u4e0a\u8fd8\u6302\u6709\u4e00\u5ea7\u897f\u6d0b\u65f6\u8fb0\u949f\uff0c\u7684\u7684\u7b54\u7b54\u54cd\u7740\u3002\u90a3\u65f6\u897f\u6d0b\u7684\u65f6\u8fb0\u949f\u8fd0\u5165\u4e2d\u56fd\u7684\u8fd8\u5c11\uff0c\u9648 \u5929\u5b57\u53ea\u5728\u571f\u53f8\u5bb6\u91cc\u89c1\u8fc7\u4e00\u6b21\uff0c\u7981\u4e0d\u4f4f\u5bf9\u8fd9\u65f6\u8fb0\u949f\u4e5f\u77a7\u4e86\u8001\u534a\u5929\u3002 \u3000\u3000\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u5899\u58c1\u4e0a\u8fd8\u6302\u6709\u4e24\u5e45\u5b57\u753b\uff0c\u753b\u9762\u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u662f\u4e2a\u9ec4\u8863\u5c11\u5e74\uff0c\u8170\u60ac\u957f\u5251\uff0c \u4e30\u795e\u4fca\u79c0\uff0c\u5973\u7684\u5374\u662f\u4f4d\u53e4\u88c5\u7f8e\u4eba\uff0c\u67f3\u53f6\u53cc\u7709\uff0c\u74dc\u5b50\u8138\u513f\uff0c\u6e05\u79c0\u4e4b\u6781\uff0c\u4f53\u6001\u5f62\u8c8c\u4e0e\u51b0\u5ddd\u5929\u5973\u672c \u6765\u751a\u4e0d\u76f8\u540c\uff0c\u4f46\u4e4d\u773c\u4e00\u770b\uff0c\u7709\u76ee\u4e4b\u95f4\uff0c\u5374\u53c8\u6709\u4e9b\u795e\u4f3c\u3002\u518d\u770b\u90a3\u5e45\u5b57\uff0c\u5b57\u8ff9\u5a1f\u79c0\uff0c\u4f3c\u4e4e\u662f\u5973\u5b50 \u7684\u4e66\u6cd5\u3002\u9898\u7684\u662f\u4e00\u9996\u8bcd\u3002\u8bcd\u9053\uff1a \u3000\u3000\u201c\u5f15\u79bb\u676f\uff0c\u6b4c\u79bb\u6028\uff0c\u8bc9\u79bb\u60c5\u3002\u662f\u8c01\u8c31\u63a0\u6c34\u9e3f\u60ca\uff0c\u79cb\u5a18\u91d1\u7f15\uff0c\u66f2\u7ec8\u4eba\u6563\u6570\u5cf0\u9752\uff1f\u60a0\u60a0\u4e0d\u5411 \u8c22\u6865\u53bb\uff0c\u68a6\u7ed5\u71d5\u4eac\u3002 \u3000\u3000\u676f\u7a7a\u6ee1\uff0c\u6b4c\u7a7a\u597d\uff0c\u7434\u7a7a\u5999\uff0c\u6708\u7a7a\u660e\uff1b\u53ea\u5170\u82d1\u4eba\u53bb\u5c18\u751f\u3002\u6c5f\u5357\u51ac\u66ae\uff0c\u6005\u5e74\u5e74\u96ea\u51b7\u98ce\u6e05\u3002\u6545 \u4eba\u5929\u9645\uff0c\u95ee\u8c01\u6765\u540c\u6170\u98d8\u96f6\u3002\u201d \u3000\u3000\u5e95\u4e0b\u4e00\u884c\u5c0f\u5b57\u662f\u201c\u5f55\u4ea1\u7236\u5fc6\u6bcd\u65e7\u4f5c\u3002\u6d63\u83b2\u3002\u201d\u9648\u5929\u5b87\u8fd9\u624d\u9192\u8d77\uff0c\u539f\u6765\u8fd9\u753b\u4e2d\u7537\u5973\uff0c\u4e43\u662f \u51b0\u5ddd\u5929\u5973\u7684\u7956\u7236\u7956\u6bcd\u2014\u2014\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\uff0c\u8fd9\u9996\u8bcd\u4e43\u662f\u5192\u6d63\u83b2\u7684\u7236\u4eb2\u5192\u8f9f\u7586\u7684\u4f5c\u54c1\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0d\u7531\u5f97\u7591\u4e91\u5927\u8d77\uff1a\u51b0\u5ddd\u5929\u5973\u662f\u6842\u4ef2\u660e\u7684\u5b59\u5973\uff0c\u6b64\u4e8b\u5df2\u7ecf\u5947\u602a\uff0c\u8fd9\u9ad8\u5c71\u4e0a\u7684\u5bab\u6bbf\uff0c \u548c\u5bab\u6bbf\u4e2d\u7684\u90a3\u8bb8\u591a\u86ee\u5973\uff0c\u66f4\u662f\u51fa\u5947\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u8eab\u4e16\uff0c\u867d\u7136\u5df2\u63ed\u4e86\u4e00\u89d2\uff0c\u4f46\u534a\u660e\u534a\u6697\u4e4b\u95f4\uff0c \u5374\u662f\u66f4\u589e\u795e\u79d8\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u665a\u9910\u7531\u4f8d\u5973\u9001\u6765\u3002\u9648\u5929\u5b87\u59cb\u7ec8\u6ca1\u6709\u89c1\u7740\u94c1\u62d0\u4ed9\u592b\u5987\u7684\u9762\u3002\u662f\u591c\uff0c\u9648\u5927\u5b57\u8f97\u8f6c\u53cd \u4fa7\uff0c\u4e00\u4f1a\u513f\u60f3\u8d77\u4e86\u90a3\u85cf\u65cf\u5c11\u5973\u829d\u5a1c\uff0c\u4e00\u4f1a\u513f\u60f3\u8d77\u4e86\u51b0\u5ddd\u5929\u5973\uff0c\u4e00\u4f1a\u513f\u53c8\u60f3\u8d77\u4e86\u81ea\u5df1\u6240\u62dc\u7684\u5e08 \u7236\u94c1\u62d0\u4ed9\u592b\u5987\u7684\u53e4\u602a\u884c\u5f84\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e0d\u80fd\u5165\u7761\uff0c\u5076\u4ece\u7a97\u53e3\u671b\u51fa\uff0c\u4f46\u89c1\u5916\u9762\u4e00\u7247\u94f6\u767d\uff0c\u5728\u51b0 \u5cf0\u7684\u96ea\u5149\u63a9\u6620\u4e4b\u4e0b\uff0c\u90a3\u4e9b\u5947\u82b1\u5f02\u8349\uff0c\u5982\u540c\u8499\u4e0a\u4e00\u5c42\u8584\u96fe\u51b0\u7eb3\uff0c\u53c8\u5982\u5728\u73bb\u7483\u4e16\u754c\u4e4b\u4e2d\uff0c\u6dfb\u4e86\u8bb8 \u591a\u7f8e\u5999\u7684\u795e\u79d8\u7684\u8272\u5f69\uff0c\u8fd9\u5947\u666f\u7684\u786e\u662f\u4eba\u95f4\u7f55\u9047\uff0c\u65f7\u4e16\u96be\u9022\uff0c\u9648\u5929\u5b87\u5fcd\u4e0d\u4f4f\u6084\u6084\u5730\u8d77\u6765\uff0c\u62ab\u4e0a \u8863\u88f3\uff0c\u63a8\u5f00\u5bab\u95e8\uff0c\u51fa\u53bb\u8d4f\u89c8\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u9635\u5fae\u7ec6\u7684\u8bed\u58f0\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u9648\u5929\u5b87\u5728\u5047\u5c71\u540e\u9762\u4e00\u4f0f\uff0c\u53ea\u89c1\u4e24\u6761\u4eba\u5f71\u6b63\u671d\u7740\u81ea\u5df1 \u8fd9\u9762\u884c\u6765\uff0c\u8d70\u5728\u524d\u9762\u7684\u662f\u81ea\u5df1\u7684\u5e08\u7236\u94c1\u62d0\u4ed9\uff0c\u9648\u5929\u5b87\u5fc3\u4e2d\u5927\u5947\uff0c\u60f3\u9053\uff1a\u4ed6\u4eec\u5728\u8fd9\u4e2a\u65f6\u5206\uff0c\u51fa \u6765\u505a\u751a\uff1f\u53c8\u6015\u51b0\u5ddd\u5929\u5973\u77a7\u89c1\u4e86\u4ed6\uff0c\u602a\u4ed6\u5728\u6df1\u591c\u4e4b\u65f6\uff0c\u5728\u5bab\u4e2d\u884c\u8d70\uff0c\u56e0\u6b64\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4e0d\u6562\u51fa\u53bb \u62db\u547c\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8d70\u5230\u9648\u5929\u5b87\u5341\u4f59\u4e08\u4e4b\u5730\uff0c\u5ffd\u7136\u505c\u7740\uff0c\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u8fd9\u6b21\u4e0a\u5c71\u62a5 \u4fe1\uff0c\u66f4\u591a\u8c22\u53d4\u4f2f\u4eec\u5bf9\u6211\u5173\u5fc3\uff0c\u4f46\u6211\u5df1\u7acb\u8a93\u6b64\u751f\u6b64\u4e16\uff0c\u518d\u4e0d\u4e0b\u5c71\u534a\u6b65\u7684\u4e86\u3002\u201d\u94c1\u62d0\u4ed9\u9053\uff1a \u201c\u4f46\uff0c\u4f46\u662f\u90a3\u4e2a\u91d1\u74f6\uff0c\u5173\u7cfb\u6781\u5176\u91cd\u5927\uff0c\u60f3\u5f53\u5e74\uff0c\u4e03\u5251\u4e0b\u5929\u5c71\uff0c\u4f60\u7684\u7956\u7236\u7956\u6bcd\uff0c\u540c\u51cc\u672a\u98ce\u5927\u4fa0 \u4e00\u8d77\uff0c\u540c\u6297\u6e05\u5175\uff0c\u4f60\u662f\u6842\u5927\u4fa0\u7684\u5b59\u5973\u513f\uff0c\u96be\u9053\u5c31\u5fcd\u89c1\u897f\u85cf\u6ca6\u4e3a\u6ee1\u864f\u7684\u85e9\u5c5e\u5417\uff1f\u8fd9\u91d1\u74f6\u4e00\u5230\uff0c \u897f\u85cf\u53ef\u5c31\u5b8c\u5566\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7406\u8fd9\u4e9b\u4e8b\u60c5\u3002\u201d\u58f0\u8c03\u5341\u5206\u575a\u51b3\uff0c\u6beb\u65e0\u633d\u56de\u4f59\u5730\u3002\u94c1\u62d0\u4ed9\u53f9\u4e86 \u53e3\u6c14\uff0c\u6b63\u60f3\u518d\u8bf4\uff0c\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u53c8\u9053\uff1a\u201c\u9664\u975e\u8fd9\u5ea7\u51b0\u5cf0\u5012\u4e86\uff0c\u5426\u5219\u6211\u7684\u5fc3\u5fd7\u4e0d\u79fb\u3002\u4f60\u4eec\u592b \u5987\u8fdc\u6765\uff0c\u6211\u672c\u8be5\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u62db\u5f85\u4f60\u4eec\u5c0f\u4f4f\u51e0\u65e5\uff0c\u8fd9\u8bdd\u4ea6\u8bf4\u8fc7\u4e86\u3002\u65e0\u5948\u6211\u4ee5\u524d\u66fe\u53d1\u8fc7\u8a93 \u8a00\u3002\u6709\u8c01\u6562\u529d\u6211\u4e0b\u5c71\u7684\uff0c\u5373\u7b97\u4ed6\u662f\u6211\u7684\u957f\u8f88\uff0c\u6211\u4e5f\u4e0d\u80fd\u62db\u5f85\u3002\u94c1\u62d0\u4ed9\uff0c\u591a\u8c22\u4f60\u8fd9\u6b21\u7684\u5fc3\u4e8b\uff0c \u660e\u65e5\u6211\u53eb\u4f8d\u5973\u9001\u4f60\u4eec\u4e0b\u53bb\uff0c\u4ee5\u540e\u4f60\u4eec\u4e5f\u4e0d\u5fc5\u518d\u6765\u63a2\u6211\u5566\u3002\u201d\u51b0\u5ddd\u5929\u5973\u80cc\u5411\u7740\u9648\u5929\u5b87\uff0c\u9648\u5929\u5b87 \u77a7\u4e0d\u89c1\u5979\u7684\u9762\u5bb9\uff0c\u5979\u8bf4\u8bdd\u7684\u58f0\u8c03\uff0c\u542c\u6765\u4ea6\u751a\u6e29\u67d4\uff0c\u4f46\u5374\u662f\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u5c31\u5982\u4e00\u4e2a\u5973\u738b\uff0c\u5ba3 \u5e03\u4e86\u4e00\u9053\u547d\u4ee4\u4e00\u822c\uff0c\u6b64\u8a00\u4e00\u51fa\uff0c\u94c1\u62d0\u4ed9\u767b\u65f6\u9759\u9ed8\u3002\u9648\u5929\u5b57\u4ea6\u662f\u8be7\u5f02\u975e\u5e38\uff0c\u5fc3\u9053\uff1a\u8fd9\u51b0\u5ddd\u5929\u5973 \u600e\u7684\u8fd9\u6837\u4e0d\u8fd1\u4eba\u60c5\uff0c\u8fd9\u4e0d\u662f\u516c\u7136\u4e0b\u4e86\u9010\u5ba2\u4ee4\u5417\uff1f\u4e0d\u77e5\u600e\u7684\uff0c\u9648\u5929\u5b87\u5ffd\u611f\u5bf9\u8fd9\u5982\u540c\u4ed9\u5883\u7684\u5730 \u65b9\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u7559\u604b\uff0c\u5c24\u5176\u5bf9\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\uff0c\u66f4\u662f\u4f9d\u4f9d\u4e0d\u820d\u3002\u60f3\u8d77\u660e\u65e5\u5c31\u8981\u968f\u5e08\u7236\u4e0b \u5c71\uff0c\u4ee5\u540e\u518d\u4e5f\u65e0\u7f18\u5230\u6b64\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u6005\u7136\u3002 \u3000\u3000\u4f46\u89c1\u7389\u5b87\u65e0\u5c18\uff0c\u51b0\u5cf0\u6620\u6708\uff0c\u4e07\u7c41\u65e0\u58f0\uff0c\u6ee1\u56ed\u5b50\u9759\u5bc2\u5bc2\u7684\uff0c\u9759\u9ed8\u4e86\u8bb8\u4e45\u8bb8\u4e45\uff0c\u624d\u542c\u5f97\u94c1\u62d0 \u4ed9\u9053\uff1a\u201c\u5192\u72af\u59d1\u5a18\uff0c\u4e0d\u6562\u6c42\u6055\uff0c\u59d1\u5a18\u5429\u5490\uff0c\u9075\u547d\u5c31\u662f\u3002\u201d\u968f\u5373\u53c8\u542c\u5230\u811a\u6b65\u58f0\u6e10\u8fdc\u6e10\u6c93\uff0c\u9648\u5929 \u5b87\u4ece\u5047\u5c71\u77f3\u540e\u671b\u51fa\u6765\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u94c1\u62d0\u4ed9\u7684\u80cc\u5f71\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u5401\u4e86\u53e3\u6c14\uff0c\u6b65\u51fa\u5047\u5c71\uff0c\u5ffd\u89c1\u524d\u9762\u5206\u82b1\u62c2\u67f3\uff0c\u53c8\u8d70\u51fa\u4e00\u4eba\uff0c\u9648\u5929\u5b87\u6b63\u60f3\u8eb2\u907f\uff0c\u53ea\u542c \u5f97\u4e00\u4e2a\u94f6\u94c3\u4f3c\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u8fd8\u672a\u7761\u4e48\uff1f\u201d\u5b9a\u775b\u4e00\u770b\uff0c\u6b63\u662f\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\u829d\u5a1c\u3002 \u5934\u4e0a\u62ab\u770b\u767d\u7eb1\uff0c\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\u5728\u9ed1\u591c\u91cc\u95ea\u95ea\u653e\u5149\uff0c\u5634\u89d2\u4ecd\u7136\u5b55\u80b2\u7740\u90a3\u79cd\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1 \u7684\u5fae\u7b11\u3002\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u867d\u7136\u662f\u98ce\u534e\u7edd\u4ee3\uff0c\u7f8e\u82e5\u5929\u4eba\uff0c\u4f46\u4e0d\u98df\u4eba\u95f4\u70df\u706b\u7684\u4ed9\u5b50\uff0c\u603b \u662f\u4ee4\u4eba\u4e0d\u6562\u4eb2\u8fd1\uff1b\u8fd9\u5c11\u5973\u867d\u5219\u4e5f\u4ee4\u4eba\u611f\u5230\u795e\u79d8\uff0c\u6bd4\u8f83\u8d77\u6765\uff0c\u5374\u662f\u4ee4\u4eba\u611f\u5230\u6613\u4e8e\u63a5\u8fd1\u3002 \u3000\u3000\u90a3\u85cf\u65cf\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5c61\u6b21\u6551\u547d\u4e4b\u6069\uff0c\u53ea\u53ef\u60dc\u4f60\u660e\u5929\u5c31\u8981\u8d70\u4e86\u3002\u201d\u9648\u5929\u5b87 \u9053\uff1a\u201c\u55ef\uff0c\u9002\u624d\u7684\u4e8b\u4f60\u90fd\u77e5\u9053\u4e86\uff1f\u201d\u829d\u5a1c\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\u8bf4\uff0c\u4f60\u5e08\u7236\u8981\u53bb\u62a2\u593a\u91d1 \u74f6\uff0c\u53ea\u6050\u6709\u6027\u547d\u4e4b\u9669\uff0c\u53eb\u4f60\u5c0f\u5fc3\u3002\u201d\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6211\u7ed9\u4ed6\u4eec\u5f04\u5f97\u83ab\u540d\u5176\u5999\uff0c\u7a76\u7adf \u8981\u62a2\u593a\u7684\u91d1\u74f6\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u829d\u5a1c\u9053\uff0c\u201c\u4f60\u6ca1\u6709\u542c\u8bf4\u8fc7\u91d1\u672c\u5df4\u74f6\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6ca1\u6709\u542c \u8fc7\u3002\u201d \u3000\u3000\u90a3\u85cf\u65cf\u5c11\u5973\u79c0\u7709\u5fae\u8e59\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u54b1\u4eec\u8fd9\u91cc\u7684\u8fbe\u8d56\u73ed\u7985\u4e24\u4f4d\u6d3b \u4f5b\uff0c\u4ee5\u53ca\u547c\u56fe\u514b\u56fe\u7b49\u5927\u6d3b\u4f5b\u90fd\u662f\u8f6c\u4e16\u7684\uff1f\u201d\u539f\u6765\u897f\u85cf\u5bf9\u8fbe\u8d56\u5587\u561b\u3001\u73ed\u7985\u5587\u561b\uff0c\u4ee5\u53ca\u6b21\u4e00\u7ea7\u7684 \u547c\u56fe\u514b\u56fe\uff08\u6d3b\u4f5b\u5c01\u53f7\uff09\uff0c\u90fd\u79f0\u4e3a\u6d3b\u4f5b\uff0c\u8ba4\u4e3a\u4ed6\u4eec\u5706\u5bc2\uff08\u6b7b\uff09\u4e4b\u540e\u53ef\u4ee5\u8f6c\u751f\u3002\u4f46\u662f\u7a76\u7adf\u751f\u5728\u54ea \u91cc\uff1f\u4f55\u65f6\u8f6c\u751f\uff1f\u5374\u662f\u4e00\u4e2a\u5927\u95ee\u9898\u3002\u4ee5\u5f80\u7684\u89c4\u77e9\u53ea\u51ed\u5f53\u65f6\u5f53\u5730\u6709\u58f0\u671b\u7684\u6d3b\u4f5b\u6216\u8005\u201c\u5439\u5fe0\u201d\uff08\u5deb \u5e08\uff09\u964d\u795e\u4f5c\u6cd5\uff0c\u6307\u5b9a\u4e00\u4e2a\u65b9\u5411\uff0c\u53eb\u4eba\u5bfb\u627e\u3002\u4f46\u5f80\u5f80\u5404\u6307\u4e00\u4eba\uff0c\u5f04\u5230\u540c\u65f6\u51fa\u73b0\u51e0\u4e2a\u8f6c\u751f\u7684\u8fbe\u8d56 \u6216\u8005\u73ed\u7985\uff0c\u771f\u5047\u96be\u5206\uff0c\u65e0\u6240\u9002\u4ece\uff0c\u751a\u81f3\u53d1\u751f\u4e89\u6267\uff0c\u5f15\u8d77\u7ea0\u7eb7\u3002\u4f8b\u5982\u5c31\u5728\u9a7b\u85cf\u5927\u81e3\u798f\u5eb7\u5b89\u7684\u4efb \u5185\uff0c\u5c31\u66fe\u51fa\u73b0\u8fc7\u4e24\u4e2a\u8f6c\u4e16\u7684\u7b2c\u516d\u4e16\u8fbe\u8d56\u5587\u561b\uff0c\u5f15\u8d77\u91cd\u5927\u4e89\u6267\u3002\u9648\u5929\u5b87\u5728\u897f\u85cf\u957f\u5927\uff0c\u5bf9\u8fd9\u4e9b\u4e8b \u60c5\uff0c\u5f53\u7136\u6e05\u695a\u3002 \u3000\u3000\u9648\u5929\u5b57\u70b9\u4e86\u70b9\u5934\uff0c\u829d\u5a1c\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6d3b\u4f5b\u8f6c\u4e16\uff0c\u65f6\u65f6\u53d1\u751f\u7ea0\u7eb7\uff0c\u6240\u4ee5\u542c\u8bf4\u6e05\u671d\u7684\u7687\u5e1d\u8981 \u9881\u53d1\u4e00\u4e2a\u91d1\u672c\u5df4\u74f6\uff08\u672c\u5df4\u662f\u85cf\u8bed\u201c\u74f6\u5b50\u201d\u7684\u610f\u601d\u3002\uff09\u82e5\u6709\u7ea0\u7eb7\uff0c\u5c31\u53eb\u5439\u5fe0\u5c06\u5404\u4e2a\u88ab\u8ba4\u4e3a\u662f\u8f6c \u4e16\u6d3b\u4f5b\u7684\u540d\u5b57\uff0c\u5404\u5199\u4e00\u7b7e\uff0c\u653e\u5728\u74f6\u5185\uff0c\u5bf9\u4f17\u62c8\u5b9a\u3002\u542c\u8bf4\u8fd9\u4e2a\u91d1\u672c\u5df4\u74f6\u5c31\u5feb\u8981\u7531\u5317\u4eac\u9881\u53d1\uff0c\u5230 \u65f6\u8fbe\u8d56\u73ed\u7985\u4ee5\u53ca\u5404\u50e7\u4fd7\u5b98\u5458\uff0c\u90fd\u8981\u4e3e\u884c\u6781\u9686\u91cd\u7684\u8fce\u63a5\u4eea\u5f0f\uff0c\u7136\u540e\u5c06\u5b83\u4f9b\u5728\u62c9\u8428\u5e02\u4e2d\u5fc3\u7684\u5927\u662d \u5bfa\u697c\u4e0a\uff0c\u4ece\u6b64\u6c38\u4f20\u540e\u4e16\uff0c\u4f5c\u4e3a\u897f\u85cf\u6700\u6700\u91cd\u8981\u7684\u5723\u7269\u3002\u4f60\u60f3\u8fd9\u6837\u91cd\u8981\u7684\u5723\u96f6\u7269\uff0c\u8be5\u6709\u591a\u5c11\u9ad8\u624b \u4fdd\u62a4\uff1f\u4f60\u7684\u5e08\u7236\u8981\u53bb\u62a2\u593a\uff0c\u8fd9\u53ef\u4e0d\u662f\u5bfb\u6b7b\u5417\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u6b63\u6b32\u95ee\u5979\u600e\u4f1a\u77e5\u9053\u6b64\u4e8b\uff0c\u60f3\u8d77\u5979\u662f\u6c81\u5e03\u85e9\u738b\u7684\u5973\u513f\uff0c\u5c31\u4e0d\u518d\u95ee\u4e86\u3002\u9648\u5929\u5b87\u7684\u7236\u4eb2 \u662f\u6e05\u5ef7\u6d3e\u9a7b\u897f\u85cf\u7684\u4e00\u4e2a\u5b98\u5458\uff0c\u9648\u5929\u5b87\u867d\u7136\u5bf9\u6ee1\u6d32\u4eba\u4e5f\u4e0d\u5927\u6ee1\u610f\uff0c\u4f46\u5374\u9690\u9690\u89c9\u5f97\uff0c\u671d\u5ef7\u8fd9\u4ef6\u4e8b \u60c5\uff0c\u4e5f\u4f3c\u4e4e\u505a\u5f97\u4e0d\u9519\uff0c\u6700\u5c11\u53ef\u4ee5\u51cf\u5c11\u897f\u85cf\u7684\u7ea0\u7eb7\uff0c\u4e0d\u660e\u4ed6\u7684\u5e08\u7236\u4e3a\u4f55\u8981\u53cd\u5bf9\uff1f \u3000\u3000\u829d\u5a1c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4eec\u897f\u85cf\u4eba\u6700\u5d07\u62dc\u6d3b\u4f5b\uff0c\u82e5\u7136\u4f60\u4eec\u6c49\u4eba\u6bc1\u574f\u4e86\u8fd9\u4e2a\u91d1\u672c\u5df4\u74f6\uff0c\u62a2 \u8d70\u4e86\u6211\u4eec\u7684\u5723\u7269\uff0c\u90a3\u4e48\u6c49\u85cf\u4e4b\u95f4\u7684\u4ec7\u6068\uff0c\u6050\u6015\u4f1a\u8d8a\u7ed3\u8d8a\u6df1\u3002\u542c\u8bf4\u4f60\u4eec\u6c49\u4eba\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e9b\u4fa0 \u58eb\uff0c\u751f\u6015\u4eec\u897f\u85cf\u63a5\u53d7\u4e86\u91d1\u672c\u5df4\u74f6\u4e4b\u540e\uff0c\u653f\u6559\u5236\u5ea6\u90fd\u53d7\u671d\u5ef7\u7684\u89c4\u5b9a\uff0c\u5c31\u8981\u53d8\u6210\u6ee1\u6e05\u7684\u85e9\u5c5e\uff0c\u56e0 \u6b64\u8a93\u6b7b\u4ece\u4e2d\u7834\u574f\uff0c\u4f46\u53ea\u6050\u8fd9\u756a\u597d\u5fc3\uff0c\u6211\u4eec\u897f\u85cf\u4eba\u4f1a\u628a\u5b83\u5f53\u6210\u6076\u610f\u3002\u4f60\u8fd8\u662f\u529d\u4f60\u7684\u5e08\u7236\u4e0d\u8981\u63d2 \u624b\u7684\u597d\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u5e08\u7236\u7684\u813e\u6c14\u53e4\u602a\uff0c\u6211\u8fd8\u662f\u65b0\u8fd1\u62dc\u5e08\uff0c\u600e\u6562\u5728\u4ed6\u8ddf\u524d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u4e24\u4eba\u9759\u9ed8\u4e86\u4e00\u4f1a\uff0c\u9648\u5929\u5b87\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u4f60\u662f\u600e\u6837\u548c\u8428\u8fe6\u7684\u571f\u53f8\u7ed3\u4ec7\u7684\uff1f\u201d\u8bdd\u51fa\u4e4b\u540e\uff0c\u5ffd \u89c9\u592a\u8fc7\u5192\u6627\uff0c\u4ea4\u6d45\u8a00\u6df1\uff0c\u53ea\u6015\u81ea\u8ba8\u6ca1\u8da3\u3002\u829d\u5a1c\u5374\u5e76\u4e0d\u5728\u610f\uff0c\u8f7b\u63a0\u4e91\u9b13\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u66fe\u5728 \u571f\u53f8\u5bb6\u4e2d\u6551\u8fc7\u6211\u7684\u6027\u547d\uff0c\u4f60\u4e0d\u95ee\u6211\uff0c\u6211\u4e5f\u8be5\u5bf9\u4f60\u8bf4\u8bf4\u3002\u6211\u4e14\u7ed9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\u3002\u9664\u4e86\u5929\u5973\u59d0\u59d0 \u4e4b\u5916\uff0c\u4f60\u662f\u8fd9\u4e16\u754c\u4e0a\u7b2c\u4e8c\u4e2a\u542c\u6211\u6545\u4e8b\u7684\u4eba\u3002\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u636e\u8bf4\u5728\u4f60\u4eec\u6c49\u4eba\u53eb\u505a\u5510\u671d\u7684\u65f6 \u5019\uff0c\u5410\u8c37\u6d51\uff08\u4eca\u9752\u6d77\u4e00\u5e26\uff09\u5165\u5bc7\u897f\u85cf\uff0c\u897f\u85cf\u6709\u4e00\u4e2a\u9a81\u52c7\u5584\u6218\u7684\u5c06\u519b\uff0c\u6253\u9000\u4e86\u5410\u8c37\u6d51\u7684\u519b\u961f\u3002 \u4e0d\u4e45\u85cf\u738b\u5927\u5a5a\uff0c\u7687\u540e\u5c31\u662f\u4f60\u4eec\u5510\u671d\u7684\u6587\u6210\u516c\u4e3b\uff0c\u6233\u738b\u8d81\u7740\u7ed3\u5a5a\u5927\u5178\uff0c\u5927\u5c01\u6709\u6218\u529f\u7684\u5c06\u58eb\uff0c\u90a3 \u4f4d\u5c06\u519b\u529f\u52b3\u6700\u5927\uff0c\u85cf\u738b\u4fbf\u8d4f\u7ed9\u4ed6\u8dd1\u9a6c\u4e00\u65e5\u4e4b\u5730\uff0c\u8ba9\u4ed6\u81ea\u7acb\uff0c\u90a3\u4f4d\u5c06\u519b\u5341\u5206\u5584\u4e8e\u9a91\u9a6c\uff0c\u7a7f\u5c71\u6d89 \u6c34\u5e76\u4e0d\u62e9\u8def\uff0c\u636e\u8bf4\u4e00\u65e5\u4e4b\u5185\uff0c\u4fbf\u8dd1\u4e86\u4e94\u5343\u591a\u91cc\u7684\u4e00\u4e2a\u5927\u573a\u5b50\uff0c\u4e8e\u662f\u8fd9\u7247\u571f\u5730\u5f52\u4ed6\u6240\u6709\uff0c\u53d7\u5c01 \u85e9\u738b\u7684\u8fd9\u4f4d\u5c06\u519b\u4fbf\u662f\u6211\u7684\u5148\u7956\u3002 \u3000\u3000\u4ee3\u4ee3\u76f8\u4f20\uff0c\u4f20\u5230\u4e86\u7b2c\u4e94\u4ee3\u4fbf\u662f\u6211\u7684\u7236\u4eb2\u6c81\u5e03\u85e9\u738b\uff0c\u7ba1\u8f96\u56db\u5927\u571f\u53f8\uff0c\u5176\u4e2d\u4ee5\u8428\u8fe6\u571f\u53f8\u6743\u52bf \u6700\u5927\uff0c\u4ed6\u7684\u59bb\u5b50\u53c8\u6b63\u662f\u6211\u5802\u4f2f\u7684\u5973\u513f\uff0c\u4e0a\u53f8\u4e0b\u5c5e\u7684\u5173\u7cfb\u52a0\u4e0a\u4eb2\u621a\u7684\u5173\u7cfb\uff0c\u4e24\u5bb6\u7684\u6765\u5f80\u5c31\u66f4\u4eb2 \u5bc6\u4e86\u3002 \u3000\u3000\u201c\u6211\u7684\u7236\u4eb2\u6700\u7231\u6253\u730e\uff0c\u60f3\u4e0d\u5230\u6709\u4e00\u5929\u4ed6\u4e3a\u4e86\u8ffd\u8d76\u4e00\u53ea\u91d1\u6bdb\u91ce\u72d0\uff0c\u6ca1\u7559\u795e\u88ab\u5934\u4e0a\u7684\u6811\u679d\u649e \u7740\uff0c\u5815\u9a6c\u60e8\u6b7b\u3002\u6211\u6ca1\u6709\u59d0\u59b9\uff0c\u4e5f\u6ca1\u6709\u5144\u5f1f\uff0c\u4f9d\u7167\u957f\u8f88\u7684\u516c\u8bae\uff0c\u8be5\u7531\u6211\u7684\u5ae1\u4eb2\u53d4\u53d4\u7ee7\u627f\uff0c\u7136\u540e \u624d\u662f\u6211\u7684\u5802\u5144\u5f1f\u4eec\u3002\u60f3\u4e0d\u5230\u5947\u602a\u7684\u4e8b\u60c5\u63a5\u4e8c\u8fde\u4e09\u5730\u53d1\u751f\u4e86\uff0c\u5148\u662f\u6211\u7684\u90a3\u4f4d\u53d4\u53d4\u5728\u559d\u4e86\u4e00\u7897\u9a6c \u5976\u4e4b\u540e\uff0c\u5ffd\u7136\u6d51\u8eab\u9752\u80bf\u5f53\u665a\u5c31\u54bd\u6c14\u4e86\uff0c\u63a5\u7740\u4ed6\u7684\u513f\u5b50\u5728\u73a9\u6349\u8ff7\u85cf\u7684\u65f6\u5019\uff0c\u53c8\u5ffd\u7136\u4ece\u6811\u4e0a\u8dcc\u4e0b \u6765\u6454\u6b7b\u3002\u63a5\u7740\u6211\u7684\u5802\u5144\u5f1f\u4eec\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u83ab\u540d\u5176\u5999\u7684\u5f97\u602a\u75c5\u66b4\u6bd9\uff0c\u6b7b\u8005\u90fd\u662f\u6d51\u8eab\u9752\u80bf\uff0c\u4e03\u7a8d \u6d41\u8840\uff0c\u8001\u4eba\u4eec\u8bf4\u662f\u9b3c\u9b42\u4f5c\u795f\uff0c\u5168\u5bb6\u90fd\u8eb2\u5728\u5bb6\u4e2d\u7684\u795e\u5e99\u91cc\uff0c\u795e\u5e99\u5916\u8fb9\u4e0a\u4e86\u5927\u94c1\u9501\uff0c\u5e76\u7528\u77f3\u7070\u56f4 \u7740\u9662\u5899\u6492\u4e86\u4e00\u9053\u767d\u7ebf\uff0c\u636e\u8bf4\u53ef\u4ee5\u62e6\u7740\u9b3c\u9b42\u4e0d\u80fd\u5165\u6765\uff0c\u5440\uff0c\u90a3\u4e9b\u65e5\u5b50\u53ef\u6015\u6781\u4e86\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u6253\u4e86\u4e00\u4e2a\u5bd2\u54d7\uff0c\u773c\u524d\u7f8e\u4e3d\u7684\u666f\u8272\u4e5f\u53d8\u5f97\u9634\u68ee\u53ef\u6016\u3002\u53ea\u542c\u5f97\u829d\u5a1c\u7eed\u9053\uff1a\u201c\u6211\u7684\u5802\u5144 \u5f1f\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u66b4\u6bd9\u8eab\u4ea1\uff0c\u4e0d\u5230\u4e00\u4e2a\u6708\uff0c\u90fd\u6b7b\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u8fd9\u4e00\u5929\uff0c\u6211\u6700\u540e\u4e00\u4e2a\u5802\u5f1f\uff0c\u53ea\u6709 \u4e09\u5c81\u5927\u7684\u5b69\u5b50\u4e5f\u6b7b\u4e86\uff0c\u6211\u5bb3\u6015\u975e\u5e38\uff0c\u5fc3\u91cc\u5934\u6709\u4e2a\u9884\u5146\uff0c\u597d\u50cf\u611f\u5230\u81ea\u5df1\u4e5f\u5c06\u4e0d\u4e45\u4e8e\u4eba\u4e16\u3002\u8fd9\u5929 \u662f\u6211\u7236\u4eb2\u7684\u56de\u9b42\u796d\uff08\u85cf\u4fd7\u8ff7\u4fe1\u6b7b\u540e\u4e8c\u5341\u516b\u5929\uff0c\u9b42\u9b44\u53ef\u4ee5\u56de\u6765\uff0c\u5c4a\u65f6\u5bb6\u4eba\u8981\u4e3e\u884c\u56de\u9b42\u796d\u3002\uff09\u672c \u8be5\u5728\u738b\u5e9c\u8bbe\u7075\uff0c\u8ba9\u65cf\u4eba\u62dc\u796d\uff0c\u4f46\u4e3a\u4e86\u8fd9\u4e00\u8fde\u4e32\u53e4\u602a\u7684\u53ef\u6016\u7684\u4e8b\u4ef6\uff0c\u6211\u4eec\u90fd\u4e0d\u6562\u51fa\u795e\u5e99\u534a\u6b65\uff0c \u522b\u4eba\u4e5f\u4e0d\u6562\u5230\u6211\u5bb6\u91cc\u6765\uff0c\u5bb3\u6015\u9b3c\u9b42\u4f5c\u795f\u3002 \u3000\u3000\u201c\u4f46\u5374\u6709\u4e00\u4eba\u4e0d\u6015\uff0c\u8fd9\u4eba\u662f\u6211\u7684\u8205\u8205\uff0c\u540d\u53eb\u6d1b\u73e0\uff1b\u4f60\u542c\u8fc7\u8fd9\u540d\u5b57\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u542c \u7236\u4eb2\u8bf4\u8fc7\u3002\u4ed6\u662f\u6c81\u5e03\u7684\u7b2c\u4e00\u540d\u52c7\u58eb\uff0c\u6211\u5e08\u7236\u8bf4\u4ed6\u662f\u5927\u9f99\u6d3e\u6709\u6570\u7684\u4eba\u7269\u3002\u201d\u829d\u5a1c\u70b9\u4e86\u70b9\u5934\uff0c \u9053\uff1a\u201c\u6211\u7684\u8205\u8205\u672c\u4e8b\u5f88\u5927\uff0c\u4ed6\u4e5f\u559c\u6b22\u6253\u730e\uff0c\u4ed6\u4e00\u4eba\u53ef\u4ee5\u964d\u4f0f\u4e00\u53ea\u7280\u725b\uff0c\u4ed6\u4e0d\u5bb3\u6015\u9b3c\u9b42\uff0c\u90a3\u4e00 \u5929\u4ed6\u6765\u4e86\uff0c\u665a\u4e0a\u4fbf\u540c\u6211\u4eec\u4e00\u8d77\u5b88\u7075\uff0c\u4f34\u6211\u4eec\u8fc7\u591c\u3002\u201d \u3000\u3000\u201c\u6211\u5bb3\u6015\u5f97\u5f88\uff0c\u672c\u6765\u6211\u6bcf\u5929\u665a\u4e0a\uff0c\u662f\u8ddf\u5988\u5988\u4e00\u95f4\u623f\u5b50\u7761\u7684\uff0c\u8fd9\u4e00\u665a\u6211\u8981\u8205\u8205\u8ddf\u6211\u540c\u623f\uff0c \u6211\u5988\u8981\u5b88\u4e22\u3002\u4e94\u66f4\u624d\u7761\uff0c\u548c\u4e24\u4e2a\u4f8d\u5973\u5728\u5916\u9762\u5b88\u7075\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u665a\u6211\u600e\u6837\u4e5f\u7761\u4e0d\u7740\uff0c\u6709\u4ec0\u4e48\u98ce\u5439\u8349\u52a8\uff0c\u90fd\u4ee5\u4e3a\u662f\u6211\u7238\u7238\u9b3c\u9b42\u56de\u6765\u3002\u4f46\u5fc3\u91cc\u4e00\u60f3\uff0c \u7238\u7238\u751f\u524d\u6700\u7231\u6211\uff0c\u82e5\u7136\u4ed6\u53d8\u4e86\u9b3c\u9b42\uff0c\u5080\u8be5\u4fdd\u4f51\u6211\uff0c\u4fdd\u4f51\u6211\u7684\u6bcd\u4eb2\uff0c\u8ba9\u6211\u4eec\u4e0d\u53d7\u5176\u4ed6\u91ce\u9b3c\u7684\u4fb5 \u5bb3\u3002\u201d \u3000\u3000\u201c\u4e09\u66f4\u8fc7\u53bb\u4e86\uff0c\u56db\u66f4\u4e5f\u6572\u4e86\uff0c\u5bb6\u4eba\u5a62\u4ec6\u90fd\u7761\u4e86\uff0c\u795e\u5e99\u91cc\u4e00\u7247\u5bc2\u9759\uff0c\u53ea\u6709\u5916\u9762\u90a3\u5ea7\u897f\u6d0b\u65f6 \u8fb0\u949f\u6ef4\u7b54\u6ef4\u7b54\u5730\u54cd\u7740\uff0c\u9759\u5f97\u4ee4\u4eba\u5fc3\u8df3\u3002\u623f\u91cc\u6709\u4e24\u5f20\u5e8a\uff0c\u6211\u7761\u91cc\u9762\u90a3\u5f20\uff0c\u8205\u8205\u7761\u5916\u9762\u90a3\u5f20\uff0c\u6211 \u7761\u4e0d\u7740\u4e00\u7741\u5927\u773c\u775b\uff0c\u4ece\u95e8\u7f1d\u91cc\u77a7\u51fa\u53bb\uff0c\u5916\u9762\u70db\u5149\u6447\u6643\uff1a\u6211\u60f3\u8d77\u5988\u5988\u4e00\u4e2a\u4eba\u5728\u5916\u9762\uff0c\u5f88\u5bb3\u6015\u3002 \u60f3\u5927\u58f0\u53eb\u56b7\uff0c\u53eb\u5988\u5988\u4e0d\u8981\u5b88\u4e86\uff0c\u5feb\u70b9\u56de\u6765\u4f34\u6211\u3002\u8fd8\u6ca1\u6709\u53eb\u51fa\u58f0\uff0c\u5ffd\u7136\u5916\u9762\u7684\u70db\u5149\uff0c\u4e00\u4e0b\u5b50\u5168 \u90e8\u7184\u706d\u3002\u53ea\u542c\u5f97\u5988\u5988\u4e00\u58f0\u5389\u53eb\uff0c\u53eb\u5f97\u6211\u6c57\u6bdb\u76f4\u7ad6\uff0c\u9661\u7136\u95f4\u8205\u8205\u5927\u559d\u4e00\u58f0\uff0c\u547c\u7684\u4e00\u62f3\u6363\u51fa\uff0c\u5e8a \u677f\u4e5f\u8f70\u9686\u584c\u4e86\uff0c\u8fd9\u65f6\u6211\u624d\u77a7\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u4e0e\u6211\u8205\u8205\u6253\u4f5c\u4e00\u56e2\u3002 \u3000\u3000\u6253\u4e86\u4e00\u9635\uff0c\u8205\u8205\u5c06\u4ed6\u8feb\u51fa\u623f\u5916\uff0c\u4e0d\u51c6\u4ed6\u6765\u4fb5\u5bb3\u6211\uff0c\u4ece\u623f\u5b50\u91cc\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u4e24\u6761\u9ed1\u5f71\uff0c\u7eb5 \u8dc3\u640f\u51fb\uff0c\u6bcf\u62f3\u6253\u51fa\uff0c\u90fd\u662f\u547c\u547c\u631f\u51e4\uff0c\u5df2\u5206\u4e0d\u51fa\u8c01\u662f\u8205\u7236\uff0c\u8c01\u662f\u523a\u5ba2\uff0c\u684c\u6905\u5bb6\u5177\u90fd\u7ed9\u6253\u6298\uff0c\u4e52 \u4e52\u4e53\u4e53\u7684\u4e71\u54cd\u3002\u5ffd\u542c\u5f97\u6211\u8205\u7236\u53c8\u5927\u53eb\u4e00\u58f0\uff0c\u58f0\u97f3\u60e8\u5389\u3002\u6211\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u4ee5\u4e3a\u8205\u7236\u4e5f\u4e2d\u4e86\u90a3 \u4eba\u7684\u6bd2\u624b\uff0c\u9669\u9669\u6655\u4e86\u8fc7\u53bb\u3002\u4f46\u8fd9\u4e00\u58f0\u4e4b\u540e\uff0c\u5916\u9762\u53c8\u5ffd\u7136\u9759\u4e86\u4e0b\u6765\uff0c\u6211\u7741\u5f00\u773c\u775b\uff0c\u611f\u89c9\u6709\u4eba\u5728 \u8f7b\u8f7b\u629a\u6478\u6211\u7684\u5934\u53d1\uff0c\u6211\u9053\uff1a\u201c\u662f\u8205\u8205\u5417\uff1f\u201d\u9648\u5929\u5b87\u542c\u5f97\u7d27\u5f20\u4e4b\u6781\uff0c\u4e0d\u542f\u89c9\u4e5f\u7528\u540c\u6837\u53e3\u543b\u95ee \u9053\uff1a\u201c\u662f\u8205\u8205\u5417\uff1f\u201d \u3000\u3000\u829d\u5a1c\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u8205\u8205\u3002\u4ed6\u6709\u70b9\u6c14\u5598\uff0c\u4f46\u58f0\u97f3\u5374\u5f88\u8feb\u4fc3\u3002\u800c\u4e14\u98a4\u6296\uff0c\u4ed6\u8bf4\uff1a \u2018\u55ef\uff0c\u829d\u5a1c\u3002\u662f\u6211\uff0c\u5feb\u8ddf\u6211\u8d70\u3002\u2019\u6211\u5df2\u5413\u5f97\u4e0d\u4f1a\u8d70\u52a8\uff0c\u4ed6\u5c06\u6211\u4e00\u628a\u62b1\u4e86\u8d77\u6765\uff0c\u8d70\u51fa\u5916\u9762\uff0c\u6211 \u9053\uff1a\u2018\u5988\u5988\u5462\uff1f\u53eb\u5988\u5988\u4e5f\u4e00\u540c\u8d70\u3002\u2019\u8205\u8205\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u56de\u7b54\u6211\uff0c\u8e22\u5f00\u795e\u5e99\u5e99\u95e8\uff0c\u8de8\u4e0a\u4e00\u5339\u6218 \u9a6c\uff0c\u8fde\u591c\u5954\u9003\u3002\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u5988\u5988\u548c\u90a3\u4e24\u4e2a\u4f8d\u5973\uff0c\u90e8\u7ed9\u523a\u5ba2\u6740\u4e86\uff0c\u90a3\u523a\u5ba2\u672c\u6765\u8981\u6740\u6211\u7684\uff0c \u4e0d\u662f\u8205\u8205\uff0c\u6211\u65e9\u5df2\u4e27\u547d\u4e86\u3002 \u3000\u3000\u201c\u8205\u8205\u9a6c\u4e0d\u505c\u8e44\uff0c\u4e00\u591c\u4e4b\u95f4\uff0c\u75be\u8dd1\u4e8c\u767e\u591a\u91cc\uff0c\u4ed6\u8fd9\u624d\u544a\u8bc9\u6211\uff0c\u6211\u7684\u53d4\u53d4\u548c\u5802\u5144\u5f1f\u4eec\uff0c\u90fd \u662f\u7ed9\u90a3\u4e2a\u523a\u5ba2\u5bb3\u6b7b\u7684\uff0c\u90a3\u523a\u5ba2\u7ec3\u6709\u4e00\u79cd\u6b79\u6bd2\u7684\u529f\u592b\uff0c\u53eb\u505a\u2018\u4e03\u9634\u638c\u2019\uff0c\u53ea\u8981\u8eab\u4f53\u4efb\u4f55\u90e8\u5206\uff0c \u4e2d\u4e86\u4ed6\u7684\u4e00\u638c\uff0c\u4fbf\u4f1a\u6d51\u8eab\u9752\u949f\uff0c\u4e03\u7a8d\u6d41\u8840\u800c\u4ea1\uff01\u4ed6\u6628\u665a\u62fc\u4e86\u6027\u547d\uff0c\u867d\u7136\u5c06\u90a3\u4eba\u6253\u9000\uff0c\u4f46\u4e5f\u5df2 \u4e2d\u4e86\u4e00\u638c\u3002 \u3000\u3000\u201c\u6211\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u6025\u95ee\u600e\u4e48\u529e\uff1f\u8205\u8205\u8bf4\uff0c\u4ed6\u7ec3\u6709\u5185\u529f\uff0c\u53ef\u4ee5\u62b5\u5fa1\u4e03\u65e5\uff0c\u4ed6\u542c\u8bf4\u5ff5\u9752\u5510 \u53e4\u62c9\u5c71\u4e0a\u6709\u5929\u6e56\uff0c\u6e56\u8fb9\u6709\u4e2a\u4ed9\u5973\uff0c\u5929\u6e56\u7684\u5723\u6c34\u548c\u5c71\u4e0a\u7684\u4e00\u79cd\u66fc\u9640\u7f57\u82b1\uff0c\u53ef\u4ee5\u533b\u6cbb\u767e\u75c5\uff0c\u4ed6\u60f3 \u4e0d\u51fa\u5176\u4ed6\u529e\u6cd5\uff0c\u5c31\u4e0d\u7ba1\u662f\u771f\u662f\u5047\uff0c\u80cc\u7740\u6211\u5192\u7740\u8270\u96be\u56f0\u82e6\uff0c\u6500\u767b\u4e0a\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u3002 \u3000\u3000\u201c\u53ef\u662f\u4ed6\u8eab\u53d7\u5185\u4f24\uff0c\u53c8\u8fde\u65e5\u5954\u6ce2\uff0c\u6500\u767b\u9ad8\u5c71\uff0c\u521a\u770b\u89c1\u5927\u6e56\u7684\u6e56\u6c34\uff0c\u5927\u559c\u8fc7\u671b\uff0c\u53eb\u4e86\u4e00 \u58f0\uff0c\u5c31\u6655\u5012\u4e86\u3002\u6211\u53eb\u4e0d\u9192\u4ed6\uff0c\u54c0\u54c0\u75db\u54ed\uff0c\u809a\u4e86\u53c8\u9965\u53c8\u997f\uff0c\u54ed\u4e86\u4e00\u573a\uff0c\u4e5f\u6655\u5012\u4e86\u3002 \u3000\u3000\u201c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6211\u60a0\u60a0\u9192\u8f6c\uff0c\u8205\u8205\u4e0d\u89c1\u4e86\uff0c\u5374\u89c1\u4e00\u4e2a\u7f8e\u8c8c\u7684\u5c11\u5973\uff0c\u7ad9\u5728\u6211\u7684\u9762\u524d\uff0c \u6211\u5fc3\u91cc\u60f3\u9053\uff1a\u2018\u8fd9\u4e00\u5b9a\u662f\u4f4f\u5728\u5929\u6e56\u8fb9\u7684\u4ed9\u5973\u4e86\u3002\u2019\u4fbf\u9053\uff1a\u2018\u4ed9\u5973\u59d0\u59d0\uff0c\u6211\u7684\u8205\u8205\u5462\uff1f\u2019\u90a3\u5973 \u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u2018\u90a3\u4eba\u662f\u4f60\u7684\u8205\u8205\u5417\uff1f\u6211\u4e0d\u662f\u4ed9\u5973\uff0c\u6211\u59d3\u6842\uff0c\u540d\u53eb\u51b0\u5a25\uff0c\u522b\u4eba\u4e5f\u53eb\u6211\u505a\u51b0 \u5ddd\u5929\u5973\u3002\uff0c\u6211\u53c8\u95ee\u9053\uff1a\u2018\u5929\u5973\u59d0\u59d0\uff0c\u6211\u7684\u8205\u8205\u5462\uff1f\u2019 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u2018\u6211\u8fd9\u91cc\u4e0d\u51c6\u5916\u4eba\u4e0a\u6765\uff0c\u4f60\u7684\u8205\u8205\u5df2\u7ed9\u6211\u8d76\u4e0b\u5c71\u4e86\u3002\u6211\u53f7\u9676\u5927\u54ed\uff0c\u51b0\u5ddd\u5929 \u5973\u5b89\u6170\u6211\u9053\uff1a\u2018\u4f60\u4e0d\u8981\u54ed\uff0c\u6211\u66ff\u4f60\u7684\u8205\u8205\u6cbb\u597d\u4e86\u4f24\uff0c\u4ed6\u7684\u6027\u547d\u5df2\u4fdd\u4f4f\u4e86\uff0c\u8981\u4e0d\u7136\u4ed6\u8fd8\u80fd\u4e0b\u5c71 \u5417\uff1f\u201d\u6211\u60f3\u8fd9\u4f4d\u5929\u5973\u59d0\u59d0\u6551\u4e86\u6211\u7684\u8205\u8205\uff0c\u5374\u53c8\u8d76\u4ed6\u4e0b\u5c71\uff0c\u5fc3\u91cc\u4fbf\u83ab\u540d\u5176\u5999\u7684\u5bb3\u6015\uff0c\u9053\uff1a\u2018\u5929 \u5973\u59d0\u59d0\uff0c\u4f60\u4e5f\u8d76\u6211\u4e0b\u5c71\u5417\uff1f\u2019\u90a3\u65f6\u6211\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u6b66\u529f\uff0c\u82e5\u7136\u8981\u6211\u4e00\u4eba\u4e0b\u5c71\uff0c\u4e0d\u8dcc\u6b7b\u4e5f\u4f1a\u997f \u6b7b\u3002 \u3000\u3000\u201c\u51b0\u5ddd\u5929\u5973\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u4e0e\u4f60\u6709\u7f18\uff0c\u6240\u4ee5\u5c06\u4f60\u7559\u4e0b\u6765\u4e86\u3002\u2019\u540e\u6765\u6211\u624d\u77e5 \u9053\uff0c\u5979\u4ece\u672a\u89c1\u8fc7\u5916\u4eba\uff0c\u60f3\u77e5\u9053\u4e00\u4e9b\u5c18\u4e16\u95f4\u7684\u4e8b\u60c5\uff0c\u5979\u53c8\u6b22\u559c\u6211\u7684\u773c\u775b\u50cf\u5979\uff0c\u6240\u4ee5\u5c06\u6211\u7559\u4e0b \u6765\u3002\u201d\u9648\u5929\u5b87\u7ecf\u5979\u4e00\u8bf4\uff0c\u4e0d\u7981\u7559\u610f\u5979\u7684\u773c\u775b\uff0c\u53ea\u89c9\u5979\u7684\u773c\u775b\u53c8\u5706\u53c8\u5927\uff0c\u773c\u73e0\u5fbd\u78a7\uff0c\u5728\u773c\u7736\u91cc \u6ef4\u6e9c\u6e9c\u7684\u8f6c\uff0c\u5c31\u50cf\u767d\u6c34\u94f6\u91cc\u5305\u7740\u4e24\u9897\u9ed1\u6c34\u94f6\uff0c\u679c\u7136\u6709\u70b9\u50cf\u51b0\u5ddd\u5929\u5973\u7684\u773c\u775b\u3002 \u3000\u3000\u829d\u5a1c\u9762\u4e0a\u6cdb\u8d77\u4e00\u7247\u7f9e\u7ea2\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u6211\u89c1\u5979\u5bf9\u6211\u5f88\u662f\u548c\u5584\uff0c\u4fbf\u7559\u4e0b\u6765\uff0c\u5c06\u8eab\u4e16\u7ecf\u5386\u544a \u8bc9\u4e86\u5979\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u540e\u6765\u600e\u6837\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u867d\u7136\u6ca1\u6709\u5728\u6211\u7684\u9762\u524d\u663e\u9732\u8fc7\u60ca\u4eba\u7684\u6b66 \u529f\uff0c\u4f46\u6211\u5df2\u77e5\u9053\u5979\u662f\u975e\u5e38\u4e4b\u4eba\uff0c\u4fbf\u60f3\u62dc\u5979\u4e3a\u5e08\uff0c\u8ddf\u5979\u5b66\u70b9\u672c\u9886\uff0c\u5979\u8bf4\uff1a\u6211\u7d20\u6765\u4e0d\u7406\u5c18\u4e16\u4e4b \u4e8b\uff0c\u66f4\u4e0d\u505a\u4eba\u5e08\u7236\uff0c\u6211\u82e6\u82e6\u54c0\u6c42\uff0c\u540e\u6765\u5979\u8bf4\uff1a\u597d\u5427\uff0c\u770b\u5728\u4f60\u8eab\u4e16\u53ef\u601c\uff0c\u6211\u4fbf\u4ee5\u59d0\u59b9\u4e4b\u8c0a\uff0c\u4f20 \u4f60\u6b66\u529f\u53e3\u8bc0\uff0c\u4ee5\u4e09\u65e5\u4e3a\u671f\uff0c\u4f60\u80fd\u9886\u4f1a\u591a\u5c11\uff0c\u90a3\u5c31\u5168\u770b\u4f60\u7684\u9020\u5316\u4e86\u3002\u6211\u5b66\u4e86\u53e3\u8bc0\uff0c\u53c8\u5728\u5979\u5bab\u4e2d \u4f4f\u4e86\u4e00\u6708\uff0c\u79c1\u4e0b\u91cc\u5411\u5979\u7684\u4f8d\u5973\u95e8\u8ba8\u6559\u7ec3\u4e60\uff0c\u679c\u7136\u5f97\u76ca\u4e0d\u5c11\uff0c\u672c\u6765\u5979\u8fd8\u8981\u7559\u6211\u591a\u4f4f\u7684\uff0c\u6211\u590d\u4ec7 \u5fc3\u5207\uff0c\u4f4f\u4e86\u4e00\u4e2a\u6708\u4fbf\u4e0b\u5c71\u4e86\u3002\u5440\uff0c\u54ea\u77e5\u9053\u5979\u6559\u7684\u867d\u662f\u6781\u7cbe\u5fae\u6df1\u5965\u7684\u6b66\u529f\uff0c\u6211\u8d44\u8d28\u611a\u9c81\uff0c\u5374\u662f \u9886\u4f1a\u4e0d\u591a\uff0c\u4ec7\u62a5\u4e0d\u6210\uff0c\u53cd\u9669\u4e9b\u4e22\u4e86\u6027\u547d\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u81ea\u7136\u662f\u8c26\u900a\u4e4b\u8f9e\u3002\u8981\u77e5\u4ee5\u829d\u5a1c\u73b0\u5728\u7684\u6b66\u529f\uff0c\u5728\u6c5f\u6e56\u4e0a\u5df2\u975e\u5eb8\u624b\uff0c\u8f7b\u529f\u66f4\u6bd4\u9648\u5929\u5b87 \u8fd8\u8981\u9ad8\u660e\u3002\u9648\u5929\u5b87\u542c\u4e86\u4e0d\u7531\u5f97\u5fc3\u4e2d\u9a87\u670d\uff0c\u60f3\u9053\uff1a\u201c\u5979\u53ea\u5b66\u4e86\u4e09\u65e5\u6b66\u529f\uff0c\u4fbf\u6709\u5982\u65af\u9020\u8be3\uff0c\u51b0\u5ddd \u5929\u5973\u7684\u672c\u4e8b\uff0c\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u5979\u7684\u806a\u660e\u609f\u6027\uff0c\u5728\u8fd9\u4e16\u4e0a\u4e5f\u6050\u6015\u627e\u4e0d\u5230\u7b2c\u4e8c\u4e2a\u4eba\uff01\u201d \u3000\u3000\u829d\u5a1c\u7eed\u9053\uff1a\u201c\u6211\u4e0b\u5c71\u4e4b\u540e\uff0c\u6253\u63a2\u6211\u7684\u5bb6\u4e8b\uff0c\u624d\u77e5\u9053\u6211\u5bb6\u7684\u79cd\u79cd\u60e8\u4e8b\uff0c\u90fd\u662f\u8428\u8fe6\u571f\u53f8\u7684\u6240 \u4f5c\u6240\u4e3a\u3002\u5c31\u5728\u90a3\u4e00\u665a\u4e4b\u540e\uff0c\u7ee7\u627f\u6211\u7236\u4eb2\u7684\u8fd1\u652f\u8fdc\u652f\u4eb2\u5c5e\u90fd\u6b7b\u5149\u4e86\uff0c\u6211\u5931\u4e86\u8e2a\uff0c\u6211\u5988\u5988\u4e5f\u6b7b \u4e86\uff0c\u6c81\u5e03\u85e9\u738b\u7684\u738b\u4f4d\uff0c\u518d\u4e5f\u627e\u4e0d\u5230\u9002\u5f53\u7684\u627f\u7ee7\u4e4b\u4eba\u3002\u7b2c\u4e8c\u5929\uff0c\u8428\u8fe6\u571f\u53f8\u5e26\u9886\u4eba\u9a6c\u6765\u4e86\uff0c\u4ee5\u59fb \u4eb2\u7684\u8eab\u4efd\uff0c\u786c\u8981\u62e5\u7acb\u6211\u7684\u5802\u4f2f\uff0c\u4e5f\u5c31\u662f\u4ed6\u7684\u5cb3\u7236\u4e3a\u738b\uff0c\u65cf\u4e2d\u957f\u8001\u6151\u4e8e\u4ed6\u7684\u5a01\u52bf\uff0c\u6ca1\u4eba\u6562\u9053\u534a \u4e2a\u4e0d\u5b57\uff0c\u6211\u5802\u4f2f\u5e74\u5df2\u516d\u5341\u5f00\u5916\uff0c\u72b9\u5982\u98ce\u4e2d\u6b8b\u70db\uff0c\u660f\u5eb8\u8001\u673d\uff0c\u6beb\u65e0\u4f5c\u4e3a\uff0c\u8428\u8fe6\u571f\u53f8\u6d3e\u4ed6\u7684\u957f\u5b50 \u6765\u505a\u6d85\u5df4\uff0c\u7f8e\u5176\u540d\u66f0\u5916\u5b59\u6765\u7ed9\u5916\u516c\u5206\u52b3\uff0c\u5e2e\u7406\u653f\u4e8b\uff0c\u5b9e\u9645\u662f\u4ed6\u505a\u4e86\u592a\u4e0a\u7687\uff0c\u6c81\u5e02\u85e9\u738b\u7684\u571f\u5730 \u4e5f\u88ab\u4ed6\u4fb5\u593a\u4e86\u4e0d\u5c11\u3002\u6211\u6068\u6781\u4e86\u4ed6\uff0c\u53d1\u8a93\u4e0d\u7ba1\u4efb\u4f55\u8270\u82e6\uff0c\u5b9a\u8981\u628a\u4ed6\u6740\u4e86\u3002\u540e\u6765\u6211\u62a5\u4ec7\u5931\u8d25\u7684 \u4e8b\uff0c\u4f60\u90fd\u77e5\u9053\uff0c\u6211\u4e0d\u5fc5\u591a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u7b54\u5e94\u518d\u4f20\u4f60\u7684\u6b66\u529f\u5417\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u5979\u7b54\u5e94\u518d\u6559\u6211\u4e09\u65e5\uff0c\u6b64\u540e\uff0c \u6211\u80fd\u5426\u62a5\u4ec7\uff0c\u5c31\u5168\u662f\u6211\u7684\u4e8b\u4e86\u3002\u201d\u9648\u5929\u5b87\u6fc0\u52a8\u8bf4\u9053\uff1a\u201c\u6211\u66ff\u4f60\u62a5\u4ec7\u3002\u201d\u829d\u5a1c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u662f\u4e48\uff0c\u6211\u591a\u8c22\u4f60\u5566\u3002\u53ea\u662f\u7236\u6bcd\u4e4b\u4ec7\uff0c\u82e5\u975e\u4e07\u4e0d\u5f97\u5df2\uff0c\u6211\u662f\u4e0d\u4f1a\u501f\u5916\u4eba\u4e4b\u529b\u7684\u3002\u518d\u8005\u8428\u8fe6\u571f \u53f8\u517b\u6709\u8bb8\u591a\u80fd\u4eba\uff0c\u90a3\u4f1a\u4f7f\u4e03\u9634\u638c\u7684\u523a\u5ba2\uff0c\u53ea\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4ee5\u4f60\u6211\u6b64\u523b\u7684\u6b66\u529f\uff0c\u518d\u7ec3\u4e09\u5e74\u4e94 \u8f7d\uff0c\u4e5f\u672a\u5fc5\u8fd1\u5f97\u4e86\u4ed6\u3002\u201d\u9648\u5929\u5b87\u60f3\u8d77\u81ea\u5df1\u672c\u4e8b\u4f4e\u5fae\uff0c\u5374\u53e3\u51fa\u5927\u8a00\uff0c\u4e0d\u89c9\u751a\u662f\u7f9e\u6127\u3002 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u829d\u5a1c\u6c34\u6c6a\u6c6a\u7684\u773c\u775b\uff0c\u5145\u6ee1\u4e86\u611f\u6fc0\u7684\u8c22\u610f\uff0c\u5ffd\u800c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u660e\u5929\u4f60\u4e0d\u662f \u8981\u8ddf\u4f60\u7684\u5e08\u7236\u8d70\u4e48\uff1f\u201d\u9648\u5929\u5b87\u5fc3\u795e\u52a8\u8361\uff0c\u4f4e\u58f0\u53f9\u9053\uff1a\u201c\u662f\u5475\uff0c\u660e\u5929\u6211\u5c31\u8981\u968f\u5e08\u7236\u8d70\u4e86\u3002\u201d\u8bdd \u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u82b1\u56ed\u90a3\u8fb9\uff0c\u9690\u9690\u4f20\u6765\u4e86\u94c1\u62d0\u4ed9\u7684\u53f1\u54a4\u4e4b\u58f0\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u51b0\u5bab\u6765\u602a\u5ba2\uff0c\u5251\u5e95\u89c1\u5947\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u6e05\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u5929\u5973\u98de\u82b1\u3000\u4ed9\u59dd\u5e94\u6709\u6068\u3000\u51b0\u5ddd\u6620\u6708\u3000\u9a9a\u5ba2\u52a8\u82b3\u5fc3 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u516d \u56de \u5929\u5973\u98de\u82b1\u3000\u4ed9\u59dd\u5e94\u6709\u6068 \u51b0\u5ddd\u6620\u6708\u3000\u9a9a\u5ba2\u52a8\u82b3\u5fc3 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7684\u51b0\u5bab\u56db\u5468\u900f\u660e\uff0c\u867d\u6709\u5047\u5c71\u63a9\u853d\uff0c\u4f46\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u5728\u5bab\u6bbf\u90a3\u8fb9\uff0c\u82b1\u56ed\u91cc\u9762\uff0c \u6709\u4e24\u6761\u9ed1\u5f71\uff0c\u817e\u8dc3\u640f\u6597\u3002\u5176\u4e2d\u4e00\u4eba\uff0c\u624b\u63d0\u94c1\u62d0\uff0c\u821e\u5f97\u8f66\u8f6e\u822c\u7684\u56e2\u56e2\u75be\u8f6c\uff0c\u53ef\u4e0d\u6b63\u662f\u9648\u5929\u5b87\u65b0 \u62dc\u7684\u5e08\u7236\u94c1\u62d0\u4ed9\uff01\u4ed6\u7684\u5bf9\u624b\u8eab\u6750\u9ad8\u5927\uff0c\u9762\u8c8c\u770b\u4e0d\u6e05\u695a\uff0c\u4f3c\u4e4e\u4e0d\u662f\u4e2d\u571f\u4e4b\u4eba\uff0c\u8eab\u4e0a\u62ab\u7740\u4e00\u4ef6\u5927 \u7ea2\u8888\u88df\uff0c\u5728\u51b0\u5bab\u7684\u5bd2\u5149\u63a9\u6620\u4e4b\u4e0b\uff0c\u5341\u5206\u62a2\u773c\u593a\u76ee\uff0c\u5c31\u5982\u767d\u7a97\u91cc\u6d8c\u51fa\u4e00\u6735\u7ea2\u971e\u3002\u9648\u5929\u5b87\u5927\u5403\u4e00 \u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u5c45\u7136\u80fd\u6e21\u8fc7\u51b0\u5ddd\uff0c\u76f4\u95ef\u51b0\u5bab\uff0c\u672c\u4e8b\u5b9a\u662f\u975e\u540c\u5c0f\u53ef\u3002\u201d\u829d\u5a1c\u770b\u4e86\u4e00\u773c\uff0c\u4ea6\u662f \u9a87\u7136\u8bf4\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u7981\u4ee4\u68ee\u4e25\uff0c\u600e\u4e48\u8fd8\u4e0d\u51fa\u6765\uff0c\u7adf\u5bb9\u8fd9\u4e2a\u91ce\u4eba\u6765\u95ef\u5979\u7684\u5bab\u6bbf\uff1f\u201d \u3000\u3000\u829d\u5a1c\u719f\u6089\u5bab\u4e2d\u9053\u8def\uff0c\u5e26\u7740\u9648\u5929\u5b87\u5de6\u5f2f\u53f3\u7ed5\uff0c\u4e0d\u4e00\u523b\u5c31\u5230\u4e86\u90a3\u8fb9\u51b0\u5bab\u524d\u9762\u7684\u82b1\u56ed\uff0c\u53ea\u89c1\u548c \u94c1\u62d0\u4ed9\u640f\u6597\u7684\u90a3\u4eba\u662f\u4e2a\u756a\u50e7\uff0c\u9e70\u9f3b\u72ee\u53e3\uff0c\u76f8\u8c8c\u751a\u662f\u4e11\u964b\uff0c\u4ed6\u4f7f\u7684\u662f\u4e00\u6839\u7985\u6756\uff0c\u6bd4\u94c1\u62d0\u4ed9\u7684\u94c1 \u62d0\u8981\u7ec6\u5c0f\u8bb8\u591a\uff0c\u4f46\u94c1\u62d0\u4ed9\u51f6\u731b\u640f\u51fb\uff0c\u90fd\u88ab\u4ed6\u4e00\u4e00\u8f7b\u63cf\u6de1\u5199\u7684\u5316\u89e3\u5f00\u53bb\u3002 \u3000\u3000\u518d\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u8fd8\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u501a\u5728\u5047\u5c71\u7684\u592a\u6e56\u77f3\u8fb9\uff0c\u53cc\u624b\u5408\u4ec0\uff0c\u53e3\u4e2d\u5583\u5583\u6709\u8f9e\uff0c\u5374 \u662f\u65e5\u524d\u6240\u89c1\u7684\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u9648\u5929\u5b87\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5bf9\u51b0\u5ddd\u5929\u5973\u5949 \u82e5\u795e\u660e\uff0c\u606d\u656c\u65e0\u6bd4\uff0c\u4f55\u4ee5\u4e5f\u6562\u968f\u8fd9\u4e2a\u756a\u50e7\u6765\u95ef\u5979\u7684\u5bab\u6bbf\u3002\u53ea\u542c\u5f97\u829d\u5a1c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c3c \u6cca\u5c14\u6b66\u58eb\u53eb\u8fd9\u756a\u50e7\u505a\u56fd\u5e08\uff0c\u770b\u4f3c\u751a\u6709\u6765\u5934\u3002\u201d \u3000\u3000\u829d\u5a1c\u6bd4\u9648\u5929\u5b87\u591a\u61c2\u5c3c\u6cca\u5c14\u8bdd\uff0c\u9648\u5929\u5b87\u95ee\u9053\uff0c\u201c\u4ed6\u4eec\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u6211\u4e5f\u542c\u5f97\u4e0d \u5f88\u660e\u767d\uff0c\u597d\u50cf\u662f\u529d\u4ed6\u4eec\u7684\u56fd\u5e08\u4e0d\u8981\u95ef\u7978\u3002\u201d \u3000\u3000\u94c1\u62d0\u4ed9\u8d8a\u6597\u8d8a\u52c7\uff0c\u7897\u53e3\u822c\u7c97\u5927\u7684\u62d0\u6756\u821e\u5f97\u547c\u547c\u631f\u98ce\uff0c\u62d0\u6756\u62a1\u5706\uff0c\u5c31\u5982\u4e00\u7247\u6756\u6797\uff0c\u5c06\u90a3\u7ea2 \u8863\u756a\u50e7\u56f0\u5728\u5f53\u4e2d\u3002\u53cc\u6756\u4ea4\u51fb\uff0c\u66f4\u5982\u9e23\u949f\u51fb\u9700\uff0c\u9707\u5f97\u8033\u9f13\u90fd\u55e1\u55e1\u4f5c\u54cd\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u53c8\u6597\u4e86\u4e09\u4e94 \u5341\u62db\u3002\u9648\u5927\u5b57\u8d8a\u80b2\u8d8a\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u4e00\u9635\u4e52\u4e52\u4e53\u4e53\u7684\u4e71\u6253\uff0c\u5c31\u7b97\u719f\u7761\u5982\u6ce5\uff0c\u4e5f\u8be5\u88ab\u4ed6\u4eec \u95f9\u9192\uff0c\u4f55\u4ee5\u51b0\u5ddd\u5927\u5973\u8fd8\u4e0d\u89c1\u51fa\u6765\uff1f\u201d\u975e\u4f46\u51b0\u5ddd\u5927\u5973\u4e0b\u89c1\u51fa\u6765\uff0c\u5bab\u4e2d\u7684\u4f8d\u5973\uff0c\u4e5f\u65e0\u4e00\u4eba\u51fa\u73b0\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u8981\u4e0d\u8981\u53eb\u4f60\u7684\u5929\u5973\u59d0\u59d0\u51fa\u6765\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\u884c\u4e8b\u795e\u5947\uff0c\u5979 \u73b0\u5728\u5c1a\u672a\u51fa\u6765\uff0c\u60f3\u5fc5\u5176\u4e2d\u53e6\u6709\u7f18\u6545\u3002\u9661\u7136\u542c\u5f97\u53cc\u6756\u76f8\u4ea4\uff0c\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\uff0c\u55e1\u55e1\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e \u8033\uff0c\u9648\u5929\u5b87\u6025\u5fd9\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u7ea2\u8863\u756a\u50e7\u5ffd\u7136\u5750\u5728\u5730\u4e0a\uff0c\u7985\u6756\u6162\u6162\u6325\u52a8\uff0c\u94c1\u62d0\u4ed9\u987b\u7709\u4ff1\u5f20\uff0c\u72e0 \u72e0\u6251\u51fb\uff0c\u9648\u4eba\u5b57\u5fc3\u4e2d\u559c\u9053\uff1a\u201c\u4e0d\u5fc5\u51b0\u5ddd\u5929\u5973\u5230\u6765\uff0c\u8fd9\u53ae\u975e\u6211\u5e08\u7236\u4e4b\u654c\u3002\u201d \u3000\u3000\u5374\u4e0d\u77e5\u94c1\u62d0\u4ed9\u6b64\u65f6\uff0c\u5fc3\u4e2d\u6b63\u5728\u53eb\u82e6\u4e0d\u8fed\uff01\u4ed6\u662f\u7518\u51e4\u4ed6\u7684\u9996\u5f92\uff0c\u529f\u529b\u4e4b\u9ad8\uff0c\u5927\u6c5f\u5357\u5317\uff0c\u65e0 \u4e0e\u4f26\u6bd4\uff0c\u8c01\u77e5\u78b0\u7740\u4e86\u8fd9\u7ea2\u8863\u756a\u50e7\uff0c\u7adf\u7136\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u4efb\u4ed6\u91d1\u521a\u5927\u529b\uff0c\u72e0\u653b\u731b\u6251\uff0c\u5374\u88ab\u8fd9\u756a\u50e7 \u5316\u89e3\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u79f0\u9738\u6c5f\u6e56\u4e8c\u5341\u591a\u5e74\uff0c\u4eca\u756a\u8fd8\u662f\u7b2c\u4e00\u6b21\u906d\u9022\u52b2\u654c\uff0c\u8feb\u5f97\u65bd\u5c55\u6700\u5389\u5bb3\u7684\u4f0f\u6469\u4ed7\u6cd5\uff0c\u8fd9 \u4f0f\u9b54\u6756\u6cd5\u4e43\u662f\u5f53\u5e74\u72ec\u81c2\u795e\u5c3c\u6240\u521b\uff0c\u7ecf\u8fc7\u4e86\u56e0\u548c\u5c1a\u7cbe\u7814\uff0c\u518d\u52a0\u4ee5\u589e\u76ca\uff0c\u6f14\u6210\u4e00\u767e\u96f6\u516b\u8def\u7684\u62db \u6570\uff0c\u6bcf\u4e00\u4ed7\u6253\u4e0b\uff0c\u90fd\u6709\u5343\u94a7\u4e4b\u529b\uff0c\u800c\u4e14\u6756\u5934\u6756\u5c3e\u90fd\u53ef\u7528\u4ee5\u6253\u7a74\uff0c\u5176\u4e2d\u8fd8\u5939\u6709\u5200\u5251\u7684\u8def\u6570\uff0c\u7aef \u7684\u662f\u5389\u5bb3\u65e0\u6bd4\uff0c\u4f46\u5374\u6700\u6d88\u8017\u5185\u5bb6\u771f\u529b\uff0c\u82e5\u7136\u6f14\u5b8c\u4e00\u767e\u96f6\u516b\u8def\u6756\u6cd5\uff0c\u975e\u5367\u5e8a\u9759\u517b\u4e09\u65e5\uff0c\u4e0d\u80fd\u590d \u539f\uff0c\u6240\u4ee5\u94c1\u62d0\u4ed9\u4ece\u6765\u4e0d\u7528\u3002 \u3000\u3000\u4f0f\u6469\u4ed7\u6cd5\u4e00\u5c55\uff0c\u679c\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u6570\u62db\u4e00\u8fc7\uff0c\u4fbf\u5982\u65e0\u98ce\u6d77\u96e8\uff0c\u6251\u4eba\u800c\u6765\uff0c\u9976\u662f\u90a3\u756a\u50e7\u5982\u4f55 \u9547\u5b9a\uff0c\u4e5f\u6709\u70b9\u624b\u5fd9\u811a\u4e71\uff0c\u94c1\u62d0\u4ed9\u52a0\u91cd\u5185\u529b\uff0c\u6b63\u62df\u5c06\u4ed6\u4e00\u62d0\u51fb\u5012\uff0c\u90a3\u756a\u50e7\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u5ffd\u7136 \u8dcc\u5750\u5730\u4e0a\uff0c\u53cc\u819d\u4e00\u76d8\uff0c\u7791\u76ee\u5782\u9996\uff0c\u72b6\u5982\u5750\u7985\uff0c\u624b\u4e2d\u7684\u7985\u6756\u5374\u4ecd\u662f\u7f13\u7f00\u6325\u52a8\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u867d\u662f\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u4e0d\u7531\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6253\u6cd5\uff1f\u201d\u9661\u89c9\u81ea\u5df1\u7684\u653b\u52bf \u88ab\u4ed6\u5c01\u7740\uff0c\u800c\u4e14\u9690\u9690\u6709\u4e00\u80a1\u53cd\u51fb\u4e4b\u529b\uff0c\u653b\u52bf\u6108\u731b\uff0c\u53cd\u51fb\u4e4b\u529b\u4e5f\u5c31\u6108\u5927\uff0c\u90a3\u7985\u6756\u867d\u662f\u7f13\u7f13\u6325 \u52a8\uff0c\u5374\u5982\u5728\u9762\u524d\u5e03\u4e86\u4e00\u9053\u94c1\u58c1\u94dc\u5899\uff0c\u6467\u4e4b\u4e0d\u6bc1\uff0c\u653b\u4e4b\u4e0d\u5165\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u5927\u5403\u4e00\u60ca\uff0c\u653b\u52bf\u50ac\u7d27\uff0c\u970e\u773c\u95f4\u5df2\u4f7f\u4e86\u4e09\u5341\u516d\u62db\uff0c\u4e00\u767e\u96f6\u516b\u8def\u4f0f\u9b54\u6756\u6cd5\u5206\u4e3a\u4e09\u6bb5\uff0c \u7b2c\u4e00\u6bb5\u4e09\u5341\u516d\u62db\u662f\u91d1\u94a2\u731b\u6251\u7684\u529f\u592b\uff0c\u653b\u4e4b\u4e0d\u5165\uff0c\u7b2c\u4e8c\u6bb5\u4e09\u5341\u516d\u62db\u53c8\u8fde\u63a5\u800c\u6765\uff0c\u8fd9\u4e09\u5341\u516d\u62db\u7528 \u7684\u5168\u662f\u5185\u5bb6\u771f\u529b\uff0c\u5c31\u662f\u77f3\u5934\u6371\u4e86\u4e00\u6756\uff0c\u4e5f\u4f1a\u6253\u6210\u7c89\u788e\uff0c\u800c\u4e14\u524d\u4e09\u5341\u516d\u62db\uff0c\u53d1\u6756\u4e4b\u65f6\u6709\u98ce\u96f7\u4e4b \u58f0\uff0c\u8fd9\u4e09\u5341\u516d\u62db\uff0c\u5374\u662f\u6765\u65e0\u8e2a\u53bb\u65e0\u8ff9\uff0c\u7528\u529b\u867d\u6c89\uff0c\u5374\u65e0\u58f0\u54cd\uff0c\u66f4\u96be\u9632\u5907\u3002\u53ef\u602a\u7684\u662f\u90a3\u756a\u50e7\u4ecd \u662f\u7791\u76ee\u5782\u9996\uff0c\u4f46\u5374\u4f3c\u80cc\u540e\u90fd\u957f\u7740\u773c\u775b\uff0c\u4e0d\u7ba1\u94c1\u62d0\u4ed9\u4ece\u4ec0\u4e48\u5730\u65b9\u6253\u6765\uff0c\u4ed6\u7985\u6756\u4e00\u6325\uff0c\u5c31\u6070\u597d\u6321 \u4f4f\uff0c\u800c\u4e14\u53cd\u51fb\u4e4b\u529b\u6bd4\u524d\u66f4\u5927\uff0c\u6709\u597d\u51e0\u6b21\u94c1\u62d0\u4ed9\u7684\u94c1\u62d0\uff0c\u90fd\u51e0\u4e4e\u7ed9\u4ed6\u9707\u5f97\u8131\u624b\u98de\u53bb\uff01 \u3000\u3000\u539f\u6765\u8fd9\u756a\u50e7\u7528\u7684\u662f\u5370\u5ea6\u7684\u745c\u4f3d\u529f\u592b\uff0c\u914d\u4ee5\u897f\u85cf\u5bc6\u5b97\u7684\u67d4\u529f\uff0c\u4e5f\u662f\u4e00\u79cd\u4e0a\u4e58\u7684\u5185\u5bb6\u529f\u592b\uff0c \u4f46\u5374\u4e0e\u4e2d\u571f\u7684\u6cd5\u95e8\u4e0d\u540c\uff0c\u7ecf\u7ec3\u4e94\u810f\u516d\u8151\u4e3a\u4e3b\uff0c\u529f\u592b\u6df1\u7684\uff0c\u53ef\u4ee5\u88ab\u5173\u95ed\u5728\u94dc\u68fa\u91cc\u9762\uff0c\u6c89\u4e4b\u6d77 \u5e95\uff0c\u8fc7\u4e86\u4e09\u65e5\uff0c\u518d\u6253\u635e\u4e0a\u6765\uff0c\u4ecd\u7136\u4e0d\u6b7b\u3002\u5185\u529f\u4e2d\u7d27\u96be\u7ec3\u7684\u662f\u5c4f\u7edd\u547c\u5438\uff0c\u80fd\u5230\u8fbe\u90a3\u79cd\u5883\u754c\uff0c\u8eab \u4f53\u5c31\u51e0\u4e4e\u6210\u4e86\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\u3002\u8fd9\u756a\u618e\u867d\u7136\u672a\u5230\u8fd9\u4e2a\u5883\u754c\uff0c\u4f46\u8f83\u4e4b\u94c1\u62d0\u4ed9\u7684\u5185\u529b\uff0c\u5374\u662f\u80dc\u4e86\u4e00 \u7b79\u3002\u756a\u50e7\u7ec3\u7684\u8fd9\u79cd\u529f\u592b\uff0c\u987b\u8981\u9759\u5750\u8fd0\u6c14\uff0c\u65f6\u95f4\u6108\u4e45\uff0c\u76ca\u53d1\u7684\u6f5c\u529b\u6108\u5927\u3002\u6240\u4ee5\u94c1\u62d0\u4ed9\u7684\u4f0f\u9b54\u4ed7 \u6cd5\uff0c\u867d\u7136\u4e00\u6bb5\u80dc\u4f3c\u4e00\u6bb5\uff0c\u4f46\u5bf9\u65b9\u53cd\u51fb\u4e4b\u529b\uff0c\u4e5f\u76f8\u5e94\u52a0\u5f3a\uff0c\u94c1\u62d0\u4ed9\u529b\u4e0d\u4ece\u5fc3\uff0c\u611f\u5230\u66f4\u5403\u529b\u4e86\u3002 \u3000\u3000\u770b\u770b\u7b2c\u4e8c\u6bb5\u7684\u4e09\u5341\u516d\u8def\u4f0f\u6469\u4ed7\u6cd5\u53c8\u5feb\u4f7f\u5b8c\uff0c\u94c1\u62d0\u4ed9\u5934\u4e0a\u5df2\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u51b0\u5ddd\u5929\u5973 \u4ecd\u672a\u89c1\u51fa\u6765\uff0c\u94c1\u62d0\u4ed9\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6709\u6c14\uff0c\u6697\u81ea\u601d\u91cf\uff0c\u53cd\u6b63\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u4f60\u4e0d\u51fa\u5934\uff0c\u6211\u53c8\u4f55\u5fc5\u66ff \u4f60\u591a\u7ba1\u95f2\u4e8b\uff1f\u6253\u5b9a\u4e3b\u610f\uff0c\u4e0d\u5c55\u7b2c\u4e09\u6bb5\u6756\u6cd5\uff0c\u865a\u6643\u4e00\u62db\uff0c\u4fbf\u60f3\u9000\u51fa\u5708\u5b50\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u5c06\u94c1\u62d0\u4e00\u62bd\uff0c\u6b63\u60f3\u8df3\u51fa\u5708\u5b50\uff0c\u5ffd\u89c9\u90a3\u7ea2\u8863\u756a\u50e7\u7684\u63b8\u6756\uff0c\u7adf\u4f3c\u5e26\u6709\u4e00\u80a1\u6781\u5927\u7684\u5438 \u529b\uff0c\u5c06\u4ed6\u7684\u94c1\u62d0\u7262\u7262\u5438\u7740\uff0c\u5f80\u91cc\u7275\u5f15\uff0c\u7adf\u662f\u8131\u4e0d\u4e86\u8eab\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u53c8\u60ca\u53c8\u6012\uff0c\u6025\u8fd0\u5185\u5bb6\u771f\u529b\uff0c\u5c06\u62d0\u4e00\u6446\uff0c\u867d\u7136\u4e5f\u80fd\u6446\u52a8\uff0c\u4f46\u90a3\u80a1\u5438\u529b\u5374\u8d8a\u6765\u8d8a\u7d27\uff0c \u6beb\u4e0d\u653e\u677e\uff0c\u53ea\u5f97\u8fd0\u52b2\u4e0e\u4ed6\u76f8\u6297\uff0c\u65bd\u5c55\u51fa\u4f0f\u9b54\u6756\u6cd5\u7684\u7b2c\u4e09\u6bb5\u4e09\u5341\u516d\u62db\u6765\u3002 \u3000\u3000\u4f0f\u9b54\u6756\u6cd5\u4e00\u6bb5\u5f3a\u8fc7\u4e00\u6bb5\uff0c\u6700\u540e\u7684\u4e00\u6bb5\u4e09\u5341\u516d\u62db\uff0c\u6700\u662f\u6d88\u8017\u5185\u5bb6\u771f\u529b\uff0c\u9648\u5929\u5b87\u5728\u65c1\u89c2\u770b\uff0c \u53ea\u89c1\u4e24\u4eba\u7684\u62db\u5f0f\u90fd\u662f\u8d8a\u653e\u8d8a\u6162\uff0c\u90a3\u756a\u50e7\u4ecd\u7136\u662f\u95ed\u76ee\u5782\u9996\uff0c\u76d8\u819d\u8dcc\u5750\uff0c\u5934\u4e0a\u4e5f\u5df2\u5192\u51fa\u70ed\u817e\u817e\u7684 \u767d\u6c14\uff0c\u5598\u606f\u4e4b\u58f0\u5fae\u5fae\u53ef\u95fb\uff0c\u4f46\u518d\u770b\u94c1\u62d0\u4ed9\u65f6\uff0c\u5219\u66f4\u89c1\u72fc\u72c8\uff0c\u53ea\u89c1\u4ed6\u8863\u88f3\u5c3d\u6e7f\uff0c\u6c57\u73e0\u4f3c\u9ec4\u8c46\u7c92 \u822c\u5927\u5c0f\uff0c\u4e00\u9897\u9897\u5730\u6ef4\u4e0b\u6765\uff0c\u94c1\u62d0\u6bcf\u4e00\u6325\u52a8\uff0c\u9aa8\u8282\u5c31\u201c\u683c\u52d2\u201d\u5730\u4f5c\u54cd\uff0c\u6709\u5982\u7206\u8c46\u4e00\u822c\uff0c\u9648\u5929\u5b87 \u867d\u7136\u4e0d\u61c2\u4e0a\u4e58\u6b66\u529f\uff0c\u4f46\u89c1\u6b64\u60c5\u5f62\uff0c\u5df2\u77e5\u5e08\u7236\u751a\u662f\u5403\u529b\uff01 \u3000\u3000\u90a3\u756a\u50e7\u53cc\u773c\u5ffd\u5730\u5f20\u5f00\uff0c\u6479\u7136\u559d\u9053\uff1a\u201c\u5012\uff01\u201d\u94c1\u62d0\u4ed9\u811a\u6b65\u8e09\u8dc4\uff0c\u4e0a\u8eab\u6447\u4e86\u4e24\u6447\uff0c\u54ac\u7740\u7259 \u5f88\uff0c\u5c06\u94c1\u62d0\u6325\u4e86\u534a\u4e2a\u5706\u5b64\uff0c\u5f80\u4e0b\u76f4\u538b\uff0c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u4ed6\u6b63\u4f7f\u5230\u7b2c\u4e5d\u5341\u516d\u62db\u201c\u964d\u9f99 \u4f0f\u864e\u201d\u628a\u5185\u5bb6\u771f\u529b\u5168\u90fd\u8d2f\u6ce8\u62d0\u5934\uff0c\u521a\u52b2\u4e4b\u6781\uff0c\u90a3\u756a\u618e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u547d\u4e48\uff1f\u201d\u7985\u6756\u6162\u6162\u4e0a \u6307\uff0c\u4e0e\u94c1\u62d0\u9876\u4e2a\u6b63\u7740\uff0c\u53ea\u89c1\u90a3\u7897\u53e3\u822c\u7c97\u5927\u7684\u94c1\u62d0\uff0c\u4e2d\u95f4\u90e8\u5206\u7adf\u7136\u6162\u6162\u53d8\u4e86\u4e0b\u6765\uff0c\u94c1\u62d0\u4ed9\u7684\u9762 \u8272\u66f4\u6c89\u91cd\u4e86\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u88c6\u201d\u7684\u4e00\u58f0\uff0c\u94c1\u62d0\u5ffd\u5730\u5f39\u4e86\u8d77\u6765\uff0c\u90a3\u756a\u50e7\u500f\u7136\u8df3\u8d77\uff0c\u5012\u8dc3\u51e0\u6b65\uff0c\u63b8\u6756\u5782\u4e0b\uff0c\u606d \u656c\u8083\u7acb\u3002\u9648\u5929\u5b87\u5927\u4e3a\u8be7\u5f02\uff0c\u8fd9\u756a\u50e7\u660e\u660e\u5373\u53ef\u53d6\u80dc\uff0c\u4f55\u4ee5\u5ffd\u7136\u653e\u677e\uff1f \u3000\u3000\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u62ab\u7740\u767d\u8272\u7684\u8f7b\u7eb1\uff0c\u4ece\u82b1\u5f84\u4e4b\u4e2d\u7f13\u7f13\u8d70\u51fa\uff0c\u98d8\u98d8\u82e5\u4ed9\uff0c\u508d\u7740\u5979\u8d70 \u7684\u6b63\u662f\u94c1\u62d0\u4ed9\u7684\u59bb\u5b50\uff0c\u5ce8\u5d4b\u5973\u4fa0\u8c22\u4e91\u771f\u3002\u8c22\u4e91\u771f\u5c06\u94c1\u62d0\u4ed9\u6276\u8fc7\u4e00\u8fb9\uff0c\u4e24\u4eba\u624b\u7275\u7740\u624b\uff0c\u4e5f\u5b66\u521a \u624d\u90a3\u756a\u618e\u4e00\u6837\uff0c\u8dcc\u5750\u5730\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u51b0\u5ddd\u5929\u5973\u5219\u5728\u5fae\u5fae\u51b7\u7b11\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u4e86\u8fc7\u6765\u3002\u90a3\u4e24 \u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u6ee1\u9762\u60f6\u6050\u4e4b\u5bb9\uff0c\u5ffd\u7136\u90fd\u662f\u53cc\u638c\u5408\u4ec0\uff0c\u8dea\u5728\u5730\u4e0a\uff0c\u53e3\u4e2d\u5583\u5583\u6709\u8f9e\uff0c\u4f3c\u4e4e\u662f\u5728\u4e5e\u6c42 \u51b0\u5ddd\u5929\u5973\u9976\u6055\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u629a\u7985\u6756\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u5f20\u9ec4\u7eb8\u8bcf\u4e66\uff0c\u8bf4\u4e86\u4e00\u53e5\uff0c\u829d\u5a1c\u8f7b\u8f7b\u201c\u54a6\u201d \u4e86\u4e00\u58f0\uff0c\u5728\u9648\u5929\u5b87\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u8fd9\u756a\u618e\u4f60\u5929\u5973\u59d0\u59d0\u505a\u516c\u4e3b\uff0c\u8981\u4ed6\u63a5\u8bcf\uff0c\u8fd9\u53ef\u771f\u771f\u5947\u602a\u4e86\uff01\u201d \u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u63a5\u8fc7\u8bcf\u4e66\uff0c\u7565\u4e00\u5c55\u770b\u3002\u7acb\u5373\u63b7\u8fd8\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u9762\u5b54\u6da8\u7ea2\uff0c\u7985\u6756\u4e00\u987f\uff0c\u7528\u5c3c\u6cca\u5c14 \u8bdd\u8bf4\u9053\uff1a\u201c\u6e05\u671d\u7687\u5e1d\u7684\u91d1\u74f6\uff0c\u6211\u4eec\u5b9a\u7136\u4e0d\u80fd\u5bb9\u5b83\u5230\u5f97\u62c9\u8428\uff0c\u56fd\u4e3b\u4e4b\u547d\uff0c\u8981\u4f60\u4e0b\u5c71\u76f8\u52a9\uff0c\u4f60\u4e5f \u4e0d\u80af\u7b54\u5141\u4e48\uff1f\u201d\u9648\u5929\u5b87\u542c\u5f97\u534a\u61c2\u4e0d\u61c2\uff0c\u597d\u5728\u6709\u829d\u5a1c\u5728\u65c1\u7ed9\u4ed6\u7ffb\u8bd1\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9762\u8272\u5fae\u53d8\uff0c\u4f46\u9762\u4e0a\u4ecd\u5e26\u7740\u7b11\u5bb9\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u6b63\u60f3\u518d\u8bf4\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u7389\u624b\u4e00 \u6307\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u6eda\u4e0b\u51fa\u53bb\uff01\u201d\u51b7\u6708\u51b0\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u90a3\u756a\u50e7\u7684\u9762\u5b54\u7531\u901a\u7ea2\u53d8\u5f97\u94c1\u9752\uff0c \u663e\u5f97\u5341\u5206\u5c34\u5c2c\uff0c\u66f4\u662f\u53ef\u6016\u3002\u829d\u5a1c\u9053\uff1a\u201c\u4f60\u77a7\u4ed6\u607c\u7f9e\u6210\u6012\u4e86\u3002\u201d\u90a3\u756a\u50e7\u4e43\u662f\u5c3c\u6cca\u5c14\u56fd\u5e08\uff0c\u51e0\u66fe \u53d7\u8fc7\u5982\u65af\u4fae\u8fb1\uff0c\u53ea\u89c1\u4ed6\u6c14\u5f97\u624b\u6307\u53d1\u6296\uff0c\u5ffd\u7136\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u6307\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a \u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u53eb\u6211\u6eda\uff1f\u56fd\u738b\u4e5f\u4e0d\u6562\u5bf9\u6211\u5982\u6b64\u65e0\u793c\uff01\u201d\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u8981\u4f60 \u6eda\u4e0b\u53bb\uff0c\u4f60\u5f85\u600e\u5730\uff1f\u6211\u5df2\u7ed9\u4e86\u4f60\u83ab\u5927\u7684\u60c5\u9762\uff0c\u8ba9\u4f60\u95ef\u5165\u5bab\u6765\uff0c\u89c1\u6211\u4e00\u9762\uff0c\u4f60\u8fd8\u4e0d\u77e5\u8db3\uff1f\u6211\u6709 \u8fc7\u8a93\u8a00\u5728\u524d\uff0c\u8c01\u6562\u53eb\u6211\u4e0b\u5c71\uff0c\u90fd\u5f97\u7ed9\u6211\u6eda\u8d70\uff0c\u4f60\u4e5f\u4e0d\u80fd\u4f8b\u5916\uff01\u201d \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u5f3a\u63a9\u7a98\u6001\uff0c\u53d1\u4e3a\u72c2\u7b11\uff0c\u7985\u6756\u987f\u5730\u3002\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u95f4\u5173\u4e07\u91cc\uff0c\u8fdc\u9053\u524d\u6765\uff0c\u53ea \u89c1\u7740\u516c\u4e3b\u4e00\u9762\uff0c\u5b9e\u662f\u4e0d\u80fd\u5fc3\u8db3\u3002\u95fb\u9053\u516c\u4e3b\u7684\u6b66\u529f\uff0c\u5df2\u5c3d\u5f97\u4e2d\u534e\u4e0e\u897f\u571f\u7684\u6240\u957f\uff0c\u8d2b\u50e7\u751a\u613f\u5f00\u5f00 \u773c\u754c\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u56de\u7738\u51b7\u7b11\uff0c\u62cd\u638c\u53eb\u9053\uff1a\u201c\u6765\u4eba\u54ea\uff01\u201d\u970e\u773c\u4e4b\u95f4\uff0c\u8d70\u51fa\u4e5d \u4e2a\u4f8d\u5973\uff0c\u51b0\u5ddd\u5929\u5973\u6602\u9996\u671d\u5929\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u7ed9\u6211\u5c06\u8fd9\u4e2a\u91ce\u548c\u5c1a\u64b5\u4e0b\u5c71\u53bb\uff01\u201d\u7ea2\u8863\u756a\u618e\u53eb\u9053\uff1a \u201c\u5475\uff0c\u539f\u6765\u4f60\u662f\u4e0d\u5c51\u548c\u6211\u52a8\u624b\uff0c\u90a3\u6211\u9002\u624d\u4e4b\u8bf7\uff0c\u786e\u662f\u592a\u8fc7\u5192\u6627\u4e86\uff0c\u4f46\u6211\u5e73\u751f\u4ece\u6765\u672a\u66fe\u53d7\u4eba\u9a71 \u9010\uff0c\u4e0d\u77e5\u8fdb\u9000\u4e4b\u5904\uff0c\u8fd8\u671b\u516c\u4e3b\u6d77\u91cf\u5305\u6db5\u3002\u201d\u90a3\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u60f6\u6050\u975e\u5e38\uff0c\u8fde\u8fde\u529d\u4ed6\u4eec\u7684\u56fd\u5e08\u5feb \u8d70\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u628a\u7985\u4ed7\u4e00\u987f\uff0c\u5140\u7acb\u5982\u5c71\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e0d\u7406\u4e0d\u776c\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u628a\u624b\u4e00\u6325\uff0c\u4e5d\u540d\u4f8d\u5973\u56f4\u4e86\u4e0a\u6765\uff0c\u51b0\u5ddd\u5929\u5973\u4e24\u9053\u773c\u5149\u6709\u5982 \u5229\u5251\uff0c\u76f4\u5c04\u5230\u7ea2\u8863\u756a\u50e7\u9762\u4e0a\uff0c\u4e0d\u6012\u800c\u5a01\uff0c\u4ee4\u5f97\u90a3\u7ea2\u8863\u756a\u50e7\u4e5f\u4e0d\u7531\u5f97\u5012\u9000\u4e24\u6b65\uff0c\u521a\u6c14\u987f\u9981\uff0c\u4f46 \u89c1\u90a3\u4e5d\u540d\u4f8d\u5973\u4f5c\u9a71\u9010\u4e4b\u72b6\uff0c\u53c8\u4e0d\u7981\u52c3\u7136\u53d1\u4f5c\uff0c\u7985\u6756\u4e00\u4e3e\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8ba9\u6211\u5148\u9886\u6559\u4f60\u7684 \u4f8d\u5973\u51e0\u62db\uff0c\u7136\u540e\u518d\u9886\u516c\u4e3b\u7684\u6559\u8bad\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8f7b\u79fb\u83b2\u6b65\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u62c9\u7740\u829d\u5a1c\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u5f97\u4ed4\u7ec6\u4e9b\uff0c\u4ed6\u4eec\u6240\u7528\u7684\u5251 \u6cd5\uff0c\u90fd\u662f\u6211\u6559\u8fc7\u4f60\u7684\u3002\u201d\u5bf9\u829d\u5a1c\u7684\u6001\u5ea6\uff0c\u548c\u853c\u53ef\u4eb2\uff0c\u5c31\u5982\u59d0\u59d0\u4e00\u822c\uff0c\u4e0e\u9002\u624d\u7684\u5a01\u4e25\uff0c\u5927\u4e0d\u76f8 \u7c7b\u3002 \u3000\u3000\u7ea2\u8863\u756a\u50e7\u7985\u6756\u4e00\u6325\uff0c\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\uff0c\u60f3\u662f\u4e3a\u4e86\u4fdd\u6301\u8eab\u4efd\uff0c\u5c1a\u672a\u8fdb\u62db\uff0c\u9661\u7136\u95f4\u90a3\u4e5d\u540d\u4f8d\u5973 \u957f\u5251\u4e00\u9f50\u51fa\u624b\uff0c\u5947\u602a\u7684\u662f\uff0c\u6bcf\u4e00\u67c4\u5251\u90fd\u662f\u5bd2\u5149\u95ea\u95ea\uff0c\u901a\u4f53\u6676\u83b9\uff0c\u975e\u91d1\u975e\u94c1\uff0c\u7adf\u4f3c\u4e00\u6bb5\u5bd2\u51b0\uff0c \u4e5d\u67c4\u5251\u4e00\u9f50\u4eae\u51fa\uff0c\u5bd2\u5149\u51b7\u6c14\uff0c\u7acb\u523b\u56db\u9762\u53d1\u5c04\uff0c\u9648\u5929\u5b87\u4e0d\u7531\u81ea\u5df1\u5730\u6253\u4e86\u4e00\u4e2a\u5bd2\u54c4\uff0c\u5c31\u50cf\u5815\u5728\u51b0 \u8c37\u4e4b\u4e2d\u4e00\u6837\uff0c\u51b7\u5f97\u7259\u5173\u6253\u6218\uff0c\u770b\u829d\u5a1c\u65f6\uff0c\u829d\u5a1c\u4e5f\u7ed9\u51bb\u5f97\u8eab\u8eaf\u98a4\u6296\u3002\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u4e00\u65f6\u5927\u610f\u4e86\uff0c\u60f3\u4e0d\u8d77\u4f60\u4eec\u7981\u53d7\u4e0d\u4f4f\u3002\u4f60\u4eec\u4e14\u5fcd\u53d7\u4e00\u4e0b\u3002\u201d\u5ffd\u5730\u624b\u81c2\u4e00\u62ac\uff0c\u8fc5\u5982\u95ea\u7535\u5730\u5411 \u9648\u5929\u5b87\u9888\u80cc\u4e00\u6233\u3002 \u3000\u3000\u9648\u5929\u5b87\u5413\u4e86\u4e00\u8df3\uff0c\u88ab\u5979\u624b\u6307\u4e00\u70b9\uff0c\u6d51\u8eab\u5982\u89e6\u7535\uff0c\u751a\u662f\u9178\u9ebb\u96be\u53d7\uff0c\u4f46\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u89c9\u6709\u4e00 \u80a1\u70ed\u6c14\u4ece\u4e39\u7530\u76f4\u900f\u51fa\u6765\uff0c\u6d41\u884c\u5168\u8eab\uff0c\u5fc3\u8df3\u52a0\u5267\uff0c\u8840\u6d41\u52a0\u5feb\uff0c\u5c31\u5982\u5728\u4e25\u5bd2\u4e4b\u4e0b\uff0c\u7ecf\u8fc7\u4e86\u6025\u4fc3\u7684 \u8dd1\u6b65\u4e00\u822c\uff0c\u5916\u9762\u867d\u7136\u5bd2\u51b7\uff0c\u4f53\u5185\u5374\u662f\u53d1\u70ed\uff0c\u829d\u5a1c\u4e5f\u88ab\u5979\u540c\u6837\u4f9d\u6cd5\u6ce1\u5236\uff0c\u51b7\u610f\u987f\u6d88\uff0c\u53cc\u988a\u4e14\u70ed \u5f97\u6655\u7ea2\u3002\u9648\u5929\u5b87\u4ee5\u524d\u542c\u5e08\u7236\u8c08\u8fc7\uff0c\u8bf4\u662f\u6709\u4e0a\u4e58\u5185\u529f\u4e4b\u4eba\uff0c\u4e0d\u4f46\u53ef\u7528\u70b9\u7a74\u4e4b\u6cd5\u5236\u4eba\u6b7b\u547d\uff0c\u800c\u4e14 \u53ef\u7528\u70b9\u7a74\u4e4b\u6cd5\u533b\u4eba\u4e4b\u75c5\uff0c\u6216\u8005\u662f\u6253\u901a\u75c5\u4eba\u7684\u7ecf\u8109\uff0c\u6216\u8005\u662f\u4ee4\u75c5\u4eba\u7684\u8840\u6db2\u6b63\u5e38\uff0c\u529f\u80fd\u6781\u5176\u5965 \u5999\uff0c\u5f53\u65f6\u542c\u4e86\uff0c\u8fd8\u53ea\u4e0d\u8fc7\u5f53\u4f5c\u4e00\u79cd\u5947\u8c08\uff0c\u800c\u4eca\u8eab\u53d7\uff0c\u59cb\u77e5\u4e16\u754c\u4e4b\u4e0a\uff0c\u771f\u6709\u8fd9\u6837\u4e00\u79cd\u5947\u529f\u3002 \u3000\u3000\u829d\u5a1c\u95ee\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\uff0c\u5979\u4eec\u624b\u4e0a\u7684\u957f\u5251\u662f\u575a\u51b0\u524a\u6210\u7684\u5417\uff1f\u201d\u829d\u5a1c\u89c1\u8fc7\u51b0\u5ddd\u5929\u5973\u7528\u51b0\u5251 \u6740\u8d25\u96f7\u9707\u5b50\uff0c\u662f\u4ee5\u6709\u6b64\u4e00\u95ee\u3002\u9648\u5929\u5b87\u5fc3\u4e2d\u4e5f\u6b63\u5b58\u6709\u8fd9\u4e2a\u7591\u95ee\uff0c\u53cc\u773c\u76ef\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u51b0\u5ddd\u5929\u5973 \u7b11\u9053\uff1a\u201c\u5979\u4eec\u8fd8\u6ca1\u6709\u90a3\u6837\u672c\u4e8b\uff0c\u90a3\u662f\u6211\u7ed9\u5979\u4eec\u6240\u70bc\u7684\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u662f\u7528\u51e1\u5c71\u7279\u4ea7\u7684\u5343\u5e74\u5bd2 \u7389\uff0c\u6d78\u5728\u4e07\u53e4\u5bd2\u51b0\u4e4b\u4e2d\uff0c\u7ecf\u8fc7\u4e09\u5e74\u624d\u70bc\u6210\u7684\u5b9d\u5251\uff0c\u6240\u4ee5\u4e00\u51fa\u624b\u4fbf\u6709\u4e00\u80a1\u51b7\u6c14\uff0c\u6ca1\u6709\u7ec3\u8fc7\u5185\u529f \u7684\u4eba\uff0c\u5149\u662f\u8fd9\u80a1\u51b7\u6c14\uff0c\u4fbf\u96be\u62b5\u53d7\u3002\u201d \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u9661\u7136\u89c1\u8fd9\u4e5d\u67c4\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u4e5f\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u4ed6\u5185\u529f\u7cbe\u7eaf\uff0c\u5728\u51b7\u6c14\u4fb5 \u88ad\u4e4b\u4e0b\uff0c\u5374\u4e5f\u5e76\u4e0d\u754f\u60e7\uff0c\u90a3\u4e5d\u67c4\u4f0d\u5251\u9996\u5c3e\u76f8\u8fde\uff0c\u5e03\u6210\u4e00\u9762\u5149\u7f51\uff0c\u6162\u6162\u6536\u7f29\uff0c\u7ea2\u8863\u756a\u50e7\u5fcd\u8010\u4e0d \u4f4f\uff0c\u7985\u6756\u4e00\u5f39\uff0c\u4e00\u62db\uff1a\u201c\u529b\u5212\u9e3f\u6c9f\u201d\uff0c\u5411\u5916\u63a8\u51fa\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u54e8\u5417\u51e0\u58f0\u8fde\u54cd\uff0c\u524d\u4e00\u6392\u7684\u56db\u53e3 \u5251\u90fd\u7814\u5728\u6756\u4e0a\uff0c\u7ea2\u8863\u756a\u50e7\u8fd9\u4e00\u6756\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u89c1\u8fd9\u56db\u540d\u4f8d\u5973\u5c45\u7136\u62b5\u53d7\u5f97\u4f4f\uff0c\u597d\u751f\u60ca\u5f02\uff0c\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u8882\uff0c\u540e\u4e00\u6392\u7684\u56db\u53e3\u5251\u4e00\u9f50\u523a\u5230\uff0c\u5374\u53c8\u500f\u7684\u5206\u5f00\uff0c\u524d\u540e\u5de6\u53f3\uff0c\u56db\u67c4\u5251\u540c\u65f6\u8fdb\u62db\uff0c\u7684\u662f \u602a\u5f02\u4e4b\u6781\uff0c\u654f\u6377\u65e0\u8bba\u3002\u7ea2\u8863\u756a\u50e7\u4e00\u4e2a\u95ea\u8eab\uff0c\u5de6\u638c\u4e00\u9707\uff0c\u907f\u5f00\u4e86\u540e\u9762\u7684\u4e00\u5251\uff0c\u53c8\u9707\u6b6a\u4e86\u524d\u9762\u7684 \u5251\u70b9\uff0c\u4f46\u5de6\u53f3\u4e24\u5251\uff0c\u5df2\u582a\u582a\u523a\u5230\u8eab\u4e0a\uff0c\u9648\u5929\u5b87\u5927\u58f0\u53eb\u201c\u597d\u201d\uff01\u51b0\u5ddd\u5929\u5973\u7709\u5934\u4e00\u76b1\uff0c\u53eb\u9053\uff1a \u201c\u4f8d\u513f\u5c0f\u5fc3\u4e86\uff01\u201d\u9661\u7136\u4e4b\u95f4\uff0c\u5ffd\u89c1\u90a3\u56db\u540d\u4f8d\u5973\uff0c\u4e00\u9f50\u98de\u8dc3\u8d77\u6765\uff0c\u7ed9\u7ea2\u8863\u756a\u50e7\u5927\u559d\u4e00\u58f0\uff0c\u638c\u6756 \u517c\u65bd\uff0c\u6392\u5c71\u5012\u6d77\u822c\u5730\u76f4\u5288\u8fc7\u53bb\u3002 \u3000\u3000\u539f\u6765\u90a3\u7ea2\u8863\u756a\u50e7\u7cbe\u64c5\u745c\u5496\u4e4b\u672f\uff0c\u808c\u8089\u53ef\u4ee5\u968f\u610f\u626d\u66f2\u53d8\u5f62\uff0c\u5de6\u53f3\u4e24\u540d\u4f8d\u5973\u7684\u957f\u5251\u521a\u521a\u6cbe\u7740 \u4ed6\u7684\u8863\u88f3\uff0c\u5ffd\u89c9\u5251\u5c16\u4e00\u6ed1\uff0c\u4ed6\u7684\u4e24\u6761\u81c2\u818a\u7a81\u7136\u4e00\u4e2a\u62d0\u5f2f\uff0c\u66b4\u957f\u51e0\u5bf8\uff0c\u7985\u6756\u547c\u547c\u631f\u98ce\uff0c\u638c\u52bf\u6467 \u5c71\u88c2\u77f3\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u53d1\u51fa\u5185\u5bb6\u771f\u529b\uff0c\u7acb\u5373\u8f6c\u5b88\u4e3a\u653b\uff01 \u3000\u3000\u7ea2\u8863\u756a\u50e7\u5374\u4e5f\u6599\u4e0d\u5230\u51b0\u5bab\u4f8d\u5973\u7684\u8f7b\u529f\u7adf\u7136\u5982\u6b64\u9ad8\u660e\uff0c\u4e00\u6756\u51fb\u7a7a\uff0c\u4e5d\u540d\u4f8d\u5973\u7684\u8eab\u5f62\u5df2\u6563\u56db \u65b9\uff0c\u6070\u4f3c\u6674\u8712\u63a0\u6c34\uff0c\u5f69\u8776\u7a7f\u82b1\uff0c\u5de6\u7a7f\u53f3\u63d2\uff0c\u5ffd\u5408\u5ffd\u5206\uff0c\u7ea2\u8863\u756a\u618e\u4e00\u8fde\u53d1\u51fa\u51e0\u8bb0\u6076\u62db\uff0c\u5374\u662f\u4e00 \u4e2a\u4e5f\u6253\u4e0d\u7740\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u8fd9\u4e5d\u540d\u4f8d\u5973\u5df2\u5e03\u6210\u4e00\u4e2a\u9635\u52bf\uff0c\u5c06\u7ea2\u8863\u756a\u50e7\u5f15\u5230\u6838\u5fc3\u3002 \u3000\u3000\u90a3\u756a\u50e7\u76d8\u819d\u4e00\u5750\uff0c\u53c8\u60f3\u7528\u9002\u624d\u5bf9\u4ed8\u94c1\u62d0\u4ed9\u4e4b\u6cd5\uff0c\u5e94\u4ed8\u51b0\u5bab\u4f8d\u5973\u7684\u56f4\u653b\uff0c\u5c82\u77e5\u5e94\u4ed8\u4e00\u4eba\u81ea \u53ef\uff0c\u540c\u65f6\u5e94\u4ed8\u4e5d\u4eba\u5374\u5927\u662f\u8270\u96be\u3002\u90a3\u4e5d\u540d\u4f8d\u5973\u8eab\u5f62\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u957f\u5251\u6240\u6307\u4e4b\u5904\uff0c\u5168\u662f\u4eba\u8eab\u7684\u8981\u5bb3 \u7a74\u9053\uff0c\u756a\u50e7\u7684\u745c\u4f3d\u8fd8\u672a\u7ec3\u5230\u6700\u4e0a\u4e58\u7684\u5883\u754c\uff0c\u8981\u5c01\u95ed\u5168\u8eab\u7684\u7a74\u9053\uff0c\u53c8\u8981\u5206\u795e\u5e94\u654c\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f \u4f46\u89c1\u4ed6\u7aef\u5750\u4e00\u9635\uff0c\u88ab\u653b\u5f97\u7d27\u65f6\uff0c\u4e0d\u7531\u81ea\u5df1\u5c31\u8df3\u8d77\u6765\uff0c\u7985\u6756\u6325\u821e\u4e00\u9635\uff0c\u53c8\u518d\u8dcc\u5750\u5730\u4e0a\uff0c\u5982\u662f\u8005 \u4e09\u756a\u56db\u6b21\uff0c\u5ffd\u8dc3\u5ffd\u5750\uff0c\u72b6\u751a\u6ed1\u7a3d\uff0c\u9648\u5929\u5b87\u4e0d\u89c9\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u90a3\u756a\u50e7\u5c82\u662f\u5bb9\u4eba\u803b\u7b11\u4e4b\u4eba\uff0c\u6012\u706b\u9661\u8d77\uff0c\u628a\u5fc3\u4e00\u6a2a\uff1a\u201c\u7ba1\u5979\u4ec0\u4e48\u516c\u4e3b\u4e0d\u516c\u4e3b\uff0c\u6211\u5148\u4f24\u4e86\u5979 \u7684\u4e24\u4e2a\u4f8d\u5973\u518d\u8bf4\uff01\u201d\u4e00\u8dc3\u800c\u8d77\uff0c\u5f62\u5982\u602a\u9e1f\u6469\u4e91\uff0c\u7985\u6756\u6a2a\u7a7a\u75be\u626b\u3002\u4e5d\u540d\u4f8d\u5973\u6025\u6025\u5206\u6563\uff0c\u90a3\u756a\u50e7 \u4e00\u58f0\u5927\u559d\uff0c\u7740\u7740\u62a2\u653b\uff0c\u4e00\u6839\u7985\u6756\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u4f3c\u4e4e\u5df2\u8c41\u51fa\u6027\u547d\uff0c\u4e0b\u624b\u7edd\u4e0d\u7559\u60c5\uff0c\u8fd9 \u756a\u50e7\u529f\u529b\u6781\u9ad8\uff0c\u8fdc\u5728\u51b0\u5bab\u7684\u4e00\u7fa4\u5f85\u5973\u4e4b\u4e0a\uff0c\u7985\u4ed7\u6240\u5230\u4e4b\u5904\uff0c\u5a01\u731b\u4e4b\u6781\uff0c\u4f17\u5f85\u5973\u4e0d\u6562\u786c\u63a5\uff0c\u53ea \u6709\u8eb2\u907f\uff0c\u9648\u5927\u5b57\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u4f3c\u6b64\u4e0b\u53bb\uff0c\u96be\u514d\u4e0d\u7ed9\u4ed6\u6253\u4f24\u4e00\u4e24\u4e2a\u4eba\uff0c\u8fd9\u5374\u5982\u4f55\u662f \u597d\uff1f\u201d \u3000\u3000\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u6cf0\u7136\u81ea\u82e5\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u90a3\u4e5d\u540d\u4f8d\u5973\u500f\u7136\u53d8\u9635\uff0c\u56db\u65b9\u6e38\u8d70\uff0c\u5ffd\u5408\u5ffd\u5206\uff0c\u4f9d\u4ed7 \u82b1\u56ed\u4e2d\u90a3\u4e9b\u602a\u77f3\u4f5c\u4e3a\u5c4f\u969c\uff0c\u9635\u52bf\u6446\u5f00\uff0c\u6709\u5982\u91cd\u95e8\u53e0\u6237\uff0c\u53d8\u5316\u65e0\u7aef\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u71ce\u4e71\uff0c\u4e5d\u540d\u4f8d \u5973\u5954\u8dd1\u8d77\u6765\uff0c\u5c31\u5982\u540c\u6570\u5341\u767e\u4eba\u4e00\u6837\uff0c\u6ee1\u56ed\u5b50\u7ef8\u5e26\u98d8\u98d8\uff0c\u7fbd\u8863\u95ea\u52a8\uff0c\u771f\u8c61\u201c\u5929\u5973\u6563\u82b1\u201d\u4e4b\u821e\uff0c \u597d\u770b\u715e\u4eba\u3002\u94c1\u62d0\u4ed9\u672c\u6765\u662f\u95ed\u76ee\u9759\u5750\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u81ea\u89c9\u5730\u7741\u5f00\u4e86\u773c\u775b\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u4e0d \u7981\u6697\u6697\u60ca\u5947\uff0c\u51b0\u5bab\u4f8d\u5973\u6240\u5e03\u7684\u9635\u5f62\uff0c\u7adf\u4f3c\u8bf8\u845b\u6b66\u5019\u6240\u4f20\u4e0b\u7684\u516b\u9635\u56fe\uff0c\u53ea\u662f\u5374\u53c8\u5e76\u4e0d\u5b8c\u5168\u4e00 \u6837\uff0c\u516b\u4e2a\u4f8d\u5973\u5404\u8e0f\u7740\u4e00\u4e2a\u65b9\u4f4d\uff0c\u6697\u5408\u4f11\u3001\u751f\u3001\u4f24\u3001\u675c\u3001\u6b7b\u3001\u666f\u3001\u60ca\u3001\u5f00\u516b\u95e8\uff0c\u4efb\u662f\u5982\u4f55\u8f6c \u52a8\uff0c\u8fd9\u516b\u95e8\u90fd\u5728\u4e92\u76f8\u547c\u5e94\u3002\u4f46\u4e0e\u516b\u9635\u56fe\u4e0d\u540c\u4e4b\u5904\uff0c\u5374\u5728\u591a\u51fa\u4e00\u4eba\uff0c\u8fd9\u4e00\u4eba\u5e76\u4e0d\u968f\u7740\u8f6c\u52a8\uff0c\u597d \u50cf\u662f\u9547\u5b88\u4e2d\u67a2\u7684\u4e3b\u8111\u4eba\u7269\uff0c\u5374\u53c8\u5e76\u4e0d\u51fa\u624b\u3002\u90a3\u756a\u50e7\u4e5f\u4f3c\u89c9\u5bdf\u51fa\u6765\uff0c\u8fde\u8fde\u62a2\u653b\uff0c\u60f3\u5148\u51fb\u5012\u90a3\u4e2a \u4f8d\u5973\uff0c\u53ef\u662f\u9635\u56fe\u5947\u5999\uff0c\u4ed6\u8fc8\u6b65\u5411\u4e1c\uff0c\u897f\u9762\u5c31\u94bb\u51fa\u5165\u6765\u5411\u4ed6\u88ad\u51fb\uff0c\u4ed6\u8fc8\u6b65\u5411\u897f\uff0c\u4e1c\u8fb9\u5357\u8fb9\uff0c\u957f \u5251\u53c8\u500f\u7136\u523a\u5230\uff0c\u600e\u4e48\u6837\u4e5f\u5360\u4e0d\u7740\u9635\u56fe\u7684\u5fc3\u8179\u4e4b\u5730\uff0c\u5230\u4e0d\u4e86\u90a3\u4e2a\u4f8d\u5973\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u8fd9\u756a\u50e7\u6b66\u529f\u4e5f\u786e\u662f\u9ad8\u5f3a\uff0c\u867d\u7136\u4e0d\u8bc6\u9635\u56fe\uff0c\u4ecd\u662f\u594b\u6218\u4e0d\u5df2\uff0c\u7985\u6756\u547c\u547c\u631f\u98ce\uff0c\u626b\u5728\u5047\u5c71\u6e56\u77f3 \u4e4b\u4e0a\uff0c\u77f3\u5757\u4e5f\u788e\u88c2\u6210\u7247\u7247\uff0c\u626c\u8d77\u5c18\u571f\uff0c\u51b0\u5ddd\u5929\u5973\u7709\u5934\u4e00\u76b1\uff0c\u53ea\u542c\u5f97\u90a3\u4e3a\u9996\u7684\u4f8d\u5973\u53eb\u9053\uff1a\u201c\u4f60 \u8fd9\u53ae\u592a\u8fc7\u65e0\u793c\uff0c\u5c45\u7136\u6562\u6bc1\u574f\u6211\u5bab\u4e2d\u7684\u7f8e\u666f\u4e48\uff1f\u201d\u53cc\u6307\u4e00\u5f39\uff0c\u5ffd\u542c\u5f97\u55e4\u55e4\u7684\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u9aa4 \u7136\u88ad\u5230\uff0c\u756a\u50e7\u7b11\u9053\uff1a\u201c\u6697\u5668\u5c82\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u201d\u7985\u6756\u4e00\u6325\uff0c\u5468\u8eab\u98ce\u96e8\u4e0d\u900f\uff0c\u90a3\u6697\u5668\u4e5f\u4e0d\u77e5\u662f\u4ec0 \u4e48\u4e1c\u897f\uff0c\u4e00\u9897\u9897\u597d\u4f3c\u73cd\u73e0\u5927\u5c0f\uff0c\u4eae\u6676\u6676\u7684\uff0c\u4ece\u7a7a\u4e2d\u6d12\u4e0b\uff0c\u88ab\u90a3\u6756\u98ce\u6fc0\u8361\uff0c\u500f\u5ffd\u969c\u88c2\u6210\u7c89\uff0c\u6563 \u51fa\u5bd2\u5149\u51b7\u6c14\uff0c\u90a3\u756a\u50e7\u4e0d\u7531\u81ea\u5df1\u5730\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u5929\u6e56\u5723\u5cf0\u4e4b\u4e0a\uff0c\u6709\u7684\u662f\u4e98\u53e4\u4e0d\u5316\u7684\u5bd2\u51b0\uff0c\u51b0\u5ddd\u5929\u5973\u4ece\u5343\u4e08\u51b0\u7a9f\u4e4b\u4e2d\uff0c\u64b7\u53d6\u51b0\u9b44\u7cbe\u82f1\uff0c\u7ec3 \u6210\u4e86\u4e00\u79cd\u4e16\u4e0a\u72ec\u4e00\u65e0\u4e8c\u7684\u5947\u95e8\u6674\u5668\uff0c\u5176\u540d\u5c31\u53eb\u505a\u201c\u51b0\u9b44\u795e\u5f39\u201d\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u6697\u5668\uff0c\u6216\u7528\u4ee5\u4f24 \u4eba\uff0c\u6216\u7528\u4ee5\u6253\u7a74\uff0c\u6240\u8bb2\u7a76\u7684\u4e0d\u5916\u4e4e\u662f\u51c6\u5934\uff0c\u52b2\u529b\u7684\u529f\u592b\uff0c\u6216\u8005\u518d\u52a0\u4e0a\u6697\u5668\u672c\u8eab\u7684\u950b\u5229\uff0c\u552f\u6709 \u201c\u51b0\u9b44\u795e\u5f39\u201d\u4e0e\u4f17\u4e0d\u540c\uff0c\u5b83\u6240\u501a\u4ed7\u7684\u5c31\u662f\u4e07\u8f7d\u5bd2\u51b0\u7684\u90a3\u79cd\u9634\u51b7\u4e4b\u6c14\uff0c\u7834\u88c2\u4e4b\u540e\uff0c\u5bd2\u6c14\u53d1\u51fa\uff0c \u7aef\u7684\u662f\u5389\u5bb3\u3002 \u3000\u3000\u672c\u6765\u7ea2\u8863\u756a\u50e7\u7684\u529f\u529b\u539f\u53ef\u62b5\u5fa1\uff0c\u4f46\u4ed6\u8981\u5168\u795e\u8d2f\u6ce8\u5e94\u4ed8\u51b0\u5bab\u4f8d\u5973\u7684\u56f4\u653b\uff0c\u54ea\u80fd\u5206\u51fa\u5fc3\u795e\uff0c \u8fd0\u529f\u9632\u5fa1\u3002\u51b0\u5f39\u51b0\u5251\uff0c\u5bd2\u6c14\u6fc0\u8361\uff0c\u6108\u6765\u6108\u6d53\uff0c\u7ea2\u8863\u756a\u50e7\u7259\u5173\u6253\u6218\uff0c\u6e10\u89c9\u5fcd\u53d7\u4e0d\u4f4f\u3002\u53ea\u89c1\u4ed6\u72c2 \u547c\u75be\u626b\uff0c\u72b6\u82e5\u75af\u72c2\uff0c\u989d\u89d2\u6c81\u51fa\u6c57\u73e0\uff0c\u5374\u53c8\u5168\u8eab\u98a4\u6296\u3002\u51b0\u5ddd\u592d\u5973\u7b11\u5bf9\u829d\u5a1c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ae\u5f3a\u7528\u5185 \u5bb6\u771f\u529b\uff0c\u4ee5\u4e3a\u53ef\u4ee5\u53d1\u70ed\uff0c\u54ea\u77e5\u8fd9\u6837\u4e00\u6765\uff0c\u51b7\u70ed\u4ea4\u6218\uff0c\u6700\u662f\u4f24\u4eba\uff0c\u8fd9\u6b21\u4ed6\u7eb5\u4fdd\u5f97\u4e86\u6027\u547d\uff0c\u53ea\u6050 \u4e5f\u8981\u5927\u75c5\u51e0\u5929\u3002\u201d\u9648\u5929\u5b87\u5fc3\u5730\u5584\u826f\uff0c\u5927\u7740\u80c6\u5b50\u5bf9\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90a3\u5c31\u9976\u4e86\u4ed6\u5427\uff1f\u201d\u829d\u5a1c\u8198\u4e86 \u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5012\u66ff\u4ed6\u6c42\u60c5\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u7f6e\u53ef\u5426\u3002 \u3000\u3000\u7ea2\u8863\u756a\u50e7\u9ad8\u547c\u9163\u6597\uff0c\u8d8a\u6765\u8d8a\u89c9\u7cbe\u795e\u4e0d\u6d4e\uff0c\u4f46\u89c1\u90a3\u7fa4\u51b0\u5bab\u4f8d\u5973\u7a7f\u6765\u63d2\u53bb\uff0c\u773c\u524d\u4eba\u5f71\u5982\u6f6e\uff0c \u5f69\u8272\u7f24\u7eb7\uff0c\u76ee\u7729\u795e\u8ff7\uff0c\u773c\u82b1\u9563\u4e71\uff0c\u4e3a\u9996\u7684\u4f8d\u5973\u5a07\u559d\u4e00\u58f0\uff1a\u201c\u4eba\u5012\u4e5f\uff01\u201d\u626c\u624b\u53c8\u662f\u4e00\u679a\u51b0\u9b44\u795e \u5f39\uff0c\u7ea2\u8863\u756a\u50e7\u5fc3\u5934\u4e00\u51b7\uff0c\u811a\u8ddf\u4e00\u8f6f\uff0c\u53ea\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u6447\u6447\u6b32\u5760\uff0c\u5ffd\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201c\u5f80 \u624b\uff01\u201d\u7741\u773c\u770b\u65f6\uff0c\u4e5d\u540d\u4f8d\u5973\u65e9\u5df2\u6536\u5251\u9000\u4e0b\uff0c\u6392\u6210\u4e24\u5217\uff0c\u5206\u7acb\u5728\u51b0\u5ddd\u5929\u5973\u7684\u8eab\u65c1\uff0c\u7ea2\u8863\u756a\u50e7\u6ee1 \u9762\u7f9e\u60ed\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u6df1\u6df1\u7684\u5438\u4e86\u51e0\u53e3\u6c14\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u5411\u51b0\u5ddd\u5929\u5973\u65bd\u4e86\u4e00\u793c\uff0c\u4fbf\u8dc3\u51fa\u51b0\u5bab\u3002\u4e24 \u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u5411\u51b0\u5ddd\u5929\u5973\u65bd\u793c\u4e4b\u540e\uff0c\u4e5f\u8bda\u60f6\u8bda\u6050\u5730\u8ddf\u5728\u540e\u9762\u3002\u7247\u523b\u4e4b\u540e\uff0c\u8d70\u5f97\u65e0\u8e2a\u65e0\u5f71\u3002 \u3000\u3000\u829d\u5a1c\u7b11\u9053\uff1a\u201c\u8fd9\u53ae\u5c45\u7136\u80fd\u95ef\u8fdb\u51b0\u5bab\uff0c\u672c\u4e8b\u4e5f\u59d4\u5b9e\u4e0d\u9519\uff0c\u771f\u5413\u715e\u6211\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u201c\u4e0d\u4f1a\u518d\u6709\u7b2c\u4e8c\u4e2a\u8fd9\u6837\u7684\u4eba\u4e86\uff0c\u5176\u5b9e\u8fd9\u756a\u50e7\u4e5f\u662f\u6211\u6709\u610f\u653e\u4ed6\u8fdb\u6765\u7684\uff0c\u8981\u4e0d\u7136\u4ed6\u867d\u7136\u80fd\u6e21\u8fc7\u51b0 \u5ddd\uff0c\u4e5f\u95ef\u4e0d\u8fc7\u6211\u5bab\u524d\u7684\u4e5d\u5929\u7384\u5973\u5927\u9635\u3002\u201d\u94c1\u62d0\u4ed9\u5fc3\u9053\uff1a\u539f\u6765\u5979\u628a\u8bf8\u845b\u6b66\u5019\u7684\u516b\u9635\u56fe\u52a0\u4ee5\u53d8 \u5316\uff0c\u6539\u4e86\u540d\u79f0\u3002\u5389\u5bb3\u662f\u5389\u5bb3\u7684\uff0c\u53ef\u662f\u82e5\u8bf4\u80fd\u5c3d\u6321\u5929\u4e0b\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\uff0c\u53ea\u6015\u4e5f\u672a\u89c1\u5f97\uff0c\u94c1\u62d0\u4ed9 \u662f\u7518\u51e4\u4ed6\u7684\u5927\u5f1f\u5b50\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u6df1\u77e5\u4eba\u5916\u6709\u4eba\uff0c\u5929\u5916\u6709\u5929\uff0c\u6b66\u5b66\u4e4b\u6df1\uff0c\u6709\u5982\u5927\u6d77\uff0c\u6240\u4ee5\u867d\u7136 \u8d25\u5728\u756a\u50e7\u4e4b\u624b\uff0c\u5bf9\u51b0\u5ddd\u5929\u5973\u7684\u81ea\u8d1f\uff0c\u5374\u662f\u4e0d\u4ee5\u4e3a\u7136\u3002 \u3000\u3000\u51b0\u5ddd\u5927\u5973\u89c1\u94c1\u62d0\u4ed9\u5634\u5507\u5fae\u52a8\uff0c\u4f3c\u6b32\u4f5c\u58f0\uff0c\u8d70\u8fc7\u53bb\u770b\uff0c\u53ea\u89c1\u4ed6\u9762\u8272\u7070\u767d\uff0c\u5c31\u4f3c\u5927\u75c5\u4e4b\u540e\uff0c \u5c1a\u672a\u6062\u590d\u7684\u4eba\u4e00\u6837\uff0c\u8c22\u4e91\u771f\u9053\uff1a\u201c\u4ed6\u8c22\u8c22\u4f60\u7684\u6069\u5178\uff0c\u53ea\u662f\u73b0\u4e0b\u6050\u96be\u8d70\u52a8\uff0c\u8bf7\u4f60\u6d3e\u4e24\u540d\u4f8d\u5973\u9001 \u4ed6\u4e0b\u5c71\u3002\u201d\u51b0\u5ddd\u5929\u5973\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5e78\u4e8f\u4f60\u7684\u4f0f\u9b54\u6756\u53ea\u4f7f\u5230\u4e5d\u5341\u516d\u62db\uff0c\u82e5\u7136\u628a\u4e00\u767e\u96f6\u516b\u8def \u4f7f\u5b8c\uff0c\u7eb5\u6709\u7075\u4e39\u5723\u836f\uff0c\u4e5f\u96be\u6062\u590d\u4f60\u771f\u5143\u4e4b\u6c14\u3002\u73b0\u5728\u4f60\u53ef\u4e0d\u80fd\u8d70\u4e86\uff01\u201d \u3000\u3000\u8c22\u4e91\u771f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u4ed6\u8017\u635f\u8fc7\u5ea6\uff0c\u516d\u8109\u5931\u8c03\uff0c\u6c14\u8840 \u9006\u884c\uff0c\u4e94\u810f\u6613\u4f4d\uff0c\u82e5\u7136\u5f3a\u8981\u4e0b\u5c71\uff0c\u5728\u51b0\u5ddd\u4e4b\u4e2d\uff0c\u4e00\u53d7\u6fc0\u8361\uff0c\u6b7b\u662f\u6b7b\u4e0d\u4e86\u7684\uff0c\u4f46\u53ea\u6050\u5c31\u6b64\u4fbf\u8981 \u7ec8\u8eab\u6b8b\u5e9f\uff0c\u867d\u6709\u94c1\u62d0\uff0c\u4e5f\u4e0d\u80fd\u8d70\u8def\u5566\uff01\u4ee5\u4ed6\u7684\u529f\u529b\uff0c\u9759\u517b\u4e94\u65e5\uff0c\u4f50\u4ee5\u836f\u7269\uff0c\u5927\u7ea6\u4fbf\u53ef\u590d\u539f\u3002 \u597d\uff0c\u6211\u5c31\u4ee5\u4e94\u65e5\u4e3a\u671f\u3002\u201d\u4e00\u62db\u624b\u5524\u6765\u4e00\u540d\u4f8d\u5973\uff0c\u9053\uff1a\u201c\u4f60\u7ed9\u4ed6\u6536\u62fe\u4e00\u95f4\u9759\u5ba4\uff0c\u8ba9\u4ed6\u597d\u597d\u7528 \u529f\uff0c\u8c01\u90fd\u4e0d\u8bb8\u6253\u6270\u4ed6\uff01\u5c06\u5bab\u4e2d\u7684\u6e29\u7389\u60dc\u7ed9\u4ed6\u7528\u3002\u201d\u5429\u5490\u4e86\u4f8d\u5973\u4e4b\u540e\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c \u5bf9\u8c22\u4e91\u771f\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u4e3a\u4f60\u4eec\u7279\u522b\u7834\u4f8b\uff0c\u8ba9\u4f60\u4eec\u591a\u4f4f\u4e94\u65e5\uff0c\u4e94\u65e5\u4e4b\u540e\uff0c\u4f60\u4eec\u81ea\u5df1\u4e0b\u5c71\uff0c\u4e5f\u4e0d \u5fc5\u5411\u6211\u8f9e\u884c\u5566\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8bf4\u8bdd\u795e\u60c5\uff0c\u751a\u662f\u8f7b\u63cf\u6de1\u5199\uff0c\u8c22\u4e91\u771f\u542c\u4e86\uff0c\u5374\u662f\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u4e08\u592b\u6240\u53d7\u5185 \u4f24\uff0c\u7adf\u662f\u5982\u6b64\u4e25\u91cd\u3002\u51b0\u5ddd\u5929\u5973\u770b\u4f3c\u4e00\u70b9\u4e0d\u901a\u4eba\u60c5\uff0c\u4f46\u5374\u6168\u7136\u80af\u4ee5\u51b0\u5bab\u7684\u81f3\u5b9d\u4e07\u5e74\u6e29\u7389\u501f\u7528\uff0c \u7ed9\u4ed6\u7597\u4f24\uff0c\u53c8\u975e\u5be1\u60c5\u7edd\u4e49\u4e4b\u4eba\u53ef\u6bd4\u3002\u8fd9\u756a\u8bf4\u8bdd\uff0c\u771f\u4ee4\u94c1\u62d0\u4ed9\u592b\u5987\u557c\u5956\u7686\u975e\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u53ef\u81ea\u53bb\u7167\u6599\u4ed6\uff0c\u6ca1\u4e8b\u4e0d\u5fc5\u518d\u6765\u627e\u6211\uff0c\u201d\u5e26\u4e86\u4f8d\u5973\uff0c\u81ea\u884c\u53bb\u4e86\u3002\u8c22\u4e91\u771f \u6027\u60c5\u672c\u6765\u751a\u662f\u9ad8\u505a\uff0c\u7ecf\u4e86\u591a\u5e74\u78e8\u7ec3\uff0c\u867d\u7136\u6539\u4e86\u8bb8\u591a\uff0c\u4f46\u4ecd\u7136\u53d7\u4e0d\u4e86\u522b\u4eba\u7684\u50b2\u6c14\uff0c\u60f3\u4e0d\u5230\u6b64\u6b21 \u4e07\u91cc\u8fdc\u5948\uff0c\u4e13\u8bda\u5bfb\u8bbf\uff0c\u53ea\u56e0\u529d\u5979\u4e0b\u5c71\uff0c\u5374\u53d7\u5230\u5982\u65af\u51b7\u843d\uff0c\u8d8a\u60f3\u8d8a\u89c9\u4e0d\u503c\uff0c\u51e0\u4e4e\u60f3\u51fa\u8a00\u201c\u56de \u656c\u201d\uff0c\u4f46\u51b0\u5ddd\u5929\u5973\u867d\u7136\u6bd4\u5979\u66f4\u8981\u9ad8\u505a\u5341\u500d\uff0c\u5374\u7eaf\u662f\u51fa\u4e8e\u81ea\u7136\uff0c\u81ea\u6709\u4e00\u79cd\u98ce\u534e\u9ad8\u8d35\uff0c\u51db\u7136\u4e0d\u53ef \u4fb5\u72af\u7684\u795e\u60c5\uff0c\u53eb\u4eba\u4e0d\u6562\u4e0e\u5979\u5435\u5634\u3002\u8c22\u4e91\u771f\u53ea\u89c9\u4e00\u80a1\u95f7\u70ed\uff0c\u6a2a\u6897\u80f8\u4e2d\uff0c\u7a81\u7136\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u5455 \u51fa\u4e86\u80c3\u4e2d\u7684\u82e6\u6c34\u3002\u9648\u5929\u5b87\u60ca\u9053\uff1a\u201c\u5e08\u5a18\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8c22\u4e91\u771f\u9762\u8272\u82cd\u767d\uff0c\u5ffd\u800c\u7f69\u4e0a\u4e00\u5c42\u7ea2 \u6655\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u4f60\u7559\u5728\u8fd9\u513f\uff0c\u4e0d\u53ef\u591a\u4e8b\u3002\u201d\u795e\u60c5\u751a\u662f\u5947\u7279\uff0c\u6276\u8d77\u94c1\u62d0\u4ed9\u4e5f\u81ea\u8d70 \u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u95f7\u95f7\u4e0d\u4e50\uff0c\u6014\u6014\u5730\u7ad9\u5728\u90a3\u513f\uff0c\u829d\u5a1c\u9053\uff1a\u201c\u95f9\u4e86\u534a\u591c\uff0c\u4f60\u4e5f\u8be5\u6b47\u606f\u5566\uff0c\u660e\u65e5\u6211\u5e26\u4f60 \u8d4f\u89c8\u5bab\u4e2d\u7684\u5947\u666f\u3002\u201d\u9648\u5929\u5b87\u76ee\u9001\u5979\u7684\u80cc\u5f71\u6ca1\u5165\u82b1\u4e1b\uff0c\u60f3\u8d77\u4e94\u65e5\u4e4b\u540e\uff0c\u4ecd\u5f97\u4e0b\u5c71\uff0c\u800c\u4e14\u5e08\u7236\u5f97 \u7f6a\u4e86\u51b0\u5ddd\u5929\u5973\uff0c\u6b64\u540e\u66f4\u662f\u65e0\u7f18\u76f8\u89c1\uff0c\u5fc3\u4e2d\u8d8a\u53d1\u6005\u60c6\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u65e9\u6668\uff0c\u9648\u5929\u5b87\u4e00\u89c9\u9192\u6765\uff0c\u53ea\u89c1\u971e\u5149\u4e07\u9053\uff0c\u4ece\u7a97\u53e3\u671b\u5c06\u51fa\u53bb\uff0c\u53c8\u662f\u4e00\u756a\u666f\u8c61\uff0c\u900f\u660e \u7684\u51b0\u5bab\u5728\u7ea2\u65e5\u7167\u8000\u4e4b\u4e0b\uff0c\u4e94\u5f69\u8ff7\u79bb\uff0c\u5e7b\u6210\u4eba\u95f4\u7f55\u89c1\u7684\u5947\u666f\uff0c\u66f4\u4f3c\u795e\u8bdd\u4e2d\u7684\u4e16\u754c\u3002\u51b0\u5bab\u4f8d\u5973\u9001 \u6765\u7684\u65e9\u70b9\uff0c\u53ea\u6709\u4e24\u679a\u53c8\u7ea2\u53c8\u5927\u7684\u679c\u5b50\uff0c\u4f46\u5403\u4e86\u4e4b\u540e\uff0c\u5374\u662f\u751c\u7545\u65e0\u6bd4\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u829d\u5a1c\u679c\u7136\u8df5 \u7ea6\u800c\u6765\uff0c\u5e26\u9648\u5929\u5b87\u51fa\u5916\u6e38\u89c8\u3002\u829d\u5a1c\u6765\u5230\u51b0\u5bab\u4e4b\u540e\uff0c\u795e\u60c5\u4e5f\u4f3c\u6109\u60a6\u8bb8\u591a\uff0c\u867d\u7136\u7709\u5b57\u4e4b\u95f4\uff0c\u5018\u9690 \u9690\u85cf\u6709\u5e7d\u6028\uff0c\u4f46\u4e0e\u9648\u5929\u5b87\u6709\u8bf4\u6709\u7b11\uff0c\u4e0e\u521d\u89c1\u4e4b\u65f6\uff0c\u5df2\u5927\u4e0d\u76f8\u540c\uff0c\u597d\u50cf\u6625\u5929\u4e5f\u6765\u5230\u4e86\u5979\u7684\u7709 \u68a2\uff0c\u51b7\u6f20\u7684\u795e\u60c5\u4e5f\u968f\u7740\u5916\u9762\u7684\u51b0\u6cb3\u5728\u5f00\u59cb\u89e3\u51bb\u4e86\u3002 \u3000\u3000\u5bab\u4e2d\u5947\u666f\uff0c\u8d4f\u4e4b\u4e0d\u5c3d\uff0c\u56ed\u6797\u5e03\u7f6e\uff0c\u7f8e\u5999\u7edd\u4f26\u3002\u4ead\u6a84\u6c34\u77f3\uff0c\u53c2\u5dee\u9519\u843d\uff0c\u63a9\u6620\u6709\u81f4\u3002\u56de\u5eca\u66f2 \u6298\uff0c\u5a49\u8712\u4e1c\u897f\uff0c\u53ea\u662f\u90a3\u5eca\u58c1\u7684\u82b1\u7a97\uff0c\u5f62\u5f0f\u5c31\u5404\u5404\u4e0d\u540c\uff0c\u6784\u6210\u4f73\u4e3d\u7684\u56fe\u6848\u3002\u6240\u6709\u7684\u5efa\u7b51\uff0c\u751a\u81f3 \u5047\u5c71\u6e56\u77f3\uff0c\u90fd\u662f\u5927\u534a\u901a\u4f53\u6676\u83b9\u3002\u56ed\u4e2d\u6709\u597d\u51e0\u5904\u55b7\u6cc9\uff0c\u98de\u73e0\u6e85\u7389\uff0c\u5728\u6625\u9633\u707f\u70c2\u4e4b\u4e0b\uff0c\u6cdb\u8d77\u4e00\u5708 \u5708\u7684\u5f69\u8679\u3002\u8fd8\u6709\u5c0f\u6eaa\u66f2\u6298\uff0c\u8d2f\u7a7f\u5176\u4e2d\u3002\u829d\u5a1c\u9053\uff1a\u201c\u6c60\u5858\u548c\u6eaa\u6c34\uff0c\u90fd\u662f\u4ece\u5929\u6e56\u5f15\u6765\u7684\uff0c\u7279\u522b\u6e05 \u51bd\uff0c\u6211\u7d27\u559c\u6b22\u559d\u8fd9\u91cc\u7684\u6c34\u4e86\u3002\u201d\u5bab\u4e2d\u5404\u5904\u5ead\u9662\uff0c\u90fd\u7528\u5947\u5cf0\u602a\u77f3\uff0c\u968f\u610f\u70b9\u7f00\uff0c\u4e0e\u5404\u79cd\u82b1\u6811\u4e92\u76f8 \u63a9\u6620\uff0c\u51e0\u4e4e\u6bcf\u4e00\u5904\u90fd\u6784\u6210\u7f8e\u5999\u7684\u753b\u56fe\uff0c\u90a3\u4e9b\u82b1\u6811\uff0c\u5927\u534a\u8bf4\u4e0d\u51fa\u540d\u5b57\uff0c\u707f\u5982\u971e\u5f69\uff0c\u5fae\u98ce\u5439\u6765\uff0c \u9999\u6c14\u6c81\u4eba\u813e\u8151\uff0c\u9648\u5929\u5b87\u7b11\u9053\uff1a\u201c\u6b64\u5904\u771f\u5982\u4ed9\u5883\uff0c\u602a\u4e0d\u5f97\u51b0\u5ddd\u5929\u5973\u4e0d\u613f\u4e0b\u5c71\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u4fe1\u6b65\u6240\u4e4b\uff0c\u968f\u610f\u6e38\u8d4f\uff0c\u997f\u4e86\u5c31\u91c7\u6458\u56ed\u4e2d\u7684\u679c\u5b50\u5145\u9965\uff0c\u51b0\u5bab\u5360\u5730\u751a\u5e7f\uff0c\u8d70\u4e86\u5927\u534a\u5929\u5c1a \u672a\u8d70\u5b8c\uff0c\u884c\u8d70\u4e4b\u95f4\uff0c\u5ffd\u95fb\u5f97\u4e00\u80a1\u5f02\u9999\uff0c\u975e\u5170\u975e\u8e0c\uff0c\u9648\u5929\u5b87\u8d70\u8fc7\u53bb\u770b\uff0c\u53ea\u89c1\u524d\u9762\u6709\u4e00\u95f4\u5c16\u9876\u7684 \u623f\u5b50\uff0c\u5f62\u4f3c\u795e\u9f9b\uff0c\u7ed3\u6784\u975e\u5e38\u602a\u5f02\uff0c\u4e0e\u5bab\u4e2d\u6240\u6709\u7684\u5efa\u7b51\uff0c\u90fd\u4e0d\u76f8\u540c\u3002\u5176\u4ed6\u5efa\u7b51\u90fd\u662f\u7528\u6c34\u6676\u3001\u4e91 \u77f3\u3001\u6676\u76d0\u6216\u8005\u575a\u51b0\u6240\u9020\uff0c\u6676\u83b9\u5982\u7389\uff0c\u53ea\u6709\u8fd9\u4e00\u95f4\u5c4b\u5b50\u5374\u662f\u9ed1\u9edd\u9edd\u7684\uff0c\u7279\u522b\u60f9\u4eba\u6ce8\u610f\u3002\u90a3\u975e\u5170 \u975e\u56db\u7684\u5e7d\u9999\uff0c\u5c31\u662f\u4ece\u8fd9\u95f4\u623f\u5b50\u4e2d\u53d1\u6563\u51fa\u6765\u3002\u9648\u5929\u5b87\u597d\u5947\u5fc3\u8d77\uff0c\u60f3\u63a8\u95e8\u5165\u53bb\uff0c\u829d\u5a1c\u9762\u8272\u4e00\u53d8\uff0c \u6025\u5fd9\u6b62\u4f4f\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0a\u6b21\u5728\u8fd9\u91cc\u4f4f\u7684\u65f6\u5019\uff0c\u5929\u5973\u59d0\u59d0\u5c31\u66fe\u5429\u5490\u8fc7\u6211\uff0c\u8bf4\u662f\u4ec0\u4e48\u90fd\u53ef\u4ee5 \u4efb\u6211\u81ea\u884c\u53bb\u73a9\u3002\u53ea\u6709\u8fd9\u4e00\u95f4\u5c4b\u5b50\uff0c\u4e0d\u80fd\u8fdb\u53bb\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u8c01\u77e5\u9053 \u5462\uff1f\u542c\u5bab\u4e2d\u7684\u4f8d\u5973\u8bf4\uff0c\u51b0\u5ddd\u5929\u5973\u6bcf\u9022\u6714\u671b\u4e4b\u591c\uff0c\u5c31\u8981\u72ec\u81ea\u5230\u8fd9\u95f4\u5c4b\u53bb\uff0c\u803d\u6401\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5979\u505a \u4ec0\u4e48\u3002\u8c01\u4e5f\u4e0d\u6562\u95ee\u3002\u542c\u4f8d\u5973\u8bf4\uff0c\u8fd9\u95ee\u5c4b\u5b50\u662f\u7528\u4e00\u79cd\u9999\u6728\u505a\u7684\uff0c\u8fd9\u79cd\u9999\u6728\uff0c\u82e5\u711a\u70e7\u8d77\u6765\uff0c\u9999\u6c14 \u53ef\u4ee5\u4f20\u81f3\u5341\u91cc\u4e4b\u5916\u3002\u201d\u9648\u5929\u5b87\u542c\u4e86\uff0c\u597d\u5947\u4e4b\u5fc3\uff0c\u66f4\u662f\u5927\u8d77\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u9648\u5929\u5b87\u7ffb\u6765\u8986\u53bb\uff0c\u5ff5\u5ff5\u4e0d\u5fd8\u90a3\u95ee\u795e\u79d8\u7684\u5c4b\u5b50\uff0c\u6726\u6726\u80e7\u80e7\u4e4b\u95f4\u505a\u4e86\u4e00\u4e2a\u68a6\uff0c\u68a6\u89c1\u51b0 \u5ddd\u5929\u5973\u5728\u91cc\u9762\u711a\u9999\u7948\u7977\uff0c\u829d\u5a1c\u4f8d\u7acb\u5728\u5979\u7684\u8eab\u65c1\uff0c\u81ea\u5df1\u4e0d\u77e5\u600e\u7684\uff0c\u4e5f\u5230\u4e86\u91cc\u9762\uff0c\u5ffd\u7136\u95f4\u51b0\u5ddd\u5929 \u5973\u62d4\u51fa\u4e00\u67c4\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u5411\u81ea\u5df1\u5fc3\u7a9d\u4e00\u6307\uff0c\u5979\u7684\u957f\u53d1\u7a81\u7136\u5316\u4e3a\u65e0\u6570\u98de\u86c7\uff0c\u5411\u81ea\u5df1\u98de\u6765\uff0c \u829d\u5a1c\u9a87\u53eb\u4e00\u58f0\uff0c\u90a3\u5c4b\u5b50\u9686\u4e00\u58f0\u5c31\u5012\u584c\u4e86\u3002\u9648\u5927\u5b87\u7ed9\u90a3\u5c16\u9876\u7684\u5de8\u6728\u538b\u7740\uff0c\u6323\u624e\u547c\u5524\uff0c\u5ffd\u95fb\u5f97\u829d \u5a1c\u5728\u8033\u8fb9\u53eb\u9053\uff1a\u201c\u4f60\u68a6\u89c1\u4ec0\u4e48\u4e86\uff1f\u9192\u6765\uff0c\u9192\u6765\uff01\u201d\u9648\u5929\u5b87\u521a\u7741\u5f00\u773c\uff0c\u53ea\u597d\u5f97\u5916\u9762\u53c8\u662f\u8f70\u9686\u4e00 \u58f0\uff0c\u51e0\u7591\u8fd8\u662f\u68a6\u4e2d\uff0c\u829d\u5a1c\u63a8\u4ed6\u4e00\u628a\uff0c\u9053\uff1a\u201c\u5feb\u8d77\u6765\u770b\uff0c\u51b0\u5bab\u4e2d\u53c8\u6709\u4e00\u4e2a\u602a\u5ba2\u95ef\u8fdb\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u9648\u5929\u5b87\u7761\u610f\u5168\u6d88\uff0c\u53c8\u6709\u4e00\u4e2a\u602a\u5ba2\u95ef\u8fdb\u51b0\u5bab\uff01\u771f\u771f\u662f\u9a87\u4eba\u95fb\u542c\uff01\u9648\u5929\u5b87\u9053\uff1a\u201c\u4ed6\u80fd \u591f\u6e21\u8fc7\u51b0\u6cb3\uff0c\u95ef\u8fc7\u5bab\u5916\u7684\u4e5d\u5929\u7384\u5973\u9635\u4e48\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u82e5\u975e\u95ef\u8fc7\uff0c\u600e\u80fd\u6765\u5230\u51b0\u5bab\uff0c\u73b0\u5728\u5bab\u4e2d \u9e23\u949f\u62a5\u8b66\u3002\u5929\u5973\u59d0\u59d0\u5c31\u8981\u51fa\u6765\u4e86\u5462\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u6025\u6025\u62ab\u8863\u800c\u8d77\uff0c\u8d76\u51fa\u5916\u9762\uff0c\u53ea\u89c1\u6628\u65e5\u90a3\u4e5d\u540d\u4f8d\u5973\uff0c\u53c8\u5df2\u5e03\u597d\u9635\u5f62\uff0c\u5c06\u4e00\u4e2a\u767d\u8863\u5c11 \u5e74\u56f4\u5728\u5f53\u4e2d\uff0c\u5251\u62d4\u664b\u5f20\uff0c\u5c1a\u672a\u52a8\u624b\uff0c\u9648\u5929\u5b87\u4e00\u770b\uff0c\u4e0d\u7981\u9a87\u7136\u5931\u58f0\u3002\u829d\u5a1c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9648\u5929 \u5b87\u9053\uff1a\u201c\u8fd9\u4eba\u6211\u8ba4\u8bc6\u7684\uff01\u201d\u8fd9\u5239\u90a3\u95ee\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u4e5f\u770b\u5230\u9648\u5929\u5b87\uff0c\u56de\u5934\u4e00\u7b11\uff0c\u4f3c\u662f\u62db\u547c\uff0c\u9648 \u5929\u5b87\u770b\u5f97\u66f4\u6e05\u695a\u4e86\u3002 \u3000\u3000\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u9648\u5929\u5b87\u5728\u8def\u4e0a\u6240\u9047\u89c1\u7684\u90a3\u4e2a\u5c11\u5e74\u4e66\u751f\uff0c\u66fe\u7528\u4e00\u628a\u91d1\u8ba1\u6551\u8fc7\u8427\u9752\u5cf0\uff0c\u53c8\u66fe \u5728\u65e5\u5580\u5219\u4e4b\u591c\uff0c\u5c06\u9ea6\u5927\u4fa0\u7b49\u4e00\u5e72\u4eba\u90fd\u5f15\u8d70\u7684\u90a3\u4e2a\u5c11\u5e74\u4e66\u751f\uff01 \u3000\u3000\u829d\u5a1c\u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u7684\u540d\u5b57\uff0c\u4f46\u4ed6\u66fe\u6551\u8fc7\u6211\u5e08\u7236\u7684\u6027\u547d\uff0c \u60f3\u6765\u8be5\u662f\u4e2a\u597d\u4eba\u3002\u201d\u829d\u5a1c\u9053\uff1a\u201c\u554a\uff0c\u7cdf\u4e86\uff01\u521a\u624d\u6211\u542c\u5f97\u51b0\u5bab\u4f8d\u5973\u8bf4\uff0c\u5929\u5973\u59d0\u59d0\u751f\u6c14\u5f97\u4e0d\u5f97 \u4e86\uff0c\u8bf4\u662f\u82e5\u4e0d\u91cd\u91cd\u60e9\u6212\u6765\u4eba\uff0c\u51b0\u5bab\u5c31\u96be\u4fdd\u5b81\u9759\u4e86\u3002\u51b0\u5bab\u9632\u536b\uff0c\u4e00\u5c42\u5f3a\u8fc7\u4e00\u5c42\uff0c\u8fd9\u4e5d\u540d\u4f8d\u5973\u6b66 \u529f\u9ad8\u5f3a\uff0c\u8fdc\u975e\u5bab\u5916\u7684\u53ef\u6bd4\uff0c\u4ed6\u8fd9\u6b21\u4e0d\u6b7b\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\uff01\u201d \u3000\u3000\u90a3\u4e5d\u540d\u4f8d\u5973\u521a\u521a\u62d4\u51fa\u957f\u5251\uff0c\u5ffd\u7136\u53c8\u505c\u4e0b\u624b\uff0c\u6ee1\u9662\u5b50\u5bc2\u9759\u65e0\u58f0\uff0c\u8fde\u4e00\u6839\u7ee3\u82b1\u9488\u8dcc\u5728\u5730\u4e0b\u90fd \u542c\u5f97\u89c1\u54cd\uff0c\u9648\u5929\u5b87\u626d\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u5df2\u6765\u5230\u573a\u4e2d\uff0c\u9762\u6709\u6012\u5bb9\uff0c\u89c1\u5230\u90a3\u4e2a\u5c11\u5e74\uff0c\u5fae\u5fae \u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u795e\u60c5\u7a81\u7136\u4e00\u53d8\uff0c\u4f3c\u4e4e\u9887\u4e3a\u60ca\u8be7\u3002 \u3000\u3000\u5728\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\uff0c\u5c1a\u4ee5\u4e3a\u6765\u4eba\u662f\u7ea2\u8863\u756a\u618e\u7684\u90a3\u4e00\u8def\u4eba\uff0c\u5374\u60f3\u4e0d\u5230\u7adf\u662f\u4e2a\u4e30\u795e\u4fca\u79c0\u7684\u6c49\u65cf \u5c11\u5e74\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u975e\u6709\u6570\u5341\u5e74\u529f\u529b\uff0c\u4e5f\u96be\u4ee5\u6e21\u8fc7\u51b0\u5ddd\uff0c\u95ef\u8fc7\u9635\u56fe\uff0c\u600e\u4e48\u8fd9\u4e00\u4e2a\u5c11\u5e74\uff0c\u5e74\u7eaa\u4e0e \u6211\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u96be\u9053\u4ed6\u6bd4\u90a3\u4e2a\u7ea2\u8863\u756a\u50e7\u8fd8\u66f4\u5389\u5bb3\uff1f\u201d \u3000\u3000\u4e24\u4eba\u773c\u5149\u76f8\u63a5\uff0c\u767d\u8863\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u51b0\u5bab\u7684\u4e3b\u4eba\u5417\uff1f\u600e\u4e48\u8fd9\u6837\u6020\u6162\u5ba2\u4eba \u5475\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u82e5\u8bf4\u51fa\u540d\u5b57\uff0c\u53ea\u6050\u4f60\u8981\u5bf9\u6211\u66f4\u4e0d\u5ba2\u6c14\u4e86\uff0c\u4e0d\u8fc7\u8fdf\u65e9\u4e5f\u8981\u8bf4\u7ed9\u4f60\u77e5\u9053\u7684\uff0c \u53ea\u8981\u4f60\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6709\u91d1\u672c\u5df4\u74f6 \u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u53c8\u662f\u91d1\u672c\u5df4\u74f6\uff1f\u771f\u662f\u70e6\u6b7b\u4eba\u4e86\u3002\u83ab\u975e\u4f60\u53c8\u662f\u8981\u6c42\u6211\u4e0b\u5c71\uff0c \u4e3a\u4f60\u62a2\u90a3\u4e2a\u4ec0\u4e48\u91d1\u74f6\u5417\uff1f\u4f60\u4eec\u4e0e\u6ee1\u6d32\u4eba\u4f5c\u5bf9\uff0c\u4e0e\u6211\u53ef\u4e0d\u76f8\u5e72\u3002\u201d\u90a3\u5c11\u5e74\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u731c\u9519\u4e86\uff0c\u6211\u662f\u6c42\u4f60\u4e0b\u5c71\u53bb\u4fdd\u62a4\u90a3\u4e2a\u91d1\u74f6\uff01\u5c3c\u6cca\u5c14\u4eba\u8981\u62a2\u90a3\u4e2a\u91d1\u74f6\uff0c\u6709\u4e9b\u4e0d\u660e\u5229\u5bb3\u7684\u4fa0 \u5ba2\uff0c\u597d\u50cf\u94c1\u62d0\u4ed9\u4e4b\u6d41\u7684\u4eba\u4e5f\u8981\u53bb\u62a2\u90a3\u4e2a\u91d1\u74f6\uff0c\u6211\u4e00\u4eba\u5b64\u638c\u96be\u9e23\uff0c\u4f60\u975e\u4e0b\u5c71\u52a9\u6211\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c11\u5e74\u8bf4\u8bdd\u7684\u795e\u6c14\uff0c\u7b80\u76f4\u5c31\u50cf\u5bf9\u8001\u670b\u53cb\u6c42\u52a9\u4e00\u822c\u3002\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00\u6c14\uff0c\u6697\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u6709 \u4ec0\u4e48\u4ea4\u60c5\uff1f\u201d\u67f3\u7709\u4e00\u7ad6\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u7ec3\u5230\u4eca\u65e5\u7684\u6b66\u529f\uff0c\u5df2\u7b97\u4e0d\u9519\uff0c\u5feb\u5feb\u4e0b\u5c71\uff0c\u514d\u5f97\u81ea \u8bef\uff01\u201d\u51b0\u5ddd\u5929\u5973\u4e0d\u7acb\u5373\u4e0b\u4ee4\u9a71\u9010\uff0c\u5df2\u7b97\u5ba2\u6c14\u4e07\u5206\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u5374\u662f\u4e00\u526f\u5b09\u76ae\u7b11\u8138\u7684\u795e\u6c14\uff0c\u8fc8 \u524d\u4e00\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8fd9\u70b9\u9762\u5b50\u4f60\u4e5f\u4e0d\u7ed9\u6211\u4e48\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9762\u8272\u4e00\u6c89\uff0c\u4e3a\u9996\u7684\u4f8d\u5973\u53f1\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u8bf4\u8bdd\u597d\u751f\u65e0\u793c\uff0c\u5f53\u771f\u8981\u6211\u4eec\u8d76\u4f60\u4e0b\u5c71 \u5417\uff1f\u201d\u767d\u8863\u5c11\u5e74\u61d2\u6d0b\u6d0b\u5730\u6253\u4e2a\u5475\u6b20\uff0c\u7b11\u9053\uff1a\u201c\u4e0a\u5c71\u5bb9\u6613\u4e0b\u5c71\u96be\uff0c\u6211\u4eca\u65e5\u8d70\u5f97\u7d2f\u4e86\uff0c\u4f60\u4e0d\u8d76 \u6211\uff0c\u6211\u8fd8\u771f\u60f3\u5728\u8fd9\u91cc\u7761\u4e00\u89c9\u5462\uff01\u201d\u90a3\u4f8d\u5973\u4e00\u62cd\u624b\u638c\uff0c\u50ac\u52a8\u9635\u5f62\uff0c\u516b\u53e3\u5bd2\u5149\u95ea\u95ea\u5b9d\u5251\uff0c\u4e25\u5982\u95ea \u7535\u60ca\u98ce\uff0c\u4e00\u9f50\u5377\u5230\uff0c\u767d\u8863\u5c11\u5e74\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u597d\u51b7\uff0c\u597d\u51b7\uff01\u7761\u610f\u90fd\u7ed9\u4f60\u4eec\u6253\u6d88\u5566\u3002\u201d\u8eab\u5f62\u98d8 \u98d8\uff0c\u5728\u5251\u5149\u4e4b\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u4f8d\u5973\u7684\u9635\u52bf\u5c55\u5f00\uff0c\u653b\u52bf\u6709\u5982\u6f6e\u6d8c\uff0c\u4e00\u5bf9\u624d\u8fc7\uff0c\u4e00\u5bf9\u53c8\u6765\uff0c\u5faa\u73af\u5f80 \u590d\uff0c\u51cc\u5389\u4e4b\u6781\uff0c\u767d\u8863\u5c11\u5e74\u8eab\u6cd5\u5947\u5feb\uff0c\u6bcf\u4e8e\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u95ea\u8fc7\u5251\u5c16\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u4e0d\u7531\u5f97\u6697\u6697 \u8d5e\u597d\uff0c\u9635\u52bf\u8d8a\u653b\u8d8a\u7d27\uff0c\u6162\u6162\u5f80\u91cc\u6536\u7f29\uff0c\u516b\u53e3\u51b7\u6c14\u68ee\u68ee\u7684\u957f\u5251\u5728\u767d\u8863\u5c11\u5e74\u7684\u8eab\u524d\u8eab\u540e\u8eab\u5de6\u8eab \u53f3\uff0c\u4f3c\u7a7f\u63d2\uff0c\u66f4\u662f\u4ee4\u4eba\u60ca\u5fc3\u9a87\u76ee\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u829d\u5a1c\u59d0\u59d0\uff0c\u4f60\u80fd\u4e0d\u80fd\u4ee3\u6211\u5411\u51b0\u5ddd\u5929\u5973\u8bf4 \u60c5\uff1f\u201d\u829d\u5a1c\u6447\u4e86\u6447\u5934\uff0c\u9648\u5929\u5b87\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u54ac\u7d27\u5634\u5507\uff0c\u795e\u8272\u751a\u662f\u7d27\u5f20\uff0c\u5982\u6b64\u795e \u60c5\u3001\u8fd8\u662f\u4ec5\u89c1\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u767d\u8863\u5c11\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u597d\u5251\u6cd5\uff0c\u8bf7\u6055\u5f97\u7f6a\u4e86\uff01\u201d\u9648\u5929\u5b87\u7b80\u76f4\u770b \u4e0d\u6e05\u4ed6\u7684\u52a8\u4f5c\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5c45\u7136\u80fd\u5728\u516b\u53e3\u51b0\u9b44\u5bd2\u5149\u5251\u7684\u56f4\u653b\u4e4b\u4e0b\uff0c\u817e\u51fa\u624b\u6765\uff0c\u500f\u7684\u4e5f\u62d4\u51fa \u4e00\u53e3\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u5fae\u4e00\u6325\u52a8\uff0c\u5251\u5c16\u7adf\u5e26\u7740\u9690\u9690\u7684\u5578\u58f0\uff0c\u6709\u82e5\u9f99\u541f\uff0c\u987f\u65f6\u51b7\u7535\u7cbe\u8292\uff0c\u7f24\u7eb7 \u98de\u821e\uff0c\u51b0\u5ddd\u5929\u5973\u5931\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d\u5251\uff01\u201d\u767d\u8863\u5c11\u5e74\u5c06\u5251\u4e00\u6325\uff0c\u5212\u4e86\u4e00\u4e2a\u5706\u5b64\uff0c\u53ea\u542c\u5f97\u4e00 \u9635\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u6709\u4e24\u540d\u4f8d\u5973\u7684\u5bd2\u5149\u5251\u5df2\u7ed9\u4ed6\u622a\u65ad\uff0c\u4f59\u4eba\u5927\u60ca\uff0c\u4e00\u9f50\u540e\u9000\uff0c\u767d\u8863\u5c11\u5e74\u8eab\u624b\u5feb \u6377\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u800c\u4e14\u7adf\u4f3c\u6df1\u901a\u8bf8\u845b\u6b66\u5019\u516b\u9635\u56fe\u7684\u95e8\u6237\uff0c\u8d70\u4f11\u95e8\uff0c\u8f6c\u5f00\u95e8\uff0c\u7ed5\u6b7b\u95e8\uff0c\u8e0f\u751f\u95e8\uff0c \u7740\u7740\u53cd\u653b\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u53c8\u628a\u5b88\u666f\u95e8\uff0c\u4f24\u95e8\u7684\u4e24\u540d\u4f8d\u5973\u7684\u957f\u5251\u524a\u65ad\u4e86\uff01 \u3000\u3000\u9547\u5b88\u4e2d\u67a2\u7684\u4f8d\u5973\u6025\u5fd9\u6253\u51fa\u201c\u51b0\u9b44\u795e\u5f39\u201d\uff0c\u4e00\u51fa\u624b\u4fbf\u7528\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\uff0c\u6492\u51fa\u4e00\u5927\u628a \u4eae\u6676\u6676\u5f62\u4f3c\u73cd\u73e0\u7684\u6697\u5668\uff0c\u5e03\u4e86\u6ee1\u7a7a\u3002\u90a3\u767d\u8863\u5c11\u5e74\u628a\u624b\u4e00\u626c\uff0c\u4e5f\u7a81\u7136\u53d1\u51fa\u4e00\u628a\u6697\u5668\uff0c\u51b0\u9b44\u795e\u5f39 \u5df2\u602a\uff0c\u4ed6\u7684\u6697\u5668\u66f4\u602a\uff0c\u6697\u5668\u751a\u5c0f\uff0c\u5f62\u72b6\u770b\u4e0d\u6e05\u695a\uff0c\u4f46\u5374\u5e26\u7740\u4e00\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u6697\u5668\u7a7f\u7a7a\u76f4\u4e0a\uff0c \u6ee1\u7a7a\u7684\u51b0\u9b44\u795e\u5f39\u970e\u65f6\u98de\u6563\u3002\u51b0\u5ddd\u5929\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5c11\u5e74\u7684\u52b2\u529b\u7528\u5f97\u5999\u7edd\uff0c\u4ed6\u90a3\u4e00\u628a\u5f62\u5982\u8292\u523a \u7684\u6697\u5668\uff0c\u7adf\u695a\u6bcf\u4e00\u679d\u90fd\u523a\u7740\u4e00\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u5374\u53c8\u5e76\u4e0d\u523a\u7a7f\uff0c\u53ea\u662f\u5fae\u5fae\u7c98\u7740\uff0c\u5c06\u51b0\u9b44\u795e\u5f39\u9001\u51fa \u6570\u4e08\u4e4b\u5916\uff0c\u98d8\u6563\u56db\u65b9\u3002\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u4e00\u52a8\uff0c\u731b\u71c3\u8d77\u7236\u4eb2\u751f\u524d\u6240\u66fe\u8bf4\u8fc7\u7684\u5929\u5c71\u795e\u8292\uff0c\u51fa\u624b\u4e4b\u65f6 \u5e26\u7740\u6697\u8d64\u8272\u7684\u5149\u534e\uff0c\u4e0d\u89c9\u72d0\u7591\u6ee1\u8179\uff0c\u5bf9\u8fd9\u5c11\u5e74\u53e6\u773c\u76f8\u770b\u3002 \u3000\u3000\u51b0\u9b44\u795e\u5f39\u548c\u4e5d\u5929\u7384\u5973\u9635\u90fd\u56f0\u4e0d\u7740\u8fd9\u4e2a\u5c11\u5e74\uff0c\u51b0\u5bab\u4f8d\u5973\u4e5f\u4e0d\u7531\u81ea\u5df1\u7684\u614c\u4e86\u624b\u811a\uff0c\u90a3\u5c11\u5e74\u4e00 \u4e2a\u76d8\u65cb\uff0c\u6bcf\u4e00\u4e2a\u51b0\u5bab\u4f8d\u5973\u90fd\u89c9\u5f97\u4ed6\u7684\u5f71\u5b50\u5728\u9762\u524d\u4e00\u63a0\u800c\u8fc7\uff0c\u6700\u540e\u7684\u56db\u540d\u4f8d\u5973\uff0c\u624b\u4e2d\u7684\u51b0\u9b44\u5bd2 \u5149\u5251\u4e5f\u7ed9\u4ed6\u593a\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u53ea\u89c1\u90a3\u5c11\u5e74\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u9000\u51fa\u9635\u56fe\u4e4b\u5916\uff0c\u7b11\u541f\u541f\u7684\u770b\u7740\u51b0\u5ddd \u5929\u5973\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5927\u5973\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u6211\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4ece\u65e0\u66f4\u6539\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8981\u4eb2 \u81ea\u8d76\u6211\u4e0b\u5c71\u4e86\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u65e2\u6043\u5f3a\u95ef\u5165\uff0c\u505a\u4e3b\u4eba\u7684\u4e0d\u613f\u62db\u5f85\u6076\u5ba2\uff0c\u4e5f\u53ea\u6709\u7528 \u6b66\u529b\u5c06\u4ed6\u9a71\u9010\u4e86\u3002\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u90a3\u771f\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u5f00\u5f00\u773c\u754c\uff0c\u89c1\u8bc6\u89c1\u8bc6\u4e2d\u571f\u5931\u4f20 \u7684\u8fbe\u6469\u5251\u6cd5\u4e86\u3002\u201d\u4ed6\u5bf9\u51b0\u5ddd\u5929\u5973\u51b0\u51b7\u7684\u773c\u5149\u6beb\u65e0\u60ca\u60e7\uff0c\u4ecd\u662f\u4e00\u76f4\u5fae\u7b11\u7684\u76ef\u7740\u5979\u3002 \u3000\u3000\u9648\u5929\u5b87\u548c\u829d\u5a1c\u4e8c\u4eba\u90fd\u4ee5\u4e3a\u51b0\u5ddd\u5929\u5973\u5b9a\u8981\u51fa\u624b\u4e86\uff0c\u90a3\u77e5\u51b0\u5ddd\u5929\u5973\u773c\u73e0\u4e00\u8f6c\uff0c\u5374\u9053\uff1a\u201c\u4f60\u6e21 \u8fc7\u51b0\u5ddd\uff0c\u53c8\u6253\u4e86\u4e24\u573a\uff0c\u6c14\u529b\u4e5f\u8017\u635f\u4e0d\u5c11\uff0c\u660e\u65e5\u4e2d\u5348\uff0c\u4f60\u518d\u6765\u5427\u3002\u201d\u6b64\u8a00\u867d\u751a\u81ea\u8d1f\uff0c\u5374\u4e5f\u5927\u6709 \u601c\u60dc\u4e4b\u5ff5\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e00\u7b11\u65bd\u793c\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u8bf7\u6211\u518d\u6765\uff0c\u6211\u5c82\u80fd\u4e0d\u6765\uff0c\u54b1\u4eec\u4e00\u8a00\u4e3a\u5b9a\u4e86\u3002\u201d\u63d2\u5251 \u5165\u9798\uff0c\u8f6c\u8fc7\u8eab\u53bb\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u6709\u70b9\u5bf9\u670b\u53cb\u7684\u5473\u513f\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u767d\u8863 \u5c11\u5e74\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u4eba\u751f\u5f97\u4e00\u77e5\u5df1\u53ef\u4ee5\u65e0\u61be\uff0c\u4f60\u72ec\u5904\u73e0\u5bab\u8d1d\u9608\uff0c\u5374\u65e0\u670b\u53cb\uff0c\u5982\u6b64\u4eba\u751f\uff0c\u4e5f\u662f \u7f8e\u4e2d\u4e0d\u8db3\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fd9\u5c11\u5e74\u7684\u6d3b\u6b63\u8bf4\u5230\u5979\u5fc3\u574e\u91cc\u53bb\uff0c\u5979\u81ea\u7236\u6bcd\u6b7b\u540e\uff0c\u65e0\u4e00\u4e2a\u53ef \u4e0e\u8c08\u5fc3\u7684\u4eba\uff0c\u6bcf\u4e8e\u79cb\u6708\u6625\u82b1\u4e4b\u591c\uff0c\u4e5f\u4f1a\u81ea\u611f\u5bc2\u5bde\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9762\u6cdb\u5a07\u7ea2\uff0c\u4f6f\u55c5\u8bf4\u9053\uff1a\u201c\u4e71\u56bc\u820c\u5934\uff0c\u8c01\u8981\u4f60\u591a\u7ba1\u95f2\u4e8b\u3002\u201d\u5374\u4e8e\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c \u8ddf\u7740\u4ed6\u8d70\u4e86\u51e0\u6b65\u3002\u767d\u8863\u5c11\u5e74\u6b63\u6b65\u4e0a\u6a2a\u8de8\u8377\u5858\u7684\u957f\u6865\uff0c\u6865\u4e0a\u6709\u4ead\u7ffc\u7136\uff0c\u8377\u5858\u4e0a\u9664\u4e86\u8377\u82b1\u4e4b\u5916\uff0c \u8fd8\u6709\u51e0\u79cd\u4e0d\u77e5\u540d\u7684\u6c34\u4e2d\u751f\u957f\u7684\u5f02\u82b1\uff0c\u5fae\u98ce\u5439\u6765\uff0c\u4e00\u6c34\u7686\u9999\uff0c\u4ead\u5b50\u4e24\u8fb9\uff0c\u523b\u6709\u4e00\u4ed8\u5bf9\u8054\uff0c\u5199\u7684 \u662f\uff1a \u3000\u3000\u6708\u8272\u82b1\u9999\u9f50\u5165\u68a6 \u3000\u3000\u4ed9\u5bab\u98de\u9601\u5171\u62db\u51c9 \u3000\u3000\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8054\u8bed\u867d\u4f73\uff0c\u4f46\u5374\u5e76\u4e0d\u5e94\u666f\u3002\u201d\u5374\u4e0d\u77e5\u8fd9\u526f\u5bf9\u8054\u6b63\u662f\u51b0\u5ddd\u5929\u5973\u6240\u4f5c\uff0c\u5979 \u7684\u7956\u6bcd\u5192\u6d63\u83b2\u662f\u6709\u540d\u7684\u624d\u5973\uff0c\u5979\u5e7c\u627f\u5bb6\u5b66\uff0c\u7434\u68cb\u8bd7\u8d4b\uff0c\u65e0\u4e00\u4e0d\u7cbe\uff0c\u51b0\u5bab\u4e2d\u5404\u5904\u4f73\u666f\u7684\u9898\u548f\uff0c \u90fd\u662f\u51fa\u4e8e\u5979\u7684\u624b\u7b14\uff0c\u95fb\u8a00\u751a\u662f\u4e0d\u670d\uff0c\u4e0d\u89c9\u53c8\u8ddf\u4ed6\u8d70\u4e86\u4e24\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u5e94\u666f\u5462\uff1f\u4f60\u8bf4\u8bf4 \u770b\uff1f\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u6708\u8272\u82b1\u9999\uff0c\u5904\u5904\u7686\u6709\uff1b\u4ed9\u5bab\u98de\u9601\uff0c\u4e5f\u4e0d\u8fc7\u662f\u6cdb\u6cdb\u7684\u5f62\u5bb9\u4e4b\u8bcd\uff0c\u79fb\u5230\u522b \u7684\u5730\u65b9\uff0c\u4e5f\u81ea\u53ef\u7528\u3002\u4e0d\u8db3\u4ee5\u8bf4\u660e\u6b64\u5904\u7684\u7279\u6b8a\u98ce\u666f\uff0c\u4f55\u51b5\u53ea\u5199\u666f\u800c\u4e0d\u5199\u4eba\uff0c\u4e5f\u662f\u7f8e\u4e2d\u4e0d\u8db3\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u867d\u751a\u77dc\u6301\uff0c\u4f46\u5230\u5e95\u662f\u4e2a\u7eaf\u771f\u7684\u5c11\u5973\uff0c\u542c\u4ed6\u8bf4\u8bdd\uff0c\u4e5f\u4f3c\u751a\u6709\u9053\u7406\uff0c\u53c8\u4e0d\u89c9\u5fae\u7b11 \u9053\uff1a\u201c\u4f60\u65e2\u5982\u6b64\u8bf4\uff0c\u90a3\u4e48\u4f60\u5c31\u66ff\u6211\u53e6\u62df\u4e2a\u8054\u5427\u3002\u201d\u767d\u8863\u5c11\u5e74\u5fae\u4e00\u541f\u54e6\uff0c\u6b63\u6b32\u5f20\u53e3\uff0c\u51b0\u5ddd\u5929\u5973 \u8eab\u65c1\u7684\u4f8d\u5973\u5ffd\u7136\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u526f\u5bf9\u8054\u6b63\u662f\u56e0\u4eba\u800c\u4f5c\uff0c\u96be\u505a\u5f97\u5f88\u5462\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u8981\u600e\u4e48\u5bf9\uff0c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u51b0\u5ddd\u5929\u5973\u6a2a\u4e86\u90a3\u4f8d\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u591a \u5634\u3002\u201d\u5bf9\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u8bf4\u4f60\u6240\u62df\u7684\u8054\u8bed\u3002\u5f85\u6211\u770b\u770b\u662f\u600e\u6837\u7684\u5e94\u666f\u6cd5\u3002\u201d\u767d\u8863\u5c11\u5e74\u5fae \u5fae\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u6233\u5c31\u732e\u62d9\u4e86\u3002\u201d\u541f\u9053\uff1a \u3000\u3000\u51b0\u5ddd\u6620\u6708\u5ae6\u5a25\u4e0b \u3000\u3000\u5929\u5973\u98de\u82b1\u9a9a\u5ba2\u6765 \u3000\u3000\u53c8\u7b11\u9053\uff1a\u201c\u8054\u867d\u4e0d\u4f73\uff0c\u4f46\u8054\u4e2d\u7684\u4eba\u7269\u90fd\u662f\u4f73\u7edd\uff01\u603b\u53ef\u4ee5\u5bf9\u5f97\u8fc7\u53bb\u5427\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u4e00 \u8361\uff0c\u674f\u8138\u98de\u7ea2\uff0c\u8fd9\u526f\u5bf9\u8054\u6b63\u5d4c\u7740\u201c\u51b0\u5ddd\u5929\u5973\u201d\u56db\u5b57\uff0c\u8054\u9996\u53c8\u5d4c\u6709\u5979\u7684\u540d\u5b57\u201c\u51b0\u5a25\u201d\uff0c\u90a3\u81ea\u7136 \u662f\u4e3a\u5979\u800c\u4f5c\u7684\u4e86\u3002\u800c\u4e14\u8054\u8bed\u9690\u9690\u85cf\u6709\u8d5e\u7f8e\u4e0e\u7231\u6155\u4e4b\u610f\uff0c\u51b0\u5ddd\u6620\u6708\uff0c\u6708\u5728\u6c34\u4e2d\uff0c\u597d\u50cf\u662f\u5ae6\u5a25\u5df2 \u7ecf\u4e0b\u51e1\uff1b\u5929\u5973\u6563\u82b1\uff0c\u5f15\u6765\u9a9a\u5ba2\uff0c\u8fd9\u53c8\u5206\u660e\u662f\u8bf4\u4ed6\u6155\u540d\u800c\u6765\u3002\u4f46\u8fd9\u8054\u53c8\u786e\u662f\u5e94\u666f\u4e4b\u4f5c\uff0c\u4e0d\u80fd\u8bf4 \u4ed6\u8f7b\u8584\u3002\u51b0\u5ddd\u5929\u5973\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u4ed6\u7684\u624d\u601d\u654f\u6377\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u5bf9\u4f8d\u5973\u9053\uff1a\u201c\u597d\u5566\uff0c\u6211\u4ea4\u5377\u4e86\uff0c\u4f60\u521a\u624d\u8bf4\u539f\u6765\u8fd9\u8054\u662f\u56e0\u4eba\u800c\u4f5c\uff0c\u7a76\u7adf\u662f\u56e0\u8c01\u800c \u4f5c\uff0c\u53ef\u4ee5\u89c1\u544a\u5417\uff1f\u201d\u4f8d\u5973\u62bf\u5634\u4e00\u7b11\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5c31\u544a\u8bc9\u4f60\u5427\u3002\u8fd9\u526f\u8054\u8bed\u5c31\u662f\u56e0\u5979\u800c\u4f5c \u7684\u3002\u8fd9\u4e2a\u56ed\u4e2d\u6709\u5341\u4e8c\u5904\u666f\u81f4\uff0c\u6bcf\u4e00\u5904\u7684\u9898\u8054\uff0c\u5d4c\u7684\u90fd\u662f\u6211\u4f8d\u5973\u7684\u540d\u5b57\u3002\u767d\u8863\u5c11\u5e74\u518d\u8bf5\u539f\u6765\u7684 \u8054\u8bed\u9053\uff1a\u201c\u6708\u8272\u82b1\u9999\u9f50\u5165\u68a6\uff0c\u4ed9\u5bab\u98de\u9601\u5171\u62db\u51c9\u3002\u5475\uff0c\u539f\u6765\u4f60\u7684\u540d\u5b57\u53eb\u6708\u4ed9\u3002\u201d\u4f8d\u5973\u9053\uff1a\u201c\u6b63 \u662f\u3002\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u518d\u6b21\u732e\u4e11\uff0c\u4e3a\u4f60\u518d\u62df\u4e00\u8054\u3002\u201d\u7565\u4e00\u541f\u54e6\uff0c\u7b11\u9053\uff1a\u201c\u6709\u53e4\u4eba \u7684\u8bd7\u53e5\uff0c\u6b63\u597d\u501f\u6765\u4f5c\u5bf9\u3002\u201d\u541f\u9053\uff1a \u3000\u3000\u6708\u8272\u65e0\u75d5\uff0c\u7eff\u7a97\u6731\u6237\u5e74\u5e74\u7ed5\uff1b \u3000\u3000\u4ed9\u59b9\u6709\u6068\uff0c\u78a7\u6d77\u9752\u5929\u591c\u591c\u5fc3\uff01 \u3000\u3000\u4e0b\u8054\u201c\u78a7\u6d77\u9752\u5927\u591c\u591c\u5fc3\u201d\u501f\u7528\u7684\u662f\u674e\u4e49\u5c71\u7684\u8bd7\u53e5\uff1a\u201c\u5ae6\u5a25\u5e94\u6094\u5077\u7075\u836f\uff0c\u78a7\u6d77\u9752\u5929\u591c\u591c \u5fc3\u3002\u201d\u8d34\u5207\u4e4b\u6781\u3002\u6697\u4e2d\u53c8\u662f\u5632\u8bbd\u51b0\u5ddd\u5929\u5973\u50cf\u5ae6\u5a25\u4e00\u6837\uff0c\u5bc2\u5bde\u72ec\u5b88\u51b0\u5bab\uff0c\u5d4c\u7684\u4e5f\u6b63\u662f\u5979\u4f8d\u5973\u7684 \u540d\u5b57\u3002\u51b0\u5ddd\u5929\u5973\u7709\u5934\u4e00\u76b1\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u7adf\u81ea\u966a\u4ed6\u8d70\u8fc7\u6a2a\u8de8\u8377\u5858\u7684\u957f\u6865\u3002\u8fd9\u6837\u7684\u8c08\u8bd7\u8bba \u6587\uff0c\u54ea\u91cc\u6709\u534a\u70b9\u4ec7\u654c\u7684\u610f\u5473\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53cc\u624b\u4e00\u62f1\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u52b3\u8fdc\u9001\uff0c\u4e5f\u4e0d\u52b3\u4f60\u4eec\u9a71\u9010\uff0c\u6211\u81ea\u5df1\u8d70\u4e86\uff0c\u660e\u65e5\u4e2d\u5348\uff0c\u518d \u6765\u8df5\u7ea6\u3002\u201d\u51b0\u5ddd\u5929\u5973\u4e0d\u89c9\u53c8\u662f\u9762\u4e0a\u4e00\u7ea2\uff0c\u53ea\u89c1\u767d\u8863\u5c11\u5e74\u5c55\u5f00\u8eab\u5f62\uff0c\u5df2\u81ea\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53bb\u540e\uff0c\u5bab\u4e2d\u8bf8\u4eba\u4e2a\u4e2a\u90fd\u5728\u8c08\u8bba\u4ed6\uff0c\u6ce8\u610f\u7740\u660e\u65e5\u4e4b\u4f1a\u3002\u9648\u5929\u5b87\u4e5f\u4e0d\u4f8b\u5916\uff0c\u8fd9\u665a\u60f3 \u8d77\u81ea\u5df1\u4e0a\u5c71\u4ee5\u6765\uff0c\u867d\u7136\u4ec5\u4ec5\u51e0\u65e5\uff0c\u5df2\u89c1\u4e0d\u5c11\u5947\u4eba\u3001\u5947\u666f\u3001\u5947\u4e8b\uff0c\u5fc3\u4e2d\u6697\u601d\uff0c\u767d\u8863\u5c11\u5e74\u548c\u51b0\u5ddd \u5929\u5973\u7684\u6b66\u529f\u90fd\u6df1\u4e0d\u53ef\u6d4b\uff0c\u660e\u65e5\u5b9a\u6709\u4e00\u573a\u6076\u6597\uff1a\u4e00\u5ffd\u513f\u53c8\u60f3\u5230\u90a3\u795e\u79d8\u7684\u5c4b\u5b50\uff0c\u7ffb\u6765\u8986\u53bb\uff0c\u7761\u4e0d \u7740\u89c9\u3002\u7b2c\u4e8c\u65e5\u5c06\u8fd1\u4e2d\u5348\u65f6\u5206\uff0c\u829d\u5a1c\u53c8\u6765\u4e0e\u4ed6\u4e00\u540c\u51fa\u53bb\uff0c\u521a\u521a\u8e0f\u4eba\u56ed\u4e2d\uff0c\u5c31\u542c\u89c1\u4e00\u9635\u60a0\u626c\u7684\u7434 \u58f0\uff0c\u829d\u5a1c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\u751a\u662f\u53cd\u5e38\uff0c\u4eca\u65e5\u4e00\u65e9\u5c31\u5728\u8fd9\u91cc\u5f39\u7434\u4e86\u5462\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u9526\u745f\u65e0\u7aef\u4e94\u5341\u5f26\uff0c\u4e00\u5f26\u4e00\u67f1\u601d\u534e\u5e74\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u5251\u6c14\u5c04\u73e0\u5bab\u3000\u4ea6\u771f\u4ea6\u5e7b\u3000\u67d4\u60c5\u8054\u5f69\u7b14\u3000\u5b9c\u559c\u5b9c\u55d4 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u4e03 \u56de \u5251\u6c14\u5c04\u73e0\u5bab\u3000\u4ea6\u771f\u4ea6\u5e7b \u67d4\u60c5\u8054\u5f69\u7b14\u3000\u5b9c\u559c\u5b9c\u55d4 \u3000\u3000\u5f39\u7684\u662f\u300a\u8bd7\u7ecf\xb7\u5468\u5357\u300b\u7684\u4e00\u7ae0\uff0c\u6b4c\u8bcd\u9053\uff1a\u201c\u5357\u6709\u4e54\u6728\uff0c\u4e0d\u53ef\u79f0\u601d\u3002\u6c49\u6709\u6e38\u5973\uff0c\u4e0d\u53ef\u6c42 \u601d\u3002\u6c49\u4e4b\u5e7f\u77e3\uff0c\u4e0d\u53ef\u6cf3\u601d\u3002\u6c5f\u4e4b\u6c38\u77e3\uff0c\u4e0d\u53ef\u65b9\u601d\u3002\u201d\u82e5\u8bd1\u6210\u73b0\u4ee3\u767d\u8bdd\u8bd7\u5219\u662f\uff1a \u3000\u3000\u6709\u68f5\u9ad8\u6811\u5357\u65b9\u751f\uff0c \u3000\u3000\u9ad8\u9ad8\u6811\u4e0b\u5c11\u51c9\u9634\u3002 \u3000\u3000\u6c49\u6c5f\u5973\u90ce\u6c34\u4e0a\u6e38\uff0c \u3000\u3000\u66f4\u60f3\u8ffd\u6c42\u6789\u8d39\u5fc3\u3002 \u3000\u3000\u597d\u6bd4\u6c49\u6c34\u5bbd\u53cc\u5bbd\uff0c \u3000\u3000\u6e38\u8fc7\u96be\u4f3c\u4e0a\u9752\u5929\u3002 \u3000\u3000\u597d\u6bd4\u6c5f\u6c34\u957f\u53c8\u957f\uff0c \u3000\u3000\u8981\u60f3\u7ed5\u8fc7\u662f\u6789\u7136\u3002 \u3000\u3000\u8fd9\u8bd7\u5199\u7684\u662f\u4e00\u4e2a\u9ad8\u505a\u7684\u5c11\u5973\uff0c\u4efb\u4f55\u7537\u5b50\u8ffd\u6c42\u5979\u90fd\u8feb\u4e0d\u5230\u624b\uff0c\u8bd7\u4e2d\u6240\u7528\u7684\u90fd\u662f\u6bd4\u55bb\u548c\u6697 \u793a\uff0c\u9648\u5929\u5b57\u542c\u4e86\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u4e3a\u4ec0\u4e48\u5f39\u8fd9\u9996\u6b4c\u8bcd\uff1f\u96be\u9053\u5979\u662f\u81ea\u6bd4\u6c49\u6c5f \u5973\u90ce\u4e48\uff1f\u51b0\u5ddd\u6bd4\u6c49\u6c5f\u90a3\u53ef\u662f\u66f4\u8981\u96be\u6e21\u5f97\u591a\uff01\u201d \u3000\u3000\u62ac\u5934\u4e00\u770b\uff0c\u7ea2\u65e5\u6b63\u5728\u5929\u4e2d\uff0c\u7434\u58f0\u5212\u7136\u800c\u6b62\uff0c\u56ed\u5b50\u91cc\u9759\u6084\u6084\u7684\uff0c\u4eba\u4eba\u5fc3\u60c5\u90fd\u89c9\u7d27\u5f20\uff0c\u51b0\u5ddd \u5929\u5973\u548c\u767d\u8863\u5c11\u5e74\u7ea6\u4f1a\u7684\u65f6\u523b\u5df2\u7ecf\u5230\u4e86\uff0c\u5ffd\u95fb\u4e00\u9635\u8427\u58f0\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u5439\u7684\u4e5f\u662f\u8bd7\u7ecf\u4e2d\u7684\u4e00\u7ae0\uff0c \u6b4c\u8bcd\u9053\uff1a\u201c\u84b9\u971e\u82cd\u82cd\uff0c\u767d\u9732\u4e3a\u971c\uff0c\u6240\u8c13\u4f0a\u4eba\uff0c\u5728\u6c34\u4e00\u65b9\u3002\u6eaf\u6d04\u4ece\u4e4b\u3002\u9053\u963b\u4e14\u957f\u3002\u6eaf\u6e38\u4ece\u4e4b\uff0c \u5b9b\u5728\u6c34\u4e2d\u592e\u3002\u201d\u82e5\u8bd1\u6210\u73b0\u4ee3\u7684\u767d\u8bdd\u8bd7\u5219\u662f\uff1a \u3000\u3000\u82a6\u82b1\u4e00\u7247\u767d\u82cd\u82cd\uff0c \u3000\u3000\u6e05\u65e9\u9732\u6c34\u53d8\u6210\u971c\uff0c \u3000\u3000\u5fc3\u4e0a\u7684\u4eba\u513f\u54ea\uff0c \u3000\u3000\u6b63\u5728\u6c34\u7684\u90a3\u4e00\u65b9\u3002 \u3000\u3000\u6211\u9006\u7740\u6c34\u6d41\u53bb\u627e\u5979\uff0c \u3000\u3000\u7ed5\u6765\u7ed5\u53bb\u9053\u513f\u957f\u3002 \u3000\u3000\u6211\u987a\u7740\u6c34\u6d41\u53bb\u627e\u5979\uff0c \u3000\u3000\u8c61\u5728\u56db\u8fb9\u4e0d\u7740\u9646\u7684\u6c34\u4e2d\u592e\u3002 \u3000\u3000\u8fd9\u8bd7\u662f\u7537\u5b50\u89c5\u610f\u4e2d\u4eba\u7684\u60c5\u6b4c\uff0c\u4f0a\u4eba\u53ef\u671b\u800c\u4e0d\u53ef\u5373\uff0c\u8bd7\u4e2d\u5145\u6ee1\u7231\u6155\u4e0e\u60c6\u5e10\u7684\u60c5\u6000\u3002\u7bab\u58f0\u4e00 \u505c\uff0c\u53ea\u89c1\u56ed\u4e2d\u5df2\u591a\u4e86\u4e00\u4eba\uff0c\u6b63\u662f\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u624b\u6301\u7389\u8427\uff0c\u8170\u60ac\u957f\u5251\uff0c\u53f2\u663e\u5f97\u4e30\u795e\u4fca\u79c0\uff0c\u53ea\u89c1 \u4ed6\u6536\u8d77\u7389\u8427\uff0c\u5f39\u5251\u7b11\u9053\uff1a\u201c\u51b0\u5ddd\u4f34\u591a\u7434\u5362\u5999\uff0c\u4f46\u613f\u4eba\u95f4\u5251\u6c14\u9500\u3002\u201d\u59d1\u5a18\u5f39\u5f97\u597d\u7434\u51e0\u4e4e\u4ee4\u6211\u5fd8 \u4e86\u6bd4\u5251\u4e4b\u4e8b\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u5439\u5f97\u597d\u7bab\uff0c\u656c\u8046\u96c5\u594f\uff0c\u679c\u662f\u9ad8\u660e\uff0c\u5251\u6cd5\u5fc5\u5b9a\u66f4 \u5999\uff0c\u90a3\u662f\u8981\u9886\u6559\u7684\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u6697\u6697\u597d\u7b11\uff0c\u4ed6\u4e8c\u4eba\u7434\u8427\u916c\u5531\uff0c\u54ea\u91cc\u50cf\u662f\u5373\u5c06\u51b3\u6597\u7684\u6a21\u4f6f\uff1f\u53ea\u542c\u767d\u8863\u5c11\u5e74\u5927\u7b11\u9053\uff1a \u201c\u90a3\u53ef\u4e0d\u662f\u5927\u715e\u98ce\u666f\u4e48\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u8981\u6211\u4e0b\u5c71\uff0c\u90a3\u5c82\u4e0d\u662f\u66f4\u715e\u98ce\u666f\uff1f\u4f60\u82e5\u4e0d\u613f\u6bd4 \u5251\uff0c\u6211\u4e5f\u4e0d\u613f\u5f3a\u4eba\u6240\u96be\u3002\u4f60\u4e0b\u5c71\u5427\uff0c\u8fd9\u91cc\u5b9e\u5728\u4e0d\u662f\u4f60\u8be5\u5230\u7684\u5730\u65b9\u3002\u201d\u767d\u8863\u5c11\u5e74\u6447\u4e86\u6447\u5934\uff0c\u7b11 \u9053\uff1a\u201c\u90a3\u4e48\u9664\u4e86\u6bd4\u5251\uff0c\u6211\u53ef\u662f\u6ca1\u6709\u529e\u6cd5\u8bf7\u4f60\u4e0b\u5c71\u4e86\u3002\u597d\uff0c\u54b1\u4eec\u4e00\u8a00\u4e3a\u5b9a\uff0c\u4ea7\u6211\u8f93\u4e86\uff0c\u6211\u5c31\u518d \u4e0d\u6765\u9ebb\u70e6\u4f60\uff0c\u82e5\u4f60\u8f93\u4e86\uff0c\u4f60\u53ef\u5f97\u52a9\u627e\u53bb\u4fdd\u62a4\u90a3\u91d1\u672c\u5df4\u74f6\uff01\u201d\u51b0\u5ddd\u5929\u5973\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u5c18\u4e16 \u4e4b\u4e8b\uff0c\u4f60\u4e89\u6211\u593a\uff0c\u4ee4\u4eba\u6076\u5fc3\uff0c\u597d\u5427\uff0c\u4f60\u4eae\u5251\u8fdb\u62db\uff0c\u4e5f\u843d\u5f97\u6211\u8033\u6839\u6e05\u51c0\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u4e00 \u6765\u8d23\u90a3\u5c11\u5e74\u4e0d\u591f\u9ad8\u96c5\uff0c\u4e8c\u6765\u5bf9\u8fd9\u573a\u6bd4\u5251\uff0c\u9887\u6709\u81ea\u8d1f\u4e4b\u610f\uff0c\u597d\u50cf\u53ef\u4ee5\u7a33\u80dc\u65e0\u7591\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u957f\u5251\u51fa\u9798\uff0c\u53ea\u89c1\u5bd2\u5149\u75be\u5c04\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u5979\u6240\u4f7f\u7684\u4e5f\u662f\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u4f46\u6bd4\u90a3\u4e9b\u51b0 \u5bab\u6043\u5973\u6240\u4f7f\u7684\u5bd2\u5149\u5251\uff0c\u5251\u8d28\u53c8\u81ea\u4e0d\u540c\uff0c\u90a3\u662f\u91c7\u4e94\u91d1\u4e4b\u7cbe\uff0c\u5728\u51b0\u7a9f\u5bd2\u6cc9\u4e2d\u6dec\u70bc\u800c\u6210\uff0c\u9648\u5929\u5b87\u548c \u829d\u5a1c\u867d\u7136\u65e9\u5c31\u670d\u4e0b\u5bab\u4e2d\u7684\u708e\u836f\uff0c\u53ef\u4ee5\u62b5\u5fa1\u5bd2\u6c14\u7684\u516d\u9633\u4e38\uff0c\u4ecd\u662f\u4e0d\u7531\u81ea\u4e3b\u7684\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u795e\u8272\u81ea\u82e5\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8f7b\u5f39\u5b9d\u5251\uff0c\u58f0\u82e5\u9f99\u541f\uff0c\u5728\u4e0b\u9996\u4e00\u7ad9\uff0c\u9053\uff1a\u201c\u8bf7\u8d50\u62db\uff01\u201d \u51b0\u5ddd\u5929\u5973\u957f\u5251\u4e00\u6307\uff0c\u75be\u5982\u7535\u63a3\uff0c\u9661\u7136\u98de\u8d77\u51e0\u6735\u5251\u82b1\uff0c\u9648\u5929\u5b87\u8fd8\u672a\u770b\u6e05\uff0c\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u5df2\u51ed \u7a7a\u62d4\u8d77\u6570\u5c3a\uff0c\u5251\u5149\u5728\u4ed6\u811a\u4e0b\u4e00\u63a0\u800c\u8fc7\uff0c\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u65c1\u4eba\u770b\u4e0d\u51fa\u6765\uff0c\u539f\u6765\u5979 \u8fd9\u4e00\u5251\u4e43\u662f\u8fbe\u6469\u5251\u6cd5\u4e2d\u7684\u4e00\u4e2a\u7edd\u9876\u602a\u5f02\u7684\u62db\u6570\uff0c\u4e00\u62db\u4e4b\u95f4\uff0c\u5206\u523a\u654c\u4eba\u4e09\u5927\u547d\u95e8\u8981\u7a74\uff0c\u5374\u4e0d\u6599 \u90a3\u767d\u8863\u5c11\u5e74\u7adf\u81ea\u8f7b\u8f7b\u95ea\u8fc7\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53d1\u58f0\u957f\u5578\uff0c\u624b\u8d77\u5251\u843d\uff0c\u5de6\u523a\u4e24\u5251\uff0c\u6709\u523a\u4e24\u5251\uff0c\u4e2d\u95f4\u53c8\u75be\u523a\u4e00\u5251\uff0c\u51fa\u624b\u4e94\u62db\uff0c\u7528 \u4e86\u4e94\u79cd\u4e0d\u540c\u7684\u5251\u6cd5\uff0c\u5f0f\u5f0f\u4e0d\u540c\uff0c\u51b0\u5ddd\u5929\u5973\u9053\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u6a2a\u7a7a\u4e00\u63a0\uff0c\u5251\u950b\u81ea \u5de6\u800c\u53f3\uff0c\u4e2d\u9014\u4e00\u53d8\uff0c\u5251\u52bf\u9661\u7136\u9006\u8f6c\uff0c\u51fa\u624b\u5982\u6b64\u4e4b\u5feb\uff0c\u800c\u7adf\u80fd\u4f7f\u5251\u52bf\u968f\u5fc3\u8f6c\u6362\uff0c\u8fd9\u5728\u5251\u672f\u4e4b \u4e2d\uff0c\u662f\u6700\u6700\u96be\u7ec3\u7684\u62db\u6570\uff01\u53ea\u89c1\u90a3\u5251\u5149\u4f3c\u5de6\u53cd\u53f3\uff0c\u6a2a\u7a7a\u4e00\u63a0\uff0c\u5411\u7740\u767d\u8863\u5c11\u5e74\u7684\u9888\u9879\u4e00\u7ed5\u800c\u8fc7\uff0c \u9648\u5929\u5b87\u9a87\u53eb\u4e00\u58f0\uff0c\u5ffd\u95fb\u90a3\u767d\u8863\u5c11\u5e74\u7b11\u58f0\u53c8\u8d77\uff0c\u8d5e\u9053\uff1a\u201c\u4f7f\u5f97\u597d\u4e00\u62db\u8fbe\u6469\u5251\u6cd5\u5440\uff01\u201d\u4ed6\u7adf\u7136\u5728 \u95f4\u4e0d\u5bb9\u53d1\u4e4b\u95f4\uff0c\u53c8\u907f\u5f00\u4e86\u51b0\u5ddd\u5929\u5973\u4e00\u5251\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u66f4\u662f\u8be7\u5f02\uff0c\u8fd9\u5c11\u5e74\u7adf\u81ea\u77e5\u9053\u81ea\u5df1\u7684\u5251\u6cd5\u5e08\u627f\uff0c\u800c\u81ea\u5df1\u5374\u4e0d\u77e5\u9053\u4ed6\u7684\u5251\u6cd5\u6765\u5386\uff0c \u50b2\u6c14\u4e0d\u7531\u5f97\u51cf\u4e86\u51e0\u5206\u3002\u767d\u8863\u5c11\u5e74\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u5251\u5408\u4e00\uff0c\u6765\u5f97\u6709\u5982\u9a87\u7535\u5954\u96f7\uff0c\u8f7b\u7075\u5904\u53c8\u4f3c\u884c\u4e91 \u6d41\u6c34\u3002\u6b63\u662f\u68cb\u9022\u5bf9\u624b\uff0c\u5c06\u9047\u826f\u6750\u3002\u51b0\u5ddd\u5929\u5973\u6740\u5f97\u5174\u8d77\uff0c\u5251\u5149\u56db\u5c55\uff0c\u6709\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u96e8\u7f24 \u7eb7\uff0c\u53ea\u89c1\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u51b0\u5ddd\u5929\u5973\u7684\u5f71\u5b50\uff0c\u767d\u8863\u5c11\u5e74\u5728\u5251\u5149\u4e4b\u4e2d\u98d8\u6765\u6643\u53bb\uff0c\u6709\u5982\u4e00\u53f6\u8f7b\u821f\uff0c \u5728\u72c2\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\u6323\u624e\u3002\u4e24\u4eba\u8eab\u6cd5\u8d8a\u5c55\u8d8a\u5feb\uff0c\u4e0d\u4e00\u4e00\u4f1a\u53ea\u89c1\u5bd2\u5149\u4e00\u7247\uff0c\u7ef8\u5e26\u98d8\u98d8\uff0c\u5df2\u5206\u4e0d\u51fa\u8c01 \u662f\u767d\u8863\u5c11\u5e74\uff0c\u8c01\u662f\u51b0\u5ddd\u592d\u5973\u3002\u640f\u6597\u867d\u70c8\uff0c\u7adf\u81ea\u4e0d\u95fb\u5175\u5203\u78b0\u78d5\u4e4b\u5360\u3002\u53cc\u65b9\u90fd\u4ee5\u6700\u4e0a\u4e58\u7684\u6b66\u529f\uff0c \u907f\u62db\u8fdb\u62db\uff0c\u6ee1\u56ed\u5b50\u91cc\uff0c\u4f46\u89c1\u5251\u65e0\u9563\u7ed5\uff0c\u4eba\u5f71\u5e62\u5e62\uff0c\u6b64\u53bb\u5f7c\u6765\uff0c\u773c\u82b1\u9563\u4e71\u3002\u4e24\u4eba\u6bd4\u5251\uff0c\u5c31\u5982\u6570 \u5341\u767e\u4eba\u76f8\u6597\u4e00\u822c\uff01 \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e5f\u662f\u597d\u751f\u9a87\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u5979\u5728\u8fbe\u6469\u5251\u6cd5\u4e4b\u4e2d\uff0c\u53c8\u63ba\u5165 \u8bb8\u591a\u5360\u602a\u7684\u53d8\u7740\uff0c\u771f\u662f\u53eb\u4eba\u9632\u4e0d\u80dc\u9632\uff01\u201d\u539f\u6765\u8fd9\u4e9b\u53e4\u602a\u7684\u53d8\u7740\uff0c\u4e43\u662f\u51b0\u5ddd\u5929\u5973\u7684\u7236\u6bcd\u4ee5\u8fbe\u6469 \u5251\u6cd5\u4e3a\u57fa\u7840\uff0c\u53c8\u91c7\u64b7\u963f\u62c9\u4f2f\u5251\u672f\u7684\u7cbe\u534e\u63c9\u5408\u800c\u6210\uff0c\u4e0e\u4e2d\u571f\u7684\u5251\u6cd5\uff0c\u622a\u7136\u4e0d\u540c\uff0c\u767d\u8863\u5c11\u5e74\u867d\u662f \u6b63\u5b97\u5251\u6d3e\u7684\u5ae1\u7cfb\u4f20\u4eba\uff0c\u4e5f\u4e0d\u61c2\u5f97\u3002 \u3000\u3000\u4e24\u4eba\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5140\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u51b0\u5ddd\u5929\u5973\u5251\u6cd5\u53c8\u53d8\uff0c\u5251\u52bf\u5c55\u5f00\uff0c\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\u3002 \u53ea\u89c1\u5979\u5251\u950b\u5ffd\u800c\u4e0a\u6307\uff0c\u5ffd\u800c\u4e0b\u6233\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u5251\u6cd5\u597d\u4f3c\u6742\u4e71\u65e0\u7ae0\uff0c\u5176\u4e2d\u5374\u5305\u542b\u7740\u6781\u590d\u6742\u7684\u7cbe \u5999\u62db\u6570\u3002\u767d\u8863\u5c11\u5e74\u5fc3\u4e2d\u4e00\u51db\uff0c\u7a81\u7136\u51dd\u7acb\u4e0d\u52a8\uff0c\u5b9d\u5251\u5c55\u5f00\uff0c\u5316\u6210\u4e86\u4e00\u9053\u5149\u5e62\uff0c\u62a4\u7740\u8eab\u8eaf\u3002\u51b0\u5ddd \u5929\u5973\u53ea\u89c9\u4ed6\u7684\u5251\u5149\u51dd\u91cd\u5982\u5c71\uff0c\u6251\u653b\u4e0d\u8fdb\uff0c\u5fc3\u4e2d\u4e5f\u662f\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u6b64\u4eba\u529f\u529b\uff0c\u53ea\u6709\u5728\u6211\u4e4b\u4e0a\uff0c \u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002\u51b0\u5ddd\u5929\u5973\u653b\u4e0d\u8fdb\u4ed6\u7684\u5251\u5708\uff0c\u767d\u8863\u5c11\u5e74\u4e5f\u7834\u4e0d\u4e86\u5979\u7684\u5251\u6cd5\uff0c\u4e24\u4eba\u81ea\u6b63\u5348\u6597\u81f3\u5c06 \u8fd1\u9ec4\u660f\uff0c\u5140\u662f\u4e0d\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u5212\u7136\u800c\u6b62\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u767d\u8863\u5c11\u5e74\u5404\u81ea\u6a2a\u8dc3\u4e09\u6b65\uff0c\u68c0\u89c6\u81ea\u5df1\u624b\u4e2d\u7684\u5b9d \u5251\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u4ea6\u662f\u5404\u65e0\u4f24\u635f\u3002\u767d\u8863\u5c11\u5e74\u5401\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u53ef\u4ee5\u4f11\u6218\u4e86\u5427\uff01\u201d\u51b0\u5ddd\u5929 \u5973\u9053\uff1a\u201c\u4eca\u65e5\u672a\u51b3\u80dc\u8d1f\uff0c\u660e\u65e5\u4f60\u53ef\u518d\u6765\u3002\u201d\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4f46\u635f\u574f\u4e86\u4f60\u5bab\u4e2d\u7684\u7f8e\u666f\uff0c\u6211\u5374 \u5b9e\u5728\u4e8e\u5fc3\u4e0d\u5fcd\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u51b0\u5bab\u4e2d\u7684\u4f17\u4f8d\u5973\u8fd9\u624d\u6ce8\u610f\u5230\u6709\u597d\u51e0\u5904\u5047\u5c71\u6e56\u77f3\u5df2\u88ab\u5251\u5149\u524a\u53bb\u4e86\u4e00\u5927\u7247\uff0c\u4e0d\u7981 \u8fde\u53eb\u53ef\u60dc\u3002\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u54b1\u4eec\u76f8\u6597\uff0c\u6b83\u53ca\u5c71\u77f3\uff0c\u8fd9\u771f\u662f\u4f55\u82e6\u6765\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u65e2\u7136\u5982 \u6b64\uff0c\u90a3\u5c31\u4e0d\u6597\u4e5f\u7f62\u3002\u201d\u767d\u8863\u5c11\u5e74\u5374\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u80dc\u6211\u5462\uff0c\u4f60\u53c8\u4e0d\u80af\u968f\u6211\u4e0b\u5c71\uff0c\u53eb\u6211\u5982\u4f55 \u662f\u597d\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u7709\u5934\u4e00\u76b1\uff0c\u4f3c\u662f\u5bf9\u8fd9\u5c11\u5e74\u7684\u6b6a\u7f20\u751a\u4e0d\u8010\u70e6\uff0c\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e0d\u4f1a\u4e0b\u5c71\u5417\uff1f\u201d \u767d\u8863\u5c11\u5e74\u53c8\u7b11\u9053\uff1a\u201c\u504f\u504f\u6211\u53c8\u60f3\u4ea4\u4f60\u8fd9\u4f4d\u670b\u53cb\uff0c\u6211\u4e0b\u4e86\u5c71\uff0c\u600e\u80fd\u518d\u89c1\u7740\u4f60\uff1f\u66f4\u4f55\u51b5\u68cb\u9022\u5bf9\u624b \u4e43\u662f\u4eba\u751f\u6700\u7545\u5feb\u4e4b\u4e8b\uff0c\u6211\u4e0b\u5c71\u540e\uff0c\u600e\u80fd\u518d\u627e\u5f97\u4e00\u4f4d\u4f3c\u4f60\u8fd9\u6837\u7684\u5bf9\u624b\u53ae\u6740\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u201c\u90a3\u4f60\u60f3\u600e\u5730\uff1f\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u4e24\u65e5\u4f60\u662f\u4e3b\u4eba\uff0c\u6211\u662f\u5ba2\u4eba\uff0c\u4f60\u867d\u7136\u5bf9\u5ba2\u4eba\u4e0d\u5927\u793c\u8c8c\uff0c\u4f46 \u6211\u4e5f\u8be5\u8bf7\u4f60\u4e00\u6b21\uff0c\u660e\u65e5\u4e2d\u5348\uff0c\u4f60\u5230\u4e0b\u9762\u51b0\u8c37\u4e4b\u4e2d\uff0c\u54b1\u4eec\u518d\u51b3\u4e2a\u80dc\u8d1f\u3002\u4f60\u5c31\u662f\u628a\u51b0\u5cf0\u524a\u5e73\uff0c\u4e5f \u65e0\u5173\u7cfb\uff0c\u514d\u5f97\u5728\u8fd9\u91cc\u76f8\u6597\uff0c\u635f\u574f\u4e86\u4f60\u5bab\u4e2d\u7684\u7f8e\u666f\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f9d\u4f60 \u5c31\u662f\uff01\u201d\u8a00\u51fa\u4e4b\u540e\uff0c\u8fd9\u624d\u89c9\u5f97\u88ab\u4ed6\u8bf7\u51fa\u51b0\u5bab\uff0c\u89c6\u540c\u5bbe\u5ba2\uff0c\u5012\u771f\u7684\u6709\u70b9\u50cf\u670b\u53cb\u4e86\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u770b\u7740\u90a3\u4e9b\u88ab\u635f\u574f\u7684\u5047\u5c71\u6e56\u77f3\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u56ed\u6797\u5e03\u7f6e\uff0c\u6709\u5982\u5c11\u5973\u8863\u88f3\uff0c\u4ea6\u5b9c\u65f6 \u5e38\u53d8\u6362\u3002\u635f\u574f\u4e86\u91cd\u65b0\u5e03\u7f6e\u4e5f\u597d\u3002\u201d\u53e3\u8bb2\u6307\u5212\uff0c\u4e0d\u7406\u51b0\u5ddd\u5929\u5973\u542c\u662f\u4e0d\u542c\uff0c\u7adf\u5927\u8c08\u5176\u56ed\u6797\u5e03\u7f6e\u4e4b \u9053\u3002\u5bab\u4e2d\u7684\u5e03\u7f6e\uff0c\u90fd\u662f\u51b0\u5ddd\u5929\u5973\u8bbe\u8ba1\uff0c\u53eb\u4f8d\u5973\u6240\u4e3a\uff0c\u90a3\u4e9b\u4f8d\u5973\u542c\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u7adf\u7136\u56f4\u4e0a\u6765 \u542c\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u6b32\u5728\u4eba\u524d\u8d23\u9a82\u4f8d\u5973\uff0c\u53d1\u4f5c\u4e0d\u5f97\uff0c\u767d\u8863\u5c11\u5e74\u8bb2\u4e86\u4e00\u9635\uff0c\u5ffd\u800c\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u9053\uff1a \u201c\u53ef\u60dc\u4f60\u4e0d\u80af\u7559\u5ba2\uff0c\u6211\u4eca\u665a\u53c8\u5f97\u5728\u51b0\u5cf0\u4e4b\u4e0b\uff0c\u7761\u4e00\u665a\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u6c14\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff01\u201d\u767d \u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u5bf9\u670b\u53cb\u771f\u4e0d\u5ba2\u6c14\uff0c\u597d\uff0c\u4e3b\u4eba\u65e2\u4e0d\u7559\u5ba2\uff0c\u90a3\u6211\u4e5f\u5c31\u53ea\u597d\u8d70\u4e86\u3002\u660e\u65e5\u4f60\u53ef\u8bb0\u5f97\u8df5 \u7ea6\u5475\uff01\u201d\u4e00\u8def\u8d70\uff0c\u4e00\u8def\u53c8\u8c08\u8bba\u56ed\u4e2d\u7684\u82b1\u8349\u6811\u6728\uff0c\u8bf4\u8fd9\u662f\u9999\u8354\uff0c\u90a3\u662f\u859b\u841d\uff0c\u8be5\u5982\u4f55\u5982\u4f55\u622a\u679d\u526a \u53f6\uff0c\u5bab\u4e2d\u7684\u4f8d\u5973\u542c\u5f97\u51fa\u795e\uff0c\u7adf\u6709\u51e0\u4eba\u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u597d\u50cf\u66ff\u4e3b\u4eba\u9001\u5ba2\u4e00\u822c\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u751a\u662f\u751f\u6c14\uff0c\u4e0d\u81ea\u89c9\u7684\u4e5f\u8d70\u4e0a\u524d\u53bb\uff0c\u60f3\u628a\u4f8d\u5973\u5524\u56de\uff0c\u5ffd\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u5728\u4e00\u5757\u724c\u574a \u4e4b\u524d\u505c\u4e0b\uff0c\u724c\u574a\u540e\u6709\u6570\u5341\u4e1b\u58a8\u5170\uff0c\u9999\u98d8\u8fdc\u8fd1\uff0c\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u666f\u8272\u4ea6\u751a\u4f73\u7f8e\uff0c\u4f55\u4ee5 \u6ca1\u6709\u9898\u8054\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5374\u4e0d\u4f5c\u58f0\uff0c\u4e00\u4e2a\u4f8d\u5973\u9053\uff1a\u201c\u8fd9\u4e24\u65e5\u5c31\u8981\u5199\u4e0a\u53bb\u523b\u4e86\uff0c\u516c \u4e3b\u8bf4\u2026\u2026\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u591a\u5634\uff01\u201d\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u6ca1\u6709\u62df\u597d\uff0c\u8fd9\u526f\u9898\u8054\u53c8\u8981\u5d4c \u4f60\u54ea\u4f4d\u4f8d\u5973\u7684\u540d\u5b57\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u53c8\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u770b\u4f60\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u4f60\u53c8\u8bd5\u8bd5\u4ee3\u62df\u5982 \u4f55\uff1f\u201d\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53c8\u6765\u8003\u6211\u4e86\uff0c\u6211\u8fd9\u4eba\u6700\u4e0d\u77e5\u81ea\u91cf\uff0c\u53ea\u597d\u53c8\u732e\u4e11\u4e86\u3002\u201d\u4e00\u4e2a\u4f8d \u5973\u6307\u7740\u5148\u5934\u90a3\u4f8d\u5973\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u9898\u8054\u8981\u5d4c\u5979\u7684\u540d\u5b57\uff0c\u5979\u53eb\u6167\u537f\u3002\u201d\u767d\u8863\u5c11\u5e74\u4e00\u60f3\uff0c\u8fd9\u4e2a\u5b57 \u4e00\u662f\u865a\u5b57\uff0c\u4e00\u662f\u5b9e\u5b57\uff0c\u679c\u7136\u96be\u5bf9\uff0c\u90a3\u4f8d\u5973\u662f\u670d\u4f8d\u51b0\u5ddd\u5929\u5973\u5728\u4e66\u623f\u4e2d\u5c55\u7eb8\u78e8\u58a8\u7684\uff0c\u5bf9\u8bd7\u8bcd\u8054\u8bed \u4e4b\u9053\uff0c\u4ea6\u7565\u89e3\u4e00\u4e8c\uff0c\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u51fa\u6765\u4e48\uff1f\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u52c9\u5f3a\u53ef\u4ee5\u5bf9\u5b83\u4e00\u5bf9\u3002\u8fd9\u724c\u574a\u751a \u9ad8\uff0c\u9700\u8981\u4e00\u526f\u957f\u8054\u3002\u201d\u541f\u9053\uff1a \u3000\u3000\u6167\u8d28\u80dc\u5e7d\u5170\uff0c\u6447\u66f3\u7a7a\u5c71\uff0c\u660e\u6708\u6709\u60c5\u5f92\u60c6\u6005\uff1b \u3000\u3000\u537f\u4e91\u707f\u94f6\u6d77\uff0c\u98d8\u6d6e\u5929\u9645\uff0c\u7476\u6c60\u65e0\u8def\u6f2b\u4f4e\u6d04\uff01 \u3000\u3000\u8054\u4e2d\u4e4b\u610f\uff0c\u53c8\u662f\u5f71\u5c04\u51b0\u5ddd\u5929\u5973\uff0c\u5c06\u5979\u6bd4\u4f5c\u7a7a\u8c37\u5e7d\u5170\uff0c\u53ea\u6709\u660e\u6708\u6709\u60c5\uff0c\u4e3a\u5979\u4f5c\u4f34\uff0c\u5f92\u589e\u6005 \u60c6\u3002\u51b0\u5ddd\u5929\u5973\u542c\u4e86\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u90a3\u4f8d\u5973\u5374\u53eb\u8d77\u597d\u6765\uff0c\u53c8\u6307\u7740\u4e00\u5904\u9053\uff1a\u201c\u8fd9\u91cc\u4f60\u80fd\u4e0d\u80fd\u4e5f\u62df\u4e00 \u8054\uff0c\u8981\u5d4c\u6211\u7684\u59d0\u59b9\u5e7d\u840d\u4e8c\u5b57\u3002\u201d\u90a3\u5904\u662f\u8377\u5858\u4e4b\u4e0a\u7684\u4e00\u4e2a\u516b\u89d2\u4ead\uff0c\u8377\u5858\u4e2d\u83b2\u53f6\u7530\u7530\uff0c\u6d6e\u840d\u7247 \u7247\uff0c\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u5e7d\u840d\u4e8c\u5b57\uff0c\u4e5f\u662f\u4e00\u865a\u4e00\u5b9e\uff0c\u66f4\u662f\u96be\u4ee5\u6210\u5bf9\uff0c\u597d\u5728\u6709\u773c\u524d\u7684\u666f\u8272\u53ef\u4ee5\u501f \u7528\u3002\u201d\u541f\u9053\uff1a \u3000\u3000\u5e7d\u8c37\u8352\u5c71\uff0c\u6708\u8272\u6d17\u6e05\u989c\u8272\uff1b \u3000\u3000\u840d\u6897\u83b2\u53f6\uff0c\u96e8\u58f0\u6ef4\u788e\u8377\u58f0\u3002 \u3000\u3000\u5e7d\u8c37\u8352\u5c71\u3001\u840d\u6897\u83b2\u53f6\uff0c\u5404\u81ea\u6210\u5bf9\uff0c\u8054\u5c3e\u90a3\u53e5\u5219\u662f\u8131\u80ce\u53e4\u4eba\u7684\u8bd7\u53e5\u201c\u7559\u5f97\u6b8b\u8377\u542c\u96e8\u58f0\u201d\u3002 \u4e0e\u773c\u524d\u666f\u8272\u751a\u662f\u7b26\u5408\uff0c\u4ecd\u662f\u5f71\u5c04\u51b0\u5ddd\u5929\u5973\uff0c\u597d\u50cf\u662f\u540c\u60c5\u5979\u5728\u51b0\u5bab\u4e4b\u4e2d\u7684\u5bc2\u5bde\u51c4\u51c9\u3002\u51b0\u5ddd\u5929\u5973 \u5fc3\u9b42\u52a8\u8361\uff0c\u60f3\u9053\uff1a\u8fd9\u5c11\u5e74\u7684\u6587\u624d\u6b66\u529f\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u6b64\u6765\u5374\u53c8\u5904\u5904\u90fd\u60f3\u8bf4\u6211\u4e0b\u5c71\uff0c\u96be\u9053\u53ea\u662f \u4e3a\u7740\u8981\u6211\u53bb\u4fdd\u62a4\u90a3\u635e\u4ec0\u5b50\u7684\u91d1\u672c\u5df4\u74f6\u5417\uff1f\u767d\u8863\u5c11\u5e74\u62df\u4e86\u4e24\u8054\uff0c\u5bf9\u51b0\u5ddd\u5929\u5973\u4e00\u62f1\u624b\u9053\uff1a\u201c\u89c1\u7b11 \u4e86\u3002\u5440\uff0c\u52b3\u4f60\u76f8\u9001\uff0c\u591a\u8c22\u591a\u8c22\uff01\u201d\u51b0\u5ddd\u5929\u5973\u731b\u7136\u4e00\u7701\uff0c\u539f\u6765\u53c8\u4e0d\u81ea\u89c9\u5730\u6014\u6014\u5730\u8ddf\u4ed6\u8d70\u8fc7\u4e86\u767d \u7389\u957f\u6865\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u7559\u4e9b\u7cbe\u795e\uff0c\u660e\u65e5\u6bd4\u5251\u5427\uff01\u201d\u767d\u8863\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u62f1\u624b \u9053\uff1a\u201c\u8bf7\u7559\u6b65\u3002\u201d\u7a7f\u82b1\u62c2\u53f6\uff0c\u5f84\u81ea\u53bb\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u6014\u6014\u5730\u7ad9\u5728\u6865\u4e0a\uff0c\u51dd\u89c6\u7740\u5929\u4e0a\u98d8\u8fc7\u7684\u7247\u7247 \u6d6e\u4e91\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53bb\u540e\uff0c\u9648\u5929\u5b87\u60f3\u7740\u8fc7\u4e86\u660e\u65e5\uff0c\u4fbf\u8981\u79bb\u5f00\u6b64\u5730\uff0c\u5fc3\u4e2d\u4ea6\u662f\u751a\u4e3a\u60c6\u6005\uff0c\u56de\u5230\u5367\u623f\u4f11 \u606f\u4e00\u4f1a\uff0c\u51b0\u5ddd\u5929\u5973\u5ffd\u7136\u9063\u4f8d\u5973\u6765\u8bf7\u4ed6\u540c\u8fdb\u665a\u9910\u3002 \u3000\u3000\u8fd9\u51e0\u65e5\u6765\uff0c\u9648\u5929\u5b87\u90fd\u662f\u5355\u72ec\u8fdb\u9910\uff0c\u51b0\u5ddd\u5929\u5973\u6839\u672c\u6ca1\u6709\u7ea6\u8fc7\u4ed6\u89c1\u9762\uff0c\u8fd9\u6b21\u5f97\u5230\u51b0\u5ddd\u5929\u5973\u7684 \u9080\u8bf7\uff0c\u9887\u611f\u5947\u7279\u3002\u5f53\u4e0b\u968f\u4e86\u51b0\u5bab\u4f8d\u5973\uff0c\u8d70\u51fa\u82b1\u56ed\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u8d70\u8fc7\u4e00\u9053\u66f2\u6298\u7684\u957f\u5eca\uff0c\u957f\u5eca \u7684\u5c3d\u5934\u662f\u4e00\u4e2a\u4eba\u5de5\u5f00\u6398\u7684\u51b0\u6e56\uff0c\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u7684\u51b0\u5cf0\u4e4b\u4e0b\uff0c\u57cb\u6709\u706b\u5c71\uff0c\u5730\u6c14\u6e29\u6696\uff0c\u6545\u6b64\u5bab\u4e2d \u666f\u8272\uff1a\u751a\u4e3a\u5947\u7279\uff0c\u6709\u56db\u65f6\u4e0d\u8c22\u4e4b\u82b1\uff0c\u516b\u8282\u957f\u9752\u4e4b\u8349\uff0c\u51b0\u6e56\u4e4b\u4e2d\u6709\u767d\u85c9\u7ea2\u83b2\uff0c\u6709\u98d8\u6563\u7740\u5f02\u9999\u7684 \u66fc\u9640\u7f57\u82b1\uff0c\u6709\u82b1\u5f00\u5982\u4f1e\u7684\u960e\u4f18\u51ac\u82b1\u2026\u2026\u6e56\u4e0a\u8fd8\u6709\u6d6e\u51b0\u7247\u7247\uff0c\u665a\u98ce\u5439\u6765\uff1a\u4e00\u6c34\u7686\u9999\u3002\u4e4d\u89c1\u6b64 \u666f\uff0c\u51e0\u4e0d\u77e5\u65f6\u8282\u662f\u6625\u662f\u79cb\uff1f\u662f\u51ac\u662f\u590f\uff1f \u3000\u3000\u4e34\u6e56\u6709\u4ead\uff0c\u901a\u4f53\u7528\u767d\u7389\u5efa\u6210\uff0c\u6676\u83b9\u900f\u660e\uff0c\u5728\u5915\u9633\u8fd4\u7167\u4e4b\u4e0b\uff0c\u5e7b\u4e2d\u8ff7\u5165\u7684\u5149\u5f69\uff0c\u4ead\u4e0a\u8bbe\u6709 \u9152\u5e2d\uff0c\u9664\u4e86\u51b0\u5ddd\u5929\u5973\u5750\u5728\u4e3b\u5e2d\u4e4b\u4f4d\uff0c\u5bbe\u5e2d\u4e0a\u5750\u7740\u4e8c\u4eba\uff0c\u6b63\u662f\u9648\u5929\u5b87\u5e08\u7236\u5e08\u5a18\uff1a\u94c1\u62d0\u4ed9\u548c\u8c22\u4e91 \u771f\u3002 \u3000\u3000\u9648\u5929\u5b87\u8fdb\u53bb\uff0c\u5728\u5e08\u7236\u4fa7\u8fb9\u5750\u4e0b\uff0c\u53ea\u89c1\u5e08\u7236\u795e\u60c5\u9664\u7565\u89c1\u6194\u60b4\u5916\uff0c\u9762\u8272\u5df2\u662f\u6062\u590d\u5982\u5e38\uff0c\u51b0\u5ddd \u5929\u5973\u9053\uff1a\u201c\u4f60\u5e08\u5085\u7684\u96be\u5173\u5df2\u7ecf\u8fc7\u4e86\u3002\u201d\u94c1\u62d0\u4ed9\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd8\u5f97\u591a\u8c22\u4f60\u7684\u4e07\u5e74\u6696\u7389\uff0c\u8981\u4e0d\u7136 \u6211\u8fd8\u5f97\u5728\u9759\u5ba4\u4e2d\u591a\u8eba\u51e0\u5929\u3002\u201d\u94c1\u62d0\u4ed9\u88ab\u51b0\u5ddd\u5929\u5973\u9650\u671f\u4e0b\u5c71\uff0c\u5fc3\u4e2d\u81ea\u662f\u4e0d\u8bf4\uff0c\u795e\u60c5\u4ea6\u89c9\u5c34\u5c2c\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4e00\u4e9b\u5bd2\u6c14\u672a\u5c3d\uff0c\u8be5\u7528\u795e\u519c\u8349\u714e\u6c64\u4e00\u670d\uff0c\u6b64\u5353\u51b0\u5cf0\u5357 \u9762\u751f\u6709\uff0c\u660e\u65e5\u6211\u53eb\u4f8d\u5973\u4f34\u4e91\u771f\u59d0\u59d0\u53bb\u91c7\u53d6\u56de\u6765\u3002\u201d\u8c22\u4e91\u771f\u6de1\u6de1\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u660e\u65e5\u7ea6\u4e86\u4eba\u4eba\u51b0\u5cf0\u4e0b\u9762\u6bd4\u5251\uff0c\u53ef\u80fd\u56de\u6765\u5f88\u665a\uff0c\u4f60\u4eec\u540e\u65e5\u4e00\u65e9\u8981\u8d70\uff0c\u8fd9 \u5e2d\u9152\u4fbf\u7b97\u662f\u8df5\u884c\u9152\u5566\u3002\u201d\u94c1\u62d0\u8baa\u592b\u5987\u4e00\u9f50\u6b20\u8eab\u9053\u8c22\uff0c\u795e\u8272\u4ecd\u662f\u4ee5\u4e0d\u81ea\u7136\u3002\u51b0\u5ddd\u5929\u5973\u5374\u662f\u6ee1\u4e0d \u5728\u4e4e\uff0c\u8bf7\u4ed6\u4eec\u559d\u4e86\u4e24\u676f\u9152\uff0c\u5ffd\u9053\uff1a\u201c\u94c1\u62d0\u4ed9\uff0c\u4f60\u8db3\u8ff9\u904d\u5929\u4e0b\uff0c\u77e5\u9053\u5404\u5bb6\u5404\u6d3e\u7684\u5251\u672f\uff0c\u6709\u4e00\u79cd \u5251\u672f\uff0c\u751a\u4e3a\u5947\u602a\uff0c\u5982\u6b64\u8fd9\u822c\uff0c\u4e0d\u77e5\u4f60\u89c1\u8fc7\u6ca1\u6709\uff1f\u201d\u53e3\u8bb2\u6307\u5212\uff0c\u8bf4\u4e86\u51e0\u4e2a\u7279\u522b\u7684\u62db\u5f0f\uff0c\u9053\uff1a \u201c\u8fd9\u5251\u672f\u4fbf\u662f\u7ea6\u6211\u6bd4\u5251\u7684\u90a3\u4e2a\u5c11\u5e74\u6240\u4f7f\u51fa\u6765\u7684\uff0c\u4ed6\u8fd8\u6709\u4e00\u79cd\u6697\u5668\uff0c\u51fa\u624b\u751a\u662f\u5947\u602a\uff0c\u51fa\u624b\u4fbf\u662f \u4e00\u9053\u4e4c\u91d1\u5149\u8292\uff01\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\uff0c\u4e91\u771f\u542c\u4f60\u7684\u4f8d\u5973\u8bf4\u8fc7\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90a3\u4e48 \u8fd9\u53c8\u662f\u4ec0\u4e48\u5251\u6cd5\uff0c\u6697\u5668\u53c8\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u53ef\u6709\u4ec0\u4e48\u7834\u7efd\u53ef\u5bfb\u4e48\uff1f\u201d\u94c1\u62d0\u4ed9\u5fc3\u9053\uff1a\u201c\u539f\u6765\u535c\u662f\u5411 \u6211\u8bf7\u6559\u6765\u5566\u3002\u6211\u4e14\u5413\u4f60\u4e00\u5413\u3002\u201d\u4fbf\u9053\uff1a\u201c\u8fd9\u5251\u6cd5\u6b63\u662f\u5929\u4e0b\u95fb\u540d\u7684\u5929\u5c71\u5251\u6d3e\uff0c\u662f\u524d\u8f88\u9ad8\u50e7\u6666 \u660e\u60ee\u5e08\u91c7\u5404\u5bb6\u5404\u6d3e\u7684\u5251\u6cd5\uff0c\u878d\u6c47\u8d2f\u901a\uff0c\u52a0\u4ee5\u53d8\u5316\uff0c\u72ec\u521b\u5929\u4e0b\u65e0\u4eba\u80fd\u7834\uff01\u201d\u51b0\u5ddd\u5929\u5973\u201c\u54fc\u201d\u4e86 \u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4fbf\u662f\u5929\u5c71\u5251\u6cd5\u3002\u201d\u8981\u77e5\u51b0\u5ddd\u5929\u5973\u7684\u7236\u4eb2\u66fe\u8d25\u5728\u5929\u5c71\u6d3e\u7684\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u624b \u4e0b\uff0c\u6240\u4ee5\u72ec\u8d70\u6f20\u5916\uff0c\u8981\u91c7\u7528\u897f\u571f\u7684\u5251\u6cd5\u63c9\u5408\u8fbe\u6469\u5251\u6cd5\u53e6\u521b\u65b0\u62db\uff0c\u518d\u4e0e\u5929\u5c71\u5251\u6cd5\u4e00\u51b3\u96cc\u96c4\u3002\u51b0 \u5ddd\u5929\u5973\u81ea\u5e7c\u5373\u95fb\u5929\u5c71\u5251\u6cd5\u4e4b\u540d\uff0c\u60f3\u4e0d\u5230\u90a3\u767d\u8863\u5c11\u5e74\u4f7f\u7684\u5c31\u662f\u5929\u5c71\u5251\u6cd5\u3002\u94c1\u62d0\u4ed9\u53c8\u9053\uff1a\u201c\u90a3\u6697 \u5668\u6765\u5934\u66f4\u5927\uff0c\u540d\u53eb\u5929\u5c71\u795e\u8292\uff0c\u53ea\u6709\u5929\u5c71\u624d\u6709\uff0c\u975e\u91d1\u975e\u94c1\uff0c\u5374\u575a\u903e\u91d1\u94c1\uff0c\u6709\u5404\u79cd\u5404\u6837\u7684\u5f62\u5f0f\uff0c \u957f\u8005\u5982\u7bad\uff0c\u5706\u8005\u5982\u73e0\uff0c\u60f3\u5f53\u5e74\u51cc\u672a\u98ce\u5927\u4fa0\u5c31\u662f\u4ee5\u5929\u5c71\u795e\u8292\u800c\u5f97\u540d\uff0c\u53ef\u4ee5\u60f3\u89c1\u5b83\u7684\u5389\u5bb3\uff01\u201d \u3000\u3000\u94c1\u62d0\u4ed9\u628a\u5929\u5c71\u6d3e\u7684\u5251\u6cd5\u6697\u5668\uff0c\u8bf4\u5f97\u5929\u4e0a\u6709\u5730\u4e0b\u65e0\uff0c\u51b0\u5ddd\u5929\u5973\u542c\u4e86\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e5f\u672a\u5fc5\u89c1 \u5f97\u5c31\u662f\u5929\u4e0b\u65e0\u654c\u3002\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5b66\u517c\u4e2d\u5916\uff0c\u4e5f\u8bb8\u80fd\u4e0e\u4ed6\u6253\u4e2a\u5e73\u624b\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u4e0d\u8fc7 \u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u9047\u597d\u624b\u9080\u6597\uff0c\u603b\u662f\u672a\u6599\u80dc\uff0c\u5148\u9632\u8d25\uff0c\u4f60\u8fd8\u662f\u5c0f\u5fc3\u8c28\u614e\u7684\u597d\u3002\u201d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5206\u660e \u662f\u8bf4\u51b0\u5ddd\u5929\u5973\u4e0d\u662f\u90a3\u767d\u8863\u5c11\u5e74\u4e4b\u654c\uff0c\u51b0\u5ddd\u5929\u5973\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u91cc\u597d\u751f\u4e0d\u670d\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u672c\u60f3\u5411\u94c1\u62d0\u4ed9\u8bf7\u6559\u4e24\u4e8b\uff0c\u4e00\u662f\u90a3\u767d\u8863\u5c11\u5e74\u5251\u6cd5\u7684\u6765\u5386\uff0c\u4e8c\u662f\u8fd9\u79cd\u5251\u6cd5\u7684\u4f18\u52a3\u6240 \u5728\u3002\u5982\u4eca\u524d\u8005\u5df2\u7ecf\u77e5\u9053\uff0c\u800c\u5929\u5c71\u5251\u6cd5\u7684\u7834\u7efd\uff0c\u636e\u94c1\u62d0\u4ed9\u6240\u8bf4\uff0c\u5374\u627e\u4e0d\u51fa\u6765\u3002\u51b0\u5ddd\u5929\u5973\u597d\u751f\u4e0d \u60a6\uff0c\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u4e0d\u53ef\u7834\u7684\u5251\u6cd5\uff0c\u6709\u4e00\u79cd\u6b66\u529f\uff0c\u5c31\u81ea\u5fc5\u6709\u53e6\u4e00\u79cd\u514b\u5236\u5b83\u7684\u6b66\u529f\u3002\u4e0d\u8fc7\u6211\u8fd8\u662f \u8981\u591a\u8c22\u4f60\u7684\u6307\u70b9\uff0c\u73b0\u5728\u6211\u656c\u4f60\u4eec\u592b\u5987\u4e09\u676f\uff0c\u4e00\u8868\u611f\u8c22\uff0c\u4e8c\u4f5c\u996f\u884c\u3002\u201d\u53eb\u4f8d\u5973\u659f\u4e0a\u9152\u6765\uff0c\u4e0e\u94c1 \u62d0\u4ed9\u592b\u5987\u63a5\u8fde\u5e72\u4e86\u4e09\u676f\u3002 \u3000\u3000\u8c22\u4e91\u771f\u4f3c\u662f\u4e0d\u80dc\u9152\u529b\uff0c\u5ffd\u7136\u79bb\u5e2d\u800c\u8d77\uff0c\u672a\u5230\u6e56\u8fb9\uff0c\u5c31\u201c\u54c7\u201d\u7684\u4e00\u58f0\u5455\u4e86\u51fa\u6765\uff0c\u5c06\u9152\u83dc\u55b7 \u5f97\u6ee1\u5730\u90fd\u662f\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u9152\u662f\u6211\u81ea\u917f\u7684\u767e\u82b1\u9152\uff0c\u9152\u6027\u6e29\u548c\uff0c\u5e76\u975e\u70c8\u9152\uff0c\u600e\u4e48\u4e91\u771f\u59d0\u59d0 \u5982\u6b64\u4e0d\u6d4e\uff1f\u201d\u53ea\u89c1\u8c22\u4e91\u771f\u6447\u6447\u6643\u6643\u7684\u8d70\u4e86\u56de\u6765\uff0c\u53cc\u624b\u6367\u5fc3\uff0c\u9762\u8272\u6cdb\u767d\u3002\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u5566\uff1f\u201d\u8c22\u4e91\u771f\u9762\u4e0a\u4e00\u7ea2\uff0c\u5374\u4e0d\u8a00\u8bed\u3002\u770b\u5f62\u72b6\uff0c\u53c8\u4e0d\u4f3c\u662f\u9189\u9152\u3002\u51b0\u5ddd\u5929\u5973\u53eb\u4f8d\u5973\u53bb\u53d6\u51b0\u5757\u548c\u6e7f \u624b\u4e2d\uff0c\u8c22\u4e91\u771f\u8fde\u8fde\u6447\u624b\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u4e0d\u5fc5\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e2d\u9152\u5417\uff1f\u4ee5\u51b0\u5757\u4e00\u6577\uff0c \u7acb\u523b\u6e05\u9192\u3002\u201d\u8c22\u4e91\u771f\u7ea2\u751f\u53cc\u988a\uff0c\u6447\u9996\u4e0d\u8bed\u3002\u94c1\u62d0\u4ed9\u660e\u767d\u4e86\u51e0\u5206\uff0c\u9053\uff1a\u201c\u8ba9\u6211\u731c\u731c\u770b\u3002\u201d\u8c22\u4e91 \u771f\u6015\u4ed6\u76f4\u8bf4\uff0c\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u4e0d\u5fc5\u80e1\u731c\uff0c\u662f\u6211\uff0c\u6211\u6709\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u6709\u4e86\u4ec0 \u4e48\uff1f\u201d\u8c22\u4e91\u771f\u9762\u5b54\u6da8\u7ea2\uff0c\u539f\u6765\u662f\u5979\u6709\u4e86\u5b69\u5b50\u3002\u51b0\u5ddd\u5929\u5973\u4e0e\u9648\u5929\u5b87\u90fd\u8fd8\u4e0d\u5927\u61c2\u4eba\u4e8b\uff0c\u542c\u5f97\u7cca\u91cc \u7cca\u6d82\uff0c\u94c1\u62d0\u4ed9\u5374\u662f\u5927\u559c\uff0c\u4ed6\u7ed3\u5a5a\u591a\u5e74\uff0c\u5e74\u5c06\u534a\u767e\uff0c\u5982\u4eca\u59cb\u6709\u4e86\u5b69\u5b50\uff0c\u4e00\u65f6\u559c\u4e0d\u81ea\u7981\uff0c\u628a\u9152\u676f \u4e5f\u6454\u5230\u5730\u4e0a\uff0c\u5949\u597d\u90a3\u662f\u7389\u676f\uff0c\u4e0d\u81f4\u6454\u574f\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u767d\u4e86\u4ed6\u4e00\u773c\u3002\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8fd9\u4e48\u6b22\u559c\uff1f\u4f60\u8fd8\u672a\u5b8c\u5168\u5eb7\u590d\uff0c\u5927\u559c\u5927\u6012\uff0c\u90fd\u8be5\u907f \u514d\u3002\u597d\u5566\uff0c\u65f6\u4e0d\u65e9\u4e86\u3002\u6211\u4e5f\u8be5\u56de\u53bb\u5566\uff0c\u4f60\u4eec\u540e\u65e5\u4e00\u65e9\u4e0b\u5c71\u5c31\u662f\uff0c\u6211\u4e0d\u9001\u5566\u3002\u201d \u3000\u3000\u9152\u5e2d\u4e0d\u6b22\u800c\u6563\u3002\u662f\u591c\uff0c\u51b0\u5ddd\u5929\u5973\u7761\u4e0d\u7740\u89c9\uff0c\u9648\u5929\u5b87\u4e5f\u7761\u4e0d\u7740\u89c9\uff0c\u60f3\u8d77\u540e\u65e5\u4e00\u65e9\u4fbf\u8981\u4e0b \u5c71\uff0c\u9887\u4e3a\u6005\u60c6\u3002\u4e00\u5ffd\u513f\u60f3\u8d77\u660e\u65e5\u51b0\u5ddd\u5929\u5973\u4e0e\u90a3\u767d\u8863\u5c11\u5e74\u4e4b\u4f1a\uff0c\u81ea\u5df1\u4e5f\u5f88\u60f3\u77a7\u8fd9\u573a\u70ed\u95f9\uff0c\u4f46\u5374 \u4e0d\u77e5\u51b0\u5ddd\u5929\u5973\u5141\u662f\u4e0d\u5141\uff0c\u4e00\u5ffd\u513f\u53c8\u60f3\u8d77\u90a3\u85cf\u65cf\u5c11\u5973\u829d\u5a1c\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u795e\u601d\u604d\u998f\uff0c\u7d22\u6027\u62ab \u8863\u800c\u8d77\uff0c\u8d70\u5230\u56ed\u4e2d\uff0c\u4fe1\u6b65\u6240\u4e4b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u53c8\u8d70\u8fd1\u4e86\u90a3\u5ea7\u795e\u79d8\u7684\u5c4b\u5b50\uff0c\u53ea\u89c1\u6708\u5149\u5982\u6c34\uff0c\u5730\u5982 \u94f6\uff0c\u5ffd\u7136\u542c\u5f97\u811a\u6b65\u4e4b\u58f0\uff0c\u9648\u5929\u5b87\u6025\u5fd9\u4f0f\u5728\u4e00\u7247\u5047\u5c71\u6e56\u77f3\u4e4b\u540e\uff0c\u53ea\u89c1\u90a3\u5ea7\u57a6\u4e8e\u7684\u95e8\u6253\u5f00\uff0c\u4e00\u4e2a \u62ab\u7740\u767d\u7eb1\u7684\u5c11\u5973\u8d70\u4e86\u51fa\u6765\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u51b0\u5ddd\u5929\u5973\u3002 \u3000\u3000\u9648\u5929\u5b87\u66fe\u542c\u829d\u5a1c\u8bf4\u8fc7\uff0c\u8bf4\u8fd9\u95f4\u5c4b\u5b50\u4e43\u662f\u5bab\u4e2d\u7981\u5730\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u6562\u8fdb\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u6bcf\u9022\u6714 \u671b\u4e4b\u591c\uff0c\u5c31\u8981\u72ec\u81ea\u5230\u8fd9\u95f4\u5c4b\u53bb\uff0c\u803d\u6401\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5979\u505a\u4ec0\u4e48\uff0c\u8c01\u4e5f\u4e0d\u6562\u95ee\u3002\u9648\u5929\u5b87\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u82e5\u88ab\u51b0\u5ddd\u5929\u5973\u77a7\u89c1\u6211\u5728\u8fd9\u513f\uff0c\u5b9a\u4ee5\u4e3a\u6211\u5077\u7aa5\u5979\u7684\u884c\u8e2a\uff0c\u4ee5\u5979\u7684\u813e\u6c14\u4e4b\u53e4\u602a\uff0c\u4e0d\u77e5\u9053\u8be5\u5982\u4f55 \u8d23\u7f5a\u6211\u5462\uff01\u201d\u4f0f\u5728\u5047\u5c71\u77f3\u540e\uff0c\u5927\u6c14\u4e5f\u4e0d\u6562\u900f\u5df1\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u9762\u5bb9\u5fe7\u90c1\uff0c\u7f13\u7f13\u8d70\u8fd1\u4e86\u6765\uff0c\u9648\u5929 \u5b87\u5fc3\u5934\u9e7f\u649e\uff0c\u535c\u535c\u4e71\u8df3\u3002\u51b0\u5ddd\u5929\u5973\u8d70\u5230\u8ddd\u79bb\u4e08\u4f59\u4e4b\u5730\uff0c\u5ffd\u7136\u505c\u6b65\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9648\u5929\u5b87\u5413 \u5f97\u51b7\u6c57\u76f4\u6d41\uff0c\u53ea\u9053\u5979\u5df2\u53d1\u73b0\uff0c\u4ece\u77f3\u9699\u4e4b\u4e2d\u7aa5\u51fa\uff0c\u53ea\u89c1\u53c8\u662f\u4e00\u4e2a\u5c11\u5973\u7684\u80cc\u5f71\uff0c\u5411\u7740\u897f\u5317\u65b9\u5b50\u7136 \u72ec\u884c\uff0c\u90a3\u65b9\u5411\u6b63\u5bf9\u7740\u81ea\u5df1\u7684\u4f4f\u6240\uff0c\u9648\u5929\u5b57\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u8fd9\u4e48\u591c \u4e86\uff0c\u4f60\u8fd8\u51fa\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u829d\u5a1c\u4e00\u5b9a\u662f\u60f3\u53bb\u627e\u6211\uff0c\u4e0d\u77e5\u5979\u6709\u4ec0\u4e48\u8bdd\u8981\u5bf9\u6211\u8bf4\u3002\u5440\uff0c\u8fd8\u6709 \u660e\u5929\u4e00\u5929\uff0c\u540e\u5929\u5c31\u89c1\u4e0d\u7740\u5979\u4e86\u3002\u201d\u53ea\u542c\u5f97\u829d\u5a1c\u8bf4\u9053\uff1a\u201c\u5929\u5973\u59d0\u59d0\uff0c\u6211\u5230\u5904\u627e\u4f60\uff0c\u539f\u6765\u4f60\u5728\u8fd9 \u513f\u3002\u201d\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u59ae\u5b50\u4e5f\u4f1a\u8bf4\u5047\u8bdd\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u627e\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u59d0\u59d0\u5df2\u6709\u5236\u80dc\u7834\u654c\u4e4b\u6cd5\u6ca1\u6709\uff1f\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5173\u5fc3\u8fd9\u4e2a\uff0c\u4f60\u53ef\u653e\u5fc3\uff0c\u6211\u7eb5\u4e0d\u80fd\u80dc\uff0c\u4e5f\u65ad\u4e0d\u4f1a\u8d25\u7ed9\u90a3\u4e2a\u5c11\u5e74\u3002\u201d\u829d\u5a1c\u4e00\u7b11 \u9053\uff1a\u201c\u6240\u4ee5\u5440\u2026\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6240\u4ee5\u4ec0\u4e48\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u6240\u4ee5\u5440\uff0c\u4f60\u4eec\u660e\u65e5\u8fd9\u573a\u6597\u5251\uff0c\u4e00 \u5b9a\u975e\u5e38\u597d\u770b\uff0c\u6211\u60f3\uff0c\u6211\u60f3\u2014\u2014\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4e5f\u60f3\u53bb\u770b\u70ed\u95f9\u662f\u4e0d\u662f\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u59d0\u59d0 \u771f\u731c\u5bf9\u4e86\u6211\u7684\u5fc3\u601d\uff0c\u6211\u60f3\u660e\u65e5\u8fd9\u573a\u6597\u5251\uff0c\u82e5\u7136\u9519\u8fc7\uff0c\u53ea\u6050\u4eca\u751f\u4e5f\u96be\u518d\u9047\u3002\u201d\u51b0\u5ddd\u5929\u5973\u672c\u6765\u5fc3 \u4e8b\u91cd\u91cd\uff0c\u4e0d\u5927\u9ad8\u5174\uff0c\u89c1\u829d\u5a1c\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\uff0c\u5bf9\u81ea\u5df1\u7684\u5251\u6cd5\u5982\u6b64\u94a6\u4f69\uff0c\u4e0d\u89c9\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u672c\u6765\u4e0d\u51c6\u4efb\u4f55\u4eba\u53bb\u770b\uff0c\u73b0\u5728\u7279\u522b\u51c6\u4f60\u4f8b\u5916\uff0c\u597d\u5566\uff0c\u660e\u65e5\u4f60\u548c\u6211\u7684\u8d34\u8eab\u6043\u5973\u5e7d\u840d\u5728\u897f\u8fb9\u7684 \u5c71\u5934\u770b\u5427\u3002\u201d\u829d\u5a1c\u9053\uff1a\u201c\u90a3\u5c71\u5934\u79bb\u4f60\u4eec\u6bd4\u5251\u4e4b\u5904\u4e0d\u662f\u5f88\u8fdc\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90a3\u5c71\u5934\u5f88 \u9ad8\uff0c\u53ef\u4ee5\u770b\u5f97\u89c1\u7684\u3002\u51c6\u4f60\u7279\u522b\u7834\u4f8b\uff0c\u4f60\u8fd8\u4e0d\u5fc3\u8db3\u5417\uff0c\u597d\u5566\uff0c\u4f60\u968f\u6211\u56de\u53bb\uff0c\u6211\u518d\u6307\u70b9\u4f60\u4e00\u8def\u5251 \u6cd5\u3002\u4f60\u6b64\u6b21\u4e0a\u5c71\uff0c\u6211\u7b54\u5e94\u518d\u6559\u4f60\u4e09\u65e5\uff0c\u6559\u5b8c\u8fd9\u8def\u5251\u6cd5\u4e4b\u540e\uff0c\u529f\u8bfe\u5c31\u7b97\u5b8c\u5566\u3002\u201d \u3000\u3000\u4e24\u4eba\u5728\u82b1\u6811\u4e1b\u4e2d\u5189\u5189\u800c\u6ca1\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u9648\u5929\u5b87\u770b\u5f97\u6ee1\u56ed\u5b50\u91cc\u5168\u65e0\u4eba\u5f71\uff0c\u6e05\u6e05\u5bc2\u5bc2\uff0c\u8fde\u9e1f \u513f\u4e5f\u4f3c\u90fd\u7761\u7740\u4e86\uff0c\u8fd9\u624d\u6562\u51fa\u6765\u3002\u8d70\u4e86\u4e24\u6b65\uff0c\u95fb\u5f97\u90a3\u95ee\u5c4b\u5b50\u6240\u53d1\u51fa\u7684\u5f02\u9999\uff0c\u7279\u522b\u6709\u4e00\u80a1\u5438\u4eba\u7684 \u529b\u91cf\uff0c\u4e0d\u81ea\u89c9\u5730\u8d70\u5230\u95e8\u524d\uff0c\u6478\u6478\u90a3\u4e2a\u95e8\u73af\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u4e0d\u77e5\u6709\u4ec0\u4e48\u53e4\u602a\u7269\u4e8b\u53f6\u90a3\u95e8\u73af\u8f6c \u4e86\u4e24\u8f6c\uff0c\u5ffd\u7136\u81ea\u52a8\u5f00\u4e86\uff0c\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\u3002\u4f46\u662f\u597d\u5947\u5fc3\u4f7f\u4ed6\u8d70\u4e86\u8fdb\u53bb\u3002\u91cc\u9762\u5e03\u7f6e\u5982\u540c\u795e\u6bbf\uff0c \u4e2d\u95f4\u8d2e\u6709\u4e00\u4e2a\u5973\u5b50\u7684\u5851\u50cf\uff0c\u9762\u5982\u6ee1\u6708\uff0c\u91d1\u53d1\u62ab\u80a9\uff0c\u7adf\u662f\u4e00\u4e2a\u80e1\u5973\u7684\u5851\u50cf\u3002\u9648\u5929\u5b87\u6b63\u5728\u51fa\u5947\uff0c \u5ffd\u95fb\u5f97\u80cc\u540e\u6709\u4eba\u54b3\u55fd\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u6ee1\u9762\u6012\u5bb9\uff0c\u6307\u7740\u81ea\u5df1\uff01 \u3000\u3000\u9648\u5929\u5b87\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u771f\u4e2a\u662f\u9b42\u98de\u9b44\u6563\uff0c\u4e00\u9897\u5fc3\u90fd\u4f3c\u4e4e\u8981\u4ece\u53e3\u8154\u4e2d\u8df3\u51fa\u6765\uff01\u53ea\u542c\u5f97\u51b0 \u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u5927\u80c6\uff0c\u4f60\u6765\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u9648\u5929\u5b87\u555c\u555c\u61e6\u61e6\uff0c\u9053\uff1a\u201c\u6211\u3001\u6211\u3001 \u6211\u3001\u6211\u4e0d\u77e5\u9053\u8fd9\u91cc\u4e0d\u80fd\u8fdb\u6765\uff01\u201d\u51b0\u5ddd\u5929\u5973\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u829d\u5a1c\u672a\u5bf9\u4f60\u8bf4\u5417\uff1f\u6211 \u4e0d\u76f8\u4fe1\uff01\u82e5\u7136\u662f\u5979\u672a\u8bf4\uff0c\u5c31\u662f\u5979\u7684\u4e0d\u5bf9\u3002\u56de\u5934\u6211\u53bb\u95ee\u5979\u3002\u6211\u4e0d\u4fe1\u829d\u5a1c\u4f1a\u8fd9\u6837\u7c97\u5fc3\u5927\u610f\uff0c\u8fde\u5bab \u4e2d\u7684\u7981\u5fcc\u90fd\u4e0d\u5411\u4f60\u63d0\u8d77\u3002\u4f60\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u4e0d\u8981\u502d\u8fc7\u4e8e\u4eba\u3002\u201d\u9648\u5929\u5b87\u672c\u5c31\u4e0d\u60ef\u8bf4\u8c0e\uff0c\u8fd9\u65f6\u66f4\u6015\u51b0 \u5ddd\u5929\u5973\u602a\u8d23\u829d\u5a1c\uff0c\u8981\u829d\u5a1c\u66ff\u81ea\u5df1\u53d7\u8fc7\uff0c\u62fc\u7740\u53d7\u8d23\uff0c\u5927\u4e86\u80c6\u5b50\uff0c\u9053\uff1a\u201c\u662f\u6211\u8bf4\u8c0e\uff0c\u829d\u5a1c\u5728\u6211\u6765 \u7b2c\u4e00\u5929\u5c31\u5bf9\u6211\u8bf4\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5927\u4e3a\u751f\u6c14\uff0c\u559d\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u5077\u5077\u8fdb\u6765\uff0c\u54fc\uff0c\u4f60\u4eec\u5e08\u5f92 \u90fd\u4e0d\u662f\u597d\u4eba\uff0c\u662f\u4f60\u7684\u5e08\u7236\u6559\u4f60\u6765\u7684\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4e0d\uff0c\u662f\u6211\u81ea\u5df1\u6765\u7684\uff0c\u6211\u4e00\u65f6\u597d\u5947\uff0c\u4e0d \u77e5\u4e0d\u89c9\u5730\u5c31\u8d70\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u8bf4\u4e86\u4e4b\u540e\uff0c\u5fc3\u4e2d\u5766\u7136\uff0c\u53cd\u800c\u4e0d\u4f3c\u5148\u524d\u5bb3\u6015\uff0c\u5c4b\u5b50\u91cc\u56db\u89d2\u90fd\u662f\u70b9\u6709\u957f\u660e\u706f\uff0c\u5899\u4e0a\u8fd8\u5d4c\u6709\u591c \u660e\u73e0\uff0c\u5149\u7ebf\u867d\u7136\u4e0d\u5f3a\uff0c\u4f46\u5df2\u7167\u89c1\u51b0\u5ddd\u5929\u5973\u7684\u6012\u5bb9\uff0c\u9648\u5929\u5b87\u6765\u4e86\u8fd9\u51e0\u5929\uff0c\u8fd8\u4ece\u672a\u89c1\u8fc7\u51b0\u5ddd\u5929\u5973 \u751f\u6c14\uff0c\u8fd9\u65f6\u88ab\u5979\u773c\u5149\u4e00\u5c04\uff0c\u53ea\u89c9\u4e00\u80a1\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u731b\u7136\u95f4\u5ffd\u89c9\u9888\u4e0a\u4e00\u7d27\uff0c\u6d51\u8eab\u9178\u8f6f\uff0c\u539f\u6765 \u5df2\u88ab\u51b0\u5ddd\u5929\u5973\u5939\u9886\u4e00\u628a\u63d0\u8d77\uff1b\u9648\u5929\u5b87\u4ece\u8427\u9752\u5cf0\u5b66\u4e86\u4e03\u516b\u5e74\u6b66\u529f\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u5df2\u7b97\u5f97\u4e0d\u9519\uff0c\u8fd9 \u65f6\u88ab\u51b0\u5ddd\u5929\u5973\u4e00\u628a\u63d0\u8d77\uff0c\u5982\u6349\u5c0f\u9e21\uff0c\u7adf\u662f\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8981\u6765\u8fd9\u513f\uff0c\u90a3\u5c31\u4e0d\u5fc5\u518d\u51fa\u53bb\u4e86\uff01\u201d\u5c06\u4ed6\u5728\u7a7a\u4e2d\u8f6c\u4e86 \u4e24\u8f6c\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u9648\u5929\u5b87\u53ea\u89c9\u5982\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u56db\u8fb9\u5899\u58c1\u6709\u8bb8\u591a\u53e4\u53e4\u602a\u602a\u7684\u4eba\u5f62\uff0c\u597d\u50cf\u5996\u9b54 \u9b3c\u602a\uff0c\u8981\u98de\u6251\u51fa\u6765\uff0c\u62e9\u4eba\u800c\u556e\u3002\u9648\u5929\u5b87\u88ab\u5979\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5934\u660f\u773c\u82b1\uff0c\u5ffd\u800c\u53c8\u4f3c\u4ece\u4e91\u7aef\u4e2d\u6389\u4e86\u4e0b \u6765\uff0c\u539f\u6765\u662f\u51b0\u5ddd\u5929\u5973\u7528\u529b\u5c06\u4ed6\u5411\u5730\u4e0a\u4e00\u6454\uff01 \u3000\u3000\u8fd9\u4e00\u6454\u529b\u5ea6\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u9648\u5929\u5b87\u9a87\u53eb\u4e00\u58f0\uff0c\u9b42\u98de\u9b44\u6563\uff0c\u672c\u4ee5\u4e3a\u5b9a\u88ab\u6454\u6b7b\u65e0\u7591\uff0c\u90a3\u77e5\u4e00 \u78b0\u5730\u9762\uff0c\u5730\u9762\u5ffd\u7136\u88c2\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u9648\u5929\u5b87\u8dcc\u5165\u6d1e\u4e2d\uff0c\u78b0\u5f97\u808b\u9aa8\u4f5c\u75db\uff0c\u5374\u5e76\u672a\u53d7\u4f24\uff0c\u8df3\u8d77\u6765 \u65f6\uff0c\u53ea\u89c1\u6d1e\u4e2d\u6f06\u9ed1\uff0c\u4e0d\u8fa8\u4e94\u6307\uff0c\u4e0a\u9762\u7684\u88c2\u7f1d\uff0c\u65e9\u5df2\u590d\u5408\uff0c\u9690\u9690\u7684\u542c\u5230\u4e0a\u9762\u4f20\u6765\u7684\u8f7b\u5fae\u7684\u811a\u6b65 \u58f0\uff0c\u5927\u7ea6\u662f\u51b0\u5ddd\u5929\u5973\u5df2\u7ecf\u8d70\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u88ab\u56f0\u5728\u9ed1\u6d1e\u4e2d\uff0c\u4f46\u611f\u4e00\u9635\u9635\u5bd2\u51b7\u6f6e\u6e7f\u4e4b\u6c14\u88ad\u6765\uff0c\u751a\u662f\u96be\u53d7\uff0c\u5e78\u800c\u4ed6\u7684\u5185\u529f\uff0c\u5df2\u6709 \u521d\u6b65\u6839\u57fa\uff0c\u76d8\u819d\u9759\u5750\uff0c\u8bd5\u884c\u5410\u7eb3\uff0c\u679c\u7136\u597d\u4e86\u4e00\u4e9b\u3002\u9648\u5929\u5b87\u53c8\u5bb3\u6015\uff0c\u53c8\u540e\u6094\uff0c\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\u6240 \u8bf4\u7684\u201c\u4f60\u5c31\u4e0d\u5fc5\u518d\u51fa\u53bb\u4e86\uff01\u201d\u8fd9\u4e00\u53e5\u8bdd\uff0c\u771f\u662f\u4e0d\u5bd2\u800c\u6817\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u5979\u771f\u7684\u8981\u7f5a\u6211\u5728\u8fd9\u6d1e \u4e2d\u8fc7\u4e00\u4e16\u4e0d\u6210\uff1f\u5440\uff0c\u5e08\u7236\u3001\u5e08\u5a18\u548c\u7236\u4eb2\u90fd\u4e0d\u80fd\u89c1\u4e86\uff0c\u829d\u5a1c\u4e5f\u4e0d\u80fd\u89c1\u4e86\u3002\u592a\u9633\u3001\u6708\u4eae\u548c\u4e00\u5207\u7684 \u7f8e\u666f\u90fd\u4e0d\u80fd\u89c1\u4e86\u3002\u9648\u5929\u5b87\u8fd8\u662f\u4e2a\u5927\u5b69\u5b50\uff0c\u60f3\u5230\u4f24\u5fc3\u4e4b\u5904\uff0c\u4e0d\u89c9\u9e23\u545c\u54bd\u54bd\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4e0a\u9762\u53c8\u9690\u9690\u6709\u811a\u6b65\u58f0\uff0c\u9648\u5929\u5b87\u5ffd\u800c\u60f3\u9053\uff1a\u201c\u82e5\u662f\u51b0\u5ddd\u5929\u5973\u8fdb\u6765\uff0c\u89c1\u6211 \u54ed\u6ce3\uff0c\u5c82\u4e0d\u7b11\u6211\uff1f\u201d\u4ed6\u5bf9\u51b0\u5ddd\u5929\u5973\u672c\u6765\u4e0d\u6562\u6028\u6068\uff0c\u4f46\u5374\u4e0d\u613f\u5bf9\u5979\u793a\u5f31\uff0c\u7acb\u523b\u6536\u4e86\u773c\u6cea\uff0c\u53c8\u518d \u76d8\u819d\u9759\u5750\u3002\u90a3\u811a\u6b65\u58f0\u8fd1\u4e86\u53c8\u8fdc\u4e86\uff0c\u6d1e\u4e2d\u4e00\u7247\u6f06\u9ed1\uff0c\u51b0\u5ddd\u5929\u5973\u6ca1\u6709\u8fdb\u6765\u3002\u9648\u5929\u5b87\u54ea\u91cc\u77e5\u9053\uff0c\u8fd9 \u6b63\u662f\u829d\u5a1c\u548c\u51b0\u5ddd\u5929\u5973\u90a3\u4e00\u4f4d\u8d34\u8eab\u4f8d\u5973\u5e7d\u840d\u7684\u811a\u6b65\u58f0\u3002\u5979\u4eec\u6b66\u529f\u7684\u6839\u57fa\u5c1a\u6d45\uff0c\u811a\u7a0b\u4e0d\u5feb\uff0c\u6240\u4ee5 \u5929\u672a\u4eae\u5c31\u8d77\u6765\uff0c\u51c6\u5907\u8d76\u5230\u51b0\u5cf0\u4fa7\u9762\u7684\u5c71\u5934\uff0c\u770b\u51b0\u5ddd\u5929\u5973\u4e0e\u767d\u8863\u5c11\u5e74\u4e2d\u5348\u90a3\u4e00\u573a\u6bd4\u5251\u3002 \u3000\u3000\u9648\u5929\u5b87\u597d\u751f\u5931\u671b\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u542c\u5f97\u56ed\u4e2d\u557c\u9e1f\u4e4b\u58f0\uff0c\u9648\u5929\u5b87\u60f3\u9053\uff1a\u201c\u5510\u4eba\u8bd7\u4e91\uff1a\u6625\u7720\u4e0d \u89c9\u6653\uff0c\u5904\u5904\u95fb\u557c\u9e1f\uff0c\u591c\u6765\u98ce\u96e8\u58f0\uff0c\u82b1\u843d\u77e5\u591a\u5c11\uff1f\u8fd9\u610f\u5883\u4f55\u7b49\u5e7d\u7f8e\uff0c\u4f46\u4e0e\u6211\u73b0\u5728\u7684\u5883\u9047\u5374\u6070\u6070 \u76f8\u53cd\u3002\u542c\u8fd9\u9e1f\u557c\u4e4b\u58f0\uff0c\u60f3\u5fc5\u662f\u5929\u4eae\u4e86\u3002\u829d\u5a1c\u6628\u591c\u60f3\u53bb\u627e\u6211\u672a\u9042\uff0c\u5979\u54ea\u77e5\u9053\uff0c\u6211\u88ab\u56f0\u5728\u8fd9\u513f\uff0c \u4e00\u591c\u672a\u7761\u89c9\u5462\uff01\u5440\uff0c\u591c\u6765\u867d\u65e0\u98ce\u96e8\uff0c\u4f46\u5bf9\u6211\u6765\u8bf4\uff0c\u6628\u591c\u4e4b\u4e8b\uff0c\u4e5f\u4f3c\u9047\u5230\u4e00\u573a\u5927\u98ce\u66b4\u5475\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u80e1\u601d\u4e71\u60f3\uff0c\u867d\u89c9\u773c\u795e\u56f0\u5026\uff0c\u5374\u662f\u7761\u4e0d\u7740\u89c9\u3002\u67af\u5750\u9ed1\u6d1e\uff0c\u6e21\u65e5\u5982\u5e74\uff0c\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a \u4e45\uff0c\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u5514\uff0c\u5feb\u65e5\u4e2d\u4e86\uff0c\u4ed6\u4eec\u8be5\u5728\u51b0\u5cf0\u4e0b\u9762\u6bd4\u4e86\uff0c\u53ef\u60dc\u6211\u6ca1\u8fd9\u4e2a\u773c\u798f\u3002\u201d\u6b63\u81ea\u80e1 \u601d\u4e71\u60f3\uff0c\u5ffd\u5730\u4e0b\u4f20\u6765\u602a\u58f0\uff0c\u6108\u6765\u6108\u54cd\uff0c\u5899\u58c1\u4e5f\u4f3c\u6709\u4e9b\u9707\u52a8\uff0c\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u5ffd\u53c8\u89c9\u6709\u4e00\u80a1 \u70ed\u6c14\u4ece\u5730\u5e95\u4e0b\u900f\u4e0a\u6765\uff0c\u9648\u5929\u5b87\u66f4\u662f\u60ca\u5947\uff0c\u602a\u58f0\u66f4\u54cd\uff0c\u4e0d\u4f46\u5899\u58c1\u9707\u52a8\uff0c\u8fde\u5730\u5e95\u7684\u9707\u52a8\u4e5f\u611f\u89c9\u5230 \u4e86\uff0c\u5ffd\u5730\u201c\u54d7\u5566\u201d\u4e00\u58f0\uff0c\u5899\u58c1\u7684\u7816\u5934\u9707\u843d\u51e0\u5757\uff0c\u4e00\u7247\u9633\u5149\u900f\u4e86\u8fdb\u6765\uff0c\u9648\u5929\u5b87\u4e5f\u7ed9\u9707\u5012\u5730\u4e0a\uff0c \u731b\u5730\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u5730\u9707\uff01\u201d\u897f\u85cf\u7684\u5730\u5c42\uff0c\u636e\u5730\u8d28\u5b66\u5bb6\u7684\u7814\u7a76\uff0c\u5f62\u6210\u8f83\u665a\uff0c\u5730\u5c42\u4e0b\u8fd8\u6709\u8bb8\u591a\u6d3b \u706b\u5c71\uff0c\u6240\u4ee5\u65f6\u65f6\u6709\u5927\u5c0f\u5730\u9707\uff0c\u9648\u5929\u5b87\u4e5f\u542c\u8001\u4eba\u8bf4\u8fc7\uff0c\u4e0d\u8fc7\u5374\u672a\u4eb2\u81ea\u7ecf\u5386\u8fc7\u3002\u8fd9\u65f6\u731b\u7136\u7701\u8d77\u8fd9 \u662f\u5730\u9707\uff0c\u6bd4\u8d77\u6628\u665a\u9aa4\u7136\u95f4\u89c1\u5230\u51b0\u5ddd\u5929\u5973\u4e4b\u65f6\u8fd8\u8981\u5403\u60ca\uff0c\u6b63\u60f3\u722c\u8d77\uff0c\u731b\u7136\u95f4\u4e00\u58f0\u5de8\u54cd\uff0c\u6709\u5982\u5929 \u5d29\u5730\u9677\uff0c\u9648\u5929\u5b87\u8499\u7740\u8033\u6735\uff0c\u4f46\u89c9\u4e00\u9635\u6655\u7729\uff0c\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u6655\u5012\u5730\u4e0a\uff0c\u4eba\u4e8b\u4e0d\u77e5\uff01 \u3000\u3000\u8fc7\u4e86\u8bb8\u4e45\uff0c\u9648\u5929\u5b87\u60a0\u60a0\u9192\u8f6c\uff0c\u4ece\u9707\u88c2\u7684\u7f3a\u53e3\u722c\u51fa\uff0c\u53ea\u89c1\u6574\u4e2a\u5929\u7a7a\u5e03\u6ee1\u4e00\u5c42\u9ec4\u8272\u7684\u5c18\u6c99\uff0c \u8fde\u9633\u5149\u4e5f\u662f\u9ec4\u8272\uff0c\u770b\u65e5\u5934\u7684\u5f71\u5b50\uff0c\u5df2\u662f\u7b2c\u4e8c\u65e5\u7684\u9ec4\u660f\u3002\u9648\u5929\u5b87\u8fd0\u4e86\u4e00\u4e0b\u6c14\u529b\uff0c\u7ad9\u8d77\u6765\u884c\u4e86\u51e0 \u6b65\uff0c\u53ea\u89c1\u90a3\u5ea7\u5c16\u9876\u7684\u795e\u79d8\u5c4b\u5b50\uff0c\u5899\u58c1\u4e5f\u7ed9\u9707\u5f97\u6b6a\u6b6a\u659c\u659c\uff0c\u4f46\u5374\u672a\u5012\u584c\u3002\u8fd9\u65f6\uff0c\u9648\u5929\u5b87\u4e5f\u65e0\u5fc3 \u518d\u8fdb\u53bb\u770b\u4e86\uff0c\u8dd1\u5230\u56ed\u4e2d\uff0c\u4f46\u89c1\u8bb8\u591a\u5047\u5c71\u90fd\u7ed9\u9707\u5f97\u6216\u662f\u5012\u584c\uff0c\u6216\u662f\u53d8\u4e86\u5f62\u72b6\uff0c\u6709\u51e0\u5ea7\u5bab\u6bbf\uff0c\u4e5f \u7ed9\u9707\u5012\uff0c\u53d8\u6210\u4e00\u7247\u74e6\u783e\uff0c\u4f46\u4e5f\u8fd8\u6709\u597d\u51e0\u5ea7\u5b8c\u6574\u3002\u9648\u5929\u5b87\u5927\u58f0\u547c\u53eb\uff0c\u5374\u65e0\u4eba\u58f0\u76f8\u5e94\uff0c\u6574\u5ea7\u51b0 \u5bab\uff0c\u6b7b\u4e00\u822c\u7684\u6c89\u5bc2\u3002\u9648\u5929\u5b87\u604d\u4f3c\u521a\u505a\u4e86\u573a\u6076\u68a6\uff0c\u9a87\u6015\u6781\u4e86\uff0c\u56db\u5904\u5954\u8dd1\uff0c\u53eb\u829d\u5a1c\uff0c\u5524\u5e08\u7236\uff0c\u4f46 \u4ec0\u4e48\u4eba\u4e5f\u6ca1\u6709\u89c1\u5230\uff0c\u98de\u79bd\u8d70\u517d\u4e5f\u65e9\u5df2\u9003\u547d\u53bb\u4e86\uff0c\u4ec0\u4e48\u58f0\u606f\u90fd\u6ca1\u6709\uff0c\u53ea\u89c1\u51b0\u6e56\u4e2d\u4e00\u7247\u9ec4\u8272\u7684\u5c18 \u57c3\uff0c\u53ea\u6709\u6ce8\u5165\u51b0\u6e56\u4e2d\u7684\u6d41\u6c34\u8fd8\u51c0\u743c\u4f5c\u54cd\uff01 \u3000\u3000\u731b\u4e00\u62ac\u5934\uff0c\u53c8\u53d1\u73b0\u4e86\u4e00\u6869\u66f4\u4ee4\u4eba\u60ca\u5fc3\u9a87\u76ee\u7684\u5947\u4e8b\uff1a\u51b0\u5bab\u5bf9\u9762\uff0c\u50cf\u4e00\u652f\u7389\u7b0b\uff0c\u9ad8\u63d2\u4e91\u9704\u7684 \u51b0\u5cf0\u7adf\u7136\u4e0d\u89c1\u4e86\uff01\u597d\u50cf\u9aa4\u7136\u4e4b\u95f4\uff0c\u7ed9\u4eba\u7528\u9b54\u6cd5\u79fb\u53bb\u4f3c\u7684\uff0c\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff01\u8fd9\u51b0\u5cf0\u65e5\u591c\u53d1\u51fa \u5bd2\u5149\uff0c\u4e43\u662f\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u5947\u666f\u4e4b\u4e00\uff0c\u9aa4\u7136\u4e0d\u89c1\uff0c\u4ee4\u9648\u5929\u5b87\u5728\u60ca\u5f02\u4e4b\u4e2d\u53c8\u5e26\u7740\u60cb\u60dc\u3002\u767b\u4e0a\u5bab\u4e2d \u9ad8\u5904\uff0c\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u4f46\u89c1\u6ee1\u5c71\u90fd\u662f\u78e8\u76d8\u5927\u7684\u51b0\u5757\uff0c\u6eda\u6eda\u800c\u4e0b\uff0c\u5bab\u4e2d\u4e5f\u5e73\u6dfb\u4e86\u8bb8\u591a\u5de8\u77f3\uff0c\u4e0d\u95ee \u53ef\u77e5\uff0c\u8fd9\u4e43\u662f\u51b0\u5cf0\u53d7\u5730\u9707\u9707\u584c\u4e4b\u65f6\uff0c\u98de\u5230\u8fd9\u513f\u6765\u7684\u3002\u5e78\u800c\u53ea\u6709\u51e0\u5ea7\u5bab\u6bbf\u53d7\u5de8\u77f3\u6240\u538b\uff0c\u5176\u4ed6\u5c1a \u672a\u53d7\u5230\u6ce2\u53ca\uff0c\u5f97\u4ee5\u4fdd\u5b58\u3002 \u3000\u3000\u76ee\u7779\u8fd9\u573a\u5de8\u53d8\uff0c\u9648\u5929\u5b87\u4e0d\u7981\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\u4e0e\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u6b63\u5728\u51b0\u5cf0\u4e0b\u9762\u6bd4 \u5251\uff0c\u7a81\u7136\u78b0\u5230\u5730\u9707\uff0c\u5343\u4e08\u51b0\u5cf0\u5012\u584c\u4e0b\u6765\uff0c\u6015\u4e0d\u88ab\u538b\u6210\u8089\u997c\uff01\u9648\u5929\u5b87\u6628\u665a\u867d\u7136\u53d7\u5230\u51b0\u5ddd\u5929\u5973\u7684 \u8d23\u9a82\u4e0e\u5904\u7f5a\uff0c\u4f46\u60f3\u8d77\u5979\u7684\u7eee\u5e74\u7389\u8c8c\uff0c\u7edd\u4ee3\u98ce\u534e\uff0c\u5374\u906d\u53d7\u5982\u6b64\u60e8\u7978\uff0c\u771f\u6b32\u6602\u9996\u95ee\u5929\uff1a\u5929\u4f55\u592a \u5fcd\uff01\u8fd8\u6709\u829d\u5a1c\u5462\uff01\u829d\u5a1c\u5728\u4fa7\u9762\u7684\u5c71\u5cf0\u770b\u4ed6\u4eec\u6bd4\u8bd5\uff0c\u4f1a\u4e0d\u4f1a\u4e5f\u88ab\u6ce2\u53ca\uff1f\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u773c\u524d \u73b0\u51fa\u829d\u5a1c\u90a3\u604d\u6127\u8ff7\u79bb\u3002\u795e\u79d8\u5947\u5f02\u7684\u7b11\u5bb9\uff0c\u53c8\u73b0\u51fa\u51b0\u5ddd\u5929\u5973\u96c5\u4e3d\u9ad8\u534e\u7684\u5029\u5f71\uff0c\u4e0d\u7981\u6253\u4e86\u4e00\u4e2a\u5bd2 \u6218\uff0c\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u9648\u5929\u5b87\u6458\u4e86\u4e24\u679a\u679c\u5b50\uff0c\u5403\u4e0b\u4e4b\u540e\uff0c\u7cbe\u795e\u7a0d\u632f\uff0c\u53c8\u518d\u5927\u58f0\u547c\u53eb\uff0c\u5230\u5904\u627e\u4eba\uff0c\u504c\u5927\u4e00\u4e2a\u51b0 \u5bab\uff0c\u51b7\u51b7\u6e05\u6e05\uff0c\u6beb\u65e0\u58f0\u606f\uff0c\u4e16\u754c\u4e0a\u6ca1\u6709\u4ec0\u4e48\u6bd4\u6b7b\u4e00\u6837\u7684\u5bc2\u5bde\u66f4\u4ee4\u4eba\u6050\u60e7\u7684\u4e86\uff0c\u9648\u5929\u5b87\u8fd9\u65f6\u4f46 \u613f\u9047\u7740\u4efb\u4f55\u6709\u751f\u547d\u7684\u4e1c\u897f\uff0c\u5373\u7b97\u662f\u4e00\u53ea\u732b\u4e00\u53ea\u72d7\u4e5f\u597d\uff0c\u53ef\u662f\u5374\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u56ed\u4e2d\u7684\u82b1\u8349\u8fd8\u662f \u50cf\u6628\u65e5\u4e00\u6837\uff0c\u53d1\u6563\u7740\u7f15\u7f15\u5e7d\u9999\uff0c\u6709\u5404\u79cd\u5404\u6837\u5947\u4e3d\u7684\u8272\u5f69\uff0c\u53ef\u662f\u6b64\u65f6\u6b64\u9645\uff0c\u5728\u9648\u5929\u5b87\u773c\u4e2d\u53ea\u611f \u5230\u4e00\u7247\u9eef\u6de1\uff0c\u9648\u5929\u5b87\u56db\u5904\u5bfb\u89c5\u3001\u547c\u53eb\uff01\u518d\u65e0\u987e\u5fcc\uff0c\u7a7f\u8fdb\u5404\u5904\u5bab\u6bbf\uff0c\u4ed4\u7ec6\u627e\u5bfb\uff0c\u4ecd\u662f\u4efb\u4f55\u4eba\u4e5f \u6ca1\u89c1\u5230\uff0c\u5728\u5012\u584c\u4e86\u7684\u5bab\u6bbf\u65c1\u8fb9\u5bfb\u89c5\uff0c\u4e5f\u6ca1\u6709\u53d1\u73b0\u4efb\u4f55\u5c38\u9ab8\uff01 \u3000\u3000\u8fd9\u4e48\u591a\u7684\u51b0\u5bab\u4f8d\u5973\u600e\u4e48\u4e00\u4e0b\u5b50\u5168\u90fd\u6d88\u5931\u4e86\uff1f\u5373\u7b97\u90fd\u88ab\u538b\u6b7b\uff0c\u4e5f\u8be5\u6709\u4e9b\u5c38\u4f53\u4f1a\u88ab\u53d1\u73b0\uff0c\u4f46 \u5374\u4ec0\u4e48\u90fd\u6ca1\u6709\uff01\u5982\u679c\u662f\u9003\u8d70\u4e86\uff0c\u4e5f\u8be5\u6709\u4eba\u56de\u6765\u63a2\u89c6\uff0c\u4f46\u8fd9\u65f6\u9ec4\u660f\u5df2\u901d\uff0c\u6708\u4eae\u4e5f\u5347\u4e0a\u6765\u4e86\uff0c\u4ecd \u662f\u6beb\u65e0\u4eba\u5f71\u3002\u8fd9\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u602a\u4e8b\uff01\u9648\u5929\u5b87\u771f\u6000\u7591\u773c\u524d\u6240\u89c1\uff0c\u53ea\u662f\u4e00\u573a\u5e7b\u666f\u3002\u7edd\u5bf9\u4e0d\u53ef\u80fd \u5b58\u5728\u7684\u5e7b\u666f\uff01\u4f46\u628a\u6307\u5934\u9001\u8fdb\u53e3\u4e2d\u4e00\u54ac\uff0c\u5206\u660e\u53c8\u89c9\u75bc\u75db\uff0c\u8bc1\u660e\u8fd9\u4e0d\u662f\u6076\u68a6\uff0c\u4e0d\u662f\u5e7b\u666f\u3002\u9661\u7136\u95f4 \u9648\u5929\u5b87\u89c9\u5f97\u5468\u56f4\u7684\u7a7a\u6c14\u4e5f\u4f3c\u4e4e\u51dd\u7ed3\u8d77\u6765\uff0c\u4eba\u5feb\u8981\u7a92\u606f\u4e86\u3002 \u3000\u3000\u4e00\u8f6e\u660e\u6708\uff0c\u6302\u5728\u5929\u5fc3\uff0c\u51b0\u5cf0\u5012\u584c\u4e4b\u65f6\u6240\u626c\u8d77\u7684\u5c18\u6c99\uff0c\u5df2\u6e10\u6e10\u88ab\u5c71\u98ce\u5439\u6563\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u51b0 \u5bab\u7684\u591c\u666f\u4ecd\u662f\u90a3\u4e48\u7f8e\u4e3d\uff0c\u4f46\u5374\u662f\u4e00\u79cd\u5f02\u6837\u51c4\u6e05\uff0c\u4ee4\u4eba\u4f24\u611f\u7684\u201c\u7f8e\u4e3d\u201d\u3002\u9648\u5929\u5b87\u50cf\u53d1\u4e86\u72c2\u822c\u7684 \u547c\u558a\uff0c\u5728\u56ed\u5b50\u91cc\u8dd1\u6765\u8dd1\u53bb\uff0c\u4eba\u4e0d\u77e5\u75b2\u5026\uff0c\u58f0\u97f3\u5374\u5df2\u5636\u54d1\u4e86\u3002\u65f6\u4ea4\u5348\u591c\uff0c\u5ffd\u7136\u542c\u5f97\u6709\u4e00\u4e2a\u5fae\u5f31 \u7684\u58f0\u97f3\u5524\u9053\uff1a\u201c\u662f\u5b87\u513f\u4e48\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u8fd9\u65f6\u50cf\u53d1\u73b0\u4e86\u4e16\u4e0a\u6700\u6700\u5b9d\u8d35\u7684\u4e1c\u897f\uff0c\u6b22\u559c\u5f97\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u6025\u5fd9\u5faa\u58f0\u5bfb\u89c5\uff0c\u5c31\u5728\u8eab\u8fb9 \u6709\u4e00\u95f4\u5012\u584c\u7684\u5b64\u72ec\u623f\u5b50\uff0c\u58f0\u97f3\u4ece\u6ce5\u571f\u4e4b\u4e2d\u53d1\u51fa\uff0c\u9648\u5929\u5b87\u6316\u5f00\u6ce5\u571f\uff0c\u53ea\u89c1\u94c1\u62d0\u4ed9\u8eba\u5728\u91cc\u9762\uff0c\u8863 \u88f3\u4e0a\u4e5f\u6709\u4e9b\u8840\u8ff9\u3002\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u662f\u4f60\uff1f\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u3002\u7ed9\u6211\u5f04\u4e9b\u5403 \u7684\uff0c\u62ff\u4e00\u7897\u6c34\u6765\u3002\u201d\u9648\u5929\u5b87\u6458\u4e86\u4e24\u679a\u679c\u5b50\uff0c\u53c8\u7528\u8549\u53f6\u7f16\u8d77\u6765\u76db\u4e86\u51b0\u6e56\u7684\u6c34\u7ed9\u5e08\u7236\u559d\uff0c\u94c1\u62d0\u4ed9 \u6b47\u4e86\u4e00\u9635\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u54b1\u4eec\u5e08\u5f92\u603b\u7b97\u9003\u8fc7\u8fd9\u573a\u52ab\u96be\u4e86\uff0c\u9664\u4e86\u54b1\u4eec\u4e4b\u5916\uff0c\u8fd9\u5bab\u4e2d\u8fd8\u6709\u751f\u4eba \u5417\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u5c06\u6240\u89c1\u7684\u60c5\u666f\u8bf4\u4e86\u4e00\u904d\uff0c\u94c1\u62d0\u4ed9\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u8bf4\u8fc7\uff0c\u8981\u5979\u4e0b\u5c71\u9664\u975e\u51b0 \u5cf0\u5012\u584c\uff0c\u73b0\u5728\u51b0\u5cf0\u5df2\u5012\uff0c\u53ea\u662f\u6050\u6015\u5979\u88ab\u57cb\u5728\u5c71\u4e2d\uff0c\u518d\u4e5f\u96be\u4ee5\u91cd\u73b0\u4eba\u95f4\u4e86\u3002\u201d\u9aa4\u7136\u95ee\u60f3\u8d77\u81ea\u5df1 \u7684\u59bb\u5b50\u51fa\u5916\u91c7\u836f\uff0c\u4e0d\u77e5\u751f\u6b7b\u5982\u4f55\uff0c\u5341\u5206\u6302\u5ff5\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u53d7\u4e86\u4f24\u4e48\uff1f\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u53ea\u7ed9\u77f3\u5934\u522e\u7834\u4e86\u4e00\u70b9\u76ae\u8089\u3002\u201d \u5176\u5b9e\u4ed6\u53d7\u4f24\u8fdc\u4e0d\u6b62\u6b64\uff0c\u4ed6\u672c\u6765\u8fd8\u672a\u5b8c\u5168\u8bd9\u590d\uff0c\u53d7\u4e86\u8fd9\u4e00\u573a\u5927\u5730\u9707\u7684\u9707\u8361\uff0c\u867d\u7136\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185 \u529f\uff0c\u5f97\u4ee5\u5c4e\u5168\uff0c\u4f46\u5df2\u8017\u4e86\u5341\u5e74\u529f\u529b\uff0c\u53ea\u80fd\u4ed7\u7740\u94c1\u62d0\uff0c\u52c9\u5f3a\u884c\u8d70\u4e86\u3002 \u3000\u3000\u4e24\u5e08\u5f92\u5728\u5bab\u4e2d\u7f13\u7f13\u884c\u8d70\uff0c\u53d1\u58f0\u547c\u5524\uff0c\u53c8\u662f\u5931\u671b\u3002\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u6211\u5728\u9759\u5ba4\u4e4b\u4e2d\u8fd0\u529f\u7597\u4f24\uff0c \u53ea\u89c9\u5730\u5e95\u9707\u52a8\uff0c\u63a5\u7740\u542c\u5f97\u5bab\u4e2d\u4f8d\u5973\u7684\u840d\u8d70\u547c\u5524\u4e4b\u58f0\uff0c\u8fd8\u4f3c\u4e4e\u6709\u4eba\u53eb\u6211\u7684\u540d\u5b57\uff0c\u6211\u7ec3\u529f\u6b63\u5230\u7d27 \u8981\u5173\u5934\uff0c\u960e\u8d70\u706b\u5165\u9b54\uff0c\u4e0d\u6562\u7b54\u5e94\u3002\u6b63\u60f3\u6536\u655b\u771f\u6c14\uff0c\u5148\u884c\u6563\u529f\uff0c\u518d\u51fa\u5916\u6253\u542c\uff0c\u90a3\u77e5\u5de8\u53d8\u7a81\u6765\uff0c \u6211\u7684\u9759\u5ba4\u4e5f\u7ed9\u9707\u584c\u4e86\u3002\u201d\u9648\u5929\u5b87\u542c\u5e08\u7236\u5982\u6b64\u8bf4\u6cd5\uff0c\u5730\u9707\u6765\u65f6\uff0c\u5bab\u4e2d\u5206\u660e\u8fd8\u6709\u8bb8\u591a\u4f8d\u5973\uff0c\u4f46\u5374 \u600e\u4e48\u5168\u90fd\u6d88\u5931\uff0c\u66f4\u662f\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u4e24\u5e08\u5f92\u6b47\u4e86\u4e00\u5bb5\uff0c\u7b2c\u4e8c\u65e5\u8d77\u6765\u5de1\u89c6\uff0c\u5bab\u4e2d\u9664\u4e86\u5012\u584c\u4e86\u51e0\u5ea7\u5bab\u6bbf\u4e4b\u5916\uff0c\u201c\u707e\u60c5\u201d\u5c1a\u4e0d\u7b97\u4e25 \u91cd\uff0c\u79bd\u9e1f\u4e5f\u6e10\u6e10\u6709\u4e9b\u56de\u6765\uff0c\u53ea\u662f\u6ca1\u6709\u4eba\u3002\u5bab\u4e2d\u8d2e\u85cf\u7684\u7cae\u98df\u751a\u4e30\uff0c\u4e24\u5e08\u5f92\u5012\u4e0d\u6015\u6328\u997f\u3002\u9648\u5929\u5b87 \u9053\uff1a\u201c\u54b1\u4eec\u8be5\u600e\u4e48\u529e\uff1f\u201d\u94c1\u62d0\u4ed9\u82e6\u7b11\u9053\uff1a\u201c\u4f9d\u7167\u51b0\u5ddd\u5929\u5973\u7684\u547d\u4ee4\uff0c\u54b1\u4eec\u672c\u8be5\u4eca\u65e5\u4e0b\u5c71\uff0c\u53ef\u662f \u4ee5\u6211\u73b0\u5728\u7684\u529f\u592b\uff0c\u975e\u518d\u7ec3\u5341\u5e74\uff0c\u662f\u96be\u4ee5\u4e0b\u5c71\u7684\u4e86\u3002\u201d\u9648\u5929\u5b87\u60f3\u8d77\u90a3\u51b0\u5ddd\u7684\u5947\u9669\uff0c\u82e5\u975e\u6709\u4e0a\u4e58 \u7684\u529f\u592b\uff0c\u6216\u8005\u719f\u77e5\u51b0\u5ddd\u7684\u6c34\u6027\uff0c\u786e\u662f\u4e0d\u80fd\u98de\u6e21\u3002\u53ea\u542c\u5f97\u94c1\u62d0\u4ed9\u53c8\u82e6\u7b11\u9053\uff1a\u201c\u9047\u6b64\u610f\u6599\u4e0d\u5230\u7684 \u5de8\u53d8\uff0c\u54b1\u4eec\u53ea\u597d\u8fdd\u80cc\u51b0\u5ddd\u5929\u5973\u7684\u547d\u4ee4\uff0c\u5728\u8fd9\u91cc\u4f4f\u4e0b\u53bb\u4e86\u3002\u4f46\u613f\u51b0\u5ddd\u5929\u5973\u80fd\u591f\u751f\u8fd8\uff0c\u6551\u6211\u4eec\u4e0b \u53bb\u3002\u201d \u3000\u3000\u8fd9\u5e0c\u671b\u5f53\u7136\u6781\u662f\u6e3a\u832b\uff0c\u8fc7\u4e86\u4e03\u65e5\uff0c\u4e0d\u8bf4\u51b0\u5ddd\u5929\u5973\uff0c\u5c31\u662f\u51b0\u5bab\u4f8d\u5973\uff0c\u4e5f\u65e0\u4e00\u4eba\u9732\u9762\u3002\u8fd9\u4e03 \u65e5\u5f53\u4e2d\uff0c\u94c1\u62d0\u4ed9\u65e5\u65e5\u7ec3\u529f\uff0c\u8981\u628a\u4f53\u5185\u4f59\u5bd2\u4e4b\u6c14\u6d88\u5c3d\uff0c\u9648\u5929\u5b87\u5bc2\u5bde\u4e4b\u6781\uff0c\u5230\u5904\u884c\u8d70\uff0c\u8fd9\u4e00\u65e5\uff0c \u6765\u5230\u4e86\u90a3\u5ea7\u795e\u79d8\u7684\u6bbf\u5b87\u4e4b\u524d\uff0c\u8fd9\u5ea7\u6bbf\u5b87\uff0c\u5899\u58c1\u90fd\u7ed9\u9707\u5f97\u6b6a\u6b6a\u659c\u659c\uff0c\u5374\u5c1a\u672a\u5012\u584c\uff0c\u9648\u5929\u5b87\u60f3\u8d77 \u90a3\u665a\u4e4b\u4e8b\uff0c\u5bf9\u8fd9\u5c4b\u5b50\u867d\u7136\u6781\u65e0\u597d\u611f\uff0c\u5374\u5fcd\u4e0d\u4f4f\u63a8\u95e8\u8fdb\u53bb\u3002 \u3000\u3000\u6bbf\u5b87\u4e2d\u6240\u4f9b\u7684\u90a3\u5ea7\u80e1\u5973\u5851\u50cf\uff0c\u4ecd\u7136\u5b8c\u597d\u65e0\u7f3a\uff0c\u6b6a\u6b6a\u659c\u659c\u7684\u5899\u58c1\u4e0a\u523b\u6ee1\u5404\u79cd\u4eba\u50cf\u56fe\u5f62\uff0c\u6709 \u5750\u50cf\u6709\u5367\u50cf\uff0c\u8fd8\u6709\u4f5c\u6301\u5251\u76f8\u6251\u4e4b\u72b6\u7684\u5404\u79cd\u5404\u5f0f\u5f62\u50cf\uff0c\u59ff\u52bf\u53e4\u602a\u4e4b\u6781\uff0c\u5251\u6cd5\u5927\u6b8a\u4e2d\u571f\uff0c\u9648\u5929\u5b87 \u5fc3\u9053\uff1a\u201c\u8fd9\u5fc5\u662f\u51b0\u5ddd\u5929\u5973\u7236\u6bcd\u6240\u5408\u521b\u7684\u65b0\u5947\u5251\u6cd5\uff0c\u602a\u4e0d\u5f97\u5979\u4e0d\u80af\u8ba9\u65c1\u4eba\u8fdb\u53bb\u3002\u201d\u53c8\u60f3\u9053\uff1a \u201c\u51b0\u5ddd\u5929\u5973\u5e38\u5230\u8fd9\u91cc\u793c\u62dc\uff0c\u8fd9\u4e2a\u5851\u50cf\u5b9a\u662f\u5979\u7684\u6bcd\u4eb2\u65e0\u7591\u3002\u201d\u5bf9\u51b0\u5ddd\u5929\u5973\u7684\u8eab\u4e16\uff0c\u66f4\u611f\u79bb\u5947\u83ab \u6d4b\u3002\u9648\u5929\u5b87\u4e0d\u613f\u5077\u5b66\u4eba\u5bb6\u7684\u5251\u6cd5\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u9000\u51fa\u53bb\u627e\u5e08\u7236\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u7ecf\u8fc7\u4e86\u4e03\u65e5\u7684\u9759\u517b\uff0c\u7384\u529f\u5185\u8fd0\uff0c\u5df2\u628a\u4f53\u5185\u4f59\u5bd2\u4e4b\u6c14\u53bb\u5c3d\uff0c\u867d\u7136\u529f\u529b\u51cf\u635f\uff0c\u884c\u52a8\u5df2 \u5982\u5e38\u4eba\uff0c\u4e0d\u5fc5\u518d\u501a\u9760\u94c1\u62d0\u4e86\u3002\u9648\u5929\u5b87\u627e\u5230\u5e08\u7236\uff0c\u8bf4\u51fa\u5bc6\u5ba4\u6240\u89c1\uff0c\u94c1\u62d0\u4ed9\u6c89\u541f\u534a\u664c\uff0c\u5ffd\u9053\uff1a \u201c\u5b87\u513f\uff0c\u4f60\u8be5\u591a\u62dc\u4e00\u4f4d\u5e08\u7236\u3002\u201d\u9648\u5929\u5b87\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u4e0d\u8981\u6211\u4e86\u4e48\uff1f\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u4e0d\uff0c \u4f60\u542c\u6211\u8bf4\uff0c\u6b66\u5b66\u65e0\u6709\u6b62\u5883\uff0c\u4f60\u7eb5\u7ec3\u5230\u6211\u4eca\u65e5\u7684\u5883\u754c\uff0c\u4e5f\u5c1a\u96be\u4ee5\u62b5\u654c\u4e00\u6d41\u9ad8\u624b\u3002\u4e0d\u8981\u8bf4\u50cf\u51b0\u5ddd \u5929\u5973\u6216\u8005\u767d\u8863\u5c11\u5e74\u90a3\u6837\u7684\u8d85\u4eba\u6b66\u529f\uff0c\u5c31\u7b97\u65e5\u524d\u65e5\u591c\u95ef\u51b0\u5bab\u7684\u7ea2\u8863\u5587\u561b\uff0c\u6b66\u529f\u4e5f\u8fdc\u5728\u6211\u8f88\u4e4b \u4e0a\u3002\u201d\u9648\u5929\u5b87\u76ee\u7779\u79cd\u79cd\uff0c\u77e5\u9053\u5e08\u7236\u6240\u8bf4\u7684\u7edd\u4e0d\u662f\u5ba2\u6c14\u8bdd\uff0c\u4e0d\u7981\u9ed8\u7136\u3002\u94c1\u62d0\u4ed9\u7eed\u9053\uff1a\u201c\u6211\u529f\u529b \u672a\u590d\uff0c\u975e\u8fc7\u5341\u5e74\uff0c\u96be\u4ee5\u4e0b\u5c71\u3002\u5728\u8fd9\u5341\u5e74\u4e4b\u4e2d\uff0c\u82e5\u6709\u5f3a\u654c\u524d\u6765\u4fb5\u6270\uff0c\u5982\u4f55\u62b5\u5fa1\uff0c\u6240\u4ee5\u6211\u8981\u4f60\u591a \u5b66\u4e00\u4e9b\u4e0a\u4e58\u529f\u592b\uff0c\u518d\u62dc\u4e00\u4f4d\u5e08\u7236\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5728\u8fd9\u51b0\u5bab\u4e4b\u4e2d\uff0c\u53ea\u6709\u54b1\u4eec\u4e8c\u4eba\uff0c\u8fd8\u62dc\u4f55\u4eba \u4e3a\u5e08\uff1f\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\uff01\u201d\u9648\u5929\u5b87\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u5373\u660e\u767d\u5e08\u7236\u7528\u610f\uff0c\u6447\u5934\u8bf4\u9053\uff1a\u201c\u51b0 \u5ddd\u5929\u5973\u5b58\u4ea1\u672a\u535c\uff0c\u54b1\u4eec\u600e\u597d\u5077\u5b66\u5979\u7684\u5251\u6cd5\uff1f\u201d\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u5979\u5b58\u4ea1\u672a\u535c\uff0c\u4f60\u624d\u8be5\u5b66\u3002 \u8bd5\u60f3\u5979\u82e5\u6b7b\u4e86\uff0c\u51b0\u5bab\u5f85\u5973\u4e5f\u90fd\u6b7b\u4e86\uff0c\u5979\u8fd9\u4e00\u6d3e\u6b66\u529f\u5c82\u975e\u5931\u4f20\u3002\u60f3\u51b0\u5ddd\u5929\u5973\u7684\u7236\u6bcd\uff0c\u5408\u521b\u8fd9\u5957 \u65b0\u5947\u7684\u5251\u6cd5\uff0c\u8017\u4e86\u591a\u5c11\u5fc3\u529b\uff0c\u82e5\u7136\u7edd\u4f20\uff0c\u4ed6\u4eec\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u4e5f\u4e0d\u7791\u76ee\uff0c\u800c\u4e14\u4e5f\u662f\u6b66\u5b66\u7684\u4e00\u5927\u635f \u5931\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u88ab\u4ed6\u5e08\u7236\u8bf4\u670d\uff0c\u4e8e\u662f\u4e0e\u5e08\u7236\u540c\u5230\u5bc6\u5ba4\u4e4b\u4e2d\u770b\u90a3\u5899\u4e0a\u7684\u56fe\u5f62\uff0c\u8fd9\u5957\u6b66\u529f\u7e41\u590d\u4e4b\u6781\uff0c \u8be1\u53d8\u5f02\u5e38\u3002\u82e5\u975e\u5185\u529f\u6709\u4e86\u6839\u5e95\u4e4b\u4eba\uff0c\u6b8a\u96be\u5b66\u4e60\u3002\u5e78\u800c\u94c1\u62d0\u4ed9\u662f\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c \u6240\u4e60\u7684\u6b63\u662f\u7384\u95e8\u6b63\u5b97\u7684\u5185\u529f\uff0c\u6b66\u5b66\u7684\u6d41\u6d3e\u867d\u6709\u4e0d\u540c\uff0c\u539f\u7406\u5374\u65e0\u591a\u5927\u5206\u522b\uff0c\u800c\u4e14\u5899\u4e0a\u7684\u56fe\u50cf\uff0c \u4e5f\u5217\u6709\u5165\u95e8\u7684\u529f\u592b\uff0c\u94c1\u62d0\u4ed9\u5728\u5bc6\u5ba4\u4e2d\u770b\u4e86\u4e09\u65e5\uff0c\u5df2\u77e5\u7a8d\u8981\uff0c\u4fbf\u5148\u4f20\u6388\u9648\u5929\u5b57\u5185\u5bb6\u7ec3\u6c14\u7684\u529f \u592b\uff0c\u9648\u5929\u5b87\u66fe\u8ddf\u8427\u9752\u5cf0\u5b66\u4e86\u4e03\u516b\u5e74\uff0c\u5185\u529f\u5df2\u6709\u5e95\u5b50\uff0c\u518d\u7ecf\u94c1\u62d0\u4ed9\u6307\u70b9\uff0c\u8fdb\u5883\u751a\u662f\u795e\u901f\uff0c\u4e00\u6708 \u4e4b\u540e\uff0c\u5b66\u4e0a\u4e58\u5251\u6cd5\u7684\u521d\u6b65\u6839\u57fa\u5df2\u7ecf\u6253\u597d\uff0c\u4fbf\u540c\u65f6\u517c\u5b66\u4e24\u6d3e\u7684\u6b66\u529f\uff0c\u4e0a\u534a\u65e5\u5b66\u94c1\u62d0\u4ed9\u8fd9\u4e00\u6d3e\u7684 \u6b66\u6280\uff0c\u4e0b\u534a\u65e5\u5b66\u51b0\u5ddd\u5929\u5973\u8fd9\u4e00\u6d3e\u7684\u5251\u6cd5\u3002\u65f6\u65e5\u5306\u5306\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u8fc7\u4e86\u4e09\u6708\u3002 \u3000\u3000\u6709\u4e00\u665a\u94c1\u62d0\u4ed9\u72ec\u81ea\u7ec3\u529f\uff0c\u9648\u5929\u5b87\u5728\u56ed\u4e2d\u6563\u6b65\uff0c\u53ea\u89c1\u6708\u534e\u5982\u7ec3\uff0c\u82b1\u8349\u98d8\u9999\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u4e48\u591a \u65f6\u65e5\uff0c\u56ed\u4e2d\u666f\u8272\uff0c\u6e10\u5df2\u6062\u590d\u65e7\u89c2\uff0c\u8bb8\u591a\u4e0d\u77e5\u540d\u7684\u9e1f\u513f\u4e5f\u56de\u6765\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u5bf9\u6b64\u666f\u8272\uff0c\u5fc3\u4e2d\u6005\u89e6\uff0c\u60f3\u8d77\u4e09\u6708\u4e4b\u524d\uff0c\u829d\u5a1c\u5e26\u4ed6\u5728\u5bab\u4e2d\u6e38\u89c8\u7684\u60c5\u666f\uff0c\u5982\u4eca\u5374\u53ea\u6709 \u81ea\u5df1\u5b64\u4f36\u4f36\u5730\u5728\u8fd9\u513f\u3002\u53c8\u60f3\u8d77\u65f6\u8fc7\u4e09\u6708\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u4e00\u4f17\u4f8d\u5973\u8fd8\u672a\u89c1\u6709\u4e00\u4e2a\u56de\u6765\uff0c\u60f3\u5fc5\u51f6\u591a \u5409\u5c11\uff0c\u4f46\u5bf9\u90a3\u4e48\u591a\u7684\u51b0\u5bab\u4f8d\u5973\u7a81\u7136\u95f4\u4e00\u65e6\u5931\u8e2a\uff0c\u5c38\u4f53\u4ea6\u65e0\u53d1\u73b0\uff0c\u53c8\u89c9\u5f97\u96be\u4ee5\u601d\u8bae\u3002 \u3000\u3000\u9648\u5929\u5b87\u6f2b\u6b65\u6c89\u601d\uff0c\u5ffd\u95fb\u5f97\u6709\u4e00\u80a1\u524d\u6240\u672a\u95fb\u7684\u9999\u5473\u4ece\u56ed\u4e2d\u4e00\u89d2\u98d8\u6765\uff0c\u9648\u5929\u5b87\u5728\u5bab\u4e2d\u4e09\u6708\uff0c \u5bf9\u5404\u79cd\u82b1\u8349\u6811\u6728\u5df2\u7ecf\u719f\u6089\uff0c\u5bab\u4e2d\u7684\u5947\u82b1\u5f02\u8349\uff0c\u6709\u5404\u79cd\u4e0d\u540c\u7684\u6e05\u9999\uff0c\u4f46\u5374\u65e0\u4e00\u79cd\u6709\u8fd9\u6837\u6d53\u4f8b\u7684 \u9999\u6c14\uff0c\u9648\u5929\u5b87\u597d\u5947\u5fc3\u8d77\uff0c\u4e0d\u7981\u8d70\u8fc7\u53bb\u770b\uff0c\u8d70\u5230\u82b1\u56ed\u7684\u4e00\u89d2\uff0c\u53ea\u89c1\u4e00\u68f5\u5927\u6811\uff0c\u633a\u7136\u72ec\u7acb\uff0c\u5947\u602a \u7684\u662f\uff0c\u5927\u6811\u4e0a\u53ea\u7ed3\u6709\u4e00\u4e2a\u679c\u5b50\uff0c\u5176\u5927\u5982\u7897\uff0c\u989c\u8272\u9c9c\u7ea2\uff0c\u90a3\u80a1\u900f\u4eba\u7684\u9999\u5473\u5c31\u662f\u8fd9\u4e2a\u679c\u5b50\u53d1\u6563\u51fa \u6765\u7684\u3002\u9648\u5929\u5b99\u6500\u4e0a\u6811\u53bb\uff0c\u5c06\u679c\u5b50\u6458\u4e86\u4e0b\u6765\uff0c\u95fb\u4e86\u4e00\u95fb\uff0c\u9999\u900f\u813e\u8151\uff0c\u5fcd\u4e0d\u4f4f\u9001\u5230\u53e3\u4e2d\u4e00\u54ac\uff0c\u53ea \u89c9\u53c8\u9999\u53c8\u751c\uff0c\u4e14\u6709\u4e00\u80a1\u6e05\u51c9\u4e4b\u6c14\uff0c\u76f4\u900f\u4e39\u7530\uff0c\u7adf\u662f\u751f\u5e73\u4ece\u672a\u5c1d\u8fc7\u7684\u4f73\u679c\u3002\u9648\u5929\u5b87\u628a\u679c\u5b50\u5403 \u5b8c\uff0c\u6068\u4e0d\u5f97\u518d\u627e\u4e00\u4e2a\uff0c\u53ef\u662f\u5bab\u4e2d\u5c31\u53ea\u6709\u8fd9\u6837\u7684\u4e00\u68f5\u6811\uff0c\u6811\u4e0a\u5c31\u53ea\u6709\u4e00\u679a\u679c\u5b50\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u9648\u5929\u5b87\u5ffd\u89c9\u8179\u4e2d\u7ede\u75db\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u662f\u6bd2\u679c\u4e0d\u6210\uff1f\u201d\u6025\u5fd9\u8dd1\u53bb \u627e\u5e08\u7236\uff0c\u521a\u8dd1\u4e86\u51e0\u6b65\uff0c\u75bc\u75db\u96be\u5f53\uff0c\u53ea\u89c9\u8179\u4e2d\u6d4a\u6c14\u4e0b\u6c89\uff0c\u8feb\u4e0d\u53ca\u5f85\uff0c\u53ea\u597d\u62e3\u4e86\u4e00\u5904\u50fb\u9759\u6240\u5728\uff0c \u5927\u6cfb\u4e86\u4e00\u573a\uff0c\u6cfb\u8fc7\u4e4b\u540e\uff0c\u75bc\u75db\u5ffd\u6b62\u3002\u9648\u5929\u5b87\u751a\u89c9\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u679c\u5b50\u5982\u6b64\u9999\u751c\uff0c\u600e\u4e48\u5374\u662f \u6cfb\u836f\uff1f\u201d \u3000\u3000\u7ad9\u8d77\u6765\u8d70\u4e86\u51e0\u6b65\uff0c\u53c8\u53d1\u73b0\u4e86\u4e00\u4e2a\u5947\u8ff9\uff0c\u53ea\u89c9\u8f7b\u98d8\u98d8\u7684\uff0c\u4f3c\u4e4e\u8eab\u5b50\u4e5f\u8f7b\u4e86\u8bb8\u591a\uff0c\u9648\u5929\u5b87\u8bd5 \u4e00\u8df3\u8dc3\uff0c\u8eab\u8eaf\u62d4\u7a7a\u800c\u8d77\uff0c\u4e00\u4e0b\u5b50\u5c31\u8dc3\u4e0a\u4e86\u4e00\u68f5\u5927\u6811\u7684\u6811\u9876\uff0c\u8fd9\u68f5\u6811\u9ad8\u8fbe\u4e8c\u4e08\u6709\u4f59\uff0c\u9648\u5929\u5b87\u5e73 \u65f6\u7eb5\u8dc3\uff0c\u6700\u9ad8\u4e0d\u8fc7\u4e08\u8bb8\uff0c\u800c\u4eca\u670d\u4e86\u8fd9\u5f02\u679c\u4e4b\u540e\uff0c\u8f7b\u8eab\u529f\u592b\u7adf\u7136\u5e73\u7a7a\u5f3a\u4e86\u4e00\u500d\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8 \u559c\u3002\u8fde\u5fd9\u53bb\u89c1\u5e08\u7236\uff0c\u94c1\u62d0\u4ed9\u542c\u4ed6\u8bf4\u540e\uff0c\u8bd5\u4ed6\u7684\u8f7b\u529f\uff0c\u679c\u7136\u4eca\u975e\u6614\u6bd4\uff0c\u4e5f\u4e0d\u7981\u559c\u9053\uff1a\u201c\u51b0\u5ddd\u5929 \u5973\u8fd9\u5957\u6b66\u529f\uff0c\u80dc\u5728\u8f7b\u7075\u5947\u8be1\uff0c\u6211\u6b63\u6101\u4f60\u7684\u8f7b\u529f\u6839\u5e95\u4e0d\u597d\uff0c\u60f3\u4e0d\u5230\u4f60\u5374\u6709\u6b64\u5947\u9047\uff01\u73b0\u5728\u82e5\u53ea\u8bba \u8f7b\u8eab\u7684\u529f\u592b\uff0c\u4f60\u867d\u7136\u8fd8\u6bd4\u4e0d\u4e0a\u51b0\u5ddd\u5929\u5973\u4e0e\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u4f46\u6bd4\u6211\u5374\u8981\u5f3a\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u4e00\u5bbf\u65e0\u8bdd\uff0c\u7b2c\u4e8c\u65e5\u9648\u5929\u5b87\u518d\u7ec3\u51b0\u5ddd\u5929\u5973\u7684\u90a3\u5957\u5251\u6cd5\uff0c\u53ea\u89c9\u5f97\u5fc3\u5e94\u624b\uff0c\u679c\u7136\u7075\u6d3b\u8bb8\u591a\u3002\u5fc3 \u4e0b\u9ad8\u5174\uff0c\u665a\u9910\u8fc7\u540e\uff0c\u53c8\u72ec\u81ea\u5230\u56ed\u4e2d\u7ec3\u5251\uff0c\u7ec3\u5230\u9163\u5904\uff0c\u53ea\u89c1\u94f6\u5149\u531d\u5730\uff0c\u62db\u6570\u4e0d\u5047\u601d\u7d22\u5730\u4fbf\u81ea\u7136 \u53d1\u4e86\u51fa\u6765\u3002\u5ffd\u95fb\u5f97\u6709\u4eba\u8d5e\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u62ac\u5934\u770b\u65f6\uff0c\u5374\u662f\u5e08\u7236\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u4f60\u7684\u529f\u529b\u5927 \u8fdb\uff0c\u770b\u6765\u6216\u8005\u4e0d\u5fc5\u5341\u5e74\uff0c\u54b1\u4eec\u4fbf\u53ef\u4e0b\u5c71\u4e86\u3002\u53ea\u662f\u4f60\u8f7b\u529f\u867d\u7a81\u7136\u589e\u5f3a\uff0c\u8033\u76ee\u5c1a\u672a\u7ec3\u5f97\u7075\u654f\u3002\u6211 \u5230\u4f60\u7684\u8eab\u8fb9\uff0c\u4f60\u624d\u77e5\u9053\u3002\u201d\u5f53\u4e0b\u53c8\u4f20\u6388\u9648\u5929\u5b87\u542c\u98ce\u8fa8\u5668\u7684\u529f\u592b\uff0c\u7ec3\u4e86\u4e00\u9635\uff0c\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u73b0 \u5728\u8bd5\u4f60\u4e00\u8bd5\uff0c\u4f60\u56de\u8f6c\u5934\u53bb\uff0c\u6211\u5728\u4f60\u7684\u80cc\u540e\u8d70\u6765\uff0c\u4f60\u4e00\u95fb\u58f0\u606f\uff0c\u4fbf\u53cd\u624b\u63b7\u51fa\u4e00\u7c92\u77f3\u5b50\uff0c\u770b\u770b\u4f60 \u63b7\u7684\u65b9\u4f4d\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5bab\u4e2d\u66f2\u5f84\u8fc2\u6cc5\uff0c\u94c1\u62d0\u4ed9\u8d70\u5230\u8fdc\u5904\u85cf\u8eb2\u8d77\u6765\uff0c\u9648\u5929\u5b87\u80cc\u5411\u800c\u7acb\uff0c\u9759\u5019\u5e08\u7236\u524d\u6765\u8bd5\u9a8c\uff0c\u8fc7\u4e86 \u4e00\u9635\uff0c\u5ffd\u95fb\u5f97\u6709\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u4ece\u4fa7\u9762\u4f20\u6765\uff0c\u9648\u5929\u5b87\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u600e\u4e48\u542c\u8d77\u6765\u662f\u4e24 \u4eba\u7684\u8d70\u8def\u58f0\uff0c\u662f\u5e08\u7236\u6545\u5f04\u7384\u865a\uff0c\u8fd8\u662f\u6211\u7684\u542c\u98ce\u4e4b\u672f\u8fd8\u672a\u5230\u5bb6\uff1f\u201d\u58f0\u97f3\u6e10\u8fd1\uff0c\u9648\u5929\u5b57\u4e0d\u5047\u601d \u7d22\uff0c\u53cd\u624b\u4e00\u63b7\uff0c\u5c06\u77f3\u5757\u5411\u58f0\u97f3\u6765\u5904\u63b7\u53bb\uff0c\u5ffd\u95fb\u5f97\u54c8\u54c8\u602a\u7b11\u4e4b\u58f0\uff0c\u90a3\u5757\u77f3\u5934\u5df2\u7ed9\u53cd\u63b7\u56de\u6765\uff0c\u542c \u90a3\u7834\u7a7a\u4e4b\u58f0\uff0c\u6025\u9510\u4e4b\u6781\uff0c\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u89e3\u5e08\u7236\u4f55\u4ee5\u7528\u5982\u6b64\u5389\u5bb3\u7684\u624b\u6cd5\u53cd\u63b7\u56de\u6765\uff1f\u5c31\u5728 \u8fd9\u4e00\u77ac\u95f4\uff0c\u542c\u5f97\u94c1\u62d0\u4ed9\u5927\u559d\u9053\uff1a\u201c\u51f6\u50e7\u4f11\u5f97\u4f24\u6211\u5f92\u5f1f\uff01\u201d\u7d27\u63a5\u7740\u6697\u5668\u4e4b\u58f0\u5212\u7a7a\u800c\u8fc7\uff0c\u542c\u5f97\u51fa \u662f\u4e0e\u90a3\u77f3\u5757\u76f8\u649e\uff0c\u4e00\u540c\u8dcc\u843d\u51b0\u6e56\u53bb\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u56de\u5934\u4e00\u671b\uff0c\u4e0d\u7981\u5413\u5f97\u5446\u4e86\uff0c\u4ece\u4fa7\u9762\u6765\u7684\u7adf\u7136\u4e0d\u662f\u5e08\u7236\uff0c\u800c\u662f\u4ee5\u524d\u66fe\u5230\u8fc7\u51b0\u5bab\u7684\u90a3 \u4e2a\u7ea2\u8863\u756a\u50e7\uff0c\u5728\u756a\u50e7\u540e\u9762\uff0c\u8fd8\u6709\u4e00\u4e2a\u5c11\u5e74\u6b66\u58eb\u3002\u8fd9\u4e24\u4eba\u6b63\u5728\u6bd7\u7259\u88c2\u5634\u5730\u5411\u81ea\u5df1\u602a\u7b11\u3002\u5e08\u7236\u6b63 \u4ece\u540e\u9762\u5306\u5306\u8d76\u6765\uff0c\u8138\u4e0a\u4e00\u6d3e\u60ca\u9a87\u7684\u795e\u8272\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u51b7\u51b7\u4e00\u7b11\uff0c\u671d\u7740\u94c1\u62d0\u4ed9\u53fd\u54e9\u5495\u565c\u4ed6\u8bb2\u4e86\u4e00\u987f\u8bdd\uff0c\u94c1\u62d0\u4ed9\u4e00\u53e5\u4e5f\u542c\u4e0d\u61c2\uff0c\u6447\u4e86 \u6447\u5934\u3002\u9648\u5929\u5b87\u7565\u89e3\u5c3c\u6cca\u5c14\u8bdd\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4ed6\u662f\u6765\u67e5\u95ee\u51b0\u5ddd\u5929\u5973\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u7528\u5c3c\u6cca\u5c14\u8bdd\u53eb\u51fa\u201c\u51b0\u5ddd\u5929\u5973\u201d\u56db\u5b57\uff0c\u94c1\u62d0\u4ed9\u5c06\u62d0\u6756\u5411\u539f\u6765\u7684\u51b0\u5cf0\u65b9\u5411\u4e00\u6307\uff0c\u505a\u4e86 \u4e00\u4e2a\u624b\u52bf\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u5927\u5730\u9707\u4e4b\u540e\uff0c\u51b0\u5cf0\u5012\u584c\uff0c\u51b0\u5ddd\u5929\u5973\u5927\u6982\u662f\u538b\u6b7b\u4e86\uff0c\u7ea2\u8863\u756a\u50e7\u9762\u8272\u6e29 \u6012\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u53c8\u5411\u94c1\u62d0\u4ed9\u6307\u4e86\u4e00\u6307\uff0c\u5728\u756a\u50e7\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u8d8a\u53d1\u607c\u6012\uff0c\u7a81\u7136 \u7528\u85cf\u8bed\u8bf4\u51fa\u201c\u91d1\u672c\u5df4\u74f6\u201d\u51e0\u5b57\uff0c\u505a\u4e86\u4e00\u4e2a\u62a2\u593a\u7684\u59ff\u52bf\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u201c\u5c31\u662f\u4f60\u60f3\u62a2\u91d1\u672c\u5df4\u74f6 \u5417\uff1f\u201d\u8fd9\u53e5\u85cf\u8bed\u548c\u8fd9\u4e2a\u624b\u52bf\u94c1\u62d0\u4ed9\u5012\u80fd\u9886\u609f\uff0c\u4ed6\u662f\u4e00\u4ee3\u5927\u4fa0\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u867d\u77e5\u5371\u9669\uff0c\u5374\u4e5f\u4e0d \u80af\u4e71\u6253\u8c0e\u8bed\uff0c\u4e00\u6307\u5fc3\u53e3\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u60f3\u593a\u91d1\u672c\u5df4\u74f6\uff01\u201d \u3000\u3000\u7ea2\u8863\u756a\u50e7\u4e00\u58f0\u6012\u543c\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u7985\u6756\u5c31\u5411\u94c1\u62d0\u4ed9\u5f53\u5934\u626b\u4e0b\uff0c\u539f\u6765\u4ed6\u8bef\u89e3\u4e86\u94c1\u62d0\u4ed9\u7684\u624b \u52bf\uff0c\u4ee5\u4e3a\u51b0\u5ddd\u5929\u5973\u5df2\u7ed9\u4ed6\u4eec\u5f04\u6b7b\uff0c\u53c8\u542c\u5f97\u90a3\u5c11\u5e74\u6b66\u58eb\u6307\u8bc1\u94c1\u62d0\u4ed9\u662f\u60f3\u62a2\u593a\u91d1\u672c\u5df4\u74f6\u4e4b\u4eba\uff0c\u4e24 \u6068\u9f50\u53d1\uff0c\u6240\u4ee5\u4e0d\u5206\u7682\u767d\uff0c\u4fbf\u548c\u94c1\u62d0\u4ed9\u53ae\u62fc\u3002\u94c1\u62d0\u4ed9\u4ee5\u524d\u66fe\u5403\u8fc7\u4ed6\u7684\u4e8f\uff0c\u8fd9\u65f6\u89c1\u4ed6\u5982\u6b64\u6a2a\u86ee\uff0c \u4e5f\u662f\u607c\u6012\uff0c\u94c1\u62d0\u4e00\u4e3e\uff0c\u8fd8\u52b2\u62db\u67b6\uff0c\u53ea\u89c1\u53cc\u6756\u76f8\u4ea4\uff0c\u632b\u7136\u6709\u58f0\uff0c\u94c1\u62d0\u4ed9\u8ddf\u8e09\u8dc4\u8dc4\u7684\u5012\u9000\u51e0\u6b65\u3002 \u3000\u3000\u9648\u5929\u5b87\u8fd9\u4e00\u60ca\u975e\u5c0f\uff0c\u5fc3\u9053\uff1a\u201c\u5e08\u7236\u529f\u529b\u672a\u590d\uff0c\u5982\u4f55\u80fd\u662f\u4ed6\u7684\u5bf9\u624b\uff1f\u201d\u53ea\u542c\u5f97\u5728\u5175\u5668\u4ea4\u51fb \u58f0\u4e2d\uff0c\u94c1\u62d0\u4ed9\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5b87\u513f\uff0c\u4f60\u5feb\u9003\u8d70\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u8ddf\u4ed6\u4eec\u52a8\u624b\uff0c\u82e5\u7136\u4f60\u4e0d\u542c\u8bdd\uff0c\u6211\u5c31 \u518d\u4e0d\u8ba4\u4f60\u4e3a\u5f92\u3002\u201d\u9648\u5929\u5b87\u77e5\u9053\u8fd9\u662f\u5e08\u7236\u8981\u4fdd\u5168\u4ed6\u7684\u597d\u610f\uff0c\u53ef\u662f\u5728\u6b64\u7d27\u8981\u5173\u5934\uff0c\u4ed6\u600e\u5fcd\u5f03\u5e08\u79c1 \u9003\uff0c\u5446\u4e86\u4e00\u9635\uff0c\u94c1\u62d0\u4ed9\u4e0e\u90a3\u7ea2\u8863\u756a\u50e7\u5df2\u7ecf\u6597\u4e86\u5341\u4f59\u4e8c\u5341\u62db\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u501a\u5728\u6811\u65c1\uff0c\u7528\u773c\u89d2\u626b\u4e86\u9648\u5929\u5b87\u4e00\u773c\uff0c\u5374\u4e0d\u52a8\u624b\u3002\u539f\u6765\u4ed6\u521a\u624d\u89c1\u8fc7\u9648\u5929\u5b87\u63b7\u77f3\u88ab\u756a \u50e7\u53cd\u51fb\u56de\u6765\uff0c\u77e5\u4ed6\u529f\u529b\u751a\u6d45\uff0c\u6240\u4ee5\u4e0d\u653e\u5728\u773c\u5185\uff0c\u53ea\u662f\u6ce8\u89c6\u7740\u573a\u4e2d\u7684\u6076\u6597\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u4e0e\u7ea2\u8863\u756a\u50e7\u970e\u773c\u4e4b\u95f4\u5df2\u6597\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u867d\u662f\u8fde\u8fde\u540e\u9000\uff0c\u8eab\u6cd5\u6b65\u6cd5\u5374\u5e76\u4e0d\u4e71\uff0c\u770b \u6765\u8fd8\u80fd\u62db\u67b6\u3002\u9648\u5929\u5b87\u597d\u751f\u60ca\u5f02\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u53c8\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u5e08\u7236\u8e0f\u7740\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c \u9762\u8272\u6c89\u91cd\u4e4b\u6781\uff0c\u5c06\u94c1\u62d0\u821e\u5f97\u547c\u547c\u631f\u98ce\uff0c\u9707\u5f97\u8033\u9f13\u90fd\u55e1\u55e1\u4f5c\u54cd\uff0c\u5e08\u7236\u4f7f\u7684\u6b63\u662f\u6700\u635f\u8017\u5185\u5bb6\u771f\u529b \u7684\u4f0f\u9b54\u6756\u6cd5\u3002\u9648\u5929\u5b87\u8bb0\u5f97\u51b0\u5ddd\u5929\u5973\u8bf4\u8fc7\uff0c\u4e0a\u6b21\u5e08\u7236\u4e0e\u8fd9\u756a\u50e7\u4f5c\u6218\uff0c\u4f0f\u9b54\u6756\u6cd5\u5e78\u559c\u53ea\u4f7f\u5230\u7b2c\u4e5d \u5341\u516d\u62db\uff0c\u82e5\u7136\u628a\u5168\u90e8\u4e00\u767e\u96f6\u516b\u8def\u6756\u6cd5\u4f7f\u5b8c\uff0c\u5fc5\u5f97\u5927\u75c5\u4e00\u573a\u3002\u9648\u5929\u5b87\u5fc3\u60f3\uff1a\u201c\u5e08\u7236\u73b0\u5728\u7684\u529f\u529b \u5df2\u5927\u4e0d\u5982\u524d\uff0c\u7adf\u7136\u8fd8\u4f7f\u8fd9\u8def\u6756\u6cd5\uff0c\u5c82\u4e0d\u662f\u5371\u9669\u4e4b\u6781\uff1f\u8981\u60f3\u4e0a\u53bb\u76f8\u52a9\uff0c\u53ea\u89c1\u5e08\u7236\u5706\u7741\u53cc\u773c\uff0c\u53c8 \u5411\u81ea\u5df1\u77aa\u4e86\u4e00\u773c\uff0c\u94c1\u62d0\u4e00\u6325\uff0c\u731b\u542c\u5f97\u8f70\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u94c1\u62d0\u4ed9\u4e0e\u7ea2\u8863\u756a\u618e\u90fd\u5404\u81ea\u659c\u7a9c\u4e09\u6b65\u3002\u4e24 \u4eba\u4e00\u9000\u590d\u8fdb\uff0c\u53cc\u6756\u76d8\u65cb\u98de\u821e\uff0c\u53c8\u518d\u4ea4\u950b\u3002\u9648\u5929\u5b87\u61c2\u5f97\u5e08\u7236\u7684\u773c\u8272\u662f\u8d23\u4ed6\u4e0d\u542c\u8bdd\uff0c\u53eb\u4ed6\u5feb\u8d70\uff0c \u9648\u5929\u5b87\u4e00\u9635\u8fdf\u7591\uff0c\u573a\u4e2d\u6597\u5f97\u8d8a\u53d1\u51f6\u9669\u6fc0\u70c8\u4e86\u3002 \u3000\u3000\u539f\u6765\u94c1\u62d0\u4ed9\u81ea\u77e5\u4e0d\u654c\uff0c\u62fc\u4e86\u6027\u547d\uff0c\u4f7f\u51fa\u5e08\u95e8\u6240\u6388\u6700\u5389\u5bb3\u7684\u4f0f\u9b54\u6756\u6cd5\uff0c\u7528\u610f\u662f\u60f3\u62d6\u5ef6\u65f6 \u5019\uff0c\u63a9\u62a4\u9648\u5929\u5b87\u9003\u4ea1\uff0c\u53ef\u662f\u9648\u5929\u5b87\u7ffb\u7231\u5e08\u5fc3\u5207\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u8d70\uff0c\u94c1\u62d0\u4ed9\u5fc3\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u65e2\u6df1 \u613f\u5f92\u513f\u5929\u6027\u7eaf\u539a\uff0c\u53c8\u607c\u6012\u4ed6\u4e0d\u542c\u8bdd\u3002\u5728\u8fd9\u6027\u547d\u76f8\u6251\u7684\u5173\u5934\uff0c\u53ef\u601c\u94c1\u62d0\u4ed9\u5df2\u4e0d\u80fd\u5206\u795e\u8bf4\u8bdd\u3002 \u3000\u3000\u4f0f\u9b54\u6756\u6cd5\u5206\u4e3a\u4e09\u6bb5\uff0c\u7b2c\u4e00\u6bb5\u4e09\u5341\u516d\u62db\u970e\u5ffd\u4f7f\u5b8c\uff0c\u7b2c\u4e8c\u6bb5\u7684\u4e09\u5341\u516d\u62db\u53c8\u76f8\u7ee7\u800c\u81f3\uff0c\u8fd9\u4e09\u5341 \u516d\u62db\u7528\u7684\u5168\u662f\u5185\u5bb6\u771f\u529b\uff0c\u66f4\u8017\u7cbe\u795e\uff0c\u94c1\u62d0\u4ed9\u54ac\u7740\u7259\u6839\uff0c\u6697\u8fd0\u771f\u6c14\uff0c\u82e6\u82e6\u652f\u6491\u3002\u4e00\u4e2a\u4eba\u62b1\u4e86\u5fc5 \u6b7b\u62fc\u547d\u4e4b\u5fc3\uff0c\u529b\u91cf\u65e0\u5f62\u4e2d\u52a0\u5f3a\u51e0\u500d\uff0c\u662f\u4ee5\u4ed6\u529f\u529b\u867d\u7136\u5927\u4e0d\u5982\u524d\uff0c\u5374\u4e5f\u8fd8\u52c9\u5f3a\u652f\u6491\u5f97\u4f4f\u3002 \u3000\u3000\u9661\u542c\u5f97\u7ea2\u8863\u756a\u50e7\u4e00\u58f0\u602a\u7b11\uff0c\u7985\u6756\u4e00\u6307\uff0c\u5c06\u94c1\u62d0\u9876\u7740\uff0c\u76f4\u903c\u8fc7\u53bb\uff0c\u94c1\u62d0\u4ed9\u8863\u88f3\u5c3d\u6e7f\uff0c\u6c57\u5982 \u96e8\u4e0b\uff0c\u731b\u5730\u4e5f\u5927\u559d\u4e00\u58f0\uff0c\u94c1\u62d0\u4e00\u633a\uff0c\u53c8\u5c06\u7ea2\u8863\u756a\u50e7\u7684\u7985\u6756\u8361\u5f00\uff0c\u4f46\u90a3\u7897\u53e3\u822c\u7c97\u5927\u7684\u94c1\u62d0\uff0c\u5df2 \u663e\u5f97\u5fae\u5fae\u53d8\u66f2\uff0c\u9648\u5929\u5b87\u89c1\u4e86\uff0c\u66f4\u662f\u5fc3\u60ca\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u7684\u7b2c\u4e8c\u6bb5\u4f0f\u9b54\u6756\u6cd5\u53c8\u5df2\u4f7f\u5b8c\uff0c\u62d0\u6756\u6162\u6162\u6325\u52a8\uff0c\u5c31\u5982\u633d\u7740\u5343\u65a4\u91cd\u7269\u4e00\u6837\uff0c\u4e1c\u4e00\u6307\uff0c \u897f\u4e00\u5212\uff0c\u5168\u65e0\u58f0\u606f\uff0c\u7ea2\u8863\u756a\u50e7\u8f7b\u72c2\u4e4b\u6001\u5c3d\u655b\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u4e0d\u6562\u8f7b\u89c6\u3002\u4f46\u542c\u5f97\u94c1\u62d0\u4ed9\u8eab\u5b50\u4e00 \u52a8\uff0c\u9aa8\u5934\u5c31\u683c\u683c\u4f5c\u54cd\uff0c\u5934\u4e0a\u7ea2\u7b4b\u6bd5\u73b0\uff0c\u4f3c\u662f\u5728\u82e6\u82e6\u652f\u6491\u3002\u90a3\u756a\u50e7\u5ffd\u5730\u91cd\u65bd\u6545\u6280\uff0c\u4f7f\u51fa\u745c\u4f3d\u5750 \u529f\uff0c\u76d8\u819d\u4e00\u5750\uff0c\u7985\u6756\u4e00\u5e26\uff0c\u5c06\u94c1\u62d0\u4ed9\u6162\u6162\u62c9\u8fd1\u8eab\u524d\u3002 \u3000\u3000\u94c1\u62d0\u4ed9\u5fc3\u4e2d\u4e00\u51c9\uff0c\u4ed6\u5df2\u7aed\u5c3d\u5168\u529b\uff0c\u7ec8\u56e0\u529f\u529b\u4e0d\u654c\uff0c\u65e0\u53ef\u62b5\u6321\uff0c\u4ed6\u5fc3\u77e5\u82e5\u88ab\u756a\u50e7\u62c9\u8fd1\u8eab \u8fb9\uff0c\u5fc5\u7acb\u4e0b\u6740\u624b\uff0c\u6b32\u60f3\u6446\u8131\uff0c\u94c1\u62d0\u5374\u88ab\u7985\u6756\u7c98\u7740\uff0c\u7262\u7262\u5730\u5f80\u91cc\u7275\u5f15\uff0c\u6446\u8131\u4e0d\u5f00\uff0c\u8fd9\u65f6\u4ed6\u7684\u4e00 \u767e\u96f6\u516b\u8def\u4f0f\u9b54\u6756\u6cd5\uff0c\u5df2\u4f7f\u5230\u7b2c\u4e00\u767e\u96f6\u516d\u62db\u4e86\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u5168\u795e\u6ce8\u89c6\u6756\u7aef\uff0c\u7528\u529b\u4e00\u5e26\uff0c\u5927\u58f0\u4e00\u559d\uff1a\u201c\u5012\uff01\u201d\u9648\u5929\u5b87\u53ea\u89c1\u5e08\u7236\u8eab\u8eaf\u6643\u4e86\u51e0 \u6643\uff0c\u4f3c\u662f\u4e0d\u7531\u81ea\u4e3b\u7684\u7ed9\u90a3\u756a\u50e7\u62c9\u8fd1\u8eab\u8fb9\uff0c\u5934\u5411\u524d\u51b2\uff0c\u770b\u770b\u5c31\u8981\u5012\u4e0b\u5730\u53bb\u3002\u9648\u5929\u5b87\u5927\u5403\u4e00\u60ca\uff0c \u9661\u7136\u98de\u8eab\u4e00\u63a0\uff0c\u6dae\u7684\u4e00\u5251\uff0c\u5c31\u5411\u90a3\u756a\u50e7\u808b\u4e0b\u7684\u201c\u9f99\u85cf\u7a74\u201d\u731b\u523a\u3002\u9648\u5929\u5b87\u81ea\u77e5\u6b66\u529f\u4e0e\u90a3\u756a\u50e7\u5dee \u5f97\u592a\u8fdc\uff0c\u8fd9\u4e00\u5251\u53ea\u662f\u8feb\u4e8e\u6551\u5e08\uff0c\u804a\u5c3d\u4eba\u4e8b\u800c\u5df2\uff0c\u539f\u4e0d\u6307\u671b\u80fd\u591f\u523a\u4e2d\uff0c\u54ea\u77e5\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5ffd \u542c\u5f97\u90a3\u756a\u50e7\u5927\u53eb\u4e00\u58f0\uff0c\u8dcc\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\uff01 \u3000\u3000\u539f\u6765\u7ea2\u8863\u756a\u50e7\u4e0e\u9648\u5929\u5b57\u76f8\u8ddd\u6570\u4e08\uff0c\u4ed6\u53c8\u77e5\u9053\u9648\u5929\u5b87\u6b66\u529f\u4f4e\u5fae\uff0c\u7edd\u4e0d\u628a\u4ed6\u653e\u5728\u773c\u5185\uff0c\u800c\u4e14 \u53c8\u6709\u90a3\u5c11\u5e74\u6b66\u58eb\u5728\u65c1\u76d1\u89c6\uff0c\u66f4\u662f\u5bf9\u4ed6\u6beb\u65e0\u9632\u5907\u3002\u54ea\u77e5\u9648\u5929\u5b87\u529f\u529b\u867d\u5f31\uff0c\u5403\u4e86\u5f02\u679c\u4e4b\u540e\uff0c\u8f7b\u8eab \u7684\u529f\u592b\uff0c\u5df2\u53ca\u5f97\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u8fd9\u6570\u4e08\u4e4b\u5730\uff0c\u4e00\u63a0\u5373\u5230\uff0c\u800c\u4e14\u53c8\u662f\u7a81\u5982\u5176\u6765\uff0c\u9aa4\u7136\u4e00 \u51fb\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u51fa\u624b\u5df2\u6765\u4e0d\u53ca\uff0c\u7ea2\u8863\u756a\u50e7\u5168\u795e\u8d2f\u6ce8\uff0c\u8981\u628a\u94c1\u62d0\u4ed9\u51fb\u5012\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u808b\u4e0b\u7684\u7a74 \u9053\u7adf\u7ed9\u9648\u5929\u5b87\u4e00\u5251\u523a\u4e2a\u6b63\u7740\uff01\u672c\u6765\u4ee5\u7ea2\u8863\u756a\u50e7\u7684\u6df1\u6e5b\u5185\u529f\uff0c\u8fd9\u4e00\u5251\u5c1a\u4e0d\u8db3\u4ee4\u4ed6\u91cd\u4f24\uff0c\u4f46\u94c1\u62d0 \u4ed9\u7684\u4f0f\u9b54\u6756\u6cd5\uff0c\u4e00\u62db\u5f3a\u903e\u4e00\u62db\uff0c\u8fd9\u65f6\u6b63\u4f7f\u5230\u7b2c\u4e00\u767e\u96f6\u4e03\u62db\uff0c\u62fc\u5c3d\u5168\u8eab\u7684\u6c14\u529b\uff0c\u8fd0\u52b2\u4e00\u6233\uff0c\u7ea2 \u8863\u756a\u50e7\u7ed9\u9648\u5929\u5b87\u523a\u7740\uff0c\u8eab\u8eaf\u98a4\u4e86\u4e00\u98a4\uff0c\u53c8\u88ab\u94c1\u62d0\u4ed9\u4e58\u865a\u800c\u5165\uff0c\u5728\u4ed6\u5a1c\u819b\u91cd\u91cd\u7684\u6233\u5382\u4e00\u4e0b\uff0c\u8fd9 \u4e00\u6765\u4e24\u4e0b\u5939\u653b\uff0c\u90a3\u756a\u50e7\u7eb5\u662f\u94c1\u94f8\u7684\u8eab\u5b50\uff0c\u4e5f\u62b5\u53d7\u4e0d\u4e86\uff0c\u8fd8\u7b97\u4ed6\u6b66\u529f\u786e\u662f\u9ad8\u5f3a\uff0c\u6ca1\u6709\u5f53\u573a\u9001 \u547d\u3002\u4f46\u4ea6\u5df2\u5455\u4e00\u6ee9\u9c9c\u8840\uff0c\u6563\u4e86\u5185\u5bb6\u771f\u6c14\uff0c\u975e\u518d\u4fee\u7ec3\u4e09\u5e74\u4e94\u8f7d\uff0c\u4e0d\u80fd\u6062\u590d\u539f\u6765\u7684\u529f\u529b\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e00\u62db\u5f97\u624b\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u6b63\u60f3\u6276\u8d77\u5e08\u7236\uff0c\u5ffd\u542c\u5f97\u94c1\u62d0\u4ed9\u53c8\u662f\u4e00\u58f0\u5927\u53eb\u9053\u201c\u95ea\u5f00\uff01\u201d \u9648\u5929\u5b87\u672c\u80fd\u7684\u5411\u65c1\u8fb9\u4e00\u95ea\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u6b63\u5411\u81ea\u5df1\u98de\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u94c1\u62d0\u4ed9\u731b\u7684\u8131 \u624b\u4e00\u63b7\uff0c\u94c1\u62d0\u817e\u7a7a\u98de\u53bb\u3002\u8fd9\u662f\u4f0f\u9b54\u6756\u6cd5\u7684\u6700\u540e\u4e00\u62db\uff0c\u540d\u53eb\u201c\u795e\u9b54\u5f52\u4f4d\u201d\uff0c\u56e0\u4e3a\u4f0f\u9b54\u6756\u6cd5\u4ece\u65e0 \u4f7f\u6700\u540e\u4e00\u62db\u7684\u9053\u7406\uff0c\u82e5\u7136\u8981\u4f7f\u5230\u6700\u540e\u4e00\u62db\uff0c\u5c31\u662f\u654c\u4eba\u672c\u4e8b\u59d4\u5b9e\u592a\u5f3a\uff0c\u65e0\u53ef\u5236\u670d\uff0c\u8fd9\u4e00\u62db\u4e0e\u654c \u4eba\u62fc\u4e2a\u540c\u5f52\u4e8e\u5c3d\u4e86\u3002\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u795e\u9b54\u5f52\u4f4d\u201d\uff0c\u5c31\u662f\u8fd9\u4e2a\u7389\u77f3\u4ff1\u711a\u7684\u610f\u601d\u3002\u8fd9\u4e00\u62db\u53c8\u662f\u94c1\u62d0 \u4ed9\u62fc\u5c3d\u6700\u540e\u7684\u6c14\u529b\uff0c\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u5982\u4f55\u7981\u53d7\u5f97\u4f4f\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u60e8\u53eb\uff0c\u90a3\u5c11\u5e74 \u6b66\u58eb\u7ed9\u94c1\u62d0\u81ea\u524d\u5fc3\u900f\u8fc7\u540e\u5fc3\uff0c\u767b\u65f6\u6b7b\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u6709\u751f\u4ee5\u6765\uff0c\u672a\u66fe\u89c1\u8fc7\u5982\u6b64\u60e8\u72b6\uff0c\u53ea\u515a\u624b\u9178\u811a\u8f6f\uff0c\u4e0d\u6562\u518d\u671b\u3002\u53ea\u542c\u5f97\u82b1\u6811\u8349\u6728\u95f4\u6089 \u6089\u7d22\u7d22\u7684\u58f0\u97f3\uff0c\u60f3\u662f\u90a3\u7ea2\u8863\u50e7\u5df2\u7ecf\u9003\u547d\u3002\u5ffd\u95fb\u94c1\u62d0\u4ed9\u53f9\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u9053\uff1a\u201c\u5b87\u513f\uff0c\u4f60\u8fc7\u6765\uff01 \u9648\u5929\u5b57\u8f6c\u8fc7\u5934\u6765\uff0c\u4f46\u89c1\u5e08\u7236\u9762\u5982\u91d1\u7eb8\uff0c\u501a\u5728\u6811\u6839\uff0c\u5c31\u50cf\u60a3\u4e86\u91cd\u75c5\u7684\u75c5\u4eba\u4e00\u6837\uff0c\u795e\u8272\u6bd4\u524d\u6b21\u53d7 \u4f24\u8fd8\u66f4\u9a87\u4eba\uff0c\u9648\u5929\u5b57\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u94c1\u62d0\u4ed9\u9053\uff1b\u201c\u5f92\u513f\uff0c\u4eca\u665a\u662f\u54b1\u4eec\u5206\u624b \u4e4b\u671f\u4e86\uff01\u201d\u9648\u5929\u5b57\u4e00\u60ca\uff0c\u773c\u6cea\u7c41\u7c41\u800c\u4e0b\u3002\u94c1\u62d0\u4ed9\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u767e\u5e74\u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u8fd9\u53c8\u6709\u4ec0 \u4e48\u503c\u5f97\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u7236\u529f\u529b\u6df1\u539a\uff0c\u8fd9\u5bab\u4e2d\u4e39\u836f\u751a\u591a\uff0c\u5f85\u6211\u6bcf\u4e00\u6837\u90fd\u6293\u4e00\u628a\u6765\u7ed9\u5e08\u7236\u770b\u770b\uff0c\u770b \u54ea\u4e00\u6837\u5408\u7528\uff1f\u201d\u94c1\u62d0\u4ed9\u51c4\u7136\u7b11\u9053\uff1a\u201c\u6211\u5728\u5927\u75c5\u4e4b\u540e\uff0c\u53c8\u628a\u4e00\u767e\u96f6\u516b\u8def\u4f0f\u9b54\u6756\u6cd5\u4f7f\u5b8c\uff0c\u5c31\u7b97\u628a \u5929\u4e0b\u6240\u6709\u7684\u7075\u4e39\u5999\u836f\uff0c\u90fd\u7ed9\u6211\u641c\u96c6\u4e86\u6765\uff0c\u4e5f\u6ca1\u7528\u4e86\u3002\u65f6\u5019\u65e0\u591a\uff0c\u4f60\u8fd8\u662f\u7ec6\u5fc3\u542c\u6211\u8bf4\u51e0\u53e5\u6d3b \u5427\u3002\u201d\u9648\u5929\u5b87\u5fcd\u4e86\u773c\u6cea\uff0c\u503e\u542c\u5e08\u7236\u9057\u8a00\u3002\u94c1\u62d0\u4ed9\u9053\uff1a\u201c\u54b1\u4eec\u5e08\u5f92\u867d\u53ea\u76f8\u5904\u4e09\u6708\uff0c\u6211\u5df2\u77e5\u4f60\u5929 \u6027\u7eaf\u539a\uff0c\u5c06\u6765\u5b9a\u6709\u5927\u6210\u3002\u6211\u8981\u62dc\u6258\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e08\u7236\u5429\u5490\u4fbf\u662f\u3002\u201d\u94c1\u62d0\u4ed9\u9053\uff1a \u201c\u82e5\u662f\u4e0a\u5929\u601c\u60af\uff0c\u4e0d\u6559\u6211\u592b\u59bb\u90fd\u906d\u6a2a\u6b7b\uff0c\u90a3\u4e48\u65e5\u540e\u4f60\u82e5\u89c1\u7740\u5e08\u5a18\uff0c\u5c31\u53eb\u5979\u597d\u597d\u5c06\u5b69\u5b50\u517b\u5927\uff0c \u5230\u5b69\u5b50\u5341\u5c81\u4e4b\u65f6\uff0c\u53eb\u4ed6\u62dc\u4f60\u4e3a\u5e08\u3002\u201d\u9648\u5929\u5b87\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u53ef\u4ece\u6ca1\u6709\u542c\u5e08\u7236\u8bf4\u8fc7\u6709\u5b69\u5b50\uff0c\u53ef\u662f \u6b64\u65f6\u6b64\u9645\uff0c\u4e5f\u4e0d\u4fbf\u591a\u95ee\u4e86\uff0c\u53ea\u542c\u5f97\u94c1\u62d0\u4ed9\u7eed\u9053\uff1a\u201c\u672c\u95e8\u7684\u6b66\u529f\u53e3\u8bc0\uff0c\u6211\u5df2\u5c3d\u4f20\u4e86\u4f60\uff0c\u62d0\u6cd5\u4f60 \u4ea6\u719f\u4e60\uff0c\u4f60\u5c31\u5c06\u6211\u672c\u95e8\u7684\u6b66\u529f\uff0c\u4f20\u4e0e\u6211\u7684\u5b69\u5b50\u4fbf\u4e86\u3002\u8fd9\u652f\u94c1\u62d0\uff0c\u4f60\u66ff\u6211\u4fdd\u5b58\uff0c\u5f85\u5b69\u5b50\u957f\u6210\u4e4b \u540e\uff0c\u518d\u4ea4\u4e0e\u4ed6\u3002\u53eb\u4ed6\u7ee7\u627f\u5e08\u7956\u3002\u81f3\u4e8e\u90a3\u4e2a\u756a\u50e7\uff0c\u4ed6\u4eca\u665a\u7eb5\u80fd\u9003\u5f97\u6027\u547d\u3002\u4ea6\u5c06\u6b8b\u5e9f\uff0c\u53eb\u4ed6\u4eec\u4e5f \u4e0d\u5fc5\u8fdc\u8d74\u5f02\u56fd\u66ff\u6211\u62a5\u4ec7\u4e86\u3002\u4f60\u7b54\u5e94\u505a\u6211\u5b69\u5b50\u7684\u5e08\u7236\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u53ea\u8981\u5f92\u513f\u6709\u547d\u4e0b\u5c71\uff0c \u5e08\u7236\u5429\u5490\u7684\u4e8b\uff0c\u4e00\u5b9a\u505a\u5230\u3002\u201d\u94c1\u62d0\u4ed9\u7b11\u4e86\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6211\u66fe\u53d7\u4f60\u5e08\u7956\u4e0e\u5192\u5ddd\u751f\u8001\u524d\u8f88\u7684\u5631 \u6258\u3001\u627e\u5bfb\u6842\u534e\u751f\u524d\u8f88\u4e0e\u4ed6\u7684\u540e\u55e3\uff0c\u5982\u4eca\u5df2\u786e\u5b9e\u77e5\u9053\u51b0\u5ddd\u5929\u5973\u4fbf\u662f\u6842\u534e\u751f\u7684\u5973\u513f\uff0c\u82e5\u7136\u51b0\u5ddd\u5929 \u5973\u672a\u6b7b\uff0c\u4f60\u4e00\u5b9a\u8981\u5bfb\u7740\u5979\uff0c\u8bf4\u4e0e\u5979\u77e5\u3002\u73b0\u4e0b\u51b0\u5cf0\u5df2\u5012\uff0c\u5979\u4e5f\u53ef\u4ee5\u4e0b\u5c71\u53bb\u5bfb\u5979\u7684\u4f2f\u7236\u4e86\u3002\u201d\u9648 \u5929\u5b87\u53c8\u5e94\u4e86\u4e00\u58f0\u3002\u94c1\u62d0\u4ed9\u6c14\u82e5\u6e38\u4e1d\uff0c\u58f0\u97f3\u8d8a\u6765\u8d8a\u5fae\u5f31\u4e86\uff0c\u9648\u5929\u5b87\u6276\u7740\u4ed6\uff0c\u53ea\u542c\u5f97\u4ed6\u53c8\u65ad\u65ad\u7eed \u7eed\u5730\u9053\uff1a\u201c\u90a3\u3001\u90a3\u91d1\u672c\u5df4\u74f6\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u8be5\u5e2e\u54ea\u8fb9\u624d\u662f\uff0c\u603b\u4e4b\u4e0d\u80fd\u8ba9\u5b83\u843d\u5728\u5916\u4eba\u624b\u4e2d\u3002\u90a3\u3001 \u90a3\u767d\u8863\u5c11\u5e74\uff0c\u8bf4\u8bdd\u6709\u70b9\u9053\u7406\uff0c\u4f60\u4f60\u53bb\u627e\u4ed6\u2026\u2026\u201d\u8d8a\u8bf4\u58f0\u97f3\u8d8a\u5f31\uff0c\u8fd9\u6bb5\u8bdd\u597d\u4f3c\u5c1a\u672a\u8bf4\u5b8c\uff0c\u53cc\u811a \u4e00\u4f38\uff0c\u5c31\u6b64\u4e00\u7791\u4e0d\u89c6\u3002 \u3000\u3000\u9648\u5929\u5b87\u53f7\u9676\u75db\u54ed\uff0c\u5c06\u5e08\u7236\u57cb\u5728\u56ed\u4e2d\uff0c\u628a\u90a3\u5c11\u5e74\u6b66\u58eb\uff0c\u4e5f\u53e6\u6398\u4e00\u5904\u57cb\u4e86\uff0c\u53d6\u4e86\u94c1\u62d0\uff0c\u62ed\u5e72 \u8840\u8ff9\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u6708\u4eae\u897f\u6c89\uff0c\u6b8b\u661f\u660e\u706d\uff0c\u9ed1\u591c\u5c06\u901d\uff0c\u4e0d\u4e45\u53c8\u8981\u9ece\u660e\u4e86\u3002\u9648\u5929\u5b87\u832b\u7136\u5730\u5728 \u5bab\u4e2d\u4e71\u8d70\uff0c\u504c\u5927\u7684\u51b0\u5bab\uff0c\u8fd9\u65f6\u53ea\u6709\u4ed6\u4e00\u4e2a\u751f\u4eba\uff0c\u9648\u5929\u5b87\u53c8\u662f\u60b2\u75db\uff0c\u53c8\u662f\u9a87\u6015\uff0c\u4efb\u5bab\u4e2d\u666f\u81f4\u5982 \u4f55\u7f8e\u5999\uff0c\u4ed6\u4e5f\u4e0d\u613f\u518d\u5728\u6b64\u5730\u9017\u7559\u4e86\u3002 \u3000\u3000\u5f85\u5f97\u4e1c\u65b9\u9732\u51fa\u66d9\u5149\uff0c\u9648\u5929\u5b87\u5e26\u4e86\u4e00\u4e9b\u5e72\u7cae\uff0c\u6536\u62fe\u968f\u8eab\u884c\u674e\uff0c\u832b\u7136\u8d70\u51fa\u51b0\u5bab\uff0c\u5ffd\u5730\u60f3\u9053\uff1a \u201c\u4ee5\u6211\u73b0\u5728\u7684\u672c\u9886\uff0c\u600e\u80fd\u6e21\u8fc7\u51b0\u5ddd\uff1f\u201d\u4f46\u53eb\u4ed6\u72ec\u81ea\u7559\u5728\u51b0\u5bab\uff0c\u4ed6\u5fc3\u4e2d\u53c8\u5b9e\u662f\u4e0d\u613f\uff0c\u6b63\u5728\u8fdb\u9000 \u4e24\u96be\uff0c\u5ffd\u542c\u5f97\u5730\u4e0b\u53c8\u4f3c\u9690\u9690\u6709\u58f0\uff0c\u9648\u5929\u5b87\u5927\u529b\u5403\u60ca\uff0c\u751f\u6015\u53c8\u662f\u4e00\u573a\u5730\u9707\uff0c\u8fd9\u58f0\u97f3\u82e5\u65ad\u82e5\u7eed\uff0c \u5ffd\u53c8\u505c\u6b62\uff0c\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u82e5\u662f\u5730\u9707\u7684\u5f81\u5146\uff0c\u600e\u4e48\u8fd9\u58f0\u97f3\u5e76\u4e0d\u52a0\u5f3a\uff1f\u201d\u5fc3\u4e2d\u53d1\u614c\uff0c\u4e00\u53e3\u6c14\u5f80 \u5916\u8dd1\u53bb\uff0c\u90a3\u58f0\u97f3\u5ffd\u7136\u53c8\u8d77\uff0c\u9648\u5929\u5b87\u518d\u8dd1\u4e00\u9635\uff0c\u53c8\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u51b0\u5bab\u4ed9\u5883\u591a\u5947\u4e8b\uff0c\u4ea6\u771f\u4ea6\u5e7b\u8d39\u731c\u7591\u3002 \u3000\u3000\u6b32\u77e5\u4e8b\u540e\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u6ca7\u6d77\u6851\u7530\u3000\u4ed9\u5c71\u4f24\u52ab\u540e\u3000\u767d\u4e91\u82cd\u72d7\u3000\u4f8d\u5973\u8bdd\u524d\u56e0 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u516b \u56de \u6ca7\u6d77\u6851\u7530\u3000\u4ed9\u5c71\u4f24\u52ab\u540e \u767d\u4e91\u82cd\u72d7\u3000\u4f8d\u5973\u8bdd\u524d\u56e0 \u3000\u3000\u9648\u5929\u5b87\u5b9a\u4e86\u5b9a\u795e\uff0c\u77e5\u9053\u8fd9\u7edd\u5bf9\u4e0d\u662f\u5730\u9707\u4e86\uff0c\u4f46\u5374\u66f4\u4e3a\u7591\u60d1\uff0c\u60f3\u4e0d\u900f\u8fd9\u662f\u4ec0\u4e48\u602a\u58f0\u3002\u5fc3 \u9053\uff1a\u201c\u5bab\u4e2d\u7075\u836f\u5b9d\u7269\u751a\u591a\uff0c\u83ab\u4e0d\u8981\u88ab\u574f\u4eba\u5077\u8fdb\u624d\u597d\u3002\u201d\u9648\u5929\u5b87\u867d\u7136\u518d\u4e5f\u4e0d\u613f\u5728\u5bab\u4e2d\u9017\u7559\uff0c\u4f46 \u4f4f\u4e86\u4e09\u4e2a\u591a\u6708\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5bf9\u51b0\u5bab\u5374\u603b\u6709\u4e00\u79cd\u5f02\u6837\u7684\u611f\u60c5\uff0c\u867d\u7136\u660e\u77e5\u81ea\u5df1\u53bb\u540e\uff0c\u8fd9\u4ed9\u5883\u822c\u7684 \u73e0\u5bab\u8d1d\u9619\u4e5f\u8bb8\u5c31\u6ca6\u4e3a\u72d0\u9f20\u4e4b\u7a9d\uff0c\u4f46\u53ea\u8981\u81ea\u5df1\u8fd8\u5728\u5c71\u4e0a\u4e00\u65e5\uff0c\u5374\u4e0d\u613f\u89c1\u5b83\u88ab\u574f\u4eba\u5360\u636e\u3002\u4e8e\u662f\u53c8 \u6298\u56de\u5934\u53bb\uff0c\u518d\u56de\u5230\u51b0\u5bab\u91cc\u9762\u3002 \u3000\u3000\u521a\u8fdb\u56ed\u5b50\uff0c\u5730\u4e0b\u602a\u58f0\u53c8\u8d77\uff0c\u9648\u5929\u5b87\u60f3\u9053\uff1a\u201c\u82e5\u7136\u662f\u4eba\uff0c\u5b9a\u65e0\u5728\u5730\u5e95\u884c\u8d70\u4e4b\u7406\uff0c\u6211\u662f\u592a\u8fc7 \u8651\u4e86\u3002\u201d\u4f46\u65e2\u7136\u56de\u8f6c\uff0c\u5c31\u7d22\u6027\u518d\u8fdb\u91cc\u9762\u5de1\u793c\u4e00\u756a\u3002\u8d70\u5230\u51b0\u6e56\u9644\u8fd1\uff0c\u5ffd\u4f3c\u542c\u5f97\u6709\u8f7b\u5fae\u7684\u811a\u6b65\u4e4b \u58f0\uff0c\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u50f3\uff0c\u6084\u6084\u7684\u63a9\u8fc7\u53bb\u3002\u9648\u5929\u5b87\u5bf9\u5bab\u4e2d\u7684\u9053\u8def\uff0c\u4e86\u5982\u6307\u638c\uff0c\u8f7b\u529f\u53c8\u9ad8\uff0c\u5faa\u58f0\u89c5 \u8fdb\uff0c\u6084\u6084\u8d70\u53bb\uff0c\u6765\u4eba\u7adf\u6ca1\u53d1\u73b0\u3002 \u3000\u3000\u53ea\u89c1\u5c31\u5728\u90a3\u5ea7\u5c16\u9876\u7684\u795e\u6bbf\u524d\u9762\uff0c\u5e76\u6392\u7ad9\u7740\u4e09\u4eba\uff0c\u5f53\u4e2d\u7684\u8eab\u8eaf\u80a5\u5927\uff0c\u6b63\u662f\u8428\u8fe6\u5b97\u571f\u53f8\u7684\u6d85 \u5df4\u4fc4\u9a6c\u767b\uff0c\u4e24\u65c1\u7684\u4eba\u5374\u662f\u524d\u6b21\u9047\u8fc7\u7684\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u3002\u53ea\u542c\u5f97\u4fc4\u9a6c\u767b\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u602a \u58f0\uff1f\u8be5\u4e0d\u4f1a\u662f\u5730\u9707\u5427\uff1f\u201d\u90a3\u5e74\u957f\u7684\u6b66\u58eb\u9053\uff1a\u201c\u770b\u6765\u4e0d\u662f\u5730\u9707\u3002\u201d\u4ed6\u4eec\u8bf4\u7684\u4e43\u662f\u85cf\u8bdd\uff0c\u9648\u5929\u5b87 \u542c\u5f97\u660e\u767d\uff0c\u5fc3\u4e2d\u66f4\u662f\u72d0\u7591\uff0c\u8fd9\u602a\u58f0\u65e2\u4e0d\u662f\u4ed6\u4eec\u5f04\u51fa\u6765\u7684\uff0c\u90a3\u5c31\u8d8a\u53d1\u795e\u79d8\u4e86\u3002\u53ea\u542c\u5f97\u4fc4\u9a6c\u767b\u53c8 \u9053\uff1a\u201c\u521a\u624d\u6211\u4eec\u8fd8\u5728\u5730\u4e0a\u53d1\u73b0\u4e00\u6ee9\u9c9c\u8840\uff0c\u4f3c\u4e4e\u8fd9\u91cc\u8fd8\u4f4f\u7684\u6709\u4eba\uff0c\u5374\u4f55\u4ee5\u4e00\u65e0\u6240\u89c1\uff1f\u201d\u90a3\u4e24\u4e2a \u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u53cc\u624b\u5408\u4ec0\uff0c\u9ad8\u53eb\u4e86\u51e0\u58f0\u201c\u51b0\u5ddd\u5929\u5973\uff01\u201d\u81ea\u7136\u9664\u4e86\u56de\u58f0\u4e4b\u5916\uff0c\u4ec0\u4e48\u4e5f\u542c\u4e0d\u89c1\u3002\u90a3 \u4e24\u4e2a\u6b66\u58eb\u73b0\u51fa\u6781\u5176\u60f6\u6050\u7684\u795e\u60c5\uff0c\u5495\u565c\u5bf9\u8bed\uff0c\u4e00\u4e2a\u9053\uff1a\u201c\u82e5\u7136\u516c\u4e3b\u8fd8\u5728\uff0c\u5b9a\u4f1a\u51fa\u6765\uff01\u201d\u4e00\u4e2a \u9053\uff1a\u201c\u96be\u9053\u5979\u771f\u662f\u906d\u4e86\u52ab\u96be\uff0c\u8fd9\u53eb\u54b1\u4eec\u600e\u751f\u5411\u56fd\u738b\u4ea4\u4ee3\uff1f\u201d\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u5949\u5c3c \u6cca\u5c14\u56fd\u738b\u4e4b\u547d\uff0c\u6765\u67e5\u63a2\u51b0\u5ddd\u5929\u5973\u7684\u4e0b\u843d\u7684\u3002\u4fc4\u9a6c\u767b\u8fd9\u53ae\u966a\u4ed6\u4eec\u6765\u6b64\uff0c\u5374\u53c8\u662f\u4f55\u7528\u610f\uff1f\u201d\u4fc4\u9a6c \u767b\u867d\u7136\u6551\u8fc7\u829d\u5a1c\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u9648\u5929\u5b87\u5bf9\u4ed6\u5374\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u618e\u538c\uff0c\u603b\u89c9\u5f97\u8fd9\u4eba\u662f\u4e2a\u5916\u8c8c\u8bda \u5b9e\u3001\u5185\u5fc3\u5978\u733e\u7684\u4f2a\u541b\u5b50\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u4e0d\u7ba1\u516c\u4e3b\u5728\u4e0e\u4e0d\u5728\uff0c\u54b1\u4eec\u4e14\u8fdb\u53bb\u641c\u641c\u3002\u201d\u8bf4\u7740\u5c31\u60f3\u8d70\u8fdb\u90a3\u5ea7\u795e\u6bbf\u3002\u5e74\u957f\u7684 \u5c3c\u6cca\u5c14\u6b66\u58eb\u6025\u9053\uff1a\u201c\u8fd9\u662f\u54b1\u4eec\u56fd\u6559\u7684\u5723\u6bbf\uff0c\u82e5\u4e0d\u5f97\u4e3b\u4eba\u5141\u8bb8\uff0c\u4e0d\u80fd\u968f\u4fbf\u8fdb\u53bb\u3002\u201d\u4fc4\u9a6c\u767b\u9053\uff1a \u201c\u6b64\u5730\u54ea\u8fd8\u6709\u4ec0\u4e48\u4e3b\u4eba\uff0c\u8fdb\u53bb\u770b\u770b\u4f55\u59a8\u3002\u201d\u5730\u9707\u4e4b\u540e\uff0c\u6bbf\u95e8\u65e9\u5df2\u5d29\u574f\uff0c\u4fc4\u9a6c\u767b\u4e00\u9762\u5411\u90a3\u4e24\u4e2a \u6b66\u58eb\u966a\u7b11\uff0c\u4e00\u9762\u8de8\u5927\u811a\u6b65\uff0c\u5c31\u8981\u8d70\u5165\u6bbf\u4e2d\u3002 \u3000\u3000\u9648\u5929\u5b87\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\u7684\u7981\u4ee4\uff0c\u53c8\u6015\u4ed6\u5077\u5b66\u5176\u4e2d\u7684\u5251\u6cd5\uff0c\u9661\u7136\u5927\u559d\u4e00\u58f0\uff0c\u98de\u6b65\u62a2\u51fa\uff0c\u53eb \u9053\uff1a\u201c\u4fc4\u9a6c\u767b\uff0c\u4f60\u597d\u5927\u80c6\uff01\u201d\u4fc4\u9a6c\u767b\u56de\u5934\u4e00\u770b\uff0c\u7b11\u9053\uff1a\u201c\u9648\u516c\u5b50\uff0c\u539f\u6765\u662f\u4f60\uff01\u829d\u5a1c\u5462\uff1f\u201d\u9648 \u5929\u5b87\u9053\uff1a\u201c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u4f60\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\uff0c\u4f60\u662f\u8fd9\u91cc\u7684\u4e3b\u4eba \u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u7740\uff0c\u4f60\u6eda\u4e0d\u6eda\uff1f\u201d\u4fc4\u9a6c\u767b\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u53c8\u51ed\u4ec0\u4e48\u6765\u7ba1\u6211\uff1f\u201d\u8138\u73b0 \u597d\u7b11\uff0c\u624b\u4e2d\u5df2\u62d4\u51fa\u5200\u6765\u3002 \u3000\u3000\u9648\u5929\u5b87\u70ed\u8840\u4e0a\u6d8c\uff0c\u5587\u7684\u4e00\u5251\u523a\u53bb\uff0c\u53c8\u559d\u9053\uff1a\u201c\u4f60\u6eda\u4e0d\u6eda\uff1f\u201d\u4fc4\u9a6c\u767b\u7b11\u9053\uff1a\u201c\u9648\u516c\u5b50\uff0c\u4f60 \u8981\u52a8\u624b\u4e48\uff1f\u5475\u5440\uff0c\u5440\uff0c\u54fc\uff01\u201d\u539f\u6765\u4fc4\u9a6c\u767b\u89c1\u8fc7\u9648\u5929\u5b87\u7684\u672c\u9886\uff0c\u81ea\u6043\u6b66\u529f\u8fdc\u5728\u4ed6\u4e0a\uff0c\u6545\u6b64\u4e1d\u6beb \u4e0d\u4ee5\u4e3a\u610f\uff0c\u6ee1\u62df\u4e00\u5200\u5288\u8fc7\uff0c\u4fbf\u53ef\u5c06\u4ed6\u7684\u957f\u5251\u683c\u98de\uff0c\u54ea\u77e5\u9648\u5929\u5b87\u4eca\u975e\u6614\u6bd4\uff0c\u8fd9\u4e00\u5251\u7adf\u662f\u8fbe\u6469\u5251 \u6cd5\u4e2d\u7684\u4e00\u4e2a\u602a\u62db\uff0c\u5251\u5c16\u4e00\u6643\uff0c\u4f3c\u5de6\u53cd\u53f3\uff0c\u5587\u7684\u4e00\u5251\uff0c\u5728\u4ed6\u7684\u80a9\u5934\u5212\u4e86\u4e00\u9053\u4f24\u53e3\uff0c\u8fd9\u8fd8\u662f\u56e0\u4e3a \u9648\u5929\u5b87\u7684\u529f\u592b\u672a\u5230\uff0c\u800c\u4fc4\u9a6c\u767b\u4e5f\u8fd8\u4e0d\u5f31\uff0c\u8981\u4e0d\u7136\u53ea\u8fd9\u4e00\u5251\uff0c\u5c31\u80fd\u5c06\u4ed6\u7684\u4e00\u6761\u81c2\u818a\u5378\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u7b11\u5bb9\u987f\u655b\uff0c\u51dd\u795e\u5bf9\u654c\uff0c\u8fd8\u4e86\u4e09\u5200\uff0c\u4f46\u5374\u654c\u4e0d\u4f4f\u9648\u5929\u5b87\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u7ed9\u4ed6\u8feb\u5f97\u6b65\u6b65 \u540e\u9000\uff0c\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5728\u65c1\u89c2\u671b\uff0c\u751a\u662f\u60ca\u5f02\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6ee1\u6e05\u5b98\u5458\u7684\u513f\u5b50\uff0c\u4ed6\u5077\u5230\u8fd9\u513f\uff0c\u53c8\u5b66\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\uff0c\u4e0d\u95ee\u53ef \u77e5\uff0c\u5b9a\u662f\u5728\u5730\u9707\u4e4b\u540e\uff0c\u51b0\u5ddd\u5929\u5973\u53d7\u4f24\uff0c\u7ed9\u4ed6\u4e58\u673a\u5bb3\u6b7b\u4e86\u3002\u4ed6\u7a83\u636e\u6b64\u5bab\uff0c\u5c45\u7136\u6562\u4ee5\u4e3b\u4eba\u81ea \u547d\uff01\u201d\u4e00\u756a\u8bdd\u717d\u52a8\u4e86\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u4ed6\u4eec\u62d4\u51fa\u6708\u7259\u5f2f\u5200\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u767b\u65f6\u4e0a\u6765\u5939\u653b\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u542c\u6211\u8bf4\u3002\u201d\u4fc4\u9a6c\u767b\u559d\u9053\uff1a\u201c\u8fd8\u8bf4\u4ec0\u4e48\uff01\u201d\u9648\u5929\u5b87\u4e0d\u5584\u63aa\u8bcd\uff0c\u81ea\u5df1\u53c8\u786e\u662f \u5077\u5b66\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\uff0c\u8feb\u5207\u4e4b\u95f4\uff0c\u89e3\u91ca\u4e0d\u6e05\uff0c\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e00\u62db\u7d27\u8fc7\u4e00\u62db\uff0c\u9648\u5929\u5b87\u5251 \u4ea4\u5de6\u624b\uff0c\u53f3\u624b\u6837\u52a8\u94c1\u62d0\uff0c\u540c\u65f6\u4f7f\u51fa\u4e24\u5957\u6b66\u6797\u7edd\u5b66\uff0c\u62db\u67b6\u4e86\u4e8c\u4e09\u5341\u62db\u3002 \u3000\u3000\u9648\u5929\u5b87\u5de6\u5251\u53f3\u62d0\uff0c\u62db\u6570\u867d\u7136\u7cbe\u5999\uff0c\u4f46\u706b\u5019\u672a\u5230\uff0c\u529f\u529b\u5c1a\u6d45\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u6321\u4e0d\u4e86\u4e09\u4e2a\u9ad8\u624b \u7684\u8fdb\u653b\uff0c\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u53ea\u662f\u5c06\u9648\u5929\u5b87\u7684\u62db\u6570\u7834\u5f00\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4fc4\u9a6c\u767b\u5374\u5200\u5200\u72e0\u8fa3\uff0c\u5c3d\u662f \u62e3\u81f4\u547d\u4e4b\u5904\u5288\u523a\uff0c\u9762\u4e0a\u53c8\u9732\u51fa\u4e86\u5f97\u610f\u7684\u597d\u7b11\u3002 \u3000\u3000\u5ffd\u5730\u91cc\u602a\u58f0\u53c8\u8d77\uff0c\u6bd4\u524d\u66f4\u4e3a\u6e05\u695a\u5b8f\u4eae\uff0c\u5404\u4eba\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u9648\u5929\u5b87\u677e\u4e86\u53e3\u6c14\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c \u90a3\u602a\u58f0\u53c8\u505c\u6b62\u4e86\u3002\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u5148\u628a\u8fd9\u53ae\u64d2\u4e86\uff0c\u518d\u884c\u62f7\u95ee\u3002\u201d\u6325\u5200\u518d\u6218\uff0c\u9648\u5929\u5b87\u6c14\u529b\u4e0d\u7ee7\uff0c \u66f4\u662f\u96be\u652f\u3002 \u3000\u3000\u9648\u5929\u5b87\u6c14\u8870\u529b\u7aed\uff0c\u6697\u53f9\u53e3\u6c14\uff1a\u60f3\u4e0d\u5230\u7cca\u91cc\u7cca\u6d82\u6b7b\u5728\u8fd9\u513f\u3002\u4fc4\u9a6c\u767b\u5f97\u610f\u4e4b\u6781\uff0c\u4e00\u58f0\u597d\u7b11\uff0c \u624b\u8d77\u4e00\u5200\uff0c\u5411\u4ed6\u53f3\u81c2\u659c\u659c\u5207\u4e0b\uff0c\u9648\u5929\u5b87\u88ab\u90a3\u4e24\u4e2a\u6b66\u58eb\u7684\u6708\u7259\u5f2f\u5200\u8feb\u7740\uff0c\u65e0\u6cd5\u62db\u67b6\uff0c\u6b63\u5728\u7edd\u9669 \u5173\u5934\uff0c\u53ea\u89c1\u4fc4\u9a6c\u767b\u548c\u90a3\u4e24\u4e2a\u6b66\u58eb\u90fd\u4e5e\u55e4\u4e00\u58f0\uff0c\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u653b\u52bf\u767b\u89c1\u677e\u61c8\u3002\u9648\u5929\u5b87\u5927\u4e3a\u60ca \u5947\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u95fb\u5f97\u5a07\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u95ef\u8fdb\u51b0\u5bab\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u60f3\u627e\u6b7b\u4e48\uff1f\u201d\u58f0\u97f3\u8106\u82e5\u94f6 \u94c3\uff0c\u9648\u5929\u5b87\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u82b1\u6811\u4e1b\u4e2d\uff0c\u51b0\u5bab\u4f8d\u5973\u7eb7\u7eb7\u8d70\u51fa\uff0c\u8bf4\u8bdd\u7684\u6b63\u662f\u540d\u53eb\u6708\u4ed9\u7684\u90a3\u4f4d\u4e66\u623f \u4f8d\u5973\uff0c\u5979\u8bf4\u8bdd\u7684\u53e3\u6c14\u548c\u795e\u6001\uff0c\u90fd\u5f88\u50cf\u51b0\u5ddd\u5929\u5973\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u53c8\u60ca\u53c8\u559c\uff0c\u8fd9\u4e48\u591a\u7684\u51b0\u5bab \u4f8d\u5973\u4e00\u4e0b\u5b50\u53c8\u90fd\u51fa\u73b0\u4e86\uff01\u9648\u5929\u5b87\u51e0\u4e4e\u7591\u5fc3\u53c8\u662f\u4e00\u573a\u5e7b\u68a6\u3002 \u3000\u3000\u539f\u6765\u51b0\u5ddd\u5929\u5973\u7684\u7236\u6bcd\u5b9a\u5c45\u6b64\u5c71\uff0c\u65e9\u5c31\u9884\u9632\u4f1a\u6709\u5730\u9707\uff0c\u51b0\u5bab\u7684\u4e2d\u5fc3\uff0c\u5730\u5e95\u4e0b\u662f\u4e2a\u51b0\u7a9f\uff0c\u4e98 \u53e4\u4e0d\u89c1\u9633\u5149\uff0c\u575a\u51b0\u79ef\u805a\uff0c\u575a\u903e\u5ca9\u77f3\u3002\u51b0\u5ddd\u5929\u5973\u7684\u7236\u6bcd\u5df2\u6d4b\u77e5\u5730\u4e0b\u706b\u5c71\u5728\u51b0\u5cf0\u9644\u8fd1\uff0c\u79bb\u51b0\u5bab\u6240 \u5728\u7ea6\u6709\u56db\u4e94\u5341\u91cc\uff0c\u7eb5\u662f\u706b\u5c71\u7206\u53d1\uff0c\u5927\u5730\u9707\u52a8\uff0c\u51b0\u5bab\u6240\u53d7\u7684\u9707\u8361\u4e5f\u4e0d\u4f1a\u592a\u5927\uff0c\u4e3a\u4e86\u9632\u5907\u51b0\u5cf0\u5012 \u584c\u4e4b\u65f6\u7684\u98de\u7802\u8d70\u77f3\u53ef\u80fd\u4f24\u4eba\uff0c\u56e0\u6b64\u5728\u51b0\u7a9f\u4e0b\u9762\uff0c\u9884\u5148\u5e03\u7f6e\u4e86\u907f\u96be\u7684\u6240\u5728\uff0c\u5f00\u4e86\u4e00\u6761\u5730\u9053\uff0c\u7528 \u6700\u575a\u786c\u7684\u82b1\u5c97\u5ca9\u77f3\u7b51\u6210\u4e24\u9053\u56f4\u5899\uff0c\u5730\u4e0b\u7ecf\u5e38\u5b58\u6709\u6570\u6708\u7cae\u98df\uff0c\u98df\u6c34\u53ef\u4ee5\u6eb6\u51b0\u53d6\u5f97\uff0c\u51c6\u5907\u5f97\u5341\u5206 \u5468\u5bc6\u3002\u6240\u4ee5\u90a3\u65e5\u5927\u5730\u9707\u4e4b\u65f6\uff0c\u9664\u4e86\u94c1\u62d0\u4ed9\u56e0\u4e3a\u5728\u9759\u5ba4\u7ec3\u529f\uff0c\u9648\u5929\u5b87\u56e0\u4e3a\u88ab\u51b0\u5ddd\u5929\u5973\u56da\u5728\u5bc6 \u5ba4\uff0c\u65e0\u6cd5\u8131\u8eab\u4e4b\u5916\uff0c\u5176\u4f59\u6240\u6709\u7684\u51b0\u5bab\u4f8d\u5973\u90fd\u5df2\u8eb2\u8fdb\u51b0\u7a9f\u7684\u907f\u96be\u5ba4\u53bb\u4e86\u3002\u4f46\u5979\u4eec\u867d\u7136\u51c6\u5907\u5f97\u5341 \u5206\u5468\u5bc6\uff0c\u4e5f\u8fd8\u6709\u4e00\u6837\u672a\u66fe\u7b97\u5230\uff0c\u5730\u9707\u4e4b\u540e\uff0c\u5730\u5c42\u51f9\u4e0b\uff0c\u4ece\u51b0\u7a9f\u8d70\u51fa\u51b0\u5bab\u7684\u901a\u9053\u7adf\u7ed9\u5835\u4f4f\uff0c\u8d70 \u4e0d\u51fa\u6765\u3002\u5e78\u800c\u51b0\u5bab\u4f8d\u5973\u4f17\u591a\uff0c\u5927\u5bb6\u9f50\u5fc3\u5408\u529b\uff0c\u6316\u4e86\u4e09\u4e2a\u6708\uff0c\u65b9\u59cb\u5728\u4eca\u65e5\u6316\u901a\u4e86\u5730\u9053\u3002\u9648\u5929\u5b87 \u4ed6\u4eec\u6240\u542c\u5230\u7684\u5730\u4e0b\u201c\u602a\u58f0\u201d\uff0c\u5c31\u662f\u51b0\u5bab\u4f8d\u5973\u4eec\u5c06\u8981\u901a\u51fa\u51b0\u5bab\u4e4b\u65f6\uff0c\u5728\u5730\u4e0b\u6316\u6398\u5730\u9053\u7684\u58f0\u97f3\u3002 \u3000\u3000\u51b0\u5bab\u4f8d\u5973\u4eec\u521a\u521a\u51fa\u6765\uff0c\u5c31\u89c1\u6709\u751f\u4eba\u95ef\u8fdb\uff0c\u4e2a\u4e2a\u542b\u55d4\uff0c\u7b2c\u4e00\u5708\u7684\u4e5d\u540d\u4f8d\u5973\uff0c\u4ee5\u6708\u4ed9\u4e3a\u9996\uff0c \u5df2\u5404\u81ea\u62d4\u51fa\u4e86\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u5e03\u6210\u4e86\u4e5d\u5929\u7384\u5973\u9635\uff0c\u5947\u5bd2\u4e4b\u6c14\uff0c\u89e6\u4f53\u5982\u5272\uff0c\u4fc4\u9a6c\u767b\u51bb\u5f97\u6296\u6296\u7d22 \u7d22\uff0c\u90a3\u4e24\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e5f\u51b7\u5f97\u8fde\u8fde\u6253\u6218\u3002\u9648\u5929\u5b87\u7ec3\u8fc7\u51b0\u5ddd\u5929\u5973\u8fd9\u4e00\u6d3e\u6b66\u529f\uff0c\u53c8\u670d\u8fc7\u5bab\u4e2d\u5fa1\u5bd2 \u7684\u7075\u836f\u9633\u548c\u4e38\uff0c\u6545\u6b64\u529f\u529b\u867d\u53ca\u4e0d\u4e0a\u90a3\u4e24\u540d\u6b66\u58eb\uff0c\u5374\u53cd\u800c\u5fcd\u53d7\u5f97\u4f4f\u3002 \u3000\u3000\u4e3a\u9996\u7684\u4f8d\u5973\u5a07\u53f1\u4e00\u58f0\uff0c\u5bd2\u5149\u5251\u6643\u4e86\u4e24\u4e0b\uff0c\u5c31\u60f3\u52a8\u624b\uff0c\u4fc4\u9a6c\u767b\u7259\u5173\u6253\u6218\uff0c\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u90a3\u4e24 \u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u6025\u5fd9\u54c0\u58f0\u6c42\u544a\uff0c\u7980\u8fbe\u6765\u610f\u3002\u4f8d\u5973\u4e2d\u6709\u4eba\u66fe\u542c\u51b0\u5ddd\u5929\u5973\u8bf4\u8fc7\u4ed6\u4eec\u7684\u6765\u5386\u7684\uff0c\u77e5\u9053 \u51b0\u5ddd\u5929\u5973\u90a3\u65e5\u4e5f\u66fe\u5728\u5929\u6e56\u65c1\u8fb9\u9976\u8fc7\u4ed6\u4eec\uff0c\u5f53\u5373\u5411\u4e3a\u9996\u7684\u4f8d\u5973\u8bf4\u4e86\u3002\u4e3a\u9996\u7684\u4f8d\u5973\u53d1\u4e00\u58f0\u53f7\u4ee4\uff0c \u5c06\u9635\u5f62\u6563\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u975e\u89c1\u4f60\u7b49\u5c1a\u65e0\u6076\u610f\uff0c\u4f60\u7b49\u4eca\u65e5\u5c31\u6765\u5f97\u53bb\u4e0d\u5f97\u4e86\u3002\u597d\uff0c\u4f60\u4eec\u8d70\u5427\uff0c\u4e0b \u6b21\u82e5\u518d\u4e71\u95ef\uff0c\u90a3\u5c31\u7edd\u4e0d\u7559\u60c5\u4e86\u3002\u201d\u5e74\u957f\u7684\u90a3\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5c1a\u6b32\u8bf4\u8bdd\uff0c\u51b0\u5bab\u4f8d\u5973\u559d\u9053\uff1a\u201c\u6211\u4eec \u7684\u516c\u4e3b\u4e0d\u8981\u4f60\u7ba1\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u628a\u51b0\u9b44\u5bd2\u5149\u5251\u8fde\u8fde\u6643\u52a8\uff0c\u4fc4\u9a6c\u767b\u62b5\u53d7\u4e0d\u4f4f\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u8f6c\u8eab \u6025\u8d70\uff0c\u90a3\u4e24\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u53f9\u4e86\u53e3\u6c14\uff0c\u53cc\u624b\u5408\u4ec0\uff0c\u5411\u5723\u6bbf\u62dc\u4e86\u4e00\u62dc\uff0c\u4e5f\u8f6c\u8eab\u8d70\u4e86\u3002\u53ea\u5269\u4e0b\u9648\u5929 \u5b87\u4e00\u4eba\uff0c\u5446\u5446\u5730\u7ad9\u5728\u51b0\u5bab\u4f8d\u5973\u7684\u9762\u524d\u3002 \u3000\u3000\u90a3\u540d\u53eb\u6708\u4ed9\u7684\u4f8d\u5973\u5411\u9648\u592d\u5b57\u76ef\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u5728\u6b64\u5730\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5e78\u514d\u52ab \u96be\uff0c\u8d70\u4e0d\u51fa\u53bb\uff0c\u64c5\u7559\u5bab\u4e2d\uff0c\u5c1a\u671b\u6055\u7f6a\u3002\u201d\u6708\u4ed9\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u5077\u5b66\u6211\u4eec\u7684\u5251\u6cd5\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u6211\u4ee5\u4e3a\u4f60\u4eec\u4e0d\u56de\u6765\u4e86\uff0c\u6050\u6015\u8fd9\u5251\u6cd5\u5931\u4f20\u2026\u2026\u201d\u9648\u5929\u5b87\u4e0d\u5584\u63aa\u8bcd\uff0c\u51b0\u5bab\u4f8d\u5973\u5df2\u6709\u591a\u4eba\u52a8\u6012\uff0c \u7eb7\u7eb7\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5fc3\u672f\u5374\u6041\u5730\u4e0d\u6b63\uff0c\u76fc\u6211\u4eec\u6b7b\uff01\u201d\u201c\u6211\u4eec\u5f85\u4f60\u4ee5\u5bbe\u5ba2\u4e4b\u793c\uff0c\u4f60 \u5374\u79c1\u5165\u5723\u6bbf\u4e8e\u524d\uff0c\u53c8\u60f3\u7a83\u636e\u51b0\u5bab\u4e8e\u540e\uff0c\u5c82\u6709\u6b64\u7406\uff01\u201d\u6709\u51e0\u4e2a\u6c14\u91cf\u7a84\u6d45\u7684\uff0c\u5c31\u60f3\u62d4\u5251\u5c06\u4ed6\u9a71 \u9010\u3002 \u3000\u3000\u9648\u5929\u5b87\u5728\u4f17\u4f8d\u5973\u653b\u5939\u4e4b\u4e0b\uff0c\u6709\u53e3\u96be\u8a00\uff0c\u4e3a\u9996\u7684\u4f8d\u5973\u5bf9\u9648\u5929\u5b87\u5c1a\u6709\u597d\u611f\uff0c\u6446\u4e86\u6446\u624b\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u5077\u5165\u5723\u6bbf\uff0c\u6211\u4eec\u7684\u516c\u4e3b\u672c\u8981\u5c06\u4f60\u7ec8\u751f\u56da\u7981\uff0c\u5982\u4eca\u4f60\u53c8\u5077\u5b66\u5979\u7684\u5251\u6cd5\uff0c\u6211\u4eec\u662f\u518d\u4e5f\u5bb9 \u4f60\u4e0d\u5f97\u4e86\u3002\u5ff5\u4f60\u66fe\u662f\u6211\u4eec\u516c\u4e3b\u7684\u5bbe\u5ba2\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u6b64\u5904\u4f60\u5374\u4e0d\u80fd\u7559\u4e86\uff01\u201d\u8981\u77e5\u51b0\u5ddd\u5929\u5973\u7981\u4ee4 \u751a\u4e25\uff0c\u800c\u4eca\u5979\u867d\u7136\u4e0d\u5728\uff0c\u4f17\u4f8d\u5973\u5bf9\u5979\u6240\u8981\u8d23\u7f5a\u7684\u4eba\uff0c\u4f9d\u7136\u4e0d\u6562\u5047\u4ee5\u8bcd\u8272\uff0c\u6709\u4e00\u4e24\u4e2a\u4e0d\u660e\u4e8b\u7406 \u7684\uff0c\u66f4\u64c5\u4f5c\u5a01\u798f\uff0c\u66ff\u51b0\u5ddd\u5929\u5973\u9010\u5ba2\u3002 \u3000\u3000\u9648\u5929\u5b87\u6c14\u5f80\u4e0a\u51b2\uff0c\u5fc3\u9053\uff1a\u600e\u4e48\u8fd9\u4e9b\u51b0\u5bab\u4f8d\u5973\uff0c\u4e2a\u4e2a\u90fd\u4e0d\u8fd1\u4eba\u60c5\u3002\u5f53\u4e0b\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u6211\u672c \u6765\u5c31\u60f3\u8d70\u4e86\uff0c\u53ea\u662f\u89c1\u4f60\u4eec\u5c1a\u672a\u56de\u6765\uff0c\u6050\u6709\u574f\u4eba\u79c1\u4eba\uff0c\u8fd9\u624d\u7559\u5230\u4eca\u65e5\u3002\u201d\u6709\u4e00\u4e2a\u4f8d\u5973\u9053\uff1a\u201c\u5982 \u6b64\u8bf4\u6765\u3002\u4f60\u5012\u662f\u6709\u529f\u4e4b\u4eba\u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u8fc7\u6211\u7684\u5e08\u7236\u5374\u662f\u56e0\u4e3a\u8981\u4fdd\u62a4\u6b64\u5bab\uff0c\u4ee5 \u81f3\u5728\u6b64\u4e27\u751f\u3002\u6211\u53bb\u4e86\u4e4b\u540e\uff0c\u4ed6\u7684\u575f\u5893\uff0c\u613f\u4f60\u4eec\u80fd\u591f\u4fdd\u5168\u3002\u201d\u8bf4\u7740\u4e0d\u89c9\u6f5c\u7136\u6cea\u4e0b\u3002\u6708\u4ed9\u9053\uff1a \u201c\u5475\uff0c\u94c1\u62d0\u4ed9\u6b7b\u4e86\u5417\uff1f\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u9648\u5929\u5b87\u7ea6\u7565\u8bf4\u4e86\u4e00\u904d\uff0c\u6708\u4ed9\u4e5f\u81ea\u5fc3\u4e2d\u540e\u6094\uff0c\u53ef\u662f\u5979\u5904\u5904 \u6a21\u4eff\u5979\u7684\u4e3b\u4eba\uff0c\u8bf4\u4e86\u7684\u8bdd\uff0c\u4e0d\u613f\u66f4\u6539\uff0c\u800c\u4e14\u5bab\u4e2d\u90fd\u662f\u5c11\u5973\uff0c\u53ea\u6709\u9648\u5929\u5b87\u662f\u4e2a\u7537\u4eba\uff0c\u5979\u4e5f\u4e0d\u6562 \u64c5\u81ea\u4f5c\u4e3b\uff0c\u5c06\u4ed6\u7559\u4e0b\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u4fee\u5efa\u94c1\u62d0\u4ed9\u7684\u575f\u5893\u4fbf\u662f\uff0c\u4f60\u597d\u751f\u53bb\u5427\u3002\u8981\u6211 \u6d3e\u4eba\u9001\u4f60\u4e0b\u5c71\u5417\uff1f\u201d\u8bf4\u8bdd\u5df2\u5ba2\u6c14\u8bb8\u591a\uff0c\u9648\u5929\u5b87\u4f59\u6012\u672a\u6d88\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\uff01\u201d\u6708\u4ed9\u53c8\u9053\uff1a \u201c\u516c\u4e3b\u66fe\u7ecf\u56de\u6765\u8fc7\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u6708\u4ed9\u6014\u4e86\u4e00\u6014\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u516c\u4e3b\uff0c \u66fe\u4e0b\u8fc7\u547d\u4ee4\uff0c\u4e0d\u51c6\u6211\u4eec\u79c1\u81ea\u4e0b\u5c71\uff0c\u4e0d\u8bba\u5979\u5728\u4e0e\u4e0d\u5728\uff0c\u8fd9\u547d\u4ee4\u6211\u4eec\u90fd\u4e0d\u6562\u8fdd\u80cc\uff0c\u4f60\u4e0b\u5c71\u4e4b\u540e\uff0c \u82e5\u6211\u4eec\u7684\u516c\u4e3b\u8fd8\u5728\u4eba\u95f4\uff0c\u5c31\u62dc\u6258\u4f60\u4ee3\u4e3a\u67e5\u8bbf\u3002\u201d\u9648\u5929\u5b87\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\u7684\u97f3\u5bb9\uff0c\u867d\u7136\u4e0d\u8fd1\u4eba \u60c5\uff0c\u5374\u751a\u662f\u5f97\u4eba\u5fc6\u5ff5\uff0c\u5979\u7684\u9ad8\u50b2\uff0c\u4e43\u662f\u4e0e\u751f\u4ff1\u6765\uff0c\u51fa\u4e8e\u81ea\u7136\uff0c\u4e0e\u521a\u624d\u90a3\u51e0\u4e2a\u50b2\u6162\u7684\u4f8d\u5973\uff0c\u7edd \u5bf9\u4e0d\u53ef\u76f8\u63d0\u5e76\u8bba\u3002\u9648\u5929\u5b87\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u8f6f\uff0c\u9053\uff1a\u201c\u542c\u660e\u767d\u4e86\uff0c\u9075\u547d\u5c31\u662f\u3002\u201d\u5728 \u4f17\u4f8d\u5973\u7684\u6ce8\u89c6\u4e0b\uff0c\u4ecd\u7136\u80cc\u8d77\u539f\u6765\u7684\u884c\u674e\uff0c\u63d0\u8d77\u5e08\u7236\u9057\u7559\u7684\u94c1\u62d0\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u8d70\u51fa\u51b0\u5bab\u3002\u80cc\u540e \u4f9d\u7a00\u542c\u5f97\u53f9\u606f\u4e4b\u58f0\uff0c\u9648\u5929\u5b87\u60f3\u9053\uff1a\u201c\u51b0\u5bab\u4f8d\u5973\u4e4b\u4e2d\uff0c\u539f\u6765\u4e5f\u6709\u597d\u7684\u3002\u201d\u5fc3\u4e2d\u7a0d\u89c9\u5bbd\u6170\u3002 \u3000\u3000\u9648\u5929\u5b87\u6ee1\u6000\u6005\u60d8\uff0c\u832b\u7136\u8d70\u51fa\u51b0\u5bab\uff0c\u60f3\u8d77\u51b0\u5ddd\u5929\u9669\uff0c\u81ea\u5df1\u672c\u9886\u5c1a\u4f4e\uff0c\u600e\u80fd\u98de\u6e21\uff1f\u53ef\u662f\u521a\u624d \u7684\u8bf4\u8bdd\u53c8\u8bf4\u5f97\u592a\u6ee1\uff0c\u4e0d\u597d\u610f\u601d\u518d\u56de\u53bb\u8bf7\u5979\u4eec\u9001\u4e0b\uff0c\u4e0d\u89c9\u5927\u662f\u8e0c\u8e87\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0a\u5c71\u4e4b\u65f6\uff0c\u5c1a\u662f\u521d\u590f\uff0c\u5982\u4eca\u8fc7\u4e86\u4e09\u4e2a\u591a\u6708\uff0c\u4e0b\u5c71\u4e4b\u65f6\uff0c\u5df2\u662f\u91d1\u98ce\u9001\u723d\u7684\u4ef2\u79cb\uff0c\u5c71 \u9876\u96ea\u7247\u8f7b\u98d8\uff0c\u534a\u5c71\u7ea2\u53f6\u5982\u971e\uff0c\u5730\u9707\u4e4b\u540e\uff0c\u5c18\u6c99\u672a\u51c0\uff0c\u90a3\u7eb5\u6a2a\u4ea4\u9519\uff0c\u531d\u7740\u5c71\u8170\uff0c\u50cf\u94f6\u86c7\u4e00\u822c\u7684 \u51b0\u5ddd\uff0c\u4e5f\u8499\u4e0a\u4e00\u5c42\u6de1\u9ec4\uff0c\u7ecf\u8fc7\u9633\u5149\u6298\u5c04\uff0c\u6de1\u9ec4\u4e4b\u4e2d\u53c8\u900f\u7740\u6d45\u84dd\uff0c\u522b\u662f\u4e00\u756a\u666f\u81f4\uff0c\u9648\u5929\u5b87\u606b\u606b \u6005\u6005\uff0c\u4fe1\u6b65\u6240\u4e4b\uff0c\u5ffd\u89c1\u524d\u9762\u9ed1\u70df\u5f25\u7a7a\uff0c\u706b\u7130\u51b2\u5929\uff0c\u539f\u6765\u90a3\u51b0\u5cf0\u5012\u584c\u4e4b\u540e\uff0c\u9732\u51fa\u4e86\u55b7\u706b\u53e3\uff0c\u4f59 \u706b\u672a\u7184\uff0c\u7194\u5ca9\u5982\u6d46\uff0c\u65c1\u8fb9\u7684\u5730\u5f62\u5df2\u9677\u4e0b\u6210\u6e56\uff0c\u9648\u5929\u5b87\u76ee\u77aa\u53e3\u5446\uff0c\u5fc3\u9053\uff1a\u201c\u53e4\u4eba\u6ca7\u6d77\u6851\u7530\u7684\u8bf4 \u8bdd\uff0c\u679c\u771f\u6709\u5176\u4e8b\u3002\u201d\u4e0d\u7981\u6697\u53f9\u9020\u7269\u4e4b\u5947\uff0c\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\u4e0e\u767d\u8863\u5c11\u5e74\uff0c\u90a3\u65e5\u5c31\u6b63\u662f\u5728\u51b0\u5cf0\u4e4b\u4e0b \u6bd4\u5251\uff0c\u770b\u6765\u53ef\u662f\u51f6\u591a\u5409\u5c11\u4e86\uff0c\u53c8\u60f3\u8d77\u91c7\u836f\u7684\u5e08\u5a18\u4e0e\u89c2\u6218\u7684\u829d\u5a1c\uff0c\u66f4\u662f\u4e0d\u5b89\u3002\u5fc3\u9053\uff1a\u201c\u4f46\u613f\u4e0a \u5929\u4fdd\u4f51\uff0c\u82e5\u5979\u4eec\u5c1a\u5728\u4eba\u95f4\uff0c\u6211\u5c31\u662f\u8e0f\u904d\u6d77\u89d2\u5929\u6daf\uff0c\u4e5f\u8981\u5bfb\u8bbf\u5979\u4eec\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u53ef\u662f\u600e\u80fd\u98de\u6e21\u51b0\u6cb3\u5929\u9669\uff1f\u9648\u5929\u5b87\u5927\u611f\u8e0c\u8e87\uff0c\u53ea\u597d\u832b\u7136\u5730\u5411\u5c71\u4e0b\u76f4\u8d70\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u53ea\u89c9\u5730 \u5f62\u53d8\u6362\uff0c\u4e0d\u4f3c\u4ece\u524d\uff0c\u90a3\u901a\u5411\u5929\u6e56\u7684\u51b0\u6cb3\uff0c\u672c\u6765\u5c31\u5728\u51b0\u5bab\u4e0b\u9762\u4e0d\u8fdc\uff0c\u9648\u5929\u5b87\u8bb0\u5f97\u51b0\u6cb3\u4e4b\u8fb9\uff0c\u8fd8 \u6709\u4e00\u4e1b\u4e1b\u7684\u6768\u67f3\uff0c\u4e34\u6cb3\u7684\u90a3\u68f5\u5927\u67f3\u6811\u7cfb\u6709\u5c0f\u821f\uff0c\u53ef\u662f\u800c\u4eca\u8fde\u90a3\u6761\u51b0\u6cb3\u4e5f\u4e0d\u89c1\u4e86\u3002\u518d\u8d70\u4e86\u534a\u4e2a \u65f6\u8fb0\uff0c\u5ffd\u611f\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u4e0b\u9762\u7adf\u662f\u4e00\u7247\u832b\u832b\u767d\u6c34\uff0c\u6d6e\u51b0\u95ea\u95ea\u53d1\u5149\uff0c\u4e00\u671b\u65e0\u5c3d\uff0c\u604d\u5982\u5929\u8fde \u6c34\uff0c\u6c34\u8fde\u5929\uff0c\u8fd9\u4e0d\u662f\u5929\u6e56\u662f\u4ec0\u4e48\uff1f\u539f\u6765\u5927\u5730\u9707\u4e4b\u540e\uff0c\u5c71\u5cb3\u53d8\u5f62\uff0c\u90a3\u6761\u901a\u5411\u5929\u6e56\u7684\u51b0\u5ddd\u5df2\u88ab\u5012 \u584c\u7684\u51b0\u5cf0\u586b\u5e73\u4e86\uff0c\u53d8\u6210\u4e86\u4e00\u6761\u7b14\u76f4\u7684\u659c\u5761\uff0c\u4ece\u6b64\u51b0\u5bab\u5230\u4e0b\u9762\u7684\u901a\u9053\uff0c\u5df2\u88ab\u6253\u5f00\uff0c\u4e0d\u5fc5\u518d\u7528\u5c0f \u821f\u5728\u51b0\u5ddd\u6d89\u9669\u4e86\u3002\u9648\u5929\u5b87\u53c8\u60ca\u53c8\u559c\uff0c\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u548c\u4fc4\u9a6c\u767b\u4e5f\u80fd\u4e0a\u5230\u51b0 \u5bab\u3002\u201d \u3000\u3000\u5929\u6e56\u4ecd\u7136\u5982\u65e7\uff0c\u6e56\u8fb9\u7eff\u8349\u5982\u8335\uff0c\u6742\u82b1\u751f\u6811\uff0c\u6e56\u6c34\u4ecd\u662f\u4e00\u6837\u6e05\u83b9\uff0c\u539f\u6765\u5929\u6e56\u9762\u79ef\u592a\u5927\uff0c\u53c8 \u6709\u8bb8\u591a\u652f\u6d41\uff0c\u5316\u4e3a\u6d41\u6cc9\u5c71\u7011\uff0c\u901a\u5411\u5c71\u4e0b\uff0c\u5730\u9707\u4e4b\u540e\u7684\u5c18\u6c99\uff0c\u65e9\u5df2\u6c89\u6dc0\uff0c\u6216\u8005\u51b2\u4e0b\u53bb\u4e86\uff0c\u9648\u5929 \u5b87\u5728\u6e56\u8fb9\u6b47\u4e86\u4e00\u4f1a\uff0c\u5c06\u76ae\u888b\u76db\u6ee1\u6e56\u6c34\uff0c\u604b\u604b\u6005\u6005\uff0c\u5f98\u5f8a\u4e45\u4e4b\uff0c\u770b\u65e5\u5934\u8fc7\u5348\uff0c\u8fd9\u624d\u79bb\u5f00\u3002 \u3000\u3000\u8d70\u4e86\u4e09\u65e5\uff0c\u5df2\u5230\u5c71\u4e0b\uff0c\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u751f\u6b7b\u672a\u535c\uff0c\u53ea\u80fd\u76fc\u673a\u7f18\u51d1\u5de7\uff0c\u53ef\u78b0\u7740 \u5979\u3002\u5982\u4eca\u8fd8\u662f\u5148\u5230\u62c9\u8428\u53bb\u5427\u3002\u201d\u62c9\u8428\u662f\u897f\u85cf\u7684\u9996\u5e9c\uff0c\u6ee1\u6e05\u9a7b\u85cf\u5927\u81e3\u798f\u5eb7\u5b89\u5c31\u9a7b\u5728\u90a3\u513f\uff0c\u9648\u5929 \u5b87\u7684\u7236\u4eb2\u9648\u5b9a\u57fa\u5728\u90a3\u65e5\u5ba3\u6170\u4f7f\u7684\u8859\u95e8\u88ab\u6bc1\u4e4b\u540e\uff0c\u7acb\u5373\u79bb\u5f00\u8428\u8fe6\uff0c\u5230\u62c9\u8428\u53bb\u5411\u798f\u5eb7\u5b89\u8bf7\u793a\uff0c\u6b64 \u4e8b\u9648\u5929\u5b87\u5df2\u4ece\u4e66\u7ae5\u6c5f\u752b\u7684\u53e3\u4e2d\u77e5\u9053\uff0c\u6545\u6b64\u51b3\u5b9a\u5148\u5230\u62c9\u8428\u53bb\u4f1a\u7236\u4eb2\u3002 \u3000\u3000\u4e0b\u5c71\u4e4b\u540e\uff0c\u53c8\u8d70\u4e86\u4e03\u516b\u5929\uff0c\u5230\u4e86\u4ece\u65e5\u5580\u5219\u5230\u62c9\u8428\u7684\u4e2d\u9014\u4e00\u4e2a\u5927\u9547\uff0c\u540d\u53eb\u624e\u4f26\uff0c\u897f\u85cf\u5730\u50fb \u4eba\u7a00\uff0c\u6709\u6570\u767e\u4eba\u5bb6\uff0c\u805a\u96c6\u6210\u5e02\uff0c\u5df2\u7b97\u57ce\u9547\uff0c\u624e\u4f26\u867d\u662f\u5927\u9547\uff0c\u4e5f\u53ea\u6709\u4e00\u95f4\u65c5\u5e97\uff0c\u9648\u5929\u5b87\u6295\u5bbf\u4e4b \u540e\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u56e0\u8fde\u65e5\u5954\u6ce2\uff0c\u6b63\u60f3\u4f11\u606f\uff0c\u5ffd\u95fb\u5f97\u90bb\u623f\u6709\u4eba\u547b\u541f\uff0c\u95f4\u9694\u7684\u677f\u58c1\u4e5f\u56e0\u75c5\u4eba\u7684\u6323\u624e \u800c\u9707\u52a8\uff0c\u9648\u5929\u5b87\u9887\u611f\u5947\u602a\uff0c\u5c31\u5524\u4e86\u5e97\u5c0f\u4e8c\u6765\u95ee\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u9694\u623f\u4f4f\u7684\u662f\u4e24\u4f4d\u519b\u5b98\uff0c\u5367\u75c5\u5728\u5e8a\uff0c\u5df1\u4e09\u65e5\u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5ba2\u9014\u751f\u75c5\uff0c \u6700\u662f\u53ef\u601c\uff0c\u8fd9\u9547\u4e0a\u6ca1\u6709\u533b\u751f\u5417\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6709\u662f\u6709\u4e00\u4e24\u4e2a\uff0c\u4f46\u90fd\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u75c5\uff0c\u533b \u751f\u628a\u4e86\u628a\u8109\uff0c\u836f\u65b9\u4e5f\u4e0d\u6562\u5f00\u3002\u201d\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u602a\u75c5\uff1f\u201d\u5e97\u5c0f\u4e8c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u8bf4\u6765 \u53ef\u771f\u5947\u602a\uff0c\u90a3\u65e5\u8fd9\u4e24\u4f4d\u519b\u5b98\u6295\u5bbf\uff0c\u5728\u5916\u9762\u996e\u9152\uff0c\u4f60\u77e5\u6211\u4eec\u8fd9\u95f4\u5ba2\u5e97\u662f\u517c\u505a\u9152\u98df\u4e70\u5356\uff0c\u4fbf\u8fd9\u5f80 \u5ba2\u5546\u7684\u3002\u6709\u4e00\u4e2a\u5c11\u5973\uff0c\u597d\u50cf\u662f\u4ece\u5916\u56fd\u6765\u7684\uff0c\u9f3b\u4e8e\u9ad8\u7fd8\uff0c\u773c\u73e0\u6de1\u78a7\uff0c\u4e5f\u8fdb\u6765\u6b47\u606f\uff0c\u90a3\u4e24\u4f4d\u5b98\u7237 \u4e0d\u5408\u5411\u5979\u8c03\u7b11\u4e86\u51e0\u53e5\u3002\u90a3\u5973\u5b50\u4e0d\u52a8\u6012\uff0c\u5374\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6b22\u559c\u5728\u8fd9\u91cc\u73a9\u4e50\uff0c\u90a3\u5c31\u5728\u8fd9\u91cc\u8eba\u51e0 \u5929\u5427\u3002\u201d\u4e5f\u4e0d\u77e5\u5979\u4f7f\u7684\u662f\u4ec0\u4e48\u90aa\u6cd5\uff0c\u5ffd\u542c\u5f97\u6ce2\u7684\u4e00\u58f0\uff0c\u5728\u90a3\u4e24\u4e2a\u519b\u5b98\u7684\u9762\u524d\uff0c\u5ffd\u7136\u6563\u51fa\u4e00\u7247 \u5bd2\u5149\uff0c\u6211\u4eec\u8fdc\u8fdc\u7684\u7ad9\u5728\u5916\u9762\uff0c\u4e5f\u6253\u4e86\u51e0\u4e2a\u51b7\u6218\u3002\u90a3\u5973\u5b50\u8bf4\u4e86\u8fd9\u540e\uff0c\u7acb\u523b\u629b\u4e0b\u4e00\u952d\u94f6\u5b50\uff0c\u5306\u5306 \u8d70\u4e86\u3002\u5979\u8d70\u4e86\u4e4b\u540e\uff0c\u90a3\u4e24\u4f4d\u5b98\u7237\u76f4\u56b7\u53d1\u51b7\uff0c\u76d6\u51e0\u5e8a\u68c9\u88ab\uff0c\u90fd\u6ca1\u6709\u7528\u3002\u8fd9\u51e0\u65e5\u4e00\u76f4\u8ff7\u8ff7\u7cca\u7cca\uff0c \u6709\u65f6\u53d1\u70e7\uff0c\u6709\u65f6\u53d1\u51b7\uff0c\u4f60\u8bf4\u8fd9\u53ef\u4e0d\u662f\u602a\u4e8b\u4e48\uff1f\u201d\u9648\u5929\u5b87\u542c\u4e86\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u542c\u4ed6\u8bf4 \u6765\uff0c\u8fd9\u5973\u5b50\u653e\u7684\u6697\u5668\uff0c\u4f3c\u662f\u51b0\u9b44\u795e\u5f39\u3002\u83ab\u975e\u5c31\u662f\u51b0\u5ddd\u5929\u5973\uff1f\u201d\u9053\uff1a\u201c\u6211\u7a0d\u61c2\u533b\u9053\uff0c\u5f85\u6211\u8fdb\u53bb \u770b\u770b\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u5c06\u9648\u5929\u5b87\u5e26\u5230\u90bb\u623f\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5b98\u7237\uff0c\u6709\u4f4d\u5b98\u4eba\u524d\u6765\u770b\u4f60\u3002\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u6b63\u5728\u53d1 \u70e7\u4e4b\u540e\uff0c\u795e\u667a\u7a0d\u89c1\u6e05\u9192\uff0c\u7741\u5f00\u773c\u775b\uff0c\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u9648\u5929\u5b87\u5b9a\u775b\u4e00 \u770b\uff0c\u8ba4\u5f97\u8fd9\u4e24\u4eba\u5c31\u662f\u90a3\u6b21\u5728\u65e5\u5580\u5219\u65c5\u5e97\u4e2d\u6240\u9047\uff0c\u62a4\u9001\u5047\u91d1\u672c\u5df4\u74f6\u7684\u90a3\u4e24\u4e2a\u519b\u5b98\u3002\u9648\u5929\u5b87\u9053\uff1a \u201c\u5bb6\u7236\u662f\u8428\u8fe6\u5b97\u5ba3\u6170\u4f7f\u9648\u5b9a\u57fa\uff0c\u5728\u4e0b\u540d\u53eb\u9648\u5929\u5b87\uff0c\u5728\u65e5\u5580\u5219\u6211\u4eec\u4f3c\u4e4e\u4f1a\u8fc7\u3002\u201d\u90a3\u4e00\u665a\uff0c\u9648\u5929 \u5b87\u7684\u5e08\u7236\u66fe\u548c\u4ed6\u4eec\u52a8\u624b\uff0c\u9648\u5929\u5b87\u5374\u672a\u66fe\u9732\u9762\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u542c\u4ed6\u8bf4\u4e86\u59d3\u540d\u6765\u5386\uff0c\u9053\uff1a\u201c\u54e6\uff0c\u539f \u6765\u662f\u9648\u516c\u5b50\u3002\u201d\u53eb\u5e97\u5c0f\u4e8c\u8d70\u5f00\uff0c\u95ee\u9053\uff1a\u201c\u9648\u5144\u6b64\u6765\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u9645\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u7684\u75c5\u53c8\u53d1\u4f5c\u4e86\uff0c\u51b7\u5f97\u7259\u5173\u6253\u6218\uff0c\u9648\u5929\u5b87\u770b\u4e86\u4e0d\u5fcd\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u75c5 \u5c0f\u5f1f\u8fd8\u61c2\u5f97\u533b\u6cbb\u3002\u201d\u53d6\u51fa\u4e24\u9897\u78a7\u7eff\u8272\u7684\u836f\u4e38\uff0c\u9001\u8fdb\u90a3\u4e24\u4e2a\u519b\u5b98\u53e3\u4e2d\uff0c\u53eb\u4ed6\u4eec\u54bd\u4e0b\uff0c\u8fc7\u4e86\u4e00 \u9635\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\uff0c\u53ea\u89c9\u6709\u4e00\u80a1\u70ed\u6c14\u76f4\u900f\u4e39\u7530\uff0c\u4ed6\u4eec\u7684\u5185\u529f\u4e5f\u6709\u76f8\u5f53\u706b\u5019\uff0c\u8fd0\u6c14\u8f85\u52a9\uff0c\u5c06\u90a3\u80a1 \u9633\u548c\u4e4b\u6c14\u8fd0\u884c\u56db\u80a2\uff0c\u8d8a\u6765\u8d8a\u89c9\u8212\u670d\uff0c\u9648\u5929\u5b87\u9053\uff1a\u201c\u518d\u8fc7\u4e00\u5929\uff0c\u5f85\u4f59\u5bd2\u4e4b\u6c14\u53bb\u51c0\uff0c\u4e24\u4f4d\u5927\u4eba\u5c31 \u53ef\u884c\u52a8\u5982\u5e38\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u519b\u5b98\uff0c\u4e00\u53eb\u6bdb\u5f66\uff0c\u4e00\u53eb\u4f26\u535a\uff0c\u662f\u798f\u5eb7\u5b89\u5e10\u4e0b\u7684\u9ad8\u624b\uff0c\u672c\u6765\u4ee5\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u82e5\u7136\u65e9 \u6709\u63d0\u9632\uff0c\u8fd0\u6c14\u62a4\u8eab\uff0c\u90a3\u65e5\u867d\u4e2d\u4e86\u51b0\u9b44\u795e\u5f39\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u75c5\u5f97\u5982\u6b64\u4e25\u91cd\uff0c\u504f\u504f\u90a3\u65e5\u4ed6\u4eec\u5728\u66b4\u996e\u4e4b \u540e\uff0c\u8086\u65e0\u5fcc\u60ee\uff0c\u53c8\u6599\u4e0d\u5230\u90a3\u5973\u5b50\u8eab\u6000\u7edd\u6280\uff0c\u4ee5\u81f3\u88ab\u5bd2\u6c14\u4fb5\u5165\u9aa8\u9ad3\uff0c\u518d\u8fd0\u771f\u6c14\u76f8\u6297\uff0c\u5df2\u7ecf\u65e0 \u6548\uff0c\u8fd9\u65f6\u4e00\u670d\u4e0b\u9648\u5929\u5b87\u7684\u836f\u4e38\uff0c\u7acb\u89c1\u8212\u670d\u8bb8\u591a\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u60ca\u5f02\uff0c\u53c8\u8bb0\u8d77\u5728\u65e5\u5580\u5219\u4e4b\u591c\uff0c\u548c \u4ed6\u4eec\u52a8\u624b\u7684\u4eba\u4e2d\uff0c\u6709\u4e00\u4e2a\u8001\u5934\u5b50\u5c31\u662f\u4e0e\u9648\u5929\u5b87\u540c\u884c\u7684\uff0c\u4e0d\u7981\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u95f4\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f \u8c01\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf4\u8fc7\u4e86\u5417\uff1f\u201d\u90a3\u4e2a\u540d\u53eb\u6bdb\u5f66\u7684\u519b\u5b98\u9053\uff1a\u201c\u4f60\u771f\u662f\u9648\u516c\u5b50\uff1f\u201d\u9648\u5929\u5b87 \u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u5f85\u6211\u4eec\u5230\u4e86\u62c9\u8428\u4e4b\u540e\uff0c\u540c\u5f80\u798f\u5927\u5e05\u7684\u8859\u95e8\u5bfb\u6211\u7236\u4eb2\u4fbf\u662f\u3002\u201d\u4f26\u535a\u9053\uff1a\u201c\u4f60 \u600e\u7684\u4f1a\u6709\u89e3\u90a3\u4e2a\u5996\u5973\u90aa\u6cd5\u7684\u836f\u4e38\uff1f\u201d\u9648\u5929\u5b87\u7b2c\u4e00\u6b21\u79bb\u5f00\u51b0\u5bab\u4e4b\u65f6\uff0c\u90a3\u65f6\u51b0\u5bab\u4f8d\u5973\u8fd8\u672a\u56de\u6765\uff0c \u9648\u5929\u5b87\u89c1\u51b0\u5bab\u4e2d\u7684\u4e39\u836f\u751a\u591a\uff0c\u6bcf\u4e00\u6837\u968f\u624b\u627e\u4e86\u4e00\u628a\uff0c\u653e\u5165\u5305\u91cc\uff0c\u5176\u4e2d\u62b5\u5fa1\u5947\u5bd2\u4e4b\u6c14\u7684\u9633\u548c \u4e38\uff0c\u9648\u5929\u5b87\u8ba4\u5f97\uff0c\u6070\u597d\u6d3e\u4e86\u7528\u573a\u3002\u8fd9\u65f6\u89c1\u8fd9\u519b\u5b98\u67e5\u6839\u95ee\u5e95\uff0c\u6b63\u4e0d\u77e5\u4ece\u54ea\u91cc\u8bf4\u8d77\uff0c\u6bdb\u5f66\u66f4\u662f\u8d77 \u7591\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u90a3\u5996\u5973\u6d3e\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u8a00\u8fd8\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u7a97\u5916\u6709\u5973\u5b50\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u771f\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\u3002\u6211\u5982\u4eca\u7ed9 \u4f60\u9001\u89e3\u836f\u6765\u4e86\uff0c\u4f60\u8fd8\u9a82\u6211\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u518d\u75c5\u51e0\u5929\uff1f\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u75c5\u60c5\u867d\u5df2\u51cf\u8f7b\uff0c\u6c14\u529b\u5c1a\u672a\u6062 \u590d\uff0c\u4e00\u542c\u5230\u90a3\u65e5\u90a3\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u5413\u5f97\u5664\u58f0\u4e0d\u6562\u518d\u8bf4\u3002\u53ea\u542c\u5f97\u90a3\u5973\u5b50\u53c8\u9053\uff1a\u201c\u662f\u4f60\u5077\u4e86\u6211\u5bab \u4e2d\u7684\u7075\u836f\u5417\uff1f\u5feb\u51fa\u6765\u89c1\u6211\uff01\u201d\u58f0\u97f3\u8bed\u6c14\uff0c\u6709\u70b9\u4f3c\u51b0\u5ddd\u5929\u5973\uff0c\u9648\u5929\u5b87\u6b63\u5728\u6fc0\u52a8\u4e4b\u4e2d\uff0c\u5206\u8fa8\u5f97\u4e0d \u5f88\u6e05\u695a\uff0c\u6025\u5fd9\u4e00\u8dc3\u800c\u51fa\uff0c\u53ea\u89c1\u90a3\u5973\u5b50\u5df2\u4e0a\u4e86\u5c4b\u9876\u3002\u9648\u5929\u5b87\u6025\u5fd9\u56de\u623f\u643a\u4e86\u968f\u8eab\u5305\u88f9\uff0c\u4e22\u4e0b\u623f \u94b1\uff0c\u8dc3\u51fa\u53bb\u8ffd\uff0c\u90a3\u5973\u5b50\u8dd1\u5f97\u5f88\u5feb\uff0c\u5e78\u800c\u9648\u5929\u5b87\u7684\u8f7b\u529f\u5927\u6709\u8fdb\u5883\u3001\u4e00\u51fa\u57ce\u95e8\uff0c\u7acb\u5373\u8ffd\u4e0a\uff0c\u90a3\u5973 \u5b50\u56de\u7738\u4e00\u7b11\uff0c\u9053\uff1a\u2018\u4f60\u7684\u6b66\u529f\u5927\u6709\u8fdb\u5883\u4e86\u3002\u662f\u6211\u4eec\u516c\u4e3b\u6307\u70b9\u4f60\u7684\u5417\uff1f\u5979\u662f\u4e0d\u662f\u5df2\u56de\u5bab\u4e86\uff1f\u201d \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u770b\u5f97\u5206\u660e\uff0c\u539f\u6765\u662f\u51b0\u5ddd\u5929\u5973\u7684\u8d34\u8eab\u4f8d\u5973\u5e7d\u840d\uff0c\u5979\u81ea\u5c0f\u968f\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u6587\u5b66\u6b66 \u529f\uff0c\u5728\u4f17\u4f8d\u5973\u4e4b\u4e2d\uff0c\u90fd\u662f\u51fa\u7c7b\u62d4\u9769\u7684\u4eba\u7269\uff0c\u5730\u9707\u4e4b\u65e5\u7684\u65e9\u6668\uff0c\u4fbf\u662f\u5979\u5949\u51b0\u5ddd\u5929\u5973\u4e4b\u547d\uff0c\u966a\u94c1 \u62d0\u4ed9\u7684\u59bb\u5b50\u8c22\u4e91\u771f\u53bb\u91c7\u836f\u7684\u3002 \u3000\u3000\u9648\u5929\u5b87\u89c1\u5230\u4e86\u5979\uff0c\u81ea\u662f\u5fc3\u4e2d\u6b22\u559c\uff0c\u4f46\u88ab\u5979\u4e00\u95ee\uff0c\u53c8\u89c9\u4e0d\u5b89\uff0c\u9053\uff1a\u201c\u662f\u6211\u79c1\u81ea\u5b66\u7684\uff0c\u4f60\u662f \u4e0d\u662f\u8981\u6267\u884c\u4f60\u4e3b\u4eba\u7684\u547d\u4ee4\uff0c\u518d\u6765\u7f5a\u6211\u3002\u201d\u5e7d\u840d\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4eec\u7684\u516c\u4e3b\u4e5f\u5f88\u6b22\u559c \u4f60\uff0c\u5979\u672c\u6765\u60f3\u7b49\u4f60\u4e34\u8d70\u4e4b\u524d\uff0c\u53eb\u6211\u6559\u4f60\u51e0\u8def\u529f\u592b\uff0c\u4f5c\u4e3a\u8d60\u793c\u7684\uff0c\u60f3\u4e0d\u5230\u90a3\u665a\u4f60\u79c1\u5165\u5723\u6bbf\uff0c\u60f9 \u8d77\u5979\u7684\u607c\u6012\uff0c\u636e\u6211\u731c\u6d4b\uff0c\u5979\u662f\u5413\u4e00\u5413\u4f60\uff0c\u5f85\u5979\u548c\u90a3\u5c11\u5e74\u6bd4\u5251\u4e4b\u540e\uff0c\u5c31\u653e\u4f60\u7684\u3002\u7ecf\u8fc7\u8fd9\u573a\u52ab \u96be\uff0c\u60f3\u4e0d\u5230\u4f60\u6211\u5c1a\u80fd\u751f\u5b58\uff0c\u4f60\u5feb\u8bf4\u8fd9\u4e09\u6708\u6765\u5bab\u4e2d\u7684\u60c5\u51b5\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u7ea6\u7565\u8bf4\u4e86\u4e00\u904d\uff0c\u5e7d\u840d\u9053\uff1a\u201c\u6211\u4e5f\u6599\u60f3\u4f17\u59d0\u59b9\u4e0d\u81f4\u4e27\u751f\u3002\u8001\u5b9e\u8bf4\uff0c\u5f53\u65f6\u6211\u53ea\u62c5\u5fc3\u4f60 \u56da\u5728\u5bc6\u5ba4\uff0c\u4e0d\u80fd\u51fa\u6765\uff0c\u82e5\u7136\u4e27\u547d\uff0c\u516c\u4e3b\u4e5f\u5b9a\u611f\u4e0d\u5b89\u3002\u201d\u9648\u5929\u5b87\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u51b0\u5ddd\u5929\u5973\u5462\uff1f\u201d \u5e7d\u840d\u9053\uff1a\u201c\u6211\u966a\u4f60\u7684\u5e08\u5a18\u53bb\u91c7\u836f\uff0c\u89c1\u5230\u5730\u9707\u7684\u5f81\u5146\uff0c\u5c31\u7acb\u523b\u4e58\u821f\u76f4\u4e0b\u5927\u6e56\uff0c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u516c\u4e3b \u7684\u60c5\u5f62\u3002\u201d\u9648\u5929\u5b87\u542c\u4e86\uff0c\u597d\u751f\u5931\u671b\uff0c\u9053\uff1a\u201c\u6211\u7684\u5e08\u5a18\u5462\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5979\u5148\u56de\u56db\u5ddd\u7b49\u5019\u4e34\u76c6 \u4e86\u3002\u201d\u9648\u5929\u5b87\u542c\u4e86\uff0c\u604d\u7136\u5927\u609f\uff0c\u9053\uff1a\u201c\u539f\u6765\u5979\u6709\u4e86\u5b69\u5b50\u3002\u201d\u5e7d\u840d\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u5feb\u6dfb \u4e00\u4f4d\u5e08\u5f1f\u6216\u8005\u4e00\u4f4d\u5e08\u59b9\u4e86\uff0c\u8fd8\u4e0d\u9ad8\u5174\u5417\uff1f\u201d\u9648\u5929\u5b87\u60f3\u8d77\u94c1\u62d0\u4ed9\u4e4b\u6b7b\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u6709\u70b9\u602a\u8d23\u7684 \u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5f53\u65e5\u4f60\u4eec\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u90a3\u65e5\u706b\u5c71\u7206\u53d1\uff0c\u5927\u5730\u9707\u52a8\uff0c\u5730\u9707\u4e4b\u540e\uff0c\u6ee1\u5c71\u90fd\u662f\u77f3\u5757\u548c\u6eb6\u5ca9\uff0c\u4e0a\u5c71\u7684\u9053\u8def\u5df2 \u88ab\u5c01\u4e86\uff0c\u6211\u4eec\u89c1\u6b64\u60c5\u5f62\uff0c\u770b\u6765\u975e\u7b49\u8fc7\u4e86\u4e00\u4e9b\u65f6\u65e5\uff0c\u5f85\u90a3\u6eb6\u5ca9\u51dd\u7ed3\u4e4b\u540e\uff0c\u4e0a\u5c71\u662f\u4e0d\u53ef\u80fd\u7684\u4e86\u3002 \u4f60\u7684\u5e08\u5a18\u6709\u5b55\uff0c\u96be\u9053\u53eb\u5979\u7559\u5728\u8352\u5c71\uff1f\u6211\u77e5\u9053\u5bab\u4e2d\u65e9\u51c6\u5907\u6709\u9632\u5907\u5730\u9707\u7684\u6240\u5728\uff0c\u9664\u4e86\u62c5\u5fc3\u4f60\u4e4b \u5916\uff0c\u5bf9\u4f17\u59d0\u59b9\u548c\u94c1\u62d0\u4ed9\u90fd\u4e0d\u5fc5\u62c5\u5fc3\u3002\u6240\u4ee5\u529d\u4f60\u7684\u5e08\u5a18\u5148\u56de\u56db\u5ddd\u751f\u4ea7\uff0c\u5f85\u5230\u5730\u9707\u7684\u707e\u7978\u6d88\u51cf\u4e4b \u540e\uff0c\u94c1\u62d0\u4ed9\u81ea\u7136\u4f1a\u56de\u6765\u3002\u201d\u9648\u5929\u5b87\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u662f\u6211\u7684\u5e08\u7236\u518d\u4e5f\u4e0d\u4f1a\u56de\u6765\u4e86\u3002\u201d\u5e7d\u840d\u542c\u4e86 \u94c1\u62d0\u4ed9\u7684\u6b7b\u8baf\uff0c\u4e5f\u662f\u5341\u5206\u96be\u8fc7\uff0c\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u95f4\u9053\uff1a\u201c\u90a3\u4f60\u73b0\u5728\u51c6\u5907\u4f55\u5f80\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u60f3\u53bb\u62c9\u8428\uff0c\u4f60\u5462\uff1f\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u8981\u5230\u54ea\u91cc\u53bb\u3002\u6211\u672c\u60f3\u7b49\u5f85\u4e00\u4e9b\u65f6\u65e5\uff0c\u5f85\u5c71\u4e0a \u7684\u7194\u5ca9\u51dd\u7ed3\u4e4b\u540e\uff0c\u5c31\u56de\u53bb\u7684\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u73b0\u5728\u9664\u4e86\u51b0\u5cf0\u5012\u584c\u4e4b\u5904\u8fd8\u7559\u4e0b\u55b7\u706b\u53e3\u4e4b\u5916\uff0c\u5176 \u4ed6\u5730\u65b9\u5df2\u4e0d\u89c1\u7194\u5ca9\u4e86\u3002\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e0d\u77e5\u9053\u5440\uff01\u6211\u8fd8\u60f3\u7b49\u5230\u660e\u5e74\u6625\u6696\u82b1\u5f00\u7684\u65f6\u5019\uff0c \u518d\u56de\u53bb\u63a2\u671b\u5462\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u6b47\u4e86\u4e00\u6b47\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u8fd8\u8bb0\u5f97\u90a3\u767d\u8863\u5c11\u5e74\u7ed9\u6211\u62df\u7684\u5bf9\u8054 \u4e48\uff1f\u90a3\u662f\uff1a\u5e7d\u8c37\u8352\u5c71\uff0c\u6708\u8272\u6d17\u6e05\u989c\u8272\uff1b\u840d\u6897\u83b2\u53f6\uff0c\u96e8\u58f0\u6ef4\u788e\u8377\u58f0\u3002\u4ed6\u628a\u6211\u60f3\u50cf\u4e3a\u4e00\u4e2a\u5e7d\u8c37\u7684 \u9759\u5973\uff0c\u5176\u5b9e\u6211\u4e5f\u5f88\u60f3\u770b\u770b\u5916\u9762\u7684\u4e16\u754c\uff0c\u8fd9\u4e48\u591a\u5e74\uff0c\u5728\u51b0\u5bab\u4e2d\u4e5f\u771f\u662f\u591f\u5bc2\u5bde\u7684\u4e86\u3002\u201d\u6708\u5149\u4e4b \u4e0b\uff0c\u53ea\u89c1\u5979\u8f7b\u63a0\u4e91\u9b13\uff0c\u5fae\u9732\u7b11\u5bb9\uff0c\u6d3b\u50cf\u4e00\u4e2a\u987d\u76ae\u7684\u5973\u5b69\u5b50\uff0c\u9648\u5929\u5b87\u4e5f\u5c1a\u662f\u7ae5\u5fc3\u672a\u8131\uff0c\u7ed9\u5979\u9017 \u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u662f\u8d81\u6b64\u65f6\u673a\uff0c\u5230\u5904\u53bb\u73a9\uff0c\u897f\u85cf\u5730\u65b9\uff0c\u4ee5\u62c9\u8428\u6700\u4e3a\u7e41\u534e\uff0c\u8fd8\u6709 \u91d1\u5854\u7684\u5587\u561b\u5e99\u5b57\u54e9\uff0c\u4f60\u4e0d\u5982\u548c\u6211\u5230\u62c9\u8428\u53bb\u770b\u4e00\u770b\u5427\u3002\u201d\u5e7d\u840d\u559c\u9053\uff1a\u201c\u90a3\u6562\u60c5\u597d\uff0c\u54b1\u4eec\u4e5f\u53ef\u8d81 \u6b64\u6253\u542c\u516c\u4e3b\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u63d0\u8d77\u51b0\u5ddd\u5929\u5973\uff0c\u9648\u5929\u5b87\u4e0d\u7981\u9ed8\u7136\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u90a3\u65e5\u5728\u51b0\u5cf0\u4e4b\u4e0b\u6bd4\u5251\uff0c\u8fd9\u573a\u52ab\u96be\uff0c\u53ef\u4e0d\u77e5 \u80fd\u5426\u907f\u8fc7\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u6211\u4eec\u7684\u516c\u4e3b\u53eb\u51b0\u5ddd\u5929\u5973\uff0c\u672c\u4e8b\u867d\u7136\u672a\u5fc5\u6bd4\u5f97\u4e0a\u5929\u4e0a\u7684\u795e\u4ed9\uff0c\u4f46\u5374\u786e \u662f\u795e\u5947\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u6211\u4e0d\u4fe1\u8fd9\u4e00\u573a\u5730\u9707\u4f1a\u4f7f\u5979\u4e27\u547d\uff01\u201d\u8a00\u8bcd\u795e\u8272\u4e4b\u95f4\uff0c\u5bf9\u51b0\u5ddd\u5929\u5973\u771f\u662f\u89c6\u82e5 \u5929\u4eba\uff0c\u9648\u5929\u5b87\u4e5f\u7ed9\u5979\u8fd9\u79cd\u575a\u4fe1\u6240\u611f\u67d3\uff0c\u89c9\u5f97\u51b0\u5ddd\u5929\u5973\u679c\u7136\u662f\u6ca1\u6709\u4e27\u547d\u7684\u9053\u7406\u3002\u5e7d\u840d\u53c8\u7b11\u9053\uff1a \u201c\u4f60\u522b\u770b\u5979\u548c\u90a3\u767d\u8863\u5c11\u5e74\u51e0\u5ea6\u6bd4\u5251\uff0c\u5982\u540c\u4ec7\u654c\uff0c\u5176\u5b9e\u6211\u77a7\u5f97\u51fa\u6765\uff0c\u5979\u5fc3\u91cc\u559c\u6b22\u4ed6\u3002\u201d\u9648\u5929\u5b87 \u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u6ee1\u809a\u7075\u7cbe\u7684\u5c0f\u9b3c\u5934\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u4f60\u662f\u8bc8\u98a0\u626e\u50bb\u7684\u5c0f\u9b3c\u5934\uff0c\u4f60\u559c\u6b22\u4ec0\u4e48\u4eba\uff0c \u6211\u4e5f\u77e5\u9053\u5462\uff01\u201d\u9648\u5929\u5b87\u60f3\u8d77\u829d\u5a1c\uff0c\u5fc3\u9053\uff1a\u201c\u829d\u5a1c\u672c\u4e8b\u4f4e\u5fae\uff0c\u5979\u672a\u5fc5\u80fd\u9003\u5f97\u8fc7\u8fd9\u573a\u707e\u96be\u3002\u201d\u7b11 \u5bb9\u987f\u655b\uff0c\u795e\u8272\u751a\u662f\u4f18\u4f24\u3002 \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u5409\u4eba\u81ea\u6709\u5929\u76f8\uff0c\u829d\u5a1c\u82e5\u662f\u547d\u4e0d\u8be5\u6b7b\uff0c\u5979\u5c31\u5b9a\u7136\u4e0d\u6b7b\u3002\u201d\u8fd9\u8bdd\u8bf4\u4e86\u7b49\u4e8e\u4e0d\u8bf4\uff0c \u4f46\u9648\u5929\u5b87\u542c\u4e86\uff0c\u5fc3\u4e2d\u5374\u5b89\u6170\u8bb8\u591a\u3002\u4e24\u4eba\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u9648\u5929\u5b87\u5ffd\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u79f0\u51b0 \u5ddd\u5929\u5973\u505a\u516c\u4e3b\uff0c\u5979\u5230\u5e95\u662f\u54ea\u4e00\u56fd\u7684\u516c\u4e3b\uff1f\u4e3a\u4ec0\u4e48\u5979\u7684\u7236\u4eb2\u5374\u662f\u6211\u4eec\u4e2d\u539f\u7684\u4fa0\u5ba2\uff1f\u201d\u5e7d\u840d\u7b11 \u9053\uff1a\u201c\u597d\uff0c\u957f\u591c\u65e0\u804a\uff0c\u6211\u5c31\u4e3a\u4f60\u8bf4\u4e00\u8bf4\u6211\u4eec\u516c\u4e3b\u7684\u6545\u4e8b\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5bab\u95f9\u5f02\u4e8b\u4ece\u5934\u8bf4\uff0c\u5f02\u56fd\u60c5\u9e33\u4f73\u8bdd\u591a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u5999\u5883\u534e\u4e25\u3000\u8273\u8bf4\u795e\u4ed9\u4fa3\u3000\u4ed9\u97f3\u7389\u7b1b\u3000\u559c\u8054\u5f02\u56fd\u60c5 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u4e5d \u56de \u5999\u5883\u534e\u4e25\u3000\u8273\u8bf4\u795e\u4ed9\u4fa3 \u4ed9\u97f3\u7389\u7b1b\u3000\u559c\u8054\u5f02\u56fd\u60c5 \u3000\u3000\u6708\u51c9\u5982\u6c34\uff0c\u5e7d\u840d\u632a\u52a8\u8eab\u5b50\uff0c\u5fae\u5fae\u504e\u8fd1\u9648\u5929\u5b87\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u5c3c\u6cca\u5c14\u56fd\u6709\u4e00\u4f4d\u516c \u4e3b\uff0c\u53eb\u540d\u534e\u7389\uff0c\u5979\u4e4b\u53d6\u540d\u534e\u7389\uff0c\u662f\u56e0\u4e3a\u56fd\u541b\u4ef0\u6155\u4e2d\u534e\u5927\u56fd\uff0c\u53c8\u56e0\u5979\u751f\u5f97\u53ef\u7231\uff0c\u6709\u5982\u4e2d\u534e\u7684\u7f8e \u7389\uff0c\u6545\u6b64\u547d\u540d\u3002\u534e\u7389\u516c\u4e3b\u957f\u5927\u4e4b\u540e\uff0c\u6587\u6b66\u53cc\u4fee\uff0c\u4ece\u4e2d\u56fd\u8bf7\u6765\u6587\u5b66\u6559\u5e08\uff0c\u719f\u8bfb\u4e2d\u56fd\u7684\u7ecf\u53f2\u8bcd \u7ae0\uff0c\u53c8\u4ece\u963f\u62c9\u4f2f\u8bf7\u6765\u5251\u5e08\uff0c\u6559\u5979\u5251\u672f\uff0c\u81f3\u4e8e\u9a91\u9a6c\u5c04\u7bad\uff0c\u90a3\u66f4\u4e0d\u6d88\u8bf4\u5f97\uff0c\u6837\u6837\u7686\u80fd\u3002\u201d \u3000\u3000\u201c\u5c81\u6708\u5982\u6d41\uff0c\u8f6c\u773c\u516c\u4e3b\u957f\u5927\u6210\u4eba\uff0c\u82b3\u9f84\u5341\u516b\uff0c\u56fd\u4e2d\u8d35\u4ecb\u516c\u5b50\uff0c\u4e2a\u4e2a\u90fd\u60f3\u6c42\u516c\u4e3b\u4e3a\u59bb\uff0c\u53ef \u662f\u534e\u7389\u516c\u4e3b\u4e00\u4e2a\u90fd\u4e0d\u5408\u610f\u3002\u5e74\u590d\u4e00\u5e74\uff0c\u516c\u4e3b\u4e8c\u5341\u4e8c\u5c81\u4e86\uff0c\u56fd\u738b\u53ea\u6709\u5979\u4e00\u4e2a\u5973\u513f\uff0c\u4e0d\u514d\u7126\u6025\uff0c \u4e3a\u4e86\u4e0d\u8ba9\u516c\u4e3b\u82b3\u534e\u865a\u5ea6\uff0c\u610f\u6b32\u4e3a\u5979\u9009\u62e9\u9a78\u9a6c\uff0c\u8feb\u5979\u6210\u4eb2\u3002\u516c\u4e3b\u4e0d\u5141\uff0c\u81ea\u5df1\u63d0\u51fa\u4e00\u4e2a\u529e\u6cd5\uff0c\u8981 \u4eff\u7167\u4e2d\u56fd\u5c0f\u8bf4\u4e2d\u5e38\u89c1\u4e4b\u4e8b\uff0c\u6446\u8bbe\u64c2\u53f0\u3002\u4eb2\u9003\u90ce\u541b\u3002\u8fd9\u64c2\u53f0\u6709\u6587\u6709\u6b66\uff0c\u5148\u8bd5\u6b66\u827a\uff0c\u540e\u8bd5\u6587\u624d\uff0c \u8bd5\u6b66\u827a\u7684\u901a\u8fc7\u4e86\u51e0\u5173\u6781\u96be\u7684\u8003\u8bd5\u4e4b\u540e\uff0c\u8fd8\u8981\u4e0e\u5979\u6bd4\u5251\uff1b\u6bd4\u6b66\u80dc\u4e86\uff0c\u7136\u540e\u518d\u8003\u6587\u624d\uff0c\u8003\u6587\u624d\u4e0d \u4f46\u8981\u61c2\u5c3c\u6cca\u5c14\u6587\u5b66\uff0c\u8fd8\u5f97\u61c2\u505a\u4e2d\u56fd\u7684\u6587\u7ae0\u3002\u5c3c\u6cca\u5c14\u61c2\u5f97\u6c49\u6587\u7684\u4e0d\u5c11\uff0c\u4f46\u53ea\u662f\u7c97\u89e3\u76ae\u6bdb\uff0c\u90a3\u5f53 \u5f97\u516c\u4e3b\u9762\u8bd5\u3002\u6545\u6b64\u5728\u4e24\u5e74\u4e4b\u4e2d\uff0c\u6c42\u4eb2\u8005\u5171\u6709\u4e00\u767e\u4e8c\u5341\u56db\u4eba\uff0c\u5148\u8bd5\u6b66\u827a\uff0c\u591f\u8d44\u683c\u4e0e\u5979\u6700\u540e\u6bd4\u5251 \u7684\u53ea\u6709\u4e03\u4eba\uff0c\u6bd4\u5251\u80dc\u4e86\u5979\u7684\u53ea\u6709\u4e09\u4eba\uff0c\u8fd9\u4e09\u4eba\u4e00\u88ab\u8003\u5230\u4e2d\u56fd\u7684\u6587\u5b66\uff0c\u5168\u90fd\u7b54\u4e0d\u51fa\u6765\u3002\u56fd\u738b\u5927 \u6025\uff0c\u51c6\u6c49\u4eba\u524d\u53bb\u5e94\u8bd5\uff0c\u53ef\u662f\u90a3\u4e9b\u6c49\u4eba\u7b49\u4e0d\u5230\u516c\u4e3b\u8bd5\u4ed6\u6587\u624d\uff0c\u6bd4\u6b66\u5148\u5df2\u8f93\u4e86\u3002\u201d \u3000\u3000\u201c\u8f6c\u773c\u516c\u4e3b\u5df2\u4e8c\u5341\u56db\uff0c\u56fd\u738b\u9053\uff1a\u2018\u4f60\u82e5\u518d\u9009\u4e0d\u51fa\u9a78\u9a6c\uff0c\u5c31\u8be5\u7531\u6211\u4f5c\u4e3b\uff0c\u4e0d\u80fd\u8ba9\u4f60\u628a\u64c2\u53f0 \u957f\u6446\u4e0b\u53bb\u3002\u2019\u516c\u4e3b\u8bf7\u518d\u5bbd\u9650\u767e\u65e5\uff0c\u767e\u65e5\u4e4b\u540e\uff0c\u518d\u4f5c\u5b9a\u593a\u3002\u5b9e\u662f\u516c\u4e3b\u5fc3\u4e2d\u65e9\u6709\u4e3b\u610f\uff0c\u5979\u5fc3\u9ad8\u6c14 \u505a\uff0c\u7edd\u4e0d\u5ac1\u51e1\u592b\u4fd7\u5b50\uff0c\u82e5\u7136\u8fc7\u4e86\u767e\u65e5\uff0c\u8fd8\u9009\u4e0d\u5230\u5982\u610f\u90ce\u541b\uff0c\u90a3\u5c31\u8981\u820d\u8eab\u4e3a\u5c3c\uff0c\u7ec8\u751f\u4e0d\u5ac1\u3002\u201d \u3000\u3000\u201c\u8fc7\u4e86\u4e5d\u5341\u4e5d\u65e5\uff0c\u8fd8\u662f\u65e0\u4eba\u5165\u9009\uff0c\u81f3\u6700\u540e\u4e00\u65e5\uff0c\u516c\u4e3b\u4ea6\u5df2\u7edd\u671b\uff0c\u5ffd\u7136\u6765\u4e86\u4e00\u4e2a\u4e2d\u534e\u7537 \u5b50\uff0c\u6ee1\u9762\u98ce\u5c18\u4e4b\u8272\uff0c\u8bf4\u662f\u8fdc\u9053\u8d76\u6765\uff0c\u4e5e\u6c42\u516c\u4e3b\u4e00\u8bd5\u3002\u6b64\u4eba\u9a91\u672f\u7cbe\u7edd\uff0c\u5c04\u7bad\u767e\u53d1\u767e\u4e2d\uff0c\u80fd\u4e3e\u5343 \u65a4\u77f3\u62c5\uff0c\u53ef\u670d\u5fa1\u56ed\u72ee\u864e\u3002\u79cd\u79cd\u96be\u5173\uff0c\u4e00\u4e00\u901a\u8fc7\uff0c\u6700\u540e\u6bd4\u5251\uff0c\u4e0e\u516c\u4e3b\u4ece\u65e5\u4e2d\u6597\u81f3\u65e5\u66ae\uff0c\u6700\u540e\u4e00 \u5251\u6311\u5f00\u4e86\u516c\u4e3b\u7684\u9762\u7eb1\uff0c\u8d62\u5f97\u5341\u5206\u6f02\u4eae\u3002\u201d \u3000\u3000\u201c\u516c\u4e3b\u8bd5\u4ed6\u6587\u5b66\uff0c\u4ed6\u5bf9\u7b54\u5982\u6d41\uff0c\u5bf9\u5c3c\u6cca\u5c14\u7684\u53e4\u8bd7\u7ecf\u5178\uff0c\u968f\u610f\u5f15\u7528\uff0c\u5982\u6570\u5bb6\u73cd\u3002\u5bf9\u4e2d\u56fd\u7684 \u6587\u5b66\uff0c\u90a3\u5c31\u66f4\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u4ed6\u6240\u89e3\u91ca\u7684\u7ecf\u53f2\u5965\u4e49\uff0c\u8fde\u516c\u4e3b\u4e5f\u95fb\u6240\u672a\u95fb\uff0c\u516c\u4e3b\u5341\u5206\u4f69\u670d\uff0c\u9053\uff1a \u2018\u6700\u540e\u8bd5\u4f60\u4e24\u9898\uff0c\u8003\u8003\u4f60\u7684\u6025\u624d\u3002\u82e5\u7136\u8003\u8bd5\u4e2d\u5f0f\uff0c\u90a3\u4f60\u2026\u2026\uff0c\u8bf4\u7740\u9762\u4e0a\u4e00\u7ea2\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4 \u4e0d\u4e0b\u53bb\uff0c\u90a3\u4e2d\u534e\u7537\u5b50\u4fbf\u7acb\u523b\u8bf7\u5979\u547d\u9898\u2026\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u9648\u5929\u5b87\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u4e2d\u534e\u7537\u5b50\uff0c\u60f3\u5fc5\u5c31 \u662f\u51b0\u5ddd\u5929\u5973\u7684\u7236\u4eb2\u6842\u534e\u751f\u5927\u4fa0\uff0c\u6842\u5927\u4fa0\u5e7c\u627f\u6bcd\u6559\uff0c\u65e0\u602a\u4ed6\u7684\u6587\u624d\u6b66\u827a\uff0c\u90fd\u51fa\u8272\u5f53\u884c\u4e86\uff0c\u4e0d\u77e5 \u6700\u540e\u90a3\u4e24\u9053\u662f\u4ec0\u4e48\u8bd5\u9898\uff1f\u201d \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u534e\u7389\u516c\u4e3b\u51fa\u7684\u4e24\u9053\u8bd5\u9898\uff0c\u7b2c\u4e00\uff0c\u9053\u662f\u8981\u4ed6\u505a\u4e00\u5bf9\u8054\uff0c\u516c\u4e3b\u9053\uff1a\u4e2d\u56fd\u7684\u6587\u5b57\u662f \u5355\u97f3\u5b57\uff0c\u6700\u5947\u5999\u7684\u7279\u8272\u662f\u80fd\u505a\u5bf9\u8054\uff0c\u4f60\u5c31\u5c06\u6211\u7684\u540d\u5b57\u505a\u4e00\u5bf9\u5d4c\u540d\u8054\u5427\u3002\u4ee5\u4e00\u652f\u9999\u7684\u65f6\u523b\u4e3a \u9650\uff0c\u9999\u82e5\u70e7\u5b8c\uff0c\u8fd8\u505a\u4e0d\u51fa\u6765\uff0c\u8fd9\u4e00\u573a\u5c31\u7b97\u5931\u8d25\u4e86\u3002\u90a3\u540d\u53eb\u6842\u534e\u751f\u7684\u4e2d\u56fd\u7537\u4e86\u4e0d\u614c\u4e0d\u5fd9\u7684\u770b\u4e86 \u516c\u4e3b\u4e00\u7528\u826f\uff0c\u9053\uff1a\u8054\u8bed\u6211\u5df2\u6709\u4e86\uff0c\u53ea\u6050\u6709\u5192\u6627\u4e4b\u5904\uff0c\u8bf7\u516c\u4e3b\u89c1\u8c05\u3002\u968f\u5373\u5c06\u5d4c\u6709\u516c\u4e3b\u82b3\u540d\u7684\u5bf9 \u8054\u5199\u51fa\uff0c\u90a3\u8054\u8bed\u662f\uff1a \u3000\u3000\u534e\u5ca9\u5999\u5883\u5055\u51c6\u6e38\uff1f\u770b\u9f99\u53f6\u62c8\u82b1\uff0c\u91ca\u8fe6\u5fae\u7b11\uff1b \u3000\u3000\u7389\u7b1b\u4ed9\u97f3\u9080\u5ba2\u548c\uff0c\u542c\u76f8\u5982\u9f13\u745f\uff0c\u5b50\u664b\u5439\u8427\u3002 \u3000\u3000\u201c\u4e0a\u8054\u5168\u7528\u4f5b\u5178\uff0c\u4e0b\u8054\u5219\u7528\u53f8\u9a6c\u76f8\u5982\u7434\u6311\u5353\u6587\u541b\u4e0e\u5b50\u664b\u5439\u9636\u5f15\u51e4\u6c42\u79e6\u7a46\u516c\u5973\u513f\u5f04\u7389\u4e3a\u59bb \u7684\u5178\u6545\uff0c\u4e0a\u8054\u4e0b\u8054\u90fd\u542b\u6709\u6c42\u5076\u4e4b\u610f\u3002\u8054\u8bed\u5199\u5b8c\uff0c\u90a3\u53ea\u9999\u53ea\u70e7\u4e86\u4e09\u5206\u4e4b\u4e00\uff0c\u516c\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u4fbf \u51fa\u7b2c\u4e8c\u9053\u8bd5\u9898\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u63d2\u53e3\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u51b0\u5ddd\u5929\u5973\u8fd9\u4e48\u6b22\u559c\u505a\u5d4c\u540d\u8054\uff0c\u539f\u6765\u662f\u627f\u7ee7\u7236\u98ce\u3002\u201d\u5e7d\u840d\u9053\uff1a \u201c\u90a3\u767d\u8863\u5c11\u5e74\u5230\u51b0\u5bab\u7684\u60c5\u666f\uff0c\u4e5f\u5f88\u50cf\u6842\u534e\u751f\u5411\u534e\u7389\u516c\u4e3b\u6c42\u5a5a\u7684\u60c5\u666f\u5462\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u7b2c\u4e8c \u9053\u8bd5\u9898\u53c8\u662f\u4ec0\u4e48\uff1f\u4e0d\u8981\u591a\u8bf4\u95f2\u8bdd\uff0c\u5148\u8bf4\u6545\u4e8b\u5427\u3002\u201d \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u6545\u4e8b\u4e4b\u4e2d\u53c8\u6709\u6545\u4e8b\uff0c\u516c\u4e3b\u7684\u7b2c\u4e8c\u9053\u9898\u662f\u5148\u8bf4\u4e00\u4e2a\u6545\u4e8b\uff0c\u8fd9\u6545\u4e8b\u6ca1\u6709\u7ed3\u5c40\uff0c\u53ef \u4ee5\u968f\u4f60\u6b22\u559c\uff0c\u5c06\u5b83\u53d8\u6210\u559c\u5267\u6216\u8005\u60b2\u5267\uff0c\u516c\u4e3b\u8981\u6842\u534e\u751f\u4e3a\u8fd9\u4e2a\u6545\u4e8b\u5199\u7ed3\u5c40\uff0c\u4ee5\u8003\u4ed6\u7684\u6025\u624d\u548c\u673a \u667a\u3002 \u3000\u3000\u201c\u8fd9\u6545\u4e8b\u8bf4\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6709\u4e00\u4e2a\u516c\u4e3b\u7231\u4e0a\u4e00\u4e2a\u82f1\u4fca\u7684\u6b66\u58eb\uff0c\u4e0d\u6599\u8fd9\u6b66\u58eb\u6697\u4e2d\u5374\u548c\u5979\u7684 \u5bab\u5973\u52fe\u642d\uff0c\u79c1\u60c5\u7737\u604b\uff0c\u7ed9\u516c\u4e3b\u649e\u7834\uff0c\u4e00\u6c14\u4e4b\u4e0b\uff0c\u4fbf\u7980\u544a\u7236\u738b\u3002\u6b66\u58eb\u52fe\u5f15\u5bab\u5973\uff0c\u8fd9\u7f6a\u540d\u975e\u540c\u5c0f \u53ef\uff0c\u4f9d\u5f8b\u8981\u5904\u4ee5\u4e25\u5211\u3002 \u3000\u3000\u201c\u53ef\u662f\u8fd9\u56fd\u5bb6\u7684\u5211\u7f5a\u751a\u4e3a\u5947\u602a\uff0c\u4ed6\u4eec\u76f8\u4fe1\u5929\u4e0a\u6709\u4e00\u4e2a\u771f\u795e\uff0c\u4e3b\u5bb0\u4eba\u7684\u547d\u8fd0\uff0c\u72af\u4eba\u6709\u7f6a\u65e0 \u7f6a\uff0c\u4e5f\u90fd\u7531\u795e\u6765\u51b3\u5b9a\u3002\u529e\u6cd5\u662f\u5c06\u72af\u4eba\u653e\u5728\u4e00\u4e2a\u5e7f\u573a\u4e2d\uff0c\u5e7f\u573a\u7684\u5de6\u53f3\u4e24\u8fb9\uff0c\u5404\u6709\u4e00\u9053\u4fa7\u95e8\uff0c\u4e00 \u9053\u4fa7\u95e8\u5185\u4e2d\u6709\u4e00\u53ea\u51f6\u731b\u7684\u997f\u72ee\uff0c\u72af\u4eba\u8d70\u5165\u95e8\u5185\uff0c\u5b9a\u5fc5\u7ed9\u72ee\u6495\u788e\uff0c\u5f53\u4f5c\u70b9\u5fc3\uff1b\u53e6\u4e00\u9053\u4fa7\u95e8\u901a\u5411 \u5916\u8fb9\uff0c\u72af\u4eba\u82e5\u8d70\u5165\u6b64\u5185\uff0c\u5219\u53ef\u83b7\u5f97\u81ea\u7531\u3002\u6240\u6709\u7f6a\u540d\u4e00\u7b14\u52fe\u9500\uff0c\u56e0\u4e3a\u90a3\u662f\u771f\u795e\u7ed9\u4ed6\u964d\u798f\uff0c\u80fd\u5f97 \u5230\u771f\u795e\u964d\u798f\u7684\u5c31\u4e0d\u4f1a\u662f\u574f\u4eba\u3002 \u3000\u3000\u201c\u56fd\u738b\u4e0d\u77e5\u9053\u516c\u4e3b\u6697\u604b\u6b66\u58eb\uff0c\u53c8\u7d20\u6765\u6b22\u559c\u8fd9\u6b66\u58eb\uff0c\u4fbf\u7d22\u6027\u66f4\u52a0\u4ee5\u6069\u5178\uff0c\u4e00\u9053\u95e8\u4e2d\u85cf\u6709\u4e00 \u53ea\u72ee\uff0c\u53e6\u4e00\u9053\u95e8\u4e2d\u5219\u85cf\u7740\u90a3\u4e2a\u5bab\u5973\uff0c\u82e5\u7136\u6b66\u58eb\u8d70\u5165\u85cf\u6709\u997f\u72ee\u7684\u4fa7\u95e8\uff0c\u90a3\u5f53\u7136\u4e0d\u5fc5\u8bf4\uff0c\u90a3\u662f\u771f \u795e\u4e5f\u8ba4\u4e3a\u4ed6\u6709\u7f6a\uff0c\u5e94\u8be5\u5145\u4f5c\u72ee\u5b50\u7684\u70b9\u5fc3\uff1b\u82e5\u7136\u6b66\u58eb\u8d70\u5165\u85cf\u6709\u5bab\u5973\u7684\u4fa7\u95e8\uff0c\u90a3\u4e48\u8fd9\u6b66\u58eb\u975e\u4f46\u6ca1 \u7f6a\uff0c\u8fd8\u53ef\u4ee5\u5f97\u90a3\u5bab\u5973\u4e3a\u59bb\u3002\u201d \u3000\u3000\u201c\u51b3\u5b9a\u8fd9\u6b66\u58eb\u547d\u8fd0\u4e4b\u65e5\uff0c\u516c\u4e3b\u4e5f\u5728\u573a\u89c2\u770b\uff0c\u770b\u53f0\u5c31\u5728\u4e24\u9053\u4fa7\u95e8\u7684\u4e2d\u95f4\u3002\u6b66\u58eb\u8d70\u8fc7\u770b\u53f0\uff0c \u62ac\u5934\u76ef\u7740\u516c\u4e3b\uff0c\u773c\u4e2d\u9732\u51fa\u54c0\u6c42\u9976\u6055\u7684\u795e\u60c5\u3002\u516c\u4e3b\u662f\u77e5\u9053\u4fa7\u95e8\u4e2d\u7684\u79d8\u5bc6\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u65f6\u53ea\u8981\u516c\u4e3b\u4e00\u6307\uff0c\u5c31\u53ef\u4ee5\u51b3\u5b9a\u8fd9\u6b66\u58eb\u7684\u751f\u6b7b\u547d\u8fd0\u3002\u662f\u5c06\u4ed6\u6307\u5411\u85cf\u6709\u72ee\u4e8e\u7684\u4fa7\u95e8\u5462\uff1f \u8fd8\u662f\u5c06\u4ed6\u6307\u5411\u85cf\u6709\u5bab\u5973\u7684\u4fa7\u95e8\u5462\uff1f\u516c\u4e3b\u60f3\u8d77\u4ed6\u628a\u81ea\u5df1\u7684\u60c5\u610f\u4ed8\u4e4b\u6d41\u6c34\uff0c\u5374\u52fe\u642d\u4e0a\u81ea\u5df1\u7684\u5bab \u5973\uff0c\u5992\u5fcc\u4e4b\u706b\u65e0\u53ef\u6291\u6b62\uff0c\u8981\u8ba9\u4ed6\u4e0e\u5bab\u5973\u79f0\u5fc3\u5982\u610f\uff0c\u7ed3\u4e3a\u592b\u59bb\uff0c\u90a3\u662f\u4e00\u4e07\u4e2a\u4e0d\u80fd\uff01\u53ef\u662f\u5979\u6781\u7231 \u8fd9\u4e2a\u6b66\u58eb\uff0c\u82e5\u8bf4\u8981\u8ba9\u4ed6\u7ed9\u997f\u72ee\u6495\u88c2\u3002\u5145\u4f5c\u70b9\u5fc3\uff0c\u90a3\u53c8\u662f\u4e00\u4e07\u4e2a\u4e0d\u5fcd\uff01\u8fd9\u5239\u90a3\u95ee\uff0c\u65e0\u6570\u5e7b\u8c61\u6cdb \u4e0a\u516c\u4e3b\u5fc3\u5934\uff0c\u4e00\u5ffd\u513f\u73b0\u51fa\u6b66\u58eb\u4e0e\u5bab\u5973\u914d\u5408\u4e4b\u540e\uff0c\u537f\u537f\u6211\u6211\u6069\u6069\u7231\u7231\u7684\u60c5\u666f\uff1b\u4e00\u5ffd\u513f\u73b0\u51fa\u6b66\u58eb \u7ed9\u997f\u72ee\u6495\u88c2\u3001\u9c9c\u8840\u6dcb\u6f13\u7684\u60e8\u8c61\u3002\u4e00\u62ac\u5934\u53c8\u770b\u89c1\u6b66\u58eb\u5145\u6ee1\u54c0\u6c42\u7684\u773c\u5149\uff0c\u6b66\u58eb\u5373\u5c06\u8d70\u8fc7\u770b\u53f0\uff0c\u65f6 \u673a\u95f4\u4e0d\u5bb9\u53d1\uff0c\u516c\u4e3b\u8981\u5c06\u4ed6\u6307\u5411\u54ea\u4e00\u8fb9\u5462\uff1f\u662f\u613f\u610f\u89c1\u4ed6\u4e0e\u60c5\u654c\u7ed3\u5a5a\uff1f\u8fd8\u662f\u8ba9\u4ed6\u7ed9\u997f\u72ee\u5403\u6389\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u542c\u5f97\u5165\u795e\uff0c\u5fc3\u4e2d\u66ff\u90a3\u516c\u4e3b\u8bbe\u60f3\uff0c\u4e5f\u5b9e\u662f\u96be\u4ee5\u9009\u62e9\u3002\u53ea\u542c\u5f97\u5e7d\u840d\u7b11\u4e86\u4e00\u7b11\uff0c\u7eed\u9053\uff1a \u201c\u5f53\u65f6\u534e\u7389\u516c\u4e3b\u4e5f\u5c31\u662f\u8fd9\u6837\u95ee\u6842\u534e\u751f\uff1a\u82e5\u7136\u4f60\u662f\u90a3\u4f4d\u516c\u4e3b\uff0c\u4f60\u5c06\u6b66\u58eb\u6307\u5411\u90a3\u4e00\u8fb9\uff0c\u7b54\u9898\u8981\u5408 \u534e\u7389\u516c\u4e3b\u7684\u5fc3\u610f\uff0c\u5979\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\uff0c\u51b3\u5b9a\u4f60\u7684\u7b54\u9898\u662f\u5bf9\u8fd8\u662f\u9519\uff01\u201d \u3000\u3000\u201c\u8fd9\u8bd5\u9898\u5b9e\u662f\u96be\u5230\u6781\u70b9\uff0c\u65e2\u8981\u63e3\u6469\u6545\u4e8b\u4e2d\u90a3\u4f4d\u516c\u4e3b\u7684\u5fc3\u610f\uff0c\u53c8\u8981\u63e3\u6469\u534e\u7389\u516c\u4e3b\u7684\u5fc3\u610f\u3002 \u4e0d\u8bba\u5c06\u6b66\u58eb\u6307\u5411\u90a3\u4e00\u8fb9\uff0c\u90fd\u53ef\u80fd\u7ed9\u534e\u7389\u516c\u4e3b\u8bf4\u4ed6\u4e0d\u61c2\u7231\u60c5\uff0c\u56e0\u4e3a\u5bf9\u7231\u60c5\u7684\u770b\u6cd5\uff0c\u672c\u5c31\u56e0\u4eba\u800c \u5f02\u3002\u50cf\u6545\u4e8b\u4e2d\u7684\u516c\u4e3b\uff0c\u82e5\u5c06\u6b66\u58eb\u6307\u5411\u85cf\u6709\u72ee\u5b50\u7684\u90a3\u4e00\u8fb9\uff0c\u90a3\u53ef\u4ee5\u89e3\u91ca\u4e3a\u56e0\u7231\u751f\u5992\uff0c\u7231\u4e4b\u6c72\u4e5f \u5c31\u6068\u4e4b\u6781\uff0c\u6068\u4e4b\u6781\u4e5f\u5c31\u662f\u7231\u4e4b\u6781\uff1b\u82e5\u5c06\u90a3\u6b66\u58eb\u6307\u5411\u85cf\u6709\u5bab\u5973\u7684\u90a3\u4e00\u8fb9\uff0c\u90a3\u53ef\u4ee5\u89e3\u91ca\u4e3a\u56e0\u7231\u751f \u6055\uff0c\u7231\u5230\u6df1\u65f6\uff0c\u4e00\u5207\u4e3a\u7231\u4eba\u8bbe\u60f3\uff0c\u90a3\u4e48\u727a\u7272\u81ea\u5df1\u7684\u5e78\u798f\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u53ef\u662f\u534e\u7389\u516c\u4e3b\u7684\u60f3\u6cd5 \u662f\u600e\u6837\u5462\uff1f \u3000\u3000\u201c\u6842\u534e\u751f\u60f3\u4e86\u4e00\u4f1a\uff0c\u95ee\u534e\u7389\u516c\u4e3b\u9053\uff1a\u2018\u6545\u4e8b\u4e2d\u5047\u8bbe\u7684\u90a3\u4f4d\u516c\u4e3b\u662f\u4e1c\u65b9\u53e4\u56fd\u7684\u516c\u4e3b\u8fd8\u662f\u897f \u65b9\u53e4\u56fd\u7684\u516c\u4e3b\uff1f\u2019\u8fd9\u6545\u4e8b\u672c\u662f\u6b27\u6d32\u7684\u6545\u4e8b\uff0c\u4f20\u5230\u4e1c\u65b9\uff0c\u9042\u4e5f\u4ea7\u751f\u4e86\u8bb8\u591a\u5927\u540c\u5c0f\u5f02\u7684\u6545\u4e8b\uff0c\u7ed9 \u8bf4\u4e66\u4eba\u4f5c\u4e3a\u9898\u6750\uff0c\u6842\u534e\u751f\u672c\u6765\u77e5\u9053\u7684\uff0c\u4f46\u4ed6\u5374\u660e\u77e5\u6545\u95ee\u3002 \u3000\u3000\u201c\u534e\u7389\u516c\u4e3b\u4e0d\u660e\u5176\u610f\uff0c\u53cd\u95ee\u9053\uff1a\u2018\u662f\u4e1c\u65b9\u53e4\u56fd\u7684\u600e\u4e48\u6837\uff1f\u662f\u897f\u65b9\u53e4\u56fd\u7684\u53c8\u600e\u4e48\u6837\uff1f\u2019\u6842 \u534e\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\uff0c\u82e5\u662f\u4e1c\u65b9\u53e4\u56fd\u7684\u516c\u4e3b\uff0c\u90a3\u5c31\u5c06\u6b66\u58eb\u6307\u5411\u85cf\u6709\u5bab\u5973\u7684\u90a3\u4e00\u8fb9\uff1b\u82e5\u662f\u897f \u65b9\u53e4\u56fd\u7684\u516c\u4e3b\uff0c\u90a3\u5c31\u5c06\u6b66\u58eb\u6307\u5411\u6709\u72ee\u5b50\u7684\u90a3\u4e00\u8fb9\uff0c\u4e1c\u65b9\u56fd\u5bb6\u4e3b\u5f20\u5bbd\u6055\u4e4b\u9053\uff0c\u5973\u5b50\u66f4\u662f\u4ec1\u6148\uff0c \u5341\u4e5d\u4e0d\u5fcd\u89c1\u60c5\u4eba\u7ed9\u997f\u72ee\u6495\u88c2\uff1b\u897f\u65b9\u7684\u5973\u5b50\u5bf9\u7231\u60c5\u7740\u91cd\u2018\u72ec\u5360\u2019\uff0c\u897f\u8c1a\u6709\u4e91\uff1a\u2018\u7231\u60c5\u6709\u5982\u773c \u775b\uff0c\u4e0d\u80fd\u5bb9\u534a\u7c92\u7802\u5b50\u3002\u2019\u6240\u4ee5\u82e5\u662f\u897f\u65b9\u53e4\u56fd\u7684\u516c\u4e3b\uff0c\u5341\u4e5d\u5b81\u613f\u60c5\u4eba\u8ba9\u997f\u72ee\u5403\u6389\uff0c\u4e5f\u4e0d\u613f\u4ed6\u6295 \u5165\u522b\u4eba\u6000\u62b1\u3002\u4f46\u5047\u82e5\u90a3\u6b66\u58eb\u662f\u4e2d\u56fd\u4eba\u5462\uff0c\u4ed6\u65e9\u5c31\u4f1a\u5bdf\u89c9\u516c\u4e3b\u7231\u4ed6\uff0c\u8fd9\u4e8b\u60c5\u6839\u672c\u5c31\u4e0d\u4f1a\u53d1\u751f \u5566\uff01\u201d \u3000\u3000\u201c\u8fd9\u7b54\u590d\u751a\u662f\u6ed1\u5934\uff0c\u4f46\u534e\u7389\u516c\u4e3b\u5fc3\u610f\u5176\u5b9e\u4e0d\u5fc5\u8d39\u795e\u731c\u6d4b\uff0c\u4e0d\u8bba\u6842\u534e\u751f\u600e\u6837\u56de\u7b54\uff0c\u6211\u4eec\u90fd \u9884\u6599\u5979\u5fc5\u7136\u6ee1\u610f\u3002 \u3000\u3000\u201c\u4e8e\u662f\u516c\u4e3b\u9009\u5b9a\u6842\u534e\u751f\u4e3a\u9a78\u9a6c\uff0c\u56fd\u738b\u6b22\u559c\u65e0\u9650\uff0c\u4e0b\u5ac1\u4e4b\u65e5\u3002\u5168\u56fd\u653e\u5047\u4e09\u5929\uff0c\u5c3d\u60c5\u6b22\u4e50\u3002 \u7b2c\u4e8c\u5e74\u516c\u4e3b\u751f\u4e0b\u4e00\u4e2a\u5973\u513f\uff0c\u9a78\u9a6c\u7ed9\u5979\u53d6\u540d\u51b0\u5a25\uff0c\u5979\u4fbf\u662f\u4eca\u65e5\u7684\u51b0\u5ddd\u5929\u5973\u3002\u56fd\u738b\u65e0\u5b50\uff0c\u53ea\u6709\u534e \u7389\u516c\u4e3b\u4e00\u4e2a\u5973\u513f\uff0c\u6240\u4ee5\u5916\u5b59\u5973\u51b0\u5a25\uff0c\u4e5f\u6709\u2018\u516c\u4e3b\u2019\u7684\u5c01\u53f7\u3002\u201d \u3000\u3000\u201c\u4e24\u4eba\u5a5a\u540e\uff0c\u751f\u6d3b\u5341\u5206\u5e78\u798f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u8fc7\u4e86\u4e94\u5e74\u3002\u56fd\u738b\u5e74\u8001\u4f53\u8870\uff0c\u4e3a\u4e86\u55e3\u541b\u7684\u95ee\u9898\uff0c\u9042 \u5f15\u8d77\u70e6\u607c\u3002 \u3000\u3000\u201c\u672c\u6765\u4f9d\u7167\u897f\u6b27\u4e0e\u4e2d\u4e9a\u5404\u56fd\u7684\u89c4\u77e9\uff0c\u5973\u513f\u4ea6\u53ef\u7ee7\u627f\u738b\u4f4d\uff0c\u82e5\u4f9d\u7167\u4e2d\u56fd\u7684\u4e60\u60ef\uff0c\u5219\u53ea\u6709\u7537 \u513f\u53ef\u4ee5\u4e3a\u541b\uff0c\u5973\u513f\u65ad\u65ad\u4e0d\u80fd\u4f20\u4f4d\u3002\u5c3c\u6cca\u5c14\u6c49\u5316\u65e5\u6df1\uff0c\u56fd\u4e2d\u5bf9\u4e8e\u738b\u4f4d\u7ee7\u627f\u7684\u95ee\u9898\u9042\u5206\u4e3a\u4e24\u6d3e\uff0c \u4e00\u6d3e\u4e3b\u5f20\u62e5\u7acb\u534e\u7389\u516c\u4e3b\uff0c\u53e6\u4e00\u6d3e\u5219\u4e3b\u5f20\u62e5\u7acb\u56fd\u738b\u7684\u4f84\u5b50\u3002\u56fd\u738b\u7684\u4f84\u5b50\u89ca\u89ce\u7687\u4f4d\u5df2\u4e45\uff0c\u6276\u690d\u515a \u7fbd\uff0c\u6697\u80b2\u6b7b\u58eb\uff0c\u5bf9\u534e\u7389\u516c\u4e3b\u751a\u4e3a\u5992\u5fcc\uff0c\u4e24\u6d3e\u62e5\u7acb\u4e4b\u4e89\u65e5\u8d8b\u6fc0\u70c8\uff0c\u5e73\u9759\u7684\u5c0f\u56fd\uff0c\u9042\u8574\u917f\u4e86\u6781\u5927 \u7684\u98ce\u66b4\u3002\u201d \u3000\u3000\u201c\u534e\u7389\u516c\u4e3b\u4e0d\u5fcd\u89c1\u81ea\u5df1\u7684\u56fd\u5bb6\u9677\u4e8e\u52a8\u4e71\uff0c\u9042\u548c\u9644\u9a6c\u5546\u8bae\uff0c\u9a78\u9a6c\u529d\u5979\u653e\u5f03\u738b\u4f4d\uff0c\u907f\u5165\u897f \u85cf\uff0c\u4e24\u4eba\u5408\u4fee\u4e0a\u4e58\u6b66\u529f\uff0c\u5c06\u4e2d\u571f\u5251\u6cd5\u4e0e\u897f\u57df\u5251\u6cd5\u6eb6\u4e8e\u4e00\u7089\uff0c\u522b\u521b\u65b0\u6d3e\u3002\u534e\u7389\u516c\u4e3b\u4e5f\u89c9\u5f97\u4e0e\u9a78 \u9a6c\u505a\u4e00\u5bf9\u795e\u4ed9\u7737\u5c5e\uff0c\u6bd4\u505a\u5973\u738b\u8981\u5e78\u798f\u5f97\u591a\uff0c\u4e8e\u662f\u9042\u7559\u4e66\u7236\u738b\uff0c\u6084\u6084\u8d70\u51fa\u6df1\u5bab\uff0c\u6765\u5230\u897f\u85cf\u3002\u516c \u4e3b\u6781\u5f97\u4eba\u5fc3\uff0c\u5fc3\u8179\u5bab\u5973\u6570\u5341\u4eba\uff0c\u820d\u4e0d\u5f97\u5979\uff0c\u4e00\u5b9a\u8981\u8ddf\u5979\u540c\u884c\uff0c\u5230\u4e86\u897f\u85cf\u4e4b\u540e\uff0c\u4ed7\u7740\u9a78\u9a6c\u4e0e\u516c \u4e3b\u8d85\u51e1\u5165\u80dc\u7684\u6b66\u529f\uff0c\u9042\u9009\u5b9a\u4e98\u53e4\u4ee5\u6765\u4eba\u8ff9\u7f55\u5230\u7684\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u4f5c\u4f4f\u5740\uff0c\u5728\u5929\u6e56\u4e4b\u4e0a\uff0c\u5efa\u8d77\u51b0 \u5bab\uff0c\u7ecf\u8fc7\u5341\u591a\u5e74\u7684\u523b\u610f\u7ecf\u8425\uff0c\u9020\u6210\u4e86\u4eca\u65e5\u7684\u7f8e\u666f\u3002\u5efa\u4e86\u51b0\u5bab\u4e4b\u540e\uff0c\u8001\u4e00\u8f88\u7684\u5bab\u5973\u53c8\u9646\u7eed\u63a5\u5f15 \u4e86\u4eb2\u621a\u4e2d\u7684\u82e5\u5e72\u5e7c\u5973\u4e0a\u5c71\uff0c\u670d\u4f8d\u51b0\u5a25\u5c0f\u516c\u4e3b\uff0c\u8fd9\u4e9b\u51b0\u5bab\u4f8d\u5973\u4e0e\u51b0\u5ddd\u5929\u5973\u4e00\u540c\u957f\u5927\uff0c\u4e2a\u4e2a\u90fd\u5b66 \u5f97\u4e00\u8eab\u672c\u9886\u3002\u201d \u3000\u3000\u201c\u516c\u4e3b\u51fa\u8d70\u7684\u7b2c\u4e09\u5e74\uff0c\u56fd\u738b\u75c5\u6545\u3002\u4f84\u513f\u7ee7\u627f\u738b\u4f4d\uff0c\u542c\u8bf4\u5f53\u65f6\u4ed6\u5230\u5904\u641c\u7d22\u516c\u4e3b\u7684\u4e0b\u843d\uff0c\u5f53 \u7136\u641c\u7d22\u4e0d\u5230\uff0c\u65e5\u4e45\u4e5f\u5c31\u6de1\u5fd8\u3002\u534e\u7389\u516c\u4e3b\u907f\u5c45\u5927\u6e56\u4e4b\u540e\uff0c\u5bf9\u56fd\u4e8b\u5fc3\u7070\u610f\u51b7\uff0c\u53c8\u77e5\u7ee7\u4f4d\u4e3a\u738b\u7684\u5802 \u5144\uff0c\u66b4\u8650\u9a84\u5962\uff0c\u66f4\u4e0d\u613f\u91cd\u5c65\u6545\u571f\u3002\u534e\u7389\u516c\u4e3b\u6bd4\u6842\u9a78\u9a6c\u5148\u6b7b\uff0c\u4e34\u6b7b\u4e4b\u65f6\uff0c\u4f20\u4e0b\u9057\u547d\uff0c\u4e0d\u51c6\u51b0\u5bab \u4eba\u7b49\u4e0b\u5c71\uff0c\u9664\u975e\u51b0\u5cf0\u5012\u584c\uff0c\u5426\u5219\u51b0\u5a25\u5c0f\u516c\u4e3b\u4e5f\u5c06\u7ec8\u8001\u4ed9\u5c71\uff0c\u4e0d\u80fd\u518d\u5c65\u5c18\u4e16\u3002\u201d \u3000\u3000\u201c\u516c\u4e3b\u6b7b\u540e\uff0c\u6842\u9a78\u9a6c\u4e3a\u5979\u7acb\u5e99\u5efa\u50cf\uff0c\u4eff\u5c3c\u6cca\u5c14\u795e\u5e99\u7684\u5f0f\u6837\uff0c\u5e76\u5728\u795e\u5e99\u56db\u58c1\uff0c\u523b\u4e0b\u4ed6\u592b\u5987 \u5408\u521b\u7684\u62f3\u7ecf\u5251\u6cd5\uff0c\u9664\u51b0\u5ddd\u5929\u5973\u5916\uff0c\u4f59\u4eba\u4e0d\u51c6\u5165\u5185\uff0c\u6210\u4e3a\u5bab\u4e2d\u7981\u5730\u3002\u534e\u7389\u516c\u4e3b\u6b7b\u540e\u7684\u7b2c\u4e8c\u5e74\uff0c \u6842\u9a78\u9a6c\u4e5f\u76f8\u7ee7\u901d\u4e16\uff0c\u51b0\u5ddd\u5929\u5973\u6210\u4e86\u51b0\u5bab\u7684\u4e3b\u4eba\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u9189\u5fc3\u6c49\u5b66\uff0c\u6240\u4ee5\u7ed9\u5bab\u4e2d\u7684\u4f8d\u5973\uff0c \u90fd\u53d6\u4e86\u4e2d\u56fd\u5973\u5b50\u5e38\u89c1\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u51b0\u5bab\u4f8d\u5973\u5e7d\u840d\u5c06\u6545\u4e8b\u8bf4\u5b8c\u4e4b\u540e\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u597d\u542c\u5417\uff1f\u201d\u6708\u4eae\u5347\u81f3\u4e2d\u5929\uff0c\u5df2\u662f\u5348 \u591c\u65f6\u5206\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u542c\u5f97\u5fc3\u795e\u4ff1\u9189\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u4e5f\u8fd8\u6ca1\u6709\u7ed3\u5c40\uff0c\u53ef\u4ee5\u559c\u5267\u6536\u573a\uff0c\u4e5f\u53ef\u4ee5\u60b2\u5267\u7ed3 \u675f\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5f02\u56fd\u60c5\u9e33\uff0c\u795e\u4ed9\u7737\u5c5e\uff0c\u8fd9\u6545\u4e8b\u7f8e\u6781\u4e86\u3002\u4f55\u51b5\u8fd9\u5bf9\u795e \u4ed9\u4f34\u4fa3\u8fd8\u6709\u4e00\u4f4d\u771f\u7684\u7f8e\u82e5\u5929\u4ed9\u7684\u5973\u513f\uff0c\u6211\u8bf4\u5440\uff0c\u82e5\u7136\u4ed6\u4eec\u7684\u5973\u513f\u2014\u2014\u51b0\u5ddd\u5929\u5973\uff0c\u4ed6\u65e5\u82e5\u4e0e\u90a3 \u767d\u8863\u5c11\u5e74\uff0c\u4e5f\u50cf\u4ed6\u7684\u7236\u6bcd\u4e00\u6837\uff0c\u7ed3\u4e3a\u795e\u4ed9\u7737\u5c5e\uff0c\u90a3\u5c31\u662f\u559c\u5267\u6536\u573a\uff1b\u82e5\u7136\u51b0\u5ddd\u5929\u5973\u907f\u4e0d\u8fc7\u90a3\u573a \u707e\u96be\uff0c\u4e27\u8eab\u51b0\u7a9f\uff0c\u90a3\u5c31\u662f\u60b2\u5267\u7ed3\u675f\u4e86\u3002\u201d\u5e7d\u840d\u5fd9\u9053\uff1a\u201c\u4e00\u5b9a\u80fd\u907f\u8fc7\u7684\u3002\u4e00\u5b9a\u80fd\u907f\u8fc7\u7684\uff01\u201d\u9648 \u5929\u5b87\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\uff01\u201d\u62ac\u9996\u671b\u5929\uff0c\u6708\u534e\u5982\u7ec3\uff0c\u51b0\u8f6e\u6b63\u6ee1\uff0c\u9762\u5bf9\u7389\u4eba\uff0c\u731b\u7136\u60f3\u8d77\u829d\u5a1c\uff0c\u81ea\u5df1 \u4e0e\u829d\u5a1c\u7684\u7ed3\u5c40\uff0c\u4e5f\u4e0d\u77e5\u662f\u60b2\u5267\u8fd8\u662f\u559c\u5267\u3002 \u3000\u3000\u9648\u5929\u5b87\u5fc3\u5934\u6005\u6005\uff0c\u826f\u4e45\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u5e7d\u840d\u5ae3\u7136\u4e00\u7b11\uff0c\u6233\u4ed6\u989d\u89d2\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u60f3\u4e9b\u4ec0 \u4e48\u5440\uff1f\u201d\u5ffd\u89c1\u9648\u5929\u5b87\u9762\u8272\u6709\u5f02\uff0c\u4f3c\u662f\u4fa7\u8033\u503e\u542c\u4ec0\u4e48\uff0c\u5e7d\u840d\u51dd\u795e\u5bdf\u542c\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u6709\u4eba\u5411\u8fd9\u8fb9 \u6765\u3002\u201d\u4e24\u4eba\u95ea\u8eab\u5ca9\u77f3\u4e4b\u540e\uff0c\u53ea\u89c1\u51e0\u6761\u9ed1\u5f71\u76f8\u7ee7\u5954\u6765\uff0c\u4e1c\u8fb9\u6709\u4eba\u62cd\u4e86\u4e24\u4e0b\u624b\u638c\uff0c\u897f\u8fb9\u4e5f\u6709\u4eba\u56de \u4e86\u4e24\u4e0b\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u54b1\u4eec\u7a9c\u4e0a\u9ad8\u5904\uff0c\u83ab\u8981\u7ed9\u4ed6\u4eec\u53d1\u73b0\u3002\u4e14\u770b\u770b\u8fd9\u5e2e\u4eba\u662f\u4ec0\u4e48\u8def\u9053\uff1f\u201d\u4e24\u4eba \u90fd\u662f\u4e0a\u4e0a\u8f7b\u529f\uff0c\u65bd\u5c55\u8d77\u6765\uff0c\u6377\u903e\u733f\u7334\uff0c\u6500\u4e0a\u534a\u5c71\uff0c\u4ecd\u7136\u9009\u4e86\u4e00\u5904\u6709\u5229\u7684\u5730\u5f62\uff0c\u85cf\u8eab\u5728\u4e00\u5757\u51f8 \u51fa\u6765\u7684\u5ca9\u77f3\u4e4b\u540e\uff0c\u51ed\u7c4d\u6708\u5149\uff0c\u53ef\u4ee5\u5c06\u4e0b\u9762\u4fef\u77b0\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u9ed1\u5f71\u76f8\u7ee7\u5954\u81f3\uff0c\u5c31\u5750\u5728\u9002\u624d\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u8c08\u8bdd\u7684\u5730\u65b9\uff0c\u9996\u5c3e\u76f8\u63a5\uff0c\u5750\u6210\u4e00\u4e2a\u5706\u5708\u3002\u9648\u5929 \u5b87\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5927\u7ea6\u662f\u4ec0\u4e48\u5e2e\u4f1a\u805a\u96c6\uff0c\u4e0d\u662f\u51b2\u7740\u6211\u4eec\u6765\u7684\u3002\u201d\u9648\u5929\u5b87\u542c\u8fc7\u94c1\u62d0\u4ed9\u8bb2\u8ff0\u7684\u6c5f\u6e56 \u5e38\u8bc6\uff0c\u6240\u4ee5\u6bd4\u5e7d\u840d\u77e5\u9053\u7684\u591a\u3002\u5e7d\u840d\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90a3\u513f\u4ee5\u661f\u671f\u7eaa\u65e5\uff0c\u4e03\u65e5\u4e00\u5468\uff0c\u4ece\u6b27\u6d32\u6765 \u7684\u5ba2\u5546\uff0c\u5e26\u6765\u4e00\u6837\u8ff7\u4fe1\uff0c\u8bf4\u662f\u661f\u671f\u4e94\u53c8\u517c\u6709\u5341\u4e09\u7684\uff0c\u4e3b\u4eba\u51f6\u5146\u3002\u4f60\u770b\u4e0b\u9762\u6b63\u662f\u5341\u4e09\u4e2a\u4eba\uff0c\u6211 \u8bb0\u5f97\u4eca\u65e5\u53c8\u6b63\u662f\u661f\u671f\u4e94\u3002\u201d\u9648\u5929\u5b87\u4e0d\u89c9\u5931\u7b11\uff0c\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u4e2a\u9053\u7406\u3002\u5373\u6709\u51f6\u4e8b\uff0c\u4ea6\u662f\u5076 \u5408\u3002\u201d\u542c\u5e7d\u840d\u8c08\u8d77\u65e5\u5b50\uff0c\u5ffd\u800c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\uff0c\u6211\u4e0d\u662f\u95ee\u661f\u671f\uff0c\u6211\u662f\u95ee \u6c49\u5386\u3002\u201d \u3000\u3000\u5e7d\u840d\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u5fc3\u8bb0\u65e5\u5b50\uff0c\u4f60\u4eec\u6c49\u4eba\u7684\u5386\u6cd5\u53c8\u6781\u4e4b\u9ebb\u70e6\uff0c\u6709\u65f6\u6708\u5927\uff0c\u6709\u65f6\u6708 \u5c0f\uff0c\u6781\u4e0d\u597d\u8bb0\uff0c\u53ea\u662f\u6211\u6628\u665a\u548c\u4eca\u65e9\u90fd\u89c1\u57ce\u4e2d\u6709\u8bb8\u591a\u6c49\u4eba\u8d76\u5e02\u96c6\u4e70\u4e1c\u897f\uff0c\u542c\u4ed6\u4eec\u8bf4\u662f\u51c6\u5907\u8fc7\u4e2d \u79cb\u8282\u7684\u3002\u201d\u897f\u85cf\u662f\u6c49\u85cf\u4e24\u79cd\u5386\u6cd5\u517c\u7528\uff0c\u5dee\u5f02\u751a\u5927\uff0c\u6240\u4ee5\u8bb0\u4e0d\u8d77\u6c49\u5386\uff0c\u751a\u5c5e\u5e73\u5e38\u3002\u9648\u5929\u5b87\u7b11 \u9053\uff1a\u201c\u4f60\u66fe\u968f\u5c0f\u516c\u4e3b\u8bfb\u8fc7\u8bb8\u591a\u6c49\u5386\uff0c\u96be\u9053\u4e0d\u77e5\u9053\u4e2d\u79cb\u4f73\u8282\u4e43\u662f\u6c49\u4eba\u6700\u91cd\u8981\u7684\u8282\u65e5\u4e4b\u4e00\uff0c\u4e5f\u5c31 \u662f\u516b\u6708\u5341\u4e94\uff1f\uff0c\u5e7d\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u77e5\u9053\u3002\u600e\u4e48\uff0c\u516b\u6708\u5341\u4e94\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u4f60\u5c3d\u95ee\u65e5\u5b50\u5e72 \u561b\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u8bb0\u8d77\u4e86\u51b0\u5ddd\u5929\u5973\u8bf4\u8fc7\u7684\u8bdd\uff0c\u54a6\uff0c\u53ea\u6015\u4f60\u6240\u8bf4\u7684\u771f\u4f1a\u5de7\u5408\uff0c\u771f\u662f\u51f6 \u5146\uff01\u201d\u5e7d\u840d\u5927\u4e3a\u5947\u8be7\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u51b0\u5ddd\u5929\u5973\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u6211\u521d\u5230\u51b0\u5bab\u4e4b\u65e5\uff0c\u90a3\u65e5\u6b63\u5de7\u6211\u524d\u4e00\u4f4d\u5e08\u7236\uff0c\u8427\u5e08\u7236\u7684\u4ec7\u4eba\u627e\u4ed6 \u7b97\u5e10\uff0c\u5e78\u4e8f\u51b0\u5ddd\u5929\u5973\u7ed9\u6253\u53d1\u4e86\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5f53\u65f6\u6211\u6ca1\u5728\u573a\uff0c\u4e0d\u8fc7\u540e\u6765\u542c\u5979\u8bf4\u8fc7\u3002\u542c\u8bf4\u4f60\u8427 \u5e08\u7236\u7684\u4ec7\u4eba\u53eb\u505a\u4ec0\u4e48\u96f7\u9707\u5b50\uff0c\u8fd9\u540d\u5b57\u597d\u602a\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u8427\u5e08\u7236\u7684\u4ec7\u4eba\u5171\u6709\u4e09\u4e2a\uff0c\u4e00\u4e2a \u53eb\u96f7\u9707\u5b50\uff0c\u4e00\u4e2a\u53eb\u5d14\u4e91\u5b50\uff0c\u4e00\u4e2a\u53eb\u738b\u7624\u5b50\u3002\u738b\u7624\u5b50\u5df2\u7ed9\u6211\u5e08\u7236\u6253\u6b7b\u3002\u90a3\u65e5\u8ffd\u5230\u5929\u6e56\u5bfb\u4ec7\u7684\u662f \u96f7\u9707\u5b50\u548c\u5d14\u4e91\u5b50\uff0c\u5d14\u4e91\u5b50\u6ca1\u6709\u51fa\u624b\u3002\u51b0\u5ddd\u5929\u5973\u7528\u51b0\u5251\u5c06\u96f7\u9707\u5b50\u6253\u8d25\uff0c\u96f7\u9707\u5b50\u5f53\u65f6\u5c31\u60f3\u81ea\u5c3d\uff0c \u51b0\u5ddd\u5929\u5973\u8bf4\u4ed6\u88ab\u738b\u7624\u5b50\u611a\u5f04\uff0c\u53eb\u4ed6\u82e5\u6b32\u77e5\u8be6\u60c5\uff0c\u53ef\u5728\u4eca\u5e74\u516b\u6708\u5341\u4e94\u5230\u624e\u4f26\u53bb\u770b\u3002\u4f60\u77a7\u6b64\u5730\u6b63 \u662f\u624e\u4f26\uff0c\u4eca\u665a\u53c8\u6b63\u662f\u516b\u6708\u5341\u4e94\u3002\u201d \u3000\u3000\u5e7d\u840d\u66f4\u662f\u60ca\u8be7\uff0c\u5fc3\u60f3\uff1a\u201c\u54b1\u4eec\u7684\u5c0f\u516c\u4e3b\u4ece\u4e0d\u4e0b\u5c71\uff0c\u600e\u77e5\u6b64\u5730\u4eca\u591c\u4e4b\u4e8b\uff1f\u201d\u4f46\u76f8\u4fe1\u9648\u5929\u5b87 \u4e0d\u4f1a\u8bf4\u8c0e\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u770b\u4e0b\u9762\u5341\u4e09\u4eba\u4e4b\u4e2d\uff0c\u53ef\u6709\u96f7\u9707\u5b50\u5728\u5185\uff1f\u201d\u9648\u5929\u5b87\u770b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u6ca1 \u6709\uff0c\u8fd9\u5012\u5947\u4e86\u3002\u96be\u9053\u4ed6\u7adf\u4f1a\u4e0d\u6765\uff1f\u55ef\uff0c\u4f60\u9759\u542c\uff0c\u4ed6\u4eec\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4e0b\u9762\u9999\u706b\u7edf\u7ed5\uff0c\u4f3c\u662f\u6b63\u4e3e\u884c\u8fc7\u4ec0\u4e48\u4eea\u5f0f\uff0c\u53ea\u542c\u5f97\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u738b\u7624\u5b50\u8fd9\u65f6\u5019\u8fd8\u6ca1 \u6765\uff1f\u201d\u9648\u5929\u5b87\u6014\u4e86\u4e00\u6014\uff1a\u539f\u6765\u8fd9\u4e9b\u4eba\u8fd8\u4e0d\u77e5\u9053\u738b\u7624\u5b50\u5df2\u6b7b\uff01\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u7ea6\u54b1\u4eec\u5230\u6b64 \u805a\u96c6\u7684\uff0c\u600e\u4f1a\u4e0d\u6765\uff1f\u201d\u5148\u5934\u90a3\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u7b49\u4ed6\u4e86\uff0c\u5148\u8c08\u8c08\u5427\u3002\u798f\u5927\u5e05\u672c\u6765\u8981\u54b1\u4eec\u53bb\u4fdd\u62a4 \u91d1\u672c\u5df4\u74f6\u7684\uff0c\u73b0\u5728\u4e0d\u7528\u5566\uff0c\u53eb\u54b1\u4eec\u901a\u77e5\u95e8\u4eba\uff0c\u5728\u5e74\u5e95\u4ee5\u524d\uff0c\u90fd\u8d76\u5230\u56de\u7586\u53bb\u3002\u201d\u4e00\u4eba\u9053\uff1a\u201c\u600e \u4e48\u53c8\u4e0d\u7528\u6211\u4eec\u4e86\uff1f\u201d\u5148\u5934\u90a3\u4eba\u9053\uff1a\u201c\u542c\u8bf4\u56de\u7586\u54c8\u8428\u514b\u9020\u53cd\uff0c\u6709\u8bb8\u591a\u6b66\u5f53\u6d3e\u7684\u95e8\u4eba\u6742\u5728\u5176\u4e2d\uff0c \u975e\u6211\u4eec\u53bb\u5bf9\u4ed8\u4e0d\u53ef\u3002\u4fdd\u62a4\u91d1\u672c\u5df4\u74f6\u56fa\u7136\u6781\u4e3a\u91cd\u8981\uff0c\u8fd9\u4e8b\u60c5\u4e5f\u4e0d\u8f7b\u677e\u3002\u6240\u4ee5\u798f\u5927\u5e05\u5e76\u6ca1\u6709\u5c0f\u89c6 \u6211\u4eec\uff0c\u5404\u4f4d\u5144\u5f1f\u4e0d\u5fc5\u591a\u5fc3\u3002\u201d\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u8d77\u8427\u9752\u5cf0\u548c\u94c1\u62d0\u4ed9\u8bf4\u8fc7\u7684\u5404\u5927\u5251\u6d3e\u7684\u5386 \u53f2\u3002\u6b66\u5f53\u6d3e\u672c\u6765\u662f\u5b9a\u6709\u4e25\u89c4\uff0c\u4e0d\u51c6\u8fc7\u95ee\u653f\u4e8b\u7684\u3002\u540e\u6765\u5728\u660e\u672b\u6e05\u521d\u4e4b\u9645\uff0c\u51fa\u4e86\u4e00\u4e2a\u5353\u4e00\u822a\uff0c\u53d7 \u4e86\u5973\u4fa0\u7389\u7f57\u5239\uff08\u5373\u540e\u6765\u540d\u9707\u897f\u57df\u7684\u767d\u53d1\u9b54\u5973\uff09\u7684\u5f71\u54cd\uff0c\u79bb\u5f00\u6b66\u5f53\u5c71\uff0c\u8d70\u4eba\u56de\u7586\uff0c\u53e6\u7acb\u65b0\u6d3e\uff0c \u5e2e\u52a9\u6666\u660e\u7985\u5e08\u7684\u5f92\u5f1f\u6768\u4e91\u9a8b\u7b49\u6297\u51fb\u6e05\u5175\uff0c\u4e8e\u662f\u6b66\u5f53\u6d3e\u7684\u95e8\u89c4\uff0c\u9042\u88ab\u6253\u7834\u3002\uff08\u8bf8\u4e8b\u8be6\u89c1\u62d9\u8457 \u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0e\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u5207\u8fd9\u5df2\u7ecf\u662f\u5dee\u4e0d\u591a\u4e00\u767e\u5e74\u4ee5\u524d\u7684\u4e8b\u60c5\uff0c\u5176\u540e\u5230\u6842\u4ef2\u660e\u4f5c\u6b66 \u5f53\u6d3e\u638c\u95e8\uff0c\u5728\u56de\u7586\u4f20\u4e0b\u7684\u6b66\u5f53\u5f1f\u5b50\u4ea6\u751a\u591a\uff0c\u5341\u4e5d\u90fd\u6210\u4e3a\u6297\u6e05\u7684\u4e49\u58eb\u3002\u6bd4\u4e2d\u539f\u201c\u6b63\u7edf\u201d\u7684\u6b66\u5f53 \u6d3e\uff0c\u66f4\u5f97\u6c5f\u6e56\u666f\u4ef0\u3002 \u3000\u3000\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff0c\u201c\u539f\u6765\u8fd9\u6279\u4eba\u662f\u8981\u53bb\u5bf9\u4ed8\u56de\u7586\u7684\u6b66\u5f53\u6d3e\u5f1f\u5b50\u7684\u3002\u53ea\u662f\u8fd9\u548c\u738b\u7624 \u5b50\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u600e\u4e48\u8981\u7b49\u4ed6\u5462\uff1f\u738b\u7624\u4e8e\u548c\u6b66\u5f53\u6d3e\u7684\u96f7\u9707\u5b50\u662f\u7ed3\u62dc\u5144\u5f1f\uff0c\u7167\u8bf4\u4e5f\u8be5\u662f\u8fd9\u5e2e\u4eba \u7684\u654c\u4eba\u5440\uff01\u201d\u6b63\u81ea\u4e0d\u89e3\uff0c\u53ea\u542c\u5f97\u5148\u524d\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u8981\u5bf9\u4ed8\u6b66\u5f53\u6d3e\uff0c\u975e\u738b\u7624\u5b50\u6765\u4e0d\u884c\uff0c\u54a6\uff0c\u4ed6 \u600e\u4e48\u8fd8\u4e0d\u6765\uff0c\u96be\u9053\u4ed6\u771f\u7684\u88ab\u6b66\u5f53\u6d3e\u62c9\u8fc7\u53bb\u4e86\uff1f\u201d \u3000\u3000\u90a3\u50cf\u662f\u5927\u54e5\u6a21\u6837\u7684\u4eba\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4f11\u8981\u5982\u6b64\u7591\u5fc3\uff0c\u738b\u7624\u5b50\u662f\u54b1\u4eec\u5d06\u5cd2\u6d3e\u4e2d\u6770\u51fa\u7684\u4eba\u7269\uff0c \u4ed6\u82e6\u5fc3\u5b64\u8be3\uff0c\u6545\u610f\u4e0e\u6b66\u5f53\u6d3e\u7684\u95e8\u4e0b\u4eb2\u8fd1\uff0c\u6df7\u4e86\u5c06\u8fd1\u4e8c\u5341\u5e74\uff0c\u6240\u4e3a\u4f55\u6765\uff1f\u4e0d\u5c31\u662f\u60f3\u7aa5\u7834\u6b66\u5f53\u6d3e \u5251\u6cd5\u7684\u79d8\u5bc6\u5417\uff1f\u53ea\u8981\u54b1\u4eec\u80fd\u591f\u5e94\u4ed8\u6b66\u5f53\u5251\u6cd5\u7684\u602a\u62db\uff0c\u90a3\u4e48\u5e73\u5b9a\u56de\u7586\u4e4b\u4e71\uff0c\u5c31\u5927\u6709\u628a\u63e1\u5566\uff0c\u738b \u7624\u5b50\u65e2\u7136\u7ea6\u54b1\u4eec\u5728\u6b64\u805a\u96c6\uff0c\u8c05\u6765\u4e0d\u4f1a\u5931\u4fe1\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u4eca\u6625\u4ed6\u4e0e\u4ed6\u7684\u4e24\u4e2a\u628a\u5144\u5f1f\u540c\u6765\uff0c \u6709\u4eba\u89c1\u8fc7\uff0c\u53ea\u662f\u8fd9\u51e0\u4e2a\u6708\u5374\u53c8\u6ca1\u4e86\u6d88\u606f\uff0c\u4e0d\u77e5\u6709\u5426\u610f\u5916\uff1f\u201d\u5148\u524d\u90a3\u4eba\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u8bb8\u591a\u610f \u5916\uff1f\u201d\u8fd9\u4eba\u9053\uff1a\u201c\u83ab\u4e0d\u662f\u88ab\u96f7\u9707\u5b50\u7eca\u4f4f\uff0c\u8131\u4e0d\u4e86\u8eab\uff1f\u201d\u90a3\u50cf\u662f\u5927\u54e5\u6a21\u6837\u7684\u4eba\u9053\uff1a\u201c\u8d24\u5f1f\u6709\u6240 \u4e0d\u77e5\uff0c\u96f7\u9707\u5b50\u6b64\u4eba\u4e00\u5fc3\u8bb0\u6302\u62a5\u4ec7\uff0c\u4ed6\u624d\u4e0d\u4f1a\u7406\u671d\u5ef7\u7684\u95f2\u4e8b\u3002\u4ed6\u4e0e\u738b\u7624\u5b50\u540c\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u5411\u8427 \u9752\u5cf0\u62a5\u4ec7\uff0c\u4ed6\u4eec\u6765\u4e86\u6570\u6708\uff0c\u60f3\u6765\u8fd9\u4ec7\u5b9a\u5df2\u62a5\u4e86\uff0c\u96f7\u9707\u5b50\u8fd8\u6709\u4e0d\u56de\u53bb\u7684\u4e48\uff1f\u738b\u7624\u5b50\u4eca\u6625\u6d3e\u4eba\u5411 \u6211\u62a5\u4fe1\uff0c\u5c31\u8bf4\u8fc7\u6b64\u4e8b\u3002\u8bf4\u662f\u4ed6\u5df2\u60f3\u597d\u85c9\u53e3\uff0c\u4e0d\u548c\u96f7\u9707\u5b50\u518d\u56de\u56db\u5ddd\uff0c\u8981\u7559\u5728\u6b64\u95f4\u5566\u3002\u201d\u9648\u5929\u5b87 \u542c\u4e86\uff0c\u6bdb\u9aa8\u60ca\u7136\uff0c\u60f3\u4e0d\u5230\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u6709\u5982\u6b64\u9634\u9669\u4e4b\u4eba\uff0c\u9634\u9669\u4e4b\u4e8b\uff01\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u82e5\u7136\u8fd9\u756a\u8bdd \u88ab\u96f7\u9707\u5b50\u5f97\u77e5\uff0c\u4e0d\u77e5\u600e\u751f\u611f\u60f3\uff1f\u201d\u9648\u5929\u5b87\u867d\u662f\u6ee1\u6e05\u9a7b\u85cf\u5b98\u5458\u7684\u513f\u5b50\uff0c\u4f46\u4ed6\u81ea\u5e7c\u53d7\u8427\u9752\u5cf0\u6167 \u9676\uff0c\u5bf9\u6e05\u5ef7\u6b8a\u65e0\u597d\u611f\u3002 \u3000\u3000\u6708\u4eae\u6162\u6162\u897f\u79fb\uff0c\u4e0b\u9762\u8bf8\u4eba\u66f4\u662f\u7126\u8e81\uff0c\u201c\u600e\u4e48\u8fd8\u4e0d\u6765\uff1f\u201d\u201c\u738b\u7624\u5b50\u662f\u600e\u4e48\u641e\u7684\uff1f\u201d\u201c\u8fd8\u7b49 \u4e0d\u7b49\u4ed6\uff1f\u201d\u5404\u79cd\u7591\u95ee\u7684\u58f0\u97f3\uff0c\u95f9\u6210\u4e00\u7247\u3002\u53c8\u6709\u4eba\u53eb\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u4fe1\u6b66\u5f53\u6d3e\u7684\u5251\u6cd5\u6709\u90a3\u4e48\u5389 \u5bb3\uff0c\u738b\u7624\u5b50\u4e0d\u6765\uff0c\u96be\u9053\u54b1\u4eec\u5c31\u4e0d\u6562\u53bb\u56de\u7586\u4e48\uff1f\u201d\u201c\u4e0d\u7b49\u4ed6\u5566\uff0c\u4e0d\u7b49\u4ed6\u5566\uff01\u201d \u3000\u3000\u55a7\u95f9\u58f0\u4e2d\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u53ea\u89c1\u4e00\u5806\u4e71\u77f3\u4e4b\u540e\uff0c\u7a81\u7136\u8df3\u51fa\u4e24\u4eba\uff0c\u8d70\u5728\u524d\u9762\u90a3\u4eba\uff0c \u9762\u4e0a\u4ea4\u53c9\u4e24\u9053\u5200\u75d5\uff0c\u4f3c\u7b11\u975e\u7b11\u3002\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u66f4\u663e\u5f97\u8be1\u79d8\u4e4b\u6781\uff0c\u53ef\u6016\u975e\u5e38\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f \u6b66\u5f53\u6d3e\u7b2c\u4e8c\u4ee3\u7684\u7b2c\u4e00\u9ad8\u624b\u3001\u738b\u7624\u5b50\u7684\u628a\u5144\u96f7\u9707\u5b50\uff1b\u540e\u9762\u90a3\u4eba\uff0c\u63d0\u7740\u4e00\u628a\u5927\u5f13\uff0c\u624b\u62c2\u5f13\u5f26\uff0c\u6323 \u7136\u4f5c\u54cd\uff0c\u6b63\u662f\u5ce8\u5d4b\u6d3e\u7684\u597d\u624b\uff0c\u96f7\u9707\u5b50\u7684\u628a\u5f1f\u5d14\u4e91\u5b50\u3002 \u3000\u3000\u5341\u4e09\u4e2a\u5d06\u5cd2\u6d3e\u95e8\u4eba\u4e00\u9f50\u60ca\u8d77\uff0c\u4e3a\u9996\u7684\u540d\u53eb\u8d75\u7075\u541b\uff0c\u662f\u5d06\u5cd2\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5475\uff0c \u539f\u6765\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u96f7\u5927\u54e5\uff0c\u5d14\u5927\u54e5\uff0c\u4f60\u4eec\u51e0\u65f6\u6765\u7684\uff1f\u201d\u96f7\u9707\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6765\u4e86\u8bb8\u4e45\u5566\u3002\u201d \u8d75\u7075\u541b\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u5230\u8fd9\u8fb9\u6765\u5750\uff1f\u201d\u96f7\u9707\u5b50\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4e0d\u6562\u548c\u4f60\u4eec\u6500\u81ea\u5df1\u4eba\uff01\u201d\u8d75\u7075\u541b\u9762 \u8272\u5927\u53d8\uff0c\u77e5\u9053\u4ed6\u4eec\u7684\u8bf4\u8bdd\u5df1\u5168\u88ab\u96f7\u9707\u5b50\u542c\u53bb\uff0c\u6599\u60f3\u4eca\u665a\u8fd9\u4e00\u573a\u6076\u6597\uff0c\u5b9a\u6240\u96be\u514d\uff0c\u5f53\u4e0b\u5411\u4f17\u540c \u95e8\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u96f7\u5927\u54e5\u65e2\u7136\u5982\u6b64\u89c1\u5916\uff0c\u90a3\u4e48\u8bf7\u95ee\u96f7\u5927\u54e5\u4eca\u665a\u5230\u6765\uff0c\u6709\u4f55\u89c1 \u6559\uff1f\u201d \u3000\u3000\u96f7\u9707\u5b50\u9053\uff1a\u201c\u6211\u7279\u6765\u544a\u8bc9\u4f60\u4eec\uff0c\u738b\u7624\u5b50\u4eca\u665a\u4e0d\u80fd\u6765\uff0c\u4ee5\u540e\u4e5f\u6c38\u4e0d\u80fd\u6765\u5566\uff01\u201d\u8d75\u7075\u541b\u9053\uff1a \u201c\u4ec0\u4e48\uff1f\u201d\u96f7\u9707\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u8981\u627e\u4ed6\uff0c\u53ef\u5f97\u5230\u5730\u5e9c\u53bb\u95ee\u960e\u7f57\u738b\u5566\uff01\u201d\u5d06\u5cd2\u95e8\u4eba\u9f50\u90fd\u9707\u52a8\uff0c\u8d75 \u7075\u541b\u559d\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u6740\u5bb3\u4e86\u4ed6\uff01\u8fd8\u6562\u5230\u8fd9\u91cc\u62a5\u8baf\uff01\u201d\u628a\u624b\u4e00\u6446\uff0c\u5341\u4e09\u4e2a\u540c\u95e8\u5144\u5f1f\uff0c\u6392\u6210\u5706 \u9635\uff0c\u5c06\u96f7\u9707\u5b50\u4e0e\u5d14\u4e91\u5b50\u56f4\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u96f7\u9707\u5b50\u6602\u9996\u5411\u5929\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u6211\u6ca1\u6709\u4eb2\u624b\u6740\u4ed6\uff01\u201d\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7f14 \u4ea4\u8fd1\u4e8c\u5341\u5e74\u7684\u7ed3\u62dc\u5144\u5f1f\uff0c\u7adf\u7136\u662f\u5d06\u5cd2\u6d3e\u6d3e\u6765\u7684\u201c\u5367\u5e95\u201d\u7684\u5978\u7ec6\uff0c\u795e\u60c5\u60b2\u6124\uff0c\u7b11\u5f97\u6781\u662f\u51c4\u51c9\u3002 \u8d75\u7075\u541b\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u738b\u7624\u5b50\u4e0d\u662f\u4f60\u6740\u7684\uff1f\u201d\u5d06\u5cd2\u4f17\u540c\u95e8\u7eb7\u7eb7\u559d\u9a82\u3002\u201c\u8fd8\u8981\u62b5\u8d56\uff1f\u201d\u201c\u4e0d \u662f\u4f60\u6740\u7684\u53c8\u662f\u8c01\u6740\u7684\uff1f\u201d\u201c\u54fc\uff0c\u4f60\u5f53\u6211\u4eec\u5d06\u5cd2\u6d3e\u662f\u597d\u6b3a\u8d1f\u7684\u5417\u201d\u96f7\u9707\u5b50\u5fc3\u9ad8\u6c14\u505a\uff0c\u52a0\u4ee5\u6c14\u6068 \u4e4b\u6781\uff0c\u518d\u4e5f\u4e0d\u52a0\u5206\u8fa9\uff0c\u53ea\u51b7\u7b11\u9053\uff1a\u201c\u738b\u7624\u5b50\u6b7b\u6709\u4f59\u8f9c\uff0c\u8c01\u90fd\u53ef\u4ee5\u6740\u4ed6\uff0c\u4f60\u4eec\u82e5\u8981\u62a5\u4ec7\uff0c\u51b2\u7740 \u6211\u6765\u4fbf\u662f\u3002\u201d\u8d75\u7075\u541b\u5927\u6012\uff0c\u671d\u624b\u4e00\u6307\uff0c\u9635\u52bf\u53d1\u52a8\u3002\u96f7\u9707\u5b50\u51b7\u51b7\u4e00\u7b11\uff0c\u4e0e\u5d14\u4e91\u5b50\u8d34\u80cc\u800c\u7acb\uff0c\u624b \u8d77\u4e00\u5251\uff0c\u5237\u7684\u4fbf\u523a\u5411\u8d75\u7075\u541b\u547d\u95e8\u8981\u7a74\u3002 \u3000\u3000\u8d75\u7075\u541b\u662f\u81f3\u8717\u6d3e\u7684\u638c\u95e8\uff0c\u6b66\u529f\u81ea\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u89c1\u96f7\u9707\u5b50\u6765\u5f97\u52bf\u51f6\uff0c\u5de6\u624b\u4e00\u62db\uff0c\u53cc\u6307\u5fae \u5f2f\uff0c\u7528\u5d06\u5cd2\u6d3e\u7684\u5c0f\u64d2\u62ff\u624b\u6cd5\uff0c\u52fe\u96f7\u9707\u5b50\u7684\u624b\u8155\uff0c\u53f3\u624b\u957f\u5251\u4e00\u6307\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u5251 \u6307\u517c\u65bd\uff0c\u53ea\u8981\u96f7\u9707\u5b50\u653b\u52bf\u4e00\u53d1\uff0c\u5c31\u7acb\u523b\u5236\u7740\u5148\u673a\u3002\u54ea\u77e5\u96f7\u9707\u5b50\u7684\u201c\u8fbe\u6469\u5251\u6cd5\u201d\u5168\u4e0d\u4f9d\u5e38\u8f68\uff0c \u53ea\u542c\u5f97\u5237\u7684\u4e00\u54cd\uff0c\u96f7\u9707\u5b50\u7684\u5251\u52bf\u7a81\u7136\u5012\u8f6c\uff0c\u53cd\u624b\u4e00\u6325\uff0c\u8d75\u7075\u541b\u7684\u4e00\u4e2a\u5e08\u5f1f\uff0c\u6b63\u597d\u4ece\u53f3\u659c\u65b9\u653b \u4e0a\uff0c\u6070\u6070\u7ed9\u4e00\u5251\u524a\u4e2d\uff0c\u80a9\u5934\u4e0a\u8fde\u8863\u5e26\u8089\uff0c\u7ed9\u4ed6\u524a\u4e86\u4e00\u5757\u3002 \u3000\u3000\u8d75\u7075\u541b\u6325\u5251\u6025\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u56db\u65b9\u8054\u653b\u3002\u53eb\u4ed6\u817e\u4e0d\u51fa\u6765\u3002\u201d\u5d06\u5cd2\u6d3e\u5341\u4e09\u4e2a\u5f1f\u5b50\uff0c\u5206\u6210\u4e09 \u7ec4\uff0c\u6bcf\u7ec4\u56db\u4eba\uff0c\u5982\u6f6e\u6c34\u822c\u7684\u500f\u8fdb\u500f\u9000\uff0c\u8d75\u7075\u541b\u5219\u5c45\u4e2d\u7b56\u5e94\uff0c\u4e13\u95e8\u9632\u5907\u96f7\u9707\u5b50\u7684\u602a\u62db\u7a81\u51fb\u3002\u96f7 \u9707\u5b50\u4e0e\u5d14\u4e91\u5b50\u8d34\u80cc\u800c\u7acb\uff0c\u6325\u5f13\u8fde\u5251\uff0c\u5bf8\u6b65\u4e0d\u79fb\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\u3002\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4ece\u4e0a\u9762 \u4fef\u77b0\u4e0b\u6765\uff0c\u4f46\u89c1\u4eba\u5f62\u5e62\u5e62\uff0c\u5c18\u6c99\u6eda\u6eda\uff0c\u5f26\u58f0\u6323\u6323\uff0c\u5251\u5149\u970d\u970d\uff0c\u6740\u5f97\u4e2a\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u5e7d\u840d\u6084\u6084\u8bf4\u9053\uff1a\u201c\u96f7\u9707\u5b50\u5251\u6cd5\u867d\u7cbe\uff0c\u53ef\u60dc\u672a\u5f97\u8fbe\u6469\u5251\u6cd5\u7684\u795e\u9ad3\u3002\u201d\u9648\u5929\u5b87\u70b9\u4e86\u70b9\u5934\uff0c \u9053\uff1a\u201c\u96f7\u9707\u5b50\u5927\u7ea6\u8fd8\u53ef\u652f\u6301\u534a\u4e2a\u65f6\u8fb0\uff0c\u5d14\u4e91\u5b50\u5374\u662f\u96be\u4ee5\u5e94\u4ed8\u3002\u201d\u5d14\u4e91\u5b50\u7684\u5f13\u5f26\uff0c\u672c\u662f\u86df\u7b4b\u4e0e \u4e4c\u91d1\u5408\u7ec3\uff0c\u53ef\u4ee5\u62c9\u65ad\u654c\u4eba\u7684\u5175\u5203\uff0c\u7b97\u5f97\u662f\u6b66\u6797\u7684\u4e00\u4ef6\u5f02\u5b9d\uff0c\u4f46\u524d\u6b21\u5728\u8428\u8ff9\u7ed9\u8427\u9752\u5cf0\u7684\u62c2\u5c18\u62c2 \u65ad\uff0c\u540e\u6765\u867d\u7ecf\u9a73\u7eed\uff0c\u529f\u6548\u5374\u5927\u4e0d\u5982\u524d\uff0c\u540c\u65f6\u4f7f\u7528\u8fd9\u79cd\u5947\u95e8\u6b66\u5668\uff0c\u82e5\u53ea\u662f\u5e94\u4ed8\u4e00\u4e2a\u529f\u529b\u4e0e\u81ea\u5df1 \u76f8\u5f53\u7684\u654c\u624b\uff0c\u8fd8\u53ef\u4ee5\u5728\u5175\u5668\u4e0a\u5360\u4fbf\u5b9c\uff0c\u5e94\u4ed8\u7fa4\u6bb4\uff0c\u5374\u662f\u96be\u4ee5\u53d1\u6325\u5a01\u529b\u3002 \u3000\u3000\u5728\u8fd9\u91cc\u805a\u96c6\u7684\u5341\u4e09\u4e2a\u4eba\uff0c\u4e2a\u4e2a\u90fd\u662f\u5d06\u5cd2\u6d3e\u51fa\u7c7b\u62d4\u8403\u7684\u4eba\u7269\uff0c\u82e5\u7136\u4ee5\u4e00\u5bf9\u4e00\uff0c\u867d\u7136\u90fd\u4e0d\u662f \u96f7\u9707\u5b50\u7684\u5bf9\u624b\uff0c\u4f46\u5206\u7ec4\u8f6e\u653b\uff0c\u5374\u662f\u62a2\u5c3d\u4e0a\u98ce\u3002\u6fc0\u6218\u4e4b\u4e2d\uff0c\u8d75\u7075\u541b\u7a81\u7136\u98de\u8eab\u8dc3\u8d77\uff0c\u957f\u5251\u4e00\u62db \u201c\u6a2a\u6c5f\u65ad\u6d41\u201d\uff0c\u4ece\u96f7\u9707\u5b50\u4e0e\u5d14\u4e91\u5b50\u7684\u5f53\u4e2d\u65a9\u4e0b\uff0c\u96f7\u3001\u5d14\u4e8c\u4eba\u6b63\u5728\u5e94\u4ed8\u56eb\u65b9\u7684\u653b\u52bf\uff0c\u7ed9\u4ed6\u8fd9\u6837 \u5f53\u4e2d\u4e00\u65a9\uff0c\u65e0\u6cd5\u62bd\u5251\u9632\u5fa1\uff0c\u8feb\u4e0d\u5f97\u5df2\u7684\u4e24\u8fb9\u4e00\u5206\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5d06\u5cd2\u5f1f\u5b50\u7acb\u523b\u586b\u4e86\u7a7a \u6863\uff0c\u5c06\u4e24\u4eba\u9694\u65ad\uff0c\u4f7f\u4ed6\u4eec\u518d\u4e0d\u80fd\u8d34\u80cc\u800c\u7acb\uff0c\u9677\u5165\u5404\u81ea\u4e3a\u6218\u7684\u9669\u5883\uff01 \u3000\u3000\u8d75\u7075\u541b\u54c8\u54c8\u5927\u7b11\uff0c\u6307\u6325\u4e00\u4ece\u540c\u95e8\uff0c\u5c06\u4e24\u4eba\u5404\u81ea\u5305\u56f4\uff0c\u96f7\u9707\u5b50\u4ec5\u80fd\u4ed7\u7740\u602a\u62db\u81ea\u4fdd\uff0c\u5251\u52bf\u8d8a \u6765\u8d8a\u65bd\u5c55\u4e0d\u5f00\uff1b\u5d14\u4e91\u5b50\u66f4\u662f\u5e94\u4ed8\u4e3a\u96be\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u9635\u53ee\u53ee\u54c6\u54af\u7684\u7e41\u97f3\u5bc6\u54cd\uff0c\u63a5\u7740\u6025\u4fc3\u4e00\u58f0\uff0c \u58f0\u5982\u88c2\u5e1b\uff0c\u5d14\u4e91\u5b50\u72c2\u53eb\u4e00\u58f0\uff0c\u5de6\u80a9\u5df2\u4e2d\u4e86\u4e00\u5251\uff0c\u5f13\u68a2\u4e86\u4e5f\u7ed9\u5229\u5203\u5272\u88c2\u3002\u8d75\u7075\u541b\u559d\u9053\uff1a\u201c\u5d14\u8001 \u4e8c\uff0c\u4f60\u4e0d\u662f\u4e3b\u51f6\uff0c\u63b7\u4e0b\u5f13\u6765\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u5d14\u4e91\u5b50\u5927\u7b11\u9053\uff1a\u201c\u53eb\u6211\u5411\u4f60\u4eec\u8fd9\u6279\u9f20\u8f88\u6295\u964d\u4e48\uff1f\u54fc\uff0c\u6211\u5d14\u4e91\u5b50\u7eb5\u7136\u5f13\u6298\u8eab\u6b7b\uff0c\u4e5f\u65ad\u4e0d\u53d7 \u8fb1\uff01\u201d\u96f7\u9707\u5b50\u53eb\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u624d\u662f\u6211\u7684\u5144\u5f1f\uff01\u201d\u957f\u5251\u4e00\u5c55\uff0c\u62fc\u547d\u51b2\u523a\uff0c\u60f3\u7a81\u56f4\u800c\u51fa\uff0c\u4e0e\u5d14\u4e91 \u5b50\u4f1a\u5408\uff0c\u4f46\u7ed9\u8d75\u7075\u541b\u622a\u4f4f\uff0c\u5bf8\u6b65\u96be\u79fb\u3002 \u3000\u3000\u9648\u5929\u5b87\u5bf9\u96f7\u3001\u5d14\u4e8c\u4eba\uff0c\u672c\u662f\u751a\u65e0\u597d\u611f\uff0c\u89c2\u6b64\u4e00\u6218\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e8c\u4eba\u4e5f\u8fd8\u6709\u70b9\u9aa8 \u6c14\u3002\u201d\u4e0d\u7981\u8d77\u4e86\u540c\u4ec7\u654c\u5ffe\u4e4b\u5fc3\uff0c\u518d\u5411\u4e0b\u770b\u65f6\uff0c\u53ea\u89c1\u4e24\u4eba\u5f62\u52bf\u66f4\u662f\u5371\u9669\uff0c\u5c24\u5176\u662f\u5d14\u4e91\u5b50\uff0c\u867d\u7136 \u6325\u5f13\u529b\u6218\uff0c\u90a3\u5f13\u5f26\u4e4b\u58f0\uff0c\u5374\u5df2\u6c89\u54d1\u3002\u5e7d\u840d\u9053\uff1a\u201c\u6b64\u4eba\u6027\u547d\u5df2\u5728\u547c\u5438\u4e4b\u95f4\uff0c\u5582\uff0c\u4f60\u4e0d\u53bb\u52a9\u540c\u95e8 \u4e00\u81c2\u4e4b\u529b\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u6e90\u51fa\u6b66\u5f53\uff0c\u4f60\u5b66\u4e86\u51b0\u5ddd\u5929 \u5973\u7684\u5251\u6cd5\uff0c\u4e5f\u7b97\u5f97\u662f\u96f7\u9707\u5b50\u7684\u540c\u95e8\u5440\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u540c\u53bb\uff01\u201d\u7a81\u7136\u4ece\u5ca9\u77f3\u4e4b\u540e\u73b0 \u51fa\u8eab\u6765\uff0c\u53eb\u9053\uff1a\u201c\u96f7\u9707\u5b50\u4f60\u4e0d\u8981\u614c\u3002\u6211\u6765\u6551\u4f60\uff01\u201d \u3000\u3000\u5d06\u5cd2\u95e8\u4eba\u4e0e\u96f7\u3001\u5d14\u4e8c\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u62ac\u5934\u671b\u65f6\uff0c\u53ea\u89c1\u4e00\u5bf9\u9752\u5e74\u7537\u5973\u4ece\u5c71\u4e0a\u76f4\u8dd1\u4e0b\u6765\uff0c\u5728 \u4e2d\u79cb\u660e\u6708\u4e4b\u4e0b\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u96f7\u9707\u5b50\u5fc3\u4e0a\u4e00\u51c9\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u9053\u662f\u4ec0\u4e48\u9ad8\u4eba\uff0c\u539f\u6765\u662f\u8427\u9752\u5cf0 \u7684\u5f92\u5f1f\uff01\u201d\u8981\u77e5\u96f7\u9707\u5b50\u81ea\u8d1f\u4e0d\u51e1\uff0c\u6b66\u529f\u4e5f\u786e\u5728\u8427\u9752\u5cf0\u4e4b\u4e0a\uff0c\u8fde\u8427\u9752\u5cf0\u4e5f\u4e0d\u5728\u4ed6\u7684\u773c\u5185\uff0c\u4f55\u51b5 \u662f\u8427\u9752\u5cf0\u7684\u5f92\u5f1f\uff1f \u3000\u3000\u8d75\u7075\u541b\u89c1\u8fd9\u4e24\u4eba\u4e0d\u8fc7\u662f\u5341\u516d\u516b\u5c81\u7684\u5927\u5b69\u5b50\uff0c\u8138\u4e0a\u7a1a\u6c14\u672a\u6d88\uff0c\u8e66\u8e66\u8df3\u8df3\u7684\u8d70\u6765\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\u5929\u6709\u591a\u9ad8\uff1f\u5730\u6709\u591a\u539a\uff0c\u4e73\u81ed\u672a\u9664\uff0c\u5c31\u80c6\u6562\u8dd1\u6765\u9001\u6b7b\uff01\u201d\u96f7\u9707\u5b50\u4e5f\u53eb\u9053\uff1a\u201c\u4f60 \u4eec\u5feb\u8d70\uff0c\u9648\u516c\u5b50\uff0c\u4f60\u62a5\u4e0e\u4f60\u5e08\u7236\u77e5\u9053\uff0c\u6211\u518d\u4e0d\u6068\u4ed6\u5566\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u4e0d\u518d\u6068\u6211\u7684\u5e08\u7236\uff0c\u6211\u624d\u6551\u4f60\uff01\u201d\u845b\u5730\u8eab\u5f62\u4e00\u8d77\uff0c\u75be\u4f3c\u6d41\u661f\uff0c\u4e00\u5251 \u5411\u8d75\u7075\u541b\u75be\u523a\uff0c\u8d75\u7075\u541b\u5de6\u624b\u4e00\u5e26\uff0c\u53f3\u5251\u5e73\u51fa\uff0c\u8981\u628a\u9648\u5929\u5b87\u6454\u5012\uff0c\u90a3\u6599\u9648\u5929\u5b87\u7684\u5251\u52bf\u6bd4\u96f7\u9707\u5b50 \u66f4\u602a\u3002\u5ffd\u5730\u5251\u950b\u4e00\u8f6c\uff0c\u5237\u7684\u4fbf\u6307\u5230\u8d75\u7075\u541b\u80f8\u524d\uff0c\u8d75\u7075\u541b\u5927\u5403\u4e00\u60ca\uff0c\u5e78\u800c\u4ed6\u529f\u529b\u6df1\u6e5b\uff0c\u7ecf\u9a8c\u8001 \u5230\uff0c\u7acb\u5373\u540e\u8eab\u4e00\u4ef0\uff0c\u65bd\u5c55\u201c\u94c1\u677f\u6865\u201d\u3001\u7684\u529f\u529b\uff0c\u5934\u5411\u540e\u5782\uff0c\u51e0\u4e4e\u89e6\u53ca\u5730\u9762\uff0c\u4f46\u89c9\u5251\u98ce\u63a0\u9762\u800c \u8fc7\uff0c\u9876\u4e0a\u4e00\u7247\u6c81\u51c9\uff0c\u9976\u662f\u4ed6\u95ea\u907f\u5f97\u5b9c\uff0c\u5934\u53d1\u4e5f\u7ed9\u5272\u53bb\u4e00\u7edc\uff0c\u8d75\u7075\u541b\u8eab\u5b50\u4e00\u633a\uff0c\u6025\u5fd9\u62cd\u51fa\u4e00 \u638c\uff0c\u9648\u5929\u5b87\u521a\u518d\u51fa\u62db\uff0c\u5251\u950b\u7ed9\u4ed6\u638c\u529b\u4e00\u9707\uff0c\u4e5f\u81ea\u523a\u6b6a\u3002\u539f\u6765\u9648\u5929\u5b87\u5251\u6cd5\u867d\u5f3a\uff0c\u529f\u529b\u672a\u5230\uff0c\u4e0e \u8d75\u7075\u541b\u5404\u6709\u987e\u5fcc\uff0c\u5fc3\u4e2d\u90fd\u662f\u6697\u6697\u5403\u60ca\uff0c\u5d06\u5cd2\u4e00\u4f17\u9ad8\u624b\uff0c\u89c1\u4ed6\u4eec\u7684\u638c\u95e8\u88ab\u4e00\u4e2a\u5c11\u5e74\u5f04\u5f97\u5982\u6b64\u72fc \u72c8\uff0c\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u8d75\u7075\u541b\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u7559\u5fc3\u5bf9\u4ed8\u8fd9\u4e2a\u5a03\u5a03\uff01\u201d \u3000\u3000\u5e7d\u840d\u4e00\u8dc3\u800c\u4e0a\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u6211\u5462\uff01\u6211\u8981\u7528\u6697\u5668\u6253\u4f60\u4e86\uff0c\u4f60\u4e5f\u5f97\u7559\u5fc3\u5e94\u4ed8\u5475\uff01\u201d \u201c\u6697\u5668\u201d\u672c\u6765\u5c31\u662f\u8981\u6697\u7b97\u654c\u4eba\u7684\u4e00\u79cd\u6697\u5668\uff0c\u5929\u4e0b\u6253\u6697\u5668\u4e4b\u4eba\uff0c\u65ad\u65e0\u9884\u5148\u8a00\u660e\u4e4b\u7406\u3002\u8d75\u7075\u541b\u4e0d \u89c9\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u59ae\u5b50\u6709\u4ec0\u4e48\u53e4\u602a\u7684\u6697\u5668\uff0c\u62ff\u51fa\u6765\u8ba9\u6211\u77a7\u77a7\u3002\u201d\u5e7d\u840d\u53cc\u6307\u4e00 \u5f39\uff0c\u53ea\u542c\u5f97\u5506\u55e4\u7684\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u9aa4\u7136\u88ad\u5230\u3002\u8d75\u7075\u541b\u7591\u5fc3\u8fd9\u662f\u6885\u82b1\u9488\uff0c\u957f\u5251\u4e00\u6325\uff0c\u821e\u8d77\u4e00\u5708 \u94f6\u8679\uff0c\u62a4\u7740\u8eab\u8eaf\uff0c\u5ffd\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9897\u5f62\u4f3c\u73cd\u73e0\u7684\u6697\u5668\uff0c\u7a81\u88ab\u5251\u98ce\u9707\u788e\uff0c\u4e00\u56e2\u5bd2\u5149\u51b7 \u6c14\uff0c\u5ffd\u5730\u53d1\u6563\u5f00\u6765\uff0c\u8d75\u7075\u541b\u9996\u5f53\u5176\u51b2\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u51b0\u5bab\u72ec\u6709\uff0c\u4e16\u4e0a\u65e0\u53cc\u7684\u6697\u5668\u2014\u2014\u51b0\u9b44\u795e\u5f39\uff0c\u8d75\u7075\u541b\u529f\u529b\u867d\u9ad8\uff0c\u7ed9\u8fd9\u80a1\u51b7\u6c14\u4e00\u51b2\uff0c \u4e5f\u89c9\u5947\u5bd2\u5f7b\u9aa8\uff0c\u523a\u4f53\u4fb5\u80a4\u3002\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u771f\u662f\u90aa\u95e8\uff0c\u8d76\u5feb\u56f4\u653b\uff0c\u53eb\u4ed6\u4eec\u817e\u4e0d\u51fa\u624b \u6765\uff01\u201d\u5e7d\u840d\u53cc\u6307\u75be\u5f39\uff0c\u8fde\u53d1\u56db\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u6253\u4e2d\u4e09\u540d\u5d06\u5cd2\u95e8\u4eba\uff0c\u53e6\u4e00\u679a\u5374\u7ed9\u8d75\u7075\u541b\u7528\u91d1\u9488\u7f25 \u6253\u98de\uff0c\u8fd9\u4e09\u540d\u5d06\u5cd2\u95e8\u4eba\u529f\u529b\u8f83\u4f4e\uff0c\u4e00\u66f4\u662f\u7259\u5173\u6253\u6218\uff0c\u8eab\u8eaf\u98a4\u6296\uff0c\u989d\u4e0a\u6c81\u51fa\u6c57\u73e0\u3002\u5e7d\u840d\u5fc3\u9053\uff1a \u201c\u6bcf\u4eba\u518d\u5949\u9001\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u4ed6\u4eec\u5c31\u7981\u4e0d\u4f4f\u5566\uff0c\u5440\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u6211\u6ca1\u6709\u591a\u5e26\u3002\u201d\u8981\u77e5\u51b0\u9b44 \u795e\u5f39\u4e43\u51b0\u5ddd\u5929\u5973\u4ece\u5343\u4e08\u51b0\u7a9f\u4e4b\u4e2d\uff0c\u64b7\u53d6\u51b0\u9b44\u7cbe\u82f1\uff0c\u51dd\u70bc\u800c\u6210\uff0c\u9664\u4e86\u5728\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u4e4b\u51b0\u5cf0\u4e4b \u5916\uff0c\u5176\u4ed6\u5730\u65b9\uff0c\u6839\u672c\u65e0\u6cd5\u518d\u70bc\uff0c\u5e7d\u840d\u968f\u8eab\u53ea\u643a\u6709\u5341\u591a\u679a\uff0c\u8fd9\u79cd\u6697\u5668\uff0c\u53c8\u662f\u4e00\u7ecf\u6253\u51fa\uff0c\u5373\u81ea\u884c \u6d88\u706d\uff0c\u5316\u4e3a\u4e4c\u6709\uff0c\u6545\u6b64\u6253\u4e00\u679a\u5c31\u5c11\u4e00\u679a\uff0c\u5e7d\u840d\u4e5f\u820d\u4e0d\u5f97\u591a\u7528\uff0c\u7565\u4e00\u8fdf\u7591\uff0c\u7b2c\u4e8c\u7ec4\u8f6e\u653b\u7684\u654c \u4eba\uff0c\u65e9\u5df2\u5c06\u5979\u56f4\u4f4f\u3002 \u3000\u3000\u5e7d\u840d\u5a07\u53f1\u4e00\u58f0\uff0c\u7acb\u5373\u62d4\u51fa\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u90a3\u56db\u4e2a\u56f4\u653b\u5979\u7684\u654c\u4eba\u53c8\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00 \u4e2a\u51b7\u6218\uff0c\u51b0\u5bab\u4f8d\u5973\u6bcf\u4eba\u90fd\u6709\u4e00\u628a\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u4e43\u662f\u7528\u51b0\u5cf0\u7279\u4ea7\u7684\u6e29\u7389\uff0c\u6d78\u5728\u5bd2\u6cc9\u4e4b\u4e2d\uff0c\u7ecf\u8fc7 \u4e09\u5e74\u624d\u70bc\u6210\u7684\u5b9d\u5251\uff0c\u6240\u4ee5\u4e00\u51fa\u624b\u4fbf\u6709\u4e00\u80a1\u51b7\u6c14\uff0c\u5a01\u529b\u867d\u4e0d\u53ca\u51b0\u9b44\u795e\u5f39\u7684\u9aa4\u7136\u4e00\u51fb\uff0c\u4f46\u82e5\u6ca1\u6709 \u7ec3\u8fc7\u5185\u529f\u7684\u4eba\uff0c\u9762\u5bf9\u8fd9\u56e2\u51b7\u6c14\u5bd2\u5149\uff0c\u4e5f\u662f\u96be\u4ee5\u7981\u53d7\u3002 \u3000\u3000\u8fd9\u5341\u4e09\u4e2a\u5d06\u5cd2\u9ad8\u624b\uff0c\u529f\u529b\u867d\u6709\u53c2\u5dee\uff0c\u4f46\u5185\u529f\u4ff1\u6709\u6839\u5e95\uff0c\u5728\u51b7\u6c14\u5bd2\u5149\u95ea\u51fb\u4e4b\u4e0b\uff0c\u867d\u89c9\u751a\u4e0d \u8212\u670d\uff0c\u4e5f\u8fd8\u62b5\u53d7\u5f97\u4f4f\uff0c\u8d75\u7075\u541b\u5f53\u4e2d\u6307\u6325\uff0c\u4ecd\u7528\u524d\u6cd5\uff0c\u5c06\u96f7\u9707\u5b50\u3001\u5d14\u4e91\u5b50\u3001\u9648\u5929\u5b87\u3001\u5e7d\u840d\u56db\u4eba \u5206\u9694\u5f00\u6765\uff0c\u8f6e\u756a\u62a2\u653b\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u65bd\u5c55\u51b0\u5ddd\u5929\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u500f\u7136\u800c\u6765\uff0c \u500f\u7136\u800c\u53bb\uff0c\u5f00\u5934\u4e09\u4e94\u5341\u62db\uff0c\u6740\u5f97\u90a3\u4e9b\u8f6e\u653b\u7684\u654c\u4eba\uff0c\u4e2a\u4e2a\u80c6\u6218\u5fc3\u60ca\uff0c\u6478\u4e0d\u6e05\u4ed6\u4eec\u5251\u6cd5\u7684\u6765\u8def\uff0c \u66f4\u4f11\u8bf4\u8fd1\u8eab\u653b\u51fb\u4e86\u3002\u8d75\u7075\u541b\u8fde\u8fde\u6447\u9996\u9053\uff1a\u201c\u90aa\u95e8\uff0c\u90aa\u95e8\uff01\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u90aa\u95e8\uff01\u201d\u51b0\u9b44 \u5bd2\u5149\u5251\u4e00\u6307\uff0c\u5251\u5c16\u4e00\u5f39\uff0c\u53c8\u53d1\u51fa\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u8d75\u7075\u541b\u6025\u53d1\u91d1\u94b1\u9556\uff0c\u6253\u98de\u4e00\u9897\uff0c\u53e6\u4e00\u9897\u5374\u56e0 \u529b\u5ea6\u7528\u5f97\u4e0d\u5f53\uff0c\u672a\u66fe\u6253\u98de\uff0c\u5148\u81ea\u70b8\u88c2\uff0c\u6b63\u6b63\u6253\u4e2d\u8d75\u7075\u541b\u7684\u9762\u5b54\uff0c\u5947\u5bd2\u4e4b\u6c14\uff0c\u7adf\u7136\u4fb5\u5165\u4e86\u8d75\u7075 \u541b\u7684\u773c\u775b\u3002 \u3000\u3000\u8d75\u7075\u541b\u6709\u5982\u89e6\u7535\uff0c\u773c\u775b\u7acb\u523b\u7741\u4e0d\u5f00\u6765\uff0c\u5e7d\u840d\u4e58\u673a\u4e00\u62db\u201c\u51b0\u6cb3\u89e3\u51bb\u201d\uff0c\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u5bd2 \u5149\u5251\u4e00\u6296\uff0c\u4e00\u62db\u5316\u4e3a\u4e09\u5f0f\uff0c\u5206\u523a\u4e0a\u4e2d\u4e0b\u4e09\u8def\uff0c\u8fd9\u4e00\u624b\u5b9e\u662f\u51b0\u5ddd\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999\u4e4b\u7740\uff0c\u6781\u80fd\u8ff7\u5165 \u773c\u76ee\uff0c\u53eb\u4ed6\u5206\u4e0d\u51fa\u653b\u52bf\u5728\u54ea\u4e00\u65b9\uff0c\u5e94\u4ed8\u4e4b\u65f6\uff0c\u4fbf\u53ef\u80fd\u81ea\u884c\u51fa\u9517\u3002\u81ea\u840d\u957f\u5904\u51b0\u5bab\uff0c\u4e34\u654c\u7684\u7ecf\u9a8c \u4e0d\u591a\uff0c\u53c8\u662f\u5c11\u5e74\u597d\u80dc\uff0c\u610f\u6b32\u64d2\u8d3c\u64d2\u738b\uff0c\u5148\u628a\u8d75\u7075\u541b\u523a\u4f24\u518d\u8bf4\uff0c\u54ea\u77e5\u4e00\u5251\u523a\u51fa\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2 \u7136\uff0c\u8d75\u7075\u541b\u7684\u8eab\u5f62\u4e00\u6643\u4e4b\u95f4\uff0c\u5df2\u4ece\u81ea\u5df1\u53f3\u8fb9\u88ad\u5230\uff0c\u9648\u5929\u5b87\u8981\u62a2\u4e0a\u6559\u62a4\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u53ea\u542c\u5f97 \u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5e7d\u840d\u7684\u9999\u80a9\u7adf\u7ed9\u8d75\u7075\u541b\u7684\u638c\u950b\u626b\u4e86\u4e00\u4e0b\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u51e0\u4e4e\u8131\u624b\u6b32\u98de\uff0c\u8e09\u8e09\u8dc4 \u8dc4\u5012\u9000\u51e0\u6b65\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e00\u62db\u7684\u5999\u5904\u5c31\u5168\u5728\u8ff7\u4e71\u654c\u4eba\u773c\u76ee\uff0c\u5e7d\u840d\u5728\u6025\u4fc3\u4e4b\u95f4\u3002\u5374\u60f3\u4e0d\u8d77\u8d75\u7075\u541b\u7684\u773c\u775b\u5df2\u7741 \u4e0d\u5f00\u6765\uff0c\u4e0d\u89c1\u653b\u52bf\uff0c\u4e0d\u4e3a\u6240\u4e71\uff0c\u8d75\u7075\u541b\u4ecd\u7136\u662f\u7167\u5e73\u5e38\u5e94\u654c\u4e4b\u6cd5\uff0c\u4ed7\u7740\u6570\u5341\u5e74\u529f\u529b\uff0c\u4f7f\u51fa\u5d06\u5cd2 \u201c\u8ff7\u8e2a\u638c\u201d\u7684\u5de7\u62db\uff0c\u8ffd\u7740\u5251\u73af\u54cd\u52a8\u4e4b\u5904\uff0c\u9aa4\u7136\u51fa\u624b\uff0c\u5e78\u4e8f\u4ed6\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u626b\u7740\u5e7d\u840d\u7684\u9999\u80a9\uff0c \u5426\u5219\u518d\u4e0b\u79fb\u6570\u5bf8\uff0c\u5c31\u8981\u89e6\u53ca\u5e7d\u840d\u7684\u9165\u80f8\uff0c\u53ea\u8fd9\u4e00\u638c\u5c31\u80fd\u53eb\u5e7d\u840d\u91cd\u4f24\u3002 \u3000\u3000\u8d75\u7075\u541b\u4e00\u62db\u5f97\u624b\uff0c\u7acb\u523b\u5012\u8dc3\u6570\u6b65\uff0c\u628a\u773c\u775b\u4e00\u63c9\uff0c\u53ea\u89c9\u773c\u524d\u767d\u8499\u8499\u4e00\u7247\uff0c\u666f\u7269\u6a21\u7cca\uff0c\u53c8\u60ca \u53c8\u6012\uff0c\u7834\u53e3\u9a82\u9053\uff1a\u201c\u597d\u72e0\u7684\u8d31\u4eba\uff0c\u975e\u5f97\u628a\u4f60\u7684\u773c\u73e0\u525c\u4e86\uff0c\u96be\u6d88\u6211\u5fc3\u5934\u4e4b\u6c14\uff01\u201d\u6307\u6325\u540c\u95e8\uff0c\u81ea \u5df1\u4e5f\u4ed7\u7740\u201c\u542c\u98ce\u8fa8\u5668\u201d\u4e4b\u672f\uff0c\u56f4\u7740\u5e7d\u840d\u5f3a\u653b\u3002\u5e7d\u840d\u5728\u51b0\u5bab\u4f8d\u5973\u4e4b\u4e2d\uff0c\u867d\u7136\u662f\u6570\u4e00\u6570\u4e8c\u4eba\u7269\uff0c \u771f\u5b9e\u7684\u672c\u9886\u4e0e\u654c\u4eba\u5230\u5e95\u76f8\u5dee\u8fd8\u8fdc\uff0c\u88ab\u8d75\u7075\u541b\u7387\u4f17\u4e00\u9635\u5f3a\u653b\uff0c\u7acb\u523b\u9669\u8c61\u73af\u751f\uff0c\u53ea\u80fd\u4ed7\u7740\u7cbe\u5999\u7684 \u5251\u672f\u4e0e\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u817e\u632a\u95ea\u907f\uff0c\u906e\u62e6\u62db\u67b6\uff0c\u518d\u4e5f\u817e\u4e0d\u51fa\u624b\u518d\u53d1\u51b0\u9b44\u795e\u5f39\u3002 \u3000\u3000\u9648\u5929\u5b87\u89c1\u72b6\u5927\u60ca\uff0c\u62fc\u4e86\u6027\u547d\uff0c\u6325\u5251\u4e00\u9635\u8fde\u73af\u75be\u523a\uff0c\u8fde\u4f7f\u51b0\u5ddd\u5251\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\uff0c\u970e\u65f6\u4e4b \u95f4\uff0c\u53ea\u89c1\u5bd2\u5149\u531d\u5730\uff0c\u5251\u52bf\u5982\u8679\uff0c\u653b\u52bf\u51cc\u5389\u4e4b\u6781\u3002\u8981\u77e5\u51b0\u5bab\u4f8d\u5973\u867d\u5f97\u51b0\u5ddd\u5929\u5973\u4f20\u6388\uff0c\u4f46\u5374\u65e0\u4e00 \u4eba\u5b66\u5f97\u9f50\u5168\u3002\u9648\u5929\u5b87\u79c1\u5b66\u4e86\u5bc6\u5ba4\u77f3\u58c1\u7684\u5251\u6cd5\uff0c\u53c8\u662f\u4ece\u6839\u672c\u7684\u529f\u592b\u505a\u8d77\uff0c\u6240\u4ee5\u53cd\u800c\u6bd4\u51b0\u5bab\u4e00\u4ece \u4f8d\u5973\uff0c\u66f4\u5f97\u51b0\u5ddd\u5251\u6cd5\u7684\u7cbe\u9ad3\u3002\u4e00\u8f6e\u62fc\u547d\u62a2\u653b\uff0c\u7adf\u7ed9\u4ed6\u6740\u5f00\u4e86\u4e00\u6761\u8840\u8def\uff0c\u4e0e\u5e7d\u840d\u4f1a\u5408\u3002\u8fd9\u65f6\u81f3 \u95f4\u95e8\u4eba\u6240\u5e03\u7684\u9635\u52bf\uff0c\u56e0\u8981\u5e94\u4ed8\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u8fd9\u4e24\u4e2a\u65b0\u6765\u7684\u5f3a\u654c\uff0c\u9635\u52bf\u5fae\u89c1\u6563\u4e71\uff0c\u96f7\u9707\u5b50\u4e0e\u5d14 \u4e91\u5b50\u4e5f\u51b2\u51fa\u5305\u56f4\uff0c\u4f1a\u5408\u5728\u4e00\u5904\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u56db\u4eba\u5206\u6210\u4e24\u5bf9\uff0c\u5171\u540c\u5e94\u4ed8\u5d06\u5cd2\u95e8\u4eba\u7684\u56f4\u653b\uff0c\u53cc\u65b9\u5f62\u52bf\uff0c\u53c8\u544a\u7a33\u5b9a\u4e0b\u6765\u3002\u96f7\u9707\u5b50 \u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u53ea\u4ec5\u4ec5\u6570\u6708\u7684\u5de5\u592b\uff0c\u9648\u5929\u5b87\u7684\u6b66\u529f\u5c31\u7cbe\u8fdb\u5982\u65af\uff0c\u770b\u6765\u7adf\u5df2\u8d85\u51fa\u4e86\u4ed6\u7684\u5e08\u7236\u3002 \u5f53\u4e0b\u7cbe\u795e\u5927\u632f\uff0c\u8fbe\u6469\u5251\u6cd5\u4f7f\u5f97\u8fdb\u9000\u81ea\u5982\uff0c\u5df2\u4e0e\u654c\u4eba\u6709\u653b\u6709\u5b88\u3002\u5d14\u4e91\u5b50\u7684\u5f13\u5f26\u91cd\u53c8\u6323\u6323\u4f5c\u54cd\uff0c \u4e0e\u654c\u4eba\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u6708\u4eae\u6e10\u6e10\u897f\u6c89\uff0c\u53cc\u65b9\u5df2\u6597\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u5f62\u52bf\u53c8\u662f\u4e00\u53d8\u3002 \u3000\u3000\u96f7\u9707\u5b50\u3001\u5d14\u4e91\u5b50\u4e8c\u4eba\uff0c\u5728\u4e45\u6218\u4e4b\u4e0b\uff0c\u5df2\u6e10\u89c9\u7b4b\u75b2\u529b\u7aed\uff0c\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u7684\u5251\u6cd5\u867d\u7136\u7cbe\u5999\uff0c \u7a76\u5acc\u529f\u529b\u4e0d\u591f\uff0c\u6218\u4e86\u4e2a\u591a\u65f6\u8fb0\uff0c\u4ea6\u662f\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\uff0c\u8d75\u7075\u541b\u8fd0\u5251\u5982\u98ce\uff0c\u970d\u970d\u8fdb\u8feb\uff0c\u6012\u58f0\u559d \u9053\uff1a\u201c\u5996\u5973\uff0c\u4f60\u53ef\u77e5\u9053\u5389\u5bb3\u4e86\u4e48\uff1f\u5feb\u5c06\u89e3\u836f\u62ff\u6765\uff01\u201d\u8d75\u7075\u541b\u88ab\u51b0\u9b44\u795e\u5f39\u7684\u5947\u5bd2\u4e4b\u6c14\u4fb5\u5165\u773c \u775b\uff0c\u867d\u7136\u4ed7\u7740\u672c\u8eab\u7684\u5185\u529f\u706b\u5019\uff0c\u53ef\u4ee5\u6682\u65f6\u62b5\u5fa1\uff0c\u4f46\u773c\u73e0\u9ebb\u75db\uff0c\u6709\u5982\u53d7\u5229\u9488\u6240\u523a\uff0c\u6781\u4e0d\u597d\u53d7\uff0c \u751f\u6015\u65f6\u5019\u4e00\u4e45\uff0c\u4fbf\u6210\u6b8b\u5e9f\uff0c\u6545\u6b64\u7740\u7740\u8fdb\u8feb\uff0c\u8981\u5e7d\u840d\u5148\u5c06\u89e3\u836f\u62ff\u51fa\u3002 \u3000\u3000\u5e7d\u840d\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u89e3\u836f\uff1f\u201d\u4e0e\u9648\u5929\u5b87\u53cc\u5251\u5408\u58c1\uff0c\u8fde\u6321\u5f00\u4e86\u8d75\u7075\u541b\u7684\u4e09\u62db\u6740 \u624b\u3002\u8d75\u7075\u541b\u559d\u9053\uff1a\u201c\u4f60\u62ff\u4e0d\u62ff\u6765\uff1f\u4f60\u82e5\u518d\u4e0d\u62ff\u6765\uff0c\u6211\u5c31\u662f\u773c\u775b\u778e\u4e86\uff0c\u4e5f\u80fd\u6740\u4f60\uff01\u201d\u5de6\u624b\u63c9 \u773c\uff0c\u53f3\u624b\u957f\u5251\u4e00\u5c55\uff0c\u53c8\u662f\u8fde\u4e0b\u6740\u624b\uff0c\u4ed6\u53cc\u773c\u7ea2\u80bf\uff0c\u4e0d\u4f4f\u6d41\u6cea\uff0c\u50cf\u7efd\u5f00\u4e86\u7684\u80e1\u6843\u4e00\u822c\uff0c\u540c\u95e8\u89c1 \u4e86\uff0c\u4e2a\u4e2a\u6697\u6697\u60ca\u5fc3\u3002\u5e7d\u840d\u751a\u662f\u4fcf\u76ae\uff0c\u867d\u5728\u5371\u9669\u4e4b\u4e2d\uff0c\u4ecd\u662f\u53d1\u58f0\u51b7\u5632\uff1a\u201c\u54c8\uff0c\u6211\u65e9\u53eb\u4f60\u7559\u5fc3\uff0c \u4f60\u4e0d\u7559\u5fc3\uff0c\u602a\u5f97\u8c01\u6765\uff1f\u201d\u5979\u4e5f\u5b66\u96f7\u9707\u5b50\u7684\u6a21\u6837\uff0c\u4e0e\u9648\u5929\u5b87\u8d34\u80cc\u800c\u7acb\uff0c\u53cc\u5251\u76f8\u8054\uff0c\u53c8\u6321\u4e86\u51e0 \u62db\uff0c\u7b11\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4f60\u4eec\u6c49\u4eba\u662f\u7537\u5b50\u6c49\u6d41\u8840\u4e0d\u6d41\u6cea\uff0c\u5440\uff0c\u4f60\u5374\u54ed\u8d77\u6765\u5566\uff0c\u4e0d\u5bb3\u7f9e\u4e48\uff1f\u201d\u8d75\u7075 \u541b\u5927\u6012\uff0c\u75db\u4e0b\u6740\u624b\uff0c\u6307\u6325\u56db\u4e2a\u540c\u95e8\uff0c\u4e00\u9f50\u8fdb\u51fb\uff0c\u628a\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u7684\u5251\u52bf\u538b\u5f97\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u53ea\u662f\u51b0\u5ddd\u5251\u6cd5\u7cbe\u5999\u975e\u5e38\uff0c\u8feb\u5207\u4e4b\u95f4\uff0c\u672a\u80fd\u51fb\u7834\uff0c\u8d75\u7075\u541b\u5927\u6025\uff0c\u8fd0\u8db3\u5185\u529f\uff0c\u75db\u4e0b\u6740\u624b\uff0c\u53c8 \u8fc7\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u547c\u5438\u7d27\u4fc3\uff0c\u88ab\u4ed6\u4eec\u653b\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u770b\u770b\u5c31\u96be\u4ee5\u652f\u6301\uff0c\u8d75\u7075 \u541b\u7684\u773c\u775b\u66f4\u89c9\u523a\u75db\uff0c\u9762\u524d\u4e00\u7247\u6a21\u7cca\uff0c\u53cc\u65b9\u90fd\u6781\u7126\u6025\uff0c\u6b63\u5728\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u66fc\u58f0\u6b4c\u9053\uff1a \u201c\u4e2d\u79cb\u660e\u6708\u5b9c\u540c\u8d4f\uff0c\u5251\u6c14\u817e\u9704\u5374\u4e3a\u4f55\u3002\u201d\u6b4c\u58f0\u4f3c\u662f\u4ece\u5f88\u8fdc\u4e4b\u5904\u4f20\u6765\uff0c\u4f46\u5374\u6765\u5f97\u975e\u5e38\u8fc5\u75be\uff0c\u6b4c \u58f0\u752b\u6b47\uff0c\u53ea\u89c1\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\uff0c\u5df2\u7b11\u541f\u541f\u7684\u6765\u5230\u9762\u524d\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u8eab\u6cd5\u5947\u5feb\uff0c\u5728\u573a\u4eba\u7b49\uff0c\u65e0\u4e0d\u5403\u60ca\uff0c\u8d75\u7075\u541b\u6a2a\u8dc3\u4e09\u6b65\uff0c\u624b\u634f\u5251\u8bc0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u662f\u54ea \u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7\u95ee\u6709\u4f55\u6307\u6559\uff1f\u201d\u767d\u8863\u5c11\u5e74\u51b7\u51b7\u4e00\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u662f\u8981\u6559\u8bad\u6559\u8bad\u4f60 \u4eec\uff0c\u4f60\u4eec\u5d06\u5cd2\u6d3e\u4e5f\u662f\u6b66\u6797\u7684\u4e00\u5927\u5b97\u6d3e\uff0c\u524d\u4ee3\u521b\u4e1a\u6b8a\u4e0d\u5bb9\u6613\uff0c\u4f60\u4e0a\u4e00\u4ee3\u7684\u638c\u95e8\u4e4c\u8499\u9053\u957f\u95e8\u89c4\u751a \u4e25\uff0c\u4e5f\u7b97\u5f97\u662f\u4f4d\u6709\u9053\u4e4b\u58eb\uff0c\u5230\u4f60\u7684\u624b\u4e0a\uff0c\u5374\u5012\u884c\u9006\u65bd\uff0c\u4e0d\u6015\u6127\u5bf9\u5217\u7956\u5217\u5b97\u4e48\uff1f\u201d\u767d\u8863\u5c11\u5e74\u770b \u6765\u4e0d\u8fc7\u4e8c\u5341\u6765\u5c81\uff0c\u8bf4\u8bdd\u5374\u662f\u4e00\u6d3e\u8001\u524d\u8f88\u7684\u53e3\u6c14\uff0c\u8d75\u7075\u541b\u5fc3\u5934\u706b\u8d77\uff0c\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u53cd\u800c \u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9601\u4e0b\u5012\u662f\u60f3\u66ff\u6211\u4eec\u5d06\u5cd2\u6d3e\u6e05\u7406\u95e8\u6237\u4e86\uff1f\u201d\u767d\u8863\u5c11\u5e74\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u4e00\u70b9 \u4e0d\u9519\uff0c\u6211\u6b63\u662f\u4e0d\u5fcd\u89c1\u5d06\u5cd2\u6d3e\u846c\u9001\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u6240\u4ee5\u624d\u4e0d\u6015\u9ebb\u70e6\uff0c\u8981\u7ba1\u7ba1\u4f60\u4eec\u3002\u201d\u8981\u77e5\u6b66\u6797\u4e4b \u4e2d\u7684\u89c4\u77e9\uff0c\u6e05\u7406\u95e8\u6237\u4e4b\u4e8b\u53ea\u6709\u672c\u6d3e\u7684\u5c0a\u957f\u624d\u6709\u6743\u5904\u7f6e\uff0c\u82e5\u7136\u522b\u6d3e\u7684\u4eba\u8981\u4ee3\u4e3a\u6e05\u7406\uff0c\u90a3\u4eba\u5c31\u4e00 \u5b9a\u5f97\u662f\u6b66\u6797\u4e2d\u5fb7\u9ad8\u671b\u91cd\u7684\u5927\u5b97\u5e08\u3001\u8001\u524d\u8f88\uff0c\u800c\u4eca\u8fd9\u767d\u8863\u5c11\u5e74\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8bf4\u8bdd\u7684\u53e3\u6c14\u5374\u4e25\u7136\u4ee5 \u8d75\u7075\u541b\u7684\u524d\u8f88\u81ea\u5c45\u3002\u4e0d\u4f46\u8d75\u7075\u541b\u88ab\u6fc0\u8d77\u65e0\u540d\u706b\u8d77\uff0c\u6240\u6709\u5d06\u5cd2\u6d3e\u7684\u95e8\u4eba\uff0c\u4e5f\u65e0\u4e0d\u607c\u6012\u3002 \u3000\u3000\u8d75\u7075\u541b\u63c9\u63c9\u773c\u775b\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u957f\u5251\u4e00\u6307\uff0c\u9053\uff1a\u201c\u6211\u5fdd\u4e3a\u5d06\u5cd2\u6d3e\u638c\u95e8\uff0c\u8981\u52b3\u8001\u524d\u8f88\u4ee3\u4e3a\u6e05 \u7406\u95e8\u6237\uff0c\u5b9e\u5728\u60ed\u6127\uff01\u53ea\u662f\u6211\u8d75\u67d0\u51a5\u987d\u4e0d\u7075\uff0c\u96be\u4ee5\u542c\u4f60\u7684\u6559\u8bad\uff0c\u8bf7\u6055\u5c0f\u8f88\u6297\u547d\u5566\uff01\u201d\u5d06\u5cd2\u95e8 \u4eba\uff0c\u9f50\u90fd\u5927\u7b11\uff0c\u7b11\u90a3\u767d\u8863\u5c11\u5e74\u72c2\u5984\u4e0d\u77e5\u81ea\u91cf\u3002\u767d\u8863\u5c11\u5e74\u4e0d\u52a8\u58f0\u8272\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u6a2a\u626b\u5168\u573a\uff0c \u9053\uff1a\u201c\u4f60\u4eec\u771f\u8981\u6211\u52a8\u624b\u5417\uff1f\u201d\u773c\u5149\u5982\u7535\uff0c\u8bdd\u8bed\u5a01\u4e25\uff0c\u4e00\u526f\u6267\u6cd5\u8005\u7684\u53e3\u543b\u3002\u8d75\u7075\u541b\u5927\u6012\u559d\u9053\uff1a \u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6d3b\u5f97\u4e0d\u8010\u70e6\u5566\uff0c\u5feb\u62d4\u51fa\u5251\u6765\uff0c\u770b\u662f\u4f60\u6559\u8bad\u6211\u8fd8\u662f\u6211\u6559\u8bad\u4f60\uff01\u201d\u767d\u8863\u5c11\u5e74\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5bf9\u4ed8\u4f60\u4eec\uff0c\u4f55\u987b\u62d4\u5251\uff01\u5929\u5b87\uff0c\u4f60\u4eec\u90fd\u9000\u51fa\u53bb\uff0c\u514d\u5f97\u788d\u6211\u65bd\u5c55\u3002\u8d75\u7075\u541b\uff0c\u4f60\u628a\u540c\u95e8\u90fd\u53eb \u4e0a\u6765\uff0c\u7701\u5f97\u6211\u591a\u8d39\u624b\u811a\uff01\u201d\u9648\u5929\u5b87\u5e94\u4e86\u4e00\u58f0\uff0c\u4e0e\u5e7d\u840d\u53cc\u53cc\u8dc3\u51fa\u5708\u5b50\u3002\u96f7\u9707\u5b50\u60ca\u5f02\u4e4b\u6781\uff0c\u8138\u4e0a \u4e00\u526f\u7591\u60d1\u7684\u795e\u60c5\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u6b66\u529f\u7eb5\u9ad8\uff0c\u4e5f\u672a\u5fc5\u80fd\u662f\u8d75\u7075\u541b\u654c\u624b\uff1b\u5982\u4eca\u4ed6\u5374\u8981\u72ec\u81ea\u5bf9\u4ed8 \u5d06\u5cd2\u6d3e\u7684\u5341\u4e09\u540d\u9ad8\u624b\uff0c\u83ab\u975e\u4ed6\u662f\u72c2\u4eba\u4e48\uff1f\u201d\u9648\u5929\u5b87\u6025\u9053\uff1a\u201c\u96f7\u5927\u54e5\uff0c\u5feb\u9000\uff01\u201d\u96f7\u9707\u5b50\u4e0e\u5d14\u4e91 \u5b50\u521a\u8dc3\u51fa\u5708\u5b50\uff0c\u53ea\u89c1\u5d06\u5cd2\u5341\u4e09\u540d\u9ad8\u624b\u4e00\u6d8c\u800c\u4e0a\uff0c\u5c31\u5728\u8fd9\u540c\u4e00\u77ac\u95f4\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u628a\u624b\u4e00\u626c\uff0c\u6ee1 \u7a7a\u55e4\u55e4\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u63a5\u7740\u662f\u4e00\u7247\u60e8\u53eb\u4e4b\u58f0\uff0c\u5d06\u5cd2\u5341\u4e09\u540d\u9ad8\u624b\uff0c\u8fde\u8d75\u7075\u541b\u5728\u5185\uff0c\u4e00\u9f50\u5012 \u5730\uff0c\u4e2a\u4e2a\u6323\u624e\u547c\u53f7\uff0c\u5374\u662f\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u96f7\u9707\u5b50\u76ee\u77aa\u53e3\u5446\uff0c\u53ea\u542c\u5f97\u90a3\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8d75\u7075\u541b\u4f60\u670d\u4e86\u5417\uff1f\u201d\u8d75\u7075\u541b\u529f\u529b\u8f83\u9ad8\uff0c\u5f3a \u5fcd\u7740\u75bc\u75db\uff0c\u6b20\u8eab\u5750\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u6559\u8bad\uff0c\u6211\u4eec\u82e5\u7136\u4e0d\u6b7b\uff0c\u5fc5\u5f53\u94ed\u8bb0\u4e8e\u5fc3\uff0c\u8bf7\u6559\u9601\u4e0b\u5c0a \u59d3\u5927\u540d\u3002\u201d\u4ed6\u5230\u5e95\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u60e8\u8d25\u4e4b\u4f59\uff0c\u8fd8\u4e0d\u5fd8\u4ea4\u4ee3\u51e0\u53e5\u573a\u9762\u8bf4\u8bdd\uff0c\u8bdd\u4e2d\u7684\u610f\u601d\u5176\u5b9e\u662f\uff1a \u201c\u82e5\u7136\u4e0d\u6b7b\uff0c\u5fc5\u5f53\u62a5\u4ec7\u3002\u201d\u90a3\u767d\u8863\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u62a5\u4ec7\uff0c\u522b\u505a\u68a6\u5566\uff1f\u4f60\u5341\u4e09\u4e2a\u4eba\uff0c\u4e2a \u4e2a\u7684\u7435\u7436\u9aa8\u90fd\u5df2\u88ab\u523a\u7a7f\uff0c\u6b7b\u662f\u6b7b\u4e0d\u4e86\u7684\uff0c\u4f46\u518d\u60f3\u901e\u80fd\uff0c\u90a3\u53ef\u662f\u4e0d\u884c\u5566\uff0c\u597d\u597d\u56de\u5bb6\u53bb\u5b89\u4efd\u8fc7\u6d3b \u5427\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5728\u573a\u4eba\u7b49\uff0c\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff01\u8fd9\u5c11\u5e74\u5728\u4e00\u4e3e\u624b\u4e4b\u95f4\uff0c\u8fde\u4f24\u5341\u4e09\u540d\u5d06\u5cd2\u9ad8\u624b\uff0c\u5df2 \u662f\u9a87\u4eba\u95fb\u542c\uff0c\u800c\u6240\u4f24\u4e4b\u5904\u53c8\u90fd\u662f\u7435\u7436\u9aa8\u7684\u8981\u5bb3\u5173\u8282\uff0c\u90a3\u7b80\u76f4\u662f\u4e0d\u53ef\u601d\u8bae\u3002\u8d75\u7075\u541b\u4e0d\u7531\u81ea\u4e3b\u7684 \u7528\u624b\u4e00\u634f\uff0c\u89e6\u624b\u4e4b\u5904\uff0c\u7435\u7436\u9aa8\u679c\u7136\u788e\u4e86\uff0c\u53ea\u75db\u5f97\u4ed6\u773c\u6cea\u76f4\u6d41\uff0c\u767e\u9ab8\u6b32\u6563\uff0c\u7435\u7436\u9aa8\u4e00\u7a7f\uff0c\u5373\u6210 \u5e9f\u4eba\uff0c\u7eb5\u6709\u591a\u5f3a\u7684\u6b66\u529f\uff0c\u4e5f\u65bd\u5c55\u4e0d\u5f00\uff0c\u53ea\u80fd\u4f5c\u5bfb\u5e38\u4eba\u6240\u80fd\u505a\u7684\u4f53\u529b\u64cd\u4f5c\u4e86\u3002\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a \u201c\u9976\u4f60\u4e00\u6b7b\uff0c\u8fd8\u4e0d\u77e5\u8db3\u5417\uff1f\u597d\u597d\u5730\u56de\u5bb6\u8fc7\u6d3b\u5427\u3002\u201d\u8d75\u7075\u541b\u6cae\u4e27\u4e4b\u6781\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u8bf7\u9601\u4e0b \u5f00\u6069\u3002\u5c06\u6697\u5668\u53d6\u51fa\uff0c\u8ba9\u6211\u4eec\u4e5f\u5f00\u5f00\u773c\u754c\u3002\u4ed6\u8fde\u4e2d\u7684\u662f\u4ec0\u4e48\u6674\u5668\uff0c\u5c1a\u672a\u77e5\u9053\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4e00\u628a\u7cbe\u8292\u56db\u5c04\u7684\u5b9d\u5251\uff0c\u9053\uff1a\u201c\u521a\u624d\u7528\u4e0d\u7740\u4ed6\uff0c\u73b0\u5728 \u53ef\u7528\u5f97\u7740\u4e86\u3002\u201d\u8d75\u7075\u541b\u80c6\u6218\u5fc3\u60ca\uff0c\u672a\u53ca\u8bf4\u8bdd\uff0c\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u628a\u5b9d\u5251\u6401\u5728\u4ed6\u7684\u80a9\u4e0a\uff0c\u8f7b\u8f7b\u4e00 \u62cd\uff0c\u767b\u65f6\u4f3c\u89c9\u6709\u4e00\u6839\u957f\u523a\u4ece\u9aa8\u5934\u91cc\u8df3\u51fa\u6765\uff0c\u90a3\u5c11\u5e74\u53cc\u6307\u4e00\u62c8\uff0c\u5728\u8d75\u7075\u541b\u773c\u524d\u4e00\u6643\uff0c\u9053\uff1a\u201c\u77a7 \u6e05\u695a\u4e86\uff01\u201d\u8fd9\u6697\u5668\u975e\u91d1\u975e\u94c1\uff0c\u9ed1\u9edd\u9edd\u7684\u597d\u50cf\u4e00\u652f\u77ed\u5c0f\u7684\u6ca1\u7fbd\u7bad\uff0c\u90a3\u5c11\u5e74\u53c8\u5728\u5251\u4e0a\u4e00\u5f39\uff0c\u5b9d\u5251 \u53d1\u51fa\u5578\u58f0\uff0c\u6e05\u8d8a\u4e4b\u6781\uff0c\u8d75\u7075\u541b\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5929\u5c71\u795e\u8292\u548c\u6e38\u9f99\u5b9d\u5251\uff01\u201d\u767d\u8863\u5c11\u5e74 \u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u73b0\u5728\u53ef\u77e5\u9053\u6211\u7684\u6765\u5386\u4e86\u5427\uff1f\u201d\u6e38\u9f99\u5251\u662f\u5929\u5c71\u6d3e\u7684\u9547\u5c71\u4e4b\u5b9d\uff0c\u4f69\u6709\u8fd9\u628a\u5b9d\u5251 \u7684\u4eba\uff0c\u4e0d\u95ee\u53ef\u77e5\u5b9a\u662f\u5929\u5c71\u6d3e\u7684\u5ae1\u7cfb\u4f20\u4eba\u3002\u6392\u7b97\u8d77\u6765\uff0c\u5929\u5c71\u6d3e\u7684\u73b0\u5728\u638c\u95e8\u5510\u6653\u6f9c\u8981\u6bd4\u8d75\u7075\u541b\u9ad8 \u4e24\u8f88\uff0c\u8fd9\u5c11\u5e74\u82e5\u662f\u4ed6\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u90a3\u4e48\u8f88\u4efd\u5c31\u786e\u5728\u8d75\u7075\u541b\u4e4b\u4e0a\u3002 \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u4f9d\u4f6f\u65bd\u4e3a\uff0c\u7247\u523b\u4e4b\u95f4\u3002\u5c06\u5341\u4e09\u6839\u5929\u5c71\u795e\u8292\u5c3d\u884c\u53d6\u51fa\uff0c\u53c8\u5bf9\u5e7d\u840d\u8bf4\u9053\uff1a\u201c\u4ed6\u6b66 \u529f\u5df2\u5e9f\uff0c\u4e0d\u80fd\u4e3a\u60a3\uff0c\u4e0d\u5fc5\u53eb\u4ed6\u773c\u76f2\u4e86\u5427\u3002\u201d\u5e7d\u840d\u5bf9\u8fd9\u767d\u8863\u5c11\u5e74\u4f69\u670d\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u4f9d\u4f60\u7684\u5429\u5490 \u4fbf\u662f\u3002\u201d\u53d6\u51fa\u4e13\u89e3\u5947\u5bd2\u4e4b\u6c14\u7684\u9633\u548c\u4e38\uff0c\u53eb\u8d75\u7075\u541b\u54bd\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u597d\u597d\u56de\u53bb\u9759\u517b\u4e09\u5929\u5427\u3002\u201d \u3000\u3000\u8d75\u7075\u541b\u63c9\u63c9\u773c\u775b\uff0c\u6ca1\u7cbe\u6253\u5f69\uff0c\u6709\u5982\u6597\u8d25\u7684\u516c\u9e21\uff0c\u5411\u767d\u8863\u5c11\u5e74\u65bd\u4e86\u4e00\u793c\uff0c\u5728\u540c\u95e8\u6276\u6301\u4e4b \u4e0b\uff0c\u843d\u8352\u800c\u9003\uff0c\u767d\u8863\u5c11\u5e74\u54c8\u54c8\u5927\u7b11\uff0c\u5bf9\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u4e00\u4ed7\u771f\u662f\u75db\u5feb\u4e4b\u6781\uff01\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u597d\u9020 \u5316\uff0c\u5728\u51b0\u5bab\u4f4f\u4e86\u4e09\u6708\uff0c\u5927\u975e\u6614\u6bd4\u4e86\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u548c\u51b0\u5ddd\u5929\u5973\u5728\u4e00\u8d77\u5417\uff1f\u201d\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u5979\u624d\u4e0d\u80af\u548c\u6211\u5728\u4e00\u8d77 \u5462\u3002\u6211\u6b63\u8981\u5411\u4f60\u6253\u542c\u5979\u7684\u4e0b\u843d\u3002\u201d\u5e7d\u840d\u6025\u9053\uff1a\u201c\u90a3\u4e00\u65e5\u4f60\u4e0d\u662f\u548c\u6211\u4eec\u7684\u516c\u4e3b\u6bd4\u5251\u5417\uff1f\u201d\u767d\u8863 \u5c11\u5e74\u9053\uff1a\u201c\u6bd4\u5251\u4e4b\u7ea6\uff0c\u53ea\u6709\u5f85\u4e4b\u5f02\u65e5\u4e86\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5251\u867d\u6ca1\u6bd4\u6210\uff0c\u4f60\u603b\u8be5\u89c1\u7740\u5979\u963f\uff01\u201d\u767d \u8863\u5c11\u5e74\u9053\uff1a\u201c\u6211\u672a\u5230\u51b0\u5cf0\u4e4b\u4e0b\uff0c\u5df1\u53d1\u89c9\u5730\u9707\u7684\u9884\u5146\uff0c\u6211\u8fd8\u4f1a\u53bb\u9001\u6b7b\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u90a3\u4e48 \u8bf4\uff0c\u4f60\u6839\u672c\u6ca1\u89c1\u7740\u5979\u4e48\uff1f\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u62c5\u5fc3\u4ec0\u4e48\uff1f\u6211\u80fd\u9003\u5f97\u51fa\uff0c\u5979\u5c82\u6709\u9003\u4e0d\u51fa\u4e4b\u7406\uff1f \u90a3\u65e5\u6211\u5411\u5317\u9003\uff0c\u89c1\u5979\u7684\u5f71\u5b50\u5411\u5357\u65b9\u9003\u8d70\uff0c\u540e\u6765\u706b\u5c71\u7206\u53d1\uff0c\u7194\u5ca9\u8ff8\u88c2\uff0c\u8981\u627e\u4e5f\u4e0d\u6210\u5566\u3002\u539f\u6765\u5979 \u8fd8\u6ca1\u6709\u56de\u5230\u51b0\u5bab\u5417\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u542c\u90a3\u767d\u8863\u5c11\u5e74\u8bf4\u66fe\u89c1\u51b0\u5ddd\u5929\u5973\u9003\u8d70\uff0c\u7a0d\u7a0d\u5bbd\u5fc3\u3002\u90a3\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u4eec\u662f \u8981\u53bb\u62c9\u8428\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u662f\u3002\u201d\u767d\u8863\u5c11\u5e74\u6c89\u541f\u534a\u54cd\uff0c\u5ffd\u5730\u638f\u51fa\u4e00\u4e2a\u9526\u76d2\uff0c\u9053\uff1a\u201c\u4f60\u7236\u4eb2 \u5728\u798f\u5eb7\u5b89\u90a3\u513f\uff0c\u5c31\u6258\u4f60\u5c06\u8fd9\u9526\u76d2\u4ea4\u7ed9\u798f\u5eb7\u5b89\uff0c\u7701\u5f97\u6211\u591a\u8d70\u4e00\u8d9f\u4e86\u3002\u201d\u9648\u5929\u5b87\u63a5\u8fc7\u9526\u76d2\uff0c\u6b63\u6b32 \u8be2\u95ee\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u66ff\u6211\u4ea4\u5230\u4fbf\u662f\u3002\u5bf9\u4f60\u7684\u7236\u4eb2\u5927\u6709\u597d\u5904\u3002\u54b1\u4eec\u540e\u4f1a\u6709\u671f\uff0c\u4f60\u4e5f \u4e0d\u5fc5\u518d\u95ee\u5566\u3002\u201d\u53c8\u5bf9\u96f7\u9707\u5b50\u9053\uff1a\u201c\u4f60\u4e5f\u8be5\u56de\u56db\u5ddd\u4e86\uff0c\u89c1\u7740\u5192\u5927\u4fa0\u4e4b\u65f6\uff0c\u8bf7\u4ee3\u6211\u95ee\u5019\u4e00\u58f0\u3002\u201d \u626c\u624b\u9053\u522b\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u4eba\u5f71\u4e0d\u89c1\u3002\u96f7\u9707\u5b50\u8fde\u9047\u5f02\u4eba\uff0c\u50b2\u6c14\u5c3d\u6d88\uff0c\u76ee\u9001\u90a3\u5c11\u5e74\u80cc\u5f71\uff0c\u597d\u4e45\u8bf4\u4e0d \u51fa\u8bdd\u3002 \u3000\u3000\u9648\u5929\u5b87\u7b49\u56db\u4eba\u6b47\u4e86\u4e00\u9635\uff0c\u5927\u8fb9\u9732\u51fa\u66d9\u5149\uff0c\u56db\u4eba\u5206\u9053\u626c\u9570\uff0c\u96f7\u9707\u5b50\u4e0e\u5d14\u4e91\u5b50\u56de\u56db\u5ddd\uff0c\u9648\u5929 \u5b87\u4e0e\u5e7d\u840d\u53bb\u62c9\u8428\uff0c\u4e00\u8def\u65e0\u4e8b\u3002\u8bdd\u4f11\u70e6\u7d6e\uff0c\u8fd9\u65e5\u5230\u4e86\u62c9\u8428\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u8857\u4e0a\u884c\u4eba\u7199\u6765\u6518 \u5f80\uff0c\u597d\u4e0d\u70ed\u95f9\u3002 \u3000\u3000\u9648\u5929\u5b87\u8fdb\u5230\u57ce\u5185\uff0c\u4fbf\u60f3\u5411\u884c\u4eba\u6253\u542c\u9a7b\u85cf\u5927\u81e3\u798f\u5eb7\u5b89\u7684\u603b\u90e8\u6240\u5728\uff0c\u5e7d\u840d\u5ffd\u9053\uff1a\u201c\u4f55\u5fc5\u5fd9\u5728 \u8fd9\u4e00\u65f6\uff0c\u54b1\u4eec\u5148\u73a9\u5b83\u4e00\u665a\uff0c\u770b\u770b\u62c9\u8428\u7684\u591c\u666f\uff0c\u660e\u5929\u518d\u53bb\u627e\u4f60\u7236\u4eb2\u4e5f\u4e0d\u8fdf\u3002\u201d\u9648\u5929\u5b87\u5fae\u5fae\u4e00 \u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u4f4f\u8fdb\u798f\u5927\u5e05\u7684\u5b98\u8859\uff0c\u8981\u518d\u51fa\u6765\u73a9\u53ef\u771f\u662f\u4e0d\u80fd\u4efb\u610f\u5566\u3002\u201d\u4ed6\u66fe\u7b54\u5e94\u966a\u5e7d\u840d\u6e38 \u89c8\u62c9\u8428\uff0c\u8fd9\u65f6\u53ea\u597d\u8df5\u8a00\uff0c\u62c9\u7740\u5e7d\u840d\u7684\u624b\uff0c\u5230\u5904\u6e9c\u8fbe\u3002 \u3000\u3000\u62c9\u8428\u662f\u897f\u85cf\u7684\u9996\u5e9c\uff0c\u4ea6\u662f\u4e16\u754c\u8457\u540d\u7684\u9ad8\u57ce\uff0c\u5728\u6d77\u62d4\u4e09\u5343\u591a\u516c\u5c3a\u4e4b\u4e0a\uff0c\u62c9\u8428\u57ce\u80cc\u5411\u5c97\u5e95\u65af \u5c71\u8109\uff0c\u56db\u5468\u7684\u9ad8\u5c71\u5927\u5cad\u8038\u5165\u4e91\u9704\uff0c\u5c42\u5c42\u5cf0\u5ce6\u4e0a\u767d\u96ea\u7691\u7691\uff0c\u8857\u5e02\u4e2d\u5e73\u9876\u7684\u623f\u5c4b\u4e0e\u5e10\u7bf7\u4ea4\u6742\uff0c\u4e0e \u5185\u5730\u57ce\u5e02\u7684\u98ce\u5149\u5927\u4e0d\u76f8\u540c\uff0c\u4ece\u65e0\u6570\u5e10\u7bf7\u4e2d\u53d1\u51fa\u70b9\u70b9\u70db\u5149\uff0c\u66f4\u663e\u51fa\u62c9\u8428\u4e4b\u591c\u7684\u4e00\u79cd\u795e\u79d8\u6c14\u6c1b\u3002 \u501a\u5c71\uff08\u8461\u8404\u5c71\uff09\u5efa\u7b51\u7684\u5e03\u8fbe\u62c9\u5bab\u7684\u5c16\u9876\u53d1\u51fa\u95ea\u95ea\u91d1\u5149\uff0c\u5728\u96ea\u5c71\u6620\u7167\u4e4b\u4e0b\uff0c\u6781\u4e3a\u58ee\u4e3d\u3002\u5e7d\u840d \u9053\uff1a\u201c\u54b1\u4eec\u5230\u90a3\u91cc\u53bb\u770b\u770b\u3002\u201d\u9648\u5929\u5b87\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6d3b\u4f5b\u6240\u4f4f\u7684\u5bab\u6bbf\uff0c\u8f7b\u6613\u600e\u80fd\u8fdb\u53bb\u3002\u6211\u5e26\u4f60 \u5230\u4e0b\u9762\u7684\u5e7f\u573a\u53bb\u770b\u5427\u3002\u201d \u3000\u3000\u5e03\u8fbe\u62c9\u5bab\u4e0b\u9762\u7684\u5e7f\u573a\uff0c\u662f\u62c9\u8428\u57ce\u4e00\u4e2a\u70ed\u95f9\u7684\u4e2d\u5fc3\uff0c\u56db\u5468\u5168\u662f\u5e10\u7bf7\uff0c\u4e2d\u592e\u6709\u5404\u79cd\u5404\u5f0f\u7684\u5c0f \u8d29\u644a\u6863\uff0c\u8fd8\u6709\u5356\u5531\u7684\u3001\u73a9\u620f\u7684\u3001\u800d\u6742\u6280\u7684\u4e94\u5149\u5341\u8272\uff0c\u76ee\u4e0d\u80dc\u6536\u3002\u5e7d\u840d\u957f\u5e74\u4f4f\u5728\u51b0\u5bab\uff0c\u51e0\u66fe\u89c1 \u8fc7\u5982\u6b64\u70ed\u95f9\u7684\u4eba\u95f4\u666f\u8272\uff0c\u53ea\u89c9\u90a3\u4e9b\u706f\u8272\u7684\u70df\u96fe\uff0c\u6bd4\u51b0\u5bab\u4e2d\u7684\u7f8e\u666f\u8fd8\u8981\u60a6\u76ee\u3002\u4ed6\u4eec\u770b\u4e86\u5370\u5ea6\u4eba \u7684\u5f04\u86c7\u5999\u6280\uff0c\u53c8\u53bb\u770b\u56de\u7586\u6765\u7684\u54c8\u8428\u514b\u4eba\u800d\u7684\u6742\u6280\uff0c\u4e00\u4e2a\u4eba\u8868\u6f14\u541e\u5251\uff0c\u4e00\u4e2a\u4eba\u8868\u6f14\u5410\u706b\uff0c\u8868\u6f14 \u541e\u5251\u7684\u4eba\u5c06\u4e00\u628a\u957f\u8fbe\u4e09\u5c3a\u7684\u5229\u5251\u523a\u4eba\u53e3\u4e2d\uff0c\u53ea\u9732\u51fa\u4e00\u622a\u77ed\u77ed\u7684\u5251\u67c4\u3002\u5e7d\u840d\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4eba\u7684 \u6b66\u529f\u5c82\u975e\u6bd4\u90a3\u767d\u8863\u5c11\u5e74\u8fd8\u8981\u5389\u5bb3\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u662f\u5047\u7684\u3002\u201d\u8bdd\u8bf4\u672a\u5b8c\uff0c\u90a3\u4eba\u5c06\u5251\u62d4\u51fa\uff0c \u8f7b\u8f7b\u4e00\u6298\uff0c\u5c06\u5229\u5251\u6298\u4e3a\u4e09\u53e0\uff0c\u539f\u6765\u90a3\u662f\u9521\u5236\u7684\u5251\uff0c\u53ef\u4ee5\u6298\u66f2\u7684\u3002 \u3000\u3000\u5e7d\u840d\u770b\u5f97\u563b\u563b\u54c8\u54c8\uff0c\u597d\u4e0d\u5f00\u5fc3\u3002\u5ffd\u89c9\u6709\u4eba\u8f7b\u8f7b\u5730\u78b0\u4e86\u5979\u4e00\u4e0b\uff0c\u628a\u624b\u4e00\u6478\uff0c\u90a3\u67c4\u968f\u8eab\u4f69\u5e26 \u7684\u51b0\u9b44\u5bd2\u5149\u5251\u7adf\u7136\u4e0d\u89c1\u4e86\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u95f9\u5e02\u795e\u5077\u65bd\u5999\u624b\uff0c\u51b0\u5bab\u4f8d\u5973\u4e5f\u60ca\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\uff0c  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u6f20\u5916\u9690\u795e\u9f99\u3000\u9ad8\u6df1\u83ab\u6d4b\u3000\u8352\u5c71\u9022\u5f02\u58eb\u3000\u865a\u5b9e\u96be\u77e5 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c \u5341 \u56de \u6f20\u5916\u9690\u795e\u9f99\u3000\u9ad8\u6df1\u83ab\u6d4b \u8352\u5c71\u9022\u5f02\u58eb\u3000\u865a\u5b9e\u96be\u77e5 \u3000\u3000\u5e7d\u840d\u8fd9\u4e00\u5413\u975e\u540c\u5c0f\u53ef\uff0c\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u9648\u5929\u5b87\u6b63\u6293\u7740\u4e00\u4e2a\u4eba\uff0c\u53eb\u9053\uff1a\u201c\u5c31\u662f\u4ed6\uff01\u201d\u51b0\u9b44 \u5bd2\u5149\u5251\u7684\u5251\u9798\u8fd8\u9690\u9690\u5728\u4ed6\u7f69\u888d\u5e95\u4e0b\u9732\u51fa\u3002\u5e7d\u840d\u6025\u5fd9\u4e0a\u524d\u62a2\u5251\uff0c\u90a3\u4eba\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u5f80\u4eba\u4e1b\u4e2d \u4e00\u94bb\uff0c\u4e00\u6e9c\u70df\u5730\u8dd1\u4e86\uff0c\u9648\u5929\u5b87\u624b\u4e2d\u5374\u591a\u51fa\u4e00\u4ef6\u957f\u886b\u3002\u8fd9\u4e00\u62db\u6b63\u662f\u6252\u624b\u4eec\u60ef\u7528\u7684\u201c\u91d1\u8749\u8131\u58f3\u201d \u4e4b\u8ba1\u3002 \u3000\u3000\u9648\u5929\u5b87\u5927\u53eb\u201c\u6349\u8d3c\u201d\uff0c\u8ddf\u8e2a\u8ffd\u62ff\uff0c\u9648\u5929\u5b87\u8f7b\u529f\u867d\u597d\uff0c\u5374\u8fdc\u4e0d\u5982\u90a3\u4eba\u6e9c\u6ed1\uff0c\u4e00\u6643\u773c\u95f4\uff0c\u90a3 \u4eba\u5df2\u6e9c\u51fa\u4eba\u4e1b\u3002\u9648\u5929\u5b87\u649e\u5f97\u770b\u70ed\u95f9\u7684\u4e1c\u5012\u897f\u6b6a\uff0c\u8ffd\u51fa\u6765\u65f6\uff0c\u53ea\u89c1\u90a3\u4eba\u5df2\u98de\u8eab\u8df3\u4e0a\u4e00\u5ea7\u5e10\u7bf7\u3002 \u5728\u8fd9\u79cd\u4e09\u6559\u4e5d\u6d41\u4f1a\u96c6\u7684\u9732\u5927\u5e02\u573a\uff0c\u6252\u624b\u62a2\u4e1c\u897f\u4e43\u662f\u5e38\u89c1\u4e4b\u4e8b\uff0c\u770b\u70ed\u95f9\u7684\u4eba\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u53cd\u800c \u9a82\u9648\u5929\u5b87\u83bd\u649e\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u6324\u51fa\u4eba\u4e1b\uff0c\u53ea\u89c1\u90a3\u4e2a\u6252\u624b\u5728\u5e10\u7bf7\u4e0a\u6367\u7740\u51b0\u9b44\u5bd2\u5149\u5251\u7ec6\u5fc3\u89c2\u8d4f\uff0c\u5567\u5567\u8d5e\u9053\uff1a \u201c\u597d\u5251\uff0c\u597d\u5251\uff01\u201d\u5e7d\u840d\u5927\u6012\uff0c\u4e0e\u9648\u5929\u5b87\u53cc\u53cc\u8dc3\u8d77\uff0c\u4e5f\u98de\u8eab\u8df3\u4e0a\u5e10\u7bf7\uff0c\u90a3\u4eba\u7fe9\u5982\u98de\u9e1f\uff0c\u4e09\u8d77\u4e09 \u843d\uff0c\u5df2\u8df3\u8fc7\u51e0\u9053\u5e10\u7bf7\uff0c\u843d\u5728\u5e7f\u573a\u540e\u9762\u7684\u7a7a\u5730\u3002 \u3000\u3000\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u51db\uff1a\u8fd9\u6252\u624b\u7684\u8f7b\u529f\u7adf\u7136\u4e0d\u5728\u4ed6\u4eec\u4e4b\u4e0b\uff01\u8fd9\u5e7f\u573a\u662f\u62c9\u8428\u57ce\u5185\u8461\u8404\u5c71\u4e0b\u7684\u4e00\u5927 \u7247\u7a7a\u5730\uff0c\u800c\u5e03\u8fbe\u62c9\u5bab\u5c31\u5efa\u5728\u5c71\u4e0a\u3002\u8fd9\u6252\u624b\u5954\u4e0a\u5c71\u5761\uff0c\u5374\u4e0d\u662f\u671d\u7740\u5e03\u8fbe\u62c9\u5bab\u7684\u65b9\u5411\uff0c\u800c\u662f\u5411\u897f \u5357\u65b9\u843d\u8352\u800c\u9003\u3002\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u7d27\u7d27\u8ddf\u8e2a\uff0c\u603b\u662f\u8ddd\u79bb\u6570\u4e08\u4e4b\u5730\uff0c\u8ffd\u4ed6\u4e0d\u4e0a\u3002\u9648\u5929\u5b87\u6697\u6697\u60ca\u5947\uff0c \u9053\uff1a\u201c\u6b64\u4eba\u6050\u6015\u4e0d\u662f\u5bfb\u5e38\u6252\u624b\uff01\u201d\u5e7d\u840d\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u4ed6\u628a\u6211\u7684\u5b9d\u5251\u5077\u53bb\uff0c\u6211\u5c31\u653e\u4ed6 \u4e0d\u8fc7\uff01\u201d \u3000\u3000\u6252\u624b\u5728\u524d\uff0c\u4e24\u4eba\u5728\u540e\uff0c\u98ce\u9a70\u7535\u9010\uff0c\u518d\u8ffd\u4e86\u7247\u523b\uff0c\u5df2\u4ece\u5c71\u524d\u8ffd\u5230\u5c71\u540e\uff0c\u8ffd\u5165\u65f7\u50fb\u7684\u5c71\u5730\uff0c \u5c71\u4e0a\u5e03\u8fbe\u62c9\u5bab\u7684\u706f\u706b\uff0c\u9690\u9690\u7167\u89c1\u90a3\u4eba\u7684\u80cc\u5f71\uff0c\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\uff0c\u8bf7\u522b\u620f\u800d\u5566\uff01\u201d\u90a3 \u4eba\u4e0d\u7406\u4e0d\u776c\uff0c\u4e00\u80a1\u52b2\u5730\u5f80\u524d\u98de\u9003\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u63e1\u5728\u4ed6\u7684\u624b\u4e2d\uff0c\u6b63\u597d\u501f\u7740\u5b9d\u5251\u7684\u5149\u8292\u7ed9\u4ed6\u7167 \u8def\uff0c\u8ffd\u4e86\u4e00\u9635\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u66f4\u8fdc\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u90a3\u6252\u624b\u53c8\u505c\u4e86\u4e0b\u6765\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u5ea7\u623f\u5c4b\u900f\u51fa\u706f\u706b\uff0c\u623f\u5c4b\u5f62\u5f0f\u751a\u602a\uff0c\u597d\u50cf\u5e10\u84ec\u4e00\u6837\uff0c \u4e0d\u662f\u5e38\u89c1\u7684\u65b9\u5f62\u623f\u5c4b\u800c\u662f\u5706\u5f62\u7684\uff0c\u56db\u5468\u56f4\u6709\u56f4\u5899\uff0c\u6c14\u6d3e\u4e0d\u5c0f\u3002\u90a3\u6252\u624b\u5954\u5230\u5706\u5c4b\u4e4b\u524d\uff0c\u7eb5\u8eab\u4e00 \u8df3\uff0c\u8df3\u4e0a\u56f4\u5899\uff0c\u907f\u8fdb\u5c4b\u5185\u3002 \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u539f\u6765\u8fd9\u91cc\u7adf\u662f\u5f3a\u76d7\u7a9d\u3002\u201d\u98de\u8eab\u8ddf\u5165\u3002\u9648\u5929\u5b87\u60f3\u529d\u5979\u4e0d\u53ef\u9020\u6b21\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u53ea \u597d\u8ddf\u5979\u8fdb\u53bb\u3002 \u3000\u3000\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u5927\u5385\u4e0a\u70b9\u7740\u4e24\u884c\u7c97\u5982\u513f\u81c2\u7684\u725b\u6cb9\u70db\uff0c\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\uff0c\u5385\u4e0a\u5750\u7740\u4e00\u4f4d\u7a7f \u7740\u6ee1\u5dde\u670d\u9970\u7684\u6b66\u5b98\uff0c\u90a3\u6252\u624b\u5c06\u51b0\u9b44\u5bd2\u5149\u5251\u6367\u4e0a\uff0c\u6b66\u5b98\u62bd\u51fa\u6765\u4e00\u770b\uff0c\u201c\u54a6\u201d\u7684\u4e00\u58f0\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u662f\u8fd9\u628a\u5251\u3002\u90a3\u5973\u5b50\u4e5f\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u51b0\u9b44\u5bd2\u5149\u5251\u540d\u7b26\u5176\u5b9e\uff0c\u4e00\u79bb\u5251\u9798\uff0c\u4fbf\u662f\u4e00\u7247\u5bd2\u5149\uff0c\u5bfb\u5e38\u4eba\u53ea\u8981\u88ab\u8fd9\u5bd2\u5149\u51b7\u6c14\u4e00\u51b2\uff0c\u7acb\u523b \u4fbf\u4f1a\u6655\u5012\u3002\u8fd9\u519b\u5b98\u5374\u89c6\u82e5\u65e0\u4e8b\uff0c\u628a\u5bd2\u5149\u5251\u5728\u9762\u524d\u6643\u6765\u6643\u53bb\uff0c\u8fde\u5bd2\u6218\u4e5f\u4e0d\u6253\u4e00\u4e2a\u3002 \u3000\u3000\u5e7d\u840d\u7fe9\u5982\u98de\u4e4c\uff0c\u63a0\u4e0a\u53f0\u9636\uff0c\u53eb\u9053\uff1a\u201c\u8fd8\u6211\u5251\u6765\uff01\u201d\u90a3\u519b\u5b98\u76ef\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u5251\u662f\u4f60 \u7684\u5417\uff1f\u5440\uff0c\u4e0d\u5bf9\u5440\uff01\u201d\u5e7d\u840d\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d\u90a3\u519b\u5b98\u772f\u7740\u4e00\u53cc\u773c\u5411\u5979\u4e0a\u4e0b\u6253\u91cf\uff0c\u9053\uff1a\u201c\u4f60 \u518d\u8d70\u4e24\u6b65\u770b\u770b\u3002\u201d\u5e7d\u840d\u5927\u6012\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u4e00\u626c\u624b\u5c31\u662f\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u5206\u6253\u519b\u5b98\u4e0e\u90a3\u6252\u624b\uff0c\u90a3 \u519b\u5b98\u8eab\u6cd5\u597d\u5feb\uff0c\u53ea\u89c1\u4ed6\u4e00\u4f38\u624b\uff0c\u5c31\u62a2\u5728\u6252\u624b\u7684\u524d\u5934\uff0c\u7528\u201c\u5343\u81c2\u5982\u6765\u201d\u7684\u63a5\u6697\u5668\u624b\u6cd5\uff0c\u5c06\u4e24\u679a \u51b0\u9b44\u795e\u5f39\u90fd\u63a5\u5230\u624b\u4e2d\u3002\u51b0\u9b44\u795e\u5f39\u7ed9\u4ed6\u4e00\u634f\uff0c\u90fd\u5728\u638c\u5fc3\u7206\u88c2\u4e86\uff0c\u4e00\u7f15\u7f15\u5bd2\u6c14\u5728\u4ed6\u6307\u7f1d\u4e4b\u95f4\u900f \u51fa\u3002 \u3000\u3000\u5e7d\u840d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5389\u5bb3\u4e86\u4e48\uff1f\u8fd8\u6562\u4e0d\u6562\u8981\u6211\u7684\u5b9d\u5251\uff1f\u201d\u51b0\u9b44\u795e\u5f39\u7684\u5bd2\u6c14\uff0c\u79bb\u8eab\u6570 \u5c3a\uff0c\u5c31\u5df2\u523a\u4f53\u4fb5\u80a4\uff0c\u4f55\u51b5\u5728\u638c\u5fc3\u634f\u788e\uff1f\u5e7d\u840d\u53ea\u9053\u4ed6\u5b9a\u7136\u7981\u53d7\u4e0d\u4f4f\uff0c\u5fc5\u8981\u8ba8\u9976\uff0c\u90a3\u6599\u8fd9\u519b\u5b98\u628a \u624b\u638c\u4e00\u644a\uff0c\u968f\u624b\u5728\u8863\u4e0a\u4e00\u63e9\uff0c\u5c06\u51b0\u6c34\u62b9\u5e72\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff1a\u201c\u8fd9\u6697\u5668\u5012\u6709\u70b9\u90aa\u95e8\uff0c\u5e78\u4e8f\u662f \u6211\uff0c\u8981\u662f\u522b\u4eba\uff0c\u4e0d\u51b7\u6b7b\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u9a87\uff0c\u8fd9\u519b\u5b98\u624b\u634f\u51b0\u9b44\u795e\u5f39\uff0c\u4ecd\u662f\u82e5\u65e0\u5176\u4e8b\uff0c\u8fd9\u4efd\u672c\u4e8b\uff0c\u770b\u6765\u4e0d\u5728\u767d \u8863\u5c11\u5e74\u4e4b\u4e0b\u3002\u4ed6\u6b63\u6b32\u4e0a\u524d\u884c\u793c\uff0c\u5e7d\u840d\u5df2\u6b3a\u8eab\u6025\u8fdb\uff0c\u5de6\u638c\u4e00\u6325\uff0c\u53f3\u638c\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u638c\u52bf\u98d8 \u5ffd\uff0c\u4f3c\u5de6\u53cd\u53f3\uff0c\u8fd9\u662f\u8fbe\u6469\u638c\u6cd5\u4e2d\u4e00\u4e2a\u5389\u5bb3\u7684\u64d2\u62ff\u62db\u6570\uff0c\u90a3\u519b\u5b98\u6447\u6447\u5934\u9053\uff1a\u201c\u8d8a\u53d1\u4e0d\u5bf9\u4e86\uff01\u201d \u624b\u81c2\u4e00\u4f38\uff0c\u500f\u7684\u6293\u4e0b\u3002\u9648\u5929\u5b87\u5927\u5403\u4e00\u60ca\uff0c\u770b\u8fd9\u519b\u5b98\u51fa\u624b\uff0c\u51cc\u5389\u65e0\u6bd4\uff0c\u53ea\u6050\u5e7d\u840d\u53d7\u4f24\uff0c\u5fc3\u6025\u4e4b \u4e0b\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u62d4\u5251\u4fbf\u523a\u3002\u90a3\u519b\u5b98\u9053\uff1a\u201c\u597d\u4fca\u7684\u529f\u592b\uff0c\u540e\u8f88\u4e4b\u4e2d\uff0c\u4e5f\u662f\u4e0d\u53ef\u591a\u89c1 \u7684\u4e86\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u4e0d\u7f13\uff0c\u5de6\u81c2\u53c8\u500f\u4e00\u4f38\uff0c\u9648\u5929\u5b87\u53ea\u89c9\u624b\u6307\u4e00\u677e\uff0c\u957f\u5251\u5df2\u7ed9\u4ed6\u5939\u624b\u62a2 \u53bb\uff0c\u4eba\u4e5f\u88ab\u6293\u7740\u3002 \u3000\u3000\u90a3\u519b\u5b98\u53cc\u624b\u9f50\u51fa\uff0c\u5c06\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u90fd\u6293\u4e86\u8d77\u6765\uff0c\u968f\u624b\u4e00\u63b7\uff0c\u4e24\u4eba\u8fd8\u672a\u53eb\u51fa\u58f0\u97f3\uff0c\u90fd\u5df2\u88ab \u4ed6\u8f7b\u8f7b\u7684\u63b7\u843d\u4e00\u5f20\u6709\u9760\u80cc\u7684\u6905\u4e0a\uff0c\u7aef\u7aef\u6b63\u6b63\u5730\u5750\u7740\uff0c\u4e1d\u6beb\u4e5f\u6ca1\u6709\u53d7\u4f24\uff0c\u529b\u5ea6\u7528\u5f97\u4e4b\u5999\uff0c\u771f\u662f \u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u7741\u5927\u4e86\u773c\uff0c\u53ea\u89c1\u90a3\u519b\u5b98\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u628a\u5251\u8fd8\u4f60\u4eec\u4e0d\u96be\uff0c\u4f46\u4f60\u4eec\u53ef\u5f97 \u5b9e\u8bf4\uff0c\u7a76\u7adf\u662f\u4f55\u4eba\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5bb6\u7236\u662f\u8428\u8fe6\u5ba3\u6170\u4f7f\u9648\u5b9a\u57fa\u3002\u201d\u90a3\u519b\u5b98\u5475\u5440\u4e00\u58f0\u9053\uff1a\u201c\u539f \u6765\u662f\u9648\u516c\u5b50\uff0c\u9002\u624d\u5f97\u7f6a\u4e86\u3002\u201d\u53c8\u95ee\u5e7d\u840d\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u5e7d\u840d\u8d4c\u6c14\u4e0d\u7b54\uff0c\u90a3\u519b\u5b98\u9053\uff1a\u201c\u9002\u624d\u5192 \u72af\uff0c\u5b9e\u662f\u51fa\u4e8e\u4e00\u573a\u8bef\u4f1a\u3002\u6211\u4ee5\u4e3a\u4f60\u662f\u53e6\u4e00\u4e2a\u5973\u5b50\uff0c\u8c01\u77e5\u4f60\u548c\u5979\u6240\u7528\u7684\u5b9d\u5251\uff0c\u867d\u7136\u76f8\u4f3c\uff0c\u4f60\u7684 \u6b66\u529f\u5374\u4e0e\u5979\u5dee\u5f97\u592a\u8fdc\uff01\u6240\u4ee5\u6211\u8fde\u8bf4\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u90fd\u8df3\u4e86\u8d77\u6765\uff0c\u5e7d \u840d\u62a2\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u5230\u4ec0\u4e48\u5973\u5b50\u4e86\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u5979\u4ec0\u4e48\u4eba\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u6211\u662f\u5979 \u7684\u4f8d\u5973\uff01\u201d\u90a3\u519b\u5b98\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5514\uff0c\u8fd9\u5c31\u5bf9\u4e86\u3002\u90a3\u4f60\u7684\u4e3b\u4eba\u53c8\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u5e7d\u840d\u4e0d\u77e5\u8fd9\u519b\u5b98\u662f\u4f55\u6837\u4eba\uff0c\u5fc3\u4e2d\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u90a3\u519b\u5b98\u9053\uff1a\u201c\u6211\u59d3\u9f99\uff0c\u540d\u53eb\u7075\u77eb\uff0c\u6392\u884c\u7b2c \u4e09\uff0c\u670b\u53cb\u5acc\u6211\u540d\u5b57\u96be\u8bb0\uff0c\u90fd\u53eb\u6211\u505a\u9f99\u4e09\u3002\u9648\u516c\u5b50\u60f3\u5fc5\u542c\u8fc7\u6211\u7684\u540d\u5b57\uff1f\u201d\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u51db\uff0c\u539f \u6765\u773c\u524d\u8fd9\u4f4d\u5176\u8c8c\u4e0d\u626c\u7684\u519b\u5b98\uff0c\u5c31\u662f\u798f\u5eb7\u5b89\u5e10\u4e0b\u7b2c\u4e00\u5947\u4eba\u2014\u2014\u9f99\u4e09\u5148\u751f\uff01 \u3000\u3000\u9648\u5929\u5b87\u66fe\u542c\u7236\u4eb2\u8bf4\u8fc7\uff0c\u8bf4\u798f\u5927\u5e05\u5e10\u4e2d\uff0c\u6709\u4e00\u4e2a\u4e0d\u9732\u9762\u7684\u795e\u79d8\u5e55\u5ba2\uff0c\u4eba\u79f0\u9f99\u4e09\u5148\u751f\uff0c\u5b98\u8854 \u53ea\u662f\u53c2\u8d5e\uff0c\u4f46\u798f\u5927\u5e05\u5374\u5bf9\u4ed6\u8a00\u542c\u8ba1\u4ece\uff0c\u8fb9\u7586\u7684\u8bb8\u591a\u63aa\u65bd\uff0c\u90fd\u662f\u51fa\u4e8e\u4ed6\u7684\u8ba1\u5212\u3002\u636e\u8bf4\u6b64\u4eba\u672c\u9886 \u4e4b\u9ad8\uff0c\u4e0d\u53ef\u601d\u8bae\uff0c\u798f\u5eb7\u5b89\u5728\u60c5\u51b5\u6700\u590d\u6742\u7684\u62c9\u8428\u505a\u9a7b\u85cf\u5927\u81e3\uff0c\u51e0\u5e74\u6765\u5168\u65e0\u98ce\u9669\uff0c\u5f97\u9f99\u4e09\u4e4b\u529b\u4e0d \u5c11\u3002\u4f46\u9f99\u4e09\u4e4b\u540d\uff0c\u4e5f\u53ea\u662f\u798f\u5eb7\u5b89\u624b\u4e0b\u7684\u82e5\u5e72\u8981\u5458\u77e5\u9053\uff0c\u5916\u95f4\u77e5\u8005\u7edd\u5c11\u3002\u5373\u5982\u8427\u9752\u5cf0\u4e0e\u9648\u5929\u5b87 \u8c08\u8d77\u65f6\uff0c\u5bf9\u9f99\u4e09\u7684\u672c\u4e8b\uff0c\u4e5f\u6781\u8868\u6000\u7591\uff1b\u8ba4\u4e3a\u771f\u6709\u5927\u672c\u4e8b\u7684\uff0c\u5fc5\u4e0d\u4f1a\u5728\u798f\u5eb7\u5b89\u624b\u4e0b\u505a\u4e00\u4e2a\u5c0f\u5c0f \u7684\u53c2\u8d5e\u3002\u9648\u5929\u5b87\u4e5f\u8ba4\u4e3a\u5e08\u7236\u8bf4\u7684\u6709\u7406\uff0c\u4f46\u540e\u6765\u5728\u51b0\u5bab\u4e4b\u65f6\uff0c\u4e0e\u94c1\u62d0\u4ed9\u8c08\u8bba\u5f53\u4eca\u7684\u6b66\u6797\u5947\u58eb\uff0c \u63d0\u8d77\u9f99\u4e09\uff0c\u94c1\u62d0\u4ed9\u5374\u5927\u4e3a\u4f69\u670d\uff0c\u8bf4\u9f99\u4e09\u771f\u662f\u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\u7684\u795e\u79d8\u4eba\u7269\u3002\u5f53\u65f6\u9648\u5929\u5b87\u66fe\u95ee\u8d77 \u9f99\u4e09\u7684\u4e8b\u8ff9\uff0c\u4f46\u94c1\u62d0\u4ed9\u5374\u4e0d\u80af\u591a\u8bf4\uff0c\u53ea\u8bf4\u82e5\u6709\u4e00\u65e5\u80fd\u591f\u4e0b\u5c71\uff0c\u90a3\u65f6\u4ed6\u4e5f\u8bb8\u8981\u5e26\u9648\u5929\u5b87\u53bb\u4f1a\u4e00 \u4f1a\u4ed6\uff0c\u53ef\u60dc\u7b49\u4e0d\u5230\u4e0b\u5c71\uff0c\u94c1\u62d0\u4ed9\u5c31\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u4eca\u65e5\u9648\u5929\u5b87\u76ee\u7779\u9f99\u4e09\u7684\u6b66\u529f\uff0c\u59cb\u77e5\u540d\u4e0b\u65e0\u865a\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u4f69\u670d\u3002\u9f99\u4e09\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u53ef \u4ee5\u5c06\u4f60\u4e3b\u4eba\u7684\u540d\u5b57\u89c1\u544a\u4e86\u5427\uff1f\u201d\u5e7d\u840d\u4ecd\u4e0d\u77e5\u9053\u9f99\u4e09\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u773c\u5149\u95ea\u70c1\uff0c\u4e3b\u610f\u4e0d\u5b9a\u3002\u9648\u5929 \u5b87\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u8fc7\u90a3\u5973\u5b50\u7684\uff1f\u201d\u9f99\u4e09\u9053\uff1a\u201c\u4f60\u4e5f\u8ba4\u5f97\u5979\u7684\u4e3b\u4eba\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5979\u7684\u4e3b \u4eba\u4fbf\u662f\u51b0\u5ddd\u5929\u5973\uff01\u201d \u3000\u3000\u9f99\u4e09\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u60ca\u5f02\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u539f\u6765\u662f\u51b0\u5ddd\u5929\u5973\uff0c\u6211\u8fd8\u4ee5\u4e3a\u51b0\u5ddd\u5929\u5973\u53ea\u662f\u795e \u8bdd\u4f20\u8bf4\u4e2d\u7684\u4eba\u7269\uff0c\u539f\u6765\u771f\u6709\u5176\u4eba\uff01\u201d\u5e7d\u840d\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u8fc7\u5979\u7684\uff1f\u201d\u9f99\u4e09\u9053\uff1a\u201c\u5c31\u5728\u524d\u4e09\u5929 \u7684\u665a\u4e0a\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u600e\u4e48\u89c1\u7740\u7684\uff1f\u201d\u9f99\u4e09\u9053\uff1a\u201c\u5979\u5230\u6211\u8fd9\u91cc\u62ff\u4e86\u4e00\u4ef6\u4e1c\u897f\u53bb\u3002\u201d\u5e7d\u840d\u51b7 \u7b11\u9053\uff1a\u201c\u5979\u4f1a\u62ff\u4f60\u7684\u4e1c\u897f\uff1f\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u5927\u76f8\u4fe1\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u9f99\u4e09\u907f\u800c\u4e0d\u7b54\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u8981\u7d27\u7684\u7269\u4e8b\uff0c\u4f46\u6211\u4e0d\u613f\u8ba9 \u5979\u62ff\u53bb\uff0c\u53ef\u60dc\u5f53\u65f6\u7559\u4e0d\u4f4f\u5979\u3002\u201d\u539f\u6765\u524d\u4e09\u5929\u665a\u4e0a\uff0c\u6709\u4e00\u4e2a\u5973\u5b50\u5230\u9f99\u4e09\u7684\u5bb6\u4e2d\u76d7\u53bb\u4e86\u4e00\u4efd\u9f99\u4e09 \u6240\u62df\u8ba2\u7684\uff0c\u9a7b\u85cf\u5927\u81e3\u51c6\u5907\u600e\u6837\u53bb\u8fce\u63a5\u91d1\u672c\u5df4\u74f6\u7684\u8ba1\u5212\uff0c\u90a3\u5973\u5b50\u8f7b\u529f\u8d85\u5999\u4e4b\u6781\uff0c\u9f99\u4e09\u8d76\u51fa\u53bb\u548c \u5979\u52a8\u624b\uff0c\u5979\u51fa\u624b\u5982\u98ce\uff0c\u624b\u4e0a\u7684\u5b9d\u5251\uff0c\u53c8\u5bd2\u5149\u95ea\u95ea\uff0c\u523a\u4eba\u773c\u76ee\uff0c\u9f99\u4e09\u548c\u5979\u4ea4\u624b\u4e94\u62db\uff0c\u5360\u4e0d\u4e86\u534a \u70b9\u4fbf\u5b9c\uff0c\u5728\u5bd2\u5149\u95ea\u70c1\u4e4b\u4e0b\uff0c\u9762\u8c8c\u8fd8\u672a\u66fe\u770b\u5f97\u6e05\u695a\uff0c\u90a3\u5973\u5b50\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u795e\u9f99\u5999\u6280\uff0c \u4ea6\u4e0d\u8fc7\u5982\u6b64\uff01\u201d\u7a81\u7136\u4e00\u8bb0\u602a\u62db\uff0c\u5c06\u4ed6\u903c\u9000\uff0c\u98d8\u8eab\u8d70\u4e86\u3002\u8fd9\u5973\u5b50\u7684\u602a\u5f02\u884c\u5f84\uff0c\u4ee4\u89c1\u591a\u8bc6\u5e7f\u7684\u9f99 \u7075\u77eb\u4e5f\u6349\u6478\u4e0d\u5b9a\uff0c\u6545\u6b64\u624d\u6709\u4eca\u65e5\u7684\u4e00\u573a\u8bef\u4f1a\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u542c\u4e86\u9f99\u4e09\u5148\u751f\u7684\u53d9\u8ff0\uff0c\u5404\u6709\u6240\u601d\uff0c\u9648\u5929\u5b87\u5fc3\u9053\uff1a\u8fd9\u5973\u5b50\u5fc5\u662f\u51b0\u5ddd\u5929\u5973\u65e0 \u7591\u3002\u5e7d\u840d\u5374\u60f3\u9053\uff1a\u51b0\u5bab\u4e2d\u4ec0\u4e48\u5947\u73cd\u5f02\u5b9d\u6ca1\u6709\uff0c\u54b1\u4eec\u7684\u5c0f\u516c\u4e3b\u5c82\u4f1a\u770b\u4e0a\u5c18\u4e16\u7684\u4e1c\u897f\uff1f\u51b0\u5bab\u591a \u5b9d\uff0c\u8bb8\u591a\u5f02\u6d3e\u4e2d\u4eba\u95fb\u98ce\u89ca\u89ce\uff0c\u8fd9\u4eba\u60f3\u5fc5\u662f\u4e0d\u6000\u597d\u5fc3\uff0c\u6545\u610f\u634f\u9020\u8fd9\u4e00\u756a\u8bf4\u8bdd\uff0c\u60f3\u5957\u53d6\u53e3\u98ce\uff0c\u63a2 \u542c\u54b1\u4eec\u516c\u4e3b\u7684\u79d8\u5bc6\u3002\u5979\u54ea\u91cc\u6599\u60f3\u5f97\u5230\u51b0\u5ddd\u5929\u5973\u6240\u76d7\u53d6\u7684\u6587\u4ef6\uff0c\u6bd4\u4ec0\u4e48\u5947\u73cd\u5f02\u5b9d\u90fd\u91cd\u8981\u5f97\u591a\u3002 \u3000\u3000\u5e7d\u840d\u795e\u8272\u6709\u5f02\uff0c\u9f99\u7075\u77eb\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u65e9\u770b\u51fa\u5979\u7684\u7591\u5fc3\uff0c\u4fbf\u4e5f\u4e0d\u518d\u591a\u95ee\uff0c\u5c06\u51b0\u9b44\u5bd2\u5149\u5251\u53d1 \u8fd8\u7ed9\u5979\u3002\u9648\u5929\u5b87\u6b63\u5f85\u544a\u8f9e\uff0c\u9f99\u7075\u77eb\u5ffd\u9053\uff1a\u201c\u9648\u516c\u5b50\uff0c\u4f60\u4eec\u5982\u4e0d\u5acc\u8717\u5c45\u5c48\u819d\uff0c\u8bf7\u5728\u8fd9\u91cc\u4f4f\u5bbf\u4e00 \u5bb5\u3002\u660e\u65e5\u6211\u548c\u4f60\u5230\u798f\u5927\u5e05\u5b98\u8859\uff0c\u4f60\u7239\u4e5f\u4f1a\u5728\u90a3\u91cc\u7684\u3002\u201d\u9648\u5929\u5b87\u95ee\u9053\uff1a\u201c\u5bb6\u7236\u4e5f\u4f4f\u5728\u8859\u95e8\u91cc \u5417\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u5728\u5916\u8fb9\u79df\u6709\u623f\u5b50\uff0c\u660e\u65e5\u662f\u798f\u5927\u5e05\u7ea6\u4ed6\u8c08\u8bdd\uff0c\u542c\u8bf4\u4ed6\u5f88\u5feb\u5c31\u53ef\u4ee5\u518d \u56de\u8428\u8fe6\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u9648\u5929\u5b87\u3001\u9f99\u7075\u77eb\u53bb\u89c1\u798f\u5eb7\u5b89\uff0c\u7559\u4e0b\u5e7d\u840d\u5728\u9f99\u5bb6\u7b49\u5019\u3002\u9a7b\u85cf\u5927\u81e3\u7684\u8859\u95e8\u5c31\u8bbe \u5728\u62c9\u8428\u5e02\u4e2d\u5fc3\u5927\u662d\u5bfa\u9644\u8fd1\uff0c\u8def\u4e0a\u9f99\u7075\u77eb\u95ee\u8d77\u51b0\u5ddd\u5929\u5973\u7684\u4e00\u4e9b\u4e8b\u60c5\uff0c\u9648\u5929\u5b87\u5c3d\u81ea\u5df1\u6240\u77e5\u7684\u8bf4 \u4e86\uff0c\u9f99\u7075\u77eb\u66f4\u662f\u6697\u6697\u79f0\u5f02\u3002 \u3000\u3000\u5230\u4e86\u5e9c\u8859\uff0c\u9f99\u7075\u77eb\u53eb\u9648\u5929\u5b87\u5728\u7b7e\u62bc\u623f\u7a0d\u5019\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u91cc\u9762\u7684\u4f8d\u4ece\u4f20\u51fa\u8bdd\u6765\uff0c\u53eb\u9648\u5929\u5b87 \u8fdb\u53bb\u3002\u9648\u5929\u5b87\u8e0f\u4e0a\u77f3\u9636\uff0c\u4fbf\u542c\u5f97\u9f99\u7075\u77eb\u7684\u7b11\u58f0\u9053\uff1a\u201c\u9648\u5927\u4eba\uff0c\u6211\u8bf4\u4f60\u4eca\u65e5\u6709\u610f\u5916\u7684\u60ca\u559c\u4e4b \u4e8b\uff0c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u4f60\u770b\u662f\u8c01\u6765\u4e86\uff1f\u201d\u9648\u5929\u5b87\u8d70\u8fdb\u5c4b\u5185\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e74\u7ea6\u56db\u65ec\u7684\u6ee1\u6d32\u8d35\u5b98\u5750\u5728\u4e2d \u5802\uff0c\u53cc\u76ee\u70af\u70af\uff0c\u7709\u5b57\u4e4b\u95f4\u5374\u4f3c\u9690\u6709\u91cd\u5fe7\u3002\u5750\u5728\u8fd9\u8d35\u5b98\u65c1\u8fb9\u7684\u4eba\uff0c\u6b63\u662f\u9648\u5929\u5b87\u7684\u7236\u4eb2\u9648\u5b9a\u57fa\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u559c\u51fa\u671b\u5916\uff0c\u53eb\u9053\uff1a\u201c\u5b87\u513f\uff0c\u5feb\u6765\u62dc\u89c1\u798f\u5927\u5e05\u3002\u201d\u9648\u5929\u5b87\u4f9d\u5b98\u573a\u4e4b\u793c\uff0c\u89c1\u8fc7\u4e86\u798f\u5eb7 \u5b89\u4e4b\u540e\uff0c\u4f8d\u7acb\u5728\u7236\u4eb2\u8eab\u8fb9\u3002\u798f\u5eb7\u5b89\u671b\u4e86\u9648\u5929\u5b87\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4ee4\u90ce\u4e00\u8868\u4eba\u6750\uff0c\u96cf\u51e4\u6e05\u5982\u8001\u98ce \u58f0\uff0c\u5c06\u6765\u7684\u529f\u540d\u5bcc\u8d35\uff0c\u6211\u770b\u5b9a\u5728\u8001\u5927\u4eba\u4e4b\u4e0a\uff0c\u53ef\u559c\u53ef\u8d3a\u5440\uff01\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u5168\u4ed7\u5927\u5e05\u683d \u57f9\u3002\u201d\u9648\u5929\u5b87\u5bf9\u8fd9\u5957\u5b98\u573a\u5e94\u916c\uff0c\u5fc3\u4e2d\u751a\u662f\u538c\u70e6\uff0c\u4e0d\u5f85\u798f\u5eb7\u5b89\u95ee\u6d3b\uff0c\u4fbf\u9053\uff1a\u201c\u798f\u5927\u5e05\uff0c\u6709\u4e00\u4e2a \u4eba\u6258\u6211\u5e26\u4e00\u4ef6\u4e1c\u897f\u7ed9\u4f60\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u8be7\u9053\uff1a\u201c\u6709\u4eba\u6258\u4f60\u5e26\u4e1c\u897f\u7ed9\u6211\uff1f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u9648\u5929\u5b87\u4ece\u6000\u4e2d\u638f\u51fa\u767d\u8863\u5c11\u5e74\u7ed9\u4ed6\u7684 \u90a3\u4e2a\u9526\u76d2\uff0c\u53cc\u624b\u6367\u4e0a\uff0c\u798f\u5eb7\u5b89\u6253\u5f00\u9526\u76d2\uff0c\u5185\u88f9\u4e43\u662f\u4e00\u4efd\u6587\u4e66\uff0c\u798f\u5eb7\u5b89\u5c55\u5f00\u4e00\u770b\uff0c\u9762\u8272\u500f\u53d8\uff0c \u5ffd\u5730\u6309\u7740\u90a3\u4efd\u6587\u4e66\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u4ea4\u7ed9\u4f60\u7684\uff1f\u201d\u9762\u4e0a\u73b0\u51fa\u53c8\u60ca\u53c8\u559c\u7684\u795e\u60c5\uff0c\u9648\u5b9a\u57fa\u60f4\u60f4\u4e0d \u5b89\uff0c\u671b\u7740\u513f\u5b50\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u5728\u8def\u4e0a\u76f8\u9047\u7684\u5c11\u5e74\u4e66\u751f\u6258\u7684\u3002\u201d\u9648\u5b9a\u57fa\u4e0d\u77e5\u8fd9\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5fc3\u4e2d\u6697 \u9a82\u513f\u5b50\u8352\u5510\uff0c\u600e\u597d\u968f\u4fbf\u5c06\u964c\u751f\u4eba\u6240\u6258\u7684\u4e1c\u897f\u4ea4\u7ed9\u798f\u5eb7\u5b89\u3002\u798f\u5eb7\u5b89\u5374\u5e76\u4e0d\u53d1\u6012\uff0c\u53ea\u5411\u9f99\u7075\u77eb\u62db \u4e00\u62db\u624b\uff0c\u793a\u610f\u53eb\u4ed6\u6765\u770b\uff0c\u9f99\u7075\u77eb\u77a5\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u798f\u5927\u5e05\uff0c\u4f60\u7684\u5fc3\u4e8b\u53ef\u653e\u4e0b\u6765\u4e86\uff0c\u54c8\uff0c\u9648\u516c \u5b50\uff0c\u4f60\u8fd9\u4f4d\u670b\u53cb\u53ef\u5e2e\u4e86\u6211\u4eec\u4e0d\u5c11\u5fd9\u5440\uff01\u201d \u3000\u3000\u9648\u5b9a\u57fa\u83ab\u660e\u6240\u4ee5\uff0c\u53ea\u542c\u5f97\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u5947\u602a\u900f\u4e86\uff0c\u9648\u5144\uff0c\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u90a3\u4f4d\u670b\u53cb \u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u840d\u6c34\u76f8\u9022\uff0c\u6211\u8fd8\u672a\u77e5\u9053\u4ed6\u7684\u6765\u5386\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff0c\u5b9a \u7136\u662f\u4f4d\u5927\u6709\u672c\u9886\u7684\u4eba\uff0c\u4f46\u636e\u6211\u770b\u6765\uff0c\u8fd9\u6587\u4e66\u4e0d\u662f\u4ed6\u76d7\u7684\u3002\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u600e\u6837\u89c1\u5f97\uff1f\u201d\u9f99\u7075 \u77eb\u9053\uff1a\u201c\u82e5\u7136\u662f\u4ed6\u6240\u76d7\u3002\u4ed6\u5c31\u4e0d\u4f1a\u8f6c\u5f2f\u62b9\u89d2\u7684\u6258\u4eba\u9001\u56de\u6765\u4e86\u3002\u201d\u798f\u5eb7\u5b89\u6c89\u541f\u4e0d\u8bed\uff0c\u9f99\u7075\u77eb \u9053\uff1a\u201c\u8fd9\u7c7b\u7684\u6c5f\u6e56\u5f02\u4eba\uff0c\u884c\u4e8b\u591a\u51fa\u4eba\u610f\u5916\uff0c\u6211\u770b\u9648\u516c\u5b50\u6240\u8bf4\u7684\u4e5f\u662f\u5b9e\u60c5\uff0c\u5927\u5e05\u4e0d\u5fc5\u67e5\u95ee\u4e86\u3002 \u54b1\u4eec\u6b63\u6709\u7528\u7740\u9648\u516c\u5b50\u4e4b\u5904\u5462\uff01\u201d\u798f\u5eb7\u5b89\u7fdf\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u8fd8\u662f\u5546\u91cf\u600e\u6837\u8fce\u63a5\u91d1\u672c\u5df4\u74f6 \u7684\u4e8b\u8981\u7d27\uff0c\u9648\u5144\uff0c\u8bf7\u5750\u3002\u201d \u3000\u3000\u9648\u5b9a\u57fa\u6309\u637a\u4e0d\u4f4f\uff0c\u95f4\u9053\uff1a\u201c\u6562\u95ee\u5927\u5e05\uff0c\u90a3\u662f\u4ec0\u4e48\u6587\u4e66\uff1f\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u662f\u7687\u4e0a\u5fa1\u5236\uff0c\u516b \u767e\u91cc\u52a0\u7d27\u9001\u6765\u7684\u8bcf\u4e66\u3002\u201d\u9648\u5b9a\u57fa\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u9762\u5982\u571f\u8272\uff0c\u65e2\u7136\u662f\u8fd9\u6837\u7d27\u8981\u7684\u6587\u4e66\uff0c\u4f55\u4ee5\u4f1a \u5230\u4e86\u964c\u751f\u4eba\u7684\u624b\u4e0a\uff0c\u800c\u4e14\u53c8\u8f6c\u5230\u4e86\u81ea\u5df1\u513f\u5b50\u7684\u624b\u4e2d\uff1f\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u4e0d\u77e5\u662f\u7978\u662f\u798f\u3002\u53ea\u542c\u5f97 \u798f\u5eb7\u5b89\u53c8\u9053\uff1a\u201c\u8bcf\u4e66\u4e0a\u5199\u660e\u7531\u4eac\u4e2d\u62a4\u9001\u6765\u7684\u91d1\u672c\u5df4\u74f6\uff0c\u5c06\u7ecf\u7531\u54ea\u6761\u8def\u7ebf\uff0c\u6bcf\u65e5\u5728\u4f55\u5904\u6b47\u5bbf\u7684 \u65e5\u7a0b\u4e5f\u5199\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6309\u8fd9\u65e5\u7a0b\uff0c\u51c6\u5b9a\u5728\u660e\u5e74\u5927\u5e74\u521d\u4e00\uff0c\u9001\u5230\u62c9\u8428\uff0c\u8981\u6211\u4eec\u90ca\u8fce\u4e94\u767e\u91cc\uff0c\u9001 \u5230\u62c9\u8428\u4e4b\u540e\uff0c\u5c06\u4f9b\u5949\u5728\u5927\u662d\u5bfa\u3002\u4e00\u5e94\u4eea\u5f0f\uff0c\u4e5f\u90fd\u5728\u8bcf\u4e66\u4e0a\u6ce8\u660e\u4e86\u3002\u6211\u81ea\u4e0a\u6b21\u7684\u90aa\u62a5\uff0c\u5df2\u77e5\u9053 \u91d1\u74f6\u5373\u5c06\u79bb\u4eac\uff0c\u6b63\u5728\u7126\u8651\uff0c\u4f55\u4ee5\u8fd9\u4efd\u8bcf\u4e66\u8fd8\u4e0d\u9001\u5230\uff0c\u53c8\u4e0d\u6562\u8bf7\u793a\uff0c\u73b0\u5728\u53ef\u5b89\u5fc3\u4e86\u3002\u201d \u3000\u3000\u9648\u5b9a\u57fa\u5413\u5f97\u51b7\u6c57\u90fd\u6d41\u51fa\u6765\uff0c\u6014\u6014\u5730\u671b\u7740\u90a3\u4e2a\u9526\u76d2\uff0c\u53c8\u770b\u770b\u513f\u5b50\u3002\u53ea\u542c\u5f97\u798f\u5eb7\u5b89\u7eed\u9053\uff1a \u201c\u53ea\u662f\u5982\u6b64\u4e00\u6765\uff0c\u663e\u660e\u8fd9\u4efd\u8bcf\u4e66\u66fe\u5728\u9014\u4e2d\u88ab\u4eba\u52ab\u4e86\uff0c\u9001\u8bcf\u4e66\u7684\u4f8d\u536b\uff0c\u4e0b\u843d\u4e5f\u8fd8\u4e0d\u77e5\uff0c\u5c06\u6765\u7687 \u4e0a\u8ffd\u7a76\uff0c\u8fd9\u7f6a\u540d\u4e5f\u7740\u5b9e\u4e0d\u8f7b\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5927\u5e05\u653e\u5fc3\uff0c\u8fd9\u4efd\u8bcf\u4e66\u5df2\u5230\u4e86\u6211\u4eec\u624b\u4e0a\uff0c\u5c06\u6765\u5f85 \u4f8d\u536b\u5230\u65f6\uff0c\u54b1\u4eec\u5c31\u5f53\u662f\u4ed6\u9001\u6765\u7684\u597d\u4e86\u3002\u4ed6\u4e5f\u6015\u62c5\u5f53\u4e0d\u8d77\u62a4\u536b\u4e0d\u529b\u7684\u7f6a\u540d\u5475\uff01\u8fd9\u8bcf\u4e66\u66fe\u5728\u4e2d\u9014 \u5931\u53bb\u7684\u4e8b\u60c5\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u8ba9\u7687\u4e0a\u77e5\u9053\u7684\u3002\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u4f60\u600e\u77e5\u90a3\u9053\u8bcf\u4e66\u7684\u4f8d\u536b\u662f\u6b7b\u662f\u751f\uff1f\u201d \u9f99\u7075\u77eb\u9053\uff1a\u201c\u82e5\u7136\u662f\u6b7b\uff0c\u4f9d\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\uff0c\u65e2\u7136\u9001\u6765\u9526\u76d2\uff0c\u76d2\u4e2d\u8fd8\u5f53\u9644\u6709\u5315\u9996\u6216\u5176\u5b83\u62a5\u8b66\u7684 \u4e1c\u897f\u3002\u201d\u798f\u5eb7\u5b89\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4f9d\u8fd9\u79cd\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\uff0c\u4ed6\u5b9e\u5728\u4e0d\u5927\u76f8\u4fe1\uff0c\u4f46\u4e8b\u5df2\u5982\u6b64\uff0c\u4e5f\u53ea \u597d\u7531\u4e4b\u4e86\u3002 \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6211\u5012\u662f\u62c5\u5fc3\uff0c\u91d1\u74f6\u4f1a\u4e0d\u4f1a\u5728\u4e2d\u9014\u5931\u4e8b\uff1f\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u4e00\u5b9a\u4e0d\u80fd\u51fa\u4e8b\uff01\u82e5 \u7136\u51fa\u4e8b\uff0c\u6211\u4eec\u9a7b\u85cf\u5b98\u5458\u7684\u5934\uff0c\u90fd\u8981\u88ab\u780d\u6389\uff01\u9f99\u5148\u751f\uff0c\u4f60\u770b\uff0c\u54b1\u4eec\u597d\u4e0d\u597d\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\u8fce\u63a5 \u91d1\u74f6\uff1f\u201d\u4ed6\u53ef\u4e0d\u77e5\uff0c\u8fd9\u8ba1\u5212\u7684\u8349\u6848\uff0c\u4e5f\u5df2\u7ecf\u7ed9\u51b0\u5ddd\u5929\u5973\u76d7\u53bb\u3002\u82e5\u7136\u77e5\u9053\uff0c\u6050\u6015\u66f4\u8981\u5413\u6b7b\u3002 \u3000\u3000\u9f99\u7075\u77eb\u6c89\u541f\u534a\u664c\uff0c\u5ffd\u5730\u77a5\u4e86\u9648\u5929\u5b87\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4ecd\u6309\u539f\u6765\u7684\u8ba1\u5212\u8fce\u63a5\u91d1\u74f6\uff0c\u53ea\u662f\u7565\u6709\u4fee \u6539\u3002\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u600e\u4e48\u4fee\u6539\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u539f\u6765\u7684\u8349\u6848\uff0c\u662f\u7531\u6211\u8944\u52a9\u5927\u5e05\uff0c\u5750\u9547\u62c9\u8428\uff0c \u4e3b\u6301\u5927\u5178\uff0c\u73b0\u5728\u6539\u4e3a\u7531\u6211\u53bb\u8fce\u63a5\u91d1\u74f6\u3002\u201d\u798f\u5eb7\u5b89\u773c\u5149\u95ea\u52a8\uff0c\u795e\u6c14\u8fdf\u7591\u3002\u8981\u77e5\u9f99\u7075\u77eb\u662f\u4ed6\u501a\u4e3a \u5de6\u53f3\u624b\u7684\u4eba\u7269\uff0c\u82e5\u7136\u4e0d\u5728\u8eab\u8fb9\uff0c\u4ed6\u751f\u6015\u4f1a\u6709\u5371\u9669\uff0c\u9f99\u7075\u77eb\u9053\uff1a\u201c\u82e5\u6709\u4e0d\u901e\u4e4b\u5f92\u6b32\u52ab\u91d1\u74f6\uff0c\u591a \u534a\u4f1a\u5728\u4e2d\u9014\u52a8\u624b\uff0c\u62c9\u8428\u8b66\u536b\u68ee\u4e25\uff0c\u5f53\u53ef\u65e0\u8651\u3002\u6211\u53e6\u6d3e\u5e08\u5f1f\u4f8d\u5019\u5927\u5e05\uff0c\u7eb5\u6709\u98de\u8d3c\uff0c\u60f3\u4ed6\u4e5f\u80fd\u5e94 \u4ed8\u5f97\u4e86\u3002\u201d\u9f99\u7075\u77eb\u7684\u5e08\u5f1f\u540d\u53eb\u989c\u6d1b\uff0c\u5c31\u662f\u5728\u5e02\u96c6\u4e4b\u4e2d\uff0c\u65bd\u5c55\u7a7a\u7a7a\u5999\u624b\uff0c\u5077\u53bb\u4e86\u5e7d\u840d\u7684\u51b0\u9b44\u5bd2 \u5149\u5251\uff0c\u5c06\u4ed6\u4eec\u5f15\u8fdb\u9f99\u5b85\u7684\u4eba\u3002\u6b64\u4eba\u529f\u529b\u867d\u8fdc\u4e0d\u5982\u5e08\u5144\uff0c\u8f7b\u529f\u5374\u6709\u7279\u6b8a\u7684\u9020\u8be3\u3002\u798f\u5eb7\u5b89\u867d\u89c9\u5e08 \u5f1f\u4e0d\u5982\u5e08\u5144\uff0c\u4e0d\u5927\u653e\u5fc3\uff0c\u4f46\u6743\u8861\u5229\u5bb3\uff0c\u6b32\u8981\u4fdd\u8bc1\u91d1\u672c\u5df4\u74f6\u80fd\u591f\u5b89\u5168\u5230\u8fbe\u62c9\u8428\uff0c\u4e5f\u786e\u4e4e\u9700\u8981\u6709 \u9f99\u4e09\u8fd9\u6837\u7684\u4eba\u7269\u53bb\u4e3b\u6301\u3002\u53ea\u597d\u70b9\u4e86\u70b9\u5934\u3002\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5230\u65f6\u8fd8\u8981\u8bf7\u9648\u516c\u5b50\u76f8\u52a9\u3002\u201d\u9648\u5b9a\u57fa\u5fd9 \u9053\uff1a\u201c\u5c0f\u513f\u61c2\u5f97\u4ec0\u4e48\uff01\u201d\u9f99\u7075\u5a07\u7b11\u9053\uff1a\u201c\u77e5\u5b50\u83ab\u82e5\u7236\uff0c\u9648\u516c\u5b50\u6709\u4e00\u8eab\u60ca\u4eba\u7684\u6280\u4e1a\uff0c\u9648\u5927\u4eba\u8fd8 \u8981\u66ff\u4ed6\u5ba2\u6c14\u4e48\uff1f\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u9f99\u5148\u751f\u63a8\u8350\u7684\u4e00\u5b9a\u9519\u4e0d\u4e86\uff0c\u597d\uff0c\u5c31\u8fd9\u6837\u529e\u5427\u3002\u201d\u9648\u5b9a\u57fa\u63a8\u8f9e \u4e0d\u4e86\uff0c\u53ea\u597d\u548c\u513f\u5b50\u8c22\u6069\u3002 \u3000\u3000\u9f99\u7075\u77eb\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u8981\u9ebb\u70e6\u9648\u5927\u4eba\u3002\u201d\u9648\u5b9a\u57fa\u8be7\u9053\uff1a\u201c\u6211\u662f\u4e00\u4ecb\u6587\u5b98\uff0c\u80fd\u505a\u4ec0 \u4e48\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5230\u65f6\u6211\u548c\u9648\u516c\u5b50\u7387\u9886\u6570\u9a91\uff0c\u8d70\u5728\u5927\u961f\u4e4b\u5148\u4e09\u5341\u91cc\uff0c\u66ff\u4f60\u4eec\u63a2\u9053\u3002\u9648\u5927\u4eba \u7387\u9886\u4e00\u5343\u7cbe\u5175\uff0c\u90ca\u8fce\u4e94\u767e\u91cc\uff0c\u5c31\u8bf7\u798f\u5927\u5e05\u5373\u884c\u59d4\u6d3e\u9648\u5927\u4eba\u505a\u8fce\u63a5\u91d1\u74f6\u7684\u4e13\u4f7f\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a \u201c\u9f99\u5148\u751f\uff0c\u8fd9\u3001\u8fd9\u4e0d\u662f\u5f00\u73a9\u7b11\u5417\uff1f\u6211\u600e\u4e48\u4f1a\u5e26\u5175\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u53bb\u6253\u4ed7\uff0c\u65e2\u4e0d\u5fc5\u4f60 \u53bb\u51b2\u950b\uff0c\u53c8\u4e0d\u8981\u4f60\u6765\u5e03\u9635\u3002\u9886\u5175\u8fd8\u6709\u4ec0\u4e48\u4e0d\u4f1a\u7684\u5417\uff1f\u9648\u5927\u4eba\u662f\u7ff0\u6797\u51fa\u8eab\uff0c\u719f\u8bc6\u671d\u5ef7\u793c\u4eea\uff0c\u7531 \u4f60\u505a\u90ca\u8fce\u91d1\u672c\u5df2\u74f6\u4e13\u4f7f\uff0c\u90a3\u662f\u6700\u9002\u5f53\u4e0d\u8fc7\u7684\u4e86\u3002\u201d \u3000\u3000\u6309\u7406\u6765\u8bf4\uff0c\u9648\u5b9a\u57fa\u53ea\u662f\u8428\u8fb9\u5b97\u4e00\u4e2a\u5730\u65b9\u7684\u5ba3\u6170\u4f7f\uff0c\u4e0d\u8fc7\u56db\u54c1\u6587\u5b98\uff0c\u5b9e\u5728\u8fd8\u6ca1\u6709\u8d44\u683c\u505a\u8fce \u63a5\u91d1\u74f6\u7684\u4e13\u4f7f\uff0c\u53ea\u662f\u798f\u5eb7\u5b89\u5bf9\u9f99\u7075\u77eb\u8a00\u542c\u8ba1\u4ece\uff0c\u800c\u4e14\u89c1\u9f99\u7075\u77eb\u5148\u8bf7\u6d3e\u5176\u5b50\uff0c\u518d\u8bf7\u6d3e\u5176\u7236\uff0c\u5176 \u4e2d\u5927\u4f3c\u542b\u6709\u6df1\u610f\uff0c\u518d\u60f3\u8d77\u90a3\u8bcf\u4e66\u662f\u7531\u9648\u5929\u5b87\u4ea4\u6765\uff0c\u9001\u8bcf\u4e66\u7684\u4eba\u867d\u7136\u672a\u5fc5\u5c31\u662f\u60f3\u52ab\u91d1\u74f6\u7684\u4eba\uff0c \u4f46\u4e5f\u4e00\u5b9a\u6709\u4e9b\u5173\u8fde\uff0c\u73b0\u5728\u7531\u9648\u5b9a\u57fa\u505a\u8fce\u63a5\u91d1\u74f6\u7684\u4e13\u4f7f\uff0c\u82e5\u6709\u5dee\u9519\uff0c\u552f\u4ed6\u662f\u95ee\uff0c\u90a3\u9001\u8bcf\u4e66\u4eba\u65e2 \u662f\u9648\u5929\u5b87\u7684\u670b\u53cb\uff0c\u9648\u5929\u5b87\u4e5f\u5c31\u4e0d\u6562\u4e0d\u5c3d\u529b\u4fdd\u62a4\u91d1\u74f6\u4e86\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u7565\u4e00\u601d\u91cf\uff0c\u7acb\u523b\u51b3\u5b9a\uff0c\u53eb\u8bb0\u5ba4\u5199\u4e86\u59d4\u4efb\u7684\u6587\u4e66\uff0c\u7b11\u9053\uff1a\u201c\u9648\u5927\u4eba\u8fdc\u6ef4\u7a77\u8fb9\uff0c\u591a\u5e74 \u6765\u6df1\u53d7\u59d4\u5c48\u4e86\u3002\u8fd9\u56de\u53bb\u8fce\u63a5\u91d1\u74f6\uff0c\u4e0a\u8fbe\u5929\u542c\uff0c\u4e8b\u60c5\u8fc7\u540e\uff0c\u6062\u590d\u539f\u804c\uff0c\u751a\u6216\u5347\u8fc1\uff0c\u90fd\u6709\u5e0c\u671b\u3002 \u8fd9\u6b63\u662f\u4e00\u4e2a\u597d\u673a\u4f1a\u5440\u3002\u201d\u9648\u5b9a\u57fa\u60f3\u60f3\u4e5f\u662f\u9053\u7406\uff0c\u867d\u89c9\u8d23\u4efb\u91cd\u5927\uff0c\u4e5f\u53ea\u597d\u786c\u7740\u5934\u76ae\u63a5\u53d7\u3002\u9f99\u7075 \u77eb\u53c8\u7b11\u9053\uff1a\u201c\u9648\u516c\u5b50\u6709\u4ec0\u4e48\u6709\u672c\u4e8b\u7684\u670b\u53cb\uff0c\u5230\u65f6\u4e5f\u8bf7\u5e2e\u5fd9\u3002\u201d\u6b64\u8a00\u6697\u6307\u5e7d\u840d\uff0c\u9648\u5929\u5b87\u542c\u4e86\uff0c \u4e0d\u89c9\u5fc3\u4e2d\u4e00\u51db\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u7531\u5e7d\u840d\u800c\u8054\u60f3\u5230\u51b0\u5ddd\u5929\u5973\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u94c1\u62d0\u4ed9\u529d\u5979\u53bb\u52ab\u91d1\u74f6\uff0c\u767d\u8863 \u5c11\u5e74\u529d\u5979\u53bb\u4fdd\u62a4\u91d1\u74f6\uff0c\u5979\u90fd\u6ca1\u7b54\u5e94\u3002\u53ef\u662f\u5979\u53c8\u5230\u9f99\u5bb6\u53bb\u5077\u6587\u4e66\uff0c\u867d\u4e0d\u77e5\u90a3\u662f\u4ec0\u4e48\u6587\u4e66\uff0c\u4f46\u60f3 \u6765\u548c\u91d1\u672c\u5df4\u74f6\u5b9a\u6709\u5173\u7cfb\u3002\u82e5\u662f\u5979\u6765\u52ab\u74f6\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u96be\u9053\u5e7d\u840d\u4e0e\u6211\u8fd8\u80fd\u4e0e\u5979\u4f5c\u5bf9\u5417\uff1f\u201d \u53ea\u662f\u7236\u4eb2\u5df2\u7b54\u5e94\u62c5\u4efb\u8fce\u63a5\u91d1\u74f6\u7684\u4e13\u4f7f\uff0c\u9648\u5929\u5b87\u4e5f\u53ea\u6709\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u798f\u5eb7\u5b89\u7aef\u8336\u9001\u5ba2\uff0c\u9648\u5b9a\u57fa\u5e26\u4e86\u513f\u5b50\uff0c\u544a\u8f9e\u51fa\u8859\uff0c\u4e00\u8def\u4e0a\u53c8\u60ca\u53c8\u559c\uff0c\u5bf9\u513f\u5b50 \u9053\uff1a\u201c\u6b64\u4e8b\u60c5\u771f\u662f\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u3002\u6211\u6765\u5230\u62c9\u8428\u4e4b\u540e\uff0c\u5c61\u6b21\u8fdb\u8c12\u5927\u5e05\uff0c\u8bf7\u4ed6\u62e8\u6b3e\u91cd\u4fee\u5ba3\u6170\u4f7f\u7684 \u8859\u95e8\uff0c\u5e76\u589e\u6d3e\u6b66\u5b98\u9632\u536b\uff0c\u5426\u5219\u4fbf\u8bf7\u4ed6\u5c06\u6211\u514d\u804c\uff0c\u8ba9\u6211\u544a\u8001\u8fd8\u4e61\uff0c\u4ed6\u5374\u65e2\u4e0d\u51c6\u6211\u8f9e\u804c\uff0c\u53c8\u4e0d\u5141 \u6211\u6240\u8bf7\uff0c\u4e00\u62d6\u5c31\u62d6\u4e86\u51e0\u4e2a\u6708\uff0c\u5f04\u5f97\u6211\u9876\u7740\u4e2a\u8428\u8fe6\u5ba3\u6170\u4f7f\u7684\u7a7a\u8854\uff0c\u5374\u53d8\u6210\u4e86\u5728\u8fd9\u91cc\u8dd1\u8859\u95e8\u3001\u5403 \u95f2\u996d\u7684\u4eba\u3002\u771f\u662f\u6ca1\u6709\u610f\u601d\u3002\u60f3\u4e0d\u5230\u4eca\u65e5\u65e0\u7aef\u7aef\u5374\u59d4\u6d3e\u6211\u505a\u8fce\u63a5\u91d1\u74f6\u7684\u4e13\u4f7f\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u65e2\u7136\u63a8\u8f9e\u4e0d\u4e86\uff0c\u90a3\u4e48\u54b1\u4eec\u53ea\u6709\u5c0f\u5fc3\u53bb\u505a\u5c31\u662f\u3002\u8428\u8fce\u7684\u60c5\u5f62\u600e\u6837\uff1f\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u5ba3\u6170\u4f7f\u7684 \u8859\u95e8\u88ab\u90a3\u573a\u706b\u6bc1\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u6211\u53c8\u4e0d\u5728\u8859\u95e8\uff0c\u571f\u53f8\u66f4\u662f\u65e0\u6240\u987e\u5fcc\uff0c\u64c5\u4f5c\u5a01\u798f\u4e86\u3002\u4e0d\u8fc7\u4ed6\u5bf9\u4f60 \u5012\u597d\u50cf\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u4e0a\u6708\u4ed6\u8fd8\u6d3e\u4eba\u5411\u6211\u4e00\u518d\u67e5\u8be2\u4f60\u7684\u6d88\u606f\u3002\u201d\u9648\u5929\u5b87\u60f3\u8d77\u571f\u53f8\u8feb\u5a5a\u4e4b\u4e8b\uff0c\u4e0d\u89c9 \u82e6\u7b11\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u6240\u79df\u4f4f\u7684\u623f\u5b50\u8ddd\u79bb\u603b\u8859\u4e0d\u8fc7\u4e24\u6761\u8857\uff0c\u7247\u523b\u5c31\u5230\uff0c\u90a3\u662f\u666e\u901a\u7684\u4e24\u8fdb\u6c11\u5c45\uff0c\u9648\u5b9a\u57fa\u5ba6 \u56ca\u6709\u9650\uff0c\u53ea\u96c7\u4e86\u4e00\u4e2a\u770b\u95e8\u7684\u4eba\uff0c\u91cc\u9762\u56db\u58c1\u8427\u6761\uff0c\u4e0e\u5ba3\u6170\u4f7f\u8859\u95e8\u7684\u6c14\u6d3e\uff0c\u76f8\u5dee\u6781\u8fdc\u3002\u9648\u5929\u5b87\u968f \u7236\u4eb2\u8d70\u8fdb\u5385\u623f\uff0c\u6253\u5f00\u623f\u95e8\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5c11\u5973\uff0c\u7b11\u76c8\u76c8\u5730\u7acb\u5728\u5f53\u4e2d\uff0c\u6b63\u662f\u51b0\u5bab\u7684\u4f8d\u5973\u5e7d\u840d\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u5413\u4e86\u4e00\u8df3\uff0c\u9648\u5929\u5b87\u5fd9\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5c31\u662f\u548c\u6211\u540c\u6765\u62c9\u8428\u7684\u4eba\u3002\u55ef\uff0c\u4f60\u662f\u600e\u4e48\u6765 \u7684\uff1f\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8010\u70e6\u5728\u9f99\u5bb6\u7b49\u5019\uff0c\u4fbf\u5411\u4ed6\u5bb6\u7684\u4eba\u8981\u4e86\u4f60\u4eec\u7684\u5f80\u5740\uff0c\u81ea\u5df1\u6478\u6765\u4e86\u3002\u8fd9 \u4f4d\u8001\u4eba\u5bb6\u662f\u5c0a\u5927\u4eba\u5417\uff1f\u201d\u4f9d\u7167\u6c49\u4eba\u793c\u8282\uff0c\u798f\u4e86\u4e00\u798f\u3002\u9648\u5b9a\u57fa\u4e00\u770b\uff0c\u8fd9\u5c11\u5973\u82b1\u5bb9\u6708\u8c8c\uff0c\u521a\u5a1c\u591a \u59ff\uff0c\u6bd4\u90a3\u571f\u53f8\u7684\u5973\u513f\u4e0d\u77e5\u80dc\u8fc7\u51e0\u8bb8\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u8fd9\u5973\u5a03\u5b50\u914d\u5b87\u513f\u5012\u662f\u4e0d\u9519\uff0c\u53ea\u662f\u884c\u4e8b\u592a\u8fc7\u795e \u51fa\u9b3c\u6ca1\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u89c1\u7236\u4eb2\u6014\u6014\u5730\u770b\u7740\u5e7d\u840d\u3002\u7b11\u9053\uff1a\u201c\u7239\uff0c\u5979\u662f\u4ed9\u5973\u5462\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5478\uff0c\u80e1\u8bf4\uff0c\u80e1 \u8bf4\uff01\u201d\u4e00\u4ed8\u5a07\u5f26\u7684\u795e\u6001\uff0c\u9648\u5b9a\u57fa\u7709\u5f00\u773c\u7b11\uff0c\u9053\uff1a\u201c\u771f\u7684\u50cf\u4e00\u4f4d\u4ed9\u5973\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u8001\u7237\u5b50\u4e5f \u62ff\u6211\u53d6\u7b11\uff0c\u6211\u4e0d\u4f9d\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u7239\uff0c\u5979\u771f\u7684\u662f\u4ed9\u5973\u5462\u3002\u4f60\u542c\u6211\u8bf4\u8bf4\u5979\u7684\u6545\u4e8b\u3002\u201d\u5f53\u4e0b\u5c06 \u51b0\u5bab\u4e2d\u7684\u906d\u9047\u4e0e\u8fd9\u51e0\u4e2a\u6708\u6765\u7684\u7ecf\u5386\uff0c\u90fd\u544a\u8bc9\u4e86\u7236\u4eb2\u3002\u53ea\u542c\u5f97\u9648\u5b9a\u57fa\u76ee\u77aa\u53e3\u5446\uff0c\u771f\u50cf\u542c\u4e00\u4e2a\u795e \u4ed9\u6545\u4e8b\u4e00\u822c\u3002 \u3000\u3000\u81ea\u6b64\u5e7d\u840d\u4fbf\u5728\u9648\u5bb6\u5c45\u4e0b\uff0c\u4ed6\u4eec\u6697\u4e2d\u5bfb\u8bbf\u51b0\u5ddd\u5929\u5973\uff0c\u5374\u662f\u603b\u65e0\u6d88\u606f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5230\u4e86\u9686\u51ac\u814a \u6708\uff0c\u798f\u5eb7\u5b89\u5df2\u5b9a\u4e0b\u671f\u9650\uff0c\u8981\u4ed6\u4eec\u53bb\u8fce\u63a5\u91d1\u74f6\u4e86\u3002 \u3000\u3000\u4f9d\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u9648\u5929\u5b87\u968f\u9f99\u7075\u77eb\u5148\u4e00\u65e5\u51fa\u53d1\uff0c\u5e7d\u840d\u4ea6\u548c\u4ed6\u540c\u884c\u3002\u9648\u5929\u5b87\u5c06\u5fc3\u4e2d\u7684\u987e\u8651 \u5bf9\u5e7d\u840d\u8bf4\u4e86\uff0c\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u82e5\u7136\u54b1\u4eec\u516c\u4e3b\u6765\u5230\uff0c\u5979\u8981\u52ab\u91d1\u74f6\u6211\u4fbf\u52a9\u5979\u52ab\u91d1\u74f6\u3002\u5230\u65f6\u4f60\u5feb\u5feb\u9003 \u5f00\uff0c\u6211\u4e0d\u6253\u4f60\u4fbf\u662f\u3002\u201d\u9648\u5929\u5b87\u60f3\u8d77\uff0c\u66f4\u662f\u62c5\u5fc3\u3002 \u3000\u3000\u9f99\u7075\u77eb\u9009\u4e86\u4e09\u5339\u85cf\u9a6c\uff0c\u5341\u4e8c\u6708\u5341\u4e94\u52a8\u8eab\uff0c\u51c6\u5907\u5728\u4e8c\u5341\u4e09\u8d76\u5230\u4e39\u8fbe\u5c71\u53e3\u4e0e\u5317\u4eac\u62a4\u9001\u91d1\u74f6\u6765 \u7684\u4eba\u4f1a\u5408\uff0c\u4e39\u8fbe\u5c71\u53e3\u5357\u884c\u767e\u4f59\u91cc\u4e4b\u5730\uff0c\u5730\u52bf\u9669\u5cfb\u65e0\u6bd4\uff0c\u76d7\u532a\u5982\u6bdb\uff0c\u6700\u6613\u51fa\u4e8b\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u9f99\u7075\u77eb\u4e0e\u9648\u5929\u5b87\u751a\u4e3a\u76f8\u5f97\uff0c\u5e7d\u840d\u5374\u5bf9\u4ed6\u4e0d\u5927\u7406\u776c\uff0c\u9686\u51ac\u814a\u6708\uff0c\u5c71\u91ce\u96ea\u76d6\uff0c\u6781\u662f\u96be \u884c\uff0c\u5e78\u5f97\u9648\u5929\u5b87\u7684\u6b66\u529f\u5df2\u662f\u4eca\u975e\u6614\u6bd4\uff0c\u8981\u4e0d\u7136\u771f\u662f\u96be\u4ee5\u62b5\u53d7\u3002 \u3000\u3000\u9f99\u7075\u77eb\u6bcf\u4e00\u5904\u90fd\u7ec6\u5fc3\u5bdf\u89c6\uff0c\u53c8\u52a0\u662f\u5c71\u8def\u9669\u5cfb\uff0c\u6240\u4ee5\u867d\u6709\u826f\u9a6c\uff0c\u4ea6\u4e0d\u8fc7\u65e5\u884c\u767e\u91cc\u3002\u8d70\u4e86\u4e03 \u5929\uff0c\u624d\u8fdb\u5165\u4e39\u8fbe\u5c71\u7684\u5c71\u533a\u5730\u5e26\uff0c\u9f99\u7075\u77eb\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u884c\u8fc7\u8fd9\u4e00\u6bb5\u5c71\u8def\uff0c\u660e\u65e5\u4e00\u65e9\u4fbf\u53ef \u4ee5\u5230\u5c71\u53e3\u548c\u4ed6\u4eec\u4f1a\u5408\u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4eac\u4e2d\u4e0d\u77e5\u6d3e\u8c01\u6765\u9001\u91d1\u74f6\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u542c\u8bf4\u662f\u7531 \u548c\u7855\u4eb2\u738b\u4e3b\u6301\uff0c\u5927\u5185\u7684\u516b\u5927\u9ad8\u624b\u4e5f\u5168\u90e8\u6765\u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u516b\u5927\u9ad8\u624b\u7684\u672c\u4e8b\u5982\u4f55\uff1f\u201d\u9f99 \u7075\u77eb\u7b11\u9053\uff1a\u201c\u591f\u8d44\u683c\u79f0\u4e3a\u5927\u5185\u9ad8\u624b\u7684\uff0c\u5927\u7ea6\u603b\u4e0d\u8be5\u5728\u4f60\u6211\u4e4b\u4e0b\u3002\u201d\u770b\u6765\u4ed6\u4e5f\u5e76\u4e0d\u600e\u6837\u628a\u8fd9\u516b \u5927\u9ad8\u624b\u653e\u5728\u773c\u5185\u3002 \u3000\u3000\u524d\u9762\u4e24\u5cf0\u76f8\u5939\uff0c\u5c71\u9053\u76d8\u65cb\uff0c\u6108\u8d70\u6108\u7a84\uff0c\u8d70\u8fc7\u4e00\u4e2a\u5c71\u62d7\uff0c\u5ffd\u89c1\u524d\u9762\u4e09\u9a91\u5065\u9a6c\uff0c\u6392\u6210\u4e00\u7ebf\uff0c \u9a6c\u4e0a\u9a91\u58eb\u90fd\u662f\u4e00\u8272\u9ed1\u8272\u8863\u88f3\uff0c\u5934\u4e0a\u6234\u7684\u4e5f\u662f\u9ed1\u8272\u7684\u6be1\u5e3d\uff0c\u5728\u96ea\u5730\u91cc\u9ed1\u767d\u76f8\u6620\uff0c\u751a\u662f\u62a2\u773c\u3002\u524d \u9762\u90a3\u9a91\u7684\u9a91\u5ba2\u5076\u7136\u56de\u5934\uff0c\u9648\u5929\u5b87\u4e00\u77a7\u4e4b\u4e0b\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u5728\u65e5\u5580\u5219\u5ba2\u5e97\u4e2d \u66fe\u89c1\u8fc7\u7684\u9655\u7518\u5927\u4fa0\u9ea6\u6c38\u660e\u3002\u9648\u5929\u5b87\u77e5\u9053\u9ea6\u6c38\u660e\u662f\u8981\u62a2\u91d1\u74f6\u7684\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u7cdf\u3002\u5728\u65e5\u5580\u5219\u4e4b \u591c\uff0c\u9648\u5929\u5b87\u6ca1\u6709\u9732\u9762\uff0c\u9ea6\u6c38\u660e\u770b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u597d\u50cf\u4e0d\u5f88\u6ce8\u610f\uff0c\u53ea\u662f\u50ac\u540c\u4f34\u7d27\u7d27\u76f8\u8fde\uff0c\u63d0\u9632\u5750 \u9a91\u8dcc\u5012\u3002 \u3000\u3000\u9648\u5929\u5b87\u6084\u6084\u8bf4\u9053\uff1a\u201c\u524d\u9762\u90a3\u9a91\u662f\u9655\u7518\u5927\u4fa0\u9ea6\u6c38\u660e\u3002\u201d\u9f99\u7075\u77eb\u7b11\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u7684\u4eba\u5012\u4e0d \u5c11\u3002\u9ea6\u6c38\u660e\u867d\u6709\u9655\u7518\u5927\u4fa0\u4e4b\u540d\uff0c\u5012\u4e0d\u600e\u6837\u624e\u624b\uff0c\u540e\u9762\u90a3\u4e24\u9a91\u5374\u5389\u5bb3\u5f97\u591a\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4ed6 \u4eec\u662f\u8c01\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u77a7\u8fd9\u80cc\u5f71\uff0c\u4f3c\u4e4e\u662f\u7ec8\u5357\u6d3e\u7684\u4e24\u4f4d\u9ad8\u624b\uff0c\u6b66\u6c0f\u5144\u5f1f\u3002\u201d\u9648\u5929\u5b87\u53c8\u5403\u4e00 \u60ca\uff0c\u4ed6\u66fe\u542c\u94c1\u62d0\u4ed9\u8c08\u8fc7\u5f53\u4ee3\u82f1\u4fa0\uff0c\u8fd9\u6b66\u6c0f\u5144\u5f1f\u4e43\u662f\u987a\u6cbb\u5e74\u95ee\u6b66\u5143\u82f1\u5927\u4fa0\u7684\u91cd\u5b59\uff0c\u4ed6\u4eec\u7684\u7956\u59d1 \u5a46\u4fbf\u662f\u5927\u5c71\u4e03\u5251\u4e4b\u4e00\u7684\u6b66\u743c\u7476\uff0c\u4ed6\u4eec\u8fd9\u4e00\u5bb6\u4e00\u5411\u9690\u5c45\u5728\u7ec8\u5357\u5c71\uff0c\u4e0d\u6599\u800c\u4eca\u4e5f\u6765\u5230\u897f\u85cf\u3002 \u3000\u3000\u524d\u9762\u662f\u8fde\u63a5\u4e24\u5ea7\u5c71\u5cf0\u7684\u4e00\u6761\u7f8a\u80a0\u7a84\u9053\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u53ee\u88c6\uff0c\u4e00\u9a91\u963f\u62c9\u4f2f\u79cd\u7684\u9ad8\u5934\u5927\u9a6c\u98de\u5954 \u800c\u6765\uff0c\u9a91\u5728\u9a6c\u80cc\u4e0a\u7684\u4eba\u62ab\u7740\u4e00\u4ef6\u5927\u7ea2\u8888\u88df\uff0c\u66f4\u662f\u89e6\u76ee\uff0c\u5e7d\u840d\u548c\u9648\u5929\u5b87\u90fd\u5931\u58f0\u53eb\u9053\uff1a\u201c\u55ef\uff0c\u662f \u4ed6\uff01\u201d\u8fd9\u4eba\u6b63\u662f\u66fe\u4e24\u95ef\u51b0\u5bab\uff0c\u6253\u6b7b\u94c1\u62d0\u4ed9\u7684\u90a3\u4e2a\u7ea2\u8863\u756a\u50e7\uff01\u9648\u5929\u5b87\u60ca\u5947\u4e4b\u6781\uff0c\u5f53\u65e5\u4ed6\u5206\u660e\u53d7 \u4e86\u91cd\u4f24\uff0c\u5e08\u7236\u8bf4\u4ed6\u975e\u8fc7\u4e09\u5e74\u4e94\u8f7d\uff0c\u4e0d\u80fd\u6062\u590d\uff0c\u5982\u4eca\u4e0d\u8fc7\u4ec5\u4ec5\u8fc7\u4e86\u56db\u4e2a\u6708\uff0c\u770b\u4ed6\u795e\u6001\uff0c\u5df2\u662f\u5a01 \u731b\u903e\u524d\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u4e00\u58f0\u545b\u559d\uff0c\u505a\u9a6c\u5954\u6765\uff0c\u9ea6\u6c38\u660e\u95ea\u745f\u4e0d\u53ca\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u649e\u5012\uff0c\u9ea6\u6c38\u660e\u5927\u6012\uff0c\u547c\u7684 \u4e00\u638c\u671d\u4ed6\u9a6c\u5934\u4e00\u65a9\uff0c\u90a3\u756a\u50e7\u624b\u81c2\u4e00\u62ac\uff0c\u9ea6\u6c38\u660e\u8eab\u8eaf\u51cc\u7a7a\u98de\u8d77\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u6b66\u5bb6\u5144 \u5f1f\u5728\u9a6c\u80cc\u4e0a\u4e00\u7eb5\uff0c\u56db\u638c\u9f50\u63a8\uff0c\u90a3\u756a\u50e7\u5927\u53eb\u4e00\u58f0\uff0c\u8dcc\u4e0b\u9a6c\u6765\u3002\u5288\u9762\u5c31\u662f\u4e24\u62f3\uff0c\u6b66\u5bb6\u5144\u5f1f\u9a82\u9053\uff1a \u201c\u597d\u4e2a\u4e0d\u8bb2\u7406\u7684\u4e1c\u897f\uff01\u201d\u4e24\u5144\u5f1f\u5fc3\u610f\u5982\u4e00\uff0c\u500f\u7684\u8f6c\u8eab\u5927\u559d\u2014\u2014\u4e00\u4e2a\u98de\u8d77\u5de6\u817f\uff0c\u4e00\u4e2a\u98de\u8d77\u53f3 \u817f\uff0c\u90a3\u756a\u50e7\u624b\u638c\u4e00\u6309\uff0c\u65cb\u8eab\u53d8\u62db\uff0c\u5ffd\u542c\u5f97\u90a3\u5339\u963f\u62c9\u4f2f\u9a6c\u4e00\u58f0\u72c2\u5636\uff0c\u539f\u6765\u662f\u53d7\u4e0d\u4e86\u60ca\u5413\uff0c\u7adf\u7136 \u5931\u8db3\u8dcc\u5012\uff0c\u7ffb\u4e0b\u5c71\u5761\uff0c\u4e0b\u9762\u662f\u767e\u4e08\u6df1\u8c37\uff0c\u5cfb\u5ca9\u9cde\u5ccb\uff0c\u4e71\u77f3\u5982\u7b0b\uff0c\u8dcc\u4e0b\u53bb\u5b9a\u7136\u7c89\u8eab\u788e\u9aa8\uff0c \u3000\u3000\u90a3\u756a\u50e7\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u89c1\u6b66\u5bb6\u5144\u5f1f\u98de\u8eab\u75be\u8d77\uff0c\u4e00\u4e2a\u62c9\u7740\u9a6c\u7684\u53f3\u9762\u540e\u8e44\uff0c\u4e00\u4e2a\u62c9\u7740\u5de6\u9762\u540e \u8e44\uff0c\u7adf\u7136\u786c\u751f\u751f\u7684\u628a\u4e00\u9a91\u5065\u9a6c\u62c9\u4e86\u4e0a\u6765\uff0c\u4e24\u5144\u5f1f\u628a\u9a6c\u62ac\u8d77\uff0c\u5f80\u540e\u4e00\u63b7\uff0c\u529b\u5ea6\u7528\u5f97\u751a\u5de7\uff0c\u90a3\u9a6c \u4e5f\u662f\u826f\u9a6c\uff0c\u843d\u5728\u5730\u9762\u7adf\u7136\u6ca1\u6709\u53d7\u4f24\u3002 \u3000\u3000\u6b66\u5bb6\u5144\u5f1f\u663e\u4e86\u8fd9\u4e00\u624b\u975e\u51e1\u7684\u6b66\u529f\uff0c\u756a\u50e7\u4e00\u770b\uff0c\u77e5\u9053\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u628a\u521a\u521a\u53d1\u53bb\u7684\u638c\u5f0f\uff0c\u500f \u7684\u4e00\u53d8\uff0c\u5355\u624b\u5728\u5ca9\u77f3\u4e0a\u4e00\u6309\uff0c\u8eab\u8eaf\u4e5f\u51cc\u7a7a\u98de\u8d77\uff0c\u8fd9\u65f6\u9ea6\u6c38\u660e\u5df2\u5b89\u5b89\u7a33\u7a33\u7684\u843d\u5728\u9a6c\u80cc\u4e0a\uff0c\u6b63\u60f3 \u51fa\u624b\u963b\u62e6\uff0c\u6b66\u5bb6\u5144\u5f1f\u9053\uff1a\u201c\u9ea6\u5927\u54e5\uff0c\u8ba9\u8fd9\u53ae\u8fc7\u53bb\u3002\u201d\u9ea6\u6c38\u660e\u4e00\u4f4e\u5934\uff0c\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u7ea2\u8863 \u756a\u50e7\u5e9e\u5927\u7684\u8eab\u5f71\u5df1\u4ece\u5934\u9876\u63a0\u8fc7\uff0c\u843d\u5728\u90a3\u5339\u963f\u62c9\u4f2f\u9a6c\u7684\u80cc\u4e0a\u3002 \u3000\u3000\u9f99\u7075\u77eb\u7b11\u9053\uff1a\u201c\u8fd9\u756a\u50e7\u6b66\u529f\u4e0d\u4fd7\uff0c\u82e5\u7136\u4ee5\u4e00\u654c\u4e00\uff0c\u6b66\u5bb6\u5144\u5f1f\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u3002\u201d\u9648\u5929\u5b87\u89c1\u7740 \u6740\u5e08\u4ec7\u654c\uff0c\u6c14\u7ea2\u773c\u775b\uff0c\u90a3\u756a\u50e7\u9aa4\u7136\u89c1\u7740\u4ed6\u548c\u51b0\u5bab\u4f8d\u5973\uff0c\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u9a6c\u97ad\u556a\u7684\u4e00\u54cd\uff0c\u53c8\u671d\u4ed6 \u51b2\u6765\u3002 \u3000\u3000\u9648\u5929\u5b87\u53cd\u624b\u62d4\u5251\uff0c\u5728\u9a6c\u80cc\u4e0a\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u5ffd\u542c\u5f97\u9f99\u7075\u77eb\u7528\u5c3c\u6cca\u5c14\u8bdd\u9a82\u9053\uff1a\u201c\u79c3\u9a74\uff0c\u6eda \u5f00\uff01\u201d\u51fa\u624b\u6bd4\u9648\u5929\u5b87\u7684\u5251\u62db\u66f4\u5feb\u3002\u53ea\u89c1\u4ed6\u4e00\u4e2a\u987a\u624b\u7275\u7f8a\uff0c\u4fbf\u628a\u7ea2\u8863\u756a\u50e7\u4ece\u9a6c\u80cc\u4e0a\u63d0\u4e86\u8fc7\u6765\uff0c \u731b\u7684\u5411\u540e\u4e00\u6454\uff0c\u963f\u62c9\u4f2f\u9a6c\u4ecd\u7136\u5411\u524d\u51b2\u53bb\uff0c\u8fd9\u756a\u50e7\u6b66\u529f\u4e5f\u786e\u662f\u9ad8\u5f3a\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u626d\u8170\uff0c\u7adf\u7136 \u5728\u6beb\u65e0\u51ed\u85c9\u4e4b\u4e0b\uff0c\u4f7f\u4e86\u4e00\u4e2a\u201c\u9ca4\u9c7c\u7ffb\u8eab\u201d\uff0c\u53c8\u843d\u5728\u9a6c\u4e0a\u3002\u53ea\u662f\u4ed6\u63a5\u8fde\u53d7\u4e86\u4e24\u4e2a\u632b\u6298\uff0c\u4ea6\u5df2\u5782 \u5934\u4e27\u6c14\uff0c\u4e0d\u6562\u518d\u901e\u5a01\u98ce\u3002\u5c06\u90a3\u5339\u9a6c\u52d2\u7740\uff0c\u6014\u6014\u5730\u671b\u7740\u9f99\u7075\u77eb\u3002 \u3000\u3000\u9f99\u7075\u77eb\u4e0d\u518d\u7406\u4ed6\uff0c\u50ac\u9648\u5929\u5b87\u5feb\u8d70\uff0c\u9648\u5929\u5b87\u72e0\u72e0\u5730\u76ef\u4e86\u90a3\u756a\u50e7\u4e00\u773c\uff0c\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u756a\u50e7 \u548c\u4f60\u6709\u4ec7\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u6211\u6740\u5e08\u4ec7\u4eba\u3002\u201d\u9f99\u7075\u77eb\u9887\u611f\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u756a\u50e7 \u7684\u6b66\u529f\u867d\u8f83\u9648\u5929\u5b87\u4e3a\u9ad8\uff0c\u4f46\u53ea\u4e0d\u8fc7\u80dc\u5728\u529f\u529b\u800c\u5df2\uff0c\u4ee5\u9648\u5929\u5b87\u7684\u6b66\u529f\u800c\u8bba\uff0c\u5947\u62db\u5999\u7740\uff0c\u8fde\u6211\u4e5f \u672a\u89c1\u8fc7\uff0c\u4ed6\u7684\u5e08\u7236\u5fc5\u7136\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u4f55\u81f3\u4e8e\u88ab\u90a3\u756a\u50e7\u6240\u6740\uff1f\u201d\u65e0\u6687\u591a\u95ee\uff0c\u53ea\u9053\uff1a \u201c\u73b0\u4e0b\u4e0d\u662f\u62a5\u4ec7\u4e4b\u65f6\uff0c\u5feb\u5feb\u8d70\u5427\u3002\u201d\u9648\u5929\u5b87\u53ea\u597d\u8ddf\u7740\u9f99\u7075\u77eb\u7b56\u9a6c\u524d\u884c\u3002\u8fd9\u65f6\u524d\u9762\u90a3\u4e09\u9a91\u5df2\u8fc7 \u4e86\u5bf9\u9762\u7684\u5c71\u62d7\uff0c\u6b66\u5bb6\u5144\u5f1f\u56de\u5934\u671b\u671b\u4ed6\u4eec\uff0c\u795e\u60c5\u4e5f\u662f\u751a\u4e3a\u8be7\u5f02\u3002 \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u597d\uff0c\u8ddf\u7740\u524d\u9762\u90a3\u4e09\u9a91\uff0c\u4f46\u4e5f\u4e0d\u8981\u76f8\u8ddd\u592a\u8fd1\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u9f99\u5148\u751f\uff0c\u4f60\u521a \u624d\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff1f\u201d\u9f99\u7075\u77eb\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u8fc7\u662f\u6781\u5bfb\u5e38\u7684\u987a\u624b\u7275\u7f8a\u62db\u6570\u800c\u5df2\u3002\u90a3\u756a\u50e7\u82e5\u4e0d\u662f \u76ee\u4e2d\u65e0\u4eba\uff0c\u6a2a\u51b2\u76f4\u95ef\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u88ab\u6211\u501f\u529b\u6253\u529b\uff0c\u53ea\u4e00\u62db\u5c31\u5c06\u4ed6\u6454\u4e2a\u7b4b\u6597\u4e86\u3002\u201d\u9f99\u7075\u77eb\u8bf4\u5f97\u751a \u4e3a\u8c26\u865a\uff0c\u4f46\u4e00\u5f0f\u666e\u901a\u7684\u62db\u6570\uff0c\u7adf\u88ab\u4ed6\u4f7f\u5f97\u51fa\u795e\u5165\u5316\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u786e\u662f\u9a87\u4eba\u95fb\u542c\uff0c\u9648\u5929\u5b87\u4e0d\u7531 \u5f97\u66f4\u4e3a\u4f69\u670d\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u540e\u9762\u9a6c\u94c3\u53c8\u54cd\uff0c\u53ea\u89c1\u90a3\u7ea2\u8863\u756a\u50e7\u62e8\u8f6c\u9a6c\u5934\uff0c\u8fdc\u8fdc\u7684\u8ddf\u5728\u80cc\u540e\u3002\u9648\u5929\u5b87\u9053\uff1a \u201c\u8fd9\u79c3\u9a74\u662f\u5c3c\u6cca\u5c14\u7684\u56fd\u5e08\uff0c\u4ed6\u4fbf\u662f\u60f3\u52ab\u91d1\u74f6\u4e4b\u4eba\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4e0d\u8981\u7406\u4ed6\uff0c\u51ed\u4ed6\u8fd9\u70b9\u6b66 \u529f\uff0c\u4e0d\u8db3\u4e3a\u60a3\uff1b\u524d\u9762\u53ea\u6050\u8fd8\u6709\u66f4\u5389\u5bb3\u7684\u4eba\u7269\uff0c\u54b1\u4eec\u591a\u52a0\u5c0f\u5fc3\u3002\u201d\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u89c1\u524d\u9762\u4e09\u9a91\u4e00\u9f50 \u505c\u4e0b\uff0c\u9f99\u7075\u77eb\u6025\u53eb\u9648\u5929\u5b87\u548c\u5e7d\u840d\u52d2\u9a6c\uff0c\u5728\u76f8\u8ddd\u5341\u4f59\u4e08\u4e4b\u5730\uff0c\u9a7b\u5730\u800c\u89c2\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u62d7\u53e3\u4e00\u4e2a\u67af\u7626\u50e7\u4eba\uff0c\u9762\u5bb9\u9edd\u9ed1\uff0c\u4e00\u4ed8\u5370\u5ea6\u7684\u82e6\u884c\u50e7\u7684\u6a21\u6837\uff0c\u501a\u7740\u5ca9\u77f3\uff0c\u5730\u4e0b\u653e\u7740 \u4e00\u4e2a\u7834\u76c2\uff0c\u8fd8\u7684\u4e00\u6839\u7af9\u6756\uff0c\u90a3\u82e6\u884c\u50e7\u6b63\u4f38\u51fa\u624b\u6765\uff0c\u4f3c\u662f\u5411\u524d\u9762\u4e09\u4eba\u6284\u5316\u3002 \u3000\u3000\u9ea6\u6c38\u660e\u4e0e\u6b66\u5bb6\u5144\u5f1f\u76f8\u5bf9\u770b\u4e86\u4e00\u773c\uff0c\u6b66\u8001\u5927\u9053\uff1a\u201c\u597d\uff0c\u7ed9\u4ed6\uff01\u201d\u9ea6\u6c38\u660e\u6478\u51fa\u4e00\u952d\u5927\u94f6\uff0c\u5411 \u76c2\u94b5\u4e00\u4e22\uff0c\u90a3\u82e6\u884c\u50e7\u5495\u55dc\u5495\u55dc\u4ed6\u8bb2\u4e86\u51e0\u53e5\uff0c\u5ffd\u7136\u4f38\u51fa\u624b\u6765\uff0c\u671d\u9ea6\u6c38\u660e\u7684\u5934\u9876\u4e00\u6478\uff0c\u9f99\u7075\u77eb\u7b11 \u9053\uff1a\u201c\u8fd9\u50e7\u4eba\u7ed9\u4ed6\u8d50\u798f\u54e9\u3002\u201d\u9ea6\u6c38\u660e\u4f3c\u4e4e\u4e0d\u660e\u767d\u8fd9\u662f\u5370\u5ea6\u50e7\u4eba\u7684\u795d\u798f\u4eea\u793c\uff0c\u80a9\u5934\u4e00\u7f29\uff0c\u90a3\u82e6 \u884c\u50e7\u7684\u624b\u638c\u7f13\u7f13\u843d\u4e0b\uff0c\u5374\u4ecd\u7136\u6309\u5230\u9ea6\u6c38\u660e\u7684\u80a9\u4e0a\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u9ea6\u6c38\u660e\u6d51\u8eab\u5982\u70db\u7535\uff0c\u8dc3\u51fa\u4e08 \u8bb8\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u90aa\u95e8\uff0c\u90aa\u95e8\uff01\u201d \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u968f\u7f18\u4e50\u52a9\u3002\u201d\u4e24\u5144\u5f1f\u90fd\u6478\u51fa\u4e00\u628a\u788e\u94f6\uff0c\u5411\u90a3\u50e7\u4eba\u63b7\u53bb\uff0c\u90a3\u50e7 \u4eba\u53cc\u8896\u4e00\u626c\uff0c\u4e24\u628a\u788e\u94f6\u5c3d\u5165\u4ed6\u7684\u8896\u4e2d\uff0c\u90a3\u50e7\u4eba\u53cc\u8896\u4e00\u6446\uff0c\u5c06\u788e\u94f6\u90fd\u503e\u4e86\u51fa\u6765\uff0c\u5012\u5165\u76c2\u94b5\u3002\u6b66 \u6c0f\u5144\u5f1f\u7528\u7684\u662f\u5929\u5973\u6563\u82b1\u7684\u6697\u5668\u624b\u6cd5\uff0c\u52a0\u4e0a\u4ed6\u4eec\u7684\u52b2\u529b\uff0c\u8fd9\u4e24\u628a\u788e\u94f6\uff0c\u6bd4\u5341\u51e0\u679d\u91d1\u94b1\u6f02\u540c\u65f6\u9f50 \u53d1\uff0c\u8fd8\u8981\u5389\u5bb3\u5f97\u591a\uff0c\u4e0d\u6599\u8fd9\u82e6\u884c\u50e7\u5374\u89c6\u82e5\u65e0\u4e8b\uff0c\u4e00\u626c\u624b\u5c31\u90fd\u63a5\u4e86\u8fc7\u53bb\uff0c\u4e24\u5144\u5f1f\u90fd\u4e0d\u7981\u5446\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u50e7\u4eba\u7f13\u7f13\u884c\u6765\uff0c\u53cc\u624b\u4e00\u4f38\uff0c\u53c8\u8981\u7ed9\u6b66\u5bb6\u5144\u5f1f\u201c\u8d50\u798f\u201d\u3002\u6b66\u5bb6\u5144\u5f1f\u6025\u9053\uff1a\u201c\u4e0d\u52b3\u591a \u793c\uff01\u201d\u540c\u4ee5\u5927\u529b\u91d1\u521a\u624b\u6cd5\u5f80\u4e0a\u4e00\u6321\uff0c\u53ea\u89c9\u89e6\u624b\u4e4b\u5904\uff0c\u5176\u8f6f\u5982\u68c9\uff0c\u4e1d\u6beb\u65e0\u53ef\u7740\u529b\u4e4b\u5904\u3002\u4e24\u5144\u5f1f \u5403\u4e86\u4e00\u60ca\uff0c\u9661\u7136\u95f4\uff0c\u53ea\u89c9\u4e00\u80a1\u6f5c\u529b\u63a8\u6765\uff0c\u4e24\u5144\u5f1f\u6025\u5fd9\u6536\u52b2\uff0c\u8dc3\u51fa\u4e08\u8bb8\uff0c\u8bd5\u4e00\u4e2a\u547c\u5438\uff0c\u77e5\u9053\u5e76 \u6ca1\u53d7\u5230\u5185\u4f24\uff0c\u4e0d\u53d6\u591a\u6240\u7ea0\u7f20\uff0c\u6025\u5fd9\u4e58\u9a6c\u800c\u53bb\u3002 \u3000\u3000\u9f99\u7075\u77eb\u7d22\u9a6c\u524d\u884c\uff0c\u90a3\u50e7\u4eba\u5495\u55dc\u5495\u55dc\u4ed6\u8bf4\u4e86\u51e0\u53e5\uff0c\u53c8\u4f38\u51fa\u624b\u6765\u6284\u5316\u3002\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a \u5c0f\u5a03\u5a03\u6ca1\u94b1\uff0c\u90fd\u7531\u6211\u51fa\u5427\u3002\u201d\u90a3\u5370\u5ea6\u50e7\u4eba\u9053\uff1a\u201c\u968f\u7f18\u4e50\u52a9\uff0c\u591a\u5c11\u4e0d\u62d8\u3002\u201d\u9648\u5929\u5b87\u4e00\u6014\uff0c\u8fd9\u82e6 \u884c\u50e7\u7adf\u7136\u4f1a\u8bf4\u6c49\u8bed\u3002\u53ea\u89c1\u9f99\u7075\u77eb\u4e5f\u6478\u51fa\u4e00\u628a\u788e\u94f6\uff0c\u50cf\u6b66\u5bb6\u5144\u5f1f\u521a\u624d\u90a3\u6837\uff0c\u5411\u82e6\u884c\u50e7\u63b7\u53bb\uff0c\u9648 \u5929\u5b87\u4e0e\u5e7d\u840d\u90fd\u611f\u5947\u602a\uff0c\u660e\u660e\u90a3\u6b66\u5bb6\u5144\u5f1f\u5df2\u5403\u4e86\u4e8f\uff0c\u4f55\u4ee5\u4ed6\u8fd8\u662f\u7528\u8fd9\u624b\u6cd5\uff1f \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u60ca\u89c1\u98ce\u6ce2\u5e73\u5730\u8d77\uff0c\u5947\u50e7\u5f02\u58eb\u663e\u795e\u901a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u5cfb\u5cad\u98de\u9a91\u3000\u4e66\u751f\u65bd\u5999\u624b\u3000\u795e\u5f39\u5374\u654c\u3000\u5929\u5973\u62a4\u91d1\u74f6 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u4e00\u56de \u5cfb\u5cad\u98de\u9a91\u3000\u4e66\u751f\u65bd\u5999\u624b \u795e\u5f39\u5374\u654c\u3000\u5929\u5973\u62a4\u91d1\u74f6 \u3000\u3000\u9f99\u7075\u77eb\u628a\u624b\u4e00\u626c\uff0c\u50cf\u6b66\u5bb6\u5144\u5f1f\u4e00\u6837\uff0c\u4ecd\u7528\u201c\u5929\u5973\u6563\u82b1\u201d\u624b\u6cd5\uff0c\u5c06\u4e00\u628a\u788e\u94f6\u5411\u90a3\u50e7\u4eba\u63b7 \u53bb\uff0c\u90a3\u50e7\u4eba\u53cc\u62bd\u4e00\u5c55\uff0c\u53ea\u89c1\u788e\u94f6\u5982\u96e8\uff0c\u5c3d\u843d\u5165\u5bbd\u888d\u5927\u8896\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u54cd\uff0c\u50e7\u888d\u7adf \u7ed9\u4e00\u5757\u788e\u94f6\u4ece\u5185\u800c\u5916\u5212\u7834\u4e86\u4e00\u9053\u88c2\u7f1d\uff0c\u6536\u5165\u7684\u788e\u94f6\u53c8\u6709\u4e00\u534a\u6f0f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u9f99\u7075\u77eb\u7684\u53d1\u6697\u5668\u624b\u6cd5\u602a\u5f02\u975e\u5e38\uff0c\u4e00\u628a\u788e\u94f6\uff0c\u5728\u6296\u624b\u4e4b\u95f4\u540c\u65f6\u53d1\u51fa\uff0c\u5374\u53c2\u5dee\u4e0d\u9f50\uff0c\u52b2 \u529b\u4e0d\u4e00\uff0c\u800c\u4e14\u5176\u4e2d\u6709\u4e00\u5757\u788e\u94f6\u7adf\u7ed9\u4ed6\u53cc\u6307\u5939\u78e8\uff0c\u634f\u5f97\u4f3c\u91d1\u94b1\u5ad6\u4e00\u822c\u5927\u5c0f\uff0c\u56db\u8fb9\u950b\u5229\uff0c\u6545\u6b64\u80fd \u5c06\u50e7\u888d\u5212\u7834\uff0c\u9648\u5929\u5b87\u770b\u4e0d\u51fa\u5176\u4e2d\u5965\u5999\uff0c\u90a3\u82e6\u884c\u50e7\u5374\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u82e6\u884c\u50e7\u5e72\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u529f\u592b\uff01\u201d\u53cc\u624b\u4e00\u4f38\uff0c\u624b\u5fc3\u7f13\u7f13\u5411\u4e0b\uff0c\u53c8\u8981\u7ed9\u9f99\u7075\u77eb\u201c\u8d50 \u798f\u201d\uff0c\u9f99\u7075\u77eb\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u8f7b\u8f7b\u4e00\u6321\uff0c\u4e24\u4eba\u90fd\u5982\u89e6\u7535\u822c\u659c\u8dc3\u51e0 \u6b65\uff0c\u9f99\u7075\u77eb\u8fd8\u4e86\u4e00\u793c\uff0c\u4e00\u58f0\u80e1\u54e8\uff0c\u50ac\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u5feb\u8d70\uff0c\u90a3\u50e7\u4eba\u628a\u788e\u94f6\u6361\u8d77\uff0c\u90fd\u653e\u5165\u76c2\u94b5\uff0c \u4ecd\u7136\u50cf\u521d\u89c1\u65f6\u7684\u6a21\u6837\uff0c\u7791\u76ee\u5782\u7740\uff0c\u501a\u7740\u5c71\u58c1\uff0c\u53c8\u5728\u7b49\u5f85\u7b2c\u4e8c\u4f4d\u65bd\u4e3b\u3002 \u3000\u3000\u9648\u5929\u5b87\u60ca\u5947\u4e0d\u5b9a\uff0c\u95ee\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u50e7\u4eba\u662f\u4ec0\u4e48\u8def\u9053\uff1f\u201d\u9f99\u7075\u77eb\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f46\u613f \u4ed6\u4e0d\u662f\u4e3a\u91d1\u74f6\u800c\u6765\u3002\u8fd9\u50e7\u4eba\u7ec3\u7684\u662f\u5370\u5ea6\u6700\u4e0a\u4e58\u7684\u745c\u4f3d\u6c14\u529f\uff0c\u4e0d\u5728\u4f60\u4eec\u4e2d\u571f\u6b63\u5b97\u7684\u7384\u95e8\u5185\u529f\u4e4b \u4e0b\uff0c\u8981\u662f\u4ed6\u4e5f\u63d2\u624b\u8fdb\u6765\uff0c\u5012\u662f\u6211\u7684\u4e00\u5927\u52b2\u654c\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8d70\u8fc7\u4e86\u4e24\u5904\u5c71\u62d7\uff0c\u5ffd\u542c\u5f97\u540e\u9762\u90a3 \u7ea2\u8863\u756a\u50e7\u4e00\u58f0\u5927\u53eb\uff0c\u56de\u9996\u671b\u65f6\uff0c\u53ea\u89c1\u4ed6\u4f0f\u5728\u9a6c\u80cc\u4e0a\uff0c\u7adf\u7136\u62ac\u4e0d\u8d77\u5934\u6765\uff01 \u3000\u3000\u9f99\u7075\u77eb\u7b11\u9053\uff1a\u201c\u8fd9\u756a\u50e7\u5b9a\u662f\u901e\u51f6\u6043\u5f3a\uff0c\u88ab\u90a3\u82e6\u884c\u50e7\u8d50\u798f\uff0c\u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u82e6\u884c\u50e7 \u7684\u2018\u8d50\u798f\u2019\u5012\u597d\u50cf\u8003\u5b98\u51fa\u9010\u8003\u8bd5\u4e00\u6837\uff0c\u51e1\u7ecf\u8fc7\u4ed6\u9762\u524d\u7684\u4eba\uff0c\u4e00\u4e2a\u4e2a\u90fd\u8981\u7ed9\u4ed6\u4f38\u91cf\u3002\u8fd9\u884c\u5f84\u771f \u662f\u602a\u5f97\u53ef\u4ee5\u3002\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u82e5\u7136\u662f\u51b0\u5ddd\u5929\u5973\u5230\u6765\uff0c\u5b9a\u6709\u82e6\u5934\u53cd\u8fc7\u6765\u8ba9\u4ed6\u5c1d\u5c1d\u3002\u201d\u9f99\u7075\u77eb\u9ed8 \u9ed8\u82e5\u6709\u6240\u601d\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u4ed6\u4eec\u5c31\u5728\u4e39\u8fbe\u5c71\u4e2d\u642d\u7bf7\u591c\u5bbf\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u524d\u540e\u77aa\u671b\uff0c\u7ea2\u8863\u756a\u50e7\u3001\u9ea6\u6c38\u660e \u548c\u6b66\u6c0f\u5144\u5f1f\u7684\u8e2a\u5f71\u90fd\u4e0d\u89c1\u4e86\uff0c\u9f99\u7075\u77eb\u957f\u5401\u4e00\u58f0\uff0c\u770b\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u5728\u65e5\u51fa\u4e4b\u524d\uff0c\u8d76\u5230 \u5c71\u53e3\u7b49\u5019\u91d1\u74f6\uff01\u201d \u3000\u3000\u4e09\u4eba\u50ac\u9a6c\u524d\u884c\uff0c\u8d76\u5230\u4e39\u8fbe\u5c71\u5ce1\u8c37\u7684\u8c37\u53e3\uff0c\u65e5\u5934\u521a\u521a\u5347\u8d77\uff0c\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u513f\u7a0d \u5019\uff0c\u6211\u5230\u524d\u9762\u770b\u770b\u3002\u201d\u8bdd\u72b9\u672a\u4e86\u3001\u53ea\u542c\u5f97\u5ce1\u8c37\u5185\u9a6c\u8e44\u5954\u817e\u4e4b\u58f0\u6709\u5982\u6ce2\u6d6a\u822c\u7684\u6d8c\u5230\uff0c\u9f99\u7075\u77eb\u5fae \u5fae\u53d8\u8272\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u6309\u7167\u65e5\u7a0b\uff0c\u4ece\u5317\u4eac\u62a4\u9001\u91d1\u74f6\u6765\u7684\u4eba\u8981\u4e2d\u5348\u65f6\u5206\u624d \u5230\u8fd9\u513f\uff0c\u600e\u4e48\u4ed6\u4eec\u63d0\u65e9\u6765\u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\u3002\u524d\u9762\u5c18\u5934\u5927\u8d77\uff0c\u9a6c\u5339\u9a91\u58eb\uff0c\u5747\u5df2\u9690\u7ea6\u53ef\u89c1\uff0c\u9648\u5929 \u5b87\u5fc3\u5934\u4e71\u8df3\uff0c\u65e2\u6015\u91d1\u74f6\u51fa\u4e8b\uff0c\u8fde\u7d2f\u4ed6\u7684\u7236\u4eb2\uff0c\u53c8\u6e34\u671b\u51b0\u5ddd\u5929\u5973\u80fd\u679c\u5982\u6240\u6599\u7684\u5728\u6b64\u51fa\u73b0\u3002 \u3000\u3000\u90a3\u5ce1\u8c37\u5f62\u5982\u5587\u53ed\uff0c\u91cc\u7a84\u5916\u5bbd\uff0c\u62a4\u9001\u91d1\u74f6\u7684\u5fa1\u6797\u519b\u6392\u6210\u4e24\u5217\uff0c\u6d69\u6d69\u8361\u8361\uff0c\u6709\u5982\u957f\u9f99\u51fa\u6d1e\uff0c \u65cf\u65d7\u853d\u65e5\uff0c\u4e07\u9a6c\u5636\u9e23\uff0c\u519b\u5bb9\u6781\u58ee\uff0c\u884c\u5217\u4e2d\u4e00\u9762\u8fce\u98ce\u98d8\u8361\u7684\u674f\u9ec4\u65d7\uff0c\u540e\u9762\u56db\u5f20\u9ec4\u7f57\u4f1e\u76d6\uff0c\u5bfc\u7740 \u56db\u5339\u4e00\u8272\u7684\u767d\u9a6c\uff0c\u7f13\u7f13\u524d\u884c\u3002\u4ee4\u4eba\u4e00\u770b\uff0c\u5c31\u77e5\u9053\u90a3\u56db\u5339\u767d\u9a6c\u4e4b\u4e2d\uff0c\u5fc5\u7136\u6709\u4e00\u5339\u9a6e\u7740\u91d1\u74f6\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u4e13\u4f7f\u672a\u6765\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u5148\u4e0a\u53bb\u8fce\u63a5\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4e14\u5f85\u7247\u523b\u3002\u201d\u5fa1\u6797\u519b \u524d\u5217\u521a\u51fa\u5230\u5c71\u8c37\u7684\u5587\u53ed\u53e3\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff0c\u5c71\u8170\u91cc\u7a9c\u51fa\u4e00\u4f19\u4eba\u6765\uff0c\u4e3a\u9996\u7684\u6b63\u662f\u90a3\u7ea2\u8863\u756a \u50e7\u3002\u53ea\u89c1\u4ed6\u624b\u6325\u7985\u6756\uff0c\u50cf\u51f6\u795e\u6076\u715e\u822c\u5730\u5f53\u5148\u51b2\u5165\uff0c\u7985\u6756\u547c\u547c\u4e71\u626b\uff0c\u4e13\u6253\u9a6c\u8db3\uff0c\u540e\u9762\u516d\u540d\u5c3c\u6cca \u5c14\u6b66\u58eb\uff0c\u5404\u630e\u4e00\u5f0f\u7684\u6708\u7259\u5f2f\u5200\uff0c\u7ed9\u4ed6\u63a0\u9635\u3002\u5fa1\u6797\u519b\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u524d\u5217\u961f\u5f62\uff0c\u767b\u65f6\u5927\u4e71\u3002 \u3000\u3000\u961f\u4f0d\u4e2d\u62a2\u51fa\u4e24\u540d\u519b\u5b98\uff0c\u4e00\u4f7f\u94c1\u62d0\uff0c\u4e00\u4f7f\u5355\u5200\uff0c\u6025\u6025\u4e0a\u524d\u5835\u622a\uff0c\u90a3\u756a\u50e7\u6b63\u6253\u5f97\u9ad8\u5174\uff0c\u731b\u542c \u5f97\u91d1\u5200\u631f\u98ce\u4e4b\u58f0\uff0c\u5206\u4ece\u4e24\u4fa7\u88ad\u5230\uff0c\u90a3\u756a\u50e7\u4e00\u4e2a\u76d8\u65cb\uff0c\u53ea\u542c\u5f97\u90a3\u4e24\u4e2a\u519b\u5b98\u6012\u58f0\u559d\u9053\uff1a\u201c\u597d\u5927\u7684 \u80c6\u5b50\uff0c\u51ed\u4f60\u8fd9\u51e0\u4e2a\u756a\u8d3c\uff0c\u5c31\u6562\u6765\u62a2\u52ab\u91d1\u74f6\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u5fa1\u6797\u519b\u9635\u5f62\u4e00\u53d8\uff0c\u7528\u5f3a\u5f13\u5c04\u4f4f\u9635 \u811a\uff0c\u5c06\u516d\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u6321\u5728\u5916\u56f4\uff0c\u4e24\u540d\u519b\u5b98\u4e0e\u90a3\u7ea2\u8863\u756a\u50e7\u4fbf\u5728\u6838\u5fc3\u6076\u6597\u3002 \u3000\u3000\u9f99\u7075\u77eb\u7b49\u4e09\u4eba\u5728\u5ca9\u77f3\u540e\u9762\u89c2\u6218\uff0c\u9648\u5929\u5b87\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u53bb\u52a9\u9635\u4e86\u5427\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4e14\u770b \u770b\u5927\u5185\u516b\u5927\u9ad8\u624b\u7684\u672c\u9886\u3002\u201d\u53ea\u89c1\u90a3\u4e24\u540d\u519b\u5b98\u62d0\u53bb\u5200\u6765\uff0c\u94c1\u62d0\u8d77\u5904\u6709\u5982\u86df\u9f99\u51fa\u6d77\uff0c\u5355\u5200\u98de\u821e\uff0c \u4e25\u5982\u5339\u7ec3\u6a2a\u7a7a\uff0c\u786e\u662f\u9ad8\u624b\uff1b\u4f46\u90a3\u756a\u50e7\u7684\u7985\u4ed7\u547c\u547c\u4e71\u626b\uff0c\u529b\u5927\u62db\u6c89\uff0c\u6bcf\u4e00\u6756\u53d1\u51fa\uff0c\u90fd\u6253\u5f97\u6c99\u98de \u77f3\u821e\uff0c\u8fd9\u4e24\u540d\u519b\u5b98\u867d\u662f\u7cbe\u901a\u6b66\u827a\uff0c\u5374\u5df2\u663e\u5f97\u5904\u5728\u4e0b\u98ce\uff0c\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u4e24\u540d\u519b\u5b98\u662f\u516b\u5927\u9ad8\u624b \u4e2d\u7684\u94c1\u62d0\u5f20\u534e\u548c\u5355\u5200\u5468\u4e94\uff0c\u4ed6\u4eec\u516b\u5927\u9ad8\u624b\u5bf9\u654c\uff0c\u4ece\u6765\u4e0d\u8981\u4eba\u76f8\u5e2e\uff0c\u8fd9\u56de\u53ea\u6015\u8981\u7834\u4f8b\u4e86\u3002\u201d \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u8d8a\u6218\u8d8a\u52c7\uff0c\u4f7f\u5230\u75be\u5904\uff0c\u53ea\u542c\u5f97\u547c\u547c\u8f70\u8f70\u4e4b\u58f0\uff0c\u4e00\u6839\u7985\u6756\u5c31\u5982\u5316\u4e86\u6570\u5341\u767e\u6839\uff0c \u6756\u5f71\u5982\u5c71\uff0c\u5c06\u90a3\u4e24\u540d\u519b\u5b98\u90fd\u7b3c\u7f69\u5728\u6756\u5f71\u4e4b\u5185\uff0c\u6b63\u62df\u65bd\u5c55\u6740\u624b\uff0c\u53ea\u89c1\u4e00\u9a91\u5feb\u9a6c\uff0c\u5728\u540e\u5217\u98de\u5954\u51fa \u9635\uff0c\u9a6c\u672a\u51b2\u5230\uff0c\u4eba\u5df2\u5728\u9a6c\u80cc\u4e0a\u51cc\u7a7a\u98de\u8d77\uff0c\u94f6\u8679\u4e00\u9053\uff0c\u98de\u63a0\u800c\u4e0b\uff0c\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u597d\u4e00\u62db\u5c55\u7ffc \u6469\u4e91\u5475\uff01\u201d\u53ea\u89c1\u94f6\u8679\u4e00\u7ed5\uff0c\u90a3\u756a\u50e7\u4e00\u62db\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u88c6\u7684\u4e00\u58f0\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\uff0c\u5df2\u968f \u7740\u94f6\u8679\u98de\u8d77\uff0c\u539f\u6765\u662f\u90a3\u756a\u50e7\u7684\u516b\u89d2\u50e7\u5e3d\uff0c\u7ed9\u6765\u4eba\u4e00\u5251\u524a\u4e3a\u4e24\u8fb9\u3002 \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u516b\u5927\u9ad8\u624b\u4e2d\u5750\u7b2c\u4e8c\u628a\u4ea4\u6905\u7684\u94f6\u8679\u5251\u6e38\u4e00\u9102\uff0c\u90a3\u756a\u50e7\u9047\u7740\u52b2\u654c\u4e86\u3002\u201d \u9648\u5929\u5b87\u6ce8\u76ee\u6218\u573a\uff0c\u679c\u7136\u53ea\u89c1\u90a3\u756a\u50e7\u8fde\u8fde\u540e\u9000\uff0c\u53ea\u6709\u62db\u67b6\u7684\u529f\u592b\u3002 \u3000\u3000\u6e38\u4e00\u9102\u662f\u6b66\u5f53\u6d3e\u7684\u9ad8\u624b\uff0c\u4e00\u624b\u8fde\u73af\u5251\u6cd5\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff0c\u6b63\u5728\u5360\u5f97\u4e0a\u98ce\uff0c\u731b\u5730\u91cc\u53c8\u542c\u5f97\u54e8 \u58f0\u56db\u58f0\uff0c\u5357\u5317\u4e24\u9762\u5c71\u53e3\u90fd\u51b2\u51fa\u4e00\u80a1\u4eba\u6765\uff0c\u5357\u9762\u7684\u662f\u9655\u7518\u5927\u4fa0\u9ea6\u6c38\u660e\u9886\u5934\uff0c\u5317\u9762\u5374\u662f\u6b66\u6c0f\u5144\u5f1f \u4e3a\u9996\uff0c\u9f99\u7075\u77eb\u77a5\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u9ea6\u8001\u5934\u7684\u4ea4\u6e38\u786e\u662f\u5e7f\u9614\u3001\u5317\u4e94\u7701\u4fa0\u4e49\u9053\u4e2d\u7684\u4eba\u7269\uff0c\u51e0\u4e4e \u5168\u6765\u4e86\u3002\u201d\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u6817\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7236\u4eb2\u662f\u8fce\u63a5\u91d1\u74f6\u7684\u4e13\u4f7f\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5c82\u975e\u6211\u8981\u548c\u5317 \u4e94\u7701\u4fdf\u4e49\u9053\u4e2d\u7684\u4eba\u7269\u4f5c\u5bf9\u4e86\uff1f\u201d\u5fc3\u4e0b\u8e0c\u8e87\u96be\u51b3\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u8fd9\u4e24\u80a1\u4eba\u9a6c\u5df2\u4ece\u4e24\u7ffc\u6740\u4eba\uff0c \u628a\u5fa1\u6797\u519b\u6740\u5f97\u671b\u98ce\u62ab\u9761\u3002 \u3000\u3000\u4e2d\u519b\u5e05\u65d7\u4e00\u5c55\uff0c\u516b\u5927\u9ad8\u624b\u4e5f\u5206\u51fa\u4eba\u6765\uff0c\u7387\u9886\u7cbe\u9510\uff0c\u4e0a\u524d\u5835\u622a\uff0c\u9ea6\u6c38\u660e\u8fd9\u4e00\u80a1\u88ab\u4e00\u4e2a\u624b\u821e \u7ec3\u5b50\u9524\u7684\u519b\u5b98\u5835\u4f4f\uff0c\u9677\u4e8e\u6df7\u6218\u4e4b\u4e2d\uff0c\u6b66\u6c0f\u5144\u5f1f\u5374\u6a2a\u51b2\u76f4\u95ef\uff0c\u6740\u5165\u9635\u4e2d\uff0c\u4e00\u4e2a\u7528\u5de6\u624b\u5251\uff0c\u4e00\u4e2a \u7528\u53f3\u624b\u5251\uff0c\u4e92\u4e3a\u63a9\u62a4\uff0c\u4e24\u9053\u5251\u5149\uff0c\u5de6\u53f3\u5c55\u5f00\uff0c\u6709\u5982\u53cc\u9f99\u51fa\u6d77\uff0c\u592d\u77eb\u98de\u821e\uff0c\u6709\u4e24\u540d\u519b\u5b98\uff0c\u4e5f\u662f \u516b\u5927\u9ad8\u624b\u4e2d\u7684\u4eba\u7269\uff0c\u4e00\u4e2a\u624b\u4f7f\u952f\u9f7f\u5200\uff0c\u4e00\u4e2a\u624b\u821e\u5434\u94a9\u5251\uff0c\u6025\u6025\u4e0a\u524d\u5835\u622a\uff0c\u6b66\u5bb6\u5144\u5f1f\u9aa4\u7684\u5f20\u76ee \u559d\u9053\uff1a\u201c\u6321\u6211\u8005\u6b7b\uff0c\u907f\u6211\u8005\u751f\uff01\u201d\u53cc\u5251\u9f50\u51fa\uff0c\u6709\u5982\u5954\u96f7\u63a3\u7535\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u590f\u7389\u4e4b\u58f0\uff0c\u952f \u9f7f\u5200\u7684\u952f\u9f7f\u5168\u7ed9\u524a\u5e73\uff0c\u5434\u94a9\u5251\u4e5f\u7ed9\u6311\u5230\u534a\u7a7a\u3002\u90a3\u4e24\u540d\u519b\u5b98\u6025\u5fd9\u4e00\u62e8\u9a6c\u5934\uff0c\u6b66\u6c0f\u5144\u5f1f\u5251\u51fa\u5982 \u98ce\uff0c\u6bd4\u9a6c\u8fd8\u5feb\uff0c\u53ea\u89c1\u9752\u5149\u95ea\u5904\uff0c\u4e24\u540d\u519b\u5b98\u5404\u81ea\u4e2d\u5251\uff0c\u8dcc\u4e0b\u9a6c\u6765\u3002\u6b66\u6c0f\u5144\u5f1f\u523a\u7ffb\u654c\u4eba\uff0c\u5f84\u5411\u4e2d \u519b\u90a3\u56db\u5339\u767d\u9a6c\u51b2\u53bb\u3002 \u3000\u3000\u6e38\u4e00\u9102\u5927\u5403\u4e00\u60ca\uff0c\u820d\u4e86\u756a\u50e7\uff0c\u56de\u8eab\u6551\u63f4\uff0c\u6b66\u6c0f\u5144\u5f1f\u8eab\u6cd5\u6781\u5feb\uff0c\u53ea\u89c1\u4ed6\u4eec\u5de6\u4e00\u515c\uff0c\u53f3\u4e00 \u7ed5\uff0c\u7adf\u4ece\u4eba\u4e1b\u4e4b\u4e2d\u76f4\u6740\u51fa\u53bb\uff0c\u770b\u770b\u5c31\u8981\u62a2\u5230\u4e2d\u519b\u7684\u674f\u9ec4\u65d7\u4e0b\u3002 \u3000\u3000\u731b\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u4e2a\u7a7f\u7740\u4e09\u54c1\u6b66\u5b98\u670d\u9970\u7684\u866c\u9aef\u6c49\u5b50\uff0c\u6325\u52a8\u4e00\u4ef6\u5947\u5f62\u72b6\u7684\u5175\u5668\uff0c\u51b2\u51fa\u9635 \u6765\uff0c\u8fce\u7740\u6b66\u6c0f\u5144\u5f1f\u7834\u53e3\u9a82\u9053\uff1a\u201c\u4e8f\u4f60\u4eec\u8fd8\u662f\u6c49\u4eba\uff0c\u4e3a\u4f55\u5e2e\u756a\u90a6\u9791\u5b50\u62a2\u52ab\u91d1\u74f6\uff1f\u201d\u58f0\u5982\u6d2a\u949f\uff0c \u867d\u5728\u5343\u519b\u4e07\u9a6c\u4e4b\u4e2d\uff0c\u4e5f\u9707\u5f97\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u6b66\u6c0f\u5144\u5f1f\u4e00\u6014\uff0c\u7acb\u5373\u4e5f\u56de\u9a82\u9053\uff1a\u201c\u4e8f\u4f60\u4e5f\u662f\u6c49 \u4eba\uff0c\u4e3a\u4f55\u5e2e\u6ee1\u5dde\u9791\u5b50\uff1f\u6211\u4eec\u5c31\u662f\u4e0d\u51c6\u4f60\u5c06\u91d1\u74f6\u9001\u5230\u62c9\u8428\uff0c\u4f60\u4eec\u7684\u6ee1\u6d32\u4e3b\u5b50\u5360\u636e\u4e2d\u539f\u5c1a\u5acc\u4e0d\u591f \u5417\uff1f\u4e3a\u4f55\u8fd8\u8981\u541e\u5e76\u56de\u7586\u8499\u85cf\uff1f\u6211\u4eec\u62a2\u6211\u4eec\u7684\uff0c\u4e0e\u90a3\u756a\u90a6\u79c3\u9a74\u6beb\u4e0d\u76f8\u5173\uff1a\u4f60\u8fd9\u53ae\u53e3\u51fa\u5927\u8a00\uff0c\u5403 \u6211\u4e00\u5251\uff01\u201d \u3000\u3000\u90a3\u866c\u9aef\u6b66\u5b98\u559d\u9053\uff1a\u201c\u4f60\u4eec\u52fe\u7ed3\u756a\u90a6\uff0c\u72af\u4e0a\u4f5c\u4e71\uff0c\u8fd8\u6562\u5de7\u8a00\u8fa9\u9970\uff0c\u6709\u672c\u9886\u5c31\u4ece\u6211\u624b\u4e2d\u5c06\u91d1 \u74f6\u593a\u53bb\u3002\u201d\u6b66\u6c0f\u5144\u5f1f\u4ea6\u77e5\u6b64\u4eba\u4e43\u662f\u52b2\u654c\uff0c\u53cc\u5251\u4e00\u51fa\uff0c\u4fbf\u5c55\u7edd\u62db\uff0c\u6b66\u8001\u5927\u5de6\u5251\u6a2a\u62ab\uff0c\u6b66\u8001\u4e8c\u53f3 \u5251\u76f4\u523a\uff0c\u53cc\u5251\u4e00\u62ab\u4e00\u523a\uff0c\u500f\u5730\u5408\u6210\u4e00\u4e2a\u5706\u5f27\uff0c\u5411\u90a3\u519b\u5b98\u62e6\u8170\u75be\u7ed5\u3002\u90a3\u519b\u5b98\u7684\u602a\u5175\u5668\u5f53\u4e2d\u4e00 \u63d2\uff0c\u786c\u63d2\u8fdb\u5706\u5f27\u4e4b\u4e2d\uff0c\u628a\u53cc\u5251\u51b2\u5f97\u5de6\u53f3\u5206\u5f00\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u53ee\u5f53\u4e4b\u58f0\uff0c\u4e45\u4e45\u4e0d\u7edd\uff0c\u4ed6\u7adf\u7136\u5168\u7528 \u672c\u8eab\u529f\u529b\uff0c\u786c\u5c06\u53cc\u5251\u51b2\u5f00\uff0c\u9f99\u7075\u77eb\u89c1\u4e86\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u70b9\u5934\uff0c\u5bf9\u9648\u5929\u5b87\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u6127\u662f\u516b \u5927\u9ad8\u624b\u7684\u9996\u9886\uff0c\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff01\u201d \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u7684\u65e0\u6781\u5251\u6cd5\u5f97\u81ea\u7956\u7236\u771f\u4f20\uff0c\u9aa4\u9047\u5f3a\u654c\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u53cc\u5251\u4e00\u5206\u5373\u5408\uff0c\u970e\u773c\u4e4b\u95f4\uff0c \u8fde\u8fdb\u6570\u62db\u3002\u90a3\u519b\u5b98\u6240\u4f7f\u7684\u602a\u5175\u5203\u6bd4\u5e73\u5e38\u7684\u6746\u68d2\u7a0d\u77ed\uff0c\u6bd4\u5224\u5b98\u7b14\u53c8\u7a0d\u957f\uff0c\u68d2\u4e0a\u957f\u6ee1\u660e\u6643\u6643\u7684\u5012 \u94a9\uff0c\u53ef\u4ee5\u9501\u62ff\u5200\u5251\uff0c\u5728\u5175\u5668\u4e0a\u5148\u5360\u4e86\u4fbf\u5b9c\uff0c\u6b66\u6c0f\u5144\u5f1f\u5251\u6cd5\u867d\u7136\u51cc\u5389\u4e4b\u6781\uff0c\u5374\u4e5f\u9887\u6709\u987e\u5fcc\uff0c\u582a \u582a\u6253\u4e2a\u5e73\u624b\u3002\u9648\u5929\u5b87\u95ee\u9053\uff1a\u201c\u8fd9\u519b\u5b98\u4f7f\u7684\u5175\u5668\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u600e\u7684\u5982\u6b64\u5389\u5bb3\uff1f\u201d\u9f99\u7075\u77eb\u7b11\u9053\uff1a \u201c\u8fd9\u519b\u5b98\u540d\u53eb\u7126\u6625\u96f7\uff0c\u662f\u5927\u5185\u516b\u5927\u9ad8\u624b\u7684\u9996\u9886\uff0c\u529f\u529b\u5728\u6b66\u6c0f\u5144\u5f1f\u4e4b\u4e0a\uff0c\u5c31\u662f\u7528\u5bfb\u5e38\u7684\u5200\u5251\uff0c \u6b66\u6c0f\u5144\u5f1f\u4e5f\u8ba8\u4e0d\u4e86\u4ed6\u7684\u4fbf\u5b9c\uff0c\u52a0\u4e0a\u8fd9\u6839\u4e13\u95e8\u514b\u5236\u5200\u5251\u7684\u72fc\u7259\u68d2\uff0c\u5728\u4e94\u5341\u62db\u4e4b\u5185\uff0c\u6b66\u6c0f\u5144\u5f1f\u5fc5 \u7136\u843d\u8d25\u3002\u201d \u3000\u3000\u5b98\u519b\u9635\u52bf\u6e10\u7a33\uff0c\u9ea6\u6c38\u660e\u8fd9\u4e00\u80a1\u88ab\u5305\u56f4\u5728\u9635\u4e2d\uff0c\u7ea2\u8863\u756a\u50e7\u548c\u90a3\u516d\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u66f4\u88ab\u6321\u5728\u9635 \u5916\u3002\u9648\u5929\u5b87\u5fc3\u4e2d\u7a0d\u5bbd\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u4e0d\u5fc5\u54b1\u4eec\u51fa\u624b\uff0c\u5b98\u519b\u5df1\u80fd\u5e94\u4ed8\u4e86\u3002\u201d\u9f99\u7075\u77eb\u9762\u8272 \u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u54ea\u6709\u5982\u6b64\u8f7b\u6613\u4e86\u7ed3\u4e4b\u7406\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u4e1c\u9762\u5c71\u53e3\u6740\u51fa\u4e09\u4e2a\u4eba \u6765\uff0c\u670d\u9970\u4e00\u5982\u897f\u85cf\u5587\u561b\uff0c\u4f46\u8eab\u4e0a\u62ab\u7684\u8888\u88df\u5374\u662f\u767d\u8272\u7684\u3002 \u3000\u3000\u897f\u85cf\u7684\u5587\u561b\u5206\u4e3a\u7ea2\u9ec4\u4e8c\u6559\uff0c\u6240\u62ab\u7684\u8888\u88df\u4e0d\u662f\u7ea2\u8272\u5c31\u662f\u9ec4\u8272\uff0c\u62ab\u767d\u8888\u88df\u7684\u5587\u561b\uff0c\u9648\u5929\u5b87\u8fd8 \u672a\u89c1\u8fc7\uff0c\u6b63\u81ea\u5947\u602a\uff0c\u53ea\u542c\u5f97\u9f99\u7075\u77eb\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u9752\u6d77\u6cd5\u738b\u5c45\u7136\u4e5f\u6d3e\u4eba\u6765\u8d81\u8fd9\u8d9f\u6d51\u6c34\uff0c\u770b\u6765\u54b1 \u4eec\u8be5\u51fa\u624b\u4e86\u3002\u201d\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u6817\uff0c\u60f3\u8d77\u7236\u4eb2\u66fe\u5bf9\u4ed6\u8bb2\u8fc7\u897f\u85cf\u5587\u561b\u6559\u7684\u5386\u53f2\uff1a\u5f53\u4eca\u5728\u897f\u85cf\u5904 \u201c\u81f3\u9ad8\u65e0\u4e0a\u201d\u5730\u4f4d\u7684\u8fbe\u8d56\u548c\u73ed\u7985\u4e43\u662f\u9ec4\u6559\u7684\u9886\u8896\uff0c\u7ea2\u6559\u5219\u662f\u5728\u5143\u671d\u65f6\u5019\u5f97\u52bf\uff0c\u4f46\u7ea2\u9ec4\u4e8c\u6559\u4e4b \u524d\uff0c\u8fd8\u6709\u4e00\u6d3e\u767d\u6559\uff0c\u53c8\u79f0\u4e3a\u201c\u5676\u62f3\u6d3e\u201d\uff0c\u9886\u8896\u79f0\u4e3a\u201c\u6cd5\u738b\u201d\uff0c\u8fd9\u4e00\u6d3e\u5f97\u52bf\u5728\u7ea2\u6559\u4e4b\u540e\uff0c\u5728\u9ec4 \u6559\u4e4b\u524d\uff0c\u6709\u660e\u4e00\u4ee3\uff0c\u90fd\u662f\u5676\u62f3\u6d3e\u7684\u6cd5\u738b\u7edf\u6cbb\u897f\u85cf\uff0c\u4e00\u76f4\u5230\u660e\u4ee3\u6700\u540e\u7684\u90a3\u4e2a\u7687\u5e1d\uff0c\u5d07\u796f\u5341\u516d\u5e74 \u7684\u65f6\u5019\uff0c\u897f\u85cf\u683c\u9c81\u6d3e\uff08\u5373\u9ec4\u6559\uff09\u9886\u8896\u8fbe\u8d56\u4e94\u4e16\u548c\u73ed\u7985\u56db\u4e16\u501f\u9752\u6d77\u8499\u53e4\u65cf\u914b\u957f\u56fa\u59cb\u6c57\u7684\u5175\u529b\uff0c \u63a8\u7ffb\u4e86\u5676\u62f3\u6d3e\u6cd5\u738b\u5728\u897f\u85cf\u7684\u7edf\u6cbb\u5730\u4f4d\uff0c\u8fd9\u624d\u53d6\u800c\u4ee3\u4e4b\uff0c\u76f4\u5230\u5982\u4eca\u3002\u767d\u6559\u88ab\u9010\u51fa\u897f\u85cf\u4e4b\u540e\uff0c\u9003 \u81f3\u9752\u6d77\uff0c\u4f9d\u9644\u53e6\u4e00\u4f4d\u914b\u957f\u52a0\u817e\u6c57\uff0c\u4ecd\u7136\u53f7\u79f0\u6cd5\u738b\uff0c\u9648\u5929\u5b87\u8bb0\u8d77\u8fd9\u6bb5\u5386\u53f2\u3001\u5fc3\u4e2d\u60f3\u9053\uff0c\u201d\u539f\u6765 \u8fd9\u4e09\u4e2a\u5587\u561b\uff0c\u4e43\u662f\u9752\u6d77\u5676\u62f3\u6d3e\u6cd5\u738b\u7684\u4eba\uff0c\u602a\u4e0d\u5f97\u8eab\u4e0a\u62ab\u7684\u4e43\u662f\u767d\u8272\u8888\u88df\uff0c\u53ea\u662f\u5982\u6b64\u4e00\u6765\uff0c\u82e5 \u88ab\u4ed6\u4eec\u593a\u53bb\u91d1\u74f6\uff0c\u897f\u85cf\u5c82\u4e0d\u662f\u53c8\u8981\u9677\u5165\u4e00\u573a\u5185\u4e71\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u6765\u52bf\u51f6\u731b\u4e4b\u6781\uff0c\u7528\u7684\u5175\u5668\u90fd\u662f\u4e5d\u73af\u9521\u6756\uff0c\u6325\u52a8\u65f6\u54d7\u5566\u5566\u4e00\u7247\u4f5c\u54cd\u3002\u9f99\u7075 \u77eb\u624b\u6309\u5251\u67c4\uff0c\u9053\uff1a\u201c\u4e14\u518d\u770b\u4e00\u770b\u3002\u201d\u970e\u773c\u4e4b\u95f4\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u5df2\u51b2\u5165\u9635\u4e2d\uff0c\u6e38\u4e00\u9102\u7387\u9886\u536b \u58eb\u4e0a\u524d\u5835\u622a\uff0c\u5140\u662f\u8fde\u8fde\u540e\u9000\uff0c\u770b\u770b\u5c31\u8981\u7ed9\u4ed6\u4eec\u51b2\u7834\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u89c1\u5c71\u5934\u4e0a\u9ed1\u5f71\u4e00\u95ea\uff0c\u9f99\u7075 \u77eb\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u62d4\u5251\u51b2\u51fa\uff0c\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4e5f\u6025\u6025\u8ddf\u7740\u5954\u524d\uff0c\u9648\u5929\u5b87\u5fc3\u4e2d\u6b63\u81ea\u5947\u602a\uff1a \u4ee5\u9f99\u7075\u77eb\u5982\u6b64\u9547\u5b9a\u7684\u4eba\uff0c\u5c45\u7136\u4e00\u89c1\u8fd9\u5c71\u5934\u4e0a\u7684\u9ed1\u5f71\u4fbf\u5927\u60ca\u5931\u8272\uff0c\u6765\u7684\u4e0d\u77e5\u662f\u4ec0\u4e48\u6837\u5389\u5bb3\u7684\u4eba \u7269\u3002 \u3000\u3000\u90a3\u9ed1\u5f71\u6765\u5f97\u4e4b\u5feb\uff0c\u5b9e\u662f\u96be\u4ee5\u5f62\u5bb9\uff0c\u5728\u4ed6\u73b0\u8eab\u7684\u4e39\u8fbe\u5c71\u5934\uff0c\u8ddd\u79bb\u4e0b\u9762\u7684\u5ce1\u8c37\uff0c\u4f55\u6b62\u5343\u5c3a\uff0c \u521d\u73b0\u65f6\u53ea\u89c1\u4e00\u70b9\u9ed1\u70b9\uff0c\u970e\u773c\u4e4b\u95f4\u4fbf\u51fa\u73b0\u5168\u8eab\uff0c\u518d\u4e00\u8f6c\u773c\uff0c\u5df2\u5230\u5c71\u8170\uff0c\u9648\u5929\u5b87\u770b\u5f97\u5206\u660e\uff0c\u4e5f\u4e0d \u7981\u62c2\u4e2d\u5927\u60ca\uff0c\u539f\u6765\u8fd9\u4f4d\u4ece\u4e39\u8fbe\u5c71\u5934\u98de\u5954\u800c\u4e0b\u7684\u5f02\u4eba\uff0c\u7adf\u7136\u5c31\u662f\u6628\u65e5\u6240\u89c1\u7684\u90a3\u4e2a\u82e6\u884c\u50e7\uff0c\u8ddf\u5728 \u4ed6\u80cc\u540e\u7684\u8fd8\u6709\u51e0\u6761\u9ed1\u5f71\uff0c\u9648\u5929\u5b87\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u82e6\u884c\u50e7\u5df2\u96be\u5e94\u4ed8\uff0c\u4ed6\u8fd8\u5e26\u6709 \u4eba\u6765\uff0c\u8fd9\u91d1\u74f6\u53ea\u6015\u96be\u4ee5\u4fdd\u4f4f\u4e86\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u8fc5\u903e\u98d8\u98ce\uff0c\u4e00\u5251\u5f53\u5148\uff0c\u62a2\u5165\u9635\u4e2d\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u798f\u5927\u5e05\u6d3e\u6211\u6765\u63a5\u91d1\u74f6\uff01\u201d\u5fa1\u6797\u519b \u4e24\u8fb9\u4e00\u8ba9\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u6b63\u5728\u9635\u4e2d\uff0c\u542c\u5f97\u9f99\u7075\u77eb\u8fd9\u4e48\u4e00\u56b7\uff0c\u90fd\u56de\u8fc7\u5934\u6765\uff0c\u4e09\u67c4\u4e5d\u73af\u9521\u6756\u540c \u65f6\u6253\u5230\uff0c\u9f99\u7075\u77eb\u65e0\u5fc3\u604b\u6218\uff0c\u957f\u5251\u4e00\u6307\uff0c\u5728\u4e00\u67c4\u9521\u6756\u4e0a\u4e00\u6309\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u8eab\u5b50\u51cc\u7a7a\u98de\u8d77\uff0c\u4e00\u4e2a \u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u5df2\u4ece\u4e09\u4e2a\u767d\u6559\u5587\u561b\u7684\u5934\u4e0a\u98de\u8fc7\uff0c\u76f4\u5411\u4e2d\u519b\u5954\u53bb\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u540e\u81f3\uff0c\u8ddf\u7740\u95ef\u9635\uff0c\u90a3\u516d\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u6b63\u5728\u5916\u56f4\uff0c\u6392\u6210\u4e00\u5217\uff0c\u624b\u4e3e\u6708\u7259\u5f2f \u5200\uff0c\u6b32\u7814\u672a\u6240\uff0c\u5e7d\u840d\u7528\u5c3c\u6cca\u5c14\u8bdd\u53eb\u9053\uff1a\u201c\u5c0f\u516c\u4e3b\u5c31\u8981\u6765\u5566\uff0c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u9003\uff1f\u201d\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e00 \u6014\uff0c\u90a3\u756a\u50e7\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u4fe1\u5979\u7684\u9b3c\u8bdd\uff0c\u51b0\u5ddd\u5929\u5973\u65e9\u5df2\u88ab\u706b\u5c71\u541e\u6ca1\u4e86\u3002\u201d\u5e7d\u840d\u628a\u624b\u4e00\u626c\uff0c\u53d1 \u51fa\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u90a3\u516d\u540d\u6b66\u58eb\u90fd\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u5176\u4e2d\u6709\u4e24\u4e2a\u6b66\u58eb\u66fe\u8ddf\u8fc7\u7ea2\u8863\u756a\u50e7\u4e0a \u5c71\uff0c\u8ba4\u5f97\u5979\u662f\u51b0\u5bab\u4f8d\u5973\uff0c\u5fc3\u6709\u987e\u5fcc\uff0c\u8eab\u5f62\u4e00\u632a\uff0c\u5e7d\u840d\u4e0e\u9648\u5929\u5b87\u6025\u4ece\u7f3a\u53e3\u51b2\u8fc7\u3002 \u3000\u3000\u9635\u4e2d\u5230\u5904\u6df7\u6218\uff0c\u9648\u5929\u5b87\u4e0d\u613f\u4e0e\u5317\u4e94\u7701\u7684\u8c6a\u6770\u4ea4\u950b\uff0c\u62db\u547c\u5e7d\u840d\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u5bf9\u4ed8\u90a3\u4e2a\u756a \u50e7\u3002\u201d\u4ed6\u4eec\u6709\u5b98\u519b\u8ba9\u8def\uff0c\u95ef\u9635\u8f83\u6613\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u5ffd\u7684\u53c8\u56de\u8fc7\u5934\u6765\uff0c\u6bd7\u7259\u4e00\u7b11\uff0c\u9648\u5929\u5b87\u4e0e \u5e7d\u840d\u6b63\u5f85\u51b2\u8fc7\uff0c\u8033\u8fb9\u53ea\u542c\u5f97\u54d7\u561f\u561f\u7684\u4e00\u7247\u54cd\u58f0\uff0c\u4e00\u67c4\u4e5d\u73af\u6756\u5df2\u8fce\u9762\u5954\u6765\uff0c\u5f53\u5934\u7684\u90a3\u767d\u6559\u5587\u561b \u53eb\u9053\uff1a\u201c\u5c0f\u5a03\u5a03\u5feb\u6eda\u56de\u53bb\uff01\u201d\u9521\u6756\u4e00\u6325\uff0c\u5e7d\u840d\u548c\u9648\u5929\u5b87\u90fd\u89c9\u5f97\u6709\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u4e24\u53e3\u957f\u5251\u8131 \u624b\u98de\u51fa\u3002\u8fd9\u8fd8\u662f\u90a3\u4e2a\u767d\u6559\u5587\u561b\uff0c\u89c1\u4ed6\u4eec\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e0d\u5fcd\u901f\u4e0b\u6740\u624b\uff0c\u8981\u4e0d\u7136\u66f4\u96be\u5e94\u4ed8\u3002 \u3000\u3000\u90a3\u4e2a\u767d\u6559\u5587\u561b\u6b63\u5728\u6bd7\u7259\u54a7\u5634\u7684\u602a\u7b11\uff0c\u54ea\u77e5\u5e7d\u840d\u65e9\u6709\u51c6\u5907\uff0c\u5c31\u5728\u957f\u5251\u8131\u624b\u4e4b\u65f6\uff0c\u4e09\u679a\u51b0\u9b44 \u795e\u5f39\u5df2\u662f\u540c\u65f6\u53d1\u51fa\u3002\u4e09\u4e2a\u767d\u6559\u5587\u561b\u54ea\u77e5\u4e16\u95f4\u6709\u5982\u6b64\u53e4\u602a\u7684\u5389\u5bb3\u6697\u5668\uff0c\u6dec\u4e0d\u53ca\u9632\uff0c\u7adf\u7136\u90fd\u7ed9\u51b0 \u9b44\u795e\u5f39\u6253\u4e2d\u655e\u5f00\u7684\u80f8\u53e3\uff0c\u53ea\u89c9\u4e00\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u523a\u4f53\u4fb5\u80a4\uff0c\u4e0d\u7531\u5f97\u4e5f\u673a\u4f36\u4f36\u90fd\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002 \u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u8d81\u6b64\u65f6\u673a\u3001\u500f\u5730\u4e00\u63a0\u5373\u8fc7\uff0c\u63a5\u4e86\u90a3\u4e24\u67c4\u9707\u98de\u7684\u957f\u5251\uff0c\u5411\u524d\u6025\u5954\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u767d\u6559\u5587\u561b\u529f\u529b\u751a\u9ad8\uff0c\u867d\u88ab\u51b0\u9b44\u795e\u5f39\u6253\u4e2d\uff0c\u8fd0\u6c14\u4e00\u8f6c\uff0c\u5374\u4e5f\u65e0\u4e8b\u3002\u4ed6\u60f3\u8fd8\u662f\u62a2\u593a\u91d1 \u74f6\u8981\u7d27\uff0c\u4e5f\u5c31\u4e0d\u518d\u7406\u4f1a\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4e8c\u4eba\uff0c\u7ffb\u8eab\u62a1\u6756\u53c8\u518d\u6251\u5411\u4e2d\u519b\u9ec4\u5e10\u3002\u8fd9\u65f6\u9f99\u7075\u77eb\u5df2\u6bd4\u90a3 \u82e6\u884c\u50e7\u62a2\u5feb\u51e0\u6b65\uff0c\u5148\u5230\u4e86\u674f\u9ec4\u65d7\u4e0b\u3002 \u3000\u3000\u90a3\u82e6\u884c\u50e7\u7684\u6765\u52bf\u715e\u662f\u60ca\u4eba\uff0c\u53ea\u89c1\u4ed6\u624b\u6325\u7af9\u6756\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u70b9\uff0c\u9ad8\u8eab\u4e03\u6b65\u4e4b\u5185\u7684\u5fa1\u6797 \u519b\uff0c\u4e00\u88ab\u7af9\u6756\u6cbe\u7740\uff0c\u7acb\u523b\u8dcc\u5012\u4e00\u8eab\u4e3a\u5927\u5185\u516b\u5927\u9ad8\u624b\u9886\u8896\u7684\u7126\u6625\u96f7\uff0c\u4e5f\u4e0d\u7981\u5927\u60ca\u5931\u8272\uff0c\u6025\u5c06\u72fc \u7259\u68d2\u4e00\u62bd\uff0c\u6446\u8131\u4e86\u6b66\u6c0f\u5144\u5f1f\u7684\u7ea0\u7f20\uff0c\u4e0a\u524d\u8fce\u6218\u3002 \u3000\u3000\u9f99\u7075\u77eb\u4e0e\u90a3\u82e6\u884c\u50e7\u51e0\u4e4e\u540c\u65f6\u8d76\u5230\uff0c\u7126\u6625\u96f7\u62bd\u8eab\uff0c\u9f99\u7075\u77eb\u8865\u4e0a\uff0c\u6b66\u6c0f\u5144\u5f1f\u6740\u5f97\u6027\u8d77\uff0c\u53cc\u5251 \u4e00\u5408\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u4e00\u9f50\u53cd\u8eab\u8fdb\u5251\uff0c\u5de6\u5251\u201c\u6d41\u661f\u8d76\u6708\u201d\uff0c\u53f3\u5251\u201c\u63a3\u7535\u98de\u4e91\u201d\uff0c\u5206\u523a\u9f99\u7075\u77eb\u4e24\u80c1 \u5927\u7a74\u3002\u6309\u5251\u52bf\u6765\u8bf4\uff0c\u5728\u8fd1\u8ddd\u79bb\u4e4b\u5185\uff0c\u8fd9\u53cc\u5251\u523a\u7a74\u7684\u6740\u624b\uff0c\u5b9e\u662f\u96be\u4ee5\u95ea\u907f\u3002\u54ea\u77e5\u9f99\u7075\u77eb\u5251\u6cd5\u602a \u5f02\u4e4b\u6781\uff0c\u5b8c\u5168\u4e0d\u4f9d\u5e38\u89c4\uff0c\u5251\u8eab\u4e00\u6296\uff0c\u5251\u950b\u63a5\u7740\u4e86\u6b66\u8001\u5927\u7684\u5251\u950b\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u9f99\u7075\u77eb\u7684\u5251 \u8d81\u52bf\u53cd\u5f39\uff0c\u5251\u67c4\u4e00\u649e\uff0c\u53c8\u5c06\u6b66\u8001\u4e8c\u7684\u5251\u78b0\u6b6a\uff0c\u771f\u662f\u62ff\u634f\u65f6\u5019\uff0c\u4e0d\u5dee\u6beb\u53d1\u3002\u6b66\u6c0f\u5144\u5f1f\u5403\u4e86\u4e00 \u60ca\u3002\u53ea\u542c\u5f97\u9f99\u7075\u77eb\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u957f\u5251\u4e00\u4f38\u4e00\u7f29\uff0c\u8fde\u524a\u4e09\u4e0b\uff0c\u5c06\u6b66\u6c0f\u5144\u5f1f\u8feb\u5f97\u51e0\u4e4e\u7a33 \u4e0d\u4f4f\u8eab\u5f62\u3002\u9ad8\u624b\u8bd5\u62db\uff0c\u4e00\u4f38\u624b\u4fbf\u77e5\u865a\u5b9e\uff0c\u6b66\u6c0f\u5144\u5f1f\u63a5\u4e86\u8fd9\u51e0\u62db\uff0c\u77e5\u9053\u6765\u4eba\u6b66\u529f\uff0c\u8fdc\u5728\u81ea\u5df1\u4e4b \u4e0a\uff0c\u800c\u4e14\u4f3c\u662f\u6545\u610f\u7559\u60c5\uff0c\u4e0d\u65bd\u6740\u624b\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u70b9\u5230\u5373\u6b62\u3002\u4e0d\u6562\u518d\u7f20\uff0c\u4e24\u5144\u5f1f\u5de6\u53f3\u4e00\u5206\uff0c\u9f99 \u7075\u77eb\u9053\u58f0\uff1a\u201c\u627f\u8ba9\u201d\uff0c\u8eab\u5f62\u4e00\u60ca\u5373\u8fc7\u3002 \u3000\u3000\u7126\u6625\u96f7\u6446\u8131\u4e86\u6b66\u6c0f\u5144\u5f1f\u7ea0\u7f20\uff0c\u72fc\u7259\u68d2\u4e00\u6446\uff0c\u4e0a\u524d\u8fce\u6218\u90a3\u4e2a\u5370\u5ea6\u50e7\u4eba\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u90a3 \u5370\u5ea6\u82e6\u884c\u50e7\u53c8\u5df2\u70b9\u5012\u4e86\u4fdd\u62a4\u91d1\u74f6\u7684\u4e24\u540d\u9ad8\u624b\uff0c\u7af9\u4ed7\u5411\u524d\u4e00\u70b9\uff0c\u8f7b\u98d8\u98d8\u7684\u597d\u50cf\u6beb\u4e0d\u7ecf\u610f\uff0c\u6756\u5c16 \u5df2\u500f\u5730\u6307\u5230\u4e86\u7126\u6625\u96f7\u7684\u98ce\u5e9c\u7a74\u3002\u5728\u5343\u519b\u4e07\u9a6c\u4e4b\u4e2d\uff0c\u4fe1\u624b\u70b9\u7a74\uff0c\u8ba4\u7a74\u8ba4\u5f97\u5982\u6b64\u51c6\u786e\uff0c\u800c\u4e14\u5982\u6b64 \u5feb\u6377\u5229\u843d\uff0c\u4ee4\u5f97\u7126\u6625\u96f7\u4e5f\u4e0d\u7981\u4e00\u60ca\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u8fde\u5fd9\u8fd0\u6c14\u4f7f\u529b\uff0c\u52b2\u529b\u76f4\u900f\u68d2\u7aef\uff0c\u53cd\u624b\u4e00\u68d2\uff0c \u7528\u7684\u5012\u94a9\u628a\u7af9\u6756\u94a9\u7740\u3002\u7126\u6625\u96f7\u7528\u529b\u4e00\u9707\uff0c\u4ee5\u4e3a\u8fd9\u7af9\u6756\u4e0d\u88ab\u94a9\u88c2\u5c31\u5b9a\u88ab\u9707\u65ad\uff0c\u54ea\u6599\u4ed6\u7528\u5c3d\u5168 \u529b\uff0c\u8fd9\u50e7\u4eba\u7684\u7af9\u6756\u5374\u4f3c\u9644\u5728\u4ed6\u7684\u68d2\u4e0a\u4f3c\u7684\uff0c\u91c9\u8fde\u7262\u9644\uff0c\u5982\u540c\u4e00\u4f53\uff0c\u529b\u65e0\u6240\u65bd\uff0c\u52b2\u4ea6\u6d88\u89e3\uff0c\u800c \u4e14\u8fd8\u9690\u9690\u6709\u4e00\u80a1\u6f5c\u529b\u8feb\u6765\u3002\u7126\u6625\u96f7\u6b64\u60ca\u975e\u5c0f\uff0c\u72fc\u7259\u68d2\u8981\u62bd\u5f00\u4e5f\u4e0d\u53ef\u80fd\u3002\u5fc3\u77e5\u8fd9\u50e7Ato\u5185\u529b\uff0c \u9ad8\u51fa\u81ea\u5df1\u4e0d\u6b62\u4e00\u500d\uff0c\u82e5\u7136\u76f8\u6301\u4e0b\u53bb\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u5b9a\u53d7\u5185\u4f24\uff0c\u6b63\u81ea\u7126\u6025\uff0c\u5ffd\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u201c\u548b \u568f\u201d\u4e00\u58f0\uff0c\u9f99\u7075\u77eb\u4e00\u5251\u98de\u6765\uff0c\u5728\u5f53\u4e2d\u8f7b\u8f7b\u4e00\u6311\uff0c\u5c06\u72fc\u7259\u68d2\u548c\u7af9\u6756\u5206\u5f00\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u7126\u5927\u4eba \u4f60\u8fd8\u662f\u56de\u53bb\u4fdd\u62a4\u91d1\u74f6\u8981\u7d27\u3002\u201d \u3000\u3000\u82e6\u884c\u50e7\u62bd\u51fa\u7af9\u6756\uff0c\u89c1\u6756\u8eab\u5df2\u88ab\u5b9d\u5251\u5212\u4e86\u4e00\u9053\u5251\u75d5\u3002\u4e5f\u4e0d\u7981\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u5ffd\u800c\u53cc\u773c\u4e00 \u5f20\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u6765\u4e86\uff01\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6628\u65e5\u4f60\u8f83\u91cf\u6211\uff0c\u4eca\u65e5\u6211\u53ef\u8981\u8f83\u91cf\u4f60\u4e86\u3002\u201d\u957f\u5251 \u4e00\u5c55\uff0c\u4e00\u62db\u201c\u9a8f\u9a6c\u660e\u9a7c\u201d\uff0c\u5411\u524d\u75be\u524a\uff0c\u90a3\u50e7\u4eba\u7adf\u628a\u7af9\u6756\u4e00\u6a2a\uff0c\u8fce\u7740\u5b9d\u5251\u906e\u6321\u3002\u6309\u8bf4\u7af9\u6756\u9047\u7740 \u5229\u5251\uff0c\u90a3\u662f\u5fc5\u65ad\u65e0\u7591\uff0c\u54ea\u77e5\u4ed6\u8fd9\u4e00\u6756\uff0c\u6240\u4f7f\u7684\u52b2\u529b\uff0c\u5374\u662f\u5de7\u5230\u6781\u70b9\uff0c\u4e00\u6cbe\u5251\u5203\uff0c\u4fbf\u5373\u968f\u624b\u4e00 \u5e26\uff0c\u9f99\u7075\u77eb\u7adf\u4e0d\u7531\u81ea\u4e3b\u7684\u8ddf\u4ed6\u79fb\u52a8\u4e09\u6b65\u3002 \u3000\u3000\u82e6\u884c\u50e7\u7684\u7af9\u6756\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u7528\u4e00\u4e2a\u201c\u6cbe\u201d\u5b57\u8bc0\uff0c\u8981\u5c06\u9f99\u7075\u77eb\u7684\u8eab\u5f62\u5e26\u52a8\uff0c\u9f99\u7075\u77eb\u5de6\u624b\u672c \u6765\u634f\u7740\u201c\u5251\u8bc0\u201d\uff0c\u5ffd\u5730\u53cc\u6307\u4e00\u5f39\uff0c\u7af9\u6756\u7adf\u7ed9\u5f39\u6b6a\uff0c\u90a3\u7af9\u6756\u821e\u5230\u6025\u5904\uff0c\u52b2\u529b\u751a\u5927\uff0c\u9f99\u7075\u77eb\u7adf\u80fd \u4ee5\u5f39\u6307\u529b\u4e4b\u529b\uff0c\u5c06\u5b83\u6d88\u89e3\uff0c\u90a3\u50e7\u4eba\u4e5f\u4e0d\u7981\u53eb\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9f99\u7075\u77eb\u7684\u957f \u5251\u4e00\u6446\u8131\u7af9\u6756\u7684\u6cbe\u7f20\uff0c\u7acb\u523b\u8fde\u8fdb\u4e09\u62db\uff0c\u6bcf\u4e00\u62db\u53c8\u5206\u4e3a\u4e09\u5f0f\uff0c\u5251\u5c16\u6240\u6307\uff0c\u90fd\u662f\u50e7\u4eba\u7684\u8981\u5bb3\u7a74 \u9053\uff0c\u5373\u662f\u8bf4\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u8981\u8fde\u523a\u4e5d\u5904\u7a74\u9053\uff0c\u800c\u4e14\u624b\u6cd5\u6709\u865a\u6709\u5b9e\uff0c\u5404\u5177\u5965\u5999\uff0c\u90a3\u50e7\u4eba\u672c\u662f\u70b9\u7a74 \u7684\u9ad8\u624b\uff0c\u89c1\u4e86\u4ea6\u81ea\u6127\u4e0d\u5982\u3002\u4f46\u4ed6\u7684\u6b66\u529f\u786e\u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u7af9\u6756\u4e00\u5c01\uff0c\u7adf\u7136\u4e5f\u662f\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u4e0b \u56db\u8bb0\u6740\u624b\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u5c06\u9f99\u7075\u77eb\u7684\u653b\u52bf\u4e00\u4e00\u5316\u89e3\uff0c\u4e24\u4eba\u65d7\u9f13\u76f8\u5f53\uff0c\u529f\u529b\u6089\u654c\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u6740 \u5f97\u96be\u5206\u96be\u89e3\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4e8c\u4eba\uff0c\u95ef\u8fc7\u4e86\u767d\u6559\u5587\u561b\u90a3\u5173\u4e4b\u540e\uff0c\u4fbf\u76f4\u6251\u7ea2\u8863\u756a\u50e7\u3002\u5e7d\u840d\u53f1\u9053\uff1a \u201c\u4e0a\u6b21\u5728\u51b0\u5bab\u4e4b\u4e2d\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u5c0f\u516c\u4e3b\u600e\u6837\u5429\u5490\u4f60\u6765\uff1f\u201d\u5f53\u65f6\u51b0\u5ddd\u5929\u5973\u662f\u53eb\u4ed6\u4ece\u901f\u56de\u56fd\uff0c\u4f11 \u591a\u751f\u4e8b\u7684\u3002\u7ea2\u8863\u756a\u50e7\u662f\u5c3c\u6cca\u5c14\u7684\u56fd\u5e08\uff0c\u6709\u751f\u4ee5\u6765\uff0c\u53ea\u66fe\u5728\u51b0\u5ddd\u5bab\u4e2d\u906d\u8fc7\u4e24\u6b21\u60e8\u8d25\uff0c\u542c\u5e7d\u840d\u63d0 \u8d77\u6b64\u4e8b\uff0c\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5c0f\u4e2b\u5934\uff0c\u6d12\u5bb6\u4e14\u628a\u4f60\u9001\u5f80\u897f\u5929\uff0c\u8ba9\u4f60\u53bb\u89c1\u4f60\u7684\u5c0f\u516c \u4e3b\u3002\u201d\u7ea2\u8863\u756a\u50e7\u4ee5\u4e3a\u51b0\u5ddd\u5929\u5973\u5df2\u6b7b\uff0c\u6545\u6709\u6b64\u8a00\u3002 \u3000\u3000\u9648\u5929\u5b87\u89c1\u4e86\u6740\u5e08\u4ec7\u4eba\uff0c\u4e5f\u662f\u6012\u4ece\u5fc3\u8d77\uff0c\u7ea2\u8863\u756a\u50e7\u7985\u6756\u5c1a\u672a\u843d\u4e0b\uff0c\u4ed6\u5df2\u5148\u65bd\u6740\u624b\uff0c\u4e00\u62db \u201c\u5012\u5377\u51b0\u6cb3\u201d\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u767b\u5bf9\u5c06\u56db\u9762\u5c01\u4f4f\u3002\u7ea2\u8863\u756a\u50e7\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5728\u51b0\u5bab\u6570 \u6708\uff0c\u6b66\u529f\u7adf\u7136\u7cbe\u8fdb\u5982\u65af\uff01\u201d\u7985\u6756\u5f80\u5916\u4e00\u8361\uff0c\u9aa4\u7136\u95f4\u5ffd\u89c9\u4e00\u80a1\u51b7\u6c14\u5c04\u6765\uff0c\u7ea2\u8863\u756a\u50e7\u6253\u4e86\u4e00\u4e2a\u5bd2 \u568e\uff0c\u7985\u6756\u53bb\u52bf\u8f83\u6162\uff0c\u4f46\u4ecd\u7136\u628a\u9648\u5929\u5b87\u7684\u5b9d\u5251\u8361\u5f00\uff0c\u9707\u5f97\u4ed6\u864e\u53e3\u751f\u75db\u3002 \u3000\u3000\u672c\u6765\u7ea2\u8863\u756a\u50e7\u7684\u529f\u529b\uff0c\u6bd4\u9648\u5929\u5b87\u9ad8\u5f3a\u6570\u500d\uff0c\u4f46\u4e00\u8005\u662f\u4ed6\u5df2\u5267\u6597\u534a\u5929\uff0c\u5c24\u5176\u662f\u5bf9\u5927\u5185\u9ad8\u624b \u6e38\u4e00\u9102\u90a3\u573a\uff0c\u6d88\u8017\u4e86\u4e0d\u5c11\u6c14\u529b\uff1b\u4e8c\u8005\u662f\u9648\u5929\u5b87\u7684\u5251\u672f\u7cbe\u5999\uff0c\u4ee4\u4ed6\u6709\u6240\u987e\u5fcc\uff1a\u4e09\u8005\u662f\u9648\u5929\u5b87\u6709 \u5e7d\u840d\u7684\u76f8\u52a9\uff0c\u5e7d\u840d\u7684\u6b66\u529f\uff0c\u5728\u51b0\u5bab\u4f8d\u5973\u4e4b\u4e2d\uff0c\u6570\u4e00\u6570\u4e8c\uff0c\u90a3\u67c4\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u66f4\u662f\u4eba\u95f4\u5c11\u6709\u7684 \u5175\u5203\uff0c\u4ee4\u4ed6\u4e0d\u80fd\u4e0d\u5206\u795e\u8fd0\u529f\uff0c\u4ee5\u6297\u5fa1\u5bd2\u6c14\u3002\u6709\u6b64\u4e09\u4e2a\u539f\u7528\uff0c\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u5408\u6218\u7ea2\u8863\u756a\u50e7\uff0c\u4ea6 \u662f\u96be\u5206\u9ad8\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u5ce1\u8c37\u4e4b\u4e2d\uff0c\u6df7\u6218\u6b63\u9163\uff0c\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4e8c\u4eba\u5168\u529b\u5bf9\u4ed8\u5f3a\u654c\uff0c\u65e0\u6687\u65c1\u987e\uff0c\u5ffd\u95fb\u5f97\u5b98\u519b\u8f70 \u7136\u5927\u53eb\uff0c\u6f6e\u6c34\u822c\u5730\u4e71\u6d8c\uff0c\u9648\u5929\u5b87\u3001\u5e7d\u840d\u4e0e\u90a3\u756a\u50e7\u90fd\u7ed9\u51b2\u5f00\uff0c\u968f\u7740\u4eba\u6d41\u5411\u524d\u79fb\u52a8\u3002\u9648\u5929\u5b87\u4e3e\u5934 \u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\uff0c\u5df2\u6740\u8fdb\u4e2d\u519b\uff0c\u62a2\u4e86\u4e09\u5339\u767d\u9a6c\u5176\u4e2d\u7684\u4e00\u5339\u9a6e\u7740\u4e00\u4e2a\u7528\u9f99\u7eb9\u9ec4 \u7ee2\u8986\u7f69\u7684\u3001\u5f62\u5982\u7b3c\u5b50\u4f3c\u7684\u4e1c\u897f\uff0c\u516b\u5927\u9ad8\u624b\u7684\u9886\u8896\u7126\u6625\u96f7\u5486\u54ee\u5982\u96f7\uff0c\u6b63\u5411\u90a3\u5339\u767d\u9a6c\u8ffd\u53bb\u3002\u9648\u5929 \u5b87\u5927\u60ca\u5931\u8272\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5339\u767d\u9a6c\u9a6e\u7684\uff0c\u4e00\u5b9a\u662f\u91d1\u672c\u5df4\u74f6\u3002\u201d\u518d\u4e00\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b \u90fd\u5df2\u8de8\u4e0a\u9a6c\u80cc\u3002\u4e09\u5339\u767d\u9a6c\u4e00\u9f50\u5636\u9e23\uff0c\u4e00\u9f50\u5411\u524d\u6a2a\u51b2\u76f4\u649e\u3002 \u3000\u3000\u7126\u6625\u96f7\u8ffd\u4e0d\u4e0a\uff0c\u770b\u770b\u90a3\u4e09\u5339\u767d\u9a6c\u5c31\u8981\u51b2\u51fa\u91cd\u56f4\u3002\u9f99\u7075\u77eb\u4e00\u58f0\u5927\u559d\uff0c\u594b\u8d77\u795e\u529b\uff0c\u65bd\u5c55\u5e73\u751f \u7f55\u7528\u7684\u201c\u62db\u9b42\u5341\u516b\u62db\u201d\u5251\u6cd5\uff0c\u8fd9\u5341\u516b\u62db\u4e00\u6c14\u5475\u6210\u3001\u4e00\u62db\u5feb\u4f3c\u4e00\u62db\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u865a\u5b9e\u5e76\u7528\uff0c\u4e13 \u523a\u654c\u4eba\u8981\u5bb3\u9053\uff0c\u5389\u5bb3\u662f\u5389\u5bb3\u6781\u4e86\uff0c\u4f46\u5374\u751a\u4e3a\u635f\u8017\u5185\u529b\uff0c\u5251\u6cd5\u4e00\u5c55\uff0c\u521a\u4f7f\u5230\u7b2c\u4e03\u62db\u201c\u8ffd\u9b42\u593a \u9b44\u201d\u90a3\u82e6\u884c\u50e7\u4eba\u4fbf\u6c14\u5598\u5401\u5401\uff0c\u7af9\u6756\u4e00\u62d6\uff0c\u95ea\u5f00\u5251\u950b\uff0c\u8ba9\u9f99\u7075\u77eb\u75be\u51b2\u800c\u8fc7\u3002\u9f99\u7075\u77eb\u5fc3\u5934\u4e00\u52a8\uff0c \u6781\u662f\u8be7\u5f02\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u8fd9\u5996\u50e7\u7684\u529f\u529b\uff0c\u4e0d\u5e94\u5982\u6b64\uff01\u82e6\u884c\u50e7\u4f55\u4ee5\u8981\u5047\u8d25\uff0c\u9f99\u7075\u77eb\u4e00\u65f6\u4e4b \u95f4\uff0c\u731c\u60f3\u4e0d\u900f\uff0c\u65f6\u95f4\u6025\u8feb\uff0c\u4e5f\u4e0d\u5bb9\u4ed6\u601d\u7d22\uff0c\u7acb\u5373\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u5c55\u5f00\u8f7b\u7075\u8eab\u6cd5\uff0c\u4e13\u4ece\u7a7a\u9699\u4e4b \u5904\u94bb\u8fc7\uff0c\u98de\u8eab\u8ffd\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u8feb\u8fc7\u7126\u6625\u96f7\u7684\u524d\u5934\uff0c\u7ecf\u8fc7\u4ed6\u8eab\u65c1\u4e4b\u65f6\uff0c\u9690\u7ea6\u542c\u5f97\u7126\u6625\u96f7\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8ba9\u4ed6 \u53bb\u5427\u3002\u201d\u9f99\u7075\u77eb\u8eab\u6cd5\u592a\u7094\uff0c\u6536\u52bf\u4e0d\u53ca\uff0c\u8f6c\u5934\u4e00\u671b\uff0c\u7126\u6625\u96f7\u5df2\u5728\u8eab\u540e\u6570\u4e08\uff0c\u5374\u4ecd\u662f\u626c\u6367\u4f5c\u52bf\uff0c \u811a\u6b65\u4e0d\u505c\uff0c\u9f99\u7075\u77eb\u4e0d\u7531\u5f97\u53c8\u662f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u6211\u542c\u9519\u4e86\uff1f\u7126\u6625\u96f7\u662f\u5927\u5185\u9ad8\u624b\u7684\u9996 \u9886\uff0c\u4fdd\u62a4\u91d1\u74f6\u4e4b\u8d23\u5c31\u6401\u5728\u4ed6\u7684\u80a9\u4e0a\uff0c\u600e\u4e48\u4ed6\u5374\u8bf4\u2018\u8ba9\u4ed6\u53bb\u5427\u2019\u65e2\u662f\u4efb\u8ba9\u4ed6\u53bb\uff0c\u4f55\u4ee5\u7126\u6625\u96f7\u81ea \u5df1\u5374\u53c8\u5411\u524d\u8ffd\u8d76\uff1f\u201d\u9f99\u7075\u77eb\u5fc3\u4e2d\u867d\u7136\u8be7\u5f02\uff0c\u811a\u6b65\u5374\u4e0d\u505c\uff0c\u500f\u5ffd\u8ffd\u5230\u90a3\u4e09\u5339\u767d\u9a6c\u4e4b\u540e\uff0c\u90a3\u4e09\u4e2a \u767d\u6559\u5587\u561b\u4e00\u62e8\u9a6c\u5934\uff0c\u4e09\u67c4\u4e5d\u73af\u9521\u6756\u540c\u65f6\u626b\u5230\uff0c\u9f99\u7075\u77eb\u4e00\u62db\u201c\u957f\u8679\u7ecf\u5929\u201d\uff0c\u5b9d\u5251\u6a2a\u7a7a\u4e00\u5212\uff0c\u5c06 \u4e09\u67c4\u9521\u6756\u4e00\u9f50\u6321\u5f00\uff0c\u8fd9\u4e09\u4e2a\u767d\u6559\u5587\u9ebb\u6b66\u529f\u4e5f\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u66f4\u52a0\u4ee5\u4e00\u5728\u9a6c\u4e0a\uff0c\u4e00\u5728\u9a6c\u4e0b\uff0c\u9f99\u7075 \u77eb\u81ea\u662f\u96be\u5360\u4e0a\u98ce\u3002\u5ffd\u542c\u5f97\u7126\u6625\u96f7\u53eb\u4e86\u4e00\u58f0\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u4ed6\u6ee1\u9762\u60ca\u60f6\u4e4b\u8272\uff0c\u9065\u9065\u5411\u81ea\u5df1\u62db \u624b\u3002 \u3000\u3000\u9f99\u7075\u77eb\u8be7\u5f02\u4e4b\u6781\uff0c\u4e0d\u7531\u5251\u52bf\u4e00\u6162\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u4e58\u673a\u62e8\u8f6c\u9a6c\u5934\uff0c\u5411\u659c\u523a\u75be\u51b2\uff0c\u500f\u5ffd\u8fc7 \u4e86\u540e\u9762\u5ce1\u8c37\u7684\u5587\u53ed\u53e3\uff0c\u6e05\u519b\u540e\u9632\u8f83\u5f31\uff0c\u88ab\u4ed6\u4eec\u4e00\u9635\u4e71\u6253\uff0c\u51b2\u51fa\u53bb\u4e86\u3002\u9f99\u7075\u77eb\u5fc3\u5ff5\u4e00\u52a8\uff0c\u731b\u7684 \u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u662f\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u4e48\uff1f\u90a3\u767d\u9a6c\u9a6e\u7684\u96be\u9053\u4e0d\u662f\u91d1\u74f6\uff1f\u201d\u60f3\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u8fd9\u5173\u7cfb\u592a \u5927\uff0c\u4e07\u4e00\u6599\u9519\uff0c\u91d1\u74f6\u88ab\u52ab\uff0c\u897f\u85cf\u6e05\u5ef7\u5b98\u540f\uff0c\u4e2a\u4e2a\u90fd\u662f\u6740\u5934\u7684\u7f6a\u540d\u3002 \u3000\u3000\u9f99\u7075\u77eb\u7565\u4e00\u8e0c\u8e87\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u5df2\u51b2\u51fa\u5b98\u519b\u5305\u56f4\uff0c\u6b63\u8d70\u4e0a\u5ce1\u8c37\u7684\u659c\u5761\uff0c\u6570\u5343\u5fa1\u6797\u519b\u89c1 \u91d1\u74f6\u88ab\u52ab\uff0c\u767b\u65f6\u5927\u4e71\uff0c\u9f13\u566a\u4e4b\u58f0\u5982\u6f6e\uff0c\u540e\u519b\u53d8\u4f5c\u524d\u519b\uff0c\u6539\u8f6c\u9635\u5f62\uff0c\u4e07\u7bad\u9f50\u53d1\uff0c\u5343\u9a6c\u540c\u8ffd\uff0c\u4f46 \u90a3\u4e09\u5339\u767d\u9a6c\u4e43\u662f\u5fa1\u82d1\u5b9d\u9a6c\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5df2\u51b2\u4e0a\u659c\u5761\uff0c\u5fa1\u6797\u519b\u5982\u4f55\u8ffd\u8d76\u5f97\u4e0a\uff1f \u3000\u3000\u6b63\u5728\u8fd9\u6781\u5ea6\u7d27\u5f20\u4e4b\u65f6\uff0c\u5343\u519b\u6ce8\u76ee\u4e4b\u9645\uff0c\u5ffd\u95fb\u5f97\u5c71\u5761\u4e0a\u4e00\u58f0\u957f\u5578\uff0c\u7a81\u7136\u95ea\u4e86\u4e00\u4e2a\u767d\u8863\u5c11 \u5e74\uff0c\u8863\u5e26\u98d8\u98d8\uff0c\u62e6\u5728\u8def\u4e2d\uff0c\u628a\u624b\u4e00\u626c\uff0c\u4e09\u5339\u767d\u9a6c\uff0c\u4e00\u9f50\u5636\u53eb\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\uff0c\u52c3\u7136\u5927\u6012\uff0c\u4e09\u67c4\u7985\u6756\u4e00\u9f50\u5411\u524d\u626b\u53bb\u3002\u731b\u7136\u95f4\uff0c\u5ffd\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u53cc\u624b\u4e00 \u626c\uff0c\u4e09\u9053\u6697\u8d64\u8272\u7684\u5149\u534e\u7535\u5c04\u800c\u5230\uff0c\u602a\u94ed\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u90a3\u4e09\u4e2a\u5587\u561b\u7684\u7985\u6756\uff0c\u88ab\u6697\u5668\u6253\u4e2a\u6b63 \u7740\uff0c\u53ea\u89c9\u864e\u53e3\u75bc\u75db\u3001\u7985\u6756\u51e0\u4e4e\u638c\u63e1\u4e0d\u7262\uff0c\u53ea\u542c\u5f97\u5ce1\u8c37\u4e0b\u9762\uff0c\u6709\u4eba\u5728\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5929\u5c71\u795e\u8292\uff0c \u5929\u5c71\u795e\u8292\uff01\u201d\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u6014\u4e86\u4e00\u6014\uff0c\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u7559\u4e0b\u91d1\u74f6\uff0c\u5feb\u6eda\u56de\u53bb\uff01\u201d\u90a3\u4e09\u4e2a \u5587\u561b\u89c1\u5927\u529f\u5373\u5c06\u544a\u6210\uff0c\u5982\u4f55\u80af\u542c\uff0c\u731b\u7684\u62cd\u9a6c\uff0c\u4e00\u9f50\u524d\u51b2\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u767d\u8863\u5c11\u5e74\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u771f\u4e2a\u8981\u89c1\u89c1\u5389\u5bb3\uff0c\u624d\u80af\u7f62\u624b\u5417\uff1f\u201d\u53f3\u624b \u500f\u5730\u4e00\u626c\uff0c\u53c8\u662f\u4e09\u9053\u6697\u8d64\u8272\u7684\u5149\u534e\u7535\u5c04\u98de\u6765\uff0c\u4e09\u4e2a\u767d\u6559\u5587\u561b\u4e3e\u6756\u4e00\u6321\uff0c\u5374\u90fd\u6ca1\u6709\u6321\u7740\uff0c\u90a3\u4e09 \u5339\u767d\u9a6c\u4e00\u9f50\u5636\u53eb\uff0c\u524d\u8db3\u4eba\u7acb\uff0c\u4e09\u4e2a\u5587\u561b\u5927\u53eb\u4e00\u58f0\uff0c\u4ece\u9a6c\u80cc\u4e0a\u4e00\u4e2a\u5012\u683d\u8471\u649e\u4e0b\u9a6c\u6765\uff01 \u3000\u3000\u9f99\u7075\u77eb\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u6765\u7684\u539f\u6765\u662f\u5929\u5c71\u6d3e\u7684\u9ad8\u624b\uff01\u201d\u773c\u89c1\u8fd9\u767d\u8863\u5c11\u5e74\u7684\u672c\u9886\u5c1a\u5728\u81ea \u5df1\u4e4b\u4e0a\uff0c\u8db3\u4ee5\u5236\u670d\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\uff0c\u5fc3\u4e2d\u653e\u5bbd\uff0c\u6b63\u5f85\u56de\u53bb\u6551\u5e94\uff0c\u659c\u523a\u91cc\u5ffd\u7136\u53c8\u6740\u51fa\u4e94\u4e2a\u5370\u5ea6 \u50e7\u4eba\uff0c\u6597\u5f8b\u9ed1\u8272\u7684\u50e7\u670d\uff0c\u4f7f\u7684\u4e5f\u90fd\u662f\u7af9\u6756\u3002\u539f\u6765\u8fd9\u4e94\u4e2a\u50e7\u4eba\uff0c\u4e43\u662f\u90a3\u82e6\u884c\u50e7\u5e26\u6765\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u9f99\u7075\u77eb\u529f\u529b\u867d\u9ad8\uff0c\u4f46\u4ee5\u4e00\u654c\u4e94\uff0c\u6025\u5207\u95f4\uff0c\u5374\u662f\u8131\u4e0d\u4e86\u8eab\u3002\u770b\u8fd9\u4e94\u4e2a\u50e7\u4eba\u7684\u7528\u610f\uff0c\u662f\u60f3\u628a \u4ed6\u62e6\u5728\u5916\u56f4\uff0c\u4e0d\u8ba9\u4ed6\u56de\u5230\u4e2d\u519b\u6551\u5e94\uff0c\u9f99\u7075\u77eb\u66f4\u662f\u8d77\u7591\u3002\u6597\u4e86\u51e0\u4e2a\u56de\u5408\uff0c\u53ea\u542c\u5f97\u767d\u8863\u5c11\u5e74\u5927\u58f0 \u5406\u559d\uff0c\u90a3\u4e09\u5339\u767d\u9a6c\uff0c\u5954\u56de\u9635\u4e2d\uff0c\u65e9\u5c31\u6709\u6e05\u519b\u4e0a\u524d\u63a5\u5e94\uff0c\u9a6c\u80cc\u4e0a\u6240\u9a6e\u7684\u91d1\u74f6\uff0c\u4ecd\u7136\u653e\u5728\u91d1\u4e1d\u78a7 \u7389\u7b3c\u4e2d\uff0c\u6ca1\u6709\u635f\u4f24\u4e00\u89d2\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u8dcc\u8dcc\u649e\u649e\u7684\u4ecd\u7d27\u8ddf\u5728\u5c11\u5e74\u540e\u9762\uff0c\u9532\u800c\u4e0d\u820d\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u56de\u5934\u7b11\u9053\uff1a\u201c\u5feb \u56de\u9752\u6d77\u53bb\u5427\uff0c\u4f60\u4eec\u90fd\u5df2\u4e2d\u4e86\u6211\u7684\u795e\u8292\uff0c\u56de\u53bb\u9759\u517b\u56db\u5341\u4e5d\u5929\uff0c\u6216\u8005\u8fd8\u6709\u53ef\u6cbb\uff0c\u4f60\u4eec\u6d3b\u547d\u8981\u7d27\uff0c \u8fd8\u7f20\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u4e09\u4e2a\u5587\u561b\u4e5f\u90fd\u77e5\u9053\u4e86\u4e2d\u4e86\u4ed6\u7684\u6697\u5668\uff0c\u53ef\u662f\u4ed6\u4eec\u90fd\u5f85\u7740\u6709\u4e00\u8eab\u6a2a\u7ec3\u8fc7\u7684\u91d1\u949f \u7f69\u7684\u529f\u592b\uff0c\u4ee5\u4e3a\u4e2d\u4e86\u6697\u5668\uff0c\u4ea6\u65e0\u5927\u788d\uff0c\u5f85\u4e8b\u8fc7\u4e4b\u540e\u518d\u5c06\u6697\u5668\u94b3\u51fa\uff0c\u4ea6\u672a\u4e3a\u8fdf\uff0c\u542c\u767d\u8863\u5c11\u5e74\u8bf4 \u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u90fd\u4e0d\u5927\u76f8\u4fe1\uff0c\u53c8\u6000\u7591\u8fd9\u6697\u5668\u6709\u6bd2\uff0c\u66f4\u60f3\u518d\u51b3\u96cc\u96c4\uff0c\u8feb\u767d\u8863\u5c11\u5e74\u53d6\u51fa\u89e3\u836f\uff0c\u6240\u4ee5 \u4ecd\u662f\u7d27\u8ffd\u4e0d\u820d\u3002 \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u8eab\u6cd5\u5feb\u6781\uff0c\u500f\u5373\u51b2\u5165\u9635\u4e2d\uff0c\u56f4\u7740\u9f99\u7075\u77eb\u7684\u4e94\u4e2a\u5370\u5ea6\u50e7\u4eba\u4e00\u9f50\u6563\u5f00\uff0c\u9f99\u7075\u77eb\u6b63 \u60f3\u4e0a\u524d\u9053\u8c22\uff0c\u5ffd\u542c\u5f97\u6b66\u6c0f\u5144\u5f1f\u5728\u9635\u4e2d\u5927\u53eb\u9053\uff0c\u201c\u7ecf\u5929\u5144\uff0c\u4f60\u6765\u5f97\u597d\u6781\u4e86\u3002\u90a3\u5339\u767d\u9a6c\u80cc\u4e0a\u9a6e\u7684 \u5c31\u662f\u91d1\u74f6\uff0c\u4f60\u5feb\u52a9\u6211\u4eec\u5c06\u91d1\u74f6\u5148\u62ff\u53bb\u5427\uff01\u201d\u9f99\u7075\u77eb\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u5c11\u5e74\u6bd4\u90a3\u5370\u5ea6\u82e6\u884c\u50e7 \u66f4\u4e3a\u53ef\u6015\uff0c\u82e5\u7136\u662f\u4ed6\u4f38\u624b\uff0c\u8c01\u4eba\u963b\u62e6\u5f97\u4f4f\uff1f \u3000\u3000\u53ea\u542c\u5f97\u90a3\u767d\u8863\u5c11\u5e74\u4e00\u7b11\u5e94\u9053\uff1a\u201c\u4e24\u4f4d\u6b66\u5927\u54e5\uff0c\u9ea6\u8001\u524d\u8f88\uff0c\u6211\u8981\u5411\u4f60\u4eec\u6c42\u4e00\u4e2a\u60c5\uff0c\u8bf7\u4f60\u4eec \u90fd\u6563\u53bb\uff0c\u5df2\u8ba9\u8fd9\u91d1\u74f6\u8fd0\u5230\u62c9\u8428\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5728\u573a\u7684\u5317\u4e94\u7701\u82f1\u8c6a\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u9ea6\u6c38\u660e\u6c14\u547c\u547c\u7684\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u8981\u66ff\u6e05 \u5ef7\u4fdd\u62a4\u8fd9\u4e2a\u91d1\u74f6\uff1f\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u8981\u4fdd\u62a4\u8fd9\u4e2a\u91d1\u74f6\uff01\u201d\u6b66\u5bb6\u5144\u5f1f\u53eb\u9053\uff1a\u201c\u7ecf \u5929\uff0c\u4f60\u4e3a\u6e05\u5ef7\u5c3d\u529b\uff0c\u6709\u4f55\u989c\u9762\u89c1\u4f60\u7236\u4eb2\u3002\u201d\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u5bb6\u7236\u7684\u610f\u601d\u3002\u6b66\u5927\u54e5\uff0c \u4f60\u4eec\u5148\u6563\u53bb\u5427\uff0c\u54b1\u4eec\u5728\u524d\u5c71\u76f8\u4f1a\uff0c\u6211\u518d\u5411\u4f60\u4eec\u89e3\u91ca\u3002\u201d\u6b66\u6c0f\u5144\u5f1f\u5927\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d\u5ce1 \u8c37\u7fa4\u8c6a\u60ca\u8be7\u4e4b\u6781\uff0c\u201c\u4ec0\u4e48\uff0c\u4ed6\u662f\u5510\u5927\u4fa0\u5510\u6653\u6f9c\u7684\u513f\u5b50\uff1f\u201d\u201c\u5510\u5927\u4fa0\u600e\u4f1a\u8ba9\u513f\u5b50\u4f5c\u671d\u5ef7\u7684\u9e70 \u72ac\uff0c\u83ab\u975e\u662f\u5047\u5192\u7684\u4e48\uff1f\u201d\u770b\u4ed6\u8eab\u624b\uff0c\u542c\u6b66\u6c0f\u5144\u5f1f\u7684\u79f0\u547c\uff0c\u7edd\u975e\u5047\u5192\uff0c\u5440\uff0c\u8fd9\u5c82\u4e0d\u6210\u4e86\u5510\u5bb6\u7684 \u4e0d\u8096\u5b50\u5417\uff1f\u201d\u5ce1\u8c37\u7fa4\u8c6a\u8bae\u8bae\u7eb7\u7eb7\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u90fd\u505c\u4e0b\u624b\u4e2d\u5175\u5203\uff0c\u9a7b\u9a6c\u800c\u89c2\u3002 \u3000\u3000\u8fd9\u767d\u8863\u5c11\u5e74\u6b63\u662f\u5929\u5c71\u6d3e\u638c\u95e8\u4eba\u5510\u6653\u6f9c\u7684\u72ec\u751f\u513f\u5b50\uff0c\u540d\u5524\u5510\u7ecf\u5929\uff0c\u5510\u6653\u6f9c\u548c\u6b66\u5bb6\u4e43\u662f\u4e16 \u4ea4\uff0c\u6b66\u6c0f\u5144\u5f1f\u5c11\u65f6\u4e5f\u66fe\u5230\u5929\u5c71\u53bb\u89c1\u8fc7\u5510\u6653\u6f9c\uff0c\u6545\u6b64\u4ed6\u4eec\u8ba4\u5f97\u3002\u4f46\u5510\u7ecf\u5929\u8fd8\u662f\u521d\u521d\u51fa\u9053\uff0c\u5176\u4ed6 \u7684\u524d\u8f88\u82f1\u96c4\uff0c\u5374\u8fd8\u672a\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u5fc3\u4e2d\u90fd\u5728\u60f3\u9053\uff1a\u201c\u5510\u5927\u4fa0\u5f53\u5e74\u548c\u7518\u51e4\u6c60\u5415\u56db\u5a18\u7b49\u7ed3\u4e3a\u597d \u53cb\uff0c\u5171\u6297\u671d\u5ef7\uff0c\u505a\u8fc7\u8bb8\u591a\u8f70\u8f70\u70c8\u70c8\u7684\u4e8b\uff0c\u4e09\u5973\u4fa0\u5165\u5bab\u6697\u6740\u96cd\u6b63\uff0c\u5176\u4e2d\u4e4b\u4e00\uff0c\u5c31\u662f\u5510\u6653\u6f9c\u7684\u59bb \u5b50\u3002\u4ed6\u7684\u7236\u6bcd\u8fde\u7687\u5e1d\u7684\u5934\u90fd\u6562\u6740\uff0c\u4ed6\u5374\u8981\u4fdd\u62a4\u91d1\u74f6\uff0c\u771f\u662f\u5c82\u6709\u6b64\u7406\uff01\u201d\u4f17\u82f1\u8c6a\u867d\u7136\u9707\u4e8e\u5929\u5c71 \u5251\u6cd5\u7684\u5a01\u540d\uff0c\u5374\u4e0d\u4ee5\u5510\u7ecf\u5929\u7684\u6240\u4f5c\u4e3a\u7136\uff0c\u4e2a\u4e2a\u6012\u76ee\u800c\u89c6\uff0c\u6709\u5982\u98ce\u66b4\u5c06\u81f3\uff0c\u55a7\u95f9\u987f\u6b47\uff0c\u53cd\u800c\u4e00 \u7247\u6c89\u5bc2\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u7126\u6625\u96f7\u4e00\u58f0\u9a87\u53eb\u3001\u9ec4\u9f99\u65d7\u4e0b\u7684\u671d\u5ef7\u519b\u5b98\u7eb7\u7eb7\u547c\u53eb\uff0c \u4e2d\u519b\u53c8\u4e71\u3002\u53ea\u89c1\u90a3\u624b\u6301\u7af9\u6756\u7684\u82e6\u884c\u50e7\uff0c\u6b63\u8d81\u7740\u4f17\u4eba\u6ce8\u89c6\u5510\u7ecf\u5929\u4e4b\u9645\u3002\u8df3\u4e0a\u4e00\u8f86\u9aa1\u8f66\uff0c\u9aa1\u8f66\u4e2d \u7a81\u7136\u98de\u51fa\u4e24\u67c4\u94c1\u6bd9\uff0c\u5411\u90a3\u50e7\u4eba\u8fce\u5934\u75db\u51fb\uff0c\u90a3\u50e7\u4eba\u7684\u7af9\u6756\u4e00\u4e2a\u76d8\u65cb\uff0c\u4e24\u67c4\u94c1\u69cc\u817e\u7a7a\u98de\u53bb\u3002\u90a3\u50e7 \u4eba\u5de6\u624b\u4e00\u4f38\u4e00\u7f29\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u5c06\u4e24\u4e2a\u519b\u5b98\u90fd\u63b7\u51fa\u8f66\u5916\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a \u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u53c8\u8dc3\u8d77\u6765\uff0c\u76f4\u6251\u9aa1\u8f66\uff0c\u82e6\u884c\u50e7\u6b64\u65f6\u5df2\u8df3\u51fa\u9aa1\u8f66\uff0c\u5411\u897f\u75be\u8dd1\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u52a8\u4f5c\u5feb\u5230\u6781\u70b9\uff0c\u5f85\u7126\u6625\u96f7\u548c\u4e00\u4f17\u519b\u5b98\u53d1\u89c9\u4e4b\u65f6\uff0c\u90a3\u50e7\u4eba\u5df2\u5954\u51fa\u4e86\u6570\u5341\u4e08\u4e4b\u9065\uff0c\u4ed6 \u7684\u7af9\u6756\u604d\u82e5\u7075\u86c7\u6641\u52a8\uff0c\u8fd1\u8eab\u516b\u5c3a\u4e4b\u5185\u7684\u5fa1\u6797\u519b\uff0c\u88ab\u4ed6\u7af9\u6756\u4e00\u6cbe\uff0c\u7acb\u5373\u5012\u5730\u3002\u9644\u8fd1\u5e76\u65e0\u9ad8\u624b\u62e6 \u622a\uff0c\u770b\u770b\u5c31\u8981\u88ab\u4ed6\u593a\u56f4\u800c\u51fa\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5927\u53eb\u4e00\u58f0\u4e0d\u597d\uff0c\u62d4\u5251\u4fbf\u8ffd\uff0c\u539f\u6765\u8fd9\u9aa1\u8f66\u867d\u4e0d\u8d77\u773c\uff0c\u9a7e\u8f66\u7684\u9aa1\u5b50\u53c8\u7626\u53c8\u5c0f\uff0c\u8f66\u4e0a\u7684 \u5e03\u84ec\u4ea6\u662f\u7834\u7834\u70c2\u70c2\uff0c\u770b\u6765\u4f3c\u662f\u4e00\u8f86\u7cae\u8f66\uff0c\u5176\u4e2d\u85cf\u7684\u5374\u662f\u771f\u6b63\u7684\u91d1\u672c\u5df4\u74f6\uff1b\u767d\u9a6c\u80cc\u4e0a\uff0c\u88c5\u5728\u91d1 \u4e1d\u78a7\u7389\u7b3c\u4e2d\u7684\u90a3\u4e2a\u53cd\u800c\u662f\u5047\u7684\u3002\u6240\u4ee5\u7126\u6625\u96f7\u521a\u624d\u867d\u7136\u5927\u547c\u5c0f\u53eb\uff0c\u4f5c\u52bf\u8ffd\u8d76\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\uff0c \u5176\u5b9e\u5374\u662f\u5df4\u4e0d\u5f97\u4ed6\u4eec\u79bb\u5f00\uff0c\u597d\u51cf\u5c11\u80a1\u52b2\u654c\u3002\u800c\u90a3\u82e6\u884c\u50e7\u7684\u4e94\u4e2a\u5f1f\u5b50\uff0c\u963b\u622a\u9f99\u7075\u77eb\u56de\u5230\u9635\u4e2d\uff0c \u7528\u610f\u4ea6\u5c31\u662f\u4fbf\u5229\u4ed6\u4eec\u7684\u5e08\u7236\u4e0b\u624b\u3002\u8fd9\u82e6\u884c\u50e7\u5e76\u4e0d\u662f\u666e\u901a\u50e7\u4eba\uff0c\u800c\u662f\u5370\u5ea6\u5580\u6797\u90a6\u7684\u6c57\u738b\u6240\u6d3e\u6765 \u7684\u745c\u4f3d\u9ad8\u624b\uff0c\u5580\u6797\u90a6\u4ea6\u6709\u63a7\u5236\u897f\u85cf\u7684\u91ce\u5fc3\uff0c\u6240\u4ee5\u4e5f\u5728\u56fe\u8c0b\u52ab\u593a\u91d1\u74f6\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u8def\u8ddf\u8e2a\uff0c\u65e9\u77e5\u4e2a\u4e2d\u79d8\u5bc6\uff0c\u4e00\u89c1\u91d1\u74f6\u88ab\u52ab\uff0c\u5927\u547c\u201c\u4e0d\u597d\uff01\u201d\u62d4\u5251\u4fbf\u8ffd\u3002\u9f99\u7075\u77eb\u4e5f \u98de\u6251\u53bb\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5370\u5ea6\u50e7\u4eba\u90a3\u4e94\u4e2a\u5f1f\u5b50\u5df2\u4f1a\u5728\u4e00\u8d77\uff0c\u4ed6\u4eec\u65e9\u6709\u51c6\u5907\uff0c\u4e00\u89c1\u5e08\u7236\u5f97 \u624b\u3001\u7acb\u5373\u963b\u622a\u4e24\u4e2a\u9ad8\u624b\uff0c\u8fd9\u4e94\u4e2a\u50e7\u4eba\u7684\u6b66\u529f\uff0c\u867d\u7136\u6bd4\u8d77\u5510\u9f99\u4e8c\u4eba\u76f8\u53bb\u751a\u8fdc\uff0c\u4f46\u4ed6\u95e8\u914d\u5408\u6709 \u7d20\uff0c\u6240\u7528\u7684\u5929\u7afa\u6756\u6cd5\uff0c\u53c8\u81ea\u6210\u4e00\u5bb6\uff0c\u5927\u6b8a\u4e2d\u571f\uff0c\u4e94\u6068\u7af9\u6756\uff0c\u9996\u5c3e\u76f8\u8fde\uff0c\u98ce\u8f66\u75be\u8f6c\uff0c\u7262\u7262\u5730\u7f20 \u7740\u5510\u3001\u9f99\u4e8c\u4eba\u7684\u957f\u5251\u3002\u5510\u7ecf\u5929\u6b63\u62df\u65bd\u7528\u6740\u624b\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u4e5f\u6298\u4e86\u56de\u6765\uff0c\u4e09\u67c4\u4e5d\u73af\u9521\u6756\uff0c \u54d7\u5587\u5587\u7684\u54cd\uff0c\u72c2\u547c\u75be\u626b\uff0c\u4e00\u62e5\u800c\u4e0a\u3002\u5370\u5ea6\u50e7\u4eba\u52a0\u4e0a\u767d\u6559\u5587\u561b\uff0c\u4ee5\u516b\u4eba\u4e4b\u529b\uff0c\u5408\u654c\u5510\u3001\u9f99\u4e8c \u4eba\uff0c\u56f4\u5f97\u4e2a\u98ce\u96e8\u4e0d\u900f\uff0c\u66f4\u662f\u4e0d\u6613\u51b2\u7834\uff0c\u8fd9\u65f6\u90a3\u82e6\u884c\u50e7\u6000\u7740\u91d1\u74f6\uff0c\u5df2\u95ef\u51fa\u5b98\u519b\u9635\u5916\u3002 \u3000\u3000\u5510\u7ecf\u5929\u559d\u9053\uff1a\u201c\u4f60\u4eec\u771f\u7684\u4e0d\u8981\u6027\u547d\u4e48\uff1f\u4f60\u4eec\u4e2d\u4e86\u6211\u7684\u5929\u5c71\u795e\u8292\uff0c\u5df2\u900f\u8fc7\u7a74\u9053\uff0c\u6df1\u5165\u4f53 \u5185\uff0c\u56de\u53bb\u8fd0\u529f\u9759\u517b\uff0c\u8fd8\u53ef\u6709\u6551\uff0c\u4f60\u4eec\u518d\u4e00\u62fc\u547d\uff0c\u795e\u8292\u94bb\u5fc3\uff0c\u90a3\u5c31\u7eb5\u6709\u7075\u4e39\u5999\u836f\uff0c\u4e5f\u96be\u8d77\u6b7b\u56de \u751f\u4e86\uff01\u201d \u3000\u3000\u4e09\u4e2a\u767d\u6559\u5587\u561b\u81ea\u6043\u5185\u529f\u6df1\u6e5b\uff0c\u4e0d\u4fe1\u5929\u5c71\u795e\u8292\u5982\u6b64\u5389\u5bb3\uff0c\u4ecd\u7136\u6325\u6756\u6025\u653b\u3002\u8fd9\u65f6\uff0c\u90a3\u5370\u5ea6\u82e6 \u884c\u50e7\u5df2\u5954\u51fa\u8c37\u53e3\uff0c\u8d70\u4e0a\u659c\u5761\uff0c\u4ed6\u8eab\u6cd5\u5feb\u6377\u4e4b\u6781\uff0c\u5feb\u9a6c\u4e5f\u8feb\u4e0d\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u82e6\u884c\u50e7\u4e00\u58f0\u957f\u5578\uff0c\u5c71\u8170\u53c8\u7a9c\u51fa\u4e94\u4e2a\u50e7\u4eba\uff0c\u539f\u6765\u4ed6\u6df1\u8c0b\u8fdc\u8651\uff0c\u52a1\u6c42\u4e00\u4e3e\u6210\u529f\uff0c\u5e26 \u4e86\u5341\u540d\u5f1f\u5b50\u524d\u6765\uff0c\u5206\u4e3a\u4e24\u62e8\uff0c\u4e94\u4eba\u5728\u9635\u4e2d\u6bbf\u540e\uff0c\u4e94\u4eba\u5728\u5c71\u8170\u63a5\u5e94\uff0c\u672c\u6765\u662f\u51c6\u5907\u5e94\u4ed8\u6e05\u5ef7\u7684\u516b \u5927\u9ad8\u624b\u7684\uff0c\u516b\u5927\u9ad8\u624b\u5df2\u88ab\u9ea6\u6c38\u660e\u5e26\u6765\u7684\u897f\u5317\u7fa4\u8c6a\u7f20\u4f4f\uff0c\u7adf\u65e0\u4e00\u4eba\u5728\u540e\u65b9\u9632\u536b\u3002 \u3000\u3000\u770b\u770b\u4ed6\u5c31\u8981\u5954\u5230\u534a\u5c71\uff0c\u7f20\u7740\u5510\u3001\u9f99\u4e8c\u4eba\u7684\u90a3\u4e94\u4e2a\u5370\u5ea6\u50e7\u4eba\u6b63\u60f3\u64a4\u9000\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u4ecd \u7136\u72c2\u653b\uff0c\u5510\u7ecf\u5929\u5927\u6025\uff0c\u4e00\u7b97\u65f6\u8fb0\u5df2\u5230\uff0c\u5ffd\u7684\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4eba\u80c1\u4e0b\u7684\u5929\u6f29\u7a74\u6709\u4f55\u5f02\u8c61\uff1f\u201d\u90a3 \u4e09\u4e2a\u767d\u6559\u5587\u561b\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c9\u80c1\u4e0b\u7a74\u9053\u9644\u8fd1\uff0c\u6709\u5982\u866b\u884c\u8681\u8d70\uff0c\u9ebb\u75d2\u96be\u7981\uff0c\u800c\u4e14\u8d8a\u6765\u8d8a\u5389\u5bb3\u3002 \u4e09\u4eba\u90fd\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u77e5\u9053\u8fd9\u662f\u6240\u4e2d\u7684\u6697\u5668\uff0c\u5728\u4f53\u5185\u987a\u7740\u6c14\u8840\u8fd0\u884c\u7684\u8ff9\u8c61\uff0c\u4e0d\u7981\u5927\u60ca\uff0c\u653b\u52bf\u4e00 \u7f13\u3002\u90a3\u4e94\u4e2a\u5370\u5ea6\u50e7\u4eba\u6b63\u5728\u6b32\u64a4\u672a\u64a4\u4e4b\u9645\uff0c\u5510\u7ecf\u5929\u5ffd\u5730\u4e00\u58f0\u5927\u559d\uff0c\u6e38\u9f99\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5251\u5149\u66b4 \u957f\uff0c\u5251\u82b1\u7f24\u7eb7\uff0c\u90a3\u4e94\u4e2a\u50e7\u4eba\u90fd\u89c9\u5f97\u5251\u5149\u662f\u5411\u7740\u81ea\u5df1\u523a\u6765\uff0c\u4e94\u6068\u7af9\u6756\u4e0d\u7531\u5f97\u4e0d\u62c6\u6563\u8ba1\u6765\u9632\u5fa1\uff0c \u53ea\u542c\u5f97\u5510\u7ecf\u5927\u53eb\u9053\uff1a\u2018\u8ba9\u4f60\u4eec\u4e5f\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u70b9\u7a74\u4e0b\u6cd5\uff0c\u5012\uff01\u201d\u6296\u624b\u4e4b\u95f4\uff0c\u5251\u5c16\u8fde\u523a\u4e94\u4e2a\u50e7 \u4eba\u7684\u7a74\u9053\uff0c\u4e94\u4e2a\u50e7\u4eba\u51e0\u4e4e\u662f\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u4e00\u9f50\u5012\u4e0b\uff0c\u90a3\u4e09\u4e2a\u767d\u6559\u5587\u561b\u5927\u60ca\uff0c\u6025\u5fd9\u95ea\u5f00\uff0c\u5510\u7ecf \u5929\u4e0e\u9f99\u7075\u77eb\u4e00\u63a0\u800c\u8fc7\u3002 \u3000\u3000\u628a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u82e6\u884c\u50e7\u5df2\u5954\u4e0a\u5c71\u8170\uff0c\u4e39\u8fbe\u5c71\u9ad8\u903e\u5e72\u4e08\uff0c\u5bfb\u5e38\u4eba\u722c\u4e0a\u534a\u5c71\uff0c\u4e5f\u8981\u534a\u65e5\uff0c \u5510\u3001\u9f99\u4e8c\u4eba\u5c1a\u672a\u8ffd\u5230\u5c71\u811a\uff0c\u8f7b\u529f\u518d\u9ad8\uff0c\u4e5f\u8d76\u4ed6\u4e0d\u4e0a\u4e86\uff0c\u6e05\u519b\u9635\u4e2d\u4e00\u7247\u54d7\u53eb\u60ca\u547c\u4e4b\u58f0\uff0c\u897f\u5317\u7fa4 \u96c4\u89c1\u91d1\u74f6\u88ab\u5f02\u90a6\u6240\u52ab\uff0c\u4e5f\u90fd\u6c14\u6cae\uff0c\u505c\u4e0b\u624b\u6765\uff0c\u5927\u5bb6\u90fd\u5411\u4e0a\u5934\u9065\u671b\u3002 \u3000\u3000\u6b63\u5728\u5927\u5bb6\u5c4f\u606f\u800c\u89c2\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u7434\u58f0\uff0c\u968f\u7740\u5929\u98ce\uff0c\u60a0\u626c\u98d8\u4e0b\uff0c\u5c71\u9ad8\u5165\u4e91\uff0c\u6c93\u4e0d\u89c1 \u4eba\uff0c\u7434\u58f0\u5374\u662f\u6e05\u8106\u53ef\u542c\uff0c\u4e09\u5343\u519b\u58eb\uff0c\u8fc7\u767e\u82f1\u8c6a\uff0c\u4e2a\u4e2a\u60ca\u6115\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u83ab\u975e\u8fd9\u662f\u4ed9\u5973\u5c71\u7075\uff0c \u72ec\u7acb\u5cf0\u5dc5\uff0c\u9f13\u7434\u89c2\u6218\u3002 \u3000\u3000\u5510\u7ecf\u5929\u66f4\u542c\u5f97\u5446\u4e86\uff0c\u7434\u58f0\u9690\u9690\uff0c\u5f39\u7684\u6b63\u662f\u300a\u8bd7\u7ecf\u300b\u4e2d\u201c\u5357\u6709\u4e54\u6728\uff0c\u4e0d\u53ef\u4f11\u601d\u3002\u201d\u90a3\u4e00\u7ae0 \u8bd7\uff0c\u8fd9\u662f\u51b0\u5ddd\u5929\u5973\u521d\u89c1\u4ed6\u65f6\uff0c\u4e3a\u4ed6\u6240\u5f39\u7684\u6b4c\u8bcd\u5475\uff01 \u3000\u3000\u53ea\u89c1\u767d\u96ea\u7691\u7691\u7684\u5cf0\u5dc5\uff0c\u500f\u5730\u73b0\u51fa\u4e00\u4e2a\u5c11\u5973\u8eab\u5f71\uff0c\u4e00\u8eab\u6e56\u6c34\u8272\u7684\u8863\u88f3\uff0c\u7cfb\u7740\u5927\u7ea2\u4e1d\u4e2d\uff0c\u9752 \u5c71\u7709\u9edb\uff0c\u7d20\u91cc\u7ea2\u5986\uff0c\u989c\u8272\u9c9c\u660e\uff0c\u96ea\u6620\u4ed9\u59ff\uff0c\u66f4\u663e\u5f97\u98ce\u534e\u7edd\u4ee3\uff01\u8fd9\u6b63\u662f\u4ed6\u65e5\u5915\u601d\u5ff5\u7684\u4eba\u2014\u2014\u51b0 \u5ddd\u5929\u5973\uff01\u8fd9\u5239\u90a3\u95f4\uff0c\u4e2a\u4e2a\u62ac\u5934\uff0c\u51dd\u7738\u6ce8\u671b\uff0c\u5ce1\u8c37\u4e4b\u4e2d\uff0c\u867d\u6709\u4e07\u9a6c\u5343\u519b\uff0c\u5374\u51e0\u4e4e\u8fde\u4e00\u6068\u9488\u8dcc\u5230 \u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6765\u5f97\u4e4b\u5feb\uff0c\u7b80\u76f4\u65e0\u6cd5\u5f62\u5bb9\uff0c\u5728\u4e0b\u9762\u770b\u4e0a\u53bb\uff0c\u4f46\u53ea\u89c1\u88d9\u5e26\u98d8\u98d8\u604d\u82e5\u9752\u5973\u7d20\u5a25\uff0c\u5fa1 \u98ce\u800c\u964d\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5df2\u5230\u4e86\u5c71\u8170\uff0c\u6070\u597d\u8fce\u7740\u90a3\u5370\u5ea6\u50e7\u4eba\u548c\u4ed6\u7684\u4e94\u4e2a\u5f1f\u5b50\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u50e7\u4eba\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u628a\u91d1\u74f6\u7559\u4e0b\u6765\uff0c\u8ba9\u4f60\u8fc7\u53bb\u3002\u201d\u8bf4 \u8bdd\u7684\u795e\u6c14\uff0c\u5c31\u50cf\u4e00\u4e2a\u5973\u738b\u5728\u9881\u5e03\u547d\u4ee4\uff0c\u58f0\u8c03\u867d\u662f\u67d4\u548c\uff0c\u5374\u6beb\u65e0\u53ef\u4ee5\u5546\u91cf\u4e4b\u4f59\u5730\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u50e7\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u628a\u624b\u4e00\u6325\uff0c\u516d\u6068\u7af9\u6756\uff0c\u500f\u5730\u540c\u65f6\u6253\u51fa\uff0c\u5370\u5ea6\u50e7\u4eba\u89c1\u4e86\u51b0\u5ddd\u5929\u5973\u8fd9 \u8eab\u8f7b\u529f\uff0c\u5df1\u77e5\u5979\u662f\u4e2a\u6700\u53ef\u6015\u7684\u52b2\u654c\uff0c\u6240\u4ee5\u4e00\u4e0b\u624b\u4fbf\u6307\u6325\u5f1f\u5b50\uff0c\u516d\u4ed7\u9f50\u98de\uff0c\u8fd9\u662f\u5929\u7afa\u6756\u6cd5\u4e2d\u7684 \u201c\u5927\u5929\u7f57\u201d\u6756\u9635\uff0c\u516d\u6756\u9f50\u51fa\uff0c\u7eb5\u6709\u4e09\u5934\u516d\u81c2\uff0c\u4e5f\u96be\u62db\u67b6\u3002 \u3000\u3000\u9f99\u7075\u77eb\u4e0e\u5510\u7ecf\u5929\u5e76\u7acb\u800c\u89c2\uff0c\u89c1\u6b64\u60c5\u666f\uff0c\u4e0d\u7531\u5f97\u60ca\u53eb\u4e00\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u72e0\u7684\u50e7\u4eba\uff0c\u4e00\u5e08\u4e94 \u5f92\uff0c\u7adf\u7136\u8054\u624b\u6765\u5bf9\u4ed8\u4e00\u4e2a\u5973\u5b50\u3002\u201d\u54ea\u77e5\u5fc3\u5ff5\u752b\u52a8\uff0c\u558a\u58f0\u672a\u6b47\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u8eab\u5f62\u4e00\u6643\uff0c\u53cc\u6307 \u75be\u5f39\uff0c\u987f\u65f6\u98de\u8d77\u4e00\u7247\u9a87\u53eb\u4e4b\u58f0\uff0c\u4e94\u6761\u9ed1\u5f71\uff0c\u5c31\u50cf\u8131\u4e86\u7ebf\u7684\u98ce\u7b5d\u4e00\u6837\uff0c\u81ea\u5c71\u5934\u98d8\u843d\u3002 \u3000\u3000\u539f\u6765\u51b0\u5ddd\u5929\u5973\u89c1\u4ed6\u4eec\u6765\u5f97\u51f6\u6076\uff0c\u5fc3\u5934\u751f\u6c14\uff0c\u7adf\u53d1\u51fa\u51b0\u5bab\u72ec\u6709\u5e76\u4e16\u65e0\u53cc\u7684\u6697\u5668\u2014\u2014\u51b0\u9b44\u795e \u5f39\uff0c\u5979\u7684\u529f\u529b\u6bd4\u5e7d\u840d\u9ad8\u51fa\u4e0d\u77e5\u591a\u5c11\u500d\uff0c\u6240\u4ee5\u540c\u662f\u4e00\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u51fb\u4e2d\u654c\u4eba\u4e4b\u65f6\uff0c\u5374\u662f\u5927\u4e0d\u76f8 \u540c\uff0c\u82e5\u7136\u662f\u5e7d\u840d\u6240\u53d1\uff0c\u4ee5\u5370\u5ea6\u50e7\u4eba\u90a3\u4e94\u5f1f\u5b50\u7684\u6b66\u529f\uff0c\u6700\u591a\u4e0d\u8fc7\u6253\u4e2a\u5bd2\u6218\uff0c\u8fd8\u53ef\u62b5\u5fa1\uff0c\u88ab\u51b0\u5ddd \u5929\u5973\u51fb\u4e2d\uff0c\u795e\u5f39\u5374\u900f\u8fc7\u7a74\u9053\uff0c\u5947\u5bd2\u4e4b\u6c14\uff0c\u767b\u65f6\u4ee4\u5f97\u4ed6\u4eec\u7684\u8840\u6db2\u4e5f\u51dd\u7ed3\u8d77\u6765\uff0c\u4e00\u4e2a\u7acb\u8db3\u4e0d\u7a33\uff0c \u8dcc\u4e0b\u5c71\u8c37\u3002 \u3000\u3000\u90a3\u82e6\u884c\u50e7\u4e2d\u4e86\u4e00\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u4ea6\u89c9\u5947\u5bd2\u4e4b\u6c14\uff0c\u523a\u4f53\u4fb5\u80a4\uff0c\u4f46\u4ed6\u7684\u745c\u4f3d\u6c14\u529f\uff0c\u5df2\u7ec3\u5230\u4e86\u7b2c \u4e03\u6bb5\u5883\u754c\uff0c\u662f\u5929\u7afa\u6709\u6570\u7684\u9ad8\u624b\uff0c\u867d\u89c9\u4e0d\u5999\uff0c\u8fd8\u53ef\u7981\u53d7\uff0c\u7af9\u6756\u6a2a\u98de\uff0c\u7adf\u4e0d\u6362\u62db\uff0c\u4ecd\u5411\u51b0\u5ddd\u5929\u5973 \u6253\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u4e00\u7b11\uff0c\u89e3\u4e0b\u675f\u8863\u7684\u7ef8\u5e26\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u90a3\u7ef8\u5e26\u77eb\u82e5\u6e38\u9f99\uff0c\u4e00\u4e0b\u5b50\u5c31\u5c06\u7af9\u6756 \u7f20\u7740\u3002\u82e6\u884c\u50e7\u6697\u8fd0\u5185\u529b\uff0c\u7adf\u89e3\u4e0d\u5f00\u5979\u7684\u62db\u6570\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u593a\u4e0d\u4e0b\u4ed6\u7684\u7af9\u6756\uff0c\u4e5f\u9887\u4e3a\u8be7\u5f02\uff0c\u5fae\u201c\u81c6\u201d\u4e00\u58f0\uff0c\u624b\u6307\u53c8\u5f39\u4e86\u4e24\u5f39\uff0c\u90a3\u82e6\u884c\u50e7\u7684 \u7af9\u4ed7\u88ab\u7ef8\u5e26\u7f20\u7740\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u80f8\u53e3\u7684\u201c\u6e38\u673a\u7a74\u201d\u548c\u8111\u540e\u7684\u201c\u5929\u67f1\u7a74\u201d\u53c8\u4e2d\u4e86\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c \u767b\u65f6\u8fde\u6253\u51e0\u4e2a\u51b7\u6218\uff0c\u6c14\u529f\u7684\u8fd0\u7528\uff0c\u5df2\u4e0d\u80fd\u968f\u5fc3\u6240\u6b32\u3002\u51b0\u5ddd\u5927\u5973\u53f1\u9053\uff1a\u201c\u8fd8\u4e0d\u670d\u8f93\u5417\uff01\u201d\u53f3\u624b \u62d4\u51fa\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u7565\u4e00\u6325\u52a8\uff0c\u53ea\u89c1\u4e00\u7247\u5bd2\u5149\uff0c\u4e00\u56e2\u51b7\u6c14\uff0c\u597d\u50cf\u8584\u971e\u8f7b\u7ee2\u4e00\u6837\uff0c\u5c06\u90a3 \u82e6\u884c\u50e7\u7b3c\u7f69\u5f53\u4e2d\u3002\u8fd9\u65f6\uff0c\u5c71\u8c37\u4e0b\u9762\uff0c\u9690\u9690\u4f20\u6765\u82e6\u884c\u50e7\u90a3\u4e94\u4e2a\u5f1f\u5b50\u7684\u547c\u53f7\u4e4b\u58f0\uff0c\u4e00\u542c\u4fbf\u77e5\u4ed6\u4eec \u6b63\u5728\u9003\u547d\u3002 \u3000\u3000\u90a3\u82e6\u884c\u50e7\u957f\u53f9\u4e00\u58f0\uff0c\u817e\u51fa\u5de6\u624b\uff0c\u81ea\u6000\u4e2d\u4e00\u63a2\uff0c\u4f46\u89c1\u5b9d\u6c14\u5916\u5ba3\uff0c\u5149\u8292\u56db\u5c04\uff0c\u9576\u7740\u5927\u7ea2\u5b9d\u77f3 \u7684\u91d1\u672c\u5df4\u74f6\u53d6\u4e86\u51fa\u6765\u3002\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u8fc7\u91d1\u74f6\uff0c\u7ef8\u5e26\u98d8\u5f00\uff0c\u653e\u677e\u7af9\u6756\uff0c\u8eab\u5f62\u4eba\u95f4\u7c3f\uff1a \u8ba9\u51fa\u8def\u6765\uff0c\u90a3\u82e6\u884c\u50e7\u6025\u5fd9\u62b1\u5934\u9f20\u7a9c\u800c\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u5c06\u5b9d\u5251\u63d2\u56de\u9798\u4e2d\uff1a\u6367\u7740\u91d1\u74f6\uff0c\u98d8\u7136\u800c\u4e0b\u3002 \u6e05\u519b\u62a4\u9001\u91d1\u74f6\u7684\u4e3b\u5e05\u548c\u7855\u4eb2\u738b\u6025\u5fd9\u4f20\u4ee4\uff0c\u628a\u540e\u961f\u6539\u4e3a\u524d\u961f\uff0c\u5206\u5175\u4e24\u7ffc\uff0c\u4e0a\u53bb\u5305\u56f4\u51b0\u5ddd\u5929\u5973\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u6b63\u5728\u548c\u90a3\u7ea2\u8863\u756a\u50e7\u6076\u6597\uff0c\u5ffd\u7136\u4e07\u9a6c\u65e0\u58f0\uff0c\u5343\u519b\u6c89\u5bc2\uff0c\u6218\u6597\u7adf\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u8fd9\u6b63\u662f\u51b0\u5ddd\u5929\u5973\u521d\u521d\u73b0\u8eab\u7684\u65f6\u5019\u3002\u5e7d\u840d\u62ac\u773c\u671b\u53bb\uff0c\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u72c2\u53eb\u9053\uff1a\u201c\u5929\u5b87\uff0c\u4f60\u770b \u770b\u662f\u8c01\u6765\u4e86\uff1f\u201d\u7ea2\u8863\u756a\u50e7\u4e5f\u4e0d\u7531\u81ea\u5df1\u7684\u56de\u5934\u4e00\u671b\uff0c\u8fd9\u4e00\u671b\u53ea\u5413\u5f97\u9b42\u9b44\u9f50\u98de\uff0c\u8033\u8fb9\u53ea\u542c\u5f97\u9648\u5929 \u5b87\u5927\u53eb\u201c\u51b0\u5ddd\u5929\u5973\u201d\u4e4b\u58f0\uff0c\u500f\u5730\u9752\u5149\u4e00\u95ea\uff0c\u9648\u5929\u5b87\u53e3\u4e2d\u5927\u53eb\uff0c\u624b\u5e95\u6beb\u4e0d\u653e\u677e\uff0c\u4e00\u62db\u201c\u51b0\u6cb3\u89e3 \u51bb\u201d\uff0c\u957f\u5251\u4e00\u5212\uff0c\u7ea2\u8863\u756a\u50e7\u51b7\u4e0d\u53ca\u9632\uff0c\u80f8\u53e3\u7ed9\u4ed6\u5212\u5f00\uff0c\u5e7d\u840d\u9053\uff1a\u201c\u53eb\u4f60\u8d70\u4f60\u4e0d\u8d70\uff0c\u73b0\u5728\u53ef\u8fdf \u4e86\uff01\u201d\u8865\u4e0a\u4e00\u5251\uff0c\u523a\u5165\u80f8\u87b3\uff0c\u90a3\u756a\u50e7\u72c2\u53eb\u4e00\u58f0\uff0c\u9c9c\u8840\u56db\u6e85\uff0c\u9648\u5929\u5b87\u4e00\u811a\u5c06\u4ed6\u5c38\u4f53\u8e22\u7ffb\uff0c\u62a5\u4e86 \u6740\u5e08\u4e4b\u4ec7\uff0c\u7acb\u5373\u62d6\u7740\u5e7d\u840d\uff0c\u5954\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u6e05\u519b\u6b63\u5206\u5175\u4e24\u7ffc\uff0c\u8981\u4e0a\u53bb\u5305\u56f4\u51b0\u5ddd\u5929\u5973\uff0c\u5317\u4e94\u7701\u7684\u82f1\u8c6a\uff0c\u4e5f\u7eb7\u7eb7\u62e5\u4e0a\u3002\u51b0\u5ddd\u5929\u5973\u624b \u6367\u91d1\u74f6\u98d8\u7136\u800c\u4e0b\uff0c\u770b\u770b\u5c31\u8981\u843d\u5230\u5c71\u811a\u3002 \u3000\u3000\u9f99\u7075\u77eb\u6309\u5251\u6b32\u52a8\uff0c\u5510\u7ecf\u5929\u6025\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5feb\u5feb\u6b62\u4f4f\u5b98\u5175\uff0c\u5f85\u6211\u4e0a\u524d\u63a5\u5979\u3002\u6211\u6599\u5979\u6ca1 \u6709\u6076\u610f\u3002\u201d\u9f99\u7075\u77eb\u534a\u4fe1\u534a\u7591\uff0c\u4ed6\u4ea6\u5df2\u8ba4\u51fa\uff0c\u51b0\u5ddd\u5929\u5973\u5c31\u662f\u76d7\u53bb\u4ed6\u8349\u62df\u7684\u201c\u8fce\u63a5\u91d1\u74f6\u8349\u6848\u201d\u7684 \u90a3\u4e2a\u795e\u79d8\u5973\u5b50\uff0c\u5fc3\u4e2d\u5b9e\u5728\u4e0d\u6562\u76f8\u4fe1\u5979\u4f1a\u6697\u52a9\u81ea\u5df1\uff0c\u4f46\u89c1\u5979\u5f97\u4e86\u91d1\u74f6\uff0c\u5374\u4e0d\u9003\u8d70\uff0c\u53cd\u800c\u4e0b\u6765\uff0c \u5fc3\u4e2d\u662f\u6349\u6478\u4e0d\u5b9a\u3002\u8fd9\u65f6\u516b\u5927\u9ad8\u624b\u5df2\u5954\u51fa\u9635\u4e2d\uff0c\u5de6\u53f3\u5305\u6284\u3002 \u3000\u3000\u5ffd\u89c1\u6b66\u6c0f\u5144\u5f1f\uff0c\u75be\u8d70\u5982\u98ce\uff0c\u62a2\u5728\u5927\u5185\u516b\u5927\u9ad8\u624b\u7684\u524d\u5934\u3002\u51b2\u51fa\u9635\u6765\uff0c\u540e\u9762\u8ddf\u7740\u7684\u5341\u591a\u4f4d\u897f \u5317\u9ed1\u9053\u82f1\u96c4\uff0c\u4e5f\u4e00\u6d8c\u800c\u4e0a\uff0c\u4e89\u5148\u8fce\u63a5\u51b0\u5ddd\u5929\u5973\u3002 \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u53ea\u9053\u51b0\u5ddd\u5929\u5973\u662f\u540c\u9053\u4e2d\u4eba\uff0c\u624b\u629a\u5251\u67c4\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5973\u4fa0\u62d4\u5200\u76f8\u52a9\uff0c \u8bf7\u5c06\u91d1\u672c\u5df4\u74f6\u4ea4\u4e0e\u6211\u5427\u3002\u54b1\u4eec\u5927\u529f\u544a\u6210\uff0c\u53ef\u4ee5\u968f\u5927\u961f\u64a4\u9000\u4e86\u3002\u201d\u5728\u6b66\u6c0f\u5144\u5f1f\uff0c\u539f\u662f\u4e00\u756a\u597d \u610f\uff0c\u4ed6\u4eec\u89c1\u6e05\u5ef7\u5927\u5185\u516b\u5927\u9ad8\u624b\uff0c\u90fd\u51c6\u5907\u56f4\u653b\u51b0\u5ddd\u5929\u5973\uff0c\u6015\u5979\u6000\u6709\u91d1\u74f6\uff0c\u76ee\u6807\u592a\u5927\uff0c\u4e0d\u6613\u9003 \u8131\uff0c\u6240\u4ee5\u5efa\u8bae\u5979\u4ea4\u4e86\u91d1\u74f6\uff0c\u597d\u63a9\u62a4\u5979\u4e00\u540c\u64a4\u9000\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u5176\u65f6\u6e05\u519b\u5df2\u5305\u6284\u800c\u4e0a\uff0c\u6b66\u6c0f\u5144\u5f1f\u6025\u9053\uff1a\u201c\u54b1\u4eec \u90fd\u662f\u6765\u593a\u53d6\u91d1\u74f6\u7684\u4e00\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u95f2\u8bdd\u4ee5\u540e\u518d\u53d9\u5427\u3002\u201d\u4f38\u624b\u5c31\u8981\u6765\u63a5\u91d1\u74f6\u3002\u51b0\u5ddd\u5929\u5973\u51b7\u51b7 \u8bf4\u9053\uff1a\u201c\u4f60\u95ea\u4e0d\u95ea\u5f00\u3002\u201d\u6479\u5730\u53cc\u6307\u4e00\u5f39\uff0c\u8fde\u53d1\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u6b66\u6c0f\u5144\u5f1f\u7a81\u611f\u5947\u5bd2\u900f\u9aa8\uff0c\u767b\u65f6 \u8dcc\u5012\u3002\u540e\u9762\u7684\u4f19\u4f34\u5927\u60ca\uff0c\u6025\u5fd9\u62a2\u4e0a\uff0c\u51b0\u5ddd\u5929\u5973\u53cc\u6307\u75be\u5f39\uff0c\u53c8\u5c06\u4e94\u516d\u4e2a\u4eba\u6253\u5012\uff0c\u4f59\u4eba\u6025\u907f\uff0c\u51b0 \u5ddd\u5929\u5973\u51b2\u5f00\u7f3a\u53e3\uff0c\u4e00\u63a0\u5373\u8fc7\u3002 \u3000\u3000\u9ea6\u6c38\u660e\u53c8\u60ca\u53c8\u6c14\uff0c\u6e05\u519b\u5c06\u9886\u559c\u51fa\u671b\u5916\uff0c\u60f3\u4e0d\u5230\u51b0\u5ddd\u5929\u5973\u5374\u662f\u7ad9\u5728\u4ed6\u4eec\u8fd9\u8fb9\u3002\u7126\u6625\u96f7\u4e00\u9a6c \u5f53\u524d\uff0c\u62b1\u7740\u72fc\u7259\u68d2\u5c31\u5728\u9a6c\u80cc\u4e0a\u5531\u4e86\u4e2a\u6697\uff0c\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u5973\u4fa0\u6df1\u660e\u5927\u4e49\uff0c\u52a9\u671d\u5ef7\u6740\u8d3c\uff0c\u593a\u56de\u91d1 \u74f6\uff0c\u8fd9\u529f\u52b3\u975e\u540c\u5c0f\u53ef\uff0c\u6211\u7126\u6625\u96f7\u6709\u793c\u4e86\u3002\u6211\u662f\u5185\u5ef7\u4f8d\u536b\u7edf\u9886\uff0c\u8bf7\u5c06\u91d1\u74f6\u4ea4\u4e0e\u6211\u5427\u3002\u201d\u4f38\u624b\u4e5f \u8981\u6765\u63a5\u91d1\u74f6\u3002\u51b0\u5ddd\u5929\u5973\u7709\u6bdb\u4e00\u626c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8c01\u7ba1\u4f60\u4ec0\u4e48\u7edf\u9886\u4e0d\u7edf\u9886\uff1f\u6211\u6ca1\u6709\u5de5\u592b\u4e0e\u4f60\u591a \u53d9\u865a\u793c\u7e41\u6587\u3002\u201d\u52df\u5730\u53c8\u662f\u53cc\u6307\u4e00\u5f39\uff0c\u7126\u6625\u96f7\u767b\u65f6\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u4ece\u9a6c\u80cc\u4e0a\u76f4\u63bc\u4e0b\u6765\u3002\u5927\u5185\u9ad8 \u624b\u9f50\u90fd\u5927\u60ca\uff0c\u6025\u6025\u4e0a\u524d\uff0c\u6709\u51e0\u4e2a\u62a2\u7740\u53bb\u6551\u62a4\u9996\u9886\uff0c\u6709\u51e0\u4e2a\u62a2\u7740\u53bb\u653b\u51fb\u51b0\u5ddd\u5929\u5973\uff0c\u51b0\u5ddd\u5929\u5973\u8fde \u8fde\u51b7\u7b11\uff0c\u53cc\u6307\u75be\u5f39\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5c06\u5927\u5185\u9ad8\u624b\u51fb\u5012\u4e00\u534a\u3002 \u3000\u3000\u6e05\u519b\u4e2a\u4e2a\u5403\u60ca\uff0c\u4eba\u4eba\u9519\u6115\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u7b11\u9765\u751f\u6625\uff0c\u5df2\u662f\u8feb\u8fd1\u9635\u524d\uff0c\u60f3\u4e0d\u5230\u8fd9\u6837\u4e00\u4f4d\u7f8e \u82e5\u5929\u4ed9\u7684\u5c0f\u5a07\u5a18\uff0c\u624b\u5e95\u5374\u662f\u5982\u6b64\u72e0\u8fa3\uff0c\u800c\u4e14\u51b0\u5ddd\u5929\u5973\u81ea\u7136\u5e26\u7740\u4e00\u80a1\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u5c0a\u8d35\u795e \u60c5\u3002\u800c\u5bf9\u7740\u8fd9\u6837\u4e00\u4f4d\u8c8c\u7f8e\u5982\u5316\u6b66\u529f\u53c8\u6df1\u4e0d\u53ef\u6d4b\u7684\u5973\u5b50\uff0c\u5f13\u7bad\u624b\u7adf\u7136\u4e0d\u6562\u653e\u7bad\uff0c\u94a9\u9570\u624b\u4e5f\u4e3e\u4e0d \u8d77\u94a9\u9570\u67aa\u3002 \u3000\u3000\u9ea6\u6c38\u660e\u662f\u6b63\u5728\u53c8\u60ca\u53c8\u559c\uff0c\u5ffd\u89c1\u5510\u7ecf\u5929\u4ece\u4eba\u4e1b\u4e2d\u94bb\u51fa\uff0c\u62a2\u5230\u81ea\u5df1\u8eab\u8fb9\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u9ea6\u5927 \u4fa0\uff0c\u4eca\u65e5\u7edd\u4e0d\u80fd\u593a\u53d6\u91d1\u74f6\u4e86\uff0c\u8bf7\u9ea6\u5927\u4fa0\u4e0b\u4ee4\uff0c\u53eb\u4f17\u5bb6\u5144\u5f1f\u64a4\u9000\u3002\u201d\u9ea6\u6c38\u660e\u9053\uff1a\u201c\u54fc\uff0c\u60f3\u4e0d\u5230 \u4f60\u4e0e\u6e05\u5ef7\u4e00\u9f3b\u5b54\u51fa\u6c14\uff01\u201d\u4e3e\u62f3\u6b32\u51fb\uff0c\u5510\u7ecf\u5929\u4e09\u6307\u4e00\u6263\uff0c\u6309\u7740\u4ed6\u7684\u62f3\u5934\uff1a\u59dc\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\uff0c\u8ba9\u6e05\u5ef7\u4fdd\u6709\u897f\u85cf\uff0c\u603b\u80dc\u4e8e\u8ba9\u4e0e\u5f02\u90a6\u3002\u8fd9\u91d1\u74f6\u4e07\u4e07\u4e0d\u80fd\u52ab\u593a\uff01\u201d\u9ea6\u6c38 \u660e\u5fc3\u4e2d\u4e00\u6817\uff0c\u6479\u5730\u51b7\u6c57\u76f4\u6d41\uff0c\u5374\u9053\uff1a\u201c\u6b66\u6c0f\u5144\u5f1f\u4ed6\u4eec\u4e2d\u4e86\u90a3\u5973\u5b50\u7684\u90aa\u6076\u6697\u5668\u5462\uff0c\u6b64\u4ec7\u5c82\u53ef\u4e0d \u62a5\uff01\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u5305\u5728\u6211\u8eab\u4e0a\u7ed9\u4ed6\u4eec\u533b\u6cbb\u4fbf\u662f\uff0c\u5feb\u5feb\u64a4\u9000\uff0c\u5feb\u5feb\u64a4\u9000\uff01\u201d \u3000\u3000\u9ea6\u6c38\u660e\u7565\u4e00\u6c89\u541f\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u5df2\u53cd\u8986\u60f3\u4e86\u51e0\u8f6c\uff0c\u4ed6\u521d\u610f\u672c\u662f\u4e3a\u4e86\u4e0e\u6e05\u5ef7\u4f5c\u5bf9\uff0c\u624d \u52ab\u593a\u91d1\u74f6\uff0c\u60f3\u4e0d\u5230\u4e8b\u60c5\u5982\u6b64\u590d\u6742\uff0c\u5c3c\u6cca\u5c14\u4eba\u548c\u5370\u5ea6\u7684\u5580\u6797\u90a6\u6c57\u738b\u4e5f\u90fd\u4e3a\u4e86\u60f3\u67d3\u6307\u897f\u85cf\u800c\u6765\u52ab \u593a\u91d1\u74f6\uff0c\u5510\u7ecf\u5929\u7684\u201c\u5b81\u4e0e\u6e05\u5ba4\uff0c\u4e0d\u4e0e\u756a\u90a6\u3002\u201d\u8bf4\u6765\u786e\u662f\u9053\u7406\u3002\u4e8e\u662f\u7565\u4e00\u6c89\u541f\uff0c\u66ae\u7136\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u6211\u4f9d\u4f60\u4fbf\u662f\u3002\u54b1\u4eec\u7b49\u4e0b\u5728\u524d\u5c71\u76f8\u89c1\uff01\u201d\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5317\u4e94\u7701\u82f1\u8c6a\u6276\u7740\u6b66\u6c0f\u5144\u5f1f\u7b49\u53d7\u4f24\u7684 \u4eba\u90fd\u5411\u524d\u5c71\u64a4\u9000\u3002 \u3000\u3000\u5728\u5510\u7ecf\u5929\u529d\u9ea6\u6c38\u660e\u4e4b\u65f6\uff0c\u9f99\u7075\u77eb\u4e5f\u6b63\u5728\u529d\u62a4\u9001\u91d1\u74f6\u7684\u94a6\u5dee\u5927\u81e3\u548c\u7855\u4eb2\u738b\uff0c\u529d\u4ed6\u6b62\u4f4f\u5fa1\u6797 \u519b\uff0c\u8ba9\u51b0\u5ddd\u5929\u5973\u5165\u9635\u3002\u548c\u7855\u4eb2\u738b\u773c\u89c1\u51b0\u5ddd\u5929\u5973\u5982\u6b64\u5389\u5bb3\uff0c\u800c\u4e14\u91d1\u74f6\u53c8\u5728\u5979\u7684\u624b\u4e2d\uff0c\u7eb5\u7b97\u80fd\u628a \u51b0\u5ddd\u5929\u5973\u64d2\u6740\uff0c\u91d1\u74f6\u82e5\u6709\u635f\u574f\uff0c\u62a4\u9001\u91d1\u74f6\u7684\u5b98\u5458\uff0c\u53ea\u6050\u4e2a\u4e2a\u90fd\u8981\u95ee\u65a9\uff0c\u5982\u6b64\u4e00\u60f3\uff0c\u4e5f\u662f\u51b7\u6c57 \u76f4\u6d41\uff0c\u53ea\u597d\u542c\u4ece\u9f99\u7075\u77eb\u7684\u529d\u544a\uff0c\u4e0b\u4ee4\u6b62\u4f4f\u6e05\u519b\uff0c\u4e0d\u8bb8\u52a8\u624b\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4e8c\u4eba\u6742\u5728\u519b\u58eb\u4e4b\u4e2d\uff0c\u6324\u5230\u524d\u9762\uff0c\u5ffd\u89c1\u6e05\u519b\u524d\u7ffc\uff0c\u4e24\u9762\u6563\u5f00\uff0c\u8ba9\u51fa\u4e00\u6761\u901a \u9053\uff0c\u7adf\u8ba9\u51b0\u5ddd\u5929\u5973\u4ece\u5bb9\u8d70\u8fdb\uff0c\u4e0d\u7981\u5927\u4e3a\u8be7\u5f02\uff0c\u5bf9\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u770b\u8fd9\u6a21\u6837\uff0c\u771f\u50cf\u8fce\u4ee5\u516c\u4e3b\u4e4b\u793c \u5462\uff01\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5979\u672c\u6765\u662f\u516c\u4e3b\u561b\uff0c\u54a6\uff0c\u5979\u597d\u50cf\u662f\u5728\u627e\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u624b\u6367\u91d1\u74f6\uff0c\u795e\u6c14\u5e84\u4e25\u4e4b\u6781\uff0c\u5728\u5343\u519b\u4e07\u9a6c\u7684\u5305\u56f4\u4e4b\u4e0b\uff0c\u4ece\u5bb9\u4e3e\u6b65\uff0c\u7f13\u7f13\u884c \u6765\uff0c\u7f8e\u76ee\u6d41\u76fc\uff0c\u660e\u8273\u7167\u4eba\uff0c\u88ab\u5979\u773c\u5149\u626b\u7740\u7684\u4eba\uff0c\u90fd\u89c9\u5f97\u795e\u6447\u76ee\u7729\uff0c\u4e0d\u6562\u4ef0\u89c6\u3002\u5ffd\u89c1\u5979\u5728\u9635\u4e2d \u505c\u4e86\u4e0b\u6765\uff0c\u773c\u5149\u6ce8\u89c6\u5230\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\uff0c\u9648\u5929\u5b87\u8ddf\u7740\u5979\u7684\u773c\u5149\u671b\u53bb\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\uff0c\u6084\u58f0\u5bf9\u5e7d \u840d\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5979\u662f\u627e\u4ed6\uff01\u201d\u5e7d\u840d\u9053\uff1a\u201c\u8c01\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5c31\u662f\u90a3\u767d\u8863\u5c11\u5e74\uff01\u201d\u8fd9\u65f6\u5e7d\u840d \u4e5f\u770b\u89c1\u4e86\uff0c\u51b0\u5ddd\u592d\u5973\u8ddd\u79bb\u5979\u4e0d\u8fc7\u767e\u6765\u6b65\uff0c\u5979\u51e0\u4e4e\u8981\u53eb\u51fa\u58f0\u6765\uff0c\u4f46\u5ce1\u8c37\u4e2d\u9759\u6084\u6084\u7684\uff0c\u6570\u5343\u519b\u58eb \u90fd\u5728\u51dd\u795e\u89c2\u671b\uff0c\u5e7d\u840d\u88ab\u8fd9\u6c14\u6c1b\u5413\u5f97\u895f\u4e0d\u6562\u58f0\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u679c\u7136\u5728\u8fd9\u91cc\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u4e5f\u7ec8\u4e8e \u4e0b\u5c71\u4e86\u3002\u201d\u4e24\u4eba\u773c\u5149\u78b0\u5728\u4e00\u8d77\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u7981\u8138\u6cdb\u7ea2\u6f6e\uff0c\u5510\u7ecf\u5929\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6127\u65e0\u4f73\u53e5\u916c\u77e5 \u5df1\uff0c\u559c\u89c1\u91d1\u74f6\u5386\u52ab\u56de\u3002\u4eca\u6b21\u4f60\u6168\u7136\u76f8\u52a9\uff0c\u4e0d\u53ea\u6211\u591a\u8c22\u4f60\uff0c\u8fd9\u91cc\u7684\u4eba\u548c\u897f\u85cf\u7684\u5b98\u5458\uff0c\u90fd\u8981\u591a\u8c22 \u4f60\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u7b11\u4e86\u4e00\u7b11\uff0c\u82e5\u65e0\u5176\u4e8b\u5730\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u91d1\u74f6\u4e0e\u6211\u6709\u4ec0\u4e48\u76f8\u5e72\uff1f\u6211\u53c8\u4e0d\u662f\u66ff \u4ed6\u4eec\u53bb\u593a\u91d1\u74f6\uff0c\u8c01\u8981\u4ed6\u4eec\u591a\u8c22\u4e86\u3002\u8fd9\u91d1\u74f6\u6709\u4ec0\u4e48\u5b9d\u8d1d\uff0c\u503c\u5f97\u4f60\u4e89\u6211\u593a\uff1f\u6211\u624d\u4e0d\u8981\u5462\uff01\u4f60\u66fe\u66ff \u6211\u7684\u51b0\u5bab\u98ce\u666f\uff0c\u9898\u8fc7\u51e0\u9996\u4f73\u8054\uff0c\u6211\u77e5\u9053\u4f60\u60f3\u8981\u8fd9\u91d1\u74f6\uff0c\u73b0\u5728\u6211\u5c31\u5c06\u8fd9\u91d1\u74f6\u9001\u4e0e\u4f60\u4f5c\u4e3a\u7b14\u8d44\uff0c \u4ee5\u540e\u54b1\u4eec\u8c01\u4e5f\u4e0d\u6b20\u8c01\u7684\uff0c\u4f60\u4e5f\u5c31\u4e0d\u5fc5\u518d\u6765\u7ea0\u7f20\u6211\u4e86\u3002\u201d\u5510\u7ecf\u5929\u4e00\u7b11\u63a5\u8fc7\u91d1\u74f6\uff0c\u5ffd\u9053\uff0c\u201c\u4f60\u5fd8 \u8bb0\u4e00\u4ef6\u4e8b\uff0c\u54b1\u4eec\u90a3\u65e5\u7ea6\u597d\u5728\u51b0\u5cf0\u4e0b\u9762\u6bd4\u5251\uff0c\u8fd8\u6ca1\u6709\u6bd4\u6210\u5462\uff01\u201d\u51b0\u5ddd\u5929\u5973\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u4f60 \u8fd8\u60f3\u4e0e\u6211\u6bd4\u5251\u5417\uff1f\u597d\uff0c\u90a3\u4f60\u4eca\u591c\u4e09\u66f4\u518d\u5230\u8fd9\u5c71\u4e0a\u6765\u5427\u3002\u201d\u773c\u5149\u4e00\u77a5\uff0c\u770b\u89c1\u4e86\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4e8c \u4eba\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9887\u611f\u610f\u5916\uff0c\u62db\u4e00\u62db\u624b\uff0c\u5c06\u4e8c\u4eba\u5524\u5230\u8ddf\u524d\uff0c\u95ee\u5e7d\u840d\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e5f\u5230\u8fd9\u513f\uff1f\u201d\u5e7d \u840d\u9053\uff1a\u201c\u90a3\u65e5\u6211\u548c\u8c22\u59d1\u59d1\u53bb\u91c7\u8349\u836f\uff0c\u51b0\u5cf0\u5012\u584c\uff0c\u706b\u5c71\u7206\u53d1\uff0c\u7194\u5ca9\u963b\u8def\uff0c\u56de\u4e0d\u4e86\u5c71\uff0c\u6240\u4ee5\u6765 \u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u773c\u5149\u505c\u5728\u9648\u5929\u5b87\u7684\u9762\u4e0a\uff0c\u9648\u5929\u5b87\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff0c\u8f8d\u5685\u8bf4\u9053\uff1a \u201c\u6211\u672a\u5f97\u4f60\u7684\u91ca\u653e\uff0c\u53ea\u56e0\u90a3\u65e5\u5730\u9707\uff0c\u4e0d\u5f97\u4e0d\u9003\u51fa\u6765\uff0c\u4f60\u8981\u5904\u7f5a\u4fbf\u5904\u7f5a\u5427\u3002\u5176\u4ed6\u7684\u4e8b\u95ee\u4f60\u7684\u4f8d \u5973\u4fbf\u77e5\u9053\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u5c0f\u4eba\u5b50\u5012\u5f88\u5014\u5f3a\uff0c\u6211\u8fd8\u771f\u6015\u4f60\u9003\u4e0d\u51fa\u6765\u5462\u3002\u4f60\u72af\u4e86 \u6211\u7684\u7981\u4ee4\uff0c\u672c\u8be5\u7ec8\u8eab\u88ab\u56da\uff0c\u4f46\u7ecf\u8fc7\u8fd9\u573a\u5927\u96be\uff0c\u7b49\u5982\u6b7b\u4e86\u4e00\u6b21\uff0c\u4e5f\u53ef\u4ee5\u4f5c\u62b5\u4e86\u3002\u5f80\u4e8b\u4e00\u7b14\u52fe \u9500\uff0c\u4f60\u81ea\u53bb\u5427\u3002\u201d\u53eb\u5e7d\u840d\u9053\uff1a\u201c\u4f60\u4e5f\u53ef\u4ee5\u8ddf\u6211\u56de\u5c71\u4e86\u3002\u201d\u5e7d\u840d\u5fc3\u5934\u4e00\u9707\uff0c\u5979\u4e0b\u5c71\u4ee5\u6765\uff0c\u65e0\u62d8 \u65e0\u675f\uff0c\u6b63\u81ea\u73a9\u5f97\u9ad8\u5174\uff0c\u5c24\u5176\u5728\u89c1\u4e86\u9648\u5929\u5b87\u4e4b\u540e\uff0c\u4e00\u8def\u540c\u884c\uff0c\u751a\u4e3a\u76f8\u5f97\uff0c\u66f4\u820d\u4e0d\u5f97\u5206\u5f00\uff0c\u4f46\u4e3b \u4eba\u6709\u547d\uff0c\u5c82\u6562\u4e0d\u9075\uff0c\u53ea\u597d\u4f4e\u4e0b\u5934\u6765\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u77a7\u5728\u773c\u91cc\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4ea4\u4e86\u91d1\u74f6\uff0c\u643a\u4e86\u5e7d\u840d\uff0c\u6b63\u60f3\u8f6c\u8eab\uff0c\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u6025\u4e0d\u53ca\u5f85\uff0c\u5c31\u60f3\u5728\u8fd9\u5730\u65b9\u4e0e\u6211\u6bd4\u5251\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u6bd4\u5251\uff0c\u4f60\u7684\u51b0 \u9b44\u795e\u5f39\u592a\u5389\u5bb3\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u751a\u662f\u5f97\u610f\uff0c\u9053\uff1a\u201c\u4f60\u6015\u6211\u7684\u51b0\u9b44\u795e\u5f39\uff0c\u6211\u4e0d\u7528\u5b83\u5c31\u662f\u3002\u201d\u5510\u5929 \u7ecf\u9053\uff1a\u201c\u4f60\u7528\u51b0\u9b44\u795e\u5f39\u6253\u4f24\u4e86\u6211\u7684\u8bb8\u591a\u670b\u53cb\uff0c\u8bf7\u4f60\u9001\u4e00\u4e9b\u89e3\u836f\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u539f\u6765\u5982 \u6b64\uff0c\u597d\u5427\uff0c\u8fd9\u89e3\u836f\u7ed9\u4f60\u4fbf\u662f\u3002\u201d\u5510\u7ecf\u5929\u63a5\u8fc7\u89e3\u836f\uff0c\u957f\u63d6\u4f5c\u8c22\uff0c\u51b0\u5ddd\u5929\u5973\u597d\u50cf\u81ea\u8a00\u81ea\u8bed\uff0c\u53c8\u597d \u50cf\u5bf9\u5e7d\u840d\u8bf4\u9053\uff1a\u201c\u4e16\u4fd7\u4e4b\u4eba\uff0c\u5c31\u662f\u5982\u6b64\u7f57\u55e6\u8ba8\u538c\u3002\u201d\u5510\u7ecf\u5929\u715e\u6709\u4ecb\u4e8b\u7684\u8bf4\u9053\uff1a\u201c\u6211\u518d\u7f57\u55e6\u4e00 \u6b21\uff0c\u4eca\u665a\u4e4b\u7ea6\uff0c\u4e0d\u8981\u5fd8\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u88ab\u4ed6\u9017\u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u643a\u4e86\u5e7d\u840d\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u961f\u4f0d\u4e2d\u5ffd\u7136\u6324\u51fa\u516d\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u8d70\u5230\u51b0\u5ddd\u5929\u5973\u9762\u524d\uff0c\u4e00\u9f50\u8dea\u4e0b\uff0c\u53cc\u624b\u642d\u5728\u5934\u9876\uff0c\u53e3\u4e2d \u54fa\u54fa\u6709\u8f9e\uff0c\u72b6\u82e5\u7977\u544a\u3002\u548c\u7855\u4eb2\u738b\u751a\u4e3a\u5947\u602a\uff0c\u95ee\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u756a\u8d3c\u8981\u4e0d\u8981\u6346\u7f1a\u8d77\u6765\uff1f\u201d \u9f99\u7075\u77eb\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\u90fd\u8ba9\u51b0\u5ddd\u5929\u5973\u5904\u7f6e\uff0c\u5426\u5219\u6709\u53d8\u3002\u201d\u548c\u7855\u4eb2\u738b\u867d\u89c9\u6b64\u8bdd\u4ee4\u4ed6\u4e0d\u5927\u8212\u670d\uff0c \u4f46\u5f97\u56de\u91d1\u74f6\uff0c\u5df2\u662f\u4e07\u5e78\uff0c\u4e5f\u5c31\u4e0d\u6562\u591a\u7ba1\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u53eb\u505a\u51b0\u5ddd\u5929\u5973\u4e48\uff1f\u540d\u5b57\u771f\u662f\u5947 \u7279\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7528\u5c3c\u6cca\u5c14\u8bdd\u4e0e\u90a3\u51e0\u4e2a\u6b66\u58eb\u8c08\u8bdd\uff0c\u5728\u573a\u7684\u4eba\uff0c\u9664\u4e86\u9f99\u7075\u77eb\u3001\u5510\u7ecf\u5929\u4e0e\u5e7d\u840d\u5916\uff0c\u5176 \u4f59\u65e0\u4eba\u61c2\u5f97\u3002\u53ea\u542c\u5f97\u90a3\u51e0\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4f17\u53e3\u4e00\u540c\uff0c\u90fd\u662f\u529d\u51b0\u5ddd\u5929\u5973\u53e3\u56fd\u3002\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u7684\u8bdd\u4ece\u4e0d\u66f4\u6539\uff0c\u4f60\u4eec\u56de\u53bb\u544a\u8bc9\u5927\u6c57\uff0c\u53eb\u4ed6\u591a\u8bfb\u6c49\u4eba\u7684\u5723\u8d24\u4e4b\u4e66\uff0c\u597d\u751f\u6cbb\u7406\u56fd \u4e8b\u3002\u201d\u90a3\u51e0\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e0d\u6562\u4f5c\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7684\u56fd\u5e08\u5462\uff1f\u201d\u6b66\u58eb\u9053\uff1a\u201c\u4ed6\u5df2\u6b7b \u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ed6\u603b\u7231\u591a\u4e8b\uff0c\u65e0\u7aef\u7684\u6765\u62a2\u4ec0\u4e48\u91d1\u74f6\uff1f\u56de\u53bb\u544a\u8bc9\u4f60\u4eec\u7684\u5927\u6c57\uff0c\u6cbb\u7406\u597d\u81ea \u5df1\u7684\u56fd\u5bb6\u5df2\u591f\u4ed6\u8d39\u4e00\u751f\u7cbe\u529b\u4e86\uff0c\u4f55\u5fc5\u8fd8\u6d3e\u4eba\u5230\u897f\u85cf\u6765\u6363\u4e71\u3002\u4ed6\u7684\u56fd\u5e08\u6b7b\u4e86\u4e5f\u597d\uff0c\u7ed9\u4ed6\u4e00\u4e2a\u6559 \u8bad\u3002\u201d\u9f99\u7075\u77eb\u4e0e\u5510\u7ecf\u5929\u542c\u4e86\uff0c\u4e00\u60ca\u4e00\u559c\u3002 \u3000\u3000\u4ee4\u9f99\u7075\u77eb\u5403\u60ca\u7684\u662f\uff1a\u8fd9\u51b0\u5ddd\u5929\u5973\u4e0d\u4f46\u6b66\u529f\u5947\u5e7b\uff0c\u800c\u4e14\u8fd8\u662f\u5c3c\u6cca\u5c14\u7684\u516c\u4e3b\u3002\u5510\u7ecf\u5929\u559c\u7684\u5374 \u662f\uff1a\u51b0\u5ddd\u5929\u5973\u867d\u8bf4\u4e0d\u7406\u4e16\u4e8b\uff0c\u4f46\u770b\u5979\u6b64\u6b21\u6240\u4e3a\uff0c\u5374\u662f\u6697\u62a4\u4e2d\u56fd\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5490\u5490\u5b8c\u6bd5\uff0c\u628a\u624b\u4e00\u6325\uff0c\u90a3\u516d\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u9c7c\u8d2f\u9000\u51fa\uff0c\u6e05\u519b\u65e9\u5f97\u5230\u4e3b\u5e05\u547d\u4ee4\uff0c\u4e0d \u52a0\u963b\u62e6\uff0c\u8ba9\u4ed6\u4eec\u81ea\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u6602\u5934\u4e00\u7b11\uff0c\u5bf9\u5e7d\u840d\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u8be5\u8d70\u5566\uff01\u201d\u6570\u5343\u5fa1\u6797\u519b\u5c4f\u4f4f \u547c\u5438\uff0c\u76ee\u9001\u5979\u7f8e\u4e3d\u7684\u80cc\u5f71\uff0c\u8d70\u51fa\u9635\u4e2d\uff0c\u6068\u4e0d\u5f97\u80fd\u633d\u7559\u5979\u518d\u505c\u534a\u523b\u3002 \u3000\u3000\u9648\u5929\u5b87\u76ee\u9001\u5979\u4eec\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6101\u601d\u5982\u6f6e\uff0c\u53ea\u89c1\u5979\u4eec\u4e3b\u4ec6\u4e00\u5148\u4e00\u540e\uff0c\u7f13\u7f13\u8d70\u51fa\u5ce1\u8c37\uff0c \u5e7d\u840d\u5ffd\u5730\u56de\u7738\u4e00\u7b11\uff0c\u76ee\u5149\u548c\u9648\u5929\u5b87\u78b0\u4e2a\u6b63\u7740\u3002\u9648\u5929\u5b87\u5fc3\u5934\u9707\u8361\uff0c\u5ffd\u5730\u60f3\u8d77\u90a3\u85cf\u65cf\u7684\u795e\u79d8\u5c11\u5973 \u829d\u5a1c\uff0c\u829d\u5a1c\u59fb\u9759\u6df1\u6c89\uff0c\u6709\u5982\u5e7d\u8c37\u767e\u5408\uff0c\u800c\u5e7d\u840d\u5374\u987d\u76ae\u6d3b\u6cfc\uff0c\u6709\u5982\u590f\u65e5\u73ab\u7470\uff0c\u98ce\u60c5\u5404\u64c5\u80dc\u573a\uff0c \u9648\u5929\u5b87\u5fc3\u4e2d\u6697\u6697\u7977\u544a\uff1a\u4f46\u613f\u829d\u5a1c\u8fd8\u5728\u4eba\u95f4\u3002 \u3000\u3000\u5ffd\u89c1\u6e05\u519b\u4e00\u9635\u9a9a\u52a8\uff0c\u539f\u6765\u51b0\u5ddd\u5929\u5973\u4e0e\u5e7d\u840d\u5df2\u8d70\u4e0a\u534a\u5c71\uff0c\u80cc\u5f71\u5728\u6811\u6728\u4e1b\u4e2d\u5189\u5189\u800c\u6ca1\uff0c\u519b\u58eb \u4eec\u7eb7\u7eb7\u7ad9\u5728\u9a6c\u80cc\uff0c\u7eb5\u76ee\u9065\u671b\uff0c\u53d1\u51fa\u5567\u5567\u7684\u53f9\u606f\u4e4b\u58f0\u3002 \u3000\u3000\u548c\u7855\u4eb2\u738b\u677e\u4e86\u53e3\u6c14\uff0c\u4f20\u4ee4\u6574\u961f\uff0c\u5e76\u4eb2\u81ea\u6765\u89c1\u5510\u7ecf\u5929\u3002\u5510\u7ecf\u5929\u6de1\u6de1\u5730\u548c\u4ed6\u70b9\u4e00\u70b9\u5934\uff0c\u5374\u5c06 \u91d1\u74f6\u4ea4\u4e0e\u9f99\u7075\u77eb\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u751f\u4fdd\u62a4\uff0c\u4e0d\u8981\u518d\u5931\u53bb\u4e86\u3002\u201d\u9f99\u7075\u77eb\u5c06\u91d1\u74f6\u4ea4\u4e0e\u548c\u7855\u4eb2\u738b\uff0c \u5b89\u7f6e\u59a5\u540e\uff0c\u548c\u7855\u4eb2\u738b\u7709\u5f00\u773c\u7b11\uff0c\u5bf9\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4fa0\u58eb\u5c0a\u59d3\u5927\u540d\uff1f\u6b64\u6b21\u5efa\u7acb\u5927\u529f\uff0c\u5c0f\u738b\u81ea\u5f53\u7980 \u594f\u7687\u4e0a\uff0c\u5b9a\u6709\u539a\u8d4f\u3002\u201d\u5510\u7ecf\u5929\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c71\u91ce\u5c0f\u6c11\uff0c\u95f2\u6563\u60ef\u4e86\uff0c\u4e0d\u6c42\u529f\u540d\uff0c\u4e0d\u6c42\u5229\u7984\uff0c\u6709 \u751a\u539a\u8d4f\uff0c\u8bf7\u5206\u4e0e\u62a4\u9001\u91d1\u74f6\u7684\u5b98\u5175\u5427\u3002\u201d\u638f\u51fa\u51e0\u9897\u836f\u4e38\uff0c\u4ea4\u4e0e\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u4fbf\u662f\u89e3\u51b0\u9b44\u795e\u5f39 \u7684\u7075\u836f\uff0c\u5f00\u6c34\u670d\u4e86\uff0c\u4e0d\u51fa\u534a\u65e5\uff0c\u4fbf\u53ef\u75ca\u6108\u3002\u540e\u4f1a\u6709\u671f\uff0c\u6211\u5148\u8d70\u4e86\u3002\u201d\u548c\u7855\u4eb2\u738b\u89c1\u4ed6\u51b7\u6de1\u81ea \u5df1\uff0c\u53cd\u800c\u5bf9\u9f99\u7075\u77eb\u4eb2\u70ed\uff0c\u5fc3\u4e2d\u751a\u662f\u4e0d\u5feb\u3002 \u3000\u3000\u9f99\u7075\u77eb\u8fc8\u524d\u534a\u6b65\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u5510\u5144\u4e14\u6162\u3002\u201d\u5510\u7ecf\u5929\u56de\u5934\u8bf4\u9053\uff1a\u201c\u6709\u4f55\u89c1\u6559\uff1f\u201d\u9f99\u7075\u77eb \u6478\u51fa\u4e00\u4e2a\u4e94\u5bf8\u89c1\u65b9\u7684\u7389\u5323\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u8fd9\u4ef6\u4e1c\u897f\uff0c\u8bf7\u5510\u5144\u7559\u4e0b\u3002\u201d\u5510\u7ecf\u5927\u4f5b\u7136\u4e0d\u60a6\uff0c\u9053\uff1a \u201c\u96be\u9053\u6211\u662f\u8d2a\u56fe\u793c\u7269\uff0c\u624d\u6765\u62a4\u9001\u91d1\u74f6\u7684\u5417\uff1f\u201d\u9f99\u7075\u77eb\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u9001\u4f60\u7684\u793c\u7269\uff0c\u8fd9\u662f\u541b \u5bb6\u6545\u7269\uff0c\u56e0\u7f18\u65f6\u4f1a\uff0c\u843d\u5728\u6211\u7684\u624b\u4e2d\uff0c\u6211\u66ff\u4f60\u5bb6\u4fdd\u7ba1\u4e86\u51e0\u5341\u5e74\uff0c\u73b0\u5728\u5f52\u8fd8\u7ed9\u4f60\uff0c\u4f60\u82e5\u6709\u6240\u7591 \u60d1\uff0c\u56de\u53bb\u4e00\u95ee\u4ee4\u5c0a\uff0c\u4fbf\u5f53\u660e\u767d\u3002\u201d\u5510\u7ecf\u5929\u7591\u4e91\u5927\u8d77\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u542c\u4ed6\u6240\u8bf4\uff0c\u8fd9\u4ef6\u4e1c\u897f\u597d\u50cf \u975e\u6bd4\u5bfb\u5e38\uff0c\u6211\u7236\u4eb2\u7684\u6b66\u529f\uff0c\u5728\u5f53\u4eca\u4e4b\u4e16\uff0c\u6570\u4e00\u6570\u4e8c\uff0c\u600e\u4f1a\u6709\u4e1c\u897f\u843d\u5728\u4ed6\u7684\u624b\u4e0a\uff0c\u8fd9\u5012\u5947\u4e86\u3002 \u8fd9\u4f4d\u9f99\u8001\u4e09\uff0c\u6b66\u529f\u4e0d\u5728\u6211\u4e0b\uff0c\u884c\u5f84\u5947\u7279\uff0c\u5982\u6b64\u4eba\u624d\uff0c\u5374\u80af\u5728\u798f\u5eb7\u5b89\u5e10\u4e0b\u5f53\u4e00\u540d\u4e0d\u5927\u4e0d\u5c0f\u7684\u5b98 \u513f\uff0c\u96be\u9053\u662f\u4ed6\u5f53\u771f\u53e6\u6709\u6765\u5386\uff1f\u201d\u5f53\u4e0b\u767e\u601d\u4e0d\u89e3\uff0c\u53ea\u597d\u63a5\u8fc7\u90a3\u4e2a\u7389\u5323\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u7389\u5323\u85cf\u73cd\u4fa0\u58eb\u7591\u3002"}),showOnHomepage:!0},{name:"\u7434\u97f5\u5bc4\u6df1\u5fc3\u3000\u5c18\u7f18\u672a\u4e86 ",content:(0,s.jsx)(s.Fragment,{children:"\u8fb9\u57ce\u7aa5\u9690\u79d8\u3000\u65e7\u5730\u91cd\u6765 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u81ea\u7591\u60d1\uff0c\u5ffd\u542c\u5f97\u540e\u9762\u4e09\u58f0\u70ae\u54cd\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u961f\u4eba\u9a6c\uff0c\u7532\u80c4\u9c9c\u660e\uff0c\u7845\u65d7\u62db \u5c55\uff0c\u6392\u6210\u4e24\u5217\uff0c\u6709\u5982\u4e24\u9053\u957f\u86c7\uff0c\u5a49\u8712\u8d70\u5165\u5ce1\u8c37\uff0c\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fce\u63a5\u91d1\u74f6\u7684\u4e13\u4f7f\u5230\u5566\uff01\u201d\u5510\u7ecf \u5929\u9053\uff1a\u201c\u8c01\u662f\u4e13\u4f7f\uff1f\u201d\u9f99\u7075\u77eb\u4e00\u62db\u624b\uff0c\u9648\u5929\u5b87\u4ece\u4eba\u4e1b\u4e2d\u8d70\u51fa\uff0c\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4fbf\u662f\u4ed6\u7684\u7236 \u4eb2\u3002\u201d\u9648\u5929\u5b87\u8fc7\u6765\u4e0e\u5510\u7ecf\u5929\u76f8\u89c1\uff0c\u76f8\u8c22\u5f53\u65e5\u6551\u547d\u4e4b\u6069\u3002\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5927\u6709\u8fdb\u5c55 \u4e86\uff0c\u6709\u4f60\u548c\u9f99\u8001\u4e09\u5728\u6b64\uff0c\u5c06\u91d1\u74f6\u62a4\u5230\u62c9\u8428\uff0c\u5f53\u53ef\u65e0\u8651\u3002\u6211\u4e5f\u5e94\u8be5\u8d70\u4e86\u3002\u201d\u4e0e\u9f99\u7075\u77eb\u70b9\u5934\u9053 \u522b\uff0c\u98d8\u7136\u8d70\u51fa\u5ce1\u8c37\u3002\u548c\u7855\u4eb2\u738b\u751a\u4e3a\u4e0d\u5feb\uff0c\u4f46\u4ed6\u6b64\u65f6\u5fd9\u4e8e\u63a5\u5f85\u4e13\u4f7f\uff0c\u4e5f\u5c31\u4e0d\u518d\u7406\u4f1a\u5510\u7ecf\u5929\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5306\u5306\u8d76\u5230\u524d\u5c71\uff0c\u4e0e\u9ea6\u6c38\u660e\u7b49\u897f\u5317\u7fa4\u8c6a\u76f8\u4f1a\uff0c\u7fa4\u8c6a\u610f\u72b9\u6124\u6124\uff0c\u7eb7\u7eb7\u8d23\u95ee\uff0c\u5510\u7ecf\u5929\u518d \u4e09\u89e3\u91ca\uff0c\u8bf4\u660e\u4e0d\u80fd\u52ab\u593a\u91d1\u74f6\u4e4b\u7406\uff0c\u53c8\u53d6\u51fa\u89e3\u836f\uff0c\u5c06\u53d7\u4f24\u8bf8\u4eba\u6551\u6cbb\uff0c\u6b66\u6c0f\u5144\u5f1f\u6027\u60c5\u76f4\u7387\uff0c\u542c\u5510 \u7ecf\u5929\u8bf4\u5f97\u6709\u7406\uff0c\u8bf4\u9053\uff1a\u201c\u5510\u5144\u667a\u8651\u6df1\u8fdc\uff0c\u679c\u975e\u543e\u7b49\u6240\u53ca\u3002\u4eca\u65e5\u4e4b\u4e8b\uff0c\u543e\u7b49\u544a\u7f6a\u4e86\u3002\u201d\u5510\u7ecf\u5929 \u9053\uff1a\u201c\u7d2f\u4e24\u4f4d\u5144\u5f1f\u53d7\u4f24\uff0c\u6211\u624d\u8be5\u5411\u4f60\u4eec\u8d54\u7f6a\u3002\u201d\u6b66\u5bb6\u5144\u5f1f\u9053\uff1a\u201c\u600e\u80fd\u602a\u5230\u8001\u5144\u8eab\u4e0a\uff0c\u90a3\u5973\u5b50 \u662f\u5510\u5144\u7684\u4ec0\u4e48\u4eba\uff0c\u8981\u4f60\u66ff\u5979\u8d54\u7f6a\uff1f\u201d\u5510\u7ecf\u5929\u9762\u4e0a\u4e00\u7ea2\uff0c\u6b66\u6c0f\u5144\u5f1f\u53c8\u7b11\u9053\uff1a\u201c\u90a3\u5973\u5b50\u76f8\u52a9\u5510 \u5144\uff0c\u7528\u610f\u867d\u597d\uff0c\u624b\u5e95\u5374\u662f\u592a\u8fa3\uff0c\u4ed6\u65e5\u82e5\u6709\u673a\u7f18\uff0c\u6211\u4eec\u8fd8\u8981\u5411\u5979\u9886\u6559\u9886\u6559\u3002\u54b1\u4eec\u90fd\u662f\u5929\u5c71\u4e03\u5251 \u4e4b\u540e\uff0c\u5230\u65f6\u4f60\u53ef\u4e0d\u8bb8\u5e2e\u52a9\u5916\u4eba\u5475\uff01\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e24\u4f4d\u5144\u5f1f\u4f11\u8981\u53d6\u7b11\u3002\u201d\u5fc3\u4e2d\u5374\u6697\u81ea\u7b11\u9053\uff1a \u201c\u5927\u6c34\u51b2\u5012\u9f99\u738b\u5e99\uff0c\u672c\u6765\u90fd\u662f\u4e00\u5bb6\u4eba\u3002\u5979\u662f\u6842\u4ef2\u660e\u7684\u5b59\u5973\uff0c\u7b97\u8d77\u6765\u8fd8\u662f\u4f60\u4eec\u7684\u957f\u8f88\u54e9\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u522b\u8fc7\u897f\u5317\u7fa4\u8c6a\uff0c\u72ec\u81ea\u4e0a\u5c71\u3002\u60f3\u8d77\u9f99\u7075\u77eb\u4e4b\u4e8b\uff0c\u7591\u56e2\u6ee1\u8179\uff0c\u6253\u5f00\u90a3\u7389\u5323\u4e00\u770b\uff0c\u53ea\u89c1 \u91cc\u9762\u85cf\u7740\u4e00\u5757\u6c49\u7389\u3002\u78a7\u7eff\u6676\u83b9\uff0c\u4e2d\u592e\u4e00\u9053\u7ea2\u5370\uff0c\u523b\u7740\u51e0\u4e2a\u7bc6\u5b57\u9053\uff1a\u201c\u53d7\u547d\u4e8e\u5929\uff0c\u56fd\u8fd0\u4e45 \u957f\u3002\u201d\u5510\u7ecf\u5929\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u6c49\u7389\u7389\u8d28\u4f73\u7edd\uff0c\u4ef7\u503c\u8fde\u57ce\uff0c\u5e76\u4e0d\u51fa\u5947\uff0c\u770b\u8fd9\u51e0\u4e2a\u7bc6\u5b57\uff0c\u5206\u660e\u4e43\u662f \u5e1d\u738b\u4f69\u5e26\u4e4b\u7269\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u9f99\u8001\u4e09\u600e\u4e48\u8bf4\u6b64\u7389\u4e43\u662f\u6211\u5bb6\u7684\u4e1c\u897f\uff1f\u201d\u5ffd\u7136\u60f3\u8d77\u6bcd\u4eb2\uff08\u51af\u745b\uff09 \u548c\u4ed6\u8c08\u8fc7\u7684\u7236\u4eb2\u5f53\u5e74\u7684\u82f1\u96c4\u4e8b\u8ff9\uff0c\u8bf4\u5eb7\u7199\u7687\u5e1d\u66fe\u8d50\u8fc7\u7236\u4eb2\u4e00\u5757\u6c49\u7389\uff0c\u4e0d\u77e5\u662f\u5426\u5373\u662f\u6b64\u7269\uff1f \u3000\u3000\u4ed6\u54ea\u77e5\u9053\uff0c\u539f\u6765\u4ed6\u7684\u7236\u4eb2\u5510\u6653\u6f9c\u4e43\u662f\u5eb7\u7199\u7687\u5e1d\u7684\u79c1\u751f\u5b50\uff0c\u5510\u6653\u6f9c\u5f53\u5e74\u5165\u5bab\u89c1\u6bcd\u4e4b\u65f6\uff0c\u5eb7 \u7199\u66fe\u4ee5\u6b64\u7389\u76f8\u8d50\uff1b\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u4e0d\u613f\u513f\u5b50\u77e5\u9053\u6b64\u6bb5\u5bb6\u4e16\uff0c\u5f92\u589e\u70e6\u607c\uff0c\u56e0\u6b64\u5728\u8c08\u5230\u5f97\u7389\u7684\u7ecf\u8fc7 \u65f6\uff0c\u53ea\u63d0\u5230\u5f53\u65f6\u8bf8\u7687\u5b50\u593a\u4f4d\uff0c\u81ea\u5df1\u56e0\u7f18\u65f6\u4f1a\uff0c\u66fe\u5076\u7136\u6551\u8fc7\u5eb7\u7199\uff0c\u6545\u6b64\u5f97\u7389\uff0c\u5176\u4ed6\u7684\u4e8b\uff0c\u4e00\u6982 \u4e0d\u63d0\u3002\u540e\u6765\u5931\u7389\u7684\u7ecf\u8fc7\uff0c\u51af\u745b\u4e5f\u53ea\u662f\u6beb\u4e0d\u7ecf\u610f\u7684\u8c08\u8fc7\u4e00\u6b21\uff0c\u81f3\u4ee4\u5510\u7ecf\u5929\u4eca\u65e5\u89c1\u4e86\u6b64\u7389\uff0c\u5fc3\u4e2d \u66f4\u589e\u7591\u60d1\u3002\u5c24\u5176\u662f\u6b64\u7389\u4f55\u4ee5\u4f1a\u843d\u5230\u9f99\u7075\u77eb\u624b\u91cc\uff0c\u66f4\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u5510\u7ecf\u5929\u601d\u7d22\u4e0d\u660e\uff0c\u5fc3\u4e2d\u7b11\u9053\uff0c\u201c\u4ed6\u65e5\u89c1\u4e86\u7236\u6bcd\uff0c\u5fc5\u7136\u5206\u6653\uff0c\u4f55\u5fc5\u82e6\u601d\u3002\u201d\u5f53\u4e0b\u6536\u597d\u7389 \u5323\uff0c\u72ec\u81ea\u4e0a\u5c71\u3002 \u3000\u3000\u9ec4\u660f\u65e5\u843d\uff0c\u5c71\u95f4\u660e\u6708\u5347\u8d77\uff0c\u8fd9\u5c71\u4e0a\u4e5f\u6709\u51b0\u5ddd\uff0c\u867d\u7136\u6ca1\u6709\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u3002\u5929\u6e56\u9644\u8fd1\u7684\u5927\u51b0 \u5ddd\u4e4b\u58ee\u4e3d\uff0c\u4f46\u5a49\u8712\u6709\u5982\u94f6\u9f99\uff0c\u56f4\u7740\u5c71\u8170\uff0c\u4e00\u7247\u94f6\u767d\uff0c\u51b0\u5149\u6708\u8272\uff0c\u4e92\u76f8\u8f89\u6620\uff0c\u4e5f\u4f3c\u4eba\u5728\u5e7f\u5bd2\u6df1 \u5904\u3002\u5510\u7ecf\u5929\u5ff5\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u5fc3\u4e2d\u6005\u89e6\uff0c\u5fae\u5531\u541f\u9053\uff0c\u201c\u51b0\u6cb3\u6620\u6708\u5ae6\u5a25\u4e0b\uff0c\u5929\u5973\u98de\u82b1\u9a9a\u5ba2\u6765\u3002\u6211 \u4e00\u5b9a\u8981\u628a\u6708\u91cc\u7aef\u5a25\uff0c\u8bf7\u56de\u5c18\u4e16\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5c71\u5934\u4e0a\u4e00\u7247\u7434\u58f0\uff0c\u968f\u7740\u5929\u98ce\uff0c\u98d8\u4eba\u8033\u9f13\uff0c\u51b0\u5bab\u4f8d\u5973\u5e7d\u840d\u548c\u7740\u7434\u58f0\u6b4c\u9053\uff1a\u201c\u4e91\u6bcd\u5c4f \u98ce\u70db\u5f71\u6df1\uff0c\u957f\u6cb3\u6e10\u843d\u6653\u661f\u6c89\uff0c\u5ae6\u5a25\u5e94\u6094\u5077\u7075\u836f\uff0c\u78a7\u6d77\u9752\u5929\u591c\u591c\u5fc3\u3002\u201d\u8fd9\u662f\u5510\u8bd7\u4eba\u674e\u5546\u9690\u7684\u548f \u5ab3\u5a25\u8bd7\uff0c\u5510\u7ecf\u5929\u66fe\u7528\u8fc7\u8fd9\u8bd7\u7684\u6700\u540e\u4e00\u53e5\uff0c\u66ff\u5e7d\u840d\u4f5c\u5d4c\u540d\u8054\u3002\u8fd9\u65f6\u542c\u5979\u4eec\u4e3b\u4ec6\u5f39\u594f\u8fd9\u4e00\u9996\u8bd7\uff0c \u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u5e7f\u5bd2\u4ed9\u5b50\uff0c\u4e5f\u6bd5\u7adf\u601d\u51e1\u4e86\u3002\u201d\u5bfb\u89c5\u7434\u58f0\uff0c\u6500\u767b\u5cf0\u9876\u3002 \u3000\u3000\u6b63\u5728\u62ac\u5934\u8fdc\u671b\uff0c\u5ffd\u542c\u5f97\u79bb\u524d\u9762\u5341\u4f59\u4e08\u5904\uff0c\u5587\u5566\u5566\u7684\u4e00\u7247\u54cd\uff0c\u4e24\u4e2a\u4e00\u8eab\u9752\u8272\u7bad\u8863\u7684\u4eba\uff0c\u7adf \u5728\u8346\u68d8\u8305\u8349\u4e4b\u4e0a\uff0c\u5c55\u5f00\u4e86\u201c\u767b\u840d\u6e21\u6c34\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u6643\u773c\u95ee\u4fbf\u6ca1\u5165\u8349\u83bd\u5bc6\u8537\u6df1\u5904\u3002\u5510\u7ecf\u5929\u5fc3 \u4e2d\u5927\u9a87\uff1a\u8fd9\u4e24\u4eba\u7684\u8f7b\u529f\uff0c\u7adf\u7136\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff0c\u4e0d\u77e5\u4ed6\u4eec\u4f55\u4ee5\u8981\u5728\u6df1\u591c\u5230\u6b64\u8352\u5c71\u3002 \u3000\u3000\u5510\u7ecf\u5929\u501f\u7269\u969c\u5f62\uff0c\u6084\u6084\u63a9\u8fd1\uff0c\u9065\u89c1\u90a3\u4e24\u4eba\u8eb2\u5728\u4e71\u8349\u4e1b\u4e2d\u3002\u5510\u7ecf\u5929\u4e5f\u8eb2\u5728\u4e00\u5757\u77f3\u5934\u540e\u9762\uff0c \u5c4f\u606f\u547c\u5438\uff0c\u542c\u4ed6\u4eec\u8bf4\u9053\uff1a\u201c\u95fb\u8bf4\u4eca\u65e5\u5317\u4e94\u7701\u9ed1\u9053\u4e0a\u7684\u4eba\u7269\u90fd\u6765\u52ab\u593a\u91d1\u74f6\uff0c\u7126\u6625\u96f7\u4ed6\u4eec\u51e0\u4e4e\u5403 \u4e86\u8d25\u4ed7\uff0c\u5e78\u6709\u90a3\u9f99\u8001\u4e09\u5927\u663e\u795e\u901a\uff0c\u91d1\u74f6\u5931\u767e\u590d\u5f97\u3002\u5982\u6b64\u770b\u6765\uff0c\u90a3\u9f99\u8001\u4e09\u4e5f\u59d4\u5b9e\u4e0d\u53ef\u8f7b\u89c6 \u5440\uff01\u201d\u8fd9\u662f\u4e00\u4e2a\u82cd\u8001\u7684\u7537\u5b50\u58f0\u97f3\u3002\u5510\u7ecf\u5929\u6697\u81ea\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u662f\u77e5\u5176\u4e00\u4e0d\u77e5\u5176\u4e8c\uff0c\u6765\u52ab\u91d1\u74f6 \u7684\u5c82\u6b62\u5317\u4e94\u7701\u8fd9\u4e00\u5e72\u4eba\u7269\uff0c\u5370\u5ea6\u548c\u5c3c\u6cca\u5c14\u90fd\u6d3e\u6709\u4eba\u6765\u554a\uff1b\u82e5\u975e\u51b0\u5ddd\u5929\u5973\u3001\u91d1\u74f6\u65e9\u5c31\u88ab\u52ab\u5230\u5370 \u5ea6\u53bb\u4e86\u3002\u201d\u4f46\u542c\u4ed6\u7279\u522b\u8c08\u5230\u9f99\u7075\u77eb\uff0c\u5374\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u5973\u58f0\u7b54\u9053\uff1a\u201c\u9f99\u8001\u4e09\u6b66\u529f\u8d85\u5353\uff1b\u5374\u7518\u5fc3\u5728\u798f\u5eb7\u5b89\u5e10\u4e0b\uff0c\u5f53\u4e00\u540d\u53c2\u8d5e\uff0c\u6b64\u4e8b\u786e \u662f\u53ef\u7591\u3002\u602a\u4e0d\u5f97\u60e0\u603b\u7ba1\u7279\u522b\u8bf7\u6211\u4eec\u51fa\u6765\uff0c\u8981\u6478\u4e00\u6478\u4ed6\u7684\u2018\u6d77\u5e95\u2019\uff08\u6765\u5386\u5e95\u7ec6\u4e4b\u610f\uff09\u4e86\uff0c\u6562\u60c5 \u662f\u7687\u4e0a\u4e5f\u8d77\u4e86\u7591\u5fc3\u54e9\u3002\u201d\u5510\u7ecf\u5929\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5bf9\u7537\u5973\u662f\u6e05\u5bab\u65b0\u8058\u7684\u80fd\u624b\uff0c\u4ed6\u4eec\u6b66\u529f\u770b\u6765\u8fdc \u8fdc\u5728\u90a3\u516b\u5927\u9ad8\u624b\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u6b47\u4e86\u4e00\u6b47\u5c71\u9876\u4e0a\u7684\u7434\u58f0\u53c8\u8d77\u3001\u8fd9\u56de\u5f39\u7684\u5374\u662f\u82cf\u4e1c\u5761\u7684\u4e00\u9996\u5c0f\u4ee4\u535c\u7b97\u5b50\uff0c\u8bcd\u9053\uff1a \u3000\u3000\u7f3a\u6708\u6302\u758f\u6850\uff0c\u6f0f\u6d1e\u4eba\u521d\u9759\uff0c\u65f6\u89c1\u5e7d\u4eba\u72ec\u5f80\u6765\uff0c\u7f25\u7f08\u5b64\u9e3f\u5f71\u3002\u60ca\u76f4\u5374\u56de\u5934\uff0c\u6709\u6068\u65e0\u4eba\u7701\u3002 \u62e3\u5c3d\u5bd2\u679d\u4e0d\u80af\u6816\uff0c\u5bc2\u5bde\u6c99\u6d32\u51b7\u3002 \u3000\u3000\u8bcd\u610f\u5e7d\u6028\uff0c\u7434\u58f0\u51c4\u8ff7\uff0c\u5510\u7ecf\u5929\u4e0d\u7981\u542c\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u5973\u7684\u9053\uff1a\u201c\u6211\u4eec\u660e\u65e5\u591c\u95f4\u4fbf\u8981\u8d76\u5230\u62c9\u8428\uff0c\u4f60\u5374\u504f\u504f\u8981\u4e0a\u5c71\u6765\u542c\u8fd9\u7434\u58f0\uff0c\u4f60\u5b89\u7684 \u662f\u4ec0\u4e48\u5fc3\uff1f\u201d\u7537\u7684\u9053\uff1a\u201c\u542c\u8bf4\u4eca\u65e5\u8fd8\u6709\u4e00\u4e2a\u5973\u7684\u6765\u52a9\u9635\uff0c\u6562\u60c5\u5c31\u662f\u5728\u6b64\u5f39\u7434\u7684\u4eba\uff0c\u6b64\u4e8b\u751a \u5947\uff0c\u54b1\u4eec\u65e2\u7136\u7ecf\u8fc7\uff0c\u4e0d\u53ef\u4e0d\u770b\u3002\u201d \u3000\u3000\u90a3\u5973\u7684\u9053\uff1a\u201c\u54fc\uff0c\u82e5\u662f\u4e00\u4e2a\u81ed\u7537\u5b50\u5728\u8fd9\u91cc\u5f39\u7434\uff0c\u4f60\u5c31\u4e0d\u4f1a\u5df4\u5df4\u7684\u6500\u4e0a\u6765\u4e86\u3002\u201d\u542c\u8fd9\u8bed \u6c14\u3001\u918b\u5473\u751a\u6d53\uff0c\u4f3c\u4e4e\u662f\u5bf9\u592b\u5987\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u897f\u57df\u592b\u5987\u53cc\u4fee\uff0c\u50cf\u8fd9\u7ea7\u5e74\u7eaa\u800c\u53c8 \u5927\u6709\u6765\u5934\u7684\u7684\uff0c\u9664\u4e86\u59e8\u7236\u59e8\u6bcd\uff08\u674e\u6cbb\u548c\u51af\u7433\uff09\u548c\u6211\u7684\u7236\u6bcd\u4e4b\u5916\uff0c\u4fbf\u6570\u5230\u9752\u6d77\u7075\u5c71\u6d3e\u7684\u4e91\u7075\u5b50 \u592b\u5987\uff0c\u96be\u9053\u8fd9\u4e24\u4eba\u4e5f\u5e94\u4e86\u6e05\u5bab\u7684\u793c\u8058\u4e48\uff1f\u201d\u53ea\u542c\u90a3\u7537\u7684\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8bf4\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5728\u8fd9\u5c71 \u4e0a\u5f39\u7434\u7684\u5973\u5b50\uff0c\u5373\u4f7f\u4e0d\u662f\u51b0\u5ddd\u5929\u5973\uff0c\u4ea6\u5fc5\u662f\u5927\u6709\u6765\u5386\u4e4b\u4eba\u3002\u54b1\u4eec\u65e2\u5949\u7687\u4e0a\u5dee\u9063\uff0c\u81ea\u8be5\u5904\u5904\u5c0f \u5fc3\uff0c\u65e2\u7136\u7ecf\u8fc7\uff0c\u5c82\u53ef\u4e0d\u63a2\u63a2\u5979\u7684\u5e95\u7ec6\u3002\u201d\u90a3\u5973\u7684\u9053\uff1a\u201c\u7687\u4e0a\u8981\u4f60\u63a2\u7684\u662f\u9f99\u8001\u4e09\u7684\u6765\u5386\u3002\u201d\u7537 \u7684\u9053\uff1a\u201c\u9f99\u8001\u4e09\u73b0\u6b63\u5fd9\u4e8e\u4fdd\u62a4\u91d1\u74f6\uff0c\u4ed6\u54ea\u6599\u5230\u6709\u4eba\u6697\u4e2d\u5bf9\u4ed6\u7aa5\u4f3a\uff1f\u54b1\u4eec\u6b64\u53bb\uff0c\u5fc5\u7136\u4e00\u4e3e\u6210 \u529f\uff0c\u4f55\u51b5\u8001\u5927\u5df2\u5148\u5230\u4e86\u62c9\u8428\u5462\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\u3002\u54b1\u4eec\u8fd8\u662f\u51fa\u53bb\u770b\u770b\u8fd9\u5f39\u7434\u7684\u5973\u5b50\u5427\uff0c\u4ece\u8fd9\u5973\u5b50 \u7684\u53e3\u4e2d\uff0c\u4e5f\u53ef\u4ee5\u63a2\u542c\u5230\u4e86\u4e9b\u9f99\u8001\u4e09\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u90a3\u5bf9\u7537\u5973\u5530\u5566\u4e00\u58f0\u4ece\u8305\u8349\u4e1b\u4e2d\u8df3\u51fa\u3002\u51b0\u5ddd\u5929\u5973\u5f39\u4e86\u4e24\u9615\uff0c\u8fd8\u672a\u89c1\u767d\u8863\u5c11\u5e74\u6765\u5230\uff0c\u6b63\u662f\u82b3 \u5fc3\u5fae\u6012\uff0c\u5ffd\u89c1\u4e24\u4e2a\u76f8\u8c8c\u4e11\u964b\u7684\u7537\u5973\u8df3\u51fa\u6765\uff0c\u90a3\u7537\u7684\u8fd8\u6ecb\u7259\u9732\u9f7f\uff0c\u51b2\u7740\u5979\u563b\u563b\u5730\u7b11\uff0c\u4e0d\u7531\u5f97\u5927 \u4e3a\u607c\u6012\uff0c\u90a3\u5973\u7684\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u4e0d\u662f\u65e5\u95f4\u52a9\u9635\u3001\u66ff\u9f99\u8001\u4e09\u4fdd\u62a4\u91d1\u74f6\u4e4b\u4eba\uff1f\u201d\u90a3\u5973\u7684\u89c1\u51b0\u5ddd\u5929 \u5973\u5982\u6b64\u7f8e\u8c8c\uff0c\u4e08\u592b\u53c8\u51b2\u7740\u5979\u7b11\uff0c\u65e0\u540d\u706b\u8d77\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u751a\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u4e00\u7b11\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u5bf9\u72d7\u7537\u5973\u6562\u6765\u5077\u542c\u6211\u5f39\u7434\uff0c\u7ed9\u6211\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u4e00\u626c\u624b \u5c31\u662f\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff01\u5510\u7ecf\u5929\u6240\u6599\u4e0d\u9519\uff0c\u8fd9\u5bf9\u7537\u5973\u6b63\u662f\u4e91\u7075\u5b50\u592b\u5987\u3002\u4ed6\u4eec\u662f\u4e00\u6d3e\u7684\u9886\u8896\uff0c\u51e0\u66fe \u53d7\u8fc7\u4eba\u8fd9\u822c\u8fb1\u9a82\uff0c\u592b\u5987\u4fe9\u52c3\u7136\u5927\u6012\uff0c\u6b63\u5f85\u51fa\u624b\uff0c\u5ffd\u89c9\u4e00\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u6251\u9762\u5c04\u6765\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a \u60ca\u9a87\uff0c\u6025\u5fd9\u8fd0\u6c14\u95ed\u7a74\uff0c\u9976\u662f\u5982\u6b64\uff0c\u4e5f\u4e0d\u7531\u81ea\u5df1\u5730\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u89c1\u51b0\u9b44\u795e\u5f39\u6253\u4ed6\u4eec\u4e0d\u5012\uff0c\u4ea6\u662f\u597d\u751f\u60ca\u8be7\uff0c\u7389\u624b\u4e00\u626c\uff0c\u53c8\u662f\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u8fd9\u53e3 \u52a0\u91cd\u4e86\u5185\u5bb6\u52b2\u529b\uff0c\u53ef\u4ee5\u900f\u7a74\u800c\u8fc7\uff0c\u4e91\u7075\u5b50\u6025\u5fd9\u95ea\u8eab\uff0c\u90a3\u51b0\u9b44\u795e\u5f39\u4ece\u4ed6\u8eab\u65c1\u63a0\u8fc7\uff0c\u7206\u53d1\u5f00\u6765\uff0c \u987f\u65f6\u98de\u51fa\u4e00\u56e2\u5bd2\u5149\u51b7\u6c14\u3002\u4ed6\u7684\u59bb\u5b50\u6321\u51b0\u9b44\u795e\u5f39\u7684\u624b\u6cd5\u6bd4\u4ed6\u8fd8\u8981\u9ad8\u660e\uff0c\u89e3\u4e0b\u67ec\u8eab\u8170\u5e26\uff0c\u8f7b\u8f7b\u4e00 \u5377\uff0c\u5c31\u628a\u51b0\u5f39\u88f9\u4f4f\uff0c\u6296\u624b\u4e00\u7ede\uff0c\u51b0\u5f39\u5728\u8170\u5e26\u88f9\u788e\u4e86\uff0c\u5316\u6210\u51b0\u6c34\uff0c\u6e17\u4e86\u51fa\u6765\u3002\u90a3\u5973\u7684\u5c31\u628a\u8170\u5e26 \u5f53\u4f5c\u8f6f\u97ad\u4f7f\u7528\uff0c\u5f84\u6251\u51b0\u5ddd\u5929\u5973\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e5f\u89e3\u4e0b\u4e86\u675f\u8eab\u7684\u7ef8\u5e26\uff0c\u7528\u529b\u4e00\u6325\uff0c\u6709\u5982\u7389\u9f99\u592d\u77eb\uff0c\u7acb\u523b\u7f20\u7740\u4e86\u90a3\u5973\u7684\u8170\u5e26\uff0c\u970e \u773c\u4e4b\u95f4\uff0c\u4e09\u8fdb\u4e09\u9000\uff0c\u7ef8\u5e26\u98d8\u821e\uff0c\u5f69\u8272\u7f24\u7eb7\uff0c\u597d\u770b\u4e4b\u6781\uff0c\u4e91\u7075\u5b50\u559d\u9053\uff1a\u201c\u4f60\u83ab\u975e\u5c31\u662f\u51b0\u5ddd\u5929\u5973 \u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u79c0\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u662f\u6211\uff0c\u8fd8\u4e0d\u5feb\u5feb\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u4e91\u7075\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u5c31 \u7b97\u4f60\u771f\u662f\u5929\u5973\u4e0b\u51e1\uff0c\u4e5f\u5f97\u9886\u6559\u9886\u6559\u4f60\u7684\u51b0\u5ddd\u5251\u6cd5\uff01\u201d\u4ece\u8170\u95f4\u62bd\u51fa\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u70b9\u6253\u51b0\u5ddd\u5929\u5973 \u80cc\u5fc3\u7684\u4e24\u9053\u5927\u7a74\u3002 \u3000\u3000\u53cc\u7b14\u631f\u98ce\uff0c\u70b9\u6253\u7a74\u9053\uff0c\u53c8\u72e0\u53c8\u51c6\uff0c\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00\u6817\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u4e11\u6c49\u7adf\u7136\u4e5f\u662f\u4e00\u4e2a\u70b9 \u7a74\u9ad8\u624b\u3002\u4e0d\u6562\u8f7b\u654c\uff0c\u7acb\u523b\u7528\u4e86\u4e00\u62db\u201c\u56de\u98ce\u6298\u67f3\u201d\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u628a\u51b0\u9b44\u5bd2\u5149\u5251\u62d4\u5728\u624b\u4e2d\u3002\u4e91\u7075 \u5b50\u631f\u6570\u5341\u5e74\u529f\u529b\uff0c\u53cc\u7b14\u4e00\u5c01\uff0c\u7528\u4e86\u4e00\u62db\u201c\u6a2a\u67b6\u91d1\u6881\u201d\u5f80\u4e0a\u4e00\u5d29\uff0c\u6ee1\u62df\u5c06\u51b0\u5ddd\u5929\u5973\u7684\u5175\u5203\u5f53\u573a \u6298\u65ad\u3002\u54ea\u77e5\u51b0\u5ddd\u5927\u5973\u5251\u8d70\u8f7b\u7075\uff0c\u4e00\u6cbe\u5373\u8fc7\uff0c\u5bd2\u5149\u51b7\u6c14\uff0c\u8000\u773c\u6c81\u51c9\uff0c\u4e91\u7075\u4e8e\u7adf\u4e0d\u7531\u81ea\u4e3b\u7684\u6253\u4e86 \u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u63a5\u8fde\u523a\u4e86\u4e09\u5251\uff0c\u4e91\u7075\u5b50\u8f6c\u653b\u4e3a\u5b88\uff0c\u8db3\u8e0f\u516b\u5366\u65b9\u4f4d\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u4f46 \u53cc\u7b14\u4ea4\u53c9\uff0c\u5c01\u95ed\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u7b14\u5c16\u6307\u7740\u51b0\u5ddd\u5929\u5973\u7684\u7a74\u9053\uff0c\u968f\u65f6\u53ef\u4ee5\u4f3a\u673a\u53cd\u51fb\u3002\u4e91\u7075\u5b50\u7684\u59bb\u5b50 \u6851\u9752\u5a18\u529f\u529b\u4e5f\u4e0d\u5728\u4e43\u592b\u4e0b\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u5251\u6cd5\u51cc\u5389\uff0c\u6025\u5c06\u8170\u5e26\u6296\u5f97\u7b14\u76f4\uff0c\u4f7f\u51fa\u4e00\u8def\u98de\u9f99\u97ad\u6cd5\u3002 \u6851\u9752\u5a18\u7ec3\u7684\u662f\u897f\u85cf\u5bc6\u5b97\u7684\u201c\u67d4\u529f\u201d\uff0c\u5584\u80fd\u4ee5\u67d4\u514b\u521a\uff0c\u90a3\u8170\u5e26\u6325\u821e\u8d77\u6765\uff0c\u6709\u7f20\u3001\u6253\uff0c\u5708\u3001\u531d\u3001 \u6cbe\u3001\u626b\u3001\u62d6\u3001\u5377\u516b\u6cd5\uff0c\u53ef\u4f5c\u51e0\u79cd\u5175\u5668\u4f7f\u7528\uff0c\u5e76\u80fd\u593a\u53d6\u654c\u4eba\u7684\u5200\u5251\uff0c\u6bd4\u5bfb\u5e38\u7684\u8f6f\u97ad\uff0c\u5389\u5bb3\u4f55\u6b62 \u767e\u500d\uff0c\u51b0\u5ddd\u5929\u5973\u5206\u5fc3\u4f7f\u5251\u3001\u7ef8\u5e26\u821e\u6210\u7684\u5706\u5708\u9632\u5fa1\u7a0d\u758f\uff0c\u5fae\u9732\u7a7a\u9699\uff0c\u6851\u9752\u5a18\u7684\u8170\u5e26\u7acb\u5373\u94bb\u5165\uff0c \u4e00\u4f38\u4e00\u7f29\uff0c\u6709\u5982\u6bd2\u86c7\u5410\u4fe1\uff0c\u7adf\u60f3\u653b\u5165\u5185\u5708\uff0c\u4e0a\u523a\u51b0\u5ddd\u5929\u5973\u7684\u53cc\u76ee\u3002\u51b0\u5ddd\u5929\u5973\u8feb\u5f97\u5c06\u51b0\u9b44\u5bd2\u5149 \u5251\u6a2a\u8f6c\u8fc7\u6765\uff0c\u5de6\u4e00\u62db\u201c\u96ea\u82b1\u516d\u51fa\u201d\uff0c\u53f3\u4e00\u62db\u201c\u79ef\u6c34\u51dd\u51b0\u201d\uff0c\u5de6\u53f3\u4e24\u5251\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u77ac\u606f\u4e4b \u95f4\uff0c\u53d8\u5316\u516b\u4e2a\u62db\u5f0f\uff0c\u6851\u9752\u5a18\u4e0d\u6562\u5f3a\u653b\uff0c\u62bd\u51fa\u8170\u5e26\uff0c\u9632\u62a4\u8981\u5bb3\uff0c\u51b0\u5ddd\u5929\u5973\u89e3\u4e86\u672c\u8eab\u7684\u6210\u5fc5\u2103\u6b63 \u60f3\u6389\u8f6c\u5251\u950b\uff0c\u4e91\u7075\u5b50\u7684\u5224\u5b98\u7b14\u65e9\u5df2\u98de\u70b9\u8fc7\u6765\uff0c\u62a2\u4e86\u5148\u624b\uff0c\u4e00\u62db\u7d27\u8fc7\u4e00\u62db\uff0c\u4e0d\u8ba9\u51b0\u5ddd\u5929\u5973\u6709\u53cd \u653b\u7684\u673a\u4f1a\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u53cc\u65b9\u90fd\u662f\u6697\u6697\u5403\u60ca\u3002\u4e91\u7075\u5b50\u592b\u5987\u662f\u4e00\u6d3e\u5de8\u5b7d\uff0c\u5408\u85c9\u53cc\u4fee\uff0c\u5728 \u897f\u57df\u4e45\u4eab\u76db\u540d\uff0c\u4ee5\u4e8c\u654c\u4e00\uff0c\u7adf\u7136\u4e0d\u80fd\u53d6\u80dc\uff0c\u5fc3\u4e2d\u81ea\u662f\u65e0\u9650\u60ca\u5f02\u3002\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u878d\u4e2d\u897f\u5251\u6cd5 \u4e4b\u957f\uff0c\u7cbe\u5999\u65e0\u6bd4\uff0c\u4f46\u88ab\u4ed6\u4eec\u592b\u59bb\u8054\u624b\u56f4\u653b\uff0c\u5374\u4e5f\u53ea\u80fd\u6253\u4e2a\u5e73\u624b\uff0c\u5360\u4e0d\u4e86\u534a\u70b9\u4fbf\u5b9c\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4f0f\u5728\u5ca9\u77f3\u4e4b\u540e\uff0c\u770b\u4e86\u8bb8\u4e45\uff0c\u53ea\u89c1\u4e91\u7075\u5b50\u592b\u5987\u653b\u52bf\u6e10\u6e10\u52a0\u5f3a\uff0c\u5224\u5b98\u7b14\u7b14\u8d70\u9f99\u86c7\uff0c\u6bcf \u4e00\u62db\u90fd\u662f\u63aa\u5411\u8981\u547d\u7684\u7a74\u9053\u6240\u5728\uff1b\u6851\u9752\u5a18\u7684\u8170\u5e26\u66f4\u662f\u5201\u94bb\u53e4\u602a\u4e4b\u6781\uff0c\u6709\u5982\u7075\u86c7\u6e38\u52a8\uff0c\u9047\u9699\u5373 \u4eba\uff0c\u51b0\u5ddd\u5929\u5973\u6e10\u6e10\u5904\u5728\u4e0b\u98ce\u3002\u4f46\u5979\u7684\u5251\u6cd5\u7cbe\u5fae\u5965\u5999\u3001\u6bcf\u6bcf\u5728\u4e0b\u98ce\u4e4b\u9645\uff0c\u7a81\u51fa\u5947\u62db\uff0c\u4e91\u7075\u5b50\u592b \u5987\u6478\u4e0d\u900f\u5979\u7684\u95e8\u8def\uff0c\u4ea6\u662f\u6709\u6240\u987e\u5fcc\uff0c\u867d\u7136\u5360\u4e86\u4e0a\u98ce\uff0c\u4ecd\u662f\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u4e0d\u6562\u6709\u534a\u70b9\u8f7b\u8fdb\u5192\u9669\u3002 \u3000\u3000\u5510\u7ecf\u5929\u51dd\u795e\u7ec6\u770b\uff0c\u6697\u4e2d\u63e3\u6478\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\uff0c\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u6211\u53ea\u9053\u5929\u5c71\u5251\u6cd5\u5929\u4e0b\u65e0\u654c\uff0c \u800c\u4eca\u770b\u6765\uff0c\u5979\u7684\u5251\u6cd5\u5947\u8be1\u53d8\u5e7b\uff0c\u6709\u8bb8\u591a\u5730\u65b9\u8fd8\u8981\u80dc\u8fc7\u5929\u5c71\u5251\u6cd5\uff0c\u771f\u662f\u5b66\u65e0\u6b62\u5883\uff0c\u5fc5\u987b\u7cbe\u76ca\u6c42 \u7cbe\u3002\u201d\u5176\u5b9e\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u5728\u5947\u8be1\u53d8\u5e7b\u4e4b\u5904\u81ea\u662f\u7a0d\u80dc\uff0c\u4f46\u8bba\u5230\u535a\u5927\u7cbe\u6df1\uff0c\u6c89\u7a33\u6d51\u539a\uff0c\u5374\u5c1a\u4e0d \u5982\u5929\u5c71\u5251\u6cd5\uff0c\u5929\u5c71\u5251\u6cd5\u9047\u5230\u529f\u529b\u6bd4\u81ea\u5df1\u9ad8\u7684\u4eba\uff0c\u53ef\u4ee5\u51dd\u5b88\u81ea\u4fdd\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u957f\u4e8e\u653b\u800c\u9632 \u5b88\u8f83\u758f\u3001\u9047\u5230\u529f\u529b\u8f83\u81ea\u5df1\u9ad8\u7684\u4eba\uff0c\u5374\u4e0d\u514d\u7a0d\u5403\u4e8f\u3002 \u3000\u3000\u4e91\u7075\u5b50\u592b\u5987\u7684\u529f\u529b\u4e0e\u90a3\u5370\u5ea6\u7684\u82e6\u884c\u50e7\u53ca\u9f99\u7075\u77eb\u7b49\u4eba\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u82e5\u7136\u4ee5\u4e00\u654c\u4e00\uff0c\u767e\u62db\u4e4b \u5185\uff0c\u5fc5\u7136\u8f93\u7ed9\u51b0\u5ddd\u5929\u5973\u3002\u4f46\u800c\u4eca\u592b\u5987\u8054\u653b\uff0c\u4ee5\u4e8c\u654c\u4e00\uff0c\u81ea\u662f\u5927\u5360\u4fbf\u5b9c\u3002\u4f46\u56e0\u51b0\u5ddd\u5929\u5973\u90a3\u628a\u51b0 \u9b44\u5bd2\u5149\u5251\u662f\u5929\u4e0b\u6700\u5947\u602a\u7684\u5b9d\u5251\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u51b7\u6c14\u4fb5\u80a4\uff0c\u4ed6\u4eec\u4e0d\u80fd\u4e0d\u5206\u51fa\u5fc3\u795e\uff0c\u8fd0\u6c14\u9632\u5fa1\uff0c\u5982 \u6b64\u4e00\u6765\uff0c\u867d\u5360\u4fbf\u5b9c\uff0c\u8feb\u5207\u95f4\u5374\u4e5f\u8c01\u5948\u5979\u4f55\u3002 \u3000\u3000\u6708\u4eae\u6e10\u6e10\u897f\u79fb\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u4ed6\u4eec\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u6e10\u89c9\u6c14\u5598\u5fc3\u8df3\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u90a3\u767d\u8863\u5c11 \u5e74\u4e3a\u4f55\u8fd8\u4e0d\u6765\u5462\uff1f\u201d\u5fc3\u4e2d\u70e6\u607c\uff0c\u4e0d\u80fd\u9547\u5b9a\u3002\u4e91\u7075\u5b50\u592b\u5987\u90fd\u662f\u8001\u624b\uff0c\u4e00\u89c1\u6709\u673a\u53ef\u4e58\uff0c\u7acb\u5373\u52a0\u5f3a \u538b\u529b\uff0c\u4e91\u7075\u5b50\u7684\u5224\u5b98\u7b14\u4ee5\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\uff0c\u7d27\u7d27\u538b\u7740\u51b0\u5ddd\u5929\u5973\u7684\u5b9d\u5251\uff0c\u4e0d\u8ba9\u5979\u4f7f\u51fa\u5947\u8be1\u7684\u53d8 \u62db\uff0c\u6851\u9752\u5a18\u7684\u8170\u5e26\u53c8\u4e58\u9699\u94bb\u5165\uff0c\u7740\u7740\u8fdb\u8feb\uff0c\u5e7d\u840d\u672c\u662f\u6ee1\u4e0d\u5728\u4e4e\u7684\u5728\u65c1\u89c2\u6218\uff0c\u8fd9\u65f6\u4e5f\u6e10\u6e10\u6709\u70b9 \u4e3a\u4e3b\u4eba\u62c5\u5fc3\u3002\u5ffd\u89c1\u4e91\u7075\u5b50\u7684\u90a3\u5bf9\u5224\u5b98\u7b14\u4e00\u62db\u201c\u6d41\u661f\u5954\u6708\u201d\uff0c\u53cc\u63d2\u8111\u95e8\uff0c\u800c\u6851\u9752\u5a18\u7684\u8170\u5e26\u4e5f\u51e0 \u4e4e\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u653b\u5165\u5185\u5708\uff0c\u5e26\u4e0a\u91d1\u73af\uff0c\u7405\u7405\u4f5c\u54cd\u3002\u51b0\u5ddd\u5929\u5973\u7684\u5251\u88ab\u5c01\u5728\u5916\u95e8\uff0c\u8feb\u5207\u4e4b\u95f4\uff0c\u64a4 \u4e0d\u56de\u6765\uff0c\u52bf\u5c06\u843d\u8d25\uff0c\u5e7d\u840d\u4e0d\u7981\u201c\u5475\u5440\u201d\u4e00\u58f0\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u597d\u4e2a\u51b0\u5ddd\u5929\u5973\uff0c\u5c31\u5728\u8fd9\u5c06\u8d25\u672a\u8d25\u3001\u5371\u9669\u4e07\u5206\u4e4b\u9645\uff0c\u663e\u51fa\u4e86\u975e\u51e1\u7684\u672c\u9886\uff0c\u53ea\u89c1\u5979\u5251\u67c4\u4e00 \u6296\uff0c\u5251\u950b\u5728\u5224\u5b98\u7b14\u4e0a\u78b0\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6\u98de\u51fa\u6570\u5341\u767e\u6735\u5251\u82b1\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u4eba\u5f71\u4e0d\u8fa8\uff0c\u4e00\u53e3\u5251\u4e5f\u4f3c \u5316\u4e86\u6570\u5341\u767e\u53e3\u4e00\u822c\uff0c\u8fd9\u4e00\u62db\u540d\u5524\u201c\u51b0\u6cb3\u89e3\u51bb\u201d\uff0c\u662f\u51b0\u5ddd\u5251\u6cd5\u4e2d\u4e34\u5371\u89e3\u56f0\u3001\u8d25\u91cc\u53cd\u653b\u7684\u7edd\u62db\u3002 \u8fd9\u65f6\u4e91\u7075\u5b50\u7684\u5224\u5b98\u7b14\u82e5\u4ecd\u7136\u4e0b\u63d2\uff0c\u51c6\u53ef\u4ee5\u5728\u51b0\u5ddd\u5929\u5973\u7684\u8111\u95e8\u4e0a\u68da\u4e24\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\uff0c\u4f46\u4ed6\u4eec\u592b \u5987\u4e8c\u4eba\u4e5f\u5fc5\u7136\u8981\u88ab\u51b0\u9b44\u5bd2\u5149\u5251\u5728\u8eab\u4e0a\u6233\u5341\u51e0\u9053\u4f24\u53e3\u3002\u4e91\u7075\u5b50\u592b\u5987\u4e00\u6765\u4e0d\u8bc6\u8fd9\u5251\u6cd5\u7684\u5965\u5999\uff0c\u88ab \u5979\u7684\u51b0\u9b44\u5bd2\u5149\u7729\u76ee\u6b32\u8ff7\uff0c\u770b\u4e0d\u6e05\u654c\u4eba\u65b9\u4f4d\uff0c\u54ea\u6562\u5192\u7136\u4e0b\u63d2\uff1b\u4e8c\u6765\u4ed6\u4eec\u592b\u5987\u90fd\u662f\u8001\u624b\uff0c\u6b66\u6797\u9ad8 \u624b\u6bd4\u6b66\uff0c\u603b\u662f\u672a\u6599\u80dc\u5148\u9632\u8d25\uff0c\u4e45\u5df2\u5949\u4e3a\u91d1\u79d1\u7389\u5f8b\u3002\u54ea\u6599\u5f97\u5230\u51b0\u5ddd\u5929\u5973\u7684\u8fd9\u62db\u5251\u6cd5\uff0c\u5168\u7136\u4e0d\u987e \u81ea\u8eab\uff0c\u72e0\u8fa3\u65e0\u6bd4\uff0c\u4ed6\u4eec\u4e8c\u4eba\u88ab\u51b0\u5ddd\u5929\u5973\u7684\u653b\u52bf\u6240\u80c1\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u6025\u6025\u62bd\u56de\u5175\u5668\uff0c\u5c01\u95ed\u95e8\u6237\uff0c \u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u9644\u8fd1\u6709\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u597d\u5475\uff01\u201d\u539f\u6765\u662f\u5510\u7ecf\u5929\u5728\u5ca9\u77f3\u540e\u770b\u5f97\u60c5\u4e0d\u81ea\u7981\uff0c\u53eb \u51fa\u58f0\u6765\u3002 \u3000\u3000\u6b64\u58f0\u4e00\u51fa\uff0c\u4e91\u7075\u5b50\u592b\u5987\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u4e91\u7075\u5b50\u5224\u5b98\u7b14\u4e00\u5206\uff0c\u8d70\u51fa\u4e00\u62db\u201c\u71d5\u4e8e\u659c\u98de\u201d\u4e4b \u52bf\uff0c\u534a\u653b\u534a\u5b88\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u7075\u5c71\u6d3e\u7684\u4e91\u7075\u5b50\u5728\u6b64\uff0c\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7\u51fa\u6765\u76f8\u4f1a\u3002\u201d\u4e91\u7075 \u5b50\u5a01\u9707\u897f\u57df\uff0c\u4ed6\u81ea\u62a5\u540d\u5934\uff0c\u65e0\u975e\u662f\u60f3\u9707\u6151\u5bf9\u65b9\uff0c\u4ee4\u4ed6\u77e5\u6240\u987e\u5fcc\u7684\u610f\u601d\u3002\u4e0d\u6599\u58f0\u72b9\u672a\u6bd5\uff0c\u5ffd\u89c1 \u4e24\u9053\u4e4c\u91d1\u5149\u534e\uff0c\u7535\u5c04\u800c\u6765\uff0c\u53ee\u9677\u4e00\u58f0\uff0c\u4e24\u652f\u5224\u5b98\u7b14\u7adf\u7ed9\u654c\u4eba\u7684\u6697\u5668\u5c04\u5f97\u659c\u98de\u8d77\u6765\uff0c\u62db\u5f0f\u88ab \u7834\u3001\u95e8\u6237\u6d1e\u5f00\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5bd2\u5149\u5251\u8fc5\u903e\u98d8\u98ce\uff0c\u4e00\u95ea\u5373\u8fdb\uff01 \u3000\u3000\u4e91\u7075\u5b50\u9b42\u9b44\u9f50\u98de\uff0c\u53ea\u89c9\u5bd2\u5149\u8000\u773c\uff0c\u51b7\u6c14\u653b\u5fc3\uff0c\u65e0\u53ef\u62db\u67b6\uff0c\u5fc3\u4e2d\u53eb\u9053\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u5ffd \u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u90a3\u5251\u5149\u7ed5\u9876\u800c\u8fc7\u8c03\u5374\u672a\u843d\u4e0b\uff0c\u4e91\u7075\u5b50\u6b66\u529f\u4e5f\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b \u95f4\u3001\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u6025\u5fd9\u5411\u540e\u4e00\u7eb5\uff0c\u98de\u63a0\u6570\u4e08\uff0c\u8fde\u722c\u5e26\u6eda\uff0c\u8dcc\u4e0b\u5c71\u5761\u3002 \u3000\u3000\u539f\u6765\u90a3\u88c2\u5e1b\u4e4b\u58f0\uff0c\u4e43\u662f\u4ed6\u59bb\u5b50\u6851\u9752\u5a18\u7684\u8170\u5e26\u88ab\u51b0\u5ddd\u5929\u5973\u7684\u5b9d\u5251\u6240\u5272\u65ad\uff0c\u6851\u9752\u5a18\u89c1\u4e08\u592b\u5371 \u6025\uff0c\u8f89\u5e26\u86ee\u653b\uff0c\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u8170\u5e26\u7b14\u76f4\u5982\u77e2\uff0c\u7adf\u5f53\u4f5c\u4e94\u884c\u5251\u4f7f\u7528\uff0c\u4e0a\u523a\u51b0\u5ddd\u5929\u5973\u53cc \u76ee\uff0c\u51b0\u5ddd\u5929\u5973\u6a2a\u5251\u5c06\u5b83\u5272\u65ad\uff0c\u7f13\u4e86\u4e00\u7f13\uff0c\u4e91\u7075\u5b50\u624d\u9003\u5f97\u51fa\u6027\u547d\u3002 \u3000\u3000\u6851\u9752\u5a18\u5f03\u4e86\u8170\u5e26\uff0c\u7d27\u8ddf\u5728\u4e08\u592b\u4e4b\u540e\uff0c\u9003\u4e0b\u5c71\u5761\u3002\u4e24\u592b\u5987\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u626c\u5251\u6b32 \u8ffd\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u5374\u7ad9\u5728\u5979\u7684\u9762\u524d\uff0c\u6307\u624b\u5212\u811a\uff0c\u4f3c\u662f\u4f5c\u529d\u6b62\u4e4b\u72b6\u3002\u4e91\u7075\u5b50\u62d4\u51fa\u523a\u5728\u5224\u5b98\u7b14\u4e0a\u7684 \u6697\u5668\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u5929\u5c71\u5510\u6653\u6f9c\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u5510\u7ecf\u5929\u9053\u3002\u201c\u6211\u66ff\u5bb6\u7236\u5411\u4e24\u4f4d\u8005\u524d\u8f88\u95ee \u5019\uff0c\u8bf7\u6012\u5c0f\u8f88\u65e0\u793c\u3002\u201d\u4e91\u7075\u5b50\u592b\u5987\u76f8\u987e\u5931\u8272\uff0c\u51ed\u4ed6\u4eec\u6709\u591a\u5927\u7684\u80c6\u5b50\u4e5f\u4e0d\u6562\u53bb\u62db\u60f9\u5929\u5c71\u6d3e\u7684\u638c \u95e8\u5510\u6653\u6f9c\uff0c\u4f55\u51b5\u773c\u89c1\u5510\u7ecf\u5929\u7684\u6b66\u529f\uff0c\u7adf\u7136\u80fd\u7528\u5929\u5c71\u795e\u8292\u5c04\u5165\u4ed6\u7684\u94c1\u7b14\uff0c\u53ea\u8fd9\u4efd\u672c\u9886\uff0c\u5c31\u4e0d\u5728 \u4ed6\u4eec\u4e4b\u4e0b\u3002\u4e91\u7075\u5b50\u51b7\u6c57\u76f4\u6d41\u3002\u5374\u626c\u58f0\u9a82\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u4e0d\u4e0e\u4f60\u4e00\u822c\u89c1\u8bc6\uff0c\u6211\u627e\u4f60\u7236\u4eb2\u7b97\u5e10 \u53bb\u3002\u201d\u8fd9\u5f53\u7136\u662f\u4e3a\u4e86\u63a9\u9970\u989c\u9762\uff0c\u6545\u610f\u81ea\u9ad8\u8eab\u4efd\u4e4b\u8a00\u3002\u51b0\u5ddd\u5929\u5973\u51b7\u7b11\u9053\uff1a\u201c\u79c3\u9a74\uff0c\u4f60\u4e0d\u786c\u5634\u3002 \u518d\u8bd5\u6211\u4e00\u5251\uff01\u201d\u4e91\u7075\u5b50\u542c\u5979\u9a82\u81ea\u5df1\u662f\u201c\u79c3\u9a74\u201d\uff0c\u6014\u4e86\u4e00\u6014\uff1b\u4e0d\u81ea\u89c9\u7684\u4e00\u6478\u5934\u9876\uff0c\u53ea\u89c9\u89e6\u624b\u5149 \u6ed1\uff0c\u539f\u6765\u9876\u5fc3\u7684\u4e00\u7247\u5934\u53d1\uff0c\u5df2\u88ab\u51b0\u5ddd\u5929\u5973\u524a\u53bb\uff0c\u8fd9\u4e00\u5413\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u6562\u518d\u591a\u8bf4\u534a\u53e5\uff0c\u4e24\u592b\u5987 \u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u614c\u5fd9\u9003\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u8d3c\u4eba\u5077\u542c\u6211\u7684\u7434\u58f0\uff0c\u867d\u7136\u524a\u4e86\u4ed6\u4eec\u7684\u5175\u5668\u548c\u5934\u53d1\uff0c\u5c1a\u672a\u6d88\u6211\u5fc3\u4e2d \u4e4b\u6124\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e16\u4e0a\u597d\u90aa\u4e4b\u8f88\u6bd4\u4ed6\u4eec\u53ef\u6076\u7684\u591a\u7684\u662f\u3002\u4f60\u54ea\u80fd\u65f6\u5e38\u8ddf\u4ed6\u4eec\u751f\u6c14\u3002\u201d\u6b47\u4e86\u4e00 \u6b47\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u5f39\u7434\u5c31\u53ea\u8bb8\u6211\u6765\u542c\u4e48\uff1f\u53ef\u60dc\u6211\u4e0d\u662f\u4f2f\u7259\uff0c\u4e0d\u77e5\u7434\u5fc3\u4f55\u5904\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9762\u4e0a\u4e00\u7ea2\uff0c\u547b\u9053\uff1a\u201c\u8c01\u4e3a\u4f60\u5f39\u7434\u6765\u4e86\uff1f\u4f60\u8fd8\u8981\u4e0d\u8981\u4e0e\u6211\u6bd4\u5251\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a \u201c\u4e0d\u5fc5\u6bd4\u4e86\uff0c\u9002\u624d\u6211\u89c1\u4e86\u4f60\u7684\u771f\u5b9e\u672c\u9886\uff0c\u5251\u6cd5\u786e\u662f\u9ad8\u660e\uff0c\u6211\u7518\u62dc\u4e0b\u98ce\u5c31\u662f\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u201c\u6211\u6700\u8ba8\u538c\u4eba\u53e3\u4e0d\u5bf9\u5fc3\uff0c\u4f60\u5fc3\u4e2d\u5206\u660e\u5728\u90a3\u91cc\u8bf4\uff0c\u51b0\u5ddd\u5251\u6cd5\u4e5f\u4e0d\u8fc7\u5982\u6b64\u5982\u6b64\uff0c\u54ea\u6bd4\u5f97\u4e0a\u6211\u7684\u5929 \u5c71\u5251\u6cd5\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u6709\u770b\u900f\u4eba\u5fc3\u4e8b\u7684\u672c\u9886\u4e48\uff1f\u8fd9\u6b21\u4f60\u5374\u770b\u9519\u4e86\u3002\u6211\u5fc3\u4e2d\u8bf4\uff1a \u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u679c\u662f\u9ad8\u660e\uff0c\u5728\u4e09\u5e74\u4e94\u8f7d\u4e4b\u5185\uff0c\u6211\u8d62\u4e0d\u4e86\u5979\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u624d\u662f\u771f \u8bdd\u3002\u201d\u539f\u6765\u5979\u5fc3\u5185\u6b63\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u5979\u89c1\u4e86\u5510\u7ecf\u5929\u51e0\u6b21\u663e\u9732\u7684\u672c\u9886\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u81ea\u5df1\u867d\u7136\u672a \u5fc5\u8f93\u7ed9\u4ed6\uff0c\u4f46\u5728\u4e09\u5e74\u4e94\u8f7d\u4e4b\u5185\uff0c\u5374\u662f\u8d62\u4ed6\u4e0d\u4e86\u3002\u88ab\u5510\u7ecf\u5929\u62a2\u5148\u8bf4\u4e86\u51fa\u6765\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u7531\u5f97\u5e7d \u5e7d\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\u53c8\u53f9\u6c14\u4f5c\u4ec0\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u534a\u54cd\u4e0d\u7b54\uff0c\u5ffd\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5929\u5c71\u5510 \u5927\u4fa0\u7684\u513f\u5b50\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u54b1\u4eec\u5f7c\u6b64\u7684\u8eab\u4e16\u6765\u5386\u90fd\u5df2\u77e5\u9053\uff0c\u8bf4\u6765\u4e0d\u662f\u5916\u4eba\uff0c\u6211\u542c\u7236\u4eb2\u8bf4\uff0c \u4ed6\u60f3\u62db\u96c6\u5929\u5c71\u4e03\u5251\u7684\u540e\u4ee3\u548c\u95e8\u4eba\u6765\u4e00\u6b21\u76db\u4f1a\uff0c\u5230\u65f6\u6211\u548c\u4f60\u4e00\u9f50\u53bb\uff0c\u8ba9\u4f60\u8ba4\u8bc6\u4f60\u7236\u4eb2\u6614\u65e5\u7684\u670b \u53cb\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9762\u8272\u5fae\u53d8\uff0c\u9053\uff1a\u201c\u6211\u7236\u4eb2\u8fdc\u8d70\u57df\u5916\uff0c\u4ed6\u65e9\u5c31\u4e0d\u628a\u81ea\u5df1\u5f53\u4f5c\u5929\u5c71\u4e00\u6d3e\u4e86\u3002\u6211\u600e \u6562\u53c2\u52a0\u4f60\u4eec\u7684\u76db\u4f1a\uff01\u201d\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u77e5\u51b0\u5ddd\u5929\u5973\u80e1\u4e3a\u800c\u51fa\u6b64\u8a00\u3002\u4f46\u770b\u5979\u8bf4\u5f97\u751a\u662f\u8ba4 \u771f\uff0c\u7709\u5b87\u4e4b\u95f4\uff0c\u7adf\u9690\u9690\u6709\u4e00\u79cd\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\u7684\u795e\u8272\u3002\u5510\u7ecf\u5929\u5fae\u611f\u4e0d\u5feb\uff0c\u4fbf\u4e0d\u518d\u63d0\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6709\u6240\u4e0d\u77e5\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u7236\u4eb2\u6842\u534e\u751f\uff0c\u5f53\u5e74\u6b63\u662f\u56e0\u4e3a\u6bd4\u5251\u8f93\u7ed9\u81ea\u5df1\u7684\u7236\u6bcd\uff0c\u4e00\u6c14\u4e4b \u4e0b\uff0c\u800c\u8fdc\u8d70\u5f02\u56fd\uff0c\u91c7\u96c6\u897f\u571f\u5251\u672f\uff0c\u60f3\u878d\u4f1a\u4e2d\u897f\u4e4b\u957f\uff0c\u53e6\u521b\u5251\u6d3e\u80dc\u8fc7\u5929\u5c71\u7684\u3002 \u3000\u3000\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u51dd\u773a\u8fdc\u65b9\uff0c\u82e5\u6709\u6240\u601d\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u968f\u7f18\u800c\u9047\uff0c\u7f18\u5c3d\u5373\u6563\u3002\u4f60\u4e0a\u51b0\u5cf0\u4e00 \u573a\uff0c\u6211\u4e5f\u66ff\u4f60\u593a\u56de\u91d1\u74f6\u4ee5\u4e3a\u62a5\u7b54\u4e86\u3002\u54b1\u4eec\u5b8c\u4e86\u8fd9\u6bb5\u56e0\u679c\uff0c\u65e2\u4e0d\u6bd4\u5251\uff0c\u8fd8\u662f\u6563\u4e86\u5427\u3002\u201d\u5c3c\u6cca\u5c14 \u4e43\u662f\u4f5b\u6559\u56fd\u5bb6\uff0c\u6240\u4ee5\u51b0\u5ddd\u5929\u5973\u4e5f\u751a\u53d7\u4f5b\u6559\u5f71\u54cd\uff0c\u5510\u7ecf\u5929\u542c\u4e86\uff0c\u53c8\u662f\u4e00\u6014\uff0c\u6c89\u9ed8\u534a\u664c\uff0c\u5fae\u7b11\u8bf4 \u9053\uff1a\u201c\u51b0\u5cf0\u5df2\u5012\uff0c\u4f60\u65e2\u5165\u7ea2\u5c18\uff0c\u5c18\u7f18\u90a3\u80fd\u4fbf\u4e86\uff1f\u51b0\u5bab\u867d\u597d\uff0c\u51b7\u51b7\u6e05\u6e05\uff0c\u5373\u7b97\u771f\u80fd\u4fee\u6210\u4ed9\u5973\uff0c \u4e5f\u4e0d\u8fc7\u7b49\u4e8e\u6842\u6bbf\u5ae6\u5a25\uff0c\u5ae6\u5a25\u4e5f\u8fd8\u6709\u2018\u78a7\u6d77\u9752\u5929\u591c\u591c\u5fc3\u2019\u7684\u53f9\u606f\u5462\uff01\u96be\u9053\u51b0\u5b98\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u503c \u5f97\u4f60\u7559\u604b\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u6f6e\u8361\u8d77\u5fae\u6ce2\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u4e00\u8eab\u767d\u8272\u8863\u88f3\uff0c\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u66f4\u663e\u5f97\u5eb8 \u6d12\u51fa\u5c18\uff0c\u4e00\u53cc\u660e\u5982\u51b0\u955c\u7684\u773c\u775b\u6ce8\u89c6\u7740\u81ea\u5df1\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u7981\u9762\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u7adf\u4f3c\u89c9\u5f97\u6709 \u6240\u7559\u604b\uff0c\u81f3\u4e8e\u604b\u7684\u662f\u4ec0\u4e48\uff1f\u81ea\u5df1\u4e5f\u4e0d\u6e05\u695a\u3002\u4e5f\u8bb8\u5c31\u662f\u6b64\u65f6\u6b64\u523b\u7684\u7f8e\u7684\u611f\u53d7\uff0c\u4e5f\u8bb8\u5c31\u662f\u6b64\u4eba\u6b64 \u8bed\uff0c\u4e0e\u81ea\u5df1\u751a\u662f\u6295\u7f18\uff0c\u4f46\u60f3\u8d77\u6b64\u4eba\u53c8\u6b63\u662f\u81ea\u5df1\u5fc5\u987b\u4e0e\u4ed6\u5206\u4e2a\u9ad8\u4e0b\u7684\u4eba\uff0c\u6b64\u523b\u4e0d\u80fd\u5206\u51fa\uff0c\u4e09\u5e74 \u4e94\u5e74\u751a\u6216\u5341\u5e74\u4e4b\u540e\uff0c\u4e5f\u5fc5\u987b\u4e0e\u4ed6\u5206\u4e2a\u9ad8\u4e0b\uff0c\u8fd9\u624d\u4e0d\u8d1f\u7236\u4eb2\u521b\u7acb\u5251\u6d3e\u7684\u9057\u5fd7\uff0c\u601d\u5ff5\u81f3\u6b64\uff0c\u4e0d\u89c9 \u60d8\u7136\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u53c8\u9053\uff1a\u201c\u4f60\u7684\u4e24\u4f4d\u4f2f\u6015\uff0c\u4e00\u5728\u5ddd\u4e2d\uff0c\u4e00\u5728\u6e56\u5317\uff0c\u4f60\u5c31\u4e0d\u60f3\u53bb\u770b\u770b\u4ed6\u4eec\u5417\uff1f \u4ed6\u4eec\u51e0\u5341\u5e74\u6765\u601d\u5ff5\u4f60\u7684\u7236\u4eb2\uff0c\u5230\u5904\u8bf7\u4eba\u6253\u542c\u3002\u9648\u5929\u5b87\u7684\u5e08\u7236\u94c1\u62d0\u4ed9\u4fbf\u662f\u53d7\u4ed6\u4eec\u6240\u6258\uff0c\u5192\u9669\u4e0a \u5230\u51b0\u5cf0\uff0c\u4ee5\u81f3\u8eab\u6b7b\u51b0\u5bab\u7684\uff0c\u96be\u9053\u4f60\u4e5f\u4e0d\u52a8\u5fc3\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u94c1\u62d0\u4ed9\u5728\u51b0\u5bab\u6b7b \u4e86\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u542c\u8bf4\u4ed6\u4eec\u5e08\u5f92\u662f\u4e3a\u4e86\u4fdd\u62a4\u51b0\u5bab\uff0c\u4ee5\u81f3\u94c1\u62d0\u4ed9\u88ab\u7ea2\u8863\u756a\u50e7\u6240\u4f24\uff0c\u56e0\u800c \u81f4\u6b7b\u7684\u3002\u201d\u5c06\u9648\u5929\u5b87\u544a\u8bc9\u4ed6\u7684\u79cd\u79cd\u4e8b\u60c5\uff0c\u8f6c\u8ff0\u7ed9\u51b0\u5ddd\u5929\u5973\u77e5\u9053\u3002\u51b0\u5ddd\u5929\u5973\u60f3\u8d77\u94c1\u62d0\u4ed9\u592b\u5987\u7684 \u4e00\u7247\u70ed\u80a0\uff0c\u4e0d\u89c9\u9eef\u7136\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u7684\u4e24\u4f4d\u4f2f\u4f2f\u82e5\u77e5\u9053\u6709\u4f60\u8fd9\u6837\u4e00\u4e2a\u51b0\u96ea\u806a\u660e\u7684\u4f84\u5973\u513f\uff0c \u4e0d\u77e5\u9053\u8be5\u591a\u9ad8\u5174\u5462\uff0c\u4f60\u4e0d\u60f3\u53bb\u4f1a\u4f1a\u4f60\u5728\u4e2d\u56fd\u7684\u4eb2\u4eba\u5417\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u4eec\u5c45\u4f4f\u7684\u6240\u5728\uff0c\u600e\u6837\u53bb\u6211\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6240\u4ee5\u8bf4\u54b1\u4eec\u5e76\u672a \u7f18\u5c3d\uff0c\u4e0d\u80fd\u5c31\u6b64\u5206\u6563\u3002\u6211\u966a\u4f60\u53bb\u627e\u4e24\u4f4d\u4f2f\u4f2f\u4fbf\u662f\u3002\u54b1\u4eec\u5148\u5230\u5ddd\u897f\u627e\u5192\u5ddd\u751f\u5927\u4fa0\uff0c\u7136\u540e\u518d\u4e0a\u6b66 \u5f53\u5c71\u627e\u77f3\u5e7f\u751f\u5927\u4fa0\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9762\u4e0a\u53c8\u662f\u4e00\u7ea2\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u54b1\u4eec\u4f55\u65f6\u52a8\u8eab\uff1f\u201d \u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u966a\u4f60\u627e\u4e24\u4f4d\u4f2f\u4f2f\u4e4b\u524d\uff0c\u4e5f\u8bf7\u4f60\u966a\u6211\u627e\u4e00\u4e2a\u4eba\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u8fdc \u4e0d\u8fdc\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u9f99\u7075\u77eb\uff0c\u54b1\u4eec\u5230\u62c9\u8428\u627e\u4ed6\uff0c\u803d\u6401\u4e0d\u4e86\u51e0\u5929\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u201c\u91d1\u74f6\u5df2\u66ff\u4ed6\u593a\u56de\uff0c\u8fd8\u627e\u4ed6\u4f5c\u4ec0\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6b64\u4eba\u8eab\u4efd\u5927\u6709\u53ef\u7591\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4e91\u7075\u5b50 \u592b\u5987\uff0c\u672c\u6765\u5c31\u662f\u60f3\u5411\u4ed6\u627e\u9ebb\u70e6\u7684\u3002\u201d\u5c06\u6240\u89c1\u6240\u95fb\uff0c\u8bf4\u4e86\u4e00\u904d\uff0c\u9053\uff1a\u201c\u4e91\u7075\u5b50\u592b\u5987\u7684\u6b66\u529f\u8fdc\u5728 \u7126\u6625\u96f7\u7b49\u516b\u5927\u9ad8\u624b\u4e4b\u4e0a\uff0c\u6e05\u5bab\u5374\u4e0d\u8bf7\u4ed6\u4eec\u62a4\u9001\u91d1\u74f6\uff0c\u800c\u8981\u4ed6\u4eec\u4e58\u6b64\u65f6\u673a\uff0c\u6697\u4e2d\u4fa6\u5bdf\u9f99\u8001\u4e09\u7684 \u5e95\u7ec6\uff0c\u53ef\u89c1\u6e05\u671d\u7687\u5e1d\u5bf9\u9f99\u8001\u4e09\u7684\u91cd\u89c6\uff0c\u7adf\u4f3c\u4e0d\u5728\u91d1\u74f6\u4e4b\u4e0b\u3002\u8fd9\u7591\u56e2\u6211\u975e\u63ed\u4e0d\u53ef\u3002\u201d\u51b0\u5ddd\u5929\u5973 \u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u504f\u4f60\u8fd9\u4e48\u591a\u4e8b\uff01\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4e0d\u613f\u610f\uff0c\u4e5f\u5f97\u966a\u6211\u8d70\u4e00\u8d9f\u3002\u201d\u51b0 \u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u6837\u54b1\u4eec\u5c31\u4e0d\u5fc5\u5f7c\u6b64\u9886\u60c5\uff0c\u5c06\u6765\u4f60\u518d\u8981\u4e0e\u6211\u6bd4\u5251\u4e4b \u65f6\uff0c\u4e5f\u597d\u8bf4\u8bdd\u3002\u201d\u51b0\u5ddd\u5929\u5973\u201c\u55e4\u201d\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u8bf4\u7684\u5012\u662f\uff0c\u597d\u5427\uff0c\u6211\u5c31\u5148\u966a\u4f60\u53bb\u62c9 \u8428\u4e00\u8d9f\u3002\u201d \u3000\u3000\u4e09\u4eba\u62c2\u6653\u52a8\u8eab\uff0c\u9664\u5915\u4e4b\u591c\uff0c\u8d76\u5230\u62c9\u8428\u3002\u53ea\u89c1\u62c9\u8428\u8857\u5934\uff0c\u4eba\u5982\u6f6e\u6d8c\uff0c\u5904\u5904\u9999\u70df\u9563\u7ed5\uff0c\u706f\u706b \u8f89\u714c\uff0c\u5e02\u4e2d\u5fc3\u7684\u5927\u662d\u5bfa\u66f4\u662f\u9970\u4ee5\u91d1\u7b94\uff0c\u6bcf\u5c42\u6a90\u89d2\uff0c\u90fd\u60ac\u4ee5\u4e03\u5f69\u73bb\u7483\u706f\uff0c\u8d8a\u53d1\u663e\u5f97\u5bcc\u4e3d\u5e84\u4e25\u3002 \u4eba\u4eec\u5728\u8857\u5934\u72c2\u6b22\u8df3\u821e\uff0c\u6216\u5531\u6c11\u6b4c\uff0c\u6216\u8bf5\u4f5b\u66f2\uff0c\u4eba\u7fa4\u4e0d\u6b47\u5730\u5411\u7740\u5927\u662d\u5bfa\u6b22\u547c\u3002\u6bd4\u5185\u5730\u7684\u8fc7\u5e74\u8fd8 \u8981\u70ed\u95f9\u767e\u500d\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6ee1\u6e05\u7687\u5e1d\u8fd9\u4ef6\u4e8b\u5012\u662f\u505a\u5f97\u5bf9\u4e86\u3002\u4ed6\u5c06\u91d1\u672c\u5df4\u74f6\u9001\u6765\uff0c\u4ece\u6b64 \u897f\u85cf\u7684\u653f\u6559\u5236\u5ea6\u90fd\u7531\u4e2d\u592e\u89c4\u5b9a\uff0c\u897f\u85cf\u4e0e\u4e2d\u56fd\u66f4\u4e0d\u53ef\u5206\u3002\u602a\u4e0d\u5f97\u897f\u85cf\u7684\u4eba\u5982\u6b64\u9ad8\u5174\uff0c\u5c3d\u7ba1\u6709\u4eba \u6311\u62e8\u6c49\u85cf\u6ee1\u8499\u5404\u65cf\u7684\u60c5\u611f\uff0c\u53ef\u662f\u4ed6\u4eec\u5374\u613f\u610f\u5728\u4e00\u4e2a\u5bb6\u5ead\u4e4b\u5185\uff0c\u5982\u5144\u5982\u5f1f\u5982\u624b\u5982\u8db3\u5462\uff01\u8fd9\u91d1\u672c \u5df4\u74f6\u5c31\u662f\u7edf\u4e00\u7684\u8c61\u5f81\u3002\u201d\u770b\u7740\u4eba\u4eec\u5982\u6b64\u72c2\u6b22\uff0c\u60f3\u8d77\u65e5\u95f4\u91d1\u74f6\u5230\u6765\u4e4b\u65f6\uff0c\u5168\u57ce\u50e7\u4fd7\u90fd\u53bb\u8fce\u63a5\uff0c \u90a3\u66f4\u4e0d\u77e5\u662f\u4f55\u7b49\u70ed\u95f9\uff01\u4ed6\u4eec\u8fdf\u6765\u534a\u65e5\uff0c\u9519\u8fc7\u76db\u4f1a\uff0c\u5fc3\u4e2d\u6697\u6697\u53ef\u60dc\u3002 \u3000\u3000\u4e09\u4eba\u597d\u4e0d\u5bb9\u6613\u624d\u6324\u8fc7\u5e03\u8fbe\u62c9\u5bab\u4e0b\u9762\u7684\u5e7f\u573a\uff0c\u8fdb\u5165\u8461\u8404\u5c71\u5317\u9762\u65f7\u50fb\u7684\u5c71\u5730\uff0c\u5c71\u5761\u4e0a\u6709\u4e00\u5e62 \u5f62\u5f0f\u7279\u522b\u7684\u5c4b\u5b57\uff0c\u5c4b\u4f5c\u5706\u5f62\uff0c\u6709\u5982\u7889\u5821\uff0c\u56db\u9762\u56f4\u6709\u56f4\u5899\uff0c\u8fd9\u4fbf\u662f\u9f99\u7075\u77eb\u7684\u4f4f\u5bb6\u3002\u5510\u7ecf\u5929\u53eb\u5e7d \u840d\u5728\u5916\u9762\u7b49\u5019\uff0c\u4ed6\u548c\u51b0\u5ddd\u5929\u5973\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u591c\u63a2\u9f99\u5bb6\u3002\u53ea\u89c1\u91cc\u9762\u4e00\u7247\u5bc2\u9759\uff0c\u4eba\u4eec\u60f3\u5fc5\u662f\u90fd \u5230\u5916\u9762\u51d1\u70ed\u95f9\u53bb\u4e86\u3002\u4ed6\u4eec\u6478\u5230\u4e86\u9f99\u7075\u77eb\u7684\u4e66\u623f\u5916\u9762\uff0c\u5ffd\u542c\u5f97\u91cc\u9762\u6709\u811a\u6b65\u58f0\u8e31\u6765\u8e31\u53bb\uff0c\u4e24\u4eba\u98de \u4e0a\u5c4b\u6a90\uff0c\u4f7f\u4e00\u4e2a\u201c\u73cd\u73e0\u5012\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u5411\u91cc\u7aa5\u671b\u3002\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u7684\u8f7b\u8eab\u529f\u592b\u597d\u5230\u6781 \u70b9\uff0c\u7aef\u7684\u5982\u4e00\u53f6\u98de\u5815\uff0c\u843d\u5904\u65e0\u58f0\u3002\u8fde\u9f99\u7075\u77eb\u7adf\u4e5f\u672a\u5bdf\u89c9\u3002\u4e24\u4eba\u95f4\u91cc\u4e00\u671b\uff0c\u53ea\u89c1\u9f99\u7075\u77eb\u597d\u4f3c\u795e \u9b42\u4e0d\u5c5e\u7684\u6837\u5b50\uff0c\u5728\u4e66\u623f\u91cc\u7ed5\u6765\u7ed5\u53bb\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u9041\u8ff9\u98ce\u5c18\u4eba\u4e0d\u8bc6\uff0c\u95ee\u541b\u4f55\u4e8b\u5374\u5173\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u95f9\u5e02\u5b64\u81e3\u3000\u795e\u9f99\u56fe\u5927\u4e8b\u3000\u5bd2\u5149\u70ed\u6d6a\u3000\u51b0\u5251\u6597\u5996\u90aa \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u4e09\u56de \u95f9\u5e02\u5b64\u81e3\u3000\u795e\u9f99\u56fe\u5927\u4e8b \u5bd2\u5149\u70ed\u6d6a\u3000\u51b0\u5251\u6597\u5996\u90aa \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u9f99\u8001\u4e09\u8fde\u65e5\u5954\u6ce2\uff0c\u800c\u4eca\u91d1\u74f6\u5df2\u5b89\u7136\u65e0\u4e8b\uff0c\u653e\u5230\u5927\u662d\u5bfa\u4e86\uff0c\u4ed6 \u8fd8\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u8fd8\u4e0d\u6b47\u606f\uff1f\u201d\u5ffd\u542c\u5f97\u95e8\u5916\u6709\u811a\u6b65\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u5c06\u8eab\u5b50\u4e00 \u7f29\uff0c\u9690\u4f0f\u5728\u5c4b\u6a90\u51f9\u69fd\u4e4b\u5904\uff0c\u53ea\u89c1\u95e8\u5e18\u63ed\u5904\uff0c\u4e00\u4e2a\u7626\u957f\u7684\u6c49\u5b50\u8d70\u4e86\u8fdb\u6765\uff0c\u4e43\u662f\u9f99\u7075\u77eb\u7684\u5e08\u5f1f\u989c \u6d1b\uff0c\u4ea6\u5373\u662f\u66fe\u7ecf\u65bd\u5c55\u7a7a\u7a7a\u5999\u624b\uff0c\u5077\u8fc7\u5e7d\u840d\u7684\u51b0\u9b44\u5bd2\u5149\u5251\u7684\u90a3\u4e2a\u4eba\u3002 \u3000\u3000\u9f99\u7075\u77eb\u5618\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e08\u5f1f\u8fd8\u6ca1\u7761\u4e48\uff1f\u201d\u989c\u6d1b\u9053\uff1a\u201c\u8fd9\u534a\u6708\u6765\u6211\u771f\u66ff\u5e08\u5144\u62c5\u5fc3\uff0c\u5982\u4eca \u53ef\u677e\u53e3\u6c14\u4e86\u3002\u201d\u9f99\u7075\u77eb\u82e6\u7b11\u9053\uff1a\u201c\u62ab\u4e0a\u8888\u88df\u4e8b\u66f4\u591a\uff0c\u91d1\u74f6\u5230\u540e\uff0c\u54b1\u4eec\u7684\u5927\u4e8b\u6b63\u5728\u5f00\u59cb\u5462\uff01\u201d \u989c\u6d1b\u9053\uff1a\u201c\u4f9d\u6211\u770b\u6765\uff0c\u54b1\u4eec\u8fd8\u662f\u6682\u65f6\u907f\u5f00\u7684\u597d\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4f60\u5bb3\u6015\u4e86\uff1f\u201d\u989c\u6d1b\u9053\uff1a\u201c\u4e0d \u662f\u5bb3\u6015\u3002\u4f46\u8fd9\u51e0\u65e5\u6765\uff0c\u6211\u603b\u4f3c\u611f\u5230\u4e00\u79cd\u9884\u5146\uff0c\u4f3c\u4e4e\u54b1\u4eec\u7684\u884c\u85cf\u5df2\u88ab\u4eba\u77a7\u7834\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a \u201c\u798f\u5927\u5e05\u4e5f\u6ca1\u534a\u70b9\u7591\u5fc3\uff0c\u4f60\u4e0d\u5fc5\u80e1\u601d\u4e71\u60f3\u3002\u201d\u989c\u6d1b\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u4f3c\u662f\u6b32\u8bf4\u8fd8\u4f11\u3002 \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u54b1\u4eec\u5341\u51e0\u5e74\u6765\uff0c\u5c48\u8eab\u5e55\u5e9c\uff0c\u4e3a\u7684\u4ec0\u4e48\uff1f\u773c\u770b\u76ee\u524d\u5df2\u6253\u4e0b\u4e86\u4e00\u70b9\u6839\u57fa\uff0c\u5c24\u5176 \u8fd9\u6b21\u7ecf\u8fc7\u6211\u7684\u7b56\u5212\uff0c\u5b89\u7136\u63a5\u5230\u91d1\u74f6\uff0c\u798f\u5927\u5e05\u66f4\u8981\u501a\u9760\u54b1\u4eec\uff0c\u5c31\u7b97\u6709\u4ec0\u4e48\u98ce\u6d6a\uff0c\u4e5f\u53ef\u5b89\u7136\u6e21 \u8fc7\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u201d\u989c\u6d1b\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d \u3000\u3000\u505c\u4e86\u4e00\u505c\uff0c\u9f99\u7075\u77eb\u7eed\u9053\uff1a\u201c\u6211\u53eb\u4f60\u4e0e\u5404\u4e2a\u571f\u53f8\u6253\u4ea4\u9053\uff0c\u8fdb\u884c\u5f97\u53ef\u597d\u4e48\uff1f\u201d\u989c\u6d1b\u9053\uff1a\u201c\u8fd8 \u597d\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5e55\u5e9c\u4e4b\u4e2d\u6709\u6211\uff0c\u8fd9\u6b21\u54b1\u4eec\u8981\u653e\u624b\u5e72\u5b83\u4e00\u573a\u3002\u201d\u989c\u6d1b\u9053\uff1a\u201c\u5927\u5e05\u5e9c\u4e2d\u660e\u65e5 \u4e00\u65e9\u4fbf\u8981\u4e3e\u884c\u56e2\u62dc\uff0c\u5e86\u8d3a\u65b0\u5e74\uff0c\u5e76\u6170\u52b3\u4eca\u6b21\u6709\u529f\u7684\u5c06\u58eb\uff0c\u5e08\u5144\uff0c\u4f60\u8fd8\u662f\u65e9\u70b9\u7761\u5427\u3002\u201d\u9f99\u7075\u77eb \u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u989c\u6d1b\u9053\uff1a\u201c\u660e\u65e5\u4e4b\u4f1a\uff0c\u5e08\u5144\u4f60\u662f\u8981\u89d2\uff0c\u6211\u8fd9\u4e9b\u95f2\u89d2\uff0c\u8fdf\u4e00\u70b9\u767b\u573a\u4e5f\u884c\uff0c\u6211\u8fd8 \u8981\u53bb\u5de1\u89c6\u4e00\u904d\u3002\u201d\u9f99\u7075\u77eb\u7b11\u9053\uff1a\u201c\u592a\u8fc7\u4e8e\u5c0f\u5fc3\u4e86\uff0c\u96be\u9053\u8fd8\u6709\u8c01\u6562\u6df7\u8fdb\u8fd9\u513f\u4e0d\u6210\uff1f\u201d\u989c\u6d1b\u4e5f\u7b11 \u9053\uff1a\u201c\u5e08\u5144\u8fd9\u4e48\u5feb\u5c31\u5fd8\u4e86\u6708\u524d\u4e4b\u4e8b\u4e86\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4e16\u4e0a\u80fd\u6709\u51e0\u4f4d\u51b0\u5ddd\u5929\u5973\uff1f\u4f55\u51b5\u51b0\u5ddd\u5929 \u5973\u4e5f\u4e0d\u662f\u5b58\u5fc3\u4e0e\u54b1\u4eec\u4e3a\u654c\u3002\u201d\u989c\u6d1b\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u8fd8\u662f\u5c0f\u5fc3\u7684\u597d\u3002\u201d\u4e0e\u5e08\u5144\u9053\u4e86\u4e00\u58f0\u5b89 \u6b47\uff0c\u4fbf\u81ea\u9000\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u5510\u7ecf\u5929\u542c\u4e86\u4ed6\u4fe9\u5144\u5f1f\u8fd9\u573a\u8c08\u8bdd\uff0c\u66f4\u662f\u7591\u56e2\u6ee1\u8179\uff0c\u4e0d\u77e5\u9f99\u7075\u77eb\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\uff0c\u8981\u5e72\u7684\u662f \u4ec0\u4e48\u4e8b\u60c5\uff1f\u5ffd\u542c\u5f97\u9f99\u7075\u77eb\u5728\u623f\u4e2d\u541f\u9053\uff1a\u201c\u63ed\u5730\u6380\u5929\u4e3a\u4e8b\u4e1a\uff0c\u7ffb\u6c5f\u5012\u6d77\u4f5c\u6587\u7ae0\u3002\u54c8\u54c8\uff0c\u8fd9\u91d1\u672c \u5df4\u74f6\u4e00\u5230\uff0c\u8be5\u662f\u6211\u5927\u663e\u8eab\u624b\u7684\u65f6\u5019\u4e86\u3002\u201d\u5510\u7ecf\u5929\u4e0d\u7981\u9a87\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u9f99\u8001\u4e09\u53e3\u6c14\u597d\u5927\uff0c\u83ab \u975e\u4ed6\u60f3\u9020\u53cd\u4e0d\u6210\uff0c\u53ea\u662f\u5728\u6b64\u65f6\u6b64\u5730\uff0c\u5c82\u5b9c\u9020\u53cd\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u6b63\u5728\u5fc3\u91cc\u7422\u78e8\uff0c\u5bf9\u4ed6\u7684\u8eab\u4efd\u5c1a\u672a\u5206\u660e\uff0c\u6b63\u62ff\u4e0d\u5b9a\u4e3b\u610f\u8981\u4e0d\u8981\u4e0b\u6765\u4e0e\u4ed6\u76f8\u89c1\uff0c\u5ffd\u542c \u5f97\u9662\u5b50\u5916\u8fb9\u4e00\u58f0\u5c16\u53eb\uff0c\u90a3\u662f\u989c\u6d1b\u7684\u53eb\u58f0\uff0c\u4f3c\u4e4e\u662f\u53d7\u5230\u4eba\u6697\u4e2d\u7684\u88ad\u51fb\uff0c\u9f99\u7075\u77eb\u5728\u623f\u4e2d\u4e00\u8dc3\u800c \u8d77\uff0c\u6b63\u60f3\u6380\u5e18\u8df3\u51fa\uff0c\u90a3\u5c16\u53eb\u4e4b\u58f0\u5c1a\u672a\u505c\u6b62\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u602a\u7b11\uff0c\u7d27\u63a5\u800c\u6765\uff0c\u7b11\u58f0\u521d\u8d77\u65f6\uff0c\u4f3c\u5728 \u51e0\u95ee\u5c4b\u5916\uff0c\u500f\u5ffd\u4fbf\u5230\u4e86\u9762\u524d\uff0c\u7aef\u7684\u662f\u58f0\u5230\u4eba\u5230\uff0c\u5feb\u901f\u65e0\u6bd4\uff01 \u3000\u3000\u4ee5\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u6817\uff0c\u987b\u77e5\u989c\u6d1b\u4e0e\u9f99\u7075\u77eb\u4e43\u662f\u5e08\u5144\u5f1f\uff0c \u989c\u6d1b\u529f\u592b\u867d\u7136\u900a\u4e8e\u5e08\u5144\uff0c\u5728\u6b66\u6797\u4e2d\u4e5f\u7b97\u5f97\u662f\u4e00\u6d41\u4eba\u7269\uff0c\u6765\u4eba\u7adf\u7136\u80fd\u5728\u77ac\u606f\u4e4b\u95f4\u5c06\u4ed6\u51fb\u5012\uff0c\u8fd9 \u4efd\u8eab\u624b\uff0c\u7aef\u7684\u60ca\u4eba\uff0c\u800c\u4e14\u542c\u4ed6\u7b11\u58f0\u672a\u505c\uff0c\u8eab\u5f62\u5df2\u73b0\uff0c\u8fd9\u4efd\u8f7b\u529f\u7adf\u4e5f\u4e0e\u51b0\u5ddd\u5929\u5973\u5728\u4f2f\u4ef2\u4e4b\u95f4\u3002 \u3000\u3000\u5510\u7ecf\u5929\u638c\u5fc3\u6263\u4e86\u4e24\u652f\u5929\u5c71\u795e\u8292\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u62c8\u51fa\u4e24\u679a\u51b0\u9b44\u795e\u5f39\u3002\u5510\u7ecf\u5929\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c \u793a\u610f\u53eb\u51b0\u5ddd\u5929\u5973\u6682\u65f6\u9690\u5fcd\uff0c\u53ea\u89c1\u90a3\u9ed1\u5f71\u4e00\u6e9c\u70df\u4f3c\u7684\u76f4\u95ef\u8fdb\u6765\uff0c\u6b63\u9047\u7740\u9f99\u7075\u77eb\u6380\u5e18\u800c\u51fa\uff0c\u9aa4\u542c \u5f97\u6323\u6323\u6570\u58f0\uff0c\u94f6\u5149\u56db\u5c04\uff0c\u90a3\u9ed1\u5f71\u500f\u5730\u505c\u4f4f\uff0c\u602a\u58f0\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u201c\u516b\u81c2\u54ea\u54a4\u62db\u5b9d\u2019\u7684\u7edd\u6280 \u5440\uff01\u4f60\u7684\u5e08\u7236\u662f\u56db\u5ddd\u5510\u8001\u4e8c\u4e48\uff1f\u201d\u6de1\u6708\u758f\u661f\u4e4b\u4e0b\uff0c\u7a33\u7ea6\u770b\u5230\u90a3\u9ed1\u5f71\u662f\u4e2a\u7626\u957f\u7684\u6c49\u5b50\uff0c\u9762\u988a\u6df1 \u9690\uff0c\u53cc\u775b\u5982\u706b\uff0c\u5934\u53d1\u4f3c\u4e00\u84ec\u4e71\u8349\uff0c\u72f0\u72de\u6015\u4eba\u3002 \u3000\u3000\u5510\u7ecf\u5929\u597d\u751f\u8be7\u5f02\uff0c\u8fd9\u602a\u5ba2\u53d1\u7684\u4e43\u662f\u4e00\u79cd\u6b79\u6bd2\u7684\u6697\u5668\u4e09\u68f1\u900f\u9aa8\u9489\uff0c\u4e13\u6253\u4eba\u8eab\u7a74\u9053\uff0c\u8fd9\u5c1a\u4e0d \u8db3\u4e3a\u5947\uff0c\u5947\u602a\u7684\u662f\u9f99\u7075\u77eb\u63a5\u6697\u5668\u7684\u624b\u6cd5\uff0c\u4ed6\u4e00\u62db\u4e4b\u95f4\uff0c\u4fbf\u5c06\u516d\u679a\u900f\u9aa8\u9489\u5168\u90fd\u6536\u53bb\uff0c\u8fd9\u6b63\u662f\u56db \u5ddd\u6697\u5668\u540d\u5bb6\u5510\u5bb6\u7684\u624b\u6cd5\u3002\u5510\u7ecf\u5929\u542c\u7236\u4eb2\u8bf4\u8fc7\uff0c\u5510\u5bb6\u4e0a\u4e00\u8f88\u6709\u4e00\u4e2a\u4eba\u540d\u53eb\u5510\u91d1\u5cf0\uff0c\u6392\u884c\u7b2c\u4e8c\uff0c \u4eba\u79f0\u4e8c\u90ce\u795e\u5510\u8001\u4e8c\uff0c\u5f53\u5e74\u4ee5\u4e00\u5f20\u5f39\u5f13\u79f0\u9738\u6c5f\u6e56\uff0c\u8fd9\u602a\u5ba2\u6240\u6307\u7684\u201c\u56db\u5ddd\u5510\u8001\u4e8c\u201d\u5f53\u662f\u5510\u91d1\u5cf0\u65e0 \u7591\uff0c\u4f46\u8bba\u8d77\u5e74\u9f84\uff0c\u5510\u91d1\u5cf0\u82e5\u7136\u8fd8\u5728\uff0c\u4ea6\u5f53\u5728\u516b\u5341\u5f00\u5916\uff0c\u96be\u9053\u9f99\u7075\u77eb\u7adf\u662f\u4ed6\u7684\u5f1f\u5b50\uff1f\u800c\u8fd9\u4f4d\u602a \u5ba2\u7ade\u662f\u4ed6\u7684\u5e73\u8f88\uff1f \u3000\u3000\u53ea\u89c1\u9f99\u7075\u77eb\u62e2\u8896\u4e00\u63d6\uff0c\u606d\u8c28\u7b54\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u5e08\u3002\u6562\u95ee\u8001\u524d\u8f88\u6b64\u6765\uff0c\u6709\u4f55\u6307\u6559\uff1f\u201d\u90a3\u602a\u5ba2 \u53c8\u53d1\u51fa\u602a\u7b11\u9053\uff1a\u201c\u4f60\u5728\u6f20\u5916\u5341\u5e74\uff0c\u7adf\u4e5f\u4e0d\u77e5\u9053\u6211\u662f\u8c01\u4e48\uff1f\u201d\u500f\u5730\u5c06\u624b\u638c\u4e3e\u8d77\uff0c\u5728\u9f99\u7075\u77eb\u9762\u524d \u4e00\u6643\uff0c\u90a3\u624b\u638c\u9c9c\u7ea2\u5982\u8840\uff0c\u597d\u50cf\u5265\u5f00\u4e86\u76ae\u4e00\u6837\uff0c\u5728\u6de1\u6de1\u7684\u6708\u5149\u4e4b\u4e0b\u5206\u5916\u9c9c\u660e\uff0c\u5510\u7ecf\u5929\u8fd9\u4e00\u60ca\u975e \u540c\u5c0f\u53ef\uff0c\u53ea\u542c\u5f97\u9f99\u7075\u77eb\u5728\u4e0b\u9762\u5df2\u53eb\u51fa\u58f0\u6765\uff1a\u201c\u539f\u6765\u662f\u8840\u795e\u5b50\u524d\u8f88\u6765\u5230\uff0c\u8bf7\u6055\u665a\u8f88\u65e0\u77e5\uff0c\u6709\u5931 \u8fdc\u8fd1\u3002\u201d \u3000\u3000\u8fd9\u8840\u795e\u5b50\u662f\u9690\u5c45\u5728\u5eb7\u85cf\u8fb9\u5883\u4e4b\u95f4\u7684\u4e00\u4e2a\u8001\u9b54\u5934\uff0c\u6240\u7ec3\u7684\u529f\u592b\u602a\u5f02\u4e4b\u6781\uff0c\u8981\u5c06\u56db\u80a2\u7684\u76ae\u80a4 \u5265\u53bb\uff0c\u7528\u4e00\u79cd\u6bd2\u8349\u71ac\u6c41\u6d17\u70bc\uff0c\u6545\u6b64\u624b\u8db3\u90fd\u662f\u9c9c\u7ea2\u5982\u8840\uff0c\u89e6\u4eba\u5373\u6b7b\u3002\u5f53\u5e74\u6c5f\u6e56\u4e0a\u7684\u9ed1\u9053\u767d\u9053\uff0c \u5168\u90fd\u6015\u4ed6\u51e0\u5206\uff0c\u5927\u5bb6\u79f0\u4ed6\u4e3a\u201c\u8840\u795e\u5b50\u201d\uff0c\u771f\u5b9e\u7684\u59d3\u540d\u53cd\u800c\u4e0d\u4f20\u3002\u5510\u7ecf\u5929\u7684\u7236\u4eb2\u5510\u6653\u6f9c\u51fa\u9053\u4e4b \u65f6\uff0c\u4ed6\u5df2\u5728\u897f\u5317\u4e00\u5e26\u6a2a\u884c\uff0c\u540e\u6765\u5ffd\u7136\u6d88\u58f0\u533f\u8ff9\uff0c\u636e\u4f20\u8bf4\u662f\u53d7\u4e86\u5f53\u5e74\u5929\u5c71\u4e03\u5251\u4e4b\u4e00\u3001\u5973\u4fa0\u6b66\u743c \u7476\u7684\u60e9\u6212\uff0c\u8be6\u7ec6\u60c5\u5f62\uff0c\u5374\u662f\u65e0\u4eba\u77e5\u9053\u3002\u5510\u6653\u6f9c\u5f52\u9690\u5929\u5c71\u5341\u4f59\u5e74\uff0c\u5929\u5c71\u516d\u5251\u76f8\u7ee7\u901d\u4e16\uff0c\u8fde\u6700\u540e \u7684\u4e24\u4e2a\u5973\u4fa0\u6613\u5170\u73e0\u548c\u6b66\u743c\u7476\u4e5f\u90fd\u6b7b\u4e86\uff0c\u8840\u795e\u5b50\u624d\u5076\u4e00\u9732\u9762\u3002\u5510\u7ecf\u5929\u66fe\u542c\u7236\u4eb2\u9053\u53ca\uff0c\u8bf4\u662f\u8840\u795e \u5b50\u66fe\u7ea6\u8fc7\u4ed6\u5230\u5df4\u989c\u5580\u62c9\u5c71\u7684\u5357\u9ad8\u5cf0\u6bd4\u6b66\uff0c\u4ed6\u4e0d\u613f\u4e0e\u5916\u6d3e\u5996\u90aa\u4e89\u80dc\uff0c\u7f6e\u4e4b\u4e0d\u7406\uff0c\u8840\u795e\u5b50\u906d\u5230\u62d2 \u7edd\uff0c\u4e5f\u6ca1\u6709\u53bb\u627e\u4ed6\uff0c\u60f3\u4e0d\u5230\u6b64\u4eba\u5374\u5728\u4eca\u591c\u51fa\u73b0\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8840\u795e\u5b50\u53c8\u602a\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u9053\u6211\u662f\u8c01\uff0c\u5c31\u8be5\u4e56\u4e56\u7684\u542c\u6211\u5429\u5490\uff0c\u4f60\u5728\u897f\u85cf\u5341\u591a\u5e74\uff0c \u5e72\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e00\u4e00\u4ece\u5b9e\u62db\u6765\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6211\u5341\u591a\u5e74\u6765\u5728\u798f\u5927\u5e05\u5e10\u4e0b\u4f5c\u5e55\uff0c\u6240\u505a\u7684\u4e8b \u60c5\uff0c\u798f\u5927\u5e05\u5168\u90e8\u77e5\u9053\uff0c\u8001\u524d\u8f88\u82e5\u7136\u4fe1\u6211\u4e0d\u8fc7\uff0c\u53ef\u4ee5\u53bb\u95ee\u798f\u5927\u5e05\u3002\u201d\u8840\u795e\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u62ff\u798f \u5eb7\u5b89\u5413\u6211\u5417\uff1f\u4f60\u7792\u5f97\u8fc7\u798f\u5eb7\u5b89\uff0c\u53ef\u7792\u4e0d\u8fc7\u4e5d\u91cd\u5929\u5b50\uff0c\u4f60\u66f4\u540d\u6539\u59d3\uff0c\u5c31\u4ee5\u4e3a\u6ca1\u4eba\u77e5\u9053\u4e86\u5417\uff1f\u201d \u3000\u3000\u9f99\u7075\u77eb\u5403\u4e86\u4e00\u60ca\uff0c\u5374\u4ecd\u662f\u9547\u9759\u95ee\u9053\uff1a\u201c\u6211\u4e0d\u660e\u767d\u8001\u524d\u8f88\u8bf4\u7684\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u6211\u597d\u7aef\u7aef\u7684\u53c8 \u672a\u66fe\u72af\u7f6a\uff0c\u4e3a\u4f55\u8981\u66f4\u540d\u6539\u59d3\u3002\u201d\u5510\u91d1\u5cf0\u5df2\u6b7b\u4e86\u5341\u591a\u5e74\uff0c\u8840\u795e\u5b50\u53ea\u67e5\u5230\u9f99\u7075\u77eb\u662f\u4ed6\u7684\u5f1f\u5b50\uff0c\u5374 \u8fd8\u672a\u67e5\u5230\u9f99\u7075\u77eb\u7684\u6765\u5386\uff0c\u89c1\u9f99\u7075\u77eb\u77e2\u53e3\u4e0d\u8ba4\uff0c\u62ff\u4ed6\u65e0\u6cd5\uff0c\u5fc3\u4e2d\u706b\u8d77\uff0c\u4e0d\u7406\u5927\u5185\u603b\u7ba1\u6240\u4f20\u7684\u8981 \u4ed6\u8c28\u614e\u4ece\u4e8b\u7684\u5fa1\u65e8\uff0c\u7acb\u5373\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u63a8\u5f97\u5012\u597d\u5e72\u51c0\uff0c\u597d\u5427\uff0c\u4f60\u7acb\u5373\u8ddf\u6211\u8d70\uff0c\u6709\u7f6a\u65e0 \u7f6a\uff0c\u81ea\u7136\u6709\u4eba\u7ed9\u4f60\u5224\u5b9a\u3002\uff0c\uff0c\u9f99\u7075\u77eb\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u8ddf\u4f60\u8d70\uff0c\u8fd9\u53ef\u5f97\u95ee\u8fc7\u798f\u5927\u5e05\u3002\u201d\u8840\u795e\u5b50\u6012 \u9053\uff1a\u201c\u4f60\u62ff\u798f\u5eb7\u5b89\u4f5c\u62a4\u7b26\u5417\uff0c\u798f\u5eb7\u5b89\u4e5f\u672a\u5fc5\u62a4\u5f97\u4e86\u4f60\uff0c\u4f60\u542c\u4e0d\u542c\u6211\u7684\u5429\u5490\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a \u201c\u665a\u8f88\u5e76\u975e\u6562\u6297\u4f60\u8001\u4e4b\u547d\uff0c\u53ea\u662f\u804c\u5b88\u5728\u8eab\uff0c\u4e0d\u6562\u64c5\u79bb\u3002\u201d\u8840\u795e\u5b50\u9053\uff1a\u201c\u4f60\u8fd9\u829d\u9ebb\u7eff\u8c46\u7684\u5b98 \u513f\uff0c\u968f\u65f6\u53ef\u4ee5\u9769\u6389\uff0c\u4f60\u795e\u6c14\u4ec0\u4e48\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5c31\u662f\u9769\u6389\uff0c\u4e5f\u5f97\u6709\u6b63\u5f0f\u7684\u6587\u4e66\uff0c\u6216\u8005\u662f\u798f \u5927\u5e05\u7684\u624b\u4ee4\u3002\u5927\u6e05\u5f8b\u4f8b\uff0c\u4e00\u5207\u5927\u5c0f\u5b98\u5458\uff0c\u975e\u7ecf\u4e0a\u5cf0\u5dee\u9063\uff0c\u4e0d\u5f97\u64c5\u79bb\u804c\u5b88\u3002\u6b63\u56e0\u4e3a\u6211\u662f\u4e2a\u5c0f\u5b98 \u513f\uff0c\u66f4\u4e0d\u6562\u4ee5\u8eab\u8bd5\u6cd5\u3002\u8840\u795e\u5b50\u5927\u6012\u9053\uff1a\u201c\u4f60\u5de6\u4e00\u53e5\u798f\u5927\u5e05\uff0c\u53f3\u4e00\u53e5\u798f\u5927\u5e05\uff0c\u5c3d\u548c\u6211\u6253\u5b98\u8154\uff0c \u54fc\uff0c\u4f60\u5f53\u6211\u8840\u795e\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u6211\u4e0d\u7406\u4f60\u4ec0\u4e48\u5927\u5e05\uff0c\u4ec0\u4e48\u5f8b\u4f8b\uff0c\u4f60\u4eca\u665a\u82e5\u4e0d\u8ddf\u6211\u8d70\uff0c\u53ef\u662f\u81ea\u5df1 \u627e\u82e6\u6765\u53d7\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6211\u5b81\u613f\u53d7\u8001\u524d\u8f88\u8d23\u7f5a\uff0c\u4e5f\u4e0d\u6562\u5192\u72af\u7687\u6cd5\u3002\u201d\u8840\u795e\u5b50\u7a81\u7136\u51b7\u7b11\u9053\uff1a \u201c\u7687\u6cd5\uff0c\u6211\u5c31\u662f\u7687\u6cd5\uff01\u500f\u5730\u4f38\u51fa\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u5411\u9f99\u7075\u77eb\u6402\u5934\u4e00\u6293\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u65e9\u6709\u9632\u5907\uff0c\u957f\u8896\u4e00\u6325\uff0c\u5411\u8840\u795e\u5b50\u624b\u638c\u4e00\u5377\uff0c\u7acb\u5373\u907f\u5f00\uff0c\u8fd9\u4e00\u624b\u662f\u201c\u6d41\u4e91\u98de\u8896\u201d\u7684 \u7edd\u62db\uff0c\u6697\u85cf\u5185\u529b\uff0c\u4fca\u5de7\u975e\u5e38\uff0c\u53ea\u542c\u5f97\u8840\u795e\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u5c31\u51ed\u5510\u8001\u4e8c\u4f20\u4f60\u8fd9\u51e0\u624b\u4e09\u811a\u732b \u7684\u529f\u592b\uff0c\u5c31\u5c45\u7136\u6562\u4e0e\u6211\u52a8\u624b\u52a8\u811a\u4e86\uff1f\u201d\u624b\u638c\u4e00\u7ffb\uff0c\u4ece\u53cc\u8896\u7ffb\u5377\u4e4b\u4e2d\u817e\u4e86\u51fa\u6765\uff0c\u9f99\u7075\u77eb\u8eab\u6cd5\u5df2 \u5feb\uff0c\u4ed6\u7684\u8eab\u6cd5\u66f4\u5feb\uff0c\u7adf\u5982\u95ea\u7535\u822c\u7684\u4e00\u95ea\u5373\u5230\uff0c\u5728\u76f8\u8ddd\u4e08\u8bb8\u4e4b\u5904\u51fa\u638c\uff0c\u62db\u6570\u521a\u5c55\uff0c\u638c\u950b\u4fbf\u62cd\u5230 \u9f99\u7075\u77eb\u80f8\u524d\uff0c\u9f99\u7075\u77eb\u817e\u632a\u95ea\u907f\uff0c\u4e0d\u6562\u53eb\u4ed6\u7684\u638c\u950b\u6cbe\u4e0a\uff0c\u597d\u5bb9\u6613\u95ea\u907f\u4e86\u516d\u4e03\u62db\uff0c\u5510\u7ecf\u5929\u548c\u51b0\u5ddd \u5929\u5973\u5df2\u542c\u5f97\u4ed6\u5fae\u5fae\u6c14\u5598\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u597d\u751f\u8be7\u5f02\uff0c\u770b\u9f99\u7075\u77eb\u7684\u529f\u592b\uff0c\u867d\u7136\u8fdc\u4e0d\u53ca\u8840\u795e\u5b50\uff0c\u4f46\u6700\u5c11\u4ea6\u8981\u6321\u4ed6\u4e09\u4e94\u5341\u62db\uff0c \u9f99\u7075\u77eb\u7684\u638c\u6cd5\u7ef5\u5bc6\u4e4b\u6781\uff0c\u867d\u5904\u4e0b\u98ce\uff0c\u672a\u9732\u8d25\u8c61\uff0c\u4e0d\u77e5\u4f55\u4ee5\u4fbf\u4f1a\u6c14\u5598\u5982\u725b\uff0c\u5b9e\u662f\u83ab\u540d\u5176\u5999\uff0c\u51b0 \u5ddd\u5929\u5973\u770b\u4e86\u4e00\u9635\uff0c\u4e0d\u7981\u5fae\u5fae\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8840\u795e\u5b50\u201c\u563f\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u7ea6\u6709\u80fd\u4eba\u5728\u6b64\u57cb\u4f0f\uff0c\u597d\u5440\uff0c\u90fd\u4e0b\u6765\u5427\uff01\u201d \u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u662f\u6beb\u4e0d\u653e\u677e\uff0c\u638c\u98ce\u4eba\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u9f99\u7075\u77eb\u7684\u9a6c\u8e44\u8896\u7adf\u88ab \u4ed6\u63d0\u53bb\u4e00\u622a\uff0c\u201c\u6d41\u4e91\u98de\u8896\u201d\u7684\u62db\u6570\u767b\u65f6\u7834\u4e86\uff0c\u9f99\u7075\u77eb\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u8fde\u540e\u9000\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c \u5f97\u4e00\u58f0\u5a07\u7b11\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u5df2\u4ece\u5c4b\u6a90\u4e0a\u8df3\u4e86\u4e0b\u6765\uff0c\u9f99\u7075\u77eb\u559c\u51fa\u671b\u5916\uff0c\u5446\u5728\u5f53\u573a\u3002 \u3000\u3000\u8840\u795e\u5b50\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u51b0\u5ddd\u5929\u5973\u660e\u8273\u7167\u4eba\uff0c\u7f9e\u82b1\u95ed\u6708\uff0c\u8840\u795e\u5b50\u63c9\u63c9\u773c\u775b\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u4e16\u95f4 \u7adf\u7136\u6709\u8fd9\u6837\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u51b0\u5ddd\u5929\u5973\u53cc\u6307\u4e00\u5f39\uff0c\u53f1\u9053\uff1a\u201c\u770b\u4ec0\u4e48\uff0c\u5148\u6253\u778e\u4f60\u7684\u72d7\u773c\uff01\u201d\u8840\u795e\u5b50 \u6b63\u5728\u5446\u770b\uff0c\u5ffd\u89c1\u4e24\u70b9\u5bd2\u5149\u7535\u5c04\u800c\u81f3\uff0c\u51b7\u6c14\u6c81\u5165\u773c\u5e18\uff0c\u8840\u795e\u5b50\u4e5f\u771f\u4e86\u5f97\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u53ea\u89c1 \u4ed6\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u5de6\u624b\u4e00\u6284\uff0c\u5c31\u7c91\u4e24\u679a\u51b0\u9b44\u795e\u5f39\u63a5\u5728\u624b\u4e2d\uff0c\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u51b0\u6c34\u4ece\u4ed6 \u6307\u7f1d\u6ef4\u4e0b\uff0c\u4ed6\u6325\u638c\u4e00\u6d12\uff0c\u5de6\u638c\u4e00\u8d77\uff0c\u76f8\u8ddd\u4e08\u8bb8\uff0c\u638c\u950b\u5374\u500f\u5730\u4fbf\u62cd\u5230\u51b0\u5ddd\u5929\u5973\u80f8\u524d\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4f55\u7b49\u529f\u529b\uff0c\u5979\u6240\u53d1\u7684\u51b0\u9b44\u795e\u5f39\u5373\u7b97\u5510\u7ecf\u5929\u4e0e\u9f99\u7075\u77ac\u7b49\u8f88\u4e5f\u4e0d\u6562\u786c\u63a5\uff0c\u800c\u4eca\u8840\u795e \u5b50\u63a5\u4e86\u5c45\u7136\u65e0\u4e8b\uff0c\u8fd8\u80fd\u8fc5\u901f\u51fa\u62db\uff0c\u6c34\u5ddd\u5929\u5973\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5ffd\u89c1\u773c\u524d\u7ea2\u5f71\u95ea\u52a8\uff0c\u8840\u795e\u5b50\u901a \u7ea2\u5982\u8840\u7684\u624b\u638c\u5df2\u62cd\u5230\u8ddf\u524d\uff0c\u51fa\u62db\u5982\u7535\uff0c\u638c\u52bf\u98d8\u5ffd\uff0c\u8fd9\u4e5f\u8fd8\u7f62\u4e86\uff0c\u6700\u9a87\u4eba\u7684\u662f\uff0c\u4ed6\u638c\u631f\u52b2\u98ce\uff0c \u70ed\u547c\u547c\u7684\uff0c\u7adf\u4f3c\u9f13\u98ce\u7089\u4e2d\u55b7\u51fa\u7684\u4e00\u822c\u70ed\u98ce\u3002\u51b0\u5ddd\u5929\u5973\u987f\u611f\u547c\u5438\u4e0d\u7545\uff0c\u6025\u5fd9\u4f7f\u4e00\u4e2a\u201c\u98ce\u522e\u843d \u82b1\u201d\u7684\u8eab\u6cd5\uff0c\u8fde\u95ea\u4e09\u62db\uff0c\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u5996\u50e7\uff01\u4e14\u53eb\u4f60\u4e5f\u89c1\u8bc6\u6211\u7684\u5b9d\u5251\uff01\u201d\u8840\u795e\u5b50\u8fde\u53d1\u4e09\u638c\uff0c \u8fde\u5979\u7684\u8863\u88f3\u4e5f\u6cbe\u4e0d\u7740\uff0c\u597d\u751f\u8be7\u5f02\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u4e00\u4e2a\u7ffb\u8eab\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u5df2\u62d4\u5728\u624b\u4e2d\uff0c\u5251\u950b\u4e00 \u6307\uff0c\u4e00\u9053\u5bd2\u5149\uff0c\u631f\u7740\u523a\u9aa8\u7684\u5bd2\u6c14\u3002\u767b\u65f6\u5c04\u5230\u8840\u795e\u5b50\u7684\u9762\u95e8\uff01 \u3000\u3000\u8840\u795e\u5b50\u5413\u4e86\u4e00\u8df3\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u70ed\u98ce\u51b7\u6c14\uff0c\u4e92\u76f8\u62b5\u6d88\uff0c\u500f\u7136\u4e4b\u95f4\uff0c\u6597\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u5404\u81ea \u65e0\u4e8b\uff0c\u8840\u795e\u4e8e\u81ea\u4ece\u4e09\u5341\u5e74\u524d\u88ab\u6b66\u743c\u7476\u6253\u8d25\u4e4b\u540e\uff0c\u4eca\u756a\u521d\u9022\u52b2\u654c\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u54c8\u54c8\u602a\u7b11\u9053\uff1a \u201c\u5999\u6781\u4e86\uff0c\u5999\u6781\u4e86\uff0c\u6211\u6b63\u70ed\u5f97\u96be\u53d7\uff0c\u96be\u4e3a\u4f60\u7389\u624b\u6325\u51c9\uff0c\u7ed9\u6211\u89e3\u70ed\uff01\u201d\u51b0\u5ddd\u5929\u5973\u5927\u6012\uff0c\u4e00\u67c4\u51b0 \u9b44\u5bd2\u5149\u5251\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff0c\u5979\u7684\u5251\u6cd5\u4ee5\u6b66\u6797\u7f55\u89c1\u7684\u8fbe\u6469\u5251\u6cd5\u4e3a\u57fa\u7840\uff0c\u63ba\u4ee5\u897f\u6b27\u53ca\u963f\u62c9\u4f2f\u7684\u5251 \u672f\uff0c\u5947\u8be1\u65e0\u6bd4\uff0c\u5965\u5999\u83ab\u540d\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u8840\u795e\u5b50\u88ab\u5979\u4e00\u9635\u5f3a\u653b\uff0c\u4e0d\u6562\u518d\u884c\u8bf4\u7b11\uff0c\u6697 \u81ea\u7384\u529f\u9ed8\u8fd0\uff0c\u5c06\u638c\u529b\u70ed\u98ce\u9010\u6e10\u52a0\u5f3a\uff0c\u4e24\u53ea\u817f\u597d\u50cf\u9489\u7262\u5728\u5730\u4e0a\u4e00\u822c\uff0c\u4efb\u51b0\u5ddd\u5929\u5973\u7684\u5251\u52bf\u6709\u5982\u60ca \u6d9b\u9a87\u6d6a\uff0c\u8fde\u756a\u731b\u5377\u3001\u4ed6\u7adf\u4e0d\u79fb\u52a8\u534a\u6b65\u3002\u53c8\u6218\u4e86\u4e00\u523b\uff0c\u8840\u795e\u5b50\u7f13\u4e86\u53e3\u6c14\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff01\u4f60\u80fd\u63a5 \u5230\u6211\u4e94\u5341\u62db\u4ee5\u4e0a\uff0c\u540e\u8f88\u4e4b\u4e2d\u7b97\u4f60\u7b2c\u4e00\u4e86\u3002\u4f60\u662f\u4f55\u4eba\uff1f\u5e08\u7236\u662f\u8c01\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u770b\u4f60\u4fee\u5230\u4eca\u65e5\uff0c\u4ea6\u975e\u5bb9\u6613\uff0c\u5feb\u5feb\u6eda\u5f00\uff0c\u4f11\u5f97\u591a\u4e8b\uff01\u201d\u8bf4\u8bdd\u9488\u950b\u76f8\u5bf9\uff0c\u534a\u70b9\u4e0d\u8ba9\u3002 \u3000\u3000\u8840\u795e\u5b50\u559d\u9053\uff1a\u201c\u5c0f\u599e\u513f\u4e0d\u77e5\u597d\u574f\uff0c\u7956\u5e08\u7236\u6709\u610f\u9976\u4f60\u6027\u547d\uff0c\u4f60\u5374\u6562\u4e0e\u6211\u9876\u649e\uff01\u201d\u638c\u6cd5\u4e00 \u53d8\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u7a81\u7136\u8f6c\u5b88\u4e3a\u653b\u3002\u51b0\u5ddd\u5929\u5973\u611f\u5230\u4ed6\u638c\u529b\u8d8a\u6765\u8d8a\u4e3a\u6c89\u91cd\uff0c\u867d\u7136\u8fd8 \u80fd\u5e94\u4ed8\uff0c\u989d\u5934\u5374\u5df2\u5fae\u5fae\u6c81\u51fa\u9999\u6c57\u3002 \u3000\u3000\u5728\u51b0\u5ddd\u5929\u5973\u4e0e\u8840\u795e\u5b50\u6076\u6597\u4e4b\u65f6\uff0c\u5510\u7ecf\u5929\u5374\u5c06\u9f99\u7075\u77eb\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u95ee\u9053\uff1a\u201c\u9f99\u4e09\u5148\u751f\uff0c \u4f60\u7aef\u7684\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u201d\u9f99\u7075\u77eb\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u76f8\u4fe1\u6211\u5417\uff1f\u4f60\u5c06\u90a3\u5757\u6c49\u7389\u4ea4\u4e0e\u4f60\u7684\u7236 \u4eb2\uff0c\u4ed6\u81ea\u7136\u4f1a\u77e5\u9053\u6211\u7684\u6765\u5386\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u5e76\u975e\u8981\u5411\u4f60\u67e5\u6839\u95ee\u5e95\uff0c\u6e05\u5bab \u5bf9\u4f60\u751a\u4e3a\u6ce8\u610f\uff0c\u6d3e\u6765\u7f09\u62ff\u4f60\u7684\u9ad8\u624b\u4e0d\u6b62\u8840\u795e\u5b50\u4e00\u4eba\uff0c\u4f60\u82e5\u771f\u662f\u5728\u897f\u85cf\u6709\u6240\u56fe\u8c0b\uff0c\u72af\u4e86\u2018\u5927 \u7f6a\u2019\uff0c\u90a3\u4e48\u8d81\u73b0\u5728\u7684\u65f6\u673a\uff0c\u8d76\u5feb\u9003\u8dd1\uff0c\u8fd8\u6765\u5f97\u53ca\uff01\u8840\u795e\u5b50\u8fd9\u5e72\u4eba\u6709\u6211\u4eec\u66ff\u4f60\u963b\u6321\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u773c\u73e0\u8f6c\u4e86\u51e0\u4e0b\uff0c\u4f3c\u662f\u5fc3\u4e2d\u6b63\u5728\u59d4\u51b3\u4e0d\u4e0b\uff0c\u5ffd\u7136\u7d27\u63e1\u5510\u7ecf\u5929\u7684\u624b\uff0c\u9053\uff1a\u201c\u5510\u5144\u5f1f\uff0c \u591a\u8c22\u4f60\u5566\uff0c\u6211\u4e0d\u80fd\u8d70\uff0c\u4f60\u4eec\u4e0d\u5fc5\u7ba1\u6211\u3002\u201d\u5510\u7ecf\u5929\u89c1\u4ed6\u8a00\u8f9e\u95ea\u70c1\uff0c\u6001\u5ea6\u6a21\u7cca\uff0c\u597d\u751f\u7591\u60d1\uff0c\u5bf9\u9f99 \u7075\u77eb\u5b9e\u662f\u6349\u6478\u4e0d\u900f\uff0c\u82e5\u8bf4\u4ed6\u662f\u4fa0\u4e49\u4e2d\u4eba\uff0c\u897f\u5317\u7fa4\u8c6a\u5374\u65e0\u4e00\u4eba\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff1b\u82e5\u8bf4\u4ed6\u662f\u6b7b\u5fc3\u584c \u5730\u6276\u52a9\u798f\u5eb7\u5b89\uff0c\u4ed6\u5374\u6697\u4e2d\u6d3e\u5e08\u5f1f\u53bb\u8054\u7edc\u897f\u85cf\u7684\u5404\u4e2a\u571f\u53f8\uff1b\u82e5\u8bf4\u4ed6\u662f\u53d7\u5916\u90a6\u6307\u4f7f\uff0c\u60f3\u5728\u897f\u85cf\u6405 \u8d77\u53db\u4e71\uff0c\u4ed6\u5374\u53c8\u6781\u529b\u4fdd\u62a4\u91d1\u672c\u5df4\u74f6\uff1b\u82e5\u8bf4\u4ed6\u662f\u80f8\u6000\u5927\u5fd7\uff0c\u60f3\u628a\u897f\u85cf\u4f5c\u4e3a\u6297\u6e05\u7684\u57fa\u5730\uff0c\u5219\u65f6\u5730 \u5747\u4e0d\u9002\u5b9c\uff0c\u5510\u7ecf\u5929\u767e\u601d\u4e0d\u89e3\uff0c\u5bf9\u9f99\u7075\u77eb\u7684\u5e95\u7ec6\u6478\u4e0d\u6e05\u695a\uff0c\u5bf9\u4ed6\u7a76\u7adf\u5e94\u91c7\u53d6\u4f55\u79cd\u6001\u5ea6\uff0c\u4e00\u65f6\u4e4b \u95f4\uff0c\u4e5f\u5c31\u96be\u4ee5\u51b3\u65ad\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u60f3\u518d\u8bbe\u6cd5\u5957\u95ee\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u4e0e\u8840\u795e\u5b50\u4e92\u76f8\u8ffd\u9010\uff0c\u4f60\u5288\u4e00\u638c\uff0c\u6211\u523a\u4e00\u5251\uff0c\u5154\u8d77 \u9e3d\u843d\uff0c\u7535\u63a3\u98ce\u9a70\uff0c\u90a3\u5ead\u9662\u4e0d\u8fc7\u4e09\u4e08\u89c1\u65b9\uff0c\u4e24\u4e2a\u4eba\u7a7f\u68ad\u6765\u5f80\uff0c\u638c\u98ce\u5251\u5f71\uff0c\u6b64\u53bb\u5f7c\u6765\uff0c\u5c31\u50cf\u6570\u5341 \u767e\u4eba\u5728\u6218\u573a\u4e0a\u6076\u6597\u4e00\u822c\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u7f00\u4e71\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5251\u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u4f46\u8840\u795e\u5b50\u631f\u6570\u5341\u5e74\u529f\u529b\uff0c\u52a0\u4e0a\u6240\u7ec3\u7684\u4e16\u95f4\u72ec\u4e00\u65e0\u4e8c\u7684\u6b79\u6bd2\u7684\u90aa \u6076\u5916\u529f\uff0c\u4e45\u6597\u4e4b\u4e0b\uff0c\u51b0\u5ddd\u5929\u5973\u7adf\u6e10\u6e10\u843d\u5728\u4e0b\u98ce\uff0c\u867d\u662f\u4e92\u76f8\u8ffd\u9010\uff0c\u4f46\u4ee5\u5510\u7ecf\u5929\u8fd9\u6837\u7684\u5927\u884c\u5bb6\uff0c \u5df2\u770b\u5f97\u51fa\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u6e10\u6e10\u88ab\u8840\u795e\u5b50\u8feb\u5f97\u8212\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u8001\u9b54\u5934\u7684\u8840\u795e\u638c\u89e6\u4eba\u7acb\u6b7b\uff0c\u78b0\u5b83\u4e0d\u5f97\uff0c\u4f60\u4eec\u4fe9\u4eba\u4e0d\u5fc5\u72af\u9669\uff0c\u8d76\u5feb\u8d70\u5427\uff0c \u6211\u81ea\u6709\u6cd5\u5b50\u5e94\u4ed8\u4ed6\u3002\u201d\u5510\u7ecf\u5929\u76ee\u6ce8\u6597\u573a\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u4e00\u53ea\u79cb\u6c34\u76c8\u76c8\u7684\u773c\u775b\uff0c\u4e5f\u6b63\u671b\u7740\u81ea \u5df1\uff0c\u773c\u5149\u4e2d\u542b\u6709\u602a\u8d23\u7684\u795e\u8272\u3002\u4ed6\u77e5\u9053\u51b0\u5ddd\u5929\u5973\u7684\u813e\u6c14\uff0c\u82e5\u7136\u4e0d\u80fd\u5360\u80dc\uff0c\u7edd\u4e0d\u80af\u8d70\u5f00\u3002\u5f53\u4e0b\u5bf9 \u9f99\u7075\u77eb\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e14\u5f85\u6211\u4eec\u66ff\u4f60\u628a\u8840\u795e\u5b50\u6253\u53d1\u4e4b\u540e\uff0c\u6211\u518d\u8d70\u5427\u3002\u201d\u4e0d\u7406\u9f99\u7075\u77eb\u540c\u4e0d \u540c\u610f\uff0c\u500f\u5730\u7eb5\u8eab\u4fbf\u8dc3\u5165\u6597\u573a\uff0c \u3000\u3000\u8840\u795e\u5b50\u6b63\u6740\u5f97\u6027\u8d77\uff0c\u4e00\u638c\u7d27\u4f3c\u4e00\u638c\uff0c\u8981\u5f3a\u62a2\u51b0\u5ddd\u5929\u5973\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u5ffd\u89c1\u4e00\u9053\u4e4c\u91d1\u5149\u534e\uff0c \u7535\u5c04\u800c\u6765\uff0c\u8840\u795e\u5b50\u628a\u624b\u4e00\u62db\uff0c\u6b32\u5f85\u786c\u63a5\uff0c\u5ffd\u89c9\u90a3\u6697\u5668\u631f\u98ce\uff0c\u52b2\u529b\u5947\u5927\uff0c\u4f30\u91cf\u81ea\u5df1\u7684\u529f\u529b\u3001\u82e5 \u7136\u786c\u63a5\uff0c\u53ea\u6050\u8981\u88ab\u5b83\u7a7f\u900f\u638c\u5fc3\uff01 \u3000\u3000\u8840\u795e\u5b50\u6b66\u529f\u786e\u662f\u9ad8\u5f3a\u4e4b\u6781\uff0c\u5c31\u5728\u8fd9\u795e\u8292\u5c04\u5230\u7684\u4fc4\u9877\u4e4b\u95f4\uff0c\u5ffd\u5730\u53cc\u6307\u4e00\u5f39\uff0c\u5f39\u5728\u51b0\u5ddd\u5929\u5973 \u7684\u5251\u4e0a\uff0c\u90a3\u67c4\u51b0\u9b44\u5bd2\u5149\u5251\u9aa4\u7136\u4e00\u8361\uff0c\u53ea\u542c\u5f97\u201c\u6323\u201d\u7684\u4e00\u58f0\uff0c\u5929\u5c71\u795e\u8292\u4ece\u4e24\u4eba\u7684\u7a7a\u9699\u4e4b\u95f4\u7a7f \u8fc7\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u5c04\u5230\u5f80\u4e0a\uff0c\u6574\u679d\u795e\u8292\uff0c\u6ca1\u5165\u77f3\u67f1\u4e4b\u4e2d\u3002 \u3000\u3000\u8840\u795e\u5b50\u8fd9\u62db\u5b9e\u662f\u4f7f\u5f97\u9669\u5230\u6781\u70b9\uff0c\u987b\u77e5\u51b0\u5ddd\u5929\u5973\u7684\u5251\u6cd5\u4e5f\u662f\u5feb\u82e5\u98d8\u98ce\uff0c\u8840\u795e\u5b50\u51fa\u6307\u4e00\u5f39\uff0c \u82e5\u6709\u6beb\u5398\u4e4b\u5dee\uff0c\u624b\u6307\u5c31\u8981\u88ab\u5251\u950b\u524a\u53bb\uff0c\u90a3\u65f6\u9634\u5bd2\u4e4b\u6c14\u653b\u5165\u8840\u7ba1\uff0c\u591a\u597d\u7684\u5185\u529f\u4e5f\u96be\u62b5\u5fa1\u3002\u4f46\u7ed9 \u4ed6\u4e00\u5f39\u63b8\u4e2a\u6b63\u7740\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5251\u52bf\u53cd\u800c\u4e3a\u4ed6\u6240\u7528\uff0c\u52b2\u529b\u66f4\u589e\uff0c\u6070\u6070\u66ff\u4ed6\u78b0\u98de\u4e86\u5510\u7ecf\u5929\u7684\u5929\u5c71 \u795e\u8292\u3002\u8840\u795e\u5b50\u9732\u4e86\u8fd9\u624b\u529f\u592b\uff0c\u5510\u7ecf\u5929\u56fa\u7136\u5403\u60ca\uff0c\u8840\u795e\u5b50\u66f4\u662f\u5403\u60ca\u4e0d\u5c0f\u3002\u4ed6\u4ee5\u4e3a\u51b0\u5ddd\u592d\u5973\u5728\u540e \u8f88\u4e4b\u4e2d\u5df2\u662f\u72ec\u4e00\u65e0\u4e8c\uff0c\u54ea\u6599\u5510\u7ecf\u5929\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u770b\u4ed6\u53d1\u6697\u5668\u7684\u5185\u5bb6\u52b2\u529b\uff0c\u72b9\u5728\u51b0\u5ddd\u5929\u5973\u4e4b\u4e0a\u3002 \u4e0d\u7531\u5f97\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u201c\u60f3\u4e0d\u5230\u81ea\u5df1\u6f5c\u4fee\u4e86\u51e0\u5341\u5e74\uff0c\u8fde\u4e24\u4e2a\u540e\u751f\u5c0f\u5b50\u4e5f\u4e0d\u80fd\u53d6\u80dc\u3002 \u3000\u3000\u53cc\u65b9\u52a8\u4f5c\u90fd\u662f\u5feb\u5982\u95ea\u7535\uff0c\u5510\u7ecf\u592d\u795e\u8292\u4e00\u53d1\u4e0d\u4e2d\uff0c\u6e38\u9f99\u5251\u7acb\u523b\u51fa\u9798\uff0c\u6e38\u9f99\u5251\u662f\u5f53\u5e74\u5929\u5c71\u6d3e \u59cb\u7956\u6666\u660e\u7985\u5e08\u91c7\u4e94\u91d1\u4e4b\u7cbe\u6240\u70bc\u7684\u9547\u5c71\u4e4b\u5b9d\uff0c\u5251\u8d28\u4e4b\u4f73\uff0c\u5c1a\u5728\u51b0\u9b44\u5bd2\u5149\u5251\u4e4b\u4e0a\uff0c\u7565\u4e00\u6325\u52a8\uff0c\u4fbf \u89c1\u5149\u8292\u56db\u5c04\uff0c\u679c\u7136\u77eb\u82e5\u6e38\u9f99\uff1b\u8840\u795e\u5b50\u53cd\u624b\u4e00\u638c\uff0c\u6ca1\u6709\u6253\u7740\u654c\u4eba\uff0c\u53cd\u800c\u51e0\u4e4e\u7ed9\u6e38\u9f99\u5251\u5c3e\u7684\u950b\u8292 \u626b\u7740\uff0c\u6025\u5fd9\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7528\u638c\u529b\u8feb\u5f00\u51b0\u5ddd\u5929\u5973\u7684\u5251\u3002\u5510\u7ecf\u5929\u7684\u5251\u5982\u5f71\u968f\u5f62\uff0c\u8ddf\u8e2a\u53c8\u5230\uff0c\u8840\u795e\u5b50 \u731b\u5730\u53cc\u638c\u9f50\u51fa\uff0c\u4e00\u80a1\u70ed\u98ce\uff0c\u547c\u547c\u4f5c\u54cd\uff0c\u5510\u7ecf\u5929\u5982\u8eab\u9677\u6d2a\u7089\u4e4b\u4e2d\uff0c\u8feb\u5f97\u9000\u540e\u51e0\u6b65\u3002\u8840\u795e\u5b50\u811a\u8e0f \u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c\u4e0d\u4f4f\u7684\u7ed5\u573a\u75be\u8d70\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8fd9\u624d\u660e\u767d\u9f99\u7075\u77eb\u4f55\u4ee5\u5728\u5341\u62db\u4e4b\u5185\uff0c\u5c31\u7ed9\u8840\u795e\u5b50\u8feb\u5f97\u6c14\u5598\u5982\u725b\u7684\u9053\u7406\uff0c\u539f\u6765\u662f\u4ed6\u638c \u5fc3\u6240\u53d1\u7684\u70ed\u529b\u5728\u4f5c\u602a\u3002\u5929\u5c71\u6d3e\u7684\u5185\u529f\u4e43\u6b66\u5b66\u6b63\u5b97\uff0c\u5510\u7ecf\u5929\u706b\u5019\u867d\u7136\u7a0d\u6b20\uff0c\u4f46\u5374\u662f\u5bb6\u4f20\u5fc3\u6cd5\uff0c \u6025\u5fd9\u51dd\u795e\u9759\u6c14\uff0c\u8fd0\u5251\u9632\u5fa1\uff0c\u679c\u7136\u597d\u4e86\u4e00\u4e9b\u3002\u53cc\u5251\u8054\u653b\uff0c\u5a01\u529b\u500d\u589e\u3002\u8840\u795e\u5b50\u82e5\u7136\u4ee5\u4e00\u654c\u4e00\uff0c\u539f \u53ef\u7a0d\u5360\u4e0a\u98ce\uff0c\u800c\u4eca\u4ee5\u4e00\u654c\u4e8c\uff0c\u90a3\u5c31\u53ea\u6709\u9000\u5b88\u7684\u4efd\u513f\u4e86\u3002 \u3000\u3000\u53cc\u65b9\u8d8a\u6218\u8d8a\u7d27\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u6015\u70ed\u529b\uff0c\u6b65\u6b65\u8fdb\u8feb\uff0c\u770b\u770b\u5c31\u8981\u628a\u8840\u795e\u5b50\u8feb\u5230\u5899\u8fb9\uff0c\u65e0\u8def\u53ef \u9000\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4e24\u58f0\u602a\u5578\uff0c\u53c8\u6709\u4e24\u4e2a\u4eba\u7a9c\u8fdb\u6765\uff0c\u6b63\u662f\u5728\u4e39\u8fbe\u5c71\u4e0a\u5077\u542c\u81ea\u5df1\u5f39\u7434\u7684\u90a3\u5bf9\u592b\u5987\u2014 \u2014\u4e91\u7075\u5b50\u4e0e\u6851\u9752\u5a18\u3002\u8840\u795e\u5b50\u7cbe\u795e\u4e00\u632f\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4f46\u8fd9\u4e24\u4eba\u5374\u5e76\u4e0d\u4e0a\u524d\u5e2e\u5fd9\uff0c\u98de\u5165\u5ead\u9662\uff0c\u5374 \u7a81\u7136\u4e00\u9f50\u505c\u4f4f\u3002 \u3000\u3000\u8840\u795e\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u662f\u6015\u4e8b\uff0c\u5c31\u4e0d\u5fc5\u6765\u3002\u201d\u4e91\u7075\u5b50\u9053\uff1a\u201c\u5927\u54e5\uff0c\u548c\u4f60\u52a8\u624b\u4e4b\u4eba\u4e43\u662f\u5510\u6653 \u6f9c\u5510\u5927\u4fa0\u7684\u513f\u5b50\u3002\u201d\u8840\u795e\u5b50\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u800c\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6015\u4ed6\u6211\u987b\u4e0d\u6015\u4ed6\u3002\u5728\u4f60\u4eec\u662f \u4e00\u6d3e\u540d\u5bbf\uff0c\u51e0\u5341\u5c81\u7684\u4eba\u5374\u7ed9\u5510\u6653\u6f9c\u7684\u540d\u5934\u5413\u5012\uff01\u597d\u5566\uff0c\u4f60\u4eec\u4e0d\u6562\u62db\u60f9\u5929\u5c71\u6d3e\u7684\u4eba\uff0c\u4e14\u5f85\u6211\u5355 \u72ec\u5e94\u4ed8\u4ed6\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5b9e\u662f\u6697\u793a\u4e91\u7075\u5b50\u4e0e\u6851\u9752\u5a18\u53bb\u7eca\u4f4f\u51b0\u5ddd\u5929\u5973\u3002 \u3000\u3000\u4e91\u7075\u5b50\u592b\u5987\u7ed9\u8840\u795e\u5b50\u8bf4\u5f97\u751a\u662f\u5c34\u5c2c\uff0c\u542c\u4e86\u4ed6\u7684\u6697\u793a\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u5e72\u7b11\u4e24\u58f0\uff0c\u63a9\u9970\u7a98\u6001\uff0c \u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u6015\u4ed6\uff0c\u4e0d\u8fc7\u4e0d\u613f\u4e0e\u540e\u8f88\u4e00\u822c\u89c1\u8bc6\u3002\u201d\u8840\u795e\u5b50\u6012\u9053\uff1a\u201c\u5bf9\u76ee\u4e2d\u65e0\u4eba\u7684\u540e\u8f88\uff0c \u54b1\u4eec\u4e5f\u5f97\u7ba1\u6559\u7ba1\u6559\uff0c\u597d\uff0c\u6211\u4eca\u65e5\u5c31\u5148\u628a\u5510\u6653\u6f9c\u7684\u513f\u5b50\u6349\u4e86\u3002\u628a\u4ed6\u9001\u4e0a\u5929\u5c71\uff0c\u5148\u95ee\u4ed6\u4e00\u4e2a\u6559\u5b50 \u4e0d\u4e25\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u4e91\u7075\u5b50\u592b\u5987\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u5374\u4e5f\u4e0d\u613f\u518d\u8bf4\uff0c\u7acb\u523b\u62bd\u51fa\u5175\u5668\uff0c\u5408\u653b\u51b0\u5ddd\u5929\u5973\uff0c\u628a\u5979\u4e0e\u5510\u7ecf \u5929\u5206\u9694\u5f00\u6765\u3002\u8fd9\u4e00\u4e0b\u5f62\u52bf\u7acb\u53d8\uff0c\u8840\u795e\u5b50\u53cd\u5b88\u4e3a\u653b\uff0c\u7740\u7740\u8fdb\u8feb\u5510\u7ecf\u5929\u3002 \u3000\u3000\u5510\u7ecf\u5929\u201c\u563f\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u5251\u6cd5\u4e5f\u662f\u9aa4\u7136\u4e00\u53d8\uff0c\u4f46\u89c1\u5251\u5149\u970d\u970d\uff0c\u6709\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u7d2b\u7535\u76d8 \u7a7a\uff0c\u5168\u8eab\u90fd\u85cf\u5728\u6e38\u9f99\u5251\u7684\u5149\u5e62\u4e4b\u5185\u3002\u8fd9\u662f\u5929\u5c71\u5251\u6cd5\u6700\u7cbe\u5fae\u5965\u5999\u7684\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u5251\u52bf\u5c55\u5f00\uff0c\u6709 \u5982\u94dc\u5899\u94c1\u58c1\uff0c\u5373\u4f7f\u9047\u5230\u529f\u529b\u6bd4\u81ea\u5df1\u9ad8\u7684\u4eba\uff0c\u4ea6\u96be\u653b\u5165\u3002\u5927\u987b\u5f25\u5251\u5f0f\u4e5f\u5e76\u4e0d\u662f\u53ea\u5b88\u4e0d\u653b\uff0c\u800c\u662f \u968f\u7740\u654c\u4eba\u7684\u653b\u52bf\u8f6c\u79fb\u7684\u3002\u53ea\u8981\u5bf9\u65b9\u7a0d\u4e00\u758f\u795e\uff0c\u4fbf\u53ef\u7a81\u56f4\u800c\u51fa\uff0c\u7acb\u65bd\u6740\u624b\u3002 \u3000\u3000\u8840\u795e\u5b50\u4e00\u638c\u7d27\u8fc7\u4e00\u638c\uff0c\u8fde\u653b\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u5510\u7ecf\u5929\u4ecd\u662f\u5140\u7acb\u5982\u5c71\u3002\u4f46\u8840\u795e\u5b50\u6bcf\u53d1\u4e00\u638c\uff0c\u90fd \u5e26\u7740\u4e00\u80a1\u70ed\u6d6a\u4fb5\u6765\uff0c\u8089\u638c\u867d\u7136\u4e0d\u80fd\u653b\u8fdb\u5251\u5899\uff0c\u70ed\u6d6a\u5374\u662f\u65e0\u5b54\u4e0d\u5165\u3002\u5510\u7ecf\u5929\u867d\u80fd\u8fd0\u7528\u5185\u529f\u62b5 \u5fa1\uff1b\u201c\u5230\u5e95\u4e0d\u5982\u51b0\u9b44\u5bd2\u5149\u5251\u7684\u5929\u7136\u5bd2\u6c14\u4e4b\u5999\uff0c\u6545\u6b64\u51b0\u5ddd\u5929\u5973\u72ec\u6218\u8840\u795e\u5b50\u4e4b\u65f6\uff0c\u53ef\u4ee5\u62b5\u654c\u81f3\u4e00 \u767e\u4f59\u62db\u4e4b\u540e\u59cb\u89c1\u4e0b\u98ce\uff0c\u800c\u5510\u7ecf\u5929\u6321\u4e86\u4e09\u5341\u591a\u62db\uff0c\u5374\u5df2\u6e10\u611f\u96be\u4ee5\u5e94\u4ed8\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u72ec\u6218\u4e91\u7075\u5b50\u592b\u5987\uff0c\u4e5f\u662f\u611f\u5230\u5904\u5728\u4e0b\u98ce\uff0c\u4f46\u5374\u4e0d\u5982\u5510\u7ecf\u5929\u4e4b\u751a\uff0c\u5728\u4e00\u767e\u62db\u4e4b\u5185\uff0c \u53cc\u65b9\u90fd\u662f\u6709\u5b88\u6709\u653b\uff0c\u6851\u9752\u5a18\u618e\u6068\u51b0\u5ddd\u5929\u5973\u7684\u7f8e\u8c8c\uff0c\u51fa\u624b\u7279\u522b\u72e0\u8fa3\uff0c\u90a3\u6761\u5408\u91d1\u7684\u8170\u5e26\u8be1\u62db\u767e \u51fa\uff0c\u8fde\u7528\u7f20\u3001\u6253\u3001\u56f4\u3001\u63a8\u3001\u6cbe\u3001\u626b\u3001\u62d6\u3001\u5377\u516b\u6cd5\uff0c\u6709\u5982\u7075\u86c7\u6e38\u52a8\uff0c\u9047\u9699\u5373\u94bb\u3002\u4e91\u7075\u4e8e\u4f7f\u7684\u5224 \u5b98\u53cc\u7b14\uff0c\u66f4\u662f\u4e13\u70b9\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u52a0\u4ee5\u8155\u529b\u6c89\u96c4\uff0c\u53cc\u7b14\u4f7f\u5f00\uff0c\u65e2\u53ef\u5f53\u4f5c\u70b9\u7a74\u7684\u5175\u5668\uff0c\u53c8 \u53ef\u5f53\u4f5c\u4e94\u884c\u5251\u8fd0\u7528\uff0c\u653b\u52bf\u7ef5\u7ef5\u4e0d\u65ad\u3002\u51b0\u5ddd\u5929\u5973\u4ee5\u4e00\u654c\u4e8c\uff0c\u6e10\u6e10\u611f\u5230\u96be\u4ee5\u5316\u89e3\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5168\u529b\u5b88\u5fa1\uff1b\u8fc7\u4e86\u4e94\u5341\u591a\u62db\uff0c\u53cc\u773c\u8d64\u7ea2\uff0c\u6c57\u51fa\u5982\u6d46\uff0c\u70ed\u5f97\u8d8a\u53d1\u96be\u53d7\u4e86\uff0c\u4ed6\u5077\u773c\u4e00 \u77a5\uff0c\u89c1\u9f99\u7075\u77eb\u4ecd\u662f\u501a\u95e8\u89c2\u6218\uff0c\u65e2\u4e0d\u9003\u8d70\uff0c\u4ea6\u4e0d\u52a9\u62f3\uff0c\u771f\u4e0d\u77e5\u4ed6\u6253\u4ec0\u4e48\u4e3b\u610f\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e0d\u7981 \u53d1\u607c\uff0c\u53c8\u89c1\u51b0\u5ddd\u5929\u5973\u4ea6\u5df2\u6e10\u6e10\u843d\u5728\u4e0b\u98ce\uff0c\u66f4\u4e3a\u7126\u8e81\uff0c\u9ad8\u624b\u76f8\u6597\uff0c\u6700\u5fcc\u5fc3\u795e\u4e0d\u5b89\uff0c\u5510\u7ecf\u5929\u8fd9\u4e00 \u7126\u8e81\uff0c\u5251\u6cd5\u7acb\u523b\u88ab\u8840\u795e\u5b50\u7684\u638c\u529b\u6253\u5f97\u6563\u4e71\uff0c\u5fae\u9732\u7a7a\u95e8\u3002 \u3000\u3000\u9661\u542c\u5f97\u8840\u795e\u5b50\u5927\u559d\u4e00\u58f0\uff0c\u4e58\u7740\u7a7a\u9699\uff0c\u4e00\u638c\u5288\u8fdb\uff0c\u5ffd\u89c1\u5251\u5149\u4e00\u6563\uff0c\u6709\u5982\u6d6a\u82b1\u98de\u6e85\uff0c\u5343\u70b9\u4e07 \u70b9\uff0c\u76f4\u6d12\u4e0b\u6765\uff0c\u8840\u795e\u5b50\u773c\u795e\u4e00\u82b1\uff0c\u4f46\u89c1\u56db\u9762\u516b\u65b9\u4eba\u5f71\u6643\u52a8\uff0c\u7adf\u8fa8\u4e0d\u6e05\u5510\u7ecf\u5929\u8eab\u5f62\u6240\u5728\u5730\u771f\u6b63 \u65b9\u5411\u3002\u8840\u795e\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u5f3a\u653b\uff0c\u6025\u5fd9\u56de\u638c\u81ea\u4fdd\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u53ea\u542c \u5f97\u545c\u545c\u4e24\u58f0\u602a\u5578\uff0c\u5510\u7ecf\u5929\u5df2\u662f\u817e\u51fa\u624b\u6765\uff0c\u8fde\u53d1\u4e24\u679d\u5929\u5c71\u795e\u8292\uff0c\u5206\u5c04\u4e91\u7075\u5b50\u592b\u5987\u3002\u4e91\u7075\u5b50\u592b\u5987 \u8bc6\u5f97\u5389\u5bb3\uff0c\u53cc\u53cc\u8dc3\u5f00\uff0c\u5510\u7ecf\u5929\u8eab\u6cd5\u4f55\u7b49\u4fa0\u6377\uff0c\u8d81\u7740\u8fd9\u4e09\u4e2a\u4eba\u5404\u81ea\u6563\u5f00\u4e4b\u9645\uff0c\u5df2\u4e0e\u51b0\u5ddd\u5929\u5973\u4f1a \u5408\u3002 \u3000\u3000\u539f\u6765\u5510\u7ecf\u5929\u8fd9\u4e00\u62db\u4e5f\u662f\u5192\u9669\u975e\u5e38\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u8ffd\u98ce\u5251\u5f0f\u7684\u201c\u7535\u5c04\u661f\u9a70\u201d\uff0c\u662f \u4e00\u8fde\u5341\u51e0\u4e2a\u865a\u7740\u6784\u6210\u7684\u5251\u5f0f\uff0c\u53ea\u662f\u5251\u5c16\u98a4\u52a8\uff0c\u5e76\u672a\u771f\u4e2a\u51fa\u62db\uff0c\u4f46\u56e0\u52a8\u4f5c\u592a\u5feb\uff0c\u6240\u4ee5\u5728\u654c\u4eba\u770b \u6765\uff0c\u5c31\u4f3c\u4e4e\u5904\u5904\u90fd\u662f\u6709\u5251\u950b\u523a\u5230\u3002\u8fd9\u4e00\u62db\u7684\u7528\u5904\uff0c\u5176\u5b9e\u53ea\u80fd\u8ff7\u60d1\u654c\u4eba\u7684\u773c\u76ee\uff0c\u4e0d\u80fd\u771f\u6b63\u4f24 \u4eba\u3002\u82e5\u88ab\u5bf9\u65b9\u8bc6\u7834\uff0c\u4ecd\u6309\u539f\u5f0f\u8fdb\u653b\uff0c\u4e0d\u4e3a\u6240\u60d1\uff0c\u5219\u81ea\u5df1\u53cd\u8981\u53d7\u4f24\u3002\u5510\u7ecf\u5929\u4ece\u653b\u5b88\u517c\u5907\u7684\u5927\u987b \u5f25\u5251\u5f0f\uff0c\u5ffd\u7136\u6539\u4e3a\u5f3a\u653b\u7684\u8ffd\u98ce\u5251\u5f0f\uff0c\u539f\u662f\u65e0\u53ef\u5948\u4f55\u4e4b\u7740\uff0c\u4f46\u8840\u795e\u5b50\u4e0d\u8bc6\u5929\u5c71\u5251\u6cd5\u7684\u5965\u5999\uff0c\u679c \u7136\u88ab\u4ed6\u9a97\u8fc7\u3002\u5230\u9192\u89c9\u65f6\uff0c\u5510\u7ecf\u5929\u5df2\u4e0e\u51b0\u5ddd\u5929\u5973\u5e76\u80a9\u800c\u7acb\uff0c\u8054\u5251\u540c\u9632\u4e86\u3002 \u3000\u3000\u8840\u795e\u5b50\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u6251\u4e0a\u524d\u53bb\uff0c\u4e91\u7075\u5b50\u592b\u5987\u4e5f\u662f\u4e00\u9000\u590d\u8fdb\uff0c\u4ecd\u7136\u51c6\u5907\u5408\u653b\u51b0\u5ddd\u5929\u5973\u3002 \u5510\u7ecf\u5929\u659c\u523a\u6740\u51fa\uff0c\u4e00\u5251\u6a2a\u5c01\uff0c\u5c06\u4e91\u7075\u5b50\u592b\u5987\u6321\u4e86\u4e00\u6321\uff0c\u90a3\u4e00\u8fb9\u8840\u795e\u4e8e\u8eab\u5f62\u65b9\u8d77\uff0c\u51b0\u5ddd\u5929\u5973\u7684 \u516d\u679a\u51b0\u9b44\u795e\u5f39\u65e9\u5df2\u5411\u4ed6\u6253\u6765\u3002\u8840\u795e\u5b50\u53cc\u638c\u7ffb\u8d77\u70ed\u98ce\uff0c\u516d\u679a\u51b0\u9b44\u795e\u5f39\u5168\u90fd\u5728\u8840\u795e\u5b50\u7684\u5934\u9876\u88c2 \u5f00\uff0c\u5bd2\u5149\u51b7\u6c14\uff0c\u56db\u9762\u5f25\u6f2b\uff0c\u500f\u7684\u5c31\u4f3c\u6784\u6210\u4e00\u7247\u7070\u8272\u7684\u5149\u7f51\uff0c\u5c06\u8840\u795e\u4e8e\u5168\u8eab\u5305\u6ca1\uff0c\u51b0\u9b44\u795e\u5f39\u6240 \u5305\u7684\u4e43\u662f\u4e98\u53e4\u5bd2\u51b0\u6240\u53d1\u7684\u5947\u5bd2\u4e4b\u6c14\uff0c\u516d\u679a\u9f50\u53d1\uff0c\u5389\u5bb3\u4e4b\u6781\uff0c\u6b63\u662f\u8840\u795e\u5b50\u7684\u514b\u661f\uff0c\u8840\u795e\u5b50\u638c\u5fc3 \u6240\u53d1\u7684\u70ed\u529b\uff0c\u62b5\u6321\u4e0d\u4f4f\uff0c\u4e0d\u7531\u5f97\u4e5f\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002\u867d\u7136\u4ee5\u8840\u795e\u5b50\u7684\u529f\u529b\u5c1a\u4e0d\u81f3\u53d7\u9634 \u5bd2\u4e4b\u6c14\u6240\u4f24\uff0c\u4f46\u4e00\u51b7\u4e00\u70ed\uff0c\u547c\u5438\u4ea6\u611f\u4e0d\u8212\uff0c\u80f8\u53e3\u7adf\u7136\u4f5c\u95f7\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u53c8\u662f\u4e00\u53d8\uff0c\u5510\u7ecf\u5927\u4e0e\u51b0\u5ddd\u5929\u5973\u53cc\u5251\u76f8\u8054\uff0c\u5408\u6218\u8840\u795e\u5b50\u4e0e\u4e91\u7075\u5b50\u592b\u5987\u4e09\u4eba\uff0c\u56e0 \u6709\u51b0\u9b44\u5bd2\u5149\u5251\u6321\u5f97\u4f4f\u8840\u795e\u5b50\u638c\u98ce\u6240\u53d1\u7684\u70ed\u6d6a\uff0c\u9996\u5148\u4e0d\u53d7\u5a01\u80c1\uff0c\u800c\u8840\u795e\u5b50\u9002\u624d\u88ab\u51b0\u9b44\u795e\u5f39\u6240 \u88ad\uff0c\u529f\u529b\u53c8\u4e0d\u514d\u7a0d\u53d7\u5f71\u54cd\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u4ee5\u4e8c\u654c\u4e09\uff0c\u867d\u7136\u8fd8\u662f\u62a2\u4e0d\u5230\u4e0a\u98ce\uff0c \u4f46\u5df2\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u6b63\u5728\u6df7\u6218\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4eba\u58f0\u5608\u6742\uff0c\u89d2\u95e8\u5f00\u5904\uff0c\u4e00\u6279\u519b\u5b98\u8d70\u4e86\u8fdb\u6765\uff0c\u8d70\u5728\u524d\u9762\u7684\u9876\u6234 \u82b1\u7fce\uff0c\u8eab\u7a7f\u9ec4\u9a6c\u8902\uff0c\u7adf\u7136\u662f\u4e2a\u6709\u529f\u52cb\u7684\u4e8c\u54c1\u6587\u5b98\uff0c\u540e\u9762\u8ddf\u7740\u4e03\u516b\u4e2a\u6b66\u5b98\uff0c\u9f99\u7075\u77eb\u7684\u5e08\u5f1f\u989c\u6d1b \u4e5f\u5728\u5176\u4e2d\uff0c\u8d70\u8def\u6447\u6447\u6643\u6643\uff0c\u9762\u8272\u7070\u767d\uff0c\u4f46\u4ecd\u7136\u652f\u6301\u5f97\u4f4f\u3002 \u3000\u3000\u8d70\u5728\u524d\u9762\u8fd9\u4e2a\u5927\u5b98\u4e43\u662f\u9a7b\u85cf\u5927\u81e3\u5b98\u7f72\u4e2d\u4e13\u7ba1\u5211\u540d\u7684\u768b\u53f8\uff08\u5373\u7b49\u4e8e\u5927\u6cd5\u5b98\uff09\uff0c\u540d\u53eb\u5b97\u6d1b\uff0c \u672c\u8eab\u53c8\u662f\u6ee1\u6e05\u7684\u5b97\u5ba4\uff0c\u540e\u9762\u7684\u90a3\u4e9b\u519b\u5b98\u5219\u662f\u9f99\u7075\u77eb\u7684\u540c\u50da\uff0c\u539f\u6765\u989c\u6d1b\u4e2d\u4e86\u8840\u795e\u5b50\u4e00\u638c\uff0c\u867d\u7136 \u53d7\u4f24\u4e0d\u5c0f\uff0c\u4f46\u77e5\u9053\u4e8b\u60c5\u9669\u6025\uff0c\u5f3a\u81ea\u8fd0\u6c14\u652f\u6301\uff0c\u6025\u6025\u4e58\u9a6c\u8d76\u5230\u5b98\u7f72\uff0c\u5c06\u4ed6\u4eec\u90fd\u8bf7\u4e86\u6765\u3002 \u3000\u3000\u5b97\u6d1b\u4e00\u526f\u5927\u5b98\u7684\u67b6\u5b50\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u51e0\u4e2a\u662f\u4ec0\u4e48\u4eba\uff1f\u4e3a\u4f55\u5728\u6b64\u80e1\u95f9\uff1f\u201d\u5510\u7ecf\u5929\u5fae\u5fae\u4e00 \u7b11\uff0c\u4e0e\u51b0\u5ddd\u5929\u5973\u6536\u4e86\u5b9d\u5251\uff0c\u9000\u4e86\u51fa\u6765\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u6211\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u8ddf\u4f60\u6765\u7684\u5b98\u513f\u4eec\u90fd\u77e5 \u9053\u3002\u201d\u90a3\u4e9b\u519b\u5b98\u4eec\u9f50\u58f0\u7b54\u9053\uff1a\u201c\u4ed6\u4eec\u5c31\u662f\u65e5\u524d\u5728\u4e39\u8fbe\u5c71\u53e3\u4fdd\u62a4\u91d1\u74f6\u7684\u90a3\u4e24\u4f4d\u4e49\u58eb\u3002\u5b97\u6d1b\u770b\u4e86 \u51b0\u5ddd\u5929\u5973\u4e00\u773c\uff0c\u9732\u51fa\u7b11\u5bb9\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u6362\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u4eec\u662f\u6709\u529f\u4e4b\u4eba\uff0c\u9000\u4e0b\u5f85 \u8d4f\u3002\u201d\u54b3\u4e86\u4e00\u58f0\uff0c\u773c\u5149\u5c04\u5230\u8840\u795e\u5b50\u9762\u4e0a\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u51e0\u4e2a\u51f6\u5f92\u80c6\u5b50\u53ef\u4e0d\u5c0f\u54c7\uff01\u7adf\u7136 \u591c\u5165\u5b98\u5bb6\uff0c\u6301\u68b0\u884c\u51f6\uff0c\u4f60\u4eec\u76ee\u4e2d\u8fd8\u6709\u7687\u6cd5\u5417\uff1f\u201d \u3000\u3000\u8840\u795e\u5b50\u4e8e\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u7687\u6cd5\uff1f\u8001\u5b50\u5c31\u662f\u5949\u4e86\u4f60\u4eec\u7687\u5e1d\u8001\u513f\u4e4b\u547d\u6765\u7684\uff01\u201d\u5b97\u6d1b\u6012\u9053\uff1a \u201c\u4f60\u5c31\u662f\u94a6\u5dee\u5927\u81e3\uff0c\u4e5f\u4e0d\u80fd\u5982\u6b64\u65e0\u793c\u3002\u201d\u4f17\u519b\u5b98\u90fd\u52a8\u4e86\u6012\uff0c\u9053\uff1a\u201c\u5185\u5e9c\u6d3e\u6765\u7684\u4eba\u54ea\u4f1a\u5982\u6b64\u6492 \u91ce\uff1f\u201d \u3000\u3000\u8840\u795e\u5b50\u6012\u4e0d\u53ef\u904f\uff0c\u5c06\u5927\u5185\u603b\u7ba1\u6240\u7ed9\u7684\u59d4\u4ee4\uff0c\u63b7\u7ed9\u5b97\u6d1b\uff0c\u4e0a\u9762\u7684\u94c3\u8bb0\u5206\u660e\uff0c\u679c\u7136\u662f\u5185\u5ef7\u65b0 \u8058\u7684\u201c\u4f9b\u5949\u201d\uff0c\u8fd9\u4e8b\u65e9\u5728\u9f99\u7075\u77eb\u610f\u6599\u4e4b\u4e2d\uff0c\u5b97\u6d1b\u5374\u662f\u9887\u51fa\u610f\u5916\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u653e\u8f6f\u53e3\u6c14\u9053\uff1a \u201c\u90a3\u4f60\u4eec\u5230\u6b64\u610f\u6b32\u4f55\u4e3a\uff1f\u201d\u8840\u795e\u5b50\u6307\u7740\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u4eba\u5f62\u8ff9\u53ef\u7591\uff0c\u6df7\u5728\u897f\u85cf\u5341\u591a\u5e74\u4f60\u4eec\u90fd \u4e0d\u77e5\u9053\u3002\u8981\u52b3\u52a8\u5f53\u4eca\u5929\u5b50\u8bf7\u8001\u5b50\u51fa\u5c71\uff0c\u4f60\u4eec\u8fd8\u6709\u8bf4\u7684\uff1f\u201d \u3000\u3000\u9f99\u7075\u77eb\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7980\u5927\u4eba\uff0c\u8fd9\u4e09\u4eba\u90fd\u662f\u6b66\u6797\u8d25\u7c7b\uff0c\u4ee5\u524d\u4e0e\u9f99\u67d0\u7ed3\u6709\u79c1\u4ec7\uff0c\u800c\u4eca\u4ed6\u4eec\u6df7 \u5165\u5185\u5ef7\uff0c\u516c\u62a5\u79c1\u4ec7\uff0c\u5047\u4f20\u5723\u65e8\uff0c\u4f60\u95ee\u4ed6\u4eec\uff0c\u662f\u4e0d\u662f\u6709\u94a6\u65e8\u6307\u660e\u8981\u6349\u62ff\u9f99\u67d0\uff1f\u201d\u6e05\u5bab\u4e4b\u4e2d\uff0c\u5bf9 \u9f99\u7075\u77eb\u7684\u8eab\u4efd\u4e0d\u8fc7\u6709\u6240\u6000\u7591\uff0c\u5c1a\u672a\u67e5\u660e\uff0c\u6240\u4ee5\u5927\u5185\u603b\u7ba1\u53ea\u4e0d\u8fc7\u662f\u53e3\u5934\u4f20\u4e0b\u7687\u5e1d\u7684\u5bc6\u4ee4\uff0c\u53eb\u4ed6 \u67e5\u63a2\u660e\u767d\uff0c\u6b63\u5f0f\u7684\u902e\u6355\u6587\u4e66\u81ea\u7136\u662f\u62ff\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8840\u795e\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u7687\u5e1d\u8bf7\u6211\u4eec\u6349\u4e00\u4e2a\u829d\u9ebb\u7eff\u8c46\u7684\u5b98\u513f\uff0c\u8981\u4ec0\u4e48\u6587\u4e66\uff1f\u201d\u5b97\u6d1b\u662f\u5b98 \u573a\u8001\u624b\uff0c\u8fd9\u65f6\u4e5f\u9887\u611f\u8e0c\u8e87\uff0c\u82e5\u7136\u8840\u795e\u5b50\u6240\u8bf4\u662f\u771f\uff0c\u81ea\u5df1\u5305\u5e87\u94a6\u72af\uff0c\u7f6a\u540d\u975e\u5c0f\uff1b\u4f46\u82e5\u7136\u679c\u662f\u5047 \u5723\u65e8\uff0c\u5219\u81ea\u5df1\u4efb\u4ee4\u9f99\u7075\u77eb\u88ab\u4ed6\u4eec\u6349\u53bb\uff0c\u798f\u5927\u5e05\u5fc5\u7136\u52a8\u6012\u3002\u9f99\u7075\u77eb\u867d\u4e0d\u8fc7\u4ec5\u4ec5\u662f\u4e2a\u56db\u54c1\u5e55\u50da\uff0c \u4f46\u8c01\u90fd\u77e5\u9053\u4ed6\u662f\u798f\u5eb7\u5b89\u501a\u4e3a\u5de6\u53f3\u624b\uff0c\u6700\u6700\u5ba0\u4fe1\u7684\u4eba\u3002\u5b97\u6d1b\u8e0c\u8e87\u96be\u51b3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u798f\u5927\u5e05\u662f \u8fd1\u652f\u5b97\u5ba4\uff0c\u53c8\u662f\u7687\u4e0a\u6700\u5ba0\u4fe1\u7684\u4eba\uff0c\u4e0d\u5982\u7531\u4ed6\u5904\u51b3\u3002\u201d\u5b98\u573a\u4e4b\u4e2d\u201c\u63a8\u201d\u201c\u62d6\u201d\u4e8c\u5b57\u4e43\u662f\u505a\u5b98\u7684 \u79d8\u8bc0\uff0c\u4e3b\u610f\u4e00\u5b9a\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5404\u6267\u4e00\u8f9e\uff0c\u6211\u4e5f\u96be\u4e8e\u51b3\u65ad\uff0c\u4e0d\u8fc7\u897f\u85cf\u4e4b\u4e8b\uff0c\u7687\u4e0a\u65e9\u6709\u660e \u4ee4\uff0c\u4ea4\u798f\u5927\u5e05\u5168\u6743\u5904\u7406\u3002\u4f60\u4eec\u524d\u6765\u6355\u4eba\uff0c\u4f9d\u7406\u5f53\u4e8b\u5148\u901a\u77e5\u5927\u5e05\u3002\u597d\u5427\uff0c\u4f60\u4eec\u660e\u5929\u4e00\u65e9\uff0c\u90fd\u968f \u6211\u89c1\u798f\u5927\u5e05\u53bb\uff0c\u73b0\u5728\u8c01\u4e5f\u4e0d\u8bb8\u52a8\u624b\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u60ca\u6089\u795e\u9f99\u56fe\u5927\u4e8b\uff0c\u53c8\u89c2\u5929\u5973\u6597\u5996\u90aa\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u5927\u6f20\u4f20\u58f0\u3000\u7ae5\u5fc3\u620f\u5929\u5973\u3000\u9a7c\u5cf0\u805a\u4f1a\u3000\u5999\u8ba1\u9a97\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u56db\u56de \u5927\u6f20\u4f20\u58f0\u3000\u7ae5\u5fc3\u620f\u5929\u5973 \u9a7c\u5cf0\u805a\u4f1a\u3000\u5999\u8ba1\u9a97\u4f73\u4eba \u3000\u3000\u8840\u795e\u5b50\u867d\u7136\u9a84\u6a2a\uff0c\u4f46\u4ee5\u5b97\u6d1b\u7684\u8eab\u4efd\uff0c\u53c8\u5c06\u798f\u5eb7\u5b89\u62ac\u4e86\u51fa\u6765\uff0c\u4e0d\u7531\u4ed6\u4e0d\u540c\u610f\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a \u201c\u597d\u5427\uff0c\u8c05\u798f\u5927\u5e05\u4e5f\u4e0d\u81f3\u4e8e\u5305\u5e87\u94a6\u72af\u3002\u201d\u5b97\u6d1b\u5411\u51b0\u5ddd\u5929\u5973\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4e24\u4f4d\u4e49 \u58eb\u660e\u513f\u4e5f\u4e00\u540c\u53bb\u4f5c\u4e2a\u8bc1\u4eba\u5427\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8c01\u8010\u70e6\u7406\u8fd9\u4e9b\u95f2\u4e8b\u3002\u201d\u5510\u7ecf\u5929\u4e00\u7b11\u8bf4\u9053\uff1a \u201c\u4eca\u665a\u4e4b\u4e8b\uff0c\u8bf8\u4f4d\u5927\u4eba\u90fd\u66fe\u76ee\u51fb\uff0c\u6211\u4eec\u4e8c\u4eba\u7684\u8eab\u4efd\uff0c\u798f\u5927\u5e05\u4ea6\u5df2\u77e5\u9053\uff0c\u6211\u4eec\u5c71\u91ce\u5c0f\u6c11\uff0c\u4e0d\u60ef \u89c1\u5b98\uff0c\u8fd8\u662f\u514d\u4e86\u5427\u3002\u811a\u5c16\u4e00\u70b9\uff0c\u4e0e\u51b0\u5ddd\u5929\u5973\u98de\u8eab\u63a0\u51fa\u9662\u5b50\u7684\u56f4\u5899\uff0c\u56de\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u9f99\u7075\u77eb\u542b \u7b11\u70b9\u5934\uff0c\u773c\u4e2d\u8868\u9732\u8c22\u610f\u3002 \u3000\u3000\u5510\u7ecf\u5927\u5fc3\u4e2d\u7591\u60d1\u66f4\u751a\uff0c\u4e00\u8def\u601d\u91cf\u3002\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u8fd9\u9f99\u8001\u4e09\u4e5f\u7b97\u5f97\u662f\u4e2a\u4eba\u7269\uff0c\u4e0d\u77e5\u4ed6 \u4f55\u4ee5\u4e0d\u9003\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u770b\u4ed6\u57ce\u5e9c\u751a\u6df1\uff0c\u6848\u5b50\u8f6c\u5230\u4e86\u798f\u5eb7\u5b89\u624b\u4e2d\uff0c\u60f3\u6765\u4f1a\u6709\u8f6c\u673a\u3002\u4e24\u4eba \u4e00\u9762\u8d70\u4e00\u9762\u8c08\u8bdd\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5230\u4e86\u8461\u8404\u5c71\u5357\u9762\u5c71\u811a\uff0c\u5e03\u8fbe\u62c9\u5bab\u7684\u706f\u706b\uff0c\u9065\u9065\u7684\u7167\u5c04\u5c71\u811a\u4e0b\u9762\u7684 \u5e7f\u573a\u3002\u90a3\u662f\u4ed6\u4eec\u4e0e\u5e7d\u840d\u76f8\u7ea6\u78b0\u5934\u7684\u5730\u65b9\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u811a\u4e0b\u4e00\u5bf9\u9ed1\u5f71\u9760\u5f97\u5f88\u8fd1\uff0c\u4f3c\u662f\u6b63\u5728\u9685\u9685\u7ec6\u8bed\u3002\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u770b\u8fd9\u9ed1\u5f71\u4f3c\u662f\u4e00 \u4e2a\u7537\u5b50\uff0c\u5e7d\u840d\u600e\u4e48\u548c\u4ed6\u8fd9\u6837\u4eb2\u70ed\uff1f\u201d\u6084\u6084\u63a9\u8fc7\u53bb\u4e00\u542c\uff0c\u53ea\u542c\u5f97\u5e7d\u840d\u8bf4\u9053\uff1a\u201c\u5c0f\u516c\u4e3b\u8bf4\u6682\u65f6\u4e0d \u56de\u51b0\u5bab\uff0c\u542c\u8bf4\u8981\u5230\u56db\u5ddd\u53bb\uff0c\u6211\u4e5f\u8bb8\u4f1a\u8ddf\u5979\u53bb\u7684\uff0c\u54b1\u4eec\u4ee5\u540e\u6050\u6015\u5f88\u96be\u89c1\u9762\u4e86\u3002\u201d\u90a3\u9ed1\u5f71\u9053\uff1a \u201c\u4f60\u82e5\u78b0\u89c1\u829d\u5a1c\uff0c\u5343\u4e07\u544a\u8bc9\u5979\u8bf7\u5979\u56de\u5230\u8428\u8fe6\u6765\u89c1\u6211\u4e00\u9762\u3002\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u53ea\u6302\u5ff5\u829d\u5a1c\u59d0 \u59d0\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u9b3c\u5934\u4e5f\u61c2\u5f97\u5356\u5f04\u98ce\u60c5\u4e86\u3002\u201d\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\u3002\u90a3\u9ed1\u5f71\u7a81 \u7136\u5411\u524d\u4e00\u8dc3\uff0c\u53eb\u9053\uff1a\u201c\u6709\u4eba\uff01\u201d\u6b63\u60f3\u62d4\u5251\uff0c\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u8df3\u4e86\u51fa\u6765\uff0c\u5c06\u4ed6\u7684\u4f69\u5251\u9012\u8fc7 \u53bb\u9053\uff1a\u201c\u5929\u5b87\uff0c\u4f60\u7684\u529f\u592b\u679c\u7136\u5927\u6709\u8fdb\u5883\u4e86\uff0c\u8fd9\u90fd\u662f\u5728\u51b0\u5bab\u4e2d\u5077\u5b66\u7684\u5427\uff1f\u201d \u3000\u3000\u8fd9\u9ed1\u5f71\u6b63\u662f\u9648\u5929\u5b87\uff0c\u539f\u6765\u4ed6\u4e5f\u662f\u542c\u5230\u9f99\u7075\u77eb\u5bb6\u4e2d\u6709\u4e8b\uff0c\u7279\u6765\u63a2\u671b\u7684\uff0c\u5374\u60f3\u4e0d\u5230\u5728\u5c71\u811a\u4e0b \u78b0\u5230\u7b49\u5019\u4e3b\u4eba\u7684\u5e7d\u840d\uff0c\u5e7d\u840d\u544a\u8bc9\u4ed6\u8bf4\uff0c\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u90fd\u8fdb\u53bb\u4e86\uff0c\u4e0d\u7ba1\u9f99\u7075\u77eb\u7684\u5bf9\u5934\u6709\u591a \u4e48\u5389\u5bb3\u90fd\u4e0d\u5fc5\u62c5\u5fc3\u3002\u4ed6\u4eec\u90fd\u628a\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u89c6\u82e5\u5929\u4eba\uff0c\u4ee5\u4e3a\u4ed6\u4eec\u4e00\u5230\uff0c\u5c31\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef \u4ee5\u89e3\u51b3\u7684\u95ee\u9898\uff0c\u54ea\u77e5\u9053\u9f99\u7075\u77eb\u7684\u6848\u5b50\u5185\u60c5\u5374\u662f\u975e\u5e38\u590d\u6742\u3002 \u3000\u3000\u9648\u5929\u5b87\u7a81\u7136\u89c1\u5230\u51b0\u5ddd\u5929\u5973\u51fa\u73b0\uff0c\u751a\u662f\u5c34\u5c2c\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u6b20\u4e0b\u4f60\u5e08\u7236\u7684\u60c5\u4efd\uff0c\u65e0\u4ee5 \u4e3a\u62a5\uff0c\u4f60\u867d\u672a\u7ecf\u6211\u7684\u8bb8\u53ef\uff0c\u5077\u5b66\u6211\u7684\u5251\u6cd5\uff0c\u4f46\u90a3\u662f\u5728\u5927\u5730\u9707\u4e4b\u540e\uff0c\u7531\u4e8e\u8981\u4fdd\u5b58\u6b66\u5b66\u4e4b\u5ff5\u800c \u8d77\uff0c\u6211\u53c8\u600e\u80fd\u602a\u8d23\u4f60\u5462\u3002\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u4e5f\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u9648\u5929\u5b87\u8e51\u8815\u95ee\u9053\uff1a\u201c\u90a3\u9f99\u4e09\u5148\u751f \u600e\u4e48\u4e86\uff0c\u6211\u770b\u4ed6\u5012\u662f\u4e00\u4e2a\u597d\u4eba\uff0c\u4f60\u4eec\u4f1a\u5e2e\u52a9\u4ed6\u5427\uff1f\u201d\u5510\u7ecf\u5927\u663e\u51fa\u8eab\u5f62\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f \u5b50\u5012\u6709\u4e00\u4efd\u70ed\u5fc3\u80a0\u3002\u201d\u5ffd\u800c\u9762\u8272\u4e00\u7aef\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u8fd9\u4e8b\u4f60\u8fd8\u662f\u4e0d\u8981\u591a\u7ba1\u7684\u597d\u3002\u201d\u9648\u5929\u5b87\u542c\u4ed6 \u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u89c9\u6115\u7136\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4ee4\u5c0a\u6b64\u6b21\u7acb\u4e86\u5927\u529f\uff0c\u798f\u5eb7\u5b89\u4e0e\u548c\u7855\u4eb2\u738b\u5b9a\u5f53\u53e6\u773c\u76f8\u770b\uff0c\u4ed6\u65e5\u8bba\u7235\u53d9\u529f\uff0c\u6700 \u4e0d\u6d4e\u4e5f\u53ef\u5b98\u590d\u539f\u804c\uff0c\u90a3\u65f6\u4f60\u4eec\u5f53\u53ef\u9042\u56de\u4e61\u4e4b\u613f\u4e86\u3002\u201d\u9648\u5929\u5b87\u7684\u7236\u4eb2\u9648\u5b9a\u57fa\u662f\u4eac\u5b98\uff0c\u62dc\u5fa1\u53f2\u4e4b \u804c\uff0c\u53ea\u56e0\u5f39\u52be\u5978\u81e3\u548c\u5764\uff0c\u88ab\u8d2c\u5230\u897f\u85cf\uff0c\u6643\u773c\u5341\u5348\uff0c\u65e0\u65e5\u4e0d\u60f3\u8fd8\u4e61\u3002\u5510\u7ecf\u5929\u77e5\u9053\u4ed6\u4eec\u7236\u5b50\u5fc3 \u4e8b\uff0c\u6545\u6709\u6b64\u8a00\u3002 \u3000\u3000\u9648\u5929\u5b87\u82e6\u7b11\u9053\uff1a\u201c\u548c\u5764\u73b0\u5728\u6b63\u662f\u7099\u624b\u53ef\u70ed\uff0c\u6743\u503e\u671d\u91ce\uff0c\u54ea\u80fd\u8fd9\u6837\u5bb9\u6613\u56de\u53bb\u3002\u6211\u7236\u4eb2\u73b0\u5728 \u5012\u662f\u5b98\u590d\u539f\u804c\u4e86\uff0c\u53ef\u60dc\u4e0d\u662f\u590d\u5fa1\u53f2\u4e4b\u804c\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u662f\u590d\u8428\u8fe6\u5b97 \u5ba3\u6170\u4f7f\u4e4b\u804c\uff0c\u798f\u5927\u5e05\u5df2\u6279\u51c6\u62d4\u6b3e\u91cd\u4fee\u5b98\u7f72\uff0c\u53e6\u6d3e\u4e00\u961f\u7cbe\u5175\uff0c\u9001\u6211\u7236\u4eb2\u4e0a\u4efb\u4e86\uff0c\u53ea\u6015\u8fd9\u51e0\u65e5\u5c31 \u8981\u52a8\u8eab\u3002\u798f\u5927\u5e05\u5bf9\u6211\u7236\u4eb2\u8bf4\uff1a\u4f60\u5728\u8428\u8fe6\u4e27\u5175\u8fb1\u547d\uff0c\u672c\u5f53\u6709\u7f6a\uff0c\u73b0\u5728\u5c06\u529f\u6298\u7f6a\uff0c\u5df2\u7b97\u683c\u5916\u5f00 \u6069\uff0c\u4f60\u5148\u56de\u8428\u8fe6\u53bb\u5427\uff0c\u597d\u597d\u7684\u505a\u4e09\u4e24\u5e74\uff0c\u90a3\u65f6\u6211\u518d\u4fdd\u4e3e\u4f60\uff0c\u8ba9\u4f60\u56de\u53bb\u505a\u4eac\u5b98\u3002\u54fc\uff0c\u4ed6\u7adf\u548c\u6211 \u7236\u4eb2\u5927\u6253\u5b98\u8154\uff0c\u6211\u7236\u4eb2\u8fd8\u6709\u4f55\u8bdd\u53ef\u8bf4\uff1f\u53ea\u597d\u51c6\u5907\u518d\u56de\u8428\u8fe6\u5566\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u54b3\uff0c\u60f3\u4e0d\u5230\u5b98\u573a\u5982\u6b64\u8d4f\u7f5a\u4e0d\u660e\u3002\u4f46\u56de\u8428\u8fe6\u4e5f\u4e0d\u662f\u4ec0\u4e48\u82e6\u5dee\u4f7f\uff0c\u4f60\u4eec\u4e0d\u662f\u5728 \u90a3\u91cc\u4f4f\u4e86\u5341\u5e74\u4e48\uff1f\u4f55\u5fc5\u5982\u6b64\u6101\u7709\u82e6\u8138\uff1f\u201d\u9648\u5929\u5b87\u597d\u50cf\u6ee1\u8154\u5fc3\u4e8b\u7684\u6837\u5b50\uff0c\u7709\u5934\u6df1\u9501\u3002\u6b32\u8bf4\u4e0d \u8bf4\uff0c\u5e7d\u840d\u5ffd\u7684\u201c\u5657\u55e4\u201d\u4e00\u7b11\u9053\uff1a\u201c\u8428\u8fce\u7684\u571f\u53f8\u60f3\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u4ed6\u54e9\uff0c\u8fd9\u50bb\u5c0f\u5b50\u53e6\u6709\u5fc3\u4e0a\u4e4b \u4eba\uff1b\u4ed6\u6015\u4e00\u56de\u8428\u8fe6\uff0c\u5c31\u4f1a\u60f9\u8d77\u9ebb\u70e6\uff0c\u5440\uff0c\u4f60\u8fd9\u50bb\u5c0f\u5b50\uff0c\u522b\u4eba\u6709\u65b0\u90ce\u53ef\u505a\uff0c\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c \u4f60\u5374\u614c\u6210\u8fd9\u4e2a\u6837\u5b50\uff01\u201d\u5e7d\u840d\u4e0e\u9648\u5929\u5b87\u66fe\u540c\u884c\u591a\u65e5\uff0c\u65e0\u8bdd\u4e0d\u8c08\uff0c\u6545\u6b64\u6df1\u6089\u4ed6\u7684\u5fc3\u601d\uff0c\u9648\u5929\u5b87\u88ab \u5979\u53d6\u7b11\uff0c\u66f4\u662f\u5c34\u5c2c\u3002\u51b0\u5ddd\u5929\u5973\u4e0d\u89c9\u7b11\u9053\uff1a\u201c\u6211\u5f53\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u539f\u6765\u662f\u8fd9\u7b49\u65e0\u804a\u7684\u5c0f\u4e8b\uff0c\u4f60\u4e0d \u662f\u957f\u6709\u4e00\u5bf9\u811a\u5417\uff1f\u4f60\u4e0d\u613f\u505a\u65b0\u90ce\uff0c\u53cc\u811a\u4e00\u6e9c\uff0c\u96be\u9053\u80fd\u5f3a\u62c9\u4f4f\u4f60\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u54ea\u77e5\u5b98\u573a\u4e4b\u4e2d\u9519 \u7efc\u590d\u6742\u7684\u5173\u7cfb\uff0c\u4e00\u7b11\u7f6e\u4e4b\uff0c\u9648\u5929\u5b87\u5fc3\u4e2d\u66f4\u662f\u82e6\u95f7\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u56de\u53bb\u5427\uff0c\u4f60\u6559\u4f60\u4e00\u4e2a\u5999\u6cd5\u513f\u3002\u201d\u628a\u9648\u5929\u5b87\u62c9\u8fc7\u4e00\u8fb9\uff0c\u5728\u4ed6\u8033\u8fb9\u6084\u6084\u7684\u8bf4 \u4e86\u51e0\u53e5\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u4e2a\u4eba\uff0c\u603b\u7231\u88c5\u795e\u5f04\u9b3c\uff0c\u4f60\u6559\u4ed6\u4ec0\u4e48\u8fdb\u4e00\u6b65\u4e3b\u610f\uff0c\u89c2\u4e0d\u5f97\u4eba \u7684\uff1f\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u5929\u673a\u4e0d\u53ef\u6cc4\u6f0f\uff0c\u6211\u8fd9\u574f\u4e3b\u610f\uff0c\u4ec0\u4e48\u4eba\u90fd\u89c1\u5f97\uff0c\u5c31\u662f\u4e0d\u65b9\u4fbf\u7ed9\u4f60\u4eec \u542c\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8c01\u5e0c\u7f55\u542c\u4f60\u7684\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u6101\u5bb9\u7a0d\u655b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4fc4\u9a6c\u767b\u4e5f\u5f88\u96be\u5bf9\u4ed8\u5440\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u5982\u4eca\u7684\u6b66\u529f\u5927\u975e \u6614\u6bd4\uff0c\u4fc4\u9a6c\u767b\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\u4e86\u3002\u4f60\u653e\u5fc3\u8ddf\u7236\u4eb2\u56de\u53bb\u5427\uff0c\u53ea\u662f\u8981\u591a\u70b9\u5c0f\u5fc3\u3002\u63d0\u9632\u4ed6\u7684\u8be1\u8ba1\u3002\u201d \u9648\u5929\u5b87\u4e00\u770b\u5929\u8272\uff0c\u53ea\u89c1\u6708\u4eae\u897f\u5815\uff0c\u4e1c\u65b9\u5929\u9645\uff0c\u5df2\u5fae\u9732\u66d9\u5149\uff0c\u6015\u7236\u4eb2\u5728\u5bb6\u4e2d\u6302\u5ff5\uff0c\u53ea\u597d\u5411\u51b0\u5ddd \u5929\u5973\u544a\u8f9e\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e0e\u4ed6\u626c\u624b\u9053\u522b\uff0c\u53ea\u89c1\u5e7d\u840d\u597d\u50cf\u5fc3\u795e\u4e0d\u5c5e\u7684\u6837\u5b50\uff0c\u5446\u5446\u5730\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u51b0\u5ddd\u5929\u5973 \u7b11\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u4e00\u4e2a\u571f\u53f8\u5973\u513f\u5df2\u7ecf\u591f\u4ed6\u70e6\u4e86\uff0c\u4f60\u8fd8\u60f3\u518d\u7ed9\u4ed6\u6dfb\u4e0a\u9ebb\u70e6\u5417\uff1f\u201d\u5e7d\u840d\u6485\u7740\u5c0f\u5634 \u513f\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u4e5f\u62ff\u6211\u53d6\u7b11\uff1f\u6211\u53ef\u4e0d\u6562\u670d\u4f8d\u4f60\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u5f85\u5979\u6709\u5982\u59d0\u59b9\uff0c\u5e73\u7d20\u4e5f\u5e38\u8bf4 \u7b11\uff0c\u89c1\u5979\u602a\u4e0d\u597d\u610f\u601d\u7684\uff0c\u4e00\u7b11\u4f5c\u7f62\u3002\u4e09\u4eba\u56de\u5230\u5e02\u533a\uff0c\u5df2\u662f\u5929\u8272\u5927\u660e\uff0c\u5f7b\u591c\u72c2\u6b22\u7684\u4eba\u7fa4\uff0c\u8fd9\u65f6 \u624d\u6e10\u6e10\u6563\u53bb\u3002 \u3000\u3000\u4e09\u65e5\u4e4b\u540e\uff0c\u51b0\u5ddd\u5929\u5973\u8fd9\u4e00\u884c\u4eba\u79bb\u5f00\u62c9\u8428\uff0c\u51c6\u5907\u7a7f\u8fc7\u897f\u85cf\uff0c\u8fdb\u5165\u56de\u7586\u3002\u4ed6\u4eec\u5728\u62c9\u8428\u9017\u7559\u4e09 \u65e5\uff0c\u4e3a\u7684\u5c31\u662f\u63a2\u542c\u9f99\u7075\u77eb\u7684\u4e8b\u60c5\u3002\u9f99\u7075\u77eb\u7684\u6848\u5b50\u5230\u4e86\u798f\u5eb7\u5b89\u624b\u4e2d\uff0c\u679c\u7136\u5927\u6709\u8f6c\u673a\uff0c\u798f\u5eb7\u5b89\u5c06 \u9f99\u7075\u77eb\u6263\u7559\u8d77\u6765\uff0c\u867d\u7136\u4ecd\u662f\u5c06\u4ed6\u5f53\u4f5c\u72af\u4eba\uff0c\u6253\u5165\u56da\u7262\uff0c\u4f46\u603b\u80dc\u5e72\u5c06\u4ed6\u4ea4\u4e0e\u8840\u795e\u5b50\u4e86\u3002\u798f\u5eb7\u5b89 \u7684\u4e3b\u610f\u662f\u8981\u5148\u95ee\u660e\u7687\u4e0a\uff0c\u518d\u884c\u53d1\u843d\uff0c\u8fd9\u6837\u4e00\u6765\u4e00\u56de\uff0c\u6700\u5c11\u4e5f\u5f97\u534a\u5e74\uff0c\u9f99\u7075\u77eb\u7684\u6848\u5b50\u5c31\u8fd9\u6837\u7684 \u88ab\u6401\u7f6e\u8d77\u6765\uff0c\u56e0\u800c\u5510\u7ecf\u5929\u4e5f\u653e\u5fc3\u8d70\u4e86\u3002 \u3000\u3000\u5176\u65f6\u5df2\u662f\u51ac\u53bb\u6625\u6765\uff0c\u79ef\u96ea\u867d\u5c1a\u672a\u6eb6\u89e3\uff0c\u6bd4\u4e25\u51ac\u5b63\u8282\uff0c\u5374\u5df2\u5bb9\u6613\u884c\u8d70\u5f97\u591a\u3002\u4e09\u4eba\u811a\u7a0b\u53c8 \u5feb\uff0c\u5341\u4f59\u65e5\u540e\uff0c\u5df2\u4ece\u897f\u85cf\u7684\u5357\u90e8\u8fdb\u5165\u4e86\u56de\u7586\u7684\u5854\u91cc\u6728\u76c6\u5730\u3002 \u3000\u3000\u4e00\u8def\u884c\u6765\uff0c\u53ea\u89c1\u9ec4\u6c99\u6f20\u6f20\uff0c\u5c71\u8109\u7ef5\u5ef6\uff0c\u51b0\u5ddd\u5929\u5973\u53f9\u9053\uff1a\u201c\u4e2d\u56fd\u5730\u65b9\u771f\u5927\uff0c\u8fdc\u8fdc\u671b\u53bb\u90a3\u5ea7 \u9ad8\u63d2\u5165\u4e91\u7684\u5927\u5c71\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u4fbf\u662f\u95fb\u540d\u4e16\u754c\u7684\u5929\u5c71\u4e86\uff0c\u8fd9\u91cc\u7684\u5c71\u8109\u90fd\u662f\u5b83 \u7684\u5206\u652f\uff0c\u5929\u5c71\u5c71\u8109\u7ef5\u5ef6\u4e09\u5343\u591a\u91cc\uff0c\u5357\u5317\u4e24\u9ad8\u5cf0\u4e5f\u76f8\u53bb\u4e00\u5343\u91cc\u5462\u3002\u201d\u51b0\u5ddd\u5929\u5973\u672c\u6765\u5174\u81f4\u52c3\u52c3\uff0c \u542c\u4ed6\u63d0\u8d77\u5929\u5c71\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u5fae\u9732\u4e0d\u60a6\u4e4b\u8272\u3002\u5510\u7ecf\u5929\u5c1a\u672a\u53d1\u89c9\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4ece\u6b64\u5904\u4e1c\u884c\u53ef\u5165 \u7518\u8083\uff0c\u6cbf\u7740\u53e4\u65f6\u6c49\u5218\u90a6\u6240\u4fee\u7684\u6808\u9053\uff0c\u4fbf\u53ef\u8fdb\u5165\u5ddd\u897f\uff0c\u82e5\u7136\u5317\u884c\uff0c\u53ef\u5230\u5929\u5c71\uff0c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u613f \u4e0d\u613f\u5148\u5230\u5929\u5c71\u4e00\u6e38\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5ffd\u5730\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u51e1\u662f\u5929\u4e0b\u4e60\u6b66\u4e4b\u4eba\u90fd\u8981\u5230\u4f60\u4eec\u5929 \u5c71\u53bb\u671d\u62dc\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u8be7\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u4ee4\u5c0a\u4e5f\u662f\u6e90\u51fa\u5929\u5c71\u4e00\u6d3e\uff0c\u600e\u4e48\u2018\u4f60\u4eec\u2019\u2018\u6211 \u4eec\u2019\u7684\u751f\u5206\u8d77\u6765\u4e86\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u51b7\u7b11\u4e0d\u7b54\uff0c\u53ea\u987e\u8d76\u8def\uff0c\u628a\u5510\u7ecf\u5929\u5f04\u5f97\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u5927\u6f20\u4e0a\u7ecf\u5e38\u662f\u6570\u5341\u91cc\u4e0d\u89c1\u4eba\u70df\uff0c\u53ea\u62e3\u6709\u6c34\u8349\u7684\u5730\u65b9\u4fbf\u652f\u8d77\u5e10\u5e55\u8fc7\u591c\uff0c\u8fd9\u4e00\u65e5\u4ed6\u4eec\u8d70\u4e86\u4e00 \u767e\u591a\u91cc\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u627e\u5230\u4e00\u4e2a\u4e18\u9675\u9ad8\u5730\uff0c\u53ef\u4ee5\u906e\u98ce\u6c99\u3002\u4ed6\u4eec\u4fbf\u5728\u80cc\u5c71\u9634\u5904\uff0c\u652f\u8d77\u5e10\u5e55\u3002\u51b0\u5ddd \u5929\u5973\u4e0e\u5e7d\u840d\u540c\u5bbf\u4e00\u4e2a\u5e10\u5e55\uff0c\u5510\u7ecf\u5929\u5728\u79bb\u5f00\u534a\u91cc\u4e4b\u5730\uff0c\u53e6\u5916\u72ec\u81ea\u4e00\u4e2a\u5e10\u5e55\u3002\u8fd9\u4e00\u665a\u51b0\u5ddd\u5929\u5973\u5fc3 \u601d\u5982\u6f6e\uff0c\u7761\u4e0d\u7740\u89c9\uff0c\u4e0e\u4f8d\u5973\u5e7d\u840d\u5728\u5e10\u4e2d\u95f2\u8bdd\uff0c\u51b0\u5ddd\u5929\u5973\u62ff\u5979\u53d6\u7b11\uff0c\u7b11\u5979\u820d\u4e0d\u5f97\u79bb\u5f00\u9648\u5929\u5b87\uff0c \u7b11\u5979\u4e00\u4e0b\u5c71\u5c31\u5ff5\u604b\u5c18\u4e16\u7684\u7e41\u534e\uff0c\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u9648\u5929\u5b87\u81ea\u6709\u4ed6\u7684\u829d\u5a1c\u59d0\u59d0\uff0c\u6211\u548c\u4ed6\u4e0d\u8fc7\u59d0\u5f1f\u4e00 \u822c\uff0c\u54ea\u8c08\u5f97\u4e0a\u513f\u5973\u4e4b\u60c5\u3002\u5012\u662f\u4f60\u5440\uff01\u201d\u51b0\u5ddd\u5929\u5973\u6120\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u6709\u4ec0\u4e48\u7ed9\u4f60\u8bf4\u7684\uff1f\u201d\u5e7d\u840d \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5510\u76f8\u516c\u7684\u4eba\u54c1\u6b66\u529f\u90a3\u5012\u771f\u662f\u6ca1\u6709\u53ef\u8bf4\u7684\uff0c\u4f60\u4e24\u6b21\u5f39\u7434\uff0c\u6211\u90fd\u542c\u89c1\u4e86\u5462\u3002\u563b\u563b\uff0c \u5357\u6709\u4e54\u6728\uff0c\u4e0d\u53ef\u4f11\u601d\u3002\u6c49\u6709\u6e38\u5973\uff0c\u4e0d\u53ef\u6c42\u601d\u3002\u563b\u563b\uff0c\u4f60\u4e0d\u6015\u4ed6\u6c42\u4e4b\u4e0d\u5f97\uff0c\u8f97\u8f6c\u53cd\u4fa7\u5417\uff1f\u201d\u51b0 \u5ddd\u5929\u5973\u4f6f\u55d4\u9053\uff1a\u201c\u4f60\u518d\u4e71\u56bc\u820c\u5934\uff0c\u770b\u6211\u6495\u4e0d\u6495\u7834\u4f60\u7684\u5634\uff01\u201d \u3000\u3000\u4e3b\u4ec6\u6b63\u5728\u4e92\u76f8\u53d6\u7b11\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u6709\u545c\u545c\u4e4b\u58f0\uff0c\u9690\u9690\u53ef\u95fb\uff0c\u51b0\u5ddd\u5929\u5973\u9762\u8272\u4e00\u53d8\uff0c\u51dd\u795e\u9759 \u542c\uff0c\u90a3\u602a\u58f0\u6709\u70b9\u4f3c\u5439\u89d2\u4e4b\u58f0\uff0c\u53c8\u4f3c\u5c3c\u6cca\u5c14\u4e00\u79cd\u7279\u6709\u7684\u4e50\u5668\u6240\u53d1\u4e4b\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u5ffd\u9053\uff1b\u201c\u6211\u51fa \u53bb\u77a7\u77a7\uff0c\u4f60\u4e0d\u8981\u60ca\u52a8\u5510\u76f8\u516c\u3002\u201d\u53d6\u4e86\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u7acb\u523b\u8dc3\u51fa\u5e10\u5916\uff0c\u7fe9\u5982\u98de\u9e1f\uff0c\u63a0\u5165\u4e86\u9ec4\u6c99\u6f20 \u6f20\u4e4b\u4e2d\u3002 \u3000\u3000\u5927\u6f20\u4e0a\u867d\u6709\u4e18\u9675\uff0c\u6708\u5149\u5374\u662f\u5206\u5916\u660e\u4eae\uff0c\u51b0\u5ddd\u5929\u5973\u63d0\u4e00\u53e3\u6c14\uff0c\u5954\u51fa\u4e86\u4e03\u516b\u91cc\u8def\uff0c\u679c\u7136\u5728\u4e00 \u7247\u8349\u5730\u4e0a\uff0c\u89c1\u7740\u4e00\u56e2\u4eba\u6b63\u5728\u53ae\u62fc\u3002\u5200\u5251\u78b0\u51fb\u4e4b\u58f0\u5212\u8fc7\u591c\u7a7a\uff0c\u58f0\u58f0\u7d27\u63a5\uff0c\u9707\u52a8\u8033\u819c\uff0c\u6253\u5f97\u5341\u5206 \u6fc0\u70c8\u3002\u51b0\u5ddd\u5929\u5973\u5b9a\u775b\u4e00\u77a7\uff0c\u5374\u539f\u6765\u662f\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5408\u6218\u6b66\u6c0f\u5144\u5f1f\uff0c\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5404 \u4f7f\u4e00\u67c4\u6708\u7259\u5f2f\u5200\uff0c\u5f2f\u5200\u7684\u4e0a\u534a\u622a\u5200\u67c4\u6402\u7a7a\uff0c\u8fce\u98ce\u9f13\u8361\uff0c\u545c\u545c\u6709\u58f0\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb \u7684\u5200\u6cd5\u867d\u7136\u751a\u662f\u51f6\u731b\uff0c\u4f46\u6b66\u6c0f\u5144\u5f1f\u7684\u5251\u6cd5\u66f4\u52a0\u795e\u5999\uff0c\u5251\u52bf\u5982\u8679\uff0c\u6740\u5f97\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u53ea\u6709 \u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u6b63\u81ea\u6740\u5f97\u6027\u8d77\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u5954\u6765\uff0c\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u53eb\u9053\uff1a\u201c\u53e4\u9c81\u5df4\uff0c\u4e4c\u9ed1\u8d64 \u8ff7\uff0c\u4e5e\u513f\u8d64\u8d64\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5495\u5580\u5495\u5580\u7684\u8bf4\u4e86\u51e0\u53e5\uff0c\u4f3c\u4e4e\u662f\u95ee\u8bdd\u7684\u53e3\u6c14\uff0c\u6b66\u6c0f\u5144\u5f1f\u4e00\u53e5\u4e5f\u542c\u4e0d \u61c2\uff0c\u6b66\u8001\u4e8c\u6027\u5b50\u6700\u6025\uff0c\u9a82\u9053\uff1a\u201c\u6709\u8bdd\u5411\u960e\u7f57\u738b\u8bf4\u53bb\u3002\u201d\u9aa4\u7684\u624b\u8155\u4e00\u7ffb\uff0c\u5251\u950b\u5f80\u4e0a\u4e00\u5708\uff0c\u5251\u5c16 \u4e00\u62d6\uff0c\u671d\u7740\u8bf4\u8bdd\u7684\u90a3\u4e2a\u6b66\u58eb\u9888\u4e0a\u4e00\u52d2\uff0c\u8fd9\u4e00\u5251\u5389\u5bb3\u975e\u5e38\uff0c\u90a3\u5c3c\u6cca\u5c14\u6b66\u58eb\u7684\u6708\u7259\u5f2f\u5200\u6b63\u88ab\u6b66\u8001 \u5927\u7684\u957f\u5251\u5c01\u4efb\uff0c\u64a4\u4e0d\u56de\u6765\uff0c\u770b\u770b\u54bd\u5589\u5c31\u8981\u88ab\u5251\u950b\u5272\u65ad\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201c\u5251\u4e0b\u7559\u4eba\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u6b66\u6c0f\u5144\u5f1f\u9661\u89c9\u5bd2\u5149\u75be\u5c04\uff0c\u51b7\u6c14\u4fb5\u80a4\uff0c\u90fd\u4e0d\u7531 \u81ea\u5df1\u7684\u5012\u9000\u4e09\u6b65\uff0c\u540c\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5973\u80c6\u6562\u5728\u8fd9\u91cc\u6a2a\u884c\uff0c\u54fc\u54fc\uff0c\u82e5\u4e0d\u7ed9\u4f60\u4e00\u70b9\u6559\u8bad\uff0c\u4f60\u8fd8 \u771f\u5f53\u662f\u54b1\u4e2d\u56fd\u65e0\u4eba\u80fd\u5236\u670d\u4f60\uff01\u201d\u53cc\u5251\u9f50\u51fa\uff0c\u5206\u523a\u51b0\u5ddd\u5929\u5973\u5de6\u53f3\u4e24\u808b\u7a74\u9053\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u7ec8\u5357\u6d3e \u5251\u6cd5\u4e2d\u7684\u6740\u624b\u7edd\u62db\uff0c\u540d\u4e3a\u201c\u957f\u8679\u8d2f\u65e5\u201d\u53cc\u5251\u5408\u4f7f\uff0c\u5a01\u529b\u66f4\u662f\u5927\u4e86\u4e00\u500d\u6709\u591a\u3002\u51b0\u5ddd\u5929\u5973\u67f3\u7709\u4e00 \u7ad6\uff0c\u5bd2\u5149\u5251\u9aa4\u7136\u4e00\u6296\uff0c\u4f46\u89c1\u5251\u82b1\u9519\u843d\uff0c\u4e00\u67c4\u5251\u5c31\u5982\u5316\u6210\u4e86\u5341\u6570\u67c4\u4e00\u822c\uff0c\u6b66\u6c0f\u5144\u5f1f\u5403\u4e86\u4e00\u60ca\uff0c \u4f46\u89c9\u5230\u5904\u90fd\u662f\u5229\u5251\u523a\u6765\uff0c\u6025\u5fd9\u56de\u5251\u9648\u8eab\u3002\u4ed6\u4eec\u53cc\u5251\u5408\u58c1\u7684\u5389\u5bb3\u6740\u624b\uff0c\u4e00\u7167\u9762\u5c31\u88ab\u51b0\u5ddd\u5929\u5973\u8f7b \u63cf\u6de1\u5199\u5730\u5316\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u4f46\u51b0\u5ddd\u5929\u5973\u5374\u5e76\u4e0d\u4e58\u52bf\u53cd\u51fb\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5df2\u8df3\u5f00\u4e00\u8fb9\uff0c\u8dea\u5728\u5730\u4e0a\uff0c\u597d\u4f3c\u7980\u544a \u4e00\u822c\uff0c\u7d6e\u7d6e\u4ed6\u8bf4\u4e2a\u4e0d\u4f11\u3002\u51b0\u5ddd\u5929\u5973\u633d\u7740\u5251\u67c4\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u5212\uff0c\u597d\u4f3c\u6f2b\u4e0d\u7ecf\u610f\u5730\u5c06\u6b66\u6c0f\u5144\u5f1f \u7684\u62db\u6570\u4e00\u4e00\u7834\u5f00\uff0c\u5076\u800c\u4e5f\u95ee\u90a3\u4e24\u4e2a\u6b66\u58eb\u51e0\u53e5\uff0c\u4ed6\u4eec\u8bf4\u7684\u662f\u5c3c\u6cca\u5c14\u8bdd\uff0c\u6b66\u6c0f\u5144\u5f1f\u5b8c\u5168\u4e0d\u61c2\u3002\u51b0 \u5ddd\u5929\u5973\u672c\u6765\u662f\u7ef7\u7740\u4e00\u5f20\u4fcf\u8138\uff0c\u9762\u8272\u6e29\u6012\uff0c\u968f\u7740\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u7684\u7980\u544a\uff0c\u5374\u6e10\u89c1\u67d4\u548c\uff0c\u542c\u5230 \u540e\u6765\uff0c\u8fd8\u70b9\u4e86\u70b9\u5934\uff0c\u610f\u4f3c\u5609\u8bb8\uff0c\u5fae\u5fae\u9732\u51fa\u7b11\u5bb9\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7684\u9762\u8272\u7531\u6e29\u6012\u800c\u53d8\u4e3a\u67d4\u548c\uff0c\u6b66\u6c0f\u5144\u5f1f\u5374\u88ab\u5979\u6fc0\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u8981\u77e5\u6b66 \u6c0f\u5144\u5f1f\u4e43\u662f\u540d\u5bb6\u4e4b\u540e\uff0c\u7d20\u4ee5\u5251\u6cd5\u81ea\u8d1f\uff1b\u51b0\u5ddd\u5929\u5973\u5374\u4e00\u9762\u8c08\u8bdd\uff0c\u4e00\u9762\u62c6\u62db\uff0c\u7adf\u597d\u4f3c\u620f\u800d\u4e00\u822c\uff0c \u5168\u4e0d\u628a\u4ed6\u4eec\u653e\u5728\u773c\u5185\u3002 \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u672c\u6765\u5c31\u5bf9\u51b0\u5ddd\u5929\u5973\u6000\u6709\u654c\u610f\uff0c\u5728\u62a2\u593a\u91d1\u672c\u5df4\u74f6\u4e4b\u65f6\uff0c\u82e5\u975e\u5510\u7ecf\u5929\u5728\u573a\u529d\u6b62\uff0c\u4ed6 \u4eec\u65e9\u5df2\u60f3\u4e0e\u51b0\u5ddd\u5929\u5973\u8fc7\u62db\uff0c\u8fd9\u65f6\u89c1\u5979\u5305\u5e87\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u8d8a\u53d1\u8ba4\u5b9a\u51b0\u5ddd\u5929\u5973\u4e0e\u4ed6\u95e8\u4e43\u662f \u4e00\u4e18\u4e4b\u8c89\uff0c\u66f4\u517c\u51b0\u5ddd\u5929\u5973\u597d\u4f3c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u4e00\u9762\u8c08\u8bdd\uff0c\u4e00\u9762\u62c6\u62db\uff0c\u66f4\u4ee4\u4ed6\u4eec\u96be\u582a\u3002\u4e24\u5144\u5f1f\u4e00\u58f0 \u80e1\u54e8\uff0c\u5251\u6cd5\u9aa4\u53d8\u3002\u4f7f\u51fa\u7ec8\u5357\u6d3e\u7684\u4e71\u62ab\u98ce\u5251\u6cd5\uff0c\u53cc\u5251\u9f50\u98de\uff0c\u4e00\u4e0b\u4e00\u53cd\uff0c\u5168\u90fd\u662f\u653b\u51fb\u7684\u62db\u6570\uff0c\u8fd9 \u5957\u5251\u6cd5\u3002\u5171\u6709\u5341\u516b\u62db\u6740\u624b\uff0c\u5faa\u73af\u5f80\u590d\uff0c\u5947\u6b63\u76f8\u751f\uff0c\u56e0\u662f\u53cc\u5251\u8054\u653b\uff0c\u6240\u4ee5\u5168\u65e0\u9632\u5b88\uff0c\u771f\u5982\u72c2\u98ce \u66b4\u96e8\uff0c\u75be\u5377\u800c\u6765\uff0c\u5f62\u540c\u62fc\u547d\u3002\u51b0\u5ddd\u5929\u5973\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00\u50f3\uff0c\u867d\u7136\u4ecd\u662f\u795e\u8272\u81ea\u5982\u5730\u4e00\u9762\u548c\u90a3\u4e24 \u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u8bf4\u8bdd\uff0c\u4f46\u5374\u4e0d\u6562\u50cf\u5148\u524d\u90a3\u4e48\u5927\u610f\u4e86\u3002 \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u4e00\u9635\u5f3a\u653b\uff0c\u4f46\u89c1\u51b0\u5ddd\u5929\u5973\u90a3\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u5b9d\u5251\u4e5f\u8d8a\u4f7f\u8d8a\u75be\uff0c\u7adf\u4f3c\u5316\u6210\u4e86\u4e00\u5ea7\u5149 \u5e62\uff0c\u7f69\u7740\u5168\u8eab\uff0c\u53c8\u5982\u5728\u5468\u56f4\u7b51\u8d77\u4e86\u4e00\u5ea7\u5251\u5899\uff0c\u600e\u4e48\u6837\u4e5f\u653b\u4e0d\u8fdb\u53bb\u3002\u4e24\u5144\u5f1f\u6b63\u81ea\u60ca\u5fc3\uff0c\u5ffd\u542c\u5f97 \u51b0\u5ddd\u5929\u5973\u5927\u58f0\u4ed6\u8bf4\u4e86\u4e00\u53e5\u5c3c\u6cca\u5c14\u8bdd\uff0c\u5411\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u6325\u4e86\u6325\u624b\uff0c\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5982\u83b7 \u5927\u8d66\uff0c\u5728\u5730\u4e0a\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u722c\u4e86\u8d77\u6765\uff0c\u7acb\u523b\u98de\u8dd1\u3002\u6b66\u6c0f\u5144\u5f1f\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u53bb\u8ffd\u8d76\uff0c\u53c8\u88ab\u51b0 \u5ddd\u5929\u5973\u7684\u5251\u5149\u7f69\u4f4f\uff0c\u6446\u8131\u4e0d\u5f00\uff0c\u6b63\u81ea\u7740\u6025\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u7b11\u4e86\u4e00\u58f0\uff0c\u5251\u5149\u4e00\u8361\uff0c\u6b66\u6c0f\u5144\u5f1f\u7684 \u4e24\u53e3\u957f\u5251\u51e0\u4e4e\u7ed9\u9707\u5f97\u8131\u624b\u98de\u53bb\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u6025\u5fd9\u540e\u9000\uff0c\u51b0\u5ddd\u5929\u5973\u7b11\u4e86\u4e00\u7b11\uff0c\u5ffd\u7136\u7528\u6c49\u8bed\u8bf4 \u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u6b66\u58eb\u6211\u5df2\u8ba9\u4ed6\u4eec\u56de\u56fd\u4e86\uff0c\u4f60\u4eec\u4e5f\u90fd\u8d70\u5427\u3002\u201d\u8bf4\u5f97\u751a\u662f\u67d4\u548c\uff0c\u4f46\u5374\u9690\u6709\u4e00\u822c\u5a01 \u4e25\uff0c\u597d\u50cf\u662f\u5973\u738b\u5728\u9881\u53d1\u547d\u4ee4\u4e00\u822c\u3002 \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u662f\u4e16\u4ee3\u540d\u5bb6\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u8c01\u90fd\u656c\u4ed6\u4eec\u4e09\u5206\uff0c\u9664\u4e86\u6709\u9650\u7684\u51e0\u4e2a\u524d\u8f88\uff0c\u8c01\u4e5f\u4e0d \u6562\u5bf9\u4ed6\u4eec\u4e0b\u4ee4\uff0c\u51b0\u5ddd\u5929\u5973\u8bf4\u8bdd\u867d\u7136\u67d4\u548c\uff0c\u4ed6\u4eec\u5374\u662f\u52c3\u7136\u5927\u6012\uff0c\u6b66\u8001\u5927\u9a82\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u756a\u8d3c\u8dd1 \u6765\u6363\u4e71\uff0c\u4f60\u6562\u64c5\u81ea\u653e\u8d70\u4ed6\u4eec\uff0c\u4f60\u8981\u8d70\u4e5f\u4e0d\u6210\uff01\u201d\u6b66\u8001\u4e8c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5973\uff0c\u6211\u4eec\u65e9\u770b\u51fa\u4f60\u4e0d \u662f\u597d\u4eba\uff0c\u83ab\u8bf4\u5510\u7ecf\u5929\u4e0d\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u5c31\u7b97\u4ed6\u6765\u4ee3\u4f60\u6c42\u60c5\uff0c\u6211\u4e5f\u4e0d\u80fd\u9976\u4f60\uff01\u201d\u4e24\u5144\u5f1f\u53e3\u53e3\u58f0\u58f0 \u7684\u5927\u9a82\u201c\u5996\u5973\u201d\uff0c\u7adf\u7136\u4e0d\u60e7\u51b0\u9b44\u5bd2\u5149\u5251\u7684\u5a01\u529b\uff0c\u7f20\u6597\u4e0d\u4f11\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u521d\u89c1\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e4b\u65f6\uff0c\u672c\u6765\u751a\u4e3a\u607c\u6012\uff0c\u4f46\u95ee\u660e\u4e4b\u540e\uff0c\u59cb\u77e5\u4ed6\u4eec\u5e76\u4e0d\u662f\u6562 \u4e8e\u8fdd\u6297\u81ea\u5df1\u7684\u547d\u4ee4\uff08\u51b0\u5ddd\u5929\u5973\u5728\u593a\u56de\u91d1\u74f6\u4e4b\u65f6\uff0c\u66fe\u5429\u5490\u8fc7\u4ed6\u4eec\uff0c\u8981\u4ed6\u4eec\u5373\u884c\u56de\u56fd\uff0c\u4e0d\u5f97\u518d\u5728 \u4e2d\u56fd\u6363\u4e71\u7684\uff09\uff0c\u800c\u662f\u56e0\u4e3a\u56de\u7586\u5c1a\u6709\u5c3c\u6cca\u5c14\u56fd\u738b\u6d3e\u6765\u7684\u51e0\u4e2a\u6b66\u58eb\uff0c\u4ed6\u4eec\u60f3\u5230\u56de\u7586\u6765\u901a\u77e5\u4ed6\u4eec\uff0c \u53eb\u4ed6\u4eec\u4e00\u540c\u56de\u56fd\uff0c\u54ea\u77e5\u88ab\u6b66\u6c0f\u5144\u5f1f\u53d1\u73b0\uff0c\u4ee5\u4e3a\u4ed6\u4eec\u4e0d\u6000\u597d\u610f\uff0c\u4e00\u8def\u8ffd\u8e2a\u800c\u6765\uff0c\u7ec8\u4e8e\u53d1\u751f\u4e86\u4e00 \u573a\u6076\u6597\u3002\u51b0\u5ddd\u5929\u5973\u672c\u662f\u4e00\u573a\u597d\u610f\uff0c\u610f\u56fe\u95ee\u660e\u662f\u975e\uff0c\u518d\u884c\u5904\u7f6e\uff0c\u521d\u610f\u5e76\u975e\u504f\u8892\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66 \u58eb\uff0c\u5374\u4e0d\u6599\u53c8\u5f15\u6765\u4e86\u4e00\u573a\u8bef\u4f1a\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u9ad8\u6c14\u50b2\uff0c\u88ab\u6b66\u6c0f\u5144\u5f1f\u4f60\u4e00\u53e5\u6211\u4e00\u53e5\u5730\u9a82\u5979\u201c\u5996\u5973\u201d\uff0c\u8fd8\u628a\u5510\u7ecf\u5929\u626f\u4e86\u8fdb\u6765\uff0c \u7ea0\u7f20\u4e0d\u6e05\uff0c\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6e29\u6012\uff0c\u53d1\u4e86\u813e\u6c14\u3002 \u3000\u3000\u6b66\u6c0f\u5144\u5f1f\u5404\u81ea\u957f\u5578\u4e00\u58f0\uff0c\u62d4\u811a\u4fbf\u8dd1\uff0c\u8fb9\u8dd1\u8fb9\u9a82\u201c\u5996\u5973\u201d\uff0c\u51b0\u5ddd\u5929\u5973\u5927\u6012\uff0c\u5c55\u5f00\u8eab\u5f62\uff0c\u7acb \u5373\u8ffd\u8d76\uff0c\u90a3\u67c4\u51b0\u9b44\u5bd2\u5149\u5251\u5ffd\u5de6\u5ffd\u53f3\uff0c\u59cb\u7ec8\u662f\u8f6e\u6d41\u8d34\u7740\u4e24\u5144\u5f1f\u7684\u80cc\u5fc3\uff0c\u90a3\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u6e10\u6e10\u9694 \u7740\u8863\u88f3\u4f20\u5165\u4f53\u5185\uff0c\u4f46\u6b66\u6c0f\u5144\u5f1f\u4e5f\u6e9c\u6ed1\u5f97\u5f88\uff0c\u4f3c\u662f\u731c\u5230\u51b0\u5ddd\u5929\u5973\u7684\u5fc3\u610f\uff0c\u6599\u5979\u4e0d\u6562\u65bd\u5c55\u6740\u624b\uff0c \u4e00\u89c9\u88ab\u5979\u7684\u5251\u5c16\u6cbe\u4e0a\uff0c\u7acb\u5373\u53cd\u5251\u5f3a\u653b\uff0c\u53cc\u5251\u914d\u5408\uff0c\u62db\u6570\u51cc\u5389\uff0c\u4e5f\u5f80\u5f80\u8feb\u5f97\u51b0\u5ddd\u5929\u5973\u4e0d\u80fd\u4e0d\u64a4 \u5251\u62db\u67b6\u3002\u5c31\u8fd9\u6837\u7684\u76f4\u8ffd\u51fa\u4e94\u516d\u91cc\u5730\uff0c\u6b66\u6c0f\u5144\u5f1f\u867d\u7136\u62fc\u529b\u5316\u89e3\uff0c\u4f46\u6280\u900a\u4e00\u7b79\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5251\u5c16 \u59cb\u7ec8\u662f\u5982\u5f71\u968f\u5f62\uff0c\u7d27\u7d27\u8ffd\u8feb\u3002\u4e24\u5144\u5f1f\u8fd0\u6c14\u62b5\u5fa1\uff0c\u6e10\u89c9\u96be\u4ee5\u5fcd\u53d7\uff0c\u51b7\u5f97\u7259\u9f7f\u6253\u6218\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u7b11\u9053\uff1a\u201c\u8fd8\u6562\u4e71\u9a82\u4eba\u4e48\uff1f\u201d\u5ffd\u542c\u5f97\u6b66\u6c0f\u5144\u5f1f\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u571f\u5806\u65c1\u8fb9\u7a81\u7136\u73b0 \u51fa\u4e00\u4e2a\u5c11\u5973\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u770b\u5f97\u5206\u660e\uff0c\u4e00\u8eab\u7d2b\u8272\u8863\u88f3\uff0c\u53d1\u675f\u91d1\u73af\uff0c\u957f\u7709\u5982\u753b\uff0c\u7b11\u5f97\u5982\u82b1\u679d\u4e71 \u98a4\uff0c\u6307\u7740\u6b66\u6c0f\u5144\u5f1f\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u5982\u4eca\u53ef\u78b0\u5230\u82e6\u5934\u4e86\uff0c\u771f\u662f\u4e22\u4eba\u73b0\u4e16\u3002\u8fd8\u4e0d\u8d76\u5feb\u7ed9\u6211\u9000 \u4e0b\u53bb\uff01\u201d\u6b66\u6c0f\u5144\u5f1f\u540c\u58f0\u53eb\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u8fd9\u5996\u5973\u597d\u5389\u5bb3\uff0c\u4f60\u5f97\u5c0f\u5fc3\uff0c\u8fd8\u662f\u8bf7\u5979\u8001\u4eba\u5bb6\u6765\u5427\uff01\u201d \u90a3\u5c11\u5973\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u4f60\u8fd9\u4e24\u4e2a\u8349\u5305\u8d76\u5feb\u9000\u5f00\uff0c\u8fd9\u4e00\u70b9\u4e8b\u60c5\uff0c\u8fd8\u8981\u52b3\u52a8\u5979\u8001\u4eba\u5bb6\u51fa\u624b\u5417\uff1f\u201d \u8fd9\u5c11\u5973\u4e00\u526f\u8dc3\u8dc3\u6b32\u8bd5\u7684\u6837\u5b50\uff0c\u7a1a\u6c14\u672a\u6d88\uff0c\u770b\u6765\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\uff0c\u6bd4\u6b66\u6c0f\u5144\u5f1f\u5e74\u8f7b\u5f97\u591a\uff0c\u4f46\u542c\u6b66 \u6c0f\u5144\u5f1f\u5bf9\u5979\u7684\u79f0\u547c\uff0c\u5979\u7684\u8f88\u4efd\u5374\u4f3c\u4e4e\u6bd4\u6b66\u6c0f\u5144\u5f1f\u9ad8\u4e86\u4e00\u8f88\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u7a81\u7136\u51fa\u73b0\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u7531\u5f97\u505c\u4e0b\u624b\u6765\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u4e0d\u4f4f\u5730\u5411\u81ea\u5df1\u6253\u91cf\uff0c\u5ffd\u800c\u7b11 \u9053\uff1a\u201c\u4f60\u8fd9\u67c4\u5251\u771f\u597d\u73a9\uff0c\u5149\u95ea\u95ea\u7684\uff0c\u662f\u4ec0\u4e48\u4e1c\u897f\u6253\u7684\uff1f\u201d\u6d3b\u50cf\u4e00\u4e2a\u5c0f\u5b69\u5b50\u770b\u5230\u4e00\u4ef6\u65b0\u5947\u7684\u73a9 \u5177\uff0c\u5728\u5567\u5567\u79f0\u8d4f\u7684\u795e\u6c14\u3002\u51b0\u5ddd\u5929\u5973\u4e0d\u89c9\u5931\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u53ef\u4e0d\u662f\u597d\u73a9\u7684\uff0c\u6211\u60f3\u9001\u7ed9\u4f60\u73a9\uff0c \u4f60\u4e5f\u4e0d\u80fd\u62ff\u5b83\u5462\uff0c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u62ff\u4e0d\u5f97\uff1f\u5988\uff0c\u4f60\u51c6\u4e0d\u51c6\u6211\u62ff\u522b\u4eba\u7684\u4e1c \u897f\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u4e00\u6014\uff0c\u518d\u4e00\u770b\u65f6\uff0c\u5ffd\u89c1\u571f\u5806\u65c1\u8fb9\u53c8\u591a\u4e86\u4e00\u4e2a\u4e2d\u5e74\u5973\u4eba\uff0c\u4e00\u8eab\u9ed1\u8272\u8863\u88f3\uff0c\u5934\u4e0a \u5374\u7ed3\u7740\u4e24\u53ea\u4e1d\u7ef8\u767d\u8774\u8776\uff0c\u7b11\u772f\u772f\u5730\u770b\u7740\u81ea\u5df1\u3002\u51b0\u5ddd\u5929\u5973\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u5973\u4eba \u6765\u5f97\u5982\u6b64\u5feb\u6cd5\uff1f\u65e0\u58f0\u65e0\u606f\uff0c\u8fde\u6211\u4e5f\u5bdf\u89c9\u4e0d\u51fa\uff1f\u201d\u8fd9\u4e2d\u5e74\u5987\u4eba\u53d1\u5f0f\u5374\u4f5c\u5c11\u5973\u6253\u626e\uff0c\u6700\u5999\u7684\u662f\u5979 \u7b11\u563b\u563b\u7684\uff0c\u8fde\u795e\u6001\u4e5f\u50cf\u4e00\u4e2a\u6dd8\u6c14\u7684\u5c0f\u59d1\u5a18\uff0c\u51b0\u5ddd\u5929\u5973\u6697\u7b11\u9053\uff1a\u201c\u771f\u662f\u6709\u5176\u6bcd\u5fc5\u6709\u5176\u5973\uff0c\u4e14\u770b \u5979\u4eec\u600e\u6837\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u4e2d\u5e74\u5987\u4eba\u7b11\u9053\uff1a\u201c\u6885\u513f\uff0c\u8fd9\u4f4d\u59d0\u59d0\u6bd4\u4f60\u9ad8\u660e\u5f97\u591a\u5462\uff0c\u4f60\u4e0d\u4fe1\u5c31\u8bd5\u8bd5\u770b\u3002\u4f60\u60f3\u62ff \u5979\u7684\u4e1c\u897f\u4e5f\u62ff\u4e0d\u5230\u3002\u5582\uff0c\u5927\u6b66\u5c0f\u6b66\uff0c\u8fc7\u6765\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u548c\u5979\u6253\u67b6\uff1f\u201d\u6b66\u6c0f\u5144\u5f1f\u8dd1\u5230\u90a3\u4e2d\u5e74\u5987 \u4eba\u8eab\u8fb9\uff0c\u537f\u537f\u5495\u5495\u7684\u8bf4\u4e86\u4e00\u5927\u904d\uff0c\u51b0\u5ddd\u5929\u5973\u53ea\u542c\u5230\u51e0\u58f0\u201c\u5996\u5973\u2019\u7684\u9a82\u58f0\uff0c\u4f3c\u4e4e\u662f\u6545\u610f\u9a82\u7ed9\u5979 \u542c\u7684\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u6012\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u5ffd\u89c1\u90a3\u5c11\u5973\u9f13\u8d77\u5634\u5df4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u603b\u662f\u770b\u4e0d\u8d77\u6211\uff0c\u6211\u4e0d\u662f\u5c0f \u5b69\u5b50\u5566\uff0c\u4f60\u4e0d\u5fc5\u6559\u6211\uff0c\u6211\u5c31\u8bd5\u7ed9\u4f60\u770b\u3002\u201d\u5ffd\u5730\u51b2\u7740\u51b0\u5ddd\u5929\u5973\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u59d0\u59d0\uff0c\u6211\u8981\u501f\u4f60 \u8fd9\u628a\u5251\u73a9\u73a9\u4e86\uff0c\u4f60\u820d\u5f97\u4e48\uff1f\u201d\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u51cc\u7a7a\u6251\u4e0b\uff0c\u8eab\u6cd5\u602a\u5f02\u4e4b\u6781\uff0c\u5c31\u5982\u4e00\u53ea\u5927\u9e1f\u4e00 \u822c\uff0c\u51b0\u5ddd\u5929\u5973\u5927\u5403\u4e00\u60ca\uff0c\u6a2a\u5251\u4e00\u524a\uff0c\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u679c\u7136\u662f\u62ff\u4e0d\u5f97\u7684\uff01\u201d\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb \u8eab\uff0c\u5de6\u638c\u8f7b\u8f7b\u5411\u51b0\u5ddd\u5929\u5973\u80a9\u95f4\u62cd\u4e0b\uff0c\u53f3\u624b\u4f38\u51fa\u4e94\u6307\uff0c\u6765\u6263\u51b0\u5ddd\u5929\u5973\u7684\u8109\u95e8\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7684\u8f7b\u529f\u5df2\u662f\u4e16\u95f4\u5c11\u6709\u3002\u4f46\u8fd9\u5c11\u5973\u7adf\u4f3c\u9e1f\u513f\u4e00\u6837\u80fd\u5728\u7a7a\u4e2d\u56de\u7fd4\u8f6c\u6298\uff0c\u66f4\u662f\u60ca\u4eba\uff0c \u51b0\u5ddd\u5929\u5973\u4e00\u8fde\u4e09\u5251\u90fd\u88ab\u5979\u8f7b\u8f7b\u5de7\u5de7\u5730\u907f\u5f00\uff0c\u68cb\u9022\u5bf9\u624b\uff0c\u4e0d\u7531\u5f97\u7cbe\u795e\u9661\u957f\uff0c\u8eab\u6cd5\u4e00\u5c55\uff0c\u548c\u5979\u8ba4 \u771f\u53ae\u6597\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7a9c\u9ad8\u7eb5\u4f4e\uff0c\u65f6\u800c\u8dc3\u8d77\uff0c\u65f6\u800c\u6e38\u8d70\uff0c\u4f0a\u4f3c\u7a7f\u82b1\u8774\u8776\uff0c\u5341\u6307\u5ffd\u4f38\u5ffd\u5c48\uff0c\u8ddf\u7740\u51b0\u5ddd\u5929\u5973 \u7684\u5251\u5c16\u75be\u8f6c\uff0c\u51b0\u5ddd\u5929\u5973\u8d5e\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u4fca\u7684\u8eab\u6cd5\uff01\u201d\u76c8\u76c8\u4e00\u7b11\uff0c\u5251\u62db\u500f\u53d8\uff0c\u53ea\u542c\u5f97\u90a3\u4e2d\u5e74\u5987 \u4eba\u5148\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u53eb\u9053\uff1a\u201c\u6885\u513f\uff0c\u5c0f\u5fc3\uff0c\u8fd9\u662f\u8fbe\u6469\u5251\u6cd5\u5440\uff01\u201d\u90a3\u5c11\u5973\u8fde\u8fde\u8eb2\u95ea\uff0c\u51b0\u5ddd \u5929\u5973\u5251\u6cd5\u5c55\u5f00\uff0c\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\uff0c\u4f46\u89c1\u5bd2\u5149\u56db\u5c04\uff0c\u5ffd\u805a\u5ffd\u6563\uff0c\u8f7b\u7075\u5904\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u72e0\u75be\u5904\u53c8 \u6709\u5982\u51b0\u6cb3\u5012\u6cfb\uff0c\u90a3\u5c11\u5973\u5e78\u800c\u6709\u80fd\u591f\u51cc\u7a7a\u6251\u51fb\u7684\u7edd\u6280\uff0c\u907f\u8fc7\u4e86\u4e0d\u5c11\u9669\u62db\uff0c\u4ea6\u89c9\u5403\u529b\u975e\u5e38\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u89c1\u5979\u6bd4\u81ea\u5df1\u5e74\u5c0f\uff0c\u5fc3\u4e2d\u601c\u60dc\uff0c\u6b63\u60f3\u7f62\u624b\uff0c\u90a3\u5c11\u5973\u5e94\u9053\uff1a\u201c\u7a7a\u624b\u6253\u4e0d\u8fc7\u4f60\uff0c\u6211\u4e5f \u8981\u7528\u5251\u5566\uff01\u201d\u53ea\u89c1\u5979\u5728\u7a7a\u4e2d\u6251\u51fb\u800c\u4e0b\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u7cbe\u8292\u56db\u5c04\u7684\u77ed\u5251\uff0c\u62d4\u5251\u4e4b \u7094\uff0c\u8fde\u51b0\u5ddd\u5929\u5973\u4e5f\u770b\u4e0d\u6e05\uff0c\u51b0\u5ddd\u5929\u5973\u6b63\u4f7f\u5230\u4e00\u62db\u201c\u6625\u98ce\u89e3\u51bb\u201d\uff0c\u5251\u5c16\u4e24\u8fb9\u6643\u52a8\uff0c\u4e0a\u523a\u53cc\u76ee\uff0c \u5ffd\u89c1\u90a3\u5c11\u5973\u4e00\u5251\u5e73\u6311\uff0c\u5f53\u4e2d\u76f4\u523a\uff0c\u51b0\u5ddd\u5929\u5973\u624b\u8155\u4e00\u7ffb\uff0c\u5bd2\u5149\u5251\u8f6c\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u610f\u6b32\u628a\u90a3\u5c11\u5973 \u7684\u77ed\u5251\u5377\u8d70\uff0c\u4e0d\u6599\u90a3\u5c11\u5973\u7684\u5251\u6cd5\u7adf\u662f\u5b8c\u5168\u4e0d\u4fef\u5e38\u7406\uff0c\u770b\u5979\u8fd9\u4e00\u5251\u660e\u660e\u662f\u5f53\u4e2d\u6740\u5165\uff0c\u4e0d\u77e5\u600e \u7684\uff0c\u5251\u950b\u4e00\u504f\uff0c\u5374\u7a81\u7136\u523a\u5230\u4e86\u51b0\u5ddd\u5929\u5973\u53f3\u808b\u7684\u5927\u7a74\u3002\u51b0\u5ddd\u5929\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u811a\u6b65\u4e0d \u79fb\uff0c\u808c\u8089\u9661\u7684\u5185\u9677\u4e00\u5bf8\uff0c\u90a3\u5c11\u5973\u7684\u5251\u5c16\u5df2\u89e6\u53ca\u51b0\u5ddd\u5929\u5973\u7684\u8863\u88f3\uff0c\u5ffd\u89c9\u8f6f\u7ef5\u7ef5\u7684\u6beb\u65e0\u7740\u529b\u4e4b \u5904\uff0c\u5c31\u5dee\u90a3\u4e48\u4e00\u5bf8\uff0c\u6ca1\u80fd\u523a\u8fdb\uff0c\u8fd9\u4e00\u5f3a\u52b2\u4e4b\u52bf\u53cd\u800c\u7ed9\u5979\u5316\u89e3\u4e8e\u65e0\u5f62\uff0c\u66f4\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5251\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u53c8\u4f7f\u51fa\u4e86\u4e00\u62db\u201c\u79ef\u6c34\u51dd\u51b0\u201d\uff0c\u8fd9\u4e00 \u62db\u4e00\u53cd\u8f7b\u7075\u4e4b\u52bf\uff0c\u5374\u662f\u4ee5\u6c89\u96c4\u7684\u5185\u52b2\u76f4\u538b\u4e0b\u6765\uff0c\u6599\u90a3\u5c11\u5973\u4e0d\u80fd\u62b5\u6321\uff0c\u90a3\u5c11\u5973\u679c\u7136\u88ab\u8feb\u5f97\u8fde\u9000 \u4e24\u6b65\uff0c\u624d\u5587\u7684\u4e00\u5251\uff0c\u53cd\u624b\u5288\u524a\uff0c\u8fd9\u4e00\u62db\u5374\u662f\u6b66\u5f53\u6d3e\u4e2d\u7684\u4e00\u4e2a\u5bfb\u5e38\u62db\u6570\uff0c\u540d\u4e3a\u201c\u98de\u6e21\u9634\u5c71\u201d\uff0c \u51b0\u5ddd\u5929\u5973\u5bf9\u6b66\u5f53\u6d3e\u7684\u5251\u6cd5\u6700\u4e3a\u719f\u6089\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u7528\u5f97\u4e0d\u5bf9\uff0c\u8be5\u7528\u5b88\u4e2d\u5e26\u653b\u7684\u2018\u534e\u5bb9\u622a \u9053\u2019\uff0c\u8fd8\u53ef\u52c9\u5f3a\u62b5\u6321\u3002\u201d\u98de\u6e21\u9634\u5c71\u8fd9\u4e00\u62db\u4f9d\u7740\u5251\u52bf\uff0c\u5e94\u8be5\u5728\u5de6\u8fb9\u8fde\u523a\u4e24\u5251\uff0c\u518d\u4ece\u53f3\u8fb9\u76f4\u523a\u4e00 \u5251\uff0c\u524d\u4e24\u5251\u662f\u865a\u7740\u7834\u53bb\uff0c\u53eb\u5979\u6839\u672c\u4e0d\u80fd\u4ece\u53f3\u65b9\u6362\u62db\u518d\u523a\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5973\u7684\u5251\u6cd5\u5947\u8be1\u65e0\u6bd4\uff0c\u51fa\u5251\u7684\u59ff\u52bf\u660e\u660e\u662f\u201c\u98de\u6e21\u9634\u5c71\u201d\uff0c\u5251\u950b\u4e00\u5230\uff0c\u65b9\u4f4d\u5374\u5b8c\u5168 \u53d8\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u62a2\u5148\u4e00\u6b65\uff0c\u5c01\u7740\u5979\u7684\u5de6\u65b9\uff0c\u5979\u5374\u521a\u5587\u4e24\u5251\uff0c\u4ece\u53f3\u659c\u65b9\u75be\u523a\uff0c\u9ad8\u624b\u6bd4\u5251\uff0c\u6700\u5fcc \u662f\u6599\u654c\u6709\u8bef\uff0c\u51b0\u5ddd\u5929\u5973\u5168\u795e\u8d2f\u6ce8\u5de6\u65b9\uff0c\u53f3\u65b9\u9732\u51fa\u7a7a\u95e8\uff0c\u56de\u5251\u9632\u8eab\u5df2\u6765\u4e0d\u53ca\uff0c\u90a3\u5c11\u5973\u5a07\u58f0\u4e00 \u7b11\uff0c\u5251\u950b\u4e00\u5212\uff0c\u610f\u6b32\u5272\u65ad\u51b0\u5ddd\u5929\u5973\u675f\u8eab\u7684\u5f69\u8272\u8863\u5e26\uff0c\u5ffd\u542c\u5f97\u6bcd\u4eb2\u53eb\u9053\uff1a\u201c\u6885\u513f\uff0c\u5c0f\u5fc3\uff01\u201d\u5251 \u950b\u4e00\u89e6\uff0c\u90a3\u8170\u5e26\u7a81\u7136\u98d8\u4e86\u8d77\u6765\uff0c\u5012\u5377\u5979\u7684\u5251\u67c4\uff0c\u539f\u6765\u8fd9\u4e00\u5251\u82e5\u7136\u3002\u76f4\u523a\u8fc7\u53bb\uff0c\u51b0\u5ddd\u5929\u5973\u5fc5\u7136 \u53d7\u4f24\uff0c\u90a3\u5c11\u5973\u751f\u6027\u987d\u76ae\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u7684\u8863\u5e26\u5f69\u8272\u7eda\u70c2\uff0c\u5341\u5206\u7f8e\u4e3d\uff0c\u60f3\u548c\u5979\u5f00\u4e2a\u73a9\u7b11\u3002\u62a2\u5979\u7684 \u8863\u5e26\uff0c\u54ea\u77e5\u51b0\u5ddd\u5929\u5973\u7684\u529f\u529b\u6bd4\u5979\u9ad8\u5f97\u591a\uff0c\u8eab\u4f53\u5404\u90e8\u4efd\u90fd\u7ec3\u5f97\u67d4\u8f6f\u5982\u7ef5\uff0c\u968f\u5fc3\u6240\u6b32\uff0c\u90a3\u5c11\u5973\u7a0d \u5fae\u4e00\u7f13\uff1b\u5979\u5df2\u7528\u8170\u52b2\u5c06\u8863\u821e\u8d77\u6765\uff0c\u5f53\u6210\u8f6f\u7d22\u4f7f\u7528\uff0c\u90a3\u5c11\u5973\u5e78\u5f97\u6bcd\u4eb2\u63d0\u9192\uff0c\u6025\u5fd9\u79fb\u5f62\u6613\u4f4d\uff0c\u5251 \u62db\u7acb\u53d8\uff0c\u4f46\u9976\u662f\u5982\u6b64\uff0c\u4e5f\u88ab\u51b0\u5ddd\u5929\u5973\u5236\u4e86\u673a\u5148\uff0c\u4e00\u53e3\u6c14\u62a2\u653b\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u8feb\u5f97\u5979\u53ea\u80fd\u9000 \u5b88\uff0c\u6240\u6709\u5947\u8be1\u7684\u653b\u654c\u5251\u6cd5\uff0c\u5168\u90fd\u4f7f\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8fde\u8d70\u4e0b\u98ce\uff0c\u7a81\u7136\u53d1\u8d77\u5a07\u614e\uff0c\u9f13\u8d77\u5c0f\u5634\u5df4\u9a82\u9053\uff1a\u201c\u4f60\u4e0d\u51c6\u6211\u8fd8\u624b\uff0c\u8fd9\u6837\u7684\u6bd4\u5251\u6709\u4ec0 \u4e48\u610f\u601d\uff1f\uff01\u201d\u597d\u50cf\u5979\u548c\u51b0\u5ddd\u5929\u5973\u53ea\u662f\u62c6\u62db\uff0c\u8981\u51b0\u5ddd\u5929\u5973\u8ba9\u5979\u6709\u653b\u6709\u5b88\uff0c\u800c\u4e0d\u662f\u771f\u7684\u53ae\u6740\u4f3c \u7684\u3002\u51b0\u5ddd\u5929\u5973\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u8ba9\u4f60\u8fd8\u624b\u4fbf\u662f\u3002\u201d\u5c06\u51b0\u9b44\u5bd2\u5149\u5251\u7a0d\u7a0d\u4ece\u4e2d\u8def\u79fb \u5f00\uff0c\u6545\u610f\u9732\u51fa\u7834\u7efd\uff0c\u90a3\u5c11\u5973\u679c\u7136\u5f97\u673a\u75be\u8fdb\uff0c\u77ac\u606f\u4e4b\u95f4\u62a2\u653b\u4e09\u62db\uff0c\u62db\u62db\u4e0d\u540c\uff0c\u7b2c\u4e00\u62db\u662f\u5ce8\u5d4b\u6d3e \u5251\u6cd5\u4e2d\u7684\u201c\u4e07\u6c34\u671d\u5b97\u201d\uff0c\u7b2c\u4e8c\u62db\u662f\u5025\u4f97\u6d3e\u5251\u6cd5\u4e2d\u7684\u201c\u9a8f\u9a6c\u5954\u6cc9\u201d\uff0c\u7b2c\u4e09\u62db\u662f\u5d69\u9633\u6d3e\u5251\u6cd5\u4e2d\u7684 \u201c\u91d1\u9488\u6e21\u4e16\u201d\uff0c\u8fde\u53d1\u4e09\u62db\uff0c\u7adf\u7136\u662f\u4e09\u79cd\u5b8c\u5168\u4e0d\u540c\u7684\u5251\u6cd5\uff0c\u8fd9\u8fd8\u4e0d\u5947\uff0c\u6700\u5947\u7684\u662f\u5979\u6bcf\u4e00\u62db\u5251\u6cd5 \u90fd\u4f3c\u662f\u800c\u975e\uff0c\u65b9\u4f4d\u89d2\u5ea6\u90fd\u548c\u539f\u6765\u7684\u5251\u6cd5\u4e0d\u540c\uff0c\u51b0\u5ddd\u5929\u5973\u8fd9\u6b21\u662f\u65e9\u6709\u51c6\u5907\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u4ee5\u6700\u4e0a \u4e58\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u4e00\u4e00\u907f\u8fc7\u3002\u4f46\u9976\u662f\u5982\u6b64\uff0c\u4e00\u88ab\u90a3\u5c11\u5973\u62a2\u4e86\u5148\u624b\uff0c\u653b\u5b88\u4e4b\u52bf\u53c8\u662f\u4e00\u53d8\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u7236\u4eb2\u548c\u5979\u8c08\u8fc7\u7684\u4e2d\u56fd\u5404\u5927\u5251\u6d3e\uff0c\u5176\u4e2d\u6709\u4e00\u6d3e\u662f\u767d\u53d1\u9b54\u5973\u6240\u521b\u7684 \u5251\u6d3e\uff0c\u91c7\u96c6\u5404\u5bb6\u5404\u6d3e\u7684\u5251\u6cd5\u878d\u4e8e\u4e00\u7089\uff0c\u4f46\u5251\u5f0f\u867d\u540c\uff0c\u65b9\u4f4d\u5374\u5f02\uff0c\u521a\u521a\u548c\u539f\u6765\u7684\u5251\u6cd5\u76f8\u53cd\u3002\u5929 \u5c71\u5251\u6cd5\u4ee5\u5149\u660e\u6b63\u5927\u3001\u6df1\u539a\u6e0a\u535a\u88ab\u8a89\u4e3a\u5251\u5b66\u7684\u201c\u6b63\u5b97\u201d\uff0c\u800c\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u5251\u6cd5\uff0c\u5374\u4e13\u4e8b\u5947\u8be1 \u53d8\u5e7b\uff0c\u548c\u5929\u5c71\u5251\u6cd5\u521a\u597d\u662f\u4e00\u6b63\u4e00\u53cd\uff0c\u5404\u6709\u64c5\u573a\u3002\u51b0\u5ddd\u5929\u5973\u548c\u90a3\u5c11\u5973\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u83ab\u975e\u8fd9\u5c0f\u59d1\u5a18\u4f7f\u7684\u5c31\u662f\u767d\u53d1\u9b54\u5973\u7684\u5251\u6cd5\uff1f\u201d\u6697\u6697\u79f0\u5947\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6240\u6599\u4e0d\u5dee\uff0c\u8fd9\u5c11\u5973\u6240\u4f7f\u7684\u679c\u7136\u662f\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u7684\u5251\u6cd5\uff0c\u82e5\u9047\u7740\u5bfb\u5e38\u7684\u6b66\u5b66\u4e4b \u58eb\uff0c\u7eb5\u7136\u8bc6\u7834\uff0c\u4e5f\u96be\u62b5\u6321\uff0c\u4f46\u51b0\u5ddd\u5929\u5973\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u5979\u7684\u5251\u6cd5\u4ee5\u8fbe\u6469\u5251\u6cd5\u4e3a\u57fa\uff0c\u53c8\u6742\u4ee5\u6b27\u6d32 \u548c\u963f\u62c9\u4f2f\u7684\u5251\u6cd5\uff0c\u602a\u5f02\u7cbe\u5999\u4e4b\u5904\uff0c\u5b9e\u4e0d\u5728\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u7684\u5251\u6cd5\u4e4b\u4e0b\uff0c\u521d\u65f6\u56e0\u4e3a\u5bf9\u90a3\u5c11\u5973\u5fc3 \u5b58\u601c\u60dc\uff0c\u800c\u53c8\u51fa\u4e8e\u4e0d\u610f\uff0c\u6240\u4ee5\u624d\u51e0\u4e4e\u5403\u4e8f\uff0c\u800c\u4eca\u8bc6\u7834\u4e86\u5979\u7684\u5251\u6cd5\uff0c\u5fc3\u795e\u4e00\u5b9a\uff0c\u90a3\u5c11\u5973\u7684\u5947\u62db \u602a\u7740\uff0c\u5168\u90fd\u5948\u5979\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4f7f\u51fa\u6d51\u8eab\u89e3\u6570\uff0c\u90fd\u88ab\u51b0\u5ddd\u5929\u5973\u8f7b\u63cf\u6de1\u5199\u5730\u5316\u5f00\uff0c\u6c89\u4e0d\u4f4f\u6c14\uff0c\u795e\u60c5\u7126\u8e81\uff0c\u5251\u6cd5\u6e10 \u4e71\uff0c\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u8981\u6bd4\u4e48\uff1f\u201d\u90a3\u5c11\u5973\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u77ed\u5251\u51cc\u7a7a\u4e0b\u51fb\uff0c\u75be\u5982\u9e70 \u96bc\uff0c\u5979\u7adf\u7136\u4ee5\u51cc\u7a7a\u6251\u51fb\u7684\u5947\u6280\u914d\u5408\u4e86\u767d\u53d1\u9b54\u5973\u6240\u521b\u7684\u5251\u6cd5\u6765\u4f7f\u7528\uff0c\u51b0\u5ddd\u5929\u5973\u5927\u5403\u4e00\u60ca\uff0c\u65e0\u6687 \u601d\u7d22\uff0c\u8eab\u4e8e\u51ed\u7a7a\u62d4\u8d77\u6570\u5c3a\uff0c\u4e5f\u5c55\u51fa\u8fbe\u6469\u5251\u6cd5\u7684\u7edd\u62db\u201c\u4e00\u82c7\u6e21\u6c5f\u201d\uff0c\u5251\u5203\u5e73\u524a\uff0c\u5c31\u5728\u534a\u7a7a\u4e2d\u6a2a \u622a\u8fc7\u53bb\u3002\u90a3\u5c11\u5973\u9664\u4e86\u80fd\u5728\u8dc3\u8d77\u4e4b\u65f6\uff0c\u50cf\u98de\u9e1f\u822c\u7684\u56de\u7fd4\u6251\u51fb\u4e4b\u5916\uff0c\u5176\u4ed6\u771f\u5b9e\u7684\u672c\u9886\u4e0e\u8f7b\u8eab\u7684\u529f \u592b\uff0c\u90fd\u8fd8\u4e0d\u53ca\u51b0\u5ddd\u5929\u5973\uff0c\u5979\u8fd9\u4e00\u5251\u672c\u6765\u6781\u4e3a\u5192\u9669\uff0c\u4e0d\u6599\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53cd\u88ab\u51b0\u5ddd\u5929\u5973\u5236\u4f4f\uff0c\u4e24\u4eba \u90fd\u662f\u811a\u4e0d\u6cbe\u5730\uff0c\u51cc\u7a7a\u4ea4\u624b\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u5251\u524a\u51fa\uff0c\u5fc3\u5934\u66ae\u7136\u4e00\u8f6c\uff1a\u8fd9\u4e00\u5251\u82e5\u7136\u523a \u5b9e\uff0c\u5fc5\u5b9a\u7a7f\u5589\u800c\u8fc7\uff0c\u81ea\u5df1\u4e0e\u5979\u65e0\u51a4\u65e0\u4ec7\uff0c\u5c82\u53ef\u5982\u6b64\uff1f\u4f46\u51cc\u7a7a\u4ea4\u624b\uff0c\u6536\u52bf\u5df2\u6765\u4e0d\u53ca\uff01 \u3000\u3000\u90a3\u5c11\u5973\u9a87\u53eb\u4e00\u58f0\uff0c\u5ffd\u542c\u5f97\u8033\u8fb9\u6bcd\u4eb2\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6885\u513f\uff0c\u4f60\u8fd8\u4e0d\u4fe1\u6211\u7684\u8bdd\u4e48\uff1f\u201d\u9661\u89c9\u8eab \u5b50\u4e00\u8f7b\uff0c\u88ab\u4eba\u51ed\u7a7a\u63d0\u8d77\uff0c\u8f7b\u8f7b\u629b\u51fa\uff0c\u843d\u4e8e\u5730\u4e0a\uff0c\u4e3e\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u6bcd\u4eb2\u548c\u51b0\u5ddd\u5929\u5973\u90fd\u5df2\u9762\u5bf9\u9762 \u7684\u7ad9\u5728\u5730\u4e0a\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u5251\u524a\u51fa\uff0c\u540e\u6094\u65e0\u53ca\uff0c\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u773c\u524d\u9ed1\u5f71\u4e00\u95ea\uff0c\u5c31\u5728\u4e24 \u53e3\u5b9d\u5251\u76f8\u63a5\u672a\u63a5\u7684\u4ea4\u53c9\u7f1d\u4e2d\u63a0\u8fc7\uff0c\u628a\u90a3\u5c11\u5973\u63d0\u8d70\uff0c\u51b0\u5ddd\u5929\u5973\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u4e5f\u88ab\u8fd9\u7a81 \u5982\u5176\u6765\u7684\u9ed1\u5f71\u60ca\u5f97\u5446\u4e86\u3002\u5979\u672c\u80fd\u7684\u8eab\u5b50\u5411\u540e\u4e00\u7ffb\uff0c\u53ea\u542c\u5f97\u8033\u8fb9\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u7ad9\u7a33 \u4e86\uff01\u201d\u4f46\u89c9\u6b64\u4eba\u4f3c\u4e4e\u662f\u8f7b\u8f7b\u7684\u6276\u4e86\u81ea\u5df1\u4e00\u4e0b\uff0c\u51b0\u5ddd\u5929\u5973\u7acb\u523b\u4e00\u4e2a\u7b4b\u6597\uff0c\u5934\u4e0b\u811a\u4e0a\u7684\u4e00\u4e2a\u8f6c \u8eab\uff0c\u843d\u5230\u5730\u4e0a\u3002\u51b0\u5ddd\u5929\u5973\u60ca\u7591\u4e0d\u5b9a\uff0c\u8fd9\u4e2a\u50cf\u5c11\u5973\u6253\u626e\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u7b80\u76f4\u4e0d\u53ef\u601d \u8bae\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u5979\u7b11\u76c8\u76c8\u5730\u671b\u7740\u81ea\u5df1\uff0c\u5567\u5567\u8d5e\u9053\uff1a\u201c\u597d\u6f02\u4eae\u7684\u5c0f\u59d1\u5a18\uff0c\u6709\u5a46\u5bb6\u6ca1\u6709\uff1f\u201d \u51b0\u5ddd\u5929\u5973\u81ca\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u5979\u4ee5\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u751f\u957f\u5728\u51b0\u5bab\u4e4b\u4e2d\uff0c\u9694\u79bb\u5c18\u4e16\uff0c\u81ea\u5e7c\u53d7\u4f17\u4f8d\u5973\u7684\u4f3a \u5019\uff0c\u51e0\u66fe\u6709\u4eba\u548c\u5979\u5f00\u8fc7\u73a9\u7b11\uff0c\u4f55\u51b5\u662f\u521d\u521d\u89c1\u9762\u7684\u4eba\uff1f\u4f55\u51b5\u8fd9\u4eba\u770b\u6765\u53c8\u4f3c\u4e4e\u662f\u4e00\u4f4d\u524d\u8f88\u9ad8\u624b\uff0c \u51b0\u5ddd\u5929\u5973\u8981\u9a82\u4e5f\u9a82\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u7b11\u5f97\u5934\u4e0a\u6240\u7ed3\u7684\u4e24\u4e2a\u767d\u8774\u8776\u8f7b\u8f7b\u98a4\u52a8\uff0c\u90a3\u795e\u6001\u4e0e\u5979\u7684\u5e74\u7eaa\u5927\u4e0d\u76f8\u79f0\uff0c\u7adf\u7136\u50cf \u4e00\u4e2a\u6dd8\u6c14\u7684\u59d1\u5a18\uff0c\u4e13\u5411\u5979\u7684\u5973\u4f34\u5bfb\u5f00\u5fc3\u4f3c\u7684\uff0c\u53ea\u542c\u5f97\u5979\u53c8\u5411\u7740\u81ea\u5df1\u7b11\u9053\uff1a\u201c\u5251\u6cd5\u4e5f\u4fca\u6781\u4e86\uff0c \u771f\u662f\u624d\u8c8c\u53cc\u5168\u3002\u6211\u7ed9\u4f60\u627e\u4e2a\u5a46\u5bb6\u597d\u4e0d\u597d\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u55d4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u8001\u4e0d\u6b63\u7ecf\uff0c\u518d\u5f00\u73a9 \u7b11\uff0c\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u90a3\u5987\u4eba\u8d8a\u53d1\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u4e48\u88c5\u6a21\u4f5c\u6837\uff0c\u5c31\u597d\u50cf\u6211\u7684\u59d0\u59d0\u4e00\u822c\uff0c\u54c8\uff0c \u6211\u7684\u4f84\u5b59\u4eec\u53eb\u4f60\u505a\u5996\u5973\uff0c\u6211\u770b\u4f60\u5012\u50cf\u4e2a\u5c0f\u8001\u592a\u5a46\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5927\u6012\uff0c\u5587\u7684\u4e00\u5251\u523a\u51fa\uff0c\u660e\u77e5\u523a \u5979\u4e0d\u7740\uff0c\u4e5f\u8981\u51fa\u4e00\u51fa\u6c14\uff0c\u53ea\u542c\u5f97\u90a3\u5987\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u7684\u5973\u513f\u5012\u662f\u6709\u70b9\u624b\u4e0b\u7559\u60c5\uff0c\u4f46\u5bf9\u6211 \u7684\u4f84\u5b59\u5374\u662f\u592a\u4e0d\u5ba2\u6c14\u4e86\uff0c\u4f60\u7684\u5251\u6cd5\u662f\u8ddf\u8c01\u5b66\u7684\uff0c\u4e3a\u4f55\u5982\u6b64\u901e\u5f3a\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6120\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u6b3a\u8d1f\u4e86\u4f60\u7684\u4f84\u5b59\uff0c\u4f60\u5c31\u6765\u60e9\u6212\u6211\u5427\u3002\u201d\u5979\u5fc3\u9ad8\u6c14\u505a\uff0c\u660e\u77e5\u96be \u654c\uff0c\u5374\u505a\u7136\u8fdb\u62db\uff0c\u90a3\u4e2d\u5e74\u5987\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u6211\u7231\u60dc\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c\u600e\u820d\u5f97\u60e9\u6212 \u4f60\uff1f\u201d\u5ffd\u7136\u4f38\u624b\u5728\u51b0\u5ddd\u5929\u5973\u7684\u9762\u4e0a\u6478\u4e86\u4e00\u628a\uff0c\u51b0\u5ddd\u5929\u5973\u660e\u660e\u89c1\u5979\u4f38\u624b\uff0c\u5374\u662f\u8eb2\u95ea\u4e0d\u5f00\uff0c\u51b0\u5ddd \u5929\u5973\u600e\u8010\u5f97\u5979\u5982\u6b64\u620f\u5f04\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5251\u6cd5\u4e00\u5c55\uff0c\u75be\u4f3c\u98d8\u98ce\uff0c\u8fde\u8fde\u65bd\u5c55\u6740\u624b\uff01 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u7b11\u9053\uff1a\u201c\u771f\u662f\u607c\u4e86\u6211\u4e48\uff1f\u201d\u53c8\u5728\u5979\u7684\u5934\u4e0a\u6478\u4e86\u4e00\u4e0b\u3001\u51b0\u5ddd\u5929\u5973\u8feb\u7740\u5979\u7684\u8eab \u5f62\uff0c\u54a7\u6dae\u6dae\u8fde\u523a\u6570\u5251\uff0c\u90a3\u4e2d\u5e74\u5987\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5251\u5012\u771f\u662f\u4ef6\u5b9d\u8d1d\uff0c\u53ef\u60dc\u73b0\u5728\u662f\u5bd2\u5929\uff0c\u8981 \u662f\u590f\u65e5\uff0c\u5e26\u7740\u8fd9\u628a\u5b9d\u5251\uff0c\u8fde\u6247\u5b50\u4e5f\u7528\u4e0d\u7740\uff0c\u602a\u4e0d\u5f97\u6211\u7684\u5973\u513f\u60f3\u501f\u6765\u73a9\u3002\u7ed9\u6211\u77a7\u77a7\uff0c\u770b\u662f\u4ec0\u4e48 \u505a\u7684\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00\u51db\uff0c\u6025\u5fd9\u628a\u51b0\u9b44\u5bd2\u5149\u5251\u821e\u5f97\u6cfc\u6c34\u4e0d\u8fdb\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u770b\u4f60\u5982\u4f55\u62a2\u6211 \u7684\u5b9d\u5251\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u53ef\u60dc\u817e\u4e0d\u51fa\u624b\u6765\uff0c\u8981\u4e0d\u7136\u4e00\u8fde\u5949\u9001\u5979\u5341\u7c92\u51b0\u9b44\u795e\u5f39\uff0c\u770b\u5979\u5403\u4e0d\u5403\u5f97 \u6d88\uff1f\u201d\u9661\u7136\u95f4\u5ffd\u89c9\u4e00\u80a1\u9999\u98ce\u6c81\u5165\u9f3b\u89c2\uff0c\u53ea\u542c\u5f97\u201c\u6323\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5987\u4eba\u53cc\u6307\u4e00\u5f39\uff0c\u51b0\u9b44\u5bd2\u5149\u5251 \u7adf\u7136\u8131\u624b\u98de\u51fa\u3002\u90a3\u5987\u4eba\u4e00\u628a\u6284\u7740\uff0c\u63a5\u5728\u624b\u4e2d\uff0c\u7ffb\u6765\u590d\u53bb\u5730\u77a7\u4e86\u53c8\u77a7\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u56de\u771f\u662f\u96be\u5012 \u6211\u4e86\uff0c\u662f\u4ec0\u4e48\u505a\u7684\u6211\u4e5f\u4e0d\u77e5\u9053\uff01\u201d\u51b0\u5ddd\u5929\u5973\u53c8\u60ca\u53c8\u6012\uff0c\u6251\u4e0a\u524d\u53bb\u62a2\u593a\uff0c\u90a3\u5987\u4eba\u7b11\u9053\uff1a\u201c\u7528\u4e0d \u7740\u8fd9\u6837\u7740\u6025\uff0c\u6211\u4e0d\u8981\u4f60\u7684\uff01\u201d\u9aa4\u7136\u5c06\u5251\u67c4\u4e00\u4f38\u3002\u5ffd\u7136\u5c06\u5979\u7684\u624b\u8155\u6258\u7740\uff0c\u9053\uff1a\u201c\u8ba9\u6211\u518d\u77a7\u4e00 \u77a7\uff0c\u5440\uff0c\u771f\u662f\u5982\u82b1\u4f3c\u7389\uff0c\u6211\u89c1\u72b9\u601c\u3002\u8fd9\u4e2a\u5a92\u4eba\u6211\u505a\u5b9a\u4e86\uff01\u201d\u5728\u5979\u5728\u9762\u4e0a\u53c8\u6478\u4e86\u4e00\u628a\uff0c\u9aa4\u7684\u53cc \u624b\u4e00\u677e\uff0c\u7b11\u58f0\u72b9\u81ea\u5728\u8349\u539f\u4e4b\u4e0a\u56de\u65cb\uff0c\u4eba\u5f71\u5374\u5df2\u5954\u51fa\u6570\u91cc\u4e4b\u5916\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u62ac\u5934\u770b\u65f6\uff0c\u6b66\u6c0f\u5144\u5f1f\u548c\u90a3\u5c11\u5973\u4e5f\u4e0d\u89c1\u4e86\uff0c\u539f\u6765\u4ed6\u4eec\u5f53\u90a3\u4e2d\u5e74\u5987\u4eba\u548c\u51b0\u5ddd\u5929\u5973\u620f \u800d\u4e4b\u65f6\uff0c\u5148\u81ea\u8d70\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u6ca1\u7559\u795e\u3002\u8fd9\u65f6\u9065\u671b\u90a3\u4e2d\u5e74\u5987\u4eba\u7684\u80cc\u5f71\u5728\u8349\u539f\u4e0a\u5189\u5189\u6d88\u5931\uff0c\u51b0 \u5ddd\u5929\u5973\u4e0d\u7531\u5f97\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u7236\u6bcd\u8d39\u5c3d\u5fc3\u8840\uff0c\u521b\u4e86\u8fd9\u5957\u4e2d\u897f\u5408\u58c1\u7684\u5251\u6cd5\uff0c\u4ee5\u4e3a\u53ef\u4ee5\u5929 \u4e0b\u65e0\u654c\uff0c\u54ea\u77e5\u8fde\u8fd9\u4e2a\u5987\u4eba\u4e5f\u6597\u4e0d\u8fc7\u3002\u5440\u5440\uff0c\u6211\u7236\u4eb2\u7684\u5fc3\u613f\u53ea\u6015\u96be\u4ee5\u8fbe\u5230\u4e86\u3002\u201d\u5979\u54ea\u77e5\u9053\u8fd9\u4e2a \u5987\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u8f88\u4efd\u4e4b\u5c0a\uff0c\u5728\u6b66\u6797\u4e2d\u4ec5\u4ec5\u662f\u6709\u9650\u7684\u4e09\u4e24\u4e2a\u4eba\u53ef\u4ee5\u4e0e\u4e4b\u76f8\u6bd4\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u90c1\u7ed3\uff0c\u5979\u8fd8\u662f\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u88ab\u4eba\u620f\u5f04\uff0c\u600e\u6837\u4e5f\u54bd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u4f46\u5374\u53c8\u65e0 \u53ef\u5948\u4f55\uff0c\u53ea\u597d\u6ca1\u7cbe\u6253\u5f69\u5730\u56de\u53bb\u3002\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u51b0\u8f6e\u3002\u9ad8\u60ac\u5929\u9645\uff0c\u6b63\u662f \u5348\u591c\u65f6\u5206\uff0c\u6708\u5149\u5206\u5916\u6e05\u660e\uff0c\u5728\u5927\u6f20\u4e4b\u4e2d\uff0c\u5468\u56f4\u6570\u91cc\u4e4b\u5185\u7684\u666f\u7269\u90fd\u9690\u7ea6\u53ef\u89c1\u3002\u90a3\u4e24\u5ea7\u5e10\u5e55\uff0c\u642d \u5728\u5c71\u8fb9\uff0c\u76ee\u6807\u66f4\u663e\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u90a3\u5ea7\u5e10\u5e55\u7684\u5916\u9762\uff0c\u6709\u7740\u4e24\u6761\u9ed1\u5f71\uff0c\u4f3c\u662f \u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u81ea\u662f\u5510\u7ecf\u5927\u65e0\u7591\uff0c\u90a3\u5973\u7684\u8eab\u6750\u5374\u4e0d\u4f3c\u5979\u7684\u4f8d\u5973\uff0c\u51b0\u5ddd\u5929\u5973\u597d\u4e0d\u60ca\u5947\uff0c\u518d\u8dd1\u91cc \u8bb8\uff0c\u5b9a\u775b\u4e00\u77a7\uff0c\u770b\u6e05\u695a\u4e86\uff0c\u539f\u6765\u5374\u662f\u9002\u624d\u548c\u5979\u4ea4\u624b\u7684\u90a3\u4e2a\u5c11\u5973\uff01 \u3000\u3000\u5510\u7ecf\u5929\u8fd9\u665a\u5728\u5e10\u5e55\u4e4b\u4e2d\uff0c\u7ffb\u6765\u590d\u53bb\uff0c\u7761\u4e0d\u7740\u89c9\uff0c\u8111\u6d77\u4e2d\u4e0d\u4f4f\u7684\u6cdb\u8d77\u51b0\u5ddd\u5929\u5973\u7684\u5f71\u5b50\uff0c\u90a3 \u4f3c\u559c\u8fd8\u7791\u7684\u795e\u60c5\uff0c\u90a3\u95ea\u70c1\u4e0d\u5b9a\u3001\u6709\u5982\u8349\u539f\u591c\u661f\u7684\u773c\u775b\uff0c\u4ee4\u4eba\u7729\u60d1\u7684\u8bf4\u8bdd\u3002\u51b0\u5ddd\u5929\u5973\u7684\u8eab\u4e16\u4e4b \u8ff7\u662f\u63ed\u5f00\u4e86\uff0c\u53ef\u662f\u5979\u4e3a\u4ec0\u4e48\u4e00\u542c\u4eba\u63d0\u8d77\u5929\u5c71\uff0c\u5c31\u6709\u4e00\u79cd\u8ba8\u538c\u7684\u795e\u8272\u5462\uff1f\u5979\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u5979\u672c \u6765\u4e5f\u5c5e\u4e8e\u5929\u5c71\u4e00\u6d3e\u2014\u2014\u5979\u662f\u6842\u4ef2\u660e\u7684\u5b59\u5973\u513f\u5475\uff0c\u53ef\u662f\u5979\u4e3a\u4ec0\u4e48\u5bf9\u4e8e\u5929\u5c71\u4e00\u6d3e\uff0c\u603b\u6709\u4e00\u79cd\u201c\u89c1 \u5916\u201d\u7684\u5fc3\u60c5\uff1f\u8fd9\u4e2a\u8ff7\u5510\u7ecf\u5929\u600e\u4e48\u4e5f\u731c\u4e0d\u900f\u3002\u5927\u6f20\u4e0a\u591c\u98ce\u547c\u5578\uff0c\u5510\u7ecf\u5929\u60f3\u8d77\u4e0b\u5c71\u4e4b\u65f6\u7236\u6bcd\u7684\u5631 \u5490\uff0c\u53eb\u4ed6\u53bb\u627e\u5bfb\u6842\u534e\u751f\u4f2f\u4f2f\u7684\u4e0b\u843d\uff0c\u800c\u4eca\u4ed6\u5df2\u627e\u5230\u4e86\u6842\u534e\u751f\u7684\u5973\u513f\uff0c\u53ef\u662f\u5979\u5374\u4e0d\u613f\u8ddf\u81ea\u5df1\u5230 \u5929\u5c71\u53bb\u89c1\u5979\u7236\u4eb2\u4ee5\u524d\u7684\u670b\u53cb\uff0c\u8fd9\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u5510\u7ecf\u5929\u60f3\u6765\u60f3\u53bb\uff0c\u751a\u4e3a\u82e6\u607c\u3002\u5982\u679c\u6362\u662f\u522b \u4eba\uff0c\u5510\u7ecf\u5929\u4e00\u5b9a\u8981\u95ee\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u504f\u504f\u51b0\u5ddd\u5929\u5973\u53c8\u662f\u90a3\u4e48\u9ad8\u50b2\uff0c\u4e00\u526f\u597d\u50cf\u662f\u4e0e\u751f\u4ff1\u6765\u7684\u9ad8 \u50b2\uff01\u90a3\u4e00\u80a1\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u5c0a\u8d35\u7684\u795e\u60c5\uff0c\u4f7f\u5f97\u522b\u4eba\u4e0d\u6562\u5411\u5979\u591a\u95ee\u534a\u53e5\uff01 \u3000\u3000\u5510\u7ecf\u5929\u65e2\u662f\u7591\u60d1\uff0c\u53c8\u6709\u70b9\u4e0d\u5b89\uff0c\u6709\u70b9\u53cd\u611f\uff0c\u8fd9\u590d\u6742\u7684\u60c5\u7eea\uff0c\u5728\u4ed6\u7684\u5fc3\u5934\u6253\u7ed3\u3002\u6479\u7136\u95f4\u4ed6 \u5fc3\u5934\u4e00\u8361\uff1a\u4e3a\u4ec0\u4e48\u81ea\u4ece\u8ba4\u8bc6\u4e86\u51b0\u5ddd\u5929\u5973\u4e4b\u540e\uff0c\u5c31\u8001\u662f\u8fd9\u6837\u7684\u60c5\u7eea\u4e0d\u5b81\uff1f\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u8111\u6d77\u4e2d \u53c8\u6cdb\u8d77\u53e6\u4e00\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\uff0c\u8fd9\u5c11\u5973\u6bd4\u51b0\u5ddd\u5929\u5973\u8fd8\u5c0f\u4e00\u5c81\uff0c\u662f\u4ed6\u7684\u8868\u59b9\u674e\u6c81\u6885\uff0c\u662f\u548c\u4ed6\u4ece\u5c0f\u73a9 \u5230\u5927\u7684\u3002\u53ef\u662f\u5bf9\u4e8e\u6c81\u6885\uff0c\u4ed6\u5374\u53ea\u662f\u89c9\u5f97\u5979\u6dd8\u6c14\u597d\u73a9\u800c\u5df2\u3002\u4e3a\u4ec0\u4e48\u5bf9\u6c81\u6885\u53c8\u6ca1\u6709\u90a3\u6837\u7684\u5fc3\u60c5\uff1f \u5510\u7ecf\u5929\u60f3\u5230\u8fd9\u513f\uff0c\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\uff01\u6216\u8005\u66f4\u6bcb\u5b81\u8bf4\u662f\uff1a\u4ed6\u5df2\u7ecf\u7aa5\u5bdf\u5230\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\u4e86\uff0c\u53ef \u662f\u4e0b\u610f\u8bc6\u5374\u4e0d\u613f\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5916\u9762\u98ce\u522e\u5f97\u66f4\u5927\u4e86\uff0c\u98ce\u58f0\u4e2d\u9690\u9690\u4f20\u6765\u4e86\u4e00\u9635\u201c\u545c\u545c\u201d\u7684\u58f0\u97f3\uff0c\u65f6\u65ad\u65f6\u7eed\uff0c\u5ffd\u9ad8\u5ffd\u4f4e\uff0c\u5510 \u7ecf\u5929\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u7684\u5175\u5203\u6240\u53d1\u51fa\u7684\u58f0\u97f3\u5417\uff1f\u201d\u5510\u7ecf\u5929\u4e0d\u6bd4\u51b0 \u5ddd\u5929\u5973\uff0c\u4ed6\u6709\u7236\u6bcd\uff0c\u6709\u53d4\u4f2f\u8f88\u7684\u6b66\u6797\u540d\u5bbf\uff0c\u6240\u4ee5\u867d\u7136\u548c\u51b0\u5ddd\u5929\u5973\u5dee\u4e0d\u591a\u5e74\u7eaa\uff0c\u89c1\u95fb\u4e4b\u5e7f\uff0c\u5374 \u8fdc\u975e\u51b0\u5ddd\u5929\u5973\u53ef\u6bd4\uff0c\u4ed6\u77e5\u9053\u5c3c\u6cca\u5c14\u6709\u4e00\u79cd\u6708\u7259\u5f2f\u5200\uff0c\u4e0a\u534a\u622a\u5200\u67c4\u9542\u7a7a\uff0c\u8fce\u98ce\u6709\u58f0\uff0c\u4ed6\u5728\u65e5\u5580 \u5219\u7684\u5ba2\u5e97\u66fe\u89c1\u8fc7\u90a3\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4f7f\u8fd9\u79cd\u5200\uff0c\u540e\u6765\u5728\u62a2\u593a\u91d1\u74f6\u4e4b\u65f6\u53c8\u66fe\u89c1\u8fc7\u3002\u5728\u65e5\u5580\u5219\u65f6\uff0c\u5929 \u4e0a\u6ca1\u6709\u522e\u98ce\uff0c\u7eb5\u6709\u5fae\u98ce\uff0c\u4e5f\u88ab\u5899\u58c1\u6321\u4f4f\uff0c\u6240\u4ee5\u867d\u7136\u6325\u52a8\u4e4b\u65f6\uff0c\u4e5f\u53d1\u51fa\u58f0\u97f3\uff0c\u5374\u5e76\u4e0d\u523a\u8033\uff1b\u5728 \u62a2\u593a\u91d1\u74f6\u4e4b\u65f6\uff0c\u90a3\u662f\u5728\u4e8e\u519b\u9a6c\u4e07\u4e4b\u4e2d\uff0c\u8fd9\u201c\u545c\u545c\u201d\u4e4b\u58f0\u5728\u58f0\u97f3\u7684\u6d77\u6d0b\u4e2d\u66f4\u5206\u8fa8\u4e0d\u51fa\u3002\u5982\u4eca\u5728 \u5927\u6f20\u8349\u539f\u4e4b\u4e0a\uff0c\u591c\u98ce\u63a0\u8fc7\uff0c\u58f0\u4f20\u751a\u8fdc\uff0c\u5510\u7ecf\u5929\u4e00\u542c\u5c31\u542c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5510\u7ecf\u5929\u597d\u751f\u5947\u602a\uff0c\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e3a\u4f55\u8fd8\u7559\u5728\u4e2d\u56fd\uff1f\u4ed6\u8d70\u51fa\u5e10\u5e55\u3001\u8df3\u4e0a\u7bf7\u9876\uff0c\u5f20\u76ee\u4e00 \u671b\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u7684\u80cc\u5f71\uff0c\u6b63\u5728\u5411\u897f\u5317\u65b9\u5954\u53bb\uff0c\u51b3\u6377\u5982\u7535\uff0c\u7728\u773c\u4e0d\u89c1\u3002\u5510\u7ecf\u5929\u672c\u60f3\u8ddf\u7740\u8ffd \u8e2a\uff0c\u4f46\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5374\u53c8\u505c\u4f4f\u3002 \u3000\u3000\u5510\u7ecf\u5929\u60f3\u7684\u662f\uff1a\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u662f\u51b0\u5ddd\u5929\u5973\u7684\u56fd\u4eba\uff0c\u4ed6\u4eec\u5bf9\u51b0\u5ddd\u5929\u5973\u656c\u82e5\u795e\u660e\uff0c\u51b0\u5ddd \u5929\u5973\u4e00\u53bb\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u5979\u81ea\u80fd\u89e3\u51b3\u3002\u800c\u4e14\u4e0d\u77e5\u4ed6\u4eec\u4e4b\u95f4\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u82e5\u7136\u81ea\u5df1\u4e5f\u8ffd\u8e2a\u8ddf\u53bb\uff0c \u53ea\u6050\u51b0\u5ddd\u5929\u5973\u4ee5\u4e3a\u81ea\u5df1\u597d\u7ba1\u95f2\u4e8b\uff0c\u751a\u6216\u4f1a\u602a\u81ea\u5df1\u8d8a\u5200\u4fce\u4ee3\u75b1\u3002\u8fd9\u6837\u4e00\u60f3\uff0c\u5c31\u505c\u6b62\u8ffd\u8e2a\uff0c\u6539\u5411 \u51b0\u5ddd\u5929\u5973\u7684\u5e10\u5e55\u8d70\u53bb\u3002 \u3000\u3000\u5e10\u5e55\u5916\u95ea\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u5374\u662f\u51b0\u5bab\u7684\u4f8d\u5973\u5e7d\u840d\u3002\u6708\u5149\u4e0b\u53ea\u89c1\u5e7d\u840d\u9762\u4e0a\u7565\u663e\u5f20\u7687\u7684\u795e\u8272\uff0c\u62a2 \u5148\u95ee\u9053\uff1a\u201c\u54a6\uff0c\u662f\u5510\u76f8\u516c\u5417\uff1f\u8fd9\u4e48\u665a\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u51fa\u6765\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u542c\u5230\u90a3\u545c\u545c\u7684\u58f0 \u97f3\u5417\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u542c\u5230\u7684\uff0c\u6211\u731c\u8fd9\u4e0d\u8fc7\u662f\u6c99\u6f20\u4e2d\u7684\u602a\u9e1f\u557c\u58f0\u7f62\u4e86\u3002\u201d\u5510\u7ecf\u5929\u7b11\u4e86\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u4f60\u7684\u516c\u4e3b\u5462\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5979\u8fde\u65e5\u5954\u6ce2\uff0c\u65e9\u5df2\u719f\u7761\u4e86\u3002\u6211\u542c\u5230\u4f60\u7684\u811a\u6b65\u58f0\uff0c\u4e0d\u77e5\u662f\u4ec0 \u4e48\u4eba\uff0c\u6240\u4ee5\u51fa\u6765\u67e5\u770b\u3002\u4f60\u5feb\u56de\u53bb\u3002\u5435\u9192\u4e86\u5979\uff0c\u5979\u53c8\u8981\u4e0d\u9ad8\u5174\u4e86\u3002\u201d\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\u58f0 \u201c\u6253\u6270\u201d\uff0c\u56de\u5230\u81ea\u5df1\u7684\u5e10\u5e55\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u679c\u7136\u4e0d\u613f\u81ea\u5df1\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u660e\u77e5\u51b0\u5ddd\u5929\u5973\u4e0d\u4f1a\u6709\u751a\u5371\u9669\uff0c\u53ef\u662f\u51b0\u5ddd\u5929\u5973\u79bb\u5f00\u4e86\u5979\u7684\u5e10\u5e55\uff0c\u603b\u53eb\u4ed6\u653e\u5fc3\u4e0d\u4e0b\uff0c \u66f4\u65e0\u6cd5\u5b89\u7761\u4e86\u3002\u5510\u7ecf\u5929\u7d22\u6027\u70b9\u71c3\u4e86\u897f\u85cf\u65cf\u4eba\u5e38\u5907\u7684\u5927\u725b\u6cb9\u70db\uff0c\u5750\u5728\u5e10\u5e55\u4e4b\u4e2d\u5446\u5b88\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u5e10\u5e55\u5916\u8f7b\u5fae\u7684\u58f0\u606f\uff0c\u6709\u4eba\u5728\u5916\u9762\u5f39\u4e86\u51e0\u4e0b\uff0c\u5510\u7ecf\u5929\u8df3\u8d77\u6765 \u9053\uff1a\u201c\u4f60\u56de\u6765\u4e86\u5417\uff1f\u201d\u5fc3\u4e2d\u6b63\u662f\u5947\u602a\uff0c\u51b0\u5ddd\u5929\u5973\u65e2\u4e0d\u613f\u8ba9\u4ed6\u77e5\u9053\uff0c\u5982\u4f55\u53c8\u627e\u81ea\u5df1\uff1f\u5e10\u5e55\u4e00 \u63ed\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u637b\u719f\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u5510\u54e5\u54e5\uff0c\u4f60\u60f3\u5ff5\u7740\u8c01\u5475\uff1f\u201d\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u7b11 \u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u662f\u4f60\u8fd9\u5c0f\u9b3c\u5934\uff01\u201d\u8fd9\u5c11\u5973\u772f\u7740\u773c\u775b\uff0c\u5728\u70db\u5149\u6620\u7167\u4e4b\u4e0b\uff0c\u4e00\u8138\u6dd8\u6c14\u7684\u6837\u5b50\u3002\u53ef \u4e0d\u6b63\u662f\u81ea\u5df1\u7684\u8868\u59b9\uff0c\u674e\u6cbb\u548c\u51af\u7433\u7684\u5973\u513f\u674e\u6c81\u6885\u3002 \u3000\u3000\u674e\u6c81\u6885\u9053\uff1a\u201c\u5927\u6b66\u5c0f\u6b66\u8bf4\u5f97\u4e0d\u9519\uff0c\u6709\u4e86\u5979\u5c31\u4e00\u5b9a\u6709\u4f60\uff0c\u4ed6\u4eec\u731c\u4f60\u7684\u5e10\u7bf7\u5c31\u5728\u9644\u8fd1\uff0c\u679c\u7136 \u4e00\u627e\u4fbf\u627e\u5230\u4e86\u3002\u5582\uff0c\u4f60\u8d76\u5feb\u6c42\u6211\uff0c\u4f60\u6240\u60f3\u5ff5\u7684\u4eba\uff0c\u73b0\u5728\u5982\u4f55\uff0c\u6211\u53ef\u77e5\u9053\uff01\u201d\u5510\u7ecf\u5927\u53c8\u597d\u6c14\u53c8 \u597d\u7b11\uff0c\u5374\u4e5f\u6025\u4e8e\u8981\u77e5\u9053\u51b0\u5ddd\u5929\u5973\u7684\u6d88\u606f\uff0c\u8f7b\u8f7b\u5730\u6253\u4e86\u5979\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u89c1\u5230\u8c01\u6765 \u4e86\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u6709\u4e86\u65b0\u7684\u670b\u53cb\uff0c\u5c31\u6b3a\u8d1f\u6211\u4e86\uff01\u6211\u504f\u4e0d\u8bf4\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d \u5566\uff0c\u6211\u7684\u5c0f\u8868\u59b9\uff0c\u6211\u5411\u4f60\u8d54\u793c\u4e86\uff0c\u884c\u4e0d\u884c\uff1a\u5feb\u8bf4\uff01\u201d\u674e\u6c81\u6885\u7b11\u4e86\u4e00\u7b11\u9053\uff0c\u201c\u6211\u548c\u5979\u6253\u4e86\u4e00 \u67b6\uff0c\u679c\u7136\u5389\u5bb3\uff0c\u51f6\u5f97\u5f88\u5462\uff01\u6211\u770b\u4f60\u4e5f\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u4f60\u53ef\u5f97\u5c0f\u5fc3\uff0c\u51c6\u5907\u5c06\u6765\u6371\u6253\u3002\u201d\u674e\u6c81\u6885 \u4e00\u80a1\u52b2\u5730\u5411\u5510\u7ecf\u5929\u53d6\u7b11\uff0c\u5510\u7ecf\u5929\u53ef\u65e0\u5fc3\u8bf4\u7b11\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u548c\u5979\u4ea4\u4e86\u624b\u4e86\uff0c\u5979 \u5462\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u6211\u5988\u5988\u73b0\u5728\u6b63\u548c\u5979\u73a9\u800d\u5462\uff0c\u4f60\u77e5\u9053\u6211\u5988\u5988\u7684\u6027\u5b50\uff0c\u600e\u77e5\u9053\u5979\u8981\u73a9\u5230\u51e0 \u65f6\uff1f\u201d\u5510\u7ecf\u5929\u66f4\u662f\u60ca\u5947\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u6b66\u5bb6\u5144\u5f1f\u5462\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u6211\u90a3\u4e24\u4e2a\u5b9d\u8d1d\u4f84\u513f\u8bf4 \u4f60\u8892\u62a4\u90a3\u4e2a\u2018\u5996\u5973\u2019\uff0c\u4e0d\u613f\u89c1\u4f60\u4e86\uff0c\u5176\u5b9e\u561b\uff0c\u6211\u77e5\u9053\u4ed6\u4eec\u662f\u56e0\u4e3a\u7ed9\u90a3\u2018\u5996\u5973\u2019\u6253\u8d25\uff0c\u81ea\u5df1\u96be \u4e3a\u60c5\uff0c\u6240\u4ee5\u4e0d\u6562\u89c1\u4f60\u3002\u5582\uff0c\u5979\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u6211\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7f8e\u4e3d\u7684\u5973\u5b50\uff0c\u5927\u6b66\u5c0f\u6b66\u53eb\u5979 \u505a\u2018\u5996\u5973\u2019\uff0c\u771f\u662f\u4e0d\u8be5\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u54ea\u6709\u5fc3\u60c5\u548c\u5979\u8bf4\u7b11\uff0c\u53ea\u662f\u6413\u7740\u624b\u8d70\u6765\u8d70\u53bb\uff0c\u53e3\u4e2d\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u59e8\u5988\u548c\u5979\u52a8\u624b\uff1f\u8fd9 \u600e\u4e48\u597d\uff1f\u8fd9\u600e\u4e48\u597d\uff1f\u201d\u674e\u6c81\u6885\u7b11\u9053\uff1a\u201c\u6211\u5988\u53c8\u4e0d\u662f\u8981\u6740\u5979\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u5988\u4e5f\u8bf4\u5979\u957f\u5f97\u7f8e\u4e3d\uff0c \u6240\u4ee5\u53ea\u662f\u548c\u5979\u73a9\u73a9\u5462\u3002\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u5440\uff0c\u4f60\u54ea\u91cc\u77e5\u9053\uff0c\u5bf9\u5979\u5c82\u80fd\u620f\u5f04\uff0c\u4f60\u8ba4\u771f\u548c\u5979\u53ae\u6597\uff0c \u5c06\u5979\u6253\u4f24\u4e86\u4e5f\u6bd4\u620f\u5f04\u5979\u597d\u3002\u201d\u5fc3\u4e2d\u9887\u602a\u59e8\u5988\u8d8a\u8001\u8d8a\u4e0d\u6b63\u7ecf\uff0c\u4e00\u751f\u90fd\u662f\u90a3\u4e48\u7231\u548c\u4eba\u5f00\u73a9\u7b11\u3002\u4ed6 \u5374\u5fd8\u4e86\uff0c\u4ed6\u5c0f\u65f6\u559c\u6b22\u59e8\u5988\u66f4\u751a\u4e8e\u559c\u7231\u6bcd\u4eb2\u3002 \u3000\u3000\u539f\u6765\u51af\u7433\u548c\u5510\u7ecf\u5929\u7684\u6bcd\u4eb2\u51af\u745b\u662f\u5b6a\u751f\u59d0\u59b9\uff1b\u4e24\u4eba\u7684\u6027\u683c\u5374\u6b63\u597d\u76f8\u53cd\uff0c\u51af\u745b\u5e84\u91cd\u4e4b\u6781\uff0c\u51af \u7433\u5374\u6dd8\u6c14\u975e\u5e38\uff0c\u4fd7\u8bed\u4e91\uff1a\u201c\u6c5f\u5c71\u6613\u6539\uff0c\u54c1\u6027\u96be\u79fb\u3002\u201d\u8fd9\u80a1\u813e\u6c14\uff0c\u2019\u7adf\u7136\u8001\u4ea6\u4f9d\u7136\u3002\u674e\u6c81\u6885\u7684 \u7956\u6bcd\u662f\u6b66\u743c\u7476\uff0c\u6b66\u743c\u7476\u662f\u767d\u53d1\u9b54\u5973\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u6545\u6b64\u674e\u6c81\u6885\u65e2\u7cbe\u901a\u767d\u53d1\u9b54\u5973\u7684\u5251\u6cd5\uff0c\u53c8\u4ece\u6bcd \u4eb2\u5904\u5b66\u4f1a\u8bb8\u591a\u5916\u6d3e\u7684\u6b66\u529f\uff1b\u5979\u7684\u7a7a\u4e2d\u6251\u51fb\u4e4b\u6280\uff0c\u5c31\u662f\u51af\u7433\u5f53\u5e74\u4ece\u516b\u660f\u795e\u9b54\u8428\u5929\u523a\u90a3\u513f\u5b66\u6765 \u7684\u3002\u51af\u7433\u4e0d\u4f46\u5c06\u5168\u8eab\u672c\u9886\u90fd\u4f20\u7ed9\u5973\u513f\uff0c\u8fde\u6027\u683c\u4e5f\u4f20\u4e86\u7ed9\u5979\u3002 \u3000\u3000\u674e\u6c81\u6885\u89c1\u8868\u5144\u7740\u6025\uff0c\u8d8a\u53d1\u5f97\u610f\uff0c\u7b11\u9053\uff1a\u201c\u8c01\u53eb\u5979\u6b3a\u8d1f\u5927\u6b66\u5c0f\u6b66\uff0c\u4f60\u4e0d\u89c1\u4ed6\u4eec\u90a3\u72fc\u72c8\u7684\u6837 \u513f\uff0c\u90a3\u624d\u771f\u6c14\u4eba\u5462\uff01\u5979\u5c06\u5251\u5c16\u8d34\u7740\u4ed6\u4eec\u7684\u80cc\u5fc3\uff0c\u53c8\u4e0d\u4e0b\u624b\uff0c\u53ea\u662f\u620f\u5f04\uff0c\u5c31\u50cf\u72f8\u732b\u620f\u5f04\u9f20\u5b50\u4e00 \u822c\uff0c\u6211\u4eec\u770b\u4e0d\u8fc7\u773c\u5566\uff01\u6211\u5988\u8981\u7ed9\u4ed6\u4eec\u51fa\u4e00\u53e3\u6c14\uff0c\u975e\u52a0\u500d\u620f\u5f04\u5979\u4e0d\u53ef\u3002\u5582\uff0c\u5582\uff0c\u4f60\u8fd8\u6ca1\u6709\u544a\u8bc9 \u6211\u5462\uff0c\u5979\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5509\u3002\u4f60\u8fd8\u95ee\u5462\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\u3002\u5979\u53eb\u6842\u51b0\u5a25\uff1a\u548c\u4f60\u7956\u6bcd \u540c\u8f88\u7684\u6842\u4ef2\u660e\u5c31\u662f\u5979\u7684\u7956\u7236\u3002\u4f60\u4eec\u5c06\u5979\u620f\u5f04\uff0c\u59e8\u7236\u4e00\u5b9a\u8d23\u602a\u3002\u201d\u674e\u6c81\u6885\u4f38\u4f38\u820c\u5934\u9053\uff1a\u201c\u4f60\u6253 \u7b97\u544a\u6211\u4e48\uff1f\u201d\u5ffd\u800c\u626e\u4e86\u4e2a\u9b3c\u8138\u9053\uff1a\u201c\u6211\u624d\u4e0d\u6015\uff0c\u6211\u6015\u6211\u7239\u7239\uff0c\u6211\u7239\u7239\u6015\u6211\u5988\u5988\uff0c\u6211\u5988\u5988\u53c8\u6015 \u6211\u3002\u4f60\u5440\uff0c\u4f60\u544a\u4e5f\u544a\u4e0d\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u62ff\u5979\u771f\u6ca1\u529e\u6cd5\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u59e8\u5988\u8981\u548c\u5979\u5f00\u73a9\u7b11\uff0c\u90a3\u662f\u8c01\u4e5f\u963b\u6b62\u4e0d\u6765\uff0c\u5c06\u6765\u518d\u6162 \u6162\u5f00\u89e3\u5979\u5427\u3002\u59e8\u5988\u548c\u5c0f\u8f88\u6700\u5408\u5f97\u6765\uff0c\u5979\u5c06\u6765\u82e5\u77e5\u9053\u4e86\u6211\u59e8\u5988\u7684\u6027\u683c\uff0c\u4e5f\u4f1a\u6b22\u559c\u5979\u7684\u3002\u201d\u5fc3\u4e2d \u81ea\u5df1\u5f00\u89e3\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u4f1a\u5230\u8fd9\u513f\u6765\u7684\uff1f\u201d \u3000\u3000\u674e\u6c81\u6885\u5a07\u58f0\u4e00\u7b11\uff0c\u9a84\u8d77\u53cc\u6307\uff0c\u5bf9\u51c6\u4ed6\u7684\u989d\u89d2\u6233\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u8868\u54e5\uff0c\u4f60\u771f\u662f\u660f\u4e86\u5934 \u5566\u3002\u8fde\u4f60\u81ea\u5df1\u7236\u4eb2\u4e09\u5e74\u4e00\u6b21\u7684\u5f00\u5ea7\u8003\u62f3\u90fd\u5fd8\u4e86\u5417\uff1f\u201d\u539f\u6765\u4ed6\u7684\u7236\u4eb2\u5510\u6653\u6f9c\u4e43\u662f\u5929\u5c71\u5404\u6d3e\u7684\u9886 \u8896\uff0c\u5b9a\u4e0b\u89c4\u77e9\uff0c\u6bcf\u4e09\u5e74\u4e00\u6b21\u62db\u96c6\u5929\u5c71\u7684\u540e\u8f88\uff0c\u8003\u4ed6\u4eec\u7684\u6b66\u529f\u672c\u4e8b\uff0c\u4ee5\u5b9a\u5956\u60e9\uff0c\u5e76\u52a0\u4ee5\u6307\u70b9\uff0c \u8fd9\u53eb\u505a\u5c0f\u805a\u96c6\uff1a\u6bcf\u5341\u5e74\u4e00\u6b21\u8fd8\u6709\u4e2a\u5927\u805a\u96c6\uff0c\u4ee5\u524d\u5c31\u4e0d\u53ea\u5728\u56de\u7586\u897f\u85cf\u7684\u540e\u8f88\u8981\u6765\uff0c\u5373\u8fdc\u5728\u5404\u5730 \u7684\u540c\u8f88\uff0c\u51e1\u5c5e\u4e0e\u5929\u5c71\u4e03\u5251\u6709\u6e0a\u6e90\u7684\u90fd\u8981\u6765\uff0c\u5373\u5982\u5ddd\u897f\u7684\u5192\u5ddd\u751f\uff0c\u6e56\u5317\u7684\u77f3\u5e7f\u751f\u7b49\u90fd\u8981\u6765\u7684\u3002 \u4eca\u5e74\u6070\u597d\u662f\u4e09\u5e74\u4e00\u6b21\u7684\u201c\u5c0f\u805a\u96c6\u201d\u4e4b\u671f\uff0c\u5510\u7ecf\u5929\u53bb\u5e74\u4e0b\u5c71\u4e4b\u65f6\uff0c\u5f97\u4ed6\u7236\u4eb2\u7279\u522b\u51c6\u8bb8\uff0c\u82e5\u65e0\u522b \u4e8b\uff0c\u81ea\u5f53\u8d76\u56de\uff0c\u82e5\u8679\u627e\u5bfb\u6842\u534e\u751f\u4f2f\u4f2f\uff1b\u8def\u9014\u9065\u8fdc\uff0c\u4e5f\u53ef\u4ee5\u4f5c\u4e3a\u7f3a\u5e2d\uff0c\u51c6\u4e0d\u53c2\u52a0\uff0c\u6240\u4ee5\u5510\u7ecf\u5929 \u4e00\u65f6\u6ca1\u6709\u60f3\u8d77\u6765\u3002 \u3000\u3000\u800c\u4eca\u5510\u7ecf\u5929\u867d\u7136\u60f3\u8d77\uff0c\u5374\u4ecd\u662f\u6709\u6240\u4e0d\u660e\uff0c\u95ee\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5f00\u5ea7\u8003\u5b66\uff0c\u548c\u4f60\u4eec\u6765\u5230\u8fd9\u513f\u53c8 \u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u59e8\u7236\u8bf4\u8fc7\u545c\uff1f\u6211\u7956\u6bcd\u7684\u5e08\u59d0\u98de\u7ea2\u5dfe\u8001\u524d\u8f88\u5f53\u5e74\u5728\u5357\u7586 \u54c8\u8428\u514b\u90e8\u843d\uff1b\u4f20\u6388\u8fc7\u914b\u957f\u547c\u514b\u6d4e\u592b\u5987\u7684\u51e0\u624b\u6b66\u529f\uff1b\u90a3\u4f4d\u9996\u957f\u7684\u592b\u4eba\u53eb\u5b5f\u66fc\u4e3d\u65af\uff0c\u6b7b\u4e86\u8fd8\u4e0d\u8fc7 \u5341\u5e74\u3001\u6211\u5c0f\u65f6\u5019\u8fd8\u89c1\u8fc7\u5979\u6765\u63a2\u6211\u7684\u7956\u6bcd\u5462\u3002\u540e\u6765\u6211\u7956\u6bcd\u6b7b\u4e86\u3001\u5979\u4e5f\u8001\u5f97\u4e0d\u80fd\u8d70\u52a8\u4e86\uff0c\u8fd9\u624d\u6ca1 \u6765\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u4e2a\u5b5f\u66fc\u4e3d\u65af\u6b7b\u4e86\uff0c\u548c\u4f60\u4eec\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u96be\u9053\u8bf4\u4f60\u4eec\u8981\u5230\u960e\u7f57\u738b\u90a3\u91cc \u627e\u5979\u5417\uff1f\u201d \u3000\u3000\u674e\u6c81\u6885\u788e\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u4f60\u662f\u771f\u7cca\u6d82\u8fd8\u662f\u5047\u7cca\u6d82\uff1f\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u6211\u662f\u771f\u7cca\u6d82\u3002\u201d\u90a3\u5f53 \u7136\u662f\u548c\u5979\u5f00\u5f00\u73a9\u7b11\uff0c\u674e\u6c81\u6885\u5374\u8ba4\u771f\u7684\u8bf4\u9053\uff1a\u201c\u5b5f\u66fc\u4e3d\u65af\u6b7b\u4e86\uff0c\u5979\u8fd8\u6709\u5b50\u5b59\u5440\uff01\u672c\u6765\u5b5f\u66fc\u4e3d\u65af \u53ea\u4e0d\u8fc7\u8ddf\u98de\u7ea2\u5dfe\u8001\u524d\u8f88\u5b66\u4e86\u51e0\u624b\u529f\u592b\uff0c\u4e5f\u6ca1\u6709\u5e08\u5f92\u540d\u4efd\uff0c\u7b97\u4e0d\u4e0a\u662f\u5929\u5c71\u6d3e\u7684\uff0c\u4f46\u5979\u5b59\u513f\u8fd1\u5e74 \u77e5\u9053\u59e8\u7236\u6bcf\u4e09\u5e74\u6709\u4e00\u6b21\u5f00\u5ea7\u8bb2\u5b66\uff0c\u9664\u4e86\u8f83\u8003\u540e\u8f88\u5f1f\u5b50\u4e4b\u5916\uff0c\u8fd8\u6307\u70b9\u5230\u4f1a\u540e\u8f88\u7684\u6b66\u529f\uff0c\u6240\u4ee5\u4ed6 \u4eec\u4e5f\u60f3\u6765\u3002\u6211\u6bcd\u4eb2\u770b\u5728\u6211\u6b7b\u53bb\u7684\u7956\u6bcd\u4efd\u4e0a\uff0c\u51c6\u4e86\u4ed6\u4eec\uff0c\u53c8\u6015\u4ed6\u4eec\u5e74\u8f7b\u5c0f\u8f88\uff0c\u4e0d\u77e5\u6240\u5728\uff0c\u4e0a\u4e0d \u4e86\u5929\u5c71\uff0c\u6240\u4ee5\u7279\u5730\u6765\u63a5\u4ed6\u4eec\uff0c\u5176\u5b9e\u561b\uff0c\u4e5f\u662f\u6211\u6bcd\u4eb2\u4e45\u9759\u601d\u52a8\uff0c\u60f3\u4e0b\u5c71\u73a9\u73a9\uff0c\u6211\u5440\uff0c\u6211\u603b\u662f\u559c \u6b22\u8ddf\u6211\u6bcd\u4eb2\u7684\uff0c\u6240\u4ee5\u4e5f\u5c31\u6765\u5566\u3002\u542c\u8bf4\u8fc7\u4e86\u8fd9\u4e2a\u6c99\u6f20\uff0c\u5357\u8fb9\u5c31\u662f\u54c8\u8428\u514b\u4eba\u7684\u805a\u5c45\u4e4b\u5730\u4e86\uff0c\u662f \u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u662f\u5440\u3002\u56de\u7586\u5730\u65b9\uff0c\u59e8\u5988\u6bd4\u6211\u719f\u5f97\u591a\uff0c\u4f55\u5fc5\u95ee\u6211\uff01\u201d\u674e\u6c81\u6885\u7b11\u9053\uff1a\u201c\u6211\u8d70 \u8fd9\u6c99\u6f20\u4e5f\u8d70\u5f97\u538c\u70e6\u4e86\uff0c\u6211\u5c31\u6015\u6bcd\u4eb2\u662f\u54c4\u6211\u7684\uff0c\u6240\u4ee5\u95ee\u4f60\u4e00\u95ee\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5728 \u5927\u6c99\u6f20\u8fb9\u7f18\uff0c\u6211\u4eec\u9047\u89c1\u4e86\u5927\u6b66\u5c0f\u6b66\uff0c\u4ed6\u8bf4\u8981\u8ffd\u8e2a\u4e24\u4e2a\u4eba\uff0c\u6211\u4eec\u53cd\u6b63\u8981\u7a7f\u8fc7\u6c99\u6f20\uff0c\u5c31\u548c\u4ed6\u4e00\u540c \u8d70\uff0c\u60f3\u4e0d\u5230\u4eca\u665a\u5c31\u9047\u89c1\u90a3\u4e2a\u4ec0\u4e48\u4ec0\u4e48\u6842\u51b0\u5a25\uff0c\u54c8\uff0c\u4e5f\u5c31\u662f\u4f60\u5440\u4f60\u60f3\u5ff5\u7684\u90a3\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5462\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u4ec0\u4e48\u5c3c\u6cca\u5c14\u6b66\u58eb\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a \u201c\u5c31\u662f\u5927\u6b66\u5c0f\u6b66\u8ffd\u8e2a\u7684\u90a3\u4e24\u4e2a\u4eba\u5440\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u6211\u6ca1\u6709\u89c1\u7740\u3002\u4ed6\u4eec\u5c0f\u5b69\u5b50\u95f9\u7740\u73a9\uff0c\u6211\u624d \u61d2\u5f97\u7ba1\u5462\uff01\u201d\u5510\u7ecf\u5929\u5657\u55e4\u4e00\u7b11\uff0c\u674e\u6c81\u6885\u9053\uff1a\u201c\u7b11\u4ec0\u4e48\uff1f\u54fc\uff0c\u4e5f\u8bb8\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u7ed9\u5927\u6b66\u5c0f \u6b66\u6740\u4e86\uff0c\u6240\u4ee5\u4f60\u7684\u51b0\u5a25\u59d0\u59d0\u624d\u90a3\u4e48\u751f\u6c14\u3002\u6211\u5988\u8bf4\u8fc7\uff0c\u5916\u56fd\u7684\u6b66\u58eb\u5230\u4e2d\u56fd\u6765\u591a\u534a\u4e0d\u6000\u597d\u610f\uff0c\u6740 \u4f24\u4e00\u4e24\u4e2a\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u4e00\u56de\u4e8b\u3002\u201d \u3000\u3000\u5510\u7ecf\u5927\u5fc3\u4e2d\u7126\u6025\uff0c\u8d70\u51fa\u5e10\u5e55\uff0c\u671b\u4e86\u53c8\u671b\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8fd8\u6ca1\u56de\u6765\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u6211\u5988\u4f5c \u5f04\u5979\u8fd8\u672a\u591f\u5462\uff0c\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u59e8\u5988\u7b49\u4f1a\u6765\u4e48\uff1f\u201d\u674e\u6c81\u6885\u5fae\u5fae\u4e00\u7b11\uff0c\u7a81\u7136\u4f0f\u5230\u5510\u7ecf\u5929\u80a9\u4e0a\uff0c \u5728\u4ed6\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a\u201c\u6211\u5988\u8bf4\u8981\u7ed9\u4f60\u505a\u5a92\uff0c\u5979\u4eca\u665a\u4f5c\u5f04\u4e86\u4f60\u672a\u6765\u7684\u65b0\u5a18\u5b50\uff0c\u6015\u4f60\u4eec\u4e24\u4e2a\u751f \u6c14\uff0c\u5979\u4e0d\u6765\u5566\u3002\u5979\u53eb\u6211\u5bf9\u4f60\u8bf4\uff0c\u53eb\u4f60\u5e26\u4e86\u65b0\u5a18\u5b50\u56de\u5929\u5c71\u53bb\u3002\u65e2\u7136\u5979\u4e5f\u662f\u81ea\u5df1\u4eba\uff0c\u90a3\u5c31\u66f4\u8be5\u53bb \u5566\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u201d\u674e\u6c81\u6885\u4e00\u672c\u6b63\u7ecf\u7684\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u80e1\u8bf4\uff0c\u4f60\u5230\u4e86\u8fd9\u513f\uff0c\u8fd8\u4e0d\u56de \u53bb\u3002\u96be\u9053\u5f53\u771f\u662f\u53ea\u987e\u4f34\u5979\uff0c\u8fde\u59e8\u7236\u59e8\u6bcd\u4f60\u4e5f\u4e0d\u56de\u53bb\u89c1\u89c1\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e3e\u8d77\u624b\u4f5c\u72b6 \u6253\u5979\uff0c\u674e\u6c81\u6885\u53c8\u7b11\u53c8\u56b7\uff0c\u5ffd\u89c1\u4e00\u4e2a\u767d\u8863\u4eba\u5f71\uff0c\u7a81\u7136\u6765\u5230\u9762\u524d\u3002 \u3000\u3000\u674e\u6c81\u6885\u7b11\u58f0\u4e00\u505c\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u56de\u6765\u5f97\u597d\u5feb\u5475\uff01\u201d\u5510\u7ecf\u5929\u966a\u7740\u7b11\u8138\uff0c\u8fce\u4e0a\u524d \u53bb\u3002\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u5730\u770b\u4e86\u5979\u4eec\u4e00\u773c\uff0c\u7a81\u7136\u626d\u5934\u4fbf\u8d70\uff0c\u5979\u672c\u6765\u5bf9\u674e\u6c81\u6885\u9887\u6709\u597d\u611f\uff0c\u6b64\u9645\u89c1\u4e86\u5979 \u548c\u5510\u7ecf\u5929\u8fd9\u6837\u4eb2\u70ed\uff0c\u5c45\u7136\u8fd8\u5acc\u81ea\u5df1\u201c\u56de\u6765\u5f97\u5feb\u201d\uff0c\u5fc3\u4e2d\u4e0d\u77e5\u600e\u7684\uff0c\u9887\u6709\u4e00\u79cd\u9178\u6e9c\u6e9c\u7684\u5473\u9053\uff0c \u66f4\u517c\u53d7\u4e86\u5979\u6bcd\u4eb2\u7684\u620f\u5f04\uff0c\u6c14\u6124\u96be\u5e73\uff0c\u7adf\u7136\u4e0d\u7406\u5510\u7ecf\u5929\u7684\u547c\u5524\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u81ea\u56de\u5e10\u5e55\u3002 \u3000\u3000\u674e\u6c81\u6885\u4f38\u4f38\u820c\u5c16\uff0c\u9053\uff1a\u201c\u597d\u5927\u7684\u813e\u6c14\uff0c\u5510\u54e5\u54e5\uff0c\u6211\u60f9\u607c\u4f60\u7684\u51b0\u5a25\u59d0\u59d0\u4e86\uff0c\u6211\u53ef\u4e0d\u6562\u518d\u7559 \u5566\u3002\u201d\u5510\u7ecf\u5929\u5bf9\u8fd9\u4e2a\u5c0f\u8868\u59b9\u5b9e\u662f\u6beb\u65e0\u529e\u6cd5\uff0c\u557c\u7b11\u7686\u975e\uff0c\u674e\u6c81\u6885\u8d70\u4e86\u4e24\u6b65\uff0c\u5ffd\u7136\u53c8\u8f6c\u8fc7\u5934\u9053\uff1a \u201c\u8bb0\u7740\uff0c\u5e26\u4f60\u7684\u65b0\u5a18\u5b50\u7ed9\u6211\u4eec\u5144\u5f1f\u89c1\u89c1\uff0c\u4eca\u6b21\u662f\u5728\u6155\u58eb\u5854\u683c\u5c71\u7684\u9a7c\u5cf0\u805a\u96c6\uff0c\u4f60\u6bcd\u4eb2\u66ff\u4f60\u7236\u4eb2 \u8bb2\u5b66\u5462\uff0c\u673a\u4f1a\u771f\u662f\u518d\u597d\u4e5f\u4e0d\u8fc7\u4e86\uff01\u201d\u50cf\u94f6\u94c3\u4e00\u822c\u7684\u7b11\u58f0\u98d8\u8361\u591c\u7a7a\uff0c\u674e\u6c81\u6885\u8fb9\u7b11\u8fb9\u8dd1\uff0c\u8f6c\u77ac\u95f4 \u4fbf\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u7247\u832b\u7136\uff0c\u6162\u6162\u5730\u8d70\u5411\u51b0\u5ddd\u5929\u5973\u7684\u5e10\u5e55\uff0c\u53ea\u89c1\u5e10\u4e2d\u70db\u5149\u5df2\u706d\uff0c\u4f9d\u7a00\u4f3c\u542c\u5f97\u8f8d\u4f4d\u4e4b \u58f0\uff0c\u5510\u7ecf\u5929\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\u201d\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u53eb\u4e86\u4e24\u58f0\uff0c\u4e5f\u6ca1\u56de\u7b54\uff0c\u5728\u5979\u7684\u5e10\u7bf7\u5916\u5f39\u4e86 \u51e0\u4e0b\uff0c\u4e5f\u6ca1\u56de\u7b54\uff0c\u4e0d\u8fc7\u56bc\u4f4d\u4e4b\u58f0\u5374\u6ca1\u6709\u4e86\u3002\u65f7\u91ce\u6c89\u5bc2\uff0c\u591c\u98ce\u8fd8\u5728\u547c\u5578\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f55\u82e6\u6765 \u5462\uff01\u201d\u5446\u5446\u5730\u7ad9\u5728\u51b0\u5ddd\u5929\u5973\u7684\u5e10\u7bf7\u5916\u9762\uff0c\u9065\u671b\u661f\u8fb0\uff0c\u5fc3\u4e2d\u601d\u5982\u6f6e\u6d8c\u3002 \u3000\u3000\u7a81\u7136\u95f4\u4e00\u4e2a\u5ff5\u5934\u5728\u5fc3\u4e0a\u5347\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u5c0f\u8868\u59b9\u867d\u662f\u8bf4\u7b11\uff0c\u4f46\u5e26\u51b0\u5a25\u56de\u53bb\u89c1\u89c1\u7236\u6bcd\u4e5f\u597d\u3002 \u6211\u7236\u4eb2\u548c\u51e0\u4f4d\u524d\u8f88\u90fd\u60f3\u5ff5\u534e\u751f\u4f2f\u6015\uff0c\u89c1\u4e86\u5979\u7684\u5973\u513f\u4e5f\u5b9a\u7136\u6b22\u559c\u3002\u201d\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u51b0\u5a25\u4e00\u542c \u6211\u8bf4\u8981\u53bb\u5929\u5c71\u5c31\u4e0d\u6b22\u559c\uff0c\u6211\u59e8\u5988\u53c8\u620f\u5f04\u4e86\u5979\uff0c\u5979\u66f4\u4e0d\u613f\u53bb\u4e86\uff0c\u600e\u597d\u8bf4\u5f97\uff1f\u201d\u624b\u6307\u5076\u7136\u4e00\u89e6\uff0c \u89e6\u7740\u9f99\u7075\u77eb\u9001\u8fd8\u7ed9\u4ed6\u7684\u90a3\u5757\u6c49\u7389\uff0c\u5510\u7ecf\u5929\u7981\u4e0d\u4f4f\u53c8\u60f3\u9053\uff1a\u201c\u51b0\u5a25\u8981\u53bb\u89c1\u4ed6\u7684\u4f2f\u7236\uff0c\u4e5f\u4e0d\u8fdf\u5728 \u8fd9\u51e0\u4e2a\u6708\u7684\u5de5\u592b\uff0c\u5148\u5230\u5929\u5c71\u4e00\u884c\uff0c\u5012\u662f\u4e24\u5168\u5176\u7f8e\uff0c\u65e2\u514d\u6211\u7236\u6bcd\u6302\u5fc3\uff0c\u53c8\u53ef\u95ee\u90a3\u9f99\u4e09\u5148\u751f\u7684\u6765 \u5386\u3002\u4f46\u600e\u80fd\u8bf4\u5f97\u52a8\u5979\uff1f\u201d\u60f3\u6765\u60f3\u53bb\uff0c\u5ffd\u5730\u5fc3\u751f\u4e00\u8ba1\uff0c\u8fd9\u65f6\u957f\u591c\u5c06\u901d\uff0c\u5feb\u5c06\u662f\u62c2\u6653\u7684\u65f6\u5206\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u60f3\u51fa\u529e\u6cd5\uff0c\u7cbe\u795e\u6296\u64de\uff0c\u7d22\u6027\u518d\u4e0d\u56de\u53bb\u7761\u89c9\uff0c\u5c31\u5728\u51b0\u5ddd\u5929\u5973\u7684\u5e10\u5e55\u524d\u9762\u5f98\u5f8a\u6f2b\u6b65\u3002 \u773c\u89c1\u661f\u6708\u897f\u6c89\uff0c\u671d\u9633\u5347\u8d77\uff0c\u5927\u6f20\u4e4b\u4e0a\uff0c\u5bd2\u6c14\u987f\u6d88\uff0c\u5e10\u5e55\u4e00\u63ed\u3001\u5e7d\u840d\u8d70\u4e86\u51fa\u6765\uff0c\u89c1\u5510\u7ecf\u5929\u8fd8 \u5728\uff0c\u5927\u662f\u60ca\u5947\uff0c\u5510\u7ecf\u5929\u6025\u5fd9\u4e0a\u524d\u95ee\u597d\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u5e7d\u840d\u9053\uff1a\u201c\u516c\u4e3b\u8bf4\uff0c\u4e0d\u7528\u4f60\u966a\u5979\u4e86\uff0c\u5979\u81ea \u5df1\u4f1a\u8d70\u3002\u201d\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u4e0d\u5230\u51b0\u5ddd\u5929\u5973\u5982\u6b64\u4efb\u6027\uff0c\u81ea\u5df1\u60f3\u4e86\u534a\u591c\u60f3\u51fa\u7684\u5999\u8ba1\u7adf\u662f\u767d\u8d39 \u5fc3\u673a\uff0c\u4e0d\u7531\u5f97\u5446\u82e5\u6728\u9e21\uff0c\u8feb\u5207\u4e4b\u95f4\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u5e7d\u840d\u53d7\u5979\u4e3b\u4eba\u6240\u5631\uff0c\u4f20\u8bdd\u4e4b\u65f6\uff0c\u672c\u662f\u4e00\u672c\u6b63\u7ecf\uff0c\u8fd9\u65f6\u89c1\u4e86\u5510\u7ecf\u5929\u5982\u75f4\u4f3c\u50bb\u7684\u6837\u5b50\uff0c\u4e0d\u7531 \u5f97\u53c8\u89c9\u53ef\u601c\uff0c\u53c8\u89c9\u597d\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u6628\u665a\u4e00\u665a\u90fd\u6ca1\u7761\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u51c4\u7136\u82e6\u7b11\uff0c\u4e0d\u7b54\u5e7d \u840d\u7684\u8bdd\uff0c\u81ea\u987e\u81ea\u7684\u541f\u9053\uff1a\u201c\u5982\u6b64\u661f\u8fb0\u975e\u6628\u591c\uff0c\u4e3a\u8c01\u98ce\u9732\u7acb\u4e2d\u5bb5\uff1f\u201d\u5e10\u5e55\u518d\u63ed\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973 \u4e5f\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u672c\u6765\u5bf9\u5510\u7ecf\u5929\u9887\u4e3a\u607c\u6012\uff0c\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u541f\u8fd9\u4e24\u53e5\u8bd7\uff1a\u201c\u5982\u6b64\u661f\u8fb0\u975e\u6628\u591c\uff0c\u4e3a\u8c01 \u98ce\u9732\u7acb\u4e2d\u5bb5\uff01\u201d\u4e0d\u7531\u5f97\u53c8\u559c\u53c8\u60b2\uff0c\u5fc3\u4e2d\u6005\u89e6\uff0c\u51e0\u4e4e\u6d41\u4e0b\u6cea\u6765\u3002\u8fd9\u4e24\u53e5\u662f\u9ec4\u4ef2\u5219\u8bd7\u4e2d\u7684\u540d\u53e5\uff0c \u9ec4\u4ef2\u5219\u662f\u548c\u4ed6\u4eec\u540c\u4e00\u65f6\u4ee3\u7684\u4eba\uff08\u4e7e\u9686\u5341\u56db\u5e74\u751f\uff0c\u56db\u5341\u516b\u5e74\u5352\uff0c\u6bd4\u4ed6\u4eec\u5927\u7ea6\u5927\u5341\u4e94\u516d\u5c81\u3002\uff09\u6e05 \u8bcd\u4e3d\u53e5\uff0c\u4f20\u904d\u5927\u6c5f\u5357\u5317\uff0c\u5c31\u8fde\u6f20\u5916\u8fb9\u7586\uff0c\u51e1\u662f\u6b22\u559c\u8bfb\u4e66\u7684\u4eba\u65e0\u4e0d\u80fd\u80cc\u8bf5\u4ed6\u7684\u8bd7\u53e5\uff0c\u8bd7\u540d\u4e4b \u76db\uff0c\u5c31\u5982\u6e05\u521d\u7eb3\u5170\u5bb9\u82e5\u7684\u8bcd\u540d\u4e00\u6837\u3002\u51b0\u5ddd\u5929\u5973\u7236\u4eb2\u672a\u6b7b\u4e4b\u65f6\uff0c\u5c31\u66fe\u6559\u5979\u80cc\u8fc7\u8fd9\u4e24\u53e5\u8bd7\uff0c\u90a3\u65f6 \u5979\u8fd8\u53ea\u662f\u5341\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u8fd8\u672a\u61c2\u5f97\u4ec0\u4e48\uff0c\u5982\u4eca\u4e00\u542c\u5510\u7ecf\u5927\u5ff5\u51fa\uff0c\u987f\u89c9\u8fd9\u4e24\u53e5\u8bd7\u5b9e\u662f\u51fa\u4e8e\u81f3\u6027 \u81f3\u60c5\uff0c\u611f\u4eba\u4e4b\u6781\u3002\u5c24\u5176\u9002\u5408\u773c\u524d\u7684\u60c5\u666f\uff0c\u5c31\u597d\u4f3c\u8fd9\u662f\u5510\u7ecf\u5929\u7279\u522b\u4e3a\u5979\u6240\u4f5c\u7684\u4e00\u6837\u3002\u51b0\u5ddd\u5929\u5973 \u5e7c\u5931\u7236\u6bcd\uff0c\u72ec\u5904\u51b0\u5bab\uff0c\u867d\u6709\u4e00\u7fa4\u4f8d\u5973\uff0c\u4f46\u5374\u4ece\u672a\u611f\u53d7\u8fc7\u8fd9\u822c\u7684\u5173\u6000\u4e0e\u7231\u60dc\u3002\u201c\u5440\uff0c\u8fd9\u50bb\u4e88\u7adf \u7136\u4e3a\u6211\u5728\u98ce\u9732\u4e4b\u4e2d\u7acb\u4e86\u4e00\u4e2a\u901a\u5bb5\uff01\u201d\u5fc3\u80a0\u4e0d\u7531\u5f97\u8f6f\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u51b2\u53e3\u5ff5\u4e86\u8fd9\u4e24\u53e5\u8bd7\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u51fa\u6765\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u9887\u89c9\u4e0d\u597d\u610f\u601d\uff0c\u4e0a\u524d\u5f3a\u7b11\u8bf4 \u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u597d\u65e9\u5475\uff01\u201d\u5e7d\u840d\u9053\uff1a\u201c\u4f60\u66f4\u65e9\u5462\uff01\u5582\uff0c\u5c0f\u516c\u4e3b\uff0c\u8fd9\u50bb\u5b50\u6628\u665a\u4e00\u665a\u6ca1\u6709\u7761 \u89c9\uff01\u201d\u51b0\u5ddd\u5929\u5973\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5ffd\u7136\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u966a\u4e86\u6211\u4eec\u8fd9\u4e48 \u591a\u5929\uff0c\u4ee5\u540e\u4e0d\u5fc5\u4f60\u966a\u4e86\u3002\u6211\u4eec\u81ea\u5df1\u4f1a\u95ee\u8def\u524d\u5f80\u3002\u201d\u5510\u7ecf\u5929\u542c\u8fd9\u8bed\u6c14\uff0c\u5df2\u7ecf\u8f6f\u4e86\u51e0\u5206\uff0c\u4e00\u7b11\u8bf4 \u9053\uff1a\u201c\u5927\u6f20\u4e4b\u4e2d\uff0c\u6700\u6613\u8ff7\u8def\uff0c\u4e5f\u672a\u5fc5\u9047\u5230\u719f\u6089\u8def\u9014\u4e4b\u4eba\uff0c\u6211\u53cd\u6b63\u6ca1\u4e8b\uff0c\u6b63\u597d\u7ed9\u4f60\u4eec\u5e26\u8def\uff0c\u8bf4 \u5f97\u597d\u597d\u7684\uff0c\u600e\u4e48\u53c8\u8981\u5355\u72ec\u8d70\u4e86\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00\u9178\uff0c\u672c\u60f3\u6c14\u4ed6\u51e0\u53e5\uff0c\u4f46\u4e00\u6765\u51b0\u5ddd\u5929\u5973\u662f\u4e2a\u81ea\u5c0a\u5fc3\u6781\u5f3a\u4e4b\u4eba\uff0c\u4e0d\u613f\u63d0\u8d77\u6628 \u665a\u4e4b\u4e8b\uff0c\u66f4\u4e0d\u613f\u663e\u51fa\u6709\u534a\u70b9\u5992\u5fcc\u4ed6\u548c\u90a3\u4e2a\u5973\u5b69\u5b50\u4eb2\u70ed\u4e4b\u5fc3\uff0c\u4ee5\u514d\u5931\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\uff1b\u4e8c\u6765\u89c1\u5510 \u7ecf\u5929\u90a3\u53ef\u55e4\u53ef\u7b11\u53ef\u601c\u53ef\u60af\u7684\u6837\u513f\uff0c\u4e5f\u4e0d\u5fcd\u518d\u7528\u8bf4\u8bdd\u523a\u4ed6\uff0c\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u53ea\u662f\u5fae\u5fae\u7684\u70b9\u4e86\u70b9 \u5934\uff0c\u8bf4\u58f0\u201c\u4e5f\u597d\u3002\u201d \u3000\u3000\u4e09\u4eba\u5728\u6c99\u6f20\u4e4b\u4e2d\u8d70\u4e86\u51e0\u65e5\uff0c\u51b0\u5ddd\u5929\u5973\u521d\u6b21\u4e0b\u5c71\uff0c\u53c8\u662f\u5728\u8fd9\u79cd\u4e00\u671b\u65e0\u9645\u7684\u6c99\u6f20\u8349\u539f\u4e4b\u4e2d\u65c5 \u884c\uff0c\u51e0\u4e4e\u4e0d\u8fa8\u4e1c\u897f\u5357\u5317\uff0c\u53ea\u662f\u8d8a\u8d70\u8d8a\u89c1\u5c71\u8109\u8d77\u4f0f\uff0c\u8fdc\u8fdc\u90a3\u5ea7\u9ad8\u63d2\u4e91\u9704\u7684\u5927\u5c71\uff0c\u4e5f\u8d8a\u6765\u8d8a\u663e\u73b0 \u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u5947\u9053\uff1a\u201c\u4e0d\u662f\u8bf4\u8981\u5230\u56db\u5ddd\u5417\uff1f\u600e\u4e48\u5012\u597d\u50cf\u8d70\u8fd1\u5929\u5c71\u4e86\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u5929\u5c71\u79bb\u8fd9\u513f\u8fd8\u8fdc\u7740\u5462\uff0c\u54b1\u4eec\u4e0d\u8fc7\u6284\u6377\u5f84\u524d\u5f80\u7f62\u4e86\uff0c\u54ea\u91cc\u662f\u5230\u5929\u5c71\u5440\u3002\u201d \u51b0\u5ddd\u5929\u5973\u6839\u672c\u4e0d\u77e5\u9053\u8def\uff0c\u53ea\u6709\u8ddf\u7740\u4ed6\u8d70\u3002\u5f00\u59cb\u51e0\u5929\uff0c\u51b0\u5ddd\u5929\u5973\u5bf9\u4ed6\u751a\u662f\u51b7\u6de1\uff0c\u5341\u591a\u5929\u540e\uff0c\u6e10 \u6e10\u6709\u8bf4\u6709\u7b11\u3002\u4e00\u884c\u4eba\u7a7f\u8fc7\u4e86\u6c99\u6f20\uff0c\u8fd9\u4e00\u65e5\u5230\u4e86\u4e00\u5ea7\u5927\u5c71\u524d\u9762\uff0c\u5c71\u4e0a\u51b0\u96ea\u8986\u76d6\uff0c\u534a\u5c71\u8170\u5904\uff0c\u4f38 \u51fa\u4e00\u5ea7\u767d\u96ea\u7691\u7691\u7684\u5c71\u5cf0\uff0c\u6321\u5728\u9762\u524d\uff0c\u8fd9\u5ea7\u5c71\u5cf0\uff0c\u597d\u50cf\u4e00\u5934\u5927\u9a86\u9a7c\uff0c\u5934\u4e1c\u5c3e\u897f\uff0c\u62ab\u7740\u6ee1\u8eab\u767d\u8272 \u7684\u7ed2\u6bdb\u3002\u51b0\u5ddd\u5929\u5973\u5fc3\u6709\u6240\u7591\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5929\u5c71\u5417\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u54ea\u91cc\u662f\u5929\u5c71\uff0c \u4f60\u95ee\u95ee\u7267\u4eba\u770b\u3002\u5c71\u4e0b\u662f\u4e00\u7247\u8349\u539f\uff0c\u5e38\u6709\u7267\u4eba\u6765\u5f80\uff0c\u8d70\u4e86\u6570\u91cc\u4e4b\u5730\uff0c\u679c\u7136\u9047\u89c1\u8d76\u9a86\u9a7c\u7684\u4eba\uff0c\u51b0 \u5ddd\u5929\u5973\u4e00\u95ee\uff0c\u59cb\u77e5\u8fd9\u5ea7\u5c71\u540d\u53eb\u6155\u58eb\u5854\u683c\u5c71\uff0c\u8fd9\u5ea7\u5c71\u5cf0\u4fbf\u53eb\u505a\u9a86\u9a7c\u5cf0\u3002\u51b0\u5ddd\u5929\u5973\u8fd9\u624d\u653e\u4e0b\u4e86 \u5fc3\uff0c\u5979\u54ea\u91cc\u77e5\u9053\u6155\u58eb\u5854\u683c\u5c71\u4e43\u662f\u5929\u5c71\u5c71\u8109\u7684\u5206\u652f\uff0c\u548c\u5929\u5c71\u5357\u9762\u7684\u4e3b\u5cf0\u5df2\u7ecf\u76f8\u53bb\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4e0d\u8bc6\u5929\u5c71\u771f\u9762\u76ee\uff0c\u53ea\u7f18\u8eab\u5728\u6b64\u5c71\u4e2d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u53e4\u7a9f\u4f20\u7ecf\u3000\u6e56\u8fb9\u8c08\u5f80\u4e8b\u3000\u51b0\u5f39\u53d7\u632b\u3000\u76c6\u5730\u89c5\u82b3\u8e2a \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u4e94\u56de \u53e4\u7a9f\u4f20\u7ecf\u3000\u6e56\u8fb9\u8c08\u5f80\u4e8b \u51b0\u5f39\u53d7\u632b\u3000\u76c6\u5730\u89c5\u82b3\u8e2a \u3000\u3000\u5c71\u4e0a\u4e91\u6d77\u8ff7\u832b\uff0c\u96ea\u5cf0\u77d7\u7acb\uff0c\u50cf\u6c34\u6676\u4e00\u6837\uff0c\u95ea\u95ea\u53d1\u5149\uff0c\u79ef\u96ea\u7684\u9ad8\u5cf0\u5728\u9633\u5149\u7684\u7167\u5c04\u4e4b\u4e0b\uff0c\u5e7b \u51fa\u5e72\u822c\u5f69\u8272\uff0c\u4e07\u9053\u971e\u8f89\uff0c\u51b0\u5ddd\u5929\u5973\u60f3\u8d77\u51b0\u5bab\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u8fdc\u79bb\u6545\u4e61\u7684\u65c5\u4eba\uff0c\u5ffd\u7136\u770b\u5230\u4e86\u4e0e\u6545 \u4e61\u76f8\u540c\u7684\u666f\u8272\uff0c\u5fcd\u4e0d\u4f4f\u5728\u5c71\u811a\u4e0b\u7559\u8fde\u89c2\u8d4f\uff0c\u55b7\u55b7\u8d5e\u53f9\uff0c\u9053\uff1a\u201c\u8fd9\u5c71\u597d\u50cf\u6bd4\u6211\u6240\u5c45\u4f4f\u7684\u5ff5\u9752\u5510 \u53e4\u62c9\u5c71\u8fd8\u8981\u9ad8\u5462\uff01\u666f\u8272\u4e5f\u7f8e\u4e3d\u6781\u4e86\uff01\u53ea\u662f\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u4e0a\u6709\u4e00\u4e2a\u5929\u6e56\uff0c\u6e56\u5149\u5c71\u8272\uff0c\u4e92\u76f8\u8f89\u6620 \uff0c\u5728\u522b\u7684\u5730\u65b9\u5374\u5bfb\u627e\u4e0d\u5230\u3002\u201d\u5510\u7ecf\u5929\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5ea7\u9a7c\u5cf0\u7684\u4e0a\u9762\u4e5f\u6709\u4e00\u4e2a\u51b0\u6e56\uff0c\u867d\u7136 \u53ca\u4e0d\u4e0a\u5929\u6e56\u7684\u6ce2\u6f9c\u58ee\u9614\uff0c\u4f46\u5374\u53e6\u6709\u4e00\u79cd\u5e7d\u7f8e\u7684\u60c5\u8c03\u3002\u201d\u51b0\u5ddd\u5927\u5973\u56de\u7738\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d \u4f3c\u4e4e\u88ab\u5510\u7ecf\u5929\u6240\u63cf\u5199\u7684\u666f\u8272\u8ff7\u4f4f\uff0c\u60a0\u7136\u795e\u5f80\uff0c\u5ffd\u800c\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\u54b1\u4eec\u8fd8\u8981\u8d76\u8def\u3002\u201d \u3000\u3000\u5c71\u4e0a\u4f20\u6765\u4e86\u8f7b\u5fae\u7684\u58f0\u54cd\uff0c\u597d\u50cf\u5c42\u51b0\u4e4d\u88c2\uff0c\u67af\u679d\u521d\u71c3\uff0c\u53d1\u51fa\u50fb\u50fb\u556a\u556a\u7684\u58f0\u97f3\u3002\u5e7d\u840d\u201c\u54a6\u201d \u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u8e0f\u96ea\u7834\u51b0\u7684\u58f0\u97f3\uff0c\u8fd9\u5c71\u5cf0\u4e0a\u6709\u4eba\u884c\u8d70\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u9002\u624d\u6240\u8bf4\u7684 \u90a3\u4e2a\u51b0\u6e56\uff0c\u4e0d\u4f46\u666f\u8272\u7f8e\u4e3d\uff0c\u6e56\u4e2d\u8fd8\u6709\u96ea\u83b2\u3002\u80c6\u5927\u7684\u730e\u4eba\u5e38\u5728\u5f00\u6625\u7684\u65f6\u5019\u6500\u4e0a\u53bb\u91c7\u96ea\u83b2\uff0c\u542c\u8fd9 \u58f0\u97f3\uff0c\u4f3c\u4e4e\u4e0a\u9762\u91c7\u96ea\u83b2\u7684\u8fd8\u4e0d\u6b62\u4e00\u4eba\u5462\uff01\u201d\u5929\u5c71\u96ea\u83b2\u662f\u4eba\u95f4\u5947\u8469\uff0c\u82b1\u5f00\u4e4b\u65f6\uff0c\u707f\u5982\u4e91\u971e\uff0c\u53c8 \u662f\u65e0\u4e0a\u7684\u5999\u836f\uff0c\u80fd\u6cbb\u8d25\u8840\u3001\u4e8f\u635f\uff0c\u521b\u4f24\uff0c\u5e76\u53ef\u89e3\u5404\u79cd\u5947\u6bd2\uff0c\u51b0\u5bab\u4e2d\u6709\u5404\u79cd\u7075\u4e39\u5999\u836f\uff0c\u5176\u4e2d\u4e5f \u6709\u5929\u5c71\u96ea\u83b2\u5408\u6210\u7684\uff0c\u4eec\u51b0\u5ddd\u5929\u5973\u5374\u6ca1\u6709\u89c1\u8fc7\u76db\u5f00\u7684\u96ea\u83b2\uff0c\u542c\u4e86\u5510\u7ecf\u5929\u7684\u8bdd\uff0c\u7981\u4e0d\u6ce8\u559c\u5b5c\u5b5c\u7684 \u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u5c31\u62fc\u7740\u803d\u6401\u534a\u65e5\u884c\u7a0b\uff0c\u4e0a\u53bb\u77a7\u77a7\uff0c\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u6b63\u662f\u5df4\u4e0d\u5f97\u5979\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u9053\uff1a\u201c\u65e2\u7136\u59d0\u59d0\u6709\u6b64\u96c5\u5174\uff0c\u5c0f\u5f1f\u81ea\u5f53\u5f15\u8def\u3002\u201d\u9a7c\u5cf0\u5ced\u62d4 \u5149\u6ed1\uff0c\u79bd\u517d\u4e5f\u96be\u884c\u8d70\uff0c\u5e73\u65f6\u91c7\u836f\u7684\u4eba\uff0c\u591a\u662f\u7ed3\u4f34\u540c\u884c\uff0c\u7528\u957f\u7ef3\u4e92\u76f8\u8fde\u7cfb\uff0c\u4ee5\u65a7\u51ff\u5728\u5c71\u5ca9\u4e0a\u51ff \u5f00\u88c2\u53e3\uff0c\u63d2\u4e0a\u94c1\u9489\uff0c\u6500\u63f4\u800c\u4e0a\uff0c\u4e5f\u8fd8\u5e38\u6709\u5931\u4e8b\u7684\uff0c\u5e78\u5510\u7ecf\u5929\u8fd9\u4e00\u884c\u4e09\u4eba\u90fd\u5177\u6709\u7edd\u9876\u7684\u8f7b\u529f\uff0c \u4f46\u4e5f\u722c\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\u624d\u722c\u5230\u4e0a\u9762\u3002 \u3000\u3000\u53ea\u89c9\u773c\u524d\u7a7a\u9614\uff0c\u4e00\u7247\u5149\u4eae\uff0c\u5c71\u9876\u4e0a\u6709\u4e00\u80a1\u6e05\u6cc9\uff0c\u6ce8\u5165\u4e00\u4e2a\u65b9\u5706\u6570\u5341\u4e08\u7684\u5c0f\u6e56\u4e2d\uff0c\u6e56\u4e2d\u6709 \u95ea\u5149\u7684\u6d6e\u51b0\u548c\u96f6\u843d\u7684\u82b1\u74e3\uff0c\u6e05\u6cc9\u540e\u9762\u6709\u4e00\u4e1b\u91ce\u82b1\uff0c\u751f\u957f\u5728\u7ea0\u7ed3\u7275\u8fde\u7684\u8346\u68d8\u4e4b\u4e2d\uff0c\u51b0\u4e2d\u5929\u5973\u9053 \uff1a\u201c\u8fd9\u91cc\u9762\u6709\u96ea\u83b2\u5417\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90fd\u7ed9\u4eba\u91c7\u53bb\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9887\u4e3a\u5931\u671b\uff0c\u4f46\u51b0\u6e56\u7684\u666f\u8272 \u5b9e\u5728\u6e05\u4e3d\u4e4b\u6781\uff0c\u8db3\u4ee5\u4ee4\u5979\u6d41\u8fde\u3002\u51b0\u5ddd\u5929\u5973\u4e3e\u76ee\u56db\u671b\uff0c\u53ea\u89c1\u6e56\u7554\u7684\u96ea\u5730\u4e0a\u8bb8\u591a\u811a\u5370\uff0c\u901a\u5230\u82b1\u4e1b \uff0c\u82b1\u4e1b\u540e\u9762\uff0c\u5c71\u7684\u90a3\u8fb9\uff0c\u8fd8\u9690\u9690\u95fb\u5f97\u6742\u4e71\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u5510\u7ecf\u5929\u7b11\u4e86\u4e00\u7b11\uff0c\u5ffd\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u4e2a\u5730\u65b9\uff0c\u4f60\u5b9e\u5728\u5e94\u8be5\u518d\u53bb\u770b\u770b\uff0c\u8fd9\u662f\u4f60\u4eec\u8d35\u6d3e\u53d1\u7965\u4e4b \u5730\u5475\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u7956\u7236\u5f53\u5e74\u5c31\u662f\u5728\u8fd9\u91cc\u9047\u5230\u4f60\u4eec\u7684\u5e08\u7956\u8f9b\u9f99 \u5b50\u7684\u3002\u201d\uff08\u4e8b\u8be6\u62d9\u8457\u300a\u4e03\u5251\u5929\u4e0b\u5c71\u300b\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u82b1\u4e1b\u540e\u9762\u8fd8\u5e94\u8be5\u6709\u6211\u5e08\u7956\u5f53\u5e74\u7684 \u77f3\u7a9f\u3002\u201d\u62d4\u51fa\u5b9d\u5251\uff0c\u62ab\u5f00\u8346\u68d8\uff0c\u7acb\u523b\u5f80\u91cc\u9762\u76f4\u8d70\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u82b1\u4e1b\u4e2d\u7adf\u8f9f\u6709\u4e00\u6761\u5c0f\u5f84\uff0c\u5916\u9762\u7684\u8346\u68d8\u4e0d\u8fc7\u662f\u906e\u63a9\u7684\uff0c\u94fa\u8def\u7684\u6ce5\u571f\u5c1a\u677e\uff0c\u51b0\u5ddd\u5929\u5973 \u5fc3\u4e2d\u8d77\u7591\uff0c\u8fd9\u5c0f\u8def\u770b\u6765\u662f\u65b0\u8fd1\u624d\u5f00\u8f9f\u7684\u3002 \u3000\u3000\u82b1\u4e1b\u540e\u662f\u4e00\u9762\u77f3\u58c1\uff0c\u77f3\u58c1\u4e0a\u51ff\u51fa\u4e00\u4e2a\u7a84\u7a84\u7684\u6d1e\u7a9f\uff0c\u90a3\u5f62\u72b6\u5c31\u50cf\u4e00\u4e2a\u4eba\u76d8\u819d\u800c\u5750\u4e00\u822c\uff0c\u539f \u6765\u8fd9\u4e43\u662f\u8f9b\u9f99\u5b50\u5f53\u5e74\u5750\u5173\u4e4b\u5904\uff0c\u8f9b\u9f99\u4e8e\u66fe\u9760\u7740\u8fd9\u5757\u77f3\u58c1\u5750\u4e86\u4e00\u5341\u4e5d\u5e74\uff0c\u77f3\u58c1\u4e0a\u73b0\u51fa\u4e86\u4ed6\u7684\u8eab \u4f53\u8f6e\u5ed3\uff0c\u540e\u6765\u4ed6\u5c31\u6309\u7167\u8fd9\u4e2a\u5f62\u72b6\uff0c\u51ff\u6210\u4e86\u77f3\u7a9f\u3002\u51b0\u5ddd\u5929\u5973\u7684\u7956\u7236\u6842\u4ef2\u660e\u662f\u8f9b\u9f99\u5b50\u6b7b\u540e\u9057\u4e66\u6240 \u4f20\u6388\u7684\u5f1f\u5b50\uff0c\u6240\u4ee5\u8fd9\u4e2a\u5730\u65b9\u7b97\u5f97\u662f\u6b66\u5f53\u6d3e\u5317\u5b97\u7684\u4e00\u4e2a\u5723\u5730\uff0c\u51b0\u5ddd\u5929\u5973\u62dc\u4e86\u4e09\u62dc\uff0c\u7ed5\u8fc7\u77f3\u58c1\u3002 \u3000\u3000\u7ed5\u8fc7\u77f3\u58c1\uff0c\u4eba\u58f0\u811a\u6b65\u58f0\u66f4\u662f\u6e05\u695a\uff0c\u51b0\u5ddd\u5929\u5973\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5bf9\u9762\u4e00\u5757\u5c71\u5cf0\u659c\u4f38\u51fa\u6765\uff0c\u5c71 \u8170\u5904\u51ff\u6709\u5341\u6570\u4e2a\u6d1e\u7a9f\uff0c\u6b63\u4e2d\u7684\u8fd9\u4e2a\u6d1e\u7a9f\uff0c\u5916\u9762\u8fd8\u642d\u6709\u4e00\u4e2a\u7af9\u68da\uff0c\u7af9\u68da\u5185\u9690\u6709\u4eba\u5f71\uff0c\u5c71\u5761\u4e0a\u5c71 \u8def\u95f4\u6709\u4e09\u4e94\u6210\u7fa4\u7684\u4eba\uff0c\u770b\u6765\u5012\u50cf\u8d76\u8d74\u4ec0\u4e48\u76db\u4f1a\u4f3c\u7684\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u60ca\u7591\u66f4\u751a\uff0c\u5979\u867d\u7136\u4e0d\u8bc6\u6c5f\u6e56\u8def\u9053\uff0c\u4f46\u53ea\u8981\u4e00\u770b\uff0c\u5c31\u77e5\u9053\u8fd9\u4e9b\u4eba\u7edd\u5bf9\u4e0d\u662f\u91c7\u96ea\u83b2 \u7684\u4eba\u3002\u4e00\u4e2a\u5ff5\u5934\u7a81\u7136\u5728\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u5347\u8d77\uff1a\u5510\u7ecf\u5929\u4e3a\u4ec0\u4e48\u8981\u8bf1\u6211\u4e0a\u8fd9\u5c71\u6765\uff1f \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7acb\u523b\u65bd\u5c55\u767b\u840d\u6e21\u6c34\u7684\u529f\u592b\u98de\u63a0\u8fc7\u53bb\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5140\u90a3\u5973\u5b50 \u662f\u4ec0\u4e48\u4eba\uff1f\u8fd9\u91cc\u4e0d\u8bb8\u5916\u4eba\u8d74\u4f1a\uff01\u201d\u53c8\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u54fc\uff0c\u5979\u7adf\u7136\u8fd8\u6562\u4f69\u5251\u4e0a\u5c71\u5462\uff01\u201d\u51b0\u5ddd\u5929 \u5973\u5927\u6012\uff0c\u53ea\u89c1\u5c71\u5761\u4e0a\u4e24\u4e2a\u9ed1\u8863\u5c11\u5e74\uff0c\u6b63\u5728\u5bf9\u7740\u81ea\u5df1\u6307\u6307\u70b9\u70b9\uff0c\u51b0\u5ddd\u5929\u5973\u6b63\u60f3\u53d1\u4f5c\uff0c\u5ffd\u53c8\u542c\u5f97 \u4e00\u58f0\u5a07\u7b11\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u5e26\u7740\u7a1a\u6c14\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u5510\u5bb6\u54e5\u54e5\uff0c\u4f60\u679c\u7136\u542c\u6211\u7684\u8bdd\uff0c\u771f\u628a\u5979\u5e26 \u6765\u4e86\uff0c\u5582\uff0c\u4f60\u4eec\u4f11\u5f97\u80e1\u8bf4\uff0c\u60f9\u607c\u4e86\u5510\u54e5\u54e5\uff0c\u5979\u624d\u4e0d\u662f\u5916\u4eba\u5462\uff01\u4f60\u4eec\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u4eba\uff1f\u6765\uff0c\u6765 \uff0c\u6765\uff0c\u6211\u544a\u8bc9\u4f60\u4eec\uff01\u201d\u8fd9\u5c0f\u59d1\u5a18\u6b63\u662f\u66fe\u4e0e\u51b0\u5ddd\u5929\u5973\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u674e\u6c81\u6885\uff0c\u53ea\u89c1\u5979\u4e00\u9762\u5411\u5510\u7ecf \u5929\u62db\u624b\uff0c\u4e00\u9762\u5411\u81ea\u5df1\u6307\u70b9\uff0c\u548c\u90a3\u4e24\u4e2a\u9ed1\u8863\u5c11\u5e74\u6324\u7709\u5f04\u773c\uff0c\u663e\u7136\u662f\u62ff\u51b0\u5ddd\u5929\u5973\u53d6\u7b11\uff0c\u674e\u6c81\u6885\u540e \u9762\u8fd8\u6709\u6b66\u5bb6\u5144\u5f1f\u548c\u53e6\u5916\u4e24\u4e2a\u4e0d\u77e5\u540d\u5b57\u7684\u4eba\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u4e2d\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u5510\u7ecf\u5929\u4f60\u8fd9\u5c0f\u5b50\u7adf\u7136\u6562\u5982\u6b64\u6349\u5f04\u4e8e\u6211\uff0c\u5c06 \u6211\u5e26\u4e0a\u5c71\u6765\u7ed9\u4eba\u7b11\u8bdd\uff01\u201d\u8f6c\u8fc7\u8eab\u5c31\u60f3\u627e\u5510\u7ecf\u5929\u7b97\u5e10\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u5df2\u88ab\u90a3\u5c0f\u59d1\u5a18\u622a\u7740\u3001\u4e0d\u4f4f\u5730 \u8bf4\uff1a\u201c\u5c0f\u8868\u59b9\uff0c\u4f60\u4f11\u5f97\u80e1\u8bf4\u516b\u9053\uff0c\u80e1\u8bf4\u516b\u9053\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u66f4\u662f\u6c14\u6012\uff0c\u521a\u8f6c\u8eab\u5954\u51fa\u4e24\u6b65\uff0c\u5ffd\u89c1\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u4e00\u4e2a\u7f8e\u8c8c\u7684\u4e2d\u5e74\u5987\u4eba\u6084\u6ca1\u58f0 \u606f\u5730\u62e6\u5728\u81ea\u5df1\u7684\u9762\u524d\uff0c\u6b63\u662f\u66fe\u7f9e\u8fb1\u8fc7\u5979\u7684\u90a3\u4e2a\u5987\u4eba\uff0c\u53ea\u89c1\u5979\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\uff0c\u4f60\u662f\u548c \u7ecf\u513f\u540c\u6765\u7684\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5927\u6012\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u4e00\u6296\u624b\u5c31\u662f\u516d\u679a\u51b0\u9b44\u795e\u5f39\u9f50\u5411\u90a3\u7f8e\u5987\u4eba\u98de\u53bb\uff01 \u516d\u679a\u9f50\u53d1\uff0c\u5a01\u529b\u5947\u5927\uff0c\u5373\u4f7f\u8840\u795e\u5b50\u4e5f\u7981\u53d7\u4e0d\u4f4f\uff0c\u51b0\u5ddd\u5929\u5973\u88ab\u8fd9\u5987\u4eba\u620f\u800d\uff0c\u5fc3\u4e2d\u6c14\u607c\uff0c\u53c8\u77e5\u9053 \u81ea\u5df1\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u6240\u4ee5\u4e00\u51fa\u624b\u5c31\u7528\u8fd9\u79cd\u4e16\u4e0a\u65e0\u53cc\u7684\u6697\u5668\u53d6\u80dc\u3002 \u3000\u3000\u90a3\u5987\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u73a9\u827a\uff1f\u201d\u53ea\u89c1\u5979\u4e94\u6307\u9f50\u6325\uff0c\u6709\u5982\u4e00\u6735\u5170\u82b1\u7a81\u7136\u5f00\u653e \uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\uff0c\u53ee\u53ee\u58f0\u54cd\uff0c\u4e94\u679a\u51b0\u9b44\u795e\u5f39\u89e6\u6307\u98de\u626c\uff0c\u5728\u7a7a\u4e2d\u98d8\u98d8\u8361\u8361\uff0c\u65e2\u4e0d\u7834\u88c2\uff0c\u4ea6\u4e0d\u843d \u4e0b\uff0c\u529b\u9053\u7528\u5f97\u4e4b\u5de7\uff0c\u771f\u662f\u51fa\u795e\u4eba\u5316\uff1a\u4f46\u8fd9\u8fd8\u4e0d\u8db3\u4e3a\u5947\uff0c\u66f4\u4ee4\u51b0\u5ddd\u5929\u5973\u5403\u60ca\u7684\u662f\uff1a\u6700\u540e\u4e00\u679a\u51b0 \u9b44\u795e\u5f39\uff0c\u5979\u7adf\u7136\u7528\u53e3\u54ac\u7740\uff0c\u820c\u5c16\u4e00\u5377\uff0c\u541e\u4e86\u8fdb\u53bb\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u51b0\u9b44\u7cbe\u82f1\uff0c\u6bd4\u8fd9\u5c71\u4e0a \u7684\u6e05\u6cc9\u597d\u559d\u591a\u4e86\u3002\u201d\u51b0\u9b44\u795e\u5f39\u7684\u5947\u5bd2\u4e4b\u6c14\uff0c\u5185\u529f\u706b\u5019\u672a\u5230\u7684\uff0c\u53ea\u8981\u89e6\u7740\u4fbf\u4f1a\u751f\u75c5\uff0c\u5185\u529f\u597d\u7684 \uff0c\u82e5\u88ab\u6253\u4e2d\u7a74\u9053\uff0c\u4ea6\u8981\u7981\u53d7\u4e0d\u4f4f\uff0c\u81f3\u4e8e\u80fd\u591f\u628a\u5b83\u541e\u4e0b\uff0c\u5f53\u4f5c\u96ea\u6c34\u4e00\u822c\u5403\u6389\uff0c\u90a3\u7b80\u76f4\u662f\u96be\u4ee5\u60f3 \u50cf\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51db\u7136\u4e00\u60ca\uff0c\u8f6c\u8eab\u4fbf\u8d70\uff0c\u53ea\u89c1\u90a3\u7f8e\u5987\u4eba\u8eab\u5f62\u4e00\u8d77\uff0c\u53cc\u8896\u4e00\u5377\uff0c\u628a\u5f39\u4e0a\u534a\u7a7a\u7684\u4e94\u679a \u51b0\u9b44\u795e\u5f39\u90fd\u63a5\u5165\u8896\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6697\u5668\u6211\u5012\u672a\u66fe\u89c1\u8fc7\uff0c\u5012\u5f97\u4ed4\u7ec6\u77a7\u77a7\u3002\u5582\uff0c\u5c0f\u59d1\u5a18\uff0c\u6211\u4e0e\u4f60 \u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e3a\u4f55\u4f60\u4e00\u89c1\u9762\u5c31\u7528\u8fd9\u79cd\u5389\u5bb3\u7684\u6697\u5668\u6253\u6211\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9886\u6559\u8fc7\u8fd9\u5987\u4eba\u6dd8\u6c14\u7684\u624b\u6bb5\uff0c \u53ea\u9053\u5979\u53c8\u8981\u6765\u620f\u5f04\u4e8e\u5df1\uff0c\u5fc3\u60f3\u8fd9\u5987\u4eba\u672c\u9886\u6bd4\u81ea\u5df1\u9ad8\u51fa\u5341\u500d\uff0c\u8981\u9003\u4e5f\u9003\u4e0d\u60bc\uff0c\u5fc3\u4e2d\u4e00\u5b9a\uff0c\u53cd\u800c \u7ad9\u4f4f\uff0c\u6124\u7136\u9a82\u9053\uff1a\u201c\u4f60\u82e5\u7136\u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u5c31\u4e0d\u8be5\u5982\u6b64\u4e24\u6b21\u4e09\u756a\u620f\u5f04\u540e\u8f88\u3002\u54fc\uff0c\u5929\u5c71\u6d3e\u7684\u771f\u4f1a \u6043\u5f3a\u6b3a\u5f31\uff0c\u73b0\u5728\u6211\u624d\u76f8\u4fe1\u3002\u201d\u90a3\u5987\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u51e0\u65f6\u620f\u5f04\u8fc7\u5979\uff0c\u4e3a\u4f55\u5979\u5982\u6b64\u9a82\u6211\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u5987\u4eba\u5e76\u4e0d\u662f\u674e\u6c81\u6885\u7684\u6bcd\u4eb2\u51af\u7433\uff0c\u5374\u662f\u5510\u7ecf\u5929\u7684\u6bcd\u4eb2\u51af\u745b\u3002\u51af\u745b\u51af\u7433\u662f\u4e00\u5bf9\u5b6a\u751f\u59ca \u59b9\uff0c\u6027\u60c5\u5927\u4e0d\u76f8\u540c\uff0c\u76f8\u8c8c\u5b8c\u5168\u4e00\u6837\u3002\u51af\u745b\u662f\u5f53\u5e74\u5929\u5c71\u5973\u4fa0\u6613\u5170\u73e0\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u53c8\u5f97\u8fc7\u5415\u56db\u5a18 \u7684\u6307\u70b9\uff0c\u6bd4\u5979\u7684\u4e08\u592b\uff0c\u73b0\u5728\u5929\u5c71\u6d3e\u7684\u9886\u8896\u5510\u6653\u6f9c\u7684\u6b66\u529f\u8fd8\u9ad8\u660e\u5f97\u591a\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u65e0\u4eba\u53ef\u4ee5\u4e0e \u4e4b\u76f8\u6bd4\uff01\u8fd9\u6b21\u9a7c\u5cf0\u805a\u4f1a\uff0c\u5c31\u662f\u7531\u5979\u4e3b\u6301\u7684\u3002 \u3000\u3000\u51af\u745b\u6027\u60c5\u67d4\u548c\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u53d1\u6012\uff0c\u66f4\u89c9\u695a\u695a\u53ef\u601c\uff0c\u672c\u6765\u60f3\u62ff\u7740\u5979\u95ee\u8bdd\u7684\uff0c\u542c\u5979\u5982\u6b64\u4e00\u8bf4 \uff0c\u53cd\u800c\u9000\u540e\u4e09\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5bf9\u5929\u5c71\u6d3e\u7684\u6210\u89c1\u4e5f\u672a\u514d\u592a\u6df1\u4e86\uff0c\u597d\u5427\uff0c\u6211\u4e0d\u903c\u4f60\u3002\u4f60\u613f\u8bf4\u4fbf\u8bf4 \uff0c\u4e0d\u613f\u8bf4\u6211\u4e5f\u4e0d\u95ee\u4f60\u7684\u6765\u5386\u56e0\u7531\u3002\u201d\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201c\u840d\u513f\uff0c\u4e0b\u5c71\uff01\u201d\u8bdd\u58f0\u672a\u8bf4\u5b8c\uff0c\u8eab\u5f62\u5df2 \u63a0\u51fa\u5341\u6570\u4e08\u5916\uff0c\u51af\u745b\u89c1\u4e86\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u8d5e\u9053\uff1a\u201c\u5f53\u5e74\u6211\u5728\u5979\u8fd9\u822c\u5e74\u7eaa\uff0c\u4e5f\u6ca1\u6709\u5979\u8fd9\u6837\u9ad8\u660e\u7684 \u8f7b\u529f\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u75be\u8dd1\uff0c\u9690\u9690\u542c\u5f97\u5510\u7ecf\u5929\u5728\u540e\u9762\u547c\u5524\uff0c\u51b0\u5ddd\u5929\u5973\u6c14\u607c\u4e4b\u6781\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u970e\u773c\u4e4b\u95f4 \uff0c\u5c31\u8dd1\u8fc7\u4e00\u4e2a\u5c71\u62d7\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u6885\u513f\u8bf4\u4f60\u4e00\u5b9a\u4f1a\u6765\uff0c\u6211\u8fd8\u4e0d\u76f8\u4fe1\u5462\u3002\u54c8\uff0c\u4f60\u679c\u7136\u6765 \u4e86\u3002\u770b\u6765\u6211\u8fd9\u4e2a\u5a92\u53ef\u8981\u505a\u5b9a\u4e86\uff01\u201d\u53ea\u89c1\u4e00\u4e2a\u5987\u4eba\u62e6\u5728\u524d\u9762\uff0c\u7b11\u5f97\u5934\u4e0a\u7684\u4e24\u4e2a\u8774\u8776\u7ed3\u4e5f\u8fce\u98ce\u6446 \u52a8\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u77e5\u8fd9\u662f\u51af\u7433\uff0c\u8fd8\u4ee5\u4e3a\u662f\u9002\u624d\u4e0e\u81ea\u5df1\u4ea4\u624b\u7684\u90a3\u4e2a\u5987\u4eba\uff0c\u6545\u610f\u6284\u5c0f\u5f84\u8ffd\u6765\u5c06\u5979\u620f \u5f04\uff0c\u4e00\u6643\u8eab\u5411\u659c\u5761\u5954\u4e0b\uff0c\u6b63\u60f3\u51fa\u8a00\u9a82\u5979\uff0c\u5ffd\u7136\u659c\u5761\u4e0a\u7684\u4e71\u77f3\u5806\u4e2d\u53c8\u7a9c\u51fa\u4e00\u4eba\uff0c\u5374\u662f\u8840\u795e\u5b50\u3002 \u3000\u3000\u539f\u6765\u81ea\u9f99\u7075\u77eb\u5728\u62c9\u8428\u88ab\u798f\u5eb7\u5b89\u6263\u7559\u4e4b\u540e\uff0c\u798f\u5eb7\u5b89\u8981\u9063\u4eba\u4e0a\u4eac\uff0c\u95ee\u660e\u771f\u76f8\uff0c\u4e0d\u80af\u5c06\u9f99\u7075\u77eb \u4ea4\u4e0e\u8840\u795e\u5b50\u3002\u8840\u795e\u5b50\u65e0\u6cd5\uff0c\u53ea\u597d\u6d3e\u4e91\u7075\u5b50\u5148\u8d76\u5165\u4eac\uff0c\u7980\u544a\u5927\u5185\u603b\u7ba1\uff0c\u4e00\u9762\u7559\u4e0b\u6851\u771f\u5a18\u5728\u62c9\u8428 \u76d1\u89c6\uff0c\u800c\u81ea\u5df1\u5219\u6697\u4e2d\u8ffd\u8e2a\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\uff0c\u987a\u8def\u60f3\u518d\u9080\u4e00\u4e24\u4f4d\u5f3a\u624b\u76f8\u52a9\u3002 \u3000\u3000\u8840\u795e\u5b50\u81ea\u601d\uff0c\u82e5\u7136\u4ee5\u4e00\u5bf9\u4e00\uff0c\u5219\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u90fd\u8981\u6bd4\u81ea\u5df1\u7a0d\u900a\u4e00\u7b79\u3002\u4f46\u4ee5\u4e00\u5bf9\u4e8c\u5374 \u662f\u96be\u4ee5\u53d6\u80dc\uff0c\u56e0\u6b64\u53ea\u6562\u6697\u4e2d\u8ffd\u8e2a\uff0c\u4e0d\u6562\u9732\u9762\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u6765\u5230\u4e86\u6155\u58eb\u5854\u683c\u5c71\u7684\u9a7c\u5cf0\u4e4b\u4e0b\uff0c\u89c1\u5510\u7ecf\u5929\u7b49\u4e00\u884c\u4e09\u4eba\u6500\u4e0a\u5c71\u5cf0\uff0c\u8840\u795e\u5b50\u4e5f\u8ffd\u8e2a\u800c \u81f3\uff0c\u56e0\u4ed6\u4e0d\u8bc6\u5c71\u8def\uff0c\u53c8\u662f\u5f85\u5510\u7ecf\u5929\u7b49\u4eba\u6500\u4e0a\u5c71\u624d\u8ddf\u4e0a\u6765\u7684\uff0c\u6545\u6b64\u8d76\u5230\u4e4b\u65f6\uff0c\u5df2\u7ecf\u662f\u51b0\u5ddd\u5929\u5973 \u9003\u4e0b\u6155\u58eb\u5854\u683c\u5c71\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u8840\u795e\u5b50\u7a81\u7136\u78b0\u7740\u51b0\u5ddd\u5929\u5973\u4e5f\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u89c1\u5979\u53ea\u662f\u4e00\u4eba\uff0c\u800c\u4e14\u795e\u60c5\u72fc\u72c8\uff0c\u4f3c\u4e4e\u521a\u521a\u7ed9 \u4eba\u6253\u8d25\u7684\u6837\u5b50\uff0c\u53c8\u4e0d\u7981\u5fc3\u4e2d\u6697\u559c\uff0c\u4fbf\u7a81\u7136\u7a9c\u4e86\u51fa\u6765\uff0c\u8fce\u5934\u5c31\u662f\u4e00\u638c\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u524d\u540e\u53d7\u653b\uff0c\u6697\u53eb\u4e00\u58f0\u82e6\u4e5f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8840\u795e\u5b50\u72b9\u53ef\u62b5\u654c\uff0c\u90a3\u5987\u4eba\u5374\u662f\u592a\u8fc7\u5389 \u5bb3\u3002\u201d\u4e0d\u6562\u9000\u540e\uff0c\u53ea\u597d\u5411\u8840\u795e\u5b50\u75be\u653b\uff0c\u4e00\u6296\u624b\u5148\u53d1\u51fa\u4e09\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u968f\u5373\u628a\u5bd2\u5149\u5251\u4e00\u6325\uff0c\u62a4 \u5b9a\u8eab\u8eaf\uff0c\u75be\u51b2\u800c\u8fc7\u3002 \u3000\u3000\u8840\u795e\u5b50\u77e5\u9053\u51b0\u9b44\u795e\u5f39\u5389\u5bb3\uff0c\u597d\u751f\u6e9c\u6ed1\uff0c\u9661\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u79fb\u5f62\u6362\u4f4d\uff0c\u907f\u5f00\u51b0\u9b44\u795e\u5f39\uff0c\u4e00\u4e0b \u5b50\u4fbf\u5230\u4e86\u51b0\u5ddd\u5929\u5973\u53f3\u4fa7\uff0c\u66f4\u4e0d\u6362\u62db\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u7acb\u523b\u53d8\u4e3a\u64d2\u62ff\u624b\u6cd5\uff0c\u786c\u62a2\u51b0\u5ddd\u5929\u5973\u7684\u5b9d\u5251\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6b63\u89c9\u7740\u4e00\u80a1\u70ed\u6c14\u6251\u9762\u55b7\u6765\uff0c\u6b63\u60f3\u6a2a\u5251\u524a\u4e0b\uff0c\u5ffd\u89c9\u80cc\u540e\u8863\u8882\u5e26\u98ce\u4e4b\u58f0\uff0c\u9888\u9879\u4e00\u51c9 \uff0c\u8033\u8fb9\u542c\u5f97\u90a3\u5987\u4eba\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u5929\u65f6\u4e0d\u6b63\uff0c\u53c8\u51b7\u53c8\u70ed\uff0c\u4f60\u4eec\u6363\u4ec0\u4e48\u9b3c\uff1f\u201d\u539f\u6765\u51af\u7433\u98de\u8eab\u8d76\u5230 \uff0c\u5979\u89c1\u8840\u795e\u5b50\u76f8\u8c8c\u53e4\u602a\uff0c\u638c\u53d1\u70ed\u98ce\u3001\u800c\u51b0\u5ddd\u5929\u5973\u5219\u53d1\u51fa\u4e00\u79cd\u5e26\u7740\u5947\u5bd2\u4e4b\u6c14\u7684\u6697\u5668\uff0c\u4e24\u8005\u90fd\u662f \u5979\u672a\u66fe\u89c1\u8fc7\u7684\u201c\u5b9d\u8d1d\u201d\uff0c\u5979\u4e00\u6dd8\u6c14\uff0c\u4fbf\u5728\u4e24\u4eba\u7684\u9888\u9879\u5404\u5439\u4e86\u4e00\u53e3\u51c9\u6c14\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u8dc3\u8df3\u5f00\uff0c\u90a3\u5c71\u5761\u94fa\u6ee1\u51b0\u96ea\uff0c\u51b0\u5ddd\u5929\u5973\u5728\u51b0\u5cf0\u4e4b\u4e0a\u957f\u5927\uff0c\u6e9c\u51b0\u6ed1\u96ea\u662f\u5979\u6700\u64c5\u957f \u7684\u6280\u827a\uff0c\u95ea\u5f00\u4e4b\u540e\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u4fbf\u5728\u5ced\u6ed1\u7684\u5c71\u5761\u4e0a\u76f4\u6e9c\u4e0b\u53bb\u3002\u8840\u795e\u5b50\u5374\u4e0d\u77e5\u51af\u7433\u662f\u4f55\u7b49\u6837\u4eba \uff0c\u6068\u5979\u653e\u8d70\u654c\u4eba\uff0c\u53c8\u88ab\u5979\u8fde\u5439\u4e09\u53e3\u51c9\u6c14\uff0c\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e3e\u638c\u4fbf\u5288\u51af\u7433\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6e9c\u5230\u5c71\u5761\uff0c\u5c71\u98ce\u5439\u6765\uff0c\u9690\u9690\u542c\u5f97\u5510\u7ecf\u5929\u547c\u5524\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u811a\u6b65\u7a0d\u6162\uff0c\u5ffd\u89c1 \u5c71\u5761\u8f6c\u89d2\u5904\u53c8\u7a9c\u51fa\u4e24\u4eba\uff0c\u5374\u662f\u4e0e\u674e\u6c81\u6885\u5728\u4e00\u8d77\u7684\u90a3\u4e24\u4e2a\u9ed1\u8863\u5c11\u5e74\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7559\u4e0b\u5251\u6765\uff0c \u8ba9\u4f60\u4e0b\u5c71\uff01\u201d\u8fd9\u4e24\u4e2a\u5c11\u5e74\uff0c\u4e00\u4e2a\u662f\u674e\u6c81\u6885\u7684\u54e5\u54e5\u674e\u9752\u83b2\uff0c\u4e00\u4e2a\u662f\u5510\u6653\u6f9c\u7684\u5f92\u5f1f\uff0c\u5f53\u5e74\u65e0\u6781\u6d3e \u5927\u5e08\u949f\u4e07\u5802\u7684\u4f84\u5b59\u949f\u5c55\uff0c\u4e24\u4eba\u4e00\u822c\u5e74\u7eaa\uff0c\u4e00\u6837\u6253\u626e\uff0c\u5c31\u5982\u5144\u5f1f\u4e00\u822c\u3002\u8fd9\u4e24\u4eba\u90fd\u5c5e\u5c11\u5e74\u597d\u4e8b\u4e4b \u6d41\uff0c\u88ab\u6b66\u6c0f\u5144\u5f1f\u5506\u4f7f\uff0c\u9884\u5148\u8d70\u5f00\uff0c\u6084\u6084\u5230\u8fd9\u91cc\u57cb\u4f0f\uff0c\u60f3\u6298\u8fb1\u4e00\u4e0b\u51b0\u5ddd\u5929\u5973\uff0c\u66ff\u6b66\u6c0f\u5144\u5f1f\u51fa\u53e3 \u95f7\u6c14\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u67f3\u7709\u4e00\u626c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u4eec\u5929\u5c71\u5f1f\u5b50\u5c31\u6709\u8fd9\u4e48\u9738\u9053\uff01\u201d\u811a\u5c16\u4e00\u70b9\u96ea\u5730 \uff0c\u7bad\u4e00\u822c\u7684\u7acb\u523b\u5230\u4e86\u4e24\u4e2a\u9ed1\u8863\u5c11\u5e74\u7684\u9762\u524d\uff0c\u4e00\u62db\u201c\u5343\u91cc\u51b0\u5c01\u201d\uff0c\u5bd2\u5149\u5251\u6325\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u7acb\u5373 \u628a\u4e24\u4e2a\u5c11\u5e74\u7684\u957f\u5251\u5708\u5728\u5f53\u4e2d\u3002\u5979\u7684\u6ed1\u96ea\u672c\u9886\u4e3e\u4e16\u65e0\u53cc\uff0c\u6bd4\u201c\u9646\u5730\u98de\u817e\u201d\u7684\u8f7b\u529f\u8fd8\u8981\u5feb\u5f97\u591a\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u5c11\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u53cc\u5251\u521a\u521a\u5c55\u5f00\uff0c\u5c31\u88ab\u51b0\u5ddd\u5929\u5973\u5b9d\u5251\u7684\u51b0\u9b44\u5bd2\u5149\u88f9\u4f4f\uff0c\u51b0\u5ddd\u5929\u5973 \u5251\u67c4\u8f6c\u4e86\u51e0\u8f6c\uff0c\u4e24\u4e2a\u5c11\u5e74\u7684\u957f\u5251\u51e0\u4e4e\u7ed9\u5979\u7ede\u5f97\u8131\u624b\u98de\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u607c\u6012\uff0c\u7acb\u610f\u8981\u5c06\u4ed6\u4eec \u7684\u5175\u5203\u53cd\u593a\u51fa\u624b\uff0c\u5251\u5149\u8d8a\u6536\u8d8a\u7d27\uff0c\u7ede\u8f6c\u4e5f\u8d8a\u6765\u8d8a\u5feb\u3002\u956d\u5c55\u662f\u5510\u6653\u6f9c\u6240\u6536\u7684\u552f\u4e00\u5f1f\u5b50\uff0c\u6b66\u529f\u706b \u5019\u867d\u7136\u8fdc\u4e0d\u53ca\u4ed6\u7684\u5e08\u5144\u5510\u7ecf\u5929\uff0c\u4f46\u4ea6\u5df2\u5f97\u5929\u5c71\u5251\u6cd5\u7684\u771f\u4f20\uff0c\u4e34\u573a\u4ea6\u8f83\u9547\u5b9a\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u7684\u5251 \u8fd0\u8f6c\u5982\u98ce\uff0c\u96be\u4ee5\u76f8\u6297\uff0c\u7a81\u7136\u609f\u51fa\u4ee5\u9759\u5236\u52a8\u4e4b\u9053\uff0c\u8d81\u7740\u51b0\u5ddd\u5929\u5973\u5728\u4e24\u62db\u4e4b\u95f4\uff0c\u52b2\u529b\u4e00\u7d27\u4e00\u677e\u7684 \u8fde\u63a5\u95f4\u9699\uff0c\u7a81\u7136\u4f7f\u51fa\u4e00\u62db\u201c\u6c5f\u6d77\u51dd\u5149\u201d\uff0c\u8fd9\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u7684\u4e00\u62db\u6700\u7a33\u5065\u7684\u9632 \u5b88\u62db\u6570\uff0c\u5168\u8eab\u52b2\u529b\u90fd\u51dd\u5728\u5251\u5c16\uff0c\u51b0\u5ddd\u5929\u5973\u6b63\u81ea\u5f97\u5fc3\u5e94\u624b\uff0c\u5ffd\u89c9\u654c\u4eba\u7684\u957f\u5251\u7adf\u4f3c\u5316\u6210\u4e86\u4e00\u6761\u94c1 \u67f1\uff0c\u7ede\u4e4b\u4e0d\u8f6c\uff0c\u6014\u4e86\u4e00\u6014\uff1b\u674e\u9752\u83b2\u5b66\u7684\u662f\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u7684\u5947\u8be1\u5251\u6cd5\uff0c\u8d81\u673a\u5c06\u957f\u5251\u5411\u524d\u4e00\u63a2 \uff0c\u7acb\u523b\u6d88\u89e3\u4e86\u51b0\u9b44\u5bd2\u5149\u5251\u7684\u7ede\u8f6c\u4e4b\u52bf\uff0c\u5237\u5237\u4e24\u5251\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u4f3c\u5de6\u53cd\u53f3\uff0c\u9a6c\u4e0a\u8f6c\u5b88\u4e3a\u653b\u3002 \u3000\u3000\u8bba\u5230\u771f\u5b9e\u7684\u672c\u9886\uff0c\u51b0\u5ddd\u5929\u5973\u56fa\u7136\u8981\u6bd4\u949f\u5c55\u674e\u9752\u83b2\u4efb\u4f55\u4e00\u4e2a\u90fd\u5f3a\uff0c\u4f46\u4e24\u4eba\u8054\u5251\u653b\u5979\uff0c\u51b0\u5ddd \u5929\u5973\u5374\u8981\u7a0d\u7a0d\u5403\u4e8f\uff0c\u5e78\u800c\u51b0\u5ddd\u5929\u5973\u66fe\u89c1\u8fc7\u674e\u6c81\u6885\u6240\u4f7f\u7684\u5947\u8be1\u5251\u6cd5\uff0c\u77e5\u6240\u5e94\u4ed8\uff0c\u66f4\u517c\u5728\u96ea\u5730\u4e4b \u4e0a\u6597\u5251\uff0c\u51b0\u5ddd\u5929\u5973\u6700\u662f\u64c5\u957f\uff0c\u56e0\u6b64\u5728\u4e8c\u4e09\u5341\u62db\u4e4b\u5185\uff0c\u51b0\u5ddd\u5929\u5973\u8fd8\u662f\u653b\u591a\u4e8e\u5b88\uff0c\u674e\u9752\u83b2\u548c\u949f\u5c55 \u6697\u6697\u5403\u60ca\uff0c\u5404\u547c\u60ed\u6127\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6b66\u5bb6\u5144\u5f1f\u5403\u4e86\u5927\u4e8f\uff0c\u8fd9\u5996\u5973\u679c\u7136\u5389\u5bb3\uff0c\u7adf\u80fd\u72ec\u6321 \u5929\u5c71\u4e24\u6d3e\u7684\u5251\u6cd5\u3002\u201d\u51b0\u5ddd\u5929\u5973\u4e5f\u662f\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff0c\u5929\u5c71\u5f1f\u5b50\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u8fde\u4e24\u4e2a\u540e\u751f\u5c0f \u8f88\u4e5f\u6709\u8fd9\u4e48\u9ad8\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u53cc\u65b9\u90fd\u611f\u5230\u654c\u4eba\u96be\u4ee5\u5e94\u4ed8\uff0c\u6b63\u81ea\u6597\u5f97\u7d27\u5f20\uff0c\u5ffd\u542c\u5f97\u90a3\u4e2d\u5e74\u5987\u4eba\u7684\u58f0\u97f3\uff0c\u81ea\u8fdc\u8fdc\u7684\u5c71\u5934\u4f20 \u4e0b\uff1a\u201c\u83b2\u513f\u5c55\u513f\uff0c\u8ba9\u5979\u4e0b\u5c71\uff0c\u5feb\u5feb\u56de\u6765\u3002\u201d\u51b0\u5ddd\u5929\u5973\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u58f0\u97f3\u660e\u660e\u662f\u5728\u8fdc\u8fdc \u7684\u5c71\u5934\u4f20\u6765\uff0c\u5c45\u7136\u50cf\u5728\u8033\u8fb9\u547c\u5524\u4e00\u822c\uff0c\u8fd9\u8fd8\u7f62\u4e86\uff0c\u53e6\u6709\u4e00\u4e8b\uff0c\u6700\u4ee4\u51b0\u5ddd\u5929\u5973\u6000\u7591\u96be\u91ca\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u660e\u660e\u5c31\u5728\u5c71\u5761\u4e4b\u4e0a\u5c06\u8840\u795e\u5b50\u620f\u5f04\uff0c\u4f55\u4ee5\u58f0\u97f3\u5374\u4f3c\u4ece\u9a7c\u5cf0\u4e0a\u4f20\u6765\uff1f\u51b0\u5ddd\u5929\u5973\u4e0d \u77e5\uff0c\u8fd9\u53d1\u58f0\u547c\u5524\u7684\u4e43\u662f\u51af\u745b\uff0c\u5c06\u8840\u795e\u5b50\u620f\u5f04\u7684\u5374\u662f\u51af\u7433\u3002 \u3000\u3000\u949f\u5c55\u548c\u674e\u9752\u83b2\u542c\u5230\u5e08\u6bcd\u59e8\u6bcd\u7684\u547d\u4ee4\uff0c\u54ea\u6562\u4e0d\u4f9d\uff0c\u75be\u653b\u4e24\u5251\uff0c\u60f3\u628a\u51b0\u5ddd\u5929\u5973\u8feb\u9000\u51e0\u6b65\uff0c\u5c31 \u7acb\u523b\u8131\u8eab\u5954\u56fa\u9a7c\u5cf0\u3002\u51b0\u5ddd\u5929\u5973\u65e9\u6599\u5230\u4ed6\u4eec\u6709\u6b64\u4e00\u7740\uff0c\u4e5f\u662f\u51b0\u5ddd\u5929\u5973\u5fc3\u9ad8\u6c14\u505a\uff0c\u660e\u77e5\u4ed6\u4eec\u8981\u64a4 \u8d70\uff0c\u5374\u7acb\u610f\u8981\u632b\u6298\u4ed6\u4eec\u4e00\u4e0b\uff0c\u8d81\u7740\u4ed6\u4eec\u53cc\u5251\u8981\u6536\u672a\u6536\u4e4b\u9645\uff0c\u7a81\u7136\u53cd\u524a\u4e24\u5251\uff0c\u9547\u5c55\u5df2\u89c1\u673a\u8f6c\u4e3a \u5b88\u52bf\uff0c\u8fd8\u80fd\u62b5\u6321\uff0c\u674e\u9752\u83b2\u6b63\u91c7\u653b\u52bf\uff0c\u88ab\u5979\u4e00\u7ede\uff0c\u624b\u4e2d\u7684\u957f\u5251\u7adf\u7136\u8131\u624b\u98de\u51fa\uff0c\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c \u6389\u5728\u96ea\u5730\u4e0a\u3002\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u5230\u5e95\u662f\u8c01\u89e3\u5251\u3002\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u53c8\u5df2\u6ed1\u51fa\u5341\u4f59\u4e08\u8fdc \u3002\u674e\u9752\u83b2\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u53ea\u597d\u56de\u5c71\u3002 \u3000\u3000\u5510\u7ecf\u5929\u672c\u610f\u662f\u5c06\u51b0\u5ddd\u5929\u5973\u54c4\u6765\uff0c\u8ba9\u5979\u62dc\u89c1\u81ea\u5df1\u7684\u7236\u6bcd\uff0c\u4e00\u53d9\u4e16\u4ea4\u4e4b\u8c0a\uff0c\u597d\u6d88\u91ca\u524d\u5acc\uff0c\u54ea \u77e5\u5f04\u5de7\u53cd\u62d9\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u628a\u4ed6\u7684\u6bcd\u4eb2\u8bef\u4f5c\u4ed6\u7684\u59e8\u6bcd\uff0c\u7adf\u7136\u51fa\u624b\u6253\u4ed6\u7684\u6bcd\u4eb2\u3002\u5510\u7ecf\u5929\u77e5\u9053\u6bcd\u4eb2 \u7aef\u5e84\u51dd\u91cd\uff0c\u4e0e\u59e8\u6bcd\u7684\u6027\u597d\u620f\u622a\u7136\u4e0d\u540c\uff0c\u4e0d\u7981\u6697\u53eb\u201c\u7cdf\u7cd5\u201d\uff0c\u62c5\u5fc3\u6bcd\u4eb2\u4f1a\u56f0\u6b64\u4e0d\u559c\u6b22\u51b0\u5ddd\u5929\u5973 \u3002\u5c34\u5c2c\u4e4b\u6781\uff0c\u597d\u4e0d\u5bb9\u6613\u6446\u8131\u4e86\u4ed6\u5c0f\u8868\u59b9\u7684\u80e1\u7f20\uff0c\u6025\u6025\u81ea\u540e\u8ffd\u6765\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6b63\u81ea\u6ed1\u96ea\u4e0b\u5c71\uff0c\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u7684\u547c\u5524\u4e4b\u58f0\uff0c\u8d8a\u6765\u8d8a\u8fd1\u3002\u51b0\u5ddd\u5929\u5973\u607c\u6068\u96be\u5e73\uff0c\u6012 \u6c14\u672a\u6d88\uff0c\u5bf9\u5510\u7ecf\u5929\u7684\u547c\u5524\u7406\u4e5f\u4e0d\u7406\uff0c\u5230\u5510\u7ecf\u5929\u76f8\u8ddd\u5341\u4f59\u4e08\u4e86\uff0c\u624d\u56de\u5934\u4e00\u671b\uff0c\u9119\u5937\u4e00\u7b11\uff0c\u54fc\u4e86 \u4e00\u58f0\uff0c\u5510\u7ecf\u5929\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u542c\u6211\u8bf4\u3002\u201d\u51b0\u5ddd\u5929\u5973\u62fe\u8d77\u4e00\u5757\u96ea\u5757\uff0c\u5288\u9762\u5c31\u6253\uff0c\u6124\u7136\u8bf4\u9053 \uff1a\u201c\u6211\u4eca\u65e5\u624d\u77e5\u4f60\u7684\u4e3a\u4eba\uff0c\u6211\u662f\u7ed9\u4f60\u5bfb\u5f00\u5fc3\u7684\u5417\uff1f\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u53c8\u6ed1\u51fa\u5341\u4f59\u4e08\u8fdc\uff0c\u5510\u7ecf\u5929\u53eb \u9053\uff1a\u201c\u4f60\u542c\u6211\u8bf4\u4e86\u518d\u8d70\u4e5f\u4e0d\u8fdf\uff01\u201d\u51b0\u5ddd\u5929\u5973\u53c8\u56de\u5934\u63b7\u4e86\u4e00\u58f0\u96ea\u5757\uff0c\u9053\uff1a\u201c\u8c01\u542c\u4f60\u7684\u8bf4\u8bdd\uff1f\u4f60 \u518d\u4e5f\u4e0d\u8981\u8ddf\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e5f\u662f\u4e2a\u5e26\u6709\u51e0\u5206\u50b2\u6c14\u7684\u5c11\u5e74\u4eba\uff0c\u51b0\u5ddd\u5929\u5973\u5728\u6c14\u5934\u4e0a\u7684\u8bf4\u8bdd\u4ee4\u4ed6\u751a\u662f\u96be\u582a\uff0c\u4ed6\u987f\u7136 \u6b62\u6b65\uff0c\u6b63\u6b32\u53e6\u5916\u60f3\u6cd5\uff0c\u9a7c\u5cf0\u4e0a\u53c8\u7528\u4e0b\u4e86\u4ed6\u6bcd\u4eb2\u7684\u547c\u58f0\uff1a\u201c\u7ecf\u513f\uff0c\u56de\u6765\u3002\u201d\u63a5\u7740\u662f\u4e00\u4e2a\u4e25\u5389\u7684 \u58f0\u97f3\uff1a\u201c\u7ecf\u513f\uff0c\u4e0d\u8bb8\u4f60\u62e6\u622a\u8fd9\u4e2a\u59d1\u5a18\uff01\u201d\u8fd9\u662f\u4ed6\u7236\u4eb2\u5510\u6653\u6f9c\u7684\u5c71\u9876\u4f20\u58f0\u3002\u539f\u6765\u5510\u6653\u6f9c\u592b\u5987\u8d77 \u521d\u672c\u4ee5\u4e3a\u51b0\u5ddd\u5929\u5973\u662f\u513f\u5b50\u65b0\u4ea4\u7684\u53cb\u4eba\uff0c\u5fc3\u4e2d\u867d\u7136\u6709\u4e9b\u4e0d\u6ee1\u4ed6\u64c5\u5e26\u5916\u4eba\u53c2\u52a0\u805a\u4f1a\uff0c\u4f46\u4e5f\u8fd8\u6ca1\u6709 \u4ec0\u4e48\uff0c\u540e\u6765\u89c1\u51b0\u5ddd\u5929\u5973\u83ab\u540d\u5176\u5999\u7684\uff0c\u4e00\u89c1\u9762\u5c31\u7528\u6781\u5389\u5bb3\u7684\u6697\u5668\u5077\u88ad\uff0c\u53c8\u8bef\u4ee5\u4e3a\u5979\u4e0d\u77e5\u662f\u54ea\u4e2a \u90aa\u6d3e\u9ad8\u624b\u7684\u5f1f\u5b50\uff0c\u7279\u5730\u8d81\u6b64\u673a\u4f1a\u6765\u5411\u4ed6\u4eec\u6311\u8845\u7684\uff0c\u56e0\u6b64\u4e00\u8bef\u518d\u8bef\uff0c\u8bef\u4ee5\u4e3a\u6700\u521d\u7684\u60f3\u6cd5\u9519\u4e86\uff1a \u8fd9\u5973\u5b50\u4e0d\u662f\u513f\u5b50\u5e26\u6765\u7684\u53cb\u4eba\uff0c\u8bef\u4ee5\u4e3a\u5510\u7ecf\u5929\u53bb\u8ffd\u5979\u662f\u60f3\u5c06\u5979\u622a\u56de\uff0c\u4ea4\u7ed9\u81ea\u5df1\u5904\u7f5a\u3002\u4ee5\u5510\u6653\u6f9c \u592b\u5987\u7684\u8eab\u4efd\uff0c\u7edd\u4e0d\u80fd\u4e0e\u540e\u8f88\u4e3a\u96be\uff0c\u4f55\u51b5\u51af\u745b\u65e9\u5df2\u7b54\u5e94\u8ba9\u5979\u4e0b\u5c71\uff0c\u6545\u6b64\u5510\u6653\u6f9c\u592b\u5987\u90fd\u5148\u540e\u51fa\u58f0 \u62e6\u963b\u513f\u5b50\u3002\u5510\u7ecf\u5929\u53ea\u597d\u505c\u6b65\u4e0d\u8feb\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u5728\u96ea\u5730\u4e0a\u6ed1\u8d70\u5982\u98de\uff0c\u90a3\u79ef\u96ea\u7684\u5c71\u5761\u524a\u6ed1\u5f02\u5e38 \uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u80cc\u5f71\u5df2\u53ea\u770b\u89c1\u4e00\u4e2a\u9ed1\u70b9\uff0c\u597d\u50cf\u96ea\u5730\u4e0a\u98de\u6eda\u7684\u5f39\u4e38\uff0c\u7728\u4e00\u7728\u773c\u5c31\u6eda\u5230 \u5c71\u8c37\u4e0b\u9762\u53bb\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u7247\u832b\u7136\uff0c\u5fc3\u5934\u90c1\u90c1\uff0c\u8d70\u56de\u9a7c\u5cf0\uff0c\u7ecf\u8fc7\u5c71\u8170\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u51af\u7433\u7b11\u9053\uff1a\u201c\u7ecf\u513f\uff0c\u4f60 \u770b\u6211\u800d\u8fd9\u4e2a\u8001\u7334\u513f\u3002\u77a7\u6e05\u695a\u4e86\uff0c\u8fd9\u4e00\u62db\u4f60\u4e0d\u53ef\u4e0d\u5b66\u3002\u201d\u5c71\u5761\u4e0a\uff0c\u51af\u7433\u6b63\u5728\u6349\u5f04\u8840\u795e\u5b50\uff0c\u5c31\u5982 \u7075\u732b\u620f\u9f20\u4e00\u822c\uff0c\u5ffd\u800c\u5411\u4ed6\u5439\u4e00\u53e3\u51b7\u6c14\uff0c\u5ffd\u800c\u7eca\u4ed6\u8dcc\u4e86\u4e00\u8de4\uff0c\u8840\u795e\u5b50\u66b4\u6012\u5982\u96f7\uff0c\u51ed\u7740\u542c\u98ce\u8fa8\u5668 \u4e4b\u672f\uff0c\u542c\u51fa\u51af\u7433\u6b63\u5728\u80cc\u540e\u5077\u88ad\uff0c\u80cc\u5fc3\u4e00\u649e\uff0c\u547c\u7684\u53cd\u624b\u4e00\u638c\uff0c\u51af\u7433\u4e09\u6307\u4e00\u6263\uff0c\u7528\u732b\u9e70\u6495\u6293\u7684\u7edd \u6280\u6263\u4ed6\u8109\u95e8\uff0c\u8840\u795e\u4e8e\u4e07\u6599\u4e0d\u5230\u5979\u7684\u62db\u6570\u5982\u6b64\u5201\u6bd2\uff0c\u7adf\u7136\u5728\u81ea\u5df1\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u4f38\u6307\u6b3a\u5230\u8ddf\u524d \uff0c\u8109\u95e8\u662f\u4eba\u8eab\u8981\u5bb3\uff0c\u82e5\u88ab\u5979\u6263\u7740\uff0c\u591a\u597d\u6b66\u529f\uff0c\u4ea6\u65e0\u80fd\u4e3a\u529b\uff0c\u6025\u5fd9\u7f29\u624b\uff0c\u5374\u8fd8\u662f\u7ed9\u51af\u7433\u7684\u6307\u5c16 \u8f7b\u8f7b\u5f39\u4e86\u4e00\u4e0b\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u8840\u795e\u5b50\u7684\u624b\u638c\u88ab\u5f39\u5f97\u53cd\u6253\u56de\u6765\uff0c\u5728\u81ea\u5df1\u7684\u9762\u4e0a\u72e0\u72e0\u7684\u6253\u4e86\u4e00 \u8bb0\uff0c\u70ed\u8fa3\u8fa3\u7684\u534a\u8fb9\u9762\u5b54\u767b\u65f6\u80bf\u4e86\u3002\u51af\u7433\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u53eb\u505a\u81ea\u6253\u8033\u5149\uff0c\u597d\u4e0d\u597d\u73a9\uff1f\u201d\u5510\u7ecf\u5929 \u672c\u6765\u90c1\u90c1\u4e0d\u4e50\uff0c\u4e5f\u7981\u4e0d\u4f4f\u54c8\u54c8\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8840\u795e\u5b50\u51e0\u5341\u5e74\u82e6\u7ec3\uff0c\u60f3\u4e0d\u5230\u4e8c\u6b21\u51fa\u5c71\uff0c\u4fbf\u906d\u5982\u6b64\u6298\u8fb1\uff0c\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u5148\u62a4 \u7740\u5168\u8eab\u8981\u5bb3\uff0c\u518d\u8fd0\u8d77\u771f\u6c14\uff0c\u53d1\u52a8\u638c\u5fc3\u7684\u70ed\u529b\uff0c\u72e0\u72e0\u6251\u51fb\u51af\u7433\uff0c\u5510\u7ecf\u5929\u5728\u4e09\u4e08\u4e4b\u5916\uff0c\u4e5f\u89c9\u70ed\u5f97 \u96be\u53d7\u3002\u51af\u7433\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u9b3c\u6837\u513f\u771f\u4ee4\u6211\u8ba8\u538c\uff0c\u8fd9\u5bf9\u72d7\u722a\u5b50\u4e5f\u4f1a\u5192\u6c14\uff0c\u54fc\uff0c\u54fc\uff0c\u4e14\u7ed9 \u70b9\u5389\u5bb3\u8ba9\u4f60\u77a7\u77a7\uff01\u201d\u5ffd\u800c\u8f6c\u5934\u5411\u5510\u7ecf\u5929\u9053\uff1a\u201c\u7ecf\u513f\uff0c\u4f60\u77e5\u9053\u8fd9\u8001\u5996\u602a\u662f\u4ec0\u4e48\u4e1c\u897f\u5417\uff1f\u201d\u5510\u7ecf \u5929\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u662f\u6e05\u5ef7\u7684\u9e70\u5927\u3002\u201d\u51af\u7433\u672c\u610f\u662f\u5c06\u4ed6\u620f\u800d\uff0c\u8981\u5f85\u95ee\u6e05\u695a\u540e\u624d\u51b3\u5b9a\u51fa\u624b\u7684\u8f7b\u91cd\uff0c \u4e00\u542c\u4ed6\u662f\u6e05\u5ef7\u9e70\u72ac\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u5999\u6781\u4e86\uff0c\u597d\uff0c\u4f60\u65e2\u4ed7\u8fd9\u5bf9\u72d7\u722a\u5b50\u6b3a\u4eba\uff0c\u6211\u5c31\u628a\u4f60\u7684\u8fd9 \u5bf9\u72d7\u722a\u5b50\u5207\u4e0b\u6765\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u59e8\u6bcd\uff0c\u5b9d\u5251\u7ed9\u4f60\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u54fc\uff0c\u5207\u8fd9\u5bf9\u72d7\u722a\u5b50\u8981\u4ec0\u4e48 \u5b9d\u5251\uff0c\u4f60\u77a7\u6211\u7684\u3002\u201d \u3000\u3000\u53ea\u89c1\u5979\u7b11\u5f97\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u5934\u4e0a\u7684\u4e24\u4e2a\u8774\u8776\u7ed3\u968f\u98ce\u6447\u52a8\u3002\u51af\u7433\u7a81\u7136\u5c06\u5934\u4e0a\u7684\u8774\u8776\u7ed3\u89e3\u4e0b\uff0c \u90a3\u8774\u8776\u7ed3\u662f\u7528\u5341\u6570\u6839\u5f69\u8272\u7684\u4e1d\u7ebf\u62e7\u6210\u4e00\u80a1\u7ec6\u7ef3\u6346\u7740\u7684\uff0c\u8774\u8776\u7ed3\u4e00\u89e3\uff0c\u90a3\u80a1\u5f69\u7ef3\u6296\u4e86\u5f00\u6765\uff0c\u8f7b \u98d8\u98d8\u7684\u98de\u626c\uff0c\u51af\u7433\u9053\uff1a\u201c\u597d\uff0c\u4f60\u77a7\u6e05\u695a\u4e86\u3002\u201d\u5de6\u53f3\u4e24\u624b\u5404\u6267\u4e00\u80a1\u5f69\u7ef3\uff0c\u5411\u8840\u795e\u5b50\u8eab\u4e0a\u4e00\u62db\uff0c \u5c31\u8981\u7f1a\u4ed6\u7684\u4e24\u624b\u3002\u8840\u795e\u5b50\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5996\u5987\uff0c\u4f60\u6562\u5982\u6b64\u6b3a\u6211\uff1f\u201d\u6a2a\u638c\u5982\u5200\uff0c\u76f4\u4e0a\u76f4\u4e0b\u7684\u4e71 \u524a\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8fd9\u6839\u5f69\u7ef3\uff0c\u5982\u4f55\u7f1a\u5f97\u4f4f\u6211\u3002\u4e0d\u7ed9\u6211\u6307\u7532\u6495\u65ad\uff0c\u4e5f\u5f97\u7ed9\u70ed\u529b\u70e7\u65ad\u3002\u201d\u54ea\u77e5\u8fd9\u5f69 \u7ef3\u98d8\u98d8\u6643\u6643\uff0c\u4e0d\u6bd4\u5bfb\u5e38\u5175\u5668\uff0c\u65e2\u4e0d\u4f1a\u88ab\u654c\u4eba\u6293\u4e2d\uff0c\u53c8\u4e0d\u53d7\u638c\u98ce\u4e4b\u529b\uff0c\u8840\u795e\u5b50\u53ea\u89c1\u773c\u524d\u5f69\u8272\u7f24 \u7eb7\uff0c\u90a3\u4e94\u5f69\u5934\u7ef3\uff0c\u5728\u773c\u524d\u6643\u52a8\uff0c\u4e0d\u89c9\u76ee\u7729\u795e\u8ff7\uff0c\u5fc3\u70e6\u610f\u4e71\uff0c\u5ffd\u542c\u5f97\u51af\u7433\u53eb\u9053\uff1a\u201c\u7740\uff01\u201d\u8840\u795e \u5b50\u4e24\u8fb9\u624b\u8155\u90fd\u7ed9\u5f69\u7ef3\u7f1a\u7740\uff0c\u52d2\u5f97\u4e0d\u80fd\u52a8\u5f39\u3002\u51af\u7433\u6697\u8fd0\u5185\u529b\uff0c\u529b\u900f\u4e1d\u4e1d\uff0c\u628a\u90a3\u80a1\u5f69\u7ef3\u53d8\u5f97\u6709\u5982 \u4e00\u6839\u7f51\u7ebf\uff0c\u4eba\u8089\u6570\u5206\u3002\u5185\u529f\u7ec3\u5230\u6700\u9ad8\u5883\u754c\uff0c\u53ef\u4ee5\u6458\u53f6\u4f24\u4eba\uff0c\u98de\u82b1\u6740\u654c\uff0c\u51af\u7433\u7528\u5934\u7ef3\u6346\u654c\uff0c\u5c31 \u662f\u8fd9\u79cd\u529f\u592b\u3002 \u3000\u3000\u51af\u7433\u6240\u5b66\u7684\u6b66\u529f\u4e4b\u6742\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u8fd9\u4e00\u624b\u529f\u592b\u672c\u6e90\u51fa\u4e8e\u897f\u85cf\u7ea2\u6559\u7684\u201c\u98de\u7ef3\u89e3\u8155\u201d\uff0c\u897f\u85cf \u4eba\u7528\u7ef3\u7d22\u53ef\u64d2\u7280\u725b\uff0c\u7280\u725b\u529b\u5927\uff0c\u7f1a\u5728\u5b83\u8eab\u4e0a\u4efb\u4f55\u90e8\u4efd\uff0c\u7ef3\u7d22\u90fd\u4f1a\u88ab\u5b83\u62c9\u65ad\uff0c\u53ea\u6709\u7f1a\u7740\u5b83\u7684\u524d \u8db3\u8f6f\u8e44\uff0c\u5b83\u624d\u4e0d\u80fd\u53d1\u529b\uff0c\u4e56\u4e56\u9a6f\u670d\u3002\u5f53\u5e74\u7ea2\u6559\u7684\u7956\u5e08\u5580\u5c14\u5df4\u89c1\u897f\u85cf\u4eba\u6d3b\u6349\u7280\u725b\uff0c\u609f\u4e86\u6b64\u7406\uff0c \u521b\u51fa\u201c\u98de\u7ef3\u89e3\u8155\u201d\u7684\u529f\u592b\uff0c\u53ea\u8981\u7528\u8f6f\u7ef3\u7f20\u7740\u654c\u4eba\u7684\u8109\u95e8\uff0c\u90a3\u5c31\u7eb5\u4ee4\u654c\u4eba\u6709\u91d1\u521a\u5927\u529b\uff0c\u4ea6\u81ea\u53d1 \u6325\u4e0d\u51fa\u3002\u51af\u7433\u5c0f\u65f6\u5019\u66fe\u5728\u5f53\u65f6\u7684\u56db\u7687\u5b50\u5141\u5e27\uff08\u5373\u540e\u6765\u7684\u96cd\u6b63\u5e1d\uff09\u5e9c\u4e2d\u5b66\u4f1a\u8fd9\u624b\u529f\u592b\uff0c\u5230\u5979\u5f52 \u9690\u5929\u5c71\uff0c\u53c8\u7ec3\u6210\u4e86\u6b63\u5b97\u7684\u5185\u5bb6\u6c14\u529f\uff0c\u66f4\u628a\u201c\u6458\u53f6\u4f24\u4eba\uff0c\u98de\u82b1\u6740\u654c\u201d\u7684\u5185\u529f\u8fd0\u7528\u4e0a\u4e86\uff0c\u6240\u4ee5\u867d \u7136\u53ea\u662f\u4e00\u6839\u6781\u7ec6\u7684\u5f69\u7ef3\uff0c\u4e5f\u53ef\u5f53\u6210\u94a2\u4e1d\u4f7f\u7528\uff0c\u6bd4\u7ea2\u6559\u7684\u201c\u98de\u7ef3\u89e3\u8155\u201d\u66f4\u8981\u5389\u5bb3\u591a\u4e86\u3002 \u3000\u3000\u8840\u795e\u5b50\u53cc\u624b\u88ab\u7f1a\uff0c\u8109\u95e8\u7ed9\u7ef3\u7f20\u7d27\u7d27\u52d2\u4f4f\uff0c\u8840\u8109\u4e0d\u80fd\u7545\u901a\uff0c\u4e0d\u4f46\u624b\u8155\u75bc\u75db\u3002\u6108\u6765\u6108\u751a\uff0c\u547c \u5438\u4ea6\u89c9\u7d27\u8feb\uff0c\u5185\u529b\u8fd0\u4e0d\u51fa\u6765\uff0c\u4e24\u773c\u7741\u5f97\u5927\u5982\u94dc\u94c3\uff0c\u6655\u7729\u865a\u8f6f\uff0c\u5c31\u5982\u60a3\u4e86\u91cd\u75c5\u4e00\u822c\uff0c\u53eb\u4e5f\u53eb\u4e0d \u51fa\u6765\u3002\u5510\u7ecf\u5929\u89c1\u6b64\u5f62\u72b6\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u7528\u534a\u4e2a\u65f6\u8fb0\uff0c\u8840\u795e\u5b50\u7684\u624b\u638c\u5c31\u7b97\u8fd8\u672a\u7ed9\u52d2\u65ad\uff0c\u4e5f\u8981\u6c14\u7edd \u8eab\u4ea1\u3002\u201d\u5fc3\u4e2d\u6b8a\u89c9\u4e0d\u5fcd\uff0c\u5ffd\u89c1\u4eba\u5f71\u4e00\u6643\uff0c\u5bf9\u9762\u7684\u5c71\u5934\u6709\u4eba\u53eb\u9053\uff1a\u201c\u7433\u59b9\uff0c\u4f60\u8fd9\u73a9\u7b11\u4e5f\u5f00\u5f97\u592a \u8fc7\u4efd\u4e86\uff01\u201d\u5728\u5c71\u5934\u4e0a\u7ad9\u7acb\u7684\u4eba\u6b63\u662f\u5510\u7ecf\u5929\u7684\u7236\u4eb2\u5510\u6653\u6f9c\u3002 \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u8fd9\u4eba\u591a\u53ef\u6076\uff0c\u4ed6\u662f\u6e05\u5ef7\u7684\u9e70\u72ac\u5462\uff01\u201d\u5510\u6653\u6f9c\u770b\u4e0d\u6e05\u695a\uff0c\u6447\u4e86\u6447\u5934\uff0c\u53c8 \u4f20\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4f60\u7684\u5a46\u5a46\uff08\u6b66\u743c\u7476\uff09\u5f53\u5e74\u66fe\u91ca\u653e\u8fc7\u7684\u3002\u96be\u4e3a\u4ed6\u7ec3\u4e86\u51e0\u5341\u5e74\uff0c\u82e5\u975e\u5927\u6076\uff0c \u8fd8\u662f\u9976\u4e86\u4ed6\u5427\u3002\u201d\u51af\u745b\u4e5f\u5728\u9a7c\u5cf0\u4e0a\u4f20\u58f0\u8bf4\u9053\uff1a\u201c\u7433\u59b9\uff0c\u4f60\u600e\u4e48\u8fd8\u50cf\u5c0f\u65f6\u5019\u7684\u4efb\u6027\uff0c\u7528\u8fd9\u6837\u72e0 \u6bd2\u7684\u624b\u6bb5\u3002\u653e\u4e86\u4ed6\u5427\uff0c\u6211\u4e0d\u9ad8\u5174\u89c1\u4ed6\u7684\u795e\u6c14\u3002\u201d\u51af\u7433\u6700\u662f\u656c\u754f\u59d0\u59d0\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u5f69\u7ef3\u6536\u4e86 \uff0c\u9053\uff1a\u201c\u597d\uff0c\u4ee5\u540e\u8fd9\u4eba\u82e5\u4e0e\u7ecf\u513f\u4f5c\u5bf9\uff0c\u6211\u53ef\u4e0d\u7406\u3002\u201d\u8840\u795e\u5b50\u53cc\u624b\u4e00\u677e\uff0c\u6df1\u6df1\u7684\u5438\u4e86\u53e3\u6c14\uff0c\u4e00 \u8dc3\u8dc3\u5f00\u3002\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u53cc\u8155\u5982\u7ed9\u706b\u7ef3\u70d9\u4e86\u4e00\u9053\u5706\u5708\uff0c\u5165\u8089\u6570\u5206\uff0c\u60ca\u9a87\u4e4b\u6781\uff0c\u542c\u5510\u6653\u6f9c\u7684\u79f0 \u547c\uff0c\u77e5\u9053\u8fd9\u5987\u4eba\u662f\u5510\u6653\u6f9c\u7684\u5c0f\u59e8\u51af\u7433\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u51af\u7433\u4f3c\u7b11\u975e\u7b11\u7684\u8fd8\u5728\u51b7\u51b7\u5730\u76ef\u7740\u4ed6\u3002\u8840\u795e \u5b50\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\u3002\u5fc3\u77e5\u5510\u6653\u6f9c\u592b\u5987\u7684\u6b66\u529f\u8fd8\u5728\u51af\u7433\u4e4b\u4e0a\uff0c\u60f3\u8d77\u81ea\u5df1\u4ee5\u524d\u8981\u627e\u5510\u6653\u6f9c\u6bd4\u8bd5\uff0c\u771f \u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u3001\u54fc\u4e5f\u4e0d\u6562\u518d\u54fc\uff0c\u6025\u6025\u4e0b\u5c71\u9003\u8d70\u3002 \u3000\u3000\u5510\u6653\u6f9c\u62db\u624b\u9053\uff0c\u201c\u7ecf\u513f\uff0c\u4f60\u8fc7\u6765\u3002\u201d\u4e0e\u5510\u7ecf\u5929\u56de\u5230\u9a7c\u5cf0\uff0c\u8fdb\u5165\u5f53\u4e2d\u7684\u77f3\u7a9f\uff0c\u8fd9\u4e9b\u77f3\u7a9f\u90fd \u662f\u4e3a\u4e86\u8fd9\u6b21\u805a\u96c6\u800c\u5f00\u8f9f\u7684\u3002\u5f53\u4e2d\u7684\u77f3\u7a9f\u662f\u5510\u6653\u6f9c\u592b\u5987\u6240\u5c45\u3002\u5510\u6653\u6f9c\u5c06\u513f\u5b50\u5e26\u5165\u6d1e\u7a9f\uff0c\u53c8\u5c06\u674e \u6cbb\u51af\u7433\u592b\u5987\u8bf7\u4e86\u8fc7\u6765\uff0c\u8fd9\u624d\u76d8\u95ee\u513f\u5b50\u9053\uff1a\u201c\u7ecf\u513f\uff0c\u9002\u624d\u90a3\u5973\u5b50\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u4f60\u662f\u4e0d\u662f\u8ba4\u8bc6\u5979 \u7684\uff1f\u4e3a\u4f55\u5979\u4e00\u89c1\u9762\u5c31\u7528\u51b0\u5f39\u6253\u4f60\u7684\u6bcd\u4eb2\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5979\u662f\u51b0\u5ddd\u5929\u5973\u2026\u2026\u201d\u5510\u6653\u6f9c\u5df2\u6709\u4e8c \u5341\u5e74\u4e0d\u5728\u6c5f\u6e56\u9053\u4e0a\u884c\u8d70\uff0c\u5947\u9053\uff1a\u201c\u6709\u8fd9\u6837\u53e4\u602a\u7684\u540d\u5b57\u3002\u201d\u51af\u7433\u63d2\u53e3\u7b11\u9053\uff1a\u201c\u5979\u8fd9\u4e00\u6253\u6253\u5f97\u771f \u597d\uff01\u201d\u51af\u745b\u8be7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u59d0\u59d0\u5440\uff0c\u4f60\u505a\u4e86\u6211\u7684\u66ff\u6b7b\u9b3c\u4e86\uff0c\u5979\u672c\u6765\u662f\u8981\u6253\u6211 \u7684\uff01\u201d \u3000\u3000\u51af\u745b\u77e5\u9053\u59b9\u59b9\u7684\u813e\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u4f60\u62db\u60f9\u4e86\u5979\uff0c\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u6211\u89c1\u72b9\u601c\uff0c\u4f60\u5374\u53bb\u4f5c\u5f04 \u5979\uff0c\u771f\u662f\u4e3a\u8001\u4e0d\u5c0a\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u59d0\u59d0\u597d\u504f\u5fc3\uff0c\u65b0\u5ab3\u5987\u672a\u5165\u95e8\uff0c\u5c31\u5148\u5e2e\u5979\u6765\u6570\u8bf4\u6211\u4e86\u3002\u6211\u4e0d \u8fc7\u9017\u5979\u73a9\u73a9\u800c\u5df2\uff0c\u8c01\u6b3a\u8d1f\u5979\u4e86\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u7ecf\u513f\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u59d1\u5a18\u662f\u4f60\u7279\u5730\u5e26\u5979 \u6765\u89c1\u6211\u4eec\u7684\u4e86\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5a18\u522b\u542c\u59e8\u5988\u7684\u80e1\u8bf4\u3002\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u4e0d\u77e5\u4ed6\u4eec\u591a\u4eb2 \u70ed\u5462\uff1f\u201d\u5f53\u4e0b\u5c06\u90a3\u665a\u9047\u5230\u51b0\u5ddd\u5929\u5973\u4e4b\u4e8b\u8bf4\u4e86\uff0c\u53c8\u6307\u7740\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u6562\u8bf4\u4f60\u4e0d\u662f\u7279\u5730\u5e26\u5979\u6765 \u7684\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u7279\u5730\u5e26\u5979\u6765\u7684\uff0c\u53ef\u662f\u4f60\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u5c31 \u662f\u4e0d\u77e5\u5440\uff0c\u77e5\u9053\u4e86\uff0c\u6211\u4eec\u8fd8\u95ee\u4f60\uff1f\u201d\u542f\u7ecf\u5929\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u662f\u53eb\u6211\u4e0b\u5c71\u4e4b\u540e\uff0c\u987a\u4fbf\u5bfb\u8bbf\u6842\u534e \u751f\u4f2f\u4f2f\u7684\u4e0b\u843d\u5417\uff1f\u6842\u534e\u751f\u4f2f\u4f2f\u5df2\u7ecf\u8fc7\u4e16\u4e86\uff0c\u8fd9\u4e2a\u51b0\u5ddd\u5929\u5973\uff0c\u5c31\u662f\u6302\u534e\u751f\u4f2f\u4f2f\u7684\u5973\u513f\uff0c\u5979\u53ef\u4e0d \u662f\u5916\u4eba\uff0c\u4f60\u4e0d\u602a\u6211\u5e26\u5979\u56de\u6765\u53c2\u52a0\u8fd9\u6b21\u7684\u805a\u96c6\u5427\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u6025\u95ee\u5176\u8be6\uff1b\u5510\u7ecf\u5929\u5c06\u4e24\u4e0a\u51b0\u5cf0\uff0c\u9080\u51b0\u5ddd\u5929\u5973\u4fdd\u62a4\u91d1\u672c\u5df2 \u74f6\u7b49\u7b49\u60c5\u4e8b\u8bf4\u51fa\uff0c\u8bf4\u5230\u51b0\u5bab\u7684\u4ed9\u5883\u65f6\uff0c\u4f17\u4eba\u90fd\u500f\u7136\u795e\u5f80\uff0c\u5982\u542c\u795e\u8bdd\u4e00\u822c\u3002\u51af\u745b\u9053\uff0c\u201c\u60f3\u4e0d\u5230 \u6842\u534e\u751f\u5374\u6709\u8fd9\u6837\u7684\u5947\u9047\uff0c\u8fd8\u751f\u4e0b\u4e00\u4e2a\u8fd9\u4e48\u5929\u4ed9\u822c\u7f8e\u4e3d\u7684\u5973\u513f\u3002\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u53eb\u7ecf\u513f \u5c06\u5979\u8ffd\u56de\u6765\uff0c\u8981\u4e0d\u7136\u5c31\u8981\u7ed9\u522b\u4eba\u62a2\u53bb\u4e86\u3002\u201d\u5510\u7ecf\u5929\u4e0d\u7406\u59e8\u5988\u7684\u620f\u6ee4\uff0c\u5bf9\u7236\u4eb2\u9053\uff1a\u201c\u53ea\u662f\u6211\u6709 \u4e00\u4e8b\u672a\u660e\uff0c\u6309\u8bf4\u5979\u672c\u662f\u5929\u5c71\u4e00\u8109\uff0c\u4f55\u4ee5J\u63d0\u5230\u5929\u5c71\u4e4b\u65f6\uff0c\u5979\u603b\u662f\u4e00\u526f\u6f20\u7136\u7684\u795e\u6c14\uff0c\u597d\u50cf\u751a\u4e3a \u89c1\u5916\u3002\u5929\u4e0b\u6b66\u6797\u4eba\u58eb\u6240\u5411\u5f80\u7684\u5929\u5c71\uff0c\u5728\u5979\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u7adf\u4f3c\u662f\u4e00\u4e2a\u8ba8\u538c\u7684\u5730\u65b9\u3002\u201d\u5510\u6653\u8151\u76b1\u76b1 \u7709\u5934\uff0c\u4ea6\u89c9\u5341\u5206\u4e0d\u89e3\uff0c\u51af\u745b\u5fc3\u601d\u7075\u654f\uff0c\u60f3\u4e86\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u7433\u59b9\uff0c\u8fd9\u53c8\u662f\u4f60\u79cd\u7684\u6076\u679c\u3002\u201d\u51af\u7433 \u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u603b\u662f\u628a\u4ec0\u4e48\u8fc7\u9519\u90fd\u63a8\u5230\u6211\u7684\u8eab\u4e0a\uff01\u201d\u6485\u8d77\u5634\u513f\uff0c\u5c31\u50cf\u4e00\u4e2a\u6dd8\u6c14\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u7ecf\u513f\uff0c\u4f60\u542c\u6211\u8bf4\u4e00\u4e2a\u6545\u4e8b\u3002\u7ea6\u4e09\u5341\u5e74\u524d\uff0c\u90a3\u5e74\u7684\u5929\u4e0b\u6697\u5668\u7b2c\u4e00\u9ad8\u624b\u5510\u91d1\u5cf0\u6709 \u4e2a\u5973\u5a7f\uff0c\u53eb\u505a\u738b\u6556\uff0c\u7528\u767d\u7709\u9488\u4f24\u4e86\u4f60\u7684\u59e8\u5988\uff0c\u4f60\u59e8\u5988\u4e00\u6012\uff0c\u5c06\u4ed6\u6740\u4e86\u3002\u5510\u91d1\u5cf0\u5e26\u4e86\u5973\u513f\u6765\u5bfb \u4ec7\uff0c\u90a3\u65f6\u6211\u4f4f\u5728\u5c71\u4e1c\u5927\u4fa0\u6768\u4ef2\u82f1\u7684\u5bb6\u91cc\uff0c\u5510\u5bb6\u7236\u5973\u628a\u6211\u5f53\u4f5c\u4f60\u7684\u59e8\u5988\uff0c\u6211\u52a9\u6768\u5927\u4fa0\u5c06\u4ed6\u4eec\u6740 \u9000\uff0c\u8bef\u4f1a\u66f4\u6df1\u3002\u90a3\u65f6\u6842\u534e\u751f\u662f\u5510\u5bb6\u7684\u597d\u53cb\uff0c\u7b2c\u4e8c\u6b21\u5510\u91d1\u5cf0\u9080\u4e86\u6842\u534e\u751f\u6765\uff0c\u6211\u4eec\u4e0d\u77e5\u9053\u4ed6\u662f\u6842 \u4ef2\u660e\u7684\u513f\u5b50\uff0c\u90a3\u6842\u534e\u751f\u5251\u6cd5\u975e\u5e38\u5389\u5bb3\uff0c\u7adf\u5c06\u6768\u4ef2\u82f1\u7684\u5b9d\u8d1d\u5973\u513f\u8feb\u5f97\u8dcc\u4e0b\u6e56\u4e2d\uff0c\u88ab\u5c71\u6d2a\u5377\u53bb\u3002 \u201d\u8bf4\u5230\u6b64\u5904\uff0c\u671d\u5510\u6653\u6f9c\u7b11\u4e86\u4e00\u7b11\uff0c\u539f\u6765\u6768\u4ef2\u82f1\u7684\u5973\u513f\u6768\u67f3\u9752\u66fe\u662f\u5510\u6653\u6f9c\u7684\u672a\u5a5a\u59bb\uff0c\u540e\u6765\u4e8c\u4eba \u89e3\u7ea6\u4e4b\u540e\uff0c\u5510\u6653\u6f9c\u624d\u4e0e\u51af\u745b\u7ed3\u5a5a\u7684\u3002\u51af\u745b\u7b11\u4e86\u4e00\u7b11\uff0c\u7eed\u9053\uff1a\u201c\u4f60\u7239\u7239\u90a3\u5929\u6070\u5de7\u4e5f\u5728\u90a3\u513f\uff0c\u5927 \u4e3a\u607c\u6012\uff0c\u5c31\u8981\u4e0e\u6842\u534e\u751f\u62fc\u4e2a\u6b7b\u6d3b\uff0c\u540e\u6765\u6211\u4eec\u7528\u5929\u5c71\u5251\u6cd5\u628a\u4ed6\u8feb\u5f97\u4e5f\u51e0\u4e4e\u8dcc\u4e0b\u6e56\u4e2d\uff0c\u9669\u4e27\u6027 \u547d\u3002\u5e78\u5f97\u5415\u56db\u5a18\u53ca\u65f6\u8d76\u5230\uff0c\u8fd9\u624d\u6551\u4e86\u4ed6\u3002\u5176\u540e\u6768\u5bb6\u59d1\u59d1\u6ca1\u6709\u6b7b\uff0c\u4f60\u7239\u7239\u5c06\u8fd9\u4e8b\u4e5f\u5fd8\u6000\u4e86\u3002\u6842 \u534e\u751f\u5374\u4ece\u6b64\u5931\u4e86\u8e2a\uff0c\u5927\u7ea6\u4ed6\u4e00\u751f\u90fd\u8bb0\u7740\u6b64\u4e8b\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u8fd9\u5c31\u602a\u4e0d\u5f97\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6842 \u534e\u751f\u4f2f\u4f2f\u8981\u8fdc\u6e38\u5f02\u56fd\uff0c\u535a\u91c7\u4e2d\u897f\u5251\u6cd5\u4e4b\u957f\uff0c\u53e6\u521b\u65b0\u62db\uff0c\u800c\u51b0\u5ddd\u5929\u5973\u4e5f\u4e00\u518d\u8981\u4e0e\u6211\u6bd4\u8bd5\u5251\u6cd5 \u4e86\u3002\u201d\u5510\u6653\u6f9c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6842\u534e\u751f\u5982\u6b64\u597d\u80dc\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u96be\u5f97\u6842\u534e\u751f\u5982\u6b64\u82e6\u5fc3\u3002\u4ece \u6b64\u4e2d\u534e\u5251\u6d3e\uff0c\u53c8\u589e\u5f02\u5f69\uff0c\u6b66\u5b66\u65e5\u65b0\u53c8\u65e5\u65b0\uff0c\u8fd9\u5c82\u4e0d\u53ef\u559c\u53ef\u8d3a\u3002\u201d\u5510\u6653\u6f9c\u70b9\u4e86\u70b9\u5934\uff0c\u9ed8\u7136\u4e0d \u8bed\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5a18\uff0c\u4f60\u521a\u624d\u6240\u8bf4\u7684\u90a3\u4e2a\u5929\u4e0b\u6697\u5668\u7b2c\u4e00\u9ad8\u624b\u5510\u91d1\u5cf0\uff0c\u662f\u4e0d\u662f\u6392\u884c\u7b2c\u4e8c \uff0c\u4eba\u79f0\u5510\u4e8c\u5148\u751f\uff1f\u201d\u51af\u6f20\u5947\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u5510\u4e8c\u5148\u751f\u6709\u6ca1\u6709\u5ae1\u4f20\u5f1f\u5b50 \uff1f\u201d\u5510\u6653\u6f9c\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u7ecf\u513f\uff0c\u4f60\u8fd9\u6b21\u4e0b\u5c71\uff0c\u9047\u5230\u4ec0\u4e48\u5f02\u4eba\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a \u201c\u6709\u4eba\u6258\u6211\u5c06\u4e00\u4ef6\u4e1c\u897f\u5e26\u56de\u4ea4\u7ed9\u7239\u7239\uff0c\u4ed6\u8bf4\u8fd9\u4ef6\u4e1c\u897f\u672c\u6765\u662f\u6211\u4eec\u5bb6\u91cc\u7684\u3002\u201d\u51af\u745b\u51af\u7433\u542c\u4e86\u90fd \u4e0d\u89c9\u5927\u5947\uff0c\u5510\u6653\u6f9c\u4e24\u773c\u95ea\u95ea\u653e\u5149\uff0c\u9053\uff1a\u201c\u62ff\u7ed9\u6211\u770b\u3002\u201d\u5510\u7ecf\u5929\u5c06\u90a3\u5757\u6c49\u7389\u638f\u4e86\u51fa\u6765\uff0c\u4ea4\u7ed9\u7236 \u4eb2\uff0c\u5510\u6653\u6f9c\u518d\u4e09\u6469\u638c\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u8fc7\u5f80\u7684\u5192\u9669\u7ecf\u5386\uff0c\u4e00\u4e00\u6d8c\u4e0a\u5fc3\u5934\uff0c\u51af\u6f20\u9053\uff1a\u201c\u8fd9\u662f\u8c01 \u4ea4\u7ed9\u4f60\u7684\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5c31\u662f\u798f\u5eb7\u5b89\u7684\u5e55\u5ba2\uff0c\u540d\u53eb\u9f99\u7075\u77eb\u7684\u90a3\u4e2a\u4eba\u3002\u201d\u5510\u6653\u6f9c\u5ffd\u7136\u6447\u4e86\u6447 \u5934\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u59d3\u9f99\u7684\uff1f\u4e0d\uff0c\u85cf\u6709\u6211\u8fd9\u5757\u6c49\u7389\u7684\u4eba\uff0c\u7edd\u4e0d\u80fd\u662f\u4e00\u4e2a\u666e\u901a\u7684\u5e55\u5ba2\uff0c\u4ed6\u7528\u7684\u4e00 \u5b9a\u662f\u4e2a\u5047\u59d3\u540d\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\u3002\u8840\u795e\u5b50\u627e\u4ed6\u6666\u6c14\uff0c\u4e5f\u8bf4\u4ed6\u662f\u4e2a\u66f4\u540d\u6539\u59d3\u3001 \u56fe\u8c0b\u4e0d\u8f68\u7684\u4eba\uff0c\u4f46\u8840\u795e\u5b50\u53ea\u67e5\u5230\u4e86\u4ed6\u662f\u5510\u91d1\u5cf0\u7684\u5f92\u5f1f\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u7684\u771f\u59d3\u540d\u3002\u7239\uff0c\u4ed6\u5230\u5e95\u662f \u8c01\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff0c\u201c\u4ed6\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\uff01\u201d\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u5e74\u7fb9\u5c27\u4e00\u4ee3\u6851\u96c4\uff0c\u5f53\u5e74\u5510\u6653\u6f9c\u592b \u5987\u4e0e\u6c5f\u5357\u4e03\u4fa0\u7b49\u5929\u4e0b\u82f1\u96c4\u3001\u90fd\u628a\u5e74\u7fb9\u5c27\u5f53\u505a\u7b2c\u4e00\u4e2a\u5927\u5bf9\u5934\uff0c\u90a3\u4e9b\u60ca\u5fc3\u52a8\u9b44\u7684\u6545\u4e8b\uff0c\u5510\u7ecf\u5929\u4e0d \u77e5\u542c\u7236\u6bcd\u8bf4\u8fc7\u591a\u5c11\u904d\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\uff0c\u602a\u4e0d\u5f97\u4ed6\u5728\u897f\u85cf\u62c9\u62e2\u571f\u53f8\uff0c\u5bc6\u7ed3\u515a\u7fbd\uff0c\u770b\u6765\u4ed6\u662f \u60f3\u5728\u8fb9\u7761\u53d1\u96be\uff0c\u81ea\u5efa\u7687\u671d\uff0c\u6210\u5219\u53ef\u4e0e\u6e05\u5ef7\u5206\u5ef7\u6297\u793c\uff0c\u8d25\u4ea6\u53ef\u5272\u636e\u4e00\u65b9\u4e86\u3002\u53ea\u662f\u897f\u85cf\u5f62\u52bf\u590d\u6742 \uff0c\u5728\u90a3\u91cc\u4e3e\u4e8b\uff0c\u53ea\u6050\u53cd\u88ab\u5916\u4eba\u4e58\u865a\u800c\u5165\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u513f\u6240\u89c1\u751a\u662f\u3002\u201d\u5f53\u4e0b\u6c89\u541f\u4e0d\u8bed\u3002 \u51af\u7433\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4e00\u5b9a\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5141\u796f\u767b\u4f4d\u4e4b\u540e\uff0c\u6211\u79c1\u5165 \u7687\u5bab\uff0c\u88ab\u54c8\u5e03\u9640\u4e86\u56e0\u7b49\u6240\u64d2\uff0c\u5eb7\u7199\u7687\u5e1d\u7ed9\u6211\u7684\u90a3\u5757\u6c49\u7389\u88ab\u4ed6\u4eec\u641c\u53bb\uff0c\u90a3\u65f6\u5e74\u7fb9\u5c27\u662f\u4ed6\u4eec\u7684\u534a \u4e2a\u4e3b\u5b50\uff0c\u4ed6\u4eec\u6240\u641c\u5f97\u7684\u4e1c\u897f\u65e2\u7136\u4e0d\u5728\u96cd\u6b63\u624b\u4e2d\uff0c\u90a3\u5c31\u5f53\u7136\u662f\u5728\u5e74\u7fb9\u5c27\u7684\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u82e5\u7136\u6b64\u4eba\u771f\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\uff0c\u88ab\u5f53\u4eca\u5929\u5b50\u67e5\u660e\u8eab\u4efd\uff0c\u90a3\u662f\u5fc5\u6b7b\u65e0\u7591\u3002\u4f60\u6551\u4ed6 \u4e0d\u6551\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ed6\u7236\u4eb2\u662f\u6211\u4eec\u7684\u6b7b\u5bf9\u5934\uff0c\u4ed6\u53ef\u4e0d\u662f\u3002\u518d\u8bf4\uff0c\u4ed6\u4e00\u610f\u6297\u6e05\uff0c\u60f3\u5fc5\u8fd8\u628a\u6211 \u4eec\u5f15\u4e3a\u540c\u9053\uff0c\u770b\u4ed6\u53eb\u7ecf\u513f\u5c06\u6c49\u7389\u4ea4\u56de\uff0c\u5176\u4e2d\u5b9e\u6709\u6df1\u610f\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u8fd9\u610f\u601d\u663e\u660e\u4e0d\u8fc7\uff0c\u4ed6\u5b9e \u662f\u60f3\u4e0e\u6211\u4eec\u7ed3\u7eb3\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u6b64\u4eba\uff0c\u73b0\u5728\u63d0\u8d77\u3002\u6211\u8fd8\u6068\u4e4b\u5165\u9aa8\uff0c\u4f46\u613f\u4ed6\u513f\u5b50\u4e0d\u50cf\u4ed6 \u3002\u201d\u5ffd\u7136\u5e7d\u5e7d\u7684\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u51af\u7433\u5e73\u65e5\u7b11\u53e3\u5e38\u5f00\uff0c\u597d\u50cf\u5929\u5730\u4e4b\u95f4\uff0c\u4ece\u65e0\u4e00\u4ef6\u4e8b\u60c5\uff0c\u8db3\u4ee5\u4ee4\u5979\u5fe7\u8651\u3002\u5510\u7ecf\u5929\u8fd8\u662f\u7b2c\u4e00\u6b21 \u89c1\u4ed6\u59e8\u6bcd\u53f9\u6c14\uff0c\u5fc3\u4e2d\u597d\u751f\u8be7\u5f02\u3002\u5510\u7ecf\u5929\u6709\u6240\u4e0d\u77e5\uff0c\u539f\u6765\u4ed6\u59e8\u6bcd\u51af\u7433\u5728\u5e74\u5bb6\u957f\u5927\uff0c\u4e0e\u5e74\u7fb9\u5c27\u66fe \u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u5e74\u7fb9\u5c27\u5bf9\u5979\u6781\u6709\u60c5\u610f\uff0c\u540e\u6765\u51af\u7433\u53d1\u73b0\u4e86\u5e74\u7fb9\u5c27\u51f6\u6b8b\u5351\u52a3\u7684\u771f\u9762\u76ee\uff0c\u8fd9\u624d\u53cd \u8138\u6210\u4ec7\uff0c\u6068\u4e4b\u5165\u9aa8\u3002\u4f46\u5230\u5e95\u6709\u8fc7\u4e00\u6bb5\u6545\u4eba\u60c5\u4efd\uff0c\u800c\u4eca\u5979\u542c\u5f97\u5e74\u7fb9\u5c27\u513f\u5b50\u7684\u4fe1\u606f\uff0c\u6005\u89e6\u5f80\u4e8b\uff0c \u514d\u4e0d\u4e86\u5206\u5916\u5173\u5fc3\u3002 \u3000\u3000\u51af\u745b\u770b\u4e86\u59b9\u59b9\u4e00\u773c\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f46\u613f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\u4e0d\u4f3c\u4ed6\u7684\u7236\u4eb2\u3002\u4f46\u6211\u4eec\u4e0d\u660e\u5e95\u8574\uff0c \u4e5f\u4e0d\u4fbf\u5192\u7136\u76f8\u6551\u3002\u8fd9\u6837\u5427\uff0c\u7ecf\u513f\uff0c\u4f60\u4e0d\u662f\u8981\u5f80\u56db\u5ddd\u5417\uff1f\u987a\u9053\u53ef\u4ee5\u4e00\u8bbf\u5510\u5bb6\uff0c\u544a\u77e5\u4ed6\u4eec\u9f99\u7075\u77eb \u7684\u4e0b\u843d\uff0c\u5510\u5bb6\u662f\u6b66\u6797\u4e16\u65cf\uff0c\u6309\u6c5f\u6e56\u7684\u89c4\u77e9\uff0c\u4e5f\u8be5\u8ba9\u4ed6\u4eec\u4f5c\u4e3b\u3002\u201d\u5510\u7ecf\u5929\u6b63\u6015\u7236\u6bcd\u8981\u5c06\u81ea\u5df1\u7559 \u4e0b\uff0c\u95fb\u8a00\u5927\u559c\uff0c\u51af\u745b\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u6842\u5bb6\u59b9\u59b9\uff08\u6307\u51b0\u5ddd\u5929\u5973\uff09\uff0c\u53ef\u4ee5\u544a\u8bc9\u5979\u8bf4\u6211\u5f88\u559c\u6b22\u5979 \u3002\u4e5f\u53ef\u4ee5\u8bf7\u5192\u4f2f\u4f2f\u529d\u529d\u5979\uff0c\u91ca\u4e86\u524d\u5acc\uff0c\u4e09\u5e74\u4e4b\u540e\uff0c\u518d\u8bf7\u5979\u56de\u6765\u805a\u4f1a\u3002\u201d\u51af\u7433\u5ffd\u7136\u4e00\u672c\u6b63\u7ecf\u7684 \u9053\uff1a\u201c\u7ecf\u513f\uff0c\u6211\u6559\u4f60\u4e00\u4e2a\u5999\u6cd5\uff0c\u4f60\u518d\u627e\u5979\u6bd4\u5251\uff0c\u6545\u610f\u8f93\u7ed9\u5979\u4e00\u62db\u5c31\u884c\u5566\u3002\u201d\u5510\u6653\u6f9c\u6447\u4e86\u6447\u5934 \uff0c\u9053\uff1a\u201c\u4e3a\u8001\u4e0d\u5c0a\uff0c\u4e13\u6559\u5c0f\u8f88\u4f5c\u4f2a\u3002\u201d\u51af\u7433\u715e\u6709\u4ecb\u4e8b\u7684\u8bf4\u4e86\uff0c\u968f\u5373\u81ea\u5df1\u5374\u7981\u4e0d\u4f4f\u54c8\u54c8\u5927\u7b11\u8d77 \u6765\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5510\u7ecf\u5929\u518d\u4e0b\u9a7c\u5cf0\uff0c\u7eed\u5f80\u4e1c\u884c\u3002\u4ed6\u672c\u6765\u7684\u8def\u7ebf\u662f\u81ea\u9655\u4eba\u5ddd\uff0c\u800c\u4eca\u7ed5\u4e86\u4e00\u4e2a\u5f2f\uff0c\u53ea\u80fd \u53d6\u9053\u9752\u6d77\uff0c\u7ecf\u8fc7\u5192\u90fd\u5730\u533a\uff0c\u8fdb\u5165\u5ddd\u897f\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u8def\u63a2\u542c\uff0c\u603b\u63a2\u542c\u4e0d\u51fa\u51b0\u5ddd\u5929\u5973\u7684\u884c\u8e2a\uff0c\u5fc3\u4e2d\u5927\u662f\u6302\u8651\uff0c\u6015\u5979\u4e0d\u8bc6\u9053\u8def\uff0c\u4e0d\u77e5\u649e \u5230\u54ea\u513f\u3002 \u3000\u3000\u8d70\u4e86\u5341\u591a\u5929\uff0c\u8fd9\u65e5\u5df2\u8fdb\u5165\u9752\u6d77\u4e2d\u90e8\u7684\u67f4\u8fbe\u6728\u76c6\u5730\uff0c\u4e00\u5927\u7247\u8349\u539f\uff0c\u83bd\u83bd\u82cd\u82cd\uff0c\u9065\u63a5\u5929\u9645\uff0c \u8349\u539f\u4e0a\u867d\u95f4\u6709\u9ec4\u571f\u6c99\u6f20\uff0c\u4f46\u5927\u90e8\u4efd\u90fd\u662f\u80a5\u6c83\u7684\u9ed1\u571f\uff0c\u843d\u53f6\u6210\u5c42\uff0c\u91ce\u7f8a\u4e00\u7fa4\u7fa4\u5730\u5728\u8349\u539f\u4e0a\u5954\u8d70 \u3002\u5510\u7ecf\u5929\u5728\u5927\u8349\u539f\u4e0a\u7b56\u9a6c\u5954\u9a70\uff0c\u80f8\u895f\u5f00\u9614\uff0c\u8c6a\u5174\u9057\u98de\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u7b49\u8fd9\u4e00\u5927\u7247\u76c6\u5730\uff0c\u82e5\u7136\u5c06 \u4e4b\u5f00\u53d1\uff0c\u4e0d\u77e5\u80fd\u517b\u6d3b\u51e0\u5343\u4e07\u4eba\uff1f\u53ef\u7b11\u53e4\u5728\u4eca\u6765\uff0c\u591a\u5c11\u82f1\u96c4\u8c6a\u6770\uff0c\u4e89\u738b\u4e89\u9738\uff0c\u5f92\u6614\u9ece\u6c11\uff0c\u6709\u8fd9 \u4e48\u4e00\u5927\u7247\u80a5\u6c83\u7684\u8349\u539f\uff0c\u5374\u5343\u4e07\u5e74\u6765\u90fd\u4efb\u4e4b\u8352\u5e9f\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u5728\u6781\u76ee\u9047\u601d\uff0c\u5ffd\u542c\u5f97\u9a7c\u94c3\u6df7\u548c\u9a6c\u94c3\uff0c\u4e00\u961f\u65c5\u4eba\u8fce\u9762\u800c\u6765\uff0c\u6709\u7537\u6709\u5973\uff0c\u6709\u8001\u6709\u5e7c \u3002\u5510\u7ecf\u5929\u9887\u4e3a\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u73b0\u5728\u5df1\u662f\u5f00\u6625\u65f6\u5206\uff0c\u53ea\u6709\u5317\u65b9\u7684\u4eba\u5f80\u5357\u65b9\uff0c\u4f55\u4ee5\u8fd9\u961f\u65c5\u4eba\u5374\u4ece\u5357 \u8fb9\u6765\uff1f\u4e0a\u524d\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u4e9b\u65c5\u4eba\u90fd\u9762\u6709\u4ed3\u7687\u4e4b\u8272\uff0c\u597d\u50cf\u4e00\u7fa4\u9003\u72af\uff0c\u4ec6\u4ec6\u98ce\u5c18\u3002 \u3000\u3000\u5510\u7ecf\u5929\u597d\u5947\u5fc3\u8d77\uff0c\u4e0a\u524d\u4fbf\u95ee\uff0c\u961f\u4e2d\u7684\u4e00\u4e2a\u8001\u8005\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c31\u53ea\u4f60\u5355\u8eab\u4e00\u4eba\u5417\uff1f \u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u662f\u5440\u3002\u8bf7\u95ee\u8001\u4f2f\u4f55\u4ee5\u8981\u79bb\u5f00\u5357\u8fb9\u8fd9\u6c34\u8349\u4e30\u9976\u4e4b\u5730\u3001\u662f\u8981\u5230\u897f\u85cf\u7ecf\u5546\u7684\u5417\uff1f\u201d \u90a3\u8001\u8005\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u53ea\u4f60\u5355\u8eab\u4e00\u4eba\uff0c\u90a3\u5012\u65e0\u751a\u5fe7\u8651\uff0c\u4f60\u53ef\u7ee7\u7eed\u8d76\u8def\uff1a\u518d\u8d70\u4e24\u5929\uff0c\u5c31\u662f\u5410 \u8c37\u6d51\u6c57\u738b\u6cbb\u4e0b\u7684\u5927\u57ce\u54c8\u5409\u5c14\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u5947\u9053\uff1a\u201c\u4e3a\u4f55\u5355\u8eab\u4e00\u4eba\uff0c\u4fbf\u65e0\u5fe7\u8651\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u767d\u6559\u5587\u561b\u7684\u6cd5\u738b\u4e0d\u77e5\u4e3a\u4ec0\u4e48 \u8981\u6311\u9009\u79c0\u5973\uff0c\u4e13\u6349\u5e74\u9752\u7684\u5973\u5b50\uff0c\u5916\u5730\u6765\u7684\u5973\u5ba2\uff0c\u53ea\u8981\u76f8\u8c8c\u5a1f\u79c0\uff0c\u4e00\u7ed9\u90a3\u4e9b\u5587\u561b\u53d1\u73b0\uff0c\u4fbf\u62d6\u4e86 \u53bb\u3002\u5f04\u5f97\u57ce\u4e2d\u98ce\u58f0\u9e64\u552c\uff0c\u6211\u4eec\u7ecf\u8fc7\u90a3\u513f\uff0c\u4e0d\u6562\u505c\u7559\uff0c\u9a6c\u4e0a\u4fbf\u8d70\u3002\u542c\u8bf4\u524d\u5929\u8fd8\u6709\u4e00\u4e2a\u4f1a\u6b66\u529f\u7684 \u5e74\u9752\u7f8e\u8c8c\u7684\u5355\u8eab\u5973\u5ba2\u88ab\u4ed6\u4eec\u6349\u53bb\u4e86\u5462\uff01\u201d\u5510\u7ecf\u5929\u542c\u4e86\uff0c\u5927\u4e3a\u5947\u602a\uff0c\u9053\uff1a\u201c\u767d\u6559\u5587\u561b\u7684\u6cd5\u738b\u53c8 \u4e0d\u662f\u7687\u5e1d\uff0c\u4e3a\u4f55\u8981\u6311\u9009\u79c0\u5973\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u5440\u3002\u6709\u4eba\u8bf4\u662f\u8981\u62ff\u53bb\u732e\u7ed9\u795e\u7684\uff0c \u90a3\u5c31\u66f4\u53ef\u6015\u4e86\u3002\u4e0d\u8fc7\u597d\u5728\u4ed6\u4eec\u53ea\u6349\u5973\u7684\uff0c\u4e0d\u6349\u7537\u7684\uff0c\u6240\u4ee5\u4f60\u5012\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201d\u5510\u7ecf\u5929\u76b1\u4e86\u76b1\u7709 \uff0c\u5fc3\u9053\uff1a\u201c\u767d\u6559\u5587\u561b\u7684\u6cd5\u738b\u4e43\u662f\u4e00\u6d3e\u4e4b\u5c0a\uff0c\u90fd\u662f\u8bf4\u8981\u62a4\u6301\u4f5b\u6cd5\u7684\uff0c\u4f55\u4ee5\u5982\u6b64\u80e1\u4e3a\u3002\u800c\u4e14\u5587\u561b \u6559\u4e0d\u6bd4\u5176\u4ed6\u90aa\u6559\uff0c\u4e5f\u662f\u4f5b\u95e8\u7684\u4e00\u4e2a\u522b\u6d3e\uff0c\u4ece\u6765\u672a\u542c\u8bf4\u8fc7\u5587\u561b\u6559\u8981\u7ae5\u7537\u7ae5\u5973\u796d\u795e\u7684\uff0c\u8fd9\u7a76\u7adf\u662f \u600e\u4e48\u56de\u6765\uff1f\u6211\u672c\u6765\u4e0d\u60f3\u5230\u54c8\u5409\u5c14\uff0c\u73b0\u5728\u5374\u662f\u975e\u53bb\u4e0d\u53ef\u4e86\u3002\u201d\u5f53\u4e0b\u522b\u8fc7\u90a3\u961f\u65c5\u4eba\uff0c\u7acb\u5373\u8d76\u8def\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9a6c\u884c\u5feb\u75be\uff0c\u7b2c\u4e8c\u65e5\u4e2d\u5348\uff0c\u4fbf\u5230\u4e86\u54c8\u5409\u5c14\u57ce\uff0c\u54c8\u5409\u5c14\u5728\u67f4\u8fbe\u6728\u76c6\u5730\u7684\u8fb9\u7f18\uff0c\u7b97\u5f97\u662f \u4e2a\u5927\u57ce\uff0c\u4f46\u6bd4\u4e4b\u4e2d\u539f\u7684\u57ce\u5e02\u5374\u76f8\u5dee\u751a\u8fdc\uff0c\u57ce\u4e2d\u4eba\u53e3\uff0c\u4e0d\u6ee1\u4e00\u4e07\uff0c\u53ea\u6709\u51e0\u6761\u8857\u9053\uff0c\u9664\u4e86\u9152\u697c\u5ba2 \u5e97\u4e4b\u5916\uff0c\u666e\u901a\u6c11\u5c45\uff0c\u5bb6\u5bb6\u95ed\u6237\uff0c\u66f4\u4ee4\u4eba\u6709\u8427\u6761\u4e4b\u611f\u3002\u5510\u7ecf\u5929\u62e3\u4e86\u4e00\u5bb6\u5ba2\u5e97\uff0c\u5b89\u7f6e\u597d\u9a6c\u5339\u4e4b\u540e \uff0c\u4fbf\u5c06\u5e97\u5c0f\u4e8c\u5524\u6765\uff0c\u547d\u4ed6\u6253\u9152\uff0c\u5e76\u91cd\u91cd\u7684\u8d4f\u4e86\u4ed6\u4e00\u7b14\u5c0f\u5e10\uff0c\u90a3\u5e97\u5c0f\u4e8c\u751a\u662f\u6b22\u559c\uff0c\u548c\u5510\u7ecf\u5929\u7f20 \u4e03\u5939\u516b\u7684\u95f2\u804a\u3002 \u3000\u3000\u5510\u7ecf\u5929\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u8fd9\u91cc\u7684\u6cd5\u738b\u8981\u6311\u9009\u79c0\u5973\uff0c\u6709\u8fd9\u4e8b\u5417\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff0c\u201c\u6709\u5440\u3002\u4f60 \u4e0d\u89c1\u90a3\u4e9b\u6c11\u5c45\u90fd\u95ed\u4e86\u95e8\u6237\uff0c\u5e74\u9752\u7684\u5973\u5b69\u5b50\u90fd\u4e0d\u6562\u51fa\u6765\u5417\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\uff0c\u542c\u8bf4\u4ed6\u4eec \u4e5f\u5df2\u6311\u9009\u591f\u4e86\uff0c\u4eca\u5929\u5df2\u7ecf\u6ca1\u6709\u5587\u561b\u641c\u6349\u5973\u5b50\u7684\u4e8b\u60c5\u53d1\u751f\u4e86\u3002\u201d\u5510\u7ecf\u592d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u6311\u9009\u79c0 \u5973\uff1f\u662f\u796d\u795e\u5417\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6cd5\u738b\u7684\u547d\u4ee4\uff0c\u8c01\u6562\u53bb\u5dee\u522b\uff1f\u53ea\u542c\u8bf4\u4ece\u897f\u85cf\u6765\u4e86\u4e00\u4e2a\u5927\u5587\u561b\uff0c \u6cd5\u738b\u8981\u62db\u5f85\u4ed6\uff0c\u518d\u8fc7\u4e24\u5929\uff0c\u5c31\u8981\u5f00\u4e00\u4e2a\u76db\u5927\u7684\u6cd5\u4f1a\uff0c\u662f\u4e0d\u662f\u796d\u795e\uff0c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u5510\u7ecf\u5929 \u542c\u4e86\uff0c\u66f4\u4e3a\u5947\u602a\uff0c\u987b\u77e5\u767d\u6559\u5587\u561b\u662f\u7ed9\u73b0\u5728\u897f\u85cf\u5f53\u6743\u7684\u9ec4\u6559\u5587\u561b\uff0c\u5728\u660e\u672b\u5d07\u677f\u5e74\u95ee\uff0c\u9a71\u9010\u51fa\u897f \u85cf\u5883\u5916\u7684\uff0c\u767e\u591a\u5e74\u6765\uff0c\u4e24\u6559\u5982\u540c\u6c34\u706b\uff0c\u4e92\u76f8\u4ec7\u89c6\uff0c\u600e\u4e48\u4ece\u897f\u85cf\u6765\u7684\u9ec4\u6559\u5927\u5587\u561b\uff0c\u8fd9\u513f\u7684\u767d\u6559 \u6cd5\u738b\u53cd\u800c\u4f1a\u9686\u91cd\u62db\u5f85\uff1f \u3000\u3000\u5e97\u5c0f\u4e8c\u53c8\u9053\uff1a\u201c\u597d\u5728\u4f60\u662f\u5355\u8eab\u7537\u5ba2\uff0c\u82e5\u662f\u5973\u7684\uff0c\u6349\u4e86\u53bb\u8fde\u5bb6\u4eba\u4e5f\u4e0d\u77e5\u9053\u3002\u524d\u4e24\u5929\u5c31\u6709\u4e00 \u4e2a\u5916\u6765\u7684\u5973\u5b50\u88ab\u5587\u561b\u6349\u53bb\uff0c\u5979\u8fd8\u4f1a\u6b66\u529f\u5462\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u77e5\u5979\u4f1a\u6b66\u529f\uff1f \u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5c31\u5728\u6211\u4eec\u5bf9\u9762\u7684\u8fd9\u5bb6\u9152\u5e97\u6349\u53bb\u7684\uff0c\u6211\u8fd8\u53bb\u77a7\u4e86\u70ed\u95f9\u6765\u5462\uff1f\u90a3\u5973\u5b50\u7684\u670d\u9970\u50cf\u662f \u4ece\u897f\u85cf\u6765\u7684\uff0c\u4e0d\u4f46\u4f1a\u6b66\u529f\uff0c\u8fd8\u4f1a\u5996\u6cd5\uff01\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u5149\u5927\u5316\u65e5\u4e4b\u4e0b\uff0c\u6709\u4ec0\u4e48\u5996\u6cd5\uff01 \u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u5417\uff1f\u6211\u4eb2\u773c\u89c1\u7684\u3002\u8d77\u521d\u6709\u56db\u4e2a\u5c0f\u5587\u561b\u6349\u5979\uff0c\u5979\u4e00\u62f3\u4e00\u811a\u5c31\u6253\u7ffb\u4e86\u4e24 \u4e2a\uff0c\u8fd8\u6709\u4e24\u4e2a\uff0c\u53ea\u89c1\u5979\u628a\u624b\u4e00\u626c\uff0c\u5c31\u6709\u4e00\u56e2\u767d\u832b\u832b\u7684\u51b7\u6c14\u5c04\u51fa\u6765\uff0c\u90a3\u4e24\u4e2a\u5c0f\u5587\u561b\u767b\u65f6\u5927\u6253\u51b7 \u6218\uff01\u4f60\u8bf4\u662f\u4e0d\u662f\u5996\u6cd5\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u6697\u5668\u5206\u660e\u662f\u51b0\u9b44\u795e\u5f39\uff0c\u51b0\u5ddd\u5929\u5973\u7edd\u4e0d\u4f1a\u88ab\u5587\u561b\u6349\u53bb\uff0c\u96be\u9053\u88ab\u6349\u7684\u7adf \u662f\u5979\u7684\u4f8d\u5973\u5e7d\u840d\uff0c\u53ea\u542c\u5f97\u90a3\u5e97\u5c0f\u4e8c\u53c8\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u5996\u6cd5\u5389\u4e0d\u5389\u5bb3\uff1f\u4f46\u5996\u6cd5\u7a76\u7adf\u6bd4\u4e0d\u4e0a\u4f5b\u6cd5\uff0c \u90a3\u56db\u4e2a\u5c0f\u5587\u561b\u88ab\u6253\u5012\u540e\uff0c\u53c8\u6765\u4e86\u4e24\u4e2a\u5927\u5587\u561b\uff0c\u4ed6\u4eec\u4e0d\u6015\u5996\u6cd5\uff0c\u90a3\u5973\u5b50\u53d1\u51fa\u7684\u5bd2\u5149\u51b7\u6c14\uff0c\u4e24\u4e2a \u5927\u5587\u561b\u53ea\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff0c\u7acb\u5373\u5c31\u4f38\u624b\u628a\u5979\u6349\u4e86\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u767d\u6559\u6cd5\u738b\u624b \u4e0b\uff0c\u5012\u5f88\u6709\u51e0\u4e2a\u80fd\u4eba\u3002\u5e7d\u840d\u88ab\u6349\uff0c\u51b0\u5ddd\u5929\u5973\u5fc5\u7136\u4e0d\u80af\u5e72\u4f11\uff0c\u771f\u60f3\u4e0d\u5230\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765 \u5168\u4e0d\u8d39\u5de5\u592b\uff0c\u6211\u53ea\u5728\u8fd9\u91cc\u7b49\u5979\u4fbf\u4e86\u3002\u201d\u5f53\u4e0b\u5411\u5e97\u5c0f\u4e8c\u63a2\u95ee\u5587\u561b\u5bfa\u9662\u7684\u6240\u5728\uff0c\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5ba2 \u5b98\u4e5f\u60f3\u53bb\u8fdb\u9999\u5417\uff1f\u90a3\u5bfa\u9662\u5e73\u65e5\u70ed\u95f9\u975e\u5e38\uff0c\u8fd9\u51e0\u5929\u6050\u6015\u6ca1\u6709\u4ec0\u4e48\u4eba\u53bb\u4e86\u3002\u4f46\u4f60\u662f\u5916\u6765\u9999\u5ba2\uff0c\u53bb \u4e5f\u4e0d\u59a8\u3002\u90a3\u5587\u561b\u5bfa\u5e99\u662f\u6211\u4eec\u8fd9\u91cc\u6700\u5927\u7684\u5efa\u7b51\uff0c\u4f60\u65e2\u5230\u8fd9\u513f\uff0c\u53bb\u77bb\u4ef0\u4e00\u756a\uff0c\u4e5f\u662f\u5e94\u5f53\u3002\u201d\u5510\u7ecf \u5929\u95ee\u660e\u4e86\u5730\u5740\uff0c\u5c0f\u7761\u7247\u523b\uff0c\u5403\u8fc7\u5348\u996d\uff0c\u4fbf\u5230\u767d\u6559\u5587\u561b\u5927\u5bfa\u53bb\u3002 \u3000\u3000\u8fd9\u5ea7\u5587\u561b\u5bfa\u9662\uff0c\u6bd4\u8d77\u62c9\u8428\u7684\u5e03\u8fbe\u62c9\u5b98\uff0c\u90a3\u81ea\u662f\u8fdc\u8fdc\u4e0d\u5982\uff0c\u4f46\u4ea6\u751a\u4e3a\u96c4\u4f1f\uff0c\u51e0\u5341\u5ea7\u5927\u5927\u5c0f \u5c0f\u7684\u6bbf\u5b57\uff0c\u5728\u534a\u5c71\u4e0a\u6bd7\u8fde\u800c\u8d77\uff0c\u91d1\u78a7\u8f89\u714c\uff0c\u5916\u9762\u4e09\u5ea7\u5927\u6bbf\u4f9b\u7740\u8bf8\u822c\u4f5b\u50cf\uff0c\u4efb\u4eba\u53c2\u62dc\uff0c\u9999\u5ba2\u867d \u7136\u4e0d\u5f88\u62e5\u6324\uff0c\u4f46\u4ea6\u7edc\u7ece\u4e0d\u7edd\u3002\u5510\u7ecf\u5929\u6742\u5728\u9999\u5ba2\u4e4b\u4e2d\uff0c\u542c\u4ed6\u4eec\u8c08\u8bba\uff0c\u4ed6\u4eec\u5bf9\u524d\u51e0\u65e5\u7684\u641c\u6349\u5c11\u5e74 \u5973\u5b50\u4e4b\u4e8b\uff0c\u867d\u7136\u8bae\u8bba\u7eb7\u7eb7\uff0c\u4f46\u5bf9\u90a3\u767d\u6559\u6cd5\u738b\uff0c\u5374\u662f\u5341\u5206\u5c0a\u656c\uff0c\u6709\u7684\u8fd8\u8bf4\uff0c\u6d3b\u4f5b\u8981\u8fd9\u6837\u505a\uff0c\u5fc5 \u5b9a\u6709\u4ed6\u7684\u7eab\uff0c\u90a3\u4e9b\u5973\u5b50\uff0c\u5f97\u6cbe\u6cb8\u6cfd\uff0c\u6b63\u662f\u5979\u4eec\u7684\u798f\u6c14\uff0c\u6211\u4eec\u5984\u81ea\u8c08\u8bba\uff0c\u4e0d\u6015\u5815\u5165\u62d4\u820c\u5730\u72f1\u5417 \uff1f\u770b\u4ed6\u4eec\u5bf9\u6d3b\u4f5b\u72c2\u70ed\u5d07\u62dc\u7684\u60c5\u5f62\uff0c\u7adf\u4e0d\u5728\u897f\u85cf\u7684\u5587\u561b\u6559\u4fe1\u5f92\u4e4b\u4e0b\u3002\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u7ecf\u8fc7\u4e86\u8fd9 \u4e00\u573a\u4e8b\u60c5\uff0c\u8fd8\u6709\u8fd9\u4e48\u591a\u5584\u7537\u4fe1\u58eb\u524d\u6765\u8fdb\u9999\uff0c\u770b\u6765\u8fd9\u767d\u6559\u6cd5\u738b\uff0c\u4e5f\u81ea\u6709\u5f97\u4eba\u5c0a\u656c\u4e4b\u5904\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u770b\u6e05\u695a\u4e86\u767d\u6559\u5587\u561b\u5bfa\u7684\u5f62\u52bf\uff0c\u56de\u5230\u5ba2\u5e97\uff0c\u7761\u4e86\u4e00\u89c9\uff0c\u4e09\u66f4\u65f6\u5206\uff0c\u6362\u4e86\u9ed1\u8272\u7684\u591c\u884c \u8863\u670d\uff0c\u8499\u4e0a\u9762\u4e2d\uff0c\u6084\u6084\u79bb\u5f00\u5ba2\u5e97\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u4fbf\u5230\u5587\u561b\u5bfa\u53bb\uff0c\u60f3\u63a2\u4e2a\u6c34\u843d\u77f3\u51fa\u3002 \u3000\u3000\u5bfa\u9662\u89c4\u6a21\u751a\u5927\uff0c\u4e5f\u4e0d\u77e5\u54ea\u91cc\u662f\u6cd5\u738b\u7684\u5b9d\u6bbf\uff0c\u5510\u7ecf\u5929\u9009\u5f53\u4e2d\u7684\u4e00\u5ea7\u6bbf\u5b57\u98de\u8eab\u63a0\u8fdb\uff0c\u53ea\u89c1\u9662 \u843d\u6c89\u6c89\uff0c\u5185\u4e2d\u9690\u9690\u6709\u7b19\u6b4c\u594f\u4e50\u4e4b\u58f0\uff0c\u5510\u7ecf\u5929\u76b1\u76b1\u7709\u5934\uff0c\u8df3\u8fdb\u91cc\u9762\uff0c\u5ffd\u89c1\u4e24\u4e2a\u5c0f\u5587\u561b\u8fce\u9762\u884c\u91c9 \u5510\u7ecf\u5929\u9690\u8eab\u4e00\u68f5\u83e9\u63d0\u6811\u540e\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5c0f\u5587\u561b\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u91cc\u4e5f\u6709\u5723\u5973\u4e86\uff0c\u5979\u4eec\u5ff5\u7ecf\u5531\u4f5b\u66f2 \uff0c\u5531\u5f97\u771f\u597d\u542c\u3001\u542c\u8bf4\u8fd8\u8981\u7ec3\u821e\u5462\uff0c\u4ece\u4eca\u4ee5\u540e\uff0c\u53ef\u70ed\u95f9\u4e86\u3002\u201d\u53e6\u4e0a\u4e2a\u5c0f\u5587\u561b\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u9b3c\u5934 \u4f11\u8981\u52a8\u4e86\u51e1\u5fc3\uff0c\u591a\u77a7\u5979\u4eec\u4e00\u773c\u4e5f\u6709\u7f6a\uff0c\u72af\u4e86\u6212\u5f8b\uff0c\u53ef\u4e0d\u662f\u5f53\u800d\u7684\u3002\u201d\u90a3\u5c0f\u5587\u561b\u9053\uff1a\u201c\u4f60\u4f11\u5f97 \u80e1\u8bf4\uff0c\u4f60\u624d\u52a8\u4e86\u51e1\u5fc3\u5462\uff01\u6211\u53ea\u662f\u8fdc\u8fdc\u7684\u542c\uff0c\u4f60\u5374\u4e09\u6b21\u4ece\u5723\u5973\u7684\u5bab\u524d\u8d70\u8fc7\u3002\u201d\u5510\u7ecf\u5929\u4e00\u8dc3\u800c\u51fa \uff0c\u53cc\u81c2\u4e00\u4f38\uff0c\u5c06\u4e24\u4e2a\u5c0f\u5587\u561b\u62ff\u7740\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u6211\u95ee\u4e00\u53e5\u4f60\u4eec\u7b54\u4e00\u53e5\uff0c\u82e5\u6562\u53eb\u56b7\uff0c\u5c31\u6740\u4e86\u4f60 \uff01\u201d\u4ed6\u7528\u7684\u662f\u5c0f\u64d2\u62ff\u7684\u624b\u6cd5\uff0c\u6263\u7740\u4e24\u4e2a\u5587\u561b\u7684\u624b\u8155\u5173\u8282\uff0c\u53eb\u4f60\u4eec\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u5587\u561b\u60ca\u5f97\u5446\u4e86\uff0c\u5510\u7ecf\u5929\u95ee\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u5723\u5973\uff1f\u662f\u524d\u51e0\u5929\u6349\u6765\u7684\u90a3\u4e9b\u5973\u5b50\u5417\uff1f\u201d \u4e24\u4e2a\u5c0f\u5587\u561b\u70b9\u4e86\u70b9\u5934\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5979\u4eec\u5173\u5728\u90a3\u513f\uff1f\u201d\u5c0f\u5587\u561b\u9053\uff1a\u201c\u5979\u4eec\u4f4f\u5728\u9760\u8fd1\u6cd5\u738b\u5b9d\u6bbf \u7684\u90a3\u5ea7\u5723\u5973\u5bab\u91cc\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u4eec\u4f5b\u95e8\u5f1f\u5b50\uff0c\u628a\u5e74\u9752\u5973\u5b50\u6349\u8fdb\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5c0f\u5587\u561b\u9053\uff1a \u201c\u8fd9\u662f\u5979\u4eec\u7684\u798f\u6c14\uff0c\u6cd5\u738b\u8981\u5979\u4eec\u505a\u7b2c\u4e00\u6279\u5723\u5973\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8981\u5723\u5973\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u5c0f\u5587\u561b \u9732\u51fa\u5947\u602a\u7684\u795e\u6c14\uff0c\u597d\u50cf\u5632\u7b11\u5510\u7ecf\u5929\u7684\u65e0\u77e5\uff0c\u9053\uff1a\u201c\u7537\u7684\u5f53\u5587\u561b\uff0c\u5973\u7684\u5f53\u5723\u5973\uff0c\u90a3\u662f\u7ecf\u6587\u4e0a\u4e5f \u6709\u8bf4\u7684\uff0c\u4f60\u95ee\u5f97\u597d\u5947\u602a\uff01\u201d\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff0c\u8fd9\u624d\u60f3\u8d77\u5728\u5587\u561b\u6559\u7684\u51e0\u79cd\u6d3e\u522b\u4e2d\uff0c\u7ea2\u6559\u9ec4\u6559\u90fd \u4e0d\u6536\u5973\u7684\uff0c\u53ea\u6709\u767d\u6559\uff0c\u636e\u7236\u8001\u4f20\u8a00\uff0c\u53ef\u4ee5\u6536\u5973\u7684\u4fe1\u5f92\u3002\u53ea\u56e0\u767d\u6559\u5728\u767e\u591a\u5e74\u524d\u5c31\u88ab\u9010\u51fa\u897f\u85cf\uff0c \u6240\u4ee5\u8fd9\u6559\u73b0\u5728\u897f\u85cf\u5df2\u5f88\u5c11\u4eba\u8c08\u8bba\uff0c\u8fde\u5510\u7ecf\u5929\u4e00\u65f6\u4e5f\u60f3\u4e0d\u8d77\u6765\uff0c\u539f\u6765\u5723\u5973\u5c31\u662f\u5973\u5587\u561b\u7684\u610f\u601d\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u4e2d\u7a0d\u5bbd\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u6ca1\u6709\u5165\u9a9a\u6270\u5979\u4eec\u5427\uff1f\u201d\u5c0f\u5587\u561b\u867d\u7136\u5728\u5510\u7ecf\u5929\u624b\u638c\u4e4b\u4e2d\uff0c\u4e5f \u9732\u51fa\u6120\u6012\u7684\u795e\u8272\uff0c\u8fde\u9053\uff1a\u201c\u7f6a\u8fc7\uff0c\u7f6a\u8fc7\uff0c\u4f60\u600e\u4e48\u6562\u5982\u6b64\u8bf4\uff0c\u5723\u5973\u5bab\u4e2d\uff0c\u7537\u5b50\u4e0d\u8bb8\u8fdb\u53bb\u3002\u53ea\u6709 \u51e0\u4f4d\u8001\u5723\u6bcd\u6559\u5979\u4eec\u5ff5\u7ecf\uff0c\u8981\u6709\u6cd5\u4e8b\u5979\u4eec\u624d\u51fa\u6765\u7684\uff01\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u88ab\u4f60\u4eec\u6349\u6765\u7684\u5723\u5973\uff0c\u662f\u4e0d \u662f\u6709\u4e00\u4f4d\u4f1a\u6b66\u827a\u7684\u5973\u5b50\uff1f\u201d\u5c0f\u5587\u561b\u9053\uff0c\u201c\u542c\u8bf4\u6709\u8fd9\u4e48\u4e00\u4f4d\uff0c\u4f46\u5979\u4e0d\u80af\u505a\u5723\u5973\uff0c\u8fd9\u662f\u5979\u4e0e\u4f5b\u65e0 \u7f18\u3002\u6d3b\u4f5b\u4e5f\u4e0d\u52c9\u5f3a\u5979\u7684\u3002\u201d\u5510\u7ecf\u5929\u9053\uff0c\u201c\u5979\u4e5f\u5173\u5728\u5723\u5973\u5bab\u5417\uff1f\u201d\u5c0f\u5587\u561b\u9053\uff1a\u201c\u6211\u5df2\u8bf4\u8fc7\u6211\u4eec \u90fd\u4e0d\u80fd\u8fdb\u53bb\uff0c\u600e\u77e5\u5979\u662f\u4e0d\u662f\u5728\u90a3\u513f\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u4e48\u6cd5\u738b\u6bbf\u7684\u6240\u5728\uff0c\u4f60\u4eec\u603b\u8be5\u77e5\u9053\u4e86\uff1f \u201d\u90a3\u5c0f\u5587\u561b\u6307\u4e00\u6307\u6b63\u4e2d\u7684\u6bbf\u5b87\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5510\u7ecf\u5929\u95ee\u660e\u4e4b\u540e\uff0c\u4e0d\u7406\u4f1a\u4ed6\u4eec\uff1b\u987a\u624b \u5c06\u4ed6\u4eec\u70b9\u4e86\u54d1\u7a74\uff0c\u53eb\u4ed6\u4eec\u5728\u5341\u4e8c\u65f6\u8fb0\u4e4b\u5185\uff0c\u4e0d\u80fd\u8bf4\u8bdd\u3002 \u3000\u3000\u6b63\u4e2d\u7684\u90a3\u5ea7\u6bbf\u5b57\u5708\u5728\u56f4\u5899\u4e4b\u4e2d\uff0c\u9876\u4e0a\u94fa\u7740\u91d1\u9ec4\u8272\u7684\u7409\u7483\u74e6\uff0c\u5510\u7ecf\u5929\u6599\u60f3\u662f\u6cd5\u738b\u7684\u5b9d\u6bbf\u3002 \u5c06\u4e24\u4e2a\u5c0f\u5587\u561b\u653e\u5728\u6811\u540e\uff0c\u8dc3\u8fc7\u56f4\u5899\uff0c\u53ea\u89c1\u4f5b\u6bbf\u4e4b\u524d\u3001\u6709\u4e24\u4e2a\u767d\u8863\u5587\u561b\u5b88\u62a4\uff0c\u5510\u7ecf\u5929\u7684\u8f7b\u529f\u672c \u4e8b\uff0c\u5df26\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u771f\u5982\u4e00\u53f6\u98d8\u5815\uff0c\u843d\u5904\u65e0\u58f0\uff0c\u4e24\u4e2a\u767d\u8863\u5587\u561b\u4f3c\u6709\u8b66\u89c9\u3002\u63a2\u5934\u63a2\u8111 \uff0c\u4e00\u526f\u7591\u9b3c\u7591\u795e\u7684\u795e\u8272\uff0c\u6708\u5149\u4e0b\u770b\u5f97\u5206\u660e\uff0c\u539f\u6765\u5c31\u662f\u4ee5\u524d\u5230\u897f\u85cf\u62a2\u593a\u91d1\u672c\u5df4\u74f6\u7684\u90a3\u4e24\u4e2a\u767d\u6559 \u5587\u561b\u3002\u5510\u7ecf\u5929\u66fe\u4e0e\u4ed6\u4eec\u4ea4\u8fc7\u624b\uff0c\u77e5\u9053\u9053\u4ed6\u4eec\u6b66\u529f\u4e0d\u5f31\uff0c\u867d\u7136\u62e6\u963b\u4e0d\u4e86\u81ea\u5df1\uff0c\u529e\u4e8b\uff0c\u4f46\u4e00\u88ab\u53d1 \u89c9\uff0c\u5c31\u662f\u4e00\u573a\u5927\u5927\u7684\u9ebb\u70e6\u3002 \u3000\u3000\u9662\u5b50\u91cc\u591a\u7684\u662f\u767e\u5e74\u8001\u6811\uff0c\u5510\u7ecf\u5929\u5c31\u9690\u8eab\u5728\u4e00\u68f5\u679d\u53f6\u8302\u5bc6\u7684\u53c2\u5929\u53e4\u6811\u4e4b\u4e2d\uff0c\u6811\u9876\u4e0a\u6709\u51e0\u53ea \u5927\u9e1f\u6816\u606f\uff0c\u4f3c\u4e4e\u4e5f\u53d1\u73b0\u4e0b\u9762\u6709\u4eba\uff0c\u7fc5\u8180\u62cd\u52a8\u4e0d\u5df2\uff0c\u5510\u7ecf\u592d\u6458\u4e0b\u4e00\u7247\u6811\u53f6\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c\u4f7f\u51fa\u6458 \u53f6\u98de\u82b1\u7684\u6697\u5668\u529f\u592b\uff0c\u90a3\u7247\u6811\u53f6\u7a7f\u679d\u98de\u4e0a\uff0c\u5728\u6811\u9876\u6816\u606f\u7684\u5927\u9e1f\u90fd\u7ed9\u632f\u7fc5\u98de\u8d77\uff0c\u53d1\u51fa\u53eb\u58f0\u3002\u90a3\u4e24 \u4e2a\u5587\u561b\u9053\uff1a\u201c\u539f\u6765\u662f\u9e1f\u513f\u4f5c\u602a\u3002\u201d\u5510\u7ecf\u5929\u662f\u4f55\u7b49\u529f\u592b\uff0c\u8d81\u7740\u4ed6\u4eec\u51dd\u671b\u98de\u9e1f\uff0c\u80cc\u5411\u81ea\u5df1\u4e4b\u9645\uff0c \u4e00\u4e2a\u98d8\u8eab\uff0c\u500f\u5ffd\u4e4b\u95f4\u3002\u5df2\u63a0\u8fdb\u4e86\u6cd5\u738b\u5b9d\u6bbf\uff0c\u85cf\u8eab\u6a90\u89d2\uff0c\u771f\u8981\u6bd4\u98de\u9e1f\u8fd8\u5feb\u6377\uff0c\u9976\u662f\u90a3\u4e24\u4e2a\u767d\u6559 \u5587\u561b\uff0c\u4e5f\u4e1d\u6beb\u6ca1\u6709\u53d1\u89c9\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6084\u6084\u5411\u91cc\u5f20\u671b\uff0c\u6b63\u4e2d\u4e00\u5ea7\u623f\u95f4\uff0c\u8ddd\u4ed6\u85cf\u8eab\u4e4b\u5904\u6709\u6570\u4e08\u4e4b\u9065\uff0c\u9694\u7740\u7a97\u7eb1\uff0c\u53ea\u77a7\u89c1\u4e24 \u4e2a\u4eba\u5f71\uff0c\u4e00\u4e2a\u9ad8\u5927\u7684\u5f71\u5b50\u5750\u5728\u5f53\u4e2d\uff0c\u60f3\u5fc5\u5c31\u662f\u6cd5\u738b\uff0c\u53e6\u4e00\u4e2a\u7ad9\u5728\u65c1\u8fb9\u7684\uff0c\u5f53\u662f\u4f8d\u8005\u3002\u5510\u7ecf\u5929 \u51dd\u795e\u9759\u542c\uff0c\u53ea\u542c\u5f97\u90a3\u6cd5\u738b\u9053\uff1a\u201c\u54b1\u4eec\u51e0\u4ee3\u7956\u5e08\uff0c\u76fc\u4e86\u767e\u591a\u5e74\uff0c\u7ec8\u4e8e\u76fc\u5230\u4e86\u3002\u73ed\u7985\u7684\u4f5b\u4f7f\u8bf4\uff0c \u8981\u8bf7\u54b1\u4eec\u56de\u53bb\uff0c\u4ee5\u540e\u5927\u5bb6\u4e0d\u8981\u518d\u4e89\u6597\u4e86\uff0c\u963f\u96be\u5c0a\u8005\uff0c\u4f60\u7684\u610f\u601d\u600e\u6837\uff1f\u201d\u90a3\u4e2a\u53eb\u505a\u963f\u96be\u7684\u4f8d\u8005 \u8bf4\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u6cbe\u6d3b\u4f5b\u7684\u5a01\u671b\u7075\u5149\uff0c\u4e0d\u8fc7\uff0c\u4e00\u4e00\u201d\u90a3\u6cd5\u738b\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u4f60\u662f\u8bf4\u54b1\u4eec\u8fd9\u6b21 \u56de\u6765\uff0c\u8fd8\u4e0d\u591f\u5149\u91c7\u5417\uff1f\u201d\u963f\u96be\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u4e0d\u8fc7\u54b1\u4eec\u5728\u8fd9\u91cc\u662f\u81f3\u9ad8\u65e0\u4e0a\u2014\u2014\uff0c\u90a3 \u6cd5\u738b\u6309\u53e3\u9053\uff1a\u201c\u56de\u53bb\u4e4b\u540e\uff0c\u5c31\u662f\u5bc4\u4eba\u7bf1\u4e0b\u4e86\uff0c\u662f\u5417\uff1f\u6211\u544a\u8bc9\u4f60\uff0c\u73ed\u63b8\u7684\u4f5b\u4f7f\u5df2\u8f6c\u8fbe\u4e86\u897f\u85cf\u4e24 \u4f4d\u6d3b\u4f5b\u7684\u610f\u601d\uff0c\u5212\u51fa\u4e09\u4e2a\u5730\u65b9\u8ba9\u6211\u4eec\u5efa\u7acb\u5bfa\u5e99\uff0c\u5f7c\u6b64\u76f8\u5bb9\u3002\u7eb7\u4e89\u4e86\u767e\u591a\u5e74\uff0c\u6211\u4e5f\u4e0d\u60f3\u518d\u52a8\u5e72 \u6208\u4e86\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u8fd9\u6cd5\u738b\u5012\u6709\u4e00\u4e9b\u89c1\u8bc6\u3002\u201d\u767d\u6559\u5f53\u521d\u662f\u7ed9\u9ec4\u6559\u7528\u5175\u529b\u9010\u51fa\u897f\u85cf\u7684\uff0c\u82e5 \u7136\u518d\u6253\u56de\u53bb\uff0c\u897f\u85cf\u96be\u514d\u6218\u7978\u3002 \u3000\u3000\u90a3\u6cd5\u738b\u53c8\u9053\uff0c\u201c\u6211\u4e5f\u4e0d\u60f3\u79bb\u5f00\u8fd9\u513f\uff0c\u5c06\u6765\u897f\u85cf\u7684\u90a3\u4e09\u5904\u5730\u65b9\u5c31\u7531\u4f60\u4e3b\u6301\u3002\u201d\u8bf4\u5230\u8fd9\u513f\uff0c \u5510\u7ecf\u5929\u53ea\u89c1\u963f\u96be\u7684\u9ed1\u5f71\u5408\u4ec0\u4fef\u8170\uff0c\u60f3\u662f\u8c22\u6069\u3002\u90a3\u6cd5\u738b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u80fd\u518d\u56de\u897f\u85cf\uff0c\u603b\u7b97\u4e86 \u4e86\u7956\u5e08\u7684\u5fc3\u613f\u3002\u6709\u4e09\u5904\u5730\u65b9\uff0c\u6211\u4e5f\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u90a3\u6279\u5723\u5973\u600e\u6837\uff1f\u201d\u963f\u96be\u9053\uff1a\u201c\u9664\u4e86\u51e0\u4e2a\u4eba\u5916 \uff0c\u5176\u5b83\u7684\u90fd\u613f\u542c\u6d3b\u4f5b\u7684\u6cd5\u65e8\u3002\u201d\u90a3\u6cd5\u738b\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u4e0d\u8981\u52c9\u5f3a\u5979\u4eec\u3002\u767e\u591a\u5e74\u524d\uff0c\u54b1\u4eec\u7684\u7956\u5e08 \u5728\u897f\u85cf\u638c\u6559\u4e4b\u65f6\uff0c\u6c11\u95f4\u7684\u5973\u5b50\u4e89\u7740\u6765\u505a\u5723\u5973\uff0c\u8fd9\u91cc\u7684\u98ce\u4fd7\u4e0d\u540c\uff0c\u6c49\u4eba\u5360\u4e86\u5927\u534a\uff0c\u4ed6\u4eec\u4e0d\u77e5\u505a \u5723\u5973\u7684\u5149\u8363\uff0c\u6240\u4ee5\u96be\u514d\u5927\u60ca\u5c0f\u602a\u3002\u767e\u5e74\u6765\u6211\u4eec\u4e0d\u53ec\u5723\u5973\uff0c\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\uff0c\u800c\u4eca\u65e2\u7136\u51c6\u5907 \u56de\u5230\u897f\u85cf\uff0c\u4e0d\u80fd\u4e0d\u6062\u590d\u65e7\u65f6\u7684\u4eea\u793c\uff0c\u5bfa\u5e99\u843d\u6210\u7684\u5f00\u5149\u5927\u5178\uff0c\u6ca1\u6709\u5723\u5973\u7684\u5949\u795e\u6b4c\u821e\uff0c\u90a3\u6210\u4f55\u4f53 \u7edf\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5012\u8fd8\u60c5\u6709\u53ef\u539f\u3002\u6211\u51e0\u4e4e\u5c06\u4ed6\u4eec\u5f53\u505a\u6deb\u50e7\u770b\u5f85\u5462\uff01\u201d\u90a3\u4f8d\u8005 \u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u4eec\u5927\u60ca\u5c0f\u602a\uff0c\u771f\u662f\u4e0d\u597d\u3002\u201d\u90a3\u6cd5\u738b\u9053\uff1a\u201c\u4e5f\u4e0d\u80fd\u602a\u4ed6\u4eec\uff0c\u6c49\u4eba\u8fde\u628a\u513f\u5b50\u9001\u6765 \u5f53\u5587\u561b\u7684\u90fd\u4e0d\u591a\uff0c\u4f55\u51b5\u8981\u4ed6\u4eec\u7684\u5973\u513f\u3002\u90a3\u4e9b\u4e0d\u613f\u5f53\u5723\u5973\u7684\u591a\u534a\u662f\u6c49\u4eba\uff0c\u662f\u4e48\uff1f\u201d\u4f8d\u8005\u70b9\u4e86\u70b9 \u5934\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u90a3\u6cd5\u738b\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u6b21\u4e8b\u51fa\u5ffd\u5fd9\uff0c\u4e0d\u5411\u4ed6\u4eec\u4e8b\u5148\u8bf4\u660e\uff0c\u4e5f\u4e0d\u5927\u597d\u3002\u8fd9\u6837\u529e \u5427\uff0c\u660e\u65e5\u54b1\u4eec\u5f00\u4e2a\u6cd5\u4f1a\uff0c\u4f60\u6d3e\u4eba\u53bb\u8bf7\u57ce\u4e2d\u7684\u58eb\u7ec5\u7236\u8001\u6765\u968f\u559c\uff0c\u987a\u4fbf\u5411\u4ed6\u4eec\u89e3\u91ca\u6e05\u695a\u3002\u4e0d\u613f\u5f53 \u5723\u5973\u7684\uff0c\u90fd\u8ba9\u5979\u4eec\u7684\u7236\u6bcd\u9886\u56de\u53bb\u3002\u201d\u963f\u96be\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u4e0d\u613f\u5f53\u5723\u5973\u7684\uff0c\u4e0d\u662f\u6c49\u4eba\uff0c\u4ece\u670d\u9970\u4e0a \u770b\uff0c\u662f\u4ece\u897f\u85cf\u6765\u7684\uff0c\u4ed6\u6253\u4e86\u6211\u4eec\u7684\u5587\u561b\uff0c\u8fd9\u600e\u4e48\u529e\u3001\u4e5f\u653e\u56de\u5417\uff1f\u201d\u6253\u9a82\u5587\u561b\u662f\u4e00\u6869\u5927\u7f6a\uff0c\u6cd5 \u738b\u4f3c\u4e4e\u8e0c\u8e87\u4e0d\u51b3\uff0c\u826f\u4e45\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u8fc7\u540e\u518d\u8bf4\u5427\uff0c\u4e5f\u4e0d\u8981\u96be\u4e3a\u5979\u3002\u201d\u963f\u96be\u9053\uff1a\u201c\u542c\u8bf4\u5979\u4e0d\u80af \u5403\u4e1c\u897f\u3002\u201d\u6cd5\u738b\u9053\uff1a\u201c\u660e\u513f\u6211\u53eb\u8001\u5723\u5973\u8ddf\u5979\u8bf4\u53bb\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u513f\uff0c\u90a3\u6cd5\u738b\u7a81\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u5012\u4e00\u676f\u9152\u7ed9\u6211\u559d\u559d\u3002\u201d\u53ea\u89c1\u4ed6\u6301\u7740\u9152\u676f\uff0c\u8d70\u8fd1 \u7a97\u524d\uff0c\u5ffd\u5730\u63a8\u5f00\u4e86\u7a97\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u8d38\u676f\u5f84\u5411\u5510\u7ecf\u5929\u533f\u8eab\u4e4b\u5904\u98de\u53bb\u3002 \u3000\u3000\u90a3\u9152\u676f\u5288\u7a7a\u6253\u51fa\uff0c\u5176\u58f0\u545c\u9e23\uff0c\u7adf\u4f3c\u4e00\u652f\u54cd\u7bad\uff0c\u52b2\u529b\u4e4b\u5f3a\uff0c\u53ef\u4ee5\u60f3\u89c1\uff0c\u800c\u4e14\u542c\u98ce\u8fa8\u5668\uff0c\u90a3 \u9152\u676f\u7adf\u662f\u671d\u7740\u5510\u7ecf\u5929\u80f8\u53e3\u7684\u201c\u7384\u673a\u7a74\u201d\u6253\u6765\u3002\u867d\u7136\u5728\u660f\u591c\u4e4b\u4e2d\uff0c\u8ba4\u7a74\u4e0d\u5dee\u6beb\u5398\uff0c\u5510\u7ecf\u5929\u4e0d\u7531 \u5f97\u5fc3\u4e2d\u4e00\u51db\uff1a\u60f3\u4e0d\u5230\u8fd9\u767d\u6559\u6cd5\u738b\u7ade\u6709\u8fd9\u4e48\u4fca\u7684\u6697\u5668\u529f\u592b\uff01\u5510\u7ecf\u5929\u4f38\u6307\u4e00\u5f39\uff0c\u731b\u7136\u95f4\uff0c\u53c8\u95fb\u5f97 \u4e00\u80a1\u9152\u9999\uff0c\u8fce\u9762\u55b7\u6765\uff0c\u53ea\u89c1\u773c\u524d\u4e00\u6761\u767d\u7ec3\uff0c\u500f\u5730\u6563\u5f00\uff0c\u5316\u6210\u767d\u6f3e\u6aac\u4e00\u7247\u7684\u201c\u9152\u6d6a\u201d\uff0c\u9152\u82b1\u5982 \u96e8\uff0c\u56db\u5904\u98de\u6d12\u3002\u539f\u6765\u90a3\u767d\u6559\u6cd5\u738b\uff0c\u628a\u9152\u676f\u548c\u9152\u3002\u90fd\u5f53\u6210\u4e86\u6697\u5668\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4f38\u6307\u4e00\u5f39\uff0c\u5f53\u537f\u4e00\u58f0\uff0c\u9152\u676f\u788e\u88c2\uff0c\u9976\u4ed6\u95ea\u907f\u5f97\u5feb\uff0c\u8863\u8896\u4e0a\u4e5f\u6cbe\u4e86\u51e0\u70b9\u9152\u73e0\u3002\u523a\u7a7f \u4e86\u51e0\u4e2a\u5c0f\u6d1e\u3002\u8fd9\u4e00\u624b\u529f\u592b\uff0c\u548c\u5510\u7ecf\u5929\u521a\u624d\u7528\u6811\u53f6\u6253\u9e1f\u7684\u529f\u592b\uff0c\u540c\u5c5e\u4e00\u8def\u3002\u90fd\u662f\u7b2c\u4e00\u6d41\u7684\u4e0a\u4e58 \u5185\u529f\u3002\u5510\u7ecf\u5929\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u90a3\u6cd5\u738b\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5982\u6b64\u80c6\u5927\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u500f\u5730\u7a7f\u7a97\u98de \u51fa\uff0c\u4ed6\u62ab\u7740\u5927\u7ea2\u8888\u88df\uff0c\u5c31\u50cf\u4e00\u7247\u7ea2\u4e91\uff0c\u5f53\u5934\u538b\u4e0b\uff0c\u5510\u7ecf\u592d\u53cc\u811a\u52fe\u7740\u5c4b\u6a90\uff0c\u4e0a\u534a\u8eab\u5df2\u503e\u659c\u5728 \u5916\u3002 \u3000\u3000\u90a3\u6cd5\u738b\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u63a8\uff0c\u53ea\u89c9\u6765\u4eba\u7adf\u4f3c\u94c1\u94f8\u4e00\u822c\uff0c\u63a8\u4e4b\u4e0d\u52a8\u3002\u90a3\u6cd5\u738b\u500f\u5730\u7f29\u56de\u53f3\u638c \uff0c\u52b2\u529b\u4e00\u6536\uff0c\u5510\u7ecf\u5929\u8499\u7740\u9762\u4e2d\uff0c\u4e24\u53ea\u773c\u775b\uff0c\u9732\u5728\u5916\u9762\uff0c\u90a3\u6cd5\u738b\u64a4\u56de\u53f3\u638c\uff0c\u9a84\u6307\u5982\u88c1\uff0c\u5c31\u6316\u5510 \u7ecf\u5929\u7684\u9762\u4e0a\u53cc\u775b\uff0c\u5de6\u624b\u4ecd\u7136\u4e0e\u5510\u7ecf\u5929\u7684\u53cc\u638c\u76f8\u62b5\uff0c\u731b\u529b\u63a8\u538b\u3002\u5510\u7ecf\u5929\u6b63\u5728\u6697\u8fd0\u5185\u529b\uff0c\u5ffd\u89c9\u5de6 \u8fb9\u53d7\u653b\u7684\u52b2\u529b\uff0c\u7a81\u7136\u6d88\u5931\uff0c\u800c\u53f3\u8fb9\u7684\u52b2\u529b\uff0c\u5374\u5ffd\u5c14\u589e\u5f3a\u4e00\u500d\uff0c\u9ad8\u624b\u6bd4\u8bd5\uff0c\u6700\u5fcc\u4e0d\u77e5\u654c\u4eba\u7684\u653b \u52bf\u6240\u5728\uff0c\u90a3\u6cd5\u738b\u53cc\u638c\u7684\u653b\u52bf\u7a81\u7136\u8f6c\u6362\uff0c\u52b2\u529b\u4e00\u6536\u4e00\u7d27\uff0c\u5510\u7ecf\u5929\u5931\u4e86\u5e73\u8861\uff0c\u4e0a\u534a\u8eab\u6447\u6447\u6643\u6643\uff0c \u5df2\u5c06\u8dcc\u5012\uff0c\u5ffd\u53c8\u89c1\u90a3\u6cd5\u738b\u4f38\u6307\u70b9\u4ed6\u7684\u9762\u95e8\uff0c\u8fd9\u4e00\u62db\u66f4\u662f\u6bd2\u8fa3\u65e0\u6bd4\uff01 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u60f3\u51fa\u6740\u624b\u5316\u89e3\uff0c\u66ae\u7136\u95f4\u5fc3\u4e2d\u5ff5\u5934\u4e00\u8f6c\uff1a\u8fd9\u6cd5\u738b\u4e43\u662f\u4e00\u6d3e\u4e4b\u5c0a\uff0c\u6253\u4f24\u4e86\u4ed6\uff0c\u7275\u6d89 \u592a\u5927\u3002\u90a3\u6cd5\u738b\u53cc\u6307\u70b9\u51fa\uff0c\u5ffd\u89c9\u654c\u4eba\u7684\u52b2\u529b\u4e5f\u662f\u7a81\u7136\u4e00\u6536\uff0c\u4f46\u89c1\u654c\u4eba\u7684\u8eab\u8eaf\u5e73\u7a7a\u5f7c\u8d77\uff0c\u5df2\u95ea\u8f6c \u4e86\u8eab\uff0c\u5c31\u8981\u8dc3\u4e0b\u3002\u90a3\u6cd5\u738b\u201c\u563f\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f60\u8fd9\u624b\u8f7b\u529f\uff0c\u867d\u7136\u8d85\u5999\u7edd\u4f26\uff0c\u540c \u65f6\u907f\u5f00\u4e86\u6211\u6307\u638c\u7684\u4e24\u8def\u653b\u52bf\u3001\u4f46\u5176\u5948\u4f60\u7684\u80cc\u810a\u5df2\u5356\u7ed9\u6211\u4e86\uff01\u201d\u5f53\u4e0b\u53f3\u624b\u53c8\u53d8\u6307\u4e3a\u638c\uff0c\u4e00\u62db\u201d \u624b\u6325\u7435\u7436\uff0c\u5411\u5510\u7ecf\u5929\u80cc\u5fc3\u731b\u51fb\uff0c\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\u3002\u5982\u51fb\u8d25\u9769\uff0c\u5510\u7ecf\u5929\u4f3c\u5f39\u4e38\u4e00\u822c\uff0c\u76f4\u7ed9 \u4ed6\u51fb\u51fa\u5899\u5916\uff0c\u90a3\u6cd5\u738b\u4e5f\u54ce\u54df\u4e00\u58f0\uff0c\u5012\u5728\u74e6\u9762\uff1b\u539f\u6765\u5510\u7ecf\u5929\u5728\u4ed6\u638c\u51fb\u80cc\u5fc3\u4e4b\u65f6\uff0c\u4e5f\u53cd\u624b\u4e00\u62c2\uff0c \u7528\u5929\u5c71\u6d3e\u72ec\u7279\u7684\u201c\u62c2\u7a74\u201d\u624b\u6cd5\uff0c\u53ea\u5728\u4e00\u62c2\u4e4b\u95f4\uff0c\u4e94\u6839\u624b\u6307\uff0c\u5c31\u8fde\u70b9\u4e2d\u4e86\u4ed6\u7684\u4e94\u5904\u7a74\u9053\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u6025\u5fd9\u8fd0\u6c14\u89e3\u7a74\uff0c\u4ed6\u5185\u529f\u7cbe\u6e5b\uff0c\u662f\u767d\u6559\u5587\u561b\u6709\u53f2\u4ee5\u6765\u7684\u7b2c\u4e00\u4eba\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u65b9\u81ea \u51b2\u5173\u89e3\u7a74\uff0c\u53ea\u662f\u56db\u80a2\u9ebb\u75f9\uff0c\u8fd8\u672a\u5b8c\u5168\u6062\u590d\u539f\u72b6\u3002\u90a3\u6cd5\u738b\u4e5f\u4e0d\u7981\u53c8\u60ca\u53c8\u8be7\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u8fd9\u4eba\u7684 \u529f\u592b\u7edd\u5bf9\u4e0d\u5728\u6211\u4e0b\uff0c\u4ed6\u672c\u6765\u53ef\u4ee5\u5316\u89e3\u6211\u7684\u62db\u6570\uff0c\u4f55\u4ee5\u5374\u5982\u6b64\u5192\u9669\u3002\u786c\u751f\u751f\u7684\u6328\u6211\u4e00\u638c\uff1f \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6709\u5fc3\u72af\u96be\u6c42\u771f\u76f8\uff0c\u6362\u5f97\u6cd5\u738b\u53e6\u773c\u770b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u5723\u5973\u5bab\u4e2d\u3000\u7591\u4e91\u8ff7\u4fa0\u5ba2\u3000\u5587\u561b\u5bfa\u91cc\u3000\u6cd5\u4f1a\u8d77\u5e72\u6208 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u516d\u56de \u5723\u5973\u5bab\u4e2d\u3000\u7591\u4e91\u8ff7\u4fa0\u5ba2 \u5587\u561b\u5bfa\u91cc\u3000\u6cd5\u4f1a\u8d77\u5e72\u6208 \u3000\u3000\u5916\u9762\u4e24\u4e2a\u767d\u6559\u5587\u561b\uff0c\u95fb\u58f0\u60ca\u8d77\uff0c\u6b63\u5f85\u8dc3\u51fa\u56f4\u5899\uff0c\u5f80\u5916\u8ffd\u8d76\uff0c\u90a3\u6cd5\u738b\u4f20\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u4e24 \u4e2a\u8113\u5305\uff0c\u60f3\u81ea\u8d54\u4e0a\u6027\u547d\u4e48\uff1f\u4ed6\u53d7\u4e86\u6211\u7684\u4e00\u638c\uff0c\u4e0d\u8fc7\u4e09\u5929\uff0c\u5fc5\u7136\u9001\u547d\uff0c\u4f60\u4eec\u8ffd\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d\u8bf4 \u5b8c\u4e4b\u540e\uff0c\u4f4e\u4f4e\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u8fd9\u4eba\u4fee\u5230\u5982\u6b64\u6b66\u529f\uff0c\u4ea6\u975e\u5bb9\u6613\uff0c\u5374\u4e0d\u77e5\u662f\u53d7\u8c01\u6307\u4f7f\uff0c \u5230\u6b64\u7aa5\u63a2\u3002\u767d\u767d\u8d54\u4e86\u6027\u547d\u3002\u5fc3\u4e2d\u5927\u662f\u540e\u6094\u3002 \u3000\u3000\u4e14\u8bf4\u5510\u7ecf\u5929\u6328\u4e86\u90a3\u638c\uff0c\u80cc\u5fc3\u9690\u9690\u4f5c\u75db\uff0c\u6e9c\u56de\u65c5\u5e97\u623f\u95f4\uff0c\u89e3\u4e0b\u91cc\u9762\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u5c31\u7740\u623f\u4e2d \u7684\u94dc\u955c\u4e00\u7167\uff0c\u53ea\u89c1\u80cc\u5fc3\u7600\u9ed1\u4e00\u5757\uff0c\u4ea6\u662f\u4e0d\u7981\u9a87\u7136\uff0c\u4ed6\u62ff\u8d77\u90a3\u4ef6\u91d1\u4e1d\u8f6f\u7532\uff0c\u5fc3\u9053\uff1a\u201c\u5e78\u800c\u6709\u8fd9 \u4e00\u4ef6\u5b9d\u8d1d\uff0c\u8981\u4e0d\u7136\u771f\u4f1a\u7ed9\u4ed6\u9707\u4f24\u5185\u810f\uff0c\u8fd9\u6cd5\u738b\u7684\u529f\u529b\uff0c\u679c\u7136\u975e\u540c\u5c0f\u53ef\uff01\u201d \u3000\u3000\u539f\u6765\u5510\u7ecf\u5929\u8fd9\u526f\u91d1\u4e1d\u8f6f\u7532\uff0c\u6709\u4e2a\u6765\u5386\uff0c\u90a3\u662f\u4ed6\u6bcd\u4eb2\u51af\u745b\uff0c\u5728\u5468\u5c81\u4e4b\u65f6\uff0c\u65e0\u6781\u6d3e\u7684\u5b97\u5e08\u9524 \u4e07\u5802\u9001\u7ed9\u5979\u4f5c\u89c1\u9762\u793c\u7684\uff0c\u8fd9\u91d1\u4e1d\u8f6f\u7532\u662f\u7528\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u91d1\u6bdb\u543c\u7684\u80cc\u4e0a\u91d1\u6bdb\u7f16\u7ec7\u6210\u7684\uff0c\u53c8\u8f6f\u53c8 \u8f7b\uff0c\u5200\u5251\u4e0d\u5165\uff0c\u638c\u529b\u66f4\u4e0d\u80fd\u9707\u788e\u3002\u90a3\u767d\u6559\u6cd5\u738b\u7684\u638c\u529b\uff0c\u672c\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u4f46\u53d7\u4e86\u8f6f\u7532\u4e00\u9694 \uff0c\u4f20\u5230\u5510\u7ecf\u5929\u8eab\u4e0a\u7684\u52b2\u9053\uff0c\u81ea\u7136\u6d88\u4e86\u4e00\u534a\uff0c\u52a0\u4e0a\u5510\u7ecf\u5929\u672c\u8eab\u7684\u529f\u529b\uff0c\u5185\u810f\u867d\u53d7\u9707\u8361\uff0c\u5374\u65e0\u5927 \u788d\uff0c\u5510\u7ecf\u5929\u8fd8\u4e0d\u653e\u5fc3\uff0c\u4e49\u7528\u5929\u5c71\u96ea\u83b2\u6240\u7ec3\u6210\u7684\u78a7\u7075\u4e39\uff0c\u5185\u670d\u5916\u6577\uff0c\u7136\u540e\u5b89\u5b89\u9759\u9759\u7684\u7761\u4e86\u4e00\u5927 \u89c9\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\u8d77\u6765\uff0c\u90a3\u5e97\u5c0f\u4e8c\u8fdb\u6765\u95f2\u804a\uff0c\u4e24\u4eba\u4e0d\u514d\u53c8\u8c08\u8d77\u767d\u6559\u6cd5\u738b\u4e4b\u4e8b\uff0c\u90a3\u5e97\u5c0f\u4e8c\u9053\uff1a\u201d \u4ed6\u95e8\u90fd\u8bf4\u524d\u4e24\u5927\u5587\u561b\u5bfa\u641c\u6355\u7f8e\u8c8c\u5c11\u5973\u5fc5\u6709\u6765\u7531\u3002\u6cd5\u738b\u4eca\u665a\u5927\u5f00\u6cd5\u4f1a\uff0c\u8bf7\u4e86\u8bb8\u591a\u58eb\u7ec5\uff0c\u8ba9\u4ed6\u4eec \u62c8\u9999\u968f\u559c\uff0c\u8fd8\u8bf7\u4e86\u90a3\u4e9b\u88ab\u6349\u8fdb\u5587\u561b\u5bfa\u7684\u5c11\u5973\u7684\u7236\u5144\uff0c\u542c\u8bf4\u4e00\u5171\u8bf7\u4e86\u767e\u591a\u4f4d\u5916\u4eba\uff0c\u8fd9\u662f\u81ea\u5587\u561b \u5bfa\u5efa\u6210\u4ee5\u6765\uff0c\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\u3002\u660e\u5929\u4e00\u65e9\u6211\u4eec\u5c31\u77e5\u9053\u5587\u561b\u5bfa\u4e3a\u4ec0\u4e48\u8981\u6293\u5c11\u5973\u4e86\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053 \uff1a\u201c\u4ed6\u53c8\u4e0d\u8bf7\u4f60\uff0c\u4f60\u54ea\u80fd\u8fd9\u6837\u5feb\u77e5\u9053\uff1f\u201d\u90a3\u5e97\u5c0f\u4e8c\u6ee1\u8138\u795e\u6c14\u7684\u9053\uff1a\u201c\u4ed6\u867d\u7136\u4e0d\u8bf7\u6211\uff0c\u53ef\u662f\u5374 \u8bf7\u4e86\u54b1\u4eec\u638c\u67dc\u7684\uff0c\u638c\u67dc\u7684\u56de\u6765\uff0c\u8fd8\u4f1a\u4e0d\u548c\u6211\u8bf4\uff1f\u201d\u539f\u6765\u5f00\u8bbe\u8fd9\u95ee\u5ba2\u5e97\u7684\u4e3b\u4eba\uff0c\u4e5f\u662f\u57ce\u4e2d\u4e8c\u6d41 \u58eb\u7ec5\uff0c\u4ecd\u5728\u88ab\u8bf7\u4e4b\u5217\u3002\u5510\u7ecf\u5929\u5927\u559c\uff0c\u53c8\u548c\u4ed6\u804a\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u63a2\u542c\u5173\u4e8e\u8fd9\u95f4\u5ba2\u5e97\u4e3b\u4eba\u7684\u4e8b\u60c5\uff0c \u539f\u6765\u8fd9\u4f4d\u638c\u67dc\u662f\u7ee7\u627f\u7236\u4e1a\uff0c\u5e74\u7eaa\u751a\u8f7b\uff0c\u8fd8\u4e0d\u5230\u4e09\u5341\u5c81\u3002\u5510\u7ecf\u5929\u53c8\u6253\u542c\u5230\u4e86\u4eca\u665a\u7684\u6cd5\u4f1a\u662f\u51ed\u5e16 \u5165\u5ea7\uff0c\u60f3\u4ed6\u6240\u8bf7\u7684\u5bbe\u5ba2\u751a\u591a\uff0c\u5fc5\u4e0d\u4f1a\u4ed4\u7ec6\u76d8\u67e5\u3002 \u3000\u3000\u9ec4\u660f\u8fc7\u540e\uff0c\u5510\u7ecf\u5929\u65e9\u5df2\u63a2\u542c\u6e05\u695a\uff0c\u6084\u6084\u6e9c\u5165\u638c\u67dc\u7684\u623f\u4e2d\uff0c\u4f0f\u5728\u5c4b\u6881\u4e4b\u4e0a\uff0c\u53ea\u89c1\u90a3\u5e97\u4e3b\u4eba \u9ad8\u5174\u975e\u5e38\uff0c\u62ff\u51fa\u9ed1\u7f0e\u9a6c\u8902\uff0c\u6b63\u5728\u66f4\u8863\uff0c\u90a3\u5f20\u63cf\u91d1\u7684\u5927\u7ea2\u8bf7\u5e16\uff0c\u5c31\u653e\u5728\u5751\u4e0a\uff0c\u5510\u7ecf\u5929\u522e\u4e0b\u5899\u4e0a \u7684\u6ce5\u5c51\uff0c\u6413\u6210\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u6ce5\u4e38\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c\u5c31\u6253\u4e2d\u4e86\u90a3\u5e97\u4e3b\u4eba\u7684\u660f\u7761\u7a74\uff0c\u975e\u8fc7\u5341\u4e8c\u4e2a\u65f6\u8fb0 \uff0c\u4e0d\u80fd\u81ea\u89e3\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4ece\u6881\u4e0a\u8df3\u4e0b\u6765\uff0c\u5c06\u5e97\u4e3b\u4eba\u653e\u5728\u7095\u4e0a\uff0c\u7ed9\u4ed6\u76d6\u597d\u4e86\u88ab\uff0c\u7b11\u9053\uff1a\u201c\u8ba9\u4f60\u597d\u597d\u7761\u4e00\u5927\u89c9 \u3002\u201d\u6362\u4e86\u4ed6\u7684\u8863\u88f3\uff0c\u5e97\u4e3b\u4eba\u7684\u8eab\u6750\u548c\u5510\u7ecf\u5929\u5012\u5dee\u4e0d\u591a\uff0c\u53ea\u662f\u9762\u5e9e\u7a0d\u4e3a\u7626\u524a\u7d2b\u9ed1\uff0c\u5510\u7ecf\u5929\u53d6\u51fa \u968f\u8eab\u643a\u5e26\u7684\u201c\u6613\u5bb9\u4e39\u201d\uff08\u8fd9\u662f\u53e4\u4ee3\u8d70\u6c5f\u6e56\u7684\u9ed1\u9053\u4eba\u7269\u6240\u5fc5\u5907\u7684\u4e1c\u897f\uff0c\u4ea6\u662f\u539f\u59cb\u7684\u5316\u88c5\u672f\u7528\u54c1 \uff0c\u6709\u6e05\u4e00\u4ee3\u4ee5\u7518\u98ce\u4ed6\u6700\u4e3a\u64c5\u6c0f\uff0c\u5510\u7ecf\u5929\u7684\u7236\u4eb2\u5510\u6653\u6f9c\u5c31\u662f\u4ece\u7518\u98ce\u6c60\u5b66\u5230\u5236\u7ec3\u6613\u5bb9\u4e39\u7684\u6cd5\u5b50\u7684 \u3002\u8c03\u4e86\u4e00\u70b9\u7164\u7070\uff0c\u7528\u70ed\u6c34\u5316\u5f00\uff0c\u643d\u5728\u8138\u4e0a\uff0c\u62b9\u5e72\u4e86\u624b\uff0c\u968f\u5373\u53d6\u4e86\u6cd5\u536b\u90a3\u5f20\u8bf7\u5e16\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c \u6084\u6084\u6e9c\u51fa\u5ba2\u5e97\u3002 \u3000\u3000\u5587\u561b\u5bfa\u7684\u77e5\u5ba2\u50e7\u5e76\u4e0d\u8ba4\u8bc6\u6240\u6709\u9080\u8bf7\u7684\u5ba2\u4eba\u3002\u52a0\u4e4b\u5343\u767e\u5e74\u6765\uff0c\u4ece\u65e0\u4eba\u6562\u5230\u5587\u561b\u5bfa\u6363\u4e71\uff0c\u800c \u5587\u561b\u5bfa\u4e2d\u53c8\u662f\u9ad8\u624b\u5982\u4e91\uff0c\u6545\u6b64\u5e76\u65e0\u7279\u522b\u9632\u5907\uff0c\u679c\u7136\u7ed9\u5510\u7ecf\u5929\u6599\u4e2d\uff0c\u6ca1\u6709\u7ecf\u8fc7\u4ed4\u7ec6\u7684\u76d8\u67e5\uff0c\u53ea \u662f\u51ed\u7740\u8bf7\u5e16\uff0c\u5c31\u653e\u5165\u4e86\u3002 \u3000\u3000\u6cd5\u4f1a\u5b8f\u5f00\uff0c\u6b63\u4e2d\u5927\u6bbf\u62db\u5f85\u7684\u662f\u62dc\u5904\u5587\u561b\u5bfa\u9662\u7684\u4e3b\u6301\u548c\u5176\u4ed6\u8d35\u5bbe\uff0c\u4e1c\u8fb9\u504f\u6bbf\u5219\u62db\u5f85\u57ce\u4e2d\u7684 \u58eb\u7ec5\u548c\u88ab\u6349\u53bb\u5f53\u5723\u5973\u7684\u5bb6\u957f\uff0c\u9152\u8fc7\u4e09\u5de1\uff0c\u767d\u6559\u6cd5\u738b\u7684\u9996\u5ea7\u5f1f\u5b50\u963f\u96be\u5c0a\u8005\u8d70\u6765\u656c\u9152\uff0c\u6717\u58f0\u8bf4\u9053 \uff1a\u201c\u4eca\u65e5\u6709\u5929\u5927\u7684\u559c\u4e8b\u544a\u4e0e\u4f60\u4eec\u77e5\u9053\uff0c\u897f\u85cf\u7684\u6d3b\u4f5b\u4e0e\u54b1\u4eec\u7684\u6d3b\u4f5b\u5df2\u7ecf\u8bb2\u548c\u5566\uff01\u201d\u5ea7\u4e0a\u58eb\u7ec5\u4e00 \u9f50\u6b22\u547c\uff0c\u8fc7\u53bb\u767e\u5e74\uff0c\u4e24\u6559\u5927\u5c0f\u51b2\u7a81\u4e0d\u4e0b\u6570\u5341\u6b21\u4e4b\u591a\uff0c\u4eba\u547d\u8d22\u4ea7\u7684\u635f\u5931\u96be\u4ee5\u4f30\u8ba1\uff0c\u4eca\u65e5\u4e00\u65e6\u5316 \u5e72\u6208\u800c\u4e3a\u7389\u5e1b\uff0c\u542f\u7136\u4e2a\u4e2a\u559c\u60a6\u3002\u6709\u4e9b\u58eb\u7ec5\uff0c\u6b22\u547c\u4e4b\u540e\uff0c\u5ffd\u5730\u9192\u8d77\u4e0d\u59a5\uff0c\u53c8\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u54b1\u4eec \u613f\u6d3b\u4f5b\u6c38\u8fdc\u9a7b\u9521\u9752\u6d77\uff0c\u4e0d\u8981\u79bb\u5f00\u6211\u4eec\u3002\u201d\u963f\u96be\u5c0a\u8005\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u73ed\u7985\u6d3b\u4f5b\u5df2\u4e0e\u6cd5\u738b\u8bb2 \u597d\uff0c\u897f\u85cf\u62e8\u51fa\u6c81\u5361\u3001\u8428\u8fe6\u3001\u6ec7\u5e03\u4e09\u4e2a\u5730\u65b9\uff0c\u7531\u54b1\u4eec\u5efa\u7acb\u5bfa\u9662\uff0c\u6cd5\u738b\u5728\u5bfa\u9662\u5efa\u6210\u4e4b\u65f6\uff0c\u81ea\u5f53\u524d \u53bb\u4e3b\u6301\u5f00\u5149\u5927\u5178\uff0c\u5927\u5178\u8fc7\u540e\uff0c\u6559\u52a1\u4fbf\u7531\u5144\u5f1f\u4e3b\u6301\uff0c\u6cd5\u738b\u4f53\u8c05\u4f60\u4eec\uff0c\u4ed6\u4f1a\u518d\u56de\u6765\u6c38\u8fdc\u836b\u5e87\u4f60\u4eec \u3002\u201d\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u6b22\u547c\u3002\u963f\u96be\u5c0a\u8005\u6240\u5ba3\u5e03\u7684\u4e8b\u60c5\uff0c\u5510\u7ecf\u5929\u65e9\u5df2\u77e5\u9053\uff0c\u4f46\u897f\u85cf\u6240\u62e8\u51fa\u7684\u90a3\u4e09\u4e2a \u5730\u65b9\uff0c\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u52a8\uff1a\u90a3\u4e09\u4e2a\u5730\u65b9\u4e4b\u4e2d\u7684\u8428\u8fe6\u5b97\u5730\u65b9\uff0c\u6b63\u662f\u9648\u5929\u5b87\u7236\u4eb2 \u7684\u5b98\u8859\u6240\u5728\u4e4b\u5730\u3002 \u3000\u3000\u963f\u96be\u5c0a\u8005\u5f85\u6b22\u547c\u58f0\u505c\u4e0b\u4e4b\u540e\uff0c\u9762\u5bb9\u4e00\u7aef\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e3a\u4e86\u5230\u897f\u85cf\u4e3b\u6301\u5bfa\u9662\u5f00\u5149\u5927\u5178\uff0c \u54b1\u4eec\u6309\u7167\u6559\u89c4\uff0c\u6311\u9009\u5723\u5973\u3002\u80fd\u5f53\u4e0a\u5723\u5973\u7684\uff0c\u90fd\u662f\u4e0e\u4f5b\u6709\u7f18\uff0c\u5929\u5927\u7684\u798f\u6c14\u3002\u4f46\u6cd5\u738b\u4e3a\u4e86\u4f53\u8c05\u4f60 \u4eec\uff0c\u6709\u4e0d\u613f\u5973\u513f\u5f53\u5723\u5973\u7684\u4e5f\u53ef\u4ee5\u5766\u7387\u9648\u660e\uff0c\u6cd5\u738b\u51c6\u8bb8\u4ed6\u4eec\u9886\u5973\u513f\u56de\u53bb\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5ea7\u65e0 \u58f0\uff0c\u963f\u96be\u6273\u8d77\u9762\u5b54\uff0c\u518d\u95ee\u4e86\u4e00\u6b21\uff0c\u7ed3\u679c\u4e09\u5341\u516d\u4e2a\u5723\u5973\u7684\u5bb6\u957f\uff0c\u53ea\u6709\u4e03\u4eba\u6562\u8bf4\u51fa\u8981\u9886\u5973\u513f\u56de\u53bb \uff0c\u5341\u591a\u4e2a\u4eba\u4e0d\u6562\u4f5c\u58f0\uff0c\u8fd8\u6709\u5341\u591a\u4e2a\u4eba\u5219\u8877\u5fc3\u559c\u60a6\u5730\u53e9\u8c22\u6d3b\u4f5b\u7684\u6069\u5178\u3002 \u3000\u3000\u963f\u96be\u5c0a\u8005\u8bf4\u5b8c\u4e4b\u540e\uff0c\u53c8\u656c\u4e86\u4e00\u9053\u9152\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6cd5\u738b\u4eca\u65e5\u7279\u51c6\u4f60\u4eec\u62c8\u9999\u968f\u559c\uff0c\u4f60\u4eec\u73b0 \u5728\u5c31\u53ef\u8fdb\u5165\u6b63\u6bbf\uff0c\u5728\u9636\u4e0b\u6392\u5217\uff0c\u4e0d\u51c6\u62e5\u6324\u4e89\u5148\uff0c\u81ea\u6709\u6cd5\u575b\u4f7f\u8005\u6536\u4f60\u4eec\u7684\u4f5b\u9999\uff0c\u66ff\u4f60\u4eec\u901a\u540d\u7980 \u544a\u3002\u201d\u963f\u96be\u5148\u8d70\uff0c\u63a5\u7740\u90a3\u4e9b\u5bbe\u5ba2\u4fbf\u9c7c\u8d2f\u800c\u5165\uff0c\u6392\u5217\u9636\u4e0b\u3002\u5510\u7ecf\u5929\u81ea\u4ea6\u6742\u5728\u4f17\u4eba\u4e4b\u4e2d\u3002 \u3000\u3000\u5927\u6bbf\u96c4\u4f1f\u975e\u5e38\uff0c\u6bbf\u4e0a\u767e\u4f59\u5587\u561b\uff0c\u9636\u4e0b\u767e\u4f59\u5bbe\u5ba2\uff0c\u5730\u65b9\u8fd8\u662f\u7ef0\u6709\u4f59\u88d5\uff0c\u6bbf\u4e0a\u795e\u9f9b\u6570\u5341\uff0c\u5404 \u5f0f\u4f5b\u50cf\uff0c\u5947\u5f62\u602a\u72b6\uff0c\u5927\u6b8a\u4e2d\u571f\uff0c\u5ffd\u7136\u4f17\u58f0\u4ff1\u5bc2\uff0c\u90a3\u767d\u6559\u6cd5\u738b\u7f13\u7f13\u8d77\u7acb\u3001\u8d70\u5230\u4e3b\u5ea7\u7684\u5982\u6765\u4f5b\u50cf \u4e4b\u524d\uff0c\u71c3\u70b9\u7b2c\u4e00\u652f\u9999\uff0c\u5510\u7ecf\u5929\u6628\u591c\u867d\u66fe\u548c\u4ed6\u4ea4\u624b\uff0c\u800c\u4eca\u59cb\u77a7\u5f97\u771f\u5207\uff0c\u53ea\u89c1\u90a3\u6cd5\u738b\u8eab\u6750\u9b41\u4f1f\uff0c \u9762\u5982\u6ee1\u6708\uff0c\u4e0d\u6012\u800c\u5a01\uff0c\u7aef\u7684\u662f\u6cd5\u76f8\u5e84\u4e25\uff0c\u662f\u4e00\u4e2a\u6709\u9053\u9ad8\u618e\u7684\u6a21\u6837\uff0c\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u5e78\u559c\u6628\u665a\u6ca1 \u6709\u9c81\u83bd\u4ece\u4e8b\uff0c\u4f46\u4ed6\u62ff\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u4f8d\u5973\uff0c\u51b0\u5ddd\u5929\u5973\u5c82\u80af\u4e0e\u4ed6\u5e72\u4f11\u3002 \u3000\u3000\u6cd5\u738b\u70b9\u4e86\u7b2c\u4e00\u652f\u9999\u540e\uff0c\u6cd5\u575b\u4f7f\u8005\u4fbf\u63a5\u53d7\u5bbe\u5ba2\u7684\u85cf\u9999\uff0c\u63d2\u8fdb\u5404\u5ea7\u4f5b\u50cf\u524d\u9762\u7684\u9999\u7089\uff0c\u4ee3\u4e3a\u901a \u540d\u7980\u62a5\u3002\u9999\u70df\u9563\u7ed5\u4e4b\u4e2d\uff0c\u5ffd\u7136\u949f\u58f0\u9f50\u9e23\uff0c\u4f5b\u6bbf\u540e\u8d70\u51fa\u4e24\u961f\u767d\u8863\u5c11\u5973\uff0c\u6bcf\u8fb9\u90fd\u662f\u4e00\u5341\u516b\u4eba\uff0c\u7531 \u4e24\u4e2a\u5e74\u8001\u7684\u201c\u5723\u6bcd\u201d\u7387\u9886\uff0c\u4e2d\u5ba3\u4f5b\u53f7\uff0c\u624b\u821e\u8db3\u8e48\u5730\u5728\u4f5b\u50cf\u4e4b\u524d\uff0c\u968f\u7740\u949f\u58f0\u7684\u8282\u594f\uff0c\u8e51\u8ddf\u8d77\u821e \uff0c\u4e14\u821e\u65e6\u5531\uff0c\u5531\u7684\u662f\u5587\u561b\u6559\u7ecf\u6587\u4e2d\u7684\u4f5b\u66f2\uff0c\u9636\u4e0b\u5bbe\u5ba2\uff0c\u867d\u7136\u5341\u4e5d\u4e0d\u61c2\uff0c\u4f46\u4ea6\u89c9\u5f97\u97f3\u97f5\u60a0\u626c\uff0c \u5341\u5206\u60a6\u8033\u3002\u90a3\u4e9b\u5c0f\u4e2d\u5587\u561b\uff0c\u66f4\u662f\u4e2a\u4e2a\u4f38\u957f\u4e86\u9888\u9879\uff1b\u542c\u5f97\u51fa\u795e\u3002 \u3000\u3000\u90a3\u6cd5\u738b\u62cd\u4e86\u4e24\u4e0b\u624b\u638c\uff0c\u4eea\u5f0f\u5b8c\u6210\uff0c\u4e24\u961f\u5c11\u5973\u9c7c\u8d2f\u9000\u5165\uff0c\u53ea\u810a\u5341\u9886\u961f\u7684\u201c\u5723\u6bcd\u201d\u7559\u7740\u672a\u8d70 \uff0c\u8d70\u5230\u6cd5\u738b\u8ddf\u524d\uff0c\u4f4e\u58f0\u7980\u544a\u3002\u6cd5\u4e0a\u8bf4\u8bdd\uff0c\u5927\u6bbf\u535c\uff0c\u6dee\u6562\u55a7\u54d7\u3002\u5510\u7ecf\u5929\u5185\u529f\u7cbe\u6e5b\uff0c\u542c\u89c9\u6781\u4e3a\u7075 \u654f\uff0c\u53ea\u542c\u5f97\u90a3\u5723\u6bcd\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u529d\u8fc7\u5979\u4e86\uff0c\u5979\u8fd8\u662f\u4e0d\u80af\u7b54\u5141\u3002\u201d\u90a3\u6cd5\u738b\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u9886 \u5979\u51fa\u6765\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u5f26\u98a4\u52a8\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u5927\u6bbf\u65c1\u8fb9\u7684\u6708\u7259\u89d2\uff0c\u60f3\u9053\uff1a\u7b49\u4f1a\u5e7d\u840d\u88ab\u5e26\u51fa\u672a\uff0c\u8981\u4e0d \u8981\u7acb\u5373\u51b2\u4e0a\u524d\u53bb\u5c06\u5979\u6551\u8d70\uff1f \u3000\u3000\u4e3b\u610f\u5c1a\u672a\u6253\u5b9a\uff0c\u53ea\u542c\u5f97\u7ec6\u788e\u7684\u811a\u6b65\u58f0\u4ece\u6bbf\u540e\u8d70\u6765\uff0c\u89d2\u95e8\u4e2d\u767d\u8863\u98d8\u52a8\uff0c\u521a\u624d\u8fdb\u53bb\u7684\u90a3\u4e2a\u5723 \u6bcd\u5df2\u5e26\u4e86\u4e00\u4e2a\u5c11\u5973\u51fa\u6765\u3002\u8fd9\u5239\u90a3\u95ee\uff0c\u5927\u6bbf\u4e0a\u4e0b\uff0c\u5bc2\u9759\u65e0\u58f0\uff0c\u6570J\u4eba\u4e2a\u4e2a\u4ef0\u5934\u800c\u89c6\uff0c\u8fde\u4e00\u6839\u9488 \u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u62ab\u7740\u767d\u7eb1\u7684\u85cf\u65cf\u5c11\u5973\uff0c\u53ea\u89c1\u5979\u7d27\u7d27\u95ed\u7740\u5634\u513f\uff0c\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\u51dd\u671b\u7740\u524d\u9762 \u7684\u4eba\u7fa4\uff0c\u663e\u51fa\u4e00\u6d3e\u832b\u7136\u7684\u795e\u8272\uff0c\u51b0\u51b7\u7684\u9762\u5b54\uff0c\u77a7\u4e0d\u51fa\u4e00\u70b9\u8868\u60c5\uff0c\u65e2\u4e0d\u662f\u5bb3\u6015\u6050\u60e7\u3002\u4e5f\u4e0d\u662f\u6124 \u6012\u63a9\u4f24\uff0c\u9762\u5bf9\u7740\u6570\u767e\u7684\u964c\u751f\u4eba\uff0c\u5979\u8fde\u7709\u6bdb\u4e5f\u4e0d\u52a8\u4e00\u4e0b\uff0c\u597d\u50cf\u9762\u524d\u4e00\u5207\u90b5\u4e0d\u5b58\u5728\u4f3c\u7684\uff0c\u6bbf\u4e0a\u7ea2 \u70db\u5149\u8f89\uff0c\u5982\u540c\u767d\u663c\uff0c\u5728\u70db\u5149\u6620\u7167\u4e4b\u4e0b\uff0c\u66f4\u663e\u5f97\u51b7\u8273\u65e0\u4f26\uff0c\u5979\u7684\u9762\u8c8c\u6709\u70b9\u50cf\u51b0\u5ddd\u5929\u5973\uff0c\u4f46\u5374\u5e76 \u4e0d\u662f\u51b0\u5ddd\u5929\u5973\u7684\u4f8d\u5973\u5e7d\u840d\uff01 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u5fc3\u4ee5\u4e3a\u8fd9\u88ab\u64d2\u7684\u5c11\u5973\u5b9a\u662f\u5e7d\u840d\u3002\u54ea\u77e5\u5374\u662f\u4e00\u4e2a\u4ece\u4e0d\u76f8\u8bc6\u7684\u85cf\u65cf\u5c11\u5973\uff0c\u4f46\u5374\u53c8\u4f3c \u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u9762\u4f3c\u7684\uff0c\u5510\u7ecf\u5929\u60ca\u8be7\u4e4b\u6781\uff0c\u4ed6\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5979\u9707\u4e4b\u540e\uff0c\u9003\u4e0b\u51b0\u5bab\u7684\u4f8d\u5973 \uff0c\u53ea\u6709\u4e00\u4e2a\u5e7d\u840d\uff0c\u8fd9\u5c11\u5973\u65e2\u975e\u5e7d\u840d\uff0c\u4f55\u4ee5\u5979\u53c8\u80fd\u4f7f\u51fa\u4e16\u4e0a\u6240\u65e0\uff0c\u51b0\u5bab\u72ec\u6709\u7684\u51b0\u9b44\u795e\u5f39\uff1f\u5510\u7ecf \u5929\u82e6\u82e6\u601d\u7d22\uff0c\u4e0d\u7981\u5446\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e0d\u77e5\uff0c\u8fd9\u5c11\u5973\u6b63\u662f\u9648\u5929\u5b87\u7684\u5fc3\u4e0a\u4eba\u513f\uff0c\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\u829d\u5a1c\u3002\u5510\u7ecf\u5927\u521d\u4e0a\u51b0\u5bab \u4e0e\u51b0\u5ddd\u5929\u5973\u6bd4\u5251\u4e4b\u65f6\uff0c\u5979\u4e5f\u66fe\u6742\u5728\u4f8d\u5973\u7fa4\u4e2d\u89c2\u770b\uff0c\u53ea\u662f\u90a3\u65f6\u5510\u7ecf\u5929\u5168\u795e\u6ce8\u5728\u51b0\u5ddd\u5929\u5973\u8eab\u4e0a\uff0c \u54ea\u7559\u610f\u5230\u6742\u5728\u4f17\u591a\u4f8d\u5973\u4e2d\u7684\u5979\u3002 \u3000\u3000\u90a3\u5723\u6bcd\u8d70\u5230\u6cd5\u7389\u8ddf\u524d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u5979\u4e86\uff0c\u5979\u4e0d\u4f46\u5a1f\u79c0\u5723\u6d01\uff0c\u8fd8\u4f1a\u51e0\u624b\u6b66\u529f\uff0c\u6211\u672c\u60f3 \u53eb\u5979\u5728\u5c06\u6765\u7684\u8428\u8ff9\u5bfa\u9662\u4e2d\u505a\u5723\u5973\u4e3b\u6301\u7684\uff0c\u54ea\u77e5\u5979\u4e0e\u4f5b\u65e0\u7f18\uff0c\u53ea\u597d\u7f62\u4e86\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\uff0c\u9636\u4e0b\u8bf8 \u4eba\u53ea\u6709\u5510\u7ecf\u5929\u542c\u5f97\u6e05\u695a\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u5ffd\u89c1\u90a3\u85cf\u65cf\u5c11\u5973\u7684\u79cb\u6ce2\u4e00\u8f6c\uff0c\u76ee\u5149\u7f13\u7f13\u79fb\u52a8\uff0c\u671d\u7740\u90a3\u6cd5 \u738b\u770b\u4e86\u4e00\u773c\uff0c\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u60ca\u5f02\u7684\u795e\u8272\uff0c\u76c8\u76c8\u7709\u773c\uff0c\u82e5\u6709\u6240\u601d\uff0c\u4f46\u4ea6\u662f\u4e00\u63a0\u5373\u8fc7\uff0c\u968f\u5373\u53c8\u662f \u51b0\u51b7\u5982\u524d\u3002\u66fe\u4e0e\u5510\u7ecf\u5929\u4ea4\u8fc7\u624b\u7684\u4e24\u4e2a\u767d\u6559\u5587\u561b\uff0c\u8fd9\u65f6\u4e5f\u4f8d\u7acb\u6cd5\u738b\u5de6\u53f3\uff0c\u5176\u4e2d\u4e00\u4eba\u4e0a\u524d\u7980\u9053\uff1a \u201c\u8fd9\u5996\u5973\u66fe\u7528\u90aa\u6bd2\u6697\u5668\u6253\u4f24\u4e86\u54b1\u4eec\u5bfa\u4e2d\u7684\u5587\u561b\uff0c\u653e\u5979\u4e0d\u5f97\u3002\u201d\u90a3\u6cd5\u738b\u9762\u5bb9\u6c89\u8083\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c \u4e5f\u4e0d\u77e5\u4ed6\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\u3002 \u3000\u3000\u4e0e\u767d\u6559\u6cd5\u738b\u5e76\u80a9\u800c\u5750\u7684\u662f\u5410\u8c37\u6d51\u7684\u5927\u6c57\uff0c\u81ea\u829d\u5a1c\u4e00\u8d70\u51fa\u6765\uff0c\u4ed6\u5c31\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u7740\u5979\uff0c \u8fd9\u65f6\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u6cd5\u738b\u5408\u4ec0\u4e00\u62dc\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c42\u6d3b\u4f5b\u6148\u60b2\uff0c\u9976\u4e86\u8fd9\u4e2a\u5973\u5b50\uff0c\u8ba9\u6211\u5e26\u56de \u5bab\u53bb\u5904\u7f6e\u3002\u6211\u613f\u66ff\u8fd9\u5973\u5b50\u8d4e\u7f6a\uff0c\u91cd\u4fee\u4f5b\u6bbf\uff0c\u518d\u9970\u91d1\u8eab\u3002\u201d \u3000\u3000\u6cd5\u738b\u7ba1\u6559\uff0c\u5927\u6c57\u638c\u653f\uff0c\u5728\u897f\u85cf\u9752\u6d77\u7b49\u5730\u65b9\uff0c\u6559\u6743\u9ad8\u4e8e\u653f\u6743\uff0c\u6cd5\u738b\u5c0a\u4e8e\u5927\u6c57\u3002\u4f46\u767d\u6559\u5587\u561b \uff0c\u9003\u81f3\u9752\u6d77\uff0c\u5230\u5e95\u662f\u6258\u5e87\u4e8e\u5927\u6c57\u6cbb\u4e0b\uff0c\u9760\u5927\u6c57\u4f5c\u62a4\u6cd5\u3002\u5410\u8c37\u6d51\u5927\u6c57\u6b64\u8a00\u4e00\u51fa\uff0c\u767d\u6559\u6cd5\u738b\u7709\u5934 \u4e00\u76b1\uff0c\u770b\u6765\u751a\u662f\u8e0c\u8e87\uff0c\u4e45\u4e45\u5c1a\u672a\u7b54\u8bdd\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6697\u81ea\u52a8\u6012\uff0c\u542c\u8fd9\u8bf4\u8bdd\uff0c\u5410\u8c37\u6d51\u5927\u6c57\u5fc3\u4e2d\u5b9e\u662f\u4e0d\u6000\u597d\u610f\uff0c\u8fd9\u5c11\u5973\u867d\u7136\u4e0d\u662f\u5e7d\u840d\uff0c\u5510 \u7ecf\u5929\u4ea6\u4e0d\u613f\u5979\u843d\u5728\u5927\u6c57\u624b\u4e2d\uff0c\u5fc3\u5934\u6b63\u81ea\u76d8\u7b97\u6551\u5979\u4e4b\u8ba1\uff0c\u6bbf\u4e0a\u8d35\u5bbe\u5e2d\u4e2d\uff0c\u5ffd\u7136\u8d70\u51fa\u4e00\u4eba\uff0c\u4ea6\u8d70 \u5230\u6cd5\u738b\u8ddf\u524d\u5408\u4ec0\u4e00\u62dc\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5996\u5973\u4f3c\u4e4e\u522b\u6709\u6765\u5386\uff0c\u6c42\u6d3b\u4f5b\u6069\u51c6\uff0c\u8ba9\u6211\u8bd5\u5979\u4e00\u8bd5\u3002\u201d \u5510\u7ecf\u5927\u5728\u9636\u4e0b\u770b\u5f97\u5206\u660e\uff0c\u8fd9\u4eba\u7adf\u7136\u662f\u4e0e\u8840\u795e\u5b50\u4e00\u9053\uff0c\u66fe\u5728\u62c9\u8428\u7f09\u62ff\u9f99\u7075\u77eb\u7684\u90a3\u4e2a\u4e91\u7075\u5b50\u3002 \u3000\u3000\u4e91\u7075\u5b50\u662f\u6e05\u5ef7\u5927\u5185\u7684\u201c\u4f9b\u5949\u201d\uff0c\u4e3a\u9f99\u7075\u77eb\u4e4b\u4e8b\uff0c\u56de\u4eac\u7980\u62a5\uff0c\u8def\u8fc7\u9752\u6d77\uff0c\u4ed6\u4e0e\u767d\u6559\u6cd5\u738b\u4ee5 \u524d\u76f8\u8bc6\uff0c\u7279\u6765\u89c2\u793c\u7684\u3002\u4ee5\u4e91\u7075\u5b50\u7684\u8eab\u4efd\uff0c\u4e43\u662f\u6e05\u5ef7\u7684\u4f7f\u8005\uff0c\u5410\u8c37\u6d0b\u7684\u5927\u6c57\u867d\u7136\u5272\u636e\u4e00\u65b9\uff0c\u5f62 \u540c\u72ec\u7acb\uff0c\u540d\u4e49\u4e0a\u5230\u5e95\u662f\u53d7\u6e05\u5ef7\u7ba1\u8f96\uff0c\u542c\u4e86\u4e91\u7075\u5b50\u4e4b\u8a00\uff0c\u5fc3\u4e2d\u867d\u7136\u607c\u6012\uff0c\u5374\u4e5f\u4e0d\u4fbf\u53d1\u4f5c\uff0c\u4f46\u4ea6 \u53d8\u4e86\u9762\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5f85\u600e\u751f\u8bd5\u5979\uff1f\u201d\u4e91\u7075\u5b50\u7b11\u9053\uff1a\u201c\u5927\u6c57\u653e\u5fc3\uff0c\u6211\u603b\u4e0d\u81f3\u4e8e\u6bc1\u4e86\u5979\u7684 \u5bb9\u989c\u4fbf\u662f\u3002\u201d\u4e91\u7075\u5b50\u81ea\u6043\u6b66\u529f\uff0c\u7adf\u7136\u4e0d\u7406\u5410\u8c37\u6d51\u5927\u6c57\u7684\u607c\u6012\uff0c\u4ea6\u672a\u5f97\u6cd5\u738b\u7684\u70b9\u5934\uff0c\u4fbf\u8d70\u5230\u4e86 \u829d\u5a1c\u9762\u524d\uff0c\u4f38\u51fa\u53cc\u6307\uff0c\u5ffd\u7136\u7167\u7740\u829d\u5a1c\u80f8\u524d\u7684\u201c\u4e73\u7a81\u7a74\u201d\u4e00\u6233\uff0c\u8fd9\u4e00\u62db\u65e2\u8f7b\u8584\u53c8\u72e0\u6bd2\uff0c\u770b\u6765\u662f \u4e91\u7075\u5b50\u6709\u610f\u8feb\u829d\u5a1c\u51fa\u624b\u62db\u67b6\u3002 \u3000\u3000\u539f\u6765\u4e91\u7075\u5b50\u5230\u4e86\u54c8\u5409\u5c14\uff0c\u542c\u8bf4\u829d\u5a1c\u66fe\u7528\u8fc7\u90a3\u79cd\u4f1a\u4ee4\u4eba\u53d1\u51b7\u7684\u6697\u5668\uff0c\u4e5f\u4e0e\u5510\u7ecf\u5929\u4e00\u6837\uff0c\u6000 \u7591\u829d\u5a1c\u662f\u51b0\u5ddd\u5929\u5973\u7684\u4f8d\u5973\u5e7d\u840d\uff0c\u89c1\u4e86\u4e4b\u540e\uff0c\u59cb\u77e5\u4e0d\u662f\u3002\u4f46\u51b0\u9b44\u795e\u5f39\u53ea\u6709\u51b0\u5bab\u624d\u6709\uff0c\u4e91\u7075\u5b50\u867d \u7136\u672a\u66fe\u76ee\u51fb\u829d\u5a1c\u4f7f\u8fc7\u51b0\u9b44\u795e\u5f39\uff0c\u5fc3\u4e2d\u5230\u5e95\u7591\u56e2\u83ab\u91ca\uff0c\u6000\u7591\u5979\u7eb5\u4e0d\u662f\u51b0\u5bab\u4f8d\u5973\uff0c\u4e5f\u5fc5\u6709\u70b9\u6e0a\u6e90 \u3002\u4e91\u7075\u5b50\u592b\u5987\u5403\u8fc7\u51b0\u5ddd\u5929\u5973\u7684\u5927\u4e8f\uff0c\u5bf9\u51b0\u5ddd\u5929\u5973\u6068\u4e4b\u4eba\u9aa8\uff0c\u6545\u6b64\u7acb\u5fc3\u8981\u4e0e\u829d\u5a1c\u4e3a\u96be\uff0c\u6709\u610f\u8bd5 \u5979\u4e00\u8bd5\uff0c\u770b\u5979\u7684\u6b66\u529f\uff0c\u662f\u5426\u4e0e\u51b0\u5ddd\u5929\u5973\u4e00\u8def\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u53cc\u6307\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u7f13\u7f13\u6233\u4e0b\uff0c\u5410\u8c37\u6d51\u5927\u6c57\u52c3\u7136\u5927\u6012\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f11\u5f97\u4eb5\u8bfb\u5723 \u5973\uff01\u201d\u4e00\u8dc3\u800c\u8d77\uff0c\u559d\u624b\u4e0b\u4e0a\u524d\u62e6\u963b\uff0c\u4e91\u7075\u5b50\u5934\u4e5f\u4e0d\u56de\uff0c\u624b\u6307\u5df2\u7136\u6233\u5230\u829d\u5a1c\u80f8\u524d\uff0c\u5ffd\u5730\u4e00\u58f0\u5389 \u53eb\uff0c\u5012\u8dc3\u4e08\u4f59\uff0c\u80cc\u5fc3\u4e00\u649e\uff0c\u5c06\u5927\u6c57\u624b\u4e0b\u7684\u4e24\u540d\u6b66\u58eb\u649e\u5f97\u56db\u811a\u671d\u5929\uff0c\u722c\u4e0d\u8d77\u6765\u3002\u800c\u4e91\u7075\u5b50\u4ea6\u6367 \u7740\u624b\u8155\uff0c\u989d\u4e0a\u6c81\u51fa\u9ec4\u8c46\u822c\u5927\u5c0f\u7684\u6c57\u73e0\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u7136\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u5927\u4e3a\u60ca\u9a87\uff0c\u4e91\u7075\u5b50\u7684\u6b66\u529f\u4ed6\u7d20\u6240\u77e5\u9053\uff0c\u5e76\u4e0d\u5728\u4ed6\u4e4b\u4e0b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u867d \u4f1a\u6b66\u529f\uff0c\u4f46\u6bd4\u8d77\u6211\u5ea7\u4e0b\u7684\u767d\u8863\u5587\u561b\uff0c\u4ea6\u8fd8\u76f8\u5dee\u751a\u8fdc\uff0c\u4f55\u4ee5\u4e91\u7075\u5b50\u4f1a\u5403\u4e86\u5979\u7684\u6697\u7b97\uff1f\u201d\u63a0\u9a87\u4e4b \u4e0b\uff0c\u7adf\u81ea\u5fd8\u4e86\u201c\u6d3b\u4f5b\u2019\u2019\u7684\u8eab\u4efd\uff0c\u79bb\u5ea7\u800c\u8d77\uff0c\u4e0a\u524d\u5bdf\u770b\u3002 \u3000\u3000\u5ffd\u89c1\u90a3\u85cf\u65cf\u5c11\u5973\u56de\u8eab\u5408\u4ec0\uff0c\u76c8\u76c8\u8bf4\u9053\uff0c\u201c\u8c22\u6d3b\u4f5b\u6069\u5178\uff1b\u5c0f\u5973\u5b50\u613f\u820d\u8eab\u732e\u4f5b\uff0c\u6c38\u4e3a\u4f8d\u5973\u3002 \u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u201c\u5723\u6bcd\u201d\u4e0e\u4e00\u4f17\u5587\u561b\u90fd\u5927\u611f\u60ca\u5947\uff1a\u8fd9\u5973\u5b50\u66fe\u7edd\u98df\u4e24\u65e5\uff0c\u4efb\u51ed\u5982\u4f55\u529d\u89e3\uff0c\u603b\u662f\u4e0d \u53d1\u4e00\u58f0\uff0c\u4e0d\u6599\u5230\u4e86\u6b64\u65f6\uff0c\u5374\u7a81\u7136\u5728\u6cd5\u738b\u9762\u524d\u5e94\u5141\u3002\u90a3\u201c\u5723\u6bcd\u201d\u9996\u5ba3\u4f5b\u53f7\uff0c\u8ba4\u4e3a\u90a3\u662f\u6d3b\u4f5b\u7684\u611f \u53ec\u3002 \u3000\u3000\u6cd5\u738b\u773c\u5229\uff0c\u5374\u89c1\u829d\u5a1c\u80f8\u524d\uff0c\u591a\u4e86\u7532\u4ef6\u9970\u54c1\uff0c\u4e43\u662f\u4e00\u5757\u7528\u8c61\u7259\u96d5\u6210\u7684\u5c0f\u5706\u724c\uff0c\u4e0a\u9762\u5199\u6709\u51e0 \u884c\u68b5\u6587\uff0c\u7adf\u7136\u662f\u5587\u561b\u6559\u4e2d\uff0c\u9881\u7ed9\u5fb7\u884c\u5723\u6d01\u7684\u5584\u7537\u4fe1\u5973\u7684\u62a4\u8eab\u7075\u7b26\u3002\u5587\u561b\u6559\u4ee5\u767d\u8c61\u4e3a\u5c0a\u8d35\u4e4b\u7269 \uff0c\u56e0\u6b64\u7528\u8c61\u7259\u96d5\u6210\u7684\u7075\u7b26\u6700\u4e3a\u73cd\u8d35\uff0c\u9881\u7ed9\u5973\u5b50\u7684\u66f4\u662f\u6781\u4e3a\u5c11\u6709\uff0c\u829d\u5a1c\u672c\u6765\u662f\u6c81\u5e03\u85e9\u738b\u7684\u72ec\u751f \u5973\u513f\uff0c\u6c81\u5e03\u85e9\u738b\u5728\u4ee5\u524d\u7684\u897f\u85cf\u8bf8\u85e9\u4e4b\u51b2\uff0c\u9886\u5730\u6700\u5e7f\uff0c\u52bf\u529b\u6700\u5927\uff0c\u73ed\u7985\u5587\u561b\u4ea6\u66fe\u9760\u4ed6\u62a4\u6cd5\uff1b\u6240 \u4ee5\u8d50\u4e86\u4ed6\u5973\u513f\u4e00\u9762\u8c61\u7259\u7075\u7b26\uff0c\u65e0\u975e\u662f\u4fdd\u4f51\u5979\u5409\u7965\u5982\u610f\u3001\u767e\u90aa\u4e0d\u4fb5\u7684\u610f\u601d\u3002\u5587\u561b\u6559\u4e2d\u76f8\u4fe1\u8fd9\u79cd \u7075\u7b26\u6709\u5f88\u5927\u7684\u9a71\u90aa\u6548\u529b\uff0c\u975e\u4e0e\u4f5b\u6709\u7f18\uff0c\u6216\u88ab\u8ba4\u4e3a\u5fb7\u884c\u5723\u6d01\u7684\u5584\u7537\u4fe1\u5973\uff0c\u6d3b\u4f5b\u4e0d\u4f1a\u6069\u8d50\uff0c\u4f46\u829d \u5a1c\u5374\u662f\u4f8b\u5916\uff0c\u5979\u4e09\u5c81\u4e4b\u65f6\uff0c\u7236\u4eb2\u5c31\u6c42\u4e86\u6d3b\u4f5b\u628a\u8fd9\u9053\u7075\u7b26\u8ba9\u5979\u4f69\u6234\u4e86\u3002 \u3000\u3000\u9ec4\u6559\u767d\u6559\u867d\u7136\u4f5c\u5bf9\uff0c\u4f46\u5374\u662f\u540c\u51fa\u4e00\u6e90\u3002\u9ec4\u6559\u6d3b\u4f5b\u4ee5\u201c\u4f5b\u201d\u7684\u540d\u4e49\u5e87\u62a4\u7684\u5973\u5b50\uff0c\u767d\u6559\u4ea6\u5f53 \u5c0a\u91cd\uff0c\u90a3\u6cd5\u738b\u4e0d\u77e5\u9053\u829d\u5a1c\u672c\u6765\u7684\u8eab\u4efd\uff0c\u8fd8\u4ee5\u4e3a\u5979\u539f\u5c31\u662f\u9ec4\u6559\u4e2d\u7684\u5723\u5973\uff0c\u542c\u5979\u8bf4\u613f\u6c38\u8fdc\u732e\u8eab\u767d \u6559\uff0c\u4f5c\u4e3a\u4ed6\u6559\u4e2d\u7684\u5723\u5973\uff1b\u81ea\u7136\u662f\u5fc3\u4e2d\u6b22\u559c\u3002\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u4e91\u7075\u5b50\u54c7\u54c7\u5927\u53eb\uff0c\u539f\u6765\u662f\u4ed6\u81ea \u5df1\u901a\u4e86\u7a74\u9053\uff0c\u76db\u6012\u4e4b\u4e0b\uff1b\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u8fd8\u4e0d\u80fd\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u628a\u624b\u4e00\u6325\uff0c\u9053\uff1a\u201c\u547c\u513f\u9c81\u8d64\uff0c\u54c8\u4e5e\u5143\u62c9\u739b\u8d64\u8d64\u3002\u201d\u8fd9\u662f\u85cf\u8bed\uff0c\u610f\u601d\u662f\u8bf4\uff0c\u4f60\u8fd8 \u8981\u8fd0\u6c14\u7597\u4f24\uff0c\u4e0d\u53ef\u5984\u52a8\u3002\u4e91\u7075\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u500f\u7136\u6b62\u6b65\u3002\u5ffd\u89c1\u5410\u8c37\u6d51\u5927\u6c57\u5e26\u7740\u4e24\u540d\u6b66\u58eb\uff0c\u5954\u4e0a \u524d\u6765\uff0c\u5927\u58f0\u547c\u559d\u9053\uff1a\u201c\u628a\u8fd9\u91ce\u4eba\u64b5\u8d70\uff0c\u54fc\uff0c\u54fc\uff0c\u8c01\u6562\u4fb5\u72af\u6211\u7684\u5723\u5973\u3002\u201d\u4e24\u4e2a\u6b66\u58eb\u53bb\u64b5\u4e91\u7075\u5b50 \uff0c\u5927\u6c57\u5374\u5954\u5411\u829d\u5a1c\u3002\u767d\u6559\u6cd5\u738b\u5fae\u5fae\u4e00\u7b11\uff0c\u8f6c\u5934\u8bf4\u9053\uff1a\u201c\u5927\u6c57\uff0c\u4f60\u8bf4\u5f97\u5f88\u5bf9\uff0c\u5979\u73b0\u5728\u5df2\u662f\u6211\u6559 \u4e0b\u7684\u5723\u5973\uff0c\u8c01\u4e5f\u4e0d\u80fd\u4fb5\u72af\u5979\u4e86\u3002\u201d\u5410\u8c37\u6d51\u5927\u6c57\u500f\u7136\u53d8\u8272\uff0c\u5782\u624b\u8bf4\u9053\uff1a\u201c\u6709\u6d3b\u4f5b\u5e87\u62a4\uff0c\u90a3\u6211\u5c31 \u4e0d\u5fc5\u591a\u4e8b\u5566\u3002\u201d\u6cd5\u738b\u4ee5\u6d3b\u4f5b\u7684\u8eab\u4efd\u5728\u5723\u6bbf\u4e4b\u4e0a\u8bf4\u51fa\u8981\u75b5\u62a4\u829d\u5a1c\u7684\u8bdd\uff0c\u5410\u8c37\u6d51\u5927\u6c57\u7eb5\u7136\u5fc3\u6709\u4e0d \u7518\uff0c\u4e5f\u4e0d\u6562\u518d\u5411\u6cd5\u738b\u6c42\u7d22\u4e86\u3002\u6bbf\u4e0a\u7684\u5587\u561b\u90fd\u611f\u5947\u602a\uff0c\u6cd5\u738b\u7adf\u80af\u4e3a\u4e86\u8fd9\u4e2a\u4e0d\u77e5\u540d\u7684\u85cf\u65cf\u5c11\u5973\uff0c \u7b2c\u4e00\u6b21\u548c\u5927\u6c57\u62ac\u6760\u3002\u4f17\u4eba\u7684\u9601\u5149\u90fd\u662f\u96c6\u4e2d\u5728\u6cd5\u738b\u548c\u5927\u6c57\u7684\u8eab\u4e0a\uff0c\u5927\u6c57\u7684\u9762\u8272\u663e\u5f97\u751a\u662f\u5c34\u5c2c\uff0c \u80cc\u8f6c\u4e86\u8eab\uff0c\u8fd8\u672a\u4e3e\u6b65\uff0c\u5ffd\u53c8\u542c\u5f97\u201c\u7830\u7830\u2019\u2019\u4e24\u58f0\u5de8\u54cd\uff0c\u539f\u6765\u662f\u81ea\u5df1\u7684\u4e24\u540d\u6b66\u58eb\uff0c\u53c8\u88ab\u4e91\u7075\u5b50 \u6454\u5012\u5730\u4e0a\u3002 \u3000\u3000\u5410\u8c37\u6d51\u5927\u6c57\u52c3\u7136\u5927\u6012\uff0c\u4ed6\u5948\u4f55\u4e0d\u4e86\u6d3b\u4f5b\uff0c\u628a\u4e00\u8154\u6012\u6c14\u90fd\u53d1\u6cc4\u5728\u4e91\u7075\u5b50\u8eab\u4e0a\uff0c\u5927\u58f0\u559d\u9053\uff1a \u201c\u6765\u4eba\u5566\uff01\u201d\u4ed6\u5e26\u6765\u7684\u5728\u9636\u4e0b\u62a4\u536b\u7684\u6b66\u58eb\u90fd\u5954\u4e0a\u6bbf\u6765\u3002\u773c\u89c1\u5c31\u662f\u4e00\u573a\u56f4\u6bb4\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6742\u5728\u9636\u4e0b\u7684\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u4e3e\u5934\u4ef0\u671b\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u8fd9\u5c40\u9762\u53ef\u96be\u6536\u62fe\uff0c\u4e14\u770b\u6cd5\u738b\u5982\u4f55\u5e94 \u4ed8\uff1f\u6cd5\u738b\u7f13\u7f13\u8d70\u5411\u5927\u6c57\uff0c\u80cc\u5411\u829d\u5a1c\uff0c\u5ffd\u6709\u4e24\u6761\u9ed1\u5f71\u75be\u5982\u9e70\u96bc\u7684\u4ece\u6cd5\u738b\u8eab\u8fb9\u7a9c\u8fc7\uff0c\u5954\u5411\u829d\u5a1c\uff0c \u53cc\u53cc\u51fa\u624b\uff0c\u6402\u5934\u4fbf\u6293\uff0c\u8fd9\u4e24\u4eba\u5374\u662f\u6cd5\u738b\u7684\u62a4\u575b\u5f1f\u5b50\uff0c\u4e5f\u5373\u662f\u66fe\u4e0e\u5510\u7ecf\u5929\u4ea4\u8fc7\u624b\u7684\u90a3\u4e24\u4e2a\u767d\u6559 \u5587\u561b\u3002\u8fd9\u4e24\u4e2a\u5587\u561b\u4ee5\u524d\u5949\u6cd5\u738b\u4e4b\u547d\u8fdb\u897f\u85cf\u62a2\u593a\u91d1\u672c\u5df4\u74f6\u4e4b\u65f6\uff0c\u66fe\u5f97\u8fc7\u4e91\u7075\u5b50\u7684\u52a9\u529b\uff0c\u8fd9\u65f6\u89c1 \u4e91\u7075\u5b50\u53d7\u4f24\uff0c\u4ed6\u4e24\u4eba\u751f\u6027\u9c81\u83bd\uff0c\u4e5f\u4e0d\u53bb\u60f3\u4e91\u7075\u5b50\u7684\u6b66\u529f\u6bd4\u4ed6\u4eec\u5f3a\u788d\u591a\uff0c\u53ea\u9053\u4e91\u7075\u5b50\u662f\u53d7\u4e86\u829d \u5a1c\u6240\u4f24\uff0c\u800c\u829d\u5a1c\u7684\u6697\u5668\u5374\u662f\u4ed6\u4eec\u6240\u80fd\u514b\u5236\u3002 \u3000\u3000\u6cd5\u738b\u5fc3\u4e2d\u65b9\u81ea\u601d\u91cf\u5982\u4f55\u8c03\u89e3\uff0c\u5f85\u53d1\u89c9\u4e4b\u65f6\uff0c\u62e6\u963b\u5df2\u6765\u4e0d\u53ca\uff0c\u6b63\u60f3\u51fa\u58f0\u559d\u6b62\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0 \u6e05\u8106\u7684\u7b11\u58f0\uff0c\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u767b\u65f6\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u8df3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e24\u4e2a \u5c11\u5973\u7b11\u76c8\u76c8\u7684\u8d70\u4e0a\u5723\u6bbf\uff0c\u524d\u9762\u7684\u5c11\u5973\u4e00\u8eab\u6e56\u6c34\u8272\u7684\u8863\u88f3\uff0c\u8138\u5982\u65b0\u6708\uff0c\u6d45\u753b\u53cc\u7709\uff0c\u78a7\u7eff\u7684\u773c\u73e0 \u6709\u5982\u9ed1\u591c\u4e2d\u95ea\u95ea\u653e\u5149\u7684\u4e24\u9897\u5b9d\u77f3\uff0c\u59ff\u5bb9\u6de1\u96c5\uff0c\u4ee4\u4eba\u4e00\u89c1\u5c31\u8d77\u4e86\u4e00\u79cd\u98d8\u98d8\u51fa\u5c18\u7684\u611f\u89c9\uff0c\u51e0\u7591\u662f \u7d20\u5a25\u9752\u5973\uff0c\u8c2a\u843d\u4eba\u95f4\uff0c\u90a3\u7edd\u4e16\u59ff\u5bb9\uff0c\u628a\u6bbf\u4e0a\u7684\u829d\u5a1c\u4e5f\u6bd4\u4e86\u4e0b\u53bb\u3002\u970e\u65f6\u95f4\uff0c\u8fde\u53bb\u64b5\u4e91\u7075\u5b50\u7684\u90a3 \u4e9b\u6b66\u58eb\u4e5f\u90fd\u4e0d\u7531\u81ea\u5df1\u7684\u505c\u4e86\u4e0b\u6765\uff0c\u5446\u5446\u5411\u5979\u6ce8\u89c6\u3002 \u3000\u3000\u540e\u9762\u7684\u90a3\u4e2a\u5c11\u5973\uff0c\u4e5f\u662f\u4e00\u5f0f\u6253\u626e\uff0c\u4f46\u5934\u4e0a\u7684\u79c0\u53d1\u5374\u6210\u4e24\u6761\u97ad\u5b50\uff0c\u675f\u4ee5\u7ea2\u7eeb\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c \u73b0\u51fa\u4e00\u8138\u987d\u76ae\u7684\u7a1a\u6c14\uff0c\u8ddf\u7740\u524d\u5934\u7684\u5c11\u5973\uff0c\u5c31\u597d\u50cf\u4e2b\u9b1f\u8ddf\u7740\u5c0f\u59d0\u4e00\u6837\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u4e3b\u4eba\u7684\u4ed9\u59ff \u8131\u4fd7\uff0c\u5374\u4e5f\u7f8e\u8273\u5982\u82b1\u3002\u5927\u6bbf\u4e0a\u4e0b\uff0c\u6709\u56db\u4e94\u767e\u4eba\u4e4b\u591a\uff0c\u5916\u9762\u8fd8\u6709\u62a4\u575b\u7684\u5587\u561b\u5f1f\u5b50\uff0c\u8fd9\u4e24\u4e2a\u5c11\u5973 \u7a81\u5982\u5176\u6765\uff0c\u7adf\u65e0\u4e00\u4eba\u53d1\u89c9\u3002 \u3000\u3000\u5510\u7ecf\u5929\u867d\u6599\u5230\u51b0\u5ddd\u5929\u5973\u4f1a\u5728\u6b64\u5730\uff0c\u5374\u60f3\u4e0d\u5230\u5979\u4f1a\u5728\u8fd9\u4e2a\u573a\u5408\u4e4b\u4e0b\u7a81\u7136\u51fa\u73b0\uff1a\u51e0\u4e4e\u5fcd\u4e0d\u4f4f \u53eb\u51fa\u58f0\u6765\u3002\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u5e26\u7740\u5e7d\u840d\u8f7b\u79fb\u83b2\u6b65\uff0c\u500f\u5ffd\u4fbf\u5230\u4e86\u90a3\u85cf\u65cf\u5c11\u5973\u7684\u8eab\u8fb9\u3002\u90a3\u4e24\u4e2a\u767d\u6559\u5587 \u561b\u521a\u521a\u843d\u5730\uff0c\u8ba4\u51fa\u662f\u4fdd\u62a4\u91d1\u74f6\u7684\u51b0\u5ddd\u5929\u5973\uff0c\u52c3\u7136\u5927\u6012\uff0c\u56db\u62f3\u9f50\u51fa\uff0c\u51b0\u5ddd\u5929\u5973\u811a\u6b65\u4e1d\u6beb\u4e0d\u52a8\uff0c \u8863\u8896\u5ffd\u7684\u4e00\u6325\u4e00\u5377\uff0c\u8f7b\u8f7b\u4e00\u9001\uff0c\u4e24\u4e2a\u5587\u561b\u6c34\u725b\u822c\u7684\u8eab\u8eaf\uff0c\u7adf\u7136\u98de\u51fa\u4e86\u4e00\u4e08\u5f00\u5916\uff0c\u76f4\u6eda\u5230\u6cd5\u738b \u7684\u811a\u4e0b\uff0c\u8fd9\u662f\u6700\u4e0a\u4e58\u7684\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\uff01\u51b0\u5ddd\u5929\u5973\u4e00\u628a\u62c9\u7740\u829d\u5a1c\uff0c\u4fbf\u5411\u5916\u8d70\uff0c\u8fd9\u4e00\u77ac \u95f4\uff0c\u4f17\u4eba\u7684\u76ee\u5149\u90fd\u8ddf\u7740\u6ce8\u89c6\u90a3\u88ab\u6454\u7684\u4e24\u4e2a\u767d\u6559\u5587\u561b\uff0c\u53ea\u6709\u5510\u7ecf\u5929\u76ee\u4e0d\u8f6c\u775b\u7684\u76ef\u7740\u51b0\u5ddd\u5929\u5973\uff0c \u53ea\u89c1\u5979\u773c\u775b\u7728\u4e86\u4e24\u4e0b\uff0c\u4f3c\u4e4e\u662f\u89c1\u5230\u4e86\u829d\u5a1c\u6240\u4f69\u6234\u7684\u7075\u7b26\uff0c\u8f7b\u8f7b\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u829d\u5a1c\u4e0e\u5979\u8033 \u9b13\u53ae\u78e8\uff0c\u4f3c\u4e4e\u5728\u5979\u7684\u8033\u8fb9\u6084\u6084\u7684\u8bf4\u4e86\u4e24\u53e5\u8bdd\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u6c89\u58f0\u559d\u9053\uff0c\u201c\u90fd\u7ed9\u6211\u7ad9\u4f4f\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u500f\u5730\u4e5f\u5230\u4e86\u51b0\u5ddd\u5929\u5973\u8eab\u8fb9\u3002\u5510\u7ecf\u5929\u5fc3 \u4e2d\u5927\u6025\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u90fd\u8db3\u4ee5\u9707\u4e16\u9a87\u4fd7\uff0c\u4e00\u4ea4\u4e0a\u624b\uff0c\u53ea\u6050\u81ea\u5df1\u4e5f\u62c6\u89e3\u4e0d\u5f00\u3002\u5ffd\u89c1\u90a3\u85cf\u65cf\u5c11\u5973\uff0c \u9000\u4e86\u4e24\u6b65\uff0c\u5411\u7740\u51b0\u5ddd\u5929\u5973\u76c8\u76c8\u4e00\u63d6\uff0c\u6e05\u58f0\u8bf4\u9053\uff1a\u201c\u767d\u6559\u4fee\u5973\uff0c\u62dc\u89c1\u62a4\u6cd5\u3002\u201d\u767d\u6559\u6cd5\u738b\u5403\u4e86\u4e00 \u60ca\uff0c\u773c\u5149\u843d\u5904\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u7684\u80f8\u524d\uff0c\u4e5f\u4f69\u7740\u4e00\u9053\u7075\u7b26\uff0c\u53d1\u6563\u7740\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u6b63\u662f\u4f5b\u6559\u4e2d\u89c6 \u4e3a\u5f02\u5b9d\u7684\u8d1d\u53f6\u7075\u7b26\uff0c\u8fd9\u79cd\u7075\u7b26\uff0c\u9664\u4e86\u6709\u9650\u7684\u51e0\u4e2a\u9ad8\u618e\u6d3b\u4f5b\uff0c\u4ee5\u53ca\u66fe\u4ee5\u5927\u529b\u62a4\u6301\u8fc7\u4f5b\u6cd5\u7684\u4e16\u4e0a \u541b\u738b\u4e4b\u5916\uff0c\u5176\u4ed6\u4f5b\u95e8\u9ad8\u5f1f\uff0c\u4e00\u751f\u4e4b\u4e2d\u4e5f\u672a\u5fc5\u80fd\u89c1\u8fc7\u4e00\u6b21\u3002 \u3000\u3000\u539f\u6765\u51b0\u5ddd\u5929\u5973\u8fd9\u9053\u8d1d\u53f6\u7075\u7b26\uff0c\u662f\u5979\u7684\u6bcd\u4eb2\u534e\u7389\u516c\u4e3b\u9057\u7ed9\u5979\u7684\u3002\u5c3c\u6cca\u5c14\u662f\u4e2a\u4f5b\u6559\u56fd\u5bb6\uff0c\u524d \u4efb\u56fd\u738b\u4e00\u751f\u62a4\u6cd5\uff0c\u6240\u4ee5\u5f97\u4e86\u4e00\u9053\u6807\u660e\u4ed6\u62a4\u6cd5\u8eab\u4efd\u7684\u8d1d\u53f6\u7075\u7b26\u3002\u4ed6\u751f\u524d\u672c\u60f3\u4f9d\u7167\u897f\u65b9\u7684\u7ee7\u627f\u5927 \u6cd5\uff0c\u5c06\u7687\u4f4d\u4f20\u7ed9\u5973\u513f\uff0c\u662f\u4ee5\u8fd9\u9053\u8d1d\u53f6\u7075\u7b26\u4e5f\u5c31\u4f20\u5230\u4e86\u534e\u7389\u516c\u4e3b\u624b\u4e0a\u3002\u51b0\u5ddd\u5929\u5973\u4ee5\u524d\u72ec\u4f4f\u51b0\u5bab \uff0c\u4e0e\u4e16\u9694\u7edd\uff0c\u6bcd\u4eb2\u7ed9\u5979\u7684\u8fd9\u9053\u8d1d\u53f6\u7075\u7b26\uff0c\u5979\u4ece\u672a\u5411\u4eba\u5c55\u793a\uff0c\u8c01\u4e5f\u4e0d\u77e5\u6b64\u4e8b\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8fd9\u9053\u8d1d\u53f6\u7075\u7b26\uff0c\u6bd4\u8d77\u829d\u5a1c\u90a3\u4e2a\u7531\u6d3b\u4f5b\u6240\u8d50\u7684\u62a4\u8eab\u7075\u7b26\uff0c\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\uff0c\u829d\u5a1c\u662f \u201c\u5723\u5973\u201d\u8eab\u4efd\uff0c\u5730\u4f4d\u8fd8\u5728\u5927\u5587\u561b\u4e4b\u4e0b\uff0c\u800c\u51b0\u5ddd\u5929\u5973\u5219\u662f\u201c\u62a4\u6cd5\u201d\u7684\u8eab\u4efd\uff0c\u4e0e\u6d3b\u4f5b\u53ef\u4ee5\u5e73\u8d77\u5e73 \u5750\u3002\u6545\u6b64\u5f53\u51b0\u5ddd\u5929\u5973\u5411\u767d\u6559\u6cd5\u738b\u65bd\u793c\u4e4b\u65f6\uff0c\u767d\u6559\u6cd5\u738b\u4e5f\u606d\u606d\u656c\u656c\u7684\u8fd8\u4e86\u4e00\u793c\u3002\u5728\u573a\u50e7\u4fd7\uff0c\u8fde \u5510\u7ecf\u5929\u5728\u5185\uff0c\u4e0d\u660e\u6240\u4ee5\uff0c\u89c1\u6cd5\u738b\u8fd8\u793c\uff0c\u90fd\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u5510\u7ecf\u5929\u518d\u8f6c\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u5e7d\u840d\u508d\u7740\u90a3\u85cf\u65cf\u5c11\u5973\uff0c\u6b63\u81ea\u53fd\u53fd\u55b3\u55b3\u7684\u8bf4\u4e2a\u4e0d\u4f11\uff0c\u8bed\u58f0\u6781\u4f4e\uff0c \u8bf4\u7684\u53c8\u662f\u85cf\u8bed\uff0c\u5510\u7ecf\u5929\u51dd\u795e\u9759\u542c\uff0c\u53ea\u542c\u5f97\u201c\u8428\u8fe6\u5b97\u201d\u548c\u201c\u9648\u5929\u5b87\u201d\u7b49\u540d\u5b57\uff0c\u90a3\u85cf\u65cf\u5c11\u5973\u4ecd\u662f \u4e00\u6d3e\u6f20\u7136\u7684\u795e\u8272\uff0c\u773c\u5149\u95ea\u70c1\uff0c\u4f3c\u4e4e\u662f\u793a\u610f\u5e7d\u840d\u4e0d\u8981\u591a\u8bf4\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u5927\u7591\uff0c\u5ffd\u542c\u5f97\u767d\u6559\u6cd5\u738b \u6c89\u58f0\u559d\u9053\uff1a\u201c\u566b\uff0c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u6b63\u6307\u7740\u81ea\u5df1\u3002\u539f\u6765\u5510\u7ecf\u5929\u542c\u5f97\u5fd8\u5f62\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u632a\u52a8\u8eab\u5b50\uff0c \u6324\u5230\u4e86\u961f\u4f0d\u524d\u9762\u3002 \u3000\u3000\u4e0e\u6b64\u540c\u65f6\uff0c\u4e91\u7075\u5b50\u4e00\u58f0\u5927\u543c\uff0c\u5ffd\u5730\u5411\u51b0\u5ddd\u5929\u5973\u51b2\u6765\uff0c\u767d\u6559\u6cd5\u738b\u5c55\u8896\u4e00\u62c2\uff0c\u559d\u9053\uff1a\u201c\u4e91\u7075 \u5b50\u4f11\u5f97\u65e0\u793c\uff01\u201d\u4e91\u7075\u5b50\u624b\u6307\u62c8\u7740\u4e00\u6839\u9ed1\u6f06\u53d1\u5149\u7684\u8292\u523a\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u662f\u4ec0\u4e48\uff1f\u8fd9\u662f\u5929\u5c71\u795e \u8292\uff01\u5929\u5c71\u6d3e\u7684\u4eba\u52fe\u7ed3\u8fd9\u4e2a\u5996\u5973\u5230\u6b64\u6363\u4e71\uff0c\u6d3b\u4f5b\uff0c\u4f60\u8fd8\u4e0d\u5c06\u4ed6\u4eec\u62ff\u4e0b\u5417\uff1f\u201d\u539f\u6765\u4e91\u7075\u5b50\u9002\u624d\u6240 \u4e2d\u7684\u6697\u5668\uff0c\u6b63\u662f\u5510\u7ecf\u5929\u5077\u653e\u7684\u5927\u5c71\u795e\u8292\uff0c\u4ed6\u7a74\u9053\u4e00\u89e3\uff0c\u5c31\u8fd1\u4fbf\u5411\u51b0\u5ddd\u5929\u5973\u53d1\u96be\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u5fc3\u4e2d\u4e00\u51db\uff0c\u888d\u8896\u518d\u5c55\uff0c\u559d\u9053\uff1a\u201c\u4e91\u7075\u5b50\u4f11\u5f97\u80e1\u8a00\uff0c\u8fd9\u4f4d\u5973\u83e9\u8428\u662f\u6211\u4f5b\u95e8\u7684\u62a4\u6cd5 \u3002\u201d\u4e91\u7075\u5b50\u88ab\u6cd5\u738b\u4e00\u62c2\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u4f46\u5374\u4e0d\u6562\u5411\u6cd5\u738b\u53d1\u4f5c\u3002\u8fd9\u65f6\u5927\u6c57\u5e26\u6765\u7684\u6b66\u58eb \u5df2\u662f\u7eb7\u7eb7\u5954\u5411\u4e91\u7075\u5b50\uff0c\u4e91\u7075\u5b50\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u624b\u76f4\u4e0a\u76f4\u4e0b\u3002\u628a\u4e00\u7fa4\u6b66\u58eb\u6253\u5f97\u7ffb\u7ffb\u6eda\u6eda\u3002\u5927\u6c57\u53eb \u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u4e91\u7075\u5b50\u63a8\u5f00\u5587\u561b\uff0c\u5954\u4e0b\u77f3\u9636\uff0c\u767b\u65f6\u5927\u4e71\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4ecd\u662f\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u51b0\u5ddd\u5929\u5973\u9002\u624d\u5f53\u4e91\u7075\u5b50\u51b2\u5411\u5979\u65f6\uff0c\u5fae\u5fae\u4e00\u95ea\uff0c\u5f69 \u8896\u8f7b\u8212\uff0c\u4f3c\u4e4e\u662f\u907f\u5f00\u5f3a\u654c\uff0c\u5374\u9762\u7740\u5510\u7ecf\u5929\uff0c\u95ea\u5f00\u4e4b\u9645\uff0c\u90a3\u957f\u8896\u5728\u7a7a\u4e2d\u6325\u821e\u3001\u5377\u4e86\u4e00\u4e2a\u8349\u4e66\u201d \u8d70\u201d\u5b57\uff0c\u5206\u660e\u662f\u5411\u5510\u7ecf\u5929\u793a\u610f\uff0c\u53eb\u4ed6\u901f\u8d70\u3002\u5510\u7ecf\u5929\u66f4\u662f\u8d77\u7591\uff0c\u5ffd\u89c1\u773c\u524d\u4eba\u5f71\u75be\u5954\u5982\u7bad\uff0c\u4e91\u7075 \u5b50\u5df2\u7ecf\u51b2\u81f3\uff0c\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u4e5f\u8ddf\u7740\u5954\u6765\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u2019\u2019\uff0c\u5de6\u624b\u9a84\u6307\u524d\u4f38\uff0c\u53f3\u624b\u865a\u63e1\uff0c\u5411\u540e\u4e00\u62c9\uff0c\u4f5c\u5f20\u5f13\u653e\u7bad\u4e4b\u72b6\uff0c \u8fd9\u6b63\u662f\u592d\u5c71\u6d3e\u4e00\u4e2a\u6781\u5389\u5bb3\u7684\u62db\u6570\uff0c\u540d\u4e3a\uff1a\u201c\u540e\u7fbf\u5c04\u65e5\u201d\uff0c\u524d\u9762\u7528\u7684\u662f\u94c1\u6307\u7985\u529f\uff0c\u540e\u9762\u7528\u7684\u662f \u65f6\u9524\u3002\u4e91\u7075\u5b50\u6b66\u529f\u672c\u6765\u5c31\u900a\u5510\u7ecf\u5929\u4e00\u7b79\uff0c\u66f4\u517c\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u66f4\u975e\u5bf9\u624b\uff0c\u88ab\u5510\u7ecf\u5929\u94c1\u6307\u4e00\u6233\uff0c \u4ed6\u6043\u7740\u6709\u201c\u94c1\u5e03\u886b\u201d\u7684\u6a2a\u7ec3\u529f\u592b\uff0c\u633a\u8d77\u80a9\u5934\uff0c\u5411\u524d\u4e00\u649e\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u80a9\u4e0a\u7684\u9aa8\u5934 \u65ad\u4e86\u4e24\u6839\uff0c\u75db\u5f97\u4ed6\u51e0\u4e4e\u6655\u5012\uff0c\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u6b63\u597d\u5954\u81f3\uff0c\u6070\u53c8\u78b0\u4e0a\u4e86\u5510\u7ecf\u5929\u7684\u8098\u9524\uff0c\u524d\u9762\u7684 \u5587\u561b\u53d7\u4ed6\u7684\u8098\u9524\u4e00\u649e\uff0c\u5411\u540e\u8dcc\u7ffb\uff0c\u53c8\u78b0\u5012\u4e86\u540e\u9762\u7684\u5587\u561b\uff0c\u53d8\u6210\u4e86\u4e24\u4e2a\u6eda\u5730\u846b\u82a6\u3002 \u3000\u3000\u4e91\u7075\u5b50\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u6b66\u529f\u786e\u6709\u8fc7\u4eba\u9020\u8be3\uff0c\u53d7\u4e86\u4e00\u6307\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u5ffd\u5730\u63d0\u4e00\u53e3\u6c14\uff0c\u53c8\u518d\u7ffb \u8eab\u6251\u4e0a\uff0c\u53ea\u89c1\u4e24\u70b9\u5bd2\u5149\uff0c\u9aa4\u7136\u5728\u5728\u5510\u7ecf\u5929\u4e0e\u4e91\u7075\u5b50\u4e4b\u95f4\u6563\u5f00\uff0c\u5510\u7ecf\u5929\u4ee5\u4e3a\u51b0\u5ddd\u5929\u5973\u51fa\u624b\u76f8\u52a9 \uff0c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5ffd\u89c9\u9762\u4e0a\u51b0\u51c9\uff0c\u6e7f\u6f09\u6f09\u7684\u597d\u4e0d\u96be\u53d7\uff0c\u5510\u7ecf\u5929\u672c\u80fd\u7684\u5c06\u8863\u8896\u4e00\u62b9\uff0c\u53ea\u542c\u5f97\u90a3\u4e24\u4e2a \u767d\u6559\u5587\u561b\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u653e\u8d70\u6b64\u4eba\uff01\u201d\u767d\u6559\u6cd5\u738b\u8fd9\u65f6\u4e5f\u770b\u6e05\u4e86\u5510\u7ecf\u5929\u7684\u8eab\u6cd5\uff0c\u8ba4\u51fa\u4ed6\u5c31\u662f \u6628\u665a\u6765\u7684\u8499\u9762\u602a\u5ba2\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u5411\u51b0\u5ddd\u5929\u5973\u7a3d\u9996\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5973\u83e9\u8428\u51fa\u624b\u76f8\u52a9\u201d\uff0c\u5c31\u6b32\u4e0b\u573a\uff0c\u4eb2\u81ea\u6349\u62ff\u3002\u51b0\u5ddd \u5929\u5973\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6d3b\u4f5b\u65e2\u5df2\u8ba4\u6e05\u6b64\u4eba\u9762\u76ee\uff0c\u4f55\u6545\u5c1a\u52a8\u65e0\u660e\u3002\u6d3b\u4f5b\u96be\u9053\u8fd8\u60f3\u4e0e\u897f\u85cf\u7684\u9ec4\u6559\u5927\u52a8 \u5e72\u6208\u4e48\uff1f\u201d \u3000\u3000\u767d\u6559\u6cd5\u738b\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5973\u83e9\u8428\u4f55\u51fa\u6b64\u8a00\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6b64\u4eba\u52a9\u6e05\u5ef7\u4e0e\u9ec4\u6559\u593a\u56de \u91d1\u672c\u5df4\u74f6\uff0c\u6d3b\u4f5b\u60f3\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d\u8fd9\u65f6\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u6b63\u5728\u7834\u53e3\u5927\u9a82\uff0c\u9a82\u5510\u7ecf\u5929\u4ee5\u524d\u5728\u5ce1\u8c37 \u62a2\u593a\u91d1\u74f6\u4f24\u4e86\u4ed6\u4eec\u4e4b\u4e8b\u3002\u6cd5\u738b\u5411\u51b0\u5ddd\u5929\u5973\u770b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u751a\u662f\u7591\u60d1\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5f53\u65f6\u66ff \u9ec4\u6559\u593a\u56de\u91d1\u74f6\uff0c\u6211\u4e5f\u5728\u573a\u3002\u201d\u767d\u6559\u6cd5\u738b\u6000\u7591\u7684\u6b63\u662f\u6b64\u4e8b\uff0c\u4ed6\u4ece\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u53e3\u4e2d\uff0c\u5df2\u77e5\u9053 \u5f53\u65f6\u7684\u4e24\u4e2a\u52b2\u654c\uff0c\u9664\u4e86\u4e00\u4e2a\u5929\u5c71\u6d3e\u7684\u5f1f\u5b50\u4e4b\u5916\uff0c\u8fd8\u6709\u51b0\u5ddd\u5929\u5973\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979 \u662f\u4f5b\u95e8\u62a4\u6cd5\uff0c\u62a4\u7684\u5230\u5e95\u662f\u8c01\uff1f\u96be\u9053\u4e91\u7075\u5b50\u6240\u8bf4\u662f\u771f\uff0c\u5979\u7adf\u662f\u4e0e\u6211\u4f5c\u5bf9\u6765\u7684\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u9ec4\u6559\u767d\u6559\u540c\u51fa\u4e00\u6e90\uff0c\u65e2\u5df2\u8bb2\u548c\uff0c\u5c31\u4e0d\u8be5\u518d\u4e0e\u6b64\u4eba\u4e3a\u96be\u3002\u91d1\u74f6\u7559\u5728 \u62c9\u8428\uff0c\u6b63\u662f\u4e24\u6559\u4e4b\u798f\uff0c\u6d3b\u4f5b\u8be5\u4e0d\u5acc\u6211\u591a\u4e8b\u5427\u3002\u201d\u767d\u6559\u6cd5\u738b\u672c\u662f\u806a\u660e\u6770\u51fa\u4e4b\u58eb\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u51db \u7136\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u679c\u7136\u4e8f\u4e86\u4ed6\u4eec\uff0c\u5f53\u65e5\u5047\u5982\u91d1\u74f6\u8ba9\u6211\u4eec\u593a\u4e86\uff0c\u4eca\u65e5\u5982\u4f55\u80fd\u8ba2\u548c\u7ea6\u3002\u539f\u6765\u4ed6\u4eec \u65e9\u5df2\u5177\u6709\u6df1\u5fc3\uff0c\u6697\u4e2d\u6d88\u6d31\u6211\u4e24\u6559\u7684\u7978\u60a3\u6765\u7684\u3002\u201d\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u5bf9\u51b0\u5ddd\u5929\u5973\u65bd\u4e86\u4e00\u793c\uff0c\u62cd \u4e86\u4e00\u4e0b\u624b\u638c\uff0c\u6025\u5fd9\u53eb\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u56de\u6765\uff0c\u5176\u5b9e\u62a2\u593a\u91d1\u74f6\u4e4b\u4e8b\uff0c\u5168\u662f\u5510\u7ecf\u5929\u7684\u7b56\u5212\uff0c\u51b0\u5ddd\u5929 \u5973\u53ea\u662f\u540e\u6765\u624d\u4ece\u5510\u7ecf\u5929\u7684\u53e3\u4e2d\u77e5\u9053\u4ed6\u7684\u7528\u5fc3\uff0c\u800c\u4eca\u8f6c\u8ff0\u51fa\u6765\uff0c\u4e0d\u8fc7\u662f\u60f3\u6cd5\u738b\u4e0d\u4e0e\u5510\u7ecf\u5929\u4e3a\u96be \u800c\u5df2\u3002 \u3000\u3000\u5510\u7ecf\u5929\u89c1\u51b0\u5ddd\u5929\u5973\u7a81\u7136\u7528\u51b0\u9b44\u795e\u5f39\u88ad\u51fb\uff0c\u4f7f\u81ea\u5df1\u9732\u51fa\u672c\u6765\u9762\u76ee\uff0c\u5148\u662f\u83ab\u660e\u6240\u4ee5\uff0c\u968f\u5373\u60f3 \u8d77\uff0c\u8fd9\u662f\u51b0\u5ddd\u5929\u5973\u8981\u8feb\u81ea\u5df1\u79bb\u5f00\u6b64\u5730\uff0c\u5fc3\u9053\uff1a\u5979\u65e2\u4e0d\u613f\u5728\u6b64\u548c\u6211\u76f8\u8ba4\uff0c\u90a3\u786e\u662f\u975e\u8d70\u4e0d\u53ef\u4e86\u3002 \u4f46\u4e91\u7075\u5b50\u4e0e\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u7f20\u5f97\u751a\u7d27\uff0c\u4ee5\u4e09\u654c\u4e00\uff0c\u5510\u7ecf\u5929\u4e00\u65f6\u4e4b\u95f4\u7adf\u81ea\u4e0d\u80fd\u6446\u8131\uff0c\u4e5f\u65e0\u6687\u5206 \u5fc3\u542c\u51b0\u5ddd\u5929\u5973\u4e0e\u6cd5\u738b\u7684\u8c08\u8bdd\uff0c\u6b63\u5728\u9ad8\u547c\u9163\u6597\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u6cd5\u738b\u5c06\u90a3\u4e24\u4e2a\u5587\u561b\u5524\u4e86\u56de\u53bb\uff0c\u5510\u7ecf \u5929\u6b63\u6101\u767d\u6559\u6cd5\u738b\u4e5f\u4e0b\u573a\u52a8\u624b\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5012\u662f\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u654c\u4eba\u4e09\u53bb\u5176\u4e8c\uff0c\u4e91\u7075\u5b50\u6765\u4e0d\u53ca\u64a4\u8d70\uff0c\u53ea\u542c\u5f97\u5510\u7ecf\u5929\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf7\u6055\u6211\u65e0\u793c\u4e86\u3002 \u201d\u5de6\u53f3\u5f00\u5f13\uff0c\u547c\u547c\u4e24\u638c\uff0c\u90fd\u6253\u4e2d\u4e91\u7075\u5b50\u8981\u5bb3\uff0c\u66f4\u5999\u7684\u662f\u4ed6\u7528\u7684\u4e43\u662f\u9634\u529b\uff0c\u8868\u9762\u5e76\u4e0d\u53d7\u4f24\uff0c\u8fc7 \u540e\u65b9\u624d\u53d1\u4f5c\uff0c\u4e91\u7075\u5b50\u5de6\u53f3\u6811\u654c\uff08\u5927\u6c57\u7684\u6b66\u58eb\u4e5f\u8981\u64d2\u4ed6\uff09\uff0c\u53c8\u8981\u7ef4\u6301\u9762\u5b50\uff0c\u4e0d\u613f\u8bf7\u6c42\u6cd5\u738b\u836b\u5e87 \uff0c\u5373\u5728\u5bfa\u4e2d\u7597\u4f24\uff0c\u5374\u5f3a\u7528\u8f7b\u529f\u63d0\u7eb5\u4e4b\u672f\uff0c\u8df3\u51fa\u5899\u5934\uff0c\u4ee5\u81f3\u4ed6\u540e\u6765\u9759\u517b\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u65b9\u80fd\u590d\u539f \uff0c\u6b66\u529f\u4e5f\u4ece\u6b64\u51cf\u4e86\u4e09\u6210\uff0c\u4e0a\u4eac\u7980\u62a5\u9f99\u7075\u77eb\u7684\u4e8b\u60c5\uff0c\u4e5f\u56e0\u6b64\u5ef6\u8bef\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u89c1\u4e91\u7075\u5b50\u8dc3\u51fa\u5899\u5934\uff0c\u8ddf\u7740\u4e5f\u4ece\u53e6\u4e00\u9762\u9ad8\u5899\u8dc3\u51fa\u3002\u8dc3\u51fa\u4e4b\u65f6\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u51b0 \u5ddd\u5929\u5973\u6b63\u5728\u671d\u7740\u81ea\u5df1\u5fae\u7b11\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u51b0\u5f39\u7a81\u88ad\u72b9\u542b\u7b11\uff0c\u83ab\u6d4b\u82b3\u5fc3\u610f\u82e5\u4f55\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u5927\u6f20\u85cf\u9f99\u3000\u4e5d\u91cd\u60ca\u86f0\u4f0f\u3000\u98ce\u5c18\u4fa0\u9690\u3000\u4e00\u5251\u770b\u96c4\u98de \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u4e03\u56de \u5927\u6f20\u85cf\u9f99\u3000\u4e5d\u91cd\u60ca\u86f0\u4f0f \u98ce\u5c18\u4fa0\u9690\u3000\u4e00\u5251\u770b\u96c4\u98de \u3000\u3000\u5510\u7ecf\u5929\u56de\u5230\u5ba2\u5e97\uff0c\u5ba2\u5e97\u4e2d\u7684\u4f19\u8ba1\u6b63\u5728\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u539f\u6765\u4ed6\u4eec\u89c1\u4e3b\u4eba\u8fdf\u8fdf\u4e0d\u53bb\u8d74\u6cd5\u738b\u4e4b \u7ea6\uff0c\u8d77\u521d\u5c1a\u4e0d\u6562\u50ac\uff0c\u540e\u6765\u89c1\u5929\u5df2\u5165\u9ed1\uff0c\u4e3b\u4eba\u5c1a\u672a\u51fa\u623f\uff0c\u638c\u67dc\u7684\u5927\u4e86\u80c6\u5b50\uff0c\u63a8\u95e8\u5165\u5185\uff0c\u53ea\u89c1\u4e3b \u4eba\u719f\u7761\u5982\u6b7b\uff0c\u5524\u4e4b\u4e0d\u9192\uff0c\u4e0d\u7981\u5927\u60ca\uff0c\u4ee5\u4e3a\u4ed6\u662f\u4e2d\u4e86\u90aa\uff0c\u6b63\u5728\u5916\u9762\u8bf7\u4e86\u5deb\u5e08\u524d\u6765\uff0c\u5fd9\u7740\u66ff\u4ed6\u6518 \u89e3\u3002\u5510\u7ecf\u5929\u751a\u662f\u597d\u7b11\uff0c\u6084\u6084\u5c06\u6cd5\u738b\u7684\u8bf7\u5e16\uff0c\u518d\u9001\u56de\u5e97\u4e3b\u4eba\u7684\u623f\u4e2d\u3002\u53c8\u66ff\u4ed6\u89e3\u4e86\u7a74\u9053\u3002\u4f4f\u5ba2\u4eec \u5927\u534a\u60ca\u9192\uff0c\u5230\u5ead\u9662\u53bb\u77a7\u70ed\u95f9\uff0c\u5510\u7ecf\u5929\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u56de\u5230\u623f\u95f4\uff0c\u5c06\u884c\u674e\u6536\u62fe\u597d\uff0c\u6253\u4e86\u4e00\u4e2a\u5305 \u88f9\uff0c\u7559\u4e0b\u4e86\u4e00\u952d\u94f6\u5b50\uff0c\u53c8\u6084\u6084\u7684\u6e9c\u51fa\u4e86\u5ba2\u5e97\u3002 \u3000\u3000\u4ed6\u5bf9\u4eca\u665a\u4e4b\u4e8b\uff0c\u751a\u591a\u4e0d\u89e3\u3002\u9996\u5148\u662f\u90a3\u85cf\u65cf\u5c11\u5973\u7a76\u7adf\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u4f55\u4ee5\u5979\u8d77\u5148\u8a93\u6b7b\u4e0d\u4ece\uff0c \u5176\u540e\u53c8\u7518\u505a\u767d\u6559\u5587\u561b\u7684\u5723\u5973\uff1f\u51b0\u5ddd\u5929\u5973\u521d\u6b21\u4e0b\u5c71\u4e0d\u8bc6\u9053\u8def\uff0c\u4f55\u4ee5\u4f1a\u649e\u5230\u6b64\u5730\uff1f\u662f\u5426\u5de7\u5408\uff1f\u51b0 \u5ddd\u5929\u5973\u8feb\u4ed6\u8d70\u5374\u53c8\u5bf9\u4ed6\u5fae\u7b11\uff0c\u662f\u607c\u4ed6\u8fd8\u662f\u8c05\u89e3\u4e86\u4ed6\uff1f\u51b0\u5ddd\u5929\u5973\u4e5f\u66fe\u4e3a\u9ec4\u6559\u4fdd\u62a4\u91d1\u74f6\uff0c\u4f55\u4ee5\u767d \u6559\u6cd5\u738b\u5374\u53c8\u5bf9\u5979\u4ee5\u793c\u76f8\u5f85\uff1f\u79cd\u79cd\u7591\u56e2\u6a2a\u4e98\u5fc3\u4e2d\uff0c\u4ed6\u4e00\u5fc3\u60f3\u89c1\u51b0\u5ddd\u5929\u5973\uff0c\u542c\u5f97\u6572\u8fc7\u4e86\u56db\u66f4\uff0c\u53c8 \u518d\u5954\u5411\u767d\u6559\u7684\u5587\u561b\u5bfa\u9662\u3002\u8fd9\u4e00\u6b21\u662f\u719f\u8def\u91cd\u6765\uff0c\u4e0d\u7528\u6478\u7d22\uff0c\u4fbf\u76f4\u5954\u4e1c\u8fb9\u7684\u201c\u5723\u5973\u5bab\u201d\u3002\u4ed6\u6253\u5b9a \u4e3b\u610f\uff0c\u5149\u53bb\u67e5\u63a2\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\uff0c\u4e0d\u6101\u4e0d\u77e5\u9053\u51b0\u5ddd\u5929\u5973\u7684\u4e0b\u843d\u3002 \u3000\u3000\u201c\u5723\u5973\u5bab\u201d\u91cd\u95e8\u6df1\u9501\uff0c\u679c\u7136\u7981\u536b\u68ee\u4e25\u3002\u5510\u7ecf\u5929\u7565\u4e00\u8e0c\u8e87\uff0c\u4fbf\u98de\u8eab\u63a0\u4e0a\u74e6\u9762\uff0c\u5176\u65f6\u6240\u6709\u7684 \u201c\u5723\u5973\u201d\u90fd\u5df2\u56de\u6765\uff0c\u5bab\u4e2d\u7684\u706f\u706b\u4ea6\u65e9\u5df2\u7184\u706d\uff0c\u4f46\u90a3\u4e9b\u201c\u5723\u5973\u201d\u7ecf\u8fc7\u4eca\u665a\u7684\u4e00\u573a\u5927\u95f9\uff0c\u90fd\u7761\u4e0d \u7740\u89c9\uff0c\u72b9\u81ea\u5728\u623f\u4e2d\u8c08\u4e0d\u4f11\u3002\u5510\u7ecf\u5929\u5728\u74e6\u9762\u4e0a\u86c7\u884c\u5154\u4f0f\uff0c\u4f46\u95fb\u5f97\u5904\u5904\u83ba\u58f0\u71d5\u8bed\uff0c\u591c\u98ce\u7a7f\u6237\uff0c\u8102 \u9999\u6251\u9f3b\u3002\u5510\u7ecf\u5929\u76b1\u4e86\u76b1\u7709\uff0c\u8fa8\u4e0d\u51fa\u90a3\u85cf\u65cf\u5c11\u5973\u7684\u53e3\u97f3\uff0c\u53c8\u4e0d\u6562\u95ef\u8fdb\u201c\u5723\u5973\u201d\u7684\u9999\u95fa\u53bb\u9010\u95ee\u67e5 \u8bbf\u3002 \u3000\u3000\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u4e1c\u9762\u5c0f\u697c\u4e00\u89d2\uff0c\u5c1a\u6709\u6b8b\u706f\uff0c\u5510\u7ecf\u5929\u8df3\u8fc7\u4e24\u91cd\u74e6\u9762\uff0c\u770b\u6e05\u695a\u65f6\uff0c\u7409\u7483\u7a97\u4e0a\uff0c \u73b0\u51fa\u4e09\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\uff0c\u53ef\u4e0d\u6b63\u662f\u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u548c\u90a3\u85cf\u65cf\u7684\u5c11\u5973\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u771f \u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u3002\u201d\u6084\u6084\u63a9\u8fd1\uff0c\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u9875\u662f\u6211\u6284 \u7ed9\u4f60\u7684\u6253\u6697\u5668\u7684\u624b\u6cd5\uff0c\u4f60\u85cf\u597d\u4e86\u3002\u201d\u90a3\u85cf\u65cf\u5c11\u5973\u9053\uff1a\u201c\u59d0\u59d0\u5927\u6069\uff0c\u6211\u5230\u6b7b\u4e5f\u4e0d\u5fd8\u8bb0\u3002\u201d\u5510\u7ecf \u5929\u5fc3\u9053\uff1a\u201c\u5979\u4eec\u679c\u7136\u662f\u76f8\u8bc6\u7684\u3002\u4f46\u591a\u5c11\u6b66\u529f\uff0c\u4e3a\u4ec0\u4e48\u4e13\u6559\u5979\u6253\u6697\u5668\u5462\uff1f\u201d\u53ea\u542c\u5f97\u5e7d\u840d\u201c\u5657\u55e4 \u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6b7b\u5440\u6d3b\u5440\u7684\u4e71\u8bf4\uff0c\u6211\u820d\u5f97\u4f60\u6b7b\uff0c\u6709\u4eba\u53ef\u820d\u4e0d\u5f97\u4f60\uff01\u201d\u7a97\u5185\u4eba\u5f71\u95ea\u52a8\uff0c\u90a3 \u85cf\u65cf\u5c11\u5973\u53bb\u6495\u5e7d\u840d\u7684\u5634\uff0c\u5e7d\u840d\u53c8\u9053\uff1a\u201c\u6211\u53ef\u662f\u8bf4\u771f\u7684\uff0c\u522b\u4eba\u5728\u771f\u5fc3\u5730\u7b49\u4f60\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00 \u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u8fd9\u5973\u5b50\u6709\u5fc3\u4e0a\u7684\u4eba\u513f\u5728\u8428\u8fe6\uff0c\u4ed6\u53c8\u662f\u8c01\u5462\uff1f\u201d\u5510\u7ecf\u5929\u867d\u7136\u806a\u660e\uff0c\u5374\u60f3\u4e0d \u8d77\u90a3\u662f\u9648\u5929\u5b87\u3002\u56e0\u4e3a\u5510\u7ecf\u5929\u66fe\u4eb2\u773c\u89c1\u8fc7\u9648\u5929\u5b87\u548c\u5e7d\u840d\u4eb2\u70ed\u7684\u60c5\u5f62\uff0c\u731c\u4e0d\u5230\u9648\u5929\u5b87\u7684\u610f\u4e2d\u4eba\u4e0d \u662f\u5e7d\u840d\uff0c\u5374\u662f\u9762\u524d\u8fd9\u4e2a\u85cf\u65cf\u5c11\u5973\u3002 \u3000\u3000\u7409\u7483\u7a97\u4e0a\uff0c\u51b0\u5ddd\u5929\u5973\u5029\u5f71\u5982\u82b1\uff0c\u53ea\u542c\u5979\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u5e7d\u840d\u522b\u80e1\u95f9\u5566\uff0c\u829d\u5a1c\u59b9\u5b50\uff0c\u4f60\u597d\u81ea \u4e3a\u4e4b\uff0c\u73cd\u91cd\uff0c\u73cd\u91cd\uff01\u201d\u5510\u7ecf\u5929\u53ea\u9053\u5979\u5c31\u8981\u544a\u8f9e\uff0c\u5ffd\u89c1\u5979\u624b\u6307\u4e00\u5f39\uff0c\u201c\u556a\u201d\u7684\u4e00\u54cd\uff0c\u697c\u4e0a\u6709\u4eba \u53eb\u9053\uff1a\u201c\u597d\u8d3c\u5b50\uff0c\u5c45\u7136\u6562\u95ef\u5230\u8fd9\u513f\u6765\u5566\uff01\u7075\u7352\u54ac\u4ed6\uff01\u201d\u63a5\u7740\u4e00\u58f0\u602a\u5578\uff0c\u7a81\u89c1\u56db\u6761\u5c0f\u725b\u822c\u5927\u7684 \u602a\u517d\u53d1\u51fa\u543c\u58f0\uff0c\u5411\u7740\u5510\u7ecf\u5929\u6251\u6765\uff0c\u7adf\u662f\u897f\u85cf\u6240\u7279\u6709\u7684\u4e00\u79cd\u72fc\u5927\uff0c\u662f\u91ce\u72fc\u548c\u72d7\u6742\u4ea4\u6240\u751f\u7684\uff0c\u51f6 \u6076\u5f02\u5e38\uff0c\u6bd4\u72fc\u8fd8\u8981\u5389\u5bb3\uff0c\u4f3c\u8fd9\u822c\u5927\u5c0f\u7684\u66f4\u662f\u5c11\u89c1\uff01 \u3000\u3000\u56db\u6761\u72d7\u9732\u51fa\u767d\u82b1\u82b1\u7684\u7259\u9f7f\uff0c\u5206\u6210\u56db\u8def\u653b\u6765\uff0c\u5c45\u7136\u4f3c\u61c2\u5f97\u6b66\u529f\u7684\u4eba\u4e00\u6837\uff0c\u5206\u8fdb\u5408\u51fb\uff0c\u5510\u7ecf \u5929\u4e00\u4e2a\u95ea\u8eab\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u521a\u5c06\u4e00\u6761\u72d7\u6253\u5f00\uff0c\u4e24\u4fa7\u201c\u6c6a\u6c6a\u201d\u5420\u58f0\uff0c\u8165\u98ce\u6251\u9762\uff0c\u4e00\u6761\u72d7\u4ece\u6b63\u9762\u54ac \u4ed6\u54bd\u5589\uff0c\u53e6\u4e00\u6761\u72d7\u4ece\u4fa7\u9762\u7a9c\u8fdb\uff0c\u524d\u722a\u642d\u4e0a\u4ed6\u7684\u80a9\u818a\uff0c\u5510\u7ecf\u5929\u6c89\u80a9\u4e00\u7529\uff0c\u5de6\u624b\u4e00\u6293\uff0c\u5c06\u4e24\u6761\u6076 \u72ac\u90fd\u6454\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u9661\u542c\u5f97\u53c8\u4f3c\u534a\u7a7a\u4e2d\u8d77\u4e86\u4e00\u58f0\u9739\u96f3\uff0c\u62bc\u9635\u90a3\u6761\u6076\u72ac\u4f3c\u4e4e\u662f\u7fa4\u5927\u7684\u9996\u9886\uff0c\u78a7 \u6cb9\u6cb9\u7684\u53cc\u77b3\u597d\u50cf\u653e\u5c04\u6012\u706b\u4e00\u822c\uff0c\u5de8\u5c3e\u4e00\u526a\uff0c\u817e\u7a7a\u7a9c\u8d77\uff0c\u5411\u7740\u5510\u7ecf\u5929\u4e00\u526a\u4e00\u6251\uff0c\u4e34\u654c\u4e4b\u52bf\uff0c\u7adf \u5982\u731b\u864e\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8eab\u5f62\u4e00\u8f6c\uff0c\u5f85\u90a3\u731b\u72ac\u53cc\u722a\u642d\u6765\u4e4b\u65f6\uff0c\u9661\u7684\u98de\u8d77\u4e00\u811a\uff0c\u4e0d\u6599\u8fd9\u6761\u6076\u72ac\u7adf\u662f\u4e45\u4ee5\u8bad\u7ec3 \uff0c\u77e5\u9053\u8d8b\u907f\uff0c\u5510\u7ecf\u5929\u6ca1\u8e22\u4e2d\u5b83\uff0c\u4e0d\u7531\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u6761\u72d7\u95ea\u907f\u4e4b\u5feb\uff0c\u7adf\u80dc\u4f3c\u7ec3\u8fc7\u5341 \u5e74\u7684\u8f7b\u529f\u4e4b\u58eb\uff01\u201d\u5fc3\u5b58\u601c\u501f\uff0c\u672c\u6765\u4ed6\u8fd9\u4e00\u8e22\uff0c\u4e43\u662f\u9e33\u9e2f\u8fde\u73af\u817f\u6cd5\uff0c\u8e22\u4e86\u5de6\u811a\uff0c\u53f3\u811a\u968f\u4e4b\u800c\u53d1 \uff0c\u4e24\u811a\u8e22\u51fa\uff0c\u975e\u4e2d\u4e0d\u53ef\u3002\u53ea\u56e0\u5fc3\u5b58\u601c\u60dc\uff0c\u5de6\u817f\u4e00\u62ac\uff0c\u5e76\u4e0d\u8e22\u51fa\uff0c\u90a3\u6761\u731b\u72ac\uff0c\u4f55\u7b49\u5feb\u75be\uff0c\u968f\u7740 \u5510\u7ecf\u5929\u7684\u8eab\u5f62\uff0c\u5f20\u7259\u821e\u722a\uff0c\u53c8\u518d\u6251\u5230\u3002 \u3000\u3000\u9002\u624d\u88ab\u6253\u5f00\u7684\u4e09\u6761\u731b\u72ac\u867d\u7136\u8dcc\u5f97\u4e0d\u8f7b\uff0c\u4f46\u8fd9\u79cd\u72d7\u76ae\u7c97\u8089\u539a\uff0c\u5e76\u6ca1\u53d7\u5230\u91cd\u4f24\uff0c\u5403\u4e86\u5927\u4e8f\uff0c \u66f4\u52a0\u6124\u6012\uff0c\u5501\u5501\u72c2\u5420\uff0c\u53c8\u518d\u5408\u56f4\uff0c\u8fd9\u4e00\u56de\uff0c\u56db\u6761\u731b\u72ac\u90fd\u4f3c\u77e5\u9053\u654c\u4eba\u5389\u5bb3\uff0c\u7adf\u5982\u9ad8\u624b\u5bf9\u654c\u4e00\u822c \uff0c\u6709\u653b\u6709\u5b88\u3002\u5510\u7ecf\u5929\u624b\u811a\u4e00\u52a8\uff0c\u5b83\u4eec\u5c31\u7acb\u523b\u7a9c\u5f00\uff0c\u51b7\u4e0d\u9632\u5c31\u662f\u4e00\u53e3\uff0c\u697c\u4e0a\u7684\u5578\u58f0\uff0c\u4ea6\u82e5\u5408\u7b26 \u8282\uff0c\u5728\u4e0a\u9762\u9690\u9690\u6307\u6325\uff0c\u56db\u6761\u72d7\u968f\u7740\u5578\u58f0\uff0c\u5ffd\u5206\u5ffd\u5408\uff0c\u5ffd\u8fdb\u5ffd\u9000\uff0c\u548c\u5510\u7ecf\u5929\u7ea0\u7f20\u4e0d\u4f11\uff0c\u201c\u5723\u5973 \u5bab\u201d\u4e2d\u767b\u65f6\u4eba\u58f0\u9f0e\u6cb8\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5408\u4ec0\u4e00\u63d6\uff0c\u4f7f\u51fa\u5185\u5bb6\u771f\u529b\uff0c\u5c06\u56db\u6761\u72d7\u72ac\u8feb\u51fa\u79bb\u8eab\u516b\u5c3a\u4e4b\u5916\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6b64 \u6765\uff0c\u53ea\u6b32\u4e00\u89c1\u655d\u53cb\uff0c\u5e76\u65e0\u6076\u610f\u3002\u8d35\u4e3b\u4eba\u8bf7\u5c06\u7075\u7f8e\u5524\u56de\uff0c\u82e5\u518d\u7ea0\u7f20\uff0c\u8bf7\u4f11\u602a\u5728\u4e0b\u6253\u72d7\u4e0d\u770b\u4e3b\u4eba \u9762\u4e86\u3002\u201d \u3000\u3000\u697c\u4e0a\u5578\u58f0\u66ae\u7136\u505c\u6b62\uff0c\u53ea\u89c1\u4e00\u4e2a\u9752\u8863\u8001\u5987\uff0c\u624b\u6325\u957f\u5251\uff0c\u4e00\u8dc3\u800c\u4e0b\u3002\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u6076\u8d3c\uff0c\u4eca \u65e5\u5728\u5b9d\u6bbf\u4e4b\u4e0a\u95f9\u5f97\u8fd8\u4e0d\u591f\u4e48\uff1f\u5723\u5973\u5bab\u4e2d\uff0c\u5c82\u662f\u4f60\u8fd9\u81ed\u7537\u5b50\u6765\u5f97\u7684\uff1f\u80e1\u8a00\u4e71\u8bed\uff0c\u4eb5\u8bfb\u795e\u7075\uff0c\u5403 \u6211\u4e00\u5251\uff01\u201d\u5c45\u7136\u662f\u6781\u4e0a\u4e58\u7684\u897f\u85cf\u5929\u9f99\u6d3e\u5251\u6cd5\uff0c\u5510\u7ecf\u5929\u4e0d\u5f97\u4e0d\u95ea\uff0c\u90a3\u56db\u6761\u731b\u72ac\uff0c\u53c8\u968f\u5728\u4e3b\u4eba\u4e4b \u540e\uff0c\u7a9c\u4e0a\u524d\u6765\u731b\u556e\u3002\u5510\u7ecf\u5929\u4e00\u770b\uff0c\u8fd9\u9752\u8863\u5987\u4eba\u539f\u6765\u5c31\u662f\u65e5\u95f4\u7387\u9886\u201c\u5723\u5973\u201d\uff0c\u51fa\u6765\u8c12\u89c1\u767d\u6559\u6cd5 \u738b\u7684\u90a3\u4e2a\u201c\u5723\u6bcd\u201d\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u6307\u697c\u623f\uff0c\u9053\uff1a\u201c\u6211\u786e\u662f\u6765\u8bbf\u670b\u53cb\u3002\u201d\u90a3\u5723\u6bcd\u8d8a\u53d1\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u518d\u51fa\u6c61\u8a00\uff0c\u53eb \u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d\u8981\u77e5\u5979\u6559\u4e2d\u7684\u5723\u5973\u4e00\u4f55\u7b49\u8d1e\u6d01\uff0c\u8fde\u7537\u5b50\u591a\u770b\u4e00\u773c\uff0c\u4e5f\u4e0d\u53ef\u4ee5\uff0c\u600e\u80fd\u4e0e\u5916 \u4eba\u4ea4\u4e3a\u670b\u53cb\uff1f\u5510\u7ecf\u5929\u4e4b\u8a00\uff0c\u5b9e\u662f\u72af\u4e86\u5979\u6559\u4e2d\u7684\u5927\u5fcc\uff0c\u4e5f\u5c31\u602a\u4e0d\u5f97\u8981\u88ab\u5979\u76ee\u4e3a\u72c2\u5984\u4e4b\u8f88\u4e86\u3002\u5979 \u4e00\u9762\u6325\u5251\u75be\u653b\uff0c\u4e00\u9762\u6307\u6325\u56db\u6761\u7075\u7352\u731b\u556e\uff0c\u53eb\u5510\u7ecf\u592d\u4e0d\u80fd\u5206\u8fa9\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e0d\u80af\u4e0b\u697c\u76f8\u8ba4\uff0c\u5510\u7ecf\u5929\u4e3a\u96be\u4e4b\u6781\uff0c\u53c8\u6015\u90a3\u767d\u6559\u6cd5\u738b\u5230\u6765\uff0c\u66f4\u662f\u7ea0\u7f20\u4e0d\u6e05\uff0c\u628a\u5fc3 \u4e00\u6a2a\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u7a81\u7136\u5c06\u4e00\u6761\u731b\u72ac\u63d0\u8d77\uff0c\u65cb\u98ce\u4e00\u821e\uff0c\u5411\u7740\u53e6\u4e00\u6761\u731b\u72ac\u4e00\u63b7\uff0c\u4e24\u6761\u731b\u72ac\u78b0\u4e2a\u6b63 \u7740\uff0c\u540c\u65f6\u60e8\u53eb\u4e00\u58f0\uff0c\u6454\u5012\u5730\u4e0a\uff0c\u518d\u4e5f\u722c\u4e0d\u8d77\u6765\uff0c\u90a3\u5723\u6bcd\u5927\u6012\uff0c\u5237\u5237\u5237\u8fde\u523a\u4e09\u5251\uff0c\u5510\u7ecf\u5929\u4e00\u4e2a \u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u7fe9\u5982\u98de\u9e1f\uff0c\u4ece\u5979\u8eab\u65c1\u63a0\u51fa\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u7528\u201c\u5c0f\u64d2\u62ff\u201d\u624b\u6cd5\u6293\u4f4f\u4e86\u4ece\u4fa7\u8fb9\u6251\u6765 \u7684\u731b\u72ac\uff0c\u4ecd\u4f9d\u524d\u6cd5\u3001\u65cb\u98ce\u4e00\u821e\uff0c\u5411\u53e6\u4e00\u6761\u731b\u72ac\u63b7\u53bb\uff0c\u5c82\u6599\u8fd9\u6761\u731b\u72ac\u6b63\u662f\u6700\u5389\u5bb3\u7684\u90a3\u4e00\u6761\u7075\u82f1 \uff0c\u4ea6\u662f\u7fa4\u5927\u7684\u9996\u9886\uff0c\u7adf\u7136\u5728\u534a\u7a7a\u4e2d\u6012\u53eb\u4e00\u58f0\uff0c\u7ffb\u8eab\u6251\u4e0b\uff0c\u975e\u552f\u95ea\u5f00\u4e86\u5510\u7ecf\u5929\u8fd9\u4e00\u63b7\uff0c\u800c\u4e14\u53cc \u722a\u582a\u582a\u642d\u4e0a\u4e86\u5510\u7ecf\u5929\u7684\u8863\u88f3\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4f7f\u51fa\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u632f\u8863\u4e00\u5f39\uff0c\u5c06\u90a3\u6761\u731b\u72ac\u5f39\u5f00\u6570\u5c3a\uff0c\u4e00\u95ea\u8eab\u53c8\u907f \u5f00\u4e86\u90a3\u5723\u6bcd\u7684\u4e00\u5251\uff0c\u5ffd\u542c\u5f97\u6323\u7684\u4e00\u58f0\uff0c\u773c\u524d\u5bd2\u5149\u95ea\u95ea\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u5510\u7ecf\u5929\u77e5\u662f\u51b0\u9b44\u795e\u5f39\uff0c\u53cc \u6307\u4e00\u5d4c\uff0c\u5c06\u51b0\u5f39\u634f\u5728\u624b\u4e2d\uff0c\u53ea\u89c9\u5185\u4e2d\u6709\u7269\uff0c\u51b0\u5f39\u89e6\u4f53\u9047\u70ed\u4fbf\u5316\uff0c\u85cf\u5728\u51b0\u5f39\u5185\u7684\u7eb8\u56e2\u5374\u7559\u5728\u4ed6 \u7684\u624b\u4e2d\u3002\u5510\u7ecf\u5929\u6b63\u81ea\u4e00\u6115\uff0c\u5ffd\u542c\u5f97\u51b0\u5ddd\u5973\u53eb\u9053\uff0c\u201c\u4f60\u5bfa\u4e2d\u6709\u4e8b\uff0c\u6211\u4e0d\u4fbf\u518d\u7559\uff0c\u5723\u6bcd\uff0c\u8bf7\u6055\u6211 \u5148\u8d70\u5566\uff01\u201d\u697c\u4e0a\u98de\u51fa\u4e24\u6761\u767d\u8863\u4eba\u5f71\uff0c\u51b0\u5ddd\u5929\u5973\u643a\u7740\u5e7d\u840d\uff0c\u5df2\u662f\u98d8\u7136\u800c\u53bb\u3002 \u3000\u3000\u5510\u7ecf\u5929\u65e0\u5fc3\u604b\u6218\uff0c\u7a81\u53d1\u4e00\u638c\uff0c\u5c06\u5723\u6bcd\u8feb\u5f00\uff0c\u98de\u8eab\u7a9c\u51fa\uff0c\u4fbf\u6b32\u9003\u8dd1\uff0c\u5723\u6bcd\u6c14\u5f97\u54ac\u7259\u5207\u9f7f\uff0c \u9053\uff1a\u201c\u7075\u7352\uff0c\u8ffd\u4ed6\uff01\u54fc\uff0c\u4f60\u4eb5\u6e0e\u795e\u7075\uff0c\u53c8\u6c14\u8d70\u62a4\u6cd5\uff0c\u628a\u4f60\u5582\u72d7\uff0c\u4e5f\u662f\u8be5\u5f53\uff01\u201d\u90a3\u6761\u731b\u72ac\u4e00\u4e0b \u5b50\u6251\u5230\u5510\u7ecf\u5929\u80cc\u540e\uff0c\u5510\u7ecf\u5929\u77e5\u9053\u5389\u5bb3\uff0c\u8feb\u5f97\u56de\u8eab\u62b5\u6321\uff0c\u8fd9\u72d7\u7075\u654f\u673a\u8b66\uff0c\u7528\u64d2\u62ff\u624b\u6293\u5b83\u4e0d\u7740\uff0c \u6253\u6b7b\u4e86\u53c8\u89c9\u53ef\u60dc\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5510\u7ecf\u5929\u62ff\u5b83\u65e0\u6cd5\uff0c\u88ab\u5b83\u7f20\u7740\uff0c\u90a3\u5723\u6bcd\u53c8\u6325\u5251\u653b\u6765\uff0c\u5723\u6bcd\u5bab\u4e2d\u4ea6 \u5df2\u53d1\u51fa\u8b66\u53f7\uff01 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u76b1\u7709\u5934\uff0c\u7a81\u7136\u5fc3\u751f\u4e00\u8ba1\uff0c\u5f85\u90a3\u731b\u72d7\u6251\u6765\uff0c\u5c06\u957f\u8896\u6325\u51fa\u3002\u8f7b\u8f7b\u4e00\u5e26\uff0c\u90a3\u6761\u72d7\u6536\u52bf \u4e0d\u4f4f\uff0c\u88ab\u4ed6\u4e00\u5e26\uff0c\u7adf\u6251\u5230\u201c\u5723\u6bcd\u201d\u8eab\u4e0a\uff0c\u5510\u7ecf\u5929\u8fd9\u4e00\u62db\u5feb\u6377\u4e4b\u6781\uff0c\u90a3\u5723\u6bcd\u5c1a\u672a\u770b\u5f97\u5206\u660e\uff0c\u5ffd \u542c\u5f97\u8033\u8fb9\u201c\u6c6a\u201d\u7684\u4e00\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u8138\u4e0a\u8165\u6c14\u6251\u9f3b\uff0c\u539f\u6765\u662f\u90a3\u6761\u72d7\u5f20\u53e3\u72c2\u5420\uff0c\u6ef4\u4e0b\u53e3\u6d8e\uff0c\u6e85 \u4e86\u201c\u5723\u6bcd\u201d\u6ee1\u9762\uff0c\u5723\u6bcd\u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u755c\u751f\uff01\u201d\u5c06\u72d7\u6454\u5f00\uff0c\u53ea\u542c\u5f97\u54c8\u54c8\u5927\u7b11\u4e4b\u58f0\uff1a\u5510\u7ecf\u5929\u8df3 \u51fa\u56f4\u5899\u53bb\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8dd1\u5230\u5916\u9762\uff0c\u5f20\u773c\u56db\u671b\uff0c\u54ea\u91cc\u8fd8\u6709\u51b0\u5ddd\u5929\u5973\u7684\u8e2a\u8ff9\u3002\u51b0\u5ddd\u5929\u5973\u7684\u8f7b\u529f\u6bd4\u4ed6\u8fd8\u8981\u7a0d\u9ad8 \u4e00\u7b79\uff0c\u53c8\u5148\u8d70\u4e00\u523b\uff0c\u8981\u8ffd\u4e5f\u8feb\u4e0d\u53ca\u3002\u5510\u7ecf\u5929\u53f9\u4e86\u53e3\u6c14\uff0c\u6253\u5f00\u7eb8\u56e2\uff0c\u501f\u7740\u6708\u5149\u4e00\u770b\uff0c\u4e0a\u9762\u5199\u7740 \u4e00\u884c\u5c0f\u5b57\uff1a\u201c\u4f11\u8981\u591a\u7ba1\u95f2\u4e8b\uff01\u201d\u5510\u7ecf\u5929\u4e0d\u89c9\u5fc3\u4e2d\u82e6\u7b11\uff1a\u201c\u6211\u53ea\u662f\u6b32\u89c1\u4f60\u4e00\u9762\uff0c\u4f60\u4e0d\u89c1\u6211\u4e5f\u8fd8 \u7f62\u4e86\uff0c\u5374\u4e09\u756a\u4e24\u6b21\u5c06\u6211\u620f\u5f04\uff0c\u56de\u5934\u4e00\u671b\uff0c\u201c\u5723\u5973\u5bab\u201d\u9694\u90bb\u7684\u6cd5\u738b\u5b9d\u6bbf\uff0c\u4ea6\u5df2\u706f\u706b\u901a\u660e\uff0c\u5510\u7ecf \u5929\u5fc3\u9053\uff1a\u201c\u767d\u6559\u6cd5\u738b\u5fc5\u7136\u60ca\u8d77\uff0c\u5440\uff0c\u60f3\u4e0d\u5230\u7cca\u91cc\u7cca\u6d82\u4e0e\u4ed6\u7ed3\u4e86\u4ec7\u3002\u90a3\u85cf\u65cf\u5c11\u5973\u65e2\u7518\u5fc3\u613f\u505a\u5723 \u5973\uff0c\u6211\u4e5f\u4e0d\u5fc5\u518d\u53bb\u6551\u5979\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e00\u53e3\u6c14\u5954\u51fa\u4e86\u54c8\u5409\u5c14\u57ce\uff0c\u5fc3\u4e2d\u95f7\u95f7\u4e0d\u4e50\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u603b\u8981\u5230\u5ddd\u897f\u53bb\u627e \u5979\u7684\u50e7\u4f2f\uff0c\u5c31\u7b97\u5979\u4e0d\u8bc6\u8def\u9014\uff0c\u591a\u8d39\u4e9b\u65f6\u65e5\u4e5f\u7ec8\u80fd\u5bfb\u5230\uff0c\u6211\u4e0d\u5982\u5230\u5192\u4f2f\u4f2f\u90a3\u91cc\u53bb\u7b49\u5979\u3002\u201d\u4e3b\u610f \u6253\u5b9a\uff0c\u80f8\u4e2d\u90c1\u95f7\u7a0d\u8212\uff0c\u4e8e\u662f\u5728\u5c71\u5c97\u4e0a\u80e1\u4e71\u7761\u4e86\u4e00\u89c9\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u7eed\u5411\u4e1c\u884c\u3002 \u3000\u3000\u4ece\u9752\u6d77\u8d8a\u8fc7\u5df4\u989c\u5580\u62c9\u5c71\uff0c\u4fbf\u662f\u56db\u5ddd\u897f\u90e8\uff0c\u5ddd\u897f\u53e4\u79f0\u8352\u50fb\u7684\u201c\u91ce\u4eba\u201d\u4e4b\u5730\uff0c\u5510\u7ecf\u5929\u8d70\u4e86\u6570 \u65e5\uff0c\u4e0d\u89c1\u4eba\u70df\uff0c\u597d\u5728\u91ce\u679c\u751a\u591a\uff0c\u6e34\u4e86\u6458\u679c\u5b50\u98df\uff0c\u997f\u4e86\u5c31\u6253\u91ce\u7f8a\u70e4\u5403\uff0c\u5012\u4e5f\u4e0d\u6101\u3002\u8fd9\u4e00\u65e5\uff0c\u8e0f \u8fdb\u4e86\u5ddd\u897f\u7684\u5929\u9669\u96c0\u513f\u5c71\uff0c\u8fc7\u4e86\u96c0\u513f\u5c71\uff0c\u5c31\u662f\u6c49\u4eba\u7684\u5730\u533a\u4e86\u3002 \u3000\u3000\u96c0\u513f\u5c71\u5929\u9669\u7aef\u7684\u540d\u4e0d\u865a\u4f20\uff0c\u867d\u7136\u6ca1\u6709\u5929\u5c71\u9ad8\u5cfb\uff0c\u4f46\u56db\u5468\u9ad8\u5cf0\u72ac\u7259\u4ea4\u9519\uff0c\u884c\u4ee5\u5c71\u810a\u4e4b\u65f6\uff0c \u9065\u671b\u56db\u5468\u7fa4\u5c71\uff0c\u90fd\u597d\u50cf\u62ab\u6625\u96ea\u8863\u4fef\u4f0f\u5728\u5c71\u811a\u5e95\u4e0b\uff0c\u4e25\u5982\u4e00\u7fa4\u6216\u8dea\u6216\u5367\u7684\u7f8a\u7fa4\uff0c\u851a\u6210\u5947\u666f\u3002\u89e6 \u76ee\u6240\u53ca\uff0c\u5230\u5904\u90fd\u662f\u78cb\u5ce8\u602a\u77f3\uff0c\u7a81\u51fa\u96ea\u4e0a\uff0c\u8fdc\u8fdc\u671b\u53bb\uff1b\u53c8\u597d\u4f3c\u4e00\u6392\u7cbe\u5de5\u96d5\u523b\u7684\u5c4f\u98ce\u3002 \u3000\u3000\u8d70\u4e86\u4e24\u5929\uff0c\u5c71\u52bf\u6108\u6765\u6108\u9669\uff0c\u8fd9\u4e00\u65e5\u5510\u7ecf\u5929\u7ffb\u8fc7\u4e86\u5c71\u810a\u3002\u8fdc\u8fdc\u89c1\u5230\u5c71\u80cc\u5347\u8d77\u7684\u8885\u8885\u708a\u70df\u3002 \u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u559c\uff0c\u4f46\u968f\u5373\u60f3\u8d77\uff0c\u7fa4\u5c71\u91cd\u53e0\uff0c\u867d\u4f3c\u8fd1\u5728\u773c\u524d\u7684\u666f\u7269\u3002\u4e5f\u5e38\u5e38\u8981\u8dd1\u5927\u534a\u5929\uff0c\u8981\u627e \u5230\u90a3\u5c71\u80cc\u4eba\u5bb6\uff0c\u53ea\u6015\u8fd8\u5f97\u4e24\u5929\u8def\u7a0b\u3002\u5510\u7ecf\u5929\u653e\u5feb\u811a\u6b65\uff0c\u5ffd\u89c1\u5929\u8272\u7a81\u7136\u9634\u6697\uff0c\u539f\u6765\u5df2\u8d70\u5230\u96c0\u513f \u5c71\u6700\u9669\u7a84\u4e4b\u5904\uff0c\u4e24\u9762\u5c71\u5cf0\uff0c\u7d27\u76f8\u5408\u62b1\uff0c\u5c71\u77f3\u5c42\u5c42\u5bf9\u7acb\uff0c\u6700\u72ed\u7a84\u5904\uff0c\u76f8\u53bb\u4e8c\u4e09\u4e08\u8ddd\u79bb\uff0c\u66f2\u66f2\u6298 \u6298\uff0c\u597d\u4f3c\u91cd\u95e8\u6df1\u9501\u3002\u8d70\u4e86\u4e00\u6bb5\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u6709\u5598\u606f\u4e4b\u58f0\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u8863\u886b\u8934\u891b\u7684\u6c49\u5b50\uff0c\u8eab\u501a\u5371\u5d16\uff0c\u6c14\u5598\u5401\u5401\u3002\u5510\u7ecf\u5929\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u6c49\u5b50\u5440 \u5440\u7684\u53d1\u51fa\u4e24\u4e2a\u6a21\u7cca\u7684\u58f0\u97f3\uff0c\u5510\u7ecf\u5929\u518d\u8d70\u524d\u4e24\u6b65\uff0c\u90a3\u6c49\u5b50\u7a81\u7136\u4f38\u51fa\u4e24\u53ea\u624b\u6765\uff0c\u5598\u6c14\u8bf4\u9053\uff1a\u201c\u90a3 \u4f4d\u5ba2\u5b98\uff0c\u53ef\u601c\u53ef\u601c\u6211\u8fd9\u5c0f\u53eb\u5316\u5427\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u5f20\u773c\u4e00\u671b\uff0c\u6479\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u6c49\u5b50\u4f38\u51fa\u6765\u7684\u4e24\u6761\u624b\u81c2\u4e0a\u9762\u7ed3\u6ee1\u4e00\u4e2a\u4e2a\u5927\u5927\u5c0f\u5c0f\u7684 \u7599\u7629\uff0c\u5341\u6307\u5f2f\u66f2\uff0c\u6ee1\u9762\u7ea2\u4e91\uff0c\u9762\u4e0a\u4e0b\u988a\uff0c\u5de6\u53f3\u4e5f\u5404\u6709\u4e00\u4e2a\u7599\u7629\uff0c\u770b\u6765\u7adf\u662f\u4e2a\u5468\u8eab\u6bd2\u53d1\u7684\u5927\u9ebb \u75af\u3002\u5510\u7ecf\u5929\u867d\u65e0\u4e16\u4fd7\u4e4b\u89c1\uff0c\u5728\u8fd9\u9634\u68ee\u53ef\u6015\u7684\u5c71\u9053\u9aa4\u7136\u89c1\u7740\u8fd9\u9ebb\u75af\u7684\u602a\u76f8\uff0c\u4e5f\u4e0d\u7531\u5f97\u5012\u9000\u4e09\u6b65 \u3002\u90a3\u6c49\u5b50\u5f20\u7740\u4e00\u53cc\u5931\u795e\u7684\u773c\u775b\uff0c\u5446\u671b\u7740\u5510\u7ecf\u5929\uff0c\u597d\u50cf\u662f\u997f\u4e86\u51e0\u5929\u7684\u6837\u5b50\uff0c\u9759\u5019\u4ed6\u7684\u5e03\u65bd\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u5b9a\u5fc3\u795e\uff0c\u6df1\u89c9\u5947\u602a\uff0c\u9ebb\u75af\u60a3\u8005\u5357\u65b9\u6700\u591a\uff0c\u897f\u5317\u6781\u5c11\uff0c\u5728\u5ddd\u897f\u201c\u91ce\u4eba\u201d\u4e4b\u5730\u89c1\u5230 \u9ebb\u75af\uff0c\u5df2\u662f\u4e00\u5947\uff0c\u8fd9\u96c0\u513f\u5c71\u662f\u4eba\u8ff9\u7f55\u81f3\u4e4b\u5730\uff0c\u8fd9\u9ebb\u75af\u5374\u5c45\u7136\u80fd\u6765\u5230\u6b64\u5904\uff0c\u66f4\u662f\u4e00\u5947\u3002\u4f46\u968f\u5373 \u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4e00\u5b9a\u662f\u9003\u907f\u4e16\u4eba\uff0c\u6d89\u8fc7\u4e07\u6c34\u5343\u5c71\u9003\u5230\u6b64\u5904\u6765\u7684\u3002\u201d\u8981\u77e5\u6e05\u4ee3\u7684\u533b\u5b66\u8fdc\u4e0d\u5982 \u4eca\u65e5\u53d1\u8fbe\uff0c\u9ebb\u75af\u672c\u6765\u4e0d\u4f1a\u4f20\u67d3\uff0c\u4f46\u5f53\u65f6\u7684\u4e00\u822c\u4eba\u5374\u6df1\u4fe1\u9ebb\u75af\u5fc5\u4f1a\u4f20\u67d3\uff0c\u628a\u9ebb\u75af\u60a3\u8005\u770b\u6210\u6700\u6700 \u5371\u9669\u4e4b\u4eba\uff0c\u53d1\u73b0\u6709\u4eba\u60a3\u4e86\u9ebb\u75af\uff0c\u5c31\u7acb\u523b\u8981\u5c06\u90a3\u4eba\u70e7\u6b7b\uff0c\u5c06\u9aa8\u7070\u6df1\u6df1\u5730\u57cb\u5728\u5730\u4e0b\u3002\u7531\u4e8e\u897f\u5317\u9ebb \u75af\u60a3\u8005\u6781\u5c11\uff0c\u8bc6\u5f97\u6b64\u75c5\u7684\u4eba\u4e0d\u591a\u3002\u56e0\u6b64\u6709\u4e9b\u75c5\u4eba\uff0c\u4e0d\u8f9e\u7ffb\u5c71\u6d89\u6c34\uff0c\u5e0c\u671b\u80fd\u6765\u5230\u897f\u5317\u5c71\u533a\uff0c\u82df \u5ef6\u6b8b\u5598\u3002\u8fd9\u7b49\u4e8e\u957f\u9014\u9003\u96be\uff0c\u4f46\u9003\u96be\u5c1a\u6709\u4eba\u5e03\u65bd\uff0c\u9ebb\u75af\u5374\u662f\u4eba\u89c1\u4eba\u6015\uff0c\u9ebb\u75af\u60a3\u8005\u4e0d\u6562\u6295\u6751\u5bbf\u5e97 \uff0c\u4e0d\u662f\u9965\u997f\u800c\u6b7b\uff0c\u4fbf\u662f\u529b\u7aed\u800c\u6b7b\uff0c\u80fd\u5230\u897f\u5317\u9003\u751f\u8005\u767e\u4e0d\u5f97\u4e00\u3002 \u3000\u3000\u5510\u7ecf\u5929\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u89c9\u8d77\u4e86\u601c\u60af\u4e4b\u60c5\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u8eab\u7f79\u6076\u75be\uff0c\u5b81\u613f\u9003\u5165\u6df1\u5c71\u4e0e\u9e1f\u517d\u4e3a\u90bb \uff0c\u8fd9\u662f\u4f55\u7b49\u53ef\u54c0\uff0c\u53c8\u9700\u8981\u4f55\u7b49\u52c7\u6c14\uff01\u201d\u4fbf\u4ece\u56ca\u4e2d\u53d6\u51fa\u4e00\u6761\u70e4\u719f\u7684\u7f8a\u817f\uff0c\u63b7\u8fc7\u53bb\u9053\uff1a\u201c\u7ed9\u4f60\uff01 \u524d\u9762\u91ce\u679c\u6781\u591a\uff0c\u4f60\u53ef\u4ee5\u81ea\u5df1\u91c7\u6458\u3002\u201d\u7f8a\u817f\u843d\u5728\u90a3\u4eba\u8ddf\u524d\uff0c\u90a3\u4eba\u5374\u4e0d\u4fef\u8170\u53bb\u62fe\uff0c\u4ed6\u773c\u775b\u5374\u7a81\u7136 \u4e00\u95ea\uff0c\u4e00\u53cc\u6676\u4eae\u7684\u7738\u5b50\uff0c\u53d1\u51fa\u9a87\u4eba\u7684\u5149\u8292\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u5510\u7ecf\u5929\u5ffd\u89c9\u6b64\u4eba\u867d\u7136\u5f62\u5bb9\u4e11\u602a\uff0c\u4f46\u5374 \u662f\u7709\u6e05\u76ee\u79c0\uff0c\u4e0d\u7c7b\u5e38\u4eba\u3002\u5c24\u5176\u5728\u773c\u775b\u5f20\u5f00\u4e4b\u65f6\uff0c\u90a3\u773c\u5149\u5982\u540c\u95ea\u7535\uff0c\u7adf\u4f3c\u7ec3\u6b66\u4e4b\u4eba\u4e00\u6837\u3002\u90a3\u9ebb \u75af\u60a3\u8005\u53cc\u773c\u4e00\u5f20\u4fbf\u9616\uff0c\u53c8\u53d8\u5f97\u6194\u60b4\u65e0\u795e\uff0c\u6162\u6162\u5f2f\u8170\u53bb\u62fe\u90a3\u6761\u7f8a\u817f\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5582\uff0c\u4f60\u53eb\u4ec0 \u4e48\u540d\u5b57\uff1f\u662f\u7ec3\u8fc7\u6b66\u7684\u4e48\uff1f\u201d\u90a3\u9ebb\u75af\u5750\u5728\u5730\u4e0a\uff0c\u6367\u7740\u7f8a\u817f\u5927\u56bc\uff0c\u7adf\u4f3c\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u662f\u997f\u5f97\u614c\u4e86\u3002\u201d\u53c8\u6697\u7b11\u9053\uff1a\u201c\u6211\u95ee\u4ed6\u8fd9\u4e9b\u5e72\u561b\uff1f\u5c31\u7b97\u4ed6\u662f\u6b66\u5b66\u4e2d \u4eba\uff0c\u6211\u4e5f\u4e0d\u80fd\u4e0e\u4ed6\u4f5c\u4f34\u3002\u4f55\u51b5\uff0c\u6211\u53c8\u6025\u7740\u8d76\u8def\u3002\u201d\u53ea\u89c1\u90a3\u9ebb\u75af\u60a3\u8005\u4e00\u4e0b\u5b50\u5c31\u56bc\u4e86\u534a\u6761\u7f8a\u817f\uff0c \u500f\u5730\u53c8\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u72e0\u72e0\u5730\u76ef\u4e86\u5510\u7ecf\u5929\u4e00\u773c\uff0c\u90a3\u773c\u5149\u4f3c\u662f\u6124\u6012\uff0c\u53c8\u4f3c\u618e\u6076\uff0c\u6bd4\u9002\u624d\u66f4\u662f\u9a87\u4eba \u3002\u5728\u5982\u6b64\u9634\u6c89\u7684\u5c71\u8c37\u4e4b\u4e2d\uff0c\u4e00\u4e2a\u5927\u9ebb\u75af\u9732\u51fa\u5982\u6b64\u7684\u773c\u5149\uff0c\u5510\u7ecf\u5929\u4e5f\u4e0d\u7531\u5f97\u6253\u4e86\u4e00\u4e2a\u5bd2\u568e\uff0c\u63d0 \u8d77\u811a\u6b65\uff0c\u5c55\u5f00\u8eab\u5f62\uff0c\u5728\u4ed6\u8eab\u8fb9\u75be\u63a0\u800c\u8fc7\u3002 \u3000\u3000\u8d70\u4e0d\u5230\u5341\u6b65\u5149\u666f\uff0c\u521a\u5230\u5c71\u62d7\u4e4b\u5904\uff0c\u5ffd\u542c\u5f97\u8f70\u7684\u4e00\u58f0\uff0c\u4e00\u5757\u78e8\u76d8\u822c\u5927\u5c0f\u7684\u5de8\u77f3\uff0c\u7a81\u7136\u4ece\u4e0a \u9762\u6389\u4e0b\u6765\uff0c\u5c71\u9053\u72ed\u7a84\uff0c\u8f6c\u8eab\u4ea6\u96be\uff0c\u5510\u7ecf\u5929\u594b\u8d77\u795e\u529b\uff0c\u53cc\u81c2\u4e00\u6258\uff0c\u5c06\u90a3\u5927\u77f3\u4e00\u63b7\uff0c\u53ea\u542c\u5f97\u8f70\u8f70 \u4e4b\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u90a3\u5757\u5de8\u77f3\u5e26\u52a8\u5c71\u6ce5\uff0c\u5815\u4e0b\u6df1\u8c37\uff0c\u5510\u7ecf\u5929\u56de\u5934\u4e00\u77a7\uff0c\u53ea\u89c1\u90a3\u9ebb\u75af\u63d0\u7740\u4e00\u6839\u62d0 \u6756\u3002\u9876\u7740\u4e0a\u9762\u7684\u4e00\u5757\u5927\u77f3\uff0c\u5510\u7ecf\u5929\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53c8\u662f\u8f70\u9686\u4e00\u58f0\u5de8\u54cd\uff0c\u90a3 \u5757\u5de8\u77f3\u51cc\u7a7a\u98de\u5815\uff0c\u58f0\u52bf\u6bd4\u521a\u624d\u8fd8\u731b\u3002\u5510\u7ecf\u5929\u7ad9\u7a33\u811a\u6b65\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u81c2\u4e00\u6258\uff0c\u53c8\u5c06\u90a3\u5757\u5de8\u77f3 \u63b7\u4e0b\u6df1\u8c37\u3001\u6ce5\u571f\u98de\u6e85\uff0c\u679d\u53f6\u98de\u821e\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u7adf\u81ea\u5f20\u4e0d\u5f00\u773c\u775b\uff0c\u5f85\u5230\u5f20\u5f00\u773c\u775b\u4e4b\u65f6\uff0c\u90a3\u9ebb\u75af \u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5927\u6124\uff0c\u559d\u9053\uff1a\u201c\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4e3a\u4f55\u52a0\u5bb3\u4e8e\u6211\uff1f\u201d\u201c\u4f60\u4e3a\u4f55\u52a0\u5bb3\u4e8e\u6211\uff1f\u52a0\u5bb3\u4e8e\u6211\uff0c \u4e8e\u6211\u2026\u2026\u201d\u7fa4\u5cf0\u56de\u54cd\uff0c\u4e45\u4e45\u4e0d\u7edd\u3002\u90a3\u9ebb\u75af\u60a3\u8005\u5df1\u4e0d\u77e5\u8eb2\u5230\u54ea\u513f\u53bb\u4e86\uff01 \u3000\u3000\u5510\u7ecf\u5929\u81ea\u4e0b\u5c71\u4ee5\u6765\uff0c\u4ea6\u66fe\u7ecf\u5386\u8fc7\u4e0d\u5c11\u60ca\u5fc3\u52a8\u9b44\u7684\u602a\u4e8b\uff0c\u4f46\u4ece\u65e0\u4e00\u6b21\u6709\u4eca\u65e5\u4e4b\u602a\u5f02\uff01\u8fd9\u5927 \u9ebb\u75af\u7adf\u7136\u662f\u4e2a\u5177\u6709\u7edd\u9876\u6b66\u529f\u7684\u5f02\u4eba\uff0c\u6b64\u4e8b\u5df2\u662f\u4e0d\u53ef\u601d\u8bae\uff01\u66f4\u4ee4\u5510\u7ecf\u5929\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u7684\u662f\uff1a\u4ed6 \u5bf9\u8fd9\u4e2a\u9ebb\u75af\u6709\u6069\u65e0\u4ec7\uff0c\u5b9e\u4e0d\u660e\u4ed6\u4f55\u6545\u5982\u6b64\u9634\u9669\u4f0f\u51fb\uff0c\u96be\u9053\u771f\u662f\u6cef\u706d\u4e86\u4eba\u6027\u4e0d\u6210\u3002 \u3000\u3000\u8d70\u51fa\u5c71\u58a9\uff0c\u5929\u7a7a\u8c41\u7136\u5f00\u6717\uff0c\u5c71\u8def\u76d8\u65cb\u503e\u659c\uff0c\u8fd9\u662f\u96c0\u513f\u5c71\u7684\u91cc\u9762\uff0c\u5f62\u52bf\u8fdc\u4e0d\u53ca\u5317\u9762\u9669\u9661\uff0c \u6709\u5c71\u8def\u5373\u662f\u5df2\u6709\u4eba\u8ff9\uff0c\u5510\u7ecf\u5929\u8212\u4e86\u53e3\u6c14\uff0c\u4e00\u76f4\u5954\u51fa\u5341\u4f59\u91cc\u5730\uff0c\u518d\u4e5f\u4e0d\u53bb\u60f3\u90a3\u83ab\u540d\u5176\u5999\u4ee4\u4eba\u618e \u538c\u7684\u9ebb\u75af\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u508d\u665a\uff0c\u5df2\u4e0b\u5230\u534a\u5c71\uff0c\u5c71\u5761\u4e0a\u6709\u95f4\u6ce5\u5c4b\uff0c\u5c4b\u8fb9\u4e00\u4e2a\u8349\u68da\uff0c\u5c4b\u4e2d\u5347\u8d77\u7f15\u7f15\u708a\u70df\uff0c\u665a\u98ce \u4e2d\u8fd8\u5439\u9001\u6765\u70e4\u8089\u548c\u7c73\u996d\u7684\u9999\u6c14\u3002\u5510\u7ecf\u5927\u770b\u8fd9\u6ce5\u5c4b\u7684\u5f0f\u6837\uff0c\u5f62\u5982\u9a6c\u623f\uff0c\u4e1c\u897f\u957f\u8fbe\u4e09\u4e08\uff0c\u5bbd\u4ea6\u4e08 \u4f59\uff0c\u77e5\u9053\u8fd9\u662f\u5c71\u6237\u4eba\u5bb6\uff0c\u7279\u5730\u8f9f\u6765\u62db\u547c\u8fc7\u8def\u7684\u65c5\u5ba2\uff0c\u4ee5\u53ca\u51c6\u5907\u4e0a\u5c71\u91c7\u836f\u6216\u6253\u730e\u7684\u4eba\u4eec\u6295\u5bbf\u7684 \uff0c\u6362\u8a00\u4e4b\uff0c\u5373\u662f\u7b80\u964b\u7684\u5c71\u4e2d\u5ba2\u5e97\u3002\u5510\u7ecf\u5929\u8fd9\u51e0\u5929\u6765\u53ea\u662f\u5403\u70e4\u7f8a\u8089\u548c\u5c71\u679c\uff0c\u6781\u60f3\u4e00\u5c1d\u767d\u7c73\u996d\u548c \u852c\u83dc\u7684\u6ecb\u5473\uff0c\u4e5f\u60f3\u80fd\u591f\u5b89\u9002\u7684\u7761\u4e00\u89c9\uff0c\u4fbf\u5230\u90a3\u6ce5\u5c4b\u6572\u95e8\u6c42\u5bbf\u3002 \u3000\u3000\u5c4b\u4e3b\u4eba\u662f\u4e2a\u4e94\u5341\u5c81\u5de6\u53f3\u7684\u5c71\u6c11\uff0c\u76f8\u8c8c\u6734\u5b9e\uff0c\u89c1\u5510\u7ecf\u5929\u6c42\u5bbf\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u513f\u597d\u51e0\u4e2a\u6708\u6ca1 \u6709\u4eba\u6765\uff0c\u4e00\u6765\u4fbf\u662f\u4e00\u5927\u5806\uff0c\u5ba2\u5b98\uff0c\u4f60\u4eca\u665a\u4e0d\u6101\u5bc2\u5bde\u4e86\u3002\u91cc\u9762\u6709\u5357\u65b9\u6765\u7684\u836f\u5546\uff0c\u6709\u5341\u51e0\u4e2a\u4eba\u5462 \uff01\u201d\u5510\u7ecf\u5929\u4ea4\u4e86\u4e00\u952d\u94f6\u5b50\uff0c\u53eb\u4ed6\u505a\u996d\uff0c\u8fdb\u5165\u5c4b\u4e2d\uff0c\u53ea\u89c1\u91cc\u9762\u5806\u6709\u5341\u51e0\u6311\u836f\u6311\uff0c\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08 \u5077\u5077\u7684\u62ff\u773c\u775b\u779f\u7740\u81ea\u5df1\u3002\u5ffd\u5730\u542c\u5f97\u5f53\u4e2d\u90a3\u4e2a\u8001\u5e74\u9556\u5e08\u54b3\u4e86\u4e00\u58f0\uff0c\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\u4f4e\u4e0b\u4e86\u5934\uff0c\u88c5 \u4f5c\u82e5\u65e0\u5176\u4e8b\u7684\u6837\u5b50\u3002 \u3000\u3000\u9664\u4e86\u4e09\u4e2a\u9556\u5e08\u4e4b\u5916\uff0c\u8fd8\u6709\u4e03\u516b\u4e2a\u7cbe\u58ee\u7684\u6c49\u5b50\uff0c\u6a2a\u4e03\u575a\u516b\u5730\u5367\u5728\u5730\u4e0a\uff0c\u62ff\u6241\u6311\u5f53\u4f5c\u6795\u5934\uff0c \u60f3\u662f\u836f\u884c\u7684\u4f19\u8ba1\u3002\u5c4b\u4e2d\u4e00\u4e2a\u4e94\u5341\u5de6\u53f3\u6ee1\u9762\u6cb9\u5149\u7684\u5546\u4eba\uff0c\u508d\u7740\u90a3\u8001\u5e74\u9556\u5e08\uff0c\u4e5f\u5077\u5077\u5730\u62ff\u773c\u775b\u779f \u5510\u7ecf\u5929\uff0c\u773c\u5149\u843d\u5230\u4ed6\u7684\u5251\u7a57\u4e4b\u4e0a\uff0c\u5251\u7a57\u4e24\u8fb9\u6446\u52a8\uff0c\u4ed6\u7684\u773c\u5149\u4e5f\u4f3c\u4e4e\u6643\u6765\u6643\u53bb\uff0c\u9732\u51fa\u60ca\u60e7\u7684\u795e \u60c5\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u662f\u5230\u9752\u6d77\u53bb\u5417\uff1f\u201d\u90a3\u8001\u9556\u5e08\u6de1\u6de1\u5730\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u836f \u5546\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5144\u5f1f\u662f\u5230\u5ddd\u897f\u53bb\u7684\uff0c\u4eca\u665a\u5e78\u4f1a\uff0c\u5927\u5bb6\u6709\u4f34\u4e86\u3002\u8352 \u5c71\u91ce\u5cad\uff0c\u4eba\u591a\u80c6\u58ee\uff0c\u53ef\u4ee5\u597d\u597d\u7684\u7761\u4e00\u89c9\u3002\u201d\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u90a3 \u8001\u5e74\u9556\u5e08\u9053\uff1a\u201c\u5144\u53f0\u5355\u8eab\u72ec\u884c\uff0c\u80c6\u6c14\u8fc7\u4eba\uff0c\u4f69\u670d\u4f69\u670d\uff01\u8001\u673d\u5403\u8fd9\u53e3\u9556\u884c\u996d\uff0c\u5168\u9760\u5916\u9762\u670b\u53cb\u7684 \u5e2e\u5fd9\uff0c\u4e0d\u6015\u5144\u53f0\u89c1\u7b11\uff0c\u82e5\u53ea\u662f\u6211\u4e00\u4e2a\u4eba\uff0c\u6211\u4e5f\u4e0d\u6562\u7ffb\u8fc7\u8fd9\u96c0\u513f\u5c71\u3002\u201d\u8bf4\u9053\uff0c\u7528\u773c\u775b\u779f\u5510\u7ecf\u5929\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8001\u513f\u5b9a\u662f\u5c06\u6211\u5f53\u4f5c\u72ec\u811a\u5927\u76d7\u4e86\u3002\u201d\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u8001\u5e08\u7236\u592a \u5ba2\u6c14\u4e86\uff0c\u8fd8\u672a\u8bf7\u6559\u5927\u540d\u3002\u201d\u90a3\u8001\u9556\u5e08\u9053\uff1a\u201c\u655d\u59d3\u90ed\uff0c\u8d31\u5b57\u53f0\u57fa\uff0c\u8f6c\u8bf7\u5144\u53f0\u9ad8\u59d3\u5927\u540d\u3002\u201d\u5510\u7ecf \u5929\u4e5f\u8bf4\u4e86\u3002\u90a3\u8001\u9556\u5e08\u4f3c\u4e4e\u4e0d\u613f\u548c\u5510\u7ecf\u5929\u591a\u8bf4\u8bdd\uff0c\u4ea4\u4ee3\u4e86\u6c5f\u6e56\u5957\u8bed\u4e4b\u540e\uff0c\u5510\u7ecf\u5929\u95ee\u4e00\u53e5\u4ed6\u7b54\u534a \u53e5\uff0c\u6577\u6577\u884d\u884d\uff0c\u7edd\u4e0d\u591a\u8a00\u3002 \u3000\u3000\u5510\u7ecf\u5929\u77e5\u9053\u6c5f\u6e56\u7981\u5fcc\uff0c\u4ea6\u77e5\u9053\u4ed6\u4eec\u6697\u4e2d\u5bf9\u81ea\u5df1\u6212\u60e7\uff0c\u4fbf\u4e5f\u4e0d\u518d\u591a\u95ee\uff0c\u5fc3\u4e2d\u5374\u81ea\u60f3\u9053\uff1a\u201d \u90ed\u53f0\u57fa\uff0c\u8fd9\u4e2a\u540d\u5b57\u53ef\u6ca1\u542c\u8fc7\u3002\u201d\u897f\u85cf\u9752\u6d77\u65b0\u7586\u7b49\u5730\uff0c\u6709\u51e0\u79cd\u8d35\u91cd\u7684\u836f\u7269\uff0c\u5982\u7280\u725b\u9ec4\u3001\u623f\u9999\u3001 \u718a\u80c6\u4e4b\u7c7b\uff0c\u4f46\u5bf9\u666e\u901a\u836f\u7269\uff0c\u5374\u6781\u7f3a\u4e4f\uff0c\u6545\u6b64\u6bcf\u5e74\u90fd\u6709\u4e00\u4e8c\u5e2e\u8d22\u96c4\u52bf\u539a\u7684\u5927\u836f\u5546\uff0c\u8fd0\u5404\u79cd\u836f\u7269 \u5230\u897f\u85cf\uff0c\u4ea4\u6362\u5f53\u5730\u7684\u7279\u4ea7\u56de\u53bb\uff0c\u6bcf\u505a\u4e00\u6b21\u751f\u610f\uff0c\u5c11\u8bf4\u4e5f\u6709\u5341\u4e07\u4e24\u94f6\u5b50\u4ee5\u4e0a\u7684\u4ea4\u6613\uff0c\u66ff\u8fd9\u7b49\u836f \u5546\u4fdd\u9556\u7684\u4eba\uff0c\u975e\u6709\u60ca\u4eba\u7684\u672c\u9886\uff0c\u53ef\u4e0d\u6562\u8fe2\u8fe2\u4e07\u91cc\uff0c\u8dcb\u6d89\u957f\u9014\uff0c\u8d70\u8fd9\u4e0d\u6bdb\u4e4b\u5730\u3002 \u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u836f\u884c\u7684\u4eba\u5728\u5c4b\u5b50\u5f53\u4e2d\u71c3\u8d77\u4e00\u5927\u628a\u67af\u679d\uff0c\u56f4\u7740\u706b\u5806\u7761\u89c9\uff0c\u90a3\u4e09\u4e2a\u9556\u5e08\uff0c\u8f6e\u6d41\u5b88 \u591c\uff0c\u5510\u7ecf\u5927\u81ea\u5728\u4e00\u4e2a\u89d2\u843d\u5c55\u5f00\u968f\u8eab\u643a\u5e26\u7684\u8f7b\u4fbf\u5367\u5177\u7761\u4e86\u3002 \u3000\u3000\u521a\u778c\u4e0a\u773c\u775b\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u811a\u6b65\u4e4b\u58f0\uff0c\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff01 \u201d\u8001\u5e74\u9556\u5e08\u201c\u5618\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u95f9\u4ec0\u4e48\uff0c\u7ed9\u6211\u8eba\u4e0b\u3002\u201d\u90a3\u5c4b\u5b50\u7684\u4e24\u6247\u677f\u95e8\uff0c\u7167\u7740\u5c71\u4e2d\u5ba2\u5e97 \u7684\u89c4\u77e9\uff0c\u4e3a\u4e86\u65b9\u4fbf\u5ba2\u4eba\u7684\u6295\u5bbf\uff0c\u7ec8\u591c\u90fd\u662f\u865a\u63a9\u7740\u7684\uff0c\u90a3\u811a\u6b65\u58f0\u6765\u5f97\u5feb\u6781\uff0c\u4e00\u4e0b\u5b50\u5c31\u5230\u4e86\u95e8\u524d \uff0c\u95e8\u672a\u63a8\u5f00\uff0c\u5c31\u542c\u5f97\u563b\u563b\u54c8\u54c8\u7684\u7b11\u58f0\uff0c\u5510\u7ecf\u5929\u548c\u90a3\u4e09\u4e2a\u9556\u5e08\u90fd\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u58f0\u6e05\u8106\u975e\u5e38\uff0c\u6765 \u7684\u7adf\u662f\u5973\u5b50\uff01 \u3000\u3000\u53ea\u89c1\u4e24\u4e2a\u5973\u5b50\u5148\u5165\u95e8\u6765\u3002\u540e\u9762\u8ddf\u7740\u4e00\u4e2a\u7537\u5b50\uff1b\u90a3\u4e24\u4e2a\u5973\u5b50\u4e00\u8001\u4e00\u5c11\uff0c\u76f8\u8c8c\u76f8\u4f3c\uff0c\u4f3c\u4e4e\u662f \u4e24\u6bcd\u5973\uff0c\u5c11\u5973\u7684\u5934\u4e0a\u63d2\u7740\u4e00\u6735\u91ce\u82b1\u3002\u6625\u98ce\u6ee1\u9762\uff0c\u4e00\u8fdb\u95e8\u4fbf\u56b7\u7740\uff1a\u201c\u54c8\uff0c\u8fd9\u4e48\u591a\u4eba\uff0c\u53ef\u771f\u70ed\u95f9 \uff01\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u7a7f\u7740\u4e00\u4ef6\u7ee3\u6709\u767d\u7261\u4e39\u82b1\u7684\u6d45\u7ea2\u8863\u88f3\uff0c\u753b\u7740\u4e24\u9053\u957f\u957f\u7684\u7709\u6bdb\uff0c\u4f38\u51fa\u6307\u5934\u5728\u5634\u8fb9 \u5618\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u8bdd\u5c0f\u58f0\u70b9\u513f\uff0c\u522b\u5435\u9192\u4e86\u5ba2\u4eba\uff01\u201d\u662f\u6559\u8bad\u5973\u513f\u7684\u8bf4\u8bdd\uff0c\u4f46\u795e\u60c5\u8bed\u6c14\uff0c\u5374\u6ca1 \u6709\u6bcd\u4eb2\u7684\u5a01\u4e25\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u6211\u59e8\u5988\uff08\u51af\u7433\uff09\u662f\u5973\u4e2d\u602a\u7269\uff0c\u8fd9\u5987\u4eba\u770b\u6765\u4e5f \u548c\u5979\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u8fd9\u4e24\u6bcd\u5973\u8170\u95f4\u90fd\u6302\u7740\u4e00\u5f20\u5f39\u5f13\uff0c\u563b\u563b\u54c8\u54c8\u7684\u50cf\u4e00\u5bf9\u4e0d\u77e5\u4e16\u6545\u7684\u59d0\u59b9\uff0c\u7709\u5b87\u4e4b\u95f4\u5374\u7a33\u9690\u7740 \u4e00\u80a1\u8feb\u4eba\u7684\u82f1\u6c14\uff0c\u8ddf\u5728\u5979\u4eec\u80cc\u540e\u7684\u90a3\u4e2a\u7537\u5b50\uff0c\u5e74\u7ea6\u4e94\u65ec\uff0c\u8eab\u6750\u9b41\u4f1f\uff0c\u864e\u80cc\u718a\u8170\uff0c\u51fa\u6b65\u6c89\u7a33\uff0c \u867d\u6ca1\u89c1\u4ed6\u8eab\u4e0a\u5e26\u6709\u5175\u5668\uff0c\u663e\u7136\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\u3002 \u3000\u3000\u836f\u884c\u7684\u4eba\u672c\u6765\u5c31\u6ca1\u6709\u7761\uff0c\u8fd9\u4e09\u4eba\u4e00\u6765\uff0c\u4e2a\u4e2a\u90fd\u5077\u5077\u7528\u773c\u775b\u779f\u5979\u4eec\uff0c\u5c24\u5176\u662f\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556 \u5e08\uff0c\u81ea\u90a3\u4e24\u6bcd\u5973\u4e00\u8de8\u4eba\u95e8\uff0c\u773c\u775b\u4fbf\u4e0d\u79bb\u5de6\u53f3\u3002\u90a3\u5c11\u5973\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u66ae\u7136\u65a5\u9053\uff1a\u201c\u8981\u5c31\u5927\u5927 \u65b9\u65b9\u5730\u770b\u4e2a\u9971\uff0c\u9b3c\u9b3c\u795f\u795f\u5730\u5077\u5077\u5f20\u6211\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u6f02\u5e08\u81ca\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u4e00\u77aa\u773c\u775b\uff0c\u5c31\u60f3\u53d1\u4f5c\uff0c\u540e\u9762\u90a3\u8eab\u6750\u9b41\u4f1f\u7684\u8001\u8005\u4e00\u6b65\u8de8\u4e0a\u524d\u6765\uff0c \u53cc\u62f3\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u5a07\u7eb5\u60ef\u4e86\uff0c\u8bf7\u5404\u4f4d\u6055\u5979\u5e74\u5e7c\u65e0\u77e5\uff0c\u4f11\u4e0e\u5979\u4e00\u822c\u89c1\u8bc6\u3002\u201d\u5c06\u5973\u513f\u63a8\u4e0a \u4e00\u6b65\uff0c\u9053\uff1a\u201c\u971e\u513f\uff0c\u8fd8\u4e0d\u7ed9\u4f2f\u53d4\u4eec\u8d54\u793c\u4e48\uff1f\u201d\u90a3\u4e24\u4e2a\u9556\u5e08\u6b63\u81ea\u5600\u5495\uff1a\u201c\u4ec0\u4e48\u8def\u9053\u2026\u2026\u201d\u89c1\u90a3 \u7537\u5b50\u8d54\u8bdd\uff0c\u53c8\u53eb\u5973\u513f\u8d54\u793c\uff0c\u96be\u4ee5\u53d1\u4f5c\uff0c\u53cd\u89c9\u4e0d\u597d\u610f\u601d\uff0c\u90a3\u5c11\u5973\u5ffd\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u8bf4\u4ec0\u4e48\uff1f\u7239 \uff0c\u4f60\u542c\uff0c\u4ed6\u4eec\u9a82\u6211\uff01\u201d\u90a3\u8eab\u6750\u9b41\u4f1f\u7684\u8001\u8005\u9762\u8272\u4e00\u6c89\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4e00\u51fa\u95e8\u5c31\u5230\u5904\u60f9\u4eba\u7b11\u8bdd\u3002\u201d \u90a3\u8005\u9556\u5e08\u54b3\u4e86\u4e00\u58f0\uff0c\u6025\u5fd9\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u5b69\u513f\u5bb6\u8bf4\u7b11\uff0c\u8001\u5144\u4e0d\u5fc5\u5f53\u771f\uff0c\u6211\u8fd9\u4e24\u4e2a\u4f19\u8ba1\u7c97\u7c97\u9c81\u9c81 \uff0c\u4e0d\u77e5\u793c\u6570\uff0c\u8fd9\u4f4d\u59d1\u5a18\uff0c\u4f60\u4e5f\u83ab\u602a\u3002\u201d \u3000\u3000\u9556\u884c\u4f19\u8ba1\u548c\u90a3\u5c11\u5973\u90fd\u6c89\u7740\u9762\u5b54\uff0c\u8d70\u8fc7\u4e00\u8fb9\uff0c\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u522b\u55b3\u53e8\u5566\uff0c\u4e0d\u662f\u8bf4 \u4eba\u5bb6\u8981\u7761\u89c9\u5417\uff1f\u201d\u5979\u5e73\u7d20\u5ba0\u60ef\u5973\u513f\uff0c\u89c1\u9556\u884c\u4f19\u8ba1\u548c\u5979\u5973\u513f\u201c\u5435\u67b6\u201d\uff0c\u4e5f\u4e0d\u95ee\u8c01\u662f\u8c01\u975e\uff0c\u5fc3\u4e2d \u4e0d\u5927\u9ad8\u5174\uff0c\u8fd9\u4e00\u53e5\u8bdd\u660e\u91cc\u662f\u8bf4\u5979\u7684\u8001\u4f34\uff0c\u6697\u4e2d\u8c01\u4e5f\u542c\u5f97\u51fa\u6765\uff0c\u5979\u662f\u607c\u4e86\u9556\u884c\u7684\u4eba\u3002\u8001\u9556\u5e08\u5fc3 \u5185\u557c\u5495\uff0c\u5fc3\u9053\uff1a\u201c\u6c5f\u6e56\u9053\u4e0a\uff0c\u6700\u5fcc\u548c\u5c1a\u3001\u9053\u58eb\u3001\u4e66\u751f\u3001\u5987\u4eba\u4e4b\u8f88\uff0c\u8fd9\u4e24\u4e2a\u96cc\u513f\uff0c\u80cc\u7740\u4e00\u5f20\u5f39 \u5f13\uff0c\u53c8\u4e0d\u50cf\u5356\u89e3\u7684\u5a18\u513f\uff0c\u4eca\u665a\u53ef\u5f97\u5c0f\u5fc3\u9632\u5907\u3002\u201d \u3000\u3000\u8fd9\u5bf9\u6bcd\u5973\u79bb\u5f00\u9556\u884c\u7684\u4eba\uff0c\u60f3\u627e\u5bfb\u4e00\u5904\u5408\u9002\u7684\u5730\u65b9\uff0c\u5c55\u5f00\u5367\u5177\uff0c\u5510\u7ecf\u5929\u501a\u7740\u5899\u58c1\uff0c\u8fd8\u672a\u5367 \u4e0b\uff0c\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u90a3\u4e2d\u5e74\u5987\u4eba\u76ee\u9732\u5f02\u5149\uff0c\u4e00\u6b65\u4e00\u6b65\u5411\u4ed6\u7f13\u7f13\u884c\u6765\uff0c\u8d70\u5230\u79bb\u4ed6\u6570\u6b65\u4e4b\u5730\uff0c\u5ffd\u7136 \u7ad9\u4f4f\uff0c\u76f4\u4e0a\u76f4\u4e0b\u7684\u6253\u91cf\u4ed6\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e00\u5c42\u7ea2\u6655\uff0c\u624b\u71c3\u88d9\u5e26\uff0c\u597d\u50cf\u4e00\u4e2a\u5a07\u7f9e\u7684\u5c11\u5973\uff0c\u7a81\u7136\u4e4b\u95f4 \uff0c\u78b0\u5230\u4e86\u591a\u5e74\u4e0d\u89c1\u7684\u60c5\u90ce\uff0c\u90a3\u8eab\u6750\u9b41\u4f1f\u7684\u8001\u8005\u8d70\u6765\u9053\uff1a\u201c\u9752\u59b9\uff0c\u54b1\u4eec\u5230\u90a3\u8fb9\u5899\u89d2\u53bb\u5427\u3002\u201d\u5ffd \u7136\u53cc\u773c\u53d1\u5149\uff0c\u4e5f\u5446\u5446\u5730\u671b\u7740\u5510\u7ecf\u5929\u3002\u5510\u7ecf\u5929\u5947\u602a\u4e4b\u6781\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u600e\u4e48\u8001\u662f\u78b0\u7740\u83ab\u540d\u5176 \u5999\u7684\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u5446\u4e86\u4e00\u5446\uff0c\u4f3c\u662f\u53d1\u89c9\u4e86\u81ea\u5df1\u7684\u5931\u6001\uff0c\u5c34\u5c2c\u4e00\u7b11\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u4f60\u8d35\u59d3\uff1f\u201d \u5510\u7ecf\u5929\u9053\uff0c\u201c\u5c0f\u59d3\u5510\u3002\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u59d3\u5510\uff1f\u201d\u836f\u884c\u7684\u4f19\u8ba1\u4e0d\u77e5\u662f\u8c01\u201c\u5618 \u201d\u4e86\u4e00\u58f0\uff0c\u90a3\u8001\u8005\u9053\uff1a\u201c\u8bf4\u8bdd\u5c0f\u58f0\u70b9\u513f\u3002\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u538b\u4f4e\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u5510\u76f8\u516c\uff0c\u4f60\u662f\u4ece\u54ea \u513f\u6765\u7684\uff0c\u8981\u4e0a\u54ea\u513f\u53bb\uff1f\u201d\u90a3\u5c11\u5973\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5988\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u76d8\u95ee\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u7a0d\u7a0d\u8fdf\u7591\uff0c\u7ec8\u4e8e\u7b54\u9053\uff1a\u201c\u6211\u4ece\u897f\u85cf\u6765\uff0c\u51c6\u5907\u5230\u5ddd\u897f\u53bb\u627e\u4e2a\u670b\u53cb\u3002\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u9053 \uff1a\u201c\u55ef\uff0c\u4ece\u897f\u85cf\u6765\u7684\uff1f\u770b\u4f60\u7684\u6837\u513f\uff0c\u7ec3\u8fc7\u597d\u591a\u5e74\u7684\u6b66\u529f\u5427\uff1f\u201d\u773c\u5149\u843d\u5728\u5979\u7684\u6e38\u9f99\u5251\u4e0a\uff0c\u5510\u7ecf \u5929\u5c06\u8fd9\u67c4\u5251\u6795\u5728\u8eab\u4e0b\uff0c\u53ea\u9732\u51fa\u534a\u622a\u5251\u67c4\u3002\u90a3\u5c11\u5973\u53c8\u662f\u201c\u683c\u683c\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5988\uff0c\u4f60\u771f\u662f\u8001\u7cca \u6d82\u5566\uff01\u4f60\u4e0d\u89c1\u4eba\u5bb6\u5e26\u7740\u5251\u5417\uff1f\u8fd8\u7528\u95ee\u7684\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5355\u4eba\u72ec\u884c\uff0c\u5e26\u628a\u5251\u4e0d\u8fc7\u58ee\u58ee\u80c6\u5b50\u5427 \u4e86\uff0c\u6211\u54ea\u61c2\u4ec0\u4e48\u6b66\u529f\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u5fae\u5fae\u4e00\u7b11\uff0c\u4f3c\u662f\u8d5e\u4ed6\u8c26\u865a\uff0c\u53c8\u4f3c\u5632\u4ed6\u8bf4\u8c0e\u3002\u90a3\u4e2d\u5e74\u5987\u4eba\u5ffd\u9053\uff0c\u201c\u6211\u5411\u4f60\u6253\u542c\u4e00\u4e2a \u4eba\uff0c\u4e5f\u662f\u59d3\u5510\u7684\uff0c\u4e0d\u77e5\u662f\u5426\u4f60\u7684\u672c\u5bb6\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8c01\uff1f\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u53eb\u505a \u5510\u6653\u6f9c\uff01\u201d\u5510\u7ecf\u5929\u5fc3\u5934\u4e00\u9707\uff0c\u987b\u77e5\u4ed6\u7236\u6bcd\u5f53\u5e74\u5927\u95f9\u6e05\u5bab\uff0c\u6740\u4e86\u96cd\u6b63\uff0c\u867d\u7136\u4e8b\u9694\u591a\u5e74\uff0c\u5230\u5e95\u8fd8 \u662f\u671d\u5ef7\u7684\u94a6\u72af\u3002\u5510\u7ecf\u5929\u5728\u964c\u751f\u4eba\u7684\u9762\u524d\uff0c\u5982\u4f55\u6562\u6cc4\u9732\u51fa\u6765\uff1f\u90a3\u5987\u4eba\u4e00\u5bf9\u6c34\u6c6a\u6c6a\u7684\u773c\u775b\uff0c\u542b\u7740 \u7126\u6025\u4e0e\u671f\u5f85\u7684\u795e\u60c5\uff0c\u770b\u6765\u5b9e\u65e0\u4e1d\u6beb\u6076\u610f\uff0c\u5510\u7ecf\u5929\u5b9a\u4e00\u5b9a\u795e\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5510\u5927\u4fa0\u7684\u540d\u5b57\u6211\u662f \u542c\u8bf4\u8fc7\u7684\uff0c\u4f46\u4ed6\u4e43\u4e00\u6d3e\u5b97\u5e08\uff0c\u6211\u4ef0\u6155\u975e\u5e38\uff0c\u5374\u662f\u65e0\u7f18\u62dc\u89c1\u3002\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u597d\u751f\u5931\u671b\uff0c\u90a3\u5c11\u5973 \u7b11\u9053\uff1a\u201c\u5988\uff0c\u4f60\u65f6\u5e38\u548c\u6211\u4eec\u63d0\u8d77\u5510\u4f2f\u4f2f\uff0c\u60f3\u8fd9\u4f4d\u5510\u4f2f\u4f2f\u9ad8\u5904\u5929\u5c71\uff0c\u5bfb\u5e38\u4eba\u5c82\u80fd\u89c1\u5230\uff1f\u4f60\u78b0\u5230 \u4ece\u56de\u7586\u897f\u85cf\u6765\u7684\u4eba\u4fbf\u95ee\uff0c\u4e5f\u4e0d\u6015\u4eba\u7b11\u8bdd\u4e48\uff1f\u201d\u88c5\u51fa\u5979\u7236\u4eb2\u5e73\u65e5\u8bf4\u8bdd\u7684\u795e\u6c14\uff0c\u90a3\u5987\u4eba\u7ed9\u5979\u7684\u5973 \u513f\u9017\u5f97\u7b11\u8d77\u6765\uff0c\u65a5\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u5012\u6559\u8bad\u6211\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u6015\u5979\u7f57\u55e6\u76d8\u95ee\uff0c\u6253\u4e86\u4e00\u4e2a\u5475\u6b20\uff0c\u90a3\u8001\u8005\u9053\uff1a\u201c\u971e\u513f\uff0c\u9752\u59b9\uff0c\u8fd9\u4f4d\u5c0f\u54e5\u660e\u5929\u8fd8\u8981\u8d76 \u8def\uff0c\u54b1\u4eec\u4e5f\u8be5\u5b89\u6b47\u5566\u3002\u201d\u5728\u79bb\u5510\u7ecf\u5929\u6570\u5c3a\u4e4b\u5730\u5c55\u5f00\u5367\u5177\uff0c\u501a\u7740\u5899\u58c1\uff0c\u534a\u5750\u534a\u5367\u3001\u95ed\u76ee\u5047\u5bdd\u3002 \u3000\u3000\u4e24\u65e5\u4e4b\u95f4\uff0c\u8fde\u9022\u8bb8\u591a\u602a\u5f02\u4e4b\u4e8b\uff0c\u5510\u7ecf\u5929\u54ea\u7761\u5f97\u7740\uff0c\u5fc3\u4e2d\u4ed4\u7ec6\u7422\u78e8\uff0c\u731c\u4e0d\u900f\u8fd9\u7236\u5973\u4e09\u4eba\u7684 \u6765\u5386\u3002\u5077\u773c\u659c\u7aa5\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\uff0c\u624b\u4e2d\u63d0\u7740\u5175\u5203\uff0c\u5b88\u7740\u706b\u5806\uff0c\u4e5f\u65f6\u4e0d\u65f6\u7684\u5077\u7aa5\u5979\u4eec\uff0c \u90a3\u8001\u9556\u5e08\u5219\u547c\u547c\u5730\u6253\u9f3e\uff0c\u5510\u7ecf\u5929\u4e00\u542c\uff0c\u5c31\u77e5\u4ed6\u662f\u5047\u88c5\u719f\u7761\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\u3002\u836f\u884c\u7684\u4f19\u8ba1\u71ac\u4e0d\u7740\u75b2\u5026\uff0c\u9f3e\u58f0\u5927\u4f5c\uff0c\u90fd\u7761\u7740\u4e86\u3002\u90a3\u8001\u9556\u5e08\u5ffd\u5730\u5f20 \u5f00\u773c\u775b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff0c\u201c\u5c0f\u5fc3\uff01\u201d\u968f\u5373\u63d0\u8d77\u4e00\u652f\u70df\u6746\uff0c\u90a3\u70df\u9505\u6709\u8336\u676f\u53e3\u822c\u5927\u5c0f\uff0c\u9ed1\u9edd\u9edd\u7684\uff0c\u663e \u662f\u94c1\u94f8\u7684\u70df\u6746\uff0c\u90a3\u8001\u9556\u5e08\u88c5\u4e86\u4e00\u888b\u65f1\u70df\uff0c\u547c\u547c\u7684\u5438\u8d77\u6765\u3002\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u4e24\u6247\u677f\u95e8\u7ed9 \u4eba\u4e00\u811a\u8e22\u5f00\uff0c\u6d8c\u8fdb\u5341\u51e0\u4e2a\u4eba\uff0c\u8d70\u5728\u524d\u5934\u7684\u662f\u4e2a\u56db\u5341\u5de6\u53f3\u3001\u8eab\u6750\u9ad8\u5927\u7684\u6c49\u5b50\uff0c\u63d0\u7740\u4e00\u5f20\u5f39\u5f13\uff0c \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u80a5\u7f8a\u90fd\u8d76\u5230\u5c4b\u91cc\u6765\u4e86\uff0c\u54b1\u4eec\u53ef\u4e0d\u7528\u8d39\u529b\u5566\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\u970d\u5730\u8df3\u8d77\uff0c\u4fbf\u6b32\u4e0a\u524d\u8fce\u654c\uff0c\u90a3\u8001\u9556\u5e08\u4e00\u8fc8\u6b65\uff0c\u62e6\u5728\u4ed6\u4eec\u524d\u9762\uff0c\u5c06\u65f1\u70df\u7ba1 \u5f90\u5f90\u4e00\u6325\uff0c\u5de6\u624b\u629a\u7740\u70df\u7ba1\uff0c\u56e2\u56e2\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u4eec\u8bf7\u4e86\u3002\u5728\u4e0b\u662f\u5317\u4eac\u632f\u5a01\u9556\u5c40\u7684\u90ed \u53f0\u57fa\uff0c\u5728\u9556\u884c\u4e0a\u6df7\u53e3\u996d\u5403\uff0c\u8bf7\u6055\u5728\u4e0b\u773c\u62d9\u8033\u9675\uff0c\u4e0d\u77e5\u5be8\u4e3b\u5728\u6b64\u5f00\u5c71\u7acb\u67dc\uff0c\u672a\u6295\u62dc\u5e16\uff0c\u5931\u793c\u4e4b \u6781\u3002\u4ffa\u90ed\u67d0\u5728\u8fd9\u53a2\u966a\u7f6a\u4e86\u3002\u201d \u3000\u3000\u76d7\u9b41\u540e\u9762\u7684\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u624d\u4e0d\u7406\u8fd9\u5957\u865a\u793c\u7e41\u6587\u3002\u54b1\u4eec\u53ef\u53ea\u77e5\u9053\u80a5\u7f8a\u5230 \u53e3\uff0c\u5c31\u5f97\u968f\u624b\u64d2\u6765\uff0c\u6cbd\u4e4b\u54c9\uff01\u5f53\u5bb6\u7684\uff0c\u4f60\u8bf4\u53ef\u662f\uff1f\u201d\u90a3\u76d7\u9b41\u6253\u91cf\u4e86\u90ed\u53f0\u57fa\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5c0f \u4e09\u5b50\u4f11\u8981\u6cb9\u5634\u6ed1\u820c\u3002\u4ffa\u77a7\u8fd9\u4f4d\u90ed\u9556\u5934\u4e5f\u662f\u4e00\u5c0a\u4eba\u7269\uff0c\u6c5f\u6e56\u4e0a\u54ea\u91cc\u4e0d\u4ea4\u670b\u53cb\uff0c\u5c31\u8fd9\u4e48\u529e\u5427\uff0c\u8fd9 \u6279\u836f\u6750\uff0c\u53ef\u5de7\u6b63\u5408\u5c71\u5be8\u4e4b\u7528\uff0c\u54b1\u4eec\u5c31\u4e0d\u5ba2\u6c14\u8981\u7559\u4e0b\u5566\uff0c\u9556\u884c\u7684\u4f19\u8ba1\u53ef\u4ee5\u8d70\u5f00\uff0c\u5e94\u5f97\u7684\u9556\u94f6\u54b1 \u4eec\u4e5f\u90fd\u4e0d\u8981\u3002\u597d\uff0c\u90ed\u9556\u5934\uff0c\u4f60\u77a7\u8fd9\u6837\u53ef\u591f\u670b\u53cb\u4e86\u5427\uff1f\u201d\u90a3\u836f\u5546\u5413\u5f97\u6296\u6296\u7d22\u7d22\uff0c\u77a7\u7740\u90ed\u9556\u5934\uff0c \u751f\u6015\u4ed6\u4e0e\u5f3a\u76d7\u59a5\u534f\u3002 \u3000\u3000\u90ed\u53f0\u57fa\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u591a\u627f\u5be8\u4e3b\u624b\u4e0b\u7559\u60c5\uff0c\u672c\u8be5\u542c\u5be8\u4e3b\u7684\u5429\u5490\uff0c\u53ea\u662f\u98df\u541b\u4e4b\u7984 \uff0c\u62c5\u541b\u4e4b\u5fe7\uff0c\u96c7\u4e3b\u5c31\u662f\u54b1\u4eec\u9556\u884c\u7684\u8863\u98df\u7236\u6bcd\uff0c\u54b1\u4eec\u82e5\u662f\u53ea\u56fe\u81ea\u5df1\uff0c\u5f03\u4e86\u8863\u98df\u7236\u6bcd\uff0c\u4ee5\u540e\u8fd9\u9556 \u884c\u7684\u751f\u610f\u4e5f\u4e0d\u7528\u505a\u5566\uff0c\u9556\u884c\u4e0a\u4e0b\u6570\u5341\u4eba\u90fd\u5f97\u997f\u6b7b\uff0c\u5be8\u4e3b\u3002\u4ffa\u8001\u673d\u8fd8\u5f97\u8bf7\u4f60\u4f53\u8c05\u4e0b\u60c5\u3002\u201d \u3000\u3000\u90a3\u76d7\u9b41\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90ed\u9556\u5934\u679c\u7136\u591f\u4e49\u6c14\uff0c\u4f46\u4ffa\u5144\u5f1f\u4eec\u82e5\u4e0d\u505a\u4e70\u5356\uff0c\u96be\u9053\u90ed\u9556\u5934\u53eb\u6211 \u4eec\u559d\u897f\u5317\u98ce\u4e0d\u6210\uff1f\u201d\u90a3\u4e24\u4e2a\u4e2d\u5e74\u6f02\u5e08\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u7136\u4e0d\u5356\u9762\u5b50\uff0c\u5e08\u7236\uff0c\u54b1\u4eec\u8fd8\u4e0e\u4ed6\u591a\u8bf4\u505a\u751a \uff1f\u563f\uff0c\u8bf4\u4e0d\u5f97\u53ea\u6709\u5175\u5203\u4e0a\u5b9a\u8f93\u8d62\u4e86\uff01\u201d\u90a3\u76d7\u9b41\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u8fd9\u4e24\u4f4d\u5c11\u9556\u5934\u5e72\u8106\uff01\u201d \u6479\u5730\u5f39\u5f13\u4e00\u62fd\uff0c\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\u4e3e\u5200\u76f8\u683c\uff0c\u5ffd\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5f39\u4e38\u5ffd\u5730\u88c2\u5f00\uff0c\u631f\u7740\u4e00 \u6e9c\u706b\u5149\uff0c\u767b\u65f6\u71c3\u70e7\u4e86\u8863\u670d\uff0c\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\u5c31\u5730\u4e00\u6eda\uff0c\u76ae\u8089\u7126\u75db\uff0c\u8dc3\u8d77\u6765\u65f6\u3001\u53ea\u89c1\u8001\u9556\u5e08\u5df2 \u4e0e\u76d7\u9b41\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u90a3\u8001\u9556\u5e08\u5e74\u7eaa\u867d\u8001\uff0c\u8eab\u624b\u53ef\u662f\u77eb\u6377\u4e4b\u6781\uff0c\u76d7\u9b41\u8fd8\u6765\u4e0d\u53ca\u62fd\u5f13\uff0c\u4ed6\u7684\u65f1\u70df\u888b\u5df2\u8fce\u5934\u78d5\u4e0b\uff0c \u76d7\u9b41\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5c06\u94c1\u80ce\u5f13\u4e00\u62c9\uff0c\u7528\u5f13\u6765\u5272\u8001\u9556\u5e08\u7684\u624b\u8155\u3001\u8fd9\u4e00\u62db\u4f7f\u5f97\u751a\u662f\u602a\u5f02\u3001\u90a3 \u8001\u9556\u5e08\u4e00\u4e2a\u8f6c\u8eab\uff0c\u70df\u6746\u53cd\u624b\u4e0a\u9001\uff0c\u500f\u5730\u5f53\u6210\u5c0f\u82b1\u67aa\u4f7f\u7528\uff0c\u8ddf\u7740\u4e00\u4e2a\u4e00\u8fdb\u6b65\u8fde\u73af\uff0c\u70df\u888b\u4e00\u6572\uff0c \u53d8\u6210\u4e86\u94c1\u9524\u7684\u624b\u6cd5\u3002\u518d\u4e00\u8f6c\uff0c\u5374\u53c8\u5f53\u6210\u4e86\u5224\u5b98\u7b14\uff0c\u70b9\u6253\u90a3\u76d7\u9b41\u808b\u4e0b\u7684\u8f6f\u9ebb\u7a74\u3002\u90a3\u76d7\u9b41\u4e3e\u8d77\u94c1 \u5f13\uff0c\u5de6\u8fce\u53f3\u6321\uff0c\u4e5f\u662f\u63a5\u8fde\u7528\u4e86\u4e09\u79cd\u624b\u6cd5\uff0c\u89e3\u5f00\u4e86\u8001\u9556\u5e08\u4e09\u79cd\u4e0d\u540c\u7684\u62db\u6570\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4eba\u9053 \u5a01\u9556\u5c40\u7684\u9556\u5934\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u4f46\u78b0\u5230\u4ffa\u98de\u706b\u5f39\u6731\u5b9a\uff0c\u8fd9\u5a01\u4e5f\u6050\u6015\u4e0d\u80fd\u626c\u5566\uff01\u201d\u624b\u6cd5\u4e00\u53d8\uff0c\u4e00 \u5f20\u94c1\u5f13\u76d8\u65cb\u98de\u821e\uff0c\u5f13\u80cc\u626b\u51fb\u3002\u5f13\u5f26\u62c9\u5272\uff0c\u5484\u5484\u8feb\u4eba\u3002\u7528\u94c1\u5f13\u5f53\u4f5c\u5175\u5668\uff0c\u4e43\u662f\u5728\u5341\u516b\u822c\u5175\u5668\u4e4b \u5916\u7684\u72ec\u7279\u6b66\u6280\uff0c\u90a3\u8001\u9556\u5e08\u53ef\u8fd8\u6ca1\u6709\u89c1\u8fc7\uff0c\u9976\u4ed6\u6709\u6570\u5341\u5e74\u706b\u5019\uff0c\u4e5f\u53ea\u662f\u582a\u582a\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u4e2d\u5e74\u9556\u5e08\u5728\u5730\u4e0b\u722c\u8d77\uff0c\u76d7\u4f17\u5df2\u8702\u6d8c\u800c\u4e0a\uff0c\u836f\u884c\u7684\u4f19\u8ba1\u4e5f\u7fa4\u8d77\u8fce\u654c\uff0c\u4e24\u8fb9\u4eba\u6570\u5dee\u4e0d \u591a\uff0c\u76d7\u4f17\u80dc\u5728\u901a\u6653\u6b66\u827a\u3002\u836f\u884c\u5219\u6709\u4e24\u4e2a\u9556\u5e08\u529b\u6218\uff0c\u7b49\u4e8e\u5e73\u6dfb\u4e86\u5341\u6765\u4e2a\u4eba\uff0c\u8fd9\u6df7\u6218\u4e00\u65f6\u95f4\u96be\u5206 \u4e0a\u4e0b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5750\u4e86\u8d77\u6765\uff0c\u4e0d\u613f\u5148\u9732\u8eab\u4efd\uff0c\u4e14\u77a7\u90a3\u7236\u5973\u4e09\u4eba\u7684\u52a8\u9759\u3002\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u683c\u683c\u7b11\u9053\uff1a\u201d \u5988\uff0c\u8fd9\u5f3a\u76d7\u4e5f\u4f1a\u4f7f\u5f39\u5f13\u5462\uff01\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u5478\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u5c31\u53ea\u6709\u4f60\u4f1a\u4f7f\u5f39\u5f13\u4e48\uff1f\u201d\u90a3 \u5c11\u5973\u9053\uff1a\u201c\u563f\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u5c31\u53ea\u6709\u54b1\u4eec\u6768\u5bb6\u7684\u5f39\u5f13\u6253\u5f97\u6700\u597d\uff0c\u5988\uff0c\u6211\u53ef\u8bb0\u5f97\u4f60\u8bf4\u8fc7\u8fd9\u8bdd\u3002\u201d \u90a3\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u4f60\u5fd9\u4ec0\u4e48\uff1f\u4e14\u8ba9\u4ed6\u4eec\u5403\u70b9\u82e6\u5934\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6768\u5bb6\u7684\u5f39\u5f13 \uff1f\u54ea\u4e00\u4e2a\u6768\u5bb6\u7684\u5f39\u5f13\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u76d7\u9b41\u4e00\u58f0\u602a\u5578\uff0c\u5f13\u5f26\u4e00\u5f39\uff0c\u5728\u8001\u9556\u5e08\u7684\u80a9\u4e0a\u62c9\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u5b50\uff0c\u90a3\u8001\u954d\u5e08\u8e09 \u8e09\u8dc4\u8dc4\u5012\u9000\u4e09\u6b65\uff0c\u5927\u559d\u9053\uff1a\u201c\u4ffa\u4e0e\u4f60\u62fc\u4e86\uff01\u201c\u90a3\u76d7\u9b41\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u65f6\u5019\u6709\u7684\u662f\uff01 \u201d\u84e6\u5730\u5f20\u5f13\u8fde\u53d1\uff0c\u55d6\u55d6\u55d6\uff0c\u4e00\u8fde\u6253\u51fa\u5341\u51e1\u679a\u8fde\u73e0\u5f39\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4e0b\u624b\u6cd5\u8fd8\u7b97\u4e0d\u9519\u3002\u201d\u90a3\u76d7\u9b41\u7684\u7409\u78fa\u706b\u7130\u5f39\u4e00\u53d1\uff0c\u7acb\u523b\u6709\u51e0\u4e2a\u836f\u884c\u4f19 \u8ba1\u5e94\u58f0\u5012\u5730\uff0c\u8fd8\u6709\u51e0\u4e2a\u7ed9\u70e7\u7126\u4e86\u76ae\u8089\uff0c\u6025\u5fd9\u4f0f\u5730\u6253\u6eda\u3002\u90a3\u76d7\u9b41\u5f39\u5f13\u8fde\u66f3\uff0c\u5ffd\u542c\u5f97\u90a3\u8001\u8005\u9053\uff1a \u201c\u971e\u513f\uff0c\u77a7\u4f60\u6280\u75d2\u96be\u71ac\uff0c\u73b0\u5728\u53ef\u4ee5\u51fa\u624b\u5566\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u84e6\u7136\u8d77\u7acb\uff0c\u5f39\u5f13\u4e00\u66f3\uff0c\u75be\u4f3c\u6d41\u661f\uff0c\u628a\u90a3\u76d7\u9b41\u7684\u706b\u7130\u5f39\u90fd\u5728\u7a7a\u4e2d\u78b0\u88c2\uff0c \u706b\u661f\u56db\u5904\u98de\u6563\uff0c\u90a3\u76d7\u9b41\u5927\u6012\uff0c\u4e00\u4e2a\u95ea\u8eab\uff0c\u907f\u5f00\u4e86\u90a3\u8001\u9556\u5e08\u7684\u4e00\u51fb\uff0c\u5f39\u5982\u96e8\u53d1\uff0c\u90fd\u5411\u90a3\u5c11\u5973\u6253 \u6765\uff01 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u971e\u513f\uff0c\u4f60\u7684\u6253\u6cd5\u8fd8\u4e0d\u6210\uff0c\u4f60\u77a7\u6e05\u695a\u4e86\uff01\u201d\u5f39\u5f13\u4e00\u66f3\uff0c\u4e25\u5982\u51b0\u96f9\u4e71\u843d\uff0c \u5c06\u90a3\u4e9b\u706b\u7130\u5f39\u90fd\u634f\u4e86\u56de\u53bb\uff0c\u5f39\u4e5d\u7adf\u4f3c\u957f\u7740\u773c\u775b\u4e00\u6837\uff0c\u90fd\u843d\u5230\u76d7\u4f17\u7684\u8eab\u4e0a\uff0c\u70e7\u5f97\u4ed6\u4eec\u6eda\u5730\u54c0\u53f7 \uff0c\u76d7\u9b41\u4e5f\u51e0\u4e4e\u7740\u4e86\u4e00\u5f39\uff0c\u52c3\u7136\u5927\u6012\u3002\u90a3\u8001\u9556\u5e08\u6b63\u5728\u8ffd\u51fb\u76d7\u9b41\uff0c\u8981\u4e0e\u4ed6\u62fc\u547d\uff0c\u9aa4\u89c1\u8fd9\u4e24\u6bcd\u5973\u51fa \u624b\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u90a3\u76d7\u9b41\u53cd\u8eab\u4e00\u4e2a\u201c\u8e6c\u811a\u201d\u5411\u8001\u9556\u5e08\u80f8\u53e3\u5012\u8e22\uff0c\u773c\u89c1\u90a3\u8001\u9556\u5e08\u5c31\u8981\u53d7\u4f24\uff0c\u90a3\u8eab \u6750\u9b41\u4f1f\u7684\u8001\u8005\u9053\uff1a\u201c\u9752\u59b9\uff0c\u4f60\u6536\u62fe\u8fd9\u4e9b\u76d7\u515a\u3002\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u4e25\u5982\u5140\u9e70\u4e0b\u51fb\uff0c\u4e00\u628a\u5c31\u5c06\u90a3\u76d7\u9b41 \u5012\u63d0\u8d77\u6765\uff0c\u6454\u51fa\u95e8\u5916\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u7b11\uff0c\u7eb7\u4e71\u4e4b\u4e2d\u8c01\u4e5f\u6ca1\u6709\u77a7\u51fa\uff0c\u7adf\u7136\u53c8\u6709\u4e00\u4e2a\u964c\u751f\u7684\u6c49\u5b50\u6e9c\u4e86\u8fdb\u6765\uff0c\u5510\u7ecf\u5929 \u542c\u8fd9\u7b11\u58f0\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u5f20\u773c\u77a7\u65f6\uff0c\u53ea\u89c1\u6765\u4eba\u62ab\u7740\u4e00\u8eab\u7834\u7834\u70c2\u70c2\u7684\u9ebb\u8863\uff0c\u63d0\u7740\u4e00\u6839\u9ed1\u6f06\u6f06\u7684\u62d0 \u6756\uff0c\u6ee1\u9762\u7ea2\u4e91\uff0c\u4e0b\u988a\u4e24\u4e2a\u7599\u7629\uff0c\u4e00\u7b11\u4e4b\u65f6\uff0c\u7275\u52a8\u808c\u8089\uff0c\u66f4\u663e\u5f97\u4e11\u6076\u602a\u5f02\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u4ed6 \u524d\u65e5\u5728\u96c0\u513f\u5c71\u6700\u9669\u5cfb\u4e4b\u5904\u6240\u78b0\u89c1\u7684\u602a\u9ebb\u75af\uff01 \u3000\u3000\u5510\u7ecf\u5929\u659c\u501a\u5899\u58c1\uff0c\u5c06\u4e0a\u8863\u4e00\u62c9\uff0c\u906e\u4e86\u534a\u8fb9\u8138\u5b54\uff0c\u53ea\u89c1\u90a3\u9ebb\u75af\u5c11\u5e74\u4f38\u624b\u4e00\u683c\uff0c\u90a3\u8001\u8005\u767b\u65f6 \u9000\u4e86\u4e09\u6b65\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u9ebb\u75af\u6076\u4e10\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u6211\uff0c\u6211\u53ef\u77e5\u9053\u4f60\uff01\u4f60\u5728\u5c71\u4e1c\u9f0e\u9f0e\u5927\u540d\uff0c\u6211\u4ee5\u4e3a\u4f60\u8fd8\u5728\u5c71\u4e1c \uff0c\u66fe\u8bbf\u8fc7\u4f60\u4e24\u6b21\uff0c\u90fd\u6ca1\u89c1\u7740\uff0c\u8c01\u77e5\u4f60\u5374\u5728\u6b64\u3002\u54c8\u54c8\uff0c\u771f\u5999\u6781\u5566\uff01\u542c\u8bf4\u4f60\u7684\u4e94\u884c\u62f3\u662f\u5927\u6c5f\u5357\u5317 \u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u6211\u5012\u8981\u89c1\u8bc6\u89c1\u8bc6\uff01\u54ce\uff0c\u8fd8\u6709\u4f60\u8fd9\u4f4d\u592b\u4eba\u542c\u8bf4\u662f\u94c1\u638c\u795e\u5f39\u7684\u540e\u4eba\uff0c\u5509\uff0c\u4f59\u751f\u4e5f\u665a \uff0c\u6765\u4e0d\u53ca\u89c1\u94c1\u638c\u795e\u5f39\uff0c\u5374\u5e78\u8fd8\u80fd\u5728\u8fd9\u513f\u9047\u5230\u4e8c\u5341\u591a\u5e74\u3001\u540d\u9707\u6c5f\u6e56\u7684\u524d\u8f88\u5973\u4fa0\uff0c\u8bf4\u4e0d\u5f97\u4e5f\u8981\u4e00 \u5e76\u9886\u6559\u5566\uff01\u201d \u3000\u3000\u9002\u624d\u88ab\u8001\u8005\u6454\u51fa\u95e8\u5916\u7684\u76d7\u9b41\uff0c\u53c8\u8d70\u4e86\u8fdb\u6765\uff0c\u542c\u4e86\u8fd9\u6076\u4e10\u7684\u8bf4\u8bdd\uff0c\u4e00\u65f6\u672a\u77a7\u6e05\u695a\uff0c\u4ee5\u4e3a\u4ed6 \u662f\u4e2a\u72ec\u811a\u5927\u76d7\uff0c\u5927\u559c\u8fc7\u671b\uff0c\u53eb\u9053\uff1a\u201c\u201c\u5582\uff0c\u80a5\u7f8a\u5404\u5206\u4e00\u8fb9\uff0c\u4e00\u7897\u6c34\u5927\u5bb6\u559d\u5566\uff01\u201d\u90a3\u9ebb\u75af\u500f\u5730 \u7741\u773c\u9053\uff1a\u201c\u8c01\u7406\u4f60\u7684\u80a5\u7f8a\uff1f\u4f60\u7ed9\u6211\u6eda\uff01\u201d\u53cc\u624b\u4e00\u4e3e\uff0c\u90a3\u76d7\u9b41\u548c\u8001\u8005\u77a7\u6e05\u695a\u4e86\u4ed6\u624b\u81c2\u4e0a\u5927\u5927\u5c0f \u5c0f\u7684\u7599\u7629\uff0c\u4e0d\u7531\u5f97\u90fd\u9a87\u53eb\u4e00\u58f0\uff0c\u53ea\u89c1\u90a3\u9ebb\u75af\u6076\u4e10\u4f38\u624b\u4e00\u6325\uff0c\u5c06\u90a3\u4e2a\u76d7\u9b41\u8fde\u540c\u4e00\u6247\u677f\u95e8\uff0c\u90fd\u649e \u5f97\u98de\u51fa\u5916\u8fb9\uff0c\u5c71\u98ce\u4e2d\u9690\u9690\u95fb\u5f97\u90a3\u76d7\u9b41\u7684\u54c0\u53f7\uff0c\u7adf\u4e0d\u77e5\u7ed9\u6454\u5230\u54ea\u513f\u53bb\u4e86\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6e38\u620f\u98ce\u5c18\u4e00\u5f02\u4e10\uff0c\u5c11\u5e74\u82f1\u4fa0\u4e5f\u5fc3\u60ca\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u9752\u5973\u7d20\u5a25\u3000\u6d6e\u4e91\u63a9\u660e\u6708\u3000\u5947\u4eba\u75af\u4e10\u3000\u94c1\u5251\u9a87\u82f1\u8c6a \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u516b\u56de \u9752\u5973\u7d20\u5a25\u3000\u6d6e\u4e91\u63a9\u660e\u6708 \u5947\u4eba\u75af\u4e10\u3000\u94c1\u5251\u9a87\u82f1\u8c6a \u3000\u3000\u76d7\u5f92\u4eec\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u4e5f\u987e\u4e0d\u5f97\u76ae\u8089\u7684\u7684\u4f24\uff0c\u8fde\u90a3\u4e9b\u8fd8\u5728\u5730\u4e0a\u6253\u6eda\u7684\uff0c\u4e5f\u53d1\u4e00\u58f0\u558a\uff0c\u8fde \u722c\u5e26\u6eda\uff0c\u7eb7\u7eb7\u593a\u547d\u5954\u9003\uff1b\u9556\u884c\u548c\u836f\u884c\u7684\u4f19\u8ba1\uff0c\u5982\u89c1\u9b3c\u9b45\u8fdc\u8fdc\u907f\u5f00\uff0c\u7f29\u5230\u5899\u8fb9\uff0c\u8fde\u90a3\u4e2a\u8001\u9556\u5e08 \u4e5f\u5413\u5f97\u5446\u4e86\u3002 \u3000\u3000\u90a3\u8001\u8005\u5530\u7684\u4e00\u4e0b\u9762\u8272\u53d8\u5f97\u7070\u767d\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4e13\u4e0e\u5929\u4e0b\u82f1\u96c4\u4f5c\u5bf9\u7684\u6bd2\u624b\u75af\u4e10\uff1f\u201d\u90a3\u9ebb \u75af\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4e0d\u9519\uff0c\u591f\u8d44\u683c\u4e0e\u6211\u4f5c\u5bf9\u7684\u82f1\u96c4\u53ef\u4e0d\u591a\uff0c\u4f60\u4eec\u7684\u4e94\u884c\u62f3\u5440\uff0c\u795e\u5f39\u5b50\u5440\uff0c\u8fd8\u4e0d\u8d76 \u5feb\u65bd\u5c55\uff1f\u201d\u90a3\u8001\u8005\u53eb\u9053\uff1a\u201c\u971e\u513f\uff0c\u5feb\u8d70\uff01\u201d\u53cd\u8eab\u4e00\u8dc3\uff0c\u62fe\u8d77\u4e00\u67c4\u9556\u884c\u4f19\u8ba1\u6240\u7528\u7684\u957f\u5200\uff0c\u6ca1\u5934 \u6ca1\u8111\u7684\u4fbf\u5411\u90a3\u9ebb\u75af\u6025\u65ab\u3002\u4ed6\u672c\u6765\u4ee5\u4e94\u884c\u62f3\u8457\u540d\uff0c\u7528\u5200\u5b9e\u975e\u6240\u957f\uff0c\u53ea\u56e0\u77a7\u89c1\u4e86\u5927\u9ebb\u75af\u957f\u6ee1\u7599\u7629 \u7684\u53cc\u81c2\uff0c\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u4e0d\u6562\u4e0e\u4ed6\u808c\u80a4\u76f8\u63a5\u3002\u4ed6\u867d\u7136\u4e0d\u957f\u4e8e\u5200\u6cd5\uff0c\u8fd9\u51e0\u5200\u4e5f\u5288\u5f97\u864e\u864e\u98ce\u751f\u3002\u90a3\u9ebb \u75af\u53cc\u76ee\u4e00\u7741\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6562\u4e0e\u6211\u78b0\u624b\u78b0\u811a\uff1f\u6211\u504f\u8981\u53eb\u4f60\u5c1d\u5c1d\u6211\u8eab\u4e0a\u7684\u8113\u8840\uff01\u201d\u4ed6\u5c06\u94c1 \u62d0\u4ea4\u7ed9\u5de6\u624b\uff0c\u820d\u800c\u4e0d\u7528\uff0c\u5355\u624b\u98ce\u8f66\u822c\u5730\u75be\u8f6c\uff0c\u76f4\u5728\u5200\u5149\u4e4b\u4e2d\u8feb\u8fd1\u8001\u8005\u8eab\u524d\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u559d\u9053\uff1a\u201c\u971e\u513f\uff0c\u5feb\u8d70\uff01\u201d\u5f39\u5f13\u4e00\u66f3\uff0c\u8fde\u53d1\u4e09\u5f39\uff0c\u4e00\u53d6\u90a3\u75af\u4e10\u9762\u4e0a\u7684\u201c\u7709\u5c16\u7a74 \u201d\uff0c\u4e00\u53d6\u80f8\u524d\u7684\u201c\u7075\u5e9c\u7a74\u201d\uff0c\u4e00\u53d6\u4e0b\u8eab\u7684\u201c\u4f1a\u9634\u7a74\u201d\uff0c\u8fd9\u4e09\u5f39\u8fde\u53d1\uff0c\u66fe\u6253\u8d25\u8fc7\u4e0d\u5c11\u540d\u5bb6\u9ad8\u624b \uff0c\u5386\u5bb3\u65e0\u6bd4\u3002\u90a3\u75af\u4e10\u53eb\u58f0\uff1a\u201c\u6768\u5bb6\u795e\u5f39\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u95ea\u5f00\u4e86\u5954 \u4e0a\u76d8\u7684\u5f39\u5b50\uff0c\u53cc\u6307\u4e00\u5d4c\uff0c\u63a5\u4e86\u5954\u4e2d\u76d8\u7684\u5f39\u5b50\uff0c\u94c1\u62d0\u4e00\u62d4\uff0c\u5c06\u5954\u4e0b\u76d8\u7684\u90a3\u9897\u4e5f\u53cd\u51fb\u5f97\u65e0\u5f71\u65e0\u8e2a \u3002\u5893\u5730\u4e00\u58f0\u602a\u53eb\uff0c\u5f20\u53e3\u4e00\u54ac\uff0c\u54ac\u7740\u90a3\u957f\u67c4\u5f2f\u5200\u5782\u4e0b\u7684\u5200\u73af\uff0c\u90a3\u8001\u8005\u4e00\u751f\u8d70\u5357\u95ef\u5317\uff0c\u4e0d\u77e5\u4f1a\u8fc7 \u591a\u5c11\u9ad8\u4eba\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e2a\u602a\u62db\uff0c\u864e\u53e3\u4e00\u9ebb\u3002\u957f\u5200\u7adf\u7ed9\u4ed6\u54ac\u53bb\u3002\u90a3\u75af\u4e10\u563b\u563b\u602a\u7b11\uff0c\u624b\u81c2\u4e00\u6a2a \uff0c\u4f38\u638c\u5c31\u62b9\u90a3\u8001\u8005\u7684\u53e3\u9762\u3002\u8001\u8005\u5927\u543c\u4e00\u58f0\uff0c\u515c\u80f8\u5c31\u662f\u4e00\u62f3\uff0c\u4e34\u6025\u4e4b\u65f6\uff0c\u4f7f\u51fa\u4e86\u4e94\u884c\u62f3\u7684\u6740\u624b \uff0c\u90a3\u75af\u4e10\u4e00\u58f0\u602a\u53eb\uff0c\u817e\u7684\u5012\u8dc3\u4e09\u6b65\uff0c\u62d0\u6756\u5f80\u5730\u4e0a\u4e00\u70b9\u3001\u9b3c\u8138\u4e00\u822c\uff0c\u53c8\u5230\u4e86\u8001\u8005\u8eab\u524d\uff0c\u563b\u563b\u7b11 \u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u80fd\u6321\u6211\u4e09\u62db\uff01\u201d\u90a3\u8005\u8005\u8fd9\u62f3\u5c11\u8bf4\u4e5f\u6709\u4e03\u516b\u767e\u65a4\u6c14\u529b\uff0c\u515c\u5fc3\u4e00\u62f3\uff0c\u7adf\u6253\u4ed6\u4e0d\u5012 \uff0c\u8fd9\u771f\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6025\uff0c\u84e6\u89c1\u90a3\u75af\u4e10\u53c8\u4e3e\u8d77\u624b\u81c2\uff0c\u4f38\u638c\u6765\u62b9\uff0c\u5f85\u8981\u8dc3\u5f00\uff0c\u5374 \u7ed9\u4ed6\u7684\u94c1\u62d0\u4e00\u628a\u52fe\u4f4f\u4e86\u9888\u9879\u3002 \u3000\u3000\u90a3\u5c11\u5973\u75be\u53d1\u5f39\u5b50\uff0c\u5979\u7684\u201c\u9694\u8863\u6253\u7a74\u201d\u529f\u592b\uff0c\u8fd8\u672a\u7ec3\u5f97\u7eaf\u719f\uff0c\u7528\u7684\u662f\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u624b\u6cd5 \uff0c\u4e00\u53d1\u5c31\u662f\u4e00\u5927\u628a\u3002\u90a3\u75af\u4e10\u94c1\u62d0\u4e00\u52fe\uff0c\u5148\u628a\u90a3\u8001\u8005\u7eca\u5012\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u5f85\u4e0b\u518d\u53eb\u4f60\u5c1d\u5c1d\u6ecb\u5473 \uff01\u201d\u94c1\u62d0\u76d8\u7a7a\u4e00\u821e\uff0c\u5c11\u5973\u7684\u5f39\u5b50\u90fd\u7ed9\u4ed6\u7684\u6756\u98ce\u9707\u5f97\u5316\u4e3a\u7c89\u5c51\u3002\u53e6\u5321\u75af\u4e10\u53eb\u9053\uff1a\u201c\u597d\uff0c\u5148\u8bf7\u4f60 \u8fd9\u4f4d\u5982\u82b1\u4f3c\u7389\u7684\u5c0f\u59d1\u5a18\u5c1d\u5c1d\u6211\u8eab\u4e0a\u7684\u7f8e\u5473\uff01\u201d\u94c1\u62d0\u70b9\u5730\uff0c\u51cc\u7a7a\u98de\u51fa\uff0c\u5c11\u5973\u9a87\u6781\u5927\u547c\uff0c\u4e00\u8db3\u8dcc \u5012\u5730\u4e0a\u3002\u90a3\u5987\u4eba\u6025\u53d1\u5f39\u5b50\uff0c\u8fde\u6253\u75af\u4e10\u8eab\u4e0a\u4e03\u5904\u5927\u7a74\uff0c\u867d\u660e\u77e5\u4f24\u4ed6\u4e0d\u5f97\uff0c\u4f46\u6551\u5973\u60c5\u6bb7\uff0c\u53ea\u76fc\u80fd \u5c06\u90a3\u75af\u4e10\u6682\u8feb\u5f00\uff0c\u4e0d\u53eb\u4ed6\u6cbe\u6c61\u4e86\u5973\u513f\u3002\u90a3\u75af\u4e10\u7adf\u7136\u7406\u4e5f\u4e0d\u7406\uff0c\u5f2f\u8170\u4f38\u81c2\uff0c\u5c31\u8981\u62b1\u8fd9\u4e2a\u6655\u5012\u5730 \u4e0a\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u545c\u9e23\u4e24\u58f0\uff0c\u53ea\u89c1\u6697\u8d64\u8272\u7684\u5149\u534e\u95ea\u4e86\u4e24\u95ea\uff1b\u90a3\u75af\u4e10\u4e00\u58f0\u602a\u53eb\uff0c\u8dc3\u8d77\u4e08\u9ad8\uff0c\u51e0\u4e4e\u78b0\u5230 \u5c4b\u9876\uff0c\u94c1\u62d0\u4e00\u6325\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u90a3\u5987\u4eba\u5927\u4e3a\u60ca\u9a87\uff0c\u5c06\u5f39\u5f13\u63b7\u4e8e\u5730\u4e0b\uff0c\u53d6\u51fa\u67f3\u53f6\u53cc\u5200\uff0c\u8fde\u5fd9\u62db\u67b6 \uff0c\u90a3\u75af\u4e10\u52bf\u5982\u731b\u864e\uff0c\u5de6\u53f3\u4e00\u626b\uff0c\u5f53\u4e2d\u4e00\u51fb\uff0c\u4e0d\u8fc7\u4e09\u62db\uff0c\u5c31\u5c06\u90a3\u5987\u4eba\u7684\u67f3\u53f6\u53cc\u5200\u5168\u90fd\u51fb\u98de\uff0c\u5ffd \u5730\u5f20\u53e3\u4e00\u5410\uff0c\u53eb\u9053\uff1a\u201c\u6df7\u5c0f\u5b50\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u90a3\u5987\u4eba\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u5f20\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u5bd2\u5149\u523a\u76ee\uff0c\u5251\u6c14\u5982\u8679\uff0c\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\u6b63\u5728\u4e0e\u90a3\u75af \u4e10\u6076\u6218\uff0c\u4e2d\u5e74\u5987\u4eba\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u6e38\u9f99\u5251\uff01\u201d \u3000\u3000\u8fd9\u767d\u8863\u5c11\u5e74\u6b63\u662f\u5510\u7ecf\u5929\uff0c\u4ed6\u5728\u90a3\u4e24\u6bcd\u5973\u6700\u5371\u6025\u7684\u65f6\u5019\uff0c\u7528\u6c72\u5de7\u5999\u7684\u624b\u6cd5\uff0c\u53d1\u51fa\u4e24\u652f\u5929\u5c71 \u795e\u8292\uff0c\u6742\u5728\u5f39\u5b50\u4e4b\u4e2d\u6253\u51fa\uff0c\u90a3\u75af\u5f13\u95ed\u4e86\u5168\u8eab\u7684\u7a74\u9053\uff0c\u4ed6\u53c8\u4e0d\u77e5\u5929\u5c71\u795e\u8292\u7684\u5386\u5bb3\uff0c\u4ee5\u4e3a\u95ed\u4e86\u7a74 \u9053\uff0c\u4ece\u88ab\u6253\u4e2d\u4e5f\u662f\u65e0\u59a8\uff0c\u90a3\u77e5\u8fd9\u4e24\u652f\u795e\u8292\u914d\u4e0a\u5510\u7ecf\u5929\u7684\u5185\u7a7a\u52b2\u529b\uff0c\u7adf\u76ae\u4e86\u4ed6\u95ed\u7a74\u7684\u529f\u592b\uff0c\u795e \u8292\u94bb\u5934\uff0c\u76f4\u653b\u5fc3\u80ba\uff0c\u90a3\u75af\u4e10\u53d7\u4e86\u91cd\u4f24\u3002\u5510\u7ecf\u5929\u4e00\u53d1\u795e\u8292\uff0c\u7acb\u523b\u51fa\u624b\uff0c\u90a3\u75af\u4e10\u515c\u5934\u4e00\u5410\uff0c\u5510\u7ecf \u5929\u75be\u5201\u95ea\u5f00\uff0c\u62d4\u51fa\u6e38\u9f99\u5251\uff0c\u5c82\u77e5\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u4e24\u58f0\uff0c\u624b\u8155\u4e0a\u4f3c\u7ed9\u5927\u8682\u8681\u53ee\u4e86\u4e24\u53e3 \u4e00\u6837\uff0c\u5e76\u4e0d\u75bc\u75db\uff0c\u4f46\u5374\u75d2\u4e4b\u6781\u3002\u5510\u7ecf\u5929\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u7b80\u76f4\u662f\u4e00\u6761\u9022\u4eba\u4fbf\u556e\u7684\u6bd2\u86c7\uff01 \u201d\u90a3\u75af\u4e10\u5408\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e00\u70b9\u4e0d\u9519\uff0c\u4f60\u5c31\u662f\u4eca\u665a\u7b2c\u4e00\u4e2a\u7ed9\u6bd2\u86c7\u54ac\u7740\u7684\u4eba\u3002\u201d\u5510\u7ecf\u5929\u8fde\u5251 \u5982\u98ce\uff0c\u5237\u5237\u5237\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u8fde\u53d1\u4e09\u5251\uff0c\u75af\u4e10\u90a3\u53cc\u624b\u62ff\u7740\u94c1\u62d0\uff0c\u4e24\u8fb9\u4e00\u626f\uff0c\u5ffd\u5730\u626f\u51fa\u4e00\u628a\u9ed1\u6f06 \u53d1\u5149\u7684\u94c1\u5251\uff0c\u539f\u6765\u90a3\u94c1\u62d0\u4e2d\u7a7a\uff0c\u7adf\u662f\u4e00\u4e2a\u5947\u7279\u7684\u5251\u9798\u3002 \u3000\u3000\u5510\u7ecf\u5929\u7684\u6e38\u9f99\u5251\u4f55\u7b49\u5386\u5bb3\uff0c\u94ff\u9535\u4e00\u58f0\uff0c\u65ab\u5728\u90a3\u75af\u7684\u94c1\u5251\u4e0a\uff0c\u767b\u65f6\u6e85\u8d77\u4e00\u6e9c\u706b\u5149\uff0c\u5c06\u90a3\u67c4 \u94c1\u5251\u6240\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u90a3\u9ebb\u75af\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u6325\u5251\u659c\u5288\uff0c\u5510\u7ecf\u5929\u7684\u5b9d\u5251\u524a\u94c1\u5982\u6ce5\uff0c\u65ab\u5b83\u4e0d\u65ad\uff0c \u4e5f\u81ea\u5927\u51fa\u610f\u5916\u3002\u53ea\u89c1\u90a3\u9ebb\u75af\u5251\u62db\u5b8c\u5168\u4e0d\u4f9d\u5e38\u8f68\uff0c\u770b\u4f3c\u6742\u4e71\u65e0\u7ae0\uff0c\u5176\u5b9e\u6bcf\u4e00\u62db\u90fd\u6709\u6781\u6df1\u5965\u7684\u53d8 \u5316\uff0c\u4e00\u8fde\u6321\u4e86\u4ed6\u8ffd\u98ce\u5251\u6cd5\u7684\u5341\u516b\u62db\u8fdb\u624b\u62db\u5f0f\uff0c\u4e1d\u6beb\u4e0d\u9732\u7834\u7efd\uff0c\u8fd9\u9ebb\u75af\u7684\u5185\u529b\u4e5f\u5927\u5f97\u51fa\u5947\uff0c\u4ee5 \u5510\u7ecf\u5929\u6240\u4fee\u7684\u7eaf\u51c0\u5185\u529f\uff0c\u7adf\u7136\u5360\u4e0d\u5230\u534a\u70b9\u4fbf\u5b9c\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u6551\u9192\u5973\u513f\uff0c\u90a3\u8001\u8005\u4ea6\u5df1\u8df3\u8d77\uff0c\u4e09\u4eba\u540c\u65f6\u5927\u547c\uff0c\u5e2e\u5510\u7ecf\u5929\u6597\u8fd9\u6076\u4e10\u3002\u8fd9\u6076\u4e10\u53f3 \u624b\u6325\u821e\u94c1\u5251\uff0c\u654c\u4f4f\u5510\u7ecf\u5929\u7684\u6e38\u9f99\u5b9d\u5251\uff0c\u5de6\u624b\u6325\u821e\u201c\u5251\u9798\u201d\u654c\u4f4f\u90a3\u7236\u5973\u4e09\u4eba\u7684\u5175\u5668\uff0c\u53f3\u624b\u5b88\u591a \u4e8e\u653b\uff0c\u5de6\u624b\u5374\u662f\u653b\u591a\u4e8e\u5b88\uff0c\u5510\u7ecf\u5929\u4f7f\u51fa\u8ffd\u98ce\u5251\u6cd5\u7684\u7cbe\u5999\u62db\u5f0f\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u90a3 \u75af\u4e10\u5934\u4e0a\u5192\u51fa\u817e\u817e\u70ed\u6c14\uff0c\u6c57\u6d41\u6ee1\u9762\uff0c\u5510\u7ecf\u5929\u77e5\u9053\u795e\u8292\u5df2\u5faa\u7740\u7a74\u9053\u653b\u4ed6\u5fc3\u80ba\uff0c\u624b\u5e95\u66f4\u4e0d\u653e\u677e\uff0c \u5237\u5237\u4e24\u5251\uff0c\u5206\u5fc3\u76f4\u523a\uff01 \u3000\u3000\u90a3\u75af\u4e10\u53cc\u773c\u4e00\u7741\uff0c\u76ee\u5149\u5982\u7535\uff0c\u626b\u4e86\u4e00\u4e0b\uff0c\u84e6\u7136\u559d\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u52a8\u4e86\u771f\u6c14\uff0c\u8fd8\u8981\u547d\u4e48 \uff1f\u201d\u5510\u7ecf\u5929\u54ac\u7259\u4e00\u5251\uff0c\u90a3\u75af\u4e10\u4e3e\u5251\u4e00\u6321\uff0c\u5728\u706b\u661f\u84ec\u98de\u4e2d\u5ffd\u7136\u4e00\u4e2a\u7b4b\u5934\uff0c\u7ffb\u51fa\u95e8\u5916\uff0c\u5510\u7ecf\u5929\u4e3e \u6b65\u6b32\u8ffd\uff0c\u5ffd\u89c9\u904d\u4f53\u6709\u5982\u9488\u523a\uff0c\u4e00\u80a1\u8165\u6c14\u4f3c\u4ece\u5fc3\u80ba\u4e4b\u95f4\u6cdb\u51fa\uff0c\u76f4\u51b2\u5589\u5934\uff0c\u9661\u7136\u95f4\uff0c\u4f46\u89c9\u91d1\u661f\u4e71 \u5192\uff0c\u773c\u524d\u4e00\u7247\u9ed1\u6f06\uff0c\u8dcc\u5012\u5730\u4e0a\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6025\u6025\u8fd0\u6c14\u9547\u62a4\u5fc3\u795e\uff0c\u53ea\u542c\u5f97\u6ee1\u5c4b\u5b50\u7684\u811a\u6b65\u58f0\uff0c\u54d7\u53eb\u58f0\uff0c\u9053\u8c22\u58f0\uff0c\u90a3\u8001\u8005\u9053\uff1a\u201c\u8001 \u9556\u5934\u4e14\u4f11\u8a00\u8c22\uff0c\u8bf7\u6765\u5e2e\u773c\u770b\u770b\u8fd9\u4f4d\u670b\u53cb\u53d7\u7684\u5230\u5e95\u662f\u4ec0\u4e48\u4f24\uff1f\u201d\u5510\u7ecf\u5929\u53e3\u4e0d\u80fd\u8a00\uff0c\u5fc3\u5934\u4e5f\u6e10\u89c9 \u9ebb\u6728\uff0c\u8ff7\u7cca\u4e2d\u4f3c\u542c\u5f97\u5468\u56f4\u7eb7\u7eb7\u8bae\u8bba\u7684\u58f0\u97f3\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u4ec0\u4e48\u6697\u5668\uff1f\u201d\u201c\u4e0d\u53ef\u4e71\u7528\u89e3\u836f\uff0c\u7528\u5f97 \u4e0d\u5bf9\uff0c\u53cd\u800c\u4f1a\u52a0\u91cd\u4f24\u52bf\u3002\u201d\u201c\u54a6\uff0c\u600e\u4e48\u597d\u50cf\u86c7\u54ac\u7684\u4f24\u53e3\uff1f\u201d\u201c\u770b\uff0c\u8fd9\u8138\u4e0a\u7684\u9ed1\u6c14\uff0c\u771f\u50cf\u662f\u88ab \u6bd2\u86c7\u54ac\u7684\uff01\u201d\u201c\u8c01\u5e26\u6709\u91d1\u9488\uff0c\u523a\u4e00\u70b9\u6bd2\u8840\u770b\u770b\u3002\u201d\u201c\u4e0d\u5fc5\u770b\u5566\uff0c\u8fd9\u6697\u5668\u51c6\u662f\u7528\u6bd2\u86c7\u7684\u53e3\u6d8e\u70bc \u7684\u3002\u201d\u8fd9\u65f6\u95f4\u5510\u7ecf\u5929\u53ea\u89c9\u8111\u888b\u597d\u8c61\u6709\u4e00\u5757\u94c5\u4f3c\u7684\uff0c\u8d8a\u6765\u8d8a\u6c89\u91cd\uff0c\u8eab\u4e0a\u597d\u8c61\u6709\u65e0\u6570\u5c0f\u86c7\u6e38\u52a8\uff0c \u4e71\u556e\u4e71\u54ac\uff0c\u5510\u7ecf\u5929\u60f3\u53eb\u4ed6\u4eec\u53d6\u51fa\u4ed6\u56ca\u4e2d\u7684\u7528\u5929\u5c71\u96ea\u83b2\u6240\u6ce1\u5236\u7684\u78a7\u7075\u4e39\uff0c\u53ea\u662f\u820c\u5934\u4ea6\u5df2\u9ebb\u6728\uff0c \u65c1\u8fb9\u7684\u4eba\u53ea\u542c\u5f97\u4ed6\u53d1\u51fa\u201c\u554a\u5440\u201d\u7684\u6a21\u7cca\u58f0\u97f3\uff0c\u8d8a\u53d1\u624b\u5fd9\u811a\u4e71\u3002\u518d\u8fc7\u7247\u523b\uff0c\u5510\u7ecf\u5929\u9690\u9690\u542c\u89c1\u6709 \u4eba\u8bf4\u9053\uff1a\u201c\u4e14\u770b\u8fd9\u4e2a\u836f\u80fd\u4e0d\u80fd\u7528\uff1f\u201d\u773c\u775b\u4e00\u9ed1\uff0c\u7acb\u523b\u5931\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u5230\u5510\u7ecf\u5929\u6709\u4e86\u77e5\u89c9\u4e4b\u65f6\uff0c\u5df2\u662f\u4e03\u65e5\u4e4b\u540e\u3002\u5510\u7ecf\u5929\u53ef\u4e0d\u77e5\u9053\u8fc7\u4e86\u8fd9\u4e48\u957f\u7684\u65e5\u5b50\uff0c\u53ea\u89c9\u5f97\u4f3c \u4ece\u4e00\u573a\u6076\u68a6\u4e2d\u9192\u6765\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u4f9d\u7a00\u8bb0\u5f97\u524d\u4e8b\uff0c\u5f20\u773c\u4e00\u77a7\uff0c\u4f46\u89c1\u7ea2\u65e5\u5f53\u7a97\uff0c\u7a97\u5916\u82b1\u679d\u98a4\u52a8\uff0c \u623f\u4e2d\u7f15\u7f15\u5e7d\u9999\uff0c\u5f88\u662f\u8212\u670d\uff0c\u8033\u8fb9\u542c\u5f97\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u9192\u8fc7\u6765\u5566\uff01\u201d\u53ea\u89c1\u90a3\u4e24\u6bcd\u5973\u5750 \u5728\u5e8a\u524d\uff0c\u542b\u7b11\u5730\u770b\u7740\u81ea\u5df1\uff0c\u90a3\u67c4\u6e38\u9f99\u5b9d\u5251\uff0c\u60ac\u5728\u5e8a\u5934\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u5728\u8fd9\u513f\uff1f\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u9053\uff1a\u201c\u971e\u513f\uff0c\u7aef\u4e00\u7897\u53c2\u6c64 \u6765\u3002\u201d\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u90a3\u75af\u4e10\u7684\u5582\u6bd2\u6697\u5668\uff0c\u5df2\u8eba\u4e86\u4e03\u5929\u5566\u3002\u8fd9\u51e1\u662f\u6211\u4eec\u7684\u5bb6\u3002\u201d\u5510\u7ecf\u5929 \u95ed\u76ee\u4e00\u60f3\uff0c\u60f3\u8d77\u90a3\u75af\u4e10\u7684\u602a\u72b6\uff0c\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff0c\u9053\uff0c\u201c\u591a\u8c22\u4f60\u5566\u3002\u201d\u90a3\u5987\u4eba\u9053\uff1a\u201c\u6211\u4eec\u624d\u8be5 \u8c22\u4f60\u3002\u201d\u5c11\u5973\u7aef\u4e86\u53c2\u6c64\u8fdb\u6765\uff0c\u5510\u7ecf\u5929\u5477\u4e86\u4e24\u53e3\uff0c\u795e\u667a\u66f4\u89c1\u6e05\u9192\uff0c\u90a3\u5987\u4eba\u9053\uff1a\u201c\u971e\u513f\uff0c\u628a\u5510\u54e5 \u54e5\u6362\u4e0b\u7684\u8863\u670d\u62ff\u51fa\u53bb\uff0c\u90a3\u4e24\u4ef6\u65b0\u8863\u88f3\u4f60\u7f1d\u4e86\u6ca1\u6709\uff1f\u201d\u5c11\u5973\u7b54\u9053\uff1a\u201c\u65e9\u7f1d\u597d\u5566\u3002\u201d\u5510\u7ecf\u5929\u95fb\u5230 \u8863\u886b\u4e0a\u7684\u4e00\u80a1\u8165\u81ed\u4e4b\u5473\uff0c\u53c8\u89c1\u8fd9\u4e24\u6bcd\u5973\u53cc\u773c\u53d1\u7ea2\uff0c\u60f3\u662f\u71ac\u4e86\u51e0\u4e2a\u591c\u665a\uff0c\u5b88\u62a4\u81ea\u5df1\uff0c\u5fc3\u4e2d\u5927\u662f \u8fc7\u610f\u4e0d\u53bb\uff0c\u9053\uff1a\u201c\u6d3b\u547d\u4e4b\u6069\uff0c\u7ec8\u8eab\u4e0d\u5fd8\uff01\u201d\u90a3\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5988\uff0c\u4ed6\u7239\u5f53\u5e74\u662f\u4e0d\u662f\u4e5f \u8fd9\u6837\u6587\u7ec9\u7ec9\u7684\uff1f\u201d\u90a3\u5987\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u6697\u5668\u7684\u6bd2\u771f\u662f\u4eba\u95f4\u5c11\u89c1\uff0c\u8bf4\u6765\u8fd8\u662f\u4f60\u81ea\u5df1\u533b\u597d\u7684\uff0c\u591a\u8c22 \u6211\u4eec\u505a\u4ec0\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u5987\u4eba\u7b11\u9053\uff1a\u201c\u5e78\u597d\u6211\u8ba4\u5f97\u4f60\u8fd9\u628a\u6e38\u9f99\u5b9d\u5251\uff0c\u53c8\u77e5\u9053 \u78a7\u7075\u4e39\u7684\u7528\u6cd5\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u675f\u624b\u65e0\u7b56\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u7b11\u4e86\u4e00\u7b11\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u5148\u662f\u90a3\u836f\u5546\u770b\u51fa\u8fd9\u662f\u86c7\u6bd2\uff0c\u9001\u4e86\u4f60\u4e24\u4e38\u4e13\u89e3\u86c7\u6bd2\u7684\u836f\u4e38 \uff0c\u90a3\u836f\u5546\u539f\u6765\u662f\u4e13\u5356\u5317\u4eac\u6700\u8457\u540d\u7684\u4f17\u5bb6\u836f\u6750\u7684\uff0c\u4ed6\u611f\u8c22\u6211\u4eec\u6253\u9000\u5f3a\u76d7\uff0c\u4e0d\u60dc\u4ee5\u6700\u73cd\u8d35\u7684\u7075\u836f \u76f8\u8d60\uff0c\u4f46\u4e5f\u53ea\u662f\u80fd\u6682\u65f6\u963b\u904f\u6bd2\u6c14\u4e0d\u81f3\u53d1\u4f5c\uff0c\u6211\u4eec\u96c7\u4e86\u4e00\u4e58\u7af9\u8f7f\uff0c\u5c06\u4f60\u62ac\u56de\u5bb6\u4e2d\uff0c\u66ff\u4f60\u63a8\u6469\u6324 \u8840\uff0c\u90fd\u6ca1\u6709\u7528\u3002\u6211\u5ffd\u7136\u60f3\u8d77\uff0c\u4f60\u65e2\u662f\u8fd9\u67c4\u6e38\u9f99\u5251\u7684\u4e3b\u4eba\uff0c\u56ca\u4e2d\u4e00\u5b9a\u6709\u5929\u5c71\u7684\u7075\u836f\u78a7\u7075\u4e39\uff0c\u6211 \u7528\u96ea\u6c34\u5c06\u7075\u4e39\u5f00\u4e86\uff0c\u4e00\u534a\u5185\u670d\uff0c\u4e00\u534a\u5916\u6577\uff0c\u5440\uff0c\u90a3\u75af\u4e10\u7684\u6697\u5668\uff0c\u5947\u6bd2\u771f\u662f\u4e16\u95f4\u7f55\u6709\uff0c\u4ee5\u5927\u5c71 \u96ea\u83b2\u8fd9\u6837\u5584\u89e3\u5404\u79cd\u65e0\u540d\u80bf\u6bd2\u7684\u7075\u836f\uff0c\u4e5f\u5f97\u82b1\u4e03\u5927\u5de5\u592b\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u795e\u667a\u6e05\u9192\uff0c\u60f3\u8d77\u90a3\u665a\u4e4b\u4e8b\uff0c\u53c8\u542c\u5979\u73b0\u5728\u7684\u8bf4\u8bdd\uff0c\u4e0d\u7531\u5f97\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6211\u7239\u7239\u5417 \uff1f\u201d\u90a3\u5987\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u8138\u4e0a\u5ffd\u7136\u6cdb\u8d77\u4e00\u5c42\u7ea2\u6655\uff0c\u5c31\u50cf\u90a3\u665a\u5979\u521d\u89c1\u5510\u7ecf\u5927\u4e4b\u65f6\uff0c\u4e00\u6a21\u4e00\u6837\uff0c\u8f7b \u63a0\u4e91\u9b13\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f55\u6b62\u8ba4\u5f97\uff0c\u6211\u4eec\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\u5462\uff01\u4f60\u7239\u6ca1\u6709\u548c\u4f60\u63d0\u8fc7\u94c1\u638c\u795e\u5f39\u6768\u4ef2 \u82f1\u7684\u540d\u5b57\u5417\uff1f\u6211\u5c31\u662f\u94c1\u638c\u795e\u5f39\u7684\u5973\u513f\u3002\u201d\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u4f60\u5c31\u662f\u6768\u67f3\u9752\uff0c\u55ef\uff0c\u6768\u4f2f \u6bcd\u3002\u6211\u5988\u5e38\u8bf4\u8d77\u4f60\u3002\u201d\u90a3\u5987\u4eba\u67f3\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u4f60\u5988\u597d\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\u3002\u6211\u5988\u8bf4\u4e8c\u5341\u591a \u5e74\u524d\uff0c\u4ed6\u4eec\u90fd\u66fe\u53d7\u8fc7\u4f60\u7236\u4eb2\u7684\u5927\u6069\uff0c\u6211\u7239\u66fe\u5728\u4f60\u7239\u95e8\u4e0b\u4e60\u6280\u4e94\u5e74\uff0c\u8bf4\u6765\u4f60\u8be5\u662f\u6211\u7684\u5e08\u53d4\u3002\u201d \u90a3\u5987\u4eba\u60f3\u8d77\u4e8c\u5341\u4f59\u5e74\u524d\u7684\u60c5\u4e8b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7239\u7239\u597d\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\u3002\u6211\u7239\u5728\u5929\u5c71\u4e4b\u65f6\u8fd8 \u4f9b\u5949\u6709\u6768\u5e08\u7956\u7684\u7075\u4f4d\u5462\uff01\u201d\u90a3\u5987\u4eba\u8fd9\u624d\u771f\u6b63\u5f00\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u662f\u8981\u5230\u5929\u5c71\u63a2\u671b\u4f60\u7684 \u7236\u6bcd\u7684\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u513f\u9047\u89c1\u4e86\u4f60\u3002\u8fd9\u4e5f\u771f\u662f\u7f18\u6cd5\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5987\u4eba\u540d\u5524\u6768\u67f3\u9752\uff0c\u66fe\u7ecf\u662f\u8fc7\u5510\u6653\u6f9c\u7684\u672a\u5a5a\u59bb\uff0c\u540e\u6765\u89e3\u9664\u4e86\u5a5a\u7ea6\uff0c\u624d\u6539\u5ac1\u4e94\u884c\u62f3\u540d \u5bb6\u90b9\u9521\u4e5d\u7684\u3002\u5973\u5b50\u6700\u96be\u5fd8\u521d\u604b\u60c5\u4eba\uff0c\u6768\u67f3\u9752\u751f\u4e86\u5973\u513f\uff0c\u5fc3\u4e2d\u8fd8\u4e0d\u65f6\u4f1a\u5fc6\u8d77\u5f80\u4e8b\uff0c\u4e0e\u5510\u6653\u6f9c\u591a \u5e74\u4e0d\u89c1\uff0c\u96be\u514d\u60ac\u5ff5\u3002\u90b9\u9521\u4e5d\u4e5f\u77e5\u9053\u59bb\u5b50\u60c5\u610f\uff0c\u6df1\u77e5\u5979\u4e0e\u81ea\u5df1\u5df2\u662f\u4e00\u5bf9\u6069\u7231\u592b\u59bb\uff0c\u5bf9\u5510\u6653\u6f9c\u7684 \u5fc6\u5ff5\u7edd\u975e\u65e7\u65e5\u4e4b\u60c5\uff0c\u800c\u4e14\u4ed6\u4e5f\u60f3\u89c1\u5510\u6653\u6f9c\u4e00\u9762\uff0c\u56e0\u6b64\u966a\u7740\u59bb\u5b50\u8fdc\u6765\u3002\u4ed6\u4eec\u672c\u6765\u662f\u5728\u5c71\u4e1c\u6768\u4ef2 \u82f1\u7684\u65e7\u5bb6\u5c45\u4f4f\uff0c\u4e09\u5e74\u4e4b\u524d\uff0c\u4e3a\u4e86\u4e00\u6869\u4e8b\u60c5\uff0c\u624d\u642c\u5230\u56db\u5ddd\u6765\u7684\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e2d\u6bd2\u5927\u6df1\uff0c\u9192\u540e\u6570\u5929\uff0c\u624d\u80fd\u6276\u58c1\u8bd5\u884c\uff0c\u770b\u6765\u975e\u7597\u517b\u4e00\u6708\u534a\u6708\uff0c\u96be\u4ee5\u6062\u590d\u3002\u56e0\u6b64\u53ea \u597d\u5728\u90b9\u5bb6\u4f4f\u4e0b\u6765\u3002\u90b9\u5bb6\u4e09\u7236\u5973\u5bf9\u4ed6\u7231\u62a4\u5907\u81f3\uff0c\u5c24\u5176\u662f\u6768\u67f3\u9752\uff0c\u7b80\u76f4\u5c06\u4ed6\u5f53\u6210\u4eb2\u751f\u513f\u5b50\u4e00\u822c\uff0c \u767e\u822c\u5475\u62a4\u3002\u6768\u67f3\u9752\u7684\u5973\u513f\u90b9\u7edb\u971e\u5929\u771f\u6d3b\u6cfc\uff0c\u6709\u5982\u4f9d\u4eba\u5c0f\u9e1f\uff0c\u65f6\u5e38\u8bf7\u5510\u7ecf\u5929\u6307\u6559\u6b66\u529f\u7cbe\u4e49\uff0c\u5510 \u7ecf\u592d\u521d\u521d\u4f24\u6108\uff0c\u5979\u5c31\u6276\u4ed6\u5728\u5ead\u9662\u91cc\u6563\u6b65\uff0c\u5510\u7ecf\u5929\u5fc3\u65e0\u90aa\u5ff5\uff0c\u4e5f\u5e76\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u8fc7\u4e86\u5341\u5929\uff0c\u5510\u7ecf\u592d\u9664\u4e86\u4f53\u529b\u5c1a\u5dee\u4e4b\u5916\uff0c\u6bd2\u6c14\u5df2\u7ecf\u53bb\u5c3d\uff0c\u4eba\u4ea6\u6e10\u6e10\u590d\u539f\uff0c\u8fd9\u4e00\u665a\u548c\u90b9\u7edb\u971e \u5728\u5c4b\u5916\u6563\u6b65\uff0c\u5c4b\u5916\u82b1\u5f71\u6276\u758f\uff0c\u6708\u5149\u5982\u6c34\uff0c\u8fd9\u65f6\u5df2\u662f\u6625\u5c3d\u590f\u6765\uff0c\u8309\u8389\u82b1\u5f00\u5f97\u6b63\u9999\uff0c\u665a\u98ce\u5439\u6765\uff0c \u4e2d\u4eba\u6b32\u9189\u3002 \u3000\u3000\u90b9\u7edb\u971e\u7b11\u8bed\u76c8\u76c8\uff0c\u4e0d\u77e5\u600e\u7684\u63d0\u8d77\u5929\u5c71\uff0c\u90b9\u7edb\u971e\u95ee\u9053\uff1a\u201c\u5929\u5c71\u4e4b\u4e0a\u597d\u4e0d\u597d\u73a9\uff1f\u201d\u5510\u7ecf\u5929\u9053 \uff1a\u201c\u4f4f\u60ef\u4e86\u4e0d\u89c9\u600e\u6837\uff0c\u82e5\u6ca1\u6709\u5230\u8fc7\u7684\u4eba\uff0c\u6837\u6837\u90fd\u4f1a\u89c9\u5f97\u65b0\u5947\uff0c\u90a3\u91cc\u7ec8\u5e74\u79ef\u96ea\uff0c\u51b0\u6cb3\u4ea4\u9519\uff0c\u4ece \u5c71\u9876\u671b\u4e0b\uff0c\u5c31\u50cf\u5343\u767e\u9053\u94f6\u8272\u7684\u957f\u9f99\u4e00\u6837\u3002\u201d\u90b9\u7edb\u971e\u95ee\u9053\uff1a\u201c\u5475\uff0c\u90a3\u5c82\u4e0d\u6210\u4e86\u795e\u8bdd\u4e2d\u4ed9\u5973\u6240\u5c45 \u7684\u7409\u7483\u4e16\u754c\u4e86\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u8fd8\u89c1\u8fc7\u51b0\u5bab\u5462\uff01\u201d\u9aa4\u7136\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\uff0c\u4e0d\u89c9\u9eef\u7136\u3002\u90b9\u7ec8\u971e \u9053\uff1a\u201c\u5728\u5929\u5c71\u4e0a\u5417\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u5728\u5929\u5c71\u3002\u201d\u90b9\u7edb\u971e\u5ffd\u7136\u53d1\u73b0\u5510\u7ecf\u5929\u4f3c\u662f\u6709\u70b9\u90c1\u90c1 \u4e0d\u6b22\uff0c\u5fd9\u95ee\u9053\uff1a\u201c\u63d0\u8d77\u5929\u5c71\uff0c\u4f60\u5b9a\u60f3\u5bb6\u4e86\uff1f\u5f85\u4f60\u4f24\u597d\u4e4b\u540e\uff0c\u6211\u4eec\u90fd\u966a\u4f60\u53bb\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201d \u4e0d\uff0c\u6211\u8fd8\u8981\u5230\u5ddd\u897f\u4e00\u8d9f\u3002\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u5728\u5929\u5c71\u4e0a\uff0c\u5bc2\u4e0d\u5bc2\u5bde\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u4eec\u6709\u51e0\u5bb6 \u4eba\u5bb6\uff0c\u65f6\u5e38\u6765\u5f80\uff0c\u4e5f\u4e0d\u7b97\u5bc2\u5bde\u3002\u6211\u59e8\u5988\u4e5f\u5728\u5929\u5c71\uff0c\u5979\u6700\u6b22\u559c\u987d\u76ae\u7684\u5973\u5b69\u5b50\u3002\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201d \u55ef\uff0c\u6211\u542c\u5988\u5988\u8bf4\u8fc7\uff0c\u5979\u8bf4\u4f60\u5988\u59d0\u59b9\u4fe9\u975e\u5e38\u76f8\u50cf\uff0c\u597d\u73a9\u5f97\u5f88\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u5979\u4eec\u672c\u662f\u4e00\u5bf9 \u5b6a\u751f\u59d0\u59b9\uff0c\u6709\u65f6\u5019\u8fde\u6211\u4e5f\u5206\u8fa8\u4e0d\u51fa\u6765\u3002\u201d\u90b9\u7edb\u971e\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8868\u5144\u5f1f\u8c61\u4f60\u4e48\uff1f\u201d\u5510\u7ecf\u5927\u9053\uff1a \u201c\u4e0d\u8c61\u3002\u201d\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u6211\u7684\u8868\u59b9\u5012\u6709\u70b9\u50cf\u4f60\u3002\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f60\u7684\u8868\u59b9\u7f8e\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053 \uff1a\u201c\u5f88\u7f8e\uff0c\u50cf\u4f60\u4e00\u6837\u3002\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f60\u8bf4\u614c\uff0c\u5979\u4e00\u5b9a\u7f8e\u5f97\u591a\uff01\u201d\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u6211\u5988\u8bf4\u4f60\u795e \u60c5\u4e3e\u6b62\uff0c\u90fd\u50cf\u4f60\u7236\u4eb2\u5c11\u65f6\u4e00\u6837\uff0c\u90a3\u4e48\u4f60\u4e5f\u4e00\u5b9a\u662f\u4e2a\u591a\u60c5\u79cd\u5b50\u4e86\uff1f\u201d \u3000\u3000\u6b64\u8bdd\u7a81\u5982\u5176\u6765\uff0c\u5510\u7ecf\u5929\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u90b9\u7edb\u971e\u9053\uff0c\u201c\u4f60\u7239\u4ee5\u524d\u5728\u6211\u5916\u7956\u5bb6\u66fe\u5199\u8fc7\u4e00 \u9996\u8bcd\uff0c\u90a3\u5f20\u8bcd\u7b3a\uff0c\u6211\u5988\u8fd8\u6536\u7740\uff0c\u6211\u77a7\u7740\u597d\u73a9\uff0c\u5e26\u5728\u8eab\u8fb9\uff0c\u60f3\u8bf7\u4f60\u89e3\u7ed9\u6211\u542c\uff0c\u6211\u4e0d\u5927\u61c2\uff0c\u4f46\u8bfb \u8d77\u6765\u4e5f\u89c9\u5f97\u5199\u8bcd\u7684\u4eba\uff0c\u4e00\u5b9a\u591a\u60c5\u5f97\u5f88\u3002\u201d\u90b9\u7edb\u971e\u5973\u5b69\u513f\u5bb6\uff0c\u53e3\u6ca1\u906e\u62e6\uff0c\u5510\u7ecf\u5927\u542c\u5979\u8c08\u8bba\u81ea\u5df1 \u7684\u7236\u4eb2\uff0c\u5374\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u4f46\u5fc3\u4e2d\u597d\u5947\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u5e26\u5728\u8eab\u8fb9\u4e48\uff1f\u62ff\u6765\u7ed9\u6211\u77a7\u77a7\u3002\u201d \u3000\u3000\u90a3\u5f20\u8bcd\u7b3a\u5df2\u6709\u70b9\u6b8b\u7834\u4e86\uff0c\u4f46\u6bcf\u4e00\u4e2a\u5b57\u90fd\u8fd8\u5b8c\u6574\uff0c\u586b\u7684\u8bcd\u724c\u662f\u767e\u5b57\u4ee4\uff0c\u8bcd\u9053\uff1a \u3000\u3000\u98d8\u840d\u5026\u4fa3\uff0c\u7b97\u832b\u832b\u4eba\u6d77\uff0c\u53cb\u670b\u77e5\u5426\uff1f \u3000\u3000\u5251\u5323\u8bd7\u56ca\u957f\u4f5c\u4f34\uff0c\u8e0f\u7834\u665a\u98ce\u671d\u9732\u3002 \u3000\u3000\u957f\u5578\u7a7f\u4e91\uff0c\u9ad8\u6b4c\u6563\u96fe\uff0c\u5b64\u96c1\u6765\u8fd8\u53bb\uff01 \u3000\u3000\u76df\u9e25\u793e\u71d5\uff0c\u96ea\u6ce5\u9e3f\u722a\u65e0\u636e\uff01 \u3000\u3000\u4e91\u5c71\u68a6\u5f71\u6a21\u7cca\uff0c\u4e73\u71d5\u5bfb\u5de2\uff0c\u53c8\u60e7\u91cd\u5e18\u963b\uff1b \u3000\u3000\u9732\u767d\u84d6\u82cd\u80a0\u65ad\u53e5\uff0c\u5374\u60c5\u4f55\u4eba\u4f20\u8bed\uff1f \u3000\u3000\u8549\u6850\u72ec\u62b1\uff0c\u9713\u88f3\u7ec6\u8c31\uff0c\u671b\u65ad\u5927\u6daf\u8def\uff01 \u3000\u3000\u7d20\u5a25\u9752\u5973\uff0c\u4ed9\u8e2a\u751a\u65e5\u91cd\u9047\u3002 \u3000\u3000\u8fd9\u9996\u8bcd\u672c\u6765\u662f\u5510\u6653\u6f9c\u5f53\u5e74\u601d\u5fc6\u5415\u56db\u5a18\u800c\u5199\u7684\uff0c\u6768\u67f3\u9752\u4e00\u77e5\u534a\u89e3\uff0c\u5374\u8bef\u4f1a\u6210\u662f\u4e3a\u5979\u5199\u7684\uff0c \u4fdd\u7559\u81f3\u4eca\u3002\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f60\u5988\u5988\u771f\u597d\u798f\u6c14\uff0c\u4f60\u7239\u7239\u628a\u5979\u5f53\u6210\u4ed9\u5973\u5462\uff01\u4f60\u5988\u90a3\u65f6\u5019\u4e3a\u4ec0\u4e48\u5c06\u4ed6 \u51b7\u6de1\uff1f\u201d\u5979\u628a\u8bcd\u4e2d\u7684\u201c\u7d20\u5a25\u9752\u5973\u201d\u5f53\u6210\u662f\u5510\u7ecf\u5929\u73b0\u5728\u7684\u6bcd\u4eb2\uff0c\u5510\u7ecf\u5929\u5374\u662f\u5fc3\u4e2d\u5947\u602a\u3002 \u3000\u3000\u5510\u7ecf\u5929\u53cd\u590d\u541f\u54e6\uff0c\u7ec6\u5473\u8bcd\u4e2d\u4e4b\u610f\uff0c\u4e43\u662f\u6000\u5ff5\u8fdc\u4eba\uff0c\u800c\u53c8\u6709\u4e00\u79cd\u201c\u53ef\u671b\u800c\u4e0d\u53ef\u5373\u201d\u7684\u5e7d\u6028 \uff0c\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u90a3\u65f6\u7236\u4eb2\u6b63\u4f4f\u5728\u626c\u5bb6\uff0c\u8fd9\u9996\u8bcd\u81ea\u7136\u4e0d\u662f\u5199\u7ed9\u6768\u67f3\u9752\u7684\u4e86\u3002\u201d\u4ed6\u4e5f\u4e0d\u77e5\u6b64\u8bcd \u6765\u5386\uff0c\u53ea\u9053\u662f\u7236\u4eb2\u5f53\u5e74\u5199\u7ed9\u6bcd\u4eb2\u7684\u8bcd\u7b3a\uff0c\u6697\u81ea\u7b11\u9053\uff1a\u201c\u6211\u53ea\u89c1\u7239\u7239\u548c\u5988\u5988\u76f8\u656c\u5982\u5bbe\uff0c\u539f\u6765\u5f53 \u5e74\u4e5f\u66fe\u95f9\u8fc7\u4e00\u573a\u522b\u626d\u3002\u201d\u90b9\u7edb\u971e\u5fae\u5fae\u7b11\u9053\uff1a\u4e00\u6709\u5176\u7236\u5fc5\u6709\u5176\u5b50\uff0c\u60f3\u6765\u4f60\u4e5f\u662f\u4e2a\u591a\u60c5\u79cd\u5b50\u7684\u4e86 \uff0c\u53ef\u60dc\u4f60\u7684\u5c0f\u8868\u59b9\u4e0d\u5728\u8eab\u8fb9\u5475\uff01\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u7f20\u7ef5\u5f98\u607b\uff0c\u5982\u6028\u5982\u6155\uff0c\u5510\u7ecf\u5929\u53cd\u590d\u541f\u54e6\uff0c\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\uff0c\u4e0d\u89c9\u75f4\u4e86\u3002\u89c1\u90b9\u7edb\u971e\u7b11 \u8bed\u76c8\u76c8\uff0c\u4e00\u526f\u65e0\u90aa\u7684\u5929\u771f\u5c11\u5973\u795e\u6001\uff0c\u5fc3\u4e2d\u6697\u81ea\u7b11\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u77e5\u9053\uff0c\u6211\u7684\u5c0f\u8868\u59b9\u4e0d\u8fc7\u50cf\u5982\u4eca \u4e4b\u4f60\uff0c\u5f53\u5e74\u4f60\u6bcd\u4eb2\u4e00\u6837\uff0c\u800c\u6211\u4e5f\u548c\u6211\u7236\u4eb2\u4e00\u6837\uff0c\u5fc3\u4e2d\u6000\u5ff5\u7684\u5b9e\u662f\u53e6\u6709\u5176\u4eba\u3002\u201d \u3000\u3000\u90b9\u7edb\u971e\u89c1\u5510\u7ecf\u5929\u5ffd\u800c\u6c89\u601d\uff0c\u5ffd\u800c\u5fae\u7b11\uff0c\u65e2\u4f3c\u610f\u607c\uff0c\u53c8\u4f3c\u795e\u4f24\uff0c\u53ea\u9053\u662f\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u64a9 \u8d77\u4ed6\u7684\u60c5\u7eea\uff0c\u5fc3\u4e2d\u6697\u6697\u540e\u6094\u3002\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u8309\u8389\u82b1\u4e0b\uff0c\u5979\u7684\u6bcd\u4eb2\u8d70\u4e86\u51fa\u6765\uff0c \u90b9\u7edb\u971e\u7791\u9053\uff1a\u201c\u5988\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5077\u542c\u6211\u4eec\u8bf4\u8bdd\uff1f\u201d\u6768\u67f3\u9752\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u4e86\u4ec0\u4e48\u8bdd\u6765\u4e86\uff1f\u8fde\u5988 \u4e5f\u542c\u4e0d\u5f97\u3002\u201d\u5979\u4fe9\u6bcd\u5973\u6709\u5982\u59d0\u59b9\uff0c\u8bf4\u60ef\u7b11\u8bdd\uff1a\u5510\u7ecf\u5929\u5374\u662f\u6709\u70b9\u5c34\u5c2c\uff0c\u95ee\u9053\uff1a\u201c\u4f2f\u6bcd\u8fd9\u4e48\u665a\u4e86 \uff0c\u8fd8\u4e00\u4e2a\u4eba\u51fa\u6765\uff1f\u201d\u6768\u67f3\u9752\u770b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u9053\uff1a\u201c\u662f\u5475\uff0c\u662f\u5f88\u665a\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9762\u4e0a\u4e00\u7ea2\uff0c\u53ea\u542c\u5f97\u6768\u67f3\u9752\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7ecf\u5929\uff0c\u4f60\u73b0\u5728\u5c1a\u672a\u6062\u590d\uff0c\u971e\u513f\u4f60\u966a\u5510\u54e5\u54e5 \u73a9\uff0c\u53ef\u4e0d\u8981\u79bb\u5f00\u5bb6\u95e8\u592a\u8fdc\u3002\u201d\u90b9\u7edb\u971e\u89c1\u6bcd\u4eb2\u8fd9\u56de\u8bf4\u8bdd\uff0c\u4e0d\u4f3c\u53d6\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u6768 \u67f3\u9752\u9053\uff1a\u201c\u7ecf\u5929\uff0c\u4f60\u8fd8\u8bb0\u5f97\u90a3\u75af\u4e10\u5417\uff1f\u201d\u90b9\u7ece\u971e\u6253\u4e86\u4e2a\u5bd2\u6218\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e11\u516b\u602a\uff0c\u6b7b\u9ebb \u75af\uff0c\u70e7\u53d8\u4e86\u7070\u6211\u4e5f\u8bb0\u5f97\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4ed6\u4e5f\u4e0d\u7b97\u4e11\u602a\uff0c\u4e0d\u662f\u6709\u610f\u7684\u5413\u4eba\u7684\u65f6\u5019\uff0c\u770b \u6765\u5012\u662f\u4e00\u4e2a\u7709\u6e05\u76ee\u79c0\u7684\u5c11\u5e74\u3002\u201d\u8bdd\u8bf4\u51fa\u540e\uff0c\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\uff0c\u6697\u6697\u8be7\u5f02\u3002 \u3000\u3000\u5510\u7ecf\u5929\u66fe\u542c\u7236\u6bcd\u8c08\u8fc7\u4ed6\u4eec\u5f53\u5e74\u5728\u6d77\u5c9b\u4e0a\u5927\u6218\u6bd2\u9f99\u5c0a\u8005\u4e4b\u4e8b\uff0c\u6bd2\u9f99\u5c0a\u8005\u66fe\u7ecf\u662f\u4e2a\u5927\u9ebb\u75af\uff0c \u540e\u6765\u9003\u5230\u6d77\u5c9b\u4e2d\u81ea\u5df1\u7597\u597d\uff0c\u56e0\u800c\u618e\u6068\u4e16\u4eba\u3002\u5510\u7ecf\u5929\u66fe\u8bfb\u8fc7\u4e00\u4e9b\u533b\u4e66\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u50cf\u4ed6\u90a3\u6837 \u6ee1\u8eab\u7599\u7629\uff0c\u9ebb\u75af\u75c5\u8be5\u662f\u67d3\u5f97\u5f88\u91cd\u7684\u4e86\uff0c\u4f55\u4ee5\u7709\u6bdb\u5e76\u4e0d\u8131\u843d\uff1f\u83ab\u975e\u4ed6\u4e5f\u662f\u548c\u6bd2\u9f99\u5c0a\u8005\u4e00\u6d41\u4eba\u7269 \uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u82e5\u7136\u5982\u6b64\uff0c\u90a3\u4ed6\u7684\u75c5\u4e5f\u8be5\u65e9\u5df2\u6cbb\u597d\u3002\u6bd2\u9f99\u5c0a\u8005\u5f53\u5e74\u9003\u5230\u6d77\u5916\uff0c\u7ec3\u4e86\u51e0\u5341\u5e74\u624d \u7ec3\u5230\u4e0a\u4e58\u6b66\u529f\u3002\u4ed6\u8fd9\u6837\u5e74\u9752\uff0c\u60a3\u4e86\u9ebb\u75af\uff0c\u81ea\u7136\u65e0\u4eba\u80af\u6559\uff0c\u4ed6\u53c8\u600e\u4e48\u7ec3\u5230\u4e86\u4e00\u8eab\u4e0a\u4e58\u7684\u6b66\u529f\u53f6 \u5ffd\u7136\u60f3\u8d77\u83ab\u975e\u4ed6\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u5f92\u5f1f\uff0c\u4f46\u8fd9\u662f\u7edd\u4e0d\u53ef\u80fd\u4e4b\u4e8b\u3002\u4ed6\u7684\u6bcd\u4eb2\u66fe\u7ecf\u8c08\u53ca\uff0c\u5f53\u5415\u56db\u5a18\u5c06 \u6bd2\u9f99\u5c0a\u8005\u6536\u670d\u4e4b\u540e\uff0c\u6bd2\u9f99\u5c0a\u8005\u56de\u5230\u4e2d\u539f\uff0c\u4e0d\u5230\u4e09\u5e74\u5c31\u6b7b\u4e86\u3002\u90a3\u65f6\u8fd9\u75af\u4e10\u6700\u591a\u4e0d\u8fc7\u662f\u4e09\u4e24\u5c81\uff0c \u8bf4\u8bdd\u8fd8\u672a\u8bf4\u5f97\u6e05\u695a\u7684\u5a03\u5a03\u3002 \u3000\u3000\u5510\u7ecf\u5929\u672c\u662f\u4e2a\u5fc3\u601d\u7075\u654f\u7684\u4eba\uff0c\u75c5\u6108\u4e4b\u540e\uff0c\u795e\u667a\u6e05\u660e\uff0c\u7ec6\u60f3\u90a3\u75af\u4e10\u7684\u97f3\u5bb9\u4e3e\u52a8\uff0c\u53ea\u89c9\u6709\u4e0d \u5c11\u53ef\u7591\u4e4b\u5904\uff0c\u95ee\u6768\u67f3\u9752\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u4f60\u63d0\u8d77\u8fd9\u4e2a\u75af\u4e10\uff0c\u83ab\u975e\u4ed6\u53c8\u5728\u9644\u8fd1\u51fa\u73b0\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u90bb\u53bf\u4e00\u4e2a\u6b66\u5e08\u524d\u6765\u62a5\u8baf\uff0c\u8bf4\u662f\u4ed6\u4eec\u90a3\u513f\u53d1\u73b0\u8fd9\u4e48\u6837\u4e00\u4e2a\u602a\u4eba\uff0c\u4e13\u4e0e\u6b66\u6797\u597d\u624b\u4f5c\u5bf9\uff0c \u542c\u8bf4\u5510\u8001\u592a\u5a46\u4e5f\u7ed9\u4ed6\u6253\u4e86\uff0c\u4ed6\u4eec\u524d\u6765\u62a5\u8baf\u7684\u5e08\u7236\u8fd8\u60f3\u9080\u8bf7\u971e\u513f\u7684\u7239\u53bb\u52a9\u62f3\u5462\uff0c\u4ed6\u5374\u4e0d\u77e5\u6211\u4eec \u65e9\u4e0e\u90a3\u75af\u4e10\u4f1a\u8fc7\u4e86\u3002\u201d\u5510\u7ecf\u5929\u4e00\u60f3\uff0c\u81ea\u5df1\u5c1a\u672a\u590d\u539f\uff0c\u82e5\u7136\u90a3\u75af\u4e10\u4e00\u6765\uff0c\u7684\u786e\u65e0\u4eba\u662f\u4ed6\u5bf9\u624b\u3002 \u90b9\u7edb\u971e\u95ee\u9053\uff1a\u201c\u662f\u90a3\u4e2a\u66fe\u6559\u6211\u6253\u8fc7\u6697\u5668\u7684\u5510\u8001\u592a\u5a46\u4e48\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u7b11\u5bf9\u5510\u7ecf\u5929 \u9053\uff1a\u201c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u5979\u7684\u5927\u592b\u88ab\u4f60\u7684\u59e8\u6bcd\u6240\u6740\uff0c\u90a3\u65f6\u5979\u66fe\u51e0\u6b21\u5411\u6211\u4eec\u5bfb\u4ec7\uff0c\u540e\u6765\u5f97\u4eba\u5316\u89e3\uff0c \u5982\u4eca\u4e0e\u6211\u4eec\u53cd\u800c\u6210\u4e3a\u4e86\u597d\u53cb\u4e86\u3002\u201d\u5979\u4eec\u6240\u8c08\u7684\u201c\u5510\u8001\u592a\u5a46\u201d\u5c31\u662f\u5510\u8d5b\u82b1\uff0c\u7b97\u8d77\u8f88\u4efd\u6765\u4ea6\u5373\u9f99 \u7075\u77eb\u7684\u5e08\u59d0\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u672c\u6765\u8981\u53bb\u5bfb\u8bbf\u5510\u5bb6\u7684\u4eba\u7684\uff0c\u5374\u539f\u6765\u5c31\u5728\u90bb\u53bf\u3002 \u3000\u3000\u90b9\u7edb\u971e\u9a82\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u5927\u9ebb\u75af\uff0c\u771f\u662f\u50cf\u4e71\u54ac\u4eba\u7684\u75af\u72d7\u4e00\u822c\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f2f\u6bcd\u53ef\u77e5\u9053 \u4ed6\u7684\u6765\u5386\u4e48\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u542c\u4f60\u90b9\u4f2f\u4f2f\u8bf4\uff0c\u8fd9\u75af\u4e10\u662f\u6700\u8fd1\u4e24\u5e74\u624d\u51fa\u73b0\u7684\uff0c\u4ed6\u4ece\u4e2d\u539f\u5230\u897f\u5317 \uff0c\u4e13\u627e\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u7f9e\u8fb1\u4e00\u756a\uff0c\u4fbf\u626c\u957f\u800c\u53bb\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u6765\u5386\u3002\u201d\u5510\u7ecf\u5929\u6c89\u541f\u4e0d \u8bed\uff0c\u5fc3\u4e2d\u53cd\u590d\u601d\u91cf\uff0c\u4e0d\u5f97\u5176\u89e3\u3002\u5ffd\u542c\u5f97\u6768\u67f3\u9752\u9053\uff1a\u201c\u5c45\u7136\u6709\u4e24\u4e2a\u7f8e\u82e5\u5929\u4ed9\u7684\u5973\u5b50\u80af\u4e0e\u8fd9\u5927\u9ebb \u75af\u4e00\u9053\u3002\u201d\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\u3002\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u6709\u4eba\u89c1\u4ed6\u4eec\u4e09\u4eba\u4e00\u9053\u3002\u8fd8\u6709\u8bf4\u6709\u7b11 \u5462\u3002\u542c\u8bf4\u90a3\u4e24\u4e2a\u5973\u7684\u4e5f\u66fe\u8fdb\u5165\u5510\u5bb6\uff0c\u8be6\u7ec6\u60c5\u5f62\u53ef\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d\u5510\u7ecf\u5929\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u4e2d\u60f3\u9053 \uff1a\u201c\u96be\u9053\u8fd9\u4e24\u4e2a\u5973\u5b50\u7adf\u662f\u51b0\u5ddd\u5929\u5973\u4e0e\u5979\u7684\u4f8d\u5973\u5e7d\u840d\uff1f\u201d\u60f3\u51b0\u5ddd\u5929\u5973\u4f55\u7b49\u9ad8\u50b2\uff0c\u7b49\u95f2\u4e4b\u4eba\u90fd\u4e0d \u653e\u5728\u5979\u773c\u5185\uff0c\u5979\u80af\u4e0e\u90a3\u9ebb\u75af\u4e00\u9053\uff1f\u6b64\u4e8b\u8bf4\u6765\u5b9e\u662f\u8fc7\u4e8e\u602a\u8bde\uff0c\u96be\u4ee5\u5165\u4fe1\u3002\u4f46\u9664\u4e86\u5979\u4eec\u4e8c\u4eba\uff0c\u53c8 \u8fd8\u6709\u8c01\u79f0\u5f97\u4e0a\u201c\u7f8e\u82e5\u5929\u4ed9\u201d\uff1f \u3000\u3000\u4ed6\u6ca1\u60f3\u5230\uff0c\u8fd9\u4e24\u4e2a\u201c\u7f8e\u82e5\u5929\u4ed9\u201d\u7684\u5973\u5b50\uff0c\u5f53\u771f\u5c31\u662f\u51b0\u5ddd\u5929\u5973\u548c\u5979\u7684\u4f8d\u5973\u5e7d\u840d\u3002\u5979\u4eec\u5230\u4e86 \u4f46\u5409\u5c14\uff0c\u5f97\u89c1\u767d\u6559\u6cd5\u738b\uff0c\u95ee\u660e\u4e86\u4eba\u5ddd\u7684\u9053\u8def\uff0c\u65b9\u5411\u662f\u8d70\u5bf9\u4e86\uff0c\u53ef\u662f\u5374\u8d70\u4e86\u51e0\u6b21\u5c94\u8def\uff0c\u8fdb\u5165\u96c0 \u513f\u5c71\u65f6\uff0c\u53cd\u843d\u5728\u5510\u7ecf\u5929\u4e4b\u540e\uff0c\u8fd9\u5929\u5979\u4eec\u4e5f\u5230\u4e86\u96c0\u513f\u5c71\u7684\u9669\u5cfb\u4e4b\u5904\uff0c\u5e7d\u840d\u5ffd\u7136\u4f4e\u58f0\u60ca\u547c\uff0c\u8dc3\u540e \u6570\u6b65\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u770b\uff0c\u53ea\u89c1\u5ca9\u77f3\u4e4b\u4e0b\uff0c\u5367\u7740\u4e00\u4e2a\u4e5e\u4e10\uff0c\u6321\u7740\u53bb\u8def\u3002\u8fd9\u4e5e\u4e10\u8863\u88f3\u7834\u70c2\uff0c\u9732\u51fa\u4e24 \u6761\u624b\u81c2\uff0c\u81c2\u4e0a\u7ed3\u6ee1\u4e00\u4e2a\u4e2a\u5927\u5927\u5c0f\u5c0f\u7684\u7599\u7629\uff0c\u8fd8\u6709\u51e0\u5904\u75ae\u53e3\uff0c\u73b0\u51fa\u6697\u7d2b\u8272\u7684\u76ae\u8089\u3002\u9762\u4e0a\u4e00\u7247\u7ea2 \u4e91\uff0c\u7565\u5e26\u6d6e\u80bf\uff0c\u5f62\u8c61\u5341\u5206\u96be\u770b\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u8bc6\u9ebb\u75af\uff0c\u89c1\u4e86\u8fd9\u4e5e\u4e10\u5944\u5944\u4e00\u606f\u7684\u6837\u5b50\uff0c\u8d77\u4e86\u601c\u60af \u4e4b\u5fc3\uff0c\u7565\u4e00\u601d\u91cf\uff0c\u5bf9\u5e7d\u840d\u9053\uff1a\u201c\u6551\u4eba\u4e00\u547d\uff0c\u80dc\u9020\u516d\u7ea7\u6d6e\u5c60\uff0c\u4f60\u628a\u4ed6\u6276\u8d77\u6765\uff0c\u5f85\u6211\u770b\u770b\u3002\u201d\u5e7d \u840d\u60f3\u4e0d\u5230\u4e3b\u4eba\u7adf\u6709\u5982\u6b64\u5429\u5490\uff0c\u5927\u611f\u4e3a\u96be\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6b64\u5730\u4eba\u8ff9\u7f55\u5230\uff0c\u6211\u4eec\u4e0d\u6551\u4ed6\u5c1a\u6709\u8c01\u6551\u4ed6\uff1f\u5e7d\u840d\uff0c\u4f60\u5feb\u53bb\u5c06\u4ed6\u6276\u8d77\u3002\u201d\u51b0 \u5ddd\u5929\u5973\u672a\u7ecf\u4e16\u6545\uff0c\u4e00\u7247\u597d\u5fc3\uff0c\u5374\u672a\u60f3\u5230\uff0c\u65e2\u7136\u6b64\u5730\u4eba\u8ff9\u7f55\u5230\uff0c\u8fd9\u4e5e\u4e10\u5c31\u5b9a\u975e\u5e38\u4eba\u3002\u5e7d\u840d\u65e0\u5948 \uff0c\u4e0a\u524d\u4e24\u6b65\uff0c\u77a7\u4e86\u90a3\u4e5e\u4e10\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u770b\u4ed6\u53ea\u6015\u4e0d\u80fd\u6d3b\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053 \uff1f\u201d\u5e7d\u840d\u6298\u4e86\u4e00\u6280\u6811\u679d\uff0c\u8f7b\u8f7b\u4e00\u64a9\uff0c\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u50f5\u5367\u5982\u6b7b\uff0c\u5df2\u7ecf\u4e0d\u80fd\u52a8\u4e86\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c \u90a3\u4e5e\u4e10\u5ffd\u7136\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u5750\u4e86\u8d77\u6765\uff0c\u5f20\u5f00\u4e24\u53cc\u5446\u6ede\u7684\u773c\u775b\uff0c\u6728\u7136\u5730\u770b\u4e86\u51b0\u5ddd \u5929\u5973\u4e00\u773c\uff0c\u547b\u541f\u8bf4\u9053\uff0c\u201c\u6211\u5feb\u6b7b\u4e86\u5566\uff0c\u4f60\u4eec\u8fd8\u6b3a\u8d1f\u6211\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u542c\u4ed6\u8bf4\u8bdd\uff0c\u58f0\u97f3\u867d\u7136\u5fae \u5f31\uff0c\u5374\u65e0\u6c14\u8d25\u795e\u8870\u4e4b\u8c61\uff0c\u4e8e\u662f\u5bf9\u90a3\u4e5e\u4e10\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u662f\u997f\u4e86\u591a\u5929\u4e86\uff0c\u5148\u5403\u70b9\u4e1c\u897f\u3002\u201d \u5c06\u4e00\u53cc\u719f\u7f8a\u817f\u9012\u5230\u4ed6\u7684\u624b\u4e2d\uff0c\u90a3\u9ebb\u75af\u6f20\u7136\u65e0\u52a8\u4e8e\u4e2d\uff0c\u65e2\u65e0\u611f\u6fc0\uff0c\u66f4\u65e0\u9053\u8c22\uff0c\u5c06\u7f8a\u817f\u62ff\u4e86\u8fc7\u6765 \uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u56bc\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u600e\u4e48\u957f\u4e86\u6ee1\u8eab\u6bd2\u75ae\u5475\uff1f\u201d\u90a3\u4e5e\u4e10\u628a\u773c\u4e00\u7741\uff0c \u9053\uff1a\u201c\u6211\u751f\u6765\u5c31\u662f\u5982\u6b64\uff0c\u4f60\u6015\u770b\u5c31\u8d70\u8fdc\u4e9b\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8ba8\u538c\u4f60\uff0c\u6211\u662f\u60f3\u7ed9\u4f60\u533b \u6cbb\u3002\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u533b\u6cbb\uff1f\u201d\u773c\u775b\u7728\u4e86\u4e00\u4e0b\uff0c\u968f\u5373\u53c8\u6beb\u65e0\u8868\u60c5\u3002 \u3000\u3000\u51b0\u5bab\u4e2d\u6709\u7684\u662f\u5404\u79cd\u7075\u836f\uff0c\u51b0\u5ddd\u5929\u5973\u968f\u8eab\u4ea6\u643a\u6709\u591a\u79cd\uff0c\u53ea\u9053\u4ed6\u60a3\u7684\u662f\u4e00\u822c\u6bd2\u75ae\uff0c\u4fbf\u62ff\u51fa\u4e00 \u74f6\u4e13\u89e3\u65e0\u540d\u80bf\u6bd2\u7684\u836f\u7c89\uff0c\u9012\u7ed9\u4ed6\u9053\uff1a\u201c\u4f60\u5c06\u8fd9\u836f\u6577\u4e0a\uff0c\u770b\u770b\u5982\u4f55\uff1f\u201d\u90a3\u4e5e\u4e10\u6577\u4e86\u624b\u9762\u4e4b\u540e\uff0c \u6253\u5f00\u8d64\u818a\uff0c\u80cc\u4e0a\u6709\u4e00\u4e2a\u4e2a\u575f\u8d77\u7684\u7ed3\u8282\uff0c\u9053\uff1a\u201c\u6211\u6577\u4e0d\u5230\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5e7d\u840d\uff0c\u4f60\u7ed9\u4ed6\u6577 \u3002\u201d\u5e7d\u840d\u4e0d\u6562\u4e0d\u5141\uff0c\u6298\u4e86\u4e00\u652f\u6811\u679d\uff0c\u88f9\u4ee5\u767d\u5e03\uff0c\u5728\u5c71\u6da7\u4e2d\u4e00\u6d78\uff0c\u9178\u4e0a\u836f\u7c89\uff0c\u66ff\u4ed6\u643d\u4e86\u80cc\u810a\u3002 \u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u8fd9\u836f\u51c9\u6d78\u6d78\u7684\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u4f46\u6211\u8fd9\u75ae\u4ee5\u524d\u4e5f\u66fe\u533b\u8fc7\uff0c\u767e\u836f\u65e0\u6548\uff0c\u4f60\u7684\u836f\u672a\u5e05\u80fd \u5c06\u6211\u533b\u597d\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u518d\u8fc7\u4e24\u5929\uff0c\u82e5\u8fd9\u836f\u65e0\u6548\uff0c\u5c31\u518d\u8bd5\u7b2c\u4e8c\u79cd\u3002\u201d\u5e7d\u840d\u6025\u9053\uff1a\u201c\u6211\u4eec \u8fd8\u8981\u8d76\u8def\u5475\uff01\u201d\u90a3\u4e5e\u4e10\u76ef\u4e86\u5e7d\u840d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\u6781\u5566\uff0c\u6211\u6b63\u6101\u627e\u4e0d\u5230\u98df\u7269\uff0c\u540c\u4f60\u4eec\u8d70\uff0c\u65e2\u6709 \u836f\u533b\uff0c\u53c8\u4e0d\u6101\u6ca1\u5403\u7684\u3002\u201d\u51b0\u5ddd\u5929\u5973\u672c\u672a\u60f3\u5230\u4e0e\u4ed6\u540c\u8d70\uff0c\u4f46\u8bdd\u4e00\u8bf4\u51fa\uff0c\u90a3\u4e5e\u4e10\u7acb\u5373\u7f20\u4e0a\uff0c\u51b0\u5ddd \u5929\u5973\u7a0d\u4e00\u8e0c\u8e87\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6551\u4eba\u6551\u5f7b\uff0c\u90a3\u4f60\u5c31\u8ddf\u7740\u8d70\u5427\uff0c\u4f60\u80fd\u8d70\u5417\uff1f\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u6211\u4e00\u5403 \u9971\uff0c\u8d70\u5c71\u8def\u90a3\u662f\u6beb\u4e0d\u8d39\u529b\u3002\u201d\u62fe\u8d77\u62d0\u6756\uff0c\u5c31\u8ddf\u5728\u51b0\u5ddd\u5929\u5973\u540e\u9762\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u540c\u4ed6\u8d70\u4e86\u4e24\u5929\uff0c\u5230\u4e86\u96c0\u513f\u5c71\u7684\u5357\u9762\uff0c\u8fdc\u8fdc\u671b\u53bb\u5df2\u53ef\u89c1\u5230\u5c71\u4e0b\u7684\u4eba\u5bb6\u3002\u8fd9\u4e24\u5929\u6765 \uff0c\u90a3\u4e5e\u4e10\u90fd\u662f\u4e00\u58f0\u4e0d\u54cd\uff0c\u51b0\u5ddd\u5929\u5973\u6253\u5230\u91ce\u517d\uff0c\u70e4\u719f\u4e86\u7ed9\u4ed6\u5403\uff0c\u4ed6\u4ea6\u7167\u6837\u5927\u56bc\uff0c\u5e76\u65e0\u9053\u8c22\uff0c\u836f \u6577\u4e86\u4e24\u5927\uff0c\u4ed6\u8eab\u4e0a\u7684\u7ea2\u80bf\u7a0d\u9000\uff0c\u5c1a\u672a\u77e5\u6548\u679c\u5982\u4f55\u3002\u5e7d\u840d\u5fc3\u9053\uff1a\u201c\u8fc7\u4e86\u96c0\u513f\u5c71\uff0c\u5c31\u662f\u4eba\u70df\u7a20\u5bc6 \u4e4b\u5730\uff0c\u5e26\u7740\u8fd9\u6837\u4e00\u4e2a\u4e5e\u4e10\u540c\u8d70\uff0c\u5c82\u4e0d\u6559\u4eba\u7b11\u8bdd\uff1f\u201d\u6b63\u60f3\u548c\u51b0\u5ddd\u5929\u5973\u8bf4\uff0c\u90a3\u4e5e\u4e10\u5ffd\u7136\u5750\u4e86\u4e0b\u6765 \uff0c\u5bf9\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u6211\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5947\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u6015\u4f60\uff1f\u201d \u3000\u3000\u90a3\u4e5e\u4e10\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4e16\u4e0a\u8c01\u90fd\u6015\u6211\uff0c\u5c31\u53ea\u6709\u4f60\u4e0d\u6015\u6211\u3002\u201d\u5e7d\u840d\u55e4\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709 \u4ec0\u4e48\u672c\u9886\u522b\u4eba\u8981\u6015\u4f60\uff1f\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u522b\u4eba\u4e0d\u662f\u6015\u6211\uff0c\u662f\u8ba8\u538c\u6211\uff01\u201d\u51b0\u5ddd \u5929\u5973\u77aa\u4e86\u5e7d\u840d\u4e00\u773c\uff0c\u90a3\u4e5e\u4e10\u53c8\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6551\u6211\uff1f\u4f60\u4e0d\u8ba8\u538c\u6211\u7684\u6bd2\u75ae\u5417\uff0c\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u4e00\u751f\u5d07\u4fe1\u4f5b\u6cd5\uff0c\u5979\u5bf9\u6211\u8bf4\u8fc7\u4f5b\u7956\u7684\u6545\u4e8b\uff0c\u4f5b\u7956\u66fe\u5272\u8089\u5582\u9e70\uff0c\u820d\u8eab \u6551\u864e\uff0c\u53c8\u8bf4\u2018\u6211\u4e0d\u5165\u5730\u72f1\uff0c\u8c01\u5165\u5730\u72f1\uff1f\u2019\uff0c\u4e3a\u4e86\u6551\u4eba\uff0c\u4f5b\u7956\u5b81\u613f\u5982\u6b64\uff0c\u6211\u867d\u7136\u4e0d\u662f\u4f5b\u95e8\u5f1f\u5b50 \u3002\u4f46\u6bcd\u4eb2\u7684\u8bdd\u5374\u6ca1\u6709\u5fd8\u8bb0\u3002\u201d\u90a3\u60a3\u9ebb\u75af\u75c5\u7684\u4e5e\u4e10\u53cc\u773c\u4e00\u7741\uff0c\u4f3c\u6120\u4f3c\u6012\uff0c\u5374\u5ffd\u5730\u51b7\u51b7\u4e00\u7b11\uff0c\u9053 \uff0c\u201c\u539f\u6765\u4f60\u4e4b\u6551\u6211\uff0c\u7adf\u662f\u5f53\u6210\u4e0b\u5730\u72f1\u6551\u4eba\u4e00\u6837\uff0c\u90a3\u6211\u5c82\u4e0d\u6210\u4e86\u5730\u72f1\u4e2d\u7684\u6076\u9b3c\u4e86\uff1f\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u6211\u6ca1\u6709\u8fd9\u6837\u7684\u610f\u601d\uff0c\u55ef\u2026\u2026\u201d\u5fc3\u4e2d\u611f\u89c9\u8fd9\u4e5e\u4e10\u65e0\u53ef\u7406\u55bb\uff0c\u672c\u60f3\u89e3\u91ca\u5374\u53c8\u5fcd\u7740\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u53c8\u770b\u4e86\u51b0\u5ddd\u5929\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8eab\u4f69\u5b9d\u5251\uff0c\u60f3\u5fc5\u662f\u4e2a\u5927\u6709\u672c\u9886\u4e4b\u4eba\u4e86\uff1f\u4f60\u7684\u5b9d\u5251 \u53ef\u4ee5\u501f\u6211\u4e00\u770b\u4e48\uff1f\u201d\u5e7d\u840d\u53c8\u5916\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u7684\u516c\u4e3b\u672c\u610f\u662f\u8981\u6551\u4f60\uff0c\u5979\u7684\u5b9d\u5251\u82e5\u7136\u501f\u7ed9 \u4f60\u770b\uff0c\u90a3\u5c31\u53cd\u800c\u5bb3\u4e86\u4f60\u4e86\u3002\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5979\u7684\u5b9d\u5251\u4e0d\u662f\u5e38\u4eba\u6240\u80fd\u770b\u7684 \uff0c\u770b\u4e86\u4e0d\u6b7b\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\u3002\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u8fd9\u6837\u5389\u5bb3\uff1f\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5927\u4e0d\u76f8\u4fe1\uff0c\u5ffd\u53c8\u62cd\u638c\u7b11 \u9053\uff1a\u201c\u90a3\u66f4\u5999\u4e86\uff0c\u6211\u65e2\u6015\u91ce\u517d\u5403\u6211\uff0c\u53c8\u6015\u522b\u4eba\u5bb3\u6211\u3002\u4f60\u4eec\u65e2\u6709\u8fd9\u6837\u5927\u7684\u672c\u4e8b\uff0c\u53c8\u6709\u8fd9\u6837\u5386\u5bb3 \u7684\u5b9d\u5251\uff0c\u90a3\u6211\u8ddf\u7740\u4f60\u4eec\uff0c\u5c31\u4ec0\u4e48\u4e5f\u4e0d\u7528\u6015\u4e86\u3002\u201d\u5e7d\u840d\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8ddf\uff1f\u201d\u90a3\u4e5e\u4e10 \u9053\uff1a\u201c\u6551\u4eba\u6551\u5f7b\u5e95\uff0c\u4f60\u4eec\u521a\u624d\u8bf4\u5f97\u5982\u6b64\u597d\u542c\uff0c\u73b0\u5728\u53c8\u4e0d\u7406\u6211\u4e86\u5417\uff1f\u201d\u5e7d\u840d\u5fc3\u9053\uff1a\u201c\u90a3\u90fd\u662f\u5c0f \u516c\u4e3b\u60f9\u7684\u9ebb\u70e6\uff0c\u6211\u51e0\u65f6\u8bf4\u8fc7\u6551\u4f60\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u613f\u610f\u8ddf\u6211\u4eec\u8d70\uff0c\u5c31\u4e00 \u540c\u8d70\u5427\u3002\u201d\u8fd9\u4e5e\u4e10\u5c45\u7136\u80fd\u770b\u51fa\u5979\u7684\u5b9d\u5251\uff0c\u51b0\u5ddd\u5927\u5973\u4e5f\u4e0d\u7981\u6697\u6697\u5fc3\u7591\u4e86\u3002 \u3000\u3000\u5e7d\u840d\u65e0\u5948\uff0c\u53ea\u597d\u8ba9\u90a3\u4e2a\u4e5e\u4e10\u8ddf\u7740\u5979\u4eec\uff0c\u8d70\u4e86\u534a\u5929\uff0c\u773c\u524d\u4e00\u4eae\uff0c\u53ea\u89c1\u4e00\u6761\u7011\u5e03\u50cf\u4e00\u5f20\u73cd\u73e0 \u5e18\u4e8e\u4ece\u5c71\u4e0a\u5012\u6302\u4e0b\u6765\uff0c\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u6211\u8d70\u4e0d\u8fc7\u53bb\u5566\uff0c\u4f60\u80cc\u6211\u8fc7\u53bb\u3002\u201d\u5e7d\u840d\u5927\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a \u4eba\u600e\u7684\u5982\u6b64\u4e0d\u77e5\u81ea\u91cf\uff0c\u4f60\u5c31\u662f\u6211\u7684\u7236\u4eb2\u6211\u4e5f\u4e0d\u80fd\u80cc\u4f60\u3002\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u90a3\u8fd8\u8bf4\u4ec0\u4e48\u5165\u5730\u72f1\u6551 \u4eba\uff1f\u4e0a\u6709\u7011\u5e03\uff0c\u4e0b\u6709\u5c71\u6da7\uff0c\u4f60\u4eec\u8df3\u5f97\u8fc7\u53bb\uff0c\u6211\u53ef\u4e0d\u80fd\u3002\u201d\u7d22\u6027\u5728\u5c71\u6da7\u8fb9\u5927\u9a6c\u91d1\u5200\u7684\u5750\u4e86\u4e0b\u6765 \u3002\u5e7d\u840d\u54ed\u7b11\u4e0d\u5f97\uff0c\u6012\u9053\uff1a\u201c\u5c0f\u516c\u4e3b\u4e0d\u8981\u518d\u7406\u4ed6\u5566\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd \u542c\u5f97\u4e00\u58f0\u602a\u7b11\uff0c\u58f0\u9707\u5c71\u8c37\uff0c\u534a\u5c71\u4e71\u77f3\u5806\u4e2d\u5ffd\u7136\u8df3\u51fa\u4e24\u4eba\uff0c\u4e3a\u9996\u7684\u6b63\u662f\u8d64\u795e\u5b50\u3002 \u3000\u3000\u8d64\u795e\u5b50\u6643\u52a8\u9c9c\u7ea2\u5982\u8840\u7684\u624b\u638c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u54b1\u4eec\u53c8\u78b0\u4e0a\u5566\uff0c\u5510\u7ecf\u5929\u90a3\u81ed\u5c0f\u5b50\u4eca \u65e5\u53ef\u4e0d\u80fd\u518d\u5e87\u62a4\u4f60\u4e86\uff01\u201d\u7eb5\u8eab\u4e00\u8dc3\uff0c\u7acb\u5373\u8df3\u5230\u51b0\u5ddd\u5929\u5973\u8ddf\u524d\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u8fde\u73af\u62cd\u51fa\u3002\u540e\u9762\u90a3 \u4eba\u4e5f\u8ddf\u7740\u4e00\u8dc3\u800c\u4e0b\uff0c\u51b2\u7740\u5e7d\u840d\u5c31\u662f\u4e00\u62f3\uff0c\u5e7d\u840d\u98de\u8eab\u95ea\u907f\uff0c\u4f46\u90a3\u4eba\u62f3\u52bf\u6765\u5f97\u731b\u6781\uff0c\u5e7d\u840d\u521a\u4e00\u95ea \u8eab\uff0c\u62f3\u98ce\u5df2\u5230\u80cc\u540e\u3002 \u3000\u3000\u8fd9\u4eba\u4e43\u662f\u8d64\u795e\u4e8e\u9080\u6765\u7684\u52a9\u624b\uff0c\u540d\u53eb\u8c37\u77f3\u541b\uff0c\u662f\u96c0\u513f\u5c71\u7684\u91ce\u4eba\uff0c\u7ec3\u5c31\u4e00\u8eab\u91d1\u949f\u7f69\u529f\u592b\uff0c\u5200 \u67aa\u4e0d\u5165\uff0c\u4ed6\u4e00\u8eab\u4e4b\u529b\u53ef\u4ee5\u51fb\u6bd9\u731b\u864e\uff0c\u8d64\u795e\u5b50\u5728\u6155\u58eb\u5854\u683c\u5c71\u7684\u7edd\u5cf0\u4e4b\u4e0a\uff0c\u5403\u4e86\u51af\u7433\u7684\u5927\u4e8f\u4e4b\u540e \uff0c\u5fc3\u4e2d\u4e0d\u5fff\uff0c\u4ecd\u60f3\u4e0e\u5510\u7ecf\u5927\u4e3a\u96be\uff0c\u6240\u4ee5\u9080\u4e86\u4ed6\u6765\uff0c\u51c6\u5907\u5bf9\u4ed8\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\uff0c\u4eca\u65e5\u5728\u6b64\u649e \u4e0a\uff0c\u89c1\u5510\u7ecf\u5929\u4e0d\u5728\uff0c\u8d64\u795e\u5b50\u66f4\u662f\u6c14\u7130\u9ad8\u6da8\u3002 \u3000\u3000\u8c37\u77f3\u541b\u4e00\u62f3\u76f4\u51fb\uff0c\u5e7d\u840d\u95ea\u8eab\u4e00\u8dc3\uff0c\u8c37\u77f3\u541b\u624b\u81c2\u4e00\u5f2f\uff0c\u6597\u5927\u7684\u62f3\u5934\u6a2a\u52fe\u4e86\u8fc7\u6765\uff0c\u770b\u8fd9\u62f3\u52bf \u5e7d\u840d\u4e07\u4e07\u8eb2\u95ea\u4e0d\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u6b63\u5728\u62b5\u5fa1\u8d64\u795e\u5b50\u7684\u6025\u88ad\uff0c\u65e0\u6687\u56de\u987e\uff0c\u89c1\u6b64\u60c5\u72b6\uff0c\u53eb\u4e86\u4e00\u58f0\uff1a\u201d \u4e0d\u597d\uff01\u201d\u5ffd\u89c1\u8c37\u77f3\u541b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u627e\u6b7b\u4e48\uff1f\u201d\u539f\u6765\u662f\u90a3\u4e2a\u4e5e\u4e10\uff0c\u4e0d \u77e5\u600e\u7684\uff0c\u5ffd\u7136\u5728\u5730\u4e0b\u4e00\u6eda\uff0c\u6070\u6070\u6eda\u5230\u4e86\u8c37\u77f3\u541b\u4e0e\u5e7d\u840d\u4e4b\u95f4\uff0c\u5c31\u50cf\u4e00\u5757\u77f3\u5934\u4e00\u6837\uff0c\u8c37\u77f3\u541b\u51e0\u4e4e \u7ed9\u4ed6\u7eca\u8dcc\u3002 \u3000\u3000\u8c37\u77f3\u541b\u5927\u6012\uff0c\u63d0\u8d77\u53f3\u8db3\uff0c\u4e00\u811a\u7aef\u4e0b\uff0c\u90a3\u4e5e\u4e10\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u62b1\u5934\u4e00\u6eda\uff0c\u8c37\u77f3\u541b\u8fd9\u4e00\u811a\u5feb \u6377\u5f02\u5e38\uff0c\u7adf\u7136\u6ca1\u6709\u5c06\u4ed6\u745e\u7740\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u9661\u89c1\u773c\u524d\u5bd2\u5149\u8fde\u95ea\uff0c\u601c\u610f\u6c81\u4eba\uff0c\u51b0\u5ddd\u5929\u5973\u8fde\u53d1 \u4e09\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u90fd\u6253\u4e2d\u4e86\u8c37\u77f3\u541b\u7684\u7a74\u9053\u3002 \u3000\u3000\u8c37\u77f3\u541b\u4e00\u8eab\u94dc\u76ae\u94c1\u9aa8\uff0c\u88ab\u5bfb\u5e38\u7684\u6697\u5668\u6253\u4e2d\u7a74\u9053\u81ea\u662f\u65e0\u59a8\uff0c\u4f46\u90a3\u51b0\u9b44\u795e\u5f39\u631f\u7740\u5947\u5bd2\u4e4b\u6c14\uff0c \u4ece\u6bdb\u5b54\u4e4b\u4e2d\u94bb\u5165\uff0c\u8c37\u77f3\u541b\u4e5f\u4e0d\u7e41\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff0c\u51b0\u5ddd\u5929\u5973\u8d81\u6b64\u65f6\u673a\uff0c\u51b0\u5251\u4e00\u5c55\uff0c\u5df2\u5c06\u5e7d\u840d\u62a4 \u4f4f\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e5e\u4e10\u6eda\u5230\u6570\u4e08\u4e4b\u5916\uff0c\u5934\u6795\u4e00\u5757\u5927\u77f3\uff0c\u773c\u775b\u534a\u5f00\u534a\u95ed\uff0c\u61d2\u6d0b\u6d0b\u5730\u770b\u7740\u773c\u524d\u8fd9\u4e00\u573a\u51f6 \u6076\u7684\u6740\uff0c\u8d64\u795e\u5b50\u559d\u9053\uff1a\u201c\u54ea\u4e00\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bc6\u76f8\u70b9\u513f\u3002\u201d\u90a3\u4e5e\u4e10\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u53eb\u9053\uff1a\u201d \u57ce\u95e8\u5931\u706b\uff0c\u6b83\u53ca\u6c60\u9c7c\uff0c\u4e0d\u5999\uff01\u4e0d\u5999\uff01\u201d\u7a81\u7136\u53c8\u662f\u4e00\u6eda\uff0c\u8d64\u795e\u5b50\u8eab\u5f62\u65b9\u8d77\uff0c\u4ed6\u53c8\u5df2\u6eda\u5230\u4e09\u4e08\u5f00 \u5916\uff0c\u6795\u7740\u53e6\u4e00\u5757\u77f3\u5934\uff0c\u4ecd\u7136\u662f\u61d2\u6d0b\u6d0b\u5730\u772f\u7740\u773c\u775b\uff0c\u88c5\u51fa\u4e00\u526f\u6ca1\u4e8b\u4eba\u7684\u95f2\u89c2\u795e\u6c14\u3002\u8d64\u795e\u5b50\u8fd9\u98de \u8eab\u4e00\u6251\uff0c\u672c\u60f3\u5c06\u90a3\u4e5e\u4e10\u4e00\u638c\u51fb\u6bd9\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4e5f\u4e0d\u7981\u5fc3\u4e2d\u51db\u7136\u3002\u6b63\u60f3\u8ffd\u8e2a\uff0c\u518d\u65bd\u6740\u624b\uff0c\u5374\u542c \u5f97\u8c37\u77f3\u541b\u5927\u53eb\u4e00\u58f0\uff0c\u539f\u6765\u4ed6\u53c8\u4e2d\u4e86\u51b0\u5ddd\u5929\u5973\u4e00\u5251\u3002 \u3000\u3000\u8c37\u77f3\u541b\u7684\u786c\u529f\u592b\u5df2\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u4f46\u51b0\u5ddd\u5929\u5973\u7684\u5b9d\u5251\u662f\u4e16\u95f4\u72ec\u4e00\u65e0\u4e8c\u7684\u5b9d\u7269\uff0c\u5979 \u4e0d\u7528\u635f\u4f24\u654c\u4eba\u7684\u76ae\u8089\uff0c\u53ea\u90a3\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u5df2\u4ee4\u4eba\u7981\u53d7\u4e0d\u4f4f\u3002\u8c37\u77f3\u541b\u7684\u5185\u529f\u6765\u5230\u706b\u5019\uff0c\u88ab\u5979\u5728 \u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u523a\u4e09\u5251\uff0c\u4f53\u5185\u7684\u8840\u6db2\uff0c\u90fd\u51e0\u4e4e\u51b7\u5f97\u51dd\u7ed3\uff0c\u7981\u4e0d\u4f4f\u54c7\u54c7\u5927\u53eb\u3002 \u3000\u3000\u8d64\u795e\u5b50\u5f53\u521d\u9080\u8c37\u77f3\u541b\u76f8\u52a9\uff0c\u539f\u662f\u60f3\u7528\u6765\u5bf9\u4ed8\u5510\u7ecf\u5929\uff0c\u4e0d\u60f3\u5510\u7ecf\u5929\u4e0d\u5728\uff0c\u4ed6\u90a3\u4e00\u8eab\u91d1\u949f\u7f69 \u7684\u529f\u592b\uff0c\u5374\u6070\u6070\u88ab\u51b0\u5ddd\u5929\u5973\u7684\u51b0\u5f39\u51b0\u5251\u514b\u5236\uff0c\u5c55\u4e0d\u51fa\u6765\u3002\u8d64\u795e\u5b50\u987e\u4e0d\u5f97\u90a3\u4e2a\u4e5e\u4e10\uff0c\u6025\u6025\u56de\u8f6c \u8eab\u6765\uff0c\u5148\u89e3\u8c37\u77f3\u541b\u4e4b\u56f0\uff0c\u53ea\u89c1\u4ed6\u547c\u547c\u547c\u8fde\u53d1\u4e09\u638c\uff0c\u70ed\u98ce\u56db\u64ad\uff0c\u51b7\u6c14\u5168\u6d88\uff0c\u8c37\u77f3\u541b\u8eab\u4e0a\u6696\u548c\uff0c \u7cbe\u795e\u4e00\u632f\uff0c\u53c8\u518d\u6325\u62f3\u6025\u4e0a\uff0c\u52a9\u53cb\u5f3a\u653b\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5251\u8d70\u8f7b\u7075\uff0c\u5251\u950b\u6307\u5904\uff0c\u5bd2\u5149\u56db\u5c04\uff0c\u8d64\u795e\u5b50\u8fd0\u638c\u6210\u98ce\uff0c\u6bcf\u53d1\u4e00\u638c\uff0c\u4ea6\u662f\u70ed\u6d6a\u88ad\u4eba \u3002\u6b64\u5f80\u5f7c\u6765\uff0c\u51b7\u70ed\u4ea4\u6218\uff0c\u5251\u638c\u4e89\u96c4\uff0c\u8bba\u529f\u529b\u662f\u8d64\u795e\u5b50\u6df1\u539a\u6ca6\u5251\u6cd5\u662f\u51b0\u5ddd\u5929\u5973\u795e\u5947\u3002\u5404\u6709\u64c5\u957f \uff0c\u76f8\u5dee\u65e0\u51e0\uff0c\u4f46\u8c37\u77f3\u541b\u90a3\u4e00\u8eab\u6a2a\u7ec3\u7684\u529f\u592b\uff0c\u5374\u8fdc\u975e\u5e7d\u840d\u6240\u80fd\u62b5\u654c\uff0c\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u51b0\u5ddd\u5929\u5973 \u8fd8\u6ca1\u6709\u4ec0\u4e48\uff0c\u5e7d\u840d\u5374\u5df2\u5a07\u5598\u5401\u5401\uff0c\u9669\u8c61\u56db\u9732\uff0c\u8d64\u795e\u5b50\u4e00\u9635\u5f3a\u653b\uff0c\u9661\u7684\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u4e2a\u201c\u96ea\u82b1 \u76d6\u9876\u201d\uff0c\u62cd\u5411\u51b0\u5ddd\u5929\u5973\u8111\u95e8\u3002\u51b0\u5ddd\u5929\u5973\u8feb\u5f97\u632a\u52a8\u811a\u6b65\uff0c\u56de\u5251\u6a2a\u524a\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u5979\u4e0e\u5e7d\u840d \u4e4b\u95f4\uff0c\u5df2\u662f\u9732\u51fa\u7a7a\u9699\uff0c\u8d64\u795e\u5b50\u5de6\u81c2\u4e00\u6296\uff0c\u9661\u7684\u66b4\u957f\u51e0\u5bf8\uff0c\u5411\u5e7d\u840d\u6402\u5934\u6293\u4e0b\u3002 \u3000\u3000\u5e7d\u840d\u5413\u5f97\u5446\u4e86\uff0c\u5ffd\u89c9\u5c0f\u817f\u51b0\u51c9\uff0c\u6709\u4eba\u5728\u5730\u4e0b\u5c06\u5979\u7684\u5c0f\u817f\u4e00\u62b1\uff0c\u5e7d\u840d\u4e00\u4e2a\u5012\u683d\u8471\u5411\u540e\u76f4\u8dcc \uff0c\u88ab\u90a3\u4eba\u63a8\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5c0f\u817f\u4e0a\u6e7f\u6da9\u6da9\u7684\uff0c\u5370\u7740\u4e24\u4e2a\u5927\u638c\u5370\uff0c\u90a3\u75af\u4e10\u6b63\u6a2a\u5367 \u8def\u4e2d\uff0c\u4e24\u8fb9\u6eda\u52a8\u3002\u62b1\u5979\u5c0f\u817f\u7684\u4eba\uff0c\u4e0d\u662f\u8fd9\u75af\u4e10\u8fd8\u6709\u8c01\uff1f\u5e7d\u840d\u4e00\u770b\u638c\u5370\uff0c\u60f3\u8d77\u8fd9\u662f\u6ee1\u8eab\u957f\u7740\u6bd2 \u75ae\u7684\u75af\u4e10\u5370\u4e0a\u7684\uff0c\u4e0d\u89c9\u4e00\u9635\u6076\u5fc3\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u5410\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8c37\u77f3\u541b\u6070\u597d\u6325\u62f3\u653b\u4e0a\uff0c\u5ffd\u89c1\u90a3\u75af\u4e10\u53c8\u83ab\u540d\u5176\u5999\u5730\u6eda\u6765\uff0c\u4e0d\u7981\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u81ed\u53eb\u5316 \u662f\u6210\u5fc3\u6df7\u6405\u6765\u7684\uff1f\u201d\u53cc\u811a\u9f50\u8d77\uff0c\u8fde\u73af\u75be\u8e22\uff0c\u90a3\u75af\u4e10\u4ecd\u662f\u61d2\u6d0b\u6d0b\u5730\u772f\u7740\u773c\u775b\uff0c\u5ffd\u5730\u4e00\u4e2a\u9ca4\u9c7c\u6253 \u633a\uff0c\u5750\u4e86\u8d77\u6765\uff0c\u56b7\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5bb6\u7684\u5730\u65b9\u4e48\uff1a\u8001\u4e8e\u559c\u6b22\u5728\u8fd9\u91cc\u7761\u89c9\uff0c\u5929\u5b50\u4e5f\u7ba1\u4e0d\u7740\uff1f\u201d\u201c\u556a \u201d\u7684\u4e00\u53e3\u553e\u6d8e\u5411\u8c37\u77f3\u541b\u5410\u53bb\uff0c\u8c37\u77f3\u541b\u8e22\u4ed6\u4e0d\u4e2d\uff0c\u6015\u4ed6\u7684\u53e3\u6d8e\u98de\u6e85\uff0c\u6025\u5fd9\u5411\u65c1\u659c\u8dc3\uff0c\u5ffd\u542c\u5f97\u8d64 \u795e\u5b50\u53eb\u9053\uff1a\u201c\u8c37\u5144\u5f1f\uff0c\u5c0f\u5fc3\u4e86\uff01\u201d\u53ea\u542c\u5f97\u54e7\u54e7\u58f0\u54cd\uff0c\u8c37\u77f3\u541b\u4e07\u4e07\u60f3\u4e0d\u5230\u8fd9\u75af\u4e10\u7684\u6697\u5668\u7adf\u662f\u6742 \u5728\u53e3\u6d8e\u4e4b\u4e2d\u55b7\u5c04\u51fa\u6765\u3002\u53ea\u89c9\u80a9\u4e0a\u4e00\u9635\u9ebb\u75db\uff0c\u767b\u65f6\u6655\u7729\uff0c\u90a3\u75af\u4e10\u8eab\u624b\u597d\u4e0d\u5feb\u6377\uff0c\u8eab\u5b50\u4ecd\u7136\u5750\u5728 \u5730\u4e0a\uff0c\u53cc\u8db3\u4e00\u4e2a\u76d8\u65cb\u5df2\u6eda\u5230\u8c37\u77f3\u541b\u8ddf\u524d\uff0c\u4f38\u51fa\u94c1\u62d0\uff0c\u559d\u4e00\u58f0\u201c\u7740\u201d\u628a\u8c37\u77f3\u541b\u52fe\u5012\uff0c\u51b0\u5ddd\u5929\u5973 \u5530\u7684\u4e00\u5251\uff0c\u5c06\u4ed6\u523a\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u8d64\u795e\u4e8e\u5185\u5916\u529f\u592b\u90fd\u6709\u6781\u6df1\u539a\u7684\u9020\u8be3\uff0c\u75af\u4e10\u90a3\u4e00\u53e3\u553e\u6d8e\u6697\u5668\uff0c\u5e76\u6ca1\u6709\u5c06\u4ed6\u5c04\u4e2d\uff0c\u5927\u5bb6\u8eab\u6cd5 \u90fd\u5feb\u5230\u6781\u70b9\uff0c\u5c31\u5728\u51b0\u5ddd\u5929\u5973\u5251\u523a\u8c37\u77f3\u541b\u7684\u540c\u65f6\uff0c\u4ed6\u4e0e\u75af\u4e10\u5df2\u78b0\u5728\u4e00\u8d77\uff0c\u8d64\u795e\u5b50\u53cc\u638c\u4e00\u5206\u4e00\u5408 \uff0c\u5c55\u51fa\u6740\u624b\u795e\u62db\uff0c\u4e0a\u627c\u5589\u5499\uff0c\u4e0b\u6293\u80f8\u53e3\uff0c\u90a3\u75af\u4e10\u6a2a\u62d0\u4e00\u52fe\uff0c\u5ffd\u89c9\u70ed\u6c14\u653b\u5fc3\uff0c\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\uff0c \u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e56\u4e56\u4e0d\u5f97\u4e86\uff01\u201d\u88ab\u8d64\u795e\u5b50\u7684\u638c\u950b\u4e00\u5e26\uff0c\u201c\u535c\u901a\u201d\u4e00\u58f0\u8dcc\u5165\u5c71\u6da7\u4e4b\u4e2d\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6025\u5fd9\u4e0a\u524d\u8fce\u654c\uff0c\u8d64\u795e\u5b50\u5ffd\u5730\u9762\u8272\u4e00\u53d8\uff0c\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u4e0d \u63a5\u51b0\u5ddd\u5929\u5973\u7684\u5251\u62db\uff0c\u8dc3\u8fc7\u6570\u4e08\u5bbd\u7684\u5c71\u6da7\u5411\u5c71\u4e0a\u6025\u5954\uff0c\u8fde\u8c37\u77f3\u541b\u7684\u6b7b\u6d3b\u4e5f\u4e0d\u987e\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u5927 \u4e3a\u5947\u602a\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u75af\u4e10\u8d64\u7740\u4e0a\u534a\u8eab\uff0c\u5750\u5728\u5c71\u6da7\u4e2d\u7684\u77f3\u5757\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\u4e00\u4e0b\uff0c\u51b0\u5ddd\u5929 \u5973\u4e00\u773c\u77a5\u53bb\uff0c\u4f4e\u547c\u4e00\u58f0\uff0c\u5446\u5446\u6014\u4e86\uff01 \u3000\u3000\u90a3\u75af\u4e10\u7684\u4e24\u6761\u624b\u81c2\uff0c\u672c\u6765\u662f\u7ed3\u6ee1\u7599\u7629\uff0c\u5f62\u8c8c\u5341\u5206\u96be\u770b\uff1b\u5982\u4eca\u5728\u5c71\u6da7\u4e4b\u4e2d\u4e00\u6d78\uff0c\u4f46\u89c1\u76ae\u5149 \u8089\u6d01\u3002\u76ee\u79c0\u7709\u6e05\uff0c\u867d\u7136\u8fd8\u4e0d\u53ca\u5510\u7ecf\u5929\u90a3\u4e48\u4fca\u6717\u633a\u62d4\uff0c\u5374\u4e5f\u957f\u5f97\u4e0d\u4fd7\uff0c\u51b0\u5ddd\u5929\u5973\u60ca\u8be7\u4e4b\u6781\uff0c\u4e00 \u65f6\u4e4b\u95f4\u8bf4\u4e0d\u51fa\u540e\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5e7d\u840d\u4e00\u58f0\u60ca\u547c\uff0c\u51b0\u5ddd\u5929\u5973\u968f\u7740\u5979\u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u8c37\u77f3\u541b\u7684\u624b\u81c2\u80bf\u5f97\u8c61\u540a \u6876\u4e00\u822c\u5927\u4e0d\uff0c\u9762\u76ee\u7600\u9ed1\uff0c\u808c\u8089\u62bd\u6410\uff0c\u53e3\u662f\u53d1\u51fa\u6a21\u7cca\u7684\u51c4\u5389\u7684\u53eb\u58f0\uff0c\u770b\u90a3\u6837\u5b50\uff0c\u7adf\u50cf\u662f\u7ed9\u6781\u5389 \u5bb3\u7684\u6bd2\u86c7\u54ac\u4f24\u4e00\u6837\uff0c\u53eb\u4e86\u51e0\u58f0\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u5927\u7ffb\uff0c\u5ffd\u7684\u5f20\u53e3\u4e00\u54ac\uff0c\u72e0\u72e0\u7684\u54ac\u7740\u4e00\u64ae\u8349\u6839 \uff0c\u53cc\u624b\u4e71\u6293\u4e71\u6316\uff0c\u663e\u89c1\u96be\u53d7\u4e4b\u6781\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u5fcd\uff0c\u968f\u624b\u6361\u8d77\u4e00\u5757\u77f3\u5b50\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u6253\u5165\u4e86\u4ed6 \u7684\u6b7b\u7a74\u3002 \u3000\u3000\u90a3\u75af\u4e10\u7eb5\u8eab\u5927\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u4fbf\u5b9c\u4e86\u90a3\u8d64\u795e\u5b50\u3002\u6ca1\u6709\u6253\u4e2d\u4ed6\u7684\u8981\u5bb3\uff01\u201d\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201d \u4f60\u662f\u8c01\uff1f\u201d\u90a3\u75af\u4e10\u53cc\u811a\u4e00\u8df3\uff0c\u8dc3\u4e0a\u8349\u5730\uff0c\u62fe\u8d77\u90a3\u6839\u9ed1\u6f06\u6f06\u7684\u94c1\u62d0\u3002\u789f\u789f\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e2a\u795e\u618e \u9b3c\u538c\u7684\u5927\u9ebb\u75af\uff01\u201d\u51b0\u5ddd\u5929\u5973\u535a\u89c8\u7fa4\u4e66\uff0c\u8bb0\u8d77\u6c49\u4eba\u7684\u533b\u4e66\u4e2d\u6709\u8fc7\u8fd9\u4e2a\u75c5\u540d\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60 \u662f\u9ebb\u75af\uff01\u201d\u90a3\u9ebb\u75af\u4e00\u58f0\u4e0d\u54cd\uff0c\u5ffd\u5730\u5c06\u94c1\u62d0\u4e24\u8fb9\u4e00\u626f\uff0c\u90a3\u94c1\u62d0\u7adf\u7136\u662f\u6402\u7a7a\u4e86\u7684\uff0c\u75af\u4e10\u626f\u51fa\u4e00\u628a \u9ed1\u6f06\u53d1\u5149\u7684\u94c1\u5251\uff0c\u5c06\u4e2d\u7a7a\u7684\u94c1\u62d0\u5012\u8f6c\uff0c\u5728\u638c\u5fc3\u4e0b\u4e00\u637a\uff0c\u968f\u5373\u4f38\u624b\u5728\u9762\u4e0a\u4e00\u62b9\uff0c\u5e7d\u840d\u4e00\u58f0\u9a87\u53eb \uff0c\u53ea\u89c1\u90a3\u75af\u4e10\u5728\u77ac\u606f\u4e4b\u95f4\u53c8\u6062\u590d\u539f\u5f62\uff0c\u81c2\u4e0a\u957f\u51fa\u7599\u7629\uff0c\u9762\u4e0a\u73b0\u51fa\u7ea2\u4e91\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u67f3\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u65e2\u5df2\u9732\u51fa\u672c\u6765\u9762\u76ee\uff0c\u4e3a\u4f55\u5f04\u9b3c\u88c5\u795e\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u8fd9\u65f6\u5df2\u7ecf\u770b \u51fa\uff0c\u90a3\u75af\u4e10\u7684\u53ef\u6015\u76f8\u8c8c\uff0c\u4e43\u662f\u6545\u610f\u5f04\u51fa\u6765\u7684\uff0c\u4ed6\u81c2\u4e0a\u7684\u7599\u7629\uff0c\u4e43\u662f\u6697\u8fd0\u5185\u52b2\uff0c\u5c06\u808c\u8089\u8feb\u8d77\uff0c \u5f62\u6210\u4e86\u4e00\u4e2a\u4e2a\u7684\u7ed3\uff0c\u9762\u4e0a\u7684\u7ea2\u4e91\uff0c\u5374\u662f\u67d3\u4e0a\u53bb\u7684\uff0c\u90a3\u836f\u6599\u5c31\u8d2e\u85cf\u5728\u94c1\u62d0\u4e4b\u4e2d\uff0c\u82e5\u975e\u4eb2\u773c\u89c1\u4ed6 \u6d82\u62b9\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u662f\u5047\u88c5\u3002 \u3000\u3000\u90a3\u75af\u4e10\u773c\u5149\u4e00\u626b\uff0c\u5ffd\u5730\u53c8\u7eb5\u58f0\u602a\u7b11\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u672c\u6765\u9762\u76ee\uff1f\u4f60\u77e5\u9053\u6211\u7684\u672c\u6765\u9762\u76ee\u662f\u4ec0\u4e48 \uff1f\u201d\u5411\u524d\u4e00\u8dc3\uff0c\u4fe1\u624b\u4e00\u5251\uff0c\u5c31\u5411\u51b0\u5ddd\u5929\u5973\u5288\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u51b0\u5ddd\u5929\u5973\u610f\u5916\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u75af\u4e10\u4e0d\u7531\u5206\u8bf4\uff0c\u5237\u3001\u5237\u3001\u5237\u4e00\u8fde\u8fdb \u4e86\u4e09\u5f0f\u5251\u62db\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u51cc\u5389\u4e4b\u6781\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u66fe\u89c1\u8fc7\u542c\u8fc7\u65e0\u6570\u602a\u5f02\u4e4b\u4e8b\uff0c\u5374\u4ece\u65e0\u4e00\u4ef6\u6bd4\u5f97 \u4e0a\u4eca\u65e5\u4e4b\u4e8b\u7684\u602a\u5f02\u7edd\u4f26\uff0c\u4ee5\u51b0\u5ddd\u5929\u5973\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u4e5f\u9669\u9669\u8eb2\u907f\u4e0d\u5f00\uff0c\u5e7d\u840d\u53eb\u9053\uff1a\u201c\u516c\u4e3b\u62d4\u5251 \uff01\u201d\u51b0\u5ddd\u5927\u5973\u4e00\u4e2a\u201c\u4e73\u71d5\u7a7f\u5e18\u201d\u907f\u5f00\u4e86\u75af\u4e10\u7684\u56db\u4e94\u4e24\u62db\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u4e00\u4e2a\u56de\u73af\u75be\u524a\uff0c\u90a3\u4e5e\u4e10 \u6253\u4e86\u4e00\u4e2a\u5bd2\u5564\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u8981\u89c1\u8bc6\u4f60\u8fd9\u628a\u5b9d\u5251\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u662f\u4e1d\u6beb\u4e0d\u7f13\uff0c \u5de6\u5251\u53f3\u62d0\uff0c\u4e71\u5288\u4e71\u523a\uff0c\u7ade\u4f3c\u5929\u98ce\u6d77\u96e8\uff0c\u8feb\u4eba\u800c\u6765\uff0c\u6bcf\u4e00\u62db\u90fd\u85cf\u7740\u6781\u590d\u6742\u6781\u5389\u5bb3\u7684\u53d8\u5316\uff0c\u51b0\u5ddd \u5929\u5973\u8feb\u5f97\u5c55\u5f00\u4e2d\u897f\u5408\u58c1\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u5c06\u4ed6\u6321\u4f4f\u3002 \u3000\u3000\u90a3\u75af\u4e10\u8155\u529b\u5947\u5927\uff0c\u51b0\u5ddd\u5929\u5973\u8bd5\u4e86\u51e0\u62db\uff0c\u53ea\u8981\u4e00\u78b0\u7740\u4ed6\u7684\u94c1\u5251\uff0c\u864e\u53e3\u4fbf\u9690\u9690\u53d1\u9ebb\u3002\u51b0\u5ddd\u5929 \u5973\u6296\u64de\u7cbe\u795e\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u4e0d\u4e0e\u4ed6\u7684\u94c1\u5251\u6b63\u9762\u4ea4\u950b\uff0c\u5374\u5c55\u5f00\u4e86\u7edd\u5999\u7684\u7684\u8eab\u6cd5\uff0c\u4e00\u53e3\u51b0\u9b44\u5bd2\u5149\u5251 \u5c31\u50cf\u5316\u6210\u4e86\u6570\u5341\u53e3\u4e00\u822c\uff0c\u4f46\u89c1\u51b7\u6c14\u817e\u7a7a\uff0c\u5bd2\u5149\u531d\u5730\uff0c\u5c06\u654c\u6211\u53cc\u65b9\u90fd\u7b3c\u7f69\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u82e5\u662f\u6b66 \u529f\u7a0d\u900a\u4e4b\u4eba\uff0c\u7eb5\u4e0d\u4e2d\u5251\u53d7\u4f24\uff0c\u4e5f\u4f1a\u51b7\u4e2a\u534a\u6b7b\uff0c\u90a3\u75af\u4e10\u5374\u89c6\u82e5\u65e0\u4e8b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781 \uff01\u7701\u5f97\u53eb\u4eba\u6247\u51c9\u3002\u201d\u4e24\u4eba\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u4ea4\u6362\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u96be\u5206\u4e0a\u4e0b\u3002\u5e7d\u840d\u89c1\u90a3\u75af\u4e10\u7684\u94c1\u5251\u864e \u864e\u751f\u98ce\uff0c\u4e0d\u7981\u4e3a\u4e3b\u4eba\u6697\u6697\u5fe7\u8651\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u9053\uff1a\u201c\u8fd9\u75af\u4e10\u5b9a\u662f\u53e6\u6709\u6765\u7531\uff0c\u6211\u4f55\u82e6\u4e0e\u4ed6\u6b7b\u62fc\uff1f\u201d\u4f7f\u51fa\u8fbe\u6469\u5251\u6cd5\u4e2d\u7684\u795e\u5999\u62db \u6570\uff0c\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u5bd2\u5149\u8d77\u5904\uff0c\u5c06\u90a3\u75af\u4e10\u4e71\u8349\u822c\u7684\u5934\u53d1\u524a\u53bb\u4e86\u4e00\u5927\u7edc\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u90a3\u4e5e \u4e10\u7684\u94c1\u5251\u4e00\u6325\uff0c\u4e5f\u6b63\u597d\u4e0e\u51b0\u9b44\u5bd2\u5149\u78b0\u4e2a\u6b63\u7740\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5b9d\u5251\uff0c\u8131\u624b \u98de\u4e0a\u534a\u7a7a\u3002\u539f\u6765\u90a3\u4e5e\u4e10\u4e5f\u62b1\u7740\u540c\u6837\u7684\u5fc3\u601d\uff0c\u53cc\u65b9\u90fd\u60f3\u7565\u5360\u4e0a\u98ce\u4fbf\u884c\u6536\u624b\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5251\u52bf\u8f83 \u4e3a\u8fc5\u6377\uff0c\u62a2\u5382\u5149\u673a\uff0c\u4f46\u90a3\u75af\u4e10\u5185\u52b2\u8f83\u5f3a\uff0c\u8d81\u52bf\u4e00\u6325\uff0c\u4e5f\u78d5\u98de\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u5b9d\u5251\uff0c\u8bba\u8d77\u6765\u8fd8\u662f \u5404\u4e0d\u8f93\u4e8f\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u52a8\u4f5c\u5982\u7535\uff0c\u5e7d\u840d\u54ea\u770b\u5f97\u6e05\u695a\uff0c\u89c1\u4e3b\u4eba\u7684\u5251\u88ab\u75af\u4e10\u78d5\u98de\uff0c\u4e0d\u7531\u5f97\u9a87\u53eb\u4e00\u58f0\uff0c\u8131\u53e3\u9a82 \u9053\uff1a\u201c\u8d3c\u9ebb\u75af\uff0c\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff0c\u6211\u5bb6\u516c\u4e3b\u600e\u6837\u5bf9\u5f85\u4f60\u6765\uff0c\u4f60\u5374\u6069\u5c06\u4ec7\u62a5\uff01\u201d\u90a3\u75af \u4e10\u6602\u5934\u4e00\u7b11\uff0c\u55e4\u55e4\u58f0\u54cd\uff0c\u4e24\u70b9\u9ec4\u8c46\u822c\u5927\u5c0f\u7684\u9ed1\u70b9\uff0c\u671d\u7740\u5e7d\u840d\u5288\u9762\u800c\u53bb\uff0c\u51b0\u5ddd\u5929\u5973\u5927\u9a87\uff0c\u5251\u5df2 \u843d\u624b\uff0c\u6251\u6551\u65e0\u53ca\uff0c\u5e7d\u840d\u6025\u5fd9\u4f7f\u4e2a\u201c\u9563\u88f9\u85cf\u8eab\u201d\uff0c\u626d\u8170\u95ea\u907f\uff0c\u53ea\u89c9\u4e24\u9b13\u6c81\u51c9\uff0c\u4fe9\u8fb9\u7684\u5934\u53d1\u7ed9\u4ed6 \u5272\u53bb\u4e86\u4e00\u7edc\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7eb5\u8eab\u4e00\u63a5\uff0c\u5c06\u51b0\u9b44\u5bd2\u5149\u5251\u63a5\u5728\u624b\u4e2d\uff0c\u62a4\u7740\u4f8d\u513f\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u5ffd\u89c1\u90a3\u4e5e\u4e10\u545c\u9e23\u54ed \u6ce3\uff0c\u54ed\u5f97\u9e1f\u98de\u733f\u8dc3\uff0c\u5230\u4e86\u540e\u6765\uff0c\u5927\u653e\u60b2\u58f0\uff0c\u95fb\u8005\u5fc3\u9178\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6709 \u4ec0\u4e48\u4f24\u5fc3\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u75af\u4e10\u5c06\u94c1\u5251\u63d2\u5165\u9798\u4e2d\uff0c\u53c8\u6210\u4e86\u4e00\u652f\u94c1\u62d0\u4e00\u62d0\u4e00\u62d0\u5730\u8d70\u5230\u6eaa\u8fb9\uff0c\u638f\u8d77\u5c71\u6da7\u6e05\u6cc9\uff0c\u5728\u9762\u4e0a \u4e00\u62b9\uff0c\u4e00\u5239\u90a3\u95f4\uff0c\u7ea2\u4e91\u5c3d\u9000\uff0c\u7599\u7629\u5168\u6d88\uff0c\u53c8\u53d8\u6210\u4e86\u4e00\u4e2a\u7709\u6e05\u76ee\u79c0\u3001\u5507\u7ea2\u9f7f\u767d\u7684\u5c11\u5e74\u3002\u4ed6\u5411\u51b0 \u5ddd\u5929\u5973\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u4f60\u7834\u4e86\u8a93\u8a00\uff0c\u4f60\u662f\u8fd9\u4e16\u4e0a\u7b2c\u4e00\u4e2a\u4e0d\u8ba8\u538c\u6211\u7684\u4eba\uff0c\u597d\uff0c\u4f60\u4eec\u8d70\u5427\uff01\u201d \u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u6211\u7acb\u8a93\u4e0e\u5929\u4e0b\u6b66\u529f\u9ad8\u5f3a\u4e4b\u4eba\u4f5c\u5bf9\uff0c\u4f60\u4e0e \u6211\u6253\u6210\u5e73\u624b\uff0c\u672c\u6765\u6211\u8981\u4e0e\u4f60\u518d\u51b3\u80dc\u8d1f\uff0c\u73b0\u5728\u7b97\u5566\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u4e0d\u8ba8\u538c\u6211\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u9664\u6211 \u4e4b\u5916\uff0c\u4e5f\u4e0d\u89c1\u5f97\u4eba\u4eba\u90fd\u8ba8\u538c\u4f60\u3002\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u9664\u975e\u662f\u5415\u56db\u5a18\u8fd8\u5728\u4eba\u95f4\u3002\u6211\u5e08\u7236\u8bf4\uff0c\u8fd9\u4e16\u4e0a \u5c31\u53ea\u5415\u56db\u5a18\u4e00\u4eba\u4e0d\u8ba8\u538c\u9ebb\u75af\u3002\u201d\u51b0\u5ddd\u5929\u5973\u66fe\u542c\u7236\u4eb2\u8bf4\u8fc7\u5415\u56db\u5a18\u7684\u540d\u5b57\uff0c\u77e5\u9053\u5979\u662f\u5f53\u4eca\u5929\u4e0b\u7684 \u7b2c\u4e00\u9ad8\u624b\uff0c\u4f46\u5374\u4e0d\u660e\u5415\u56db\u5a18\u600e\u5730\u4e0e\u8fd9\u75af\u4e10\u626f\u4e86\u5173\u7cfb\uff0c\u5947\u800c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u77e5\u5979\u4e0d\u8ba8\u538c\u9ebb\u75af\uff0c\u800c \u4e14\uff0c\u4f60\u5b9e\u5728\u4e5f\u4e0d\u662f\u9ebb\u75af\uff01\u201d \u3000\u3000\u90a3\u75af\u4e10\u62b9\u5e72\u773c\u6cea\uff0c\u5ffd\u5730\u53c8\u7eb5\u58f0\u957f\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5e08\u7236\u8bf4\u7684\uff0c\u54ea\u80fd\u6709\u5047\uff1f\u8fd9\u4e16\u4e0a\u5c31\u53ea\u5979\u4e00\u4eba \u4e0d\u8ba8\u538c\u9ebb\u75af\uff0c\u4e0d\uff0c\u73b0\u5728\u8fde\u4e0a\u4e86\u4f60\uff0c\u6709\u4e24\u4e2a\u4eba\u5566\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u660e\u660e\u4e0d\u662f\u9ebb\u75af\uff0c\u4f60\u5e08\u7236 \u96be\u9053\u662f\u9ebb\u75af\u5417\uff1f\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u6211\u4e0e\u6211\u5e08\u7236\u4e00\u822c\uff0c\u82e5\u4e0d\u662f\u6211\u7684\u5e08\u7236\uff0c\u6211\u5c31\u65e9\u88ab\u4e16\u4eba\u629b\u5f03\uff0c\u6b7b \u5728\u8def\u65c1\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u4e00\u8be7\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u533b\u4e66\u4e0a\u8bf4\uff0c\u9ebb\u75af\u65e0\u6cd5\u53ef\u6cbb\uff0c\u542c\u8fd9\u4eba\u53e3\u6c14\uff0c\u53c8\u5374\u50cf \u4ed6\u5e08\u5f92\u672c\u6765\u662f\u4e2a\u9ebb\u75af\uff0c\u540e\u6765\u533b\u597d\u4e86\u7684\u3002\u597d\u5947\u4e4b\u5fc3\u4e00\u8d77\uff0c\u4e0d\u80af\u653e\u4ed6\u4fbf\u8d70\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f \u8c01\uff1f\u201d\u90a3\u75af\u4e10\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u6211\u5e08\u7236\u662f\u8c01\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u4e2a\u9053\u7406 \uff1f\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u4f60\u4e09\u56db\u5c81\u8fd9\u65f6\uff0c\u662f\u5426\u5168\u61c2\u4eba\u4e8b\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u662f\u4e09\u5c81\u4e4b\u65f6\u4fbf\u5165 \u5e08\u95e8\u7684\u3002\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u521a\u5b66\u4f1a\u6ee1\u5c71\u8d70\u4e4b\u65f6\uff0c\u6211\u5e08\u7236\u4fbf\u6b7b\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u70b9\u70b9\u5934\u9053 \uff1a\u201c\u55ef\uff0c\u4f60\u771f\u53ef\u601c\uff01\u201d \u3000\u3000\u90a3\u75af\u4e10\u9762\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4eba\u53ef\u601c\uff01\u201d\u4e3e\u8d77\u94c1\u62d0\uff0c\u4f5c\u52bf\u6b32\u51fb\uff0c\u5ffd\u53c8\u7f13\u7f13\u653e\u4e0b\u3002\u51b0 \u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u5e08\u7236\u2026\u2026\u201d\u5979\u672c\u60f3\u95ee\uff1a\u201c\u4f60\u5e08\u7236\u65e2\u7136\u5728\u4f60\u4e09\u56db\u5c81\u4e4b\u65f6\u4fbf\u6b7b\uff0c\u4f60\u53c8\u4ece\u54ea\u91cc\u5b66\u6765 \u8fd9\u4e00\u8eab\u4e0a\u4e58\u7684\u529f\u592b\uff1f\u201d\u5374\u89c1\u90a3\u75af\u4e10\u53cc\u773c\u5706\u7741\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6211\u4e0d\u8bb8\u53ef\u601c\u9ebb\u75af\u7684\u4eba\u518d\u63d0\u6211\u5e08\u7236 \u7684\u540d\u5b57\uff01\u201d\u5e7d\u840d\u5c0f\u58f0\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6446\u4e86\u6446\u624b\uff0c\u9762\u5411\u90a3\u4e2a\u75af\u4e10\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u8fd9\u53ef\u4ee5\u95ee\u4e86\u5427\uff1f\u201d\u8bf4\u5f97\u751a\u4e3a \u59d4\u5a49\uff0c\u90a3\u75af\u4e10\u770b\u4e86\u51b0\u5ddd\u5929\u5973\u4e00\u773c\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7b2c\u4e00\u4e2a\u80af\u95ee\u6211\u540d\u5b57\u7684\u4eba\u3002\u597d \uff0c\u6211\u5c31\u544a\u8bc9\u4f60\u5427\uff0c\u6211\u53eb\u91d1\u4e16\u9057\uff0c\u8fd9\u540d\u5b57\u662f\u6211\u5e08\u7236\u8d77\u7684\u3002\u201d\u51b0\u5ddd\u5929\u5973\u51b0\u96ea\u806a\u660e\uff0c\u4e00\u542c\u8fd9\u540d\u5b57\uff0c \u4fbf\u77e5\u8fd9\u662f\u201c\u4eca\u4e16\u9057\u201d\u7684\u540c\u97f3\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u771f\u662f\u9ebb\u75af\uff0c\u53c8\u672a\u66fe\u533b\u597d\u7684\u8bdd\uff0c\u7167\u6c49\u4eba\u7684\u4e60\u4fd7\uff0c \u4ed6\u786e\u662f\u8981\u88ab\u4e16\u4eba\u9057\u5f03\u3002 \u3000\u3000\u90a3\u9ebb\u75af\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4ecd\u7136\u51fa\u795e\u7684\u671b\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4e0a\u54ea\u513f\uff1f\u201d\u90a3\u75af\u4e10\u9053\uff1a \u201c\u6211\u559c\u6b22\u4e0a\u54ea\u513f\u4fbf\u4e0a\u54ea\u513f\u3002\u4f60\u4e0a\u54ea\u513f\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u53bb\u5ddd\u897f\u3002\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u90a3\u4e48\uff0c \u6211\u4e5f\u4e0a\u5ddd\u897f\u3002\u4f60\u8ba4\u5f97\u8def\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u867d\u65e0\u610f\u4e0e\u4ed6\u540c\u884c\uff0c\u4f46\u4e0d\u60ef\u8bf4\u8c0e\uff0c\u4f46\u7136\u8bf4\u9053\uff1a\u201c\u95ee\u662f\u95ee \u8fc7\u4e86\u7684\uff0c\u8fc7\u4e86\u6b64\u5c71\uff0c\u6ca1\u6709\u8bb0\u8ba4\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u8d70\u9519\u5566\u3002\u201d\u90a3\u75af\u4e10\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u966a\u4f60\u4e00\u540c\u8d70 \u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5e7d\u840d\u5927\u4e3a\u7740\u6025\uff0c\u7528\u773c\u89d2\u779f\u770b\u4e3b\u4eba\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4e5f\u597d\uff01\u201d\u5979\u5fc3\u5730\u6148\u60b2 \uff0c\u89c1\u90a3\u75af\u4e10\u6124\u4e16\u75be\u4fd7\uff0c\u4e0d\u613f\u4ee4\u4ed6\u8bef\u4f1a\u662f\u81ea\u5df1\u8ba8\u538c\u4ed6\uff0c\u6545\u6b64\u7b54\u5141\u3002\u5e7d\u840d\u9053\uff1a\u201c\u51fa\u4e86\u6b64\u5c71\uff0e\u4fbf\u6709 \u4eba\u70df\uff0c\u5c0f\u516c\u4e3b\uff0c\u54b1\u4eec\u600e\u597d\u4e0e\u4ed6\u540c\u8d70\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u4e00\u7247\u7eaf\u771f\uff0c\u88ab\u5e7d\u840d\u63d0\u9192\uff0c\u8fd9\u624d\u60f3\u8d77\uff0c\u9762\u524d\u8fd9 \u4e2a\u75af\u4e10\uff0c\u8d64\u7740\u4e0a\u8eab\uff0c\u4e0b\u8eab\u7528\u9ebb\u888b\u7f1d\u6210\u7684\u88e4\u5b50\uff0c\u88e4\u7ba1\u4ea6\u5df2\u7834\u70c2\uff0c\u8d70\u5230\u5916\u9762\uff0c\u786e\u662f\u4e0d\u96c5\u3002\u90a3\u75af\u4e10 \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u5acc\u6211\u96be\u770b\u5417\uff1f\u201d\u4e00\u8f6c\u8eab\u7acb\u5373\u5982\u98de\u5954\u8d70\uff0c\u8f6c\u77ac\u4e4b\u95f4\u3001\u6ca1\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u65e0\u539f\u65e0\u6545\uff0c\u53c8\u7ed3\u4e86\u6028\u5566\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u602a\u7269\uff0c\u6211\u77a7\u7740\u4ed6\u4fbf\u89c9 \u80c6\u5bd2\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1b\u201c\u5e78\u4e8f\u6211\u4e0d\u77e5\u9053\u9ebb\u75af\u7684\u75c7\u72b6\uff0c\u82e5\u7136\u77e5\u9053\uff0c\u521d\u521d\u4e00\u89c1\uff0c\u6211\u4e5f\u96be\u514d\u5bb3\u6015\u3002 \u201d\u60f3\u8d77\u8fd9\u9ebb\u75af\u626e\u6210\u75af\u4e10\u7684\u8be1\u5f02\u884c\u4e3a\uff0c\u5fc3\u4e2d\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6e56\u6d77\u98d8\u96f6\u6124\u4fd7\u4e16\uff0c\u5947\u884c\u602a\u8ff9\u60f9\u4eba\u731c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u6d45\u7b11\u8f7b\u98a6\u3000\u82b1\u524d\u8c08\u5f80\u4e8b\u3000\u5170\u56e0\u7d6e\u679c\u3000\u6708\u4e0b\u89c1\u4f0a\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u5341\u4e5d\u56de \u6d45\u7b11\u8f7b\u98a6\u3000\u82b1\u524d\u8c08\u5f80\u4e8b \u5170\u56e0\u7d6e\u679c\u3000\u6708\u4e0b\u89c1\u4f0a\u4eba \u3000\u3000\u96c0\u513f\u5c71\u56db\u5468\u9ad8\u5cf0\uff0c\u72ac\u7259\u4ea4\u9519\uff0c\u4eba\u5728\u5c71\u4e2d\uff0c\u89c6\u754c\u7a84\u72ed\uff0c\u9887\u6709\u4e00\u79cd\u9634\u68ee\u7684\u611f\u89c9\u3002\u8981\u7ffb\u8fc7\u5c71\u9876 \u4e4b\u540e\uff0c\u8fd9\u624d\u8c41\u7136\u5f00\u6717\uff0c\u4fef\u89c6\u7fa4\u5cf0\uff0c\u5c31\u50cf\u62ab\u7740\u96ea\u8863\u4f0f\u5728\u5c71\u4e0b\u7684\u7f8a\u7fa4\u3002\u5e7d\u840d\u7cbe\u795e\u4e00\u632f\uff0c\u62cd\u624b\u7b11\u9053 \uff1a\u201c\u597d\u5728\u54b1\u4eec\u6446\u8131\u4e86\u90a3\u4ee4\u4eba\u8ba8\u538c\u7684\u9ebb\u75af\u3002\u201d\u5c31\u597d\u8c61\u90a3\u201c\u9ebb\u75af\u201d\u82e5\u5728\u8eab\u65c1\uff0c\u8fde\u8fd9\u7f8e\u666f\u4e5f\u4f1a\u88ab\u73b7 \u6c61\u4f3c\u7684\u3002\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u4ed6\u65e2\u4e0d\u662f\u771f\u7684\u9ebb\u75af\uff0c\u53c8\u6ca1\u6709\u4f24\u5bb3\u4e86\u54b1\u4eec\uff0c\u4f60\u4f55\u4ee5\u5bf9\u4ed6\u5982\u6b64\u618e\u6076\uff1f \u201d\u5e7d\u840d\u9053\uff1a\u201c\u6211\u5c31\u662f\u8ba8\u538c\u4ed6\u90a3\u9634\u9633\u602a\u6c14\u7684\u884c\u5f84\uff0c\u4f60\u8bf4\u4ed6\u54ea\u4e00\u70b9\u6bd4\u5f97\u4e0a\u5510\u76f8\u516c\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u542c \u4f8d\u5973\u63d0\u8d77\u5510\u7ecf\u5929\uff0c\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8d70\u4e86\u4e24\u4e2a\u65f6\u8fb0\uff0c\u8d70\u51fa\u5357\u9762\u7684\u5c71\u9698\uff0c\u5c71\u4e0b\u4eba\u5bb6\uff0c\u5df2\u7136\u5728\u671b\uff0c\u5e7d\u840d\u8212\u4e86\u53e3\u6c14\uff0c\u66f4\u662f\u6b22\u559c\uff0c\u7b11 \u9053\uff1a\u201c\u8fd9\u51e0\u65e5\u5c71\u8def\uff0c\u771f\u628a\u6211\u95f7\u6b7b\u5566\u3002\u6574\u5929\u5403\u70e4\u7f8a\u817f\uff0c\u4e5f\u5403\u5f97\u817b\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9065 \u9065\u6307\u9053\uff1a\u201c\u4f60\u77a7\u662f\u8c01\u6765\u4e86\uff1f\u201d\u5e7d\u840d\u4e00\u770b\uff0c\u53ea\u89c1\u534a\u5c71\u8170\u5904\uff0c\u7a81\u7136\u7a9c\u51fa\u4e00\u4eba\uff0c\u7a7f\u7740\u4e00\u8eab\u6574\u6d01\u7684\u9752 \u5e03\u8863\u88f3\uff0c\u957f\u8896\u4e34\u98ce\uff0c\u5934\u4e0a\u675f\u7740\u65b9\u4e2d\uff0c\u4e4d\u773c\u770b\u6765\uff0c\u4f3c\u662f\u4e00\u4e2a\u6ede\u6d12\u4e0d\u7f81\u7684\u4e66\u751f\uff0c\u770b\u771f\u5207\u65f6\uff0c\u7adf\u539f \u6765\u5c31\u662f\u4e2a\u81ea\u79f0\u201c\u91d1\u4e16\u9057\u201d\u7684\u75af\u4e10\u3002 \u3000\u3000\u5e7d\u840d\u6c14\u5f97\u8f6c\u8fc7\u4e86\u8138\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53c8\u56de\u6765\u4e86\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f5b\u8981\u91d1\u88c5 \uff0c\u4eba\u8981\u8863\u88c5\uff0c\u4f60\u65e2\u7136\u5acc\u6211\uff0c\u6211\u5c31\u53ea\u597d\u53bb\u5077\u4e86\u4e00\u8eab\u8863\u88f3\uff0c\u597d\u966a\u4f60\u8d70\u8def\u5440\u3002\u201d\u8bf4\u8bdd\u795e\u6001\uff0c\u751a\u662f\u6ed1 \u7a3d\uff0c\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u4f1a\u505a\u8d3c\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u6211\u8fd8\u5077\u4e86\u53e6\u5916\u7684\u4e1c\u897f\u5462\uff0c\u4f60 \u8981\u4e0d\u8981\uff1f\u201d\u5728\u80cc\u56ca\u4e2d\u53d6\u51fa\u4e00\u4e2a\u7ea2\u6f06\u996d\u76d2\uff0c\u63ed\u5f00\u76d2\u5b50\uff0c\u91cc\u9762\u88c5\u7684\u7ade\u662f\u56db\u5f0f\u7cbe\u7f8e\u7684\u5c0f\u83b1\u3001\u8fd8\u6709\u55b7 \u9999\u7684\u767d\u7c73\u7688\u3002\u51b0\u5ddd\u5929\u5973\u4e00\u7247\u7eaf\u771f\u3002\u5fc3\u65e0\u82a5\u8482\uff0c\u53d6\u8fc7\u6765\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5566\u3002\u201d\u8981\u5206\u4e00\u534a\u7ed9\u5e7d\u840d\uff0c \u5e7d\u840d\u60f3\u8d77\u8fd9\u201c\u9ebb\u75af\u201d\u524d\u51e0\u65e5\u90a3\u6ee1\u8eab\u8113\u75ae\u7684\u4e11\u6076\u6a21\u6837\uff0c\u867d\u7136\u660e\u77e5\u4ed6\u662f\u5047\u88c5\uff0c\u4e5f\u4e0d\u89c9\u6076\u5fc3\u3001\u6447\u6447 \u5934\u9053\uff1a\u201c\u6211\u4e0d\u8981\u3002\u201d\u81ea\u5df1\u6311\u8def\u8fb9\u7684\u91ce\u679c\u5403\u3002\u91d1\u4e16\u9057\u770b\u51b0\u5ddd\u5929\u5973\u6beb\u4e0d\u4ecb\u610f\u7684\u5c06\u996d\u83dc\u5403\u4e86\uff0c\u9732\u51fa \u611f\u6fc0\u7684\u773c\u5149\uff0c\u4e0d\u77e5\u4e0d\u89c9\u6ef4\u51fa\u4e24\u9897\u6cea\u73e0\u3002 \u3000\u3000\u91d1\u4e16\u9057\u966a\u5979\u4eec\u8d70\u4e86\u4e24\u5929\uff0c\u6545\u4f5c\u75af\u72c2\u7684\u7985\u6001\u5df2\u6536\u655b\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u6709\u8bf4\u6709\u7b11\uff0c\u95f2\u65f6\u4e5f\u7ed9\u51b0\u5ddd \u5929\u5973\u8bb2\u4e00\u4e9b\u6c5f\u7322\u4e0a\u7684\u5947\u95fb\u602a\u4e8b\uff0c\u53ea\u662f\u6bcf\u5f53\u51b0\u5ddd\u5929\u5973\u8981\u8bd5\u63a2\u4ed6\u7684\u6765\u5386\u4e4b\u65f6\uff0c\u4ed6\u5c31\u987e\u5de6\u53f3\u800c\u8a00\u4ed6 \u3002\u51b0\u5ddd\u5929\u5973\u4e5f\u5c31\u4e0d\u518d\u591a\u95ee\u3002 \u3000\u3000\u8fd9\u65e5\u5230\u4e86\u96c0\u513f\u5c71\u5357\u9762\u7684\u4e00\u4e2a\u5c0f\u9547\uff0c\u4e09\u4eba\u8d70\u4eba\u9547\u4e2d\uff0c\u5e7d\u840d\u53d1\u73b0\u8def\u4eba\u90fd\u597d\u50cf\u5bf9\u4ed6\u4eec\u6295\u4ee5\u8be7\u5f02 \u7684\u773c\u5149\uff0c\u5fc3\u4e2d\u6781\u4e0d\u8212\u670d\uff0c\u6697\u6697\u57cb\u6028\u516c\u4e3b\u8981\u8fd9\u75af\u4e10\u540c\u884c\uff0c\u91d1\u4e16\u9057\u5ffd\u9053\uff1a\u201d\u8fd9\u91cc\u6709\u6211\u4e00\u4f4d\u670b\u53cb\uff0c \u54b1\u4f55\u53bb\u8bbf\u4e00\u8bbf\u4ed6\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4e0d\u8ba4\u8bc6\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u8bbf\u53cb\u81ea\u4e2a\u513f\u53bb\u3002\u201d\u51b0\u5ddd\u5929\u5973\u597d\u5947 \u5fc3\u8d77\uff0c\u5374\u60f3\u77a7\u77a7\u4ed6\u7684\u670b\u53cb\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u5df2\u540c\u884c\u591a\u65e5\uff0c\u8ba4\u8bc6\u4e00\u4e0b\u4f60\u7684\u670b\u53cb\u4e5f\u662f \u5e94\u8be5\u7684\u3002\u201d\u5e7d\u840d\u6c14\u5f97\u8bf4\u4e0d\u51fa\u6d3b\uff0c\u53ea\u597d\u540c\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u968f\u7740\u91d1\u4e16\u9057\u8d70\uff0c\u8d70\u5230\u4e86\u4e00\u5bb6\u6731\u6f06\u5927\u95e8\u7684\u4eba\u5bb6\uff0c\u91d1\u4e16\u9057\u5524\u4e86\u51e0\u58f0\uff0c\u6ca1\u4eba\u7b54\u5e94\uff0c\u4e5f\u4e0d\u77e5 \u4ed6\u7528\u4ec0\u4e48\u624b\u6cd5\uff0c\u90a3\u95e8\u4e00\u4e0b\u5b50\u5c31\u7ed9\u4ed6\u5f04\u5f00\uff0c\u91cc\u9762\u8d70\u51fa\u4e86\u4e00\u4e2a\u5c11\u5e74\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u8eab\u7a7f\u5bf9\u895f\u63cf\u91d1\u9a6c\u8902\uff0c\u9886\u4e0a\u56f4\u7740\u4e00\u6761\u72d0\u76ae\u62ab\u80a9\uff0c\u4e3e\u6b62\u5b89\u8be6\uff0c\u7684\u786e\u662f\u5927\u5b50\u5f1f\u98ce\u5ea6\u3002\u51b0 \u5ddd\u5929\u5973\u6697\u6697\u8be7\u5f02\uff1a\u91d1\u4e16\u9057\u7adf\u6709\u8fd9\u822c\u670b\u53cb\uff0c\u8fd9\u5c11\u5e74\u770b\u4e86\u5979\u4eec\u4e00\u773c\uff0c\u5bf9\u7740\u8fd9\u4e9b\u7a81\u5982\u5176\u6765\u7684\u5ba2\u4eba\u867d \u611f\u8be7\u5f02\uff0c\u5374\u5e76\u4e0d\u73b0\u8bf8\u58f0\u8272\uff0c\u4ed6\u8fce\u7740\u91d1\u4e16\u9057\u53cc\u62f3\u4e00\u62f1\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u77e5\u5144\u53f0\u4f55\u4e8b\u8fc7 \u8bbf\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5403\u4e86\u4e0a\u60ca\uff0c\u6069\u4e0d\u5230\u91d1\u4e16\u9057\u6b64\u6765\u53c8\u662f\u80e1\u95f9\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u6765\u62dc\u59a8\u5510\u4e8c\u5148\u751f\uff0c\u8c01\u8981\u89c1\u4f60\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u4e00\u52a8\uff1a\u5510\u4e8c\u5148\u751f\uff0c\u6b64\u540d\u597d \u719f\u3002\u6b63\u5728\u601d\u7d22\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5e74\u53c8\u8bf4\uff1a\u201c\u5148\u7956\u5df2\u53bb\u4e16\u591a\u5e74\uff0c\u7b49\u4e0d\u53ca\u9601\u4e0b\u4e86\u3002\u201d\u91d1\u4e16\u9057\u8bf4\u3002\u201c\u4ec0 \u4e48\uff0c\u5510\u4e8c\u5148\u751f\u5df2\u53bb\u4e16\u4e86\uff1f\u771f\u53ef\u60dc\u3001\u771f\u53ef\u60dc\u3001\u771f\u53ef\u60dc\u5440\uff01\u55ef\uff0c\u90a3\u4f60\u8fd8\u6709\u4ec0\u4e48\u957f\u8f88\uff1f\u201d\u90a3\u5c11\u5e74\u9053 \uff1a\u201c\u6211\u7956\u7236\u4f2f\u53d4\u5747\u5df2\u5f03\u4e16\uff0c\u65e0\u4eba\u62db\u5f85\u4f60\u4e86\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u957f\u4e00\u8f88\u7684\u7537\u7537\u5973\u5973 \u90fd\u6b7b\u7edd\u4e86\u5417\uff1f\u201d\u90a3\u5c11\u5e74\u867d\u6709\u6559\u517b\uff0c\u81f3\u6b64\u4ea6\u4e0d\u7981\u6120\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u957f\u4e00\u8f88\u7684\u53ea\u6709\u59d1\u59d1\u8fd8\u5728\uff0c\u5979 \u5e74\u8001\u591a\u75c5\uff0c\u5df2\u6709\u597d\u51e0\u5e74\u8db3\u4e0d\u51fa\u6237\u4e86\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u4f60\u59d1\u59d1\u51fa\u6765\uff01\u201d\u90a3\u5c11\u5e74\u60f3\u4e0d \u5230\u91d1\u4e16\u9057\u5982\u6b64\u4e0d\u901a\u60c5\u7406\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u524d\u5e74\u5192\u5ddd\u751f\u8001\u4f2f\u6765\u63a2\u671b\u59d1\u59d1\uff0c\u59d1\u59d1\u4e5f\u6ca1\u6709\u51fa\u8fce\u3002\u5979\u5b9e \u662f\u5e74\u8001\u591a\u75c5\uff0c\u5e76\u975e\u6709\u610f\u6162\u5ba2\u3002\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\uff0c\u8bf7\u4e88\u8d50\u793a\uff0c\u5f85\u5728\u4e0b\u8f6c\u7980\u59d1\u59d1\uff0c\u8bf4\u4f60\u6765\u8fc7\u4fbf\u662f\u3002 \u5c0f\u5f1f\u4e0d\u8fdc\u9001\u4e86\u3002\u201d\u53cc\u62f3\u4e00\u62f1\uff0c\u6446\u51fa\u4e86\u9001\u5ba2\u7684\u59ff\u6001\u3002 \u3000\u3000\u51b0\u5ddd\u5927\u5973\u5fc3\u4e2d\u4e00\u51db\uff0c\u5c11\u5e74\u6240\u8bf4\u7684\u5192\u5ddd\u751f\u6b63\u662f\u5979\u8981\u5bfb\u8bbf\u7684\u4f2f\u4f2f\uff0c\u539f\u6765\u7adf\u662f\u4e0e\u4ed6\u4eec\u8fd9\u5bb6\u76f8\u719f \u7684\u3002\u987b\u77e5\u5192\u5ddd\u751f\u4e43\u662f\u4e2d\u539f\u7684\u6b66\u6797\u9886\u8896\uff0c\u8fd9\u5c11\u5e74\u7684\u8bed\u610f\u8bf4\u5f97\u5341\u5206\u660e\u767d\uff0c\u8bd5\u60f3\u50cf\u5192\u5ddd\u751f\u90a3\u6837\u7684\u6b66 \u6797\u540d\u5bbf\u5230\u6765\uff0c\u4ed6\u59d1\u59d1\u5c1a\u4e0d\u8fce\u63a5\uff0c\u91d1\u4e16\u9057\u767b\u95e8\u6c42\u89c1\uff0c\u5c82\u975e\u592a\u4e0d\u81ea\u91cf\uff1f \u3000\u3000\u53ea\u89c1\u91d1\u4e16\u9057\u9762\u8272\u4e00\u53d8\u9053\uff1a\u201c\u4f60\u662f\u8981\u9010\u5ba2\u4e86\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u8bf7\u8c05\uff01\u201d\u53cc\u624b \u5f20\u5f00\u4ecd\u7136\u6446\u51fa\u9001\u5ba2\u7684\u59ff\u6001\u3002\u91d1\u4e16\u9057\u6840\u6840\u4e00\u7b11\uff0c\u7a81\u7136\u4f38\u624b\u5728\u9762\u4e0a\u4e00\u62b9\uff0c\u90a3\u5c11\u5e74\u9aa4\u89c1\u91d1\u4e16\u9057\u7684\u9762 \u7a81\u7136\u53d8\u5f97\u6d6e\u80bf\uff0c\u73b0\u51fa\u7ea2\u4e91\uff0c\u624b\u81c2\u4e0a\u53c8\u957f\u51fa\u7599\u7629\uff0c\u4e0d\u7531\u5f97\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff01\u201d\u91d1\u4e16\u9057 \u201c\u5478\u201d\u7684\u4e00\u53e3\u553e\u6d8e\uff0c\u5410\u5728\u90a3\u5c11\u5e74\u7684\u534e\u670d\u4e0a\uff0c\u53cc\u624b\u4e00\u9001\uff0c\u628a\u90a3\u5c11\u5e74\u91cd\u91cd\u7684\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u54c8\u54c8 \u5927\u7b11\u9053\uff1a\u201c\u4f60\u8981\u9001\u5ba2\uff0c\u6211\u504f\u4e0d\u8d70\uff0c\u5510\u8001\u592a\u5a46\uff0c\u770b\u4f60\u51fa\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u58f0\u97f3\u9053\uff1a\u201c\u597d\u672c\u4e8b\uff0c\u597d\u672c\u4e8b\uff01\u201d\u4e00\u4e2a\u767d\u53d1\u8427\u8427\u7684\u8001\u592a\u5a46\u6276\u7740\u5973\u4ec6\u7684\u80a9\u5934 \u98a4\u5dcd\u8d70\u4e0b\u5ead\u9636\uff0c\u90a3\u5c11\u5e74\u5728\u5730\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\u6076\u4e10\uff0c\u4ed6\u4e00\u5b9a\u8981\u89c1\u59d1\u59d1\u3002\u201d\u90a3\u8001\u5a46 \u5a46\u9053\uff1a\u201c\u5bf9\u4ed8\u6076\u72d7\uff0c\u8be5\u5f53\u5982\u4f55\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u53d6\u6211\u7684\u5f39\u5f13\u6765\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u795e\u6001\u5b8c\u5168\u53d8\u4e86 \uff0c\u4e00\u4e2a\u770b\u4f3c\u4f53\u8870\u529b\u5f31\u7684\u8001\u5a46\u5a46\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u53d8\u5f97\u82f1\u6c14\u8feb\u4eba\u3002\u53ea\u89c1\u5979\u5728\u5973\u4ec6\u624b\u4e2d\u63a5\u8fc7\u5f39\u5f13\uff0c\u53f3 \u624b\u5982\u6258\u6cf0\u5c71\uff0c\u5de6\u624b\u5982\u62b1\u5a74\u513f\uff0c\u5f13\u5f26\u4e00\u5f20\uff0c\u5506\u5506\u8fde\u58f0\uff0c\u5f39\u4e38\u75be\u53d1\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u7ec8\u4e8e\u89c1\u7740\u4f60\u4eec\u5bb6\u7684\u6697\u5668\u4e86\uff01\u201d\u7a81\u7136\u4e00\u4e2a\u7b4b\u6597\uff0c\u5728\u5730\u4e0a\u6253\u4e00\u4e2a \u98ce\u8f66\uff0c\u90a3\u6839\u94c1\u62d0\uff0c\u968f\u7740\u4ed6\u7684\u8eab\u5f62\uff0c\u4e5f\u821e\u5f97\u547c\u547c\u98ce\u54cd\u3002\u51b0\u5ddd\u5929\u5973\u770b\u5f97\u4e0d\u7981\u9a87\u7136\uff0c\u8fd9\u8001\u5a46\u5a46\u7684\u5f39 \u4e38\u6253\u5f97\u53c8\u72e0\u53c8\u51c6\uff0c\u5341\u4e8c\u9897\u5f39\u4e38\uff0c\u9897\u9897\u65b9\u5411\u4e0d\u540c\uff0c\u6709\u7684\u659c\u98de\uff0c\u6709\u7684\u76f4\u5c04\uff0c\u6709\u7684\u6253\u8fc7\u4e86\u5934\u4e0e\u53e6\u4e00 \u7c92\u5f39\u5b50\u4e00\u649e\uff0c\u53c8\u6298\u5c04\u56de\u6765\uff0c\u770b\u4f3c\u51cc\u4e71\uff0c\u5374\u662f\u6bcf\u4e00\u9897\u5f39\u4e38\uff0c\u90fd\u5954\u5411\u4eba\u8eab\u4e00\u5904\u5927\u7a74\uff0c\u8fd9\u79cd\u53d1\u6697\u5668 \u7684\u624b\u6cd5\uff0c\u771f\u662f\u6b66\u6797\u7f55\u89c1\uff0c\u4e16\u4e0a\u65e0\u53cc\u3002\u91d1\u4e16\u9057\u597d\u50cf\u65e9\u6709\u51c6\u5907\uff0c\u6210\u7af9\u5728\u80f8\uff0c\u90a3\u4e00\u4e2a\u7b4b\u6597\u6253\u5f97\u5999\u5230 \u6beb\u5dc5\uff0c\u4e0a\u4e0b\u7a74\u9053\u98a0\u5012\uff0c\u5c06\u90a3\u98de\u5f39\u88ad\u7a74\u7684\u51cc\u5389\u529f\u52bf\u9690\u9690\u5316\u89e3\u3002\u53ea\u542c\u5f97\u4e00\u9635\u53ee\u53ee\u54e8\u5417\u7684\u7e41\u97f3\u5bc6\u54cd \uff0c\u706b\u661f\u56db\u6e85\uff0c\u5341\u4e8c\u9897\u5f39\u4e38\u90fd\u7ed9\u94c1\u62d0\u9707\u98de\uff0c\u4f46\u91d1\u4e16\u9057\u90a3\u6839\u94c1\u62d0\u4e5f\u7ed9\u90a3\u4e9b\u5f39\u4e38\u6253\u5f97\u4f3c\u7edb\u7a9d\u4e00\u6837\uff0c \u70b9\u70b9\u6591\u6591\uff0c\u91d1\u4e16\u9057\u4e0d\u7531\u5f97\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u522b\u770b\u8fd9\u8001\u5a46\u5a46\u5e74\u8fc8\u82cd\u82cd\uff0c\u5185\u52b2\u4e4b\u5f3a\uff0c\u7edd\u4e0d\u5728\u4ed6\u4e4b \u4e0b\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9053\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u77e5\u81ea\u7231\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u5f39\u4e38\u98de\u51fa\uff0c\u5374\u662f\u6084\u65e0 \u58f0\u54cd\uff0c\u6bcf\u4e09\u9897\u4e00\u7ec4\uff0c\u5217\u6210\u54c1\u5b57\uff0c\u56db\u7ec4\u5f39\u4e38\uff0c\u5206\u5411\u56db\u65b9\u98de\u6765\uff0c\u7adf\u50cf\u5979\u662f\u4ece\u56db\u4e2a\u4e0d\u540c\u7684\u65b9\u5411\u6240\u53d1 \u3002\u5f39\u4e38\u5feb\u6162\u4e0d\u4e00\uff0c\u98de\u5230\u8fd1\u8eab\uff0c\u5ffd\u7684\u540e\u5217\u6539\u6210\u524d\u5217\uff0c\u6709\u5982\u51b0\u96f9\u4e71\u843d\uff0c\u82b1\u96e8\u88ad\u4eba\u3002\u91d1\u4e16\u9057\u53eb\u9053\uff1a \u201c\u5510\u5bb6\u6697\u5668\uff0c\u786e\u662f\u540d\u4e0d\u865a\u4f20\uff01\u201d\u624b\u8db3\u5e76\u7528\uff0c\u9661\u7684\u53c8\u5728\u5730\u4e0a\u8fde\u7ffb\u4e24\u4e2a\u7b4b\u6597\uff0c\u66ae\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u602a \u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4e5f\u5c1d\u5c1d\u6211\u7684\u6697\u5668\uff01\u201d\u4e00\u4e2a\u7b4b\u6597\u7ffb\u5230\u4e86\u8001\u592a\u5a46\u7684\u9762\u524d\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u53e3\u4fbf\u5410\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5927\u5403\u4e00\u60ca\uff0c\u5979\u770b\u4e86\u8fd9\u8001\u5927\u5a46\u7684\u6697\u5668\u624b\u6cd5\uff0c\u8fd9\u65f6\u5df2\u6479\u7136\u60f3\u8d77\uff0c\u8fd9\u8001\u592a\u5a46\u662f\u5510\u7ecf\u5929 \u66fe\u5bf9\u5979\u8bf4\u8fc7\u7684\u5510\u8d5b\u82b1\uff0c\u4ea6\u5373\u662f\u6570\u5341\u5e74\u524d\u5a01\u9707\u6c5f\u6e56\uff0c\u53f7\u79f0\u5929\u4e0b\u6697\u5668\u7b2c\u4e00\u624b\u7684\u5510\u91d1\u5cf0\u7684\u72ec\u751f\u5973\u513f \u3002\u5510\u91d1\u5cf0\u6392\u884c\u7b2c\u4e8c\uff0c\u4eba\u79f0\u201c\u5510\u4e8c\u5148\u751f\u201d\uff0c\u5f53\u5e74\u4ed6\u4eec\u7236\u5973\u548c\u5510\u7ecf\u5929\u7236\u6bcd\u6709\u8fc7\u4e00\u6bb5\u201c\u6881\u5b50\u201d\uff0c\u540e \u6765\u5f97\u5415\u56db\u5a18\u4e4b\u52a9\uff0c\u624d\u91ca\u5acc\u4fee\u597d\u7684\u3002\u9f99\u7075\u77eb\u662f\u5510\u91d1\u5cf0\u7684\u5173\u95e8\u5f92\u5f1f\uff0c\u4ea6\u5373\u662f\u8fd9\u4e2a\u8001\u592a\u5a46\u7684\u5e08\u5f1f\uff0c \u5510\u7ecf\u5929\u8fd9\u6b21\u987a\u9053\u5165\u5ddd\uff0c\u4e3a\u4e86\u9f99\u7075\u77eb\u4e4b\u4e8b\uff0c\u6b63\u8981\u5bfb\u5979\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u60f3\u8d77\u4e86\u5510\u8d5b\u82b1\u7684\u6765\u5386\uff0c\u89c1\u91d1\u4e16\u9057\u5f20\u53e3\u8981\u5410\u4ed6\u90a3\u72ec\u95e8\u7684\u6b79\u6bd2\u6697\u5668\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00 \u60ca\uff0c\u5f53\u4e0b\u4e0d\u5047\u601d\u7d22\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u6296\u8d77\u4e00\u9053\u51b0\u9b44\u5bd2\u5149\uff0c\u98de\u8eab\u6025\u4e0a\uff0c\u5728\u4e24\u4eba\u4e2d\u95f4\u5de6\u53f3\u4e00\u5206\uff0c\u5bd2\u5149 \u5251\u7684\u5251\u5c16\u76f4\u6307\u5230\u91d1\u4e16\u9057\u80f8\u524d\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u8981\u8feb\u4ed6\u4e0d\u80fd\u4f24\u5bb3\u5510\u8001\u592a\u5a46\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u8f70\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u5510\u8d5b\u82b1\u7684\u5b9d\u5f13\u5df2\u4e0e\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u76f8\u63a5\uff0c\u4e94\u6839\u5f13 \u5f26\u5168\u90fd\u9707\u65ad\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u4e5f\u98de\u4e0a\u534a\u5929\uff0c\u63a5\u7740\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u4e16\u9057\u7684\u8863\u670d\u7ed9\u51b0\u5251\u5272\u5f00\uff0c\u91d1 \u4e16\u9057\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u4e00\u7eb5\u8eab\u63a5\u4e86\u94c1\u62d0\uff0c\u7acb\u523b\u8f6c\u8eab\u98de\u5954\u3002\u51b0\u5ddd\u5929\u5973\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u51f6\u6b8b\u6210 \u6027\u7684\u4e1c\u897f\uff0c\u4ee5\u540e\u6c38\u4e0d\u8981\u518d\u89c1\u6211\u3002\u201d\u91d1\u4e16\u9057\u4e00\u58f0\u4e0d\u54cd\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u8eab\u5f62\u8d8a\u8fc7\u5899\u5934\uff0c\u98de\u51fa\u56ed\u5916\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u7247\u832b\u7136\uff0c\u770b\u7740\u91d1\u4e16\u9063\u7684\u80cc\u5f71\u4f3c\u60ca\u9e3f\u75be\u901d\uff0c\u5bf9\u4ed6\u4e5f\u4e0d\u77e5\u662f\u618e\u6076\u3001\u662f\u60cb\u60dc\uff0c\u8fd8\u662f \u540c\u60c5\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u5c06\u65ad\u4e86\u5f26\u7684\u94c1\u5f13\u63b7\u4e8e\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u597d\u6f02\u4eae\u7684\u5c0f\u59d1\u5a18\uff0c\u4f60\u548c\u4ed6\u4e0d\u662f\u4e00\u8def\u7684\u5417\uff1f\u201d\u51b0 \u5ddd\u5929\u5973\u9053\uff1a\u201c\u5192\u5ddd\u751f\u6b63\u662f\u5bb6\u4f2f\u3002\u201d\u5510\u8d5b\u82b1\u9887\u611f\u60ca\u5947\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u662f\u5192\u5ddd\u751f\u7684\u4f84\u5973\u513f\uff1f\u4f60\u600e \u7684\u4f1a\u4e0e\u8fd9\u75af\u4e10\u5728\u4e00\u8d77\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\u5bf9\u90a3\u75af\u4e10\uff0c\u4f3c\u4e4e\u9732\u51fa\u6781\u5ea6\u9119\u5937\u7684\u795e\u8272\uff0c\u51b0\u5ddd\u5929\u5973\u867d\u7136\u5e76\u4e0d \u628a\u91d1\u4e16\u9057\u8ba4\u4e3a\u670b\u53cb\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u5bf9\u5510\u8d5b\u82b1\u8bf4\u8bdd\u7684\u795e\u6c14\uff0c\u611f\u5230\u751a\u4e0d\u8212\u670d\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8def \u4e0a\u78b0\u5230\u7684\u3002\u201d\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u5510\u8d5b\u82b1\u8138\u4e0a\u9690\u9690\u7b3c\u7f69\u7740\u4e00\u5c42\u9ed1\u6c14\uff0c\u60ca\u53eb\u9053\uff1a\u201c\u5510\u4f2f\u6bcd\uff0c\u4f60\u4e2d\u4e86\u4ed6 \u7684\u6697\u5668\u4e86\uff01\u201d\u60f3\u8d77\u91d1\u4e16\u9057\u6697\u5668\u7684\u6b79\u6bd2\uff0c\u6bdb\u9aa8\u8038\u7136\uff0c\u5bf9\u91d1\u4e16\u9057\u7684\u540c\u60c5\u5316\u4e3a\u4e4c\u6709\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201d \u771f\u60f3\u4e0d\u5230\u4ed6\u662f\u9022\u4eba\u4fbf\u54ac\u7684\u6076\u72d7\uff01\u201d \u3000\u3000\u5510\u8d5b\u82b1\u51b7\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4f2f\u6bcd \uff0c\u4f60\u8981\u4e0d\u8981\u8bd5\u670d\u6211\u7684\u89e3\u6bd2\u6563\uff1f\u201d\u90a3\u5c11\u5e74\u5bf9\u51b0\u5ddd\u5929\u5973\u751a\u662f\u597d\u611f\uff0c\u65e9\u6328\u8fd1\u4e86\u6765\uff0c\u8fd9\u65f6\u624d\u6709\u673a\u4f1a\u63d2 \u53e3\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u771f\u591a\u8c22\u4f60\u4e86\uff01\u5e78\u5f97\u4f60\u5c06\u4ed6\u9010\u8d70\u3002\u4f60\u6709\u89e3\u4ed6\u6697\u5668\u7684\u7075\u836f\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201d \u90a3\u662f\u6211\u81ea\u5df1\u914d\u5236\u7684\uff0c\u6bd4\u4e0d\u4e0a\u5929\u5c71\u96ea\u83b2\uff0c\u4f46\u5bf9\u4ed8\u4e00\u822c\u6bd2\u836f\u8fd8\u5f88\u6709\u6548\uff0c\u5bf9\u8fd9\u53ae\u7684\u6b79\u6bd2\u6697\u5668\uff0c\u5374\u4e0d \u77e5\u6210\u4e0e\u4e0d\u6210\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u957f\u5904\u51b0\u5bab\uff0c\u4e0d\u77e5\u4eba\u95f4\u4e16\u6545\uff0c\u65e2\u4e0d\u4ee5\u5c0f\u8f88\u4e4b\u793c\u4e0e\u5510\u8d5b\u82b1\u76f8\u89c1\uff0c\u5bf9\u90a3\u5c11\u5e74\u7684\u9053\u8c22\u53c8 \u4e0d\u77e5\u8c26\u8ba9\uff0c\u66f4\u517c\u5979\u90a3\u4e0e\u751f\u4ff1\u6765\uff0c\u81ea\u7136\u5e26\u7740\u7684\u4e00\u526f\u9ad8\u50b2\u7684\u795e\u60c5\u3001\u5510\u8d5b\u82b1\u5fc3\u4e2d\u4ea6\u662f\u751a\u4e0d\u9ad8\u5174\uff0c\u51b0 \u5ddd\u5929\u5973\u4e0d\u77e5\u522b\u4eba\u5bf9\u5979\u8bef\u4f1a\uff0c\u6b63\u60f3\u638f\u51fa\u836f\u6765\uff0c\u5510\u8d5b\u82b1\u53cc\u773c\u671d\u5929\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u3002\u201d\u90a3\u5c11\u5e74 \u9053\uff1a\u201c\u59d1\u59d1\uff0c\u8bd5\u8bd5\u4e5f\u597d\u3002\u201d\u5510\u8d5b\u82b1\u53cc\u773c\u4e00\u7741\uff0c\u9053\uff1a\u201c\u7aef\u513f\uff0c\u54b1\u4eec\u5510\u5bb6\u7684\u6697\u5668\u4ece\u65e0\u7a7a\u53d1\uff0c\u6709\u4e9b \u5b64\u964b\u5be1\u95fb\u7684\u5916\u4eba\u6216\u8bb8\u4e0d\u77e5\u9053\uff0c\u4f60\u96be\u9053\u4e5f\u4e0d\u77e5\u5417\uff1f\u4e09\u5929\u4e4b\u5185\uff0c\u5305\u7ba1\u90a3\u75af\u4e10\u8981\u5c06\u89e3\u836f\u4e56\u4e56\u7684\u9001\u6765 \uff0c\u4e0e\u6211\u4ea4\u6362\u3002\u4f60\u59d1\u59d1\u867d\u7136\u5e74\u8fc8\uff0c\u8fd9\u4e09\u65e5\u8fd8\u80fd\u633a\u4f4f\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u90a3\u9b54\u5934\u4e2d\u4e86\u4f60\u4ec0\u4e48 \u6697\u5668\uff1f\u201d\u5510\u8d5b\u82b1\u9053\uff1a\u5b57\u4e09\u65e5\u4e4b\u5185\u53d1\u4f5c\uff0c\u4e03\u65e5\u4e4b\u5185\u6bd9\u547d\u7684\u767d\u7709\u9488\uff01\u201d\u51b0\u5ddd\u5929\u5973\u89c1\u5510\u8d5b\u82b1\u8fd9\u6837\u54ac \u7259\u5207\u9f7f\u7684\u795e\u60c5\uff0c\u60f3\u4e24\u4eba\u7684\u6697\u5668\u90fd\u662f\u8fd9\u822c\u6b79\u6bd2\uff0c\u601d\u4e4b\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u5192\u5ddd\u751f\u524d\u5e74\u66fe\u5230\u6211\u5bb6\u4e2d\u6765\u8fc7\uff0c\u73b0\u5728\u9752\u57ce\u5c71\u9690\u5c45\u3002\u4ed6\u662f\u4e00\u4ee3\u540d\u5bbf\uff0c\u602a\u4e0d\u5f97\u4f60 \u8fd9\u6837\u9ad8\u660e\uff0c\u6211\u8001\u5a46\u5b50\u4e00\u6765\u662f\u8d70\u4e0d\u52a8\u4e86\uff0c\u4e8c\u6765\u662f\u6015\u522b\u4eba\u8bf4\u6211\u5949\u627f\uff0c\u6055\u6211\u4e0d\u9886\u4f60\u53bb\u627e\u4f60\u7684\u4f2f\u4f2f\u4e86 \u3002\u201d\u8bdd\u4e2d\u9690\u6709\u9001\u5ba2\u4e4b\u610f\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u4f2f\u6bcd\uff0c\u6211\u81ea\u5df1\u4f1a\u53bb\u3002\u4f46\u6709\u4e00\u4e8b\u5374\u8981\u7980\u660e\u4f2f \u6bcd\u3002\u9f99\u7075\u77eb\u5728\u62c9\u8428\u4e0b\u72f1\uff1b\u6b64\u4e8b\u4e0d\u77e5\u4f60\u4eec\u77e5\u9053\u4e0d\u9053\uff1f\u201d \u3000\u3000\u5510\u8d5b\u82b1\u773c\u76ae\u4e00\u7ffb\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u9f99\u7075\u77eb\u5728\u62c9\u8428\u88ab\u4eba\u6349\u4e86\uff1f\u201d\u8981\u77e5\u5510\u8d5b\u82b1\u4e00\u751f\u65e0\u5b50\uff0c\u9f99 \u7075\u77eb\u5165\u5510\u5bb6\u4e4b\u65f6\uff0c\u53ea\u6709\u4e03\u5c81\uff0c\u540d\u4e49\u4e0a\u867d\u662f\u5510\u8d5b\u82b1\u7684\u5e08\u5f1f\uff0c\u5510\u8d5b\u82b1\u5b9e\u5219\u5c06\u4ed6\u5f53\u4f5c\u513f\u5b50\u770b\u5f85\u3002\u201d \u5c06\u4ed6\u629a\u517b\u6210\u4eba\uff0c\u6545\u6b64\u5206\u5916\u5173\u6000\u3002\u51b0\u5ddd\u5929\u5973\u5c06\u9f99\u7075\u77eb\u4e0b\u72f1\u4e4b\u4e8b\u7b80\u5355\u7684\u8bf4\u4e86\u4e00\u904d\uff0c\u5510\u8d5b\u82b1\u201c\u54fc\u201d \u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u798f\u5eb7\u5b89\u4e0e\u8d64\u795e\u5b50\u6709\u8fd9\u4e48\u5927\u80c6\uff0c\u54fc\uff0c\u770b\u6765\u4ed6\u4eec\u662f\u4e0d\u8bb8\u6211\u8fd9\u8001\u5a46\u5b50\u5b89\u5b89\u4efd\u4efd\u5730\u5b88 \u5728\u5bb6\u4e2d\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u522b\u52a8\u6c14\uff0c\u517b\u597d\u4e86\u4f24\u518d\u8bf4\u3002\u201d\u5510\u8d5b\u82b1\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\u3002 \u4f8d\u513f\u6276\u6211\u56de\u53bb\u3002\u201d\u4e0d\u7406\u51b0\u5ddd\u5929\u5973\uff0c\u5f84\u81ea\u8d70\u8fdb\u5c4b\u5185\u53bb\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u54ea\u66fe\u53d7\u8fc7\u5982\u6b64\u51b7\u6de1\uff0c\u5bf9\u5e7d\u840d\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\u3002\u201d\u90a3\u5c11\u5e74\u6025\u5fd9\u4e0a\u524d\u65bd\u4e86\u4e00\u793c\uff0c\u9053 \uff1a\u201c\u6211\u59d1\u59d1\u5e74\u8001\u7cca\u6d82\uff0c\u4f60\u4e0d\u8981\u89c1\u602a\u3002\u4ee4\u5c0a\u662f\u77f3\u5927\u4fa0\u8fd8\u662f\u6842\u5927\u4fa0\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5bb6\u7236\u6392\u884c \u7b2c\u4e09\uff0c\u540d\u5b57\u4e0a\u534e\u4e0b\u751f\u3002\u201d\u90a3\u5c11\u5e74\u542c\u8bf4\u5979\u662f\u6842\u534e\u751f\u7684\u5973\u513f\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6842 \u59d0\u59d0\uff0c\u6211\u53eb\u5510\u7aef\uff0c\u8bf7\u6842\u59d0\u59d0\u5ff5\u5728\u6211\u59d1\u59d1\u65e0\u4eba\u4fdd\u62a4\uff0c\u5c48\u9a7e\u591a\u7559\u4e24\u65e5\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u59d1\u59d1 \u4e0d\u662f\u7528\u767d\u7709\u9488\u5c06\u90a3\u2018\u75af\u4e10\u2019\u4f24\u4e86\uff0c\u73b0\u4e0b\u53ea\u5f85\u4ed6\u6765\u4ea4\u6362\u89e3\u836f\u5417\uff1f\u6211\u672c\u4e8b\u4f4e\u5fae\uff0c\u600e\u80fd\u4fdd\u62a4\u4f60\u7684\u59d1 \u59d1\uff1f\u201d\u5510\u7aef\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u59d1\u59d1\u8fc7\u4e8e\u81ea\u4fe1\uff0c\u600e\u77e5\u90a3\u75af\u4e10\u5728\u4e09\u65e5\u4e4b\u5185\u6765\u662f\u4e0d\u6765\uff1f\u800c\u4e14\u82e5\u7136\u4ed6\u4e0d \u77e5\u81ea\u7709\u9488\u7684\u5389\u5bb3\uff0c\u4e0d\u80af\u4ea4\u6362\uff0c\u4e09\u65e5\u4e4b\u5185\uff0c\u524d\u6765\u884c\u51f6\uff0c\u90a3\u53c8\u6709\u4f55\u4eba\u80fd\u591f\u62b5\u6321\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u4e00\u60f3 \uff0c\u5510\u7aef\u7684\u8bf4\u8bdd\u679c\u7136\u5e76\u975e\u591a\u8651\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u8001\u5a46\u5b50\u867d\u7136\u65e0\u793c\uff0c\u5230\u5e95\u662f\u4f4d\u524d\u8f88\uff0c\u6211\u82e5\u5c31\u6b64\u8d70\u5f00\uff0c \u5979\u6709\u4e09\u957f\u4e24\u77ed\uff0c\u6211\u826f\u5fc3\u4e0a\u4e5f\u8bf4\u4e0d\u8fc7\u53bb\u3002\u201d\u6148\u60b2\u4e4b\u5ff5\u4e00\u8d77\uff0c\u4fbf\u7b54\u5e94\u5728\u5510\u5bb6\u7559\u4e0b\u3002 \u3000\u3000\u8f6c\u77ac\u8fc7\u4e86\u4e09\u65e5\uff0c\u5510\u8d5b\u82b1\u628a\u81ea\u5df1\u5173\u5728\u9759\u5ba4\u4e2d\uff0c\u9759\u5750\u5fa1\u6bd2\uff0c\u8db3\u4e0d\u51fa\u6237\uff0c\u51b0\u5ddd\u5929\u5973\u89c1\u5510\u7aef\u65e5\u589e \u6101\u70e6\uff0c\u5fc3\u4e2d\u4ea6\u662f\u60f4\u60f4\u4e0d\u5b89\u3002\u60f3\u90a3\u91d1\u4e16\u9057\u867d\u56e0\u6124\u4e16\u5ac9\u4fd7\uff0c\u4e13\u4e0e\u6210\u540d\u7684\u6b66\u6797\u4eba\u7269\u4f5c\u5bf9\uff0c\u4f46\u7528\u8fd9\u79cd \u6b79\u6bd2\u7684\u6697\u5668\u4f24\u5bb3\u4e00\u4e2a\u8001\u5a46\u5a46\uff0c\u603b\u662f\u4e0d\u80fd\u539f\u8c05\u3002\u4e0d\u77e5\u4e0d\u89c9\u53c8\u4ece\u91d1\u4e16\u9057\u800c\u60f3\u5230\u5510\u7ecf\u5929\uff0c\u4e24\u4eba\u90fd\u662f \u5e74\u5c11\u7fe9\u7fe9\uff0c\u5510\u7ecf\u5929\u7684\u6559\u517b\u4e0e\u91d1\u4e16\u9063\u5374\u662f\u4e0d\u53ef\u76f8\u63d0\u5e76\u8bba\u3002\u4f46\u51b0\u5ddd\u5929\u5973\u60f3\u8d77\u5510\u7ecf\u5929\u5bf9\u5979\u7684\u620f\u5f04\uff0c \u5374\u53c8\u89c9\u5f97\u91d1\u4e16\u9057\u90a3\u79cd\u6e38\u620f\u98ce\u5c18\u7684\u6001\u5ea6\uff0c\u4ea6\u6709\u4e00\u79cd\u5766\u7387\u4e4b\u5904\uff0c\u5176\u5b9e\u51b0\u5ddd\u5929\u5973\u81ea\u5df1\u4e0d\u77e5\uff0c\u5979\u5bf9\u5510 \u7ecf\u5929\u5df2\u9690\u9690\u6709\u4e86\u60c5\u612b\uff0c\u6545\u6b64\u5bf9\u5510\u7ecf\u5929\u7684\u4efb\u4f55\u7f3a\u70b9\uff0c\u4efb\u4f55\u8bef\u4f1a\uff0c\u90fd\u4f1a\u8d23\u5907\u6c42\u5168\uff1b\u5bf9\u91d1\u4e16\u9057\u5219\u53ea \u662f\u4e00\u79cd\u597d\u5947\uff0c\u6700\u591a\u6742\u6709\u601c\u60dc\u4e4b\u5ff5\uff0c\u6545\u6b64\u53cd\u800c\u80fd\u4ece\u4ed6\u7684\u602a\u50fb\u884c\u5f84\u4e2d\uff0c\u4e5f\u770b\u5230\u4ed6\u6709\u201c\u53ef\u53d6\u4e4b\u5904\u201d\u3002 \u3000\u3000\u8fd9\u65e5\u5df2\u662f\u7b2c\u4e09\u65e5\u9ec4\u660f\uff0c\u91d1\u4e16\u9057\u8fd8\u4e0d\u89c1\u6765\uff0c\u51b0\u5ddd\u5929\u5973\u5bf9\u5510\u8d5b\u82b1\u7684\u4f24\u52bf\u751a\u4e3a\u6302\u5ff5\uff0c\u8d70\u51fa\u5367\u623f \uff0c\u60f3\u53bb\u63a2\u671b\uff0c\u5510\u5bb6\u751a\u5927\uff0c\u5374\u5c11\u5a62\u4ec6\uff0c\u51b0\u5ddd\u5929\u5973\u8d70\u5230\u5510\u8d5b\u82b1\u7684\u9759\u5ba4\u5916\u9762\uff0c\u542c\u5f97\u91cc\u9762\u6709\u4eba\u8bf4\u8bdd\uff0c \u6b63\u662f\u5510\u8d5b\u82b1\u7684\u58f0\u97f3\uff0c\u53ea\u542c\u5f97\u5979\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u75af\u4e10\u4eca\u665a\u5fc5\u6765\uff0c\u4ed6\u82e5\u4e0d\u5411\u5510\u5bb6\u53e9\u5934\u8c22\u7f6a\uff0c\u8fd9\u89e3 \u836f\u4e0d\u8981\u4e0e\u4ed6\uff01\u201d \u3000\u3000\u5510\u7aef\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u54b1\u4eec\u4e5f\u8981\u4ed6\u7684\u89e3\u836f\uff01\u201d\u5510\u8d5b\u82b1\u5389\u58f0\u8bf4\u9053\u3002\u201c\u54b1\u4eec\u5510\u5bb6\u4e16\u4ee3\u4ee5\u6765\uff0c\u6ca1\u4eba \u6562\u5c0f\u89d1\u4e00\u8ddf\u3001\u5982\u4eca\u4e00\u4e2a\u75af\u4e10\u95ef\u51fa\uff0c\u4f20\u51fa\u53bb\u8fd8\u6709\u4f55\u9762\u5b50\uff1f\u975e\u53e9\u5934\u8d54\u7f6a\uff0c\u8fd9\u89e3\u836f\u7edd\u4e0d\u80fd\u62ff\u51fa\u3002\u201d \u5510\u7aef\u9053\u3002\u201c\u53ef\u662f\u59d1\u59d1\uff0c\u4f60\u2026\u2026\u201d\u5510\u8d5b\u82b1\u65a5\u9053\uff1a\u201c\u6211\u62fc\u7740\u4e0d\u8981\u4ed6\u7684\u89e3\u836f\uff0c\u82e5\u4ed6\u4e0d\u80af\u8d54\u7f6a\uff0c\u5c31\u6559\u4ed6 \u966a\u7740\u6211\u4e00\u540c\u6b7b\u3002\u597d\u53eb\u5929\u4e0b\u4eba\u77e5\u9053\uff0c\u8c01\u6562\u5728\u5510\u5bb6\u653e\u8086\u7684\uff0c\u5c31\u5f97\u628a\u547d\u513f\u8d54\u4e0a\u3002\u201d\u5510\u7aef\u9053\uff1a\u201c\u59d1\u59d1 \uff0c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d\u8bdd\u58f0\u98a4\u6296\uff0c\u663e\u5f97\u5fc3\u60c5\u6781\u662f\u60f6\u6050\uff0c\u5510\u8d5b\u82b1\u201c\u556a\u201d\u7684\u4e00\u638c\u51fb\u6253\u5e8a\u6cbf\uff0c\u53c8\u5386\u58f0\u65a5\u9053 \uff1a\u201c\u4f60\u8fd9\u6837\u4e0d\u4e89\u6c14\uff0c\u8fd8\u7b97\u5f97\u5510\u5bb6\u7684\u4eba\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5728\u5916\u9762\u542c\u5f97\u6bdb\u9aa8\u60ca\u7136\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u672c \u6765\u53cc\u65b9\u4ea4\u6362\u89e3\u836f\uff0c\u4e92\u4e0d\u8f93\u4e8f\uff0c\u5c82\u975e\u751a\u597d\uff0c\u60f3\u4e0d\u5230\u8fd9\u8001\u5a46\u5a46\u5374\u5982\u6b64\u597d\u5f3a\u8981\u8138\uff0c\u72e0\u5fc3\u6bd2\u624b\uff01\u201d\u5979 \u672c\u6765\u5bf9\u91d1\u4e16\u9057\u7edd\u4e0d\u540c\u60c5\uff0c\u5982\u4eca\u542c\u4e86\u8fd9\u4e00\u756a\u8bdd\uff0c\u5bf9\u5510\u8d5b\u82b1\u4e5f\u9690\u9690\u8d77\u4e86\u53cd\u611f\u3002 \u3000\u3000\u91cc\u9762\u5510\u7aef\u653e\u4f4e\u58f0\u97f3\uff0c\u60f3\u662f\u5bf9\u59d1\u59d1\u529d\u8bf4\uff0c\u5ffd\u542c\u5f97\u5510\u8d5b\u82b1\u53c8\u662f\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201d \u4f60\u4e0d\u542c\u8bdd\uff0c\u6211\u6ca1\u7ed9\u8fd9\u75af\u4e10\u5bb3\u6b7b\uff0c\u5c31\u5148\u7ed9\u4f60\u6c14\u6b7b\u4e86\uff01\u201d\u65a5\u8d23\u4e4b\u58f0\u8fc7\u540e\uff0c\u623f\u95e8\u4e00\u5f00\uff0c\u5510\u7aef\u8d70\u4e86\u51fa \u6765\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u614c\u5fd9\u4e00\u95ea\uff0c\u5979\u8eab\u6cd5\u5feb\u6781\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5df2\u9690\u5230\u5047\u5c71\u80cc\u540e\u3002\u5510\u7aef\u672c\u9886\u867d\u7136\u4e0e \u5979\u76f8\u5dee\u751a\u8fdc\uff0c\u4f46\u4ed6\u81ea\u5e7c\u7ec3\u4e60\u6697\u5668\uff0c\u542c\u89c9\u5374\u6781\u7075\u654f\uff0c\u6025\u5fd9\u8d70\u53bb\uff0c\u51b0\u5ddd\u5929\u5973\u7f13\u7f13\u8d70\u4e86\u51fa\u6765\uff0c\u53ea\u89c1 \u5510\u7aef\u6b63\u5f20\u53e3\u6b32\u547c\uff0c\u5374\u5ffd\u5730\u53c8\u653e\u67d4\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u662f\u6842\u59d0\u59d0\uff0c\u4f60\u662f\u627e\u6211\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u662f\u5440\uff01\u201d\u5979\u4e0d\u60ef\u8bf4\u8c0e\uff0c\u987a\u7740\u5510\u7aef\u7684\u95ee\u8bdd\u8bf4\u4e86\u4e4b\u540e\uff0c\u9762\u5b54\u901a\u7ea2\uff0c\u5510\u7aef\u773c\u5149\u5145\u6ee1\u559c\u60a6\uff0c\u6df1 \u6df1\u5730\u770b\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6842\u59d0\u59d0\u4f60\u627e\u6211\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5450\u5450\u8bf4\u9053\uff1a\u201c\u6211\u627e\u4f60\uff0c\u627e\u4f60\uff0c \u60f3\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d\u5510\u7aef\u9053\uff1a\u201c\u8c01\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5c31\u662f\u6211\u524d\u65e5\u4e0e\u4f60\u8bf4\u8fc7\u7684\u90a3\u4f4d\u5510\u5927\u4fa0\u4e4b\u5b50 \u3001\u5510\u7ecf\u5929\u3002\u60f3\u4ed6\u4e5f\u5b9a\u7136\u7ecf\u8fc7\u6b64\u95f4\uff0c\u4f60\u4eec\u662f\u672c\u5730\u4eba\uff0c\u5bb9\u6613\u6253\u542c\u3002\u201d\u5979\u672c\u6765\u4e0d\u60f3\u63d0\u5510\u7ecf\u5929\uff0c\u4e34\u6025 \u4e4b\u65f6\uff0c\u4e3a\u4e86\u5706\u8c0e\uff0c\u5374\u83ab\u540d\u5176\u5999\u7684\u81ea\u7136\u800c\u7136\u7684\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5510\u7aef\u597d\u751f\u5931\u671b\uff0c\u4f46\u4ed6\u5e7c\u627f\u5bb6\u6559\uff0c\u7d20\u6709\u6db5\u517b\uff0c\u5374\u4e5f\u4e0d\u5728\u9762\u4e0a\u8868\u9732\u51fa\u6765\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0 \u65e5\u4e3a\u4e86\u7167\u6599\u59d1\u59d1\uff0c\u6ca1\u7a7a\u51fa\u5916\u6253\u542c\uff0c\u8fc7\u4e86\u4eca\u665a\uff0c\u6211\u4e00\u5b9a\u66ff\u59d0\u59d0\u7559\u5fc3\u3002\u54a6\uff0c\u59d0\u59d0\uff0c\u4f60\u8eb2\u4e00\u8eb2\uff01\u201d \u51b0\u5ddd\u5929\u5973\u4e00\u542c\uff0c\u542c\u7684\u51fa\u534a\u91cc\u4e4b\u5916\u6709\u5fae\u98ce\u843d\u53f6\u4e4b\u58f0\uff0c\u5510\u7aef\u6025\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5bb6\u4e4b\u4e8b\uff0c\u5f85\u7d27\u6025\u4e4b\u65f6 \uff0c\u518d\u8bf7\u59d0\u59d0\u76f8\u52a9\u3002\u201d\u51b0\u5ddd\u5929\u5973\u77e5\u662f\u91d1\u4e16\u9057\u5230\u4e86\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8eb2\u5230\u5047\u5c71\u80cc\u540e\uff0c\u5fc3\u4e2d\u5947\u602a\uff0c\u5510\u7aef \u524d\u65e5\u8fd8\u575a\u7559\u81ea\u5df1\uff0c\u6015\u5bf9\u4ed8\u4e0d\u4e86\u91d1\u4e16\u9057\uff0c\u8981\u81ea\u5df1\u76f8\u52a9\uff0c\u600e\u4e48\u5982\u4eca\u53c8\u4e0d\u8981\u4e86\uff1f\u7ee7\u800c\u4e00\u60f3\uff0c\u604d\u7136\u5927 \u609f\uff0c\u60f3\u662f\u90a3\u8001\u5a46\u5a46\u592a\u8fc7\u8981\u5f3a\uff0c\u6240\u4ee5\u575a\u6301\u8981\u5510\u5bb6\u7684\u4eba\u81ea\u884c\u4e86\u7ed3\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u521a\u8eb2\u8fdb\u5047\u5c71\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u602a\u7b11\uff0c\u91d1\u4e16\u9057\u5df2\u5230\u56ed\u4e2d\uff0c\u771f\u662f\u5feb\u6377\u65e0\u6bd4\u3002\u5510\u7aef\u6273\u8d77\u9762 \u5b54\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u91d1\u4e16\u9057\u5df2\u54c8\u54c8\u5927\u7b11\uff0c\u62a2\u5148\u8bf4\u9053\uff0c\u201c\u597d\u5389\u5bb3\u7684\u767d\u7709\u9488\uff0c\u6211\u603b\u7b97\u89c1\u8bc6\u4f60\u5510\u5bb6\u7684\u6697 \u5668\u4e86\uff01\u8fd9\u79cd\u6b79\u6bd2\u7684\u6697\u5668\uff0c\u4e5f\u4e8f\u4f60\u4eec\u9022\u4eba\u4fbf\u7528\uff0c\u8fd9\u662f\u4f60\u4eec\u6697\u5668\u4e16\u5bb6\u7684\u5bb6\u98ce\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u6697\u6697 \u5947\u602a\uff0c\u672c\u6765\u662f\u91d1\u4e16\u9057\u65e0\u7f18\u65e0\u6545\u627e\u4e0a\u5510\u5bb6\uff0c\u600e\u4e48\u4ed6\u53cd\u800c\u5148\u602a\u8d77\u5510\u5bb6\u6765\uff1f \u3000\u3000\u5510\u7aef\u5927\u7ea6\u4e5f\u662f\u540c\u6837\u5fc3\u601d\uff0c\u53ea\u89c1\u4ed6\u53cc\u773c\u4e00\u7741\uff0c\u6012\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u7684\u6697\u5668\u5c31\u4e0d\u6b79\u6bd2\uff1f\u65e0\u7f18\u65e0\u6545 \u5730\u6253\u4f24\u4e00\u4e2a\u8001\u5a46\u5a46\uff0c\u96be\u9053\u8fd9\u4e5f\u7b97\u5f97\u662f\u4fa0\u4e49\u9053\u7684\u6240\u4e3a\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u6b63\u81ea\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u95ee\u5f97\u597d \uff0c\u8d23\u5f97\u597d\uff01\u201d\u4f46\u542c\u5f97\u91d1\u4e16\u9057\u54c8\u54c8\u5927\u7b11\uff0c\u602a\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u4fa0\u4e49\u9053\uff0c\u4f60\u8fd9\u8bdd\u53ef\u662f\u5e9f\u8bdd \uff01\u201d\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u7684\u4eba\uff0c\u5373\u7b97\u672c\u662f\u4e2a\u4e0b\u4e09\u6d41\u7684\u5bb5\u5c0f\u4e4b\u8f88\uff0c\u4e5f\u591a\u4ee5\u201c\u4fa0\u4e49\u201d\u4e24\u5b57\u4f5c\u4e3a\u5e4c\u5b50\uff0c\u7edd \u4e0d\u80af\u50cf\u91d1\u4e16\u9057\u90a3\u6837\u81ea\u627f\uff0c\u5510\u7aef\u4e0d\u89c9\u4e00\u6014\u3002 \u3000\u3000\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e13\u95e8\u9886\u6559\u4fa0\u4e49\u9053\u672c\u9886\u7684\u4eba\uff0c\u4f60\u5bb6\u7684\u59d1\u59d1\u82e5\u7136\u662f\u4e2a\u666e\u901a \u5a46\u5b50\uff0c\u6211\u81ea\u7136\u4e0d\u4f1a\u5bfb\u5979\uff0c\u53ef\u5979\u5374\u662f\u81ea\u79f0\u5929\u4e0b\u6697\u5668\u7b2c\u4e00\u7684\u9ad8\u624b\uff01\u4f60\u4eec\u5510\u5bb6\u4e5f\u66fe\u4e16\u4ee3\u4ee5\u4fa0\u4e49\u6807\u699c \uff0c\u54c8\u54c8\uff0c\u5982\u4eca\u4e5f\u9886\u6559\u4e86\u3002\u201d\u5510\u7aef\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u4eec\u5510\u5bb6\u7684\u4eba\u603b\u4e0d\u81f3\u4e8e\u50cf\u4f60\u90a3\u6837\u9119\u52a3\u5077\u88ad\uff01\u201d \u91d1\u4e16\u9057\u53c8\u4ef0\u5929\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e14\u95ee\u4f60\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u53ef\u662f\u5e38\u4e8b\uff1f\u201d\u5510\u7aef\u9053\uff1a \u201c\u4e0d\u9519\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u672c\u6765\u53ea\u662f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4f60\u4eec\u5510\u5bb6\u7684\u6b66\u529f\u548c\u6697\u5668\u624b\u6cd5\uff0c\u4f60\u59d1\u59d1\u5374\u5148\u7528 \u4e86\u5267\u6bd2\u7684\u767d\u7709\u9488\uff0c\u8981\u628a\u6211\u7f6e\u4e8e\u6b7b\u5730\uff0c\u4f60\u8bf4\u6211\u8be5\u5982\u4f55\uff1f\u6709\u6bd2\u7684\u6697\u5668\u5929\u4e0b\u4e5f\u4e0d\u53ea\u662f\u4f60\u5510\u5bb6\u72ec\u6709\uff0c \u54c8\u54c8\uff0c\u90a3\u6211\u4e5f\u53ea\u597d\u5949\u966a\u4e86\uff01\u4f60\u5bb6\u7684\u767d\u76fe\u9488\u8981\u4e03\u65e5\u65b9\u80fd\u81f4\u4eba\u6b7b\u547d\uff0c\u6211\u7684\u6bd2\u9f99\u9489\u4f60\u7684\u59d1\u59d1\u6700\u591a\u53ea \u80fd\u6371\u4e09\u5929\uff01\u4f60\u8981\u6211\u6b7b\uff0c\u90a3\u4e5f\u5bb9\u6613\uff0c\u53ea\u662f\u6211\u53ef\u770b\u4f60\u54ed\u7075\u4e4b\u540e\uff0c\u9093\u624d\u4f1a\u6b7b\u5374\u54ea\uff01\u201d\u5510\u7aef\u5fc3\u4e2d\u53d1\u6bdb \uff0c\u4ed6\u8fd9\u624d\u77e5\u9053\u539f\u6765\u662f\u59d1\u59d1\u5148\u53d1\u4e86\u767d\u7709\u9488\uff0c\u8fd9\u624d\u5f15\u51fa\u8fd9\u75af\u4e10\u7684\u6bd2\u9f99\u9489\u7684\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8bf4\u7684\u4e5f\u6709\u6709\u4ed6\u7684\u4e00\u7247\u6b6a\u7406\uff0c\u6309\u8bf4\u82e5\u7136\u5510\u8d5b\u82b1\u77e5\u9053\u4ed6\u53ea\u662f\u60f3\u5370\u8bc1\u6b66\u529f\uff0c\u5373\u7b97\u7528\u6697\u5668 \u6253\u4f24\u4e86\u4ed6\uff0c\u4e5f\u4e0d\u8be5\u7528\u5582\u6bd2\u6697\u5668\uff0c\u53ef\u662f\u91d1\u4e16\u9057\u4ece\u5c71\u4e1c\u95ef\u5230\u5ddd\u5317\uff0c\u4e13\u4ee5\u6298\u8fb1\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\u4e3a \u4e50\uff0c\u540d\u6c14\u5927\u574f\uff0c\u5510\u8d5b\u82b1\u60f3\u4e0b\u624b\u9664\u4ed6\uff0c\u4e5f\u6709\u5979\u7684\u9053\u7406\uff0c\u5510\u7aef\u88ab\u91d1\u4e16\u9057\u4e00\u95ee\uff0c\u6014\u4e86\u4e00\u6014\u968f\u5373\u6012\u51b2 \u51b2\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u75af\u72d7\u4e00\u822c\u7684\u4e1c\u897f\uff0c\u8c01\u4e0e\u4f60\u8bb2\u6c5f\u6e56\u89c4\u77e9\uff1f\u6211\u59d1\u59d1\u624d\u4e0d\u5c51\u4e8e\u4e0e\u4f60\u5370\u8bc1\u6b66\u529f\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u9762\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u591a\u8bf4\u4e00\u53e5\uff0c\u6211\u4e5f\u5c31\u4e0d\u987e\u6c5f\u6e56\u89c4\u77e9\uff0c\u5148\u53d6\u4f60\u7684\u6027\u547d\uff01\u201d\u53cc \u76ee\u500f\u5730\u9732\u51fa\u51f6\u5149\uff0c\u5510\u7aef\u4e00\u55f2\uff0c\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u59d1\u59d1\u4e0d\u5c51\u4e8e\u4e0e\u6211\u5370\u8bc1\u6b66\u529f\uff0c\u5982\u4eca \u53ef\u8981\u54c0\u6c42\u6211\u7ed9\u89e3\u836f\u4e86\u5427\uff1f\u201d\u5510\u7aef\u6297\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5982\u4eca\u4e5f\u8981\u54c0\u6c42\u6211\u4eec\u5510\u5bb6\u7684\u89e3\u836f\u4e86\u5427\uff1f\u201d\u91d1\u4e16 \u9063\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u4f60\u53ef\u522b\u5fd8\u8bb0\uff0c\u4f60\u59d1\u59d1\u8fc7\u4e0d\u4e86\u4eca\u665a\uff0c\u6211\u53ef\u8fd8\u8981\u8fc7\u56db\u65e5\u624d\u6b7b\u3002\u8fd9\u56db\u65e5\u4e4b\u5dee\uff0c\u5c31 \u503c\u5f97\u4f60\u5411\u6211\u78d5\u4e09\u4e2a\u54cd\u5934\u3002\u201d\u5510\u7aef\u6012\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5f7c\u6b64\u4ea4\u6362\u89e3\u836f\uff0c\u8fd8\u8981\u6211\u5411\u4f60\u78d5\u5934\u8d54\u7f6a\uff01\u201d\u91d1 \u4e16\u9057\u9053\uff1a\u201c\u4f60\u59d1\u59d1\u73b0\u5728\u8c05\u4e5f\u4e0d\u80fd\u8d70\u52a8\uff0c\u90a3\u53ea\u6709\u4f60\u66ff\u5979\u78d5\u4e86\u3002\u201d\u5510\u7aef\u5927\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u78d5\u5934\u8d54\u7f6a \uff0c\u4f11\u60f3\u5f97\u6211\u89e3\u836f\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u90a3\u6211\u53ea\u597d\u64e6\u4eae\u773c\u775b\uff0c\u770b\u4f60\u54ed\u7075\u4e86\uff01\u201d\u5510\u7aef\u53c8\u6c14\u53c8\u6025\uff0c\u5fc3\u4e2d \u5ffd\u601d\uff1a\u8fd9\u75af\u4e10\u4e2d\u4e86\u6211\u59d1\u59d1\u7684\u767d\u7709\u9488\uff0c\u6309\u8bf4\u5982\u4eca\u6bd2\u529b\u8be5\u5df2\u53d1\u4f5c\uff0c\u6211\u672a\u5fc5\u5c31\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff1f\u6b63\u60f3 \u52a8\u624b\uff0c\u91d1\u4e16\u9057\u7adf\u4f3c\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\uff0c\u968f\u624b\u4e00\u638c\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u628a\u4e00\u6280\u5c0f\u6811\u5288\u5012\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981 \u7528\u5f3a\u5417\uff1f\u90a3\u4e5f\u6210\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u5feb\u903e\u98d8\u98ce\uff01 \u3000\u3000\u5510\u7aef\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u9053\u662f\u90a3\u75af\u4e10\u7a81\u7136\u53d1\u96be\uff0c\u5de6\u624b\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u53f3\u624b\u4e00\u4e2a\u201c\u62ab\u98ce\u6a2a\u65a9 \u201d\uff0c\u5510\u5bb6\u7684\u6697\u5668\u5929\u4e0b\u95fb\u540d\uff0c\u638c\u6cd5\u4e0a\u4e5f\u6709\u72ec\u95e8\u6740\u624b\uff0c\u8fd9\u4e24\u62db\u4e3a\u4e86\u4e34\u5371\u6551\u547d\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u66f4\u662f\u5510 \u5bb6\u638c\u6cd5\u7684\u7cbe\u534e\u6240\u5728\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u4f46\u53ea\u89c9\u5fae\u98ce\u68d8\u7136\uff0c\u6765\u4eba\u4ece\u8eab\u8fb9\u63a0\u8fc7\uff0c\u8fde\u8863\u89d2\u4e5f\u635e\u4e0d\u7740\uff0c\u62ac\u5934 \u770b\u65f6\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u8863\u8882\u8f7b\u98d8\uff0c\u62e6\u5728\u4e24\u4eba\u4e2d\u95f4\u3002 \u3000\u3000\u5510\u7aef\u53eb\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u76f8\u52a9\uff0c\u5510\u5bb6\u4e4b\u4e8b\uff0c\u7531\u6211\u627f\u5f53\u3002\u201d\u4f46\u89c1\u51b0\u5ddd\u5929\u5973\u9762\u82e5\u51b0\u971c\uff0c\u8f6c\u5411\u90a3 \u75af\u4e10\u9053\uff1a\u201c\u8fd9\u4e2a\u7ed9\u4f60\uff0c\u4f60\u7684\u89e3\u836f\u4e5f\u62ff\u51fa\u6765\uff01\u201d\u5510\u7aef\u5403\u4e86\u4e00\u60ca\uff0c\u4f38\u624b\u4e00\u6478\uff0c\u6000\u4e2d\u7684\u89e3\u836f\u5df2\u5728\u90a3 \u77ac\u606f\u4e4b\u95f4\u7ed9\u51b0\u5ddd\u5929\u5973\u5077\u53bb\u3002\u5510\u7aef\u53ea\u9053\u662f\u51b0\u5ddd\u5929\u5973\u51fa\u6765\u76f8\u52a9\uff0c\u4e0d\u6599\u5979\u7adf\u7136\u5077\u4e86\u81ea\u5df1\u7684\u89e3\u836f\u9001\u7ed9 \u654c\u4eba\u3002\u4e0d\u7531\u5f97\u5f20\u53e3\u7ed3\u820c\uff0c\u534a\u664c\u624d\u51fa\u58f0\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u51e0\u4e4e\u4e0e\u5510\u7aef\u540c\u58f0\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u628a\u89e3\u836f\u62ff\u51fa \u6765\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u597d\u5475\uff01\u201d\u51b0\u5ddd\u5929\u5973\u957f\u5251\u4e00\u6307\uff0c\u9053\uff1a\u201c\u5f7c\u6b64\u4ea4\u6362\uff0c\u4e24\u4e0d\u8f93\u4e8f\u3002\u628a\u89e3\u836f\u62ff \u51fa\u6765\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4e0d\u8981\u518d\u6765\u89c1\u6211\uff01\u201d\u91d1\u4e16\u9063\u770b\u4e86\u51b0\u5ddd\u5929\u5973\u4e00\u773c\uff0c\u66ae\u7136\u628a\u624b\u4e00\u626c\uff0c\u9053\uff1a\u201c\u7ed9\u4f60 \uff01\u201d\u51b0\u5ddd\u5929\u5973\u4f38\u624b\u4e00\u63a5\uff0c\u91d1\u4e16\u9063\u5de6\u624b\u53c8\u662f\u4e00\u626c\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4e2a\u4e5f\u7ed9\u4f60\uff01\u201d\u51b0\u5ddd\u5929\u5973\u957f\u8896\u4e00\u5377 \uff0c\u53ea\u89c1\u540e\u6765\u63b7\u6765\u7684\u90a3\u5b97\u7269\u4e8b\uff0c\u5374\u662f\u7528\u7f8a\u76ae\u7eb8\u5305\u88f9\u7684\u4e00\u4e2a\u77f3\u5934\uff0c\u6b63\u81ea\u4e0d\u660e\u5176\u610f\uff0c\u91d1\u4e16\u9057\u6124\u7136\u8bf4 \u9053\uff1a\u201c\u4f60\u8981\u89c1\u7684\u4eba\u5728\u8fd9\u91cc\u9762\uff0c\u4f60\u597d\u597d\u5730\u53bb\u77a7\u5427\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5df2\u81ea\u7ffb\u5899\u98de\u51fa\uff0c\u5510\u7aef\u4e0d\u7531\u5f97\u6253 \u4e86\u4e00\u4e2a\u5bd2\u8776\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u75af\u4e10\u4e2d\u4e86\u59d1\u59d1\u7684\u767d\u7709\u9488\uff0c\u53ea\u8fd8\u6709\u56db\u5929\u6027\u547d\uff0c\u5c45\u7136\u8fd8\u662f\u8fd9\u4e48\u4e86\u5f97\uff01\u5e78 \u4e8f\u672a\u66fe\u4e0e\u4ed6\u771f\u4e2a\u52a8\u624b\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u628a\u90a3\u7f8a\u76ae\u7eb8\u644a\u5f00\u4e00\u770b\uff0c\u767b\u65f6\u5446\u4e86\u3002\u53ea\u89c1\u90a3\u7eb8\u4e0a\u753b\u7740\u4e24\u4e2a\u4eba\u50cf\uff0c\u4e00\u4e2a\u662f\u5510\u7ecf\u5929\uff0c \u53e6\u4e00\u4e2a\u5374\u662f\u7f8e\u8273\u7684\u5c11\u5973\uff0c\u753b\u5f97\u975e\u5e38\u751f\u52a8\uff0c\u90a3\u5c11\u5973\u5de7\u76ee\u542b\u7b11\uff0c\u7709\u9edb\u751f\u6625\uff0c\u534a\u9762\u8138\u5411\u7740\u5510\u7ecf\u5929\uff0c \u624b\u6307\u62c8\u7740\u88d9\u89d2\uff0c\u6d3b\u753b\u51fa\u4e00\u4e2a\u521d\u89e3\u98ce\u60c5\u7684\u5a07\u75f4\u5c11\u5973\uff0c\u90a3\u7f8a\u76ae\u7eb8\u4e0a\u8fd8\u753b\u7740\u5730\u56fe\uff0c\u6307\u51fa\u600e\u6837\u53bb\u627e\u5510 \u7ecf\u5929\u7684\u9053\u8def\u3002\u51b0\u5ddd\u5929\u5973\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5510\u7ecf\u5929\u5c31\u5728\u90bb\u53bf\uff0c\u6b64\u53bb\u4e0d\u8fc7\u4e24\u65e5\u8def\u7a0b\u3002\u8fd9\u5c11\u5973\u7a76\u662f\u4f55\u4eba \uff1f\u91d1\u4e16\u9057\u7ed9\u6211\u8fd9\u753b\u53c8\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u5510\u7aef\u53eb\u9053\uff1a\u201c\u6842\u59d0\u59d0\uff0c\u6842\u59d0\u59d0\uff01\u201d\u51b0\u5ddd\u5929\u5973\u628a\u7f8a\u76ae\u753b\u6536\u8fdb\u6000\u4e2d\uff0c\u5fc3\u70e6\u610f\u4e71\uff0c\u542c\u4ed6 \u8fde\u53eb\u51e0\u58f0\u8fd9\u624d\u56de\u8f6c\u5934\u6765\u3002\u5510\u7aef\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u5982\u4f55\u662f\u597d\uff0c\u59d1\u59d1\u4e00\u5b9a\u602a\u8d23\u6211\u4e86\uff0c\u201d\u51b0\u5ddd\u5929\u5973\u7a81\u89c9 \u5fc3\u4e2d\u4e00\u9635\u538c\u70e6\uff0c\u628a\u91d1\u4e16\u9063\u7684\u89e3\u836f\u585e\u5230\u5510\u7aef\u624b\u91cc\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u4ed6\u5411\u4f60\u8d54\u7f6a\uff0c\u8fd9\u6210\u4e0d\u6210\uff1f \u201d\u5510\u7aef\u614c\u5fd9\u907f\u5f00\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u59d1\u59d1\u5429\u5490\u8fc7\u4f60\uff0c\u82e5\u7136\u4ed6\u4e0d\u78d5\u5934\u8d54\u7f6a\uff0c\u4f60\u4eec\u5510\u5bb6\u7684\u89e3\u836f\u5c31 \u4e0d\u80fd\u4ea4\u51fa\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5510\u7aef\u9053\uff0c\u201c\u6b63\u662f\u5440\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4eec\u5510\u5bb6\u7684\u89e3\u836f\u662f\u6211\u4ea4\u7ed9\u4ed6 \u7684\uff0c\u4e0e\u4f60\u65e0\u5173\uff0c\u4f60\u59d1\u59d1\u82e5\u7136\u602a\u8d23\u4e5f\u4e0d\u4f1a\u602a\u5230\u4f60\u7684\u8eab\u4e0a\uff0c\u8fd9\u4e00\u5305\u89e3\u836f\u4f60\u5feb\u62ff\u53bb\u7ed9\u4f60\u59d1\u59d1\uff0c\u9ebb\u70e6 \u4f60\u66ff\u6211\u5411\u5979\u95f4\u597d\uff01\u201d\u7a81\u7136\u5927\u58f0\u53eb\u9053\u3002\u201c\u5e7d\u840d\uff0c\u5e7d\u840d\uff01\u201d \u3000\u3000\u5510\u7aef\u8bf4\u9053\uff1a\u201c\u5f84\u59d0\u59d0\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u53ea\u89c1\u6708\u5149\u4e4b\u4e0b\uff0c\u5e7d\u840d\u5306\u5306\u5954\u51fa\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff0c\u201c\u4e09 \u65e5\u6765\u591a\u8c22\u4f60\u7684\u62db\u5f85\uff0c\u518d\u89c1\u5566\uff01\u201d\u5510\u7aef\u9053\uff0c\u201c\u6842\u59d0\u59d0\uff0c\u8fd9\u4e0d\u662f\u89c1\u602a\u6211\u4eec\u5417\uff1f\u201d\u51b0\u5ddd\u592d\u5973\u9053\uff0c\u201d \u4f60\u59d1\u59d1\u5b89\u7136\u65e0\u4e8b\uff0c\u6211\u53ef\u4ee5\u653e\u5fc3\u8d70\u4e86\u3002\u54ea\u8c08\u5f97\u4e0a\u4ec0\u4e48\u89c1\u602a\uff1f\u201d\u4e0e\u5e7d\u840d\u4e00\u4e2a\u56de\u8eab\u53cd\u8dc3\uff0c\u63a0\u8fc7\u5899\u5934 \uff0c\u5510\u7aef\u8ffd\u51fa\u53bb\u65f6\uff0c\u4f46\u89c1\u660e\u6708\u5728\u5929\uff0c\u661f\u6cb3\u803f\u803f\uff0c\u54ea\u8fd8\u6709\u5979\u4eec\u4e8c\u4eba\u7684\u5f71\uff0c\u5510\u7aef\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u8d77\u51b0 \u5ddd\u5929\u5973\u521a\u624d\u7684\u51fa\u624b\uff0c\u5b9e\u662f\u4e00\u7247\u82e6\u5fc3\uff0c\u8981\u4e0d\u7136\u4ed6\u548c\u90a3\u75af\u4e10\u5728\u6012\u6c14\u5934\u4e0a\uff0c\u5927\u7ea6\u8c01\u90fd\u4e0d\u4f1a\u8ba9\u6b65\uff0c\u7ed3 \u679c\u59d1\u59d1\u548c\u90a3\u75af\u4e10\u5fc5\u4e24\u8d25\u4ff1\u4f24\u3002\u60f3\u4e0d\u5230\u5982\u6b64\u840d\u6c34\u76f8\u9022\uff0c\u5306\u5306\u4fbf\u6563\uff0c\u552f\u6709\u6ca1\u7cbe\u6253\u91c7\u7684\u5c06\u89e3\u836f\u6367\u56de \u53bb\u7980\u544a\u59d1\u59d1\u3002\u5510\u7aef\u5fc3\u60c5\u7d0a\u4e71\uff0c\u5374\u4e0d\u77e5\u9053\u51b0\u5ddd\u5929\u5973\u66f4\u662f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u51b0\u5ddd\u5929\u5973\u672c\u6765\u4e0d\u89e3\u4eba\u4e16\u7684\u5fe7 \u6101\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u81ea\u4e0e\u5510\u7ecf\u5929\u5206\u5f00\u4e4b\u540e\uff0c\u603b\u89c9\u5f97\u90c1\u90c1\u4e0d\u4e50\uff0c\u4eca\u665a\u89c1\u4e86\u90a3\u7f8a\u76ae\u56fe\u753b\uff0c\u66f4\u662f\u89e6\u52a8 \u5fc3\u5934\uff0c\u4e00\u5ffd\u513f\u60f3\u7acb\u523b\u53bb\u89c1\u5510\u7ecf\u5929\uff0c\u4e00\u5ffd\u513f\u53c8\u60f3\u4ece\u6b64\u907f\u5f00\uff0c\u6c38\u4e0d\u76f8\u89c1\u3002\u8fde\u81ea\u5df1\u4e5f\u4e0d\u77e5\u662f\u7231\u662f\u6068 \uff1f\u6240\u601d\u4e3a\u4f55\uff1f \u3000\u3000\u51b0\u5ddd\u5929\u5973\u54ea\u91cc\u77e5\u9053\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5510\u7ecf\u5929\u4e5f\u6b63\u662f\u5fc3\u601d\u7ee9\u4e71\uff0c\u60f3\u5ff5\u7740\u5979\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u5510\u7ecf\u5929\u5927\u75c5\u521d\u6108\uff0c\u5728\u6708\u591c\u4e4b\u4e0b\uff0c\u548c\u90b9\u7edb\u971e\u5728\u5c4b\u5916\u6f2b\u6b65\uff0c\u90b9\u7edb\u971e\u7684\u6bcd\u4eb2\u5ffd\u7136\u6765\u627e\u4ed6 \u4eec\uff0c\u8c08\u8d77\u90a3\u75af\u4e10\u4f24\u4e86\u5510\u8d5b\u82b1\u4e4b\u4e8b\u3002\u5510\u7ecf\u5929\u542c\u8bf4\u6709\u4e24\u4e2a\u7f8e\u82e5\u5929\u4ed6\u7684\u5973\u5b50\u548c\u90a3\u75af\u4e10\u4e00\u9053\uff0c\u4e0d\u89c9\u5927 \u5403\u4e00\u60ca\uff0c\u731c\u60f3\u8fd9\u4e24\u4e2a\u5973\u5b50\uff0c\u5341\u4e4b\u516b\u4e5d\u5fc5\u662f\u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u3002\u89c9\u5f97\u8fd9\u4e8b\u60c5\u8fc7\u4e8e\u602a\u8bde\uff0c\u96be\u4ee5\u7f6e\u4fe1\uff0c \u4f46\u65e2\u7136\u8bb8\u591a\u4eba\u89c1\u5230\uff0c\u4f1a\u5f71\u4f1a\u58f0\uff0c\u53c8\u4e0d\u7531\u4e0d\u4fe1\uff0c\u5fc3\u4e2d\u81ea\u662f\u6697\u6697\u7eb3\u95f7\u3002\u6768\u67f3\u9752\u89c1\u5510\u7ecf\u5929\u6ca1\u7cbe\u6253\u91c7 \uff0c\u53ea\u9053\u4ed6\u662f\u542c\u5f97\u90a3\u75af\u4e10\u51fa\u73b0\uff0c\u5fc3\u4e2d\u4e0d\u5b89\uff0c\u8a00\u9053\uff1a\u201c\u8fd9\u4e24\u65e5\u54b1\u4eec\u4e14\u907f\u4ed6\u4e00\u907f\uff0c\u5f85\u4f60\u5b8c\u5168\u590d\u539f\u4e4b \u540e\uff0c\u54b1\u4eec\u518d\u5408\u529b\u6597\u4e00\u6597\u4ed6\u3002\u201d\u90b9\u7edb\u971e\u542c\u6bcd\u4eb2\u8bf4\u4e0d\u8bb8\u5979\u5728\u5c4b\u5916\u6563\u6b65\uff0c\u6485\u8d77\u5c0f\u5634\u513f\u9053\uff1a\u201c\u5510\u5bb6\u54e5 \u54e5\u521a\u75c5\u597d\uff0c\u6b63\u8981\u5230\u5916\u5934\u8d70\u8d70\u6563\u6563\u5fc3\uff0c\u5173\u5728\u5c4b\u4e2d\uff0c\u90a3\u591f\u591a\u95f7\uff01\u201d\u5510\u7ecf\u5929\u89c1\u5979\u90a3\u5a07\u75f4\u7684\u6837\u5b50\uff0c\u4e0d \u7531\u5f97\u53f6\u55e4\u4e00\u7b11\uff0c\u5fc3\u77e5\u90b9\u7edb\u971e\u597d\u52a8\u7231\u73a9\uff0c\u8fd9\u5341\u591a\u5929\u6765\uff0c\u5979\u4e0d\u79bb\u75c5\u69bb\uff0c\u670d\u4f8d\u81ea\u5df1\uff0c\u5b9e\u662f\u96be\u4e3a\u4e86\u5979 \uff0c\u4fbf\u9053\uff1a\u201c\u5176\u5b9e\u4e5f\u4e0d\u5fc5\u5982\u6b64\u754f\u60e7\uff0c\u6211\u867d\u7136\u5c1a\u672a\u5341\u5206\u590d\u539f\uff0c\u4f46\u80be\u540c\u82e5\u518d\u9047\u5230\u90a3\u4e2a\u9ebb\u75af\uff0c\u4ed6\u4e5f\u65ad \u4e0d\u80fd\u518d\u4f24\u5f97\u6211\u3002\u201d\u90b9\u7edb\u971e\u542c\u4ed6\u8bf4\u5f97\u751a\u4e3a\u81ea\u4fe1\uff0c\u559c\u9053\uff1a\u201c\u5510\u54e5\u54e5\uff0c\u4f60\u60f3\u51fa\u4e86\u4ec0\u4e48\u7834\u654c\u7684\u5999\u6cd5\uff1f \u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u75af\u4e10\u6700\u5389\u5bb3\u7684\u662f\u53e3\u4e2d\u7684\u6697\u5668\uff0c\u4f46\u4e0d\u80fd\u53ca\u8fdc\uff0c\u6211\u7684\u5929\u5c71\u795e\u8292\u53ef\u4ee5\u6253\u5230\u4e94\u516d\u4e08 \u5916\uff0c\u82e5\u518d\u89c1\u4ed6\uff0c\u6211\u53ea\u7528\u6697\u5668\u62d2\u654c\uff0c\u5c31\u6559\u4ed6\u4e0d\u6562\u8fd1\u8eab\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4f60\u6709\u628a\u63e1\uff0c\u90a3\u4f60\u5c31\u548c\u971e\u513f\u6563\u6563\u5fc3\u5427\u3002\u6211\u4e0d\u62e6\u963b\u4f60\u4eec\u4e86\u3002\u201d \u5979\u89c1\u5510\u7ecf\u5929\u548c\u5973\u513f\u90fd\u6b22\u559c\u5728\u82b1\u4e0b\u6563\u6b65\uff0c\u5fc3\u4e2d\u5fc5\u6709\u6240\u601d\uff0c\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u90b9\u5bb6\u5c4b\u5b50\u501a\u5c71\u800c\u5efa\uff0c\u5c4b\u5916\u90b9\u7edb\u971e\u6240\u79cd\u7684\u8309\u8389\u82b1\u6b63\u5728\u76db\u5f00\uff0c\u4e00\u7247\u94f6\u767d\uff0c\u5728\u6708\u5149\u4e0b\u53d1\u6563\u7740\u6de1 \u6de1\u7684\u5e7d\u9999\uff0c\u4e2d\u4eba\u5982\u9152\u3002\u90b9\u7edb\u971e\u4e25\u4f3c\u4f9d\u4eba\u5c0f\u9e1f\uff0c\u7d27\u7d27\u5730\u508d\u7740\u5510\u7ecf\u5929\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5728\u8309\u8389\u82b1\u4e0b\u7f13\u7f13\u6f2b\u6b65\uff0c\u8bb8\u4e45\u8bb8\u4e45\uff0c\u90fd\u4e0d\u8bf4\u8bdd\uff0c\u90b9\u7edb\u971e\u9053\uff1a\u201c\u5510\u54e5\u54e5\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f \u201d\u5510\u7ecf\u5929\u9053\uff0c\u201c\u6ca1\u60f3\u4ec0\u4e48\u3002\u201d\u90b9\u7edb\u971e\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\uff0c\u4f60\u4e00\u5b9a\u662f\u542c\u5f97\u6211\u5988\u8bf4 \u90a3\u4e24\u4e2a\u5973\u5b50\u7f8e\u82e5\u5929\u4ed9\uff0c\u5fc3\u4e2d\u60f3\u89c1\u5979\u4eec\u5566\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u90b9\u7edb\u971e\u672c\u662f\u6545\u610f\u53d6\u7b11\uff0c\u5374\u89c1\u5510\u7ecf\u5927\u5ffd \u5730\u4f4e\u4e0b\u4e86\u5934\uff0c\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u662f\u60f3\u5ff5\u5979\u4eec\u3002\u201d\u90b9\u7edb\u971e\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5510 \u54e5\u54e5\uff0c\u4f60\u771f\u662f\u8ba4\u8bc6\u5979\u4eec\u7684\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5979\u95e8\u672c\u6765\u662f\u6211\u5f88\u8981\u597d\u7684\u670b\u53cb\u3002\u201d\u90b9\u7ece\u971e\u9053 \uff1a\u201c\u90a3\u4e48\uff0c\u5979\u4eec\u4e3a\u4f55\u4e0d\u4e0e\u4f60\u4e00\u9053\uff0c\u5374\u53cd\u800c\u4e0e\u90a3\u4eba\u618e\u9b3c\u538c\u7684\u9ebb\u75af\u540c\u884c\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u4e5f\u6b63 \u60f3\u627e\u5979\u4eec\u95ee\u4e2a\u660e\u767d\u3002\u201d\u90b9\u7f18\u971e\u9762\u8272\u4e00\u6697\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u60f3\u89c1\u90a3\u9ebb\u75af\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8c01\u8981\u4f60 \u53bb\u89c1\u4ed6\uff1f\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f46\u6211\u5374\u60f3\u53bb\u89c1\u90a3\u4e24\u4f4d\u7f8e\u82e5\u5929\u4ed9\u7684\u59d0\u59d0\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f60\u6b22\u559c\u7684\u4eba\u6211\u4e5f\u6b22\u559c\uff0c\u4f60\u5e26\u6211\u53bb\u89c1\u5979\u4eec\u6210\u4e0d\u6210\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff0c\u201c\u5979\u4eec\u662f\u5426\u613f\u610f \u89c1\u6211\uff0c\u6211\u4e5f\u8fd8\u4e0d\u77e5\u9053\u5462\u3002\u201d\u90b9\u7ece\u971e\u9053\uff0c\u201c\u8fd9\u5374\u662f\u4e3a\u4f55\uff1f\u4f60\u4e0d\u662f\u8bf4\u5979\u4eec\u90fd\u662f\u4f60\u7684\u597d\u670b\u53cb\u5417\uff1f\u201d \u5510\u7ecf\u5929\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u971e\u59b9\uff0c\u4f60\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u8bb8\u591a\u4e8b\u60c5\u6211\u8bf4\u4f60\u4e5f\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u90b9\u7edb\u971e\u55d4\u9053\uff1a\u201c\u4f60\u4e5f\u6bd4\u6211\u5927\u4e0d\u4e86\u51e0\u5e74\u3002\u201d\u5ffd\u9053\uff1a\u201c\u8bb8\u4e45\u8bb8\u4e45\u4ee5\u524d\uff0c\u6211\u521a\u521a\u61c2\u4e8b\u7684\u65f6\u5019\uff0c \u5c31\u60f3\u89c1\u4f60\u4e86\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u600e\u77e5\u9053\u4e16\u4e0a\u6709\u6211\u8fd9\u4e2a\u4eba\uff1f\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201d \u6211\u521a\u61c2\u4e8b\u7684\u65f6\u5019\uff0c\u5988\u5c31\u548c\u6211\u8c08\u8d77\u4f60\u5566\uff01\u201d\u5510\u7ecf\u5929\u9053\uff0c\u201c\u6211\u4e0d\u4fe1\uff0c\u4f60\u5988\u4e5f\u662f\u534a\u6708\u4e4b\u524d\u624d\u8ba4\u8bc6\u6211 \u7684\u3002\u201d\u90b9\u7ece\u971e\u9053\uff1a\u201c\u6211\u5988\u5e38\u5e38\u548c\u6211\u8bf4\u8d77\u4f60\u7684\u7236\u4eb2\uff0c\u8bf4\u8d77\u4ed6\u4eec\u540c\u5b66\u4e4b\u65f6\u7684\u8bb8\u591a\u6709\u8da3\u4e4b\u4e8b\u3002\u8fd9\u4e9b \u5e74\u6765\uff0c\u5988\u8001\u662f\u60f3\u5230\u5929\u5c71\u63a2\u671b\u4f60\u4eec\uff0c\u5979\u8bf4\u4f60\u7236\u4eb2\u4e0d\u5927\u7231\u8bf4\u8bdd\uff0c\u6709\u65f6\u8fd8\u4f1a\u5bf9\u5979\u53d1\u813e\u6c14\u3002\u55ef\uff0c\u8fd9\u4e00 \u70b9\u597d\u50cf\u4f60\u4e0d\u662f\u8fd9\u6837\u3002\u6211\u5988\u5e38\u8bf4\uff1a\u971e\u513f\uff0c\u4f60\u5f88\u50cf\u6211\uff1b\u5510\u4f2f\u4f2f\u4e5f\u4e00\u5b9a\u6709\u513f\u5973\u4e86\uff0c\u4e0d\u77e5\u50cf\u4e0d\u50cf\u4ed6\uff1f \u6240\u4ee5\u6211\u5c0f\u65f6\u5019\u5c31\u60f3\uff0c\u5510\u54e5\u54e5\u4e0d\u77e5\u957f\u5f97\u5982\u4f55\uff1f\u6211\u672a\u89c1\u8fc7\u4f60\uff0c\u751a\u81f3\u4e0d\u77e5\u9053\u4e16\u4e0a\u662f\u4e0d\u662f\u6709\u4f60\uff1f\u4f46\u6211 \u65e2\u542c\u5988\u5988\u65f6\u5e38\u8c08\u8bb2\uff0c\u5c31\u5728\u5fc3\u4e2d\u753b\u51fa\u4f60\u7684\u5f62\u8c61\uff0c\u60f3\u50cf\u4f60\u662f\u600e\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u73b0\u5728\u89c1\u5230\u4e86\uff0c\u4f60\u679c\u7136 \u50cf\u6211\u54e5\u54e5\u4e00\u6837\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u542c\u7edb\u971e\u6240\u8bf4\uff0c\u5979\u6bcd\u4eb2\u7adf\u4f3c\u5c06\u6211\u7239\u5f53\u6210\u4eb2\u4eba\u4e00\u822c \uff0c\u4e3a\u4f55\u6211\u7239\u7239\u5374\u4e0d\u5927\u63d0\u8d77\u5979\uff1f\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u5510\u54e5\u54e5\uff0c\u4f60\u53c8\u5728\u60f3\u4ec0\u4e48\u5566\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211 \u4e5f\u5728\u60f3\uff0c\u4f60\u4e5f\u771f\u50cf\u6211\u7684\u59b9\u59b9\u3002\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u771f\u7684\uff1f\u90a3\u4f60\u559c\u6b22\u6211\u4e48\uff1f\u201d\u4fa7\u8138\u51dd\u776c\uff0c\u6d3b\u781a\u51fa\u4e00 \u4e2a\u5a07\u61a8\u7684\u5973\u513f\u795e\u6001\uff0c\u5510\u7ecf\u592d\u7b11\u9053\uff0c\u201c\u5f53\u7136\u559c\u6b22\u4f60\u5566\uff0c\u4f60\u5c31\u50cf\u4e00\u4e2a\u5c0f\u767e\u7075\u9e1f\uff0c\u6211\u6709\u4ec0\u4e48\u6101\u95f7\uff0c \u7ed9\u4f60\u53fd\u53fd\u5495\u5495\u7684\u4e00\u53eb\uff0c\u5c31\u4ec0\u4e48\u6101\u95f7\u90fd\u6ca1\u6709\u5566\uff01\u201d\u90b9\u7ece\u971e\u9053\uff1a\u201c\u55ef\uff0c\u6211\u4e5f\u5f88\u6b22\u559c\u548c\u4f60\u73a9\u3002\u201d\u4e24 \u4eba\u90fd\u662f\u4e00\u7247\u65e0\u90aa\uff0c\u4e0d\u77e5\u4e0d\u515a\u7684\u8f7b\u8f7b\u642d\u624b\uff0c \u3000\u3000\u6708\u5149\u900f\u8fc7\u82b1\u6811\uff0c\u6ee1\u5730\u82b1\u5f71\u6276\u758f\uff0c\u5510\u7ecf\u5929\u5ffd\u53c8\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\uff0c\u60f3\u51b0\u5ddd\u5929\u5973\u4e5f\u662f\u6781\u7231\u82b1\u8349\u7684 \u4eba\uff0c\u82e5\u7136\u5979\u4e5f\u5728\u8fd9\u513f\uff0c\u5728\u8fd9\u8309\u8389\u82b1\u4e2d\u540c\u884c\uff0c\u8fd9\u60c5\u666f\u8be5\u591a\u7f8e\u5999\uff01\u5076\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u5728\u8fdc\u5904\u7684\u82b1\u4e1b \u4e2d\uff0c\u9732\u51fa\u4e00\u4e2a\u5c11\u5973\u7684\u534a\u8fb9\u9762\u5b54\u3002 \u3000\u3000\u900f\u8fc7\u82b1\u4e1b\uff0c\u4f46\u89c1\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\u51dd\u671b\u7740\u81ea\u5df1\uff0c\u4f3c\u6028\u5982\u5bb8\uff0c\u6708\u5149\u6620\u5f97\u90a3\u5c11\u5973\u7684\u9762\u5b54\u5982 \u540c\u767d\u7389\uff0c\u7f8e\u5230\u6781\u70b9\uff0c\u4e5f\u201c\u51b7\u201d\u5230\u6781\u70b9\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u5510\u7ecf\u5929\u7684\u5fc3\u5934\u5c31\u4f3c\u6709\u4e00\u80a1\u7535\u6d41\u901a\u8fc7\uff0c\u5168\u8eab \u98a4\u6296\uff0c\u84e6\u7136\u5c16\u53eb\u4e00\u58f0\uff0c\u98de\u8eab\u6251\u53bb\u3002\u90b9\u7edb\u971e\u53eb\u9053\uff1a\u201c\u5510\u54e5\u54e5\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u662f\u90a3\u8ba8\u538c\u7684\u4eba\u6765\u4e86\u4e48 \uff1f\u201d\u5979\u8fd8\u4ee5\u4e3a\u662f\u5510\u7ecf\u5929\u53d1\u73b0\u4e86\u90a3\u9ebb\u75af\u7684\u8e2a\u8ff9\uff0c\u4e00\u62ac\u5934\uff0c\u89c1\u4e00\u4e2a\u79c0\u53d1\u5e76\u80a9\u7684\u5c11\u5973\u4ece\u82b1\u4e2d\u5954\u51fa\uff0c \u5929\u59ff\u56fd\u8272\uff0c\u95ed\u6708\u7f9e\u82b1\uff0c\u4e0d\u89c9\u5446\u4e86\uff01 \u3000\u3000\u4f46\u542c\u5f97\u5510\u7ecf\u5929\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u51b0\u5a25\uff0c\u51b0\u5a25\uff01\u201d\u90a3\u5c11\u5973\u56de\u5934\u4e00\u671b\uff0c\u7adf\u7136\u662f\u90a3\u6837\u51b0\u51b7\u7684\u6028\u6068\u7684 \u773c\u5149\uff01\u90b9\u7edb\u971e\u7981\u4e0d\u4f4f\u6253\u4e86\u4e2a\u5bd2\u6218\u3002\u53ea\u89c1\u90a3\u5c11\u5973\u56de\u5934\u4e00\u671b\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u53c8\u8f6c\u8fc7\u4e86\u8eab\uff0c\u62c2\u67f3\u5206\u82b1 \uff0c\u5c31\u597d\u50cf\u795e\u8bdd\u4e2d\u7684\u7d20\u5a25\u9752\u5973\uff0c\u5189\u5189\u800c\u6765\uff0c\u5189\u5189\u800c\u6ca1\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5927\u4ecd\u662f\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u51b0\u5a25\uff0c\u51b0\u5a25\uff01\u6842\u59d0\u59d0\uff0c\u6842\u59d0\u59d0\uff01\u201d\u98de\u8eab\u6025\u8d76\uff0c\u53ef\u601c\u4ed6\u5927\u75c5\u521d\u6108 \uff0c\u9976\u662f\u4f7f\u5c3d\u4e86\u5403\u5976\u7684\u6c14\u529b\uff0c\u4ea6\u662f\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u521a\u521a\u8ffd\u4e0b\u5c71\u5761\uff0c\u52fe\u7740\u4e00\u5757\u77f3\u5934\uff0c\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u5012 \u5730\u4e0a\u3002 \u3000\u3000\u90b9\u7edb\u971e\u6c14\u5598\u5401\u5401\u5730\u4ece\u540e\u8ffd\u5230\uff0c\u89c1\u72b6\u5927\u60ca\uff0c\u6025\u5fd9\u628a\u5510\u7ecf\u5929\u6276\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u8dcc\u4f24\u4e86\u4e48\uff1f\u201d\u5510 \u7ecf\u5929\u4eba\u5982\u6728\u77f3\uff0c\u773c\u5982\u5b9a\u73e0\uff0c\u7adf\u50cf\u662f\u9b42\u7075\u513f\u65e9\u8131\u79bb\u4e86\u8eaf\u4f53\uff0c\u5446\u5446\u5730\u9760\u7740\u90b9\u7edb\u971e\uff0c\u9762\u8272\u5982\u7eb8\uff0c\u6b8a \u65e0\u534a\u70b9\u751f\u6c14\u3002 \u3000\u3000\u90b9\u7edb\u971e\u614c\u9053\uff1a\u201c\u5510\u54e5\u54e5\uff0c\u5510\u54e5\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5510\u7ecf\u5929\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5979 \u6765\u4e86\uff0c\u5979\u53c8\u8d70\u4e86\uff01\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u5979\u662f\u8c01\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5c31\u662f\u6211\u4eec\u521a\u624d\u8bf4\u7684\u90a3\u4f4d\u51b0\u5ddd\u5929\u5973 \uff0c\u5440\uff0c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u80af\u548c\u6211\u8bf4\u8bdd\uff1f\u201d\u90b9\u7edb\u971e\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u60f3\u51b0\u5ddd\u5929\u5973\u65e2\u7136\u662f\u5510\u7ecf\u5927\u7684\u670b\u53cb\uff0c\u5374 \u4e3a\u4f55\u5982\u6b64\uff1f\u4f46\u89c1\u5510\u7ecf\u5929\u81ea\u55df\u81ea\u53f9\uff0c\u7adf\u597d\u50cf\u5fd8\u8bb0\u4e86\u8fd8\u6709\u53e6\u4e00\u4e2a\u5c11\u5973\u5728\u81ea\u5df1\u7684\u8eab\u8fb9\u3002\u90b9\u7edb\u971e\u5fc3\u4e2d \u4e00\u9178\uff0c\u65e2\u66ff\u5510\u7ecf\u5929\u53ef\u601c\uff0c\u53c8\u4e3a\u81ea\u5df1\u96be\u8fc7\uff0c\u4e24\u4eba\u4e45\u4e45\u4e0d\u4f5c\u4e00\u58f0\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u90b9\u7ece\u971e\u8f7b\u8f7b\u8bf4\u9053\uff1a \u201c\u5510\u54e5\u54e5\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u5440\uff0c\u4e16\u95f4\u4e0a\u539f\u6765\u771f\u6709\u8fd9\u4e48\u7f8e\u4e3d\u7684\u5973\u5b50\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u62ab\u661f\u6234\u6708\uff0c\u524d\u6765\u5bfb\u8bbf\uff0c\u5728\u82b1\u4e1b\u4e2d\u6070\u597d\u89c1\u7740\u5510\u7ecf\u5929\u4e0e\u90b9\u7edb\u971e\u5e76\u80a9\u642d\u624b\uff0c\u7b11\u8bdd\u9685\u9685 \u7684\u4eb2\u70ed\u6a21\u6837\uff0c\u4e0e\u753b\u56fe\u4e2d\u6240\u63cf\u7ed8\u7684\u6beb\u65e0\u4e8c\u81f4\uff0c\u51b0\u5ddd\u5929\u5973\u82b3\u5fc3\u6b32\u788e\uff0c\u518d\u4e5f\u4e0d\u7406\u5510\u7ecf\u592d\u7684\u8ffd\u8d76\u547c\u5524 \uff0c\u4e00\u53e3\u6c14\u5954\u51fa\u4e86\u5341\u4f59\u91cc\u8def\uff0c\u5e7d\u840d\u5728\u5c71\u811a\u4e0b\u5c0f\u6eaa\u65c1\u7b49\u5019\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u4e00\u4e2a\u4eba\u56de\u6765\uff0c\u90a3\u5931\u9b42\u843d\u9b44 \u7684\u7965\u513f\uff0c\u7adf\u662f\u524d\u6240\u672a\u89c1\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u53ea\u662f\u4f60\u4e00\u4e2a\u4eba\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ed6 \uff0c\u4ed6\u2026\u2026\u201d\u56de\u5934\u4e00\u671b\uff0c\u7693\u6708\u4e4b\u4e0b\uff0c\u7530\u91ce\u5982\u753b\uff0c\u666f\u7269\u6089\u89c1\uff0c\u53ef\u5c31\u53ea\u6ca1\u89c1\u7740\u5510\u7ecf\u5929\u3002\u51b0\u5ddd\u5929\u5973\u5e76 \u4e0d\u77e5\u5510\u7ecf\u5929\u53d7\u4f24\u521d\u6108\uff0c\u8f7b\u529f\u53d7\u4e86\u5f71\u54cd\uff0c\u6240\u4ee5\u8feb\u4e0d\u4e0a\u81ea\u5df1\uff0c\u8bef\u4f1a\u66f4\u589e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff0c\u539f\u6765\u4ed6\u7684\u547c \u5524\u8ffd\u8d76\uff0c\u90fd\u662f\u505a\u51fa\u6765\u7684\uff0c\u66f4\u89c9\u5fc3\u9178\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\u4e0d\u6765\u4e86\u3002\u201d\u5e7d\u840d\u60ca\u9053\uff1a\u201c\u4f60\u89c1\u5230\u4e86 \u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u548c\u4f60\u540c\u6765\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u4f46\u89c9\u5343\u822c\u60c5\u7eea\uff0c\u7ea0\u7ed3\u5fc3\u5934\uff0c\u81ea\u5df1\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u4f4e\u4f4e\u7684\u555c \u4f4d\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u60f3\u8d77\u4e86\u5510\u7ecf\u5929\u521d\u4e0a\u51b0\u5cf0\u7684\u60c5\u666f\uff0c\u60f3\u8d77\u4e86\u5bab\u4e2d\u6bd4\u5251\u3001\u56ed\u5185\u9898\u8054\u2026\u2026\u79cd\u79cd\u4ee4\u4eba\u96be\u4ee5 \u5fd8\u6000\u7684\u5f80\u4e8b\uff0c\u8033\u8fb9\u9690\u9690\u542c\u5f97\u5e7d\u840d\u81ea\u8a00\u81ea\u8bed\u5730\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u82e5\u77e5\u5c18\u4e16\u662f\u8fd9\u822c\u70e6\u607c\uff0c\u8fd8\u4e0d\u5982\u56de\u51b0 \u5bab\u7684\u597d\u3002\u201d\u5ffd\u800c\u53c8\u60f3\u8d77\u4e86\u5510\u7ecf\u5929\u4e3a\u5979\u6240\u9898\u7684\u90a3\u526f\u5bf9\u8054\uff1a\u201c\u6708\u8272\u65e0\u75d5\uff0c\u7eff\u7a97\u6731\u6237\u5e74\u5e74\u7ed5\uff1b\u4ed9\u59b9 \u6709\u6068\uff0c\u78a7\u6d77\u9752\u5929\u591c\u591c\u5fc3\u3002\u201d\u66f4\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u7eb5\u58f0\u957f\u7b11\uff0c\u51b0\u5ddd\u5929\u5973\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u91d1\u4e16\u9057\u6491\u7740\u94c1\u62d0\uff0c\u4e00\u8df3\u4e00\u8df3\u5730\u4ece\u6811\u836b\u4e2d \u8df3\u51fa\u6765\uff0c\u4ed6\u4e0d\u77e5\u4ece\u54ea\u513f\u5077\u6765\u4e86\u4e00\u5957\u5112\u51a0\u5112\u670d\uff0c\u6253\u626e\u8d77\u6765\uff0c\u5012\u6709\u51e1\u5206\u50cf\u5510\u7ecf\u5929\u7684\u6837\u5b50\uff0c\u8fd9\u8eab\u670d \u9970\uff0c\u886c\u7740\u4ed6\u90a3\u6491\u7740\u94c1\u62d0\u8df3\u8dc3\u987d\u76ae\u7684\u795e\u6c14\uff0c\u5927\u662f\u4e0d\u4f26\u4e0d\u7c7b\u3002\u51b0\u5ddd\u5929\u5973\u607c\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u91d1 \u4e16\u9057\u563b\u563b\u7b11\u9053\uff1a\u201c\u7b11\u4f60\uff01\u201d\u82e5\u5728\u5e73\u65e5\u51b0\u5ddd\u5929\u5973\u5fc5\u7136\u53d1\u6012\uff0c\u6b64\u523b\u4f46\u89c9\u5fc3\u795e\u4e0d\u5b9a\uff0c\u5bf9\u4e00\u5207\u7684\u53cd\u5e94 \u4e5f\u90fd\u4f3c\u4e4e\u9ebb\u6728\u4e86\u3002\u91d1\u4e16\u9057\u7eed\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e00\u5fc3\u4e00\u610f\u60f3\u89c1\u4ed6\u4e48\uff1f\u5982\u4eca\u89c1\u4e86\uff0c\u4e0d\u559c\u53cd\u60b2\uff0c\u8fd9\u5c82\u4e0d \u5927\u662f\u53ef\u7b11\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8c01\u8981\u4f60\u7ba1\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u7ba1\uff0c\u4f60\u8fd8\u8499\u5728\u9f13\u91cc\u5462\u3002\u5176\u5b9e \u4e5f\u597d\uff0c\u8fdf\u54ed\u4e0d\u5982\u65e9\u54ed\uff0c\u54ed\u4e2a\u75db\u5feb\uff0c\u5fc3\u91cc\u5c31\u8212\u670d\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u7ed9\u4ed6\u4e00\u8bf4\uff0c\u773c\u6cea\u53cd\u800c\u5fcd\u7740\u4e0d\u6d41 \u3002\u91d1\u4e16\u9057\u53c8\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u90a3\u753b\u56fe\u753b\u5f97\u5982\u4f55\uff0c\u662f\u4e0d\u662f\u4f20\u795e\u4e4b\u6781\uff01\u201d\u51b0\u5ddd\u5929\u5973\u4e00\u607c\uff0c\u55e4\u7684\u4e00\u58f0 \uff0c\u5c06\u90a3\u7f8a\u76ae\u753b\u56fe\u6495\u4e3a\u4e24\u534a\u3002\u91d1\u4e16\u9057\u62cd\u638c\u7b11\u9053\uff1a\u201c\u6495\u4e86\u66f4\u597d\uff0c\u4e50\u5f97\u5fc3\u65e0\u7275\u6302\uff0c\u5e72\u5e72\u51c0\u51c0\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7684\u8bf4\u8bdd\u5b9e\u662f\u53e5\u53e5\u5fc3\u5b58\u6311\u62d4\uff0c\u8fde\u5e7d\u840d\u4e5f\u542c\u5f97\u51fa\u6765\u3002\u51b0\u5ddd\u5929\u5973\u5374\u662f\u5fc3\u795e\u52a8\u8361\uff0c\u89c9\u5f97\u4ed6 \u7684\u8bdd\u4e5f\u6709\u51e0\u5206\u9053\u7406\uff1a\u771f\u662f\u4e00\u5207\u6487\u5f00\uff0c\u8ba9\u5fc3\u5934\u5e72\u5e72\u51c0\u51c0\u7684\u597d\u3002\u5e7d\u840d\u9053\uff1a\u201c\u5c0f\u516c\u4e3b\uff0c\u54b1\u4eec\u8d70\u5427\u3002 \u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u4eec\u8fd8\u662f\u56de\u8f6c\u51b0\u5bab\u7684\u597d\uff01\u201d\u51b0\u5ddd\u5929\u5973\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u5982\u4f55\u77e5\u6211\u7684\u6765 \u5386\uff1f\u201d\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u53f9\u4e86\u53e3\u6c14\uff0c\u6362\u4e86\u4e00\u526f\u53e3\u543b\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u8fd9\u4e16\u4e0a\u7684\u4eba\u672c\u6765\u5c31\u6ca1\u6709 \u51e0\u4e2a\u597d\u7684\uff01\u5b81\u4e0e\u9e1f\u517d\u540c\u7fa4\uff0c\u83ab\u4e0e\u4e16\u4eba\u76f8\u5904\uff0c\u4f60\u5982\u4eca\u76f8\u4fe1\u4e86\u5427\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5446\u5446\u4e0d\u8bed\uff0c\u91d1\u4e16\u9057\u53c8\u9053\uff1a\u201c\u5728\u8fd9\u5c18\u4e16\u4e4b\u4e2d\u6df7\uff0c\u6211\u4e5f\u538c\u5026\u6781\u4e86\u3002\u4f60\u7684\u51b0\u5bab\u6709\u5982\u4e16 \u5916\u6843\u6e90\uff0c\u4e22\u5f03\u4e0d\u4f4f\uff0c\u771f\u771f\u53ef\u60dc\u3002\u4e0d\u5982\u54b1\u4eec\u90fd\u56de\u53bb\uff0c\u8bf7\u4f60\u501f\u51b0\u5bab\u4e00\u89d2\uff0c\u8ba9\u6211\u5b89\u5c45\u3002\u201d\u5e7d\u840d\u6309\u637a \u4e0d\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u7b80\u76f4\u4e0d\u77e5\u81ea\u91cf\uff0c\u5c0f\u516c\u4e3b\u80af\u8ba9\u4f60\u8fd9\u81ed\u9ebb\u75af\u6cbe\u6c61\u4e86\u6211\u4eec\u4ed9\u5c71\u7684\u80dc\u666f\uff01\u201d\u91d1 \u4e16\u9057\u9762\u8272\u4e00\u6c89\uff0c\u84e6\u7136\u4e00\u58f0\u602a\u7b11\uff0c\u94c1\u62d0\u4e00\u62a1\uff0c\u4f5c\u52bf\u6b32\u51fb\uff0c\u5e7d\u840d\u65e9\u6709\u9632\u5907\uff0c\u62d4\u51fa\u51b0\u5251\uff0c\u5374\u95ea\u5728\u51b0 \u5ddd\u5929\u5973\u8eab\u540e\uff0c\u51b0\u5ddd\u5929\u5973\u53cc\u773c\u671b\u5929\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff0c\u56de\u4e0d\u56de\u53bb\uff0c\u6211\u81ea\u6709\u4e3b\u5f20\uff0c\u4e0d\u5fc5\u4f60\u591a \u7ba1\u95f2\u4e8b\u3002\u4f60\u8bf4\u8bdd\u65e0\u793c\uff0c\u6211\u4e5f\u4e0d\u7a79\u4f60\u8ba1\u8f83\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u671b\u4e86\u51b0\u5ddd\u5929\u5973\u4e00\u773c\uff0c\u50cf\u4e2a\u6cc4\u6c14\u7684\u76ae\u7403\u4e00\u6837\uff0c\u5c06\u94c1\u62d0\u7f13\u7f13\u6536\u56de\uff0c\u9053\uff1a\u201c\u597d\uff0c\u770b\u5728\u4f60 \u7684\u4efd\u4e0a\uff0c\u6211\u4e5f\u4e0d\u4e0e\u8fd9\u5c0f\u4e2b\u5934\u8ba1\u8f83\u3002\u201d\u5ffd\u800c\u53c8\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4eec\u90fd\u662f\u88ab\u8fd9\u5c18\u4e16\u5f03\u9057\u4e4b\u4eba\uff0c \u5f7c\u6b64\u6b63\u8be5\u76f8\u60dc\u76f8\u601c\uff0c\u5982\u4eca\u4f60\u53cd\u800c\u5c06\u6211\u770b\u4f5c\u5bf9\u5934\uff0c\u771f\u6ca1\u6765\u7531\uff01\u51e0\u65f6\u4f60\u609f\u5f7b\u4e16\u95f4\u7f18\u6cd5\uff0c\u518d\u8bf4\u4e0e\u6211 \u77e5\u9053\u5427\u3002\u201d\u7b11\u58f0\u9707\u8361\u5c71\u8c37\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8d70\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u7247\u832b\u7136\uff0c\u5e7d\u840d\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8fd9\u75af\u4e10\u5c31\u8c61\u6eba\u6b7b\u7684\u6c34\u9b3c\u4e00\u822c\u3002\u201d\u51b0\u5ddd\u5929\u5973\u542c\u5979\u8bf4 \u5f97\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u6c49\u4eba\u7684\u4f20\u8bf4\uff0c\u8bf4\u6c34\u9b3c\u5fc3\u80a0\u6700\u6bd2\uff0c\u4ed6\u81ea\u5df1\u6eba\u6b7b\u4e86\uff0c\u603b\u60f3 \u627e\u4e2a\u66ff\u8eab\uff0c\u4e00\u77e5\u9053\u6709\u8c01\u53d7\u4e86\u59d4\u5c48\uff0c\u4fbf\u5343\u65b9\u767e\u8ba1\u7684\u53bb\u5f15\u8bf1\u4ed6\uff0c\u53eb\u4ed6\u4e5f\u6295\u6c34\u81ea\u5c3d\u3002\u54fc\uff0c\u54fc\u3002\u4f60\u770b \u4ed6\u521a\u624d\u8bf4\u4e86\u90a3\u4e48\u4e00\u5927\u8f66\u7684\u8bdd\uff0c\u65e0\u975e\u662f\u60f3\u4f60\u518d\u4e5f\u4e0d\u7406\u5510\u76f8\u516c\uff0c\u548c\u4ed6\u4e00\u9053\u3002\u8fd9\u5c82\u4e0d\u50cf\u6c49\u4eba\u4f20\u8bf4\u4e2d \u90a3\u79cd\u72e0\u6bd2\u7684\u6c34\u9b3c\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u6ee1\u8179\u6101\u70e6\uff0c\u7ed9\u5979\u4e00\u8bf4\uff0c\u4e5f\u7981\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u4e0b\u5c71\u6765\u5230\u4e00\u5e74\uff0c\u8fd9 \u628a\u53e3\u5374\u5b66\u5f97\u8fd9\u4e48\u5201\u6bd2\u4e86\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4e0d\u4fe1\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u6211\u5fc3 \u4e2d\u81ea\u6709\u4e3b\u610f\uff0c\u4e0d\u5fc5\u4f60\u4e71\u56bc\u820c\u5934\u3002\u201d\u5e7d\u840d\u6447\u4e86\u6447\u5934\uff0c\u4e0d\u6562\u8bf4\u8bdd\u3002\u51b0\u5ddd\u5929\u5973\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c \u54b1\u4eec\u5feb\u53bb\u5ddd\u897f\uff0c\u5f85\u89c1\u8fc7\u6211\u7684\u4f2f\u4f2f\u4e4b\u540e\uff0c\u6211\u5c31\u56de\u8f6c\u51b0\u5bab\uff0c\u518d\u4e5f\u4e0d\u7406\u5c18\u4e16\u4fd7\u4e8b\u4e86\u3002\u201d\u5e7d\u840d\u53f9\u4e86\u53e3 \u6c14\uff0c\u9ed8\u9ed8\u8ddf\u968f\u4e3b\u4eba\u3002 \u3000\u3000\u5510\u7ecf\u5929\u88ab\u90b9\u7edb\u971e\u6276\u56de\u5c4b\u5b50\uff0c\u4e00\u8def\u65e0\u8a00\u3002\u90b9\u7edb\u971e\u751a\u662f\u62c5\u5fc3\uff0c\u770b\u4ed6\u5173\u4e0a\u623f\u95e8\uff0c\u81ea\u5df1\u5374\u4e0d\u6562\u56de \u623f\u53bb\u7761\uff0c\u6084\u6084\u5730\u5728\u4ed6\u5c4b\u5916\u5f98\u5f8a\u3002\u773c\u770b\u660e\u6708\u5df2\u8fc7\u4e2d\u5929\uff0c\u60f3\u6765\u5df2\u662f\u56db\u66f4\u65f6\u5206\uff0c\u5510\u7ecf\u5929\u623f\u4e2d\u5140\u65e0\u534a \u70b9\u58f0\u606f\uff0c\u90b9\u7ece\u971e\u6e10\u89c9\u9732\u51b7\u98ce\u51c9\uff0c\u773c\u795e\u56f0\u5026\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u50bb\u54e5\u54e5\u5927\u7ea6\u5df2\u7ecf\u7761\u4e86\u3002\u201d\u6b63\u60f3\u56de\u623f\uff0c \u5ffd\u89c1\u5510\u7ecf\u5929\u5367\u623f\u7684\u7a97\u95e8\u500f\u5730\u6253\u5f00\uff0c\u4e00\u6761\u767d\u8863\u4eba\u5f71\u7a7f\u7a97\u98de\u51fa\u3002\u90b9\u7edb\u971e\u98de\u8eab\u4e0a\u5c4b\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201d \u5510\u54e5\u54e5\uff0c\u5510\u54e5\u54e5\uff01\u201d\u5510\u7ecf\u5929\u56de\u5934\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u5435\u9192\u4f60\u5a18\uff0c\u591a\u8c22\u4f60\u4eec\u76f8\u6551\u4e4b\u6069\uff0c\u6211\u6709\u4e8b\u5148\u8d70\u4e86 \uff01\u201d\u90b9\u7edb\u971e\u53eb\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff0c\u4f60\u4e0d\u80fd\u8d70\uff01\u201d\u53ea\u89c1\u5510\u7ecf\u5929\u5728\u5c4b\u80cc\u98de\u8eab\u63a0\u8d77\uff0c\u4e09\u8d77\u4e09\u843d\uff0c\u7bad \u4e00\u822c\u7684\u98de\u51fa\u4e86\u56f4\u5899\u3002 \u3000\u3000\u90b9\u7edb\u971e\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u4f60\u5feb\u6765\u5440\uff01\u5510\u54e5\u54e5\u8d70\u5566\uff01\u201d\u6768\u67f3\u9752\u592b\u5987\u4f4f\u897f\u9762\u53a2\u623f\uff0c\u7eb5\u7136\u95fb\u58f0 \u5373\u8d77\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u662f\u96be\u4ee5\u8d76\u5230\uff0c\u5510\u7ecf\u5929\u542c\u5979\u53eb\u558a\uff0c\u8dd1\u5f97\u66f4\u5feb\uff0c\u90b9\u7ece\u971e\u6025\u4e86\uff0c\u4e0d\u7b49\u5988\u5988\uff0c\u7acb \u523b\u4fbf\u8ffd\u3002 \u3000\u3000\u5510\u7ecf\u5929\u867d\u662f\u5927\u75c5\u521d\u6108\uff0c\u8f7b\u8eab\u7684\u529f\u592b\u8fd8\u662f\u8981\u6bd4\u90b9\u7edb\u971e\u597d\u5f97\u591a\uff0c\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fdc\u3002\u90b9\u7edb\u971e\u6025\u9053 \uff1a\u201c\u5510\u54e5\u54e5\uff0c\u4f60\u771f\u7684\u5982\u6b64\u4fbf\u8d70\u4e86\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u5df2\u8dd1\u4e0b\u5c71\u5761\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u611f\u52a8\uff0c\u811a \u6b65\u7a0d\u7f13\uff0c\u62ac\u5934\u53eb\u9053\uff1a\u201c\u971e\u59b9\uff0c\u4f60\u56de\u53bb\u5427\u3002\u660e\u5e74\u4f60\u5230\u5929\u5c71\uff0c\u54b1\u4eec\u8fd8\u53ef\u76f8\u89c1\u3002\u6211\u6709\u8981\u4e8b\uff0c\u975e\u8d70\u4e0d \u53ef\uff0c\u4e0d\u6562\u52b3\u4f60\u8fdc\u9001\u4e86\u3002\u201d\u5306\u5306\u8bf4\u5b8c\uff0c\u7acb\u523b\u53c8\u8dd1\uff0c\u6562\u60c5\u4ed6\u662f\u6015\u518d\u542c\u5230\u90b9\u7edb\u971e\u5e26\u7740\u560e\u54bd\u7684\u547c\u5524\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u53e3\u6c14\u8dd1\u51fa\u4e86\u5341\u591a\u91cc\u5730\uff0c\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u653e\u6162\u811a\u6b65\uff0c\u5fc3\u4e2d\u5374\u662f\u96be\u8fc7\u4e4b\u6781\u3002\u4ed6\u4e3a\u4e86 \u8981\u8ffd\u8e2a\u51b0\u5ddd\u5929\u5973\uff0c\u8feb\u4e0d\u5f97\u5df2\uff0c\u7559\u4e66\u9053\u522b\uff0c\u4e0d\u8f9e\u800c\u884c\uff0c\u5bf9\u6768\u67f3\u9752\u6bcd\u5973\u60c5\u610f\u6bb7\u6bb7\uff0c\u5fc3\u4e2d\u81ea\u611f\u6b49\u759a \u3002\u4ed6\u4e5f\u6599\u5230\u51b0\u5ddd\u5929\u5973\u5fc5\u662f\u524d\u53bb\u5ddd\u897f\uff0c\u5bfb\u8bbf\u5979\u7684\u4f2f\u7236\uff0c\u4f46\u4e00\u8def\u8ffd\u8e2a\uff0c\u5411\u6cbf\u8def\u4e4b\u4eba\u6253\u542c\uff0c\u5374\u4e00\u70b9 \u4e5f\u6253\u542c\u4e0d\u51fa\u51b0\u5ddd\u5929\u5973\u7684\u8e2a\u8ff9\u3002\u95ee\u8d77\u5982\u6b64\u8fd9\u822c\u7684\u4e24\u4e2a\u5c11\u5973\uff0c\u8def\u4eba\u90fd\u8bf4\u6ca1\u6709\u89c1\u8fc7\u3002 \u3000\u3000\u5510\u7ecf\u5929\u60c6\u60c6\u6005\u6005\uff0c\u8d8a\u5cad\u7ffb\u5c71\uff0c\u8fde\u884c\u591a\u65e5\uff0c\u8fdb\u5165\u4e86\u56db\u5ddd\u897f\u9762\u7684\u5df4\u90ce\u5c71\u8109\u4e4b\u4e2d\uff0c\u5df4\u90ce\u5c71\u8109\u5a49 \u8712\u5357\u8d70\uff0c\u8fc7\u4e86\u96c5\u5b89\uff0c\u4fbf\u8fde\u63a5\u5ce8\u5d4b\u5c71\u8109\u3002\u5df2\u90ce\u5c71\u867d\u4e0d\u5982\u96c0\u513f\u5c71\u4e4b\u9669\uff0c\u4f46\u4e00\u8def\u652f\u8109\u7ef5\u5ef6\uff0c\u5c71\u8def\u5374 \u6bd4\u96c0\u513f\u5c71\u957f\u5f97\u591a\u3002\u800c\u4e14\u5c71\u5cad\u5c42\u53e0\uff0c\u6709\u5982\u91cd\u95e8\u6df1\u6237\uff0c\u5cf0\u56de\u8def\u8f6c\uff0c\u66f2\u6298\u4e4b\u6781\uff0c\u5e38\u5e38\u4e00\u4e2a\u5c71\u5934\uff0c\u770b \u4f3c\u6781\u8fd1\uff0c\u8d70\u8d77\u6765\u5374\u5f88\u8fdc\u3002\u5373\u4f7f\u50cf\u5510\u7ecf\u5927\u8fd9\u6837\u5177\u6709\u6781\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u800c\u4e14\u6709\u884c\u5c71\u7ecf\u9a8c\u7684\u4eba\uff0c\u6bcf\u5929 \u6700\u591a\u4e5f\u4e0d\u8fc7\u8d70\u4e00\u767e\u591a\u91cc\u3002\u53ef\u5e78\u7684\u662f\uff0c\u8700\u4e2d\u7684\u5c71\u6c34\u5947\u4e3d\uff0c\u5ce8\u5d4b\u66f4\u662f\u53f7\u79f0\u201c\u5929\u4e0b\u4e4b\u79c0\u201d\uff0c\u4ece\u5df2\u90ce \u5c71\u8109\u5357\u4e0b\uff0c\u8d8a\u8d70\u8d8a\u89c9\u5c71\u6c34\u6e05\u5e7d\uff0c\u5012\u662f\u53ef\u4ee5\u7a0d\u89e3\u80f8\u4e2d\u70e6\u95f7\u3002 \u3000\u3000\u5c71\u4e2d\u751a\u5c11\u4eba\u5bb6\uff0c\u9519\u8fc7\u5bbf\u5934\uff0c\u5728\u6240\u96be\u514d\u3002\u8fd9\u4e00\u65e5\u5510\u7ecf\u5929\u8d70\u591a\u4e86\u8def\uff0c\u5230\u5165\u665a\u65f6\u5206\uff0c\u62ac\u5934\u4e00\u671b \uff0c\u56db\u5904\u6ca1\u6709\u708a\u70df\uff0c\u672c\u6765\u6253\u7b97\u5bfb\u89c5\u4e00\u4e2a\u5ca9\u6d1e\uff0c\u4f4f\u5bbf\u4e00\u5bb5\uff0c\u4f46\u89c1\u660e\u6708\u5347\u8d77\uff0c\u5706\u5982\u7389\u76d8\uff0c\u6240\u5230\u53ca\u5904 \uff0c\u5c71\u6c34\u5982\u753b\uff0c\u4e0d\u89c9\u52a8\u4e86\u8c6a\u5174\uff0c\u8e0f\u6708\u591c\u884c\u3002\u8d70\u4e86\u8bb8\u4e45\uff0c\u5ffd\u89c1\u9762\u524d\u65e0\u6570\u5947\u5cf0\uff0c\u597d\u50cf\u5e73\u5730\u6d8c\u8d77\u7684\u4e00 \u7247\u77f3\u6797\uff0c\u5982\u7b0b\u5982\u7b14\uff0c\u5cf0\u5cf0\u76f8\u8fde\u3002\u6bcf\u4e00\u4e2a\u77f3\u5cf0\u90fd\u662f\u5c0f\u5de7\u73b2\u62e2\uff0c\u6709\u5982\u76c6\u666f\u3002\u6700\u9ad8\u7684\u4e5f\u4e0d\u8fc7\u4e8c\u4e09\u5341 \u4e08\uff0c\u4f46\u5404\u5177\u59ff\u6001\uff0c\u5982\u864e\u5982\u72ee\uff0c\u5982\u718a\u5982\u8c79\u3002\u7aef\u7684\u662f\u4e07\u996c\u671d\u5929\uff0c\u5e72\u5ca9\u7adf\u79c0\u3002\u5510\u7ecf\u5927\u770b\u60ef\u4e86\u897f\u5317\u7684 \u5927\u5c71\uff0c\u5373\u5df4\u90ce\u5c71\u4e00\u8def\u5357\u6765\uff0c\u4e5f\u662f\u96c4\u4f1f\u4e4b\u6781\uff0c\u4e4d\u89c1\u9762\u524d\u8fd9\u4e00\u7247\u77f3\u6797\uff0c\u4e0d\u89c9\u55b7\u55b7\u79f0\u5f02\uff0c\u8d70\u8fd1\u524d\u53bb \u6b23\u8d4f\u3002\u90a3\u7247\u77f3\u6797\uff0c\u604d\u5982\u4e00\u9762\u5c4f\u98ce\uff0c\u906e\u7740\u5929\u5149\uff0c\u4f46\u8d70\u8fd1\u4e4b\u65f6\uff0c\u5ffd\u89c1\u4e24\u5cf0\u76f8\u8fde\u4e4b\u5904\uff0c\u4e2d\u95f4\u5f00\u4e86\u4e00 \u4e2a\u5927\u6d1e\uff0c\u521a\u521a\u53ef\u4ee5\u5bb9\u5f97\u4e00\u4e2a\u4eba\u901a\u8fc7\uff0c\u6708\u5149\u900f\u8fc7\u8fd9\u4e2a\u6d1e\u53e3\uff0c\u7167\u5c04\u4e0b\u6765\uff0c\u91cc\u9762\u8fd8\u6709\u7011\u6f0f\u7684\u6d41\u6c34\u58f0\u3002 \u3000\u3000\u5510\u7ecf\u5929\u597d\u5947\u5fc3\u8d77\uff0c\u722c\u5165\u6d1e\u53e3\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u4e00\u7247\u7a7a\u5730\uff0c\u6742\u82b1\u76db\u5f00\uff0c\u7a7a\u5730\u56db\u8fb9\uff0c\u4ecd\u662f\u65e0\u6570 \u77f3\u5ca9\uff0c\u5176\u95f4\u53c8\u5404\u6709\u8bb8\u591a\u5947\u5f62\u602a\u72b6\u7684\u5ca9\u6d1e\uff0c\u597d\u50cf\u77f3\u6797\u4e4b\u4e2d\uff0c\u53c8\u6709\u597d\u591a\u95e8\u6237\u4e00\u822c\u3002\u5510\u7ecf\u592d\u6361\u4e86\u4e00 \u4e2a\u6700\u5927\u7684\u6d1e\u53e3\uff0c\u722c\u8fdb\u53bb\u770b\uff0c\u8d8a\u5165\u8d8a\u6df1\uff0c\u5ffd\u95fb\u5f97\u91cc\u9762\u9690\u6709\u4eba\u58f0\uff0c\u4e0d\u89c9\u5927\u5947\uff0c\u53c8\u7a7f\u8fc7\u4e00\u4e2a\u6d1e\u53e3\uff0c \u8fd9\u6d1e\u53e3\u5728\u77f3\u5cf0\u4e0a\u7aef\uff0c\u867d\u4e0d\u7b97\u9ad8\uff0c\u4e5f\u6709\u4e8c\u5341\u6765\u4e08\uff0c\u5510\u7ecf\u5929\u65bd\u5c55\u201c\u58c1\u864e\u529f\u201d\u9644\u8eab\u5728\u5ced\u58c1\u4e4b\u4e0a\uff0c\u5411 \u4e0b\u4e00\u671b\uff0c\u5927\u4e3a\u60ca\u8be7\u3002 \u3000\u3000\u4f46\u89c1\u4e0b\u9762\u4e00\u7247\u7a7a\u9614\uff0c\u6ee1\u8c37\u5e7d\u5170\uff0c\u8c37\u4e2d\u53c8\u957f\u51fa\u65e0\u6570\u5c0f\u77f3\u5ca9\uff0c\u6700\u9ad8\u7684\u4e0d\u8fc7\u4e94\u516d\u4e08\uff0c\u602a\u77f3\u78f7\u5ccb \uff0c\u5982\u5251\u5982\u622a\uff0c\u800c\u4e14\u9690\u9690\u6392\u6210\u9635\u52bf\uff0c\u77f3\u9635\u4e2d\u6709\u4e24\u4e2a\u4eba\u4e1c\u7a7f\u897f\u63d2\uff0c\u4f3c\u662f\u88ab\u56f0\u5728\u5185\uff0c\u8ff7\u4e86\u51fa\u8def\uff0c\u770b \u6e05\u695a\u65f6\uff0c\u4e43\u662f\u4e00\u5bf9\u4e2d\u5e74\u7537\u5973\uff0c\u4e24\u4eba\u76f8\u8ddd\u751a\u8fd1\uff0c\u770b\u6765\u53ea\u8981\u7ed5\u8fc7\u4e24\u679d\u77f3\u5ca9\uff0c\u4fbf\u53ef\u78b0\u5934\uff0c\u4f46\u4ed6\u4eec\u7ed5 \u6765\u7ed5\u53bb\uff0c\u660e\u660e\u5f7c\u6b64\u90fd\u53ef\u4ee5\u4ece\u77f3\u9699\u4e2d\u770b\u89c1\u5bf9\u65b9\u8eab\u5f71\uff0c\u5374\u603b\u662f\u8d70\u4e0d\u5230\u4e00\u5904\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5bb6\u5b66\u6e0a\u6e90\uff0c\u4e0d\u4ec5\u6b66\u529f\u9ad8\u6df1\uff0c\u4e5f\u7565\u61c2\u4e00\u4e9b\u5947\u95e8\u516b\u5366\u4e4b\u9635\uff0c\u8fd9\u65f6\u4ed6\u5728\u9ad8\u5904\u4e0b\u671b\uff0c\u65f6\u95f4 \u7a0d\u957f\uff0c\u4fbf\u7ed9\u4ed6\u770b\u51fa\u4e86\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u8fd9\u77f3\u5ca9\u867d\u662f\u5929\u751f\uff0c\u4f46\u5374\u6697\u5408\u8bf8\u845b\u6b66\u4faf\u7684\u516b\u9635\u56fe\u5f62\u52bf\uff0c\u5206\u6210 \u4f11\u3001\u751f\u3001\u60ca\u3001\u675c\u3001\u6b7b\u3001\u666f\u3001\u60ca\u3001\u5f00\u516b\u95e8\uff0c\u82e5\u975e\u627e\u5230\u4e86\u201c\u751f\u95e8\u201d\u95e8\u6237\uff0c\u4efb\u4f60\u5982\u4f55\u778e\u6478\u778e\u649e\uff0c\u4e5f \u8d70\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u77f3\u9635\u6697\u85cf\u751f\u6b7b\u8def\uff0c\u8c37\u4e2d\u8001\u602a\u663e\u5947\u529f\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u7384\u529f\u5185\u8fd0\u3000\u4fa0\u58eb\u7834\u795e\u62db\u3000\u5f02\u5883\u5929\u5f00\u3000\u4e66\u7ae5\u6709\u5947\u9047 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u56de \u7384\u529f\u5185\u8fd0\u3000\u4fa0\u58eb\u7834\u795e\u62db \u5f02\u5883\u5929\u5f00\u3000\u4e66\u7ae5\u6709\u5947\u9047 \u3000\u3000\u5510\u7ecf\u5929\u6697\u6697\u60ca\u5f02\uff0c\u6b63\u60f3\u4e0b\u53bb\u5c06\u4ed6\u4eec\u5e26\u51fa\u9635\u56fe\uff0c\u4f46\u4ed4\u7ec6\u770b\u65f6\uff0c\u5374\u53c8\u770b\u51fa\u6709\u70b9\u201c\u4e0d\u5bf9\u201d\uff0c\u4e00 \u65f6\u95f4\u4e0d\u6562\u9020\u6b21\uff0c\u90a3\u4e24\u4eba\u6b66\u529f\u5f88\u662f\u4e0d\u5f31\uff0c\u65f6\u4e0d\u65f6\u8dc3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u624b\u6500\u77f3\u5ca9\uff0c\u4f46\u90a3\u4e9b\u77f3\u5ca9\u7b14\u76f4\u5149 \u6ed1\uff0c\u65e0\u53ef\u7740\u529b\uff0c\u8bd5\u4e86\u51e0\u6b21\uff0c\u90fd\u4e0d\u6210\u529f\u3002\u53c8\u6709\u4e24\u6b21\u4e24\u4eba\u597d\u50cf\u662f\u65e0\u610f\u4e4b\u4e2d\u5076\u7136\u8d70\u8fd1\u751f\u95e8\uff0c\u5374\u5ffd\u5730 \u6709\u4e00\u9897\u77f3\u5b50\u6253\u6765\uff0c\u77f3\u9635\u4e4b\u4e2d\u95e8\u6237\u72ed\u7a84\uff0c\u90a3\u77f3\u5b50\u53c8\u6253\u5f97\u975e\u5e38\u5de7\u5999\uff0c\u4ee5\u90a3\u5bf9\u4e2d\u5e74\u7537\u5973\u7684\u8eab\u624b\uff0c\u7adf \u7136\u6ca1\u6cd5\u62db\u67b6\uff0c\u7ec8\u4e8e\u53c8\u7ed9\u8feb\u4e86\u56de\u53bb\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u51db\uff0c\u770b\u60c5\u5f62\u8fd9\u77f3\u6797\u4e2d\u7684\u5e7d\u8c37\u7ade\u4f3c\u6709\u9ad8\u4eba\u5728\u5185 \uff0c\u6697\u4e2d\u6446\u5e03\u3002 \u3000\u3000\u90a3\u5bf9\u4e2d\u5e74\u7537\u5973\u4e5f\u4f3c\u89c9\u5bdf\u5230\u4e86\uff0c\u90a3\u7537\u7684\u9996\u5148\u53eb\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u5408\u52a8\u4e86\u597d\u5947\u4e4b\u5ff5\uff0c\u95ef\u5165\u6b64\u95f4\uff0c \u8bf7\u4e3b\u4eba\u6055\u7f6a\u3002\u201d\u5510\u7ecf\u5929\u4e00\u542c\uff0c\u58f0\u97f3\u597d\u719f\uff0c\u6b63\u5728\u5bfb\u601d\uff0c\u5ffd\u542c\u5f97\u8c37\u4e2d\u6709\u4eba\u201c\u5475\u5440\u201d\u53eb\u4e86\u4e00\u58f0\uff0c\u5c16 \u9510\u6e05\u8106\uff0c\u4f3c\u662f\u4e00\u4e2a\u521a\u521a\u53d1\u80b2\u7684\u5c11\u5e74\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u5927\u5947\uff0c\u518d\u770b\u65f6\uff0c\u53ea\u89c1\u8ddd\u79bb\u90a3\u77f3\u9635\u6570\u4e08\u4e4b\u5730\u7684 \u53e6\u4e00\u5806\u4e71\u77f3\u540e\u9762\uff0c\u7a81\u7136\u8dd1\u51fa\u4e2a\u4eba\uff0c\u679c\u7136\u662f\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u5927\u5b69\u5b50\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4ed6\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u662f\u8427\u8001\u5e08\u5417\uff1f\u201d\u90a3\u4e2d\u5e74\u7537\u4e8e\u9053\uff1a\u201c\u662f\u6211\uff0c\u662f\u8427\u9752\u5cf0\uff01\u5440\uff0c\u4f60\u662f\u6c5f \u5357\uff01\u201d\u8bed\u6c14\u4e2d\u5145\u6ee1\u60ca\u8be7\u4e0e\u72c2\u559c\u4e4b\u60c5\u3002\u5510\u7ecf\u5927\u4e5f\u662f\u5341\u5206\u60ca\u8bb6\uff0c\u5728\u897f\u85cf\u4e4b\u65f6\uff0c\u4ed6\u66fe\u89c1\u8fc7\u8427\u9752\u5cf0\u4e00 \u9762\uff0c\u4f46\u90a3\u65f6\u7684\u8427\u9752\u5cf0\u9762\u8272\u67af\u9ec4\uff0c\u76f8\u8c8c\u6e05\u7626\uff0c\u80cc\u5fae\u8be2\u502d\uff0c\u6d3b\u50cf\u4e2a\u79d1\u573a\u5931\u610f\u7684\u8001\u5112\uff0c\u800c\u73b0\u5728\u770b\u6765 \uff0c\u867d\u662f\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u4e0d\u4ee5\u767d\u5927\u7684\u770b\u5f97\u771f\u5207\uff0c\u4f46\u4ea6\u53ef\u89c9\u51fa\u4ed6\u82f1\u59ff\u98d2\u723d\uff0c\u4e0e\u4ee5\u524d\u5224\u82e5\u4e24\u4eba\uff0c\u5e74\u8f7b \u4f55\u6b62\u5341\u5c81\uff01\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u8ba4\u4e0d\u51fa\u4ed6\uff0c\u522b\u6765\u8fd8\u672a\u591f\u4e00\u5e74\uff0c\u4ed6\u600e\u7684\u5374\u5b8c\u5168\u53d8\u4e86\u6837\u5b50\uff1f \u201d\u5510\u7ecf\u5929\u4e0d\u77e5\uff0c\u8427\u9752\u5cf0\u662f\u670d\u4e86\u94c1\u62d0\u4ed9\u7684\u4f18\u6619\u4ed9\u82b1\uff0c\u8fd9\u624d\u8fd4\u8001\u8fd8\u7ae5\u7684\u3002 \u3000\u3000\u6c5f\u5357\u8dd1\u5230\u77f3\u9635\u5916\u9762\uff0c\u53c8\u53eb\u53c8\u8df3\uff0c\u563b\u563b\u54c8\u54c8\u5730\u7b11\u9053\uff0c\u201c\u771f\u662f\u8427\u8001\u5e08\uff0c\u8427\u8001\u5e08\u5440\uff0c\u4e0d\u662f\u4f60\u51fa \u58f0\uff0c\u6211\u7b80\u76f4\u5c31\u4e0d\u6562\u8ba4\u4f60\u3002\u4f60\u600e\u4e48\u80cc\u4e0d\u9a7c\u4e86\uff0c\u8fde\u989d\u4e0a\u7684\u76b1\u7eb9\u4e5f\u6ca1\u6709\u4e86\u3002\u563b\u563b\uff0c\u8fd9\u4f4d\u592a\u592a\u662f\u8c01\uff1f \u54c8\uff0c\u662f\u8427\u5e08\u5a18\uff0c\u8427\u8001\u5e08\uff0c\u4f60\u5927\u559c\u5440\uff0c\u8ba8\u4e86\u5a18\u5b50\u4e86\uff0c\u6211\u6c5f\u5357\u53ef\u8981\u53e8\u6270\u4f60\u4e00\u676f\uff01\u8427\u5e08\u5a18\uff0c\u8427\u8001\u5e08 \u6709\u6ca1\u6709\u548c\u4f60\u63d0\u8d77\u8fc7\u6211\u6c5f\u5357\u7684\u540d\u5b57\uff1f\u201d\u6c5f\u752b\u4e00\u5f00\u53e3\u5c31\u50cf\u8fde\u73e0\u70ae\u4f3c\u7684\u54cd\u4e2a\u4e0d\u505c\uff0c\u90a3\u5973\u7684\u7981\u4e0d\u4f4f\u7b11 \u9053\uff1a\u201c\u5927\u540d\u9f0e\u9f0e\u7684\u6c5f\u5357\u8fd8\u4f1a\u4e0d\u63d0\u8d77\uff0c\u4f60\u662f\u9648\u516c\u5b50\u7684\u4e66\u7ae5\uff0c\u8859\u95e8\u4e4b\u4e2d\u5c31\u6570\u4f60\u6700\u7231\u8bf4\u8bdd\uff01\u201d\u5510\u7ecf \u5929\u53c8\u662f\u4e00\u756a\u60ca\u8be7\uff0c\u9648\u5929\u5b87\u7684\u4e66\u7ae5\u8ddf\u8c01\u5b66\u7684\u6b66\u529f\uff0c\u8def\u6570\u548c\u9648\u5929\u5b87\u7adf\u662f\u5b8c\u5168\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5582\uff0c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u4f60\u5148\u628a\u6211\u653e\u51fa\u6765\u3002\u201d\u6c5f\u5357\u54ed\u4e27\u7740\u8138\u9053\uff1a\u201c\u6211\u600e\u4e48\u80fd\u5c06\u4f60\u653e \u51fa\u6765\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u80fd\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u61c2\u5f97\u8fd9\u53e4\u91cc\u53e4\u602a\u7684\u77f3\u9635\u3002\u201d\u8427\u9752\u5cf0\u9053 \uff1a\u201c\u600e\u4e48\u4f60\u521a\u624d\u53c8\u62ff\u77f3\u5934\u6253\u6211\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u662f\u4f60\u5440\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5176\u4ed6\u4eba\u5c31\u53ef \u4ee5\u6253\u5417\uff1f\u4f60\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\u5566\uff0c\u8fd8\u8fd9\u6837\u987d\u76ae\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6709\u4eba\u8981\u6211\u8fd9\u6837\u505a\u7684\u3002\u201d\u8427\u9752\u5cf0\uff1a\u201c\u8c01 \uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\uff0c\u4e0d\uff0c\u662f\u90a3\u4e2a\u4e00\u5b9a\u8981\u505a\u6211\u5e08\u7236\u7684\u8001\u5bb6\u4f19\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4ec0\u4e48\u8001\u5bb6\u4f19\uff1f\u4f60\u8ddf\u4e86\u4ed6\u591a\u4e45\u4e86\uff1f\u5929\u5b87\u5462\uff1f\u4ed6\u5f85\u4f60\u6709\u5982\u5144\u5f1f\uff0c\u4f60\u600e\u4e48\u5077\u5077\u9003 \u8dd1\uff0c\u62dc\u522b\u4eba\u4e3a\u5e08\uff1f\u4f60\u5077\u8dd1\u51fa\u6765\uff0c\u6709\u591a\u4e45\u4e86\uff1f\u201d\u8427\u9752\u5cf0\u8fde\u73e0\u70ae\u4f3c\u7684\u53d1\u95ee\uff0c\u6c5f\u5357\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5c31 \u53eb\u8d77\u649e\u5929\u5c48\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u5077\u8dd1\u51fa\u6765\uff1f\u6211\u54ea\u91cc\u662f\u8981\u62dc\u522b\u4eba\u4e3a\u5e08\uff1f\u516c\u5b50\u53eb\u6211\u51fa\u6765\u7684\uff0c\u4f60\u4e0d \u660e\u4e0d\u767d\uff0c\u600e\u4e48\u80e1\u4e71\u51a4\u6789\u6211\uff01\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u7b11\u9053\uff1a\u201c\u4ed6\u6027\u6025\uff0c\u4f60\u4e5f\u522b\u6025\uff0c\u9752\u5cf0\u5440\uff0c\u4f60\u5f97\u4e00\u53e5\u4e00 \u53e5\u95ee\u4ed6\uff0c\u8981\u4e0d\u7136\u4ec0\u4e48\u4e5f\u8bf4\u4e0d\u51fa\u6e05\u695a\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5012\u5fd8\u4e86\u6c5f\u5357\u706b\u7206\u7684\u813e\u6c14\u4e86\u3002\u597d\u5427\uff0c\u6211\u4e00\u53e5\u53e5\u95ee\u4f60\uff0c\u9648 \u516c\u5b50\u4e3a\u4ec0\u4e48\u53eb\u4f60\u51fa\u6765\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u9648\u516c\u5b50\uff0c\u4e0d\uff0c\u4e0d\uff0c\u662f\u8001\u7237\u53eb\u6211\u51fa\u6765\u7684\u3002\u4ed6\u53eb\u6211\u5e26\u4e00\u5c01\u4fe1 \u7ed9\u4eac\u5e08\u7684\u5468\u5927\u4eba\u3002\u4ec0\u4e48\u4fe1\uff1f\u4ed6\u5f53\u7136\u4e0d\u4f1a\u8ddf\u6211\u8bf4\u3002\u54c8\uff0c\u53ef\u662f\u6211\u77e5\u9053\uff0c\u8fd9\u4f4d\u5468\u5927\u4eba\u662f\u4ed6\u7684\u59fb\u4eb2\uff0c \u8fd9\u662f\u6211\u5077\u95ee\u4e0a\u623f\u7684\u4e2b\u5934\u5f69\u51e4\uff0c\u5979\u544a\u8bc9\u6211\u7684\u3002\u6211\u8fd8\u77e5\u9053\u4ed6\u4e3a\u7684\u662f\u4ec0\u4e48\u5462\uff01\u5582\u5582\uff0c\u8427\u6559\u5e08\uff0c\u4f60\u53ef \u77e5\u9053\u8001\u7237\u4e3a\u4ec0\u4e48\u7ed9\u6211\u53d6\u540d\u6c5f\u5357\uff01\u539f\u6765\u662f\u4ed6\u60f3\u5ff5\u4ed6\u7684\u5bb6\u4e61\uff0c\u897f\u85cf\u8fd9\u5730\u65b9\uff0c\u6211\u8fd8\u89c9\u5f97\u597d\u73a9\uff0c\u4ed6\u8001 \u7237\u53ef\u53d7\u4e0d\u4e86\uff0c\u8001\u662f\u60f3\u56de\u5bb6\u3002\u6211\u6709\u4e00\u665a\u5077\u542c\u4ed6\u548c\u516c\u5b50\u8bf4\u8bdd\uff0c\u8001\u7237\u8bf4\u8fd9\u6b21\u4ed6\u505a\u4e86\u4ec0\u4e48\u8fce\u63a5\u91d1\u74f6\u7684 \u4e13\u4f7f\uff0c\u7acb\u4e0b\u529f\u52b3\uff0c\u53ef\u60dc\u798f\u5927\u4eba\uff0c\u54fc\u798f\u5eb7\u5b89\u90a3\u5c0f\u5b50\u4e0d\u80af\u7ed9\u4ed6\u4fdd\u594f\uff0c\u8fd8\u662f\u53eb\u4ed6\u56de\u8428\u8fea\u53bb\u505a\u5ba3\u6170\u4f7f \uff0c\u8001\u7237\u56e0\u6b64\u4fbf\u60f3\u5230\u5199\u4fe1\u7ed9\u4ed6\u7684\u4eb2\u5bb6\u5468\u5927\u4eba\uff0c\u8bf7\u4ed6\u8f6c\u594f\u7687\u4e0a\uff0c\u76fc\u7687\u4e0a\u5ff5\u5728\u4ed6\u8fd9\u756a\u529f\u52b3\uff0c\u8d66\u4ed6\u56de \u53bb\u3002\u8001\u7237\u8bf4\uff1a\u4f46\u4e07\u91cc\u8fe2\u8fe2\uff0c\u53eb\u8c01\u9001\u4fe1\u624d\u653e\u5fc3\u5f97\u4e0b\uff1f\u54c8\u54c8\uff0c\u8427\u6559\u5e08\uff0c\u4f60\u731c\u5c11\u7237\u4fdd\u4e3e\u8c01\uff1f\u4ed6\u8bf4\u53eb \u6c5f\u5357\u9001\u4fe1\u6700\u59a5\u5f53\uff01\u4f60\u4eec\u8001\u662f\u8bf4\u6211\u591a\u5634\uff0c\u4f1a\u8bf4\u4e0d\u4f1a\u505a\uff0c\u6ca1\u7528\uff01\u5c11\u7237\u5440\uff0c\u4ed6\u53ef\u770b\u91cd\u6211\uff01\u6240\u4ee5\u6211\u8bf4 \u662f\u5c11\u7237\u53eb\u6211\u51fa\u6765\u7684\u3002\u4e5f\u6ca1\u6709\u8bf4\u9519\uff01\u201d \u3000\u3000\u8427\u9752\u5cf0\u4ec5\u4ec5\u95ee\u4e86\u4e00\u53e5\uff0c\u6c5f\u5357\u5c31\u5520\u5520\u53e8\u53e8\u7684\u8bf4\u4e86\u4e00\u5927\u8f66\u5b50\u7684\u8bdd\uff0c\u5510\u7ecf\u5929\u8eb2\u5728\u77f3\u58c1\u7684\u7f1d\u9699\u95f4 \uff0c\u542c\u7740\u4e5f\u4e0d\u89c9\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u6c5f\u5357\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u771f\u7231\u8bf4\u8bdd\uff01\u201d\u8427\u9752\u5cf0\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201d \u5c11\u7237\u600e\u4e48\u8fd9\u6837\u770b\u91cd\u4f60\uff0c\u4ed6\u5077\u5077\u6559\u4e86\u4f60\u7684\u6b66\u529f\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u7740\u5440\uff0c\u4f60\u731c\u5f97\u4e00\u70b9\u4e0d\u9519 \uff01\u5c31\u662f\u53bb\u5e74\u7684\u6625\u5929\uff0c\u90a3\u51e0\u4e2a\u5077\u9a6c\u8d3c\u70e7\u8859\u95e8\uff0c\u5c06\u4f60\u8d76\u8dd1\u4e4b\u540e\uff0c\u6211\u624d\u77e5\u9053\u4e86\u4f60\u8427\u8001\u5e08\u662f\u8eab\u6000\u7edd\u6280 \u7684\u5947\u4eba\uff0c\u54b1\u4eec\u516c\u5b50\u4e5f\u6709\u4e00\u8eab\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u4e8e\u662f\u6211\u5c31\u592e\u6c42\u516c\u5b50\u6559\u6211\uff0c\u516c\u5b50\u90a3\u65f6\u4e00\u4e3a\u9003\u5a5a\uff0c\u4e8c\u4e3a \u8981\u9001\u4f60\u8fd9\u4f4d\u8001\u5e08\uff0c\u4ed6\u6ca1\u7a7a\u6559\u6211\u3002\u540e\u6765\u4ed6\u4ece\u62c9\u8428\u56de\u6765\uff0c\u8fd9\u624d\u6559\u4e86\u6211\u4e00\u4e9b\u7c97\u6d45\u7684\u529f\u592b\u3002\u8981\u4e0d\u662f\u6211 \u61c2\u5f97\u4e00\u70b9\u529f\u592b\uff0c\u4f60\u60f3\uff0c\u4ed6\u600e\u4e48\u653e\u5fc3\u8ba9\u6211\u7ed9\u8001\u7237\u9001\u8fd9\u6837\u91cd\u8981\u7684\u4fe1\u4ef6\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u5fcd\u4e0d\u4f4f\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\u8fd9\u4fe1\u91cd\u8981\uff0c\u4e3a\u4ec0\u4e48\u53c8\u5728\u6b64\u95f4\u803d\u6401\u4e0b\u6765\uff0c\u8fd8\u8ba9\u4ec0\u4e48\u8001 \u5bb6\u4f19\u6536\u4f60\u505a\u5f92\u5f1f\uff1f\u201d\u6c5f\u5357\u53c8\u53eb\u5c48\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u662f\u6709\u610f\u803d\u6401\u7684\uff1f\u6211\u7ecf\u8fc7\u6b64\u95f4\uff0c\u4e5f\u4e0d\u8fc7\u662f\u50cf\u4f60\u8001 \u5e08\u4e00\u6837\uff0c\u5fc3\u4e2d\u597d\u5947\uff0c\u6240\u4ee5\u8dd1\u8fdb\u6765\u77a7\uff0c\u54ea\u77e5\u9053\u5440\uff0c\u4e00\uff0c\u8dd1\u8fdb\u6765\uff0c\u53c8\u50cf\u4f60\u4e00\u6837\uff0c\u88ab\u56f0\u5728\u8fd9\u77f3\u9635\u4e4b \u4e2d\uff0c\u8d70\u4e0d\u51fa\u6765\u4e86\u3002\u201d\u8427\u9752\u5cf0\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u4e0d\u602a\u8d23\u4f60\uff0c\u540e\u6765\uff0c\u4f60\u600e\u4e48\u53c8\u51fa\u6765\u4e86\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\u201c\u6211\u88ab\u56f0\u5728\u77f3\u9635\u4e4b\u4e2d\uff0c\u8d70\u4e0d\u51fa\u6765\uff0c\u809a\u5b50\u53c8\u997f\u3002\u6211\u4e71\u9a82\u4e00\u901a\uff1a\u54c8\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e00\u9a82\uff0c \u5374\u628a\u4eba\u5f15\u51fa\u6765\u4e86\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u662f\u90a3\u4e2a\u8001\u5bb6\u4f19\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u4e00\u4e0d\u9519\u3002\u6211\u9a82\u5440\u9a82\u7684\uff0c\u773c\u775b\u4e00 \u82b1\u3002\u4e00\u4e2a\u7a7f\u7740\u7d2b\u9ec4\u9053\u888d\u7684\u8001\u5bb6\u4f19\u5c31\u5230\u4e86\u6211\u7684\u9762\u524d\u4e86\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4ece\u90a3\u513f\u54ea\u51fa\u6765\u7684\uff0c\u8fd9\u8001\u5bb6\u4f19 \u9053\uff1a\u201c\u4f60\u82e5\u80af\u505a\u6211\u7684\u5f92\u5f1f\uff0c\u6211\u5c31\u5e26\u4f60\u51fa\u53bb\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e8e\u662f\u4f60\u5c31\u80af\u4e86\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e0d \u613f\u610f\u4e5f\u6ca1\u529e\u6cd5\u5440\u3002\u6211\u56f0\u5728\u77f3\u9635\u4e2d\u6574\u6574\u4e00\u5929\uff0c\u6bd4\u4f60\u4eec\u88ab\u56f0\u7684\u65f6\u95f4\u8fd8\u957f\u5f97\u591a\uff0c\u6211\u4e0d\u8981\u5403\u996d\u5417\uff1f\u6211 \u5fc3\u91cc\u867d\u7136\u4e00\u767e\u4e2a\u4e0d\u613f\u610f\uff0c\u53e3\u5934\u4e5f\u8bf4\u80af\u4e86\u3002\u90a3\u8001\u5bb6\u4f19\u7709\u5f00\u773c\u7b11\uff0c\u7275\u7740\u6211\u7684\u624b\u4e1c\u4e00\u7ed5\u897f\u4e00\u7ed5\uff0c\u4e0d \u77e5\u600e\u7684\u5c31\u7a81\u7136\u8d70\u51fa\u6765\u4e86\u3002\u6211\u8bf4\uff1a\u5bf9\u4e0d\u4f4f\uff0c\u4f60\u8981\u6536\u5f92\u5f1f\u5c31\u53e6\u6536\u4e00\u4e2a\u5427\uff0c\u6211\u53ef\u8981\u8d76\u8def\u3002\u90a3\u8001\u5bb6\u4f19 \u9053\uff1a\u4f60\u8fd9\u5b69\u5b50\u771f\u662f\u4e0d\u77e5\u597d\u6b79\uff0c\u522b\u4eba\u7ed9\u6211\u78d5\u5934\uff0c\u6c42\u6211\u4e09\u5927\u4e09\u591c\u6211\u4e5f\u4e0d\u4f1a\u6536\u5462\u3002\u5982\u4eca\u6211\u7acb\u4e0b\u4e86\u8a93 \uff0c\u8981\u5728\u672a\u6b7b\u4e4b\u524d\u6536\u4e00\u4e2a\u8863\u94b5\u4f20\u4eba\uff0c\u4f46\u6211\u53c8\u4e0d\u80af\u8d70\u51fa\u6b64\u8c37\uff0c\u53ea\u597d\u7b49\u8c01\u8d70\u5165\u6765\uff0c\u53ea\u8981\u4ed6\u672a\u6ee1\u5341\u516b \u5c81\u6211\u5c31\u6536\u8c01\uff0c\u8fd9\u5c82\u4e0d\u662f\u4f60\u7684\u9020\u5316\uff1f\u6211\u8bf4\u6211\u5c31\u4e0d\u8981\u4f60\u8fd9\u4e2a\u9020\u5316\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u8fd9\u8001\u5bb6\u4f19\u9053\uff1a\u4f60\u672c \u4e8b\u518d\u5f3a\u767e\u500d\uff0c\u4e5f\u8d70\u4e0d\u6389\uff0c\u4f60\u8d70\u8d70\u770b\u3002\u6211\u4e00\u8d70\uff0c\u4e0d\u77e5\u600e\u7684\u817f\u5f2f\u4e00\u9ebb\u8dcc\u5012\u4e86\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5012\u7ffb\u4e86 \u4e09\u4e2a\u7b4b\u6597\uff0c\u76f4\u7ffb\u5230\u90a3\u8001\u5bb6\u4f19\u8ddf\u524d\uff0c\u8fd9\u624d\u81ea\u7136\u505c\u6b62\uff0c\u817f\u5f2f\u4e5f\u4e0d\u9ebb\u4e0d\u75db\u4e86\uff0c\u90a3\u8001\u5bb6\u4f19\u9053\uff1a\u4f60\u7b2c\u4e8c \u6b21\u9003\u8dd1\uff0c\u5c31\u6ca1\u8fd9\u4e48\u597d\u8fc7\u4e86\uff0c\u6211\u8981\u4f60\u5168\u8eab\u9ebb\u75d2\u75d5\u75db\u4e09\u5929\uff0c\u7b2c\u4e09\u6b21\u518d\u8dd1\uff0c\u6211\u5c31\u628a\u4f60\u6253\u6b7b\u3002\u4ed6\u8bf4\u5f97 \u5f88\u5e73\u6de1\uff0c\u597d\u8c61\u6253\u6b7b\u4e2a\u4eba\uff0c\u6839\u672c\u5c31\u4e0d\u7b97\u4e00\u56de\u4e8b\u3002\u4f46\u4ed6\u7684\u76ee\u5149\u5374\u662f\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\u3002\u6211\u5bb3\u6015\u5566\uff0c\u6211 \u8bf4\u6211\u8981\u7ed9\u6211\u5bb6\u5c11\u7237\u9001\u4fe1\uff0c\u90a3\u8001\u5bb6\u4f19\u8bf4\uff1a\u8c01\u7ba1\u4f60\u7684\u4ec0\u4e48\u5c11\u7237\uff0c\u6211\u8bf4\u8fc7\u7684\u8bdd\u4ece\u4e0d\u66f4\u6539\u3002\u6211\u6ca1\u529e\u6cd5 \uff0c\u53ea\u597d\u7ed9\u4ed6\u5f53\u5f92\u5f1f\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4f60\u8ddf\u4e86\u4ed6\u591a\u4e45\uff1f\u201d\u6c5f\u5357\u5c48\u6307\u5934\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u4e03\u5929\u3002\u201d\u8427 \u9752\u5cf0\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u4f60\u53c8\u8bf4\u8c0e\u4e86\uff01\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u6211\u51e0\u65f6\u8bf4\u8fc7\u8c0e\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u53ea\u6709\u4e03\u5929\uff0c \u4f60\u600e\u4e48\u5b66\u4f1a\u4e86\u6697\u5668\u6253\u7a74\u7684\u529f\u592b\uff1f\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5c31\u662f\u6697\u5668\u6253\u7a74\u7684\u529f\u592b\u5417\uff1f\u6211\u8fd8\u53ea\u9053\u4ed6 \u662f\u6559\u6211\u4e22\u77f3\u5b50\u73a9\u513f\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u542c\u4e86\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u9707\uff0c\u53ea\u4e03\u5929\u529f\u592b\uff0c\u5c31\u5c45\u7136\u80fd\u6559\u4eba\u7528\u77f3\u5b50\u6253\u7a74\uff0c\u8fd9\u8c37\u4e2d\u5f02\u4eba\u7684 \u529f\u592b\u5f53\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\u4e86\u3002\u8427\u9752\u5cf0\u53c8\u9053\uff1a\u201c\u662f\u90a3\u8001\u5bb6\u4f19\u9884\u77e5\u6211\u4eec\u8fdb\u6765\uff0c\u53eb\u4f60\u7528\u77f3\u5934\u6253\u6211\u5417\uff1f\u201d \u6c5f\u5357\u9053\uff1a\u201c\u6562\u60c5\u4ed6\u662f\u77e5\u9053\u3002\u4ed6\u4eca\u665a\u5bf9\u6211\u8bf4\uff0c\u6709\u4e24\u4e2a\u4eba\u8d70\u4eba\u8c37\u4e2d\uff0c\u6211\u65e2\u7136\u6536\u4e86\u5f92\u5f1f\u5c31\u4e0d\u6b22\u559c\u5916 \u4eba\u5230\u6b64\uff0c\u4f60\u7ed9\u6211\u53bb\u7528\u77f3\u5934\u6253\u4ed6\u3002\u4e5f\u4e0d\u5fc5\u4e71\u6253\uff0c\u53ea\u8981\u89c1\u4ed6\u5411\u5de6\u8fb9\u8f6c\u4e86\u4e24\u8f6c\u82e5\u7136\u53c8\u5411\u53f3\u65b9\u8f6c\u4e24\u8f6c \uff0c\u518d\u60f3\u8df3\u8d77\u65f6\uff0c\u4f60\u5c31\u6253\u4ed6\u3002\u8427\u8001\u5e08\uff0c\u6211\u4e0d\u77e5\u9053\u662f\u4f60\u4eec\u5440\uff0c\u6211\u89c9\u5f97\u8fd9\u4e5f\u86ee\u597d\u73a9\uff0c\u6211\u5c31\u4f9d\u4ed6\u6240\u6559 \u6765\u53bb\u77f3\u5934\u4e86\u3002\u8427\u8001\u5e08\uff0c\u4f60\u53ef\u4e0d\u80fd\u602a\u6211\u3002\u201d\u8427\u9752\u5cf0\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u8bf4\uff0c\u4f60\u662f\u6ca1\u6cd5\u5c06 \u6211\u4eec\u5e26\u51fa\u53bb\u4e86\u3002\u201d\u6c5f\u5357\u644a\u5f00\u624b\u9053\uff1a\u201c\u786e\u662f\u6ca1\u529e\u6cd5\uff0c\u4f60\u4eec\u82e5\u662f\u809a\u5b50\u997f\uff0c\u6211\u5077\u4e00\u70b9\u4e1c\u897f\u7ed9\u4f60\u5403\u8fd8 \u6210\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u6211\u4eec\u81ea\u5df1\u8bd5\u8bd5\u770b\u3002\u201d\u5de6\u8f6c\u4e24\u8f6c\uff0c\u53f3\u8f6c\u4e24\u8f6c\uff0c\u8f6c\u6765\u8f6c\u53bb\uff0c\u5374\u4ecd\u662f\u8d70 \u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8427\u9752\u5cf0\u5927\u4e3a\u7740\u6025\uff0c\u6708\u4eae\u897f\u843d\uff0c\u6b8b\u661f\u660e\u706d\uff0c\u770b\u770b\u53c8\u662f\u9ed1\u591c\u5c06\u901d\uff0c\u6653\u8272\u4e91\u5f00\u3002\u6c5f\u5357\u9053\uff1a\u201c\u8427 \u8001\u5e08\uff0c\u54b1\u4eec\u95f9\u4e86\u4e00\u665a\u5566\uff0c\u4f60\u997f\u4e0d\u997f\uff1f\u6211\u56de\u53bb\u5077\u70b9\u4e1c\u897f\u7ed9\u4f60\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e0d\u7528\u3002\u201d\u6414\u5934\u6293 \u8033\uff0c\u65e0\u6cd5\u8131\u8eab\u3002\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u60ac\u5ca9\u4e0a\u73b0\u51fa\u8eab\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8427\u5148\u751f\uff0c\u4e45\u8fdd\u4e86\uff01\u201d\u500f \u5982\u82cd\u9e70\u5c55\u7fc5\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u4e00\u63a0\u800c\u4e0b\u3002 \u3000\u3000\u8427\u9752\u5cf0\u770b\u6e05\u695a\u4e86\uff0c\u559c\u51fa\u671b\u5916\u9053\uff1a\u201c\u5510\u76f8\u516c\uff0c\u4f60\u600e\u4e48\u4e5f\u5230\u4e86\u8fd9\u513f\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u50cf\u4f60\u4eec \u4e00\u6837\uff0c\u4e5f\u662f\u52a8\u4e86\u597d\u5947\u4e4b\u5ff5\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u811a\u6b65\u4e0d\u505c\uff0c\u76f4\u4eba\u77f3\u9635\u4e4b\u4e2d\u3002\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u5582\uff01\u8d70\u8fdb \u53bb\u8d70\u4e0d\u51fa\u6765\u7684\uff0c\u6211\u4e0d\u8ba4\u8bc6\u4f60\uff0c\u6211\u53ef\u4e0d\u80fd\u7ed9\u4f60\u591a\u5077\u4e00\u4efd\u4e1c\u897f\u3002\u201d\u4f46\u89c1\u5510\u7ecf\u5929\u5fae\u5fae\u542b\u7b11\uff0c\u5e26\u7740\u8427 \u9752\u5cf0\u592b\u5987\uff0c\u5de6\u8fb9\u4e00\u515c\uff0c\u53f3\u8fb9\u4e00\u7ed5\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u5df2\u8d70\u51fa\u77f3\u9635\u3002 \u3000\u3000\u6c5f\u5357\u770b\u5f97\u7741\u5927\u773c\u775b\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e2a\u5927\u6709\u672c\u4e8b\u4e4b\u4eba\uff0c\u4f60\u662f\u8c01\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4ed6\u66fe\u6551 \u8fc7\u4f60\u5bb6\u516c\u5b50\u2026\u2026\u201d\u6c5f\u5357\u622a\u7740\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u6211\u77e5\u9053\u5566\uff0c\u4f60\u662f\u5510\u7ecf\u5927\u3002\u5510\u76f8\u516c\uff0c\u5c11\u7237\u548c\u6211\u8c08\u8fc7\u4f60 \uff0c\u4ed6\u8bf4\u4f60\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u4e3e\u4e16\u65e0\u53cc\u3002\u5582\uff0c\u5582\uff0c\u4f60\u80fd\u4e0d\u80fd\u5e26\u6211\u51fa\u8fd9\u4e2a\u5e7d\u8c37\uff1f\u6211\u521a\u624d\u7684\u6d3b\u4f60\u90fd\u542c\u5230 \u4e86\u662f\u4e0d\u662f\uff0c\u6211\u8fd8\u8981\u8d76\u7740\u7ed9\u516c\u5b50\u9001\u4fe1\uff0c\u5618\uff0c\u4f60\u80fd\u4e0d\u80fd\u5e26\u6211\u51fa\u53bb\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u9759\u4e00\u4f1a\u513f\uff0c\u6211\u81ea\u6709\u5206\u6570\u3002\u201d\u8f6c\u8eab\u5bf9\u8427\u9752\u5cf0\u9053\uff1a\u201c\u8427\u5148 \u751f\uff0c\u606d\u559c\u4f60\u5475\uff0c\u51e0\u65f6\u8ba8\u7684\u65b0\u5a18\u5b50\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u53bb\u5e74\u56de\u5230\u6210\u90fd\u4e4b\u540e\uff0c\u5373\u91cd\u8fd4\u9752\u57ce\u95e8\u4e0b\u3002 \u5979\uff0c\u5979\u4e5f\u8fd8\u5728\u6210\u90fd\uff0c\u7b49\u7740\u6211\u3002\u201d\u7ed9\u5510\u7ecf\u5929\u4ecb\u7ecd\u65b0\u5987\uff0c\u539f\u6765\u5c31\u662f\u8868\u59b9\u5434\u7edb\u4ed9\u3002\u4ed6\u4eec\u4e8c\u4eba\u672c\u662f\u9752 \u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u53ea\u56e0\u5f53\u5e74\u8427\u9752\u5cf0\u75f4\u604b\u8c22\u4e91\u771f\uff0c\u5434\u7ee6\u4ed9\u4e0d\u6562\u8868\u9732\u5fc3\u610f\uff0c\u540e\u6765\u8427\u9752\u5cf0\u5728\u51b0\u5bab\u4e4b\u5916\u91cd \u9047\u8c22\u4e91\u771f\uff0c\u77e5\u9053\u8c22\u4e91\u771f\u5df2\u5ac1\u4e86\u94c1\u62d0\u4ed9\uff0c\u53c8\u77e5\u9053\u5434\u7edb\u4ed9\u8fd8\u5728\u7b49\u7740\u4ed6\uff0c\u4e8e\u662f\u9042\u79bb\u5f00\u897f\u85cf\uff0c\u56de\u5230\u6210 \u90fd\uff0c\u5411\u5434\u7edb\u4ed9\u6c42\u5a5a\uff0c\u81ea\u7136\u662f\u4e00\u6c42\u5373\u5141\u3002\u8427\u9752\u5cf0\u56db\u5341\u591a\u5c81\u59cb\u505a\u65b0\u90ce\uff0c\u8bf4\u6765\u751a\u662f\u626d\u6ce5\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u4eec\u592b\u5987\u6b32\u4e0a\u54ea\u513f\uff1f\u600e\u4e48\u4e5f\u7ecf\u8fc7\u6b64\u95f4\uff1f\u201d\u8427\u9752\u5cf0\uff1a\u201c\u53bb\u5e74\u6211\u548c\u5929\u5b87\u4e0a\u5ff5\u9752 \u5510\u53e4\u62c9\u5c71\uff0c\u5f97\u89c1\u51b0\u5ddd\u5929\u5973\uff0c\u77e5\u9053\u5979\u5c31\u662f\u6842\u534e\u751f\u7684\u5973\u513f\uff0c\u56de\u6765\u4e4b\u540e\uff0c\u4fbf\u6b32\u5411\u5979\u7684\u6015\u7236\u5192\u5ddd\u751f\u8001 \u524d\u8f88\u62a5\u6b64\u559c\u8baf\uff0c\u53ea\u56e0\u4fd7\u52a1\u803d\u6401\u2026\u2026\u201d\u6c5f\u5357\u63d2\u7b11\u9053\uff1a\u201c\u8427\u8001\u5e08\uff0c\u4f60\u6210\u5bb6\u7acb\u4e1a\uff0c\u600e\u80fd\u8bf4\u662f\u4fd7\u52a1\uff1f \u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4e0d\u8981\u6253\u65ad\u8427\u8001\u5e08\u7684\u8bdd\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u53ea\u56e0\u4fd7\u52a1\u803d\u6401\uff0c\u81f3\u4eca\u672a\u66fe\u62dc\u89c1 \u3002\u6070\u597d\u53c8\u542c\u5230\u4e00\u6869\u4e8b\u60c5\uff0c\u975e\u5f97\u67e5\u4e2a\u660e\u767d\uff0c\u5411\u5192\u8001\u524d\u8f88\u7980\u544a\u4e0d\u53ef\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f \u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5192\u8001\u524d\u8f88\u662f\u6b66\u5f53\u540d\u5bbf\uff0c\u5f53\u4eca\u4e2d\u539f\u6b66\u6797\u516c\u8ba4\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u4ed6\u81ea\u5df1\u5b9a\u4e0b\uff0c\u6bcf\u5341\u5e74 \u4e00\u6b21\uff0c\u5f00\u5c71\u7ed3\u7f18\uff0c\u5609\u60e0\u540e\u5b66\u3002\u5982\u4eca\u5341\u5e74\u4e4b\u671f\u53c8\u5c4a\uff0c\u518d\u8fc7\u534a\u6708\uff0c\u5c31\u662f\u4ed6\u5f00\u5c71\u7ed3\u7f18\u4e4b\u671f\u4e86\u3002\u201d\u5510 \u7ecf\u5929\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u521a\u597d\u53ef\u4ee5\u8d76\u4e0a\u5417\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4f46\u4eca\u5e74\u4ed6\u5f00\u5c71\u4e4b\u65f6\uff0c\u53ef\u80fd \u6709\u4eba\u4e0e\u4ed6\u4e3a\u96be\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u7741\u5927\u53cc\u8ddf\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u3002\u60f3\u90a3\u5192\u5ddd\u751f\u4e43\u662f\u4e00\u4ee3\u5927\u4fa0\uff0c\u4e0d\u53ea\u6b66\u529f\u5df2\u81fb \u5316\u5883\uff0c\u800c\u4e14\u5fb7\u9ad8\u671b\u91cd\uff0c\u6709\u5982\u6cf0\u5c71\u5317\u6597\uff0c\u5404\u5bb6\u5404\u6d3e\uff0c\u65e0\u4e0d\u666f\u4ef0\uff0c\u6709\u4f55\u4eba\u6562\u4e0e\u4ed6\u4e3a\u96be\uff1f \u3000\u3000\u8427\u9752\u5cf0\u6b47\u4e86\u4e00\u6b47\uff0c\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u542c\u8bf4\u51c6\u5907\u9886\u5934\u6363\u4e71\u7684\u662f\u5025\u4f97\u6d3e\u7684\u4e00\u4e2a\u5947\u4eba\u3002\u201d\u5510\u7ecf\u5929\u5fae \u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5025\u4f97\u6d3e\u7684\u638c\u95e8\u8d75\u7075\u541b\uff0c\u4e0e\u4ee4\u9ad8\u8db3\u5929\u5b87\u5144\uff0c\u5927\u6982\u8fd8\u53ef\u4ee5\u4e89\u4e00\u65e5\u4e4b\u77ed\u957f\u3002\u201d\u8a00\u4e0b \u4e4b\u610f\u662f\u8bf4\uff0c\u8fde\u8d75\u7075\u541b\u4ea6\u4e0d\u8fc7\u5982\u6b64\uff0c\u5176\u4f59\u8bf8\u5b50\u66f4\u4e0d\u8db3\u9053\u3002\u51ed\u4ec0\u4e48\u53bb\u4e0e\u4e2d\u539f\u7684\u7b2c\u4e00\u9ad8\u624b\u4e3a\u96be\uff1a\u8427 \u9752\u5cf0\u5374\u662f\u9762\u8272\u51dd\u91cd\uff0c\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u5025\u4f97\u6d3e\u8fd1\u4e09\u5341\u5e74\u6765\u4eba\u624d\u51cb\u843d\uff0c\u524d\u540e\u4e24\u8f88\u7684\u638c\u95e8\u4eba\u90fd\u591f\u4e0d\u4e0a \u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\uff0c\u6240\u4ee5\u5404\u5927\u5251\u6d3e\u90fd\u4e0d\u628a\u4ed6\u4eec\u653e\u5728\u5fc3\u4e0a\uff0c\u5176\u5b9e\u8fd9\u4e00\u6d3e\u7684\u6b66\u529f\u4e5f\u6709\u5176\u72ec\u7279\u4e4b\u5904\u3002\u201d \u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u6b64\u8bdd\u4e0d\u9519\uff0c\u82e5\u975e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u5c31\u4e0d\u80fd\u6210\u4e3a\u4e00\u5bb6\u3002\u53ea\u662f\u5404\u4eba\u7980\u8d4b\u4e0d\u540c \uff0c\u9886\u609f\u4e0d\u540c\uff0c\u7528\u529f\u7684\u7a0b\u5ea6\u4e0d\u540c\uff0c\u8fd9\u624d\u5206\u51fa\u4e86\u9ad8\u4e0b\u6d45\u6df1\uff0c\u539f\u4e0d\u53ef\u4e00\u6982\u800c\u8bba\uff0c\u6211\u521a\u624d\u56e0\u8d75\u7075\u541b\u7684 \u529f\u592b\u5c1a\u6d45\uff0c\u800c\u8d2c\u4f4e\u4e86\u5025\u4f97\u4e00\u6d3e\uff0c\u8fd9\u662f\u6211\u5931\u8a00\u4e86\u3002\u201d\u5510\u7ecf\u5929\u6bd5\u7adf\u662f\u540d\u95e8\u9ad8\u5f1f\uff0c\u4ece\u5584\u5982\u6d41\u3002 \u3000\u3000\u8427\u9752\u5cf0\u7eed\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u5165\u662f\u5025\u4f97\u6d3e\u4e0a\u4e00\u8f88\u7684\u4eba\u7269\uff0c\u56e0\u89c1\u672c\u6d3e\u6b66\u529f\u4e0d\u632f\uff0c\u65e5\u76ca\u5f0f\u5fae\uff0c\u56e0\u800c \u5728\u4e09\u5341\u5e74\u524d\uff0c\u4fbf\u9009\u4e86\u4e00\u4e2a\u9690\u50fb\u6240\u5728\uff0c\u907f\u4e16\u82e6\u4fee\uff0c\u7a77\u7814\u7956\u5e08\u5251\u8c31\uff0c\u5e76\u521b\u65b0\u62db\u3002\u51e0\u5341\u5e74\u6765\uff0c\u8c01\u4e5f \u4e0d\u77e5\u9053\u4ed6\u7684\u529f\u592b\u7a76\u7adf\u7ec3\u5230\u4e86\u4f55\u7b49\u7a0b\u5ea6\u3002\u6700\u8fd1\u6211\u5728\u4e00\u4e2a\u5076\u7136\u7684\u673a\u4f1a\u4e2d\uff0c\u6253\u542c\u5230\u4ed6\u6709\u51fa\u5c71\u4e4b\u610f\u3002 \u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u4e00\u51fa\u5c71\u5c31\u51c6\u5907\u53bb\u4e0e\u5192\u5927\u4fa0\u8f83\u91cf\uff0c\u597d\u95ef\u540d\u7acb\u4e07\u4e48\uff1f\u54fc\uff0c\u8fd9\u4e5f\u662f\u6c5f\u6e56\u4e0a\u5e38 \u89c1\u4e4b\u4e8b\uff0c\u4f46\u771f\u6b63\u9ad8\u4eba\u5374\u4e0d\u5c51\u4e3a\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4ed6\u82e5\u975e\u4e0e\u4e2d\u539f\u7b2c\u4e00\u9ad8\u624b\u6bd4\u8bd5\uff0c\u5c31\u663e\u4e0d\u51fa\u4ed6\u7684 \u672c\u4e8b\uff0c\u4e5f\u4e0d\u80fd\u91cd\u632f\u5025\u4f97\u7684\u5a01\u98ce\u4e86\u3002\u4e0d\u8fc7\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u542c\u8bf4\u8fd8\u53e6\u6709\u4e00\u56e0\u3002\u201d\u8bf4\u81f3\u6b64\u5904\u5ffd\u5730\u5411\u5510 \u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6050\u6015\u662f\u56e0\u4f60\u800c\u8d77\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff01\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u542c\u8bf4 \u4f60\u66fe\u7528\u5929\u5c71\u795e\u8292\u6253\u4f24\u8fc7\u5341\u4e09\u540d\u5025\u4f97\u9ad8\u624b\uff0c\u6709\u8fd9\u4e8b\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d75\u7075\u541b\u4e5f\u5728\u5176\u5185 \u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4f7f\u51b0\u5251\u7684\u5c11\u5973\u4e0e\u4f60\u4e00\u9053\uff0c\u662f\u4e48\u53f6\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u662f\u51b0\u5ddd\u5929\u5973 \u7684\u4f8d\u5973\u5e7d\u840d\u3002\u53e6\u5916\u8fd8\u6709\u4e00\u4eba\uff0c\u5c31\u662f\u4ee4\u9ad8\u8db3\u9648\u5929\u5b87\u3002\u5025\u4f97\u6d3e\u82e5\u56e0\u6b64\u4e8b\u8bb0\u6068\uff0c\u5f53\u6765\u627e\u6211\uff0c\u4f55\u4ee5\u53bb \u627e\u5192\u5927\u4fa0\uff1f\u201d \u3000\u3000\u8427\u9752\u5cf0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u7236\u5b50\u9690\u5c45\u5929\u5c71\uff0c\u540d\u5934\u6bd4\u5192\u5ddd\u751f\u66f4\u5927\uff0c\u4ed6\u81ea\u5fd6\u672a\u5fc5\u80fd\u80dc\u8fc7\u4ee4\u5c0a\u3002\u800c\u4e14 \u4e0e\u4f60\u4eec\u6bd4\u8bd5\uff0c\u5728\u5929\u5c71\u4e4b\u4e0a\uff0c\u8c01\u6709\u672c\u9886\u4e0a\u53bb\u89c2\u6218\uff0c\u80dc\u8d1f\u65e0\u4eba\u5f97\u77e5\uff0c\u6bd4\u8f83\u4e4b\u4e0b\uff0c\u90a3\u81ea\u7136\u662f\u627e\u5192\u5ddd \u751f\u66f4\u4e0a\u7b97\u4e86\u3002\u4ed6\u4eec\u628a\u90a3\u4e2a\u4f8d\u5973\u5e7d\u840d\u5f53\u6210\u51b0\u5ddd\u5929\u5973\uff0c\u4e0d\u77e5\u5982\u4f55\u5de7\u5ccb\u53c8\u63a2\u542c\u51fa\u51b0\u5ddd\u5929\u5973\u662f\u5192\u5ddd\u751f \u7684\u4f84\u5973\uff0c\u5982\u6b64\u74dc\u845b\u7275\u8fde\u3002\u4ed6\u4eec\u57c2\u590f\u6709\u85c9\u53e3\u8981\u4e0e\u5192\u8001\u524d\u8f88\u4e3a\u96be\u4e86\u3002\u542c\u8bf4\u4ed6\u4eec\u8fd8\u51c6\u5907\u5927\u7ea6\u5916\u6d3e\u80fd \u4eba\uff0c\u5230\u5192\u8001\u524d\u8f88\u5f00\u5c71\u7ed3\u7f18\u4e4b\u65e5\uff0c\u53bb\u95f9\u4e2a\u5929\u7ffb\u5730\u8986\u3002\u6211\u4e00\u6765\u8981\u53bb\u89c1\u5192\u5927\u4fa0\uff0c\u544a\u77e5\u4ed6\u6211\u66fe\u89c1\u8fc7\u51b0 \u5ddd\u5929\u5973\u4e4b\u4e8b\uff0c\u4e8c\u6765\u5c31\u662f\u8981\u8bf7\u4ed6\u63d0\u9632\u6363\u4e71\u3002\u60f3\u5192\u5927\u4fa0\u662f\u4f55\u7b49\u58f0\u671b\uff0c\u7eb5\u80fd\u5728\u4e8b\u53d1\u4e4b\u540e\u9547\u538b\u4e0b\u53bb\uff0c \u4e5f\u662f\u4e0d\u59a5\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u6c89\u601d\u6709\u9877\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5025\u4f97\u6d3e\u7684\u90a3\u4f4d\u602a\u4eba\u8981\u53bb\u4e0e\u5192\u8001\u524d\u8f88\u4e3a\u96be\uff0c\u600e\u4e48\u53cd\u800c\u597d\u5462\uff1f\u201d\u5510\u7ecf\u5929\u5fae\u7b11\u9053 \uff0c\u201c\u54b1\u4eec\u53ef\u6709\u70ed\u95f9\u770b\u4e86\u5475\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u8981\u53bb\u8c12\u89c1\u5192\u8001\u524d\u8f88\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e0d \u9517\u3001\u7b97\u6765\u521a\u597d\u53ef\u4ee5\u53ca\u65f6\u8d76\u5230\u3002\u4f46\u613f\u51b0\u5ddd\u5929\u5973\u4e5f\u80fd\u53ca\u65f6\u8d74\u4f1a\uff0c\u90a3\u65f6\u6211\u4eec\u5012\u8981\u77a7\u77a7\uff0c\u8fd9\u4f4d\u5025\u4f97\u6d3e \u7684\u602a\u4eba\u5230\u5e95\u7ec3\u4e86\u4e9b\u4ec0\u4e48\u5947\u5f02\u7684\u6b66\u529f\uff1f\u5c45\u7136\u6562\u5230\u5192\u8001\u524d\u8f88\u9762\u524d\uff0c\u56fe\u540d\u95ef\u4e07\uff01\u201d\u8427\u9752\u5cf0\u4e00\u542c\uff0c\u4fbf \u77e5\u5510\u7ecf\u5929\u5230\u65f6\u6709\u610f\u51fa\u624b\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u201c\u5192\u8001\u524d\u8f88\u51fa\u624b\uff0c\u90a3\u81ea\u7136\u662f\u5931\u4e86\u8eab\u4efd\u3002\u5510\u7ecf\u5929\u548c \u51b0\u5ddd\u5929\u5973\u6b66\u529f\u6781\u9ad8\uff0c\u5374\u662f\u5c0f\u8f88\uff0c\u6709\u4ed6\u4eec\u4e8c\u4eba\u5728\u573a\uff0c\u8fd9\u786e\u662f\u597d\u6781\u4e86\uff01\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec \u518d\u7b49\u7247\u65f6\uff0c\u5f85\u5929\u8272\u5927\u660e\uff0c\u4fbf\u53ef\u4e00\u8def\u8d70\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u6ee1\u809a\u76ae\u8bf4\u8bdd\uff0c\u95f7\u4e86\u8bb8\u4e45\uff0c\u89c1\u4e24\u4eba\u4e00\u505c\uff0c\u7acb\u523b\u63d2\u53e3\u9053\uff1a\u201c\u5582\uff0c\u8fd8\u6709\u6211\u5462\uff01\u201d\u5510\u7ecf\u5929\u9053 \uff1a\u201c\u4f60\uff0c\u4f60\u4ec0\u4e48\uff1f\u4f60\u6709\u4e86\u4e00\u4e2a\u597d\u5e08\u7236\u8fd8\u8981\u8d70\u5417\uff1f\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u4e8f\u4f60\u662f\u6211\u4eec\u516c\u5b50\u7684\u597d\u53cb\uff0c\u4f60 \u4e0d\u77e5\u9053\u6211\u7ed9\u4ed6\u9001\u8981\u7d27\u7684\u4fe1\u5417\uff1f\u4f60\u600e\u80fd\u4e0d\u5e26\u6211\u53bb\uff1f\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u8fdf\u5728\u8fd9\u4e00\u4f1a\uff0c\u6211\u4e14\u95ee \u4f60\uff0c\u4f60\u4eec\u7684\u516c\u5b50\u597d\u5417\uff1f\u201d\u6c5f\u5357\u9f13\u8d77\u5634\u5df4\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u597d\uff0c\u4e00\u9910\u5403\u4e09\u7897\u5927\u7c73\u996d\uff01\u201d\u5510\u7ecf\u5929\u9053 \uff1a\u201c\u4e0d\u662f\u95f4\u4f60\u8fd9\u4e2a\uff0c\u90a3\u571f\u53f8\u7684\u5973\u513f\u600e\u4e48\u5566\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u5929\u5929\u6253\u626e\uff0c\u50cf\u4e2a\u5c0f\u5a3c\u5987\u4f3c \u7684\uff0c\u671d\u65e9\u591c\u665a\uff0c\u51fa\u53bb\u6253\u730e\uff0c\u90fd\u7ecf\u8fc7\u6211\u4eec\u7684\u8859\u95e8\uff0c\u5c11\u7237\u7b97\u662f\u6015\u4e86\u5979\uff0c\u4ece\u65e9\u5230\u665a\uff0c\u8eb2\u5728\u8859\u5185\uff0c\u7b80 \u76f4\u4e0d\u6562\u51fa\u6765\u3002\u6562\u60c5\u662f\u6015\u78b0\u89c1\u4e86\u5979\uff0c\u88ab\u5979\u4e00\u53e3\u54ac\u53bb\u3002\u201d\u8bf4\u7740\u81ea\u5df1\u7b11\u8d77\u6765\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fcd\u4fca\u4e0d\u7981\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ed6\u4eec\u7684\u5a5a\u4e8b\u5df2\u6210\u5b9a\u5c40\u4e86\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6ca1\u6709\u5440 \uff0c\u516c\u5b50\u7ed9\u4ed6\u4e00\u4e2a\u63a8\u5b57\uff0c\u4e0d\u8fc7\u73b0\u5728\u8bf4\u6e05\u695a\u4e86\uff0c\u662f\u571f\u53f8\u8feb\u8001\u7237\u7b54\u5141\u7684\uff0c\u5230\u660e\u5e74\u6625\u5929\uff0c\u90a3\u4e2a\u5587\u561b\u5e99 \u9020\u8d77\u6765\u4e86\uff0c\u542c\u8bf4\u6709\u4e00\u4e2a\u4ec0\u4e48\u767d\u6559\u7684\u6d3b\u4f5b\u8981\u53bb\u4e3b\u6301\u5f00\u5149\u5927\u5178\uff0c\u90a3\u65f6\u5c31\u8981\u7531\u6d3b\u4f5b\u66ff\u4ed6\u4eec\u8bc1\u5a5a\uff0c\u518d \u4e5f\u9003\u4e0d\u4e86\u4e86\uff01\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u9648\u5929\u5b87\u5ff5\u5ff5\u4e0d\u5fd8\u90a3\u795e\u79d8\u7684\u85cf\u65cf\u5c11\u5973\u829d\u5a1c\uff0c\u4ed6\u5927\u7ea6 \u8fd8\u672a\u77e5\u9053\uff0c\u829d\u5a1c\u5df2\u505a\u4e86\u5723\u5973\uff0c\u660e\u5e74\u6625\u5929\uff0c\u5c31\u8981\u8ddf\u767d\u6559\u7684\u6cd5\u738b\u5230\u8428\u8ff9\u53bb\u53c2\u52a0\u5f00\u5149\u5927\u5178\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5929\u5df2\u5927\u767d\uff0c\u671d\u9633\u900f\u8fc7\u77f3\u6797\u7684\u7a7a\u9699\uff0c\u6d12\u4e0b\u6ee1\u5730\u91d1\u5149\uff0c\u6797\u4e2d\u7684\u5c0f\u6e56\u4e5f\u95ea\u7740\u91d1\u8272\u7684\u6c34\u7eb9\uff0c \u666f\u81f4\u5947\u4e3d\u7edd\u4fd7\u3002\u8427\u9752\u5cf0\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u4ee5\u8d70\u4e86\u5427\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8bf4\u8fc7\u8981\u5e26\u6211\u8d70\u7684\u5440\uff01\u201d \u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\uff0c\u70e6\u4f60\u5e26\u5f15\u6211\u4eec\uff0c\u5411\u4f60\u7684\u5e08\u7236\u8f9e\u884c\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5411\u90a3\u4e2a\u8001\u5bb6\u4f19\u8f9e\u884c \uff0c\u4ed6\u4e0d\u8bb8\u6211\u8d70\u7684\u5440\uff01\u201d\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54ea\u4f4d\u9ad8\u4eba\uff0c\u770b\u4e0a\u4e86\u6211\u8fd9\u4e2a\u4e0d\u6210\u624d\u7684\u5f1f \u5b50\uff1f\u201d\u58f0\u97f3\u5e76\u4e0d\u5f88\u5927\uff0c\u4f46\u5343\u5cf0\u56de\u54cd\uff0c\u649e\u5f97\u77f3\u6797\u5185\u55e1\u55e1\u4f5c\u58f0\u3002\u6c5f\u5357\u8eb2\u5230\u5510\u7ecf\u5929\u80cc\u540e\uff0c\u53ea\u89c1\u5510\u7ecf \u5929\u5408\u4ec0\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u540e\u5b66\u5510\u7ecf\u5929\uff0c\u8bef\u5165\u4ed9\u5883\uff0c\u5c1a\u671b\u6055\u7f6a\u3002\u201d\u58f0\u97f3\u9ad8\u4ea2\u800c\u6e05\uff0c\u597d\u8c61\u4e00\u628a \u5251\u523a\u5165\u77f3\u6797\u4e4b\u4e2d\uff0c\u78b0\u7740\u77f3\u58c1\uff0c\u53d1\u51fa\u91d1\u5c5e\u58f0\u97f3\u3002\u53cc\u65b9\u5404\u663e\u529f\u529b\uff0c\u65d7\u9f13\u76f8\u5f53\u3002\u5510\u7ecf\u5929\u521a\u521a\u628a\u8bdd\u8bf4 \u5b8c\uff0c\u500f\u5730\u773c\u524d\u4e00\u4eae\uff0c\u6e56\u8fb9\u5df2\u591a\u4e86\u4e00\u4eba\uff0c\u7a7f\u7740\u7d2b\u9ec4\u8272\u7684\u9053\u888d\uff0c\u76f8\u8c8c\u5947\u53e4\u3002 \u3000\u3000\u6c5f\u5357\u5413\u5f97\u624b\u98a4\u811a\u9707\uff0c\u8eb2\u5728\u5510\u7ecf\u5929\u80cc\u540e\uff0c\u4e0d\u6562\u9732\u51fa\u5934\u6765\u3002\u90a3\u9ec4\u888d\u9053\u58eb\u5374\u4e0d\u7406\u4ed6\uff0c\u5f84\u5411\u5510\u7ecf \u5929\u8bf4\u9053\uff1a\u201c\u6570\u5341\u5e74\u6765\uff0c\u80fd\u8d70\u51fa\u6211\u7684\u77f3\u9635\u7684\uff0c\u53ea\u6709\u9601\u4e0b\u4e00\u4eba\u3002\u80fd\u8005\u79f0\u5f3a\uff0c\u8fd9\u6709\u4ec0\u4e48\u6055\u7f6a\u7684\u3002\u4f60 \u65e2\u80fd\u8d70\u51fa\u77f3\u9635\uff0c\u60f3\u5fc5\u4e5f\u6709\u80fd\u529b\u5e26\u6211\u8fd9\u4e2a\u4e0d\u6210\u6750\u7684\u5f1f\u5b50\u51fa\u53bb\uff0c\u597d\u5427\uff0c\u4f60\u5c31\u5e26\u5427\uff01\u201d\u5510\u7ecf\u5929\u4e0d\u7531 \u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u521a\u624d\u542c\u8fd9\u9053\u58eb\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u867d\u56e0\u7fa4\u5cf0\u56de\u54cd\uff0c\u6d4b\u4e0d\u51fa\u4ed6\u7684\u5b9e\u9645\u6240\u5728\uff0c\u4f46\u6700\u5c11\u4e5f\u5f53 \u5728\u767e\u4e08\u4e4b\u5916\uff0c\u4ed6\u7adf\u7136\u58f0\u5230\u4eba\u5230\uff0c\u8fd9\u77f3\u6797\u4e2d\u53e6\u6709\u6d1e\u5929\uff0c\u90a3\u662f\u4e0d\u6d88\u8bf4\u4e86\uff0c\u800c\u8fd9\u9053\u58eb\u8eab\u6cd5\u4e4b\u5feb\uff0c\u4e5f \u59d4\u5b9e\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u542c\u4ed6\u73b0\u5728\u7684\u53e3\u6c14\uff0c\u90a3\u5f53\u7136\u662f\u6697\u4e2d\u542b\u6709\u8f83\u91cf\u7684\u610f\u601d\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5438\u4e86\u53e3\u6c14\uff0c\u6697\u8fd0\u5929\u5c71\u6b63\u5b97\u7684\u7384\u529f\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5f85\u4ed6\u4e8b\u60c5\u529e\u4e86\uff0c\u65e5\u540e\u518d\u6765\u8bf7 \u76ca\u3002\u201d\u643a\u7740\u6c5f\u5357\uff0c\u7f13\u7f13\u7684\u6b65\u51fa\u77f3\u6797\u3002\u90a3\u9053\u58eb\u624b\u4e2d\u62ff\u7740\u4e00\u67c4\u62c2\u5c18\uff0c\u4f46\u89c1\u4ed6\u8eab\u5f62\u4e0d\u52a8\uff0c\u4ecd\u662f\u7ad9\u7acb \u539f\u5904\uff0c\u62c2\u5c18\u53ea\u662f\u8f7b\u8f7b\u4e00\u62c2\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u987d\u7ae5\u8fd8\u6ca1\u957f\u7fc5\u5c31\u60f3\u98de\u5566\uff0c\u9601\u4e0b\u53ef\u5f97\u597d\u751f\u7ba1\u6559\u5475\uff01 \u201d\u5510\u7ecf\u5929\u5df2\u5c3d\u5f97\u5929\u5c71\u5fc3\u6cd5\uff0c\u90a3\u62c2\u5c18\u867d\u53ea\u662f\u8f7b\u8f7b\u4e00\u62c2\uff0c\u4ed6\u5df2\u542c\u51fa\u98ce\u58f0\uff0c\u800c\u4e14\u4e0d\u7528\u56de\u5934\uff0c\u5c31\u77e5\u90a3 \u62c2\u5c18\u5df2\u98de\u51fa\u51e0\u6761\u7384\u4e1d\uff0c\u76f4\u523a\u4ed6\u548c\u6c5f\u5357\u7684\u7a74\u9053\u3002\u60f3\u90a3\u62c2\u5c18\u4e1d\u662f\u6781\u5fae\u7ec6\u4e4b\u7269\uff0c\u90a3\u8001\u9053\u7adf\u80fd\u8f7b\u8f7b\u4e00 \u62c2\uff0c\u5c31\u5c04\u51fa\u51e0\u6761\uff0c\u5f53\u4f5c\u523a\u7a74\u7684\u98de\u9488\u4f7f\u7528\uff0c\u8fd9\u771f\u662f\u9632\u4e0d\u80dc\u9632\u3002\u5510\u7ecf\u5929\u8eab\u5f62\u4e00\u95ea\uff0c\u62c9\u7740\u6c5f\u5357\u9053\uff1a \u201c\u5c0f\u5fc3\u70b9\u513f\uff0c\u8fd9\u513f\u6709\u5757\u77f3\u5934\u3002\u201d\u82e5\u4e0d\u7ecf\u610f\u5730\u6321\u4e86\u4e00\u6321\uff0c\u5c06\u672c\u6765\u8981\u5c04\u6c5f\u5357\u7684\u51e0\u6761\u62c2\u5c18\u7384\u4e1d\uff0c\u5168 \u90fd\u6321\u5728\u81ea\u5df1\u7684\u8eab\u4e0a\u3002\u5510\u7ecf\u5929\u867d\u7136\u6697\u8fd0\u7384\u529f\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4e5f\u89c9\u5f97\u8eab\u4e0a\u5341\u51e0\u5904\u7a74\u9053\uff0c\u540c\u65f6\u53d1\u9ebb\uff0c \u597d\u8c61\u7ed9\u8bb8\u591a\u8682\u8681\u53ee\u4e86\u4e00\u53e3\u4f3c\u7684\uff0c\u82e5\u975e\u65e9\u6709\u9632\u5907\uff0c\u51e0\u4e4e\u7740\u4e86\u4ed6\u7684\u6697\u7b97\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u9053\u58eb\u679c \u662f\u529f\u529b\u975e\u51e1\uff0c\u867d\u7136\u8fd8\u53ca\u4e0d\u4e0a\u6211\u59e8\u6bcd\u98de\u82b1\u6458\u53f6\uff0c\u4f24\u4eba\u7acb\u6b7b\u7684\u529f\u592b\uff0c\u6bd4\u8d77\u6211\u6765\uff0c\u5374\u662f\u6df1\u539a\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u83ab\u660e\u6240\u4ee5\uff0c\u53eb\u9053\uff1a\u201c\u54ea\u513f\u6709\u77f3\u5934\u5440\uff1f\u600e\u4e48\u6211\u770b\u4e0d\u89c1\uff01\u201d\u4ed6\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u82e5\u975e\u5510\u7ecf \u5929\u6545\u610f\u8fd9\u4e48\u4e00\u6321\uff0c\u4ed6\u4e24\u817f\u65e9\u6210\u6b8b\u5e9f\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u5feb\u8c22\u5e08\u7236\u653e\u884c\uff01\u201d\u4ed6\u77e5\u9053\u8c61\u8fd9\u7b49\u5f02 \u4eba\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u90a3\u5c31\u518d\u4e5f\u4e0d\u80fd\u4e0e\u4e00\u4e2a\u672a\u5b66\u540e\u8fdb\uff0c\u662f\u81ea\u5df1\u5f92\u5f1f\u8eab\u4efd\u7684\u4e00\u4e2a\u987d\u7ae5\u4e3a\u96be\u3002\u6c5f\u5357\u4e5f\u7b97 \u673a\u7075\uff0c\u867d\u7136\u4e0d\u660e\u7528\u610f\uff0c\u5374\u4ecd\u662f\u606d\u606d\u656c\u656c\u7684\u4f5c\u4e86\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5e08\u7236\u653e\u884c\uff01\u201d\u5510\u7ecf\u5929\u653e\u5f00\u4e86 \u624b\uff0c\u8ba9\u6c5f\u5357\u81ea\u5df1\u8d70\u4e86\u3002\u90a3\u9ec4\u888d\u9053\u58eb\u9762\u8272\u94c1\u9752\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u6211\u518d\u65e0\u5e08\u5f92\u540d\u4efd\uff0c \u4f60\u597d\u751f\u53bb\u5427\u3002\u201d\u90a3\u58f0\u97f3\u76f4\u523a\u8fdb\u6c5f\u5357\u7684\u8033\u9f13\uff0c\u6c5f\u5357\u5fc3\u5934\u4e00\u9707\uff0c\u9669\u9669\u8dcc\u5012\u5730\u4e0a\uff0c\u6025\u5fd9\u63a9\u8033\u75be\u8d70\uff0c \u53ea\u89c9\u8eab\u4e0a\u5fae\u5fae\u53d1\u70ed\uff0c\u4f46\u4ed6\u6025\u4e8e\u9003\u8d70\uff0c\u5374\u4e5f\u5e76\u4e0d\u5728\u610f\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u60f3\u544a\u8f9e\uff0c\u53ea\u89c1\u90a3\u9ec4\u888d\u9053\u58eb\u773c\u77aa\u77aa\u5730\u76ef\u7740\u81ea\u5df1\uff0c\u53d1\u51fa\u4e00\u79cd\u6781\u96be\u542c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u597d \u672c\u4e8b\uff0c\u597d\u672c\u4e8b\uff0c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u8bf4\u51fa\u6765\u8ba9\u8001\u673d\u597d\u53bb\u8bf7\u6559\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u201d\u7b11\uff0c\u9053\uff1a\u201c\u665a\u8f88\u6240\u5c45\u4e4b\u5730\uff0c\u79bb\u6b64\u751a\u8fdc\uff0c\u54ea\u6562\u6709\u52b3\u524d\u8f88\u51fa\u5c71\u3002\u201d\u6b64\u8bdd\u660e\u662f \u5ba2\u6c14\uff0c\u5b9e\u662f\u5360\u4e86\u8eab\u4efd\uff0c\u5373\u662f\u8bf4\u81ea\u5df1\u7684\u5e08\u7236\u8db3\u53ef\u4ee5\u5f53\u5f97\u4ed6\u7684\u201c\u8bf7\u6559\u201d\u4e0d\u8fc7\u4e0d\u6562\u201c\u6709\u52b3\u201d\u7f62\u4e86\u3002 \u5510\u7ecf\u5929\u672c\u6765\u8c26\u4e0b\u81ea\u6043\uff0c\u56e0\u89c1\u90a3\u8001\u9053\u8bf4\u8bdd\u592a\u8fc7\u72c2\u5984\uff0c\u6240\u4ee5\u523a\u4e86\u4e00\u53e5\u3002\u987b\u77e5\u5510\u7ecf\u5927\u7684\u7236\u4eb2\u4e43\u662f\u5f53 \u4ee3\u7684\u6b66\u5b66\u5927\u5b97\u5e08\uff0c\u8f88\u4efd\u6781\u5c0a\uff0c\u56e0\u6b64\u5510\u7ecf\u5929\u4e0d\u5fc5\u4e3a\u4ed6\u7684\u7236\u4eb2\u5ba2\u6c14\u3002 \u3000\u3000\u90a3\u9ec4\u888d\u9053\u58eb\u602a\u773c\u4e00\u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u6b64\u751f\u4e0d\u60f3\u8d70\u51fa\u8fd9\u7247\u77f3\u6797\u4e8e\uff0c\u51b2\u7740\u4f60\u8fd9\u53e5\u8bdd\uff0c \u6211\u975e\u627e\u4f60\u7684\u5e08\u7236\u4e0d\u884c\uff0c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u60f3\u7b54\u8bdd\uff0c\u5ffd\u542c\u5f97\u77f3\u6797\u4e2d\u4e00\u9635\u6840\u6840 \u7684\u602a\u7b11\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4ece\u91cc\u9762\u7684\u77f3\u6d1e\u53c8\u8e7f\u51fa\u4e00\u4e2a\u4eba\uff0c\u602a\u58f0\u7b11\u9053\uff1a\u201c\u9ec4\u77f3\u9053\u53cb\uff0c\u4f60\u8f93\u4e86\u773c\u4e86\u3002\u5929 \u5c71\u6d3e\u7684\u6b66\u529f\u5bb6\u6570\uff0c\u4f60\u4e5f\u770b\u4e0b\u51fa\u6765\u5417\uff1f\u4f60\u8bd5\u60f3\u5929\u4e0b\u540e\u8f88\uff0c\u9664\u4e86\u5510\u6653\u6f9c\u7684\u72ec\u751f\u7231\u5b50\uff0c\u8fd8\u6709\u8c01\u6562\u5728 \u4f60\u7684\u9762\u524d\u5982\u6b64\u653e\u8086\uff1f\u6211\u65e9\u8bf4\u8fc7\u5929\u5c71\u6d3e\u4ee5\u6b63\u5b97\u81ea\u5c45\uff0c\u5c06\u4e00\u5207\u5f02\u6d3e\u90fd\u770b\u4f5c\u90aa\u9b54\u5916\u9053\uff0c\u5982\u4eca\u4f60\u8be5\u76f8 \u4fe1\u4e86\u5427\uff1f\u201d\u8fd9\u8bdd\u663e\u660e\u6311\u62d4\uff0c\u5510\u7ecf\u5929\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u4eba\u53c8\u9ed1\u53c8\u7626\uff0c\u5f62\u5982\u6854\u7af9\uff0c\u9762\u988a\u6df1\u9677\uff0c\u53cc \u775b\u5982\u706b\uff0c\u5934\u53d1\u4f3c\u4e00\u84ec\u4e71\u8349\uff0c\u72f0\u72de\u6015\u4eba\uff0c\u6b63\u662f\u90a3\u4e2a\u88ab\u51af\u7433\u620f\u5f04\u4e2a\u591f\uff0c\u8d76\u4e0b\u6155\u58eb\u5854\u683c\u5c71\u7684\u8d64\u795e\u5b50\u3002 \u3000\u3000\u6c5f\u5357\u9a87\u53eb\u4e00\u58f0\uff0c\u614c\u5fd9\u94bb\u51fa\u5916\u9762\u7684\u77f3\u6d1e\uff0c\u5fc3\u4e2d\u6697\u81ea\u5947\u602a\uff1a\u91cc\u9762\u7684\u77f3\u7a9f\u53ea\u6709\u5e08\u7236\u4e00\u4eba\uff0c\u8fd9\u602a \u7269\u662f\u4ece\u54ea\u513f\u6765\u7684\uff0c\u96be\u9053\u5728\u77f3\u6797\u4e2d\u53e6\u6709\u901a\u8def\uff1f \u3000\u3000\u5510\u7ecf\u5929\u4ea6\u662f\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u8d64\u795e\u5b50\u4e00\u6765\uff0c\u53ea\u6015\u4e0d\u5bb9\u6613\u8d70\u51fa\u53bb\u4e86\u3002\u201d\u8d64\u795e\u5b50\u8bf4\u5b8c\u4e4b \u540e\uff0c\u90a3\u9ec4\u888d\u9053\u58eb\u679c\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u5730\u9762\u8272\u4e00\u6c89\uff0c\u62c2\u5c18\u4e00\u4e3e\uff0c\u5ced\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u672c\u4e0d\u6b32\u4e0e\u540e\u8f88\u4e3a \u96be\uff0c\u4f46\u65e2\u7136\u662f\u4f60\uff0c\u6211\u82e5\u653e\u4f60\u51fa\u53bb\uff0c\u522b\u4eba\u53ea\u9053\u6211\u6015\u4e86\u5929\u5c71\u7684\u5510\u6653\u6f9c\u592b\u5987\u3002\u201d\u5510\u7ecf\u5927\u867d\u77e5\u5f62\u5371\u52bf \u9669\uff0c\u4ecd\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4e24\u4f4d\u8001\u524d\u8f88\u8981\u7559\u6211\uff0c\u90a3\u4e48\u6211\u8fd8\u6709\u4f55\u6cd5\u8d70\u51fa\uff0c\u53ea\u597d\u7559\u4e0b \u6765\u4efb\u4f60\u4eec\u5904\u7f6e\u4e86\u3002\u201d\u8bdd\u4e2d\u9690\u85cf\u8ba5\u4fcf\u3002\u9ec4\u888d\u9053\u58eb\u6012\u9053\uff1a\u201c\u6211\u8981\u7559\u4f60\uff0c\u4f3a\u987b\u522b\u4eba\u5e2e\u624b\uff0c\u8d64\u795e\u5b50\uff0c \u4f60\u5728\u8fd9\u513f\u655e\u8bc1\u4eba\uff0c\u8fd9\u5c0f\u5b50\u82e5\u63a5\u5f97\u6211\u4e03\u62db\uff0c\u6211\u5c31\u8ba9\u8fd9\u4eba\u51fa\u53bb\uff0c\u4f60\u4e5f\u4e0d\u8bb8\u62e6\u963b\u3002\u597d\u4e2a\u72c2\u5984\u7684\u5c0f\u5b50 \uff0c\u4f60\u8fd8\u4e0d\u628a\u5175\u5203\u4eae\u51fa\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u5212\u51fa\u9053\u6765\uff0c\u53ea\u9650\u4e03\u62db\uff0c\u90a3\u5373\u4ecd\u662f\u5360\u7740\u8001\u524d\u8f88\u7684\u8eab\u4efd\u3002\u5510\u7ecf\u5929\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053 \uff1a\u201c\u65e2\u7136\u5b9a\u8981\u8d50\u6559\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u9650\u5b9a\u4e03\u62db\uff0c\u6211\u7ad9\u5728\u8fd9\u91cc\uff0c\u4e0d\u4f1a\u9003\u8dd1\uff0c\u8001\u524d\u8f88\u4f60\u4e0d\u8fdb\u62db\u8fd8\u5f85\u4f55\u65f6 \uff1f\u201d\u5510\u7ecf\u5929\u4e0d\u80af\u5148\u4eae\u5175\u5203\uff0c\u53e3\u4e2d\u867d\u79f0\u4ed6\u201c\u524d\u8f88\u201d\uff0c\u5b9e\u662f\u5c06\u4ed6\u5f53\u4f5c\u5e73\u8f88\u770b\u5f85\u7f62\u4e86\u3002\u9ec4\u77f3\u9053\u4eba\u52c3 \u7136\u5927\u6012\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u662f\u4f60\u81ea\u5df1\u627e\u6b7b1\u201d\u62c2\u5c18\u4e00\u4e3e\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u4f5c\u52bf\u7eb5\u8dc3\uff0c\u8eab\u5b50\u7adf\u7a81\u7136\u79fb\u524d\u4e08 \u8bb8\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u62c2\u5c18\u5df2\u8fce\u9762\u62c2\u5230\uff01 \u3000\u3000\u8fd9\u62c2\u5c18\u4e00\u62c2\uff0c\u770b\u4f3c\u5bfb\u5e38\uff0c\u5176\u5b9e\u5374\u542b\u6709\u4e24\u79cd\u4e0d\u540c\u7684\u52b2\u9053\uff0c\u5148\u662f\u9633\u521a\u4e4b\u529b\uff0c\u90a3\u62c2\u5c18\u805a\u5728\u4e00\u8d77 \uff0c\u5f62\u5982\u94c1\u7b14\uff0c\u547c\u547c\u631f\u98ce\uff1a\u9633\u521a\u4e4b\u529b\u5018\u82e5\u672a\u80fd\u6536\u6548\uff0c\u62c2\u5c18\u4e00\u5230\u5bf9\u65b9\u9762\u524d\uff0c\u5c18\u5c3e\u7acb\u5373\u6563\u5f00\uff0c\u5316\u6210 \u9634\u67d4\u4e4b\u52b2\uff0c\u5e72\u4e1d\u4e07\u7f15\uff0c\u9f50\u523a\u654c\u4eba\u7a74\u9053\uff0c\u4efb\u662f\u5982\u4f55\u9ad8\u624b\uff0c\u4e5f\u96be\u9632\u5907\u3002\u5510\u7ecf\u5929\u7adf\u7136\u51dd\u7acb\u4e0d\u52a8\uff0c\u9ec4 \u77f3\u9053\u4eba\u559d\u9053\uff1a\u201c\u4f60\u771f\u4e2a\u8981\u6b7b\uff1f\u201d\u8fd9\u65f6\u62c2\u5c18\u5df2\u662f\u8fce\u9762\u6563\u5f00\uff0c\u9ec4\u77f3\u9053\u4eba\u6697\u601d\uff1a\u201c\u6253\u6b7b\u4e86\u4e00\u4e2a\u624b\u65e0 \u5bf8\u94c1\u5c0f\u8f88\uff0c\u5c82\u4e0d\u60f9\u4eba\u7b11\u8bdd\uff1f\u800c\u4e14\u6211\u4f55\u5fc5\u4e0e\u5510\u6653\u6f9c\u7ed3\u8fd9\u6837\u6df1\u4ec7\uff01\u201d\u4ed6\u8fd9\u7b2c\u4e00\u62db\u672c\u6765\u672a\u7528\u5168\u529b\uff0c \u8fd9\u6837\u4e00\u60f3\uff0c\u52b2\u529b\u53c8\u51cf\u4e86\u4e8c\u5206\uff0c\u4f46\u82e5\u88ab\u4ed6\u62c2\u4e2d\uff0c\u4e0d\u6b7b\u4e5f\u5f97\u6210\u4e3a\u6b8b\u5e9f\u3002 \u3000\u3000\u62c2\u5c18\u8fce\u9762\u6563\u5f00\uff0c\u5343\u4e1d\u4e07\u7f15\uff0c\u4e00\u9f50\u7f69\u4e0b\uff0c\u5c31\u5728\u8fd9\u95ee\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u5510\u7ecf\u5929\u5ffd\u5730\u5f20\u53e3\u4e00\u5439\uff0c\u5c18 \u5c3e\u98d8\u98d8\uff0c\u6709\u5982\u67f3\u7d6e\u968f\u98ce\uff0c\u90fd\u62c2\u4e86\u5f00\u53bb\u3002\u672c\u6765\u9ec4\u77f3\u9053\u4eba\u7684\u529f\u529b\u8981\u8fdc\u6bd4\u5510\u7ecf\u5927\u4e3a\u9ad8\uff0c\u4f46\u56e0\u4ed6\u6709\u6240 \u5fcc\u60ee\uff0c\u53ea\u7528\u4e86\u4e00\u534a\u529b\u91cf\uff0c\u800c\u5510\u7ecf\u5374\u662f\u6f5c\u795e\u84c4\u6c14\uff0c\u7528\u4e86\u5929\u5c71\u5fc3\u6cd5\u201c\u5439\u4e91\u52b2\u201d\uff0c\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u6b64 \u6d88\u6ce2\u957f\uff0c\u9ec4\u77f3\u9053\u4eba\u8fd9\u4e00\u8bb0\u7edd\u62db\uff0c\u7adf\u662f\u4f24\u4ed6\u4e0d\u5f97\uff01 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u6014\u4e86\u6014\uff0c\u62c2\u5c18\u4e00\u8f6c\uff0c\u5168\u7528\u4e86\u9633\u521a\u4e4b\u529b\uff0c\u90a3\u5343\u6839\u7384\u4e1d\uff0c\u6839\u6839\u7ad6\u8d77\uff0c\u90fd\u4f3c\u5229\u9488\u4e00\u6837 \uff0c\u4e0b\u523a\u54bd\u5589\uff0c\u4e0a\u523a\u53cc\u76ee\u3002\u8427\u9752\u5cf0\u662f\u4f7f\u62c2\u5c18\u7684\u9ad8\u624b\uff0c\u89c1\u4ed6\u4f7f\u5f97\u5982\u6b64\u51fa\u795e\u5165\u5316\uff0c\u4e5f\u4e0d\u7981\u9a87\u7136\uff01\u8bf4 \u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51e0\u4e4e\u5c31\u5728\u8fd9\u540c\u4e00\u77ac\u95f4\uff0c\u53ea\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u8c28\u9075\u547d\uff0c \u8bf7\u63a5\u62db\uff01\u201d\u5510\u7ecf\u5927\u7684\u6e38\u9f99\u5251\uff0c\u4e43\u5929\u5c71\u6d3e\u7684\u9547\u5c71\u4e4b\u5b9d\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u9ec4\u77f3\u9053\u4eba\u6599\u4e0d\u5230\u4ed6\u51fa\u5251\u5982\u6b64 \u4e4b\u5feb\uff0c\u770b\u8fd9\u5251\u52bf\uff0c\u5439\u6bdb\u7acb\u65ad\uff0c\u9ec4\u77f3\u9053\u4eba\u6015\u5251\u950b\u5272\u65ad\u4ed6\u7684\u5c18\u5c3e\uff0c\u53ea\u5f97\u786c\u628a\u90a3\u9633\u521a\u4e4b\u52b2\u64a4\u4e86\u4e0b\u6765 \uff0c\u4e00\u8f6c\u62c2\u5c18\uff0c\u907f\u5f00\u90a3\u6e38\u9f99\u5251\u7684\u950b\u8292\u3002\u5510\u7ecf\u5929\u8fd9\u4e00\u51fa\u624b\u4e43\u662f\u5927\u5c71\u5251\u5f0f\u4e2d\u7684\u8ffd\u98ce\u5251\u6cd5\uff0c\u524d\u62db\u672a\u8001 \uff0c\u540e\u62db\u7eed\u5230\u3002\u9ec4\u77f3\u9053\u4eba\u6b63\u60f3\u6362\u62db\uff0c\u4f46\u89c1\u4ed6\u5251\u950b\u4e00\u98a4\uff0c\u94f6\u5149\u4e71\u6d12\uff0c\u7aef\u7684\u662f\u52bf\u631f\u98ce\u96f7\u3002\u9ec4\u77f3\u9053\u4eba \u559d\u58f0\uff1a\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u201d\u79fb\u5f62\u6362\u4f4d\uff0c\u5c18\u5c3e\u4e00\u62c2\uff0c\u6539\u7528\u4e86\u9634\u67d4\u4e4b\u52b2\uff0c\u534a\u653b\u534a\u5b88\uff0c\u5c06\u5510\u7ecf\u5929\u7684\u5251\u52bf \u89e3\u5f00\u3002\u8fd9\u65f6\u9ec4\u77f3\u9053\u4eba\u5df1\u4f7f\u4e86\u4e09\u62db\u4e86\uff01 \u3000\u3000\u4f46\u9ec4\u77f3\u9053\u4eba\u90a3\u62c2\u5c18\u7684\u62db\u6570\u786e\u662f\u602a\u5f02\u975e\u51e1\uff0c\u5510\u7ecf\u5929\u8fd9\u4e24\u8bb0\u8ffd\u98ce\u5251\u7684\u6740\u624b\uff0c\u4f55\u7b49\u5a01\u529b\uff0c\u770b\u6765 \u5df2\u8feb\u5f97\u4ed6\u8981\u8f6c\u653b\u4e3a\u5b88\uff0c\u54ea\u77e5\u5c31\u5728\u8fd9\u4e00\u8f6c\u773c\u95f4\uff0c\u4ed6\u5df2\u75be\u5954\u7caa\u4f4d\uff0c\u8f6c\u8fc7\u4e7e\u65b9\uff0c\u5c01\u4e86\u5510\u7ecf\u5929\u7684\u5251\u8def \uff0c\u62c2\u5c18\u8d77\u5904\uff0c\u904d\u88ad\u5510\u7ecf\u5929\u4e0a\u534a\u8eab\u5341\u4e09\u5904\u7a74\u9053\u3002\u5510\u7ecf\u5929\u4ecd\u7136\u4f9d\u7167\u8ffd\u98ce\u5251\u7684\u5251\u52bf\u51fa\u62db\uff0c\u90a3\u540e\u5fc3\u80cc \u8179\u7684\u7a7a\u95e8\uff0c\u5c31\u7acb\u523b\u8981\u88ab\u654c\u4eba\u653b\u5165\uff0c\u9ec4\u77f3\u9053\u4eba\u6674\u4e2d\u5f97\u610f\uff0c\u62c2\u5c18\u6b63\u5f85\u4e58\u9699\u523a\u5165\uff0c\u5ffd\u89c1\u5251\u5149\u4e00\u805a\uff0c \u7adf\u4f3c\u5e73\u3001\u5730\u4e0a\u6d8c\u8d77\u4e00\u5ea7\u5149\u5e62\uff0c\u5c06\u5510\u7ecf\u5929\u5168\u8eab\u5305\u6ca1\u3002\u8fd9\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u6df1\u5965\u7684\u987b\u5f25\u5251\u5f0f\uff0c\u4e00\u5b9a \u8981\u78b0\u5230\u6bd4\u81ea\u5df1\u9ad8\u660e\u7684\u5f3a\u654c\uff0c\u8fd9\u624d\u65cb\u5c55\uff0c\u65cb\u5c55\u5f00\u65f6\uff0c\u5374\u50cf\u94dc\u5899\u94c1\u58c1\uff0c\u65e0\u6687\u53ef\u51fb\u3002\u9ec4\u77f3\u9053\u4eba\u653b\u4e0d \u8fdb\u53bb\uff0c\u8fd9\u4e00\u62db\u7528\u5c3d\u5fc3\u529b\uff0c\u7adf\u662f\u767d\u8d39\u7cbe\u795e\uff01 \u3000\u3000\u6c5f\u5357\u4ece\u5916\u9762\u7684\u77f3\u6d1e\u4e2d\u63a2\u8fdb\u5934\u6765\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u53ea\u5269\u4e0b\u4e09\u62db\u4e86\uff0c\u6211\u6570\u7740\u54e9\uff01\u201d\u9ec4\u77f3\u9053\u4eba \u52c3\u7136\u5927\u6012\uff0c\u5ffd\u5730\u5f3a\u884c\u8fdb\u62db\uff0c\u62c2\u5c18\u4e00\u626b\uff0c\u4e00\u62db\u4e4b\u95f4\uff0c\u540c\u65f6\u653b\u5510\u7ecf\u5929\u7684\u5947\u7ecf\u516b\u8109\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00 \u51db\uff1a\u4ed6\u660e\u77e5\u6211\u8fd9\u5927\u987b\u5f25\u5251\u5f0f\u65e0\u9699\u53ef\u4e58\uff0c\u4f55\u4ee5\u8fd8\u6562\u5f3a\u653b\uff1f\u5fc3\u5ff5\u65b9\u52a8\uff0c\u5251\u5149\u4e00\u7ed5\uff0c\u62c2\u5c18\u5df1\u88ab\u524a\u65ad \u4e86\u6570\u5341\u6839\uff0c\u518d\u88ab\u5251\u98ce\u4e00\u8361\uff0c\u66f4\u788e\u6210\u65e0\u6570\u7ec6\u5c51\uff0c\u53ea\u89c1\u9ec4\u77f3\u9053\u4eba\u5f20\u53e3\u4e00\u5439\uff0c\u90a3\u65e0\u6570\u5c18\u4e1d\u788e\u5c51\uff0c\u90fd \u900f\u5165\u5251\u5149\u5c42\u5185\uff01 \u3000\u3000\u5927\u987b\u5f25\u5251\u5f0f\u867d\u7136\u6cfc\u6c34\u96be\u5165\uff0c\u5439\u6bdb\u7acb\u65ad\uff0c\u4f46\u5374\u4e0d\u80fd\u6321\u7740\u90a3\u53d1\u5c51\u822c\u7684\u5c18\u4e1d\u3002\u5510\u7ecf\u5927\u5927\u5403\u4e00\u60ca \uff0c\u77e5\u9053\u82e5\u88ab\u8fd9\u4e9b\u7834\u5c51\u5439\u5165\u4e03\u7a8d\uff0c\u90a3\u5c31\u6709\u518d\u597d\u7684\u6b66\u529f\uff0c\u4e5f\u96be\u62b5\u53d7\u3002\u8feb\u5f97\u8eab\u5f62\u63a0\u8d77\uff0c\u659c\u8eab\u4e00\u8f6c\uff0c \u8863\u8896\u4e00\u6325\uff0c\u5c06\u90a3\u4e9b\u5c18\u4e1d\u788e\u5c51\u62c2\u5f00\u3002\u53ea\u662f\u5982\u6b64\u4e00\u6765\uff1b\u5927\u987b\u5f25\u5251\u5f0f\u7acb\u65f6\u73b0\u51fa\u7834\u7efd\uff0c\u9ec4\u77f3\u9053\u4eba\u559d\u58f0 \u201c\u7740\uff01\u201d\u5012\u8f6c\u62c2\u5c18\uff0c\u5f80\u524d\u4e00\u523a\uff0c\u201c\u51db\u201d\u7684\u4e00\u58f0\uff0c\u5510\u7ecf\u5929\u7684\u80a9\u5934\u4e0b\u9762\u4e09\u5bf8\u5df2\u88ab\u523a\u5165\uff0c\u8863\u88f3\u4e5f\u7a7f \u4e86\u4e00\u5b54\uff01 \u3000\u3000\u539f\u6765\u9ec4\u77f3\u9053\u4eba\u8fd9\u62c2\u5c18\u4e0a\u7684\u62db\u6570\uff0c\u4e00\u5171\u5c31\u53ea\u6709\u4e03\u62db\uff0c\u4e0d\u8fc7\u4ece\u4e03\u62db\u4e4b\u4e2d\u53c8\u53ef\u4ee5\u751f\u51fa\u8bb8\u591a\u53d8\u5316 \uff0c\u6240\u4ee5\u9ec4\u77f3\u9053\u4eba\u8bf4\u201c\u53ea\u9650\u4e03\u62db\u201d\u5176\u5b9e\u5df2\u662f\u7528\u4e86\u4ed6\u5168\u90e8\u7684\u770b\u5bb6\u672c\u9886\u3002\u8fd9\u4e03\u62db\u6740\u624b\uff0c\u4e00\u62db\u6bd4\u4e00\u62db \u5389\u5bb3\uff0c\u9ec4\u77f3\u9053\u4eba\u89c1\u7528\u4e86\u56db\u62db\u8fd8\u5948\u4f55\u4e0d\u4e86\u5510\u7ecf\u5929\uff0c\u6545\u6b64\u62fc\u7740\u727a\u7272\u4e00\u64ae\u5c18\u5c3e\uff0c\u5728\u7b2c\u4e94\u7b2c\u516d\u62db\u4f7f\u51fa \u4e86\u6700\u53e4\u602a\u7684\u6740\u7740\uff0c\u4e00\u62db\u7834\u4ed6\u7684\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u53e6\u4e00\u62db\u5219\u5012\u8f6c\u5c18\u5c3e\uff0c\u6539\u4f5c\u5224\u5b98\u7b14\u7528\uff0c\u5728\u4ed6\u4e0d\u81f4\u547d \u7684\u5730\u65b9\u4f7f\u52b2\u4e00\u63d2\uff01 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u8fd9\u67c4\u62c2\u5c18\u975e\u5e38\u7279\u522b\uff0c\u5c18\u67c4\u4e43\u662f\u7cbe\u94a2\u5408\u91d1\u6240\u94f8\uff0c\u5c16\u7aef\u950b\u5229\uff0c\u53ef\u4ee5\u523a\u7a74\uff0c\u53ef\u4ee5\u4f24\u4eba \uff0c\u8fd8\u53ef\u4ee5\u7834\u654c\u4eba\u7684\u5185\u5bb6\u6c14\u529f\u3002\u8fd9\u4e00\u63d2\u6b63\u63d2\u5728\u5510\u7ecf\u5929\u80a9\u80cc\u7684\u201c\u75ca\u6c14\u7a74\u201d\u4e4b\u5904\uff0c\u6ee1\u4ee5\u4e3a\u5510\u7ecf\u5929\u5fc5 \u5c06\u53d7\u4f24\u5012\u5730\uff0c\u90a3\u77e5\u5c18\u67c4\u6240\u89e6\u4e4b\u5904\uff0c\u7adf\u4f3c\u78b0\u7740\u5f39\u7c27\u4e00\u6837\uff0c\u5ffd\u5730\u53cd\u5f39\u8d77\u6765\u3002\u5510\u7ecf\u5929\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7b11 \u541f\u541f\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u62db\uff01\u201d \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u81ea\u5df1\u8fd9\u4e00\u63d2\u4e1a\u5df2\u624e\u7834\u8863\u88f3\uff0c\u63d2\u6b63\u7a74\u9053\u3001\u5373\u7b97\u662f\u7ec3\u5230\u7b2c\u4e00\u6d41\u7684\u91d1\u949f\u7f69 \u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u4ea6\u662f\u96be\u4ee5\u62b5\u6321\u3002\u96be\u9053\u8fd9\u4eba\u5e74\u7eaa\u9752\u9752\uff0c\u5c31\u7ec3\u6210\u4e86\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff1f \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u6709\u6240\u4e0d\u77e5\uff0c\u539f\u6765\u5e76\u975e\u5510\u7ecf\u5929\u5df2\u7ec3\u6210\u4e86\u90a3\u79cd\u5200\u67aa\u4e0d\u5165\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u800c\u662f\u4ed6\u8eab\u4e0a\u7a7f \u6709\u6bcd\u4eb2\u7ed9\u4ed6\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u8fd9\u8f6f\u7532\u662f\u56db\u5341\u591a\u5e74\u4e4b\u524d\uff0c\u65e0\u6781\u6d3e\u7684\u5927\u5b97\u5e08\u949f\u4e07\u5802\u5c06\u5e08\u7956\u5085\u9752\u4e3b\u9057\u4e0b \u7684\u5b9d\u7269\uff0c\u9001\u7ed9\u4ed6\u6bcd\u4eb2\u51af\u745b\u4f5c\u201c\u6293\u5468\u201d\u7684\u793c\u7269\u7684\u3002\u8fd9\u8f6f\u7532\u5b9d\u5251\u4e5f\u523a\u4e0d\u7a7f\uff0c\u4f55\u60e7\u4e8e\u4ed6\u7684\u7cbe\u94a2\u5c18\u67c4\uff1f \u3000\u3000\u8fd9\u51e0\u4e0b\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u65c1\u89c2\u7684\u8d64\u795e\u5b50\u4e0e\u8427\u9752\u5cf0\u592b\u5987\u7b49\u4eba\uff0c\u773c\u89c1\u5510\u7ecf\u5929\u4ece\u6b7b\u91cc\u9003\u751f\uff0c\u90fd\u4e0d \u7981\u60ca\u547c\uff0c\u8427\u9752\u5cf0\u662f\u5148\u60ca\u540e\u559c\uff0c\u8d64\u795e\u5b50\u5219\u662f\u5148\u559c\u540e\u60ca\u3002\u8427\u9752\u5cf0\u521a\u521a\u4f38\u624b\u62ed\u6c57\uff0c\u5ffd\u542c\u5f97\u9ec4\u77f3\u9053\u4eba \u4e00\u58f0\u5927\u547c\uff0c\u6574\u4e2a\u8eab\u8eaf\u98de\u8d77\u6765\uff0c\u5012\u6301\u62c2\u5c18\uff0c\u4f5c\u6700\u540e\u7684\u51cc\u7a7a\u4e00\u51fb\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u8fd9\u6700\u540e\u4e00\u62db\u3002\u62c2\u5c18\u4e0e\u94c1\u638c\u4e00\u9f50\u65bd\u7528\uff0c\u62c2\u5c18\u62c2\u7a74\uff0c\u94c1\u638c\u51fb\u80f8\uff0c\u7adf\u662f\u7528\u4e86\u5341\u6210\u529b\u91cf \uff0c\u52bf\u9053\u6781\u662f\u9a87\u4eba\uff0c\u5510\u7ecf\u5929\u8fd8\u6765\u4e0d\u53ca\u8fd0\u7528\u5927\u987b\u5f25\u5251\u5f0f\u9632\u8eab\uff0c\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u94c1\u638c\u5df2\u51cc\u7a7a\u51fb\u4e0b\uff0c \u5468\u56f4\u4e09\u4e08\u4e4b\u5185\uff0c\u5168\u88ab\u4ed6\u7684\u5a01\u529b\u7b3c\u7f69\uff0c\u9003\u4ea6\u96be\u9003\u3002\u5510\u7ecf\u5927\u7684\u8f6f\u7532\u53ea\u80fd\u9632\u62a4\u4e0a\u534a\u8eab\uff0c\u800c\u4e14\u4e5f\u6321\u4e0d \u4f4f\u8fd9\u79cd\u638c\u529b\u3002\u5510\u7ecf\u5929\u89c1\u52bf\u4e0d\u597d\uff0c\u62fc\u7740\u6371\u4ed6\u4e00\u638c\uff0c\u6025\u8f6c\u8eab\u8eaf\uff0c\u5c06\u80cc\u5fc3\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u53c8\u542c\u5230\u8d64\u795e\u5b50\u7684\u602a\u53eb\u4e4b\u58f0\u3002\u5510\u7ecf\u5929\u5168\u529b\u5bf9\u4ed8\u9ec4\u77f3\u9053\u4eba\u5df2\u65e0\u6687\u987e\u53ca\uff1b\u8427\u9752\u5cf0\u592b \u5987\u5ffd\u89c1\u8d64\u795e\u5b50\u4e5f\u6765\u5077\u88ad\uff0c\u66f4\u4e0d\u7981\u9a87\u6781\u800c\u547c\uff01 \u3000\u3000\u5c31\u5728\u5510\u7ecf\u5929\u8fd9\u6027\u547d\u60ac\u4e8e\u4fc4\u503e\u4e4b\u9645\uff0c\u5ffd\u53c8\u542c\u5f97\u8d64\u795e\u5b50\u4e00\u58f0\u5389\u53eb\uff0c\u9ec4\u77f3\u9053\u4eba\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c \u638c\u52bf\u7a0d\u504f\uff0c\u5510\u7ecf\u5929\u4f55\u7b49\u5feb\u6377\uff0c\u7acb\u523b\u98de\u8eab\u63a0\u5f00\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u628a\u9ec4\u77f3\u9053\u4eba\u7684\u8863\u8896\u523a\u7a7f \u4e86\u4e00\u4e2a\u7a9f\u7abf\u3002\u9ec4\u77f3\u9053\u4eba\u53eb\u9053\uff1a\u201c\u4f55\u65b9\u5c0f\u5b50\uff0c\u6562\u65bd\u6697\u7b97\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u5934\u9876\u4e0a\u77f3\u6797\u4ea4\u9519\u4e4b\u5904\uff0c\u4e00\u4e2a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u8001\u4e0d\u6b7b\uff0c\u4f55\u5c1d\u4e5f\u4e0d\u662f\u5077\u65bd\u6697 \u7b97\uff0c\u4e24\u4e2a\u8001\u4e0d\u6b7b\u5408\u529b\u6b3a\u8d1f\u4e00\u4e2a\u6d51\u5c0f\u4e8e\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u54c8\u54c8\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u8fd9\u7b11\u58f0\u4eba\u8033\u523a\u5fc3\uff0c\u5510 \u7ecf\u5929\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u77f3\u6797\u4e0a\u9732\u5929\u5149\u7684\u4e00\u5757\u602a\u77f3\u4e0a\uff0c\u7aef\u5750\u7740\u90a3\u5047\u88c5\u9ebb\u75af\u7684\u602a\u53eb\u5316\u91d1\u4e16\u9057\u3002\u800c\u5728 \u91d1\u4e16\u9057\u7684\u80cc\u540e\uff0c\u5219\u662f\u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u3002\u6562\u60c5\u662f\u4ed6\u4eec\u5f53\u7740\u81ea\u5df1\u6fc0\u6218\u4e4b\u9645\uff0c\u6084\u6084\u63a9\u6765\uff0c\u6797\u4e2d\u8bf8\u4eba\uff0c \u6ce8\u76ee\u6076\u6597\uff0c\u6240\u4ee5\u90fd\u6ca1\u6709\u53d1\u73b0\u3002\u800c\u8d64\u795e\u5b50\u7684\u5389\u53eb\uff0c\u9ec4\u77f3\u9053\u4eba\u7684\u6253\u98a4\uff0c\u90a3\u5f53\u7136\u662f\u51b0\u5ddd\u5929\u5973\u4e0e\u91d1\u4e16 \u9057\u6240\u65bd\u7684\u72ec\u95e8\u6697\u5668\uff0c\u521b\u4e0b\u7684\u6770\u4f5c\u4e86\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u5927\u6012\uff0c\u4e00\u7eb5\u8eab\uff0c\u5c31\u60f3\u8dc3\u4e0a\u53bb\u6293\u91d1\u4e16\u9057\uff0c\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u4f60\u8fde\u4e00\u4e2a\u6d51\u5c0f\u5b50\u90fd\u6253\u4e0d \u5012\uff0c\u6211\u4f55\u5fc5\u4e0e\u4f60\u52a8\u624b\uff1f\u201d\u8eab\u5f62\u4e00\u95ea\uff0c\u624b\u8db3\u5e76\u7528\uff0c\u733f\u7334\u822c\u7684\u63c9\u5347\u4e0a\u90a3\u7b14\u76f4\u5982\u7b0b\u7684\u77f3\u5cf0\uff0c\u9003\u51fa\u5916 \u9762\u3002\u9ec4\u77f3\u9053\u4eba\u8981\u60f3\u8ffd\u4ed6\u672c\u4ea6\u4e0d\u96be\uff0c\u4f46\u8fd9\u65f6\u53c8\u542c\u5f97\u8d64\u795e\u5b50\u53eb\u4e86\u4e00\u58f0\uff0c\u56de\u5934\u4e00\u770b\uff0c\u89c1\u8d64\u795e\u5b50\u9ed1\u6c14 \u6ee1\u9762\uff0c\u6599\u60f3\u5df2\u4e2d\u4e86\u5267\u6bd2\u6697\u5668\uff0c\u9ec4\u77f3\u9053\u4eba\u5b64\u638c\u96be\u9e23\uff0c\u53ea\u597d\u56de\u53bb\u6551\u8d64\u795e\u5b50\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e03\u62db\u5df2\u6ee1\uff0c\u6211\u8d70\u4e86\uff01\u201d\u4ed6\u4f9d\u7167\u6c5f\u6e56\u793c\u8282\uff0c\u5c06\u8bf4\u8bdd\u4ea4\u4ee3\u4e4b\u540e\uff0c\u5fc3\u6025\u5982\u711a\uff0c\u7acb \u523b\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u7d27\u7d27\u8feb\u8e2a\u3002\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u5728\u524d\uff0c\u90a3\u75af\u4e10\u624b\u821e\u8db3\u8e48\u5730\u7d27\u8ddf\u540e\u9762\u3002\u5510\u7ecf\u5929 \u5927\u53eb\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u51b0\u5a25\u59d0\u59d0\uff01\u201d\u51b0\u5ddd\u5929\u5973\u56de\u5934\u770b\u4e00\u770b\u4ed6\uff0c\u76ee\u5149\u9690\u542b\u5e7d\u6028\u3002\u5510\u7ecf\u5929\u53eb\u9053\uff1a \u201c\u51b0\u5a25\u59d0\u59d0\uff1b\u4f60\u505c\u4e00\u505c\uff0c\u542c\u6211\u8bf4\u4e24\u53e5\u8bdd\u3002\u201d\u51b0\u5ddd\u5929\u5973\u659c\u773c\u4e00\u77a5\uff0c\u7adf\u4e0d\u505c\u7559\uff0c\u643a\u7740\u5e7d\u840d\uff0c\u5982\u98de \u75be\u8d70\u3002\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u505c\u4e00\u505c\uff0c\u542c\u6211\u8bf4\u4e86\u518d\u8d70\u4e5f\u4e0d\u8fdf\u3002\u201d\u91d1\u4e16\u9057\u5ffd\u5730\u54c8\u54c8\u5927\u7b11 \uff0c\u6321\u7740\u53bb\u8def\uff0c\u201c\u5478\u201d\u7684\u5410\u4e86\u4e00\u53e3\u553e\u6d8e\uff0c\u602a\u53eb\u9053\uff1a\u201c\u8c01\u8010\u70e6\u542c\u4f60\u7684\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4e3a\u6c42\u5929\u5973\u79cb\u6ce2\u987e\uff0c\u75af\u4e10\u82f1\u8c6a\u5404\u7528\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5bfb\u89c5\u82b3\u8e2a\u3000\u540d\u5c71\u9022\u602a\u5ba2\u3000\u8ffd\u67e5\u65e7\u4e8b\u3000\u53e4\u5bfa\u9047\u826f\u670b \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u4e00\u56de \u5bfb\u89c5\u82b3\u8e2a\u3000\u540d\u5c71\u9022\u602a\u5ba2 \u8ffd\u67e5\u65e7\u4e8b\u3000\u53e4\u5bfa\u9047\u826f\u670b \u3000\u3000\u5510\u7ecf\u5929\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8ba9\u4e0d\u8ba9\u5f00\uff1f\u201d\u91d1\u4e16\u9057\u54c8\u54c8\u5927\u7b11\u3002\u7ad9\u5728\u8def\u4e2d\uff0c\u624b\u821e\u8db3\u8e48\uff0c\u602a\u58f0\u53eb \u9053\uff1a\u201c\u4e0d\u5bb3\u81ca\u4e48\uff1f\u8ffd\u4eba\u5bb6\u7684\u5927\u59d1\u5a18\uff01\u201d\u5510\u7ecf\u5929\u53cd\u624b\u4e00\u632f\uff0c\u6253\u51fa\u4e00\u652f\u5929\u5c71\u795e\u8292\uff0c\u53ea\u89c1\u4e00\u9053\u6697\u8d64 \u8272\u7684\u5149\u534e\uff0c\u5982\u7bad\u75be\u5c04\u3002\u91d1\u4e16\u9057\u4e0a\u6b21\u4e0e\u5510\u7ecf\u5929\u4ea4\u624b\u65f6\uff0c\u66fe\u9886\u6559\u8fc7\u5929\u5c71\u795e\u8292\u7684\u5389\u5bb3\uff0c\u88ab\u4ed6\u5c04\u4e2d\uff0c \u8fd0\u4e86\u4e03\u65e5\u7684\u7384\u529f\uff0c\u65b9\u624d\u5e73\u590d\uff0c\u8fd9\u65f6\u65e9\u6709\u9632\u5907\uff0c\u4f46\u89c1\u4e00\u7bad\u98de\u6765\uff0c\u4ed6\u7a81\u7136\u4e00\u4e2a\u7b4b\u6597\uff0c\u5012\u7ffb\u51fa\u53bb\u4e09 \u4e08\u6709\u4f59\uff0c\u4e3e\u62d0\u4e00\u8fce\uff0c\u53ee\u5417\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\u3002\u90a3\u5929\u5c71\u795e\u8292\u7684\u53bb\u52bf\u5df2\u88ab\u4ed6\u6d88\u4e86\u4e00\u534a\uff0c\u518d\u7ecf\u8fd9\u4e48\u4e00 \u6321\uff0c\u7acb\u523b\u659c\u98de\u51fa\u53bb\uff0c\u6ca1\u5165\u8346\u68d8\u4e1b\u4e2d\u3002\u91d1\u4e16\u9057\u53c8\u4e00\u4e2a\u7b4b\u6597\uff0c\u7ffb\u8f6c\u8eab\u5f62\uff0c\u633a\u8170\u602a\u53eb\uff1a\u201c\u5927\u59d1\u5a18\u5df2 \u8d70\u5f97\u8fdc\u5566\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u7126\u6025\u4e4b\u6781\uff0c\u89c1\u5929\u5c71\u795e\u8292\u867d\u80fd\u628a\u4ed6\u8feb\u9000\uff0c\u4f46\u4ed6\u4ecd\u7136\u662f\u62e6\u4f4f\u53bb\u8def\uff0c\u53ea\u597d\u786c\u51b2\uff0c\u5f53\u4e0b\u66f4 \u4e0d\u6253\u8bdd\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u6e38\u9f99\u5251\u6296\u8d77\u4e00\u9053\u5bd2\u5149\uff0c\u4e00\u62db\u201c\u7a7f\u4e91\u88c2\u77f3\u201d\uff0c\u540c\u65f6\u523a\u91d1\u4e16\u9057\u5589\u5934\u3001\u80f8\u53e3\u4e24 \u5904\u8981\u5bb3\u3002\u91d1\u4e16\u9057\u62d4\u51fa\u4e86\u94c1\u5251\uff0c\u5de6\u62d0\u53f3\u5251\u8fd8\u4e86\u4e00\u62db\u3002\u4e24\u4eba\u529f\u529b\u6089\u654c\uff0c\u90fd\u7ed9\u5bf9\u65b9\u9707\u5f97\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5251\u8d70\u8f7b\u7075\uff0c\u5de6\u523a\u4e09\u5251\uff0c\u53f3\u523a\u4e09\u5251\uff0c\u4f7f\u51fa\u5929\u5c71\u5251\u5f0f\u4e2d\u7684\u8ffd\u98ce\u5251\u6cd5\uff0c\u7740\u7740\u5f3a\u653b\uff0c\u7aef\u7684 \u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u9022\u9699\u5373\u5165\u3002\u6218\u5230\u5206\u9645\uff0c\u5510\u7ecf\u5929\u89d1\u7740\u4e2a\u7834\u7efd\uff0c\u6e38\u9f99\u5251\u81ea\u5de6\u81f3\u53f3\uff0c\u7a81\u7136\u5212\u4e86\u4e00\u4e2a\u5706 \u5708\uff0c\u5c06\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u94c1\u5251\u90fd\u5708\u5728\u5f53\u4e2d\u3002\u53ea\u5f85\u5706\u5708\u4e00\u8f6c\uff0c\u5251\u70b9\u7acb\u523b\u56db\u5904\u6492\u5f00\uff0c\u53ef\u4ee5\u540c\u65f6\u523a\u4ed6\u4e0a \u8eab\u7684\u4e5d\u5904\u9ebb\u7a74\u3002\u91d1\u4e16\u9057\u602a\u53eb\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff0c\u4f60\u8fd9\u6d51\u5c0f\u5b50\u4e3a\u4e86\u4e00\u4e2a\u5927\u59d1\u5a18\u5c31\u4e0d\u5ff5\u6211\u9002\u624d\u7684\u6551\u547d \u4e4b\u6069\u4e86\u4e48\uff1f\u201d\u7a81\u7136\u5c06\u53f3\u624b\u7684\u94c1\u5251\u5728\u5de6\u624b\u7684\u94c1\u62d0\u4e0a\u4e00\u51fb\uff0c\u62d0\u5251\u9f50\u98de\uff0c\u81ea\u8eab\u4e5f\u51ed\u7740\u8fd9\u4e00\u9707\u4e4b\u52bf\uff0c \u98de\u51fa\u5708\u5916\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u51db\uff0c\u6697\u60f3\u9053\uff1a\u9002\u624d\u9ec4\u77f3\u9053\u4eba\u90a3\u6700\u540e\u4e00\u51fb\uff0c\u82e5\u975e\u4ed6\u4e0e\u51b0\u5ddd\u5929\u5973\u7684\u6697\u5668\u53ca\u65f6\u6253 \u5230\uff0c\u6211\u5fc5\u7136\u7ed9\u9ec4\u77f3\u9053\u4eba\u6253\u4e2d\uff0c\u867d\u8bf4\u6211\u6709\u8f6f\u7532\u62a4\u8eab\uff0c\u5373\u7b97\u53d7\u4e86\u638c\u529b\u6240\u4f24\uff0c\u6211\u4e5f\u6709\u5929\u5c71\u96ea\u83b2\u8c03\u6cbb \uff0c\u65ad\u65ad\u4e0d\u81f3\u4e8e\u4e27\u547d\uff0c\u4f46\u4ed6\u4eec\u603b\u7b97\u662f\u6709\u76f8\u6551\u4e4b\u6069\u3002\u5982\u6b64\u4e00\u60f3\uff0c\u4ed6\u8fd9\u4e00\u5251\u672c\u6765\u8fd8\u6709\u4e24\u4e2a\u6781\u5389\u5bb3\u7684 \u540e\u7740\uff0c\u8fd9\u65f6\u5374\u81ea\u7136\u6536\u4e86\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4ee5\u524d\u65e0\u539f\u65e0\u6545\u7684\u4f24\u6211\uff0c\u5f04\u5f97\u6211\u51e0\u4e4e\u9001\u547d\uff1b\u4eca\u65e5\u770b\u5728 \u4f60\u51fa\u624b\u7684\u4efd\u4e0a\uff0c\u8fd9\u6069\u6028\u4e00\u7b14\u52fe\u9500\uff0c\u4f60\u8ba9\u5f00\u8def\uff0c\u4ee5\u540e\u54b1\u4eec\u8fd8\u53ef\u505a\u505a\u670b\u53cb\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u5411\u540e\u4e00\u671b\uff0c\u5ffd\u5730\u53c8\u602a\u7b11\u9053\uff1a\u201c\u8c01\u548c\u4f60\u505a\u670b\u53cb\uff0c\u4f60\u8fd9\u4e0d\u8981\u8138\u7684\u5c0f\u5b50\uff0c\u7b80\u76f4\u4e0d\u61c2\u6c5f\u6e56 \u4e49\u6c14\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6211\u4e0d\u61c2\u6c5f\u6e56\u4e49\u6c14\uff1f\u4f60\u8fd9\u8bdd\u662f\u9a82\u8c01\uff1f\u8fd9\u6b63\u8be5\u662f\u9a82\u4f60\uff01\u201d\u91d1\u4e16\u9057\u9053 \uff1a\u201c\u662f\u9a82\u4f60\uff01\u4e0d\u70b9\u9192\u4f60\uff0c\u4f60\u4e0d\u670d\u6c14\uff0c\u6211\u6765\u95ee\u4f60\uff0c\u6c5f\u6e56\u4e0a\u7684\u4e49\u6c14\u662f\u4e0d\u662f\u8bb2\u7a76\u6709\u996d\u5927\u5bb6\u5403\uff0c\u6709\u8863 \u5927\u5bb6\u7a7f\uff0c\u81ea\u5df1\u6709\u4e86\u7684\u66f4\u4e0d\u5e94\u62a2\u522b\u4eba\u7684\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\u662f\u8bb2 \u7a76\u8fd9\u4e00\u5957\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u6709\u4e86\u90b9\u5bb6\u7684\u5c0f\u59d1\u5a18\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u6842\u5bb6\u7684\u5927\u59d1\u5a18\uff1f\u7eb5\u7136\u6211 \u548c\u4f60\u4e0d\u662f\u670b\u53cb\uff0c\u6842\u5bb6\u7684\u5927\u59d1\u5a18\u53ef\u662f\u6211\u7684\u670b\u53cb\u54e9\u3002\u4f60\u6709\u4e86\u4e00\u4e2a\u8fd8\u8981\u8ffd\u6211\u7684\u670b\u53cb\uff0c\u8fd9\u7b97\u4ec0\u4e48\u6c5f\u6e56 \u4e49\u6c14\uff1f\u201d\u5510\u7ecf\u5929\u4e43\u6b63\u6d3e\u5f1f\u5b50\uff0c\u4e07\u6599\u4e0d\u5230\u4ed6\u8bb2\u51fa\u8fd9\u4e00\u756a\u6df7\u5e10\u7684\u8bdd\u6765\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u90a3\u91d1\u4e16\u9057\u5140\u662f\u563b\u563b\u602a\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u5f97\u5bf9\u4e86\u5427\uff1f\u4f60\u8fd9\u56de\u53ef\u670d\u6c14\u4e86 \uff1f\u201d\u5510\u7ecf\u5929\u5927\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4f60\u518d\u4e71\u56bc\u820c\u5934\uff0c\u6211\u5c31\u4e00\u5251\u628a\u4f60\u5241\u4e86\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u53ea\u6015 \u4f60\u5241\u4e0d\u7740\uff01\u201d\u5510\u7ecf\u5929\u5927\u6012\uff0c\u6e38\u9f99\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u53c8\u518d\u51fa\u62db\uff0c\u91d1\u4e16\u9057\u4e00\u9762\u62db\u67b6\uff0c\u4e00\u9762\u65f6\u4e0d\u65f6\u5730\u5411 \u540e\u5f20\u671b\uff0c\u770b\u4ed6\u8fd9\u60c5\u5f62\uff0c\u6562\u60c5\u662f\u8981\u7b49\u5230\u51b0\u5ddd\u5929\u5973\u8d70\u5f97\u8fdc\u8fdc\u4e4b\u540e\uff0c\u6599\u5510\u7ecf\u5929\u518d\u4e5f\u8ffd\u5979\u4e0d\u7740\u4e4b\u65f6\uff0c \u624d\u80af\u7f62\u624b\uff0c\u4e0d\u518d\u7ea0\u7f20\u3002\u5510\u7ecf\u5929\u53c8\u6025\u53c8\u6c14\uff0c\u4f46\u4e24\u4eba\u529f\u529b\u6089\u654c\uff0c\u5510\u7ecf\u5929\u5728\u5251\u6cd5\u4e0a\u867d\u7136\u7a0d\u7a0d\u5360\u4e00\u70b9 \u4e0a\u98ce\uff0c\u8981\u60f3\u6446\u8131\u4ed6\u7684\u7ea0\u7f20\uff0c\u5374\u662f\u4e0d\u80fd\u3002\u8fd9\u65f6\u5510\u7ecf\u5929\u4e00\u8154\u6012\u6c14\uff0c\u5168\u90fd\u53d1\u6cc4\u5728\u91d1\u4e16\u9057\u8eab\u4e0a\uff0c\u60f3\u9053 \uff1a\u201c\u539f\u6765\u662f\u8fd9\u53ae\u6311\u62e8\u7684\uff01\u201d\u521a\u624d\u5bf9\u91d1\u4e16\u9057\u90a3\u4e00\u70b9\u601c\u60dc\u4e4b\u60c5\u5df2\u5316\u4e3a\u4e4c\u6709\uff0c\u5c06\u6700\u7cbe\u5999\u7684\u5929\u5c71\u5251\u6cd5 \uff0c\u65bd\u5c55\u51fa\u6765\uff0c\u76f4\u5982\u60ca\u6d9b\u9a87\u6d6a\uff0c\u64bc\u5c71\u88c2\u77f3\u3002\u91d1\u4e16\u9057\u7528\u94c1\u62d0\u5c01\u95ed\u95e8\u6237\uff0c\u7528\u94c1\u5251\u8fd8\u653b\uff0c\u7ade\u4e5f\u5982\u6c5f\u5fc3 \u5de8\u77f3\uff0c\u505a\u7136\u5140\u7acb\u3002\u53cc\u65b9\u5404\u4e2a\u76f8\u8ba9\uff0c\u6597\u4e86\u4e00\u767e\u591a\u62db\uff0c\u672a\u5206\u80dc\u8d1f\uff0c\u8427\u9752\u5cf0\u592b\u5987\u4e0e\u6c5f\u5357\u90fd\u5df2\u8d76\u81f3\uff0c \u89c1\u8fd9\u58f0\u52bf\uff0c\u6bd4\u521a\u624d\u6597\u9ec4\u77f3\u9053\u4eba\u8fd8\u66f4\u6fc0\u70c8\uff0c\u90fd\u662f\u6697\u6697\u5fc3\u60ca\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5510\u7ecf\u5929\u53f1\u54a4\u4e00\u58f0\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u5c06\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u52fe\u7740\uff0c\u53f3\u811a\u8fd9\u8d77\uff0c\u6e38\u9f99\u5251\u53c8\u5206\u5fc3\u76f4 \u523a\u3002\u4ed6\u7528\u4e86\u4e09\u8bb0\u6740\u624b\u7edd\u62db\uff0c\u5168\u662f\u62fc\u547d\u7684\u62db\u6570\uff0c\u53ea\u9053\u603b\u6709\u4e00\u62db\u5f97\u624b\u3002\u4e0d\u6599\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u4e00\u58f0\u602a \u7b11\uff0c\u7a81\u7136\u53c8\u662f\u4e00\u4e2a\u7b4b\u6597\uff0c\u5012\u7ffb\u7ad6\u5730\uff0c\u201c\u5478\u201d\u7684\u5410\u4e86\u4e00\u53e3\u6d53\u75f0\uff0c\u9a82\u9053\uff1a\u201c\u4e3a\u4e86\u4e00\u4e2a\u599e\u599e\u513f\u62fc\u547d \uff0c\u503c\u5f97\u4e48\uff1f\u597d\uff0c\u89c1\u4f60\u8fd9\u5c0f\u5b50\u5982\u6b64\u53ef\u601c\uff0c\u53eb\u8001\u4e8e\u5c31\u8ba9\u4f60\u8fc7\u53bb\u3002\u201d\u4ed6\u8fd9\u4e00\u4e2a\u5012\u7ffb\uff0c\u5510\u7ecf\u5929\u90a3\u4e00\u5251 \u5c31\u523a\u4e86\u4e2a\u7a7a\u3002\u5510\u7ecf\u5929\u518d\u4e00\u811a\u8e22\u53bb\uff0c\u53c8\u521a\u521a\u8e22\u7740\u7ad6\u5728\u5730\u4e0a\u7684\u94c1\u62d0\u3002\u94c1\u62d0\u4e00\u98de\uff0c\u91d1\u4e16\u9057\u4e5f\u5c31\u5728\u8fd9 \u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u85c9\u7740\u90a3\u94c1\u62d0\u4e00\u9707\u4e4b\u529b\uff0c\u5e73\u5730\u98de\u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u63a5\u4e86\u90a3\u6839\u62d0\u6756\uff0c\u843d\u5230\u516d\u4e03\u4e08\u5916\u3002 \u91d1\u4e16\u9057\u5411\u6797\u4e2d\u4e00\u8dd1\uff0c\u8fd8\u81ea\u597d\u6574\u4ee5\u6687\u7684\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5411\u5510\u7ecf\u5929\u88c2\u5634\u4e00\u7b11\uff0c\u5510\u7ecf\u5929\u6b63\u60f3\u518d\u53d1\u5929\u5c71 \u795e\u8292\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u63a0\u8d77\uff0c\u8df3\u4e0a\u4e00\u68f5\u5927\u6811\uff0c\u50cf\u733f\u7334\u822c\u6328\u7740\u679d\u5934\uff0c\u7eb5\u8dc3\u5982\u98de\uff0c\u6ca1\u5165\u6797\u4e2d\uff0c\u500f\u5ffd\u4e0d \u89c1\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5446\u7136\u51dd\u7acb\uff0c\u91d1\u4e16\u9057\u90a3\u56de\u5934\u4e00\u7b11\uff0c\u795e\u6001\u6f47\u6d12\u4e4b\u6781\uff0c\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8111\u4e2d\u6d6e\u8d77\u91d1\u4e16 \u9057\u4ee5\u524d\u90a3\u4ed8\u80ae\u810f\u7684\u989c\u5bb9\uff0c\u4e0e\u73b0\u5728\u76f8\u6bd4\uff0c\u7b80\u76f4\u5982\u540c\u4e24\u4eba\uff0c\u5fc3\u9053\uff1a\u539f\u6765\u4ed6\u4e5f\u662f\u8fd9\u822c\u4fca\u79c0\u7684\u5c11\u5e74\uff0c \u4ed6\u82e6\u82e6\u7ea0\u7f20\u51b0\u5ddd\u5929\u5973\uff0c\u8fd9\u662f\u4e3a\u4f55\uff1f\u5510\u7ecf\u5929\u4e00\u5411\u4ee5\u4e3a\u4e16\u4e0a\u9664\u4ed6\u4e4b\u5916\uff0c\u518d\u65e0\u7b2c\u4e8c\u4eba\u53ef\u914d\u5f97\u4e0a\u51b0\u5ddd \u5929\u5973\uff0c\u8fd9\u65f6\u5374\u4e0d\u81ea\u7981\u7684\u7adf\u7136\u6709\u4e86\u918b\u610f\uff0c\u6709\u4e86\u918b\u610f\uff0c\u5373\u662f\u5728\u5fc3\u5e95\u91cc\u627f\u8ba4\u8fd9\u5192\u5145\u9ebb\u75af\u7684\u602a\u7269\u4e5f\u7b97 \u5f97\u662f\u4e2a\u5389\u5bb3\u7684\u5bf9\u624b\u4e86\u3002\u53c8\u60f3\u8d77\u4ed6\u9002\u624d\u9003\u907f\u81ea\u5df1\u7684\u4e24\u8bb0\u6740\u624b\uff0c\u90a3\u4e24\u6b21\u6240\u663e\u7684\u8eab\u624b\uff0c\u7686\u662f\u602a\u5f02\u7edd \u4f26\uff0c\u51ed\u81ea\u5df1\u5bf9\u5404\u5bb6\u5404\u6d3e\u6b66\u529f\u7684\u719f\u6089\uff0c\u7ade\u4e5f\u77a7\u4e0d\u51fa\u4ed6\u534a\u70b9\u5bb6\u6570\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u81ea\u7981\u7684\u6697\u6697\u53f9\u606f\uff0c\u51ed \u8fd9\u5c11\u5e74\u7684\u8eab\u624b\uff0c\u786e\u7b97\u5f97\u4e0a\u662f\u6c5f\u6e56\u4e0a\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u5374\u600e\u4e48\u884c\u4e8b\u602a\u7656\u5f97\u5982\u6b64\u4e0d\u8fd1\u4eba\u60c5\uff1f \u3000\u3000\u8427\u9752\u5cf0\u592b\u5987\u4e0e\u6c5f\u5357\u81ea\u540e\u8d76\u4e0a\uff0c\u6c5f\u5357\u60ca\u9b42\u521d\u5b9a\uff0c\u53c8\u53fd\u53fd\u55b3\u55b3\u7684\u53eb\u9053\uff1a\u201c\u771f\u9669\uff0c\u771f\u9669\uff01\u5582\uff0c \u5510\u76f8\u516c\uff0c\u90a3\u4e2a\u5c11\u5e74\u662f\u4ec0\u4e48\u4eba\uff1f\u600e\u4e48\u4ed6\u7528\u6697\u5668\u52a9\u4f60\uff0c\u5374\u53c8\u62e6\u963b\u4f60\u53bb\u8feb\u8d76\u90a3\u4e2a\u5c11\u5973\uff1f\u201d\u5510\u7ecf\u5929\u6ee1 \u6000\u5fc3\u4e8b\uff0c\u7f6e\u4e4b\u4e0d\u7b54\uff0c\u6c5f\u5357\u53c8\u81ea\u4f5c\u806a\u660e\u7684\u53eb\u9053\uff1a\u201c\u90a3\u5973\u5b50\u771f\u7f8e\uff0c\u6211\u77e5\u9053\u6211\u4eec\u7684\u516c\u5b50\u6b22\u559c\u4e00\u4e2a\u795e \u79d8\u7684\u85cf\u65cf\u5c11\u5973\uff0c\u90a3\u5973\u5b50\u6211\u89c1\u8fc7\uff0c\u5f53\u65f6\u6211\u4ee5\u4e3a\u4e16\u4e0a\u518d\u6ca1\u6709\u6bd4\u5979\u66f4\u6f02\u4eae\u7684\u4e86\uff0c\u54c8\uff0c\u5982\u4eca\u89c1\u4e86\u8fd9\u4e2a \u5973\u5b50\u624d\u77e5\u9053\u771f\u7684\u662f\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u54c8\uff0c\u5510\u76f8\u516c\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u662f\u4e86\uff01\u201d\u5510\u7ecf\u5929\u6115\u7136\u9053 \uff1a\u201c\u600e\u4e48\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u662f\u50cf\u6211\u4eec\u7684\u516c\u5b50\u4e00\u6837\uff0c\u4e00\u89c1\u4e86\u7f8e\u8c8c\u7684\u5973\u5b50\uff0c\u5c31\u795e\u8ff7\u610f\u8361\u4e86\u3002 \u8fd9\u4e0d\u602a\u4f60\uff0c\u4f46\u4eba\u5bb6\u5230\u5e95\u662f\u540c\u6765\u7684\u5440\uff0c\u4f60\u5c31\u662f\u6709\u610f\u601d\uff0c\u4e5f\u8be5\u5148\u8bf7\u90a3\u4e2a\u7537\u7684\u66ff\u4f60\u5f15\u89c1\u3002\u8bf4\u4e0d\u5b9a\u4ed6 \u4eec\u662f\u4e00\u5bf9\u5144\u59b9\uff0c\u8fd9\u8fd8\u597d\uff0c\u82e5\u662f\u4e00\u5bf9\u592b\u5987\uff0c\u90a3\u5c31\u602a\u4e0d\u5f97\u4ed6\u8981\u6253\u4f60\u4e86\u3002\u201d\u5510\u7ecf\u5929\u54ed\u7b11\u4e0d\u5f97\uff0c\u4ed6\u5e72 \u8f9b\u4e07\u82e6\u5730\u6500\u767b\u51b0\u5ddd\uff0c\u8bf7\u5f97\u51b0\u5ddd\u5929\u5973\u4e0b\u5c71\uff0c\u5374\u60f3\u4e0d\u5230\u843d\u5230\u5982\u65af\u7ed3\u679c\uff0c\u8fde\u6c5f\u5357\u4e5f\u4ee5\u4e3a\u5979\u548c\u81ea\u5df1\u4e43 \u662f\u521d\u89c1\u9762\u7684\u964c\u751f\u4eba\u3002 \u3000\u3000\u8427\u9752\u5cf0\u77aa\u4e86\u6c5f\u5357\u4e00\u773c\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u591a\u5634\uff01\u201d\u6c5f\u5357\u5600\u5600\u5495\u5495\uff0c\u5fc3\u4e2d\u9a82\u9053\uff1a\u201c\u521a\u8d70\u51fa\u9669\u5883 \uff0c\u53c8\u6446\u8d77\u8001\u5e08\u7684\u67b6\u5b50\u6765\u4e86\u3002\u201d\u4f46\u89c1\u8427\u9752\u5cf0\u795e\u8272\u751a\u662f\u8ba4\u771f\uff0c\u4e0d\u6562\u591a\u8bdd\uff0c\u4e00\u8d4c\u6c14\u4fbf\u8d70\u5f97\u4e5f\u4e0d\u8d77\u52b2 \uff0c\u81ea\u7136\u843d\u5728\u540e\u9762\u3002\u8427\u9752\u5cf0\u4e0a\u524d\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u5510\u76f8\u516c\u4f11\u8981\u70e6\u607c\uff0c\u73b0\u5728\u867d\u8d76\u5979\u4e0d\u4e0a\uff0c\u4f46\u5230\u4e86\u5192\u8001 \u524d\u8f88\u90a3\u513f\uff0c\u4e00\u5b9a\u53ef\u4ee5\u89c1\u9762\u3002\u201d\u5510\u7ecf\u5929\u5982\u68a6\u521d\u9192\uff0c\u6697\u81ea\u7b11\u9053\uff1a\u201c\u771f\u7684\u662f\u6211\u7cca\u6d82\u4e86\uff0c\u5979\u5373\u7136\u6765\u5230 \u6b64\u5730\uff0c\u5f53\u7136\u662f\u8981\u53bb\u627e\u5979\u7684\u4f2f\u4f2f\u4e86\u3002\u4f46\uff0c\u60f3\u5230\u8fd8\u6709\u534a\u6708\u4e4b\u671f\uff0c\u624d\u80fd\u89c1\u9762\uff0c\u800c\u8fd9\u534a\u6708\u5979\u5374\u4e0e\u90a3\u201d \u75af\u4e10\u201d\u540c\u884c\uff0c\u4e0d\u7981\u5fc3\u4e2d\u9690\u9690\u4f5c\u75db\u3002\u5176\u5b9e\uff0c\u5510\u7ecf\u5929\u6599\u9519\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u5e76\u4e0d\u662f\u4e0e\u91d1\u4e16\u9057\u4e00\u9053\uff0c\u800c \u662f\u91d1\u4e16\u9057\u4e00\u8def\u7684\u8ddf\u8e2a\u5979\u3002\u91d1\u4e16\u9057\u77e5\u9053\u5979\u5fc3\u7eea\u4e0d\u4f73\uff0c\u8fd8\u4e0d\u6562\u8fc7\u4e8e\u63a5\u8fd1\u5979\u5462\uff0c\u8fd9\u6b21\u5728\u77f3\u6797\u4e4b\u4e2d\uff0c \u4e43\u662f\u51b0\u5ddd\u5929\u5973\u5148\u5230\uff0c\u91d1\u4e16\u9057\u968f\u540e\u624d\u5230\uff0c\u89c1\u5979\u51fa\u624b\uff0c\u77e5\u9053\u5979\u5c1a\u672a\u5fd8\u60c5\u4e8e\u5510\u7ecf\u5929\uff0c\u5fc3\u4e2d\u4ea6\u6697\u6697\u7740 \u607c\u5462\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6ca1\u7cbe\u6253\u91c7\uff0c\u4e00\u8def\u524d\u540a\u8427\u9752\u5cf0\u662f\u4e0e\u5510\u7ecf\u5929\u540c\u4e00\u65f6\u5019\u4e0a\u51b0\u5cf0\u62dc\u4f1a\u51b0\u5ddd\u5929\u5973\u7684\u4eba\uff0c\u77e5\u9053 \u5176\u4e2d\u56e0\u679c\uff0c\u4ea6\u662f\u90c1\u90c1\u4e0d\u4e50\u3002\u6b63\u8d70\u8def\u95f4\uff0c\u5ffd\u542c\u5f97\u6c5f\u5357\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u54ce\u54df\uff01\u201d\u8427\u9752\u5cf0\u56de\u8fc7\u5934\u6765\uff0c \u95ee\u9053\uff1a\u201c\u4f5c\u4ec0\u4e48\uff1f\u201d\u6c5f\u5357\u8e72\u5728\u5730\u4e0a\uff0c\u6367\u7740\u809a\u76ae\uff0c\u9053\uff1a\u201c\u809a\u5b50\u75db\uff01\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u521a\u624d\u8fd8\u597d\u7aef \u7aef\u7684\uff0c\u600e\u4e48\u5ffd\u7136\u4e4b\u95f4\u809a\u5b50\u75db\uff1f\u201d\u8427\u9752\u5cf0\u7cbe\u4e8e\u533b\u7406\uff0c\u66ff\u6c5f\u5357\u628a\u8109\uff0c\u5374\u65e0\u534a\u70b9\u8113\u75db\u7684\u75c5\u8c61\uff1b\u9a82\u9053 \uff1a\u201c\u5c0f\u9b3c\u5934\u88c5\u795e\u5f04\u602a\uff0c\u54b1\u4eec\u90fd\u6709\u6b63\u7ecf\u4e8b\u513f\uff0c\u8981\u8d76\u8def\uff0c\u8c01\u8010\u70e6\u548c\u4f60\u620f\u800d\uff01\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u8c01\u548c \u4f60\u5f00\u73a9\u7b11\uff1b\u6211\u771f\u7684\u809a\u75db\uff01\u201d\u5510\u7ecf\u5929\u4e0a\u524d\u66ff\u4ed6\u628a\u8109\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u9762\u4e0a\u8d8a\u6765\u8d8a\u73b0\u51fa\u60ca\u8bb6\u7684\u795e \u8272\uff0c\u8427\u9752\u5cf0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4ed6\u771f\u7684\u809a\u75db\u5417\uff1f\u201d\u5510\u7ecf\u5929\u5ffd\u7136\u9a88\u8d77\u53cc\u6307\uff0c\u500f\u7684\u5411\u6c5f\u5357\u80f8\u53e3\u7684\u201c\u7487\u7391 \u7a74\u201d\u70b9\u53bb\uff0c\u8fd9\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u8427\u9752\u5cf0\u5927\u9a87\uff0c\u5fc3\u9053\uff0c\u4ed6\u7eb5\u591a\u5634\uff0c\u62db\u60f9\u4e86\u4f60\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u6b7b\u5440\uff01 \u4f46\u5510\u7ecf\u5929\u51fa\u624b\u5982\u7535\uff0c\u8427\u9752\u5cf0\u90a3\u80fd\u62e6\u963b\uff1f \u3000\u3000\u53ea\u542c\u5f97\u6c5f\u5357\u563b\u563b\u4e00\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u597d\u75d2\uff0c\u597d\u75d2\uff01\u6211\u6700\u6015\u75d2\uff0c\u5510\u76f8\u516c\uff0c\u6211\u4e0d\u548c\u4f60\u95f9\uff01\u201d\u5510\u7ecf \u5929\u9053\uff1a\u201c\u809a\u5b50\u8fd8\u75db\u4e0d\u75db\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u54a6\uff0c\u5947\u602a\uff0c\u4e00\u75d2\u5c31\u4e0d\u75db\u4e86\u3002\u201d\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u51fa \u53cc\u6307\uff0c\u8f7b\u8f7b\u5728\u4ed6\u80a9\u4e0a\u4e00\u5f39\uff0c\u8427\u9752\u5cf0\u7ad9\u5728\u65c1\u8fb9\uff0c\u770b\u5f97\u771f\u5207\uff0c\u8fd9\u6b63\u662f\u201c\u901a\u6d77\u7a74\u201d\u7684\u6240\u5728\uff0c\u6309\u6469\u8fd9 \u4e2a\u5730\u65b9\uff0c\u53ef\u4ee5\u8212\u7b4b\u6d3b\u8840\uff0c\u5e73\u65f6\u6b66\u6797\u4e2d\u4eba\uff0c\u82e5\u88ab\u654c\u4eba\u70b9\u4e86\u5176\u4ed6\u7a74\u9053\uff0c\u4e00\u65f6\u4e0d\u77e5\u9053\u89e3\u7a74\u4e4b\u6cd5\uff0c\u5c31 \u8bf7\u4eba\u70b9\u4ed6\u7684\u201c\u901a\u6d77\u7a74\u201d\u4f7f\u8840\u8109\u6d41\u901a\uff0c\u7eb5\u4e0d\u80fd\u89e3\uff0c\u4ea6\u53ef\u5ef6\u957f\u65f6\u523b\uff0c\u6240\u4ee5\u70b9\u8fd9\u4e2a\u7a74\u9053\uff0c\u53ea\u6709\u76ca\uff0c \u7edd\u65e0\u5bb3\u3002\u4e0d\u6599\u5510\u7ecf\u5929\u53ea\u662f\u90a3\u4e48\u8f7b\u8f7b\u7684\u4e00\u5f39\uff0c\u6c5f\u5357\u53c8\u6367\u8179\u53eb\u9053\uff1a\u201c\u54ce\u54df\uff0c\u597d\u75db\uff0c\u597d\u75db\uff01\u201d\u5510\u7ecf \u5929\u6025\u5fd9\u4f38\u6307\uff0c\u53c8\u5728\u4ed6\u5c0f\u8179\u4e0a\u7684\u201c\u5fd7\u5802\u7a74\u201d\u4e00\u6233\uff0c\u8fd9\u201c\u5fd7\u5802\u7a74\u201d\u4e5f\u662f\u4e0a\u8eab\u4e5d\u5904\u6b7b\u7a74\u4e4b\u4e00\uff0c\u8427\u9752 \u5cf0\u53c8\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u6c5f\u5357\u53c8\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u5510\u76f8\u516c\u4f60\u662f\u600e\u4e48\u5f04\u7684\uff0c\u6211\u53c8\u4e0d\u75db\u4e86\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1b \u201c\u75d2\u4e0d\u75d2\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e0d\u75d2\uff0c\u53ea\u662f\u6709\u70b9\u9ebb\u6728\u3002\u201d\u5510\u7ecf\u5929\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4e0d\u662f\u6211\u4f5c \u5f04\u4f60\uff0c\u8fd9\u662f\u4f60\u5e08\u7236\u4f5c\u5f04\u4f60\u7684\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u5927\u5947\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u662f\u90a3\u4e2a\u8001\u9053\u58eb\u505a\u7684\u624b\u811a\u4e48\uff1f\u770b\u4ed6\u5982\u6b64\u6b66\u529f\uff0c\u5982\u6b64\u8eab\u4efd\uff0c\u65e2 \u7136\u4eb2\u53e3\u7b54\u5141\u4e86\u6c5f\u5357\uff0c\u8ba9\u4ed6\u51fa\u53bb\uff0c\u6c38\u4e0d\u8ffd\u7a76\uff0c\u600e\u4e48\u53c8\u8981\u4f5c\u5f04\u4ed6\uff1f\u201d\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8bf4\u8d77 \u6765\u4e5f\u7b97\u4e0d\u5f97\u662f\u6349\u5f04\uff0c\u53ef\u80fd\u8fd8\u662f\u6c5f\u5357\u7684\u597d\u9020\u5316\u5462\uff01\u201d\u8427\u9752\u5cf0\u8be7\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d\u5510\u7ecf\u5929\u6c89\u541f \u534a\u664c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u8427\u5148\u751f\uff0c\u4f60\u8bf4\u90a3\u4e2a\u60f3\u4e0e\u5192\u8001\u524d\u8f88\u4e3a\u96be\u7684\u5025\u4f97\u6d3e\u5947\u4eba\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u7684\u540d\u5b57 \uff0c\u4f4f\u5728\u4f55\u65b9\u5417\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5c31\u662f\u4e0d\u77e5\u5440\uff0c\u82e5\u7136\u77e5\u9053\uff0c\u6211\u65e9\u5c31\u7980\u544a\u5192\u5927\u4fa0\u4e86\uff0c\u4f55\u987b\u56db\u5904\u6253 \u542c\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u5728\u5929\u5c71\u4e4b\u65f6\uff0c\u66fe\u542c\u7236\u4eb2\u548c\u59e8\u7236\u8c08\u8bba\uff0c\u8bf4\u662f\u5025\u4f97\u53e4\u4f20\u6709\u4e00\u79cd\u7ec3\u529f\u4e4b\u6cd5\u3002 \u53ef\u4ee5\u5c06\u7ecf\u8109\u7684\u8fd0\u884c\u6253\u4e71\uff0c\u4ee5\u9006\u4e3a\u6b63\uff0c\u4ee5\u6b63\u4e3a\u9006\u3002\u6240\u4ee5\u70b9\u4e86\u6b7b\u7a74\u53cd\u800c\u65e0\u4e8b\uff0c\u4f46\u8fd9\u79cd\u529f\u592b\uff0c\u5fc5\u987b \u7ec8\u751f\u4e0d\u65ad\u7684\u7ec3\uff0c\u4e00\u95f4\u65ad\u5c31\u4e8e\u4eba\u6709\u5bb3\u3002\u800c\u4e14\u5373\u7b97\u7ec8\u751f\u82e6\u7ec3\uff0c\u4e5f\u96be\u4fdd\u4e0d\u4f1a\u8d70\u706b\u5165\u9b54\u3002\u6240\u4ee5\u540e\u6765\u5c11 \u6709\u80af\u7ec3\uff0c\u8fd9\u79cd\u529f\u592b\u5c31\u5931\u4f20\u4e86\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u83ab\u975e\u90a3\u8001\u9053\u58eb\u6559\u6c5f\u5357\u6240\u7ec3\u7684\uff0c\u5c31\u662f \u8fd9\u79cd\u529f\u592b\u5417\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff0c\u201c\u6211\u770b\u591a\u534a\u662f\u4e86\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6c5f\u5357\u5982\u4eca\u4e0e\u4ed6\u867d\u7136\u7edd\u4e86 \u5e08\u5f92\u4e4b\u4efd\uff0c\u5c82\u975e\u4e5f\u8981\u7ec8\u751f\u7ec3\u4ed6\u8fd9\u79cd\u529f\u592b\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6c5f\u5357\u53ea\u5728\u4ed6\u95e8\u4e0b\u4e03\u5929\uff0c\u5b66\u7684\u4e0d\u8fc7\u662f \u6700\u521d\u6b65\u7684\u529f\u592b\uff0c\u8fd9\u79cd\u529f\u592b\u4e5f\u662f\u8981\u8bb2\u7a76\u5a5a\u5e8f\u6e10\u8fdb\uff0c\u7531\u6d45\u4eba\u6df1\u7684\uff0c\u975e\u5f97\u5e08\u7236\u4f20\u6388\uff0c\u4ed6\u54ea\u80fd\u7ee7\u7eed\u7ec3 \u529f\uff1f\u4e0d\u8fc7\uff0c\u597d\u5728\u65f6\u65e5\u8fd8\u6d45\uff0c\u53d1\u4f5c\u8d77\u6765\u3002\u4e5f\u4e0d\u8fc7\u662f\u809a\u75db\u3001\u9aa8\u75db\u3001\u8170\u9178\u3001\u811a\u8f6f\u800c\u5df2\uff0c\u82e5\u7136\u65f6\u65e5\u6df1 \u4e86\uff0c\u53d1\u4f5c\u8d77\u6765\uff0c\u4e0d\u6b7b\u4e5f\u6210\u6b8b\u5e9f\u3002\u6240\u4ee5\u5728\u6570\u767e\u5e74\u524d\uff0c\u5025\u4f97\u6d3e\u4e2d\uff0c\u51e1\u662f\u7ec3\u8fd9\u79cd\u529f\u592b\u7684\uff0c\u90fd\u4e0d\u6562\u79bb \u5f00\u5e08\u95e8\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6c5f\u5357\u5c82\u4e0d\u662f\u8981\u91cd\u56de\u90a3\u53e4\u602a\u7684\u6797\u5b50\u91cc\uff0c\u4e00\u751f\u4f34\u90a3\u4e2a\u8001\u5996\u9053 \uff1f\u201d\u6c5f\u5357\u53eb\u9053\uff0c\u201c\u6211\u6b7b\u4e5f\u4e0d\u53bb\uff0c\u90a3\u8001\u5996\u9053\u4e0d\u6253\u6b7b\u6211\uff0c\u6211\u95f7\u4e5f\u95f7\u6b7b\u4e86\u3002\u5510\u76f8\u516c\uff0c\u4f60\u5f97\u66ff\u6211\u60f3\u6cd5 \u5440\uff0c\u6211\u4e0d\u53bb\uff0c\u4e0d\u53bb\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u4e0d\u53bb\u4e5f\u884c\uff0c\u90a3\u4f60\u5f97\u957f\u5e74\u56db\u5b63\uff0c\u6bcf\u5929\u809a\u75db\u4e00\u4e2a\u65f6\u8fb0\u3002\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u4e0d\uff0c \u6211\u6700\u6015\u809a\u75db\uff0c\u809a\u75db\u4e86\u5c31\u5403\u4e0d\u5f97\u4e1c\u897f\uff0c\u90a3\u591a\u7cdf\u7cd5\u3002\u5510\u76f8\u516c\uff0c\u4f60\u4e00\u5b9a\u4f1a\u6cbb\uff0c\u4f60\u66ff\u6211\u6cbb\u4e86\uff0c\u8bf4\u4ec0\u4e48 \u6211\u4e5f\u7b54\u5e94\u3002\u201d\u5eb8\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u6211\u7ed9\u4f60\u6cbb\u4e86\uff0c\u4ee5\u540e\u4f60\u4e0d\u8bb8\u518d\u591a\u5634\u3002\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u6210\uff0c\u6210 \uff0c\u4f60\u7ed9\u6211\u6cbb\u4e86\uff0c\u4ee5\u540e\u522b\u4eba\u95ee\u6211\u4e00\u53e5\uff0c\u6211\u53ea\u7b54\u534a\u53e5\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u7981\u4e0d\u4f4f\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u5bf9\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\u8fd9\u662f\u6c5f\u5357\u7684\u9020\u5316\u4e86\u3002\u5f53\u65e5\u6211\u7236\u4eb2 \u548c\u59e8\u7236\u8c08\u8bba\uff0c\u4f60\u77e5\u9053\u6211\u59e8\u7236\u66fe\u5f97\u5085\u9752\u4e3b\u6240\u9057\u4e0b\u7684\u533b\u4e66\uff0c\u7cbe\u4e8e\u533b\u7406\uff0c\u5728\u5085\u9752\u4e3b\u7684\u533b\u4e66\u4e2d\uff0c\u4e5f\u66fe \u8c08\u5230\u8fd9\u79cd\u7ec3\u529f\u4e4b\u5bb3\uff0c\u636e\u8bf4\u8981\u514d\u6b64\u5bb3\uff0c\u53ea\u6709\u7ec3\u6b63\u6d3e\u7684\u6700\u4e0a\u4e58\u5185\u529f\uff0c\u628a\u4e94\u810f\u516d\u8151\u90fd\u7ec3\u5f97\u767e\u90aa\u4e0d\u4fb5 \uff0c\u90a3\u81ea\u7136\u6ca1\u4e8b\u4e86\u3002\u6240\u4ee5\u6211\u53ea\u597d\u4f20\u6388\u6c5f\u5357\u4e00\u70b9\u6211\u6d3e\u5185\u529f\u7684\u7a8d\u8981\u4e86\u3002\u201d\u6c5f\u5357\u5927\u559c\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211 \u7ed9\u4f60\u78d5\u5934\uff0c\u53eb\u4f60\u505a\u5e08\u7236\u3002\u201d\u8bf4\u4e86\u5c31\u505a\uff0c\u8dea\u4e0b\u78d5\u5934\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8f7b\u8f7b\u4e00\u62e6\uff0c\u6c5f\u5357\u5168\u8eab\u633a\u76f4\uff0c\u8dea\u4e0d\u4e0b\u53bb\uff0c\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u6211\u624d\u4e0d\u8981\u4f60\u8fd9\u4e2a\u591a\u5634\u7684\u5f92 \u5f1f\u5462\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u54ce\u54df\uff0c\u6211\u65e9\u8bf4\u8fc7\u4e0d\u591a\u5634\u4e86\u3002\u201d\u5510\u7ecf\u5929\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6211\u5929\u5c71\u6d3e\u6536\u5f92 \u6700\u4e25\uff0c\u6211\u5e74\u7eaa\u53c8\u8f7b\uff0c\u4f60\u8981\u62dc\u6211\u4e3a\u5e08\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u53ef\u3002\u800c\u5de8\uff0c\u6211\u53ea\u4f20\u4f60\u4e00\u4e9b\u5185\u529f\u7684\u7a8d\u8bc0\uff0c\u4ea6\u5e76 \u975e\u5168\u8c79\uff0c\u5176\u4ed6\u5251\u8bc0\u62f3\u6280\u7b49\u66f4\u4e00\u6982\u4e0d\u4f20\uff0c\u4f60\u4e0d\u80fd\u7b97\u662f\u5929\u5c71\u5f1f\u5b50\u3002\u201d\u8427\u9752\u5cf0\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u5f97\u5230 \u5929\u5c71\u6d3e\u7684\u5185\u529f\u7a8d\u8bc0\uff0c\u90a3\u5df2\u7ecf\u662f\u6bd5\u751f\u5f02\u6570\uff0c\u4f60\u5c1a\u672a\u77e5\u8db3\uff0c\u60f3\u5f97\u9647\u671b\u8700\u5417\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u54ce\u54df\uff0c \u539f\u6765\u62dc\u5e08\u7236\u8fd8\u6709\u8fd9\u4e48\u4e9b\u8bb2\u7a76\uff0c\u6211\u53ea\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c\u6240\u4ee5\u624d\u60f3\u62dc\u5e08\u7236\u7f62l\u201d\uff0c\u4f60\u65e2\u4e0d\u8981\u6211\u505a\u5f92\u5f1f \uff0c\u90a3\u66f4\u597d\uff0c\u6211\u5c11\u5f97\u4e00\u4e2a\u4eba\u7ba1\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u77a7\uff0c\u4f60\u53c8\u591a\u5634\u4e86\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u8bf4\uff0c\u4e0d \u8bf4\uff01\u4f60\u7ed9\u6211\u6cbb\u4e86\uff0c\u6211\u8fde\u591a\u8c22\u4e5f\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u751a\u662f\u6b21\u559c\u6c5f\u5357\uff0c\u5148\u7ed9\u4ed6\u5403\u4e86\u4e24\u9897\u7528\u5929\u5c71\u96ea\u83b2\u5408\u6210\u7684\u78a7\u7075\u4e39\uff0c\u589e\u957f\u4ed6\u7684\u771f\u5143\u4e4b\u6c14\uff0c \u7136\u540e\u6388\u4ed6\u7684\u5185\u529f\u7a8d\u8981\u3002\u6c5f\u5357\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\uff0c\u4ed6\u8fd9\u4e00\u4e0b\u53ef\u662f\u53d7\u76ca\u975e\u6d45\uff0c\u65e2\u6709\u4e86\u5025\u4f97\u6d3e\u6d3e\u5947\u529f\u7684 \u5e95\u5b50\uff0c\u4e0d\u6015\u4eba\u70b9\u7a74\uff0c\u53c8\u5f97\u4e86\u5929\u5c71\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u81ea\u6b64\u529f\u529b\u5927\u589e\uff0c\u65e5\u540e\u7adf\u6210\u4e3a\u6b66\u6797\u4e2d\u4e00\u4f4d\u54cd\u5f53\u5f53 \u7684\u4eba\u7269\uff0c\u8fd9\u662f\u540e\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u5df2\u8bf4\u5510\u7ecf\u5929\u4e3a\u4e86\u4f20\u6388\u6c5f\u5357\u7684\u5185\u529f\uff0c\u4e8c\u65e5\u6765\u53ea\u884c\u4e86\u767e\u591a\u91cc\u8def\uff0c\u8fd8\u7b97\u6c5f\u5357\u806a\u660e\uff0c\u7b2c\u56db\u65e5\u5df1\u5fc3 \u9886\u795e\u4f1a\uff0c\u5c3d\u5f97\u6240\u4f20\uff0c\u5510\u7ecf\u5929\u9042\u548c\u6c5f\u5357\u5206\u624b\u3002\u6c5f\u5357\u4e1c\u4e0b\u91cd\u5e86\uff0c\u51c6\u5907\u4ece\u91cd\u5e86\u4e58\u8239\u4e09\u5ce1\uff0c\u81ea\u6b66\u6c49\u53d6 \u9053\u4e0a\u4eac\u9001\u4fe1\uff1a\u5510\u7ecf\u5929\u548c\u8427\u9752\u5cf0\u592b\u5987\u5f80\u5ddd\u5357\uff0c\u51c6\u5907\u4e0a\u5ce8\u5d4b\u5c71\u62dc\u4f1a\u5192\u5ddd\u751f\uff0c\u4ed6\u4eec\u65e5\u591c\u517c\u7a0b\u3002\u8d70\u4e86 \u5341\u5929\uff0c\u5ce8\u5d4b\u5c71\u5df2\u7ecf\u5728\u671b\u3002\u8d8a\u8fd1\u5ce8\u5d4b\uff0c\u5510\u7ecf\u4eba\u8d8a\u662f\u60c5\u601d\u7ee6\u4e71\uff0c\u60f3\u8d77\u5373\u53ef\u89c1\u5230\u51b0\u5ddd\u5929\u5973\uff0c\u81ea\u662f\u8877 \u5fc3\u6b22\u559c\uff0c\u4f46\u60f3\u8d77\u90a3\u201c\u75af\u4e10\u201d\u548c\u5979\u4e00\u8d77\uff0c\u89c1\u4e86\u4e4b\u540e\uff0c\u4e0d\u77e5\u5982\u4f55\uff1f\u53c8\u4e0d\u7981\u9eef\u7136\u3002 \u3000\u3000\u5192\u5ddd\u751f\u548c\u5ce8\u5d4b\u5c71\u91d1\u5149\u5bfa\u7684\u957f\u8001\u662f\u65b9\u5916\u81f3\u4ea4\uff0c\u6240\u4ee5\u4e8c\u5341\u591a\u5e74\u6765\uff0c\u90fd\u5c45\u5728\u91d1\u5149\u5bfa\u91cc\uff0c\u8fd9\u6b21\u7684 \u201c\u5f00\u5c71\u7ed3\u7f18\u201d\u4e5f\u5728\u91d1\u5149\u5bfa\u4e3e\u884c\u3002\u91d1\u5149\u5bfa\u5efa\u5728\u5ce8\u5d4b\u7684\u6700\u9ad8\u5904\u2014\u2014\u91d1\u9876\uff0c\u5510\u7ecf\u5929\u7b49\u4eba\u8d76\u5230\u4e4b\u65f6\uff0c \u5df2\u7ecf\u201d\u662f\u76db\u4f1a\u7684\u524d\u5915\u4e86\u3002 \u3000\u3000\u5ce8\u5d4b\u662f\u4e2d\u56fd\u7684\u4f5b\u6559\u56db\u5927\u540d\u5c71\u4e4b\u4e00\uff08\u5176\u4f59\u4e09\u5904\u662f\u6d59\u6c5f\u7684\u666e\u9640\u5c71\u3001\u5b89\u5fbd\u7684\u4e5d\u534e\u5c71\u548c\u5c71\u897f\u7684\u4e94 \u53f0\u5c71\uff09\u3002\u7eb5\u6a2a\u56db\u767e\u4f59\u91cc\uff0c\u5c71\u52bf\u65e2\u96c4\u4f1f\u800c\u53c8\u79c0\u4e3d\uff0c\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u5c31\u50cf\u4e24\u9053\u6e05\u79c0\u7684\u6d53\u7709\uff0c\u5ce8\u5d4b\u4fbf \u662f\u7531\u6b64\u5f97\u540d\u7684\u3002\u5510\u7ecf\u5929\u7b49\u4e00\u884c\u4e09\u4eba\uff0c\u6668\u65e9\u767b\u5c71\uff0c\u4f46\u89c1\u82cd\u677e\u4ea4\u9053\uff0c\u602a\u77f3\u9cde\u5ccb\uff0c\u7011\u5e03\u98de\u60ac\uff0c\u6d41\u6cc9 \u5e7d\u51b7\uff0c\u201c\u5ce8\u5d4b\u5929\u4e0b\u79c0\u201d\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u5510\u7ecf\u5929\u867d\u662f\u6ee1\u6000\u5fc3\u4e8b\uff0c\u81f3\u6b64\u4ea6\u89c9\u80f8\u895f\u4e00\u723d\u3002 \u3000\u3000\u5c71\u5f84\u4e0a\uff0c\u6811\u6797\u4e2d\uff0c\u65f6\u4e0d\u65f6\u89c1\u6709\u4e09\u4e94\u6210\u7fa4\u7684\u80cc\u5f71\uff0c\u90a3\u81ea\u7136\u662f\u6765\u671d\u5c71\u542c\u8bb2\u7684\u5404\u65b9\u4eba\u7269\u4e86\u3002\u5510 \u7ecf\u5929\u4e00\u5411\u50fb\u5904\u5929\u5c71\uff0c\u672a\u66fe\u5230\u8fc7\u4e2d\u539f\uff0c\u8427\u9752\u5cf0\u4ea6\u9690\u5c45\u5728\u897f\u85cf\u5341\u6709\u4f59\u5e74\uff0c\u97f3\u5bb9\u5df2\u6539\uff0c\u90a3\u4e9b\u6c5f\u6e56\u4eba \u7269\u65e0\u4e00\u8ba4\u8bc6\u4ed6\u4eec\u3002\u53ea\u5f53\u4ed6\u4eec\u4e5f\u662f\u6765\u5411\u5192\u5ddd\u751f\u8bf7\u76ca\u7684\u540e\u8f88\u3002 \u3000\u3000\u5510\u7ecf\u5929\u7b49\u4e09\u4eba\u90fd\u5177\u6709\u4e00\u8eab\u4e0a\u597d\u7684\u8f7b\u529f\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u4fbf\u5230\u4e86\u5ce8\u5d4b\u7684\u6700\u9ad8\u5904\u201c\u91d1\u9876\u201d\u3002\u4ece\u91d1 \u9876\u773a\u671b\u56db\u5468\uff0c\u4f46\u89c1\u5cf0\u5ce6\u53e0\u53e0\uff0c\u4e91\u70df\u56db\u8d77\uff0c\u7aef\u7684\u662f\u53d8\u5316\u4e07\u5343\uff0c\u4e0d\u53ef\u540d\u72b6\u3002\u91d1\u5149\u5bfa\u5efa\u5728\u5c71\u5dc5\uff0c\u5c31 \u50cf\u9690\u85cf\u5728\u4e91\u70df\u4e4b\u95f4\u3002\u5510\u7ecf\u5929\u548c\u8427\u9752\u5cf0\u592b\u5987\uff0c\u8fdb\u5165\u5bfa\u95e8\uff0c\u6709\u4e2a\u77e5\u5ba2\u50e7\u524d\u6765\u8fce\u63a5\uff0c\u5510\u7ecf\u5929\u95ee\u9053\uff1a \u201c\u5192\u5927\u4fa0\u7cbe\u795e\u597d\u4e48\uff0c\u70e6\u4f60\u66ff\u6211\u4eec\u7980\u62a5\u4e00\u58f0\uff0c\u8bf4\u662f\u6709\u4ed6\u7684\u5b50\u4f84\u8f88\u6c42\u89c1\u3002\u201d\u77e5\u5ba2\u50e7\u770b\u4e86\u4ed6\u4eec\u4e00\u773c \uff0c\u5408\u4ec0\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5192\u5927\u4fa0\u5df2\u5165\u5b9a\u4e09\u65e5\uff0c\u6211\u4e0d\u4fbf\u53bb\u60ca\u52a8\u4ed6\u3002\u53cd\u6b63\u660e\u513f\u4f60\u4eec\u4fbf\u53ef\u89c1\u5230\uff0c\u4e5f\u4e0d \u5fc5\u591a\u793c\u4e86\u3002\u201d\u90a3\u77e5\u5ba2\u50e7\u4e5f\u662f\u4e00\u70b9\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u53ea\u5f53\u4ed6\u4eec\u662f\u5c11\u5e74\u540e\u8f88\u3002\u987b\u77e5\u4ee5\u5192\u5ddd\u751f\u7684 \u8eab\u4efd\uff0c\u6765\u6b64\u671d\u5c71\u542c\u8bb2\u4e4b\u4eba\uff0c\u5341\u6709\u516b\u4e5d\u90fd\u8ba4\u662f\u4ed6\u7684\u201c\u5b50\u4f84\u8f88\u201d\uff0c\u4e5f\u6709\u4e0d\u5c11\u5e0c\u5180\u80fd\u5355\u72ec\u4f1a\u89c1\u5192\u5ddd \u751f\u7684\uff0c\u82e5\u7136\u6765\u8005\u4e0d\u62d2\uff0c\u5192\u5ddd\u751f\u54ea\u89c1\u5f97\u8bb8\u591a\uff0c\u6545\u6b64\u83ab\u8bf4\u5192\u5ddd\u751f\u771f\u662f\u5165\u5b9a\uff0c\u5373\u7b97\u4e0d\u662f\u5165\u5b9a\uff0c\u77e5\u5ba2 \u50e7\u4e5f\u4e0d\u4f1a\u66ff\u4ed6\u4eec\u5f15\u89c1\u7684\u3002\u77e5\u5ba2\u50e7\u5c06\u4ed6\u4eec\u5b89\u7f6e\u5728\u4e24\u95f4\u50e7\u623f\u5185\uff0c\u4fbf\u53c8\u5fd9\u7740\u62db\u5f85\u5176\u4ed6\u6709\u5934\u9762\u7684\u4eba\u7269 \u4e86\u3002 \u3000\u3000\u5192\u5ddd\u751f\u662f\u6b66\u5f53\u6d3e\u540d\u5bbf\uff0c\u6765\u542c\u8bb2\u201c\u7ed3\u7f18\u201d\u7684\u4eba\u81ea\u662f\u4ee5\u6b66\u5f53\u6d3e\u7684\u4e3a\u6700\u591a\uff0c\u4ed6\u4eec\u4e0d\u77e5\u4ece\u54ea\u513f\u542c \u6765\u7684\u98ce\u58f0\uff0c\u4e5f\u9690\u7ea6\u77e5\u9053\u4eca\u5e74\u53ef\u80fd\u6709\u4eba\u6363\u4e71\uff0c\u90fd\u5728\u4e09\u4e09\u4e94\u4e94\u7684\u8c08\u8bba\u3002\u6709\u7684\u8bf4\u82e5\u7136\u8981\u5192\u5ddd\u751f\u4eb2\u81ea \u51fa\u624b\uff0c\u90a3\u5c31\u662f\u6b66\u5f53\u6d3e\u7684\u5947\u803b\u5927\u8fb1\u4e86\uff0c\u6709\u7684\u8bf4\u6b66\u5f53\u5251\u6cd5\uff0c\u5a01\u9707\u56db\u6d77\uff0c\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u4e5f \u4e0d\u8db3\u5f53\u6211\u4eec\u540e\u8f88\u7684\u4e00\u51fb\uff0c\u6709\u8c01\u6562\u6765\u6363\u4e71\uff0c\u6562\u60c5\u8fd9\u6839\u672c\u5c31\u662f\u8c23\u8a00\u3002\u5510\u7ecf\u5929\u542c\u5728\u8033\u4e2d\uff0c\u6697\u6697\u597d\u7b11 \uff0c\u5374\u4e5f\u6674\u6697\u62c5\u5fc3\u3002\u662f\u591c\uff0c\u5510\u7ecf\u5929\u95ed\u76ee\u8c03\u795e\uff0c\u505a\u4e86\u4e00\u4e2a\u65f6\u8fb0\u7684\u5185\u529f\u529f\u8bfe\uff0c\u5230\u4e86\u4e2d\u591c\uff0c\u63a8\u7a97\u4e00\u770b \uff0c\u53ea\u89c1\u6708\u534e\u5982\u7ec3\uff0c\u5916\u9762\u5c71\u5934\uff0c\u5ffd\u7136\u770b\u89c1\u5982\u8424\u5149\u822c\u7684\u70b9\u70b9\u706b\u5149\uff0c\u7531\u5c11\u800c\u591a\uff0c\u5189\u5189\u5347\u8d77\uff0c\u98d8\u5ffd\u4e0d \u5b9a\uff0c\u4e0e\u5929\u7a7a\u4e2d\u7684\u57a6\u6708\u4e4b\u5149\u76f8\u4e92\u8f89\u6620\u3002 \u3000\u3000\u8fd9\u662f\u5ce8\u5d4b\u5c71\u7279\u6709\u7684\u5947\u666f\uff0c\u4f5b\u6559\u4eba\u58eb\u79f0\u4e3a\u201c\u5723\u706f\u201d\uff0c\u6bcf\u5f53\u5929\u6c14\u6674\u6717\u7684\u665a\u4e0a\uff0c\u4fbf\u6709\u70b9\u70b9\u8424\u5149 \u51fa\u73b0\uff0c\u8d8a\u805a\u8d8a\u591a\uff0c\u604d\u5982\u5728\u7a7a\u9645\u98d8\u6d6e\u7684\u4e07\u70b9\u706f\u5149\uff0c\u6545\u6b64\u79f0\u4e3a\u201c\u5723\u706f\u201d\uff0c\u5176\u5b9e\u4e43\u662f\u56e0\u4e3a\u5ce8\u5d4b\u5c71\u7279 \u591a\u78f7\u77ff\uff0c\u6240\u8c13\u201c\u5723\u706f\u201d\uff0c\u5b9e\u9645\u5c31\u662f\u5c71\u4e2d\u7684\u78f7\u5149\u3002 \u3000\u3000\u91d1\u5149\u5bfa\u5bfa\u89c4\u6700\u4e25\uff0c\u53c8\u5f53\u6cd5\u4f1a\u5b8f\u5f00\u7684\u524d\u5915\uff0c\u6c14\u6c1b\u8083\u7a46\uff0c\u5bfa\u4e2d\u7684\u50e7\u4f17\u4e0e\u5404\u65b9\u6765\u7684\u5ba2\u4eba\u5408\u8ba1\u6709 \u6570\u767e\u4eba\u4e4b\u591a\uff0c\u5374\u65e0\u4e00\u70b9\u58f0\u54cd\u3002\u5510\u7ecf\u5929\u4e2d\u591c\u65e0\u7720\uff0c\u51ed\u7a97\u9065\u671b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b64\u95f4\u4e00\u7247\u5b81\u9759\u548c\u5e73 \uff0c\u82e5\u7136\u771f\u4e2a\u6709\u4eba\u6363\u4e71\uff0c\u53ef\u662f\u5927\u715e\u98ce\u666f\u3002\u201d\u968f\u5373\u60f3\u8d77\u77f3\u6797\u4e2d\u90a3\u4e2a\u9ec4\u77f3\u9053\u4eba\uff0c\u4e0d\u77e5\u4ed6\u662f\u5426\u5c31\u662f\u8427 \u9752\u5cf0\u6240\u8bf4\u7684\u90a3\u4e2a\u5025\u4f97\u5947\u58eb\uff0c\u82e5\u7136\u662f\u4ed6\uff0c\u81ea\u5df1\u4e00\u4eba\u53ef\u96be\u5bf9\u4ed8\uff1b\u5ffd\u5730\u53c8\u60f3\u8d77\u4e86\u51b0\u5ddd\u5929\u5973\uff0c\u82e5\u7136\u4e0e \u5979\u8054\u624b\u5e94\u654c\uff0c\u90a3\u4e48\u5c31\u662f\u5bf9\u4ed8\u6bd4\u9ec4\u77f3\u9053\u4eba\u66f4\u5f3a\u7684\u654c\u4eba\uff0c\u4ea6\u4e0d\u8db3\u4e3a\u8651\u4e86\u3002\u60f3\u5230\u6b64\u5904\uff0c\u8111\u6d77\u4e2d\u5ffd\u5730 \u53c8\u6d6e\u8d77\u91d1\u4e16\u9057\u90a3\u8d56\u76ae\u7b11\u8138\u7684\u65e0\u8d56\u795e\u6c14\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u4f1a\u504f\u504f\u8ddf\u4ed6\u4e00\u8d77\uff0c\u5b9e\u662f\u4ee4\u4eba\u96be\u89e3\u3002\u8d8a\u60f3\u60c5 \u601d\u8d8a\u4e71\uff0c\u5fc3\u4e2d\u90c1\u90c1\u4e0d\u4e50\uff0c\u9042\u62ab\u8863\u800c\u8d77\uff0c\u60f3\u5230\u9694\u623f\u627e\u8427\u9752\u5cf0\u592b\u5987\u591c\u8bdd\uff0c\u54ea\u77e5\u8427\u9752\u5cf0\u592b\u5987\u5df2\u4e0d\u77e5 \u4f55\u5f80\u3002 \u3000\u3000\u539f\u6765\u8427\u9752\u5cf0\u6b64\u65f6\u4e5f\u662f\u60c5\u601d\u5982\u6f6e\uff0c\u4ed6\u8fd9\u6b21\u662f\u7b2c\u4e8c\u6b21\u53c2\u52a0\u5192\u5ddd\u751f\u7684\u201c\u7ed3\u7f18\u201d\u76db\u4f1a\uff0c\u60f3\u8d77\u4e0a\u6b21 \u5728\u76db\u4f1a\u7684\u524d\u5915\uff0c\u95f9\u51fa\u4e86\u8c22\u4e91\u771f\u4e0e\u96f7\u9707\u5b50\u6bd4\u5251\u4e4b\u4e8b\uff0c\u81ea\u5df1\u65e0\u7f18\u65e0\u6545\u7684\u88ab\u5377\u5165\u6f29\u6da1\uff0c\u201c\u4ee5\u81f3\u4e0e\u96f7 \u9707\u5b50\u4ed6\u4eec\u7ed3\u4e86\u5927\u4ec7\uff0c\u8fdc\u907f\u897f\u85cf\uff0c\u51e0\u4e4e\u8001\u6b7b\u5f02\u4e61\uff0c\u800c\u4eca\u5c48\u6307\u6570\u6765\uff0c\u53c8\u5c06\u8fd1\u4e8c\u5341\u5e74\u4e86\u3002\u5e78\u800c\u53bb\u5e74 \u5728\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u4e0e\u96f7\u9707\u5b50\u89e3\u4e86\u524d\u4ec7\uff0c\u4e07\u91cc\u5f52\u6765\uff0c\u53c8\u505a\u4e86\u65b0\u90ce\uff0c\u800c\u4eca\u518d\u5230\u5ce8\u5d4b\uff0c\u91cd\u53c2\u76db\u4f1a\uff0c\u5fc3\u4e2d \u81ea\u662f\u65e0\u9650\u611f\u6168\u3002\u8427\u9752\u5cf0\u7684\u59bb\u5b50\u81ea\u7136\u77e5\u9053\u4e08\u592b\u7684\u5fc3\u610f\uff0c\u4e00\u65f6\u5174\u8d77\uff0c\u4fbf\u8981\u4e08\u592b\u5e26\u5979\u5230\u5f53\u5e74\u6bd4\u5251\u7684 \u5730\u65b9\u4e00\u770b\u3002 \u3000\u3000\u540c\u6837\u662f\u76db\u4f1a\u7684\u524d\u5915\uff0c\u53ea\u662f\u90a3\u4e00\u665a\u661f\u6708\u65e0\u5149\uff0c\u4eca\u665a\u5374\u662f\u94f6\u6cb3\u660e\u51c0\uff0c\u591c\u7a7a\u768e\u6d01\uff0c\u66f4\u52a0\u4e0a\u7a7a\u4e2d \u98d8\u6d6e\u7684\u4e07\u70b9\u201c\u5723\u706f\u201d\uff0c\u534a\u91cc\u4e4b\u5185\u7684\u666f\u7269\u90fd\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u8427\u9752\u5cf0\u6307\u70b9\u5f53\u5e74\u6bd4\u5251\u7684\u6240\u5728\uff0c\u5c06\u90a3 \u4e00\u665a\u60ca\u9669\u7684\u60c5\u4e8b\uff0c\u548c\u59bb\u5b50\u7ec6\u8bf4\u3002\u8fd9\u4e9b\u4e8b\u60c5\u4ed6\u65e9\u5df2\u8bf4\u8fc7\u4e0d\u77e5\u591a\u5c11\u904d\u4e86\uff0c\u4f46\u5982\u4eca\u8eab\u5904\u5176\u5730\uff0c\u542c\u8d77 \u6765\u5c31\u66f4\u52a0\u771f\u5207\u3002 \u3000\u3000\u5434\u7edb\u4ed9\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u593a\u547d\u4ed9\u5b50\u8c22\u4e91\u771f\u73b0\u5728\u4e0d\u77e5\u4f55\u5f80\uff0c\u4f60\u8fd8\u601d\u5ff5\u5979\u4e48\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201d \u8c22\u4e91\u771f\u624b\u5e95\u72e0\u8fa3\uff0c\u4f46\u5374\u662f\u4e2a\u591f\u4ea4\u60c5\u7684\u670b\u53cb\uff0c\u5bf9\u597d\u670b\u53cb\u8c01\u90fd\u4f1a\u601d\u5ff5\u7684\u3002\u201d\u5434\u7edb\u4ed9\u9053\uff1a\u201c\u5c31\u662f\u8fd9 \u6837\u4e48\uff1f\u201d\u8427\u9752\u5cf0\u7eed\u9053\uff1a\u201c\u6211\u8fd8\u975e\u5e38\u7684\u611f\u8c22\u5979\uff0c\u539f\u6765\u5979\u6bd4\u6211\u66f4\u77e5\u9053\u4f60\u3002\u201d\u5434\u7edb\u4ed9\u9053\uff1a\u201c\u600e\u4e48\uff1f \u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5979\u8bf4\u4f60\u662f\u4e2a\u6e29\u67d4\u8d24\u6167\u7684\u597d\u5973\u5b50\u3002\u73b0\u5728\u6211\u53c8\u77e5\u9053\uff0c\u4f60\u8fd8\u662f\u4e2a\u6700\u5584\u4e8e\u4f53\u8d34\u4e08\u592b\u7684 \u59bb\u5b50\u3002\u53ef\u60dc\u6211\u662f\u4e2a\u7b28\u9a74\uff0c\u8981\u662f\u6211\u4e8c\u5341\u5e74\u524d\u5df2\u77e5\u9053\u4f60\u7684\u60c5\u610f\uff0c\u6211\u5c31\u4e0d\u4f1a\u8dd1\u5230\u897f\u85cf\u53bb\u6371\u90a3\u5341\u5e74\u4e4b \u82e6\u4e86\u3002\u201d\u8bdd\u4e2d\u5145\u6ee1\u871c\u610f\u67d4\u60c5\uff0c\u4ed6\u662f\u771f\u5b9e\u7684\u611f\u5230\u59bb\u5b50\u6bd4\u8c22\u4e91\u771f\u597d\u5f97\u591a\uff0c\u4e16\u4e0a\u6709\u5979\u90a3\u6837\u8c05\u89e3\u4e08\u592b \u4f53\u8d34\u4e08\u592b\u7684\u53ef\u771f\u96be\u5f97\u3002\u5434\u7edb\u4ed9\u5fae\u7b11\u9053\uff1a\u201c\u6211\u53ef\u771f\u60f3\u89c1\u8c22\u4e91\u771f\u4e00\u9762\u3002\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5979\u548c\u94c1\u62d0 \u4ed9\u73b0\u5728\u4e0d\u77e5\u662f\u5426\u8fd8\u5728\u897f\u85cf\uff0c\u600e\u80fd\u89c1\u5979\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\u5076\u7136\u4e00\u77a5\uff0c\u5ffd\u89c1\u8fdc\u5904\u91ce\u82b1\u4e1b\u4e2d\uff0c\u9690\u7ea6\u9732\u51fa \u4e00\u4e2a\u5c11\u5987\u7684\u9762\u5b54\u3002 \u3000\u3000\u90a3\u5c11\u5987\u8f6c\u4e86\u4e2a\u8eab\uff0c\u539f\u6765\u5979\u8fd8\u62b1\u7740\u4e00\u4e2a\u5a74\u5b69\uff0c\u5927\u7ea6\u662f\u91ce\u82b1\u7684\u679d\u53f6\u62c2\u7740\u4e86\u5a74\u5b69\u9163\u7761\u7684\u9762\u5b54\uff0c \u54c7\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\u3002\u8fd9\u5239\u90a3\u95ee\u8427\u9752\u5cf0\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5434\u7edb\u4ed9\u9053\uff1a\u201c\u54a6\uff0c\u5979\u662f\u8c01\uff1f \u201d\u201c\u8c22\u4e91\u771f\u201d\u4e09\u5b57\u9669\u9669\u5c31\u8981\u4ece\u8427\u9752\u5cf0\u53e3\u4e2d\u53eb\u51fa\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5c0f\u5996\u5987\uff0c\u4f60\u5c45\u7136\u8fd8\u6709\u80c6 \u91cf\u4e0a\u5ce8\u5d4b\u5c71\uff1f\u201d\u201c\u54c8\uff0c\u4f60\u5f53\u6211\u4eec\u8ba4\u4e0d\u5f97\u4e86\u4f60\u5417\uff1f\u518d\u8fc7\u4e8c\u5341\u5e74\uff0c\u4f60\u6b7b\u4e86\u53d8\u7070\u6211\u4eec\u8fd8\u8bb0\u5f97\u4f60\uff01\u201d \u201c\u6211\u4eec\u5012\u8981\u89c1\u8bc6\u89c1\u8bc6\u593a\u547d\u4ed9\u5b50\u7a76\u7adf\u662f\u600e\u6837\u8ffd\u5165\u7684\u9b42\uff0c\u593a\u4eba\u7684\u547d\uff1f\u201d\u58f0\u52bf\u6c79\u6c79\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf \u6765\u4e86\u56db\u540d\u9ed1\u8863\u9053\u58eb\uff0c\u6bcf\u4eba\u624b\u4e0a\uff0c\u90fd\u62ff\u7740\u4e00\u67c4\u95ea\u95ea\u53d1\u5149\u7684\u957f\u5251\uff0c\u5728\u79bb\u5f00\u8c22\u4e91\u771f\u5341\u4f59\u4e08\u8fdc\u7684\u5730\u65b9 \uff0c\u5206\u7ad9\u5728\u4e1c\u5357\u897f\u5317\u56db\u4e2a\u89d2\u843d\uff0c\u5c06\u5979\u56f4\u4f4f\u3002 \u3000\u3000\u8427\u9752\u5cf0\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u5165\u4e16\u95f4\u7684\u51a4\u4ec7\uff0c\u6709\u65f6\u771f\u662f\u7ed3\u5f97\u83ab\u540d\u5176\u5999\uff0c\u770b\u8fd9\u5149\u666f\uff0c\u5206\u660e\u662f\u8fd9\u51e0 \u4e2a\u9053\u571f\u8fd8\u8bb0\u7740\u4e8c\u5341\u5e74\u524d\u8c22\u4e91\u771f\u523a\u4f24\u4e86\u96f7\u9707\u5b50\u7684\u90a3\u4e00\u573a\u4ec7\u6068\uff0c\u5176\u5b9e\u90a3\u65f6\u7684\u96f7\u9707\u5b50\u9a84\u5984\u81ea\u5927\uff0c\u8bbe \u4e0b\u9677\u9636\uff0c\u6697\u7b97\u4f24\u4eba\u7b49\u7b49\u4e8b\u60c5\uff0c\u4ed6\u7684\u540c\u95e8\u5144\u5f1f\u53c8\u6709\u51e0\u4eba\u77e5\u9053\uff1f\u8427\u9752\u5cf0\u672c\u60f3\u51fa\u53bb\u529d\u89e3\uff0c\u4f46\u8f6c\u5ff5\u4e00 \u60f3\uff0c\u81ea\u5df1\u4e5f\u662f\u5f53\u65e5\u95ef\u4e0b\u7978\u4e8b\u7684\u4eba\uff0c\u82e5\u7136\u9732\u9762\uff0c\u8868\u660e\u8eab\u4efd\uff0c\u53ea\u6050\u53c8\u8981\u5377\u4eba\u6f29\u6da1\uff0c\u4e14\u5149\u770b\u770b\u8c22\u4e91 \u771f\u5982\u4f55\u5e94\u4ed8\uff0c\u518d\u4f5c\u6253\u7b97\uff0c\u4e8e\u662f\u5c06\u65b0\u5a5a\u7684\u59bb\u5b50\u4e00\u62c9\uff0c\u8eb2\u5728\u4e00\u68f5\u5927\u6811\u540e\u9762\u3002 \u3000\u3000\u82e5\u4f9d\u8c22\u4e91\u771f\u4e8c\u5341\u5e74\u524d\u7684\u813e\u6c14\uff0c\u90a3\u5bb9\u5f97\u8fd9\u73ed\u9053\u58eb\u559d\u9a82\uff0c\u53ea\u6015\u65e9\u5df2\u62d4\u5251\u52a8\u624b\uff0c\u5982\u4eca\u7ecf\u8fc7\u4e86\u4e8c \u5341\u591a\u5e74\u6765\u7684\u98d8\u8361\u6c5f\u6e56\uff0c\u706b\u6c14\u6536\u655b\u4e86\u4e0d\u5c11\uff0c\u53ea\u89c1\u5979\u62cd\u4e86\u62cd\u80cc\u4e0a\u7684\u5a74\u513f\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5192\u5927\u4fa0\u501f \u5ce8\u5d4b\u5c71\u5f00\u5c71\u7ed3\u7f18\uff0c\u5404\u5bb6\u5404\u6d3e\uff0c\u6765\u8005\u4e0d\u62d2\uff0c\u6211\u672c\u6765\u5c31\u662f\u5ce8\u5d4b\u6d3e\u7684\u4eba\uff0c\u600e\u4e48\u53cd\u800c\u6765\u4e0d\u5f97\u4e86\uff1f\u201d\u7ad9 \u5728\u4e1c\u89d2\u7684\u9053\u58eb\u51b7\u7b11\u9053\uff1a\u201c\u5192\u5927\u4fa0\u662f\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u957f\u8f88\uff0c\u4f60\u4f24\u4e86\u6211\u4eec\u7684\u5927\u5e08\u5144\u96f7\u9707\u5b50\uff0c\u5f04\u5f97\u4ed6 \u800c\u4eca\u4e0d\u77e5\u4e0b\u843d\uff0c\u4f60\u8fd8\u6709\u8138\u76ae\u542c\u5192\u5927\u4fa0\u7684\u8bb2\u5ea7\u5417\uff1f\u201d\u897f\u89d2\u7684\u9053\u58eb\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u96f7\u9707\u5b50\u4e5f\u906d\u4e86\u4f60 \u7684\u8fa3\u624b\uff0c\u4f60\u8fd8\u5c51\u4e8e\u5b66\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u8fd9\u70b9\u5fae\u672a\u529f\u592b\u5417\uff1f\u201d\u8427\u9752\u5cf0\u542c\u4e86\uff0c\u6697\u6697\u53f9\u606f\uff0c\u60f3\u6b66\u5f53\u4e00\u6d3e \uff0c\u5728\u660e\u4ee3\u4e2d\u53f6\u66fe\u76db\u6781\u4e00\u65f6\uff0c\u5176\u540e\u7531\u76db\u800c\u8870\uff0c\u540e\u6765\u5230\u6e05\u4ee3\u5eb7\u7199\u5e74\u95f4\uff0c\u6842\u4ef2\u660e\u5f97\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c\u6b66 \u5f53\u6d3e\u65b9\u59cb\u58f0\u5a01\u91cd\u632f\uff0c\u5982\u4eca\u6842\u4ef2\u660e\u7684\u513f\u5b50\u5192\u5ddd\u751f\u5192\u5ddd\u751f\u662f\u8ddf\u6bcd\u4eb2\u5192\u6d63\u83b2\u7684\u59d3\uff0c\u867d\u7136\u662f\u4e00\u4ee3\u6b66\u5b66 \u5927\u5e08\uff0c\u8db3\u4ee5\u7ee7\u627f\u4e43\u7236\uff0c\u4f46\u4e0d\u7406\u7410\u4e8b\uff0c\u6b66\u5f53\u7684\u638c\u95e8\uff0c\u6b66\u529f\u867d\u597d\uff0c\u4e3a\u4eba\u5eb8\u788c\uff0c\u95e8\u4e0b\u5e08\u5144\u5f1f\u8f88\u90fd\u4e0d \u6015\u4ed6\uff0c\u4ee5\u81f4\u53c8\u50cf\u767e\u4f59\u5e74\u524d\u4e00\u6837\uff0e\u867d\u662f\u540d\u95fb\u5929\u4e0b\u7684\u6b63\u5b97\u5927\u6d3e\uff0c\u4eec\u5374\u662f\u6709\u5b9e\u5b66\u8005\u5c11\uff0c\u9a84\u5984\u8005\u591a\u4e86\u3002 \u3000\u3000\u8c22\u4e91\u771f\u542c\u4ed6\u4eec\u63d0\u8d77\u96f7\u9707\u5b50\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u96f7\u9707\u5b50\u867d\u7136\u53d7\u4e86\u70b9\u4f24\uff0c\u5374\u662f\u5f97\u76ca\u4e0d\u5c11\u3002 \u201d\u90a3\u56db\u4e2a\u9053\u58eb\u8f70\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5996\u5987\u8fa3\u624b\u4f24\u4eba\uff0c\u8fd8\u8bf4\u98ce\u51c9\u8bdd\u513f\uff01\u201d\u8c22\u4e91\u771f\u672c\u60f3\u628a\u96f7\u9707\u5b50 \u5728\u51b0\u5cf0\u4e0a\u7684\u4e8b\u60c5\u8bf4\u51fa\uff0c\u89c1\u4ed6\u4eec\u5982\u6b64\uff0c\u6545\u610f\u4e0d\u8bf4\u3002\u5374\u4ef0\u5929\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u5440\uff01\u53ef\u60dc\uff01\u201d\u90a3\u56db\u4e2a\u9053 \u58eb\u540c\u58f0\u53eb\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53ea\u89c1\u8c22\u4e91\u771f\u62cd\u62cd\u80cc\u540e\u4e0a\u7684\u5a74\u5b69\uff0c\u9053\uff1a\u201c\u5c0f\u5b9d\u5b9d\uff0c\u4e0d\u8981\u614c\uff0c\u4e0d\u8981\u6015\uff0c\u8fd9\u51e0\u4e2a\u725b\u9f3b\u5b50\u91ce\u9053\u58eb \u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d\u90a3\u5b69\u5b50\u4e5f\u771f\u5947\u602a\uff0c\u521a\u624d\u7a7f\u8fc7\u82b1\u4e1b\uff0c\u88ab\u82b1\u679d\u62c2\u4e86\u4e00\u4e0b\uff0c\u54ed\u51fa\u58f0\u6765\uff0c\u5982\u4eca\u89c1\u90a3\u56db \u4e2a\u9053\u58eb\u4eae\u51fa\u5149\u8292\u95ea\u95ea\u7684\u957f\u5251\uff0c\u53cd\u800c\u89c9\u5f97\u597d\u73a9\uff0c\u4e24\u53ea\u5c0f\u624b\u4ece\u892a\u8913\u91cc\u4f38\u51fa\u6765\uff0c\u6293\u5440\u6293\u7684\uff0c\u8fd8\u53d1\u51fa \u563b\u563b\u7684\u7b11\u58f0\u5462\u3002\u8c22\u4e91\u771f\u7eed\u9053\uff1a\u201c\u53ef\u60dc\u5192\u8001\u524d\u8f88\u672c\u662f\u4e00\u4ee3\u5b97\u5e08\uff0c\u6b66\u6797\u4e2d\u4eba\u4eba\u94a6\u4ef0\uff0c\u63a8\u4e3a\u9886\u8896\uff0c \u800c\u4f60\u4eec\u5374\u53ea\u628a\u4ed6\u5f53\u505a\u6b66\u5f53\u6d3e\u7684\u957f\u8001\uff0c\u8fd9\u5c82\u4e0d\u53cd\u800c\u8d2c\u635f\u4e86\u4ed6\u7684\u5a01\u671b\uff1f\u5440\uff0c\u6211\u771f\u4e3a\u4ed6\u53ef\u60dc\uff0c\u6b66\u5f53 \u6d3e\u51fa\u4e86\u4f60\u4eec\u8fd9\u51e0\u4e2a\u4e0d\u6210\u5668\u7684\u8822\u7269\uff01\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u9053\u58eb\u4e43\u662f\u6b66\u5f53\u5c71\u672c\u5b97\u5f1f\u5b50\uff0c\u6280\u4e1a\u5f97\u81ea\u5192\u5ddd\u751f\u7684\u4e8c\u5f1f\u77f3\u5ddd\u751f\u4eb2\u6388\uff0c\u77f3\u5ddd\u751f\u5341\u51e0\u5e74\u524d \u5df2\u7ecf\u901d\u4e16\uff0c\u8fd9\u51e0\u4e2a\u9053\u58eb\u5728\u6b66\u5f53\u5c71\u672c\u5b97\u4e2d\uff0c\u7b97\u5f97\u662f\u8f88\u4efd\u9887\u9ad8\u7684\u6709\u5730\u4f4d\u7684\u9053\u58eb\u4e86\u3002\u8fd9\u65f6\u88ab\u8c22\u4e91\u771f \u4e00\u9a82\uff0c\u5747\u662f\u6012\u4ece\u5fc3\u8d77\uff0c\u897f\u89d2\u7684\u9053\u58eb\u4e00\u6296\u957f\u5251\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8c22\u4e91\u771f\u628a\u4f60\u7684\u5b69\u5b50\u653e\u4e0b\uff0c\u54b1\u4eec\u5f97 \u9886\u6559\u9886\u6559\u4f60\u7684\u593a\u547d\u5251\u6cd5\uff01\u201d\u8c22\u4e91\u771f\u82e5\u65e0\u5176\u4e8b\u5730\u53c8\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6b66\u5f53\u6d3e\u660e\u65e5\u4fbf\u6709\u8840\u5149\u4e4b\u707e \uff0c\u4f60\u4eec\u4e0d\u77e5\u6212\u60e7\uff0c\u53cd\u800c\u8981\u4e0e\u6211\u4e3a\u96be\uff0c\u8fd9\u5c82\u4e0d\u662f\u53ef\u7b11\u5475\u53ef\u7b11\uff01\u201d\u8427\u9752\u5cf0\u5728\u6811\u540e\u542c\u4e86\u6b64\u8a00\uff0c\u5403\u4e86 \u4e00\u60ca\uff0c\u600e\u4e48\u8c22\u4e91\u771f\u4e5f\u542c\u5230\u4e86\u98ce\u58f0\uff0c\u800c\u4e14\u8bf4\u5f97\u5982\u6b64\u786e\u5207\uff0c\u6562\u60c5\u662f\u5979\u53e6\u6709\u6240\u77e5\uff1f \u3000\u3000\u90a3\u51e0\u4e2a\u9053\u58eb\u7d20\u6765\u9a84\u5984\uff0c\u4ee5\u4e3a\u672c\u6d3e\u65e0\u4eba\u6562\u72af\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u975e\u4f46\u4e0d\u52a0\u611f\u6fc0\uff0c\u53cd\u800c\u66f4\u4e3a\u52a8\u6012\uff0c \u4e1c\u89d2\u7684\u9053\u58eb\u9661\u7684\u559d\u9053\uff1a\u201c\u6562\u60c5\u5c31\u662f\u4f60\u52fe\u7ed3\u5916\u6d3e\u597d\u90aa\uff0c\u524d\u6765\u6363\u9b3c\uff1f\u653e\u4e0b\u8fd9\u5c0f\u5b69\u5b50\uff0c\u9886\u9053\u7237\u4e00\u5251 \uff01\u201d\u90a3\u5b69\u5b50\u6b63\u5728\u563b\u563b\u7684\u7b11\uff0c\u7a81\u7136\u95fb\u8fd9\u559d\u58f0\uff0c\u5413\u4e86\u4e00\u4e0b\uff0c\u53c8\u54c7\u7684\u54ed\u4e86\u51fa\u6765\u3002\u8c22\u4e91\u771f\u9053\uff1a\u201c\u6211\u672c \u4e0d\u6b32\u4e0e\u4f60\u7b49\u4e00\u822c\u89c1\u8bc6\uff0c\u800c\u4eca\u4f60\u8fd9\u725b\u9f3b\u5b50\u91ce\u9053\u5413\u4e86\u6211\u7684\u5b69\u5b50\uff0c\u6211\u53ef\u9976\u4f60\u4e0d\u5f97\uff01\u201d\u90a3\u9053\u58eb\u6b63\u5f85\u8bf4 \u9053\uff0c\u201c\u90a3\u5c31\u5feb\u653e\u4e0b\u5b69\u5b50\u8fdb\u62db\uff01\u201d\u8bdd\u672a\u51fa\u53e3\uff0c\u5ffd\u89c1\u9752\u5149\u4e00\u95ea\u3001\u8c22\u4e91\u771f\u62d4\u5251\u5feb\u6781\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5251 \u950b\u5df2\u62b5\u5230\u4e86\u4ed6\u7684\u54bd\u5589\u3002\u90a3\u9053\u58eb\u614c\u5fd9\u62db\u67b6\uff0c\u8c22\u4e91\u771f\u7684\u5251\u6cd5\u662f\u4e0d\u51fa\u624b\u5219\u5df2\uff0c\u4e00\u51fa\u624b\u5c31\u72e0\u8fa3\u975e\u5e38\uff0c \u4f46\u542c\u5f97\u54e8\u7684\u4e00\u58f0\uff0c\u90a3\u9053\u58eb\u624b\u4e2d\u7684\u957f\u5251\u5df2\u65ad\u4e86\u4e00\u622a\uff0c\u5251\u5149\u4e00\u7ed5\uff0c\u9053\u58eb\u5934\u4e0a\u7684\u4e09\u4e49\u9afb\u5df2\u7ecf\u88ab\u524a\u53bb \u4e00\u80a1\uff0c\u614c\u5fd9\u4e00\u4e2a\u5012\u8dc3\uff0c\u907f\u5979\u8ffd\u51fb\uff0c\u72fc\u72c8\u975e\u5e38\u3002\u8c22\u4e91\u771f\u80cc\u4e0a\u7684\u5a74\u5b69\u5b50\u77a7\u7740\u597d\u73a9\uff0c\u53c8\u518d\u7834\u6d95\u4e3a\u7b11 \u3002\u4ed6\u521a\u4e00\u5c81\u591a\u4e9b\uff0c\u542b\u7cca\u53eb\u9053\uff1a\u201c\u563b\u563b\uff0c\u5988\u5988\uff01\u563b\u563b\uff0c\u5988\u5988\uff01\u201d\u7259\u97f3\u8fd8\u672a\u6e05\u695a\uff0c\u4f46\u5374\u542c\u5f97\u51fa\u662f \u8d5e\u8d4f\u4ed6\u5988\u5988\u7684\u610f\u601d\u3002\u8427\u9752\u5cf0\u542c\u5230\uff0c\u4e5f\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u82bd\u513f\u5230\u5e95\u662f\u94c1\u62d0\u4ed9\u548c\u8c22\u4e91 \u771f\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u9053\u58eb\u53c8\u60ca\u53c8\u6012\uff0c\u8fd9\u65f6\u518d\u4e5f\u4e0d\u7406\u4f1a\u8c22\u4e91\u771f\u62b1\u7740\u5b69\u5b50\u4e86\uff0c\u4e00\u9f50\u5927\u559d\uff0c\u5404\u6296\u957f\u5251\uff0c\u4fbf\u8981 \u5408\u56f4\u3002\u90a3\u7ad9\u5728\u4e1c\u89d2\u7684\u9053\u58eb\uff0c\u60ca\u9b42\u7a0d\u5b9a\uff0c\u6293\u8d77\u65ad\u5251\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5728\u8fd9\u5996\u5987\u8eab\u4e0a\u7559\u4e0b\u4e24\u5904\u8bb0\u53f7 \uff0c\u52a8\u624b\u65f6\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4e0d\u8981\u4f24\u4e86\u5b69\u5b50\uff01\u201d\u56db\u4e2a\u9053\u58eb\u5c55\u5f00\u4e86\u5408\u56f4\u7684\u56db\u8c61\u5251\u9635\uff0c\u7f13\u7f13\u800c\u8fdb\uff0c\u9996\u5c3e\u8054 \u9632\uff0c\u770b\u770b\u5c31\u8981\u53d1\u96be\uff01 \u3000\u3000\u8fd9\u56db\u8c61\u5251\u9635\u4e43\u6b66\u5f53\u6d3e\u9547\u5c71\u9635\u6cd5\u4e4b\u4e00\uff0c\u5c01\u95ed\u5f97\u5f02\u5e38\u4e25\u5bc6\uff0c\u9664\u975e\u5c06\u5176\u4e2d\u4e00\u4e8c\u4eba\u6740\u4f24\uff0c\u5426\u5219\u9635 \u52bf\u8d8a\u7f29\u8d8a\u7d27\uff0c\u88ab\u56f4\u8005\u7edd\u96be\u8d70\u51fa\u3002\u53ea\u89c1\u8c22\u4e91\u771f\u53e3\u89d2\u6302\u7740\u51b7\u7b11\uff0c\u957f\u5251\u4e00\u632f\uff0c\u55e1\u55e1\u4f5c\u58f0\uff0c\u770b\u6765\u4e5f\u4f3c \u5c31\u8981\u65bd\u5c55\u6740\u624b\u3002\u8427\u9752\u5cf0\u6697\u53eb\u201c\u4e0d\u5999\u201d\uff0c\u6b63\u60f3\u8d70\u51fa\uff0c\u5ffd\u89c1\u5c71\u5761\u4e0a\u4e00\u6761\u4eba\u5f71\u75be\u51b2\u800c\u4e0b\uff0c\u53e3\u4e2d\u53d1\u51fa \u563b\u563b\u7684\u602a\u7b11\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u5c31\u5230\u4e86\u4e0b\u9762\uff0c\u90a3\u56db\u4e2a\u9053\u58eb\u201c\u5475\u5440\u201d\u4e00\u58f0\uff0c\u5ffd\u5730\u6563\u5f00\uff0c\u540c\u58f0\u53eb\u9053\uff1a\u201d \u5927\u5e08\u5144\uff01\u201d \u3000\u3000\u8427\u9752\u5cf0\u4ece\u5927\u6811\u540e\u9762\u63a2\u5934\u7aa5\u89c6\uff0c\u89c1\u6765\u7684\u679c\u7136\u662f\u96f7\u9707\u5b50\uff0c\u4e0a\u8863\u4e00\u7247\u9c9c\u7ea2\uff0c\u50cf\u662f\u521a\u521a\u548c\u4eba\u53ae\u6740 \u8fc7\u540e\u4e00\u822c\u3002\u53ea\u89c1\u4ed6\u4e00\u8df3\u4e00\u8df3\u7684\u76f4\u4e0a\u76f4\u5382\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u7384\u6b66\u3001\u7384\u6db5\uff0c\u4f60\u4eec\u5e72\u4ec0\u4e48\uff1f\u563b\u563b\uff01\u8fd8 \u4e0d\u8d76\u5feb\u4f4f\u624b\uff01\u563b\u563b\uff0c\u201d\u201c\u8c22\u5927\u59d0\uff0c\u662f\u4f60\u5440\uff0c\u563b\u563b\uff01\u201d\u524d\u4e00\u53e5\u672c\u6765\u662f\u559d\u9a82\u90a3\u56db\u4e2a\u9053\u5341\u7684\uff0c\u4e00\u80a1 \u5a01\u4e25\u795e\u6c14\uff0c\u4f46\u5176\u4e2d\u6742\u7740\u83ab\u540d\u5176\u5999\u7684\u602a\u7b11\uff0c\u53cd\u800c\u663e\u5f97\u6781\u662f\u6ed1\u7a3d\uff0c\u66f4\u52a0\u4e0a\u4ed6\u5230\u4e86\u5e73\u5730\uff0c\u4ecd\u662f\u4e00\u8df3 \u4e00\u8df3\u7684\u7f29\u5934\u7f29\u818a\u597d\u8c61\u5fcd\u4e0d\u6ce8\u75d5\u75d2\u4e00\u822c\uff0c\u8d8a\u53d1\u663e\u5f97\u795e\u60c5\u8be1\u5f02\u3002 \u3000\u3000\u6b66\u5f53\u6d3e\u95e8\u89c4\u7d20\u4e25\uff0c\u96f7\u9707\u4e8e\u662f\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u5927\u5f1f\u5b50\uff0c\u9664\u4e86\u957f\u8001\u548c\u638c\u95e8\u4e4b\u5916\uff0c\u5c31\u8981\u6570\u4ed6\u6700\u5c0a\uff0c \u90a3\u56db\u4e2a\u9053\u58eb\u88ab\u4ed6\u4e00\u9a82\uff0c\u90fd\u4e0d\u6562\u7b11\uff0c\u8c22\u4e91\u771f\u5374\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u9707\u5b50\uff0c\u4f60\u662f\u600e\u4e48\u5566\uff1f\u201d\u96f7\u9707 \u5b50\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u548c\u4ed6\u4eec\u52a8\u624b\uff0c\u563b\u563b\uff01\u54b3\uff0c\u6709\u4ec0\u4e48\u4e0d\u662f\u4e5f\u5f97\u770b\u6211\u7684\u8584\u9762\u561b\uff0c\u563b\u563b\uff01\u201d\u53c8\u662f \u602a\u7b11\u53c8\u662f\u54b3\uff0c\u8c22\u4e91\u771f\u5148\u662f\u597d\u7b11\uff0c\u6e10\u89c9\u60c5\u5f62\u4e0d\u5bf9\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u6211\u8feb\u5f97\u4f60\u4e0d\u77e5\u4e0b\u843d\uff0c\u4e00\u5b9a\u8981 \u548c\u6211\u8fc7\u62db\uff0c\u54c8\uff0c\u597d\u5728\u4f60\u4e5f\u6765\u4e86\uff0c\u5426\u5219\u6211\u53f7\u79f0\u593a\u547d\u4ed9\u5b50\uff0c\u8fd9\u6761\u5c0f\u547d\u5374\u5148\u8981\u7ed9\u4f60\u4eec\u6b66\u5f53\u95e8\u4e0b\u593a\u53bb \u3002\u201d\u90a3\u56db\u4e2a\u9053\u58eb\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u5979\u4e8c\u5341\u5e74\u524d\u6b3a\u8d1f\u4f60\uff0c\u73b0\u5728\u53c8\u6b3a\u8d1f\u6211\u4eec\uff0c\u5927\u5e08\u5144\uff0c\u4eca\u56de\u4e07\u4e0d\u80fd\u53eb \u5979\u8dd1\u4e86\u3002\u201d\u201c\u5979\u8fd8\u8bf4\u660e\u65e5\u6211\u4eec\u6b66\u5f53\u6d3e\u4fbf\u6709\u8840\u5149\u4e4b\u707e\u5462\uff0c\u54fc\uff0c\u5927\u5e08\u5144\uff0c\u4f60\u8bf4\u600e\u80fd\u5bb9\u5979\u5982\u6b64\u80e1\u8bf4 \u4e71\u9053\uff1f\u201d\u96f7\u9707\u5b50\u5ffd\u5730\u4e00\u8dc3\u6570\u4e08\uff0c\u53eb\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u660e\u65e5\u4fbf\u6709\u8840\u5149\u4e4b\u707e\uff01\u563b\u563b\uff0c\u4f60\u4eec\u7b80\u76f4\u662f \u4e22\u4e86\u6b66\u5f53\u6d3e\u7684\u9762\u5b50\uff0c\u563b\u563b\uff01\u201d\u8dc3\u8d77\u843d\u4e0b\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7adf\u7136\u5728\u56db\u4e2a\u5e08\u5f1f\u9762\u4e0a\u6328\u6b21\u6253\u4e86\u4e00\u5df4\u638c\u3002 \u96f7\u9707\u5b50\u6027\u70c8\u5982\u706b\uff0c\u8fd9\u4e00\u5df4\u638c\u8fd8\u6253\u5f97\u786e\u5b9e\u4e0d\u8f7b\u3002\u56db\u4e2a\u9053\u58eb\u88ab\u4ed6\u6253\u5f97\u5929\u660f\u5730\u8f6c\uff0c\u5ffd\u542c\u5f97\u96f7\u9707\u4e8e\u602a \u7b11\u4e00\u58f0\uff0c\u4e00\u8de4\u8dcc\u5012\uff0c\u53e3\u4e2d\u53d1\u51fa\u5636\u5636\u4e4b\u58f0\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u624b\u8db3\u6410\u52a8\uff0c\u6478\u8d77\u6765\u4e00\u7247\u51b0\u51b7\u3002 \u3000\u3000\u56db\u4e2a\u9053\u58eb\u90fd\u5413\u5f97\u614c\u4e86\uff0c\u63a2\u4ed6\u9f3b\u606f\uff0c\u8fd8\u6709\u547c\u5438\uff0c\u629a\u4ed6\u8109\u818a\uff0c\u4ea6\u662f\u6b63\u5e38\uff0c\u53ea\u662f\u602a\u7b11\u4e0d\u5df2\uff0c\u58f0 \u5636\u529b\u7aed\uff0c\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u56db\u4e2a\u9053\u58eb\u5927\u4e3a\u8be7\u5f02\uff0c\u8c22\u4e91\u771f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u89e3\u5f00\u4ed6\u7684\u8863\u670d\u770b\u770b\uff0c\u4e5d\u6210 \u662f\u7ed9\u4eba\u5728\u7a74\u9053\u8981\u5bb3\u4e4b\u5904\u505a\u4e86\u624b\u811a\u5566\uff01\u201d\u8c22\u4e91\u771f\u80cc\u8fc7\u9762\u53bb\uff0c\u90a3\u56db\u4e2a\u9053\u58eb\u89e3\u5382\u4ed6\u4e0a\u8863\u4e00\u770b\uff0c\u4e0d\u770b \u72b9\u5df2\uff0c\u4e00\u770b\u4e4b\u4e0b\u90fd\u540c\u58f0\u602a\u53eb\u8d77\u6765\uff0c\u5982\u9047\u9b3c\u8d43\u3002\u8c22\u4e91\u771f\u5fcd\u4e0d\u7740\u597d\u5947\uff0c\u4e0d\u518d\u907f\u5fcc\uff0c\u56de\u8f6c\u5934\u6765\uff0c\u5728 \u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u96f7\u9707\u5b50\u7684\u80cc\u4e0a\u6709\u4e00\u4e2a\u9c9c\u7ea2\u624b\u5370\uff0c\u53e6\u5916\u4e09\u5904\u5730\u65b9\uff0c\u7600\u9ed1\u4e00\u7247\uff0c\u6210\u4e86\u4e00\u4e2a\u4e0d\u89c4\u5219 \u7684\u4e09\u89d2\u5f62\uff0c\u90a3\u4e09\u5904\u5730\u65b9\uff0c\u4e00\u5904\u662f\u9ebb\u7a74\uff0c\u4e00\u5904\u662f\u75d5\u75d2\u7a74\uff0c\u4e00\u5904\u662f\u7b11\u8170\u7a74\u3002 \u3000\u3000\u56db\u9053\u58eb\u9762\u9762\u76f8\u89d1\uff0c\u5446\u4e86\u4e00\u9635\uff0c\u5ffd\u5730\u540c\u58f0\u5c16\u53eb\uff0c\u5fc3\u4e2d\u5b9e\u662f\u60ca\u9a87\u5df2\u6781\u3002\u987b\u77e5\u96f7\u9707\u4e8e\u4e43\u662f\u6b66\u5f53 \u7b2c\u4e8c\u8f88\u5f1f\u5b50\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u540c\u95e8\u5e08\u5144\u5f1f\u5bf9\u4ed6\u65e0\u4e0d\u6151\u670d\uff0c\u5982\u4eca\u5374\u89c1\u4ed6\u53d7\u654c\u4eba\u6697\u7b97\uff0c\u800c\u4e14\u6240\u53d7\u7684 \u4f24\u5982\u6b64\u8be7\u5f02\uff0c\u60f3\u8d77\u8c22\u4e91\u771f\u548c\u96f7\u9707\u5b50\u521a\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u5747\u662f\u4e0d\u5bd2\u800c\u6817\uff0c\u53ea\u6015\u660e\u65e5\u771f\u6709\u8840\u5149\u4e4b\u707e\u3002\u201d \u3000\u3000\u8c22\u4e91\u771f\u6b66\u529f\u867d\u8f83\u4ed6\u4eec\u9ad8\u660e\u5f97\u591a\uff0c\u89c1\u4e86\u8fd9\u9c9c\u7ea2\u7684\u638c\u5370\uff0c\u548c\u90a3\u4e09\u5904\u7600\u9ed1\u7684\u7a74\u9053\uff0c\u4e5f\u81ea\u5fc3\u60ca\uff0c \u60f3\u6765\u60f3\u53bb\uff0c\u60f3\u4e0d\u51fa\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u7a76\u7adf\u4f55\u5bb6\u4f55\u6d3e\uff0c\u6709\u5982\u6b64\u90aa\u6076\u7684\u6bd2\u624b\uff1f\u90a3\u51e0\u4e2a\u9053\u58eb\u624b\u5fd9\u811a\u4e71\u5730\u8bd5 \u7ed9\u96f7\u9707\u5b50\u63a8\u8840\u8fc7\u5bab\uff0c\u89e3\u7a74\u6d3b\u8109\uff0c\u96f7\u9707\u5b50\u8d8a\u53d1\u5636\u5636\u602a\u53eb\uff0c\u6c57\u6c34\u4e00\u6ef4\u6ef4\u7684\u6d41\u4e0b\u6765\uff0c\u8c22\u4e91\u771f\u9053\uff1a\u201d \u4f60\u4eec\u522b\u4e71\u8bd5\u4e86\uff0c\u82e5\u662f\u4f60\u4eec\u80fd\u591f\u6551\u6cbb\uff0c\u4ed6\u8fd8\u4e0d\u4f1a\u81ea\u5df1\u89e3\u4e48\uff1f\u201d\u56db\u9053\u58eb\u81ea\u5df1\u65e0\u6cd5\u53ef\u65bd\uff0c\u88ab\u8c22\u4e91\u771f \u4e00\u8bf4\uff0c\u4ee5\u4e3a\u662f\u8c22\u4e91\u771f\u6545\u610f\u5632\u7b11\u4ed6\u4eec\uff0c\u53c8\u7f9e\u53c8\u6012\uff0c\u7adf\u7136\u4e0d\u7ea6\u800c\u540c\u5730\u8fc1\u6012\u4e8e\u8c22\u4e91\u771f\uff0c\u9a82\u9053\uff1a\u201c\u6211 \u4eec\u4e0d\u884c\uff0c\u4e14\u770b\u770b\u4f60\u7684\u9ad8\u660e\u624b\u6bb5\u3002\u201d \u3000\u3000\u8c22\u4e91\u771f\u5fc3\u4e2d\u6709\u6c14\uff0c\u5ffd\u542c\u5f97\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u5979\u53f7\u79f0\u593a\u547d\u4ed9\u5b50\uff0c\u5e76\u4e0d\u662f\u6551\u547d\u4ed9\u5b50\u5475\uff01\u201d\u56db\u9053\u58eb \u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\u6084\u65e0\u58f0\u5730\u7ad9\u5728\u4ed6\u4eec\u80cc\u540e\uff0c\u7adf\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\u3002\u8c22\u4e91\u771f\u4e00\u89c1 \uff0c\u8ba4\u5f97\u8fd9\u767d\u8863\u5c11\u5e74\u6b63\u662f\u5728\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u4e0e\u51b0\u5ddd\u5929\u5973\u4e24\u5ea6\u6bd4\u5251\u7684\u5510\u7ecf\u5929\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a \u201c\u6551\u547d\u7684\u795e\u4ed9\u6765\u5566\uff0c\u4f60\u4eec\u8fd9\u56db\u4e2a\u725b\u9f3b\u5b50\u91ce\u9053\u58eb\u8fd8\u4e0d\u8d76\u5feb\u6c42\u4ed6\uff01\u201d\u56db\u9053\u58eb\u89c1\u5510\u7ecf\u5929\u5982\u6b64\u5e74\u8f7b\uff0c \u54ea\u91cc\u80af\u4fe1\uff0c\u542c\u8c22\u4e91\u771f\u610f\u5b58\u8ba5\u7b11\uff0c\u6b63\u6b32\u53d1\u4f5c\uff0c\u5510\u7ecf\u5929\u5fae\u7b11\u9053\uff1a\u201c\u4e14\u5f85\u6211\u8bd5\u4e00\u8bd5\uff0c\u770b\u662f\u884c\u4e0d\u884c\uff1f \u8c22\u5973\u4fa0\uff0c\u4f60\u8fd8\u6709\u4e24\u4e2a\u8001\u670b\u53cb\u5728\u90a3\u8fb9\u7b49\u7740\u4f60\u5462\uff01\u201d\u8c22\u4e91\u771f\u65e9\u5c31\u5bdf\u89c9\u4e86\u8427\u9752\u5cf0\u592b\u5987\u8eb2\u5728\u6811\u540e\uff0c\u8fd9 \u65f6\u8ba8\u538c\u90a3\u56db\u4e2a\u9053\u58eb\uff0c\u6b63\u597d\u4e58\u673a\u8dd1\u5f00\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u96f7\u9707\u5b50\u80cc\u4e0a\u7684\u638c\u5370\u9c9c\u7ea2\u5982\u8840\uff0c\u8fd9\u65f6\u7ade\u6709\u70ed\u6c14\u5192\u8d77\uff0c\u51d1\u8fd1\u4e00\u95fb\uff0c\u9690 \u9690\u6709\u4e00\u80a1\u76ae\u8089\u70e7\u7126\u4e86\u7684\u5473\u9053\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u6b63\u662f\u8d64\u795e\u5b50\u7684\u72ec\u95e8\u90aa\u624b\uff0c\u770b\u6765\u4ed6\u638c\u529b\u53ea\u662f\u7528\u4e86\u4e00 \u4e8c\u5206\uff0c\u4e0d\u8fc7\u610f\u6b32\u7559\u4e00\u4e2a\u6807\u8bb0\u800c\u5df2\u3002\u518d\u770b\u88ab\u70b9\u7740\u7684\u90a3\u4e09\u5904\u9ebb\u7a74\u3001\u75d5\u75d2\u7a74\u548c\u7b11\u8170\u7a74\uff0c\u90fd\u662f\u7600\u9ed1\u575f \u80bf\uff0c\u70b9\u7a74\u7684\u624b\u6cd5\uff0c\u602a\u5f02\u7edd\u4f26\uff0c\u4e5f\u4e0d\u4f3c\u4e2d\u539f\u7684\u6b66\u5bb6\u6240\u4e3a\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6c89\u541f\u4e00\u9635\uff0c\u731b\u5730\u60f3\u8d77\u4e00\u4eba\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u5df2\u7ecf\u6765\u4e86\uff01\u201d\u6025\u5fd9\u53d6\u51fa\u7528\u5929\u5c71\u96ea\u83b2\u5236 \u7ec3\u7684\u78a7\u7075\u4e39\uff0c\u56bc\u788e\u4e86\u5728\u638c\u5370\u5468\u56f4\u6577\u4e0a\uff0c\u96f7\u9707\u5b50\u5728\u8ff7\u7cca\u4e2d\u4f46\u89c9\u4e00\u9635\u6c81\u51c9\uff0c\u76f4\u900f\u5fc3\u813e\uff0c\u7ffb\u4e86\u4e2a\u8eab \uff0c\u5750\u8d77\u6765\u4e00\u773c\u77a7\u89c1\u4e86\u5510\u7ecf\u5929\uff0c\u8ba4\u5f97\u4ed6\u662f\u5f53\u65e5\u7528\u795e\u8292\u4e00\u8fde\u6253\u4f24\u4e86\u5341\u4e09\u540d\u5025\u4f97\u9ad8\u624b\u7684\u767d\u8863\u5c11\u5e74\uff0c \u867d\u4e0d\u77e5\u5176\u540d\uff0c\u4f46\u5374\u77e5\u4ed6\u662f\u5929\u5c71\u5f1f\u5b50\uff0c\u6025\u9053\uff1b\u201c\u7384\u6b66\u3001\u7384\u6db5\uff0c\u4f60\u4eec\u8fd8\u4e0d\u53e9\u5934\uff0c\u563b\u563b\uff01\u201d\u5510\u7ecf\u5929 \u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u793c\u3002\u201d\u53c8\u5c06\u4e00\u7c92\u78a7\u7075\u4e39\u7ed9\u4ed6\u670d\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u9047\u89c1\u4ec0\u4e48\u4eba\u4e86\uff1f\u201d\u96f7\u9707\u5b50\u9053\uff1a\u201d \u5148\u662f\u4e00\u4e2a\u5927\u9ebb\u75af\uff0c\u563b\u563b\uff0c\u540e\u6765\u662f\u4e00\u4e2a\u53d1\u5982\u67af\u8349\u7684\u8001\u602a\u7269\u3002\u563b\u563b\uff01\u201d\u5510\u7ecf\u5929\u7684\u6599\u60f3\u679c\u7136\u4e0d\u9519\uff0c \u771f\u662f\u8d64\u795e\u5b50\uff0c\u53ea\u4e0d\u77e5\u8fd9\u4e24\u4eba\u53c8\u600e\u4f1a\u540c\u5728\u4e00\u8d77\uff1f \u3000\u3000\u96f7\u9707\u5b50\u65ad\u65ad\u7eed\u7eed\u8bf4\u9053\uff1a\u201c\u90a3\u5927\u9ebb\u75af\u6253\u4e86\u6211\uff0c\u563b\u563b\uff01\u540e\u6765\u53c8\u6551\u4e86\u6211\uff0c\u563b\u563b\uff01\u201d\u4ed6\u88ab\u8d64\u795e\u5b50 \u6240\u5370\u7684\u90a3\u8bb0\u8840\u624b\u5370\uff0c\u7ecf\u7528\u5929\u5c71\u96ea\u83b2\u6577\u6cbb\u4e4b\u540e\uff0c\u75db\u695a\u5927\u51cf\uff0c\u5df2\u4e0d\u788d\u4e8b\uff0c\u53ea\u662f\u90a3\u4e09\u5904\u7a74\u9053\u5c1a\u672a\u89e3 \u5f00\uff0c\u6240\u4ee5\u4ecd\u7136\u53d1\u51fa\u563b\u563b\u7684\u602a\u7b11\u3002\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff0c\u65e0\u6687\u591a\u95ee\u3002\u4ed6\u4e0e\u91d1\u4e16\u9057\u66fe\u4ea4\u8fc7\u597d\u51e0\u6b21\u624b\uff0c \u77e5\u9053\u4ed6\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u7acb\u5373\u5728\u76f8\u5e94\u7684\u7a74\u9053\u4e0a\u63c9\u6413\uff0c\u66ff\u96f7\u9707\u5b50\u63a8\u8840\u8fc7\u5bab\uff0c\u53d1\u73b0\u91d1\u4e16\u9057\u7684\u70b9\u7a74\u624b\u6cd5 \u867d\u91cd\uff0c\u770b\u6765\u7adf\u662f\u7528\u62d0\u6756\u7684\u5c16\u7aef\u70b9\u7684\uff0c\u4f46\u5374\u5e76\u4e0d\u4f24\u53ca\u7b4b\u8109\uff0c\u770b\u6765\u53ea\u662f\u6709\u610f\u6c57\u4e00\u4e2a\u5927\u73a9\u7b11\uff0c\u4ee4\u96f7 \u9707\u5b50\u602a\u7b11\u72c2\u8df3\uff0c\u4e0d\u5f97\u89e3\u6551\uff0c\u8981\u8fc7\u4e8c\u5341\u56db\u4e2a\u65f6\u8fb0\u65b9\u80fd\u81ea\u4f11\uff01\u5510\u7ecf\u5929\u66ff\u96f7\u9707\u5b50\u89e3\u7a74\uff0c\u53c8\u597d\u6c14\u53c8\u597d \u7b11\uff0c\u4e16\u4e0a\u9664\u4e86\u91d1\u4e16\u9057\u8fd9\u4e2a\u602a\u4eba\uff0c\u518d\u65e0\u7b2c\u4e8c\u4e2a\u4f1a\u505a\u51fa\u8fd9\u6837\u602a\u8bde\u987d\u76ae\u4e4b\u4e8b\u3002 \u3000\u3000\u7a74\u9053\u4e00\u89e3\uff0c\u9ebb\u75d2\u81ea\u6b62\uff0c\u96f7\u9707\u5b50\u6162\u6162\u5750\u8d77\u5401\u4e86\u53e3\u6c14\uff0c\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u5927\u9ebb\u75af\u600e\u6837\u5148\u6253\u4e86\u4f60 \u540e\u6765\u53c8\u6551\u4e86\u4f60\uff1f\u201d\u96f7\u9707\u5b50\u9053\uff1a\u201c\u6211\u8d76\u56de\u6765\u53c2\u52a0\u76db\u4f1a\uff0c\u5728\u5c71\u53e3\u9047\u89c1\u4e00\u4e2a\u5927\u9ebb\u75af\uff0c\u6211\u5fc3\u60f3\u6cd5\u4f1a\u4f55 \u7b49\u5e84\u4e25\uff0c\u600e\u5bb9\u5f97\u4e00\u4e2a\u5927\u9ebb\u75af\u4e5f\u6765\u6270\u4e71\u4f1a\u573a\uff0c\u4e8e\u662f\u6211\u4fbf\u8981\u9a71\u9010\u4ed6\u8d70\uff0c\u4ed6\u95ee\u6211\u662f\u4f55\u4eba\uff0c\u6211\u8bf4\u6211\u662f \u6b66\u5f53\u5c71\u7b2c\u4e8c\u8f88\u7684\u5927\u5f1f\u5b50\u96f7\u9707\u5b50\uff0c\u5e78\u4e8f\u662f\u6211\u9047\u89c1\u4e86\u4f60\uff0c\u8981\u662f\u6211\u7684\u5e08\u5f1f\u9047\u89c1\u4f60\uff0c\u51c6\u4f1a\u5c06\u4f60\u6253\u6b7b\uff0c \u6211\u8fd8\u5e03\u65bd\u4e86\u4ed6\u51e0\u4e24\u94f6\u5b50\uff0c\u53eb\u4ed6\u5feb\u5feb\u8d70\u5f00\u3002\u4e0d\u6599\u4ed6\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u539f\u6765\u662f\u96f7\u9707\u5b50\u4e48\u3001\u542c \u8bf4\u5728\u6b66\u5f53\u7b2c\u4e8c\u8f88\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u8981\u6570\u4f60\u7684\u6b66\u529f\u6700\u9ad8\u3002\u6211\u6b63\u5fc3\u60f3\uff1a\u539f\u6765\u8fd9\u4e2a\u5927\u9ebb\u75af\u4e5f\u77e5\u9053\u6211\u7684\u540d\u6c14 \u3002\u54ea\u77e5\u4ed6\u7b11\u58f0\u672a\u6b47\uff0c\u5ffd\u7136\u62ff\u8d77\u62d0\u6756\u5c31\u5728\u6211\u8eab\u535c\u6233\u4e86\u51e0\u4e0b\uff0c\u6beb\u65e0\u529e\u6cd5\u62db\u67b6\uff0c\u90a3\u9ebb\u75af\u7684\u672c\u4e8b\uff0c\u53ef \u60f3\u800c\u77e5\uff0c\u201d\u5510\u7ecf\u592d\u5374\u662f\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u91d1\u4e16\u9057\u4e13\u4e0e\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\u5f00\u73a9\u7b11\uff0c\u82e5\u4f60\u4e0d\u81ea \u62a5\u540d\u53f7\uff0c\u4e5f\u8fd8\u7f62\u4e86\u3002\u4f60\u8fd9\u9a84\u5984\u4e4b\u5fc3\u4e00\u8d77\uff0c\u81ea\u70ab\u540d\u5934\uff0c\u5c31\u662f\u4e0d\u8d76\u4ed6\u8d70\uff0c\u4e5f\u96be\u514d\u53d7\u4ed6\u6349\u5f04\uff01\u201d \u3000\u3000\u96f7\u9707\u5b50\u53c8\u9053\uff1a\u201c\u6211\u88ab\u4ed6\u6349\u5f04\uff0c\u81ea\u662f\u6012\u4e0d\u53ef\u904f\uff0c\u90a3\u77e5\u8d70\u4e86\u51e0\u6b65\uff0c\u53c8\u9047\u5230\u4e00\u4e2a\u53d1\u5982\u4e71\u8349\u7684\u602a \u4eba\uff0c\u6211\u8fd8\u672a\u8bf4\u8bdd\uff0c\u4ed6\u5df2\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u95ee\u9053\uff1a\u2018\u96f7\u9707\u5b50\u5475\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u60c5\u8fd9\u6837\u597d\u7b11\uff1f\u2019\u6211\u9053 \uff1a\u2018\u5e72\u4f60\u4ec0\u4e48\u4e8b\uff1f\u2018\u90a3\u602a\u4eba\u5ffd\u9053\uff1a\u2018\u597d\uff0c\u6211\u518d\u53eb\u4f60\u54ed\u7b11\u4e0d\u5f97\uff0c\u6211\u8981\u5728\u4f60\u8eab\u4e0a\u523b\u4e00\u6807\u5fd7\uff0c\u8ba9\u4f60 \u66ff\u6211\u62a5\u7ed9\u5192\u5ddd\u751f\u77e5\u9053\u3002\u2019\u6211\u6025\u5fd9\u62d4\u5251\uff0c\u5ffd\u5730\u611f\u5230\u4e00\u80a1\u70ed\u6c14\u6251\u9762\u800c\u6765\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5ffd\u53c8\u542c \u5f97\u55e4\u55e4\u602a\u54cd\uff0c\u90a3\u9ebb\u75af\u5728\u5ca9\u3002k\u73b0\u8eab\uff0c\u9a82\u9053\uff1a\u2018\u8001\u602a\uff0c\u4f60\u61c2\u4e0d\u61c2\u6c5f\u6e56\u7684\u89c4\u77e9\uff1f\u6211\u505a\u4e86\u7684\u4e70\u5356\uff0c \u4f60\u600e\u4e48\u53c8\u6765\u63d2\u624b\u3002\u2019\u90a3\u602a\u4eba\u638c\u52bf\u5982\u98ce\uff0c\u88ab\u4ed6\u4e00\u9a82\uff0c\u5ffd\u5730\u8df3\u5f00\uff0c\u4f46\u624b\u638c\u5df1\u5728\u6211\u80cc\u4e0a\u8f7b\u8f7b\u6cbe\u4e86\u4e00 \u4e0b\u3002\u5510\u7ecf\u5929\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u5e76\u975e\u8d64\u795e\u5b50\u624b\u4e0b\u7559\u60c5\uff0c\u800c\u662f\u4ed6\u5fcc\u60ee\u91d1\u4e16\u9057\u7684\u72ec\u95e8\u6076\u6697\u5668\uff0c\u6240\u4ee5\u6765 \u4e0d\u53ca\u91cd\u4f24\u96f7\u9707\u5b50\uff0c\u5982\u4eca\u8d64\u795e\u5b50\u60f3\u662f\u53bb\u627e\u90a3\u91d1\u4e16\u9057\u7b97\u8d26\u53bb\u4e86\u3002 \u3000\u3000\u96f7\u9707\u5b50\u4e2d\u4e86\u6bd2\u638c\u4e4b\u4f24\uff0c\u521a\u5f97\u5929\u5c71\u96ea\u83b2\u4e4b\u529b\uff0c\u66ff\u4ed6\u6d88\u4e86\u70ed\u6bd2\uff0c\u4f46\u56e0\u5185\u4f24\u5c1a\u672a\u75ca\u6108\uff0c\u8bf4\u4e86\u4e00 \u5927\u5806\u8bdd\uff0c\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\u3002\u5176\u65f6\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff0c\u5df2\u6709\u6570\u4eba\u95fb\u8baf\u8d76\u6765\u3002\u5510\u7ecf\u5929\u5fc3\u5ff5\u51b0\u5ddd\u5929\u5973\uff0c\u9053 \uff1a\u201c\u96f7\u5144\uff0c\u4f60\u56de\u5bfa\u4e2d\u9759\u517b\uff0c\u7528\u666e\u901a\u7684\u63d0\u795e\u8865\u6c14\u4e4b\u836f\uff0c\u4e0d\u8fc7\u4e09\u65e5\uff0c\u4ea6\u53ef\u4ee5\u81ea\u7597\u4e86\u3002\u201d\u96f7\u9707\u4e8e\u4e24 \u6b21\u548c\u5510\u7ecf\u5929\u76f8\u9047\uff0c\u5c1a\u672a\u8bf7\u6559\u59d3\u540d\uff0c\u8fd9\u65f6\u65b9\u6b32\u8bf7\u95ee\uff0c\u5510\u7ecf\u5929\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u98de\u8fc7\u82b1\u4e1b\uff0c\u7aef\u7684\u662f\u6765 \u53bb\u65e0\u58f0\uff0c\u500f\u5ffd\u4e0d\u89c1\u3002\u90a3\u56db\u4e2a\u9053\u5341\u540c\u77aa\u53e3\u5446\uff0c\u8fd9\u624d\u77e5\u9053\u771f\u7684\u662f\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002 \u3000\u3000\u5510\u7ecf\u5929\u672c\u5c31\u6599\u60f3\u5230\u91d1\u4e16\u9057\u5fc5\u7136\u4f1a\u5230\u6b64\u95f4\uff0c\u4f46\u6b64\u65f6\u77e5\u9053\u4ed6\u786e\u5b9e\u5230\u4e86\uff0c\u5fc3\u4e2d\u4ecd\u662f\u5fd0\u5fd1\u4e0d\u5b89\uff0c \u60f3\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u662f\u966a\u7740\u51b0\u5ddd\u5929\u5973\u6765\u4e86\uff0c\u51b0\u5ddd\u4eba\u5973\u6700\u4e3a\u597d\u6d01\uff0c\u4ed6\u7684\u672c\u6765\u9762H\u4ea6\u662f\u4e2a\u82f1\u4fca\u7684\u5c11\u5e74 \uff0c\u4f55\u4ee5\u5982\u4eca\u53c8\u5047\u88c5\u4e86\u9ebb\u75af\u51fa\u73b0\uff1f\u96be\u9053\u4e0d\u6015\u51b0\u5ddd\u5929\u5973\u618e\u6076\u4e48\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u91d1\u4e16\u9057\u4e00\u8def\u548c\u5979\u95ee \u884c\uff0e\u5b9a\u5f53\u77e5\u9053\u5979\u662f\u5192\u5927\u4fa0\u7684\u4f84\u5973\u513f\uff0c\u6e90\u51fa\u6b66\u4e0e\u4e00\u6d3e\uff0c\u4ed6\u600e\u4e48\u5374\u4f5c\u5f04\u4e86\u6b66\u5f53\u7684\u4eba\uff1f\u5c31\u662f\u602a\u50fb\u4e5f \u4e0d\u5e94\u5982\u6b64\u4e0d\u8fd1\u60c5\u7406\u3002\u96be\u9053\u4ed6\u4e2a\u6015\u51b0\u5ddd\u5927\u5973\u89c1\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u95f7\u95f7\u524d\u884c\uff0c\u53c8\u60f3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u6765\u4e86\uff0c\u600e\u4e48\u4e0d\u8d76\u5feb\u5230\u5bfa\u4e2d\u53bb\u89c1\u5979\u7684\u4f2f\u4f2f\u3002\u96be\u9053\u5979 \u4e5f\u5b66\u4e86\u91d1\u4e16\u9057\u602a\u50fb\u7684\u884c\u5f84\uff0c\u5728\u8fd9\u9644\u8fd1\u5c71\u5934\u6e38\u8361\u5417\uff1f\u201d\u5510\u7ecf\u5929\u672c\u6765\u662f\u4e2a\u806a\u660e\u7684\u5c11\u5e74\uff0c\u8fd9\u65f6\u5374\u4e0d \u7531\u81ea\u5df1\u7684\u795e\u601d\u660f\u4e71\uff0c\u5fc3\u4e2d\u5ffd\u8d77\u5947\u60f3\uff0c\u60f3\u62fc\u7740\u4e00\u665a\u4e0d\u7761\uff0c\u5728\u9644\u8fd1\u5c71\u5934\uff0c\u627e\u5bfb\u91d1\u4e16\u9057\u548c\u51b0\u5ddd\u5929\u5973 \u7684\u8e2a\u8ff9\u3002\u6b63\u5728\u80e1\u601d\u4e71\u60f3\u4e4b\u9645\uff0c\u5ffd\u89c1\u5c71\u5761\u4e0a\u677e\u836b\u4e0b\uff0c\u4e24\u5973\u4e00\u7537\uff0c\u5e76\u80a9\u540c\u884c\uff0c\u53f3\u624b\u8fb9\u90a3\u4e2a\u5973\u7684\uff0c \u80cc\u7740\u5a74\u5b69\u5b50\uff0c\u81ea\u7136\u662f\u8c22\u4e91\u771f\u4e86\u3002\u53e6\u5916\u4e24\u4eba\u5219\u662f\u8427\u9752\u5cf0\u592b\u5987\u3002\u5510\u7ecf\u5929\u63a0\u8fc7\u4ed6\u4eec\u8eab\u8fb9\uff0c\u6b63\u542c\u5f97\u8c22 \u4e91\u771f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u90a3\u4e2a\u5927\u9ebb\u75af\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u672c\u6765\u4e0d\u60f3\u60ca\u52a8\u4ed6\u4eec\uff0c\u95fb\u5f97\u6b64\u8bed\uff0c\u5fc3\u4e2d\u4e00\u8df3\uff0c\u8eab\u5f62\u4e00\u843d\uff0c\u811a\u6b65\u8e0f\u5728\u5730\u4e0a\uff0c\u53d1\u51fa\u58f0\u54cd \u3002\u8c22\u4e91\u771f\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u96f7\u9707\u5b50\u7684\u4f24\u4e0d\u788d\u4e8b\u5427\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5e78\u597d\u8d64\u795e\u5b50\u7684\u638c \u529b\u672a\u66fe\u7528\u8db3\uff0c\u6709\u4e86\u5929\u5c71\u96ea\u83b2\u5408\u6210\u7684\u78a7\u7075\u4e39\uff0c\u6599\u5f53\u65e0\u4e8b\u3002\u8bf4\u6765\u8fd8\u5f97\u591a\u8c22\u90a3\u4e2a\u5927\u9ebb\u75af\u3002\u201d\u8c22\u4e91\u771f \u9053\uff1a\u201c\u600e\u4e48\uff0c\u53c8\u662f\u90a3\u4e2a\u5927\u9ebb\u75af\uff1f\u201d\u5510\u7ecf\u5929\u5c06\u91d1\u4e16\u9057\u6349\u5f04\u4e86\u96f7\u9707\u5b50\u7136\u540e\u53c8\u6551\u4ed6\u7684\u4e8b\u60c5\u7ea6\u7565\u8bf4\u4e86 \u4e00\u904d\uff0c\u7b11\u9053\uff1a\u201c\u8d64\u795e\u5b50\u72e0\u6bd2\u4e4b\u6781\uff0c\u90a3\u5927\u9ebb\u75af\u7684\u602a\u50fb\u884c\u5f84\u4e5f\u4ee4\u4eba\u60ca\u6015\uff0c\u5e78\u800c\u6211\u77e5\u9053\u4ed6\u7684\u70b9\u7a74\u624b \u6cd5\uff0c\u8981\u4e0d\u7136\u5c31\u662f\u5c06\u8d64\u795e\u5b50\u90a3\u638c\u529b\u6240\u5e26\u7684\u70ed\u6bd2\u89e3\u4e86\uff0c\u96f7\u9707\u5b50\u4ecd\u7136\u5f97\u72c2\u7b11\u72c2\u8df3\u5341\u4e8c\u4e2a\u65f6\u8fb0\u3002\u96f7\u9707 \u5b50\u662f\u4ed6\u4eec\u6b66\u5f53\u6d3e\u7684\u7b2c\u4e8c\u4ee3\u5927\u5f1f\u5b50\uff0c\u90a3\u53ef\u6709\u591a\u96be\u4e3a\u60c5\uff01\u201d \u3000\u3000\u8c22\u4e91\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5e78\u559c\u6211\u5f97\u9ad8\u4eba\u6240\u6551\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u7740\u4e86\u8fd9\u4e2a\u5927\u9ebb\u75af\u7684\u9053\u513f\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u4e5f\u78b0\u7740\u4ed6\u4e86\uff1f\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u6b63\u60f3\u7528\u77f3\u5934\u6253\u6211\u7684\u7a74\u9053\uff0c\u5e78\u5f97\u4e00 \u4f4d\u4e0d\u9732\u9762\u7684\u5c11\u5973\u5c06\u4ed6\u5413\u8d70\u3002\u201d\u5510\u7ecf\u5929\u5927\u5947\uff0c\u6025\u9053\uff1a\u201c\u4ec0\u4e48\u5c11\u5973\u6709\u8fd9\u6837\u7684\u672c\u4e8b\uff0c\u662f\u51b0\u5ddd\u5929\u5973\u5417 \uff1f\u4f60\u53c8\u662f\u600e\u4e48\u9047\u5230\u4e86\u90a3\u5927\u9ebb\u75af\u7684\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u60c6\u6005\u4f0a\u4eba\u4f55\u5904\u89c5\uff0c\u60ca\u9e3f\u4e00\u77a5\u6c93\u65e0\u8e2a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u4f1a\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u7a7a\u9645\u9999\u82b1\u3000\u7389\u4eba\u620f\u82f1\u4fa0\u3000\u84ec\u83b1\u5f02\u5c9b\u3000\u7ae5\u5b50\u62dc\u5947\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de \u7a7a\u9645\u9999\u82b1\u3000\u7389\u4eba\u620f\u82f1\u4fa0 \u84ec\u83b1\u5f02\u5c9b\u3000\u7ae5\u5b50\u62dc\u5947\u4eba \u3000\u3000\u8c22\u4e91\u771f\u62cd\u62cd\u80cc\u4e0a\u7684\u5b69\u5b50\uff0c\u5b69\u5b50\u5df2\u7ecf\u719f\u7761\uff0c\u8138\u4e0a\u9732\u51fa\u751c\u7f8e\u7684\u7b11\u5bb9\uff0c\u5c31\u50cf\u5c71\u8c37\u4e2d\u76db\u5f00\u7684\u82b1\u6735 \u3002\u8c22\u4e91\u771f\u9053\uff1a\u201c\u542c\u58f0\u97f3\u4e0d\u50cf\u662f\u51b0\u5ddd\u5929\u5973\u3002\u4f60\u95ee\u6211\u600e\u4e48\u9047\u89c1\u4e86\u5927\u9ebb\u75af\u3001\u8fd9\u4e8b\u5f97\u4ece\u5934\u8bf4\u8d77\u3002\u201d\u5510 \u7ecf\u5929\u6b63\u5728\u503e\u542c\uff0c\u8c22\u4e91\u771f\u62cd\u62cd\u5b69\u5b50\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u4ed6\u957f\u5f97\u4e00\u70b9\u4e5f\u4e0d\u50cf\u4ed6\u7684\u7236\u4eb2\u3002\u201d\u8427\u9752\u5cf0 \u9053\uff1a\u201c\u4ed6\u5f88\u50cf\u4f60\uff0c\u5c06\u6765\u5fc5\u5b9a\u662f\u4e2a\u82f1\u4fca\u7684\u5c11\u5e74\u4fa0\u5ba2\u3002\u201d\u8fd9\u8bdd\u5b9e\u662f\u79f0\u8d5e\u8c22\u4e91\u771f\u7684\u7f8e\u8c8c\uff0c\u8c22\u4e91\u771f\u5fae \u5fae\u4e00\u7b11\uff0c\u95ee\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u4ece\u897f\u85cf\u6765\uff0c\u53ef\u77e5\u9053\u8fd9\u5b69\u5b50\u7684\u7236\u4eb2\u73b0\u5728\u8fd8\u5728\u51b0\u5cf0\u4e0a\u9762\u5417\uff1f\u90a3\u65e5\u5c71\u5d29 \u5730\u88c2\uff0c\u6211\u521a\u4ece\u5916\u9762\u91c7\u836f\u56de\u6765\u3002\u5730\u9707\u4e4b\u540e\uff0c\u4e0a\u5c71\u7684\u901a\u8def\u5df2\u7ed9\u7194\u5ca9\u5835\u585e\uff0c\u6211\u5728\u5c71\u8170\uff0c\u89c1\u51b0\u5bab\u8fd8\u5728 \uff0c\u4e0d\u77e5\u90a3\u573a\u5927\u5730\u9707\u6709\u5426\u6d89\u53ca\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e00\u9635\u4f24\u5fc3\uff0c\u8427\u9752\u5cf0\u4e0d\u77e5\u9053\uff0c\u4ed6\u5374\u662f\u77e5\u9053\u94c1\u62d0\u4ed9\u5df2\u7136\u8eab\u6b7b\uff0c\u8c22\u4e91\u771f\u6c38\u8fdc\u4e0d\u80fd\u518d\u89c1\u4ed6 \u4e86\u3002\u4f46\u89c1\u5979\u5982\u6b64\u671f\u5f85\u7684\u795e\u60c5\uff0c\u600e\u5fcd\u5fc3\u660e\u767d\u544a\u8bc9\uff0c\u53ea\u5f97\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u540e\u6765\u6211\u4e5f\u6ca1\u6709\u518d\u4e0a\u51b0\u5bab\uff0c \u5c0a\u592b\u60c5\u5f62\u4e0d\u5927\u6e05\u695a\u3002\u8bf7\u4f60\u5728\u6b64\u6b21\u76db\u4f1a\u4e4b\u540e\uff0c\u5373\u5230\u8428\u8ff9\u53bb\u5bfb\u4f60\u4eec\u7684\u5f92\u5f1f\u9648\u5929\u5b87\uff0c\u4ed6\u4e00\u5b9a\u6e05\u695a\u7684 \u3002\u201d\u8c22\u4e91\u771f\u542c\u4ed6\u6b64\u8a00\uff0c\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u4f46\u4ea6\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u6211\u672c\u6765\u65e9\u5c31\u60f3\u5230\u91d1\u5149\u5bfa \u62dc\u89c1\u5192\u5927\u4fa0\uff0c\u544a\u8bc9\u4ed6\uff0c\u4ed6\u6709\u4e00\u4f4d\u4f84\u5973\uff0c\u73b0\u5728\u5728\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u7684\u51b0\u5cf0\u4e4b\u4e0a\u3002\u5df2\u5b66\u6210\u4e86\u7edd\u4e16\u6b66\u529f \u3002\u4e3a\u4e86\u8fd9\u5b69\u5b50\uff0c\u76f4\u5230\u5982\u4eca\uff0c\u65b9\u80fd\u524d\u6765\u3002\u52a8\u8eab\u4e4b\u524d\uff0c\u6211\u4e5f\u66fe\u542c\u5230\u4e00\u70b9\u98ce\u58f0\uff0c\u8bf4\u662f\u6709\u8bb8\u591a\u5f02\u6d3e\u9b54 \u5934\uff0c\u8981\u8d81\u4eca\u5e74\u7684\u76db\u4f1a\u4e0e\u5192\u5927\u4fa0\u4e3a\u96be\uff0c\u6211\u8fd8\u4e0d\u5927\u76f8\u4fe1\uff0c\u54ea\u77e5\u679c\u7136\u7ed9\u6211\u78b0\u4e0a\u4e86\uff01\u201d\u770b\u6765\u660e\u65e5\u5fc5\u5b9a \u6709\u4e00\u573a\u5927\u95f9\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u9664\u4e86\u90a3\u5927\u9ebb\u75af\u4e4b\u5916\uff0c\u4f60\u8fd8\u78b0\u89c1\u4e86\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u8c22\u4e91\u771f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5c31\u662f\u5728\u4eca\u65e5\u7684\u9ec4\u660f\u65f6\u5206\uff0c\u6211\u521a\u521a\u8fdb\u5165\u5c71\u53e3\uff0c\u5b69\u5b50\u997f\u4e86\uff0c\u6211\u8eb2\u5728\u4e00\u5757 \u5ca9\u77f3\u4e4b\u540e\uff0c\u7ed9\u4ed6\u5582\u5976\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u58f0\u8d70\u5165\u5c71\u8c37\uff0c\u6211\u4e00\u770b\uff0c\u539f\u6765\u662f\u51e0\u4e2a\u6b66\u5f53\u5c71\u7684\u9053\u58eb\u548c\u5d14\u4e91\u5b50 \uff0c\u4ed6\u4eec\u4f3c\u4e4e\u4e00\u8def\u5728\u4e89\u8bba\u4ec0\u4e48\uff0c\u53ea\u542c\u5f97\u5d14\u4e91\u5b50\u53eb\u9053\uff1a\u2018\u96f7\u5927\u54e5\u6ca1\u6709\u6b7b\uff0c\u4ed6\u7684\u6211\u4eca\u665a\u5230\u91d1\u5c71\u5bfa\u76f8 \u4f1a\uff0c\u4f60\u4eec\u4e0d\u4fe1\uff0c\u7b49\u4e0b\u4f60\u4eec\u81ea\u5df1\u5c31\u53ef\u4eb2\u773c\u89c1\u4ed6\u3002\u2019\u770b\u6765\u4ed6\u4e0e\u96f7\u9707\u5b50\u662f\u5206\u9053\u800c\u6765\uff0c\u6240\u4ee5\u6211\u9002\u624d\u89c1 \u7740\u96f7\u9707\u5b50\u4e5f\u5e76\u4e0d\u89c9\u610f\u5916\u3002\u90a3\u51e0\u4e2a\u9053\u58eb\u4e0d\u77e5\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u53ea\u542c\u5f97\u5d14\u4e91\u5b50\u53c8\u5927\u58f0\u8bf4\u9053\uff1a\u2018\u8fd9\u5176\u5b9e \u5e76\u4e0d\u5173\u593a\u547d\u4ed9\u5b50\u8c22\u4e91\u771f\u7684\u4e8b\uff01\u90fd\u662f\u738b\u7624\u5b50\u4ece\u4e2d\u6363\u7684\u9b3c\uff01\u2019\u6211\u542c\u4ed6\u63d0\u8d77\u6211\u7684\u540d\u5b57\u3002\u66f4\u662f\u7559\u795e\uff0c \u90a3\u51e0\u4e2a\u9053\u58eb\u4f3c\u662f\u5341\u5206\u60ca\u8be7\uff0c\u53eb\u9053\uff1a\u2018\u738b\u7624\u5b50\u4e0d\u662f\u4f60\u4eec\u7ed3\u62dc\u7684\u4e09\u5f1f\u5417\uff1f\uff0c\u5d14\u4e91\u5b50\u9053\uff1a\u2018\u4e0d\u9519\uff0c \u4ed6\u662f\u5025\u4f97\u7684\u95e8\u5f92\uff0c\u5025\u4f97\u6d3e\u2026\u2026\u2019\u521a\u521a\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u53eb\uff0c\u53ea\u89c1\u5c71\u5ca9\u4e0a\u7a81\u7136\u98de\u4e0b\u4e00\u6761 \u9ed1\u5f71\uff0c\u6251\u5230\u5d14\u4e91\u5b50\u8eab\u4e0a\uff0c\u5d14\u4e91\u5b50\u4e3e\u8d77\u4ed6\u7684\u5927\u5f13\u4e00\u6321\uff0c\u4f46\u542c\u5f97\u58f0\u5982\u88c2\u5e1b\uff0c\u5d14\u4e91\u5b50\u602a\u53eb\u51e0\u58f0\uff0c\u767b \u65f6\u8dcc\u5012\u3002\u90a3\u53eb\u58f0\u771f\u662f\u51c4\u5389\u975e\u5e38\uff0c\u4ee4\u4eba\u6c57\u6bdb\u51db\u51db\u3002\u6b63\u5f53\u6b64\u65f6\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u7684\u4e1c\u897f\uff0c\u5ffd\u7136\u671d\u6211\u7684\u5934 \u98de\u6765\uff01\u201d \u3000\u3000\u8c22\u4e91\u771f\u53f7\u79f0\u593a\u547d\u4ed9\u5b50\uff0c\u5e73\u7d20\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u53ea\u6709\u522b\u4eba\u6015\u5979\uff0c\u4f46\u5982\u4eca\u5979\u8bf4\u5230\u6b64\u5904\uff0c\u4e5f\u4e0d\u81ea\u7981 \u58f0\u97f3\u98a4\u6296\uff0c\u4ee4\u4eba\u5fc3\u60b8\u3002\u8427\u9752\u5cf0\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u90a3\u662f\u5d14\u4e91\u5b50\u4ed7\u4ee5\u6210\u540d\u7684\u94c1\u80ce \u795e\u5f13\uff0c\u88ab\u62c9\u76f4\u4e86\u6210\u4e3a\u4e00\u6761\u94c1\u68cd\uff0c\u60f3\u662f\u5728\u90a3\u4eba\u98de\u6251\u800c\u4e0b\u4e4b\u65f6\uff0c\u4e24\u8fb9\u7528\u529b\u4e00\u593a\uff0c\u5c31\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50 \uff01\u201d\u5510\u7ecf\u5929\u542c\u4e86\u4e5f\u4e0d\u89c9\u9a87\u7136\uff0c\u60f3\u593a\u5f13\u63b7\u5f13\uff0c\u53ea\u4e0d\u8fc7\u4e00\u77ac\u95f4\u4e4b\u4e8b\uff0c\u5185\u529b\u6240\u81f3\uff0c\u94c1\u5f13\u4fbf\u53d8\u6210\u4e86\u94c1 \u68cd\uff0c\u8fde\u81ea\u5df1\u4e5f\u672a\u5fc5\u80fd\u591f\u3002\u8c22\u4e91\u771f\u53c8\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u7b97\u5389\u5bb3\uff0c\u5d14\u4e91\u5b50\u90a3\u628a\u795e\u5f13\uff0c\u662f\u4ef6\u5b9d\u7269\uff0c\u5f13\u5f26 \u7528\u94c2\u91d1\u7cbe\u70bc\uff0c\u5200\u5251\u96be\u65ad\uff0c\u5982\u4eca\u5374\u90fd\u6574\u6574\u9f50\u9f50\u7684\u4ece\u4e2d\u65ad\u4e86\u3002\u5f13\u5f26\u968f\u98ce\u98d8\u626c\uff0c\u6709\u5982\u4e00\u84ec\u4e71\u8349\uff0c\u6545 \u6b64\u53d1\u51fa\u545c\u545c\u58f0\u54cd\u3002\u5f04\u65ad\u5341\u6839\u516b\u6839\u5c1a\u4e0d\u8db3\u4e3a\u5947\uff0c\u53ea\u662f\u8fd9\u4ec5\u4ec5\u662f\u4e00\u62c2\u4e4b\u529b\uff0c\u5c31\u5168\u90e8\u5f04\u65ad\uff0c\u82e5\u975e\u773c \u89c1\uff0c\u8fde\u6211\u4e5f\u4e0d\u6562\u76f8\u4fe1\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u4ece\u5ca9\u77f3\u4e0a\u98de\u6251\u4e0b\u6765\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u4e00\u4e2a\u8eab\u7a7f\u9ec4\u8863\u7684\u8001 \u9053\u58eb\uff1f\u201d\u8c22\u4e91\u771f\u90533\u201c\u4e0d\uff0c\u770b\u6837\u5b50\u4e0d\u8fc7\u662f\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u6c49\u5b50\uff0c\u53c8\u9ad8\u53c8\u7626\uff0c\u5934\u53d1\u4e25\u5982\u4e71\u8349\uff0c\uff0c \u6708\u5149\u4e0b\u9762\u8272\u82cd\u767d\u4e4b\u6781\uff0c\u4ee4\u4eba\u60ca\u6050\u3002\u201d\u5510\u7ecf\u5929\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u53c8\u4e0d\u662f\u9ec4\u77f3 \u9053\u4eba\u4e86\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u51e0\u4f4d\u6b63\u6d3e\u7684\u524d\u8f88\u4e4b\u5916\uff0c\u53c8\u6709\u8c01\u6709\u8fd9\u6837\u7684\u529f\u529b\uff1f\u201d \u3000\u3000\u8427\u9752\u5cf0\u4e5f\u662f\u6781\u4e3a\u60ca\u8be7\uff0c\u4f46\u4ed6\u8001\u4e8e\u4e16\u6545\uff0c\u4e00\u60f3\u4e4b\u4e0b\uff0c\u4fbf\u9053\uff1a\u201c\u770b\u6765\u6b64\u4eba\u4e0d\u662f\u5025\u4f97\u6d3e\u7684\uff0c\u4ea6 \u662f\u4e0e\u5025\u4f97\u5927\u6709\u5173\u7cfb\u4e4b\u4eba\uff0c\u6240\u4ee5\u5f53\u5d14\u4e91\u5b50\u521a\u63d0\u5230\u5025\u4f97\u6d3e\u65f6\uff0c\u4ed6\u4fbf\u60f3\u6740\u4eba\u706d\u53e3\u3002\u201d\u5510\u7ecf\u5929\u60f3\u8d77\u8d75 \u7075\u541b\u7b49\u5341\u4e09\u4e2a\u5025\u4f97\u9ad8\u624b\u56f4\u653b\u96f7\u9707\u5b50\u4e4b\u4e8b\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5025\u4f97\u6d3e\u4e2d\u4ee5\u8d75\u7075\u541b\u4e3a\u9996\u7684\u6709\u4e00 \u73ed\u4eba\uff0c\u6548\u529b\u6e05\u5ef7\uff0c\u60f3\u88ad\u706d\u56de\u7586\u4e00\u5e26\u6297\u6e05\u7684\u6b66\u5f53\u6d3e\u95e8\u4eba\uff0c\u5d14\u4e91\u5b50\u4e00\u5b9a\u662f\u60f3\u8bf4\u660e\u6b64\u4e8b\uff0c\u6240\u4ee5\u88ab\u90a3 \u4eba\u6740\u4e86\u3002\u201d \u3000\u3000\u8c22\u4e91\u771f\u9053\uff1a\u201d\u4e0d\u9519\uff0c\u90a3\u4eba\u662f\u60f3\u706d\u53e3\u3002\u4e0d\u8fc7\uff0c\u4eba\u6ca1\u6709\u6740\uff0c\u53e3\u5374\u706d\u4e86\u3002\u201d\u8427\u9752\u5cf0\u5947\u9053\uff1a\u201c\u600e \u4e48\uff1f\u5d14\u4e91\u5b50\u7ed9\u4ed6\u70b9\u4e86\u54d1\u7a74\u5417\uff1f\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u8fd8\u4e0d\u4ec5\u662f\u88ab\u70b9\u4e86\u54d1\u7a74\u5462\uff01\u90a3\u94c1\u5f13\u8dcc\u5728\u6211\u7684\u8eab\u8fb9 \uff0c\u6211\u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\uff0c\u5e78\u597d\u5b69\u5b50\u5403\u9971\u5976\u4e86\uff0c\u4e5f\u719f\u7761\u4e86\uff0c\u6ca1\u6709\u58f0\u606f\uff0c\u90a3\u4eba\u6ca1\u6709\u53d1\u73b0\u3002\u6211\u4ece\u5ca9\u77f3\u7684 \u7f1d\u9699\u4e2d\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u90a3\u4eba\u5c06\u5d14\u4e91\u5b50\u6253\u5012\u4e4b\u540e\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u53ea\u542c\u5f97\u90a3\u51e0\u4e2a\u9053\u58eb\u4e2a\u4e2a\u8377\u8377\u602a\u53eb\uff0c \u624b\u821e\u8db3\u8e48\u7684\u4e71\u8df3\uff0c\u5c31\u50cf\u811a\u4e0b\u662f\u4e00\u76c6\u70ad\u706b\u4e00\u6837\u3002\u90a3\u4eba\u602a\u7b11\u9053\uff1a\u2018\u770b\u4f60\u4eec\u8fd8\u6562\u4e0d\u6562\u4e71\u56bc\u820c\u5934\uff01\u2019 \u8f6c\u77ac\u4e4b\u95f4\uff0c\u53c8\u63c9\u5347\u5230\u5c71\u5761\u4e4b\u4e0a\uff0c\u7aef\u7684\u662f\u6377\u4f3c\u733f\u7334\uff0c\u5e7d\u8c37\u4e4b\u4e2d\u95fb\u5f97\u602a\u53eb\u58f0\u4e0e\u602a\u7b11\u4e4b\u58f0\u4ea4\u54cd\uff0c\u9a87 \u4eba\u5fc3\u9b44\u3002\u4e0d\u4e45\u7b11\u58f0\u6e10\u6b47\uff0c\u9053\u58eb\u7684\u602a\u53eb\u4e5f\u6e10\u6e10\u5636\u54d1\uff0c\u518d\u8fc7\u4e00\u4f1a\u5df2\u53d1\u4e0d\u51fa\u58f0\u6765\u3002\u6211\u6599\u90a3\u602a\u4eba\u662f\u53bb \u5f97\u8fdc\u4e86\uff0c\u60f3\u6551\u4eba\u662f\u6211\u8f88\u5e94\u4e3a\u4e4b\u4e8b\uff0c\u4fbf\u5927\u7740\u80c6\u5b50\uff0c\u51fa\u6765\u4e00\u770b\uff0c\u5f53\u521d\u6211\u4e5f\u4ee5\u4e3a\u4ed6\u4eec\u6216\u8005\u662f\u88ab\u70b9\u4e86 \u54d1\u7a74\uff0c\u54ea\u77e5\u51fa\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u51e0\u4e2a\u9053\u58eb\u8fde\u540c\u5d14\u4e91\u5b50\u5728\u5185\uff0c\u4e2a\u4e2a\u5f20\u5927\u5634\u5df4\uff0c\u53e3\u4e2d\u7684\u820c\u5934\uff0c\u90fd\u5df2 \u5272\u65ad\uff0c\u518d\u4ed4\u7ec6\u5ba1\u89c6\uff0c\u80a9\u5934\u7684\u7435\u7436\u9aa8\u4e5f\u90fd\u88ab\u634f\u788e\uff0e\u4e0d\u4f46\u4e2a\u4e2a\u6210\u4e86\u54d1\u5df4\uff0c\u800c\u5df2\u6b66\u529f\u4ea6\u4ff1\u6d88\u5931\uff0c\u5168 \u90e8\u6210\u4e86\u5e9f\u4eba\uff01\u201d \u3000\u3000\u8427\u5947\u5cf0\u592b\u5987\u542c\u5f97\u9a87\u7136\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u6837\u72e0\u6bd2\uff01\u7b80\u76f4\u6bd4\u90a3\u5927\u9ebb\u75af\u8fd8\u8981\u53ef\u6076\u5341\u500d\uff01\u90a3\u5927\u9ebb\u75af \u53ea\u4e0d\u8fc7\u5f00\u5f00\u73a9\u7b11\u800c\u5df2\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u51fa\u624b\u4fbf\u5f04\u4eba\u6b8b\u5e9f\u3002\u201d\u5510\u7ecf\u5929\u9ed8\u7136\u4e0d\u8bed\uff0c\u53ea\u542c\u5f97\u8c22\u4e91\u771f\u5f80\u4e0b\u7eed \u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u4e2a\u4e2a\u76ee\u5149\u5446\u6ede\uff0c\u5634\u5df4\u5f20\u5f00\uff0c\u5408\u62e2\u4e0d\u6765\uff0c\u53c8\u4e0d\u80fd\u53d1\u58f0\uff0c\u8138\u4e0a\u7684\u808c\u8089\u4e5f\u626d\u66f2\u53d8\u5f62\uff0c \u5341\u5206\u53ef\u6015\uff0c\u6211\u53c8\u4e0d\u80fd\u5c06\u4ed6\u4eec\u4e00\u4e2a\u4e2a\u80cc\u51fa\u53bb\uff0c\u5fc3\u4e0b\u53ef\u662f\u5f53\u771f\u5bb3\u6015\uff0c\u56e0\u6b64\u53ea\u597d\u4e0d\u987e\u51f6\u9669\uff0c\u60f3\u8d76\u5230 \u91d1\u5149\u5bfa\u62a5\u8baf\u3002\u51fa\u4e86\u5c71\u8c37\u4e4b\u540e\u4e0d\u4e45\uff0c\u89c1\u6709\u5341\u591a\u4e2a\u9053\u58eb\u6253\u7740\u706b\u628a\uff0c\u4ece\u8c37\u53e3\u7684\u53e6\u4e00\u7aef\u8fdb\u6765\uff0c\u5927\u58f0\u547c \u5524\uff0c\u731c\u60f3\u662f\u4ed6\u4eec\u7684\u540c\u95e8\u5e08\u5144\u5f1f\uff0c\u6765\u627e\u5bfb\u4ed6\u4eec\u7684\u3002\u6211\u7a0d\u4e3a\u5bbd\u5fc3\uff0c\u4f46\u60f3\u6b64\u4e8b\u8fd8\u662f\u8be5\u62a5\u4e0e\u5192\u5927\u4fa0\u77e5 \u9053\uff0c\u56e0\u6b64\u4ecd\u7136\u8d76\u5f80\u3002\u54ea\u77e5\u5230\u4e86\u91d1\u9876\u7684\u9644\u8fd1\uff0c\u53c8\u78b0\u5230\u4e86\u90a3\u4e2a\u5927\u9ebb\u75af\uff01\u7adf\u5728\u4e00\u591c\u4e4b\u95f4\uff0c\u8fde\u906d\u4e24\u6b21 \u9669\u4e8b\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u5fae\u7b11\u9053\uff1a\u201c\u60f3\u662f\u90a3\u5927\u9ebb\u75af\u4e5f\u77e5\u9053\u4f60\u593a\u547d\u4ed9\u5b50\u7684\u5927\u540d\uff0c\u56e0\u6b64\u6545\u610f\u4e0e\u4f60\u4e3a\u96be\u3002\u201d\u8c22\u4e91 \u771f\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u4ed6\u5982\u4f55\u8ba4\u5f97\u6211\uff0c\u6211\u8d70\u5230\u91d1\u9876\u9644\u8fd1\uff0c\u91d1\u5149\u5bfa\u5df2\u662f\u9065\u9065\u5728\u671b\uff0c\u60f3\u662f\u56e0\u4e3a\u6211\u8dd1\u5f97 \u5927\u5feb\uff0c\u5b69\u5b50\u53c8\u9192\u4e86\uff0c\u54c7\u54c7\u7684\u54ed\u51fa\u58f0\u6765\u3002\u6211\u505c\u4e86\u4e0b\u6765\uff0c\u8f7b\u8f7b\u629a\u62cd\u4ed6\uff0c\u60f3\u8d77\u81ea\u5df1\u4e00\u4eba\uff0c\u80cc\u7740\u5b69\u5b50 \u5954\u6ce2\uff0c\u4e0d\u514d\u6709\u4e9b\u4f24\u611f\uff0c\u6211\u62cd\u7740\u5b69\u5b50\u9053\uff1a\u5440\uff0c\u82e5\u4f60\u7239\u7239\u5728\u6b64\uff0c\u4ec0\u4e48\u51f6\u9669\u4e4b\u4e8b\uff0c\u54b1\u4eec\u90fd\u4e0d\u7528\u5bb3\u6015 \uff01\uff0c\u5b69\u5b50\u4e5f\u4f3c\u4e4e\u77e5\u9053\u5927\u4eba\u5fc3\u610f\uff0c\u54ed\u58f0\u987f\u6b62\u3002\u6211\u6b63\u6b32\u7ee7\u7eed\u8d76\u8def\uff0c\u5ffd\u542c\u5f97\u563b\u563b\u7684\u602a\u7b11\u4e4b\u58f0\uff0c\u53d1\u81ea \u5934\u9876\u3002\u6211\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5728\u5934\u9876\u7684\u4e00\u4e2a\u5ca9\u77f3\u4e0a\uff0c\u4e00\u4e2a\u6ee1\u9762\u7ea2\u4e91\u3001\u6d53\u7709\u5927\u773c\u7684\u6c49\u5b50\uff0c\u62ab\u895f\u8fce\u98ce \uff0c\u7b95\u8e1e\u77f3\u4e0a\uff0c\u8d64\u818a\u9732\u80f8\uff0c\u81c2\u4e0a\u957f\u6ee1\u7599\u7629\uff0c\u80f8\u524d\u9732\u51fa\u4e00\u64ae\u9ed1\u6bdb\uff0c\u7adf\u7136\u662f\u4e2a\u9ebb\u75af\uff0c\u8fd9\u4e00\u4e0b\u5413\u5f97\u6211 \u6bd4\u521a\u624d\u8fd8\u8981\u5bb3\u6015\uff01\u90a3\u9ebb\u75af\u51ed\u9ad8\u671b\u4e0b\uff0c\u8fce\u7740\u6211\u563b\u563b\u7b11\u9053\uff1a\u2018\u6765\u7684\u662f\u593a\u547d\u4ed9\u5b50\u8c22\u4e91\u771f\u5417\uff1f\u9aa4\u7136\u95f4 \u6211\u60f3\u8d77\u4e86\u4ed6\u83ab\u975e\u5c31\u662f\u90a3\u4e2a\u6c5f\u6e56\u4e0a\u6240\u4f20\u8bf4\u7684\u4eba\u89c1\u4eba\u4f2f\u7684\u5927\u9ebb\u75af\uff1f\u5b69\u5b50\u53c8\u54ed\u4e86\uff0c\u6211\u9f13\u8d77\u52c7\u6c14\u9053\uff1a \u2018\u5582\uff0c\u4f60\u4e0d\u8981\u5413\u4e86\u6211\u7684\u5b69\u5b50\uff01\u2019\u90a3\u9ebb\u75af\u9053\uff1a\u2018\u4f60\u4e0d\u662f\u53f7\u79f0\u593a\u547d\u4ed9\u4e8e\u5417\uff1f\u600e\u4e48\u4f60\u5374\u6015\u6211\uff1f\u5ffd\u7136 \u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u5439\u4e86\u4e00\u58f0\u80e1\u54e8\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5b69\u5b50\u7ade\u7ed9\u4ed6\u9017\u5f97\u7b11\u4e86\u8d77\u6765\u3002\u90a3\u9ebb\u75af\u5f97\u610f\u6d0b\u6d0b\u7684\u7b11 \u9053\uff1a\u2018\u5206\u660e\u662f\u4f60\u6015\u6211\uff0c\u4f60\u5374\u5047\u8bf4\u662f\u5b69\u5b50\u6015\u6211\u3002\u5b69\u5b50\u975e\u4f46\u4e0d\u6015\u6211\uff0c\u8fd8\u559c\u6b22\u6211\u5462\uff01\u5582\uff0c\u4f60\u7684\u4e08\u592b \u94c1\u62d0\u4ed9\u5462\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u4e0e\u4f60\u540c\u6765\uff1f\u6211\u6b63\u5728\u60f3\u5e94\u4ed8\u4e4b\u6cd5\uff0c\u4e0d\u7b54\u4ed6\u7684\u8bf4\u8bdd\u3002\u90a3\u9ebb\u75af\u53c8\u7b11\u9053\uff1a\u2018\u5440\uff0c \u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u542c\u4f60\u521a\u624d\u81ea\u8a00\u81ea\u8bed\uff0c\u94c1\u62d0\u4ed9\u5927\u7ea6\u662f\u6ca1\u6709\u6765\u4e86\uff0c\u8981\u4e0d\u7136\u6211\u5012\u8981\u5411\u8fd9\u4f4d\u540d\u6ee1\u5929\u4e0b\u7684 \u540c\u884c\u8bf7\u6559\u8bf7\u6559\uff01\u90a3\u9ebb\u75af\u4f5c\u53eb\u5316\u5b50\u6253\u626e\uff0c\u7528\u7684\u53c8\u662f\u4e00\u679d\u94c1\u62d0\uff0c\u770b\u6765\u5012\u771f\u50cf\u6211\u7684\u4e08\u592b\u7684\u540c\u884c\u3002\u90a3 \u9ebb\u75af\u53c8\u9053\uff1a\u2018\u5582\uff0c\u6211\u597d\u6b79\u90fd\u662f\u4f60\u4e08\u592b\u7684\u540c\u8f88\uff0c\u4f60\u600e\u4e48\u5bf9\u6211\u4e0d\u7406\u4e0d\u776c\uff1f\u2019\u6211\u624b\u629a\u5251\u67c4\uff0c\u4fbf\u60f3\u51b2 \u8fc7\uff0c\u559d\u4ed6\u8ba9\u5f00\u3002\u90a3\u9ebb\u75af\u9053\uff1a\u2018\u884c\uff0c\u4f46\u4f60\u6273\u8d77\u9762\u5b54\uff0c\u5374\u6559\u4eba\u89c1\u4e86\u751f\u6c14\uff0c\u4f60\u5f97\u5bf9\u6211\u7b11\u4e00\u7b11\uff0c\u6211\u5c31 \u5c06\u8def\u8ba9\u5f00\u3002\u2019\u6211\u4e0d\u7531\u4e0d\u6012\uff0c\u62d4\u5251\u4fbf\u51b2\uff0c\u90a3\u9ebb\u75af\u7b11\u9053\uff1a\u2018\u54c8\uff0c\u6211\u4e5f\u4e0d\u593a\u4f60\u7684\u547d\uff0c\u5c31\u662f\u8981\u4f60\u7b11\uff0c \u4f60\u4e0d\u7b11\u4e5f\u4e0d\u884c\uff01\u2019\u4ed6\u7b95\u8e1e\u5728\u5ca9\u77f3\u4e0a\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5ffd\u7136\u968f\u624b\u4e00\u6293\uff0c\u5c06\u4e00\u5757\u77f3\u5934\uff0c\u634f\u6210\u4e86\u51e0\u4e2a\u5c0f \u5757\uff0c\u4e00\u6296\u624b\u5c31\u5411\u6211\u6253\u6765\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e5f\u50cf\u4ed6\u6253\u96f7\u9707\u5b50\u4e00\u6837\uff0c\u4e0d\u8fc7\u6253\u96f7\u9707\u5b50\u662f\u7528\u94c1\u62d0\uff0c\u800c\u6253\u4f60\u5219\u7528\u7684\u662f\u788e \u77f3\u3002\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u90a3\u77f3\u5b50\u6765\u5f97\u5feb\u6781\uff0c\u4e00\u5757\u6253\u5de6\u808b\u7684\u8f6f\u9ebb\u7a74\uff0c\u4e00\u5757\u6253\u53f3\u808b\u7684\u75d5\u75d2 \u7a74\uff0c\u8fd8\u6709\u4e00\u5757\u6253\u7b11\u8170\u7a74\u3002\u4f5c\u54c1\u5b57\u5f62\u6253\u6765\uff0c\u624b\u6cd5\u602a\u5f02\u4e4b\u6781\u3002\u524d\u9762\u662f\u5ced\u58c1\u60ac\u5ca9\uff0c\u6211\u82e5\u7528\u8f7b\u529f\u8eb2\u95ea \uff0c\u53ea\u80fd\u540e\u659c\u7eb5\u8dc3\u3002\u4f46\u8fd9\u9ebb\u75af\u771f\u662f\u53ef\u6076\u4e4b\u6781\uff0c\u4ed6\u6253\u51fa\u7684\u4e00\u628a\u788e\u77f3\uff0c\u6709\u7684\u76f4\u5c04\uff0c\u6709\u7684\u659c\u98de\uff0c\u6709\u7684 \u5374\u5411\u5de6\u53f3\u65cb\u8f6c\uff0c\u6709\u7684\u98de\u8fc7\u4e86\u5934\u9876\u53c8\u5012\u8f6c\u56de\u6765\uff0c\u9664\u4e86\u5411\u6b63\u9762\u5954\u6765\u7684\u90a3\u4e09\u5757\u5c0f\u77f3\u5b50\u4e4b\u5916\uff0c\u5de6\u53f3\u659c \u65b9\u548c\u540e\u9762\u6389\u8f6c\u5934\u7684\u77f3\u5b50\uff0c\u4e5f\u90fd\u662f\u6bcf\u4e09\u9897\u6210\u4e3a\u4e00\u7ec4\uff0c\u5206\u6253\u4e09\u5904\u7a74\u9053\uff0c\u5728\u8fd9\u60c5\u52bf\u4e4b\u4e0b\uff0c\u6211\u4e0d\u8bba\u5411 \u4f55\u65b9\u8eb2\u95ea\uff0c\u90fd\u4e00\u5b9a\u662f\u81ea\u5df1\u8fce\u4e0a\u53bb\u8981\u7ed9\u4ed6\u6253\u4e2a\u6b63\u7740\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u79cd\u6253\u6697\u5668\u7684\u624b\u6cd5\u786e\u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u6211\u770b\u9664\u4e86\u56db\u5ddd\u5510\u5bb6\uff0c\u4e0e\u4ee5\u524d\u7075\u5c71\u6d3e\u7684\u540d \u5bbf\u97e9\u91cd\u5c71\u4e4b\u5916\uff0c\u6050\u6015\u5c31\u8981\u6570\u5230\u4ed6\u4e86\u3002\u4f60\u624b\u4e0a\u6ca1\u6709\u5b9d\u5200\u5b9d\u5251\uff0c\u53c8\u80cc\u7740\u5b69\u5b50\uff0c\u90a3\u662f\u66f4\u96be\u8eb2\u95ea\u7684\u4e86 \u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u6211\u4e5f\u4ee5\u4e3a\u5b9a\u88ab\u6253\u4e2d\uff0c\u767e\u5fd9\u4e4b\u4e2d\uff0c\u53ea\u597d\u8fd0\u6c14\u95ed\u7a74\uff0c\u4f46\u90a3\u4e9b\u77f3\u5b50\u6765\u5f97\u592a\u5feb\uff0c\u5373\u7b97 \u8fd0\u6c14\u95ed\u7a74\u4e5f\u6765\u4e0d\u53ca\uff0c\u4e0d\u6599\u5c31\u5728\u8fd9\u4e00\u77ac\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6781\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u63a5\u7740\u53ee\u53ee\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c \u6211\u8fde\u770b\u4e5f\u770b\u4e0d\u6e05\u695a\uff0c\u90a3\u4e9b\u77f3\u5b50\u500f\u7684\u4fbf\u5411\u6211\u8eab\u65c1\u98de\u8fc7\uff0c\u5815\u4e0b\u5e7d\u8c37\uff0c\u90a3\u9ebb\u75af\u5927\u53eb\u4e00\u58f0\uff0c\u767b\u65f6\u5728\u5ca9 \u77f3\u4e0a\u98de\u8dc3\u800c\u8d77\uff0c\u653e\u5f00\u4e86\u6211\uff0c\u5954\u5165\u5bc6\u6797\u4e4b\u4e2d\uff0c\u5bc6\u6797\u4e2d\u53ea\u89c1\u9752\u8863\u4e00\u95ea\uff0c\u662f\u4e2a\u5973\u5b50\uff0c\u53ea\u77a7\u89c1\u5979\u7684\u80cc \u5f71\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5c31\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u5927\u5947\uff0c\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u90a3\u628a\u788e\u77f3\u5b9a\u662f\u7ed9\u8fd9\u5973\u5b50\u7528\u6697\u5668\u6253\u843d\u4e86\uff0c\u4f60\u77a7\u51fa\u4e86\u662f\u4ec0\u4e48 \u6697\u5668\u5417\uff1f\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u6ca1\u6709\u77a7\u51fa\uff0c\u4e0d\u8fc7\u542c\u8fd9\u58f0\u97f3\uff0c\u90a3\u662f\u4e00\u79cd\u6781\u5fae\u7ec6\u7684\u6697\u5668\uff0c\u6562\u60c5\u662f\u6885\u82b1\u9488 \u4e4b\u7c7b\u3002\u201d\u81f3\u6b64\uff0c\u5510\u7ecf\u5929\u4e5f\u4e0d\u7981\u9a87\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u5973\u5b50\u8eab\u533f\u6797\u4e2d\uff0c\u6bd4\u90a3\u75af\u4e10\u8ddd\u79bb\u8c22\u4e91\u771f\u8fd8\u8fdc\uff0c \u5c45\u7136\u80fd\u7528\u98de\u9488\u78b0\u843d\u788e\u77f3\uff0c\u8fd9\u4efd\u6b66\u529f\u5c82\u4e0d\u662f\u5c1a\u5728\u6211\u4e4b\u4e0a\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u6c89\u601d\u534a\u664c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u771f\u7684\u4e0d\u662f\u51b0\u5ddd\u5929\u5973\uff1f\u201d\u8fd9\u8bdd\u4ed6\u5df2\u95ee\u8fc7\u4e00\u6b21\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u662f \u6000\u7591\u4e4b\u6781\uff0c\u9664\u4e86\u51b0\u5ddd\u5929\u5973\u8fd8\u6709\u4f55\u4eba\uff1f\u8c22\u4e91\u771f\u9053\uff1a\u201c\u5f53\u65f6\u6211\u6b63\u5728\u60ca\u9a87\u4e4b\u4e2d\uff0c\u90a3\u5973\u5b50\u53c8\u8dd1\u5f97\u5feb\u6781 \uff0c\u6797\u5b50\u4e2d\u7684\u6811\u679d\u6811\u53f6\uff0c\u53c8\u906e\u4f4f\u5979\u7684\u8eab\u5b50\uff0c\u6211\u4ec5\u4ec5\u77a7\u4e86\u4e00\u773c\u5979\u7684\u80cc\u5f71\uff0c\u60ca\u9e3f\u4e00\u77a5\uff0c\u8fc7\u773c\u4e0d\u89c1\u3002 \u51b0\u5ddd\u5929\u5973\u8eab\u5b50\u4fee\u957f\uff0c\u800c\u8fd9\u4e2a\u5973\u5b50\u7684\u80cc\u5f71\u5374\u6bd4\u5979\u77ee\u5f97\u591a\uff0c\u770b\u6765\u4e0d\u4f3c\u662f\u51b0\u5ddd\u5929\u5973\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u8fc7\u4e86\u5348\u591c\uff0c\u6708\u4eae\u6e10\u6e10\u897f\u79fb\uff0c\u5c71\u4e2d\u7684\u201c\u5723\u706f\u201d\u4e00----\u90a3\u4e9b\u78f7\u706b\u6240\u53d1\u7684\u70b9\u70b9\u4e4b\u5149\uff0c\u4e5f \u534a\u660e\u534a\u706b\uff0c\u98d8\u6d6e\u5c71\u8c37\uff0c\u6e10\u6e10\u6d88\u901d\u3002\u5510\u7ecf\u5929\u4e00\u5fc3\u60f3\u5ff5\u51b0\u5ddd\u5929\u5973\uff0c\u5fc3\u9053\uff1a\u201c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e4b\u4e0b\uff0c\u8c22 \u4e91\u771f\u8d70\u4e86\u773c\u4e5f\u662f\u6709\u7684\u3002\u6211\u5c31\u4e0d\u4fe1\u4e16\u95f4\u9664\u4e86\u51b0\u5ddd\u5927\u5973\u4e4b\u5916\uff0c\u8fd8\u6709\u54ea\u4e00\u4e2a\u5c11\u5973\u6709\u6b64\u672c\u9886\u3002\u201d\u8c22\u4e91 \u771f\u9053\uff1a\u201c\u4f60\u5c61\u6b21\u63d0\u8d77\u51b0\u5ddd\u5929\u5973\uff0c\u51b0\u5ddd\u5929\u5973\u4e0d\u662f\u8bf4\u8fc7\u4e0d\u4e0b\u51b0\u5cf0\u7684\u5417\uff0c\u96be\u9053\u5979\u4e5f\u5230\u6b64\u95f4\u6765\u4e86\uff1f\u201d \u5510\u7ecf\u5927\u9053\uff1a\u201c\u51b0\u5cf0\u5012\u4e86\uff0c\u5979\u81ea\u7136\u4e5f\u4e0b\u5c71\u4e86\u3002\u53ea\u6015\u73b0\u5728\u5c31\u5728\u6b64\u95f4\uff01\u201d \u3000\u3000\u8c22\u4e91\u771f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u82e5\u7136\u662f\u5979\uff0c\u4f46\u613f\u5979\u4e0d\u8981\u78b0\u4e0a\u90a3\u4e2a\u5927\u9ebb\u75af\u3002\u51b0\u5ddd\u5929\u5973\u6709\u5982\u5e7d\u8c37\u767e \u5408\uff0c\u6e05\u51c0\u9ad8\u6d01\uff0c\u82e5\u7136\u89c1\u7740\u90a3\u5927\u9ebb\u75af\uff0c\u4e0d\u8981\u8bf4\u4ea4\u624b\uff0c\u53ea\u6015\u89c1\u4e86\u4ed6\u7684\u5f62\u8c8c\uff0c\u4e5f\u4f1a\u6076\u5fc3\uff0c\u90a3\u5c82\u4e0d\u662f \u73b7\u8fb1\u4e86\u6211\u4eec\u9ad8\u8d35\u7684\u516c\u4e3b\uff01\u201d\u5510\u7ecf\u5929\u542c\u4e86\uff0c\u8111\u6d77\u4e2d\u53c8\u6d6e\u8d77\u4e86\u51b0\u5ddd\u5929\u5973\u4e0e\u90a3\u75af\u4e10\u540c\u884c\u7684\u60c5\u5f62\uff0c\u4eba \u4e16\u4e4b\u4e8b\uff0c\u786e\u662f\u96be\u6599\uff0c\u51b0\u5ddd\u5927\u5973\u5c45\u7136\u4f1a\u4e0e\u90a3\u75af\u4e10\u7ed3\u4ea4\uff0c\u8bf4\u51fa\u6765\u4e5f\u65e0\u4eba\u76f8\u4fe1\u3002\u5982\u6b64\u4e00\u60f3\u3002\u5fc3\u4e2d\u66f4 \u662f\u96be\u8fc7\u3002\u8c22\u4e91\u771f\u89c1\u4ed6\u5165\u4e45\u4e0d\u8bed\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u4e48\uff1f\u662f\u60f3\u51b0\u5ddd\u5929\u5973\u8fd8\u662f\u60f3\u90a3\u4e2a\u5927\u9ebb\u75af\uff1a\u4e0d\u5982 \u4f60\u53bb\u51fa\u624b\uff0c\u5c06\u90a3\u9ebb\u75af\u9a71\u9010\u4e86\u5427\uff0c\u514d\u5f97\u4ed6\u5728\u6b64\u95f4\u6363\u4e71\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u773c\u73e0\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u62fc\u7740\u4eca\u591c\u4e0d\u7761\uff0c\u4e5f\u8981\u53bb\u5bfb\u627e\u4ed6\u4eec\u3002\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u4ed6 \u4eec\uff1f\u201d\u5947\u602a\u5510\u7ecf\u5929\u4f55\u4ee5\u5c06\u51b0\u5ddd\u5929\u5973\u4e0e\u90a3\u5927\u9ebb\u75af\u8fde\u5728\u4e00\u8d77\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u77a7\u4ed6\u4eec\u65e2\u4e0d\u8fdb\u5bfa\u4e2d \u6295\u5bbf\uff0c\u4e00\u5b9a\u8fd8\u5728\u9644\u8fd1\u7684\u5c71\u5934\u3002\u96f7\u9707\u5b50\u73b0\u5728\u60f3\u5df2\u6e10\u6e10\u6062\u590d\uff0c\u53ef\u4ee5\u884c\u8d70\u4e86\u3002\u4f60\u4eec\u518d\u53bb\u627e\u4ed6\uff0c\u53eb\u4ed6 \u5e26\u9886\u4f60\u4eec\u5230\u91d1\u5149\u5bfa\u53bb\u3002\u4eca\u665a\u4e4b\u4e8b\u5e94\u8be5\u7980\u544a\u5192\u5927\u4fa0\u77e5\u9053\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u79bb\u5f00\u4ed6\u4eec\uff0c\u72ec\u81ea\u6500\u767b\u5cf0\u9876\u3002\u5c71\u98ce\u632f\u8863\uff0c\u5e7d\u8c37\u733f\u557c\u3002\u661f\u6708\u897f\u79fb\uff0c\u7130\u706b\u660e\u706d\uff0c\u51b7\u51b7\u6e05 \u6e05\uff0c\u54ea\u91cc\u6709\u4eba\u7684\u5f71\u5b50\u3002\u5510\u7ecf\u5929\u8ff7\u8ff7\u832b\u832b\uff0c\u60f3\u8d77\u4e00\u665a\u4e4b\u95f4\uff0c\u6240\u89c1\u6240\u95fb\uff0c\u7adf\u7136\u6709\u8fd9\u4e48\u591a\u602a\u4e8b\u3002\u81ea \u5df1\u6b64\u6765\uff0c\u4e00\u8005\u662f\u4e3a\u4e86\u5bfb\u89c5\u51b0\u5ddd\u5929\u5973\uff0c\u4e8c\u8005\u662f\u4e3a\u4e86\u62a4\u6301\u6cd5\u4f1a\u3002\u4f46\u4f9d\u4eca\u665a\u4e4b\u4e8b\u770b\u6765\uff0c\u90a3\u4e2a\u628a\u5d14\u4e91 \u5b50\u4e0e\u6b66\u5f53\u9053\u58eb\u5f04\u6210\u6b8b\u5e9f\u7684\u602a\u4eba\uff0c\u65e2\u7136\u4e0d\u662f\u9ec4\u77f3\u9053\u4eba\uff0c\u90a3\u5c31\u66f4\u4e3a\u53ef\u8651\u3002\u4e00\u7b97\u8d77\u6765\uff0c\u654c\u4eba\u65b9\u9762\uff0c \u6700\u5c11\u6709\u4e09\u4e2a\u9ad8\u624b\uff0c\u9ec4\u77f3\u9053\u4eba\u3001\u8d64\u795e\u5b50\u548c\u90a3\u602a\u4eba\u3002\u8fd9\u4e09\u4eba\u7684\u6b66\u529f\uff0c\u81ea\u5df1\u90fd\u96be\u53d6\u80dc\uff0c\u4f55\u51b5\u8fd8\u6709\u90a3 \u4e2a\u75af\u4e10\uff0c\u5230\u65f6\u53c8\u4e0d\u77e5\u800d\u51fa\u4ec0\u4e48\u82b1\u6837\uff0c\u654c\u53cb\u96be\u77e5\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8ff7\u8ff7\u832b\u832b\uff0c\u5728\u5c71\u5dc5\u4e0a\u56db\u4e0b\u773a\u671b\uff0c\u4e0d\u81ea\u7981\u7684\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u51b0\u5a25\u59d0\u59d0\uff01\u201d \u4ed6\u8fd0\u5929\u5c71\u7684\u6b63\u5b97\u5185\u529f\uff0c\u4eba\u53c8\u5904\u5728\u5c71\u5dc5\uff0c\u63a5\u8fde\u53eb\u4e86\u51e0\u58f0\uff0c\u4f46\u542c\u5f97\u7fa4\u5cf0\u56de\u54cd\uff0c\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u51b0\u5a25 \u59d0\u59d0\uff0c\u51b0\u5a25\u59d0\u59d0\u2026\u2026\u201d\u4e4b\u58f0\u56de\u65cb\u7a7a\u9645\uff0c\u4e45\u4e45\u4e0d\u7edd\u3002\u8c05\u5728\u5468\u56f4\u5341\u4f59\u91cc\u5185\uff0c\u4e0d\u7ba1\u51b0\u5ddd\u5929\u5973\u662f\u85cf\u5728 \u5bc6\u6797\u8fd8\u662f\u5e7d\u8c37\uff0c\u53ea\u8981\u5979\u4eba\u5728\u6b64\u95ee\uff0c\u5c31\u5fc5\u5b9a\u80fd\u591f\u542c\u89c1\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u53eb\u4e86\u51e0\u58f0\uff0c\u6b47\u4e86\u4e00\u9635\uff0c\u53c8\u53eb\u51e0\u58f0\uff0c\u5f53\u90a3\u56de\u58f0\u6e10\u6e10\u6d88\u6b47\u4e4b\u9645\uff0c\u5510\u7ecf\u5929\u6b63\u81ea\u5fc3\u4e2d\u5fd6\u5ea6 \uff1a\u201c\u5979\u542c\u89c1\u4e86\u6211\u7684\u558a\u58f0\uff0c\u4f1a\u4e0d\u4f1a\u5bfb\u58f0\u89c5\u8ff9\uff0c\u524d\u6765\u89c1\u6211\u5462\uff1f\u201d\u5fc3\u5ff5\u752b\u52a8\uff0c\u5ffd\u95fb\u5f97\u4e00\u58f0\u6781\u5176\u6e05\u8106 \u7684\u7b11\u58f0\uff0c\u8d77\u81ea\u5bf9\u9762\u5c71\u5cf0\uff0c\u8fd9\u7b11\u58f0\u719f\u6089\u4e4b\u6781\uff0c\u4f46\u5510\u7ecf\u5929\u5728\u8ff7\u832b\u4e4b\u9645\uff0c\u4e00\u65f6\u4e4b\u95f4\u5374\u4e0d\u6562\u65ad\u5b9a\u7a76\u662f \u51b0\u5ddd\u5929\u5973\u8fd8\u662f\u53e6\u5916\u7684\u719f\u4eba\uff1f\u5510\u7ecf\u5929\u81ea\u7136\u5e0c\u671b\u662f\u51b0\u5ddd\u5929\u5973\uff0c\u4e0d\u5047\u7ec6\u60f3\uff0c\u53c8\u53eb\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c \u6211\u5728\u8fd9\u513f\u3002\u4f60\u51fa\u6765\u5440\uff01\u201d\u5ffd\u5730\u773c\u524d\u5f69\u8272\u7f24\u7eb7\uff0c\u989d\u4e0a\u4e00\u7247\u6c81\u51c9\uff0c\u5510\u7ecf\u5929\u8fd8\u4ee5\u4e3a\u662f\u51b0\u5ddd\u5929\u5973\u7684\u51b0 \u9b44\u795e\u5f39\u3002 \u3000\u3000\u4f46\u51b0\u9b44\u795e\u5f39\u54ea\u6709\u5f69\u8272\uff1f\u5510\u7ecf\u5929\u4f38\u624b\u4e00\u63a5\uff0c\u53ea\u89c1\u624b\u4e2d\u63a5\u7740\u7684\u662f\u4e00\u4e2a\u82b1\u73af\uff0c\u7f16\u5f97\u5341\u5206\u7cbe\u81f4\uff0c \u5fc3\u4e2d\u5947\u602a\u4e07\u5206\uff01 \u3000\u3000\u7ec6\u770b\u65f6\uff0c\u539f\u6765\u90a3\u82b1\u73af\u7528\u82b1\u679d\u7ed3\u6210\u4e86\u4e00\u4e2a\u540c\u5fc3\u7ed3\uff0c\u4e0a\u9762\u8fd8\u7ed3\u51fa\u4e03\u4e2a\u5c0f\u5b57\u201c\u662f\u4f60\u7684\u603b\u662f\u4f60\u7684 \u201d\u82b1\u73af\u4e0a\u9732\u73e0\u6b32\u6ef4\uff0c\u770b\u6765\u8fd8\u662f\u521a\u521a\u7ed3\u6210\uff01\u5510\u7ecf\u5929\u5927\u559c\u82e5\u72c2\uff0c\u5bf9\u9762\u7684\u5c71\u5cf0\u4e0e\u8fd9\u8fb9\u6709\u602a\u77f3\u76f8\u8fde\uff0c \u4e0d\u8fc7\u6570\u4e08\uff0c\u5510\u7ecf\u5929\u98de\u8eab\u4e09\u63a0\uff0c\u5954\u4eba\u90a3\u8fb9\u7684\u5bc6\u6797\uff0c\u4e0d\u4f4f\u53e3\u7684\u53eb\u9053\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u51b0\u5a25\u59d0\u59d0\uff01\u201d\u5510 \u7ecf\u5929\u7684\u8f7b\u529f\uff0c\u9664\u4e86\u6709\u9650\u7684\u51e0\u4e2a\u524d\u8f88\u4e4b\u5916\uff0c\u80fd\u4e0e\u4ed6\u5339\u654c\u7684\u5b9e\u5728\u6ca1\u6709\u51e0\u4eba\uff0c\u5982\u4eca\u641c\u904d\u6797\u4e2d\uff0c\u7adf\u7136 \u4e0d\u89c1\u4eba\u5f71\u3002\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u5373\u7b97\u662f\u51b0\u5a25\u59d0\u59d0\uff0c\u4e5f\u9003\u4e0d\u5f97\u5982\u6b64\u4e4b\u7094\uff01\u201d\u5fc3\u4e2d\u5ffd\u7136\u4e00\u9635\u51b0\u51c9\uff0c\u60f3 \u9053\uff1a\u201c\u60f3\u51b0\u5ddd\u5929\u5973\u4f55\u7b49\u77dc\u6301\uff0c\u5979\u600e\u4f1a\u76f4\u8a00\u65e0\u9690\uff0c\u6beb\u65e0\u987e\u5fcc\u7684\u8bf4\u51fa\u5fc3\u4e2d\u7231\u610f\uff0c\u8fd9\u4e2a\u82b1\u73af\u4e00\u5b9a\u4e0d \u662f\u5979\u7f16\u7684\uff01\u201d \u3000\u3000\u4f46\u4e0d\u662f\u51b0\u5ddd\u5929\u5973\u7f16\u7684\uff0c\u53c8\u662f\u8c01\u4eba\u8fd9\u6837\u987d\u76ae\uff0c\u4e0e\u81ea\u5df1\u620f\u800d\uff1f\u5510\u7ecf\u5929\u51b7\u9759\u7ec6\u601d\uff0c\u5927\u559c\u4e4b\u540e\uff0c \u7ee7\u4e4b\u4ee5\u5927\u5931\u671b\uff0c\u4e0d\u89c9\u5fc3\u667a\u8ff7\u7cca\uff0c\u8ff7\u832b\u6005\u60c6\uff0c\u5728\u6797\u5b50\u4e2d\u6f2b\u65e0\u76ee\u7684\u5730\u8d70\u6765\u8d70\u53bb\uff0c\u76f4\u5230\u5929\u660e\u3002 \u3000\u3000\u8fd9\u5c71\u4e2d\u8fd8\u6709\u53e6\u4e00\u4e2a\u4eba\uff0c\u4e5f\u662f\u5982\u6b64\u8ff7\u832b\u6005\u60c6\u3002\u8fd9\u4e2a\u6bd4\u5510\u7ecf\u5929\u8fd8\u8981\u5931\u671b\u7684\u4eba\uff0c\u6b63\u662f\u91d1\u4e16\u9057\u3002 \u3000\u3000\u91d1\u4e16\u9057\u81ea\u4ece\u5ddd\u5eb7\u8fb9\u5883\u7684\u96c0\u513f\u5c71\u4e2d\uff0c\u89c1\u4e86\u51b0\u5ddd\u5929\u5973\u4e4b\u540e\uff0c\u4e00\u76f4\u6697\u6697\u8ffd\u8e2a\uff0c\u6216\u9690\u6216\u73b0\uff0c\u76f4\u8ffd \u5230\u4e86\u5ce8\u5d4b\u5c71\u3002\u8fd9\u4e0b\u65e5\u521a\u521a\u8fdb\u5165\u5ce8\u5d4b\u5c71\uff0c\u91d1\u4e16\u9057\u56e0\u4e3a\u4e0d\u613f\u8ba9\u5979\u53d1\u73b0\uff0c\u603b\u843d\u540e\u534a\u91cc\u4e4b\u9065\uff0c\u501f\u7740\u5c71 \u77f3\u6797\u6728\u906e\u853d\u8eab\u5f62\u3002\u5ce8\u5d4b\u5c71\u5c71\u52bf\u96c4\u5947\uff0c\u5730\u5f62\u590d\u6742\uff0c\u5343\u5ca9\u4e07\u7b1b\uff0c\u4ed6\u7a0d\u4e0d\u7559\u795e\uff0c\u62ac\u5934\u8fdc\u671b\uff0c\u5ffd\u7136\u5c31 \u4e0d\u89c1\u4e86\u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u7684\u80cc\u5f71\u3002\u4ed6\u6025\u6025\u52a0\u5feb\u811a\u6b65\uff0c\u5f80\u524d\u76f4\u8ffd\uff0c\u773c\u775b\u56db\u4e0b\u641c\u7d22\uff0c\u521a\u521a\u8f6c\u5165\u4e00\u5904\u5c71 \u62d7\uff0c\u8fd9\u65f6\u5929\u8272\u5c06\u665a\uff0c\u4f59\u971e\u6563\u5d0e\uff0c\u5c71\u62d7\u6709\u4e00\u9053\u98de\u7011\u6d41\u6cc9\uff0c\u4ece\u5c71\u9876\u76f4\u6cfb\u4e0b\u6765\uff0c\u6c47\u6210\u4e00\u4e2a\u6e05\u6f84\u5e7d\u51b7 \u7684\u6c34\u6f6d\uff0c\u6f6d\u8fb9\u91ce\u82b1\u6742\u5f00\uff0c\u5f62\u6210\u4e86\u9526\u5c4f\u4e00\u6837\u7684\u82b1\u4e1b\uff0c\u82b1\u4e1b\u4e2d\u5ffd\u542c\u5f97\u6709\u4e2a\u5973\u5b69\u5b50\u683c\u683c\u7b11\u9053\uff1a\u201c\u5c0f \u516c\u4e3b\uff0c\u6211\u8bf4\u5510\u76f8\u516c\u4e00\u5b9a\u5148\u6765\u4e86\u8fd9\u91cc\u7b49\u4f60\u3002\u201d\u6b63\u662f\u51b0\u5ddd\u5929\u5973\u7684\u4f8d\u5973\u5e7d\u840d\u4e4b\u58f0\u3002\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00\u8df3 \uff0c\u51b0\u5ddd\u5929\u5973\u4e45\u4e45\u65e0\u8a00\uff0c\u53ea\u542c\u5f97\u5e7d\u840d\u53c8\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5c31\u662f\u6068\u4e86\u4ed6\uff0c\u4e5f\u8be5\u5411\u4ed6\u95ee\u4e2a\u6e05\u695a\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u8eb2\u5728\u4e00\u5757\u77f3\u5934\u540e\u9762\uff0c\u90a3\u77f3\u5934\u6ca1\u6709\u4eba\u9ad8\uff0c\u91d1\u4e16\u9057\u8737\u7f29\u8eab\u8eaf\uff0c\u624b\u811a\u4ecd\u7136\u7a0d\u7a0d\u9732\u51fa\u6765\u3002 \u91d1\u4e16\u9057\u6025\u7740\u8981\u542c\u5979\u4eec\u8bf4\u8bdd\uff0c\u4e5f\u4e0d\u7559\u610f\u3002\u82b1\u4e1b\u4e2d\u4f20\u51fa\u5f88\u4f4e\u5f31\u7684\u53f9\u606f\uff0c\u9690\u7ea6\u542c\u5f97\u662f\u51b0\u5ddd\u5929\u5973\u7684\u58f0 \u97f3\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u7ba1\u3002\u201d\u5e7d\u840d\u53c8\u662f\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u516c\u4e3b\uff0c\u5176\u5b9e\u4f60\u8fd9\u662f\u4f55\u82e6\u6765\u5462\uff0c\u6211\u660e\u660e \u77e5\u9053\u4f60\u6b22\u559c\u4ed6\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u56bc\u820c\u5934\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u82e5\u662f\u4f60\u4e0d\u6b22\u559c\u4ed6\uff0c\u4f60\u4e5f\u5c31\u4e0d\u4f1a\u6068\u4ed6 \u4e86\u3002\u201d\u91d1\u4e16\u9057\u542c\u4e86\uff0c\u5fc3\u5934\u53c8\u662f\u535c\u901a\u4e00\u8df3\uff0c\u7ec6\u60f3\u6b64\u8a00\uff0c\u5927\u6709\u9053\u7406\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e0d\u89c1\u8bf4\u8bdd\uff0c\u5e7d\u840d\u53c8\u9053\uff1a\u201c\u6211\u8bf4\u5440\uff0c\u4f60\u82e5\u518d\u548c\u5510\u76f8\u516c\u751f\u8fd9\u65e0\u8c13\u7684\u95f2\u6c14\uff0c\u5012\u6559\u5c0f\u4eba \u5f97\u610f\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\uff0c\u6709\u4e2a\u4eba\u5440\uff0c\u5c31\u50cf\u730e\u72ac\u4e00\u6837 \u8ffd\u9010\u6211\u4eec\uff0c\u4e0d\uff0c\u4e0d\u662f\u730e\u72ac\uff0c\u662f\u4e2a\u8d56\u86e4\u87c6\u5440\uff0c\u765e\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\u3002\u201d\u91d1\u4e16\u9057\u5927\u6012\uff0c\u4e0d\u7531\u81ea\u5df1\u7684 \u8df3\u4e86\u51fa\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6211\u662f\u765e\u86e4\u87c6\uff01\u201d \u3000\u3000\u82b1\u4e1b\u4e2d\u7f57\u8882\u8f7b\u98d8\uff0c\u7fe0\u73af\u5fae\u54cd\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u5e7d\u840d\u8d70\u4e86\u51fa\u6765\uff0c\u5e7d\u840d\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u516c\u4e3b\uff0c\u4f60\u77a7 \u6211\u8bf4\u5f97\u4e0d\u9519\u5427\u3002\u4f60\u8bf4\u4ed6\u662f\u4e0d\u662f\u50cf\u4e00\u5934\u730e\u72ac\uff0c\u9f3b\u5b50\u5012\u771f\u7075\u5462\uff0c\u54b1\u4eec\u5728\u54ea\u91cc\u4ed6\u90fd\u55c5\u5f97\u51fa\u6765\u3002\u5582\uff0c \u7b97\u6211\u8bf4\u9519\u4e86\uff0c\u597d\u4e0d\u597d\uff0c\u730e\u72ac\u6bd4\u765e\u86e4\u87c6\u8981\u9ad8\u4e00\u7b49\u3002\u201d\u91d1\u4e16\u9057\u4e00\u58f0\u51b7\u7b11\uff0c\u9762\u8272\u500f\u53d8\uff0c\u94c1\u62d0\u4e00\u4e3e\uff0c \u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u62e6\u5728\u524d\u9762\uff0c\u9053\uff1a\u201c\u4f60\u8981\u600e\u7684\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u662f\u5929\u9e45\uff0c\u6211\u8fd9\u765e\u86e4\u87c6\u671b\u90fd\u4e0d\u6562 \u4e00\u671b\uff0c\u4f60\u7684\u4f8d\u5973\u662f\u6c34\u9e2d\uff0c\u6211\u8fd9\u765e\u86e4\u87c6\u5012\u60f3\u54ac\u5979\u4e00\u53e3\uff01\u201d\u51b0\u5ddd\u5929\u5973\u6a2a\u7709\u4e00\u77a5\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u91d1 \u4e16\u9057\uff0c\u4f60\u773c\u4e2d\u8fd8\u6709\u6211\u5417\uff1f\u201d\u91d1\u4e16\u9057\u4e00\u751f\u4efb\u6027\u3002\u4ee5\u4ed6\u7684\u6b66\u529f\uff0c\u8981\u4f24\u5e7d\u840d\u90a3\u662f\u6613\u5982\u53cd\u638c\uff0c\u8fd9\u65f6\u88ab \u51b0\u5ddd\u5929\u5973\u4e00\u65a5\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u4f46\u89c9\u51b0\u5ddd\u5929\u5973\u81ea\u7136\u800c\u7136\u7684\u5177\u6709\u4e00\u79cd\u5a01\u4e25\u5c0a\u8d35\u7684\u795e\u6c14\uff0c\u6559\u4ed6 \u4e0d\u6562\u653e\u8086\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u518d\u8bf4\u51e0\u53e5\u51b7\u5632\u70ed\u8bbd\u7684\u8bb2\uff0c\u8bdd\u5230\u53e3\u8fb9\u53c8\u541e\u4e86\u4e0b\u53bb\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4f8d\u5973\u51fa\u8a00\u65e0\u72b6 \uff0c\u6211\u2026\u2026\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u8981\u6559\u8bad\u5979\u5417\uff1f\u6211\u7684\u4f8d\u5973\u4e0d\u5fc5\u4f60\u4ee3\u4e3a\u6559\u8bad\u3002\u201d\u91d1\u4e16\u9057\u6012\u706b\u53c8\u8d77\uff0c \u867d\u7136\u4e0d\u6562\u53d1\u4f5c\uff0c\u8d1f\u6c14\u7684\u8bf4\u8bdd\u5374\u51b2\u53e3\u8bf4\u4e86\u51fa\u6765\uff0c\u5c31\u7528\u51b0\u5ddd\u5929\u5973\u9002\u624d\u7684\u8bdd\u53cd\u95ee\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\uff0c \u4f60\u773c\u4e2d\u4e5f\u8fd8\u6709\u6211\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5411\u4ed6\u77a7\u4e86\u4e00\u773c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u672c\u662f\u840d\u6c34\u76f8\u9022\uff0c\u773c\u4e2d\u6709\u8c01 \u6ca1\u8c01\uff0c\u672c\u6765\u5c31\u65e0\u5173\u7d27\u8981\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u51b7\u4e86\u534a\u622a\uff0c\u5992\u6068\u60ed\u6012\u79cd\u79cd\u60c5\u7eea\u500f\u65f6\u6d8c\u4e0a\u5fc3\u5934\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u773c\u4e2d\u5c31\u53ea\u6709\u59d3\u5510\u7684\u90a3\u4e2a \u5c0f\u5b50\uff01\u201d\u5e7d\u840d\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u53c8\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u53f9\u4e86\u53e3\u6c14\uff0c\u773c\u5149\u5728\u91d1\u4e16\u9057\u9762\u4e0a\u6e9c\u8fc7\uff0c \u76ee\u5149\u5145\u6ee1\u601c\u60dc\u6e29\u67d4\uff0c\u867d\u7136\u5979\u7684\u5e74\u7eaa\u8981\u6bd4\u91d1\u4e16\u9057\u5c0f\uff0c\u5374\u50cf\u4e00\u4e2a\u59d0\u59d0\u6559\u8bad\u5f1f\u5f1f\u7684\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u4f60 \u6709\u8fd9\u8eab\u672c\u4e8b\uff0c\u82e5\u7136\u5f52\u4e86\u6b63\u9014\uff0c\u53ef\u4ee5\u6210\u4e3a\u4e00\u4ee3\u4fa0\u58eb\uff0c\u518d\u4e0d\u5c31\u662f\u6f5c\u5fc3\u6b66\u5b66\uff0c\u4e5f\u53ef\u6210\u4e00\u4ee3\u7684\u5b97\u5e08\u3002 \u600e\u4e48\u4f60\u5374\u8981\u6545\u610f\u5c06\u81ea\u5df1\u53d8\u5f97\u8fd9\u822c\u65e0\u8d56\uff1f\u201d\u91d1\u4e16\u9057\u5fc3\u5934\u4e00\u9707\uff0c\u8fd9\u79cd\u8bf4\u8bdd\uff0c\u4ed6\u5e73\u751f\u4ece\u672a\u542c\u4eba\u8bf4\u8fc7 \uff0c\u5728\u8bf4\u8bdd\u4e2d\u4e5f\u542c\u5f97\u51fa\u51b0\u5ddd\u5929\u5973\u5bf9\u4ed6\u7684\u7231\u60dc\u5173\u6000\uff0c\u4f46\u8fd9\u65f6\u5728\u5982\u6b64\u7684\u5fc3\u60c5\u4e4b\u4e0b\uff0c\u4ed6\u53c8\u54ea\u80fd\u591f\u51b7\u9759 \u7684\u53bb\u60f3\uff1f\u4ed6\u53ea\u89c9\u5168\u8eab\u8840\u8109\u6124\u5f20\uff0c\u8111\u4e2d\u7eb7\u4e71\uff0c\u8eab\u4e8e\u4f3c\u8981\u7206\u70b8\u4e00\u822c\uff0c\u534a\u664c\u624d\u8ff8\u51fa\u4e00\u53e5\u8bf4\u8bdd\uff1a\u201c\u6211 \u600e\u4e48\u65e0\u8d56\u4e86\uff1f\u201d\u4ed6\u81ea\u61c2\u4eba\u4e8b\u4ee5\u6765\uff0c\u5c31\u662f\u8fd9\u6837\u6124\u4e16\u5ac9\u4fd7\uff0c\u563b\u7b11\u6012\u9a82\uff0c\u6e38\u620f\u98ce\u5c18\uff0c\u4ece\u6765\u672a\u60f3\u8fc7\u81ea \u5df1\u7684\u884c\u5f84\u5bf9\u662f\u4e0d\u5bf9\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u8003\u8651\u8fc7\u4ec0\u4e48\u65e0\u8d56\u4e0d\u65e0\u8d56\u7684\u3002\u51b0\u5ddd\u5929\u5973\u88ab\u4ed6\u4e00\u95ee\uff0c\u987f\u7136\u6014\u4f4f\uff0c \u8bf4\u4e0d\u4e0a\u6765\u3002\u987b\u77e5\u51b0\u5ddd\u5929\u5973\u6240\u53d7\u7684\u6559\u517b\u548c\u4ed6\u5168\u7136\u4e0d\u540c\uff0c\u5979\u80af\u76f4\u8a00\u8bf4\u91d1\u4e16\u9057\u65e0\u8d56\uff0c\u5df2\u7ecf\u662f\u7834\u4e86\u5979 \u5e73\u65e5\u542b\u84c4\u77dc\u6301\u7684\u60ef\u4f8b\uff0c\u518d\u8981\u5979\u5f53\u9762\u6570\u8bf4\u522b\u4eba\u5982\u4f55\u65e0\u8d56\uff0c\u90a3\u7b80\u76f4\u662f\u4e0d\u53ef\u60f3\u50cf\u4e4b\u4e8b\u3002 \u3000\u3000\u53ea\u89c1\u91d1\u4e16\u9057\u7684\u76ee\u5149\u5982\u75f4\u4f3c\u50bb\uff0c\u5446\u5446\u5730\u671b\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u5e7d\u840d\u5fc3\u4e2d\u5bb3\u6015\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u8ddf\u7740 \u6211\u4eec\uff0c\u8fd9\u4e0d\u5c31\u662f\u65e0\u8d56\u5417\uff1f\u201d\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u8def\u53c8\u4e0d\u662f\u4f60\u7684\uff0c\u4f60\u6709\u4f60\u8d70\uff0c\u6211\u6709\u6211\u8d70\uff0c\u8fd9\u600e\u4e48\u662f \u65e0\u8d56\u4e86\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u5fae\u611f\u4e0d\u5feb\uff0c\u907f\u5f00\u4e86\u91d1\u4e16\u9057\u7684\u773c\u5149\uff0c\u9053\uff1a\u201c\u4e16\u9057\u5144\uff0c\u8def\u4e5f\u6709\u5f88\u591a\uff0c\u54b1 \u4eec\u8fd8\u662f\u5404\u8d70\u5404\u7684\u597d\u3002\u201d\u91d1\u4e16\u9057\u5ffd\u5730\u5927\u53eb\u4e00\u58f0\uff0c\u7acb\u5373\u50cf\u733f\u7334\u4e00\u822c\u6500\u4e0a\u9644\u8fd1\u5c71\u5cf0\uff0c\u8fdc\u8fdc\u7684\u9003\u5f00\u4e86 \u51b0\u5ddd\u5929\u5973\u7684\u89c6\u7ebf\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6500\u4e0a\u5c71\u5cf0\uff0c\u5ffd\u800c\u957f\u5401\uff0c\u5ffd\u800c\u602a\u7b11\uff0c\u5ffd\u800c\u624b\u821e\u8db3\u8e48\uff0c\u5ffd\u800c\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u4ed6\u8eab\u4e0a\u90a3\u5957 \u6109\u6765\u7684\u534e\u7f8e\u7684\u8863\u88f3\u7ed9\u8346\u68d8\u523a\u7a7f\uff0c\u9762\u4e0a\u624b\u8db3\uff0c\u4e5f\u64e6\u4f24\u6d41\u8840\uff0c\u4ed6\u5374\u5168\u7136\u4e0d\u7406\uff0c\u4f46\u515a\u81ea\u5df1\u7684\u7075\u9b42\u4f3c \u8981\u7206\u7834\u8eaf\u58f3\u5411\u51a5\u51a5\u7684\u592a\u7a7a\u98de\u53bb\uff0c\u53c8\u6068\u4e0d\u5f97\u8eab\u4f53\u80fd\u970e\u65f6\u95f4\u5316\u4f5c\u5fae\u5c18\uff0c\u6d12\u904d\u5927\u5730\u5c71\u6cb3\u3002\u8fd9\u5fc3\u60c5\u662f \u7f9e\u60ed\u3002\u662f\u6124\u6012\u8fd8\u662f\u81ea\u4f24\uff1f\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\uff0c\u6599\u60f3\u4e16\u4e0a\u4ea6\u65e0\u522b\u4eba\u80fd\u591f\u7406\u89e3\u3002\u4ed6\u4e00\u628a\u6495\u88c2\u4e86\u8eab \u4e0a\u7684\u8863\u88f3\u3002\u5728\u5c71\u6da7\u65c1\u4e34\u6d41\u7167\u5f71\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u4e5f\u662f\u7236\u6bcd\u6240\u751f\u7684\u6e05\u767d\u4e4b\u8eaf\uff0c\u4e3a\u4f55\u4e16\u4eba\u5bf9\u6211\u8fd9 \u822c\u8f7b\u8d31\uff01\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95ee\uff0c\u4ed6\u4e00\u751f\u7684\u7ecf\u5386\u95ea\u7535\u822c\u7684\u5728\u8111\u6d77\u4e2d\u4e00\u5e55\u5e55\u95ea\u8fc7\u3002\u4ed6\u8bb0\u8d77\u4e86\u81ea\u5df1\u7684\u7ae5\u5e74\uff0c\u522b\u4eba\u7684 \u7ae5\u5e74\u662f\u6b22\u4e50\u65e0\u4f18\uff0c\u800c\u4ed6\u7684\u7ae5\u5e74\u5374\u662f\u8f9b\u9178\u75db\u695a\u3002\u4ed6\u6bcd\u4eb2\u65e9\u901d\uff0c\u7236\u4eb2\u662f\u4e00\u4e2a\u843d\u62d3\u6c5f\u6e56\u7684\u6559\u5b66\u5148\u751f \uff0c\u5728\u5f02\u4e61\u6559\u9986\uff0c\u5728\u4ed6\u4e94\u5c81\u90a3\u5e74\uff0c\u56e0\u4e3a\u5e74\u8001\u591a\u75c5\uff0c\u4e1c\u5bb6\u4e0d\u8c05\uff0c\u8f9e\u4e86\u4ed6\u7684\u6559\u804c\uff0c\u4ed6\u7236\u4eb2\u522b\u65e0\u5176\u4ed6 \u8c0b\u751f\u6280\u80fd\uff0c\u53c8\u5e26\u7740\u5b69\u5b50\uff0c\u8feb\u5f97\u4e5e\u8ba8\u56de\u5bb6\uff0c\u5728\u9014\u4e2d\u65f6\u5e38\u751f\u75c5\uff0c\u5e78\u5f97\u540c\u4f34\u7684\u4e5e\u4e10\u7167\u987e\uff0c\u5b69\u5b50\u624d\u5f97 \u4e0d\u6b7b\u3002\u6c42\u4e5e\u4e09\u5e74\uff0c\u8fd8\u672a\u56de\u5230\u5bb6\u4e61\uff0c\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4ed6\u7684\u7236\u4eb2\u5374\u75c5\u6b7b\u4e86\u3002\u4ed6\u4ece\u6b64\u53d8\u6210\u4e86\u5c0f\u53eb\u5316\uff0c\u6df7 \u5728\u4e5e\u4e10\u5806\u4e2d\u6cbf\u95e8\u6c42\u4e5e\uff0c\u8863\u670d\u7834\u70c2\uff0c\u8eab\u4e0a\u957f\u6ee1\u866b\u5b50\uff0c\u5c31\u50cf\u5176\u4ed6\u4e5e\u4e10\u4e00\u822c\uff0c\u6ca1\u6709\u4eba\u6765\u6599\u7406\u3002\u5982\u662f \u8005\u7684\u6c42\u4e5e\u751f\u6d3b\u53c8\u8fc7\u4e86\u4e09\u5e74\uff0c\u4e0d\u77e5\u662f\u56e0\u4e3a\u80ae\u810f\u8fd8\u662f\u75be\u75c5\uff0c\u4ed6\u6ee1\u8eab\u751f\u4e86\u4e00\u7c92\u7c92\u7684\u5c0f\u75ae\uff0c\u8138\u4e0a\u73b0\u51fa \u7ea2\u73ed\uff0c\u76ae\u80a4\u8d77\u7ed3\uff0c\u4ed6\u81ea\u5df1\u662f\u5c0f\u5b69\u5b50\u81ea\u7136\u4ec0\u4e48\u4e5f\u4e0d\u61c2\uff0c\u4f46\u89c1\u5176\u4ed6\u7684\u4e5e\u4e10\u4ece\u6b64\u907f\u5f00\u4e86\u4ed6\uff0c\u51fa\u5916\u6c42 \u4e5e\uff0c\u4eba\u4eec\u4e5f\u8fdc\u89c1\u8fdc\u8d70\uff0c\u51e0\u4e4e\u7ecf\u5e38\u6371\u997f\u3002\u6709\u4e00\u4e2a\u8001\u4e5e\u4e10\u544a\u8bc9\u4ed6\u9053\uff1a\u201c\u770b\u6765\u4f60\u662f\u60a3\u4e86\u75af\u75c5\u4e86\uff0c\u4f60 \u4e0d\u8981\u5230\u4eba\u591a\u7684\u5730\u65b9\u53bb\u6c42\u4e5e\u4e86\uff0c\u522b\u4eba\u4f1a\u628a\u4f60\u6d3b\u751f\u751f\u7684\u6253\u6b7b\u7684\uff01\u201d\u4ed6\u9a87\u6015\u5f97\u4e0d\u5f97\u4e86\uff0c\u8fd9\u624d\u77e5\u9053\u4e3a \u4ec0\u4e48\u8fde\u4e5e\u4e10\u4e5f\u8eb2\u5f00\u4ed6\u7684\u539f\u6545\uff0c\u4ed6\u81ea\u6b64\u4e0d\u6562\u6c42\u4e5e\uff0c\u53ea\u662f\u5728\u665a\u4e0a\u624d\u6084\u6084\u51fa\u6765\uff0c\u5077\u522b\u4eba\u56ed\u5730\u91cc\u7684\u74dc \u679c\u852c\u83dc\u751f\u98df\uff0c\u6709\u597d\u51e0\u6b21\u9669\u4e9b\u7ed9\u4eba\u8ffd\u4e0a\u6253\u6b7b\uff0c\u767d\u5929\u5076\u4e00\u9732\u9762\uff0c\u5c31\u6709\u4eba\u9a82\u4ed6\u662f\u201c\u5c0f\u9ebb\u75af\u201d\u3002\u80c6\u5c0f \u7684\u8fdc\u8d70\uff0c\u80c6\u5927\u7684\u5c31\u8ffd\u4ed6\uff0c\u56b7\u7740\u8981\u628a\u4ed6\u6d3b\u57cb\uff0c\u5e78\u800c\u4ed6\u8dd1\u5f97\u5feb\uff0c\u5c61\u6b21\u9669\u6b7b\u8fd8\u751f\u3002\u8fd9\u6837\u7684\u8fc7\u4e86\u51e0\u4e2a \u6708\u91ce\u4eba\u822c\u7684\u751f\u6d3b\uff0c\u5c0f\u5c0f\u7684\u5fc3\u7075\uff0c\u5305\u4e0d\u4f4f\u5927\u7684\u60b2\u75db\uff0c\u81ea\u601d\u81ea\u60f3\u8fd9\u6837\u505a\u4eba\u5b9e\u5728\u6beb\u65e0\u5473\u9053\uff0c\u6709\u4e00\u5929 \u4ed6\u8dd1\u4e0a\u9ad8\u5c71\uff0c\u809a\u5b50\u997f\uff0c\u8eab\u4e0a\u51b7\uff0c\u53eb\u4e00\u4f1a\u7239\uff0c\u53eb\u4e00\u4f1a\u5a18\uff0c\u7a81\u7136\u628a\u5fc3\u4e00\u6a2a\uff0c\u5c31\u4ece\u5c71\u5ca9\u4e0a\u8df3\u4e0b\u6765\uff0c \u4ed6\u7684\u811a\u4e0b\u662f\u4e00\u6761\u7011\u5e03\uff0c\u7011\u5e03\u51b2\u4e0b\u767e\u4e08\u5e7d\u8c37\uff0c\u8fd9\u5c0f\u5b69\u5b50\u62fc\u7740\u4e00\u6b7b\u7684\u72c2\u6fc0\u5fc3\u60c5\uff0c\u5c31\u50cf\u7011\u5e03\u4e00\u6837\u3002 \u3000\u3000\u5f80\u4e8b\u4e00\u5e55\u5e55\u95ea\u8fc7\uff0c\u91d1\u4e16\u9057\u56de\u5fc6\u81f3\u6b64\uff0c\u53ea\u89c9\u811a\u4e0b\u5c71\u5cf0\u98a4\u52a8\uff0c\u773c\u524d\u4e5f\u662f\u4e00\u6761\u7011\u5e03\uff0c\u811a\u5e95\u4e5f\u662f \u6df1\u4e0d\u53ef\u6d4b\u7684\u5e7d\u8c37\uff0c\u8fd9\u65f6\u7684\u5fc3\u60c5\u548c\u5f53\u5e74\u4e5f\u751a\u4e3a\u76f8\u4f3c\uff0c\u4ed6\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u65f6\u6709\u4eba\u6551\u6211\uff0c\u73b0\u5728\u6709\u8c01 \u6551\u6211\u5462\uff1f\u201d\u4ed6\u8111\u6d77\u4e2d\u53c8\u95ea\u8fc7\u53e6\u4e00\u5e55\u5f80\u4e8b\uff0c\u90a3\u662f\u5947\u602a\u4e07\u5206\u7684\u906d\u9047\uff0c\u6539\u53d8\u4ed6\u4e00\u751f\u547d\u8fd0\u7684\u5947\u9047\uff01 \u3000\u3000\u5c31\u5728\u90a3\u4e00\u5239\u90a3\uff0c\u5c31\u5728\u4ed6\u4ece\u5c71\u5ca9\u4e0a\u8df3\u4e0b\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u660f\u660f\u8ff7\u8ff7\u611f\u89c9\u8fd8\u672a\u5b8c\u5168\u6d88\u5931\u7684\u90a3\u4e00\u5239 \u90a3\uff0c\u4ed6\u4f3c\u4e4e\u89c9\u5f97\u6709\u4e00\u53ea\u5927\u624b\u4ece\u534a\u7a7a\u6293\u7740\u4e86\u4ed6\uff0c\u5c06\u4ed6\u62c9\u51fa\u4e86\u6b7b\u4ea1\u7684\u5e7d\u8c37\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u505a\u4e86\u4e00\u573a\u6781\u5176\u53ef\u6015\u7684\u6076\u68a6\uff0c\u8eab\u4e8e\u7a81\u7136\u95f4\u597d\u50cf\u88ab\u629b\u4e0a\u4e91\u7aef\uff0c\u53c8\u4f3c\u7a81\u7136\u95f4\u88ab\u629b\u4e0b\u5927\u6d77 \uff0c\u8033\u8fb9\u9690\u9690\u542c\u5f97\u8f70\u8f70\u7684\u6ce2\u6d9b\u4e4b\u58f0\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5ffd\u4f3c\u542c\u5f97\u6709\u4eba\u8f7b\u58f0\u7684\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u597d\u53ef \u601c\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u6709\u4eba\u8f7b\u8f7b\u7684\u629a\u62cd\u7740\u4ed6\uff0c\u5582\u4e1c\u897f\u7ed9\u4ed6\u5403\uff0c\u8fd9\u4f7f\u4ed6\u8ffd\u56de\u4e86\u51e0\u4e4e\u5931\u6389\u4e86\u7684\u8bb0\u5fc6\uff1a\u5c31\u50cf\u4ed6\u5728\u6eba\u8913 \u4e4b\u65f6\uff0c\u4ed6\u7684\u6bcd\u4eb2\u5bf9\u4ed6\u4e00\u6837\u3002\u4ed6\u7741\u5f00\u4e86\u773c\u775b\uff0c\u51e0\u4e4e\u7591\u5fc3\u81ea\u5df1\u8fd8\u5728\u68a6\u4e2d\uff0c\u53ea\u89c1\u773c\u524d\u662f\u4e00\u7247\u832b\u832b\u3001 \u6ce2\u6d9b\u8d77\u4f0f\u7684\u5927\u6d77\uff0c\u81ea\u5df1\u7f6e\u8eab\u4e8e\u4e00\u53f6\u8f7b\u821f\u4e4b\u4e2d\uff0c\u8239\u4e0a\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u76f8\u8c8c\u5947\u7279\u7684\u8001\u4eba \uff0c\u6b63\u5728\u770b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u63c9\u63c9\u773c\u775b\uff0c\u770b\u6e05\u695a\u4e86\u90a3\u4e2a\u8001\u4eba\uff0c\u53ea\u89c1\u8fd9\u8001\u4eba\u53c8\u9ad8\u53c8\u5927\uff0c\u7a7f\u7740\u4e00\u8eab\u91ce\u9ebb\u6240\u7ec7\u7684\u8863\u88f3\uff0c\u5728 \u9633\u5149\u6d77\u6d6a\u7684\u6620\u886c\u4e4b\u4e0b\uff0c\u53d1\u51fa\u4e00\u79cd\u9ec4\u8272\u7684\u5149\u6cfd\uff0c\u8fd9\u8001\u4eba\u7684\u5934\u53d1\u975e\u5e38\u957f\uff0c\u76f4\u62ab\u5230\u80a9\u5934\uff0c\u6bd4\u4ed6\u6240\u89c1 \u8fc7\u7684\u90a3\u4e9b\u5341\u51e0\u5e74\u6ca1\u6709\u7406\u8fc7\u53d1\u7684\u4e5e\u4e10\u7684\u5934\u53d1\u8fd8\u8981\u957f\uff0c\u82e5\u662f\u5e73\u65e5\u4ed6\u89c1\u5230\u8fd9\u4e2a\u8001\u4eba\uff0c\u4e00\u5b9a\u4f1a\u5413\u4e00\u5927 \u8df3\uff0c\u8fd9\u65f6\u4ed6\u5374\u611f\u5230\u4ed6\u7684\u76ee\u5149\u6709\u65e0\u6bd4\u7684\u6e29\u67d4\uff0c\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u5c31\u50cf\u6709\u6bcd\u4eb2\u4fdd\u62a4\u7684\u5b69\u5b50\u4e00\u6837\uff0c\u53cd\u800c \u5fd8\u8bb0\u4e86\u4e00\u5207\u5bb3\u6015\u3002 \u3000\u3000\u90a3\u8001\u4eba\u671b\u7740\u4ed6\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u7ec8\u4e8e\u9192\u4e86\uff0c\u809a\u5b50\u997f\u5417\uff1f\u201d\u4ed6\u6447\u6447\u5934\uff0c\u90a3\u8001\u4eba\u5374\u62ff\u51fa\u4e00 \u4e2a\u5927\u7ea2\u846b\u82a6\uff0c\u5c06\u91cc\u9762\u7684\u6db2\u4f53\u5012\u7ed9\u4ed6\u5403\uff0c\u751c\u751c\u7684\u6709\u4e00\u70b9\u9152\u5473\u3002\u4ed6\u559d\u4e86\u4e4b\u540e\u4e03\u3001\u7cbe\u795e\u597d\u4f3c\u597d\u4e86\u8bb8 \u591a\u3002\u4f3a\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u662f\u4f60\u6551\u6211\u7684\u5417\uff1f\u201d\u90a3\u8001\u4eba\u70b9\u70b9\u5934\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u6211\u5df2\u7ecf\u6ce8\u610f\u4f60\u597d\u591a \u5929\u4e86\uff0c\u4f60\u4e00\u4e2a\u4eba\u5728\u6df1\u5c71\u91ce\u5cad\u4e5f\u6709\u52c7\u6c14\u6c42\u751f\uff0c\u8fd9\u672c\u6765\u5f88\u96be\u5f97\u5440\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u5bfb\u6b7b\u5462\uff1f\u5e78\u4e8f\u6211\u4f38 \u624b\u5f97\u5feb\uff0c\u8981\u4e0d\u7136\u4f60\u65e9\u5df2\u7c89\u8eab\u788e\u9aa8\u4e86\u3002\u201d \u3000\u3000\u4ed6\u54ac\u54ac\u6307\u5934\uff0c\u5f88\u75db\uff0c\u7684\u786e\u4e0d\u662f\u505a\u68a6\uff0c\u201c\u68a6\u4e2d\u201d\u7684\u666f\u8c61\u4e5f\u5e76\u4e0d\u5168\u662f\u5e7b\u89c9\uff0c\u4ed6\u4eec\u7684\u5c0f\u821f\u6b63\u5728 \u5927\u6d77\u4e2d\u822a\u884c\uff0c\u6ce2\u6d9b\u5c06\u5c0f\u821f\u629b\u4e0a\u629b\u4e0b\uff0c\u6709\u5982\u817e\u4e91\u9a7e\u96fe\u3002 \u3000\u3000\u90a3\u8001\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u660f\u8ff7\u4e86\u4e94\u5929\u5566\u3002\u4f60\u7684\u4f53\u8d28\u5f88\u597d\uff0c\u522b\u7684\u5b69\u5b50\u53ef\u6ca1\u6709\u4f60\u6062\u590d\u5f97\u8fd9\u6837 \u5feb\u3002\u201d \u3000\u3000\u4ed6\u9aa8\u788c\u5730\u722c\u4e86\u8d77\u6765\uff0c\u671b\u7740\u90a3\u8001\u4eba\u53eb\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u8981\u6551\u6211\uff1f\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u6015\u6211\u3001\u6211\u662f\u4e2a\u9ebb \u75af\uff0c\u4eba\u89c1\u4eba\u6015\u7684\u5c0f\u9ebb\u75af\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u7b11\u4e86\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u9ebb\u75af\uff0c\u6211\u624d\u662f\u9ebb\u75af\uff01\u201d\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u671b\u90a3\u8001\u4eba\uff0c \u90a3\u8001\u4eba\u867d\u7136\u76f8\u8c8c\u5947\u7279\uff0c\u957f\u53d1\u62ab\u80a9\uff0c\u4f46\u9762\u8272\u7ea2\u6da6\uff0c\u8fde\u4e00\u70b9\u6591\u75b9\u4e5f\u6ca1\u6709\uff0c\u624b\u6307\u4fee\u957f\uff0c\u76ae\u80a4\u5149\u6d01\uff0c \u4e00\u70b9\u4e5f\u4e0d\u50cf\u4ed6\uff0c\u600e\u4e48\u662f\u4e2a\u9ebb\u75af\u5462\uff1f \u3000\u3000\u90a3\u8001\u4eba\u9053\uff1a\u201c\u6211\u4ee5\u524d\u771f\u7684\u662f\u4e2a\u5927\u9ebb\u75af\uff0c\u540e\u6765\u81ea\u5df1\u533b\u597d\u4e86\uff0c\u4f60\u60a3\u7684\u662f\u76ae\u80a4\u75c5\uff0c\u90a3\u662f\u56e0\u4e3a\u80ae \u810f\u800c\u5f15\u8d77\u7684\u76ae\u80a4\u75c5\uff0c\u7ecf\u6d77\u6c34\u6d17\u4e86\u51e0\u5929\uff0c\u592a\u9633\u6652\u4e86\u51e0\u65e5\uff0c\u65e9\u5c31\u597d\u5566\u3002\u5440\uff0c\u53ef\u60dc\u4f60\u4e0d\u662f\u4e00\u4e2a\u9ebb\u75af\uff01\u201d \u3000\u3000\u58f0\u97f3\u4f34\u7740\u53f9\u606f\uff0c\u7adf\u4f3c\u5341\u5206\u9057\u61be\u3002\u91d1\u4e16\u9057\u90a3\u65f6\u4e0d\u8fc7\u662f\u4e2a\u5341\u4e00\u5c81\u7684\u5b69\u5b50\uff0c\u89c9\u5f97\u975e\u5e38\u5947\u602a\uff1a\u8fd9 \u8001\u4eba\u7adf\u4f1a\u5acc\u81ea\u5df1\u4e0d\u662f\u9ebb\u75af\uff0c\u4ed6\u6014\u6014\u5730\u770b\u7740\u90a3\u4e2a\u8001\u4eba\uff0c\u90a3\u8001\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u56e0\u4e3a\u66fe\u7ecf\u662f\u4e2a\u9ebb \u75af\uff0c\u5f53\u5e74\u6240\u53d7\u7684\u75db\u82e6\uff0c\u5341\u500d\u4e8e\u4f60\uff0c\u540e\u6765\u9003\u81f3\u8352\u5c9b\uff0c\u53d1\u8a93\u4e0d\u89c1\u4e16\u4eba\uff0c\u76f4\u81f3\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u88ab\u4e00\u4e2a \u5973\u4fa0\u70b9\u5316\uff0c\u89c9\u5f97\u8fd9\u6837\u907f\u4e16\u9690\u5c45\uff0c\u72ec\u5584\u5176\u8eab\uff0c\u5b9e\u5728\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u6240\u4ee5\u53c8\u6539\u4e86\u5fc3\u5fd7\uff0c\u53e6\u53d1\u5b8f \u613f\uff0c\u7acb\u8a93\u8981\u6551\u5929\u4e0b\u7684\u9ebb\u75af\u60a3\u8005\uff0c\u8fd9\u5341\u5e74\u6765\u4e5f\u66fe\u6551\u4e86\u4e0d\u5c11\u4eba\uff0c\u5982\u4eca\u6211\u81ea\u77e5\u5df2\u5165\u66ae\u5e74\uff0c\u6765\u65e5\u65e0\u591a \uff0c\u56e0\u6b64\u53c8\u60f3\u5728\u60a3\u9ebb\u75af\u7684\u5e7c\u7ae5\u4e2d\u6311\u9009\u4e00\u4e2a\u5f92\u5f1f\uff0c\u53ef\u60dc\u603b\u9009\u4e0d\u7740\u4e00\u4e2a\u5408\u9002\u7684\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u798f\u81f3\u5fc3\u7075\uff0c\u7acb\u523b\u6323\u624e\u8d77\u6765\uff0c\u7eb3\u5934\u4fbf\u62dc\uff0c\u54c0\u58f0\u6c42\u9053\uff1a\u201c\u80dc\u4eba\u90fd\u5f53\u6211\u662f\u4e2a\u5c0f\u9ebb\u75af\uff0c\u6211 \u82e5\u56de\u5230\u9646\u5730\u4e4b\u4e0a\u4e5f\u662f\u4e00\u6b7b\uff0c\u5e08\u7236\uff0c\u4f60\u82e5\u4e0d\u8981\u6211\uff0c\u6211\u53ea\u6709\u8df3\u4e0b\u6d77\u53bb\uff01\u201d\u90a3\u8001\u4eba\u6c89\u601d\u534a\u664c\uff0c\u9053\uff1a \u201c\u597d\u5427\uff0c\u4f46\u4f60\u53ef\u5f97\u6709\u8fd9\u4e2a\u80c6\u91cf\u8ddf\u6211\u5230\u8352\u5c9b\u53bb\u8fc7\u4e00\u751f\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u8fde\u6b7b\u90e8\u4e0d\u6015\uff0c\u8fd8\u6015\u4ec0 \u4e48\uff1f\u201d\u4e8e\u662f\u5c31\u5728\u5c0f\u821f\u4e2d\u884c\u4e86\u5e08\u5f92\u4e4b\u793c\u3002 \u3000\u3000\u5c0f\u821f\u518d\u884c\u6570\u65e5\uff0c\u91d1\u4e16\u9057\u5728\u6d77\u6d74\u9633\u5149\u7684\u5929\u7136\u6cbb\u7597\u4e4b\u4e0b\uff0c\u6062\u590d\u5f88\u5feb\uff0c\u4e0d\u4f46\u4f53\u529b\u5145\u6c9b\uff0c\u800c\u4e14\u76ae \u5149\u8089\u6d01\uff0c\u5b8c\u5168\u53d8\u4e86\u4e2a\u6837\u5b50\uff0c\u821f\u884c\u6570\u65e5\uff0c\u5ffd\u89c1\u4e00\u4e2a\u6d77\u5c9b\uff0c\u6a2a\u5728\u524d\u9762\uff0c\u6d77\u98ce\u5439\u6765\uff0c\u5f02\u9999\u6251\u9f3b\uff0c\u9999 \u6c14\u4e4b\u4e2d\uff0c\u5374\u53c8\u5e26\u7740\u8165\u5473\u3002\u8fdc\u671b\u8fc7\u53bb\uff0c\u53ea\u89c1\u7eff\u836b\u590d\u76d6\u5168\u5c9b\uff0c\u82b1\u5f00\u6811\u4e0a\uff0c\u707f\u5982\u4e91\u971e\u3002\u6709\u6e05\u6cc9\u4ece\u5c9b \u4e2d\u6d41\u51fa\uff0c\u6c47\u6210\u5c0f\u6eaa\uff0c\u6ce8\u5165\u5927\u6d77\uff0c\u8fd1\u5c9b\u5904\u6c99\u6e7e\u73af\u62b1\uff0c\u6c34\u6ce2\u4e0d\u5174\uff0c\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u91cc\u771f\u597d\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u7b11\u9053\uff1a\u201c\u597d\u4e0e\u4e0d\u597d\uff0c\u8981\u4f60\u770b\u540e\u65b9\u77e5\u3002\u201d\u643a\u91d1\u4e16\u9057\u820d\u821f\u767b\u9646\uff0c\u4e00\u8e0f\u4e0a\u6c99\u6ee9\uff0c\u53ea\u542c\u5f97 \u6d77\u5c9b\u5185\u7684\u6811\u6797\u91cc\u6c99\u6c99\u4e4b\u58f0\u5927\u4f5c\uff0c\u65e0\u6570\u957f\u86c7\u7a9c\u4e86\u51fa\u6765\uff0c\u6709\u7684\u4e03\u5f69\u73ed\u6593\uff0c\u6709\u7684\u5934\u4e0a\u751f\u89d2\uff0c\u6602\u5934\u5410 \u820c\uff0c\u5bc6\u5bc6\u5c42\u5c42\uff0c\u51e0\u4e4e\u628a\u6c99\u6ee9\u90fd\u906e\u4f4f\u4e86\u3002\u91d1\u4e16\u9057\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u4f46\u89c1\u90a3\u8001\u4eba\u5fae\u5fae\u542b\u7b11\uff0c\u4e00\u70b9\u4e5f \u4e0d\u5bb3\u6015\u3002\u90a3\u4e9b\u86c7\u671d\u7740\u4ed6\u6602\u5934\u8d77\u4f0f\uff0c\u4f0a\u5982\u6b22\u8fce\u4e45\u522b\u7684\u597d\u53cb\uff0c\u70b9\u5934\u81f4\u656c\u4e00\u822c\uff0c\u91d1\u4e16\u9057\u60ca\u9b42\u7a0d\u5b9a\u3002 \u8001\u4eba\u56de\u5934\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u5bb3\u4e0d\u5bb3\u6015\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u4e9b\u6bd2\u86c7\uff0c\u5145\u5176\u91cf\u4e5f\u4e0d\u8fc7\u50cf\u5916\u9762\u7684\u4e16 \u5165\u4e00\u6837\uff0c\u8981\u5c06\u6211\u5f04\u6b7b\uff0c\u8fd9\u53c8\u6709\u4ec0\u4e48\u5bb3\u6015\uff1f\u201d\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5fc3\u601d\uff0c\u5012\u548c\u6211\u521d\u6765\u4e00\u6837\u3002\u201d \u3000\u3000\u81ea\u6b64\u91d1\u809a\u9057\u4fbf\u5728\u8fd9\u5c0f\u5c9b\u4e0a\u4f4f\u4e0b\u6765\uff0c\u8ddf\u968f\u90a3\u4e2a\u8001\u4eba\u5b66\u4e60\u6b66\u827a\uff0c\u91d1\u4e16\u9057\u672c\u6765\u53ea\u77e5\u6709\u59d3\uff0c\u672a\u66fe \u8d77\u540d\uff0c\u201c\u4e16\u9057\u201d\u4e8c\u5b57\u4e43\u662f\u90a3\u8001\u4eba\u5230\u4e86\u6d77\u5c9b\u4e4b\u540e\u624d\u66ff\u4ed6\u53d6\u7684\u3002 \u3000\u3000\u5230\u4e86\u6d77\u5c9b\u4e4b\u540e\uff0c\u91d1\u4e16\u9057\u624d\u77e5\u9053\u90a3\u8001\u4eba\u540d\u53eb\u6bd2\u9f99\u5c0a\u8005\uff0c\u8fd9\u4e2a\u6d77\u5c9b\u540d\u53eb\u201c\u86c7\u5c9b\u201d\uff0c\u5728\u9ec4\u6d77\u4e0e \u6e24\u6d77\u4ea4\u63a5\u4e4b\u5904\uff0c\u4e98\u53e4\u4ee5\u6765\uff0c\u4eba\u8ff9\u4e0d\u5230\u3002\u6bd2\u9f99\u5c0a\u8005\u5c11\u5e74\u65f6\u5019\uff0c\u662f\u4e2a\u6b66\u5e08\uff0c\u81ea\u4ece\u60a3\u4e86\u9ebb\u75af\uff0c\u88ab\u4eba \u9a71\u9010\uff0c\u65e0\u610f\u4e4b\u4e2d\uff0c\u98d8\u6d41\u5230\u8fd9\u4e2a\u6d77\u5c9b\uff0c\u4e0e\u6bd2\u86c7\u4e3a\u53cb\uff0c\u53d6\u6bd2\u86c7\u7684\u53e3\u6d8e\uff0c\u6cbb\u6108\u5382\u9ebb\u75af\uff0c\u4ed6\u4e00\u8eab\u7edd\u4e16 \u60ca\u4eba\u7684\u6b66\u529f\uff0c\u5c31\u662f\u5728\u86c7\u5c9b\u4e4b\u4e2d\u7ec3\u51fa\u6765\u7684\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u643a\u91d1\u4e16\u9057\u5230\u4e86\u86c7\u5c9b\u4e4b\u540e\uff0c\u5c31\u6089\u5fc3\u4f20\u4ed6\u6b66\u827a\uff0c\u91d1\u4e16\u9057\u806a\u660e\u4e4b\u6781\uff0c\u6bcf\u79cd\u6b66\u529f\uff0c\u4ece\u6765 \u4e0d\u8981\u5e08\u7236\u6307\u70b9\u4e09\u904d\uff0c\u6700\u591a\u4e24\u9057\uff0c\u5c31\u80fd\u8bb0\u5f97\u3002\u6bd2\u9f99\u5c0a\u8005\u6bcf\u5e74\u603b\u8981\u51fa\u5916\u4e00\u4e24\u6b21\uff0c\u6bcf\u6b21\u4e00\u4e24\u4e2a\u6708\u4e0d \u7b49\uff0c\u5e08\u7236\u51fa\u53bb\u4e4b\u540e\uff0c\u4ed6\u5c31\u72ec\u81ea\u5728\u86c7\u5c9b\u4e4b\u4e2d\u7ec3\u529f\u3002\u5e08\u7236\u6bcf\u6b21\u56de\u6765\uff0c\u8bf4\u7684\u603b\u662f\u6551\u4e86\u591a\u5c11\u4e2a\u9ebb\u75af\u60a3 \u8005\u4e4b\u4e8b\u3002\u5e08\u7236\u5e38\u5e38\u548c\u4ed6\u8bf4\u8d77\u9ebb\u75af\u60a3\u8005\u7684\u82e6\u695a\uff0c\u4ee5\u53ca\u4ed6\u5c11\u5e74\u4e4b\u65f6\uff0c\u600e\u6837\u9669\u9669\u88ab\u4eba\u70e7\u6b7b\u7b49\u7b49\u60c5\u4e8b \u3002\u91d1\u4e16\u9057\u81ea\u5df1\u66fe\u8eab\u53d7\u5176\u82e6\uff0c\u5bf9\u5916\u9762\u4eba\u4e16\uff0c\u618e\u6068\u4e4b\u6781\uff0c\u53ea\u613f\u4e00\u751f\u80fd\u5728\u8fd9\u6d77\u5c9b\u4e4b\u4e0a\uff0c\u518d\u4e0d\u91cd\u8e0f\u4eba \u4e16\u3002 \u3000\u3000\u5982\u662f\u8005\u5e74\u590d\u4e00\u5e74\uff0c\u970e\u773c\u4e4b\u95f4\u5df2\u8fc7\u4e86\u4e03\u5e74\uff0c\u91d1\u4e16\u9057\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u7ec3\u6210\u4e86\u7b2c\u4e00\u6d41\u7684 \u6b66\u529f\uff0c\u5ffd\u7136\u6765\u5230\u4e86\u8fd9\u4e00\u5929\uff0c\u53c8\u53d1\u751f\u4e86\u4e00\u4e2a\u7a81\u7136\u7684\u53d8\u6545\u2026\u2026 \u3000\u3000\u5f80\u4e8b\u4e00\u5e55\u5e55\u7684\u95ea\u8fc7\uff0c\u91d1\u4e16\u9057\u8111\u6d77\u4e2d\u6cdb\u8d77\u90a3\u4e00\u5e55\u666f\u8c61\uff1a\u4e00\u65e5\u9ec4\u660f\uff0c\u7ea2\u65e5\u897f\u843d\uff0c\u706b\u7403\u4e00\u822c\u7684 \u592a\u9633\u5c31\u50cf\u6c89\u5165\u5927\u6d77\u4e4b\u4e2d\uff0c\u4f59\u971e\u6563\u7ee9\uff0c\u6d77\u4e0a\u4e00\u7247\u91d1\u78a7\u3002\u91d1\u4e16\u9057\u5ffd\u88ab\u5e08\u7236\u53eb\u5230\u8ddf\u524d\uff0c\u53ea\u89c1\u5e08\u7236\u9762 \u5bb9\u6709\u5f02\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u5c3d\u5f97\u6211\u7684\u6240\u4f20\uff0c\u5982\u679c\u91cd\u56de\u9646\u5730\uff0c\u884c\u8d70\u6c5f\u6e56\uff0c\u6599\u60f3\u5f53\u4eca\u4e4b\u4e16\uff0c\u5df2 \u65e0\u51e0\u4eba\u80fd\u4e0e\u4f60\u4e3a\u654c\u4e86\u3002\u201d\u91d1\u4e16\u9057\u6025\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5916\u9762\u4eba\u5fc3\u53f5\u6d4b\uff0c\u6211\u8fd8\u662f\u7559\u5728\u8fd9\u91cc\u7684\u597d\u3002\u201d\u6bd2 \u9f99\u5c0a\u8005\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5916\u9762\u679c\u7136\u662f\u4eba\u5fc3\u53f5\u6d4b\uff0c\u8fde\u6b66\u6797\u4e2d\u4eba\uff0c\u4ea6\u591a\u534a\u5982 \u6b64\u3002\u4f46\u5176\u4e2d\u4ea6\u4e0d\u662f\u6ca1\u6709\u597d\u4eba\uff0c\u50cf\u6c13\u5c71\u7684\u5415\u56db\u5a18\u548c\u6c5f\u5357\u7684\u7518\u51e4\u6c60\u5c31\u662f\u597d\u4eba\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u4ece\u6765\u6ca1\u542c\u8fc7\u4ed6\u5e08\u7236\u63d0\u8fc7\u4e2d\u539f\u7684\u6b66\u6797\u5b97\u6d3e\uff0c\u751a\u662f\u597d\u5947\uff0c\u6b63\u60f3\u95f4\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u662f\u4ec0 \u4e48\u4eba\uff1f\u53ea\u542c\u5f97\u5e08\u7236\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u5929\u5c71\u6d3e\u7684\uff0c\u5440\uff0c\u4f60\u82e5\u4e0d\u51fa\u53bb\u5bfb\u8bbf\u5230\u5929\u5c71\u6d3e\u7684\u95e8\u4e0b\uff0c\u5c31\u6709\u6740\u8eab \u4e4b\u5fe7\u3002\u201d\u91d1\u4e16\u9057\u83ab\u540d\u5176\u5999\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u7f18\u6545\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u6211\u6240\u521b\u7684\u8fd9\u5bb6\u6b66\u529f\uff0c \u81ea\u4fe1\u4e0d\u5728\u5929\u5c71\u8bf8\u4fa0\u4e4b\u4e0b\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u2019\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u76b1\u4e86\u76b1\u7709 \uff0c\u9053\uff1a\u201c\u518d\u8fc7\u4e9b\u65f6\uff0c\u4f60\u5c31\u77e5\u9053\u4e86\uff0c\u5440\uff0c\u4e0d\u77e5\u5929\u5c71\u95e8\u4e0b\uff0c\u5982\u4eca\u8fd8\u6709\u4f55\u4eba\uff1f\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u5e78\u707e\u4e50\u7978 \uff0c\u8ba9\u54b1\u4eec\u8fd9\u6d3e\u7684\u6b66\u529f\u7edd\u706d\uff0c\u552f\u4ed6\u72ec\u5c0a\uff1f\u201d\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u73b0\u4eca\u5929\u5c71\u6d3e\u7684\u5f1f\u5b50\u662f\u6ca1\u6709\u5fc3 \u809d\u7684\u574f\u4eba\u5417\uff1f\u5f1f\u5b50\u613f\u968f\u5e08\u7236\u51fa\u53bb\uff0c\u627e\u4ed6\u4eec\u6bd4\u6b66\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u53c8\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u7b49\u4e0b\u6211\u90fd\u548c \u4f60\u8bf4\u4e2a\u660e\u767d\u3002\u4f60\u66ff\u6211\u5c06\u86c7\u513f\u53eb\u6765\u3002\u201d\u91d1\u4e16\u9057\u5728\u86c7\u5c9b\u4e03\u5e74\uff0c\u5df2\u5b66\u4f1a\u4e86\u9a71\u86c7\u4e4b\u672f\uff0c\u542c\u4e86\u5e08\u7236\u5429\u5490 \uff0c\u4fbf\u60f3\u51fa\u53bb\u547c\u5524\uff0c\u5ffd\u89c1\u6bd2\u9f99\u5c0a\u8005\u5934\u9876\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u5ffd\u9053\uff1a\u201c\u4e16\u9057\uff0c\u4f60\u8981\u8bb0\u7740\u4f60\u5c11\u65f6 \u6240\u53d7\u7684\u75db\u82e6\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5f1f\u5b50\u8bb0\u5f97\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u6325\u624b\u9053\uff1a\u201c\u5feb\u53bb\u5feb\u6765\uff0c\u6211\u8fd8\u6709\u8bb8\u591a\u8bdd\u8981 \u548c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u5728\u6d77\u5c9b\u5404\u5904\u8d70\u4e86\u4e00\u904d\uff0c\u5c06\u7fa4\u86c7\u90fd\u5524\u4e86\u51fa\u6765\uff0c\u90a3\u4e9b\u86c7\u5982\u6709\u7075\u6027\uff0c\u4e00\u961f\u961f\u7684\u6392\u5728\u6797\u5916 \uff0c\u6bcf\u4e00\u961f\u6709\u4e00\u6761\u5927\u86c7\u968f\u91d1\u4e16\u9057\u6e38\u8fdb\u6797\u4e2d\uff0c\u4f3c\u662f\u8981\u5411\u6bd2\u9f99\u5c0a\u8005\u8bf7\u5b89\u95ee\u5019\u3002\u91d1\u4e16\u9057\u8d70\u8fdb\u6797\u4e2d\uff0c\u53eb \u9053\uff1a\u201c\u5e05\u7236\uff0c\u86c7\u513f\u90fd\u5524\u6765\u4e86\u3002\u201d\u62ac\u5934\u4e00\u770b\uff0c\u731b\u5730\u91cc\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u5e08\u7236\u6c57\u51fa\u5982\u6d46\uff0c\u4e24\u76ee\u5706\u7741\uff0c\u773c\u73e0\u4e00\u52a8\u4e0d\u52a8\u3002\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6bd2 \u9f99\u5c0a\u8005\u4e00\u58f0\u4e0d\u51fa\uff0c\u91d1\u4e16\u9057\u4e0a\u524d\u4e00\u6478\uff0c\u53ea\u89c1\u4ed6\u8eab\u4f53\u5df2\u7ecf\u50f5\u786c\uff0c\u7ade\u662f\u6b7b\u4e86\uff01\u4ed6\u7684\u8eab\u8fb9\u6446\u7740\u4ed6\u65e5\u5e38 \u6240\u7528\u7684\u94c1\u62d0\uff0c\u94c1\u62d0\u4e0b\u9762\u6709\u4e00\u672c\u4e66\uff0c\u5c01\u9762\u5199\u7740\uff1a\u300a\u6bd2\u9f99\u79d8\u53d1\u300b\u56db\u5b57\uff0c\u5c01\u9762\u6b6a\u6b6a\u659c\u659c\u5730y\u53f3\u51e0\u4e2a \u5b57\uff1a\u201c\u6b66\u529f\u5927\u6210\u540e\uff0c\u8981\u627e\u5929\u5c71\u6d3e\uff0c\u5448\u4e66\u4e0e\u4ed6\u770b\uff0c\u6c42\u2026\u2026\u201d\u5199\u5230\u201c\u6c42\u201d\u5b57\uff0c\u7b14\u5212\u5df2\u662f\u6f66\u8349\u6a21\u7cca \u4e4b\u6781\uff0c\u51e0\u4e4e\u8fa8\u4e0d\u51fa\u6765\uff0c\u60f3\u662f\u6c14\u529b\u7528\u7aed\uff0c\u672a\u5f85\u5199\u5b8c\uff0c\u4fbf\u6b7b\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u653e\u58f0\u75db\u54ed\uff0c\u7fa4\u86c7\u4fef\u9996\uff0c\u4ea6\u4f3c\u81f4\u54c0\u3002\u91d1\u4e16\u9057\u8fd9\u624d\u77e5\u9053\u5e08\u7236\u539f\u6765\u662f\u60f3\u5524\u7fa4\u86c7\u524d\u6765\u8bdd\u522b \uff0c\u4ed6\u8bf4\u6709\u8bb8\u591a\u8bdd\u8981\u548c\u81ea\u5df1\u8bf4\uff0c\u53ea\u6068\u672a\u53ca\u542c\u4ed6\u6700\u540e\u7684\u8bdd\uff0c\u4e0d\u77e5\u4ed6\u8981\u8bf4\u7684\u662f\u4ec0\u4e48\u3002\u91d1\u4e16\u9057\u5c06\u5e08\u7236 \u57cb\u846c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u8bb0\u5f97\u4f60\u7684\u8bdd\uff0c\u6211\u8bb0\u5f97\u4f60\u6211\u90fd\u540c\u53d7\u8fc7\u7684\u75db\u82e6\uff0c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c \u6211\u8981\u618e\u6068\u4e16\u4eba\uff01\u2026\u2026 \u3000\u3000\u91d1\u4e16\u9057\u54ea\u77e5\u4ed6\u5c06\u5e08\u7236\u7684\u610f\u601d\u5b8c\u5168\u7406\u89e3\u9519\u4e86\uff01\u6bd2\u9f99\u5c0a\u8005\u5728\u9003\u81f3\u6d77\u5c9b\u4e4b\u540e\uff0c\u4e0d\u9519\uff0c\u4ed6\u662f\u4e00\u76f4 \u618e\u6068\u4e16\u4eba\uff0c\u4f46\u5728\u5341\u516d\u5e74\u524d\uff0c\u5415\u56db\u5a18\u3001\u7518\u51e4\u6c60\u3001\u51af\u745b\u3001\u5510\u6653\u6f9c\u8bf8\u4eba\u6765\u5230\u86c7\u5c9b\uff0c\u5415\u56db\u5a18\u3001\u51af\u745b\u8054 \u5251\u6740\u8d25\u6bd2\u9f99\u5c0a\u8005\uff0c\u53c8\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u5c06\u4e16\u4eba\u6709\u597d\u4e5f\u6709\u574f\uff0c\u4e0e\u7acb\u8eab\u5904\u4e16\u7684\u5927\u662f\u5927\u975e\u7b49\u7b49\u9053\u7406\uff0c \u53cd\u590d\u548c\u4ed6\u8c08\u8bba\uff0c\u7ec8\u4e8e\u4ee4\u6bd2\u9f99\u5c0a\u8005\u6062\u590d\u4e86\u4eba\u6027\uff0c\u5316\u6068\u4e3a\u7231\uff0c\u56e0\u6b64\u4ed6\u624d\u4ee5\u6709\u9650\u7684\u4f59\u751f\uff0c\u5c3d\u529b\u53bb\u6551 \u6cbb\u4e16\u4e0a\u7684\u9ebb\u75af\u60a3\u8005\u3002\u4ed6\u8981\u91d1\u4e16\u9057j\u5df1\u4f4f\u66fe\u53d7\u8fc7\u7684\u75db\u82e6\uff0c\u65e0\u975e\u662f\u60f3\u91d1\u4e16\u9057\u7ee7\u627f\u4ed6\u7684\u9057\u5fd7\uff0c\u5c06\u6765 \u4e5f\u51fa\u53bb\u6551\u6cbb\u9ebb\u75af\u60a3\u8005\uff0c\u63a8\u800c\u5e7f\u4e4b\uff0c\u6551\u4e00\u5207\u53d7\u82e6\u53d7\u96be\u7684\u4eba\uff0c\u53ef\u60dc\u6700\u540e\u7684\u9057\u8a00\u6765\u4e0d\u53ca\u8be6\u7ec6\u8a00\u8bf4\uff0c \u7adf\u4ee4\u91d1\u4e16\u9057\u65ad\u7ae0\u53d6\u4e49\uff0c\u5b8c\u5168\u8bef\u4f1a\u4e86\u5e08\u7236\u7684\u610f\u601d\u3002 \u3000\u3000\u91d1\u4e16\u9057\u846c\u4e86\u5e08\u7236\u4e4b\u540e\uff0c\u5c06\u5e08\u7236\u7684\u9057\u4e66\u300a\u6bd2\u9f99\u79d8\u7c4d\u300b\u63ed\u5f00\u6765\u770b\uff0c\u5176\u4e2d\u7684\u6b66\u529f\uff0c\u867d\u7136\u5341\u4e4b\u4e03 \u516b\u81ea\u5df1\u90fd\u66fe\u7ecf\u7ec3\u8fc7\uff0c\u4f46\u8bc0\u7a8d\u7cbe\u5fae\u4e4b\u5904\u53ef\u4e0d\u80fd\u5168\u90e8\u61c2\u5f97\uff0c\u6709\u4e86\u6b64\u4e66\u7684\u89e3\u8bf4\uff0c\u8fd9\u624d\u8c41\u7136\u5999\u609f\uff0c\u5c06 \u6240\u7ec3\u8fc7\u7684\u6b66\u529f\u8d2f\u901a\u3002\u4e66\u4e2d\u8fd8\u6709\u5236\u70bc\u5404\u79cd\u5267\u6bd2\u6697\u5668\u7684\u6cd5\u5b50\uff0c\u4ee5\u53ca\u5404\u79cd\u6253\u6697\u5668\u7684\u5947\u5999\u624b\u6cd5\uff0c\u91d1\u4e16 \u9057\u90fd\u4e00\u4e00\u4f9d\u4e66\u7ec3\u4e60\uff0c\u53c8\u7ec3\u4e86\u4e09\u5e74\uff0c\u8bd5\u638c\u529b\u5219\u53d1\u638c\u53ef\u4ee5\u6467\u6811\uff0c\u8bd5\u6697\u5668\u5219\u7528\u4e00\u679a\u6bd2\u9488\u5c31\u53ef\u5c04\u6740\u6d77 \u5e95\u9ca8\u9c7c\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u5e08\u7236\u5728\u86c7\u5c9b\u4e00\u751f\uff0c\u521b\u51fa\u4e86\u8fd9\u79cd\u5389\u5bb3\u7684\u6b66\u529f\uff0c\u5e94\u8be5\u53eb\u5916\u9762\u7684\u4eba\u77e5\u9053\uff0c \u8fd9\u624d\u4e0d\u81f3\u57cb\u6ca1\u4e86\u4ed6\u4e00\u751f\u7684\u5fc3\u8840\uff01\u201d\u53c8\u60f3\u9053\u3002\u201c\u542c\u5e08\u7236\u65e5\u5e38\u8c08\u8bba\uff0e\u4e2d\u539f\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u4e5f\u6ca1\u6709\u4ec0 \u4e48\u4e0d\u5f97\u4e86\u4e4b\u5904\uff0c\u90a3\u4e9b\u4eba\u4ee5\u524d\u5c45\u7136\u6562\u6b67\u89c6\u6211\u7684\u5e08\u7236\uff0c\u6211\u4e0d\u5982\u51fa\u53bb\u4e00\u73a9\uff0c\u5c06\u4ed6\u4eec\u6253\u4e2a\u843d\u82b1\u6d41\u6c34\uff0c \u5f85\u5230\u6253\u8d25\u4e86\u5929\u4e0b\u6240\u6709\u7684\u6210\u540d\u4eba\u7269\u4e4b\u540e\uff0c\u6211\u624d\u8bf4\u51fa\u6211\u7684\u5e08\u627f\u6765\u5386\uff0c\u597d\u53eb\u5e08\u7236\u540d\u5782\u4e0d\u673d\uff01\u201d\u5982\u6b64 \u4e00\u60f3\uff0c\u91d1\u4e16\u9057\u4fbf\u6709\u4e86\u79bb\u5f00\u86c7\u5c9b\u4e4b\u610f\u3002\u53ea\u662f\u8fd9\u4e09\u5e74\u6765\u5374\u6709\u4e24\u4e2a\u6781\u5927\u7684\u7591\u95ee\uff0c\u76d8\u585e\u5fc3\u4e2d\uff0c\u65e0\u6cd5\u601d \u89e3\u3002\u90a3\u4fbf\u662f\u5e08\u7236\u4e34\u6b7b\u4e4b\u524d\uff0c\u63d0\u53ca\u5929\u5c71\u6d3e\u7684\u90a3\u4e9b\u8bf4\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u4ee5\u53ca\u5e08\u7236\u4f55\u4ee5\u4f1a\u7a81\u7136\u95f4\u6b7b\u53bb\uff1f \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5ffd\u7136\u66b4\u6b7b\u5927\u79bb\u5947\uff0c\u4e24\u4e2a\u7591\u96be\u8c01\u53ef\u89e3\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u6124\u4e16\u5947\u884c\u3000\u8d62\u6765\u75af\u4e10\u53f7\u3000\u72c2\u6b4c\u9a87\u4fd7\u3000\u60f9\u5f97\u7f8e\u4eba\u601c \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u4e09\u56de \u6124\u4e16\u5947\u884c\u3000\u8d62\u6765\u75af\u4e10\u53f7 \u72c2\u6b4c\u9a87\u4fd7\u3000\u60f9\u5f97\u7f8e\u4eba\u601c \u3000\u3000\u91d1\u4e16\u9057\u4e09\u5e74\u6765\u82e6\u82e6\u601d\u7d22\uff0c\u8fd9\u4e24\u4e2a\u7591\u56e2\u7ec8\u662f\u65e0\u6cd5\u6253\u7834\uff0c\u4ed6\u5e08\u7236\u4e3a\u4ec0\u4e48\u8981\u4ed6\u5728\u6b66\u529f\u5927\u6210\u4e4b\u540e \u4e0a\u627e\u5929\u5c71\u6d3e\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u627e\u5929\u5c71\u6d3e\u5c06\u6765\u4fbf\u6709\u6027\u547d\u4e4b\u5fe7\uff1f\u7ec6\u7ec6\u5480\u56bc\u5e08\u7236\u51e0\u53e5\u8bdd\uff0c\u53c8\u4f3c\u4e0d\u662f\u548c\u5929 \u5c71\u6d3e\u6709\u4ec7\u3002\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u8981\u628a\u8fd9\u672c\u300a\u6bd2\u9f99\u79d8\u7c4d\u300b\u201c\u5448\u201d\u4e0e\u5929\u5c71\u7684\u638c\u95e8\u770b\uff0c\u90a3\u66f4\u662f\u83ab\u540d\u5176\u5999\u3002\u91d1 \u4e16\u9057\u867d\u7136\u4ece\u672a\u6d89\u8db3\u6b66\u6797\uff0c\u4f46\u4ea6\u77e5\u9053\u6bcf\u4e00\u6d3e\u90fd\u628a\u81ea\u5df1\u7684\u72ec\u95e8\u6b66\u529f\u89c6\u4e3a\u4e0d\u4f20\u4e4b\u79d8\uff0c\u4e07\u4e07\u4e0d\u80fd\u6cc4\u6f0f \u7ed9\u5916\u4eba\u77e5\u9053\uff0c\u5e08\u7236\u4e34\u7ec8\u65f6\u5728\u6c99\u6ee9\u5199\u7684\u8bdd\uff0c\u4f1a\u4e0d\u4f1a\u662f\u795e\u667a\u660f\u8ff7\u7684\u201c\u4e71\u547d\u201d\u3001\u6700\u540e\u90a3\u4e2a\u201c\u6c42\u201d\u5b57 \u66f4\u4ee4\u91d1\u4e16\u9057\u4e0d\u670d\u6c14\uff0c\u8fd9\u53e5\u8bdd\u6bd2\u9f99\u5c0a\u8005\u6ca1\u6709\u5199\u5b8c\uff0c\u91d1\u4ed8\u9057\u4e0d\u77e5\u9053\u5e08\u7236\u8981\u4ed6\u201c\u6c42\u201d\u5929\u5c71\u4e00\u6d3e\u4ec0 \u4e48\uff0c\u4ed6\u81ea\u5df1\u601d\u7d22\u672c\u6d3e\u6b66\u529f\u5982\u6b64\u795e\u5999\uff0c\u53c8\u6709\u751a\u4e48\u9700\u8981\u6c42\u4eba\u7684\uff1f \u3000\u3000\u81f3\u4e8e\u5e08\u7236\u4e4b\u7a81\u7136\u6b7b\u53bb\uff0c\u90a3\u5c31\u66f4\u662f\u5947\u602a\u4e86\u3002\u4ee5\u5e08\u7236\u90a3\u6837\u6df1\u4e0d\u53ef\u6d4b\u7684\u6b66\u529f\uff0c\u5373\u7b97\u4eab\u5c3d\u5927\u5e74\uff0c \u5bff\u6570\u5e94\u5c3d\uff0c\u4f46\u4ed6\u660e\u660e\u8fd8\u6709\u8bb8\u591a\u8bdd\u8981\u548c\u81ea\u5df1\u8bf4\uff0c\u4ee5\u4ed6\u7684\u6b66\u529f\u3002\u600e\u4e48\u4e0d\u80fd\u591a\u62d6\u5ef6\u4e00\u4e00\u523b\uff0c\u4e3a\u4ec0\u4e48 \u7b49\u4e0d\u5230\u81ea\u5df1\u56de\u6765\u5c31\u6b7b\u53bb\u4e86\uff1f \u3000\u3000\u91d1\u4e16\u9057\u6700\u521d\u968f\u5e08\u7236\u5230\u86c7\u5c9b\u4e4b\u65f6\uff0e\u672c\u6765\u60f3\u5728\u8fd9\u6d77\u5c9b\u5ea6\u8fc7\u4e00\u751f\uff0c\u5e08\u7236\u6b7b\u540e\uff0c\u4ed6\u4e00\u4eba\u4e0e\u6bd2\u86c7\u4e3a \u4f34\uff0e\u6e10\u6e10\u89c9\u5f97\u5bc2\u5bde\u65e0\u804a\uff0c\u52a0\u4ee5\u4ed6\u73b0\u5728\u5df2\u957f\u5927\u6210\u4eba\uff0c\u4ece\u521d\u6765\u65f6\u5341\u4e00\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u500f\u5ffd\u8fc7\u4e86\u5341 \u5e74\uff0c\u5df1\u53d8\u6210\u4e8c\u5341\u4e00\u5c81\u7684\u5c11\u5e74\u4e86\uff0c\u5c11\u5e74\u7684\u5fc3\u60c5\u548c\u5b69\u5b50\u7684\u5fc3\u60c5\u81ea\u7136\u6709\u5f88\u5927\u4e0d\u540c\uff0c\u5c0f\u5b69\u5b50\u53ef\u4ee5\u81ea\u5f97 \u5176\u4e50\u9676\u9189\u4e8e\u81ea\u5df1\u7684\u5c0f\u5927\u5730\uff0c\u5728\u8fd9\u6d77\u5c9b\u4e0a\u73a9\u86c7\u3001\u6349\u9e1f\u3001\u620f\u6c34\u3001\u5806\u6c99\uff0c\u5df2\u8db3\u591f\u4ed6\u73a9\u4e86\uff0c\u5c11\u5e74\u4eba\u5374 \u61a7\u61ac\u5916\u9762\u7684\u4e16\u754c\uff0c\u61a7\u61ac\u5916\u9762\u66f4\u5e7f\u9614\u7684\u5929\u5730\uff0c\u867d\u7136\u5916\u9762\u7684\u4e16\u754c\u5bf9\u4ed6\u662f\u5982\u6b64\u964c\u751f\uff0c\u800c\u4e14\u4ee4\u4ed6\u618e\u6068\u3002 \u3000\u3000\u4ed6\u6000\u7740\u8fd9\u4e24\u4e2a\u7591\u95ee\uff0c\u5728\u5e08\u7236\u6b7b\u540e\uff0c\u53c8\u5728\u86c7\u5c9b\u72ec\u81ea\u8fc7\u4e86\u4e09\u5e74\uff0c\u7ec8\u4e8e\u6309\u637a\u4e0d\u6ce8\uff0c\u4e8e\u662f\u53d6\u4e86\u5e08 \u7236\u7559\u7ed9\u4ed6\u7684\u90a3\u6839\u94c1\u62d0\uff0c\u5e26\u4e86\u5e08\u7236\u7684\u9057\u4e66\uff0c\u5c31\u5750\u4e0a\u4ed6\u6765\u65f6\u7684\u90a3\u8258\u5c0f\u8239\uff0c\u5212\u8fc7\u6e24\u6d77\uff0c\u53c8\u56de\u5230\u4e86\u5927 \u9646\u3002 \u3000\u3000\u5341\u5e74\u7684\u65f6\u95f4\u4e0d\u7b97\u77ed\uff0c\u4e5f\u4e0d\u7b97\u5927\u957f\uff0c\u4f46\u4ed6\u5df2\u5b8c\u5168\u53d8\u4e86\u6837\u4e86\uff0c\u4ece\u4e00\u4e2a\u88ab\u4eba\u6b3a\u8d1f\u7684\u5c0f\u9ebb\u75af\u53d8\u6210 \u4e00\u4e2a\u6000\u6709\u60ca\u4eba\u6b66\u529f\u7684\u82f1\u4fca\u5c11\u5e74\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4eba\u5374\u6000\u7740\u4e00\u80a1\u72c2\u6fc0\u7684\u5fc3\u60c5\uff0c\u5411\u8fd9\u4e2a\u66fe\u6b3a\u8d1f\u8fc7\u4ed6\u7684\u4e16\u754c\u6311\u8845\uff1a\u4ed6\u4ee5\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u968f \u65f6\u6613\u5bb9\u53d8\u8c8c\uff0c\u6545\u610f\u628a\u81ea\u5df1\u53d8\u6210\u4e00\u4e2a\u5927\u9ebb\u75af\uff0c\u51c6\u6562\u6b3a\u4fae\u4ed6\uff0c\u4ed6\u5c31\u4ee5\u773c\u8fd8\u773c\uff0c\u4ee5\u7259\u8fd8\u7259\uff0c\u5c06\u522b\u4eba \u6349\u5f04\u5f97\u54ed\u7b11\u4e0d\u5f97\u3002\u4ed6\u5230\u5904\u53bb\u627e\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\u6bd4\u8bd5\uff0c\u4e0d\u8fc7\u6570\u5e74\u201c\u6bd2\u624b\u75af\u4e10\u201d\u4e4b\u540d\u5c31\u4f20\u904d\u6c5f \u6e56\uff0c\u6ca1\u6709\u4e00\u4eba\u662f\u4ed6\u5bf9\u624b\u3002\u8d8a\u662f\u4eab\u6709\u76db\u540d\u7684\u524d\u8f88\uff0c\u4ed6\u5c31\u8d8a\u53d1\u8981\u620f\u5f04\u4ed6\uff0c\u5f04\u5f97\u4e2d\u539f\u7684\u6b66\u6797\u4eba\u7269\uff0c \u95fb\u98ce\u8fdc\u907f\u3002 \u3000\u3000\u4ed6\u4e5f\u66fe\u60f3\u53bb\u627e\u7518\u51e4\u6c60\u4e0e\u5415\u56db\u5a18\uff0c\u4f46\u540e\u6765\u542c\u5f97\u7518\u51e4\u6c60\u5df2\u6b7b\uff0c\u5415\u56db\u5a18\u5df2\u4e0d\u77e5\u8e2a\u8ff9\uff0c\u4ed6\u624d\u653e\u5f03 \u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002\u4ed6\u8bb0\u7740\u5e08\u7236\u7684\u8bdd\uff0c\u4ee5\u4e3a\u6b66\u6797\u4e2d\u53ea\u6709\u8fd9\u4e24\u4e2a\u662f\u597d\u4eba\uff0c\u5176\u4ed6\u7684\u4eba\u4ed6\u5c31\u6beb\u65e0\u987e\u5fcc\u7684\u6b22 \u559c\u600e\u6837\u6349\u5f04\u4fbf\u600e\u6837\u6349\u5f04\u3002 \u3000\u3000\u51e0\u5e74\u6765\u4ed6\u6253\u8d25\u4e86\u65e0\u6570\u6210\u540d\u9ad8\u624b\uff0c\u6bcf\u4e00\u6b21\u6253\u8d25\u654c\u624b\uff0c\u4ed6\u5fc3\u4e2d\u603b\u662f\u5341\u5206\u5f97\u610f\uff0c\u4f46\u968f\u5373\u53c8\u611f\u5230 \u5bc2\u5bde\u4e0e\u60b2\u4f24\uff0c\u8d8a\u662f\u80dc\u5229\uff0c\u8d8a\u662f\u60b2\u4f24\uff0c\u800c\u5df2\u8fd9\u6837\u7684\u60c5\u7eea\uff0c\u968f\u7740\u6bcf\u4e00\u6b21\u7684\u80dc\u5229\u800c\u52a0\u6df1\uff0c\u6bcf\u6b21\u7684\u80dc \u5229\u7684\u5f97\u610f\uff0c\u90fd\u53ea\u4e0d\u8fc7\u662f\u5929\u8fb9\u4e00\u77ac\u5373\u901d\u7684\u5f69\u8679\uff0c\u800c\u5bc2\u5bde\u4e0e\u60b2\u4f24\u5374\u662f\u6c38\u8fdc\u7b3c\u7f69\u5fc3\u5934\u7684\u6d53\u96fe\uff01 \u3000\u3000\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u4ed6\u5632\u5f04\u4e86\u8fd9\u4e2a\u4e16\u754c\uff0c\u800c\u8fd9\u4e16\u754c\u4e5f\u4fbf\u9057\u5f03\u4e86\u4ed6\uff01\u6ca1\u6709\u4e00\u4e2a\u4eba\u548c\u4ed6\u4ea4\u670b\u53cb\u3002\u751a \u800c\u6ca1\u6709\u4e00\u4e2a\u4eba\u628a\u4ed6\u5f53\u4f5c\u6b63\u5e38\u7684\u4eba\u4e00\u6837\u3002\u63a5\u5f85\u4ed6\u6216\u548c\u4ed6\u4ea4\u8c08\u3002\u4ed6\u5047\u5192\u9ebb\u75af\uff0c\u5411\u8fd9\u4e2a\u66fe\u6b3a\u8d1f\u8fc7\u4ed6 \u7684\u4e16\u754c\u6311\u8845\uff0e\u800c\u8fd9\u4e2a\u4e16\u754c\u5374\u4ee5\u8d85\u8fc7\u5de8\u6daa\u5343\u500d\u7684\u529b\u91cf\u8fd8\u51fb\u4e86\u4ed6\uff01\u90a3\u4fbf\u662f\u5bc2\u5bde\u3001\u51b7\u6de1\uff0c\u96be\u4ee5\u5fcd\u53d7 \u7684\u6b67\u89c6\uff01\u4ed6\u6b66\u529f\u8d8a\u6765\u8d8a\u9ad8\uff0c\u4f46\u90a3\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u4ed6\u6240\u611f\u53d7\u7684\uff0c\u6240\u83b7\u5f97\u7684\u4e0d\u662f\u5c0a\u656c\uff0c\u800c\u662f\u5f02\u6837\u7684 \u51b7\u6de1\u4e0e\u8f7b\u8511\u3002\u8fd9\u611f\u53d7\u4e0e\u5c81\u6708\u4ff1\u589e\uff0c\u4ee5\u81f3\u672c\u6765\u6709\u4e9b\u4eba\u5bf9\u4ed6\u5e76\u65e0\u6076\u610f\uff0c\u5e76\u65e0\u8f7b\u89c6\uff0c\u800c\u4ed6\u4e5f\u4e00\u4f8b\u770b \u5f85\uff1b\u628a\u522b\u4eba\u5f53\u6210\u5bf9\u4ed6\u6000\u6709\u6076\u610f\u7684\u4eba\u3002\u4ed6\u5728\u81ea\u5df1\u7684\u5468\u56f4\u5f20\u8d77\u65e0\u5f62\u7684\u5e10\u5e55\uff0c\u628a\u81ea\u5df1\u4e0e\u8fd9\u4e16\u754c\u9694\u7edd \u5f00\u6765\u3002 \u3000\u3000\u56e0\u679c\u76f8\u4e58\uff0c\u4ed6\u884c\u4e8b\u8d8a\u602a\u8bde\u4e0d\u7ecf\uff0c\u4fbf\u8d8a\u611f\u5230\u82e6\u607c\u5bc2\u5bde\u3002\u4e2d\u539f\u7684\u6b66\u5e08\u51e0\u4e4e\u90fd\u88ab\u4ed6\u6253\u8d25\u4e86\uff0c\u4ed6 \u81ea\u4fe1\u6b66\u529f\u5df2\u662f\u5929\u4e0b\u65e0\u654c\uff0c\u4e8e\u662f\u4fbf\u79bb\u5f00\u4e2d\u539f\uff0c\u6d6a\u6e38\u897f\u5317\uff0c\u60f3\u8981\u53bb\u627e\u5929\u5c71\u6d3e\u7684\u638c\u95e8\u3002\u60f3\u4e0d\u5230\u672a\u66fe \u8e0f\u5165\u56de\u7586\uff0c\u5c31\u5728\u5ddd\u5eb7\u4ea4\u754c\u7684\u96c0\u513f\u5c71\uff0c\u7adf\u7136\u9047\u5230\u4e86\u4e00\u4e2a\u5c06\u4ed6\u5f53\u4f5c\u670b\u53cb\u7684\u770b\u5f85\u7684\u4eba\uff0c\u5bf9\u4ed6\u5e76\u4e0d\u6b67 \u89c6\u8f7b\u8511\uff0c\u5e76\u4e0d\u618e\u6076\u8fdc\u907f\uff0c\u751a\u800c\u5bf9\u4ed6\u7684\u9ebb\u75af\u4e5f\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u8fd8\u7ed9\u4ed6\u6cbb\u75c5\uff0c\u643a\u4ed6\u540c\u884c\u3002\u8fd9\u4eba\u4fbf \u662f\u51b0\u5ddd\u5929\u5973\u3002\u4ed6\u53ef\u4e0d\u77e5\u9053\uff0c\u51b0\u5ddd\u5929\u5973\u6839\u672c\u6ca1\u89c1\u8fc7\u9ebb\u75af\uff0c\u4e5f\u4e0d\u77e5\u9053\u9ebb\u75af\u662f\u4ec0\u4e48\u6a21\u6837\uff0c\u4ed6\u5047\u626e\u9ebb \u75af\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u5413\u7740\u5979\u3002 \u3000\u3000\u5c31\u50cf\u9177\u5bd2\u7684\u5e7d\u8c37\u91cc\u5ffd\u7136\u900f\u8fdb\u4e86\u9633\u5149\uff0c\u5373\u4f7f\u662f\u4e00\u7ebf\u9633\u5149\uff0c\u4e5f\u4ee4\u5e7d\u8c37\u5927\u6709\u751f\u610f\uff0c\u4ed6\u7684\u5fc3\u6249\u7ed9 \u51b0\u5ddd\u5929\u5973\u5728\u65e0\u610f\u4e4b\u4e2d\u6253\u5f00\u4e86\u3002\u4ed6\u9664\u4e86\u5e08\u7236\u4e4b\u5916\uff0c\u4ece\u672a\u6709\u8fc7\u8981\u4e0e\u4eba\u4eb2\u8fd1\u7684\u5ff5\u5934\uff0c\u4f46\u81ea\u4ece\u89c1\u4e86\u51b0 \u5ddd\u5929\u5973\u4e4b\u540e\uff0c\u5c31\u4e0d\u613f\u79bb\u5f00\u5979\uff0c\u7eb5\u8bb8\u662f\u6697\u6697\u8ddf\u8e2a\u4e5f\u597d\u3002\u8fd9\u5012\u5e76\u4e0d\u662f\u5e7d\u840d\u6240\u8bf4\u7684\u201c\u766b\u86e4\u87c6\u60f3\u5403\u5929 \u9e45\u8089\u201d\uff0c\u800c\u4ed6\u53ea\u662f\u89c9\u5f97\uff0c\u8fd9\u4e16\u4e0a\u53ea\u6709\u51b0\u5ddd\u5929\u5973\u624d\u662f\u4ed6\u53ef\u4ee5\u4eb2\u8fd1\u7684\u4eba\u3002 \u3000\u3000\u5728\u96c0\u513f\u5c71\u4e2d\uff0c\u4ed6\u53c8\u9047\u89c1\u4e86\u5510\u7ecf\u5929\uff0c\u8d77\u5148\u4ed6\u5e76\u4e0d\u77e5\u9053\u5510\u7ecf\u5929\u662f\u5929\u5c71\u95e8\u4e0b\uff0c\u540e\u6765\u77e5\u9053\u4e86\uff0c\u5374 \u53c8\u540c\u65f6\u77e5\u9053\u5510\u7ecf\u5929\u662f\u51b0\u5ddd\u5929\u5973\u7684\u7231\u4fa3\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u7684\u5fc3\u4e2d\u7adf\u81ea\u8d77\u4e86\u83ab\u540d\u5176\u5999\u7684\u5992\u610f\u3002\u4ed6\u672c \u6765\u662f\u8981\u627e\u5929\u5c71\u6d3e\u7684\u638c\u95e8\uff0c\u5148\u884c\u6bd4\u8bd5\uff0c\u518d\u63a2\u542c\u5929\u5c71\u6d3e\u4e0e\u81ea\u5df1\u5e08\u7236\u7684\u6e0a\u6e90\uff0c\u89e3\u7b54\u81ea\u5df1\u80f8\u4e2d\u7684\u7591\u95ee \u7684\uff0c\u4f46\u5728\u89c1\u4e86\u5510\u7ecf\u5929\u4e4b\u540e\uff0c\u8fd9\u4e2a\u5ff5\u5934\u5c31\u5ffd\u7136\u6253\u6d88\u4e86\u3002\u4e00\u6765\u662f\u4ed6\u4e0d\u613f\u5bf9\u5929\u5c71\u6d3e\u6709\u6240\u6c42\uff0c\u4e8c\u6765\u662f \u4ed6\u53d1\u73b0\u5510\u7ecf\u5929\u7684\u6b66\u529f\u7ade\u4e0e\u4ed6\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002\u5510\u7ecf\u5929\u662f\u5929\u5c71\u6d3e\u638c\u95e8\u4eba\u5510\u6653\u6f9c\u4e4b \u5b50\uff0c\u513f\u5b50\u5df2\u7ecf\u5982\u6b64\uff0c\u7236\u4eb2\u53ef\u60f3\u800c\u77e5\uff0c\u4ed6\u662f\u4e2a\u5fc3\u9ad8\u6c14\u50b2\u4e4b\u4eba\uff0c\u81ea\u5fd6\u4e0d\u662f\u5510\u6653\u6f9c\u7684\u5bf9\u624b\uff0c\u4fbf\u7acb\u5fd7 \u518d\u4e0b\u82e6\u529f\uff0c\u7ec3\u90a3\u300a\u6bd2\u9f99\u79d8\u7c4d\u300b\u7684\u5965\u5999\u6b66\u529f\uff0c\u51c6\u5907\u7ec3\u5230\u5e08\u7236\u7684\u90a3\u822c\u5883\u754c\u65f6\uff0c\u518d\u4e0a\u5929\u5c71\u6311\u8845\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6697\u6697\u8ffd\u8e2a\u51b0\u5ddd\u5929\u5973\uff0c\u6545\u610f\u5728\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u4e4b\u95f4\u6311\u62e8\u79bb\u95f4\uff0c\u5174\u6ce2\u4f5c\u6d6a\u3002\u8fd9\u672c\u6765 \u662f\u6b63\u4eba\u541b\u5b50\u6240\u4e0d\u9f7f\u7684\u4e8b\u60c5\uff0c\u4f46\u5bf9\u91d1\u4e16\u9057\u6765\u8bf4\uff0c\u4ed6\u7684\u8111\u6d77\u4e2d\u6839\u672c\u5c31\u6ca1\u6709\u4e16\u4fd7\u7684\u9053\u5fb7\u89c2\u5ff5\uff0c\u66f4\u6ca1 \u6709\u60f3\u8fc7\u4ec0\u4e48\u662f\u201c\u6b63\u6d3e\u201d\u7684\u884c\u4e3a\uff0c\u4ec0\u4e48\u662f\u201c\u65e0\u8d56\u201d\u7684\u884c\u5f84\uff0c\u4ed6\u53ea\u662f\u50cf\u4e00\u4e2a\u5b69\u5b50\u4e00\u6837\uff0c\u6b22\u559c\u4e00\u4ef6 \u4e1c\u897f\uff0c\u5c31\u4e0d\u613f\u610f\u8ba9\u7b2c\u4e8c\u4e2a\u5b69\u5b50\u62a2\u53bb\u3002\u5e78\u597d\u4ed6\u5fc3\u5730\u5c1a\u975e\u90aa\u6076\uff0c\u5426\u5219\u4ed6\u8d81\u7740\u5510\u7ecf\u5929\u5728\u90b9\u5bb6\u7597\u4f24\u672a \u6108\u4e4b\u9645\uff0c\u5927\u53ef\u4ee5\u5c06\u4ed6\u6253\u6b7b\u3002 \u3000\u3000\u5c31\u662f\u6000\u7740\u8fd9\u6837\u7684\u5fc3\u60c5\uff0c\u91d1\u4e16\u9057\u8ffd\u8e2a\u51b0\u5ddd\u5929\u5973\uff0c\u4e00\u76f4\u8ffd\u8e2a\u5230\u5ce8\u5d4b\u5c71\u53e3\uff0c\u4ed6\u5b8c\u5168\u6599\u60f3\u4e0d\u5230\uff0c \u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u7adf\u4f1a\u6beb\u4e0d\u7559\u60c5\u5730\u6307\u65a5\u4ed6\uff0c\u5e7d\u840d\u9a82\u4ed6\u662f\u201c\u60f3\u5403\u5929\u9e45\u8089\u7684\u766b\u86e4\u819c\u3002\u201d\u8fd9\u8fd8\u7f62\u4e86\uff0c\u8fde \u51b0\u5ddd\u5929\u5973\u4e5f\u5f53\u9762\u8bf4\u4ed6\u201c\u65e0\u8d56\u201d\uff0c\u8f7b\u8f7b\u7684\u4e00\u53e5\u8bdd\uff0c\u5c31\u50cf\u6674\u5929\u4e4b\u4e2d\u7a81\u7136\u8d77\u4e86\u9739\u96f3\uff0c\u8f70\u6563\u4e86\u4ed6\u5e7b\u60f3 \u7684\u5f69\u8679\u3002 \u3000\u3000\u6b64\u9645\uff0c\u4ed6\u72ec\u7acb\u5ce8\u5d4b\u4e4b\u5dc5\uff0c\u5f80\u4e8b\u4e00\u5e55\u4e00\u5e55\u4ece\u8111\u6d77\u4e2d\u95ea\u8fc7\uff0c\u5929\u4e0a\u661f\u6708\u897f\u6c89\uff0c\u5c71\u95f4\u78f7\u706b\u660e\u706d\uff0c \u4ed6\u7684\u5fc3\u60c5\u4e5f\u5c31\u50cf\u78f7\u706b\u4e00\u6837\u95ea\u70c1\u65e0\u5b9a\uff0c\u4e00\u5ffd\u513f\u66b4\u6012\u5982\u96f7\uff0c\u4e00\u5ffd\u513f\u5fc3\u4f24\u6b32\u7edd\uff0c\u5ffd\u7136\u95f4\u8111\u5b50\u91cc\u597d\u50cf \u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u5168\u7136\u4e0d\u80fd\u601d\u60f3\uff0c\u771f\u7684\u4f3c\u6574\u4e2a\u4e16\u754c\u9057\u5f03\u4e86\u4ed6\uff0c\u79bb\u4ed6\u800c\u53bb\u3002\u4ed6\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u6323\u624e\u547c \u53f7\u3002\u8346\u68d8\u523a\u4f24\u4e86\u4ed6\u7684\u624b\u8db3\uff0c\u523a\u4f24\u4e86\u4ed6\u7684\u5934\u9762\uff0c\u4ed6\u4e5f\u4e0d\u611f\u89c9\u4e1d\u6beb\u75db\u695a\u3002\u5076\u7136\u95f4\u5728\u5c71\u6da7\u8fd9\u8fb9\u4e34\u6d41 \u7167\u5f71\uff0c\u7167\u89c1\u81ea\u5df1\u4fca\u79c0\u7684\u9762\u5e9e\uff0c\u9762\u4e0a\u51e0\u6761\u88ab\u8346\u68d8\u523a\u4f24\u7684\u6de1\u6de1\u7684\u8840\u75d5\uff0c\u4ed6\u4fbf\u6309\u637a\u4e0d\u4f4f\u6fc0\u52a8\u7684\u5fc3 \u60c5\uff0c\u53d1\u72c2\u4f3c\u7684\u53eb\u9053\uff1a\u201c\u6211\u4e5f\u662f\u7236\u6bcd\u6240\u751f\u7684\u6e05\u767d\u4e4b\u8eaf\uff0c\u4e3a\u4f55\u4e16\u4eba\u5bf9\u6211\u8fd9\u822c\u8f7b\u8d31\uff1f\u201d \u3000\u3000\u4ed6\u72c2\u53eb\u3001\u51b7\u7b11\uff0c\u5ffd\u5730\u5c06\u8863\u88f3\u90fd\u6293\u88c2\u4f5c\u7247\u7247\u788e\uff0c\u8d64\u4e86\u8eab\u5b50\u5728\u5c71\u6da7\u91cc\u6d17\u4e86\u4e00\u4f1a\uff0c\u51dd\u89c6\u6c34\u4e2d\u6e05 \u767d\u7684\u7684\u5f71\u5b50\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u4e0d\u662f\u6211\uff0c\u6211\u7684\u672c\u6765\u9762\u76ee\u662f\u8fd9\u6837\u7684\u5417\uff1f\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c \u89e3\u5f00\u4ed6\u653e\u5728\u6811\u4e0b\u7684\u968f\u8eab\u643a\u5e26\u7684\u5305\u88b1\uff0c\u91cc\u9762\u6709\u4ed6\u4ee5\u524d\u5047\u626e\u9ebb\u75af\u65f6\u7684\u90a3\u5957\u8934\u697c\u8863\u88f3\uff0c\u4ed6\u6296\u4e86\u4e00 \u4e0b\uff0c\u91cd\u65b0\u62ab\u5728\u8eab\u4e0a\uff0c\u624b\u6d82\u836f\u6599\uff0c\u5728\u9762\u4e0a\u4e00\u62b9\u3002\u7384\u529f\u5185\u8fd0\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u9762\u4e0a\u5e03\u6ee1\u7ea2\u4e91\uff0c\u624b\u81c2\u957f \u51fa\u7599\u7629\uff0c\u53c8\u53d8\u6210\u4e86\u4e00\u4e2a\u5f62\u5bb9\u4e11\u602a\u7684\u5927\u9ebb\u75af\uff01\u53c8\u8dd1\u5230\u5c71\u6da7\u65c1\u8fb9\u4e34\u6d41\u7167\u5f71\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u662f \u6211\u7684\u672c\u6765\u9762\u76ee\uff0c\u8fd9\u624d\u662f\u4eba\u4eba\u618e\u538c\u7684\u6211\u7684\u672c\u6765\u9762\u76ee\uff01\u201d \u3000\u3000\u4ed6\u5728\u81ea\u8f7b\u81ea\u8d31\u4e4b\u4e2d\u611f\u5230\u4e00\u79cd\u83ab\u540d\u5176\u5999\u7684\u75db\u5feb\uff0c\u672c\u6765\u4ed6\u5728\u9047\u5230\u51b0\u5ddd\u5929\u5973\u4e4b\u540e\uff0c\u548c\u5979\u540c\u884c\u51e0 \u65e5\uff0c\u602a\u50fb\u7684\u6027\u60c5\u5df2\u6e10\u6e10\u6709\u6240\u6539\u53d8\uff0c\u5f53\u4ed6\u77e5\u9053\u4e86\u5979\u4e0d\u559c\u6b22\u81ea\u5df1\u7684\u9ebb\u75af\u5f62\u8c8c\u4e4b\u540e\uff0c\u751a\u81f3\u66fe\u7acb\u4e0b\u8a93 \u613f\uff0c\u4ece\u6b64\u6062\u590d\u672c\u6765\u7684\u9762\u76ee\u548c\u4e16\u4eba\u76f8\u89c1\uff0c\u4e0d\u518d\u5413\u4eba\u4e86\u3002\u8fd8\u4e3a\u6b64\u800c\u5077\u4e86\u4e00\u5957\u534e\u7f8e\u7684\u8863\u88f3\u3002\u5374\u60f3\u4e0d \u5230\u4eca\u665a\u88ab\u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u7684\u8bf4\u8bdd\u523a\u4f24\uff0c\u4ed6\u975e\u4f46\u4e0d\u6253\u7b97\u6062\u590d\u672c\u6765\u9762\u76ee\uff0c\u5374\u53cd\u800c\u6062\u590d\u4e86\u6124\u4e16\u5ac9\u4fd7\u7684 \u5fc3\u60c5\uff0c\u6bd4\u524d\u66f4\u751a\uff01 \u3000\u3000\u5509\uff0c\u8fd9\u4e5f\u4e0d\u80fd\u602a\u4ed6\u201c\u504f\u6fc0\u201d\uff0c\u987b\u77e5\u4ed6\u6709\u751f\u4ee5\u6765\uff0c\u9664\u4e86\u5e08\u7236\u4e4b\u5916\uff0c\u53ea\u78b0\u89c1\u8fc7\u4e00\u4e2a\u51b0\u5ddd\u5929\u5973 \u662f\u628a\u4ed6\u5f53\u4f5c\u201c\u4eba\u201d\u770b\u5f85\u7684\u4eba\uff0c\u6240\u4ee5\u4ed6\u8fd9\u5fc3\u60c5\uff0c\u5e76\u4e0d\u662f\u666e\u901a\u7684\u5931\u604b\u3002\u4e5f\u8bb8\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u5230\u8fc7 \u7231\u60c5\uff0c\u800c\u662f\u611f\u5230\u88ab\u4eba\u629b\u5f03\uff0c\u88ab\u4eba\u8f7b\u8511\uff0c\u4ee5\u53ca\u81ea\u5c0a\u5fc3\u88ab\u6bc1\u706d\u7684\u7684\u60b2\u4f24\uff0c\u800c\u8fd9\u79cd\u60b2\u4f24\u6bd4\u5931\u604b\u7684\u60b2 \u4f24\u90a3\u662f\u4e0d\u77e5\u8d85\u8fc7\u51e0\u5343\u4e07\u500d\uff01 \u3000\u3000\u661f\u6708\u897f\u6c89\uff0c\u78f7\u706b\u660e\u706d\uff0c\u5c71\u9876\u7684\u767d\u4e91\u7ed3\u6210\u6eda\u6eda\u7684\u6ce2\u6d9b\uff0c\u50cf\u4e00\u4e2a\u65e0\u8fb9\u65e0\u9645\u88ab\u716e\u6cb8\u4e86\u7684\u6d77\u6d0b\uff0c \u7ffb\u7ffb\u6eda\u6eda\u3002\u8fd9\u662f\u9ed1\u591c\u5c06\u5c3d\uff0c\u66d9\u5149\u5373\u73b0\u4e4b\u524d\u7684\u666f\u8c61\u3002\u5c71\u98ce\u5439\u6765\uff0c\u62c2\u9762\u6e05\u723d\uff0c\u91d1\u4e16\u9057\u4f4e\u5934\u4e00\u770b\uff0c \u53d1\u73b0\u81ea\u5df1\u65e0\u610f\u4e4b\u95f4\u5df2\u8d70\u5230\u60ac\u5d16\u7684\u8fb9\u6cbf\uff0c\u90a3\u60ac\u5d16\u5b64\u5cf0\u51f8\u51fa\uff0c\u4f38\u5165\u4e91\u6d77\u4e4b\u4e2d\uff0c\u5ca9\u4e0a\u523b\u6709\u201c\u820d\u8eab \u5d16\u201d\u4e09\u4e2a\u5927\u5b57\uff0c\u8fd9\u6b63\u662f\u5ce8\u5d4b\u5c71\u4e0a\u6700\u9ad8\u6700\u9669\u7684\u5371\u5d16\uff0c\u5e38\u6709\u4eba\u4ece\u8fd9\u91cc\u8df3\u4e0b\u53bb\u81ea\u6740\u3002\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00 \u51db\uff0c\u7adf\u4e0d\u77e5\u81ea\u5df1\u600e\u4e48\u4f1a\u8d70\u5230\u6b64\u5904\uff1f\u8bd5\u4e00\u4fef\u89c6\uff0c\u4f46\u89c1\u5ced\u58c1\u5343\u4e08\uff0c\u5e7d\u8c37\u65e0\u5e95\uff0c\u82e5\u7136\u5fc3\u667a\u8ff7\u7cca\uff0c\u7a0d \u4e00\u4e0b\u614e\uff0c\u8dcc\u4e0b\u53bb\u4fbf\u662f\u7c89\u8eab\u788e\u9aa8\u4e4b\u7978\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4fef\u89c6\u5e7d\u8c37\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u9661\u7136\u95f4\uff0c\u4ed6\u8111\u6d77\u4e2d\u6cdb\u8d77\u51b0\u5ddd\u5929\u5973\u7684\u5f71\u5b50\uff0c\u90a3\u756a\u529d\u4ed6\u7acb\u5fd7\u505a \u4eba\u7684\u8bf4\u8bdd\uff0c\u90a3\u5e26\u6709\u601c\u60dc\u7684\u773c\u5149\uff0c\u50cf\u4e00\u80a1\u6696\u6d41\u6d41\u8fc7\u5fc3\u7530\uff0c\u4ed6\u4f4e\u5524\u4e00\u58f0\uff0c\u5374\u53c8\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u5c31\u662f \u4f60\u4e0d\u8bf4\u8fd9\u756a\u8bf4\u8bdd\uff0c\u6211\u4e5f\u4e0d\u4f1a\u4ece\u8fd9\u91cc\u8df3\u4e0b\uff01\u201d\u98de\u8eab\u4e00\u8dc3\uff0c\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u7ad9\u8d77\u6765\u65f6\uff0c\u5df2\u5728\u5c71\u5934 \u7a7a\u65f7\u4e4b\u5730\uff0c\u8fdc\u8fdc\u79bb\u5f00\u4e86\u9669\u5883\uff0c\u751f\u547d\u4e5f\u4ece\u6b7b\u4ea1\u7684\u8fb9\u7f18\u62c9\u4e86\u56de\u6765\u3002 \u3000\u3000\u53ea\u662f\u72c2\u6fc0\u7684\u5fc3\u60c5\u8fd8\u672a\u8d8b\u4e8e\u5e73\u9759\uff0c\u4ed6\u53d1\u58f0\u957f\u5578\uff0c\u58f0\u632f\u6797\u6728\uff0c\u53ef\u662f\u8fd9\u58f0\u97f3\u80fd\u4f20\u5230\u51b0\u5ddd\u5929\u5973\u7684 \u8033\u8fb9\u5417\uff1f\u4ed6\u72ec\u7acb\u5cf0\u5dc5\uff0c\u51dd\u671b\u4e91\u6d77\uff0c\u6eda\u6eda\u7684\u4e91\u6d6a\u5e7b\u6210\u5404\u79cd\u5404\u6837\u7684\u5f62\u8c61\uff0c\u4e91\u6d77\u4e2d\u51b0\u5ddd\u5929\u5973\u597d\u50cf\u4ecd \u662f\u5e26\u7740\u90a3\u4e00\u80a1\u9ad8\u8d35\u5c0a\u4e25\u3001\u4e0d\u53ef\u63a5\u89e6\u7684\u795e\u6c14\uff0c\u7528\u9ad8\u9ad8\u5728\u4e0a\u7684\u3001\u601c\u60af\u7684\u773c\u5149\u770b\u7740\u4ed6\u3002\u201c\u6211\u4e0d\u8981\u4eba \u601c\u60af\u201d\u4ed6\u5fc3\u4e2d\u53eb\u9053\uff0c\u518d\u4e00\u51dd\u89c6\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5f62\u8c61\u4ea6\u5df1\u6a21\u7cca\uff0c\u5728\u4e91\u6d77\u4e2d\u9690\u9690\u6de1\u53bb\uff0c\u767d\u4e91\u5189\u5189\uff0c \u51b0\u5ddd\u5929\u5973\u7684\u5e7b\u5f71\u4e5f\u8d8a\u98de\u8d8a\u9ad8\uff0c\u8fdc\u8fdc\u7684\u79bb\u5f00\u4e86\u4ed6\uff0c\u597d\u50cf\u8981\u98de\u5230\u53e6\u4e00\u4e2a\u4e16\u754c\uff0c\u4ed6\u62fe\u8d77\u94c1\u62d0\uff0c\u53c8\u5230 \u5c71\u6da7\u8fd9\u8fb9\u4e34\u6d41\u7167\u5f71\uff0c\u6c34\u4e2d\u73b0\u51fa\u4ed6\u53d8\u5f62\u4e4b\u540e\u7684\u4e11\u964b\u9762\u8c8c\uff0c\u4ed6\u5982\u75af\u4f3c\u50bb\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u662f\u4e91 \u7aef\u7684\u5929\u9e45\uff0c\u6211\u662f\u6da7\u5e95\u7684\u86e4\u87c6\u3002\u201d\u72c2\u7b11\u4e00\u4f1a\uff0c\u53c8\u75db\u54ed\u4e00\u4f1a\uff0c\u4f46\u89c9\u4e16\u754c\u4e4b\u5927\uff0c\u7adf\u65e0\u4e00\u4eba\u7406\u89e3\u81ea \u5df1\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff01\u4ed6\u4ee5\u81ea\u66b4\u81ea\u5f03\u7684\u5fc3\u60c5\uff0c\u7d22\u6027\u7528\u6c61\u6ce5\u6d82\u5728\u81ea\u5df1\u7684\u8eab\u4e0a\u3001\u9762\u4e0a\uff0c\u628a\u81ea \u5df1\u5f04\u5f97\u66f4\u50cf\u4e2a\u6ce5\u9996\u6c61\u9762\u7684\u75af\u4e10\uff01\u5fc3\u4e2d\u53eb\u9053\uff1a\u201c\u4e16\u4eba\u90fd\u618e\u538c\u6211\uff0c\u8f7b\u8d31\u6211\uff0c\u597d\u5427\uff0c\u6211\u5c31\u8981\u8ba9\u4f60\u4eec \u66f4\u591a\u4e09\u500d\u7684\u8ba8\u538c\uff01\u201d \u3000\u3000\u4ed6\u6b63\u5728\u81ea\u8f7b\u81ea\u8d31\uff0c\u81ea\u6028\u81ea\u827e\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u8eab\u540e\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u7b11\u5f97\u975e\u5e38\u67d4\u5a9a\uff0c\u5374\u53c8\u975e\u5e38 \u987d\u76ae\uff0c\u4e00\u4e2a\u5973\u4e86\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u766b\u86e4\u87c6\u771f\u597d\u73a9\uff01\u201d\u91d1\u4e16\u9057\u4e00\u8154\u6124\u6fc0\u4e4b\u6c14\uff0c\u6b63\u81ea\u65e0\u4ece\u53d1 \u6cc4\uff0c\u95fb\u8a00\u5927\u6012\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u62fe\u8d77\u4e00\u56e2\u6c61\u6ce5\u4fbf\u5411\u53d1\u58f0\u4e4b\u5904\u6454\u53bb\uff0c\u53ea\u542c\u5f97\u90a3\u5973\u4e8e\u7684\u58f0\u97f3\u53c8\u9053\uff1a \u201c\u771f\u662f\u4e2a\u5927\u50bb\u74dc\uff0c\u4f60\u8fd9\u6837\u81ea\u8f7b\u81ea\u8d31\uff0c\u53c8\u6709\u8c01\u4eba\u601c\u60dc\u4f60\uff1f\u201d\u91d1\u4e16\u9057\u8eab\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c \u4ed6\u5df2\u629b\u51fa\u6c61\u6ce5\uff0c\u98de\u8eab\u524d\u6251\uff0c\u4ed6\u7684\u72ec\u95e8\u6697\u5668\u624b\u6cd5\u53c8\u72e0\u53c8\u51c6\uff0c\u867d\u662f\u4e00\u56e2\u6c61\u6ce5\uff0c\u88ab\u4ed6\u4f7f\u52b2\u629b\u51fa\uff0c\u4e5f \u50cf\u4e00\u5757\u77f3\u5934\u3002\u53ea\u542c\u5f97\u201c\u5580\u5587\u201d\u4e00\u58f0\uff0c\u4e00\u6280\u6811\u679d\uff0c\u5df2\u7ed9\u6ce5\u56e2\u6298\u65ad\uff0c\u4f46\u90a3\u4eba\u5f71\u5374\u4e5f\u4e0d\u89c1\u4e86\u3002\u6ce5\u56e2 \u5c1a\u6253\u4e0d\u7740\uff0c\u4ed6\u8fd9\u4e00\u6251\uff0c\u81ea\u7136\u4e5f\u662f\u6251\u4e86\u4e2a\u7a7a\uff0c\u989d\u5934\u51e0\u4e4e\u78b0\u5230\u6811\u4e0a\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u81ea\u79bb\u5f00\u86c7\u5c9b\u4ee5\u6765\uff0c\u95ef\u8361\u6c5f\u6e56\uff0c\u8d25\u5728\u4ed6\u624b\u4e0b\u7684\u6210\u540d\u4eba\u7269\uff0c\u4e0d\u8ba1 \u5176\u6570\uff0c\u80fd\u4e0e\u4ed6\u6253\u6210\u5e73\u624b\u7684\uff0c\u4ea6\u4e0d\u8fc7\u662f\u5510\u7ecf\u5929\u3001\u51b0\u5ddd\u5929\u5973\u3001\u8d64\u795e\u5b50\u7b49\u6709\u9650\u51e0\u4eba\u800c\u5df2\uff01\u60f3\u4e0d\u5230\u800c \u4eca\u5374\u7a81\u7136\u9047\u5230\u4e86\u52b2\u654c\uff0c\u800c\u4e14\uff0c\u542c\u8fd9\u58f0\u97f3\uff0c\u8fd9\u52b2\u654c\u8fd8\u7adf\u662f\u4e2a\u5e74\u9752\u7684\u5973\u5b50\uff0c\u522b\u7684\u529f\u592b\u867d\u672a\u77e5\u9053\uff0c \u53ea\u51ed\u8fd9\u4efd\u8f7b\u529f\uff0c\u5c31\u5df2\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff01 \u3000\u3000\u4e16\u95f4\u7adf\u7136\u6709\u8fd9\u6837\u7684\u5973\u5b50\uff01\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u3001\u96be\u4ee5\u76f8\u4fe1\u7684\u795e\u5947\u4e4b\u4e8b\uff01\u91d1\u4e16\u9057\u672c\u5c31\u597d\u80dc\uff0c\u8fd9\u65f6 \u66f4\u64a9\u8d77\u4e86\u8f83\u6280\u4e89\u96c4\u4e4b\u5ff5\uff0c\u4ed6\u8ffd\u5165\u6797\u4e2d\uff0c\u773c\u5149\u56db\u4e0b\u641c\u7d22\uff0c\u5ffd\u53c8\u542c\u5f97\u90a3\u5973\u5b50\u7684\u58f0\u97f3\u5728\u80cc\u540e\u683c\u683c\u4e00 \u7b11\uff0c\u6e05\u8106\u7684\u58f0\u97f3\u5b9b\u82e5\u94f6\u94c3\uff0c\u7b11\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4f60\u4e5f\u63a5\u6211\u8fd9\u4e2a\u6697\u5668\uff01\u201d\u91d1\u4e16\u9057\u5927\u53eb\u4e00 \u58f0\uff0c\u500f\u5730\u56de\u5934\uff0c\u4f38\u624b\u4fbf\u6293\u3002\u58f0\u97f3\u5c31\u5728\u80cc\u540e\uff0c\u91d1\u4e16\u9057\u5fc3\u60f3\u8fd9\u4e00\u6293\u65e0\u843d\u7a7a\u4e4b\u7406\uff0c\u4ed6\u7684\u5185\u529f\u5df2\u7ec3\u5230 \u6536\u53d1\u81ea\u5982\u7684\u5883\u754c\uff0c\u5c31\u5728\u8fd9\u56de\u8eab\u6293\u654c\u7684\u5239\u90a3\u95f4\uff0c\u540c\u65f6\u5c01\u95ed\u4e86\u5168\u8eab\u7684\u5927\u7a74\uff0c\u6559\u4efb\u4f55\u6697\u5668\u90fd\u96be\u4f24\u5bb3\u3002 \u3000\u3000\u4f46\u542c\u5f97\u7b11\u58f0\u6447\u66f3\uff0c\u53ea\u89c1\u4e00\u4e2a\u767d\u8863\u5c11\u5973\u7684\u80cc\u5f71\u817e\u7a7a\u98de\u8d77\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u56de\u65cb\uff0c\u5df2\u659c\u63a0\u51fa\u6570\u4e08 \u4e4b\u5916\u3002\u91d1\u4e16\u9057\u98de\u8eab\u6251\u53bb\uff0c\u773c\u775b\u5ffd\u7136\u4e00\u82b1\uff0c\u4f46\u89c1\u4e94\u8272\u7f24\u7eb7\uff0c\u624b\u8db3\u5934\u9762\u90fd\u5df1\u7ed9\u654c\u4eba\u7684\u201c\u6697\u5668\u201d\u6253 \u4e2d\u3002\u8fd9\u6697\u5668\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u7c98\u5728\u9762\u4e0a\u6e7f\u6d8c\u6d8c\u7684\u4e00\u7247\u51b0\u51c9\uff0c\u91d1\u4e16\u9057\u6025\u5fd9\u505c\u6b65\uff0c\u4f38\u624b\u4e00\u62b9\uff0c \u539f\u6765\u7adf\u662f\u65e0\u6570\u82b1\u74e3\uff0c\u82b1\u74e3\u4e0a\u9732\u73e0\u672a\u4e8e\uff0c\u6240\u4ee5\u7c98\u5728\u9762\u4e0a\u6e7f\u6e21\u692d\u7684\u4e00\u7247\u51b0\u51c9\uff0c\u8fd9\u4e00\u62b9\u628a\u4ed6\u5934\u9762\u624b \u8db3\u7684\u6c61\u79fd\uff0c\u90fd\u62b9\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5c31\u5982\u7ed9\u90a3\u5c11\u5973\u5f3a\u8feb\u6d17\u4e86\u4e00\u4e2a\u8138\uff01 \u3000\u3000\u91d1\u4e16\u9057\u4e00\u751f\u6b22\u559c\u620f\u5f04\u4eba\u5bb6\uff0c\u60f3\u4e0d\u5230\u800c\u4eca\u4e3a\u4eba\u620f\u5f04\uff0c\u4ed6\u53c8\u662f\u6c14\u607c\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u90a3\u5973\u5b50\u5df2\u7ecf \u4e0d\u89c1\uff0c\u91d1\u4e16\u9057\u77e5\u9053\u518d\u627e\u4e5f\u627e\u4e0d\u89c1\uff0c\u7d22\u6027\u5c31\u5728\u6797\u4e2d\u7761\u4e86\u4e00\u4e2a\u5927\u89c9\u3002\u8fd9\u65f6\u4ed6\u7684\u6ce8\u610f\u529b\u5df2\u88ab\u90a3\u795e\u9f99 \u89c1\u9996\u4e0d\u89c1\u5c3e\u7684\u5973\u5b50\u6240\u5438\u5f15\uff0c\u5fc3\u601d\u4e00\u5206\uff0c\u51b0\u5ddd\u5929\u5973\u7ed9\u4ed6\u7684\u523a\u6fc0\u81ea\u7136\u51cf\u4e86\u51e0\u5206\uff0c\u8fd9\u4e00\u89c9\u5012\u7761\u5f97\u9999 \u751c\uff0c\u76f4\u5230\u7b2c\u4e8c\u65e5\u65e5\u4e0a\u4e09\u7aff\u624d\u9192\uff0c\u8fd9\u5df2\u662f\u5192\u5ddd\u751f\u5f00\u5c71\u7ed3\u7f18\u7684\u524d\u4e00\u5929\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8fd9\u4e00\u65e5\u51e0\u4e4e\u7ffb\u904d\u4e86\u5ce8\u5d4b\u5c71\uff0c\u627e\u4e0d\u5230\u90a3\u5c11\u5973\u7684\u534a\u70b9\u8e2a\u5f71\uff0c\u4ed6\u6599\u60f3\u51b0\u5ddd\u5929\u5973\u5df2\u8fdb\u5165\u91d1 \u5149\u5bfa\uff0c\u672c\u60f3\u95ef\u5165\u91d1\u5149\u5bfa\u53bb\u95f9\u4e00\u573a\uff0c\u4f46\u5728\u5c71\u9876\u9065\u89c1\u5510\u7ecf\u5929\u5165\u5bfa\uff0c\u5fc3\u5934\u4e0d\u89c9\u53c8\u6d8c\u8d77\u51b0\u5ddd\u5929\u5973\u5bf9\u4ed6 \u90a3\u51b7\u6de1\u7684\u795e\u6001\uff0c\u4e0e\u9a82\u4ed6\u201c\u65e0\u8d56\u201d\u7684\u58f0\u97f3\u3002\u5992\u6068\u3001\u7f9e\u60ed\u3001\u81ea\u4f24\u3001\u81ea\u8d31\u7b49\u7b49\u5fc3\u60c5\uff0c\u4ea4\u5e76\u7ea0\u7ed3\uff0c\u76d8 \u4e98\u80f8\u81c6\uff0c\u8fd9\u4e00\u665a\u4ed6\u5c31\u5728\u91d1\u5149\u5bfa\u9644\u8fd1\uff0c\u5b58\u5fc3\u5bf9\u4eba\u5c71\u7684\u9ad8\u624b\u6311\u8845\uff0c\u7b2c\u4e00\u6b21\u620f\u5f04\u4e86\u96f7\u9707\u5b50\uff0c\u5413\u8d70\u4e86 \u8d64\u795e\u5b50\uff0c\u5fc3\u4e2d\u751a\u662f\u5f97\u610f\u3002\u7b2c\u4e8c\u6b21\u620f\u5f04\u8c22\u4e91\u771f\uff0c\u60f3\u4e0d\u5230\u90a3\u5c11\u5973\u53c8\u7a81\u7136\u51fa\u73b0\uff0c\u5c31\u5728\u4ed6\u7528\u77f3\u5b50\u5206\u6253 \u8c22\u4e91\u771f\u7684\u9ebb\u7a74\u3001\u75d5\u75d2\u7a74\u3001\u7b11\u8170\u7a74\u4e4b\u65f6\uff0c\u6240\u53d1\u51fa\u7684\u77f3\u5b50\u5168\u88ab\u90a3\u5c11\u5973\u7684\u98de\u9488\u6697\u5668\u5c04\u843d\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u906d\u9047\uff0c\u8c22\u4e91\u771f\u66fe\u8be6\u8be6\u7ec6\u7ec6\u7684\u8bb2\u7ed9\u5510\u7ecf\u5929\u77e5\u9053\uff0c\u4ee4\u5230\u5510\u7ecf\u5929\u60ca\u8bb6\u4e0d\u5df2\u3002\u91d1\u4e16\u9057\u662f\u8eab \u53d7\u4e4b\u4eba\uff0c\u5f53\u65f6\u7684\u60ca\u8bb6\u90a3\u5c31\u66f4\u4e0d\u7528\u63d0\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5728\u542c\u8c22\u4e91\u771f\u8bb2\u8ff0\u4e4b\u65f6\uff0c\u8bef\u4ee5\u4e3a\u8fd9\u5973\u5b50\u4e00\u5b9a\u662f\u51b0\u5ddd\u5929\u5973\uff0c\u4f46\u91d1\u4e16\u9057\u5f53\u7136\u77e5\u9053\u4e0d\u662f\uff0c \u6240\u4ee5\u4ed6\u5f53\u65f6\u7acb\u523b\u629b\u5f00\u4e86\u8c22\u4e91\u771f\uff0c\u6025\u8ffd\u8fd9\u795e\u79d8\u7684\u5973\u5b50\u3002\u9ad8\u5c71\u5bc6\u6797\uff0c\u90a3\u5973\u5b50\u500f\u7684\u8dc3\u4eba\u6797\u4e2d\uff0c\u8eab\u6cd5 \u5374\u4e0d\u4f3c\u6628\u665a\u4e4b\u5feb\uff0c\u4f3c\u4e4e\u662f\u6545\u610f\u5f15\u91d1\u4e16\u9057\u53bb\u8ffd\uff0c\u4f46\u91d1\u4e16\u9057\u4ec5\u7136\u662f\u8ffd\u5979\u4e0d\u4e0a\u3002\u53ea\u89c1\u90a3\u5973\u5b50\u7adf\u4f3c\u98de \u9e1f\u4e00\u822c\uff0c\u4ece\u4e00\u68f5\u5927\u6811\u98de\u5230\u53e6\u4e00\u68f5\u5927\u6811\uff0c\u6811\u53f6\u906e\u7740\u89c6\u7ebf\uff0c\u4f55\u51b5\u53c8\u662f\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u867d\u6709\u6708\u5149\u78f7 \u706b\uff0c\u4ea6\u662f\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u9690\u9690\u89c1\u5979\u7684\u80cc\u5f71\uff0c\u5ffd\u8d77\u5ffd\u843d\uff0c\u88d9\u636e\u98d8\u98d8\uff0c\u4f53\u6001\u8f7b\u76c8\u4e4b\u6781\uff01\u91d1\u4e16\u9057\u4e5f\u7ed9 \u5f04\u5f97\u8ff7\u60d1\u8d77\u6765\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u4e16\u95f4\u90a3\u4f1a\u6709\u8f7b\u529f\u5982\u6b64\u9ad8\u660e\u7684\u5973\u5b50\uff1f\u83ab\u975e\u5979\u7ade\u662f\u8fd9\u5c71\u4e2d\u7684\u4ed9\u5973\uff1f \u3000\u3000\u91d1\u4e16\u9057\u4ece\u5ce8\u5d4b\u7684\u6700\u9ad8\u5cf0\u2014\u2014\u91d1\u9876\uff0c\u4e00\u76f4\u8ffd\u5230\u4e86\u7334\u5b50\u5761\uff0c\u90a3\u5973\u5b50\u5df2\u4e0d\u89c1\u4e86\u3002\u91d1\u4e16\u9057\u77e5\u9053\u5979 \u82e5\u4e0d\u662f\u6545\u610f\u73b0\u8eab\uff0c\u5b9e\u662f\u65e0\u6cd5\u5bfb\u89c5\uff0c\u4e0d\u89c9\u5927\u4e3a\u6c14\u9981\uff0c\u5fc3\u4e2d\u60f3\u5230\uff1a\u201c\u4ed9\u5973\u90a3\u662f\u7edd\u5bf9\u4e0d\u4f1a\u6709\u7684\uff0c\u5982 \u6b64\u770b\u6765\uff0c\u6211\u81ea\u4ee5\u4e3a\u662f\u5929\u4e0b\u65e0\u654c\uff0c\u90a3\u77e5\u5374\u7aef\u7684\u662f\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u5510\u7ecf\u5929\u51b0\u5ddd\u5929\u5973\u4e0e\u6211\u5e74 \u7eaa\u76f8\u82e5\uff0c\u6b66\u529f\u4ea6\u81ea\u76f8\u7b49\uff1b\u8fd9\u5973\u5b50\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u770b\u5979\u4f53\u6001\uff0c\u7edd\u4e0d\u4f1a\u662f\u8001\u592a\u5a46\uff0c\u6b66\u529f\u7adf\u6bd4\u6211 \u9ad8\u660e\u4e86\u4e0d\u77e5\u591a\u5c11\u500d\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u81ea\u601d\u81ea\u60f3\uff0c\u5ffd\u542c\u5f97\u7334\u5b50\u7684\u53eb\u58f0\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6709\u597d\u51e0\u53ea\u7334\u5b50\u961f\u5ced\u58c1\u4e0a\u722c\u4e0b\uff0c\u91d1 \u4e16\u9057\u6b63\u767e\u65e0\u804a\u8d56\uff0c\u4e00\u65f6\u5174\u8d77\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u5df2\u628a\u4e00\u5934\u7334\u5b50\u6293\u7740\uff0c\u90a3\u7334\u5b50\u5431\u5431\u602a\u53eb\uff0c\u5176\u4f59\u7684\u7334\u5b50 \u90fd\u5413\u8dd1\u4e86\uff01 \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4f60\u8dd1\u5f97\u5feb\u4e5f\u9003\u4e0d\u51fa\u6211\u7684\u638c\u5fc3\uff01\u201d\u653e\u5f00\u624b\u4e2d\u7684\u7334\u5b50\uff0c\u98de\u8eab\u4e00\u6293\uff0c\u53c8\u6293\u5230\u4e86 \u7b2c\u4e8c\u53ea\u7334\u5b50\uff0c\u4ed6\u7ae5\u5fc3\u5927\u8d77\uff0c\u7adf\u8981\u548c\u5c71\u4e2d\u7684\u7fa4\u7334\u5f00\u5f00\u73a9\u7b11\uff0c\u9010\u4e00\u620f\u5f04\u3002\u5ffd\u542c\u5f97\u5c71\u5ca9\u4e0a\u53c8\u98d8\u4e0b\u90a3 \u719f\u6089\u7684\u201c\u683c\u683c\u201d\u7684\u7b11\u58f0\u3002\u91d1\u4e16\u9057\u5fd9\u62ac\u5934\u4e00\u770b\uff0c\u6708\u4eae\u6b63\u5728\u4e2d\u5929\uff0c\u5c71\u5ca9\u4e0a\u6beb\u65e0\u906e\u853d\uff0c\u8fd9\u56de\u53ef\u662f\u770b \u5f97\u6e05\u6e05\u695a\u695a\uff0c\u53ea\u89c1\u5ca9\u77f3\u4e0a\u5750\u7740\u4e00\u4e2a\u5c11\u5973\uff0c\u7d2b\u8863\u7384\u88f3\uff0c\u53d1\u4e0a\u675f\u7740\u4e24\u4e2a\u91d1\u73af\uff0c\u957f\u7709\u5982\u753b\uff0c\u7b11\u5f97\u5982 \u82b1\u679d\u4e71\u98a4\uff0c\u770b\u6837\u5b50\u6700\u591a\u4e0d\u8fc7\u5341\u516d\u516b\u5c81\uff0c\u4e00\u8138\u7a1a\u6c14\u672a\u6d88\uff0c\u4f38\u51fa\u4e00\u53ea\u624b\u6307\u6258\u816e\uff0c\u4fa7\u76ee\u659c\u8131\uff0c\u7785\u7740 \u91d1\u4e16\u9057\u7b11\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u91d1\u4e16\u9057\u600e\u6837\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5c11\u5973\u7adf\u662f\u5982\u6b64\u5e74\u8f7b\uff0c\u7b80\u76f4\u5c31\u50cf\u4e2a\u7792\u7740\u7236\u6bcd\u5077\u8dd1\u51fa\u6765\u620f\u800d\u7684\u5927\u5b69 \u5b50\uff01\u9976\u662f\u4ed6\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u4e0d\u89c9\u5446\u4f4f\u3002\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u7334\u5b50\u53c8\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f60\u6349\u5f04\u5b83\u505a\u4ec0 \u4e48\uff1f\u201d\u542c\u5979\u8bf4\u8bdd\uff0c\u7adf\u4f3c\u77e5\u9053\u4ed6\u4ee5\u5f80\u7684\u884c\u5f84\u3002 \u3000\u3000\u91d1\u4e16\u9057\u53c8\u662f\u4e00\u6014\uff0c\u8fd9\u662f\u7b2c\u4e8c\u4e2a\u4e0d\u6015\u9ebb\u75af\u7684\u5c11\u5973\uff0c\u800c\u4e14\u6bd4\u51b0\u5ddd\u5927\u5973\u968f\u4fbf\u5f97\u591a\uff0c\u7b11\u58f0\u4e2d\u5e26\u7740 \u5632\u8bbd\u4e5f\u597d\u4f3c\u65a5\u8d23\uff0c\u4f46\u5374\u50cf\u987d\u7ae5\u6570\u8bf4\u5979\u7684\u540c\u4f34\u4e00\u6837\uff0c\u719f\u7edc\u4e4b\u6781\uff0c\u65e0\u62d8\u65e0\u67ec\u3002\u91d1\u4e16\u9057\u5446\u5446\u5730\u671b\u7740 \u5979\uff0c\u4e00\u65f6\u95f4\u7adf\u4e0d\u77e5\u600e\u6837\u548c\u8fd9\u5973\u5b69\u5b50\u8bf4\u8bdd\u3002\u53ea\u542c\u5f97\u90a3\u5973\u5b69\u5b50\u53c8\u9053\uff1a\u201c\u4f60\u7528\u5f3a\u6700\u591a\u6349\u5230\u4e00\u4e2a\u7334 \u5b50\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u670d\u4f60\uff0c\u8fd9\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u4f60\u770b\u6211\u7684\uff01\u201d\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u563b\u563b\u5730\u7b11\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u597d\uff0c\u6211\u770b\u4f60\u600e\u6837\u6349\u7334\u5b50\uff1f\u201d\u5fc3\u9053\uff1a\u201c\u4f60\u8f7b\u529f\u7eb5\u6bd4\u6211\u597d\uff0c\u96be\u9053\u5c31\u80fd\u4e00\u4e0b\u5b50\u6349 \u5230\u8bb8\u591a\u7334\u5b50\uff1f\u201d\u90a3\u5c11\u5973\u563b\u563b\u5730\u7b11\uff0c\u5531\u9053\uff1a\u201c\u7334\u513f\u53eb\uff0c\u7334\u513f\u8df3\uff0c\u987d\u76ae\u7684\u7334\u513f\u6ca1\u70e6\u607c\u3002\u6765\uff0c\u6765\uff0c \u6765\uff01\u6211\u6709\u679c\u5b50\u7ed9\u4f60\u5403\uff0c\u54b1\u4eec\u4ea4\u4e2a\u597d\u670b\u53cb\uff01\u201d\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u4fbf\u6709\u51e0\u53ea\u7334\u5b50\u4ece\u6811\u6797\u4e2d\u94bb\u51fa\uff0c\u63a5\u7740\u8d8a \u6765\u8d8a\u591a\uff0c\u5728\u5973\u90ce\u9762\u524d\u8df3\u8dc3\u6b22\u53eb\uff0c\u90a3\u5c11\u5973\u62ff\u51fa\u4e00\u5305\u6817\u5b50\u5206\u7ed9\u7334\u98df\uff0c\u7334\u591a\u6817\u5c11\uff0c\u5206\u4e0d\u80dc\u5206\uff0c\u90a3\u4e9b \u7334\u5b50\u771f\u50cf\u548c\u5973\u90ce\u4ea4\u4e0a\u4e86\u670b\u53cb\u4f3c\u7684\uff0c\u6ca1\u6709\u6817\u5b50\uff0c\u4e5f\u4f9d\u604b\u4e0d\u53bb\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u5973\u90ce\u6709\u4ec0\u4e48\u5999\u672f\uff0c\u539f\u6765\u5ce8\u5d4b\u5c71\u4e0a\u7334\u5b50\u5761\u7684\u7334\u5b50\uff0c\u4ece\u4e0d\u6015\u4eba\uff0c\u5b83\u4eec\u548c\u5bfa\u5e99\u91cc\u7684\u548c \u5c1a\u53ae\u6df7\u719f\u4e86\uff0c\u7fa4\u7334\u5e38\u6276\u8001\u643a\u5e7c\u5230\u5bfa\u91cc\u6765\uff0c\u548c\u5c1a\u4e5f\u7ecf\u5e38\u5907\u6709\u4e00\u4e9b\u7c97\u7cae\uff0c\u4ee5\u62db\u5f85\u8fd9\u4e9b\u201c\u4e0d\u901f\u4e4b \u5ba2\u201d\u5b83\u4eec\u4e5f\u4f1a\u5728\u6e38\u5ba2\u9762\u524d\u778e\u620f\uff0c\u535a\u53d6\u98df\u7269\u3002\u9664\u975e\u4f60\u6545\u610f\u5413\u5b83\uff0c\u5426\u5219\u4e0d\u4f1a\u9003\u8dd1\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5446\u5446\u51fa\u795e\uff0c\u53ea\u542c\u5f97\u90a3\u5973\u90ce\u7b11\u9053\uff1a\u201c\u4f60\u5bf9\u5b83\u597d\uff0c\u5b83\u5c31\u5bf9\u4f60\u597d\uff0c\u4f60\u8981\u6b3a\u4fae\u5b83\uff0c\u5b83\u5f53\u7136 \u4e0d\u548c\u4f60\u505a\u670b\u53cb\u3002\u4f60\u600e\u4e48\u8fde\u8fd9\u70b9\u9053\u7406\u90fd\u4e0d\u61c2\u5475\uff1f\u201d\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd9\u8bdd\u8bf4\u7684\u662f\u7334\u5b50\uff0c\u4f46\u5374\u4f55 \u5c1d\u4e0d\u662f\u8bf4\u4eba\uff1f\u91d1\u4e16\u9057\u770b\u5f97\u6709\u8da3\uff0c\u6251\u4e0a\u5c71\u5ca9\uff0c\u4e5f\u60f3\u548c\u7fa4\u7334\u620f\u800d\uff0c\u7fa4\u7334\u8ba4\u5f97\u4ed6\u662f\u9002\u624d\u6b3a\u4fae\u540c\u4f34\u7684 \u201c\u6076\u4eba\u201d\uff0c\u4e0d\u5f85\u4ed6\u6251\u5230\u8ddf\u524d\uff0c\u4fbf\u4e00\u54c4\u800c\u6563\u3002\u5973\u90ce\u6012\u9053\uff1a\u201c\u521a\u73a9\u5f97\u597d\u597d\u7684\uff0c\u4f60\u600e\u4e48\u53c8\u628a\u6211\u7684\u7334 \u513f\u5413\u8dd1\u4e86\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u770b\u5979\u4f6f\u55d4\u8584\u6012\uff0c\u62e6\u7740\u53bb\u8def\uff0c\u7adf\u662f\u6beb\u65e0\u9632\u5907\uff0c\u7a81\u7136\u5012\u6301\u94c1\u62d0\uff0c\u811a\u5c16\u5728\u5ca9\u77f3\u4e0a\u8f7b\u8f7b\u4e00 \u70b9\uff0c\u4f7f\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\u4e4b\u52bf\uff0c\u51ed\u7a7a\u7a9c\u8d77\u4e09\u4e08\u6765\u9ad8\uff0c\u4ed6\u672c\u5728\u5973\u90ce\u4e0b\u9762\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u53cd\u800c\u5c45\u9ad8\u4e34 \u4e0b\uff0c\u5728\u7a7a\u4e2d\u4e00\u6251\uff0c\u7acb\u523b\u7528\u62d0\u67c4\u5012\u52fe\uff0c\u4ed6\u987e\u8651\u7528\u7a7a\u624b\u6349\u4e0d\u7740\u5979\uff0c\u6539\u7528\u94c1\u62d0\uff0c\u4e0d\u557b\u5c06\u624b\u81c2\u7eed\u957f\u5382 \u516b\u5c3a\u3002\u90a3\u5973\u5b50\u53eb\u9053\uff1a\u201c\u597d\u5475\uff0c\u4f60\u771f\u4f1a\u6b3a\u8d1f\u4eba\uff01\u201d\u4e5f\u4e0d\u89c1\u5979\u4f5c\u52bf\uff0c\u8eab\u4e8e\u7a81\u7136\u817e\u7a7a\u98de\u8d77\uff0c\u811a\u5c16\u5728 \u4ed6\u62d0\u4e0a\u4e00\u70b9\u3002\u987a\u52bf\u53c8\u98de\u9ad8\u6570\u4e08\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u659c\u98de\u51fa\u53bb\uff0c\u843d\u4e0b\u5c71\u5761\uff0c\u90a3\u59ff\u52bf\u75be\u4f3c\u7a7a\u4e2d\u98de \u9e1f\uff0c\u7aef\u7684\u7f8e\u5999\u7edd\u4f26\u3002\u91d1\u4e16\u9057\u5728\u5979\u811a\u70b9\u62d0\u6756\u4e4b\u65f6\uff0c\u5de6\u624b\u4e00\u5e26\uff0c\u6ca1\u6709\u5c06\u5979\u5e26\u7740\uff0c\u53ea\u662f\u624b\u6307\u8f7b\u8f7b\u78b0 \u7740\u5979\u7684\u6307\u5c16\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u795e\u4e00\u5206\uff0c\u90a3\u5973\u90ce\u53c8\u5df2\u8eb2\u5165\u68ee\u6797\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u731b\u7136\u7701\u8d77\uff0c\u8fd9\u5973\u5b50\u7684\u8f7b\u529f\uff0c\u81ea\u5df1\u4f3c\u4e4e\u662f\u5728\u54ea\u513f\u89c1\u8fc7\u4e00\u822c\uff0c\u518d\u4e00\u60f3\uff0c\u539f\u6765\u8fd9\u5728\u7a7a\u4e2d \u8f6c\u8eab\u7684\u98de\u6251\u4e4b\u52bf\uff0c\u9177\u4f3c\u732b\u9e70\u3002\u201c\u86c7\u5c9b\u201d\u9644\u8fd1\u6709\u4e2a\u201c\u732b\u9e70\u5c9b\u201d\uff0c\u4e0a\u6709\u602a\u9e1f\uff0c\u5176\u5f62\u4f3c\u732b\uff0c\u5e38\u5e38\u98de \u4e34\u86c7\u5c9b\uff0c\u548c\u7fa4\u86c7\u6076\u6218\uff0c\u6b63\u662f\u6bd2\u86c7\u7684\u514b\u661f\uff0c\u91d1\u4e16\u9057\u5728\u86c7\u5c9b\u5341\u5e74\uff0c\u5df2\u89c1\u8fc7\u4e0d\u5c11\u6b21\u4e86\u3002\u542c\u5e08\u7236\u6bd2\u9f99 \u5c0a\u8005\u8bf4\uff0c\u4ee5\u524d\u5728\u732b\u9e70\u5c9b\u4e0a\uff0c\u6709\u4e00\u5bf9\u53cc\u751f\u5144\u5f1f\uff0c\u540d\u53eb\u8428\u5929\u523a\u3001\u8428\u5929\u90fd\uff0c\u64c5\u957f\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u53ea \u662f\u4e24\u4eba\u65e9\u5df2\u6b7b\u6389\uff0c\u542c\u5e08\u7236\u8bf4\u4ed6\u4eec\u53c8\u6ca1\u7559\u4e0b\u4f20\u4eba\uff0c\u5374\u600e\u7684\u8fd9\u5973\u90ce\u4e5f\u4f1a\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u91d1\u4e16\u9057\u4e0d \u89c9\u5927\u5947\uff0c\u518d\u4e00\u60f3\uff0c\u8fd8\u6709\u66f4\u5947\u602a\u3001\u66f4\u4ee4\u4eba\u83ab\u540d\u5176\u5999\u7684\u4e8b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u7684\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u786e\u662f\u4e16\u95f4\u7f55\u89c1\u7684\u8f7b\u529f\uff0c\u4f46\u5979\u9002\u624d\u5728\u94c1\u62d0\u4e0a \u7684\u90a3\u4e00\u8e0f\uff0c\u529b\u9053\u5374\u4e5f\u4e0d\u89c1\u5f97\u600e\u6837\u5f3a\u52b2\uff0c\u638c\u529b\u4e5f\u4f3c\u4e4e\u8fd8\u6bd4\u4e0d\u4e0a\u6211\uff0c\u8fd9\u662f\u4ec0\u4e48\u7f18\u6545\uff1f\u201d\u987b\u77e5\u5185\u529f \u5f3a\u5f31\uff0c\u4e00\u89e6\u5373\u77e5\uff0c\u534a\u70b9\u4e5f\u63a9\u9970\u4e0d\u5f97\uff0c\u8fd9\u5973\u4e8e\u5728\u4e24\u665a\u4e4b\u95f4\uff0c\u66fe\u4e09\u6b21\u51fa\u73b0\uff0c\u7b2c\u4e00\u6b21\u7528\u98de\u82b1\u4f5c\u4e3a\u6697 \u5668\uff0c\u91d1\u4e16\u9057\u7ed9\u6253\u4e2d\u4e86\u8fd8\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u3002\u7b2c\u4e8c\u6b21\u7528\u6885\u82b1\u51fb\u78b0\u843d\u91d1\u4e16\u9057\u7684\u77f3\u4e8e\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u66f4\u662f \u4e0d\u53ef\u601d\u8bae\u3002\u4f46\u5230\u7b2c\u4e09\u6b21\u51fa\u73b0\uff0c\u5374\u5ffd\u7136\u6bd4\u524d\u4e24\u6b21\u5f31\u4e86\u8bb8\u591a\u3002\u91d1\u4e16\u9057\u5927\u60d1\u4e0d\u89e3\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5979 \u662f\u6545\u610f\u505a\u4f5c\uff1f\u96be\u9053\u5979\u5df2\u505a\u5230\u52b2\u529b\u5927\u5c0f\uff0c\u53d1\u653e\u968f\u5fc3\u7684\u5730\u6b65\uff1f\u4f46\u4ee5\u6211\u73b0\u5728\u7684\u9020\u8be3\uff0c\u5979\u82e5\u662f\u9690\u529b\u4e0d \u53d1\uff0c\u6211\u4e5f\u8be5\u89c9\u5bdf\u51fa\u6765\u3002\u53c8\u96be\u9053\u524d\u4e24\u6b21\u51fa\u73b0\u7684\u5e76\u4e0d\u662f\u5979\uff1f\u201d\u7ec6\u7ec6\u4e00\u60f3\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u4e0d\u4f1a\u5440\uff0c \u4e0d\u4f1a\uff01\u4e16\u754c\u867d\u5927\uff0c\u6709\u4e00\u4e2a\u6b66\u529f\u5982\u6b64\u9ad8\u660e\u7684\u5c11\u5973\uff0c\u5df2\u662f\u51fa\u5947\uff0c\u54ea\u53ef\u80fd\u8fd8\u6709\u4e00\u4e2a\uff1f\u800c\u5de8\u5979\u524d\u4e24\u6b21 \u51fa\u73b0\uff0c\u6211\u867d\u7136\u53ea\u89c1\u80cc\u5f71\uff0c\u672a\u7779\u771f\u5bb9\uff0c\u4f46\u90a3\u8eab\u88c1\u4f53\u6001\uff0c\u524d\u540e\u5374\u662f\u4e00\u6837\uff0c\u8f7b\u529f\u7684\u8def\u6570\uff0c\u4e5f\u5b8c\u5168\u76f8 \u540c\uff0c\u660e\u660e\u662f\u4e00\u4e2a\u4eba\uff0c\u65ad\u65e0\u770b\u9519\u7684\u9053\u7406\u3002\u201d\u4ed6\u8d8a\u60f3\u8d8a\u89c9\u5947\u602a\uff0c\u8fd9\u4e00\u665a\u4ed6\u4e5f\u50cf\u5510\u7ecf\u5929\u4e00\u6837\uff0c\u6ee1\u8179 \u7591\u4e91\uff0c\u5728\u5c71\u6797\u4e2d\u641c\u7d22\u4e86\u4e00\u591c\u3002 \u3000\u3000\u9976\u662f\u91d1\u4e16\u9057\u5982\u4f55\u9b3c\u602a\u7cbe\u7075\uff0c\u6b66\u529f\u8d85\u5353\uff0c\u5374\u662f\u731c\u60f3\u4e0d\u5230\uff1a\u5148\u540e\u51fa\u73b0\u7684\u7adf\u662f\u4e24\u4e2a\u4eba\uff0c\u8fd9\u4e24\u4e2a \u4eba\u4e43\u662f\u6bcd\u5973\u3002\u7528\u98de\u82b1\u620f\u5f04\u91d1\u4e16\u9057\u7684\u662f\u51af\u7433\uff0c\u5f15\u4ed6\u5230\u6811\u6797\u4e2d\u7684\u90a3\u4e2a\u5c11\u5973\u5374\u662f\u51af\u7433\u7684\u5973\u513f\u674e\u6c81\u6885\u3002 \u3000\u3000\u51af\u7433\u5e74\u8fc7\u56db\u65ec\uff0c\u4f46\u5979\u9a7b\u989c\u6709\u672f\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u8fd8\u4f3c\u4e00\u4e2a\u5c11\u5973\u3002\u66f4\u5999\u7684\u662f\uff0c\u5979\u7684\u813e\u6c14\uff0c\u81f3\u8001 \u4e0d\u6539\uff0c\u4e0d\u4f46\u5f62\u8c8c\u4f3c\u5c11\u5973\uff0c\u6027\u60c5\u4e5f\u4f3c\u5c11\u5973\uff0c\u800c\u4e14\u662f\u4e0d\u7ecf\u4e16\u6545\u7684\u987d\u76ae\u5c11\u5973\u3002\u5979\u56e0\u4e3a\u4e00\u518d\u6349\u5f04\u51b0\u5ddd \u5929\u5973\uff0c\u5728\u6155\u58eb\u5854\u683c\u5c71\u9020\u6210\u4e86\u4e00\u573a\u8bef\u4f1a\uff0c\u5c06\u51b0\u5ddd\u5929\u5973\u6c14\u8d70\u3002\u4e8b\u540e\u5979\u59d0\u59d0\u51af\u73bb\u57cb\u6028\u5979\uff0c\u51af\u7433\u770b\u51fa \u5510\u7ecf\u5929\u5bf9\u51b0\u5ddd\u5929\u5973\u7684\u60c5\u612b\uff0c\u5f53\u5373\u5728\u59d0\u59d0\u8ddf\u524d\u8bb8\u8bfa\uff0c\u4e00\u5b9a\u8981\u64ae\u5408\u4ed6\u4e8c\u4eba\u7684\u59fb\u7f18\u3002\u51af\u745b\u77e5\u9053\u59b9\u59b9 \u7684\u813e\u6c14\uff0c\u5e76\u4e0d\u600e\u6837\u5f53\u771f\u3002\u5c82\u77e5\u51af\u7433\u8fd9\u6b21\u5374\u662f\u8bf4\u4e86\u5c31\u505a\uff0c\u7adf\u7136\u6697\u6697\u8ddf\u7740\u5510\u7ecf\u5929\u6765\u5230\u4e86\u5ce8\u5d4b\u5c71\u3002 \u5979\u672c\u6765\u662f\u4e0d\u60f3\u5e26\u5973\u513f\u7684\uff0c\u4f46\u5979\u7684\u5973\u513f\u6bd4\u5979\u8fd8\u8981\u987d\u76ae\uff0c\u4e00\u5b9a\u8981\u8ddf\u5979\u6bcd\u4eb2\u53bb\u77a7\u70ed\u95f9\u3002\u51af\u7433\u88ab\u5979\u7f20 \u4e0d\u8fc7\uff0c\u53ea\u597d\u643a\u5979\u540c\u884c\u3002\u5510\u7ecf\u5929\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u91d1\u4e16\u9057\u4e00\u8def\u6240\u95f9\u4e4b\u4e8b\uff0c\u5979\u5168\u90fd\u770b\u5728\u773c\u91cc\uff0c\u91d1\u4e16\u9057 \u7684\u81ea\u6028\u81ea\u827e\uff0c\u5979\u4e5f\u5168\u542c\u5728\u8033\u4e2d\uff0c\u51af\u7433\u5e7c\u5e74\u7684\u906d\u9047\uff0c\u867d\u7136\u4e0d\u4f3c\u91d1\u4e16\u9057\u7684\u51c4\u60e8\uff0c\u5374\u4e5f\u6709\u76f8\u540c\u4e4b \u5904\uff0c\u5979\u5468\u5c81\u4e4b\u65f6\uff0c\u7236\u4eb2\u60e8\u6b7b\uff0c\u5979\u88ab\u732b\u9e70\u5c9b\u7684\u53cc\u9b54\u8428\u5929\u523a\u3001\u8428\u5929\u90fd\u6349\u53bb\uff0c\u85cf\u5728\u56db\u738b\u4e8e\u5141\u5e27\u5e9c \u4e2d\uff0c\u867d\u7136\u5b66\u5230\u4e86\u8bb8\u591a\u5f02\u6d3e\u6b66\u529f\uff0c\u732b\u9e70\u6251\u51fb\u4e4b\u6280\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4e5f\u53d7\u4e86\u8bb8\u591a\u52ab\u96be\u3002\u6240\u4ee5\u5979\u7aa5\u4f3a \u4e86\u91d1\u4e16\u9057\u591a\u65e5\uff0c\u4e0d\u4f46\u4e0d\u89c9\u5f97\u4ed6\u8ba8\u538c\uff0c\u53cd\u800c\u5927\u6709\u6027\u60c5\u76f8\u6295\u4e4b\u611f\u3002 \u3000\u3000\u6b64\u523b\u5979\u4eec\u6bcd\u5973\u6b63\u5728\u5bc6\u6797\u4e4b\u4e2d\u7ec6\u8bed\uff0c\u51af\u7433\u7b11\u9053\uff1a\u201c\u6211\u4e0a\u6b21\u79bb\u5f00\u5929\u5c71\u4e4b\u540e\uff0c\u4fbf\u542c\u5f97\u6b66\u6797\u540c\u9053 \u8bf4\uff0c\u8bf4\u4e2d\u539f\u51fa\u73b0\u4e86\u4e00\u4e2a\u6bd2\u624b\u75af\u4e10\uff0c\u5341\u6076\u4e0d\u8d66\uff0c\u539f\u6765\u5374\u5c31\u662f\u4ed6\uff01\u5582\uff0c\u4f60\u8bf4\u5f85\u6211\u628a\u4ed6\u620f\u5f04\u591f\u4e86\uff0c \u518d\u5c06\u4ed6\u6740\u6389\uff0c\u597d\u662f\u4e0d\u597d\uff1f\u201d \u3000\u3000\u674e\u6c81\u6094\u53eb\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u6211\u77a7\u4ed6\u602a\u53ef\u601c\u7684\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u770b\u5979\u6bd4\u8868\u54e5\u5982\u4f55\uff1f\u201d\u674e\u6c81\u6885 \u9053\uff1a\u201c\u6b66\u529f\u5012\u662f\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5e74\u7eaa\u4e5f\u5dee\u4e0d\u591a\u3002\u53ea\u662f\u8868\u54e5\u50cf\u4e00\u4e2a\u5927\u4eba\uff0c\u6ca1\u4ed6\u90a3\u6837\u6709\u8da3\u3002\u201d\u51af\u7433\u5ffd \u7684\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u6211\u5c31\u4e0d\u6740\u4ed6\uff0c\u7559\u4ed6\u7ed9\u4f60\u4f5c\u4f34\u513f\u3002\u201d\u674e\u6c81\u6885\u672a\u89e3\u7537\u5973\u4e4b\u60c5\uff0c\u5374\u4e5f \u77e5\u9053\u6bcd\u4eb2\u662f\u5f00\u5979\u73a9\u7b11\uff0c\u6251\u5230\u6bcd\u4eb2\u8eab\u4e0a\uff0c\u4e24\u6bcd\u5973\u95f9\u4f5c\u4e00\u56e2\u3002\u51af\u7433\u9053\uff1a\u201c\u522b\u95f9\uff0c\u522b\u95f9\uff0c\u6211\u6559\u4f60\u4e00 \u4e2a\u620f\u800d\u4ed6\u7684\u6cd5\u5b50\u3002\u201d\u674e\u6c81\u6885\u88ab\u6bcd\u4eb2\u4e00\u54c4\uff0c\u9759\u4e86\u4e0b\u6765\u3002\u51af\u7433\u9053\uff1a\u201c\u4f60\u7684\u8f7b\u529f\u6bd4\u4ed6\u9ad8\u660e\uff0c\u5176\u4ed6\u529f \u592b\uff0c\u5374\u662f\u6709\u6240\u4e0d\u53ca\u3002\u6211\u6559\u4f60\u4e00\u4e2a\u6cd5\u5b50\uff0c\u53eb\u4ed6\u6c38\u8fdc\u4e5f\u6253\u4e0d\u8d62\u4f60\uff0c\u90a3\u4e48\u5c31\u53ea\u6709\u4f60\u620f\u800d\u4ed6\uff0c\u4ed6\u4e0d\u80fd \u620f\u800d\u4f60\u4e86\u3002\u201d\u674e\u6c81\u6885\u4e0d\u76f8\u4fe1\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u5f53\u6211\u662f\u5c0f\u5b69\u5b50\u5417\uff1f\u6b66\u529f\u54ea\u6709\u8fd9\u6837\u5feb\u4fbf\u53ef\u4ee5\u7ec3\u6210\u7684\u9053 \u7406\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u6559\u4f60\u8fd9\u79cd\u6b66\u529f\uff0c\u5c31\u53ea\u80fd\u6253\u8d62\u4ed6\uff0c\u5bf9\u522b\u7684\u4eba\u5374\u6ca1\u6709\u7528\u5904\u7684\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u674e\u6c81\u6885\u89c1\u6bcd\u4eb2\u8bf4\u5f97\u751a\u662f\u8ba4\u771f\uff0c\u534a\u4fe1\u534a\u7591\uff0c\u968f\u6bcd\u4eb2\u5230\u5bc6\u6797\u4e2d\u53bb\u7ec3\u6b66\u529f\u3002\u4e24\u6bcd\u5973\u4e00\u6837\u6027\u60c5\uff0c\u51e1\u4e8b \u4e00\u5f00\u4e86\u5934\uff0c\u5c31\u4e0d\u80fd\u7f62\u4f11\uff0c\u5979\u672c\u6765\u662f\u60f3\u5728\u5192\u5ddd\u751f\u5f00\u5c71\u7ed3\u7f18\u4e4b\u65e5\uff0c\u53bb\u77a7\u70ed\u95f9\u7684\uff0c\u5982\u4eca\u4e00\u65f6\u5174\u8d77\uff0c \u6bcd\u5973\u4fe9\u7ec3\u529f\u7ec3\u5f97\u5165\u8ff7\uff0c\u628a\u5192\u5ddd\u751f\u5f00\u5c71\u7ed3\u7f18\u7684\u5927\u4e8b\u4e5f\u629b\u4e4b\u8111\u540e\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u5510\u7ecf\u5929\u4e0e\u91d1\u4e16\u9057\u90fd\u662f\u5f7b\u591c\u65e0\u7720\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u4fbf\u5230\u4e86\u7b2c\u4e8c\u65e5\u6e05\u6668\uff0c\u662f\u5192\u5ddd\u751f\u5f00\u5c71 \u7ed3\u7f18\u7684\u6b63\u65e5\u4e86\u3002 \u3000\u3000\u6653\u65e5\u900f\u51fa\u4e91\u6d77\uff0c\u5ce8\u5d4b\u5c71\u91d1\u5149\u5bfa\u54cd\u8d77\u4e86\u4e00\u767e\u96f6\u516b\u54cd\u949f\u58f0\uff0c\u5927\u96c4\u5b9d\u6bbf\u6253\u626b\u7684\u5e72\u5e72\u51c0\u51c0\uff0c\u5f00\u59cb \u63a5\u5f85\u4ece\u5404\u5730\u800c\u6765\u8981\u5411\u5192\u5ddd\u751f\u9886\u76ca\u7684\u6b66\u6035\u597d\u624b\u3002\u8fd9\u6b21\u6765\u7684\u4eba\u7279\u522b\u591a\uff0c\u56e0\u4e3a\u5192\u5ddd\u751f\u662f\u6b66\u5f53\u6d3e\u8f88\u4efd \u6700\u9ad8\u7684\u4eba\uff0c\u6240\u4ee5\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u81ea\u52a8\u7684\u62c5\u4efb\u4e86\u62db\u5f85\u4e4b\u804c\uff0c\u9760\u8fd1\u8bb2\u575b\u7684\u5730\u65b9\u4e5f\u90fd\u7ed9\u4ed6\u4eec\u5360\u636e\u3002\u5510 \u7ecf\u5929\u6df7\u6742\u5728\u5bbe\u5ba2\u4e4b\u4e2d\uff0c\u89c1\u8fd9\u60c5\u5f62\uff0c\u4e0d\u7981\u6697\u53f9\u6b66\u5f53\u6d3e\u5357\u652f\u7684\u4eba\u624d\u96f6\u843d\uff0c\u4e0d\u8bf4\u6b66\u529f\uff0c\u5728\u6c14\u5ea6\u4e0a\uff0c \u6b66\u5f53\u6d3e\u7684\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u8db3\u4ee5\u7ee7\u627f\u524d\u8d24\u3002 \u3000\u3000\u6b63\u5728\u4e3e\u5ea7\u8083\u9759\uff0c\u9759\u5f85\u5192\u5ddd\u751f\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u6bbf\u5802\u5916\u563b\u563b\u54c8\u54c8\u7684\u5b09\u7b11\u4e4b\u58f0\uff0c\u95f9\u6210\u4e00\u7247\u3002\u96f7 \u9707\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u62a2\u51fa\u53bb\u770b\uff0c\u53ea\u89c1\u5341\u51e0\u4e2a\u540c\u95e8\uff0c\u624b\u821e\u8db3\u8e48\uff0c\u8df3\u4e0a\u8df3\u4e0b\uff0c\u4e0d\u7528\u8bf4\u53c8\u662f\u90a3\u75af\u4e10 \u5f04\u7684\u628a\u620f\u4e86\u3002\u6b66\u5f53\u5f1f\u5b50\u5927\u611f\u9762\u4e0a\u65e0\u5149\uff0c\u624b\u8db3\u65e0\u63aa\uff0c\u53ea\u542c\u5f97\u5bbe\u5ba2\u4e2d\u6709\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4eea\u793c \u5440\uff1f\u201d\u5510\u7ecf\u5929\u6025\u5fd9\u8d8a\u4f17\u800c\u51fa\uff0c\u5411\u7740\u90a3\u51e0\u4e2a\u5982\u4e2d\u75af\u90aa\u7684\u6b66\u5f53\u5f1f\u5b50\uff0c\u5de6\u6253\u4e00\u62f3\uff0c\u53f3\u6253\u4e00\u62f3\uff0c\u4f17\u4eba \u54d7\u7136\u5927\u547c\uff0c\u968f\u96f7\u9707\u4e8e\u4e00\u540c\u51fa\u6765\u7684\u56db\u5927\u5f1f\u5b50\u4fbf\u60f3\u4e0a\u524d\u52a8\u624b\uff0c\u96f7\u9707\u5b50\u9762\u8272\u94c1\u9752\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u522b \u4eba\u51fa\u624b\u76f8\u6551\uff0c\u4f60\u4eec\u4e5f\u77a7\u4e0d\u51fa\u6765\u5417\uff1f\u201d\u679c\u7136\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u90a3\u5341\u51e0\u4e2a\u6b66\u5f53\u5f1f\u5b50\u90fd\u590d\u4e86\u539f\u72b6\u3002\u539f\u6765 \u5510\u7ecf\u5927\u540c\u4e3a\u6765\u4e0d\u53ca\u66ff\u4ed6\u4eec\u4e00\u4e2a\u4e2a\u201c\u89e3\u7a74\u201d\uff0c\u8feb\u5f97\u7528\u201c\u795e\u62f3\u89e3\u7a74\u201d\u7684\u672c\u9886\u3002\u4ee5\u5185\u5bb6\u771f\u529b\uff0c\u5728\u5239 \u90a3\u4e4b\u95f4\uff0c\u51b2\u5f00\u5404\u4eba\u7684\u7a74\u9053\u3002\u5404\u6d3e\u9ad8\u624b\u4e2a\u4e2a\u60ca\u5947\uff0c\u6b63\u5728\u55a7\u95f9\u4e4b\u65f6\uff0c\u91cc\u9762\u949f\u58f0\u9e23\u53eb\uff0c\u5192\u5ddd\u751f\u5c31\u5c06 \u5f00\u575b\u4e86\u3002 \u3000\u3000\u5192\u5ddd\u751f\u662f\u4e2d\u539f\u516c\u8ba4\u7684\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\uff0c\u6bcf\u5341\u5e74\u5f00\u5c71\u4e00\u6b21\uff0c\u4ed6\u6625\u79cb\u5df2\u9ad8\uff0c\u8fd9\u6b21\u5f00\u5c71\u4e4b\u540e\uff0c\u53ea \u6015\u672a\u5fc5\u6709\u4e0b\u4e00\u6b21\u4e86\u3002\u662f\u4ee5\u5404\u6d3e\u9ad8\u624b\uff0c\u4e00\u542c\u949f\u58f0\uff0c\u7acb\u5373\u8083\u9759\u65e0\u58f0\uff0c\u4f9d\u6b21\u5165\u5ea7\uff0c\u5510\u7ecf\u5929\u4e5f\u56e0\u6b64\u514d \u4e86\u88ab\u4eba\u67e5\u95ee\uff0c\u5f53\u4ea6\u6df7\u5728\u4f17\u4eba\u4e4b\u4e2d\u3002\u5510\u7ecf\u5929\u6697\u6697\u7559\u795e\uff0c\u53ea\u89c1\u5750\u5728\u524d\u9762\u51e0\u6392\u7684\uff0c\u5341\u4e5d\u90fd\u662f\u90aa\u91cc\u90aa \u6c14\uff0c\u4e0e\u8654\u8bda\u542c\u9053\u7684\u4eba\uff0c\u4e00\u773c\u5c31\u53ef\u4ee5\u5206\u522b\u51fa\u6765\uff0c\u5510\u7ecf\u5927\u5fc3\u4e2d\u53f9\u9053\uff1a\u201d\u6811\u5927\u62db\u98ce\uff0c\u9ad8\u540d\u62db\u5992\uff0c\u8fd9 \u8bdd\u5012\u771f\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u949f\u58f0\u63a5\u8fde\u54cd\u4e86\u5341\u516b\u4e0b\uff0c\u91d1\u5149\u5bfa\u7684\u8001\u65b9\u4e08\u5c06\u5192\u5ddd\u751f\u5f15\u51fa\u8bb2\u575b\uff0c\u5510\u7ecf\u5929\u4e00\u770b\uff0c\u53ea\u4ef6\u5192\u5ddd\u751f\u76f8 \u8c8c\u6e05\u766f\uff0c\u987b\u7709\u7686\u767d\uff0c\u6ee1\u9762\u6148\u7965\u4e4b\u6c14\uff0c\u767b\u4e0a\u8bb2\u575b\uff0c\u53cc\u76ee\u795e\u5149\uff0c\u8fde\u5750\u5728\u6700\u8fdc\u7684\u4eba\u90e8\u89c9\u5f97\u5192\u5ddd\u751f\u770b \u89c1\u6211\u4e86\u3002\u53ea\u542c\u5f97\u5192\u5ddd\u751f\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6b66\u5b66\u4e4b\u9053\uff0c\u6709\u5982\u5927\u6d77\u65e0\u6daf\uff0c\u8001\u673d\u867d\u75f4\u957f\u51e0\u5e74\uff0c\u5176\u5b9e\u4ea6\u4e0d \u8fc7\u7565\u7aa5\u85e9\u7bf1\uff0c\u4e0d\u8db3\u8a00\u9053\u3002\u4eca\u6b21\u5f00\u5c71\u7ed3\u7f18\uff0c\u975e\u6562\u597d\u4e3a\u4eba\u5e08\uff0c\u4e0d\u8fc7\u4e92\u76f8\u5207\u78cb\u800c\u5df2\u3002\u201d\u5192\u5ddd\u751f\u7684\u5f00 \u5c71\u7ed3\u7f18\uff0c\u8d77\u6e90\u662f\u7531\u4e8e\u672c\u6d3e\u5f1f\u5b50\u8bf7\u4ed6\u5b9a\u671f\u8bb2\u6388\u6b66\u529f\uff0c\u540e\u6765\u5404\u6d3e\u95fb\u98ce\u800c\u6765\uff0c\u8fd9\u624d\u6269\u5927\u4e86\u6210\u4e3a\u4e86\u6bcf \u5341\u5e74\u4e00\u6b21\u7684\u76db\u4f1a\uff0c\u90a3\u662f\u5404\u6d3e\u627f\u8ba4\u4ed6\u8db3\u4e3a\u5e08\u8303\u7684\u3002\u5982\u4eca\u542c\u4ed6\u8bf4\u4e86\u8fd9\u4e00\u756a\u8bdd\uff0c\u771f\u662f\u8c26\u51b2\u81ea\u7267\uff0c\u4e0d \u69d0\u6709\u9053\u4e4b\u8a00\u3002\u8fde\u5b58\u5fc3\u6765\u6311\u8845\u7684\u4e5f\u81ea\u5fc3\u4e2d\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u90a3\u5927\u96c4\u5b9d\u6bbf\u957f\u5bbd\u5404\u5341\u4f59\u4e08\uff0c\u5192\u5ddd\u751f\u8bf4\u8bdd\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u6bbf\u4e2d\u6bcf\u4e00\u4e2a\u4eba\u542c\u6765\uff0c\u58f0\u97f3\u4e00\u6837\u5927 \u5c0f\uff0c\u4e00\u822c\u6e05\u695a\u3002\u5510\u7ecf\u5929\u5927\u4e3a\u5fc3\u6298\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5192\u8001\u524d\u8f88\u679c\u7136\u662f\u540d\u4e0b\u65e0\u865a\uff0c\u867d\u5728\u66ae\u5e74\uff0c\u4e2d\u6c14 \u7684\u5145\u6c9b\uff0c\u5374\u4e5f\u4e0d\u5728\u6211\u7239\u7239\u4e4b\u4e0b\u3002\u201d\u8981\u77e5\u5185\u529f\u6709\u9020\u8be3\u7684\u4eba\uff0c\u56fa\u7136\u53ef\u4ee5\u4f20\u58f0\u53ca\u8fdc\uff0c\u4f46\u50cf\u5192\u5ddd\u751f\u8fd9 \u6837\u7684\u5728\u5927\u6bbf\u7684\u8bb2\u575b\u4e0a\u8bf4\u8bdd\uff0c\u8fd1\u8eab\u7684\u4eba\u5fc5\u6709\u9707\u8033\u5982\u96f7\u4e4b\u611f\uff0c\u5750\u5728\u4e2d\u95f4\u7684\u4eba\u53c8\u5fc5\u7136\u906d\u53d7\u56de\u58f0\u7684\u5e72 \u6270\uff0c\u5750\u5728\u540e\u6392\u7684\u4eba\u5219\u4e00\u5b9a\u6709\u523a\u8033\u4e4b\u611f\u3002\u4f46\u5192\u5ddd\u751f\u5374\u5982\u5bb6\u5e38\u95f2\u8bdd\uff0c\u4e0d\u75be\u4e0d\u5f90\uff0c\u8fdc\u8fd1\u5404\u4eba\uff0c\u90fd\u50cf \u662f\u611f\u5230\u4ed6\u5c31\u5750\u5728\u5bf9\u9762\u548c\u81ea\u5df1\u8c08\u5929\u4e00\u6837\uff0c\u4e1d\u6beb\u6ca1\u6709\u8fd0\u7528\u5185\u529f\u4ee5\u6c14\u4f20\u58f0\u7684\u611f\u89c9\u3002\u8fd9\u6b63\u662f\u5185\u529f\u5df2\u7ec3 \u5230\u5316\u5883\uff0c\u624d\u80fd\u8fbe\u5230\u7684\u5883\u754c\u3002 \u3000\u3000\u5192\u5ddd\u751f\u63a5\u7740\u8bb2\u4e86\u4e00\u6bb5\u300a\u6613\u7b4b\u7ecf\u300b\u7684\u7cbe\u4e49\uff0c\u5185\u529f\u6709\u4e86\u9020\u8be3\u7684\u4eba\uff0c\u56fa\u7136\u9886\u609f\u5f97\u591a\uff0c\u521d\u5b66\u4e4b \u58eb\uff0c\u4e5f\u4ece\u4e2d\u609f\u5230\u8bb8\u591a\u6b66\u5b66\u7684\u539f\u7406\uff0c\u4ea6\u662f\u83b7\u76ca\u4e0d\u6d45\u3002\u5192\u5ddd\u751f\u8bb2\u5b8c\u90a3\u4e00\u6bb5\u300a\u6613\u7b4b\u7ecf\u300b\u540e\uff0c\u6309\u7167\u4ee5 \u5f80\u7684\u89c4\u77e9\uff0c\u5f00\u59cb\u6bcf\u65e5\u7684\u201c\u7ed3\u7f18\u201d\u3002\u7531\u8bf7\u76ca\u7684\u4eba\u5c06\u4ed6\u672c\u8eab\u6700\u64c5\u957f\u7684\u6b66\u529f\u6f14\u7ec3\u51fa\u6765\uff0c\u8bf7\u5192\u5ddd\u751f\u6307 \u70b9\u3002\u8fd9\u6b21\u4ecd\u4f9d\u5f80\u4f8b\uff0c\u7531\u6b66\u5f53\u6d3e\u540e\u4e00\u8f88\u7684\u9996\u5e2d\u5f1f\u5b50\u5148\u884c\u8bf7\u6559\u3002\u96f7\u9707\u5b50\u662f\u4eca\u6b21\u8d74\u4f1a\u7684\u6b66\u5f53\u6d3e\u7b2c\u4e8c \u4ee3\u5927\u5f1f\u5b50\uff0c\u9042\u51fa\u6765\u7ec3\u4e86\u4e00\u5957\u6b66\u5f53\u6d3e\u7684\u201c\u4e5d\u5bab\u516b\u5366\u638c\u201d\u3002\u53ea\u89c1\u4ed6\u6b65\u4f3c\u733f\u7334\uff0c\u62f3\u5982\u864e\u8c79\uff0c\u6253\u6765\u751a \u6709\u5a01\u52bf\u3002\u4f46\u8d74\u4f1a\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u5374\u662f\u6697\u6697\u8be7\u5f02\uff0c\u5927\u5bb6\u90fd\u770b\u51fa\u4e86\u96f7\u9707\u5b50\u5185\u52b2\u4e0d\u8db3\u7684\u7f3a\u70b9\u3002\u96f7\u9707\u5b50 \u5728\u4e2d\u539f\u6b66\u6797\u4e2d\u4e5f\u7b97\u5f97\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u719f\u6089\u4ed6\u6b66\u529f\u7684\u4eba\u4ee5\u53ca\u4e0a\u4e00\u5c4a\u770b\u8fc7\u4ed6\u6f14\u6b66\u7684\u4eba\uff0c\u90fd\u89c9\u5f97\u4ed6\u8fd9 \u5341\u5e74\u6765\u4e0d\u4f46\u6ca1\u6709\u8fdb\u5883\uff0c\u53cd\u800c\u9000\u6b65\u8bb8\u591a\uff0c\u6309\u7406\u6765\u8bf4\uff0c\u7ec3\u6b66\u4e4b\u4eba\uff0c\u62f3\u4e0d\u79bb\u624b\uff0c\u5373\u7b97\u8fdb\u6b65\u4e0d\u5927\uff0c\u4ea6 \u65ad\u65e0\u9000\u6b65\u4e4b\u7406\u3002 \u3000\u3000\u4f17\u4eba\u4e0d\u660e\u5176\u4e2d\u9053\u7406\uff0c\u5510\u7ecf\u5929\u5374\u662f\u5fc3\u4e2d\u55df\u53f9\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u6628\u665a\u53d7\u4e86\u8d64\u795e\u5b50\u7684\u4e00\u62f3\uff0c\u53c8\u88ab\u91d1 \u4e16\u9057\u8fde\u70b9\u4e09\u5904\u7a74\u9053\uff0c\u867d\u7136\u5f97\u6211\u6551\u6cbb\uff0c\u5143\u6c14\u5374\u662f\u635f\u8017\u4e0d\u5c11\u3002\u201d \u3000\u3000\u96f7\u9707\u5b50\u5c06\u4e00\u5957\u201c\u4e5d\u5bab\u516b\u5366\u638c\u201d\u7ec3\u5b8c\u4e4b\u540e\uff0c\u5782\u624b\u606d\u7acb\u575b\u4e0b\uff0c\u8bf7\u7956\u5e08\u6307\u70b9\u3002\u5192\u5ddd\u751f\u53cc\u773c\u4e00 \u5f20\uff0c\u76ee\u5149\u95ea\u7535\u822c\u7684\u5728\u4ed6\u8eab\u4e0a\u626b\u8fc7\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u638c\u6cd5\u4e5f\u8fd8\u7eaf\u719f\uff0c\u4f46\u6b66\u5f53\u6d3e\u8fd9\u5957\u638c\u6cd5\uff0c\u5176\u4e2d\u5374 \u662f\u5939\u6709\u70b9\u7a74\u6cd5\u7684\uff0c\u8981\u638c\u6307\u5e76\u7528\uff0c\u4f60\u7684\u70b9\u7a74\u6cd5\u90a3\u8fd8\u5dee\u5f97\u5f88\u8fdc\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5ea7\u4e2d\u9ad8\u624b\u751a\u662f\u8be7 \u5f02\uff0c\u96f7\u9707\u5b50\u7684\u7f3a\u70b9\uff0c\u660e\u660e\u662f\u5185\u52b2\u4e0d\u8db3\uff0c\u4ed6\u5374\u6307\u6458\u4ed6\u7684\u70b9\u7a74\u6cd5\uff0c\u8fd9\u4e0d\u7b49\u4e8e\u8001\u5b98\u8bc4\u5377\uff0c\u5c06\u597d\u7684\u8bf4 \u574f\uff0c\u574f\u5904\u5374\u53cd\u800c\u770b\u4e0d\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u96f7\u9707\u5b50\u867d\u7136\u4e0d\u5927\u670d\u6c14\uff0c\u8fd8\u662f\u606d\u606d\u656c\u656c\u7684\u8bf4\u9053\uff1a\u201c\u8bf7\u7956\u5e08\u6307\u70b9\u3002\u201d\u5192\u5ddd\u751f\u8bf4\u9053\uff1a\u201c\u4f60\u8d70\u8fd1 \u4e86\u6765\uff0c\u77a7\u6e05\u695a\u4e86\uff01\u201d\u4ed6\u4ecd\u7136\u7aef\u5750\u8bb2\u575b\uff0c\u7a81\u7136\u4f38\u624b\u4e00\u70b9\uff0c\u5c31\u70b9\u7740\u4e86\u96f7\u9707\u5b50\u624b\u8155\u4e0a\u7684\u4e09\u7126\u7a74\uff0c\u96f7 \u9707\u5b50\u7a81\u7136\u4e00\u9707\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u5192\u5ddd\u751f\u53cd\u624b\u4e00\u70b9\uff0c\u53c8\u70b9\u7740\u4e86\u4ed6\u80cc\u810a\u7684\u5929\u67f1\u7a74\uff0c\u5192\u5ddd\u751f\u51fa\u624b\u6216\u7f13\u6216 \u75be\uff0c\u8eab\u4e0d\u79bb\u5e2d\uff0c\u811a\u4e0d\u6cbe\u5730\uff0c\u70b9\u4e00\u4e0b\uff0c\u96f7\u9707\u4e8e\u8df3\u4e00\u4e0b\uff0c\u4efb\u4ed6\u8df3\u8361\u4e0d\u4f11\uff0c\u5192\u5ddd\u751f\u6bcf\u51fa\u4e00\u6307\uff0c\u90fd\u5fc5 \u7136\u70b9\u4e2d\u4ed6\u4e00\u5904\u7a74\u9053\uff0c\u52b2\u529b\u53c8\u7528\u5f97\u5999\u6781\uff0c\u7edd\u4e0d\u4ee4\u96f7\u9707\u5b50\u53d7\u4f24\u3002\u4f17\u9ad8\u624b\u5927\u5f00\u773c\u754c\uff0c\u90fd\u5728\u6697\u9053\uff1a \u201c\u70b9\u7a74\u6cd5\u7adf\u7136\u6709\u8fd9\u6837\u795e\u5999\u7684\uff01\u201d\u4f46\u5fc3\u4e2d\u5374\u662f\u4e0d\u80fd\u65e0\u7591\uff1a\u201c\u5192\u5ddd\u751f\u7684\u70b9\u7a74\u6cd5\uff0c\u90a3\u662f\u6570\u5341\u5e74\u529f\u529b \u4e4b\u6240\u805a\uff0c\u96f7\u9707\u5b50\u8981\u5b66\u4e5f\u5b66\u4e0d\u6765\uff0c\u50cf\u96f7\u9707\u5b50\u9002\u624d\u6240\u6f14\u7684\u70b9\u7a74\u6cd5\uff0c\u5b9e\u5728\u4e5f\u4e0d\u5e94\u8bf4\u4ed6\u5dee\u5f97\u592a\u8fdc \u4e86\u3002\u201d\u6b66\u5b66\u4e4b\u9053\uff0c\u5e94\u8be5\u6839\u636e\u4ed6\u90a3\u4e00\u7ea7\u7684\u7a0b\u5ea6\u6765\u8bc4\u8bba\uff0c\u6bd4\u5982\u7ae5\u751f\u7684\u6587\u7ae0\uff0c\u53ea\u8981\u80fd\u591f\u901a\u987a\uff0c\u4fbf\u53ef \u201c\u8d34\u5802\u201d\uff0c\u5c82\u80fd\u62ff\u53bb\u4e0e\u72b6\u5143\u7684\u6587\u7ae0\u76f8\u6bd4\uff1f\u6240\u4ee5\u4e00\u4f17\u9ad8\u624b\uff0c\u867d\u7136\u5bf9\u5192\u5ddd\u751f\u7684\u70b9\u7a74\u6cd5\uff0c\u4f69\u670d\u5f97\u4e94 \u4f53\u6295\u5730\uff0c\u4f46\u5bf9\u4ed6\u7684\u8bc4\u8bba\uff0c\u5374\u4ecd\u662f\u6709\u6240\u975e\u8bae\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5374\u662f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u89c9\u5192\u5ddd\u751f\u7684\u773c\u5149\u4f3c\u4e4e\u662f\u5728\u6709\u610f\u65e0\u610f\u4e4b\u95f4\uff0c\u77a7\u4e86\u81ea\u5df1\u4e24\u773c\uff0c\u4ed4\u7ec6 \u770b\u65f6\uff0c\u5192\u5ddd\u751f\u70b9\u96f7\u9707\u5b50\u7684\u9ebb\u7a74\u3001\u75d5\u75d2\u7a74\u3001\u7b11\u8170\u7a74\u4e09\u5904\uff0c\u7528\u7684\u5168\u662f\u53cd\u624b\u6307\u6cd5\uff0c\u9006\u70b9\u7a74\u9053\uff0c\u5510\u7ecf \u5929\u7684\u5185\u529f\u9020\u8be3\u4ee5\u53ca\u70b9\u7a74\u7684\u529f\u5927\uff0c\u81ea\u7136\u8fdc\u975e\u96f7\u9707\u5b50\u53ef\u53ca\uff0c\u8fd9\u4e00\u770b\u7acb\u5373\u9886\u609f\uff0c\u770b\u6765\u5192\u5ddd\u751f\u7684\u70b9\u7a74 \u6cd5\uff0c\u6b63\u6070\u5de7\u5c31\u662f\u7834\u91d1\u4e16\u9057\u90a3\u79cd\u72ec\u95e8\u70b9\u7a74\u6cd5\u7684\u529f\u592b\uff0c\u518d\u770b\u4ed6\u70b9\u5176\u4ed6\u7a74\u9053\uff0c\u65e0\u4e00\u4e0d\u662f\u514b\u91d1\u4e16\u9057\u7684 \u70b9\u7a74\u6cd5\u7684\uff0c\u5510\u7ecf\u5929\u4e0d\u89c9\u5927\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5192\u8001\u524d\u8f88\u89c1\u8fc7\u91d1\u4e16\u9057\u4e86\uff1f\u96be\u9053\u4ed6\u662f\u6709\u5fc3\u4f20\u6388\u6211 \u4e48\uff1f\u201d\u5510\u7ecf\u5929\u4e0e\u91d1\u4e16\u9057\u529f\u529b\u6089\u654c\uff0c\u5404\u6709\u6240\u957f\uff0c\u5510\u7ecf\u5929\u987e\u5fcc\u91d1\u4e16\u9057\u7684\u6b79\u6bd2\u6697\u5668\u548c\u6bd2\u9f99\u70b9\u7a74\u6cd5\uff0c \u91d1\u4e16\u9057\u4e5f\u987e\u5fcc\u4ed6\u7684\u5929\u5c71\u795e\u8292\u548c\u987b\u5f25\u5251\u6cd5\u3002\u800c\u4eca\u5510\u7ecf\u5929\u5728\u70b9\u7a74\u6cd5\u4e0a\u9886\u609f\u5230\u4e86\u514b\u5236\u91d1\u4e16\u9057\u4e4b\u9053\uff0c \u5176\u4ed6\u6b66\u529f\u867d\u7136\u4e0e\u91d1\u4e16\u9057\u662f\u534a\u65a4\u516b\u4e24\uff0c\u4f46\u70b9\u7a74\u7684\u529f\u592b\u7a0d\u80dc\uff0c\u81ea\u4ed8\u4e0b\u6b21\u76f8\u9047\uff0c\u4fbf\u6709\u4e86\u53d6\u80dc\u7684\u53ef \u80fd\uff0c\u4e0d\u89c9\u5927\u4e3a\u5174\u594b\uff1a\u4e8e\u662f\u4e00\u9762\u6697\u8bb0\u5192\u5ddd\u751f\u7684\u624b\u6cd5\uff0c\u4e00\u9762\u63e3\u6478\u4ed6\u52b2\u529b\u5927\u5c0f\u7684\u5de7\u5999\u5730\u65b9\u3002\u4e00\u76f4\u770b \u5b8c\u5192\u5ddd\u751f\u70b9\u5b8c\u4e86\u96f7\u9707\u5b50\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u8fde\u773c\u775b\u4e5f\u4e0d\u7728\u4e00\u4e0b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u81ea\u4ee5\u4e3a\u5df2\u9886\u609f\u4e86\u5192\u5ddd\u751f\u70b9\u7a74\u7684\u795e\u5999\u4e4b\u5904\uff0c\u4f46\u8fd8\u6709\u4e00\u6837\u5999\u5904\uff0c\u5374\u8fde\u5510\u7ecf\u5929\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u96f7\u9707\u5b50\u5148\u524d\u4e0d\u5927\u670d\u6c14\uff0c\u88ab\u5192\u5ddd\u751f\u4e00\u70b9\u4e4b\u540e\uff0c\u5168\u8eab\u9707\u52a8\uff0c\u53ea\u89c9\u4e00\u80a1\u70ed\u6c14\uff0c\u81ea\u6240\u70b9\u7a74\u9053\u4e4b \u5904\uff0c\u76f4\u4f20\u5230\u5fc3\u7530\uff0c\u53ea\u70b9\u4e86\u51e0\u4e0b\uff0c\u9633\u77eb\u8109\u7acb\u523b\u7545\u901a\uff0c\u518d\u51e0\u4e0b\uff0c\u9634\u7ef4\u8109\u7684\u8df3\u52a8\u4e5f\u7531\u5f31\u800c\u5f3a\u3002\u665a\u4e0a \u4ed6\u53d7\u4e86\u8d64\u795e\u5b50\u4e00\u638c\uff0c\u70ed\u6c14\u653b\u5fc3\uff0c\u5c24\u4ee5\u9634\u77eb\u8109\u548c\u9634\u7ef4\u8109\u53d7\u635f\u6700\u751a\uff0c\u867d\u5f97\u5510\u7ecf\u5929\u7684\u5929\u5c71\u96ea\u83b2\u89e3 \u6551\uff0c\u8fd9\u4e24\u5904\u7ecf\u8109\u4ecd\u662f\u963b\u6ede\u4e0d\u901a\uff0c\u5982\u4eca\u88ab\u5192\u5ddd\u751f\u4e00\u70b9\uff0c\u770b\u4f3c\u70b9\u7a74\uff0c\u5b9e\u5374\u662f\u66ff\u4ed6\u89e3\u7a74\uff0c\u975e\u4f46\u5982 \u6b64\uff0c\u800c\u4e14\u66ff\u4ed6\u52a0\u5f3a\u5404\u5904\u7ecf\u8109\u7684\u8fd0\u884c\uff0c\u4ee4\u96f7\u9707\u5b50\u672c\u8eab\u6240\u5177\u6709\u7684\u5185\u5bb6\u771f\u6c14\u8fc5\u5373\u7591\u56fa\uff0c\u8fd9\u4e00\u4e0b\u7b49\u4e8e \u52a9\u957f\u4e86\u4ed6\u4e09\u5e74\u7684\u529f\u529b\uff0c\u5f97\u76ca\u4e4b\u5927\uff0c\u4e0d\u8a00\u53ef\u55bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5192\u5ddd\u751f\u70b9\u5b8c\u4e86\u96f7\u9707\u5b50\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u4e0d\u4f46\u9633\u77eb\u8109\u9634\u7ef4\u8109\u7531\u5f31\u800c\u5f3a\uff0c\u5176\u4ed6\u5404 \u5904\u7ecf\u8109\uff0c\u5982\u4efb\u8109\u3001\u5e26\u8109\u3001\u51b2\u8109\u3001\u7763\u8109\u3001\u8db3\u5c11\u9633\u80be\u7ecf\u8109\u3001\u624b\u5c11\u9633\u4e09\u7126\u7ecf\u8109\u2026\u2026\u7b49\u7b49\uff0c\u65e0\u4e0d\u7545 \u901a\uff0c\u53ea\u89c9\u65e0\u9650\u8212\u670d\u3002\u65c1\u89c2\u9ad8\u624b\u4f46\u89c1\u970d\u9707\u5b50\u8df3\u8361\u4e0d\u4f11\uff0c\u547c\u5438\u6c14\u606f\u6781\u91cd\uff0c\u53e3\u4e2d\u4e0d\u65ad\u55b7\u51fa\u70ed\u6c14\uff0c\u8fd8 \u4ee5\u4e3a\u4ed6\u4e0d\u80dc\u6307\u529b\uff0c\u54ea\u77e5\u4ed6\u5374\u662f\u5f97\u4e86\u5192\u5ddd\u751f\u4e4b\u52a9\uff0c\u5c06\u8d64\u795e\u5b50\u7684\u638c\u6bd2\u4e0e\u7559\u5728\u8eab\u4e2d\u7684\u90aa\u6c14\u5168\u90e8\u9a71\u51fa \u4e86\u3002 \u3000\u3000\u5192\u5ddd\u751f\u4e00\u7b11\u655b\u624b\uff0c\u6c14\u5b9a\u795e\u95f2\uff0c\u4e00\u6d3e\u82e5\u65e0\u5176\u4e8b\u7684\u6837\u5b50\uff0c\u4ecd\u7136\u7aef\u5750\u8bb2\u575b\u7684\u84b2\u56e2\u4e4b\u4e0a\uff0c\u5fae\u7b11\u95ee \u9053\uff1a\u201c\u9886\u609f\u4e86\u4e48\uff1f\u201d\u96f7\u9707\u5b50\u606d\u8eab\u8bf4\u9053\uff1a\u201c\u9886\u609f\u4e86\uff01\u201d\u5192\u5ddd\u751f\u5f90\u5f90\u8bf4\u9053\uff1a\u201c\u6015\u672a\u5fc5\u5462\uff0c\u4e0d\u8fc7\u4f60 \u9886\u609f\u51e0\u5206\uff0c\u4e5f\u4e0d\u9519\u4e86\u3002\u201d\u5510\u7ecf\u5929\u6b63\u81ea\u51fa\u795e\uff0c\u5728\u5fc3\u4e2d\u590d\u4e60\u5192\u5ddd\u751f\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u542c\u8fd9\u6570\u8a00\uff0c\u76f4\u523a \u8033\u9f13\uff0c\u62ac\u8d77\u773c\u775b\uff0c\u5ffd\u89c9\u5192\u5ddd\u751f\u7684\u76ee\u5149\u53c8\u4f3c\u505c\u7559\u5728\u81ea\u5df1\u7684\u9762\u4e0a\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u8fd9\u8bdd\u5927 \u7ea6\u662f\u8bf4\u7ed9\u6211\u542c\u7684\u3002\u96f7\u9707\u5b50\u54ea\u80fd\u9886\u609f\uff1f\u201d\u6b8a\u4e0d\u77e5\u4ed6\u548c\u96f7\u9707\u5b50\u90fd\u53ea\u662f\u9886\u609f\u4e86\u4e00\u534a\uff0c\u5192\u5ddd\u751f\u8fd9\u6b21\u51fa \u624b\u70b9\u7a74\uff0c\u5b9e\u662f\u4e00\u4e3e\u4e09\u7528\uff0c\u4e00\u8005\u662f\u5411\u8d74\u4f1a\u5b58\u5fc3\u6311\u8845\u7684\u7fa4\u90aa\u793a\u5a01\uff0c\u8ba9\u4ed6\u4eec\u5927\u5f00\u773c\u754c\uff1b\u4e8c\u8005\u662f\u6697\u6388 \u5510\u7ecf\u5929\u514b\u5236\u91d1\u4e16\u9057\u7684\u70b9\u7a74\u6cd5\uff1b\u4e09\u8005\u662f\u501f\u6b64\u66ff\u96f7\u9707\u5b50\u6062\u590d\u5143\u6c14\u3002\u80fd\u5b8c\u5168\u9886\u609f\u5192\u5ddd\u751f\u7684\u5999\u7528\u8005\uff0c \u5ea7\u4e2d\u5e76\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u96f7\u9707\u4e8e\u6b63\u60f3\u5f52\u5ea7\uff0c\u7b2c\u4e8c\u6392\u4e2d\u8df3\u51fa\u4e00\u4eba\uff0c\u6717\u58f0\u62a5\u9053\uff1a\u201c\u672b\u5b66\u540e\u8f88\u5357\u6d77\u79bb\u706b\u5c9b\u90dd\u4e2d\u6d69\u6c42\u5927\u5b97 \u5e08\u6307\u70b9\u3002\u201d\u5192\u5ddd\u751f\u9053\uff1a\u201c\u539f\u6765\u662f\u8d64\u57ce\u5c9b\u4e3b\u7684\u9ad8\u8db3\uff0c\u597d\u8bf4\u597d\u8bf4\uff01\u4f60\u5bb6\u7684\u79bb\u706b\u574e\u6c34\u638c\u6cd5\uff0c\u8001\u673d\u4e5f \u4f69\u670d\u5f97\u5f88\u3002\u201d\u90dd\u4e2d\u6d69\u9053\uff1a\u201c\u5192\u8001\u524d\u8f88\u5982\u6b64\u8bf4\u6cd5\uff0c\u90a3\u5c82\u4e0d\u662f\u6559\u540e\u8f88\u5982\u5165\u5b9d\u5c71\u7a7a\u624b\u56de\u5417\uff1f\u201d\u5192\u5ddd \u751f\u7684\u201c\u5f00\u5c71\u7ed3\u7f18\u201d\uff0c\u7167\u4f8b\u4e0d\u80fd\u62d2\u7edd\u540e\u8f88\u7684\u8bf7\u6559\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5404\u5bb6\u6709\u5404\u5bb6\u7684\u72ec\u5230\u6b66\u529f\uff0c\u8d35\u6d3e \u7684\u638c\u6cd5\u6211\u4e0d\u6562\u5984\u8a00\u6307\u70b9\uff0c\u4f46\u4f60\u4e5f\u4e0d\u59a8\u8bd5\u6f14\u51fa\u6765\uff0c\u5f85\u6211\u770b\u770b\uff0c\u662f\u5426\u8fd8\u6709\u5176\u4ed6\u5730\u65b9\uff0c\u54b1\u4eec\u53ef\u4ee5\u5207 \u78cb\u3002\u201d\u90dd\u4e2d\u6d69\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u6c42\u4e0e\u8d35\u6d3e\u7684\u5927\u5f1f\u5b50\u96f7\u5e08\u5144\u5bf9\u638c\uff0c\u5bf9\u638c\u4e2d\u6709 \u4f55\u7834\u7efd\uff0c\u6c42\u8001\u524d\u8f88\u4e00\u4e00\u6307\u51fa\uff0c\u8fd9\u6837\u83b7\u76ca\u66f4\u5927\u3002\u201d\u8d74\u4f1a\u8bf8\u4eba\u542c\u4e86\uff0c\u5fc3\u5934\u90fd\u662f\u4e00\u9707\uff01 \u3000\u3000\u7167\u5f80\u5c4a\u5192\u5ddd\u751f\u5f00\u5c71\u7ed3\u7f18\u7684\u89c4\u77e9\uff0c\u6c42\u5192\u5ddd\u751f\u6307\u6559\u7684\u540e\u8f88\uff0c\u672c\u6765\u5c31\u6709\u4e24\u79cd\u529e\u6cd5\uff0c\u4e00\u79cd\u662f\u81ea\u5df1 \u5c06\u672c\u8eab\u6700\u5f97\u610f\u7684\u6b66\u529f\u7ec3\u51fa\u6765\uff0c\u4e00\u79cd\u662f\u4e24\u4eba\u5408\u624b\uff0c\u8bf7\u5192\u5ddd\u751f\u6307\u70b9\uff0c\u6bcf\u5c4a\u6c42\u5192\u5ddd\u751f\u6307\u70b9\u7684\u4eba\u90fd\u5f88 \u591a\uff0c\u540e\u4e00\u79cd\u529e\u6cd5\uff0c\u56e0\u4e3a\u540c\u65f6\u53ef\u4ee5\u6307\u70b9\u4e8c\u4eba\uff0c\u8282\u7701\u65f6\u95f4\uff0c\u6240\u4ee5\u4e5f\u5e38\u5e38\u91c7\u7528\u3002\u4f46\u82e5\u662f\u4e24\u4eba\u5408\u624b \u8005\uff0c\u591a\u6570\u662f\u540c\u95e8\u5e08\u5144\u5f1f\uff0c\u6216\u8005\u662f\u597d\u53cb\u4e16\u4ea4\uff0c\u80dc\u8d25\u4e0d\u4f24\u548c\u6c14\u3002\u800c\u4ee4\u90dd\u4e2d\u6d69\u8981\u4e0e\u96f7\u9707\u4e8e\u5408\u624b\uff0c\u4e24 \u4eba\u7edd\u65e0\u6e0a\u6e90\uff0c\u90a3\u5f53\u7136\u662f\u90dd\u4e2d\u6d69\u6709\u5fc3\u5411\u6b66\u5f53\u7684\u5927\u5f1f\u5b50\u6311\u8845\u4e86\u3002 \u3000\u3000\u6545\u6b64\u8d74\u4f1a\u8bf8\u4eba\u90fd\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u5373\u8fde\u5510\u7ecf\u5929\u4e5f\u6697\u6697\u66ff\u96f7\u9707\u5b50\u62c5\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u90dd\u4e2d\u6d69\u8fd9\u5c82\u4e0d \u662f\u5b58\u5fc3\u6361\u4fbf\u5b9c\u5417\uff1f\u79bb\u706b\u5c9b\u8d64\u57ce\u5c9b\u4e3b\u7684\u638c\u6cd5\u72ec\u521b\u4e00\u5bb6\uff0c\u96f7\u9707\u5b50\u548b\u665a\u82e5\u672a\u53d7\u4f24\uff0c\u6015\u8fd8\u672a\u5fc5\u80fd\u6253\u6210 \u5e73\u624b\u3001\u5982\u4eca\u4ed6\u5143\u6c14\u672a\u590d\uff0c\u5982\u4f55\u80fd\u662f\u90dd\u4e2d\u6d69\u4e4b\u654c\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5192\u5ddd\u751f\u4ecd\u662f\u795e\u8272\u5982\u5e38\uff0c\u6beb\u65e0\u6162\u6001\uff0c\u5fae\u7b11\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u597d\u3002\u96f7\u9707\u5b50\uff0c\u4f60\u5c31\u7528\u4e5d\u5bab \u8fde\u73af\u638c\u6cd5\uff0c\u5411\u90dd\u5e08\u5144\u9886\u6559\u9886\u6559\u5427\u3002\u201d \u3000\u3000\u96f7\u9707\u5b50\u7ad9\u4e86\u51fa\u6765\uff0c\u5927\u6bbf\u4e2d\u95f4\u7a7a\u51fa\u6570\u4e08\u65b9\u5706\u4e4b\u5730\uff0c\u4e24\u4eba\u5728\u5f53\u4e2d\u4e00\u7ad9\uff0c\u96f7\u9707\u5b50\u7acb\u597d\u95e8\u6237\uff0c \u9053\uff1a\u201c\u8bf7\u90dd\u5e08\u5144\u8d50\u62db\u3002\u201d\u90dd\u4e2d\u6d69\u4e00\u70b9\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u96f7\u9707\u5b50\u521a\u521a\u8bf4\u5b8c\uff0c\u4ed6\u53f3\u638c\u4e00\u8d77\uff0c\u547c\u7684\u4e00\u58f0\uff0c \u7acb\u523b\u5288\u9762\u6253\u4e0b\u3002 \u3000\u3000\u8d64\u57ce\u5c9b\u4e3b\u6240\u521b\u7684\u79bb\u706b\u574e\u6c34\u638c\u6cd5\uff0c\u4e00\u9633\u4e00\u9634\uff0c\u53f3\u638c\u6781\u521a\uff0c\u5982\u706b\u4e4b\u70c8\uff0c\u5de6\u638c\u6781\u67d4\uff0c\u5982\u6c34\u4e4b \u6027\uff0c\u521a\u67d4\u76f8\u6d4e\uff0c\u9634\u9633\u76f8\u914d\uff0c\u5999\u7528\u65e0\u7a77\uff0c\u90dd\u4e2d\u6d69\u662f\u8d64\u57ce\u5c9b\u4e3b\u7684\u5927\u5f1f\u5b50\uff0c\u5c3d\u5f97\u4e43\u5e08\u6240\u4f20\u3002\u8d64\u57ce\u5c9b \u4e3b\u50fb\u5904\u6d77\u5916\uff0c\u90dd\u4e2d\u6d69\u5374\u5e38\u5728\u4e2d\u539f\u8d70\u52a8\uff0c\u8fd9\u6b21\u7acb\u5fc3\u6765\u5411\u5192\u5ddd\u751f\u6311\u8845\u7684\u7fa4\u90aa\uff0c\u5148\u53bb\u6e38\u8bf4\u8d64\u57ce\u5c9b\u4e3b \u76f8\u52a9\uff0c\u8d64\u57ce\u5c9b\u4e3b\u7d20\u95fb\u5192\u5ddd\u751f\u4e4b\u540d\uff0c\u4e0d\u6b32\u591a\u4e8b\uff0c\u90dd\u4e2d\u6d69\u5374\u88ab\u8bf4\u52a8\uff0c\u6765\u5230\u5ce8\u5d4b\u3002\u524d\u4e00\u5915\u7fa4\u90aa\u8ba1 \u8bae\uff0c\u53ea\u8981\u6fc0\u5f97\u5192\u5ddd\u751f\u51fa\u624b\uff0c\u90a3\u5c31\u662f\u5df2\u574d\u4e86\u4ed6\u7684\u53f0\u3002\u6545\u7b2c\u4e00\u4ed7\u5c31\u6d3e\u90dd\u4e2d\u6d69\u51fa\u6765\u6311\u8845\u96f7\u9707\u5b50\u3002 \u3000\u3000\u90dd\u4e2d\u6d69\u81ea\u7136\u4e5f\u770b\u51fa\u4e86\u96f7\u9707\u5b50\u5185\u529f\u4e0d\u8db3\u7684\u5f31\u70b9\uff0c\u6240\u4ee5\u7b2c\u4e00\u624b\u5c31\u7528\u79bb\u706b\u9633\u638c\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u521a \u52b2\u4e4b\u6781\uff0c\u96f7\u9707\u5b50\u53cc\u638c\u4e00\u5206\uff0c\u53f3\u638c\u4ece\u5de6\u638c\u638c\u80cc\u64e6\u8fc7\uff0c\u5f53\u4e2d\u4e00\u5212\uff0c\u556a\u7684\u4e00\u54cd\uff0c\u90dd\u4e2d\u6d69\u638c\u80cc\u8d77\u4e86\u4e94 \u9053\u7ea2\u5370\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u96f7\u9707\u4e86\u7684\u638c\u5fc3\u4e5f\u76ae\u8089\u7834\u635f\uff0c\u73b0\u51fa\u8840\u4e1d\uff0c\u4e0a\u8eab\u6447\u5195\u4e0d\u5b9a\u3002\u4f46\u5374\u5e76\u672a\u7ed9\u5bf9\u65b9 \u7684\u638c\u529b\u9707\u9000\uff0c\u8fd9\u4e00\u4e0b\u53cc\u65b9\u90fd\u662f\u4ee5\u786c\u78b0\u786c\uff0c\u5404\u81ea\u53d7\u4f24\uff0c\u4f46\u6bd4\u5bf9\u4e4b\u4e0b\uff0c\u5374\u663e\u7136\u662f\u96f7\u9707\u5b50\u5360\u4e86\u4e0a \u98ce\uff01\u90dd\u4e2d\u6d69\u5927\u5403\u4e00\u60ca\uff0c\u4f1a\u4e2d\u8bf8\u4eba\uff0c\u8fde\u5510\u7ecf\u5927\u5728\u5185\u4e5f\u5747\u60ca\u8be7\u4e0d\u5df2\uff01\u5927\u5bb6\u90fd\u662f\u83ab\u540d\u5176\u5999\uff0c\u600e\u4e48\u96f7 \u9707\u5b50\u7684\u529f\u529b\u4f1a\u7a81\u7136\u589e\u8fdb\u4e86\u8fd9\u8bb8\u591a\uff1f \u3000\u3000\u4ed6\u4eec\u600e\u77e5\u96f7\u9707\u5b50\u7684\u529f\u529b\u672c\u6765\u4e0e\u90dd\u4e2d\u6d69\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4f46\u7ecf\u8fc7N\u53eb\u4e2d\u7684\u6697\u52a9\uff0c\u8fd9\u5c31\u6bd4\u90ca\u4e2d\u6d69 \u5f3a\u4e86\u4e09\u5206\u3002\u96f7\u9707\u5b50\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u7acb\u5373\u8de8\u6b65\u5bc4\u638c\uff0c\u547c\uff0e\u547c\u3002\u547c\uff0c\u8fde\u5288\u4e09\u638c\uff0c\u90ca\u4e2d\u6d69\u8fde\u8fde\u540e\u9000\uff0c \u7a81\u7136\u5de6\u638c\u4e00\u8fce\uff0c\u96f7\u9707\u5b50\u5ffd\u89c9\u5bf9\u65b9\u5168\u4e0d\u53d7\u529b\uff0c\u90dd\u4e2d\u6d69\u5de6\u638c\u4e00\u642d\uff0c\u642d\u4e0a\u4e86\u96f7\u9707\u5b50\u7684\u638c\u80cc\uff0c\u53f3\u638c\u7acb \u523b\u53cd\u624b\u6790\u4e0b\uff0c\u6b66\u5f53\u5f1f\u5b50\u6709\u7684\u88ab\u5413\u5f97\u53eb\u51fa\u58f0\u6765\uff0c\u773c\u770b\u5927\u5e08\u5144\u7684\u624b\u8155\u5c31\u8981\u88ab\u654c\u4eba\u6298\u65ad\uff01 \u3000\u3000\u5ffd\u89c1\u96f7\u9707\u5b50\u6307\u5c16\u4e00\u7fd8\uff0c\u6b63\u6b63\u6307\u7740\u90dd\u4e2d\u6d69\u7684\u864e\u53e3\u7a74\u9053\uff0c\u90dd\u4e2d\u6d69\u4e00\u51db\uff0c\u5de6\u638c\u677e\u5f00\uff0c\u4e00\u62db\u201c\u91d1 \u751f\u4e3d\u6c34\u201d\uff0c\u89e3\u5f00\u96f7\u9707\u5b50\u8ffd\u51fb\u7684\u638c\u52bf\u3002\u96f7\u9707\u5b50\u7b2c\u4e00\u6b21\u9047\u5230\u574e\u706b\u79bb\u6c34\u638c\u6cd5\uff0c\u672c\u6765\u8fd8\u672a\u61c2\u5f97\u8fd9\u638c\u6cd5 \u7684\u5965\u5999\u4e4b\u5904\uff0c\u4f46\u4ed6\u521a\u624d\u542c\u5f97\u7956\u5e08\u8bf4\u4ed6\u7684\u70b9\u7a74\u6cd5\u4e0d\u884c\uff0c\u9886\u609f\u4e86\u4e5d\u5bab\u516b\u5366\u638c\u4e2d\u5fc5\u987b\u4ee5\u70b9\u7a74\u7684\u6307 \u6cd5\uff0c\u914d\u5408\u638c\u529b\uff0c\u51fa\u5947\u5236\u80dc\uff0c\u6240\u4ee5\u4e00\u9047\u5371\u6025\uff0c\u7acb\u523b\u4fbf\u7528\u70b9\u7a74\u89e3\u6551\uff0c\u90dd\u4e2d\u6d69\u4e0d\u6562\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c \u679c\u7136\u594f\u4e86\u5947\u6548\u3002 \u3000\u3000\u5192\u5ddd\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90dd\u4e2d\u6d69\u521a\u624d\u90a3\u4e00\u638c\u5e94\u8be5\u6a2a\u6790\u65f6\u5c16\uff0c\u5de6\u638c\u5e94\u7acb\u5373\u53d8\u62db\u6293\u654c\u8109\u95e8\uff0c \u8fd9\u6837\u5c31\u4e0d\u81f3\u4e8e\u7ed9\u5bf9\u65b9\u7aa5\u9699\u70b9\u7a74\u4e86\uff01\u201d\u4f17\u9ad8\u624b\u90fd\u6697\u6697\u70b9\u5934\u3002\u90dd\u4e2d\u6d69\u5fc3\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u8bdd\u4f60\u4f55\u4e0d\u65e9 \u8bf4\uff01\u201d\u6014\u4e86\u4e00\u6014\uff0c\u96f7\u9707\u5b50\u53cc\u638c\u9f50\u5230\uff0c\u90dd\u4e2d\u6d69\u6b63\u5f85\u53f3\u638c\u8fce\u654c\uff0c\u7528\u201c\u529b\u5288\u4e09\u5c71\u201d\u7684\u62db\u6570\u786c\u6321\uff0c\u4e0b \u4e00\u624b\u5c31\u7528\u5de6\u638c\u7684\u201c\u987a\u6c34\u63a8\u821f\u201d\u7684\u9634\u67d4\u638c\u529b\u53cd\u51fb\uff0c\u5ffd\u542c\u5f97\u5192\u5ddd\u751f\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff01\u8be5\u5148\u7528\u574e \u6c34\u638c\u6cd5\u7684\u5fae\u6b65\u51cc\u6ce2\u6d88\u654c\u6765\u52bf\uff01\u201d\u90dd\u4e2d\u6d69\u65e0\u6687\u601d\u7d22\uff0c\u4e0d\u81ea\u89c9\u7684\u7acb\u523b\u7167\u5192\u5ddd\u751f\u7684\u6307\u70b9\u5e94\u654c\uff0c\u679c\u7136 \u5c06\u96f7\u9707\u5b50\u5e26\u8fc7\u4e00\u8fb9\uff0c\u8fd9\u624d\u5fc3\u4e2d\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u4ed6\u8bf4\u5f97\u65e9\uff0c\u8981\u4e0d\u7136\u4ee5\u786c\u78b0\u786c\uff0c\u96f7\u9707\u5b50\u7684\u529f \u529b\u9ad8\u6211\u4e09\u6210\uff0c\u8fd9\u624b\u8155\u5c82\u4e0d\u662f\u7ed9\u4ed6\u65ab\u65ad\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e00\u5206\u5373\u5408\uff0c\u53c8\u518d\u4ea4\u950b\uff0c\u5192\u5ddd\u751f\u4f9d\u7740\u201c\u5f00\u5c71\u7ed3\u7f18\u201d\u7684\u89c4\u77e9\uff0c\u968f\u65f6\u6307\u70b9\uff0c\u800c\u4e14\u5bf9\u90dd\u4e2d\u6d69 \u7684\u6307\u70b9\uff0c\u6bd4\u5bf9\u96f7\u9707\u5b50\u7684\u8fd8\u591a\uff0c\u53eb\u8d74\u4f1a\u7684\u9ad8\u624b\u542c\u4e86\uff0c\u90fd\u4f69\u670d\u5192\u5ddd\u751f\u786e\u6709\u5927\u5b97\u5e08\u7684\u6c14\u5ea6\uff0c\u975e\u4f46\u4e00 \u70b9\u4e5f\u4e0d\u504f\u8892\u672c\u95e8\u5f1f\u5b50\uff0c\u800c\u4e14\u8fd8\u6697\u6697\u76f8\u52a9\u5bf9\u65b9\uff0c\u5373\u662f\u90dd\u4e2d\u6d69\u672c\u4eba\uff0c\u4ea6\u662f\u5927\u529b\u5fc3\u6298\u3002 \u3000\u3000\u5c82\u77e5\u5192\u5ddd\u751f\u522b\u6709\u5999\u7528\uff0c\u4ed6\u77e5\u9053\u4e5d\u5bab\u516b\u5366\u638c\u4ee5\u6b63\u5236\u5947\uff0c\u7edd\u5bf9\u80fd\u5e94\u4ed8\u5f97\u4e86\u90dd\u4e2d\u6d69\u7684\u90aa\u95e8\u638c \u6cd5\uff0c\u800c\u96f7\u9707\u5b50\u672c\u8eab\u7684\u529f\u529b\u53c8\u9ad8\u4e8e\u5bf9\u65b9\uff0c\u90a3\u5df2\u662f\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u6240\u53ef\u8651\u8005\u662f\u96f7\u9707\u5b50\u521d\u9047\u8fd9\u79cd\u638c \u6cd5\uff0c\u672a\u66fe\u6df1\u6089\u5176\u4e2d\u5965\u5999\uff0c\u53ef\u80fd\u88ab\u5bf9\u65b9\u9634\u9633\u638c\u6cd5\u6240\u8ff7\uff0c\u6240\u4ee5\u4ed6\u4e0d\u6015\u53bb\u6307\u70b9\u90dd\u6d69\uff0c\u5728\u6307\u70b9\u90dd\u4e2d\u6d69 \u4e4b\u65f6\uff0c\u4ea6\u5373\u662f\u4ee4\u96f7\u9707\u5b50\u66f4\u9886\u609f\u5bf9\u65b9\u638c\u6cd5\u7684\u5965\u5999\u6240\u5728\uff0c\u597d\u77e5\u6240\u9884\u9632\u3002\u800c\u6307\u70b9\u96f7\u9707\u5b50\u4e4b\u5904\uff0c\u90fd\u662f \u5173\u952e\u6240\u5728\uff0c\u96f7\u9707\u5b50\u7684\u638c\u6cd5\u672c\u5c31\u7eaf\u719f\u4e4b\u6781\uff0c\u4e00\u7ecf\u6307\u70b9\uff0c\u90a3\u5c31\u66f4\u52a0\u53d8\u5316\u65e0\u65b9\u4e86\uff01 \u3000\u3000\u4e24\u4eba\u5404\u5c55\u5e73\u751f\u6240\u5b66\uff0c\u62c6\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u90dd\u4e2d\u6d69\u867d\u7136\u5f97\u5192\u5ddd\u751f\u6307\u70b9\u8f83\u591a\uff0c\u800c\u4e14\u6bcf\u4e00\u6b21\u6307\u70b9\u90fd \u975e\u5e38\u4e2d\u80af\uff0c\u6beb\u65e0\u865a\u5047\uff0c\u4f46\u8ff0\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u6258\u7684\u8df3\u51fa\u5708\u5b50\uff0c\u62f1\u624b\u8bf4\u9053\uff1a \u201c\u96f7\u5e08\u5144\u7684\u638c\u6cd5\u975e\u6211\u6240\u80fd\u654c\uff0c\u591a\u8c22\u5927\u5b97\u5e08\u6307\u70b9\uff0c\u6211\u56de\u5c9b\u53bb\u4e00\u5b9a\u518d\u4f9d\u5927\u5e08\u7684\u6307\u70b9\u82e6\u7ec3\u3002\u201d\u90dd\u4e2d \u6d69\u53e3\u670d\u5fc3\u670d\uff0c\u4ece\u6b64\u6c38\u4e0d\u6562\u518d\u4e0e\u6b66\u5f53\u6d3e\u4e3a\u654c\uff0c\u800c\u4ed6\u81ea\u5df1\u4e5f\u786e\u5b9e\u56e0\u6b64\u5f97\u76ca\u4e0d\u5c11\u3002 \u3000\u3000\u4e24\u4eba\u521a\u521a\u5f52\u5ea7\uff0c\u5750\u5728\u7b2c\u4e09\u6392\u7684\u4e00\u73ed\u4eba\u5ffd\u7136\u9c7c\u8d2f\u800c\u51fa\uff0c\u8fd9\u4e00\u73ed\u4eba\u4e00\u5f8b\u9ed1\u8272\u8863\u51a0\uff0c\u624b\u6301\u957f \u5251\uff0c\u8170\u60ac\u6697\u5668\u56ca\uff0c\u5171\u6709\u4e5d\u4eba\u4e4b\u591a\uff0c\u8d70\u51fa\u6765\u4e5f\u5404\u6309\u7740\u516b\u5366\u65b9\u4f4d\uff0c\u6ee1\u900f\u7740\u602a\u6c14\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u540d\u5c71\u5904\u5904\u5996\u90aa\u5230\uff0c\u63a5\u4e8c\u63a5\u4e09\u8d77\u4e8b\u7aef\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u7fbd\u58eb\u9b54\u5934\u3000\u7fa4\u90aa\u671d\u6cd5\u4f1a\u3000\u51b0\u5f39\u7389\u5251\u3000\u5929\u5973\u4e0a\u5ce8\u7709 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u56db\u56de \u7fbd\u58eb\u9b54\u5934\u3000\u7fa4\u90aa\u671d\u6cd5\u4f1a \u51b0\u5f39\u7389\u5251\u3000\u5929\u5973\u4e0a\u5ce8\u5d4b \u3000\u3000\u90a3\u4e3a\u9996\u7684\u9ed1\u8863\u4eba\u629a\u5251\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7d20\u4ef0\u6b66\u5f53\u6d3e\u7684\u51e1\u5bab\u516b\u5366\u638c\u5947\u5999\u65e0\u65b9\uff0c\u54b1\u4eec\u6709\u4e2a \u5c0f\u5c0f\u7684\u9635\u6cd5\uff0c\u4e5f\u662f\u6309\u7740\u4e5d\u5bab\u516b\u5366\u7684\u5faa\u73af\u4e4b\u7406\u6240\u5e03\uff0c\u6b63\u597d\u4e0e\u8d35\u6d3e\u5370\u8bc1\uff0c\u6c42\u5927\u5b97\u5e08\u591a\u591a\u6307\u70b9\u3002\u201d \u8fd9\u51e0\u4eba\u6b65\u51fa\u6765\u65f6\u5df1\u662f\u6309\u7740\u4e5d\u5bab\u516b\u5366\u65b9\u4f4d\uff0c\u5c06\u575b\u524d\u7684\u4e00\u4f17\u6b66\u5f53\u5f1f\u5b50\u90fd\u6697\u6697\u56f4\u7740\uff0c\u4e3a\u7235\u7684\u8bdd\u4e00\u8bf4 \u5b8c\uff0c\u4e00\u58f0\u547c\u5484\uff0c\u7adf\u7136\u4e0d\u5f85\u5192\u5ddd\u751f\u5141\u51c6\uff0c\u51e0\u67c4\u5251\u5237\u7684\u5c31\u4e00\u9f50\u51fa\u9798\uff0c\u5c06\u5341\u591a\u540d\u5728\u575b\u524d\u4f8d\u5949\u7684\u6b66\u5f53 \u6d3e\u5f1f\u5b50\uff0c\u8fde\u540c\u96f7\u9707\u5b50\u5728\u5185\uff0c\u90e8\u4e00\u9f50\u5708\u5728\u4e03\u4e2d\uff0c\u4e3a\u9996\u90a3\u4eba\u5251\u8bc0\u4e00\u9886\uff0c\u8fce\u5411\u5c31\u7ed9\u4e86\u96f7\u9707\u5b50\u4e00\u5251\uff01 \u3000\u3000\u5ea7\u4e0a\u5404\u6d3e\u82f1\u8c6a\uff0c\u65e0\u4e0d\u5931\u8272\uff0c\u8fd9\u51e0\u4eba\u5b9e\u662f\u65e0\u793c\u4e4b\u6781\uff0c\u6b66\u5f53\u6d3e\u5f1f\u5b50\u66f4\u662f\u5927\u6012\uff0c\u53cc\u65b9\u66f4\u4e0d\u4ea4\u4ee3 \u5ba2\u5957\u7684\u8bf4\u8bdd\uff0c\u7acb\u5373\u638c\u6765\u5251\u5f80\uff0c\u50fb\u50fb\u556a\u556a\u7684\u4e71\u6253\u8d77\u6765\u3002\u88ab\u56f4\u5728\u9635\u4e2d\u7684\u6b66\u5f53\u5f1f\u5b50\u867d\u6709\u591a\u4eba\uff0c\u5728\u6570 \u91cf\u4e0a\u4f18\u52bf\uff0c\u4f46\u90a3\u51e0\u540d\u9ed1\u8863\u4eba\u540c\u8fdb\u540c\u9000\uff0c\u9996\u5c3e\u76f8\u8fde\uff0c\u6b64\u547c\u5f7c\u5e94\uff0c\u65f6\u800c\u4e00\u5b57\u6563\u5f00\uff0c\u65f6\u800c\u56db\u56f4\u5408 \u51fb\uff0c\u4e5d\u4eba\u4f5c\u6218\uff0c\u4e25\u5982\u4e00\u4f53\uff0c\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u88ab\u56f4\u5728\u4e2d\uff0c\u5de6\u51b2\u6709\u7a81\uff0c\u7adf\u7136\u51b2\u4e0d\u51fa\u4e09\u4e08\u65b9\u5706\u4e4b\u5730\u3002 \u800c\u4e14\u5f7c\u6b64\u62e5\u6324\uff0c\u5404\u81ea\u4e3a\u6218\uff0c\u6e10\u6e10\u8fde\u624b\u811a\u4e5f\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u5510\u7ecf\u5929\u770b\u5f97\u6697\u6697\u5fc3\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e5d\u5bab\u516b\u5366\u9635\u679c\u7136\u751a\u662f\u5947\u5999\u3002\u4eca\u5929\u6b66\u5f53\u5f1f\u5b50\u53ea\u6050\u8981\u5403\u5927 \u4e8f\u3002\u201d\u8e0c\u8e87\u4e0d\u51b3\uff0c\u662f\u5426\u8981\u51fa\u624b\u76f8\u6551\uff0c\u53ea\u542c\u5f97\u5192\u5ddd\u751f\u5fae\u7b11\u9053\uff1a\u201c\u97e9\u91cd\u5c71\u4e0e\u53f6\u6a2a\u6ce2\u7559\u4e0b\u7684\u9635\u6cd5\u679c \u662f\u9ad8\u660e\uff0c\u53ea\u662f\u8fd9\u9635\u6cd5\u8981\u914d\u4ee5\u6697\u5668\u4e4b\u529b\uff0c\u95e8\u6237\u624d\u80fd\u7d27\u5c01\uff0c\u5a01\u529b\u624d\u80fd\u5927\u663e\uff0c\u4f60\u4eec\u4e3a\u4f55\u4e0d\u7528\u5168\u529b\uff0c \u53ea\u65bd\u5c55\u4e86\u4e00\u534a\uff1f\u201d\u5510\u7ecf\u5929\u5fc3\u5934\u4e00\u9707\uff0c\u539f\u6765\u5192\u5ddd\u751f\u6240\u8bf4\u7684\u90a3\u97e9\u3001\u53f6\u4e8c\u4eba\u4e43\u662f\u592b\u5987\uff0c\u6b66\u529f\u6781\u9ad8\uff0c \u6697\u5668\u529f\u592b\u5c24\u5176\u51fa\u795e\u5165\u5316\uff0c\u4e0e\u56db\u5ddd\u5510\u5bb6\u9f50\u540d\u3002\u4ed6\u4eec\u662f\u7075\u5c71\u6d3e\u7684\u957f\u8001\uff0c\u8bba\u8d77\u8f88\u4efd\uff0c\u548c\u5192\u5ddd\u751f\u540c \u8f88\u3002\u4e09\u5341\u5e74\u524d\uff0c\u5f53\u96cd\u6b63\u5e1d\u5141\u5e27\u8fd8\u662f\u56db\u7687\u5b50\u4e4b\u65f6\uff0c\u4ed6\u4eec\u66fe\u53d7\u5141\u5e27\u4e4b\u8058\u3002\u52a9\u5141\u5e27\u593a\u5f97\u5e1d\u4f4d\u3002\u4e8b\u9694 \u4e09\u5341\u5e74\uff0c\u6362\u4e86\u4e24\u4e2a\u7687\u5e1d\uff08\u4ece\u96cd\u6b63\u81f3\u4e7e\u9686\u3002\uff09\u7075\u5c71\u6d3e\u7684\u4eba\u4ece\u4e0d\u5728\u6c5f\u6e56\u9732\u9762\uff0c\u53f6\u6a2a\u6ce2\u4e0e\u5929\u53f6\u6563\u4eba \u4e5f\u65e9\u5df2\u6b7b\u4e86\u3002\u5927\u5bb6\u90fd\u5df2\u6de1\u5fd8\uff0c\u54ea\u77e5\u7075\u5c71\u6d3e\u8fd8\u7559\u4e0b\u97e9\u91cd\u5c71\u7684\u9635\u6cd5\uff0c\u4eca\u65e5\u7adf\u7136\u642c\u5230\u5ce8\u5d4b\u5c71\u6765\u3002 \u3000\u3000\u5192\u5ddd\u751f\u6b64\u8a00\u4e00\u51fa\uff0c\u90a3\u4e5d\u4e2a\u7075\u5c71\u6d3e\u5f1f\u5b50\u548c\u5510\u7ecf\u5929\u90fd\u662f\u5fc3\u4e2d\u6697\u60ca\uff0c\u7075\u5c71\u6d3e\u5f1f\u5b50\u60ca\u7684\u662f\uff1a\u7956\u5e08 \u7684\u9635\u6cd5\uff0c\u4e09\u5341\u5e74\u6765\u4ece\u672a\u7528\u8fc7\uff0c\u4e0d\u77e5\u5192\u5ddd\u751f\u4f55\u4ee5\u80fd\u7aa5\u7834\u5176\u4e2d\u5965\u5999\uff1f\u5510\u7ecf\u5927\u60ca\u7684\u662f\uff1a\u8fd9\u4e5d\u5bab\u516b\u5366 \u9635\u4e0d\u7528\u6697\u5668\u5df2\u662f\u5389\u5bb3\u975e\u5e38\uff0c\u82e5\u7528\u6697\u5668\uff0c\u53ea\u6050\u6b66\u5f53\u5f1f\u5b50\uff0c\u4e2a\u4e2a\u90fd\u96be\u9003\u52ab\u8fd0\uff01 \u3000\u3000\u8fd9\u65f6\u4e5d\u5bab\u516b\u5366\u9635\u5df2\u8d8a\u6536\u8d8a\u7d27\uff0c\u4e5d\u4e2a\u9ed1\u8863\u4eba\u4e5d\u53e3\u957f\u5251\u4ea4\u53c9\u7a7f\u63d2\uff0c\u5c06\u6b66\u5f53\u5f1f\u5b50\u8feb\u5728\u4e00\u9685\uff0c\u6beb \u65e0\u53cd\u653b\u4e4b\u529b\u3002\u4e3a\u9996\u7684\u9ed1\u8863\u4eba\u662f\u7075\u5c71\u6d3e\u7684\u638c\u5c71\u95e8\u5f1f\u5b50\u53f6\u5929\u4efb\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b64\u6765\u4e3a\u7684\u662f\u628a\u6b66\u5f53 \u6253\u4e2a\u5168\u519b\u7686\u58a8\uff0c\u597d\u7ed9\u7075\u5c71\u6d3e\u91cd\u65b0\u626c\u5a01\u7acb\u4e07\uff0c\u770b\u8fd9\u60c5\u52bf\uff0c\u4e0d\u51fa\u4e00\u65f6\u4e09\u523b\uff0c\u6211\u65b9\u4fbf\u53ef\u5927\u83b7\u5168\u80dc\uff0c \u4f55\u5fc5\u518d\u7528\u6697\u5668\u6740\u4f24\uff0c\u82e5\u7136\u6740\u6b7b\u4e86\u6b66\u5f53\u7684\u5f1f\u5b50\uff0c\u6fc0\u5f97\u5192\u5ddd\u751f\u51fa\u624b\uff0c\u4ed6\u867d\u7136\u5931\u4e86\u8eab\u4efd\uff0c\u54b1\u4eec\u4e5f\u662f \u5f04\u5de7\u53cd\u62d9\u3002\u201d\u4e8e\u662f\u7b54\u9053\uff1a\u201c\u5927\u5b97\u5e08\u6307\u70b9\u5f97\u662f\uff0c\u8fd9\u9635\u52bf\u78b0\u7740\u4e86\u6781\u5f3a\u7684\u5bf9\u624b\uff0c\u81ea\u7136\u8be5\u7528\u6697\u5668\u52a0\u5f3a \u5a01\u529b\uff0c\u4e00\u822c\u7684\u654c\u624b\uff0c\u4e0d\u7528\u6697\u5668\u4ed6\u4eec\u4e5f\u9003\u4e0d\u51fa\u9635\u53bb\u3002\u201d\u8fd9\u8bdd\u8bf4\u5f97\u6781\u5176\u81ea\u6ee1\uff0c\u7b80\u76f4\u4e0d\u628a\u96f7\u9707\u5b50\u8fd9 \u4e00\u73ed\u6b66\u5f53\u95e8\u4e0b\u653e\u5728\u773c\u5185\uff0c\u96f7\u9707\u4e8e\u5927\u6012\uff0c\u957f\u5251\u5e73\u80f8\uff0c\u201c\u5237\u201d\u7684\u5c31\u662f\u201c\u6012\u6d9b\u5377\u7a7a\u201d\uff0c\u76f4\u523a\u53f6\u5929\u4efb \u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u53f6\u5929\u4efb\u8fc8\u524d\u4e00\u6b65\uff0c\u5e76\u4e0d\u53cd\u51fb\uff0c\u81ea\u6709\u4e24\u65c1\u7684\u5e08\u5f1f\uff0c\u67b6\u5f00\u4e86\u96f7\u9707\u5b50\u7684\u5251\u62db\uff0c\u5c06\u4ed6 \u66f4\u8feb\u8fdb\u6838\u5fc3\uff0c\u53f6\u5929\u4efb\u5927\u529b\u5f97\u610f\uff0c\u9053\uff1a\u201c\u5148\u5e08\u4e5d\u5bab\u516b\u5366\u9635\u4e0d\u77e5\u8fd8\u6709\u4f55\u7834\u7efd\uff0c\u8bf7\u5192\u8001\u524d\u8f88\u6307\u70b9\u3002\u201d \u3000\u3000\u5192\u5ddd\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7684\u9635\u52bf\u5a01\u529b\uff0c\u53ea\u7528\u4e86\u4e00\u534a\uff0c\u81ea\u7136\u8fd8\u662f\u6709\u7834\u7efd\u3002\u563f\uff0c\u96f7\u9707\u5b50\uff0c \u4f60\u8d70\u4e7e\u65b9\uff0c\uff0c\u51cc\u4e00\u74e2\uff0c\u4f60\u8d70\u79bb\u65b9\uff0c\u5954\u574e\u4f4d\uff0c\u907f\u8fd1\u653b\u8fdc\uff0c\u90a3\u5c31\u8d70\u51fa\u6765\u4e86\u3002\u201c\u96f7\u9707\u5b50\u7b49\u4eba\u4f9d\u7740\u6307 \u70b9\uff0c\u4e0d\u7406\u8fd1\u8eab\u4e4b\u654c\uff0c\u5404\u62a2\u65b9\u4f4d\uff0c\u5de6\u638c\u53f3\u5251\uff0c\u653b\u51fb\u5916\u56f4\u5835\u622a\u7684\u654c\u4eba\uff0c\u4e5d\u5bab\u516b\u5366\u9635\u6309\u7740\u9635\u52bf\u8f6c \u52a8\uff0c\u4e00\u7ed9\u654c\u4eba\u6b3a\u8eab\u63a0\u8fc7\uff0c\u5176\u52bf\u5c31\u4e0d\u80fd\u56de\u8eab\u53cd\u51fb\u3002\u96f7\u9707\u5b50\u7b49\u4eba\u65b9\u4f4d\u62a2\u5f97\u6070\u5230\u597d\u5904\uff0c\u820d\u8fd1\u653b\u8fdc\uff0c \u679c\u7136\u4e0d\u8fc7\u7247\u523b\uff0c\u5341\u591a\u540d\u6b66\u5f53\u5f1f\u5b50\u5168\u90fd\u8131\u51fa\u5305\u56f4\u3002 \u3000\u3000\u53f6\u5929\u4efb\u53c8\u7f9e\u53c8\u6012\uff0c\u56e0\u4ed6\u6709\u8a00\u5728\u5148\uff0c\u8bf7\u5192\u5ddd\u751f\u6307\u70b9\uff0c\u53c8\u58f0\u660e\u4e0d\u7528\u6674\u5668\uff0c\u4ea6\u53ef\u56f0\u654c\uff0c\u6240\u4ee5\u5192 \u5ddd\u751f\u4e09\u8a00\u4e24\u8bed\uff0c\u6307\u5f15\u95e8\u4e0b\u8131\u51fa\u5305\u56f4\uff0c\u4ed6\u4ea6\u662f\u96be\u4ee5\u53d1\u4f5c\u3002\u53ea\u542c\u5f97\u5192\u5ddd\u751f\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u9635 \u6cd5\uff0c\u5373\u7b97\u65bd\u7528\u4e86\u6697\u5668\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u56f0\u5f97\u4f4f\u654c\u4eba\uff0c\u5185\u4e2d\u7684\u7834\u7efd\u5176\u5b9e\u8fd8\u591a\u7740\u54e9\uff01\u201d\u7075\u5c71\u6d3e\u4e5d\u4e2a\u5f1f\u5b50 \u76f8\u987e\u5931\u8272\uff0c\u4eba\u4eba\u52a8\u6012\uff0c\u4e2a\u4e2a\u6c14\u6124\u3002 \u3000\u3000\u53f6\u5929\u4efb\u5bd2\u4e86\u9762\u5b54\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u96f7\u9707\u5b50\u5404\u4f4d\u5e08\u5144\u518d\u4eba\u9635\u4e2d\u6307\u6559\uff0c\u6709\u751a\u7834\u7efd\uff0c\u5192\u8001 \u524d\u8f88\u968f\u65f6\u6307\u6b63\u3002\u201d\u5ea7\u4e2d\u5404\u6d3e\u9ad8\u624b\u867d\u7136\u89c9\u5f97\u7075\u5c71\u6d3e\u8fd9\u4e5d\u4e2a\u9ed1\u8863\u4eba\u592a\u8fc7\u65e0\u793c\uff0c\u88ab\u5192\u5ddd\u751f\u6beb\u4e0d\u7559\u60c5 \u7684\u6307\u6458\uff0c\u4eba\u4eba\u79f0\u5feb\uff0c\u4f46\u4ea6\u89c9\u5f97\u5192\u5ddd\u751f\u6b64\u8a00\u53ef\u80fd\u4ee4\u96f7\u9707\u5b50\u7b49\u53cd\u62db\u8d25\u8fb1\uff0c\u5510\u7ecf\u5929\u4ea6\u662f\u5982\u6b64\u60f3\u6cd5\uff0c \u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5192\u8001\u524d\u8f88\u7406\u8be5\u89c1\u597d\u4fbf\u6536\uff0c\u8fd9\u9635\u6cd5\u7eb5\u6709\u7834\u7efd\uff0c\u4f46\u7075\u5c71\u6d3e\u7684\u6697\u5668\u975e\u540c\u5c0f\u53ef\uff0c\u82e5\u96f7\u9707 \u5b50\u7b49\u518d\u5165\u9635\u4e2d\uff0c\u7eb5\u6709\u6307\u70b9\uff0c\u53d7\u4f24\u6050\u662f\u514d\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u5192\u5ddd\u751f\u7aef\u5750\u575b\u4e0a\uff0c\u770b\u4e86\u53f6\u5929\u4efb\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f55\u987b\u9002\u624d\u90a3\u4e48\u591a\u4eba\uff0c\u8981\u7834\u4f60\u8fd9\u9635\u6cd5\uff0c\u53ea\u987b\u4e00 \u4eba\u4fbf\u591f\uff01\u201d \u3000\u3000\u53f6\u5929\u4efb\u9762\u5b54\u94c1\u9752\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u5192\u8001\u524d\u8f88\u8981\u4eb2\u81ea\u6307\u6559\uff0c\u90a3\u771f\u662f\u6211\u4eec\u4e09\u751f\u6709\u5e78\uff0c\u6562\u4e0d \u62dc\u8c22\uff01\u201d\u4e0d\u4f46\u53f6\u5929\u4efb\u4ee5\u4e3a\u662f\u5192\u5ddd\u751f\u60f3\u4eb2\u81ea\u4e0b\u573a\uff0c\u5ea7\u4e0a\u7fa4\u82f1\u4e5f\u90fd\u662f\u4eba\u540c\u6b64\u5fc3\uff0c\u5fc3\u540c\u6b64\u7406\uff0c\u60f3 \u9053\uff1a\u201c\u82e5\u8981\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u7834\u7075\u5c71\u6d3e\u8fd9\u4e5d\u5bab\u516b\u5366\u9635\uff0c\u90a3\u786e\u662f\u975e\u5192\u5ddd\u751f\u83ab\u529e\uff0c\u4f46\u90a3\u4e0d\u662f\u5927\u5931\u8eab\u4efd \u4e86\u5417\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5192\u5ddd\u751f\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u673d\u54ea\u8fd8\u6709\u8fd9\u4e2a\u5174\u81f4\uff0c\u6211\u53eb\u6211\u6b66\u5f53\u6d3e\u7684\u4e00\u4e2a\u540e \u8f88\u4e0e\u4ed6\u4eec\u5370\u8bc1\u4e00\u4e0b\uff0c\u770b\u6211\u7684\u8bdd\u8bf4\u5f97\u5bf9\u4e0d\u662f\u5bf9\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u53c8\u662f\u5408\u5ea7\u7686\u60ca\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u6b66\u5f53 \u6d3e\u7684\u540e\u8f88\u4eba\u7269\u4e4b\u4e2d\uff0c\u6700\u5f3a\u7684\u4fbf\u662f\u96f7\u9707\u5b50\uff0c\u4ee5\u96f7\u9707\u5b50\u672c\u8eab\u7684\u529f\u529b\uff0c\u4ee5\u4e00\u654c\u4e00\uff0c\u6050\u6015\u8fd8\u4e0d\u662f\u53f6\u5929 \u4efb\u7684\u5bf9\u624b\uff0c\u5982\u4f55\u80fd\u7834\u5f97\u4e86\u8fd9\u4e5d\u5bab\u516b\u5366\u9635\u3001 \u3000\u3000\u5510\u7ecf\u5929\u4ea6\u662f\u6781\u4e3a\u60ca\u8be7\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7136\u662f\u6211\u9677\u5728\u8fd9\u4e5d\u5bab\u516b\u5366\u9635\u4e2d\uff0c\u4ed6\u4eec\u4e0d\u7528\u6697\u5668\uff0c\u6211\u53ef\u4ee5 \u7834\u3002\u82e5\u7136\u4f7f\u51fa\u6697\u5668\uff0c\u4ece\u516b\u4e2a\u65b9\u4f4d\u9f50\u5411\u4e2d\u592e\u6253\u6765\uff0c\u90a3\u6211\u4ed7\u7740\u5b9d\u5251\u4e4b\u529b\uff0c\u5927\u7ea6\u4ec5\u80fd\u81ea\u4fdd\uff0c\u66f4\u4e0d\u8981 \u8bf4\u7834\u4ed6\u7684\u9635\u4e86\u3002\u6b66\u5f53\u6d3e\u7684\u540e\u8f88\u4e2d\u8c01\u6709\u90a3\u4e48\u5927\u7684\u672c\u9886\u3002\u201d\u6b63\u81ea\u7591\u60d1\u4e0d\u5df2\uff0c\u5ffd\u542c\u5f97\u5192\u5ddd\u751f\u8f7b\u8f7b\u62cd \u4e86\u4e00\u4e0b\u624b\u638c\uff0c\u6bbf\u5802\u540e\u9762\u73af\u4f69\u53ee\u54e8\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u5e7d\u9999\u5148\u6563\uff0c\u4e00\u80a1\u9189\u4eba\u7684\u9999\u5473\uff0c\u76f4\u51b2\u9f3b\u89c2\uff0c\u4f17\u4eba \u76ee\u4e0d\u8f6c\u6674\uff0c\u4f46\u89c1\u5c4f\u98ce\u540e\u9762\uff0c\u8f6c\u51fa\u6765\u4e00\u4e2a\u5973\u5b50\uff0c\u8eab\u7a7f\u6e56\u6c34\u8272\u7684\u8863\u88f3\uff0c\u8138\u5982\u65b0\u6708\uff0c\u6d45\u753b\u53cc\u7709\uff0c\u5c0f \u53e3\u5982\u6843\uff0c\u773c\u73e0\u5fae\u78a7\uff0c\u53ea\u662f\u8fd9\u4e48\u8f7b\u8f7b\u4e00\u76fc\uff0c\u6ee1\u573a\u9e26\u96c0\u65e0\u58f0\uff0c\u5510\u7ecf\u5929\u53c8\u60ca\u53cc\u559c\uff0c\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff01 \u3000\u3000\u8fd9\u5c11\u5973\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u51b0\u5ddd\u5929\u5973\uff01\u5510\u7ecf\u5929\u867d\u7136\u6599\u5230\u5979\u4e00\u5b9a\u4f1a\u6765\uff0c\u5374\u60f3\u4e0d\u5230\u5979\u5728\u8fd9\u7b49\u573a\u9762 \u4e4b\u4e0b\u51fa\u73b0\u3002\u53ea\u89c1\u5979\u5411\u5192\u5ddd\u751f\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u4f60\u8981\u6211\u7834\u7684\u5c31\u662f\u8fd9\u4e5d\u5bab\u516b\u5366\u9635\u5417\uff1f\u6211\u53ef \u4e0d\u613f\u4f24\u4eba\u3002\u201d\u5192\u5ddd\u751f\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u597d\u4e86\uff0c\u6211\u81ea\u7136\u4f1a\u7ed9\u4ed6\u4eec\u533b\u6cbb\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6050 \u6015\u4e5f\u5f97\u5c0f\u75c5\u4e2a\u628a\u6708\u5462\u3002\u201d\u51b0\u5ddd\u5929\u5973\u7edd\u4e16\u5bb9\u989c\uff0c\u7075\u5c71\u6d3e\u7684\u4e5d\u540d\u5f1f\u5b50\u4e4d\u89c1\u5979\u65f6\uff0c\u4e2a\u4e2a\u795e\u8ff7\u5fc3\u9189\uff0c \u51e0\u4e4e\u6ca1\u4eba\u60f3\u8d77\u5979\u5c31\u662f\u6765\u7834\u9635\u7684\u654c\u4eba\u3002\u5f85\u542c\u5f97\u5979\u548c\u5192\u5ddd\u751f\u4e00\u95ee\u4e00\u7b54\uff0c\u7adf\u7136\u597d\u4f3c\u7834\u9635\u90a3\u662f\u5fc5\u7136\u4e4b \u4e8b\uff0c\u6240\u987e\u8651\u7684\u53ea\u662f\u4ed6\u4eec\u53d7\u4f24\u6216\u751f\u75c5\u800c\u5df2\u3002\u8fd9\u4e00\u4e0b\uff0c\u987f\u65f6\u4ee4\u5f97\u7075\u5c71\u6d3e\u7684\u4e5d\u540d\u5f1f\u5b50\u90fd\u8d77\u4e86\u540c\u4ec7\u654c \u6c7d\u4e4b\u5fc3\uff0c\u53f6\u5929\u4efb\u957f\u5251\u4e00\u6325\uff0c\u5e03\u597d\u9635\u52bf\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5c31\u662f\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u53ea\u6028\u81ea\u5df1\u5b66\u827a\u4e0d \u7cbe\uff0c\u4f46\u5200\u5251\u65e0\u60c5\uff0c\u59d1\u5a18\uff0c\u4f60\u4e5f\u5f97\u5c0f\u5fc3\u5219\u4e2a\uff0c\u82e5\u7136\u4e00\u4e2a\u5931\u624b\uff0c\u5212\u4f24\u4e86\u4f60\u7684\u989c\u5bb9\uff0c\u8fd9\u7f6a\u6211\u4eec\u53ef\u62c5 \u5f85\u4e0d\u8d77\u3002\u201d \u3000\u3000\u4e5d\u67c4\u957f\u5251\uff0c\u95ea\u95ea\u53d1\u5149\uff0c\u53f6\u5929\u4efb\u8fd9\u756a\u8bdd\u867d\u662f\u6124\u6fc0\u4e4b\u8a00\uff0c\u5374\u4e5f\u6b63\u662f\u4f17\u4eba\u5fc3\u4e2d\u6240\u601d\uff0c\u51b0\u5ddd\u5929\u5973 \u5439\u5f39\u5f97\u7834\u7684\u7c89\u8138\uff0c\u53ea\u8981\u88ab\u5251\u5c16\u8f7b\u8f7b\u5212\u4e86\u4e00\u4e0b\uff0c\u90a3\u5c31\u662f\u5927\u715e\u98ce\u666f\u4e4b\u4e8b\u3002\u53ef\u662f\u5728\u5192\u5ddd\u751f\u7684\u8ddf\u524d\uff0c \u6709\u8a00\u5728\u5148\uff0c\u8c01\u53c8\u6562\u51fa\u58f0\u529d\u963b\uff1f \u3000\u3000\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u50b2\u7136\u4e00\u7b11\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u81ea\u7136\u663e\u51fa\u4e00\u79cd\u9ad8\u8d35\u5c0a\u4e25\u7684\u6c14\u6d3e\uff0c\u5bf9\u53f6\u5929\u4efb\u7684\u6d3b\u7ade\u4f3c \u4e0d\u5c51\u7f6e\u7b54\uff0c\u8f7b\u79fb\u83b2\u6b65\uff0c\u4e00\u4e0b\u5c31\u8fdb\u5165\u9635\u4e2d\uff0c\u6309\u9635\u52bf\u5e94\u8be5\u662f\u53f6\u5929\u4efb\u5148\u51fa\u5251\u5fa1\u654c\uff0c\u53f6\u5929\u4efb\u4e00\u9635\u8e0c \u8e87\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u53cc\u624b\u7a7a\u7a7a\uff0c\u4ed6\u7684\u5251\u4e3e\u4e86\u8d77\u6765\uff0c\u60f3\u523a\u53c8\u4e0d\u6562\u523a\u4e0b\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u80c6\u602f\u4e48\uff1f\u6211\u662f\u8ba9\u4f60\u4eec\u5148\u8fd0\u6c14\u62a4\u8eab\uff0c\u8981\u4e0d\u7136\u6211\u4e00\u52a8\u624b\uff0c\u4f60\u4eec\u5c31\u4e0d \u6b62\u75c5\u4e2a\u628a\u6708\u7684\u3002\u201d\u7075\u5c71\u6d3e\u7684\u5f1f\u5b50\u4e00\u9f50\u5927\u6012\uff0c\u9635\u52bf\u4e00\u8f6c\uff0c\u53f6\u5929\u4efb\u65c1\u8fb9\u7684\u4e24\u4e2a\u5e08\u5f1f\u7ed5\u4e86\u4e0a\u6765\uff0c\u6124 \u7136\u56b7\u9053\uff1a\u201c\u5e08\u5144\uff0c\u548c\u5979\u5ba2\u6c14\u4f5c\u751a\uff1f\u201d\u53cc\u5251\u9f50\u51fa\uff0c\u5404\u6309\u65b9\u4f4d\uff0c\u5de6\u8fb9\u7684\u9ed1\u8863\u4eba\u633d\u5251\u5e73\u524a\uff0c\u4f7f\u7684\u62db \u6570\u662f\u201c\u96c1\u843d\u5e73\u6c99\u201d\uff0c\u53f3\u8fb9\u7684\u6325\u5251\u659c\u523a\uff0c\u7528\u7684\u62db\u6570\u662f\u201c\u7384\u9e1f\u5212\u6c99\u201d\uff0c\u5408\u6210\u4e86\u4e00\u4e2a\u6781\u5389\u5bb3\u7684\u5251 \u5708\uff0c\u5c01\u7740\u4e86\u51b0\u5ddd\u5929\u5973\u5de6\u53f3\u4e24\u65b9\u7684\u9000\u8def\uff0c\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff0c\u9664\u4e86\u96f7\u9707\u5b50\u89c1\u8fc7\u51b0\u5ddd\u5929\u5973\u7684\u672c\u9886\u4e4b \u5916\uff0c\u4f59\u4eba\u90fd\u662f\u6697\u6697\u5fc3\u60ca\uff0c\u53ea\u6050\u8fd9\u53cc\u5251\u4e00\u5212\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u7c89\u8138\u4fbf\u5f97\u7559\u4e0b\u75a4\u75d5\u3002 \u3000\u3000\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u5a07\u58f0\u4e00\u7b11\uff0c\u8eab\u5f62\u5fae\u6643\uff0c\u7075\u5c71\u6d3e\u7684\u4e5d\u540d\u5f1f\u5b50\u8fde\u770b\u4e5f\u672a\u770b\u5f97\u6e05\u695a\uff0c\u53cc\u5251\u5df2\u523a\u4e86 \u4e2a\u7a7a\u3002\u9661\u7136\u95ee\uff0c\u4f46\u542c\u5f97\u6323\u7684\u4e00\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u62d4\u5251\u51fa\u9798\uff0c\u5bd2\u5149\u75be\u5c04\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u53f6\u5929\u4efb\u8fde\u6253\u4e09 \u4e2a\u5bd2\u54c4\uff0c\u90a3\u4e24\u4e2a\u523a\u51b0\u5ddd\u5929\u5973\u7684\u9ed1\u8863\u4eba\u529f\u529b\u8f83\u4f4e\uff0c\u66f4\u662f\u51b7\u5f97\u7259\u5173\u6253\u6218\uff0c\u5982\u5815\u51b0\u8c37\u3002 \u3000\u3000\u53f6\u5929\u4efb\u53eb\u9053\uff1a\u201c\u53d8\u9635\u6563\u5f00\uff0c\u7528\u6697\u9752\u5b50\u62db\u547c\u8fd9\u4e2a\u5996\u5973\uff01\u201d\u4e5d\u5bab\u516b\u5366\u9635\u672c\u6765\u662f\u5411\u91cc\u6536\u7d27\uff0c\u8fd9 \u65f6\u9aa4\u7684\u5411\u5916\u6269\u5f00\uff0c\u5916\u56f4\u65c1\u89c2\u7684\u4eba\u7eb7\u7eb7\u8d70\u907f\uff0c\u8ddd\u79bb\u7a0d\u8fdc\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u5c04\u51fa\u7684\u51b7\u6c14\uff0c\u52c9\u5f3a\u53ef\u4ee5\u62b5 \u53d7\uff0c\u53f6\u5927\u4efb\u4e00\u58f0\u547c\u54e8\uff0c\u516b\u4e2a\u65b9\u4f4d\uff0c\u6697\u5668\u9f50\u98de\uff0c\u90fd\u5411\u7740\u4e2d\u5fc3\u7ad9\u7acb\u7684\u51b0\u5ddd\u5929\u5973\u75be\u5c04\u3002\u51b0\u5ddd\u5929\u5973\u9053 \u4e86\u58f0\uff1a\u201c\u597d\uff01\u201d\u53cc\u6307\u9891\u5f39\uff0c\u5c06\u51b0\u9b44\u795e\u5f39\u4f3c\u51b0\u96f9\u822c\u7684\u4e71\u98de\u51fa\u53bb\uff0c\u90a3\u4e9b\u8f83\u4e3a\u7ec6\u5c0f\u7684\u6697\u5668\uff0c\u5982\u6885\u82b1 \u9488\u3001\u94c1\u83b2\u5b50\uff0c\u98de\u8757\u77f3\u3001\u8896\u7bad\u3001\u900f\u9aa8\u9489\u4e4b\u7c7b\uff0c\u88ab\u51b0\u5f39\u4e00\u78b0\uff0c\u7acb\u523b\u5815\u5730\uff0c\u51b0\u9b44\u795e\u5f39\u4e00\uff0c\u6563\uff0c\u4e00\u9897 \u9897\u597d\u4f3c\u73cd\u73e0\u5927\u5c0f\uff0c\u4eae\u6676\u6676\u7684\u4ece\u7a7a\u4e2d\u6d12\u4e0b\uff0c\u7834\u88c2\u4e4b\u540e\uff0c\u90a3\u5bd2\u5149\u51b7\u6c14\uff0c\u66f4\u662f\u5f25\u6f2b\u6269\u5f20\uff0c\u5b9b\u4f3c\u4ece\u7a7a \u4e2d\u7f69\u4e0b\u4e00\u5f20\u65e0\u5f62\u7684\u51b0\u7f51\u3002\u51b0\u9b44\u795e\u5f39\u662f\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u51b0\u8c37\u4e4b\u4e2d\u7684\u4e07\u8f7d\u5bd2\u51b0\u6240\u70bc\uff0c\u90a3\u52fa\u5bd2\u4e4b\u6c14\uff0c \u523a\u4f53\u4fb5\u80a4\uff0c\u6bd4\u51b0\u9b44\u5bd2\u5149\u5251\u8fd8\u5389\u5bb3\u5f97\u591a\uff0c\u65c1\u89c2\u8005\u529f\u529b\u6084\u4f4e\u7684\u90fd\u4e0d\u7981\u98a4\u6296\uff0c\u6324\u5230\u5916\u8fb9\uff0c\u7075\u5c71\u6d3e\u7684 \u5f1f\u5b50\u9996\u5f53\u5176\u51b2\uff0c\u66f4\u662f\u7981\u53d7\u4e0d\u8d77\uff0c\u6709\u51e0\u4e2a\u5df1\u51b7\u5f97\u6d51\u8eab\u65e0\u529b\uff0c\u762b\u5728\u5730\u4e0a\uff0c \u3000\u3000\u8f83\u5927\u7684\u6697\u5668\u51b0\u9b44\u795e\u5f39\u78b0\u5b83\u4e0d\u843d\uff0c\u51b0\u5ddd\u5929\u5973\u4f7f\u7528\u51b0\u5251\u62e8\u5f00\uff0c\u5176\u4e2d\u4e00\u4ef6\u6697\u5668\uff0c\u5f62\u5982\u66f2\u5c3a\uff0c\u5e26 \u7740\u545c\u545c\u7684\u602a\u5578\u4e4b\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u89c9\u5f97\u5947\u602a\uff1b\u7528\u51b0\u5251\u4e00\u62e8\uff0c\u90a3\u6697\u5668\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u56de\u65cb\uff0c \u201c\u76f4\u523a\u51b0\u5ddd\u5929\u5973\u9165\u80f8\uff0c\u8fd9\u4e00\u4e0b\u602a\u5f02\u7684\u6765\u52bf\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u4eba\u4e1b\u4e2d\u5ffd\u542c\u5230\u6709\u4eba\u53eb \u9053\uff1a\u201c\u91d1\u521a\u6307\u201d\u3002\u51b0\u5ddd\u5929\u5973\u719f\u4e60\u5404\u6d3e\u6b66\u5668\uff0c\u5bf9\u91d1\u521a\u6307\u4ea6\u66fe\u7ec3\u8fc7\uff0c\u6025\u5fd9\u53cc\u6307\u4e00\u67d1\uff0c\u5c06\u6697\u5668\u67d1 \u4f4f\uff0c\u5140\u662f\u8dc3\u52a8\u4e0d\u4f11\u3002\u51b0\u5ddd\u5929\u5973\u56de\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u6b63\u7ad9\u5728\u4eba\u4e1b\u4e4b\u4e2d\u5411\u5979\u5fae\u7b11\u3002\u518d\u4e00\u770b\uff0c\u53ea \u89c1\u53f6\u5929\u4efb\u53cc\u773c\u901a\u7ea2\uff0c\u53cc\u624b\u5404\u6263\u7740\u4e00\u4ef6\u5947\u5f62\u6697\u5668\uff0c\u6b63\u5f85\u53d1\u653e\u3002\u539f\u6765\u8fd9\u6697\u5668\u540d\u4e3a\u201c\u56de\u73af\u94a9\u201d\uff0c\u4e43 \u662f\u97e9\u91cd\u5c71\u5f53\u5e74\u8d56\u4ee5\u6210\u540d\u7684\u6697\u5668\uff0c\u53ef\u4ee5\u659c\u98de\u8f6c\u6298\uff0c\u78b0\u7269\u56de\u7fd4\uff0c\u6076\u6bd2\u65e0\u6bd4\u3002\u5e78\u800c\u53f6\u5929\u4efb\u529f\u529b\u4e0e\u51b0 \u5ddd\u5929\u5973\u76f8\u5dee\u751a\u8fdc\uff0c\u8981\u4e0d\u7136\u7528\u91d1\u521a\u6307\u4e5f\u67d1\u5b83\u4e0d\u4f4f\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u7167\u9762\u4e4b\u95f4\uff0c\u53f6\u5929\u4efb\u53cc\u624b\u9f50\u626c\uff0c\u4e24\u67c4\u56de\u73af\u94a9\u90fd\u5e26\u7740\u602a\u5578\u4e4b\u58f0\u76d8\u65cb\u98de\u51fa\uff0c\u51b0\u5ddd\u5929\u5973\u4e00 \u624b\u6301\u5251\uff0c\u5355\u51ed\u5de6\u624b\u7684\u91d1\u521a\u6307\u529b\uff0c\u4e0d\u80fd\u67d1\u4f4f\u4e24\u67c4\u56de\u73af\u94a9\uff0c\u90a3\u4e24\u67c4\u56de\u73af\u94a9\u6765\u52bf\u6781\u6025\uff0c\u5de6\u53f3\u76d8\u65cb\uff0c \u5408\u6210\u4e86\u4e00\u4e2a\u5706\u5b64\uff0c\u4e0d\u8bba\u5411\u54ea\u65b9\u8eb2\u95ea\uff0c\u90fd\u96be\u514d\u88ab\u94a9\u4e0a\u7684\u5229\u5203\u6240\u523a\uff0c\u5728\u964d\u9ad8\u624b\uff0c\u6035\u76ee\u60ca\u5fc3\uff0c\u90fd\u5728 \u60f3\u9053\uff1a\u7075\u5c71\u6d3e\u7684\u6b66\u529f\u5012\u4e0d\u89c1\u5f97\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u4e4b\u5904\uff0c\u4f46\u8fd9\u6697\u5668\u7684\u53e4\u602a\uff0c\u5374\u662f\u5389\u5bb3\u975e\u5e38\uff0c\u7aef\u7684\u4e0d \u5728\u5510\u5bb6\u4e4b\u4e0b\u3002 \u3000\u3000\u6b63\u5728\u5927\u5bb6\u5c4f\u606f\u800c\u89c2\u4e4b\u9645\uff0c\u90a3\u4e24\u67c4\u56de\u73af\u94a9\u770b\u770b\u5c31\u8981\u78b0\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u5ffd\u89c1\u9752\u8863\u95ea\u52a8\uff0c\u88d9\u5e26\u98de \u626c\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5927\u6bbf\u4e4b\u4e2d\uff0c\u5ffd\u7136\u4e0d\u89c1\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u5f71\u5b50\uff0c\u4f17\u4eba\u6b63\u5728\u9519\u6115\uff0c\u90a3\u4e24\u67c4\u56de\u73af\u94a9\u65e0\u4eba \u62e6\u6321\uff0c\u7adf\u7136\u5e26\u7740\u9e23\u545c\u7684\u5578\u58f0\uff0c\u76f4\u5411\u4eba\u4e1b\u4e4b\u4e2d\u98de\u6765\u3002\u4f17\u4eba\u767b\u65f6\u9a9a\u52a8\uff0c\u6709\u7684\u95ea\u907f\uff0c\u6709\u7684\u4fbf\u60f3\u51fa\u624b \u786c\u63a5\uff0c\u4e71\u7cdf\u7cdf\u4e4b\u9645\uff0c\u5ffd\u89c1\u4e24\u9053\u9e1f\u91d1\u5149\u534e\u817e\u7a7a\u98de\u8d77\uff0c\u53ee\u53ee\u4e24\u58f0\uff0c\u90a3\u4e24\u67c4\u56de\u73af\u94a9\u5ffd\u7136\u6389\u5934\u98de\u56de\uff0c \u53bb\u52bf\u5982\u7535\uff0c\u6bd4\u521a\u624d\u53f6\u5929\u4efb\u53d1\u51fa\u4e4b\u65f6\u8fd8\u8981\u5feb\u901f\u5f97\u591a\uff01 \u3000\u3000\u4f17\u4eba\u53c8\u662f\u5927\u9a87\uff0c\u8fd9\u56de\u73af\u94a9\u76d8\u65cb\u98de\u51fa\uff0c\u529b\u9053\u6781\u5f3a\uff0c\u7adf\u7136\u7ed9\u4eba\u7528\u6697\u5668\u6253\u56de\u3002\u8fd9\u4efd\u529f\u529b\u6bd4\u96f7\u9707 \u5b50\u53f6\u5929\u4efb\u7b49\u8f88\uff0c\u9ad8\u51fa\u4f55\u6b62\u5341\u500d\uff01\u90a3\u4e24\u67c4\u56de\u73af\u94a9\u6389\u5934\u4e4b\u540e\uff0c\u76f4\u98de\u5982\u77e2\uff0c\u7adf\u7136\u98de\u5230\u4e86\u5192\u5ddd\u751f\u7684\u8bb2 \u575b\uff0c\u5ea7\u4e2d\u8bb8\u591a\u9ad8\u624b\u672c\u5f85\u5bfb\u89c5\u90a3\u53d1\u6697\u5668\u7684\u4eba\uff0c\u4f46\u5728\u8fd9\u6837\u7d27\u5f20\u7684\u5173\u5934\uff0c\u54ea\u80fd\u5206\u51fa\u5fc3\u795e\u65c1\u89c2\u3002 \u3000\u3000\u4f46\u89c1\u5192\u5ddd\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u6325\u8896\u4e00\u62c2\uff0c\u90a3\u4e24\u67c4\u56de\u73af\u94a9\u53c8\u6fc0\u5c04\u800c\u51fa\uff0c\u98de\u5f97\u751a\u9ad8\uff0c\u970e\u773c\u4e4b\u65f6\uff0c\u4fbf \u4ece\u4f17\u4eba\u5934\u9876\u8d8a\u8fc7\uff0c\u5c04\u5230\u5927\u6bbf\u4e4b\u5916\u3002\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u5ffd\u542c\u5f97\u53f6\u5929\u4efb\u60e8\u53eb\u4e00\u58f0\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c \u624b\u98a4\u811a\u6296\uff0c\u5728\u5730\u4e0a\u6eda\u8f6c\uff0c\u5982\u4e2d\u75af\u9b54\u3002\u4f17\u4eba\u773c\u775b\u9aa4\u7136\u4e00\u4eae\uff0c\u51b0\u5ddd\u5929\u5973\u8eab\u5f62\u53c8\u500f\u5730\u91cd\u73b0\uff0c\u7ad9\u5728\u575b \u524d\u3002\u539f\u6765\u5979\u9002\u624d\u8dc3\u81f3\u6881\u4e0a\uff0c\u53ea\u56e0\u8eab\u6cd5\u592a\u5feb\uff0c\u4f17\u4eba\u8fde\u770b\u4e5f\u770b\u4e0d\u6e05\u695a\u3002\u5979\u6068\u90a3\u53f6\u5929\u4efb\u592a\u8fc7\u6b79\u6bd2\uff0c \u907f\u8fc7\u56de\u73af\u94a9\u540e\u3002\u968f\u624b\u5f39\u51fa\u4e00\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u6253\u4e2d\u4e86\u65f6\u5929\u4efb\u7684\u592a\u9633\u7a74\uff0c\u90a3\u5947\u5bd2\u4e4b\u6c14\u968f\u7740\u7a74\u9053\u76f4\u94bb \u5fc3\u5934\uff0c\u53f6\u5929\u4efb\u5982\u4f55\u62b5\u53d7\u5f97\u4f4f\u3001 \u3000\u3000\u5192\u5ddd\u751f\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u4f17\u5f1f\u5b50\u8d76\u5feb\u6551\u4eba\uff01\u201d\u96f7\u9707\u5b50\u7b49\u4e00\u4f17\u6b66\u5f53\u5f1f\u5b50\u65e9\u5df2\u4f3a\u5019 \u5728\u65c1\uff0c\u8fd9\u65f6\u7075\u5c71\u6d3e\u4e5d\u4e2a\u9ed1\u8863\u4eba\u4e2a\u4e2a\u90fd\u53d7\u51b0\u9b44\u795e\u5f39\u4e4b\u4f24\uff0c\u5c24\u4ee5\u53f6\u5929\u4efb\u4f24\u5f97\u6700\u91cd\uff0c\u96f7\u9707\u4e8e\u6025\u6307\u6325 \u540c\u95e8\u5c06\u4ed6\u4eec\u625b\u5165\u540e\u9662\u7985\u5c45\u3002\u6bbf\u4e2d\u79e9\u5e8f\u521a\u521a\u6062\u590d\uff0c\u5ffd\u542c\u5f97\u789f\u789f\u7684\u602a\u7b11\u4e4b\u58f0\uff0c\u4ece\u5916\u4f20\u6765\u3002 \u3000\u3000\u7b11\u58f0\u6447\u66f3\uff0c\u9707\u5f97\u5927\u6bbf\u55e1\u55e1\u4f5c\u54cd\uff0c\u4f17\u4eba\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5934\u4e0a\u9aa4\u7136\u98de\u8d77\u4e00\u7247\u7ea2\u4e91\uff0c\u81ea\u6bbf\u5916\u4e00 \u63a0\u800c\u5165\uff0c\u4ece\u4f17\u4eba\u5934\u4e0a\u8d8a\u8fc7\uff0c\u843d\u5728\u575b\u524d\u3002\u539f\u6765\u662f\u4e00\u4e2a\u7a7f\u7740\u7ea2\u8863\u7684\u7626\u957f\u6c49\u5b50\uff0c\u4e24\u988a\u6df1\u9677\uff0c\u53cc\u775b\u5982 \u706b\uff0c\u5934\u53d1\u84ec\u4e71\uff0c\u72f0\u72de\u6015\u4eba\u3002\u5ea7\u4e2d\u6709\u4e00\u4e24\u4e2a\u8f83\u4e3a\u5e74\u957f\u7684\uff0c\u558a\u51fa\u6765\u9053\uff1a\u201c\u8d64\u795e\u5b50\uff01\u201d \u3000\u3000\u8d64\u795e\u5e72\u789f\u789f\u602a\u7b11\uff0c\u5bf9\u7740\u5192\u5ddd\u751f\u53ea\u662f\u5fae\u5fae\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u50b2\u5cb8\u4e4b\u6781\uff0c\u7a81\u7136\u4f38\u51fa\u84b2\u6247\u822c\u7684\u5927 \u624b\uff0c\u5411\u4e0b\u4e00\u6454\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u6bd4\u8bd5\u6b66\u529f\uff0c\u600e\u4e48\u6697\u5668\u5315\u5230\u6211\u7684\u5934\u4e0a\u6765\u4e86\u3002\u201d\u54e8\u54e8\u4e24\u58f0\uff0c\u6454 \u4e0b\u7684\u5c31\u662f\u90a3\u4e24\u67c4\u56de\u73af\u94a9\uff0c\u8dcc\u5728\u5730\u4e0a\uff0c\u88c2\u6210\u516b\u7247\u3002\u4f17\u4eba\u5747\u5403\u4e86\u4e00\u60ca\uff0c\u8d64\u795e\u5b50\u7684\u6307\u529b\u4e4b\u5f3a\uff0c\u786e\u5df2 \u5230\u4e86\u634f\u77f3\u5982\u7c89\u7684\u5730\u6b65\u3002 \u3000\u3000\u5192\u5ddd\u751f\u9053\uff1a\u201c\u8d64\u795e\u9053\u53cb\uff0c\u4ed6\u4eec\u540e\u8f88\u7684\u6697\u5668\uff0c\u600e\u4e48\u4f24\u5f97\u4e86\u4f60\uff0c\u4f55\u5fc5\u52a8\u6c14\uff1f\u201d\u8d64\u795e\u4e8e\u201c\u54fc\u201d \u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u628a\u90a3\u53d1\u6697\u5668\u7684\u540e\u8f88\u53eb\u51fa\u6765\u3002\u201d\u5192\u5ddd\u751f\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6b64\u523b\u6b63\u5728\u51b7\u70ed\u4ea4\u4f5c\uff0c\u5f85 \u4ed6\u4eec\u75c5\u597d\u4e4b\u540e\uff0c\u4f60\u518d\u5230\u7075\u9e70\u5c71\u627e\u4e91\u7075\u5b50\u592b\u5987\u53bb\u5427\u3002\u201d\u4e91\u7075\u5b50\u592b\u5987\u662f\u7075\u5c71\u6d3e\u7684\u957f\u8001\uff0c\u4ea6\u662f\u8d64\u795e \u4e8e\u7684\u597d\u53cb\u3002\u8d64\u795e\u5b50\u4e00\u542c\uff0c\u76b1\u76b1\u7709\u5934\uff0c\u671d\u5730\u4e0a\u4e00\u77a7\uff0c\u8ba4\u51fa\u90a3\u662f\u7075\u5c71\u6d3e\u7684\u72ec\u95e8\u6697\u5668\u56de\u73af\u94a9\uff0c\u4ed6\u672c \u6765\u5b58\u5fc3\u6311\u8845\uff0c\u4e00\u8ba1\u4e0d\u552e\uff0c\u63a5\u7740\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u53cc\u6307\u4e4b\u95f4\u67d1\u7740\u4e24\u652f\u8896\u7bad\u822c\u957f\u77ed\u7684\u8292 \u523a\uff0c\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u7075\u5c71\u6d3e\u7684\u6697\u5668\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e00\u8dc3\u79bb\u5ea7\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u6211\u53d1\u7684\u5929\u5c71\u795e\u8292\uff0c\u4f60\u5f85\u600e\u6837\uff1f\u201d\u539f\u6765\u5510\u7ecf\u5929\u521a\u624d\u7528\u5929\u5c71 \u795e\u8292\u6253\u98de\u53f6\u5929\u4efb\u7684\u56de\u73af\u94a9\uff0c\u5929\u5c71\u795e\u8292\u5d4c\u5165\u94a9\u4e2d\uff0c\u8fd9\u65f6\u4e5f\u5230\u4e86\u8d64\u795e\u4e8e\u624b\u4e0a\uff0c\u5929\u5c71\u795e\u8292\u575a\u903e\u91d1 \u94c1\uff0c\u4ed6\u634f\u4e4b\u4e0d\u65ad\u3002\u8d64\u795e\u5b50\u77aa\u4e86\u5510\u7ecf\u5929\u4e00\u773c\uff0c\u5411\u5192\u5ddd\u751f\u7a3d\u9996\u8bf4\u9053\uff1a\u201c\u4f60\u5f00\u5c71\u7ed3\u7f18\uff0c\u76db\u4f1a\u96be\u9022\uff0c \u6211\u4e5f\u6c42\u4f60\u6307\u70b9\u6307\u70b9\u3002\u201d\u8d64\u795e\u5b50\u672c\u610f\u662f\u60f3\u85c9\u6b64\u4e0e\u5510\u7ecf\u5929\u52a8\u624b\uff0c\u4f46\u6151\u4e8e\u5192\u5ddd\u751f\u7684\u5fb7\u5c0a\u671b\u91cd\uff0c\u5230\u5e95 \u4e0d\u6562\u8fc7\u4e8e\u653e\u8084\uff0c\u6240\uff0c\u4ee5\u59d1\u4e14\u7167\u201c\u7ed3\u7f18\u201d\u7684\u89c4\u77e9\uff0c\u8bdd\u660e\u5728\u5148\uff0c\u7136\u540e\u597d\u4e0e\u5510\u7ecf\u5929\u6bd4\u8bd5\u3002\u4e0d\u610f\u5192\u5ddd \u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u9053\u53cb\u4e5f\u6765\uff0c\u201d\u6307\u70b9\u2019\u90a3\u662f\u4e0d\u6562\u5f53\u7684\uff0c\u6211\u53eb\u6211\u7684\u4f84\u5973\u5411\u4f60\u9886\u6559\u5427\u3002 \u51b0\u5a25\uff0c\u4f60\u5c31\u4f7f\u4e00\u8d9f\u8fbe\u6469\u5251\u6cd5\uff0c\u5411\u8fd9\u4f4d\u524d\u8f88\u8bf7\u76ca\u5427\u3002\u201d \u3000\u3000\u8d64\u795e\u5b50\u4e0e\u5192\u5ddd\u751f\u540c\u4e00\u8f88\u4efd\uff0c\u5192\u5ddd\u751f\u6b64\u8a00\uff0c\u8868\u9762\u4f3c\u662f\u8c26\u865a\uff0c\u5b9e\u5373\u4ecd\u662f\u628a\u4ed6\u5f53\u505a\u6765\u201c\u7ed3\u7f18\u201d \u7684\u4e00\u822c\u540e\u8f88\u770b\u5f85\uff0c\u8d64\u795e\u5b50\u52c3\u7136\u5927\u6012\uff0c\u6b63\u5f85\u53d1\u4f5c\uff0c\u53ea\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u524d\u8f88\u6211\u5df1\u9886\u6559 \u8fc7\u591a\u6b21\u4e86\uff0c\u6211\u770b\u4ed6\u518d\u82e6\u7ec3\u5341\u5e74\uff0c\u4e0b\u6b21\u518d\u6765\uff0c\u6c42\u4f60\u8001\u4eba\u5bb6\u7ed3\u7f18\uff0c\u4e5f\u8fd8\u672a\u665a\u3002\u201d\u8fd9\u8bf4\u8bdd\u5373\u662f\u8bf4\u4ee5 \u8d64\u795e\u5b50\u73b0\u5728\u7684\u672c\u9886\uff0c\u8fde\u5979\u4e5f\u6253\u4e0d\u8fc7\u3002\u5192\u5ddd\u751f\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u771f\u662f\u521d\u51fa\u8305\u5e90\uff0c\u4e0d\u77e5\u6ca7\u6d77\u4e4b \u5927\u3002\u201d\u6b64\u8bed\u4f3c\u8d23\u4f3c\u8d5e\uff0c\u8d64\u795e\u5b50\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u4f38\u51fa\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u671d\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u547c\u7684\u4e00\u638c \u62cd\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u770b\u662f\u8c01\u8981\u82e6\u7ec3\u5341\u5e74\u3002\u201d\u5510\u7ecf\u5929\u624b\u629a\u6e38\u9f99\u5251\u67c4\uff0c\u8e0c\u8e87\u672a\u9000\uff0c\u5192\u5ddd\u751f\u5411 \u4ed6\u6325\u4e00\u6325\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u8981\u6765\u7ed3\u7f18\u5417\uff1f\u8fd9\u6b21\u672a\u66fe\u8f6e\u5230\u4f60\uff0c\u4f60\u4e0b\u53bb\u6b47\u6b47\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9000\u56de\u539f\u5ea7\uff0c\u8d64\u795e\u5b50\u4e0e\u51b0\u5ddd\u5929\u5973\u5df2\u5728\u575b\u524d\u4ea4\u624b\uff0c\u8d64\u795e\u5b50\u4f38\u51fa\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u626c\u7a7a\u4e00 \u6293\uff0c\u4e00\u6293\u4e0d\u4e2d\uff0c\u7acb\u5373\u53d8\u62db\uff0c\u53cc\u638c\u7275\u5f15\uff0c\u5212\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u5f90\u5f90\u63a8\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u6709 \u4e00\u4e2a\u4eba\u5df2\u6655\u5012\u5730\u4e0a\u3002\u5ea7\u4e2d\u79bb\u624b\uff0c\u5747\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8fd9\u8d64\u795e\u5b50\u7684\u529f\u592b\u602a\u5f02\u4e4b\u6781\uff0c\u53cc\u638c\u901a\u7ea2\u5982\u8840\uff0c\u539f\u6765\u4ed6\u624b\u638c\u4e0a\u7684\u76ae\u80a4\u90fd\u5df2\u5265\u53bb\uff0c\u8fde\u9aa8\u5934\u90fd\u5fd1 \u4e86\u51fa\u6765\uff0c\u8fd9\u8fd8\u4e0d\u8db3\u9a87\u4eba\uff0c\u66f4\u9a87\u4eba\u7684\u662f\uff0c\u4ed6\u638c\u631f\u52b2\u98ce\uff0c\u70ed\u547c\u547c\u7684\uff0c\u7adf\u4f3c\u9f13\u98ce\u7089\u4e2d\u55b7\u51fa\u7684\u4e00\u80a1\u70ed \u98ce\uff0c\u56f4\u5728\u524d\u9762\u89c2\u6218\u7684\u4eba\uff0c\u529f\u529b\u7a0d\u4f4e\u7684\u90fd\u7acb\u611f\u547c\u5438\u4e0d\u8212\uff0c\u95f7\u70ed\u96be\u53d7\uff0c\u6709\u4e00\u4e2a\u4eba\u7adf\u56e0\u6b64\u6655\u5012\u3002\u4f17 \u4eba\u88ab\u70ed\u6d6a\u8feb\u5f97\u4e0d\u7531\u81ea\u5df1\u7684\u540e\u9000\uff0c\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u9ed4\u9a74\u4e4b\u6280\uff0c\u4e0d\u8fc7\u5c14\u5c14\u3002\u201d\u51b0\u9b44\u5bd2\u5149\u5251\u9661\u7684 \u4e00\u6325\uff0c\u987f\u65f6\u5bd2\u5149\u8000\u773c\uff0c\u51b7\u98ce\u56db\u5c04\uff0c\u90a3\u95f7\u70ed\u4e4b\u6c14\uff0c\u5168\u88ab\u9a71\u6563\u3002\u51b7\u70ed\u76f8\u6d88\uff0c\u4f17\u4eba\u90fd\u89c9\u7cbe\u795e\u4e00\u723d\uff0c \u53c8\u56f4\u4e0a\u524d\u6765\uff0c\u770b\u4ed6\u4eec\u4ea4\u624b\u3002 \u3000\u3000\u53ea\u89c1\u8d64\u795e\u5b50\u72c2\u547c\u75be\u640f\uff0c\u4f0a\u5982\u4e00\u5934\u53d1\u4e86\u72c2\u7684\u91ce\u517d\u3002\u4ed6\u638c\u52bf\u98d8\u5ffd\uff0c\u51fa\u62db\u5982\u7535\uff0c\u51b0\u5ddd\u5929\u5973\u8eab\u6cd5 \u867d\u662f\u8f7b\u7075\u4e4b\u6781\u3002\u4ecd\u7136\u7ed9\u4ed6\u5982\u5f71\u968f\u5f62\uff0c\u638c\u950b\u603b\u662f\u4e0d\u79bb\u8981\u5bb3\u3002\u4f46\u4ed6\u7684\u638c\u52bf\u867d\u662f\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u5374\u4e5f\u78b0 \u4e0d\u7740\u51b0\u5ddd\u5929\u5973\u7684\u8863\u88f3\u3002\u4f17\u4eba\u90fd\u4e0d\u7981\u55b7\u55b7\u79f0\u5f02\u3002\u770b\u6765\u51b0\u5ddd\u5929\u5973\u4f3c\u662f\u6682\u5904\u4e0b\u98ce\uff0c\u4f46\u5979\u5251\u968f\u8eab\u8f6c\uff0c \u6bcf\u4e00\u62db\u6bcf\u4e00\u5f0f\u90fd\u523a\u524a\u5f97\u6070\u5230\u597d\u5904\uff0c\u53cc\u65b9\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u8d64\u795e\u5b50\u7adf\u6ca1\u5360\u5230\u4e1d\u6beb\u4fbf\u5b9c\u3002 \u3000\u3000\u5192\u5ddd\u751f\u9762\u9732\u7b11\u5bb9\uff0c\u4e00\u9762\u770b\u4e00\u9762\u70b9\u9996\uff0c\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u4e24\u4eba\u653b\u5b88\u5747\u6b63\u3002\u53ea\u662f\u8d64\u795e\u9053\u53cb\u7684\u638c\u529b \u8fd8\u672a\u53d1\u6325\u5c3d\u81f4\uff1b\u51b0\u5a25\uff0c\u4f60\u7684\u6218\u6cd5\u8f7b\u7075\u5df2\u662f\u6070\u5230\u597d\u5904\uff0c\u7a33\u5065\u4e5f\u8db3\u9632\u5fa1\uff0c\u53ea\u662f\u5251\u5b66\u6709\u5982\u5175\u6cd5\uff0c\u8981 \u8bb2\u7a76\u51fa\u5947\u5236\u80dc\uff0c\u4f60\u7684\u504f\u950b\u53d8\u5316\uff0c\u5c1a\u672a\u5c3d\u8fbe\u6469\u5251\u6cd5\u7684\u6240\u957f\u3002\u201d\u4ed6\u968f\u5373\u5c31\u4e24\u4eba\u7684\u638c\u6cd5\u5251\u6cd5\uff0c\u6307\u70b9 \u4e86\u51e0\u62db\uff0c\u8bb2\u7684\u90fd\u662f\u6700\u4e0a\u4e58\u7684\u6b66\u529f\u5965\u4e49\uff0c\u9664\u4e86\u5510\u7ecf\u5929\u7b49\u6709\u9650\u51e0\u4eba\uff0c\u4f59\u4eba\u90fd\u662f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u8d64\u795e\u5b50\u5374\u662f\u53c8\u60ca\u53c8\u6012\uff0c\u4ed6\u548c\u5192\u5ddd\u751f\u672c\u662f\u5e73\u8f88\uff0c\u800c\u4eca\u542c\u4ed6\u7684\u6307\u70b9\uff0c\u7adf\u662f\u6df1\u901a\u81ea\u5df1\u6b66\u529f\u7684\u7a8d \u8981\uff0c\u800c\u4e14\u4e24\u8fb9\u6307\u70b9\uff0c\u4ea6\u5e76\u65e0\u504f\u8892\u4e4b\u5904\u3002\u56e0\u6b64\u8d64\u795e\u5b50\u867d\u6068\u5192\u5ddd\u751f\u5f53\u4f17\u8d2c\u4f4e\u4ed6\u7684\u8eab\u4efd\uff0c\u5c06\u4ed6\u5f53\u4f5c \u540e\u8f88\u6765\u201c\u7ed3\u7f18\uff0c\u2019\u7684\u4eba\u4e00\u6837\u770b\u5f85\uff0c\u5374\u4e5f\u505a\u58f0\u4e0d\u5f97\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u7ecf\u6307\u70b9\uff0c\u51fa\u62db\u8d8a\u53d1\u7cbe\u5999\uff0c\u771f\u7684 \u662f\u610f\u5728\u5251\u5148\uff0c\u8d64\u795e\u5b50\u7684\u540e\u7740\u4e5f\u5e38\u88ab\u5979\u6599\u53ca\uff0c\u9884\u5148\u9632\u5fa1\u3002\u8d64\u795e\u5b50\u8fd9\u4e00\u6d3e\u7684\u6b66\u529f\u662f\u8d8a\u6218\u5a01\u529b\u8d8a \u5f3a\uff0c\u638c\u529b\u8d8a\u6765\u8d8a\u91cd\uff0c\u8d64\u795e\u4e8e\u66fe\u4e0e\u51b0\u5ddd\u5929\u5973\u4ea4\u624b\u6570\u6b21\uff0c\u6df1\u77e5\u5979\u7684\u529f\u529b\u6bd4\u81ea\u5df1\u5c1a\u900a\u4e00\u7b79\u3002\u8fd9\u65f6\u5df2 \u6597\u5230\u4e86\u5c06\u8fd1\u4e24\u767e\u62db\uff0c\u8d64\u795e\u4e8e\u7684\u638c\u529b\u5df2\u53d1\u6325\u5230\u5c3d\u5904\u3002\u4e00\u4e3e\u624b\u4e00\u6295\u8db3\u90fd\u5e26\u7740\u4e00\u80a1\u52b2\u98ce\uff0c\u56f4\u89c2\u8bf8 \u4eba\uff0c\u6e10\u6e10\u89c9\u5f97\u70ed\u98ce\u76d6\u8fc7\u4e86\u51b7\u6c14\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u53c8\u5411\u540e\u632a\u52a8\u3002\u8d64\u795e\u5b50\u6597\u5230\u5206\u9645\uff0c\u5ffd\u5730\u4e00\u58f0\u72de\u7b11\uff0c \u5168\u8eab\u9aa8\u683c\u683c\u683c\u4f5c\u54cd\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u4e24\u53ea\u84b2\u6247\u822c\u7684\u5927\u624b\u4ea4\u53c9\u65a9\u4e0b\uff0c\u5468\u56f4\u7684\u6570\u4e08\u65b9\u5706\u4e4b\u5730\uff0c\u5168 \u5728\u4ed6\u7684\u638c\u529b\u7b3c\u7f69\u4e0b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e5f\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u67f3\u8170\u4e00\u6298\uff0c\u5251\u5149\u970d\u5730\u6563\u5f00\uff0c\u987f\u89c9\u5bd2\u6f6e\u531d\u5730\uff0c\u51b7\u6c14 \u5f25\u7a7a\uff0c\u51b0\u5ddd\u5929\u5973\u5168\u8eab\u7ade\u4f3c\u88ab\u5305\u56f4\u5728\u4e00\u5c42\u8f7b\u7ee2\u8584\u96fe\u4e4b\u4e2d\uff0c\u65c1\u89c2\u8005\u5fc3\u8ff7\u76ee\u7729\uff0c\u53ea\u6709\u5510\u7ecf\u5929\u7b49\u6709\u9650 \u51e0\u4eba\u770b\u5f97\u6e05\u695a\u3002\u53ea\u89c1\u8d64\u795e\u5b50\u90a3\u80a1\u51f6\u731b\u5982\u631f\u98ce\u96f7\u7684\u638c\u52bf\uff0c\u5728\u51b0\u9b44\u5bd2\u5149\u7684\u963b\u9694\u4e4b\u4e0b\uff0c\u505c\u4e86\u4e00\u505c\uff0c \u4e0d\u6562\u5373\u884c\u4e0b\u6251\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8d64\u795e\u5b50\u7684\u638c\u529b\u5c06\u53d1\u672a\u53d1\u4e4b\u9645\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u4e2a\u8e09\u8dc4\u5012 \u9000\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u5251\uff0c\u5bd2\u5149\u9aa4\u8d77\uff0c\u7adf\u7136\u4ece\u8d64\u795e\u5b50\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u523a\u4e86\u5165\u6765\uff0c\u8d64\u795e\u5b50\u5403\u4e00 \u60ca\uff0c\u56de\u638c\u62a4\u80f8\uff0c\u53ea\u542c\u5f97\u5237\u7684\u4e00\u5251\uff0c\u8d64\u795e\u5b50\u5934\u4e0a\u7684\u4e71\u53d1\u5df2\u88ab\u524a\u53bb\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u5510\u7ecf\u5929\u53c8\u60ca\u53c8\u559c\uff0c\u4ed6\u6df1\u77e5\u8d64\u795e\u5b50\u529f\u529b\u9ad8\u4e8e\u51b0\u5ddd\u5929\u5973\uff0c\u4e00\u76f4\u4e3a\u51b0\u5ddd\u5929\u5973\u63d0\u5fc3\uff0c\u60f3\u4e0d\u5230\u5979\u5728 \u4e34\u5371\u4e4b\u9645\uff0c\u5148\u540e\u4f7f\u51fa\u4e24\u62db\u8fbe\u9b54\u5251\u6cd5\u7684\u602a\u62db\uff0c\u4e00\u62db\u201c\u6d77\u4e0a\u660e\u971e\u201d\u3001\u4e00\u62db\u201c\u4e00\u82c7\u6e21\u6c5f\u201d\uff0c\u653b\u5b88\u8054 \u6210\u4e00\u6c14\uff0c\u5947\u6b63\u76f8\u751f\uff0c\u7adf\u7136\u628a\u8d64\u795e\u5b50\u6740\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u8fde\u5510\u7ecf\u5929\u4e5f\u6599\u4e0d\u5230\u5979\u7684\u5251\u6cd5\u7a81\u7136\u95ee\u7cbe\u8fdb\u5982 \u65af\uff01\u539f\u6765\u51b0\u5ddd\u5929\u5973\u5230\u4e86\u91d1\u5149\u5bfa\u540e\uff0c\u5f97\u5192\u5ddd\u751f\u7684\u6307\u70b9\uff0c\u66f4\u609f\u4e86\u8fbe\u6469\u5251\u6cd5\u7684\u7cbe\u9ad3\uff0c\u52a0\u4ee5\u5979\u4e0d\u754f\u8d64 \u795e\u5b50\u7684\u638c\u5fc3\u70ed\u529b\uff0c\u8fbe\u6469\u5251\u6cd5\u7684\u5947\u62db\u4e00\u51fa\uff0c\u6070\u6070\u6210\u4e86\u8d64\u795e\u5b50\u7684\u514b\u661f\u3002 \u3000\u3000\u8d64\u795e\u5b50\u54ea\u7518\u8d25\u5728\u540e\u8f88\u540e\u4e2d\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u53c8\u805a\u4e86\u5168\u8eab\u529f\u529b\uff0c\u8fde\u73af\u8fd0\u638c\uff0c\u52bf\u5982\u6392\u5c71\u5012\u6d77\u3002\u51b0 \u5ddd\u5929\u5973\u8e0f\u7740\u4e5d\u5bab\u516b\u5366\u65b9\u4f4d\uff0c\u4e0d\u4f4f\u540e\u9000\uff0c\u4f46\u6bcf\u4e00\u5251\u90fd\u6c89\u7a33\u5f02\u5e38\uff0c\u6697\u6d88\u8d64\u795e\u5b50\u7684\u653b\u52bf\uff0c\u8d64\u795e\u5b50\u8fde \u53d1\u4e86\u4e8c\u4e5d\u4e00\u5341\u516b\u638c\uff0c\u867d\u7136\u628a\u51b0\u5ddd\u5929\u5973\u7684\u5251\u5149\u538b\u5f97\u53ea\u80fd\u9632\u8eab\uff0c\u5374\u662f\u672a\u80fd\u53d6\u80dc\u3002\u8d64\u795e\u5b50\u5fc3\u4e2d\u70e6 \u8e81\uff0c\u628a\u5185\u529b\u5168\u8fd0\u5230\u638c\u4e0a\uff0c\u4e00\u62db\u201c\u6392\u5c71\u8fd0\u638c\u201d\u628a\u51b0\u5ddd\u5929\u5973\u7684\u62a4\u8eab\u5251\u5149\u8feb\u5f97\u6447\u6643\u4e0d\u5b9a\uff0c\u8fde\u5b9d\u5251\u4e5f \u7ed9\u9707\u5f97\u79bb\u8eab\uff0c\u8fd9\u638c\u529b\u521a\u52b2\u975e\u5e38\uff0c\u773c\u770b\u51b0\u5ddd\u5929\u5973\u5c31\u8981\u6bc1\u5728\u4ed6\u53cc\u638c\u4e4b\u4e0b\uff01 \u3000\u3000\u4f17\u4eba\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u7981\u4e0d\u4f4f\u54d7\u7136\u5927\u547c\uff0c\u5374\u5ffd\u5730\u542c\u5f97\u8d64\u795e\u5b50\u4e00\u58f0\u5389\u547c\uff0c\u6251\u5012\u5730\u4e0a\uff0c\u63a5\u7740\u95f7 \u96f7\u822c\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u5c18\u571f\u98de\u626c\uff0c\u6bbf\u67f1\u6447\u52a8\uff0c\u539f\u6765\u662f\u8d64\u795e\u5b50\u9aa4\u7136\u8dcc\u5012\uff0c\u638c\u529b\u51fb\u5728\u5730\u4e0a\uff0c\u5730\u9762\u7adf\u7136 \u88c2\u6210\u4e86\u4e24\u9053\u5c0f\u5751\u3002\u53ea\u542c\u5f97\u5192\u5ddd\u751f\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u51b0\u5a25\uff0c\u4f60\u8fd8\u4e0d\u5411\u8001\u524d\u8f88\u966a\u7f6a\u5417\uff1f\u201d\u8d64\u795e\u5b50\u4e00\u8dc3 \u800c\u8d77\uff0c\u9762\u8272\u94c1\u9752\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u75be\u5411\u6bbf\u5916\u5954\u53bb\uff0c\u51b0\u5ddd\u5929\u5973\u8fd8\u672a\u51fa\u58f0\uff0c\u4ed6\u5df2\u7ecf\u8d70\u5f97\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u539f\u6765\u4ee5\u51b0\u5ddd\u5929\u5973\u7684\u529f\u529b\uff0c\u672c\u6321\u4e0d\u4f4f\u8d64\u795e\u5b50\u90a3\u4e00\u62db\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\u7684\u201c\u6392\u5c71\u8fd0\u638c\u201d\uff0c\u4f46\u5979 \u66fe\u5f97\u5192\u5ddd\u751f\u6307\u70b9\uff0c\u6df1\u6089\u5e94\u4ed8\u4e4b\u65b9\uff0c\u8d81\u7740\u8d64\u795e\u5b50\u5168\u529b\u524d\u6251\u4e4b\u9645\uff0c\u5374\u7528\u8fbe\u6469\u62db\u5f0f\u4e2d\u7684\u602a\u5f02\u8eab\u6cd5\uff0c \u5728\u95ee\u4e0d\u5bb9\u53d1\u7684\u7a7a\u9699\uff0c\u7ed5\u5230\u8d64\u795e\u5b50\u8eab\u540e\uff0c\u5c06\u4e03\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u4e00\u9f50\u6253\u5165\u8d64\u795e\u5b50\u7684\u7a74\u9053\uff0c\u8d64\u795e\u5b50\u7684 \u5168\u8eab\u529f\u529b\u90fd\u8fd0\u5728\u638c\u4e0a\uff0c\u8eab\u4e0a\u5176\u4ed6\u90e8\u4efd\uff0c\u5168\u65e0\u9632\u5fa1\uff0c\u5373\u7b97\u662f\u666e\u901a\u58ee\u6c49\u7684\u4e00\u51fb\uff0c\u4ed6\u4ea6\u5df2\u7981\u53d7\u4e0d \u8d77\uff0c\u4f55\u51b5\u662f\u4e03\u679a\u51b0\u9b44\u795e\u5f39\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u4ee4\u5f97\u5168\u573a\u6151\u4f0f\uff0c\u6709\u4e9b\u60f3\u6765\u6311\u8845\u7684\u5f02\u6d3e\u7684\u5996\u90aa\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u51b0\u5f39\uff0c\u5982\u6b64\u795e \u5f02\uff0c\u81ea\u95ee\u6b66\u529f\u8fdc\u53ca\u4e0d\u4e0a\u8d64\u795e\u5b50\uff0c\u90fd\u6084\u6084\u7684\u7f29\u5728\u4e00\u89d2\uff0c\u4e0d\u6562\u51fa\u5934\u3002 \u3000\u3000\u79e9\u5e8f\u521a\u521a\u6062\u590d\uff0c\u5ffd\u89c1\u5927\u6bbf\u95e8\u53e3\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u4e2a\u9ec4\u888d\u9053\u58eb\uff0c\u62a2\u4e86\u5165\u6765\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u5954\u8dd1\u4f5c \u52bf\uff0c\u5374\u662f\u500f\u5730\u5c31\u5230\u4e86\u575b\u524d\uff0c\u7aef\u7684\u662f\u8fc5\u6377\u65e0\u4f26\uff0c\u5192\u5ddd\u751f\u672c\u6765\u76d8\u819d\u7aef\u5750\uff0c\u8fd9\u65f6\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u663e\u89c1 \u662f\u4e0d\u6562\u5c06\u6765\u4eba\u5f53\u4f5c\u540e\u8f88\u770b\u5f85\u3002\u4f17\u4eba\u4ff1\u90fd\u60ca\u8bb6\uff0c\u53ea\u89c1\u8fd9\u9053\u58eb\u76f8\u8c8c\u6e05\u704c\uff0c\u6267\u7740\u4e00\u652f\u62c2\u5c18\uff0c\u98d8\u98d8\u7136 \u9887\u6709\u4ed9\u98ce\u9053\u9aa8\u4e4b\u6982\uff0c\u5728\u5ea7\u9ad8\u624b\uff0c\u9762\u524d\u76f8\u89d1\uff0c\u65e0\u4e00\u4eba\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u4e0d\u89e3\u5192\u5ddd\u751f\u4f55\u4ee5\u5bf9\u4ed6\u5982\u6b64 \u8c26\u900a\u3002\u90a3\u9053\u58eb\u62c2\u5c18\u4e00\u626c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5192\u8001\u5934\u5b50\uff0c\u54b1\u4eec\u4e5f\u6765\u7ed3\u7f18\u7ed3\u7f18\uff01\u201d\u62c2\u5c18\u4e00\u8d77\uff0c\u90a3\u5343\u767e \u6839\u5c18\u5c3e\uff0c\u6839\u6839\u575a\u7acb\uff0c\u6709\u5982\u94a2\u523a\uff0c\u51b0\u5ddd\u5929\u5973\u5251\u672a\u5f52\u9798\uff0c\u90a3\u9ec4\u888d\u9053\u58eb\u62c2\u5c18\u6b63\u5f85\u62c2\u4e0b\uff0c\u51b0\u5ddd\u5929\u5973\u8eab \u5f62\u4e00\u8d77\uff0c\u4e00\u5251\u5c31\u6321\u5728\u4e2d\u95f4\uff0c\u5192\u5ddd\u751f\u9053\uff1a\u201c\u51b0\u5a25\u9000\u4e0b\u3002\u201d\u53ea\u542c\u5f97\u9524\u9523\u9abc\u9abc\u7684\u4e00\u9635\u7e41\u97f3\u5bc6\u54cd\uff0c\u6709 \u5982\u788e\u91d1\u590f\u7389\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u88ab\u4ed6\u4e00\u62c2\uff0c\u9661\u7684\u53cd\u5f39\u8d77\u6765\uff0c\u90a3\u9ec4\u888d\u9053\u58eb\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u6f02\u4eae\u7684 \u5c0f\u599e\u599e\uff0c\u6bc1\u4e86\u4f60\u5c82\u4e0d\u53ef\u60dc\uff1f\u4f60\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u5192\u8001\u5934\u5b50\u3001\u4f60\u8fd8\u88c5\u8154\u4f5c\u52bf\u7684\u5728\u575b\u4e0a\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4eba\u4e1b\u4e2d\u5510\u7ecf\u5929\u98de\u8eab\u8dc3\u8d77\uff0c\u8fd9\u4e00\u8dc3\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\uff0c\u6070\u6070\u843d\u5728\u9ec4\u888d\u9053\u58eb\u4e0e\u51b0\u5ddd\u5929\u5973\u7684\u4e2d\u95f4\uff0c \u9ec4\u888d\u9053\u58eb\u9053\uff1a\u201c\u4e0a\u6b21\u9976\u4f60\u4e0d\u6b7b\uff0c\u4f60\u8fd8\u6562\u6765\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u559d\u9053\uff1a\u201c\u9ec4\u77f3\u9053\u4eba\uff0c\u4f11\u5f97\u65e0\u793c\uff01\u5192\u8001 \u524d\u8f88\u5c82\u80fd\u4e0e\u4f60\u8fd9\u53ae\u52a8\u624b\uff0c\u6765\uff0c\u6765\uff0c\u6211\u548c\u4f60\u7ed3\u7f18\uff01\u201d\u6e38\u9f99\u5251\u500f\u5730\u7684\u51fa\u9798\uff0c\u4e00\u9053\u767d\u5149\uff0c\u4e25\u5982\u957f\u8679 \u63a0\u8fc7\u7a7a\u9645\uff0c\u9ec4\u77f3\u9053\u4eba\u89c1\u8bc6\u8fc7\u8fd9\u628a\u6e38\u9f99\u5251\u7684\u5389\u5bb3\uff0c\u5012\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u62c2\u5c18\u4e00\u62c2\uff0c\u5510\u7ecf\u5929\u7684\u5251\u52bf\u88ab \u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u5316\u5f00\uff0c\u9ec4\u77f3\u9053\u4eba\u62db\u6570\u5feb\u6781\uff0c\u4e00\u62c2\u4e4b\u540e\uff0c\u66f4\u4e0d\u6362\u62db\uff0c\u62c2\u5c18\u4e00\u4fa7\uff0c\u5c06\u5c18\u6746\u5f53\u4f5c\u4e94\u884c \u5251\u7528\uff0c\u5f80\u4e0a\u4e00\u8fce\uff0c\u201c\u5417\u201d\u7684\u4e00\u58f0\uff0c\u5510\u7ecf\u5929\u7684\u6e38\u9f99\u5251\u4e5f\u5f39\u4e86\u8d77\u6765\uff0c\u9000\u540e\u4e24\u6b65\u3002\u9ec4\u77f3\u9053\u4eba\u4e00\u4e2a\u76d8 \u9f99\u7ed5\u6b65\uff0c\u62c2\u5c18\u53c8\u8d77\uff0c\u5343\u4e1d\u4e07\u7f15\uff0c\u5f53\u5934\u7f69\u4e0b\uff0c\u5510\u7ecf\u5929\u65e9\u5df2\u4f7f\u51fa\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u5251\u5149\u56db\u4e0b\u5c55\u5f00\uff0c\u62a4 \u4e86\u5168\u8eab\uff0c\u62c2\u5c18\u4e00\u626b\uff0c\u5c18\u5c3e\u78b0\u5728\u5251\u4e0a\uff0c\u53ee\u53ee\u5417\u5417\uff0c\u6709\u5982\u594f\u4e50\u3002\u9ec4\u77f3\u9053\u4eba\u8fd9\u4e00\u62db\u7528\u7684\u4e43\u662f\u67d4\u529f\uff0c \u5c18\u5c3e\u6beb\u4e0d\u53d7\u529b\uff0c\u6e38\u9f99\u5b9d\u5251\u867d\u5229\uff0c\u5374\u65e0\u4e00\u6839\u524a\u65ad\u3002\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u9ec4\u77f3\u9053\u4eba\u65cb\u98ce\u822c\u5730\u4ece\u4ed6\u8eab \u65c1\u63a0\u8fc7\uff0c\u62c2\u5c18\u4e00\u8d77\uff0c\u7adf\u8981\u5954\u4e0a\u8bb2\u575b\uff0c\u5f84\u53d6\u4e2d\u539f\u516c\u8ba4\u7684\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\u5192\u5ddd\u751f\uff01 \u3000\u3000\u672c\u6765\u4ee5\u5510\u7ecf\u5929\u7684\u6b66\u529f\uff0c\u867d\u975e\u9ec4\u77f3\u9053\u4eba\u4e4b\u654c\uff0c\u4e5f\u53ef\u4ee5\u6321\u5f97\u4e09\u4e94\u5341\u62db\uff0c\u53ea\u662f\u9ec4\u77f3\u9053\u4eba\u4e00\u751f\u82e6 \u7ec3\uff0c\u7acb\u4e0b\u5b8f\u613f\u8981\u4e3a\u8154\u987a\u6d3e\u91cd\u632f\u58f0\u5a01\uff0c\u4ed6\u54ea\u80af\u8017\u8d39\u7cbe\u529b\u4e0e\u5510\u7ecf\u5929\u8fc7\u62db\uff1f\u6240\u4ee5\u5f00\u9996\u4e09\u62db\uff0c\u4fbf\u7528\u5a01 \u529b\u7edd\u5927\u7684\u6740\u624b\uff0c\u8feb\u5f97\u5510\u7ecf\u5929\u5168\u53d6\u5b88\u52bf\uff0c\u8fd9\u6837\u81ea\u7136\u987e\u4e0d\u53ca\u62e6\u963b\u4ed6\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u51fa\u5251\u62e6\u963b\uff0c\u5df2\u6765\u4e0d\u53ca\u3002\u4ed6\u867d\u7136\u660e\u77e5\u9ec4\u77f3\u9053\u4eba\u7edd\u4e0d\u80fd\u4f24\u5bb3\u5f97\u4e86\u5192\u5ddd\u751f\uff0c \u4f46\u53ea\u8981\u4ed6\u8feb\u5f97\u5192\u5ddd\u751f\u52a8\u624b\uff0c\u80fd\u5728\u5341\u62db\u4e4b\u5185\u4e0d\u8d25\u7684\u8bdd\uff0c\u4e2d\u539f\u6b66\u6797\u7684\u9762\u5b50\u4fbf\u5c06\u4e22\u5c3d\uff0c\u8fd9\u201c\u5f00\u5c71\u7ed3 \u7f18\u201d\u7684\u76db\u4f1a\uff0c\u4e5f\u5c06\u88ab\u7834\u574f\u65e0\u9057\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u51b0\u5ddd\u5929\u5973\u5df2\u62a2\u5230\u575b\u524d\uff0c\u4e00\u62db\u201c\u98de\u7011\u6d41\u6cc9\u201d\uff0c\u5251\u5149\u98de\u6d12\uff0c\u5b9b\u5982\u9ed1\u591c\u7e41\u661f\uff0c \u5343\u70b9\u4e07\u70b9\u76f4\u6d12\u4e0b\u6765\uff0c\u8fd9\u6b63\u662f\u5979\u7236\u6bcd\u5408\u521b\u7684\u51b0\u5ddd\u5251\u6cd5\u4e2d\u6700\u5389\u5bb3\u7684\u4e00\u62db\uff0c\u9ec4\u77f3\u9053\u4eba\u4e5f\u4e0d\u7531\u5f97\u6253\u4e86 \u4e2a\u5bd2\u5657\uff0c\u62c2\u5c18\u7adf\u88ab\u6321\u4f4f\u3002\u9ec4\u77f3\u9053\u4eba\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u513f\u4e5f\u627e\u6b7b\u4e48\uff1f\u201d\u62c2\u5c18\u4e00\u7f29\uff0c\u51b0\u5ddd \u5929\u5973\u6536\u52bf\u4e0d\u53ca\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u582a\u582a\u523a\u5230\u9ec4\u77f3\u9053\u4eba\u7684\u80f8\u524d\uff0c\u5ffd\u89c9\u624b\u4e2d\u4e00\u7d27\uff0c\u4e00\u80a1\u5927\u529b\u76f4\u5f80\u5916\u62c9\u3002 \u539f\u6765\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u80fd\u67d4\u80fd\u521a\uff0c\u6545\u610f\u8ba9\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u653b\u5165\u5185\u56f4\uff0c\u62db\u6570\u7528\u8001\uff0c\u529b\u9053\u5df2\u6210\u5f3a\u5f2f \u4e4b\u672a\u4e4b\u9645\uff0c\u62c2\u5c18\u4e00\u7ed5\uff0c\u7528\u67d4\u52b2\u7f20\u7740\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\uff0c\u518d\u7528\u9633\u521a\u4e4b\u529b\u7d27\u8feb\uff0c\u4e00\u67d4\u4e00\u5219\uff0c\u4e24\u80a1\u529b \u9053\u7275\u5f15\uff0c\u51b0\u5ddd\u5927\u5973\u7981\u53d7\u4e0d\u4f4f\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u51e0\u4e4e\u5c31\u8981\u8131\u624b\u98de\u53bb\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u6323\u7136\u4e00\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u9aa4\u611f\u8f7b\u677e\uff0c\u539f\u6765\u662f\u5510\u7ecf\u5929\u5df2\u7136\u8d76\u4e0a\uff0c\u6e38\u9f99\u5b9d\u5251\u76f4\u523a\u9ec4\u77f3\u9053\u4eba \u7684\u80cc\u5fc3\uff0c\u9ec4\u77f3\u9053\u4eba\u7684\u5185\u529f\u867d\u7136\u5df2\u7ec3\u5230\u4e00\u6d41\u5883\u754c\uff0c\u5bfb\u5e38\u5200\u5251\u4f24\u5bb3\u4e0d\u4e86\uff0c\u4f46\u6e38\u9f99\u5251\u662f\u5929\u5c71\u6d3e\u7684\u9547 \u5c71\u4e4b\u5b9d\uff0c\u9ec4\u77f3\u9053\u4eba\u53ef\u4e0d\u6562\u786c\u63a5\u4e00\u5251\uff0c\u8feb\u5f97\u5c06\u5bf9\u51b0\u5ddd\u5929\u5973\u7684\u6740\u624b\u64a4\u4e86\u56de\u6765\uff0c\u4ee5\u5c18\u6746\u67b6\u5f00\u5510\u7ecf\u5929 \u7684\u5b9d\u5251\u3002\u51b0\u5ddd\u5929\u5973\u8eab\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u5251\u950b\u4e00\u6307\uff0c\u8fde\u6296\u4e09\u4e0b\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u8fde\u523a\u9ec4\u77f3\u9053\u4eba\u7684\u4e09\u5904\u7a74 \u9053\uff0c\u9ec4\u77f3\u9053\u4eba\u6b66\u529f\u786e\u662f\u5965\u5999\u65e0\u6bd4\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u4e00\u77ee\uff0c\u957f\u8896\u4e00\u62c2\uff0c\u6ef4\u6e9c\u6e9c\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u628a\u51b0\u5ddd \u5929\u5973\u7684\u4e00\u62db\u4e09\u5f0f\uff0c\u6216\u6321\u6216\u907f\uff0c\u5168\u90fd\u5316\u89e3\u5f00\u53bb\uff0c\u800c\u4e14\u5728\u8f6c\u8eab\u4e4b\u9645\uff0c\u53cd\u624b\u4e00\u62c2\uff0c\u8fd8\u628a\u5510\u7ecf\u5929\u4e5f\u8feb \u5f97\u5012\u9000\u4e24\u6b65\uff01 \u3000\u3000\u524d\u6765\u6311\u8845\u7684\u5404\u5f02\u6d3e\u5996\u90aa\u5927\u58f0\u559d\u91c7\uff0c\u5404\u6b63\u6d3e\u7684\u9ad8\u624b\u4e5f\u7981\u4e0d\u4f4f\u60ca\u7136\u9707\u60ca\u3002\u54ea\u77e5\u9ec4\u77f3\u9053\u4eba\u9053\u5374 \u662f\u6709\u82e6\u8bf4\u4e0d\u51fa\u6765\uff0c\u8868\u9762\u770b\u6765\uff0c\u4ed6\u4f3c\u8f7b\u63cf\u6de1\u5199\uff0c\u6beb\u4e0d\u8d39\u529b\u7684\u4e00\u4e3e\u4fbf\u5c06\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u7684\u653b\u52bf \u5168\u90fd\u5316\u89e3\uff0c\u5176\u5b9e\u90a3\u4e00\u4e0b\u5374\u662f\u5371\u9669\u975e\u5e38\u3002\u53ea\u56e0\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u8054\u624b\u5bf9\u654c\u7684\u6b21\u6570\u672a\u591a\uff0c\u5c1a\u672a\u66fe \u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u8981\u4e0d\u7136\u4ed6\u7eb5\u80fd\u89e3\u5f00\u51b0\u5ddd\u5929\u5973\u7684\u7a81\u88ad\uff0c\u4e5f\u907f\u4e0d\u4e86\u5510\u7ecf\u5927\u7684\u6740\u624b\u3002 \u3000\u3000\u4e09\u4eba\u5728\u575b\u524d\u6076\u6218\uff0c\u970e\u5ffd\u4e4b\u95f4\u5c31\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u6e10\u6e10\u5fc3\u610f\u76f8\u901a\uff0c\u6216\u6b64\u653b \u5f7c\u5b88\uff0c\u6216\u53cc\u5251\u8054\u653b\uff0c\u65e0\u4e0d\u6536\u53d1\u81ea\u5982\uff0c\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u6beb\u65e0\u963b\u6ede\u3002\u51b0\u5ddd\u5927\u5973\u7684\u5251\u6cd5\u4ee5\u8f7b\u7075\u5947\u8be1 \u89c1\u957f\uff0c\u5510\u7ecf\u5927\u7684\u5251\u6cd5\u5219\u8d70\u6c89\u7a33\u51dd\u7ec3\u7684\u8def\u4e8e\uff0c\u4e24\u4eba\u90fd\u662f\u6700\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u6b63\u597d\u76f8\u8f85\u76f8\u6210\u3002\u9ec4\u77f3\u9053 \u4eba\u529f\u529b\u867d\u6bd4\u4ed6\u4eec\u9ad8\u5f97\u591a\uff0c\u5e76\u4ee5\u6570\u5341\u5e74\u6f5c\u5fc3\u82e6\u7ec3\u7684\u602a\u5f02\u529f\u592b\u5e94\u654c\uff0c\u4ecd\u7136\u5360\u4e0d\u4e0a\u534a\u70b9\u4fbf\u5b9c\uff0c\u800c\u4e14 \u6e10\u6e10\u6709\u88ab\u8feb\u5904\u4e0b\u98ce\u4e4b\u52bf\u3002\u65c1\u4eba\u867d\u7136\u8fd8\u672a\u770b\u5f97\u51fa\u6765\uff0c\u9ec4\u77f3\u9053\u4eba\u5374\u662f\u81ea\u5df1\u77e5\u9053\uff0c\u4e0d\u7981\u5012\u5438\u4e86\u4e00\u53e3 \u51c9\u6c14\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u8054\u5251\u5408\u653b\uff0c\u6e10\u6e10\u5c06\u9ec4\u77f3\u9053\u4eba\u7684\u51f6\u7130\u538b\u4f4f\uff0c\u5510\u7ecf\u5929\u5b9a\u4e86\u5fc3\u795e\uff0c\u5077\u770b\u51b0\u5ddd \u5929\u5973\uff1a\u53ea\u89c1\u5979\u4f3c\u559c\u5982\u614e\uff0c\u5982\u6028\u5982\u6012\uff0c\u5510\u7ecf\u5929\u5fc3\u9b44\u4e00\u8361\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u573a\u76db\u4f1a\u4e4b\u540e\uff0c\u4f46\u613f\u5979\u80af\u542c \u6211\u7ec6\u8bc9\u5fc3\u66f2\u3002\u201d\u9ad8\u624b\u6bd4\u62fc\uff0c\u54ea\u5bb9\u5206\u795e\uff0c\u9ec4\u77f3\u9053\u4eba\u4e00\u6296\u62c2\u5c18\uff0c\u8d81\u7740\u5510\u7ecf\u5929\u7a0d\u4e3a\u677e\u61c8\u4e4b\u9645\uff0c\u7acb\u523b \u8fde\u4e0b\u6740\u624b\uff0c\u51b0\u5ddd\u5929\u5973\u6025\u5fd9\u51fa\u5251\u6d88\u89e3\u3001\u4f46\u5df2\u88ab\u9ec4\u77f3\u9053\u4eba\u53cd\u62a2\u5148\u624b\uff0c\u518d\u6597\u5230\u4e09\u5341\u62db\u4e4b\u540e\uff0c\u4e24\u65b9\u624d \u6273\u6210\u5e73\u5c40\u3002 \u3000\u3000\u5510\u7ecf\u5929\u77e5\u9053\u6b64\u6218\u5173\u7cfb\u91cd\u5927\uff0c\u518d\u4e5f\u4e0d\u6562\u5206\u795e\u5927\u610f\uff0c\u5c55\u5f00\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u628a\u6e38\u9f99\u5b9d\u5251\u5316\u6210\u4e00\u5ea7 \u5149\u5e62\uff0c\u5c06\u51b0\u5ddd\u5927\u5973\u4e00\u5e76\u62a4\u4f4f\uff0c\u5927\u987b\u5f25\u5251\u5f0f\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u5965\u5999\u7684\u5251\u5f0f\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u5a01\u529b\u5f3a\u4e86 \u4e00\u500d\uff0c\u7aef\u7684\u662f\u98ce\u96e8\u4e0d\u900f\uff0c\u9976\u662f\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u9022\u9699\u5373\u4eba\uff0c\u4e5f\u81ea\u653b\u4e0d\u8fdb\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u6709\u5510\u7ecf\u5929 \u9632\u62a4\uff0c\u53ef\u4ee5\u5168\u529b\u8fdb\u653b\uff0c\u5251\u6cd5\u8d8a\u53d1\u51cc\u5389\u3002\u8fd9\u4e00\u573a\u6076\u6218\uff0c\u53cc\u65b9\u90fd\u4ee5\u6700\u4e0a\u4e58\u7684\u6b66\u529f\u5251\u6cd5\u6bd4\u62fc\uff0c\u5728\u573a \u9ad8\u624b\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u4e2a\u4e2a\u90fd\u770b\u5f97\u5b9a\u4e86\u795e\uff0c\u8fde\u773c\u775b\u4e5f\u4e0d\u6562\u7728\u4e00\u4e0b\u3002\u62c2\u5c18\u67d4\u97e7\uff0c\u78b0\u51fb\u65e0 \u58f0\uff0c\u5927\u6bbf\u4e4b\u4e2d\uff0c\u4f46\u542c\u5f97\u5251\u98ce\u98d2\u7136\uff0c\u4eba\u5f71\u6765\u5f80\uff0c\u9759\u5f97\u8fde\u5598\u606f\u4e4b\u58f0\u3002\u90fd\u53ef\u4ee5\u542c\u5f97\u89c1\uff0c\u82e5\u975e\u8eab\u5728\u6bbf \u4e2d\uff0c\u771f\u4e0d\u77e5\u6b64\u95f4\u6709\u5982\u6b64\u6fc0\u6218\u3002 \u3000\u3000\u6b63\u5728\u56db\u5ea7\u51dd\u795e\u4e4b\u9645\uff0c\u95e8\u5916\u5ffd\u7136\u4e00\u9635\u9a9a\u52a8\uff0c\u4f46\u542c\u5f97\u563b\u563b\u54c8\u54c8\u7684\u602a\u7b11\u4e4b\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u4e0d\u65ad \u4f20\u6765\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u60ca\uff0c\u77e5\u9053\u5b9a\u662f\u91d1\u4e16\u9057\u524d\u6765\u6363\u86cb\uff0c\u53ef\u662f\u5927\u654c\u5f53\u524d\uff0c\u90a3\u5bb9\u5f97\u4ed6\u5206\u5fc3\u65c1\u60ca\u3002 \u3000\u3000\u5ea7\u4e2d\u4e00\u4f17\u9ad8\u624b\u3001\u5374\u88ab\u8fd9\u7a81\u5982\u5176\u6765\u7684\u602a\u4e8b\u8f6c\u79fb\u4e86\u76ee\u5149\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u4e2a\u4e2a\u56de\u5934\uff0c\u4f46\u89c1\u5341\u591a\u540d \u6b66\u5f53\u9053\u58eb\uff0c\u4e00\u8df3\u4e00\u8df3\u7684\u6d8c\u5165\u6bbf\u4e2d\uff0c\u4e2a\u4e2a\u88c2\u5f00\u5634\u5df4\uff0c\u602a\u7b11\u4e0d\u5df2\u3002\u96f7\u9707\u5b50\u52c3\u7136\u5927\u6012\uff0c\u5728\u575b\u524d\u7a3d\u9996 \u7980\u544a\u5192\u5ddd\u751f\u9053\uff1a\u201c\u6628\u665a\u90a3\u75af\u4e10\u53c8\u6765\u6363\u4e71\u4e86\uff0c\u7ed3\u7f18\u76db\u4f1a\uff0c\u5c82\u5bb9\u4ed6\u6765\u4fae\u8fb1\uff0c\u6c42\u7956\u5e08\u793a\u4e0b\u3002\u201d\u96f7\u9707 \u5b50\u6068\u6781\u91d1\u4e16\u9057\uff0c\u6025\u6012\u5f53\u5934\uff0c\u5374\u4e5f\u4e0d\u60f3\u4e00\u60f3\u4ee5\u5192\u5ddd\u751f\u7684\u8eab\u4efd\uff0c\u600e\u80fd\u4e0e\u91d1\u4e16\u9057\u4e00\u822c\u89c1\u8bc6\uff0c\u4e0e\u4ed6\u52a8 \u624b\u3002 \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u90a3\u4e9b\u6b66\u5f53\u9053\u58eb\u4e00\u8df3\u4e00\u8df3\u7684\u90fd\u6d8c\u5165\u6bbf\u4e2d\uff0c\u540e\u9762\u4e00\u4e2a\u9762\u76ee\u6e05\u79c0\u7684\u5c11\u5e74\uff0c\u7a7f\u7740\u4e00\u8eab \u534e\u4e3d\u7684\u8863\u88f3\uff0c\u5374\u6545\u610f\u6495\u88c2\u4e86\u51e0\u5904\uff0c\u8fd9\u5c11\u5e74\u624b\u6301\u94c1\u62d0\uff0c\u5de6\u8fb9\u4e00\u62e6\uff0c\u53f3\u8fb9\u4e00\u6446\uff0c\u539f\u6765\u8fd9\u7fa4\u9053\u58eb\u7adf \u662f\u88ab\u91d1\u4e16\u9057\u597d\u50cf\u8d76\u9e2d\u5b50\u4e00\u6837\u8d76\u8fdb\u6765\u7684\u3002\u5ea7\u4e2d\u9ad8\u624b\u90fd\u8033\u95fb\u201c\u6bd2\u624b\u75af\u4e10\u201d\u4e4b\u540d\uff0c\u9aa4\u7136\u89c1\u4ed6\u5982\u6b64\u8fd9 \u822c\u7684\u51fa\u73b0\uff0c\u90fd\u4e0d\u7981\u9a87\u7136\u3002\u91d1\u4e16\u9057\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u70ed\u95f9\u5440\u597d\u70ed\u95f9\uff01\u201d\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6765\u7ed3\u7f18 \u7ed3\u7f18\u3002\u201d\u5ffd\u89c1\u5192\u5ddd\u751f\u9762\u8272\u4e00\u6c89\uff0c\u4e00\u6447\u5934\uff0c\u5c06\u4e00\u4e32\u5ff5\u73e0\u7529\u51fa\uff0c\u5ff5\u73e0\u5728\u7a7a\u4e2d\u98de\u6563\uff0c\u7a81\u7136\u95f4\u602a\u7b11\u4e4b \u58f0\u987f\u6b62\uff0c\u6bbf\u4e2d\u9759\u5f97\u53ef\u6015\uff0c\u5ffd\u5730\u542c\u5f97\u6709\u4eba\u602a\u53eb\u9053\uff1a\u201c\u597d\u70ed\u95f9\u5440\uff0c\u6211\u4e5f\u6765\u7ed3\u7f18\u7ed3\u7f18\uff01\u201d\u6323\u6323\u6570\u58f0 \u54cd\u8fc7\uff0c\u4e00\u6761\u4eba\u5f71\u98de\u6251\u4e0a\u575b\uff0c\u7adf\u7136\u5411\u5192\u4e32\u751f\u5077\u88ad\uff0c\u51b0\u4e32\u5927\u5973\u6025\u5fd9\u820d\u4e86\u9ec4\u77f3\u9053\u4eba\uff0c\u4e0a\u524d\u62e6\u6321\u3002 \u3000\u3000\u53ea\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u5bd2\u5149\u56db\u6563\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u51e0\u4e4e\u628a\u6301\u4e0d\u4f4f\uff0c\u624b\u81c2\u4e00\u9635\u9178\u9ebb\uff0c\u7275\u52a8\u5f97\u808b \u9aa8\u90fd\u9690\u9690\u4f5c\u75db\u3002\u8fd9\u4eba\u6765\u5f97\u592a\u5feb\uff0c\u51b0\u5ddd\u5929\u5973\u521d\u65f6\u8fd8\u4ee5\u4e3a\u662f\u91d1\u4e16\u9057\u524d\u6765\u80e1\u95f9\uff0c\u751a\u4e3a\u607c\u6012\uff0c\u4f46\u8fd9\u4e00 \u5251\u4ecd\u7136\u672a\u7528\u5168\u529b\uff0c\u4e00\u7167\u9762\u540e\uff0c\u53ea\u89c1\u8fd9\u4eba\u62ab\u5934\u6563\u53d1\uff0c\u7adf\u662f\u4e2a\u5e72\u7626\u5f97\u50cf\u4e00\u6839\u67af\u7af9\u7684\u6c49\u5b50\uff0c\u5f62\u8c8c\u6bd4 \u91d1\u4e16\u9057\u626e\u9ebb\u75af\u65f6\u8fd8\u8981\u96be\u770b\u3002\u51b0\u4e32\u5929\u5973\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u602a\u4eba\u7684\u529f\u529b\u4e0d\u4f46\u6bd4\u91d1\u4e16\u9057\u9ad8\u7684\u591a\uff0c\u5373\u8fde\u9ec4 \u77f3\u9053\u4eba\u4e5f\u4f3c\u4e4e\u6bd4\u4ed6\u4e0d\u4e0a\u3002 \u3000\u3000\u5ea7\u4e2d\u7684\u8c22\u4e91\u771f\u4e5f\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u602a\u4eba\u6b63\u662f\u5979\u524d\u665a\u6240\u89c1\u5272\u4e86\u8bb8\u591a\u6b66\u5f53\u9053\u58eb\u820c\u5934\u7684\u90a3\u4e2a\u602a \u4eba\u3002\u53ea\u542c\u5f97\u5192\u5ddd\u751f\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6d1e\u51a5\u9053\u53cb\uff0c\u56db\u5341\u5e74\u524d\u65e7\u4e8b\uff0c\u4f60\u8fd8\u672a\u5fd8\u6000\u5417\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5ea7 \u4e2d\u4e0a\u4e94\u5341\u5c81\u7684\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u56db\u5341\u5e74\u524d\uff0c\u6606\u4ed1\u5c71\u67af\u7af9\u6d1e\u6709\u4e00\u4e2a\u4fee\u58eb\u540d\u53eb\u6d1e\u51a5\u5b50\uff0c\u7ec3\u6210\u4e00 \u8eab\u90aa\u5f02\u7684\u529f\u592b\uff0c\u4e13\u4e0e\u6b63\u6d3e\u4e2d\u4eba\u4e3a\u96be\uff0c\u90a3\u65f6\u5192\u5ddd\u751f\u65b9\u5728\u58ee\u5e74\uff0c\u706b\u6c14\u672a\u655b\uff0c\u542c\u540c\u9053\u4e2d\u4eba\u8bf4\u8d77\u6b64 \u4e8b\uff0c\u7acb\u5373\u4e0a\u6606\u4ed1\uff01\u53bb\u627e\u4ed6\u6bd4\u8bd5\uff0c\u6fc0\u6597\u534a\u65e5\uff0c\u5c06\u4ed6\u6253\u8d25\uff0c\u5f53\u4e0b\u8feb\u4ed6\u7acb\u8a93\uff0c\u6c38\u4e0d\u8bb8\u4ed6\u5728\u6c5f\u6e56\u884c \u8d70\uff0c\u8fd9\u624d\u5c06\u4ed6\u91ca\u653e\uff0c\u56db\u5341\u5e74\u6765\uff0c\u4ed6\u6beb\u65e0\u6d88\u606f\uff0c\u6c5f\u6e56\u4e0a\u90fd\u4ee5\u4e3a\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff0c\u60f3\u4e0d\u5230\u4ed6\u5374\u5728\u5192\u5ddd \u751f\u7b2c\u4e09\u5c4a\u5f00\u5c71\u7ed3\u7f18\u7684\u9996\u65e5\u7a81\u7136\u51fa\u73b0\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u4e43\u662f\u524d\u6765\u6311\u8845\u3002\u5728\u5ea7\u9ad8\u624b\u90fd\u4e0d\u7981\u5fc3\u5934\u9707\u60ca\uff0c \u8bba\u8d77\u5e74\u9f84\uff0c\u8fd9\u6d1e\u51a5\u5b50\u8be5\u4e0e\u5192\u5ddd\u751f\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u800c\u4eca\u770b\u6765\uff0c\u4e0d\u8fc7\u8fd8\u4f3c\u56db\u5341\u591a\u5c81\u7684\u6837\u5b50\uff0c\u6b66\u6797\u4e2d\u53ea \u6709\u6700\u4e0a\u4e58\u5185\u529f\u7684\u4eba\uff0c\u6709\u610f\u4fee\u6301\uff0c\u624d\u80fd\u9a7b\u989c\u4e0d\u8001\uff0c\u4f17\u9ad8\u624b\u4e0d\u7ea6\u800c\u540c\u7684\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u6d1e\u51a5\u5b50\u4fee \u7ec3\u4e86\u56db\u5341\u5e74\u590d\u51fa\u6c5f\u6e56\uff0c\u82e5\u975e\u4ed6\u6709\u5236\u80dc\u7684\u628a\u63e1\uff0c\u7109\u6562\u51fa\u6765\uff1f\u53ea\u6050\u4ed6\u7684\u6b66\u529f\u6bd4\u5192\u5ddd\u751f\u8fd8\u8981\u7ec3\u5f97\u9ad8 \u4e86\u3002\u201d \u3000\u3000\u6d1e\u51a5\u5b50\u789f\u789f\u602a\u7b11\uff0c\u9053\uff1a\u201c\u5192\u5ddd\u751f\uff0c\u4f60\u800c\u4eca\u5df2\u6210\u4e00\u4ee3\u5b97\u5e08\uff0c\u6211\u8fd8\u662f\u4e2a\u56da\u5f92\uff0c\u8fd9\u5c82\u975e\u592a\u4e0d\u516c \u9053\uff1f\u6211\u8981\u5411\u4f60\u6c42\u60c5\uff0c\u4f60\u5230\u5e95\u8fd8\u8bb8\u4e0d\u8bb8\u6211\u5728\u6c5f\u6e56\u884c\u8d70\uff1f\u201d\u5192\u5ddd\u751f\u9053\uff1a\u201c\u56db\u5341\u5e74\u95ee\uff0c\u661f\u79fb\u7269\u6362\uff0c \u6ca7\u6d77\u5c1a\u6709\u53d8\u4e3a\u6851\u7530\uff0c\u4eba\u4e8b\u66f4\u591a\u53d8\u5316\u3002\u4f60\u7684\u8a93\u8a00\uff0c\u5b88\u662f\u4e0d\u5b88\uff0c\u90a3\u81ea\u7136\u662f\u968f\u4f60\u5fc3\u610f\u4e86\u3002\u201d\u5192\u5ddd\u751f \u8fd9\u756a\u8bf4\u8bdd\u7684\u610f\u601d\uff0c\u5373\u662f\u8bf4\u7ea6\u675f\u53ef\u4ee5\u968f\u7740\u4eba\u4e8b\u7684\u53d8\u66f4\uff0c\u4f60\u82e5\u81ea\u95ee\u5df2\u7ecf\u6539\u90aa\u5f52\u6b63\uff0c\u90a3\u81ea\u7136\u4e0d\u5fc5\u518d \u5b88\u8a93\u8a00\uff0c\u6d1e\u51a5\u5b50\u4e00\u65f6\u95f4\u609f\u4e0d\u51fa\u4ed6\u7684\u8bdd\u610f\uff0c\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u5f53\u65f6\u4f60\u4ee5\u6b66\u529b\u8feb\u6211\u81ea\u56da\uff0c\u800c\u4eca\u6211\u4e8c\u6b21 \u51fa\u5c71\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u914d\u4e0d\u914d\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u5c11\u4e0d\u5f97\u8fd8\u8981\u5411\u4f60\u9886\u6559\u4e00\u756a\u3002\u201d\u5192\u5ddd\u751f\u5fae\u7b11\u9053\uff1a\u201c\u6c5f\u6e56 \u4e4b\u4e0a\u5c82\u662f\u53ea\u51ed\u6b66\u529f\uff1f\u201d\u6d1e\u51a5\u5b50\u563f\u563f\u51b7\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u6211\u5f53\u65e5\u5728\u638c\u4e0a\u8f93\u4e86\u7ed9\u4f60\uff0c\u4eca\u65e5\u53ea\u77e5\u9053\u8981\u5728 \u638c\u4e0a\u8ba8\u56de\u6765\uff01\u201d\u98de\u8eab\u4e00\u8dc3\uff0c\u518d\u884c\u6251\u51fb\uff0c\u51b0\u5ddd\u5929\u5973\u65e9\u5df2\u6263\u597d\u4e03\u679a\u51b0\u5f39\uff0c\u6d1e\u51a5\u5b50\u8eab\u5f62\u4e00\u8d77\uff0c\u5979\u7684 \u4e03\u679a\u51b0\u5f39\u4ea6\u5df2\u540c\u65f6\u5c04\u51fa\uff0c\u6d1e\u51a5\u5b50\u53eb\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff1f\u201d\u5341\u6307\u9f50\u5f39\uff0c\u90a3\u4e9b\u51b0\u9b44\u795e \u5f39\uff0c\u90fd\u7ed9\u4ed6\u5f39\u7834\uff0c\u5bd2\u5149\u51b7\u6c14\uff0c\u5316\u4e3a\u96fe\u7f51\uff0c\u6d1e\u51a5\u5b50\u8fde\u4e5e\u55e4\u4e5f\u4e0d\u6253\u4e00\u4e2a\uff0c\u4f38\u5f00\u624b\u6307\uff0c\u5411\u51b0\u5ddd\u5929\u5973 \u5c31\u662f\u4e00\u6293\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u56db\u5341\u5e74\u6765\u6000\u5bbf\u6028\uff0c\u8981\u5c06\u94c1\u638c\u6597\u5b97\u5e08\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u5984\u52a8\u65e0\u660e\u3000\u7384\u529f\u6d88\u4e00\u65e6\u3000\u5b89\u6392\u6709\u9053\u3000\u8863\u94b5\u5f97\u771f\u4f20 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u4e94\u56de \u5984\u52a8\u65e0\u660e\u3000\u7384\u529f\u6d88\u4e00\u65e6 \u5b89\u6392\u6709\u9053\u3000\u8863\u94b5\u5f97\u771f\u4f20 \u3000\u3000\u4e03\u679a\u51b0\u9b44\u795e\u5f39\u540c\u65f6\u51fa\u624b\uff0c\u6d1e\u51a5\u4e8e\u7adf\u7136\u82e5\u65e0\u5176\u4e8b\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3 \u65f6\u5feb\uff0c\u53ea\u89c1\u6d1e\u51a5\u5b50\u4e00\u8dc3\u800c\u8d77\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u671d\u7740\u51b0\u5ddd\u5929\u5973\u7684\u9762\u95e8\uff0c\u4fbf\u662f\u4e00\u6293\u3002\u6d1e\u51a5\u5b50\u4e00\u8eab\u9ed1\u8272 \u8863\u88f3\uff0c\u8eab\u5f62\u8d77\u5904\uff0c\u5982\u4e00\u7f15\u9ed1\u70df\uff0c\u500f\u5ffd\u6eda\u81f3\uff0c\u4ed6\u5341\u6307\u90fd\u957f\u7740\u6781\u957f\u7684\u6307\u7532\uff0c\u8fd9\u4e00\u722a\u6293\u4e0b\uff0c\u83ab\u8bf4\u7ed9 \u4ed6\u6293\u7834\u9762\u95e8\uff0c\u53ea\u8981\u5728\u51b0\u5ddd\u5929\u5973\u5439\u5f39\u5f97\u7834\u7684\u7c89\u8138\u4e0a\u7740\u4e86\u4e00\u4e0b\uff0c\u8fd9\u540e\u679c\u4fbf\u662f\u4e0d\u582a\u60f3\u50cf\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6ee1\u8154\u6124\u6c14\uff0c\u672c\u60f3\u5230\u4f1a\u4e0a\u80e1\u95f9\xb7\u4e00\u573a\uff0c\u4ed6\u7528\u788e\u77f3\u5c06\u5341\u591a\u4e2a\u5728\u5916\u9762\u8f6e\u503c\u7684\u6b66\u5f53\u9053\u58eb\u6253 \u4e86\u7b11\u7a74\u548c\u9ebb\u75d2\u4eba\uff0c\u50cf\u8d76\u9e2d\u5b50\u4e00\u6837\u8d76\u5165\u4f1a\u573a\uff0c\u6b63\u5728\u6d0b\u6d0b\u5f97\u610f\uff0c\u4e0d\u6599\u5192\u5ddd\u751f\u5c06\u4e00\u4e32\u5ff5\u73e0\u7529\u4e86\u51fa \u6765\uff0c\u53ea\u662f\u4e00\u4e3e\u624b\u4e4b\u95f4\uff0c\u5c31\u7834\u4e86\u91d1\u4e16\u9057\u7684\u6253\u7a74\u6cd5\uff0c\u4f7f\u90a3\u5341\u591a\u4e2a\u6b66\u5f53\u9053\u58eb\u7acb\u65f6\u6062\u590d\u5e38\u6001\u3002\u6bd2\u9f99\u5c0a \u8005\u7684\u70b9\u7a74\u6cd5\u72ec\u521b\u4e00\u5bb6\uff0c\u91d1\u4e16\u9057\u66fe\u4ee5\u6b64\u6253\u8d25\u4e0d\u5c11\u5f3a\u654c\uff0c\u81ea\u4ee5\u4e3a\u5929\u4e0b\u65e0\u4eba\u80fd\u7834\uff0c\u54ea\u77e5\u4e0e\u5510\u7ecf\u5929\u51e0 \u6b21\u4ea4\u624b\u4e4b\u540e\uff0c\u8fd9\u788e\u77f3\u6253\u7a74\u7684\u529f\u592b\u5df2\u88ab\u5510\u7ecf\u5929\u8bc6\u7834\uff0c\u867d\u7136\u5c1a\u672a\u80fd\u514b\u5236\u4ed6\uff0c\u4f46\u5df2\u77e5\u9053\u4e86\u89e3\u6cd5\uff0c\u6628 \u5929\u5510\u7ecf\u5929\u66ff\u96f7\u9707\u5b50\u7b49\u4eba\u89e3\u7a74\uff0c\u91d1\u4e16\u9057\u540e\u6765\u77e5\u9053\u3002\u5fc3\u4e2d\u5df2\u662f\u4e00\u9707\uff0c\u800c\u4eca\u89c1\u5192\u5ddd\u751f\u4e0d\u8d39\u5439\u7070\u4e4b \u529b\uff0c\u5f39\u6307\u4e4b\u95f4\u540c\u65f6\u89e3\u4e86\u591a\u4e2a\u4eba\u7684\u7a74\u9053\uff0c\u8fd9\u6b66\u529f\u66f4\u662f\u6df1\u4e0d\u53ef\u6d4b\uff01\u542c\u90a3\u5ff5\u73e0\u7834\u7a74\u4e4b\u58f0\uff0c\u91d1\u4e16\u9057\u81ea \u5fd6\uff0c\u82e5\u7136\u6253\u5230\u81ea\u5df1\u8eab\u4e0a\uff0c\u81ea\u5df1\u4e5f\u4e0d\u80fd\u62b5\u6321\uff0c\u5e78\u800c\u5192\u5ddd\u751f\u53ea\u662f\u66ff\u95e8\u4e0b\u5f1f\u4e8e\u89e3\u7a74\uff0c\u5e76\u4e0d\u4e0e\u4ed6\u4e3a \u96be\uff0c\u91d1\u4e16\u9057\u4e0d\u7531\u5f97\u5fc3\u5934\u6c14\u9981\u3002\u9a84\u6c14\u5927\u655b\uff0c\u4f46\u8f6c\u773c\u4e00\u77a5\uff0c\u89c1\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u8054\u5251\u5bf9\u4ed8\u9ec4\u77f3\u9053 \u4eba\uff0c\u91d1\u4e16\u9057\u5fc3\u5934\u53c8\u5982\u6253\u7834\u4e86\u4e94\u5473\u74f6\u5b50\uff0c\u53c8\u9178\u53c8\u82e6\uff0c\u6781\u4e0d\u8212\u670d\uff0c\u6b63\u5f85\u6084\u7136\u9000\u51fa\uff0c\u5ffd\u89c1\u6d1e\u51a5\u5b50\u7a81 \u7136\u98de\u5165\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u5c31\u5f39\u5f00\u4e86\u5192\u5ddd\u751f\u7684\u51e0\u7c92\u5ff5\u73e0\uff0c\u63a5\u7740\u7adf\u7136\u5bf9\u51b0\u5ddd\u5929\u5973\u8fde\u65bd\u6740\u624b\u3002\u8fd9\u65f6\u6d1e\u51a5 \u5b50\u7684\u957f\u722a\u770b\u770b\u5c31\u8981\u6293\u5230\u51b0\u5ddd\u5929\u5973\u8138\u4e0a\uff0c\u91d1\u4e16\u9057\u5373\u7b97\u5bf9\u5510\u7ecf\u5929\u6709\u591a\u5927\u6068\u610f\uff0c\u8fd9\u65f6\u4ea6\u7109\u80fd\u4e0d\u6551\uff1f \u3000\u3000\u4f46\u89c1\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u51b0\u5ddd\u5929\u5973\u970d\u5730\u4e00\u4e2a\u51e4\u70b9\u5934\u3002\u53cd\u5251\u4e00\u524a\uff0c\u6d1e\u51a5\u5b50\u8fd9\u4e00\u722a\u6293\u5979\u4e0d \u4f4f\uff0c\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u8fde\u73af\u53c8\u6293\uff0c\u91d1\u4e16\u9057\u5927\u559d\u4e00\u58f0\uff0c\u65cb\u98ce\u822c\u7684\u6740\u4e86\u8fdb \u6765\uff0c\u94c1\u62d0\u5f53\u5934\u7838\u4e0b\uff0c\u6d1e\u51a5\u5b50\u4f38\u624b\u4e00\u6293\uff0c\u6070\u6070\u6293\u7740\u6756\u5934\uff0c\u8fd9\u4e00\u4ea4\u624b\uff0c\u4e24\u4eba\u90fd\u4ee5\u4e0a\u4e58\u7684\u5185\u529f\u76f8 \u62fc\uff0c\u91d1\u4e16\u9057\u8eab\u4e0d\u7531\u5df1\u7684\u88ab\u4ed6\u62d6\u4e86\u4e24\u6b65\u3002\u51b0\u5ddd\u5929\u5973\u89c1\u52bf\u4e0d\u5999\u5587\u7684\u4e00\u5251\uff0c\u523a\u6d1e\u51a5\u5b50\u9888\u690e\u7684\u201c\u5929\u67f1 \u7a74\u201d\uff0c\u8fd9\u4e00\u62db\u6b63\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u54ea\u77e5\u6d1e\u51a5\u5b50\u6b66\u529f\u5df2\u81fb\u5316\u5883\uff0c\u7adf\u4e0d\u56de\u5934\uff0c\u968f\u624b\u4e00\u6296\uff0c\u5c06\u91d1\u4e16 \u9057\u7684\u94c1\u62d0\u6296\u4e86\u8d77\u6765\uff0c\u54e8\u7684\u4e00\u58f0\uff0c\u5f39\u5f00\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\uff0c\u53f3\u638c\u63a5\u7740\u4f38\u51fa\uff0c\u5728\u94c1\u6756\u4e0a\u4e00\u6309\uff0c\u72de \u7b11\u53eb\u9053\uff1a\u201c\u72c2\u5984\u5c0f\u5b50\uff0c\u53eb\u4f60\u77e5\u9053\u5389\u5bb3\uff01\u201d\u6d1e\u51a5\u5b50\u5355\u638c\u4e4b\u529b\uff0c\u91d1\u4e16\u9057\u5df1\u611f\u4e0d\u652f\uff0c\u8fd9\u65f6\u88ab\u4ed6\u5de6\u638c \u4e00\u9001\uff0c\u53f3\u638c\u4e00\u62cd\uff0c\u94c1\u62d0\u7adf\u7136\u5185\u5f2f\uff0c\u91d1\u4e16\u9057\u864e\u53e3\u6d41\u8840\uff0c\u51b0\u5ddd\u592d\u5973\u5927\u60ca\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u5237\uff0c\u5237\uff0c \u5237\uff0c\u4e00\u8fde\u4e09\u5251\uff01 \u3000\u3000\u6d1e\u51a5\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u53f3\u638c\u4ecd\u7136\u6309\u5728\u62d0\u4e0a\uff0c\u5de6\u624b\u6293\u7740\u91d1\u4e16\u9057\u7684\u6756\u5934\u81ea\u5de6\u81f3\u53f3\u8f6c\u4e86\u4e00\u4e2a\u5706\u5708\uff0c \u51b0\u5ddd\u5929\u5973\u7684\u5251\u523a\u5f97\u5feb\uff0c\u4ed6\u7684\u62d0\u4e5f\u8f6c\u5f97\u5feb\uff0c\u91d1\u4e16\u9057\u53cc\u624b\u6293\u7262\u94c1\u62d0\uff0c\u88ab\u4ed6\u62d6\u5f97\u6253\u5708\u75be\u8f6c\uff0c\u5ea7\u4e0a\u8bf8 \u4eba\u90fd\u770b\u5f97\u773c\u82b1\u7f00\u4e71\uff0c\u4f46\u89c1\u94c1\u62d0\u76d8\u65cb\uff0c\u4eba\u5f71\u98de\u821e\uff0c\u6d1e\u51a5\u5b50\u4e0e\u91d1\u4e16\u9057\u5404\u5728\u94c1\u62d0\u4e00\u7aef\uff0c\u6e10\u6e10\u8fde\u54ea\u4e2a \u662f\u6d1e\u51a5\u5b50\u54ea\u4e2a\u662f\u91d1\u4e16\u9057\u4e5f\u5206\u8fa8\u4e0d\u51fa\u6765\u3002\u51b0\u5ddd\u5929\u5973\u4e00\u8fde\u4e09\u5251\u90fd\u780d\u5728\u94c1\u62d0\u4e2d\u95f4\uff0c\u773c\u89c1\u4eba\u5f71\u8d8a\u8f6c\u8d8a \u75be\uff0c\u8bda\u6050\u8bef\u4f24\u4e86\u91d1\u4e16\u9057\uff0c\u7b2c\u56db\u5251\u4e0d\u6562\u523a\u51fa\u3002\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u602a\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u817e\u7a7a\u98de\u8d77\uff0c\u51b0\u5ddd\u5929 \u5973\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u6d1e\u51a5\u5b50\u4ecd\u7136\u6301\u7740\u94c1\u62d0\u4e00\u7aef\uff0c\u91d1\u4e16\u9057\u5374\u9a91\u5728\u94c1\u62d0\u4e0a\uff0c\u5ffd\u5730\u201c\u5478\u201d\u4e00\u58f0\uff0c\u5410\u51fa \u4e00\u53e3\u553e\u6d8e\uff0c\u9690\u9690\u6742\u7740\u55e4\u55e4\u7684\u98de\u9488\u7834\u7a7a\u4e4b\u58f0\uff0c\u51b0\u5ddd\u5929\u5973\u8d76\u5fd9\u79fb\u5f62\u6362\u4f4d\uff0c\u53cd\u8eab\u4e00\u5251\uff0c\u4e00\u62db\u201c\u5012\u6302 \u5929\u8679\u201d\uff0c\u75be\u523a\u6d1e\u51a5\u5b50\u80cc\u5fc3\u7684\u201c\u592d\u67a2\u7a74\u201d\uff01 \u3000\u3000\u91d1\u4e16\u9057\u672c\u6765\u5df2\u88ab\u6d1e\u51a5\u5b50\u5b8c\u5168\u5236\u4f4f\uff0c\u8fd9\u4e00\u4e0b\u53d8\u5316\uff0c\u5374\u662f\u5927\u51fa\u6d1e\u51a5\u5b50\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u4ed6\u7ec3\u6709\u4e0a \u4e58\u7684\u95ed\u7a74\u529f\u592b\uff0c\u5374\u4e5f\u5e76\u4e0d\u60e7\u6015\u91d1\u4e16\u9057\u7684\u6697\u5668\u3002\u51b0\u5ddd\u5927\u5973\u7684\u5251\u62db\u6765\u5f97\u5feb\uff0c\u6d1e\u51a5\u5b50\u65e0\u6687\u53d1\u653e\u91d1\u4e16 \u9057\uff0c\u8f6c\u8eab\u4e00\u62c2\u6cb9\u5148\u89e3\u5f00\u51b0\u5ddd\u5929\u5973\u7684\u5251\u52bf\uff0c\u4e09\u4eba\u51fa\u624b\u90fd\u662f\u8fc5\u903e\u98d8\u98ce\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\u4e4b \u95f4\uff0c\u51b0\u5ddd\u5927\u5973\u88ab\u4ed6\u4e00\u62c2\uff0c\u7acb\u5373\u5f15\u5251\u4fbf\u9000\uff0c\u6d1e\u51a5\u4e8e\u672a\u53ca\u8f6c\u8eab\uff0c\u53ea\u89c9\u9888\u9879\u6ed1\u817b\u817b\u7684\uff0c\u4f3c\u662f\u88ab\u91d1\u4e16 \u9057\u7684\u553e\u6d8e\u6cbe\u4e0a\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u53cd\u4e4e\u4e00\u6325\uff0c\u94c1\u62d0\u98de\u8d77\uff0c\u91d1\u4e16\u9057\u5728\u534a\u7a7a\u4e00\u4e2a\u7b4b\u6597\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u53cc\u624b \u4e00\u6309\uff0c\u63e1\u7d27\u94c1\u62d0\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u523a\u4ed6\u98ce\u5e9c\u7a74\u3001\u6f29\u77fe\u7a74\uff0c\u77fe\u7a74\u3001\u6f5c\u6e05\u7a74\uff01\u6c60\u4e2d\u4e86\u6211\u7684\u6697\u5668\uff0c\u6bd2 \u6c14\u5c31\u8981\u53d1\u4f5c\u4e86\uff01\u201d \u3000\u3000\u6d1e\u51a5\u5b50\u7684\u5185\u529f\u5df2\u7ec3\u5230\u4e00\u6d41\u5883\u754c\uff0c\u867d\u7136\u8fd8\u672a\u7ec3\u6210\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff0c\u4f46\u5df2\u662f\u767e\u90aa\u4e0d\u4fb5\uff0c\u66f4\u517c\u4ed6 \u95ed\u4e86\u5168\u8eab\u7a74\u9053\uff0c\u6bd2\u6c14\u66f4\u96be\u6f5c\u5165\uff0c\u6240\u4ee5\u5bf9\u91d1\u4e16\u9057\u7684\u8bdd\uff0c\u521d\u65f6\u8fd8\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4e0d\u6599\u6321\u4e86\u51b0\u5ddd\u5927\u5973\u51e0 \u62db\u4e4b\u540e\uff0c\u5ffd\u515a\u98ce\u5e9c\u7a74\u3001\u6f29\u73b0\u7a74\u3001\u6f5c\u7cbe\u7a74\u4e09\u5904\u9690\u9690\u53d1\u9ebb\uff0c\u679c\u7136\u662f\u6bd2\u6c14\u5faa\u7740\u8840\u7ba1\u5185\u653b\u5fc3\u80ba\u7684\u5f81 \u5146\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u6012\u3002 \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u9002\u624d\u6240\u7528\u7684\u6697\u5668\u4e43\u662f\u5929\u4e0b\u81f3\u6bd2\u7684\u6697\u5668\u3002\u86c7\u5c9b\u6709\u4e00\u79cd\u602a\u86c7\uff0c\u540d\u4e3a\u201c\u91d1\u89d2\u795e \u86c7\u201d\uff0c\u86c7\u5934\u5fae\u51f8\u82e5\u89d2\uff0c\u6bd2\u6027\u6700\u5927\uff0c\u91d1\u4e16\u9057\u7684\u98de\u9488\u4fbf\u662f\u8fd9\u79cd\u201c\u91d1\u89d2\u795e\u86c7\u201d\u7684\u6d8e\u6240\u7ec3\u8fc7\u7684\u3002\u91d1\u4e16 \u9057\u5728\u7ec3\u8fd9\u79cd\u6697\u5668\u4e4b\u65f6\uff0c\u5148\u670d\u4e0b\u7279\u5236\u7684\u89e3\u836f\uff0c\u8ba9\u8fd9\u79cd\u86c7\u54ac\u8fc7\u51e0\u6b21\uff0c\u56e0\u800c\u8eab\u4f53\u81ea\u7136\u4ea7\u751f\u4e86\u4e00\u79cd\u6297 \u6bd2\u7d20\uff0c\u4ed6\u628a\u98de\u9488\u542b\u5728\u53e3\u4e2d\uff0c\u4ea6\u662f\u65e0\u5bb3\u3002\u4f46\u522b\u4eba\u82e5\u7ed9\u6253\u4e2d\u7a74\u9053\uff0c\u9664\u975e\u786e\u5df2\u7ec3\u5230\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff0c \u5426\u5219\u6bd2\u9488\u89c1\u8840\uff0c\u6bd2\u6c14\u5373\u4fb5\uff0c\u95ed\u4e86\u7a74\u9053\uff0c\u4ecd\u662f\u65e0\u6cd5\u9632\u5fa1\uff0c\u8fd9\u79cd\u6bd2\u8ba1\u4ea6\u5206\u51e0\u79cd\uff0c\u4ee5\u524d\u5510\u7ecf\u5929\u5510\u8d5b \u82b1\u6240\u4e2d\u7684\u662f\u6bd2\u6027\u8f83\u8f7b\uff0c\u6162\u6162\u53d1\u4f5c\u7684\u3002\u800c\u4eca\u6d1e\u51a5\u5b50\u6240\u4e2d\u7684\u4e09\u652f\u6bd2\u9488\uff0c\u5374\u662f\u6bd2\u6027\u6700\u5f3a\uff0c\u7acb\u5373\u4fbf\u8981 \u53d1\u4f5c\u7684\u6bd2\u9488\u3002 \u3000\u3000\u6d1e\u51a5\u5b50\u5ffd\u515a\u98ce\u5e9c\u7a74\u3001\u6f29\u77fe\u7a74\u3001\u6f5c\u7cbe\u7a74\u4e09\u5904\u9690\u9690\u53d1\u9ebb\uff0c\u53c8\u60ca\u53c8\u6012\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1 \u91d1\u4e16\u9057\u53cc\u624b\u6309\u7740\u94c1\u62d0\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53c8\u5df2\u843d\u5230\u5730\u4e0a\u3002\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149 \u534e\uff0c\u4f60\u8981\u5411\u5192\u8001\u524d\u8f88\u8bf7\u6559\uff0c\u5478\uff0c\u4f60\u914d\u4e48\u3001\u8fd8\u662f\u6211\u548c\u4f60\u7ed3\u7f18\u7ed3\u7f18\u5427\uff01\u201d\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149 \u534e\uff01\u201d\u4e43\u662f\u6d1e\u51a5\u5b50\u9002\u624d\u8ba5\u7b11\u51b0\u5ddd\u5929\u5973\u7684\u6d3b\u8bed\uff0c\u800c\u4eca\u91d1\u4e16\u9057\u4e5f\u7528\u6765\u5632\u7b11\u4ed6\uff0c\u4e00\u6765\u662f\u8ba8\u597d\u51b0\u5ddd\u5929 \u5973\uff0c\u66ff\u5979\u51fa\u4e00\u53e3\u6c14\uff1b\u4e8c\u6765\u662f\u6709\u610f\u6fc0\u52a8\u6d1e\u51a5\u5b50\u7684\u6012\u706b\uff0c\u4ee4\u6bd2\u6c14\u53d1\u4f5c\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u6d1e\u51a5\u5b50\u5f53\u7136\u77e5\u9053\u4ed6\u7684\u7528\u610f\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u4e00\u58f0\u4e0d\u54cd\u5730\u53c8\u6321\u5f00\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u8fde\u73af \u4e09\u5251\uff0c\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u6697\u5668\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u89e3\uff0c\u4f60\u7ed9\u6211\u78d5\u4e09\u4e2a\u54cd\u5934\uff0c\u53eb\u6211\u7237\u7237\uff0c\u6211\u770b\u5728 \u65b0\u6536\u7684\u7070\u5b59\u5b50\u7684\u8138\u4e0a\uff0c\u6216\u8bb8\u80fd\u9976\u4f60\u6027\u547d\u3002\u201d\u6d1e\u51a5\u5b50\u602a\u773c\u4e00\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5c0f\u8f88\uff0c\u6559 \u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\u3002\u201d\u957f\u8896\u4e00\u62c2\uff0c\u628a\u51b0\u5ddd\u5929\u5973\u62c2\u5f00\uff0c\u5ffd\u5730\u547c\u547c\u4e24\u638c\uff0c\u5411\u91d1\u4e16\u9057\u75be\u5288\uff0c\u638c\u52bf\u6709\u5982 \u6392\u5c71\u5012\u6d77\u3001\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4f60\u52a8\u4e86\u771f\u529b\uff0c\u6b7b\u5f97\u66f4\u5feb\uff01\u201d\u5374\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u6a2a\u62d0\u4e00\u6321\uff0c\u62d0\u6756\u53c8\u7ed9 \u4ed6\u62ff\u7740\u3002\u91d1\u4e16\u9057\u9002\u624d\u5192\u4e86\u6027\u547d\u4e4b\u9669\uff0c\u7528\u201c\u5929\u9b54\u89e3\u4f53\u201d\u7684\u602a\u62db\u624d\u80fd\u8131\u8eab\uff0c\u8fd9\u65f6\u4e0d\u6562\u88ab\u4ed6\u629b\u8f6c\uff0c \u4ed7\u4e00\u88ab\u4ed6\u62ff\u7740\uff0c\u7acb\u5373\u7528\u4e8e\u65a4\u5760\u7684\u529f\u592b\u5b9a\u4f4f\u8eab\u5f62\uff0c\u540c\u65f6\u8fd0\u52b2\u5916\u593a\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u6296\u7389\u5251\uff0c\u8d70\u504f\u950b \u75be\u4e0a\uff0c\u8fde\u73af\u51fa\u5251\uff0c\u53c8\u523a\u4ed6\u90a3\u4e09\u5904\u4e2d\u4e86\u6bd2\u9488\u7684\u9053\u7a74\uff0c\u53ea\u542c\u5f97\u201c\u568f\u201d\u7684\uff0e\u4e00\u58f0\uff0c\u94c1\u62d0\u5ffd\u7136\u5206\u5f00\uff0c \u91d1\u4e16\u9057\u624b\u4e2d\u62ff\u7740\u4e00\u201c\u628a\u94c1\u5251\uff0c\u539f\u6765\u4ed6\u8fd9\u628a\u94c1\u5251\u4e43\u662f\u85cf\u5728\u62d0\u4e2d\u7684\u3002\u6d1e\u51a5\u5b50\u62ff\u7740\u94c1\u62d0\u7684\u5916\u58f3\uff0c\u67b6 \u5f00\u51b0\u5ddd\u5927\u5973\u7684\u5b9d\u5251\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u5251\u4e5f\u662f\u4e00\u4ef6\u5b9d\u7269\uff0c\u6a2a\u7814\u76f4\u523a\uff0c\u62db\u6570\u602a\u5f02\u65e0\u8bba\uff0c\u6325\u52a8\u4e4b\u9645\uff0c\u9690 \u9690\u6709\u80a1\u6bd2\u86c7\u7684\u8165\u5473\uff0c\u6d1e\u51a5\u5b50\u5c06\u94c1\u62d0\u4e00\u63b7\uff0c\u5ffd\u7136\u5411\u5730\u4e00\u5012\uff0c\u76d8\u819d\u5750\u5728\u5730\u4e0a\u3002\u5c55\u5f00\u53cc\u638c\uff0c\u529b\u6321\u51b0 \u5ddd\u5929\u5973\u548c\u91d1\u4e16\u9057\u7684\u56f4\u653b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u91d1\u4e16\u9057\u5de6\u624b\u6301\u62d0\uff0c\u53f3\u624b\u6301\u5251\uff0c\u653b\u52bf\u8d8a\u53d1\u51cc\u5389\uff0c\u6d1e\u51a5\u5b50\u7aef\u5750\u5730\u4e0a\uff0c\u8eab\u5b50\u52a8\u4e5f\u4e0d\u52a8\uff0c \u53ea\u51ed\u53cc\u638c\u7684\u4f38\u7f29\u64d2\u62ff\u4e4b\u52bf\uff0c\u529b\u654c\u4e09\u822c\u5175\u5668\uff0c\u770b\u6765\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u91d1\u4e16\u9057\u53c8 \u4e0d\u65ad\u7684\u51fa\u8a00\u8ba5\u7b11\uff0c\u8981\u6fc0\u4ed6\u6012\u706b\u653b\u5fc3\u3002\u6d1e\u51a5\u5b50\u62c6\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u9ed1\u6c14\u5df2\u6e10\u6e10\u900f\u51fa\u534e\u76d6\u3002\u51b0\u5ddd\u5929\u5973 \u5fc3\u5730\u4ec1\u6148\uff0c\u5ff5\u4ed6\u7ec8\u662f\u524d\u8f88\uff0c\u6709\u4e9b\u4e0d\u5fcd\uff0c\u89c1\u91d1\u4e16\u9057\u4e0d\u65ad\u7684\u65bd\u5c55\u6740\u624b\uff0c\u53eb\u9053\uff0c\u201c\u8ba9\u4ed6\u8d70\u5427\u3002\u201d\u6d1e \u51a5\u5b50\u602a\u773c\u4e00\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8ba9\uff0c\u4f60\u8981\u8d70\u4e5f\u4e0d\u80fd\u5462\uff01\u201d\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u4ed6\u81ea\u5df1\u8981\u5411 \u960e\u7f57\u738b\u62a5\u5230\uff0c\u8c01\u963b\u5f97\u6765\uff1f\u201d\u62a1\u8d77\u94c1\u62d0\uff0c\u53c8\u91cd\u91cd\u7684\u5f53\u5934\u6572\u4e0b\u3002\u51b0\u5ddd\u5929\u5973\u8f6c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u5510\u7ecf\u5929 \u5728\u53e6\u4e00\u8fb9\u6218\u9ec4\u77f3\u9053\u4eba\uff0c\u9ec4\u77f3\u9053\u4eba\u8f6c\u5b88\u4e3a\u653b\uff0c\u90a3\u67c4\u62c2\u5c18\u5b9b\u5982\u7389\u9f99\u592d\u77eb\uff0c\u5728\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u4e0d\u4f4f \u4ef7\u7684\u89c5\u9699\u5f3a\u653b\uff0c\u5510\u7ecf\u5929\u4ed7\u7740\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u4ec5\u80fd\u81ea\u4fdd\uff0c\u5c31\u5728\u51b0\u5ddd\u5929\u5973\u4e00\u77a5\u4e4b\u95f4\uff0c\u4ed6\u5df2\u63a5\u8fde\u9047\u4e86 \u51e0\u6b21\u9669\u62db\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u89c1\u5510\u7ecf\u5929\u8fed\u9047\u9669\u62db\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u7740\u6025\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6d1e\u51a5\u5b50\u5df2\u53d7\u91cd\u4f24\uff0c\u6599\u91d1\u4e16 \u9057\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d\u53cd\u8eab\u4e00\u8dc3\uff0c\u6536\u5251\u8df3\u51fa\u5708\u5b50\uff0c\u5ffd\u89c9\u6d1e\u51a5\u5b50\u53cc\u638c\u4f3c\u6709\u4e00\u80a1\u7275\u5f15\u4e4b\u529b\uff0c\u51e0\u4e4e\u6446\u8131\u4e0d \u5f00\uff0c\u4f46\u9002\u503c\u5176\u65f6\uff0c\u91d1\u4e16\u9057\u53c8\u662f\u4e00\u62d0\u6253\u4e0b\uff0c\u51b0\u5ddd\u5929\u5973\u7528\u529b\u5411\u5916\u4e00\u67b6\uff0c\u957f\u5251\u64a4\u4e86\u51fa\u6765\uff0c\u5fc3\u4e2d\u60ca\u7591 \u4e0d\u5b9a\u3002\u4f46\u89c1\u5510\u7ecf\u5929\u6b63\u88ab\u9ec4\u77f3\u9053\u4eba\u653b\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u7389\u5251\u4e00\u633a\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u7acb\u5373\u4e0a\u53bb \u523a\u9ec4\u77f3\u9053\u4eba\u7684\u80cc\u5fc3\uff0c\u89e3\u4e86\u5510\u7ecf\u5929\u4e4b\u56f0\u3002 \u3000\u3000\u4e24\u4eba\u518d\u5ea6\u8054\u5251\uff0c\u4e0d\u8fc7\u4e09\u5341\u62db\u3002\u53c8\u62a2\u4e86\u4e0a\u98ce\uff0c\u628a\u9ec4\u77f3\u9053\u4eba\u8feb\u5f97\u8f6c\u653b\u4e3a\u5b88\u3002\u53cc\u5251\u7eb5\u6a2a\uff0c\u6b63\u5728 \u6740\u5f97\u75db\u5feb\uff0c\u5510\u7ecf\u5927\u5ffd\u7136\u7709\u5934\u4e00\u76b1\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u5feb\u53bb\u52a9\u90a3\u75af\u4e10\u4e0d\u5fc5\u7406\u6211\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u65f6\u91d1\u4e16\u9057\u5df2\u78b0\u5230\u4e86\u6027\u547d\u7684\u5371\u9669\u3002\u51b0\u5ddd\u5929\u5973\u548c\u4ed6\u8054\u624b\u5bf9\u4ed8\u6d1e\u51a5\u4e8e\u4e4b\u65f6\uff0c\u8fd8\u4e0d\u89c9\u4ec0 \u4e48\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u53bb\uff0c\u4f46\u89c9\u6d1e\u51a5\u5b50\u7684\u638c\u529b\u8d8a\u6765\u8d8a\u5f3a\uff0c\u91d1\u4e16\u9057\u62d0\u5251\u517c\u65bd\uff0c\u770b\u4f3c\u653b\u52bf\u6781\u4e3a\u51cc\u5389\uff0c\u4f46 \u5df2\u88ab\u4ed6\u7684\u638c\u529b\u80f6\u7740\uff0c\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u7adf\u662f\u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00\u3002\u62a1\u62d0\u8f6c\u5251\u4e4b\u65f6\uff0c\u90fd\u8981\u975e\u5e38\u7528\u529b\u3002\u91d1\u4e16 \u9057\u53c8\u60ca\u53c8\u6025\uff0c\u7528\u529b\u5916\u593a\uff0c\u6d1e\u51a5\u5b50\u5ffd\u7136\u6539\u5b88\u4e3a\u653b\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u867d\u7136\u5750\u5728\u5730\u4e0a\uff0c\u638c\u529b\u6240\u53ca\uff0c\u5468\u56f4 \u4e08\u4f59\u65b9\u5706\u4e4b\u5730\uff0c\u90fd\u5df1\u88ab\u4ed6\u5c01\u4f4f\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u94c1\u5251\u5c31\u4f3c\u9677\u5165\u4e86\u6ce5\u6cbc\u4e4b\u4e2d\uff0c\u53ea\u80fd\u52c9\u5f3a\u6325\u52a8\uff0c\u60f3 \u6392\u9f50\u51fa\u6765\u8131\u8eab\u800c\u8d70\uff0c\u5df2\u662f\u4e0d\u80fd\u3002\u91d1\u4e16\u9057\u4e5f\u66fe\u8fde\u55b7\u4e24\u6b21\u6bd2\u9488\uff0c\u4f46\u8fd9\u65f6\u6d1e\u51a5\u5b50\u65f1\u6709\u9632\u5907\uff0c\u7109\u80fd\u518d \u7ed9\u4ed6\u6bd2\u9488\u5c04\u4e2d\u3001\u4ed6\u6bd2\u9488\u4e00\u626b\uff0c\u5c31\u88ab\u638c\u98ce\u9707\u6210\u7c89\u5c51\uff0c\u975e\u4f46\u4e0d\u80fd\u89e3\u56f0\uff0c\u53cd\u800c\u56e0\u4e3a\u5206\u4e86\u5206\u5fc3\uff0c\u66f4\u88ab \u6d1e\u51a5\u5b50\u7684\u638c\u529b\u6240\u5438\uff0c\u770b\u770b\u5c31\u8981\u88ab\u4ed6\u7275\u8fdb\u5185\u5708\u3002\u91d1\u4e16\u9057\u5fc3\u4e2d\u660e\u767d\uff0c\u6d1e\u51a5\u5b50\u662f\u5728\u6d88\u8017\u4ed6\u7684\u5185\u5bb6\u771f \u529b\uff0c\u5982\u6b64\u4e0b\u53bb\uff0c\u518d\u8fc7\u4e09\u5341\u62db\uff0c\u81ea\u5df1\u4fbf\u8981\u6c14\u8870\u529b\u7aed\uff0c\u90a3\u65f6\u7eb5\u7136\u4e0d\u6b7b\uff0c\u4e5f\u8981\u53d8\u6210\u5e9f\u4eba\u3002\u53ef\u662f\u5bf9\u65b9 \u7684\u638c\u529b\u8d8a\u6765\u8d8a\u5f3a\uff0c\u53c8\u8feb\u5f97\u81ea\u5df1\u975e\u8981\u4f7f\u7528\u5185\u5bb6\u771f\u529b\u76f8\u62d2\u4e0d\u53ef\u3002\u6b63\u5728\u82e6\u82e6\u6491\u6301\u4e4b\u9645\uff0c\u6d1e\u51a5\u4e8e\u5ffd\u5730 \u5389\u58f0\u53eb\u9053\uff1a\u201c\u72c2\u5984\u5c0f\u8f88\uff0c\u5982\u4eca\u77e5\u9053\u4e86\u6211\u7684\u5389\u5bb3\u4e48\uff1f\u201d\u53cc\u638c\u4e00\u7ffb\u4e00\u8986\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u91d1\u4e16\u9057\u7684 \u94c1\u62d0\u94c1\u5251\u90fd\u5df2\u88ab\u4ed6\u6293\u7740\uff0c\u8fd9\u65f6\u5ffd\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff0c\u201c\u4e0d\uff0c\u54b1\u4eec\u5148\u6536\u62fe\u4e86\u8fd9\u4e2a\u5996\u9053\u518d\u53bb\u52a9 \u4ed6\u3002\u201d\u539f\u6765\u51b0\u5ddd\u5929\u5973\u8fd8\u672a\u770b\u51fa\u91d1\u4e16\u9057\u7684\u5371\u9669\uff0c\u4e00\u5fc3\u60f3\u6253\u8d25\u9ec4\u77f3\u9053\u4eba\u518d\u5408\u529b\u53bb\u52a9\u91d1\u4e16\u9057\u3002\u5979\u8fd9 \u8bdd\u662f\u7b54\u8986\u5510\u7ecf\u5929\u7684\u3002\u91d1\u4e16\u9057\u542c\u4e86\uff0c\u5374\u5982\u5229\u7bad\u7a7f\u5fc3\uff0c\u6c14\u6124\u60b2\u9178\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4e00\u5fc3\u52a9\u4f60\uff0c\u4f60 \u5374\u53ea\u987e\u90a3\u4e2a\u5c0f\u5b50\u3002\u201d\u5fc3\u4e2d\u60b2\u75db\uff0c\u6597\u5fd7\u6d88\u5931\uff0c\u88ab\u6d1e\u51a5\u5b50\u5185\u529b\u6240\u5438\uff0c\u66f4\u662f\u62b5\u6321\u4e0d\u4f4f\uff0c\u770b\u770b\u5c31\u8981\u4ec6 \u5012\u3002\u3001\u5ffd\u53c8\u542c\u5f97\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u5148\u6551\u4ed6\uff01\u201d\u53ea\u89c1\u8d64\u8272\u5149\u534e\u75be\u95ea\uff0c\u602a\u9abc\u4e24\u58f0\uff0c\u4e24\u679d\u5929\u5c71\u795e \u8292\u88ab\u6d1e\u51a5\u5b50\u6296\u8d77\u94c1\u62d0\u6253\u98de\uff0c\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u91d1\u4e16\u9057\u6240\u53d7\u7684\u538b\u529b\u51cf\u4e86\u51e0\u5206\uff0c\u8eab\u5f62\u91cd\u65b0\u6062\u590d\u7a33\u5b9a\u3002 \u91d1\u4e16\u9057\u5fc3\u4e2d\u5927\u6127\uff0c\u4f46\u6597\u610f\u53c8\u589e\uff0c\u62fc\u4e86\u5168\u529b\u518d\u548c\u6d1e\u51a5\u4e8e\u76f8\u6301\u3002\u4f46\u5510\u7ecf\u5929\u7684\u5929\u5c71\u795e\u8292\u867d\u7136\u5389\u5bb3\uff0c \u5bf9\u6d1e\u51a5\u5b50\u5374\u53ea\u6709\u5a01\u80c1\u4e4b\u529f\uff0c\u4e0d\u80fd\u81f4\u4ed6\u6b7b\u547d\u3002\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u94c1\u5251\u88ab\u5bf9\u65b9\u6293\u4f4f\uff0c\u6b32\u653b\u4e0d\u80fd\uff0c\u8981\u653e \u624b\u4e5f\u4e0d\u884c\uff0c\u5185\u529b\u88ab\u8feb\u5f97\u6d88\u8017\u66f4\u751a\u3002 \u3000\u3000\u5510\u7ecf\u5929\u89c1\u52bf\u4e0d\u5999\uff0c\u7a81\u7136\u8f6c\u5b88\u4e3a\u653b\uff0c\u4ece\u5927\u987b\u5f25\u5251\u5f0f\u4e00\u53d8\u800c\u4e3a\u8ffd\u98ce\u5251\u6cd5\uff0c\u4e25\u5982\u96f7\u9706\u75be\u53d1\uff0c\u6012 \u6f6e\u5954\u817e\uff0c\u9ec4\u77f3\u9053\u4eba\u8feb\u5f97\u9000\u540e\u4e24\u6b65\uff0c\u6682\u907f\u950b\u8292\uff0c\u5510\u7ecf\u5929\u53cd\u8eab\u4e00\u8dc3\uff0c\u6e38\u9f99\u5251\u51cc\u7a7a\u4e0b\u523a\uff0c\u6709\u5982\u9e70\u96bc \u7a7f\u6797\uff0c\u5411\u6d1e\u51a5\u4e8e\u9888\u9879\u6325\u53bb\u3002\u4ed6\u4ee5\u9000\u4e3a\u8fdb\uff0c\u653b\u52bf\u4e00\u53d1\u5373\u8d70\uff0c\u5728\u4e00\u62db\u4e4b\u5185\uff0c\u6446\u8131\u4e86\u9ec4\u77f3\u9053\u4eba\u7684\u7f81 \u7eca\uff0c\u4fbf\u7acb\u5373\u8f6c\u653b\u6d1e\u51a5\u5b50\uff0c\u7aef\u7684\u662f\u8fc5\u6377\u4e4b\u6781\uff0c\u7f8e\u5999\u975e\u5e38\u3002\u51e0\u4e4e\u540c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u98de\u8eab \u63a0\u8d77\uff0c\u624b\u4e2d\u7389\u5251\u5316\u6210\u4e86\u4e00\u9053\u5bd2\u5149\uff0c\u4e5f\u523a\u5411\u6d1e\u51a5\u5b50\u7684\u80cc\u5fc3\u3002\u539f\u6765\u5979\u5df2\u770b\u51fa\u4e86\u91d1\u4e16\u9057\u7684\u5371\u9669\uff0c\u4e0e \u5510\u7ecf\u5929\u62b1\u7740\u4e00\u6837\u7684\u5fc3\u601d\uff0c\u540c\u6765\u63f4\u6551\u3002 \u3000\u3000\u6d1e\u51a5\u5b50\u672c\u4e8b\u518d\u5927\uff0c\u4e5f\u96be\u6321\u5510\u7ecf\u5929\u7b49\u4e09\u4e2a\u4eba\u7684\u540c\u65f6\u653b\u51fb\uff0c\u53ea\u89c1\u5728\u5251\u5149\u4eba\u5f71\u4e4b\u4e2d\uff0c\u6d1e\u51a5\u5b50\u9aa4 \u7136\u7ad9\u8d77\uff0c\u5c06\u91d1\u4e16\u9057\u4e00\u63a8\uff0c\u94c1\u62d0\u94c1\u5251\u4e00\u9f50\u53cd\u5f39\uff0c\u4e0e\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u78b0\u4e2a\u6b63\u7740\uff0c\u6323\u950c\u58f0\u54cd\uff0c\u4e00\u9f50 \u8361\u5f00\uff0c\u5148\u5316\u89e3\u4e86\u51b0\u5ddd\u5929\u5973\u653b\u4ed6\u540e\u5fc3\u8981\u7a74\u7684\u5251\u62db\uff0c\u5510\u7ecf\u5929\u7684\u8ffd\u98ce\u5251\u6cd5\u4f55\u7b49\u8fc5\u75be\uff0c\u8d81\u7740\u4ed6\u63a8\u62d0\u6321 \u5251\u7684\u7a7a\u9699\uff0c\u5237\u7684\u4e00\u5251\uff0c\u6539\u62b9\u4e3a\u524a\uff0c\u76f4\u6b3a\u5230\u8eab\u524d\u3002\u6d1e\u51a5\u5b50\u53cc\u638c\u65b9\u51fa\uff0c\u64a4\u638c\u5df2\u6765\u4e0d\u53ca\uff0c\u9976\u662f\u4ed6\u95ea \u907f\u5f97\u5feb\uff0c\u80a9\u5934\u4e0a\u4e5f\u5df1\u7740\u4e86\u4e00\u5251\u3002\u4f46\u5510\u7ecf\u5929\u88ab\u4ed6\u53cd\u638c\u4e00\u5e26\uff0c\u4ea6\u662f\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u524d\u6251\u4e86\u51e0\u6b65\u3002\u8fd9 \u4e00\u62db\uff0c\u53cc\u65b9\u51e0\u4e4e\u662f\u540c\u65f6\u53d1\u52a8\uff0c\u5510\u7ecf\u5929\u7684\u5b9d\u5251\u5148\u5230\uff0c\u6d1e\u51a5\u5b50\u7684\u638c\u529b\u672a\u5f97\u53d1\u6325\uff0c\u5510\u7ecf\u5929\u8fd9\u624d\u4e0d\u81f4 \u4e8e\u7ed9\u4ed6\u9707\u5012\uff1b\u4f46\u5510\u7ecf\u5927\u56e0\u907f\u4ed6\u638c\u529b\uff0c\u8fd9\u4e00\u62db\u653b\u52bf\u4e5f\u672a\u4f7f\u8db3\uff0c\u8981\u4e0d\u7136\u6d1e\u51a5\u5b50\u7684\u7435\u7436\u9aa8\u53ea\u6015\u4e5f\u8981 \u88ab\u6e38\u9f99\u5251\u523a\u7a7f\u3002 \u3000\u3000\u6d1e\u51a5\u5b50\u5148\u4e2d\u6697\u5668\uff0c\u540e\u906d\u5251\u4f24\uff0c\u5f3a\u8fd0\u7384\u529f\uff0c\u95ed\u4f4f\u4e86\u5168\u8eab\u7a74\u9053\uff0c\u4e0d\u4f46\u6b62\u4f4f\u4e86\u6bd2\u6c14\u5185\u4fb5\uff0c\u4e5f\u6b62 \u4f4f\u4e86\u9c9c\u8840\u5916\u6d41\u3002\u4ed6\u8fd9\u6d3e\u7684\u5185\u529f\u867d\u975e\u6b63\u5b97\u7684\u5185\u529f\u53ef\u6bd4\uff0c\u5374\u53e6\u6709\u5176\u795e\u5999\u4e4b\u5904\uff0c\u6b63\u5b97\u7684\u5185\u529f\uff0c\u5728\u53d7 \u4e86\u91cd\u4f24\u4e4b\u540e\uff0c\u8bb2\u7a76\u7684\u662f\u8fd0\u6c14\u81ea\u4fdd\uff0c\u5fcc\u6212\u7528\u529b\uff0c\u4ed6\u8fd9\u6d3e\u7684\u5185\u529f\u5374\u662f\u4ee5\u5168\u8eab\u7cbe\u529b\u8d2f\u6ce8\u5728\u53d7\u4f24\u4e4b \u5904\uff0c\u7b49\u4e8e\u7b51\u5824\u9632\u5fa1\u6d2a\u6c34\u4e00\u6837\u3002\u5728\u6d2a\u6c34\u672a\u653b\u7834\u5824\u9632\u4e4b\u524d\uff0c\u4e00\u65e0\u5f02\u72b6\uff0c\u4e25\u5982\u5e38\u4eba\uff0c\u4e00\u6837\u53ef\u4ee5\u6251\u51fb \u653b\u654c\u3002\u4f46\u6b63\u5b97\u7684\u5185\u529f\uff0c\u81ea\u5df1\u7597\u4f24\u4e4b\u540e\uff0c\u5e76\u4e0d\u5f71\u54cd\u672c\u8eab\u5143\u6c14\uff0c\u7b49\u5982\u6cbb\u6c34\u4e2d\u7684\u201c\u758f\u5bfc\u201d\u4e4b\u6cd5\uff0c\u5c06 \u6bd2\u6c14\u6e32\u6cc4\uff0c\u4fbf\u53ef\u65e0\u788d\u3002\u4ed6\u8fd9\u6d3e\u7684\u5185\u529f\uff0c\u7b49\u5982\u6cbb\u6c34\u4e2d\u7684\u201c\u5835\u585e\u201d\u4e4b\u6cd5\uff0c\u53ea\u80fd\u6cbb\u6807\uff0c\u4e0d\u80fd\u6cbb\u672c\uff0c \u65f6\u95f4\u4e00\u4e45\uff0c\u7cbe\u529b\u6da3\u6563\uff0c\u4fbf\u7b49\u5982\u7ed9\u6d2a\u6c34\u653b\u7834\u5824\u9632\uff0c\u4e0d\u6b7b\u4ea6\u6210\u5e9f\u5165\uff0c\u5c31\u7b97\u5373\u65f6\u53ef\u4ee5\u53d6\u80dc\uff0c\u56e0\u5168\u8eab \u7cbe\u8840\u88ab\u8017\uff0c\u5c06\u6765\u6700\u5c11\u4e5f\u8981\u51cf\u5341\u5e74\u529f\u529b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e0e\u51b0\u5ddd\u5929\u5973\u4e0d\u77e5\u6d1e\u51a5\u5b50\u7684\u5185\u529f\u53e6\u6709\u602a\u5f02\u4e4b\u5904\uff0c\u89c1\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c\u5c45\u7136\u4e00\u8dc3\u5373\u8d77\uff0c\u53c8 \u65bd\u6251\u51fb\uff0c\u771f\u662f\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u5927\u662f\u60ca\u5f02\u3002\u6d1e\u51a5\u5b50\u6068\u6781\u91d1\u4e16\u9057\uff0c\u4ed6\u77e5\u9053\u6b64\u9645\u5728\u654c\u65b9\u4e09\u4eba \u4e4b\u4e2d\uff0c\u91d1\u4e16\u9057\u56e0\u9002\u624d\u6d88\u8017\u771f\u529b\u8fc7\u591a\uff0c\u5df1\u662f\u6700\u5f31\u7684\u4e00\u73af\uff0c\u6240\u4ee5\u4e00\u8dc3\u800c\u8d77\uff0c\u4e58\u7740\u5510\u7ecf\u5929\u8eab\u5f62\u672a \u5b9a\uff0c\u672a\u53ca\u56de\u63f4\u4e4b\u9645\uff0c\u547c\u7684\u4e00\u638c\uff0c\u5c31\u60f3\u628a\u91d1\u4e16\u9057\u6bd9\u4e8e\u638c\u4e0b\uff01 \u3000\u3000\u8fd9\u4e00\u638c\u52bf\u631f\u5343\u94a7\uff0c\u91d1\u4e16\u9057\u5de6\u62d0\u8fce\u51fb\uff0c\u53f3\u5251\u62a4\u80f8\uff0c\u60c5\u77e5\u62b5\u6321\u4e0d\u4e86\uff0c\u53ea\u4e0d\u8fc7\u7a0d\u5c3d\u4eba\u4e8b\uff0c\u5e0c\u671b \u5c11\u53d7\u635f\u4f24\u800c\u5df2\uff1b\u5c31\u5728\u8fd9\u95ee\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u53ea\u89c1\u5bd2\u5149\u75be\u95ea\uff0c\u51b0\u5ddd\u5929\u5973\u62e6\u5728\u91d1\u4e16\u9057\u7684\u9762\u524d\uff0c\u4e00\u62db \u201c\u96ea\u62e5\u84dd\u5173\u201d\uff0c\u5251\u52bf\u81ea\u5de6\u5411\u53f3\uff0c\u5212\u4e86\u534a\u4e2a\u5706\u5f27\u3002\u8fd9\u4e00\u5251\u534a\u5b88\u534a\u653b\uff0c\u672c\u662f\u6781\u5176\u7cbe\u5999\u7684\u62db\u6570\uff0c\u4f46 \u6d1e\u51a5\u5b50\u8fd9\u4e00\u638c\u662f\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u51b0\u5ddd\u5927\u5973\u88ab\u4ed6\u7684\u638c\u529b\u4e00\u51b2\uff0c\u4f46\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u8eab\u5f62\u5df2\u98de\u4e86 \u8d77\u6765\uff0c\u5728\u7a7a\u4e2d\u8fde\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u8fd9\u8fd8\u662f\u5979\u95ea\u907f\u5f97\u5feb\uff0c\u4ee5\u7edd\u9876\u7684\u8f7b\u529f\u4e00\u6cbe\u638c\u529b\u5373\u98de\u8eab\u800c\u8d77\uff0c\u8981 \u4e0d\u7136\uff0c\u82e5\u7ed9\u6d1e\u51a5\u5b50\u7684\u638c\u529b\u6253\u5b9e\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u5154\u4e0d\u4e86\u5251\u6298\u8eab\u4ea1\u3002 \u3000\u3000\u6d1e\u51a5\u5b50\u88ab\u5979\u4e00\u6321\uff0c\u8863\u8896\u7ed9\u5272\u53bb\u4e86\u534a\u622a\uff0c\u638c\u52bf\u81ea\u662f\u7a0d\u53d7\u5ef6\u963b\u3002\u91d1\u4e16\u9057\u94c1\u62d0\u4e00\u62db\u201c\u9a7e\u4e58\u516d \u9f99\u201d\u62e6\u8170\u6a2a\u626b\uff0c\u6d1e\u51a5\u5b50\u5de6\u638c\u4e00\u5288\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c\u4f46\u542c\u5f97\u8f70\u7684\u4e00\u58f0\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u8131\u624b\u98de\u51fa\uff0c\u5f2f \u6210\u4e86\u4e86\u4e2a\u5f13\u5f62\uff0c\u6d1e\u51a5\u5b50\u7684\u5de6\u638c\u8155\u9aa8\u4ea6\u788e\u4e86\u4e24\u5f88\uff0c\u540a\u4e86\u4e0b\u6765\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6d1e\u51a5\u4e8e\u53cd\u638c\u7a7f \u80f8\u76f4\u8fdb\uff0c\u624b\u6307\u4e00\u5f39\uff0c\u5c06\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u5f39\u5f00\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u770b\u770b\u5c31\u8981\u201c\u5370\u201d\u5230\u91d1\u4e16\u9057\u80f8\u53e3\u8981\u7a74\u3002 \u3000\u3000\u6d1e\u51a5\u5b50\u6b63\u5f85\u65bd\u5c55\u6740\u624b\uff0c\u731b\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u539f\u6765\u662f\u5510\u7ecf\u5927\u7684\u6e38\u9f99\u5251\u5df2\u7136\u523a\u5230\uff0c\u6d1e \u51a5\u5b50\u8feb\u5f97\u8f6c\u8eab\u53d1\u638c\uff0c\u4f46\u4ed6\u8fd8\u662f\u4e0d\u80af\u9519\u8fc7\u673a\u4f1a\uff0c\u867d\u7136\u4e3a\u4e86\u5e94\u4ed8\u5510\u7ecf\u5929\uff0c\u4e0d\u80fd\u5bf9\u91d1\u4e16\u9057\u65bd\u5c55\u6740 \u624b\uff0c\u4f46\u8f6c\u8eab\u4e4b\u9645\uff0c\u4ecd\u7528\u9634\u6bd2\u7684\u624b\u6cd5\uff0c\u4f38\u957f\u4e86\u6307\u7532\uff0c\u4e2d\u98df\u4e8c\u6307\u5df2\u5728\u91d1\u4e16\u9057\u7684\u80f8\u53e3\u4e00\u5212\u800c\u8fc7\uff01 \u3000\u3000\u6b63\u5982\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u5510\u7ecf\u5929\u8fdb\u51fb\u6d1e\u51a5\u5b50\uff0c\u9ec4\u77f3\u9053\u4eba\u4ea6\u5df2\u5982\u5f71\u9644\u5f62\uff0c\u8ddf\u8e2a\u8ffd\u5230\uff0c\u51b0 \u5ddd\u5929\u5973\u4eba\u672a\u843d\u5730\uff0c\u7acb\u5373\u53d1\u58f0\u53eb\u9053\uff1a\u201c\u7559\u5fc3\u540e\u9762\uff01\u201d\u8ddf\u7740\u67f3\u8170\u4e00\u6298\uff0c\u4e5f\u62a2\u7740\u5411\u9ec4\u77f3\u9053\u4eba\u7684\u540e\u5fc3 \u51fa\u5251\u3002\u8fd9\u51e0\u4e0b\u5b50\u7684\u52a8\u4f5c\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u4f46\u89c1\u9ec4\u77f3\u9053\u4eba\u62c2\u5c18\u4e00\u8d77\uff0c\u5510\u7ecf\u5927\u811a\u6b65\u4e00\u4e2a\u8e09\u8dc4\uff0c\u659c\u6251 \u51fa\u53bb\uff0c\u6d1e\u51a5\u5b50\u98de\u8eab\u75be\u63a0\uff0c\u5de6\u624b\u4e00\u62db\u201c\u624b\u6325\u4e94\u5f26\u201d\uff0c\u4e94\u6839\u957f\u6307\u7532\u90fd\u5728\u5510\u7ecf\u5927\u7684\u80cc\u5fc3\u5212\u8fc7\uff0c\u53d1\u51fa \u8f7b\u5fae\u7684\u75e4\u9576\u4e4b\u58f0\uff0c\u5510\u7ecf\u5929\u7684\u8863\u670d\u5df2\u7ed9\u4ed6\u6495\u5f00\u4e86\u51e0\u6761\u7834\u7247\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u5510\u7ecf\u592d\u811a\u8ddf\u672a\u5b9a\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u5251\u3002\u6d1e\u51a5\u4e8e\u5fc3\u4e2d\u4e00\u51db\uff0c\u4ee5\u4ed6\u548c\u9ec4\u77f3 \u9053\u4eba\u5939\u653b\u4e4b\u529b\uff0c\u5c45\u7136\u7ed9\u5510\u7ecf\u5929\u95ea\u4e86\u5f00\u53bb\uff0c\u5df2\u662f\u5927\u51fa\u610f\u5916\uff0c\u4ed6\u90a3\u4e94\u6307\u4e00\u5212\uff0c\u4e43\u662f\u6700\u9634\u72e0\u6bd2\u8fa3\u7684 \u201c\u795e\u9b54\u6293\u6cd5\u201d\u660e\u77e5\u5df2\u5212\u7834\u4e86\u5510\u7ecf\u5927\u7684\u8863\u88f3\uff0c\u6309\u8bf4\u5e94\u8be5\u628a\u4ed6\u7684\u80cc\u5fc3\u76ae\u8089\u6293\u7834\uff0c\u4ee4\u4ed6\u7a74\u9053\u7684\u7ecf\u8109 \u788e\u65ad\uff0c\u4f46\u5510\u7ecf\u592d\u7adf\u7136\u9762\u8272\u5982\u5e38\uff0c\u534a\u70b9\u8840\u73e0\u4e5f\u6ca1\u6709\u6e85\u51fa\uff01 \u3000\u3000\u6d1e\u51a5\u5b50\u5de6\u624b\u8155\u9aa8\u65ad\u4e86\u4e24\u6839\uff0c\u6025\u5207\u4e4b\u95f4\u4e0d\u80fd\u7528\u529b\uff0c\u53ea\u80fd\u7528\u53f3\u638c\u4e4b\u529b\uff0c\u4e00\u8fde\u5316\u89e3\u4e86\u5510\u7ecf\u5929\u7684 \u4e09\u62db\u653b\u52bf\u3002\u8fd9\u65f6\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u4e5f\u5df2\u4e0e\u9ec4\u77f3\u9053\u4eba\u6218\u5728\u4e00\u8d77\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5251\u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u6c14\u529b\u5374\u662f\u8fdc\u8fdc\u4e0d\u5982\u9ec4\u77f3\u9053\u4eba\uff0c\u4e03\u62db\u4e00\u8fc7\uff0c\u9999\u6c57\u6dcb\u6f13\uff0c\u5510\u7ecf\u5929\u72ec \u6218\u6d1e\u51a5\u5b50\uff0c\u66f4\u662f\u5403\u529b\u3002\u6fc0\u6218\u4e2d\u5510\u7ecf\u5929\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u9ec4\u77f3\u9053\u4eba\u5c06\u62c2\u5c18\u6563\u5f00\uff0c\u6709\u5982\u4e00\u5f20\u6e14\u7f51\uff0c \u7f69\u7740\u51b0\u5ddd\u5929\u5973\u7684\u51b0\u9b44\u5bd2\u5149\uff0c\u7d27\u7d27\u5411\u5185\u6536\u675f\u3002\u5510\u7ecf\u5929\u6df1\u77e5\u4ed6\u7684\u62c2\u5c18\u5389\u5bb3\uff0c\u51b0\u5ddd\u5929\u5973\u4ed7\u5b9d\u5251\u62a4\u5168 \u8eab\uff0c\u62c2\u5c18\u5343\u4e1d\u4e07\u7f15\uff0c\u53ea\u8981\u88ab\u4e00\u6839\u5c18\u4e1d\u900f\u8fc7\u5251\u5149\uff0c\u90a3\u4fbf\u662f\u523a\u7a74\u653b\u5fc3\u4e4b\u7978\uff0c\u8fd9\u65f6\u51b0\u5ddd\u5929\u5973\u7684\u5251\u5149 \u5df2\u88ab\u4ed6\u6108\u538b\u6108\u7f29\uff0c\u4ec5\u4ec5\u80fd\u62a4\u7740\u5934\u9762\u4e0e\u5fc3\u80f8\u5404\u5904\u8981\u5bb3\u4e86\u3002\u5510\u7ecf\u5929\u5fc3\u5185\u5403\u60ca\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u54b1\u4eec \u5feb\u8054\u5728\u4e00\u8d77\u3002\u201d\u4e00\u5206\u5fc3\uff0c\u51e0\u4e4e\u5403\u4e86\u6d1e\u51a5\u5b50\u4e00\u638c\u3002\u5510\u7ecf\u5927\u8fde\u5c55\u8ffd\u98ce\u5251\u6cd5\uff0c\u594b\u529b\u5f3a\u653b\uff0c\u4ecd\u7136\u88ab\u4ed6 \u638c\u529b\u80f6\u7740\uff0c\u51b2\u51fa\u4e24\u6b65\uff0c\u53cd\u88ab\u8feb\u9000\u4e09\u6b65\u3002\u51b0\u5ddd\u5929\u5973\u5168\u8eab\u5728\u201c\u5c18\u7f51\u201d\u5a01\u80c1\u4e4b\u4e0b\uff0c\u66f4\u662f\u8131\u4e0d\u4e86\u8eab\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5598\u606f\u672a\u5b9a\uff0c\u62fe\u8d77\u94c1\u62d0\uff0c\u90a3\u652f\u94c1\u62d0\u88ab\u6d1e\u51a5\u5b50\u62d7\u5f2f\uff0c\u5df2\u4f3c\u4e00\u5f20\u94c1\u5f13\uff0c\u91d1\u4e16\u9057\u594b\u529b\u4e00 \u626f\uff0c\u53c8\u5c06\u5b83\u626f\u76f4\uff0c\u98de\u8eab\u4e00\u8d77\uff0c\u94c1\u62d0\u70b9\u6253\u9ec4\u77f3\u9053\u4eba\u80cc\u5fc3\u7684\u201c\u5927\u67f1\u7a74\u201d\u3002\u9ec4\u77f3\u9053\u4eba\u53cd\u624b\u4e00\u62c2\uff0c\u91d1 \u4e16\u9057\u8fd9\u4e00\u62db\u5374\u662f\u865a\u62db\uff0c\u94c1\u62d0\u5411\u65c1\u4e00\u6233\uff0c\u5728\u5730\u4e0a\u4e00\u70b9\uff0c\u8eab\u5f62\u5728\u534a\u7a7a\u4e00\u8f6c\uff0c\u201c\u5478\u201d\u7684\u4e00\u53e3\u6d53\u75f0\uff0c \u53c8\u5411\u6d1e\u51a5\u5b50\u5410\u51fa\uff0c\u6d1e\u51a5\u5b50\u5927\u6012\uff0c\u5374\u4ea6\u6015\u4ed6\u7684\u75f0\u5185\u85cf\u6709\u6697\u5668\uff0c\u626c\u8896\u4e00\u62c2\uff0c\u8361\u8d77\u52b2\u98ce\uff0c\u5c06\u4ed6\u7684\u75f0 \u6d8e\u5439\u5f00\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u6240\u4e89\u7684\u53ea\u662f\u77ac\u606f\u7684\u65f6\u673a\uff0c\u91d1\u4e16\u9057\u8fde\u65bd\u5947\u88ad\uff0c\u8feb\u5f97\u9ec4\u77f3\u9053\u4eba\u4e0e\u6d1e\u51a5\u5e72\u90fd\u8981\u5206\u795e \u5bf9\u4ed8\uff0c\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u5df2\u8d81\u7740\u8fd9\u77ac\u606f\u4e4b\u95f4\u7684\u7a7a\u9699\uff0c\u5251\u5149\u9aa4\u957f\uff0c\u7a81\u51fa\u5305\u56f4\uff0c\u4f1a\u5728\u4e00\u8d77\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5c45\u4e2d\uff0c\u5510\u7ecf\u5929\u4e0e\u91d1\u4e16\u9057\u5404\u5728\u4e00\u8fb9\uff0c\u5f62\u6210\u4e86\u4e09\u5165\u8054\u624b\u5bf9\u4ed8\u4e24\u6d3e\u7684\u5b97\u5e08\uff0c\u5f62\u52bf\u7a0d \u7a33\u3002\u91d1\u4e16\u9057\u63a5\u4e86\u9ec4\u77f3\u9053\u4eba\u4e24\u62db\uff0c\u767e\u5fd9\u4e2d\u5077\u770b\u51b0\u5ddd\u5929\u5973\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u8138\u6cdb\u7ea2\u6f6e\uff0c\u4e5f\u6b63\u5728\u770b\u7740 \u5510\u7ecf\u5929\uff0c\u90a3\u773c\u5149\u4e2d\u5145\u6ee1\u5173\u6000\u611f\u6fc0\u4e0e\u7231\u601c\uff0c\u773c\u5149\u505c\u5728\u5510\u7ecf\u5929\u88ab\u6d1e\u51a5\u5b50\u6293\u7834\u8863\u88f3\u7684\u6240\u5728\uff0c\u4f4e\u58f0\u95ee \u9053\uff1a\u201c\u6ca1\u788d\u4e8b\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u5427\uff0c\u6211\u6ca1\u53d7\u4f24\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8fde\u6321\u5f00\u4e86\u6d1e\u51a5\u5b50\u7684\u4e09 \u62db\u653b\u52bf\u3002\u6fc0\u6218\u4e4b\u4e2d\uff0c\u4ed6\u4e8c\u4eba\u7adf\u662f\u871c\u610f\u67d4\u60c5\uff0c\u4e92\u76f8\u5173\u6ce8\u3002\u51b0\u5ddd\u5929\u5973\u9664\u4e86\u7559\u795e\u654c\u4eba\u7684\u653b\u52bf\uff0c\u773c\u775b \u5c31\u6ca1\u6709\u79bb\u5f00\u8fc7\u5510\u7ecf\u5929\uff0c\u5979\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u91d1\u4e16\u9057\u4e5f\u6b63\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u5077\u773c\u770b\u5979\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u5185\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u771f\u662f\u5404\u4eba\u6709\u5404\u4eba\u7684\u7f18\u4efd\uff01\u201d\u53c8\u60f3\u9053\uff1a\u201c\u5510\u7ecf\u5929\u4e2d\u4e86\u6d1e\u51a5\u5b50\u4e00 \u6293\uff0c\u5c45\u7136\u6beb\u672a\u53d7\u4f24\uff0c\u5440\uff0c\u6211\u51ed\u4ec0\u4e48\u4e0e\u4ed6\u4e89\u5f3a\u8d4c\u80dc\uff1f\u201d\u81ea\u5351\u4e4b\u611f\uff0c\u6cb9\u7136\u800c\u751f\uff0c\u4ed6\u5374\u4e0d\u77e5\u5510\u7ecf\u5929 \u8eab\u4e0a\u7a7f\u6709\u5085\u9752\u4e3b\u5f53\u5e74\u9001\u7ed9\u4ed6\u6bcd\u4eb2\u7684\u62a4\u8eab\u5b9d\u7532\u3002\u91d1\u4e16\u9057\u88ab\u6d1e\u51a5\u5b50\u6293\u4f24\u4e4b\u5904\uff0c\u5168\u4ed7\u4ed6\u7528\u771f\u6c14\u62a4 \u7740\uff0c\u8fd9\u65f6\u601d\u6f6e\u7eb7\u4e71\uff0c\u4f24\u5904\u9690\u9690\u9ebb\u75db\uff0c\u91d1\u4e16\u9057\u6697\u53eb\u201c\u2018\u4e0d\u597d\u3002\u201d\u8d76\u5fd9\u518d\u5b9a\u795e\u8fd0\u6c14\u65f6\uff0c\u6d1e\u51a5\u5b50\u5df2 \u770b\u51fa\u7834\u7efd\uff0c\u5ffd\u5730\u4e00\u638c\u5411\u4ed6\u80f8\u53e3\u626b\u53bb\uff01 \u3000\u3000\u91d1\u4e16\u9057\u7684\u94c1\u5251\u6b63\u88ab\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u62c2\u8fc7\u4e00\u8fb9\uff0c\u95e8\u6237\u5927\u5f00\uff0c\u6d1e\u51a5\u5b50\u90a3\u4e00\u638c\u5f53\u80f8\u5288\u5165\uff0c\u5b9e\u662f \u65e0\u53ef\u62b5\u5fa1\u3002\u638c\u98ce\u4eba\u5f71\u4e4b\u4e2d\uff0c\u5ffd\u89c1\u5510\u7ecf\u5929\u62a2\u5feb\u4e00\u6b65\uff0c\u201c\u7830\u201d\u7684\u4e00\u638c\u51fb\u4e2d\u91d1\u4e16\u9057\u8170\u80ef\uff0c\u91d1\u4e16\u9057\u8eab \u8eaf\u817e\u7a7a\u98de\u8d77\uff0c\u8fd9\u4e00\u4e0b\u4e0d\u4f46\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u8fde\u91d1\u4e16\u9057\u4e5f\u83ab\u77e5\u7528\u610f\uff0c\u8fd8\u4ee5\u4e3a\u662f\u5510\u7ecf\u5929\u4e58\u673a\u5077\u4e0b\u6bd2 \u624b\uff0c\u5fc3\u4e2d\u8fd8\u672a\u9a82\u51fa\uff0c\u5ffd\u89c9\u8eab\u5b50\u88ab\u4e00\u80a1\u529b\u9053\u6240\u63a8\uff0c\u5982\u6c34\u6fc0\u5c04\uff0c\u7adf\u7136\u6697\u5408\u7740\u81ea\u5df1\u5e73\u7d20\u6240\u7528\u7684\u8f7b\u529f \u98de\u63a0\u4e4b\u52bf\u3002\u8fd9\u4e00\u77ac\u95f4\uff0c\u91d1\u4e16\u9057\u987f\u7136\u9192\u609f\uff0c\u539f\u6765\u662f\u5510\u7ecf\u5929\u7528\u6700\u4e0a\u4e58\u7684\u501f\u529b\u9001\u529b\u7684\u529f\u592b\u6551\u4e86\u81ea \u5df1\uff01\u5510\u7ecf\u5929\u8fd9\u4e00\u638c\u7684\u529b\u9053\u771f\u662f\u6070\u5230\u597d\u5904\uff0c\u8868\u9762\u770b\u6765\uff0c\u6253\u5f97\u751a\u4e3a\u51f6\u731b\uff0c\u5176\u5b9e\u5bf9\u91d1\u4e16\u9057\u5374\u662f\u6beb\u65e0 \u4f24\u5bb3\uff0c\u800c\u4e14\u4ee4\u91d1\u4e16\u9057\u98de\u63a0\u4e4b\u52bf\u66f4\u5176\u8fc5\u75be\u81ea\u7136\u3002\u672c\u6765\u5510\u7ecf\u5929\u8fd8\u672a\u7528\u5f97\u5982\u6b64\u7cbe\u5999\uff0c\u53ea\u56e0\u4ed6\u4e0e\u91d1\u4e16 \u9057\u66fe\u4ea4\u624b\u6570\u6b21\uff0c\u719f\u8bc6\u4ed6\u7684\u8f7b\u529f\u8def\u6570\uff0c\u800c\u501f\u529b\u9001\u529b\u53c8\u6b63\u662f\u5929\u5c71\u6d3e\u7684\u5185\u529f\u7edd\u6280\uff0c\u6545\u6b64\u5192\u9669\u4e00\u8bd5\uff0c \u7acb\u89c1\u5947\u6548\u3002 \u3000\u3000\u6d1e\u51a5\u5b50\u662f\u524d\u8f88\u9ad8\u624b\uff0c\u5510\u7ecf\u5929\u4e00\u638c\u62cd\u51fa\uff0c\u4ed6\u53ef\u662f\u7acb\u5373\u4fbf\u770b\u51fa\u4e86\u5510\u7ecf\u5929\u7684\u624b\u6cd5\uff0c\u6d1e\u51a5\u5b50\u7aef\u7684 \u72e0\u6bd2\u4e4b\u6781\uff0c\u5de6\u624b\u4e00\u6446\uff0c\u4e94\u5f88\u957f\u6307\u7532\u5ffd\u7136\u8131\u8089\u98de\u51fa\uff0c\u5bc6\u5c04\u5510\u7ecf\u5929\u7684\u9762\u4e0a\u53cc\u775b\u3002\u51b0\u5ddd\u5929\u5973\u6025\u5fd9\u6a2a \u5251\u6321\u5f00\uff0c\u6d1e\u51a5\u5b50\u4e00\u58f0\u602a\u5578\uff0c\u8eab\u5b50\u817e\u7a7a\uff0c\u7d27\u8e51\u91d1\u4e16\u9057\u80cc\u540e\u3002\u4ed6\u8fd9\u4e00\u4e0b\u602a\u5f02\u7684\u624b\u6cd5\uff0c\u8017\u635f\u4e86\u4e0d\u5c11 \u7cbe\u8840\uff0c\u7528\u610f\u5c31\u5728\u58f0\u4e1c\u51fb\u897f\uff0c\u5c06\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u963b\u6b62\uff0c\u800c\u4ed6\u5374\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u8ffd\u5230\u91d1\u4e16\u9057 \u7684\u80cc \u3000\u3000\u91d1\u4e16\u9057\u53bb\u52bf\u6781\u901f\uff0c\u4ece\u6bbf\u4e2d\u4f17\u4eba\u5934\u4e0a\u98de\u8fc7\uff0c\u4f17\u4eba\u7eb7\u7eb7\u95ea\u907f\uff0c\u53ea\u89c1\u4ed6\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u4e0b\u6765\uff0c\u5df2 \u5230\u4e86\u5927\u6bbf\u7684\u9636\u4e0b\u3002\u6d1e\u51a5\u5b50\u7684\u8f7b\u529f\u4e5f\u786e\u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u91d1\u4e16\u9057\u521a\u521a\u843d\u5730\uff0c\u4ed6\u4e5f\u98de\u5230\u4e86 \u91d1\u4e16\u9057\u7684\u5934\u9876\uff0c\u5165\u5728\u534a\u7a7a\uff0c\u5c31\u4f3c\u5de8\u9e70\u6251\u4e0b\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u788e\u51fb\u91d1\u4e16\u9057\u7684\u9876\u5fc3\u3002\u4ed6\u6068\u6781\u4e86\u91d1\u4e16\u9057 \u7528\u6697\u5668\u4f24\u4ed6\uff0c\u5fc3\u60f3\u65e5\u540e\u81ea\u5df1\u53cd\u6b63\u8981\u6210\u5e9f\u4eba\uff0c\u8fd9\u4e00\u4e0b\u7adf\u662f\u5c06\u5168\u8eab\u6240\u6709\u7684\u7cbe\u529b\u90fd\u8fd0\u5728\u638c\u5fc3\uff0c\u51cc\u7a7a \u4e0b\u51fb\uff0c\u6bd4\u524d\u4e24\u6b21\u66f4\u4e3a\u51f6\u731b\uff0c\u5ea7\u4e2d\u9664\u4e86\u5192\u5ddd\u751f\u4e4b\u5916\uff0c\u5373\u7b97\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u5408\u529b\u62b5\u6321\uff0c\u4e5f\u6321\u4e0d \u4f4f\uff0c\u66f4\u4e0d\u8981\u8bf4\u5df2\u662f\u7b4b\u75b2\u529b\u7aed\uff0c\u53d7\u4f24\u4e4b\u540e\u7684\u91d1\u809a\u9057\u4e86\u3002 \u3000\u3000\u5c31\u5728\u5168\u4e16\u9057\u7684\u6027\u547d\u60ac\u4e8e\u4fc4\u503e\uff0c\u5e72\u94a7\u4e00\u53d1\u4e4b\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u6781\u6e05\u8106\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u8c0a\u53cb\u5e72 \u561b\u751f\u8fd9\u6837\u5927\u7684\u6c14\u5440\uff01\u201d\u6d1e\u51a5\u5b50\u8eab\u8eaf\u4e00\u9707\uff0c\u53cc\u638c\u4e0b\u51fb\uff0c\u7adf\u7136\u6253\u6b6a\u3002\u4f17\u4eba\u773c\u524d\u4e00\u82b1\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e2d \u5e74\u7f8e\u5987\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5df2\u5230\u4e86\u4e24\u4eba\u8eab\u8fb9\uff0c\u957f\u8896\u8f7b\u8f7b\u4e00\u62c2\uff0c\u6d1e\u51a5\u5b50\u5ffd\u5730\u4e00\u58f0\u5389\u53eb\uff0c\u4ec6\u5230\u5730\u4e0a\uff0c \u53c8\u7acb\u523b\u7ffb\u8d77\uff0c\u76d8\u819d\u8dcc\u5750\u3002\u91d1\u4e16\u9057\u98de\u5954\u51fa\u6bbf\uff0c\u90a3\u4e2d\u5e74\u7f8e\u5987\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u662f\u60f3\u8ffd\u51fa\u53bb\uff0c\u773c\u5149 \u4e00\u8f6c\uff0c\u770b\u89c1\u6d1e\u51a5\u5b50\u7aef\u5750\u5730\u4e0a\uff0c\u4ed6\u90a3\u6ee1\u5934\u84ec\u4e71\u7684\u5934\u53d1\uff0c\u672c\u6765\u662f\u4e4c\u9ed1\u5f97\u5149\u53ef\u9274\u4eba\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u5374 \u5ffd\u5730\u53d8\u5f97\u6839\u6068\u7070\u767d\uff0c\u9762\u4e0a\u73b0\u51fa\u65e0\u6570\u76b1\u7eb9\u3002\u6d1e\u51a5\u5b50\u7684\u5916\u8c8c\u672c\u6765\u4f3c\u4e2a\u4e2d\u5e74\u58ee\u6c49\uff0c\u53ea\u5728\u7728\u773c\u4e4b\u95f4\uff0c \u5c31\u53d8\u6210\u4e86\u4e00\u4e2a\u6781\u5176\u8870\u5f31\u3001\u5944\u5944\u4e00\u606f\u7684\u8001\u4eba\u3002\u90a3\u4e2d\u5e74\u7f8e\u5987\u4e5f\u4f3c\u9887\u611f\u610f\u5916\uff0c\u53c8\u53f9\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u8d70 \u5230\u6d1e\u51a5\u5b50\u8eab\u8fb9\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u968f\u5373\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u7f6a\u8fc7\uff0c\u7f6a\u8fc7\uff01\u9053\u53cb\uff0c\u4f60\u597d\u597d\u8d70\u5427\uff01\u201d \u3000\u3000\u6d1e\u51a5\u5b50\u5634\u89d2\u808c\u8089\u62bd\u6410\uff0c\u9690\u7ea6\u73b0\u51fa\u4e00\u79cd\u8be1\u5f02\u7684\u7b11\u5bb9\uff0c\u773c\u775b\u5fae\u5f20\uff0c\u5401\u6c14\u8bf4\u9053\uff1a\u201c\u6298\u5728\u4f60\u7684\u624b \u4e0a\uff0c\u603b\u7b97\u503c\u5f97\u4e86\u3002\u201d\u773c\u76ae\u4e00\u7ffb\uff0c\u5782\u9996\u80f8\u81c6\uff0c\u770b\u60c5\u5f62\u7ade\u662f\u6b7b\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u5168\u573a\u9707\u9a87\uff0c\u4ee5\u6d1e\u51a5\u5b50\u90a3\u62fc\u4e86\u5168\u8eab\u7cbe\u529b\u7684\u4e34\u6b7b\u4e00\u51fb\uff0c\u5373\u7b97\u5192\u5ddd\u751f\u4eb2\u81ea\u51fa\u624b\uff0c \u4e5f\u4e0d\u8fc7\u4ec5\u80fd\u5316\u89e3\uff0c\u800c\u8fd9\u5987\u4eba\u8863\u8896\u4e00\u62c2\uff0c\u5374\u5c31\u80fd\u81f4\u4ed6\u4e8e\u6b7b\uff0c\u795e\u5947\u4e4b\u5904\uff0c\u786e\u662f\u4ee4\u4eba\u96be\u4ee5\u601d\u8bae\uff01\u8fd9 \u65f6\uff0c\u5510\u7ecf\u5927\u521a\u521a\u8ffd\u5230\uff0c\u4ed6\u672c\u6765\u662f\u6765\u6551\u91d1\u4e16\u9057\u7684\uff0c\u54ea\u77e5\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u53d1\u751f\u8bb8\u591a\u53d8\u5316\uff1a\u7f8e\u5987 \u4eba\u6765\u5230\uff0c\u91d1\u4e16\u9057\u9003\u8d70\uff0c\u6d1e\u51a5\u5b50\u8eab\u6b7b\uff0c\u8fd9\u51e0\u4ef6\u4e8b\u5168\u90fd\u51fa\u4eba\u610f\u3002\u5510\u7ecf\u5929\u4e5f\u4e0d\u7981\u6309\u5251\u832b\u7136\uff0c\u4ed6\u521d\u65f6 \u8fd8\u4ee5\u4e3a\u662f\u59e8\u6bcd\u51af\u7433\uff0c\u800c\u4eca\u4e00\u770b\uff0c\u53ea\u89c1\u8fd9\u5987\u4eba\u7aef\u5e84\u6dd1\u79c0\uff0c\u7709\u5b87\u4e4b\u95f4\uff0c\u9690\u9690\u6709\u80a1\u5c0a\u4e25\u7684\u795e\u6c14\uff0c\u4f46 \u9762\u76ee\u6148\u548c\uff1b\u5374\u53c8\u4ee4\u4eba\u611f\u5230\u4eb2\u5207\u3001\u548c\u4ed6\u59e8\u6bcd\u7684\u5b69\u5b50\u6c14\uff0c\u622a\u7136\u4e24\u6837\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u9707\uff0c\u60f3\u9053\uff1a \u201c\u83ab\u975e\u5979\u5c31\u662f\u6211\u7236\u6bcd\u6700\u5c0a\u656c\u7684\u5f53\u4eca\u7b2c\u4e00\u4f4d\u524d\u8f88\u5973\u4fa0\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5192\u5ddd\u751f\u53cc\u624b\u5408\u4ec0\uff0c\u8d70\u4e0b\u8bb2\u575b\uff0c\u606d\u606d\u656c\u656c\u5730\u8fce\u4e0a\u524d\u6765\u3002\u53e3\u5ba3\u4f5b\u53f7\uff0c\u8bf4\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584 \u54c9\uff01\u6d1e\u51a5\u5b50\u5984\u8d77\u65e0\u660e\uff0c\u7ec8\u5f52\u6781\u4e50\u3002\u5973\u4fa0\u9002\u9022\u5176\u4f1a\uff0c\u4e86\u6b64\u56e0\u679c\u3002\u4f55\u987b\u803f\u803f\u4e8e\u5fc3\uff1f\u201d\u7f8e\u5987\u4eba\u8fd8\u4e86 \u4e00\u7a3d\uff0c\u9053\uff1a\u201d\u4e1c\u5e73\u4e00\u4f1a\uff0c\u5306\u5306\u53c8\u5df2\u4e09\u5341\u4f59\u5e74\uff0c\u5192\u8001\u5e08\u529f\u884c\u7cbe\u8fdb\uff0c\u5584\u679c\u53ef\u671f\u3002\u6211\u63a5\u5949\u5927\u672d\uff0c\u7279 \u6765\u9001\u884c\uff0c\u65e0\u610f\u95f4\u7ade\u5f00\u6740\u6212\uff0c\u6d1e\u51a5\u5b50\u867d\u975e\u5168\u7136\u56e0\u6211\u800c\u6b7b\uff0c\u6211\u4e5f\u611f\u6b49\u7136\u5462\uff01\u201d\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u9053\uff1a \u201c\u4e09\u5341\u591a\u5e74\uff0c\u6ca7\u6851\u53d8\u6362\uff0c\u3002\u540e\u8f88\u4e2d\u53c8\u591a\u4e86\u5982\u8bb8\u80fd\u4eba\uff0c\u771f\u53eb\u4eba\u6b22\u559c\u8d5e\u53f9\u3002\u201d\u773c\u5149\u4e00\u8f6c\uff0c\u5bf9\u5510\u7ecf \u5929\u9053\uff1a\u201c\u6653\u6f9c\u662f\u4f60\u4f55\u4eba\uff1f\u201d\u5510\u7ecf\u5929\u9732\u4e86\u4e00\u624b\u8f7b\u529f\uff0c\u5979\u5df2\u77a7\u51fa\u4ed6\u7684\u5e08\u95e8\u5b97\u6d3e\uff0c\u5510\u7ecf\u5929\u4e0d\u7531\u5f97\u5fc3 \u4e2d\u51db\u7136\uff0c\u6599\u60f3\u5979\u5b9a\u7136\u5c31\u662f\u90a3\u4f4d\u524d\u8f88\u5973\u4fa0\u3002\u8dea\u5728\u5730\u4e0a\uff0c\u884c\u4e86\u5927\u793c\uff0c\u8bf4\u9053\uff0c\u201c\u6b63\u662f\u5bb6\u7236\u3002\u8001\u524d\u8f88 \u53ef\u662f\u6c13\u5c71\u7684\u5415\u56db\u5a18\u4e48\uff1f\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u8863\u5e26\u8f7b\u98d8\uff0c\u5510\u7ecf\u592d\u88ab\u4e00\u80a1\u529b\u9053\u6258\u4e86\u8d77\u6765\uff0c\u5415\u56db\u5a18\u53ea\u53d7\u4e86 \u4ed6\u534a\u793c\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u6653\u6f9c\u51af\u745b\u6709\u6b64\u4f73\u513f\uff0c\u53ef\u559c\u53ef\u8d3a\uff01\u5440\uff0c\u5ddd\u751f\u5144\uff0c\u60f3\u4e0d\u5230\u767d\u9a79\u8fc7\u9699\uff0c\u8f6c\u773c \u4e4b\u95f4\uff0c\u54b1\u4eec\u5728\u4e16\u4e0a\u7684\u8001\u670b\u53cb\uff0c\u4e5f\u5c31\u53ea\u5269\u4e0b\u8fd9\u6709\u9650\u51e0\u4eba\u4e86\uff01\u201d \u3000\u3000\u5728\u5ea7\u7684\u5404\u6d3e\u9ad8\u624b\uff0c\u542c\u5f97\u8fd9\u4f4d\u4e2d\u5e74\u7f8e\u5987\u5c31\u662f\u5929\u4e0b\u77e5\u540d\u7684\u5415\u56db\u5a18\uff0c\u65e0\u4e0d\u60ca\u5f02\u3002\u4e00\u4e2a\u4e2a\u90fd\u8083\u7acb \u81f4\u656c\u3002\u8981\u77e5\u8fd9\u5415\u56db\u5a18\u4e43\u662f\u6c5f\u5357\u4e03\u4fa0\u4e2d\u7855\u679c\u4ec5\u5b58\u7684\u4e00\u4eba\uff0c\u4ed6\u6740\u6b7b\u53db\u5f92\u5e08\u5144\u4e86\u56e0\uff0c\u6740\u6b7b\u96cd\u6b63\u7b49 \u4e8b\uff0c\u51e0\u5341\u5e74\u6765\u810d\u7099\u4eba\u53e3\uff0c\u6b66\u6797\u4e2d\u4eba\u4e45\u4e0d\u95fb\u5979\u7684\u4fe1\u606f\uff0c\u90fd\u4ee5\u4e3a\u5979\u5df2\u6b7b\u4e86\uff0c\u54ea\u77e5\u5979\u8fd8\u662f\u5982\u6b64\u5e74 \u9752\u3002\u8bba\u8f88\u4efd\u5979\u548c\u5192\u5ddd\u751f\u3001\u5510\u6653\u6f9c\u662f\u540c\u8f88\uff0c\u8bba\u5e74\u9f84\u5979\u6bd4\u5192\u5ddd\u751f\u5c0f\uff0c\u6bd4\u5510\u6653\u6f9c\u5927\uff0c\u8bba\u58f0\u671b\u5979\u6bd4\u5510 \u6653\u6f9c\u3001\u5192\u5ddd\u751f\u8fd8\u9ad8\uff0c\u4e16\u4e0a\u65e0\u4eba\u53ef\u4e0e\u5e76\u80a9\u3002\u6765\u53c2\u6b64\u95f4\u7ed3\u7f18\u76db\u4f1a\u4e4b\u4eba\uff0c\u5f97\u89c1\u5192\u5ddd\u751f\u5df2\u81ea\u89c9\u7f18\u4efd\u4e0d \u6d45\uff0c\u800c\u4eca\u5f97\u89c1\u5f53\u4e16\u7b2c\u4e00\u4f4d\u524d\u8f88\u5973\u4fa0\u5415\u56db\u5a18\uff0c\u66f4\u662f\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u5404\u4f4d\u4e0d\u5fc5\u62d8\u793c\uff0c\u90fd\u8bf7\u5750\u4e0b\u6765\u5427\u3002\u201d\u5411\u56db\u5ea7\u70b9\u4e86\u70b9\u5934\uff0c\u4e0e\u5192\u5ddd\u751f\u5e76\u80a9\u540c\u4e0a\u5927 \u6bbf\u3002 \u3000\u3000\u4e14\u8bf4\u91d1\u4e16\u9057\u3002\u5510\u7ecf\u5929\u4e00\u8d70\uff0c\u9ec4\u77f3\u9053\u4eba\u72ec\u6218\u51b0\u5ddd\u5929\u5973\uff0c\u6b63\u5360\u4e0a\u98ce\uff0c\u5ffd\u542c\u5f97\u5415\u56db\u5a18\u6765\u5230\uff0c\u9ec4 \u77f3\u9053\u4eba\u5fc3\u5934\u4e00\u9707\uff0c\u62c2\u5c18\u4e3e\u8d77\uff0c\u521a\u521a\u67b6\u5f00\u51b0\u5ddd\u5929\u5973\u7684\u5251\u62db\uff0c\u505c\u5728\u534a\u7a7a\u8e0c\u8e87\u4e0d\u6562\u843d\u4e0b\uff0c\u5415\u56db\u5a18\u8d70 \u8fc7\u4ed6\u4eec\u8eab\u65c1\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u9053\u53cb\u82e6\u5fc3\u8654\u4fee\uff0c\u53c8\u6062\u590d\u4e86\u5cd9\u606b\u4e45\u5df2\u5931\u4f20\u7684\u6b66\u529f\uff0c\u771f\u662f\u53ef\u559c\u53ef\u8d3a \u5440\u3002\u201d\u5415\u56db\u5a18\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u597d\u4f3c\u88ab\u5fae\u98ce\u5439\u62c2\uff0c\u7f15\u7f15\u6563\u5f00\uff0c\u624b\u8155\u4ea6\u5fae\u611f\u9178\u9ebb\uff0c\u62c2 \u5c18\u4e0d\u7531\u81ea\u5df1\u7684\u843d\u4e0b\u3002\u9ec4\u77f3\u9053\u4eba\u5927\u4e3a\u5403\u60ca\uff0c\u5415\u56db\u5a18\u6240\u9732\u7684\u8fd9\u624b\u201c\u4f20\u97f3\u632b\u654c\u201d\u7684\u529f\u592b\uff0c\u4ed6\u4e5f\u53ea\u662f \u4ec5\u66fe\u8033\u95fb\uff0c\u672a\u5c1d\u76ee\u7779\uff0c\u60f3\u4e0d\u5230\u795e\u5999\u5982\u65af\uff01\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6c14\u9981\uff0c\u6025\u5fd9\u65bd\u793c\u9053\uff1a\u201c\u8d2b\u9053\u9ec4\u77f3\u53c2\u89c1\u5415 \u5927\u4fa0\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u6211\u5e08\u95e8\u7d20\u65e0\u6e0a\u6e90\uff0c\u53ea\u80fd\u4ee5\u5e73\u8f88\u53d9\u793c\uff0c\u53c2\u89c1\u90a3\u662f\u4e07\u4e0d\u6562\u5f53\u3002\u201d\u505c\u4e86\u4e00 \u505c\uff0c\u53c8\u9053\uff1a\u201c\u5404\u6d3e\u6b66\u529f\uff0c\u5404\u6709\u64c5\u573a\uff0c\u539f\u4e0d\u5fc5\u901e\u5f3a\u6597\u80dc\uff0c\u5b9a\u8981\u5206\u4e2a\u9ad8\u4e0b\u3002\u201d\u8fd9\u8bdd\u6b63\u8bf4\u4e2d\u9ec4\u77f3\u9053 \u4eba\u7684\u5fc3\u75c5\uff0c\u9ec4\u77f3\u9053\u4eba\u4e0d\u7981\u9762\u7ea2\u8033\u8d64\uff0c\u5782\u9996\u8bf4\u9053\uff1a\u201c\u656c\u8046\u6559\u5bfc\uff0c\u6562\u4e0d\u51db\u4f9d\u3002\u201d\u5415\u56db\u5a18\u7eed\u9053\uff1a \u201c\u6bd4\u5982\u6d1e\u51a5\u5b50\u9053\u53cb\uff0c\u4ee5\u5916\u5bb6\u7684\u4e0a\u4e58\u529f\u592b\u7ec3\u5230\u5185\u5bb6\u7684\u5883\u754c\uff0c\u8fd9\u4e5f\u7b97\u5f97\u5728\u6b66\u5b66\u4e2d\u53e6\u8f9f\u8e48\u5f84\u4e86\u3002\u53ea \u56e0\u5984\u8d77\u65e0\u660e\uff0c\u53cd\u800c\u4ee4\u81ea\u5df1\u51e0\u5341\u5e74\u7684\u82e6\u529f\u4ed8\u8bf8\u6d41\u6c34\uff0c\u8fde\u4f20\u4eba\u4e5f\u6ca1\u6709\u7559\u4e0b\u6765\uff0c\u8fd9\u5c82\u4e0d\u662f\u5927\u4e3a\u53ef \u60dc\u3002\u201d\u9ec4\u77f3\u9053\u4eba\u60ca\u6127\u4ea4\u4f5c\uff0c\u4e0d\u6562\u7b54\u8bdd\uff0c\u53ea\u542c\u5f97\u5415\u56db\u5a18\u53c8\u9053\uff1a\u201c\u6d1e\u51a5\u5b50\u4e43\u6606\u4ed1\u6d3e\u957f\u8001\uff0c\u9057\u4f53\u7406 \u5e94\u5f52\u846c\u6606\u4ed1\uff0c\u9053\u53cb\u4e0e\u4ed6\u4e43\u662f\u77e5\u4ea4\uff0c\u8fd9\u4e8b\u5c31\u62dc\u6258\u4f60\u4e86\u3002\u5bf9\u6606\u4ed1\u95e8\u4e0b\uff0c\u8fd8\u671b\u4f60\u5584\u4e3a\u89e3\u91ca\u5462\u3002\u201d\u9ec4 \u77f3\u9053\u4eba\u9053\uff1a\u201c\u8c22\u5973\u4fa0\u6148\u60b2\uff0c\u4f60\u51c6\u6d1e\u51a5\u9053\u53cb\u9063\u4f53\u5f52\u5c71\uff0c\u6606\u4ed1\u95e8\u4e0b\uff0c\u5df2\u662f\u611f\u6069\u4e0d\u6d45\u3002\u201d\u6309\u6c5f\u6e56\u7684 \u89c4\u77e9\uff0c\u6d1e\u51a5\u5b50\u6311\u8845\uff0c\u8eab\u6b7b\u4ea6\u662f\u81ea\u53d6\u5176\u548e\uff0c\u51c6\u4ed6\u5f52\u4e27\u672c\u4e0a\uff0c\u786e\u4e4e\u662f\u4e2a\u6069\u5178\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u8d70\u5230\u6d1e\u51a5\u5b50\u8eab\u8fb9\uff0c\u53ea\u89c1\u6d1e\u51a5\u5b50\u4ecd\u662f\u76d8\u819d\u8dcc\u5750\uff0c\u59ff\u52bf\u672a\u6539\u3002\u9ec4\u77f3\u9053\u4eba\u8f7b\u89e6\u4ed6\u7684\u8eab \u4f53\uff0c\u6d1e\u51a5\u5b50\u5e94\u624b\u8dcc\u4e0b\uff0c\u6ee1\u5934\u767d\u53d1\uff0c\u7c41\u7c41\u6389\u843d\uff0c\u8eab\u8eaf\u4e5f\u4f3c\u7f29\u5c0f\u4e86\u8bb8\u591a\uff0c\u9053\u888d\u4ea6\u663e\u5f97\u5bbd\u5927\u677e\u9a70\u3002 \u5728\u8fd9\u7247\u523b\u4e4b\u65f6\uff0c\u4ed6\u6b7b\u540e\u7adf\u53d8\u6210\u4e86\u4e2a\u5e72\u67af\u7684\u5c0f\u8001\u5934\u513f\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u960e\u5ea7\u60ca\u5f02\uff01 \u3000\u3000\u539f\u6765\u5185\u529f\u7ec3\u5f97\u6700\u9ad8\u5883\u754c\uff0c\u786e\u6709\u4e00\u79cd\u9a7b\u989c\u4e4b\u672f\uff0c\u4f46\u6709\u9053\u4e4b\u4eba\uff0c\u4e0d\u5728\u4e4e\u5916\u8c8c\u7684\u8870\u8001\u4e0e\u4fca\u6717\uff0c \u5927\u591a\u6570\u4e0d\u613f\u5206\u795e\u7ec3\u8fd9\u79cd\u9a7b\u989c\u672f\uff0c\u50cf\u5192\u5ddd\u751f\u5c31\u662f\u3002\u5415\u56db\u5a18\u662f\u5728\u5e74\u9752\u7684\u65f6\u5019\uff0c\u8d62\u5f97\u6613\u5170\u73e0\u6388\u4ee5 \u201c\u6f5c\u7cbe\u5185\u73b0\u201d\u4e4b\u6cd5\uff0c\u5176\u540e\u5185\u529f\u7cbe\u8fdb\uff0c\u4e0d\u987b\u7740\u6025\uff0c\u4fbf\u5f97\u6c38\u85fb\u9752\u6625\u3002\u6d1e\u51a5\u5b50\u5374\u662f\u8d70\u4eba\u9b54\u9053\uff0c\u65f6\u90aa \u6d3e\u7684\u7531\u5916\u800c\u5185\u7684\u7384\u529f\u4fdd\u6301\u4e0d\u8001\uff0c\u6240\u4ee5\u4e00\u5230\u7cbe\u529b\u75ea\u6563\uff0c\u7acb\u523b\u4fbf\u9732\u51fa\u4ed6\u672c\u6765\u5bff\u6570\u7684\u8870\u8001\u4e4b\u8c8c\uff0c\u800c \u4e14\u6c14\u8840\u8017\u5c3d\uff0c\u8eab\u4f53\u4e5f\u4fbf\u5e72\u67af\uff0c\u5728\u6df1\u901a\u6b66\u5b66\u4e4b\u58eb\u770b\u6765\uff0c\u8fd9\u73b0\u8c61\u662f\u6beb\u4e0d\u8db3\u5f02\u3002\u4f46\u6d1e\u51a5\u5b50\u4e4b\u788e\u7136\u800c \u6b7b\uff0c\u5373\u8fde\u5415\u56db\u5a18\u4ea6\u5c1a\u6709\u6240\u672a\u660e\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u8131\u4e0b\u9053\u888d\uff0c\u5c06\u6d1e\u51a5\u5b50\u7684\u9057\u4f53\u88f9\u597d\uff0c\u5411\u91d1\u5149\u5bfa\u7684\u4e3b\u6301\u91d1\u5149\u957f\u8001\u7a3d\u9996\u8bf4\u9053\uff1a\u201c\u8fd8\u8981 \u501f\u8d35\u5bfa\u7684\u6cd5\u575b\u4e00\u7528\u3002\u201d\u91d1\u5149\u957f\u8001\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u8001\u94a0\u4e5f\u8be5\u66ff\u6d1e\u51a5\u9053\u53cb\u9001\u884c\u3002\u201d\u6cd5\u575b\u4e0e\u5927\u6bbf\u6bd7 \u8fde\uff0c\u5185\u4e2d\u8bbe\u6709\u706b\u846c\u7684\u573a\u6240\uff0c\u539f\u6765\u9ec4\u77f3\u9053\u4eba\u4ee5\u5e26\u7740\u5c38\u4f53\u4e0a\u8def\u4e0d\u4fbf\uff0c\u6545\u6b64\u62df\u3002\u5c06\u6d1e\u51a5\u5b50\u706b\u5316\uff0c\u5c06 \u4ed6\u7684\u9aa8\u7070\u5e26\u56de\u6606\u4ed1\u5c71\u5b89\u846c\u3002\u5415\u56db\u5a18\u5192\u5ddd\u751f\u91d1\u5149\u957f\u8001\u5e26\u4e86\u5510\u7ecf\u5929\u51b0\u5ddd\u5929\u5973\u96f7\u9707\u5b50\u8bf8\u4eba\u90fd\u53bb\u89c2\u793c\u3002 \u3000\u3000\u706b\u5149\u4e2d\u6d1e\u51a5\u5b50\u7684\u9063\u4f53\u6e10\u6e10\u711a\u5316\uff0c\u91d1\u5149\u957f\u8001\u5408\u4ec0\u4e3b\u793c\uff0c\u9053\uff1a\u201c\u5484\uff0c\u5984\u5ff5\u8d2a\u7791\u4e00\u706b\u70e7\uff0c\u56db\u5927 \u7686\u7a7a\u76f8\uff01\u201d\u5192\u5ddd\u751f\u9053\uff1a\u201c\u56db\u5a18\uff0c\u6211\u672c\u6765\u60f3\u8fdf\u51e0\u5929\u624d\u8d70\uff0c\u4f60\u65e2\u7136\u63d0\u65e9\u6765\u4e86\uff0c\u6211\u4e5f\u8be5\u63d0\u65e9\u53bb \u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fdf\u53bb\u65e9\u53bb\uff0c\u90fd\u662f\u4e00\u6837\u3002\u4f60\u7684\u8863\u94b5\u4f20\u4eba\u5df2\u89c5\u597d\u4e86\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4e00 \u51db\uff0c\u6b63\u5728\u7422\u78e8\u4f2f\u4f2f\u4e0e\u5415\u56db\u5a18\u8bf4\u7684\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u53ea\u89c1\u5415\u56db\u5a18\u5982\u6709\u6240\u609f\uff0c\u5df2\u662f\u7b11\u9053\uff1a\u201c\u5979\u7684\u8fbe \u6469\u5251\u6cd5\u5df2\u5c3d\u5f97\u6b66\u5f53\u771f\u4f20\uff0c\u8fd8\u6dfb\u4e86\u4e0d\u5c11\u65b0\u7684\u53d8\u5316\uff0c\u4f60\u51e0\u65f6\u6536\u7684\u5973\u5f1f\u5b50\uff0c\u600e\u4e48\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5 \u9053\u3002\u201d\u5192\u5ddd\u751f\u9053\uff1a\u201c\u51b0\u5a25\uff0c\u4f60\u6765\u89c1\u8fc7\u5415\u5927\u4fa0\uff0c\u4ee5\u540e\u591a\u542c\u5979\u6307\u70b9\u3002\u201d\u7b11\u5bf9\u5415\u56db\u5a18\u9053\uff0c\u201c\u51b0\u5a25\u662f \u6211\u7684\u4f84\u5973\uff0c\u820d\u5f1f\u6d6a\u6e38\u5f02\u56fd\uff0c\u3001\u98d8\u6cca\u7ec8\u751f\uff0c\u6709\u4e86\u6b64\u5973\uff0c\u6b7b\u4e5f\u53ef\u4ee5\u7791\u76ee\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u518d\u65bd\u793c\u53c2 \u89c1\u4e86\u5415\u56db\u5a18\uff0c\u5415\u56db\u5a18\u6478\u5979\u7684\u5934\u9876\u9053\uff1a\u201c\u6709\u6b64\u4f73\u513f\uff0c\u4f60\u4e5f\u53ef\u4ee5\u53bb\u5f97\u5b89\u5fc3\u4e86\u3002\u201d\u96f7\u9707\u5b50\u542c\u5f97\u5927\u4e3a \u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u5e08\u7956\u5728\u91d1\u5149\u5bfa\u4f4f\u5f97\u597d\u597d\u7684\uff0c\u4ed6\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u6b63\u5b9c\u5728\u6b64\u4eab\u4e50\u5929\u5e74\uff0c\u4ed6\u8fd8\u8981\u5230\u54ea \u91cc\u53bb\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u6d1e\u51a5\u5b50\u7684\u9063\u4f53\u5df2\u711a\u5316\u51c0\u5c3d\uff0c\u706b\u5149\u4e2d\u5347\u8d77\u8c13\u733e\u7684\u9ed1\u70df\uff0c\u9690\u9690\u6709\u80a1\u8165\u5473\u3002\u5415\u56db\u5a18 \u9762\u6709\u5f02\u5bb9\uff0c\u5ffd\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\uff0c\u8fd9\u5012\u51fa\u4e4e\u6211\u7684\u610f\u6599\u5462\u3002\u201d\u5192\u5ddd\u751f\u9053\uff1a\u201c\u56db\u5a18\u770b\u51fa\u4ec0\u4e48\u6765 \u4e86\uff1f\u201d\u5415\u56db\u5a18\u56de\u9996\u95ee\u5510\u7ecf\u5929\u9053\uff0c\u201c\u9002\u624d\u4e0e\u6d1e\u51a5\u5b50\u4ea4\u624b\u7684\u90a3\u5c0f\u4f19\u5b50\u662f\u8c01\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4ed6\u540d \u53eb\u91d1\u4e16\u9057\u3002\u6c5f\u6e56\u4e0a\u4eba\u79f0\u6bd2\u624b\u75af\u4e10\u3002\u884c\u4e8b\u53ef\u6709\u70b9\u90aa\u6c14\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f\u90aa\uff1f\u975e\u90aa\uff0c\u975e\u90aa\uff1f\u662f \u90aa\uff1f\u73b0\u5728\u4e5f\u8fd8\u96be\u8bf4\u5462\u3002\u4ed6\u7684\u5e08\u7236\u662f\u6211\u81f3\u4ea4\uff0c\u5f53\u5e74\u5c31\u662f\u7531\u90aa\u5f52\u6b63\u7684\u3002\u201d\u5510\u7ecf\u5929\u76f4\u5230\u73b0\u5728\u8fd8\u672a\u77e5 \u9053\u91d1\u4e16\u9057\u7684\u6765\u5386\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u89c1\u5382\u4ed6\u8eab\u6cd5\u5df2\u81ea\u8d77\u7591\uff0c\u800c \u4eca\u89c1\u4e86\u4ed6\u5728\u6d1e\u51a5\u5b50\u4f53\u5185\u7684\u6bd2\u9488\u5316\u6210\u7684\u9ed1\u6c14\uff0c\u4ed6\u7684\u5e08\u7236\u5fc5\u5b9a\u662f\u6bd2\u9f99\u5c0a\u8005\u4e86\u3002\u201d\u5510\u7ecf\u5929\u548c\u96f7\u9707\u4e8e \u90fd\u4e0d\u7981\u60ca\u8be7\u5931\u58f0\u3002\u4ed6\u4eec\u719f\u77e5\u6b66\u6797\u638c\u6545\uff0c\u5f53\u7136\u77e5\u9053\u6bd2\u9f99\u5c0a\u8005\u662f\u524d\u8f88\u9ad8\u624b\u4e2d\u7684\u7b2c\u4e00\u4e2a\u602a\u4eba\u3002 \u3000\u3000\u5415\u56db\u5a18\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u5947\u602a\u6d1e\u51a5\u9053\u53cb\u4f55\u4ee5\u6321\u4e0d\u4f4f\u6211\u8f7b\u8f7b\u4e00\u62c2\uff0c\u539f\u6765\u4ed6\u662f\u4e2d\u6bd2\u5df2\u6df1\uff0c\u628a \u5168\u8eab\u7cbe\u529b\u90fd\u51dd\u4e8e\u4e00\u5904\uff0c\u62fc\u6b7b\u4e00\u51fb\uff0c\u88ab\u6211\u7684\u771f\u529b\u62c2\u6563\uff0c\u6bd2\u6c14\u53cd\u653b\u5fc3\u810f\uff0c\u6240\u4ee5\u4e00\u4e0b\u5b50\u4fbf\u6b7b\u4e86\u3002\u201d \u96f7\u9707\u5b50\u8bf8\u4eba\u542c\u4e86\uff0c\u90fd\u662f\u5403\u4e00\u5927\u60ca\uff0c\u91d1\u4e16\u9057\u7684\u6697\u5668\u5947\u6bd2\u65e0\u6bd4\uff0c\u90a3\u5df2\u662f\u9a87\u4eba\u542c\u95fb\uff1b\u5415\u56db\u5a18\u8f7b\u8f7b\u4e86 \u62c2\uff0c\u5c31\u80fd\u5c06\u6d1e\u51a5\u5b50\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\u7684\u638c\u529b\u4e00\u4e3e\u51fb\u6563\uff0c\u90a3\u66f4\u662f\u95fb\u6240\u672a\u95fb\u7684\u7edd\u9876\u6b66\u529f\uff01 \u3000\u3000\u5415\u56db\u5a18\u53cc\u6307\u4e00\u5f39\uff0c\u79c0\u7709\u4e00\u8e69\uff0c\u5ffd\u5730\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u53c8\u770b\u4e86\u5510\u7ecf\u5929\u4e00\u773c\u9053\uff1a \u201c\u91d1\u4e16\u9057\u4e5f\u662f\u540e\u8f88\u4e2d\u6709\u6570\u7684\u4eba\u7269\uff0c\u4f60\u4e0e\u4ed6\u4ea4\u60c5\u5982\u4f55\uff1f\u201d\u5510\u7ecf\u5929\u5b9e\u662f\u5bf9\u91d1\u4e16\u9057\u6beb\u65e0\u597d\u611f\uff0c\u4f46\u76f4 \u7b54\u9053\uff1a\u201c\u6211\u5bf9\u4ed6\u53ea\u6709\u601c\u624d\u4e4b\u5ff5\uff0c\u5bf9\u4ed6\u7684\u884c\u5f84\u53ef\u4e0d\u6562\u606d\u7ef4\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u5c31\u884c\u4e86\u3002\u4e16\u4eba\u7686 \u66f0\u6740\uff0c\u543e\u610f\u72ec\u601c\u624d\u3002\u4f55\u51b5\u91d1\u4e16\u9057\u5e76\u6ca1\u6709\u5230\u53ef\u6740\u7684\u5730\u6b65\u3002\u5f53\u5e74\u6211\u6551\u4ed6\u5e08\u7236\u6bd2\u9f99\u5c0a\u8005\u4e4b\u65f6\uff0c\u8fde\u6211 \u7684\u5e08\u5144\u7518\u51e4\u6c60\u90fd\u4e0d\u540c\u610f\uff0c\u540e\u6765\u5927\u5bb6\u8fd8\u662f\u8ba4\u4e3a\u6211\u505a\u5f97\u5bf9\u4e86\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u5934\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f \u91d1\u4e16\u9057\u6709\u751a\u707e\u96be\uff0c\u5f1f\u5b50\u53ef\u6709\u80fd\u5c3d\u529b\u4e4b\u5904\u4e48\uff1f\u201d\u5415\u56db\u5a18\u5fae\u7b11\u9053\uff1a\u201c\u5f85\u54b1\u4eec\u529e\u4e86\u5192\u8001\u5e08\u7684\u5927\u4e8b\uff0c \u6211\u518d\u4e0e\u4f60\u7ec6\u8bf4\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u6697\u6697\u7eb3\u95f7\uff0c\u60f3\u9053\uff1a\u201c\u91d1\u4e16\u9057\u867d\u7136\u4e2d\u4e86\u6d1e\u51a5\u5b50\u4e00\u6293\uff0c\u4f46\u6240\u4f24\u975e \u91cd\uff0c\u4ee5\u4ed6\u529f\u529b\uff0c\u5c3d\u53ef\u81ea\u7597\uff0c\u5415\u56db\u5a18\u7684\u53e3\u6c14\u4f55\u4ee5\u5982\u6b64\u4e25\u91cd\uff1f\u201d \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\u6d1e\u51a5\u5b50\u7684\u9063\u4f53\u5df2\u711a\u5316\u51c0\u5c3d\uff0c\u9ec4\u77f3\u9053\u4eba\u5c06\u4ed6\u7684\u9aa8\u7070\u88c5\u8fdb\u4e00\u4e2a\u7389\u575b\uff0c\u81ea\u5411\u6606\u4ed1\u5c71 \u53bb\u3002\u5192\u5ddd\u751f\u5c06\u4ed6\u9001\u51fa\u5bfa\u95e8\uff0c\u518d\u56de\u5927\u6bbf\u3002 \u3000\u3000\u5927\u6bbf\u4e2d\u5404\u6d3e\u5f1f\u5b50\u606d\u7acb\u8fce\u5019\uff0c\u9759\u5f85\u5192\u5ddd\u751f\u518d\u4e3b\u6301\u201c\u7ed3\u7f18\u76db\u4f1a\u201d\u3002\u5192\u5ddd\u751f\u767b\u575b\u5c06\u672a\u8bb2\u5b8c\u7684\u6613 \u7b4b\u7ecf\u5965\u4e49\u8bb2\u4e86\u4e00\u904d\uff0c\u7aef\u5750\u575b\u4e0a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u673d\u5fb7\u84b2\u80fd\u9c7c\uff0c\u627f\u5404\u6d3e\u540c\u9053\u4e0d\u5f03\uff0c\u63a8\u6211\u4e3b\u6301\u76db \u4f1a\uff0c\u4e09\u5ea6\u7ed3\u7f18\uff0c\u5b9e\u5728\u60ed\u6127\u4e4b\u6781\uff0c\u4e09\u5ea6\u7ed3\u7f18\u4e4b\u4e2d\uff0c\u6211\u773c\u89c1\u65b0\u4eba\u8f88\u51fa\uff0c\u6b66\u5b66\u660c\u660e\uff0c\u4e00\u4ee3\u80dc\u4e8e\u4e00 \u4ee3\uff0c\u6211\u5728\u5927\u60ed\u6127\u4e2d\u4e5f\u6709\u5927\u559c\u60a6\u3002\u4eca\u6b21\u7ed3\u7f18\u76db\u4f1a\uff0c\u5c31\u5230\u6b64\u4e3a\u6b62\u4e86\u3002\u201d\u4f9d\u5f80\u6b21\u4e4b\u4f1a\uff0c\u5192\u5ddd\u751f\u7684\u7ed3 \u7f18\u76db\u4f1a\u6700\u5c11\u4e5f\u6709\u534a\u6708\u4e4b\u4e45\uff0c\u800c\u4eca\u53ea\u4e0d\u8fc7\u4e00\u65e5\uff0c\u5192\u5ddd\u751f\u4fbf\u8bf4\u7ed3\u675f\u3002\u5408\u5ea7\u90fd\u662f\u5927\u4e3a\u60ca\u5947\uff0c\u6709\u4eba\u6b63 \u5f85\u53d1\u95ee\uff0c\u5192\u5ddd\u751f\u53cc\u624b\u4e00\u6309\uff0c\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5404\u6d3e\u6b66\u529f\u90fd\u5404\u6709\u64c5\u573a\uff0c\u5404\u4f4d\u4e5f\u90fd\u662f\u4e00\u65f6\u4fca\u5f66\uff0c\u6b66 \u5b66\u4e4b\u9053\uff0c\u4e00\u7406\u901a\u767e\u7406\u878d\uff0c\u6211\u4eca\u6b21\u7684\u6613\u7b4b\u7ecf\u5965\u4e49\uff0c\u4e43\u662f\u5185\u529f\u4fee\u6301\u7684\u57fa\u672c\u529f\u592b\uff0c\u5404\u4f4d\u4ee5\u672c\u6d3e\u529f\u592b \u53c2\u878d\u6b64\u7406\uff0c\u56de\u53bb\u5411\u672c\u95e8\u957f\u8001\u8bf7\u76ca\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u8001\u673d\u518d\u9976\u820c\u4e86\u3002\u4eca\u6b21\u591a\u8c22\u8bf8\u4f4d\u524d\u6765\uff0c\u8001\u673d\u5012\u662f\u6709 \u70b9\u79c1\u4e8b\uff0c\u8981\u8bf7\u8bf8\u4f4d\u4f5c\u4e2a\u89c1\u8bc1\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u9053\uff1a\u201c\u51b0\u5a25\uff0c\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8d70\u8fd1\u575b\u524d\uff0c\u5192\u5ddd\u751f\u9053\uff1a\u201c\u6211\u6c5e\u4e3a\u6b66\u5f53\u6d3e\u7684\u957f\u8001\uff0c\u8fd9\u51e0\u5341\u5e74\u6765\uff0c\u5374\u53ea\u505a\u4e86\u4e2a\u2018\u81ea \u4e86\u6c49\u2019\uff0c\u5bf9\u672c\u95e8\u5f1f\u5b50\uff0c\u758f\u4e8e\u6559\u5bfc\uff0c\u4ee5\u81f3\u5f04\u5f97\u4eba\u624d\u51cb\u843d\uff0c\u6211\u751a\u662f\u6127\u5bf9\u5217\u4ee3\u7956\u5e08\u3002\u6211\u770b\u4f60\u5fc3\u5730\u7eaf \u826f\uff0c\u6b66\u529f\u4e5f\u5c3d\u5f97\u672c\u95e8\u5fc3\u6cd5\uff0c\u6240\u4ee5\u6211\u4e5f\u4e0d\u907f\u5fcc\u81f3\u4eb2\uff0c\u4eca\u65e5\u6211\u5c06\u8863\u8e7f\u4f20\u4f60\uff0c\u4ee5\u540e\u9886\u5bfc\u540c\u95e8\u4e4b\u8d23\uff0c \u5c31\u5f97\u7531\u4f60\u8d1f\u8d77\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u5979\u6b63\u662f\u8ba8\u538c\u5c18\u4e16\u7684\u7e41\u56a3\uff0c\u4e00\u5fc3\u60f3\u56de\u51b0\u5bab\uff0c\u54ea\u80af\u505a\u4ec0 \u4e48\u638c\u95e8\u3002\u5192\u5ddd\u751f\u4f3c\u662f\u77e5\u6089\u5979\u7684\u5fc3\u610f\uff0c\u9053\uff0c\u201c\u4f60\u4e14\u522b\u5fd9\uff0c\u542c\u6211\u4e00\u4e00\u4ea4\u4ee3\u3002\u201d\u53c8\u5524\u9053\uff1a\u2018\u96f7\u9707 \u5b50\uff0c\u4f60\u8fc7\u6765\uff01\uff0c\u2019\u96f7\u9707\u5b50\u8d70\u5230\u575b\u524d\u65bd\u793c\uff0c\u5192\u5ddd\u751f\u9053\uff1a\u201c\u6b66\u5b66\u4e4b\u9053\uff0c\u6709\u5982\u5927\u6d77\uff0c\u4f60\u4eca\u65e5\u53ec\uff1a\u77e5 \u9053\u4e0d\u8db3\u4e86\u4e48\uff1f\u201d\u96f7\u9707\u5b50\u6ee1\u9762\u7f9e\u60ed\uff0c\u5782\u9996\u7980\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u5192\u5ddd\u751f\u5fae\u7b11\u9053\uff1a\u201c\u2018\u77e5\u9053\u4e86\u5c31\u597d\u4e86\uff0c\u638c\u95e8\u5e08\u5144\u65e5\u524d\u4e0a\u4e66\u7ed9\u6211\uff0c\u8bf4\u662f\u5e74\u8001\u529b\u8870\uff0c\u96be\u4efb\u8270 \u5de8\uff0c\u8bf7\u6211\u53e6\u7acb\u638c\u95e8\uff0c\u6211\u77a7\u4f60\u8fd9\u4e00\u5e74\u591a\u6765\uff0c\u4fee\u517b\u9887\u6709\u8fdb\u76ca\uff0c\u638c\u95e8\u7684\u62c5\u5b50\uff0c\u5c31\u7531\u4f60\u6311\u8d77\u6765\u5427\u3002\u201d \u96f7\u9707\u5b50\u505a\u68a6\u4e5f\u6599\u4e0d\u5230\u5e08\u7956\u6307\u5b9a\u4ed6\u505a\u638c\u95e8\uff0c\u60ca\u559c\u7f9e\u60ed\u4ea4\u5e76\uff0c\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u8fd9\u62c5\u5b50\u5f1f\u5b50\u53ef\u6311\u4e0d \u8d77\u3002\u201d\u773c\u775b\u770b\u7740\u51b0\u5ddd\u5927\u5973\u3002\u5192\u5ddd\u751f\u9053\uff1a\u201c\u80fd\u77e5\u4e0d\u8db3\uff0c\u4fbf\u6311\u5f97\u8d77\u3002\u505a\u638c\u95e8\u7684\u6700\u8981\u7d27\u7684\u662f\u884c\u4e8b\u516c \u5141\uff0c\u8d4f\u7f5a\u5206\u660e\uff0c\u7ea6\u675f\u540c\u95e8\uff0c\u4e0d\u79bb\u4fa0\u4e49\u4e4b\u9053\uff0c\u90a3\u4fbf\u5bf9\u4e86\u3002\u6b66\u529f\u5012\u5728\u5176\u6b21\u3002\u51b0\u5a25\u662f\u6211\u8863\u94b5\u4f20\u4eba\uff0c \u4ee5\u540e\u82e5\u6709\u5173\u672c\u6d3e\u5174\u8870\u7684\u5927\u4e8b\uff0c\u4f60\u51b3\u65ad\u4e0d\u4e0b\u7684\uff0c\u53ef\u4ee5\u53bb\u7980\u544a\u5979\u3002\u201d \u3000\u3000\u5ea7\u4e2d\u5404\u9ad8\u624b\u542c\u4e86\uff0c\u90fd\u662f\u5fc3\u4e2d\u4e00\u51db\u3002\u539f\u6765\u7167\u6b66\u6797\u7684\u89c4\u77e9\uff0c\u6bcf\u6d3e\u4e00\u4e2a\u638c\u95e8\u4eba\u82e5\u8fd8\u6709\u957f\u8f88\u5b58 \u5728\uff0c\u957f\u8f88\u5c31\u662f\u672c\u6d3e\u7684\u957f\u8001\uff0c\u638c\u95e8\u4eba\u78b0\u5230\u5927\u4e8b\u8981\u53d6\u51b3\u4e8e\u957f\u8001\uff0c\u957f\u8001\u4e2d\u7684\u81f3\u5c0a\u7684\u4e4b\u4f4d\u5b9e\u9645\u4ea6\u5373\u7b49 \u4e8e\u592a\u4e0a\u638c\u95e8\uff0c\u4e0d\u8fc7\u4ed6\u4e0d\u7406\u7e41\u6742\u7684\u7410\u4e8b\u7f62\u4e86\u3002\u4ee5\u76ee\u524d\u7684\u6b66\u5f53\u6d3e\u800c\u8bba\uff0c\u5192\u5ddd\u751f\u4e09\u5144\u5f1f\u90fd\u662f\u957f\u8001\uff0c \u4f46\u77f3\u5e7f\u751f\u524d\u51e1\u5e74\u5df2\u6b7b\uff0c\u73b0\u5728\u53c8\u77e5\u6842\u534e\u751f\u4ea6\u65e9\u5df2\u53bb\u4e16\uff0c\u90a3\u5373\u662f\u53ea\u6709\u5192\u5ddd\u751f\u4e00\u4eba\u662f\u592a\u4e0a\u638c\u95e8\u3002\u638c \u95e8\u53ef\u4ee5\u66f4\u6362\uff0c\u957f\u8001\u4e0d\u80fd\u66f4\u6362\uff0c\u9664\u975e\u957f\u8001\u90fd\u6b7b\u4e86\uff0c\u6216\u8005\u662f\u7531\u540c\u95e8\u516c\u63a8\uff0c\u6216\u8005\u662f\u7531\u524d\u4efb\u957f\u8001\u63d0 \u5b9a\uff0c\u624d\u53ef\u4ee5\u4ece\u540c\u8f88\u4e2d\u9009\u51fa\u4e00\u4eba\u4f5c\u4e3a\u672c\u6d3e\u7684\u957f\u8001\uff0c\u4f46\u8fd9\u4eba\u5fc5\u987b\u6b66\u529f\u5fb7\u671b\u90fd\u4e3a\u6b66\u6797\u5404\u6d3e\u94a6\u4f69\u7684\u624d \u884c\uff0c\u6240\u4ee5\u82e5\u7136\u957f\u8001\u90fd\u6b7b\u4e86\uff0c\u4e5f\u53ef\u4ee5\u4e0d\u5fc5\u518d\u63a8\u5b9a\u6216\u6307\u5b9a\u201c\u957f\u8001\u201d\u7684\u3002\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e0b\uff0c\u638c\u95e8\u4eba \u4ea6\u5c31\u662f\u672c\u6d3e\u7684\u81f3\u5c0a\u4e86\u3002\u73b0\u5728\u5192\u5ddd\u751f\u6307\u5b9a\u51b0\u5ddd\u5929\u5973\u662f\u4ed6\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u53c8\u8981\u96f7\u9707\u4e8e\u6709\u5927\u4e8b\u987b\u53d6\u51b3 \u4e8e\u5979\uff0c\u90a3\u5373\u662f\u8bf4\u51b0\u5ddd\u5929\u5973\u4ece\u4eca\u65e5\u8d77\u4fbf\u662f\u6b66\u5f53\u6d3e\u7684\u201c\u957f\u8001\u201d\uff0c\u4ea6\u5373\u201c\u592a\u4e0a\u638c\u95e8\u201d\uff0c\u4f46\u4f9d\u6b66\u6797\u89c4 \u77e9\uff0c\u5192\u5ddd\u535e\u672a\u6b7b\uff0c\u8fd9\u201c\u592a\u4e0a\u638c\u95e8\u201d\u5c82\u80fd\u64c5\u7acb\uff1f\u800c\u4e14\u51b0\u5ddd\u5929\u5973\u53c8\u662f\u8fd9\u6837\u5e74\u9752\uff01\u56e0\u6b64\u4f17\u4eba\u90fd\u89c9\u60ca \u8be7\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5bf9\u8fd9\u4e9b\u89c4\u77e9\u5168\u7136\u4e0d\u61c2\uff0c\u4e00\u542c\u4f2f\u4f2f\u539f\u6765\u5e76\u4e0d\u662f\u8981\u5979\u505a\u638c\u95e8\uff0c\u53ea\u662f\u8981\u5979\u201c\u7ba1\u201d\u96f7\u9707 \u5b50\uff0c\u5979\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u6211\u65f1\u5c31\u66ff\u4f60\u7ba1\u8fc7\u96f7\u9707\u5b50\u5382\uff0c\u8fd9\u5012\u4e0d\u5fc5\u63a8\u8f9e\u3002\u201d\u4e8e\u662f\u6b23\u7136\u70b9\u9996\uff0c\u9053\uff1a \u201c\u542c\u4f2f\u4f2f\u5429\u5490\uff0c\u4f46\u4f84\u5973\u53ef\u4e0d\u6b22\u559c\u5230\u6b66\u5f53\u5c71\u53bb\uff0c\u5c06\u6765\u8fd8\u8981\u56de\u8f6c\u51b0\u5bab\u7684\u3002\u201d\u5192\u5ddd\u751f\u7b11\u9053\uff1a\u201c\u4f60\u5982 \u4eca\u5c31\u662f\u672c\u6d3e\u81f3\u5c0a\uff0c\u4f60\u6b22\u559c\u5230\u54ea\u91cc\u53bb\u5c31\u5230\u54ea\u91cc\u53bb\u3001\u8c01\u4eba\u8fd8\u6765\u7ba1\u4f60\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6014\u4e86\u4e00\u5f81\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u600e\u4e48\u53d8\u6210\u4e86\u672c\u6d3e\u7684\u81f3\u5c0a\u4e86\u3002\u201d\u5ffd\u89c1\u5192\u5ddd\u751f\u7aef\u5750\u575b\u4e0a\uff0c\u95ed \u76ee\u5782\u9996\uff0c\u9762\u4e0a\u5e26\u7740\u6148\u7965\u7684\u7b11\u5bb9\uff0c\u5927\u6bbf\u5185\u6570\u767e\u4eba\u7b49\uff0c\u4e00\u9f50\u8083\u7acb\uff0c\u9e26\u96c0\u65e0\u58f0\uff0c\u5415\u56db\u5a18\u5408\u4ec0\u8d5e\u9053\uff1a \u201c\u5e26\u53d1\u4fee\u6301\u6570\u5341\u5e74\uff0c\u5148\u751f\u5999\u9053\u609f\u4eba\u5929\uff0c\u52d8\u7834\u8272\u7a7a\u65e0\u4e16\u76f8\uff0c\u66f4\u6b23\u8863\u94b5\u6709\u771f\u4f20\uff01\u201d\u91d1\u5149\u5927\u5e08\u4e5f\u8d5e \u9053\uff1a\u201c\u4e86\u65e0\u7275\u6302\u897f\u5f52\u53bb\uff0c\u5c45\u58eb\u5c45\u7136\u83e9\u8428\u884c\uff01\u201d\u96f7\u9707\u5b50\u7387\u9886\u540c\u95e8\uff0c\u4e00\u9f50\u8dea\u4e0b\uff0c\u51b0\u5ddd\u5929\u5973\u60ca\u9053\uff1a \u201c\u6211\u4f2f\u4f2f\u6b7b\u4e86\u4e48\uff1f\u201d\u5415\u56db\u5a18\u5e84\u4e25\u8bf4\u9053\uff1a\u201c\u4f60\u4f2f\u4f2f\u798f\u5bff\u5168\u5f52\uff0c\u5b89\u7136\u5750\u5316\uff0c\u8fd9\u662f\u5c18\u4e16\u95f4\u7f55\u89c1\u7684\u5927 \u559c\u4e8b\uff0c\u4f60\u54ed\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5927\u5973\u4e5f\u66fe\u94bb\u7814\u8fc7\u4f5b\u5bb6\u7684\u9053\u7406\uff0c\u77e5\u9053\u8fd9\u6837\u7684\u5b89\u7136\u5750\u5316\uff0c\u786e\u662f\u4f5b\u95e8\u5f1f\u5b50\u8ba4\u4e3a\u6700\u96be\u6c42\u5f97\u7684 \u4e8b\u60c5\uff0c\u975e\u6709\u9053\u4e4b\u58eb\u83ab\u529e\uff0c\u4f46\u60f3\u8d77\u4ece\u4eca\u4ee5\u540e\uff0c\u81ea\u5df1\u5728\u4e16\u4e0a\u518d\u65e0\u4e00\u4e2a\u4eb2\u4eba\uff0c\u5fc3\u4e2d\u5374\u4e5f\u4e0d\u514d\u6709\u70b9\u96be \u8fc7\u3002\u5f53\u4e0b\u6025\u5fd9\u968f\u4f17\u793c\u8d5e\u3002\u96f7\u9707\u5b50\u7980\u9053\uff1a\u201c\u5415\u5927\u4fa0\uff0c\u6211\u5e08\u7956\u7684\u540e\u4e8b\u8fd8\u8981\u4f60\u8001\u4e3b\u6301\u3002\u201d\u5415\u56db\u5a18\u7b11 \u9053\uff1a\u201c\u6211\u6b64\u6765\u5c31\u662f\u7279\u4e3a\u9001\u4f60\u4eec\u7684\u7956\u5e08\u897f\u5f52\u7684\uff0c\u4ed6\u7684\u540e\u4e8b\uff0c\u6211\u5f53\u7136\u4e49\u4e0d\u5bb9\u8f9e\u3002\u4f46\u6211\u5148\u8981\u548c\u5510\u7ecf \u592d\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u548c\u5510\u7ecf\u5929\u8d70\u8fc7\u4e00\u8fb9\uff0c\u5415\u56db\u5a18\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7ecf\u5929\uff0c\u4f60\u4e0d\u5fc5\u53c2\u52a0\u4e27\u793c\u4e86\u3002\u201d\u5510\u7ecf\u5929 \u9053\uff1a\u201c\u5192\u8001\u524d\u8f88\u662f\u5bb6\u7236\u7684\u77e5\u4ea4\uff0c\u6211\u4e0d\u9001\u4ed6\u4e0b\u571f\uff0c\u5c82\u975e\u4e0d\u8fd1\u60c5\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u8f88\u4f55\u987b\u62d8\u6267 \u4fd7\u793c\uff1f\u6551\u4eba\u201c\u4e00\u547d\uff0c\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\uff0c\u5192\u8001\u524d\u8f88\u77e5\u9053\u4f60\u53bb\u6551\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d\u5510\u7ecf\u5927\u60ca \u9053\uff1a\u201c\u6551\u8c01\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6551\u91d1\u4e16\u9057\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6d1e\u51a5\u5b50\u90a3\u4e00\u6293\u4f3c\u4e4e\u4e5f\u4e0d\u8db3\u81f4\u91d1\u4e16\u9057 \u4e8e\u6b7b\u5440\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e0d\u662f\u6d1e\u51a5\u5b50\u81f4\u4ed6\u4e8e\u6b7b\uff0c\u662f\u4ed6\u81ea\u5df1\u7684\u6b66\u529f\u81f4\u4ed6\u4e8e\u6b7b\u3002\u201d\u5510\u7ecf\u5929\u5982\u5760\u4e94 \u91cc\u96fe\u4e2d\uff0c\u9053\uff0c\u201c\u8fd9\u5f1f\u5b50\u5012\u4e0d\u660e\u767d\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u7684\u6b66\u529f\u662f\u4ed6\u81ea\u5df1\u5728\u8352\u5c9b\u4e2d\u609f\u51fa \u6765\u7684\uff0c\u8352\u5c9b\u4e2d\u9664\u4e86\u6bd2\u86c7\uff0c\u522b\u65e0\u751f\u4eba\uff0c\u52a0\u4e0a\u4ed6\u6124\u4e16\u5ac9\u4fd7\uff0c\u4fee\u7ec3\u5185\u529f\u4e4b\u65f6\uff0c\u80f8\u4e2d\u5145\u6ee1\u4e56\u623e\u4e4b\u6c14\uff0c \u6240\u4ee5\u4ed6\u7684\u5185\u529f\u867d\u7136\u81ea\u6210\u4e00\u5bb6\uff0c\u5965\u5999\u795e\u5947\u4e0d\u5728\u4f60\u6211\u4e24\u6d3e\u4e4b\u4e0b\uff0c\u5374\u975e\u6b63\u9053\u3002\u529f\u592b\u8d8a\u6df1\uff0c\u5185\u9b54\u8d8a\u5389 \u5bb3\uff0c\u636e\u6211\u731c\u6d4b\uff0c\u6bd2\u9f99\u5c0a\u8005\u5fc5\u7136\u662f\u8d70\u706b\u5165\u9b54\u6b7b\u7684\uff0c\u8fd9\u79cd\u5fae\u5999\u7684\u5185\u529f\u53cd\u514b\u4e4b\u7406\uff0c\u53ea\u6015\u4ed6\u8981\u5728\u4e34\u6b7b \u4e4b\u524d\u65b9\u80fd\u660e\u767d\u3002\u91d1\u4e16\u9057\u9053\u884c\u5c1a\u6d45\uff0c\u90a3\u81ea\u7136\u66f4\u4e0d\u660e\u767d\u4e86\u3002\u201d\u8fd9\u79cd\u5185\u9b54\u5916\u9b54\u4e4b\u8bf4\uff0c\u4e43\u662f\u6b66\u5b66\u4e2d\u7684 \u672f\u8bed\uff0c\u542c\u6765\u4f3c\u662f\u795e\u79d8\uff0c\u5176\u5b9e\u4ea6\u975e\u4e0d\u53ef\u89e3\u91ca\uff0c\u90a3\u5c31\u662f\u529f\u592b\u7684\u8fd0\u7528\u4e0d\u4f9d\u6b63\u9053\u6240\u62db\u81f4\u6765\u7684\u9690\u60a3\u800c \u5df2\uff0c\u4ee5\u9e26\u7247\u4f5c\u6bd4\u55bb\uff0c\u9e26\u7247\u672c\u53ef\u6cbb\u75c5\uff0c\u4e5f\u53ef\u7528\u4f5c\u632f\u594b\u7cbe\u795e\uff0c\u4f46\u4e0d\u95f4\u65ad\u7684\u5438\u670d\uff0c\u53cd\u4ee4\u4eba\u7cbe\u795e\u8870 \u9761\uff0c\u65e0\u5f02\u4e8e\u6162\u6027\u81ea\u6740\uff01\u201c\u90aa\u6d3e\u7684\u5185\u529f\u201d\u5373\u7b49\u4e8e\u9e26\u7247\uff0c\u7ec3\u4e4b\u8d8a\u4e45\u5219\u4e2d\u6bd2\u8d8a\u6df1\uff0c\u540c\u4e00\u4e00\u9053\u7406\u3002 \u3000\u3000\u5415\u56db\u5a18\u53c8\u9053\uff1a\u201c\u91d1\u4e16\u9057\u7684\u5185\u529f\u8fd8\u8fdc\u672a\u5230\u8fbe\u4ed6\u5e08\u7236\u7684\u5883\u754c\uff0c\u672c\u4e0d\u4f1a\u8d70\u706b\u5165\u9b54\uff0c\u4f46\u82e5\u4ed6\u4e0d\u81ea \u77e5\u9632\u8303\uff0c\u7ec8\u4e14\u4e00\u65e5\u50cf\u4ed6\u5e08\u7236\u90a3\u6837\u800c\u6b7b\u3002\u201d\u5510\u7ecf\u5929\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u4f55\u5fc5\u8fd9\u6837\u7740\u6025\uff0c\u5c31\u8981\u8d76\u53bb\u6551 \u4ed6\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u672c\u6765\u4ed6\u4e0d\u4f1a\u8fd9\u6837\u7532\u4fbf\u8d70\u706b\u5165\u9b54\uff0c\u4f46\u4ed6\u4e2d\u4e86\u6d1e\u51a5\u5b50\u7684\u9634\u6bd2\u638c\u529b\uff0c\u89e6\u53d1\u5185 \u9b54\uff0c\u7b49\u4e8e\u4e00\u4e2a\u5438\u6bd2\u5df2\u4e45\u7684\u4eba\uff0c\u5ffd\u9047\u5927\u75c5\uff0c\u9690\u6bd2\u53d1\u4f5c\uff0c\u90a3\u81ea\u7136\u62b5\u6321\u4e0d\u4e86\u3002\u6211\u521a\u624d\u66fe\u89c1\u8fc7\u4ed6\u4e0e\u6d1e \u51a5\u5b50\u4ea4\u624b\uff0c\u4ee5\u4ed6\u7684\u529f\u529b\uff0c\u5927\u7ea6\u5728\u4e09\u5341\u516d\u65e5\u4e4b\u5185\uff0c\u5c1a\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u4f60\u8d76\u7d27\u53bb\u627e\u4ed6\uff0c\u5149\u7ed9\u4ed6\u670d\u4e8c \u9897\u7528\u5929\u5c71\u96ea\u83b2\u6240\u5236\u7ec3\u7684\u78a7\u7075\u4e39\uff0c\u53ef\u4ee5\u5ef6\u4ed6\u6027\u547d\u81f3\u4e03\u5341\u4e8c\u5929\u3002\u201d\u5510\u7ecf\u5927\u5927\u9a87\u9053\uff1a\u201c\u5929\u5c71\u96ea\u83b2\u4ea6 \u53ea\u4e0d\u8fc7\u5ef6\u957f\u4e09\u5341\u516d\u65e5\uff01\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u7531\u4e0a\u4e58\u5185\u529f\u800c\u6765\u7684\u90aa\u9b54\u5185\u6bd2\uff0c\u4e16\u95ee\u65e0\u836f\u53ef\u533b\uff0c\u5185\u5929 \u5c71\u96ea\u83b2\u80fd\u5ef6\u957f\u6027\u547d\uff0c\u5df2\u7ecf\u662f\u975e\u5e38\u96be\u5f97\u7684\u4e86\u3002\u201d\u5510\u7ecf\u5929\u5927\u4e3a\u5931\u671b\u9053\uff1a\u201c\u8fd9\u6837\u53ea\u80fd\u6cbb\u6807\uff0c\u4e0d\u80fd\u6cbb \u672c\uff0c\u82df\u5ef6\u6027\u547d\u53c8\u6709\u4f55\u7528\uff1f\u5c82\u4e2a\u662f\u59cb\u7ec8\u4e0d\u80fd\u6551\u4ed6\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e0d\uff0c\u5c31\u4f60\u80fd\u591f\u6551\u4ed6\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f55\u4ee5\u53ea\u662f\u5f1f\u5b50\u80fd\u6551\u4ed6\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5929\u5c71\u6d3e\u7684\u5185\u529f\u81ea\u6666\u660e\u7985\u5e08\u4e00\u8109\u76f8 \u4f20\uff0c\u535a\u91c7\u4f17\u5bb6\u4e4b\u957f\uff0c\u53bb\u829c\u5b58\u9752\uff0c\u6700\u4e3a\u7eaf\u6b63\u6df1\u539a\uff0c\u52a9\u4eba\u89e3\u9664\u56e0\u5185\u529f\u4fee\u7ec3\u4e0d\u5f97\u5176\u5f53\u800c\u751f\u7684\u6bdb\u75c5\uff0c \u975e\u4f60\u4eec\u8fd9\u6d3e\u4e0d\u884c\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5f1f\u5b50\u8fd8\u662f\u4e0d\u61c2\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u529f\u529b\u672a\u5230\uff0c\u81ea\u7136\u8fd8\u672a \u61c2\u5f97\u3002\u4f46\u53ea\u8981\u4f60\u627e\u5230\u91d1\u4e16\u9057\u4e4b\u540e\uff0c\u5e26\u4ed6\u56de\u5929\u5c71\u53bb\u6c42\u4f60\u7684\u7236\u6bcd\u76f8\u6551\uff0c\u5219\u91d1\u4e16\u9057\u4e0d\u4f46\u6027\u547d\u53ef\u4fdd\uff0c \u800c\u5185\u529f\u7531\u90aa\u5f52\u6b63\uff0c\u5bf9\u4ed6\u5927\u6709\u795e\u76ca\uff0c\u5c06\u6765\u7684\u6210\u5c31\u4e0d\u5728\u4f60\u4e0b\u3002\u201d\u5510\u7ecf\u5929\u6c89\u541f\u4e0d\u8bed\uff0c\u5415\u56db\u5a18\u9053\uff1a \u201c\u4f46\u4f60\u81f3\u8fdf\u8981\u5728\u4e09\u5341\u516d\u5929\u4e4b\u5185\u627e\u5230\u4ed6\uff0c\u5728\u4e03\u5341\u4e8c\u5929\u4e4b\u5185\u8981\u4e0e\u4ed6\u540c\u5230\u5929\u5c71\u3002\u201d\u5510\u7ecf\u5929\u5185\u5fc3\u4ea4 \u6218\uff0c\u6b64\u65f6\u5fc3\u610f\u5df2\u51b3\u3001\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u5f1f\u5b50\u9a6c\u4e0a\u52a8\u8eab\u3002\u201d \u3000\u3000\u53ea\u662f\u4ed6\u8d39\u5c3d\u5fc3\u529b\uff0c\u5343\u8f9b\u4e07\u82e6\uff0c\u624d\u80fd\u91cd\u4f1a\u51b0\u5ddd\u5929\u5973\uff0c\u800c\u4eca\u53c8\u8981\u5306\u5306\u5206\u624b\uff0c\u5fc3\u4e2d\u81ea\u662f\u96be\u514d\u4e0d \u820d\u3002\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u4e5f\u6b63\u51dd\u671b\u7740\u4ed6\uff0c\u76ee\u5149\u4e00\u63a5\uff0c\u53c8\u8f6c\u5934\u8fc7\u53bb\u548c\u5e7d\u840d\u8bf4\u8bdd\u4e86\u3002\u5415\u56db\u5a18\u773c \u5149\u4f55\u7b49\u9510\u5229\uff1f\u89c1\u6b64\u60c5\u666f\uff0c\u5df2\u77a7\u6599\u4e86\u51e0\u5206\uff0c\u9053\uff1a\u201c\u51b0\u5a25\uff0c\u4f60\u9001\u4ed6\u4e00\u7a0b\u3002\u201d\u51b0\u5ddd\u5929\u5973\u89c1\u5415\u56db\u5a18\u6709 \u547d\uff0c\u7f13\u7f13\u884c\u6765\uff0c\u5916\u8868\u77dc\u6301\uff0c\u5fc3\u4e2d\u5374\u662f\u6709\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u5e7d\u6028\u548c\u61ca\u607c\uff0c\u5374\u53c8\u4e0d\u6562\u5148\u95ee\u5510\u7ecf\u5929\u56e0\u4f55 \u5306\u5306\u800c\u6765\uff0c\u5306\u5306\u800c\u53bb\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u770b\u91d1\u4e16\u9057\u6b64\u4eba\u51b7\u50b2\u4e4b\u6781\uff0c\u82e5\u7136\u77e5\u9053\u4f60\u662f\u53bb\u6551\u4ed6\uff0c\u6015\u672a\u5fc5\u80af\u53d7\u4f60\u7684\u6069\u60e0\u3002 \u4f60\u5f97\u968f\u673a\u5e94\u53d8\uff0c\u60f3\u4e2a\u6cd5\u5b50\uff0c\u9a97\u4ed6\u548c\u4f60\u540c\u4e0a\u5929\u5c71\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9053\u3002\u201d\u51b0\u5ddd\u5929\u5973\u4ece\u4e24 \u4eba\u7684\u5bf9\u8bdd\u4e2d\uff0c\u624d\u77e5\u9053\u5510\u7ecf\u5929\u662f\u53bb\u6551\u91d1\u4e16\u9057\uff0c\u5fc3\u4e2d\u5927\u662f\u611f\u52a8\u3002 \u3000\u3000\u5415\u56db\u5a18\u8d70\u5f00\uff0c\u81ea\u53bb\u548c\u96f7\u9707\u5b50\u5546\u91cf\u5192\u5ddd\u751f\u7684\u540e\u4e8b\u3002\u51b0\u5ddd\u5929\u5973\u9001\u5510\u7ecf\u5929\u8d70\u51fa\u5bfa\u95e8\uff0c\u4e24\u4eba\u90fd\u9ed8 \u4e0d\u4f5c\u58f0\uff0c\u884c\u4e86\u4e00\u6bb5\u8def\uff0c\u5230\u4e86\u4e0b\u5c71\u7684\u8def\u53e3\uff0c\u5510\u7ecf\u5929\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u8fd8\u6068\u6211\u4e48\uff1f\u201d\u51b0 \u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u6211\u6709\u4ec0\u4e48\u7275\u6d89\uff0c\u6211\u597d\u7aef\u7aef\u6068\u4f60\u4f5c\u4ec0\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u8fd8\u662f\u6068 \u6211\u4e86\u3002\u4e0d\u7ba1\u4f60\u600e\u4e48\u6068\u6211\u4e5f\u597d\uff0c\u6211\u603b\u662f\u60f3\u5ff5\u7740\u4f60\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5ffd\u5730\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u89c1\u4e86\u59b9 \u59b9\uff0c\u53c8\u5fd8\u4e86\u59d0\u59d0\u4e86\u3002\u201d\u5510\u7ecf\u5929\u8fd9\u624d\u77e5\u9053\u5979\u662f\u6000\u7591\u81ea\u5df1\u548c\u90b9\u7edb\u971e\u7684\u4e8b\u60c5\uff0c\u7b11\u9053\uff1a\u201c\u5979\u8fd8\u662f\u4e00\u4e2a \u5b69\u5b50\u5462\u3002\u90a3\u65f6\u6211\u5728\u5979\u5bb6\u91cc\u517b\u4f24\u4e00\u4e8b\u59d4\u5a49\u7684\u89e3\u91ca\u4e86\u4e00\u904d\uff0c\u4e58\u673a\u8868\u767d\u81ea\u5df1\u7684\u5fc3\u66f2\uff0c\u8bf4\u5f97\u6781\u662f\u6e29\u67d4 \u8bda\u631a\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u539f\u6765\u90fd\u662f\u91d1\u4e16\u9057\u6363\u7684\u9b3c\u3002\u201d\u5510\u7ecf\u5929\u8be7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5c06\u91d1\u4e16 \u9057\u9001\u753b\u5f15\u5979\u53bb\u770b\u7b49\u7b49\u4e8b\u60c5\u8bf4\u4e86\uff0c\u5510\u7ecf\u5929\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u771f\u662f\u5c82\u6709\u6b64\u7406\uff1f\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u4f60\u8fd8\u6551\u4ed6\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u76c8\u76c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5c31\u662f\u559c\u6b22 \u2014\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u559c\u6b22\u4ec0\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u672c\u60f3\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u559c\u6b22\u4f60\u8fd9\u6837\u7684\u80f8\u895f\u3002\u201d\u89c1\u5510\u7ecf \u5929\u8ffd\u95ee\uff0c\u5ffd\u611f\u626d\u6ce5\uff0c\u53c8\u662f\u76c8\u76c8\u4e00\u7b11\uff0c\u4e24\u4eba\u4e4b\u95f4\u7684\u8bef\u4f1a\uff0c\u5168\u90fd\u6d88\u89e3\u5728\u8fd9\u76c8\u76c8\u4e00\u7b11\u4e4b\u4e2d\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u65e0\u7aef\u60c5\u6d77\u6ce2\u6f9c\u8d77\uff0c\u5374\u559c\u4e91\u6d88\u96fe\u6563\u65f6\u3002 \u3000\u3000\u6b32\u77e5\u5510\u7ecf\u5929\u662f\u5426\u627e\u5f97\u7740\u91d1\u4e16\u9057\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u77e5\u5df1\u96be\u9022\u3000\u601c\u624d\u60dc\u75af\u4e10\u3000\u6df1\u60c5\u8c01\u8c34\u3000\u5fc6\u65e7\u5ff5\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u516d\u56de \u77e5\u5df1\u96be\u9022\u3000\u601c\u624d\u60dc\u75af\u4e10 \u6df1\u60c5\u8c01\u8c34\u3000\u5fc6\u65e7\u5ff5\u4f73\u4eba \u3000\u3000\u53ef\u662f\u5510\u7ecf\u5929\u5e76\u6ca1\u6709\u627e\u7740\u91d1\u4e16\u9057\u3002\u4ed6\u51e0\u4e4e\u641c\u904d\u4e86\u5ce8\u5d4b\u5c71\uff0c\u90fd\u6ca1\u6709\u53d1\u73b0\u91d1\u4e16\u9057\u7684\u8e2a\u8ff9\uff0c\u53ea\u662f \u5728\u91d1\u5149\u9876\u9644\u8fd1\u7684\u5cf0\u62d7\uff0c\u5c31\u662f\u5728\u76db\u4f1a\u524d\u5915\uff0c\u4ed6\u542c\u5230\u4e00\u4e2a\u5c11\u5973\u7684\u7b11\u58f0\uff0c\u63a5\u5230\u90a3\u5c11\u5973\u63b7\u7ed9\u4ed6\u7684\u82b1 \u73af\uff0c\u4fbf\u5373\u7a81\u7136\u6d88\u5931\u7684\u90a3\u4e2a\u5730\u65b9\uff0c\u53d1\u73b0\u4e86\u51e0\u5757\u7834\u5e03\uff0c\u4f3c\u662f\u4ece\u8863\u88f3\u4e0a\u6495\u4e0b\u6765\u7684\uff0c\u7834\u5e03\u7684\u82b1\u7eb9\u548c\u8272 \u6cfd\uff0c\u90fd\u4f3c\u91d1\u4e16\u9057\u90a3\u65e5\u7a7f\u7684\u8863\u88f3\uff0c\u7834\u5e03\u4e0a\u8fd8\u6709\u70b9\u70b9\u8840\u75d5\uff0c\u9644\u8fd1\u6709\u51cc\u4e71\u7684\u8db3\u5370\uff0c\u53ef\u662f\u518d\u8ffd\u8e2a\u4e0b \u53bb\uff0c\u53c8\u4ec0\u4e48\u90fd\u6ca1\u6709\u53d1\u73b0\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f \u3000\u3000\u91d1\u4e16\u9057\u90a3\u65e5\u5954\u51fa\u5bfa\u95e8\uff0c\u5fc3\u4e2d\u767e\u611f\u5982\u6f6e\uff0c\u60c5\u601d\u6df7\u4e71\uff0c\u51b0\u5ddd\u5929\u5973\u90a3\u542b\u60c5\u8109\u8109\u7684\u773c\u5149\uff0c\u5c1a\u5728\u4ed6 \u8111\u6d77\u4e2d\u7559\u4e0b\u9c9c\u660e\u7684\u5370\u8c61\uff0c\u90a3\u82b1\u6735\u4e00\u822c\u7684\u7b11\u5bb9\uff0c\u7adf\u4f3c\u662f\u6709\u751f\u547d\u7684\u4e1c\u897f\uff0c\u5c31\u8981\u4ece\u8bb0\u5fc6\u4e2d\u8df3\u51fa\u6765\u4f3c \u7684\u3002\u53ef\u60dc\u8fd9\u542b\u60c5\u8109\u8109\u7684\u773c\u5149\u5e76\u4e0d\u662f\u5bf9\u4ed6\u7684\uff0c\u800c\u662f\u5bf9\u5510\u7ecf\u5929\u7684\uff0c\u662f\u5728\u6027\u547d\u76f8\u6251\u3001\u529b\u6297\u5f3a\u654c\u4e4b \u65f6\uff0c\u5979\u8fd9\u6837\u770b\u5510\u7ecf\u5929\u7684\u3002\u51b0\u5ddd\u5929\u5973\u90a3\u82b1\u6735\u4e00\u822c\u7684\u7b11\u5bb9\uff0c\u53d8\u6210\u4e86\u6709\u523a\u7684\u73ab\u7470\u3002\u523a\u75db\u4e86\u4ed6\u7684\u5fc3\u3002 \u91d1\u4e16\u9057\u72c2\u53eb\u9053\uff1a\u201c\u5440\uff0c\u53ea\u8981\u4e16\u4e0a\u6709\u8fd9\u4e48\u4e00\u4e2a\u5973\u5b50\uff0c\u7528\u8fd9\u6837\u7684\u773c\u5149\u5bf9\u6211\u4e00\u77a5\uff0c\u6211\u5c31\u5373\u65f6\u6b7b\u4e86\uff0c \u4e5f\u662f\u5fc3\u7518\uff01\u201d\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u53c8\u60f3\u8d77\u4e86\u5e7d\u840d\u5bf9\u4ed6\u7684\u8bbd\u523a\uff1a\u201c\u765e\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\uff01\u201d\u60f3\u8d77\u4e86\u51b0\u5ddd \u5929\u5973\u5bf9\u4ed6\u7684\u529d\u52c9\uff1a\u201c\u4ee5\u4f60\u7684\u806a\u660e\u624d\u667a\uff0c\u82e5\u7136\u5f52\u5165\u6b63\u9014\uff0c\u53ef\u4ee5\u6210\u4e3a\u4e00\u4ee3\u4fa0\u58eb\uff1b\u518d\u4e0d\u5c31\u662f\u6f5c\u5fc3\u6b66 \u5b66\uff0c\u4e5f\u53ef\u4ee5\u6210\u4e00\u4ee3\u5b97\u5e08\u3002\u600e\u4e48\u4f60\u5374\u6545\u610f\u5c06\u81ea\u5df1\u53d8\u5f97\u8fd9\u822c\u65e0\u8d56\uff1f\u201d\u51b0\u5ddd\u5927\u5973\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u4e5f\u66fe\u6ce8 \u89c6\u8fc7\u4ed6\uff0c\u4f46\u90a3\u662f\u671f\u5f85\u7684\u3001\u601c\u501f\u7684\u3001\u8d23\u5907\u7684\u773c\u5149\uff0c\u548c\u5979\u5bf9\u5510\u7ecf\u5927\u7684\u773c\u5149\uff0c\u7edd\u4e0d\u76f8\u7c7b\u3002\u91d1\u4e16\u9057\u8fd9 \u65f6\u795e\u601d\u6df7\u4e71\uff0c\u4ed6\u6ca1\u6709\u7406\u667a\u53cd\u7701\u81ea\u5df1\uff0c\u6ca1\u6709\u53bb\u60f3\u51b0\u5ddd\u5927\u5973\u90a3\u756a\u8bf4\u8bdd\u4e2d\u5bf9\u4ed6\u6df1\u539a\u7684\u597d\u610f\uff0c\u53ea\u89c9\u5fc3 \u60c5\u6fc0\u8361\uff0c\u96be\u4ee5\u81ea\u4f11\uff0c\u54fa\u54fa\u81ea\u8bed\u9053\uff1a\uff0c\u2018\u6211\u662f\uff1b\u765e\u86e4\u87c6\u5417\uff1f\u6211\u771f\u7684\u5c31\u662f\u8fd9\u6837\u4e00\u4e2a\u4e0d\u6210\u6750\u7684\u4e1c\u897f \u5417\uff1f\u2019\u4ed6\u53c8\u60f3\u8d77\u5510\u7ecf\u5929\u9002\u624d\u5728\u6bbf\u4e2d\u62fc\u6b7b\u6551\u4ed6\u7684\u4e8b\u60c5\uff0c\u5fc3\u4e2d\u53eb\u9053\uff1a\u201c\u4ed6\u624d\u662f\u4e2a\u4fa0\u58eb\uff0c\u6211\u5462\uff0c\u6211 \u53ea\u662f\u51b0\u5ddd\u5929\u5973\u5fc3\u76ee\u4e2d\u7684\u65e0\u8d56\uff01\u201d\u5ffd\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u54fc\uff0c\u7109\u77e5\u4ed6\u4e0d\u662f\u6545\u610f\u505a\u7ed9\u51b0\u5ddd\u5929\u5973\u770b \u7684\uff1f\u6211\u81ea\u51fa\u751f\u4ee5\u6765\uff0c\u4ece\u6765\u5c31\u53ea\u662f\u53d7\u5230\u4e16\u4eba\u7684\u8f7b\u8d31\u3002\u4e16\u95f4\u771f\u6709\u4fa0\u58eb\u8fd9\u79cd\u2018\u4e1c\u897f\u2019\u5417\uff1f\u54c8\uff0c\u54c8\uff0c \u4fa0\u58eb\u53c8\u503c\u591a\u5c11\u94b1\u4e00\u65a4\uff1f\u8981\u77e5\u91d1\u4e16\u9057\u672c\u5c31\u5c5e\u4e8e\u6027\u60c5\u504f\u6fc0\u8fd9\u4e00\u7c7b\u4eba\uff0c\u53d7\u4e86\u6d1e\u51a5\u4e8e\u9634\u6bd2\u7684\u638c\u529b\u540e\uff0c \u795e\u667a\u8ff7\u7cca\uff0c\u8d8a\u53d1\u9b54\u957f\u9053\u6d88\uff0c\u5c24\u5176\u662f\u62ff\u81ea\u5df1\u548c\u5510\u7ecf\u5927\u76f8\u6bd4\u4e4b\u4e0b\uff0c\u81ea\u5351\u81ea\u8d31\u7684\u5fc3\u60c5\u66f4\u4e3a\u6d53\u91cd\uff0c\u795e \u667a\u5373\u7b97\u5076\u4e00\u6e05\u660e\uff0c\u4e5f\u8fc5\u5373\u88ab\u9b54\u969c\u6240\u853d\u3002\u4f46\u89c9\u56db\u6d77\u832b\u832b\uff0c\u5927\u5730\u4e4b\u5927\uff0c\u7adf\u4f3c\u6ca1\u6709\u4e00\u5904\u5730\u65b9\u53ef\u4ee5\u5bb9 \u8eab\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u8ba9\u81ea\u5df1\u5411\u5979\u7ec6\u8bc9\u5fc3\u66f2\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5c31\u5728\u8fd9\u6837\u534a\u75af\u7684\u72b6\u6001\u4e2d\uff0c\u832b\u65e0\u76ee\u7684\u5730\u5728\u5ce8\u5d4b\u5c71\u4e0a\u4e71\u8dd1\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7ecf\u8fc7\u91d1\u5149\u9876\u9644\u8fd1 \u7684\u5cf0\u62d7\uff0c\u5c31\u662f\u4ed6\u521d\u9047\u674e\u6c81\u6885\u7684\u90a3\u4e2a\u5730\u65b9\u3002\u91d1\u4e16\u9057\u5fc3\u5934\u4e00\u89e6\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5c11\u5973 \u201c\u55e4\u201d\u7684\u4e00\u7b11\uff0c\u4ece\u6797\u5b50\u91cc\u8dd1\u51fa\u6765\uff0c\u8fd9\u65f6\u91d1\u4e16\u9057\u795e\u667a\u672a\u6e05\uff0c\u4f46\u89c9\u8fd9\u5c11\u5973\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4e00\u65f6\u95f4\u5374\u672a \u60f3\u8d77\u5979\u5c31\u662f\u66fe\u620f\u5f04\u8fc7\u81ea\u5df1\u7684\u674e\u6c81\u6885\u3002 \u3000\u3000\u674e\u6c81\u6885\u8d70\u51fa\u6765\u65f6\uff0c\u6709\u51e0\u53ea\u7334\u5b50\u4e5f\u8ddf\u7740\u5979\u8e7f\u51fa\u6765\uff0c\u4e00\u89c1\u91d1\u4e16\u9057\u7684\u602a\u76f8\uff0c\u5431\u5431\u4e71\u53eb\uff0c\u90fd\u8dd1\u5f00 \u4e86\u3002\u674e\u6c81\u6885\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4f60\u4e13\u95e8\u6b22\u559c\u6b3a\u8d1f\u4eba\uff0c\u8fde\u7334\u5b50\u4e5f\u6b3a\u8d1f\u3002\u602a\u4e0d\u5f97\u8fde\u755c\u751f \u90e8\u4e0d\u613f\u610f\u548c\u4f60\u4ea4\u670b\u53cb\u3002\u201d\u91d1\u4e16\u9057\u5ffd\u5730\u8bb0\u8d77\u8fd9\u4e2a\u5c11\u5973\u66fe\u5728\u6b64\u5904\u548c\u4ed6\u4ea4\u8fc7\u624b\uff0c\u8fd9\u53e5\u8bdd\u53c8\u5927\u5927\u7684\u523a \u75db\u4e86\u4ed6\uff0c\u4e00\u65f6\u795e\u667a\u8ff7\u7cca\uff0c\u5927\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u5b81\u613f\u4e0e\u755c\u751f\u8981\u597d\uff0c\u4e5f\u4e0d\u613f\u4e0e\u6211\u8981\u597d\uff0c\u6211\u5c31\u6b3a \u8d1f\u4f60\u5566\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u4e0d\u7531\u5206\u8bf4\uff0c\u4e3e\u8d77\u94c1\u62d0\uff0c\u4fbf\u662f\u62e6\u8170\u4e00\u626b\uff0c\u674e\u6c81\u6885\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u672a\u5fc5\u6b3a\u8d1f\u5f97 \u4e86\u6211\uff01\u201d\u91d1\u4e16\u9057\u4e00\u62d0\u626b\u53bb\uff0c\u6253\u4e86\u4e2a\u7a7a\uff0c\u5fc3\u4e2d\u4e00\u614e\uff1a\u600e\u4e48\u8fd9\u5c11\u5973\u7684\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a\uff1f\u8d8a\u53d1\u6fc0\u8d77\u597d \u80dc\u4e4b\u5fc3\u3001\u94c1\u62d0\u4e00\u4e2a\u76d8\u65cb\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u4f46\u89c1\u6756\u5f71\u5982\u5c71\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5c31\u628a\u674e\u6c81\u6885\u7684\u524d\u540e\u5de6\u53f3\u7684\u9000 \u8def\uff0c\u5168\u90fd\u5c01\u4f4f\u3002\u91d1\u4e16\u9057\u8ff7\u4e86\u7406\u667a\uff0c\u62d0\u6cd5\u66f4\u662f\u51cc\u5389\uff0c\u674e\u6c81\u6885\u597d\u751f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u79f0\u4ed6\u6bd2 \u624b\u75af\u4e10\uff0c\u4f46\u4f9d\u6211\u6bcd\u4eb2\u6240\u8bf4\uff0c\u4ed6\u5e76\u4e0d\u662f\u771f\u75af\uff0c\u4e0a\u6b21\u4ed6\u867d\u65e0\u539f\u65e0\u6545\u4e0e\u6211\u52a8\u624b\uff0c\u5374\u4e5f\u770b\u5f97\u51fa\u4ed6\u53ea\u662f \u8bd5\u62db\uff0c\u60f3\u901e\u5f3a\u597d\u80dc\u800c\u5df2\uff0c\u4e3a\u4f55\u4eca\u6b21\u7adf\u4f3c\u610f\u56fe\u62fc\u547d\uff0c\u72b6\u82e5\u771f\u75af\uff1f\u5e78\u597d\u6211\u6bcd\u4eb2\u6559\u4f1a\u4e86\u6211\u5e94\u4ed8\u4ed6\u7684 \u65b9\u6cd5\uff0c\u8981\u4e0d\u7136\u7ed9\u4ed6\u94c1\u62d0\u78b0\u7740\uff0c\u90a3\u5c82\u4e0d\u662f\u7b4b\u65ad\u9aa8\u6298\u4e4b\u7978\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u8fde\u626b\u5341\u51e0\u62d0\uff0c\u6ca1\u6709\u6cbe\u7740\u674e\u6c81\u6885\u7684\u8863\u88f3\uff0c\u54c7\u54c7\u5927\u53eb\uff0c\u62d0\u6cd5\u6742\u4e71\u65e0\u7ae0\uff0c\u53ea\u662f\u72c2\u547c\u4e71 \u626b\uff0c\u674e\u6c81\u6885\u7b11\u9053\uff1a\u201c\u7559\u795e\uff0c\u6211\u8981\u70b9\u4f60\u7684\u7b11\u8170\u7a74\u5566\uff01\u201d\u5728\u6756\u98ce\u4eba\u5f71\u4e4b\u4e2d\uff0c\u6b3a\u8eab\u75be\u8fdb\uff0c\u9a84\u6307\u5982 \u5c31\uff0c\u679c\u7136\u6765\u70b9\u91d1\u4e16\u9057\u7684\u201c\u7b11\u8170\u7a74\u201d\uff0c\u91d1\u4e16\u9057\u6b66\u529f\u672c\u8981\u6bd4\u674e\u6c81\u6094\u9ad8\u5f3a\uff0c\u4f46\u674e\u6c81\u6885\u8fd9\u4e00\u624b\u70b9\u7a74\uff0c \u624b\u6cd5\u8eab\u6cd5\u90fd\u602a\u5f02\u4e4b\u6781\uff0c\u94c1\u62d0\u7adf\u7136\u62e6\u6321\u4e0d\u4f4f\uff0c\u6b66\u529f\u9ad8\u5f3a\u4e4b\u58eb\uff0c\u4e34\u5371\u4e4b\u9645\uff0c\u5e38\u4f1a\u65e0\u610f\u4e2d\u4fbf\u51fa\u7edd \u62db\uff0c\u91d1\u4e16\u9057\u795e\u667a\u867d\u7136\u660f\u8ff7\uff0c\u672c\u80fd\u8fd8\u5728\uff0c\u94c1\u62d0\u652f\u5730\uff0c\u5ffd\u7684\u4e00\u4e2a\u7b4b\u6597\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u674e \u6c81\u4fae\u5403\u4e86\u4e00\u60ca\uff0c\u8033\u8fb9\u542c\u5f97\u6bcd\u4eb2\u8bf4\u9053\uff1a\u201c\uff0c\u70b9\u4ed6\u98ce\u5e9c\u7a74\uff01\u201d\u91d1\u4e16\u9057\u4e00\u62d0\u6253\u53bb\uff0c\u674e\u6c81\u6885\u5df2\u5230\u4e86\u4ed6 \u7684\u4fa7\u8fb9\uff0c\u91d1\u4e16\u9057\u53c8\u4e00\u4e2a\u7b4b\u6597\u7ffb\u5f00\uff0c\u4e24\u4eba\u4f7f\u7684\u90fd\u662f\u602a\u62db\uff0c\u674e\u6c81\u6885\u5fc3\u4e2d\u6674\u53eb\u4e00\u60ed\u6127\u201d\uff0c\u60f3\u9053\uff1a \u201c\u6bcd\u4eb2\u548c\u6211\u62c6\u4e86\u4e09\u5929\uff0c\u6211\u8fd8\u662f\u51e0\u4e4e\u5e94\u4ed8\u4e0d\u4e86\u3002\u201d\u91d1\u4e16\u9057\u66f4\u662f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u7684\u70b9\u7a74\u6cd5 \u600e\u4e48\u5982\u6b64\u602a\u5f02\uff1f\u6211\u5012\u8981\u7528\u672c\u95e8\u7684\u70b9\u7a74\u6cd5\u7ed9\u5979\u4e00\u4e2a\u5389\u5bb3\uff01\u201d\u4f46\u674e\u6c81\u6094\u8feb\u5f97\u6781\u7d27\uff0c\u91d1\u4e16\u9057\u7adf\u7f13\u4e0d \u51fa\u624b\u6765\uff0c\u5fc3\u4e2d\u53c8\u60f3\u9053\uff1a\u201c\u90a3\u51fa\u58f0\u7684\u5973\u5b50\u53c8\u662f\u4f55\u4eba\uff1f\u600e\u4e48\u6211\u770b\u4e0d\u89c1\u5979\u5462\uff1f\u201d\u4ed6\u600e\u77e5\u9053\u90a3\u662f\u51af\u7433 \u5728\u6797\u5b50\u91cc\u7528\u7684\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u91d1\u4e16\u9057\u5927\u7ffb\u7b4b\u6597\uff0c\u8eb2\u907f\u674e\u6c81\u6885\u7684\u70b9\u7a74\uff0c\u6e10\u89c9\u6c14\u5598\uff0c\u674e\u6c81 \u6885\u67d4\u58f0\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u6b3a\u8d1f\u4e0d\u4e86\u6211\uff0c\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u5417\uff1f\u4f60\u7d2f\u5566\uff0c\u4e5f\u8be5\u6b47\u6b47\u5566\u3002\u201d\u5ffd\u542c\u5f97\u91d1\u4e16\u9057 \u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u51af\u7433\u53eb\u9053\uff1a\u201c\u6885\u513f\uff0c\u5feb\u9000\uff01\u201d\u674e\u6c81\u6885\u521a\u4e00\u95ea\u8eab\uff0c\u773c\u775b\u4e00\u82b1\uff0c\u811a\u8ddf\u4e00\u8f6f\uff0c\u5ffd\u7684 \u5012\u5730\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u91d1\u4e16\u9057\u795e\u667a\u5ffd\u5730\u6e05\u9192\uff0c\u60f3\u8d77\u4e86\u674e\u6c81\u6885\u662f\u8fd9\u4e16\u754c\u4e0a\u7b2c\u4e8c\u4e2a\u5c06\u4ed6\u5f53\u4f5c\u670b\u53cb\u7684\u4eba \uff08\u7b2c\u4e00\u4e2a\u662f\u51b0\u5ddd\u5929\u5973\uff09\uff0c\u5fc3\u4e2d\u5927\u6094\uff0c\u4ed6\u51fa\u9053\u4ee5\u6765\uff0c\u867d\u662f\u6e38\u620f\u98ce\u5c18\uff0c\u4e13\u5411\u6210\u540d\u4eba\u7269\u6311\u8845\uff0c\u5374\u4ece \u672a\u6740\u5bb3\u65e0\u8f9c\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u5374\u6740\u4e86\u4e2a\u5c06\u4ed6\u5f53\u4f5c\u670b\u53cb\u7684\u5c11\u5973\u3002\u4ed6\u81ea\u6094\u81ea\u6068\uff0c\u5934\u8111\u660f\u4e71\uff0c\u8ff7\u832b\u4e2d\u4e0d \u81ea\u89c9\u7684\u8dea\u5728\u5730\u4e0a\u5408\u4ec0\u5fcf\u6094\u3002 \u3000\u3000\u8981\u77e5\u91d1\u4e16\u9057\u6240\u55b7\u7684\u6bd2\u9f99\u9488\u5267\u6bd2\u65e0\u6bd4\uff0c\u8fde\u6d1e\u51a5\u5b50\u90a3\u4e48\u9ad8\u7684\u529f\u529b\u4e5f\u7981\u53d7\u4e0d\u8d77\uff0c\u4f55\u51b5\u662f\u674e\u6c81\u6885 \u8fd9\u6837\u4e00\u4e2a\u7a1a\u6c14\u672a\u6d88\u7684\u5c11\u5973\uff1f\u6545\u6b64\u91d1\u4e16\u9057\u795e\u667a\u4e00\u6e05\u4fbf\u6094\u6068\u4ea4\u5e76\uff0c\u8dea\u5728\u5730\u4e0a\uff0c\u5408\u4ec0\u4edf\u6094\uff0c\u4e0d\u6562\u62ac \u8d77\u5934\u6765\uff0c\u751f\u6015\u770b\u5230\u674e\u6c81\u6885\u6323\u624e\u7684\u75db\u82e6\u773c\u5149\u3002\u5374\u4e0d\u6599\u6b63\u5728\u4ed6\u81ea\u6094\u81ea\u8d23\uff0c\u5fc3\u4e2d\u8ff7\u4e71\u5df2\u6781\u4e4b\u9645\uff0c\u5ffd \u542c\u5f97\u674e\u6c81\u6885\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u6211\u53c8\u4e0d\u662f\u4f60\u7684\u5a18\u8001\u5b50\uff0c\u4f60\u5e72\u561b\u8981\u8dea\u6211\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u8fd9\u4e00\u60ca\u7aef\u7684\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u78891\u8d77\u6765\uff0c\u53ea\u89c1\u674e\u6c81\u6094\u7b11\u8bed\u76c8\u76c8\uff0c\u5c31\u7ad9\u5728\u81ea\u5df1\u7684\u9762\u524d\uff0c \u8fd9\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\uff0c\u91d1\u4e16\u9057\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff01\u5ffd\u89c1\u674e\u6c81\u6885\u7eb5\u8eab\u4e00\u8dc3\uff0c\u563b\u563b\u7b11\u9053\uff1a \u201c\u6211\u8fd8\u8981\u9886\u6559\u4f60\u7684\u70b9\u7a74\u6cd5\uff01\u201d\u9a88\u6307\u4e00\u70b9\uff0c\u91d1\u4e16\u9057\u672c\u80fd\u7684\u51fa\u6307\u53cd\u70b9\uff0c\u4ee5\u70b9\u7a74\u5236\u70b9\u7a74\uff0c\u5374\u4e0d\u6599\u674e \u6c81\u6885\u7684\u70b9\u7a74\u624b\u6cd5\u602a\u5f02\u4e4b\u6781\uff0c\u91d1\u4e16\u9057\u7684\u6307\u70b9\u5c1a\u672a\u6cbe\u5230\u5979\u7684\u8863\u88f3\uff0c\u5374\u5df2\u88ab\u5979\u5728\u8170\u95f4\u6233\u4e86\u4e00\u4e0b\uff0c\u91d1 \u4e16\u9057\u767b\u65f6\u624b\u821e\u8db3\u8e48\uff0c\u5927\u58f0\u72c2\u7b11\u8d77\u6765\u3002 \u3000\u3000\u674e\u6c81\u6094\u5f00\u5fc3\u4e4b\u6781\uff0c\u5728\u65c1\u8fb9\u987f\u8db3\u62cd\u624b\uff0c\u597d\u50cf\u5c0f\u5b69\u5b50\u5728\u770b\u800d\u628a\u620f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u4ee5\u5176 \u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u770b\u4f60\u4ee5\u540e\u8fd8\u6562\u80e1\u4e71\u6349\u5f04\u4eba\u4e48\uff1f\u201d\u53c8\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5988\uff0c\u4f60\u5feb\u51fa\u6765\u770b\uff0c\u4f60 \u6559\u7684\u70b9\u7a74\u6cd5\u771f\u884c\uff0c\u4ed6\u73b0\u5728\u5df2\u53d8\u6210\u6211\u624b\u4e2d\u5fc3\u7684\u7334\u513f\u5566\uff0c\u771f\u597d\u73a9\u5440\u771f\u597d\u73a9\uff01\u201d\u539f\u6765\u51af\u7433\u5728\u6797\u5b50\u91cc \u548c\u5973\u513f\u7ec3\u4e86\u4e09\u5929\uff0c\u6240\u7ec3\u7684\u5c31\u662f\u514b\u5236\u91d1\u4e16\u9057\u7684\u70b9\u7a74\u6cd5\uff0c\u4e5f\u6b63\u662f\u5192\u5ddd\u751f\u95f4\u63a5\u6559\u7ed9\u5510\u7ecf\u5927\u7684\u70b9\u7a74 \u6cd5\uff0c\u4e0d\u8fc7\u5192\u5ddd\u751f\u89c1\u4e86\u91d1\u4e16\u9057\u7684\u6b66\u529f\u4e4b\u540e\uff0c\u7528\u4e0d\u5230\u534a\u665a\u7684\u529f\u592b\uff0c\u5c31\u60f3\u51fa\u4e86\u514b\u5236\u4e4b\u9053\uff0c\u800c\u51af\u7433\u5374 \u8981\u60f3\u4e86\u4e24\u5929\uff0c\u4e24\u4eba\u6240\u7814\u7a76\u7684\u7ed3\u679c\uff0c\u6240\u521b\u7684\u70b9\u7a74\u6cd5\u4e0d\u8c0b\u800c\u5408\uff0c\u4e5f\u53ef\u89c1\u5230\u4e0a\u4e58\u7684\u6b66\u529f\u591a\u662f\u6b8a\u9014\u540c \u5f52\u3002 \u3000\u3000\u674e\u6c81\u6885\u62cd\u638c\u8df3\u8dc3\uff0c\u5ffd\u89c1\u91d1\u4e16\u9057\u795e\u8272\u4e0d\u5bf9\uff0c\u773c\u9732\u51f6\u5149\uff0c\u4e0e\u4e00\u822c\u4eba\u88ab\u70b9\u4e86\u201c\u7b11\u8170\u7a74\u201d\u5e94\u6709\u7684 \u73b0\u8c61\u4e0d\u5927\u76f8\u540c\uff0c\u4e0d\u81ea\u89c9\u7684\u6b62\u4e86\u7b11\u58f0\u3002\u51af\u7433\u8d70\u51fa\u6797\u5b50\uff0c\u53ea\u77a5\u4e86\u4e00\u773c\uff0c\u5c31\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9 \u662f\u5373\u5c06\u8d70\u706b\u5165\u9b54\u4e4b\u8c61\u201d\u6025\u5fd9\u5c06\u91d1\u4e16\u9057\u62c9\u8fc7\u6765\uff0c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u5168\u4e16\u9057\u7528\u529b\u4e00\u8df3\uff0c\u51af\u7433\u65e9\u5df2\u9632 \u53ca\uff0c\u5de6\u624b\u6309\u7740\u4ed6\u7684\u592a\u9633\u5c11\u9634\u7ecf\u8109\u4ea4\u4f1a\u4e4b\u5904\uff0c\u91d1\u4e16\u9057\u53ea\u89c9\u4e00\u80a1\u51c9\u6c14\u597d\u50cf\u6162\u6162\u7684\u94bb\u4eba\u4f53\u4e2d\u3002\u5fc3\u5934 \u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff0c\u773c\u76ae\u95ed\u5408\uff0c\u53c8\u89c9\u5f97\u597d\u4f3c\u5b69\u63d0\u65f6\u5019\uff0c\u6bcd\u4eb2\u5728\u7528\u624b\u62cd\u4ed6\u54c4\u4ed6\u7761\u89c9\u4e00\u6837\uff0c\u4e0d\u4e45\u5c31 \u7761\u7740\u4e86\u3002 \u3000\u3000\u51af\u7433\u6240\u5b66\u7684\u529f\u592b\u751a\u6742\uff0c\u8fd9\u6b21\u5979\u662f\u7528\u897f\u85cf\u7ea2\u6559\u7684\u201c\u6f5c\u5fc3\u9b54\u800c\u5f52\u771f\u201d\u7684\u529f\u592b\uff0c\u5927\u8017\u672c\u8eab\u7684\u529f \u529b\uff0c\u8d39\u4e86\u4e00\u652f\u9999\u7684\u65f6\u523b\u624d\u628a\u91d1\u4e16\u9057\u4f53\u5185\u9006\u884c\u6df7\u4e71\u7684\u771f\u6c14\u6536\u675f\uff0c\u4f7f\u5b83\u91cd\u5f52\u5e73\u9759\u3002\u8fd9\u65f6\u51af\u7433\u5df2\u77e5 \u9053\u91d1\u4e16\u9057\u7684\u5185\u529f\u8def\u5b50\u4e0d\u5bf9\uff0c\u4f46\u8fd8\u672a\u77e5\u5176\u6240\u4ee5\u7136\uff0c\u5230\u6495\u5f00\u4e86\u91d1\u4e16\u9057\u7684\u80f8\u8863\u4e00\u770b\uff0c\u5bdf\u770b\u4e86\u6d1e\u51a5\u5b50 \u7ed9\u4ed6\u7684\u6293\u4f24\uff0c\u77e5\u9053\u4e86\u6240\u4ee5\u7136\uff0c\u5374\u4e0d\u77e5\u7528\u4f55\u6cd5\u53ef\u4ee5\u6839\u6cbb\uff0c\u5bf9\u5973\u513f\u53f9\u6c14\u9053\uff1a\u201c\u8fd9\u4eba\u6240\u4fee\u7ec3\u7684\u5185 \u529f\uff0c\u4e0e\u4efb\u4f55\u4e00\u6d3e\u90fd\u4e0d\u76f8\u540c\uff0c\u8fdb\u5883\u6700\u901f\uff0c\u4f46\u6f5c\u4f0f\u7684\u9690\u60a3\u4ea6\u6700\u5927\uff0c\u6211\u7528\u6f5c\u5fc3\u9b54\u800c\u5f52\u771f\u7684\u529f\u592b\u4e5f\u53ea \u80fd\u4fdd\u4ed6\u4e03\u5341\u4e8c\u5929\uff0c\u65e0\u6cd5\u6551\u5f97\u4ed6\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u674e\u6c81\u6885\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u662f\u597d\uff1f\u201d\u51af\u7433\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5c06\u4ed6\u5e26\u56de\u5929\u5c71\u53bb\uff0c\u4f60\u7684\u59e8\u7236\u59e8 \u6bcd\u662f\u5929\u4e0b\u5185\u5bb6\u7684\u6b63\u5b97\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u6709\u6cd5\u5b50\u6cbb\u3002\u4f55\u51b5\u4ed6\u7684\u5e08\u95e8\u6765\u5386\uff0c\u54b1\u4eec\u53c8\u77e5\u9053\u4e86\uff0c\u8bf4\u6765\u4ed6\u7684\u5e08 \u7236\u548c\u4f60\u7684\u59e8\u7236\u59e8\u6bcd\u5927\u6709\u6e0a\u6e90\u6ce5\u3002\u201d\u674e\u6c81\u6885\u6b63\u60f3\u95ee\u6bcd\u4eb2\u4f55\u4ee5\u5ffd\u7136\u77e5\u9053\u4e86\u91d1\u4e16\u9057\u7684\u5e08\u95e8\u6765\u5386\uff0c\u53ea \u89c1\u91d1\u4e16\u9057\u5df2\u7f13\u7f13\u5f20\u5f00\u4e86\u773c\u775b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u597d\u4f3c\u4ece\u4e00\u4e2a\u7f8e\u5999\u7684\u68a6\u4e2d\u9192\u6765\uff0c\u5f20\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u9664\u4e86\u674e\u6c81\u6885\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e2d\u5e74\u5987 \u4eba\u6b63\u4f4e\u7740\u5934\u770b\u4ed6\u3002\u8fd9\u5987\u4eba\u9762\u8c8c\u4e0e\u674e\u6c81\u6094\u76f8\u4f3c\uff0c\u5934\u4e0a\u6253\u7740\u4e24\u4e2a\u8774\u8776\u7ed3\uff0c\u7b11\u563b\u563b\u7684\u663e\u5f97\u5341\u5206\u6dd8 \u6c14\u3002\u91d1\u4e16\u9057\u7741\u5927\u773c\u775b\uff0c\u5bf9\u7740\u674e\u6c81\u6885\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4f60\u4e2d\u4e86\u6211\u7684\u6bd2\u9488\uff0c\u600e\u4e48\u8fd8\u80fd\u6d3b \u7740\uff1f\u5979\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u51af\u7433\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u5f92\u5f1f\u5417\uff1f\u201d\u91d1\u4e16\u9057\u7ffb\u8eab\u5750\u8d77\uff0c\u8be7\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u65e0\u4eba\u77e5 \u9053\u6211\u7684\u6765\u5386\uff0c\u4f60\u600e\u751f\u6653\u5f97\u6211\u6069\u5e08\u7684\u540d\u5b57\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u95ee\u6211\u662f\u8c01\uff0c\u51ed\u4f60\u6240\u7528\u7684\u6bd2\u9488\uff0c \u9664\u4e86\u6bd2\u9f99\u5c0a\u8005\u4e4b\u5916\uff0c\u65e0\u4eba\u6709\u6b64\u6697\u5668\u3002\u4f60\u8fd9\u79cd\u6bd2\u9f99\u9488\uff0c\u53ea\u6709\u7528\u732b\u9e70\u7684\u53e3\u6d8e\u6ce1\u5236\u6210\u7684\u4e38\u836f\u624d\u53ef\u4ee5 \u89e3\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f46\u4e5f\u5fc5\u987b\u7acb\u65f6\u541e\u670d\uff0c\u800c\u4e14\u4ea6\u4e0d\u80fd\u6d88\u5f97\u5982\u662f\u4e4b\u5feb\uff1a\u518d\u8bf4 \u8fd9\u89e3\u836f\u5929\u4e0b\u65e0\u4eba\u85cf\u6709\uff0c\u8fde\u6211\u81ea\u5df1\u4e5f\u6ca1\u6709\u4e86\uff0c\u4f60\u53c8\u4ece\u4f55\u53d6\u5f97\uff1f\u201d\u539f\u6765\u91d1\u4e16\u9057\u6240\u85cf\u7684\u89e3\u836f\uff0c\u5728\u4ed6 \u521d\u5165\u5ce8\u5d4b\u5c71\u4e4b\u591c\uff0c\u56e0\u4e3a\u4ed6\u53d7\u4e86\u5e7d\u840d\u8bf4\u8bdd\u7684\u523a\u6fc0\uff0c\u5728\u5c71\u4e0a\u6253\u6eda\u3002\u53c8\u81ea\u5df1\u6495\u7834\u8863\u88f3\uff0c\u8df3\u4e0b\u5c71\u6da7\u6d17 \u6fa1\uff0c\u6de1\u832b\u4e4b\u4e2d\uff0c\u89e3\u836f\u88ab\u7011\u5e03\u51b2\u53bb\uff0c\u9192\u6765\u4e4b\u540e\uff0c\u6094\u5df2\u65e0\u53ca\u3002 \u3000\u3000\u51af\u7433\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u7684\u89e3\u836f\u6bd4\u4f60\u7684\u8fd8\u5f3a\u5462\uff01\u201d\u53d6\u51fa\u4e00\u4e2a\u7ea2\u8272\u7684\u836f\u7403\uff0c\u8fce\u98ce\u4e00\u6643\uff0c\u4e00\u80a1\u836f \u5473\uff0c\u51b2\u8fdb\u91d1\u4e16\u9057\u7684\u9f3b\u5b54\uff0c\u91d1\u4e16\u9057\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6709\u8fd9\u4e2a\u5b9d\u8d1d\uff1f\u5509\uff0c\u96be\u9053\u4f60\u662f\u6211\u6069\u5e08\u7684\u597d \u53cb\uff1f\u4f60\u662f\u5415\u56db\u5a18\u5417\uff1f\u201d\u51af\u7433\u53ea\u662f\u563b\u563b\u7684\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53ea\u77e5\u9053\u4e00\u4e2a\u5415\u56db\u5a18\u53f6\u539f\u6765\u5979\u8fd9\u4e2a\u836f \u7403\u4e43\u662f\u5979\u7684\u59d0\u59d0\u51af\u6f20\u4ea4\u7ed9\u5979\u7684\uff0c\u51af\u6f20\u5f97\u81ea\u732b\u9e70\u5c9b\u7684\u4e3b\u4eba\u8428\u5929\u523a\uff0c\u6bd4\u6bd2\u9f99\u5c0a\u8005\u7684\u89e3\u836f\u66f4\u4e3a\u6709\u6548\u3002 \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u5462\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6b7b\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u5440\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\u3002\u201d\u91d1\u4e16 \u9057\u542c\u5979\u60cb\u501f\u81ea\u5df1\u7684\u5e08\u7236\u4e4b\u6b7b\uff0c\u5fc3\u4e2d\u5927\u662f\u611f\u6fc0\uff0c\u60f3\u9053\uff1a\u201c\u5979\u5373\u7b97\u4e0d\u662f\u5415\u56db\u5a18\u4e5f\u5fc5\u7136\u662f\u6211\u5e08\u7236\u7684 \u597d\u53cb\u3002\u201d\u5bf9\u51af\u7433\u7684\u597d\u611f\u6cb9\u7136\u800c\u751f\u3002\u51af\u7433\u9053\u201c\u4f60\u518d\u9759\u5750\u8fd0\u6c14\u770b\u770b\u5982\u4f55\uff1f\u201d\u91d1\u4e16\u9057\u76d8\u819d\u4e00\u5750\uff0c\u521a \u4e00\u5410\u7eb3\uff0c\u4fbf\u89c9\u6d4a\u6c14\u4e0a\u5347\uff0c\u51af\u7433\u5c06\u624b\u638c\u8f7b\u629a\u4ed6\u7684\u80cc\u5fc3\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u53ef\u77e5\u9053\u4f60\u6027\u547d\u4e4b\u5fe7\u4e86 \u4e48\uff1f\u201d\u91d1\u4e16\u9057\u53ea\u89c9\u4e00\u80a1\u51c9\u6c14\u76f4\u900f\u5fc3\u5934\uff0c\u5c31\u50cf\u9002\u624d\u7684\u611f\u89c9\u4e00\u822c\uff0c\u660f\u660f\u601d\u7761\u3002\u51af\u7433\u5728\u4ed6\u989d\u89d2\u5f39\u4e86 \u4e24\u5f39\uff0c\u624b\u638c\u79fb\u5f00\uff0c\u91d1\u4e16\u9057\u53c8\u6e05\u9192\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e00\u7ec3\u5185\u529f\uff0c\u4fbf\u751f\u5f02\u8c61\uff0c\u8fd9\u4e43\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u4ed6\u6b66\u529f\u5df2\u6709\u76f8\u5f53\u9020\u8be3\uff0c\u81ea\u7136\u77e5\u9053\u8fd9 \u662f\u5fc3\u9b54\u53cd\u514b\u4e4b\u5146\uff0c\u51af\u7433\u6240\u8bf4\uff0c\u7edd\u975e\u606b\u5413\u4e4b\u8f9e\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u53cd\u800c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u697c\u8681\u96be\u4fdd\u671d\u5915\uff0c \u8783\u59d1\u4e0d\u77e5\u6625\u79cb\uff0c\u6211\u82df\u6d3b\u4eba\u95f4\u4e8c\u5341\u5e74\uff0c\u6bd4\u8d77\u6765\u4e5f\u4e0d\u7b97\u77ed\u5bff\u4e86\u3002\u53cd\u6b63\u4e16\u4e0a\u4eba\u4eba\u90fd\u8ba8\u538c\u6211\uff0c\u6211\u65e9\u6b7b \u4e86\u4e5f\u53ef\u4ee4\u4ed6\u4eec\u773c\u4e2d\u5e72\u51c0\uff01\u201d \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u600e\u89c1\u5f97\u4eba\u5165\u90fd\u8ba8\u538c\u4f60\uff1f\u82e5\u7136\u662f\u6211\uff0c\u6211\u80fd\u591f\u6d3b\u591a\u4e00\u5927\u4fbf\u8981\u6d3b\u591a\u4e00\u5929\u3002\u8fd9\u4e16\u754c \u82b1\u82b1\u7eff\u7eff\uff0c\u591a\u4e48\u597d\u73a9\uff1f\u201d\u624b\u638c\u5728\u91d1\u4e16\u9057\u80cc\u5fc3\u8f7b\u8f7b\u6eda\u8f6c\uff0c\u91d1\u4e16\u9057\u53ea\u89c9\u5fc3\u4e2d\u70e6\u8e81\u987f\u6d88\uff0c\u547c\u5438\u987a \u7545\uff0c\u77e5\u9053\u51af\u7433\u6b63\u4ee5\u4e0a\u4e58\u5185\u529f\uff0c\u52a9\u81ea\u5df1\u6536\u655b\u4f53\u5185\u9006\u884c\u7684\u771f\u6c14\uff0c\u5fc3\u4e2d\u5927\u662f\u611f\u6fc0\uff0c\u60f3\u9053\uff1a\u201c\u5979\u4e0e\u6211 \u65e0\u4eb2\u65e0\u6545\uff0c\u5374\u80af\u8017\u5e9f\u529f\u529b\u52a9\u6211\uff0c\u679c\u7136\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u8ba8\u538c\u6211\u7684\u3002\u201d\u51af\u7433\u53c8\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4f60\u8fd8 \u613f\u610f\u6b7b\u5417\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5b9a\u8981\u6551\u6211\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u6b22\u559c\u4eba\u4eba\u90fd\u5f88\u5feb\u4e50\uff0c\u82e5 \u89c1\u5230\u4f60\u4f18\u751f\u6101\u6b7b\uff0c\u6211\u5fc3\u91cc\u5c31\u4e0d\u8212\u670d\u4e86\uff0c\u6240\u4ee5\u6211\u6551\u4f60\uff0c\u5b9e\u5728\u662f\u4e3a\u4e86\u6211\u81ea\u5df1\u7684\u5feb\u4e50\u3002\u5582\uff0c\u4f60\u8ddf\u6211 \u8d70\u5427\uff0c\u6211\u7eb5\u4e0d\u80fd\u4fdd\u4f60\u957f\u547d\u767e\u5c81\uff0c\u4e5f\u53ef\u4ee4\u4f60\u5bff\u8fc7\u82b1\u7532\u3002\u8fd9\u4e16\u754c\u597d\u73a9\u7684\u4e8b\u60c5\u591a\u7740\u5462\uff0c\u4f60\u5c31\u662f\u4e0d\u61c2 \u5f97\u73a9\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e00\u751f\u6e38\u620f\u4eba\u95f4\uff0c\u563b\u7b11\u6012\u9a82\uff0c\u65e0\u5904\u4e0d\u662f\u73a9\u4e16\u4e0d\u606d\uff0c\u800c\u4eca\u542c\u5f97\u51af\u7433\u8bf4\u4ed6\u4e0d\u61c2\u5f97\u73a9\uff0c\u6014 \u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5012\u5f88\u6709\u8da3\uff0c\u597d\u5440\uff0c\u6211\u73b0\u5728\u4e0d\u613f\u6b7b\u4e86\uff0c\u5c31\u8ddf\u4f60\u53bb\u73a9\u73a9\u3002\u4f60\u8981\u5e26\u6211\u5230\u54ea\u513f \u53bb\uff1f\u201d\u51af\u7433\u9053\uff0c\u201c\u8bf4\u7ed9\u4f60\u542c\uff0c\u5c31\u4e0d\u597d\u73a9\u4e86\u3002\u201d\u91d1\u4e16\u9057\u4e0e\u5979\u6bcd\u5973\u5927\u662f\u6295\u7f18\uff0c\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\uff0c \u90a3\u4e48\u54b1\u4eec\u5c31\u8d70\u3002\u201d \u3000\u3000\u4e09\u4eba\u5373\u65e5\u79bb\u5f00\u4e86\u5ce8\u5d4b\u5c71\uff0c\u53d6\u9053\u5ddd\u5317\uff0c\u7a7f\u8fc7\u5927\u96ea\u5c71\u3001\u5b81\u9759\u5c71\u3001\u5230\u8fbe\u524d\u85cf\uff0c\u51c6\u5907\u4ece\u897f\u85cf\u800c\u81f3 \u56de\u7586\u3002\u8fd9\u4e09\u4eba\u6027\u60c5\u76f8\u8fd1\uff0c\u8c08\u8c08\u7b11\u7b11\uff0c\u563b\u563b\u54c8\u54c8\uff0c\u5012\u4e0d\u5bc2\u5bde\u3002\u53ea\u662f\u51af\u7433\u603b\u4e0d\u80af\u900f\u9732\u81ea\u5df1\u7684\u8eab \u4efd\uff0c\u4e5f\u4e0d\u80af\u8bf4\u660e\u8981\u5e26\u4ed6\u5230\u4ec0\u4e48\u5730\u65b9\u3002\u91d1\u4e16\u9057\u5f97\u5979\u4ee5\u897f\u85cf\u7ea2\u6559\u7684\u201c\u6f5c\u5fc3\u9b54\uff0c\uff0c\u5185\u529f\u76f8\u52a9\uff0c\u795e\u667a \u6e05\u660e\uff0c\u75f4\u766b\u4e4b\u6c14\u51cf\u4e86\u4e0d\u5c11\uff0c\u900f\u9732\u51fa\u5c11\u5e74\u4eba\u7684\u6d3b\u6cfc\u5929\u771f\uff0c\u4e0e\u674e\u6c81\u6885\u5c24\u5176\u76f8\u5f97\u3002 \u3000\u3000\u4ed6\u4eec\u4e09\u4eba\u90fd\u662f\u7edd\u9876\u7684\u8f7b\u529f\uff0c\u4ece\u5ce8\u5d4b\u5c71\u8d70\u5230\u897f\u85cf\uff0c\u53ea\u4e0d\u8fc7\u82b1\u4e86\u4e8c\u5341\u591a\u5929\u7684\u65f6\u95f4\uff0c\u8fd9\u4e00\u65e5\u4ed6 \u4eec\u8d70\u51fa\u5510\u53e4\u62c9\u5c71\u5c71\u53e3\uff0c\u53ea\u89c1\u4e0b\u9762\u5c71\u8c37\uff0c\u6709\u4e00\u961f\u4eba\u5a49\u8712\u7ecf\u8fc7\uff0c\u884c\u5217\u524d\u9762\u662f\u516b\u5934\u767d\u8c61\uff0c\u8c61\u961f\u4e2d\u6709 \u91d1\u5e62\u5b9d\u76d6\uff0c\u751a\u662f\u5e84\u4e25\u3002\u674e\u6c81\u6885\u7ae5\u5fc3\u5927\u8d77\uff0c\u9053\uff1a\u201c\u5988\uff0c\u4f60\u770b\uff0c\u8fd9\u662f\u85e9\u738b\u51fa\u5de1\u5417\uff1f\u201d\u51af\u7433\u770b\u4e86\u4e00 \u4f1a\uff0c\u9053\uff1a\u201c\u85e9\u738b\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u6c14\u6d3e\u3002\u597d\u50cf\u662f\u54ea\u4e00\u6d3e\u5587\u561b\u7684\u6559\u4e3b\u3002\u54c8\uff0c\u8fd9\u5012\u597d\u73a9\u5f97\u5f88\uff0c\u5f85\u6211\u53bb \u6253\u542c\u6253\u542c\u3002\u201d\u51af\u7433\u8eab\u5f62\u4e00\u6643\uff0c\u7acb\u523b\u63a0\u51fa\u4e86\u5341\u4f59\u4e08\u5730\uff0c\u5728\u534a\u5c71\u5761\u5904\u4f20\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5343\u4e07\u4e0d\u8981 \u8d70\u5f00\u3002\u82e5\u771f\u6709\u4ec0\u4e48\u597d\u73a9\u7684\u4e8b\u513f\uff0c\u6211\u518d\u56de\u6765\u540c\u4f60\u4eec\u53bb\u77a7\u70ed\u95f9\u3002\uff0c\u201d\u6d3b\u58f0\u8bf4\u5b8c\uff0c\u4eba\u5f71\u500f\u7136\u4e0d\u89c1\uff0c \u91d1\u4e16\u9057\u5927\u662f\u4f69\u670d\u3002\u4ed6\u5374\u4e0d\u77e5\u9053\u51af\u7433\u8fd9\u4e00\u79bb\u5f00\u5927\u6709\u6df1\u610f\uff0c\u51af\u7433\u559c\u6b22\u70ed\u95f9\uff0c\u56fa\u7136\u662f\u4e00\u4e2a\u539f\u56e0\uff1b\u53e6 \u4e00\u4e2a\u539f\u56e0\uff0c\u5374\u662f\u85c9\u6b64\u673a\u4f1a\u8ba9\u91d1\u4e16\u9057\u591a\u548c\u5979\u7684\u5973\u513f\u4eb2\u8fd1\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9001\u76ee\u9001\u51af\u7433\u7684\u80cc\u5f71\u5189\u5189\u800c\u6ca1\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u6709\u8da3\u7684\u6bcd\u4eb2\uff0c\u771f\u597d\u798f\u6c14\uff01\u201d \u674e\u6c81\u6885\u9053\uff1a\u201c\u4f60\u7684\u6bcd\u4eb2\u5462\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u662f\u65e0\u7236\u65e0\u6bcd\u7684\u5b64\u513f\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u5440\uff0c\u771f\u53ef \u601c\uff01\u201d\u91d1\u4e16\u9057\u9762\u8272\u4e00\u53d8\uff0c\u6e29\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4eba\u53ef\u601c\uff01\u201d\u674e\u6c81\u6885\u966a\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u9519\u4e86\uff0c\u4f60\u522b\u89c1 \u602a\u3002\u4f60\u662f\u4e2a\u72ec\u6765\u72ec\u5f80\u7684\u5947\u7537\u5b50\u3002\u201d\u674e\u6c81\u6885\u672c\u6765\u4e5f\u6781\u4efb\u6027\uff0c\u4f46\u78b0\u5230\u50cf\u91d1\u4e16\u9057\u8fd9\u6837\u6bd4\u5979\u66f4\u4efb\u6027\u7684 \u7537\u5b50\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u53cd\u800c\u6837\u6837\u8fc1\u5c31\u91d1\u4e16\u9057\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u542c\u5979\u4e00\u8d5e\uff0c\u8f6c\u6012\u4e3a\u559c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u89c1\u8fc7\u50cf\u4f60\u4eec\u6bcd\u5973\u8fd9\u6837\u5947\u602a\u7684\u4eba\u3002\u4f60\u7684\u6bcd \u4eb2\u771f\u597d\uff0c\u53c8\u6709\u672c\u4e8b\uff0c\u53c8\u597d\u73a9\u3002\u201d\u674e\u6c81\u6885\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u5417\uff1f\u50bb\u54e5\u54e5\uff0c\u5176\u5b9e\u4f60\u4e5f\u53ef\u4ee5 \u5f53\u5979\u662f\u4f60\u7684\u6bcd\u4eb2\uff0c\u5979\u75bc\u4f60\u6bd4\u75bc\u6211\u66f4\u751a\u5462\u3002\u201d\u91d1\u4e16\u9057\u7b2c\u4e00\u6b21\u542c\u5230\u6709\u4eba\u8fd9\u6837\u4eb2\u5a9b\u7684\u53eb\u4ed6\u505a\u201c\u50bb\u54e5 \u54e5\u201d\uff0c\u5fc3\u4e2d\u751c\u4e1d\u4e1d\u7684\u6781\u4e3a\u8212\u670d\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7728\u7728\u773c\u775b\uff0c\u5fc3\u4e2d\u5ffd\u7136\u4e00\u8df3\uff0c\u95f4\u9053\uff1a\u201c\u4f60\u5988\u5988\u4e3a\u4ec0\u4e48\u5bf9\u6211\u8fd9\u6837\u597d\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a \u201c\u5979\u8bf4\u4f60\u6ca1\u4eba\u7167\u987e\uff0c\u5230\u5904\u6d41\u6d6a\uff0c\u6b63\u548c\u5979\u7684\u8eab\u4e16\u76f8\u540c\u3002\u201d\u91d1\u4e16\u9057\u9053\uff0c\u201c\u4f60\u5988\u4e5f\u662f\u81ea\u5c0f\u6ca1\u4e86\u7239\u5a18 \u7684\u5417\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u55ef\uff0c\u542c\u8bf4\u5979\u5468\u5c81\u4e4b\u65f6\uff0c\u5bb6\u4e2d\u4fbf\u906d\u6a2a\u7978\uff0c\u6211\u7684\u5916\u7956\u7236\u5f53\u573a\u8eab\u6b7b\uff0c\u8fc7\u4e86\u5dee \u4e0d\u591a\u4e8c\u5341\u5e74\uff0c\u5916\u7956\u6bcd\u624d\u78b0\u89c1\u6211\u7684\u6bcd\u4eb2\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u7684\u6bcd\u4eb2\u4e0d\u662f\u5415\u56db\u5a18\u4e86\u3002\u201d\u4ed6\u7684 \u5e08\u7236\u6bd2\u9f99\u5c0a\u8005\u6700\u4f69\u670d\u5415\u56db\u5a18\uff0c\u66fe\u5bf9\u4ed6\u8bf4\u8fc7\u5415\u56db\u5a18\u7684\u8eab\u4e16\uff0c\u5415\u56db\u5a18\u7684\u7956\u7236\u5415\u7559\u826f\u662f\u4e00\u4ee3\u5927\u5112\uff0c \u7236\u4eb2\u867d\u7136\u4e5f\u662f\u906d\u53d7\u6e05\u5ef7\u6740\u622e\uff0c\u5374\u662f\u5979\u4e8c\u5341\u591a\u5c81\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u674e\u6c81\u6885\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u7684\u6bcd\u4eb2\u662f\u5415\u56db\u5a18\u5462\uff0c\u4f60\u600e\u4e48\u8001\u662f\u4ee5\u4e3a\u6211\u7684\u6bcd\u4eb2\u662f\u5415\u56db\u5a18\uff1f\u201d\u91d1\u4e16\u9057 \u9053\uff1a\u201c\u5979\u662f\u8fd9\u4e48\u597d\u7684\u6b66\u529f\uff0c\u600e\u4e0d\u4ee4\u4eba\u7591\u5fc3\u5979\u662f\u5415\u56db\u5a18\uff1f\u201d\u674e\u6c81\u6885\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e95\u5e95\u4e4b\u86d9\uff0c \u55ef\uff0c\u6211\u53c8\u9a82\u4f60\u4e86\uff0c\u4f60\u522b\u751f\u6c14\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u9a82\uff0c\u6211\u5012\u5f88\u670d\u8d34\u3002\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u4e16\u4e0a \u539f\u6765\u6709\u8fd9\u4e48\u591a\u80fd\u4eba\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u8bf4\u5b9e\u5728\u7684\uff0c\u6211\u6bcd\u4eb2\u7684\u672c\u9886\u5927\u7ea6\u8fd8\u4e0d\u53ca\u5415\u56db\u5a18\uff0c\u4e0d\u8fc7\u5979\u4eec \u5f53\u5e74\u5012\u662f\u5e76\u9e33\u9f50\u9a71\u7684\u6c5f\u6e56\u4e09\u5973\u4fa0\uff0c\u201d\u91d1\u4e16\u9057\u5927\u611f\u5174\u8da3\uff0c\u9053\uff1a\u201c\u54ea\u4e09\u4f4d\u5973\u4fa0\uff1f\u201d\u674e\u6c81\u6885\u9053\uff0c \u201c\u8fd8\u6709\u4e00\u4f4d\u662f\u6211\u7684\u59e8\u6bcd\uff0c\u5979\u7684\u672c\u4e8b\u6bd4\u6211\u7684\u6bcd\u4eb2\u8fd8\u5f3a\u3002\u201d\u6211\u7684\u59e8\u7236\u867d\u8bf4\u662f\u5927\u5c71\u6d3e\u7684\u638c\u95e8\uff0c\u4f46\u5165 \u95e8\u5374\u5728\u6211\u59e8\u6bcd\u4e4b\u540e\uff0c\u6211\u7684\u59e8\u6bcd\u662f\u5f53\u5e74\u5929\u5c71\u4e03\u5251\u4e4b\u4e00\u7684\u6613\u5170\u73e0\u5973\u4fa0\u7684\u8863\u94b5\u4f20\u4eba\uff01\u201d\u674e\u6c81\u6885\u5c0f\u5b69 \u5fc3\u6027\uff0c\u5938\u8000\u59e8\u6bcd\uff0c\u5fc3\u4e2d\u751a\u611f\u9a84\u505a\u3002\u91d1\u4e16\u9057\u9762\u8272\u4e00\u6c89\uff0c\u95ee\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u4f60\u7684\u59e8\u7236\u662f\u5929\u5c71\u6d3e\u7684 \u638c\u95e8\uff0c\u90a3\u4e48\u4f60\u7684\u59e8\u7236\u662f\u5510\u6653\u6f9c\u4e86\uff1f\u201d\u674e\u6c81\u6094\u8fd8\u6ca1\u6709\u7559\u610f\u4ed6\u7684\u9762\u8272\uff0c\u51b2\u53e3\u7b54\u9053\uff1a\u201c\u4e0d\u9519\u3002\u539f\u6765 \u4f60\u4e5f\u77e5\u9053\u6211\u59e8\u7236\u7684\u540d\u5b57\u3002\u6211\u6bcd\u4eb2\u5c31\u662f\u60f3\u5e26\u4f60\u4e0a\u5929\u5c71\uff0c\u8bf7\u6211\u59e8\u7236\u59e8\u6bcd\u6551\u4f60\u5462\uff01\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u91d1\u4e16\u9057\u7684\u5fc3\u5934\u53c8\u9178\u53c8\u82e6\uff0c\u9762\u8272\u6da8\u7ea2\uff0c\u4ed6\u4e45\u5df2\u6a2a\u4e98\u80f8\u4e2d\u7684\u7591\u95ee\u4e5f\u4e00\u4e00\u89e3\u5f00\u4e86\u3002 \u4ed6\u73b0\u5728\u5df2\u77e5\u9053\u4e86\u81ea\u5df1\u7684\u5185\u529f\u8def\u5b50\u4e0d\u5bf9\uff0c\u90a3\u4e48\u5f53\u5e74\u81ea\u5df1\u7684\u5e08\u7236\u4e4b\u6b7b\uff0c\u81ea\u662f\u7531\u4e8e\u8d70\u706b\u5165\u9b54\u65e0\u7591\uff1b \u800c\u5e08\u7236\u7684\u9057\u8a00\uff0c\u529d\u4ed6\u53bb\u627e\u5929\u5c71\u6d3e\u7684\u4eba\uff0c\u539f\u6765\u5c31\u662f\u60f3\u5929\u5c71\u6d3e\u7684\u4eba\u6551\u4ed6\uff0c\u4ee5\u514d\u4ed6\u91cd\u8e48\u81ea\u5df1\u7684\u8986\u8f99\uff01 \u3000\u3000\u91d1\u4e16\u9057\u6027\u60c5\u504f\u6fc0\uff0c\u6781\u5ea6\u7684\u81ea\u5351\uff0c\u4e5f\u6781\u5ea6\u7684\u81ea\u5c0a\u3002\u4ed6\u53c8\u4e00\u5411\u4ee5\u4e3a\u672c\u6d3e\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u8981\u4ed6 \u5411\u4efb\u4f55\u4eba\u4f4e\u5934\uff0c\u90fd\u662f\u96be\u4ee5\u5fcd\u53d7\u7684\u4e8b\u3002\u4f55\u51b5\u662f\u5411\u5510\u7ecf\u5927\u7684\u7236\u4eb2\uff1f\u5411\u81ea\u5df1\u66fe\u8f83\u91cf\u8fc7\u51e0\u6b21\u7684\u5510\u7ecf\u5929 \u7684\u7236\u4eb2\u3002\u674e\u6c81\u6885\u8fd9\u65f6\u5df2\u53d1\u89c9\u4ed6\u7684\u9762\u8272\u4e0d\u5bf9\uff0c\u5f3a\u7b11\u95ee\u9053\uff1a\u201c\u50bb\u54e5\u54e5\uff0c\u4f60\u53c8\u60f3\u4ec0\u4e48\u4e86\uff1f\u201d\u91d1\u4e16\u9057 \u5fcd\u6c14\u95ee\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5510\u7ecf\u5929\u662f\u4f60\u7684\u8868\u5144\u4e86\uff1f\u201d\u674e\u6c81\u6885\u559c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u539f\u6765\u4f60\u4eec\u662f\u65e9\u5c31\u8ba4 \u8bc6\u7684\u5417\uff1f\u201d\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u6b62\u8ba4\u8bc6\uff0c\u8fd8\u662f\u597d\u670b\u53cb\u5462\uff01\u201d\u5fc3\u4e2d\u5374\u5728\u81ea\u601d\uff1a\u201c\u539f\u6765\u5979\u7684\u6bcd\u4eb2 \u5c31\u662f\u5510\u7ecf\u5929\u7684\u59e8\u6bcd\uff0c\u6211\u9053\u5979\u6709\u8fd9\u6837\u597d\u5fc3\uff0c\u539f\u6765\u662f\u60f3\u85c9\u6b64\u673a\u4f1a\uff0c\u53eb\u5510\u7ecf\u5929\u7684\u7236\u4eb2\u5411\u6211\u5e02\u60e0\uff0c\u53eb \u6211\u4ece\u6b64\u5728\u5510\u7ecf\u5929\u7684\u9762\u524d\u6c38\u62ac\u4e0d\u8d77\u5934\u6765\uff01\u201d\u4ed6\u628a\u51af\u7433\u7684\u597d\u610f\u5168\u5f80\u574f\u5904\u60f3\uff0c\u970e\u65f6\u95f4\u70ed\u8840\u4e0a\u6d8c\uff0c\u53ea \u89c9\u5f97\u81ea\u5df1\u5b64\u82e6\u4f36\u4f55\uff0c\u5230\u5904\u53d7\u4eba\u620f\u4fae\uff0c\u771f\u4e0d\u5982\u4efb\u7531\u547d\u8fd0\u652f\u914d\uff0c\u771f\u4e2a\u6b7b\u4e86\u5012\u4e5f\u5e72\u51c0\uff01 \u3000\u3000\u674e\u6c81\u4fae\u54ea\u91cc\u77e5\u9053\u8fd9\u4e00\u77ac\u95f4\uff0c\u91d1\u4e16\u9057\u7684\u601d\u60f3\u5c31\u6709\u4e86\u8fd9\u4e48\u5927\u7684\u53d8\u5316\uff0c\u62cd\u624b\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765 \u4f60\u4eec\u8fd8\u662f\u597d\u670b\u53cb\uff0c\u90a3\u771f\u662f\u5999\u6781\u5566\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u5999\u6781\u5566\uff0c\u4f60\u4eec\u5b89\u6392\u5f97\u771f\u5999\uff01\u4f60\u8fc7 \u6765\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u4e0d\u8212\u670d\u4e48\uff1f\u8ba9\u6211\u770b\u770b\u662f\u4e0d\u662f\u53d1\u70e7\uff1f\u201d\u5979\u89c1\u91d1\u4e16\u9057\u9762\u8272\u6da8\u7ea2\uff0c\u8fd8\u4ee5 \u4e3a\u4ed6\u70ed\u6c14\u4e0a\u5347\uff0c\u8d70\u8fd1\u4e24\u6b65\uff0c\u91d1\u4e16\u9057\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4fe9\u6bcd\u5973\u7684\u5b89\u6392\uff0c\u771f\u5999\u6781 \u5566\uff01\u201d\u7a81\u7136\u4f38\u6307\u4e00\u6233\uff0c\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u5927\u51fa\u674e\u6c81\u6885\u7684\u610f\u6599\u4e4b\u5916\uff0c\u6b32\u907f\u65e0\u4ece\uff0c\u5495\u54af\u4e00\u58f0\uff0c\u4ec6\u5012\u5730 \u4e0a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u7684\u602a\u7b11\u4e4b\u58f0\u5728\u5c71\u8c37\u4e2d\u56de\u65cb\u9707\u8361\uff0c\u674e\u6c81\u6094\u88ab\u4ed6\u70b9\u4e86\u8f6f\u9ebb\u7a74\uff0c\u7ad9\u4e0d\u8d77\u6765\uff0c\u5e78\u800c \u5979\u5f97\u6bcd\u4eb2\u6240\u6559\uff0c\u719f\u6089\u91d1\u4e16\u9057\u70b9\u7a74\u6cd5\u7684\u5965\u5999\uff0c\u81ea\u5df1\u8fd0\u6c14\u51b2\u5173\u89e3\u7a74\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u56db\u80a2\u5df2\u80fd\u8f6c \u52a8\u3002\u91d1\u4e16\u9057\u7684\u5f71\u5e72\u65e9\u5df2\u4e0d\u89c1\u4e86\u3002\u4f46\u95fb\u7fa4\u5cf0\u56de\u54cd\uff0c\u4f59\u97f3\u672a\u7edd\uff0c\u91d1\u4e16\u9057\u7684\u602a\u7b11\u4e4b\u58f0\u5c24\u81ea\u6447\u66f3\u5728\u5c71 \u5dc5\u6c34\u6daf\u3002\u674e\u6c81\u6885\u4f46\u89c9\u4e00\u7247\u832b\u7136\u3002\u54fa\u54fa\u81ea\u8bed\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\uff0c\u600e\u4e48\u7a81\u7136\u95f4\u53c8\u53d1\u75af\u4e86\uff1f\u201d\u5979\u8fd8\u5f53 \u771f\u6015\u91d1\u4e16\u9057\u53d1\u75af\uff0c\u75be\u5fd9\u8ffd\u4e0b\u5c71\u53bb\uff01 \u3000\u3000\u5728\u5c71\u8c37\u4e0b\u9762\uff0c\u5ffd\u89c1\u4e00\u961f\u5587\u561b\u8fce\u9762\u800c\u6765\u3002\u524d\u9762\u516b\u5934\u767d\u8c61\uff0c\u5f53\u4e2d\u4e00\u5934\u767d\u8c61\uff0c\u5750\u7740\u4e00\u4e2a\u8eab\u6750\u9ad8 \u5927\u5587\u561b\uff0c\u8986\u4ee5\u9ec4\u5e62\u4e3b\u76d6\uff0c\u4e2d\u95f4\u5341\u516d\u540d\u5587\u561b\u9a91\u9a6c\u76f8\u968f\u3002\u5728\u8c61\u961f\u4e24\u65c1\uff0c\u5219\u5404\u6709\u4e00\u5217\u5c11\u5973\uff1a\u4e2a\u4e2a\u767d \u8863\u5982\u96ea\uff0c\u957f\u88d9\u6447\u66f3\u3002\u4e2d\u95f4\u4e00\u4e2a\u5c11\u5973\uff0c\u660e\u8273\u7167\u4eba\uff0c\u795e\u6c14\u5374\u51b7\u505a\u4e4b\u6781\uff0c\u5750\u5728\u9a6c\u80cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5b9b \u5982\u4e00\u5c0a\u5927\u7406\u77f3\u50cf\u3002 \u3000\u3000\u674e\u6c81\u6885\u65cb\u98ce\u822c\u7684\u8dd1\u6765\uff0c\u7a81\u7136\u78b0\u7740\u8fd9\u961f\u767d\u8863\u5587\u561b\uff0c\u811a\u6b65\u8fd8\u672a\u6765\u5f97\u53ca\u6536\u4f4f\uff0c\u4fbf\u542c\u5f97\u6709\u4eba\u5a07\u58f0 \u65a5\u9053\uff1a\u201c\u8c01\u4eba\u6562\u95ef\u6cd5\u738b\u6cd5\u9a7e\uff1f\u201d\u4e00\u4e2a\u6234\u7740\u9762\u7eb1\u7684\u5973\u4eba\u8df3\u4e0b\u9a6c\u6765\uff0c\u4e0d\u7531\u5206\u8bf4\u5c31\u4f38\u624b\u6765\u6293\u674e\u6c81 \u6885\u3002\u674e\u6c81\u6885\u672c\u80fd\u7684\u95ea\u8eab\u4e00\u683c\uff0c\u90a3\u5987\u4eba\u8fd9\u4e00\u6293\u5feb\u6377\u4e4b\u6781\uff0c\u4e0d\u6599\u6293\u4e86\u4e2a\u7a7a\uff0c\u53cd\u800c\u7ed9\u674e\u6c81\u6885\u63a8\u5f00\u51e0 \u6b65\uff0c\u81c6\u4e86\u4e00\u58f0\uff0c\u8ddf\u8e2a\u6025\u8ffd\u3002\u8fd9\u5973\u4eba\u6b63\u662f\u767d\u6559\u5587\u561b\u4e2d\u7684\u201c\u5723\u6bcd\u201d\u3002\u674e\u6c81\u6885\u54ea\u91cc\u77e5\u9053\uff0c\u5979\u5728\u65e0\u610f \u4e4b\u4e2d\u7adf\u95ef\u4e86\u767d\u6559\u6cd5\u738b\u7684\u6cd5\u9a7e\u3002\u767d\u6559\u6cd5\u738b\u7684\u5730\u4f4d\u548c\u8fbe\u8d56\u73ed\u63b8\u540c\u4e00\u73ed\u8f88\uff0c\u90fd\u662f\u6d3b\u4f5b\u7684\u8eab\u4efd\uff0c\u8fd9\u4e00 \u95ef\u9a7e\uff0c\u5728\u5587\u561b\u5f1f\u4e8e\u773c\u4e2d\uff0c\u4e43\u662f\u975e\u540c\u5c0f\u53ef\u7684\u5192\u72af\u6cd5\u738b\uff01 \u3000\u3000\u674e\u6c81\u6885\u89c1\u5341\u516d\u4e2a\u767d\u8863\u5587\u561b\uff0c\u6392\u6210\u4e2a\u5706\u5708\uff0c\u4e0d\u58f0\u4e0d\u54cd\u5730\u4e2a\u4e2a\u6ce8\u89c6\u7740\u5979\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8feb\u8fd1\uff0c \u4e0d\u89c9\u6709\u4e9b\u5fc3\u614c\uff0c\u53eb\u9053\uff1a\u201c\u5582\u3001\u4f60\u4eec\u8981\u5e72\u4ec0\u4e48\uff1f\u201d\u4e24\u4e2a\u62a4\u6cd5\u5587\u561b\u9053\uff0c\u201c\u4f60\u8fd9\u5996\u5973\uff0c\u80c6\u6562\u95ef\u6d3b\u4f5b \u6cd5\u9a7e\uff0c\u8fd8\u4e0d\u5feb\u5411\u6d3b\u4f5b\u6551\u9976\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u54a6\uff0c\u54ea\u4f4d\u662f\u6d3b\u4f5b\uff1f\u4f60\u6307\u7ed9\u6211\u77a7\u77a7\u3002\u201d\u8bf4\u8bdd\u7684\u53e3 \u6c14\uff0c\u5c31\u50cf\u5c0f\u5b69\u5b50\u8981\u53bb\u89c1\u8bc6\u4e00\u4ef6\u7a00\u5947\u7684\u4e8b\u7269\u4f3c\u7684\u3002\u90a3\u4e24\u4e2a\u62a4\u6cd5\u5587\u561b\u5927\u6012\uff0c\u4e00\u51fa\u5de6\u638c\uff0c\u4e00\u51fa\u53f3 \u638c\uff0c\u5408\u6210\u4e00\u4e2a\u5706\u5f27\uff0c\u53cc\u638c\u9f50\u6293\uff0c\u767d\u6559\u5587\u561b\u7684\u6b66\u529f\u81ea\u6210\u4e00\u6d3e\uff0c\u8fd9\u4e00\u624b\u4e24\u4eba\u5408\u7528\u7684\u201c\u91d1\u521a\u6349\u5996\u201d \u624b\u6cd5\uff0c\u6bd4\u4e2d\u539f\u6b66\u6797\u7684\u5927\u64d2\u62ff\u624b\u8fd8\u8981\u5389\u5bb3\uff0c\u5374\u4e0d\u6599\u674e\u6c81\u6885\u81ea\u5e7c\u5f97\u6bcd\u4eb2\u6240\u6388\uff0c\u6700\u7cbe\u5e72\u5c0f\u5de7\u817e\u632a\u7684 \u529f\u592b\uff0c\u4e24\u4e2a\u5587\u561b\u53cc\u638c\u4e00\u5408\uff0c\u53ea\u542c\u5f97\u674e\u6c81\u563b\u563b\u4e00\u7b11\uff0c\u7adf\u50cf\u6e38\u9c7c\u4e00\u822c\u7684\u6ed1\u51fa\u4e86\u4ed6\u4eec\u7684\u624b\u5fc3\u3002\u4e24\u4e2a \u5587\u561b\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u5f52\u56de\u539f\u4f4d\uff0c\u5e78\u559c\u674e\u6c81\u6885\u8fd8\u4e0d\u95ef\u51fa\u5706\u5708\u4e4b\u5916\u3002 \u3000\u3000\u674e\u6c81\u6885\u53eb\u9053\uff1a\u201c\u5582\uff0c\u8fd9\u6761\u5927\u8def\u53c8\u4e0d\u662f\u4f60\u4eec\u7684\u3002\u65e2\u7136\u53f7\u79f0\u6d3b\u4f5b\uff0c\u5c31\u8be5\u6709\u6148\u60b2\u4e4b\u5fc3\uff0c\u600e\u4e48\u5360 \u4e86\u5927\u8def\uff0c\u4e0d\u8bb8\u4eba\u884c\u8d70\uff1f\u8d70\u8def\u4e5f\u6709\u7f6a\u4e48\uff1f\u201d\u90a3\u5341\u516d\u4e2a\u767d\u8863\u5587\u561b\u4e0d\u7406\u4e0d\u776c\uff0c\u5706\u5708\u6162\u6162\u56f4\u62e2\uff0c\u674e\u6c81 \u6885\u53cc\u638c\u4e00\u63a8\uff0c\u5341\u516d\u4e2a\u5587\u561b\u5408\u529b\u6321\u4f4f\uff0c\u4e25\u4f3c\u94dc\u94c1\u58c1\uff0c\u54ea\u63a8\u5f97\u52a8\uff0c\u94bb\u53c8\u94bb\u4e0d\u51fa\u53bb\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u9a82 \u9053\uff0c\u201c\u5582\uff0c\u5341\u516d\u4e2a\u5927\u7537\u4eba\uff0c\u6b3a\u8d1f\u6211\u4e00\u4e2a\u5973\u4e8e\uff0c\u8fd8\u8981\u8138\u4e48\uff1f\u201d\u60c5\u6025\u4e4b\u4e0b\uff0c\u4e00\u4f4e\u5934\u4fbf\u786c\u51b2\u8fc7\u53bb\u3002 \u5ffd\u542c\u5f97\u5f53\u524d\u7684\u4e24\u4e2a\u5587\u561b\u201c\u5501\u5501\u201d\u7684\u7b11\u4e86\u4e24\u58f0\uff0c\u7b11\u5f97\u751a\u602a\uff0c\u8138\u4e0a\u4e00\u6d3e\u6b63\u7ecf\u795e\u8272\uff0c\u597d\u50cf\u7a81\u7136\u7ed9\u4eba \u6293\u7740\u75d2\u5904\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u7b11\u4e86\u51fa\u6765\u4f3c\u7684\uff0c\u8fd9\u4e24\u4e2a\u5587\u561b\u4e00\u7b11\u4e4b\u4e0b\uff0c\u8eab\u5f62\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u674e\u6c81\u6885\u4ece\u7f1d\u9699 \u4e2d\u4e00\u94bb\u800c\u51fa\uff0c\u5fc3\u4e2d\u5927\u662f\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u54c8\uff0c\u662f\u4e86\uff0c\u4ed6\u4eec\u5b9a\u7136\u662f\u7ed9\u6211\u9a82\u5f97\u4e0d\u597d\u610f\u601d\uff0c\u6240\u4ee5\u6545\u610f \u653e\u6211\u8d70\u4e86\u3002\u201d\u56de\u5934\u505a\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u62d4\u811a\u4fbf\u8dd1\u3002 \u3000\u3000\u521a\u8dd1\u5f97\u4e24\u6b65\uff0c\u4e24\u5934\u767d\u8c61\u5df2\u62e6\u5728\u9762\u524d\uff0c\u8c61\u80cc\u4e0a\u4e24\u4e2a\u5587\u561b\u5404\u4f38\u4e00\u5f88\u4e38\u73af\u9521\u6756\uff0c\u62e6\u4f4f\u53bb\u8def\u3002\u674e \u6c81\u6885\u9053\uff1a\u201c\u5582\uff0c\u771f\u8981\u52a8\u624b\u4e48\uff1f\u201d\u62d4\u51fa\u77ed\u5251\u4e00\u524a\uff0c\u53ee\u9677\u4e24\u8d8b\u52bf\uff0c\u77ed\u5251\u7ed9\u53cd\u5f39\u8d77\u6765\uff0c\u90a3\u4e24\u5f88\u7985\u6756 \u5374\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u5587\u561b\u6b63\u662f\u767d\u6559\u6cd5\u738b\u6700\u5f97\u529b\u7684\u5f1f\u5b50\uff0c\u524d\u5e74\u6625\u521d\u6d3e\u53bb\u62a2\u91d1\u672c\u5df4\u74f6\u7684\u5c31\u662f \u8fd9\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u674e\u6c81\u6094\u7ed9\u62e6\u4f4f\u53bb\u8def\uff0c\u80cc\u540e\u90a3\u5341\u516d\u4e2a\u5587\u561b\u53c8\u8f6c\u4e0a\u6765\uff0c\u674e\u6c81\u6885\u6b63\u60f3\u6492\u91ce\u4e71\u9a82\uff0c\u5ffd\u89c1\u9a91\u5728\u4e2d\u95f4 \u90a3\u5934\u767d\u8c61\u4e0a\u7684\u90a3\u4e2a\u8138\u8272\u7ea2\u6da6\u53d1\u5149\u7684\u9ad8\u5927\u5587\u561b\u9053\uff1a\u201c\u5b69\u5b50\u65e0\u77e5\uff0c\u7531\u5979\u53bb\u5427\u3002\u201d\u5728\u8c61\u80ccL\u6325\u8d77\u62c2 \u5c18\u4e00\u62c2\uff0c\u674e\u6c81\u6885\u9661\u89c9\u4e00\u80a1\u52b2\u98ce\u5439\u6765\uff0c\u501f\u52bf\u4e00\u4e2a\u7b4b\u6597\uff0c\u7ffb\u4e86\u51fa\u53bb\u3002\u540e\u9762\u90a3\u5341\u516d\u4e2a\u5587\u561b\u679c\u7136\u6563 \u5f00\uff0c\u65e0\u4eba\u963b\u6321\uff0c\u90a3\u767d\u8c61\u80cc\u4e0a\u7684\u5587\u561b\u53c8\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u8bf4\u5f97\u4e0d\u9519\uff0c\u6d3b\u4f5b\u7406\u8be5\u6148\u60b2\uff0c\u5428\u54c8\u53f6\u54aa\u5587 \u54c4\u2026\u2026\u201d\u53fd\u54e9\u5495\u55dc\u7684\u8bf4\u4e86\u4e00\u53e5\u85cf\u8bdd\uff0c\u4f3c\u662f\u7ed9\u5979\u795d\u798f\u3002\u674e\u6c81\u6885\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5587\u561b\u4e00\u5b9a\u662f\u4ec0\u4e48\u6d3b \u4f5b\u4e86\u3002\u201d\u56de\u8fc7\u5934\u53bb\u770b\uff0c\u5374\u89c1\u90a3\u4e9b\u5587\u561b\u4e2a\u4e2a\u795e\u60c5\u8083\u7a46\uff0c\u674e\u6c81\u6094\u6709\u70b9\u80c6\u602f\uff0c\u4e0d\u6562\u591a\u770b\uff0c\u6025\u6025\u5954\u9003\u3002 \u3000\u3000\u970e\u65f6\u95f4\u8d70\u51fa\u4e86\u4e8c\u4e09\u91cc\u8def\uff0c\u5ffd\u89c1\u5c71\u5761\u4e0a\u6709\u4eba\u62db\u624b\u9053\uff1a\u201c\u6c81\u513f\uff0c\u4f60\u597d\u5927\u80c6\uff0c\u5feb\u8fc7\u6765\uff01\u201d\u62ac\u5934 \u4e00\u770b\uff0c\u6b63\u662f\u5979\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u674e\u6c81\u6094\u5927\u559c\uff0c\u6025\u5fd9\u8dd1\u53bb\uff0c\u6295\u5165\u6bcd\u4eb2\u6000\u4e2d\u3002\u51af\u7433\u7b11\u9053\uff1a\u201c\u8fde\u6211\u4e5f\u4e0d\u6562\u53bb\u62db\u60f9\u4ed6\u4eec\uff0c\u4f60\u5374\u80e1 \u95f9\u3002\u8981\u4e0d\u662f\u6211\uff0c\u4f60\u8fd9\u6b21\u82e6\u5934\u6709\u5f97\u5403\u5462\uff01\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u54c8\uff0c\u6211\u77e5\u9053\uff0c\u90a3\u5706\u5708\u4e2d\u7684\u4e24\u4e2a\u5587\u561b\u662f \u4f60\u7528\u6697\u5668\u6253\u7740\u4ed6\u4eec\u7684\u7b11\u7a74\u7684\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u662f\u7ed9\u6211\u9a82\u6015\u4e86\u5427\uff01\u201d\u51af\u7433\u7684\u98de\u82b1\u6458\u53f6\uff0c\u53ef\u4ee5\u4f24\u4eba \u7acb\u6b7b\uff0c\u4e5f\u53ef\u4ee5\u6253\u5165\u7a74\u9053\uff0c\u4f46\u7531\u4e8e\u674e\u6c81\u6885\u529f\u529b\u672a\u5230\uff0c\u5c1a\u672a\u80fd\u5b66\u3002\u5979\u731c\u4e2d\u662f\u6bcd\u4eb2\u6697\u4e2d\u52a9\u5979\uff0c\u7b11 \u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u6d3b\u4f5b\u662f\u4e2a\u597d\u4eba\uff0c\u539f\u6765\u662f\u4ed6\u6015\u4e86\u4f60\uff0c\u624d\u653e\u6211\u7684\u3002\u201d \u3000\u3000\u51af\u7433\u9762\u8272\u4e00\u7aef\uff0c\u9053\uff1a\u201c\u90a3\u767d\u6559\u6cd5\u738b\u8c41\u8fbe\u5927\u5ea6\uff0c\u6211\u4e5f\u5bf9\u4ed6\u8d77\u656c\uff0c\u4f60\u600e\u597d\u80e1\u4e71\u8bf4\u4ed6\uff1f\u4f60\u77e5\u9053 \u4ed6\u4eec\u662f\u505a\u4ec0\u4e48\u6765\u7684\u5417\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u9002\u624d\u6211\u53bb\u6253\u542c\uff0c\u539f\u6765\u524d\u9762\u5c31\u662f \u8428\u8fce\u57ce\u3002\u767d\u6559\u6cd5\u738b\u4e0e\u9ec4\u6559\u5587\u561b\u8bb2\u548c\uff0c\u73ed\u7985\u8bb8\u4ed6\u56de\u897f\u85cf\u4f20\u6559\u3002\u8428\u8fce\u8d77\u4e86\u4e00\u4e2a\u5f88\u5927\u5f88\u5927\u7684\u767d\u6559\u5587 \u561b\u5bfa\u5e99\uff0c\u767d\u6559\u6cd5\u738b\u662f\u7387\u9886\u4ed6\u7684\u5f1f\u5b50\u6765\u4e3b\u6301\u5f00\u5149\u5927\u5178\u7684\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u8fd9\u4e00\u56de\u5b50\u529f\u592b\uff0c\u4f60\u7adf \u7136\u5230\u4e86\u8428\u8fe6\u57ce\u5417\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u8fd8\u8bf4\u4e00\u4f1a\u5b50\uff0c\u597d\u534a\u5929\u4e86\u5462\uff01\u4f60\u4eec\u8c08\u5f97\u8fd8\u4e0d\u591f\u5417\uff1f\u55ef\uff0c\u91d1\u4e16 \u9057\u5462\uff1f\u4ed6\u8fd9\u56de\u5012\u5f88\u6b63\u7ecf\u4e86\uff0c\u560e\uff1f\u6ca1\u6709\u8ddf\u4f60\u6765\u80e1\u95f9\uff1f\u201d\u674e\u6c81\u6885\u5fc3\u5934\u4e00\u9178\uff0c\u9053\uff1a\u201c\u4ed6\u53c8\u53d1\u75af\u4e86 \u5462\uff0c\u8dd1\u5f97\u65e0\u5f71\u65e0\u8e2a\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u8fde\u65e5\u7528\u2018\u6f5c\u5fc3\u9b54\u2019\u7684\u5185\u529f\uff0c\u52a9\u4ed6\u5236\u4f4f\u5185\u9b54\uff0c\u6700\u5c11\u5728\u4e03\u5341\u4e8c\u5929\u5185\u53ef\u4ee5 \u65e0\u4e8b\uff0c\u597d\u7aef\u7aef\u7684\u600e\u4e48\u4f1a\u53d1\u75af\uff1f\u4f60\u548c\u4ed6\u8bf4\u4e86\u4ec0\u4e48\u6765\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u6211\u54ea\u6709\u8bf4\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u8bf4 \u4f60\u8981\u5c06\u4ed6\u5e26\u4e0a\u5929\u5c71\uff0c\u8bf7\u59e8\u7236\u6551\u4ed6\u3002\u201d\u51af\u7433\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5440\uff0c\u4f60\u771f\u662f\u4e0d\u61c2\u4e8b\u3002\u6211\u5c31\u662f\u6015\u4ed6 \u5fc3\u9ad8\u6c14\u505a\uff0c\u4e0d\u613f\u53d7\u4eba\u6069\u60e0\uff0c\u6240\u4ee5\u6545\u610f\u7792\u7740\u4ed6\u7684\u3002\u4f60\u5374\u504f\u504f\u7ed9\u6211\u62c6\u7a7f\u4e86\u3002\u4f60\u4e0d\u77e5\u9053\uff0c\u4ed6\u548c\u5510\u7ecf \u5929\u8fd8\u6709\u5fc3\u75c5\u5462\u3002\u201d\u674e\u6c81\u6885\u597d\u5947\u5fc3\u53c8\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5fc3\u75c5\uff1f\u201d\u51af\u7433\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u54b3\uff0c\u4f60\u8fd9\u75f4 \u4e2b\u5934\u6bd4\u6211\u5f53\u5e74\u8fd8\u50bb\uff0c\u6bd4\u6211\u8fd8\u66f4\u6b22\u559c\u7406\u95f2\u4e8b\u3002\u4e0d\u8bf4\u5566\uff0c\u8c01\u53eb\u6211\u662f\u4f60\u7684\u6bcd\u4eb2\uff0c\u53ea\u5f97\u53c8\u8d39\u5fc3\u673a\u7ed9\u4f60 \u627e\u4ed6\u5566\u3002\u5440\uff0c\u5973\u513f\u5927\u4e86\uff0c\u771f\u662f\u9ebb\u70e6\u3002\u201d\u674e\u6c81\u6885\u9762\u4e0a\u4e00\u7ea2\uff0c\u8d4c\u6c14\u8bf4\u9053\uff1a\u201c\u8c01\u8981\u4f60\u53bb\u627e\u4ed6\uff1f\u7a00\u7f55 \u4e48\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u7a00\u7f55\uff0c\u4e0d\u7a00\u7f55\uff01\u5929\u4e0b\u7537\u5b50\u6709\u7684\u662f\u3002\u53ef\u5c31\u6ca1\u4e00\u4e2a\u5bf9\u4f60\u5fc3\u601d\uff0c\u662f \u4e48\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u51af\u7433\u626e\u4e86\u4e2a\u9b3c\u8138\u9053\uff1a\u201c\u662f\uff0c\u4e0d\u9519\u4e86\u5427\uff1f\u65e2\u7136\u6ca1\u4e00\u4e2a\u5bf9\u4f60\u5fc3\u601d\uff0c \u90a3\u5c31\u53ea\u597d\u627e\u4ed6\u4e86\u3002\u53bb\uff0c\u53bb\uff0c\u54b1\u4eec\u5230\u8428\u8fe6\u77a7\u70ed\u95f9\u53bb\uff0c\u91d1\u4e16\u9057\u4e5f\u662f\u4e2a\u7231\u70ed\u95f9\u7684\u4eba\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u8d70 \u5f97\u8fdc\u7684\u3002\u201d \u3000\u3000\u8428\u8ff9\u662f\u85cf\u5357\u7684\u4e00\u4e2a\u5c71\u57ce\uff0c\u5e73\u65e5\u5bc2\u9759\u5f97\u6709\u5982\u4e16\u5916\u6843\u6e90\uff0c\u8fd9\u56de\u767d\u6559\u6cd5\u738b\u6765\u5230\uff0c\u4e43\u662f\u65f7\u53e4\u672a\u6709 \u7684\u5927\u4e8b\uff0c\u987f\u65f6\u70ed\u95f9\u8d77\u6765\u4e86\uff0c\u8bb8\u591a\u8fdc\u5730\u7684\u9999\u5ba2\u90fd\u95fb\u98ce\u8d76\u6765\uff0c\u8428\u8fce\u7684\u571f\u53f8\u548c\u6e05\u5ef7\u6d3e\u9a7b\u8428\u8fce\u7684\u5ba3\u6170 \u4f7f\u9648\u5b9a\u57fa\u66f4\u662f\u5fd9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u8fde\u65e5\u6253\u70b9\uff0c\u66ff\u767d\u6559\u6cd5\u738b\u5b89\u6392\u884c\u5bab\uff0c\u7b79\u5907\u4f9b\u5949\u3002\u53ea\u6709\u4e00\u4e2a\u4eba\u8fd9\u65f6 \u5374\u95f2\u5f97\u65e0\u804a\uff0c\u72ec\u81ea\u5728\u5ba3\u5c09\u5e9c\u7684\u540e\u82b1\u56ed\u4e2d\u5f98\u5f8a\u53f9\u606f\u3002\u8fd9\u4eba\u5c31\u662f\u9648\u5b9a\u57fa\u7684\u513f\u5b50\u9648\u5929\u5b87\u3002 \u3000\u3000\u9648\u5929\u5b87\u81ea\u4ece\u968f\u4ed6\u7684\u7236\u4eb2\u91cd\u56de\u8428\u8fce\u4e4b\u540e\uff0c\u571f\u53f8\u65e7\u4e8b\u91cd\u63d0\uff0c\u53c8\u8981\u8feb\u4ed6\u548c\u81ea\u5df1\u7684\u5973\u513f\u6210\u5a5a\uff0c\u9648 \u5929\u5b87\u7528\u4e2a\u201c\u62d6\u201d\u5b57\u8bc0\uff0c\u62d6\u5f97\u4e00\u5929\u7b97\u4e00\u5929\u3002\u9648\u5b9a\u57fa\u5ff5\u5ff5\u4e0d\u5fd8\u6545\u4e61\uff0c\u4ed6\u4ea6\u4e0d\u613f\u513f\u5b50\u505a\u571f\u53f8\u7684\u5973 \u5a7f\uff0c\u53ef\u53c8\u4e0d\u80fd\u4e0d\u6577\u884d\u4ed6\uff0c\u9648\u5b9a\u57fa\u672c\u6709\u6253\u7b97\uff0c\u4ed6\u542c\u513f\u5b50\u7684\u8bdd\uff0c\u6d3e\u4e86\u6c5f\u5357\u643a\u51fd\u5165\u4eac\uff0c\u6c42\u4e00\u4f4d\u505a\u5fa1 \u53f2\u7684\u4eb2\u621a\uff0c\u8bf7\u4ed6\u8f6c\u594f\u7687\u5e1d\uff0c\u6c42\u7687\u5e1d\u5ff5\u4ed6\u8fce\u62e6\u91d1\u74f6\u7684\u529f\u52b3\uff0c\u8d66\u4ed6\u56de\u53bb\u3002\u53ef\u662f\u4ece\u897f\u85cf\u5230\u5317\u4eac\u8def\u9014 \u9065\u8fdc\uff0c\u6c5f\u5357\u53bb\u4e86\u534a\u5e74\uff0c\u5140\u65e0\u97f3\u8baf\uff0c\u4e24\u7236\u5b50\u771f\u662f\u5ea6\u65e5\u5982\u5e74\uff0c\u571f\u53f8\u53c8\u5e38\u5e38\u62db\u8bf7\u4ed6\u4eec\u53bb\u8d74\u5bb4\uff0c\u786c\u53eb \u5973\u513f\u51fa\u6765\u7ea0\u7f20\u9648\u5929\u5b87\uff0c\u4ee4\u9648\u5929\u5b87\u82e6\u607c\u975e\u5e38\u3002 \u3000\u3000\u5e78\u559c\u8fd9\u51e0\u5929\u571f\u53f8\u5fd9\u7740\u8fce\u63a5\u767d\u6559\u6cd5\u738b\uff0c\u9648\u5929\u5b87\u5012\u4e50\u5f97\u8033\u6839\u6e05\u9759\u3002\u8fd9\u4e00\u65e5\u6cd5\u738b\u6765\u5230\uff0c\u9648\u5b9a\u57fa \u548c\u571f\u53f8\u90fd\u53bb\u966a\u4f34\u6cd5\u738b\uff0c\u8859\u95e8\u91cc\u7684\u4eba\u4e5f\u4e0a\u8857\u53bb\u77a7\u70ed\u95f9\uff0c\u9648\u5929\u5b87\u767e\u65e0\u804a\u8d56\uff0c\u4ec0\u4e48\u4e8b\u90fd\u65e0\u5fc3\u7eea\uff0c\u4e00 \u4e2a\u4eba\u8eb2\u5728\u8859\u95e8\u91cc\u9762\u3002\u53ea\u542c\u5f97\u6253\u4e86\u4e09\u66f4\uff0c\u57ce\u4e2d\u8fd8\u662f\u5904\u5904\u98d8\u8d77\u70df\u82b1\uff0c\u55a7\u95f9\u4e4b\u58f0\u672a\u51cf\u3002\u7236\u4eb2\u53c8\u672a\u56de \u6765\uff0c\u4e0e\u5916\u9762\u70ed\u95f9\u7684\u6c14\u6c1b\u76f8\u6bd4\uff0c\u8859\u4e2d\u66f4\u662f\u5bc2\u9759\u5f97\u53ef\u6015\u3002\u9648\u5929\u5b87\u72ec\u81ea\u4e00\u4eba\u5230\u540e\u82b1\u56ed\u53bb\u6563\u6b65\uff0c\u6708\u51c9 \u5982\u6c34\uff0c\u5bd2\u6c14\u88ad\u4eba\uff0c\u9648\u5929\u5b87\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6708\u534e\u5982\u7ec3\uff0c\u957f\u662f\u4eba\u5343\u91cc\uff01\u4e00\u6837\u7684\u6625\u591c\uff0c\u4e00\u6837 \u7684\u6708\u5149\uff0c\u53ef\u662f\u6211\u7684\u829d\u5a1c\u5374\u5728\u4f55\u65b9\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u85cf\u65cf\u5c11\u5973\u7684\u5029\u5f71\u5728\u4ed6\u5fc3\u5e95\u6162\u6162\u6d6e\u8d77\uff0c\u51b7\u8273\u7684\u989c\u5bb9\uff0c\u795e\u79d8\u7684\u5fae\u7b11\uff0c\u66fe\u5728\u591a\u5c11\u4e2a\u68a6\u4e2d\u56f0 \u60d1\u8fc7\u4ed6\uff0c\u9648\u5929\u5b87\u4e0e\u829d\u5a1c\u867d\u7136\u662f\u4f1a\u5c11\u79bb\u591a\uff0c\u4f46\u90a3\u51e0\u6b21\u77ed\u77ed\u7684\u805a\u4f1a\uff0c\u90fd\u662f\u4ed6\u4e00\u751f\u4e2d\u6c38\u96be\u5fd8\u6000\u7684\u4e8b \u4ef6\uff0c\u4ed6\u60f3\u8d77\u4e86\u5728\u571f\u53f8\u5bb6\u4e2d\u98de\u5200\u5288\u679c\u6551\u5979\u7684\u4e8b\uff0c\u60f3\u8d77\u4e86\u5728\u8352\u5c71\u6708\u591c\uff0c\u7b2c\u4e00\u6b21\u77e5\u9053\u4e86\u5979\u7684\u8eab\u4e16\u4e4b \u8c1c\uff1b\u800c\u66f4\u96be\u5fd8\u6000\u7684\u662f\u5728\u51b0\u5bab\u7684\u90a3\u51e0\u4e2a\u665a\u4e0a\uff0c\u5728\u90a3\u795e\u8bdd\u8239\u7684\u4ed9\u5883\u91cc\uff0c\u542c\u829d\u5a1c\u7ec6\u8bc9\u8877\u66f2\u3002\u53ef\u662f\u8c01 \u4e5f\u6599\u4e0d\u5230\u4e16\u53d8\u4e4b\u5947\uff0c\u51b0\u5cf0\u5012\u584c\u4e4b\u540e\uff0c\u81ea\u5df1\u53c8\u91cd\u56de\u5230\u8fd9\u4ee4\u4eba\u70e6\u607c\u7684\u8428\u8fce\u800c\u829d\u5a1c\u5374\u4ece\u6b64\u6c93\u65e0\u97f3\u8baf\u3002 \u3000\u3000\u201c\u829d\u5a1c\u662f\u4e0d\u662f\u5728\u90a3\u573a\u5929\u707e\u5de8\u52ab\u4e4b\u4e2d\u6b7b\u53bb\u4e86\u5462\uff1f\u201d\u9648\u5929\u5b87\u771f\u4e0d\u6562\u8fd9\u6837\u60f3\uff0c\u53ef\u662f\u5374\u53c8\u4e0d\u80fd\u4e0d \u5982\u6b64\u60f3\u3002\u66ae\u7136\u95f4\u4ed6\u53c8\u60f3\u8d77\u5e7d\u840d\uff0c\u60f3\u9053\uff1a\u201c\u5e7d\u840d\u4e5f\u9003\u5f97\u51fa\u6765\uff0c\u829d\u5a1c\u672a\u5fc5\u9047\u9669\u3002\u201d\u81ea\u5bbd\u81ea\u89e3\uff0c\u5fc3 \u4e2d\u5374\u4ecd\u662f\u6291\u90c1\u96be\u6d88\u3002\u82e5\u5c06\u829d\u5a1c\u53bb\u6bd4\u571f\u53f8\u7684\u5973\u513f\uff0c\u90a3\u771f\u65e0\u5f02\u4e8e\u628a\u7075\u829d\u4ed9\u8349\u53bb\u6bd4\u6b8b\u82b1\u8d25\u67f3\u3002\u602a\u4e0d \u5f97\u571f\u53f8\u8d8a\u662f\u8feb\u5a5a\uff0c\u4ed6\u5c31\u8d8a\u53d1\u601d\u5ff5\u829d\u5a1c\u4e86\u3002 \u3000\u3000\u591c\u66f4\u6df1\uff0c\u5916\u9762\u55a7\u58f0\u6e10\u6e10\u5e73\u9759\uff0c\u9648\u5929\u5b87\u81ea\u5728\u82b1\u4e1b\u4e2d\u75f4\u75f4\u7684\u60f3\uff0c\u5ffd\u542c\u5f97\u82b1\u4e1b\u4e2d\u4f3c\u6709\u7ec6\u5fae\u7684\u811a \u6b65\u58f0\uff0c\u9648\u5929\u5b87\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c1\u4e00\u4e2a\u62ab\u7740\u767d\u7eb1\u7684\u5c11\u5973\uff0c\u5206\u82b1\u62c2\u53f6\uff0c\u8f7b\u8f7b\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u4e00\u53cc\u660e\u5982 \u79cb\u6c34\u7684\u773c\u775b\u6df1\u60c5\u7684\u6ce8\u89c6\u7740\u4ed6\uff0c\u8138\u4e0a\u6709\u4e00\u6735\u7b11\u5bb9\uff0c\u6de1\u6de1\u7684\u7b11\u5bb9\u66f4\u886c\u51fa\u5979\u795e\u60c5\u7684\u5fe7\u90c1\u3002\u9648\u5929\u5b87\u53eb \u9053\uff1a\u201c\u8fd9\u662f\u505a\u68a6\u5417\uff1f\u4f60\u662f\u829d\u5a1c\uff01\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u662f\u505a\u68a6\uff0c\u4f46\u548c\u505a\u68a6\u4e5f\u5dee\u4e0d\u591a\u3002\u4f60\u628a\u5b83\u5f53\u505a \u4e00\u573a\u6625\u68a6\u597d\u4e86\u3002\u201d\u7b11\u5bb9\u672a\u655b\uff0c\u773c\u89d2\u5374\u6ef4\u4e0b\u4e24\u9897\u4eae\u83b9\u7684\u6cea\u73e0\u3002\u6b63\u662f\uff1a\u5982\u6b64\u826f\u5bb5\u5982\u6b64\u6708\uff0c\u5c1a\u6050\u76f8 \u9022\u662f\u68a6\u4e2d\u3002\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u4e91\u7834\u6708\u6765\u3000\u7a7a\u52b3\u9b42\u68a6\u7ed5\u3000\u949f\u58f0\u68b5\u8d1d\u3000\u60ca\u89c1\u5251\u5149\u5bd2 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u4e03\u56de \u4e91\u7834\u6708\u6765\u3000\u7a7a\u52b3\u9b42\u68a6\u7ed5 \u949f\u58f0\u68b5\u8d1d\u3000\u60ca\u89c1\u5251\u5149\u5bd2 \u3000\u3000\u9648\u5929\u5b87\u5c06\u4e2d\u6307\u9001\u8fdb\u53e3\u4e2d\u4e00\u54ac\uff0c\u75bc\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u8fd9\u4e0d\u662f\u68a6\uff0c\u8fd9\u4e0d\u662f \u68a6\uff01\u54b1\u4eec\u662f\u771f\u7684\u76f8\u805a\u4e86\uff1b\u54b1\u4eec\u4ece\u6b64\u6c38\u4e0d\u5206\u5f00\u4e86\uff01\u201d\u829d\u5a1c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u6c38\u4e0d\u5206\u5f00\u3002\u201d\u9648\u5929 \u5b87\u7d27\u7d27\u5c06\u5979\u6402\u4f4f\uff0c\u597d\u50cf\u751f\u6015\u5979\u7a81\u7136\u98de\u8d70\u4f3c\u7684\uff0c\u4f46\u89c1\u5979\u773c\u89d2\u6cea\u73e0\u83b9\u83b9\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\u4e5f\u5e26\u7740\u4e00\u80a1 \u51c4\u51c9\u7684\u51b5\u5473\uff0c\u66f4\u663e\u5f97\u795e\u8272\u5341\u5206\u5fe7\u90c1\u3002\u9648\u5929\u5b87\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u62c5\u5fe7\u8bf4\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0 \u4e48\uff0c\u4f60\u771f\u7684\u7b54\u5e94\u4e86\u4e48\uff1f\u54b1\u4eec\u4ece\u6b64\u6c38\u4e0d\u5206\u5f00\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u65f6\u5019\u90fd\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u4f60\u6ca1\u6709 \u5728\u68a6\u4e2d\u68a6\u89c1\u6211\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u662f\u5475\uff0c\u6211\u6bcf\u4e00\u4e2a\u68a6\u4e2d\u90fd\u68a6\u89c1\u4f60\u3002\u6709\u65f6\u4f60\u5411\u6211\u62c8\u82b1\u5fae\u7b11\uff1b\u6709 \u65f6\u53c8\u89c1\u4f60\u5728\u6708\u591c\u7684\u60ac\u5ca9\u8fb9\uff0c\u5077\u5077\u5730\u54ed\u6ce3\u3002\u7136\u800c\u8fd9\u90fd\u662f\u68a6\u5883\uff0c\u8fd9\u4e9b\u90fd\u8fc7\u53bb\u4e86\u3002\u4ee5\u540e\u54b1\u4eec\u6ca1\u6709\u54ed \u6ce3\uff0c\u53ea\u6709\u6b22\u7b11\u3002\u201d\u829d\u5a1c\u9053\uff1a\u201c\u6211\u4e5f\u65f6\u65f6\u68a6\u89c1\u4f60\u3002\u8fd9\u53ef\u89c1\u5f97\uff0c\u54b1\u4eec\u672c\u6765\u5c31\u6ca1\u6709\u79bb\u5f00\u8fc7\u3002\u201d\u9648\u5929 \u5b87\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8981\u7684\u4e0d\u662f\u68a6\u5883\uff0c\u8822\u8981\u7684\u662f\u6c38\u6052\u7684\u76f8\u805a\u3002\u201d\u829d\u5a1c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u662f\u771f\uff1f\u4ec0 \u4e48\u662f\u68a6\uff1f\u4ec0\u4e48\u53eb\u505a\u4e00\u77ac\uff1f\u4ec0\u4e48\u53eb\u505a\u6c38\u6052\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u4e2a\u95ee\u9898\uff0c\u662f\u5343\u53e4\u4ee5\u6765\uff0c\u591a\u5c11\u54f2\u4eba\u6240\u82e6\u601d\u672a\u89e3\u7684\u95ee\u9898\uff0c\u9648\u5929\u5b87\u7a81\u7136\u89c9\u5f97\u88ab\u5979\u7684\u5fe7\u90c1\u60c5 \u7eea\u6240\u4f20\u67d3\uff0c\u4e00\u65f6\u95f4\u832b\u7136\u4e0d\u77e5\u6240\u5bf9\u3002\u56ed\u5916\u94b5\u58f0\u68b5\u5457\uff0c\u9690\u9690\u4f20\u6765\uff0c\u8dd1\u6c5f\u6e56\u7684\u8d29\u9a6c\u4eba\u5531\u8d77\u300a\u6d41\u6d6a\u4e4b \u6b4c\u300b\uff1a\u201c\u4f60\u53ef\u66fe\u89c1\u8fc7\u8352\u6f20\u5f00\u82b1\uff1f\u4f60\u53ef\u66fe\u89c1\u8fc7\u51b0\u5ddd\u878d\u5316\u3002\u4f60\u6ca1\u6709\u89c1\u8fc7\uff1f\u4f60\u6ca1\u6709\u89c1\u8fc7\uff01\u5440\uff01\u90a3\u4e48 \u6d41\u6d6a\u7684\u65c5\u4eba\u54ea\uff0c\u4ed6\u4e5f\u6c38\u4e0d\u4f1a\u505c\u4e0b\uff01\u201d\u8fd9\u8d29\u9a6c\u4eba\u7684\u6d41\u6d6a\u4e4b\u6b4c\u4e5f\u5df2\u5531\u5230\u5c3e\u58f0\u4e86\u3002 \u3000\u3000\u829d\u5a1c\u63a5\u7740\u8f7b\u58f0\u5531\u9053\uff1a \u3000\u3000\u201c\u6c38\u6052\u7684\u7231\u60c5\u77ed\u4fc3\u800c\u660e\u4eae\uff0c \u3000\u3000\u50cf\u9ed1\u591c\u7684\u5929\u7a7a\u66ae\u5730\u7535\u5149\u4e00\u95ea\uff01 \u3000\u3000\u867d\u65cb\u5373\u53c8\u5f52\u4e8e\u6f20\u6f20\u7684\u957f\u7a7a\uff0c \u3000\u3000\u4f46\u5df2\u7167\u89c1\u4e86\u60c5\u4eba\u6700\u7f8e\u7684\u5f62\u8c61\uff01\u201d \u3000\u3000\u8fd9\u662f\u4ece\u5c3c\u6cca\u5c14\u4f20\u6765\uff0c\u5728\u897f\u85cf\u6d41\u884c\u7684\u4e00\u9996\u6c11\u6b47\uff0c\u662f\u6b22\u6109\u7684\u60c5\u6b4c\uff0c\u4e5f\u662f\u60b2\u51c9\u7684\u60c5\u6b4c\u3002\u9648\u5929\u5b87 \u5fc3\u5934\u4f3c\u94c5\u822c\u6c89\u91cd\uff0c\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u662f\u4e00\u77ac\uff1f\u4ec0\u4e48\u662f\u6c38\u6052\uff1f\u4e0d\uff0c\u6211\u8981\u7684\u662f\u6b22\u4e50\u7684\u6c38\u6052\uff01\u201d \u3000\u3000\u829d\u5a1c\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u5c31\u4e0d\u8981\u5c3d\u5728\u76f8\u805a\u4e0e\u5206\u79bb\u4e0a\u7ea0\u7f20\uff0c\u54b1\u4eec\u73b0\u5728\u5230\u5e95\u662f\u89c1\u7740\u4e86\uff0c\u867d\u7136 \u2018\u50cf\u9ed1\u591c\u7684\u5929\u7a7a\u66ae\u5730\u7535\u5149\u4e00\u95ea\u2019\uff0c\u54b1\u4eec\u5728\u7535\u5149\u4e00\u95ea\u7684\u77ac\u606f\u4e4b\u95f4\uff0c\u96be\u9053\u5c31\u4e0d\u80fd\u5c3d\u60c5\u6b22\u4e50\uff0c\u5929 \u5b87\uff0c\u4f60\u8bf4\u4e9b\u6b22\u4e50\u7684\u8bdd\u5427\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u6211\u542c\u4ec0\u4e48\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u54b1\u4eec\u7684\u76f8\u4f1a\u53ea\u80fd\u50cf\u9ed1\u591c\u7684\u5929\u7a7a\u6479\u5730\u7535\u5149\u4e00\u95ea\uff1f\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u80fd\u7559\u4e0b \u6765\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u77ac\u606f\u7684\u65f6\u95f4\u6211\u5df2\u4e0d\u77e5\u5192\u4e86\u591a\u5927\u7684\u5371\u9669\uff0c\u5929\u5b87\uff0c\u8bf4\u5427\uff0c\u8bf4\u4e9b\u6211\u6b22\u559c\u542c \u7684\u8bdd\u3002\u6211\u4e0d\u80fd\u518d\u9017\u7559\u5566\uff0c\u6211\u5c31\u8981\u8d70\u5566\uff01\u5440\uff0c\u6211\u5c31\u8981\u8d70\u5566\uff01\u201d \u3000\u3000\u829d\u5a1c\u6c89\u90c1\u7684\u9762\u4e0a\u73b0\u51fa\u4e00\u6d3e\u51b3\u7136\u6bc5\u7136\u7684\u795e\u6c14\uff0c\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u7136\u8d77\u4e86\u4e0d\u7965\u4e4b\u611f\uff0c\u201c\u829d \u5a1c\u662f\u6765\u5411\u6211\u8bc0\u522b\u7684\u4e48\uff1f\u201d\u8fd9\u5ff5\u5934\u77ac\u606f\u4e4b\u95f4\u5728\u4ed6\u5fc3\u4e2d\u8f6c\u4e86\u65e0\u6570\u6b21\uff0c\u4ed6\u4e0d\u5fcd\u8bf4\u51fa\u6765\uff0c\u5446\u5446\u5730\u671b\u7740 \u829d\u5a1c\u3002\u829d\u5a1c\u53cd\u800c\u5fae\u7b11\u9053\uff1a\u201c\u5929\u5b87\uff0c\u8bf4\u4e9b\u6b22\u4e50\u7684\u8bdd\u513f\u5427\u3002\u201d\u5979\u58f0\u97f3\u6296\u98a4\uff0c\u867d\u7136\u52c9\u5f3a\u9732\u51fa\u7b11\u5bb9\uff0c \u90a3\u7b11\u58f0\u6bd4\u54ed\u6ce3\u8fd8\u66f4\u51c4\u9178\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u79bb\u5f00\u4e86\u4f60\uff0c\u8fd8\u6709\u4ec0\u4e48\u6b22\u4e50\uff1b\u55ef\uff0c\u829d\u5a1c\uff0c\u54b1\u4eec\u8fd9\u6b21\u90fd\u5728\u51b0\u5cf0\u6d69\u52ab\u4e4b\u4e2d\u9003\u51fa\u6027 \u547d\uff0c\u54b1\u4eec\u96be\u9053\u8fd8\u8981\u518d\u53d7\u7b2c\u4e8c\u6b21\u66f4\u5927\u7684\u52ab\u96be\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u6211\u4e00\u51fa\u751f\u3002\u52ab\u96be\u4fbf\u968f\u4e4b\u800c\u6765\u4e86\uff0c\u8981 \u907f\u4e5f\u907f\u4e0d\u5f00\uff0c\u5440\uff0c\u4f60\u4e0d\u6653\u5f97\u3002\u201d\u9648\u5929\u5b87\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u90fd\u6653\u5f97\u3002\u6211\u77e5\u9053\u4f60\u8981\u62a5\u4ec7\u3002\u829d\u5a1c\u5440\uff0c \u54b1\u4eec\u751f\u5219\u540c\u751f\uff0c\u6b7b\u5219\u540c\u6b7b\u3002\u6211\u548c\u4f60\u4e00\u9053\u53bb\u62a5\u4ec7\u3002\u82e5\u7136\u6fc0\u5e78\u4e0d\u6b7b\u5462\uff0c\u6211\u5c31\u548c\u4f60\u7acb\u5373\u9003\u56de\u5357\u8fb9 \u53bb\uff0c\u9003\u56de\u6211\u7684\u5bb6\u4e61\u53bb\u3002\u201d\u829d\u5a1c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u50bb\u60f3\u5934\u3002\u8840\u6d77\u6df1\u4ec7\u5c82\u80fd\u8bf7\u4eba\u4ee3\u62a5\uff1f\u518d\u8bf4\uff0c\u6211\u80fd\u4ee4 \u4f60\u4e3a\u6211\u7684\u79c1\u4e8b\u800c\u5f15\u8d77\u897f\u85cf\u7684\u98ce\u4e91\u4e48\uff1f\u6211\u7684\u62a5\u4ec7\u4e8b\u5c0f\u3002\u4f60\u4e00\u626c\u624b\u8fdb\u53bb\uff0c\u7ea0\u7eb7\u53ef\u5c31\u5927\u5566\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u4e00\u60f3\uff0c\u81ea\u5df1\u7236\u4eb2\u662f\u6e05\u5ef7\u6d3e\u9a7b\u8428\u8fe6\u7684\u201c\u5ba3\u6170\u4f7f\u201d\uff0c\u829d\u5a1c\u7684\u4ec7\u4eba\u5219\u662f\u8428\u8fe6\u7684\u4e0a\u53f8\uff0c\u6e05 \u5ef7\u4e3a\u4e86\u6015\u897f\u85cf\u5404\u571f\u53f8\u53cd\u53db\uff0c\u6240\u4ee5\u9664\u4e86\u6d3e\u798f\u5eb7\u5b89\u9547\u5b88\u62c9\u8428\u4e4b\u5916\uff0c\u8fd8\u6d3e\u6709\u5404\u5730\u7684\u201c\u5ba3\u6170\u4f7f\u201d\uff0c\u5ba3 \u6170\u4f7f\u7684\u4efb\u52a1\u4e4b\u4e00\u5c31\u662f\u8981\u7b3c\u7edc\u571f\u53f8\u3002\u82e5\u7136\u81ea\u5df1\u771f\u7684\u52a9\u829d\u5a1c\u523a\u6740\u571f\u53f8\uff0c\u7236\u4eb2\u5fc5\u88ab\u5904\u6b7b\u65e0\u7591\uff1b\u800c\u4e14 \u8bf4\u4e0d\u5b9a\u4f1a\u5f15\u8d77\u66f4\u5927\u7684\u7ea0\u7eb7\uff0c\u5f04\u51fa\u897f\u85cf\u7684\u52a8\u4e71\u3002 \u3000\u3000\u829d\u5a1c\u62ac\u7740\u6cea\u773c\u51dd\u671b\u5929\u9645\u6d6e\u4e91\uff0c\u9648\u5929\u5b87\u5fc3\u60c5\u6fc0\u52a8\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u6b7b\u4e86\u3002\u6211\u4e5f\u4e0d\u6d3b\u3002\u201d\u829d \u5a1c\u9053\uff1a\u201c\u4e0d\uff0c\u8fd8\u662f\u6d3b\u7740\u597d\u3002\u591a\u5c11\u4e8b\u60c5\u8fd8\u8981\u4f60\u505a\u5462\u3002\u518d\u8bf4\uff0c\u6211\u4e5f\u672a\u5fc5\u51c6\u6b7b\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u90a3 \u4e48\uff0c\u6211\u5c31\u7b49\u7740\u4f60\uff0c\u4e0d\u7ba1\u4f60\u662f\u6b7b\u662f\u6d3b\uff0c\u6211\u90fd\u7b49\u7740\u4f60\u3002\u201d\u829d\u5a1c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5566\u3002\u4f60\u77e5 \u9053\u6211\u73b0\u5728\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u8fd9\u4e00\u751f\u4e0d\u7ba1\u662f\u6b7b\u662f\u6d3b\uff0c\u6c38\u4e0d\u80fd\u548c\u7537\u5b50\u76f8\u7231\u76f8\u4eb2\u3002\u6211\u6b64\u6b21\u6765\u5df2\u7ecf\u662f\u72af\u4e86 \u6212\u5f8b\u5566\u3002\u5929\u5b87\uff0c\u8fd8\u662f\u8bf7\u4f60\u628a\u8fd9\u6b21\u76f8\u805a\u5f53\u4f5c\u4e00\u573a\u6625\u68a6\u7684\u597d\uff01\u201d\u9648\u5929\u5b87\u4e00\u770b\uff0c\u53ea\u89c1\u5979\u767d\u8863\u5982\u96ea\uff0c \u8138\u4e0a\u5ffd\u7136\u6cdb\u51fa\u4e00\u5c42\u5723\u6d01\u7684\u5149\u6d01\uff0c\u5979\u521a\u624d\u8bf4\u8fc7\u5192\u4e86\u7edd\u5927\u5371\u9669\uff0c\u624d\u80fd\u6765\u6b64\u4f5c\u4e00\u77ac\u95f4\u7684\u805a\u4f1a\u3002\u9648\u5929 \u5b87\u60ca\u7591\u4ea4\u5e76\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff0c\u6211\u77e5\u9053\u4f60\u662f\u6c81\u5e03\u85e9\u738b\u7684\u5973\u513f\u3002\u662f\u4e0d\u662f\u4f60\u4eec\u7684\u4e60\u4fd7\uff0c\u85e9\u738b\u7684\u5973\u513f \u4e0d\u80fd\u4e0b\u5ac1\u6c49\u4eba\uff1f\u201d\u897f\u85cf\u7684\u85e9\u738b\u786e\u4e4e\u6709\u8fd9\u4e2a\u89c4\u77e9\uff0c\u4f46\u9648\u5929\u5b87\u5374\u731c\u5f97\u9519\u4e86\uff0c\u829d\u5a1c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8fd9\u4e2a\u3002 \u3000\u3000\u9648\u5929\u5b87\u53c8\u53eb\u9053\uff1a\u201c\u82e5\u7136\u5982\u6b64\uff0c\u90a3\u6211\u5c31\u7ec8\u8eab\u4e0d\u5a36\u3002\u201d\u829d\u5a1c\u8f7b\u8f7b\u4e3e\u8896\uff0c\u62ed\u4e86\u773c\u89d2\u7684\u6cea\u73e0\uff0c\u5ffd \u7136\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6211\u6b64\u751f\u7684\u7b2c\u4e00\u4e2a\u77e5\u5df1\u3002\u4f60\u7684\u5feb\u4e50\u5c31\u662f\u6211\u7684\u5feb\u4e50\uff0c\u6211\u613f\u610f\u89c1\u5230\u4f60\u7ec8\u751f\u5feb\u4e50\uff0c \u4f60\u77e5\u9053\u4e48\uff1f\u201d\u9648\u5929\u5b87\u5fc3\u60c5\u52a8\u8361\uff0c\u829d\u5a1c\u6536\u4e86\u773c\u6cea\uff0c\u4ed6\u7684\u773c\u6cea\u5374\u4e0d\u81ea\u7981\u5730\u593a\u7736\u800c\u51fa\uff0c\u54ac\u54bd\u8bf4\u9053\uff1a \u201c\u55ef\uff0c\u6211\u77e5\u9053\uff01\u201d\u829d\u5a1c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u5c31\u542c\u6211\u8bf4\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u829d\u5a1c\uff0c\u53ea\u89c1\u829d\u5a1c\u773c\u775b\u9aa4\u7136\u660e\u4eae\uff0c\u5c04\u81ea\u4e00\u79cd\u4ee4\u4eba\u5fc3\u9189\u7684\u5149\u8f89\uff0c\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u5f85\u6211\u5f88\u597d\uff0c\u5979\u662f\u6211\u7684\u53c8\u4e00\u4e2a\u77e5\u5df1\uff0c\u6211\u628a\u5979\u5f53\u6210\u59d0\u59d0\u4e00\u822c\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u55ef\u3001\u6211\u77e5\u9053\uff0c\u6211\u4e5f\u66fe\u5f97\u8fc7\u5979\u8bb8\u591a\u597d\u5904\uff0c\u5f88\u611f\u6fc0\u5979\u3002\u201d\u829d\u5a1c\u9053\uff1a\u5979\u6bd4\u6211\u798f\u6c14\u7684\u591a\uff0c\u5510\u7ecf\u5929\u5bf9 \u5979\u4e00\u7247\u75f4\u60c5\uff0c\u55ef\uff0c\u5c31\u50cf\u4f60\uff0c\u4f60\u3002\u3002\u3002\u201d\u5979\u672c\u60f3\u8bf4\uff1a\u201c\u5c31\u8c61\u4f60\u5bf9\u6211\u4e00\u6837\u3002\u201d\u8138\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u4e0d\u4e0b \u53bb\u4e86\u3002\u9648\u5929\u5b87\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u6211\u7684\u672c\u4e8b\u6bd4\u4e0d\u4e0a\u5510\u7ecf\u5929\uff0c\u4f46\u81ea\u95ee\u5bf9\u4eba\u7684\u771f\u8bda\uff0c\u5374\u4e0e\u4ed6\u5e76\u65e0\u4e8c \u81f4\u3002\u201d\u4ed6\u4e0d\u987b\u591a\u8bf4\uff0c\u5df2\u731c\u5230\u4e86\u829d\u5a1c\u6240\u8981\u8bf4\u7684\u8bdd\u3002\u829d\u5a1c\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd9\u4e00\u7b11\u50cf\u521d\u7efd\u7684\u84d3\u857e\uff0c\u626b\u9664 \u4e86\u8138\u4e0a\u7684\u5fe7\u90c1\uff0c\u90a3\u662f\u771f\u6b63\u51fa\u4e8e\u5185\u5fc3\u6b22\u6109\u7684\u5fae\u7b11\uff0c\u53ea\u542c\u5f97\u5979\u53c8\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u7684\u7b2c\u4e09\u4e2a \u77e5\u5df1\u5219\u662f\u51b0\u5ddd\u5929\u5973\u7684\u4f8d\u5973\u5e7d\u840d\uff0c\u5979\u5feb\u4e50\u65e0\u6101\uff0c\u60f9\u4eba\u559c\u7231\uff0c\u8c01\u82e5\u548c\u5979\u76f8\u5904\uff0c\u5fc5\u7136\u5f97\u5230\u5feb\u4e50\u3002\u201d \u9648\u5929\u5b87\u5fc3\u5934\u4e00\u9707\uff0c\u201c\u829d\u5a1c\u8bf4\u8fd9\u756a\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4ed6\u4e0d\u613f\u610f\u7ec6\u5fc3\u63a8\u6572\uff0c\u6fc0\u52a8\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u613f \u4e0e\u4f60\u6c38\u8fdc\u76f8\u805a\u3002\u4e16\u4e0a\u518d\u6ca1\u6709\u4efb\u4f55\u5feb\u4e50\uff0c\u53ef\u4ee5\u4e0e\u4f60\u7ed9\u6211\u7684\u76f8\u6bd4\uff01\u201d \u3000\u3000\u829d\u5a1c\u53c8\u62ac\u8d77\u773c\u775b\u4ef0\u671b\uff0c\u6708\u4eae\u5feb\u8981\u843d\u4e0b\u53bb\u4e86\u3002\u829d\u5a1c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u771f\u7684\u8981\u8d70\u5566\uff01\u201d\u9648\u5929\u5b87 \u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e0d\u8981\u8d70\uff01\u201d\u829d\u5a1c\u9053\uff1a\u201c\u8fdf\u65e9\u90fd\u8981\u5206\u624b\uff0c\u4f60\u770b\u5f00\u4e00\u4e9b\uff0c\u5fc3\u4e2d\u5c31\u4e0d\u4f1a\u6101\u95f7\u4e86\u3002\u201d \u9648\u5929\u5b87\u7d27\u7d27\u7275\u7740\u5979\u7684\u8863\u8896\uff0c\u5ffd\u542c\u5f97\u545c\u545c\u7684\u949f\u58f0\uff0c\u968f\u7740\u665a\u98ce\u5439\u6765\uff0c\u65ad\u65ad\u7eed\u7eed\uff0c\u829d\u5a1c\u6570\u9053\uff1a \u201c\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u2026\u2026\u5341\u4e8c\u3001\u5341\u4e09\u3001\u2026\u2026\u5341\u516d\u3001\u5341\u4e03\u3001\u5341\u516b\u3002\u201d\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u4f60\u6570\u8fd9\u949f\u58f0\u505a \u4ec0\u4e48\uff1f\u8fd9\u662f\u6cd5\u738b\u884c\u5bab\u7684\u949f\u58f0\u3002\u201d\u829d\u5a1c\u9053\uff1a\u201c\u5c31\u8981\u505a\u65e9\u8bfe\u4e86\u3002\u201d\u9648\u5929\u5b87\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u65e9\u8bfe\uff1f\u201d \u829d\u5a1c\u907f\u5f00\u4e86\u9648\u5929\u5b87\u7684\u773c\u5149\uff0c\u5ffd\u9053\uff1a\u201c\u6cd5\u738b\u6765\u4e86\uff0c\u8428\u8fe6\u53ef\u771f\u70ed\u95f9\u3002\u8fc7\u4e24\u5929\u5c31\u662f\u5587\u561b\u5bfa\u7684\u5f00\u5149\u5927 \u5178\u5566\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4ec0\u4e48\u70ed\u95f9\u90fd\u96be\u4ee4\u6211\u52a8\u5fc3\u3002\u82e5\u7136\u4e0d\u662f\u548c\u4f60\u4e00\u8d77\uff0c\u6211\u4e5f\u4e0d\u60f3\u53bb\u770b\u4ec0\u4e48\u5f00\u5149 \u5927\u5178\u3002\u201d\u829d\u5a1c\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u53bb\u770b\u4e5f\u597d\u3002\u90a3\u4e48\u54b1\u4eec\u5c31\u6b64\u5206\u522b\u5566\uff01\u201d\u62bd\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u7a81\u7136 \u4e00\u5212\uff0c\u5c06\u9648\u5929\u5b87\u62c9\u7740\u5979\u7684\u90a3\u6bb5\u8863\u8896\u5207\u4e0b\u53bb\u3002 \u3000\u3000\u9648\u5929\u5b87\u6b63\u5728\u7528\u529b\uff0c\u5ffd\u7136\u5931\u4e86\u91cd\u5fc3\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u53ea\u89c1\u829d\u5a1c\u5df2\u8df3\u4e0a\u5899\u5934\uff0c\u7ffb\u8fc7\u53bb\u4e86\u3002\u56de\u5934\u4e00 \u77a5\uff0c\u90a3\u773c\u5149\u5145\u6ee1\u65e0\u9650\u60b2\u82e6\uff0c\u65e0\u9650\u7737\u604b\uff0c\u800c\u53c8\u662f\u7a81\u7136\u8bc0\u522b\u7684\u795e\u6c14\u3002\u9648\u5929\u5b87\u672c\u6765\u53ef\u4ee5\u8ffd\u4e0a\u5979\uff0c\u4f46 \u8ffd\u4e0a\u4e86\u4e5f\u96be\u4ee5\u633d\u56de\u8fd9\u8bc0\u522b\u7684\u547d\u8fd0\uff0c\u9648\u5929\u5b87\u4f46\u611f\u4e00\u7247\u832b\u7136\uff0c\u4e0d\u77e5\u6b64\u8eab\u4f55\u5904\uff01\u829d\u5a1c\u7684\u6b4c\u58f0\u72b9\u4f3c\u5728 \u8033\u8fb9\u7edf\u7ed5\uff1a\u201c\u6c38\u6052\u7684\u7231\u60c5\u77ed\u800c\u660e\u4eae\uff0c\u50cf\u9ed1\u591c\u7684\u5929\u7a7a\u6479\u5730\u7535\u5149\u4e00\u95ea\uff0c\u867d\u65cb\u5373\u53c8\u5f52\u4e8e\u6f20\u6f20\u7684\u957f \u7a7a\uff0c\u4f46\u5df2\u7167\u89c1\u4e86\u60c5\u4eba\u6700\u7f8e\u7684\u5f62\u8c61\u3002\u201d\u829d\u5a1c\u7684\u534a\u622a\u8896\u5b50\u5c1a\u5728\u624b\u4e2d\uff0c\u8863\u8896\u4e0a\u4e00\u7247\u6da6\u6e7f\uff0c\u4e5f\u4e0d\u77e5\u662f \u829d\u5a1c\u7684\u6cea\u8fd8\u662f\u81ea\u5df1\u7684\u6cea\u3002 \u3000\u3000\u9648\u5929\u5b87\u72ec\u7acb\u56ed\u4e2d\uff0c\u4e0d\u89c9\u5df2\u662f\u5929\u660e\uff0c\u5bb6\u4eba\u4eec\u5728\u57ce\u4e2d\u8fc7\u4e86\u4e00\u4e2a\u72c2\u6b4c\u4e4b\u591c\uff0c\u90fd\u56de\u6765\u4e86\u3002\u4ed6\u4eec\u5e76 \u4e0d\u77e5\u9053\u5c11\u7237\u4e00\u591c\u672a\u7761\uff0c\u7eb7\u7eb7\u5728\u90a3\u91cc\u8c08\u8bb2\u8fce\u63a5\u6cd5\u738b\u7684\u70ed\u95f9\u60c5\u666f\u3002\u6709\u4e00\u4e2a\u4eba\u9053\uff1a\u53ef\u60dc\u90a3\u7fa4\u5723\u5973\u90fd \u62ab\u7740\u9762\u7eb1\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd9\u8d70\u51fa\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5723\u5973\uff1f\u201d\u53bb\u770b\u4e86\u70ed\u95f9\u7684\u5bb6\u4eba\u4e03\u53e3\u516b\u820c\u4ed6\u8bf4 \u9053\uff1a\u201c\u5c31\u662f\u6d3b\u4f5b\u5e26\u6765\u7684\u5723\u5973\u5440\uff01\u54c8\uff0c\u8fd9\u4e2a\u767d\u5587\u561b\u6559\u53ef\u4e0e\u9ec4\u6559\u4e0d\u540c\uff0c\u6536\u4e86\u8bb8\u591a\u6f02\u4eae\u7684\u5c11\u5973\u505a\u5587 \u561b\uff01\u201d\u542c\u8bf4\u8fd9\u4e9b\u5723\u5973\u4e2a\u4e2a\u80fd\u6b4c\u5584\u821e\uff0c\u5230\u5587\u561b\u5bfa\u5f00\u5149\u4e4b\u65f6\uff0c\u5979\u4eec\u90fd\u8981\u51fa\u6765\u7ed9\u6211\u4eec\u770b\u5462\uff01\u201d\u201c\u5c31 \u53ef\u60dc\u7f69\u7740\u9762\u7eb1\u3002\u201d\u201c\u5979\u4eec\u7684\u88c5\u675f\u771f\u6f02\u4eae\uff0c\u66f3\u7740\u767d\u8272\u7684\u957f\u88d9\uff0c\u7ea4\u8170\u4e00\u6eba\uff0c\u98d8\u7740\u4e24\u6761\u7ef8\u5e26\uff0c\u884c\u8d77 \u8def\u6765\u8885\u8885\u5a1c\u5a1c\uff0c\u771f\u4f3c\u5ab3\u5a25\u4e0b\u754c\uff0c\u4ed9\u5b50\u4e34\u51e1\uff01\u201d\u201c\u4f60\u522b\u5fc3\u90aa\u5566\uff0c\u542c\u8bf4\u5723\u5973\u662f\u767d\u5587\u561b\u6559\u4e2d\u6700\u5723\u6d01 \u4e0d\u53ef\u5192\u72af\u7684\u4eba\uff0c\u82e5\u7136\u4e0d\u662f\u5979\u4eec\u6765\u8d74\u76db\u4f1a\uff0c\u5077\u770b\u5979\u4eec\u4e00\u773c\u4e5f\u662f\u6709\u7f6a\u7684\u3002\u201d\u201c\u5979\u4eec\u80fd\u4e0d\u80fd\u5ac1 \u4eba\uff1f\u201d\u201c\u548c\u6559\u5916\u7684\u7537\u4eba\u8bf4\u8bdd\u90fd\u4e0d\u53ef\u4ee5\uff0c\u8fd8\u8bf4\u5ac1\u4eba\u5462\uff1f\u201d\u201c\u5440\uff0c\u5440\uff0c\u771f\u53ef\u60dc\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u5e73\u7d20\u4e0e\u5bb6\u4eba\u65e0\u751a\u62d8\u675f\uff0c\u6240\u4ee5\u5bb6\u4eba\u4eec\u4e5f\u5728\u4ed6\u9762\u524d\u8c08\u7b11\u65e0\u5fcc\u3002\u9648\u5929\u5b87\u4e00\u8a00\u4e0d\u53d1\uff0c\u9759\u542c \u4ed6\u4eec\u63cf\u7ed8\u767d\u6559\u5723\u5973\u7684\u88c5\u675f\uff0c\u7adf\u7136\u5c31\u662f\u829d\u5a1c\u6628\u591c\u7684\u88c5\u675f\u3002\u201c\u83ab\u975e\u829d\u5a1c\u505a\u4e86\u5723\u5973\uff1f\u201d\u829d\u5a1c\u4e3a\u4ec0\u4e48 \u8981\u505a\u5723\u5973\uff1f\u201d\u9648\u5929\u5b87\u60c5\u601d\u660f\u660f\uff0c\u6709\u5982\u4e71\u4e1d\uff0c\u6108\u60f3\u6108\u4e71\u3002 \u3000\u3000\u7236\u4eb2\u5927\u7ea6\u662f\u5fd9\u4e8e\u63a5\u5f85\u767d\u6559\u6cd5\u738b\uff0c\u6628\u665a\u5728\u571f\u53f8\u5bb6\u4e2d\u8fc7\u591c\uff0c\u76f4\u81f3\u4e2d\u5348\u8fd8\u672a\u56de\u6765\uff0c\u9648\u5929\u5b87\u72ec\u81ea \u5750\u5728\u4e66\u623f\uff0c\u4e0d\u65ad\u5730\u5728\u60f3\u829d\u5a1c\u8fd9\u79cd\u795e\u79d8\u7684\u884c\u52a8\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u63d0\u8d77\u7b14\u5728\u7eb8\u4e0a\u4e71\u753b\uff0c\u753b\u4e86\u8bb8\u591a\u829d\u5a1c \u7684\u50cf\uff0c\u53c8\u5728\u7eb8\u4e0a\u5199\u4e86\u65e0\u6570\u829d\u5a1c\u7684\u540d\u5b57\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u5bb6\u4eba\u547c\u5524\uff0c\u9648\u5929\u5b87\u5982\u68a6\u521d\u9192\uff0c\u770b\u7740\u6ee1\u7eb8 \u201c\u829d\u5a1c\u201d\u4f3c\u6b32\u5728\u753b\u4e2d\u8df3\u51fa\uff0c\u5fc3\u91cc\u4e00\u9178\uff0c\u5374\u53c8\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff01 \u3000\u3000\u5bb6\u4eba\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5916\u9762\u6709\u4eba\u627e\u4f60\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u76b1\u76b1\u7709\u5934\uff0c\u6325\u624b\u8bf4\u9053\uff1a \u201c\u4eca\u5929\u6211\u4e0d\u60f3\u89c1\u5ba2\uff0c\u4f60\u60f3\u4e2a\u6cd5\u5b50\u7ed9\u6211\u56de\u4e86\u5427\u3002\u201d\u5bb6\u4eba\u5e94\u4e86\u4e00\u58f0\u3001\u201c\u662f\u201d\uff0c\u5374\u8fdf\u8fdf\u7591\u7591\uff0c\u7ad9\u5728 \u4e66\u623f\u95e8\u53e3\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5bb6\u4eba\u9053\uff1a\u201c\u8fd9\u4eba\u8bf4\uff0c\u4ed6\u548c\u516c\u5b50\u662f\u597d\u670b\u53cb\u3002\u975e\u89c1\u4f60\u4e0d\u53ef\u3002\u7ba1 \u5bb6\u7684\u5df2\u8bf7\u4ed6\u8fdb\u6765\u4e86\u3002\u201d\u9648\u5929\u5b87\u5947\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u5fc3\u4e2d\u9887\u602a\u90a3\u4e2a\u7ba1\u5bb6\u672a\u66fe\u7980\u62a5\uff0c\u5c31\u64c5\u4f5c\u4e3b \u5f20\u3002\u5bb6\u4eba\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4e2a\u5c11\u5e74\u4e66\u751f\uff0c\u4ed6\u8bf4\u4ed6\u59d3\u5510\u3002\u7ba1\u5bb6\u7684\u6084\u6084\u544a\u8bc9\u6211\uff0c\u8bf4\u662f\u8fd9\u4e2a\u4eba\u66fe\u5e2e\u8fc7\u8001 \u7237\u7684\u5927\u5fd9\u3002\u201d\u9648\u5929\u5b87\u201c\u5475\u5440\u201d\u4e00\u58f0\uff0c\u6765\u4e0d\u53ca\u6362\u8863\u670d\uff0c\u6025\u5fd9\u8dd1\u51fa\u53bb\u8fce\u63a5\u3002 \u3000\u3000\u53ea\u89c1\u6765\u7684\u5ba2\u4eba\u679c\u7136\u662f\u5510\u7ecf\u5929\u3002\u539f\u6765\u90a3\u8001\u7ba1\u5bb6\u5f53\u5e74\u66fe\u968f\u9648\u5b9a\u57fa\u53bb\u8fce\u63a5\u91d1\u74f6\uff0c\u6240\u4ee5\u8ba4\u5f97\u5510\u7ecf \u5929\u3002\u4e24\u4eba\u4e00\u89c1\uff0c\u6b22\u559c\u65e0\u9650\uff0c\u9648\u5929\u5b87\u7d27\u7d27\u63e1\u7740\u5510\u7ecf\u5929\u53cc\u624b\uff0c\u53eb\u9053\uff1a\u201c\u5510\u5144\uff0c\u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u5230\u8fd9 \u513f\u6765\uff1f\u771f\u662f\u60f3\u6b7b\u5c0f\u5f1f\u5566\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u8def\u8fc7\u6b64\u5730\uff0c\u7279\u6765\u62dc\u5019\u3002\u54c8\uff0c\u4f60\u4eec\u8fd9\u513f\u53ef\u70ed\u95f9 \u54e9\u3002\u201d\u9648\u5929\u5b87\u89c1\u4ed6\u4e5f\u4f3c\u6709\u6ee1\u6000\u5fc3\u4e8b\u7684\u6837\u5b50\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u53bb\u8c08\u8c08\u3002\u201d\u643a\u624b\u8fdb\u5165\u4e66\u623f\uff0c\u8ba9\u5510\u7ecf \u5929\u5750\u4e0b\uff0c\u6b63\u5728\u8bf7\u8336\uff0c\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u4f4e\u58f0\u547c\u9053\uff1a\u201c\u54a6\uff0c\u829d\u5a1c\uff0c\u829d\u5a1c\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u8df3\u4e86\u8d77\u6765\uff0c\u624b\u4e2d\u7aef\u7740\u7684\u8336\u676f\uff0c\u201c\u54e8\u561f\u201d\u4e00\u58f0\uff0c\u8dcc\u843d\u5730\u4e0a\uff0c\u788e\u6210\u7247\u7247\uff0c\u6025\u5fd9\u95ee\u9053\uff1a \u201c\u5510\u5144\uff0c\u4f60\u8ba4\u5f97\u829d\u5a1c\u5417\uff1f\u201d\u5510\u7ecf\u5929\u4f55\u7b49\u806a\u660e\uff0c\u4e00\u77a7\u9648\u5929\u5b87\u7684\u795e\u60c5\uff0c\u4fbf\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4ee5\u524d\u8bf4 \u8fc7\u7684\u90a3\u4f4d\u85cf\u65cf\u5c11\u5973\uff0c\u4fbf\u662f\u829d\u5a1c\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u5979\u4e86\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211 \u66fe\u5728\u9752\u6d77\u7684\u767d\u6559\u6cd5\u738b\u5bab\u4e2d\uff0c\u89c1\u8fc7\u5979\u4e00\u9762\u3002\u53ef\u60dc\u6211\u90a3\u65f6\u5019\u4e0d\u77e5\u9053\u5979\u5c31\u662f\u4f60\u7684\u610f\u4e2d\u4eba\uff0c\u8981\u4e0d\u7136\u6211 \u4e00\u5b9a\u66ff\u4f60\u529d\u5979\uff0c\u53eb\u5979\u4e0d\u8981\u505a\u4ec0\u4e48\u635e\u4ec0\u5b50\u7684\u5723\u5973\u4e86\u3002\u201d\u5c06\u5f53\u65e5\u5728\u6cd5\u738b\u5bab\u4e2d\u6240\u89c1\uff0c\u53ca\u540e\u6765\u591c\u63a2\u5723 \u5973\u5bab\uff0c\u78b0\u89c1\u51b0\u5ddd\u5929\u5973\u4e3b\u4ec6\u4e0e\u829d\u5a1c\u540c\u5728\u4e00\u5904\u7b49\u7b49\u60c5\u4e8b\uff0c\u4ed4\u7ec6\u8bf4\u8bdd\u4e86\u4e00\u904d\u3002\u9648\u5929\u5b87\u832b\u7136\u82e5\u5931\uff0c\u5583 \u5583\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5979\u662f\u81ea\u5df1\u7518\u5fc3\u505a\u5723\u5973\u7684\uff0c\u8fd9\u3001\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u4e24\u4eba\u4ed4\u7ec6\u53c2\u8be6\uff0c\u731c\u4e0d\u900f\u829d\u5a1c\u7684\u7528\u610f\u3002\u9ec4\u660f\u65f6\u5206\uff0c\u9648\u5929\u5b87\u7684\u7236\u4eb2\u56de\u6765\uff0c\u542c\u8bf4\u5510\u7ecf\u5929\u6765\u8bbf\uff0c \u751a\u662f\u9ad8\u5174\uff0c\u867d\u7136\u7cbe\u795e\u75b2\u5026\uff0c\u4ecd\u7136\u63a5\u89c1\u4e86\u4ed6\u3002\u9648\u5929\u5b87\u968f\u4f8d\u5728\u4eec\u3002\u9648\u5b9a\u585e\u548c\u5510\u7ecf\u5929\u5bd2\u6684\u4e4b\u540e\uff0c\u81ea \u7136\u800c\u7136\u5730\u8c08\u5230\u4e86\u767d\u6559\u6cd5\u738b\u6765\u5230\u8428\u8fe6\u7684\u4e8b\u3002\u8bf4\u5230\u4e86\u90a3\u73ed\u5723\u5973\uff0c\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u571f\u53f8\u672c\u60f3\u5728\u4ed6\u7684\u5821 \u5792\u4e2d\u56f4\u8d77\u4e00\u5904\u5730\u65b9\uff0c\u62db\u5f85\u8fd9\u73ed\u5723\u5973\u7684\u3002\u571f\u53f8\u60f3\u53eb\u4ed6\u7684\u5973\u5974\u53bb\u8ddf\u968f\u8fd9\u73ed\u5723\u5973\u5b66\u62dc\u795e\u7684\u821e\u8e48\u5462\u3002 \u6cd5\u738b\u8d77\u521d\u5e76\u4e0d\u62d2\u7edd\uff0c\u540e\u6765\u542c\u8bf4\u5723\u6bcd\u4e0d\u5141\uff0c\u5b81\u53ef\u5728\u6cd5\u738b\u884c\u5bab\u7684\u82b1\u56ed\u4e2d\u53e6\u5916\u95f4\u5f00\u4e00\u5904\u5730\u65b9\uff0c\u8ba9\u8fd9 \u73ed\u5723\u5973\u8fdb\u53bb\u4f4f\u3002\u571f\u53f8\u751a\u4e3a\u626b\u5174\uff0c\u53ef\u4ea6\u65e0\u53ef\u5982\u4f55\u3002\u201d\u9648\u5929\u5b87\u542c\u4e86\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6ca1\u8bf4\u4ec0\u4e48\u3002\u4e0d \u4e45\uff0c\u4ed6\u7684\u7236\u4eb2\u56e0\u4e3a\u7cbe\u795e\u592a\u8fc7\u75b2\u5026\uff0c\u5411\u5510\u7ecf\u5929\u544a\u4e86\u4e2a\u7f6a\uff0c\u8fdb\u5185\u6b47\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5510\u7ecf\u5929\u56de\u5230\u4e66\u623f\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u6211\u60f3\u53bb\u63a2\u671b\u829d\u5a1c\u3002\u201d\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a \u201c\u6cd5\u738b\u7684\u884c\u5bab\uff0c\u5c82\u662f\u53ef\u4ee5\u968f\u4fbf\u53bb\u7684\uff1f\u6211\u53bb\u5e74\u53bb\u63a2\u5723\u5973\u5bab\uff0c\u4e5f\u51e0\u4e4e\u8131\u4e0d\u4e86\u8eab\u5462\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u5c31\u662f\u6c34\u91cc\u706b\u91cc\uff0c\u7c89\u9aa8\u788e\u8eab\uff0c\u6211\u4e5f\u8981\u518d\u89c1\u5979\u4e00\u9762\u3002\u5440\uff0c\u5c31\u662f\u4e0d\u80fd\u548c\u5979\u8bf4\u8bdd\uff0c\u5077\u5077\u5730\u77a7\u5979\u4e00 \u773c\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d\u773c\u5149\u4e2d\u5145\u6ee1\u6e34\u671b\u4e0e\u51c4\u6028\uff0c\u8fd9\u662f\u82e6\u604b\u4e2d\u7684\u60c5\u4eba\u7684\u773c\u5149\u3002\u5510\u7ecf\u5929\u61c2\u5f97\u8fd9\u4e2a\u773c \u5149\uff0c\u4ed6\u81ea\u5df1\u4e5f\u66fe\u6709\u8fc7\u4e0e\u9648\u5929\u5b87\u76f8\u4f3c\u7684\u5fc3\u60c5\uff0c\u4e0d\u7531\u5f97\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u4eba\u95f4\u4ea6\u6709\u75f4\u5982 \u6211\uff0c\u5c82\u72ec\u4f24\u5fc3\u662f\u5c0f\u9752\u3002\u597d\u5427\uff0c\u4eca\u65e5\u6211\u5c31\u966a\u4f60\u53bb\u8d70\u4e00\u8d9f\u3002\u201d\u5510\u7ecf\u5929\u662f\u987e\u8651\u5230\u9648\u5929\u5b87\u53ef\u80fd\u88ab\u9677\u5bab \u4e2d\uff0c\u6240\u4ee5\u613f\u966a\u4ed6\u540c\u53bb\u3002\u9648\u5929\u5b87\u6b22\u559c\u65e0\u9650\uff0c\u7d27\u63e1\u7740\u5510\u7ecf\u5929\u7684\u624b\uff0c\u597d\u4e45\u597d\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u597d\u597d\u7684\u7761\u4e00\u89c9\uff0c\u517b\u8db3\u7cbe\u795e\u5427\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u7761\u4e0d\u7740\uff0c\u5510\u5144\uff0c \u6211\u5fc3\u6025\u7740\u5462\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u518d\u5fc3\u6025\u4e5f\u8981\u7b49\u5230\u4e09\u66f4\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u5c31\u95f2\u804a\u6253\u53d1 \u65f6\u5149\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u5411\u4f60\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e00 \u4e2a\u75af\u75af\u766b\u766b\uff0c\u5230\u5904\u60f9\u4e8b\u7684\u4e5e\u4e10\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u524d\u51e0\u5929\u6211\u542c\u5bb6\u4eba\u8bf4\u8d77\uff0c\u6709\u4e00\u4e2a\u50bb\u91cc\u50bb\u6c14\u7684\u5c11 \u5e74\uff0c\u5728\u8857\u4e0a\u8d70\u8fc7\uff0c\u4e00\u8fb9\u8d70\u4e00\u8fb9\u628a\u7cd6\u679c\u997c\u98df\u548c\u94dc\u94b1\u629b\u7ed9\u8ddf\u5728\u4ed6\u8eab\u8fb9\u7684\u5c0f\u5b69\u5b50\uff0c\u53ef\u662f\u8fd9\u5c11\u5e74\u8863\u670d \u5149\u9c9c\uff0c\u5374\u4e0d\u662f\u4ec0\u4e48\u4e5e\u4e10\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5462\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u540e\u6765\u5c31\u4e0d\u77e5\u6d88\u606f\u4e86\u3002\u8fd9\u51e0\u5929\u5927\u5bb6\u90fd\u5fd9\u7740 \u63a5\u5f85\u6cd5\u738b\u7684\u4e8b\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4eba\u518d\u53bb\u7559\u610f\u4ed6\u3002\u6211\u4e5f\u53ea\u662f\u5f53\u505a\u4e00\u4ef6\u6709\u8da3\u7684\u4e8b\u60c5\uff0c\u542c\u8fc7\u5c31\u7b97\u4e86\u3002\u201d \u5510\u7ecf\u5929\u9ed8\u9ed8\u51dd\u601d\u535c\u5fc3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u91d1\u4e16\u9057\u5df2\u5230\u4e86\u8428\u8fe6\uff0c\u4ed6\u559c\u6b22\u70ed\u95f9\uff0c\u653e\u7740\u8fd9\u4e2a\u5587\u561b\u5bfa\u7684 \u5f00\u5149\u5927\u5178\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u80af\u9519\u8fc7\u3002\u201d\u9648\u5929\u5b87\u95ee\u9053\uff1a\u201c\u5510\u5144\u6253\u542c\u8fd9\u4e2a\u4eba\u505a\u4ec0\u4e48\uff1f\u770b\u4f60\u4e5f\u4f3c\u5fc3\u4e2d\u6709 \u4e8b\uff0c\u53ef\u4ee5\u8bf4\u6765\u542c\u542c\u5417\uff1f\u201d\u5510\u7ecf\u5929\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7684\u4e8b\u6ca1\u4f60\u90a3\u6837\u4f24\u5fc3\uff0c\u53ef\u4e5f\u9ebb\u70e6\u5f97\u5f88\u3002\u6211\u8981\u53bb \u6551\u4e00\u4e2a\u6211\u6240\u4e0d\u559c\u6b22\u7684\u4eba\uff0c\u8fd9\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u54b3\uff0c\u5c06\u6765\u6211\u518d\u548c\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u5728\u5510\u7ecf\u5929\u82e6\u529d\u4e0b\uff0c\u9759\u5750\u4e86\u4e00\u4f1a\u3002\u5510\u7ecf\u5929\u7528\u672c\u8eab\u7684\u5185\u529f\u52a9\u4ed6\u5b81\u795e\u5410\u7eb3\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31 \u5230\u4e86\u4e09\u66f4\u3002\u4e24\u4eba\u6362\u4e0a\u4e86\u591c\u884c\u8863\uff0c\u4fbf\u5230\u6cd5\u738b\u7684\u884c\u5bab\u53bb\u3002 \u3000\u3000\u6cd5\u738b\u7684\u884c\u5bab\u501a\u5c71\u5efa\u7b51\uff0c\u90a3\u672c\u6765\u662f\u4e00\u4e2a\u6d85\u5df4\uff08\u897f\u85cf\u5b98\u8854\uff0c\u571f\u53f8\u4e4b\u4e0b\u7684\u5927\u7ba1\u4e8b\u3002\uff09\u7684\u5e9c\u90b8\uff0c \u4e3a\u4e86\u62db\u5f85\u6cd5\u738b\uff0c\u4e09\u4e2a\u6708\u4e4b\u524d\uff0c\u571f\u53f8\u5c31\u8981\u90a3\u4e2a\u6d85\u5df2\u5168\u5bb6\u642c\u4e86\u51fa\u6765\uff0c\u91cd\u52a0\u4fee\u5efa\uff0c\u91cc\u91cc\u5916\u5916\uff0c\u5e03\u7f6e \u5f97\u5341\u5206\u5802\u7687\u5bcc\u4e3d\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ef\u671b\u89c1\u884c\u5bab\u5c16\u9876\u94dc\u5854\u7684\u7409\u7483\u706f\u5149\u3002\u9648\u5929\u5b87\u5fc3\u6025\u975e\u5e38\uff0c\u65bd\u5c55\u8f7b \u529f\uff0c\u51e0\u4e4e\u811a\u4e0d\u6cbe\u5730\uff0c\u5510\u7ecf\u5929\u8ddf\u4ed6\u98de\u8dd1\uff0c\u4e5f\u89c9\u5f97\u6709\u70b9\u513f\u5403\u529b\uff0c\u5fc3\u4e2d\u5927\u662f\u60ca\u8be7\uff0c\u60f3\u4e0d\u5230\u5e74\u591a\u4e0d \u89c1\uff0c\u9648\u5929\u5b87\u7684\u8f7b\u529f\u7adf\u7136\u7cbe\u8fdb\u5982\u65af\uff01\u5510\u7ecf\u5929\u6709\u6240\u4e0d\u77e5\uff0c\u9648\u5929\u5b87\u662f\u5728\u51b0\u5b98\u4e2d\u673a\u7f18\u5de7\u5408\uff0c\u5403\u4e86\u4e00\u4e2a \u516d\u5341\u5e74\u624d\u7ed3\u679c\u4e00\u6b21\u3001\u6bcf\u6b21\u53ea\u7ed3\u679c\u4e00\u679a\u7684\u5f02\u679c\uff0c\u8981\u4e0d\u662f\u4ed6\u706b\u5019\u672a\u591f\uff0c\u672c\u8eab\u529f\u529b\u672a\u80fd\u914d\u5408\uff0c\u4ed6\u7684 \u8f7b\u529f\u5df2\u7ecf\u53ef\u4ee5\u72ec\u6b65\u5929\u4e0b\u3002 \u3000\u3000\u7528\u4e0d\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e24\u4eba\u5c31\u6765\u5230\u4e86\u6cd5\u738b\u7684\u884c\u5bab\uff0c\u98de\u8fdb\u82b1\u56ed\uff0c\u4f46\u89c1\u56ed\u4e2d\u4f73\u6728\u8471\u7b3c\uff0c\u5947\u82b1\u70c2 \u6f2b\uff0c\u6e05\u6d41\u66f2\u6298\uff0c\u5c71\u77f3\u7741\u5cd9\uff0c\u6709\u4e00\u5217\u7ea2\u697c\uff0c\u9690\u5728\u5c71\u62d7\u6811\u6284\u4e4b\u95f4\uff0c\u666f\u8272\u5728\u5e7d\u96c5\u4e4b\u4e2d\u663e\u5f97\u534e\u4e3d\u3002\u5510 \u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u77ed\u77ed\u4e09\u4e2a\u6708\u4e2d\uff0c\u5e03\u7f6e\u51fa\u5982\u6b64\u4e00\u5ea7\u795e\u4ed9\u6d1e\u5e9c\uff0c\u771f\u4e0d\u77e5\u8d39\u5c3d\u591a\u5c11\u4eba\u529b\u7269\u529b\u3002\u201d\u9648\u5929 \u5b87\u6b63\u60f3\u7ed5\u8fc7\u5047\u5c71\uff0c\u8df3\u4e0a\u7ea2\u697c\uff0c\u5510\u7ecf\u5929\u5ffd\u7136\u5c06\u4ed6\u4e00\u62c9\uff0c\u4e24\u4eba\u540c\u9690\u5728\u4e00\u5ea7\u5047\u5c71\u80cc\u540e\u3002 \u3000\u3000\u53ea\u542c\u5f97\u98d2\u7136\u98ce\u8fc7\uff0c\u4e09\u6761\u4eba\u5f71\u98de\u8fdb\u56ed\u4e2d\uff0c\u770b\u90a3\u8eab\u6cd5\u4e5f\u662f\u4e0a\u4e0a\u7684\u8f7b\u529f\uff0c\u843d\u4e0b\u6765\u65f6\uff0c\u53ea\u6709\u4e00\u4e2a \u4eba\u4f3c\u4e4e\u662f\u8e29\u7740\u788e\u77f3\uff0c\u53d1\u51fa\u8f7b\u5fae\u7684\u58f0\u54cd\u3002\u5176\u4ed6\u4e8c\u4eba\uff0c\u90fd\u5982\u4e00\u53f6\u98d8\u5815\uff0c\u843d\u5904\u65e0\u58f0\u3002\u8fd9\u4e09\u4e2a\u4eba\u4e00\u8df3 \u5165\u6765\uff0c\u56db\u9762\u4e00\u671b\uff0c\u4fbf\u5373\u548c\u4ed6\u4eec\u4e00\u6837\uff0c\u9690\u85cf\u5728\u4e00\u5ea7\u5047\u5c71\u540e\u9762\u3002 \u3000\u3000\u9648\u5929\u5b87\u548c\u5510\u7ecf\u5929\u8eb2\u5728\u5047\u5c71\u77f3\u7684\u7f1d\u9699\u4e2d\uff0c\u9690\u7ea6\u53ef\u89c1\u5230\u4ed6\u4eec\u7684\u80cc\u666f\u3002\u5176\u4e2d\u4e00\u4eba\uff0c\u4e5f\u5c31\u662f\u9002\u624d \u843d\u4e0b\u6765\u65f6\u53d1\u51fa\u58f0\u54cd\uff0c\u8f7b\u529f\u663e\u7136\u7a0d\u900a\u4e00\u7b79\u7684\u90a3\u4e2a\u3002\u4ed6\u7531\u4e8e\u8eab\u8eaf\u80a5\u80d6\uff0c\u8eb2\u5728\u5047\u5c71\u80cc\u540e\u3002\u7ed9\u540c\u4f34\u6324 \u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u628a\u8eab\u4f53\u7565\u7565\u5411\u5916\u5a1c\u52a8\uff0c\u4fa7\u8f6c\u8eab\u5f62\uff0c\u9732\u51fa\u9762\u90e8\u7684\u8f6e\u5ed3\u3002\u9648\u5929\u5b87\u4e00\u89c1\uff0c\u5403\u4e86\u4e00 \u60ca\uff0c\u539f\u6765\u8fd9\u4e2a\u4eba\u7adf\u7136\u662f\u571f\u53f8\u624b\u4e0b\u6700\u5f97\u5ba0\u4fe1\u7684\u4fc4\u9a6c\u767b\uff0c\u4e5f\u5c31\u662f\u4e24\u5e74\u524d\u5728\u6708\u591c\u8352\u5c71\u4e0a\u8ffd\u8e2a\u8fc7\u829d\u5a1c \u7684\u90a3\u4e2a\u4fc4\u9a6c\u767b\uff01 \u3000\u3000\u9648\u5929\u5b87\u4f0f\u5728\u5047\u5c71\u540e\u9762\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6781\u7ec6\u5fae\u7684\u8bdd\u8bed\u4f20\u4e86\u8fc7\u6765\uff0c\u82e5\u975e\u9648\u5929\u5b87\u66fe\u82e6\u7ec3\u8fc7\u201c\u542c\u98ce \u8fa8\u5668\u201d\u4e4b\u672f\uff0c\u8fd8\u51e0\u4e4e\u4ee5\u4e3a\u90a3\u662f\u8349\u866b\u537f\u537f\u3002\u90a3\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u77a7\u6e05\u695a\u4e86\uff1f\u679c\u7136\u662f\u6c81\u5e03\u85e9\u738b \u7684\u6c5f\u739b\u53e4\u4fee\uff1f\u201d\u968f\u5373\u53e6\u4e00\u4e2a\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u867d\u7136\u7f69\u4e86\u9762\u7eb1\uff0c\u603b\u7792\u4e0d\u8fc7\u6211\u7684\u773c\u775b\u3002\u201d\u6b63\u662f\u4fc4 \u9a6c\u767b\u7684\u58f0\u97f3\u3002\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u614e\uff0c\u60f3\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u4e3a\u4ec0\u4e48\u8fd9\u6837\u6ce8\u610f\u829d\u5a1c\uff1f\u4ed6\u6765\u8fd9\u91cc\u7aa5\u63a2\uff0c\u60f3\u4e5f \u662f\u4e3a\u4e86\u829d\u5a1c\u4e86\u3002\u201d\u9648\u5929\u5b87\u60f3\u8d77\u4e86\u829d\u5a1c\u521d\u5230\u8428\u8fe6\u90a3\u6b21\uff0c\u843d\u5728\u571f\u53f8\u624b\u4e2d\uff0c\u4fc4\u9a6c\u767b\u66fe\u8bf7\u8fc7\u81ea\u5df1\u7684\u7236 \u4eb2\u53bb\u63f4\u6551\uff0c\u4f46\u5176\u540e\u5374\u53c8\u4e00\u76f4\u8ffd\u8e2a\u829d\u5a1c\uff0c\u76f4\u81f3\u51b0\u5cf0\u3002\u4fc4\u9a6c\u767b\u5bf9\u829d\u5a1c\u662f\u597d\u610f\u8fd8\u662f\u574f\u610f\uff1f\u81f3\u4eca\u4ecd\u662f \u4e00\u4e2a\u96be\u89e3\u4e4b\u8c1c\u3002 \u3000\u3000\u5148\u5934\u90a3\u4e2a\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u6253\u7b97\u544a\u8bc9\u571f\u53f8\u5417\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u544a\u8bc9\u571f\u53f8\u6709\u597d\u5904\u4e5f\u6709\u574f \u5904\uff0c\u6700\u597d\u662f\u80fd\u591f\u89c1\u89c1\u829d\u5a1c\u3002\u53ef\u662f\uff0c\u53ef\u662f\u2026\u2026\u201d\u8bdd\u58f0\u5ffd\u5730\u590f\u7136\u5546\u6b62\u3002\u9648\u5929\u5b87\u62ac\u5934\u4e0a\u671b\uff0c\u4f46\u89c1\u7ea2 \u697c\u4e00\u89d2\uff0c\u5f00\u4e86\u4e00\u6247\u95e8\u6237\uff0c\u4e00\u4e2a\u62ab\u7740\u767d\u7eb1\u7684\u5c11\u5973\uff0c\u8f7b\u76c8\u8d70\u51fa\u697c\u6765\uff0c\u624b\u4e2d\u62b1\u7740\u4e00\u4ef6\u4e50\u5668\uff0c\u501a\u7740\u680f \u6746\uff0c\u51c0\u51c0\u743c\u743c\u7684\u5f39\u4e86\u8d77\u6765\uff0c\u4f4e\u58f0\u5531\u9053\uff1a \u3000\u3000\u201c\u5723\u5cf0\u7684\u51b0\u5ddd\u50cf\u5929\u6cb3\u5012\u6302\uff0c \u3000\u3000\u4f60\u542c\u90a3\u6d6e\u51b0\u6d41\u52a8\u8f7b\u8f7b\u7684\u54cd\u4e00\u3002 \u3000\u3000\u50cf\u662f\u59d1\u5a18\u7684\u5de7\u624b\u5f39\u8d77\u4e86\u4e1c\u4e0d\u62c9\u3002 \u3000\u3000\u5979\u5728\u95ee\u90a3\u6d41\u6d6a\u7684\u65c5\u4eba\uff1a \u3000\u3000\u4f60\u8fd8\u8981\u6500\u8d8a\u51e0\u5ea7\u51b0\u5c71\uff1f\u7ecf\u5386\u51e0\u8bb8\u98ce\u6c99\uff1f \u3000\u3000\u2026\u2026\u201d \u3000\u3000\u90a3\u662f\u8d76\u9a6c\u4eba\u7684\u300a\u6d41\u6d6a\u4e4b\u6b4c\u300b\uff0c\u6b4c\u58f0\u6c89\u90c1\u51c4\u8ff7\uff0c\u65e0\u9650\u9178\u82e6\uff0c\u9648\u5929\u5b87\u60f3\u8d77\u521d\u89c1\u829d\u5a1c\u7684\u60c5\u666f\uff0c \u4e0d\u89c9\u75f4\u4e86\u3002\u7ea2\u697c\u7684\u73bb\u7483\u7a97\u683c\uff0c\u6620\u7167\u51fa\u706f\u706b\u6d41\u8f89\uff0c\u91cc\u9762\u53e6\u4e00\u4e2a\u5723\u5973\u7684\u58f0\u97f3\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u591c\u5df2\u6df1 \u5566\uff0c\u829d\u5a1c\u59d0\u59d0\uff0c\u4f60\u8fd8\u4e0d\u7761\u5417\uff1f\u4e0d\u8981\u80e1\u60f3\u5fc3\u4e8b\u5566\uff01\u201d\u829d\u5a1c\u9053\uff1a\u201c\u6211\u7761\u4e0d\u7740\u3002\u6211\u6458\u4e00\u6280\u96ea\u6885\u56de\u6765 \u7ed9\u4f60\u3002\u201d\u7d22\u6027\u62b1\u7740\u4e1c\u4e0d\u62c9\u8d70\u4e0b\u7ea2\u697c\uff0c\u53c8\u4f4e\u58f0\u5531\u9053\uff1a \u3000\u3000\u201c\u5929\u4e0a\u5140\u9e70\u76d8\u65cb\uff0c \u3000\u3000\u5730\u4e0b\u7fa4\u517d\u4e71\u8d70\uff1b \u3000\u3000\u5475\uff0c\u6211\u4f46\u613f\u80fd\u53d8\u4f5c\u5929\u4e0a\u7684\u5140\u9e70\uff0c \u3000\u3000\u6211\u4f46\u613f\u80fd\u53d8\u4f5c\u590d\u4ec7\u7684\u5315\u9996\uff0c \u3000\u3000\u5140\u9e70\u4e00\u722a\u6293\u6b7b\u90a3\u6b8b\u66b4\u7684\u72ee\u738b\uff0c \u3000\u3000\u5315\u9996\u4e00\u523a\u523a\u5165\u4ec7\u4eba\u7684\u5fc3\u53e3\uff01\u201d \u3000\u3000\u8fd9\u662f\u8349\u539f\u4e0a\u7c97\u6269\u7684\u300a\u590d\u4ec7\u4e4b\u6b4c\u300b\uff0c\u4ece\u4e00\u4e2a\u6de1\u96c5\u5982\u4ed9\u7684\u201c\u5723\u5973\u201d\u53e3\u4e2d\u5531\u51fa\u6765\uff0c\u66f4\u4ee4\u4eba\u5fc3\u7075 \u98a4\u6817\u3002\u829d\u5a1c\u62b1\u7740\u4e1c\u4e0d\u62c9\u6b63\u5728\u4e00\u6b65\u4e00\u6b65\u5730\u5f80\u9648\u5929\u5b87\u85cf\u8eab\u8fd9\u8fb9\u8d70\u6765\uff0c\u5728\u9648\u5929\u5b87\u4e0e\u829d\u5a1c\u4e4b\u95f4\uff0c\u659c\u4fa7 \u7684\u4e00\u5ea7\u5047\u5c71\uff0c\u4fc4\u9a6c\u767b\u6b63\u5728\u626d\u66f2\u4ed6\u90a3\u80a5\u80d6\u7684\u8eab\u8eaf\u63a2\u5934\u7aa5\u89c6\u3002\u5728\u5bd2\u51b7\u7684\u6708\u5149\u4e4b\u4e0b\uff0c\u9648\u5929\u5b57\u4e00\u773c\u77a5 \u53bb\uff0c\u53ea\u89c1\u4fc4\u9a6c\u767b\u7684\u9762\u4e0a\u73b0\u51fa\u4e00\u79cd\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u7684\u597d\u733e\u7b11\u5bb9\u3002\u8fd9\u7b11\u5bb9\uff0c\u9648\u5929\u5b87\u66fe\u89c1\u8fc7\u4e00\u6b21\uff0c\u5c31 \u662f\u90a3\u665a\u5728\u8352\u5c71\u6708\u591c\u4e4b\u4e0b\uff0c\u4fc4\u9a6c\u767b\u89c1\u4e86\u829d\u5a1c\u4e4b\u540e\uff0c\u4ece\u51b0\u5ca9\u4e0a\u60ac\u7ef3\u800c\u4e0b\u65f6\u6240\u53d1\u51fa\u7684\u7b11\u5bb9\u3002\u9648\u5929\u5b87 \u4e0d\u7981\u6253\u4e86\u4e00\u4e2a\u5bd2\u54c4\uff0c\u4e0d\u77e5\u9053\u4fc4\u9a6c\u767b\u5fc3\u5934\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\u3002 \u3000\u3000\u829d\u5a1c\u8d70\u4e86\u51e0\u6b65\uff0c\u53c8\u8f7b\u8f7b\u5730\u5f39\u8d77\u4e1c\u4e0d\u62c9\uff0c\u5531\u9053\uff1a \u3000\u3000\u201c\u817e\u683c\u91cc\u7684\u5927\u6e56\u6df1\u5343\u4e08\uff0c \u3000\u3000\u6211\u5bf9\u4f60\u7684\u5fc6\u5ff5\u554a\uff0c\u6bd4\u6e56\u6c34\u8fd8\u8981\u6df1\uff1b \u3000\u3000\u963f\u5c14\u6cf0\u5c71\u7684\u91d1\u5b50\u5149\u95ea\u95ea\uff0c \u3000\u3000\u6211\u5bf9\u4f60\u7684\u60c5\u610f\u5475\uff0c\u8d5b\u8fc7\u4e86\u9ec4\u91d1\u3002 \u3000\u3000\u51b0\u8c37\u7684\u66fc\u9640\u7f57\u82b1 \u3000\u3000\u7b49\u5f85\u4ed9\u5b50\u4e0b\u51e1\u5c06\u5b83\u91c7\uff1b \u3000\u3000\uff08\u6309\uff1a\u897f\u85cf\u4f20\u8bf4\uff0c\u66fc\u9640\u7f57\u82b1\u662f\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u82b1\u79cd\uff0c\u8981\u7b49\u5f85\u4ed9\u5b50\u4e0b\u51e1\u4e66\u5b83\u5e26\u56de\u5929\u4e0a\u3002\uff09 \u3000\u3000\u98d8\u6cca\u7684\u5c11\u5973\u554a\uff0c \u3000\u3000\u7b49\u5f85\u60c5\u90ce\u4f60\u6765\u5c06\u5979\u7231\u3002 \u3000\u3000\u66fc\u9640\u7f57\u82b1\u8981\u5929\u4e0a\u7684\u743c\u6d46\u6765\u704c\u6e89\uff0c \u3000\u3000\u5c11\u5973\u7231\u60c5\u7684\u9c9c\u82b1\u5475\uff0c \u3000\u3000\u8981\u60c5\u90ce\u7684\u5fc3\u8840\u628a\u5b83\u683d\uff01\u201d \u3000\u3000\u6b4c\u58f0\u6447\u66f3\uff0c\u871c\u610f\u67d4\u60c5\uff0c\u5373\u7b97\u76d6\u4e16\u82f1\u96c4\u4e5f\u7981\u4e0d\u4f4f\u56de\u80a0\u8361\u6c14\u3002\u9648\u5929\u5b87\u66f4\u662f\u5982\u9189\u5982\u75f4\uff0c\u53ea\u542c\u5f97 \u829d\u5a1c\u53cd\u590d\u5f39\u9053\uff1a\u201c\u66fc\u9640\u7f57\u82b1\u8981\u5929\u4e0a\u7684\u743c\u6d46\u6765\u704c\u6e89\uff0c\u5c11\u5973\u7231\u60c5\u7684\u9c9c\u82b1\u5475\uff0c\u8981\u60c5\u90ce\u7684\u5fc3\u8840\u628a\u5b83 \u683d\u3002\u201d\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u5929\u5b87\u5475\u5929\u5b87\uff0c\u6211\u8f9c\u8d1f\u4e86\u4f60\u7684\u5fc3\u8840\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u7684\u5fc3\u6e56\u6ce2\u6d9b\u6f8e\u6e43\uff0c\u7b80\u76f4\u4e0d\u77e5\u9053\u4eba\u95f4\u4f55\u4e16\uff0c\u6b64\u8eab\u4f55\u5728\uff0c\u54ea\u91cc\u8fd8\u8bb0\u5f97\u8fd9\u662f \u6cd5\u738b\u7684\u884c\u5bab\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u7eb5\u8eab\u8df3\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u829d\u5a1c\uff01\u201d \u3000\u3000\u4e94\u5f26\u4e00\u5212\uff0c\u6b4c\u58f0\u9aa4\u6b62\uff0c\u829d\u5a1c\u60ca\u53eb\u4e00\u58f0\uff0c\u56ed\u5b50\u91cc\u987f\u65f6\u4eba\u58f0\u9f0e\u6cb8\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u5ffd\u7136\u88ab \u4eba\u5939\u7740\u9886\u5b50\u4e00\u62bd\uff0c\u817e\u4e91\u9a7e\u96fe\u822c\u88ab\u90a3\u4eba\u5e26\u7740\u98de\u51fa\u56f4\u5899\uff0c\u4e00\u9053\u6697\u8d64\u8272\u7684\u5149\u534e\u5e26\u7740\u5578\u58f0\u63a0\u8fc7\u56ed\u5b50\uff0c \u8033\u8d8a\u53ea\u542c\u5f97\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u9648\u5929\u5b87\u8eab\u4e0d\u7531\u5df1\u5730\u5411\u524d\u5b69\u8dd1\uff0c\u8f6c\u77ac\u4e4b\u95f4\u4e0a\u4e86\u5c71 \u5cf0\uff0c\u4fef\u5934\u4e0b\u671b\uff0c\u53ea\u89c1\u56ed\u5b50\u91cc\u9ed1\u5f71\u5e62\u5e62\uff0c\u4e71\u6210\u4e00\u7247\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6cd5\u738b\u5df2\u8d76\u6765\u4e86\u3002\u6d3b\u8be5\u4fc4\u9a6c\u767b \u90a3\u53ae\u5012\u9709\u3002\u201d\u539f\u6ee6\u662f\u5510\u7ecf\u5929\u89c1\u60c5\u52bf\u5371\u9669\uff0c\u4e0d\u5f85\u540c\u610f\u5c31\u7acb\u5373\u5c06\u9648\u5929\u5b87\u5e26\u51fa\uff0c\u540c\u65f6\u5c04\u4e86\u4e00\u679d\u5929\u5c71 \u795e\u8292\u5230\u4fc4\u9a6c\u767b\u90a3\u8fb9\uff0c\u4ee4\u4fc4\u9a6c\u767b\u90a3\u8fb9\u4e09\u4e2a\u4eba\u90fd\u88ab\u60ca\u5f97\u8df3\u4e86\u51fa\u6765\u3002\u8fd9\u6837\u4fbf\u7acb\u5373\u8f6c\u79fb\u4e86\u767d\u6559\u5587\u561b\u7684 \u76ee\u6807\uff0c\u90fd\u53bb\u5305\u56f4\u4fc4\u9a6c\u767b\u90a3\u4e00\u4f19\u4eba\u3002\u5510\u7ecf\u5929\u4e0e\u9648\u5929\u5b87\u8f7b\u529f\u5353\u7edd\uff0c\u8d81\u7740\u8fd9\u6df7\u4e71\u7684\u5239\u90a3\u8131\u8eab\uff0c\u90a3\u4e9b \u767d\u6559\u5587\u561b\u77a7\u4e5f\u77a7\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u90a3\u4e00\u4f19\u4eba\u8f7b\u529f\u6bd4\u4e0d\u4e0a\u5510\u3001\u9648\u4e8c\u4eba\uff0c\u5f85\u60ca\u89c9\u65f6\uff0c\u672a\u53ca\u8df3\u51fa\u56f4\u5899\uff0c\u5df2\u88ab\u4eba\u56f4\u4f4f\u3002\u9996\u5148 \u6765\u5230\u7684\u662f\u767d\u6559\u7684\u201c\u5723\u6bcd\u201d\u548c\u5728\u56ed\u4e2d\u5de1\u903b\u7684\u56db\u4e2a\u62a4\u6cd5\u5927\u5f1f\u5b50\uff0c\u4e0e\u4fc4\u9a6c\u767b\u540c\u6765\u7684\u90a3\u4e24\u4e2a\u4eba\u662f\u5370\u5ea6 \u5580\u6797\u90a6\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\uff0c\u4e00\u4e2a\u53eb\u505a\u5fb7\u9c81\u5947\uff0c\u4e00\u4e2a\u53eb\u505a\u57fa\u91cc\u661f\u3002\u767d\u6559\u201c\u5723\u6bcd\u201d\u7528\u7684\u662f\u5c3a\u6765\u957f\u7684 \u4e24\u80a1\u94f6\u94f0\uff0c\u9996\u5148\u6765\u5230\uff0c\u8fce\u7740\u5fb7\u9c81\u5947\u4e00\u523a\uff0c\u5fb7\u9c81\u5947\u4e00\u95ea\u95ea\u5f00\u3002 \u3000\u3000\u5fb7\u9c81\u5947\u4e00\u626d\u81c2\u818a\uff0c\u90a3\u53cc\u80a1\u94f6\u94a1\u660e\u660e\u5df2\u523a\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u5374\u5ffd\u5730\u5f80\u65c1\u4e00\u6ed1\uff0c\u5fb7\u9c81\u5947\u4e58\u673a\u4e00 \u5e26\uff0c\u767d\u6559\u5723\u6bcd\u6536\u52bf\u4e0d\u4f4f\uff0c\u548c\u4e00\u4e2a\u62a4\u6cd5\u5f1f\u649e\u4e2a\u6b63\u7740\uff0c\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u6025\u5fd9\u6323\u5f00\uff0c\u5fb7\u9c81\u5947\u4e00\u6e9c\u70df \u5730\u6e9c\u8fc7\u53bb\u4e86\u3002\u539f\u6765\u5fb7\u9c81\u5947\u64c5\u957f\u5370\u5ea6\u745c\u5496\u4e4b\u672f\uff0c\u8eab\u4f53\u5404\u90e8\u90fd\u7ec3\u5f97\u968f\u5fc3\u6240\u6b32\uff0c\u67d4\u82e5\u65e0\u9aa8\uff0c\u56db\u5927\u5587 \u561b\uff0c\u4e0d\u6562\u5728\u884c\u5bab\u4e4b\u4e2d\u5c06\u4eba\u6253\u6b7b\uff0c\u5374\u662f\u64d2\u4ed6\u4e0d\u4f4f\u3002\u57fa\u91cc\u661f\u6ca1\u6709\u8fd9\u79cd\u745c\u5496\u529f\u592b\uff0c\u4f46\u4ed6\u672c\u8eab\u7684\u6b66\u529f \u5374\u5728\u5fb7\u9c81\u5947\u4e4b\u4e0a\uff0c\u4ed6\u548c\u6cd5\u738b\u7684\u9996\u5ea7\u5f1f\u5b50\u5bf9\u4e86\u4e00\u638c\uff0c\u5c45\u7136\u5c06\u6cd5\u738b\u7684\u9996\u5ea7\u5f1f\u4e8e\u63a8\u5f00\u6570\u6b65\u3002\u767d\u6559\u5723 \u6bcd\u4e58\u7740\u57fa\u91cc\u661f\u4e5f\u88ab\u53cd\u529b\u9707\u5f97\u6447\u6447\u6643\u6643\u4e4b\u9645\uff0c\u53cc\u80a1\u94f6\u94a1\u4e00\u7fd8\uff0c\u75be\u523a\u4ed6\u5c0f\u8179\u7684\u201c\u4e2d\u5e73\u201d\u201c\u5c45\u85cf\u201d \u4e24\u5904\u8981\u7a74\uff0c\u8fd9\u4f4d\u767d\u6559\u5723\u6bcd\u7684\u6b66\u529f\u4ec5\u5728\u56db\u5927\u5587\u561b\u4e4b\u4e0b\uff0c\u800c\u94f6\u9488\u523a\u7a74\u7684\u529f\u592b\u66f4\u662f\u72ec\u6b65\u5eb7\u85cf\uff0c\u8fd9\u4e00 \u4e0b\u6765\u52bf\u5982\u7535\uff0c\u672c\u6765\u4e0d\u6613\u8eb2\u95ea\uff0c\u4f46\u57fa\u91cc\u661f\u7684\u5929\u7afa\u5a46\u7f57\u95e8\u6b66\u529f\u8be1\u5f02\u4e4b\u6781\uff0c\u5ffd\u7136\u4e00\u4e2a\u7b4b\u6597\u5012\u7ad6\u8d77 \u6765\uff0c\u94f6\u94a1\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u523a\u7a7f\u4e86\u4ed6\u7684\u88e4\u88c6\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u6cbe\u7740\u4ed6\u7684\u7a74\u9053\u3002\u57fa\u91cc\u661f\u90e8\u52bf\u8fde\u7ffb\u4e24\u4e2a \u7b4b\u6597\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8dc3\u4e86\u8d77\u6765\uff0c\u98de\u8fc7\u5047\u5c71\u8d70\u4e86\u3002 \u3000\u3000\u201c\u5723\u6bcd\u201d\u52c3\u7136\u5927\u6012\uff0c\u4ee5\u5979\u5728\u6559\u4e2d\u5730\u4f4d\u4e4b\u5c0a\uff0c\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u65e0\u793c\uff1f\u5979\u8ba4\u5b9a\u8fd9\u4e24\u4e2a\u5370\u5ea6\u6b66\u58eb \u5b58\u5fc3\u4fae\u8fb1\uff0c\u52a8\u4e86\u771f\u6c14\uff0c\u53d1\u4e0b\u53f7\u4ee4\uff0c\u56ed\u4e2d\u7684\u56db\u5927\u5f1f\u5b50\u548c\u4e00\u4f17\u5587\u561b\u90fd\u53bb\u56f4\u622a\u5fb7\u9c81\u5947\u548c\u57fa\u91cc\u661f\uff0c\u8fd9 \u53ef\u4fbf\u5b9c\u4e86\u4fc4\u9a6c\u767b\uff0c\u522b\u770b\u4ed6\u8eab\u8eaf\u80a5\u80d6\uff0c\u9003\u8d77\u547d\u6765\uff0c\u53ef\u662f\u673a\u4f36\u4e4b\u6781\uff0c\u4ed6\u548c\u5fb7\u9c81\u5947\u91c7\u53d6\u76f8\u53cd\u7684\u65b9 \u5411\uff0c\u4e0d\u5411\u5916\u9003\uff0c\u53cd\u800c\u501f\u7269\u969c\u5f62\uff0c\u6084\u6084\u5730\u5954\u4e0a\u7ea2\u697c\uff0c\u5728\u697c\u4e2d\u6697\u89d2\u85cf\u533f\uff0c\u53ea\u5f85\u90a3\u4e9b\u5587\u561b\u8ffd\u51fa\u56ed \u5916\uff0c\u4ed6\u5c31\u53ef\u4ee5\u4e58\u673a\u9003\u8d70\u3002 \u3000\u3000\u5374\u4e0d\u6599\u767d\u6559\u6cd5\u738b\u5ffd\u7136\u4ece\u884c\u5bab\u91cc\u9762\u8d70\u4e86\u51fa\u6765\uff0c\u89c1\u4fc4\u9a6c\u767b\u7684\u5f71\u5b50\u7a9c\u4e0a\u201c\u5723\u5973\u201d\u6240\u5c45\u7684\u7ea2\u697c\uff0c \u8fd9\u8fd8\u4e86\u5f97\uff1f\u767d\u6559\u6cd5\u738b\u968f\u624b\u6298\u4e86\u4e00\u6761\u6811\u679d\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u5176\u75be\u5982\u7bad\uff0c\u4fc4\u9a6c\u767b\u6b63\u5728\u4e3e\u6b65\uff0c\u7a81\u89c9\u81c2\u4e0a \u4e00\u75db\uff0c\u6709\u5982\u88ab\u5229\u9488\u7a7f\u8089\uff0c\u767b\u65f6\u4e00\u4e2a\u5012\u8f7d\u8471\u8dcc\u4e86\u4e0b\u6765\uff0c\u62ac\u5934\u4e00\u89c1\u6cd5\u738b\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\u3002\u6cd5\u738b\u8ba4 \u5f97\u4ed6\u662f\u571f\u53f8\u624b\u4e0b\u7684\u5927\u6d85\u5df4\uff0c\u6014\u4e86\u4e00\u5f81\uff0c\u5c06\u4e3e\u8d77\u7684\u624b\u638c\u7f13\u7f13\u653e\u4e0b\uff0c\u53eb\u5c0f\u5587\u561b\u8fc7\u6765\uff0c\u5c06\u4ed6\u7f1a\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5fb7\u9c81\u5947\u548c\u57fa\u91cc\u661f\u5df1\u9003\u5230\u5899\u8fb9\uff0c\u57fa\u91cc\u661f\u89e3\u5f00\u7f20\u8170\u7684\u8f6f\u7d22\u55d3\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u4e00\u4e08\u4e4b\u5185\uff0c\u98ce \u96e8\u4e0d\u900f\u3002\u56db\u5927\u5f1f\u5b50\u6b66\u529f\u867d\u9ad8\uff0c\u4e00\u6ed1\u4e4b\u95f4\uff0c\u5374\u4e5f\u8fd1\u4e0d\u4e86\u4ed6\u3002\u6cd5\u738b\u4e00\u6012\uff0c\u98de\u8eab\u8ffd\u53bb\uff0c\u5fb7\u9c81\u5947\u6b63\u7a9c \u4e0a\u59e5\u5934\uff0c\u88ab\u6cd5\u738b\u4e00\u6293\uff0c\u6293\u7740\u4ed6\u7684\u811a\u8ddf\uff0c\u5ffd\u89c9\u624b\u4e2d\u8f6f\u7ef5\u7ef5\u7684\uff0c\u5fb7\u9c81\u8861\u7684\u811a\u8ddf\u4f3c\u4e4e\u7a81\u7136\u7f29\u5c0f\u4e86\u4e00 \u5bf8\uff0c\u628a\u63e1\u4e0d\u4f4f\uff0c\u6cd5\u738b\u5185\u529f\u7cbe\u6df1\uff0c\u6b63\u62df\u7528\u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u592b\uff0c\u5f39\u788e\u4ed6\u7684\u811a\u7b4b\uff0c\u57fa\u91cc\u661f\u6551\u53cb\u5fc3 \u5207\uff0c\u8f6f\u7d22\u671d\u7740\u6cd5\u738b\u4e00\u626b\uff0c\u6cd5\u738b\u5927\u6012\uff0c\u53cd\u624b\u4e00\u524a\uff0c\u6709\u5982\u5200\u65a7\uff0c\u90a3\u6839\u8f6f\u7d22\uff0c\u767b\u65f6\u65ad\u4e86\u3002\u4f46\u4e00\u5fc3\u4e0d \u80fd\u4e8c\u7528\uff0c\u6cd5\u738b\u4f7f\u51fa\u4e86\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u5bf9\u4ed8\u57fa\u91cc\u661f\u7684\u6025\u88ad\uff0c\u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u592b\u4e0d\u80fd\u540c\u65f6\u4f7f\u5c06\u51fa \u6765\uff0c\u7adf\u7ed9\u5fb7\u9c81\u5947\u6323\u8131\uff0c\u8d8a\u5899\u8d70\u4e86\u3002\u6cd5\u738b\u4e00\u6307\u70b9\u5012\u4e86\u57fa\u91cc\u661f\uff0c\u5429\u5490\u5c0f\u5587\u561b\u5c06\u4ed6\u4e00\u5e76\u7f1a\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u53d8\u751f\u610f\u5916\uff0c\u867d\u7136\u5148\u540e\u8fd8\u4e0d\u5230\u4e00\u6280\u9999\u7684\u65f6\u523b\uff0c\u6cd5\u738b\u884c\u5bab\u5df2\u662f\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u829d\u5a1c\u62b1 \u7740\u4e1c\u4e0d\u62c9\uff0c\u4ecd\u7136\u7ad9\u5728\u539f\u5730\uff0c\u5446\u82e5\u6728\u9e21\u3002\u5979\u76ee\u7779\u9648\u5929\u5b87\u7684\u5f71\u5b50\u968f\u7740\u5510\u7ecf\u5929\u4e00\u95ea\u5373\u901d\uff0c\u8033\u8fb9\u8fd8\u54cd \u7740\u9648\u5929\u5b87\u7684\u201c\u829d\u5a1c\uff0c\u829d\u5a1c\uff01\u201d\u7684\u547c\u5524\uff0c\u4e00\u4e2a\u591a\u6df1\u60c5\u7684\u547c\u5524\uff01\u56ed\u4e2d\u95f9\u5f97\u4e71\u7cdf\u7cdf\u7684\uff0c\u5979\u7adf\u4f3c\u89c6\u800c \u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u76f4\u5230\u6cd5\u738b\u5c06\u4fc4\u9a6c\u767b\u3001\u5893\u91cc\u661f\u4e8c\u4eba\u62bc\u89e3\u8fc7\u6765\uff0c\u6cd5\u738b\u6c89\u58f0\u547c\u5524\u5979\u65f6\uff0c\u5979\u624d\u5982\u68a6 \u521d\u89c9\u3002 \u3000\u3000\u4e00\u62ac\u5934\uff0c\u6b63\u78b0\u7740\u4fc4\u9a6c\u767b\u95ea\u70c1\u4e0d\u5b9a\u7684\u773c\u5149\uff0c\u829d\u5a1c\u60ca\u53eb\u4e00\u58f0\uff1a\u201c\u55ef\uff0c\u4fc4\u9a6c\u767b\uff01\u201d \u3000\u3000\u6cd5\u738b\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\u5417\uff1f\u201d\u829d\u5a1c\u9053\uff1a\u201c\u8ba4\u5f97\uff0c\u4ed6\u662f\u571f\u53f8\u624b\u4e0b\u7684\u5927\u6d85\u5df4\u3002\u201d\u4fc4\u9a6c\u767b\u5fd9\u62a2\u7740 \u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u81f3\u4eb2\u8868\u59b9\u3002\u201d\u5723\u6bcd\u5947\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u54b1\u4eec\u4e00\u8def\u6765\u5230\u8428\u8fe6\uff0c\u4e3a\u4f55\u603b\u672a\u542c\u4f60\u63d0 \u8fc7\uff1f\u201d\u829d\u5a1c\u773c\u5149\u98d8\u8fc7\uff0c\u53ea\u89c1\u4fc4\u9a6c\u767b\u5145\u6ee1\u7740\u7126\u6025\u671f\u5f85\u7684\u795e\u60c5\u770b\u7740\u5979\uff0c\u829d\u5a1c\u60f3\u8d77\u4e86\u4fc4\u9a6c\u767b\u66fe\u8bf7\u8fc7 \u9648\u5b9a\u57fa\u6551\u5979\u7684\u4e8b\u60c5\uff0c\u60f3\u8d77\u4e86\u4fc4\u9a6c\u767b\u5728\u65e5\u5580\u5219\u5c71\u533a\u7684\u6708\u591c\uff0c\u66fe\u5411\u5979\u8bf4\u8fc7\u571f\u53f8\u4e43\u662f\u4ed6\u4eec\u5171\u540c\u7684\u4ec7 \u4eba\uff0c\u4ed6\u613f\u610f\u4e3a\u829d\u5a1c\u7684\u590d\u4ec7\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u867d\u7136\u9648\u5929\u5b87\u66fe\u5c61\u6b21\u8bf4\u8fc7\u4fc4\u9a6c\u767b\u6b64\u4eba\u4e0d\u53ef\u9760\uff0c\u4f46\u5374\u4e5f\u6ca1 \u6709\u4ed6\u600e\u4e48\u4e0d\u53ef\u9760\u7684\u8bc1\u636e\u3002\u829d\u5a1c\u5fc3\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u597d\u4eba\u574f\u4eba\uff0c\u4ed6\u603b\u662f\u66fe\u7ecf\u60f3\u6551\u8fc7\u6211\u3002\u201d\u7531\u4e8e\u5979 \u5982\u6b64\u60f3\u6cd5\uff0c\u5979\u5bf9\u4fc4\u9a6c\u767b\u7684\u8c0e\u8bdd\uff0c\u975e\u4f46\u6ca1\u6709\u5f53\u9762\u62c6\u7a7f\uff0c\u53cd\u800c\u66ff\u4ed6\u5706\u8c0e\uff0c\u5f53\u4e0b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u5df2 \u5949\u8eab\u6d3b\u4f5b\uff0c\u6c38\u4e3a\u5723\u5973\uff0c\u81ea\u5f53\u4e00\u5c18\u4e0d\u67d3\uff0c\u56db\u5927\u7686\u7a7a\u3002\u5373\u7b97\u6211\u7236\u6bcd\u5c1a\u751f\uff0c\u800c\u4eca\u5728\u6b64\uff0c\u6211\u4e5f\u4e0d\u5f53\u7275 \u6302\uff0c\u4f55\u51b5\u8868\u54e5\uff1f\u201d\u5723\u6bcd\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u6127\u662f\u4e2a\u5fb7\u884c\u5723\u6d01\uff0c\u5168\u5fc3\u5949\u732e\u7684\u5723\u5973\uff01\u201d \u3000\u3000\u6cd5\u738b\u6012\u6c14\u7a0d\u655b\uff0c\u65a5\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u4f60\u8eab\u4e3a\u6d85\u5df4\uff0c\u53ef\u77e5\u7f6a\u4e48\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u77e5\u7f6a\u3002\u4f46\u6c42\u6d3b \u4f5b\u9976\u6055\u3002\u201d\u6cd5\u738b\u9053\uff1a\u201c\u4f60\u64c5\u95ef\u884c\u5bab\uff0c\u5c31\u4e3a\u7684\u662f\u89c1\u829d\u5a1c\u4e00\u9762\u5417\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u6211\u77e5\u9053\u5723\u5973\u4e0d \u80fd\u79c1\u89c1\u5916\u4eba\uff0c\u6211\u53c9\u4e0d\u6562\u6c42\u6d3b\u4f5b\u901a\u878d\uff1f\u6240\u4ee5\u5192\u6627\u72ec\u6765\uff0c\u6c42\u6d3b\u4f5b\u6055\u6211\u9c81\u83bd\u65e0\u77e5\u4e4b\u7f6a\u3002\u201d\u4fc4\u9a6c\u767b\u4e00 \u53e3\u54ac\u5b9a\u662f\u60f3\u89c1\u829d\u5a1c\uff0c\u8fd9\u5c31\u8fde\u4ed6\u95ef\u4e0a\u7ea2\u697c\u7684\u5927\u4e0d\u656c\u4e4b\u7f6a\u4e5f\u63a9\u9970\u4e86\u3002\u6cd5\u738b\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4f60\u662f \u72ec\u81ea\u6765\u7684\u4e48\uff1f\u4ed6\u4eec\u4e0d\u662f\u4f60\u7684\u540c\u4f34\u4e48\uff0c\u4f60\u4eec\u64c5\u95ef\u884c\u5bab\u4e5f\u8fd8\u7f62\u4e86\uff0c\u600e\u4e48\u5c45\u7136\u6562\u548c\u6211\u52a8\u624b\uff1f\u201d\u4fc4\u9a6c \u767b\u9053\uff1a\u201c\u6e05\u6d3b\u4f5b\u5bb9\u6211\u8be6\u7980\uff0c\u6211\u672c\u662f\u60f3\u89c1\u4e00\u89c1\u829d\u5a1c\uff0c\u6765\u5230\u4e4b\u540e\uff0c\u6b63\u597d\u89c1\u7740\u8fd9\u4e24\u4e2a\u6b79\u5f92\u4e5f\u5077\u8fdb \u6765\uff0c\u6211\u5c31\u53d1\u77f3\u793a\u8b66\u3002\u8981\u662f\u6211\u548c\u4ed6\u4eec\u4e00\u4f19\uff0c\u6211\u5c82\u6562\u60ca\u52a8\u4f17\u4eba\uff0c\u5c06\u4ed6\u4eec\u64d2\u6349\uff1f\u201d \u3000\u3000\u4fc4\u9a6c\u767b\u7741\u7740\u773c\u775b\u8bf4\u8c0e\u8bdd\uff0c\u5c06\u5510\u7ecf\u5929\u53d1\u795e\u8292\u793a\u8b66\u63fd\u5230\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u5f53\u6210\u662f\u81ea\u5df1\u6295\u63b7\u7684\u77f3 \u5b50\u3002\u6cd5\u738b\u5c06\u4fe1\u5c06\u7591\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u662f\u6b79\u5f92\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5370\u5ea6\u7684\u6d6a\u4eba\uff0c\u66fe \u5230\u8fc7\u8428\u8fe6\u6363\u4e71\uff0c\u5978\u6deb\u826f\u5bb6\u5987\u5973\u3002\u6211\u66ff\u571f\u53f8\u7ba1\u7406\u5730\u65b9\uff0c\u6709\u6743\u5c06\u4ed6\u64d2\u6349\uff0c\u53ea\u53ef\u6068\u6211\u4eec\u8fd9\u91cc\u6ca1\u6709\u80fd \u4eba\uff0c\u4ee5\u81f3\u8fc7\u53bb\u4e24\u6b21\u90fd\u88ab\u4ed6\u9003\u8131\uff01\u201d\u4fc4\u9a6c\u767b\u4e00\u7247\u80e1\u8a00\uff0c\u6c61\u8511\u5fb7\u9c81\u5947\u548c\u57fa\u91cc\u661f\u3002\u57fa\u91cc\u661f\u6c14\u70b8\u5fc3 \u80ba\uff0c\u53ef\u662f\u4ed6\u88ab\u6cd5\u738b\u70b9\u4e86\u7a74\u9053\uff0c\u6c14\u5728\u5fc3\u4e2d\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u6cd5\u738b\u6253\u4e86\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u662f\u8fd9\u6837\u5417\uff1f\u201d\u4fc4\u9a6c\u767b\u5ffd\u5730\u8fc8\u4e0a\u4e00\u6b65\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u671d\u7740\u57fa\u91cc\u661f\u7684\u5929 \u7075\u76d6\u91cd\u91cd\u7684\u62cd\u4e86\u4e00\u638c\uff0c\u6cd5\u738b\u559d\u9053\uff1a\u201c\u4f60\u5343\u4ec0\u4e48\uff1f\u201d\u4e00\u6325\u624b\uff0c\u5c06\u4fc4\u9a6c\u767b\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u4f46\u57fa\u91cc \u661f\u5df2\u7ed9\u4ed6\u7528\u91cd\u624b\u6cd5\u6253\u788e\u4e86\u5929\u7075\u76d6\uff0c\u5f53\u573a\u8eab\u6b7b\uff0c\u4e00\u5bf9\u773c\u73e0\u51f8\u4e86\u51fa\u6765\uff0c\u663e\u89c1\u4e34\u6b7b\u4e4b\u65f6\uff0c\u5341\u5206\u6c14 \u6124\u3002\u4fc4\u9a6c\u767b\u722c\u4e86\u8d77\u6765\uff0c\u4e5f\u88c5\u7740\u5341\u5206\u6c14\u6124\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u5c61\u6b21\u5230\u8428\u8fb9\u6363\u4e71\uff0c\u4eca\u756a\u5c45\u7136\u6765\u95ef \u884c\u5bab\uff0c\u8fd8\u6562\u548c\u6d3b\u4f5b\u52a8\u624b\uff0c\u6211\u5b9e\u5728\u6c14\u4ed6\u4e0d\u8fc7\uff0c\u672a\u66fe\u8bf7\u51c6\u6d3b\u4f5b\uff0c\u4fbf\u5931\u624b\u5c06\u4ed6\u6253\u6b7b\uff0c\u6c42\u6d3b\u4f5b\u6055 \u7f6a\u3002\u201d\u6cd5\u738b\u867d\u662f\u6000\u7591\uff0c\u5fc3\u4e2d\u5374\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u597d\u574f\u4e5f\u662f\u571f\u53f8\u624b\u4e0b\u7684\u5927\u6d85\u5df4\uff0c\u6211\u82e5\u5c06\u4ed6\u5904\u7f6a\uff0c\u5927 \u8fc7\u4e0d\u7ed9\u571f\u53f8\u9762\u5b50\u3002\u4f55\u51b5\u4ed6\u53c8\u662f\u829d\u5a1c\u7684\u8868\u5144\u3002\u201d\u60f3\u4e86\u4e00\u60f3\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u56de\u53bb\u5427\uff0c\u4eca\u665a \u4e4b\u4e8b\uff0c\u6211\u6d3e\u4eba\u544a\u8bc9\u571f\u53f8\uff0c\u4f60\u505a\u5f97\u5bf9\u662f\u4e0d\u5bf9\uff0c\u8be5\u8d4f\u8be5\u7f5a\uff0c\u7531\u4f60\u7684\u571f\u53f8\u5904\u7f6e\u3002\u201d \u3000\u3000\u4fc4\u9a6c\u767b\u6740\u4eba\u706d\u53e3\uff0c\u634f\u4e86\u4e00\u5927\u628a\u6c57\uff0c\u5ffd\u542c\u5f97\u6cd5\u738b\u4ea4\u7531\u571f\u53f8\u5904\u7f6e\uff0c\u771f\u662f\u559c\u51fa\u671b\u5916\uff0c\u614c\u5fd9\u8dea\u4e0b \u53bb\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u9053\uff1a\u201c\u591a\u8c22\u6d3b\u4f5b\u6069\u5178\u3002\u6211\u8fd8\u60f3\u548c\u829d\u5a1c\u8bf4\u4e00\u53e5\u8bdd\u3002\u201d\u6cd5\u738b\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u5728 \u8fd9\u91cc\u8bf4\u5427\uff0c\u8981\u4e0d\u8981\u6211\u4eec\u907f\u5f00\uff1f\u201d\u9732\u51fa\u5a01\u4e25\u8083\u715e\u7684\u773c\u5149\uff0c\u626b\u4e86\u4fc4\u9a6c\u767b\u548c\u829d\u5a1c\u4e00\u773c\u3002\u4fc4\u9a6c\u767b\u5fd9 \u9053\uff1a\u201c\u4e00\u70b9\u70b9\u5c0f\u4e8b\u513f\uff0c\u6d3b\u4f5b\u51c6\u6211\u548c\u5723\u5973\u8bf4\u8bdd\uff0c\u6211\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u55ef\uff0c\u829d\u5a1c\uff0c\u4f60\u77e5\u9053\u6211\u7ec3\u8fc7\u51e0 \u5e74\u7ea2\u6559\u7684\u5916\u529f\uff0c\u9aa8\u5934\u4e00\u5411\u5f88\u786c\u6717\uff0c\u8fd1\u6765\u5440\u4e0d\u77e5\u600e\u7684\uff0c\u540e\u8111\u4e0b\u9762\u4e09\u5bf8\u4e4b\u5904\uff0c\u65f6\u65f6\u53d1\u75db\uff0c\u6211\u8bb0\u5f97 \u4f60\u4ee5\u524d\u5bb6\u4e2d\u6709\u5343\u8f7d\u7684\u6c89\u9999\u6728\uff0c\u542c\u8bf4\u7528\u8fd9\u79cd\u6c89\u9999\u6728\u714e\u6c34\u4e09\u773c\uff0c\u53ef\u4ee5\u6cbb\u6108\u8111\u75db\uff0c\u4e0d\u77e5\u4f60\u6709\u6ca1\u6709\u5e26 \u5728\u8eab\u8fb9\uff0c\u53ef\u4ee5\u7ed9\u6211\u4e00\u70b9\u4e48\uff1f\u201d\u829d\u5a1c\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u600e\u77e5\u9053\u4f60\u7ec3\u8fc7\u7ea2\u6559\u7684\u5916\u529f\uff1f\u6211\u54ea\u6709 \u4ec0\u4e48\u5343\u8f7d\u7684\u6c89\u9999\u6728\uff1f\u4fc4\u9a6c\u767b\u8fd9\u53ae\u4eca\u665a\u600e\u4e48\u8001\u662f\u4e00\u9675\u9b3c\u8bdd\uff1f\u201d\u53ea\u89c1\u4fc4\u9a6c\u767b\u7fd8\u8d77\u5927\u59c6\u6307\uff0c\u6307\u7740\u81ea \u5df1\u540e\u8111\u5200\u3002\u51f9\u4e0b\u4e4b\u5904\uff0c\u8bf4\uff1a\u201c\u5c31\u662f\u8fd9\u513f\uff0c\u5c31\u662f\u8fd9\uff01\u201d\u6cd5\u738b\u7a81\u7684\u4f38\u624b\u4e00\u634f\uff0c\u9053\uff1a\u201c\u662f\u8fd9\u513f \u4e48\uff1f\u201d\u4fc4\u9a6c\u767b\u201c\u54ce\u54df\u201d\u5927\u53eb\u547b\u541f\u9053\uff1a\u201c\u662f\u8fd9\u513f\u3002\u201d\u6cd5\u738b\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u7ed9\u4f60\u6cbb\u3002\u201d\u5728\u4ed6\u8111 \u540e\u63c9\u4e86\u4e24\u63c9\uff0c\u4fc4\u9a6c\u767b\u75db\u695a\u82e5\u5931\uff0c\u53c8\u8fde\u8fde\u9053\u8c22\u3002\u6cd5\u738b\u4e5f\u4e0d\u7406\u4ed6\uff0c\u7531\u5f97\u4ed6\u81ea\u5df1\u8d70\u51fa\u56ed\u5b50\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u8d70\u540e\uff0c\u6cd5\u738b\u6c89\u7740\u9762\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u771f\u4e0d\u77e5\u9053\uff0c\u571f\u53f8\u600e\u4e48\u7528\u8fd9\u6837\u9b3c\u9b3c\u795f\u795f\u7684\u4eba \u505a\u5927\u6d85\u5df4\uff0c\u4e00\u6d3e\u9b3c\u8bdd\u3002\u201d\u829d\u5a1c\u5403\u4e86\u4e00\u60ca\uff0c\u5723\u6bcd\u95ee\u9053\uff1a\u201c\u6d3b\u4f5b\u77a7\u51fa\u4ec0\u4e48\u6765\u4e86\uff1f\u201d\u6cd5\u738b\u9053\uff1a\u201c\u4ed6 \u7ec3\u8fc7\u51e0\u5e74\u7ea2\u6559\u7684\u5916\u529f\uff0c\u90a3\u662f\u771f\u7684\uff1b\u7ec3\u529f\u4e0d\u5f53\uff0c\u8111\u540e\u4f1a\u53d1\u75db\uff0c\u90a3\u4e5f\u662f\u771f\u7684\uff1b\u4e0d\u8fc7\u6211\u8bd5\u51fa\u4ed6\u8fd9\u75db \u662f\u88c5\u51fa\u6765\u7684\uff0c\u82e5\u7136\u771f\u662f\u7ec3\u529f\u4e0d\u5f53\u6240\u751f\u75bc\u75db\uff0c\u521a\u624d\u6211\u90a3\u4e00\u634f\uff0c\u4ed6\u7acb\u523b\u8981\u5410\u51fa\u7600\u9ed1\u7684\u6bd2\u8840\u3002\u201d\u5723 \u6bcd\u5947\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u80e1\u8a00\u4e71\u8bed\uff1f\u201d\u6cd5\u738b\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u829d\u5a1c\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u5343\u8f7d \u7684\u6c89\u9999\u6728\uff1f\u7528\u6c89\u9999\u6728\u714e\u6c34\u4e09\u670d\uff0c\u53ef\u6cbb\u8111\u75db\uff0c\u8fd9\u5012\u4e5f\u662f\u771f\u7684\u3002\u201d\u829d\u5a1c\u9053\uff1a\u201c\u6211\u8fd9\u8868\u54e5\u81ea\u5c0f\u60a3\u6709 \u8111\u75c5\uff0c\u6709\u70b9\u75af\u766b\uff0c\u4e0d\u8fc7\u4e0d\u5e38\u53d1\u4f5c\uff0c\u6709\u65f6\u4e00\u4e24\u5e74\u53d1\u4e00\u6b21\uff0c\u4eca\u665a\u8bf4\u4e0d\u5b9a\u521a\u662f\u4ed6\u53d1\u4e86\u5931\u5fc3\u75af\u4e86\u3002\u201d \u3000\u3000\u829d\u5a1c\u53c8\u9053\uff1a\u201c\u5343\u8f7d\u6c89\u9999\u6728\u6211\u5bb6\u4e2d\u4ee5\u524d\u5012\u662f\u6709\u7684\u3002\u540e\u6765\u6211\u7236\u4eb2\u6545\u4e16\uff0c\u6c89\u9999\u6728\u5c31\u653e\u5728\u68fa\u4e2d\u6b89 \u846c\uff0c\u6211\u8868\u5144\u5374\u4e0d\u77e5\u9053\u3002\u201d\u5343\u8f7d\u6c89\u9999\u6728\u653e\u5728\u68fa\u4e2d\uff0c\u53ef\u4ee4\u5c38\u4f53\u5386\u4e45\u800c\u4e0d\u8150\u70c2\uff0c\u897f\u85cf\u7684\u5bcc\u8d35\u4eba\u5bb6\u4e5f \u786e\u4e4e\u6709\u8fd9\u4e2a\u98ce\u4fd7\uff0c\u6cd5\u738b\u76f8\u4fe1\u829d\u5a1c\uff0c\u7adf\u7136\u4e0d\u518d\u8ffd\u7a76\uff0c\u54ea\u77e5\u9053\u829d\u5a1c\u8bf4\u7684\u4e5f\u662f\u4e00\u6d3e\u9b3c\u8bdd\u3002 \u3000\u3000\u8fd9\u665a\u829d\u5a1c\u4e00\u591c\u65e0\u7720\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u7684\u60f3\uff0c\u4fc4\u9a6c\u767b\u8bf4\u8fd9\u756a\u201c\u9b3c\u8bdd\u201d\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u829d\u5a1c\u662f\u4e2a\u806a\u660e \u4f36\u4fd0\u7684\u5973\u5b50\uff0c\u60f3\u4e86\u8bb8\u4e45\uff0c\u5ffd\u7136\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u7fd8\u8d77\u5927\u62c7\u6307\uff0c\u4e00\u5b9a\u662f\u6697\u793a\u571f\u53f8\uff0c \u571f\u53f8\u4e0d\u662f\u8fd9\u91cc\u7684\u9996\u5c48\u4e00\u6307\u7684\u4eba\u7269\u4e48\uff1f\u4e5f\u8bb8\u571f\u53f8\u4e5f\u7ec3\u6709\u7ea2\u6559\u7684\u5916\u529f\uff0c\u8fc5\u4ec0\u4e3b\u53f8\u7a7f\u6709\u62a4\u8eab\u7532\uff0c\u5468 \u8eab\u5200\u67aa\u4e0d\u5165\uff0c\u5c31\u662f\u8111\u4e0b\u4e09\u5bf8\u4e4b\u5904\u662f\u4ed6\u7684\u547d\u95e8\u3002\u201d\u8d8a\u60f3\u8d8a\u6709\u9053\u7406\uff0c\u6697\u6697\u611f\u6fc0\u4fc4\u9a6c\u767b\u5bf9\u81ea\u5df1\u7684 \u201c\u6307\u70b9\u201d\u53c8\u60f3\u9053\uff1a\u201c\u9648\u5929\u5b87\u8001\u662f\u8bf4\u4ed6\u597d\u72e1\uff0c\u60f3\u4e0d\u5230\u4ed6\u5012\u662f\u771f\u5fc3\u5b9e\u610f\u5730\u60f3\u52a9\u6211\u590d\u4ec7\u3002\u201d\u60f3\u8d77\u4e86 \u9648\u5929\u5b87\uff0c\u5fc3\u4e2d\u4e00\u9635\u5fc3\u9178\uff0c\u5fc3\u77e5\u4eca\u665a\u60ca\u9e3f\u4e00\u77a5\uff0c\u4ee5\u540e\u4fbf\u662f\u751f\u79bb\u6b7b\u522b\uff0c\u76f8\u89c1\u65e0\u7531\u4e86\uff0c\u80e1\u601d\u4e71\u60f3\uff0c \u4e0d\u89c9\u5929\u660e\uff0c\u5723\u6bcd\u8fdb\u6765\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u4f60\u8fd8\u4e0d\u5feb\u53bb\u6253\u626e\uff0c\u6b63\u5348\u65f6\u5206\uff0c\u54b1\u4eec\u4fbf\u8be5\u5230\u5723\u5e99\u53bb\u4e3e\u884c\u5f00\u5149 \u5927\u5178\u4e86\u3002\u201d\u829d\u5a1c\u67d4\u80a0\u5bf8\u65ad\uff0c\u4e00\u8fb9\u6253\u626e\uff0c\u4e00\u8fb9\u4ecd\u5728\u75f4\u75f4\u5730\u60f3\u9053\uff1a\u201c\u5929\u5b87\u4ed6\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u6765\uff1f\u554a\uff0c \u6211\u662f\u591a\u4e48\u6e34\u671b\u6700\u540e\u518d\u89c1\u4ed6\u4e00\u9762\uff1b\u5374\u53c8\u591a\u4e48\u4e3a\u4ed6\u62c5\u5fe7\u5bb3\u6015\uff0c\u4f46\u613f\u4ed6\u4e0d\u8981\u5230\u8fd9\u662f\u975e\u4e4b\u573a\u3002\u201d\u5fc3\u4e2d \u767e\u822c\u77db\u76fe\uff0c\u96be\u4ee5\u81ea\u89e3\uff0c\u7ec8\u4e8e\u5411\u7740\u5ba4\u4e2d\u7684\u4f5b\u50cf\uff0c\u8dd1\u4e86\u4e0b\u53bb\uff0c\u5583\u5583\u7948\u7977\u9053\uff1a\u201c\u5929\u5b87\u5440\uff0c\u4f46\u613f\u6211\u4f5b \u6148\u60b2\uff0c\u7ed9\u4f60\u4fdd\u4f51\uff0c\u4ee4\u4f60\u5fc3\u4e2d\u5b89\u9759\uff0c\u4eca\u65e5\u5343\u4e07\u4e0d\u8981\u5230\u5587\u561b\u5bfa\u6765\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u65f6\u5019\uff0c\u9648\u5929\u5b87\u4e5f\u6b63\u662f\u809d\u80a0\u5bf8\u65ad\u3002\u5510\u7ecf\u5929\u6628\u665a\u966a\u4ed6\u56de\u53bb\u4e4b\u540e\uff0c\u5c31\u4e00\u76f4\u529d\u4ed6\u4eca\u65e5\u4e0d\u8981\u5230 \u5587\u561b\u5bfa\u53bb\u770b\u5f00\u5149\u5927\u5178\u3002\u8fd9\u65f6\u4e24\u4eba\u8fd8\u5728\u8fa9\u8bba\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u53bb\uff0c \u4f60\u7559\u5728\u5bb6\u4e2d\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u53ef\u4ee5\u53bb\uff0c\u6211\u4e0d\u80fd\u53bb\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u53bb\u662f\u60f3\u53bb\u78b0\u4e00 \u4e2a\u4eba\u3002\u4f60\u5440\uff0c\u4f60\u660e\u660e\u77e5\u9053\u829d\u5a1c\u5df2\u505a\u4e86\u5723\u5973\uff0c\u4f60\u8fd8\u53bb\u505a\u4ec0\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u77e5\u9053\u829d \u5a1c\u5df2\u505a\u4e86\u5723\u5973\uff0c\u6211\u624d\u60f3\u53bb\u518d\u89c1\u5979\u4e00\u9762\u3002\u8981\u4e0d\u7136\u6211\u624d\u6ca1\u6709\u5fc3\u60c5\u53bb\u770b\u8fd9\u4ec0\u4e48\u5f00\u5149\u5927\u5178\u3002\u201d\u5510\u7ecf\u5929 \u9053\uff1a\u201c\u6628\u665a\u8981\u4e0d\u662f\u54b1\u4eec\u8dd1\u5f97\u5feb\uff0c\u5df2\u7136\u95f9\u51fa\u5927\u4e8b\u3002\u4eca\u5929\u7684\u5f00\u5149\u5927\u5178\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u8fbe\u8d56\u73ed\u7985\u7684\u4f7f \u8005\uff0c\u8428\u8fe6\u7684\u4e0a\u53f8\uff0c\u50e7\u5cea\u5b98\u5458\u5168\u90fd\u8981\u5230\u573a\u89c2\u793c\uff0c\u4f60\u5fc3\u7eea\u4e0d\u5b81\uff0c\u82e5\u7136\u8fd9\u4e00\u53bb\u95f9\u51fa\u4e8b\u60c5\uff0c\u8bd5\u95ee\u4f60\u5c06 \u5982\u4f55\u6536\u62fe\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u6df7\u5728\u4eba\u5806\u4e4b\u4e2d\uff0c\u53ea\u662f\u8fdc\u8fdc\u7684\u770b\u5979\u4e00\u9762\uff0c\u600e\u4f1a\u95f9\u51fa\u4e8b\u6765\uff1f\u201d\u5510\u7ecf \u5929\u6447\u6447\u5934\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u53ef\u4e0d\u6562\u62c5\u4fdd\uff0c\u6628\u665a\u8981\u4e0d\u662f\u4f60\u53d1\u58f0\u53eb\u558a\uff0c\u4e5f\u4e0d\u4f1a\u60ca\u52a8\u6cd5\u738b\u3002\u201d\u9648\u5929\u5b87 \u8d4c\u6c14\u9053\uff1a\u201c\u6211\u53d1\u8a93\u4e0d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u8981\u4e0d\u7136\u4f60\u7d22\u6027\u70b9\u4e86\u6211\u7684\u54d1\u7a74\uff0c\u8fd9\u603b\u53ef\u4ee5\u4e86\u5427\uff1f\u201d\u5510\u7ecf\u5929\u7b11 \u9053\uff1a\u201c\u4f60\u65e2\u5982\u6b64\u56fa\u6267\uff0c\u8bf4\u4e0d\u5f97\u6211\u53ea\u597d\u518d\u966a\u4f60\u4e00\u6b21\u4e86\u3002\u54b1\u4eec\u6362\u8fc7\u4e00\u5957\u666e\u901a\u7684\u8863\u88f3\u53bb\u5427\u3002\u201d \u3000\u3000\u8428\u8fe6\u7684\u767d\u6559\u5587\u561b\u5bfa\u5e99\u4eff\u7167\u62c9\u8428\u9ec4\u6559\u7684\u5e03\u8fbe\u62c9\u5bab\u5f62\u5f0f\uff0c\u4fee\u5efa\u5728\u5676\u5c14\u90a3\u5c71\u4e0a\uff0c\u5e03\u8fbe\u62c9\u5bab\u6709\u5341 \u4e09\u5c42\uff0c\u5b83\u6bd4\u4e0d\u4e0a\u5e03\u8fbe\u62c9\u5bab\uff0c\u4f46\u4e5f\u6709\u4e03\u5c42\uff0c\u9ad8\u4e8c\u5341\u4f59\u4e08\uff0c\u91d1\u9ccc\u753b\u680b\uff0c\u7ea2\u5899\u767d\u77f3\uff0c\u501a\u5c71\u8e1e\u5cad\uff0c\u6c14 \u6982\u5e9e\u5927\uff0c\u5728\u5341\u4f59\u91cc\u5916\uff0c\u8fdc\u8fdc\u5c31\u53ef\u671b\u89c1\u3002\u5510\u7ecf\u5929\u4e0e\u9648\u5929\u5b87\u4e8c\u4eba\uff0c\u6362\u4e86\u8428\u8fea\u5c45\u6c11\u7684\u4e00\u822c\u670d\u88c5\uff0c\u6df7 \u5728\u540e\u9762\u8fdb\u9999\u793c\u62dc\u7684\u4e00\u7fa4\u5584\u7537\u4fe1\u5973\u4e2d\uff0c\u968f\u7740\u4eba\u6d41\uff0c\u7f13\u7f13\u8fdb\u5165\u5c71\u8c37\uff0c\u5c06\u8fd1\u4e2d\u5348\u65f6\u5206\uff0c\u624d\u6324\u5230\u4e86\u5587 \u5587\u5bab\u4e0b\u9762\u7684\u5c71\u5f84\uff0c\u4f46\u89c1\u5728\u84dd\u5929\u767d\u4e91\u4e4b\u4e0b\uff0c\u5587\u561b\u5bab\u4e0a\u5341\u51e0\u53ea\u5706\u9525\u91d1\u9876\u95ea\u8000\u7740\u7eda\u70c2\u7684\u8272\u5f69\uff0c\u5bab\u6bbf \u91cc\u56de\u8361\u7740\u60a0\u60a0\u7684\u949f\u9f13\u58f0\u3002\u6709\u4e00\u961f\u62ab\u7740\u7ece\u8272\u8888\u88df\u7684\u5587\u561b\u80cc\u8d1f\u7ecf\u5323\uff0c\u4f5c\u4e3a\u524d\u5bfc\uff0c\u6cbf\u7740\u5927\u9752\u77f3\u94fa\u7684 \u4eba\u884c\u8def\uff0c\u7f13\u7f13\u767b\u4e0a\u5bab\u6bbf\uff0c\u5341\u4e8c\u5ea7\u5927\u95e8\u90fd\u5df2\u5f00\u653e\uff0c\u7f15\u7f15\u6a80\u9999\u4ece\u91cc\u9762\u98d8\u51fa\u6765\uff0c\u8fd9\u6c14\u6c1b\u6709\u8bf4\u4e0d\u51fa\u7684 \u5e84\u4e25\u8083\u7a46\u3002\u524d\u6765\u8fdb\u9999\u793c\u62dc\u7684\u5584\u7537\u4fe1\u5973\u5343\u5343\u4e07\u4e07\uff0c\u5e76\u65e0\u534a\u70b9\u5608\u58f0\u6742\u54cd\u3002 \u3000\u3000\u5510\u3001\u9648\u4e8c\u4eba\u968f\u7740\u4eba\u6d41\u7a7f\u8fc7\u6797\u7acb\u7684\u5eca\u67f1\uff0c\u4e24\u5eca\u90fd\u9970\u6709\u58c1\u753b\uff0c\u5176\u4e2d\u6709\u4e00\u5e45\u300a\u516b\u601d\u5df4\u671d\u89d0\u5ffd\u5fc5 \u70c8\u53bb\u8499\u53e4\u300b\u7684\u58c1\u753b\u5c24\u5176\u753b\u5f97\u7cbe\u5f69\u7edd\u4f26\uff0c\u8fd9\u753b\u5199\u516b\u601d\u5df4\u53bb\u671d\u89c1\u5ffd\u5fc5\u70c8\uff0c\u5de6\u9762\u753b\u4e00\u7fa4\u58eb\u5175\u5b98\u5458\u7c07 \u62e5\u516b\u601d\u5df4\u7684\u8f7f\u5b50\uff0c\u524d\u9762\u6709\u8499\u53e4\u5b98\u5458\u6765\u8fce\u63a5\uff0c\u66f4\u524d\u9762\u6709\u4e00\u4e2a\u7855\u5927\u65e0\u670b\u7684\u8499\u53e4\u5e10\u5e55\uff0c\u5e10\u5e55\u540e\u6709\u4eba \u70e7\u706b\u7b49\u5019\u516b\u601d\u5df2\u7684\u5230\u6765\u3002\u753b\u4e0a\u8fd8\u6709\u6210\u7fa4\u7684\u9a86\u9a7c\u3001\u9aa1\u9a6c\u7281\u725b\u4e4b\u7c7b\u5728\u8349\u5730\u4e0a\u5403\u8349\uff0c\u8349\u5730\u4e0a\u8fd8\u6709\u4e00 \u4e2a\u7a7f\u7740\u5c3c\u6cca\u5c14\u8d35\u65cf\u5987\u5973\u670d\u9970\u7684\u5c11\u5973\uff0c\u8fd9\u5c11\u5973\u7f8e\u8273\u7edd\u4f26\uff0c\u9762\u8c8c\u7adf\u7136\u6709\u51e0\u5206\u76f8\u4f3c\u51b0\u5ddd\u5929\u5973\uff0c\u56e0\u4e3a \u4eba\u6d41\u884c\u8fdb\u6781\u6162\uff0c\u5510\u7ecf\u5929\u767e\u65e0\u804a\u6555\uff0c\u81ea\u7136\u800c\u7136\u7684\u521b\u89c8\u4e24\u65c1\u7684\u58c1\u753b\uff0c\u521d\u65f6\u4e0d\u8fc7\u62b1\u7740\u6d88\u78e8\u65f6\u95f4\u7684\u5fc3 \u60c5\uff0c\u770b\u5230\u8fd9\u5e45\u58c1\u753b\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u897f\u85cf\u8fb9\u9119\u4e4b\u5730\uff0c\u54ea\u91cc\u6765\u7684\u8fd9\u7b49\u753b\u5bb6\u9ad8\u624b\u3001\u753b\u4e2d \u53ea\u6709\u8fd9\u4e00\u4e2a\u5c11\u5973\uff0c\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u4e3a\u4ec0\u4e48\u90a3\u6837\u8096\u4f3c\u51b0\u5ddd\u5929\u5973\uff1f\u201d\u770b\u9648\u5929\u5b87\u65f6\uff0c\u9648\u5929\u5b87\u5374\u662f\u76ee \u4e0d\u659c\u89c6\uff0c\u6382\u7740\u811a\u8ddf\uff0c\u53ea\u662f\u51dd\u671b\u524d\u9762\uff0c\u597d\u50cf\u4ed6\u7684\u829d\u5a1c\u5c31\u4f1a\u5ffd\u7136\u5728\u524d\u9762\u51fa\u73b0\uff0c\u6015\u8d70\u4e86\u773c\u4f3c\u7684\u3002\u5176 \u5b9e\u524d\u9762\u662f\u62e5\u6324\u7684\u4eba\u7fa4\uff0c\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\u3002\u5510\u7ecf\u5929\u6697\u53f9\u9648\u5929\u5b87\u7684\u75f4\u5fc3\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u4e5f\u4f55\u5c1d \u4e0d\u662f\u5982\u6b64\uff1f\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u3002 \u3000\u3000\u597d\u5bb9\u6613\u6324\u5230\u4e86\u5927\u6bbf\u7684\u524d\u9762\uff0c\u5510\u3001\u9648\u4e8c\u4eba\u6324\u5230\u524d\u9762\u7684\u77f3\u9636\u7ad9\u7acb\uff0c\u53ea\u89c1\u8fd9\u5ea7\u5927\u6bbf\u6709\u56db\u4e2a\u5927\u98de \u6a90\uff0c\u4e0a\u7f00\u4eba\u9762\u9e1f\u8eab\u7684\u91d1\u50cf\uff0c\u4e0b\u7cfb\u94c3\u950b\uff0c\u96d5\u6402\u5f97\u6781\u5176\u7cbe\u7ec6\uff0c\u5927\u6bbf\u5185\u6709\u4e24\u5ea7\u91d1\u5236\u7684\u201c\u5587\u561b\u7075\u5854\u201d \u5854\u4e0a\u904d\u7f00\u73e0\u5bd2\u7f28\u57f9\uff0c\u9576\u7740\u5404\u8272\u7389\u77f3\u3001\u73cd\u73e0\u3001\u739b\u745e\u3001\u7fe1\u7fe0\u96d5\u6210\u7684\u82b1\u6735\uff0c\u7aef\u7684\u662f\u5bcc\u4e3d\u5e84\u4e25\uff0c\u5510\u7ecf \u5929\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u53ea\u8fd9\u5ea7\u5587\u561b\u5bab\u5c31\u4e0d\u77e5\u6d6a\u8d39\u4e86\u591a\u5c11\u4eba\u529b\u7269\u529b\u3002\u201d\u9648\u5927\u5b57\u5374\u5728\u77f3\u9636\u4e0a\u5b9a\u4e86\u795e\uff0c\u5ffd \u542c\u5f97\u949f\u9f13\u9f50\u9e23\uff0c\u4e00\u961f\u767d\u6559\u5587\u561b\u62ab\u7740\u767d\u8272\u7684\u6cd5\u8863\u9c7c\u8d2f\u800c\u51fa\uff0c\u8d70\u5728\u6700\u524d\u9762\u7684\u662f\u90a3\u4e2a\u767d\u6559\u6cd5\u738b\uff0c\u5de6 \u53f3\u4e24\u65c1\u662f\u56db\u5927\u5f1f\u5b50\uff0c\u8f6c\u77ac\u5c31\u8d70\u5230\u4e24\u5ea7\u201c\u7075\u5854\u201d\u4e4b\u95f4\u7ad9\u5b9a\u3002 \u3000\u3000\u63a5\u7740\u51fa\u6765\u7684\u662f\u8fbe\u8d56\u73ed\u63b8\u7684\u4f7f\u8005\uff0c\u5404\u7387\u9886\u56db\u4e2a\u5927\u50e7\u4fa3\uff0c\u548c\u767d\u6559\u6cd5\u738b\u5e76\u80a9\u5404\u7ad9\u5728\u4e00\u4e2a\u7075\u5854\u7684 \u65c1\u8fb9\uff0c\u4ed6\u4eec\u662f\u767d\u6559\u6cd5\u738b\u6700\u5c0a\u8d35\u7684\u5bbe\u5ba2\u3002\u518d\u540e\u51fa\u6765\u7684\u662f\u8428\u8fe6\u571f\u53f8\uff0c\u5e26\u7740\u56db\u5927\u6d85\u5df4\uff0c\u4fc4\u9a6c\u767b\u4e5f\u5728 \u5176\u4e2d\uff0c\u9762\u4e0a\u6302\u7740\u72e1\u7ed8\u7684\u7b11\u5bb9\uff0c\u5374\u53c8\u4f5c\u51fa\u4e00\u526f\u8bda\u60f6\u8bda\u6050\u7684\u795e\u6c14\uff0c\u5782\u9996\u7acb\u5728\u571f\u53f8\u8eab\u540e\u3002\u770b\u8fd9\u6837 \u5b50\uff0c\u8981\u5c31\u662f\u6cd5\u738b\u8fd8\u6ca1\u6709\u5c06\u6628\u665a\u4e4b\u4e8b\u544a\u8bc9\u4e0a\u53f8\uff0c\u8981\u5c31\u662f\u571f\u53f8\u66f2\u4e88\u4f18\u5bb9\uff0c\u6839\u672c\u6ca1\u6709\u8d23\u7f5a\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e00\u5fc3\u76fc\u671b\u829d\u5a1c\uff0c\u5723\u5973\u5374\u8fdf\u8fdf\u672a\u51fa\uff1b\u5510\u7ecf\u5929\u5219\u56db\u9762\u6ce8\u6ce8\u89c6\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u5730\u5728\u60f3\uff1a\u201c\u91d1 \u4e16\u9057\u4f1a\u4e0d\u4f1a\u6765\u5462\uff1f\u201d\u4f46\u524d\u540e\u5de6\u53f3\uff0c\u4eba\u5934\u5bc6\u5bc6\u9ebb\u9ebb\uff0c\u5373\u7b97\u91d1\u4e16\u9057\u6df7\u5728\u5176\u4e2d\uff0c\u5510\u7ecf\u5929\u4e5f\u8ba4\u4ed6\u4e0d\u51fa\u3002 \u3000\u3000\u53ea\u89c1\u6cd5\u738b\u7f13\u7f13\u6325\u624b\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u672c\u6559\u79bb\u5f00\u897f\u85cf\uff0c\u5c48\u6307\u8fc7\u4e86\u591a\u5e74\uff0c\u4eca\u65e5\u4ed7\u4f5b\u7956\u6148\u60b2\uff0c\u5f97 \u4ee5\u91cd\u56de\u6545\u571f\uff0c\u53c8\u5f97\u8fbe\u8d56\u73ed\u63b8\u4e24\u4f4d\u6d3b\u4f5b\uff0c\u5927\u529b\u652f\u6301\uff0c\u8d50\u4ee5\u8428\u8fe6\uff0c\u5b8f\u5ba3\u4f5b\u6cd5\uff0c\u4f46\u613f\u4ee5\u540e\u5e72\u6208\u6c38 \u5b81\uff0c\u6211\u4f5b\u836b\u5e87\uff0c\u6c38\u4eab\u592a\u5e73\u3002\u201d\u8981\u77e5\u767d\u6559\u81ea\u4ece\u5728\u660e\u4ee3\u5d07\u4fa6\u5341\u516d\u5e74\u95ee\u88ab\u9ec4\u6559\u9010\u51fa\u897f\u85cf\u4e4b\u540e\uff0c\u767e\u4f59 \u5e74\u6765\uff0c\u66fe\u6709\u8fc7\u4e0d\u5c11\u7684\u7ea0\u7eb7\uff0c\u5175\u620e\u76f8\u89c1\u4ea6\u6709\u5341\u6570\u6b21\u4e4b\u591a\uff0c\u800c\u4eca\u4e24\u6559\u548c\u7766\uff0c\u897f\u85cf\u4eba\u867d\u7136\u5df2\u662f\u5f88\u5c11 \u767d\u6559\u6559\u5f92\uff0c\u4ea6\u662f\u8877\u5fc3\u559c\u60a6\uff0c\u542c\u5f97\u6cd5\u738b\u6b64\u756a\u8bf4\u8bdd\uff0c\u6b22\u58f0\u96f7\u52a8\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u771f\u80fd\u4ece \u6b64\u6c38\u606f\u4e89\u7aef\uff0c\u8d39\u4e86\u8fd9\u4e48\u591a\u7684\u4eba\u529b\u5efa\u8fd9\u5ea7\u5587\u561b\u5e99\u4e5f\u8fd8\u503c\u5f97\u3002\u201d \u3000\u3000\u6bbf\u4e0a\u949f\u9f13\u6572\u4e86\u4e09\u904d\uff0c\u4e24\u961f\u5c0f\u5587\u561b\u7ed5\u884c\u5927\u6bbf\u4e00\u5468\uff0c\u5583\u5583\u8bf5\u7ecf\uff0c\u7ea2\u9152\u6cd5\u6c34\uff0c\u949f\u58f0\u68b5\u5457\u4e4b\u4e2d\uff0c \u4e00\u961f\u767d\u8863\u5c11\u5973\u9c7c\u8d2f\u8d70\u51fa\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u5927\u6bbf\u4e0a\u4e0b\u4e00\u7247\u9759\u5bc2\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u5f00\u5149\u5927\u5178\u5373\u5c06\u4e3e\u884c\uff0c\u5343 \u4e07\u5bf9\u773c\u90fd\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u610f\u8fd9\u961f\u5723\u5973\uff0c\u9648\u5929\u5b87\u66f4\u662f\u7126\u8e81\u4e0d\u5b89\uff0c\u5c4f\u4f4f\u547c\u5438\u5411\u524d\u89c2\u671b\uff0c\u4f46\u89c1\u4e09\u5341\u516d \u540d\u5723\u5973\u4e2a\u4e2a\u62ab\u7740\u9762\u7eb1\uff0c\u6367\u7740\u51c0\u74f6\uff0c\u5ffd\u5728\u4f5b\u50cf\u4e4b\u524d\uff0c\u76c8\u76c8\u8d77\u821e\uff0c\u9648\u5929\u5b87\u7aed\u529b\u60f3\u8fa8\u8ba4\u8c01\u662f\u829d\u5a1c\uff0c \u4e00\u65f6\u95f4\uff0c\u5374\u662f\u8ba4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5723\u5973\u904d\u6d12\u6768\u679d\u7518\u9732\uff0c\u8df3\u7684\u662f\u201c\u9a71\u90aa\u821e\u201d\uff0c\u4e09\u5341\u516d\u540d\u5723\u5973\u66f3\u7740\u957f\u88d9\uff0c\u7a7f\u68ad\u6765\u5f80\uff0c\u821e\u59ff\u7f16 \u8e51\uff0c\u9c7c\u9f99\u66fc\u884d\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u7f2d\u7ed5\u3002\u53ea\u542c\u5f97\u7537\u201d\u4e9b\u201c\u5723\u5973\u201d\u7528\u85cf\u8bed\u4e14\u821e\u4e14\u6b4c\u9053\uff1a \u3000\u3000\u4e00\u6d12\u6768\u679d\u7518\u9732\uff0c \u3000\u3000\u6d88\u5c3d\u4eba\u95f4\u90aa\u6c14\u3002 \u3000\u3000\u6211\u4f5b\u4f5b\u529b\u65e0\u8fb9\uff0c \u3000\u3000\u4fdd\u4f51\u592a\u5e73\u76db\u4e16\u3002 \u3000\u3000\u821e\u6001\u8f7b\u76c8\uff0c\u6b4c\u58f0\u66fc\u5999\uff0c\u8f6c\u800c\u6b4c\u9053\uff1a \u3000\u3000\u518d\u6d12\u6768\u679d\u7518\u9732\uff0c \u3000\u3000\u793c\u8d5e\u8bf8\u5929\u4f5b\u7956\u3002 \u3000\u3000\u4f5b\u7956\u5584\u7f18\u5e7f\u7ed3\u3002 \u3000\u3000\u4f17\u751f\u540c\u767b\u4e50\u571f \u3000\u3000\u6b4c\u58f0\u672c\u6781\u548c\u8c10\uff0c\u5531\u5230\u7b2c\u4e8c\u3001\u6cca\u540e\u4e00\u97f3\uff0c\u5ffd\u5730\u6709\u4e00\u58f0\u9ad8\u4ea2\uff0c\u5fae\u5fae\u98a4\u6296\uff0c\u9648\u5929\u5b87\u3001\u5510\u7ecf\u5929\u7cbe \u4e8e\u97f3\u5f8b\uff0c\u542c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53ea\u89c1\u5176\u4e2d\u4e00\u4e2a\u5723\u5973\uff0c\u957f\u88d9\u66f3\u5730\uff0c\u65e0\u98ce\u81ea\u98d8\uff0c\u60f3\u662f\u56e0\u4e3a\u80a2\u4f53\u98a4\u52a8\u6240\u81f4\uff0c\u9648\u5929\u5b87\u731b\u7684\u5fc3\u5934\u4e00 \u9707\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u829d\u5a1c\u4e5f\u77a7\u89c1\u6211\u4e86\u3002\u201d\u773c\u775b\u7d27\u7d27\u8ddf\u7740\u90a3\u4f4d\u5723\u5973\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u4efb\u5b83\u821e\u5f71\u9a97\u8e51\uff0c \u4eba\u5f71\u7f2d\u7ed5\uff0c\u9648\u5929\u5b87\u7684\u5fc3\u76ee\u4e2d\u5374\u53ea\u6709\u8fd9\u4e2a\u5723\u5973\u3002\u8fd9\u5723\u5973\u867d\u7136\u4e5f\u62ab\u7740\u9762\u7eb1\uff0c\u4f46\u9648\u5929\u5b87\u5374\u4f3c\u900f\u8fc7\u9762 \u7eb1\uff0c\u770b\u5230\u5979\u90a3\u5bf9\u795e\u79d8\u7684\u773c\u775b\uff0c\u5728\u5411\u81ea\u5df1\u76c8\u76c8\u7709\u8bed\uff0c\u90a3\u521a\u5e62\u5a1c\u5a1c\u7684\u80cc\u5f71\uff0c\u90a3\u62ab\u80a9\u5149\u6da6\u7684\u67d4\u53d1\uff0c \u518d\u52a0\u4e0a\u90a3\u521a\u624d\u65c1\u4eba\u6240\u672a\u7ecf\u610f\u800c\u9648\u5929\u5b87\u5374\u5df2\u53d1\u515a\u7684\u201c\u5931\u6001\u201d\uff0c\u8fd9\u4e00\u5207\u90fd\u544a\u8bc9\u4e86\u9648\u5929\u5b87\uff0c\u8fd9\u5723\u5973 \u4e00\u5b9a\u4fbf\u662f\u829d\u5a1c\u3002 \u3000\u3000\u9648\u5929\u5b87\u773c\u775b\u7d27\u7d27\u968f\u7740\u829d\u5a1c\uff0c\u829d\u5a1c\u8df3\u4e86\u4e24\u4e2a\u5706\u821e\u6b65\uff0c\u6742\u5728\u4e09\u5341\u516d\u540d\u5723\u5973\u5f53\u4e2d\uff0c\u518d\u65e0\u5f02\u6001\uff0c \u821e\u6b65\u4e5f\u975e\u5e38\u5ae1\u719f\uff0c\u60f3\u662f\u5fc3\u4e2d\u5df2\u6062\u590d\u4e86\u5e73\u9759\u3002\u9648\u5929\u5b87\u5fc3\u5934\u9178\u75db\uff0c\u9ed8\u9ed8\u60f3\u9053\uff1a\u201c\u9053\u662f\u65e0\u60c5\u5374\u6709 \u60c5\uff0c\u5440\uff0c\u829d\u5a1c\uff0c\u96be\u9053\u4f60\u8fd9\u4e00\u8f88\u5b50\u5c31\u771f\u7684\u7518\u5fc3\u505a\u4e00\u4e2a\u6c38\u4f34\u9752\u706f\u53e4\u4f5b\u65c1\u7684\u5723\u5973\uff1f\u201d\u9648\u5929\u5b87\u54ea\u91cc\u77e5 \u9053\uff0c\u829d\u5a1c\u7684\u5fc3\u4e2d\u60b2\u82e6\u6bd4\u4ed6\u66f4\u751a\u767e\u500d\uff0c\u829d\u5a1c\u662f\u95f2\u4e86\u6574\u4e2a\u751f\u547d\u7684\u529b\u91cf\uff0c\u628a\u5fc3\u4e2d\u7684\u60b2\u82e6\u5f3a\u538b\u4e0b\u53bb \u7684\u3002\u9648\u5929\u5b87\u54ea\u91cc\u77e5\u9053\uff0c\u829d\u5a1c\u6b63\u5728\u51c6\u5907\u628a\u5979\u7684\u751f\u547d\u4f5c\u5b64\u6ce8\u4e00\u63b7\uff0c\u751f\u6015\u9732\u51fa\u534a\u70b9\u75d5\u8ff9\u5475\u3002 \u3000\u3000\u90a3\u961f\u5723\u5973\u8df3\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u63a5\u7740\u6b4c\u9053\uff1a \u3000\u3000\u201c\u4e09\u6d12\u6768\u679d\u7518\u9732\uff0c \u3000\u3000\u6d17\u51c0\u5fc3\u5934\u5c18\u6c61\u3002 \u3000\u3000\u4eba\u5929\u540c\u8bc1\u771f\u5982\uff0c \u3000\u3000\u52d8\u7834\u8272\u7a7a\u5999\u609f\u3002 \u3000\u3000\u821e\u6b65\u7531\u75be\u800c\u5f90\uff0c\u6b4c\u58f0\u4e00\u6536\uff0c\u4e09\u5341\u516d\u540d\u5723\u5973\uff0c\u5df2\u5728\u4f5b\u50cf\u4e4b\u524d\u6392\u6210\u4e00\u5217\uff0c\u6162\u6162\u63ed\u5f00\u906e\u5728\u4f5b\u50cf \u5916\u9762\u7684\u9ec4\u7f15\u68c9\u9992\u3002\u4f5b\u50cf\u5171\u662f\u4e00\u5341\u516b\u5c0a\uff0c\u5f53\u4e2d\u7684\u4e00\u5ea7\u91ca\u8fea\u725f\u5c3c\u50cf\u9ad8\u4e8c\u4e08\u56db\u5c3a\uff0c\u6307\u5934\u7c97\u5982\u513f\u81c2\uff0c \u5723\u5973\u5c06\u6768\u679d\u7518\u9732\u904d\u6d12\u4f5b\u50cf\u4e4b\u524d\uff0c\u7f13\u7f13\u9000\u7acb\u4e24\u65c1\uff0c\u5f00\u5149\u5927\u5178\u4fbf\u544a\u63ed\u5e55\u3002 \u3000\u3000\u81fc\u6559\u6cd5\u738b\u606d\u606d\u656c\u656c\u5730\u5411\u6b63\u4e2d\u4f5b\u50cf\u732e\u4e86\u201c\u54c8\u8fbe\u201d\uff08\u4e1d\u7ee2\u3002\u732e\u54c8\u8fbe\u4e43\u662f\u897f\u85cf\u4e00\u79cd\u8868\u793a\u656c\u610f\u7684 \u793c\u8282\uff09\u63a5\u7740\u662f\u8fbe\u8d56\u73ed\u7985\u4e24\u4f4d\u6d3b\u4f5b\u7684\u4ee3\u8868\u6765\u732e\u54c8\u8fbe\uff0c\u8fd9\u65f6\u5408\u6bbf\u4e0a\u4e0b\u4eba\u4f17\uff0c\u90fd\u5408\u4ec0\u4f4e\u9996\uff0c\u5728\u5fc3\u4e2d \u9ed8\u8bf5\u4f5b\u53f7\uff0c\u53ea\u6709\u9648\u5929\u5b87\u4e00\u4eba\uff0c\u867d\u7136\u4e5f\u968f\u7740\u4f17\u4eba\u4f4e\u4e0b\u4e86\u5934\uff0c\u773c\u89d2\u5374\u4ecd\u7136\u5077\u779f\u829d\u5a1c\u3002 \u3000\u3000\u8ddf\u5728\u7422\u7985\u4f7f\u8005\u540e\u9762\u732e\u7ed9\u54c8\u8fbe\u7684\u662f\u8428\u8fe6\u7684\u571f\u53f8\uff0c\u571f\u53f8\u98a4\u52a8\u7740\u80a5\u80d6\u7684\u8eab\u8eaf\uff0c\u530d\u4f0f\u5728\u91ca\u8fce\u725f\u5c3c \u4f5b\u50cf\u7684\u811a\u4e0b\uff0c\u53cc\u624b\u5448\u4e0a\u54c8\u8fbe\u3002\u6267\u6cd5\u7684\u5587\u561b\u6b63\u5f85\u63a5\u8fc7\u54c8\u8fbe\uff0c\u62ab\u5728\u5982\u6765\u4f5b\u50cf\u7684\u81c2\u4e0a\uff0c\u5ffd\u542c\u5f97\u571f\u53f8 \u5927\u53eb\u4e00\u58f0\uff0c\u53ea\u89c1\u94f6\u5149\u4e00\u95ea\uff0c\u4e00\u67c4\u98de\u5200\u5df2\u63d2\u5165\u4e86\u571f\u53f8\u7684\u540e\u8111\u3002\u767d\u6559\u6cd5\u738b\u5c16\u53eb\u9053\uff1a\u201c\u662f\u4f60\uff1f\u829d \u5a1c\uff01\u201d\u4fc4\u9a6c\u767b\u5927\u53eb\u201c\u6709\u523a\u5ba2\u5440\uff01\u201d\u5723\u6bcd\u5413\u7684\u9b42\u4e0d\u9644\u4f53\uff0c\u5495\u54af\u4e00\u58f0\uff0c\u6655\u5012\u575b\u524d\uff0c\u767b\u65f6\u4e00\u7247\u6df7\u4e71\u3002 \u3000\u3000\u829d\u5a1c\u84c4\u5fd7\u62a5\u4ec7\u5df2\u4e45\uff0c\u8fd9\u98de\u5200\u4e4b\u6280\u5df2\u4e0d\u77e5\u7ec3\u4e86\u513f\u5343\u767e\u904d\uff0c\u5979\u6015\u4e00\u63b7\u4e0d\u4e2d\uff0c\u5728\u6cd5\u738b\u4e0e\u4fc4\u9a6c\u767b \u7684\u547c\u559d\u58f0\u4e2d\uff0c\u7b2c\u4e8c\u67c4\u7b2c\u4e09\u67c4\u98de\u53c8\u75be\u98de\u800c\u51fa\uff0c\u6cd5\u738b\u79bb\u4f5b\u50cf\u6570\u4e08\uff0c\u4e3e\u8896\u4e00\u62c2\uff0c\u7b2c\u4e8c\u67c4\u98de\u5200\u5012\u98de\u56de \u53bb\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u76f4\u523a\u5165\u829d\u5a1c\u7684\u80a9\u5934\u3002\u9648\u5929\u5b87\u5413\u5f97\u51e0\u4e4e\u5c31\u8981\u558a\u51fa\u58f0\u6765\uff0c\u5634\u5df4\u5374\u88ab\u5510\u7ecf\u5929\u63a9\u4f4f\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u66fc\u821e\u8f7b\u6b4c\u60c5\u672a\u5df1\uff0c\u98de\u5200\u60ca\u89c1\u5973\u8346\u67ef\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u821e\u5f71\u8e41\u5343\u3000\u98de\u5200\u6740\u4ec7\u654c\u3000\u98ce\u4e91\u52a8\u8361\u3000\u4fa0\u58eb\u62a4\u5371\u57ce \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u516b\u56de \u821e\u5f71\u8e41\u5343\u3000\u98de\u5200\u6740\u4ec7\u654c \u98ce\u4e91\u52a8\u8361\u3000\u4fa0\u58eb\u62a4\u5371\u57ce \u3000\u3000\u829d\u5a1c\u4f4e\u547c\u4e00\u58f0\uff0c\u8eab\u8eaf\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u90a3\u7b2c\u4e09\u67c4\u98de\u5200\u5931\u4e86\u51c6\u5934\uff0c\u63d2\u4e0d\u6b63\u540e\u8111\u4e0b\u9762\u7684\u547d\u95e8\u8981 \u5bb3\uff0c\u5374\u523a\u7740\u4e86\u4e0a\u53f8\u7684\u80cc\u5fc3\uff0c\u201c\u6323\u201d\u7684\u4e00\u58f0\uff0c\u98de\u5200\u6fc0\u8d77\uff0c\u6700\u9760\u8fd1\u571f\u53f8\u7684\u4eba\u662f\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\uff0c \u4ed6\u4e0d\u61c2\u6b66\u529f\uff0c\u731b\u7136\u95f4\u89c1\u98de\u5200\u5c04\u5230\uff0c\u614c\u4e0d\u8fed\u7684\u4f4e\u5934\u4e00\u95ea\uff0c\u4e0d\u6599\u90a3\u98de\u5200\u4e4b\u52bf\u662f\u659c\u98de\u800c\u4e0b\uff0c\u4ed6\u8fd9\u4e00 \u95ea\uff0c\u51d1\u4e2a\u6b63\u7740\uff0c\u201c\u542d\u568f\u201d\u4e00\u58f0\uff0c\u98de\u5200\u63d2\u5165\u4e86\u4ed6\u7684\u80cc\u810a\uff0c\u534a\u622a\u5200\u5203\u8fde\u7740\u5200\u67c4\u9732\u5728\u5916\u9762\uff0c\u98a4\u52a8\u4e0d \u4f11\u3002 \u3000\u3000\u6cd5\u738b\u626c\u8896\u4e00\u62c2\uff0c\u7acb\u5373\u4e00\u8dc3\u800c\u524d\uff0c\u4ee5\u4ed6\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e00\u4f38\u624b\u5c31\u80fd\u5c06\u829d\u5a1c\u62ff\u7740\uff0c\u4f46\u56e0\u5ffd\u89c1\u73ed\u7985 \u7684\u4ee3\u8868\u53d7\u4e86\u98de\u5200\u8bef\u4f24\uff0c\u8fd9\u4e00\u6765\uff0c\u9976\u4ed6\u662f\u201c\u6d3b\u4f5b\u201d\u8eab\u4efd\uff0c\u4e5f\u5413\u5f97\u5446\u4e86\uff0c\u6025\u5fd9\u5148\u4e0a\u53bb\u6551\u62a4\u73ed\u7985\u7684 \u4ee3\u8868\u3002\u829d\u5a1c\u4e00\u8df3\u8df3\u4e0a\u795e\u5ea7\uff0c\u500f\u7684\u6495\u5f00\u9762\u7eb1\uff0c\u53eb\u9053\uff1a\u201c\u6211\u662f\u6c81\u5e03\u85e9\u738b\u7684\u5973\u513f\uff0c\u523a\u4e0a\u53f8\u662f\u62a5\u7236 \u4ec7\uff0c\u4e0e\u65c1\u4eba\u65e0\u6d89\uff01\u201d\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\u3002\u767d\u6559\u7684\u56db\u5927\u62a4\u6cd5\u5f1f\u5b50\u4e00\u6d8c\u800c\u524d\uff0c\uff0c\u4e3a\u9996\u7684\u5927\u5f1f\u5b50\u624b\u6307\u5df2 \u89e6\u53ca\u4e86\u829d\u5a1c\u767d\u8272\u7684\u957f\u88d9\uff0c\u829d\u5a1c\u4e00\u8bf4\u5b8c\u8bdd\uff0c\u4f38\u624b\u4e00\u62d4\u62d4\u51fa\u63d2\u5728\u5979\u80a9\u4e0a\u7684\u90a3\u67c4\u98de\u5200\uff0c\u500f\u7684\u56de\u5200\u5411 \u54bd\u5589\u4e00\u523a\uff0c\u767b\u65f6\u9c9c\u8840\u6cc9\u6d8c\uff0c\u8f6f\u7ef5\u7ef5\u5730\u501a\u5728\u4f5b\u50cf\u7684\u8eab\u4e0a\uff0c\u773c\u775b\u52c9\u5f3a\u6323\u5f00\u5411\u5802\u4e0b\u4e00\u671b\uff0c\u53c8\u5f90\u5f90\u5408 \u4e0a\uff0c\u8138\u4e0a\u5e26\u7740\u6ee1\u610f\u7684\u4e5f\u662f\u75db\u82e6\u7684\u5fae\u7b11\u3002\u5979\u4e34\u6b7b\u4e4b\u524d\uff0c\u5728\u4eba\u4e1b\u4e2d\u77a7\u89c1\u4e86\u9648\u5929\u5b87\uff0c\u9648\u5929\u5b87\u7684\u773c\u5149 \u59cb\u7ec8\u6ca1\u6709\u79bb\u5f00\u5979\u3002 \u3000\u3000\u5f00\u5149\u5927\u5178\uff0c\u4f55\u7b49\u795e\u5723\u5e84\u4e25\uff0c\u5374\u5ffd\u7136\u53d1\u751f\u4e86\u8840\u6e85\u6cd5\u575b\u4e4b\u4e8b\uff0c\u5927\u6bbf\u4e0a\u4e0b\u4eba\u4f17\u90fd\u60ca\u5f97\u5446\u4e86\uff0c\u5ffd \u53c8\u89c1\u829d\u5a1c\u81ea\u6740\uff0c\u7a7a\u6c14\u6b7b\u5bc2\uff0c\u731b\u7136\u95f4\u4e0d\u77e5\u662f\u8c01\u5931\u58f0\u9a87\u53eb\uff0c\u767b\u65f6\u5927\u5bb6\u90fd\u60ca\u53eb\u8d77\u6765\uff0c\u5411\u5916\u4e71\u6d8c\u3002\u8fd9 \u5239\u90a3\uff0c\u9648\u5929\u5b87\u8981\u54ed\u5374\u54ed\u4e0d\u51fa\u6765\u3002\u773c\u89c1\u829d\u5a1c\u7684\u5c38\u4f53\u6162\u6162\u5012\u4e0b\uff0c\u53ea\u89c9\u80f8\u4e2d\u70ed\u8840\u4e0a\u6d8c\u3001\u7a81\u95f4\u53eb\u51fa\u58f0 \u6765\uff1a\u201c\u829d\u5a1c\uff0c\u829d\u5a1c\uff01\u201d\u4e0d\u5411\u540e\u9000\uff0c\u53cd\u60f3\u6324\u4e0a\u524d\u53bb\uff0c\u4ed6\u662f\u7ec3\u8fc7\u5185\u529f\u7684\u4eba\uff0c\u88ab\u5510\u7ecf\u5929\u7981\u6b62\u4ed6\u8bf4 \u8bdd\uff0c\u80f8\u4e2d\u90c1\u79ef\u5df2\u4e45\uff0c\u8fd9\u4e00\u4e0b\u62fc\u547d\u5927\u547c\uff0c\u5728\u8bf8\u58f0\u55dc\u6742\u4e4b\u4e2d\uff0c\u66f4\u663e\u5f97\u5206\u5916\u7a81\u51fa\u3002\u5510\u7ecf\u5929\u6025\u5fd9\u5728\u4ed6 \u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u6682\u5fcd\u60b2\u75db\uff0c\u4f11\u60f9\u98ce\u6ce2\uff01\u201d\u626f\u4ed6\u75be\u5411\u5916\u8d70\u3002\u9648\u5929\u5b87\u8fd9\u65f6\u5df2\u5931\u4e86\u77e5\u89c9\uff0c\u6d51\u6d51\u7096\u9968\u5730 \u88ab\u5510\u7ecf\u5929\u7740\uff0c\u4efb\u4ed6\u6446\u5e03\u3002 \u3000\u3000\u6bbf\u4e0a\u6bbf\u4e0b\uff0c\u4e71\u6210\u4e00\u7247\u3002\u53ea\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u571f\u53f8\u5df2\u88ab\u523a\u6b7b\u5566\uff1a\u201d\u662f\u571f\u53f8\u7684\u968f\u8eab\u6b66\u58eb\u68c0\u67e5 \u4e86\u571f\u53f8\u7684\u4f24\u52bf\u4e4b\u540e\u8bf4\u7684\uff0c\u571f\u53f8\u62ab\u7740\u62a4\u7532\uff0c\u4ed6\u672c\u8eab\u53c8\u7ec3\u6709\u7ea2\u6559\u7684\u5916\u529f\uff0c\u82e5\u4e0d\u662f\u98de\u5200\u521a\u521a\u63d2\u4e2d\u4ed6 \u8111\u4e0b\u4e09\u5bf8\u7684\u547d\u95e8\u8981\u5bb3\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u4f1a\u6bd9\u547d\u3002 \u3000\u3000\u4f17\u4eba\u867d\u90fd\u6599\u5230\u571f\u53f8\u5fc5\u6b7b\uff0c\u4f46\u542c\u5f97\u4f17\u6b66\u58eb\u90fd\u9f50\u58f0\u5450\u558a\uff0c\u4ecd\u662f\u60ca\u5fc3\u52a8\u9b44\uff0c\u5f80\u5916\u62e5\u6324\u4e4b\u52bf\u66f4\u751a \u4e86\u3002\u5927\u6bbf\u5916\u9762\u7684\u5584\u7537\u4fe1\u5973\u4e0d\u77e5\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8ddf\u7740\u9a9a\u52a8\u4e71\u8dd1\uff0c\u5c31\u5982\u4e00\u7fa4\u88ab\u654c\u4eba\u8ffd\u9010\u7684\u8d25\u5175 \u4e00\u822c\uff0c\u6f6e\u6c34\u822c\u5730\u5f80\u5916\u6d8c\u3002\u53ea\u542c\u5f97\u5927\u6bbf\u4e0a\u7684\u4fc4\u9a6c\u767b\u53c8\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u6349\u523a\u5ba2\u7684\u540c\u515a\u5440\uff01\u201d\u5510\u7ecf \u5929\u6b63\u6324\u51fa\u4e86\u5916\u9762\u7684\u6708\u7259\u95e8\u3001\u4e00\u4e2a\u62a4\u6cd5\u561b\u7a81\u7136\u5c06\u4ed6\u622a\u4f4f\uff01 \u3000\u3000\u5510\u7ecf\u5929\u811a\u4e0d\u505c\u6b65\uff0c\u6a2a\u65f6\u4e00\u649e\uff0c\u90a3\u62a4\u6cd5\u5587\u561b\u5927\u53eb\u4e00\u58f0\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u540e\u9762\u4eba\u5982\u6f6e\u6d8c\uff0c\u6709\u51e0\u4e2a \u4eba\u5728\u4ed6\u8eab\u4e0a\u8e0f\u8fc7\uff0c\u5f85\u4ed6\u722c\u8d77\u6765\u65f6\uff0c\u5510\u7ecf\u5929\u4e0e\u9648\u5929\u5b87\u65e9\u5df2\u94bb\u5165\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u6ca1\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u867d\u5728\u60ca\u60f6\u6050\u4e71\u4e4b\u4e2d\uff0c\u4ecd\u662f\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u9648\u5929\u5b87\u90a3\u4e24\u58f0\u5927\u53eb\uff0c\u65e9\u5df2\u88ab\u4ed6 \u7559\u610f\u4e0a\u4e86\uff0c\u4f46\u6bbf\u4e0b\u4eba\u5934\u7c07\u62e5\uff0c\u9648\u5929\u5b87\u3001\u5510\u7ecf\u5929\u4e8c\u4eba\u7a7f\u7684\u53c8\u662f\u4e00\u822c\u8428\u8fb9\u5c45\u6c11\u7684\u670d\u9970\uff0c\u6025\u5207\u95f4\u77a7 \u4e0d\u6e05\u4ed6\u4eec\u7684\u9762\u76ee\u3002\u8fd9\u65f6\u89c1\u62a4\u6cd5\u5587\u561b\u88ab\u4eba\u6253\u5012\uff0c\u6cd5\u738b\u6025\u5fd9\u8ffd\u4e86\u51fa\u6765\uff0c\u6307\u7740\u6708\u7259\u95e8\u5927\u53eb\u9053\uff1a\u201c\u95f2 \u5165\u5feb\u5feb\u95ea\u8fc7\u4e24\u8fb9\uff0c\u523a\u5ba2\u7684\u540c\u515a\u662f\u5f53\u4e2d\u8fd9\u4e24\u5c0f\u5b50\uff01\u5927\u5bb6\u4e0d\u51c6\u4e71\u8dd1\uff0c\u539f\u5730\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u6cd5\u738b\u4e00\u53eb\uff0c\u679c\u7136\u628a\u6324\u5411\u6708\u7259\u95e8\u7684\u4eba\u6d41\u904f\u4f4f\uff0c\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u6cd5\u738b\u5f53\u771f\u5389 \u5bb3\uff01\u201d\u6b63\u5728\u76d8\u7b97\u8131\u8eab\u4e4b\u8ba1\uff0c\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a\u6781\u719f\u6089\u7684\u54c8\u54c8\u602a\u7b11\u58f0\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u95f2\u5165\u95ea\u5f00\u5440\u95ea \u5f00\uff0c\u5f85\u6211\u6765\u77bb\u4ef0\u6d3b\u4f5b\uff01\u201d\u6b63\u662f\u91d1\u4e16\u9057\u7684\u602a\u58f0\uff0c\u5510\u7ecf\u5929\u6765\u770b\u5f00\u5149\u5927\u5178\uff0c\u672c\u6765\u662f\u4e3a\u7740\u649e\u91d1\u4e16\u9057\uff0c \u4f46\u8fd9\u65f6\u5374\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u505c\u4e0b\u4e0e\u4ed6\u76f8\u89c1\u4e86\uff0c\u8d81\u7740\u6df7\u4e71\u518d\u8d77\uff0c\u5510\u7ecf\u5929\u62c9\u7740\u9648\u5929\u5b87\u6324\u8fc7\u4e86\u6708\u7259\u6d1e \u95e8\uff0c\u767e\u5fd9\u4e2d\u56de\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u6cd5\u738b\u5df2\u4e0e\u91d1\u4e16\u9057\u6597\u5728\u4e00\u8d77\u3002\u5510\u7ecf\u5929\u83ab\u540d\u5176\u5999\uff0c\u91d1\u4e16\u9057\u867d\u662f\u73a9\u4e16\u4e0d \u606d\uff0c\u4f46\u7adf\u6562\u5728\u6b64\u65f6\u6b64\u5730\uff0c\u5411\u6cd5\u738b\u95f9\u4e8b\uff0c\u90a3\u5374\u662f\u8fde\u5510\u7ecf\u5929\u4e5f\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u7684\u4e8b\uff0c\u4e0d\u660e\u4ed6\u662f\u4e3a\u4e86 \u4f55\u6765\uff1f \u3000\u3000\u6324\u5230\u5916\u95f4\uff0c\u5730\u65b9\u5bbd\u9614\uff0c\u5510\u7ecf\u5929\u62c9\u7740\u9648\u5929\u5b87\u8fc5\u901f\u9003\u8d70\uff0c\u7247\u523b\u5c31\u8dd1\u51fa\u5bfa\u95e8\uff1a\u6cbf\u7740\u5c71\u540e\u5c0f\u5f84\u5954 \u9003\uff0c\u8fc7\u4e86\u4e00\u652f\u9999\u7684\u65f6\u523b\uff0c\u4ed6\u4eec\u5df2\u9003\u5230\u4e86\u5676\u5c14\u90a3\u5c71\u7684\u5c71\u80cc\uff0c\u4eba\u7fa4\u90fd\u88ab\u9694\u5728\u5c71\u524d\uff0c\u8fde\u4e00\u70b9\u4eba\u58f0\u90fd \u542c\u4e0d\u5230\u4e86\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u7a0d\u5bbd\uff0c\u5728\u9648\u5929\u5b87\u7684\u80cc\u5fc3\u8f7b\u8f7b\u4e00\u62cd\uff0c\u9053\uff1a\u201c\u9648\u5144\u9192\u6765\uff01\u201d\u9648\u5929\u5b87\u4e24\u773c\u5446 \u5446\u5730\u671b\u7740\u4ed6\uff0c\u832b\u7136\u65e0\u795e\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u829d\u5a1c\uff0c\u829d\u5a1c\uff0c\u800c\u4eca\u6211\u660e\u767d\u4f60\u4e3a\u4ec0\u4e48\u53bb\u505a\u5723\u5973 \u4e86\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u6211\u770b\u8fd9\u6b21\u4e71\u5b50\uff0c\u53ea\u6015\u8981\u751f\u51fa\u6781\u5927\u7684\u98ce\u6ce2\u3002\u4f60\u6211\u8fd8\u662f\u8d76\u5feb \u56de\u8859\uff0c\u5546\u91cf\u5584\u540e\u4e3a\u597d\u3002\u201d\u9648\u5929\u5b87\u4ecd\u662f\u660f\u660f\u8ff7\u8ff7\uff0c\u4f3c\u542c\u61c2\u53c8\u4f3c\u672a\u66fe\u542c\u61c2\uff0c\u7741\u7740\u773c\u775b\u8bf4\u9053\uff1a\u201c\u6211 \u53c8\u4e0d\u80fd\u5c06\u5979\u7684\u5c38\u4f53\u9886\u56de\u57cb\u846c\uff0c\u600e\u4e48\u66ff\u5979\u529e\u540e\u4e8b\u5440\uff1f\u201d\u5510\u7ecf\u5929\u6025\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u540e\u4e8b\u3002\u201d\u60c5\u77e5 \u4e00\u65f6\u4e4b\u95f4\uff0c\u8bf4\u4e0d\u660e\u767d\uff0c\u53ea\u5f97\u62d6\u7740\u9648\u5929\u5b87\u53c8\u8dd1\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u7528\u85cf\u8bed\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u95f9\u51fa\u4e86\u5927\u4e8b\uff0c\u5c31\u60f3\u4e00\u8d70\u4e86\u4e4b\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u62ac\u5934\u4e00 \u770b\uff0c\u53ea\u89c1\u5c71\u6811\u540e\u9762\uff0c\u8f6c\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u4e00\u4e2a\u662f\u5370\u5ea6\u50e7\u4eba\uff0c\u53f3\u624b\u63e1\u7740\u4e00\u6839\u78a7\u8272\u7684\u7af9\u6756\uff0c\u5de6\u624b\u6258\u7740 \u4e00\u4e2a\u91d1\u76c2\u94b5\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u4ee5\u524d\u6765\u62a2\u8fc7\u91d1\u672c\u5df4\u74f6\u3001\u88ab\u51b0\u5ddd\u5929\u5973\u6253\u8d25\u7684\u90a3\u4e2a\u82e6\u884c\u50e7\u3002\u53e6\u4e00\u4e2a \u5219\u662f\u6628\u591c\u79c1\u63a2\u6cd5\u738b\u884c\u5bab\u7684\u90a3\u4e2a\u5370\u5ea6\u6b66\u58eb\u5fb7\u9c81\u5947\uff0c\u5510\u7ecf\u5929\u5fc3\u4e2d\u6b63\u5728\u5947\u602a\uff0c\u4ed6\u4eec\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u77e5 \u9053\u4e86\uff1f\u90a3\u82e6\u884c\u50e7\u4e0d\u7531\u5206\u8bf4\uff0c\u5c31\u662f\u4e00\u6756\u626b\u6765\uff0c\u5de6\u624b\u5c06\u91d1\u76c2\u94b5\u4e00\u7ffb\uff0c\u53c8\u5411\u9648\u5929\u5b87\u8fce\u5934\u7f69\u4e0b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u89c1\u90a3\u91d1\u76c2\u7f69\u4e0b\uff0c\u6765\u52bf\u6781\u731b\uff0c\u6015\u9648\u5929\u5b87\u62b5\u6321\u4e0d\u4f4f\uff0c\u6a2a\u8098\u4e00\u649e\uff0c\u65bd\u4e60\u7edd\u5999\u7684\u5de7\u52b2\uff0c\u5728 \u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u5c06\u9648\u5929\u5b87\u649e\u5f97\u8eab\u5f62\u98de\u8d77\uff0c\u8fc5\u5373\u5de6\u62f3\u4e0a\u51fb\uff0c\u53f3\u638c\u6a2a\u524a\uff0c\u5de6\u62f3\u7528\u7684\u662f\u5927\u529b\u91d1\u521a\u624b\u7684 \u529f\u592b\uff0c\u53ea\u542c\u5f97\u5531\u7684\u4e00\u58f0\u54cd\uff0c\u6709\u5982\u94c1\u9524\u51fb\u949f\uff0c\u90a3\u82e6\u884c\u618e\u5b5f\u94b5\u4e00\u7ffb\uff0c\u94b5\u5934\u671d\u5916\uff0c\u4e00\u4e0b\u5b50\u7f69\u7740\u4e86\u5510 \u7ecf\u5929\u7684\u62f3\u5934\uff0c\u5b5f\u94b5\u98de\u4e00\u822c\u7684\u65cb\u8f6c\uff0c\u5510\u7ecf\u5929\u53ea\u89c9\u5f97\u94b5\u4e2d\u9690\u9690\u6709\u4e00\u80a1\u5438\u529b\uff0c\u81ea\u5df1\u7684\u62f3\u5934\u7adf\u7136\u62bd\u4e0d \u51fa\u6765\uff0c\u5403\u4e86\u4e00\u60ca\u3002\u4f46\u4ed6\u7a76\u7adf\u662f\u5929\u5c71\u6d3e\u5ae1\u4f20\u5f1f\u5b50\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u614c\u4e71\uff0c\u53f3\u638c\u4e00\u7ffb\uff0c\u7528\u7684\u662f\u81f3\u521a\u81f3\u731b \u7684\u201c\u4e94\u4e01\u5f00\u5c71\u201d\u5de8\u7075\u638c\u529b\uff0c\u90a3\u82e6\u884c\u50e7\u4e00\u6756\u626b\u6765\uff0c\u88ab\u638c\u529b\u4e00\u9707\uff0c\u6756\u5934\u5ffd\u5730\u7fd8\u8d77\uff0c\u4e58\u52bf\u6233\u5510\u7ecf\u5929 \u80f8\u53e3\u7684\u201c\u6f29\u7391\u7a74\u201d\uff0c\u5510\u7ecf\u5929\u65e9\u5df2\u6599\u5230\u6709\u6b64\u4e00\u7740\uff0c\u5316\u638c\u4e3a\u62ff\uff0c\u5ffd\u5730\u4ece\u81f3\u731b\u81f3\u521a\u7684\u201c\u4e94\u4e01\u5f00\u5c71\u201d \u638c\u6cd5\u53d8\u4e3a\u521a\u67d4\u5e76\u6d4e\u7684\u5927\u64d2\u62ff\u624b\uff0c\u6247\u638c\u4e00\u6293\uff0c\u7acb\u523b\u5c06\u82e6\u884c\u50e7\u7684\u7af9\u6756\u6293\u4f4f\u3002\u82e6\u884c\u50e7\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c \u6025\u8fd0\u5185\u529b\u5f80\u5916\u593a\u6756\uff0c\u5374\u4e5f\u593a\u4e0d\u51fa\u6765\u3002\u8fd9\u4e00\u6765\u53d8\u6210\u4e86\u82e6\u884c\u50e7\u7684\u7af9\u6756\u88ab\u5510\u7ecf\u5929\u53f3\u638c\u6240\u5236\uff0c\u800c\u5510\u7ecf \u5929\u5de6\u624b\u7684\u62f3\u5934\u5374\u88ab\u82e6\u884c\u50e7\u7684\u91d1\u76c2\u6240\u5236\uff0c\u4e24\u4eba\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u8c01\u90fd\u4e0d\u80fd\u89e3\u8131\uff0c \u53d8\u6210\u4e86\u50f5\u6301\u4e4b\u5c40\u3002\u5fb7\u9c81\u5947\u662f\u8fd9\u4e2a\u82e6\u884c\u50e7\u7684\u5e08\u4f84\uff0c\u77e5\u9053\u5e08\u53d4\u7684\u813e\u6c14\uff0c\u52a8\u624b\u7edd\u4e0d\u8981\u522b\u4eba\u76f8\u52a9\uff0c\u4f46 \u6b64\u65f6\u89c1\u5510\u7ecf\u5929\u6b66\u529f\u592a\u5f3a\uff0c\u5e08\u53d4\u5934\u9876\u4e0a\u76f4\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u62fc\u7740\u4e8b\u540e\u88ab\u5e08\u53d4\u8d23 \u9a82\uff0c\u89e3\u4e0b\u7f20\u5728\u8170\u95f4\u7684\u94a2\u7d22\uff0c\u547c\u7684\u4e00\u6296\uff0c\u94a2\u7d22\u6709\u5982\u957f\u86c7\u51fa\u6d1e\uff0c\u6d41\u661f\u95ea\u7535\u822c\u5730\u626b\u5230\u5510\u7ecf\u5929\u9762\u95e8\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u5510\u7ecf\u5929\u54ea\u4f1a\u628a\u5fb7\u9c81\u5947\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u6b64\u65f6\u4ed6\u4e0e\u82e6\u884c\u50e7\u82e6\u82e6\u76f8\u6301\uff0c\u8c01\u90fd\u4e0d\u80fd\u8131 \u8eab\uff0c\u773c\u89c1\u94a2\u7d22\u98de\u6765\uff0c\u7ade\u662f\u65e0\u6cd5\u95ea\u907f\u3002\u9648\u5929\u5b87\u5374\u5446\u5446\u5730\u7ad9\u5728\u9053\u65c1\uff0c\u4e00\u526f\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\u3002\u5510\u7ecf \u5929\u4e00\u6025\uff0c\u731b\u5730\u5927\u559d\u4e00\u58f0\uff0c\u8fd9\u4e00\u559d\u6709\u5982\u534a\u7a7a\u91cc\u7a81\u7136\u6253\u4e0b\u4e00\u4e2a\u7126\u96f7\uff0c\u5fb7\u9c81\u5947\u7a92\u4e86\u4e00\u7a92\uff0c\u94a2\u7d22\u5782\u4e86 \u4e0b\u6765\uff0c\u5dee\u4e09\u5bf8\u6ca1\u6709\u6253\u5230\u5510\u7ecf\u5929\uff0c\u9648\u5929\u5b87\u88ab\u8fd9\u4e00\u559d\u559d\u9192\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u6325\u5251\u76f4\u53d6\u5fb7\u9c81\u5947\u3002 \u3000\u3000\u5fb7\u9c81\u5947\u89c1\u9648\u5929\u5b87\u75be\u5982\u98de\u9e1f\uff0c\u5df2\u81ea\u5413\u4e86\u4e00\u8df3\uff0c\u9648\u5929\u5b87\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4e00\u62db\u201c\u5012\u633d\u94f6\u6cb3\u201d\uff0c\u5c06\u5fb7 \u9c81\u5947\u7684\u94a2\u7d22\u8361\u5f00\uff0c\u518d\u4e00\u62db\u201c\u5927\u9e4f\u5c55\u7fc5\u201d\uff0c\u5c06\u5fb7\u9c81\u5947\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u5f85\u5230\u8eab\u5f62\u843d\u5730\uff0c\u7b2c\u4e09\u62db \u201c\u51b0\u5ddd\u98de\u6068\u201d\u53c8\u5230\uff0c\u8fd9\u4e09\u62db\u4e00\u6c14\u5475\u6210\uff0c\u6b63\u662f\u51b0\u5ddd\u5251\u6cd5\u4e2d\u7684\u7cbe\u5999\u6740\u7740\uff0c\u5fb7\u9c81\u5947\u54ea\u91cc\u62b5\u6321\u5f97\u4f4f\uff0c \u53ea\u542c\u5f97\u5587\u7684\u4e00\u58f0\uff0c\u5fb7\u9c81\u5947\u5934\u4e0a\u7684\u516d\u89d2\u6bd7\u5362\u5e3d\u88ab\u9648\u5929\u5b87\u5229\u5251\u524a\u4e3a\u4e24\u534a\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5927\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u9648\u5929\u5b87\u88ab\u56f0\u51b0\u5bab\u6570\u6708\uff0c\u53cd\u800c\u56e0\u7978\u5f97\u798f\uff0c\u5f53\u771f\u662f\u5f97\u76ca\u4e0d\u6d45\u3002\u201d\u5fc3\u60f3 \u5fb7\u9c81\u5947\u4e0d\u662f\u9648\u5927\u5b57\u7684\u5bf9\u624b\uff0c\u81ea\u5df1\u80dc\u5238\u5728\u64cd\uff0c\u5f53\u4e0b\u7cbe\u795e\u5927\u632f\uff0c\u53f3\u638c\u4e00\u7275\u4e00\u5f15\uff0c\u628a\u90a3\u82e6\u884c\u50e7\u8eab\u5f62 \u7275\u52a8\uff0c\u5728\u539f\u5730\u8f6c\u4e86\u4e00\u4e2a\u5708\u5708\u3002 \u3000\u3000\u5510\u7ecf\u5929\u773c\u89c1\u90a3\u82e6\u884c\u50e7\u88ab\u81ea\u5df1\u7684\u5185\u529b\u6240\u8feb\uff0c\u6e10\u6709\u652f\u6301\u4e0d\u4f4f\u4e4b\u52bf\uff0c\u6b63\u62df\u518d\u8fd0\u7384\u529f\uff0c\u6323\u8131\u4ed6\u7684 \u91d1\u76c2\u5438\u529b\u3002\u5ffd\u542c\u5f97\u5fb7\u9c81\u5947\u53fd\u54e9\u5495\u5580\u7684\u7528\u85cf\u8bed\u8bf4\u9053\uff1a\u201c\u4f60\u5bf9\u610f\u4e2d\u4eba\u5c1a\u81ea\u65e0\u529b\u4fdd\u62a4\uff0c\u8fd8\u901e\u4ec0\u4e48\u5f3a \u66ff\u670b\u53cb\u52a9\u62f3\uff1f\u201d\u773c\u4e2d\u53d1\u51fa\u51b7\u51b7\u7684\u5149\u8292\uff0c\u76f4\u76ef\u7740\u9648\u5929\u5b87\u7684\u773c\u775b\uff0c\u9648\u5929\u5b87\u795e\u667a\u672c\u6765\u8fd8\u672a\u6e05\u9192\uff0c\u88ab \u4ed6\u8bf4\u8bdd\u4e00\u523a\uff0c\u5b9b\u5982\u5229\u9488\u523a\u5230\u4e86\u5fc3\u4e0a\uff0c\u5ffd\u7136\u63a9\u9762\u72c2\u53eb\uff0c\u8df3\u8fc7\u4e00\u8fb9\uff0c\u501a\u5728\u6811\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u6211\u8fde\u610f\u4e2d\u4eba\u90fd\u65e0\u6cd5\u4fdd\u62a4\uff0c\u4f55\u4ee5\u4e3a\u4eba\uff1f\u5440\uff0c\u829d\u5a1c\u5440\u829d\u5a1c\uff0c\u6211\u5bf9\u4e0d\u8d77\u4f60\u4e86\uff01\u201d \u3000\u3000\u5fb7\u9c81\u5947\u9053\uff1a\u201c\u5bf9\u5475\uff0c\u4f60\u597d\u597d\u54ed\u4e00\u573a\u5427\uff01\u201d\u5ffd\u5730\u789f\u789f\u602a\u7b11\uff0c\u94a2\u7d22\u4e00\u6296\uff0c\u53c8\u671d\u5510\u7ecf\u5929\u626b\u6765\uff0c \u94a2\u7d22\u5934\u4e0a\u7684\u4e24\u9897\u94a2\u73e0\u53ee\u5417\u4f5c\u54cd\uff0c\u773c\u89c1\u8fd9\u4e00\u4e0b\u975e\u628a\u5510\u7ecf\u5929\u6253\u778e\u4e0d\u53ef\uff0c\u5374\u5ffd\u89c1\u5510\u7ecf\u5929\u4e0e\u82e6\u884c\u50e7\u4e24 \u4eba\u7684\u8eab\u5b50\u90fd\u65cb\u8f6c\u4e0d\u4f11\uff0c\u8d8a\u8f6c\u8d8a\u75be\uff0c\u5fb7\u9c81\u5947\u7adf\u5206\u4e0d\u51fa\u8c01\u662f\u5e08\u53d4\uff0c\u8c01\u662f\u654c\u4eba\uff0c\u94a2\u7d22\u6253\u5230\u4e86\u4e24\u4eba\u7684 \u5934\u4e0a\uff0c\u53c8\u786c\u751f\u751f\u7684\u6536\u56de\uff0c\u6015\u6253\u9519\u4e86\u4eba\u3002\u5c31\u5728\u8fd9\u5239\u90a3\u7f8a\uff0c\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u4e00\u58f0\u957f\u5578\uff0c\u4e0d\u77e5\u600e\u7684\uff0c \u4e24\u4eba\u7684\u8eab\u5f62\u500f\u7684\u5206\u5f00\uff0c\u5510\u7ecf\u5929\u624b\u4e0a\u5df2\u591a\u4e86\u4e00\u67c4\u7cbe\u8292\u56db\u5c04\u7684\u957f\u5251\u3002\u5fb7\u9c81\u5947\u7684\u94a2\u7d22\u6b63\u5728\u4e24\u4eba\u5934\u4e0a \u76d8\u65cb\uff0c\u4e00\u8ba4\u51fa\u4e86\u5510\u7ecf\u5927\u7684\u8eab\u5f62\uff0c\u7acb\u523b\u626b\u4e0b\uff0c\u90a3\u82e6\u884c\u50e7\u5927\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u5fb7\u9c81\u5947\u6536\u7d22\u4e0d\u53ca\uff0c \u54e8\u7684\u4e00\u58f0\uff0c\u94a2\u7d22\u88ab\u5510\u7ecf\u5929\u7684\u6e38\u9f99\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u622a\uff0c\u7d22\u7aef\u7684\u4e24\u9897\u94a2\u73e0\u4e5f\u88ab\u524a\u6389\u4e86\u3002 \u3000\u3000\u539f\u6765\u5510\u7ecf\u5929\u4e0e\u90a3\u82e6\u884c\u50e7\u76f8\u6301\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5df2\u609f\u51fa\u4e86\u82e6\u884c\u50e7\u90a3\u4e2a\u91d1\u76c2\u94b5\u4e4b\u6240\u4ee5\u80fd\u5438\u4f4f\u81ea\u5df1 \u7684\u62f3\u5934\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u8fd9\u91d1\u76c2\u94b5\u662f\u4ec0\u4e48\u201c\u6cd5\u5b9d\u201d\uff0c\u800c\u662f\u56e0\u4e3a\u76c2\u94b5\u6025\u901f\u65cb\u8f6c\u6240\u751f\u7684\u5f15\u529b\uff0c\u8fd9\u9053\u7406 \u4e0e\u6025\u6d41\u6e4d\u4e2d\u7684\u6f29\u6da1\u80fd\u591f\u541e\u6ca1\u5de8\u821f\u7684\u9053\u7406\u76f8\u540c\u3002\u5510\u7ecf\u5929\u7684\u5929\u5c71\u6d3e\u5185\u529f\u662f\u6700\u4e0a\u4e58\u7684\u6b63\u5b97\u5185\u529f\uff0c\u6bd4 \u90a3\u82e6\u884c\u50e7\u672c\u5c31\u7a0d\u7a0d\u9ad8\u51fa\u4e00\u7b79\uff0c\u4e00\u609f\u51fa\u4eba\u5236\u80dc\u7684\u5999\u7406\uff0c\u77e5\u9053\u62f3\u5934\u4e0d\u80fd\u5411\u5916\u62c9\uff0c\u8d8a\u5411\u5916\u62c9\u5c31\u8d8a\u8981 \u88ab\u5b83\u96c4\uff0c\u4e8e\u662f\u88ab\u76c2\u94b5\u5957\u7740\u7684\u62f3\u5934\u4e5f\u8ddf\u7740\u65cb\u8f6c\uff0c\u4e0d\u8fc7\u65cb\u8f6c\u7684\u65b9\u5411\u5374\u5916\u9762\u76c2\u94b5\u65cb\u8f6c\u7684\u65b9\u5411\u76f8\u53cd\uff0c \u8fd9\u6837\u8f6c\u4e86\u4e24\u8f6c\u679c\u7136\u8131\u4e86\u51fa\u6765\u3002\u800c\u8a00\u884c\u50e7\u4e5f\u8d81\u7740\u5510\u7ecf\u5929\u5168\u529b\u65bd\u4e3a\u4e4b\xb7\u9645\uff0c\u5c06\u7af9\u6756\u593a\u51fa\uff0c\u8131\u79bb\u4e86 \u5510\u5929\u7684\u638c\u63e1\u3002 \u3000\u3000\u5510\u7ecf\u5929\u77e5\u9053\u8fd9\u4e24\u4eba\u4e00\u5b9a\u8fd8\u4e0d\u80af\u5e72\u4f11\uff0c\u4e00\u8131\u56f0\u4fbf\u7acb\u523b\u62d4\u51fa\u6e38\u9f99\u5b9d\u5251\uff0c\u679c\u7136\u90a3\u82e6\u884c\u50e7\u53c8\u6251\u4e86 \u4e0a\u6765\uff0c\u5de6\u624b\u7af9\u6756\uff0c\u53f3\u624b\u91d1\u76c2\uff0c\u8fde\u8d70\u602a\u62db\uff0c\u4ed6\u5403\u4e86\u4e8f\uff0c\u518d\u4e0d\u987e\u5e73\u65e5\u5355\u6253\u72ec\u6597\u7684\u89c4\u77e9\uff0c\u7d22\u6027\u6307\u70b9 \u5fb7\u9c81\u5947\u52a9\u4ed6\u88ad\u51fb\u3002\u8fd9\u65f6\u4e24\u4eba\u90fd\u4e0d\u6562\u4f3c\u9002\u624d\u7684\u4ee5\u5185\u529b\u76f8\u6301\uff08\u82e6\u884c\u50e7\u56e0\u4e3a\u77e5\u9053\u5510\u7ecf\u5929\u80dc\u4e8e\u81ea\u5df1\uff0c \u800c\u5510\u7ecf\u5929\u5219\u987e\u5fcc\u5fb7\u9c81\u5947\u5728\u65c1\uff09\uff0c\u5510\u7ecf\u5929\u65bd\u5c55\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u8ffd\u98ce\u5251\u5f0f\uff0c\u8fde\u53d6\u653b\u52bf\uff0c\u82e6\u884c\u50e7\u5219\u4ee5 \u7af9\u6756\u70b9\u6233\uff0c\u5206\u654c\u5fc3\u795e\uff0c\u800c\u4ee5\u91d1\u94b5\u63a5\u5510\u7ecf\u5929\u7684\u5251\u62db\u3002\u9ec4\u91d1\u7684\u786c\u5ea6\u80dc\u4e8e\u94dc\u94c1\uff0c\u76c2\u94b5\u53c8\u539a\uff0c\u5373\u7b97\u88ab \u6e38\u9f99\u5251\u523a\u7740\uff0c\u4e5f\u4e0d\u865e\u635f\u574f\uff0c\u5728\u5175\u5668\u82e6\u884c\u50e7\u5e76\u4e0d\u5403\u4e8f\u3002 \u3000\u3000\u8fd9\u82e6\u884c\u50e7\u66fe\u662f\u51b0\u5ddd\u5929\u5973\u624b\u4e0b\u7684\u8d25\u5c06\uff0c\u6309\u8bf4\u4e5f\u4e0d\u662f\u5510\u7ecf\u5929\u7684\u5bf9\u624b\u3002\u4e0d\u8fc7\uff0c\u60c5\u5f62\u53c8\u6709\u70b9\u4e0d \u540c\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u5175\u5668\u4e00---\u51b0\u9b44\u5bd2\u5149\u5251\u548c\u6697\u5668\uff0c\u51b0\u9b44\u795e\u5f39\u6b63\u662f\u8fd9\u82e6\u884c\u50e7\u7684\u514b\u661f\uff0c\u800c\u5510\u7ecf\u5929\u8bba \u8d77\u6b66\u529f\u4e0d\u8f93\u4e8e\u51b0\u5ddd\u5929\u5973\uff0c\u6e38\u9f99\u5251\u5374\u5236\u8fd9\u82e6\u884c\u50e7\u4e0d\u4f4f\u3002 \u3000\u3000\u5fb7\u9c81\u5947\u662f\u90a3\u82e6\u884c\u50e7\u7684\u5e08\u4f84\uff0c\u5fb7\u9c81\u5947\u7684\u529f\u529b\u867d\u7136\u8fdc\u8fdc\u4e0d\u5982\u5510\u60ca\u5929\uff0c\u4e5f\u66fe\u82e6\u7ec3\u8fc7\u745c\u5496\u7684\u529f \u592b\uff0c\u79fb\u5f62\u6362\u6b65\uff0c\u5de7\u5999\u654f\u6377\u3002\u5510\u7ecf\u5929\u7684\u5251\u62db\u88ab\u82e6\u884c\u50e7\u7684\u91d1\u76c2\u4e00\u4e00\u63a5\u53bb\uff0c\u817e\u4e0d\u51fa\u5b9d\u5251\u6765\u524a\u5fb7\u9c81\u5947 \u7684\u94a2\u7d22\uff0c\u5fb7\u9c81\u5947\u4fbf\u5ffd\u7136\u4ece\u4fa7\u9762\u8fdb\u653b\uff0c\u5ffd\u7136\u53c8\u8dd1\u5230\u5510\u7ecf\u5929\u80cc\u540e\u88ad\u51fb\uff0c\u5f04\u5f97\u5510\u7ecf\u5929\u4e0d\u5f97\u4e0d\u5206\u795e\u5bf9 \u4ed8\uff0c\u5e38\u5e38\u8981\u95ea\u907f\u5fb7\u9c81\u5947\u7684\u5077\u88ad\u3002 \u3000\u3000\u4e09\u4eba\u8d70\u9a6c\u706f\u4f3c\u7684\u65cb\u8f6c\uff0c\u5404\u5c55\u5947\u62db\u5999\u7740\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5510\u7ecf\u5929\u7684\u653b\u52bf\u53d7\u5230\u7275 \u5236\uff0c\u6e10\u6e10\u5904\u4e8e\u4e0b\u98ce\u3002\u5077\u773c\u770b\u9648\u5929\u5b87\u65f6\uff0c\u9648\u5929\u5b87\u4ecd\u662f\u5446\u5446\u5730\u501a\u5728\u6811\u4e0a\uff0c\u51dd\u671b\u7740\u60a0\u60a0\u7684\u767d\u4e91\u3002\u5510 \u7ecf\u5929\u65e2\u4e3a\u81ea\u5df1\u7740\u6025\uff0c\u4e5f\u4e3a\u9648\u5929\u5b87\u53ef\u601c\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u4e4d\u9022\u60e8\u53d8\uff0c\u4f24\u75db\u672a\u8fc7\uff0c\u602a\u4e0d \u5f97\u5982\u6b64\u4e86\u3002\u201d\u4e0d\u5fcd\u50ac\u4ed6\u76f8\u52a9\u3002\u9648\u5929\u5b87\u5728\u4f24\u75db\u4e4b\u4e2d\uff0c\u5373\u7b97\u50ac\u4ed6\uff0c\u4e5f\u672a\u5fc5\u80fd\u5c06\u4ed6\u5524\u9192\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8feb\u5904\u4e0b\u98ce\uff0c\u82e6\u884c\u50e7\u4e0e\u5fb7\u9c81\u5947\u653b\u52bf\u9aa4\u76db\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u5f53\u201d\u4e24\u58f0\uff0c\u5510\u7ecf\u5929\u523a\u5fb7\u9c81\u5947\u7684 \u4e24\u62db\uff0c\u5251\u5c16\u90fd\u523a\u5230\u82e6\u884c\u50e7\u7684\u91d1\u76c2\u94b5\u4e0a\u3002\u5fb7\u9c81\u5947\u7684\u94a2\u7d22\u6296\u5f97\u7b14\u76f4\uff0c\u7adf\u7136\u5f53\u4f5c\u957f\u67aa\u4f7f\u7528\uff0c\u523a\u5510\u7ecf \u5929\u7684\u54bd\u5589\u3002\u5510\u7ecf\u5929\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u94a2\u7d22\u4ece\u4ed6\u7684\u5934\u9876\u63a0\u8fc7\uff0c\u5ffd\u5730\u53c8\u53d8\u4f5c\u8f6f\u97ad\u4f7f\u7528\uff0c\u547c\u7684 \u4e00\u58f0\u5708\u4e86\u56de\u6765\uff1b\u90a3\u82e6\u884c\u50e7\u7528\u91d1\u76c2\u94b5\u538b\u4f4f\u5510\u7ecf\u5929\u7684\u6e38\u9f99\u5251\uff0c\u5de6\u624b\u7684\u7eff\u7af9\u6756\u4e5f\u70b9\u5230\u4e86\u5510\u7ecf\u5929\u5c0f\u8179 \u7684\u201c\u6108\u6c14\u7a74\u201d\u3002\u8fd9\u4e24\u62db\u914d\u5408\u5f97\u7cbe\u5999\u65e0\u4f26\uff0c\u5510\u7ecf\u5929\u4e0d\u8bba\u5411\u54ea\u65b9\u9003\u907f\u90fd\u96be\u4ee5\u907f\u8fc7\uff0c\u5510\u7ecf\u5929\u5438\u4e00\u53e3 \u6c14\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u5e73\u7a7a\u62d4\u8d77\uff0c\u80cc\u5fc3\u540e\u649e\uff0c\u4ed6\u8eab\u4e0a\u7a7f\u6709\u91d1\u4e1d\u5b9d\u7532\uff0c\u51c6\u5907\u786c\u63a5\u5fb7\u9c81\u5947\u7684\u4e00\u97ad\uff0c\u540c\u65f6 \u4e5f\u51c6\u5907\u4ee5\u95ed\u7a74\u7684\u529f\u592b\uff0c\u63a5\u82e6\u884c\u50e7\u7684\u7af9\u6756\u70b9\u7a74\u6740\u624b\u3002\u4f46\u8fd9\u6837\u505a\u5b9e\u662f\u5371\u9669\u4e4b\u6781\uff0c\u5fb7\u9c81\u5947\u7684\u529f\u529b\u4e0d \u9ad8\uff0c\u90a3\u4e00\u97ad\u4e5f\u8bb8\u65e0\u751a\u4f24\u5bb3\uff0c\u82e6\u884c\u50e7\u90a3\u4e00\u6233\uff0c\u5374\u662f\u5929\u7afa\u7684\u5929\u9b54\u6756\u6cd5\u4e2d\u6700\u5389\u5bb3\u7684\u6740\u624b\uff0c\u4e13\u7834\u5185\u5bb6 \u6c14\u529f\uff0c\u5510\u7ecf\u5929\u7684\u95ed\u7a74\u529f\u592b\u662f\u5426\u80fd\u633a\u4f4f\uff0c\u90a3\u5c31\u5728\u672a\u53ef\u77e5\u4e4b\u6570\u4e86\u3002 \u3000\u3000\u6b63\u5728\u94a2\u7d22\u7af9\u6756\u5939\u51fb\u800c\u6765\uff0c\u582a\u582a\u5c31\u8981\u89e6\u5230\u5510\u7ecf\u5929\u8eab\u4f53\u4e4b\u9645\uff0c\u90a3\u82e6\u884c\u50e7\u5ffd\u5730\u4e00\u58f0\u602a\u53eb\uff0c\u7af9\u6756 \u4e0d\u5411\u524d\u70b9\uff0c\u53cd\u800c\u5411\u540e\u4e00\u4e2a\u540e\u7ffb\uff0c\u4f3c\u4e4e\u7ed9\u4e00\u80a1\u5927\u529b\u63a8\u4e86\u51fa\u53bb\uff0c\u7ad9\u7acb\u4e0d\u7a33\uff0c\u6025\u7528\u7af9\u6756\u652f\u5730\uff0c\u63a5\u8fde \u6253\u4e86\u51e0\u4e2a\u5927\u7ffb\uff0c\u6eda\u4e0b\u5c71\u5761\u3002\u90a3\u5fb7\u9c81\u5947\u88ab\u5510\u7ecf\u5929\u80cc\u5fc3\u4e00\u649e\uff0c\u8eab\u5f62\u4e5f\u98de\u4e86\u8d77\u6765\uff0c\u5e78\u800c\u4ed6\u7684\u745c\u5496\u529f \u592b\u4e5f\u7ec3\u5230\u4e86\u7b2c\u4e09\u6bb5\u7684\u5883\u754c\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5b66\u4ed6\u7684\u5e08\u53d4\u6837\u5b50\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u7b4b\u6597\uff0c\u6d88\u53bb\u4e86\u5510 \u7ecf\u5929\u53cd\u51fb\u7684\u5185\u529b\uff0c\u8ddf\u7740\u5e08\u53d4\u6eda\u4e0b\u5c71\u5761\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5b50\u52a8\u4f5c\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u5510\u7ecf\u5929\u5ffd\u800c\u8131\u9669\uff0c\u81ea\u5df1\u4e5f\u5f04\u5f97\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u5fb7\u9c81\u5947\u662f\u7ed9\u5510\u7ecf\u5929\u649e\u8dcc\u7684\uff0c\u4f46\u90a3\u82e6\u884c\u50e7\u7684\u7af9\u6756\u5e76\u672a\u89e6\u53ca\u5510\u7ecf\u5929\u7684\u8eab\u4f53\uff0c\u5374\u4f55\u4ee5\u7a81\u7136\u6536\u6756 \u4e0d\u6233\uff0c\u800c\u4e14\u597d\u4f3c\u88ab\u4e00\u80a1\u65e0\u5f62\u7684\u6f5c\u529b\u63a8\u5f00\u4e00\u822c\uff0c\u96be\u9053\u662f\u90a3\u82e6\u884c\u50e7\u5ffd\u53d1\u6148\u60b2\uff0c\u8fd8\u662f\u6697\u4e2d\u6709\u4eba\u76f8 \u52a9\uff1f\u5510\u7ecf\u5929\u76ee\u9001\u8fd9\u4e24\u4eba\u6eda\u4e0b\u5c71\u5761\uff0c\u500f\u5ffd\u4e0d\u89c1\uff0c\u5fc3\u4e2d\u4e00\u7247\u832b\u7136\uff0c\u5341\u5206\u4e0d\u89e3\u3002 \u3000\u3000\u5ffd\u95fb\u5f97\u4e00\u58f0\u6781\u5176\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u4ece\u6797\u5b50\u91cc\u53d1\u51fa\uff0c\u8fd9\u7b11\u58f0\u5341\u5206\u719f\u6089\uff0c\u5510\u7ecf\u5929\u4e0d\u5047\u601d\u7d22\uff0c\u8eab\u5f62 \u6025\u8d77\uff0c\u6b63\u5f85\u7a7f\u6797\u800c\u5165\uff0c\u5bfb\u89c5\u8fd9\u53d1\u58f0\u4e4b\u4eba\uff0c\u5ffd\u5730\u773c\u524d\u5f69\u8272\u7f24\u7eb7\uff0c\u4e00\u4e2a\u82b1\u73af\u4ece\u6797\u4e2d\u98de\u51fa\uff0c\u89e6\u624b\u6c81 \u51c9\uff0c\u82b1\u73af\u4e0a\u8fd8\u5e26\u6709\u9732\u73e0\uff0c\u597d\u50cf\u521a\u521a\u7f16\u5c31\u3002 \u3000\u3000\u5510\u7ecf\u5929\u63a5\u4e86\u82b1\u73af\u4e00\u770b\uff0c\u4e0a\u9762\u7528\u82b1\u679d\u7ed3\u6210\u56db\u4e2a\u5c0f\u5b57\uff1a\u201c\u901f\u79bb\u8428\u8fe6\u201d\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff0c\u8fd9\u7b11 \u58f0\uff0c\u8fd9\u82b1\u73af\uff0c\u8fd9\u63b7\u82b1\u73af\u7684\u624b\u6cd5\uff0c\u4e0e\u81ea\u5df1\u4e0a\u6b21\u5728\u5ce8\u5d4b\u5c71\u4e0a\u5bfb\u89c5\u51b0\u5ddd\u5929\u5973\u4e4b\u65f6\uff0c\u6240\u78b0\u5230\u7684\u4e00\u6a21\u4e00 \u6837\uff0c\u4e0a\u5510\u7ecf\u5929\u4ee5\u4e3a\u90a3\u63b7\u82b1\u73af\u7684\u4eba\u662f\u51b0\u5ddd\u5929\u5973\uff0c\u4f46\u540e\u6765\u4ed4\u7ec6\u601d\u91cf\uff0c\u51b0\u5ddd\u5929\u5973\u53c8\u4f3c\u4e4e\u6ca1\u6709\u8fd9\u79cd\u529f \u529b\u3002\u4eca\u6b21\u5510\u7ecf\u5929\u77e5\u9053\u51b0\u5ddd\u5929\u5973\u4e00\u5b9a\u8fd8\u6ca1\u8d76\u5230\uff0c\u63b7\u82b1\u73af\u7684\u4eba\u65ad\u4e4e\u4e0d\u4f1a\u662f\u51b0\u5ddd\u5929\u5973\u4e86\uff0c\u90a3\u4e48\u4e0d\u662f \u51b0\u5ddd\u5929\u5973\u53c8\u662f\u8c01\u5462\uff1f \u3000\u3000\u7b11\u58f0\u6447\u66f3\uff0c\u4ece\u6e05\u8106\u54cd\u4eae\u53d8\u4e3a\u5e7d\u5fae\uff0c\u6e10\u9ad8\u6e10\u8fdc\uff0c\u5b9b\u82e5\u6e38\u4e1d\u8885\u7136\uff0c\u82e5\u65ad\u82e5\u7eed\uff0c\u4f46\u4ecd\u662f\u97f3\u7ec6\u800c \u6e05\u3002\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u8fd9\u5239\u90a3\u95f4\uff0c\u7b11\u58f0\u7531\u8fd1\u800c\u8fdc\uff0c\u8fd9\u4eba\u5df2\u7ecf\u662f\u5728\u6570\u91cc\u4e4b\u5916\u4e86\uff0c\u6709\u8fd9\u7b49\u672c\u4e8b \u4eba\u4e16\u4e0a\u5be5\u5be5\u53ef\u6570\uff0c\u5510\u7ecf\u5929\u5fc3\u5934\u4e00\u52a8\uff0c\u53eb\u9053\uff1a\u201c\u59e8\u5988\uff0c\u59e8\u5988\uff01\u201d\u8fd9\u4ed6\u624d\u60f3\u5230\u51af\u7433\u5934\u4e0a\u3002\u51af\u7433\u5584 \u4f1a\u6458\u53f6\u98de\u82b1\u7684\u529f\u592b\uff0c\u53c8\u5929\u751f\u4e00\u526f\u6dd8\u6c14\u7684\u6027\u60c5\uff0c\u6700\u559c\u6b22\u548c\u5c0f\u8f88\u5f00\u73a9\u7b11\uff0c\u8fd9\u4e24\u6b21\u5411\u81ea\u5df1\u63b7\u82b1\u73af\u7684 \u4eba\uff0c\u9664\u4e86\u5979\u7edd\u65e0\u522b\u4eba\uff0c\u53ea\u53ef\u7b11\u81ea\u5df1\u4ee5\u524d\u53ea\u662f\u8bb0\u6302\u51b0\u5ddd\u5929\u5973\uff0c\u8fd9\u6837\u5bb9\u6613\u6599\u5230\u7684\u4eba\u7adf\u6ca1\u6709\u60f3\u5230\u3002 \u3000\u3000\u5510\u7ecf\u5929\u53eb\u4e86\u4e24\u58f0\u201c\u59e8\u5988\u201d\uff0c\u7b11\u58f0\u53bb\u5f97\u66f4\u8fdc\uff0c\u542c\u4e0d\u89c1\u3002\u5510\u7ecf\u5929\u77e5\u9053\u59e8\u5988\u7684\u813e\u6c14\uff0c\u8ffd\u4e5f\u6ca1 \u7528\u3002\u56de\u5934\u770b\u90a3\u82b1\u73af\uff0c\u5fc3\u9053\uff1a\u201c\u59e8\u5988\u600e\u4e48\u4f1a\u6765\u5230\u6b64\u95ee\uff0c\u5979\u4e3a\u4ec0\u4e48\u53eb\u6211\u79bb\u5f00\u8428\u8fce\u5462\uff1f\u201d\u60f3\u4e0d\u51fa\u4e2a \u6240\u4ee5\u7136\u6765\uff0c\u53ea\u5f53\u662f\u59e8\u5988\u5f00\u4ed6\u73a9\u7b11\u3002\u5c82\u77e5\u51af\u7433\u81ea\u4ed6\u4e8c\u6b21\u79bb\u5f00\u5929\u5c71\uff0c\u5357\u4e0b\u5ce8\u5d4b\u65f6\u5f00\u59cb\uff0c\u5c31\u8ddf\u7740\u4ed6 \u4e86\uff0c\u800c\u8fd9\u4e00\u6b21\u4e5f\u5e76\u975e\u53ea\u662f\u5f00\u73a9\u7b11\u7684\u3002 \u3000\u3000\u5510\u7ecf\u5929\u56de\u8fc7\u5934\u6765\uff0c\u5bfb\u89c5\u9648\u5929\u5b87\uff0c\u53ea\u89c1\u9648\u5929\u5b87\u8e72\u5728\u6811\u4e0a\uff0c\u6b63\u5728\u6811\u679d\u5728\u5730\u4e0a\u4e71\u5212\uff0c\u5730\u4e0a\u6b6a\u6b6a \u659c\u659c\u7684\u6ee1\u662f\u201c\u829d\u5a1c\u201d\u4e8c\u5b57\u3002\u5510\u7ecf\u5929\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u5c06\u4ed6\u62c9\u8d77\uff0c\u9053\uff1a\u201c\u8d70\u5475\u3002\u201d\u9648\u5929\u5b87\u832b\u7136\u8bf4 \u9053\uff1a\u201c\u53bb\u54ea\u513f\uff1f\u54ea\u513f\u627e\u5f97\u7740\u829d\u5a1c\uff1f\u201d\u5510\u7ecf\u5929\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u829d\u5a1c\u662f\u6b7b\u4e86\uff0c\u5979\u6b7b\u540e\u5fc5\u7136\u5f15\u8d77\u4e8b \u60c5\uff0c\u4f60\u4e0d\u66ff\u5979\u6599\u7406\uff0c\u5979\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u9648\u5929\u5b87\u609a\u7136\u4e00\u60ca\uff0c\u9192\u4e86\u51e0\u5206\uff0c\u9053\uff1a\u201c\u600e\u4e48\u6599\u7406\uff1f\u201d\u5510\u7ecf \u5929\u9053\uff1a\u201c\u5148\u8981\u4fdd\u91cd\u8eab\u5b50\uff0c\u56de\u53bb\u6211\u548c\u4f60\u8bf4\u3002\u201d\u4e24\u4eba\u98de\u6b65\u5954\u56de\u5ba3\u6170\u4f7f\u7684\u8859\u95e8\uff0c\u5230\u5185\u5ba4\u5750\u5b9a\uff0c\u5510\u7ecf \u5929\u66ff\u4ed6\u628a\u8109\uff0c\u89c1\u4ed6\u516d\u8109\u4e0d\u8c03\uff0c\u809d\u8109\u5c24\u5176\u90c1\u7ed3\uff0c\u77e5\u4ed6\u662f\u56e0\u4f24\u75db\u8fc7\u751a\u6240\u81f3\uff0c\u82e5\u4e0d\u5584\u4e3a\u8c03\u6cbb\uff0c\u53ea\u6015 \u4ed6\u7ec3\u6210\u7684\u90a3\u70b9\u5185\u529f\u6839\u57fa\uff0c\u90fd\u8981\u4ed8\u4e4b\u6d41\u6c34\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u73b0\u5728\u4ec0\u4e48\u4e5f\u4e0d\u8981\u60f3\uff0c\u597d\u597d\u9759\u5750\u4e00\u4f1a\u3002\u201d\u9648\u5929\u5b87\u8bd5\u4e00\u9759\u5750\uff0c\u534a\u664c\u53c8\u7741\u5f00\u773c \u775b\u8bf4\u9053\uff1a\u201c\u600e\u80fd\u591f\u4e0d\u60f3\u5475\u3002\u201d\u5510\u7ecf\u5929\u7565\u4e00\u6c89\u541f\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u6211\u6559\u4f60\u5982\u4f55\u4e0d\u60f3\u3002\u201d\u4f20\u4e86\u4ed6\u4e00 \u904d\u5929\u5c71\u6d3e\u4fee\u7ec3\u5185\u529f\u7684\u5fc3\u6cd5\uff0c\u5b66\u6b66\u4e4b\u4eba\uff0c\u5ffd\u95fb\u5185\u529f\u5999\u7406\uff0c\u5fc3\u4e2d\u7eb5\u6709\u4f55\u7b49\u5927\u4e8b\uff0c\u6ce8\u610f\u529b\u4e5f\u7ed9\u79fb\u8f6c \u4e86\u3002\u9648\u5929\u5b87\u8bd5\u6309\u5510\u7ecf\u5929\u6240\u4f20\u6388\u7684\u5fc3\u6cd5\u4fee\u7ec3\uff0c\u4f46\u89c9\u5965\u5999\u65e0\u7a77\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u6c89\u6d78\u5176\u4e2d\uff0c\u90a3\u6d88\u534a\u4e2a \u65f6\u8fb0\uff0c\u4fbf\u89c9\u5fc3\u5730\u7a7a\u660e\uff0c\u679c\u7136\u767e\u5ff5\u4e0d\u751f\uff0c\u5510\u7ecf\u5929\u77e5\u9053\u4ed6\u8fd9\u6837\u4e00\u5750\uff0c\u53ef\u4ee5\u5750\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u8ba9\u4ed6 \u5728\u623f\u4e2d\u9759\u5750\uff0c\u81ea\u5df1\u6084\u6084\u8d70\u5230\u5916\u9762\u6253\u542c\u3002 \u3000\u3000\u8fd9\u65f6\u5e9c\u8859\u5185\u5df2\u77e5\u9053\u4e86\u5587\u561b\u5bfa\u6240\u53d1\u751f\u7684\u5927\u4e8b\uff0c\u4eba\u5fc3\u6d6e\u52a8\uff0c\u5510\u7ecf\u5929\u5c06\u603b\u7ba1\u5524\u6765\uff0c\u547d\u4ed6\u5429\u5490\u8859 \u5185\u4eba\u4f17\uff0c\u4e0d\u8bb8\u5916\u51fa\uff0c\u5e76\u5c0f\u5fc3\u5de1\u89c6\uff0c\u4e0d\u5f97\u677e\u61c8\u3002\u76f4\u5230\u508d\u665a\u65f6\u5206\uff0c\u5ba3\u6170\u4f7f\u9648\u5b9a\u57fa\u624d\u56de\u5230\u8859\u95e8\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u6ee1\u9762\u5fe7\u8651\u7684\u795e\u8272\uff0c\u6101\u7709\u4e0d\u5c55\uff0c\u7ba1\u5bb6\u7684\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8001\u7237\u751f\u5e73\u7ecf\u8fc7\u591a\u5c11\u98ce \u6d6a\uff0c\u4e5f\u672a\u66fe\u89c1\u8fc7\u4f3c\u4eca\u65e5\u7684\u60ca\u5fe7\u3002\u201d\u9648\u5b9a\u57fa\u53eb\u7ba1\u5bb6\u7684\u5173\u4e0a\u5927\u95e8\uff0c\u52a0\u6d3e\u4e8c\u5341\u540d\u7cbe\u58ee\u5175\u4e01\u5728\u5916\u9762\u5b88 \u536b\uff0c\u5b89\u6392\u59a5\u5f53\u4e4b\u540e\uff0c\u9080\u5510\u7ecf\u5929\u8fdb\u5185\u5ba4\u5bc6\u8c08\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u7b2c\u4e00\u53e5\u8bdd\u5c31\u95ee\u9053\uff1a\u201c\u5b87\u513f\u5462\uff1f\u201d\u5510\u7ecf\u5929\u5c06\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\uff0c\u9648\u5b9a\u57fa\u5947\u9053\uff1a\u201c\u5b87\u513f\u7684 \u610f\u4e2d\u4eba\u5c31\u662f\u6c81\u5e03\u85e9\u738b\u7684\u5973\u513f\u5417\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u90a3\u4e2a\u540d\u5b57\u53eb\u505a\u5e7d\u840d\u7684\u51b0\u5bab\u4ed9\u5b50\u5462\u3002\u201d\u5e7d\u840d\u66fe\u5728\u9648 \u5929\u5b57\u5bb6\u4e2d\u4f4f\u8fc7\u8bb8\u591a\u5929\uff0c\u4e0e\u9648\u5929\u5b87\u5f62\u8ff9\u4eb2\u5bc6\uff0c\u6545\u6b64\u9648\u5b9a\u57fa\u6709\u6b64\u7591\u5fc3\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5982\u6b64\uff0c\u4e8b\u60c5\u5c31\u66f4\u4e0d\u597d\u4e86\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9648\u5b9a\u57fa\u9053\uff1a \u201c\u770b\u6765\u4fc4\u9a6c\u767b\u5c31\u8981\u6380\u8d77\u4e00\u573a\u5185\u4e71\u3002\u6211\u628a\u4f60\u4eec\u9003\u8d70\u4e4b\u540e\u5587\u561b\u5bfa\u4e2d\u6240\u53d1\u751f\u7684\u4e8b\u60c5\u544a\u8bc9\u4f60\u5427\uff0c\u8bf7\u4f60 \u66ff\u6211\u53c2\u8be6\u53c2\u8be6\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u4e5f\u77a7\u89c1\u6211\u4eec\u5417\uff1f\u201d\u9648\u5b9a\u57fa\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u867d\u6362\u4e86\u85cf \u4eba\u7684\u670d\u9970\uff0c\u5c82\u80fd\u7792\u8fc7\u6211\u7684\u773c\u775b\uff1f\u5f53\u4f60\u4eec\u8fd8\u672a\u9003\u51fa\u51fa\u90a3\u6708\u7259\u95e8\u7684\u65f6\u5019\uff0c\u6cd5\u738b\u8ffd\u8d76\u4e0a\u53bb\uff0c\u6211\u5413\u5f97 \u4e00\u9897\u5fc3\u90fd\u51e0\u4e4e\u8df3\u4e86\u51fa\u6765\uff0c\u5ffd\u7136\u6709\u4e00\u4e2a\u53e4\u53e4\u602a\u602a\u7684\u9752\u5e74\u51fa\u6765\u4e86\uff0c\u957f\u5f97\u633a\u7075\u4fca\uff0c\u76f8\u8c8c\u770b\u6765\u8fd8\u6709\u4e24 \u4e09\u5206\u50cf\u5b87\u513f\u5462\u3002\u5440\uff0c\u8fd9\u4eba\u771f\u4e0d\u77e5\u662f\u5403\u4e86\u72ee\u5b50\u7684\u5fc3\u8fd8\u662f\u8c79\u5b50\u80c6\uff1f\u4ed6\u5c45\u7136\u6562\u548c\u6d3b\u4f5b\u52a8\u624b\uff01\u201d\u5510\u7ecf \u5929\u77e5\u9053\u9648\u5b9a\u57fa\u53e3\u4e2d\u8fd9\u4e2a\u53e4\u602a\u7684\u9752\u5e74\u5fc5\u662f\u91d1\u4e16\u9057\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u540e\u6765\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4f3c\u5927\u9e1f\u4e00\u6837\u4ece\u5c4b\u6a90\u4e0a\u4e0b\u6765\uff0c\u6d3b\u4f5b\u7ad9\u5728\u5730\u4e0a\uff0c\u51b2\u7740\u4ed6\u5c31\u662f\u4e00\u62f3\uff0c\u8bf4\u4e5f \u5947\u602a\uff0c\u62f3\u5934\u8fd8\u5dee\u7740\u8001\u8fdc\uff0c\u53ea\u662f\u51cc\u7a7a\u4e00\u51fb\uff0c\u5c11\u5e74\u5c31\u4f3c\u7ed9\u4eba\u63a8\u4e86\u4e00\u628a\u7684\uff0c\u53c8\u6298\u56de\u5c4b\u6a90\u4e0a\uff0c\u63a5\u7740\u53c8 \u4e0b\u6765\uff0c\u6cd5\u738b\u51b2\u7740\u4ed6\u53c8\u662f\u4e00\u62f3\uff0c\u4ed6\u53c8\u6298\u56de\u539f\u5904\uff0c\u5982\u662f\u8005\u4e09\u6b21\u4e4b\u591a\uff0c\u8fd9\u65f6\u6cd5\u738b\u7684\u56db\u5927\u5f1f\u5b50\u90fd\u5df1\u8df3 \u4e0a\u5c4b\u6a90\uff0c\u91c7\u53d6\u4e86\u5305\u56f4\u4e4b\u52bf\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u6cd5\u738b\u5462\uff1f\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u56db\u5927\u5f1f\u5b50\u8df3\u4e0a\u5c4b\u9876\uff0c\u663e\u51fa\u5341\u5206\u614e\u91cd\u7684\u6837\u5b50\uff0c\u5982 \u4e34\u5927\u654c\uff0c\u4ece\u56db\u65b9\u6162\u6162\u5408\u56f4\uff0c\u6cd5\u738b\u8fd8\u7ad9\u5728\u5c4b\u6a90\u5e95\u4e0b\uff0c\u5411\u7740\u90a3\u5c11\u5e74\u7684\u8eab\u5f71\uff0c\u63a5\u8fde\u731b\u51fb\u6570\u62f3\uff0c\u5c11\u5e74 \u4e0d\u6562\u8df3\u4e0b\u6765\uff0c\u53ea\u89c1\u6cd5\u738b\u6bcf\u51fb\u4e00\u62f3\uff0c\u90a3\u5c11\u5e74\u8eab\u5b50\u5c31\u6447\u6643\u4e00\u4e0b\uff0c\u773c\u89c1\u90a3\u56db\u5927\u5f1f\u5b50\u5c31\u8981\u6349\u7740\u4ed6\u4e86\uff0c \u6cd5\u738b\u7a81\u7136\u4e5f\u6643\u4e86\u4e00\u4e0b\uff0c\u4e00\u62f3\u5c06\u53d1\u672a\u53d1\uff0c\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u6325\u6325\u624b\u9053\uff1a\u2018\u8ba9\u4ed6\u8d70\u5427\uff01\u2019\u90a3\u5c11\u5e74\u4e00 \u58f0\u957f\u7b11\uff0c\u5728\u56db\u6c70\u5f1f\u5b50\u5305\u56f4\u4e4b\u4e2d\uff0c\u8eab\u5b50\u51cc\u7a7a\u98de\u8d77\uff0c\u4e00\u970e\u773c\u5c31\u5230\u4e86\u53e6\u4e00\u95f4\u5c4b\u9762\uff0c\u7aef\u4f2a\u662f\u75be\u5982\u9e70 \u96bc\uff0c\u500f\u5ffd\u8df3\u8fc7\u51e0\u91cd\u74e6\u9762\uff0c\u770b\u4e0d\u89c1\u4e86\u3002\u5927\u6bbf\u4e0a\u50e7\u4fd7\u5b98\u5458\u8bae\u8bba\u7eb7\u7eb7\uff0c\u6709\u7684\u8bf4\u8fd9\u662f\u6d3b\u4f5b\u5927\u663e\u795e\u901a\uff0c \u6709\u7684\u8bf4\u90a3\u5c11\u5e74\u662f\u5239\u652f\u5229\u9b54\u7684\u5316\u8eab\u4e0b\u4e16\u3002\u6545\u610f\u6765\u8bd5\u767d\u6559\u6cd5\u5de5\u7684\u6cd5\u529b\u7684\u3002\u201d\u5587\u561b\u6559\u7684\u795e\u8bdd\uff0c\u5239\u652f \u5229\u9b54\u662f\u4e0e\u4f5b\u7956\u5bf9\u654c\u7684\u4e00\u4e2a\u6076\u9b54\uff0c\u88ab\u4f5b\u7956\u5e7d\u7981\u5728\u6052\u6cb3\u6cb3\u5e95\u3002\u767d\u6559\u6cd5\u738b\u62ff\u4e0d\u4f4f\u4ed6\uff0c\u53ef\u89c1\u6cd5\u529b\u4e5f\u662f \u6709\u9650\u3002\u8bf4\u8fd9\u4e9b\u8bdd\u7684\u591a\u534a\u662f\u9ec4\u6f31\u5587\u561b\u7684\u50e7\u5b98\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u4e2d\u597d\u751f\u60ca\u8be7\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u767d\u6559\u6cd5\u738b\u7528\u7684\u662f\u968f\u5c71\u6253\u725b\u7684\u767e\u6b65\u795e\u62f3\uff0c\u81ea\u8db3\u4ee5\u9707\u4e16\u9a87 \u4fd7\u3002\u91d1\u4e16\u9057\u7684\u6b66\u529f\u9876\u591a\u53ea\u80fd\u4e0e\u6cd5\u738b\u6253\u4e2a\u5e73\u624b\uff0c\u4ed6\u600e\u4e48\u80fd\u5728\u6cd5\u738b\u795e\u62f3\u731b\u51fb\u4e4b\u4e0b\uff0c\u56db\u5927\u5f1f\u5b50\u5305\u56f4 \u4e4b\u4e2d\uff0c\u5b89\u7136\u8131\u8eab\u800c\u53bb\uff1f\u3002\u96be\u9053\u53e6\u6709\u4ec0\u4e48\u4eba\u6697\u4e2d\u76f8\u52a9\u4ed6\u4e48\uff1f\u542c\u9648\u5b9a\u57fa\u6240\u8bf4\u7684\u60c5\u5f62\uff0c\u6cd5\u738b\u4f3c\u662f\u88ab \u4ec0\u4e48\u9ad8\u4eba\u6697\u4e2d\u8b66\u544a\u4e86\u3002\u8fd9\u4e0d\u51fa\u9762\u7684\u9ad8\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d\u6e05\u7ecf\u5929\u600e\u4e48\u4e5f\u731c\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2a\u6697\u52a9\u91d1\u4e16 \u9057\u7684\u4eba\u53c8\u662f\u4ed6\u7684\u59e8\u6bcd\u51af\u7433\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u7eed\u9053\uff1a\u201c\u518d\u8bf4\u5927\u6bbf\u4e0a\u7684\u4e8b\u60c5\u3002\u6c81\u5e03\u85e9\u738b\u7684\u5973\u513f\u2026\u2026\u201d\u5510\u7ecf\u5929\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u5979\u540d\u53eb \u829d\u5a1c\u3002\u201d\u9648\u5b9a\u57fa\u70b9\u70b9\u5934\u9053\uff1a\u201c\u829d\u5a1c\u523a\u6b7b\u4e86\u571f\u53f8\uff0c\u7acb\u523b\u62d4\u5200\u81ea\u520e\uff0c\u8fd9\u6869\u4e8b\u4f60\u4eec\u5df1\u89c1\u5230\u4e86\u3002\u829d\u5a1c \u81ea\u520e\u4e4b\u540e\uff0c\u4fc4\u9a6c\u767b\u5c31\u8fc7\u6765\u5c06\u5979\u7684\u9762\u7eb1\u5b8c\u5168\u6495\u5f00\uff0c\u5ffd\u7136\u53eb\u9053\u3002\u4f60\u4eec\u8fc7\u6765\u770b\uff0c\u8fd9\u4e2a\u6c81\u5e03\u85e9\u738b\u7684\u5973 \u513f\uff0c\u539f\u6765\u5c31\u662f\u4ee5\u524d\u5077\u8fdb\u571f\u53f8\u5bb6\u4e2d\u5077\u9a6c\u7eb5\u706b\u7684\u5973\u8d3c\u3002\u2019\u571f\u53f8\u5e26\u6765\u7684\u4eba\u90fd\u62e5\u4e0a\u53bb\u770b\uff0c\u6709\u4e00\u5927\u534a\u8ba4 \u5f97\uff0c\u7eb7\u7eb7\u8bae\u8bba\u3002\u4fc4\u9a6c\u767b\u53c8\u51b2\u7740\u6211\u7b11\u9053\uff1a\u2018\u9648\u5927\u4eba\uff0c\u8fd9\u4e5f\u5c31\u662f\u4f60\u4ee5\u524d\u6781\u529b\u6073\u6c42\u571f\u53f8\uff0c\u4fdd\u91ca\u7684\u90a3 \u4e2a\u5973\u8d3c\u5462\uff01\u2019\u4fc4\u9a6c\u767b\u7684\u7b11\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\uff0c\u6211\u6b63\u60f3\u56de\u8bf4\uff1a\u2018\u90a3\u662f\u4f60\u8bf7\u6211\u4fdd\u91ca\u7684\u2019\u6cd5\u738b\u7387\u9886\u56db\u5927 \u5f1f\u5b50\u5df2\u4ece\u4e0b\u9762\u8d70\u4e0a\u6765\uff0c\u4fc4\u9a6c\u767b\u548c\u571f\u53f8\u7684\u4eba\u5ffd\u7136\u62a2\u4e86\u571f\u53f8\u4e0e\u829d\u5a1c\u7684\u5c38\u4f53\uff0c\u53c8\u8bf4\u52a8\u4e86\u8fbe\u8d56\u6d3b\u4f5b\u7684 \u4ee3\u8868\uff0c\u5c06\u53d7\u4f24\u7684\u73ed\u63b8\u6d3b\u4f5b\u7684\u4ee3\u8868\u4e5f\u4e00\u5e76\u5e26\u8d70\u4e86\u3002\u4fc4\u9a6c\u767b\u4e34\u8d70\u65f6\u5927\u58f0\u75be\u547c\uff0c\u8bf4\u8981\u66ff\u571f\u53f8\u62a5\u4ec7\uff0c \u53eb\u571f\u53f8\u7684\u4eba\u8ddf\u7740\u4ed6\u6025\u901f\u56de\u5e9c\uff0c\u767d\u6559\u6cd5\u738b\u4e5f\u4e0d\u4fbf\u963b\u62e6\uff0c\u773c\u89c1\u4ed6\u6d0b\u6d0b\u5f97\u610f\u7684\u4e0e\u8fbe\u8d56\u73ed\u7985\u7684\u4e24\u4f4d\u4ee3 \u8868\u8d70\u51fa\u5bfa\u95e8\uff0c\u771f\u4e0d\u77e5\u4ed6\u8981\u95f9\u51fa\u4f55\u7b49\u4e71\u5b50\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u7684\u6765\u5386\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u662f\u5b58\u5fc3\u8981\u5728\u897f\u85cf\u641e\u8d77 \u4e00\u573a\u66b4\u4e71\u3002\u9648\u5927\u4eba\uff0c\u4f60\u5e94\u8be5\u8d76\u5feb\u4fee\u4e66\u62a5\u544a\u798f\u5eb7\u5b89\u3002\u201d\u9648\u5b9a\u57fa\u4e5f\u89c9\u5f97\u53ea\u80fd\u5982\u6b64\u505a\u4e86\uff0c\u6b63\u5728\u4fee \u4e66\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u5df2\u662f\u95f9\u58f0\u5927\u4f5c\u3002 \u3000\u3000\u7ba1\u5bb6\u7684\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u7387\u9886\u4e00\u5927\u961f\u85cf\u5175\uff0c\u5df2\u5c06\u8859\u95e8\u56e2\u56e2\u56f4\u4f4f\u4e86\u3002\u201d\u9648\u5b9a\u57fa\u82e6\u7b11\u9053\uff1a \u201c\u8fd9\u4fc4\u9a6c\u767b\u4e0e\u6211\u4f55\u4ec7\u4f55\u6068\uff1f\u6765\u5f97\u8fd9\u822c\u5feb\uff0c\u96be\u9053\u8fd8\u6015\u6211\u8fd9\u671d\u5ef7\u547d\u5b98\u9003\u8d70\u4e0d\u6210\uff1f\u201d\u4e0e\u5510\u7ecf\u5929\u8d70\u4e0a \u5973\u5899\u7684\u57ce\u697c\u4e00\u770b\uff0c\u53ea\u89c1\u4fc4\u9a6c\u767b\u966a\u7740\u571f\u53f8\u7684\u592b\u4eba\u5728\u5899\u4e0b\u5927\u9a82\uff0c\u56db\u5927\u6d85\u5df4\u5206\u5217\u5de6\u53f3\uff0c\u90a3\u5370\u5ea6\u6614\u884c \u618e\u548c\u5fb7\u9c81\u5947\u4e5f\u5728\u519b\u4e2d\u3002\u4fc4\u9a6c\u767b\u628a\u624b\u4e00\u6837\uff0c\u4f17\u915d\u5175\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u628a\u6c49\u5b98\u65a9\u5c3d\u6740\u7edd\uff0c\u628a\u6c49\u4eba\u90fd\u8d76 \u51fa\u53bb\u3002\u4ed6\u4eec\u6ca1\u6709\u4e00\u4e2a\u662f\u597d\u4e1c\u897f\uff0c\u90fd\u662f\u5230\u897f\u85cf\u6765\u6363\u4e71\u7684\u3002\u201d \u3000\u3000\u9648\u5b9a\u57fa\u5728\u57ce\u5899\u4e0a\u5411\u4e0a\u53f8\u7684\u592b\u4eba\u65bd\u793c\uff0c\u9053\uff1a\u201c\u8d35\u571f\u53f8\u88ab\u523a\uff0c\u771f\u662f\u4e0d\u5e78\u4e4b\u4e8b\u3002\u672c\u5ba3\u6170\u4f7f\u8c28\u81f4 \u60bc\u5ff5\u4e4b\u610f\u3002\u4f46\u8d35\u4e0a\u53f8\u88ab\u523a\uff0c\u4e0e\u6211\u4f55\u4e8e\uff1f\u6562\u95ee\u592b\u4eba\u9886\u5175\u524d\u6765\uff0c\u6240\u4e3a\u4f55\u4e8b\uff1f\u8fd9\u4e8b\u60c5\u53c8\u600e\u4e48\u80fd\u8fc1\u6012 \u6240\u6709\u6c49\u4eba\uff1f\u201d\u571f\u53f8\u592b\u4eba\u88c1\u6307\u54ed\u9a82\u9053\uff1a\u201c\u9648\u5b9a\u57fa\u4f60\u4f11\u5f97\u5047\u6487\u6e05\uff0c\u8fd9\u5973\u8d3c\u82e5\u4e0d\u662f\u4f60\u4eec\u5506\u4f7f\u7684\uff0c\u5f53 \u5e74\u4f60\u4e3a\u4ec0\u4e48\u66ff\u5979\u4fdd\u91ca\uff0c\u4f60\u513f\u5b50\u53c8\u600e\u80af\u820d\u547d\u6551\u5979\uff1f\u201d\u4fc4\u9a6c\u767b\u63a5\u53e3\u9a82\u9053\uff1a\u201c\u6211\u4eec\u897f\u85cf\u7684\u4e8b\u60c5\u81ea\u5df1 \u4f1a\u7406\uff0c\u8981\u9632\u4eec\u6c49\u4eba\u6765\u4f5c\u4ec0\u4e48\uff1f\u4f60\u4eec\u8fd9\u6b21\u5506\u4f7f\u4e00\u4e2a\u5973\u8d3c\u51fa\u6765\u884c\u523a\uff0c\u6559\u5979\u5192\u5145\u662f\u6c81\u5e03\u85e9\u738b\u7684\u5973 \u513f\uff0c\u5206\u660e\u662f\u60f3\u6311\u8d77\u897f\u85cf\u7684\u5185\u4e71\uff0c\u597d\u8ba9\u4f60\u4eec\u6c49\u4eba\u6e14\u7fc1\u5f97\u5229\uff0c\u5b9e\u884c\u5206\u800c\u6cbb\u4e4b\u4e4b\u8ba1\uff0c\u4e0d\u628a\u4f60\u4eec\u8d76 \u8d70\uff0c\u54b1\u4eec\u897f\u85cf\u4f11\u5f97\u5e73\u5b89\u3002\u201d \u3000\u3000\u9648\u5b9a\u57fa\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u5206\u660e\u662f\u4fc4\u9a6c\u767b\u85c9\u7aef\u751f\u4e8b\uff0c\u60f3\u6311\u8d77\u897f\u85cf\u7684\u53db\u53d8\uff0c\u5374\u53cd\u800c\u8bec\u8d56\u4e86 \u4ed6\u3002\u6b63\u5f85\u6b63\u8a00\u65a5\u8d23\uff0c\u4fc4\u9a6c\u767b\u62c9\u5f00\u4e94\u77f3\u5927\u5f13\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u7236\u5b50\u5c31\u662f\u6740\u571f\u53f8\u7684\u4e3b\u4f7f\u4eba\uff0c\u8fd8\u8fa9\u4ec0 \u4e48\uff1f\u770b\u7bad\uff01\u201d\u5506\u7684\u4e00\u7bad\u5c04\u6765\uff0c\u5510\u7ecf\u5929\u8eab\u5f62\u4e00\u6643\uff0c\u62e6\u5728\u9648\u5b9a\u57fa\u7684\u9762\u524d\uff0c\u53cc\u6307\u4e00\u67d1\uff0c\u628a\u90a3\u652f\u5229\u7bad \u67d1\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u65e0\u803b\u597d\u5f92\uff0c\u4f60\u4e5f\u770b\u7bad\uff01\u201d\u53cc\u6307\u4e00\u5f39\uff0c\u90a3\u652f\u5229\u7bad\u98de\u4e86\u56de\u53bb\uff0c\u6bd4\u7528\u5f13\u5f26\u5c04\u51fa\u8fd8\u66f4 \u5389\u5bb3\u3002\u4fc4\u9a6c\u767b\u6025\u5fd9\u7f29\u5934\uff0c\u7528\u5927\u5f13\u4e00\u6321\uff0c\u50fb\u5566\u4e00\u54cd\uff0c\u90a3\u5f20\u5927\u5f13\u7adf\u88ab\u5c04\u65ad\uff01\u4fc4\u9a6c\u767b\u614c\u5f97\u5728\u5730\u4e0a\u6253 \u4e86\u4e2a\u6eda\uff0c\u907f\u8fdb\u5165\u4e1b\u4e4b\u4e2d\uff0c\u4ecd\u81ea\u5927\u58f0\u559d\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d\u9877\u65f6\u5343\u7bad\u9f50\u53d1\uff0c\u85cf\u5175\u52c7\u731b\u8fdb\u653b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u821e\u5251\u6321\u7bad\uff0c\u4fdd\u62a4\u9648\u5b9a\u57fa\u8d70\u4e0b\u5973\u5899\uff0c\u7136\u540e\u4eb2\u81ea\u6307\u6325\uff0c\u8859\u95e8\u5185\u7684\u5175\u4e01\u53ea\u6709\u4e00\u767e\u591a\u4eba\uff0c \u800c\u56f4\u653b\u7684\u85cf\u5175\u8d77\u7801\u4e5f\u6709\u4e00\u5343\uff0c\u51e0\u4e4e\u5321\u4ee5\u4e00\u5f53\u5341\uff0c\u5e78\u800c\u8fd9\u4e00\u767e\u591a\u4eba\u90fd\u66fe\u7ecf\u8fc7\u9648\u5929\u5b87\u7684\u8bad\u7ec3\uff0c\u800c \u5ba3\u6170\u9675\u8859\u95e8\u91cd\u4fee\u4e4b\u540e\uff0c\u5efa\u7b51\u4e5f\u5f88\u5de9\u56fa\uff0c\u85cf\u5175\u867d\u591a\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5374\u662f\u7736\u4ee5\u653b\u4e0b\u3002\u85cf\u5175\u4eec\u51e0\u6b21\u7528 \u4e91\u68af\u5f3a\u653b\uff0c\u90fd\u88ab\u5510\u7ecf\u5929\u6298\u65ad\u68af\u5b50\uff0c\u4f46\u80c4\u7ecf\u5929\u4e5f\u4e0d\u613f\u6740\u4f24\u85cf\u5175\uff0c\u53ea\u662f\u5c3d\u529b\u628a\u4ed6\u4eec\u7684\u653b\u52bf\u904f\u6b62\u3002 \u3000\u3000\u5982\u662f\u8005\u56f4\u653b\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u53cc\u65b9\u90fd\u7b4b\u75b2\u529b\u7aed\uff0c\u5510\u7ecf\u5929\u5728\u8fd9\u4e00\u65e5\u4e00\u591c\u4e4b\u4e2d\uff0c\u6ca1\u6709\u7761\u8fc7\u7247\u523b\uff0c \u4ea6\u611f\u96be\u4ee5\u652f\u6301\uff0c\u5230\u7b2c\u4e09\u65e5\u65e9\u4e0a\uff0c\u85cf\u5175\u5ffd\u7136\u64a4\u9000\u4e86\u4e00\u534a\uff0c\u5510\u7ecf\u5929\u5947\u9053\uff1a\u201c\u6211\u6b63\u6015\u4ed6\u589e\u5175\u518d\u653b\uff0c \u600e\u4e48\u4ed6\u53cd\u800c\u51cf\u5175\uff1f\u83ab\u975e\u4fc4\u9a6c\u767b\u53c8\u6709\u4ec0\u4e48\u8be1\u8ba1\uff1f\u201d\u770b\u90a3\u4e9b\u85cf\u5175\u53ea\u662f\u5217\u9635\u56f4\u4f4f\uff0c\u5374\u5e76\u65e0\u8fdb\u653b\u7684\u8ff9 \u8c61\u3002\u4fc4\u9a6c\u767b\u548c\u5fb7\u9c81\u5947\u4ea6\u5df2\u4e0d\u5728\u519b\u4e2d\uff0c\u5510\u7ecf\u5929\u6b63\u5728\u601d\u7591\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\u4ece\u4e1c\u9762\u7a7a\u9699\u4e4b\u5730\u75be\u5954\u800c \u6765\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u62c2\u6653\u65f6\u5206\uff0c\u4eba\u5f71\u8fd8\u672a\u80fd\u770b\u5f97\u771f\u5207\uff0c\u90a3\u4e9b\u85cf\u5175\u4e0d\u77e5\u662f\u53cb\u662f\u654c\uff0c\u4e00\u65f6\u95f4\u5012\u4e0d\u6562\u653b \u51fb\uff0c\u90a3\u4eba\u5f71\u6765\u5f97\u6781\u5feb\uff0c\u500f\u5ffd\u95f4\u5df2\u8d8a\u8fc7\u4e24\u961f\u85cf\u5175\uff0c\u8fd9\u65f6\u624d\u770b\u6e05\u695a\u6765\u7684\u662f\u4e2a\u56db\u5341\u591a\u5c81\u4e66\u751f\u88c5\u675f\u7684 \u4eba\uff0c\u5b88\u7740\u5899\u5934\u7684\u5175\u4e01\u4e5f\u5df2\u6709\u4e00\u5927\u534a\u8ba4\u5f97\u51fa\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u662f\u8427\u8001\u5e08\uff01\u201d\u8427\u9752\u5cf0\u4ee5\u524d\u5728\u8859\u95e8 \u6559\u4e66\u65f6\uff0c\u5f62\u8c8c\u8870\u8001\uff0c\u6d3b\u50cf\u4e2a\u624b\u65e0\u64c5\u9e21\u4e4b\u529b\u3001\u79d1\u573a\u5931\u610f\u7684\u8001\u5112\u751f\uff0c\u4f17\u5175\u4e01\u89c1\u4ed6\uff0c\u53e3\u6b64\u77eb\u6377\uff0c\u90fd \u4e0d\u7981\u5567\u5567\u79f0\u5f02\u3002 \u3000\u3000\u85cf\u5175\u8fd9\u65f6\u4e5f\u770b\u6e05\u695a\u4e86\uff0c\u7eb7\u7eb7\u62e6\u622a\u3002\u8427\u9752\u5cf0\u62c2\u5c18\u8d77\u5904\uff0c\u78b0\u7740\u7684\u85cf\u5175\u7acb\u5373\u5012\u5730\uff0c\u85cf\u5175\u4e0d\u77e5\u9053 \u8fd9\u662f\u201c\u62c2\u7a74\u201d\u7684\u529f\u592b\uff0c\u4ee5\u4e3a\u662f\u5996\u6cd5\uff0c\u4e0b\u6562\u518d\u8ffd\u3002\u82e6\u884c\u50e7\u6025\u5fd9\u5954\u51fa\uff0c\u8427\u9752\u5cf0\u8dd1\u5f97\u5feb\uff0c\u4ed6\u8dd1\u5f97\u66f4 \u5feb\uff0c\u4e09\u4f0f\u4e09\u8d77\uff0c\u53e3\u7bad\u79bb\u5f26\uff0c\u500f\u5ffd\u8ffd\u5230\u4e86\u8427\u9752\u5cf0\u7684\u80cc\u540e\u3002\u5510\u7ecf\u5929\u77e5\u9053\u8427\u9752\u5cf0\u4e0d\u662f\u82e6\u884c\u50e7\u7684\u5bf9 \u624b\uff0c\u628a\u624b\u4e00\u626c\uff0c\u6025\u5fd9\u53d1\u51fa\u4e24\u652f\u5929\u5c71\u795e\u8292\uff0c\u82e6\u884c\u50e7\u7528\u91d1\u76c2\u94b5\u4e00\u6321\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u5f53\u201d\u4e24\u58f0\uff0c\u91d1\u661f \u98de\u6e85\uff0c\u82e6\u884c\u50e7\u4e00\u770b\uff0c\u53ea\u89c1\u4e24\u652f\u5929\u5c71\u795e\u8292\u90fd\u5c04\u5165\u4e86\u76c2\u94b5\u4e4b\u4e2d\uff0c\u6df1\u5165\u6570\u5bf8\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff1a\u5929\u4e0b \u7adf\u6709\u8fd9\u6837\u5389\u5bb3\u7684\u6697\u5668\uff0c\u80fd\u591f\u7a7f\u8fc7\u9ec4\u91d1\uff01\u9976\u4ed6\u7684\u745c\u73c8\u5de5\u592b\u5df2\u7ec3\u81f3\u5c06\u8fd1\u6700\u9ad8\u5883\u754c\uff0c\u4e5f\u81ea\u751f\u602f\u610f\u3002 \u3000\u3000\u82e6\u884c\u50e7\u88ab\u5929\u5c71\u795e\u8292\u4e00\u963b\uff0c\u8427\u9752\u5cf0\u5df2\u8dc3\u4e0a\u5899\u5934\u3002\u5510\u7ecf\u5929\u5019\u4ed6\u5598\u606f\u8fc7\u540e\uff0c\u95ee\u9053\uff1a\u201c\u8427\u8001\u5e08\uff0c \u4f60\u51e0\u65f6\u6765\u7684\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u5728\u5ce8\u5d4b\u5c71\u91d1\u5149\u5bfa\u9001\u5192\u5927\u4fa0\u4e0b\u571f\u4e4b\u540e\uff0c\u7acb\u5373\u8d76\u6765\uff0c\u7b97\u6765\u4f60\u6bd4\u6211 \u65e9\u8d70\u4e00\u5927\u534a\u3002\u201d\u5510\u7ecf\u5929\u5fd9\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u5462\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5979\u4e3a\u6b66\u5f53\u6d3e\u95e8\u6237\u4e4b\u4e8b\uff0c\u5c1a\u987b\u6599 \u7406\uff0c\u6240\u4ee5\u4e0e\u5415\u56db\u5a18\u4e00\u9053\uff0c\u8981\u8fdf\u6211\u4e24\u5929\u624d\u80fd\u52a8\u8eab\u3002\u201d\u5510\u7ecf\u5927\u6c89\u541f\u60f3\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u7684\u8f7b\u529f\u8fdc\u80dc\u4e8e \u8427\u9752\u5cf0\uff0c\u5373\u4f7f\u8fdf\u4e24\u5929\u52a8\u8eab\uff0c\u8fd9\u65f6\u4e5f\u8be5\u8d76\u5230\u4e86\uff0c\u96be\u9053\u53c8\u6709\u4ec0\u4e48\u53d8\u6545\u4e48\uff1f\u201d\u95ee\u9053\uff1a\u4f60\u5230\u4e86\u8428\u8fe6\u591a \u4e45\u4e86\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6628\u5929\u5230\u7684\u3002\u4f60\u4e0d\u662f\u8bf4\u53eb\u6211\u627e\u5929\u5b87\u6253\u542c\u6211\u5a18\u5b50\u7684\u4e0b\u843d\u4e48\uff1f\u6211\u4e00\u5230\u8428\u8fe6\uff0c \u5f53\u65e5\u4fbf\u60f3\u6765\u6b64\uff0c\u5305\u56f4\u5f97\u7d27\uff0c\u76f4\u5230\u73b0\u5728\u624d\u89c5\u5f97\u673a\u4f1a\u8fdb\u6765\u3002\u5929\u5b87\u5462\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c \u4ed6\u6b63\u5728\u91cc\u9762\u9759\u517b\uff0c\u4f60\u5148\u8bf4\u8bf4\uff0c\u5916\u9762\u600e\u4e48\u6837\u4e86\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5916\u9762\u4e71\u5f97\u5f88\u5462\uff01\u542c\u8bf4\u4fc4\u9a6c\u767b\u5506 \u4f7f\u8fbe\u8d56\u73ed\u7985\u7684\u4ee3\u8868\uff0c\u8bf4\u767d\u6559\u6cd5\u738b\u7684\u5723\u5973\u7adf\u7136\u8fde\u73ed\u7985\u7684\u4f7f\u8005\u4e5f\u6562\u7528\u98de\u5200\u523a\u4f24\uff0c\u8fd9\u4e43\u662f\u5bf9\u9ec4\u6559\u5587 \u561b\u5927\u5927\u7684\u4fae\u8fb1\uff0c\u4ed6\u4eec\u8981\u53eb\u8fbe\u8d56\u73ed\u7985\u6d3e\u5175\u6765\u9a71\u9010\u767d\u6559\uff0c\u53ea\u6015\u53c8\u8981\u5377\u5165\u4e00\u573a\u5b97\u6559\u6218\u4e89\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u521d\u65f6\u4ee5\u4e3a\u4fc4\u9a6c\u767b\u53ea\u662f\u60f3\u9a71\u9010\u6c49\u4eba\uff0c\u5982\u662f\u770b\u6765\uff0c\u7adf\u662f\u5230\u5904\u4e71\u70b9\u706b\u5934\uff0c \u60f3\u628a\u897f\u85cf\u5f04\u6210\u7cdc\u70c2\u4e4b\u5c40\uff0c\u771f\u4e0d\u77e5\u5176\u5fc3\u4f55\u5c45\uff1f\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5587\u561b\u5e99\u4e5f\u6709\u85cf\u5175\u76d1\u89c6\u4e86\u3002\u4f46\u4ed6\u4eec\u5fcc \u60ee\u6cd5\u738b\uff0c\u8fd8\u4e0d\u6562\u80e1\u95f9\u3002\u53ea\u662f\u542c\u8bf4\u4fc4\u9a6c\u767b\u8fd8\u60f3\u5230\u5370\u5ea6\u7684\u5580\u6797\u90a6\u548c\u5c3c\u6cca\u5c14\u8fd9\u4e24\u4e2a\u5730\u65b9\u53bb\uff0c\u8bf7\u5916\u5175 \u6765\u5e2e\u5fd9\u4ed6\u7edf\u4e00\u897f\u85cf\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u56de\u5982\u4f55\u662f\u597d\uff1f\u987b\u5f97\u8d76\u5feb\u6d3e\u4eba\u9001\u4fe1\u7ed9\u798f\u5eb7\u5b89\uff0c\u6d3e\u6551\u5175 \u6765\u3002\u201d\u53ef\u662f\u6d3e\u8c01\u9001\u4fe1\uff1f\u5374\u65e0\u9002\u5f53\u4eba\u9009\uff0c\u6b63\u5728\u8e0c\u8e87\uff0c\u5ffd\u89c1\u5916\u9762\u85cf\u5175\u4e24\u8fb9\u5206\u5f00\uff0c\u4fc4\u5426\u767b\u966a\u7740\u4e24\u4e2a \u767d\u6559\u5587\u561b\u4e58\u7740\u4e00\u5339\u767d\u8c61\u8d70\u6765\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u85cf\u8fb9\u5ffd\u89c1\u98ce\u6ce2\u6076\uff0c\u5927\u7978\u5f25\u5929\u5b70\u4e0e\u5e73\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u585e\u5916\u5174\u6ce2\u3000\u5978\u5f92\u56f0\u4fa0\u58eb\u3000\u5bab\u4e2d\u5bf9\u638c\u3000\u4fa0\u4e10\u6597\u795e\u50e7 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de \u585e\u5916\u5174\u6ce2\u3000\u5978\u5f92\u56f0\u4fa0\u58eb \u5bab\u4e2d\u5bf9\u638c\u3000\u4fa0\u4e10\u6597\u795e\u50e7 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u773c\u77a5\u53bb\uff0c\u8ba4\u5f97\u8fd9\u4e24\u4e2a\u767d\u6559\u5587\u561b\u6b63\u662f\u6cd5\u738b\u5ea7\u4e0b\u7684\u62a4\u6cd5\u5927\u5f1f\u5b50\uff0c\u4e5f\u5c31\u662f\u90a3\u5e74\u6765\u62a2\u593a \u91d1\u672c\u5df4\u74f6\u7684\u4eba\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u5176\u5b9e\u5728\u6697\u4e2d\u4e5f\u548c\u6cd5\u738b\u4f5c\u5bf9\uff0c\u6cd5\u738b\u5374\u6d3e\u8fd9\u4e24\u4e2a\u5927\u5f1f\u5b50\u6765\u4f5c \u4ec0\u4e48\uff1f\u201d\u5ffd\u89c1\u571f\u53f8\u7684\u961f\u4f0d\u4e24\u8fb9\u5206\u5f00\uff0c\u4e00\u4e2a\u85cf\u65cf\u5c11\u5973\uff0c\u7a7f\u7740\u4e00\u8eab\u9752\u8272\u7684\u730e\u88c5\uff0c\u9a91\u7740\u4e00\u5339\u806a\u82b1 \u9a6c\uff0c\u6cfc\u5587\u5587\u5730\u98de\u5954\u800c\u6765\uff0c\u85cf\u519b\u4e2d\u7684\u5b98\u5458\u5927\u81f3\u201c\u6d85\u5df4\u201d\uff0c\u5c0f\u81f3\u201c\u6208\u4ec0\u201d\uff08\u6ce8\uff1a\u5f53\u4e8e\u4f0d\u957f\uff09\u90fd\u5728 \u9053\u65c1\u8083\u7acb\u81f4\u656c\u3002\u8427\u9752\u5cf0\u9053\uff1a\u201c\u8fd9\u662f\u571f\u53f8\u7684\u5973\u513f\uff01\u201d\u571f\u53f8\u7684\u5973\u513f\u7eb5\u9a6c\u98de\u5954\uff0c\u4e00\u8fb9\u53eb\u9053\uff1a\u201c\u4fc4\u9a6c \u767b\uff0c\u4fc4\u9a6c\u767b\uff01\u201d\u4fc4\u9a6c\u767b\u56de\u5934\u8bf4\u9053\uff1a\u201c\u6851\u58c1\u4f0a\u6c5f\u739b\u53e4\u4fee\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u56de\u53bb\uff0c\u56de\u53bb\uff01\u201d\u6851\u58c1\u4f0a\u662f \u571f\u53f8\u5973\u513f\u7684\u540d\u5b57\uff0c\u6c5f\u739b\u53e4\u4fee\u662f\u5c0a\u79f0\uff08\u76f8\u5f53\u4e8e\u6c49\u8bed\u4e2d\u7684\u201c\u9ad8\u8d35\u7684\u5c0f\u59d0\u201d\uff09\u3002\u6851\u58c1\u4f0a\u67f3\u7709\u4e00\u7ad6\uff0c \u559d\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\uff0c\u4f60\u5728\u548c\u8c01\u8bf4\u8bdd\uff0c\u6211\u53eb\u4f60\u56de\u53bb\uff01\u201d\u4fc4\u9a6c\u767b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u662f\u5949\u4e86\u6cd5\u738b\u4e4b\u547d\uff0c \u53c8\u5f97\u4f60\u6bcd\u6837\u7684\u5141\u53ef\u6765\u7684\uff0c\u4f60\u7684\u7236\u4eb2\u88ab\u5973\u8d3c\u6240\u523a\uff0c\u6b7b\u4e0d\u7791\u76ee\uff0c\u6b63\u5728\u6cc9\u4e0b\u7b49\u5f85\u4ed6\u7684\u4ec7\u4eba\uff0c\u6211\u5c31\u7687 \u6765\u66ff\u4f60\u7236\u4eb2\u6293\u4ec7\u4eba\u7684\u5475\uff01\u201d\u6851\u58c1\u4f0a\u5934\u53d1\u84ec\u4e71\uff0c\u9999\u6c57\u6dcb\u6f13\uff0c\u663e\u89c1\u5fc3\u4e2d\u7126\u6025\u4e4b\u6781\uff0c\u4f46\u88ab\u4fc4\u9a6c\u767b\u8fd9 \u4e48\u4e00\u8bf4\uff0c\u6025\u5207\u95f4\u7adf\u65e0\u8a00\u4ee5\u5bf9\uff0c\u4fc4\u9a6c\u767b\u5df2\u8ddf\u7740\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u5230\u5ba3\u6170\u4f7f\u8859\u95e8\u5916\u9762\u558a\u8bdd\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u5728\u767d\u8c61\u4e0a\u7ad6\u8d77\u4e5d\u73af\u9521\u6756\uff0c\u9521\u6756\u4e0a\u6302\u7740\u4e00\u4e2a\u516b\u89d2\u5f62\u7684\u7528\u73cd\u73e0\u9576\u6210\u7684\u8f6e\u5b50\uff0c \u8fd9\u662f\u4ee3\u8868\u6cd5\u738b\u7684\u6cd5\u7269\uff0c\u7528\u85cf\u8bed\u9ad8\u58f0\u9053\uff1a\u201c\u6d3b\u4f5b\u4f7f\u8005\u6765\u89c1\u5927\u6e05\u672c\u5e03\u3002\uff08\u672c\u5e03\u5373\u5927\u4eba\u4e4b\u610f\uff09\u3002\u201d \u8427\u9752\u5cf0\u9053\uff1a\u201c\u5f00\u4e0d\u5f00\u95e8\uff1f\u201d\u9648\u5b9a\u57fa\u7565\u4e00\u8fdf\u7591\uff0c\u9053\uff1a\u201c\u5f00\u95e8\uff01\u201d \u3000\u3000\u9648\u5b9a\u57fa\u5f00\u95e8\u63a5\u7eb3\uff0c\u5f15\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u4e0e\u4fc4\u9a6c\u767b\u3001\u6851\u58c1\u4f0a\u56db\u4eba\u5230\u5ba2\u5385\u5750\u5b9a\uff0c\u5510\u7ecf\u5929\u5145\u4f5c\u9648 \u5b9a\u57fa\u7684\u968f\u5458\uff0c\u620e\u88c5\u4f69\u5251\uff0c\u966a\u5750\u4e00\u65c1\u3002\u9648\u5b9a\u57fa\u5411\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u5949\u732e\u54c8\u8fbe\u3001\u8bf7\u8fc7\u9999\u8336\u4e4b\u540e\uff0c\u606d \u95ee\u6765\u610f\uff0c\u4e3a\u9996\u7684\u90a3\u4e2a\u767d\u6559\u5587\u561b\u9053\uff1a\u201c\u6d3b\u4f5b\u4e0d\u5fcd\u5175\u8fde\u7978\u7ed3\uff0c\u613f\u4f5c\u8c03\u505c\uff0c\u73b0\u5728\u571f\u53f8\u7684\u90e8\u4e0b\u90fd\u8bf4\u4ee4 \u90ce\u9648\u5929\u5b87\u662f\u5973\u8d3c\u7684\u540c\u515a\uff0c\u662f\u523a\u6740\u571f\u53f8\u7684\u540c\u8c0b\uff0c\u8bf7\u672c\u5e03\u5c06\u4ee4\u90ce\u4ea4\u4e0e\u6d3b\u4f5b\uff0c\u518d\u4f5c\u8c03\u5904\u3002\u201d \u3000\u3000\u9648\u5b9a\u57fa\u5927\u5403\u4e00\u60ca\uff0c\u6599\u4e0d\u5230\u4fc4\u9a6c\u767b\u7adf\u8bf7\u5f97\u6d3b\u4f5b\u51fa\u5934\uff0c\u5411\u4ed6\u63d0\u51fa\u8fd9\u4e2a\u8981\u6c42\uff0c\u4ed6\u5e74\u8fc7\u534a\u767e\uff0c\u53ea \u6709\u8fd9\u4e00\u4e2a\u513f\u5b50\uff0c\u5982\u4f55\u80af\u9001\u51fa\u53bb\uff1f\u6b63\u5f85\u8bf4\u8bdd\uff0c\u571f\u53f8\u7684\u5973\u513f\u5374\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u6211\u7236\u4eb2\u662f\u6c81\u5e03\u85e9\u738b\u7684 \u5973\u513f\u523a\u6b7b\u7684\uff0c\u523a\u5ba2\u5df2\u81ea\u6740\u6b7b\u4e86\uff0c\u4e0d\u8be5\u7275\u8fde\u5230\u9648\u5929\u5b87\u3002\u82e5\u8bf4\u5929\u5b87\u4ee5\u524d\u66fe\u6551\u90a3\u4e2a\u523a\u5ba2\uff0c\u90a3\u4e48\u8981\u4ed6 \u5230\u6211\u5bb6\u4e2d\uff0c\u4e3a\u6211\u7236\u4eb2\u5b88\u7075\u4e03\u65e5\u4e5f\u5c31\u591f\u4e86\u3002\u201d\u571f\u53f8\u7684\u5973\u513f\u662f\u9648\u5929\u5b57\u540d\u4e49\u4e0a\u7684\u672a\u5a5a\u59bb\uff0c\u77e5\u9053\u9648\u5929 \u5b87\u82e5\u843d\u5728\u4fc4\u9a6c\u767b\u624b\u4e2d\uff0c\u90a3\u5c31\u51f6\u591a\u5409\u5c11\u4e86\uff0c\u56e0\u6b64\u4e0d\u60dc\u7792\u7740\u6bcd\u4eb2\uff0c\u98de\u9a91\u6765\u6551\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u5927\u559c\u8bf4\u9053\uff1a\u201c\u5230\u5e95\u662f\u6851\u58c1\u4f0a\u6c5f\u739b\u53e4\u4fee\u660e\u767d\u9053\u7406\u3002\u5c31\u8fd9\u4e48\u529e\u5427\uff0c\u4f60\u4eec\u9000\u5175\u4e4b\u540e\uff0c\u6211 \u53eb\u5c0f\u513f\u66ff\u571f\u53f8\u5b88\u7075\u53bb\u3002\u201d \u3000\u3000\u4fc4\u9a6c\u767b\u51b7\u7b11\u9053\uff1a\u201c\u8428\u8fe6\u5b97\u7684\u4e8b\u60c5\uff0c\u6709\u4f60\u6bcd\u4eb2\u548c\u6211\u4e3b\u6301\uff0c\u8fd8\u672a\u8f6e\u5230\u4f60\u7ba1\u5462\u3002\u6211\u518d\u8bf4\u4e00\u904d\uff0c \u6211\u662f\u5949\u4e86\u6cd5\u738b\u548c\u4f60\u6bcd\u4eb2\u4e4b\u547d\u6765\u7684\uff0c\u4f60\u8fd8\u672a\u542c\u6e05\u695a\u4e48\uff1f\u201d\u82e5\u5728\u571f\u53f8\u751f\u524d\uff0c\u4fc4\u9a6c\u767b\u5bf9\u4ed6\u7684\u5973\u513f\u81ea \u4e0d\u6562\u6709\u534a\u70b9\u8fdd\u62d7\uff0c\u4f46\u5982\u4eca\u571f\u53f8\u5df2\u6b7b\uff0c\u5927\u6743\u90fd\u5df2\u843d\u5230\u4fc4\u9a6c\u767b\u624b\u4e2d\uff0c\u4ed6\u4e00\u65e6\u53cd\u989c\u76f8\u5411\uff0c\u6851\u58c1\u4f0a\u6c14 \u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u800c\u4e14\u4fc4\u9a6c\u767b\u53e3\u53e3\u58f0\u58f0\u8bf4\u662f\u4e3a\u4ed6\u7236\u4eb2\u62a5\u4ec7\uff0c\u53c8\u6709\u6d3b\u4f5b\u548c\u5979\u6bcd\u4eb2\u7684\u610f\u65e8\uff0c\u6851\u58c1\u4f0a \u66f4\u6ca1\u6709\u53cd\u9a73\u7684\u4f59\u5730\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u4e0d\u518d\u7406\u776c\u6851\u58c1\u4f0a\uff0c\u8f6c\u8fc7\u4e00\u526f\u9762\u5b54\uff0c\u53c8\u5806\u7740\u597d\u733e\u7684\u7b11\u5bb9\u5bf9\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u672c\u5e03\uff0c\u8bf7\u4f60 \u4ee5\u5927\u5c40\u4e3a\u91cd\uff0c\u8fd8\u662f\u53eb\u4ee4\u90ce\u8ddf\u6211\u4eec\u8d70\u5427\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u4f60\u4eec\u6c49\u4eba \u8bf4\u5f97\u597d\uff0c\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u4f60\u513f\u5b50\u5f53\u5e74\u6709\u80c6\u5728\u571f\u53f8\u5bb6\u4e2d\u98de\u5200\u5288\u679c\uff0c\u6551\u8d70\u9093\u4e2a\u5973\u8d3c\uff0c\u5982\u4eca\u5c31\u6ca1 \u6709\u80c6\u91cf\u8ddf\u6211\u4eec\u8d70\u5417\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u9635\u6e05\u8106\u7684\u7b11\u58f0\u4ece\u540e\u5802\u4f20\u51fa\uff0c\u4e00\u4e2a\u9752\u5e74\u7f13\u7f13\u8d70\u51fa\uff0c\u9648\u5b9a\u57fa\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5b87\u513f\uff0c \u4f60\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u5f20\u53e3\u7ed3\u820c\uff0c\u50cf\u78b0\u5230\u4ec0\u4e48\u602a\u5f02\u4e4b\u4e8b\u4f3c\u7684\uff0c\u4f46\u542c\u5f97\u8fd9\u5c11\u5e74\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u4fc4\u9a6c\u767b\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u597d\u6c49\u505a\u4e8b\u4e00\u8eab\u5f53\uff0c\u6211\u6b63\u60f3\u53bb\u89c1\u6cd5\u738b\uff0c\u8bf7\u4ed6\u8bc4\u8bc4\u7406\uff0c\u597d\u5427\uff0c\u54b1\u4eec\u73b0\u5728\u5c31 \u8d70\uff01\u201d \u3000\u3000\u9648\u5b9a\u57fa\u60ca\u60f6\u8ff7\u60d1\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u51e0\u4e4e\u5446\u82e5\u6728\u9e21\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u8fd9\u4e2a\u5c11\u5e74\uff0c\u8fd9\u5c11\u5e74\u7a7f\u7684\u6b63 \u662f\u9648\u5929\u5b87\u7684\u670d\u9970\uff0c\u8fde\u9762\u8c8c\u4e5f\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u53ea\u662f\u8bf4\u8bdd\u7684\u795e\u6001\u4e0e\u58f0\u97f3\uff0c\u8f7b\u6843\u4e4b\u6781\uff0c\u5374\u548c\u9648\u5929\u5b87\u7684 \u7a33\u91cd\u6c89\u539a\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u5f20\u53e3\u7ed3\u820c\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u9762\u4e0a\u4e5f\u9732\u51fa\u602a\u5f02\u7684\u795e\u60c5\uff0c\u5ffd\u7136\u5411 \u4ed6\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u51b2\u7740\u90a3\u5c11\u5e74\u53eb\u9053\uff1a\u201c\u5929\u5b87\u5144\uff0c\u4f60\u7684\u75c5\u8fd8\u6ca1\u597d\u5475\uff0c\u600e\u4e48\u53bb\u5f97\u53f6\u90a3\u5c11\u5e74\u51b7\u7b11 \u9053\uff1a\u201c\u6211\u7684\u75c5\u53ef\u4e0d\u8981\u4f60\u62c5\u5fc3\uff0c\u518d\u8bf4\uff0c\u5c31\u662f\u6211\u6ca1\u6709\u75c5\uff0c\u8fd9\u4f4d\u4fc4\u9a6c\u767b\u5927\u6d85\u5df4\u4e5f\u4e0d\u80fd\u8ba9\u6211\u6d3b\u5475\uff0c\u5927 \u6d85\u5df4\uff0c\u6211\u62fc\u7740\u4e00\u8eab\u5250\u51fa\u6765\u4e86\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u8d70\u5475\uff01\u201d\u9648\u5b9a\u57fa\u5947\u602a\u4e07\u5206\uff0c\u542c\u4ed6\u4eec\u7684\u5bf9\u7b54\uff0c\u8fd9\u5c11\u5e74 \u4f3c\u4e4e\u4e0e\u5510\u7ecf\u5929\u76f8\u8bc6\uff0c\u800c\u4e14\u6709\u5fc3\u6765\u6551\u4ed6\u7684\u513f\u5b50\u7684\uff0c\u53ef\u662f\u4e0d\u4f46\u4ed6\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u4ece\u6765\u672a \u542c\u513f\u5b50\u8bf4\u8fc7\u6709\u8fd9\u6837\u7684\u670b\u53cb\u3002 \u3000\u3000\u9648\u5b9a\u57fa\u8ff7\u60d1\u4e0d\u89e3\uff0c\u5510\u7ecf\u5929\u6bd4\u4ed6\u8fd8\u8981\u60ca\u5947\u3002\u8fd9\u5c11\u5e74\u4e0d\u662f\u65c1\u4eba\uff0c\u6b63\u662f\u4ed6\u6240\u8981\u5bfb\u8bbf\u7684\u91d1\u4e16\u9057\uff01 \u91d1\u4e16\u9057\u8f7b\u529f\u8d85\u5353\uff0c\u53c8\u5584\u4e8e\u6613\u5bb9\u53d8\u8c8c\uff0c\u4ed6\u5077\u8fdb\u5e9c\u8859\uff0c\u6362\u4e0a\u9648\u5929\u5b87\u7684\u8863\u88f3\uff0c\u5047\u626e\u6210\u9648\u5929\u5b87\u7684\u6837 \u5b50\uff0c\u8fd9\u4e9b\u90fd\u4e0d\u662f\u96be\u4e8b\uff0c\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u505a\u5462\uff1f\u5510\u7ecf\u5929\u53c8\u60f3\u9053\uff1a\u201c\u7167\u5415\u56db\u5a18\u6240\u8bf4\uff0c\u4ed6\u4e0d\u80fd\u6d3b \u4e09\uff0b\u516d\u5929\uff0c\u73b0\u5728\u5c48\u6307\u4e00\u7b97\uff0c\u5df2\u4e09\uff0b\u5929\uff0c\u4f46\u4f55\u4ee5\u770b\u4ed6\u9762\u8272\uff0c\u5374\u53c8\u4e00\u5982\u5e38\u4eba\uff0c\u5e76\u65e0\u5185\u9b54\u6270\u4f53\u4e4b \u8c61\uff1f\u201d\u5510\u7ecf\u5929\u53ef\u6ca1\u6709\u6599\u60f3\u5f97\u5230\uff0c\u91d1\u4e16\u9057\u65e9\u5f97\u8fc7\u4ed6\u7684\u59e8\u6bcd\u51af\u7433\u7528\u5bc6\u5b97\u7684\u5185\u529f\u76f8\u52a9\uff0c\u5c06\u4ed6\u7684\u5371\u9669 \u671f\u53c8\u5ef6\u957f\u4e86\u4e09\u5341\u516d\u5929\u3002 \u3000\u3000\u6851\u58c1\u4f0a\u89c1\u201c\u9648\u5929\u5b87\u201d\u51fa\u6765\uff0c\u521d\u65f6\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u542c\u542c\u4ed6\u7684\u8bf4\u8bdd\uff0c\u767b\u65f6\u9762\u4e0a\u4e5f\u73b0\u51fa\u5947\u5f02\u7684\u5149 \u8f89\u3002 \u3000\u3000\u767d\u6559\u5587\u561b\u7f13\u7f13\u8d77\u7acb\uff0c\u5bf9\u9648\u5b9a\u57fa\u5408\u4ec0\u8c22\u9053\uff1a\u201c\u6709\u6270\u4e86\u3002\u201d\u9762\u4e0a\u9732\u51fa\u6b49\u7136\u4e4b\u8272\uff0c\u60f3\u628a\u5047\u626e\u9648 \u5929\u5b87\u7684\u91d1\u4e16\u9057\u5e26\u8d70\uff0c\u539f\u6765\u767d\u6559\u6cd5\u738b\u7684\u56db\u5927\u5f1f\u5b50\u5bf9\u9648\u5b9a\u57fa\u90fd\u9887\u6709\u597d\u611f\uff0c\u800c\u5bf9\u4fc4\u9a6c\u767b\u5374\u6709\u8bf4\u4e0d\u51fa \u7684\u538c\u6076\uff0c\u53ea\u56e0\u4fc4\u9a6c\u767b\u631f\u6301\u8fbe\u8d56\u73ed\u7985\u7684\u4e24\u4f4d\u4ee3\u8868\uff0c\u4ee5\u9a71\u9010\u767d\u6559\u4f5c\u4e3a\u8981\u631f\uff0c\u767d\u6559\u6cd5\u738b\u4e3a\u4e86\u60f3\u5728\u897f \u85cf\u91cd\u7acb\u6839\u57fa\uff0c\u8fd9\u624d\u4e0d\u5f97\u4e0d\u5e94\u4fc4\u9a6c\u767b\u7684\u8bf7\u6c42\u3002\u5176\u5b9e\u767d\u6559\u6cd5\u738b\u5012\u5e76\u4e0d\u5b58\u5fc3\u4e0e\u9648\u5b9a\u57fa\u7236\u5b50\u4e3a\u96be\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u50cf\u6851\u58c1\u4f0a\u4e00\u6837\uff0c\u4e5f\u662f\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u91d1\u4e16\u9057\uff0c\u5ffd\u5730\u8d70\u4e0a\u4e00\u6b65\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f \u8c01\uff1f\u201d\u91d1\u4e16\u9057\u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u6211\u662f\u8428\u8fe6\u7684\u5927\u6d85\u5df4\u4fc4\u9a6c\u767b\uff0c\u8c01\u4e0d\u77e5 \u9053\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u662f\u4f60\u8428\u8fce\u571f\u53f8\u7684\u5973\u5a7f\u9648\u5929\u5b87\uff0c\u8c01\u4e0d\u77e5\u9053\uff1f\u800c\u4eca\u571f\u53f8\u5df2\u6b7b\uff0c\u6211\u662f\u4f60\u7684\u534a \u4e2a\u4e3b\u4eba\uff0c\u4f60\u6562\u5bf9\u6211\u65e0\u793c\uff1f\u201d\u4fc4\u9a6c\u767b\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u6df7\u5e10\u5c0f\u5b50\uff0c\u6562\u6765\u5192\u5145\uff0c\u4f60\u627e\u6b7b\u4e48\uff1f\u201d\u91d1\u4e16\u9057 \u5927\u7b11\u9053\uff1a\u201c\u6211\u662f\u5192\u5145\uff1f\u5929\u4e0b\u4e4b\u95f4\uff0c\u90a3\u6709\u5f53\u9762\u5192\u5145\u662f\u522b\u4eba\u4e08\u592b\u7684\u9053\u7406\uff1f\u201d\u767d\u6559\u5587\u561b\u770b\u7740\u6851\u58c1 \u4f0a\uff0c\u6851\u58c1\u4f0a\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u5b87\u5440\uff0c\u4fc4\u9a6c\u767b\u4e0d\u6000\u597d\u610f\uff0c\u4f60\u4e0d\u53bb\u4e5f\u7f62\u3002\u201d\u5979\u8fd9\u8bdd\u4e00\u8bf4\uff0c\u65e0\u7591\u627f\u8ba4 \u4e86\u6b64\u4eba\u4fbf\u662f\u9648\u5929\u5b87\u4e86\u3002\u539f\u6765\u6851\u58c1\u4f0a\u4e5f\u65e9\u770b\u51fa\u4e86\u8fd9\u4eba\u662f\u5047\u5192\u9648\u5929\u5b87\uff0c\u4f46\u5979\u5b9e\u4e0d\u613f\u771f\u7684\u9648\u5929\u5b87\u53bb \u6b7b\uff0c\u6240\u4ee5\u53ea\u597d\u542b\u7f9e\u5e26\u6127\uff0c\u627f\u8ba4\u91d1\u4e16\u9057\u662f\u5979\u7684\u672a\u5a5a\u592b\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u767d\u6559\u5587\u561b\u4e00\u60f3\uff0c\u5929\u4e0b\u95f4\u786e\u662f\u6ca1\u6709\u5192\u8ba4\u4e08\u592b\u4e4b\u7406\uff0c\u800c\u8fd9\u4e00\u53bb\u660e\u662f\u9001\u6b7b\uff0c\u5929\u4e0b\u53c8\u54ea\u6709 \u8fd9\u6837\u7684\u50bb\u4eba\uff0c\u80af\u5192\u5145\u522b\u4eba\u53bb\u9001\u6b7b\uff1f\u4fbf\u9053\uff1a\u201c\u6211\u770b\u4ed6\u662f\u771f\u7684\uff0c\u6d85\u5df4\u4e0d\u5fc5\u591a\u7591\u3002\u201d\u4fc4\u9a6c\u767b\u51b7\u7b11 \u9053\uff1a\u201c\u9648\u5929\u5b87\u6211\u89c1\u8fc7\u4e0d\u77e5\u591a\u5c11\u6b21\uff0c\u5484\uff0c\u4f60\u771f\u7684\u662f\u9648\u5929\u5b87\uff0c\u9648\u5929\u5b87\u7684\u6b66\u529f\u53ef\u5f88\u4e0d\u9519\u5475\uff01\u201d\u6479\u7136 \u4f38\u624b\u4e00\u6293\uff0c\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u591a\u627f\u5938\u5956\u3002\u201d\u80a9\u5934\u8f7b\u8f7b\u4e00\u649e\uff0c\u4fc4\u9a6c\u767b\u8dcc\u4e2a\u56db\u811a\u671d\u5929\uff0c\u5468\u8eab\u9aa8\u9abc\u90fd \u9690\u9690\u4f5c\u75db\uff0c\u722c\u4e86\u4e00\u4f1a\u5b50\u624d\u722c\u8d77\u6765\u3002\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u9648\u5929\u5b87\u7684\u6b66\u529f\u672c\u6765\u4e0d\u9519\uff0c\u8fd9\u56de\u4f60\u76f8\u4fe1\u4e86 \u5427\uff1f\u201d\u4fc4\u9a6c\u767b\u81ea\u6043\u4e00\u8eab\u6b66\u529f\uff0c\u4ed6\u5fc3\u4e2d\u4ee5\u4e3a\u91d1\u4e16\u9057\u5fc5\u5b9a\u662f\u9648\u5b9a\u57fa\u4e70\u6765\u5192\u5145\u513f\u5b50\u7684\uff0c\u8fd9\u6837\u88ab\u4e70\u6765 \u66ff\u6b7b\u7684\u4eba\u80fd\u6709\u771f\u5b9e\u672c\u9886\uff1f\u6240\u4ee5\u60f3\u4ee4\u91d1\u4e16\u9057\u5f53\u573a\u51fa\u4e11\uff0c\u54ea\u77e5\u91d1\u4e16\u9057\u7684\u6b66\u529f\u6bd4\u9648\u5929\u5b87\u9ad8\u51fa\u4f55\u6b62\u4e00 \u500d\uff0c\u5e78\u800c\u4ed6\u8fd9\u4e00\u649e\u672a\u7528\u5168\u529b\uff0c\u8981\u4e0d\u7136\u4fc4\u9a6c\u767b\u5168\u8eab\u9abc\u90fd\u8981\u788e\u88c2\u3002\u91d1\u4e16\u9057\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u8fd8\u6562\u8bf4\u6211 \u5192\u5145\u5417\uff1f\u201d\u4fc4\u9a6c\u767b\u7ed9\u4ed6\u9707\u4f4f\uff0c\u4e0d\u6562\u5f00\u53e3\u3002\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u7b11\u9053\uff1a\u201c\u5927\u6d85\u5df4\u4e0d\u5fc5\u751f\u679d\u8282\u4e86\uff0c\u6cd5 \u738b\u6709\u4ee4\uff0c\u54b1\u95ea\u5feb\u5e26\u4e86\u8fd9\u4e2a\u9648\u5929\u5b87\u8d70\u5427\u3002\u201d\u5510\u7ecf\u5929\u6025\u5fd9\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u5929\u5b87\u5144\uff0c\u4f60\u8fd9\u4e00\u53bb\u591a\u591a\u4fdd \u91cd\uff0c\u8fd9\u662f\u4f60\u7684\u836f\u4e38\uff0c\u4f60\u5e26\u8d70\u5427\u3002\u201d\u638f\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u94f6\u74f6\uff0c\u74f6\u4e2d\u6709\u4e09\u9897\u78a7\u7eff\u8272\u7684\u836f\u4e38\uff0c\u90a3\u6b63\u662f\u5929 \u5c71\u96ea\u83b2\u6240\u6ce1\u5236\u7684\u78a7\u7075\u4e39\u3002\u4f9d\u5415\u56db\u5a18\u6240\u8bf4\uff0c\u91d1\u4e16\u9057\u82e5\u670d\u4e0b\u8fd9\u78a7\u7075\u4e39\u53ef\u5ef6\u957f\u4ed6\u4e09\u5341\u516d\u5929\u7684\u5bff\u547d\u3002 \u672c\u6765\u4e00\u9897\u5c31\u591f\uff0c\u5510\u7ecf\u5929\u8fd9\u65f6\u5bf9\u91d1\u4e16\u9057\u9887\u6709\u597d\u611f\uff0c\u7d22\u6027\u5c06\u4ec5\u5b58\u7684\u4e09\u9897\u90fd\u9001\u4e86\u7ed9\u4ed6\u3002 \u3000\u3000\u7528\u51b0\u5c71\u96ea\u83b2\u6240\u6ce1\u5236\u7684\u78a7\u7075\u4e39\uff0c\u529f\u80fd\u89e3\u6bd2\u7597\u4f24\uff0c\u56fa\u672c\u57f9\u539f\uff0c\u73cd\u8d35\u65e0\u6bd4\u3002\u5f53\u5e74\u5d14\u4e91\u5b50\u4e0e\u8427\u9752 \u5cf0\u6076\u6597\uff0c\u5d14\u4e91\u5b50\u53d7\u4e86\u91cd\u4f24\uff0c\u534a\u8eab\u762b\u75ea\uff0c\u53ea\u670d\u4e00\u9897\uff0c\u7acb\u523b\u590d\u539f\uff0c\u800c\u4eca\u8427\u9752\u5cf0\u89c1\u5510\u7ecf\u5929\u5c06\u94f6\u74f6\u4e2d \u6240\u6709\u7684\u78a7\u7075\u4e39\uff0c\u5168\u90fd\u9001\u7ed9\u4e86\u91d1\u4e16\u9057\uff0c\u4e0d\u89c9\u9a87\u7136\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u770b\u8fd9\u91d1\u4e16\u9057\u5e76\u4e0d\u50cf\u6709\u75c5\u7684\u6837 \u5b50\uff0c\u6b66\u6797\u4e2d\u4eba\u89c6\u78a7\u7075\u4e39\u4e3a\u81f3\u5b9d\u7075\u4e39\uff0c\u5f97\u4e00\u7c92\u5df2\u662f\u7f55\u4e16\u5947\u9047\uff0c\u5510\u7ecf\u5929\u5c06\u6240\u6709\u7684\u7075\u4e39\u90fd\u9001\u4e86\u7ed9 \u4ed6\uff0c\u8fd9\u771f\u662f\u6700\u539a\u91cd\u7684\u793c\u7269\uff0c\u7eb5\u6709\u4ec0\u4e48\u4ec7\u6b49\uff0c\u4e5f\u8be5\u5316\u89e3\u4e86\u3002\u201d \u3000\u3000\u5ffd\u89c1\u91d1\u4e16\u9057\u8863\u8896\u4e00\u62c2\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5510\u7ecf\u5929\uff0c\u6211\u4e0d\u9886\u4f60\u7684\u60c5\uff01\u201d\u5510\u7ecf\u5929\u9aa4\u51fa\u4e0d\u610f\uff0c\u94f6\u74f6 \u7ed9\u4ed6\u62c2\u5f97\u8131\u624b\u98de\u8d77\uff0c\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u9886\u4f60\u7684\u60c5\u3002\u201d\u5c06\u94f6\u74f6\u63a5\u4e0b\uff0c\u6b63\u60f3\u518d\u8bf4\uff0c\u91d1\u4e16\u9057\u51b7\u7b11 \u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u60f3\u5728\u51b0\u5ddd\u5929\u5973\u7684\u9762\u524d\u535a\u5f97\u4e2a\u4fa0\u4e49\u7684\u7f8e\u540d\uff0c\u6211\u504f\u4e0d\u8ba9\u4f60\u79f0\u5fc3\u5982\u610f\uff0c\u6211\u6b7b\u751f\u6709\u547d\uff0c \u4f55\u987b\u6c42\u4f60\uff01\u201d\u795e\u8272\u51b7\u50b2\u4e4b\u6781\uff0c\u7adf\u4e0d\u5bb9\u5510\u7ecf\u5929\u518d\u8bf4\uff0c\u5f84\u81ea\u968f\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u8d70\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9001\u51fa\u95e8\u53e3\uff0c\u91d1\u4e16\u9057\u77a7\u4e5f\u4e0d\u77a7\u4ed6\u4e00\u773c\u3002\u5510\u7ecf\u5929\u56de\u5230\u5ba2\u5385\uff0c\u6447\u5934\u8bf4\u9053\uff1a\u201c\u771f\u662f\u4e2a\u602a \u7269\uff01\u201d\u9648\u5b9a\u57fa\u95ee\u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6b64\u4eba\u662f\u6c5f\u6e56\u4e0a\u4eba\u79f0\u6bd2\u624b\u75af\u4e10\u7684\u91d1\u4e16\u9057\u3002\u201d \u8427\u9752\u5cf0\u9053\uff1a\u201c\u4ed6\u6b64\u6b21\u820d\u547d\u6c42\u6551\u5b87\u513f\uff0c\u5012\u662f\u4e00\u756a\u4fa0\u4e49\u7684\u884c\u4e3a\u5462\uff0c\u4ed6\u4e0e\u5b87\u513f\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u6545\u5982 \u65af\uff1f\u201d\u5927\u5bb6\u8c08\u8bba\uff0c\u767e\u601d\u83ab\u89e3\u3002\u5374\u4e0d\u77e5\u91d1\u4e16\u9057\u4e3a\u7684\u4e0d\u662f\u9648\u5929\u5b87\uff0c\u800c\u4e3a\u5510\u7ecf\u5929\u3002\u91d1\u4e16\u9057\u6b64\u4eba\u5b64\u50fb \u72c2\u505a\uff0c\u6e38\u620f\u98ce\u5c18\uff0c\u6240\u60f3\u6240\u4e3a\uff0c\u4e0e\u6d41\u4fd7\u8fce\u5f02\u3002\u4ed6\u77e5\u9053\u4e86\u81ea\u5df1\u5fc5\u987b\u5929\u5c71\u6d3e\u7684\u5185\u529f\u76f8\u52a9\u624d\u80fd\u6551\u547d\u4e4b \u540e\uff0c\u60f3\u8d77\u81ea\u5df1\u4e00\u5411\u4e0e\u5510\u7ecf\u5929\u4f5c\u5bf9\uff0c\u600e\u80af\u5411\u4ed6\u4f4e\u9996\u4e0b\u5fc3\uff0c\u5fc3\u4e2d\u4e00\u6a2a\uff0c\u53cd\u800c\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u8981 \u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u505a\u4e00\u4ef6\u6709\u6069\u535c\u5510\u7ecf\u5929\u7684\u4e8b\u60c5\uff0c\u8ba9\u4ed6\u6c38\u8fdc\u6b20\u81ea\u5df1\u7684\u60c5\u4efd\u3002\u4ed6\u5077\u8fdb\u5ba3\u6170\u4f7f\u8859\u95e8\uff0c\u77e5 \u9053\u4e86\u5510\u7ecf\u5929\u4e0e\u9648\u5929\u5b87\u7684\u4ea4\u60c5\uff0c\u53c8\u77e5\u9053\u4e86\u5510\u7ecf\u5929\u6b63\u4e3a\u9648\u5929\u5b87\u4e4b\u4e8b\uff0c\u4f24\u795e\u4e4b\u6781\uff0c\u6beb\u65e0\u529e\u6cd5\uff0c\u4ed6\u627e \u4e0d\u5230\u4e00\u4ef6\u5bf9\u5510\u7ecf\u5929\u76f4\u63a5\u6709\u6069\u7684\u4e8b\u60c5\uff0c\u60f3\u9053\uff1a\u201c\u6551\u4ed6\u7684\u670b\u53d1\u4e5f\u662f\u4e00\u6837\uff0c\u603b\u4e4b\u8981\u8ba9\u4ed6\u6c38\u8fdc\u6b20\u6211\u7684 \u60c5\u3002\u201d\u8fd9\u5176\u5b9e\u8fd8\u662f\u51fa\u4e8e\u597d\u5f3a\u4e89\u80dc\uff0c\u8981\u538b\u5012\u5510\u7ecf\u5929\u7684\u610f\u601d\u3002\u5510\u7ecf\u5929\u54ea\u80fd\u731c\u5230\u91d1\u4e16\u9057\u8fd9\u756a\u66f2\u66f2\u6298 \u6298\u7684\u5fc3\u610f\u3002\u5510\u7ecf\u5929\u60f3\u8d77\u91d1\u4e16\u9057\u8fd8\u6709\u516d\u5929\u6027\u547d\uff0c\u63ea\u7136\u4e0d\u4e50\u3002\u4f46\u4ed6\u51b7\u505a\u5982\u6b64\uff0c\u5374\u53c8\u5b9e\u662f\u65e0\u6cd5\u53ef\u4ee5 \u6551\u4ed6\u3002 \u3000\u3000\u4e00\u76cf\u8336\u540e\uff0c\u5916\u9762\u5b88\u536b\u7684\u4eba\u8fdb\u6765\u62a5\u5bfc\uff0c\u571f\u53f8\u7684\u5175\u5df2\u8d70\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u8fde\u90a3\u5370\u5ea6\u50e7\u4eba\u4e5f\u9000\u4e86\uff0c \u4f46\u5728\u8859\u95e8\u5916\u9762\uff0c\u8fd8\u662f\u4e94\u6b65\u4e00\u5c97\u5341\u6b65\u4e00\u54e8\uff0c\u770b\u60c5\u5f62\u5c1a\u672a\u653e\u677e\u76d1\u89c6\uff0c\u5927\u5bb6\u90fd\u731c\u4e0d\u900f\u4fc4\u9a6c\u767b\u7684\u7528 \u610f\uff0c\u5510\u7ecf\u5929\u6d3e\u8427\u9752\u5cf0\u51fa\u5916\u6253\u542c\uff0c\u9ec4\u660f\u65f6\u5206\uff0c\u56de\u6765\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4fc4\u9a6c\u767b\u662f\u8981\u5e94\u4ed8\u53e6\u5916\u4e00\u573a\u6218 \u4e8b\u3002\u4f60\u4eec\u542c\u8fc7\u6d1b\u73e0\u7684\u540d\u5b57\u5417\uff1f\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u4ed6\u662f\u6c81\u5e03\u85e9\u738b\u7684\u59bb\u8205\uff0c\u542c\u8bf4\u662f\u6c81\u5e03\u8f96\u4e0b\u51e0\u5b97 \uff08\u8428\u8fe6\u5b97\u662f\u5176\u4e2d\u4e4b\u4e00\uff09\u9996\u5c48\u4e00\u6307\u7684\u6b66\u58eb\u3002\u201d \u3000\u3000\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6d1b\u73e0\u542c\u8bf4\u4ed6\u7684\u7525\u5973\u6b7b\u4e86\uff0c\u5c38\u9ab8\u53c8\u7ed9\u4fc4\u9a6c\u767b\u62a2\u53bb\uff0c\u4fbf\u7387\u5175\u524d\u6765\u66ff\u59d0\u592b\u548c\u7525\u5973 \u62a5\u4ec7\u3002\u5728\u4fc4\u9a6c\u767b\u5305\u56f4\u54b1\u4eec\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u6b63\u8d76\u6765\u5305\u56f4\u4e86\u4e0a\u53f8\u7684\u57ce\u5821\uff0c\u6240\u4ee5\u4fc4\u9a6c\u767b\u8981\u64a4\u5175\u56de\u53bb\u3002\u4fc4 \u9a6c\u767b\u4ee5\u4e3a\u5ba3\u6170\u4f7f\u8859\u95e8\u53ea\u6709\u5b87\u513f\u662f\u6700\u6709\u672c\u4e8b\u7684\u4eba\uff0c\u53bb\u4e86\u5b87\u513f\uff0c\u5c31\u65e0\u4eba\u80fd\u62b5\u6297\u4ed6\u4e86\uff0c\u6240\u4ee5\u4ed6\u53c8\u5343 \u65b9\u767e\u8ba1\u8bf7\u6cd5\u738b\u51fa\u9762\uff0c\u8981\u628a\u5b87\u513f\u62ff\u53bb\u3002\u73b0\u4e0b\u5916\u8fb9\u7684\u60c5\u51b5\u6df7\u4e71\u4e4b\u6781\uff0c\u4fc4\u9a6c\u767b\u5df2\u6d3e\u4eba\u53bb\u6c42\u5370\u5ea6\u7684\u5580 \u6797\u90a6\u5927\u516c\u548c\u5c3c\u6cca\u5c14\u7684\u56fd\u738b\u51fa\u5175\uff0c\u56fe\u8c0b\u5c3d\u9010\u6c49\u4eba\uff0c\u7edf\u4e00\u897f\u85cf\uff0c\u8fd9\u98ce\u58f0\u4e5f\u5df2\u4f20\u51fa\u6765\u4e86\uff0c\u8428\u8fe6\u57ce\u4e2d \u7684\u6c49\u4eba\uff0c\u90fd\u5173\u8d77\u5927\u95e8\uff0c\u4e0d\u6562\u51fa\u8857\u5462\u3002\u770b\u6765\u897f\u85cf\u7684\u6df7\u6218\u4e4b\u5c40\u5df2\u6210\uff0c\u82e5\u518d\u5f15\u5916\u5175\u8fdb\u6765\uff0c\u8fd9\u5c40\u9762\u4e0d \u582a\u8bbe\u60f3\u3002\u6d1b\u73e0\u7684\u5175\u5c11\uff0c\u53ea\u6015\u5728\u51e0\u5929\u4e4b\u5185\uff0c\u5c31\u8981\u7ed9\u4fc4\u9a6c\u767b\u626b\u5e73\uff0c\u90a3\u65f6\uff0c\u6599\u60f3\u4fc4\u9a6c\u767b\u8fd8\u4f1a\u518d\u6765\u4e0e \u54b1\u95e8\u4e3a\u96be\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u5b98\u505a\u4e0d\u505a\u6b8a\u65e0\u6240\u8c13\uff0c\u4f46\u773c\u770b\u897f\u85cf\u53db\u4e71\u6269\u5927\uff0c\u65e0\u6cd5\u6536\u62fe\uff0c\u6211 \u4f55\u4ee5\u4e0a\u5bf9\u671d\u5ef7\uff0c\u4e0b\u5bf9\u767e\u59d3\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u4f9d\u54b1\u4eec\u4eca\u65e9\u7684\u5546\u8bae\uff0c\u706b\u901f\u6d3e\u4eba\u62a5\u4e0e\u798f\u5eb7\u5b89\u77e5\u9053\u3002\u6c42\u4ed6\u8d76\u5feb \u51fa\u5175\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u6d3e\u8c01\u5462\uff1f\u201d\u8427\u9752\u5cf0\u9053\uff1a\u201c\u6211\u613f\u6548\u72ac\u9a6c\u4e4b\u52b3\u3002\u201d\u5510\u7ecf\u5929\u770b\u4ed6\u4e00\u773c\uff0c\u5374\u4e0d \u8a00\u8bed\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u8427\u9752\u5cf0\u7684\u6b66\u529f\uff0c\u8981\u7a81\u56f4\u8fdc\u8d74\u62c9\u8428\uff0c\u53ea\u6015\u672a\u5fc5\u80fd\u591f\u3002\u201d\u4ed6\u81ea\u5df1\u672c\u6765\u60f3 \u53bb\uff0c\u4f46\u60f3\u8d77\u7559\u5b88\u7684\u8d23\u4efb\u66f4\u91cd\uff0c\u6545\u6b64\u8e0c\u8e87\u83ab\u51b3\u3002\u8427\u9752\u5cf0\u9053\uff1a\u201c\u5510\u5927\u4fa0\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u5510\u7ecf\u5927\u4e0d\u4fbf \u8bf4\u4ed6\u7684\u672c\u9886\u4e0d\u884c\uff0c\u773c\u4e00\u8f6c\uff0c\u5ffd\u5730\u60f3\u8d77\u4e00\u4eba\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5fc3\u6025\u7740\u8981\u89c1\u5929\u5b87\u5417\uff1f\u73b0\u5728\u53ef\u4ee5\u5148\u89c1 \u89c1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u5f97\u5510\u7ecf\u5929\u4f20\u6388\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u5df2\u9759\u5750\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u8fd9\u65f6\u6b63\u505a\u5b8c\u529f\u8bfe\uff0c\u4f46\u89c9\u795e\u6717 \u6c14\u6e05\uff0c\u5fc3\u4e2d\u90c1\u7ed3\u4e4b\u6c14\uff0c\u4e5f\u81ea\u7136\u800c\u7136\u7684\u6563\u4e86\u3002\u542c\u5f97\u7236\u4eb2\u547c\u5524\uff0c\u7acb\u523b\u51fa\u6765\uff0c\u89c1\u7740\u81ea\u5df1\u5f00\u8499\u7684\u4e1a \u5e08\uff0c\u5fc3\u4e2d\u9ad8\u5174\uff0c\u795e\u8272\u66f4\u4f73\uff0c\u8427\u9752\u5cf0\u9053\uff1a\u201c\u4e24\u5e74\u4e0d\u89c1\uff0c\u542c\u8bf4\u4f60\u7684\u6b66\u529f\u5927\u6709\u957f\u8fdb\u4e86\uff0c\u53ef\u559c\u53ef\u8d3a \u5475\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u90a3\u90fd\u662f\u9760\u4e24\u4f4d\u5e08\u7236\u548c\u5510\u5927\u4fa0\u7684\u6307\u70b9\u3002\u542c\u8bf4\u5e08\u7236\u5927\u5a5a\uff0c\u5e08\u6bcd\u53ef\u6709\u540c\u6765 \u4e48\uff1f\u201d\u8427\u9752\u5cf0\u4e34\u8001\u4f5c\u65b0\u90ce\uff0c\u53cd\u800c\u6709\u4e9b\u817c\u8146\uff0c\u9053\uff1a\u201c\u5979\u8fd8\u7559\u5728\u56db\u5ddd\u3002\u201d\u8138\u4e0a\u6d6e\u51fa\u559c\u60a6\u7684\u7b11\u5bb9\u3002 \u9648\u5929\u5b87\u7a81\u7136\u89e6\u8d77\u5fc3\u4e2d\u4f24\u75db\uff0c\u9762\u8272\u53c8\u6c89\u6697\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u829d\u5a1c\u8fd9\u6b21\u624b\u5203\u7236\u4ec7\uff0c\u4e3a\u8428\u8fe6\u85cf\u6c11\u9664\u53bb\u4e00\u4e2a\u6b8b\u66b4\u7684\u571f\u53f8\uff0c\u53ef\u4f69\u4e4b \u6781\u3002\u201d\u9648\u5929\u5b87\u672c\u5df2\u6cea\u54bd\u5fc3\u9178\uff0c\u88ab\u5510\u7ecf\u5929\u4e00\u6311\uff0c\u629a\u80f8\u4f4e\u4f4d\uff0c\u53eb\u9053\uff1a\u201c\u53ef\u662f\u829d\u5a1c\u662f\u6c38\u4e0d\u4f1a\u56de\u6765 \u4e86\u3002\u201d\u9648\u5b9a\u57fa\u4ece\u5510\u7ecf\u5929\u53e3\u4e2d\uff0c\u5df2\u77e5\u9053\u513f\u5b50\u82e6\u604b\u6c81\u5e03\u85e9\u738b\u5973\u513f\u4e4b\u4e8b\uff0c\u89c1\u513f\u5b50\u4f24\u75db\uff0c\u81ea\u662f\u96be\u8fc7\uff0c \u4f46\u4ed6\u4ee5\u56fd\u4e8b\u4e3a\u91cd\uff0c\u89c1\u513f\u5b50\u5982\u6b64\uff0c\u53c8\u4e0d\u7981\u4f5b\u7136\u4e0d\u60a6\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u5b87\u513f\uff0c\u4f60\u8bfb\u5723\u8d24\u4e66\u6240\u5b66\u4f55 \u4e8b\uff1f\u201d\u9648\u5929\u5b87\u51db\u7136\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8bf7\u7236\u4eb2\u6559\u8bad\u3002\u201d\u9648\u5b9a\u57fa\u9053\uff1a\u201c\u5982\u4eca\u897f\u85cf\u53db\u4e71\u5df2\u6210\uff0c\u4f60\u4e3a\u4e00\u4e2a \u5973\u5b50\u98a0\u98a0\u5012\u5012\uff0c\u4e0d\u60ed\u6127\u4e48\uff1f\u201d\u9648\u5929\u5b87\u5446\u4e86\u4e00\u5446\uff0c\u53ea\u542c\u5f97\u5510\u7ecf\u5929\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u829d\u5a1c\u6b7b \u4e0d\u7791\u76ee\u54ea\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u5fc3\u5934\u4e00\u9707\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6b7b\u4e0d\u7791\u76ee\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u829d\u5a1c\u751f\u524d\u6df1\u5fc3\u76fc\u671b\u6c49\u85cf \u4e00\u5bb6\uff0c\u8fd9\u5fc3\u610f\u4f60\u5b9a\u7136\u77e5\u9053\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5979\u4ee5\u85e9\u738b\u5973\u513f\u7684\u8eab\u4efd\uff0c\u5374\u7edd\u4e0d\u56e0\u6211\u662f\u6c49\u4eba\u800c\u6709\u534a \u70b9\u6b67\u89c6\uff0c\u6df1\u60c5\u539a\u610f\uff0c\u6211\u6c38\u4e16\u96be\u5fd8\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5982\u4eca\u5374\u56e0\u5979\u4e4b\u6b7b\uff0c\u4fc4\u9a6c\u767b\u85c9\u81ea\u717d\u52a8\u53db\u4e71\uff0c \u6311\u62e8\u85cf\u4eba\u4ec7\u89c6\u6c49\u4eba\uff0c\u5979\u5c82\u80fd\u7791\u76ee\uff1f\u5979\u5c38\u9ab8\u88ab\u4fc4\u9a6c\u767b\u62a2\u53bb\uff0c\u8fc4\u4eca\u672a\u80fd\u5b89\u846c\uff0c\u5c82\u80fd\u7791\u65e5\uff1f\u5979\u6240\u6b22 \u559c\u7684\u4eba\uff0c\u5982\u4eca\u773c\u89c1\u751f\u524d\u6240\u4e0d\u613f\u89c1\u7684\u53db\u4e71\u53d1\u751f\uff0c\u5374\u8896\u624b\u65c1\u89c2\uff0c\u5979\u5c82\u80fd\u7791\u76ee\uff1f\u201d\u4e00\u8fde\u4e09\u4e2a\u201c\u5c82\u80fd \u7791\u76ee\u201d\uff0c\u597d\u50cf\u4e09\u4e2a\u7126\u96f7\u6253\u5728\u9648\u5929\u5b87\u7684\u5fc3\u4e0a\uff0c\u9648\u5929\u5b87\u5446\u5982\u6728\u9e21\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u62ac\u8d77\u773c\u775b\uff0c\u5583\u5583\u8bf4 \u9053\uff1a\u201c\u4f60\u53eb\u6211\u600e\u4e48\u529e\uff1f\u201d\u5510\u7ecf\u5929\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u4eec\u60f3\u6d3e\u4eba\u53bb\u5411\u798f\u5eb7\u5b89\u8bf7\u6551\u5175\uff0c\u5440\uff0c\u53ef\u60dc\u53c8 \u8bf7\u4e0d\u5230\u4eba\u53bb\u3002\u201d\u9648\u5929\u5b87\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff0c\u4e3a\u4e86\u7236\u4eb2\uff0c\u4e3a\u4e86\u829d\u5a1c\uff0c\u8fd9\u9001\u4fe1\u7684\u5dee\u4e8b\u6211\u4e49 \u4e0d\u5bb9\u8f9e\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u4fe1\u5173\u7cfb\u91cd\u5927\uff0c\u4f60\u53ef\u8981\u80c6\u5927\u5fc3\u7ec6\u5475\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u5373\u4f7f\u8d74\u6c64\u8e48 \u706b\uff0c\u8fd9\u5c01\u4fe1\u66f3\u4e5f\u5b9a\u7136\u9001\u5230\u3002\u201d\u5510\u7ecf\u5929\u5927\u559c\uff0c\u987b\u77e5\u9648\u5929\u5b87\u7684\u6b66\u529f\u73b0\u5728\u5df2\u80dc\u4e8e\u5e08\u7236\uff0c\u867d\u8fd8\u6bd4\u4e0d\u4e0a \u4fc4\u9a6c\u767b\u8bf7\u6765\u7684\u5370\u5ea6\u82e6\u884c\u50e7\u7b49\u4eba\uff0c\u4f46\u8f7b\u529f\u5374\u80dc\u8fc7\u4e86\u4e00\u6d41\u9ad8\u624b\uff0c\u7eb5\u6253\u4e0d\u8fc7\uff0c\u4e5f\u53ef\u9003\u8131\u3002\u7531\u4ed6\u9001\u4fe1 \u5f53\u7136\u6bd4\u8427\u9752\u5cf0\u597d\u591a\u3002\u9648\u5b9a\u57fa\u7acb\u523b\u5199\u4e86\u5448\u6587\uff0c\u4ea4\u7ed9\u513f\u5b50\uff0c\u8fd9\u65f6\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u9648\u5929\u5b57\u8349\u8349\u5403\u8fc7 \u665a\u996d\uff0c\u7acb\u523b\u52a8\u8eab\uff0c\u4ed6\u6362\u4e0a\u4e86\u4e00\u8eab\u9ed1\u8863\uff0c\u8eab\u5f62\u6240\u81f3\uff0c\u9632\u5982\u4e00\u6e9c\u9ed1\u70df\uff0c\u970e\u5ffd\u5373\u8fc7\uff0c\u8fde\u95ef\u4fc4\u9a6c\u767b\u5e03 \u4e0b\u7684\u5341\u51e0\u4e2a\u54e8\u5c97\uff0c\u7adf\u9647\u65e0\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u8fd9\u56de\u6ee1\u5fc3\u9ad8\u5174\uff0c\u5230\u8428\u8fe6\u4e3b\u6301\u5f00\u5149\u5927\u5178\uff0c\u6ee1\u5fc3\u4ee5\u4e3a\u961f\u6b64\u53ef\u4ee5\u5728\u897f\u85cf\u91cd\u7acb\u6839\u57fa\uff0c\u4e0d \u6599\u5374\u95f9\u51fa\u4e86\u8fd9\u7b49\u610f\u5916\u4e4b\u4e8b\uff0c\u81ea\u5df1\u624b\u4e0b\u7684\u201c\u5723\u5973\u201d\uff0c\u7adf\u6740\u4e86\u571f\u53f8\uff0c\u53c8\u8bef\u4f24\u4e86\u73ed\u7985\u7684\u4ee3\u8868\uff0c\u5f04\u5f97 \u4e0d\u59a5\uff0c\u53ea\u6050\u8fbe\u8d56\u73ed\u7985\u53c8\u8981\u5c06\u767d\u6559\u518d\u9a71\u51fa\u897f\u85cf\u3002\u800c\u81ea\u5df1\u4ee5\u201c\u6cd5\u738b\u201d\u7684\u8eab\u9636\uff0c\u4ea6\u56e0\u6b64\u800c\u53d7\u5230\u4fc4\u9a6c \u767b\u7684\u5a01\u80c1\uff0c\u8981\u52a9\u4ed6\u5c06\u9648\u5929\u5b87\u6349\u6765\uff0c\u5c24\u5176\u4f7f\u5f97\u6cd5\u738b\u95f7\u95f7\u4e0d\u4e50\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u6b63\u5728\u5587\u561b\u5bfa\u7684\u5927\u85cf\u5bab\u4e2d\u8d1f\u624b\u5f98\u5f8a\uff0c\u5fc3\u60c5\u70e6\u8e81\uff0c\u60f3\u8d77\u7ecf\u6587\u6240\u8bf4\u201c\u4f60\u5e94\u8be5\u820d\u5df1\u4e3a\u4eba\uff0c \u5927\u53d1\u5b8f\u613f\uff0c\u666e\u6551\u4f17\u751f\u3002\u201d\u66f4\u89c9\u4e0d\u5b89\uff0c\u5fc3\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u8fd9\u53ae\u597d\u733e\u5f02\u5e38\uff0c\u9648\u5b9a\u57fa\u5374\u662f\u4e00\u4e2a\u597d\u5b98\u3001 \u6211\u4e3a\u4ec0\u4e48\u8981\u66ff\u4fc4\u9a6c\u767b\u9677\u5bb3\u597d\u4eba\uff1f\u6211\u8fd9\u6837\u505a\u54ea\u8fd8\u80fd\u4f5c\u4e00\u6559\u4e4b\u4e3b\uff1f\u201d\u4f46\u968f\u5373\u53c8\u60f3\u5230\u767d\u6559\u9762\u4e34\u9a71\u9010 \u7684\u5371\u9669\uff0c\u6743\u8861\u5229\u5bb3\uff0c\u660e\u77e5\u4fc4\u9a6c\u767b\u5305\u85cf\u7978\u5fc3\uff0c\u5a01\u80c1\u81ea\u5df1\uff0c\u5374\u53c8\u4e0d\u80fd\u4e0d\u987a\u4ed6\u4e4b\u8bf7\u3002\u5440\uff0c\u5728\u5229\u5bb3\u7684 \u5173\u5934\u4e0a\uff0c\u9664\u4e86\u5927\u5723\u5927\u8d24\uff0c\u53c8\u6709\u8c01\u4e0d\u4e3a\u81ea\u5df1\u6253\u7b97\uff1f\u4ee5\u767d\u6559\u6cd5\u738b\u8fd9\u6837\u6709\u9053\u7684\u7684\u5587\u561b\u9ad8\u618e\uff0c\u5982\u4eca\u4e5f \u81ea\u5f77\u5fa8\u65e0\u8ba1\uff0c\u4e00\u5ffd\u513f\u60f3\u4e0d\u987e\u5229\u5bb3\uff0c\u5c06\u4fc4\u9a6c\u767b\u4e25\u60e9\uff0c\u62fc\u7740\u548c\u9ec4\u6559\u51b3\u88c2\u7684\u5371\u9669\uff1a\u6700\u591a\u518d\u9000\u56de\u9752 \u6d77\uff1b\u4e00\u5ffd\u513f\u60f3\u987e\u5168\u5927\u5c40\uff0c\u727a\u7272\u9648\u5b9a\u57fa\u7684\u513f\u5b50\uff1b\u6b63\u5728\u4eba\u5929\u4ea4\u865e\uff0c\u601d\u6f6e\u6df7\u4e71\u4e4b\u9645\uff0c\u5ffd\u62a5\u62a4\u6cd5\u5f1f\u5b50 \u5df2\u5c06\u9648\u5929\u5b87\u62ff\u6765\uff0c\u6cd5\u4e3b\u4e0b\u547d\u53eb\u4ed6\u4eec\u8fdb\u5bab\uff0c\u9063\u4fc4\u9a6c\u767b\u5148\u56de\u53bb\u3002\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\u5c06\u91d1\u4e16\u9057\u62bc\u8fdb\u5927 \u85cf\u5bab\uff0c\u6cd5\u738b\u4e00\u89c1\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff01 \u3000\u3000\u91d1\u4e16\u9057\u867d\u7136\u53d8\u5bb9\u6613\u8c8c\uff0c\u53c8\u6362\u4e0a\u4e86\u9648\u5929\u5b87\u7684\u8863\u88f3\uff0c\u4f46\u672c\u6765\u9762\u76ee\u5230\u5e95\u8fd8\u4e0d\u80fd\u5b8c\u5168\u6539\u53d8\uff0c\u6cd5\u738b \u773c\u5149\u4f55\u7b49\u9510\u5229\uff0c\u4e00\u89c1\u4fbf\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\uff0c\u518d\u4e00\u601d\u7d22\uff0c\u731b\u7136\u7701\u8d77\u8fd9\u4fbf\u662f\u5f00\u5149\u5927\u5178\u4e4b\u65e5\uff0c\u5230\u6765\u80e1\u95f9 \u7684\u75af\u72c2\u5c11\u5e74\u3002 \u3000\u3000\u6cd5\u738b\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6d3e\u62a4\u6cd5\u5f1f\u5b50\u524d\u6765\u8bf7\u6211\uff0c\u600e\u4e48\u8fd8\u4e0d\u77e5\u9053 \u6211\u662f\u8c01\uff1f\u201d\u90a3\u4e24\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u7980\u9053\uff1a\u201c\u571f\u53f8\u7684\u5973\u513f\u8ba4\u4ed6\u662f\u672a\u5a5a\u7684\u4e08\u592b\uff0c\u9648\u5b9a\u57fa\u4e5f\u8ba4 \u4ed6\u662f\u513f\u5b50\uff0c\u60f3\u6765\u4e0d\u4f1a\u6709\u9519\u3002\u201d\u5fc3\u4e2d\u5374\u5728\u60f3\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u8bf4\u4ed6\u4e0d\u662f\u9648\u5929\u5b87\uff0c\u771f\u4e2a\u662f\u5047\u5192\u7684\u4e0d \u6210\uff1f\u201d \u3000\u3000\u6cd5\u738b\u72d0\u7591\u66f4\u751a\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u662f\u6e05\u5ef7\u5ba3\u6170\u4f7f\u9648\u5b9a\u57fa\u7684\u513f\u5b50\uff0c\u65ad\u65e0\u4e0e\u6211\u4f5c\u5bf9\u7684\u9053\u7406\u3002\u201d\u6325 \u624b\u53eb\u4e24\u4e2a\u5f1f\u5b50\u9000\u4e0b\uff0c\u63a9\u4e0a\u5bab\u95e8\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u5728\u4f60\u4e00\u8eab\u6b66\u529f\uff0c\u4e3a\u4ec0\u4e48\u8981\u5192\u5145\u522b\u4eba\uff1f\u201d\u91d1\u4e16\u9057 \u9053\uff1a\u201c\u5728\u4f60\u662f\u4e00\u6559\u4e4b\u4e3b\uff0c\u4e3a\u4ec0\u4e48\u8981\u542c\u4fc4\u9a6c\u767b\u7684\u6446\u5e03\uff0c\u9677\u5bb3\u597d\u4eba\uff1f\u201d\u8bf4\u8bdd\u9488\u950b\u76f8\u5bf9\uff0c\u6cd5\u738b\u5fc3\u4e2d \u6709\u6127\uff0c\u5bf9\u7b54\u4e0d\u4e0a\uff0c\u91d1\u4e16\u9057\u602a\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6d3b\u4f5b\u4e5f\u6709\u4e3a\u96be\u4e4b\u5904\uff01\u54c8\u54c8\uff0c\u4f60\u7ba1\u6211\u662f\u4e0d\u662f\u9648\u5929 \u5b87\uff0c\u4f60\u4f46\u80fd\u62ff\u5f97\u51fa\u4e00\u4e2a\u4eba\u6765\u4ea4\u5dee\uff0c\u8fd9\u4e0d\u5c31\u5b8c\u4e86\uff01\u201d \u3000\u3000\u50cf\u91d1\u4e16\u9057\u8fd9\u6837\u7684\u5728\u6cd5\u738b\u9762\u524d\u653e\u8086\uff0c\u90a3\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u6cd5\u738b\u5fc3\u4e2d\u8f6c\u4e86\u597d\u51e0\u4e2a \u5ff5\u5934\uff0c\u60f3\u628a\u4ed6\u653e\u8d70\uff0c\u60f3\u628a\u4ed6\u60e9\u6212\u4e00\u756a\uff0c\u60f3\u628a\u4ed6\u4ea4\u7ed9\u4fc4\u9a6c\u767b\uff0c\u4f46\u53c8\u60f3\u8d77\u4ed6\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a\uff0c\u53ea\u6015 \u4ed6\u4e86\u571f\u53f8\u5821\u4e2d\uff0c\u53c8\u95ef\u51fa\u5f25\u5929\u5927\u7978\u3002\u91d1\u4e16\u9057\u563b\u563b\u51b7\u7b11\uff0c\u65c1\u82e5\u65e0\u4eba\uff0c\u6cd5\u738b\u9762\u8272\u4e00\u7aef\uff0c\u5ffd\u5730\u6c89\u58f0\u8bf4 \u9053\uff1a\u201c\u4f60\u771f\u4e2a\u81ea\u613f\u5230\u571f\u53f8\u5821\u4e2d\uff0c\u4ee3\u4eba\u53d7\u7f6a\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u90a3\u662f\u6211\u7684\u4e8b\u60c5\uff0c\u4f60\u4e0d\u7528\u7ba1\u3002\u201d \u6cd5\u738b\u9053\uff1a\u597d\uff0c\uff0c\u90a3\u6211\u7ed9\u4f60\u795d\u798f\u9001\u884c\u3002\u201d\u624b\u638c\u4e00\u7ffb\uff0c\u7a81\u7136\u5411\u91d1\u4e16\u9057\u9876\u5fc3\u62cd\u4e0b\uff0c\u91d1\u4e16\u9057\u51fa\u638c\u76f8 \u62b5\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u4e00\u4e0d\u4fe1\u795e\uff0c\u4e8c\u4e0d\u4fe1\u4f5b\uff0c\u8c01\u8981\u4f60\u795d\u798f\uff1f\u201d\u5ffd\u89c9\u6cd5\u738b\u638c\u529b\u5982\u5c71\uff0c\u8feb\u5f97\u4eba\u51e0\u4e4e \u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6025\u5fd9\u5168\u795e\u8fd0\u6c14\uff0c\u62fc\u529b\u62b5\u6321\uff0c\u53ea\u542c\u5f97\u6cd5\u738b\u8bf4\u9053\uff1a\u201c\u4f3c\u4f60\u8fd9\u6837\u80e1\u95f9\uff0c\u4fbf \u8be5\u5904\u7f6a\u3002\u4f60\u65e2\u81ea\u6043\u6b66\u529f\uff0c\u6211\u800c\u4eca\u5c31\u628a\u4f60\u7684\u6b66\u529f\u5e9f\u6389\uff01\u201d\u91d1\u4e16\u9057\u672c\u60f3\u53cd\u5507\u76f8\u7a3d\uff0c\u4f46\u6cd5\u738b\u7684\u638c\u529b \u8d8a\u8feb\u8d8a\u7d27\uff0c\u7adf\u7136\u4ee4\u4ed6\u4e0d\u80fd\u5206\u5fc3\u8bf4\u8bdd\u3002 \u3000\u3000\u4f46\u91d1\u4e16\u9057\u5df2\u5c3d\u5f97\u6bd2\u9f99\u5c0a\u8005\u6240\u4f20\uff0c\u6bd2\u9f99\u5c0a\u8005\u7684\u5185\u529f\u81ea\u521b\u4e00\u5bb6\uff0c\u867d\u975e\u6b63\u5b97\uff0c\u521a\u52b2\u4e4b\u5904\uff0c\u5374\u662f \u6b66\u6797\u72ec\u6b65\uff0c\u4e16\u4e0a\u65e0\u53cc\uff0c\u91d1\u4e16\u9057\u867d\u7136\u53ea\u6709\u5341\u591a\u5e74\u7684\u529f\u529b\uff0c\u4f46\u5728\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4ea6\u80fd\u4e0e\u6cd5\u738b\u76f8\u6301 \u4e0d\u4e0b\uff0c\u6cd5\u6697\u6697\u79f0\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u8fd9\u6837\u7684\u826f\u6750\u7f8e\u8d28\uff0c\u5374\u504f\u504f\u4e0d\u6b63\u8def\uff0c\u80e1\u4f5c\u975e\u4e3a\u3002\u201d \u3000\u3000\u53c8\u652f\u9999\u7684\u65f6\u523b\uff0c\u91d1\u4e16\u9057\u5ffd\u89c9\u6709\u4e00\u80a1\u70ed\u529b\u4ece\u6cd5\u738b\u7684\u638c\u5fc3\u4f20\u4e86\u8fc7\u6765\uff0c\u6709\u5982\u7f6e\u8eab\u70c8\u65e5\u4e4b\u4e0b\uff0c\u5168 \u8eab\u53d1\u6eda\uff1b\u91d1\u4e16\u9057\u6e10\u6e10\u652f\u6301\u4e0d\u4f4f\uff0c\u60c5\u77e5\u8fd9\u6837\u4e0b\u53bb\uff0c\u81ea\u5df1\u5fc5\u5c06\u7d2f\u5f97\u529b\u7aed\u795e\u75b2\uff0c\u53d8\u6210\u5e9f\u4eba\uff0c\u4f46\u53c8\u4e0d \u80fd\u4e0d\u62fc\u529b\u62b5\u6321\uff0c\u4ee5\u514d\u88ab\u4ed6\u7684\u638c\u529b\u4f24\u4e86\u4e94\u810f\u516d\u8151\u3002\u53c8\u8fc7\u7247\u523b\uff0c\u91d1\u4e16\u9057\u4f46\u89c9\u5507\u67af\u820c\u71e5\uff0c\u6709\u5185\u706b\u711a \u8eab\u4e4b\u8c61\uff0c\u6cd5\u738b\u89c9\u5f97\u5468\u8eab\u9aa8\u9abc\u9690\u9690\u4f5c\u75db\uff0c\u90a3\u662f\u5185\u529b\u6d88\u8017\u8fc7\u751a\u4e4b\u8c61\u3002\u4f46\u6bd4\u5c06\u8d77\u6765\uff0c\u6cd5\u738b\u4ee5\u6570\u5341\u5e74 \u7684\u529f\u529b\uff0c\u81ea\u662f\u8f83\u80dc\u4e00\u7b79\uff0c\u800c\u91d1\u4e16\u9057\u5374\u663e\u5df2\u652f\u6301\u4e0d\u4f4f\u3002\u6cd5\u738b\u5438\u4e00\u53e3\u6c14\uff0c\u638c\u5fc3\u4e00\u538b\uff0c\u5fc3\u4e2d\u5ffd\u5730\u60f3 \u9053\uff1a\u201c\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u7ec3\u5230\u8fd9\u822c\u672c\u9886\uff0c\u6211\u82e5\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d\u4f46\u968f\u5373\u53c8\u60f3\uff1a\u201c\u6211\u82e5 \u4e0d\u5c06\u4ed6\u5e9f\u4e86\uff0c\u5982\u4f55\u6562\u653e\u5fc3\u4ea4\u7ed9\u4fc4\u9a6c\u767b\uff1f\u201d\u5c31\u5728\u8fd9\u638c\u529b\u5c06\u53d1\u672a\u53d1\u4e4b\u9645\uff0c\u5ffd\u89c1\u91d1\u4e16\u9057\u76ee\u9732\u51f6\u5149\uff0c \u53e3\u89d2\u5fae\u5fae\u62bd\u7f29\u3002\u6cd5\u738b\u672c\u662f\u4e2a\u6709\u9053\u9ad8\u50e7\uff0c\u5f88\u96be\u4e3a\u5916\u7269\u6240\u6270\uff0c\u89c1\u4e86\u4ed6\u8fd9\u7b49\u602a\u5f02\u7684\u795e\u60c5\uff0c\u4e5f\u4e0d\u7981\u5fc3 \u4e2d\u6697\u60ca\u3002 \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u81ea\u77e5\u96be\u654c\u6cd5\u738b\u638c\u529b\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u6b63\u8d77\u4e86\u6740\u673a\uff01\u4ed6\u53e3\u4e2d\u542b\u6709\u5929\u4e0b\u6700\u6bd2\u7684\u6697\u5668\u2014\u2014 \u4e03\u715e\u593a\u547d\u795e\u9488\uff0c\u90a3\u662f\u7528\u86c7\u5c9b\u6700\u6bd2\u7684\u6bd2\u86c7\u53e3\u6d8e\u6240\u70bc\u7684\uff0c\u5f53\u5e74\u5510\u7ecf\u5929\u4e2d\u4e86\u4e00\u9488\uff0c\u867d\u6709\u5929\u5c71\u96ea\u83b2\uff0c \u4e5f\u75c5\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u6cd5\u738b\u7684\u5185\u529f\u4e0e\u5510\u7ecf\u5929\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4f46\u4ed6\u6ca1\u6709\u5929\u5c71\u96ea\u83b2\uff0c\u82e5\u4e2d\u4e86\u6bd2\u9488\uff0c\u90a3\u662f\u5fc5 \u5c06\u6bd9\u547d\u7684\u4e86\u3002\u91d1\u4e16\u9057\u53e3\u89d2\u5fae\u5fae\u62bd\u6410\uff0c\u5fc3\u4e2d\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u4ed6\u65e0\u51a4\u65e0\u4ec7\uff0c\u5c06\u4ed6\u6740\u4e86\uff0c\u4e8e\u5fc3\u4f55 \u5b89\uff1f\u201d\u968f\u5373\u53c8\u60f3\u9053\uff1a\u201c\u82e5\u4e0d\u6740\u4ed6\uff0c\u6211\u7684\u6b66\u529f\u4fbf\u8981\u5e9f\u4e86\uff0c\u6ca1\u6709\u6b66\u529f\uff0c\u66f4\u53d7\u4e16\u4eba\u6b3a\u4fae\uff0c\u6d3b\u7740\u53c8\u6709 \u4ec0\u4e48\u610f\u601d\uff1f\u201d\u6b63\u8981\u5f20\u53e3\u5c06\u6bd2\u9488\u6742\u5728\u53e3\u6d8e\u4e4b\u4e2d\u5410\u51fa\uff0c\u5ffd\u53c8\u60f3\u9053\uff1a\u201c\u4ed6\u5230\u5e95\u662f\u4e00\u6559\u4e4b\u4e3b\uff0c\u60e8\u6b7b\u6211 \u624b\uff0c\u5c82\u4e0d\u53ef\u60dc\uff1f\u53cd\u6b63\u6211\u4e5f\u6d3b\u4e0d\u4e45\u957f\u7684\u4e86\uff0c\u4e0d\u5982\u8ba9\u4ed6\u4e00\u6b21\u3002\u201d\u4f46\u89c9\u6cd5\u738b\u7684\u638c\u529b\u5484\u5484\u8feb\u4eba\uff0c\u5ffd\u5730 \u53c8\u8d77\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c\u60f3\u9053\uff1a\u201c\u6211\u81ea\u79bb\u5f00\u86c7\u5c9b\u4ee5\u6765\uff0c\u8d70\u904d\u6c5f\u6e56\uff0c\u6253\u5c3d\u5929\u4e0b\u9ad8\u624b\uff0c\u4ece\u672a\u8d25\u5f97\u5982\u6b64\u4e4b \u60e8\uff0c\u6211\u82e5\u7ed9\u4ed6\u5e9f\u4e86\u6b66\u529f\uff0c\u4e0d\u77e5\u8005\u5c82\u4e0d\u4ee5\u4e3a\u6211\u771f\u4e2a\u654c\u4e0d\u8fc7\u4ed6\uff1f\u6709\u8c01\u80fd\u60f3\u5230\u53cd\u800c\u662f\u6211\u8ba9\u4ed6\uff0c\u4e0d\u5fcd \u53d6\u4ed6\u6027\u547d\uff1f\u201d\u91d1\u4e16\u9057\u4e00\u751f\u597d\u80dc\uff0c\u6b64\u65f6\u60f3\u7684\u662f\u201c\u5b81\u6559\u8eab\u6b7b\uff0c\u4e0d\u6559\u540d\u8fb1\u3002\u201d\u5fc3\u601d\u4e00\u53d8\u518d\u53d8\uff0c\u6bd2\u9488 \u4e5f\u5df2\u5410\u5230\u5507\u8fb9\uff0c\u5c31\u5728\u5c06\u53d1\u672a\u53d1\u4e4b\u95f4\u3002 \u3000\u3000\u53ef\u601c\u5916\u9762\u7684\u56db\u5927\u62a4\u6cd5\u5f1f\u5b50\u90fd\u6b63\u5728\u5bab\u95e8\u9759\u5019\uff0c\u4ed6\u4eec\u7b49\u4e86\u4e2a\u591a\u65f6\u8fb0\uff0c\u91cc\u9762\u8fd8\u662f\u6c89\u5bc2\u65e0\u58f0\uff0c\u5fc3 \u4e2d\u90fd\u662f\u8be7\u5f02\u4e4b\u6781\uff0c\u54ea\u91cc\u77e5\u9053\uff0c\u91cc\u9762\u7684\u4e24\u5927\u9ad8\u624b\uff0c\u90fd\u5df2\u5230\u4e86\u6027\u547d\u4fc4\u9877\uff0c\u5371\u673a\u4e00\u77ac\u4e4b\u65f6\uff01 \u3000\u3000\u9648\u5929\u5b87\u5e26\u4e86\u4e66\u4fe1\uff0c\u95ef\u8fc7\u4e86\u571f\u53f8\u519b\u961f\u7684\u54e8\u5c97\uff0c\u8fde\u591c\u52a8\u8eab\uff0c\u5954\u5f80\u62c9\u8428\u3002\u5f80\u62c9\u8428\u7684\u8def\uff0c\u8981\u4ece\u571f \u53f8\u57ce\u5821\u4e0b\u9762\u7ecf\u8fc7\uff0c\u57ce\u5821\u5efa\u5728\u5c71\u4e0a\uff0c\u8def\u5219\u4ece\u5c71\u8c37\u7a7f\u8fc7\uff0c\u9648\u5927\u5b57\u7ecf\u8fc7\u5c71\u8c37\u65f6\uff0c\u53ea\u89c1\u5c71\u4e0a\u5bc6\u5bc6\u9ebb\u9ebb \u6ee1\u662f\u519b\u961f\uff0c\u57ce\u5821\u4e0a\u9ed1\u5f71\u5e62\u5e62\uff0c\u4e5f\u4f3c\u7ad9\u6ee1\u4e86\u4eba\uff0c\u9648\u5929\u5b87\u77e5\u9053\u8fd9\u662f\u6d1b\u73e0\u7684\u519b\u961f\u524d\u6765\u56f4\u653b\u57ce\u5821\uff0c\u6b63 \u4e0e\u4fc4\u9a6c\u767b\u76f8\u6301\u3002\u9648\u5929\u5b87\u7d27\u8bb0\u7740\u5510\u7ecf\u5927\u7684\u8bdd\uff1a\u4e0d\u53ef\u4e2d\u9014\u803d\u6401\uff0c\u9047\u7740\u519b\u961f\u4fbf\u8981\u7ec3\u9053\u907f\u5f00\u3002\u9648\u5929\u5b87 \u501f\u7269\u969c\u5f62\uff0c\u4ed7\u7740\u4e00\u8eab\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u7a7f\u8fc7\u5c71\u8c37\uff0c\u5e78\u559c\u5c71\u5761\u4e0a\u7684\u519b\u961f\u90fd\u6ca1\u6709\u53d1\u73b0\uff0c\u770b\u770b\u5c31\u8981\u51fa\u4e86 \u4e24\u519b\u9635\u5730\uff0c\u5df2\u5230\u5c71\u7684\u5317\u9762\uff0c\u90a3\u662f\u571f\u53f8\u7684\u9632\u5730\u8fb9\u6cbf\uff0c\u53ea\u6709\u51e0\u4e2a\u54e8\u5175\u5728\u5de1\u903b\u4e86\u3002\u9648\u5929\u5b87\u63d0\u4e00\u53e3 \u6c14\uff0c\u63a0\u8fc7\u6700\u4e3d\u7684\u54e8\u5c97\uff0c\u5ffd\u5730\u4e00\u6761\u9ed1\u5f71\u7a9c\u4e86\u51fa\u6765\uff0c\u7a84\u8def\u76f8\u9022\uff0c\u6b63\u662f\u4fc4\u9a6c\u767b\u8fb9\u6b66\u529f\u6700\u9ad8\u7684\u5370\u5ea6\u82e6 \u884c\u50e7\u3002 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u5370\u5ea6\u82e6\u884c\u50e7\u4f9d\u7a00\u8ba4\u5f97\u8fd9\u591c\u884c\u4eba\u6b63\u662f\u4ed6\u4eec\u6240\u6b32\u5f97\u7518\u5fc3\u7684\u9648\u5929\u5b87\uff0c\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u539f\u6765\u662f\u4f60\uff01\u201d\u7af9\u6756\u4e00\u6325\uff0c\u7528\u4e86\u4e2a\u201c\u7eca\u201d\u5b57\u8bc0\uff0c\u7af9\u6756\u6325\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u6ef4\u6e9c\u6e9c\u7684\u4e24\u8fb9\u65cb\u8f6c\uff0c\u5f85 \u4e00\u4e3e\u4fbf\u9648\u5929\u5b87\u7eca\u5012\u3002\u9648\u5929\u5b87\u98de\u8eab\u4e00\u63a0\uff0c\u4e00\u62db\u201c\u5012\u6302\u94f6\u6cb3\u201d\uff0c\u957f\u5251\u4e00\u524a\uff0c\u8fd9\u62db\u6b63\u662f\u51b0\u5ddd\u5251\u6cd5\u7684 \u7cbe\u534e\u6240\u5728\uff0c\u6ee1\u62df\u5c06\u7af9\u6756\u524a\u4e3a\u4e24\u6bb5\uff0c\u54ea\u77e5\u521a\u521a\u4e0e\u7af9\u6756\u76f8\u89e6\uff0c\u90a3\u7af9\u6756\u7adf\u7136\u5982\u5f71\u9644\u5f62\uff0c\u968f\u7740\u9648\u5929\u5b87 \u7684\u5251\u8f6c\uff0c\u7af9\u6756\u6709\u5982\u6beb\u4e0d\u53d7\u529b\u7684\u7eb8\u6761\u4e00\u6837\uff0c\u9644\u5728\u5251\u4e0a\u3002\u9648\u5929\u5b87\u5927\u60ca\uff0c\u5251\u67c4\u4e00\u6c89\uff0c\u5f80\u4e0b\u4e00\u5815\uff0c\u8eab \u5f62\u7ad9\u7a33\uff0c\u4fbf\u5f85\u9003\u8d70\uff0c\u5ffd\u542c\u5f97\u5370\u5ea6\u82e6\u884c\u50e7\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u7528\u85cf\u8bed\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u3001\u4f60\u8fc7\u6765 \u770b\u6e05\u695a\u8fd9\u4eba\u662f\u4e0d\u662f\u9648\u5929\u5b57\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u56fa\u7136\u5403\u60ca\uff0c\u90a3\u5370\u5ea6\u82e6\u884c\u50e7\u4e5f\u662f\u60ca\u7591\u4e0d\u5b9a\u3002\u4ed6\u66fe\u89c1\u8fc7\u9648\u5929\u5b87\u7684\u529f\u592b\uff0c\u5728\u62a2\u593a\u91d1\u672c\u5df4 \u74f6\u4e4b\u65f6\uff0c\u9648\u5929\u5b87\u4e0d\u8fc7\u4ec5\u4ec5\u80fd\u4e0e\u4ed6\u5f92\u5f1f\u6253\u4e2a\u5e73\u624b\uff0c\u54ea\u77e5\u4ed6\u5982\u4eca\u4e0d\u4f46\u6ca1\u6709\u88ab\u7af9\u6756\u7eca\u5012\uff0c\u53cd\u800c\u80fd\u5378 \u53bb\u81ea\u5df1\u7af9\u6756\u7684\u6cbe\u7c98\u4e4b\u52b2\uff0c\u770b\u6765\u5185\u529f\u7684\u9020\u8be3\u7ade\u4e0e\u81ea\u5df1\u4e5f\u5dee\u4e0d\u591a\uff01\u4ed6\u8fd8\u4ee5\u4e3a\u662f\u770b\u9519\u4e86\u4eba\uff0c\u6025\u5fd9\u5524 \u4fc4\u9a6c\u767b\u8fc7\u6765\u76f8\u8ba4\u3002\u90a3\u5370\u5ea6\u82e6\u884c\u50e7\u7b2c\u4e8c\u6756\u7b2c\u4e09\u6756\u76f8\u7ee7\u5288\u6765\uff0c\u4e00\u6756\u7528\u67d4\uff0c\u4e00\u6756\u7528\u521a\uff0c\u9648\u5929\u5b87\u62b5\u654c \u4e0d\u4f4f\uff0c\u907f\u514d\u518d\u4e0e\u7af9\u6756\u76f8\u89e6\uff0c\u865a\u6643\u4e00\u62db\uff0c\u5ffd\u5982\u5de8\u9e1f\u7a7f\u6797\u7684\u7a81\u7136\u4ece\u82e6\u884c\u50e7\u8eab\u8fb9\u7a9c\u51fa\u3002\u82e6\u884c\u50e7\u4f38\u624b \u4e00\u6293\u6ca1\u6709\u6293\u7740\uff0c\u7728\u7728\u773c\uff0c\u4f46\u89c1\u9648\u5929\u5b87\u7684\u8eab\u5f62\u5df2\u63a0\u51fa\u6570\u5341\u4e08\u5916\uff01 \u3000\u3000\u5c71\u58a9\u5904\u4e00\u6761\u9ed1\u5f71\u5954\u6765\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8fd8\u60f3\u8d70\u4e48\uff1f\u201d\u9648\u5929\u5b87\u4e00\u77a5\uff0c\u8ba4\u5f97\u662f\u4fc4\u9a6c \u767b\uff0c\u6b63\u662f\u4ec7\u4eba\u76f8\u89c1\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5929\u5b87\u60f3\u8d77\u4fc4\u9a6c\u767b\u8bf1\u9a97\u9677\u5bb3\u829d\u5a1c\uff0c\u53c8\u62a2\u8d70\u5979\u5c38\u4f53 \u7684\u4e8b\uff0c\u5fcd\u4e0d\u4f4f\u8840\u8109\u6124\u6da8\uff0c\u628a\u5510\u7ecf\u5929\u7684\u5631\u5490\u629b\u4e4b\u8111\u540e\uff0c\u624b\u8d77\u4e00\u5251\uff0c\u7acb\u523b\u523a\u51fa\uff0c\u4fc4\u9a6c\u767b\u4e3e\u5200\u4e00 \u683c\uff0c\u8fd9\u4e00\u5251\u6765\u5f97\u8fc5\u6377\u4e4b\u6781\uff0c\u4e00\u683c\u683c\u7a7a\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u6025\u5fd9\u95ea\u8eab\uff0c\u53ea\u542c\u5f97\u201c\u6dae\u201d\u7684\u4e00\u58f0\uff0c\u9648\u5929\u5b87 \u7684\u5251\u5df2\u523a\u7a7f\u4e86\u4fc4\u9a6c\u767b\u8eab\u5185\u7684\u8f6f\u7532\uff0c\u5251\u5c16\u5728\u4ed6\u80a9\u5934\u5212\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u5b50\u3002 \u3000\u3000\u4f46\u8fd9\u6837\u963b\u4e86\u4e00\u963b\uff0c\u90a3\u5370\u5ea6\u82e6\u884c\u50e7\u5df2\u7136\u8d76\u5230\uff0c\u9648\u5929\u5b87\u82e5\u8981\u9003\u8d70\uff0c\u80da\u6765\u5f97\u53ca\uff0c\u4f46\u4ed6\u6068\u6781\u4e86\u4fc4 \u9a6c\u767b\uff0c\u62bd\u5251\u518d\u523a\uff0c\u4fc4\u9a6c\u767b\u4ea6\u975e\u5f31\u8005\uff0c\u8fd9\u65f6\u4e0d\u6c42\u653b\u654c\uff0c\u4f46\u6c42\u81ea\u4fdd\uff0c\u7adf\u7136\u63a5\u8fde\u6321\u5f00\u4e86\u9648\u5929\u5b87\u7684\u4e09 \u62db\uff0c\u5f85\u9648\u5929\u5b87\u7b2c\u56db\u62db\u51fa\u624b\u4e4b\u65f6\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u5251\u5c16\u4e00\u9707\uff0c\u5370\u5ea6\u82e6\u884c\u50e7\u7684\u7af9\u6756\u5df2\u642d\u7740\u4e86 \u4ed6\u7684\u957f\u5251\u3002 \u3000\u3000\u8fd9\u56de\u5370\u5ea6\u82e6\u884c\u50e7\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u4e0d\u8ba9\u9648\u5929\u5b87\u518d\u6709\u8131\u8eab\u7684\u673a\u4f1a\uff0c\u9648\u5929\u5b87\u867d\u7136\u5f97\u4e86\u5510\u7ecf\u5929\u4f20\u6388\u7684 \u5929\u5c71\u6d3e\u5185\u529f\u5fc3\u6cd5\uff0c\u5230\u5e95\u65f6\u65e5\u5c1a\u6d45\uff0c\u672a\u80fd\u53d1\u6325\u5999\u7528\uff1b\u90a3\u82e6\u884c\u50e7\u4e56\u5de7\u4e4b\u6781\uff0c\u603b\u662f\u987a\u7740\u9648\u5929\u5b87\u7684\u5251 \u52bf\uff0c\u9648\u5929\u5b87\u8fdb\u5219\u4ed6\u9000\uff0c\u9648\u5929\u5b87\u9000\u5219\u4ed6\u8fdb\uff0c\u4e24\u4eba\u76d8\u65cb\u8fdb\u9000\uff0c\u6709\u5982\u5b69\u5b50\u5a5a\u620f\uff0c\u5176\u5b9e\u5374\u662f\u5404\u4ee5\u4e0a\u4e58 \u5185\u529f\u76f8\u62fc\u3002\u9648\u5929\u5b87\u7684\u706b\u5019\u8fdc\u900a\u5bf9\u65b9\uff0c\u672a\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u611f\u652f\u6301\u4e0d\u4f4f\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6811\u6797\u91cc\u4e00\u58f0\u5a07\u7b11\uff0c\u90a3\u7b11\u58f0\u7adf\u662f\u719f\u6089\u4e4b\u6781\uff01\u9648\u5929\u5b87\u6014\u4e86\u4e00\u6014\uff0c\u7a81\u611f\u5bd2\u6c14\u88ad\u4eba\uff0c\u9762\u524d \u51e0\u70b9\u5bd2\u661f\u9aa4\u7136\u88ad\u5230\uff01 \u3000\u3000\u9648\u5929\u5b87\u6253\u4e86\u4e00\u4e2a\u5bd2\u5524\uff0c\u5ffd\u5730\u611f\u5230\u538b\u529b\u4e00\u677e\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u9000\u540e\u51e0\u6b65\uff0c\u7528\u811a\u5c16\u652f\u5730\uff0c\u8f6c\u4e86\u4e24 \u4e2a\u5708\u5708\uff0c\u624d\u7a33\u4f4f\u8eab\u5f62\u3002\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u82e6\u884c\u50e7\u957f\u8896\u8361\u98ce\uff0c\u5c06\u4e00\u7247\u7070\u58d5\u6f3e\u7684\u5149\u7f51\uff0c\u5439\u5f97\u56db\u6563 \u98d8\u6d6e\uff0c\u573a\u4e2d\u7a81\u7136\u591a\u4e86\u4e00\u4eba\uff0c\u6b63\u662f\u51b0\u5bab\u5f85\u5973\u5e7d\u840d\uff0c\u5979\u6240\u653e\u7684\u6697\u5668\uff0c\u4e0d\u6d88\u8bf4\u4fbf\u662f\u51b0\u9b44\u795e\u5f39\u4e86\u3002\u5979 \u7684\u529f\u529b\u5c1a\u6d45\uff0c\u4f24\u4e0d\u4e86\u82e6\u884c\u50e7\uff0c\u4f46\u4e5f\u4ee4\u90a3\u82e6\u884c\u50e7\u4e0d\u5f97\u4e0d\u5206\u51fa\u5fc3\u795e\u5e94\u4ed8\u3002 \u3000\u3000\u82e6\u884c\u50e7\u5927\u6012\uff0c\u820d\u4e86\u9648\u5929\u5b87\uff0c\u4fbf\u6251\u5e7d\u840d\uff0c\u5e7d\u840d\u8eab\u6cd5\u8f7b\u7075\uff0c\u8fde\u907f\u4e09\u62db\uff0c\u9648\u5929\u5b87\u56de\u8eab\u6765\u6551\uff0c\u5ffd \u542c\u5f97\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u4e39\u8fbe\u5c71\u524d\uff0c\u6211\u4e3b\u4eba\u5df2\u653e\u4e86\u4f60\u4e00\u6b21\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5389\u5bb3\u5417\uff1f\u201d\u82e6\u884c\u50e7\u5403\u4e86\u4e00 \u60ca\uff0c\u731b\u5730\u7701\u8d77\uff1a\u8fd9\u5973\u5b50\u548c\u51b0\u5ddd\u5929\u5973\u5e38\u5728\u4e00\u8d77\uff0c\u5979\u65e2\u7136\u5728\u6b64\u51fa\u73b0\uff0c\u51b0\u5ddd\u5929\u5973\u53ea\u6015\u4e5f\u5728\u9644\u8fd1\u3002\u4ed6 \u5fc3\u4e2d\u8fdb\u9000\uff0c\u624b\u5e95\u4ecd\u662f\u6beb\u4e0d\u653e\u677e\uff0c\u53cd\u624b\u4e00\u6756\uff0c\u8361\u5f00\u9648\u5929\u5b87\u7684\u957f\u5251\uff0c\u5de6\u624b\u4e00\u4f38\u4e00\u7f29\uff0c\u970e\u773c\u4e4b\u95f4\uff0c \u53c8\u8fdb\u4e86\u4e09\u62db\uff0c\u5e7d\u840d\u7684\u88d9\u5e26\u51e0\u4e4e\u7ed9\u4ed6\u6293\u7740\u3002 \u3000\u3000\u5e7d\u840d\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6781\u6e05\u8106\u7684\u58f0\u97f3\u7d27\u63a5\u7740\u53eb\u9053\uff1a\u201c\u5e7d\u840d\uff0c\u4f60\u5728\u548c\u8c01\u52a8\u624b\uff1f\u6211 \u5c31\u6765\u5566\uff01\u201d\u58f0\u97f3\u6765\u81ea\u5c71\u5dc5\uff0c\u597d\u50cf\u548c\u5e7d\u840d\u8bdd\u5bb6\u5e38\u4e00\u822c\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c\u542c\u5f97\u6781\u4e3a\u6e05\u695a\uff0c\u82e6\u884c\u618e\u4e00\u60ca \u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u58f0\u97f3\u4e0d\u662f\u51b0\u5ddd\u5929\u5973\u8fd8\u6709\u8c01\u4eba\uff1f\u82e6\u884c\u618e\u81ea\u5230\u897f\u85cf\u4ee5\u6765\uff0c\u5c31\u5728\u51b0\u5ddd\u5929\u5973\u624b\u4e0b\u5403\u8fc7\u4e00 \u6b21\u5927\u4e8f\uff0c\u5bf9\u51b0\u5ddd\u5929\u5973\u5fcc\u60ee\u5df2\u6781\uff0c\u6025\u5fd9\u9003\u8d70\u3002\u51b0\u5ddd\u5929\u5973\u6765\u5f97\u5feb\u6781\uff0c\u90a3\u58f0\u97f3\u5c1a\u5728\u5c71\u8c37\u56de\u65cb\uff0c\u56de\u58f0 \u672a\u6563\uff0c\u4fbf\u5df2\u5728\u5c71\u5761\u4e0a\u73b0\u51fa\u8eab\u6765\uff0c\u767d\u8863\u957f\u88d9\uff0c\u98d8\u98d8\u800c\u4e0b\uff0c\u771f\u5982\u59d1\u5c04\u4ed9\u4eba\uff0c\u4e58\u865a\u8e51\u98ce\u800c\u884c\u3002\u82e6\u884c \u50e7\u5954\u5230\u534a\u5c71\uff0c\u56de\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u5df2\u968f\u540e\u8ffd\u6765\uff0c\u5413\u5f97\u8fde\u8dd1\u5e26\u6eda\uff0c\u6eda\u4e0b\u5c71\u5761\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u8eab\u8eaf\u80a5\u80d6\uff0c\u6b66\u529f\u6bd4\u8d77\u82e6\u884c\u50e7\u66f4\u662f\u76f8\u5dee\u592a\u8fdc\uff0c\u4f46\u4ed6\u6bd4\u82e6\u884c\u50e7\u4e56\u5de7\uff0c\u5e7d\u840d\u4e00\u5230\uff0c\u4ed6\u5373 \u8d77\u6b65\u5954\u9003\u3002\u4e0d\u8fc7\u7531\u4e8e\u4ed6\u8f7b\u529f\u8f83\u5f31\uff0c\u9003\u5f97\u672a\u8fdc\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u4e2a\u5927\u574f\u86cb\uff01\u201d\u633a\u5251\u8981\u8ffd\uff0c \u5e7d\u840d\u9053\uff1a\u201c\u4f55\u987b\u8fd9\u6837\u8d39\u529b\uff01\u201d\u53cc\u6307\u4e00\u5f39\uff0c\u51b0\u9b44\u795e\u5f39\u7834\u7a7a\u98de\u51fa\uff0c\u5e7d\u840d\u7684\u51b0\u5f39\u867d\u7136\u4f24\u4e0d\u4e86\u82e6\u884c \u50e7\uff0c\u5bf9\u4ed8\u4fc4\u9a6c\u767b\u5374\u662f\u7ef0\u7ef0\u6709\u4f59\uff0c\u4fc4\u9a6c\u767b\u5728\u6ca1\u547d\u5954\u9003\uff0c\u5ffd\u5730\u611f\u5230\u9888\u540e\u7684\u201c\u5929\u67f1\u7a74\u201d\u4e00\u7247\u6c81\u51c9\uff0c \u4e00\u80a1\u513f\u76f4\u4fb5\u5165\u4f53\u5185\uff0c\u534a\u8fb9\u8eab\u5b50\u767b\u65f6\u9ebb\u6728\uff0c\u51b7\u5f97\u8fde\u4f53\u5185\u7684\u8840\u6db2\u90fd\u51e0\u4e4e\u533f\u7ed3\uff0c\u5495\u54af\u4e00\u58f0\uff0c\u7acb\u523b\u5012 \u5730\uff0c\u6c14\u529b\u6d88\u5931\uff0c\u722c\u4e5f\u722c\u4e0d\u8d77\u6765\u3002\u5e7d\u840d\u9053\uff1a\u201c\u7b49\u4e0b\u54b1\u4eec\u518d\u5bf9\u4ed8\u4ed6\uff0c\u5929\u5b87\uff0c\u4e09\u66f4\u534a\u591c\uff0c\u4f60\u5192\u9669\u5230 \u8fd9\u6765\u4f5c\u4ec0\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u829d\u5a1c\uff0c\u829d\u5a1c\uff0c\u5979\uff0c\u5979\u2026\u2026\u201d\u58f0\u9178\u6cea\u4e0b\uff0c\u8bf4\u8bdd\u65ad\u65ad\u7eed\u7eed\uff0c\u826f\u4e45\u826f \u4e45\uff0c\u8fd8\u672a\u8bf4\u5f97\u6e05\u695a\u3002\u5e7d\u840d\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u829d\u5a1c\u59d0\u59d0\u4e0d\u5e78\u8eab\u6b7b\uff0c\u8fd9\u4e8b\u60c5\u6211\u5df2\u77e5\u9053\u5566\uff0c\u4f46\u5979\u5f97\u62a5 \u5927\u4ec7\uff0c\u4ea6\u53ef\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5e73\u7d20\u559c\u6012\u54c0\u4e50\u4e0d\u5f62\u4e8e\u8272\uff0c\u8fd9\u65f6\u5374\u4e3a\u829d\u5a1c\u4e4b\u6b7b\uff0c\u52a8\u4e86\u771f\u60c5\uff0c\u53f9\u9053\uff1a\u201c\u829d\u5a1c\u4ee5\u524d\u66fe \u6c42\u6211\u6307\u70b9\u4f60\u7684\u6b66\u529f\uff0c\u90a3\u65f6\u4f60\u8fd8\u6709\u62dc\u94c1\u62d0\u4ed9\u4e3a\u5e08\uff0c\u5979\u5f88\u53ef\u60dc\u4f60\u5177\u6709\u4e0a\u4f73\u7684\u8d44\u8d28\uff0c\u5374\u6ca1\u6709\u7b2c\u4e00\u6d41 \u5e08\u7236\u3002\u6240\u4ee5\u6c42\u6211\u770b\u5728\u5979\u7684\u60c5\u4efd\u4e0a\uff0c\u4f20\u4f60\u81ea\u4fee\u4e0a\u4e58\u6b66\u529f\u7684\u5fc3\u6cd5\uff0c\u5f53\u65f6\u6211\u6ca1\u6709\u7b54\u5e94\u3002\u60f3\u4e0d\u5230\u540e\u6765 \u51b0\u5cf0\u5012\u584c\uff0c\u673a\u7f18\u5076\u5408\uff0c\u4f60\u65e0\u610f\u4e2d\u670d\u4e86\u6211\u5bab\u4e2d\u7684\u6731\u679c\uff0c\u4e0d\u987b\u4fee\u4e60\uff0c\u5df2\u5f97\u4e86\u6211\u6d3e\u4e0a\u4e58\u7684\u8f7b\u529f\uff0c\u5b66 \u4e86\u6211\u672c\u95e8\u7684\u5251\u6cd5\uff0c\u8fd9\u662f\u5929\u610f\uff0c\u6211\u4e0d\u602a\u4f60\u3002\u4f46\u4f60\u867d\u5b66\u4e86\u6211\u7684\u5251\u6cd5\uff0c\u5374\u8fd8\u672a\u5f97\u5230\u6211\u7684\u5251\u8bc0\u3002\u73b0\u5728 \u829d\u5a1c\u4e0d\u5e78\u800c\u6b7b\uff0c\u6211\u5e94\u52a9\u5979\u5b8c\u6210\u5fc3\u613f\uff0c\u5c06\u5251\u8bc0\u4f20\u6388\u7ed9\u4f60\u3002\u53ea\u662f\u4f60\u6211\u5e74\u7eaa\u76f8\u82e5\uff0c\u6211\u4e0d\u80fd\u505a\u4f60\u7684\u5e08 \u7236\u3002\u597d\u5728\u5e7d\u840d\u968f\u6211\u591a\u5e74\uff0c\u867d\u7136\u672a\u5f97\u5b66\u5168\u6211\u7684\u5251\u6cd5\uff0c\u5374\u61c2\u5f97\u6211\u7684\u5251\u8882\uff0c\u6211\u51c6\u8bb8\u5e7d\u840d\u5c06\u5251\u8bc0\u5f99\u4f20 \u7ed9\u4f60\u3002\u201d\u9648\u5929\u5b87\u4e00\u5411\u56e0\u4e3a\u672a\u5f97\u51b0\u5ddd\u5929\u5973\u540c\u610f\uff0c\u800c\u5077\u5b66\u5979\u7684\u5251\u6cd5\uff0c\u803f\u803f\u4e8e\u5fc3\uff0c\u800c\u4eca\u975e\u4f46\u5f97\u5230\u51b0 \u5ddd\u5929\u5973\u8c05\u89e3\uff0c\u800c\u4e14\u7b54\u5141\u8fde\u5251\u8bc0\u4e5f\u53ef\u4ee4\u5e7d\u840d\u4ee3\u4f20\u7ed9\u4ed6\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u5f53\u5373\u62dc\u8c22\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7565\u4fa7\u534a\u8eab\uff0c\u53d7\u4e86\u9648\u5929\u5b87\u7684\u534a\u62dc\u4e4b\u793c\uff0c\u63a5\u7740\u95ee\u9053\uff1a\u201c\u5510\u7ecf\u5929\u662f\u5426\u5728\u4f60\u7684\u5bb6\u4e2d\uff1f\u201d \u9648\u5927\u5b57\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u5c31\u662f\u542c\u5510\u5927\u4fa0\u7684\u5dee\u9063\uff0c\u60f3\u5230\u62c9\u8428\u53bb\u8bf7\u6551\u5175\u7684\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u798f\u5eb7\u5b89\u90a3\u513f\u6211\u5df2\u53bb\u8fc7\u5566\uff0c\u4f60\u4e0d\u7528\u518d\u53bb\u4e86\u3002\u201d\u9648\u5929\u5b87\u5341\u5206\u60ca\u8be7\uff0c\u6b63\u60f3\u53d1\u95ee\uff0c\u51b0\u5ddd\u5929\u5973\u53c8 \u9053\uff1a\u201c\u91d1\u4e16\u9057\u5462\uff1f\u55ef\uff0c\u4f60\u8fd8\u6ca1\u6709\u89c1\u8fc7\u91d1\u4e16\u9057\uff0c\u4e0d\u8fc7\u5510\u7ecf\u5929\u5411\u4f60\u8bf4\u8fc7\u8fd9\u4eba\u6ca1\u6709\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u91d1\u4e16\u9057\u5230\u6211\u7684\u5bb6\u4e2d\uff0c\u6211\u867d\u7136\u6ca1\u89c1\u7740\u4ed6\uff0c\u4ed6\u5374\u6697\u4e2d\u6551\u4e86\u6211\u7684\u4e00\u547d\u3002\u201d\u51b0\u5ddd\u5929\u5973\u8be7\u9053\uff1a\u201c\u91d1\u4e16 \u9057\u4e0e\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4ed6\u4f1a\u6551\u4f60\u6027\u547d\uff1f\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u9648\u5929\u5b57\u5c06\u4e8b\u60c5\u7ecf\u8fc7\u8bf4\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u91d1\u4e16\u9057\u4e43\u662f\u53bb\u89c1\u6cd5\u738b \u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6050\u6015\u65e9\u89c1\u7740\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u53bb\u7684\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u5927\u7ea6\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u968f\u7740\u90a3\u4e24\u4e2a\u767d\u6559\u5587\u561b\uff0c\u4ece\u6211\u5bb6\u4e2d\u52a8\u8eab\u7684\u3002\u82e5\u7136\u6cd5\u4e3b\u4e0d\u5c06\u4ed6\u7acb\u5373\u4ea4\u7ed9\u4fc4\u9a6c \u767b\uff0c\u73b0\u5728\u5e94\u5f53\u8fd8\u5728\u5587\u561b\u5bfa\u4e2d\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u5e7d\u840d\uff0c\u6211\u65e9\u8bf4\u8fc7\uff0c\u91d1\u4e16\u9057\u6b64\u4eba\u867d\u7136\u60f9\u4eba\u8ba8\u538c\uff0c\u5185\u5fc3\u8fd8\u6709\u826f\u5584 \u4e4b\u6027\u3002\u4ed6\u80af\u6551\u4eba\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u80fd\u6551\u4ed6\uff0c\u4f60\u548c\u5929\u5b87\u5148\u56de\u53bb\u544a\u8bc9\u5510\u7ecf\u5927\uff0c\u6211\u73b0\u5728\u53bb\u89c1\u6cd5\u738b\u4e00 \u906d\u3002\u201d\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u7acb\u523b\u4fbf\u8d70\u3002\u5e7d\u8c37\u4e4b\u4e2d\uff0c\u9042\u53ea\u5269\u5e7d\u840d\u4e0e\u9648\u5929\u5b87\u4e24\u4eba\u76f8\u5bf9\uff0c\u9648\u5929\u5b57\u7a81\u7136\u60f3\u8d77\u4e86 \u829d\u5a1c\u4e34\u6b7b\u4e4b\u524d\u6240\u8bf4\u7684\u8bdd\uff0c\u5bf9\u7740\u5e7d\u840d\uff0c\u9ed8\u9ed8\u65e0\u8a00\u3002 \u3000\u3000\u5e7d\u840d\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u829d\u5a1c\u4e0e\u6211\u60c5\u540c\u59d0\u59b9\uff0c\u6211\u4f55\u5c1d\u4e0d\u4f24\u5fc3\u5462\uff1f\u4f46\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c \u56e0\u5979\u800c\u6b7b\u6240\u8d77\u7684\u98ce\u6ce2\uff0c\u6211\u4eec\u82e5\u4e0d\u4e3a\u5979\u8bbe\u6cd5\u6d88\u9975\uff0c\u5979\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u5c82\u80fd\u5b89\u5fc3\uff1f\u201d\u8f7b\u8f7b\u63e1\u7740\u9648\u5929 \u5b87\u7684\u624b\uff0c\u6e29\u8a00\u76f8\u6170\u3002\u5e7d\u840d\u6240\u8bf4\u7684\u8bdd\uff0c\u610f\u601d\u4e0e\u5510\u7ecf\u5929\u4e00\u6837\uff0c\u9648\u5929\u5b87\u542c\u8fdb\u8033\u91cc\uff0c\u5374\u662f\u66f4\u4e3a\u611f\u52a8\uff0c \u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e4b\u8981\u53bb\u62c9\u8428\uff0c\u5c31\u4e3a\u7684\u662f\u6d88\u9975\u8fd9\u573a\u98ce\u6ce2\u3002\u55ef\uff0c\u662f\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u521a\u624d\u8bf4\u5df2 \u89c1\u8fc7\u798f\u5b89\u5eb7\uff0c\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u5587\u561b\u5bfa\u4e3e\u884c\u5f00\u5149\u5927\u5178\u7684\u90a3\u4e00\u5929\uff0c\u6211\u4eec\u4e5f\u5230\u8428\u8fe6\u3002\u5f53\u65e5\u4e4b\u4e8b\uff0c\u6211\u4eec\u90fd\u77e5\u9053\u4e86\u3002 \u4e0d\u8fc7\uff0c\u4f60\u4eec\u6ca1\u89c1\u7740\u6211\u4eec\u7f62\u4e86\u3002\u6211\u4eec\u516c\u4e3b\u65e9\u5df2\u6599\u5230\u6709\u8fd9\u98ce\u6ce2\uff0c\u6240\u4ee5\u6765\u4e0d\u53ca\u53bb\u627e\u4ed6\u4eec\uff0c\u5c31\u5148\u53bb\u89c1 \u798f\u5eb7\u5b89\u3002\u5979\u66fe\u7ecf\u4e3a\u798f\u5eb7\u5b89\u51fa\u8fc7\u5927\u529b\uff0c\u4fdd\u62a4\u91d1\u74f6\uff0c\u798f\u5eb7\u5b89\u5f88\u76f8\u4fe1\u5979\uff0c\u4e00\u8bf4\u4e4b\u4e0b\uff0c\u4fbf\u7b54\u5141\u51fa\u5175\uff0c \u770b\u6765\u5728\u5370\u5ea6\u5175\u672a\u8e0f\u5165\u85cf\u5883\u4e4b\u524d\uff0c\u5c31\u5c06\u4ed6\u4eec\u622a\u4f4f\u3002\u201d\u9648\u5929\u5b87\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u51b0\u5ddd\u5929\u5973\u4e4b\u6240\u4ee5\u8fdf \u8fdf\u963b\u5230\u6765\uff0c\u4e43\u662f\u53bb\u4e86\u62c9\u8428\u3002\u5510\u7ecf\u5929\u7a7a\u81ea\u62c5\u4e86\u4e00\u573a\u5fc3\u4e8b\u3002 \u3000\u3000\u4e24\u4eba\u6b63\u5728\u5a13\u5a13\u800c\u8c08\uff0c\u5ffd\u7136\u542c\u5f97\u4fc4\u9a6c\u767b\u7684\u547b\u541f\uff0c\u9648\u5929\u5b87\u6068\u6068\u9053\uff1a\u201c\u90fd\u662f\u4fc4\u9a6c\u767b\u8fd9\u53ae\u6363\u7684 \u9b3c\uff01\u201d\u5e7d\u840d\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u73b0\u5728\u53bb\u5bf9\u4ed8\u4ed6\u3002\u201d\u4fc4\u9a6c\u767b\u4e2d\u4e86\u51b0\u9b44\u795e\u5f39\uff0c\u51b7\u5165\u9aa8\u9ad3\uff0c\u7259\u5173\u6253\u6218\uff0c \u5df2\u662f\u4e0d\u80fd\u8bdd\uff0c\u5e7d\u840d\u53eb\u9648\u5929\u5b87\u6309\u7740\u4ed6\u80cc\u5fc3\u7684\u4e24\u9053\u5927\u7a74\uff0c\u66ff\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u6682\u65f6\u51cf\u5f31\u4ed6\u4f53\u4e2d\u7684\u51b7 \u6c14\uff0c\u4fc4\u9a6c\u767b\u98a4\u6296\u8bf4\u9053\uff1a\u201c\u9648\u516c\u5b50\uff0c\u4f60\u4e0d\u770b\u50e7\u9762\u770b\u4f5b\u9762\uff0c\u770b\u5728\u829d\u5a1c\u7684\u4efd\u4e0a\uff0c\u4f60\u5e94\u8be5\u9976\u6211\u4e00 \u547d\u3002\u201d\u9648\u5929\u5b87\u6012\u9053\uff1a\u201c\u8bf4\u829d\u5a1c\u8fd8\u53ef\uff0c\u8bf4\u8d77\u829d\u5a1c\u6211\u66f4\u8981\u53d6\u4f60\u7684\u72d7\u547d\u3002\u201d\u4fc4\u9a6c\u767b\u9053\uff1a\u201c\u6211\u5bf9\u829d \u5a1c\uff0c\u53ef\u662f\u4e00\u7247\u597d\u5fc3\uff0c\u4ee5\u524d\u5979\u7b2c\u4e00\u6b21\u88ab\u571f\u53f8\u902e\u7740\u4e4b\u65f6\uff0c\u6211\u66fe\u6c42\u4ee4\u5c0a\u6c42\u60c5\uff0c\u4eca\u6b21\u5979\u884c\u523a\u571f\u53f8\uff0c\u6211 \u4e5f\u6709\u6697\u4e2d\u76f8\u52a9\u3002\u8fd9\u4e9b\u90fd\u662f\u4e8b\u5b9e\uff0c\u516c\u4e3b\uff0c\u4f60\u5c82\u6709\u4e0d\u77e5\uff1f\u201d\u5e7d\u840d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u6211\u4eec\u8fd8\u4e0d\u77e5\u9053\u4f60 \u7684\u5e95\u7ec6\u5417\uff1f\u4f60\u662f\u5370\u5ea6\u5580\u6797\u90a6\u571f\u738b\u7684\u5978\u7ec6\uff0c\u4f60\u552f\u6050\u897f\u85cf\u4e0d\u4e71\uff0c\u610f\u6b32\u52fe\u7ed3\u5916\u4eba\uff0c\u7edf\u4e00\u897f\u85cf\uff0c\u81ea\u7acb \u4e3a\u897f\u85cf\u738b\u3002\u8fd9\u597d\u8c0b\u7792\u5f97\u8fc7\u571f\u53f8\uff0c\u53ef\u7792\u4e0d\u8fc7\u6211\u4eec\u7684\u516c\u4e3b\u3002\u4f60\u6697\u52a9\u829d\u5a1c\u59d0\u59d0\u523a\u6740\u571f\u53f8\uff0c\u4e0d\u8fc7\u662f\u501f \u5200\u6740\u4eba\u4e4b\u8ba1\u7f62\u4e86\u3002\u201d \u3000\u3000\u5e7d\u840d\u6b64\u8bed\u4e00\u51fa\uff0c\u4fc4\u9a6c\u767b\u56fa\u7136\u662f\u5927\u4e3a\u5403\u60ca\uff0c\u8eab\u8eaf\u66f4\u662f\u98a4\u6296\uff0c\u5373\u4fbf\u9648\u5929\u5b57\u4ea6\u9887\u89c9\u610f\u5916\uff0c\u6b63\u60f3 \u63a2\u95ee\u5e7d\u840d\uff0c\u51b0\u5ddd\u5929\u5973\u4f55\u4ee5\u4f1a\u77e5\u9053\u4fc4\u9a6c\u767b\u7684\u5978\u8c0b\uff0c\u5ffd\u89c1\u5bf9\u9762\u5c71\u5761\u706b\u5149\u6643\u52a8\uff0c\u4eba\u5f71\u7c07\u7c07\uff0c\u5728\u524d\u884c \u7684\u51e0\u4e2a\u4eba\u4e2d\uff0c\u8ba4\u5f97\u51fa\u5176\u4e2d\u4e00\u4e2a\u662f\u5370\u5ea6\u82e6\u884c\u50e7\uff0c\u9648\u5929\u5b9a\u9053\uff1a\u201c\u60f3\u662f\u82e6\u884c\u618e\u56de\u53bb\u6c42\u6551\uff0c\u9080\u96c6\u4e86\u5821 \u4e2d\u6240\u6709\u7684\u597d\u624b\uff0c\u6765\u4e0e\u54b1\u4eec\u4e3a\u96be\u3002\u201d\u5e7d\u840d\u9053\uff1a\u201c\u54b1\u4eec\u8d76\u5feb\u7ed5\u8def\u907f\u5f00\uff0c\u4f60\u5728\u5bb6\u4e2d\u7b49\u5019\u516c\u4e3b\u3002\u201d\u9648 \u5929\u5b87\u5ffd\u9053\uff1a\u201c\u82e6\u884c\u50e7\u8c03\u96c6\u597d\u624b\u524d\u6765\uff0c\u5821\u4e2d\u5fc5\u7136\u7a7a\u865a\u3002\u54b1\u4eec\u6b63\u597d\u4e58\u673a\u5077\u88ad\u4ed6\u4eec\u7684\u8001\u5de2\uff01\u201d\u5e7d\u840d \u9053\uff1a\u201c\u4f55\u987b\u5982\u6b64\u5192\u9669\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u600e\u5fcd\u89c1\u829d\u5a1c\u7684\u9057\u4f53\uff0c\u4e00\u76f4\u88ab\u6446\u5728\u5979\u654c\u4eba\u7684\u57ce\u5821 \u4e2d\uff1f\u201d\u63d0\u8d77\u5251\u4fbf\u60f3\u6740\u4fc4\u9a6c\u767b\uff0c\u5e7d\u840d\u9053\uff1a\u201c\u7559\u4e0b\u6d3b\u53e3\uff0c\u8fd8\u6709\u7528\u5904\u3002\u201d\u4f38\u624b\u628a\u4fc4\u9a6c\u767b\u7684\u5634\u5df4\u4e00\u634f\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u88ab\u5979\u7528\u529b\u4e00\u634f\uff0c\u5634\u5df4\u5f20\u5f00\uff0c\u5e7d\u840d\u53cc\u6307\u4e00\u5f39\uff0c\u5c06\u4e24\u7c92\u51b0\u9b44\u795e\u5f39\u5f39\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u786c\u751f \u751f\u7684\u8feb\u4ed6\u54bd\u4e86\u4e0b\u53bb\u3002\u51b0\u9b44\u795e\u5f39\u542b\u6709\u5e7d\u8c37\u7384\u51b0\u7684\u4e98\u53e4\u5947\u5bd2\u4e4b\u6c14\uff0c\u6253\u4e2d\u5916\u9762\u7684\u76ae\u80a4\u5df2\u662f\u4e0d\u5f97\u4e86\uff0c \u4f55\u51b5\u54bd\u5165\u809a\u4e2d\uff1f\u4fc4\u9a6c\u767b\u53cc\u773c\u7ffb\u767d\uff0c\u5468\u8eab\u76ae\u80a4\u90fd\u8d77\u7599\u7629\uff0c\u767b\u65f6\u4e0d\u7701\u4eba\u4e8b\u3002\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u9664\u4e86\u516c \u4e3b\u548c\u6211\uff0c\u4e16\u4e0a\u65e0\u4eba\u518d\u80fd\u5c06\u4ed6\u6551\u9192\u3002\u597d\uff0c\u54b1\u4eec\u53ef\u4ee5\u653e\u5fc3\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u5077\u5077\u4ece\u5c71\u80cc\u722c\u4e0a\uff0c\u4e24\u519b\u5728\u524d\u9762\u5bf9\u5cd9\uff0c\u540e\u5c71\u53ea\u6709\u5de1\u903b\u6b65\u54e8\uff1b\u5730\u6674\u5929 \u660f\uff0c\u7adf\u662f\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u7ed9\u4ed6\u4eec\u5077\u5077\u6e9c\u5165\u4e86\u571f\u53f8\u7684\u57ce\u5821\u3002 \u3000\u3000\u4e24\u4eba\u7ed5\u4e86\u4e00\u5708\uff0c\u89c1\u4e1c\u5317\u89d2\u4e0a\u4e00\u95f4\u7cbe\u96c5\u7684\u623f\u95f4\uff0c\u5185\u6709\u7ea2\u706f\u63a9\u6620\uff0c\u7a97\u7eb1\u4e0a\u51fa\u4e24\u4e2a\u5973\u4eba\u7684\u5f71 \u5b50\uff0c\u5e7d\u840d\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fc7\u53bb\u770b\u770b\u3002\u201d\u9648\u5929\u5b87\u72b9\u7591\u8bf4\u9053\uff1a\u201c\u4f55\u5fc5\u53bb\u60f9\u5979\uff1f\u201d\u5e7d\u840d\u9053\uff1a \u201c\u597d\uff0c\u5979\u662f\u8c01\u5475\uff1f\u201d\u9648\u5927\u5b57\u9053\uff1a\u201c\u5979\u662f\u571f\u53f8\u7684\u5973\u513f\u2014\u2014\u6851\u58c1\u4f0a\u3002\u201d\u5e7d\u840d\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60 \u6015\u5979\u4e48\uff1f\u522b\u6015\uff0c\u522b\u6015\uff0c\u6709\u6211\u4fdd\u67b6\u3002\u201d\u5c06\u9648\u5929\u5b87\u4e00\u62c9\uff0c\u62c9\u5230\u4e86\u78a7\u7eb1\u7a97\u4e0b\u3002 \u3000\u3000\u623f\u4e2d\u679c\u7136\u662f\u6851\u58c1\u4f0a\u6bcd\u5973\u4e8c\u4eba\uff0c\u53ea\u542c\u5f97\u6851\u58c1\u4f0a\u7684\u6bcd\u4eb2\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u6599\u4e0d\u5230\u4e8b \u60c5\u95f9\u5f97\u8fd9\u4e48\u5927\uff0c\u6211\u53ea\u6015\u4f60\u7236\u4eb2\u7684\u57fa\u4e1a\u4f1a\u65ad\u9001\u5728\u4fc4\u9a6c\u767b\u7684\u624b\u4e2d\uff01\u201d\u6851\u58c1\u4f0a\u9053\uff1a\u201c\u6211\u4e00\u5411\u8ba8\u538c\u4fc4 \u9a6c\u767b\uff0c\u4f60\u504f\u542c\u4ed6\u7684\u8bdd\u3002\u201d\u5979\u6bcd\u4eb2\u9053\uff1a\u201c\u6211\u600e\u77e5\u9053\u4ed6\u7adf\u6562\u5982\u6b64\u5305\u85cf\u7978\u5fc3\uff1f\u4ed6\u53e3\u53e3\u58f0\u58f0\u8bf4\u8981\u66ff\u4f60 \u7236\u4eb2\u62a5\u4ec7\uff0c\u6211\u600e\u62e6\u963b\u5f97\u4e86\u3002\u201d\u6851\u58c1\u4f0a\u9053\uff1a\u201c\u597d\u5728\u5929\u5b87\u6ca1\u6709\u88ab\u4ed6\u62ff\u53bb\u3002\u201d\u5979\u6bcd\u4eb2\u9053\uff1a\u201c\u513f\u5475\uff0c \u4f60\u8fd8\u5728\u60f3\u5ff5\u5929\u5b87\u5417\uff1f\u201d\u9648\u5929\u5b87\u5fc3\u4e2d\u4e00\u8df3\u3002\u6851\u58c1\u4f0a\u8f7b\u8f7b\u4e00\u7b11\uff0c\u5374\u6ca1\u6709\u8bf4\u8bdd\u3002\u5979\u6bcd\u4eb2\u53c8\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u4e8b\u60c5\u95f9\u5230\u8fd9\u822c\u5730\u6b65\uff0c\u54b1\u4eec\u8fd8\u597d\u610f\u601d\u548c\u9648\u5bb6\u8ba4\u4eb2\uff1f\u201d \u3000\u3000\u6851\u58c1\u4f0a\u5ffd\u9053\uff1a\u201c\u6211\u628a\u4fc4\u9a6c\u767b\u7f1a\u4e86\u8d77\u6765\uff0c\u9001\u5230\u5ba3\u6170\u4f7f\u8859\u95e8\u53bb\u8bf7\u7f6a\u5982\u4f55\uff1f\u201d\u6bcd\u4eb2\u6025\u5fd9\u4e00\u624b\u63a9 \u4f4f\u4e86\u5973\u513f\u5634\u5df4\uff0c\u9053\uff1a\u201c\u513f\u554a\uff0c\u8fd9\u8bdd\u4e07\u4e0d\u80fd\u4e71\u8bf4\u3002\u73b0\u5728\u5175\u6743\u90fd\u64cd\u5728\u4fc4\u9a6c\u767b\u624b\u4e2d\uff0c\u4ed6\u82e5\u8981\u5bb3\u6211\u4eec \u5be1\u5987\u5b64\u513f\uff0c\u90a3\u662f\u6613\u5982\u53cd\u638c\uff01\u201d\u6851\u58c1\u4f0a\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u770b\u4ed6\u4e0d\u4ec5\u662f\u8981\u7be1\u593a\u8428\u8fe6\u7684\u6743\u4f4d\uff0c \u8fd8\u60f3\u505a\u85cf\u738b\u5462\u3002\u201d\u5979\u6bcd\u4eb2\u9053\uff0c\u201c\u6b63\u662f\u5440\u3002\u6211\u73b0\u5728\u624d\u77e5\u9053\uff0c\u4f60\u7236\u4eb2\u51fa\u4e8b\u4e4b\u524d\uff0c\u4ed6\u5df2\u6d3e\u4eba\u5077\u5077\u53bb \u5370\u5ea6\u4e0e\u5c3c\u6cca\u5c14\u8bf7\u5175\u4e86\u3002\u201d\u6851\u58c1\u4f0a\u9053\uff1a\u201c\u6015\u4ed6\u7ec8\u4e0d\u662f\u529e\u6cd5\uff0c\uff0c\u5f97\u60f3\u4e2a\u6cd5\u5b50\u5bf9\u4ed8\u4ed6\u3002\u5988\uff0c\u4f60\u4e3a\u4f55 \u4e0d\u4e0e\u8fbe\u8d56\u73ed\u7985\u90a3\u4e24\u4f4d\u6d3b\u4f5b\u7684\u4ee3\u8868\u8bf4\u53bb\uff1f\u201d\u6bcd\u4eb2\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u4ee3\u8868\u53ea\u6015\u81ea\u8eab\u4e5f\u96be\u4fdd\u5168\uff0c\u6211\uff0c\u6211 \u600e\u6562\u548c\u4ed6\u4eec\u8bf4\u53bb\uff1f\u201d\u6851\u58c1\u4f0a\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u96be\u9053\u4fc4\u9a6c\u767b\u8fd8\u6562\u4f24\u5bb3\u4ed6\u4eec\u5417\uff1f\u201d\u6bcd\u4eb2\u7684 \u597d\u534a\u664c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5973\u513f\u9053\uff1a\u201c\u5988\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u6851\u58c1\u4f0a\u7684\u6bcd\u4eb2\u7a81\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u63a8\u5f00\u7a97\u5b50\u4e00 \u671b\uff0c\u5e7d\u840d\u4e0e\u9648\u5929\u5b87\u65e9\u8eb2\u5728\u5c71\u77f3\u540e\u9762\uff0c\u5979\u6ca1\u6709\u770b\u5230\u4eba\u8ff9\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u8fd9\u624d\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u513f\u5440\uff0c \u6211\u65b9\u5bf8\u5df1\u4e71\uff0c\u6b63\u8981\u548c\u4f60\u5546\u91cf\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5927\u6743\u65c1\u843d\u5982\u4f55\u5904\uff1f\u6101\u715e\u5bab\u4e2d\u6851\u58c1\u4f0a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u5757\u5792\u96be\u5e73\u3000\u4f24\u5fc3\u8bdd\u6545\u56fd\u3000\u72c2\u6b4c\u5f53\u54ed\u3000\u5931\u610f\u8d70\u5929\u6daf \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u56de \u5757\u5792\u96be\u5e73\u3000\u4f24\u5fc3\u8bdd\u6545\u56fd \u72c2\u6b4c\u5f53\u54ed\u3000\u5931\u610f\u8d70\u5929\u6daf \u3000\u3000\u6851\u58c1\u4f0a\u9053\uff1a\u201c\u5988\uff0c\u4f60\u8bf4\u3002\u201d\u571f\u53f8\u592b\u4eba\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u771f\u7684\u60f3\u6740\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\uff01\u201d\u6851\u58c1\u4f0a \u5927\u4e3a\u9707\u60ca\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u571f\u53f8\u592b\u4eba\u9053\uff1a\u201c\u73ed\u63b8\u6d3b\u4f5b\u7684\u4ee3\u8868\u90a3\u65e5\u88ab\u5973\u8d3c\u8bef\u4f24\uff0c\u80cc\u4e0a\u4e2d\u4e86\u4e00\u628a\u98de\u5200\uff0c\u5e78\u4e8f\u6ca1\u6709\u81f4\u547d\u3002 \u53ef\u662f\u8fd9\u4e8b\u60c5\u975e\u540c\u5c0f\u53ef\uff0c\u4fc4\u9a6c\u767b\u4fbf\u85c9\u6b64\u60f3\u5229\u7528\u6d3b\u4f5b\u7684\u4ee3\u8868\uff0c\u8bf7\u4ed6\u4eec\u8f6c\u5448\u8fbe\u8d56\u73ed\u63b8\u4e24\u4f4d\u6d3b\u4f5b\uff0c\u628a \u4e8b\u60c5\u7275\u6d89\u81f3\u767d\u6559\u6cd5\u738b\u8eab\u4e0a\uff0c\u8bf7\u8fbe\u8d56\u73ed\u7985\u51fa\u9762\uff0c\u5c06\u767d\u6559\u5587\u561b\u518d\u9010\u51fa\u897f\u85cf\u3002\u201d \u3000\u3000\u6851\u58c1\u4f0a\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6211\u4e5f\u542c\u5230\u4e00\u70b9\u98ce\u58f0\u3002\u201d\u571f\u53f8\u592b\u4eba\u7eed\u9053\uff1a\u201c\u5e78\u4e8f\u4e24\u4f4d\u6d3b\u4f5b\u7684\u4ee3\u8868\uff0c\u505a \u4e8b\u614e\u91cd\uff0c\u53ea\u5c06\u5f53\u65e5\u7684\u7ecf\u8fc7\u4f9d\u5b9e\u7980\u62a5\u4e0a\u53bb\uff0c\u5374\u6ca1\u6709\u8bf7\u8fbe\u8d56\u73ed\u7985\u9a71\u9010\u767d\u6559\u6cd5\u738b\u3002\u4fc4\u9a6c\u767b\u65e5\u65e5\u6311\u62e8 \u717d\u52a8\uff0c\u73ed\u63b8\u6d3b\u4f5b\u7684\u4ee3\u8868\u8981\u6c42\u5148\u89c1\u767d\u6559\u6cd5\u738b\u8c08\u8c08\uff0c\u610f\u601d\u662f\u60f3\u67e5\u660e\u4e8b\u5b9e\u7684\u771f\u76f8\u3002\u4fc4\u9a6c\u767b\u600e\u80af\u8ba9\u4ed6 \u4eec\u89c1\u6cd5\u738b\uff1f\u6697\u4e2d\u6307\u4f7f\u66ff\u4ed6\u4e3b\u6cbb\u7684\u533b\u5e08\u4e0b\u836f\uff0c\u4ee4\u5f97\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u7684\u5200\u4f24\u975e\u4f46\u4e0d\u80fd\u6cbb\u6108\uff0c\u800c\u4e14 \u65e5\u89c1\u4e25\u91cd\u3002\u4fc4\u9a6c\u767b\u5c31\u63a8\u8bf4\u4ed6\u75c5\u91cd\uff0c\u4e0d\u5b9c\u89c1\u5ba2\uff0c\u5c06\u4e24\u4f4d\u6d3b\u4f5b\u7684\u4ee3\u8868\u4e0e\u5916\u95f4\u9694\u7edd\u4e86\u3002\u5728\u8fd9\u5176\u95ee\u4ed6 \u4ecd\u662f\u65e5\u65e5\u50ac\u4fc3\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u5199\u4fe1\u7980\u62a5\u6d3b\u4f5b\uff0c\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u66f4\u662f\u8d77\u7591\uff0c\u575a\u51b3\u4e0d\u80af\u7167\u4ed6\u7684\u610f \u601d\u5199\u4fe1\u3002\u4fc4\u9a6c\u767b\u6ca1\u6cd5\uff0c\u7d22\u6027\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u53eb\u90a3\u4e2a\u533b\u5e08\u4e0b\u6bd2\uff0c\u9650\u4ee4\u5728\u4eca\u665a\u4e09\u66f4\u4e4b\u524d\u7ed3\u675f\u73ed\u63b8 \u6d3b\u4f5b\u4ee3\u8868\u7684\u6027\u547d\u3002\u4eba\u4eba\u90fd\u77e5\u9053\u73ed\u63b8\u6d3b\u4f5b\u7684\u4ee3\u8868\u662f\u7ed9\u5973\u8d3c\u523a\u4f24\u7684\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u81ea\u7136\u4ee5\u4e3a\u4ed6\u662f\u56e0 \u4f24\u800c\u6b7b\uff0c\u65ad\u65e0\u4eba\u7591\u81f3\u4fc4\u9a6c\u767b\u8eab\u4e0a\u3002\u4fc4\u9a6c\u767b\u4ee5\u4e3a\u5982\u6b64\u4e00\u6765\uff0c\u4fbf\u53ef\u523a\u6fc0\u73ed\u7985\u6d3b\u4f5b\uff0c\u8fbe\u5230\u76ee\u7684\u3002\u201d \u3000\u3000\u6851\u58c1\u4f0a\u60ca\u9053\uff1a\u201c\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u82e5\u7136\u5728\u54b1\u4eec\u8fd9\u513f\u6b7b\u53bb\uff0c\u53ea\u6015\u6574\u4e2a\u8428\u8fe6\u7684\u50e7\u4fd7\u5b98\u90fd\u8981\u53d7\u6d3b \u4f5b\u964d\u7f6a\u3002\u201d\u571f\u53f8\u7684\u592b\u4eba\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u56e0\u6b64\u533b\u5e08\u4e0d\u6562\u4e0b\u624b\uff0c\u53ef\u662f\u4ed6\u53c8\u5bb3\u6015\u4fc4\u9a6c\u767b\u6740\u4ed6\uff0c\u6545 \u6b64\u5077\u5077\u544a\u8bc9\u6211\uff0c\u6c42\u6211\u66ff\u4ed6\u4f5c\u4e3b\uff0c\u53ef\u662f\u6211\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u54b1\u4eec\u7684\u6027\u547d\u90fd\u634f\u5728\u4fc4\u9a6c\u767b\u624b\u4e0a\u3002\u201d\u6851 \u58c1\u4f0a\u9053\uff1a\u201c\u54b1\u4eec\u548c\u4ed6\u62fc\u4e86\uff01\u201d\u5979\u6bcd\u4eb2\u82e6\u7b11\u9053\uff1a\u201c\u62fc\u5f97\u8fc7\u4e48\uff1f\u8fd9\u662f\u4ee5\u5375\u51fb\u77f3\uff01\u201d \u3000\u3000\u6851\u58c1\u4f0a\u6012\u9053\uff1a\u201c\u83ab\u4e0d\u6210\u773c\u7741\u7741\u5730\u8ba9\u4ed6\u60f9\u6765\u5927\u7978\uff1f\u201d\u4e24\u6bcd\u5973\u6101\u5bb9\u76f8\u5bf9\uff0c\u6beb\u65e0\u529e\u6cd5\uff0c\u5ffd\u5730\u7a97 \u95e8\u201c\u5440\u201d\u7684\u4e00\u58f0\u7ed9\u4eba\u4ece\u5916\u9762\u63a8\u5f00\uff0c\u6851\u58c1\u4f0a\u62d4\u51fa\u4f69\u5200\uff0c\u6b63\u5f85\u559d\u95ee\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6781\u719f\u6089\u7684\u58f0\u97f3\u53eb \u9053\uff1a\u201c\u662f\u6211\uff01\u201d\u6851\u58c1\u4f0a\u51e0\u4e4e\u7591\u662f\u68a6\u4e2d\uff0c\u8df3\u8fdb\u6765\u7684\u4eba\u7adf\u7136\u662f\u9648\u5929\u5b87\uff0c\u6851\u58c1\u4f0a\u60f3\u8df3\u4e0a\u53bb\u62b1\u4ed6\uff0c\u773c \u6ce2\u4e00\u8f6c\uff0c\u53ea\u89c1\u9648\u5929\u5b87\u540e\u9762\u8fd8\u8ddf\u7740\u4e00\u4f4d\u5c11\u5973\uff0c\u6851\u58c1\u4f0a\u9000\u540e\u4e24\u6b65\uff0c\u5446\u5446\u5730\u671b\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u6851\u58c1\u4f0a\uff0c\u4f60\u4fe1\u4e0d\u4fe1\u6211\uff1f\u201d\u6851\u58c1\u4f0a\u4ece\u672a\u66fe\u542c\u8fc7\u9648\u5929\u5b87\u7528\u5982\u6b64\u7684\u53e3\u6c14\u5411\u5979\u8bf4 \u8bdd\uff0c\u559c\u4e0d\u81ea\u80dc\u5730\u70b9\u4e86\u70b9\u5934\u3002\u9648\u5929\u5b87\u9053\uff1a\u5b50\u4fc4\u9a6c\u767b\u5df2\u7ed9\u6211\u4eec\u5236\u4f4f\u4e86\u3002\u4f60\u4eec\u4e00\u70b9\u4e5f\u4e0d\u7528\u5bb3\u6015\u3002\u201d \u6851\u58c1\u4f0a\u6bcd\u5973\u6709\u5982\u7edd\u5904\u9022\u751f\u7684\u4eba\uff0c\u72c2\u559c\u5f97\u8bf4\u4e0d\u51fa\u6d3b\u3002\u9648\u5929\u5b87\u9053\uff1a\u201c\u4e0d\u8fc7\u4f60\u4eec\u4e0d\u5fc5\u963b\u6320\u90a3\u4e2a\u533b \u5e08\uff0c\u8ba9\u4ed6\u53bb\u8c0b\u6740\u73ed\u63b8\u6d3b\u4f5b\u7684\u4ee3\u8868\u3002\u201d\u6851\u58c1\u4f0a\u60ca\u53eb\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u65f6\u95f4\u8feb\u901f\uff0c \u4e8b\u540e\u518d\u8bf4\u7ed9\u4f60\u77e5\u3002\u73b0\u5728\u8bf7\u4f60\u9a6c\u4e0a\u544a\u8bc9\u6211\uff0c\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u6851\u58c1\u4f0a\u7684\u6bcd\u4eb2\u5230\u5e95\u662f\u7ecf\u8fc7\u5927\u98ce\u5927\u6d6a\u7684\u571f\u53f8\u592b\u4eba\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u7acb\u523b\u660e\u767d\u4e86\u4ed6\u4eec\u7684\u7528\u610f\uff0c\u8bf4 \u9053\uff1a\u201c\u597d\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u4f60\u4eec\u5feb\u53bb\u3002\u6d3b\u4f5b\u7684\u4ee3\u8868\u5728\u897f\u9762\u90a3\u4e2a\u5c16\u5854\u4e0a\u7684\u7b2c\u4e8c\u5c42\u3002\u201d\u9648\u5929\u5b87\u62c9\u7740\u5e7d \u840d\u7acb\u523b\u4fbf\u8d70\uff0c\u6851\u58c1\u4f0a\u5fc3\u601d\u4e0d\u5b9a\uff0c\u60f3\u8ffd\u51fa\u53bb\uff0c\u53c8\u505c\u5728\u95e8\u8fb9\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u2018\u5988\uff0c\u4ed6\u4eec\u662f\u505a\u4ec0 \u4e48\uff1f\u201d\u5979\u6bcd\u4eb2\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u60f3\u5f53\u7740\u6d3b\u4f5b\u4ee3\u8868\u7684\u9762\u6233\u7834\u4fc4\u9a6c\u767b\u7684\u9634\u8c0b\u3002\u5439\u5fe0\uff08\u5deb\u5e08\u3002\u5e38\u517c\u4f5c\u533b \u5e08\uff0c\u5c31\u662f\u571f\u53f8\u592b\u4eba\u5e08\u8bf4\u7684\u66ff\u6d3b\u4f5b\u4ee3\u8868\u4e3b\u6cbb\u7684\u90a3\u4f4d\u533b\u5e08\u3002\uff09\u53ea\u6015\u8fd8\u8981\u6765\u89c1\u6211\uff0c\u4f60\u56de\u623f\u53bb\u5427\u3002\u201d \u6851\u58c1\u4f0a\u9053\uff1a\u201c\u6211\u4e0d\u662f\u95ee\u8fd9\u4e2a\u3002\u201d\u5979\u6bcd\u4eb2\u9053\uff1a\u201c\u90a3\u4f60\u95ee\u4ec0\u4e48\uff1f\u201d\u6851\u58c1\u4f0a\u773c\u5708\u4e00\u7ea2\uff0c\u5ffd\u7136\u4f4e\u4f4e\u5730 \u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u4e2a\u513f\u8d70\u51fa\u95e8\u5916\u53bb\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u9002\u624d\u5df2\u63a2\u660e\u4e86\u571f\u53f8\u5821\u4e2d\u7684\u8def\u9053\uff0c\u5f88\u5feb\u4fbf\u5bfb\u5230\u897f\u9762\u90a3\u4e2a\u5c16\u5854\uff0c\u5c16\u5854\u4e00\u5171\u4e09 \u5c42\uff0c\u897f\u85cf\u738b\u516c\u8d35\u65cf\uff0c\u5bb6\u4e2d\u4e00\u822c\u90fd\u9020\u6709\u8fd9\u79cd\u5f0f\u6837\u7684\u201c\u795e\u5854\u201d\uff0c\u9759\u60a0\u60a0\u7684\uff0c\u82e5\u975e\u4ed6\u4eec\u5f97\u5230\u571f\u53f8\u592b \u4eba\u6307\u70b9\uff0c\u771f\u4e0d\u77e5\u8fd9\u91cc\u9762\u4f9b\u7684\u7adf\u7136\u662f\u4e00\u5c0a\u201c\u6d3b\u4f5b\u201d\u7684\u66ff\u8eab\u3002\u9648\u5929\u5b87\u4e00\u7eb5\u6570\u4e08\uff0c\u98de\u9e1f\u822c\u5730\u4e0a\u4e86\u7b2c \u4e8c\u5c42\uff0c\u5e7d\u840d\u8f7b\u529f\u8f83\u900a\uff0c\u8df3\u4e0d\u5f97\u90a3\u4e48\u9ad8\uff0c\u624b\u6309\u98de\u6a90\uff0c\u501f\u4e00\u501f\u529b\uff0c\u624d\u7ffb\u4e0a\u53bb\uff0c\u5c31\u53ea\u662f\u8fd9\u4e00\u70b9\u70b9\u58f0 \u606f\uff0c\u5728\u4e0a\u9762\u7729\u671b\u7684\u4eba\u5df2\u63a2\u5934\u6765\uff0c\u5e7d\u840d\u673a\u8b66\u4e4b\u6781\uff0c\u4e0d\u5f85\u4ed6\u4eec\u51fa\u58f0\uff0c\u5c31\u7528\u4e24\u679a\u51b0\u9b44\u795e\u5f39\u6253\u4e2d\u4e86\u4ed6 \u4eec\u7684\u54d1\u7a74\u3002\u9ed1\u591c\u4e4b\u4e2d\u8ba4\u7a74\u5982\u6b64\u4e4b\u51c6\uff0c\u9648\u5929\u5b87\u4e5f\u6697\u53f9\u4e0d\u5982\uff0c\u5fc3\u9053\uff1a\u201c\u679c\u7136\u4e0d\u6127\u662f\u51b0\u5bab\u4f8d\u5973\u4e2d\u9996 \u5c48\u4e00\u6307\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u623f\u4e2d\u6709\u76cf\u6cb9\u706f\uff0c\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u6b63\u8eba\u5728\u69bb\u4e0a\u8f97\u8f6c\u53cd\u4fa7\uff0c\u53d1\u51fa\u4f4e\u4f4e\u7684\u547b\u541f\u58f0\uff0c\u4e00\u89c1\u4ed6\u4eec\u8fdb \u6765\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u4e00\u9aa8\u788c\u5730\u5750\u8d77\u6765\u3002\u5e7d\u840d\u9053\uff1a\u201c\u6211\u662f\u5949\u6d3b\u4f5b\u4e4b\u547d\u6765\u63a2\u671b\u4f60\u7684\u3002\u201d\u8d70\u8fd1\u524d\u53bb\uff0c\u9732 \u51fa\u80f8\u524d\u6240\u4f69\u7684\u4e00\u9053\u7075\u7b26\u3002\u539f\u6765\u51b0\u5ddd\u5929\u5973\u4e0e\u5e7d\u840d\u5230\u62c9\u8428\u4e4b\u65f6\uff0c\u51b0\u5ddd\u5929\u5973\u4ee5\u4f5b\u95e8\u4e4b\u5973\u62a4\u6cd5\u7684\u8eab \u4efd\uff0c\u7684\u786e\u53bb\u62dc\u8bbf\u8fc7\u8fbe\u8d56\u6d3b\u4f5b\uff0c\u5e7d\u840d\u90a3\u9053\u7075\u7b26\uff0c\u5c31\u662f\u8fbe\u8d56\u6240\u8d50\u3002\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u5c06\u4fe1\u5c06\u7591\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u8fbe\u8d56\u6d3b\u4f5b\u600e\u4f1a\u77e5\u6211\u5728\u6b64\u7f79\u96be\uff1f\u201d\u8fbe\u8d56\u73ed\u7985\u5206\u5c45\u524d\u85cf\u540e\u85cf\uff0c\u8ddd\u79bb\u9887\u8fdc\uff0c\u4ee5\u65e5\u7a0b\u63a8 \u7b97\uff0c\u73ed\u7985\u7eb5\u5df2\u63a5\u5230\u4ed6\u4f7f\u8005\u7684\u7980\u62a5\uff0c\u4e5f\u4e0d\u80fd\u5373\u65f6\u901a\u77e5\u8fbe\u8d56\u3002\u4f46\u73ed\u7985\u7684\u4ee3\u8868\u89c1\u5e7d\u840d\u4f69\u6709\u8fbe\u8d56\u7684\u7075 \u7b26\uff0c\u867d\u6709\u7591\u5fc3\uff0c\u5374\u4e5f\u4e0d\u6562\u5f20\u626c\u53eb\u558a\u3002 \u3000\u3000\u5e7d\u840d\u5c31\u6b63\u662f\u8981\u4ed6\u4e0d\u53eb\u4e0d\u558a\uff0c\u5254\u4eae\u6cb9\u706f\uff0c\u5f20\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u7247\u7ea2\u80bf\uff0c\u6e83\u70c2\u4e0d\u582a\uff0c\u5fc3\u4e2d\u6697\u6068 \u4fc4\u9a6c\u767b\u7684\u72e0\u6bd2\uff0c\u7acb\u523b\u53d6\u51fa\u4e00\u679a\u4e39\u836f\uff0c\u7528\u8336\u6c34\u5316\u4e86\uff0c\u6d82\u5728\u4f24\u53e3\u4e0a\uff0c\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u501a\u4ed7\u4f5b\u529b\uff0c\u901f \u6108\u6b64\u4f24\u3002\u201d\u51b0\u5bab\u4e2d\u7684\u7075\u4e39\u5999\u836f\uff0c\u975e\u540c\u51e1\u54c1\uff0c\u4f55\u51b5\u8fd9\u53ea\u662f\u5916\u8868\u7684\u5200\u4f24\uff0c\u4e00\u6577\u4e0a\u53bb\uff0c\u4f24\u8005\u7acb\u611f\u6c81 \u51c9\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u75db\u695a\u82e5\u5931\u3002 \u3000\u3000\u73ed\u63b8\u7684\u4ee3\u8868\u8fd9\u65f6\u518d\u4e5f\u6ca1\u6709\u7591\u5fc3\uff0c\u5408\u4ec0\u8bf5\u4f5b\uff0c\u7136\u540e\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1a\u6765\u65f6\u6ca1\u6709\u60ca\u52a8 \u4eba\u5417\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u6211\u4eec\u5c31\u662f\u4e3a\u4e86\u6551\u4f60\u7ebd\u3002\u4fc4\u9a6c\u767b\u5df2\u7ed9\u6211\u4eec\u5236\u4f4f\u4e86\uff0c\u4ed6\u7684\u624b\u4e0b\u8fd8\u6ca1\u77e5\u9053\u3002\u7b49 \u4f1a\u6709\u4eba\u7ed9\u4f60\u5403\u836f\uff0c\u4f60\u4e0d\u8981\u5403\uff01\u201d\u4e00\u8bf4\u5b8c\u8bdd\uff0c\u7acb\u523b\u4e0e\u9648\u5929\u5b87\u9690\u8eab\u5728\u5c4b\u4e2d\u7738\u50cf\u4e4b\u540e\uff0c\u73ed\u7985\u7684\u4ee3\u8868 \u83ab\u540d\u5176\u5999\uff0c\u4e0d\u4f4f\u7684\u4f4e\u58f0\u5ff5\u4f5b\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6709\u811a\u6b65\u58f0\u4ece\u5916\u9762\u8d70\u8fdb\u6765\uff0c\u73ed\u60ee\u7684\u4ee3\u8868\u95ee\u9053\uff1a\u201c\u5439\u5fe0\u600e\u4e48\u4e0d\u6765\uff1f\u201d\u6765\u7684\u4eba\u662f\u5439 \u5fe0\u7684\u52a9\u624b\uff0c\u539f\u6765\u90a3\u4e2a\u62c5\u4efb\u4e3b\u6cbb\u533b\u5e08\u7684\u52a9\u624b\uff0c\u5fc3\u4e2d\u5bb3\u6015\uff0c\u4e0d\u6562\u4eb2\u81ea\u6bd2\u6740\u201c\u6d3b\u4f5b\u201d\u7684\u66ff\u8eab\u3002\u6545\u6b64 \u914d\u4e86\u6bd2\u836f\u540e\uff0c\u5374\u53eb\u52a9\u624b\u7aef\u6765\uff0c\u52a9\u624b\u4e5f\u4e0d\u77e5\u9053\u7897\u4e2d\u76db\u7684\u4e43\u662f\u6bd2\u836f\u3002\u52a9\u624b\u7aef\u7740\u836f\u7897\u606d\u606d\u656c\u656c\u7684\u8bf4 \u9053\uff0c\u201c\u5439\u5fe0\u6709\u4e8b\uff0c\u53eb\u6211\u6765\u4f8d\u5019\u6d3b\u4f5b\u3002\u201d\u8bdd\u58f0\u672a\u5b8c\uff0c\u5e7d\u840d\u5ffd\u5730\u8df3\u4e86\u51fa\u6765\uff0c\u4f38\u624b\u4e00\u634f\uff0c\u52a9\u624b\u201c\u5475 \u5440\u201d\u53eb\u4e86\u51fa\u6765\uff0c\u5e7d\u840d\u8d81\u52bf\u593a\u8fc7\u836f\u7897\uff0c\u5f80\u4ed6\u53e3\u4e2d\u4e00\u5012\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4ed6\u9762\u8272\u7531\u7ea2\u8f6c\u767d\uff0c\u53c8\u7531\u767d\u53d8 \u4e3a\u7600\u9ed1\uff0c\u53ef\u601c\u8fd9\u4e2a\u52a9\u624b\uff0c\u7cca\u91cc\u7cca\u6d82\u5730\u5c31\u9001\u4e86\u4e00\u6761\u6027\u547d\u3002\u73ed\u7985\u7684\u4ee3\u8868\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u597d\u72e0 \u6bd2\u7684\u4fc4\u9a6c\u767b\uff01\u201d\u4e0d\u7531\u5f97\u5fc3\u4e2d\u51db\u60e7\uff0c\u5bf9\u5e7d\u840d\u9053\uff1a\u201c\u6211\u660e\u767d\u5566\uff0c\u53ef\u662f\u8fd9\u4e00\u6765\uff0c\u54b1\u4eec\u4e0e\u4ed6\u4eec\u4e5f\u6495\u7834 \u9762\u4e86\uff0c\u600e\u751f\u51fa\u5f97\u57ce\u5821\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u4e0d\u7528\u60e7\u6015\uff0c\u6211\u4eec\u4fdd\u4f60\u51fa\u53bb\u3002\u201d\u8fd9\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u5916\u9762\u4eba\u58f0 \u7eb7\u81f3\u3002\u9648\u5b57\u62d4\u51fa\u957f\u5251\uff0c\u5f00\u95e8\u4e00\u770b\uff0c\u53ea\u89c1\u5916\u9762\u5f71\u5f71\u7ef0\u7ef0\u7684\u5927\u7ea6\u6709\u56db\u4e94\u4e2a\u4eba\uff0c\u5f53\u5148\u7684\u7adf\u662f\u90a3\u4e2a\u5370 \u5ea6\u82e6\u884c\u50e7\uff0c\u6700\u540e\u9762\u7684\u662f\u4ed6\u7684\u5e08\u4f84\u5fb7\u9c81\u5947\uff0c\u62b1\u7740\u50f5\u786c\u51b0\u51b7\u7684\u4fc4\u9a6c\u767b\uff0c\u8fd8\u6709\u4e24\u4e2a\u4eba\u662f\u4fc4\u9a6c\u767b\u7684\u4eb2 \u4fe1\u6b66\u58eb\u3002\u4ed6\u4eec\u672c\u6765\u662f\u96c6\u5728\u4e00\u8d77\uff0c\u60f3\u53bb\u56f4\u653b\u51b0\u5ddd\u5929\u5973\u7684\uff0c\u60f3\u4e0d\u5230\u6ca1\u89c1\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u5374\u5bfb\u7740\u4e86\u4fc4 \u9a6c\u767b\u3002\u8fd9\u4e00\u4e0b\uff0c\u4ed6\u4eec\u81ea\u7136\u7acb\u5373\u731c\u5230\u5821\u4e2d\u6709\u4e8b\uff0c\u6240\u4ee5\u8d76\u4e86\u56de\u6765\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u82e6\u884c\u50e7\u89c1\u51b0\u5ddd\u5929\u5973\u4e0d\u5728\u5176\u5185\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4eec\u662f\u5403\u4e86\u8c79\u5b50\u7684\u5fc3 \u72ee\u5b50\u7684\u80c6\uff1f\u7adf\u6562\u52ab\u6301\u6d3b\u4f5b\u6765\u4e86\uff01\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u8bf4\uff0c\u5feb\u53eb\u4fc4\u9a6c\u767b\u524d\u6765\u9886\u7f6a\uff01\u201d\u4fc4\u9a6c\u767b \u7684\u4eb2\u4fe1\u6b66\u58eb\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u7528\u7684\u4ec0\u4e48\u5996\u6cd5\u5bb3\u6b7b\u4e86\u5927\u6d85\u5df4\uff1f\u82e5\u4e0d\u7acb\u5373\u5c06\u4ed6\u6551\u9192\uff0c\u8981\u4f60\u8fd9\u53cc \u5996\u7537\u5996\u5973\u7684\u6027\u547d\uff01\u201d\u62a1\u5200\u52a8\u65a7\uff0c\u7acb\u523b\u780d\u8fdb\u623f\u4e2d\u3002\u9648\u5929\u5b87\u9053\uff0c\u201c\u840d\u59b9\uff0c\u4f60\u4fdd\u62a4\u6d3b\u4f5b\u4ee3\u8868\u3002\u201d\u5c55 \u5f00\u957f\u5251\uff0c\u53ee\u5f53\u4e24\u58f0\uff0c\u5c06\u4e24\u4e2a\u5200\u65a7\u624b\u6321\u4e86\u56de\u53bb\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u82e6\u884c\u618e\uff0c\u5de6\u624b\u4e3e\u7af9\u6756\uff0c\u53f3\u624b\u4e3e\u76c2\u94b5\uff0c\u563f\u563f\u51b7\u7b11\uff0c\u53ea\u7b49\u9648\u5929\u5b87\u4e00\u51b2\u51fa\u6765\uff0c\u5c31\u8981\u5f53\u5934 \u7f69\u4e0b\u3002\u9648\u5929\u5b87\u4e0d\u60e7\u5821\u4e2d\u7684\u6b66\u58eb\uff0c\u5374\u4e0d\u80fd\u4e0d\u60e7\u8fd9\u4e2a\u5370\u5ea6\u82e6\u884c\u50e7\uff0c\u5fc3\u4e2d\u81ea\u77e5\u5e05\u5df1\u4e0e\u5e7d\u840d\u8054\u624b\u4e4b \u529b\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u591f\u4e0e\u8fd9\u82e6\u884c\u50e7\u76f8\u6297\uff0c\u4f55\u51b5\u53e6\u5916\u8fd8\u6709\u90a3\u4e48\u591a\u654c\u4eba\u3002\u770b\u6765\u4eca\u665a\u90a3\u662f\u4e07\u96be\u9003\u8131\u7684 \u4e86\uff01\u90a3\u5370\u5ea6\u82e6\u884c\u50e7\u89c1\u9648\u5929\u5b87\u4e0d\u6562\u51b2\u51fa\uff0c\u8d8a\u53d1\u5f97\u610f\uff0c\u563f\u563f\u51b7\u7b11\uff0c\u7d22\u6027\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u8fdb\u623f\u6765\uff0c\u76c2 \u94b5\u4e00\u7ffb\uff0c\u500f\u5730\u5c06\u9648\u5929\u5b87\u7684\u957f\u5251\u7f69\u4f4f\uff01 \u3000\u3000\u91d1\u4e16\u9057\u4e0e\u767d\u6559\u6cd5\u738b\u5728\u9759\u5ba4\u5bf9\u638c\uff0c\u767d\u6559\u6cd5\u738b\u628a\u91d1\u4e16\u9057\u8feb\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u6b63\u62df\u4f5c\u6700\u540e\u7684\u4e00\u51fb\uff0c \u91d1\u4e16\u9057\u4e5f\u628a\u6bd2\u9488\u5410\u5230\u4e86\u53e3\u8fb9\uff0c\u8981\u4e0e\u767d\u6559\u6cd5\u738b\u540c\u5f52\u4e8e\u5c3d\u3002\u5c31\u5728\u4e8e\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5a07 \u547c\uff0c\u91d1\u4e16\u9057\u7684\u6bd2\u9488\u521a\u521a\u5410\u51fa\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5931\u4e86\u51c6\u5934\uff0c\u88ab\u767d\u6559\u6cd5\u738b\u5c55\u8896\u62c2\u843d\uff0c\u800c\u767d\u6559\u6cd5\u738b\u5206\u4e86 \u5206\u795e\uff0c\u8fd9\u4e00\u638c\u63a8\u51fa\u4e5f\u51cf\u4e86\u4e94\u6210\u529b\u91cf\uff0c\u91d1\u4e16\u9057\u867d\u7136\u88ab\u4ed6\u4e00\u638c\u63a8\u5012\uff0c\u5185\u810f\u5374\u6ca1\u6709\u53d7\u4f24\uff0c\u5728\u5730\u4e0a\u6253 \u4e86\u4e2a\u6eda\uff0c\u53c8\u8df3\u8d77\u6765\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e0e\u6cd5\u738b\u5bf9\u638c\uff0c\u4e43\u662f\u4ed6\u51fa\u9053\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u4e0e\u5f3a\u654c\u4ee5\u5168\u529b\u76f8\u62fc\uff0c\u5fc3\u795e\u8d2f\u6ce8\uff0c\u8fde\u51b0\u5ddd\u5927 \u5973\u8fdb\u6765\u90fd\u4e0d\u77e5\u9053\u3002\u8fd9\u65f6\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8df3\u8d77\u6765\u65f6\uff0c\u7a81\u7136\u89c1\u5230\u4ed6\u6240\u503e\u6155\u8fc7\u53c8\u6028\u6068\u8fc7\u7684\u51b0\u5ddd\u5929\u5973 \u7b11\u76c8\u76c8\u5730\u7ad9\u5728\u9762\u524d\uff0c\u4e0d\u7981\u201c\u5475\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\u3002\u5634\u5df4\u4e00\u5f20\uff0c\u5ffd\u89c9\u4e00\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u76f4\u900f\u4eba \u4f53\u5185\uff0c\u539f\u6765\u662f\u51b0\u5ddd\u5929\u5973\u7389\u6307\u4e00\u5f39\uff0c\u5c06\u4e24\u679a\u795e\u5f39\u9001\u5165\u4e86\u4ed6\u7684\u53e3\u4e2d\uff1a \u3000\u3000\u91d1\u4e16\u9057\u9002\u624d\u88ab\u6cd5\u738b\u7684\u638c\u529b\u76f8\u8feb\uff0c\u4f53\u70ed\u5982\u711a\uff0c\u7126\u6e34\u4e4b\u6781\uff0c\u7a81\u7136\u5f97\u5230\u51b0\u9b44\u795e\u5f39\u9001\u4eba\u53e3\u4e2d\uff0c\u771f \u5982\u5728\u6c99\u6f20\u4e0a\u7684\u65c5\u4eba\uff0c\u5f97\u5230\u4ece\u5929\u800c\u964d\u7684\u7518\u9732\u3002\u53ea\u89c9\u904d\u4f53\u6c81\u51c9\uff0c\u5fc3\u5934\u90a3\u80a1\u706b\u70ed\u4e4b\u6c14\u4e5f\u7acb\u65f6\u6d88\u6563 \u4e86\u3002\u91d1\u4e16\u9057\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u7acb\u523b\u660e\u767d\u4e86\u662f\u51b0\u5ddd\u5929\u5973\u7528\u201c\u4ee5\u6bd2\u653b\u6bd2\u201d\u7684\u65b9\u6cd5\u6551 \u4e86\u81ea\u5df1\uff0c\u8981\u4e0d\u7136\u81ea\u5df1\u867d\u7136\u4fa5\u5e78\u80fd\u591f\u8131\u8eab\uff0c\u4e0d\u81f3\u4e8e\u6bd9\u5728\u6cd5\u738b\u638c\u4e0b\uff0c\u4f46\u5185\u706b\u70e7\u8eab\uff0c\u91cd\u8005\u5168\u8eab\u762b \u75ea\uff0c\u8f7b\u8005\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u91d1\u4e16\u9057\u795e\u601d\u660f\u660f\uff0c\u5fc3\u4e2d\u6df7\u4e71\u4e4b\u6781\uff0c\u4ed6\u6b64\u6765\u672c\u662f\u4e0e\u5510\u7ecf\u5929\u8d4c\u4e00\u53e3\u6c14\uff0c\u5374\u60f3\u4e0d\u5230 \u51e0\u4e4e\u9001\u547d\uff0c\u60e8\u8d25\u7684\u60c5\u5f62\u504f\u504f\u7ed9\u51b0\u5ddd\u5929\u5973\u89c1\u5230\uff0c\u800c\u4e14\u8fd8\u662f\u5979\u6551\u4e86\u81ea\u5df1\u7684\u6027\u547d\uff0c\u6027\u547d\u4e0d\u8db3\u60dc\uff0c\u81ea \u5c0a\u5fc3\u7684\u53d7\u632b\uff0c\u5374\u4ee4\u91d1\u4e16\u9057\u5927\u611f\u96be\u8fc7\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8fd9\u4e0e\u4f17\u4e0d\u540c\u7684\u5947\u602a\u5fc3\u601d\uff0c\u51b0\u5ddd\u5929\u5973\u54ea\u80fd\u731c\u5230\uff0c\u89c1\u4ed6\u7f13\u8fc7\u6c14\u6765\uff0c\u7f13\u7f13\u8d70\u8fd1\uff0c\u5fae\u7b11\u95ee \u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u6ca1\u53d7\u4f24\u5427\uff1f\u55ef\uff0c\u4f60\u89c1\u5230\u5510\u7ecf\u5929\u6ca1\u6709\uff0c\u6211\u548c\u4f60\u4e00\u540c\u8d70\u5427\uff0c\u95ee\u4ed6\u8ba8\u51e0\u9897\u78a7\u7075\u4e39 \u53bb\u3002\u5415\u56db\u5a18\u8bf4\u4f60\u7684\u5185\u529f\u7ec3\u5f97\u4e0d\u5f53\uff0c\u53ea\u6709\u5929\u5c71\u96ea\u83b2\u5236\u70bc\u7684\u78a7\u7075\u4e39\u65b9\u80fd\u7ed9\u4f60\u6682\u4fdd\u771f\u5143\u3002\u201d\u51b0\u5ddd\u5929 \u5973\u7684\u58f0\u97f3\u6e29\u67d4\u4e4b\u6781\uff0c\u91d1\u4e16\u9057\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u8fd9\u6837\u201c\u4f53\u8d34\u201d\u7684\u8bdd\uff0c\u82e5\u5728\u5f80\u65f6\uff0c\u4ed6\u542c\u5230\u51b0\u5ddd\u5929\u5973\u8fd9 \u6837\u6e29\u67d4\uff0c\u4e0d\u77e5\u8be5\u6709\u591a\u5c11\u9ad8\u5174\uff0c\u800c\u4eca\u542c\u6765\uff0c\u5374\u5982\u4e07\u7bad\u94bb\u5fc3\uff0c\u6e29\u67d4\u53d8\u6210\u4e86\u8ba5\u523a\uff0c\u4f53\u8d34\u53d8\u6210\u4e86\u6316 \u82e6\u3002\u91d1\u4e16\u9057\u7a81\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u98de\u8eab\u4fbf\u8d70\uff0c\u51b0\u5ddd\u5929\u5973\u8ffd\u51fa\u95e8\u5916\uff0c\u53ea\u89c1\u4ed6\u5df2\u4e0a\u4e86\u5c4b\u9876\uff0c\u6295\u63b7\u4e0b\u6765\u7684 \u662f\u4e00\u7247\u51b0\u51b7\u6028\u6124\u7684\u773c\u5149\uff0c\u6cd5\u738b\u5728\u5185\uff0c\u4e8e\u7406\u4e8e\u60c5\uff0c\u51b0\u5ddd\u5929\u5973\u90fd\u4e0d\u80fd\u4e22\u5f00\u6cd5\u738b\u53bb\u8ffd\u8e2a\u91d1\u4e16\u9057\u3002\u51b0 \u5ddd\u5929\u5973\u53ea\u5f97\u53f9\u4e86\u53e3\u6c14\uff0c\u56de\u8f6c\u8eab\u6765\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u771f\u662f\u65e0\u53ef\u7406\u55bb\uff01\u201d\u201c\u771f\u662f\u65e0\u53ef\u7406\u55bb\uff01\u201d\u6cd5\u738b \u4e5f\u6447\u4e86\u6447\u5934\uff0c\u968f\u5373\u5411\u51b0\u5ddd\u5929\u5973\u5408\u4ec0\uff0c\u7b11\u9053\uff1a\u201c\u9002\u624d\u8fd9\u4f4d\u5e74\u8f7b\u4eba\u662f\u5973\u62a4\u6cd5\u7684\u76f8\u8bc6\u5417\uff1f\u201d\u51b0\u5ddd\u5929 \u5973\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u89c1\u8fc7\u51e0\u6b21\u9762\u7684\u670b\u53cb\uff0c\u4ed6\u5982\u6b64\u5192\u72af\u6d3b\u4f5b\uff0c\u6211\u5fc3\u4e2d\u4e5f\u5b9e\u5728\u4e0d\u5b89\u3002\u201d\u6cd5\u738b\u5fae\u7b11\u9053\uff1a \u201c\u5982\u6b64\u5e74\u7eaa\uff0c\u5982\u6b64\u6b66\u529f\uff0c\u4e5f\u786e\u7b97\u5f97\u662f\u4eba\u6240\u5c11\u6709\u3002\u5e78\u4e8f\u5973\u62a4\u6cd5\u524d\u6765\uff0c\u8981\u4e0d\u7136\u53ea\u6015\u6211\u8981\u4e0e\u4ed6\u540c\u5f52 \u4e8e\u5c3d\u3002\u201d\u51b0\u5ddd\u5929\u5973\u968f\u7740\u6cd5\u738b\u7684\u773c\u5149\u770b\u53bb\uff0c\u53ea\u89c1\u91d1\u4e16\u9057\u55b7\u51fa\u7684\u90a3\u53e3\u6bd2\u9488\uff0c\u63d2\u5728\u7406\u77f3\u7684\u5730\u7816\u4e0a\uff0c \u5468\u56f4\u4e5f\u9ed1\u4e86\u4e00\u7247\u3002\u4e0d\u89c9\u9a87\u7136\uff01 \u3000\u3000\u5728\u9752\u6d77\u4e4b\u65f6\uff0c\u51b0\u5ddd\u5929\u5973\u66fe\u7ecf\u505a\u8fc7\u767d\u6559\u6cd5\u738b\u7684\u4e0a\u5bbe\uff0c\u8fd9\u56de\u76f8\u89c1\u751a\u89c9\u6b22\u6b23\uff0c\u6cd5\u738b\u8bf7\u5979\u5750\u4e0b\uff0c \u547d\u5f1f\u5b50\u5949\u4e0a\u9999\u8336\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u773c\u5149\uff0c\u5374\u6ce8\u89c6\u7740\u8d70\u5ed3\u5185\u4e00\u5e45\u58c1\u753b\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u5fae\u7b11\u9053\uff1a\u201c\u5973\u62a4\u6cd5\u559c\u6b22\u8fd9\u5e45\u58c1\u753b\u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u8d70\u51fa\uff0c\u7ad9 \u5728\u58c1\u753b\u4e4b\u4e0b\uff0c\u5b9a\u775b\u51dd\u89c6\uff0c\u9762\u4e0a\u6d41\u9732\u51fa\u5947\u5f02\u7684\u5149\u8f89\uff0c\u767d\u6559\u6cd5\u738b\u9053\uff1a\u201c\u8fd9\u5e45\u753b\u540d\u53eb\u300a\u516b\u601d\u5df4\u671d\u89d0 \u5ffd\u5fc5\u70c8\u53bb\u8499\u53e4\u300b\u3002\u753b\u4e2d\u4ed5\u5973\u4eba\u7269\uff0c\u9a86\u9a7c\u725b\u7f8a\uff0c\u90fd\u6829\u6829\u5982\u751f\uff0c\u8349\u539f\u98ce\u5149\uff0c\u6f20\u5317\u60c5\u8c03\uff0c\u51e0\u4e4e\u8981\u6d6e \u51fa\u753b\u9762\u3002\u786e\u662f\u4e00\u5e45\u7f8e\u5999\u7684\u58c1\u753b\u3002\u201d\u6cd5\u738b\u6b63\u5728\u53e3\u8bb2\u6307\u5212\uff0c\u66ff\u51b0\u5ddd\u5929\u5973\u89e3\u91ca\u8fd9\u5e45\u58c1\u753b\uff0c\u773c\u5149\u5ffd\u5730 \u505c\u5728\u753b\u4e2d\u4e00\u4e2a\u5c11\u5973\u7684\u9762\u4e0a\uff0c\u4e5f\u4e0d\u7981\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u5947\u602a\u8d77\u6765\u3002\u6cd5\u7f69\u4e8b\u5fd9\uff0c\u4ee5\u524d\u5bf9\u5bab\u4e2d\u7684\u58c1\u753b \u6ca1\u6709\u4ed4\u7ec6\u7559\u610f\uff0c\u8fd9\u65f6\u624d\u770b\u51fa\u4e86\u753b\u4e2d\u90a3\u4e2a\u7a7f\u7740\u5c3c\u6cca\u5c14\u8d35\u65cf\u5987\u5973\u670d\u9970\u7684\u5c11\u5973\uff0c\u9762\u8c8c\u7adf\u7136\u6709\u51e0\u5206\u76f8 \u4f3c\u51b0\u5ddd\u5929\u5973\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u753b\u8fd9\u5e45\u753b\u7684\u753b\u5de5\u8fd8\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u767d\u6559\u6cd5\u738b\u9053\uff1a\u201c\u753b\u5de5\u662f\u4ee5\u524d\u7684 \u571f\u53f8\u4ece\u62c9\u8428\u8bf7\u6765\u7684\uff0c\u8fd9\u5ea7\u5587\u561b\u5bab\u8fd8\u6709\u82e5\u5e72\u58c1\u753b\u5c1a\u672a\u753b\u597d\uff0c\u753b\u5de5\u672a\u66fe\u9063\u6563\uff0c\u6211\u53eb\u4eba\u66ff\u4f60\u67e5 \u67e5\u3002\u201d\u7acb\u523b\u5c06\u4e00\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u5524\u6765\uff0c\u53eb\u4ed6\u53bb\u67e5\u660e\u662f\u54ea\u4e00\u4e2a\u753b\u5de5\u6240\u753b\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u966a\u51b0\u5ddd\u5929\u5973\u8bf4\u8bdd\uff0c\u51b0\u5ddd\u5929\u5973\u5c06\u5979\u8d76\u5f80\u62c9\u8428\u8c03\u505c\u7684\u7ecf\u8fc7\u8bf4\u4e0e\u6cd5\u738b\u77e5\u9053\u3002\u6cd5\u738b\u95fb\u5f97 \u5979\u4e0e\u8fbe\u8d56\u6d3b\u4f5b\u4ee5\u53ca\u6e05\u5ef7\u7684\u9a7b\u85cf\u5927\u81e3\u798f\u5eb7\u5b89\u90fd\u89c1\u8fc7\u9762\uff0c\u798f\u5eb7\u5b89\u5e76\u5df2\u7b54\u5e94\u51fa\u5175\u53bb\u622a\u5370\u5ea6\u5580\u6797\u90a6\u7684 \u519b\u961f\uff0c\u800c\u8fbe\u8d56\u6d3b\u4f5b\u4e5f\u77e5\u9053\u4e86\u4fc4\u9a6c\u767b\u7684\u9634\u8c0b\uff0c\u540c\u610f\u767d\u6559\u6cd5\u738b\u5728\u8428\u8fce\u5730\u533a\u6709\u6700\u9ad8\u65e0\u4e0a\u7684\u6559\u6743\uff0c\u8428 \u8fce\u7684\u4e8b\u60c5\uff0c\u4fbf\u7531\u4ed6\u5168\u6743\u5904\u7406\uff0c\u6cd5\u738b\u5927\u559c\uff0c\u5411\u51b0\u5ddd\u5929\u5973\u8c22\u9053\uff1a\u201c\u591a\u4e8f\u5973\u62a4\u6cd5\u4ee5\u7edd\u5927\u795e\u901a\uff0c\u6d88\u9975 \u4e86\u8fd9\u573a\u5f25\u5929\u5927\u7978\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90a3\u662f\u4ef0\u4ed7\u51e0\u4f4d\u6d3b\u4f5b\u60b2\u5929\u60af\u4eba\u7684\u6148\u60b2\uff0c\u5927\u5bb6\u90fd\u4e0d\u613f\u6311\u8d77\u6218 \u4e71\uff0c\u8fd9\u624d\u5f97\u4ee5\u548c\u5e73\u89e3\u51b3\u3002\u6211\u4e0d\u8fc7\u7a0d\u5c3d\u5954\u8d70\u4e4b\u52b3\uff0c\u6709\u4f55\u529f\u5fb7\u53ef\u4ee5\u79f0\u9053\uff1f\u76ee\u4e0b\u4fc4\u9a6c\u767b\u7684\u4eb2\u5175\u5c1a\u5728 \u548c\u6d1b\u73e0\u7684\u519b\u961f\u5bf9\u5cd9\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u4e14\u5148\u5e73\u5b9a\u4e86\u8fd9\u573a\u4e71\u4e8b\u5427\u3002\u201d\u6cd5\u738b\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u8fd9\u53ae\uff0c\u6211 \u65e9\u5c31\u60f3\u5c06\u4ed6\u62ff\u6765\u6cd5\u529e\u4e86\uff0c\u4ee5\u524d\u53ea\u56e0\u788d\u4e8e\u9ec4\u6559\u7684\u9762\u5b50\uff0c\u6211\u8fdc\u6765\u662f\u5ba2\uff0c\u4e0d\u4fbf\u55a7\u5bbe\u593a\u4e3b\uff0c\u73b0\u65e2\u627f\u8fbe \u8d56\u6d3b\u4f5b\u59d4\u4ee5\u5168\u6743\uff0c\u4fc4\u9a6c\u767b\u6709\u591a\u5927\u80fd\u4e3a\uff0c\u4e5f\u9003\u4e0d\u8131\u6211\u7684\u638c\u5fc3\u3002\u201d\u7acb\u523b\u4e0b\u4ee4\u51c6\u5907\u6cd5\u9a7e\u4eea\u4ed7\uff0c\u8981\u8fde \u591c\u5230\u571f\u53f8\u5821\u4e2d\u53bb\u5e73\u5b9a\u8fd9\u573a\u4e71\u4e8b\u3002 \u3000\u3000\u62a4\u6cd5\u5f1f\u5b50\u5206\u5934\u884c\u4e8b\uff0c\u4e0d\u5230\u4e00\u523b\uff0c\u53bb\u8bbf\u67e5\u753b\u5de5\u7684\u5927\u5f1f\u5b50\u56de\u6765\u62a5\u9053\uff1a\u201c\u90a3\u5e45\u58c1\u753b\u662f\u4e00\u4e2a\u5c3c\u6cca \u5c14\u7684\u753b\u5de5\u753b\u7684\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u62a4\u6cd5\u5f1f\u5b50\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u8981\u89c1\u5230\u5973\u62a4 \u6cd5\u624d\u8bf4\u3002\u201d\u51b0\u5929\u5973\u5947\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u77e5\u9053\u6211\u5728\u6b64\u95f4\uff1f\u662f\u4f60\u5411\u4ed6\u8bf4\u6211\u8981\u67e5\u95ee\u8fd9\u5e45\u7684\u5417\uff1f\u201d\u62a4\u6cd5\u5f1f \u5b50\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8bf4\u3002\u8fd9\u753b\u5de5\u4e00\u542c\u6211\u95ee\uff0c\u4fbf\u9053\uff1a\u2018\u662f\u51b0\u5a25\u5c0f\u516c\u4e3b\u6765\u4e86\uff0c\u5426\u5219\u65e0\u4eba\u4f1a\u6765\u95ee\u6211\u3002 \u5440\uff0c\u6211\u5230\u897f\u85cf\u6765\u8fd9\u5e45\u753b\u5c31\u662f\u4e3a\u4e86\u7b49\u5979\u3002\u2019\u201d\u51b0\u5ddd\u5929\u5973\u5fd9\u9053\uff1a\u201c\u5feb\u8bf7\u4ed6\u8fdb\u6765\uff01\u201d\u62a4\u5f1f\u5b50\u9053\uff1a \u201c\u4ed6\u5c31\u5728\u5916\u8fb9\u3002\u201d\u5c06\u95e8\u6253\u5f00\uff0c\u53ea\u89c1\u4e00\u4e2a\u81ea\u53d1\u8427\u8427\u7684\u8001\u5de5\u8d70\u4e86\u8fdb\u6765\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u6253\u91cf\u7740\u51b0\u5ddd\u5929 \u5973\uff0c\u5ffd\u7136\u7528\u5c3c\u6cca\u5c14\u8bdd\u8d3f\u8bf4\u9053\uff1a\u201c\u957f\u5f97\u548c\u5f53\u5e74\u7684\u534e\u7389\u516c\u4e3b\u771f\u662f\u4e00\u6a21\u4e00\u6837\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u600e\u77e5\u9053\u6211\u6bcd\u4eb2\u7684\u540d\u5b57\uff1f\u201d\u90a3\u8001\u753b\u5de5\u9053\uff1a\u201c\u5974\u4ec6\u540d\u53eb\u989d\u90fd\uff0c\u4e09 \u5341\u5e74\u524d\uff0c\u66fe\u4f3a\u5019\u8fc7\u9a78\u9a6c\u3001\u516c\u4e3b\u3002\u201d\u51b0\u5929\u5973\u201c\u5475\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u989d\u90fd\u516c\u516c\uff0c \u60f3\u4e0d\u5230\u6709\u8fd9\u4e2a\u7f18\u4efd\u89c1\u4f60\uff0c\u5931\u656c\u4e86\uff01\u201d\u76c8\u76c8\u8d77\u7acb\uff0c\u6361\u8884\u4e00\u62dc\uff0c\u62a4\u6cd5\u5f1f\u5b50\u5f97\u5446\u4e86\u3002\u54ea\u60f3\u5f97\u5230\u6d3b\u4f5b \u7684\u8d35\u5bbe\uff0c\u4f69\u6709\u8d1d\u53f6\u7075\u7b26\u7684\u5973\u62a4\u6cd5\uff0c\u7adf\u5bf9\u8fd9\u6837\u4e00\u4e2a\u7a77\u6101\u6f66\u5012\u7684\u8001\u753b\u5de5\u606d\u656c\u65bd\u793c\u3002 \u3000\u3000\u6cd5\u738b\u4e5f\u5927\u51fa\u610f\u5916\uff0c\u8038\u7136\u52a8\u5bb9\uff0c\u5fd9\u53eb\u5f1f\u5b50\u7ed9\u8001\u753b\u5de5\u8bbe\u5ea7\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u662f\u76f8\u8bc6\uff0c\u5f53\u771f \u610f\u6599\u4e0d\u5230\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e0d\uff0c\u5982\u4eca\u624d\u662f\u7b2c\u4e00\u6b21\u548c\u989d\u90fd\u516c\u516c\u89c1\u9762\u3002\u201d\u6cd5\u738b\u4e00\u8be7\uff0c\u53ea\u542c\u5f97\u51b0 \u5ddd\u5929\u5973\u7eed\u9053\uff1a\u201c\u989d\u90fd\u516c\u516c\u662f\u6559\u6211\u6bcd\u4eb2\u753b\u753b\u7684\u5e08\u7236\uff0c\u6bcd\u4eb2\u751f\u524d\uff0c\u65f6\u65f6\u548c\u8c08\u4ed6\u7684\u753b\u3002\u4ed6\u662f\u5c3c\u6cca\u5c14 \u7684\u7b2c\u4e00\u753b\u5e08\uff0c\u6211\u7684\u51b0\u5bab\u4e2d\u8fd8\u85cf\u6709\u8bb8\u591a\u4ed6\u753b\u7684\u753b\u3002\u201d\u6cd5\u738b\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u5f02\u56fd\u76f8\u9022\uff0c\u4e24\u4ee3\u76f8\u89c1\uff0c \u771f\u662f\u7f18\u5206\u3002\u201d\u51b0\u5ddd\u5929\u5973\u6d6e\u8d77\u4e00\u7247\u601c\u60af\u4e4b\u60c5\uff0c\u95ee\u9053\uff1a\u201c\u989d\u90fd\u516c\u516c\u4e0d\u5728\u7687\u5bab\u5b89\u665a\u5e74\u6e05\u798f\uff0c\u5374\u8dcb\u6d89 \u5173\u5c71\uff0c\u8fdc\u9002\u5f02\u56fd\uff0c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u989d\u90fd\u634b\u7740\u6591\u767d\u7684\u80e1\u5b50\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c31\u4e3a\u7684\u7b49\u4f60\u5230\u8fd9\u513f\u6765\u53ec \u89c1\u6211\u3002\u6211\u672c\u6765\u4ee5\u4e3a\u4e0d\u77e5\u8981\u7b49\u5230\u4ec0\u4e48\u5e74\u6708\uff0c\u8c01\u77e5\u73b0\u5728\u5c31\u7ed9\u6211\u7b49\u7740\u4e86\u3002\u591a\u8c22\u6211\u4f5b\u6148\u60b2\uff0c\u5c3c\u6cca\u5c14\u524d \u4efb\u7684\u56fd\u738b\uff0c\u662f\u4f60\u6bcd\u4eb2\u7684\u5802\u5144\uff0c\u5728\u56fd\u4e2d\u6a2a\u5f81\u66b4\u655b\uff0c\u5927\u4f24\u6c11\u5fc3\uff1b\u5728\u56fd\u5916\u7a77\u5175\u9ed8\u6b66\uff0c\u7ed3\u6028\u56db\u90bb\uff0c\u4f60 \u77e5\u9053\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6bcd\u4eb2\u751f\u524d\u66fe\u548c\u6211\u8bf4\u8d77\uff0c\u5979\u66fe\u6258\u4eba\u529d\u8fc7\u5802\u5144\u3002\u4e5f\u56e0\u6b64\u6211\u6bcd\u4eb2\u53d1\u8a93\u4e0d \u56de\u5c3c\u6cca\u5c14\u3002\u55ef\uff0c\u4f60\u600e\u79f0\u4ed6\u505a\u524d\u738b\uff1f\u201d \u3000\u3000\u989d\u90fd\u8f8d\u4e86\u4e00\u53e3\u6e05\u8336\uff0c\u53f9\u6c14\u8bf4\u9053\uff1a\u201c\u4ed6\u6b7b\u524d\u4e00\u5e74\uff0c\u5c31\u662f\u62a2\u593a\u91d1\u672c\u5df4\u74f6\u7684\u90a3\u4e00\u5e74\uff0c\u56e0\u4e3a\u548c\u90bb \u90a6\u5f00\u4ed7\uff0c\u53d7\u4e86\u7bad\u4f24\uff0c\u56de\u5230\u5bab\u4e2d\uff0c\u6ca1\u6709\u591a\u4e45\u5c31\u6b7b\u4e86\u3002\u4ed6\u7684\u513f\u5b50\u7ee7\u4f4d\uff0c\u6bd4\u7236\u4eb2\u66f4\u4e3a\u66b4\u8650\uff0c\u5f04\u5230\u6c11 \u6028\u6cb8\u817e\uff0c\u8001\u4e00\u8f88\u7684\u90fd\u60f3\u5ff5\u8d77\u4f60\u7684\u6bcd\u4eb2\u534e\u7389\u516c\u4e3b\u6765\uff0c\u5c31\u8fd9\u738b\u4f4d\u672c\u6765\u5e94\u5f53\u662f\u4f60\u7684\u6bcd\u4eb2\u7684\uff0c\u5047\u82e5\u5f53 \u5e74\u4f60\u6bcd\u4eb2\u7ee7\u627f\u5927\u4f4d\uff0c\u5c3c\u6cca\u5c14\u5c31\u4e0d\u81f3\u5f04\u6210\u4eca\u65e5\u7684\u6837\u5b50\u4e86\u3002\u4eba\u4eba\u90fd\u76fc\u671b\u534e\u7389\u516c\u4e3b\u548c\u9a78\u9a6c\u80fd\u591f\u56de \u6765\u3002\u201d\u51b0\u5ddd\u5929\u5973\u4e5f\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u7684\u6bcd\u4eb2\u5df2\u6b7b\u4e86\u5341\u591a\u5e74\u5566\u3002\u201d\u989d\u90fd\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u6211\u662f\u77e5\u9053 \u7684\uff0c\u53ef\u662f\u56fd\u4eba\u8fd8\u672a\u77e5\u9053\uff0c\u4ed6\u4eec\u711a\u9999\u7977\u544a\uff0c\u603b\u662f\u76fc\u671b\u4f60\u7684\u6bcd\u4eb2\u56de\u6765\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u54bd\u4e86\u773c\u6cea\uff0c\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\u6211\u6bcd\u4eb2\u53bb\u4e16\u7684\u6d88\u606f\uff1f\u201d\u989d\u90fd\u9053\uff1a\u201c\u524d\u738b\u66fe\u6d3e\u9063\u56fd\u5e08 \u5230\u897f\u85cf\u6765\u63a2\u542c\u534e\u7389\u516c\u4e3b\u7684\u6d88\u606f\u3002\u542c\u8bf4\u4ed6\u66fe\u89c1\u8fc7\u4f60\u9762\u3002\u201d\u51b0\u5ddd\u5929\u5973\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u7ea2\u8863 \u756a\u50e7\u4e24\u4e0a\u51b0\u5bab\uff0c\u88ab\u6211\u9a71\u9010\u4e0b\u5c71\u7684\u3002\u540e\u6765\u4ed6\u5728\u62a2\u593a\u91d1\u672c\u5df4\u74f6\u7684\u4e8b\u4ef6\u4e2d\u4e5f\u4e27\u4e86\u547d\u4e86\u3002\u201d\u989d\u90fd\u9053\uff1a \u201c\u4ed6\u867d\u6b7b\u4e86\uff0c\u53ef\u662f\u4ed6\u5bf9\u524d\u738b\u6240\u8bf4\u7684\u8bdd\uff0c\u5374\u79cd\u4e0b\u4e00\u4e2a\u5927\u7978\u6839\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5947\u9053\uff1a\u201c\u4ed6\u548c\u56fd\u738b\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u8bdd\u6765\uff1f\u201d\u989d\u90fd\u9053\uff1a\u201c\u4ed6\u89c1\u5230\u4e86\u4eba\u4e16\u65e0\u53cc\u7684\u7edd\u8272\u4ed9 \u5b50\uff0c\u90a3\u8bf4\u7684\u5c31\u662f\u4f60\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u674f\u8138\u6cdb\u7ea2\uff0c\u9053\uff1a\u201c\u8fd9\u5996\u50e7\u53ef\u6076\uff0c\u6211\u5f53\u65f6\u771f\u4e0d\u8be5\u653e\u4ed6\u6d3b\u7740\u56de \u53bb\u3002\u201d\u989d\u90fd\u7eed\u9053\uff1a\u201c\u4ed6\u53c8\u8bf4\u4f60\u7684\u6b66\u529f\u9ad8\u6781\uff0c\u8fde\u624b\u4e0b\u7684\u4e00\u7fa4\u4f8d\u5973\uff0c\u4e5f\u90fd\u662f\u4e2a\u4e2a\u4e0d\u51e1\u3002\u82e5\u7136\u4f60\u80af \u8bda\u5fc3\u534f\u52a9\u56fd\u738b\uff0c\u5c3c\u6cca\u5c14\u5b9a\u53ef\u79f0\u96c4\u3002\u53ea\u662f\u636e\u4ed6\u770b\u6765\uff0c\u4f60\u5b9e\u65e0\u610f\u56de\u56fd\uff0c\u4f46\u4eba\u4e8b\u96be\u6599\uff0c\u4f60\u5bf9\u7687\u5ba4\u65e2 \u4e0d\u5fe0\u5fc3\uff0c\u7559\u4e0b\u6765\u4fbf\u662f\u7978\u60a3\uff0c\u6240\u4ee5\u4ed6\u529d\u56fd\u738b\u9009\u62d4\u9ad8\u624b\u53bb\u6697\u6740\u4f60\u3002\u201d\u51b0\u5ddd\u5929\u5973\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5012\u4e0d \u60e7\u3002\u201d\u989d\u90fd\u9053\uff1a\u201c\u524d\u738b\u542c\u4e86\u4ed6\u7684\u8bf4\u6cd5\uff0c\u867d\u7136\u5bf9\u4f60\u751a\u4e0d\u653e\u5fc3\uff0c\u4f46\u662f\u4ed6\u5728\u897f\u85cf\u632b\u8d25\u4e4b\u540e\uff0c\u53c8\u548c\u56db \u90bb\u7ed3\u6028\u3002\u56fd\u5bb6\u591a\u4e8b\uff0c\u6025\u5207\u4e4b\u95f4\u4e5f\u9009\u4e0d\u5230\u9ad8\u624b\uff0c\u542c\u8bf4\u4f60\u65e0\u610f\u56de\u6765\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u4f46\u592a\u5b50\u542c\u5230\u4e16\u95f4 \u6709\u8fd9\u6837\u7edd\u8272\u7684\u5973\u5b50\uff0c\u5f53\u65f6\u5c31\u7559\u4e86\u5fc3\u3002\u4ed6\u4e24\u5e74\u6765\u6ca1\u7acb\u7687\u540e\uff0c\u539f\u6765\u4ed6\u662f\u865a\u5e2d\u4ee5\u5f85\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5550 \u4e86\u4e00\u53e3\u9053\uff1a\u201c\u90a3\u662f\u766b\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\uff0c\u75f4\u5fc3\u5984\u60f3\u3002\u201d\u989d\u90fd\u9053\uff1a\u201c\u4ed6\u4e0d\u77e5\u9053\u4f60\u7684\u5fc3\u610f\uff0c\u4e00\u76f4\u90fd \u662f\u75f4\u5fc3\u5984\u60f3\u3002\u8fd9\u4e24\u5e74\uff0c\u4ed6\u8bf7\u4e86\u4e0d\u5c11\u963f\u62c9\u4f2f\u548c\u6b27\u6d32\u7684\u9ad8\u624b\u6b66\u58eb\uff0c\u53c8\u8bad\u7ec3\u4e86\u4e00\u4e2a\u767b\u5c71\u5175\u56e2\u3002\u51c6\u5907 \u5230\u897f\u85cf\u6765\uff0c\u8fce\u63a5\u4f60\u56de\u53bb\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5343\u519b\u53ef\u4ee5\u593a\u5e05\uff0c\u5339\u5b50\u53ef\u593a\u5fd7\u3002\u4ed6\u5c31\u662f\u6d3e\u5341\u4e07\u4eba \u6765\uff0c\u6211\u4e5f\u4e0d\u4f1a\u4e3a\u4ed6\u6240\u52a8\u3002\u201d\u989d\u90fd\u9053\uff1a\u201c\u4ee5\u6218\u4e89\u4f5c\u5a01\u80c1\uff0c\u4ed6\u6599\u60f3\u798f\u5eb7\u5b89\u548c\u85cf\u738b\u4e0d\u4f1a\u4e3a\u4f60\u4e00\u4eba\u800c \u8f7b\u542f\u6218\u7aef\uff0c\u4ed6\u4eb2\u81ea\u5e26\u5175\u6765\u8fce\u63a5\u4f60\uff0c\u4f60\u7eb5\u4e0d\u613f\uff0c\u897f\u85cf\u4e5f\u4e0d\u6562\u4ea9\u7559\u4f60\u5c45\u505c\u3002\u201d\u51b0\u5ddd\u5929\u5973\u53c8\u6c14\u53c8 \u6124\uff0c\u6599\u4e0d\u5230\u81ea\u5df1\u7adf\u60f9\u4e86\u8fd9\u4e48\u5927\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u989d\u90fd\u7eed\u9053\uff1a\u201c\u6211\u4ee5\u524d\u5f97\u4f60\u6bcd\u4eb2\u539a\u5f85\uff0c\u6069\u4e49\u96be\u5fd8\uff0c\u56fd\u4eba\u53c8\u90fd\u60f3\u4f60\u4eec\uff0c\u6240\u4ee5\u6211\u4e0d\u60dc\u629b\u5f03\u4e86\u7687 \u5bab\u753b\u5e08\u7684\u4f4d\u7f6e\uff0c\u8dcb\u6d89\u5173\u5c71\uff0c\u6765\u5230\u8fd9\u91cc\uff0c\u6211\u5e74\u8001\u529b\u8870\uff0c\u51b0\u5cf0\u662f\u4e0a\u4e0d\u4e86\u7684\uff0c\u6070\u5de7\u767d\u6559\u5587\u561b\u5bab\u8981\u4eba \u4f5c\u753b\uff0c\u6211\u4fbf\u5e94\u5f81\u6765\u4e86\u3002\u4f60\u6bcd\u4eb2\u4e00\u751f\u793c\u4f5b\uff0c\u6211\u6599\u4f60\u4e5f\u8bb8\u4f1a\u5230\u5587\u561b\u5bab\u4e2d\u53c2\u62dc\uff0c\u6240\u4ee5\u4fbf\u753b\u4e86\u90a3\u5e45 \u753b\uff0c\u5e0c\u671b\u4f60\u80fd\u89c1\u5230\uff0c\u679c\u7136\u6211\u4f5b\u6148\u60b2\uff0c\u4e0d\u987b\u6211\u591a\u8d39\u65f6\u65e5\u4e45\u7b49\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u660e\u767d\u4e86\u539f\u59d4\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4e0d\u8f9e\u52b3\u82e6\uff0c\u5c06\u4fe1\u606f\u5e26\u7ed9\u6211\u3002\u201d\u989d\u90fd\u9053\uff1a\u201c\u6211\u6765\u89c1 \u4f60\uff0c\u8fd8\u5e26\u6765\u4e86\u6211\u81ea\u5df1\u7684\u5fc3\u610f\u548c\u56fd\u4eba\u7684\u613f\u671b\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u613f\u542c\u6559\u8a00\uff0c\u516c\u516c\u4f60\u8bf4\u3002\u201d\u989d\u90fd \u9053\uff1a\u201c\u4f60\u82e5\u6709\u5fc3\u6740\u4ed6\uff0c\u90a3\u4e48\u4f60\u4fbf\u56de\u53bb\uff0c\u6740\u4ed6\u81ea\u7acb\u3002\u56fd\u4eba\u90fd\u62e5\u62a4\u4f60\u3002\u5373\u7b97\u4f60\u4e0d\u6740\u4ed6\u3001\u56de\u56fd\u4e4b \u540e\uff0c\u632f\u81c2\u4e00\u547c\uff0c\u56fd\u4eba\u4e5f\u4f1a\u62e5\u62a4\u4f60\u63a8\u7ffb\u66b4\u541b\uff0c\u7acb\u4f60\u4e3a\u738b\uff0c\u8fd9\u738b\u4f4d\u672c\u6765\u662f\u4f60\u6bcd\u4eb2\u7684\uff0c\u7531\u4f60\u7ee7\u627f\uff0c \u540d\u6b63\u8a00\u987a\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u54ea\u6709\u5fc3\u601d\u505a\u56fd\u738b\uff1f\u82e5\u4e0d\u662f\u51b0\u5cf0\u5012\u584c\uff0c\u8fde\u5c18\u4e16\u6211\u4e5f\u4e0d\u613f \u62db\u60f9\u3002\u6211\u672c\u6765\u5c31\u6253\u7b97\u4eca\u751f\u4eca\u4e16\uff0c\u6c38\u9690\u51b0\u5bab\u7684\u554a\uff01\u201d \u3000\u3000\u989d\u90fd\u9053\uff1a\u201c\u82e5\u4f60\u4e0d\u6b32\u4e3a\u738b\uff0c\u90a3\u5c31\u5feb\u8fdc\u8d70\u9ad8\u98de\uff0c\u56e0\u4e3a\u6050\u6015\u56fd\u738b\u4e0d\u65e5\u8981\u5e26\u5175\u6765\u4e86\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u989d\u90fd\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u65e9\u5c31\u8bf7\u4ed6\u53d1\u5175\uff0c\u4e58\u6b64\u65f6\u673a\uff0c\u6b63\u597d\u4f5c\u4e00 \u77f3\u4e24\u9e1f\u4e4b\u8ba1\u3002\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u70e6\u95f7\uff0c\u601d\u5982\u6f6e\u6d8c\uff0c\u4e45\u4e45\u4e0d\u8a00\u3002\u5c3c\u6cca\u5c14\u662f\u5979\u6bcd\u4eb2\u7684\u56fd\u5bb6\uff0c\u4e2d\u56fd\u662f \u5979\u7236\u4eb2\u7684\u56fd\u5bb6\u3002\u5979\u7231\u8fd9\u4e24\u4e2a\u56fd\u5bb6\u7684\u5fc3\u60c5\uff0c\u5c31\u5982\u540c\u7231\u5979\u81ea\u5df1\u7684\u7236\u6bcd\u4e0a\u822c\uff0c\u96be\u5206\u8f69\u8f7b\u3002\u5979\u600e\u5fcd\u89c1 \u81ea\u5df1\u7684\u8868\u5144\u5e26\u5c3c\u6cca\u5c14\u5175\u6765\u5411\u4e2d\u56fd\u6311\u8845\uff1f\u5979\u53c8\u600e\u5fcd\u89c1\u81ea\u5df1\u7684\u6bcd\u56fd\u5728\u66b4\u541b\u7edf\u6cbb\u4e4b\u4e0b\u6c11\u4e0d\u804a\u751f\uff1f\u53ef \u662f\u82e5\u7136\u81ea\u5df1\u771f\u7684\u542c\u989d\u90fd\u7684\u8bdd\uff0c\u56de\u56fd\u53bb\u5e72\u9884\u653f\u4e8b\uff0c\u53c8\u5c06\u60f9\u8d77\u591a\u5927\u7684\u98ce\u6ce2\u4e0e\u9ebb\u70e6\uff1f\u90a3\u53c8\u5c82\u662f\u5979\u5b64 \u9ad8\u7edd\u4fd7\u7684\u6027\u60c5\u6240\u582a\u5fcd\u53d7\uff1f \u3000\u3000\u5916\u9762\u62a4\u6cd5\u5f1f\u5b50\u8fdb\u6765\u62a5\u9053\uff1a\u6cd5\u738b\u7684\u4eea\u4ed7\u5df2\u7ecf\u51c6\u5907\u505c\u5f53\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u989d\u90fd\u516c\u516c\uff0c\u591a\u8c22 \u4f60\u4e00\u756a\u597d\u610f\u3002\u4f60\u6682\u65f6\u5728\u8fd9\u91cc\u4f4f\u4e0b\uff0c\u5f85\u5c3c\u6cca\u5c14\u592a\u5e73\u4e4b\u540e\uff0c\u4f60\u518d\u56de\u5bb6\u3002\u201d\u5979\u5e76\u6ca1\u6709\u8bf4\u51fa\u81ea\u5df1\u7684\u51b3 \u5b9a\u3002\u4f46\u5728\u989d\u90fd\u542c\u6765\uff0c\u597d\u50cf\u51b0\u5ddd\u5929\u5973\u5df2\u6709\u4f7f\u5f97\u5c3c\u6cca\u5c14\u592a\u5e73\u7684\u65b9\u6cd5\uff0c\u4e8e\u662f\u5fc3\u6ee1\u610f\u8db3\u7684\u65bd\u793c\u9000\u4e0b\u3002 \u51b0\u5ddd\u5929\u5973\u4e5f\u5c31\u548c\u6cd5\u738b\u4e00\u9053\u8d76\u5f80\u571f\u53f8\u7684\u57ce\u5821\u53bb\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e0e\u5e7d\u840d\u4e24\u4eba\u5728\u77f3\u5854\u7684\u9759\u5ba4\u91cc\u53d7\u81f3\uff01\u56f4\u653b\uff0c\u6b63\u5728\u5403\u7d27\uff0c\u9648\u5929\u5b87\u5c55\u5f00\u51b0\u5ddd\u5251\u6cd5\uff0c\u62fc\u547d \u62b5\u6321\u5370\u5ea6\u82e6\u884c\u50e7\u7684\u7af9\u6756\u91d1\u76c2\uff0c\u4ecd\u88ab\u4ed6\u8feb\u5f97\u6b65\u6b65\u540e\u9000\u3002\u5e7d\u840d\u4ed7\u5251\u4fdd\u62a4\u62a4\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\uff0c\u8fd9\u65f6 \u4e5f\u5df2\u4e0e\u82e6\u884c\u50e7\u7684\u5e08\u4f84\u5fb7\u9c81\u5947\u4ea4\u4e0a\u4e86\u624b\u3002\u53e6\u5916\u8fd8\u6709\u4e24\u4e2a\u897f\u85cf\u6b66\u58eb\uff0c\u662f\u4fc4\u9a6c\u767b\u7684\u624b\u4e0b\u3002\u5e7d\u840d\u52c9\u5f3a \u654c\u5f97\u4f4f\u5fb7\u9c81\u5947\uff0c\u518d\u6dfb\u4e0a\u4e24\u4e2a\u654c\u4eba\uff0c\u7acb\u523b\u9669\u8c61\u73af\u751f\u3002\u4fc4\u9a6c\u767b\u7684\u624b\u4e0b\u76ee\u7684\u5728\u4e8e\u73ed\u7985\u7684\u4ee3\u8868\uff0c\u8feb\u9000 \u4e86\u5e7d\u840d\uff0c\u7acb\u523b\u4e0a\u53bb\u6349\u4eba\u3002\u5e7d\u840d\u5927\u6025\uff0c\u626c\u624b\u98de\u51fa\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u4e24\u4e2a\u6b66\u58eb\u672a\u66fe\u78b0\u8fc7\u8fd9\u79cd\u5947\u602a\u7684 \u6697\u5668\uff0c\u7ed9\u51b0\u5f39\u4e2d\u4e86\u7a74\u9053\uff0c\u767b\u65f6\u8840\u6db2\u51b7\u51dd\uff0c\u624b\u811a\u9ebb\u6728\uff0c\u5413\u5f97\u614c\u5fd9\u7a9c\u51fa\uff0c\u8d76\u7d27\u53bb\u627e\u70c8\u9152\u5fa1\u5bd2\u3002\u5e7d \u840d\u5927\u559c\uff0c\u53c8\u7528\u51b0\u9b44\u795e\u5f39\u53bb\u6253\u5fb7\u9c81\u5947\uff0c\u5fb7\u9c81\u5947\u529f\u529b\u8f83\u9ad8\uff0c\u628a\u8f6f\u97ad\u4f7f\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u51b0\u5f39\u4e0d\u4e2d\u4ed6\u7684 \u7a74\u9053\uff0c\u867d\u7136\u88ab\u5bd2\u6c14\u4fb5\u88ad\uff0c\u51b7\u5f97\u7259\u5173\u6253\u6218\u3001\u5374\u4e5f\u8fd8\u80fd\u591f\u633a\u4f4f\u3002\u81f3\u4e8e\u90a3\u4e2a\u82e6\u884c\u50e7\uff0c\u5374\u8fde\u5bd2\u6218\u4e5f\u4e0d \u6253\u4e00\u4e2a\uff0c\u51b0\u5f39\u672a\u8fd1\u8eab\u5c31\u88ab\u4ed6\u626c\u8896\u62c2\u5f00\uff0c\u4ed6\u4ecd\u7136\u7d27\u7d27\u8ffd\u51fb\u7740\u9648\u5929\u5b87\uff0c\u534a\u70b9\u4e5f\u4e0d\u653e\u677e\u3002 \u3000\u3000\u8fd9\u65f6\u5e7d\u840d\u8fd9\u65f6\u8f6c\u5371\u4e3a\u5b89\uff0c\u9648\u5929\u5b87\u5374\u62b5\u654c\u4e0d\u4f4f\u3002\u5370\u5ea6\u82e6\u50e7\u559d\u4e00\u58f0\u5927\u559d\uff0c\u91d1\u76c2\u94b5\u5ffd\u5730\u5f53\u5934\u4e00 \u7f69\uff0c\u9648\u5929\u5b87\u7f29\u624b\u4e0d\u53ca\uff0c\u957f\u7b80\u7f69\u5728\u94b5\u4e2d\u3002\u82e5\u884c\u618e\u54c8\u54c8\u5927\u7b11\uff0c\u76c2\u94b5\u5de6\u65cb\u53f3\u8f6c\uff0c\u9648\u5929\u5b87\u8eab\u4e0d\u7531\u5df1\u5730 \u8ddf\u7740\u4ed6\u65cb\u8f6c\uff0c\u4e0d\u8bba\u600e\u6837\u7528\u529b\uff0c\u957f\u5251\u603b\u662f\u62d4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u82e6\u884c\u50e7\u5f97\u610f\u4e4b\u6781\uff0c\u6b63\u5f85\u52a0\u901f\u90a3\u76c2\u94b5\u7684\u65cb\u8f6c\u4e4b\u529b\uff0c\u5ffd\u89c9\u95e8\u5916\u9759\u5bc2\u5982\u6b7b\uff0c\u6c14\u6c1b\u6709\u5f02\uff0c\u5fc3\u4e2d\u4e00 \u61ca\uff0c\u56de\u5934\u770b\u65f6\uff0c\u5ffd\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u5947\u5bd2\u4e4b\u6c14\u4ece\u9f3b\u5b54\u4e2d\u94bb\u4eba\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u9762\u631f\u5bd2\u971c\uff0c\u6b63\u5728\u51b7 \u51b7\u5730\u76ef\u7740\u81ea\u5df1\u3002\u518d\u4e00\u770b\uff0c\u95e8\u5916\u7684\u6b66\u4e0a\u4e2a\u4e2a\u5782\u624b\u8083\u7acb\uff0c\u90a3\u62b1\u7740\u4fc4\u9a6c\u767b\u50f5\u786c\u8eab\u4f53\u7684\u6b66\u58eb\u66f4\u662f\u663e\u5f97 \u975e\u5e38\u60f6\u6050\uff0c\u539f\u6765\u767d\u6559\u6cd5\u738b\u7684\u6cd5\u9a7e\u6765\u5230\u4e86\u53e4\u5854\u4e0b\u9762\u3002 \u3000\u3000\u5370\u5ea6\u82e6\u884c\u50e7\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u54ea\u91cc\u8fd8\u6709\u4e1d\u6beb\u6597\u5fd7\uff0c\u800c\u4e14\u4ed6\u88ab\u51b0\u5ddd\u5929\u5973\u7684\u51b0\u5f39\u4ece\u9f3b\u5b54\u4e2d\u6253 \u5165\uff0c\u5947\u5bd2\u4e4b\u6c14\uff0c\u771f\u4fb5\u5230\u5fc3\u5934\uff0c\u5373\u7b97\u5c1a\u6709\u6597\u5fd7\uff0c\u4ea6\u5df2\u65e0\u80fd\u4e3a\u529b\uff0c\u5e78\u800c\u4ed6\u7684\u745c\u4f3d\u529f\u592b\u5df2\u7ec3\u5230\u7b2c\u4e8c \u6bb5\u7684\u7b2c\u4e00\u6bb5\u7684\u6700\u9ad8\uff0c\u53ef\u4ee5\u95ed\u6c14\u5341\u4e2a\u65f6\u8fb0\u4e0d\u6b7b\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u8fd9\u4e2a\u672c\u9886\uff0c\u4e5f\u53ef\u80fd\u95ed\u6c14\u4e24\u4e09\u4e2a\u65f6 \u8fb0\u3002\u5f53\u4e0b\u7acb\u5373\u95ed\u6c14\u5c4f\u606f\u547c\u5438\uff0c\u4ee4\u90a3\u80a1\u5947\u5bd2\u4e4b\u6c14\u4e0d\u80fd\u6d41\uff0c\u7528\u771f\u6c14\u4fdd\u7740\u5fc3\u5934\u7684\u4e00\u70b9\u6e29\u6696\uff0c\u7acb\u5373\u7a7f \u7a97\u98de\u8d70\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u4e0d\u8ffd\u4ed6\u3002\u5fb7\u9c81\u5947\u7eb5\u8eab\u7a0d\u6162\uff0c\u88ab\u9648\u5929\u5b87\u62c9\u4f4f\u97ad\u68a2\uff0c\u957f\u5251\u4e00\u8d77\uff0c\u6b63\u5f85\u524a\u4e0b\uff0c \u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u53d1\u8a93\u4e0d\u518d\u5230\u897f\u85cf\uff0c\u8ba9\u4ed6\u53bb\u5427\u3002\u201d\u5fb7\u9c81\u5947\u6d3b\u547d\u8981\u7d27\uff0c\u679c\u7136\u53d1\u4e86\u4e00\u4e2a\u8a93\uff0c \u9648\u5929\u5b87\u4fbf\u677e\u5f00\u624b\uff0c\u8ba9\u4ed6\u8d70\u4e86\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u8d70\u4e0a\u5854\u697c\uff0c\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u670d\u4e86\u51b0\u5bab\u7075\u836f\u4e4b\u540e\uff0c\u75db\u695a\u82e5\u5931\uff0c\u884c\u52a8\u5df2\u5982\u5e38\u4eba\uff0c\u767d \u6559\u6cd5\u738b\u5411\u4ed6\u6170\u95ee\uff0c\u4ed6\u4e5f\u5411\u6cd5\u738b\u9053\u8c22\uff0c\u591a\u8c22\u6cd5\u738b\u7684\u660e\u667a\uff0c\u6d88\u9975\u4e86\u8fd9\u573a\u9669\u6076\u7684\u98ce\u6ce2\u3002 \u3000\u3000\u4fc4\u9a6c\u767b\u7684\u51e0\u4e2a\u4eb2\u4fe1\u6b66\u58eb\u88ab\u6cd5\u738b\u7684\u5a01\u4e25\u9547\u4f4f\uff0c\u5782\u624b\u8083\u7acb\uff0c\u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\uff0c\u62b1\u7740\u4fc4\u9a6c\u767b\u50f5\u786c \u8eab\u4f53\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u66f4\u662f\u60f6\u6050\u4e0d\u5b89\u3002\u6cd5\u738b\u9053\uff1a\u201c\u4f60\u4eec\u613f\u610f\u7acb\u529f\u8d4e\u7f6a\u4e48\uff1f\u201d\u8fd9\u7fa4\u6b66\u58eb\u81ea\u662f\u6ca1\u53e3\u5e94 \u627f\uff0c\u6cd5\u738b\u9053\uff1a\u201c\u4fc4\u9a6c\u767b\u52fe\u7ed3\u5916\u4eba\u5984\u56fe\u53db\u4e71\uff0c\u4f60\u4eec\u662f\u4ed6\u7684\u4eb2\u4fe1\uff0c\u603b\u4e0d\u81f3\u4e8e\u4e0d\u77e5\u9053\u5427\uff1f\u201d\u90a3\u7fa4\u6b66 \u58eb\u4f4e\u5934\u4e0d\u6562\u4f5c\u58f0\u3002\u6cd5\u738b\u9053\uff1a\u201c\u4f60\u4eec\u628a\u4ed6\u7684\u7f6a\u8bc1\u641c\u6765\u7ed9\u6211\uff0c\u6211\u8981\u516c\u5e03\u7ed9\u8428\u8fe6\u5b97\u5168\u4f53\u50e7\u4fd7\u4eba\u4f17\u77e5 \u9053\u3002\u201d\u547d\u4e24\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u966a\u540c\u4fc4\u9a6c\u767b\u7684\u4eb2\u4fe1\u6b66\u58eb\u53bb\u641c\u67e5\uff0c\u679c\u7136\u5728\u4fc4\u9a6c\u767b\u7684\u79c1\u5ba4\u91cc\u641c\u51fa\u4e86\u8bb8\u591a\u79d8 \u5bc6\u4fe1\u4ef6\uff0c\u5176\u4e2d\u7adf\u6709\u5370\u5ea6\u5580\u6797\u90a6\u5927\u516c\u548c\u5c3c\u6cca\u5c14\u56fd\u738b\u4eb2\u7b14\u7b54\u5e94\u7684\u51fd\u4ef6\uff0c\u6cd5\u738b\u8bf7\u51b0\u5ddd\u5929\u5973\u5c06\u4fc4\u9a6c\u767b \u6551\u9192\uff0c\u7f6a\u8bc1\u786e\u51ff\uff0c\u4fc4\u9a6c\u767b\u867d\u7136\u72e1\u733e\u5982\u72d0\uff0c\u4ea6\u5df2\u65e0\u8a00\u53ef\u8fa9\u3002\u6cd5\u738b\u5c06\u4ed6\u65a5\u8d23\u4e00\u987f\uff0c\u7528\u91cd\u624b\u6cd5\u5e9f\u4e86 \u4ed6\u7684\u6b66\u529f\uff0c\u5c06\u4ed6\u4ea4\u4e0e\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u770b\u7ba1\u3002\u5f85\u8428\u8fe6\u5b97\u7684\u4e71\u4e8b\u5b8c\u5168\u5e73\u606f\u4e4b\u540e\uff0c\u518d\u62bc\u5230\u62c9\u8428\u53bb\u3002 \u3000\u3000\u571f\u53f8\u5821\u4e2d\u7684\u6076\u6597\uff0c\u7531\u4e8e\u6cd5\u738b\u548c\u51b0\u5ddd\u5929\u5973\u7684\u6765\u5230\uff0c\u7acb\u65f6\u74e6\u89e3\u51b0\u6d88\uff0c\u4f46\u5916\u9762\u5c71\u5761\uff0c\u88ab\u4fc4\u9a6c\u767b \u6240\u9a71\u4f7f\u7684\u571f\u53f8\u519b\u961f\uff0c\u4ecd\u7136\u5728\u548c\u829d\u5a1c\u7684\u8205\u8205\u6d1b\u73e0\u7684\u519b\u961f\u76f8\u6301\uff0c\u6cd5\u738b\u5904\u7406\u4e86\u4fc4\u9a6c\u767b\u4e4b\u540e\uff0c\u518d\u547d\u62a4 \u6cd5\u5f1f\u5b50\u6446\u8d77\u6cd5\u9a7e\u4eea\u4ed7\uff0c\u5230\u5916\u9762\u53bb\u8c03\u505c\u4e24\u519b\u7684\u76f8\u6597\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u966a\u73ed\u7985\u7684\u4ee3\u8868\u8bf4\u8bdd\uff0c\u9648\u5929\u5b87\u548c\u5e7d\u840d\u5219\u8d81\u8fd9\u4e2a\u7a7a\u95f2\uff0c\u5230\u540e\u5bab\u53bb\u5bfb\u89c5\u829d\u5a1c\u7684\u5c38\u4f53\u3002 \u571f\u53f8\u5821\u4e2d\u7684\u201c\u5439\u5fe0\u201d\u672c\u6765\u662f\u88ab\u4fc4\u9a6c\u767b\u8feb\u4ee4\u4ed6\u5bb3\u73ed\u7985\u6d3b\u4f5b\u7684\u4ee3\u8868\u7684\uff0c\u4ed6\u4e0d\u6562\u4e0b\u624b\uff0c\u5374\u7531\u526f\u624b\u4ee3 \u6b7b\uff0c\u73ed\u7985\u7684\u4ee3\u8868\u5bbd\u5927\u4e3a\u6000\uff0c\u4e5f\u9976\u4e86\u4ed6\u3002\u4ed6\u81ea\u613f\u5e26\u9886\u9648\u5929\u5b87\u524d\u5f80\u4e0a\u53f8\u7684\u7075\u5802\uff0c\u539f\u6765\u829d\u5a1c\u7684\u9057\u4f53 \u88ab\u4fc4\u9a6c\u767b\u6446\u5728\u4e00\u4e2a\u73bb\u7483\u68fa\u5185\uff0c\u5c31\u653e\u5728\u571f\u53f8\u7075\u5802\u7684\u65c1\u8fb9\uff0c\u5728\u4fc4\u9a6c\u767b\u7684\u610f\u601d\uff0c\u662f\u8ba9\u571f\u53f8\u7684\u624b\u4e0b\u90fd \u8ba4\u6e05\u8fd9\u4e2a\u523a\u5ba2\u4fbf\u662f\u5f53\u5e74\u5077\u9a6c\u7eb5\u706b\u7684\u201c\u5973\u8d3c\u201d\uff0c\u4e5f\u5373\u662f\u88ab\u9648\u5b9a\u57fa\u7236\u5b50\u6551\u8d70\u7684\u90a3\u4e2a\u201c\u5973\u8d3c\u201d\uff0c\u597d \u8bc1\u660e\u4ed6\u8bf4\u7684\u4e0d\u662f\u5047\u8bdd\uff0c\u597d\u6fc0\u8d77\u4e0a\u53f8\u624b\u4e0b\u5bf9\u6c49\u4eba\u201c\u5ba3\u6170\u4f7f\u201d\u7684\u4ec7\u6068\u3002\u56e0\u6b64\u4e4b\u6545\uff0c\u9648\u5929\u5b87\u53c8\u770b\u5230 \u4e86\u829d\u5a1c\u7684\u9057\u5bb9\u3002\u524d\u5c18\u5f80\u4e8b\uff0c\u4e00\u4e00\u6cdb\u4e0a\u5fc3\u5934\uff0c\u9648\u5929\u5b87\u4e0d\u89c9\u6f5c\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u897f\u85cf\u9ad8\u539f\uff0c\u6c14\u5019\u5bd2\u51b7\u4e7e\u71e5\uff0c\u829d\u5a1c\u7684\u5c38\u4f53\uff0c\u653e\u5728\u73bb\u7483\u68fa\u4e2d\uff0c\u867d\u7136\u4e3a\u65f6\u5df2\u8fc7\u4e00\u65ec\uff0c\u989c\u8272\u8fd8\u662f \u6829\u6829\u5982\u751f\uff0c\u9648\u5929\u5b87\u60f3\u8d77\u5979\u4e34\u6b7b\u4e4b\u524d\uff0c\u524d\u6765\u9053\u522b\u7684\u60c5\u666f\uff0c\u90a3\u5e7d\u6028\u7684\u795e\u60c5\uff0c\u8bc0\u522b\u7684\u773c\u5149\uff0c\u6bd5\u751f\u4e5f \u4e0d\u4f1a\u5fd8\u8bb0\u3002\u7075\u5802\u91cc\u5bc2\u9759\u65e0\u58f0\uff0c\u53ea\u6709\u5e7d\u840d\u7684\u5728\u5e7d\u5e7d\u7684\u53f9\u606f\u3002\u9648\u5929\u5b87\u9762\u5bf9\u9057\u5bb9\uff0c\u4e00\u7247\u51c4\u8ff7\uff0c\u773c\u524d \u5ffd\u7136\u6cdb\u51fa\u829d\u5a1c\u7684\u5e7b\u5f71\uff0c\u597d\u50cf\u5f39\u7740\u51ac\u4e0d\u62c9\u5411\u81ea\u5df1\u884c\u6765\u3002\u8033\u8fb9\u5ffd\u5730\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5929\u5b87\uff0c\u5929 \u5b87\uff01\u201d\u5e7b\u5f71\u4e5f\u53d8\u4f5c\u4e86\u771f\u4eba\uff0c\u9648\u5929\u5b87\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u829d\u5a1c\uff01\u201d\u5f20\u81c2\u5411\u524d\u4e00\u62b1\uff0c\u773c\u524d\u7684\u201c\u829d\u5a1c\u201d\u5ffd \u7136\u53d8\u4e86\uff0c\u53ea\u89c1\u5979\u5f20\u5927\u773c\u775b\uff0c\u60ca\u6115\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u9648\u5929\u5b87\u970e\u65f6\u95f4\u6e05\u9192\u8fc7\u6765\uff0c\u770b\u6e05\u695a\u4e86\uff0c\u539f\u6765\u662f\u81ea \u5df1\u540d\u4e49\u4e0a\u7684\u672a\u5a5a\u59bb\u3001\u571f\u53f8\u7684\u5973\u513f\u6851\u58c1\u4f0a\u3002\u5979\u7684\u6bcd\u4eb2\u4e5f\u8ddf\u7740\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6851\u58c1\u4f0a\u5fc3\u4e2d\u7684\u60b2\u75db\u5b9e\u4e0d\u5728\u9648\u5929\u5b87\u4e4b\u4e0b\uff0c\u8fd9\u5239\u90a3\u90a3\u95f4\uff0c\u5979\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\uff1a\u9648\u5929 \u5b87\u4e3a\u4ec0\u4e48\u5c61\u6b21\u62d2\u5a5a\uff1f\u9648\u5929\u5b87\u4e3a\u4ec0\u4e48\u662f\u8eb2\u907f\u5979\uff1f\u4e00\u5207\u7591\u95ee\u90fd\u5df2\u5f97\u5230\u7b54\u6848\uff1a\u539f\u6765\u4eba\u8a00\u4e0d\u5047\u3001\u9648\u5929 \u5b87\u775b\u7684\u679c\u7136\u662f\u8fd9\u4e2a\u201c\u5973\u8d3c\u201d\uff0c\u662f\u523a\u6740\u81ea\u5df1\u7236\u4eb2\u7684\u4ec7\u4eba\u3002\u5979\u7684\u6bcd\u4eb2\u662f\u60ca\u6115\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u6124\u7136\u95ee \u9053\uff1a\u201c\u55ef\uff0c\u9648\u516c\u5b50\u3001\u4f60\u8fdb\u8fd9\u7075\u5802\u4f5c\u4e48\uff1f\u4f60\u662f\u540a\u796d\u4f60\u7684\u4e08\u4eba\u8fd8\u662f\u540a\u8fd9\u4e2a\u5973\u8d3c\uff1f\u201d\u5176\u5b9e\u5979\u662f\u660e\u77e5 \u6545\u95ee\u3002\u9648\u5929\u5b87\u624b\u629a\u73bb\u7483\u68fa\u6750\u7684\u8fd9\u4efd\u60b2\u75db\u7684\u795e\u60c5\uff0c\u4efb\u8c01\u4eba\u90fd\u770b\u5f97\u51fa\uff0c\u4ed6\u662f\u540a\u796d\u829d\u5a1c\u7684\u3002 \u3000\u3000\u9648\u5929\u5b87\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u4e0d\u662f\u5973\u8d3c\uff0c\u5979\u662f\u6c81\u5e03\u85e9\u738b\u7684\u5973\u513f\u3002\u4f60\u4eec\u65e2\u7136\u770b\u7740\u5979\u4e0d\u987a\u773c\uff0c\u5c31\u8ba9 \u6211\u628a\u5979\u7684\u68fa\u6750\u642c\u8d70\u4e86\u5427\uff01\u201d\u571f\u53f8\u7684\u5be1\u5987\u767b\u65f6\u6012\u6c14\u4e0a\u51b2\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u5979\u662f\u8c01\uff0c\u6211\u53ea\u77e5 \u9053\u5979\u662f\u523a\u6211\u4e08\u592b\u7684\u4ec7\u4eba\uff0c\u6b7b\u4e86\u4e5f\u5f97\u8981\u5979\u966a\u846c\uff01\u201d\u5ffd\u5730\u568e\u9676\u54ed\u9053\uff1a\u201c\u738b\u7237\uff0c\u4f60\u6b7b\u5f97\u597d\u60e8\u5475\uff0c\u4f60 \u6b7b\u4e86\u8c01\u90fd\u6765\u6b3a\u8d1f\u6211\u4eec\u5475\uff01\u201d\u5979\u4e00\u65f6\u6c14\u6124\uff0c\u8bf4\u51fa\u8fd9\u8bdd\uff0c\u5ffd\u5730\u60f3\u8d77\u9648\u5929\u5b87\u66ff\u5979\u9664\u6389\u4fc4\u9a6c\u767b\uff0c\u5b9e\u662f \u5bf9\u5979\u6709\u6069\uff0c\u600e\u8bf4\u662f\u6b3a\u8d1f\uff1f\u54ed\u58f0\u4e0d\u89c9\u4f4e\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u9648\u5929\u5b87\u624b\u8db3\u65e0\u63aa\uff0c\u5e7d\u840d\u5ffd\u5730\u4e5f\u54ed\u9053\uff1a\u201c\u829d\u5a1c\u59d0\u59d0\u5475\uff0c\u4f60\u6b7b\u5f97\u4e0d\u503c\u5475\uff0c\u522b\u4eba\u6740\u4e86\u4f60\u7684\u4e00 \u5bb6\uff0c\u5e76\u541e\u4e86\u4f60\u7684\u571f\u5730\uff0c\u4f60\u53ea\u523a\u6740\u4e86\u4e2a\u4ec7\u4eba\uff0c\u5374\u8981\u966a\u7740\u4ec7\u4eba\u6b7b\u53bb\uff0c\u6b7b\u5f97\u597d\u4e0d\u503c\u5475\uff01\u201d\u6851\u58c1\u4f0a\u6bcd \u5973\u5fc3\u4e2d\u4e00\u9707\uff0c\u571f\u53f8\u5bb3\u6b7b\u85e9\u738b\u5168\u5bb6\u4e4b\u4e8b\uff0c\u5979\u4eec\u4e5f\u5e76\u975e\u5168\u65e0\u77e5\u6653\uff0c\u53ea\u662f\u788d\u4e8e\u592b\u5987\u7236\u5973\u4e4b\u60c5\uff0c\u5c31\u53ea \u8bb0\u5f97\u522b\u4eba\u7684\u4ec7\u6068\uff0c\u5374\u8bb0\u4e0d\u5f97\u81ea\u5df1\u4eb2\u4eba\u7ed9\u4e88\u522b\u4eba\u7684\u707e\u7978\u3002\u5e7d\u840d\u7684\u54ed\u58f0\u672a\u6b47\uff0c\u571f\u53f8\u5be1\u5987\u7684\u54ed\u58f0\u5374 \u4e0d\u81ea\u89c9\u505c\u4e86\u4e0b\u6765\u3002\u54ed\u58f0\u4e2d\u5ffd\u89c1\u6cd5\u738b\u966a\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u85cf\u65cf\u7537\u5b50\u8d70\u7075\u5802\uff0c\u8fd9\u7537\u5b50\u6b63\u662f\u829d\u5a1c\u7684\u8205 \u8205\u6d1b\u73e0\u3002 \u3000\u3000\u6d1b\u73e0\u63a5\u53d7\u4e86\u6cd5\u738b\u7684\u8c03\u89e3\uff0c\u8fdb\u6765\u5bfb\u89c5\u7525\u5973\u7684\u5c38\u4f53\uff0c\u4e00\u89c1\u829d\u5a1c\u7684\u5c38\u4f53\u6446\u5728\u571f\u53f8\u7075\u68d5\u7684\u65c1\u8fb9\uff0c \u6012\u6c14\u51b2\u51b2\u5730\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u7be1\u4f4d\u6076\u8d3c\uff0c\u600e\u914d\u5728\u6211\u7525\u5973\u7684\u65c1\u8fb9\uff1f\u201d\u52a8\u624b\u5c31\u8981\u7838\u571f\u53f8\u7684\u6850\u68fa\u3002\u6cd5 \u738b\u4f4e\u9996\u5408\u4ec0\uff0c\u53e3\u5ba3\u4f5b\u53f7\uff0c\u5e84\u4e25\u8bf4\u9053\uff1a\u201c\u56e0\u679c\u62a5\u5e94\uff0c\u4eba\u6b7b\u4ec7\u706d\uff0c\u4f60\u4eec\u4e24\u4e24\u5bb6\u4e5f\u548c\u89e3\u4e86\u5427\uff01\u201d\u571f \u53f8\u592b\u4eba\u9893\u7136\u5750\u5728\u5730\u4e0a\uff0c\u65e0\u8a00\u4ee5\u5e94\u3002\u9648\u5929\u5b87\u89c1\u5df2\u6709\u6d1b\u73e0\u51fa\u5934\uff0c\u5fc3\u4e2d\u4f24\u75db\uff0c\u4e0d\u613f\u518d\u7559\uff0c\u7275\u7740\u5e7d\u840d \u7684\u624b\u6084\u6084\u9000\u51fa\u3002\u571f\u53f8\u592b\u4eba\u7684\u54ed\u58f0\u5df2\u6b62\uff0c\u8fd9\u65f6\u5374\u8f6e\u5230\u6851\u58c1\u4f0a\u75db\u54ed\u8d77\u6765\uff0c\u5979\u4ec0\u4e48\u90fd\u7edd\u671b\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9001\u8d70\u4e86\u9648\u5929\u5b87\u4e4b\u540e\uff0c\u4e00\u591c\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u542c\u8bf4\u5916\u9762\u85cf\u5175\u7684\u6b65\u54e8\u5df2\u7ecf\u64a4 \u9664\uff0c\u6b63\u5728\u60ca\u8be7\uff0c\u5ffd\u62a5\u9648\u5929\u5b87\u548c\u4e24\u4e2a\u5973\u5b50\u5df2\u56de\u5230\u5916\u9762\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5947\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u56de\u6765\u4e86\uff1f\u6709\u53d7\u4f24\u4e48\uff1f\u201d\u8fdb\u6765\u7980\u62a5\u7684\u6208\u4ec0\u7b11\u9053\uff1a\u201c\u516c\u5b50\u7684\u7cbe \u795e\u6bd4\u6628\u5929\u8fd8\u8981\u597d\u5f97\u591a\uff0c\u54ea\u4f1a\u53d7\u4f24\u3002\u201d\u5510\u7ecf\u5929\u6025\u5fd9\u51fa\u53bb\u8fce\u63a5\uff0c\u9aa4\u7136\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u4e3b \u4ec6\uff0c\u624b\u633d\u7740\u624b\uff0c\u548c\u9648\u5929\u5b87\u4e00\u9053\uff0c\u5e76\u80a9\u8d70\u8fdb\u8859\u95e8\uff0c\u4e09\u4e2a\u4eba\u90fd\u662f\u7709\u5f00\u773c\u7b11\uff0c\u559c\u6c14\u6d0b\u6d0b\u3002\u5510\u7ecf\u5929\u8fd9 \u51e0\u5929\u6765\u4e3a\u4e86\u5e94\u4ed8\u56f4\u653b\uff0c\u8863\u4e0d\u89e3\u5e26\uff0c\u7761\u4e0d\u5b89\u6795\uff0c\u8fd9\u65f6\u5ffd\u7136\u89c1\u7740\u51b0\u5ddd\u5929\u5973\u7684\u7b11\u5bb9\uff0c\u5c31\u50cf\u5728\u9709\u96e8\u7684 \u5b63\u8282\uff0c\u9aa4\u7136\u89c1\u7740\u707f\u70c2\u7684\u9633\u5149\u4e00\u6837\uff0c\u6ee1\u5927\u7684\u9634\u9706\u90fd\u626b\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u5927\u559c\u53eb\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60 \u600e\u4e48\u73b0\u5728\u624d\u6765\u5475\uff1f\u5929\u5b87\uff0c\u5916\u8fb9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4f60\u4e3a\u4f55\u4e0d\u53bb\u62c9\u8428\uff1f\u201d\u4ed6\u540c\u65f6\u5411\u4e24\u4eba\u53d1\u95ee\uff0c\u773c\u775b\u5374 \u5c3d\u779f\u7740\u51b0\u5ddd\u5929\u5973\u3002\u5e7d\u840d\u7b11\u5f97\u5f2f\u4e0b\u4e86\u8170\uff0c\u6446\u8131\u4e86\u51b0\u5ddd\u5929\u5973\u7275\u7740\u5979\u7684\u624b\uff0c\u63a8\u4e86\u9648\u5929\u5b87\u4e00\u628a\uff0c\u5728\u4ed6 \u8033\u8fb9\u6084\u6084\u7b11\u9053\uff1a\u201c\u50bb\u5b50\uff0c\u8fd8\u7528\u5f97\u7740\u4f60\u7b54\u8bdd\u4e48\uff1f\u8d76\u5feb\u8eb2\u5f00\uff0c\u8ba9\u4ed6\u4eec\u4e8c\u4eba\u7545\u53d9\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u65e0\u987b\u5230\u62c9\u8428\u4e86\u3002\u201d\u5c06\u4e8b\u60c5\u7ecf\u8fc7\u64ae\u8981\u8bf4\u4e86\u4e00\u904d\uff0c\u5510\u7ecf\u5929\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u4e8b \u60c5\u7adf\u7136\u89e3\u51b3\u5f97\u5982\u6b64\u5bb9\u6613\uff0c\u559c\u4e0d\u81ea\u7981\u5730\u62c9\u7740\u51b0\u5ddd\u5929\u5973\u7684\u624b\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u771f\u50cf\u5929\u4e0a\u7684\u795e \u4ed9\uff0c\u4e00\u624b\u62e8\u5f00\u4e91\u96fe\uff0c\u7acb\u523b\u73b0\u51fa\u6674\u5929\u6765\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9762\u4e0a\u4e00\u7ea2\uff0c\u5077\u5077\u63a8\u5f00\u5510\u7ecf\u5929\u7684\u624b\uff0c\u9053\uff1a \u201c\u4f60\u8fd8\u8bf4\u5462\uff0c\u6211\u73b0\u5728\u6b63\u70e6\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u8f7b\u8f7b\u54fc\u7740\u65b0\u7586\u7684\u6c11\u6b4c\uff1a\u201c\u7eb5\u6709\u4e9b\u5fc3\u5e95\u7684\u6101\u70e6\uff0c\u4e5f\u53ea\u50cf\u6de1\u4e91\u906e\u76d6\u7740\u71c3\u70e7\u7684\u592a\u9633\u3002\u201d \u4ed6\u8fd8\u4ee5\u4e3a\u51b0\u5ddd\u592d\u5973\u662f\u6545\u610f\u5938\u5f20\uff0c\u51dd\u7738\u775b\uff0c\u51b0\u5ddd\u5929\u5973\u53cc\u7709\u6df1\u9501\uff0c\u4e0d\u50cf\u6492\u5a07\uff0c\u4e5f\u4e0d\u50cf\u8bf4\u7b11\u3002\u5510\u7ecf \u5929\u9053\uff1a\u201c\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5f25\u5929\u7684\u5927\u7978\u90fd\u5df2\u6d88\u9664\uff0c\u8fd8\u6709\u4ec0\u4e48\u503c\u5f97\u6101\u95f7\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u9634\u4e91\u8fd8 \u672a\u5439\u5f97\u51c0\u6563\u5462\uff0c\u4f60\u8d76\u5feb\u66ff\u6211\u51fa\u51fa\u4e3b\u610f\u601d\u3002\u201d\u5c06\u89c1\u5230\u4e86\u8001\u753b\u5e08\u989d\u90fd\uff0c\u4ee5\u53ca\u989d\u90fd\u544a\u8bc9\u5979\u7684\u3001\u5c3c\u6cca \u5c14\u56fd\u738b\u5c31\u8981\u51fa\u5175\u7684\u4e8b\u60c5\u544a\u8bc9\u4e86\u5510\u7ecf\u5929\u3002\u5510\u7ecf\u5929\u60f3\u4e0d\u5230\u6709\u8fd9\u6837\u7a81\u5982\u5176\u6765\u7684\u98ce\u6ce2\uff0c\u9762\u8272\u53d8\u5f97\u6c89\u91cd \u8d77\u6765\uff0c\u6c89\u601d\u534a\u664c\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u719f\u8bfb\u4f5b\u4e66\uff0c\u96be\u9053\u4e0d\u77e5\u9053\u4f5b\u7956\u5272\u8089\u5582\u9e70\uff0c\u820d\u8eab\u6551\u864e\u7684\u6545 \u4e8b\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u6120\u9053\uff1a\u201c\u4f60\u5fcd\u5fc3\u6559\u6211\u4e0b\u5ac1\u5c3c\u6cca\u5c14\u7684\u56fd\u738b\u4e48\uff1f\u201d\u8bed\u6c14\u4e4b\u95f4\uff0c\u7231\u6068\u4ea4\u5e76\uff0c\u771f\u60c5\u6d41 \u9732\u3002\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u6211\u5c82\u662f\u6559\u4f60\u4e0b\u5ac1\u66b4\u541b\uff1f\u6211\u662f\u529d\u4f60\u4e0d\u8f9e\u8270\u9669\uff0c\u5c31\u5f53\u4f60\u5230\u5730\u72f1\u53bb\u8d70\u4e00\u906d\uff0c\u7d22 \u6027\u53bb\u89c1\u90a3\u4e2a\u66b4\u541b\uff0c\u4e00\u6765\u6253\u6d88\u4ed6\u7684\u5984\u5ff5\uff0c\u4e8c\u6765\u4e5f\u597d\u76f8\u673a\u884c\u4e8b\uff0c\u6216\u8005\u611f\u5316\u4ed6\u5bfc\u4ed6\u5411\u5584\uff0c\u6216\u8005\u9664\u4e86 \u4ed6\u53e6\u7acb\u65b0\u541b\uff0c\u8fd9\u4e5f\u662f\u4e00\u573a\u5927\u529f\u5fb7\u5440\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u66fe\u53d1\u8a93\u4e0d\u56de\u6bcd\u56fd\uff0c\u518d\u8bf4\u53bb\u4e5f\u672a \u5fc5\u6709\u4ec0\u4e48\u6548\u679c\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e16\u4e8b\u6ca7\u6851\uff0c\u4eba\u4e8b\u96be\u6599\u3002\u4f60\u4ee5\u524d\u53c8\u4f55\u66fe\u60f3\u5230\u51b0\u5cf0\u4f1a\u5012\uff0c\u4f60\u4e5f\u7ec8 \u4e8e\u62db\u60f9\u5c18\u4e16\u7684\u9ebb\u70e6\uff1f\u4f60\u8fd9\u6b21\u5954\u6ce2\u6570\u5730\uff0c\u6d88\u9975\u4e86\u897f\u85cf\u7684\u6218\u7978\uff0c\u8fd9\u6837\u7684\u9ebb\u70e6\u4f60\u90fd\u4e0d\u6015\uff0c\u8fd8\u6015\u4ec0\u4e48 \u9ebb\u70e6\uff1f\u201d\u5176\u5b9e\u51b0\u5ddd\u5929\u5973\u672c\u6765\u5df2\u6709\u8fd9\u4e2a\u610f\u601d\uff0c\u5f97\u5230\u5510\u7ecf\u5929\u4e00\u529d\uff0c\u5fc3\u610f\u7acb\u51b3\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u90a3\u4e48 \u6211\u8981\u4f60\u548c\u6211\u4e00\u540c\u53bb\uff01\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u54b1\u4eec\u7a0d\u606f\u4e24\u5929\uff0c\u5148\u5230\u62c9\u8428\u53bb\u89c1\u798f\u5eb7 \u5b89\uff0c\u7136\u540e\u5230\u8fb9\u5883\u53bb\u2018\u8fce\u63a5\u2019\u90a3\u4f4d\u66b4\u541b\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5728\u51b0\u5bab\u4e4b\u65f6\uff0c\u4e25\u82e5\u4e0d\u98df\u70df\u706b\u7684\u4ed9\u5973\uff0c\u5168\u4e0d\u7406\u4f1a\u5c18\u4e16\u4e4b\u4e8b\uff0c\u4e0b\u5c71\u4e4b\u540e\uff0c\u6e10\u6e10\u7531\u51fa \u4e16\u800c\u201c\u5165\u4e16\u201d\uff0c\u6027\u60c5\u548c\u5510\u7ecf\u5929\u4e5f\u6e10\u6e10\u66f4\u4e3a\u63a5\u8fd1\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u5728\u5ba3\u6170\u4f7f\u5e9c\u8859\u7684\u82b1\u56ed\u4e2d\u5f98\u5f8a\u6f2b\u6b65\uff0c\u9685\u54bd\u7ec6\u8bed\uff0c\u8bf4\u8d77\u4ee5\u524d\u7684\u79cd\u79cd\u8bef\u4f1a\uff0c\u90fd\u4e0d\u7981\u54d1\u7136\u5931 \u7b11\u3002\u8fd9\u4e9b\u8bef\u4f1a\uff0c\u5927\u534a\u662f\u56e0\u4e3a\u6709\u91d1\u4e16\u9057\u7a7f\u63d2\u5176\u95f4\u800c\u5f15\u8d77\u7684\u3002\u5510\u7ecf\u5929\u8c08\u8bf4\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u771f \u662f\u96be\u4ee5\u731c\u6d4b\uff0c\u6211\u4ee5\u524d\u5bf9\u4ed6\u8ba8\u538c\u4e4b\u6781\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u4eca\u6b21\u5374\u5e2e\u4e86\u6211\u548c\u5929\u5b87\u7684\u4e00\u4e2a\u5927\u5fd9\u3002\u4fc4\u9a6c\u767b\u672c \u6765\u662f\u8981\u6355\u6349\u5929\u5b87\uff0c\u91d1\u4e16\u9057\u5374\u83ab\u540d\u5176\u5999\u5230\u6765\uff0c\u66ff\u5929\u5b87\u53bb\u89c1\u6cd5\u738b\uff0c\u4f60\u8bf4\u602a\u4e5f\u4e0d\u602a\uff1f\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4ed6\u51e0\u4e4e\u9001\u6389\u6027\u547d\u5462\uff0c\u6211\u521a\u624d\u5fd8\u8bb0\u5bf9\u4f60\u8bf4\uff0c\u6211\u5230\u5587\u561b\u5bab\u7684\u65f6\u5019\uff0c\u4ed6\u6b63\u5728\u548c\u767d \u6559\u6cd5\u738b\u5bf9\u638c\u3002\u201d\u5510\u7ecf\u5929\u542c\u4e86\u51b0\u5ddd\u5929\u5973\u7ec6\u8bf4\u5f53\u65f6\u7684\u60c5\u5f62\uff0c\u4e0d\u7981\u9a87\u7136\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5440\uff0c\u4ed6\u53ea\u6709 \u4e09\u5341\u516d\u5929\u7684\u6027\u547d\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u80af\u53d7\u4eba\u601c\u60af\uff0c\u62d2\u7edd\u522b\u4eba\u76f8\u6551\u3002\u771f\u662f\u5929\u4e0b\u7b2c\u4e00\u4e2a\u602a\u4eba\uff0c\u6211\u975e\u627e\u5230 \u4ed6\u4e0d\u80fd\u5b89\u5fc3\uff0c\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f \u3000\u3000\u91d1\u4e16\u9057\u90a3\u665a\u9003\u51fa\u4e86\u5587\u561b\u5bab\u540e\uff0c\u5fc3\u60c5\u6d51\u9968\uff0c\u4e00\u7247\u8ff7\u832b\uff0c\u6f2b\u65e0\u76ee\u7684\u5730\u51fa\u4e86\u8428\u8fea\u57ce\u95e8\uff0c\u5728\u65f7\u91ce \u5b50\u7136\u72ec\u884c\uff0c\u4e0d\u89c9\u9ed1\u591c\u6d88\u901d\uff0c\u7ea2\u65e5\u4ece\u4e1c\u65b9\u5347\u8d77\uff0c\u91d1\u4e16\u9057\u88ab\u6653\u98ce\u4e00\u5439\uff0c\u7a0d\u7a0d\u6e05\u9192\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a \u201c\u6211\u8be5\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u8be5\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u3002\u5ffd\u89c9\u53e3\u4e2d\u7126\u6e34\uff0c\u751a\u662f\u96be\u53d7\uff0c\u539f\u6765\u4ed6 \u88ab\u6cd5\u738b\u638c\u529b\u6240\u8feb\uff0c\u5f53\u65f6\u8fd0\u7528\u4e86\u5168\u8eab\u7cbe\u529b\u4e0e\u4e4b\u76f8\u6297\uff0c\u4f53\u4e2d\u6c34\u4efd\u6d88\u8017\u8fc7\u591a\uff0c\u5e78\u5f97\u51b0\u5ddd\u5929\u5973\u5c06\u4e24\u679a \u51b0\u9b44\u795e\u5f39\u9001\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u7528\u5947\u5bd2\u4e4b\u6c14\u5316\u89e3\u4e86\u4f53\u4e2d\u7684\u5947\u70ed\uff0c\u8fd9\u624d\u4e0d\u81f4\u5f15\u8d77\u5185\u706b\u711a\u8eab\uff0c\u53d8\u6210\u6b8b \u5e9f\u3002\u4f46\u51b0\u5f39\u5e76\u975e\u7075\u836f\uff0c\u6d88\u878d\u4e4b\u540e\uff0c\u53c8\u7ecf\u8fc7\u4e86\u5927\u534a\u591c\u7684\u65f6\u95f4\uff0c\u6548\u7528\u5df2\u5931\uff0c\u800c\u4ed6\u7684\u4f53\u4e2d\u70ed\u6c14\uff0c\u8fd8 \u672a\u5b8c\u5168\u6d88\u9664\uff0c\u662f\u4ee5\u81ea\u7136\u611f\u5230\u7126\u6e34\u3002\u91d1\u4e16\u9057\u6cbf\u7740\u9a7f\u9053\u5954\u8dd1\uff0c\u90a3\u662f\u901a\u5f80\u62c9\u8428\u53bb\u7684\u5927\u8def\uff0c\u8d70\u4e0d\u591a \u4e45\uff0c\u89c1\u7740\u8def\u65c1\u6709\u5bb6\u9152\u8086\uff0c\u897f\u85cf\u5929\u6c14\u5bd2\u51b7\uff0c\u8def\u4e0a\u884c\u4eba\uff0c\u4e60\u60ef\u996e\u9152\u5fa1\u5bd2\uff0c\u6240\u4ee5\u5927\u8def\u4e0a\u6bcf\u9694\u5341\u6570\u91cc \u5c31\u6709\u9152\u8086\uff0c\u597d\u50cf\u6c5f\u5357\u7684\u8336\u4ead\u4e00\u6837\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8d70\u5165\u9152\u8086\uff0c\u7acb\u523b\u5524\u9152\u89e3\u559d\uff0c\u9152\u8086\u56db\u9762\u901a\u723d\uff0c\u91d1\u4e16\u9057\u9002\u624d\u5728\u8def\u4e0a\u5954\u8dd1\uff0c\u53cd\u800c\u6ca1\u6709\u7559 \u610f\u90ca\u91ce\u666f\u8272\uff0c\u8fd9\u65f6\u5750\u4e86\u4e0b\u6765\uff0c\u7a0d\u7a0d\u5e73\u9759\uff0c\u5411\u5916\u671b\u53bb\uff0c\u4f46\u89c1\u4e00\u7247\u65b0\u7eff\uff0c\u904d\u91ce\u65b0\u751f\u7684\u5ae9\u8349\u4e2d\u8fd8\u9690 \u7ea6\u53ef\u4ee5\u89c1\u7740\u51e0\u6735\u8c08\u9ec4\u8272\u7684\u5c0f\u82b1\uff0c\u90a3\u662f\u897f\u85cf\u51ac\u5b63\u8fc7\u540e\uff0c\u6700\u65e9\u5f00\u653e\u7684\u62a5\u6625\u82b1\u3002\u8fd9\u65f6\u662f\u4ef2\u6625\u4e8c\u6708\u7684 \u65f6\u8282\uff0c\u897f\u85cf\u7684\u6625\u5929\u6765\u5f97\u8fdf\uff0c\u6709\u4e9b\u6811\u6728\u67af\u9ec4\u7684\u6811\u53f6\u8fd8\u6ca1\u6709\u843d\u5c3d\u3002\u91d1\u4e16\u9057\u767e\u611f\u4ea4\u96c6\uff0c\u5ffd\u5730\u60f3\u9053\uff1a \u201c\u8349\u539f\u751f\u673a\u84ec\u52c3\uff0c\u800c\u6211\u5374\u50cf\u7eff\u8349\u4e2d\u67af\u9ec4\u7684\u6811\u53f6\u3002\u201d\u60b2\u4ece\u4e2d\u6765\uff0c\u51fb\u684c\u72c2\u6b4c\uff0c\u5531\u7684\u662f\u4ed6\u505a\u5c0f\u4e5e\u4e10 \u65f6\u5019\u4ece\u8001\u4e5e\u4e10\u5b66\u6765\u7684\u6c5f\u5357\u201c\u83b2\u82b1\u843d\u201d\u3002\u8fd9\u672c\u6765\u6c7e\u5c0f\u8c03\uff0c\u6292\u53d1\u4e5e\u4e10\u80f8\u4e2d\u7684\u6101\u90c1\u7684\uff0c\u5728\u4ed6\u53e3\u4e2d\u5531 \u51fa\u6765\uff0c\u5145\u6ee1\u4e86\u60b2\u82e6\u4e4b\u60c5\uff0c\u5374\u5982\u72c2\u6b4c\u5f53\u54ed\uff01\u9152\u4fdd\u5413\u4e86\u4e00\u8df3\uff0c\u53eb\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u9152\u6765\u4e86\u3002\u201d\u76db\u9152\u7684 \u662f\u4e00\u79cd\u957f\u9888\u7684\u9152\u693f\uff0c\u91d1\u4e16\u9057\u770b\u4e5f\u4e0d\u770b\uff0c\u628a\u9152\u74f6\u5728\u684c\u4e0a\u4e00\u6572\uff0c\u6572\u65ad\u74f6\u9888\uff0c\u5f20\u53e3\u4e00\u5438\uff0c\u9152\u5c31\u50cf\u55b7 \u6cc9\u7684\u6c34\u67f1\u4e00\u822c\uff0c\u88ab\u4ed6\u5012\u5165\u53e3\u4e2d\u3002\u9152\u4fdd\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u559d\u9152\u7684\u6cd5\u5b50\uff0c\u60ca\u5f97\u5446\u4e86\uff0c\u5ffd\u7136\u95f4\uff0c\u91d1\u4e16\u9057 \u5927\u53eb\u4e00\u58f0\uff0c\u98de\u8eab\u8df3\u8d77\uff0c\u597d\u50cf\u78b0\u5230\u4e86\u4ec0\u4e48\u602a\u5f02\u4e4b\u4e8b\uff0c \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u72c2\u6b4c\u5f53\u54ed\u8c01\u80fd\u89e3\uff0c\u5ffd\u89c1\u6545\u4eba\u5929\u5916\u6765\uff01 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u77ed\u68a6\u51e0\u65f6\u9192\u3000\u97f3\u4f20\u6d77\u5916\u3000\u5e7d\u60c5\u8c01\u53ef\u8bc9\u3000\u4eba\u6563\u8352\u539f \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u4e00\u56de \u77ed\u68a6\u51e0\u65f6\u9192\u3000\u97f3\u4f20\u6d77\u5916 \u5e7d\u60c5\u8c01\u53ef\u8bc9\u3000\u4eba\u6563\u8352\u539f \u3000\u3000\u4f60\u9053\u662f\u4ec0\u4e48\u4e8b\u60c5\u4ee4\u5f97\u91d1\u4e16\u9057\u60ca\u8be7\u5982\u65af\uff1f\u539f\u6765\u5f53\u4ed6\u6572\u788e\u957f\u9888\u9152\u676f\uff0c\u9cb8\u541e\u72c2\u996e\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97 \u8f7b\u8f7b\u4e00\u54cd\uff0c\u7a81\u7136\u4f3c\u6709\u4e00\u5c0f\u7c92\u4e38\u836f\u4f3c\u7684\u4e1c\u897f\uff1b\u968f\u7740\u4ed6\u5438\u8d77\u6765\u7684\u9152\u67f1\uff0c\u4e00\u4e0b\u5b50\u51b2\u4eba\u4ed6\u7684\u53e3\u4e2d\uff0c\u7acb \u5982\u73e0\u8d70\u7389\u76d8\uff0c\u6ed1\u4e0b\u5589\u5499\u3002\u4e8b\u60c5\u6765\u5f97\u5927\u51fa\u610f\u5916\uff0c\u91d1\u4e16\u9057\u521a\u4e00\u60ca\u89c9\uff0c\u8981\u5410\u5df2\u6765\u4e0d\u53ca\u3002\u8bd5\u60f3\u91d1\u4e16\u9057 \u662f\u4f55\u7b49\u6b66\u529f\uff1f\u4ed6\u5777\u6697\u5668\u7684\u624b\u6cd5\u66f4\u662f\u72ec\u6b65\u5929\u4e0b\uff0c\u8fde\u56db\u5ddd\u7684\u6697\u5668\u4e16\u5bb6\u5510\u5bb6\u4e5f\u5360\u4e0d\u4e86\u4ed6\u7684\u4fbf\u5b9c\uff0c\u5c45 \u7136\u4f1a\u5728\u8fd9\u5c0f\u9152\u8086\u4e2d\u906d\u4eba\u6697\u7b97\uff0c\u4ed6\u7109\u80fd\u4e0d\u60ca\u8be7\u5f20\u60f6\uff1f \u3000\u3000\u4e00\u80a1\u51c9\u6c14\u76f4\u51b2\u4e39\u7530\uff0c\u7126\u6e34\u7acb\u523b\u6b62\u4e86\u3002\u91d1\u4e16\u9057\u53ea\u89c9\u5f97\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff0c\u6655\u7729\u3001\u8033\u9e23\u7b49\u7b49\u73b0 \u8c61\u4e5f\u7acb\u523b\u6d88\u6563\u4e86\u3002\u91d1\u4e16\u9057\u548c\u6cd5\u738b\u82e6\u6597\u534a\u591c\uff0c\u71ac\u4e86\u4e00\u665a\u6765\u7761\uff0c\u672c\u6765\u660f\u660f\u6c89\u6c89\uff0c\u8fd9\u65f6\uff0c\u773c\u775b\u4e5f\u4f3c \u7ed9\u6e05\u6668\u7684\u9732\u6c34\u6d17\u8fc7\u4e00\u6837\u6bd4\u524d\u66f4\u52a0\u660e\u4eae\uff0c\u795e\u667a\u4e5f\u6bd4\u524d\u6e05\u723d\uff0c\u770b\u6765\u90a3\u5e76\u4e0d\u662f\u6bd2\u836f\uff0c\u800c\u7adf\u662f\u4e00\u7c92\u7075 \u4e39\u3002\u91d1\u4e16\u9057\u731b\u7684\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u8d77\u51af\u7433\u66fe\u4e0e\u4ed6\u8c08\u8fc7\u5929\u5c71\u96ea\u83b2\u7684\u7075\u6548\uff0c\u83ab\u975e\u8fd9\u7adf\u662f\u5929\u5c71\u96ea\u83b2\u6240\u70ae \u5236\u7684\u78a7\u7075\u4e39\uff1f \u3000\u3000\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u54ea\u4f4d\u9ad8\u4eba\uff0c\u8d50\u6069\u60e0\uff0c\u8bf7\u6c42\u4e00\u89c1\u3002\u201d\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u9152\u8086\u7684\u56db\u9762\u7a97\u6237\uff0c\u89c2\u51fa \u4e24\u5f20\u9762\u5b54\uff0c\u53ef\u4e0d\u6b63\u662f\u51af\u7433\u6bcd\u5973\uff1f\u91d1\u4e16\u9057\u5c16\u53eb\u4e00\u58f0\uff0c\u987f\u65f6\u5446\u82e5\u6728\u9e21\u3002\u5510\u7ecf\u5929\u662f\u674e\u6c81\u6885\u7684\u8868\u5144\uff0c \u81ea\u5df1\u62d2\u7edd\u4e86\u5510\u7ecf\u5929\u7684\u6069\u60e0\uff0c\u7b26\u5510\u7ecf\u5929\u9001\u7ed9\u81ea\u5df1\u7684\u78a7\u7075\u4e39\u8fde\u74f6\u63b7\u56de\uff0c\u5374\u7ec8\u4e8e\u8fd8\u662f\u670d\u4e86\u4ed6\u7684\u78a7\u8868 \u4e39\uff0c\u867d\u8bf4\u90a3\u662f\u5510\u7ecf\u5929\u7684\u59e8\u6bcd\u51af\u7433\u9001\u6765\u7684\u4e1c\u897f\uff0c\u5f3a\u7eb3\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u4f46\u90a3\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u8fd8\u4e0d\u662f \u5929\u5c71\u6d3e\u7684\u4e39\uff1f\u8fd8\u4e0d\u662f\u7b49\u4e8e\u95f4\u63a5\u63a5\u53d7\u4e86\u5510\u7ecf\u5929\u7684\u201c\u6069\u60e0\u201c\uff1f\u91d1\u4e16\u9057\u4e00\u5fc3\u8981\u548c\u5510\u7ecf\u5929\u8d4c\u4e86\u53e3\u6c14\uff0c \u53ea\u60f3\u8ba9\u4ed6\u53d7\u81ea\u5df1\u7684\u201c\u6069\u60e0\u201d\uff0c\u81ea\u5df1\u600e\u80af\u53d7\u4ed6\u6069\u60e0\uff0c\u54ea\u77e5\u4e00\u6597\u6cd5\u738b\uff0c\u51e0\u4e4e\u9001\u547d\uff0c\u662f\u51b0\u5ddd\u5929\u5973\u6551 \u4e86\u4ed6\uff0c\u73b0\u5728\u53c8\u662f\u51af\u7433\u9001\u6765\u7684\u78a7\u7075\u4e39\uff0c\u8ba9\u81ea\u5df1\u6062\u590d\u4e86\u88ab\u6cd5\u738b\u5185\u529b\u5206\u9694\u7684\u5143\u6c14\uff0c\u800c\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f \u4e0e\u5510\u7ecf\u5929\u5173\u7cfb\u6700\u5bc6\u5207\u7684\u4eba\u3002\u91d1\u4e16\u9057\u81ea\u89c9\u81ea\u5c0a\u5fc3\u53d7\u4e86\u635f\u5bb3\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5fc3\u5ff5\u767e\u8f6c\uff0c\u7a97\u5916\u674e\u6c81\u6885 \u6b63\u5728\u7528\u624b\u6307\u522e\u8138\uff0c\u8fd8\u662f\u4ece\u524d\u90a3\u526f\u5a07\u5b64\u7684\u987d\u76ae\u7684\u795e\u6001\uff0c\u674e\u6c81\u6885\u6b63\u5728\u7b49\u5f85\u4ed6\u62db\u547c\uff0c\u53ef\u662f\u91d1\u4e16\u9057\u5374 \u4f3c\u7ed9\u4eba\u5b9a\u7740\u4f3c\u7684\uff0c\u53e3\u5507\u98a4\u52a8\uff0c\u5374\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\u6765\uff01 \u3000\u3000\u5ffd\u5730\u7a97\u5916\u4eba\u5f71\u4e00\u6643\uff0c\u4f3c\u4e4e\u542c\u5f97\u51af\u7433\u4f4e\u58f0\u7684\u8bf4\u4e86\u4e00\u53e5\u4ec0\u4e48\u6d3b\uff0c\u4e24\u6bcd\u5973\u5ffd\u7136\u53c8\u4e0d\u89c1\u4e86\u3002\u91d1\u4e16 \u9057\u9893\u7136\u5750\u4e0b\uff0c\u7a81\u7136\u540e\u6094\u8d77\u6765\uff0c\u60f3\u8d77\u674e\u6c81\u6885\u548c\u4ed6\u521d\u89c1\u9762\u65f6\u548c\u4ed6\u8bf4\u7684\u8bdd\uff0c\u90a3\u65f6\u4ed6\u6b63\u5728\u5ce8\u5d4b\u5c71\u620f\u5f04 \u91ce\u7334\uff0c\u5bf9\u4ed6\u8bf4\u7684\u8bdd\u662f\uff1a\u201c\u4f60\u5bf9\u5b83\u597d\uff0c\u5b83\u5c31\u5bf9\u4f60\u597d\uff1b\u4f60\u8981\u662f\u6b3a\u4fae\u5b83\uff0c\u5b83\u5c31\u4e0d\u548c\u4f60\u505a\u670b\u53cb\uff0c\u4f60\u600e \u4e48\u8fd9\u70b9\u9053\u7406\u4e5f\u4e0d\u61c2\u5475\uff01\u201d\u5f53\u65f6\u4e0d\u89c9\u600e\u7684\uff0c\u73b0\u5728\u60f3\u6765\u5374\u662f\u5927\u6709\u54f2\u7406\uff0c\u674e\u6c81\u6885\u8bf4\u7684\u662f\u7334\u5b50\uff0c\u4f46\u4f55 \u5c1d\u4e0d\u662f\u4eba\uff1f\u96be\u9053\u4e16\u4eba\u4e4b\u5bf9\u81ea\u5df1\u51b7\u6de1\uff0c\u7adf\u662f\u81ea\u53d6\u5176\u548e\u4e48\uff1f\u81ea\u5df1\u5076\u7136\u505a\u4e86\u597d\u4e8b\uff0c\u66ff\u9648\u5929\u5b87\u53bb\u5192\u9669 \u72af\u96be\uff0c\u4ed6\u4eec\u5c31\u8fd9\u6837\u7684\u5173\u5fc3\u81ea\u5df1\uff0c\u6551\u81ea\u5df1\uff1f\u83ab\u975e\u8fd9\u4e2a\u4e16\u754c\u5e76\u975e\u81ea\u5df1\u542c\u60f3\u50cf\u7684\u90a3\u6837\u201c\u51b0\u51b7\u201d\uff1f\u83ab \u975e\u9519\u7684\u7adf\u662f\u81ea\u5df1\u4e0d\u6210\uff1f \u3000\u3000\u9152\u4fdd\u4ece\u672a\u89c1\u8fc7\u6709\u5982\u6b64\u5947\u602a\u7684\u996e\u5ba2\uff0c\u5b9a\u4e86\u795e\u770b\u7740\u91d1\u4e16\u9057\uff0c\u51af\u7433\u6bcd\u5973\u7684\u8e2a\u8ff9\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u53d1 \u89c9\u3002\u53ea\u89c1\u91d1\u4e16\u9057\u9893\u7136\u5750\u4e0b\uff0c\u5c06\u534a\u8fb9\u9762\u8f6c\u5411\u7a97\u5916\uff0c\u8461\u8404\u7f8e\u9152\u6cfc\u4e86\u6ee1\u5730\uff0c\u4ed6\u4e5f\u4e1d\u6beb\u4e0d\u776c\uff0c\u770b\u6837\u5b50 \u7adf\u662f\u5446\u4e86\u3002\u9152\u4fdd\u5fc3\u4e2d\u9a87\u6015\uff0c\u8f7b\u58f0\u95ee\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u8fd8\u8981\u9152\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u5446\u5446\u7684\u51ed\u7a97\u9065\u671b\uff0c\u7adf\u4f3c \u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u9152\u4fdd\u5fc3\u4e2d\u4e03\u4e0a\u516b\u4e0b\uff0c\u751f\u6015\u9152\u94b1\u6ca1\u6709\u7740\u843d\uff0c\u4f46\u91d1\u4e16\u9057\u795e\u6c14\u9a87\u4eba\uff0c\u9152\u4fdd\u7ed9 \u4ed6\u5413\u7684\u4e0d\u6562\u518d\u95ee\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6b64\u9645\u5fc3\u4e2d\u70e6\u4e71\u4e4b\u6781\uff0c\u9661\u7136\u89c9\u5f97\u8fd9\u4e2a\u4e16\u754c\u4f3c\u4e4e\u4e0e\u4ed6\u63a5\u8fd1\u4e86\u5374\u53c8\u90a3\u6837\u964c\u751f\uff0c\u4ed6\u8bb0\u8d77\u4e86 \u4eba\u4e16\u7684\u51b7\u9177\u4e5f\u8bb0\u8d77\u4e86\u4eba\u4e16\u7684\u6e29\u6696\uff0c\u4ed6\u7684\u7236\u4eb2\u3001\u5e7c\u5e74\u4e4b\u65f6\u66fe\u5077\u8fc7\u756a\u85af\u7ed9\u4ed6\u5403\u7684\u8001\u4e5e\u4e10\u3001\u7b2c\u4e00\u4e2a \u5c06\u4ed6\u5f53\u4f5c\u670b\u53cb\u770b\u5f85\u7684\u51b0\u5ddd\u5929\u5973\u4ee5\u53ca\u521a\u521a\u8d70\u6389\u7684\u987d\u76ae\u800c\u53c8\u5a07\u61a8\u7684\u674e\u6c81\u6885\uff0c\u8fd9\u4e9b\u4eba\u7269\u7684\u5f71\u5b50\u4e00\u4e00 \u4ece\u4ed6\u5fc3\u4e0a\u98d8\u8fc7\uff0c\u597d\u50cf\u4ed6\u6240\u719f\u6089\u7684\u6c34\u4e0a\u7684\u6d6e\u840d\uff0c\u968f\u7740\u6eda\u6eda\u6ce2\u6d9b\u4e1c\u53bb\uff0c\u6c38\u4e0d\u56de\u5934\uff1b\u4f46\u4ed6\u5bf9\u6d6e\u840d\u65e0 \u6240\u7275\u5ff5\uff0c\u800c\u8fd9\u4e9b\u4eba\u7269\u867d\u7136\u5728\u4ed6\u7684\u751f\u547d\u4e2d\u5360\u77ed\u77ed\u7684\u65f6\u523b\uff0c\u5374\u4ee4\u4ed6\u6c38\u4e0d\u80fd\u5fd8\u3002\u4ed6\u53c8\u9661\u7136\u60f3\u8d77\u81ea\u5df1 \u7684\u751f\u547d\u5373\u5c06\u50cf\u7a97\u5916\u90a3\u67af\u9ec4\u7684\u6811\u53f6\uff0c\u8fd9\u4e9b\u4eba\u90fd\u4e0d\u80fd\u518d\u89c1\u4e86\uff0c\u4e0d\u89c9\u767e\u611f\u4ea4\u96c6\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad \u7edd\uff01\u4ed6\u771f\u7684\u60f3\u8ffd\u51fa\u53bb\u5524\u674e\u6c81\u6885\uff0c\u4f46\u5979\u4eec\u7684\u5f71\u5b50\u65e9\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u95e8\u5916\u6709\u811a\u6b65\u58f0\u8d70\u6765\uff0c\u91d1\u4e16\u9057\u5982\u9189\u5982\u75f4\uff0c\u770b\u7740\u7a97\u5916\u7684\u5e7f\u9614\u7684\u539f\u91ce\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u7559\u610f\u3002\u5ffd\u542c \u5f97\u6709\u4e00\u4e2a\u4f3c\u66fe\u76f8\u8bc6\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8981\u4e00\u676f\u9a6c\u5976\u9152\u3002\u201d\u53e6\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u64a4\u5a07\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u6211 \u4e0d\u8981\u5473\u9053\u9178\u7684\u9a6c\u5976\u9152\uff0c\u6211\u8981\u751c\u751c\u7684\u8461\u8404\u9152\u3002\u201d\u8fd9\u58f0\u97f3\u4e5f\u4f3c\u5728\u54ea\u513f\u542c\u8fc7\u7684\uff0c\u91d1\u4e16\u9057\u731b\u7684\u56de\u8fc7\u5934 \u6765\uff0c\u4e0e\u90a3\u4e24\u4e2a\u6bcd\u5973\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c\u90a3\u5c11\u5973\u5ffd\u7684\u9000\u540e\u4e09\u6b65\uff0c\u7741\u5927\u773c\u775b\uff0c\u9762\u8272\u6dae\u4e00\u4e0b\u53d8\u5f97\u7070\u767d\u5982 \u6b7b\uff01 \u3000\u3000\u91d1\u4e16\u9057\u6700\u521d\u8fd8\u4ee5\u4e3a\u662f\u51af\u7433\u6bcd\u5973\u56de\u6765\uff0c\u8c01\u77e5\u4e0d\u662f\u3002\u8fd9\u4e24\u6bcd\u5973\u4e43\u662f\u6768\u67f3\u9752\u548c\u5979\u7684\u5973\u513f\u90b9\u7edb \u971e\uff0c\u6768\u67f3\u9752\u6e34\u5ff5\u5510\u6653\u6f9c\uff0c\u90b9\u7edb\u971e\u4e5f\u60e6\u8bb0\u7740\u5510\u7ecf\u5929\uff0c\u56e0\u6b64\u4e24\u6bcd\u5973\u8fdc\u8d74\u56de\u7586\uff0c\u610f\u6b32\u4e0a\u5929\u5c71\u5bfb\u8bbf\u4ed6 \u4eec\uff0c\u5230\u4e86\u56de\u7586\uff0c\u78b0\u5230\u674e\u6cbb\uff0c\u624d\u77e5\u9053\u5510\u7ecf\u5929\u6b63\u5728\u897f\u85cf\uff0c\u800c\u5510\u6653\u6f9c\u4e5f\u56e0\u4e3a\u6302\u5ff5\u513f\u5b50\uff0c\u534a\u4e2a\u6708\u524d\u52a8 \u8eab\uff0c\u4e5f\u5230\u897f\u85cf\u53bb\u4e86\u3002\u56e0\u6b64\u6768\u67f3\u9752\u4e5f\u5e26\u7740\u5973\u513f\u8f6c\u5230\u897f\u85cf\u6765\uff0c\u5374\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u78b0\u5230\u4e86\u91d1\u4e16\u9057\u3002\u8fd9 \u65f6\u91d1\u4e16\u9057\u7a7f\u7684\u4e43\u662f\u9648\u5929\u5b87\u7684\u8863\u88f3\uff0c\u518d\u4e0d\u662f\u9ebb\u75af\u7684\u6253\u626e\u4e86\u3002\u5979\u4eec\u521a\u521a\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u8fd8\u4ee5\u4e3a\u662f\u8428 \u8fe6\u57ce\u4e2d\u8d35\u4ecb\u516c\u5b50\uff0c\u5230\u6548\u5916\u6625\u6e38\uff0c\u5728\u5c0f\u8086\u559d\u9152\uff0c\u54ea\u77e5\u770b\u6e05\u695a\u4e86\uff0c\u7adf\u7136\u662f\u66fe\u4ee4\u5979\u4eec\u5403\u8fc7\u5927\u4e8f\uff0c\u53c8 \u5bb3\u6015\u53c8\u6068\u7684\u201c\u6bd2\u624b\u75af\u4e10\u201d\uff01 \u3000\u3000\u91d1\u4e16\u9057\u5413\u5f97\u5979\u4eec\u9b42\u4e0d\u9644\u4f53\uff0c\u5c82\u77e5\u5979\u4eec\u4e5f\u5413\u8d70\u4e86\u51af\u7433\u6bcd\u5973\u3002\u539f\u6765\u51af\u7433\u5728\u5e74\u9752\u65f6\u5019\uff0c\u66fe\u5c61\u6b21 \u620f\u5f04\u6768\u67f3\u9752\uff0c\u6709\u4e00\u6b21\u751a\u81f3\u5047\u5192\u5979\u7684\u59d0\u59d0\u51af\u745b\uff0c\u7528\u98de\u5200\u524a\u53bb\u4e86\u6768\u67f3\u9752\u7684\u5934\u53d1\u3002\u6240\u4ee5\u51af\u7433\u8fdc\u8fdc\u89c1 \u5979\u8d70\u6765\uff0c\u5927\u611f\u5c34\u5c2c\uff0c\u4e0d\u597d\u610f\u601d\u548c\u5979\u76f8\u89c1\uff0c\u4fbf\u548c\u5973\u513f\u6084\u6084\u8eb2\u5f00\u3002\u8fd9\u539f\u56e0\u5979\u5973\u513f\u90fd\u4e0d\u77e5\u9053\uff0c\u91d1\u4e16 \u9057\u81ea\u7136\u66f4\u52a0\u83ab\u540d\u5176\u5999\u3002\u4ed6\u521a\u624d\u81ea\u6028\u81ea\u827e\uff0c\u8fd8\u4ee5\u4e3a\u51af\u7433\u6bcd\u5973\u662f\u8ba4\u4e3a\u4ed6\u65e0\u53ef\u6551\u836f\uff0c\u624d\u79bb\u5f00\u4ed6\u5462\uff01 \u3000\u3000\u90b9\u7edb\u971e\u6b63\u5728\u5411\u7740\u6bcd\u4eb2\u64a4\u5a07\uff0c\u5ffd\u7136\u53d1\u89c9\u90a3\u738b\u5b59\u516c\u5b50\u6a21\u6837\u7684\u996e\u9152\u7684\u4eba\u7adf\u7136\u662f\u6bd2\u624b\u75af\u4e10\u91d1\u4e16 \u9057\uff0c\u767b\u65f6\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u6768\u67f3\u9752\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u8bb0\u5f97\u4f60\u662f\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u7684\u5916\u5b59\u5973\u513f\uff01\u4e0d\u8981 \u7ed9\u4eba\u5c0f\u89c6\u4e86\uff01\u201d\u6768\u4ef2\u82f1\u662f\u51e0\u5341\u5e74\u524d\u5317\u4e94\u7701\u7684\u6b66\u6797\u9886\u8896\uff0c\u6768\u67f3\u9752\u4e00\u751f\u4ee5\u6b64\u81ea\u89c6\u4e3a\u540d\u95e8\u4e4b\u540e\uff0c\u6700 \u6015\u8fb1\u6ca1\u5bb6\u98ce\uff0c\u6768\u67f3\u9752\u867d\u7136\u660e\u77e5\u4e0d\u662f\u91d1\u4e16\u9057\u7684\u654c\u624b\uff0c\u4f46\u4ee5\u5979\u7684\u8eab\u4efd\uff0c\u600e\u80fd\u793a\u5f31\u9003\u4ea1\uff1f\u800c\u4e14\u5979\u4e5f \u89c1\u8bc6\u8fc7\u8fd9\u4e2a\u201c\u75af\u4e10\u201d\u7684\u201c\u6bd2\u624b\u201d\uff0c\u77e5\u9053\u82e5\u662f\u91d1\u4e16\u9057\u5b58\u5fc3\u8981\u4e0e\u5979\u4e3a\u96be\uff0c\u9003\u8d70\u4e5f\u9003\u4e0d\u8131\u3002\u4e0d\u5982\u51b3 \u5fc3\u4e00\u62fc\uff0c\u9759\u5f85\u4ed6\u7684\u53d1\u96be\u3002 \u3000\u3000\u82e5\u7136\u662f\u5728\u51e0\u5e74\u4e4b\u524d\uff0c\u91d1\u4e16\u9057\u542c\u5f97\u6768\u67f3\u9752\u5c06\u7236\u4eb2\u7684\u540d\u5934\u62ff\u51fa\u70ab\u8000\uff0c\u975e\u628a\u5979\u620f\u5f04\u4e2a\u591f\u4e0d\u53ef\uff01 \u7136\u800c\u6b64\u9645\uff0c\u91d1\u4e16\u9057\u975e\u4f46\u6ca1\u6709\u8fd9\u4e2a\u5fc3\u60c5\uff0c\u53cd\u800c\u5fc3\u4e2d\u611f\u5230\u6b49\u610f\uff0c\u60f3\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u5973\u5b69\u5b50\u672c\u6765\u662f\u5929 \u771f\u65e0\u90aa\u7684\uff0c\u548c\u6c81\u6885\u59b9\u59b9\u5dee\u4e0d\u591a\uff0c\u4e00\u89c1\u6211\u5374\u5413\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u8fd9\u90fd\u662f\u6211\u79cd\u4e0b\u7684\u5b7d\u679c\u3002\u5f04\u5f97\u4e16\u4eba\u90fd \u628a\u6211\u5f53\u4f5c\u602a\u7269\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u62e3\u4e86\u4e00\u4ed8\u5ea7\u5934\uff0c\u7275\u5973\u513f\u5750\u4e0b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u62ff\u4e24\u676f\u8461\u8404\u9152\u6765\uff01\u201d\u5c06\u5f39\u5f13\u53d6\u51fa\uff0c\u6446 \u5728\u684c\u4e0a\uff0c\u5979\u53e3\u4e2d\u867d\u8bf4\u4e0d\u5bb3\u6015\uff0c\u5fc3\u91cc\u5374\u662f\u5bb3\u6015\u5f97\u7d27\uff0c\u53d6\u51fa\u5f39\u5f13\uff0c\u5176\u5b9e\u81ea\u5df1\u58ee\u80c6\u800c\u5df2\uff0c\u90b9\u7edb\u971e\u53ea \u89c9\u6bcd\u4eb2\u7684\u624b\u6307\u5fae\u5fae\u53d1\u6296\uff0c\u8fde\u58f0\u97f3\u4e5f\u6709\u70b9\u53d8\u4e86\u3002\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u5fae\u5fae\u4e00\u7b11\uff0c\u5077\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u91d1\u4e16 \u9057\u6b63\u5728\u51ed\u680f\u559d\u9152\uff0c\u770b\u4e5f\u4e0d\u770b\u5979\u4eec\u3002 \u3000\u3000\u4e24\u6bcd\u5973\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5ffd\u89c1\u5916\u9762\u53c8\u6765\u4e86\u4e00\u4e2a\u4eba\uff0c\u5374\u662f\u4e2a\u4e66\u7ae5\u7684\u6253\u626e\uff0c\u80a9\u4e0a\u642d\u7740\u4e00\u4e2a\u8921\u88e2\uff08\u5f53 \u65f6\u6d41\u884c\u7684\u4e00\u79cd\u51fa\u8fdc\u95e8\u65c5\u884c\u7684\u80cc\u5305\uff09\u6ee1\u9762\u98ce\u5c18\u4e4b\u8272\uff0c\u4e0d\u8fc7\u5341\u4e03\u516b\u5c81\u7684\u5e74\u7eaa\uff0c\u795e\u60c5\u867d\u7136\u663e\u5f97\u9887\u4e3a \u52b3\u7d2f\uff0c\u9762\u4e0a\u5374\u662f\u7b11\u563b\u563b\u7684\uff0c\u4f3c\u4e4e\u6b63\u529e\u4e86\u4e00\u4ef6\u4ec0\u4e48\u5f97\u610f\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u8fd9\u4e66\u61c2\u4e00\u8fdb\u5e97\u95e8\uff0c\u4fbf\u628a\u8921\u88e2\u5f80\u684c\u4e0a\u4e00\u987f\uff0c\u81ea\u987e\u81ea\u7684\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u597d\u4e86\uff0c\u660e\u5929\u5c31\u53ef\u5230\u8428\u8fe6 \u5566\u3002\u9152\u4fdd\uff0c\u7ed9\u6211\u4e00\u676f\u51b0\u7684\u8461\u8404\u9152\u3002\u201d\u897f\u85cf\u5730\u65b9\uff0c\u5c71\u5cad\u4e0a\u957f\u5e74\u51b0\u96ea\u4e0d\u5316\uff0c\u4f46\u6bcf\u5230\u5348\u95f4\uff0c\u5e73\u5730\u5374 \u9177\u70ed\u4e0d\u582a\uff0c\u662f\u4ee5\u9152\u5e97\u4eba\u5bb6\u591a\u8d2e\u6709\u51b0\u96ea\u3002\u8fd9\u65f6\u867d\u672a\u8fd1\u5348\uff0c\u4f46\u90a3\u4e66\u7ae5\u957f\u9014\u8dcb\u6d89\uff0c\u70ed\u5f97\u76f4\u5598\u6c14\uff0c\u4ed6 \u62d6\u4e86\u4e00\u5f20\u6709\u7af9\u80cc\u7684\u9760\u6905\u8fc7\u6765\uff0c\u8eba\u4e0b\u53bb\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u9664\u4e0b\u811a\u4e0a\u7684\u8349\u978b\uff0c\u90b9\u7ee6\u971e\u9690\u7ea6\u95fb\u5230\u6709\u80a1\u81ed \u5473\uff0c\u539f\u6765\u90a3\u4e66\u61a7\u811a\u677f\u4e0a\u8d77\u4e86\u65e0\u6570\u6c34\u6ce1\uff0c\u4ed6\u6b63\u5728\u628a\u90a3\u4e9b\u6c34\u6ce1\u4e00\u4e2a\u4e2a\u7684\u5f04\u7834\uff0c\u95ed\u8d77\u773c\u775b\uff0c\u4eab\u53d7\u90a3 \u6293\u75d2\u7684\u6ecb\u5473\u3002\u90b9\u7ee6\u971e\u63a9\u7740\u9f3b\u5b50\uff0c\u6709\u70b9\u8ba8\u538c\uff0c\u4f46\u770b\u90a3\u4e66\u649e\u6ed1\u7a3d\u7684\u795e\u60c5\uff0c\u82e5\u4e0d\u662f\u5979\u5fc3\u4e2d\u6709\u4e8b\uff0c\u51e0 \u4e4e\u8981\u53d1\u51fa\u7b11\u6765\u3002 \u3000\u3000\u9152\u4fdd\u62ff\u4e86\u4e00\u676f\u5f00\u4e86\u53e3\u7684\u8461\u8404\u9152\u7ed9\u4ed6\uff0c\u4e0a\u9762\u6709\u51e0\u7247\u6d6e\u6c34\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u76d8\u788e\u51b0\u5757\uff0c\u662f\u51c6\u5907\u7ed9 \u4ed6\u52a0\u7528\u7684\u3002\u90a3\u4e66\u7ae5\u559d\u4e86\u4e00\u53e3\uff0c\u5927\u53eb\u9053\uff1a\u201c\u597d\u8212\u670d\uff0c\u5317\u4eac\u7684\u7687\u5e1d\u8001\u513f\u5bb6\u53a8\u6240\u917f\u7684\u5fa1\u9152\u4e5f\u6ca1\u6709\u8fd9 \u4e2a\u6627\u9053\uff01\u201d\u773c\u5149\u4e00\u626b\uff0c\u5ffd\u7136\u671d\u6768\u67f3\u9752\u6bcd\u5973\u8fd9\u8fb9\u7b11\u563b\u563b\u7684\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u90b9\u7edb\u971e\u6014\u4e86\u4e00\u4e0b\uff0c\u53ea\u89c1\u90a3\u4e66\u61a7\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u61c2\u559d\u9152\uff0c\u8461\u8404\u9152\u51b2\u6c34\u559d\u8fd8\u6709\u4ec0\u4e48\u5473 \u513f\uff1f\u5c0f\u59d1\u5a18\uff0c\u8fde\u8461\u8404\u9152\u4f60\u90fd\u6015\u9152\u5473\u6d53\u4e48\uff1f\u55ef\uff0c\u6211\u6765\u6559\u4f60\uff0c\u6015\u9152\u5473\u6d53\u52a0\u4e00\u70b9\u51b0\u5757\u8fdb\u53bb\uff0c\u559d\u8d77\u6765 \u53c8\u51c9\u5feb\u53c8\u8212\u670d\u3002\u201d\u6768\u67f3\u9752\u76b1\u76b1\u7709\u5934\uff0c\u5fc3\u4e2d\u70e6\u8e81\u4e4b\u6781\uff0c\u4f46\u5979\u987e\u5fcc\u7740\u91d1\u4e16\u9057\u5728\u65c1\uff0c\u4e0d\u613f\u591a\u4e8b\uff0c\u53ea \u662f\u6a2a\u4e86\u90a3\u5c0f\u4e66\u7ae5\u4e00\u773c\uff0c\u90a3\u5c0f\u4e66\u7ae5\u4e0d\u77e5\u8fdb\u9000\uff0c\u89c1\u5979\u4eec\u4e0d\u7b54\u7406\uff0c\u7adf\u4ece\u81ea\u5df1\u7684\u684c\u5b50\u4e0a\u6367\u4e86\u90a3\u76d8\u788e\u51b0 \u8fc7\u6765\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u6211\u4e0d\u9a97\u4f60\uff0c\u52a0\u4e00\u70b9\u51b0\u8bd5\u8bd5\u770b\u3002\u201d\u6293\u8d77\u4e00\u5757\u788e\u51b0\uff0c\u5c31\u5f80\u90b9\u7edb\u971e\u7684\u9152\u676f\u91cc \u4e22\u3002\u4ed6\u8dcb\u6d89\u957f\u9014\uff0c\u8fdb\u5e97\u540e\u672a\u6d17\u8fc7\u624b\uff0c\u6307\u7532\u4e0a\u585e\u6ee1\u57a2\uff0c\u90b9\u7edb\u971e\u5927\u4e3a\u607c\u6012\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u9a82\u9053\uff1a \u201c\u8c01\u8981\u4f60\u591a\u7ba1\u95f2\u4e8b\uff01\u201d\u624b\u6307\u4e00\u5f39\uff0c\u5c06\u4e24\u9897\u80e1\u6843\u6838\u5f39\u51fa\u53bb\uff0c\u8fd9\u4e00\u5f39\u6b63\u662f\u6768\u5bb6\u7684\u795e\u5f39\u5999\u6280\uff0c\u535c\u535c \u4e24\u54cd\uff0c\u5206\u522b\u6253\u4e2d\u4e86\u4e66\u61a7\u4e24\u80c1\u7684\u8f6f\u9ebb\u7a74\uff0c\u90a3\u4e66\u61a7\u54ce\u54df\u4e00\u58f0\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u4e00\u76d8\u788e\u51b0\u90fd\u6cfc\u7ffb\u4e86\uff0c\u51b0 \u6c34\u6e85\u4e86\u90b9\u7edb\u971e\u4e00\u9762\uff0c\u4e24\u4eba\u90fd\u662f\u5927\u4e3a\u72fc\u72c8\u3002\u4e66\u61a7\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u6b22\u559c\u8c03\u51b0\u4e3a\u4f55\u4e0d\u5bf9\u6211\u65e9\u8bf4\uff1f\u771f\u662f \u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\u3002\u54fc\uff0c\u6211\u5bb6\u516c\u5b50\u90fd\u6ca1\u6709\u4f60\u8fd9\u4f4d\u5c0f\u59d0\u96be\u4f3a\u5019\uff01\u201d\u90b9\u7edb\u971e\u6da8\u7ea2\u4e86\u8138\uff0c\u65a5 \u9053\uff1a\u201c\u8c01\u8981\u4f60\u4f3a\u5019\uff1f\u201d\u53cd\u624b\u4e00\u638c\uff0c\u5c31\u60f3\u63b4\u90a3\u4e66\u61c2\uff0c\u5374\u88ab\u5979\u6bcd\u4eb2\u4e00\u628a\u62c9\u4f4f\u3002\u6768\u67f3\u9752\u5fc3\u4e2d\u60ca\u7591\u4e0d \u5b9a\uff0c\u4e24\u80c1\u7684\u8f6f\u9ebb\u7a74\u662f\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\u4e4b\u4e00\uff0c\u6b66\u529f\u591a\u597d\u88ab\u6253\u4e2d\u4e86\u4e5f\u4e0d\u80fd\u52a8\u5f39\uff0c\u96be\u9053\u8fd9\u4e66\u7ae5\u7adf \u7ec3\u6709\u90aa\u95e8\u7684\u95ed\u7a74\u529f\u592b\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u54c8\u54c8\u4e00\u7b11\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6768\u67f3\u9752\u5403\u4e86\u4e00\u60ca\uff0c\u4f38\u9646\u7684\u624b\u53c8\u7f29\u4e86\u53e3\u6765\uff0c\u6293\u8d77\u684c\u4e0a \u7684\u5f39\u5f13\uff0c\u53ea\u6240\u5f97\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u4f60\u8fd9\u559d\u9152\u7684\u6cd5\u513f\u5f88\u5999\uff0c\u9152\u4fdd\uff0c\u7ed9\u6211\u4e5f\u62ff\u4e00\u76d8\u788e\u51b0 \u6765\u3002\u201d\u4e66\u7ae5\u542c\u5f97\u91d1\u4e16\u9057\u53eb\u4ed6\uff0c\u8f6c\u8fc7\u4e86\u8eab\u53bb\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u5927\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u6069\u516c\u5728\u6b64\uff0c\u9093 \u5929\u6211\u8fd8\u6ca1\u6709\u5411\u4f60\u9053\u8c22\u5462\uff0c\u4f60\u600e\u4e48\u4e5f\u5230\u8fd9\u513f\u6765\u4e86\uff1f\u54c8\uff0c\u6211\u8bf7\u4f60\u559d\u9152\uff0c\u65e0\u7269\u76f8\u8c22\uff0c\u4e00\u676f\u8584\u9152\uff0c\u8868 \u8868\u5fc3\u610f\uff0c\u6069\u516c\uff0c\u4f60\u53ef\u522b\u63a8\u8f9e\u4e86\uff01\u55ef\uff0c\u4f60\u770b\u6211\u591a\u7cca\u6d82\uff0c\u4f60\u6551\u4e86\u6211\uff0c\u6211\u8fd8\u6ca1\u8bf7\u6559\u4f60\u7684\u9ad8\u59d3\u5927\u540d \u5462\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4f60\u662f\u9648\u5929\u5b87\u90a3\u4e2a\u591a\u5634\u7684\u4e66\u7ae5\u6c5f\u5357\uff0c\u5bf9\u4e48\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u8427\u8001\u5e08\u5411 \u4f60\u8bf4\u6211\u4e86\uff0c\u5176\u5b9e\u6211\u5e76\u4e0d\u591a\u5634\uff0c\u4ed6\u4eec\u5374\u504f\u8ba8\u538c\u6211\uff0c\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u597d\u6781\uff0c\u54b1\u4eec\u90fd\u662f\u88ab\u4eba\u8ba8\u538c\u7684 \u4eba\uff0c\u6765\u559d\u4e00\u676f\uff01\u201d\u6768\u67f3\u9752\u66f4\u662f\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5fc3\u4e2d\u60f3\u9053\uff0c\u4e00\u4e2a\u91d1\u4e16\u9057\u5df2\u96be\u5bf9\u4ed8\uff0c\u53c8\u6dfb\u4e86\u8fd9\u4e2a\u53e4\u7075 \u7cbe\u602a\u7684\u4e66\u61c2\uff0c\u770b\u6765\u4eca\u5929\u5b9e\u662f\u51f6\u591a\u5409\u5c11\uff1a\u5176\u5b9e\u6c5f\u5357\u7684\u771f\u5b9e\u6b66\u529f\u8fd8\u6bd4\u4e0d\u4e0a\u90b9\u7edb\u971e\uff0c\u53ea\u56e0\u4ed6\u66fe\u88ab\u9ec4 \u77f3\u9053\u4eba\u5f3a\u6536\u4e3a\u5f92\uff0c\u65e0\u610f\u4e2d\u5b66\u4e86\u9ec4\u77f3\u9053\u4eba\u72ec\u95e8\u7684\u98a0\u5012\u7a74\u9053\u529f\u592b\uff0c\u6240\u4ee5\u7ed9\u6843\u6838\u6253\u7740\uff0c\u53ea\u5f53\u662f\u6328\u4e86 \u4e24\u9897\u77f3\u5b50\uff0c\u867d\u7136\u75bc\u75db\uff0c\u5374\u4e1d\u6beb\u6ca1\u4e8b\u3002 \u3000\u3000\u6c5f\u5357\u5f53\u65e5\u80fd\u9003\u51fa\u77f3\u6797\uff0c\u6446\u8131\u4e86\u9ec4\u77f3\u9053\u4eba\uff0c\u867d\u8bf4\u662f\u9760\u5510\u7ecf\u5929\u51fa\u529b\uff0c\u4f46\u82e5\u6ca1\u6709\u91d1\u4e16\u9057\u4e0e\u51b0\u5ddd \u5929\u5973\u6765\u52a9\uff0c\u53ea\u5510\u7ecf\u5929\u4e00\u4eba\u4e5f\u6253\u53d1\u4e0d\u4e86\u9ec4\u77f3\u9053\u4eba\u3002\u6c5f\u5357\u8bb0\u6027\u6781\u597d\uff0c\u5f53\u65e5\u867d\u7136\u53ea\u662f\u5306\u5306\u4e00\u9762\uff0c\u5374 \u5df2\u8bb0\u7262\u4e86\u91d1\u4e16\u9057\u7684\u5f62\u5bb9\uff0c\u4ed6\u77e5\u6069\u62a5\u5fb7\uff0c\u53e3\u53e3\u58f0\u58f0\u79f0\u5168\u4e16\u9057\u505a\u201c\u6069\u516c\u201d\uff0c\u8fde\u8fde\u7ed9\u4ed6\u659f\u9152\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6ee1\u8179\u7262\u9a9a\uff0c\u4e00\u8fde\u559d\u4e86\u5341\u51e0\u676f\u9152\uff0c\u77aa\u7740\u773c\u775b\u53eb\u9053\uff1a\u201c\u6211\u5e73\u751f\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u4eba\u53eb\u6211\u505a \u6069\u516c\uff0c\u6211\u4e8e\u4f60\u4f55\u6069\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4f60\uff0c\u6211\u73b0\u5728\u8fd8\u7ed9\u90a3\u8001\u4e0d\u6b7b\u7684\u81ed\u9053\u58eb\u5f3a\u8feb\u505a\u5f92\u5f1f\uff0c\u7ec8 \u5e74\u5173\u95ed\u5728\u77f3\u6797\u4e4b\u4e2d\uff0c\u90a3\u5c82\u4e0d\u662f\u8ba8\u538c\u6b7b\u4e86\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff0c\u201c\u90a3\u81ed\u9053\u58eb\u613f\u5c06\u6bd5\u751f\u7684\u7edd\u6280\u90fd\u4f20\u6388\u7ed9 \u4f60\uff0c\u4f60\u600e\u4e48\u53cd\u800c\u8ba8\u538c\u4ed6\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ed6\u5bf9\u6211\u4e0d\u597d\uff0c\u52a8\u4e0d\u52a8\u5c31\u8981\u8d23\u7f5a\u6211\uff0c\u6211\u5f53\u7136\u8ba8\u538c\u4ed6\u3002 \u55ef\uff0c\u90a3\u81ed\u9053\u58eb\u6ca1\u4e00\u70b9\u4eba\u5473\u513f\uff0c\u6211\u4ece\u672a\u89c1\u8fc7\u4ed6\u9762\u4e0a\u6709\u4e00\u4e1d\u7b11\u5bb9\u3001\u8fd8\u4e0d\u8ba8\u538c\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60 \u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d\u6c5f\u5357\u9053\uff0c\u201c\u6b63\u6b32\u8bf7\u6559\u3002\u201d\u91d1\u4e16\u9057\u5389\u58f0\u9053\uff1a\u201c\u6211\u5c31\u662f\u6c5f\u6e56\u4e0a\u4eba\u79f0\u6bd2\u624b\u75af\u4e10\u7684\u91d1 \u4e16\u9057\uff01\u201d \u3000\u3000\u6c5f\u5357\u89c1\u4ed6\u9762\u4e0a\u90a3\u526f\u51f6\u6076\u7684\u6837\u5b50\uff0c\u7adf\u4f3c\u5ffd\u7136\u53d8\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u7981\u5fc3\u4e2d\u6697\u6697\u53d1\u6296\u3002\u4f46\u4ecd\u662f\u7b11 \u7740\u8bf4\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u4f46\u662f\u4f60\u5bf9\u6211\u6709\u8fbe\u597d\u5904\u3001\u6211\u603b\u662f\u8bb0\u5f97\u7684\uff01\u201d\u8fd9\u8bf4\u8bdd\u4f3c\u5229\u9488\u4e00\u6837\u5728\u91d1 \u4e16\u9057\u5fc3\u5934\u523a\u4e86\u4e00\u4e0b\uff0c\u9661\u7136\u95f4\u4ed6\u53c8\u60f3\u8d77\u4e86\u674e\u6c81\u6885\u7684\u8bdd\uff1a\u591a\u4f60\u5bf9\u522b\u4eba\u597d\u3002\u522b\u4eba\u5c31\u5bf9\u4f60\u597d\uff0c\u4f60\u6b3a\u4fae \u522b\u4eba\u3001\u53c8\u600e\u602a\u5f97\u522b\u4eba\u51b7\u6de1\u4f60\u5462\uff0c\u7334\u5b50\u5982\u6b64\uff0c\u4eba\u4e5f\u4e00\u6837\u3002\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u5c06\u9152\u676f\u63a8\u5f00\uff0c\u6362\u4e86\u4e00 \u526f\u795e\u6c14\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u505a\u4e8b\u53ea\u51ed\u81ea\u5df1\u9ad8\u5174\uff0c\u6700\u8ba8\u538c\u4eba\u5356\u6069\u91cd\u4e49\uff0c\u5145\u4ec0\u4e48\u4fa0\u58eb\uff1f\u6069\u516c\u4e24\u5b57\uff0c\u4f11 \u8981\u518d\u63d0\uff01\u4f60\u6b22\u559c\u53eb\u3001\u5411\u5510\u7ecf\u5929\u53eb\u53bb\u3002\u201d\u6c5f\u5357\u6014\u9053\uff1a\u201c\u5510\u5927\u4fa0\u4e5f\u662f\u6211\u7684\u6069\u4eba\uff0c\u55ef\uff0c\u4f60\u548c\u5510\u5927\u4fa0 \u4e0d\u662f\u5f88\u8981\u597d\u7684\u670b\u53cb\u5417\uff1f\u5510\u5927\u4fa0\u6bcf\u6b21\u6765\u8428\u8fe6\uff0c\u90fd\u662f\u5230\u6211\u5bb6\u516c\u5b50\u5bb6\u4e2d\u4f4f\u7684\u3002\u201d\u6c5f\u5357\u542c\u91d1\u4e16\u9057\u53e3\u98ce \u6709\u70b9\u4e0d\u5bf9\uff0c\u4f46\u90a3\u65e5\u773c\u89c1\u91d1\u4e16\u9057\u4e0e\u51b0\u5ddd\u5929\u5973\u76f8\u52a9\u5510\u7ecf\u5929\u6253\u8d25\u9ec4\u77f3\u9053\u4eba\uff0c\u600e\u4e48\u4e5f\u731c\u60f3\u4e0d\u5230\u4ed6\u548c\u5510 \u7ecf\u5929\u4e4b\u95f4\u7adf\u6709\u4e00\u6bb5\u5fc3\u75c5\u3002 \u3000\u3000\u5168\u4e16\u9057\u5ffd\u5730\u628a\u559d\u5149\u4e86\u7684\u9152\u676f\u5411\u5916\u4e00\u6454\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5510\u7ecf\u5929\u662f\u5927\u4fa0\uff0c\u6211\u662f\u75af\u4e10\uff0c\u626f\u4e0d \u5230\u4e00\u5757\u513f\u3002\u6765\uff0c\u54b1\u4eec\u8fd8\u662f\u559d\u9152\uff01\u201d\u5ffd\u5730\u53c8\u505c\u676f\u95ee\u9053\uff1a\u201c\u591a\u5634\u7684\u6c5f\u5357\uff0c\u4f60\u4e0d\u53ea\u591a\u5634\uff0c\u8bb2\u5927\u8bdd\u7684 \u672c\u9886\u4e5f\u5f88\u4e0d\u9519\uff0c\u662f\u4e48\uff1f\u201d\u6c5f\u5357\u53eb\u8d77\u201c\u649e\u5929\u5c48\u201d\u6765\uff0c\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u559d\u8fc7\u7687\u5e1d\u8001\u513f\u7684\u5fa1 \u9152\uff0c\u80e1\u4e71\u62ff\u6765\u6bd4\u8f83\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u771f\u7684\u559d\u8fc7\uff0c\u6211\u8fd9\u6b21\u5230\u4eac\u57ce\u53bb\uff0c\u7ed9\uff0c\u7ed9\u2026\u2026\u201d\u4fbf\u505c\u4e86\u53e3\u3002 \u5176\u5b9e\u8fd9\u5374\u4e0d\u662f\u4ec0\u4e48\u79d8\u5bc6\u4e8b\uff0c\u4ed6\u7ed9\u9648\u5b9a\u57fa\u5e26\u4fe1\u5230\u4eac\u57ce\u53bb\uff0c\u9648\u5b9a\u57fa\u7684\u59bb\u8205\u662f\u5fa1\u53f2\uff0c\u6070\u597d\u90a3\u662f\u8fc7\u5e74 \u7684\u65f6\u5019\uff0c\u7687\u5e1d\u5c06\u5927\u5185\u5fa1\u9152\u5206\u8d50\u5404\u4eac\u5b98\uff0c\u6bcf\u4eba\u90fd\u5f97\u5230\u4e24\u74f6\uff0c\u6c5f\u5357\u9002\u9022\u5176\u4f1a\uff0c\u4e5f\u559d\u4e86\u4e00\u5c0f\u676f\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5374\u4f1a\u9519\u4e86\u610f\uff0c\u4ee5\u4e3a\u6c5f\u5357\u662f\u6015\u9152\u5e97\u4eba\u591a\uff0c\u6709\u6240\u987e\u5fcc\uff0c\u4ed6\u6709\u51e0\u5206\u9152\u610f\uff0c\u5ffd\u5730\u53eb\u9053\uff1a \u201c\u597d\uff0c\u6211\u66ff\u4f60\u628a\u95f2\u4eba\u90fd\u6253\u53d1\u51fa\u53bb\uff0c\u8fd9\u5e97\u4e2d\u4e5f\u518d\u4e0d\u8bb8\u522b\u4eba\u8fdb\u6765\u559d\u9152\uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u653e\u5fc3\u8bf4\u5427\u3002\u201d \u6768\u67f3\u9752\u67f3\u7709\u5012\u7acb\uff0c\u7acb\u523b\u6293\u8d77\u5f39\u5f13\u3002 \u3000\u3000\u53cc\u65b9\u6b63\u5728\u4e00\u89e6\u5373\u53d1\u4e4b\u9645\uff0c\u5916\u9762\u53c8\u8d70\u8fdb\u4e86\u4e24\u4e2a\u4eba\u6765\uff0c\u6c5f\u5357\u4e00\u89c1\uff0c\u76f4\u6253\u54c6\u55e6\uff0c\u6025\u6025\u5fd9\u5fd9\u8eb2\u5230 \u91d1\u4e16\u9057\u80cc\u540e\u3002 \u3000\u3000\u53ea\u89c1\u8d70\u8fdb\u6765\u4e00\u50e7\u4e00\u9053\uff0c\u90a3\u548c\u5c1a\u5168\u4e16\u9057\u5e76\u4e0d\u8ba4\u5f97\uff0c\u90a3\u9053\u58eb\u5374\u662f\u5025\u4f97\u6d3e\u7684\u602a\u6770\u9ec4\u77f3\u9053\uff01 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u563f\u563f\u51b7\u7b11\uff0c\u950b\u5229\u7684\u773c\u5149\u4ece\u6c5f\u5357\u8eab\u4e0a\u8f6c\u5411\u91d1\u4e16\u9057\uff0c\u4ece\u91d1\u4e16\u9057\u7684\u9762\u4e0a\u626b\u8fc7\uff0c\u53c8\u8f6c\u5230 \u6c5f\u5357\u8eab\u4e0a\u3002\u6c5f\u5357\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u9ec4\u77f3\u9053\u4eba\u76ef\u7740\u4ed6\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u627e\u5f97\u597d\u5e08\u7236\u5475\uff01\u201d\u91d1\u4e16\u9057\u5c06\u6c5f \u5357\u6309\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u6015\u4ec0\u4e48\uff1f\u597d\u597d\u7684\u559d\u4f60\u7684\u9152\u53bb\u3002\u201d\u8fc8\u524d\u4e00\u6b65\uff0c\u8fce\u7740\u9ec4\u77f3\u9053\u4eba\uff0c\u4e5f\u563f\u563f\u7684\u51b7\u7b11 \u9053\uff1a\u201c\u4ed6\u6709\u6ca1\u6709\u627e\u5230\u597d\u5e08\u7236\uff0c\u4f60\u7ba1\u4e0d\u7740\uff01\u201d\u5f53\u65e5\u9ec4\u77f3\u9053\u4eba\u4e0e\u5510\u7ecf\u5929\u4e03\u62db\u5b9a\u80dc\u8d1f\uff0c\u9ec4\u77f3\u9053\u4eba\u4e03 \u62db\u4e4b\u5185\u6253\u4e0d\u5012\u5510\u7ecf\u5929\uff0c\u5c31\u6c38\u4e0d\u8bb8\u518d\u5e72\u6d89\u6c5f\u5357\u3002\u6c5f\u5357\u8d70\u4e86\u4e00\u8d9f\u6c5f\u6e56\uff0c\u7565\u77e5\u6b66\u6797\u89c4\u77e9\uff0c\u60ca\u9b42\u7a0d \u5b9a\uff0c\u53eb\u9053\uff1a\u201c\u662f\u5440\uff0c\u4e00\u6d3e\u5b97\u5e08\uff0c\u8bf4\u8fc7\u7684\u8bdd\u53ef\u4e0d\u80fd\u4e0d\u7b97\uff01\u201d\u5012\u4e86\u4e00\u676f\u8461\u8404\u9152\uff0c\u4ef0\u7740\u8116\u5b50\u76f4\u559d\uff0c \u53ef\u601c\u4ed6\u624b\u98a4\u811a\u9707\uff0c\u4e00\u676f\u9152\u5012\u6709\u5927\u534a\u676f\u6cfc\u6cfb\u5730\u4e0a\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u602a\u773c\u4e00\u7ffb\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u6211\u4e0d\u7406\uff0c\u4f60\u6b20\u6211\u7684\u5e10\u3002\u53ef\u4e0d\u80fd\u4e0d\u7ba1\uff01\u201d\u91d1\u4e16\u9057\u5f53 \u65e5\u7528\u6bd2\u9488\u5c04\u9ec4\u77f3\u9053\u4eba\uff0c\u9ec4\u77f3\u9053\u4eba\u51e0\u4e4e\u906d\u4ed6\u6697\u7b97\uff0c\u9ec4\u77f3\u9053\u4eba\u8981\u7b97\u7684\u5e10\uff0c\u5c31\u662f\u8fd9\u4e00\u9488\u4e4b\u4ec7\uff01 \u3000\u3000\u91d1\u4e16\u9057\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u6211\u559d\u4e86\u4e24\u676f\uff0c\u6b63\u8981\u6253\u4eba\u6d88\u9063\uff01\u201d\u9ec4\u77f3\u9053\u4eba\u4e00\u58f0\u6012\u543c\uff0c \u62c2\u5c18\u5f53\u5934\u62c2\u4e0b\uff0c\u91d1\u4e16\u9057\u4e00\u4e2a\u7b4b\u6597\u7ffb\u8fc7\u684c\u9762\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u5413\u4e86\u6c5f\u5357\uff01\u201d\u53cd\u624b\u4e00\u6307\uff0c\u95ea\u7535\u822c\u5730\u70b9 \u9ec4\u77f3\u9053\u4eba\u624b\u8155\u7684\u201c\u5173\u5143\u7a74\u201d\uff0c\u91d1\u4e16\u9057\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\u5389\u5bb3\u975e\u5e38\uff0c\u9ec4\u77f3\u9053\u4eba\u62c2\u5c18\u4e00\u6536\uff0c\u5c18\u5c3e\u6563 \u5f00\uff0c\u6839\u6839\u5012\u5377\uff0c\u4e00\u67c4\u62c2\u5c18\uff0c\u80fd\u7528\u5185\u529b\u4f7f\u5f97\u5982\u6b64\u4e4b\u5999\uff0c\u4e5f\u786e\u662f\u6b66\u6797\u7f55\u89c1\u7684\u5947\u6280\uff0c\u91d1\u4e16\u9057\u82e5\u7136\u518d \u4f38\u624b\u70b9\u7a74\uff0c\u90a3\u662f\u5c06\u624b\u8155\u9001\u4e0a\u53bb\u7ed9\u4ed6\u7684\u62c2\u5c18\u7f20\u7ed5\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u91d1\u4e16\u9057\u673a\u7075\u4e4b\u6781\uff0c\u8fd9\u4e00\u62db\u6b3a\u8eab\u70b9\u7a74\u662f\u865a\u62db\uff0c\u7528\u610f\u6b63\u662f\u8981\u9ec4\u77f3\u9053\u4eba\u5c06\u62c2\u5c18\u53cd\u5377\u56de\u6765\uff0c \u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u672c\u5df2\u5c01\u4f4f\u4e86\u4ed6\u7684\u9000\u8def\uff0c\u8fd9\u4e00\u6536\u7acb\u523b\u9732\u51fa\u7a7a\u9699\uff0c\u53ea\u89c1\u4ed6\u865a\u70b9\u4e00\u70b9\uff0c\u4e00\u4e2a\u7b4b\u6597\u5012 \u7ffb\u51fa\u53bb\uff0c\u6293\u8d77\u4e86\u653e\u5728\u5899\u89d2\u7684\u94c1\u62d0\u3002\u3001 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u8ddf\u8e2a\u6025\u51fb\uff0c\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5582\uff0c\u54b1\u4eec\u5230\u5916\u9762\u6bd4\u5212\u53bb\uff01\u201d\u9ec4\u77f3\u9053\u4eba\u6015\u91d1\u4e16\u9057\u8be1\u8ba1\u591a \u7aef\uff0c\u5954\u5728\u4e0a\u9996\uff0c\u62e6\u4f4f\u4e86\u95e8\u53e3\u4e0d\u653e\u4ed6\u51fa\u53bb\u3002\u9152\u4fdd\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\uff0c\u5c0f\u5e97\u672c\u94b1\u77ed \u5c11\uff0c\u4e24\u4f4d\u7237\u8981\u6253\u67b6\uff0c\u8bf7\u3001\u8bf7\u3001\u8bf7\u5230\u5916\u9762\u53bb\uff0c\u6210\u4e0d\u6210\uff1f\u201d\u9ec4\u77f3\u9053\u4eba\u9053\u888d\u4e0a\u6296\uff0c\u201c\u556a\u201d\u7684\u98de\u51fa\u4e00 \u952d\u91d1\u5b50\uff0c\u7aef\u7aef\u6b63\u6b63\u7684\u63b7\u5728\u67dc\u53f0\u4e2d\u592e\uff0c\u559d\u9053\uff1a\u201c\u4e1c\u897f\u6253\u574f\u4e86\u6211\u8d54\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u602a\u58f0\u53eb\u9053\uff1a\u201c\u597d\u9614\u6c14\uff0c\u5582\uff0c\u6211\u7684\u9152\u94b1\u4e5f\u7b97\u5728\u8fd9\u952d\u91d1\u5b50\u5185\u4e86\uff0c\u591f\u4e48\uff1f\u201d\u9152\u4fdd\u9053\uff1a \u201c\u591f\u5566\uff0c\u591f\u5566\uff01\uff0cF\u62ff\u4e86\u91d1\u5b50\uff0c\u8eb2\u5230\u4e86\u67dc\u56f4\u5e95\u4e0b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u547c\u547c\u4e24\u62d0\uff0c\u5c06\u4e2d\u592e\u7684\u4e24\u5f20\u684c\u5b50\u6253\u5f97\u788e\u6210\u65e0\u6570\u6728\u7247\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6709\u5927\u7237\u80af\u51fa \u94b1\uff0c\u6211\u53ea\u597d\u820d\u547d\u966a\u5927\u7237\u73a9\u73a9\u5566\uff01\u201d\u4ed6\u4e00\u8eab\u534e\u4e3d\u8863\u88f3\uff0c\u8bf4\u7684\u5374\u662f\u4e5e\u4e10\u53e3\u6c14\uff0c\u6c5f\u5357\u60f3\u7b11\u5374\u7b11\u4e0d\u51fa \u6765\uff0c\u9ec4\u77f3\u9053\u4eba\u987e\u4e0d\u5f97\u548c\u4ed6\u6597\u53e3\uff0c\u62c2\u5c18\u4e00\u8d77\uff0c\u53c8\u51cc\u7a7a\u51fb\u4e0b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u53cd\u624b\u4e00\u626c\uff0c\u54d7\u5566\u5566\u53c8\u6253\u584c\u4e86\u4e24\u5f20\u684c\u5b50\uff0c\u6768\u67f3\u9752\u6bcd\u5973\u9000\u5230\u5899\u89d2\uff0c\u624b\u91cc\u4ecd\u7136\u6293\u7d27\u5f39 \u5f13\u3002\u53ea\u89c1\u91d1\u4e16\u9057\u4e00\u6839\u94c1\u62d0\uff0c\u7eb5\u6a2a\u98de\u821e\uff0c\u653b\u52bf\u51cc\u5389\u4e4b\u6781\uff0c\u4f46\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u5de6\u53f3\u8f7b\u62c2\uff0c\u82e5\u4e0d\u7ecf \u610f\uff0c\u5374\u5c06\u4ed6\u7684\u653b\u52bf\u4e00\u62db\u62db\u90fd\u5316\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u6768\u67f3\u9752\u5927\u559c\uff0c\u770b\u5f97\u51fa\u795e\uff0c\u7adf\u7136\u5fd8\u4e86\u9003\u8d70\u3002\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u662f\u5175\u5668\u4e2d\u7684\u81f3\u521a\u4e4b\u7269\uff0c\u800c\u9ec4\u77f3\u9053 \u4eba\u7684\u62c2\u5c18\u5374\u662f\u81f3\u67d4\u4e4b\u7269\uff0c\u4e24\u4eba\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u529f\u592b\uff0c\u628a\u8fd9\u4e24\u4ef6\u6b66\u6797\u7f55\u89c1\u7684\u5175\u5668\u4f7f\u5f97\u51fa\u795e\u5165\u5316\u3002 \u4f46\u9ec4\u77f3\u9053\u4eba\u631f\u6570\u5341\u5e74\u529f\u592b\uff0c\u7a76\u7adf\u6bd4\u91d1\u4e16\u9057\u7a0d\u80dc\u4e00\u7b79\uff0c\u4e8c\u4e09\u5341\u62db\u4e00\u8fc7\uff0c\u53ea\u89c1\u4e00\u67c4\u62c2\u5c18\u968f\u98ce\u98d8 \u821e\uff0c\u5ffd\u6563\u5ffd\u805a\uff0c\u6216\u7f20\u94c1\u62d0\uff0c\u6216\u94bb\u9699\u62c2\u7a74\uff0c\u5947\u62db\u767e\u51fa\uff0c\u7075\u6d3b\u4e4b\u6781\u3002\u53ec\u3002\u62c2\u5c18\u5168\u4e0d\u53d7\u529b\uff0c\u91d1\u4e16\u9057 \u867d\u7136\u62d0\u6c89\u529b\u731b\uff0c\u4e00\u78b0\u5230\u62c2\u5c18\uff0c\u524d\u9762\u6297\u62d2\u7684\u529b\u9053\u5f80\u5f80\u5ffd\u7136\u6d88\u5931\uff0c\u82e5\u975e\u91d1\u4e16\u9057\u7684\u5185\u529b\u5df2\u83f1\uff01\u4e86\u80fd \u591f\u63a7\u5236\u81ea\u5982\u4e4b\u5883\uff0c\u4e00\u4e2a\u6536\u52bf\u4e0d\u53ca\uff0c\u5c31\u5f97\u7acb\u523b\u683d\u5012\u5f53\u573a\uff0c\u4f46\u82e5\u7136\u6240\u7528\u7684\u529b\u9053\u7a0d\u5f31\uff0c\u9ec4\u77f3\u9053\u4eba\u7684 \u62c2\u5c18\u53c8\u5ffd\u800c\u53d8\u5f97\u6c89\u91cd\u975e\u5e38\uff0c\u5e26\u7740\u4e00\u80a1\u6781\u5927\u7684\u6f5c\u529b\u626f\u4ed6\u7684\u94c1\u62d0\u3002 \u3000\u3000\u6768\u67f3\u9752\u672c\u8eab\u7684\u6b66\u529f\u867d\u7136\u6765\u5230\u4e00\u6d41\u5883\u754c\uff1a\u4f46\u5730\u533a\u540d\u5bb6\u4e4b\u540e\uff0c\u76f8\u8bc6\u7684\u4e5f\u90fd\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684 \u4eba\u7269\uff0c\u5929\u5c71\u6d3e\u7684\u638c\u95e8\uff0c\u5f53\u4eca\u6b66\u6797\u7684\u5b97\u5e08\u5510\u6653\u6f9c\u4e5f\u66fe\u7ecf\u662f\u5979\u7684\u672a\u5a5a\u592b\uff0c\u6240\u4ee5\u5979\u5224\u65ad\u522b\u4eba\u7684\u6b66\u529f \u5f3a\u5f31\uff0c\u5012\u662f\u5177\u6709\u201c\u6cd5\u773c\u201d\u3002\u65c1\u4eba\u5c1a\u672a\u770b\u6e05\uff0c\u5979\u5df2\u77a7\u51fa\u4e86\u91d1\u4e16\u9057\u7684\u8d25\u8c61\uff0c\u5fcd\u4e0d\u4f4f\u53d1\u58f0\u53eb\u9053\uff1a \u201c\u597d\uff0c\u518d\u6765\u4e00\u62db\u521a\u67d4\u4ea4\u6d4e\uff0c\u5c18\u5c3e\u62c2\u767d\u6d77\u7a74\uff0c\u6746\u5c16\u523a\u7384\u673a\u7a74\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u6b7b\u4e5f\u4f24\uff01\u201d\u9ec4\u77f3\u9053\u4eba\u5fc3 \u5ff5\u4e00\u52a8\uff0c\u679c\u7136\u968f\u624b\u53d1\u51fa\u6768\u67f3\u9752\u6307\u70b9\u7684\u62db\u6570\uff0c\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8eab\u8eaf\u4e00\u77ee\uff0c\u4ee5\u62d0\u652f \u5730\uff0c\u500f\u5730\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u7b11\u58f0\u672a\u6b62\uff0c\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u75f0\u6d8e\u5728\u7b11 \u58f0\u4e2d\u98de\u4e86\u51fa\u6765\uff0c\u9ec4\u77f3\u9053\u4eba\u6700\u60e7\u4ed6\u7684\u6697\u5668\uff0c\u6025\u5fd9\u5012\u8f6c\u62c2\u5c18\uff0c\u6839\u6839\u6492\u5f00\uff0c\u5316\u4f5c\u5c18\u7f51\uff0c\u62a4\u7740\u8eab\u8eaf\u3002 \u91d1\u4e16\u9057\u54c8\u54c8\u5927\u5956\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u628a\u94c1\u5251\u3002\u4ed6\u7684\u94c1\u62d0\uff0c\u5f62\u5f0f\u5947\u7279\uff0c\u672c\u6765\u5c31\u662f\u4e24\u4ef6\u5175 \u5668\u5408\u6210\uff0c\u62d0\u5185\u4e2d\u7a7a\uff0c\u85cf\u6709\u94c1\u5251\uff0c\u521a\u624d\u88ab\u9ec4\u77f3\u9053\u4eba\u8feb\u5f97\u7d27\uff0c\u73b0\u5728\u624d\u89c5\u5f97\u7a7a\u9699\uff0c\u62bd\u51fa\u5251\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u91d1\u4e16\u9057\u6240\u5b66\u7684\u6bd2\u9f99\u5c0a\u8005\u81ea\u521b\u7684\u6b66\u529f\uff0c\u602a\u5f02\u65e0\u6bd4\u5165\u5de6\u62d0\u53f3\u5251\uff0c\u6709\u5982\u4e24 \u6761\u5177\u6709\u7075\u6027\u7684\u957f\u86c7\uff0c\u518d\u52a0\u4e0a\u90a3\u968f\u65f6\u53ef\u4ece\u53e3\u4e2d\u55b7\u51fa\u6765\u7684\u6bd2\u9488\uff0c\u9ec4\u77f3\u9053\u4eba\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u6709 \u6240\u987e\u5fcc\u3002\u4f46\u89c1\u4e24\u4eba\u653b\u62d2\u8fdb\u9000\uff0c\u8f97\u8f6c\u4e4b\u95f4\uff0c\u53c8\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u8fde\u6768\u67f3\u9752\u90a3\u6837\u66fe\u89c1\u8fc7\u65e0\u6570\u5927\u9635\u4ed7 \u7684\u4eba\uff0c\u4e5f\u5df2\u5206\u4e0d\u51fa\u8c01\u5f3a\u8c01\u5f31\u3002\u4f46\u89c1\u91d1\u4e16\u9057\u53f1\u54a4\u98ce\u5c6f\u602a\u72b6\u767e\u51fa\uff0c\u8fd8\u4f3c\u4e4e\u4e0d\u65f6\u659c\u773c\u81ea\u5df1\u3002 \u3000\u3000\u6768\u67f3\u9752\u4e0d\u7531\u5f97\u6697\u53eb\u201c\u4e0d\u5999\u3002\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u8fd9\u75af\u4e10\u5f97\u80dc\uff0c\u6211\u6bcd\u5973\u96be\u9003\u6027\u547d\uff0c\u4e0d\u5982\u8d81 \u4ed6\u4eec\u80dc\u8d1f\u672a\u51b3\u4e4b\u9645\uff0c\u6e9c\u8d70\u4e86\u5427\u3002\u4ed6\u8fd8\u672a\u66fe\u5411\u6211\u53eb\u9635\uff0c\u8fd9\u53ef\u7b97\u4e0d\u5f97\u793a\u5f31\u9003\u8d70\u3002\u773c\u775b\u4e00\u8f6c\uff0c\u5ffd\u89c1 \u4e0e\u9ec4\u77f3\u9053\u4eba\u540c\u6765\u7684\u90a3\u4e2a\u548c\u5c1a\uff0c\u7ad9\u5728\u95e8\u8fb9\uff0c\u4e0d\u770b\u6597\u573a\uff0c\u5374\u51b7\u51b7\u7684\u77a7\u7740\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u7626\u957f\u7684\u4e2a\u5b50\uff0c\u9762\u5e26\u75c5\u5bb9\uff0c\u8fdb\u6765\u4e4b\u65f6\uff0c\u6beb\u4e0d\u60f9\u4eba\u6ce8\u610f\uff0c\u8fd9\u65f6\u4e00\u770b\uff0c\u4f46\u89c1\u4ed6\u4e24\u9053\u773c \u5149\uff0c\u5982\u5200\u4f3c\u5251\uff0c\u773c\u795e\u5145\u8db3\uff0c\u7cbe\u534e\u5185\u8574\uff0c\u7adf\u4f3c\u4e2a\u5177\u6709\u9ad8\u6df1\u6b66\u529f\u7684\u4eba\uff0c\u6768\u67f3\u9752\u5fc3\u4e2d\u4e00\u51db\uff0c\u966a\u7b11\u8bf4 \u9053\uff1a\u201c\u5927\u5e08\uff0c\u8bf7\u8ba9\u4e00\u8ba9\u8def\u3002\u201d \u3000\u3000\u90a3\u548c\u5c1a\u53cc\u773c\u4e00\u7ffb\uff0c\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u5973\u5c45\u58eb\uff0c\u53ef\u8fd8\u8ba4\u5f97\u4ffa\u8463\u592a\u6e05\u4e48\uff1f\u201d\u6768\u67f3\u9752\u5fc3\u5934\u4e00\u9707\uff0c \u539f\u6765\u8fd9\u4e00\u4e2a\u8463\u5927\u6e05\u4e43\u662f\u5f53\u5e74\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u7684\u5927\u5f1f\u5b50\uff0c\u4e09\u5341\u5e74\u4e4b\u524d\uff0c\u6768\u67f3\u9752\u8fd8\u662f\u4e2a\u5341\u516d\u4e03\u5c81 \u7684\u5c0f\u59d1\u5a18\uff0c\u968f\u5979\u7684\u7236\u4eb2\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u8d74\u5927\u884c\u5c71\u7684\u5317\u4e94\u7701\u6b66\u6797\u5927\u4f1a\uff0c\u5176\u65f6\u8463\u592a\u6e05\u548c\u4ed6\u7684\u5e08\u7236 \u8428\u5929\u523a\u90fd\u5728\u56db\u7687\u5b50\u5141\u5e27\u95e8\u4e0b\uff0c\u5949\u547d\u5230\u592a\u884c\u5c71\u8981\u6740\u5c3d\u5317\u4e94\u7701\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u6768\u4ef2\u82f1\u7236\u5973\u5728\u9014\u4e2d\u65c5 \u5c45\uff0c\u4e0e\u4ed6\u76f8\u9047\uff1b\u4e00\u573a\u6fc0\u6218\uff0c\u6768\u4ef2\u82f1\u9669\u9669\u843d\u8d25\uff0c\u5e78\u5f97\u5173\u4e1c\u56db\u4fa0\u4e2d\u7684\u67f3\u5148\u5f00\u548c\u9648\u7384\u9738\u76f8\u52a9\uff0c\u624d\u5c06 \u4ed6\u9010\u8d70\uff0c\u800c\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u8463\u592a\u6e05\u4e5f\u53d7\u4e86\u6768\u4ef2\u82f1\u4e00\u8bb0\u94c1\u638c\uff0c\u56de\u53bb\u4e4b\u540e\uff0c\u4e00\u6761\u53f3\u81c2\u7adf\u56e0\u7b4b\u9aa8\u65ad \u6298\uff0c\u53d8\u6210\u6b8b\u5e9f\u3002\u6768\u4ef2\u82f1\u5e73\u751f\u5927\u5c0f\u767e\u6218\uff0c\u50cf\u8fd9\u6837\u7684\u4e8b\u60c5\u591a\u5230\u4e0d\u53ef\u80dc\u8bb0\uff0c\u4e8b\u60c5\u8fc7\u540e\uff0c\u5e76\u6ca1\u653e\u5728\u5fc3 \u4e0a\uff0c\u8463\u5927\u6e05\u56e0\u4ed6\u800c\u81f4\u6b8b\u5e9f\u7684\u4e8b\uff0c\u6768\u4ef2\u82f1\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6768\u67f3\u9752\u5fc3\u5934\u5927\u9707\uff0c\u9762\u4e0a\u5374\u4e1d\u6beb\u4e0d\u9732\u6050\u60e7\u4e4b\u8272\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4e09\u5341\u591a\u5e74\u4e0d\u89c1\uff0c \u539f\u6765\u5927\u5e08\u5df2\u7688\u4f9d\u6211\u4f5b\uff0c\u52d8\u7834\u7ea2\u5c18\u4e86\uff0c\u53ef\u559c\u53ef\u8d3a\u5475\uff01\u201d\u8463\u5927\u6e05\u51b7\u7b11\u9053\uff1a\u201c\u6d12\u5bb6\u4e4b\u6709\u4eca\u65e5\uff0c\u5168\u62dc \u4ee4\u5c0a\u6240\u8d50\uff0c\u54c8\u54c8\uff0c\u6211\u53ef\u4e0d\u662f\u4ec0\u4e48\u5f97\u9053\u7684\u9ad8\u50e7\uff0c\u5973\u5c45\u58eb\u7684\u9ad8\u5e3d\u5b50\u6211\u539f\u4ef6\u5949\u8fd8\u3002\u201d\u6768\u67f3\u9752\u77e5\u9053\u6b64 \u6218\u96be\u514d\uff0c\u63e1\u7d27\u5f39\u5f13\uff0c\u9053\uff1a\u201c\u5927\u5e08\u4e0d\u80af\u8ba9\u8def\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u201d\u8463\u592a\u6e05\u4ef0\u5929\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u60dc \u5475\uff0c\u53ef\u60dc\uff01\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u53ef\u60dc\u4ee4\u5c0a\u53bb\u4e16\u5f97\u65e9\uff0c\u6211\u7adf\u6765\u4e0d\u53ca\u9001 \u884c\uff0c\u518d\u4e5f\u65e0\u7f18\u9886\u6559\u4ed6\u7684\u94c1\u638c\u795e\u5f39\uff01\u201d\u6768\u67f3\u9752\u67f3\u7709\u4e00\u7ad6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u7239\u867d\u7136\u53bb\u4e16\uff0c\u94c1\u638c\u795e \u5f39\u7684\u6280\u827a\u8fd8\u672a\u5931\u4f20\uff0c\u4f60\u8981\u9886\u6559\uff0c\u90a3\u5bb9\u6613\u5f97\u5f88\uff01\u201d\u5f39\u5f13\u4e00\u66f3\uff0c\u50fb\u50fb\u556a\u556a\u8fde\u73e0\u75be\u54cd\uff0c\u6768\u67f3\u9752\u5728\u5f39 \u5f13\u4e0a\u4e0b\u8fc7\u51e0\u5341\u5e74\u529f\u592b\uff0c\u795e\u5f39\u4e00\u53d1\uff0c\u52b2\u529b\u51c6\u5934\u90fd\u6070\u5230\u597d\u5904\uff0c\u53ea\u89c1\u5f39\u4e38\u5982\u96e8\uff0c\u62ab\u98ce\u547c\u5578\uff0c\u767b\u65f6\u628a \u8463\u592a\u6e05\u7684\u524d\u540e\u5de6\u53f3\u5168\u90e8\u7f69\u7740\uff0c\u4efb\u4ed6\u907f\u5411\u54ea\u65b9\uff0c\u90fd\u96be\u514d\u6328\u4e0a\u4e00\u4e24\u9897\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8463\u592a\u6e05\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u8eaf\u9661\u7684\u4e00\u7f29\uff0c\u53f3\u624b\u957f\u81c2\u6325\u821e\uff0c\u6768\u67f3\u9752\u6b63\u81ea\u5fc3\u9053\uff1a\u201c\u4f60\u8840\u8089\u4e4b \u8eaf\uff0c\u7eb5\u7136\u7ec3\u6709\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u4e5f\u96be\u6321\u6211\u795e\u5f39\u4e00\u51fb\u3002\u201d\u5fc3\u5ff5\u65b9\u52a8\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u998a\u9abc\u4e4b \u58f0\uff0c\u5341\u5206\u60a6\u8033\uff0c\u90a3\u4e9b\u5f39\u5b50\u7adf\u4f3c\u6253\u5728\u91d1\u5c5e\u4e4b\u4e0a\uff0c\u6768\u67f3\u9752\u7ecf\u8fc7\u65e0\u6570\u9635\u4ed7\uff0c\u53ef\u4ece\u6f8e\u89c1\u8fc7\u5982\u6b64\u602a\u5f02\u4e4b \u4e8b\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8463\u592a\u6e05\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6768\u548f\u795e\u5f39\uff0c\u4e00\u4ee3\u4e0d\u5982\u4e00\u4ee3\uff0c\u53ef\u60dc\u5475\u53ef\u60dc\uff01\u201d\u7eb5\u8eab \u4e00\u8dc3\uff0c\u957f\u81c2\u547c\u7684\u4e00\u4e0b\u6293\u5230\uff0c\u90b9\u7edb\u971e\u89c1\u6bcd\u4eb2\u5371\u6025\uff0c\u62d4\u51fa\u4f69\u5251\uff0c\u4fa7\u8fb9\u7a9c\u51fa\uff0c\u671d\u7740\u4ed6\u7684\u4fee\u81c2\u4e00\u957f\u5200 \u731b\u780d\u4e0b\u53bb\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u201c\u53ee\u5f53\u201d\u5927\u54cd\uff0c\u90a3\u5200\u660e\u660e\u780d\u4e2d\uff0c\u8463\u592a\u6e05\u5374\u6beb\u65e0\u53d7\u4f24\u7684\u8ff9\u8c61\uff0c\u53cd\u800c \u662f\u90b9\u7edb\u971e\u7684\u5200\u950b\u53cd\u5377\u8f6c\u6765\uff0c\u864e\u53e3\u4e5f\u9707\u5f97\u6c81\u51fa\u8840\u73e0\uff01 \u3000\u3000\u6768\u67f3\u9752\u5f13\u68a2\u4e00\u62e8\uff0c\u53f3\u638c\u4e00\u6325\u62cd\u51fa\uff0c\u5979\u7684\u6b66\u529f\u867d\u7136\u672a\u8db3\u4e0e\u5f53\u4e16\u9ad8\u624b\u6297\u8861\uff0c\u4f46\u89c1\u591a\u8bc6\u5e7f\uff0c\u94c1 \u638c\u795e\u5f39\u53c8\u662f\u5979\u7684\u5bb6\u4f20\u7edd\u6280\uff0c\u5012\u4e5f\u4e0d\u5bb9\u5c0f\u89c6\uff0c\u5979\u6599\u77e5\u8463\u5927\u6e05\u7684\u957f\u81c2\u5fc5\u6709\u53e4\u602a\uff0c\u8fd9\u4e00\u638c\u6b3a\u8eab\u62cd\u4ed6 \u80f8\u80c1\u7684\u201c\u4e09\u7126\u7a74\u201d\uff0c\u4e00\u638c\u62cd\u4e0b\uff0c\u5316\u4e3a\u4e09\u5f0f\uff0c\u98d8\u5ffd\u65e0\u5b9a\uff0c\u5f13\u68a2\u6240\u6307\uff0c\u53c8\u662f\u654c\u4eba\u7684\u54bd\u5589\u8981\u5bb3\uff0c\u8fd9 \u4e24\u62db\u90fd\u662f\u653b\u654c\u4eba\u6240\u5fc5\u6551\uff0c\u8463\u592a\u6e05\u8feb\u5f97\u653e\u5f00\u4e86\u90b9\u7edb\u971e\uff0c\u51dd\u795e\u63a5\u4e86\u6768\u67f3\u9752\u7684\u4e24\u62db\uff0c\u6768\u67f3\u9752\u53eb\u9053\uff1a \u201c\u971e\u513f\u5feb\u8d70\uff01\u201d\u5979\u60c5\u77e5\u81ea\u5df1\u4e0d\u662f\u8463\u592a\u6e05\u7684\u5bf9\u624b\uff0c\u53ea\u5f97\u7528\u7ed5\u8eab\u6e38\u6597\u7684\u65b9\u6cd5\uff0c\u6325\u638c\u6025\u88ad\uff0c\u610f\u6b32\u5c06 \u4ed6\u7f20\u4f4f\uff0c\u8ba9\u5973\u513f\u5f97\u4ee5\u593a\u8def\u800c\u9003\u3002\u5979\u8fdb\u62db\u4e4b\u65f6\uff0c\u672c\u5df2\u5168\u795e\u7559\u610f\u4ed6\u90a3\u6761\u53e4\u602a\u7684\u53f3\u81c2\uff0c\u54ea\u77e5\u6570\u62db\u4e00 \u8fc7\uff0c\u8463\u5927\u6e05\u500f\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u90a3\u6761\u53f3\u81c2\u7adf\u4f3c\u4f1a\u8f6c\u5f2f\u4f3c\u7684\uff0c\u7a81\u7136\u53cd\u638c\u6a2a\u626b\u56de\u6765\uff0c\u6768\u67f3\u9752\u7684\u5f13\u68a2\u6b63 \u6307\u5411\u4ed6\u989d\u89d2\u7684\u201c\u767d\u864e\u7a74\u201d\uff0c\u88ab\u4ed6\u53cd\u81c2\u4e00\u635e\uff0c\u767b\u65f6\u6298\u65ad\u3002\u90b9\u7edb\u971e\u521a\u5230\u95e8\u8fb9\uff0c\u4e00\u89c1\u6bcd\u4eb2\u5371\u9669\uff0c\u6025 \u5fd9\u56de\u8eab\u6765\u6551\u3002\u6768\u67f3\u9752\u5927\u60ca\u5931\u8272\uff0c\u534a\u622a\u5f13\u68a2\u8131\u624b\u63b7\u51fa\uff0c\u5de6\u638c\u5e94\u654c\uff0c\u53f3\u638c\u5ffd\u6325\uff0c\u60f3\u7528\u4e00\u80a1\u5de7\u52b2\u5c06 \u5973\u513f\u63a8\u5f00\uff0c\u54ea\u77e5\u8463\u5927\u6e05\u8fd8\u662f\u6bd4\u5979\u5feb\u4e86\u4e00\u6b65\uff0c\u4e00\u4f4e\u5934\u8eb2\u8fc7\u4e86\u6768\u67f3\u9752\u7684\u65ad\u5f13\uff0c\u53f3\u81c2\u547c\u7684\u4e00\u58f0\u6293\u5230 \u4e86\u90b9\u7edb\u971e\u7684\u7435\u7436\u9aa8\uff0c\u53ea\u8981\u7a0d\u4e00\u7528\u529b\uff0c\u7435\u7436\u9aa8\u4e00\u788e\uff0c\u90b9\u7edb\u971e\u7684\u6b66\u529f\u5c31\u8981\u5316\u4e3a\u4e4c\u6709\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5f39\u6307\u4e4b\u95f4\uff0c\u5ffd\u89c1\u91d1\u4e16\u9057\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u8fc7\u6765\uff0c\u5feb\u6377\u65e0\u6bd4\uff0c\u8eab\u5b50\u8fd8\u672a\u7ad9\u5b9a\uff0c\u94c1\u62d0\u5df2\u6307 \u5230\u8463\u592a\u6e05\u7684\u80f8\u524d\uff0c\u8463\u592a\u6e05\u4e00\u58f0\u602a\u53eb\uff0c\u5012\u7eb5\u51fa\u516b\u5c3a\u5f00\u5916\uff0c\u6293\u7740\u90b9\u8702\u971e\u7684\u90a3\u6761\u602a\u81c2\uff0c\u81ea\u7136\u4e5f\u653e\u5f00 \u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u771f\u662f\u5927\u51fa\u6768\u67f3\u9752\u610f\u6599\u4e4b\u5916\uff0c\u5979\u5fc3\u76ee\u4e2d\u7684\u5927\u654c\u672c\u6765\u662f\u91d1\u4e16\u9057\uff0c\u5c82\u77e5\u91d1\u4e16\u9057\u53cd\u800c\u6551\u4e86 \u5979\u7684\u5973\u513f\uff0c\u6768\u67f3\u9752\u60ca\u7591\u672a\u5b9a\uff0c\u53ea\u89c1\u91d1\u4e16\u9057\u5de6\u62d0\u53f3\u5251\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2\u8fde\u8fdb\u6570\u62db\uff0c\u5c06\u8463\u5927\u6e05\u8feb\u5230 \u5899\u89d2\u3002\u8fd9\u672c\u6765\u662f\u7edd\u597d\u7684\u8131\u8eab\u673a\u4f1a\uff0c\u6768\u67f3\u9752\u5374\u53cd\u800c\u5446\u4f4f\u4e86\uff0c\u7adf\u6ca1\u6709\u60f3\u5230\u9003\u8d70\u7684\u5ff5\u5934\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u91cd\u5927\u6e05\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d\u91d1\u4e16\u9057\u201c\u5478\u201d\u7684\u4e00\u53e3\u553e\u6d8e\u98de\u53bb\uff0c\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u4e5f\u914d\u95ee\u6211\u7684\u5e08\u7236\uff1f\u201d\u8463\u5927\u6e05\u4f3c\u4e4e\u77e5\u9053\u4ed6\u7684\u553e\u6d8e\u4e4b\u4e2d\u6742\u6709\u6bd2\u9488\uff0c\u90a3\u6761\u53e4\u602a\u7684\u53f3\u81c2\u638c\u5fc3\u4e00 \u7ffb\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u4e24\u58f0\uff0c\u91d1\u4e16\u9057\u7684\u98de\u9488\u6697\u5668\u7adf\u4f3c\u5c04\u5230\u4e86\u94c1\u677f\u4e0a\u4f3c\u7684\uff0c\u53d1\u51fa\u60a6\u8033\u7684\u91d1\u5c5e\u58f0\u54cd\uff0c\u90a3 \u53e3\u553e\u6d8e\u4e5f\u6d82\u6ee1\u4e86\u8463\u592a\u6e05\u4ed6\u624b\u5fc3\u3002\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00\u51db\uff0c\u53ea\u542c\u5f97\u8463\u592a\u6e05\u53c8\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u91d1\u4e16\u9057 \u90a3\u80af\u4f4f\u624b\uff0c\u94c1\u5251\u53cd\u624b\u4e00\u6325\uff0c\u8361\u5f00\u4e86\u9ec4\u77f3\u9053\u4eba\u4ece\u80cc\u540e\u626b\u6765\u7684\u62c2\u5c18\uff0c\u5de6\u624b\u957f\u62d0\u4e00\u4e2a\u201c\u6bd2\u86c7\u51fa\u6d1e\u201d \u6025\u6233\u8463\u592a\u6e05\u7684\u80f8\u53e3\u547d\u95e8\u8981\u5bb3\u3002\u539f\u6765\u91d1\u4e16\u9057\u7684\u60f3\u6cd5\u4e0e\u4e16\u4fd7\u9047\u5f02\u3002\u4ed6\u4ee5\u524d\u56e0\u4e3a\u6768\u67f3\u9752\u662f\u94c1\u638c\u795e\u5f39 \u4e4b\u540e\uff0c\u4fbf\u6545\u610f\u8981\u632b\u6298\u5979\u7684\u5a01\u98ce\uff0c\u800c\u4eca\u89c1\u5979\u5bf9\u81ea\u5df1\u5982\u6b64\u75db\u6068\uff0c\u4fbf\u6545\u610f\u8981\u820d\u547d\u6551\u5979\uff0c\u8ba9\u5979\u81ea\u5df1\u60ed \u6127\uff0c\u540c\u65f6\uff0c\u4ed6\u9002\u624d\u89c1\u90b9\u7edb\u971e\u90a3\u822c\u5bb3\u6015\u81ea\u5df1\uff0c\u60f3\u8d77\u674e\u6c81\u6885\u7684\u8bdd\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6709\u70b9\u6094\u610f\uff0c\u6240\u4ee5\u4ed6\u4e4b \u6240\u4ee5\u7518\u613f\u5728\u5f3a\u654c\u5939\u51fb\u4e4b\u4e0b\uff0c\u51fa\u624b\u6551\u6768\u67f3\u9752\u6bcd\u5973\uff0c\u5fc3\u60c5\u53ef\u8bf4\u662f\u5341\u5206\u590d\u6742\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u89c1\u91d1\u4e16\u9057\u5ffd\u7136\u820d\u4e86\u81ea\u5df1\uff0c\u53bb\u6551\u6768\u67f3\u9752\u6bcd\u5973\uff0c\u9887\u51fa\u610f\u5916\u3002\u4ed6\u81ea\u9ad8\u8eab\u4efd\uff0c\u672c\u4e0d\u60f3\u4ee5 \u4e24\u5927\u9ad8\u624b\u4e4b\u529b\uff0c\u5408\u51fb\u91d1\u4e16\u9057\uff0c\u5982\u4eca\u89c1\u91d1\u4e16\u9057\u5bf9\u81ea\u5df1\u9080\u6765\u7684\u540c\u4f34\u8fde\u65bd\u6740\u624b\uff0c\u53ea\u5f97\u4ece\u80cc\u540e\u5077\u88ad\uff0c \u4f46\u4ed6\u7ec8\u4ee5\u5077\u88ad\u4e3a\u803b\uff0c\u8fd9\u4e00\u62c2\u5e76\u672a\u7528\u5c3d\u5168\u529b\uff0c\u7528\u610f\u53ea\u662f\u89e3\u8463\u592a\u6e05\u4e4b\u5371\u3002 \u3000\u3000\u54ea\u77e5\u91d1\u4e16\u9057\u5374\u662f\u7acb\u5fc3\u5148\u628a\u8463\u592a\u6e05\u6bd9\u4e86\u518d\u8bf4\uff0c\u542c\u5f97\u80cc\u540e\u52b2\u98ce\u62c2\u6765\uff0c\u53ea\u662f\u53cd\u5251\u4e00\u6325\uff0c\u7adf\u4e0d\u987e \u9ec4\u77f3\u9053\u4eba\u6709\u5426\u8fde\u7eed\u7684\u6740\u7740\uff0c\u811a\u6b65\u5e76\u4e0d\u505c\u7559\uff0c\u5de6\u624b\u94c1\u62d0\u4ecd\u662f\u5411\u524d\u731b\u6233\uff01 \u3000\u3000\u8463\u592a\u6e05\u7684\u81c2\u818a\u867d\u957f\uff0c\u7a76\u7adf\u4e0d\u5982\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u957f\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u5df2\u8feb\u5230\u4ed6\u4eec\u7684\u80f8\u524d\uff0c\u770b\u6765 \u4ed6\u7edd\u65e0\u53cd\u51fb\u7684\u53ef\u80fd\uff0c\u5373\u91d1\u4e16\u9057\u4e5f\u4ee5\u4e3a\u8fd9\u4e00\u62d0\u975e\u628a\u654c\u4eba\u9001\u547d\u4e0d\u53ef\uff0c\u54ea\u6599\u8463\u592a\u6e05\u8eab\u5f62\u672a\u53d8\uff0c\u957f\u81c2 \u4e00\u6325\uff0c\u201c\u5417\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u4ed6\u7adf\u7136\u786c\u751f\u751f\u7684\u6321\u4e86\u4e00\u8bb0\u3002\u91d1\u4e16\u9057\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u51ed\u4eba\u7684\u8840\u8089 \u4e4b\u8eaf\uff0c\u6b66\u529f\u7ec3\u5230\u7edd\u9876\uff0c\u4e5f\u4e0d\u80fd\u4e0e\u94c1\u62d0\u76f8\u78b0\u3002\u771f\u662f\u96be\u4ee5\u601d\u8bae\u4e4b\u4e8b\u3002\u4f46\u8fd8\u6709\u66f4\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\u6309\u7eed \u51fa\u73b0\uff0c\u8463\u592a\u6e05\u683c\u5f00\u94c1\u62d0\uff0c\u957f\u81c2\u4e00\u4f38\uff0c\u9661\u7136\u95f4\u53c8\u66b4\u957f\u4e86\u5c06\u8fd1\u4e00\u5c3a\uff0c\u4ece\u6599\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u5ffd\u7136\u6293\u5230 \u4e86\u91d1\u4e16\u9057\u7684\u80a9\u5934\u3002\u9ad8\u624b\u6bd4\u6597\uff0c\u76f8\u5dee\u6beb\u5398\uff0c\u5982\u4eca\u8463\u592a\u6e05\u7684\u81c2\u818a\u7a81\u7136\u4f1a\u957f\u51fa\u4e00\u5c3a\uff0c\u786e\u662f\u5929\u4e0b\u6b66\u529f \u5747\u65e0\u7684\u201c\u602a\u62db\u201d\uff0c\u9976\u662f\u91d1\u4e16\u9057\u673a\u8b66\u975e\u5e38\uff0c\u8d8b\u95ea\u5947\u5feb\uff0c\u4e5f\u88ab\u8463\u592a\u6e05\u53e4\u602a\u7684\u81c2\u818a\u642d\u5728\u80a9\u5934\uff0c\u6240\u89e6 \u4e4b\u5904\uff0c\u4f46\u89c9\u4e00\u7247\u51b0\u51b7\uff0c\u540c\u65f6\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u53c8\u5df2\u62c2\u5230\uff0c\u5c18\u5c3e\u6563\u5f00\uff0c\u5343\u4e1d\u4e07\u7f15\uff0c\u597d\u50cf\u4e00\u5f20\u7f69 \u7f51\uff0c\u5230\u4e86\u91d1\u4e16\u9057\u7684\u5934\u4e0a\u3002\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u4e0d\u60f3\u6211\u547d\u4e27\u6b64\u5730\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u8033\u8fb9\u6709\u4eba\u7b11\u9053\uff0c\u201c\u6211\u7b97\u8fc7\u4e86\uff0c\u4f60\u670d\u4e0b\u4e86\u78a7\u7075\u4e39\uff0c\u8fd8\u8be5\u6709\u4e09\u5341\u516d \u5929\u7684\u6027\u547d\uff0c\u6015\u4ec0\u4e48\uff1f\u201d\u9661\u89c1\u8463\u592a\u6e05\u4e00\u8dc3\u8dc3\u5f00\uff0c\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u4e5f\u79bb\u5f00\u4e86\u81ea\u5df1\u7684\u5934\u9876\uff0c\u91d1\u4e16\u9057 \u4e00\u770b\uff0c\u539f\u6765\u662f\u51af\u7433\u6bcd\u5973\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u53c8\u56de\u5230\u4e86\u5e97\u4e2d\uff0c\u9ec4\u77f3\u9053\u4eba\u4e0e\u8463\u592a\u6e05\u4e0d\u77e5\u662f\u5979\u7528\u4ec0\u4e48\u8d85\u5999 \u7684\u6b66\u529f\uff0c\u4e00\u4e3e\u624b\u5c31\u51fb\u9000\u4e86\u3002 \u3000\u3000\u6768\u67f3\u9752\u5927\u559c\u5982\u72c2\uff0c\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u6653\u6f9c\u6ca1\u6709\u548c\u4f60\u4e00\u540c\u6765\u5417\uff1f\u201d\u51af\u745b\u3001\u51af\u7433\u6781\u4e3a\u76f8\u4f3c\uff0c\u9664 \u4e86\u81f3\u4eb2\u7684\u4e08\u592b\u513f\u5b50\u4e4b\u5916\uff0c\u522b\u4eba\u5b9e\u662f\u96be\u4ee5\u5206\u8fa8\uff0c\u51af\u7433\u542c\u5f97\u6768\u67f3\u9752\u8bef\u8ba4\u81ea\u5df1\u4f5c\u59d0\u59d0\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u6653\u6f9c\u5417\uff1f\u563b\u563b\uff0c\u4ed6\u6ca1\u6709\u6765\u3002\u201d\u4e00\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u7740\u8463\u592a\u6e05\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6761\u81c2\u818a \u751a\u662f\u90aa\u95e8\uff0c\u501f\u6765\u7ed9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u4e0d\u77e5\u51af\u7433\u7684\u6765\u5386\uff0c\u89c1\u5979\u521a\u624d\u8863\u8896\u4e00\u62c2\uff0c\u5c31\u5c06\u81ea\u5df1\u7684\u62c2\u5c18\u8361\u5f00\uff0c\u6b66\u529f\u7adf\u662f\u597d\u5f97\u51fa \u5947\uff0c\u5fc3\u4e2d\u60ca\u6115\u4e0d\u5df2\uff0c\u672c\u6709\u51e0\u5206\u602f\u610f\uff0c\u4f46\u542c\u5979\u5a5a\u7b11\u81ea\u5982\uff0c\u4e00\u526f\u6beb\u4e0d\u628a\u654c\u4eba\u653e\u5728\u773c\u5185\u7684\u795e\u6c14\uff0c\u53c8 \u7981\u4e0d\u4f4f\u5fc3\u5934\u706b\u8d77\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u6709\u9760\u5c71\u6211\u4e5f\u4e0d\u60e7\uff0c\u54b1\u4eec\u518d\u51b3\u96cc\u96c4\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u8bf7 \u4eba\u5e2e\u624b\uff1f\u201d\u62c2\u5c18\u4e00\u8d77\uff0c\u8fde\u62c2\u91d1\u4e16\u9057\u7684\u201c\u5c11\u9633\u201d\u201c\u5927\u9634\u201d\u201c\u9633\u660e\u201d\u4e09\u5904\u7a74\u9053\uff01 \u3000\u3000\u91d1\u4e16\u9057\u7a81\u89c1\u51af\u7433\u6bcd\u5973\u6765\u5230\uff0c\u5fc3\u4e2d\u4e00\u7247\u832b\u7136\uff0c\u4e0d\u77e5\u6240\u63aa\uff0c\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u62c2\u5230\uff0c\u4ed6\u624b\u4e2d\u7684 \u94c1\u62d0\u8fd8\u672a\u4e3e\u8d77\u6765\u3002 \u3000\u3000\u674e\u6c81\u6885\u7a81\u7136\u4ece\u65c1\u6740\u51fa\uff0c\u5a07\u58f0\u53f1\u9053\uff1a\u201c\u725b\u9f3b\u5b50\uff0c\u81ed\u9053\u58eb\uff0c\u4f60\u6562\u6b3a\u8d1f\u6211\u7684\u54e5\u54e5\uff0c\u770b\u5251\uff01\u201d\u624b \u8155\u4e00\u7ffb\uff0c\u5251\u5149\u98d8\u5ffd\uff0c\u4f3c\u5de6\u4f3c\u53f3\uff1a\uff0c\u77bb\u524d\u5ffd\u540e\uff0c\u8981\u77e5\u674e\u6c81\u6885\u7684\u529f\u529b\u867d\u7136\u4e0d\u9ad8\uff0c\u4f46\u5251\u6cd5\u5374\u662f\u767d\u53d1 \u9b54\u5973\u8fd9\u4e00\u6d3e\u7684\u5ae1\u7cfb\u771f\u4f20\uff0c\u8be1\u8bf1\u767e\u53d8\uff0c\u4e3e\u4e16\u65e0\u53cc\uff0c\u9ec4\u77f3\u9053\u4eba\u5728\u77f3\u6797\u91cc\u6f5c\u4fee\u4e86\u51e0\u5341\u5e74\u4eba\u54ea\u66fe\u89c1\u8fc7 \u5982\u6b64\u5947\u5999\u7684\u5251\u6cd5\uff0c\u767b\u65f6\u7ed9\u8feb\u9000\u3002 \u3000\u3000\u91d1\u4e16\u9057\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u51af\u7433\u5df2\u89e3\u4e0b\u4e86\u4e00\u6761\u5f69\u8272\u7684\u7ef8\u5e26\uff0c\u8f7b\u8f7b\u98d8\u52a8\uff0c\u7b11\u563b\u563b\u5730\u76ef\u7740\u8463\u592a \u6e05\uff0c\u90a3\u60c5\u5f62\u5c31\u50cf\u732b\u6349\u8001\u9f20\u4e00\u6837\uff0c\u8981\u5c3d\u60c5\u620f\u5f04\u591f\u4e86\uff0c\u8fd9\u624d\u52a8\u624b\uff0c\u91d1\u4e16\u9057\u60f3\u7b11\u5374\u7b11\u4e0d\u51fa\u6765\u3002\u8463\u592a \u6e05\u80cc\u9760\u5899\u58c1\u3001\u84c4\u52bf\u5f85\u654c\uff0c\u770b\u60c5\u5f62\u5c31\u5c06\u51fa\u624b\uff1b\u6768\u67f3\u9752\u8fd9\u65f6\u5374\u60a0\u7136\u81ea\u5f97\uff0c\u62c9\u7740\u5973\u513f\u7ad9\u5728\u4e00\u65c1\u89c2 \u6218\uff0c\u6307\u70b9\u7b11\u9053\uff1a\u201c\u5510\u4f2f\u6bcd\u6765\u4e86\uff0c\u518d\u5389\u5bb3\u7684\u9b54\u5934\u4e5f\u4e0d\u7528\u5bb3\u6015\u4e86\u3002\u201d\u5979\u4e0e\u51af\u73bb\u65e7\u65f6\u867d\u6709\u5acc\u9699\uff0c\u5927 \u5bb6\u7ed3\u5a5a\u4e4b\u540e\uff0c\u65e9\u5df2\u70df\u6d88\u4e91\u6563\uff0c\u8fd9\u65f6\u5979\u5bf9\u5973\u513f\u5938\u8000\u201c\u51af\u745b\u201d\uff0c\u5fc3\u4e2d\u5b9e\u6709\u201c\u4e0e\u6709\u8363\u7109\u201d\u4e4b\u611f\u3002\u5979 \u8fd8\u672a\u77e5\u9053\u8fd9\u4e0d\u662f\u51af\u745b\u800c\u662f\u51af\u7433\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u662f\u5475\uff0c\u5979\u4eec\u6bcd\u5973\u6765\u4e86\uff0c\u6211\u8fd8\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u94c1\u62d0\u4e00\u70b9\uff0c\u7a81 \u7136\u98de\u8eab\u4fbf\u8d70\uff0c\u7a7f\u8fc7\u95e8\u6237\u4e4b\u65f6\uff0c\u51e0\u4e4e\u649e\u7740\u4e86\u6768\u67f3\u9752\uff0c\u6768\u67f3\u9752\u76ee\u5149\u4e0e\u4ed6\u4e00\u89e6\uff0c\u7acb\u5373\u907f\u5f00\uff0c\u6562\u60c5\u662f \u611f\u5230\u5c34\u5c2c\uff0c\u6709\u4e9b\u60ed\u6127\u3002 \u3000\u3000\u51af\u7433\u56b7\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5403\u4e86\u6211\u7684\u4e1c\u897f\uff0c\u8fd8\u672a\u591a\u8c22\u5462\uff1f\u201d\u4e3e\u6b65\u6b32\u8ffd\uff0c\u8463\u5927\u6e05\u4e58\u5979\u5206\u5fc3\u4e4b\u9645\uff0c \u7a81\u7136\u7136\u5927\u559d\u4e00\u58f0\uff0c\u957f\u81c2\u4e00\u4f38\uff0c\u6402\u5934\u4fbf\u6293\uff0c\u51af\u7433\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u5148\u628a\u4f60\u7684\u722a\u5b50\u5207\u4e86\uff0c\u518d\u8ffd\u4ed6\u4e5f \u8fd8\u4e0d\u8fdf\uff01\u201d\u7ef8\u5e26\u8f7b\u8f7b\u4e00\u5377\uff0c\u7f20\u7740\u4e86\u8463\u592a\u6e05\u90a3\u6761\u53e4\u602a\u7684\u81c2\u818a\uff0c\u4e24\u4eba\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8463\u5927\u6e05\u8fd9\u6761 \u81c2\u818a\u662f\u4ed6\u6700\u81ea\u6301\u7684\u5389\u5bb3\u6b66\u5668\uff0c\u8fd9\u4e00\u6293\u529b\u9053\u4f55\u6b62\u5343\u65a4\uff0c\u5374\u88ab\u51af\u7433\u4e00\u6761\u8f7b\u98d8\u98d8\u7684\u7ef8\u5e26\u5377\u4f4f\uff0c\u4e0d\u80fd \u5411\u524d\u63a8\u52a8\u3002\u800c\u51af\u7433\u7684\u60ca\u5f02\u66f4\u751a\uff0c\u770b\u8463\u592a\u6e05\u7684\u6b66\u529f\uff0c\u90a3\u8fd8\u5728\u91d1\u4e16\u9057\u4e4b\u4e0b\uff0c\u8fd9\u6761\u81c2\u818a\u5374\u5982\u94dc\u6d47\u94c1 \u94f8\u4e00\u822c\u3002\u8981\u77e5\u51af\u7433\u7684\u98de\u82b1\u6458\u53f6\u529f\u592b\uff0c\u5df2\u7ec3\u5230\u4e86\u6700\u4e0a\u4e58\u7684\u5883\u754c\uff0c\u5373\u7b97\u662f\u8d64\u795e\u5b50\u90a3\u6837\u7684\u5927\u9b54\u5934\uff0c \u4ee5\u524d\u88ab\u51af\u7433\u7684\u7ef8\u5e26\u6240\u5377\uff0c\u8981\u4e0d\u662f\u5510\u6653\u6f9c\u7ed9\u8d64\u795e\u5b50\u8bf4\u60c5\uff0c\u4ed6\u90a3\u6761\u81c2\u818a\u4e5f\u65e9\u5df2\u4e0d\u4fdd\uff0c\u4f46\u8fd9\u4e2a\u8463\u592a \u6e05\u5c45\u7136\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u597d\u50cf\u6beb\u65e0\u75db\u82e6\u7684\u611f\u89c9\u3002 \u3000\u3000\u51af\u7433\u751f\u6027\u987d\u76ae\uff0c\u8001\u800c\u4e0d\u6539\uff0c\u8d8a\u78b0\u5230\u5f3a\u624b\u8d8a\u4e3a\u9ad8\u5174\uff0c\u987f\u65f6\u5c06\u8ffd\u91d1\u4e16\u9057\u7684\u4e8b\u6482\u8fc7\u4e00\u8fb9\uff0c\u563b\u563b \u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6761\u81c2\u818a\u679c\u771f\u662f\u6709\u70b9\u90aa\u95e8\uff0c\u975e\u501f\u6765\u770b\u770b\u4e0d\u53ef\u3002\u201d\u7ef8\u5e26\u4e00\u677e\uff0c\u5411\u4e0a\u79fb\u52a8\u4e09\u5bf8\uff0c\u8463\u592a \u6e05\u4ecd\u4e0d\u4e3a\u6240\u52a8\uff0c\u51af\u7433\u53c8\u5411\u4e0a\u79fb\u52a8\u4e09\u5bf8\uff0c\u51e0\u4e4e\u5230\u4e86\u81c2\u818a\u4e0e\u80a9\u5934\u63a5\u89e6\uff0c\u8463\u592a\u6e05\u5389\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u65e2 \u8981\u501f\uff0c\u5c31\u9001\u7ed9\u4f60\u7528\uff01\u201d\u957f\u66fe\u818a\u5ffd\u5730\u79bb\u80a9\u98de\u8d77\uff0c\u5411\u51af\u7433\u8fce\u9762\u6293\u6765\uff0c\u51af\u7433\u8fd8\u771f\u672a\u66fe\u89c1\u8fc7\u8fd9\u79cd\u201c\u602a \u62db\u201d\uff0c\u7528\u91d1\u521a\u6307\u529b\u5c06\u8fd9\u6761\u65ad\u81c2\u63a5\u7740\uff0c\u8863\u8896\u65e9\u5df2\u892a\u4e0b\uff0c\u53ea\u89c1\u8fd9\u6761\u81c2\u818a\u5c5e\u6f06\u53d1\u5149\uff0c\u539f\u6765\u662f\u4e00\u6761\u94c1 \u81c2\uff01 \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u602a\u9053\u6211\u52d2\u5b83\u4e0d\u65ad\u3002\u201d\u539f\u6765\u8463\u592a\u6e05\u5f53\u5e74\u88ab\u6768\u4ef2\u82f1\u4e00\u638c\u6253\u6298\u53f3\u81c2\uff0c\u867d\u7136\u8fd8\u53ef\u4ee5 \u9a73\u7b4b\u7eed\u9aa8\uff0c\u4f46\u5230\u5e95\u4e0d\u5982\u5e38\u4eba\uff0c\u4ed6\u4e00\u53d1\u72e0\uff0c\u7d22\u6027\u628a\u81c2\u818a\u5207\u4e0b\u6765\uff0c\u6362\u4e86\u4e00\u6761\u94c1\u81c2\uff0c\u4ed6\u4e5f\u771f\u6709\u8010 \u5fc3\uff0c\u7adf\u7136\u524a\u53d1\u4e3a\u50e7\uff0c\u9690\u59d3\u57cb\u540d\uff0c\u82e6\u7ec3\u6210\u4e86\u94c1\u81c2\u795e\u529f\uff0c\u8fd9\u624d\u91cd\u51fa\u6c5f\u6e56\uff0c\u6ee1\u4ee5\u4e3a\u53ef\u4ee5\u79f0\u96c4\u9053\u9738\uff0c \u8c01\u77e5\u7b2c\u4e00\u6b21\u548c\u4eba\u4ea4\u624b\uff0c\u5c31\u88ab\u51af\u7433\u628a\u4ed6\u7684\u94c1\u81c2\u6536\u4e86\u3002 \u3000\u3000\u51af\u7433\u7b11\u563b\u563b\u7684\u628a\u73a9\u8fd9\u6761\u94c1\u81c2\uff0c\u5ffd\u800c\u5e84\u91cd\u8bf4\u9053\uff1a\u201c\u4e5f\u771f\u96be\u4e3a\u4f60\u7ec3\u5f97\u8fd9\u822c\u7075\u6d3b\uff0c\u5c45\u7136\u548c\u771f\u7684 \u81c2\u818a\u4e00\u822c\uff01\u5582\uff0c\u4f60\u662f\u600e\u4e48\u7ec3\u7684\uff1f\u5582\uff0c\u4f60\u4e0d\u5982\u628a\u5de6\u8fb9\u90a3\u6761\u81c2\u818a\u5207\u4e86\u4e0b\u6765\uff0c\u540c\u6837\u6362\u4e0a\u4e00\u6761\u94c1\u81c2\uff0c \u5c82\u4e0d\u662f\u6b66\u529f\u53ef\u4ee5\u7acb\u5373\u589e\u5f3a\u4e00\u500d\u5e7f\u8bf4\u5f97\u751a\u662f\u8ba4\u771f\uff0c\u7adf\u4f3c\u201c\u70ed\u5fc3\u201d\u4e3a\u4eba\u6253\u7b97\uff0c\u8463\u592a\u6e05\u7ed9\u4ed6\u5f04\u5f97\u557c \u7b11\u7686\u975e\uff0c\u966a\u7b11\u6c42\u9053\uff1a\u201c\u4f60\u628a\u8fd9\u6761\u94c1\u81c2\u8fd8\u7ed9\u6211\u5427\uff0c\u6211\u800c\u4eca\u660e\u767d\u4e86\uff0c\u4e16\u4e0a\u539f\u6765\u6709\u8fd9\u7b49\u4e0a\u4e58\u7684\u6b66 \u529f\uff0c\u6211\u5c31\u662f\u518d\u7ec3\u4e09\u5341\u5e74\uff0c\u6b66\u529f\u518d\u5f3a\u5341\u500d\uff0c\u4e5f\u8fd8\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u6211\u8981\u4e24\u6761\u94c1\u81c2\u4e5f\u6ca1\u6709\u7528\u5475\uff01\u201d \u51af\u7433\u5c0f\u5b69\u813e\u6c14\uff0c\u7ed9\u4ed6\u4e00\u6367\uff0c\u4e50\u4e0d\u53ef\u652f\uff0c\u9053\uff1a\u201c\u597d\uff0c\u8fd8\u7b97\u4f60\u6709\u81ea\u77e5\u4e4b\u660e\uff01\u201d\u8d77\u624b\u4e00\u6325\uff0c\u610f\u6b32\u628a \u4ed6\u9063\u8d70\uff0c\u5ffd\u53c8\u8bf4\u9053\uff1a\u201c\u4f60\u4e14\u7ad9\u4f4f\uff0c\u5f85\u6211\u53d1\u843d\u3002\u201d\u6b63\u6253\u7b97\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u548c\u91d1\u4e16\u9057\u6253\u67b6\uff0c\u5ffd\u542c\u5f97\u5973 \u513f\u53eb\u9053\uff1a\u201c\u5988\uff0c\u8fd9\u725b\u9f3b\u5b50\u4e0d\u597d\u5bf9\u4ed8\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u597d\u5bf9\u4ed8\uff1f\u201d\u628a\u94c1\u81c2\u4e00\u8f6c\uff0c\u6307\u7740\u8463 \u592a\u6e05\u9053\uff1a\u201c\u4f60\u968f\u8def\u6253\u67b6\uff0c\u4e0d\u662f\u597d\u4eba\uff0c\u7f5a\u4f60\u7ad9\u5728\u8fd9\u513f\uff0c\u52a8\u4e5f\u4e0d\u8bb8\u4e00\u52a8\uff0c\u4f60\u82e5\u6562\u5077\u8d70\uff0c\u6211\u5c31\u628a\u4f60 \u5de6\u8fb9\u7684\u8fd9\u6761\u81c2\u818a\u4e5f\u5207\u4e0b\u6765\u3002\u201d\u8463\u592a\u6e05\u5e74\u8fd1\u516d\u5341\uff0c\u51af\u7433\u5374\u8fd8\u662f\u4e2a\u56db\u5341\u672a\u5230\u7684\u4e2d\u5e74\u7f8e\u5987\uff0c\u8bf4\u8bdd\u7684 \u795e\u6c14\uff0c\u5374\u50cf\u5148\u751f\u7f5a\u5c0f\u5b66\u751f\u4e00\u6837\uff0c\u90b9\u7edb\u971e\u4e0d\u89c9\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u6768\u67f3\u9752\u76b1\u76b1\u7709\uff0c\u5fc3\u9053\uff1a\u201c\u591a\u5e74\u4e0d \u89c1\uff0c\u600e\u4e48\u51af\u745b\u8fde\u813e\u6c14\u90fd\u5b8c\u5168\u53d8\u4e86\uff1f\u201d \u3000\u3000\u51af\u7433\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5973\u513f\u7ed9\u9ec4\u77f3\u9053\u4eba\u8feb\u5f97\u8fde\u8fde\u540e\u9000\u3002\u539f\u6765\u674e\u6c81\u6885\u7684\u5251\u6cd5\u867d\u7136\u8be1\u8bf7\u7edd\u4f26\uff0c \u4f46\u529f\u529b\u5230\u5e95\u76f8\u5dee\u592a\u8fdc\uff0c\u5f00\u9996\u5341\u4f59\u62db\u8fc7\u540e\uff0c\u9ec4\u77f3\u9053\u4eba\u53ea\u5b88\u4e0d\u653b\uff0c\u89c1\u674e\u6c81\u6885\u65e0\u6cd5\u653b\u5165\uff0c\u5fc3\u4e2d\u6e10\u6e10 \u4e0d\u5bb3\u6015\u4e86\uff0c\u8bd5\u8fd0\u8db3\u771f\u529b\uff0c\u7528\u91cd\u624b\u6cd5\u8361\u5979\u7684\u9752\u94a2\u5251\uff0c\u674e\u6c81\u6885\u679c\u7136\u652f\u6301\u4e0d\u4f4f\uff0c\u547c\u547c\u7684\u5598\u8d77\u6c14\u6765\u3002 \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u4e2b\u5934\u5c31\u77e5\u9053\u8981\u9760\u5988\u5988\u3002\u201d\u674e\u6c81\u6885\u8d4c\u6c14\u9053\uff1a\u201c\u597d\uff01\u5c31\u4e0d\u6c42\u4f60\uff01\u201d\u8bf4\u8bdd \u4e4b\u95f4\uff0c\u5ffd\u88ab\u9ec4\u77f3\u9053\u4eba\u5c18\u5c3e\u4e00\u62c2\uff0c\u51e0\u4e4e\u628a\u5979\u7684\u9752\u94a2\u5251\u593a\u51fa\u624b\u53bb\uff0c\u51af\u7433\u9053\uff1a\u201c\u4f60\u5e72\u561b\u4e0d\u7528\u6211\u65b0\u8fd1 \u6559\u4f60\u7684\u70b9\u7a74\u624b\u6cd5\u5475\uff1f\u5148\u6765\u4e00\u62db\u2018\u51b0\u6cb3\u89e3\u51bb\u2019\uff0c\u518d\u63a5\u4e00\u62db\u2018\u94f6\u6c49\u98de\u6413\uff0c\u597d\uff0c\u5bf9\uff0c\u53cd\u624b\u70b9\u4ed6\u7684\u767d \u6d77\u7a74\uff01\u201d\u674e\u6c81\u6885\u672c\u60f3\u8d4c\u6c14\u4e0d\u542c\u6bcd\u4eb2\u6240\u6559\uff0c\u4f46\u7ed3\u679c\u8fd8\u662f\u8feb\u5f97\u7528\u4e86\u5979\u6307\u70b9\u7684\u62db\u6570\u3002\u8fd9\u5957\u70b9\u7a74\u6cd5\u662f \u51af\u7433\u5728\u5ce8\u5d4b\u5c71\u4e2d\u7528\u4e86\u6570\u65e5\u5fc3\u529b\u60f3\u51fa\u6765\u7684\uff0c\u672c\u662f\u6559\u5973\u513f\u7528\u4ee5\u5bf9\u4ed8\u91d1\u4e16\u9057\u7684\uff0c\u51fa\u624b\u5947\u7279\u4e4b\u6781\uff0c\u5f53 \u65e5\u7a7a\u624b\u620f\u6597\uff0c\u91d1\u4e16\u9057\u51e0\u4e4e\u5403\u4e86\u4e8f\uff0c\u800c\u4eca\u914d\u4e0a\u5947\u8be1\u7edd\u6ca6\u7684\u5251\u6cd5\uff0c\u9ec4\u77f3\u9053\u4eba\u7684\u653b\u52bf\uff0c\u679c\u7136\u7acb\u5373\u53d7 \u632b\uff01 \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6709\u4ec0\u4e48\u4e0d\u597d\u5bf9\u4ed8\uff0c\u6211\u8981\u4f60\u7528\u81ea\u5df1\u7684\u529b\u91cf\u6253\u8d25\u4ed6\uff0c\u54c8\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c \u4f60\u7ec8\u4e0d\u80fd\u9760\u5988\u4e00\u8f88\u5b50\u5475\uff01\u201d\u9ec4\u77f3\u9053\u4eba\u542c\u5979\u6307\u70b9\u5973\u513f\uff0c\u7adf\u7136\u662f\u628a\u81ea\u5df1\u5f53\u505a\u7ed9\u5979\u5973\u513f\u7ec3\u62db\u7684\u7528 \u5177\uff0c\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u51e0\u4e4e\u7ed9\u674e\u6c81\u6d77\u70b9\u4e2d\u7a74\u9053\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u6025\u6025\u51dd\u795e\u5bf9\u4ed8\uff0c\u548c\u674e\u6c81\u6885\u6253\u6210\u4e86\u4e00 \u4e2a\u5e73\u624b\u3002\u51af\u7433\u4e00\u9762\u6307\u70b9\uff0c\u4e00\u9762\u7559\u795e\u77a7\u9ec4\u77f3\u9053\u4eba\u7684\u6b66\u529f\uff0c\u5fc3\u4e2d\u6697\u53eb\u201c\u4e0d\u5999\uff01\u201d\u60f3\u9053\uff1a\u201c\u8fd9\u725b\u9f3b \u679c\u7136\u6709\u4e9b\u672c\u9886\uff0c\u6253\u5f97\u4e45\u4e86\uff0c\u6885\u513f\u975e\u8f93\u4e0d\u53ef\u3002\u201d\u4f46\u5979\u6709\u6d3b\u5728\u5148\uff0c\u8981\u5973\u513f\u72ec\u529b\u6253\u8d25\u654c\u4eba\uff0c\u4e0d\u597d\u610f \u601d\u4e0b\u573a\u5e2e\u624b\u3002 \u3000\u3000\u6597\u4e86\u4e00\u9635\uff0c\u674e\u6c81\u6885\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4e3a\u4ec0\u4e48\u628a\u4e16\u9057\u54e5\u653e\u8d70\u4e86\uff1f\u201d\u51af\u7433\u731b\u7684\u4e00\u9192\uff0c\u53eb \u9053\uff1a\u201c\u5bf9\uff0c\u6211\u5c31\u53bb\u8ffd\u4ed6\uff0c\u91d1\u9488\u5ea6\u52ab\uff0c\u7389\u5973\u6295\u68ad\uff0c\u5927\u6f20\u5b64\u70df\uff0c\u957f\u6cb3\u843d\u65e5\uff0c\u5feb\u70b9\u4ed6\u9633\u767d\u7a74\uff01\u201d\u674e \u6c81\u6885\u4e00\u8fde\u56db\u62db\u6740\u624b\uff0c\u6740\u5f97\u9ec4\u77f3\u9053\u4eba\u4fa7\u8eab\u95ea\u8fc7\u4e00\u8fb9\uff0c\u4f46\u4ed6\u7684\u62c2\u5c18\u5982\u5c01\u4f3c\u95ed\uff0c\u5b88\u9632\u4e4b\u4e2d\u8fd8\u5177\u6709\u6f5c \u4f0f\u7684\u53cd\u51fb\u4e4b\u529b\uff0c\u674e\u6c81\u6885\u6b63\u81ea\u60f3\u9053\uff1a\u201c\u5982\u4f55\u80fd\u70b9\u4e2d\u4ed6\u7684\u9633\u767d\u7a74\uff1f\u201d\u5ffd\u89c1\u9ec4\u77f3\u9053\u4eba\u62c2\u5c18\u4e00\u4e3e\uff0c\u5c18 \u5c3e\u5ffd\u7136\u98d8\u98d8\u56db\u6563\uff0c\u80f8\u524d\u95e8\u6237\u5927\u5f00\uff0c\u674e\u6c81\u6885\u5927\u559c\uff0c\u4e00\u6307\u6233\u53bb\uff0c\u9ec4\u77f3\u9053\u4eba\u679c\u7136\u5e94\u6307\u800c\u5012\uff0c\u52a8\u5f39\u4e0d \u5f97\u3002\u539f\u6765\u662f\u51af\u7433\u6363\u9b3c\uff0c\u8fd0\u6c14\u628a\u9ec4\u77f3\u9053\u4eba\u7684\u62c2\u5c18\u5439\u6563\uff0c\u6697\u4e2d\u52a9\u4e86\u5973\u513f\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u51af\u7433\u6025\u6025\u51fa\u95e8\u8ffd\u53bb\uff0c\u4f46\u89c1\u83bd\u83bd\u8349\u539f\uff0c\u8fdc\u5c71\u7ef5\u4e98\uff0c\u54ea\u77e5\u91d1\u4e16\u9057\u9003\u5411\u4f55\u65b9\u3002\u5c38\u7433\u5927\u6012\uff0c\u9053\uff1a \u201c\u90fd\u662f\u8fd9\u4e2a\u79c3\u9a74\u8bef\u4e86\u6211\u7684\u5927\u4e8b\uff01\u201d \u3000\u3000\u51af\u7433\u6b63\u5728\u6c14\u607c\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u5973\u513f\u53eb\u9053\uff1a\u201c\u79c3\u9a74\u9003\u5566\uff01\u201d\u539f\u6765\u8463\u592a\u6e05\u4ee5\u4e3a\u51af\u7433\u4e00\u65f6\u95f4\u4e0d\u80fd \u56de\u6765\uff0c\u8d81\u673a\u9003\u8d70\uff0c\u51af\u7433\u5927\u6012\uff0c\u63d0\u4e00\u53e3\u6c14\uff0c\u7acb\u523b\u8ffd\u53bb\uff0c\u5c06\u8ddd\u5341\u4f59\u4e08\u8fdc\uff0c\u547c\u7684\u4e00\u58f0\u5c06\u94c1\u81c2\u63b7\u53bb\uff0c \u540c\u65f6\u5f69\u5e26\u629b\u51fa\u4e00\u5377\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u80c6\u6562\u4e0d\u542c\u6211\u8bdd\uff0c\u628a\u5de6\u81c2\u4e5f\u7559\u4e0b\u6765\uff01\u201d \u3000\u3000\u90a3\u94c1\u81c2\u63b7\u5728\u7a7a\u4e2d\uff0c\u98ce\u8f66\u822c\u5730\u65cb\u8f6c\u98de\u53bb\uff0c\u672c\u662f\u5411\u54ea\u65b9\u8eb2\u907f\u4e5f\u907f\u4e0d\u5f00\uff0c\u5ffd\u89c1\u8463\u592a\u6e05\u98de\u8eab\u4e00 \u8dc3\uff0c\u5728\u7a7a\u4e2d\u63a5\u8fde\u4e24\u4e2a\u56de\u65cb\u8f6c\u6298\uff0c\u94c1\u81c2\u4ece\u4ed6\u5934\u9876\u65cb\u8fc7\uff0c\u7adf\u7136\u6253\u4ed6\u4e0d\u7740\uff0c\u51af\u7433\u4e00\u5446\uff0c\u53eb\u9053\uff1a \u201c\u5582\uff0c\u4f60\u600e\u4e48\u4e5f\u8bc6\u5f97\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u662f\u6211\u5148\u5e08\uff01\u201d\u51af\u7433\u2018\u5475 \u5440\u201d\u4e00\u58f0\uff0c\u5ffd\u7136\u7eb5\u8d77\uff0c\u7528\u7684\u4e5f\u662f\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u5f69\u5e26\u4e00\u4f38\uff0c\u5c06\u8463\u5927\u6e05\u5de6\u81c2\u7f20\u7740\uff0c\u5374\u4e0d\u7528\u529b\uff0c \u53cd\u800c\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u7ec3\u5f97\u8fd8\u4e0d\u9ad8\u660e\uff0c\u5feb\u968f\u6211\u56de\u9152\u5e97\u53bb\u3002\u201d\u5f69\u5e26\u4e00\u677e\u53c8\u5c06\u8463\u592a\u6e05\u653e\u4e86\u3002\u3000\u3000\uff20 \u3000\u3000\u8463\u592a\u6e05\u60ca\u60e7\u4ea4\u5e76\uff0c\u62fe\u8d77\u94c1\u81c2\uff0c\u51dd\u7738\u4e00\u671b\uff0c\u4f46\u89c1\u51af\u7433\u548c\u989c\u60a6\u8272\uff0c\u9762\u4e0a\u6b8a\u65e0\u6076\u610f\uff0c\u5fc3\u4e2d\u7a0d\u7a0d \u653e\u5bbd\uff0c\u60f3\u9053\uff1a\u201c\u600e\u4e48\u5979\u4e5f\u61c2\u5f97\u8fd9\u624b\u529f\u592b\uff1f\u96be\u9053\u548c\u5148\u5e08\u6709\u4ec0\u4e48\u6e0a\u6e90\u3002\u4f46\u5176\u4ed6\u6b66\u529f\uff0c\u600e\u53c8\u4e00\u70b9\u4e0d \u50cf\uff1f\u201d\u53ef\u4e5f\u4e0d\u6562\u591a\u95ee\uff0c\u4fef\u9996\u8d34\u8033\u5730\u548c\u51af\u7433\u56de\u5230\u9152\u5e97\uff0c\u51af\u7433\u6307\u7740\u9ec4\u77f3\u4eba\u9053\uff1a\u201c\u4ed6\u662f\u548c\u4f60\u540c\u6765\u7684 \u5417\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u51af\u7433\u4f38\u6307\u4e00\u70b9\uff0c\u89e3\u5f00\u4e86\u9ec4\u77f3\u9053\u4eba\u7684\u7a74\u9053\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e5f\u4e00\u540c \u6765\u559d\u9152\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6e38\u620f\u98ce\u5c18\u4e00\u4fa0\u5973\uff0c\u5f53\u573a\u6c14\u715e\u5927\u5b97\u5e08\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u4e00\u7247\u5929\u771f\u3000\u4e66\u7ae5\u620f\u7389\u5973\u3000\u5341\u5206\u60f6\u60d1\u3000\u602a\u5ba2\u52ab\u56da\u7262 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u4e8c\u56de \u4e00\u7247\u5929\u771f\u3000\u4e66\u7ae5\u620f\u7389\u5973 \u5341\u5206\u60f6\u60d1\u3000\u602a\u5ba2\u52ab\u56da\u7262 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u81ea\u5c45\u4e00\u6d3e\u5b97\u5e08\uff0c\u54ea\u66fe\u53d7\u8fc7\u5982\u6b64\u4fae\u8fb1\uff0c\u5f85\u8981\u6e9c\u8d70\uff0c\u51af\u7433\u9762\u5b54\u4e00\u677f\uff0c\u6307\u9053\uff1a\u201c\u5582\uff0c \u6211\u53eb\u4f60\u5750\u4e0b\u559d\u9152\uff0c\u4f60\u600e\u4e48\u4e0d\u542c\u8bdd\uff1f\u201d\u674e\u6c81\u6885\u5657\u55e4\u7b11\u9053\uff1a\u201c\u5988\uff0c\u4f60\u53eb\u4ed6\u5750\u5728\u5730\u4e0a\u5417\uff1f\u201d\u9002\u624d\u4e00 \u573a\u5927\u6253\uff0c\u5e97\u5b50\u5f53\u4e2d\u7684\u597d\u51e0\u5f20\u684c\u5b50\u51f3\u5b50\u5168\u90fd\u7ed9\u6253\u5f97\u7834\u7834\u70c2\u70c2\uff0c\u6728\u5934\u788e\u5757\uff0c\u5806\u6ee1\u4e00\u5730\uff0c\u51af\u7433\u9053\uff1a \u201c\u5bf9\uff0c\u662f\u6211\u7cca\u6d82\u4e86\uff0c\u4f60\u4eec\u4e8c\u4eba\u8d76\u5feb\u628a\u5730\u65b9\u6536\u62fe\u5e72\u51c0\uff0c\u5c06\u4fa7\u8fb9\u7684\u51f3\u5b50\u684c\u5b50\u642c\u51e0\u5f20\u6765\uff0c\u6c81\u513f\uff0c\u4f60 \u7ed9\u6211\u76d1\u5de5\uff0c\u4e0d\u8bb8\u4ed6\u4eec\u5077\u61d2\uff01\u201d\u6307\u7740\u9ec4\u77f3\u9053\u4eba\u4e0e\u8463\u592a\u6e05\uff0c\u547d\u4ee4\u4ed6\u4eec\u7acb\u523b\u6536\u62fe\uff0c\u9ec4\u77f3\u9053\u4eba\u6c14\u5f97\u4e03 \u7a8d\u4e03\u70df\uff0c\u53ef\u662f\u53c8\u6253\u5979\u4e0d\u8fc7\uff0c\u82e5\u7136\u4e0d\u4f9d\uff0c\u53ea\u6015\u5979\u60f3\u51fa\u66f4\u7279\u522b\u7684\u82b1\u6837\uff0c\u66f4\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u6536\u62fe\u59a5\u5f53\uff0c\u8463\u592a\u6e05\u7279\u522b\u5356\u529b\uff0c\u5c06\u5730\u4e0a\u626b\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u51af\u7433\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd8\u6709 \u9152\u5462\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u8981\u9152\u53ef\u5f97\u5524\u5e97\u4e2d\u7684\u9152\u4fdd\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u9152\u4fdd\u5462\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u8eb2\u5728 \u67dc\u56f4\u5e95\u4e0b\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u53bb\u626f\u4ed6\u7684\u8033\u6735\u3002\u201d\u90a3\u9152\u4fdd\u542c\u5f97\u5916\u9762\u4e89\u6597\u5df2\u6b62\uff0c\u6b63\u94bb\u51fa\u5934\u6765\u5f20 \u671b\uff0c\u5ffd\u542c\u51af\u7433\u8bf4\u626f\u4ed6\u7684\u8033\u6735\uff0c\u614c\u5fd9\u722c\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6709\u9152\uff0c\u6709\u9152\uff01\u8fd9\u4f4d\u9053\u7237\u7ed9\u7684\u91d1\u5b50\uff0c\u5c3d\u591f \u4e70\u5341\u516d\u575b\u9152\u3002\u201d \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u5012\u9614\u6c14\u3002\u201d\u5927\u9a6c\u91d1\u5200\u5730\u5750\u4e0b\uff0c\u53eb\u9ec4\u77f3\u9053\u4eba\u548c\u8463\u592a\u6e05\u5750\u5728\u4e0b\u9996\uff0c\u6768\u67f3\u9752\u6bcd \u5973\u5750\u5728\u53e6\u5916\u4e00\u5f20\u62ac\u4e8e\uff0c\u4e66\u7ae5\u6c5f\u5357\u4e5f\u88ab\u51af\u7433\u6307\u7740\u5750\u5728\u90b9\u7edb\u971e\u7684\u4fa7\u8fb9\u3002\u90b9\u7edb\u971e\u5927\u76b1\u7709\u5934\uff0c\u4f46\u90a3\u662f \u51af\u7433\u5429\u5490\u7684\uff0c\u5979\u53ef\u4e0d\u6562\u62d2\u7edd\u3002 \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u6211\u9010\u4e2a\u6765\u95ee\uff0c\u6211\u95ee\u4e00\u53e5\uff0c\u4f60\u4eec\u7b54\u4e00\u53e5\u3002\u201d\u6307\u7740\u8463\u592a\u6e05\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u548c\u91d1\u4e16 \u9057\u6253\u67b6\uff1f\u201d\u8463\u5927\u6e05\u6014\u4e86\u4e00\u6014\uff0c\u9762\u6709\u5f02\u8272\uff0c\u9053\uff1a\u201c\u8c01\u662f\u91d1\u4e16\u9057\uff1f\u201d\u51af\u6797\u9053\uff0c\u201c\u4f60\u88c5\u4ec0\u4e48\u50bb\uff1f\u4e0d \u5c31\u662f\u548c\u4f60\u6253\u67b6\u7684\u90a3\u4e2a\u4eba\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u4ed6\u662f\u8c01\u7684\u5f1f\u5b50\uff1f\u201d\u51af\u7433\u6012\u9053\uff1a\u201c\u662f\u6211\u95ee\u4f60\uff0c\u8fd8\u662f\u4f60 \u95ee\u6211\uff1f\u518d\u591a\u95ee\uff0c\u628a\u4f60\u7684\u5de6\u81c2\u4e5f\u5207\u4e0b\u6765\uff01\u5feb\u8bf4\uff0c\u4f60\u4e3a\u4ec0\u4e48\u548c\u4ed6\u6253\u67b6\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u662f\u4ed6\u548c\u6211 \u6253\u67b6\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4ed6\u5e72\u561b\u548c\u4f60\u6253\u67b6\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u6211\u548c\u6768\u5973\u4fa0\u8bd5\u62db\uff0c\u672c\u6765\u4e0d\u5173\u4ed6\u7684\u4e8b\uff0c \u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4f55\u8981\u548c\u6211\u6253\u67b6\uff01\u201d\u51af\u7433\u4fa7\u7740\u8138\u95ee\u6768\u67f3\u9752\u9053\uff1a\u201c\u539f\u6765\u4f60\u548c\u91d1\u4e16\u9057\u662f\u597d\u670b\u53cb\uff0c\u8fd9 \u6211\u53ef\u4e0d\u77e5\u9053\u3002\u201d\u6697\u6697\u62c5\u5fc3\uff0c\u6015\u6768\u67f3\u9752\u4e5f\u770b\u4e0a\u91d1\u4e16\u9057\uff0c\u8981\u62db\u4ed6\u4f5c\u5973\u5a7f\u3002\u6768\u67f3\u9752\u6120\u9053\uff1a\u201c\u8c01\u548c\u4ed6 \u662f\u670b\u53cb\uff1f\u4ed6\u66fe\u6b3a\u8d1f\u6211\u6bcd\u5973\u4e8c\u4eba\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u8463\u592a\u6e05\u4e3a\u4ec0\u4e48\u548c\u4f60\u6253\u67b6\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u4e09\u5341 \u591a\u5e74\u524d\uff0c\u6211\u7236\u4eb2\u66fe\u6253\u4e86\u4ed6\u4e00\u638c\u3002\u90a3\u65f6\u6b63\u5728\u4f60\u5468\u5c81\u4e4b\u65f6\uff0c\u6653\u6f9c\u5e26\u4f60\u9003\u8d70\uff0c\u6211\u7236\u5973\u5c31\u662f\u4f4f\u90a3\u95f4\u5ba2 \u5e97\u9047\u5230\u6653\u6f9c\u7684\u3002\u5f53\u65e5\u4e4b\u4e8b\uff0c\u6653\u6f9c\u4e5f\u66fe\u76ee\u51fb\uff0c\u4f60\u56de\u53bb\u95ee\u4ed6\u5c31\u77e5\u9053\u4e86\u3002\u8bf4\u6765\u4ed6\u4e5f\u662f\u4f60\u7684\u4ec7\u4eba\u5440\uff0c \u6211\u7236\u4eb2\u6253\u4ed6\u4e00\u638c\u6709\u4f55\u4e0d\u8be5\uff1f\u201d\u51af\u7433\u5446\u4e86\u4e00\u5446\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u8463\u592a\u6e05\u539f\u6765\u4e5f\u662f\u81ea\u5df1\u7684\u4ec7\u4eba\u4e4b\u4e00\u3002 \u51af\u7433\u59d0\u59b9\u6070\u597d\u5728\u5468\u5c81\u4e4b\u65f6\uff0c\u5bb6\u5ead\u4fbf\u88ab\u5f53\u65f6\u7684\u56db\u7687\u5b50\u5141\u5e27\u6240\u6bc1\uff0c\u7236\u4eb2\u5f53\u573a\u8eab\u6b7b\uff0c\u51af\u745b\u88ab\u65e0\u6781\u6d3e \u5927\u5e08\u949f\u4e07\u5802\u6551\u8d70\uff0c\u51af\u73bb\u5219\u88ab\u5510\u6653\u6f9c\u5e26\u8d70\uff0c\u5176\u540e\u4e0d\u4e45\uff0c\u51af\u7433\u53c8\u88ab\u516b\u81c2\u795e\u9b54\u62a2\u5230\u6d77\u5c9b\u4e0a\uff0c\u5c06\u5979\u5f53 \u4f5c\u5973\u513f\u629a\u517b\uff0c\u540e\u6765\u53c8\u5e26\u5230\u56db\u7687\u5b50\u5e9c\u4e2d\uff0c\u4e24\u59d0\u59b9\u5206\u79bb\u4e86\u4e8c\u5341\u5e74\u624d\u89c1\u9762\u3002 \u3000\u3000\u51af\u7433\u7236\u4eb2\u867d\u7136\u4e0d\u662f\u516b\u81c2\u795e\u9b54\u5e08\u5f92\u6240\u6740\uff0c\u4f46\u4ed6\u4eec\u5f53\u5e74\u90fd\u662f\u56db\u7687\u5b50\u5141\u4fa6\u7684\u95e8\u5ba2\uff0c\u5317\u4e94\u7701\u82f1\u96c4 \u6b7b\u5728\u516b\u81c2\u795e\u9b54\u5144\u5f1f\u4e4b\u624b\u7684\u6570\u4e0d\u80dc\u6570\uff0c\u8bf4\u6765\u8fd9\u51a4\u4ec7\u4e5f\u4e0d\u7b97\u4e0d\u6df1\u3002 \u3000\u3000\u4e09\u5341\u5e74\u6765\u7684\u524d\u5c18\u5f80\u4e8b\u7535\u5149\u77f3\u706b\u822c\u5730\u4ece\u51af\u7433\u8111\u4e2d\u95ea\u8fc7\uff0c\u5979\u60f3\u8d77\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u600e\u6837\u6559\u5979\u6b66 \u827a\uff0c\u5728\u56db\u7687\u5b50\u5e9c\u4e2d\u600e\u6837\u53d7\u5230\u5ba0\u7231\uff0c\u53d7\u4e86\u5404\u79cd\u5404\u6837\u90aa\u6d3e\u7684\u6b66\u529f\uff0c\u540e\u6765\u624d\u5f97\u5230\u65e0\u6781\u6d3e\u7684\u771f\u4f20\u3002\u56db \u7687\u5b50\u600e\u6837\u8feb\u5979\u4e3a\u5983\uff0c\u8feb\u5f97\u5979\u9003\u51fa\u7687\u5bab\uff0c\u800c\u5230\u6700\u540e\u516b\u81c2\u795e\u9b54\u4e24\u5144\u5f1f\u88ab\u5979\u7684\u59d0\u59d0\u6240\u8bdb\uff0c\u800c\u516b\u81c2\u795e \u9b54\u4e34\u6b7b\u4e4b\u65f6\uff0c\u8fd8\u5c06\u4e00\u4ef6\u5f02\u5b9d\u7559\u7ed9\u51af\u7433\uff0c\u90a3\u5c31\u662f\u4e13\u89e3\u86c7\u6bd2\u7684\u7528\u732b\u9e70\u53e3\u6d8e\u6240\u5236\u70bc\u7684\u836f\u7403\u3002\u8fd9\u4e00\u4e9b \u6069\u6069\u6028\u6028\uff0c\u7ea0\u7ed3\u4e0d\u6e05\uff0c\u51af\u7433\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u674e\u6c81\u6885\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5988\uff0c\u539f\u6765\u4f60\u4e5f\u6709\u4e3a\u96be\u4e4b\u4e8b\uff0c\u4e0d\u5982\u8bf7\u59e8\u7236\u59e8\u6bcd\u6765\u542c\u5ba1\u5427\uff0c\u6211\u77a7\u4f60\u662f\u7a7f \u4e0a\u9f99\u888d\u4e5f\u4e0d\u50cf\u4e2a\u592a\u5b50\uff0c\u5750\u4e0a\u516c\u5802\u4e5f\u4e0d\u50cf\u4e2a\u5224\u5b98\uff0c\u88c5\u6a21\u4f5c\u4f6f\u5730\u5ba1\u4e2a\u4ec0\u4e48\uff1f\u5c31\u53ef\u60dc\u59e8\u7236\u59e8\u6bcd\u8d76\u4e0d \u6765\u5475\uff01\u201d\u5979\u4eec\u6bcd\u5973\u8bf4\u7b11\u5df2\u60ef\uff0c\u51af\u7433\u5e38\u53d6\u7b11\u5973\u513f\u79bb\u4e0d\u5f00\u6bcd\u4eb2\uff0c\u800c\u674e\u6c81\u6885\u4e5f\u5e38\u53d6\u7b11\u5979\u6bcd\u4eb2\u8981\u9760\u51af \u73ab\u548c\u5510\u6653\u6f9c\u51fa\u4e3b\u610f\uff0c\u88ab\u5973\u513f\u53d6\u7b11\uff0c\u51af\u7433\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u6775\uff0c\u6768\u67f3\u9752\u53ef\u6709\u70b9\u8be7\u5f02\uff0c\u8d8a\u77a7\u5979\u7684\u795e\u6c14\u4e3e \u6b62\u8d8a\u4e0d\u50cf\u201c\u51af\u745b\u201d\u3002\u53c8\u56e0\u674e\u6c81\u6885\u8bf4\u5979\u6bcd\u4eb2\u201c\u542c\u5ba1\u201d\uff0c\u597d\u50cf\u628a\u67f3\u67f3\u9752\u4e5f\u5f53\u4f5c\u201c\u88ab\u5ba1\u201d\u4e4b\u4eba\uff0c\u6768 \u67f3\u9752\u5f53\u7136\u5927\u4e0d\u9ad8\u5174\u3002\u51af\u7433\u7b11\u9053\uff1a\u201c\u9752\u59d0\uff0c\u4f60\u770b\u6211\u7684\u5973\u513f\u88ab\u5a07\u7eb5\u5f97\u4e0d\u50cf\u8bdd\u4e86\u3002\u201d\u9762\u5b54\u4e00\u6273\uff0c\u5ffd \u5730\u5e84\u91cd\u4ed6\u8bf4\u9053\uff1a\u201c\u963f\u6885\uff0c\u4f60\u8bf4\u6211\u4e0d\u4f1a\u65ad\u6848\uff0c\u6211\u5c31\u65ad\u7ed9\u4f60\u542c\u3002\u8463\u592a\u6e05\u5f53\u5e74\u53d7\u6768\u8001\u524d\u8f88\u90a3\u4e00\u638c\u4e43 \u662f\u6d3b\u8be5\uff0c\u4ece\u4eca\u540e\u4e0d\u8bb8\u591a\u4e8b\u3002\u4e0a\u4e00\u4ee3\u7684\u4eba\u90fd\u6b7b\u5566\uff0c\u4e09\u5341\u5e74\u8fc7\u773c\u4e91\u70df\uff0c\u65e9\u5df2\u53c8\u662f\u756a\u4e16\u754c\u3002\u9752\u59d0\uff0c \u65e7\u65e5\u7684\u51a4\u4ec7\u54b1\u4eec\u4e5f\u4e0d\u5fc5\u7406\u5566\u3002\u201d\u6768\u67f3\u9752\u672c\u4e0d\u60f3\u518d\u548c\u8463\u592a\u6e05\u7ed3\u6028\uff0c\u95fb\u8a00\u81ea\u662f\u9996\u80af\u3002\u8463\u592a\u6e05\u66f4\u662f \u559c\u51fa\u671b\u5916\uff0c\u5408\u4ec0\u9053\u8c22\uff0c\u8bf4\u9053\uff1a\u201c\u5973\u5c45\u58eb\u6148\u60b2\uff0c\u8d2b\u618e\u611f\u6fc0\u4e0d\u5c3d\uff0c\u5c31\u6b64\u544a\u8f9e\u3002\u201d \u3000\u3000\u51af\u7433\u5ffd\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u8463\u5927\u6e05\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u7b97\u4e86\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u5343\u8f9b\u4e07\u82e6 \u7684\u627e\u4eba\uff0c\u5374\u7ed9\u4f60\u8bef\u4e86\u6211\u7684\u4e8b\u60c5\uff0c\u8ba9\u4ed6\u8d70\u4e86\u3002\u91cd\u7f5a\u53ef\u514d\uff0c\u8584\u60e9\u8fd8\u662f\u8981\u7684\u3002\u6211\u7f5a\u4f60\u5728\u6b64\u9762\u58c1\u4e09 \u5929\uff01\u963f\u6885\uff0c\u6211\u6559\u4f60\u4e00\u624b\u70b9\u7a74\u6cd5\uff0c\u5bfb\u5e38\u7684\u70b9\u7a74\uff0c\u6700\u591a\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u6211\u8fd9\u4e2a\u70b9\u7a74\uff0c\u975e\u4e09\u65e5\u4e4b\u540e\u4e0d \u5f97\u81ea\u89e3\uff0c\u4f60\u77a7\u6e05\u695a\u4e86\u3002\u201d\u9a84\u8d77\u4e2d\u98df\u4e8c\u6307\uff0c\u4fbf\u8981\u70b9\u8463\u592a\u6e05\u7684\u9ebb\u54d1\u7a74\uff0c\u8463\u5927\u6e05\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5c0f\u50e7 \u6709\u4e8b\uff0c\u5c0f\u50e7\u4e5f\u6025\u7740\u8981\u627e\u4eba\u5475\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u627e\u4ec0\u4e48\u4eba\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u4e43 \u662f\u5148\u5e08\u81f3\u53cb\uff0c\u6b66\u6797\u524d\u8f88\u4eba\u4eba\u7686\u77e5\u3002\u201d\u51af\u7433\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u51fa\u5bb6\u4e4b\u4eba\u4e0d\u6253\u907f\u8bed\uff0c\u4f60\u80c6\u6562\u9a97\u6211\uff1f\u91d1 \u4e16\u9057\u4fbf\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u5f92\u5f1f\uff0c\u4f60\u8981\u627e\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u548c\u4ed6\u6253\u67b6\uff1f\u201d \u3000\u3000\u8463\u592a\u6e05\u5176\u5b9e\u5df2\u6599\u5230\u4e03\u516b\uff0c\u542c\u51af\u7433\u4e00\u8bf4\uff0c\u5927\u53eb\u201c\u53ef\u60dc\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u672c\u6765\u4e0d\u8ba4\u5f97\u4ed6 \u7684\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u8981\u662f\u8ba4\u5f97\uff0c\u6211\u4e5f\u4e0d\u653e\u4ed6\u4e86\u3002\u6bd2\u9f99\u5c0a\u8005\u90a3\u6839\u94c1\u62d0\uff0c\u4e09\u5341\u591a\u5e74\u4e4b\u524d\uff0c\u6211\u89c1\u8fc7 \u4e00\u6b21\uff0c\u521a\u624d\u672c\u5df2\u6709\u70b9\u7591\u5fc3\uff0c\u53ef\u6068\u4ed6\u4e00\u5473\u86ee\u6253\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u5478\uff01\u8981\u4e0d\u662f\u4f60\u6b3a\u8d1f\u90b9\u4f2f\u6bcd\uff0c\u4ed6 \u600e\u4f1a\u6253\u4f60\uff1f\u201d\u5176\u5b9e\u91d1\u4e16\u9057\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u5230\u5904\u6311\u4e8b\uff0c\u786e\u662f\u4e00\u5473\u86ee\u6253\uff0c\u65e0\u53ef\u7406\u55bb\uff0c\u53ea\u662f\u8fd9\u4e00\u6b21\u5012 \u6709\u4e9b\u9053\u7406\u3002\u5408\u8463\u592a\u6e05\u5012\u9709\uff0c\u5fc3\u60f3\u51af\u7433\u6bcd\u5973\u5982\u6b64\u8892\u62a4\u91d1\u4e16\u9057\uff0c\u6599\u60f3\u4ed6\u4eec\u4e4b\u95f4\u5fc5\u6709\u6e0a\u6e90\u3002\u4e8e\u662f \u9053\uff1a\u201c\u90a3\u4e48\u8bf4\uff0c\u54b1\u4eec\u90fd\u4e0d\u662f\u5916\u4eba\uff0c\u4e0d\u5982\u8ba9\u6211\u5e2e\u4f60\u4e00\u9f50\u627e\u91d1\u4e16\u9057\u3002\u201d\u51af\u7433\u5ffd\u7136\u6447\u4e86\u6447\u5934\uff0c\u81ea\u8a00 \u81ea\u8bed\u9053\uff1a\u201c\u4e0d\u5bf9\u3002\u201d\u6307\u7740\u8463\u592a\u6e05\u9053\uff1a\u201c\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u6211\u8fd8\u662f\u8981\u628a\u4f60\u7684\u5de6\u81c2\u5207\u4e0b\u3002\u201d\u8463\u592a\u6e05\u5413\u4e86 \u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u88ab\u94c1\u638c\u795e\u5f39\u6253\u4e86\u53f3\u81c2\u4e4b\u540e\uff0c\u5c31\u9041\u5165\u7a7a\u95e8\uff0c\u4e0d\u7406 \u5c18\u4e16\uff0c\u90a3\u4e48\u5f53\u7136\u6ca1\u6709\u89c1\u8fc7\u6bd2\u9f99\u524d\u8f88\u7684\u4e86\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u90a3\u4f60\u600e\u4f1a\u77e5\u9053 \u6bd2\u9f99\u524d\u8f88\u6536\u6709\u5173\u95e8\u5f92\u5f1f\uff1f\u201d\u8463\u592a\u6e05\u7565\u4e00\u8fdf\u7591\uff0c\u9053\uff1a\u201c\u6211\u53bb\u5e74\u56de\u5230\u732b\u9e70\u5c9b\u3001\u987a\u4fbf\u5230\u86c7\u5c9b\u62dc\u8bbf\u6bd2 \u9f99\u5e08\u4f2f\uff0c\u5374\u7a81\u89c1\u4ed6\u7684\u575f\u5893\uff0c\u8fd9\u575f\u5893\u6599\u60f3\u662f\u4ed6\u7684\u5f92\u5f1f\u6240\u5efa\uff0c\u6211\u5ff5\u5148\u5e08\u548c\u6bd2\u9f99\u524d\u8f88\u7684\u4ea4\u60c5\uff0c\u56e0\u6b64 \u60f3\u5bfb\u89c5\u4ed6\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u8fd9\u53c8\u6709\u4e48\u4e0d\u5bf9\uff1f\u201d\u51af\u7433\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8fd9\u79cd\u91cd\u4e49\u6c14\u7684\u4eba\uff0c\u4f60 \u627e\u6bd2\u9f99\u5c0a\u8005\u7684\u5f92\u5f1f\uff0c\u5fc5\u7136\u53e6\u6709\u6240\u56e0\uff0c\u4f60\u8bf4\u4e0d\u8bf4\u5b9e\u8bdd\uff1f\u4fe1\u4e0d\u4fe1\u6211\u4e0d\u7528\u5200\u4e5f\u80fd\u628a\u4f60\u7684\u5de6\u81c2\u5207 \u6389\uff1f\u201d\u8463\u592a\u6e05\u9762\u8272\u4e00\u53d8\uff0c\u652f\u652f\u543e\u543e\uff0c\u65e0\u6cd5\u56de\u7b54\uff0c\u51af\u7433\u9053\uff1a\u201c\u6885\u513f\uff0c\u641c\u4ed6\u7684\u8eab\uff0c\u770b\u4ed6\u5728\u86c7\u5c9b\u5077 \u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51af\u7433\u673a\u7075\u4e4b\u6781\uff0c\u89c1\u4ed6\u9762\u8272\u6709\u5f02\uff0c\u624b\u6307\u4e0d\u81ea\u7981\u7684\u4e00\u6309\u50e7\u888d\uff0c\u4fbf\u60f3\u4e2d\u5b9a\u6709\u53e4\u602a\u3002\u8463\u592a\u6e05\u88ab\u5979 \u4e00\u5413\uff0c\u4e0d\u5f97\u5df2\u8bf4\u9053\uff1a\u201c\u6211\u5230\u4e86\u86c7\u5c9b\uff0c\u5728\u6bd2\u9f99\u524d\u8f88\u6545\u5c45\u4f4f\u4e86\u4e00\u665a\uff0c\u53d1\u73b0\u4e86\u6bd2\u9f99\u524d\u8f88\u624b\u5199\u7684\u4e00\u672c \u4e1c\u897f\uff0c\u6211\u60f3\u4ea4\u7ed9\u4ed6\u7684\u5f92\u5f1f\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u62ff\u6765\u7ed9\u6211\u770b\u770b\u3002\u201d\u5fc3\u9053\uff1a\u201c\u600e\u7684\u6bd2\u9f99\u5c0a\u8005\u8fd9\u6837\u7c97\u5fc3 \u5927\u610f\uff0c\u6b66\u529f\u79d8\u590d\u5728\u4e34\u6b7b\u4e4b\u524d\u5374\u4e0d\u4ea4\u7ed9\u5f92\u5f1f\uff1f\u201d\u53d6\u8fc7\u4e00\u770b\uff0c\u539f\u6765\u5374\u5e76\u4e0d\u662f\u4ec0\u4e48\u201c\u62f3\u7ecf\u201d\u201c\u5251 \u8c31\u201d\u4e4b\u7c7b\u7684\u624b\u7a3f\uff0c\u800c\u662f\u4e00\u672c\u5341\u5e74\u6765\u65ad\u65ad\u7eed\u7eed\u6240\u5199\u7684\u65e5\u8bb0\uff0c\u51af\u7433\u968f\u4fbf\u7ffb\u4e86\u4e00\u7ffb\uff0c\u524d\u9762\u5927\u90e8\u662f\u4ed6 \u8bb0\u5230\u4e86\u86c7\u5c9b\u4e4b\u540e\uff0c\u600e\u6837\u5bc2\u5bde\u65e0\u804a\uff0c\u600e\u6837\u6124\u6068\u4e16\u4eba\uff0c\u600e\u6837\u8bad\u7ec3\u6bd2\u86c7\uff0c\u600e\u6837\u81ea\u521b\u6b66\u529f\u7b49\u7b49\uff0c\u51af\u7433 \u4e0d\u80dc\u611f\u6168\uff0c\u518d\u8bf5\u4e0b\u53bb\uff0c\u4e0b\u534a\u90e8\u5374\u662f\u4ed6\u53d9\u8ff0\u89c1\u4e86\u5415\u56db\u5a18\u4e4b\u540e\uff0c\u5fc3\u60c5\u600e\u6837\u6539\u53d8\uff0c\u540e\u6765\u53c8\u600e\u6837\u6536\u4e86 \u91d1\u4e16\u9057\u7b49\u7b49\u4e8b\u60c5\u3002\u6700\u540e\u51e0\u9875\u5199\u4ed6\u5df2\u53c2\u609f\u81ea\u5df1\u6240\u4e60\u7684\u5185\u529f\uff0c\u8d70\u5165\u9b54\u9053\uff0c\u82e5\u7136\u4e0d\u5f97\u5929\u5c71\u6b63\u5b97\u7684\u5185 \u529f\u89e3\u6551\uff0c\u5fc5\u6709\u4e00\u65e5\u8d70\u706b\u5165\u9b54\uff0c\u8fd9\u4e8b\u60c5\u51af\u7433\u4ece\u91d1\u4e16\u9057\u7684\u906d\u9047\uff0c\u4ea6\u5df2\u63a8\u6d4b\u5230\u5176\u4e2d\u9053\u7406\uff0c\u770b\u5230\u6700\u540e \u4e00\u9875\uff0c\u5374\u7a81\u7136\u53d1\u73b0\u4e00\u6bb5\u60ca\u5fc3\u52a8\u9b44\u7684\u6587\u5b57\uff0c\u51af\u7433\u4e5f\u4e0d\u7981\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u90a3\u4e00\u9875\u60f3\u662f\u4ed6\u4e34\u6b7b\u4e4b\u524d\u51e0\u65e5\u6240\u5199\uff0c\u5b57\u8ff9\u6f66\u8349\uff0c\u4f46\u5c1a\u53ef\u8fa8\u8bc6\uff0c\u51af\u7433\u770b\u5b8c\u4e4b\u540e\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa \u8bdd\u3002\u539f\u6bd2\u9f99\u5c0a\u8005\u5728\u86c7\u5c9b\u4f4f\u4e86\u6570\u5341\u5e74\uff0c\u521d\u6765\u4e4b\u65f6\uff0c\u5c9b\u4e0a\u6c14\u5019\u5bd2\u51b7\u3002\u5176\u540e\u4e00\u5e74\u6bd4\u4e00\u5e74\u708e\u70ed\uff0c\u5230\u6bd2 \u9f99\u5c0a\u8005\u4e34\u6b7b\u524d\u51e0\u5e74\uff0c\u5c9b\u4e0a\u53c8\u6d8c\u51fa\u6e29\u6cc9\uff0c\u6bd2\u9f99\u5c0a\u8005\u51e0\u5341\u5e74\u6765\u7ec6\u5fc3\u8003\u5bdf\uff0c\u67e5\u52d8\u5168\u5c9b\uff0c\u7ec8\u4e8e\u53d1\u73b0\u4e86 \u5730\u5e95\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u539f\u6765\u86c7\u5c9b\u5e95\u4e0b\uff0c\u6709\u4e00\u5ea7\u6d77\u5e95\u706b\u5c71\uff0c\u5730\u58f3\u9010\u5e74\u9686\u8d77\uff0c\u706b\u5c71\u53e3\u5c31\u5728\u5c9b\u4e2d\u5fc3\u4e00\u4e2a\u6bd2\u86c7\u7a9f\u4e0b\uff0c\u7a9f \u6df1\u6570\u767e\u4e08\uff0c\u6bd2\u9f99\u5c0a\u8005\u66fe\u9524\u4e0b\u53bb\u5bdf\u52d8\uff0c\u672a\u5230\u4e00\u534a\uff0c\u70ed\u5df2\u96be\u8010\uff0c\u6781\u76ee\u671b\u4e0b\u5730\u5fc3\uff0c\u4f46\u89c1\u6d1e\u7a9f\u4e0b\u9762\u7684 \u5ca9\u5c42\uff0c\u5df2\u6cdb\u51fa\u6697\u8d64\u8272\u7684\u5149\u534e\uff0c\u53ea\u662f\u5ca9\u5c42\u592a\u539a\uff0c\u706b\u7130\u8fd8\u6ca1\u6709\u55b7\u51fa\u6765\u3002\u90a3\u4e2a\u6d1e\u7a9f\u6bd2\u86c7\u6570\u4ee5\u4e07\u8ba1\uff0c \u56e0\u4e3a\u8010\u4e0d\u4f4f\u708e\u70ed\uff0c\u6709\u4e9b\u6e38\u4e86\u51fa\u6765\uff0c\u6709\u4e9b\u4fbf\u76d8\u9644\u5728\u6d1e\u53e3\u4e0b\u9762\u6570\u5341\u4e08\u7684\u77f3\u58c1\u4e0a\uff0c\u7a9f\u5e95\u6bd2\u86c7\u7684\u53e3\u6d8e \u79ef\u6210\u4e00\u4e2a\u5c0f\u6f6d\uff0c\u5947\u6bd2\u65e0\u6bd4\uff0c\u82e5\u7136\u706b\u5c71\u4e00\u65e6\u7206\u53d1\uff0c\u53ea\u6050\u6574\u4e2a\u86c7\u5c9b\u90fd\u8981\u5316\u6210\u98de\u7070\uff0c\u9ec4\u6d77\u8fb9\u6cbf\u7684\u9646 \u5730\uff0c\u4e5f\u53ef\u80fd\u6ce2\u53ca\uff0c\u6d77\u4e2d\u7684\u751f\u7269\uff0c\u90a3\u5c31\u66f4\u662f\u906d\u9022\u6d69\u52ab\u4e86\u3002\u7167\u6bd2\u9f99\u5c0a\u8005\u7684\u63a8\u7b97\uff0c\u706b\u5c71\u7206\u53d1\u53ef\u80fd\u5728 \u5341\u4f59\u5e74\u4e4b\u540e\uff0c\u82e5\u53ca\u65e9\u8bbe\u6cd5\uff0c\u8fd8\u53ef\u4ee5\u6d88\u707e\u8fd9\u4e2a\u7978\u80ce\u3002\u6bd2\u9f99\u5c0a\u8005\u6240\u60f3\u7684\u529e\u6cd5\u662f\uff0c\u8981\u6709\u4e00\u4e2a\u4eba\u4e0d\u754f \u6b64\u86c7\u6bd2\u7684\uff0c\u5728\u706b\u5c71\u7206\u53d1\u4e4b\u524d\u6570\u6708\uff0c\u6df1\u4e0b\u6d1e\u7a9f\uff0c\u51ff\u5f00\u4e00\u6761\u901a\u8def\uff0c\u5f15\u6765\u6d77\u6c34\uff0c\u7136\u540e\u5728\u5373\u5c06\u7206\u88c2\u800c \u5c1a\u672a\u7206\u88c2\u7684\u706b\u5c71\u53e3\u51ff\u4e00\u4e2a\u5c0f\u5b54\uff0c\u8ba9\u706b\u52bf\u6e32\u6cc4\u51fa\u6765\uff0c\u8fd9\u6837\u5728\u6d77\u6c34\u5305\u56f4\u4e4b\u4e2d\uff0c\u6bd2\u706b\u55b7\u51fa\uff0c\u4e5f\u65e0\u5927 \u5bb3\u3002\u65f6\u95f4\u7b97\u51c6\u8981\u5728\u706b\u5c71\u7206\u53d1\u4e4b\u524d\u6570\u6708\uff0c\u90a3\u662f\u56e0\u4e3a\u5230\u4e86\u90a3\u4e2a\u65f6\u5019\uff0c\u5ca9\u5c42\u88ab\u5730\u706b\u70e7\u5f97\u677e\u5316\uff0c\u5bb9\u6613 \u51ff\u5f00\u901a\u8def\uff0c\u5f15\u6765\u6d77\u6c34\u4e4b\u6545\u3002\u6b64\u5c9b\u53ef\u4ee5\u91c7\u96c6\u77f3\u7ef5\uff0c\u56e0\u77f3\u7ef5\u53ef\u4ee5\u505a\u9632\u706b\u7684\u8863\u670d\uff0c\u540c\u65f6\u4e3a\u4e86\u4fbf\u4e8e\u51ff \u7a7f\u77f3\u58c1\u8d77\u89c1\uff0c\u6700\u597d\u7528\u4e00\u67c4\u53ef\u4ee5\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\u3002\u51af\u7433\u770b\u5230\u6b64\u5904\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba \u9664\u4e86\u91d1\u4e16\u9057\u4e4b\u5916\uff0c\u201c\u6050\u6015\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\u6765\u3002\u4ed6\u719f\u6089\u86c7\u5c9b\u5730\u52bf\uff0c\u53c8\u4e0d\u754f\u6bd2\u86c7\uff0c\u6240\u6b20\u7f3a\u7684\u53ea\u662f\u4e00 \u628a\u5b9d\u5251\u800c\u5df2\u3002\u201d \u3000\u3000\u518d\u770b\u4e0b\u53bb\uff0c\u539f\u6765\u6bd2\u9f99\u5c0a\u8005\u4e5f\u60f3\u5230\u4e86\u8981\u91d1\u4e16\u9057\u5c06\u6765\u6d88\u8fd9\u573a\u707e\u96be\uff0c\u53ea\u662f\u4ed6\u592a\u8fc7\u75bc\u7231\u5f92\u5f1f\uff0c\u53c8 \u820d\u4e0d\u5f97\u53eb\u4ed6\u5192\u8fd9\u573a\u5947\u9669\uff0c\u6240\u4ee5\u5728\u65e5\u8bb0\u4e2d\u8868\u73b0\u7684\u5fc3\u60c5\uff0c\u5341\u5206\u77db\u76fe\u3002\u51af\u7433\u5fc3\u4e2d\u6697\u53f9\uff0c\u60f3\u9053\uff1a\u201c\u602a \u4e0d\u5f97\u91d1\u4e16\u9057\u4e1d\u6beb\u4e0d\u77e5\u6b64\u4e8b\u3002\u539f\u6765\u6bd2\u9f99\u5c0a\u8005\u4e34\u6b7b\u4e4b\u65f6\uff0c\u5728\u6c99\u6ee9\u4e0a\u7559\u4e0b\u8ba9\u4ed6\u2018\u6b66\u529f\u5927\u6210\u540e\uff0c\u901f\u627e \u5929\u5c71\u6d3e\u2019\uff0c\u4e0d\u4f46\u662f\u4e3a\u4e86\u60f3\u4f7f\u4ed6\u7684\u5185\u529b\u4fee\u4e60\uff0c\u5f97\u4ee5\u8e0f\u5165\u6b63\u9014\uff0c\u800c\u4e14\u4e5f\u662f\u85c9\u6b64\u8981\u4ed6\u79bb\u5f00\u86c7\u5c9b\u3002\u201d \u3000\u3000\u674e\u6c81\u6885\u89c1\u6bcd\u4eb2\u7ffb\u5230\u6700\u540e\u4e00\u9875\uff0c\u773c\u5149\u597d\u50cf\u5b9a\u4e86\u4f3c\u7684\uff0c\u4e45\u4e45\u4e0d\u79bb\u5f00\u3002\u5979\u5fc3\u4e2d\u597d\u5947\uff0c\u51d1\u8fc7\u5934\u6765 \u4e00\u770b\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u53ae\u4e0d\u6000\u597d\u610f\uff01\u201d\u624b\u6307\u4e00\u6325\uff0c\u6307\u5934\u51e0\u4e4e\u89e6\u5230\u8463\u592a\u6e05\u9f3b\u4e0a\uff0c\u8463\u592a\u6e05 \u5413\u4e86\u4e00\u8df3\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u6000\u597d\u610f\uff1f\u201d\u9ec4\u77f3\u9053\u4eba\u5fc3\u4e2d\u6e29\u6012\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u8463\u592a\u6e05\u7684\u8f88\u4efd \u4e4b\u9ad8\uff0c\u7109\u80fd\u53d7\u4f60\u8fd9\u4e2b\u5934\u4e4b\u6c14\u3002\u201d\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u60f3\u51fa\u5176\u4e0d\u610f\u7684\u5c06\u674e\u6c81\u6885\u64d2\u83b7\uff0c\u4f5c\u4e3a\u8981\u631f\u3002\u51af\u7433 \u5c06\u5973\u513f\u4e00\u62c9\uff0c\u6446\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\u5173\u4f60\u4eec\u7684\u4e8b\u3002\u6885\u513f\uff0c\u4f60\u770b\u5230\u4ec0\u4e48\u4e86\uff1f\u600e\u4e48\u80e1\u4e71\u9a82\u4eba\uff1f\u201d \u3000\u3000\u51af\u7433\u6b63\u81ea\u5947\u602a\uff0c\u6bd2\u9f99\u5c0a\u8005\u8fd9\u4e00\u9875\u65e5\u8bb0\uff0c\u5b57\u8ff9\u6f66\u8349\uff0c\u5199\u5f97\u5bc6\u5bc6\u9ebb\u9ebb\uff0c\u5979\u81ea\u5df1\u770b\u4e86\u8bb8\u4e45\u624d\u770b \u5f97\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u5973\u513f\u6ca1\u6709\u4e00\u76ee\u5341\u884c\u7684\u672c\u9886\uff0c\u600e\u4e48\u4e00\u770b\u5c31\u77e5\u9053\u4e86\uff1f\u5ffd\u89c1\u674e\u6c81\u6885\u62a2\u7740\u6307\u9053\uff1a \u201c\u4f60\u770b\u8fd9\u513f\uff01\u201d\u51af\u7433\u4e00\u770b\uff0c\u539f\u6765\u7eb8\u5f20\u7684\u4e0a\u7aef\u6709\u4e00\u884c\u8f83\u7aef\u6b63\u7684\u5b57\u4f53\u662f\uff1a\u6211\u51b3\u5c06\u79d8\u590d\u4ed8\u4e0e\u9057\u513f\uff0c \u4ed6\u5e94\u7ee7\u627f\u4f59\u4e4b\u8863\u94b5\uff0c\u7ec8\u751f\u4ee5\u6551\u6cbb\u9ebb\u75af\u60a3\u8005\u4e3a\u4e1a\u3002\u201d\u674e\u6c81\u6885\u53eb\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u5c31\u4e0d\u613f\u4e16\u9057\u54e5\u770b \u5230\u8fd9\u6761\uff0c\u4e00\u751f\u4e0e\u9ebb\u75af\u60a3\u8005\u4e3a\u4f0d\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u4e50\u8da3\uff1f\u201d\u51af\u7433\u4e0d\u89c9\u5657\u55e4\u4e00\u7b11\uff0c\u201c\u6709\u6ca1\u6709\u4e50\u8da3\uff0c\u53c8 \u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u518d\u8bf4\uff0c\u8fd9\u662f\u4ed6\u5e08\u7236\u7684\u9057\u547d\uff0c\u4f60\u4e0d\u80fd\u602a\u5230\u548c\u5c1a\u9053\u58eb\u7684\u8eab\u4e0a\u5475\u3002\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5 \u7ed9\u5973\u513f\u770b\u5230\u706b\u5c71\u4e4b\u4e8b\uff0c\u5979\u66f4\u8981\u53d7\u60ca\u4e86\u3002\u201d \u3000\u3000\u8463\u592a\u6e05\u9053\uff1a\u201c\u5973\u4fa0\u660e\u89c1\u3002\u8fd9\u672c\u624b\u7a3f\u4e0a\u9762\u5199\u4e9b\u4ec0\u4e48\uff0c\u6211\u4e00\u4e2a\u5b57\u4e5f\u4e0d\u6562\u770b\u3002\u53ea\u60f3\u5e08\u7236\u7684\u4e1c \u897f\uff0c\u81ea\u5e94\u4ea4\u7ed9\u5f92\u5f1f\u3002\u6211\u5bfb\u8bbf\u6bd2\u9f99\u5c0a\u8005\u7684\u5f92\u5f1f\uff0c\u7528\u610f\u4e0d\u5916\u5982\u65af\u3002\u201d\u5176\u5b9e\u4ed6\u662f\u770b\u4e86\uff0c\u77e5\u9053\u6bd2\u9f99\u5c0a \u8005\u7684\u6b66\u5b66\u79d8\u7c4d\u5df2\u4ea4\u7ed9\u4e86\u91d1\u4e16\u9057\uff0c\u4ed6\u662f\u60f3\u7528\u8fd9\u672c\u65e5\u8bb0\u53bb\u9a97\u53d6\u91d1\u4e16\u9057\u7684\u6bd2\u9f99\u79d8\u7c4d\u3002 \u3000\u3000\u51af\u7433\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u8d39\u5fc3\u5566\uff0c\u8fd9\u672c\u4e1c\u897f\u8ba9\u6211\u4ea4\u7ed9\u4ed6\u3002\u597d\uff0c\u514d\u4f60\u7684\u7f5a\uff0c\u4f60 \u53ef\u4ee5\u8d70\u5566\uff01\u201d\u8463\u592a\u6e05\u751a\u662f\u4e0d\u7518\uff0c\u53ef\u53c8\u4e0d\u6562\u95ee\u51af\u7433\u8ba8\u56de\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u6211\u5e2e\u5fd9\u4f60\u627e\u4ed6\u597d\u4e0d \u597d\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u968f\u4f60\u7684\u4fbf\uff0c\u6211\u53ef\u4e0d\u9886\u4f60\u7684\u4eba\u60c5\u3002\u5582\uff0c\u4f60\u53c8\u4e3a\u4ec0\u4e48\u548c\u91d1\u4e16\u9057\u6253\u67b6\uff1f\u201d\u8fd9\u4e00\u53e5 \u5374\u662f\u5411\u7740\u9ec4\u77f3\u9053\u4eba\u95ee\u7684\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u6ee1\u809a\u95f7\u6c14\uff0c\u9ed1\u7740\u8138\u5b54\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u6c5f\u5357\u77a7\u4ed6\u53ef\u601c\uff0c\u62a2\u7740\u7b54\u9053\uff1a\u201c\u8fd9\u90fd\u602a\u6211\u4e0d \u597d\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd9\u5c0f\u53ae\u5012\u5f88\u6709\u4e49\u6c14\uff0c\u600e\u4e48\u602a\u4f60\u5462\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u505a\u8fd9\u9053\u957f\u7684 \u5f92\u5f1f\uff0c\u91d1\u5927\u4fa0\u548c\u5510\u5927\u4fa0\u90fd\u5e2e\u6211\uff0c\u6240\u4ee5\u8fd9\u4f4d\u9053\u957f\u8fc1\u6012\u4ed6\u4eec\u4e86\u3002\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ed\u9053\u571f\u6728\u53e3 \u6728\u9762\uff0c\u4e00\u770b\u5c31\u4ee4\u4eba\u8ba8\u538c\uff0c\u4f60\u4e0d\u60f3\u505a\u4ed6\u7684\u5f92\u5f1f\uff0c\u8fd9\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002\u201d\u51af\u7433\u54c8\u54c8\u4e00\u7b11\uff0c\u8f6c\u5411\u9ec4\u77f3 \u9053\u4eba\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5f3a\u6536\u5f92\u5f1f\uff0c\u5fc5\u6709\u707e\u6b83\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5979\u8fd9\u8bdd\u662f\u6709\u611f\u800c\u53d1\uff0c\u56e0\u4e3a\u5f53\u5e74\u53cc\u9b54\u4e5f \u66fe\u60f3\u8feb\u5979\u4e3a\u5f92\u3002 \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u6068\u6068\u8bf4\u9053\uff1a\u201c\u6211\u5b81\u613f\u628a\u8fd9\u70b9\u73a9\u827a\u57cb\u5230\u571f\u91cc\u53bb\uff0c\u4eca\u751f\u4e5f\u4e0d\u518d\u6536\u5f92\u5f1f\u3002\u201d\u51af\u7433\u9053\uff0c \u201c\u597d\uff0c\u4f60\u65e2\u613f\u6539\u524d\u975e\uff0c\u4e0d\u5f3a\u6536\u5f92\u5f1f\uff0c\u90a3\u4f60\u4e5f\u8d70\uff0c\u563b\uff0c\u4f60\u6bd4\u8fd9\u548c\u5c1a\u6709\u9aa8\u6c14\uff0c\u521a\u624d\u5f97\u7f6a\u4e86\u4f60 \u5475\uff01\u201d\u9ec4\u77f3\u9053\u4eba\u557c\u7b11\u7686\u975e\uff0c\u63d2\u597d\u62c2\u5c18\uff0c\u8ffd\u4e0a\u8463\u592a\u6e05\u8d70\u4e86\u3002 \u3000\u3000\u6768\u67f3\u9752\u7684\u9762\u5b54\u4e00\u6273\uff0c\u9053\uff1b\u201c\u6211\u4e5f\u53ef\u4ee5\u8d70\u4e86\u4e48\uff1f\u201d\u51af\u7433\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48 \u8bdd\uff1f\u54c8\uff0c\u4f60\u8fd8\u8bb0\u5f97\u65e7\u65f6\u7684\u4ec7\u6068\u4e48\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff01\u201d\u62c9\u7740\u5973\u513f\u4fbf\u8d70\uff0c\u6c5f\u5357\u7b11\u563b \u563b\u8ddf\u5728\u5979\u7684\u540e\u9762\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u4e0d\u662f\u8981\u627e\u5510\u5927\u4fa0\u4e48\uff1f\u201d\u6768\u67f3\u9752\u56de\u5934\u77aa\u4e86\u6c5f\u5357\u4e00\u773c\uff0c\u6b63\u6b32 \u53d1\u4f5c\uff0c\u90b9\u7edb\u971e\u9053\uff1a\u201c\u5bf9\u5475\uff0c\u5988\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u95ee\u5510\u4f2f\u6bcd\uff1f\u201d \u3000\u3000\u51af\u7433\u8ffd\u4e86\u51fa\u6765\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u4f60\u5510\u4f2f\u6bcd\u5728\u5929\u5c71\uff0c\u5c06\u6765\u4f60\u603b\u80fd\u770b\u5230\u3002\u201d\u90b9\u7edb\u971e\u4e00\u6115\uff0c\u8f6c\u8fc7 \u5934\u53bb\u57cb\u6028\u6bcd\u4eb2\u9053\uff1a\u201c\u5988\uff0c\u4f60\u600e\u4e48\u8981\u6211\u547c\u4ed6\u505a\u5510\u4f2f\u6bcd\uff1f\u201d\u751a\u89c9\u4e0d\u597d\u610f\u601d\u3002\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f11\u602a\u4f60 \u7684\u6bcd\u4eb2\uff0c\u6211\u7684\u719f\u4eba\u5341\u4e2a\u6709\u4e5d\u4e2a\u90fd\u4f1a\u8ba4\u9519\u7684\u3002\u201d\u6768\u67f3\u9752\u65e9\u5df2\u77a7\u51fa\u5979\u4e0d\u662f\u51af\u745b\u3001\u60f3\u8d77\u6614\u65e5\u88ab\u5979\u98de \u5200\u524a\u53d1\u4e4b\u6068\uff0c\u4e00\u809a\u76ae\u95f7\u6c14\uff0c\u4f46\u5982\u4eca\u5927\u5bb6\u90fd\u662f\u534a\u8001\u5f90\u5a18\uff0c\u5f53\u7136\u4e0d\u597d\u518d\u53d1\u4f5c\u4e86\u3002\u51af\u7433\u7b11\u9053\uff1a\u201c\u6211 \u4e5f\u6709\u4e8b\u60c5\u8981\u59d0\u59d0\u5e2e\u5fd9\uff0c\u5f85\u6211\u5bfb\u5230\u91d1\u4e16\u9057\u4e4b\u540e\uff0c\u966a\u4f60\u4e00\u9053\u4e0a\u5929\u5c71\u5427\u3002\u201d\u6768\u67f3\u9752\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211 \u81ea\u5df1\u4f1a\u8d70\uff0c\u4e0d\u7528\u8d39\u5fc3\u5566\u3002\u201d\u5979\u672c\u6765\u6253\u542c\u5230\u5510\u6653\u6f9c\u592b\u5987\u5df2\u5230\u897f\u85cf\uff0c\u521a\u624d\u5979\u9519\u5c06\u51af\u7433\u5f53\u4f5c\u51af\u745b\uff0c \u8fd8\u5728\u5947\u602a\u5510\u6653\u6f9c\u4e3a\u4ec0\u4e48\u4e0d\u4e0e\u5979\u4e00\u9053\u3002\u5979\u672c\u8be5\u5c06\u5510\u6653\u6f9c\u592b\u5987\u5df2\u79bb\u5f00\u5929\u5c71\u4e4b\u4e8b\u544a\u8bc9\u51af\u7433\uff0c\u4f46\u4e3a\u4e86 \u6b63\u5728\u6c14\u5934\uff0c\u5374\u6545\u610f\u4e0d\u8bf4\uff0c\u5f04\u5f97\u540e\u6765\u9669\u4e9b\u8bef\u4e86\u51af\u7433\u5927\u4e8b\u3002 \u3000\u3000\u6768\u67f3\u9752\u5e26\u4e86\u5973\u513f\u75be\u8d70\uff0c\u51af\u7433\u7b11\u4e86\u4e00\u7b11\uff0c\u4e5f\u4fbf\u7531\u5979\u53bb\u4e86\u3002\u90b9\u7edb\u971e\u83ab\u540d\u5176\u5999\uff0c\u60f3\u95ee\u5979\u7684\u6bcd \u4eb2\uff0c\u89c1\u6bcd\u4eb2\u6c14\u9f13\u9f13\u7684\uff0c\u4e5f\u4e0d\u6562\u95f4\u3002\u4e24\u6bcd\u5973\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u89c1\u90a3\u4e66\u7ae5\u6c5f\u5357\uff0c\u53c8\u8ffd\u4e0a\u6765\uff0c\u5927\u53eb\u9053\uff1a \u201c\u5582\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u6211\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u8ba8\u538c\uff01\u201d\u90b9\u7edb\u971e\u6298\u4e86\u4e00\u682a\u6811\u679d\uff0c\u5411\u4ed6\u4e00\u6233\uff0c\u9053\uff1a \u201c\u95ee\u4f60\u4ec0\u4e48\uff1f\u201d\u6c5f\u5357\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u51fa\u53bb\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u6ca1\u6709\u70b9\u7740\uff01\u201d\u62cd\u4e00\u62cd \u624b\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u8981\u95ee\u5510\u5927\u4fa0\u4e48\uff1f\u201d\u90b9\u7ee6\u971e\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd9\u5c0f\u53ae\u4e5f\u8ba4\u5f97\u5510\u5927\u4fa0\u4e0d\u6210\uff1f\u201d\u6c5f \u5357\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u731c\u4e0d\u900f\uff0c\u6211\u4e0d\u6b62\u8ba4\u8bc6\u4ed6\uff0c\u8fd8\u633a\u8981\u597d\u5462\uff0c\u4ed6\u6bcf\u6b21\u89c1\u6211\uff0c\u90fd\u8981\u548c\u6211\u62c9\u624b\uff0c\u8c08\u597d\u534a \u5929\uff01\u4ed6\u8fd8\u6307\u70b9\u8fc7\u6211\u7684\u529f\u592b\u5462\uff01\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u5439\u725b\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ec0\u4e48\u5439\u725b\uff1f\u5510\u5927\u4fa0\u957f\u5f97\u633a \u82f1\u4fca\u7684\uff0c\u6bd4\u6211\u5bb6\u516c\u5b50\u5927\u4e24\u4e09\u5c81\uff0c\u6709\u4e00\u67c4\u5b9d\u5251\uff0c\u53eb\u505a\u6e38\u9f99\u5b9d\u5251\u7684\uff0c\u8fd8\u4f1a\u6253\u4e00\u79cd\u5947\u5f62\u602a\u72b6\u7684\u6697\u5668 \u53eb\u505a\u5929\u5c71\u795e\u8292\u7684\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u4f60\u8bf4\u7684\u662f\u5510\u7ecf\u5929\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u5510\u7ecf\u5929\u5c31\u662f\u5510\u5927\u4fa0\uff0c\u5510\u5927\u4fa0\u5c31\u662f\u5510\u7ecf\u5929\uff0c\u96be\u9053\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u4eba\uff1f\u521a\u624d\u90a3\u4e2a\u5973\u4eba\u8bf4\u4ed6\u5728\u5929 \u5c71\uff0c\u90a3\u662f\u9a97\u4f60\u4eec\u7684\u3002\u201d\u90b9\u8702\u971e\u7b11\u9053\uff1a\u201c\u6211\u5988\u5988\u95ee\u7684\u90a3\u4e2a\u5510\u5927\u4fa0\uff0c\u662f\u5510\u7ecf\u5929\u7684\u7238\u7238\u3002\u201d\u6c5f\u5357 \u9053\uff1a\u201c\u4ed6\u7684\u7238\u7238\u6211\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u6211\u6c5f\u5357\u7d20\u4e0d\u5439\u725b\uff0c\u77e5\u9053\u5c31\u8bf4\u77e5\u9053\uff0c\u4e0d\u77e5\u9053\u5c31\u8bf4\u4e0d\u77e5\u9053\u3002\u4f60\u8981 \u627e\u5510\u7ecf\u5929\uff0c\u6211\u5c31\u5e26\u4f60\u4eec\u53bb\uff0c\u4f60\u8981\u627e\u4ed6\u7684\u7238\u7238\uff0c\u8fd9\u4e2a\u5fd9\u6211\u5c31\u5e2e\u4e0d\u4e0a\u5566\uff01\u201d\u8f6c\u8fc7\u8eab\u4fbf\u8d70\uff0c\u90b9\u7edb\u971e \u8ffd\u4e0a\u53bb\u53eb\u9053\uff1a\u201c\u5582\uff0c\u6211\u6b63\u662f\u8981\u627e\u5510\u7ecf\u5929\u3002\u201d\u6c5f\u5357\u563b\u563b\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff0c\u8fd8\u8981\u6253\u6211\uff1f \u54fc\uff0c\u7ed9\u6211\u8d54\u793c\u513f\uff01\u201d\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e00\u5927\u8f66\uff0c\u8bf4\u8bf4\u6765\u8bf4\u53bb\uff0c\u73b0\u5728\u624d\u8bf4\u51fa\u5510\u7ecf\u5929\u7684\u540d\u5b57\uff0c \u8fd8\u602a\u6211\u5462\uff01\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u8c01\u4e0d\u77e5\u6211\u53eb\u505a\u591a\u5634\u7684\u6c5f\u5357\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u971e\u513f\uff0c\u522b\u542c\u4ed6\u80e1 \u626f\u3002\u201d\u6c5f\u5357\u89c1\u5979\u4eec\u610f\u6b32\u4e0d\u7406\uff0c\u53cd\u800c\u6025\u8d77\u6765\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u80e1\u626f\uff0c\u4f60\u4eec\u5982\u8981\u77e5\u9053\u5510\u7ecf\u5929\u7684\u4e0b \u843d\uff0c\u53ea\u6709\u95ee\u6211\uff01\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u8bf4\u5427\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ed6\u5c31\u4f4f\u5728\u6211\u4e3b\u4eba\u5bb6\u4e2d\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u9053\uff1a\u201c\u4f60\u4e3b\u4eba\u662f\u8c01\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u7684\u5c11\u4e3b\u4eba\u662f\u8428\u8fe6\u5ba3\u6170\u4f7f\u9648\u5b9a\u57fa\u9648\u8001\u5927\u4eba\u7684\u516c \u5b50\u9648\u5929\u5b87\u3002\u201d\u4ed6\u4e00\u53e3\u6c14\u5c06\u4e3b\u4eba\u7684\uff0c\u2018\u8854\u5934\u201d\u5ff5\u51fa\uff0c\u6709\u5982\u5ff5\u6025\u53e3\u4ee4\u4e00\u822c\uff0c\u6768\u67f3\u9752\u4e5f\u4e0d\u7981\u5f00\u989c\u4e00 \u7b11\u3002\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u542c\u89c1\u8fc7\u5510\u7ecf\u5929\u63d0\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u201d\u6c5f\u5357\u5f97\u610f\u6d0b\u6d0b\u5730\u7b11\u9053\uff1a\u201c\u662f\u4e0d\u9519 \u4e86\u5427\uff1f\u6211\u6c5f\u5357\u6709\u5439\u725b\u6ca1\u6709\uff1f\u201d\u90b9\u7edb\u971e\u6ee1\u5fc3\u9ad8\u5174\uff0c\u89c9\u5f97\u8fd9\u4e66\u7ae5\u4e5f\u5f88\u6709\u8da3\uff0c\u5e76\u4e0d\u8ba8\u538c\u4ed6\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u5c06\u6768\u67f3\u9752\u6bcd\u5973\u5e26\u5230\u5ba3\u6170\u4f7f\u8859\u95e8\uff0c\u9648\u5b9a\u57fa\u65e5\u5915\u76fc\u671b\u4ed6\u56de\u6765\uff0c\u6b63\u81ea\u7b49\u5f97\u5fc3\u6025\uff0c\u7acb\u523b\u53ec \u89c1\uff0c\u89c1\u4ed6\u548c\u4e24\u4e2a\u5973\u4eba\u540c\u6765\uff0c\u751a\u662f\u8be7\u5f02\uff0c\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u4f4d\u90b9\u592a\u592a\u662f\u5510\u5927\u4fa0\u7684\u957f\u8f88\uff0c\u6211\u6c5f\u5357\u597d\u5927 \u7684\u9762\u5b50\u624d\u8bf7\u5f97\u5979\u6765\uff01\u201d\u9648\u5b9a\u57fa\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u4e66\u7ae5\u4e0d\u61c2\u793c\u8c8c\uff0c\u4e24\u4f4d\u4f11\u602a\u3002\u201d\u547d\u5bb6\u4eba\u5524 \u9648\u5929\u5b87\u548c\u8427\u9752\u5cf0\u51fa\u6765\u3002\u8427\u9752\u5cf0\u719f\u6089\u6b66\u6797\u638c\u6545\uff0c\u4e00\u542c\u5f97\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u7684\u5973\u513f\uff0c\u8083\u7136\u8d77\u656c\uff0c\u6025 \u5fd9\u966a\u5979\u8bf4\u8bdd\u3002\u6768\u67f3\u9752\u8fd9\u624d\u77e5\u9053\u5510\u7ecf\u5929\u679c\u7136\u662f\u5728\u9648\u5bb6\u5c45\u4f4f\uff0c\u4f46\u6070\u597d\u5728\u524d\u4e24\u5929\u52a8\u8eab\uff0c\u4e0e\u51b0\u5ddd\u5929\u5973 \u540c\u5f80\u62c9\u8428\u53bb\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e5f\u5728\u966a\u5979\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u7236\u4eb2\u53eb\u9053\uff1a\u201c\u5b87\u513f\uff0c\u8fc7\u6765\uff01\u201d\u53ea\u89c1\u7236\u4eb2\u6367\u7740\u4e00\u7eb8\u516b\u884c\u4fe1 \u7b3a\uff0c\u624b\u6307\u5fae\u5fae\u98a4\u6296\u3002\u9648\u5929\u5b87\u4e00\u770b\uff0c\u4e5f\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u72c2\u559c\u53eb\u558a\uff0c\u539f\u6765\u90a3\u662f\u6c5f\u5357\u5e26\u56de\u6765\u7684\u9648\u5b9a\u57fa\u4eb2 \u5bb6\u5468\u5fa1\u53f2\u7684\u4fe1\uff0c\u4fe1\u4e2d\u8bf4\u4ed6\u5df2\u594f\u660e\u7687\u4e0a\uff0c\u4e0d\u65e5\u5c31\u5c06\u6709\u5723\u65e8\u5230\u6765\uff0c\u8d66\u4ed6\u56de\u4eac\uff0c\u5b98\u590d\u539f\u804c\u4e86\u3002\u9648\u5b9a \u57fa\u5341\u4f59\u5e74\u6765\u68a6\u60f3\u56de\u4e61\uff0c\u8bfb\u4e86\u6b64\u4fe1\uff0c\u559c\u6781\u800c\u6ce3\u9648\u5929\u5b87\u60f3\u8d77\u4e0d\u65e5\u5357\u5f52\uff0c\u6b63\u597d\u53ef\u4ee5\u6446\u8131\u571f\u53f8\u5973\u513f\u7684 \u7ea0\u7f20\uff0c\u4ea6\u662f\u559c\u4e0d\u81ea\u80dc\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u8fd9\u6b21\u591a\u4e8f\u4e86\u4f60\u5566\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u7b97\u5f97\u4e86\u4ec0\u4e48\uff01\u201d\u9648\u5b9a\u57fa\u4e5f\u7b11\u9053\uff1a \u201c\u6c5f\u5357\uff0c\u6211\u4e00\u5411\u4e0d\u653e\u5fc3\u4f60\uff0c\u539f\u6765\u4f60\u8fd8\u5f53\u771f\u6709\u7528\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u591a\u8c22\u8001\u7237\u5938\u5956\u3002\u6211\u6c5f\u5357\u867d\u7136\u6709 \u65f6\u80e1\u95f9\uff0c\u505a\u8d77\u4e8b\u6765\u5012\u662f\u9519\u4e0d\u4e86\u7684\u3002\u201d\u9648\u5b9a\u57fa\u5e73\u65e5\u6301\u5bb6\u4e25\u8083\uff0c\u8fd9\u65f6\u4efb\u5f97\u6c5f\u5357\u80e1\u8bf4\uff0c\u4e00\u70b9\u4e5f\u4e0d\u8d23 \u602a\u3002\u9648\u5b9a\u57fa\u5c06\u4e66\u4fe1\u6298\u597d\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u53ef\u4e0e\u5929\u5b87\u5144\u5f1f\u76f8\u79f0\uff0c\u4e0d\u5fc5\u518d\u4f5c\u4e66\u7ae5 \u5566\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4ee5\u540e\u8001\u738b\u4e5f\u4e0d\u80fd\u518d\u7ba1\u6211\u5566\uff1f\u662f\u4e0d\u662f\uff1f\u201d\u8001\u738b\u662f\u7ba1\u5bb6\u7684\u8001\u4ec6\uff0c\u5e73\u65e5\u6700 \u6b22\u559c\u9a82\u6c5f\u5357\u591a\u5634\uff0c\u9648\u5b9a\u57fa\u7b11\u9053\u3002\u201c\u90a3\u4e2a\u5f53\u7136\uff0c\u4e0d\u8fc7\u4ed6\u5e74\u7eaa\u6bd4\u4f60\u5927\uff0c\u4f60\u4e5f\u4e0d\u5e94\u5bf9\u4ed6\u6446\u4e3b\u5b50\u7684\u8eab \u4efd\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u53ea\u8981\u4ed6\u4e0d\u5435\u5506\u6211\uff0c\u6211\u5c82\u4f1a\u6b3a\u8d1f\u4ed6\uff1f\u8001\u7237\uff0c\u90a3\u4e48\u6211\u53bb\u54ea\u513f\u4e5f\u53ef\u4ee5\u4efb\u7531\u6211\u610f \u4e48\uff1f\u201d \u3000\u3000\u9648\u5b9a\u57fa\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ece\u4eca\u540e\u4f60\u4e0d\u518d\u662f\u7ae5\u4ec6\uff0c\u4f60\u613f\u7559\u4fbf\u7559\uff0c\u4e0d\u613f\u7559\u5462\uff0c\u6211\u9001\u4f60\u4e09\u767e\u4e24 \u94f6\u5b50\uff0c\u8ba9\u4f60\u81ea\u5df1\u6210\u5bb6\u7acb\u5ba4\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u8c01\u613f\u610f\u8ba8\u5ab3\u5987\u81ea\u60f9\u9ebb\u70e6\u3002\u4e0d\u8fc7\u6211\u7b54\u5e94\u8fc7\u8fd9\u4e24\u4f4d\u5a18 \u513f\uff0c\u5e2e\u5979\u4eec\u627e\u5230\u5510\u5927\u4fa0\u3002\u541b\u5b50\u4e0d\u80fd\u98df\u8a00\u3002\u5510\u5927\u4fa0\u65e2\u7136\u53bb\u4e86\u62c9\u8428\uff0c\u6211\u4e5f\u5f97\u966a\u5979\u4eec\u5230\u62c9\u8428\u3002\u56de\u6765 \u540e\u6211\u518d\u670d\u4f8d\u516c\u5b50\u3002\u201d\u9648\u5b9a\u57fa\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u597d\u5427\uff0c\u4f60\u89c1\u5510\u5927\u4fa0\u65f6\uff0c\u66ff\u6211\u95ee\u5019\u3002\u201d\u6c5f\u5357\u56de \u8eab\u5bf9\u90b9\u7edb\u971e\u9053\uff1a\u201c\u6211\u966a\u4f60\u4eec\u53bb\uff0c\u4f60\u53ef\u4e0d\u80fd\u518d\u53eb\u6211\u5c0f\u53ae\u5566\uff01\u201d \u3000\u3000\u6c5f\u5357\u679c\u7136\u966a\u6768\u67f3\u9752\u6bcd\u5973\u5230\u62c9\u8428\uff0c\u4f4f\u4e86\u51e0\u5929\uff0c\u5374\u4e0d\u77e5\u5230\u54ea\u513f\u53bb\u6253\u542c\u5510\u7ecf\u5929\u3002 \u3000\u3000\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u6bd4\u5979\u4eec\u65e9\u5230\u51e0\u5929\uff0c\u8fd9\u65f6\u6b63\u5728\u62c9\u8428\u78b0\u5230\u4e00\u4ef6\u6781\u5176\u79bb\u5947\u7684\u4e8b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u662f\u7b2c\u4e09\u6b21\u6765\u5230\u62c9\u8428\uff0c\u524d\u4e24\u6b21\u4ed6\u4eec\u867d\u7136\u5fc3\u5fc3\u76f8\u5370\u3001\u5916\u8868\u5374\u8fd8\u662f\u82e5\u5373\u82e5 \u79bb\u3002\u8fd9\u6b21\u4e24\u60c5\u878d\u5408\u65e0\u95f4\uff0c\u81ea\u662f\u5927\u4e0d\u76f8\u540c\u3002\u6708\u5915\u82b1\u671d\uff0c\u6668\u660f\u7d6e\u8bed\uff0c\u6b63\u662f\u8bf4\u4e0d\u5c3d\u7684\u5d0e\u5e9e\u98ce\u5149\uff0c\u67d4 \u60c5\u871c\u610f\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e5f\u4e3a\u4e00\u4ef6\u4e8b\u60c5\u611f\u5230\u70e6\u607c\uff0c\u90a3\u4fbf\u662f\u9f99\u7075\u77eb\u7684\u4e8b\u60c5\u3002\u9f99\u7075\u77eb\u88ab\u6355\u4e0b\u72f1\uff0c\u5df2\u662f \u4e8c\u5e74\u6709\u591a\uff0c\u751f\u6b7b\u672a\u77e5\uff0c\u5409\u51f6\u96be\u6d4b\uff0c\u4ed6\u4eec\u65e2\u4e0d\u4fbf\u63a2\u76d1\uff0c\u66f4\u4e0d\u597d\u52ab\u72f1\u3002\u4f55\u51b5\u9f99\u7075\u77eb\u662f\u5510\u5bb6\u7684\u8863\u94b5 \u4f20\u4eba\uff0c\u5510\u8001\u592a\u5a46\u5510\u8d5b\u82b1\u73b0\u8fd8\u5065\u5728\uff0c\u4ee5\u5979\u7684\u813e\u6c14\uff0c\u4e5f\u4e0d\u559c\u6b22\u5916\u4eba\u5e72\u9884\u5979\u95e8\u6237\u4e4b\u4e8b\uff0c\u6240\u4ee5\u5510\u6653\u6f9c \u66fe\u53ee\u5631\u8fc7\u513f\u5b50\uff0c\u53eb\u4ed6\u5230\u5ddd\u897f\u53bb\u77e5\u4f1a\u5510\u8d5b\u82b1\u3002\u540e\u6765\u7531\u51b0\u5ddd\u5929\u5973\u8f6c\u544a\u3002\u5f53\u65f6\u5510\u8d5b\u82b1\u6012\u6c14\u51b2\u51b2\uff0c\u6068 \u4e0d\u5f97\u7acb\u5373\u8d76\u5230\u62c9\u8428\uff0c\u5374\u4e0d\u6599\u540e\u6765\u53d1\u751f\u4e86\u91d1\u4e16\u9057\u5927\u95f9\u5510\u5bb6\u4e4b\u4e8b\uff0c\u5510\u8d5b\u82b1\u548c\u91d1\u4e16\u9057\u5f7c\u6b64\u4e2d\u4e86\u5bf9\u65b9 \u7684\u6bd2\u8baf\u867d\u7136\u5176\u540e\u4e92\u76f8\u4ea4\u6362\u89e3\u836f\uff0c\u4f46\u6599\u60f3\u5979\u5e74\u8001\u4f53\u8870\uff0c\u5143\u6c14\u6050\u6015\u4e0d\u6613\u6062\u590d\u3002\u6240\u4ee5\u5510\u8d5b\u82b1\u7a76\u7adf\u5230 \u4e86\u62c9\u8428\u6ca1\u6709\uff0c\u5510\u7ecf\u5929\u4e5f\u4e00\u65e0\u6240\u77e5\uff0c\u96be\u4ee5\u9884\u6d4b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u5546\u91cf\u4e4b\u540e\uff0c\u7ec8\u4e8e\u8fd8\u662f\u51b3\u5b9a\u53bb\u62dc\u4f1a\u798f\u5eb7\u5b89\uff0c\u8bbe\u6cd5\u63a2\u542c\u6d88\u606f\u3002\u4ed6\u4eec\u66fe\u4e3a\u798f \u5eb7\u5b89\u4fdd\u62a4\u8fc7\u91d1\u672c\u5df4\u74f6\uff0c\u51b0\u5ddd\u5929\u5973\u6700\u8fd1\u53c8\u66fe\u56e0\u4e3a\u8428\u67b7\u53db\u4e71\u4e4b\u4e8b\uff0c\u4ee5\u4f5b\u95e8\u62a4\u6cd5\u7684\u8eab\u4efd\u8c12\u89c1\u8fc7\u8fbe\u8d56 \u6d3b\u4f5b\u548c\u798f\u5eb7\u5b89\uff0c\u6240\u4ee5\u4ed6\u4eec\u6599\u60f3\u798f\u5eb7\u5b89\u4e0d\u81f3\u4e8e\u4e0d\u89c1\u4ed6\u4eec\u3002 \u3000\u3000\u4ed6\u4eec\u5230\u4e86\u62c9\u8428\u7684\u7b2c\u4e09\u5929\uff0c\u4fbf\u5230\u9a7b\u85cf\u5927\u81e3\u7684\u8859\u95e8\u62dc\u4f1a\u798f\u5eb7\u5b89\uff0c\u53ea\u89c1\u8859\u4e2d\u6212\u5907\u68ee\u4e25\uff0c\u5927\u6b8a\u5f80 \u6614\uff0c\u4ed6\u4eec\u65e9\u5df2\u5907\u529e\u793c\u7269\uff0c\u62dc\u6258\u7b7e\u62bc\u623f\u7684\u95e8\u5b98\uff0c\u8bf7\u4ed6\u7acb\u5373\u901a\u62a5\uff0c\u5728\u7b7e\u62bc\u623f\uff08\u76f8\u5f53\u4e8e\u73b0\u4ee3\u673a\u5173\u7684 \u4f20\u8fbe\u5ba4\uff09\u5750\u4e86\u4e00\u4f1a\uff0c\u679c\u7136\u4fbf\u6709\u4e00\u4e2a\u5b98\u513f\u5e26\u4ed6\u4eec\u5230\u5185\u8859\u7684\u5ba2\u623f\uff0c\u5949\u8336\u4e4b\u540e\uff0c\u95e8\u5916\u6709\u4eba\u63ed\u5e18\u8d70 \u4eba\uff0c\u5510\u7ecf\u5929\u7ad9\u8d77\u6765\u4e00\u770b\uff0c\u6765\u7684\u5374\u662f\u4e00\u4f4d\u5e08\u7237\u3002 \u3000\u3000\u90a3\u5e08\u7237\u8bf4\u9053\uff1a\u201c\u798f\u5927\u5e05\u7389\u4f53\u8fdd\u548c\uff0c\u672c\u6765\u4e0d\u89c1\u5bbe\u5ba2\uff0c\u542c\u8bf4\u662f\u4e8c\u4f4d\u6765\uff0c\u7279\u5730\u53eb\u5c0f\u53ef\u8fce\u63a5\uff0c\u4e0d \u8bc6\u4e8c\u4f4d\u6709\u4f55\u89c1\u6559\uff1f\u201d\u5510\u7ecf\u5929\u5927\u5931\u6240\u671b\uff0c\u4f46\u60f3\u65e2\u7136\u6765\u4e86\uff0c\u4e0d\u613f\u7a7a\u624b\u800c\u56de\uff0c\u4fbf\u5047\u4f5c\u4e0d\u77e5\u9053\u9f99\u7075\u77eb \u88ab\u6355\u4e0b\u72f1\u4e4b\u4e8b\uff0c\u5411\u5e08\u7237\u63a2\u95ee\u9053\uff1a\u201c\u6211\u4eec\u6709\u4f4d\u670b\u53cb\uff0c\u542c\u8bf4\u5728\u798f\u5927\u5e05\u5e55\u4e2d\uff0c\u60f3\u6765\u63a2\u542c\u4e00\u4e0b\uff0c\u4e0d\u77e5 \u4ed6\u662f\u5426\u5c1a\u5728\u6b64\u5904\uff1f\u201d\u90a3\u5e08\u7237\u9887\u611f\u610f\u5916\uff0c\u95ee\u9053\uff1a\u201c\u8d35\u53cb\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u59d3\u9f99\u540d\u7075 \u77eb\u3002\u201d\u90a3\u5e08\u7237\u9762\u8272\u4e00\u53d8\uff0c\u8fde\u8fde\u6447\u624b\u9053\uff1a\u201c\u6ca1\u542c\u8bf4\u6709\u8fd9\u4e2a\u4eba\uff01\u201d\u5510\u7ecf\u5929\u89c1\u4ed6\u5982\u6b64\u5f20\u7687\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u4ed6\u80fd\u4ee3\u8868\u798f\u5eb7\u5b89\u63a5\u89c1\u5ba2\u4eba\uff0c\u81ea\u5e94\u662f\u798f\u5eb7\u5b89\u7684\u4eb2\u4fe1\u5fc3\u8179\u4e86\uff0c\u4e0d\u81f3\u4e8e\u6015\u4eba\u8bef\u4f1a\u4ed6\u4e0e\u53db\u9006\u6709 \u7275\u8fde\uff0c\u96be\u9053\u662f\u9f99\u7075\u77eb\u6709\u4ec0\u4e0d\u5999\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5e08\u7237\u4fbf\u60f3\u7aef\u8336\u9001\u5ba2\uff0c\u5510\u7ecf\u5927\u89c1\u4ed6\u6367\u8d77\u8336\u676f\uff0c\u5047\u88c5\u4e0d\u61c2\u5b98\u95e8\u793c\u8282\uff0c\u4ecd\u7136\u7aef\u5750\u4e0d\u52a8\uff0c\u6545\u610f \u7d6e\u7d6e\u7684\u95ee\u798f\u5eb7\u5b89\u662f\u4ec0\u4e48\u75c5\uff0c\u770b\u4ec0\u4e48\u533b\u751f\uff0c\u5403\u4ec0\u4e48\u836f\uff0c\u90a3\u5e08\u7237\u652f\u652f\u543e\u543e\uff0c\u5750\u7acb\u4e0d\u5b89\u3002\u770b\u60c5\u5f62\uff0c \u798f\u5eb7\u5b89\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u75c5\u3002\u5510\u7ecf\u5929\u6b63\u5728\u597d\u7b11\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u6684\u95f9\u4eba\u58f0\uff0c\u6709\u4eba\u5927\u58f0\u8bf4\u9053\uff0c\u201c\u798f\u5927 \u5e05\u4e0d\u89c1\u5ba2\uff0c\u522b\u7684\u5ba2\u4eba\u53ef\u4ee5\u4e0d\u89c1\uff0c\u6211\u90a3\u5374\u662f\u975e\u89c1\u4e0d\u6210\uff01\u201d \u3000\u3000\u4e00\u542c\u4e4b\u4e0b\u5341\u5206\u719f\u6089\uff0c\u539f\u6765\u7adf\u662f\u4e91\u7075\u5b50\u7684\u58f0\u97f3\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u51db\uff0c\u8981\u77e5\u4e91\u7075\u5b50\u4e43\u662f\u6e05\u5ef7\u5927 \u5185\u7684\u201c\u4f9b\u5949\u201d\uff0c\u804c\u4f4d\u6bd4\u4f8d\u536b\u66f4\u9ad8\u4e00\u7ea7\uff1b\u5f53\u521d\u5c31\u662f\u6d3e\u4ed6\u6765\u6349\u62ff\u9f99\u7075\u77eb\u7684\u3002\u540e\u6765\u798f\u5eb7\u5b89\u5c06\u9f99\u7075\u77eb \u6263\u62bc\u5728\u9a7b\u85cf\u5927\u81e3\u7684\u8859\u95e8\uff0c\u4e91\u7075\u5b50\u53c8\u662f\u56de\u4eac\u8bf7\u65e8\u7684\u4eba\u3002 \u3000\u3000\u897f\u85cf\u4e0e\u5185\u5730\u9694\u79bb\uff0c\u60c5\u51b5\u7279\u6b8a\uff0c\u4fd7\u8bed\u6709\u4e91\uff1a\u201c\u5c71\u9ad8\u7687\u5e1d\u8fdc\u201d\uff0c\u4f55\u51b5\u798f\u5eb7\u5b89\u53c8\u662f\u5f53\u4eca\u7687\u4e0a\u6700 \u4eb2\u4fe1\u7684\u4eba\uff0c\u5949\u547d\u5168\u6743\u5904\u7406\u85cf\u4e8b\u3002\u8859\u95e8\u4e2d\u7684\u540f\u5f79\uff0c\u6043\u7740\u798f\u5eb7\u5b89\u7684\u5a01\u52bf\uff0c\u5373\u4f7f\u662f\u5bf9\u4ece\u5317\u4eac\u6765\u7684\u5b98 \u5458\uff0c\u4e5f\u5e76\u4e0d\u600e\u6837\u5356\u5e10\uff0c\u89c1\u4e91\u7075\u5b50\u76f8\u8c8c\u7c97\u9c81\uff0c\u8bf4\u8bdd\u53c8\u5982\u6b64\u56a3\u5f20\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u738b\u516c\u8d1d\u52d2\u5230\u6765\uff0c \u4e5f\u5f97\u7b49\u5019\u6211\u4eec\u7684\u798f\u5927\u4eba\u4f20\u89c1\uff0c\u54ea\u6709\u8fd9\u6837\u4e71\u95ef\u8859\u95e8\u7684\u9053\u7406\uff1f\u201d\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u8fd8\u4e0d\u77e5 \u9053\u4ed6\u662f\u5927\u5185\u4f9b\u5949\u3002\u4e0d\u8fc7\u7167\u798f\u5eb7\u5b89\u7684\u6743\u52bf\uff0c\u5927\u5185\u4f9b\u5949\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u8bba\u7406\u53ea\u8be5\u5230\u5927\u5e05\u8425\u7684\u4e2d\u519b \u5904\u62a5\u5230\uff0c\u7136\u540e\u8bf7\u6c42\u8c12\u89c1\u624d\u662f\uff0c\u4e91\u7075\u5b50\u4e4b\u6562\u95ef\u8859\uff0c\u5b9a\u662f\u53e6\u6709\u6240\u6043\u3002\u201d\u679c\u7136\u542c\u5f97\u4e91\u7075\u5b50\u54fc\u4e86\u4e00 \u58f0\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u738b\u516c\u8d1d\u52d2\u53ef\u4ee5\u4e0d\u89c1\u3002\u82e5\u7136\u7687\u4e0a\u5230\u6765\uff0c\u4f60\u4eec\u7684\u798f\u5927\u4eba\u89c1\u662f\u4e0d\u89c1\uff1f\u201d\u90a3\u540f\u5f79\u4f3c \u662f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5949\u4e86\u5723\u65e8\u7684\u4e48\uff1f\u201d\u53ea\u542c\u5f97\u60fa\u7684\u4e00\u58f0\uff0c\u4f3c\u662f\u91d1\u5c5e\u76f8\u89e6\u7684\u58f0\u54cd\uff0c\u4e91\u7075\u5b50 \u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u2018\u5982\u6715\u4eb2\u4e34\uff0c\u8fd9\u51e0\u4e2a\u5b57\u4f60\u4eec\u8ba4\u4e0d\u8ba4\u5f97\uff1f\u5feb\u53eb\u798f\u5eb7\u5b89\u6765\u606d\u63a5\u5723\u65e8\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u8fd9\u4e00\u95ee\u623f\uff0c\u4e09\u4e2a\u4eba\u90fd\u4e0d\u81ea\u89c9\u5730\u505c\u4e86\u8bf4\u8bdd\uff0c\u63a5\u5f85\u5510\u7ecf\u5929\u7684\u90a3\u4e2a\u5e08\u7237\u9762\u8272\u66f4\u89c1\u6c89\u6697\uff0c \u539f\u6765\u4ed6\u4e0e\u9f99\u7075\u77eb\u4e43\u662f\u6614\u65e5\u540c\u50da\uff0c\u79c1\u60c5\u4e0d\u9519\uff0c\u4e5f\u6599\u5230\u4e91\u7075\u5b50\u662f\u4e3a\u9f99\u7075\u77eb\u800c\u6765\uff0c\u53ea\u662f\u7687\u4e0a\u7adf\u628a\u4e00 \u9762\u201c\u5982\u6715\u4eb2\u4e34\u2019\u7684\u91d1\u724c\uff0c\u4ea4\u7ed9\u4e00\u4e2a\u4f8d\u536b\u5e26\u6765\uff0c\u770b\u6765\u7687\u4e0a\u628a\u9f99\u7075\u77eb\u7684\u4e8b\u60c5\u770b\u5f97\u975e\u5e38\u91cd\u8981\uff0c\u800c\u9f99 \u7075\u77eb\u4e5f\u662f\u51f6\u591a\u5409\u5c11\u7684\u4e86\uff01 \u3000\u3000\u540f\u5f79\u89c1\u4e86\u91d1\u724c\uff0c\u5927\u4e3a\u9707\u60ca\uff0c\u5f53\u7136\u4e0d\u6562\u518d\u6020\u6162\u4e86\uff0c\u6025\u5fd9\u8bf7\u4ed6\u5230\u53e6\u4e00\u95f4\u5ba2\u623f\uff0c\u540c\u65f6\u53bb\u7980\u798f\u5eb7 \u5b89\u3002\u5510\u7ecf\u5929\u7ec6\u542c\u4ed6\u4eec\u811a\u6b65\u58f0\u7684\u65b9\u5411\uff0c\u5ffd\u7136\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u798f\u5927\u5e05\u65e2\u662f\u8eab\u4f53\u8fdd\u548c\uff0c\u90a3\u672a\u6211\u4eec\u4e5f\u544a \u8f9e\u4e86\u3002\u798f\u5927\u5e05\u8ddf\u524d\uff0c\u70e6\u4f60\u4ee3\u6211\u4eec\u65a5\u540d\u9053\u5019\u3002\u201d\u90a3\u5e08\u7237\u5df4\u4e0d\u5f97\u4ed6\u4eec\u65e9\u8d70\uff0c\u8fde\u5fd9\u9001\u5ba2\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8f7b\u8f7b\u62c9\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u8863\u8896\u4e00\u4e0b\uff0c\u4e24\u4eba\u4e0d\u7406\u90a3\u4e2a\u5e08\u7237\uff0c\u5f84\u81ea\u5927\u8e0f\u6b65\u7684\u5411\u524d\u884c\u8d70\uff0c\u90a3 \u5e08\u7237\u5fd9\u9053\uff1a\u201c\u8bf7\u4ece\u8fd9\u8fb9\u8d70\u3002\u201d\u4ed6\u8fd8\u4ee5\u4e3a\u5510\u7ecf\u5929\u4e0d\u8bc6\u9053\u8def\uff0c\u8d70\u9519\u4e86\u65b9\u5411\u3002\u5510\u7ecf\u5929\u5934\u4e5f\u4e0d\u56de\uff0c\u8d70 \u5230\u4e00\u95f4\u623f\u5b50\u5916\u8fb9\u53bb\uff0c\u5ffd\u7136\u505c\u4e0b\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u602a\u58f0\u602a\u6c14\u7684\u53eb\u9053\uff1a\u201c\u597d\u5927\u7684\u67b6\u5b50\uff01\u201d\u4ed6\u6545\u610f \u53d8\u4e86\u55d3\u5b50\uff0c\u542c\u8d77\u6765\u6d3b\u50cf\u4e00\u4e2a\u8001\u5e08\u7237\u5728\u6253\u5b98\u8154\uff0c\u5341\u5206\u523a\u8033\u3002 \u3000\u3000\u4e91\u7075\u5b50\u6b63\u5728\u8fd9\u95f4\u623f\u5185\uff0c\u95fb\u58f0\u5927\u6012\uff0c\u8df3\u51fa\u6765\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\u3001\u80c6\u6562\u2014\u2014\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u9661 \u7136\u89c1\u662f\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff01\u5510\u7ecf\u5929\u8bf4\u9053\uff1a\u201c\u70e6\u501f\u5723\u65e8\u4e00\u89c2\uff01\u201d\u8bf4\u6765\u7a00\u677e\u5e73 \u5e38\uff0c\u5c31\u50cf\u8ddf\u8001\u670b\u53cb\u5546\u91cf\u4e00\u6837\u3002\u51b0\u5ddd\u5929\u5973\u9762\u5411\u7740\u4e91\u7075\u5b50\uff0c\u624b\u6307\u5fae\u5fae\u7fd8\u8d77\uff0c\u6307\u7aef\u631f\u7740\u4e00\u679a\u51b0\u9b44\u795e \u5f39\uff0c\u53d1\u51fa\u523a\u9aa8\u7684\u5947\u5bd2\u4e4b\u6c14\uff01 \u3000\u3000\u4e91\u7075\u5b50\u5413\u5f97\u4e0d\u6562\u52a8\u5f39\uff0c\u5510\u7ecf\u5929\u4ece\u4ed6\u8eab\u4e0a\u641c\u51fa\u5723\u65e8\uff0c\u62c6\u5f00\u6765\u4e00\u770b\uff0c\u53ea\u4e49\u4e0a\u9762\u5199\u7684\u662f\uff1a\u201c\u524d \u671d\u9006\u81e3\u5e74\u7fb9\u5c27\u4e4b\u5b50\u5e74\u5bff\u5316\u540d\u9f99\u7075\u77eb\uff0c\u6f5c\u5165\u897f\u85cf\uff0c\u56fe\u8c0b\u53db\u4e71\uff0c\u65e2\u5df2\u64d2\u83b7\uff0c\u53ef\u5728\u5f53\u5730\u5904\u51b3\uff0c\u4e0d\u5fc5 \u89e3\u4eac\u3002\u6b64\u8c15\u9a7b\u85cf\u5927\u81e3\u798f\u5eb7\u5b89\u3002\u201d\u8c15\u65e8\u53ea\u5199\u9f99\u7075\u77eb\uff0c\u2018\u6f5c\u5165\u897f\u85cf\u2019\uff0c\u6ca1\u8bf4\u4ed6\u201c\u6df7\u4eba\u5e55\u5e9c\u201d\uff0c\u5df2 \u662f\u7ed9\u4e86\u798f\u5b89\u5eb7\u5929\u5927\u7684\u9762\u5b50\uff0c\u5510\u7ecf\u5929\u539f\u6599\u5230\u9f99\u7075\u77eb\u51f6\u591a\u5409\u5c11\uff0c\u5374\u6ca1\u6599\u6765\u5f97\u5982\u662f\u4e4b\u5feb\uff0c\u6367\u7740\u5723 \u65e8\uff0c\u767b\u65f6\u5446\u4e86\u3002 \u3000\u3000\u5185\u5802\u4f20\u6765\u53f1\u559d\u7684\u58f0\u97f3\uff0c\u662f\u798f\u5eb7\u5b89\u5373\u5c06\u51fa\u6765\u7684\u4fe1\u53f7\uff0c\u4ee3\u8868\u798f\u5eb7\u5b89\u9001\u5ba2\u7684\u5200\u9644\u5e08\u7237\u5413\u5f97\u9762\u5982 \u571f\u8272\uff0c\u5510\u7ecf\u5929\u7fdf\u7136\u4e00\u60ca\uff0c\u6025\u5fd9\u5c06\u5723\u65e8\u585e\u56de\u4e91\u7075\u5b50\u6000\u5185\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u591a\u8c22\u8d50\u9605\u3002\u201d\u4e00\u8f6c\u8eab\uff0c\u7acb \u523b\u4e0e\u51b0\u5ddd\u5929\u5973\u5954\u51fa\u96e8\u9053\u3002\u4e91\u7075\u5b50\u60ca\u9b42\u672a\u5b9a\uff0c\u89c1\u4e86\u798f\u5eb7\u5b89\u4e4b\u65f6\u6c14\u7130\u5927\u51cf\uff0c\u88ab\u5510\u7ecf\u5929\u5077\u53bb\u5723\u65e8\u89c2 \u770b\u7684\u4e8b\uff0c\u90a3\u66f4\u662f\u4e0d\u6562\u63d0\u4e86\u3002 \u3000\u3000\u56de\u5230\u65c5\u820d\uff0c\u4e24\u4eba\u5546\u91cf\u4e86\u597d\u534a\u5929\uff0c\u51b0\u5ddd\u5929\u5973\u5ffd\u7136\u60f3\u8d77\u9f99\u7075\u77eb\u8fd8\u6709\u4e00\u4e2a\u5e08\u5f1f\uff0c\u540d\u5524\u989c\u6d1b\uff0c\u4f4f \u5728\u5e03\u8fbe\u62c9\u5bab\u5185\u4e1c\u9762\u7684\u8461\u8404\u5c71\u4e0b\uff0c\u4e24\u4eba\u7acb\u5373\u51fa\u57ce\uff0c\u8d76\u5230\u989c\u6d1b\u4f4f\u6240\uff0c\u90a3\u5730\u65b9\u672c\u662f\u9f99\u7075\u77eb\u65e7\u65e5\u7684\u4f4f \u623f\uff0c\u9f99\u7075\u77eb\u56e0\u4e3a\u5411\u5f97\u798f\u5eb7\u5b89\u5ba0\u4fe1\uff0c\u88ab\u6355\u4e4b\u540e\uff0c\u798f\u5eb7\u5b89\u7279\u522b\u5bbd\u9650\uff0c\u5e76\u4e0d\u67e5\u6284\u5bb6\u4e1a\uff0c\u4ecd\u51c6\u989c\u6d1b\u4f4f \u5728\u8be5\u5904\u770b\u5b88\u3002 \u3000\u3000\u989c\u6d1b\u7acb\u523b\u8bf7\u4ed6\u5230\u5bc6\u5ba4\u5546\u8bae\uff0c\u5173\u4e0a\u623f\u95e8\uff0c\u989c\u6d1b\u4fbf\u9053\uff1a\u201c\u5510\u5927\u4fa0\u4e49\u8584\u4e91\u5929\uff0c\u5c0f\u5f1f\u6709\u4e0d\u60c5\u4e4b \u8bf7\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f46\u8bf4\u65e0\u59a8\uff01\u201d\u989c\u6d1b\u9053\uff1a\u201c\u5c0f\u5f1f\u60f3\u6765\u60f3\u53bb\uff0c\u5b9e\u65e0\u4ed6\u6cd5\u53ef\u6551 \u5e08\u5144\uff0c\u552f\u6709\u52ab\u72f1\uff01\u201d\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u9f99\u7075\u77eb\u4e0e\u6211\u6ca1\u6df1\u4ea4\uff0c\u6211\u5bf9\u4ed6\u7684\u4e3a\u4eba\u5e76\u4e0d \u77e5\u9053\u6e05\u695a\uff0c\u8fd9\u72b9\u7f62\u4e86\uff0c\u82e5\u7136\u5e2e\u4ed6\u52ab\u72f1\uff0c\u8fd9\u5c82\u4e0d\u662f\u8981\u5728\u62c9\u8428\u60f9\u8d77\u8f69\u7136\u5927\u6ce2\uff01\u201d\u7ee7\u800c\u4e00\u60f3\uff1a\u201c\u9f99 \u7075\u77eb\u867d\u662f\u5e74\u7fb9\u5c27\u7684\u540e\u4eba\uff0c\u4f46\u770b\u4ed6\u505a\u7684\u51e0\u6869\u4e8b\u60c5\uff0c\u4e5f\u8fd8\u662f\u4e2a\u6709\u809d\u80c6\u7684\u7537\u5b50\u3002\u4ea4\u60c5\u867d\u6d45\uff0c\u4f46\u773c\u770b \u8fd9\u6837\u7684\u4eba\u6750\u88ab\u6e05\u5ef7\u5904\u51b3\uff0c\u603b\u662f\u53ef\u60dc\u3002\u201d\u7ee7\u800c\u53c8\u60f3\u9053\uff1a\u201c\u542c\u7239\u7239\u5728\u5929\u5c71\u6240\u8bf4\uff0c\u9f99\u7075\u77eb\u5fc3\u5207\u7236 \u4ec7\uff0c\u770b\u4ed6\u5728\u798f\u5eb7\u5b89\u5e55\u4e2d\uff0c\u5341\u5e74\u6765\u5904\u5fc3\u79ef\u8651\uff0c\u53ea\u6015\u51fa\u72f1\u4e4b\u540e\uff0c\u66f4\u917f\u6210\u5de8\u53d8\u3002\u201d\u4f46\u968f\u5373\u60f3\u5230\uff1a \u201c\u9f99\u7075\u77eb\u4e5f\u662f\u4e2a\u660e\u767d\u4eba\uff0c\u6211\u6551\u4ed6\u51fa\u72f1\u4e4b\u540e\uff0c\u529d\u4ed6\u653e\u5f03\u5728\u897f\u85cf\u5efa\u57fa\u7acb\u4e1a\u7684\u56fe\u8c0b\uff0c\u6599\u4ed6\u80af\u542c\u3002\u7239 \u7239\u65e2\u80af\u8ba9\u6211\u53bb\u77e5\u4f1a\u5510\u8001\u592a\u5a46\uff0c\u90a3\u4e48\u51fa\u624b\u6551\u4ed6\uff0c\u8c05\u7239\u7239\u4e5f\u4e0d\u4f1a\u8d23\u5907\u3002\u201d\u5510\u7ecf\u5929\u81ea\u5e7c\u53d7\u7236\u4eb2\u7684\u718f \u9676\uff0c\u9047\u5230\u5927\u4e8b\uff0c\u603b\u662f\u8003\u8651\u5f97\u5468\u8be6\u4e4b\u6781\uff0c\u7136\u540e\u53bb\u505a\u3002\u4e3b\u610f\u4e00\u5b9a\uff0c\u90a3\u4fbf\u662f\u4e49\u65e0\u53cd\u987e\u7684\u4e86\u3002 \u3000\u3000\u989c\u6d1b\u89c1\u5510\u7ecf\u5929\u8e0c\u8e87\u518d\u56db\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u53ea\u9053\u4e8b\u60c5\u7edd\u671b\u3002\u5510\u7ecf\u5929\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u4eca\u665a\u4e8c\u66f4\uff01\u201d \u989c\u6d1b\u5927\u559c\uff0c\u8fd8\u672a\u8bf4\u5f97\u51fa\u8bdd\u6765\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u8e44\u58f0\u75be\u54cd\uff01 \u3000\u3000\u989c\u6d1b\u9053\uff1a\u201c\u59d4\u5c48\u4e24\u4f4d\u5728\u8fd9\u6597\u5ba4\u6682\u8eb2\u4e00\u4f1a\u3002\u201d\u51fa\u5916\u53bb\u770b\uff0c\u53ea\u89c1\u798f\u5eb7\u5b89\u7684\u536b\u58eb\u961f\u957f\u7f57\u8d85\u5e26\u4e86 \u516d\u4e2a\u4eba\u6765\uff0c\u989c\u6d1b\u8ba4\u5f97\u5176\u4e2d\u56db\u4eba\u90fd\u662f\u798f\u5eb7\u5b89\u5e10\u4e0b\u7684\u9ad8\u624b\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u7537\u4e00\u5973\uff0c\u76f8\u8c8c\u53e4\u602a\uff0c\u4e00\u526f \u9a84\u6001\uff0c\u8fd9\u4e24\u4eba\u4e43\u662f\u4e91\u7075\u5b50\u592b\u5987\uff0c\u989c\u6d1b\u5374\u4e0d\u8ba4\u5f97\u3002 \u3000\u3000\u989c\u6d1b\u5403\u4e86\u4e00\u60ca\uff0c\u62b1\u62f3\u95ee\u9053\uff1a\u201c\u7f57\u961f\u957f\u6df1\u591c\u964d\u4e34\uff0c\u6709\u4f55\u8d50\u6559\uff1f\u201d\u7f57\u8d85\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u989c\u6d1b\u5475\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d\u989c\u6d1b\u9053\uff1a\u201c\u5351\u804c\u5949\u516c\u5b88\u6cd5\uff0c\u5e76\u65e0\u903e\u77e9\uff0c\u7f57\u961f\u957f\u6b64\u8bdd\u662f\u4ec0\u4e48\u610f \u601d\uff1f\u201d\u7f57\u8d85\u9053\uff1a\u201c\u660e\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u4f60\u5c06\u9f99\u8001\u4e09\u52ab\u5230\u90a3\u513f\u53bb\u4e86\uff1f\u201d\u989c\u6d1b\u4e00\u9707\uff0c\u5931\u58f0\u53eb\u9053\uff1a \u201c\u4ec0\u4e48\uff0c\u6211\u5e08\u5144\u88ab\u4eba\u52ab\u53bb\u4e86\uff1f\u201d\u7f57\u8d85\u559d\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\u3002\u4f60\u8fd8\u60fa\u677e\u4f5c\u6001\uff0c\u8fd9\u672a\u514d\u592a\u4e0d\u591f\u670b\u53cb \u4e86\uff0c\u5f53\u771f\u8fd8\u8981\u6211\u52a8\u624b\u4e48\uff1f\u201d\u989c\u6d1b\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1b\u201c\u8fd9\uff0c\u8fd9\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d\u7f57\u8d85\u9053\uff1a\u201c\u82e5\u4e0d\u662f \u4f60\uff0c\u8fd8\u6709\u4f55\u4eba\u52ab\u72f1\uff1f\u201d\u989c\u6d1b\u9053\uff1a\u201c\u5c0f\u5f1f\u8db3\u4e0d\u51fa\u6237\uff0c\u5df2\u6709\u534a\u6708\uff0c\u600e\u80fd\u5206\u8eab\u524d\u5f80\u52ab\u72f1\uff1f\u201d \u3000\u3000\u7f57\u8d85\u671b\u4e86\u989c\u6d1b\u4e00\u773c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u795e\u8272\u5982\u5e38\uff0c\u5e76\u65e0\u75b2\u6001\uff0c\u6211\u4eec\u4e00\u5230\uff0c\u4ed6\u53c8\u7acb\u5373\u51fa\u6765\uff0c \u8863\u670d\u4e5f\u6574\u6d01\u65e0\u5c18\uff0c\u96be\u9053\u52ab\u72f1\u7684\u53e6\u6709\u5176\u4eba\uff0c\u786e\u5b9e\u4e0d\u662f\u4ed6\uff1f\u201d\u989c\u6d1b\u9053\uff1a\u201c\u8bf7\u95ee\u52ab\u72f1\u60c5\u5f62\u5982\u4f55\uff0c\u5927 \u7262\u536b\u58eb\u5982\u4e91\uff0c\u96be\u9053\u6ca1\u6709\u4e00\u4eba\u548c\u98de\u8d3c\u671d\u76f8\u4e48\uff1f\u201d\u7f57\u8d85\u5c34\u5c2c\u4e4b\u6781\uff0c\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u95ee \u4f60\u8981\u4eba\uff0c\u4f60\u5374\u53cd\u800c\u95ee\u8d77\u6211\u6765\u4e86\u3002\u7f57\u67d0\u867d\u662f\u65e0\u80fd\uff0c\u4e5f\u4e0d\u80fd\u4efb\u4f60\u620f\u800d\uff01\u201d\u6562\u60c5\u4ed6\u4eec\u8fde\u98de\u8d3c\u7684\u5f71\u5b50 \u90fd\u6ca1\u89c1\u7740\uff0c\u5c31\u53d1\u73b0\u9f99\u7075\u77eb\u88ab\u52ab\u8d70\u4e86\u3002\u6545\u6b64\u7f57\u8d85\u88ab\u4ed6\u95ee\u7740\uff0c\u4fbf\u4e00\u53e3\u54ac\u5b9a\u662f\u4ed6\u3002\u989c\u6d1b\u9053\uff1a\u201c\u82e5\u7136 \u662f\u6211\u52ab\u72f1\uff0c\u6211\u5c82\u80fd\u5728\u6b64\u606d\u5019\u8bf8\u4f4d\u5149\u4e34\uff0c\u8bf8\u4f4d\u4e0d\u4fe1\uff0c\u8bf7\u5c3d\u7ba1\u641c\u67e5\u3002\u201d\u7f57\u8d85\u51b7\u7b11\u9053\uff0c\u201c\u7109\u77e5\u4f60\u7528 \u7684\u4e0d\u662f\u82e6\u8089\u4e4b\u8ba1\uff1f\u628a\u9f99\u8001\u4e09\u653e\u8d70\u4e86\uff0c\u4f60\u81ea\u613f\u9876\u6869\u3002\u5ff5\u5728\u5f7c\u6b64\u540c\u4e8b\u4e00\u573a\uff0c\u4f60\u628a\u9f99\u8001\u4e09\u85cf\u8eab\u4e4b\u5904 \u544a\u8bc9\u4e8e\u6211\uff0c\u6211\u4e5f\u4e0d\u6b32\u5c06\u4f60\u96be\u4e3a\u3002\u201d\u989c\u6d1b\u9053\uff1a\u201c\u4f60\u5c31\u662f\u628a\u6211\u63d2\u4e86\u4e09\u5200\u516d\u6d1e\uff0c\u6211\u4e5f\u8bf4\u4e0d\u51fa\u5e08\u5144\u4e0b \u843d\u3002\u201d \u3000\u3000\u7f57\u8d85\u770b\u4ed6\u795e\u8272\uff0c\u989c\u6d1b\u4e0d\u4f3c\u5047\u88c5\uff0c\u5fc3\u4e2d\u8e0c\u8e87\u96be\u51b3\uff0c\u4e91\u7075\u5b50\u559d\u9053\uff1a\u201c\u65e2\u8fd9\u53ae\u662f\u9f99\u7075\u77eb\u7684\u5e08 \u5f1f\uff0c\u90a3\u5c31\u53ea\u6709\u7740\u843d\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u4e0e\u4ed6\u7f57\u55e6\u4f5c\u751a\uff1f\u201d\u8de8\u524d\u4e00\u6b65\uff0c\u5f20\u5f00\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u5411\u989c\u6d1b\u80a9 \u5934\u4e00\u6293\u6293\u4e0b\u3002\u989c\u6d1b\u8eab\u5b50\u7a0d\u4fa7\uff0c\u907f\u5f00\u4e86\u4ed6\u4e00\u6293\uff0c\u731b\u5730\u91cc\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u4e94\u8272\u6591\u6593\u7684\u5f69\u5e26\uff0c\u957f\u8679 \u822c\u7684\u75be\u5377\u800c\u6765\uff0c\u4e00\u6761\u5f69\u5e26\uff0c\u7adf\u4f7f\u5f97\u4f3c\u8f6f\u97ad\u4e00\u6837\u3002\u989c\u6d1b\u5fc3\u4e2d\u4e00\u51db\uff1b\u8fd9\u4e24\u4eba\u7684\u672c\u9886\u6bd4\u7f57\u8d85\u5389\u5bb3\u5f97 \u591a\uff0c\u767e\u5fd9\u4e2d\u5c31\u5730\u4e00\u6eda\uff0c\u4e91\u7075\u5b50\u4e00\u8dc3\u9762\u524d\uff0c\u9884\u5148\u62a2\u5230\u989c\u6d1b\u8d8b\u95ea\u7684\u65b9\u4f4d\uff0c\u4e00\u63d0\u811a\u5c31\u8e29\u4e0b\u53bb\uff01 \u3000\u3000\u5ffd\u5730\u91cc\u53ea\u89c9\u5f97\u811a\u8ddf\u7684\u6d8c\u6cc9\u7a74\u900f\u9aa8\u5947\u5bd2\uff0c\u4e91\u7075\u5b50\u8eab\u4e0d\u7531\u5df1\uff0c\u8e6c\u3001\u8e6c\u7684\u8fde\u9000\u4e09\u6b65\uff0c\u773c\u524d\u4e00 \u4eae\uff0c\u53ea\u89c1\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u5df2\u5e76\u80a9\u8d70\u5165\u5802\u4e2d\uff0c\u6851\u771f\u5a18\u7684\u90a3\u6761\u7ef8\u5e26\u4e5f\u88ab\u5510\u7ecf\u5929\u53cc\u6307\u4e00\u5939\uff0c \u201c\u526a\u201d\u53bb\u4e00\u6bb5\u3002 \u3000\u3000\u4e91\u7075\u5b50\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u56e0\u4e3a\u542c\u8bf4\u989c\u6d1b\u6b66\u529f\u4e0d\u9519\uff0c\u6545\u6b64\u7ea6\u4e86\u5a46\u5a18\u524d\u6765\u5e2e\u624b\uff0c\u51c6\u5907\u5728\u7f57 \u8d85\u8fd9\u4e00\u5e72\u4eba\u9762\u524d\u5927\u663e\u5a01\u98ce\uff0c\u90a3\u6599\u5f97\u5230\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u5374\u4f1a\u5728\u8fd9\u91cc\u51fa\u73b0\uff0c\u4e91\u7075\u5b50\u592b\u5987\u5f53\u5e74\u66fe \u5408\u6218\u51b0\u5ddd\u5929\u5973\uff0c\u4e5f\u5360\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u53c8\u66fe\u88ab\u5510\u7ecf\u5929\u7684\u5929\u5c71\u795e\u8292\u6253\u5f97\u72fc\u72c8\u800c\u9003\uff0c\u800c\u4e14\u4ed6\u53c8\u77e5\u9053\u5510\u7ecf \u5927\u662f\u5f53\u4eca\u6b66\u6797\u81f3\u5c0a\u5510\u6653\u6f9c\u7684\u513f\u5b50\uff0c\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4ed6\u4e5f\u4e0d\u6562\u4e0e\u5510\u7ecf\u5929\u76f8\u6297\uff0c\u6025\u5fd9\u8dc3\u8fc7\u4e00\u8fb9\uff0c\u50cf \u4e00\u53ea\u6597\u8d25\u516c\u9e21\u4f3c\u7684\u6697\u81ea\u8fd0\u6c14\u5fa1\u5bd2\u3002 \u3000\u3000\u7f57\u8d85\u7b49\u4eba\u90fd\u662f\u5f53\u5e74\u53bb\u8fce\u63a5\u91d1\u672c\u5df4\u74f6\u7684\u4eba\uff0c\u89c1\u8fc7\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\uff0c\u4e5f\u4e0d\u7981\u90fd\u6115\u4f4f\u4e86\u3002\u5510 \u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u7f57\u8d85\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u9f99\u4e09\u5148\u751f\u88ab\u52ab\uff0c\u53ef\u662f\u4eca\u665a\u4e4b\u4e8b\u4e48\uff1f\u201d\u7f57\u8d85\u6025\u5fd9\u8fd8 \u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u524d\uff01\u201d\u5fc3\u4e2d\u5947\u602a\u5510\u7ecf\u5929\u4f55\u4ee5\u77e5\u9053\uff1f\u83ab\u975e\u52ab\u72f1\u7684\u4eba\u662f\u4ed6\u4e0d \u6210\uff1f\u5fc3\u4e2d\u6240\u7591\uff0c\u5374\u4e0d\u6562\u5411\u5510\u7ecf\u5929\u559d\u95ee\uff0c\u5510\u7ecf\u5929\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6765\u5230\u6b64\u5904\uff0c\u5df2\u6709 \u4e24\u65f6\u8fb0\uff0c\u989c\u5148\u751f\u4e00\u76f4\u966a\u7740\u6211\u4eec\u8bf4\u8bdd\uff0c\u9664\u975e\u4ed6\u6709\u5206\u8eab\u4e4b\u672f\uff0c\u5426\u5219\u52ab\u72f1\u7684\u4eba\u5b9a\u7136\u4e0d\u662f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u4e91\u7075\u5b50\u9053\uff1a\u201c\u54af\uff0c\u90a3\u5c31\u2014\u2014\u201d\u4ed6\u6b63\u60f3\u8bf4\uff1a\u201c\u90a3\u5c31\u662f\u4f60\uff01\u201d\u521a\u8bf4\u5f97\u51e0\u4e2a\u5b57\uff0c\u5fc3\u795e\u4e00\u5206\uff0c\u5947 \u5bd2\u4e4b\u6c14\uff0c\u53c8\u5faa\u7740\u7a74\u9053\u4e0a\u4fb5\uff0c\u5510\u7ecf\u5927\u77aa\u773c\u9053\uff1a\u201c\u5c31\uff0c\u5c31\u662f\u4ec0\u4e48\uff1f\u201d\u4e91\u7075\u5b50\u4e00\u672a\u8981\u8fd0\u6c14\u5fa1\u5bd2\uff0c\u4e8c \u6765\u6015\u5510\u7ecf\u5929\u8bf4\u51fa\u5077\u770b\u5723\u65e8\u4e4b\u4e8b\uff0c\u4ed6\u539f\u6765\u5c31\u662f\u56e0\u4e3a\u6b64\u4e8b\uff0c\u800c\u6000\u7591\u662f\u5510\u7ecf\u5929\u52ab\u72f1\u7684\uff0c\u53ef\u662f\u4e00\u8bf4\u51fa \u6765\uff0c\u81ea\u5df1\u4e5f\u5927\u5931\u9762\u5b50\uff0c\u4e09\u6765\u4ed6\u4e5f\u6015\u6293\u7834\u4e86\u8138\uff0c\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u4e00\u52a8\u624b\uff0c\u81ea\u5df1\u5c31\u8981\u5148\u5403\u5927 \u4e8f\u3002\u6709\u8fd9\u4e09\u9879\u539f\u56e0\u3002\u6545\u6b64\u88ab\u5510\u7ecf\u5929\u4e00\u559d\uff0c\u4ed6\u8bdd\u5230\u53e3\u8fb9\u53c8\u541e\u4e86\u56de\u53bb\u3002 \u3000\u3000\u7f57\u8d85\u89c1\u98ce\u9a76\u8235\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u4e24\u4f4d\u4e49\u58eb\u62c5\u4fdd\uff0c\u90a3\u5c31\u5b9a\u7136\u4e0d\u662f\u989c\u5144\u4e86\uff0c\u8bf7\u6055\u521a\u624d\u9c81 \u83bd\uff0c\u7f09\u62ff\u52ab\u72f1\u7684\u7f6a\u72af\u8981\u7d27\uff0c\u6211\u4eec\u544a\u8f9e\u4e86\uff01\u201d\u989c\u6d1b\u9001\u51fa\u95e8\u5916\uff0c\u89c1\u4e91\u7075\u5b50\u4e00\u80a2\u4e00\u62d0\u7684\u8d70\u5f97\u5341\u5206\u72fc \u72c8\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u56de\u5230\u5802\u4e0a\uff0c\u5374\u89c1\u5510\u7ecf\u5929\u5fe7\u5f62\u4e8e\u8272\uff0c\u989c\u6d1b\u7b11\u9053\uff1a\u201c\u6709\u4eba\u66ff\u4ee3\u6211\u4eec\u52ab\u72f1\uff0c\u54b1\u4eec\u53ef\u7701\u4e8b\u591a \u4e86\u3002\u201d\u5510\u7ecf\u5929\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u52ab\u72f1\u7684\u7a76\u662f\u4f55\u4eba\uff1f\u798f\u5eb7\u5b89\u5e10\u4e0b\u867d\u7136\u6ca1\u6709\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u4f46\u4eca\u665a\u5b88 \u72f1\u7684\u4eba\u5fc5\u7136\u6bd4\u5bfb\u5e38\u4e25\u5bc6\u767e\u500d\uff0c\u4e91\u7075\u5b50\u592b\u5987\u53ea\u6015\u4e5f\u8981\u5728\u7262\u4e2d\u770b\u5b88\uff0c\u8fd9\u4eba\u7adf\u7136\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u5c06 \u9f99\u7075\u77eb\u52ab\u53bb\uff0c\u4e91\u7075\u5b50\u8fd9\u4e00\u5e72\u4eba\u8fde\u4ed6\u7684\u76f8\u8c8c\u90fd\u770b\u4e0d\u6e05\u695a\uff0c\u8fd9\u4eba\u7684\u6b66\u529f\u4e5f\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\u4e86\uff01\u201d\u51b0 \u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4f1a\u4e0d\u4f1a\u662f\u5510\u8001\u592a\u5a46\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u82e5\u662f\u5510\u8001\u592a\u5a46\uff0c\u4ed6\u4eec\u96be\u9053\u8fde\u7537\u5973\u90fd \u5206\u4e0d\u51fa\u6765\u5417\uff1f\u600e\u4f1a\u7591\u5230\u989c\u5144\u8eab\u4e0a\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5ffd\u9053\uff1a\u201c\u83ab\u975e\u662f\u91d1\u4e16\u9057\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u91d1\u4e16 \u9057\u867d\u8bf4\u884c\u4e8b\u602a\u8bde\uff0c\u4f46\u4e0e\u9f99\u7075\u77eb\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u4f1a\u65e0\u7aef\u7aef\u5730\u8dd1\u53bb\u52ab\u72f1\u3002\u201d\u5510\u7ecf\u5929\u77e5\u9053\u9f99\u7075 \u77eb\u5728\u897f\u85cf\u6709\u5f88\u5927\u7684\u6f5c\u52bf\u529b\uff0c\u73b0\u5728\u4e0d\u77e5\u843d\u5728\u4f55\u4eba\u624b\u4e2d\uff0c\u4e0d\u7531\u5f97\u53c8\u559c\u53c8\u5fe7\u3002\u4f17\u4eba\u8c08\u8bba\u591a\u65f6\uff0c\u90fd\u731c \u4e0d\u5230\u52ab\u72f1\u7a76\u7adf\u662f\u4f55\u65b9\u795e\u5723\uff1f \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u72f1\u4e2d\u52ab\u8d70\u5947\u7537\u5b50\uff0c\u6f20\u5916\u98ce\u4e91\u53c8\u4e00\u573a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u731c\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e09\u56de\u3000\u7f25\u7f08\u5f02\u9999\u3000\u98de\u9e3f\u5929\u9645\u8fdc\u3000\u8e0c\u8e87\u5973\u4fa0\u3000\u8d70\u9a6c\u96ea\u5c71\u9065 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u4e09\u56de \u7f25\u7f08\u5f02\u9999\u3000\u98de\u9e3f\u5929\u9645\u8fdc \u8e0c\u8e87\u5973\u4fa0\u3000\u8d70\u9a6c\u96ea\u5c71\u9065 \u3000\u3000\u4f17\u4eba\u8c08\u8bba\u591a\u65f6\uff0c\u90fd\u731c\u4e0d\u5230\u52ab\u72f1\u7684\u7a76\u662f\u4f55\u65b9\u795e\u5723\u3002\u5510\u7ecf\u5929\u4e00\u591c\u6ca1\u6709\u597d\u7761\uff0c\u601d\u6765\u60f3\u53bb\uff0c\u89c9\u5f97 \u6b64\u4e8b\u4e0d\u80fd\u4e00\u8d70\u4e86\u4e4b\uff0c\u6b63\u60f3\u7b2c\u4e8c\u65e5\u4e00\u65e9\u518d\u53bb\u62dc\u4f1a\u798f\u5eb7\u5b89\uff0c\u54ea\u77e5\u798f\u5eb7\u5b89\u7684\u4eba\u5df2\u5148\u4ed6\u800c\u5230\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u6d3e\u6765\u7684\u4e24\u4e2a\u4eba\u6b63\u662f\u5728\u4fdd\u62a4\u91d1\u672c\u5df4\u74f6\u4e4b\u5f79\u65f6\uff0c\u548c\u5510\u7ecf\u5929\u4f1a\u8fc7\u9762\u7684\u7126\u6625\u96f7\u548c\u6e38\u4e00\u9102\uff0c \u8fd9\u4e24\u4eba\u672c\u662f\u5927\u5185\u516b\u5927\u9ad8\u624b\u7684\u6b63\u526f\u5934\u9886\uff0c\u62a4\u9001\u91d1\u672c\u5df4\u74f6\u5230\u4e86\u62c9\u8428\u4e4b\u540e\uff0c\u88ab\u798f\u5eb7\u5b89\u8bf7\u51c6\u5723\u65e8\u7559\u4e86 \u4e0b\u6765\uff0c\u8944\u8d5e\u519b\u52a1\uff0c\u5730\u4f4d\u6bd4\u8fd1\u536b\u519b\u961f\u957f\u7f57\u8d85\u8fd8\u9ad8\u5f97\u591a\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5728\u5929\u521a\u62c2\u6653\u7684\u65f6\u5206\u5c31\u5230\u4e86\u989c\u5bb6\uff0c\u4e00\u89c1\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\uff0c\u4fbf\u606d\u606d\u656c\u656c\u4ed6\u8bf4\u9053\uff1a \u201c\u4e24\u4f4d\u4e49\u58eb\u6628\u65e5\u5230\u6765\uff0c\u5927\u5e05\u9002\u56e0\u5c0f\u6059\u7f20\u8eab\uff0c\u6709\u5931\u8fce\u8fd1\uff0c\u7279\u53eb\u6211\u4eec\u6765\u5411\u4e24\u4f4d\u966a\u7f6a\u3002\u201d\u5510\u7ecf\u5929\u4f55 \u7b49\u806a\u660e\uff0c\u6599\u60f3\u4ed6\u4eec\u5fc5\u662f\u6709\u6c42\u800c\u6765\uff0c\u4e0d\u52a8\u58f0\u8272\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8349\u91ce\u5339\u592b\uff0c\u600e\u6562\u60ca\u52a8\u5927\u5e05\uff1f\u4f55\u51b5 \u5927\u5e05\u65e5\u6765\u4e8b\u52a1\u6b63\u7e41\uff0c\u6211\u4eec\u66f4\u4e0d\u4fbf\u518d\u53bb\u6270\u4e86\u3002\u5927\u5e05\u8ddf\u524d\uff0c\u8bf7\u4e24\u4f4d\u4ee3\u4e3a\u9053\u8c22\uff0c\u8bf4\u6211\u4eec\u5fc3\u9886\u76db\u60c5 \u4e86\u3002\u201d\u7126\u6625\u96f7\u5fd9\u9053\uff1a\u201c\u5510\u5927\u4fa0\u4e0d\u662f\u89c1\u602a\u6211\u4eec\u5427\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5c82\u6562\u5c82\u6562\u3002\u201d\u7126\u6625\u96f7\u9053\uff1a \u201c\u8981\u662f\u5510\u5927\u4fa0\u4e0d\u89c1\u602a\u6211\u4eec\uff0c\u90a3\u5c31\u6c42\u5510\u5927\u4fa0\u8d4f\u6211\u4eec\u4e00\u53e3\u996d\u5403\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u7126\u5927\u4eba\u8a00\u91cd \u4e86\uff01\u201d\u7126\u6625\u96f7\u9053\uff1a\u201c\u6628\u665a\u52ab\u72f1\u4e4b\u4e8b\uff0c\u5510\u5927\u4fa0\u6599\u662f\u6709\u6240\u77e5\u95fb\u7684\u4e86\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u7565\u6709\u6240\u77e5\uff0c \u4e91\u7075\u5b50\u4ed6\u4eec\u6628\u665a\u5c31\u66fe\u56e0\u6b64\u4e8b\u6765\u8fc7\u3002\u201d\u7126\u6625\u96f7\u9053\uff1a\u201c\u6211\u4eec\u81ea\u6127\u65e0\u80fd\uff0c\u88ab\u98de\u8d3c\u52ab\u4e86\u91cd\u72af\uff0c\u8fde\u6765\u4eba \u7684\u76f8\u8c8c\u90fd\u77a7\u4e0d\u6e05\u695a\u3002\u5510\u5927\u4fa0\u5f53\u7136\u77e5\u9053\uff0c\u8fd9\u662f\u5723\u4e0a\u8981\u7684\u72af\u4eba\uff0c\u82e5\u7136\u8ffd\u4e0d\u56de\u6765\uff0c\u5e9c\u5185\u5b98\u5458\uff0c\u53ea\u6050 \u4e2a\u4e2a\u96be\u9003\u7f6a\u8d23\uff0c\u8fd8\u671b\u5510\u5927\u4fa0\u6307\u70b9\u8ff7\u6d25\uff0c\u9ad8\u62ac\u8d35\u624b\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e00\u542c\u53e3\u6c14\uff0c\u77e5\u9053\u81ea\u5df1\u5077\u770b\u5723\u65e8\u4e4b\u4e8b\uff0c\u4e91\u7075\u5b50\u7eb5\u4e0d\u597d\u610f\u601d\u8bf4\uff0c\u90a3\u5e08\u7237\u5b9a\u5df2\u7980\u62a5\u4e0e\u798f \u5eb7\u5b89\u77e5\u9053\u3002\u6562\u60c5\u4ed6\u4eec\u8fd8\u731c\u7591\u81ea\u5df1\u5c31\u662f\u98de\u8d3c\uff0c\u6240\u4ee5\u524d\u636e\u800c\u540e\u606d\uff0c\u7b11\u9053\uff1a\u201c\u770b\u6765\u6211\u82e5\u4e0d\u80fd\u66ff\u4f60\u4eec \u8ffd\u56de\u94a6\u72af\uff0c\u8fde\u6211\u4e5f\u8131\u4e0d\u4e86\u5173\u7cfb\u4e86\uff1f\u201d\u7126\u6625\u96f7\u9ed1\u9762\u900f\u7ea2\uff0c\u5c34\u5c2c\u966a\u7b11\u9053\uff1a\u201c\u54ea\u513f\u7684\u8bdd\uff0c\u6211\u4eec\u6709\u4e00 \u767e\u4e2a\u5934\u9885\u4e5f\u4e0d\u6562\u731c\u7591\u5510\u5927\u4fa0\u3002\u53ea\u56e0\u5510\u5927\u4fa0\u4ea4\u6e38\u5e7f\u9614\uff0c\u82e5\u6709\u7ebf\u7d22\uff0c\u4f46\u6c42\u6307\u70b9\u4e00\u4e8c\u3002\u201d\u4ed6\u795e\u8272\u8d8a \u662f\u60f6\u6050\u90a3\u5c31\u663e\u9732\u4ed6\u5185\u5fc3\u8d8a\u662f\u731c\u7591\u3002 \u3000\u3000\u5510\u7ecf\u5929\u610f\u6b32\u6253\u542c\u52ab\u72f1\u7684\u771f\u76f8\uff0c\u4e0d\u518d\u7f6e\u8fa9\uff0c\u5bf9\u4ed6\u4eec\u7684\u8bf7\u6c42\uff0c\u4ea6\u4e0d\u7f6e\u53ef\u5426\u3002\u7126\u6625\u96f7\u60f6\u6025\u4e4b \u6781\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u9f99\u8001\u4e09\u7d20\u65e0\u4ec7\u51a4\uff0c\u6211\u4ea6\u4e0d\u5fcd\u7f6e\u4ed6\u6b7b\u5730\uff0c\u4f46\u6c42\u4ed6\u80fd\u56de\u6765\u6295\u6848\uff0c\u6211\u5c06\u4ed6\u4ea4\u7ed9\u4e86 \u4e91\u7075\u5b50\uff0c\u90a3\u6211\u4fbf\u7acb\u5373\u8f9e\u5b98\u4e0d\u5e72\u3002\u563f\uff0c\u4ed6\u5230\u4e86\u4e91\u7075\u5b50\u624b\u4e2d\uff0c\u90a3\u65f6\u518d\u6709\u610f\u5916\uff0c\u6211\u4e5f\u4e0d\u5fc5\u7ba1\u5566\uff01\u201d \u8fd9\u8bdd\u7684\u610f\u601d\u662f\u4ed6\u4f46\u6c42\u80fd\u6446\u8131\u5e72\u7cfb\uff0c\u53ea\u8981\u9f99\u7075\u77eb\u4e0d\u662f\u5728\u4ed6\u770b\u7ba1\u4e4b\u4e0b\uff0c\u90a3\u4e48\u518d\u5ea6\u88ab\u52a8\uff0c\u4ed6\u4e5f\u7edd\u4e0d \u591a\u7406\u95f2\u4e8b\uff0c\u4ea6\u5373\u662f\u6697\u793a\u5510\u7ecf\u5927\u5c06\u9f99\u7075\u77eb\u9001\u56de\u4e4b\u540e\uff0c\u53ef\u4ee5\u518d\u5ea6\u52ab\u72f1\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u4e2d\u597d\u7b11\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6628\u665a\u52ab\u72f1\u4e4b\u65f6\uff0c\u7126\u5927\u4eba\u53ef\u5728\u73b0\u573a\u4e48\uff1f\u201d \u3000\u3000\u7126\u6625\u96f7\u9ed1\u8138\u900f\u7ea2\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u6628\u665a\u6b63\u662f\u6211\u4e0e\u6e38\u5144\u5f53\u503c\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u98de\u8d3c\u7eb5\u7b97\u8f7b\u529f \u7edd\u9876\uff0c\u4f46\u7262\u95e8\u6df1\u9501\uff0c\u4ed6\u5e26\u72af\u4eba\u51fa\u72f1\uff0c\u4e5f\u603b\u8be5\u542c\u5230\u58f0\u606f\u5475\uff01\u201d\u7126\u6625\u96f7\u9053\uff1a\u201c\u5c82\u6b62\u5fae\u95fb\u58f0\u606f\uff0c\u98de \u8d3c\u7b80\u76f4\u662f\u95f9\u5f97\u60ca\u5927\u52a8\u5730\u7684\u7834\u72f1\u800c\u51fa\uff01\u201d\u5510\u7ecf\u5927\u5927\u4e3a\u8be7\u5f02\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f55\u4ee5\u8fd8\u77a7\u4e0d\u6e05\u98de \u8d3c\u7684\u9762\u8c8c\u3002\u201d\u7126\u6625\u96f7\u9053\uff1a\u201c\u6628\u665a\u4e09\u66f4\u65f6\u5206\uff0c\u6211\u4eec\u7a81\u542c\u5f97\u8f70\u9686\u4e00\u58f0\u5927\u9707\uff0c\u4f46\u89c1\u4e00\u6761\u9ed1\u5f71\u631f\u7740\u9f99 \u8001\u4e09\u98de\u51fa\uff0c\u6211\u4eec\u5144\u5f1f\u8d76\u5fd9\u8ffd\u4e0a\uff0c\u5ffd\u89c9\u7cbe\u795e\u604d\u60da\uff0c\u773c\u5026\u817f\u8f6f\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u98de\u8d3c\u5c31\u9003\u5f97\u65e0\u5f71\u65e0 \u8e2a\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u5f02\u4e8b\uff1f\u98de\u8d3c\u662f\u7528\u8ff7\u9999\u4e48\uff1f\u201d\u7126\u6625\u96f7\u9053\uff1a\u201c\u5e76\u6ca1\u55c5\u5230\u4ec0\u4e48\u7279\u522b\u7684\u9999 \u5473\uff0c\u6211\u4eec\u4e5f\u65e9\u63d0\u9632\u5230\u4f1a\u6709\u4eba\u7528\u8ff7\u9999\u52ab\u72f1\uff0c\u5f53\u503c\u7684\u4eba\u90fd\u5907\u6709\u89e3\u836f\uff0c\u5c31\u662f\u6c5f\u6e56\u4e0a\u6700\u5389\u5bb3\u7684\u9e21\u9e23\u4e94 \u9f13\u8fd4\u9b42\u4e5f\u8ff7\u4e0d\u5012\u6211\u4eec\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u601d\u7591\u66f4\u751a\uff0c\u9053\uff1a\u201c\u80fd\u5e26\u6211\u4eec\u5230\u72f1\u4e2d\u770b\u770b\u4e48\uff1f\u201d\u7126\u6625\u96f7\u9053\uff1a\u201c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u201d\u5f53 \u4e0b\u7acb\u5373\u52a8\u8eab\uff0c\u5230\u8fbe\u7262\u4e2d\uff0c\u4f46\u89c1\u76d1\u7262\u90fd\u662f\u5c3a\u8bb8\u539a\u7684\u9752\u7816\u5efa\u6210\uff0c\u5341\u5206\u575a\u56fa\uff0c\u7262\u95e8\u662f\u4e00\u9053\u94c1\u95e8\uff0c\u52a0 \u4ee5\u5de8\u9501\uff0c\u5510\u7ecf\u5929\u6b63\u5728\u5bfb\u601d\uff1a\u4f3c\u6b64\u56da\u7262\uff0c\u5982\u4f55\u53ef\u4ee5\u7834\u7262\u800c\u51fa\uff1f\u8f6c\u773c\u95f4\u5230\u4e86\u9f99\u7075\u77eb\u7684\u56da\u623f\uff0c\u628a\u773c \u4e00\u770b\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u89c1\u5899\u58c1\u4e0a\u597d\u50cf\u65a7\u5c94\u4e00\u822c\u51ff\u7a7f\u4e86\u4e00\u4e2a\u4eba\u5f62\u7f3a\u53e3\uff0c\u4f9d\u7f3a\u53e3\u7684\u5f62\u72b6\u770b\u6765\uff0c \u90a3\u4eba\u7684\u8eab\u6750\u76f8\u5f53\u7c97\u5927\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u662f\u7528\u80cc\u649e\u5899\uff0c\u7834\u58c1\u800c\u5165\u7684\uff0c\u8fd9\u79cd\u6b66\u529f\u786e\u662f\u9a87\u4eba\u542c\u95fb\u3002\u4f46\u6700 \u4f7f\u5510\u7ecf\u5929\u5947\u5f02\u7684\u8fd8\u4e0d\u662f\u8fd9\u79cd\u6b66\u529f\uff0c\u800c\u662f\u6628\u665a\u5f53\u503c\u7684\u72f1\u5352\uff0c\u5728\u98de\u8d3c\u7834\u58c1\u800c\u5165\u7684\u8fd9\u4e00\u5239\u90a3\uff0c\u4e2a\u4e2a \u90fd\u89c9\u5fc3\u795e\u604d\u60da\uff0c\u5bf9\u98de\u8d3c\u7684\u4f53\u6001\uff0c\u4eba\u8a00\u4eba\u6b8a\uff0c\u6709\u7684\u8bf4\u80a5\uff0c\u6709\u7684\u8bf4\u7626\uff0c\u6709\u7684\u8bf4\u9ad8\uff0c\u6709\u7684\u8bf4\u77ee\uff0c\u7adf \u8fde\u98de\u8d3c\u7684\u8eab\u6750\u9ad8\u77ee\u90fd\u5f04\u5f97\u7cca\u91cc\u7cca\u6d82\uff01 \u3000\u3000\u56de\u5934\u4e00\u77a5\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u4e00\u6d3e\u832b\u7136\u7684\u795e\u6001\uff0c\u7adf\u7136\u4e5f\u4f3c\u5fc3\u795e\u604d\u60da\u7684\u6a21\u6837\uff0c\u5510\u7ecf\u5927\u5927\u5403\u60ca\uff0c \u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u6765\u5230\u56da\u7262\u4e4b\u540e\uff0c\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5ffd\u4f3c\u970d\u7136\u60ca \u9192\uff0c\u53eb\u9053\uff1a\u201c\u8d76\u5feb\u53bb\u6311\u9009\u4e24\u5339\u6700\u597d\u7684\u9a8f\u9a6c\uff0c\u54b1\u4eec\u7acb\u5373\u5f80\u897f\u8ffd\u53bb\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u5bdf\u89c9\u5230\u4ec0 \u4e48\u4e86\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u8bd5\u9759\u5750\u89c2\u5fc3\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u95fb\u4e00\u95fb\u770b\u3002\u201d\u5510\u7ecf\u5929\u4f9d\u8a00\u8fd0\u529f\uff0c\u5929\u5c71\u6d3e \u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u6700\u4e3a\u5947\u5999\uff0c\u5fc3\u4e2d\u7eb5\u6709\u5343\u822c\u7591\u8651\uff0c\u76d8\u819d\u4e00\u5750\uff0c\u7acb\u523b\u4fbf\u5982\u6b62\u6c34\uff0c\u7531\u865a\u81f3\u660e\u3002\u5510\u7ecf\u5929 \u9759\u5750\u4e00\u9635\uff0c\u4f46\u89c9\u6709\u4e00\u7f15\u6781\u6de1\u6781\u6de1\u7684\u5e7d\u9999\uff0c\u51b2\u5165\u9f3b\u89c2\uff0c\u6559\u4eba\u6709\u8bf4\u4e0d\u51fa\u7684\u751c\u7545\uff01\u8fd9\u79cd\u9999\u5473\uff0c\u95fb\u6240 \u672a\u95fb\uff0c\u800c\u4e14\u8981\u4e0d\u662f\u5fc3\u65e0\u6742\u5ff5\uff0c\u4e13\u5fc3\u4e00\u6ce8\uff0c\u4e00\u70b9\u4e5f\u5bdf\u89c9\u4e0d\u51fa\uff0c\u771f\u662f\u8be1\u5f02\u7edd\u4f26\u3002 \u3000\u3000\u7126\u6625\u96f7\u6d3e\u4eba\u53bb\u6311\u9009\u7684\u4e24\u5339\u9a8f\u9a6c\uff0c\u8fd9\u65f6\u4e1a\u5df2\u9001\u5230\uff0c\u5510\u7ecf\u5929\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u9999 \u5473\uff1f\u201d\u7126\u6625\u96f7\u7b49\u83ab\u540d\u5176\u5999\uff0c\u9053\uff1a\u201c\u54ea\u6709\u4ec0\u4e48\u9999\u5473\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e0d\u8981\u591a\u95ee\uff0c\u8d76\u5feb\u897f \u884c\uff01\u201d\u773c\u5149\u4e2d\u4e5f\u662f\u9732\u51fa\u4e00\u6d3e\u5947\u5f02\u7684\u795e\u60c5\uff0c\u5510\u7ecf\u5929\u5fc3\u77e5\u6709\u6545\uff0c\u6025\u4e0e\u51b0\u5ddd\u5929\u5973\u98de\u9a6c\u51fa\u57ce\uff0c\u90a3\u4e24\u5339 \u9a6c\u662f\u5927\u5b9b\u540d\u9a6c\uff0c\u8dd1\u5f97\u6709\u5982\u98ce\u9a70\u7535\u63a3\uff0c\u65e5\u672a\u5f53\u4e2d\uff0c\u5df2\u8fdb\u5165\u4e86\u90ca\u5916\u83bd\u83bd\u7684\u8349\u539f\u3002 \u3000\u3000\u897f\u85cf\u5730\u5e7f\u4eba\u7a00\uff0c\u5e02\u9547\u6751\u843d\uff0c\u591a\u96c6\u4e2d\u5728\u62c9\u8428\u4ee5\u4e1c\u3002\u62c9\u8428\u4ee5\u897f\uff0c\u4e43\u662f\u8352\u539f\u548c\u6c99\u6f20\u5730\u5e26\uff0c\u5f80\u5f80 \u6570\u5341\u91cc\u4e0d\u89c1\u4eba\u5bb6\uff0c\u8fd9\u65f6\u867d\u7136\u5df2\u662f\u6c5f\u5357\u7684\u66ae\u6625\u65f6\u8282\uff0c\u897f\u85cf\u5730\u65b9\u8fd8\u662f\u79ef\u96ea\u904d\u91ce\uff0c\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929 \u5973\u7b56\u9a6c\u5954\u9a70\uff0c\u4f46\u89c1\u83bd\u83bd\u8352\u539f\uff0c\u5b9b\u5982\u4e00\u7247\u7409\u7483\u4e16\u754c\u3002\u5510\u7ecf\u5929\u7591\u60d1\u66f4\u751a\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u52ab\u72f1\u7684\u98de \u8d3c\u662f\u4ece\u6f20\u5916\u6765\u7684\u4e0d\u6210\uff0c\u8981\u4e0d\u7136\u51b0\u5ddd\u5929\u4e3a\u4ec0\u4e48\u5e26\u6211\u5411\u8fd9\u4e2a\u65b9\u5411\u8ffd\u8e2a\uff1f\u5979\u53c8\u51ed\u4ec0\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u52d2\u9a6c\u7ee5\uff0c\u56de\u5934\u7b11\u9053\uff1a\u201c\u4f60\u6240\u6599\u4e0d\u5dee\uff0c\u9f99\u7075\u77eb\u88ab\u52ab\uff0c\u53ea\u6050\u8fd8\u8981\u751f\u51fa\u8bb8\u591a\u610f\u60f3\u4e0d \u5230\u7684\u4e8b\u3002\u201d\u5510\u7ecf\u5929\u4e0e\u5979\u5e76\u9a6c\u540c\u884c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u95fb\u5230\u4e86\u7262 \u72f1\u91cc\u90a3\u5947\u602a\u7684\u9999\u5473\u5417\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u662f\u5440\uff0c\u90a3\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u975e\u5170\u975e\u83ca\uff0c\u771f\u662f\u5947\u602a\u900f\u4e86\uff0c\u6211 \u8981\u5728\u9ed8\u8fd0\u7384\u529f\u4e4b\u540e\uff0c\u624d\u5bdf\u89c9\u51fa\u6765\uff0c\u4f60\u600e\u4e48\u4e00\u5230\u72f1\u4e2d\u5c31\u95fb\u5230\u4e86\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u6211 \u81ea\u5c0f\u5c45\u4f4f\u7684\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u5c31\u6709\u8fd9\u79cd\u82b1\u9999\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u82b1\u9999\uff1f\u600e\u7684\u5982\u6b64\u5947\u7279\uff0c\u80fd\u4ee4 \u4eba\u5fc3\u795e\u604d\u60da\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u82b1\u53eb\u505a\u963f\u4fee\u7f57\u82b1\u3002\u963f\u4fee\u7f57\u662f\u68b5\u8bed\u4e2d\u9b54\u9b3c\u7684\u610f\u601d\u3002\u6240\u4ee5\u53c8\u540d\u9b54\u9b3c\u82b1\uff01\u201d \u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u5982\u6b64\u602a\u82b1\uff0c\u786e\u662f\u540d\u7b26\u5176\u5b9e\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u82b1\u7684\u82b1\u9999\u867d\u6de1\uff0c\u4f46\u5374\u80fd\u4ee5\u4e45 \u4e0d\u6563\u3002\u5728\u82b1\u5f00\u4e4b\u65f6\uff0c\u4eba\u4e00\u55c5\u5230\u8fd9\u79cd\u9999\u6c14\uff0c\u5c31\u50cf\u9189\u4e86\u4e00\u822c\uff0c\u4f46\u89c9\u5fc3\u795e\u8ff7\u4e71\uff0c\u773c\u5026\u817f\u9178\uff0c\u9b54\u9b3c\u82b1 \u7684\u5f97\u540d\uff0c\u60f3\u662f\u7531\u6b64\u800c\u6765\uff0c\u8fd9\u79cd\u82b1\u53ea\u5728\u6781\u9ad8\u6781\u9ad8\u7684\u51b0\u5cf0\u4e4b\u4e0a\u80fd\u751f\u957f\uff0c\u542c\u8bf4\u9664\u4e86\u6211\u6240\u5c45\u4f4f\u7684\u5ff5\u9752 \u5510\u53e4\u62c9\u5c71\u4e4b\u5916\uff0c\u5c31\u53ea\u6709\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u9ad8\u5cf0\u4e4b\u4e0a\u624d\u6709\u3002\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u9664\u4e86\u6211\u4eec\u4e00\u5bb6\u4eba\u5916\uff0c\u5e76\u65e0 \u5176\u4ed6\u6b66\u529f\u7279\u5f02\u7684\u4eba\u9690\u5c45\uff0c\u6240\u4ee5\u6211\u731c\u60f3\u8fd9\u52ab\u72f1\u7684\u98de\u8d3c\uff0c\u5b9a\u7136\u662f\u4ece\u559c\u9a6c\u62c9\u96c5\u5c71\u8fd9\u8fb9\u6765\u7684\u4e86\u3002\u201d\u559c \u9a6c\u62c9\u96c5\u5c71\u5728\u4e2d\u56fd\u548c\u5c3c\u6cca\u5c14\u8fb9\u5883\uff0c\u5510\u7ecf\u5929\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u96be\u9053\u8fd9\u98de\u8d3c\u662f\u4ece\u56fd\u5916\u6765\u7684\uff1f\u770b\u4ed6\u90a3\u7834\u58c1 \u7684\u529f\u592b\uff0c\u90a3\u7edd\u4e0d\u662f\u4e2d\u571f\u7684\u6b66\u529f\u3002\u201d\u51b0\u5ddd\u5927\u5973\u9053\uff1a\u201c\u6211\u4e5f\u662f\u5982\u6b64\u731c\u60f3\uff0c\u5440\uff0c\u82e5\u662f\u4ece\u5c3c\u6cca\u5c14\u6765 \u7684\uff0c\u53ea\u6015\u4e0e\u6211\u4e5f\u6709\u5173\u8fde\u3002\u5c31\u7b97\u4e0d\u662f\u4e3a\u4e86\u9f99\u7075\u77eb\uff0c\u6211\u4e5f\u662f\u8981\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u7684\u4e86\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u60f3\u8d77\u5c3c\u6cca\u5c14\u66b4\u541b\u610f\u6b32\u5411\u81ea\u5df1\u8feb\u5a5a\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u95f7\u95f7\u4e0d\u4e50\uff0c\u5510\u7ecf\u5929\u4e00\u8def\u548c\u5979\u8bf4\u7b11\u89e3 \u95f7\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c1\u96ea\u5730\u6709\u4e00\u70b9\u8840\u8ff9\uff0c\u4f46\u5374\u53c8\u6ca1\u6709\u8db3\u5370\uff0c\u8840\u8ff9\u6e10\u6765\u6e10\u5bc6\uff0c\u597d\u4f3c\u4e24\u884c\u73e0\u4e32\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u8840\u8ff9\u662f\u600e\u4e48\u6765\u7684\uff1f\u82e5\u662f\u4eba\u8840\uff0c\u9664\u975e\u4ed6\u6709\u8e0f\u96ea\u65e0\u75d5\u7684\u529f\u592b\uff0c\u4f46\u82e5 \u6709\u90a3\u6837\u597d\u7684\u529f\u592b\uff0c\u53c8\u600e\u80fd\u8f7b\u6613\u88ab\u4eba\u6253\u4f24\uff1f\u201d \u3000\u3000\u4e24\u4eba\u6025\u5fd9\u8ddf\u7740\u90a3\u4e24\u884c\u8840\u8ff9\u8ffd\u53bb\uff0c\u8d70\u4e0d\u591a\u4e45\uff0c\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u770b\uff01\u201d\uff0c\u53ea\u89c1\u96ea\u5730\u4e0a\u6709\u4e24\u5339 \u50f5\u6bd9\u4e86\u7684\u9a6c\uff0c\u9a6c\u978d\u88ab\u8fdc\u8fdc\u7684\u629b\u5728\u53e6\u4e00\u8fb9\uff01\u770b\u6765\u4e43\u662f\u7ecf\u8fc7\u6253\u6597\uff0c\u4e0d\u662f\u7a81\u7136\u51bb\u6b7b\u7684\u3002\u6025\u5fd9\u8d70\u4e0a\u53bb \u770b\uff0c\u53ea\u89c1\u90a3\u5339\u9a6c\u7684\u56db\u4e2a\u8e44\u5b50\u90fd\u88ab\u524a\u53bb\uff0c\u904d\u5bfb\u4e0d\u83b7\uff0c\u60f3\u662f\u88ab\u79ef\u96ea\u6240\u8986\u76d6\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u5947\u602a\u4e4b \u6781\uff0c\u82e5\u7136\u662f\u8fd9\u4e24\u5339\u9a6c\u53d7\u4f24\u6240\u6d41\u7684\u8840\uff0c\u96ea\u5730\u4e0a\u53c8\u4f55\u4ee5\u6ca1\u6709\u9a6c\u8e44\u7684\u75d5\u8ff9\uff1f\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u4e0b\u9a6c \u67e5\u770b\uff0c\u5728\u6b7b\u9a6c\u7684\u5468\u56f4\uff0c\u5ffd\u7136\u53d1\u89c9\u6de1\u6de1\u7684\u8db3\u5370\uff0c\u597d\u50cf\u5e76\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\uff0c\u5176\u4e2d\u6709\u4e00\u5bf9\u8db3\u5370\u7279\u522b\u77ed \u5c0f\uff0c\u5510\u7ecf\u5929\u53eb\u51b0\u5ddd\u5929\u5973\u5c06\u5f13\u978b\u5370\u4e0a\u53bb\uff0c\u4e0e\u90a3\u8db3\u5370\u7684\u5927\u5c0f\u4e5f\u5dee\u4e0d\u591a\uff0c\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u5b9a\u662f\u5973\u4eba \u7684\u8db3\u5370\u3002\u201d\u518d\u770b\u770b\u90a3\u5012\u6bd9\u96ea\u5730\u7684\u4e24\u5339\u9a6c\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u8fd9\u8db3\u5370\u662f\u5510\u8001\u592a\u5a46\u7684\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4e24\u5339\u9a6c\u6bd4\u54b1\u4eec\u7684\u9a6c\u77ee\u5c0f\u5f97\u591a\uff0c\u4f46\u9aa8 \u80f3\u5f3a\u5065\uff0c\u80fd\u5728\u8fd9\u6837\u7684\u8352\u539f\u5954\u8dd1\uff0c\u5f53\u7136\u4e0d\u662f\u5bfb\u5e38\u7684\u5750\u9a91\u3002\u8fd9\u662f\u5ddd\u897f\u6240\u6240\u4ea7\u7684\u540d\u9a6c\uff01\u201d\u4e2d\u56fd\u7684\u540d \u9a6c\uff0c\u9664\u4e86\u897f\u57df\u5927\u5b9b\u6240\u4ea7\u7684\u4e4b\u5916\uff0c\u5c31\u4ee5\u5ddd\u897f\u6240\u4ea7\u6700\u4e3a\u8457\u540d\uff0c\u80fd\u8010\u957f\u9014\u5954\u8dd1\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u5510\u8001\u592a\u5a46\u6b63\u662f\u4ece\u5ddd\u897f\u6765\u7684\uff0c\u4f46\u8fd9\u513f\u6709\u4e24\u5339\u9a6c\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u662f\u8c01\uff1f\u54a6\uff0c\u96be\u9053\u6628\u665a\u52ab\u72f1\u7684\u662f \u5979\uff1f\u8fd9\u600e\u4e48\u4f1a\u5440\uff1f\u201d\u5510\u7ecf\u5929\u4e5f\u6709\u70b9\u6000\u7591\u52ab\u72f1\u7684\u662f\u5510\u8001\u592a\u5a46\u4e86\uff0c\u4f46\u518d\u60f3\u4e00\u60f3\uff0c\u5510\u8d5b\u82b1\u5e74\u8001\u4f53 \u8870\uff0c\u54ea\u6709\u8fd9\u79cd\u7834\u58c1\u800c\u5165\u7684\u529f\u592b\uff1f\u800c\u4e14\u72f1\u5352\u4eec\u6240\u8bf4\u7684\u98de\u8d3c\u4f53\u6001\uff0c\u867d\u7136\u4eba\u8a00\u4eba\u6b8a\uff0c\u4f46\u5374\u5e76\u65e0\u4e00\u4eba \u8bf4\u50cf\u5973\u5b50\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u800c\u4e14\u4e3a\u4ec0\u4e48\u7a81\u7136\u5230\u8fd9\u91cc\u624d\u73b0\u8db3\u5370\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u602a\u5f02\u6781 \u591a\uff0c\u6211\u4eec\u8fd8\u662f\u518d\u5f80\u524d\u9762\u77a7\u53bb\u3002\u8ddf\u7740\u90a3\u4e9b\u51cc\u4e71\u7684\u8db3\u5370\u518d\u8d70\u4e00\u4f1a\uff0c\u53ea\u89c1\u5728\u96ea\u5730\u4e0a\u9686\u8d77\u7684\u4e00\u4e2a\u5c0f\u961c \u4e0b\u9762\uff0c\u53c8\u6709\u6dcb\u6d12\u7684\u8840\u8ff9\uff0c\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u90a3\u662f\u4e00\u4e2a\u4eba\u3002\u201d\u79ef\u538b\u96ea\u63a9\u76d6\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u53ea\u9732\u51fa\u534a \u8fb9\u5934\u9762\uff0c\u4e24\u4eba\u4e0b\u9a6c\u6025\u5fd9\u5c06\u79ef\u96ea\u62e8\u5f00\uff0c\u767b\u65f6\u60ca\u5f97\u5446\u4e86\uff0c\u539f\u6765\u8fd9\u4eba\u6b63\u662f\u5510\u8d5b\u82b1\u7684\u4f84\u513f\u5510\u7aef\u3002\u53ea\u89c1 \u4ed6\u8863\u88f3\u7834\u88c2\uff0c\u80a9\u4e0a\u6709\u4e00\u4e2a\u8840\u7ea2\u7684\u638c\u5370\uff0c\u51bb\u5f97\u53d1\u7d2b\uff0c\u88ab\u6307\u7532\u6390\u7834\u7684\u5730\u65b9\uff0c\u5c31\u50cf\u5200\u75d5\u4e00\u6837\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5fc3\u5934\u8fd8\u6709\u70b9\u6696\uff01\u5feb\u62ff\u4f60\u90a3\u4e13\u89e3\u5947\u5bd2\u4e4b\u836f\u7684\u9633\u548c\u4e38\u6765\u3002\u201d\u5510\u7ecf\u5929\u64ac\u5f00\u5510\u7aef\u7684 \u7259\u9f7f\uff0c\u5c06\u4e24\u7c92\u4e38\u836f\u548c\u9152\u704c\u4eba\u4ed6\u7684\u53e3\u4e2d\uff0c\u53c8\u4ee5\u672c\u8eab\u529f\u529b\u52a9\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u4f46\u51bb\u50f5\u5df2\u4e45\uff0c\u54ea\u80fd\u5373\u65f6 \u82cf\u9192\u3002 \u3000\u3000\u51b0\u4e2d\u5929\u5973\u79fb\u76ee\u56db\u770b\uff0c\u5ffd\u5730\u4e00\u58f0\u60ca\u547c\uff0c\u53eb\u9053\uff1a\u201c\u7ecf\u5929\uff0c\u4f60\u770b\uff01\u201d\u53ea\u89c1\u4e00\u5757\u5ca9\u77f3\u4e0a\u6709\u4e00\u9053\u9c9c \u660e\u7684\u62d0\u5370\uff0c\u77f3\u5c51\u6ee1\u5730\uff0c\u770b\u5f97\u51fa\u662f\u6709\u4eba\u5728\u6b64\u5267\u6597\uff0c\u90a3\u94c1\u62d0\u5370\u662f\u5931\u624b\u6253\u5728\u77f3\u4e0a\u7684\u3002\u5510\u7ecf\u5929\u4e00\u770b\u4e4b \u4e0b\uff0c\u4e5f\u662f\u8be7\u5f02\u4e4b\u6781\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u662f\u91d1\u4e16\u9057\u7684\u94c1\u62d0\uff01\u201d\u91d1\u4e16\u9057\u4e3a\u4f55\u6765\u5230\u8fd9\u513f\uff1f\u7b97\u6765\u4ed6\u7684\u6027 \u547d\u4e0d\u591f\u4e00\u6708\u4e86\uff0c\u96be\u9053\u662f\u56e0\u6b64\u800c\u53c8\u75af\u72c2\uff1f\u5510\u7aef\u662f\u4e0d\u662f\u4ed6\u6253\u4f24\u7684\uff1f\u52ab\u72f1\u4e4b\u4e8b\u4e0e\u4ed6\u6709\u5426\u5173\u8fde\uff1f\u8fd9\u79cd \u79cd\u7591\u56e2\u90fd\u662f\u96be\u4ee5\u89e3\u91ca\uff01\u53ea\u6709\u76fc\u671b\u80fd\u591f\u5c06\u5510\u7aef\u6551\u6d3b\uff0c\u6216\u8005\u53ef\u4ee5\u7a0d\u77e5\u7aef\u502a\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5440\uff0c\u4ed6\u4e0d\u53bb\u5929\u5c71\uff0c\u53cd\u800c\u5411\u8fd9\u8fb9\u8d70\uff0c\u90a3\u5c82\u4e0d\u662f\u80cc\u9053\u800c\u9a70\uff1f\u54b1\u4eec\u5c31\u662f \u5bfb\u7740\u4ed6\uff0c\u4e5f\u96be\u4ee5\u89e3\u6551\u4e86\u3002\u201d\u5510\u7ecf\u5929\u9eef\u7136\u4e0d\u8bed\uff0c\u7528\u5fc3\u66ff\u5510\u7aef\u63a8\u8840\u8fc7\u5bab\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u624d\u542c\u5f97\u5510\u7aef \u5589\u5934\u54af\u54af\u4f5c\u54cd\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6210\u5566\uff01\u201d\u897f\u85cf\u7684\u957f\u9014\u65c5\u5ba2\uff0c\u591a\u5907\u6709\u597d\u9152\u5728\u8def\u4e0a\u5fa1\u5bd2\uff0c\u5510\u7ecf\u5929\u7684\u9a6c\u80cc\u4e5f\u6709\u4e00 \u4e2a\u88c5\u6ee1\u9a6c\u5976\u9152\u7684\u76ae\u888b\uff0c\u5510\u7ecf\u5929\u628a\u9152\u5f90\u5f90\u5012\u5165\u5510\u7aef\u53e3\u4e2d\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5510\u7aef\u7cbe\u529b\u6e10\u6e10\u6062\u590d\uff0c \u5f20\u5f00\u773c\u775b\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u662f\u4f60\uff01\u6211\u4e0d\u662f\u5728\u505a\u68a6\u5427\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fae\u7b11\u9053\uff1a\u201c\u6696\u548c\u4e86\u4e00\u70b9\u5427\uff1f\u4f60\u53d7\u7684\u53ea\u662f\u5916\u4f24\uff0c\u53ef\u4ee5\u653e\u5fc3\u3002\u8fd9\u4f4d\u662f\u5929\u5c71\u638c\u95e8\u4eba\u5510 \u6653\u6f9c\u7684\u513f\u5b50\u5510\u7ecf\u5929\u3002\u201d\u5510\u7aef\u4e00\u6d3e\u8ff7\u60af\u7684\u795e\u8272\uff0c\u671b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u6709\u6c14\u6ca1\u529b\u7684\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4eec \u5566\u3002\u4f73\u59d1\u5a18\uff0c\u8fd9\u662f\u4f60\u7b2c\u4e8c\u6b21\u642d\u6551\u6211\u4eec\u4e86\uff0c\u771f\u4e0d\u77e5\u8be5\u600e\u6837\u5411\u4f60\u9053\u8c22\u624d\u597d\u3002\u201d\u8981\u77e5\u5510\u7aef\u5bf9\u51b0\u5ddd\u5929 \u5973\u4e00\u5411\u503e\u5fc3\uff0c\u5728\u5ddd\u897f\u4e4b\u65f6\uff0c\u51b0\u5ddd\u5929\u5973\u4e3a\u4e86\u4fdd\u62a4\u5510\u8001\u592a\u5a46\uff0c\u66fe\u5728\u4ed6\u5bb6\u4f4f\u8fc7\u51e0\u5929\uff0c\u5510\u7aef\u5c31\u4e00\u76f4\u60f3 \u6cd5\u63a5\u8fd1\u51b0\u5ddd\u5929\u5973\uff0c\u53ea\u56e0\u81ea\u60ed\u5f62\u79fd\uff0c\u59cb\u7ec8\u4e0d\u6562\u8868\u9732\u5fc3\u4e8b\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u59d1\u59d1\u5462\uff1f\u201d\u5510\u7aef\u60ca \u9053\uff1a\u201c\u4f60\u6ca1\u89c1\u7740\u5979\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u91d1\u4e16\u9057\u53c8\u5411\u4f60\u4eec\u5bfb\u8845\u4e86\uff1f\u5509\u3001\u4e0a \u6b21\u4ed6\u5728\u4f60\u5bb6\u95f9\u4e8b\uff0c\u6211\u4e5f\u5f88\u89c9\u5185\u759a\u4e8e\u5fc3\u3002\u201d\u51b0\u5ddd\u5929\u5973\u8fd8\u4ee5\u4e3a\u662f\u91d1\u4e16\u9057\u5c06\u4ed6\u5f04\u4f24\uff0c\u5fc3\u4e2d\u60f4\u60f4\u4e0d \u5b89\u3002\u54ea\u77e5\u5510\u7aef\u53cc\u773c\u4e00\u5f20\uff0c\u5374\u6025\u4e0d\u53ef\u5f85\u5730\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u91d1\u4e16\u9057\u5230\u8fc7\u8fd9\uff1f\u4f60\u78b0\u5230\u4ed6\u4e86\uff1f\u201d\u5510 \u5bb6\u59d1\u4f84\uff0c\u4ee5\u5f80\u5bf9\u91d1\u4e16\u9057\u6068\u4e4b\u5207\u9aa8\uff0c\u4e00\u63d0\u8d77\u91d1\u4e16\u9057\uff0c\u5fc5\u7136\u662f\u201c\u75af\u4e10\u201d\uff0c\u201c\u6bd2\u4e10\u201d\u7684\u9a82\u4e2a\u4e0d\u4f11\uff0c \u800c\u4eca\u5374\u76f4\u547c\u201c\u91d1\u4e16\u9057\u201d\u7684\u540d\u5b57\uff0c\u8bed\u6c14\u4e2d\uff0c\u4e5f\u6ca1\u6709\u534a\u70b9\u4ec7\u6068\uff0c\u51b0\u5ddd\u5929\u5973\u6697\u6697\u79f0\u5947\uff0c\u6307\u7740\u91d1\u4e16\u9057 \u5728\u5ca9\u77f3\u4e4b\u4e0a\u7559\u4e0b\u7684\u62d0\u5370\uff0c\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u4e0d\u662f\u4ed6\u4f7f\u7684\u94c1\u62d0\uff1f\u201d\u201d \u3000\u3000\u5510\u7aef\u60ca\u9053\uff1a\u201c\u5440\uff0c\u6253\u5f97\u8fd9\u6837\u6fc0\u70c8\uff0c\u4f46\u613f\u4ed6\u80fd\u5e2e\u6211\u59d1\u59d1\u6253\u8d25\u90a3\u5429\u80e1\u50e7\uff01\u201d\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a \u201c\u4ec0\u4e48\uff0c\u91d1\u4e16\u9057\u5e2e\u4f60\u7684\u59d1\u59d1\uff1f\u80e1\u50e7\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5510\u7aef\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8981\u4e0d\u662f\u91d1\u4e16\u9057\uff0c\u6211\u65e9\u5df2 \u4e27\u547d\u5728\u80e1\u50e7\u4e4b\u624b\u4e86\u3002\u90a3\u80e1\u50e7\u5c31\u662f\u52ab\u8d70\u6211\u5e08\u53d4\u7684\u4eba\uff01\u201d\u9f99\u7075\u77eb\u81ea\u5e7c\u53d7\u5510\u8d5b\u82b1\u6536\u517b\uff0c\u89c6\u540c\u4eb2\u5b50\uff0c \u4f46\u9f99\u7075\u77eb\u7684\u6280\u827a\u5219\u662f\u5510\u8d5b\u82b1\u7684\u7236\u4eb2\u5510\u4e8c\u5148\u751f\u6240\u6388\uff0c\u4ed6\u5e74\u7eaa\u53c8\u6bd4\u5510\u7aef\u5927\u4e86\u5c06\u8fd1\u4e8c\u5341\u5e74\uff0c\u662f\u4ee5\u5510 \u7aef\u5c0a\u79f0\u4ed6\u505a\u5e08\u53d4\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8d8a\u53d1\u60ca\u5947\uff0c\u9053\uff1a\u201c\u539f\u6765\u52ab\u72f1\u7684\u771f\u662f\u80e1\u50e7\uff0c\u4f60\u4eec\u7adf\u5728\u6b64\u5730\u78b0\u5230\u4ed6\u4e86\uff0c\u600e\u4e48\u4e00\u8def\u4e0a \u4e0d\u89c1\u9a6c\u8e44\u4eba\u8ff9\uff1f\u201d \u3000\u3000\u5510\u7aef\u53c8\u559d\u4e86\u51e0\u65e5\u9a6c\u5976\u9152\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0a\u6b21\u4f60\u5230\u5ddd\u897f\uff0c\u591a\u8c22\u4f60\u5c06\u6211\u5e08\u53d4\u7684\u5669\u8017\u544a\u77e5\uff0c\u6211 \u59d1\u59d1\u672c\u60f3\u9a6c\u4e0a\u5c31\u53bb\uff0c\u4f46\u5979\u5230\u5e95\u662f\u8870\u8001\u4e86\uff0c\u4e2d\u4e86\u91d1\u4e16\u9057\u7684\u6697\u5668\uff0c\u51e0\u4e4e\u5c06\u517b\u534a\u5e74\uff0c\u624d\u5f97\u6062\u590d\u5982 \u521d\u3002\u6211\u4eec\u662f\u53bb\u5e74\u4e2d\u79cb\u4e4b\u540e\u624d\u52a8\u8eab\u7684\uff0c\u5230\u62c9\u8428\u4e0d\u8fc7\u5341\u5929\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u539f\u6765\u4f60\u4eec\u65e9\u5df2\u5230\u4e86\uff0c \u6700\u521d\u6211\u8fd8\u4ee5\u4e3a\u662f\u4f60\u59d1\u59d1\u52ab\u7684\u72f1\u5462\uff01\u201d\u5510\u7aef\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u59d1\u59d1\u662f\u60f3\u52ab\u72f1\u3002\u5979\u51c6\u5907\u4e86\u8bb8\u591a\u5929\uff0c \u63a2\u6e05\u695a\u4e86\u72f1\u4e2d\u7684\u60c5\u51b5\uff0c\u9884\u5148\u5728\u57ce\u95e8\u5916\u85cf\u597d\u4e24\u5339\u5ddd\u9a6c\uff0c\u51c6\u5907\u5e08\u53d4\u4e00\u6551\u51fa\u57ce\uff0c\u5c31\u7acb\u523b\u98de\u9a6c\u9003\u8d70\uff0c \u6211\u4eec\u7ea6\u597d\u4e86\u5728\u6628\u665a\u4e8c\u66f4\u65f6\u5019\u52ab\u72f1\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e00\u7b97\u65f6\u95f4\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u6b63\u662f\u80e1\u50e7\u52ab\u72f1\u7684\u65f6\u523b\uff1f\u201d\u5510\u7aef\u9053\uff1a\u201c\u662f\u5475\uff01\u6211\u548c\u59d1\u59d1\u4e8c\u66f4 \u65f6\u5206\u5230\u4e86\u7262\u72f1\u5916\u9762\uff0c\u8fd8\u672a\u8dc3\u4e0a\u9ad8\u5899\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u4eba\u58f0\u5608\u6742\uff0c\u811a\u6b65\u7eb7\u4e71\u3002\u59d1\u59d1\u6599\u5230\u5fc5\u662f\u53d1\u751f\u4e86 \u4ec0\u4e48\u610f\u5916\u7684\u4e8b\u60c5\uff0c\u548c\u6211\u8eb2\u5728\u5899\u811a\uff0c\u4e0d\u4e00\u4f1a\u5c31\u89c1\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u80e1\u50e7\uff0c\u631f\u7740\u4e00\u4e2a\u4eba\u98de\u51fa\u9ad8\u5899\uff0c \u59d1\u59d1\u773c\u5229\uff0c\u4e00\u773c\u77a5\u53bb\uff0c\u5c31\u77a7\u51fa\u90a3\u662f\u5e08\u53d4\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u7075\u77eb\u3001\u7075\u77eb\uff01\u5374\u4e0d\u542c\u89c1\u5e08\u53d4\u56de\u7b54\uff0c\u59d1\u59d1 \u6025\u5fd9\u8ffd\u8d76\uff0c\u4f9d\u7167\u6c5f\u6e56\u7684\u89c4\u77e9\uff0c\u548c\u90a3\u80e1\u50e7\u6253\u8bdd\uff0c\u8bf4\u660e\u5927\u5bb6\u90fd\u662f\u6765\u52ab\u72f1\u7684\u4eba\uff0c\u95ee\u4ed6\u662f\u54ea\u6761\u7ebf\u4e0a\u7684 \u670b\u53cb\uff0c\u4e0d\u77e5\u662f\u90a3\u80e1\u50e7\u542c\u4e0d\u61c2\u6211\u4eec\u7684\u8bdd\u8fd8\u662f\u6709\u610f\u4e0d\u7406\uff0c\u7adf\u662f\u6beb\u4e0d\u7406\u776c\u6211\u4eec\uff0c\u4e00\u80a1\u52b2\u5730\u5f80\u524d\u75be \u8dd1\u3002\u8fd9\u80e1\u50e7\u8f7b\u529f\u5353\u7edd\uff0c\u6211\u4eec\u59d1\u4f84\u7a7a\u624b\u5140\u662f\u8ffd\u4ed6\u4e0d\u4e0a\u3002 \u3000\u3000\u201c\u597d\u5728\u6211\u4eec\u9884\u5148\u5728\u57ce\u95e8\u5916\u85cf\u597d\u4e24\u5339\u9a6c\uff0c\u51fa\u4e86\u57ce\u95e8\uff0c\u53ea\u89c1\u90a3\u80e1\u50e7\u4e5f\u9a91\u4e0a\u4e86\u9a6c\uff0c\u9f99\u5e08\u53d4\u7ed9\u4ed6 \u6309\u5728\u9a6c\u80cc\u4e0a\u3002\u6211\u4eec\u9a91\u9a6c\u5c31\u8ffd\uff0c\u8fd9\u4e24\u5339\u9a6c\u867d\u7136\u77ee\u5c0f\uff0c\u8dd1\u8d77\u8def\u6765\uff0c\u53ef\u6bd4\u80e1\u50e7\u90a3\u5339\u9ad8\u5934\u5927\u9a6c\u8981\u5feb\u5f97 \u591a\uff0c\u8ffd\u4e86\u5c06\u8fd1\u534a\u4e2a\u66f4\u6b21\uff0c\u7ec8\u4e8e\u5728\u6b64\u5730\u8ffd\u4e0a\u4e86\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u9a6c\u8e44\u75d5\u8ff9\uff1f\u201d\u5510\u7aef\u9053\uff1b\u201c\u6211\u4eec\u51c6\u5907\u52ab\u72f1\u4e4b\u540e\u4e0a\u9a6c\u5c31 \u9003\uff0c\u6b63\u662f\u6015\u4eba\u53d1\u73b0\u9a6c\u8e44\u75d5\u8ff9\uff0c\u6240\u4ee5\u7528\u539a\u539a\u7684\u7ed2\u5e03\u5305\u7740\u9a6c\u8e44\uff0c\u6599\u90a3\u80e1\u50e7\u4e5f\u662f\u5982\u6b64\u3002\u201d\u51b0\u5ddd\u5929\u5973 \u8fd9\u624d\u604d\u7136\u5927\u609f\u3002 \u3000\u3000\u5510\u7aef\u7eed\u9053\uff1a\u201c\u8fd8\u5dee\u5341\u6765\u6b65\u6ca1\u6709\u8ffd\u4e0a\uff0c\u90a3\u80e1\u50e7\u7a81\u7136\u53cd\u624b\u4e00\u626c\uff0c\u597d\u51e0\u67c4\u98de\u5200\u4e00\u9f50\u98de\u6765\uff0c\u6211\u59d1 \u59d1\u662f\u6253\u6697\u5668\u7684\u80fd\u624b\uff0c\u6536\u53d1\u6697\u5668\uff0c\u767e\u4e0d\u5931\u4e00\uff0c\u5f53\u4e0b\u5c31\u60f3\u65bd\u5c55\u2018\u5343\u624b\u89c2\u97f3\u6536\u4e07\u5b9d\u201d\u7684\u7edd\u6280\uff0c\u5c06\u90a3 \u80e1\u50e7\u7684\u98de\u5200\u4e00\u53e4\u8111\u513f\u6536\u53bb\u3002\u5374\u4e0d\u6599\u90a3\u80e1\u50e7\u7684\u98de\u5200\u624b\u6cd5\u602a\u6781\uff0c\u7adf\u4f3c\u77e5\u9053\u6211\u59d1\u59d1\u4f1a\u63a5\u6697\u5668\u4f3c\u7684\uff0c \u521d\u521d\u98de\u6765\u4e4b\u65f6\uff0c\u660e\u662f\u5411\u4e0a\u659c\u98de\uff0c\u524a\u4eba\u4e0a\u76d8\uff0c\u5ffd\u7136\u5374\u53d8\u4e86\u8d34\u5730\u4f4e\u98de\uff0c\u524a\u9a6c\u7684\u56db\u8e44\uff0c\u5440\uff0c\u8fd9\u4e24\u5339 \u9a6c\uff0c\u7adf\u7136\u5c31\u8fd9\u6837\u5730\u846c\u9001\u5728\u80e1\u50e7\u4e4b\u624b\u3002\u8fd9\u4e5f\u56e0\u4e3a\u662f\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u6211\u59d1\u59d1\u5e74\u8001\uff0c\u76ee\u529b\u8870\u9000\uff0c\u8981\u4e0d \u7136\u98de\u5200\u7684\u65b9\u5411\u867d\u7136\u7a81\u53d8\uff0c\u6211\u59d1\u59d1\u4e5f\u4e0d\u81f3\u4e8e\u5931\u624b\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u5510\u5bb6\u767e\u591a\u5e74\u6765\uff0c\u90fd\u662f\u4ee5\u2018\u5929\u4e0b\u6697\u5668\u7b2c\u4e00\u5bb6\u2019\u996e\u8a89\u6c5f\u6e56\uff0c\u5510\u8d5b \u82b1\u8fd9\u6b21\u5931\u624b\uff0c\u4e0d\u77e5\u8be5\u591a\u96be\u8fc7\u5462\uff01\u201d\u679c\u7136\u542c\u5f97\u5510\u7aef\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u59d1\u59d1\u52c3\u7136\u5927\u6012\uff0c\u7acb\u5373\u7528\u6697\u5668 \u653b\u90a3\u80e1\u618e\u3001\u94c1\u83b2\u5b50\u3001\u6bd2\u85fb\u8475\u3001\u4e94\u96f7\u73e0\u3001\u91d1\u94b1\u7f25\u3001\u98de\u661f\u523a\uff0c\u4e00\u53d1\u5c31\u662f\u51e0\u5341\u679a\uff0c\u90a3\u80e1\u50e7\u6253\u5f97\u624b\u5fd9 \u811a\u4e71\u3002\u8fd9\u65f6\u90a3\u4e2a\u80e1\u50e7\u4e5f\u5df2\u8dc3\u4e0b\u9a6c\u80cc\uff0c\u628a\u8888\u88df\u62c9\u5f00\uff0c\u5f53\u4f5c\u76fe\u724c\uff0c\u9f99\u5e08\u53d4\u4ecd\u7136\u7aef\u5750\u9a6c\u4e0a\uff0c\u6211\u4eec\u521d \u65f6\u8fd8\u4ee5\u4e3a\u662f\u4ed6\u4e2d\u4e86\u8499\u6c57\u836f\uff0c\u8fd9\u65f6\u5728\u6708\u5149\u4e0b\u770b\u6e05\u695a\u4e86\uff0c\u5374\u89c1\u4ed6\u4e24\u53ea\u773c\u775b\u8fd8\u662f\u5f20\u7740\uff0c\u5446\u5446\u5730\u671b\u7740 \u6211\u4eec\u3002\u90a3\u80e1\u50e7\u62b5\u6321\u6211\u59d1\u59d1\u7684\u6697\u5668\uff0c\u5df2\u662f\u5341\u5206\u5403\u529b\uff0c\u82e5\u7136\u9f99\u5e08\u53d4\u5728\u80cc\u540e\u653b\u4ed6\uff0c\u7ba1\u4fdd\u53ef\u4ee5\u5236\u4ed6\u6b7b \u547d\u3002\u6211\u59d1\u59d1\u4fbf\u53eb\u9053\uff1a\u2018\u7075\u77eb\uff0c\u5feb\u62d4\u5251\u53d6\u4ed6\u80cc\u540e\u98ce\u5e9c\u7a74\uff01\u2019\u54ea\u6599\u9f99\u5e08\u53d4\u773c\u775b\u7728\u4e86\u51e0\u4e0b\uff0c\u624b\u811a\u98a4 \u6296\uff0c\u7adf\u662f\u4e00\u526f\u4e27\u9b42\u843d\u9b44\u7684\u795e\u6c14\uff0c\u5e76\u4e0d\u52a8\u624b\u3002\u8fd9\u53ef\u628a\u6211\u4eec\u6025\u574f\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u7b11\u4e4b\u58f0\uff0c\u7b11\u58f0\u672a\u6b47\uff0c\u4eba\u5f71\u5df2\u5230\u8ddf\u524d\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u5b9a\u662f \u91d1\u4e16\u9057\u6765\u4e86\uff01\u201d \u3000\u3000\u5510\u7aef\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u91d1\u4e16\u9057\u6765\u4e86\u3002\u6211\u4e0d\u77e5\u9053\u4ed6\u540e\u6765\u7adf\u4f1a\u5e2e\u6211\u7684\u59d1\u59d1\uff0c\u90a3\u65f6\u771f\u662f\u9a87\u6015\u5f97\u4e0d \u5f97\u4e86\uff01\u6562\u60c5\u6211\u7684\u59d1\u59d1\u4e5f\u662f\u4e00\u822c\u5fc3\u601d\uff0c\u5979\u5168\u9760\u6697\u5668\u4e0e\u90a3\u80e1\u50e7\u6253\u4e86\u534a\u5929\uff0c\u6697\u5668\u5df2\u7528\u5f97\u6240\u5269\u65e0\u51e0\uff0c \u90a3\u80e1\u50e7\u672c\u9886\u9ad8\u5f3a\uff0c\u82e5\u7136\u6697\u5668\u7528\u5b8c\uff0c\u53ea\u6015\u5408\u6211\u59d1\u4f84\u4e8c\u4eba\u4e4b\u529b\u4e5f\u6597\u4e0d\u8fc7\u4ed6\uff0c\u4f55\u51b5\u53c8\u6765\u4e86\u4e00\u4e2a\u65e0\u7406 \u53ef\u55bb\u7684\u5927\u4ec7\u654c\u91d1\u4e16\u9057\u3002\u5979\u53c8\u5927\u58f0\u50ac\u4fc3\u5e08\u53d4\uff0c\u4e0d\u77e5\u9f99\u5e08\u53d4\u662f\u5426\u4e2d\u4e86\u90aa\uff0c\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\uff01\u90a3\u4e00\u77ac \u95f4\uff0c\u6211\u5df2\u6253\u7b97\u8c41\u51fa\u6027\u547d\uff0c\u60f3\u5148\u628a\u90a3\u80e1\u50e7\u6253\u5012\uff0c\u7136\u540e\u518d\u5408\u6297\u91d1\u4e16\u9057\uff0c\u6211\u5f53\u7136\u719f\u77e5\u6211\u59d1\u59d1\u6253\u6697\u5668 \u7684\u624b\u6cd5\uff0c\u4fbf\u7acb\u523b\u62d4\u51fa\u8170\u5200\uff0c\u8d81\u7740\u59d1\u59d1\u7684\u6697\u5668\u4e00\u5bc6\u4e00\u758f\u7684\u95f4\u6b47\u4e4b\u9645\uff0c\u86c7\u884c\u6e38\u8d70\uff0c\u5e0c\u671b\u5728\u91d1\u4e16\u9057 \u672a\u66fe\u52a8\u624b\u653b\u51fb\u6211\u4eec\u4e4b\u524d\uff0c\u6211\u80fd\u591f\u5148\u628a\u90a3\u80e1\u50e7\u6253\u5012\uff01 \u3000\u3000\u201c\u91d1\u4e16\u9057\u6765\u5f97\u771f\u5feb\uff0c\u523a\u8033\u7684\u602a\u7b11\u58f0\u8fd8\u672a\u66fe\u6d88\u5931\uff0c\u4eba\u5df2\u5230\u4e86\u9762\u524d\uff0c\u6211\u8fd9\u65f6\u8ddd\u79bb\u90a3\u80e1\u50e7\u5927\u7ea6 \u6709\u4e03\u516b\u6b65\u8fdc\uff0c\u53ea\u89c1\u90a3\u80e1\u50e7\u628a\u8888\u88df\u4e00\u5c55\uff0c\u628a\u516d\u4e03\u5b97\u6697\u5668\u90fd\u6fc0\u5f97\u53cd\u5c04\u56de\u6765\uff0c\u6211\u59d1\u59d1\u6b63\u5728\u8f6c\u8eab\u5e94\u4ed8 \u91d1\u4e16\u9057\uff0c\u8fd8\u771f\u6599\u4e0d\u5230\u90a3\u80e1\u50e7\u4f1a\u7a81\u7136\u53cd\u51fb\uff0c\u602a\u7b11\u58f0\u4e2d\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u731b\u7136\u6253\u4e0b\uff0c\u6211\u59d1\u59d1\u82e5\u8981\u62db \u67b6\u94c1\u62d0\u5c31\u6321\u4e0d\u4f4f\u80cc\u540e\u7684\u6697\u5668\uff0c\u82e5\u8981\u8f6c\u8eab\u63a5\u6697\u5668\uff0c\u5c31\u6321\u4e0d\u4f4f\u91d1\u4e16\u9057\u7684\u94c1\u62d0\uff0c\u6211\u76ee\u7779\u8fd9\u6837\u5371\u9669\u7684 \u60c5\u5f62\uff0c\u4e00\u9897\u5fc3\u90fd\u51e0\u4e4e\u5413\u5f97\u8df3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u5ffd\u542c\u5f97\u4e00\u9635\u7e41\u97f3\u5bc6\u54cd\uff0c\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u6709\u5982\u6025\u96e8\uff0c\u90a3\u8bb8\u591a\u6697\u5668\uff0c\u53c8\u90fd\u6fc0\u5c04\u56de\u53bb\u3002\u539f\u6765\u91d1 \u4e16\u9057\u90a3\u4e00\u62d0\u626b\u4e0b\uff0c\u5374\u4e0d\u662f\u6253\u6211\u7684\u59d1\u59d1\uff0c\u53cd\u800c\u662f\u7ed9\u6211\u7684\u59d1\u59d1\u6321\u56de\u4e86\u90a3\u4e9b\u6697\u5668\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u5401\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u91d1\u4e16\u9057\u7684\u884c\u5f84\uff0c\u771f\u662f\u4eba\u6240\u96be\u6d4b\u3002\u201d\u5510\u7aef\u9053\uff1a\u201c\u90a3\u4e00\u77ac\u95f4\uff0c\u6211 \u5df2\u5168\u795e\u653e\u5728\u6211\u59d1\u59d1\u7684\u8eab\u4e0a\uff0c\u6599\u4e0d\u5230\u90a3\u80e1\u50e7\u771f\u662f\u6bd2\u8fa3\u975e\u5e38\uff0c\u8888\u88df\u4e00\u6296\uff0c\u5c06\u6697\u5668\u8361\u5f00\uff0c\u5ffd\u7136\u5411\u6211 \u5f53\u5934\u7f69\u4e0b\uff0c\u6211\u53ea\u542c\u89c1\u91d1\u4e16\u9057\u5927\u559d\u4e00\u58f0\uff0c\u62d0\u5f71\u98de\u6765\uff0c\u800c\u90a3\u8888\u88df\u4e5f\u50cf\u4e00\u7247\u7ea2\u4e91\u538b\u4e0b\uff0c\u6211\u5c31\u6b64\u4e0d\u7701 \u4eba\u4e8b\uff0c\u76f4\u5230\u800c\u4eca\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u76f8\u987e\u9a87\u7136\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u8c01\u80dc\u8c01\u8d25\u4f60\u4e5f\u4e0d\u77e5\u9053\u4e86\uff1f\u201d\u5510\u7aef\u9053\uff1a\u201c\u6211 \u7684\u6027\u547d\u8fd8\u662f\u5168\u9760\u4f60\u4eec\u6551\u56de\uff0c\u5176\u4ed6\u7684\u4e8b\uff0c\u5f53\u7136\u662f\u4e0d\u77e5\u9053\u7684\u4e86\u3002\u5440\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u4eec\u6253\u7684\u975e\u5e38\u6fc0 \u70c8\uff0c\u6211\u59d1\u59d1\u5e74\u7eaa\u8001\u8fc8\uff0c\u7684\u662f\u4ee4\u4eba\u62c5\u5fc3\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u5510\u8001\u524d\u8f88\u5b9a\u7136\u65e0\u4e8b\uff0c\u8981\u4e0d\u7136\u90a3\u80e1\u50e7\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u4f60\u4e86\u3002\u800c\u4e14\uff0c\u8981\u662f \u4ed6\u4eec\u53d7\u4f24\uff0c\u8fd9\u91cc\u7109\u6709\u4e0d\u7559\u4e0b\u8ff9\u8c61\u4e4b\u7406\uff0c\u6211\u770b\uff0c\u4ed6\u4eec\u5b9a\u662f\u8054\u624b\u8ffd\u90a3\u80e1\u50e7\u53bb\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u53ea\u6709\u7ee7\u7eed\u518d\u53bb\u8ffd\u8e2a\u3002\u201d\u5929\u8272\u4f4e\u6c89\uff0c\u53c8\u843d\u96ea\u4e86\uff0c\u96ea\u8d8a\u79ef\u8d8a\u539a\uff0c\u832b\u832b \u7684\u96ea\u5730\uff0c\u671b\u4e0d\u5230\u5934\uff0c\u7eb5\u6709\u8db3\u8ff9\u4e5f\u88ab\u79ef\u96ea\u906e\u63a9\u4e86\u3002\u4e09\u4eba\u65e0\u6cd5\uff0c\u53ea\u6709\u5411\u7740\u6b63\u897f\u65b9\u76f4\u8d70\u3002\u51b0\u5ddd\u5929\u5973 \u4e00\u8def\u95f7\u95f7\u4e0d\u4e50\uff0c\u731c\u60f3\u4e0d\u900f\u91d1\u4e16\u9057\u4f55\u4ee5\u4e0d\u53bb\u5929\u5c71\uff0c\u5374\u6765\u5230\u8fd9\u7f55\u89c1\u4eba\u70df\u7684\u8352\u539f\u3002 \u3000\u3000\u91d1\u4e16\u9057\u81ea\u4ece\u5728\u90a3\u5c0f\u9152\u5e97\u4e2d\u9003\u51fa\u4e4b\u540e\uff0c\u81ea\u89c9\u65e0\u989c\u518d\u89c1\u51af\u7433\u6bcd\u5973\uff0c\u5728\u83bd\u83bd\u7684\u8349\u539f\uff0c\u4e13\u62e3\u6700\u8352 \u50fb\u7684\u5730\u65b9\u8d70\uff0c\u832b\u65e0\u76ee\u7684\u8d70\u4e86\u4e09\u5929\uff0c\u8d70\u8fdb\u4e86\u6c99\u6f20\u5730\u5e26\uff0c\u8ff7\u5931\u4e86\u65b9\u5411\uff0c\u6781\u76ee\u671b\u53bb\uff0c\u6c93\u65e0\u4eba\u5bb6\uff0c\u5e72 \u7cae\u5403\u5c3d\uff0c\u53c8\u9965\u53c8\u6e34\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5c48\u6307\u4e00\u7b97\uff0c\u81ea\u5df1\u5927\u7ea6\u8fd8\u6709\u4e09\u5341\u6765\u5929\u6027\u547d\uff0c\u5fc3\u4e2d\u6697\u7b11\uff1a\u8fdf\u65e9\u90fd\u662f\u4e00\u6b7b\uff0c\u57cb\u9aa8\u8352\u539f\uff0c \u5316\u4e3a\u5c18\u7802\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u81ea\u8d1f\u7edd\u4e16\u6b66\u529f\uff0c\u5374\u6b7b\u5728\u6c99\u6f20\uff0c\u5982\u6b64\u6b7b\u6cd5\uff0c\u6b8a \u65e0\u5149\u91c7\uff0c\u5fc3\u6709\u4e0d\u7518\u3002\u91d1\u4e16\u9057\u4e00\u751f\u597d\u80dc\uff0c\u81ea\u4ece\u77e5\u9053\u81ea\u5df1\u96be\u514d\u4e00\u6b7b\u4e4b\u540e\uff0c\u65e5\u5915\u601d\u91cf\uff0c\u8981\u60f3\u4e00\u4e2a\u8d85 \u4e4e\u5c18\u4fd7\u7684\u6b7b\u6cd5\uff0c\u4e0d\u613f\u5e73\u5e73\u6de1\u6de1\u5730\u6b7b\u53bb\uff0c\u65e0\u95fb\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5728\u6c99\u6f20\u4e2d\u8ff7\u5931\u4e86\u65b9\u5411\uff0c\u60f3\u6253\u4e00\u6ef4\u6c34\u90fd\u96be\uff0c\u4f55\u51b5\u98df\u7269\uff1f\u8fd9\u65e5\u4ed6\u53c8\u9965\u53c8\u6e34\uff0c\u6765\u5230\u4e00\u4e2a \u7802\u4e18\uff0c\u7802\u4e18\u4e0a\u6709\u51e0\u5757\u4e2d\u7a7a\u7684\u5ca9\u77f3\uff0c\u6c99\u998d\u4e0a\u7684\u5ca9\u77f3\u6bd4\u8f83\u677e\u8f6f\uff0c\u5e38\u6709\u672a\u98ce\u5316\u7684\u77f3\u949f\u4e73\uff0c\u542b\u6709\u4e9b\u6c34 \u4efd\uff0c\u91d1\u6591\u9057\u5438\u4e86\u4e00\u4e9b\u77f3\u4e73\uff0c\u7565\u89e3\u5e72\u6e34\uff0c\u4f46\u9965\u706b\u8fd8\u662f\u96be\u71ac\uff0c\u4e8e\u662f\u4fbf\u5728\u5ca9\u53f3\u540e\u9762\u76d8\u819d\u7528\u529f\uff0c\u9759\u5750 \u7247\u523b\uff0c\u6c14\u900f\u91cd\u5173\uff0c\u7cbe\u795e\u7a0d\u632f\uff0c\u5ffd\u542c\u5f97\u9a7c\u94c3\u58f0\u8fdc\u8fdc\u98d8\u6765\u3002\u91d1\u4e16\u9057\u5927\u559c\uff0c\u60f3\u9053\uff1a\u201c\u9a86\u9a7c\u53f7\u79f0\u2018\u6c99 \u6f20\u4e4b\u821f\u2019\uff0c\u6709\u4e86\u9a86\u9a7c\uff0c\u4e0d\u6101\u8d70\u4e0d\u4e86\u8fd9\u6c99\u6f20\u4e86\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u6211\u82e5\u62a2\u4e86\u8fd9\u65c5\u4eba\u7684\u9a86\u9a7c\uff0c\u6211\u53ef\u4ee5 \u591a\u6d3b\u4e09\u5341\u591a\u5929\uff0c\u4ed6\u5c82\u975e\u8981\u56f0\u6b7b\u6c99\u6f20\uff1f\u82e5\u5728\u4ece\u524d\uff0c\u91d1\u4e16\u9057\u5b9a\u4f1a\u4e0d\u987e\u4e00\u5207\uff0c\u4f46\u81ea\u4ece\u4e0e\u51b0\u5ddd\u5929\u5973\u53ca \u51af\u7433\u6bcd\u5973\u7b49\u76f8\u8bc6\u4e4b\u540e\uff0c\u72c2\u50b2\u7684\u6027\u60c5\u867d\u7136\u672a\u6539\uff0c\u4f46\u5bf9\u4e16\u4eba\u7684\u618e\u6068\u5df2\u6697\u6697\u5730\u6539\u53d8\u4e86\uff0c\u6709\u65f6\u4ed6\u6e05\u591c \u81ea\u601d\uff0c\u89c9\u5bdf\u5230\u8fd9\u79cd\u6539\u53d8\u4e86\u7684\u5fc3\u60c5\uff0c\u8fde\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u9a7c\u94c3\u81ea\u8fdc\u800c\u8fd1\uff0c\u8981\u4e0d\u8981\u62a2\u8fd9\u5339\u9a86\u9a7c\uff0c\u91d1\u4e16\u9057\u6b63\u81ea\u8e0c\u8e87\u83ab\u51b3\uff0c\u5ffd\u542c\u5f97\u9a7c\u80cc\u4e0a\u90a3\u65c5\u4eba\u7a81\u7136\u53d1 \u51fa\u54c8\u54c8\u7684\u602a\u7b11\u4e4b\u58f0\uff0c\u5341\u5206\u719f\u6089\u3002\u91d1\u4e16\u9057\u907d\u7136\u4e00\u60ca\uff0c\u5077\u5077\u5f20\u671b\u8fc7\u53bb\uff0c\u53ea\u89c1\u4e00\u5339\u5927\u9a86\u9a7c\uff0c\u8fd8\u5728\u6570 \u91cc\u4e4b\u5916\uff0c\u6c99\u6f20\u4e0a\u65e0\u751a\u906e\u853d\uff0c\u770b\u5f97\u751a\u4e3a\u6e05\u695a\u3002\u9a7c\u80cc\u4e0a\u5750\u7684\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u800c\u662f\u4e24\u4e2a\u4eba\uff0c\u76f8\u8c8c\u90fd\u7279 \u522b\uff0c\u4e00\u773c\u77a5\u53bb\uff0c\u5c31\u8ba4\u5f97\u51fa\u6765\uff0c\u4e00\u4e2a\u662f\u8d64\u795e\u5b50\uff0c\u53e6\u4e00\u4e2a\u5219\u662f\u521a\u521a\u5728\u51e0\u5929\u4e4b\u524d\uff0c\u5728\u5c0f\u9152\u5e97\u4e2d\u548c\u81ea \u5df1\u5927\u6253\u8fc7\u4e00\u573a\u7684\u90a3\u4e2a\u94c1\u81c2\u548c\u5c1a\u8463\u592a\u6e05\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5927\u559c\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e24\u4e2a\u6df7\u86cb\uff0c\u62a2\u4e86\u4ed6\u4eec\u7684\u9a86\u9a7c\u4e5f\u4e0d\u7b97\u9020\u5b7d\uff01\u201d\u4f0f\u5730\u4e00\u542c\uff0c\u4ed6\u4eec \u8c08\u8bdd\u7684\u58f0\u97f3\u6e05\u6670\u53ef\u95fb\u3002\u53ea\u542c\u5f97\u8463\u592a\u6e05\u95ee\u9053\uff1a\u201c\u8d64\u795e\u9053\u53cb\uff0c\u6211\u542c\u9ec4\u77f3\u9053\u5144\u8bf4\uff0c\u4f60\u5df2\u53d7\u4e86\u671d\u5ef7\u4e4b \u8058\uff0c\u6709\u8363\u5c01\u56fd\u5e08\u4e4b\u671b\uff0c\u600e\u7684\u4e0d\u5728\u4eac\u5e08\u5b89\u4eab\u8363\u534e\u5bcc\u8d35\uff0c\u5374\u5230\u8fd9\u6c99\u6f20\u7684\u82e6\u5bd2\u4e4b\u5730\u53d7\u7f6a\uff0c\u96be\u9053\u6709\u4ec0 \u4e48\u516c\u4e8b\u8981\u5230\u8fd9\u7b49\u5730\u65b9\u6765\u529e\uff1f\u201d\u8d64\u795e\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u4f3c\u54ed\u975e\u54ed\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u602a\u58f0\u602a\u6c14\u7684\u7b54\u9053\uff1a \u201c\u54b3\uff0c\u8bf4\u6765\u8bdd\u957f\uff0c\u6211\u4e14\u95ee\u4f60\uff0c\u4f60\u53c8\u600e\u4e48\u6765\u5230\u8fd9\u513f\uff1f\u4f60\u8bf4\u4f60\u9041\u8ff9\u7a7a\u95e8\uff0c\u57cb\u540d\u9690\u59d3\u4e86\u4e09\u5341\u591a\u5e74\uff0c \u800c\u4eca\u521a\u662f\u4e8c\u5ea6\u51fa\u4e16\u3002\u60f3\u4f60\u5df2\u7ec3\u4e86\u7edd\u4e16\u5947\u529f\uff0c\u4f60\u53c8\u4e3a\u4f55\u4e0d\u5230\u6c5f\u6e56\u4e0a\u91cd\u632f\u96c4\u98ce\uff1f\u201d\u542c\u4ed6\u4eec\u7684\u8bf4 \u8bdd\uff0c\u8463\u5927\u6e05\u4e0e\u9ec4\u77f3\u9053\u4eba\u53ca\u8d64\u795e\u5b50\u90fd\u662f\u65e7\u76f8\u8bc6\uff0c\u8463\u592a\u6e05\u518d\u5ea6\u51fa\u5c71\u4e4b\u540e\uff0c\u7b2c\u4e00\u4e2a\u78b0\u5230\u7684\u662f\u9ec4\u77f3\u9053 \u4eba\uff0c\u7b2c\u4e8c\u4e2a\u78b0\u5230\u7684\u65e7\u53cb\u5c31\u662f\u8fd9\u4e2a\u8d64\u795e\u5b50\uff0c\u800c\u4e14\u4e5f\u662f\u521a\u521a\u78b0\u5230\u7684\u3002 \u3000\u3000\u8463\u592a\u6e05\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd8\u8bf4\u4ec0\u4e48\u7edd\u4e16\u5947\u529f\uff0c\u6211\u4e00\u51fa\u5c71\u5c31\u88ab\u4eba\u6253\u5f97\u72fc\u72c8\u4e0d\u582a\u4e86\u3002\u201d\u8d64\u795e\u5b50 \u5927\u4e3a\u5947\u602a\uff0c\u9053\uff1a\u201c\u8463\u5144\uff0c\u4f60\u4e00\u5411\u4e0d\u80af\u670d\u4eba\uff1f\u600e\u7684\u8fd9\u6b21\u5374\u5fc3\u670d\u53e3\u670d\uff1f\u662f\u4ec0\u4e48\u4eba\u7269\uff0c\u80fd\u5c06\u4f60\u6253\u5f97 \u72fc\u72c8\u4e0d\u4e0d\u582a\uff1f\u201d \u3000\u3000\u8463\u592a\u6e05\u9053\uff1a\u201c\u662f\u5510\u6653\u6f9c\u7684\u5c0f\u59e8\u5b50\u51af\u7433\u3002\u201d\u8d64\u795e\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53c8\u662f\u5929\u5c71\u6d3e\u7684\u4eba \u7269\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u9ec4\u77f3\u9053\u58eb\u5c61\u53d7\u632b\u6298\uff0c\u5fc3\u7070\u610f\u51b7\uff0c\u5df2\u51b3\u610f\u518d\u5ea6\u56de\u5230\u77f3\u6797\u82e6\u4fee\uff0c\u4ece\u6b64\u4e0d\u7406\u4e16 \u4e8b\u4e86\u3002\u6211\u8fd8\u4e0d\u80af\u7518\u4f11\uff0c\u6211\u8981\u627e\u5bfb\u4e00\u4e2a\u4eba\uff0c\u5e0c\u671b\u80fd\u53d6\u5f97\u4e00\u672c\u7edd\u4e16\u7684\u5947\u4e66\u3002\u201d\u8d64\u795e\u5b50\u51b7\u7b11\u9053\uff1a \u201c\u4ec0\u4e48\u5947\u4e66\uff1f\u96be\u9053\u4e66\u4e0a\u6240\u8f7d\u7684\u6b66\u529f\uff0c\u8fd8\u80fd\u5f3a\u5f97\u8fc7\u5929\u5c71\u6d3e\u4e0d\u6210\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u90a3\u4e5f\u8bf4\u4e0d\u5b9a\u3002 \u4f60\u77e5\u9053\u5728\u4e09\u56db\u5341\u5e74\u4ee5\u524d\uff0c\u5929\u4e0b\u6b66\u529f\u6700\u5f3a\u7684\u662f\u4ec0\u4e48\u4eba\u7269\uff1f\u201d\u8d64\u795e\u5b50\u9053\uff1a\u201c\u8be5\u662f\u6613\u5170\u73e0\u3001\u5415\u56db\u5a18 \u548c\u6bd2\u9f99\u5c0a\u8005\u5427\uff1f\u6613\u5170\u73e0\u662f\u6700\u8001\u7684\u524d\u8f88\uff0c\u5979\u5148\u53bb\u4e16\uff0c\u5269\u4e0b\u6765\u7684\u5c31\u662f\u6bd2\u9f99\u5c0a\u8005\u548c\u5415\u56db\u5a18\u4e86\u3002\u201d\u8463 \u592a\u6e05\u9053\uff1a\u201c\u6211\u6240\u8981\u627e\u5bfb\u7684\u4eba\u5c31\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u90a3\u672c\u5947\u4e66\u300a\u6bd2\u9f99\u79d8\u7c4d\u300b\u4fbf\u5728\u4ed6\u7684\u8eab \u4e0a\u3002\uff0c\uff0c\u8d64\u795e\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u80af\u7ed9\u4f60\uff1f\u201d\u91d1\u4e16\u9057\u542c\u4e86\u4e5f\u662f\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5c06\u5b83\u629b\u5165\u5927 \u6d77\u4e5f\u4e0d\u4f1a\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8463\u592a\u6e05\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u81ea\u6709\u6cd5\u5b50\u8981\u4ed6\u7ed9\u6211\u3002\u201d\u8d64\u795e\u5b50\u610f\u4f3c\u4e0d\u4fe1\uff0c\u6447\u4e86\u6447\u5934\u3002\u8463\u592a\u6e05\u9053\uff1a \u201c\u9053\u5144\uff0c\u4f60\u5462\uff0c\u4f60\u597d\u4f3c\u4e5f\u9047\u5230\u4e86\u4ec0\u4e48\u4e0d\u5982\u610f\u4e4b\u4e8b\u3002\u4e00\u4eba\u8ba1\u77ed\uff0c\u4e8c\u4eba\u8ba1\u957f\uff0c\u4f55\u4e0d\u8bf4\u51fa\u6765\u8ba9\u5c0f\u5f1f \u66ff\u4f60\u5206\u5fe7\uff1f\u201d\u8d64\u795e\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u610f\u6001\u751a\u505a\uff0c\u597d\u50cf\u662f\u8bf4\uff1a\u201c\u6211\u90fd\u53d7\u4e86\u632b\u6298\uff0c\u4f60\u6709\u4ec0\u4e48\u672c\u4e8b \u66ff\u6211\u5206\u5fe7\uff1f\u201d\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5ffd\u7136\u6362\u4e86\u4e00\u526f\u5634\u8138\uff0c\u9053\uff1a\u201c\u8463\u9053\u5144\uff0c\u4f60\u60f3\u522b\u4eba\u628a\u5e08\u95e8\u7684\u79d8\u7c4d\u7ed9\u4f60\uff0c \u90a3\u662f\u75f4\u5fc3\u5984\u60f3\uff0c\u4e0d\u9632\u548c\u6211\u4e00\u9053\u4e0a\u559c\u9a6c\u62c9\u96c5\u5c71\u53bb\u6500\u767b\u73e0\u7a46\u6717\u739b\u5cf0\u5427\u3002\u201d\u8463\u592a\u6e05\u53eb\u9053\uff1a\u201c\u73e0\u7a46\u6717 \u739b\u5cf0\uff0c\u90a3\u5c82\u4e0d\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u5cf0\uff1f\u201d\u8d64\u795e\u5b50\u9053\uff1a\u201c\u5bf9\u5475\uff0c\u5929\u4e0b\u7b2c\u4e00\u9ad8\u5cf0\uff01\u201d\u8463\u5927\u4e0d\u89e3\u9053\uff1a\u201c\u81ea \u53e4\u4ee5\u6765\u3001\u65e0\u4eba\u80fd\u4e0a\u73e0\u5cf0\uff0c\u6211\u770b\u4f60\u6bd4\u6211\u66f4\u662f\u4e0d\u5207\u5b9e\u9645\uff0c\u4f60\u600e\u4e48\u4f1a\u80fd\u6253\u8fd9\u4e3b\u610f\uff1f\u201d \u3000\u3000\u8d64\u795e\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u9001\u6b7b\uff0c\u4e5f\u6bd4\u73b0\u5728\u8fd9\u6837\u4e0d\u6b7b\u4e0d\u6d3b\uff0c\u7531\u4eba\u6b3a\u8d1f\u7684\u597d\uff01\u201d\u8463\u592a\u6e05\u9053\uff0c \u201c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d\u8d64\u795e\u5b50\u9053\uff1a\u201c\u4f60\u8d25\u5728\u51af\u7433\u624b\u4e2d\uff0c\u8fd8\u7b97\u503c\u5f97\uff0c\u6211\u5374\u8d25\u5728\u4e00\u4e2a\u540e\u8f88\u624b\u4e2d\u3002\u201d\u8463\u5927 \u6e05\u201c\u8c01\uff1f\u201d\u8d64\u795e\u5b50\u9053\uff1a\uff0c\u51b0\u5ddd\u5929\u5973\uff01\u201d\u8463\u5927\u6e05\u9053\uff1a\u201c\u597d\u53e4\u602a\u7684\u540d\u5b57\uff0c\u6211\u4ece\u6765\u672a\u542c\u8fc7\u3002\u201d\u8d64\u795e \u5b50\u9053\uff1a\u201c\u73b0\u5728\u6709\u8bb8\u591a\u65b0\u51fa\u9053\u7684\u4eba\u7269\uff0c\u4ed6\u4eec\u7684\u5389\u5bb3\uff0c\u4f60\u54ea\u80fd\u77e5\u9053\uff1f\u6211\u4e2d\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u4e03\u679a\u51b0\u9b44 \u795e\u5f39\uff0c\u73b0\u5728\u5143\u6c14\u5c1a\u672a\u6062\u590d\u3002\u542c\u8bf4\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u4ed9\u82b1\u5f02\u8349\u751a\u591a\uff0c\u5176\u4e2d\u6709\u4e00\u79cd\u4ed9\u8349\u53eb\u505a\u7edb\u4ed9\u8349\uff0c \u5403\u4e86\u53ef\u4ee5\u5f53\u5f97\u4e09\u5341\u5e74\u529f\u529b\u3002\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u672c\u6765\u662f\u5949\u547d\u548c\u4e91\u7075\u5b50\u592b\u5987\u5230\u62c9\u8428\u53bb\u76d1\u65a9\u90a3\u9f99\u8001\u4e09 \u7684\uff0c\u6211\u800c\u4eca\u529f\u529b\u5927\u635f\uff0c\u5b9e\u5728\u65e0\u989c\u518d\u5728\u6c5f\u6e56\u4e0a\u6df7\uff0c\u4ec0\u4e48\u56fd\u5e08\u7684\u5c01\u53f7\u6211\u4e5f\u4e0d\u7a00\u7f55\u5566\u3002\u6211\u5f97\u5148\u4e0a\u73e0 \u5cf0\u53bb\u89c5\u90a3\u4ed9\u8349\u3002\u6709\u4f60\u548c\u6211\u540c\u4f34\uff0c\u603b\u6bd4\u4e00\u4eba\u5192\u9669\u8981\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u542c\u4e86\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4e0d\u662f\u4f60\u4e0d\u7a00\u7f55\u56fd\u5e08\u5c01\u53f7\uff0c\u800c\u662f\u4f60\u6015\u529f\u529b\u5927\u635f \u4e4b\u540e\uff0c\u8fde\u4e91\u7075\u5b50\u4e5f\u6bd4\u4e0d\u4e0a\uff0c\u56fd\u5e08\u7684\u5c01\u53f7\u53c8\u600e\u4f1a\u8f6e\u5230\u4f60\u62ff\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u90a3\u9f99\u8001\u4e09\u53c8\u662f\u4ec0\u4e48 \u4eba\uff1f\u600e\u7684\u6e05\u5ef7\u8981\u8058\u8bf7\u4e09\u4e2a\u9ad8\u624b\u524d\u5f80\u76d1\u65a9\uff1f\u201d\u53ea\u89c1\u90a3\u5339\u5927\u9a86\u9a7c\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5df2\u5230\u4e86\u6c99\u4e18\u524d\u9762\uff0c\u91d1 \u4e16\u9057\u5ffd\u5730\u4e00\u58f0\u602a\u7b11\uff0c\u8df3\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8981\u4ed9\u8349\uff0c\u6211\u53ea\u8981\u4f60\u8fd9\u5339\u9a86\u9a7c\uff01\u201d \u3000\u3000\u90a3\u5934\u9a86\u9a7c\u7ed9\u91d1\u4e16\u9057\u4e00\u6309\uff0c\u767b\u65f6\u4e0d\u80fd\u8d70\u52a8\uff0c\u8d64\u795e\u5b50\u5927\u6012\u559d\u9053\uff1a\u201c\u91d1\u4e16\u9057\u4f60\u5f85\u600e\u5730\uff1f\uff0c\u2019\u91d1 \u4e16\u9057\u5927\u7b11\u9053\uff1a\u201c\u4f60\u8033\u6735\u804b\u4e86\u5417\uff1f\u6211\u4e0d\u662f\u5bf9\u4f60\u8bf4\u4e86\uff0c\u6211\u53ea\u8981\u8fd9\u5339\u9a86\u9a7c\uff01\u201d \u3000\u3000\u8d64\u795e\u5b50\u66fe\u548c\u91d1\u4e16\u9057\u6570\u6b21\u76f8\u6597\uff0c\u5f7c\u6b64\u90fd\u77e5\u9053\u5bf9\u65b9\u672c\u9886\uff0c\u5728\u4ee5\u524d\u6765\u8bf4\uff0c\u8d64\u795e\u5b50\u7684\u529f\u529b\u8f83\u9ad8\uff0c \u91d1\u4e16\u9057\u7684\u6697\u5668\u5389\u5bb3\uff0c\u51e0\u6b21\u76f8\u6597\uff0c\u90fd\u662f\u4e24\u96be\u53d6\u80dc\u3002\u800c\u4eca\u8d64\u795e\u5b50\u5143\u6c14\u672a\u590d\uff0c\u5bf9\u91d1\u4e16\u9057\u672c\u6709\u987e\u5fcc\uff0c \u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u6709\u8463\u592a\u6e05\u76f8\u52a9\uff0c\u4ee5\u4e8c\u654c\u4e00\uff0c\u5b9a\u7136\u53ef\u4ee5\u628a\u91d1\u4e16\u9057\u5236\u8d50\u3002\u4e8e\u662f\u5728\u9a7c\u80cc\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c \u51cc\u7a7a\u51fb\u4e0b\uff0c\u91d1\u4e16\u9057\u5927\u7b11\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u94c1\u62d0\u4e00\u4e3e\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u94c1\u62d0\u76f4\u6233\u8d64\u795e\u5b50\u5c0f \u8179\u7684\u201c\u85cf\u7cbe\u7a74\u201d\uff0c\u8d64\u795e\u5b50\u786c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u907f\u662f\u907f\u5f00\u4e86\uff0c\u53ef\u662f\u4ed6\u90a3\u4e00\u638c\u4e5f\u6253\u6b6a\u4e86\uff0c\u91d1\u4e16\u9057 \u5f97\u52bf\u4e0d\u9976\u4eba\uff0c\u63a5\u7740\u547c\u547c\u4e24\u62d0\uff0c\u72c2\u98ce\u9aa4\u96e8\u822c\u5730\u75be\u5377\u800c\u6765\uff0c\u628a\u8d64\u795e\u5b50\u903c\u5f97\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u8463\u592a\u6e05\u53eb\u9053\uff1a\u201c\u5927\u6c34\u51b2\u5230\u9f99\u738b\u5e99\uff0c\u90fd\u662f\u81ea\u5bb6\u4eba\uff0c\u5582\uff0c\u5582\uff01\u6709\u8bdd\u597d\u8bf4\uff01\u201d\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a \u201c\u8c01\u548c\u4f60\u662f\u81ea\u5bb6\u4eba\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u4f60\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u6211\u662f\u516b\u81c2\u795e\u9b54\u7684\u8863\u94b5\u4f20\u4eba\uff0c \u600e\u4e48\u4e0d\u662f\u81ea\u5df1\u4eba\uff1f\u201d\u91d1\u4e16\u9057\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5e08\u7236\u5728\u4e09\u5341\u5e74\u524d\u65e9\u5df2\u4e0e\u4ed6\u4eec\u5206\u9053\u626c \u9573\uff0c\u8c01\u5356\u4f60\u8fd9\u4e2a\u4ea4\u60c5\uff1f\u201d\u8463\u592a\u6e05\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4ea4\u60c5\u4f60\u53ef\u4ee5\u4e0d\u5356\uff0c\u6027\u547d\u4f60\u8981\u4e0d\u8981\uff1f\u201d\u91d1\u4e16\u9057\u6012 \u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u51ed\u4f60\u5c31\u8981\u5f97\u4e86\u6211\u7684\u6027\u547d\uff1f\u597d\uff0c\u4f60\u4eec\u4e24\u4e2a\u9f50\u4e0a\uff0c\u6211\u4e5f\u6beb\u4e0d\u5728\u4e4e\u3002\u201d\u6253\u5b9a\u4e3b\u610f\uff0c\u53ea \u8981\u8463\u592a\u6e05\u4e00\u4e0a\uff0c\u4ed6\u5c31\u8981\u7acb\u523b\u55b7\u51fa\u6bd2\u9488\u6697\u5668\u3002\u8463\u592a\u6e05\u9053\uff1a\u201c\u5582\uff0c\u4f60\u542c\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u4e0d\u662f\u6211\u8981\u4f60 \u7684\u6027\u547d\uff0c\u662f\u4f60\u7684\u5e08\u7236\u5bb3\u4e86\u4f60\u7684\u6027\u547d\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u4f60\u5185\u529f\u7684\u8def\u5b50\u7ec3 \u5f97\u4e0d\u5bf9\uff0c\u7ec8\u6709\u4e00\u65e5\u8981\u8d70\u706b\u5165\u9b54\uff0c\u8eab\u7ecf\u767e\u822c\u78e8\u96be\u800c\u6b7b\uff0c\u4f60\u8fd8\u6ca1\u6709\u53d1\u73b0\u8ff9\u8c61\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00 \u51db\uff1a\u4ed6\u600e\u4e48\u77e5\u9053\uff1f\u5374\u5ffd\u5730\u53c8\u602a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5728\u4e16\u95f4\u5df2\u6d3b\u4e0d\u4e86\u591a\u4e45\uff0c\u4f60\u76fc\u6211\u6b7b\uff0c\u6211\u6b63\u8981\u627e \u4eba\u966a\u4f34\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u5374\u628a\u94c1\u62d0\u4e2d\u7684\u957f\u5251\u4e5f\u62bd\u4e86\u51fa\u6765\uff0c\u5de6\u62d0\u53f3\u5251\uff0c\u653b\u52bf\u66f4\u89c1\u51cc\u5389\uff0c\u7adf\u7136\u662f\u4e00 \u526f\u62fc\u547d\u7684\u795e\u6c14\uff0c\u8d64\u795e\u5b50\u53eb\u9053\uff1a\u201c\u592a\u6e05\u9053\u53cb\uff0c\u548c\u4ed6\u591a\u8bf4\u4ec0\u4e48\uff1f\u7ed9\u4ed6\u593a\u4e86\u9a86\u9a7c\uff0c\u54b1\u4eec\u5982\u4f55\u80fd\u8d70\u51fa \u8fd9\u4e2a\u6c99\u6f20\uff1f\u201d\u8d64\u795e\u4e8e\u5b9e\u5728\u62b5\u654c\u4e0d\u4f4f\uff0c\u5374\u8fd8\u8981\u81ea\u6301\u8eab\u4efd\uff0c\u4e0d\u597d\u660e\u8a00\u8bf7\u8463\u592a\u6e05\u52a9\u62f3\uff0c\u8f6c\u4e2a\u5f2f\u513f\uff0c \u52a8\u4ee5\u5229\u5bb3\u3002 \u3000\u3000\u8463\u592a\u6e05\u54b3\u4e86\u4e00\u58f0\uff0c\u7ad9\u5728\u4e00\u8fb9\uff0c\u5374\u6162\u6761\u65af\u7406\u7684\u8bf4\u9053\uff1a\u201c\u300a\u6bd2\u9f99\u79d8\u7c4d\u300b\u662f\u4f60\u5e08\u7236\u6bd5\u751f\u5fc3\u8840\u4e4b \u6240\u805a\uff0c\u4f46\u4f60\u5374\u4e0d\u77e5\u9053\uff0c\u4ed6\u4e34\u6b7b\u4e4b\u524d\uff0c\u60f3\u5230\u4e86\u7834\u89e3\u8d70\u706b\u4eba\u9b54\u7684\u5947\u529f\u5999\u6cd5\uff0c\u672c\u4e0d\u53ca\u5199\u5165\u79d8\u53d1\uff0c\u53e6 \u8bb0\u5728\u4e00\u4e2a\u65e5\u5e38\u7684\u65e5\u8bb0\u4e8b\u672c\u4e0a\uff0c\u8fd9\u672c\u5b50\u5c31\u5728\u6211\u7684\u624b\u4e2d\u3002\u4f60\u8981\u4e0d\u8981\u6211\u628a\u5b83\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u5e08\u7236\u7edd\u4e16\u6b66\u529f\uff0c\u4ed6\u5728\u665a\u5e74\u4e4b\u65f6\uff0c\u5df2\u7ecf\u89c9\u5bdf\u5230\u81ea\u5df1\u5185\u529f\u8d70\u7684 \u8def\u5b50\u4e0d\u5bf9\uff0c\u6216\u8bb8\u771f\u60f3\u5230\u4e86\u7834\u89e3\u4e4b\u6cd5\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d\u7565\u4e00\u5206\u795e\uff0c\u8d64\u795e\u5b50\u4e58\u52bf\u53cd\u653b\uff0c\u628a\u638c\u5fc3\u7684\u70ed\u529b \u53d1\u6325\u51fa\u6765\uff0c\u547c\u547c\u6570\u638c\uff0c\u70ed\u98ce\u76f4\u88ad\u4e16\u9057\u5934\u9762\uff0c\u6c99\u6f20\u67af\u71e5\uff0c\u91d1\u4e16\u9057\u88ab\u70ed\u98ce\u4e00\u6247\uff0c\u66f4\u89c9\u7126\u6e34\u4e0d\u582a\uff0c \u52c3\u7136\u5927\u6012\uff0c\u62d0\u5251\u4e00\u9635\u731b\u653b\uff0c\u5c06\u8d64\u795e\u5b50\u7684\u51f6\u7130\u518d\u538b\u4e0b\u53bb\uff0c\u8d64\u795e\u5b50\u5fd9\u4e8e\u8fd0\u529f\u81ea\u4fdd\uff0c\u638c\u5fc3\u6240\u53d1\u51fa\u7684 \u70ed\u529b\u767b\u65f6\u5927\u51cf\u3002\u91d1\u4e16\u9057\u9053\uff1a\u201c\u597d\uff0c\u6211\u5e08\u7236\u7684\u4e66\u65e2\u5728\u4f60\u624b\uff0c\u4f60\u5c06\u4e66\u732e\u51fa\uff0c\u6211\u53ef\u4ee5\u9976\u4f60\u670b\u53cb\u4e00 \u547d\u3002\u201d\u8463\u592a\u6e05\u7b11\u9053\uff1a\u201c\u6043\u5f3a\u800c\u53d6\uff0c\u541b\u5b50\u4e0d\u4e3a\uff0c\u4f60\u5148\u505c\u624b\uff0c\u54b1\u4eec\u518d\u597d\u597d\u7684\u8bf4\u3002\u201d\u91d1\u4e16\u9057\u7591\u5fc3\u9661 \u8d77\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff0c\u201c\u6211\u8d70\u904d\u6c5f\u6e56\uff0c\u4f60\u6562\u5f53\u6211\u662f\u65e0\u77e5\u7684\u7a1a\u5b50\uff01\u6211\u624d\u4e0d\u4e0a\u4f60\u8fd9\u4e2a\u5f53\uff01\u8981\u505c\u624b\u4e5f\u5bb9 \u6613\uff0c\u5148\u628a\u4e66\u62ff\u51fa\u6765\uff01\u201d\u94c1\u62d0\u6a2a\u6572\uff0c\u957f\u5251\u76f4\u523a\uff0c\u75db\u4e0b\u6740\u624b\u3002\u8d64\u795e\u5b50\u6c14\u5598\u5401\u5401\uff0c\u53eb\u9053\uff1a\u201c\u592a\u6e05\u9053 \u53cb\uff0c\u8fd9\u53ae\u4e0d\u53ef\u7406\u55bb\uff0c\u4f60\u4e0d\u548c\u4ed6\u591a\u8bf4\u4f5c\u751a\uff1f\u201d \u3000\u3000\u8463\u592a\u6e05\u4e00\u9635\u8e0c\u8e87\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8d64\u795e\u5b50\u5982\u4eca\u529f\u529b\u5927\u51cf\uff0c\u6211\u4e0e\u4ed6\u8054\u624b\uff0c\u4e5f\u672a\u5fc5\u4fbf\u80dc\u5f97\u4e86\u91d1 \u4e16\u9057\uff0c\u800c\u4e14\u5373\u7b97\u80fd\u628a\u91d1\u4e16\u9057\u6253\u6b7b\uff0c\u53d6\u5f97\u9093\u672c\u300a\u6bd2\u9f99\u79d8\u7c4d\u300b\uff0c\u6ca1\u4eba\u6559\u6211\uff0c\u4e5f\u662f\u65e0\u7528\u3002\u4f55\u51b5\u4ed6\u53c8 \u662f\u51af\u7433\u5fc3\u76ee\u4e2d\u7684\u5973\u5a7f\uff0c\u6211\u600e\u4e48\u60f9\u5f97\u8d77\u4ed6\uff1f\u201d\u6709\u8fd9\u51e0\u5c42\u539f\u56e0\uff0c\u8463\u592a\u6e05\u8fdf\u8fdf\u4e0d\u6562\u52a8\u624b\uff0c\u4f46\u89c1\u8d64\u795e \u5b50\u5371\u6025\u4e4b\u6781\uff0c\u5fc3\u4e2d\u53c8\u6709\u4e0d\u5fcd\uff0c\u6b63\u5728\u8fdf\u7591\uff0c\u5ffd\u89c1\u91d1\u4e16\u9057\u4e00\u62d0\u626b\u4e0b\uff0c\u8d64\u795e\u5b50\u5df2\u662f\u65e0\u529b\u62db\u67b6\uff0c\u8463\u592a \u6e05\u5927\u60ca\u5931\u8272\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u94c1\u81c2\u4e00\u8fce\uff0c\u4e00\u58f0\u5927\u9707\uff0c\u94c1\u81c2\u8131\u81fc\u98de\u53bb\uff0c\u5168\u4e16\u9057\u4e00\u811a\u98de\u8d77\uff0c\u5148\u628a\u8d64\u795e \u5b50\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u94c1\u5251\u4e00\u6325\uff0c\u628a\u8463\u592a\u6e05\u7684\u50e7\u888d\u5272\u5f00\uff0c\u91cc\u9762\u7a7a\u7a7a\u5982\u4e5f\uff0c\u54ea\u91cc\u6709\u4ec0\u4e48\u4e66\u672c\uff1f \u3000\u3000\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a\u201c\u54c8\u3001\u4f60\u6562\u9a97\u6211\uff01\u201d\u8463\u592a\u6e05\u7259\u5173\u6253\u6218\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u771f\u7684\u6709\u4f60\u5e08\u7236 \u7684\u9057\u4e66\u3002\u201d\u5168\u4e16\u9057\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u85cf\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u8d76\u5feb\u62ff\u6765\u3002\u201d\u8463\u5927\u6e05\u9000\u540e\u4e24\u6b65\uff0c\u966a\u7b11\u8bf4 \u9053\uff1a\u201c\u603b\u602a\u6211\u672c\u4e8b\u4f4e\u5fae\uff0c\u65e0\u80fd\u4e3a\u529b\uff0c\u8fd9\u672c\u4e66\u53eb\u5929\u5c71\u6d3e\u7684\u638c\u95e8\u5510\u6653\u6f9c\u7f34\u53bb\u4e86\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a \u201c\u80e1\u9053\uff01\u5510\u6653\u6f9c\u8fd8\u7528\u8fd9\u672c\u4e66\uff1f\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u5510\u6653\u6f9c\u7684\u529f\u592b\u56fa\u7136\u662f\u5df2\u7ecf\u5230\u4e86\u7384 \u901a\u4e4b\u5883\uff0c\u4ee5\u4ed6\u6b66\u6797\u9886\u8896\u7684\u8eab\u4efd\uff0c\u5f53\u7136\u4e0d\u5c51\u7a83\u53d6\u522b\u4eba\u7684\u79d8\u672c\u3002\u4f46\u4ed6\u751f\u5e73\u6700\u5fcc\u60ee\u7684\u662f\u4f60\u7684\u5e08\u7236\uff0c \u82e5\u7136\u4f60\u5e08\u7236\u7684\u6b66\u529f\u6d41\u4f20\u4e0b\u6765\uff0c\u65e5\u540e\u603b\u80fd\u80dc\u8fc7\u4ed6\u5929\u5c71\u95e8\u4e0b\uff0c\u987b\u77e5\u5929\u5c71\u6d3e\u7684\u6b66\u529f\uff0c\u767e\u4f59\u5e74\u6765\uff0c\u90fd \u88ab\u5949\u4e3a\u81f3\u5c0a\u81f3\u5723\uff0c\u4ed6\u65e2\u662f\u5929\u5c71\u6d3e\u7684\u638c\u95e8\uff0c\u5c82\u80af\u7559\u4e0b\u540e\u60a3\uff0c\u8ba9\u4f60\u8fd9\u6d3e\u7684\u6b66\u529f\u65e5\u540e\u80dc\u8fc7\u4ed6\uff1f\u6240\u4ee5 \u4ed6\u5b9a\u7136\u8981\u5360\u6709\u8fd9\u672c\u4e66\uff0c\u90a3\u4e48\u4f60\u867d\u7136\u6709\u300a\u6bd2\u9f99\u79d8\u7c4d\u300b\uff0c\u4f46\u65e0\u6cd5\u7834\u89e3\u90a3\u8d70\u706b\u5165\u9b54\u7684\u707e\u96be\uff0c\u5c31\u5fc5\u7136 \u8981\u501a\u9760\u4ed6\u3002\u4e0d\u4f46\u4f60\u8981\u501a\u9760\u4ed6\uff0c\u5c06\u6765\u51e1\u662f\u5b66\u4f60\u8fd9\u6d3e\u6b66\u529f\u7684\u4eba\uff0c\u90fd\u8981\u4f9d\u9760\u5929\u5c71\u6d3e\u7684\u4eba\u89e3\u6551\uff0c\u8fd9 \u6837\uff0c\u4f60\u4eec\u4e16\u4e16\u4ee3\u4ee3\u5c31\u8981\u6210\u4e3a\u5929\u5c71\u6d3e\u7684\u5974\u96b6\u5566\uff01\u201d\u8463\u592a\u6e05\u4e00\u6d3e\u80e1\u8bf4\uff0c\u5374\u662f\u8a00\u4e4b\u6210\u7406\uff0c\u91d1\u4e16\u9057\u662f \u4e00\u4e2a\u6700\u597d\u9ad8\u8981\u80dc\u7684\u4eba\uff0c\u4e3a\u4e86\u81ea\u5df1\u8981\u9760\u5927\u5c71\u6d3e\u7684\u4eba\u89e3\u6551\uff0c\u800c\u5fc3\u6709\u4e0d\u7518\uff0c\u81f3\u6b7b\u4e0d\u80af\u6c42\u4eba\uff0c\u542c\u4e86\u8fd9 \u8bdd\uff0c\u6026\u7136\u5fc3\u52a8\uff0c\u7adf\u81ea\u4fe1\u4e86\u51e0\u6210\u3002 \u3000\u3000\u8463\u592a\u6e05\u5978\u7b11\u8bf4\u9053\uff1a\u201c\u5230\u4e86\u522b\u4eba\u624b\u91cc\uff0c\u8fd8\u5bb9\u6613\u8ba8\u56de\uff0c\u5230\u4e86\u5510\u6653\u6f9c\u624b\u91cc\uff0c\u53ea\u6015\u5929\u4e0b\u518d\u4e5f\u65e0\u4eba \u80fd\u5728\u4ed6\u624b\u4e2d\u593a\u8d70\uff01\u201d\u91d1\u4e16\u9057\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u4f46\u8463\u592a\u6e05\u8bf4\u7684\u4e43\u662f\u5b9e\u60c5\uff0c\u91d1\u4e16\u9057\u867d\u7136\u72c2 \u50b2\uff0c\u4e5f\u4e0d\u6562\u53e3\u51fa\u5927\u8a00\uff0c\u8bf4\u81ea\u5df1\u80fd\u591f\u5bf9\u4ed8\u5f97\u4e86\u5510\u6653\u6f9c\u3002\u8463\u592a\u6e05\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5012\u6709\u4e00\u4e2a\u6cd5 \u5b50\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d\u8463\u5927\u6e05\u9053\uff1a\u201c\u5510\u6653\u6f9c\u6709\u4e00\u4e2a\u72ec\u751f\u7231\u5b50\u540d\u53eb\u5510\u7ecf\u5929\uff0c\u6b64\u4eba\u6b66 \u529f\u867d\u7136\u6781\u9ad8\uff0c\u4f46\u6599\u60f3\u4f60\u8fd8\u6709\u6cd5\u5b50\u53ef\u4ee5\u6cbb\u4ed6\uff0c\u4f60\u53ea\u8981\u4e58\u4ed6\u4e0d\u9632\u5907\u7684\u65f6\u5019\uff0c\u7528\u4e03\u679a\u6bd2\u9488\u523a\u8fdb\u4ed6\u7684 \u7a74\u9053\uff0c\u90a3\u4e48\u4ed6\u7eb5\u6709\u5929\u5c71\u96ea\u83b2\u4e5f\u96be\u89e3\u6551\uff0c\u975e\u8981\u4f60\u7684\u89e3\u836f\u4e0d\u6210\u3002\u563f\uff0c\u563f\uff01\u5230\u4e86\u90a3\u65f6\uff0c\u5c31\u4e0d\u6101\u5510\u6653 \u6f9c\u4e0d\u548c\u4f60\u4ea4\u6362\u4e86\u3002\u201d \u3000\u3000\u4e09\u5341\u5e74\u4e4b\u524d\uff0c\u8463\u592a\u6e05\u7684\u4e00\u81c2\uff0c\u867d\u8bf4\u662f\u88ab\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u6240\u6298\uff0c\u4f46\u8ffd\u7a76\u8d77\u6765\uff0c\u5374\u662f\u7531\u5510\u6653 \u6f9c\u800c\u8d77\u3002\u8463\u592a\u6e05\u89c1\u91d1\u4e16\u9057\u7cbe\u660e\u4e4b\u6781\uff0c\u4e0d\u53d7\u4ed6\u9a97\uff0c\u4fbf\u7d22\u6027\u79fb\u7978\u4e1c\u5434\uff0c\u6311\u62e8\u91d1\u4e16\u9057\u4e0e\u5929\u5c71\u6d3e\u4e3a\u96be\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff0c\u201c\u8fd9\u679c\u7136\u662f\u4e00\u6761\u6bd2\u8ba1\u3002\u4f46\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\uff0c\u5728\u5ce8\u5d4b\u5c71\u4e0e \u91d1\u5149\u5bfa\u4e4b\u65f6\uff0c\u66fe\u8054\u5251\u6551\u8fc7\u6211\uff0c\u6211\u5c82\u80fd\u5bf9\u4ed6\u5077\u4e0b\u6bd2\u624b\uff1f\u4f46\u9664\u4e86\u6b64\u8ba1\uff0c\u53c8\u6709\u4f55\u6cd5\u53ef\u4ee5\u51fa\u8fd9\u53e3\u95f7\u6c14\uff1f \u3000\u3000\u8463\u592a\u6e05\u9053\uff1a\u201c\u4f60\u82e5\u6709\u51b3\u5fc3\uff0c\u6211\u8fd8\u6709\u6cd5\u5b50\u53ef\u4ee5\u66ff\u4f60\u628a\u5510\u7ecf\u5929\u9a97\u6765\u3002\u201d\u91d1\u4e16\u9057\u201c\u54fc\u201d\u4e86\u4e00 \u58f0\uff0c\u5ffd\u5730\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5c82\u80fd\u501f\u52a9\u4e8e\u4f60\u8fd9\u6837\u7684\u5351\u9119\u5c0f\u4eba\uff01\u201d\u9aa4\u53d1\u4e00\u638c\uff0c\u628a\u8463\u592a\u6e05\u6253\u5f97\u8dcc\u51fa\u4e00 \u4e08\u5f00\u5916\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e08\u592b\u4e00\u6b7b\u65e0\u7275\u6302\uff0c\u8bf4\u751a\u6069\u6765\u8bf4\u751a\u4ec7\uff01\u6211\u7684\u4e8b\u6211\u81ea\u4f1a\u7406\uff0c\u8c01\u8981\u4f60\u7ba1\uff1f \u54c8\uff0c\u54c8\uff0c\u6211\u53ea\u8981\u8fd9\u5339\u9a86\u9a7c\uff01\u4f60\u5148\u60f3\u6cd5\u6551\u81ea\u5df1\u7684\u6027\u547d\u5427\uff01\u201d\u9a91\u4e0a\u9a7c\u80cc\uff0c\u4e00\u8def\u5531\u7740\u6c5f\u5357\u53eb\u5316\u5b50\u60ef \u5531\u7684\u83b2\u82b1\u843d\uff0c\u5f84\u81ea\u8d70\u4e86\u3002\u8463\u592a\u6e05\u722c\u4e86\u8d77\u6765\uff0c\u8fde\u53eb\u6570\u58f0\uff0c\u91d1\u4e16\u9057\u5934\u4e5f\u4e0d\u56de\uff0c\u8463\u592a\u6e05\u53c8\u6012\u53c8\u6025\uff0c \u5728\u8fd9\u6c99\u6f20\u4e4b\u4e2d\uff0c\u5931\u4e86\u9a86\u9a7c\uff0c\u771f\u7b49\u5982\u5931\u4e86\u4e00\u534a\u6027\u547d\uff0c\u53ea\u5f97\u8dd1\u56de\u53bb\u6276\u8d77\u8d64\u795e\u5b50\uff0c\u66ff\u4ed6\u88f9\u521b\u7597\u4f24\uff0c \u5546\u91cf\u5982\u4f55\u8d70\u51fa\u8fd9\u4e2a\u6c99\u6f20\u3002\u9a86\u9a7c\u80cc\u4e0a\uff0c\u6709\u8d64\u795e\u5b50\u548c\u8463\u5927\u6e05\u7559\u4e0b\u7684\u8bb8\u591a\u5e72\u7cae\uff0c\u8fd8\u6709\u4e24\u5927\u76ae\u56ca\u7684\u6e05 \u6c34\uff0c\u91d1\u4e16\u9057\u559d\u4e86\u534a\u888b\u7684\u6c34\uff0c\u5403\u9971\u5e72\u7cae\uff0c\u9a91\u7740\u9a86\u9a7c\u5728\u6c99\u6f20\u4e0a\u5954\u8dd1\uff0c\u5f97\u610f\u4e4b\u6781\u3002\u6c99\u6f20\u521d\u6625\uff0c\u65e5\u77ed \u591c\u957f\uff0c\u8f6c\u773c\u53c8\u662f\u9ec4\u660f\u5c06\u5c4a\uff0c\u4f46\u89c1\u5bd2\u98ce\u9661\u8d77\uff0c\u9ec4\u7802\u5f25\u5929\uff0c\u8fde\u65e5\u5149\u4e5f\u67d3\u6210\u4e86\u4e00\u7247\u6de1\u9ec4\u7684\u989c\u8272\uff0c\u6c99 \u6f20\u4e0a\u53ea\u89c1\u6c99\u98de\uff0c\u4f46\u95fb\u98ce\u5578\uff0c\u91d1\u4e16\u9057\u4fe1\u53e3\u6240\u5531\u8244\u201c\u83b2\u82b1\u843d\u201d\u4e5f\u4ece\u8f7b\u677e\u7684\u5c0f\u8c03\uff0c\u53d8\u6210\u4e86\u60b2\u6ca7\u4e4b \u58f0\u3002\u53ea\u89c9\u5f97\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\uff01 \u3000\u3000\u5ffd\u7136\u60f3\u9053\uff1a\u201c\u8d64\u795e\u5b50\u4e0d\u662f\u8bf4\u8fc7\uff0c\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u6709\u4e00\u79cd\u4ed9\u8349\uff0c\u53ef\u4ee5\u5f53\u5f97\u5bfb\u5e38\u4fee\u58eb\u7684\u4e09\u5341\u5e74 \u529f\u529b\uff1f\u82e5\u7136\u6709\u8fd9\u6837\u7075\u5f02\uff0c\u53ea\u6015\u80fd\u533b\u597d\u6211\u4e5f\u8bf4\u4e0d\u5b9a\uff01\u53ea\u662f\u90a3\u73e0\u5cf0\u9ad8\u51fa\u793a\u9704\uff0c\u4e98\u53e4\u4ee5\u6765\uff0c\u4ece\u672a\u542c \u8bf4\u6709\u4eba\u80fd\u4e0a\u3002\u201d\u518d\u60f3\u9053\uff1a\u201c\u7eb5\u7136\u533b\u4e0d\u597d\uff0c\u7eb5\u7136\u6211\u722c\u4e0d\u4e0a\u73e0\u5cf0\u4fbf\u906d\u6a2a\u6b7b\uff0c\u4f46\u6211\u6b7b\u5728\u4e16\u754c\u7684\u6700\u9ad8 \u5cf0\uff0c\u4e5f\u53ef\u7b97\u5f97\u662f\u53e4\u4eca\u4e00\u4eba\uff0c\u8fd9\u6b7b\u6cd5\u5c82\u4e0d\u662f\u5927\u4e3a\u5feb\u610f\uff01\u201d\u4e00\u4e2a\u591a\u6708\u6765\uff0c\u91d1\u4e16\u9057\u6240\u60f3\u7684\u5c31\u662f\u5982\u4f55 \u6b7b\u6cd5\uff0c\u624d\u80fd\u8d85\u5c18\u8131\u4fd7\uff0c\u800c\u4eca\u60f3\u5230\u8981\u4e0a\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u53bb\u6b7b\uff0c\u771f\u662f\u5999\u7edd\u5343\u53e4\uff0c\u4e0d\u7981\u53c8\u624b\u821e\u8db3\u8e48\u8d77 \u6765\u3002 \u3000\u3000\u5927\u6f20\u9ec4\u660f\uff0c\u91d1\u4e16\u9057\u5728\u9a7c\u80cc\u4e0a\u72c2\u6b4c\u821e\u8e48\uff0c\u90a3\u9a86\u9a7c\u53d7\u4e86\u60ca\u5413\uff0c\u75be\u8dd1\u8d77\u6765\uff0c\u9a86\u9a7c\u53f7\u79f0\u6c99\u6f20\u4e4b \u821f\uff0c\u679c\u7136\u5982\u5c65\u5e73\u5730\uff0c\u91d1\u4e16\u9057\u4e5f\u4e0d\u7406\u5b83\u3002 \u3000\u3000\u5ffd\u542c\u524d\u65b9\u6253\u6597\u58f0\u97f3\uff0c\u91d1\u4e16\u9057\u722c\u4e0a\u5ca9\u77f3\u6765\u770b\uff0c\u8349\u539f\u767d\u96ea\u7691\u7691\uff0c\u91d1\u4e16\u9057\u76ee\u529b\u53c8\u597d\uff0c\u4f46\u89c1\u5728\u96ea \u5730\u4e0a\uff0c\u4e00\u4e2a\u8001\u592a\u5a46\u6b63\u5728\u548c\u4e00\u4e2a\u80e1\u50e7\u62fc\u6597\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u5c11\u5e74\u7ad9\u5728\u65c1\u8fb9\u3002\u91d1\u4e16\u9057\u4e00\u77a7\u90a3\u8001\u4eba\u5a46 \u7684\u6697\u5668\u6253\u6cd5\uff0c\u5c31\u8ba4\u51fa\u4e86\u662f\u5510\u8d5b\u82b1\uff0c\u90a3\u5c11\u5e74\u867d\u7136\u77a7\u4e0d\u6e05\u695a\uff0c\u4e5f\u6599\u5230\u662f\u5979\u7684\u4f84\u513f\u5510\u7aef\u4e86\u3002\u4f46\u89c1\u90a3 \u80e1\u50e7\u624b\u821e\u8888\u88df\uff0c\u5c45\u7136\u65bd\u5c55\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u6321\u5f97\u4f4f\u5510\u8d5b\u82b1\u98de\u8757\u7684\u6697\u5668\uff0c\u91d1\u4e16\u9057\u4e5f\u4e0d\u7531\u5f97\u5927\u4e3a\u60ca \u5947\u3002\u4ed6\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u770b\u4e0d\u591a\u4e45\uff0c\u4fbf\u77e5\u9053\u80e1\u50e7\u7684\u771f\u5b9e\u6b66\u529f\u8fdc\u5728\u5510\u8d5b\u82b1\u4e4b\u4e0a\u3002\u8ddd\u79bb\u5341\u4f59\u4e08 \u8fdc\uff0c\u6709\u4e00\u5339\u9a6c\uff0c\u9a6c\u4e0a\u7684\u9a91\u5ba2\u4f3c\u662f\u4e00\u4e2a\u519b\u5b98\uff0c\u91d1\u4e16\u9057\u542c\u5f97\u5510\u7aef\u5927\u53eb\u201c\u9f99\u5e08\u53d4\u201d\uff0c\u5510\u8d5b\u82b1\u53c8\u5927\u53eb \u201c\u7075\u77eb\u201d\uff0c\u7981\u4e2a\u4f4f\u5fc3\u5934\u4e00\u52a8\uff01 \u3000\u3000\u91d1\u4e16\u9057\u60f3\u8d77\u4e86\u90a3\u65e5\u8d64\u795e\u5b50\u6240\u8bf4\u7684\uff0c\u6e05\u5ef7\u8981\u8bf7\u4e8c\u5927\u9ad8\u624b\u76d1\u65a9\u9f99\u8001\u4e09\u7684\u4e8b\uff0c\u5fc3\u8fc8\uff1a\u201c\u53f2\u5c0f\u8fd9 \u4e2a\u59d3\u9f99\u7684\u4fbf\u662f\u9f99\u8001\u4e09\uff0c\u600e\u4e48\u7a7f\u7684\u5374\u662f\u6e05\u519b\u519b\u5b98\u7684\u670d\u9970\uff0c\u4e00\u70b9\u4e5f\u4e0d\u4f3c\u4e2a\u56da\u5f92\uff01\u201d\u5510\u7aef\u65e2\u79f0\u4ed6\u4e3a \u5e08\u53d4\uff0c\u4f55\u4ee5\u4ed6\u53c8\u8896\u624b\u65c1\u89c2\uff1f\u201d\u5374\u539f\u6765\u9f99\u7075\u77eb\u5728\u798f\u5eb7\u5b89\u5e55\u5382\u591a\u5e74\uff0c\u5f88\u5f97\u4fe1\u4efb\uff0c\u6240\u4ee5\u5728\u201c\u5723\u65e8\u201d \u672a\u6765\u4e4b\u7684\uff0c\u867d\u5904\u95ea\u7262\uff0c\u5374\u662f\u751a\u72b9\u4f18\u5f85\uff0c\u8fde\u670d\u9970\u4e5f\u65e0\u987b\u66f4\u6362\u3002 \u3000\u3000\u542c\u90a3\u6697\u5668\u5636\u98ce\u4e4b\u58f0\uff0c\u6e10\u6e10\u7531\u5bc6\u800c\u758f\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u90a3\u80e1\u50e7\u7684\u8888\u88df\u6709\u5982\u4e00\u7247\u7ea2\u4e91\uff0c\u7ffb\u98de\u821e \u52a8\uff0c\u5728\u96ea\u5730\u4e4b\u4e0a\uff0c\u66f4\u663e\u5f97\u5a01\u52bf\u975e\u51e1\u3002\u91d1\u4e16\u9057\u5fc3\u5934\u4e00\u9707\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u5510\u8d5b\u82b1\u7684\u6697\u5668\u5c31\u8981\u6253\u5149\uff0c \u53ea\u6015\u8981\u906d\u80e1\u50e7\u6bd2\u624b\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u592a\u5a46\u867d\u7136\u8ba8\u538c\uff0c\u7a76\u7adf\u662f\u5f53\u4eca\u6709\u6570\u7684\u6b66\u5b66\u540d\u5bb6\uff0c\u8ba9\u5979 \u6298\u5728\u80e1\u50e7\u4e4b\u624b\uff0c\u4e2d\u539f\u6b66\u6797\u4e5f\u5931\u9762\u5b50\u3002\u201d\u53c8\u60f3\u5230\u4ee5\u524d\u620f\u5f04\u5510\u8d5b\u82b1\u4e4b\u4e8b\uff0c\u81ea\u5df1\u4e00\u76f4\u5f15\u4e3a\u5feb\u610f\uff0c\u4e0d \u77e5\u600e\u7684\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u5374\u662f\u611f\u5230\u5185\u759a\u4e0d\u5b89\u3002 \u3000\u3000\u773c\u89c1\u60c5\u52bf\u8d8a\u6765\u8d8a\u6025\uff0c\u91d1\u4e16\u9057\u4e0d\u5047\u601d\u7d22\uff0c\u7a81\u7136\u8dc3\u51fa\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u6551\u4e86\u5510\u7aef\u7684\u6027\u547d\uff0c \u4e5f\u89e3\u6c57\u4e86\u5510\u8d5b\u82b1\u7684\u8888\u88df\u8986\u9876\u4e4b\u5371\uff01 \u3000\u3000\u91d1\u4e16\u9057\u5de7\u6551\u5510\u8d5b\u82b1\u7684\u7ecf\u8fc7\uff0c\u5510\u7aef\u66fe\u5411\u5510\u7ecf\u5927\u53d9\u8ff0\uff0c\u53ef\u662f\u540e\u6765\u7684\u90a3\u573a\u6fc0\u6218\uff0c\u5510\u7aef\u5df1\u6655\u5012\u5730 \u4e0a\uff0c\u90a3\u5c31\u4e00\u70b9\u4e5f\u4e0d\u77e5\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e0e\u80e1\u50e7\u4e00\u756a\u6076\u6597\uff0c\u53cc\u65b9\u90fd\u662f\u6697\u6697\u5403\u60ca\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u6c89\u91cd\u975e\u5e38\uff0c\u6bcf\u4e00\u62d0\u6253\u51fa\uff0c\u90fd \u662f\u529b\u903e\u5343\u65a4\uff0c\u53ef\u662f\u90a3\u80e1\u50e7\u5c55\u5f00\u8888\u88df\uff0c\u8d5b\u5982\u4e00\u9762\u5927\u94c1\u724c\uff0c\u94c1\u62d0\u78b0\u7740\uff0c\u53d1\u51fa\u201c\u535c\u535c\u201d\u7684\u58f0\u54cd\uff0c\u7adf \u4f3c\u6253\u5728\u786c\u7269\u4e4b\u4e0a\u4e00\u6837\u3002\u91d1\u4e16\u9057\u56fa\u7136\u6697\u53eb\u60ed\u6127\uff0c\u90a3\u80e1\u50e7\u66f4\u662f\u60ca\u60f6\uff0c\u5168\u4ed7\u7740\u8fd9\u624b\u529f\u592b\u66fe\u6a2a\u884c\u5929\u7afa \u4ee5\u53ca\u963f\u62c9\u4f2f\u5404\u56fd\uff0c\u591a\u6c89\u91cd\u7684\u5175\u5668\uff0c\u5728\u5341\u62db\u4e4b\u5185\u4e5f\u4f1a\u88ab\u4ed6\u593a\u51fa\u624b\u53bb\uff0c\u4f46\u78b0\u7740\u91d1\u4e16\u9057\u7684\u94c1\u62d0\uff0c\u5374 \u53ea\u662f\u582a\u582a\u80fd\u591f\u654c\u4f4f\u3002 \u3000\u3000\u91d1\u4e16\u9057\u52a9\u9635\uff0c\u5510\u8d5b\u82b1\u81ea\u662f\u5927\u51fa\u610f\u5916\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u5979\u7eb5\u7136\u600e\u6837\u618e\u6068\u91d1\u4e16\u9057\u4e5f\u4e0d\u80fd\u4e0d\u4e0e\u4ed6\u8054 \u624b\u5bf9\u654c\u3002\u8fd1\u8eab\u6df7\u6218\uff0c\u50a8\u5668\u65bd\u7528\u4e0d\u7740\uff0c\u5510\u8d5b\u82b1\u4fbf\u7528\u624b\u4e2d\u7684\u4e00\u5f20\u5f39\u5f13\uff0c\u5c55\u5f00\u5510\u5bb6\u4e16\u4f20\u7684\u201c\u91d1\u5f13\u5341 \u516b\u6253\u201d\u7684\u62db\u6570\uff0c\u522b\u770b\u5979\u5e74\u7eaa\u8001\u8fc8\uff0c\u62db\u6570\u5012\u662f\u6781\u4e3a\u7cbe\u5947\uff0c\u5f13\u62d0\u8054\u653b\uff0c\u767b\u65f6\u628a\u90a3\u80e1\u50e7\u903c\u5f97\u53ea\u6709\u62db \u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u53ef\u662f\u90a3\u80e1\u50e7\u72e1\u8bc8\u975e\u5e38\uff0c\u6b3a\u8d1f\u5510\u8d5b\u82b1\u5e74\u8001\u4f53\u5f31\uff0c\u4ed6\u7684\u8888\u88df\u5bf9\u91d1\u4e16\u9057\u662f\u53ea\u5b88\u4e0d\u653b\uff0c\u5bf9\u5510\u8d5b\u82b1 \u8fd9\u8fb9\u5374\u662f\u6697\u6697\u52a0\u91cd\u538b\u529b\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u5510\u8d5b\u82b1\u5df2\u6c14\u5598\u5401\u5401\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e45\u6218\u4e0d\u4e0b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5982\u6b64\u6253\u6cd5\uff0c\u518d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u53ea\u6015\u8fd9\u5510\u8001\u592a\u5a46\u53cd\u800c\u8981\u4e3a\u6210 \u7d2f\u8d58\u3002\u5355\u6253\u72ec\u6597\u6211\u867d\u4e0d\u60e7\uff0c\u4f46\u5510\u8001\u592a\u5a46\u82e5\u7136\u529b\u7aed\u6655\u5012\uff0c\u5c82\u975e\u8fd8\u8981\u6211\u6765\u7167\u6599\uff1f\u201d\u60f3\u53d1\u6bd2\u9488\u6697 \u5668\uff0c\u53c8\u56e0\u4e3a\u4e0d\u660e\u8fd9\u80e1\u50e7\u7684\u6765\u5386\uff0c\u4e0d\u613f\u81f4\u4ed6\u4e8e\u6b7b\u3002\u53ea\u542c\u5f97\u5510\u8d5b\u82b1\u53c8\u53eb\u4e86\u4e24\u58f0\u201c\u7075\u77eb\u201d\uff0c\u90a3\u519b\u5b98 \u4ecd\u662f\u6f20\u7136\u7684\u5750\u5728\u9a6c\u80cc\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u91d1\u4e16\u9057\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u5510\u8001\u592a\u5a46\uff0c\u90a3\u53ae\u662f\u4f60\u7684\u5e08\u5f1f\u5417\uff1f\u201d \u5510\u8d5b\u82b1\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7236\u4eb2\u6388\u4e1a\uff0c\u5374\u7531\u6211\u629a\u517b\u6210\u4eba\uff1b\u8bf4\u662f\u5e08\u5f1f\uff0c\u5176\u5b9e\u6211\u5f53\u4ed6\u662f\u513f\u5b50\u4e5f\u4e0d\u4e3a \u8fc7\u3002\u201d\u91d1\u4e16\u9057\u51b7\u773c\u770b\u9a6c\u80cc\u4e0a\u7684\u9f99\u7075\u77eb\uff0c\u53ea\u89c1\u4ed6\u8eab\u8eaf\u4e00\u6643\uff0c\u5374\u4ecd\u7136\u7aef\u5750\u5728\u9a6c\u80cc\u4e0a\uff0c\u6b8a\u65e0\u51fa\u624b\u4e4b \u610f\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e3a\u4f55\u4ed6\u4e0d\u5e94\u4f60\uff1f\u4f60\u770b\uff0c\u4ed6\u4e0d\u50cf\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u96be\u9053\u8fd9\u5996\u50e7\u8fd8\u771f \u4f1a\u90aa\u6cd5\u4e0d\u6210\uff1f\u201d\u5510\u8d5b\u82b1\u54ea\u77e5\u9053\u4ed6\u662f\u53d7\u4e86\u963f\u4fee\u7f57\u82b1\u7684\u5947\u9999\u6240\u60d1\uff0c\u5140\u662f\u83ab\u540d\u5176\u5999\uff0c\u53ea\u6709\u518d\u5927\u58f0\u53eb \u9053\uff0c\u201c\u7075\u77eb\uff0c\u7075\u77eb\uff01\u4f60\u542c\u89c1\u6211\u7684\u8bf4\u8bdd\u5417\uff1f\u8fd8\u662f\u88ab\u4ec0\u4e48\u5996\u672f\u6240\u5236\uff1f\u8bf4\u4e0d\u51fa\u6765\uff1f\u201d\u53ea\u89c1\u9f99\u7075\u77eb\u5728 \u9a6c\u80cc\u4e0a\u53c8\u6643\u4e86\u4e00\u6643\uff0c\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u5510\u8d5b\u82b1\u5927\u559c\uff0c\u60f3\u51b2\u51fa\u53bb\u6551\u4ed6\uff0c\u80e1\u50e7\u7684\u8888\u88df\u4e00\u7d27\uff0c\u538b\u529b\u9aa4 \u589e\uff0c\u5510\u8d5b\u82b1\u7684\u5f13\u5f26\u4e5f\u51e0\u4e4e\u7ed9\u8feb\u5f97\u8131\u51fa\u624b\u53bb\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u9f99\u8001\u4e09\u5fd8\u6069\u8d1f\u4e49\uff0c\u6211\u66ff\u4f60\u628a\u4ed6\u6293\u6765\u72e0\u72e0\u7684\u6253\u4e00\u987f\u3002\u201d\u5510\u8d5b\u82b1\u53eb \u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e0d\u597d\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u4f60\u53ea\u5b88\u4e0d\u653b\uff0c\u6321\u5f97\u5341\u62db\uff0c\u6211\u9a6c\u4e0a\u56de\u6765\uff01\u201d \u94c1\u62d0\u4e00\u8d77\uff0c\u4e00\u62db\u201c\u6f5c\u9f99\u5347\u5929\u201d\uff0c\u5411\u8888\u88df\u4e00\u6311\uff0c\u62d0\u5c16\u4e00\u504f\uff0c\u5374\u6233\u90a3\u80e1\u50e7\u80c1\u4e0b\u7684\u201c\u4e91\u95e8\u7a74\u201d\u3002\u90a3 \u80e1\u50e7\u628a\u8888\u88df\u98ce\u8f66\u822c\u5730\u4e00\u8f6c\uff0c\u62a4\u7740\u8981\u5bb3\uff0c\u53cd\u653b\u8fc7\u6765\u3002\u54ea\u77e5\u91d1\u4e16\u9057\u8fd9\u662f\u4ee5\u8fdb\u4e3a\u9000\u4e4b\u8ba1\uff0c\u90a3\u80e1\u50e7\u8888 \u88df\u4e00\u5c55\uff0c\u6321\u4f4f\u4e86\u91d1\u4e16\u9057\u4fa7\u9762\u7684\u653b\u51fb\uff0c\u53e6\u4e00\u9762\u9732\u51fa\u4e86\u7a7a\u9699\uff0c\u91d1\u4e16\u9057\u7a81\u7136\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u51fa\u53bb\uff0c\u98de \u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u9a6c\u80cc\uff0c\u610f\u6b32\u5148\u5411\u9f99\u7075\u77eb\u67e5\u95f4\u539f\u59d4\uff0c\u518d\u4f5c\u8ba1\u8f83\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5ffd\u542c\u5f97\u5510\u8001\u592a\u5a46\u5c16\u53eb\u4e4b\u58f0\uff0c\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e00\u51db\uff0c\u96be\u9053\u8fd9\u8001\u592a\u5a46\u5341\u62db\u4e5f\u5b88\u4e0d\u4f4f\uff1f\u56de \u5934\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u80e1\u50e7\u4e00\u624b\u626d\u7740\u5510\u8d5b\u82b1\u7684\u81c2\u818a\uff0c\u53cd\u526a\u80cc\u540e\uff0c\u4e00\u624b\u821e\u52a8\u8888\u88df\uff0c\u5df2\u5954\u5230\u9762\u524d\uff0c\u5927\u58f0 \u559d\u9053\uff1a\u201c\u8d76\u5feb\u4e0b\u9a6c\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u628a\u8fd9\u8001\u592a\u5a46\u6740\u4e86\uff01\u201d\u6253\u4e86\u534a\u591c\uff0c\u624d\u542c\u5230\u8fd9\u80e1\u50e7\u51fa\u58f0\uff0c\u8bf4\u7684\u5c45 \u7136\u662f\u4e00\u53e3\u6d41\u5229\u7684\u5317\u4eac\u8bdd\u3002 \u3000\u3000\u672c\u6765\u4ee5\u5510\u8d5b\u82b1\u7684\u529f\u529b\uff0c\u914d\u4e0a\u5979\u90a3\u5510\u5bb6\u4e16\u4f20\u7684\u201c\u91d1\u5f13\u5341\u516b\u6253\u201d\u7684\u7cbe\u5999\u62db\u6570\uff0c\u867d\u8bf4\u5df2\u662f\u7b4b\u75b2 \u529b\u7aed\uff0c\u4f46\u53ea\u5b88\u4e0d\u653b\uff0c\u6321\u5341\u62db\u4e8c\u5341\u62db\uff0c\u5374\u5c1a\u975e\u96be\u4e8b\u3002\u53ea\u56e0\u5979\u4ee5\u4e3a\u91d1\u4e16\u9057\u771f\u662f\u60f3\u53bb\u6293\u9f99\u7075\u77eb\u72e0\u6253 \u4e00\u987f\uff0c\u5fc3\u4e2d\u60ca\u60f6\uff0c\u60f3\u51b2\u51fa\u53bb\u62e6\u963b\uff0c\u811a\u6b65\u4e00\u79fb\uff0c\u7ae0\u6cd5\u4fbf\u4e71\uff0c\u90a3\u80e1\u50e7\u4f55\u7b49\u5389\u5bb3\uff0c\u8888\u88df\u4e00\u5377\uff0c\u7acb\u5373 \u5c06\u5979\u7684\u5f13\u5f26\u5377\u8d70\u3002\u5510\u8d5b\u82b1\u65e0\u6cd5\u62b5\u5fa1\uff0c\u7adf\u7136\u88ab\u5979\u64d2\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6295\u9f20\u5fcc\u5668\uff0c\u7a81\u7136\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u628a\u8fd9\u8001\u592a\u5a46\u653e\u5f00\uff0c\u6211\u8ba9\u4f60\u4e0a\u9a6c\u9003 \u8d70\uff01\u201d\u98de\u8eab\u4e00\u8dc3\u4e0b\u9a6c\uff0c\u90a3\u80e1\u50e7\u624b\u6307\u4e00\u677e\uff0c\u6b63\u6b32\u653e\u4eba\u6362\u9a6c\uff0c\u91d1\u4e16\u9057\u5ffd\u5730\u201c\u5478\u201d\u7684\u4e00\u53e3\u6d53\u75f0\u5410\u4e86 \u51fa\u6765\uff0c\u5b69\u4e2d\u6742\u6709\u201c\u4e1d\u4e1d\u201d\u4e4b\u58f0\uff0c\u8fd9\u80e1\u50e7\u4e5f\u771f\u7684\u5389\u5bb3\uff0c\u90a3\u6837\u5fae\u7ec6\u7684\u97f3\u5490\uff0c\u4ed6\u5c45\u7136\u542c\u5f97\u51fa\u662f\u98de\u9488 \u6697\u5668\u3002\u8888\u88df\u4e00\u5c55\uff0c\u6d53\u75f0\u5410\u5728\u8888\u88df\u4e4b\u4e0a\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u4e16\u9057\u4e00\u62d0\u5288\u4e0b\uff0c\u80e1\u50e7\u6296\u8d77\u8888\u88df\uff0c \u6321\u4e86\u4e2a\u7a7a\uff0c\u53ea\u542c\u5f97\u8f70\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u94c1\u62d0\u6253\u5728\u65c1\u8fd9\u7684\u5ca9\u77f3\u4e0a\uff0c\u77f3\u5c51\u7eb7\u98de\u3002\u80e1\u50e7\u6b63\u5728\u5947\u602a\u91d1\u4e16\u9057 \u8fd9\u4e00\u62d0\u4f55\u4ee5\u6253\u6b6a\uff0c\u500f\u7136\u95f4\uff0c\u53ea\u89c1\u9ed1\u5149\u4e00\u95ea\uff0c\u8888\u88df\u521a\u6296\uff0c\u5df2\u662f\u201c\u535c\u52d2\u201d\u4e00\u58f0\uff0c\u88ab\u6233\u7a7f\u4e86\u4e00\u4e2a\u7834 \u53e3\u3002\u8fd9\u6b63\u662f\u91d1\u4e16\u9057\u7684\u7591\u5175\u4e4b\u8ba1\uff0c\u6545\u610f\u6253\u65c1\u8fb9\u5ca9\u77f3\uff0c\u6270\u4ed6\u8033\u76ee\uff0c\u5206\u4ed6\u5fc3\u795e\uff0c\u5374\u4ee5\u6781\u8fc5\u901f\u7684\u624b \u6cd5\uff0c\u62bd\u51fa\u62d0\u4e2d\u94c1\u5251\uff0c\u8888\u88df\u4e00\u88ab\u523a\u7a7f\uff0c\u5c31\u4e0d\u80fd\u5f53\u6210\u76fe\u724c\u6765\u4f7f\u4e86\u3002 \u3000\u3000\u91d1\u5410\u9057\u5927\u559d\u4e00\u5360\uff1a\u201d\u5012\u4e0b\u201d\uff01\u4e00\u523a\u523a\u7834\u8888\u88df\uff0c\u7b2c\u4e8c\u5251\u8fde\u73af\u75be\u8ff8\uff0c\u5251\u5c16\u5165\u95f4\u5bf9\u51c6\u80e1\u50e7\u7684\u5927 \u67f1\u3001\u7384\u8ba5\u3001\u9633\u767d\u4e09\u5904\u5927\u7a74\uff0c\u5251\u950b\u53c8\u5012\u524a\u80e1\u50e7\u819d\u76d6\uff0c\u771f\u662f\u4e49\u72e0\u4e49\u51c6\u7684\u6740\u4e4e\u3002\u54ea\u77e5\u4ed6\u5feb\uff0c\u80e1\u50e7\u4e5f \u5feb\uff0c\u5251\u62fe\u65b9\u51fa\uff0c\u53ea\u542c\u5f97\u90a3\u80e1\u50e7\u53eb\u9053\uff1a\u201c\u597d\u5427\uff0c\u523a\uff01\u201d\u5ffd\u89c1\u5510\u8001\u592a\u5a46\u7684\u8eab\u8eaf\u8fce\u7740\u91d1\u5927\u9057\u94c1\u5251\u5012 \u6765\uff0c\u82e5\u4e0d\u662f\u91d1\u4e16\u9057\u6536\u52bf\u5f97\u5feb\uff0e\u6015\u4e0d\u5728\u5979\u8eab\u4e0a\u523a\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\uff01 \u3000\u3000\u8fd9\u51e0\u4e0b\u7535\u5149\u706b\u95ea\uff0c\u4e24\u8fb9\u90fd\u662f\u5947\u8be1\u83ab\u6d4b\uff0c\u51fa\u4eba\u610f\u5916\uff0c\u4f46\u7ed3\u679c\u8fd8\u662f\u90a3\u80e1\u50e7\u5360\u4e86\u4e0a\u98ce\uff0c\u5927\u7b11\u58f0 \u4e2d\uff0c\u89c1\u4ed6\u5df2\u8dd1\u4e0a\u9a6c\u80cc\uff0c\u631f\u7740\u9f99\u7075\u77eb\uff0c\u5954\u5411\u8fdc\u53bb\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u5ff5\u65b9\u52a8\uff0c\u7a81\u89c1\u5510\u8001\u592a\u5a46\u53c8\u7a81\u7136\u4f38\u624b\u5728\u4ed6\u9f3b\u4e0a\u4e00\u62b9\uff0c\u91d1\u4e16\u9057\u53ea\u89c9\u7cbe\u795e\u4e00\u723d\uff0c\u5026\u610f \u987f\u6d88\uff0c\u88ab\u95ed\u4e86\u7684\u6108\u6c14\u7a74\u4e5f\u81ea\u89e3\u4e86\u3002\u53ea\u89c1\u80e1\u50e7\u90a3\u5339\u5750\u9a91\u5df2\u5954\u51fa\u6570\u5341\u4e08\u5916\uff0c\u9f99\u7075\u77eb\u8f6f\u7ef5\u7ef5\u7684\u6837\u5b50 \u4f0f\u5728\u80e1\u50e7\u7684\u80a9\u5934\uff0c\u80e1\u50e7\u4e00\u624b\u5c06\u4ed6\u62e6\u8170\u62b1\u8d77\uff0c\u4e00\u624b\u63e1\u97ad\u7b56\u9a6c\u98de\u5954\u3002\u5510\u8001\u592a\u5a46\u5c16\u53eb\u9053\uff1a\u201c\u5feb\u8ffd\uff01 \u7075\u77eb\u662f\u4e2d\u4e86\u4ed6\u7684\u8ff7\u9b42\u6bd2\u9999\uff0c\u5e76\u975e\u4e0d\u8ba4\u6211\u3002\u201d \u3000\u3000\u80e1\u50e7\u6240\u7528\u7684\u6b63\u662f\u963f\u4fee\u7f57\u82b1\u6240\u70bc\u5236\u7684\u5947\u9999\uff0c\u6700\u80fd\u4ee4\u4eba\u5fc3\u795e\u604d\u60da\uff0c\u5e78\u800c\u5510\u8d5b\u82b1\u85cf\u6709\u80fd\u89e3\u5404\u79cd \u6bd2\u9999\u7684\u9f99\u6d8e\u818f\uff0c\u800c\u4e14\u4ed6\u548c\u91d1\u4e16\u9057\u53c8\u90fd\u662f\u5185\u529f\u6df1\u582a\u3002\u968f\u5373\u9192\u609f\uff0c\u4fbf\u5373\u95ed\u6c14\uff0c\u8fd9\u624d\u4e0d\u81f3\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u90a3\u80e1\u50e7\u5750\u9a91\u751a\u4e3a\u795e\u9a8f\uff0c\u91d1\u4e16\u9057\u660e\u77e5\u8ffd\u5b83\u4e0d\u5230\uff0c\u4f46\u89c1\u5510\u8001\u592a\u5a46\u597d\u4f3c\u5931\u4e86\u7406\u6027\u822c\u98de\u5954\u8ffd\u8d76\uff0c \u5fc3\u4e2d\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u53ef\u618e\u7684\u8001\u592a\u5a46\u5bf9\u90a3\u9f99\u8001\u4e09\u7adf\u6709\u9aa8\u8089\u6df1\u60c5\u3002\u53ef\u77e5\u4e0d\u8bba\u4f55\u4eba\uff0c\u90fd\u4e0d\u662f \u751f\u6765\u65e0\u60c5\u7684\u3002\u4e0d\u5fcd\u8ba9\u5979\u72ec\u8ffd\uff0c\u53ea\u597d\u8ddf\u4e0a\u3002 \u3000\u3000\u770b\u5510\u8d5b\u82b1\u8001\u8fc8\uff0c\u5979\u8dd1\u5f97\u8fd8\u771f\u5feb\uff0c\u5728\u5341\u6570\u91cc\u4e4b\u5185\uff0c\u7adf\u662f\u75be\u82e5\u5954\u9a6c\uff0c\u5927\u7ea6\u8ffd\u51fa\u4e86\u5341\u6570\u91cc\u5916\uff0c \u90a3\u80e1\u50e7\u7684\u9a6c\u9a91\u5df2\u77a7\u4e0d\u89c1\u4e86\u3002\u8001\u592a\u5a46\u5ffd\u7136\u4e00\u8de4\u6454\u5012\u5728\u96ea\u5730\u4e0a\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u53ef\u601c\u4e34\u8001\u6295\u8352\u6f20\uff0c\u75af\u4e10\u5c45\u7136\u8d64\u5b50\u5fc3 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\u3001\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56db\u56de\u3000\u5ced\u58c1\u73b0\u4fa0\u8e2a\u3000\u7591\u4e91\u9635\u9635\u3000\u5821\u4e2d\u6765\u602a\u5ba2\u3000\u5996\u6c14\u91cd\u91cd \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u56db\u56de \u5ced\u58c1\u73b0\u4fa0\u8e2a\u3000\u7591\u4e91\u9635\u9635 \u5821\u4e2d\u6765\u602a\u5ba2\u3000\u5996\u6c14\u91cd\u91cd \u3000\u3000\u91d1\u4e16\u9057\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u5510\u8001\u592a\u5a46\u54c7\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u6c14\u5598\u5401\u5401\u5730\u8bf4\u9053\uff1a \u201c\u6211\u4e0d\u6210\u5566\uff0c\u62dc\u6258\u4f60\u56de\u53bb\u7167\u6599\u6211\u7684\u4f84\u513f\u3002\u201d\u91d1\u4e16\u9057\u66ff\u5979\u628a\u8109\u4e00\u542c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6beb\u4e0d\u788d\u4e8b\uff0c \u8fd9\u662f\u4f60\u6c14\u529b\u6d88\u8017\u592a\u751a\uff0c\u4e00\u65f6\u865a\u8131\uff0c\u597d\u597d\u517b\u606f\u51e0\u5929\uff0c\u5305\u4fdd\u4f60\u6062\u590d\u5982\u521d\u3002\u201d\u5510\u8d5b\u82b1\u5e7d\u5e7d\u7684\u53f9\u4e86\u53e3 \u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u660e\u767d\u8fd9\u4ec5\u662f\u4e00\u65f6\u7684\u865a\u8131\uff0c\u5e76\u975e\u53d7\u4e86\u5185\u4f24\u3002\u4f46\u8fd9\u51e0\u65e5\u517b\u606f\uff0c\u8c01\u4eba\u4e3a\u6211\u7167 \u6599\uff1f\u201d\u91d1\u4e16\u9057\u597d\u4f3c\u77e5\u6089\u5979\u7684\u5fc3\u610f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4f84\u513f\u5e74\u9752\u529b\u58ee\uff0c\u867d\u7136\u53d7\u4e86\u70b9\u4f24\uff0c\u6599\u60f3\u4e0d\u81f3 \u6bd9\u547d\uff0c\u5012\u662f\u4f60\u8981\u5b89\u5fc3\u8c03\u6cbb\u8981\u7d27\u3002\u4f60\u522b\u77a7\u6211\u53ea\u77e5\u80e1\u95f9\uff0c\u6211\u8fd8\u9876\u4f1a\u670d\u4f8d\u4eba\u5462\u3002\u6211\u81ea\u5c0f\u505a\u60ef\u4e5e\u513f\uff0c \u5584\u4f1a\u4f3a\u5019\u4eba\uff0c\u540e\u6765\u5728\u5b64\u5c9b\u4e0a\u670d\u4f8d\u6211\u7684\u5e08\u7236\uff0c\u6211\u5e08\u7236\u4e5f\u5938\u5956\u6211\u662f\u4e2a\u5584\u77e5\u4eba\u610f\u7684\u597d\u5b69\u5b50\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u8fd9\u51e0\u53e5\u8bdd\u662f\u5e26\u7b11\u8bf4\u7684\uff0c\u5176\u4e2d\u81ea\u7136\u4e5f\u5ff5\u6709\u4e00\u79cd\u81ea\u5632\u81ea\u8bbd\u3001\u81ea\u60b2\u8eab\u4e16\u7684\u6210\u4efd\u3002\u4f46\u8bf4\u5f97 \u53c8\u662f\u6781\u4e3a\u8bda\u631a\uff0c\u5bf9\u5510\u8001\u5927\u5a46\u7684\u4e00\u4efd\u5173\u5fc3\uff0c\u662d\u7136\u82e5\u63ed\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u5e76\u975e\u81ea\u7518\u57cb\u9aa8\u96ea\u5730\uff0c\u53ea\u662f\u5979\u81ea\u5ff5\u4e0e\u91d1\u4e16\u9057\u6709\u8fc7\u90a3\u4e00\u6bb5\u8fc7\u8282\uff0c\u600e\u80fd\u51fa\u53e3\u6c42\u4ed6\u7167\u6599\u3002 \u54ea\u77e5\u91d1\u4e16\u9057\u5374\u8bda\u5fc3\u7684\u8981\u7167\u6599\u5979\u3002\u5510\u8d5b\u82b1\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u5fc3\u9053\uff1a\u201c\u5440\uff0c\u4eba\u4eba\u90fd\u53eb\u4ed6\u505a\u6bd2 \u624b\u75af\u4e10\uff0c\u539f\u6765\u4ed6\u5374\u4e5f\u6709\u4e00\u7247\u6148\u5fc3\uff0c\u771f\u662f\u51fa\u4eba\u610f\u8868\u3002\u53ea\u662f\u4ed6\u7684\u884c\u5f84\uff0c\u4e3a\u4f55\u5982\u6b64\u602a\u7edd\u4eba\u5bf0\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u679c\u7136\u6089\u5fc3\u7167\u6599\u5510\u8d5b\u82b1\uff0c\u8fc7\u4e86\u51e0\u5929\uff0c\u5510\u8d5b\u82b1\u7cbe\u795e\u6062\u590d\uff0c\u80fd\u591f\u8d70\u52a8\u4e86\uff0c\u4e24\u4eba\u56de\u53bb\u5bfb\u89c5 \u5510\u7aef\uff0c\u5510\u7aef\u88ab\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u6551\u8d77\u4e4b\u540e\uff0c\u8fd9\u65f6\u65e9\u5df2\u6d4a\u81ea\u56de\u5230\u62c9\u8428\u53bb\u4e86\uff0c\u5510\u8d5b\u82b1\u81ea\u662f\u5bfb\u4ed6\u4e0d \u7740\u3002\u5510\u8d5b\u82b1\u8fd8\u62c5\u5fc3\u4ed6\u51b7\u6bd9\u96ea\u5730\uff0c\u6316\u5f00\u4e86\u56db\u56f4\u7684\u79ef\u96ea\uff0c\u5e76\u65e0\u53d1\u73b0\u5c38\u4f53\uff0c\u8fd9\u624d\u5b89\u5fc3\u3002\u4e8e\u662f\u7ee7\u7eed\u897f \u884c\uff0c\u5bfb\u89c5\u90a3\u80e1\u50e7\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u9f99\u7075\u77eb\u5728\u7262\u4e2d\u88ab\u90a3\u80e1\u50e7\u83ab\u540d\u5176\u5999\u7684\u52ab\u8d70\uff0c\u4e00\u8def\u4e0a\u80e1\u50e7\u7528\u963f\u4fee\u7f57\u82b1\u7684\u5947\u9999\u5c06\u4ed6\u9ebb\u9189\uff0c\u4ed6\u5185 \u529f\u5df2\u6709\u706b\u5019\uff0c\u867d\u7136\u77e5\u89c9\u672a\u5931\uff0c\u80e1\u50e7\u4e0e\u5510\u8d5b\u82b1\u91d1\u4e16\u9057\u6fc0\u6597\u90a3\u4e00\u573a\u4ed6\u4e5f\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f46\u6c14\u529b\u6d88 \u5931\uff0c\u8eab\u8eaf\u9ebb\u8f6f\uff0c\u8fde\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u4e00\u8def\u4e0a\u767e\u601d\u83ab\u89e3\uff0c\u4e0d\u77e5\u90a3\u80e1\u50e7\u5bf9\u81ea\u5df1\u662f\u597d\u610f\u8fd8\u662f\u574f\u5fc3\uff1f \u3000\u3000\u9f99\u7075\u77eb\u5c31\u8fd9\u6837\u8ff7\u8ff7\u7cca\u7cca\u5730\u88ab\u90a3\u80e1\u50e7\u631f\u6301\u7740\u5728\u9a6c\u80cc\u4e0a\u8d70\u4e86\u51e0\u5929\uff0c\u7a7f\u8fc7\u4e86\u83bd\u83bd\u7684\u8349\u539f\uff0c\u5230\u4e86 \u5927\u5c71\u5e95\u4e0b\uff0c\u4f46\u89c1\u5c97\u5ce6\u8d77\u4f0f\uff0c\u7ef5\u5ef6\u65e0\u9645\uff0c\u6676\u83b9\u7684\u96ea\u5cf0\u50cf\u4e00\u6392\u6392\u767d\u7389\u96d5\u6210\u7684\u64ce\u5929\u67f1\uff0c\u9ad8\u63d2\u4e91\u9704\u3002 \u9f99\u7075\u77eb\u867d\u7136\u4e5f\u66fe\u6500\u767b\u8fc7\u8bb8\u591a\u540d\u5c71\uff0c\u4f46\u8fd9\u5ea7\u5927\u5c71\u5c71\u52bf\u7684\u96c4\u5947\u58ee\u4e3d\uff0c\u4ecd\u662f\u4ee4\u4ed6\u548b\u820c\u4e0d\u5df2\uff01\u80e1\u50e7\u5c06 \u89e3\u836f\u7ed9\u4ed6\u95fb\u4e86\uff0c\u5c71\u9876\u4e0a\u5439\u4e0b\u6765\u7684\u5bd2\u98ce\uff0c\u5939\u7740\u96ea\u82b1\uff0c\u4ee4\u4eba\u7cbe\u795e\u987f\u65f6\u6d88\u723d\u3002 \u3000\u3000\u90a3\u80e1\u50e7\u5fae\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u5954\u6ce2\u4e86\u8fd9\u51e0\u5929\uff0c\u73b0\u5728\u53ef\u4ee5\u6b47\u6b47\u5566\u3002\u201d\u8dc3\u4e0b\u9a6c\u80cc\uff0c\u9f99\u7075\u77eb\u4e5f\u8ddf\u7740 \u4e0b\u9a6c\uff0c\u51e0\u5929\u6765\u7684\u95f7\u846b\u82a6\uff0c\u6025\u987b\u6253\u7834\uff0c\u9f99\u7075\u77eb\u6b63\u60f3\u53d1\u8bdd\uff0c\u90a3\u80e1\u50e7\u5df2\u5148\u81ea\u8bf4\u9053\uff1a\u201c\u9f99\u4e09\u5148\u751f\uff0c \u4e0d\uff0c\u5e74\u5927\u5e05\u7684\u516c\u5b50\uff0c\u4f60\u5982\u4eca\u53ef\u4ee5\u6beb\u65e0\u5fe7\u8651\u5566\u3002\u6e05\u5ef7\u5c31\u662f\u518d\u6d3e\u5341\u4e07\u5927\u519b\uff0c\u4e5f\u4e0d\u80fd\u5c06\u4f60\u6293\u56de\u53bb \u4e86\uff01\u201d \u3000\u3000\u9f99\u7075\u77eb\u6014\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u6765\u5386\uff1f\u201d\u90a3\u80e1\u50e7\u7b11\u9053\uff1a\u201c\u82e5\u975e\u77e5\u9053\u4f60\u7684\u6765\u5386\uff0c\u6211 \u4e5f\u4e0d\u4f1a\u8d39\u5c3d\u5fc3\u673a\uff0c\u5077\u5165\u62c9\u8428\u6765\u6551\u4f60\u4e86\u3002\u201d\u9f99\u7075\u77eb\u9053\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u90a3\u80e1\u50e7\u7b11\u7740\u5c06\u9a6c\u97ad \u4e00\u6307\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48\uff1f\u4f60\u77a7\u2014\u2014\u201d\u9f99\u7075\u77eb\u968f\u7740\u4ed6\u97ad\u68a2\u6240\u6307\uff0c\u6781\u76ee\u8fdc\u671b\uff0c\u4f46\u89c1\u5c71\u8c37\u4e4b\u4e2d\u9690\u9690\u6709 \u5200\u5175\u4e4b\u6c14\uff0c\u6811\u6728\u8986\u76d6\u4e4b\u4e0b\uff0c\u884c\u519b\u7684\u8425\u5e10\u4ea6\u4f9d\u7a00\u53ef\u8fa8\uff0c\u9f99\u7075\u77eb\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u5420\uff0c\u4f60\u662f\u4f55 \u4eba\uff1f\u201d \u3000\u3000\u90a3\u80e1\u618e\u7b11\u9053\uff1a\u201c\u6211\u662f\u5c3c\u6cca\u5c14\u56fd\u7684\u7b2c\u4e00\u56fd\u5e08\u6cf0\u5409\u63d0\uff0c\u5949\u655d\u56fd\u56fd\u738b\u4e4b\u547d\uff0c\u9080\u8bf7\u5e74\u5148\u751f\u5171\u5546\u5927 \u8ba1\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u90a3\u80e1\u50e7\u9053\uff1a\u201c\u60f3\u4ee4\u5c0a\u5e74\u7fb9\u5c27\u5e74\u5927\u5c06\u519b\uff0c\u4e00\u751f\u620e\u9a6c\uff0c\u4e3a\u6e05\u5ef7\u5357\u5f81 \u5317\u8ba8\uff0c\u5f00\u7586\u8f9f\u571f\uff0c\u529f\u9ad8\u9707\u4e3b\uff0c\u5230\u5934\u6765\u7adf\u4e0d\u514d\u60e8\u6b7b\uff0c\u5440\uff0c\u5440\uff0c\u602a\u4e0d\u5f97\u5e74\u5148\u751f\u77e2\u5fd7\u590d\u4ec7\uff0c\u5c48\u8eab\u5e55 \u50da\uff0c\u655d\u56fd\u56fd\u738b\u5bf9\u4ee4\u5c0a\u4e4b\u6b7b\u6df1\u8868\u540c\u60c5\uff1b\u5bf9\u5148\u751f\u7684\u82e6\u5fc3\uff0c\u66f4\u662f\u65e0\u9650\u4f69\u670d\u5e7f\u9f99\u7075\u77eb\u9053\uff1a\u201c\u590d\u4ec7\u662f\u6211 \u7684\u4e8b\uff0c\u4e0e\u8d35\u56fd\u65e0\u5173\u3002\u201d\u90a3\u80e1\u50e7\u563f\u563f\u7b11\u9053\uff1a\u201c\u5e74\u5148\u751f\u867d\u7136\u7ed3\u7eb3\u4e86\u8bb8\u591a\u571f\u53f8\uff0c\u4f46\u798f\u5eb7\u5b89\u5728\u897f\u85cf\u62e5 \u6709\u91cd\u5175\uff0c\u5373\u7b97\u5e74\u5148\u751f\u80fd\u591f\u81ea\u5df1\u9003\u72f1\u4e3e\u4e8b\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u65e2\u591f\u6210\u529f\u5475\uff01\u201d \u3000\u3000\u9f99\u7075\u77eb\u4e00\u542c\u8fd9\u8bdd\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u539f\u6765\u56fd\u5e08\u662f\u529d\u6211\u5411\u8d35\u56fd\u501f\u5175\uff0c\u563f\uff0c\u5373\u7b97\u6210\u529f\uff0c\u4e5f\u4e3a\u4ed6\u4eba \u6240\u7b11\u3002\u201d\u90a3\u80e1\u50e7\u9053\uff1a\u201c\u501f\u5916\u5175\u4e4b\u529b\uff0c\u5728\u6211\u56fd\u5386\u53f2\uff0c\u4f8b\u5b50\u4f3c\u4ea6\u4e0d\u5c11\uff0c\u4f0d\u5b50\u80e5\u4e3a\u62a5\u7236\u4ec7\u501f\u5434\u56fd\u4e4b \u5175\uff0c\u706d\u6389\u695a\u56fd\u6709\u8c01\u7b11\u4ed6\uff1f\u201d\u8fd9\u80e1\u618e\u7adf\u7136\u719f\u8bfb\u4e2d\u5916\u5386\u53f2\uff0c\u5012\u662f\u5927\u51fa\u9f99\u7075\u77eb\u610f\u5916\u3002\u542c\u4e86\u6b64\u8bdd\uff0c\u5374 \u4e0d\u514d\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\uff0c\u5fc3\u9053\uff1a\u201c\u4f0d\u5b50\u80e5\u6240\u501f\u7684\u4e49\u5175\u4ea6\u662f\u4e2d\u539f\u4e4b\u4eba\uff0c\u8fd9\u5982\u4f55\u80fd\u591f\u6bd4\uff1f \u3000\u3000\u9f99\u7075\u77eb\u81ea\u77e5\u6848\u60c5\u91cd\u5927\uff0c\u8fd9\u80e1\u50e7\u8bf4\u7684\u4e43\u662f\u5b9e\u60c5\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u65e2\u5230\u6b64\u5730\uff0c\u4e0d\u5982\u5c31\u8fdb\u53bb\u770b \u770b\uff0c\u505a\u4e0d\u505a\u4f0d\u5b50\u80af\uff0c\u90a3\u53ef\u662f\u8fd8\u5f97\u7531\u6211\u3002\u201d \u3000\u3000\u559c\u9a6c\u62c9\u96c5\u5c71\u9ad8\u5165\u4e91\u9704\uff0c\u7aef\u7684\u662f\u4e00\u5c71\u4e4b\u4e2d\uff0c\u6c14\u5019\u4e0d\u9f50\uff0c\u5c71\u9876\u767d\u96ea\u7691\u7691\uff0c\u5c71\u8170\u96ea\u82b1\u7eb7\u98de\uff0c\u4f46 \u5c71\u811a\u5df1\u662f\u767e\u82b1\u7efd\u5f00\uff0c\u663e\u51fa\u521d\u6625\u666f\u8272\u3002\u5c71\u8c37\u56e0\u6709\u56db\u9762\u9ad8\u5c71\u6321\u7740\u5bd2\u98ce\uff0c\u5730\u201d\u5df1\u5c24\u5176\u6e29\u6696\uff0c\u56e0\u6b64\u5c3c \u6cca\u5c14\u519b\u5728\u5c71\u8c37\u5b89\u8425\u624e\u5be8\u3002\u9f99\u7075\u77eb\u968f\u90a3\u80e1\u50e7\u8d70\u5165\u5c71\u8c37\uff0c\u4f46\u89c1\u7bf7\u5e10\u76f8\u8fde\uff0c\u6218\u9a6c\u904d\u91ce\uff0c\u6b63\u4e2d\u4e00\u9762\u4e0a \u65d7\uff0c\u56db\u65b9\u5e05\u65d7\uff0c\u9f99\u7075\u77eb\u77e5\u9053\u5c3c\u6cca\u5c14\u519b\u5236\uff0c\u6bcf\u5341\u8425\u4e00\u6c40\uff0c\u6bcf\u8425\u4e94\u6c40\u4eba\uff0c\u7167\u6b64\u4f30\u8ba1\uff0c\u8c37\u4e2d\u6700\u5c11\u6709 \u4e94\u516d\u4e07\u4eba\u4e4b\u591a\uff0c\u4ee5\u5c3c\u6cca\u5c14\u8fd9\u6837\u7684\u5c0f\u56fd\uff0c\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u53d1\u4e86\u503e\u56fd\u4e4b\u5175\u4e86\u3002\u4f46\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u4e2d\uff0c \u5374\u8fd8\u586b\u4e0d\u6ee1\u4e00\u4e2a\u5c71\u8c37\uff0c\u9f99\u7075\u77eb\u4e00\u8def\u601d\u6f6e\u8d77\u4f0f\uff0c\u60f3\u60f3\u81ea\u5df1\u7236\u4eb2\u5f53\u5e74\u6307\u6325\u767e\u4e07\u5927\u519b\u7684\u5a01\u98ce\uff0c\u90a3\u662f \u4e0d\u80fd\u540c\u65e5\u800c\u8bed\u3002\u81ea\u5df1\u81ea\u61c2\u4eba\u4e8b\u4ee5\u6765\uff0c\u603b\u60f3\u6709\u4e00\u65e5\u80fd\u50cf\u7236\u4eb2\u4e00\u6837\u624b\u63e1\u5175\u7b26\uff0c\u800c\u4eca\u8fd9\u68a6\u60f3\u7adf\u53ef\u5b9e \u73b0\uff0c\u4f46\u5374\u6765\u5f97\u8fd9\u6837\u7a81\u7136\uff0c\u800c\u4e14\u4ee4\u4eba\u611f\u5230\u5c48\u8fb1\u3002\u9f99\u7075\u77eb\u5185\u5fc3\u4ea4\u6218\uff0c\u542c\u8c37\u4e2d\u80e1\u9a6c\u5636\u9e23\uff0c\u51e0\u4e4e\u7591\u5fc3 \u662f\u5728\u4f5c\u4e00\u573a\u6076\u68a6\u3002 \u3000\u3000\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u7ee7\u7eed\u897f\u884c\uff0c\u4e00\u8def\u5bfb\u89c5\u90fd\u4e0d\u89c1\u5510\u8d5b\u82b1\u548c\u91d1\u4e16\u9057\u7684\u8e2a\u8ff9\uff0c\u51b0\u5ddd\u4eba\u5973\u6bcf\u8fc7\u4e00 \u5929\u4fbf\u60f3\u8d77\u91d1\u809a\u9057\u751f\u547d\u7684\u671f\u9650\u53c8\u51cf\u4e00\u5929\uff0c\u5fe7\u8651\u4e4b\u60c5\uff0c\u73b0\u4e8e\u8f9e\u8272\uff0c\u5510\u7ecf\u5927\u672c\u6765\u5bf9\u91d1\u4e16\u9057\u6b8a\u65e0\u597d \u611f\uff0c\u7ecf\u8fc7\u4e86\u91d1\u4e16\u9057\u4e49\u6551\u9648\u5929\u5b87\u548c\u52c7\u6551\u5510\u8d5b\u82b1\u4e24\u4ef6\u4e8b\u60c5\uff0c\u5bf9\u91d1\u4e16\u9057\u6076\u52a3\u7684\u5370\u8c61\u624d\u6e10\u6e10\u6539\u53d8\uff0c\u4f46 \u6bcf\u60f3\u8d77\u91d1\u4e16\u9057\u5bf9\u51b0\u5ddd\u5929\u5973\u7684\u6311\u62e8\uff0c\u5fc3\u5934\u603b\u8fd8\u662f\u672a\u80fd\u91ca\u7136\uff0c\u800c\u4eca\u4e00\u8def\u4e0e\u51b0\u5ddd\u5929\u5973\u540c\u884c\uff0c\u89c1\u51b0\u5ddd \u5929\u5973\u5bf9\u91d1\u4e16\u9057\u7684\u5173\u6000\uff0c\u5c31\u5982\u540c\u5173\u5fc3\u4e00\u4e2a\u591a\u5e74\u7684\u670b\u53cb\u4e00\u6837\uff0c\u82e5\u5728\u5f80\u65f6\uff0c\u5510\u7ecf\u5929\u4e5f\u8bb8\u4f1a\u56e0\u6b64\u4e0d \u5b89\uff0c\u4f46\u5982\u4eca\u4ed6\u5df2\u719f\u6089\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u6027\u60c5\uff0c\u90a3\u7eaf\u7136\u662f\u4e00\u7247\u60b2\u5929\u60af\u4eba\u7684\u8d64\u5b50\u4e4b\u5fc3\uff0c\u76f8\u5f62\u4e4b\u4e0b\uff0c\u5510 \u7ecf\u5929\u53cd\u89c9\u5f97\u81ea\u5df1\u7684\u80f8\u895f\u72ed\u5c0f\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u5728\u8349\u539f\u4e0a\u5e76\u8f94\u5954\u9a70\uff0c\u76f8\u77e5\u66f4\u6df1\uff0c\u76f8\u7231\u66f4\u5207\uff0c\u5bd2\u98ce\u51b7\u96ea\uff0c\u90fd\u53d8\u6210\u4e86\u5d0e\u65cb\u6625\u5149\uff0c\u6bd4\u8d77\u91d1 \u4e16\u9057\u7684\u81ea\u5df1\u72ec\u884c\uff0c\u90a3\u81ea\u7136\u662f\u5927\u5f02\u5176\u8da3\u4e86\u3002 \u3000\u3000\u8d70\u4e86\u6570\u65e5\uff0c\u7a7f\u51fa\u8349\u539f\uff0c\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u96ea\u5cf0\uff0c\u5df2\u662f\u9065\u9065\u53ef\u89c1\u3002\u5c71\u8109\u9036\u8fe4\u800c\u6765\uff0c\u518d\u8d70\u4fbf\u8fdb\u5165 \u5c71\u533a\uff0c\u6cbf\u9014\u6240\u89c1\uff0c\u5947\u5cf0\u602a\u77f3\uff0c\u76ee\u4e0d\u6687\u7ed9\u3002\u5510\u7ecf\u5929\u53f9\u9053\uff1a\u201c\u4e00\u5c71\u8fd8\u6709\u4e00\u5c71\u9ad8\uff0c\u6b64\u8bdd\u771f\u662f\u4e0d\u9519\u3002 \u6211\u6240\u5c45\u4f4f\u7684\u5929\u5c71\uff0c\u7ef5\u4e98\u4e09\u5343\u91cc\uff0c\u5357\u5317\u4e8c\u9ad8\u5cf0\u76f4\u63d2\u4e91\u9704\uff0c\u6211\u4e00\u5411\u4ee5\u4e3a\u5929\u4e0b\u7684\u540d\u5c71\uff0c\u518d\u4e5f\u4e0d\u80fd\u4e0e \u4e4b\u76f8\u6bd4\u4e86\uff0c\u54ea\u77e5\u8fd8\u6709\u8fd9\u5ea7\u559c\u9a6c\u62c9\u96c5\u5c71\uff01\u201d \u3000\u3000\u8349\u539f\u79ef\u96ea\u672a\u5316\uff0c\u5728\u8349\u539f\u7684\u8fb9\u7f18\uff0c\u5c71\u8109\u8d77\u4f0f\u4e2d\u65ad\u4e4b\u5904\uff0c\u6709\u4e00\u4e2a\u5ced\u7acb\u5982\u58c1\u7684\u5b64\u5cf0\uff0c\u5341\u5206\u5947 \u7279\uff0c\u597d\u50cf\u662f\u4e00\u4e2a\u7855\u5927\u65e0\u670b\u7684\u660e\u955c\uff0c\u53c8\u50cf\u4e00\u652f\u5e73\u5730\u6d8c\u8d77\u7684\u7389\u66ff\uff0c\u4e0e\u5468\u56f4\u7684\u5c71\u5cf0\uff0c\u5f62\u6001\u5927\u5927\u4e0d \u540c\uff0c\u51b0\u5ddd\u5929\u5973\u5567\u5567\u79f0\u8d4f\uff0c\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u597d\u50cf\u53d1\u73b0\u4e86\u4e00\u6869\u6781\u5176\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u9762 \u8272\u7d27\u5f20\u4e4b\u6781\uff0c\u7acb\u5373\u8df3\u4e0b\u9a6c\u6765\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u773c\u77a5\u53bb\uff0c\u90a3\u5b64\u5cf0\u50cf\u4e00\u5757\u767d\u7389\u96d5\u6210\u7684\u660e\u955c\uff0c\u5728\u5c71\u5cf0\u4e0b\u9762\u7684\u201c\u955c\u53f0\u201d\u4e0a\uff0c\u4f46\u89c1\u8840 \u8ff9\u6591\u6591\uff0c\u6781\u5176\u593a\u76ee\uff0c\u51b0\u5ddd\u5929\u5973\u4e5f\u4e0d\u7981\u5947\u9053\uff1a\u201c\u54a6\uff0c\u96be\u9053\u662f\u91d1\u4e16\u9057\u4e0e\u90a3\u80e1\u50e7\u53c8\u5728\u6b64\u5730\u6fc0\u6218\u8fc7 \u6765\uff1f\u662f\u8c01\u6d41\u4e86\u8fd9\u4e48\u591a\u9c9c\u8840\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u9c9c\u8840\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u5927\u4e3a\u8be7\u5f02\uff0c\u53eb\u9053\uff1a\u201c\u8fd9 \u6837\u5f53\u773c\uff0c\u4f60\u4e5f\u770b\u4e0d\u89c1\u4e48\uff1f\u201d\u5ffd\u89c1\u5510\u7ecf\u5929\u5b9a\u4e86\u795e\u4e00\u822c\uff0c\u51dd\u7738\u4e0a\u671b\uff0c\u51b0\u5ddd\u5929\u5973\u5b9a\u6674\u4e00\u770b\uff0c\u53ea\u89c1\u90a3 \u77f3\u5cf0\u4e0a\u7adf\u4f3c\u6709\u51e0\u884c\u5b57\u8ff9\uff0c\u8fd9\u4e00\u53d1\u73b0\uff0c\u6bd4\u90a3\u8840\u8ff9\u66f4\u4ee4\u4eba\u60ca\u5947\uff0c\u50cf\u8fd9\u6837\u5e73\u6ed1\u5982\u955c\u7684\u77f3\u5cf0\uff0c\u53ea\u6015\u82cd \u8747\u722c\u4e0a\u53bb\u4e5f\u4f1a\u8dcc\u4e0b\u6765\uff0c\u5c45\u7136\u6709\u4eba\u80fd\u5728\u4e0a\u9762\u5199\u5b57\uff0c\u8fd9\u5b57\u8ff9\u53c8\u662f\u7528\u4ec0\u4e48\u5199\u7684\uff1f\u65e0\u602a\u5510\u7ecf\u5929\u4e00\u53d1\u73b0 \u8fd9\u5b57\u8ff9\uff0c\u5c31\u65e0\u5fc3\u7559\u610f\u4e0b\u9762\u7684\u8840\u8ff9\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u8fd1\u90a3\u5ea7\u5b64\u5cf0\uff0c\u53ea\u89c1\u90a3\u51e0\u884c\u5b57\u8ff9\u4e43\u662f\u4e00\u9996\u4e03\u8a00\u7edd\u53e5\uff0c\u8bd7\u9053\uff1a\u201c\u51e0\u5ea6\u5929\u5c71\u6500\u6842\u5b50\uff0c\u800c \u4eca\u53cc\u5251\u4e0a\u73e0\u5cf0\u3002\u540d\u5c71\u6b64\u5904\u5f00\u4ed9\u5883\uff0c\u5fcd\u4ee4\u80e1\u9a91\u8840\u67d3\u7ea2\uff01\u201d\u6bcf\u4e2a\u5b57\u90fd\u6709\u5c3a\u8bb8\u5927\u5c0f\uff0c\u94c1\u5212\u94f6\u94a9\uff0c\u5165 \u77f3\u6570\u5206\uff0c\u7528\u65a7\u51ff\u4e0d\u5f97\u5982\u6b64\u9f50\u6574\uff0c\u51b0\u5ddd\u5929\u5973\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u53eb\u9053\uff1a\u201c\u5929\u4e0b\u6709\u8c01\u6709\u8fd9\u6837\u7684 \u7684\u529f\u592b\uff1f\u8fd9\u662f\u7528\u6307\u5934\u4e66\u5199\u7684\uff01\u201d \u3000\u3000\u53ea\u89c1\u5510\u7ecf\u5929\u6ee1\u9762\u8654\u656c\u7684\u795e\u6c14\uff0c\u6162\u6162\u8d70\u5230\u77f3\u5cf0\u4e0b\u9762\uff0c\u7a81\u7136\u53e3\u558a\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7239\u7239\u5199\u7684\uff01\u201d \u51b0\u5ddd\u5929\u5973\u9053\uff0c\u201c\u4f60\u7239\u7239\u5199\u7684\uff1f\u4ed6\u4e0d\u662f\u5728\u5929\u5c71\u5417\uff1f\u201d\u4e00\u5480\u56bc\u8bd7\u610f\uff0c\u9664\u4e86\u5510\u6653\u6f9c\uff0c\u786e\u662f\u65e0\u4eba\u914d\u9898 \u8fd9\u6837\u7684\u8bd7\u53e5\u3002\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u7167\u6b64\u8bd7\u770b\u6765\uff0c\u4f60\u7236\u6bcd\u90fd\u540c\u6765\u4e86\u3002\u4ed6\u4eec\u4e0a\u559c\u9a6c\u62c9\u96c5\u5c71\u505a\u4ec0\u4e48\uff1f\u201d \u5510\u7ecf\u5929\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u6211\u7239\u7239\u4e8c\u5341\u5e74\u6765\u4e0d\u52a8\u5200\u5251\uff0c\u600e\u4e48\u5728\u6b64\u5730\u7834\u6212\u4f24\u4eba\uff1f\u201d\u8981\u77e5\u5510\u6653\u6f9c\u4e0e\u51af \u745b\u592b\u5987\u8fde\u624b\uff0c\u90a3\u662f\u5929\u4e0b\u65e0\u4eba\u80fd\u654c\uff0c\u8fd9\u5c71\u5cf0\u4e0b\u9762\u7684\u8840\u8ff9\u5f53\u7136\u662f\u522b\u4eba\u7684\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u65bd\u5c55\u58c1\u864e\u6e38\u5899\u7684\u529f\u592b\uff0c\u5411\u4e0a\u6162\u6162\u632a\u52a8\u6570\u4e08\uff0c\u51b0\u5ddd\u5929\u5973\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u90a3\u5757\u77f3\u5934\u597d \u4f3c\u6709\u4e9b\u677e\u52a8\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e0d\u59a8\u3002\u82e5\u662f\u6b64\u5904\u4e0d\u7a33\u5f53\uff0c\u6211\u7239\u7239\u5b9a\u4f1a\u7559\u4e0b\u8bb0\u53f7\u3002\u201d\u6709\u4e00\u5757\u5c16\u77f3 \u659c\u63d2\u51fa\u6765\uff0c\u77f3\u6839\u4e0e\u5c71\u5cf0\u7684\u672c\u4f53\u76f8\u8fde\uff0c\u5510\u7ecf\u5929\u7684\u8f7b\u529f\u867d\u7136\u5df2\u5230\u4e86\u4e00\u6d41\u5883\u754c\uff0c\u4f46\u624b\u8db3\u6beb\u65e0\u53ef\u4ee5\u7740 \u529b\u4e4b\u70b9\uff0c\u4e5f\u81ea\u89c9\u5f97\u75b2\u7d2f\u4e0d\u582a\uff0c\u4e50\u5f97\u6709\u4e00\u5757\u51f8\u51fa\u7684\u5c16\u77f3\u53ef\u4ee5\u6500\u63f4\uff0c\u4e58\u673a\u6b47\u606f\uff0c\u51b0\u5ddd\u5929\u5973\u53c8\u53eb \u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8f70\u9686\u4e00\u58f0\uff0c\u90a3\u5757\u77f3\u5934\u7a81\u7136\u4e2d\u65ad\uff0c\u98de\u5760\u4e0b\u6765\uff0c\u4e24\u8fb9\u77f3\u5c51\u7eb7 \u98de\uff0c\u51b0\u5ddd\u5929\u5973\u98de\u8eab\u6025\u8d77\uff0c\u4f46\u89c1\u5510\u7ecf\u5929\u53cd\u811a\u4e00\u6491\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u8eab\u5982\u79bb\u5f26\u4e4b\u7bad\uff0c\u5411\u4e0b\u75be\u5c04\uff0c\u90a3\u5757 \u5927\u77f3\u98de\u5760\u4e4b\u52bf\u731b\u901f\u4e4b\u6781\uff0c\u5e78\u559c\u5510\u7ecf\u5929\u7684\u53bb\u52bf\u6bd4\u77f3\u5757\u66f4\u901f\uff0c\u770b\u6765\u4f3c\u662f\u4eba\u77f3\u540c\u5760\uff0c\u7ec8\u4e8e\u90a3\u5757\u5927\u77f3 \u5728\u8ddd\u79bb\u5510\u7ecf\u5929\u80cc\u540e\u5fc3\u4e0d\u5230\u4e00\u5c3a\u4e4b\u65f6\uff0c\u5510\u7ecf\u5929\u8eab\u5f62\u4fa7\u5c04\uff0c\u90a3\u5757\u77f3\u5934\u8d8a\u8fc7\u4ed6\u7684\u5934\u9876\uff0c\u6d41\u661f\u95ea\u7535\u822c \u7684\u5411\u4e0b\u6025\u964d\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u60ca\u9b42\u672a\u5b9a\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u8f70\u7684\u4e00\u58f0\uff0c\u4e24\u5339\u9a6c\u51c4\u5389\u60e8\u53eb\uff0c\u51b0\u5ddd\u5929\u5973\u4e00 \u770b\uff0c\u539f\u6765\u8fd9\u4e24\u5339\u4ece\u62c9\u8428\u9a91\u6765\u7684\u5065\u9a6c\uff0c\u9003\u907f\u4e0d\u53ca\uff0c\u5df2\u662f\u7ed9\u5927\u77f3\u538b\u6bd9\u3002\u51b0\u5ddd\u5929\u5973\u751a\u662f\u75db\u5fc3\uff0c\u6025\u5fd9 \u53bb\u770b\u5510\u7ecf\u5929\u65f6\uff0c\u4f46\u89c1\u5510\u7ecf\u5929\u9762\u5982\u767d\u7eb8\uff0c\u4ee5\u624b\u6491\u5730\uff0c\u53cc\u817f\u4e0a\u6ee1\u662f\u8840\u75d5\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e00\u628a\u5c06\u4ed6\u6402\u4f4f\uff0c\u6cea\u73e0\u4e00\u9897\u9897\u7684\u6eda\u4e0b\u6765\uff0c\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u50bb\u516c\u4e3b\uff0c\u4f60\u54ed\u4ec0\u4e48\uff1f\u6211 \u7684\u817f\u6ca1\u6709\u65ad\uff0c\u817f\u82e5\u662f\u65ad\u4e86\uff0c\u4f60\u54ed\u4e5f\u6ca1\u6709\u7528\u3002\u201d\u51b0\u5ddd\u5929\u5973\u4e00\u770b\uff0c\u817f\u4e0a\u6240\u53d7\u7684\u4f24\u8fd8\u771f\u4e0d\u8f7b\uff0c\u88ab\u788e \u88c2\u7684\u77f3\u7247\u5272\u4f24\u7684\u76ae\u8089\u6d6e\u4f24\u4e0d\u7b97\uff0c\u8fd8\u7ed9\u9707\u7206\u4e86\u4e24\u6761\u7b4b\u8109\uff0c\u5e78\u800c\u6ca1\u6709\u65ad\u4e86\u9aa8\u5934\u3002\u51b0\u5ddd\u5929\u5973\u6697\u6697\u4f69 \u670d\u5510\u7ecf\u5929\u5e94\u53d8\u7684\u673a\u7075\uff0c\u5728\u5927\u77f3\u98de\u5760\u4e4b\u65f6\uff0c\u5510\u7ecf\u5929\u90a3\u4e00\u811a\u53cd\u6491\uff0c\u6070\u5230\u597d\u5904\uff0c\u4e00\u65b9\u9762\u52a0\u901f\u4e86\u81ea\u5df1 \u8eab\u4f53\u7684\u53bb\u52bf\uff0c\u4e00\u65b9\u9762\u963b\u51cf\u4e86\u90a3\u77f3\u5757\u7684\u98de\u5760\u4e4b\u52bf\uff0c\u8981\u4e0d\u7136\u65e9\u7ed9\u90a3\u77f3\u5757\u8ffd\u4e0a\u538b\u6bd9\u4e86\u3002\u51b0\u5ddd\u5929\u5973\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6b66\u6797\u5404\u6d3e\u90fd\u5949\u5929\u5c71\u6d3e\u4e3a\u5185\u5bb6\u6b63\u5b97\uff0c\u5510\u7ecf\u5929\u6bd4\u6211\u5927\u4e0d\u4e86\u51e0\u5c81\uff0c\u5185\u529f\u5c31\u6bd4\u6211\u6df1 \u539a\u5f88\u591a\uff0c\u90a3\u5757\u5927\u77f3\u91cd\u903e\u5343\u65a4\uff0c\u4ed6\u5c45\u7136\u6562\u786c\u78b0\u4e00\u4e0b\uff0c\u4e5f\u4e0d\u8fc7\u4f24\u4e86\u4e24\u6761\u7b4b\u8109\u800c\u5df2\uff0c\u770b\u6765\u82e5\u662f\u597d\u597d \u8c03\u6cbb\uff0c\u4e0d\u8fc7\u4e09\u5929\uff0c\u4fbf\u53ef\u6062\u590d\u5982\u521d\u3002\u201d \u3000\u3000\u4f46\u89c9\u5510\u7ecf\u5929\u7684\u6c14\u606f\u597d\u4f3c\u67d4\u548c\u7684\u6625\u98ce\uff0c\u8f7b\u62c2\u4e91\u9b13\uff0c\u8138\u4e0a\u611f\u5230\u6709\u70b9\u70ed\u547c\u547c\u7684\uff0c\u80f8\u819b\u6709\u4e00\u80a1\u4ee4 \u4eba\u900f\u4e0d\u8fc7\u6c14\u6765\u7684\u538b\u529b\uff0c\u96be\u53d7\u4e4b\u6781\uff0c\u53c8\u201c\u8212\u670d\u201d\u6781\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u76fc\u4e0a\u4e00\u70ed\uff0c\u8f7b\u8f7b\u5c06\u5510\u7ecf\u5929\u63a8 \u5f00\uff0c\u5510\u7ecf\u5929\u5374\u50cf\u5c0f\u5b69\u5b50\u6492\u5a07\u4e00\u6837\u53cd\u9760\u8fc7\u6765\uff0c\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u6211\u7684\u817f\u65ad\u5566\uff0c\u4eca\u540e\u6c38\u8fdc\u79bb\u4e0d\u5f00 \u4f60\uff0c\u8981\u4f60\u6276\u6211\u4e00\u4e00\u751f\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7ed9\u4ed6\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u53c8\u7ed9\u4ed6\u5403\u4e86\u4e00\u9897\u516d\u9633\u4e38\uff0c\u8fd9\u662f\u51b0\u5bab\u4e2d\u7684\u5999\u836f\uff0c\u529f\u80fd\u56fa\u672c\u57f9 \u5143\uff0c\u5979\u4e00\u9762\u670d\u5f85\u5510\u7ecf\u5929\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u600e\u7684\uff0c\u6211\u4e00\u6025\u5c31\u4f1a\u6d41\u6cea\uff0c\u6709\u4e00\u6b21\u6211\u517b\u7684\u9e66\u9e49\u6298\u4e86 \u7fc5\u8180\uff0c\u6211\u4e5f\u54ed\u4e86\u4e00\u573a\u3002\u6211\u4eec\u5c3c\u6cca\u5c14\u6709\u4e00\u4e2a\u795e\u8bdd\u6545\u4e8b\uff0c\u8bf4\u6709\u4e00\u4e2a\u516c\u4e3b\uff0c\u5979\u6240\u949f\u60c5\u7684\u738b\u5b50\uff0c\u7ed9\u5973 \u5deb\u7528\u9b54\u6cd5\u5f04\u6b7b\u4e86\uff0c\u6b63\u8981\u4e0b\u846c\uff0c\u516c\u4e3b\u8d76\u5230\uff0c\u4f0f\u5728\u4ed6\u8eab\u4e0a\u5927\u54ed\u4e00\u573a\uff0c\u6cea\u6c34\u6da6\u6e7f\u4e86\u5979\u7684\u5fc3\u5934\uff0c\u738b\u5b50 \u5c31\u82cf\u9192\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u54c8\uff01\u90a3\u4e48\u662f\u6211\u8bf4\u9519\u4e86\uff0c\u516c\u4e3b\u7684\u773c\u6cea\u679c\u7136\u6709\u7528\u7684\uff0c\u4e0d\u4f46\u817f\u65ad\u4e86\u53ef\u533b\uff0c \u6b7b\u4e86\u4e5f\u80fd\u590d\u6d3b\u3002\u6709\u4f60\u5728\u6211\u8eab\u65c1\uff0c\u6211\u7684\u798f\u6c14\u5c82\u4e0d\u662f\u6bd4\u90a3\u795e\u8bdd\u4e2d\u7684\u738b\u5b50\u8fd8\u597d\u5f97\u591a\uff01\u201d\u51b0\u5ddd\u5929\u5973\u55d4 \u9053\uff1a\u201c\u51e0\u65f6\u5b66\u5f97\u8fd9\u6837\u6cb9\u5634\u6ed1\u820c\uff1f\u201d\u8f7b\u8f7b\u7684\u6253\u4ed6\u4e00\u4e0b\uff0c\u5fc3\u4e2d\u5374\u662f\u5145\u6ee1\u871c\u7231\u8f7b\u601c\uff01 \u3000\u3000\u5510\u7ecf\u5929\u5ffd\u9053\uff1a\u201c\u5947\u602a\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u90a3\u5757\u77f3\u5934\uff01\u201d\u51b0\u5ddd\u5927\u5973 \u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u662f\u5475\uff01\u90a3\u5757\u77f3\u5934\u600e\u7684\u4f1a\u65e0\u7aef\u7aef\u5760\u4e0b\u6765\u3002\u4f60\u4e14\u8eba\u4e00\u4f1a\u513f\u3002\u201d\u5230\u77f3\u5cf0\u4e0b\u9762\u4e00 \u671b\uff0c\u4f46\u89c1\u539f\u5148\u4e0e\u90a3\u5757\u5927\u77f3\u76f8\u8fde\u7684\u77f3\u5934\uff0c\u4f3c\u662f\u7ed9\u4eba\u7528\u5200\u65a7\u524a\u8fc7\uff0c\u50cf\u814a\u70db\u6746\u4e00\u6837\uff0c\u51b0\u5ddd\u5929\u5973\u722c\u4e0a \u53bb\u4e00\u6478\uff0c\u65c1\u8fb9\u7684\u6ce5\u571f\u4e5f\u662f\u677e\u677e\u8f6f\u8f6f\u7684\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u7ed9\u4eba\u5f04\u4e86\u624b\u811a\uff0c\u4f46\u5374\u5e03\u7f6e\u5f97\u90a3\u4e48\u5de7\u5999\uff0c\u8981 \u4e0d\u662f\u77f3\u5934\u5df2\u7ecf\u5760\u4e0b\uff0c\u51c6\u4e5f\u4f1a\u4ee5\u4e3a\u90a3\u5757\u575a\u77f3\uff0c\u662f\u77f3\u5cf0\u7684\u4e00\u4f53\u3002\u51b0\u5ddd\u5929\u5973\u5927\u4e3a\u5947\u602a\uff0c\u8fd9\u9677\u997c\u5e03\u5f97 \u9634\u6bd2\u4e4b\u6781\uff0c\u7edd\u4e0d\u4f1a\u662f\u5510\u6653\u6f9c\u6240\u4e3a\uff0c\u800c\u4e14\u5b9a\u7136\u662f\u5510\u6653\u6f9c\u79bb\u5f00\u4e4b\u540e\uff0c\u522b\u4eba\u624d\u6562\u4f5c\u7684\u3002\u4ed6\u4e3a\u4ec0\u4e48\u8981 \u5982\u6b64\u5e03\u7f6e\uff1a\u96be\u9053\u662f\u9884\u6599\u5230\u6709\u4eba\u722c\u4e0a\u53bb\u770b\u5510\u6653\u6f9c\u7684\u9898\u8bd7\u4e48\uff1f \u3000\u3000\u5510\u7ecf\u5929\u4e5f\u662f\u731c\u60f3\u4e0d\u900f\u3002\u51b0\u5ddd\u5929\u5973\u6276\u7740\u4ed6\u5728\u96ea\u5730\u4e0a\u6162\u6162\u7684\u8d70\uff0c\u5e78\u559c\u8d70\u6ca1\u4e45\uff0c\u4fbf\u53d1\u73b0\u4e86\u4e00\u5ea7 \u53e4\u4ee3\u9057\u7559\u4e0b\u6765\u7684\u201c\u70fd\u706b\u53f0\u201d\uff0c\u90a3\u662f\u4e00\u5ea7\u597d\u50cf\u7889\u5821\u7684\u5efa\u7b51\u3002 \u3000\u3000\u53e4\u4ee3\u4ea4\u901a\u4e0d\u4fbf\uff0c\u7528\u70fd\u706b\u4f20\u9012\u519b\u60c5\u6d88\u606f\uff0c\u5728\u8fb9\u7586\u5730\u65b9\uff0c\u66f4\u662f\u5e38\u89c1\uff0c\u5c24\u5176\u5728\u897f\u85cf\u4e0e\u5370\u5ea6\u3001\u5c3c \u6cca\u5c14\u7b49\u56fd\u63a5\u58e4\u7684\u8fb9\u533a\uff0c\u76ee\u8fd9\u79cd\u4f20\u9012\u519b\u60c5\u7684\u529e\u6cd5\uff0c\u4e00\u76f4\u4fdd\u7559\u81f3\u6e05\u4ee3\u4e2d\u53f6\uff0c\u4e0d\u8fc7\u8fd9\u5ea7\u70fd\u706b\u53f0\u6ce5\u571f \u5265\u843d\u3002\u77f3\u57fa\u663e\u9732\uff0c\u5374\u662f\u4eba\u5df1\u5e9f\u5f03\u7684\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u6276\u5510\u7ecf\u5927\u8fdb\u53bb\u6b47\u606f\uff0c\u7b11\u9053\uff1a\u201c\u80fd\u591f\u906e\u853d\u5165\u96e8 \u4fbf\u597d\u3002\u4f60\u53ef\u4ee5\u5728\u8fd9\u91cc\u8c03\u517b\u51e0\u5929\u3002\u201d \u3000\u3000\u201c\u70fd\u706b\u53f0\u201d\u6709\u4e24\u5c42\u5efa\u7b51\uff0c\u4e0a\u5c16\u4e0b\u5bbd\uff0c\u4e0b\u9762\u662f\u201c\u7741\u671b\u53f0\u201d\uff0c\u4e0b\u9762\u5219\u662f\u5175\u58eb\u7684\u6b47\u5bbf\u4e4b\u6240\uff0c\u51b0 \u5ddd\u5929\u5973\u5c06\u5730\u65b9\u6253\u626b\u5e72\u51c0\uff0c\u670d\u4f8d\u5510\u7ecf\u5929\u8eba\u4e0b\u6b47\u606f\uff0c\u53c8\u51fa\u5916\u53bb\u730e\u4e86\u4e24\u53ea\u96ea\u9e21\u56de\u6765\uff0c\u5510\u7ecf\u5929\u5fc3\u4e2d\u6697 \u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u524d\u4eba\u8bd7\u9053\uff1a\u6700\u96be\u6d88\u53d7\u7f8e\u4eba\u6069\uff0c\u4fbf\u662f\u591a\u6298\u51e0\u5e74\u5bff\u547d\uff0c\u6211\u4e5f\u60c5\u613f\u3002\u201d\u4f46\u5728\u51b0\u5ddd\u5929\u5973 \u7684\u7ec6\u5fc3\u7167\u6599\u4e4b\u4e0b\uff0c\u52a0\u4e0a\u5979\u7684\u51b0\u5bab\u7075\u836f\uff0c\u5510\u7ecf\u5929\u5c31\u662f\u60f3\u591a\u75c5\u51e0\u5929\u4e5f\u4e0d\u80fd\u591f\uff0c\u7b2c\u4e8c\u5929\u4f24\u53e3\u4fbf\u5df1\u5408 \u62e2\uff0c\u7b2c\u4e09\u751f\u51fa\u65b0\u7684\u808c\u8089\uff0c\u770b\u6765\u518d\u8fc7\u4e00\u5929\uff0c\u5c31\u53ef\u4ee5\u5b8c\u5168\u6062\u590d\u4e86\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u51b0\u5ddd\u5929\u5973\u53c8\u730e\u4e86\u4e00\u53ea\u5c0f\u9ec4\u7f8a\u56de\u6765\uff0c\u70e4\u7ed9\u5510\u7ecf\u5929\u5403\u3002\u51b0\u5ddd\u5929\u5973\u81ea\u5c0f\u6709\u4eba\u670d\u4f8d\uff0c\u5bf9\u70f9 \u6266\u70e7\u70e4\u7684\u6280\u672f\u3002\u7b80\u76f4\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u4f46\u7ecf\u5979\u7684\u624b\u5f04\u51fa\u6765\u7684\u4e1c\u4e24\uff0c\u5510\u7ecf\u5929\u5403\u5728\u5634\u91cc\uff0c\u751c\u5728\u5fc3\u91cc\uff0c \u7eb5\u662f\u70e4\u7126\u70e7\u6d53\u3002\u5510\u7ecf\u5929\u4e5f\u89c9\u5f97\u90a3\u662f\u5929\u4e0b\u81f3\u7f8e\u4e4b\u5473\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u8df3\u4e0a\u201c\u4e86\u671b\u53f0\u201d\u53bb\u770b\u6708\u4eae\uff0c\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u51b0\u5cf0\u53cd\u7167\u4e4b\u4e0b\uff0c\u6708\u5149\u4e5f\u5e26 \u6709\u51b7\u610f\uff0c\u663e\u5f97\u6781\u5176\u6e05\u4eae\u3002\u51b0\u5ddd\u5929\u5973\u5ffd\u7136\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5728\u5c71\u7684\u90a3\u4e00\u8fb9\uff0c\u4fbf\u662f\u6211\u6bcd\u4eb2 \u7684\u6545\u56fd\u4e86\u3002\u53ef\u7b11\u6211\u867d\u627f\u7ee7\u4e86\u6211\u6bcd\u4eb2\u7684\u516c\u4e3b\u5c01\u53f7\u3001\u5374\u65e0\u7f18\u8df3\u4e0a\u5c3c\u6cca\u5c14\u7684\u56fd\u571f\u3002\u201d\u5510\u7ecf\u5927\u7b11\u9053\uff1a \u201c\u4f60\u82e5\u8981\u53bb\uff0c\u8c01\u80fd\u963b\u4f60\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u5f53\u5e74\u4f24\u5fc3\u4e4b\u6781\uff0c\u79bb\u4e61\u53bb\u56fd\uff0c\u907f\u4e16\u51b0\u5cf0\uff0c\u66fe \u53d1\u8a93\u4e0d\u5c65\u6545\u571f\u3002\u201d\u5510\u7ecf\u5929\u5fae\u7b11\u9053\uff1a\u201c\u6ca7\u6851\u53d8\u5e7b\uff0c\u8fde\u51b0\u5cf0\u4e5f\u5012\u584c\u4e86\uff0c\u4eba\u4e8b\u53c8\u600e\u80fd\u9884\u6d4b\uff1f\u201d\u51b0\u5ddd \u5929\u5973\u60f3\u8d77\u76ee\u4e0b\u4fbf\u6709\u4e3a\u96be\u4e4b\u4e8b\uff0c\u63ea\u7136\u4e0d\u4e50\u3002\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u82e5\u662f\u4f60\u7684\u8868\u54e5\u5b9a\u8981\u5a36\u4f60\uff0c\u4f60\u60f3\u4e0d\u56de \u4e61\u4e5f\u4e0d\u6210\u5566\u3002\u201d\u51b0\u5ddd\u5929\u5973\u55d4\u9053\uff1a\u201c\u4ec0\u4e48\u8868\u54e5\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5c3c\u6cca\u5c14\u73b0\u5728\u7684\u56fd\u738b\u4e0d\u662f\u4f60\u7684\u8868 \u54e5\u5417\uff0c\u55ef\uff0c\u6211\u770b\u90a3\u80e1\u50e7\u9003\u5165\u559c\u739b\u62c9\u96c5\u5c71\u533a\uff0c\u53ea\u6015\u771f\u662f\u5982\u4f60\u6240\u6599\uff0c\u4e43\u662f\u5c3c\u6cca\u5c14\u56fd\u738b\u6d3e\u4ed6\u6765 \u7684\u3002\u201d\u51b0\u5ddd\u5927\u5973\u9053\uff1a\u201c\u9664\u5f00\u662f\u4f60\uff0c\u6211\u600e\u80af\u4e0e\u7b2c\u4e8c\u4e2a\u7537\u5b50\u76f8\u5904\uff0c\u83ab\u8bf4\u662f\u5c3c\u6cca\u5c14\u56fd\u738b\uff0c\u4fbf\u662f\u7389\u7687 \u5927\u5e1d\u8feb\u6211\u4e5f\u4e0d\u6210\u3002\u201d\u51b0\u5ddd\u5929\u5973\u7684\u7231\u610f\u7b2c\u4e00\u6b21\u8fd9\u6837\u660e\u663e\u7684\u8868\u9732\u51fa\u6765\uff0c\u5510\u7ecf\u5929\u559c\u6781\u6cea\u4e0b\uff0c\u9053\uff1a \u201c\u4f60\u771f\u7684\u8fd9\u6837\u770b\u5f97\u8d77\u6211\u4e48\uff1f\u201d\u8f7b\u6402\u51b0\u5ddd\u5929\u5973\u9999\u80a9\uff0c\u51b0\u5ddd\u5929\u5973\u80a9\u5934\u4e00\u7f29\uff0c\u8f7b\u8f7b\u62e8\u5f00\u5510\u7ecf\u5929\u7684\u624b \u6307\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8bb8\u6211\u54ed\uff0c\u600e\u4e48\u4f60\u81ea\u5df1\u53c8\u54ed\u4e86\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u6c89\u91cd\u7684\u811a\u6b65\u58f0\u8d70\u8fdb\uff0c\u70fd\u706b\u53f0\u4e0a\u4e0b\u4e24\u5c42\u6709\u6d3b\u52a8\u7684\u697c\u677f\u9694\u5f00\uff0c\u53ef\u4ee5\u5c06\u4e0b\u9762\u7684\u4eba\u540a \u4e0a\uff0c\u540a\u7ef3\u65e9\u5df2\u8150\u70c2\uff0c\u5510\u7ecf\u5929\u719f\u8bfb\u53f2\u4e66\uff0c\u77e5\u9053\u8fd9\u79cd\u70fd\u706b\u53f0\u7684\u5efa\u7b51\u5f0f\u6837\uff0c\u521a\u624d\u662f\u4e0e\u51b0\u5ddd\u5929\u5973\u65bd\u5c55 \u8f7b\u529f\uff0c\u786c\u628a\u697c\u677f\u63ed\u5f00\uff0c\u8df3\u4e0a\u53bb\u7684\u3002\u5510\u7ecf\u5927\u542c\u5f97\u4eba\u58f0\uff0c\u6025\u5fd9\u5c06\u697c\u677f\u76d6\u597d\uff0c\u7b11\u9053\uff1a\u201c\u5982\u6b64\u6df1\u591c\uff0c \u4e14\u770b\u662f\u4ec0\u4e48\u53e4\u602a\u7684\u5ba2\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u968f\u624b\u5c06\u51b0\u5251\u4e00\u5212\uff0c\u5728\u697c\u677f\u4e0a\u523a\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u5b54\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u602a\u58f0\u602a\u6c14\u7684\u53eb\u9053\uff1a \u201c\u54c8\uff0c\u8fd9\u91cc\u5c45\u7136\u6709\u70e4\u719f\u7684\u7f8a\u8089\uff01\u4eba\u5374\u8d70\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u201d\u6b63\u662f\u8d64\u795e\u5b50\u7684\u53e3\u97f3\u3002\u53e6\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a \u201c\u6211\u548c\u5c1a\u52df\u5316\u5341\u65b9\uff0c\u6709\u4e3b\u513f\u7684\u4e1c\u897f\u6211\u90fd\u8981\u52df\u5316\u5230\u624b\uff0c\u4f55\u51b5\u662f\u65e0\u4e3b\u4e4b\u7269\u3002\u54c8\uff0c\u54c8\uff01\u6211\u4eec\u5403\u4e86\u518d \u8bf4\u3002\u201d\u5510\u7ecf\u5929\u4ece\u5c0f\u5b54\u4e2d\u671b\u4e0b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u53c8\u9ad8\u53c8\u7626\u7684\u548c\u5c1a\u624b\u821e\u8db3\u8e48\u7684\u8d70\u5728\u524d\u5934\uff0c\u624b\u81c2\u78b0\u5230\u6446 \u7740\u70e4\u7f8a\u7684\u77f3\u6848\uff0c\u7adf\u7136\u53d1\u51fa\u4e00\u79cd\u91d1\u5c5e\u7684\u9576\u9abc\u4e4b\u58f0\u3002\u5510\u7ecf\u5929\u8ba4\u5f97\u8d64\u795e\u5b50\uff0c\u5374\u4e0d\u8ba4\u5f97\u4e0e\u4ed6\u540c\u6765\u7684\u8fd9 \u4e2a\u8463\u592a\u6e05\u3002\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u4e00\u4e2a\u8d64\u795e\u5b50\u5df1\u662f\u624e\u624b\uff0c\u8fd9\u548c\u5c1a\u4e5f\u90aa\u95e8\u5f97\u7d27\uff0c\u504f\u504f\u6211\u7684\u817f\u4f24\u8fd8 \u672a\u75ca\u6108\u3002\u201d\u4f38\u624b\u638f\u51fa\u5929\u5c71\u795e\u8292\uff0c\u51b0\u5ddd\u5929\u5973\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u7406\u4ed6\uff0c\u4e14\u5f85\u4ed6\u4eec\u627e\u5230\u5934\u4e0a\u518d \u8bf4\u3002\u201d\u51b0\u5ddd\u5929\u5973\u7684\u5fc3\u91cc\u6b63\u5145\u6ee1\u871c\u610f\u67d4\u60c5\uff0c\u7eb5\u8bb8\u5510\u7ecf\u5929\u6ca1\u6709\u53d7\u4f24\uff0c\u8fd9\u65f6\u5df2\u4e5f\u4e0d\u6b32\u53ae\u6740\u3002 \u3000\u3000\u8d64\u795e\u5b50\u5403\u4e86\u4e24\u53e3\u7f8a\u8089\uff0c\u76b1\u7740\u76fe\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u70e4\u7f8a\u7684\u4eba\u7b80\u76f4\u662f\u4e2a\u7b28\u86cb\uff0c\u4e00\u8fb9\u70e4\u5f97\u7126\u4f3c\u706b \u70ad\uff0c\u53e6\u4e00\u8fb9\u5374\u5e26\u7740\u8840\u4e1d\uff0c\u7b80\u76f4\u4e0d\u80fd\u5165\u53e3\u3002\u201d\u5510\u7ecf\u5929\u542c\u4ed6\u628a\u81ea\u5df1\u51b0\u96ea\u806a\u660e\u7684\u610f\u4e2d\u4eba\u9a82\u5f97\u5982\u6b64\u4e0d \u582a\uff0c\u5927\u4e3a\u751f\u6c14\u3002\u51b0\u5ddd\u5929\u5973\u5374\u671d\u7740\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u597d\u50cf\u5728\u5bf9\u4ed6\u8868\u793a\u6b49\u610f\u3002\u8463\u592a\u6e05\u54c8\u54c8\u5927\u7b11\uff0c\u586b\uff1a \u201c\u6211\u548c\u5c1a\u53ef\u662f\u9965\u4e0d\u62e9\u98df\uff0c\u4f60\u4e0d\u5403\u90fd\u7559\u59cb\u6211\u597d\u5566\u3002\u4e0a\u4e86\u559c\u9a6c\u62c9\u96c5\u5c71\uff0c\u8981\u627e\u5403\u7684\u6050\u6015\u66f4\u96be\u5566\uff01\u201d \u8d64\u795e\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u5ffd\u9053\uff1a\u201c\u5929\u6740\u7684\u6bd2\u624b\u75af\u4e10\u91d1\u4e16\u9057\uff0c\u6211\u82e5\u627e\u5230\u7ee6\u73e0\u4ed9\u8349\uff0c\u6062\u590d\u5f53\u521d\u529f\u529b\uff0c \u54fc\uff0c\u54fc\uff0c\u4e0d\u628a\u4f60\u6162\u6162\u6298\u78e8\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d\u8463\u592a\u6e05\u7b11\u9053\uff1a\u201c\u4e98\u53e4\u4ee5\u6765\uff0c\u4ece\u672a\u542c\u8bf4\u6709\u4eba\u80fd\u6500\u767b\u4e0a \u73e0\u7a46\u6717\u739b\u5cf0\uff0c\u51ed\u54b1\u8fd9\u5757\u6599\u5b50\uff0c\u60f3\u6500\u4e0a\u73e0\u5cf0\uff0c\u9664\u975e\u662f\u5929\u8001\u7237\u4fdd\u4f51\u3002\u201d\u8d64\u795e\u5b50\u6012\u9053\uff1a\u201c\u4f60\u6015\u6b7b\u5c31 \u522b\u966a\u6211\u53bb\u3002\u201d\u8463\u592a\u6e05\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4f3c\u4f60\u4e00\u6837\uff0c\u672c\u4e8b\u4e0d\u6d4e\uff0c\u6d3b\u7740\u4e5f\u662f\u5c3d\u53d7\u4eba\u5bb6\u7684\u6c14\uff0c\u4e0d\u5982\u966a\u4f60 \u62ff\u6027\u547d\u53bb\u8d4c\u5b83\u4e00\u8d4c\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e0d\u77e5\u4ed6\u4eec\u8bf4\u7684\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4f46\u542c\u5f97\u8d64\u795e\u5b50\u8fd9\u6837\u54ac\u7259\u5207\u9f7f\u7684\u63d0\u8d77\u91d1\u4e16\u9057\uff0c\u5374\u662f\u5927 \u4e3a\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u4f7f\u4ed6\u5143\u6c14\u5927\u4f24\u7684\u4e43\u662f\u6211\uff0c\u4ed6\u5e94\u8be5\u6068\u6211\u624d\u5bf9\uff0c\u600e\u4e48\u5374\u6068\u8d77\u91d1\u4e16\u9057\u6765\u4e86\uff1f\u201d\u5979 \u54ea\u77e5\u9053\u8d64\u795e\u5b50\u5728\u6c99\u6f20\u4e0a\u5403\u4e86\u91d1\u4e16\u9057\u4e00\u62d0\uff0c\u5de6\u811a\u5df2\u7136\u8de4\u4e86\uff0c\u4e24\u4eba\u53c8\u5931\u4e86\u9a86\u9a7c\uff0c\u71ac\u4e86\u8bb8\u591a\u82e6\u5934\u624d \u9003\u5f97\u51fa\u6c99\u6f20\u3002 \u3000\u3000\u8d64\u795e\u5b50\u6b63\u5728\u72e0\u72e0\u5730\u5492\u9a82\u91d1\u4e16\u9057\uff0c\u5916\u9762\u53c8\u4f20\u6765\u4e86\u9a6c\u8e44\u58f0\uff0c\u8463\u592a\u6e05\u7b11\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u70e4\u7f8a\u8089\u7684 \u4e3b\u4eba\u56de\u6765\u4e86\uff0c\u6211\u53ef\u5feb\u8981\u628a\u4ed6\u7684\u7f8a\u8089\u5403\u5149\u5566\u3002\u201d\u8d64\u795e\u5b50\u9053\uff1a\u201c\u4ed6\u6562\u7f57\u55e6\uff0c\u6211\u5c31\u4e00\u638c\u5c06\u4ed6\u51fb\u6740\uff0c \u54b1\u4eec\u6539\u5403\u9a6c\u8089\u3002\u201d\u8463\u592a\u6e05\u9053\uff1a\u201c\u6211\u51fa\u5bb6\u4eba\u53ef\u4e0d\u613f\u610f\u968f\u4fbf\u6740\u4eba\u3002\u201d\u4e24\u4eba\u4e92\u76f8\u5632\u7b11\uff0c\u9a6c\u8e44\u58f0\u5df2\u505c \u5728\u95e8\u524d\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u7ae5\u5b50\u7684\u53e3\u97f3\u53fd\u54e9\u53ed\u5566\u7684\u60a6\u9053\uff1a\u201c\u6211\u8bf4\u4e0d\u7528\u614c\u5c31\u4e0d\u7528\u614c\uff0c\u5929\u8981\u6253\u98ce\u4e0b\u96ea\uff0c \u8fd9\u91cc\u5c31\u5e73\u5730\u6d8c\u51fa\u4e00\u95f4\u5c4b\u5b50\u6536\u7559\u6211\uff0c\u54c8\uff0c\u54c8\uff0c\u91cc\u9762\u6709\u70e4\u8089\u7684\u9999\u5473\u3002\u6211\u6562\u8ddf\u4f60\u6253\u8d4c\uff0c\u91cc\u9762\u7684\u4e3b\u4eba \u4e00\u5b9a\u662f\u4e2a\u597d\u5ba2\u7684\u4eba\u3002\u201d\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u76f8\u89c6\u4e00\u7b11\uff0c\u5fc3\u77e5\u6765\u7684\u5b9a\u7136\u662f\u9648\u5929\u5b87\u90a3\u4e2a\u591a\u5634\u7684\u4e66\u7ae5 \u2014\u2014\u6c5f\u5357\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5973\u5b69\u5b50\u6e05\u8106\u7684\u53e3\u97f3\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u602a\u5c4b\uff1f\u5988\u5988\uff0c\u4f60\u53ef\u66fe\u89c1\u8fc7\u8fd9\u6837\u5947\u602a\u7684\u4eba\u5bb6\uff1f\u201d \u4e00\u4e2a\u5987\u4eba\u7b54\u9053\uff1a\u201c\u6211\u77a7\u8fd9\u5c4b\u5b50\u91cc\u4e5f\u662f\u900f\u7740\u602a\u6c14\uff0c\u4f46\u5373\u6765\u4e4b\u5219\u5b89\u4e4b\uff0c\u54b1\u4eec\u4e14\u8fdb\u53bb\u6c42\u5bbf\u518d\u8bf4\u3002\u201d \u5510\u7ecf\u5929\u5927\u4e3a\u60ca\u8be7\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u6768\u67f3\u9752\u6bcd\u5973\u4e5f\u5230\u8fd9\u513f\u6765\u4e86\u3002\u6c5f\u5357\u600e\u7684\u548c\u5979\u4eec\u5982\u6b64\u637b\u719f\uff1f\u542c\u8fd9 \u811a\u6b65\u58f0\u5e94\u6709\u56db\u4eba\uff0c\u8fd8\u6709\u4e00\u4eba\u662f\u8c01\uff1f\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u542c\u5f97\u5916\u9762\u56db\u4eba\u89d2\u8d2f\u800c\u5165\uff0c\u5510\u7ecf\u5929\u4ece\u5c0f\u5b54\u4e2d\u5f20 \u773c\u4e00\u671b\uff0c\u90a3\u8d70\u5728\u6700\u540e\u9762\u7684\u4eba\uff0c\u5374\u662f\u5510\u7aef\u3002 \u3000\u3000\u539f\u6765\u6c5f\u5357\u5e26\u6768\u67f3\u9752\u5230\u62c9\u8428\u6765\u627e\u5510\u7ecf\u5927\uff0c\u5374\u78b0\u5230\u4e86\u5510\u7aef\uff0c\u5510\u3001\u6768\u4e8c\u5bb6\u539f\u662f\u4e16\u4ea4\u3001\u4e8c\u5341\u4f59\u5e74 \u524d\uff0c\u51af\u7433\u8bef\u6740\u5510\u8d5b\u82b1\u7684\u4e08\u592b\uff0c\u95f9\u4e86\u573a\u98ce\u6ce2\uff0c\u51e0\u4e4e\u5c06\u6768\u4ef2\u82f1\u7236\u5973\u4e5f\u7275\u8fde\u5728\u5185\uff0c\u672c\u800c\u4e8b\u60c5\u8fc7\u540e\uff0c \u5510\u5bb6\u81ea\u51b3\u7406\u4e8f\u4e8f\uff0c\u6df1\u611f\u5bf9\u4e0d\u8d77\u6b7b\u53bb\u7684\u6768\u4ef2\u82f1\uff0c\u800c\u5bf9\u6768\u67f3\u9752\u6bd4\u524d\u66f4\u597d\u3002 \u3000\u3000\u7a81\u7136\u9047\u5230\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u4f17\u4eba\u8db3\u5403\u60ca\u4e2a\u5c0f\uff0c\u6c5f\u5357\u6296\u6296\u7d22\u7d22\uff0c\u54ea\u5403\u5f97\u8fdb\u53bb\u3002\u6495\u4e86\u4e00\u4ee5\u9e20\u817f\uff0c \u5374\u9012\u7ed9\u90b9\u7edb\u971e\uff0c\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u5403\u5427\uff0c\u6211\u8fd9\u53ea\u9e21\u817f\u8fd8\u6ca1\u6709\u5403\u5b8c\u5462\u3002\u201d\u6c5f\u5357\u7a81\u7b11\u563b\u563b\u5730 \u9053\uff1a\u201c\u5510\u5927\u4fa0\u548c\u6211\u7ea6\u597d\u5382\u5728\u8fd9\u513f\u89c1\u9762\uff0c\u54b1\u4eec\u8981\u7559\u4e00\u5b9a\u9e21\u7ed9\u4ed6\u3002\u54c8\u54c8\uff0c\u5510\u5927\u9655\u548c\u6211\u5bb6\u516c\u5b50\u662f\u8981 \u597d\u7684\u670b\u53cb\uff0c\u4ece\u6765\u4e0d\u4f1a\u5931\u4fe1\uff0c\u4ed6\u8bf4\u6765\u5c31\u4e00\u5b9a\u662f\u6765\u3002\u201d\u6c5f\u5357\u80e1\u8bf4\u4e00\u901a\uff0c\u90b9\u7edb\u971e\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u9192 \u609f\uff0c\u90a3\u662f\u6c5f\u5357\u6545\u610f\u7f16\u51fa\u6765\u8bf4\u7ed9\u90a3\u4e24\u4e2a\u9b54\u5934\u542c\u7684\uff0c\u60f3\u7528\u5510\u7ecf\u5929\u6765\u5413\u8d70\u90a3\u4e24\u4e2a\u9b54\u5934\uff0c\u4e0d\u8fc7\u4ed6\u7b11\u5f97 \u6781\u5176\u52c9\u5f3a\uff0c\u5373\u7b97\u662f\u4e0d\u719f\u8bc6\u6c5f\u5357\u6027\u60c5\u7684\u4eba\u4e5f\u542c\u5f97\u51fa\u4ed6\u5185\u5fc3\u7684\u60ca\u614c\u3002 \u3000\u3000\u8d64\u795e\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u8463\u592a\u6e05\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u8fd9\u91cc\u6ca1\u6709\u6253\u66f4\u7684\uff0c\u4e0d\u77e5\u73b0\u5728\u662f\u4e09\u66f4\u8fd8\u662f\u56db\u66f4\uff1f\u201d \u6c5f\u5357\u4e5f\u4e0d\u77e5\u9053\u662f\u4e09\u66f4\u8fd8\u662f\u56db\u66f4\uff0c\u53ea\u77e5\u81ea\u5df1\u8bdd\u4e2d\u9732\u4e86\u7834\u7efd\uff0c\u6301\u7740\u9e21\u817f\uff0c\u5212\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u53c8\u9053\uff1a \u201c\u5510\u5927\u4fa0\u548c\u6211\u4eec\u4e00\u540c\u4ece\u62c9\u8428\u6765\uff0c\u4ed6\u7684\u529f\u592b\u867d\u597d\uff0c\u5750\u9a91\u5374\u6ca1\u6709\u6211\u4eec\u7094\uff0c\u4e0d\u8fc7\uff0c\u6050\u6015\u4e5f\u5feb\u8981\u5230 \u4e86\u3002\u4ed6\u6700\u6b22\u559c\u559d\u9152\uff0c\u8fd9\u4e2a\u846b\u82a6\u7684\u8461\u8404\u9152\u53ef\u5f97\u7559\u7ed9\u4ed6\u3002\u201d\u8fd9\u4e00\u4e0b\u7834\u7efd\u66f4\u5927\uff0c\u8d64\u795e\u5b50\u7a81\u7136\u4e00\u62cd\u77f3 \u684c\uff0c\u559d\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u6c5f\u5357\u5413\u4e86\u4e00\u8df3\uff0c\u6447\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\u5566\uff0c\u6211\u6015\u7f8a\u8089\u90a3\u80a1\u9a9a\u5473\u3002\u201d\u8d64\u795e\u5b50\u559d\u9053\uff1a\u201c\u4f60\u597d \u80c3\u53e3\uff0c\u8c01\u8bf7\u4f60\u5403\u7f8a\u8089\uff1f\u8fc7\u6765\uff0c\u670d\u4f8d\u8001\u7237\u559d\u9152\u3002\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u9152\u662f\u7559\u7ed9\u91d1\u5927\u4fa0\u5403\u7684\u3002\u201d\u8d64\u795e\u5b50 \u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u91d1\u5927\u4fa0\u65e9\u5c31\u5728\u6c99\u6f20\u4e2d\u6b7b\u6389\u5566\uff0c\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u60f3\u62ff\u6bd2\u624b\u75af\u4e10\u6765\u5413\u6211\u5417\uff1f\u54fc\uff0c\u4f60 \u8fc7\u4e0d\u8fc7\u6765\uff1f\u518d\u4e0d\u8fc7\u6765\uff0c\u6211\u5c31\u5c06\u4f60\u4e5f\u70e4\u7126\u4e86\u3002\u201d\u624b\u638c\u4e00\u4f38\uff0c\u70ed\u98ce\u6251\u9762\uff0c\u6c5f\u5357\u82e6\u7740\u8138\u9053\uff1a\u201c\u5582\uff0c \u5582\uff0c\u6211\u76ae\u7c97\u8089\u7cd9\uff0c\u70e4\u719f\u4e86\u6bd4\u7f8a\u8089\u8fd8\u8981\u96be\u5403\u5475\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5916\u9762\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u70e4\u7f8a\u8089\u8fd8\u8bf4\u96be\u5403\uff1f\u54c8\uff0c\u54c8\uff01\u6211\u5c31\u6700\u6b22\u559c\u5403\u7f8a\u8089\uff01\u201d\u8d64\u795e\u5b50 \u53cc\u773c\u4e00\u7741\uff0c\u53ea\u89c1\u4e24\u4e2a\u602a\u4eba\u4ee5\u624b\u6491\u5730\uff0c\u7adf\u662f\u5934\u4e0b\u811a\u4e0a\uff0c\u50cf\u65cb\u98ce\u822c\u5730\u6251\u4e86\u8fdb\u6765\u3002\u770b\u6e05\u695a\u65f6\uff0c\u539f\u6765 \u8fd9\u4e24\u4e2a\u602a\u4eba\u7684\u53cc\u811a\u81ea\u819d\u76d6\u4ee5\u4e0b\uff0c\u76d8\u5c48\u5982\u73af\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u7ed9\u4eba\u6253\u65ad\u4e86\u9aa8\u5934\uff0c\u6545\u6b64\u4e0d\u80fd\u884c\u8d70\u3002\u4f46 \u89c1\u4ed6\u4eec\u4ee5\u624b\u4ee3\u811a\uff0c\u6240\u8fc7\u4e4b\u5904\uff0c\u5730\u4e0a\u7559\u4e0b\u4e00\u4e2a\u4e00\u4e2a\u7684\u638c\u5370\u3002\u8fd9\u4efd\u529f\u592b\u867d\u7136\u5413\u4e0d\u5012\u8d64\u795e\u5b50\uff0c\u4f46\u4ea6 \u8db3\u4ee5\u4ee4\u4eba\u9a87\u5f02\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u602a\u4eba\u6df1\u76ee\u9ad8\u9f3b\uff0c\u9ec4\u53d1\u5bbd\u989d\uff0c\u770b\u88c5\u675f\u4f3c\u662f\u963f\u62c9\u4f2f\u4eba\uff0c\u5374\u8bf4\u5f97\u4e00\u53e3\u6d41\u5229\u7684\u6c49\u8bed\u3002\u53ea\u89c1 \u4ed6\u4eec\u76d8\u819d\u4e00\u5750\uff0c\u772f\u7740\u773c\u775b\uff0c\u6307\u7740\u8d64\u795e\u5b50\u9053\uff1a\u201c\u597d\u9999\u7684\u8089\u5473\uff0c\u628a\u90a3\u6761\u7f8a\u817f\u7ed9\u6211\u3002\u201d\u8d64\u795e\u5b50\u5927 \u6012\uff0c\u53cc\u638c\u4e00\u6247\uff0c\u70ed\u6d6a\u5411\u4ed6\u4eec\u76f4\u903c\u3002\u8463\u592a\u6e05\u6025\u5fd9\u6253\u773c\u8272\uff0c\u963b\u6b62\u8d64\u795e\u5b50\u52a8\u624b\u3002\u8fd9\u4e24\u4e2a\u602a\u4eba\u53eb\u9053\uff1a \u4e8f\u54c8\uff0c\u54c8\uff0c\u597d\u8212\u670d\uff0c\u4ece\u51b0\u5929\u96ea\u5730\u91cc\u8d70\u8fdb\u8fd9\u5ea7\u5323\u5b50\uff0c\u771f\u50cf\u8d70\u8fdb\u4e86\u5929\u5802\u5566\u3002\u201d\u770b\u4ed6\u4eec\u7684\u795e\u8272\u75b2\u52b3 \u4e4b\u6781\uff0c\u82e5\u662f\u6b66\u529f\u6839\u57fa\u7a0d\u5dee\u7684\u4eba\uff0c\u4ece\u96ea\u5730\u8d70\u6765\uff0c\u53c8\u53d7\u70ed\u6d6a\u6025\u653b\uff0c\u5fc5\u5c06\u6655\u5012\u65e0\u7591\uff0c\u800c\u4ed6\u4eec\u5374\u89e3\u5f00 \u895f\uff0c\u6325\u6c57\u8c08\u7b11\uff0c\u82e5\u65e0\u5176\u4e8b\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e00\u80d6\u4e00\u7626\uff0c\u80d6\u7684\u90a3\u4e2a\u9053\uff1a\u201c\u4e45\u95fb\u4e2d\u534e\u56fd\u571f\uff0c\u4eba\u4eba\u597d\u5ba2\uff0c\u8c01\u77e5\u4f20\u8a00\u662f\u5047\uff0c\u773c\u89c1 \u65b9\u771f\u3002\u201d\u8d64\u795e\u5b50\u6012\u9053\uff1a\u201c\u4f60\u75af\u8a00\u75af\u8bed\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u7626\u7684\u90a3\u4e2a\u9053\uff1a\u201c\u4f60\u60f3\u6253\u67b6\u4e48\uff1f\u201d\u8d64\u795e\u5b50\u518d \u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u6211\u4eec\u4e24\u4e2a\uff0c\u4f60\u4eec\u4e5f\u662f\u4e24\u4e2a\uff0c\u54b1\u4eec\u5c31\u6bd4\u5212\u4e00\u4e0b\u3002\u201d\u7626\u7684\u90a3\u4e2a\u6447\u5934\u7b11 \u9053\uff1a\u201c\u6211\u997f\u7740\u809a\u5b50\uff0c\u53ef\u6ca1\u6709\u6c14\u529b\u548c\u4f60\u6253\u67b6\u3002\u201d\u8d64\u795e\u5b50\u4e00\u624b\u62a2\u4e86\u8463\u592a\u6e05\u7684\u7f8a\u817f\uff0c\u629b\u8fc7\u53bb\u9053\uff1a \u201c\u5feb\u5403\uff0c\u5feb\u5403\uff01\u201d\u867d\u7136\u662f\u4e00\u6761\u65a4\u591a\u91cd\u7684\u5c0f\u7f8a\u817f\uff0c\u7ecf\u8d64\u795e\u4e8e\u63b7\u51fa\uff0c\u52b2\u529b\u4e0d\u4e9a\u4e8e\u4e00\u67c4\u6d41\u661f\u9524\uff0c\u7626 \u5730\u90a3\u4e2a\u602a\u4eba\u5374\u4e00\u5f20\u53e3\u5c31\u628a\u5b83\u54ac\u4f4f\uff0c\u80d6\u7684\u90a3\u4e2a\u9053\uff1a\u201c\u8fd8\u6709\u6211\u5462\uff01\u201d\u8d64\u795e\u5b50\u53eb\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u628a\u4e24 \u53ea\u814a\u96ea\u9e21\u7ed9\u4ed6\u3002\u201d\u6c5f\u5357\u53ea\u76fc\u671b\u6709\u4eba\u7ed9\u4ed6\u51fa\u5934\u6253\u67b6\uff0c\u8d76\u5fd9\u5c06\u4e24\u53ea\u814a\u96ea\u9e21\u606d\u606d\u656c\u656c\u7684\u6454\u8fc7\u53bb\uff0c\u8bf4 \u9053\uff1a\u201c\u5403\u5b8c\u4e86\uff0c\u4e0d\u591f\u8fd8\u6709\uff01\u201d\u80d6\u7684\u90a3\u4e2a\u8feb\uff1a\u201c\u9152\u4e5f\u62ff\u6765\u3002\u201d\u6c5f\u5357\u4e0d\u5f85\u8d64\u795e\u5b50\u5429\u5490\uff0c\u53c8\u5c06\u4e00\u5927 \u846b\u82a6\u7684\u9152\u9012\u7ed9\u90a3\u4e2a\u602a\u4eba\uff0c\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u4e0d\u9519\u3002\u996e\u9189\u98df\u9971\uff0c\u6253\u67b6\u624d\u6709\u7cbe\u795e\u3002\u201d \u3000\u3000\u8d64\u795e\u5b50\u72e0\u72e0\u7684\u77aa\u7740\u90a3\u4e2a\u602a\u4eba\uff0c\u8463\u592a\u6e05\u6447\u5934\u9053\uff1a\u201c\u4f55\u82e6\u6765\u54c9\u3001\u4f55\u82e6\u6765\u54c9\uff1f\u201d\u8d64\u795e\u5b50\u7406\u4e5f\u4e0d \u7406\uff0c\u8fde\u58f0\u50ac\u9053\uff1a\u201c\u5feb\u5403\uff0c\u5feb\u5403\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u6162\u6761\u65af\u7406\u7684\u5403\u4e86\u7f8a\u817f\u3001\u96ea\u9e21\uff0c\u53c8\u628a\u4e00\u4e2a\u5927\u846b\u82a6\u7684\u8461\u8404\u9152\u559d\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u731b\u5730 \u53d1\u4e86\u4e00\u58f0\u602a\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u8981\u6253\u67b6\u7684\u6765\u5427\uff01\u201d\u8463\u5927\u6e05\u529d\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u662f\u51fa\u95e8\u4eba\uff0c\u8fdc\u65e0 \u51a4\uff0c\u8fd1\u65e0\u4ec7\uff0c\u4f55\u82e6\u4e89\u8fd9\u4e9b\u95f2\u6c14\uff1f\u201d\u4ed6\u5fc3\u4e2d\u81ea\u5fd6\uff1a\u8d64\u795e\u5b50\u529f\u529b\u5df2\u51cf\uff0c\u4e0e\u81ea\u5df1\u8054\u624b\uff0c\u4e5f\u672a\u5fc5\u80dc\u5f97 \u4e86\u90a3\u4e24\u4e2a\u602a\u4eba\uff0c\u4f55\u51b5\u8fd8\u6709\u56db\u4e2a\u654c\u4eba\u73af\u4f3a\u7aa5\u89c6\uff0c\u8fd9\u56db\u4eba\u4e2d\uff0c\u90b9\u7edb\u971e\uff0c\u5510\u7aef\u3001\u6c5f\u5357\u7b49\u4e09\u4e2a\u90fd\u662f\u5c0f \u8f88\uff0c\u65e0\u8db3\u8f7b\u91cd\uff0c\u6768\u67f3\u9752\u7684\u5f39\u5f13\uff0c\u5374\u4e0d\u80fd\u4e0d\u63d0\u9632\u51e0\u5206\u3002\u603b\u4e4b\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u8fd9\u573a\u67b6\u4e0d\u6253\u4e5f\u7f62\u3002 \u3000\u3000\u80d6\u7684\u90a3\u4e2a\u602a\u4eba\u9762\u8272\u4e00\u6c89\uff0c\u5374\u5ffd\u5730\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u6253\u4e5f\u6210\uff0c\u53ea\u662f\u4f60\u4eec\u8981\u501f\u4e00\u6837\u4e1c\u897f\u7ed9 \u6211\u3002\u201d\u8d64\u795e\u5b50\u6012\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u628a\u4f60\u4eec\u7684\u56db\u6761\u817f\u501f\u7ed9\u6211\u4eec\uff0c\u8fd9\u662f\u4f60\u4eec\u8eab\u4e0a\u4e4b \u7269\uff0c\u73b0\u6210\u5f97\u5f88\uff0c\u4e0d\u5f20\u7f57\uff0c\u8be5\u4e0d\u7b97\u662f\u96be\u9898\u5427\uff1f\u201d\u8fd9\u51e0\u53e5\u8bf4\u8bdd\u5f97\u7a00\u677e\u5e73\u5e38\uff0c\u597d\u4f3c\u662f\u5411\u522b\u4eba\u501f\u4e00\u4ef6 \u5fae\u4e0d\u8db3\u9053\u7684\u7269\u4ef6\u4e00\u822c\u3002 \u3000\u3000\u8d64\u795e\u5b50\u8f88\u4efd\u6781\u9ad8\uff0c\u6a2a\u884c\u534a\u4e16\uff0c\u8fd1\u5e74\u6765\u867d\u5c61\u53d7\u632b\u6298\uff0c\u53ef\u4ece\u6ca1\u6709\u4eba\u6562\u5bf9\u4ed6\u8fd9\u6837\u65e0\u793c\uff0c\u95fb\u8a00\u6012 \u6781\uff0c\u4e0d\u5f85\u4ed6\u4eec\u8bf4\u5b8c\uff0c\u65e9\u5df2\u98de\u8eab\u6251\u8d77\uff0c\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u70ed\u6d6a\u56db\u6ea2\uff0c\u8fd9\u4e00\u638c\u662f\u4ed6\u5168\u8eab\u529f\u592b\u4e4b\u6240 \u805a\uff0c\u6768\u67f3\u9752\u7b49\u4eba\u8ddd\u79bb\u5728\u6570\u4e08\u4e4b\u5916\uff0c\u4ea6\u89c9\u5f97\u70ed\u4e0d\u53ef\u5f53\u3002\u6c5f\u5357\u6025\u5fd9\u76d8\u819d\u9759\u5750\uff0c\u8fd0\u7528\u5510\u7ecf\u5929\u6240\u6388\u7684 \u90a3\u70b9\u5185\u529f\u5fc3\u6cd5\uff0c\u8fde\u770b\u4e5f\u4e0d\u6562\u770b\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e24\u4e2a\u602a\u4eba\u4e0d\u614c\u4e0d\u5fd9\uff0c\u5f90\u5f90\u51fa\u638c\uff0c\u8d64\u795e\u5b50\u7684\u8eab\u5f62\u98de\u5728\u534a\u7a7a\uff0c\u5c1a\u672a\u843d\u4e0b\uff0c\u5ffd\u7136\u4f3c\u53d7\u4e86 \u4e00\u80a1\u65e0\u5f62\u7684\u6f5c\u529b\u53cd\u51fb\u4e00\u6837\uff0c\u5411\u4e0b\u4e00\u6c89\uff0c\u811a\u672a\u7740\u5730\uff0c\u5374\u5411\u5de6\u659c\u65b9\u5012\u649e\u51fa\u53bb\uff0c\u8d64\u795e\u5b50\u53cc\u81c2\u4e00\u632f\uff0c \u547c\u7684\u53c8\u53d1\u4e86\u4e00\u638c\uff0c\u4f46\u8fd9\u4e00\u638c\u7684\u70ed\u529b\u5df2\u662f\u5927\u4e0d\u5982\u524d\u3002 \u3000\u3000\u8463\u592a\u60c5\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5df2\u89c1\u8d64\u795e\u5b50\u72c2\u547c\u731b\u6251\uff0c\u8eab\u5f62\u603b\u4e0d\u80fd\u8fdb\u5230\u8ddd\u79bb\u90a3\u4e24\u4e2a\u602a\u4eba\u7684\u4e00\u4e08 \u4e4b\u5185\uff0c\u8fc7\u7247\u523b\uff0c\u53ea\u89c1\u8d64\u795e\u5b50\u5de6\u51b2\u53f3\u7a81\uff0c\u7adf\u4f3c\u6ca1\u5934\u82cd\u8747\u4e00\u6837\uff0c\u56e2\u56e2\u5c9a\u8f6c\u3002\u539f\u6765\u90a3\u4e24\u4e2a\u602a\u4eba\u6240\u53d1 \u7684\u638c\u529b\uff0c\u540d\u4e3a\u201c\u9634\u9633\u4e94\u884c\u638c\u529b\u201d\uff0c\u4e00\u80a1\u638c\u529b\u63a8\u524d\uff0c\u4e00\u80a1\u638c\u529b\u62c9\u540e\uff0c\u4e24\u80a1\u638c\u529b\u76f8\u53cd\u76f8\u6210\uff0c\u9677\u5165 \u4e86\u4ed6\u4eec\u638c\u529b\u7684\u5708\u5b50\uff0c\u5c31\u50cf\u9677\u8fdb\u4e86\u6f29\u6da1\u4e00\u6837\uff0c\u975e\u4f46\u4e0d\u80fd\u524d\u8fdb\uff0c\u8fde\u8131\u8eab\u4e5f\u96be\u3002 \u3000\u3000\u8463\u592a\u6e05\u867d\u7136\u4e0d\u613f\u62db\u60f9\u8fd9\u4e24\u4e2a\u602a\u4eba\uff0c\u4f46\u4ed6\u4e0e\u8d64\u795e\u5b50\u72fc\u72c8\u76f8\u4f9d\uff0c\u8d64\u795e\u5b50\u88ab\u56f0\uff0c\u4ed6\u81ea\u662f\u4e0d\u80fd\u8896 \u624b\u65c1\u89c2\uff0c\u4ed6\u6bd4\u8d64\u795e\u5b50\u8981\u8c28\u614e\u5f97\u591a\uff0c\u5148\u60f3\u597d\u4e86\u8131\u8eab\u4e4b\u8ba1\uff0c\u51c6\u5907\u65bd\u5c55\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c \u7acb\u523b\u9000\u5f00\uff0c\u6c38\u4e0d\u548c\u4ed6\u4eec\u7684\u638c\u529b\u6b63\u9762\u76f8\u63a5\u3002\u4ed6\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u602a\u4eba\u53cc\u811a\u5df2\u65ad\uff0c\u5982\u4f55\u80fd\u8ffd\u5f97 \u4e0a\u6211\uff1f\u201d \u3000\u3000\u5c82\u77e5\u4ed6\u60f3\u5f97\u5468\u5168\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u7684\u62db\u5f0f\u5374\u5927\u51fa\u50ac\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u51cc\u7a7a\u4e00\u51fb\uff0c\u957f\u81c2\u8fd8\u672a\u6293\u5230\u654c \u4eba\u5934\u4e0a\uff0c\u5ffd\u89c1\u80d6\u7684\u90a3\u4e2a\u602a\u4eba\u53cc\u638c\u5411\u540c\u4f34\u4e00\u63a8\uff0c\u7626\u7684\u90a3\u602a\u4eba\u8eab\u5b50\u4e5f\u7a81\u7136\u98de\u4e86\u8d77\u6765\uff01\u8463\u592a\u6e05\u53d7\u4ed6 \u638c\u529b\u529b\u7275\u5f15\uff0c\u614c\u5fd9\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5411\u540e\u5012\u8dc3\uff0c\u54ea\u77e5\u4ed6\u5feb\u522b\u4eba\u66f4\u5feb\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u602a\u4eba\u5df2\u5728 \u4ed6\u7684\u5934\u9876\u8d8a\u8fc7\uff0c\u70fd\u706b\u53f0\u56db\u8fb9\u6709\u56db\u6068\u6728\u4f4f\uff0c\u602a\u4eba\u4e00\u624b\u6293\u7740\u6728\u67f1\uff0c\u731b\u7684\u56de\u5934\u53d1\u638c\u3002\u8463\u592a\u6e05\u7684\u732b\u9e70 \u6251\u51fb\u4e4b\u6280\uff0c\u53ef\u4ee5\u5728\u534a\u7a7a\u56de\u7fd4\u8f6c\u6298\uff0c\u4f46\u5374\u4e0d\u80fd\u6301\u4e45\u3002 \u3000\u3000\u8fd9\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u662f\u5f53\u5e74\u4eba\u81c2\u795e\u9b54\u8428\u5929\u523a\u5728\u732b\u9e70\u5c9b\u4e0a\uff0c\u65e5\u4e45\u6a21\u62df\u732b\u9e70\u6251\u51fb\u59ff\u52bf\uff0c\u82e6\u7ec3\u800c \u6210\uff0c\u7aef\u7684\u662f\u6b66\u6797\u7f55\u89c1\u7684\u4e00\u79cd\u8f7b\u529f\u5999\u6280\uff0c\u522b\u6837\u8f7b\u529f\uff0c\u6700\u591a\u662f\u4ee5\u8fc5\u6377\u89c1\u957f\uff0c\u800c\u5b83\u5374\u53ef\u5728\u7a7a\u4e2d\u56de\u7fd4 \u8f6c\u6298\u3002\u8463\u592a\u6e05\u662f\u516b\u81c2\u795e\u9b54\u7684\u552f\u4e00\u4f20\u4eba\uff0c\u73b0\u4e0b\u529f\u592b\u4e0d\u51cf\u5e08\u7236\u5f53\u5e74\uff0c\u7626\u7684\u90a3\u4e2a\u602a\u4eba\u4e00\u638c\u62cd\u51fa\uff0c\u638c \u529b\u672a\u5230\uff0c\u8463\u592a\u6e05\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53c8\u6362\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u53ef\u662f\u5728\u8fd9\u8f6c\u8eab\u5f62\u6362\u65b9\u5411\u7684\u65f6\u95f4\uff0c\u90a3\u4e2a\u602a \u4eba\u624b\u4e00\u6309\u67f1\uff0c\u8eab\u5f62\u53c8\u5df2\u5f39\u51fa\uff0c\u8d8a\u8fc7\u4e86\u4ed6\u7684\u524d\u5934\uff0c\u6293\u7740\u4e86\u53e6\u4e00\u6839\u6728\u67f1\uff0c\u56de\u8eab\u53c8\u662f\u4e00\u638c\u62cd\u51fa\u3002\u5982 \u662f\u8005\u4e00\u8fde\u4e09\u6b21\uff0c\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u95ea\u8eb2\u867d\u7136\u7075\u6d3b\uff0c\u5374\u662f\u4e0d\u80fd\u6301\u4e45\uff0c\u5230\u4e86\u7b2c\u56db\u6b21\u53d1\u638c\u4e4b\u65f6\uff0c\u8463\u592a \u6e05\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u4e00\u8de4\u6454\u5012\uff0c\u88ab\u90a3\u602a\u4eba\u7684\u638c\u529b\u4e00\u6325\uff0c\u9001\u5230\u4e86\u8d64\u795e\u5b50\u7684\u8eab\u65c1\u3002\u90a3\u602a\u4eba\u54c8\u54c8\u4e00 \u7b11\uff0c\u7acb\u523b\u98de\u56de\u539f\u5730\uff0c\u4e0e\u540c\u4f34\u7684\u638c\u529b\u4e00\u5408\uff0c\u8463\u5927\u6e05\u4e5f\u4e0e\u8d64\u795e\u5b50\u4e00\u6837\uff0c\u53ea\u89c9\u597d\u4f3c\u9677\u5728\u6f29\u6da1\u4e4b\u5185\uff0c \u8131\u8eab\u4e0d\u5f97\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u602a\u4eba\u51fa\u638c\u8d8a\u6765\u8d8a\u5feb\uff0c\u8463\u592a\u6e05\u548c\u8d64\u795e\u5b50\u4e0e\u4ed6\u4eec\u7684\u8ddd\u79bb\u672c\u5728\u4e00\u4e08\u5f00\u5916\uff0c\u8fd9\u65f6\u4f46\u89c1\u4ed6\u4eec \u6ee1\u5934\u5927\u6c57\uff0c\u624b\u821e\u8db3\u8e48\u5730\u4e00\u6b65\u6b65\u5411\u524d\u79fb\u52a8\uff0c\u5728\u5bfb\u5e38\u4eba\u89c1\u6765\uff0c\u53ef\u80fd\u8fd8\u4ee5\u4e3a\u662f\u4ed6\u4eec\u5728\u9f13\u52c7\u8fdb\u653b\uff0c\u843d \u5728\u6768\u67f3\u9752\u8fd9\u6837\u7684\u6b66\u5b66\u884c\u5bb6\u773c\u91cc\uff0c\u5374\u77e5\u9053\u4ed6\u4eec\u662f\u88ab\u90a3\u4e24\u4e2a\u602a\u4eba\u7684\u638c\u529b\u6240\u7275\u5f15\uff0c\u8d8a\u9677\u8d8a\u6df1\uff0c\u53ea\u8981 \u4e00\u5230\u4e86\u90a3\u4e24\u4e2a\u602a\u4eba\u638c\u529b\u6fc0\u8361\u7684\u4e2d\u5fc3\uff0c\u5373\u7b97\u8d64\u795e\u5b50\u4e0e\u8463\u5927\u6e05\u6b66\u529f\u518d\u5f3a\uff0c\u4e5f\u5c06\u5b8c\u5168\u53d7\u5236\uff0c\u5bb0\u5272\u7531 \u4eba\u7684\u4e86\u3002 \u3000\u3000\u6768\u67f3\u9752\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u8463\u592a\u6e05\u5bf9\u6211\u7236\u4eb2\u90a3\u4e00\u638c\u4e4b\u4ec7\uff0c\u4e09\u5341\u5e74\u4e0d\u5fd8\uff0c\u867d\u6709\u51af\u7433\u8c03\u89e3\uff0c\u96be \u4fdd\u4ed6\u65e5\u540e\u4e0d\u518d\u5411\u6211\u5bfb\u4e8b\uff0c\u82e5\u80fd\u501f\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e4b\u529b\uff0c\u5c06\u4ed6\u9664\u53bb\uff0c\u5012\u53ef\u6c38\u9664\u540e\u60a3\u3002\u6ce8\u89c6\u6597\u573a\uff0c\u76ee \u4e0d\u7a0d\u77ac\u3002\u8d64\u795e\u5b50\u529f\u529b\u7a0d\u9ad8\uff0c\u8fd8\u5728\u5c3d\u529b\u6323\u624e\uff0c\u8463\u5927\u6e05\u5374\u662f\u9000\u4e00\u6b65\u3001\u8fdb\u4e24\u6b65\uff0c\u6e10\u6e10\u88ab\u90a3\u4e24\u4e2a\u602a\u4eba \u5f15\u5230\u8eab\u8fb9\uff0c\u4f46\u89c1\u4ed6\u5934\u7b4b\u6bd5\u73b0\uff0c\u706b\u7ea2\u7684\u4e24\u9897\u773c\u73e0\uff0c\u597d\u50cf\u8981\u593a\u7736\u800c\u51fa\uff0c\u6768\u67f3\u9752\u867d\u662f\u4e0e\u4ed6\u6709\u4ec7\uff0c\u89c1 \u6b64\u60e8\u72b6\uff0c\u4e5f\u89c9\u5f97\u4e8e\u5fc3\u4e0d\u5fcd\uff0c\u6025\u628a\u773c\u5149\u79fb\u5f00\uff0c\u4e0d\u6b32\u518d\u770b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u4e24\u4e2a\u602a\u4eba\u540c\u58f0\u559d\u9053\uff1a\u201c\u53cc\u817f\u62ff\u6765\uff01\u201d\u63a5\u7740\u201c\u5531\u201d\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u597d\u50cf\u94c1\u9524\u51fb\u949f\uff0c \u5de8\u65a7\u5288\u77f3\uff0c\u6768\u67f3\u9752\u5934\u672a\u62ac\u8d77\uff0c\u53ea\u89c9\u4e00\u80a1\u70ed\u6c14\uff0c\u63a0\u9762\u800c\u8fc7\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u8463\u592a\u6e05\u4e25\u5982\u5de8\u4e4c\u7a7f \u6797\uff0c\u8eab\u5f62\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u6298\uff0c\u5df2\u662f\u4ece\u4e1c\u9762\u7684\u7a97\u5b50\u98de\u51fa\uff0c\u8d64\u795e\u5b50\u4ea6\u5df2\u65e0\u5f71\u65e0\u8e2a\uff0c\u60f3\u662f\u4ed6\u9003\u8d70\u5728 \u524d\uff0c\u90a3\u80a1\u70ed\u98ce\u81ea\u7136\u662f\u4ed6\u5e26\u8d77\u7684\u4e86\u3002\u573a\u5fc3\u90a3\u4e24\u4e2a\u602a\u4eba\u4ecd\u7136\u76d8\u819d\u800c\u5750\uff0c\u80d6\u7684\u90a3\u4e2a\u6367\u7740\u4e00\u6761\u94c1\u81c2\uff0c \u54fa\u54fa\u8bf4\u9053\uff1a\u201c\u771f\u6599\u4e0d\u5230\u4ed6\u8fd8\u6709\u8fd9\u79cd\u90aa\u95e8\u529f\u592b\uff0c\u201d\u539f\u6765\u8463\u5927\u6e05\u5728\u7edd\u9669\u4e4b\u9645\uff0c\u7a81\u7136\u65bd\u5c55\u6551\u547d\u795e \u62db\uff0c\u628a\u4ed6\u7684\u94c1\u81c2\u98de\u51fa\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u5e76\u4e0d\u77e5\u9053\u4ed6\u90a3\u6761\u81c2\u818a\u662f\u94c1\u94f8\u7684\uff0c\u6479\u7136\u89c1\u4ed6\u65ad\u81c2\u98de\u6765\uff0c\u5403\u4e86 \u4e00\u60ca\uff0c\u4e0d\u77e5\u5176\u4e2d\u6709\u4ec0\u4e48\u53e4\u602a\uff0c\u6025\u5fd9\u8fd0\u4e86\u5168\u8eab\u6c14\u529b\uff0c\u5c06\u5b83\u63a5\u4f4f\uff0c\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u8d64\u795e\u5b50\u548c\u8463\u592a \u6e05\u5df2\u662f\u53cc\u53cc\u9003\u8131\u3002 \u3000\u3000\u8463\u5927\u6e05\u867d\u672a\u6bd9\u547d\uff0c\u4f46\u5df2\u88ab\u9010\u8d70\uff0c\u800c\u4e14\u53c8\u635f\u4e86\u6700\u5389\u5bb3\u7684\u94c1\u81c2\uff0c\u6768\u67f3\u9752\u81ea\u662f\u6b23\u559c\u65e0\u9650\uff0c\u5ffd\u89c1 \u90a3\u4e24\u4e2a\u602a\u4eba\u76ee\u9732\u51f6\u5149\uff0c\u5ffd\u7136\u8f6c\u5411\u81ea\u5df1\u8fd9\u8fb9\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u70fd\u706b\u53f0\u4e2d\u60ca\u602a\u5f02\uff0c\u73e0\u5cf0\u5e95\u4e0b\u96c6\u90aa\u7fa4\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e94\u56de\u3000\u5e7d\u8c37\u5c6f\u5175\u3000\u6218\u4e91\u8ff7\u585e\u5916\u3000\u519b\u524d\u9732\u9762\u3000\u5929\u5973\u9707\u756a\u738b \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u4e94\u56de \u5e7d\u8c37\u5c6f\u5175\u3000\u6218\u4e91\u8ff7\u585e\u5916 \u519b\u524d\u9732\u9762\u3000\u5929\u5973\u9707\u756a\u738b \u3000\u3000\u6768\u67f3\u9752\u5fc3\u4e2d\u4e00\u51db\uff0c\u6293\u7d27\u5f39\u5f13\u3002\u6c5f\u5357\u4e00\u76f4\u95ed\u76ee\u9759\u5750\uff0c\u8fd9\u65f6\u542c\u5f97\u6709\u4eba\u5954\u51fa\u95e8\u5916\uff0c\u811a\u6b65\u6025\u901f\u4e4b \u6781\uff0c\u8fc5\u5373\u6d88\u5931\uff0c\u56db\u4e0b\u91cc\u9759\u5f97\u51fa\u5947\uff0c\u8fd9\u624d\u500f\u5730\u5f20\u5f00\u773c\u775b\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u9b54\u5934\u7ed9\u6253\u8d70\u4e86 \u5417\uff1f\u54c8\u54c8\uff0c\u4f60\u4eec\u5f97\u591a\u8c22\u6211\u624d\u6210\uff0c\u90a3\u4e00\u846b\u82a6\u7684\u8461\u8404\u9152\u6700\u80fd\u6062\u590d\u7cbe\u795e\uff0c\u4e24\u53ea\u814a\u96ea\u9e21\u7684\u5473\u9053\u4e5f\u4e0d\u9519 \u5427\uff1f\u201d\u5ffd\u89c1\u6768\u67f3\u9752\u548c\u90a3\u4e24\u4e2a\u602a\u4eba\u76f8\u5bf9\u800c\u89c6\uff0c\u795e\u6c14\u9a87\u4eba\uff0c\u591a\u5634\u7684\u6c5f\u5357\u4e5f\u4e0d\u7981\u6014\u7740\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u76ee\u5149\u4e00\u8f6c\uff0c\u5ffd\u5730\u53d1\u4e86\u4e00\u58f0\u602a\u7b11\uff0c\u80d6\u7684\u90a3\u4e2a\u9996\u5148\u8bf4\u9053\uff1a\u201c\u786e\u662f\u4e0d\u9519\uff0c\u5e94\u8be5\u5927\u5927 \u7684\u8c22\u4f60\uff01\u201d\u7626\u7684\u90a3\u4e2a\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u53cc\u817f\u501f\u7ed9\u6211\u4eec\u7528\u7528\uff0c\u7b49\u4e0b\u6211\u7ed9\u4f60\u952f\u6389\u65f6\uff0c\u5305\u4fdd\u4f60\u5168\u65e0 \u75db\u82e6\uff01\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8981\u952f\u6389\u6211\u7684\u53cc\u817f\uff01\u201d\u7626\u7684\u90a3\u4e2a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u624b\u672f\u5de7\u5999\u4e4b \u6781\uff0c\u5148\u70b9\u4e86\u4f60\u7684\u6655\u7a74\uff0c\u4f60\u4e00\u9192\u6765\uff0c\u8840\u5c31\u6b62\u4e86\u3002\u8fd9\u4efd\u8c22\u793c\u4f60\u89c9\u5f97\u5982\u4f55\uff1f\u201d\u6c5f\u5357\u5927\u53eb\u9053\uff1a\u201c\u4e0d \u6210\uff0c\u4e0d\u6210\uff0c\u6211\u8fd9\u53ea\u817f\u8fd8\u8981\u8d70\u8def\uff01\u201d\u80d6\u7684\u90a3\u4e2a\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u8981\u8d70\u8def\u5440\uff0c\u501f\u4f60\u7684\u817f\u7ed9\u6211\u7eed\u7b4b\u9a73 \u9aa8\uff0c\u8fd9\u662f\u4e24\u4ff1\u6709\u76ca\u7684\u4e8b\u60c5\u3002\u201d\u7626\u7684\u90a3\u4e2a\u9053\uff1a\u201c\u6211\u4eec\u501f\u4e86\u4f60\u7684\u53cc\u817f\uff0c\u5c31\u6536\u4f60\u505a\u5f1f\u5b50\u3002\u4f60\u6709\u4e86\u6211 \u4eec\u505a\u9760\u5c71\uff0c\u4e0d\u4f46\u4e00\u751f\u4e0d\u6101\u8863\u98df\uff0c\u800c\u4e14\u6ca1\u4eba\u6562\u6b3a\u8d1f\u4f60\uff01\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u6211\u624d\u4e0d\u4fe1\uff0c\u4f60\u4eec\u7684 \u53cc\u817f\u4e3a\u4ec0\u4e48\u53c8\u7ed9\u4eba\u6253\u7834\u4e86\uff1f\u201d\u6c5f\u5357\u8fd9\u4e00\u95ee\uff0c\u6b63\u89e6\u4ed6\u4eec\u4e4b\u5fcc\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u9762\u8272\u4e00\u53d8\uff0c\u66b4\u6012\u559d \u9053\uff1a\u201c\u6211\u8981\u4ee4\u5929\u4e0b\u4f1a\u6b66\u529f\u7684\u4eba\u90fd\u65ad\u53cc\u817f\uff0c\u7b2c\u4e00\u4e2a\u5c31\u5148\u5411\u4f60\u4e0b\u624b\uff01\u201d\u53ea\u89c1\u4ed6\u4eec\u624b\u4e00\u6491\u5730\uff0c\u7acb\u523b \u98de\u8eab\u6251\u5230\uff0c\u4e00\u51fa\u5de6\u624b\uff0c\u4e00\u51fa\u53f3\u624b\uff0c\u5341\u6307\u957f\u7532\uff0c\u6709\u5982\u9e1f\u722a\uff0c\u90fd\u5bf9\u51c6\u4e86\u6c5f\u5357\u7684\u7a74\u9053\u3002 \u3000\u3000\u6c5f\u5357\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u7684\u5988\u5440\uff01\u201d\u7a74\u9053\u8fd8\u672a\u88ab\u70b9\uff0c\u4eba\u5df2\u51e0\u4e4e\u6655\u5012\uff01\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8fd9\u4e24\u4e2a\u602a\u4eba\u8eab\u5f62\u98de\u8d77\u4e4b\u65f6\uff0c\u6768\u67f3\u9752\u7684\u5f39\u5b50\u4e5f\u5df2\u53d1\u51fa\uff0c\u6768\u5bb6\u795e\u5f39\uff0c\u540d\u4e0d\u865a \u4f20\uff0c\u5f13\u5f26\u4e00\u66f3\uff0c\u4fbf\u662f\u8fde\u73e0\u53d1\u51fa\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4f46\u4f3c\u51b0\u96f9\u4e71\u843d\uff0c\u7adf\u65e0\u4e00\u9897\u6253\u5230\u90a3\u4e24\u4e2a\u602a\u4eba\u7684\u8eab \u4e0a\u3002\u90a3\u4e24\u4e2a\u602a\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u591a\u5c11\uff0c\u5c3d\u6570\u53d1\u6765\u5427\uff01\u4f60\u4eec\u56db\u4e2a\u4eba\u7684\u817f\u90fd\u7ed9\u6211\u7559\u4e0b\u3002\u201d \u6768\u67f3\u9752\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u89c9\u4e24\u80a1\u6f5c\u529b\uff0c\u5df2\u7136\u5377\u81f3\uff0c\u987f\u65f6\u4fbf\u4f3c\u8eab\u9677\u6f29\u6da1\u4e4b\u4e2d\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5411 \u524d\u79fb\u52a8\u3002\u539f\u6765\u6768\u67f3\u9752\u6240\u53d1\u7684\u5f39\u5b50\uff0c\u7ed9\u90a3\u4e24\u4e2a\u602a\u4eba\u6240\u53d1\u7684\u9634\u9633\u4e94\u884c\u638c\u529b\u4e00\u6324\uff0c\u5c31\u50cf\u6ce5\u6c99\u88ab\u5377\u8fdb \u4e86\u65cb\u98ce\u7684\u4e2d\u5fc3\uff0c\u54ea\u8fd8\u6709\u529b\u91cf\u3002 \u3000\u3000\u773c\u770b\u90a3\u4e24\u4e2a\u602a\u4eba\u4fbf\u8981\u65bd\u5c55\u6740\u624b\uff0c\u731b\u5730\u91cc\u201c\u8f70\u9686\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u5934\u9876\u7684\u5929\u82b1\u677f\u7a81\u7136\u88c2\u5f00\u4e00 \u4e2a\u5927\u6d1e\uff0c\u8fd9\u4e8b\u60c5\u6765\u5f97\u610f\u5916\u4e4b\u6781\uff0c\u4e24\u4e2a\u602a\u4eba\u4e5f\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u540c\u58f0\u559d\u9053\uff1a\u201c\u8c01\u8eb2\u5728\u4e0a\u9762\uff0c\u8d76\u5feb \u7ed9\u6211\u6eda\u4e0b\u6765\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u4f46\u542c\u5f97\u201c\u564e\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9053\u6697\u8d64\u8272\u7684\u5149\u534e\uff0c\u9aa4\u7136\u5c04\u4e0b\uff0c\u4e24\u4e2a\u602a\u4eba \u5413\u4e2a\u9762\u65e0\u4eba\u8272\uff0c\u624b\u638c\u4e00\u8f6c\uff0c\u4e92\u76f8\u4e00\u63a8\uff0c\u8eab\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u7acb\u65f6\u201c\u5c04\u201d\u51fa\u95e8\u5916\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5510 \u6653\u6f9c\u4f60\u53ef\u4e0d\u80fd\u4e0d\u987e\u8bfa\u8a00\uff01\u201d\u6768\u67f3\u9752\u72c2\u559c\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u662f\u4f60\u5728\u8fd9\u513f\u5417\uff1f\u201d\u4f46\u89c1\u4e00\u4e2a\u4fca\u4fcf\u5c11\u5e74\uff0c \u4ece\u88c2\u6d1e\u8dc3\u4e0b\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u5510\u7ecf\u5929\u3002\u201d \u3000\u3000\u63a5\u7740\u51b0\u5ddd\u5929\u5973\u4e5f\u8d70\u4e86\u4e0b\u6765\uff0c\u6768\u67f3\u9752\u8fd8\u662f\u7b2c\u4e00\u6b21\u548c\u5979\u89c1\u9762\uff0c\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u5929\u4e0b\u7adf\u6709\u5982\u6b64\u7f8e \u4e3d\u7684\u59d1\u5a18\uff01\u201d\u770b\u4e86\u5510\u7ecf\u5929\u4e00\u773c\uff0c\u53c8\u770b\u4e86\u5973\u513f\u4e00\u773c\uff0c\u6697\u6697\u53f9\u606f\u3002\u90b9\u7edb\u971e\u4e00\u58f0\u6b22\u547c\uff0c\u4e0a\u524d\u62c9\u7740\u51b0 \u5ddd\u5929\u5973\u7684\u8863\u8896\uff0c\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8fd9\u56de\u4f60\u53ef\u8d70\u4e0d\u4e86\u5566\uff01\u201d\u56de\u5934\u5bf9\u6bcd\u4eb2\u8bf4\u9053\uff1a\u201c\u90a3\u665a\u7ecf\u5929\u54e5\u54e5\u5728 \u6211\u4eec\u5bb6\u4e2d\u51fa\u8d70\uff0c\u6211\u600e\u4e48\u4e5f\u7559\u4e0d\u4f4f\u4ed6\uff0c\u539f\u6765\u4ed6\u662f\u53bb\u8ffd\u8fd9\u4f4d\u59d0\u59d0\u3002\u201d\u51b0\u5ddd\u5929\u5973\u89c1\u5979\u5982\u6b64\u5929\u771f\u70c2 \u6f2b\uff0c\u60f3\u8d77\u5f53\u65f6\u7684\u8bef\u4f1a\uff0c\u4e0d\u89c9\u4f4e\u7709\u4e00\u7b11\uff0c\u4e5f\u662f\u53d1\u81ea\u5185\u5fc3\u7684\u6b22\u60a6\u7684\u5fae\u7b11\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6842\u534e\u751f\u6015\u4f2f\u7684\u72ec\u751f\u5973\u513f\uff0c\u82b3\u540d\u51b0\u5a25\uff1b\u8fd9\u4f4d\u662f\u90b9\u4f2f\u6bcd\uff0c\u4e09\u5341\u5e74\u524d\uff0c\u9f0e \u9f0e\u5927\u540d\u7684\u6c5f\u4e1c\u5973\u4fa0\u6768\u67f3\u9752\uff0c\u7b97\u8d77\u6765\u6211\u7239\u7239\u8fd8\u662f\u5979\u7684\u5e08\u5f1f\u3002\u201d\u6768\u67f3\u9752\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u90fd\u4e0d \u662f\u5916\u4eba\u3002\u201d\u62c9\u7740\u51b0\u5ddd\u5929\u5973\u7684\u624b\uff0c\u4ed4\u7ec6\u7aef\u8be6\uff0c\u8d8a\u770b\u8d8a\u89c9\u5f97\u5979\u6e05\u96c5\u7edd\u4fd7\uff0c\u8273\u4e3d\u65e0\u4f26\uff0c\u6768\u67f3\u9752\u672c\u6765 \u5bf9\u5979\u6709\u70b9\u5992\u610f\uff0c\u8fd9\u65f6\u4ea6\u89c9\u5f97\u201c\u6211\u89c1\u72b9\u601c\u201d\uff01\u51b0\u5ddd\u5929\u5973\u7ed9\u5979\u770b\u5f97\u4e0d\u597d\u610f\u601d\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u7ecf \u5929\uff0c\u8fd8\u662f\u4f60\u51fa\u624b\u5f97\u5feb\u3002\u90a3\u4e24\u4e2a\u602a\u4eba\u4e0d\u77e5\u662f\u4ec0\u4e48\u9053\u8def\u3002\u786e\u6709\u70b9\u90aa\u95e8\u529f\u592b\uff0c\u770b\u6765\u5c31\u662f\u6211\u53d1\u51fa\u51b0\u9b44 \u795e\u5f39\uff0c\u4e5f\u6253\u9000\u4e0d\u4e86\u4ed6\u4eec\u3002\u201d\u6768\u67f3\u9752\u7b11\u9053\uff1a\u201c\u7ecf\u5929\uff0c\u4f60\u770b\u6211\u591a\u7cca\u6d82\uff0c\u51e0\u4e4e\u5fd8\u4e86\u5411\u4f60\u9053\u8c22\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5176\u5b9e\u6211\u7684\u5929\u5c71\u795e\u8292\u4e5f\u672a\u5fc5\u4f24\u5f97\u4e86\u4ed6\u4eec\uff0c\u4ed6\u4eec\u662f\u7ed9\u6211\u5413\u8d70\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u4eec\u5b9a\u662f\u7ed9\u6211\u7239\u7239\u7684\u795e\u8292\u6253\u65ad\u4e86\u817f\uff0c\u6545\u6b64\u4e00\u89c1\u8fd9\u4e2a \u6697\u5668\uff0c\u5c31\u4ee5\u4e3a\u662f\u6211\u7239\u7239\u6765\u5566\u3002\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u542c\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e34\u8d70\u7684\u8a00\u8bed\uff0c\u5927\u7ea6\u662f\u4f60\u7239 \u7239\u6253\u65ad\u4e86\u4ed6\u4eec\u53cc\u817f\u4e4b\u540e\uff0c\u7b54\u5e94\u8fc7\u9976\u4ed6\u4eec\u7684\u3002\u6240\u4ee5\u521a\u624d\u4ed6\u4eec\u624d\u9a82\u5510\u5927\u4fa0\u4e0d\u987e\u8bfa\u8a00\uff0c\u6562\u60c5\u4ed6\u4eec\u8fd8 \u771f\u6015\u4f60\u4f24\u4ed6\u4eec\u7684\u6027\u547d\u3002\u201d\u5510\u7ecf\u5929\u6c89\u541f\u8bf4\u9053\uff1a\u201c\u770b\u6765\u90a3\u5b64\u5cf0\u4e0a\u7684\u9677\u9631\uff0c\u5fc5\u662f\u8fd9\u4e24\u4e2a\u602a\u4eba\u6240\u5e03\u7f6e \u7684\u65e0\u7591\u3002\u53ea\u4e0d\u77e5\u4ed6\u4eec\u4f55\u6545\u4e0e\u6211\u7239\u7239\u7ed3\u4e0b\u6df1\u4ec7\u5927\u6068\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u5b64\u5cf0\u4e0a\u7684\u9677\u997c\uff1f\u201d\u5510 \u7ecf\u5929\u5c06\u90a3\u65e5\u7684\u4e8b\u60c5\u8bf4\u4e86\uff0c\u6768\u67f3\u9752\u60ca\u559c\u4ea4\u96c6\uff0c\u9053\uff1a\u201c\u539f\u6765\u679c\u7136\u662f\u4f60\u7684\u7239\u7239\u5230\u6b64\u5730\u6765\u4e86\uff0c\u4f46\u559c\u9a6c \u62c9\u96c5\u5c71\u6bd4\u5929\u5c71\u8fd8\u9ad8\u5f97\u591a\u3001\u5927\u5f97\u591a\uff0c\u600e\u751f\u53bb\u627e\uff1f\u5440\uff0c\u6211\u4e5f\u6709\u4e8c\u5341\u591a\u5e74\u6ca1\u89c1\u7740\u4f60\u7684\u7239\u7239\u5566\uff0c\u4f60\u7684 \u7239\u7239\u4e5f\u8bb8\u672a\u8001\uff0c\u6211\u7684\u5934\u4e0a\u5df2\u5f00\u59cb\u6709\u767d\u53d1\u4e86\uff01\u201d \u3000\u3000\u6768\u67f3\u9752\u60f3\u6000\u65e7\u4e8b\uff0c\u7d6e\u7d6e\u4e0d\u4f11\u3002\u90b9\u7edb\u971e\u7b11\u9053\uff1a\u201c\u5988\uff0c\u4f60\u5c3d\u62c9\u7740\u51b0\u5a25\u59d0\u59d0\u505a\u4ec0\u4e48\uff1f\u7ecf\u5929\u54e5\u54e5 \u8981\u5403\u918b\u5566\u3002\u201d\u6768\u67f3\u9752\u4e00\u7b11\u653e\u5f00\u51b0\u5ddd\u5929\u5973\uff0c\u53ea\u89c1\u5973\u513f\u5374\u62c9\u7740\u6c5f\u5357\u8d70\u8fc7\u4e00\u8fb9\uff0c\u4ea4\u5934\u63a5\u8033\uff0c\u597d\u50cf\u5728 \u8bf4\u4ec0\u4e48\u79d8\u5bc6\uff0c\u6c5f\u5357\u8fd8\u4e0d\u65f6\u6324\u7709\u5f04\u773c\u7684\u626e\u9b3c\u8138\u3002\u539f\u6765\u8fd9\u591a\u5634\u7684\u6c5f\u5357\uff0c\u6700\u559c\u6b22\u6253\u542c\u522b\u4eba\u7684\u95f2\u4e8b\uff0c \u4ed6\u4ece\u8427\u9752\u5cf0\u548c\u9648\u5929\u5b87\u90a3\u513f\uff0c\u542c\u5230\u4e00\u4e9b\u5173\u4e8e\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u7684\u4e8b\u60c5\uff0c\u8fd9\u65f6\u6b63\u50cf\u4e00\u4e2a\u8bf4\u4e66\u4eba\u4e00 \u6837\uff0c\u5728\u7ed9\u90b9\u7edb\u971e\u8bf4\u5510\u7ecf\u5929\u4e09\u4e0a\u51b0\u5cf0\uff0c\u9080\u8bf7\u51b0\u5ddd\u5929\u5973\u4e0b\u5c71\u7684\u6545\u4e8b\u5462\u3002\u6768\u67f3\u9752\u5bf9\u7740\u8fd9\u4e2a\u987d\u76ae\u7684\u4e66 \u7ae5\uff0c\u53c8\u597d\u6c14\u53c8\u597d\u7b11\u3002\u518d\u770b\u770b\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd\u5929\u5973\u4eb2\u70ed\u7684\u795e\u60c5\uff0c\u53c8\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u771f \u662f\u5404\u6709\u5404\u7684\u7f18\u4efd\uff0c\u52c9\u5f3a\u4e0d\u6765\u7684\uff01\u3002\u201d \u3000\u3000\u539f\u6765\u6768\u67f3\u9752\u5c11\u65f6\uff0c\u66fe\u5949\u7236\u4eb2\u4e4b\u547d\uff0c\u4e0e\u5510\u6653\u6f9c\u8ba2\u4e0b\u5a5a\u7ea6\uff0c\u5176\u540e\u867d\u56e0\u6027\u60c5\u4e0d\u6295\uff0c\u5404\u81ea\u5a5a\u5ac1\uff0c \u4f46\u5510\u6653\u6f9c\u5230\u5e95\u662f\u6768\u67f3\u9752\u7684\u7b2c\u4e00\u4e2a\u610f\u4e2d\u4eba\uff0c\u8fc7\u4e86\u6570\u5341\u5e74\uff0c\u6768\u67f3\u9752\u7684\u611f\u60c5\u867d\u7136\u65e9\u5df2\u7eaf\u51c0\u5347\u534e\uff0c\u4f46 \u5bf9\u5510\u6653\u6f9c\u7684\u656c\u6155\u5374\u662f\u59cb\u7ec8\u4e0d\u51cf\u3002\u6240\u4ee5\u5979\u5728\u5e74\u524d\u4e00\u89c1\u5510\u7ecf\u5929\u4e4b\u540e\uff0c\u5b9e\u5728\u6709\u610f\u601d\u5c06\u5973\u513f\u8bb8\u914d\u4e8e \u4ed6\uff0c\u800c\u4eca\u89c1\u6b64\u60c5\u5f62\uff0c\u77e5\u9053\u52c9\u5f3a\u4e0d\u5f97\uff0c\u53ea\u597d\u7f62\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u5f53\u665a\u4fbf\u5728\u70fd\u706b\u53f0\u5185\u6b47\u5bbf\uff0c\u7b2c\u4e8c\u65e5\u5510\u7ecf\u5929\u7684\u817f\u4f24\u5df2\u6108\uff0c\u4e00\u884c\u4eba\u7b49\uff0c\u7ee7\u7eed\u897f\u884c\uff0c\u6570\u65e5\u4e4b \u540e\uff0c\u5230\u4e86\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u5357\u8fb9\uff0c\u51b0\u5ddd\u5929\u5973\u89c1\u5c71\u8c37\u4e4b\u4e2d\uff0c\u9690\u9690\u9732\u51fa\u65bd\u65d7\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u96be\u9053 \u662f\u5c3c\u6cca\u5c14\u7684\u519b\u961f\u771f\u4e2a\u6765\u4e86\uff1f\u54b1\u4eec\u4e14\u53bb\u63a2\u5b83\u4e00\u63a2\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u966a\u4f60\u53bb\u3002\u90b9\u4f2f\u6bcd\uff0c \u4f60\u4eec\u6682\u4e14\u4e0d\u8981\u8fdb\u5c71\uff0c\u5f85\u6211\u4eec\u63a2\u660e\u4e4b\u540e\uff0c\u518d\u884c\u5b9a\u593a\u3002\u201d\u4f17\u4eba\u4e4b\u4e2d\u4ee5\u4ed6\u4eec\u4e8c\u4eba\u672c\u9886\u6700\u9ad8\uff0c\u5927\u5bb6\u81ea \u662f\u6beb\u65e0\u5f02\u8bae\u3002 \u3000\u3000\u559c\u9a6c\u62c9\u96c5\u5c71\u5b9e\u5728\u5927\u5f97\u60ca\u4eba\uff0c\u5c71\u4e2d\u8bb8\u591a\u4e0d\u662f\u672a\u7ecf\u4eba\u5230\u7684\u539f\u59cb\u68ee\u6797\uff0c\u65e0\u8def\u53ef\u5bfb\uff0c\u51b0\u5ddd\u592d\u5973\u867d \u7136\u770b\u89c1\u65cf\u65d7\uff0c\u671d\u7740\u90a3\u4e2a\u65b9\u5411\u8d70\u53bb\uff0c\u8fd8\u662f\u8ff7\u5931\u4e86\u8def\uff0c\u8d70\u4e86\u534a\u5929\uff0c\u6709\u65f6\u542c\u5f97\u6218\u9a6c\u5636\u9e23\u4e4b\u58f0\uff0c\u597d\u50cf \u5c31\u5728\u9644\u8fd1\uff0c\u8f6c\u8fc7\u5c71\u62d7\uff0c\u5374\u53c8\u662f\u53e6\u4e00\u4e2a\u8352\u51c9\u7684\u5c71\u8c37\u3002\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u771f\u5f97\u8981\u627e\u4e2a\u5411\u5bfc\u624d\u884c\u3002\u201d \u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u75f4\u4eba\u8bf4\u68a6\uff0c\u4f60\u5c31\u662f\u51fa\u5343\u4e24\u9ec4\u91d1\u3002\u201d\u4e5f\u65e0\u4eba\u6562\u966a\u4f60\u6500\u767b\u6b64\u5c71\u3002\u201d\u5510\u7ecf\u5929\u5ffd \u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u89c1\u5f97\uff0c\u4f60\u77a7\uff0c\u90a3\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5bf9\u9762\u7684\u4e00\u5ea7\u5c71\u5cf0\u4e0a\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u77eb\u6377\u5982\u733f\uff0c\u8f7b\u767b\u5de7\u7eb5\uff0c\u8d8a\u4e0a\u8d8a \u9ad8\uff0c\u540e\u9762\u7ea6\u6709\u4e94\u516d\u4e2a\u4eba\u8ffd\u8d76\uff0c\u4e2a\u4e2a\u90fd\u662f\u4e00\u8eab\u4e0a\u4e58\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u4e3a\u9996\u7684\u4f3c\u4e4e\u662f\u4e2a\u50e7\u4eba\uff0c\u62ab\u7740\u4e00 \u4ef6\u5927\u7ea2\u8888\u88df\uff0c\u8fce\u98ce\u62db\u5c55\uff0c\u5206\u5916\u593a\u76ee\u3002 \u3000\u3000\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u5148\u5934\u9003\u8d70\u7684\u90a3\u4eba\u662f\u9f99\u7075\u77eb\uff1a\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u540e\u9762\u8fd9\u4e2a\u80e1\u50e7\u4e00\u5b9a \u662f\u5510\u7aef\u6240\u8bf4\u7684\u90a3\u4e2a\u52ab\u72f1\u7684\u80e1\u50e7\u4e86\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4ed6\u4eec\u8ffd\u8d76\u9f99\u7075\u77eb\u5b9a\u975e\u597d\u4e8b\uff0c\u54b1\u4eec\u622a\u4f4f \u4ed6\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u9f99\u7075\u77eb\u7684\u80cc\u5f71\u5df2\u53ea\u89c1\u4e00\u4e2a\u9ed1\u70b9\uff0c\u540e\u9762\u90a3\u51e0\u4e2a\u4eba\u5f71\u5b50\u4e5f\u6a21\u7cca\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4ece\u4fa7\u8fb9\u7ed5\u8fc7\u53bb\u515c\u622a\u4ed6\uff0c\u8ba4\u5b9a\u90a3\u4e2a\u5927\u7ea2\u8888\u88df\uff01\u201d\u4e24\u5ea7\u5c71\u5cf0\u76f8\u8ddd\u4e0d \u8fdc\uff0c\u5927\u7ea2\u8888\u88df\u53c8\u662f\u6700\u6613\u8fa8\u8ba4\u7684\u65e5\u6807\uff0c\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u7684\u8f7b\u8eab\u672c\u9886\uff1b\u6bd4\u4e4b\u9f99\u7075\u77eb\u4e0e\u90a3\u80e1\u618e\u90fd \u8981\u9ad8\u51fa\u4e00\u7b79\uff0c\u5510\u7ecf\u5929\u53c8\u6709\u6e38\u9f99\u5b9d\u5251\u5f00\u8def\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u5df2\u4ece\u53e6\u4e00\u4e2a\u65b9\u5411\uff0c\u7ed5\u5230\u80e1\u50e7\u7684 \u524d\u5934\uff0c\u9f99\u7075\u77eb\u6b63\u5728\u6500\u4e0a\u7b2c\u4e8c\u4e2a\u5c71\u5cf0\uff0c\u800c\u5176\u4ed6\u51e0\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\u5374\u8fd8\u8fdc\u8fdc\u843d\u5728\u80e1\u50e7\u540e\u9762\u3002 \u3000\u3000\u539f\u6765\u9f99\u7075\u77eb\u5728\u5c3c\u6cca\u5c14\u519b\u8425\u4e2d\u4f4f\u4e86\u51e0\u65e5\uff0c\u5de6\u60f3\u6709\u60f3\uff0c\u867d\u7136\u6709\u4e89\u5929\u4e0b\u7684\u96c4\u5fc3\uff0c\u4f46\u7ec8\u4e0d\u613f\u8d1f\u6c49 \u597d\u4e4b\u540d\uff0c\u5f15\u5916\u5175\u5165\u5bc7\u672c\u56fd\uff0c\u662f\u4ee5\u4e0b\u4e86\u6781\u5927\u51b3\u5fc3\uff0c\u62fc\u7740\u4e3a\u6e05\u5ef7\u8bdb\u622e\uff0c\u4ece\u5c3c\u6cca\u5c14\u519b\u4e2d\u9003\u4e86\u51fa\u6765\uff0c \u51c6\u5907\u56de\u5230\u62c9\u8428\uff0c\u5c06\u5c3c\u6cca\u5c14\u519b\u7684\u90e8\u7f72\u544a\u8bc9\u798f\u5eb7\u5b89\u77e5\u9053\u3002\u4e0d\u6599\u5c3c\u6cca\u5c14\u519b\u4e2d\u4e5f\u9887\u6709\u80fd\u4eba\uff0c\u9f99\u7075\u77eb\u4e00 \u9003\u8d70\u4fbf\u7ed9\u53d1\u73b0\u3002\u90a3\u80e1\u50e7\u7387\u9886\u56db\u540d\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u5df2\u8ffd\u4e86\u4e00\u65e5\u4e00\u591c\u3002 \u3000\u3000\u9f99\u7075\u77eb\u4e0d\u6562\u9003\u4e0b\u5e73\u5730\uff0c\u4e13\u5411\u8349\u83bd\u5bc6\u9752\u7684\u5c71\u5934\u9003\u533f\uff0c\u8ffd\u9010\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u96ea\u6ed1\u5761 \u9661\uff0c\u5c71\u8def\u8d8a\u6765\u8d8a\u96be\u8d70\u4e86\u3002\u8fd9\u65f6\u9f99\u7075\u77eb\u6b63\u5728\u6500\u767b\u7b2c\u4e8c\u5ea7\u5c71\u5cf0\uff0c\u5c71\u4e0a\u602a\u77f3\u906e\u4e91\uff0c\u85e4\u8513\u5982\u969c\uff0c\u80e1\u50e7 \u5fc3\u9053\uff1a\u201c\u82e5\u88ab\u4ed6\u9003\u4e0a\u5c71\u5934\uff0c\u66f4\u96be\u5bfb\u89c5\u4e86\u3002\u201d\u63d0\u4e00\u53e3\u6c14\uff0c\u7d27\u7d27\u8ddf\u7740\u4e0a\u53bb\u3002\u8fd9\u80e1\u50e7\u540d\u5524\u6cf0\u5409\u63d0\uff0c \u662f\u5c3c\u6cca\u5c14\u7684\u7b2c\u4e00\u56fd\u5e08\uff0c\u8f7b\u529f\u786e\u6709\u6781\u9ad8\u7684\u9020\u8be3\uff0c\u8fd9\u4e00\u8dc3\u5e73\u5730\u62d4\u8d77\uff0c\u5c45\u7136\u8dc3\u4e0a\u4e86\u4e8c\u4e08\u6709\u4f59\uff0c\u4f46\u5c71 \u4e0a\u79ef\u96ea\u6ca1\u80dc\uff0c\u5e73\u6ed1\u5982\u955c\uff0c\u811a\u4e00\u7740\u5730\uff0c\u53c8\u6ed1\u4e0b\u4e09\u5c3a\u6709\u591a\uff0c\u770b\u90a3\u9f99\u7075\u77eb\u65f6\uff0c\u4e5f\u662f\u5982\u6b64\uff0c\u4e0a\u4e24\u6b65\u9000 \u4e00\u6b65\u7684\u4e0d\u6562\u98de\u817e\u8df3\u8dc3\uff0c\u9f99\u7075\u77eb\u7684\u8f7b\u529f\u4e0e\u6cf0\u5409\u63d0\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4f46\u5728\u8fd9\u6837\u9661\u524a\u7684\u659c\u5761\u4e0a\uff0c\u5927\u5bb6\u90fd \u96be\u4ee5\u5982\u610f\u65bd\u5c55\uff0c\u9f99\u7075\u77eb\u5360\u4e86\u5148\u8d70\u7684\u4fbf\u5b9c\uff0c\u8fd9\u65f6\u8ddd\u79bb\u90a3\u80e1\u50e7\u5df2\u6709\u767e\u6765\u6b65\u8fdc\u3002 \u3000\u3000\u90a3\u80e1\u50e7\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u5730\u628a\u8888\u88df\u8131\u4e0b\uff0c\u8fce\u98ce\u4e00\u5c55\uff0c\u597d\u4f3c\u5927\u9e1f\u7684\u53cc\u7ffc\uff0c\u98ce\u4ece\u4e0a\u9762\u5439\u4e0b\u6765\uff0c\u4ed6 \u8888\u88df\u515c\u98ce\uff0c\u5411\u4e0a\u4e00\u8dc3\uff0c\u501f\u6625\u98ce\u7684\u963b\u529b\uff0c\u5c45\u7136\u5c06\u8eab\u5f62\u5b9a\u4f4f\uff0c\u4e0d\u518d\u6ed1\u4e0b\uff0c\u90a3\u80e1\u50e7\u54c8\u54c8\u5927\u7b11\uff0c\u5411\u4e0a \u62db\u624b\u9053\uff1a\u201c\u5e74\u5148\u751f\uff0c\u56fd\u738b\u5f85\u4f60\u4e0d\u8584\uff0c\u4f55\u6545\u9003\u8d70\uff1f\u518d\u8bf4\uff0c\u6211\u5192\u4e86\u6027\u547d\u4e4b\u9669\uff0c\u4ece\u62c9\u8428\u6551\u4f60\u53e3\u6765\uff0c \u4f60\u8fd9\u6837\u4e0d\u8f9e\u800c\u884c\uff0c\u4f3c\u4e4e\u4e5f\u8fdd\u4e86\u4e2d\u56fd\u5723\u4eba\u7684\u53e4\u8bad\uff0c\u592a\u4e0d\u591f\u670b\u53cb\u7684\u4ea4\u60c5\u4e86\u5427\uff1f\u201d\u9f99\u7075\u77eb\u5934\u4e5f\u4e0d \u56de\uff0c\u62fc\u547d\u6500\u722c\uff0c\u90a3\u80e1\u618e\u58f0\u8c03\u4e00\u53d8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5e74\u5148\u751f\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u4e0b\u6765\u5427\uff0c\u656c\u9152\u4e0d\u5403\u5403\u7f5a \u9152\uff0c\u90a3\u4f55\u82e6\u6765\uff1f\u88ab\u6211\u8ffd\u4e0a\u90a3\u5c31\u4e0d\u597d\u770b\u76f8\u4e86\u3002\u201d\u8888\u88df\u4e00\u5c55\uff0c\u5411\u4e0a\u53c8\u8dc3\u4e86\u4e08\u4f59\u3001 \u3000\u3000\u8fd9\u80e1\u50e7\u80dc\u5238\u5728\u63e1\uff0c\u6b63\u81ea\u5f97\u610f\uff0c\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u5578\uff0c\u4e00\u9053\u6697\u8d64\u8272\u7684\u5149\u534e\u5288\u9762\u5c04 \u6765\uff0c\u90a3\u80e1\u618e\u6296\u8d77\u8888\u88df\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u8888\u88df\u767b\u65f6\u7a7f\u4e86\u4e8c\u4e2a\u5927\u6d1e\uff0c\u597d\u50cf\u6233\u7834\u7684\u98ce\u5e06\uff0c\u5931\u4e86\u4f5c \u7528\uff0c\u90a3\u80e1\u50e7\u63aa\u4e0d\u53ca\u9632\uff0c\u811a\u6b65\u4e00\u6ed1\uff0c\u5411\u4e0b\u6ed1\u4e86\u51e0\u4e08\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002\u8fd9\u80e1\u50e7\u7684\u8888\u88df\u662f\u91d1\u4e1d\u6240\u7ec7\uff0c\u52a0 \u4e0a\u4ed6\u7684\u5185\u529b\u8fd0\u7528\uff0c\u8d5b\u8fc7\u4e00\u9762\u76fe\u724c\uff0c\u5341\u6570\u65e5\u524d\uff0c\u4ed6\u5c31\u66fe\u7528\u8fd9\u4ef6\u8888\u88df\uff0c\u6321\u8fc7\u5510\u8d5b\u82b1\u7684\u8bf8\u822c\u6697\u5668\uff0c \u4e0d\u6599\u7adf\u7ed9\u8fd9\u9aa4\u7136\u5176\u6765\u3001\u83ab\u540d\u5176\u5999\u7684\u6697\u5668\u5c04\u7a7f\uff0c\u4e0d\u7531\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c71\u58a9\u5904\u6251\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6b63\u662f\u5510\u7ecf\u5929\uff0c\u80e1\u50e7\u4e00\u89c1\u662f\u4e2a\u5507\u7ea2\u9f7f\u767d\u7684\u5c11\u5e74\uff0c \u9a84\u5ff5\u53c8\u8d77\uff0c\u8888\u88df\u4e00\u5c55\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\u4eba\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u662f\u8c01\u4eba\uff1f\u6211\u5c31\u662f\u4e0d\u51c6 \u4f60\u4e0a\u8fd9\u5ea7\u5c71\uff01\u201d\u80e1\u50e7\u5927\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u51ed\u4f60\u4e5f\u914d\uff1f\u201d\u6325\u52a8\u8888\u88df\uff0c\u4e00\u4e2a\u76d8\u65cb\uff0c\u7a81\u7136\u51cc\u7a7a\u7f69\u4e0b\uff0c \u4ed6\u4ee5\u4e3a\u5510\u7ecf\u5929\u53ea\u662f\u6697\u5668\u5389\u5bb3\uff0c\u8fd8\u672a\u66fe\u5c06\u4ed6\u653e\u5728\u5fc3\u4e0a\uff0c\u8fd9\u4e00\u62db\u6b63\u662f\u90a3\u80e1\u50e7\u82e6\u7ec3\u4e86\u5341\u591a\u5e74\u7684\u529f \u592b\uff0c\u540d\u4e3a\u201c\u5929\u7f57\u76d6\u5730\uff01\u201d\u591a\u5f3a\u7684\u6b66\u529f\uff0c\u88ab\u4ed6\u7f69\u7740\u4e5f\u662f\u65e0\u80fd\u4e3a\u529b\uff01 \u3000\u3000\u8888\u88df\u7f69\u4e0b\uff0c\u547c\u547c\u631f\u98ce\uff0c\u6709\u5982\u4e00\u5ea7\u5c0f\u5c71\uff0c\u7a81\u7136\u7ed9\u90a3\u80e1\u50e7\u79fb\u6765\u4e00\u6837\uff0c\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u51db\uff1a\u602a\u4e0d \u5f97\u5510\u8001\u592a\u5a46\u4e0e\u91d1\u4e16\u9057\u5bf9\u4ed6\u4e5f\u5360\u4e0d\u4e86\u8ba9\u98ce\uff0c\u679c\u771f\u6709\u51e0\u5206\u672c\u9886\uff01\u4e0d\u6562\u6020\u6162\uff0c\u6e38\u9f99\u5ba4\u5251\u626c\u7a7a\u4e00\u95ea\uff0c \u7acb\u523b\u8fd8\u4e86\u4e00\u62db\u201c\u540e\u5f02\u5c04\u6708\u201d\u7684\u62db\u6570\uff01 \u3000\u3000\u6e38\u9f99\u5251\u4e43\u662f\u5929\u5c71\u6d3e\u7684\u9547\u5c71\u4e4b\u5b9d\uff0c\u4fbf\u771f\u7684\u662f\u9762\u94c1\u724c\uff0c\u4e5f\u7ed9\u5b83\u6233\u7a7f\u4e86\uff0c\u4f55\u51b5\u8fd9\u4ef6\u8888\u88df\uff0c\u53ea\u542c \u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5149\u95ea\u5904\uff0c\u8888\u88df\u53cd\u7a7f\u4e86\u4e00\u4e2a\u6c34\u6d1e\u3002\u8fd9\u4e00\u4e0b\uff0c\u90a3\u80e1\u50e7\u66f4\u662f\u5403\u60ca\uff0c\u8888\u88df\u4e00\u6536\uff0c \u6d88\u4e86\u5510\u7ecf\u5929\u7684\u5251\u52bf\uff0c\u5148\u62a4\u7740\u8eab\u5b50\uff0c\u518d\u6253\u91cf\u654c\u4eba\u3002\u5510\u7ecf\u5929\u786c\u63a5\u4e86\u4e00\u62db\uff0c\u867d\u7136\u628a\u80e1\u50e7\u7684\u8888\u88df\u6233 \u7a7f\uff0c\u81ea\u5df1\u7684\u81c2\u818a\u4e5f\u89c9\u75bc\u75db\u3002 \u3000\u3000\u90a3\u80e1\u50e7\u8888\u88df\u4e00\u5c55\uff0c\u53d8\u62db\u518d\u6251\uff0c\u7ecf\u8fd9\u4e00\u62db\uff0c\u4ed6\u5df2\u8bd5\u51fa\u5510\u7ecf\u5929\u81c2\u529b\u7a0d\u900a\uff0c\u62fc\u7740\u8888\u88df\u518d\u88ab\u5b9d\u5251 \u6233\u7a7f\u51e0\u4e2a\u5927\u6d1e\uff0c\u628a\u8888\u88df\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u7528\u7ede\u626f\u7684\u624b\u6cd5\u786c\u62a2\u5510\u7ecf\u5929\u7684\u5b9d\u5251\uff0c\u5510\u7ecf\u5929\u51dd\u795e\u5e94\u6218\uff0c \u970e\u773c\u4e4b\u95f4\uff0c\u8fc7\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u8888\u88df\u4e0a\u88ab\u5251\u5c16\u6233\u7a7f\u7684\u5c0f\u6d1e\u5bc6\u5982\u5cf0\u7a9d\uff0c\u90a3\u80e1\u50e7\u5140\u662f\u52c7\u6218\u4e0d\u9000\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8fd9\u65f6\u5df2\u4ece\u53e6\u4e00\u8fb9\u7ed5\u5230\uff0c\u5979\u7684\u8f7b\u529f\u672c\u6765\u6bd4\u5510\u7ecf\u5929\u8fd8\u9ad8\uff0c\u4f46\u8346\u68d8\u906e\u8def\uff0c\u5979\u7684\u51b0\u5251\u5374 \u4e0d\u5982\u5510\u7ecf\u5929\u7684\u6e38\u9f99\u5251\u6765\u5f97\u597d\u4f7f\uff0c\u662f\u4ee5\u53cd\u800c\u6765\u8fdf\u4e86H\u76cf\u8336\u7684\u65f6\u523b\u3002\u90a3\u80e1\u50e7\u6b63\u5728\u9ad8\u547c\u9163\u6597\uff0c\u5ffd\u89c1 \u51b0\u5ddd\u5929\u5973\u767d\u8863\u98d8\u98d8\uff0c\u6709\u5982\u4ed9\u5973\u5fa1\u98ce\uff0c\u7a81\u7136\u98d8\u5230\u9762\u524d\uff0c\u53ea\u89c9\u76ee\u7729\u795e\u8ff7\uff0c\u614c\u5fd9\u540e\u9000\u51e0\u6b65\u3002\u51b0\u5ddd\u5929 \u5973\u6309\u5251\u65a5\u9053\uff1a\u201c\u5c3c\u4e2d\u4e24\u56fd\u4e16\u4ee3\u4ea4\u597d\uff0c\u4f60\u4eec\u4e3a\u4f55\u5984\u6765\u6311\u8845\uff1f\u8fd8\u6562\u8d8a\u5883\u6355\u4eba\uff01\u5feb\u7ed9\u6211\u6eda\u56de\u53bb\uff01\u201d \u58f0\u97f3\u6e05\u8106\uff0c\u5b9b\u82e5\u94f6\u94c3\uff0c\u4f46\u5374\u53e6\u5177\u4e00\u79cd\u5a01\u4e25\uff0c\u6559\u4eba\u6151\u670d\u3002\u90a3\u80e1\u50e7\u4e0d\u89c9\u53c8\u540e\u9000\u51e0\u6b65\uff0c\u4f46\u4ed6\u662f\u7b2c\u4e00 \u56fd\u5e08\u7684\u8eab\u4efd\uff0c\u5c3c\u6cca\u5c14\u56fd\u7389\u4e5f\u4e0d\u6562\u5bf9\u4ed6\u5982\u6b64\u547c\u559d\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u65cb\u5373\u6012\u6c14\u53c8\u751f\uff0c\u8888\u88df\u518d\u5c55\uff0c\u51b7\u7b11 \u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u6562\u6765\u5e72\u9884\u6211\u56fd\u4e4b\u4e8b\uff0c\u54fc\uff0c\u54fc\uff01\u597d\u5927\u7684\u53e3\u6c14\uff01\u54ce\u54df\uff0c\u4e5e\u55e4\uff01\u201d\u539f\u6765\u662f\u51b0\u5ddd \u5929\u5973\u8f7b\u8f7b\u5f39\u51fa\u4e00\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u9976\u662f\u8fd9\u80e1\u50e7\u5185\u529f\u6df1\u539a\uff0c\u8888\u88df\u53ca\u65e9\u6321\u5f00\uff0c\u4f46\u4e5f\u4e0d\u81ea\u5df1\u7684\u6253\u4e86\u4e00\u4e2a \u5bd2\u55c5\u3002\u767b\u65f6\u6014\u5728\u5f53\u573a\uff0c\u731b\u7684\u60f3\u8d77\u4e00\u4e8b\u3002\u90a3\u51b0\u5f39\u7684\u51b7\u6c14\u8fd8\u672a\u80fd\u4f7f\u4ed6\u98a4\u6296\uff0c\u60f3\u8d77\u6b64\u4e8b\uff0c\u5374\u4e0d\u7531\u5f97 \u6296\u7d22\u8d77\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u540e\u9762\u51e0\u4e2a\u58f0\u97f3\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u53e9\u89c1\u516c\u4e3b\uff01\u201d\u90a3\u80e1\u50e7\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u8ddf\u7740\u81ea\u5df1\u6765\u7684\u56db \u540d\u6b66\u58eb\uff0c\u5728\u540e\u9762\u4e00\u6392\u8dea\u5012\uff0c\u8fd9\u80e1\u50e7\u5927\u60ca\u5931\u8272\uff0c\u5fc3\u9053\uff1a\u201c\u679c\u7136\u662f\u5979\uff01\u201d\u539f\u6765\u8fd9\u80e1\u50e7\u6cf0\u5409\u63d0\u4e43\u662f \u4ee5\u524d\u90a3\u4e2a\u66fe\u4e0a\u8fc7\u51b0\u5cf0\uff0c\u540e\u6765\u9001\u547d\u5728\u9648\u5929\u5b87\u4e4b\u624b\u7684\u90a3\u4e2a\u7ea2\u8863\u756a\u50e7\u7684\u5e08\u5144\uff0c\u4ed6\u4e5f\u66fe\u542c\u5e08\u5f1f\u8bf4\u8fc7\u51b0 \u9b44\u795e\u5f39\u7684\u795e\u5f02\uff0c\u800c\u4eca\u4eb2\u8eab\u9047\u5230\uff0c\u81ea\u7136\u4e5f\u4fbf\u77e5\u9053\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u8eab\u4efd\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u614c\u5fd9\u8c22\u7f6a\uff0c\u51b0\u5ddd\u5929\u5973\u8f7b\u8f7b\u6446\u624b\uff0c\u671d\u7740\u8dea\u5728\u524d\u9762\u8fd9\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4e00\u6325\uff0c\u65a5\u9053\uff1a \u201c\u6211\u5429\u5490\u8fc7\u4f60\u4eec\uff0c\u4e0d\u8bb8\u518d\u5230\u4e2d\u56fd\u5883\u5185\u6363\u4e71\uff0c\u4f60\u4eec\u4e3a\u4f55\u4e0d\u542c\uff1f\u201d\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u8bda\u60f6\u8bda\u6050\u7684 \u7b54\u9053\uff1a\u201c\u56fd\u738b\u6709\u547d\uff0c\u4e0d\u6562\u4e0d\u6765\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u56fd\u738b\u5728\u54ea\u513f\uff1f\u201d\u5c3c\u6cca\u5c14\u6b66\u58eb\u7b54\u9053\uff1a\u201c\u56fd\u738b \u7387\u9886\u5927\u519b\uff0c\u9a7b\u5c6f\u5728\u5357\u9762\u7684\u5c71\u8c37\u8fc7\u51ac\u3002\u201d\u6cf0\u5409\u63d0\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u56fd\u738b\u6b64\u6b21\u524d\u6765\uff0c\u6b63\u662f\u4e3a\u4e86\u627e\u5bfb\u516c \u4e3b\uff0c\u516c\u4e3b\u6765\u4e86\uff0c\u7701\u5f97\u5927\u519b\u8dcb\u6d89\u4e4b\u52b3\uff0c\u771f\u662f\u597d\u6781\u4e86\uff1a\u8bf7\u516c\u4e3b\u79fb\u7389\uff0c\u5230\u519b\u4e2d\u76f8\u89c1\u3002\u201d\u51b0\u5ddd\u5929\u5973 \u9053\uff1a\u201c\u597d\uff0c\u4ed6\u4e0d\u627e\u6211\uff0c\u6211\u4e5f\u8981\u627e\u4ed6\uff01\u201d \u3000\u3000\u6cf0\u5409\u63d0\u4e00\u542c\u51b0\u5ddd\u5929\u5973\u613f\u53bb\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u60f3\u9053\uff1a\u201c\u653e\u8d70\u4e86\u4e00\u4e2a\u9f99\u7075\u77eb\uff0c\u8bf7\u6765\u4e86\u516c\u4e3b\uff0c\u8fd9\u529f \u52b3\u53ef\u5927\u5f97\u591a\u4e86\u3002\u201d\u4e8e\u662f\u547d\u4ee4\u90a3\u56db\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u5728\u524d\u5f00\u8def\uff0c\u4e00\u884c\u4eba\u53c8\u518d\u8d70\u4e0b\u5c71\u5761\uff0c\u7a7f\u8fc7\u5e7d\u8c37\u3002 \u5510\u7ecf\u5929\u62ac\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u5c71\u5cf0\u4e0a\u4e91\u6c14\u5f25\u6f2b\uff0c\u96ea\u5149\u5728\u96ea\u5e55\u4e2d\u95ea\u52a8\uff0c\u518d\u9ad8\u5904\u5219\u8fde\u5c71\u5cf0\u7684\u9762\u8c8c\u4e5f\u770b\u4e0d \u6e05\u695a\uff0c\u66f4\u4e0d\u8981\u8bf4\u9f99\u7075\u77eb\u7684\u8e2a\u8ff9\u4e86\u3002 \u3000\u3000\u5e78\u559c\u6cf0\u5409\u63d0\u4ed6\u4eec\u5e26\u6709\u5e10\u5e55\uff0c\u665a\u4e0a\u4fbf\u5728\u5c71\u8c37\u5bbf\u8425\uff0c\u7b2c\u4e8c\u65e5\u518d\u8d70\u4e86\u534a\u5929\uff0c\u624d\u9690\u9690\u542c\u89c1\u6218\u9a6c\u7684 \u5636\u545c\uff0c\u6cf0\u5409\u63d0\u5e26\u6709\u6307\u5357\u9488\u3001\u6821\u51c6\u65b9\u5411\uff0c\u5bf9\u51b0\u5ddd\u5929\u5973\u8bf4\u9053\uff1a\u201c\u518d\u5411\u5357\u9762\u8d70\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5927\u7ea6\u5c31\u53ef \u5230\u4e86\u3002\u56fd\u738b\u5f97\u4f1a\u516c\u4e3b\uff0c\u4e0d\u77e5\u8be5\u591a\u9ad8\u5174\u5462\uff01\u201d\u51b0\u5ddd\u5929\u5973\u5e94\u4e86\u4e00\u58f0\uff0c\u51b7\u7136\u81ea\u82e5\u7684\u770b\u7740\u5929\u9645\u6d6e\u4e91\uff0c \u4efb\u80e1\u50e7\u642d\u8baa\uff0c\u5979\u603b\u4e0d\u80af\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5374\u662f\u601d\u6f6e\u6c79\u6d8c\uff0c\u4e0d\u80fd\u5e73\u9759\u3002\u51b0\u5ddd\u5929\u5973\u4e4b\u6240\u4ee5\u80af\u6765\u4f1a\u89c1\u5c3c\u6cca\u5c14\u738b\u672c\u662f\u51fa\u4e8e\u4ed6\u7684\u9f13 \u52b1\uff0c\u4f46\u5982\u4eca\u8d70\u8fd1\u4e86\u5c3c\u6cca\u5c14\u7684\u519b\u8425\uff0c\u5c06\u6765\u4f1a\u751f\u51fa\u4ec0\u4e48\u98ce\u6ce2\uff0c\u5374\u662f\u96be\u4ee5\u9884\u6599\uff0c\u5fc3\u4e2d\u7981\u4e0d\u5f80\u5fd0\u5fd1\u4e0d \u5b89\u3002\u770b\u51b0iil\u5929\u5973\u5374\u4ecd\u662f\u90a3\u6837\u9547\u9759\u81ea\u5982\uff0c\u6d77\u6c34\u4e00\u6837\u6e5b\u84dd\u7684\u773c\u73e0\u95ea\u5440\u95ea\u7684\uff0c\u8c01\u4e5f\u731c\u4e0d\u900f\u5979\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u81ea\u6c89\u601d\uff0c\u5ffd\u542c\u5f97\u51b0\u5ddd\u5929\u5973\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u90a3\u80e1\u50e7\u4e5f\u8df3\u4e86\u8d77\u6765\uff0c\u5510\u7ecf\u5929\u968f\u7740\u4ed6\u4eec \u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u58f0\u5e73\u6ed1\u5982\u955c\u7684\u5ca9\u77f3\u4e0a\uff0c\u7559\u4e0b\u4e00\u9053\u6df1\u6df1\u7684\u62d0\u5370\uff0c\u90a3\u4e0d\u662f\u91d1\u4e16\u9057\u7684\u94c1\u62d0 \u5370\u8fd8\u662f\u4ec0\u4e48\uff1b\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ed6\u8fd8\u7559\u4e0b\u51e0\u884c\u5b57\uff01\u201d\u5510\u7ecf\u5929\u8bfb\u9053\uff1a\u201c\u4eba\u95f4\u767d\u773c\u66fe\u7ecf\u60ef\uff0c\u7559\u5f97\u4f59 \u751f\u53c8\u82e5\u4f55\uff1f\u6b32\u4e0a\u9752\u5929\u6458\u661f\u6597\uff0c\u586b\u5e73\u4e1c\u6d77\u4e0d\u626c\u6ce2\uff01\u201d\u60f3\u4e0d\u5230\u91d1\u4e16\u9057\u75af\u75af\u766b\u766b\uff0c\u8fd9\u4e00\u9996\u8bd7\u5374\u5199\u5f97 \u8d85\u8131\u8c6a\u8fc8\uff0c\u9976\u6709\u4ed9\u610f\uff0c\u8bd7\u4e2d\u8574\u85cf\u7740\u591a\u5c11\u6124\u6fc0\u4e0e\u4e0d\u5e73\uff0c\u4f46\u5374\u5e76\u65e0\u5411\u4eba\u95f4\u62a5\u590d\u4e4b\u5ff5\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d \u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u771f\u662f\u4eba\u4e4b\u5c06\u6b7b\uff0c\u4fbf\u9732\u51fa\u81f3\u6027\u771f\u60c5\uff1f\u91d1\u4e16\u9057\u4e00\u751f\u6124\u4e16\u5ac9\u4fd7\uff0c\u8c01\u77e5\u4ed6\u5374\u662f\u9762 \u51b7\u5fc3\u70ed\u7684\u60b2\u6b3e\u6177\u6168\u4e4b\u58eb\uff1f\u5440\uff0c\u770b\u4ed6\u7684\u8bd7\u610f\uff0c\u771f\u662f\u60f3\u6500\u4e0a\u9ad8\u63a5\u9752\u5929\u7684\u73e0\u5cf0\u53bb\u5bfb\u6b7b\uff0c\u8fd9\u4e2a\u60f3\u6cd5\u4e5f \u592a\u602a\u8bde\u4e86\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8f7b\u8f7b\u7684\u4e00\u58f0\u53f9\u606f\uff0c\u9053\uff1a\u201c\u5728\u8fd9\u6837\u7684\u5927\u5c71\u4e2d\u5374\u600e\u751f\u53bb\u627e\u4ed6\uff1f\u201d\u6cf0\u5409\u63d0\u9053\uff1a\u201c\u4ed6\u662f \u4ec0\u4e48\u4e48\u4eba\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e00\u4e2a\u7279\u7acb\u72ec\u884c\u7684\u670b\u53cb\u3002\u201d\u6cf0\u5409\u63d0\u66fe\u88ab\u91d1\u4e16\u9057\u6253\u8fc7\u4e00\u62d0\uff0c\u5f53\u7136\u8ba4 \u5f97\u91d1\u4e16\u9057\u7684\u62d0\u5370\uff0c\u542c\u8bf4\u4ed6\u7adf\u662f\u51b0\u5ddd\u5929\u5973\u7684\u670b\u53cb\uff0c\u5fc3\u4e2d\u6697\u60ca\u3002\u51b0\u5ddd\u5929\u5973\u5374\u5728\u72ec\u81ea\u601d\u91cf\uff0c\u5e0c\u671b\u65e9 \u65e9\u7ed3\u675f\u4e86\u4e0e\u6ce5\u6cca\u5c14\u56fd\u738b\u4f1a\u89c1\u4e4b\u4e8b\uff0c\u4fbf\u9080\u5510\u7ecf\u5929\u767b\u5c71\u53bb\u641c\u5bfb\u91d1\u4e16\u9057\u7684\u8e2a\u8ff9\uff0c\u4f46\u4e00\u60f3\u5230\u8fd9\u6837\u7684\u5927 \u5c71\u4e2d\u53bb\u5bfb\u627e\u4e00\u4e2a\u4eba\uff0c\u90a3\u771f\u65e0\u5f02\u4e8e\u5927\u6d77\u5bfb\u9488\uff0c\u518d\u4e00\u7b97\uff0c\u91d1\u4e16\u9057\u7684\u751f\u547d\u671f\u9650\u53ea\u6709\u5341\u5929\uff0c\u90a3\u66f4\u662f\u51f6 \u591a\u5409\u5c11\u7684\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u95f7\u95f7\u4e0d\u4e50\uff0c\u4e0d\u77e5\u4e0d\u89c9\u968f\u7740\u90a3\u80e1\u50e7\u8d70\u4eba\u5357\u9762\u7684\u4e00\u4e2a\u5927\u5c71\u8c37\uff0c\u4f46\u89c1\u5e10\u5e55\u8fde\u8425\uff0c\u80e1\u9a6c \u5636\u9e23\uff0c\u8c37\u4e2d\u6d9f\u65d7\u62db\u5c55\uff0c\u5200\u67aa\u5982\u96ea\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u5927\u519b\uff1f\u6cf0\u5409\u63d0\u5148\u9063\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u4eba\u738b\u8425\u62a5 \u544a\uff0c\u8c37\u4e2d\u7684\u519b\u961f\u542c\u8bf4\u662f\u524d\u738b\u7684\u516c\u4e3b\u5230\u6765\uff0c\u5c06\u4ee4\u4e5f\u7981\u5236\u4e0d\u4f4f\uff0c\u90fd\u5954\u51fa\u6765\u770b\uff01 \u3000\u3000\u81ea\u4ece\u5c3c\u6cca\u5c14\u7684\u524d\u4efb\u56fd\u5e08\uff0c\u90a3\u4e2a\u7ea2\u8863\u756a\u50e7\uff0c\u4ece\u51b0\u5cf0\u5f52\u6765\uff0c\u5e26\u56de\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u6d88\u606f\u4e4b\u540e\uff0c\u5c3c \u6cca\u5c14\u56fd\u4e2d\u4fbf\u6d41\u4f20\u7740\u51b0\u5ddd\u5929\u5973\u7684\u79cd\u79cd\u795e\u8bdd\u3002\u8fd9\u65f6\u542c\u8bf4\u51b0\u5ddd\u5929\u5973\u5230\u6765\uff0c\u6570\u4e07\u5927\u519b\u90fd\u4e89\u7740\u51fa\u6765\u770b\uff0c \u5608\u6742\u58f0\u3001\u811a\u6b65\u58f0\u9707\u64bc\u5c71\u8c37\u3002\u5ffd\u89c1\u51b0\u5ddd\u5927\u5973\u5728\u8c37\u53e3\u73b0\u8eab\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u4e25\u5982\u9752\u5973\u7d20\u5a25\uff0c\u5fa1\u98ce\u4e0b \u964d\uff01\u4e00\u970e\u65f6\u95f4\uff0c\u6570\u4e07\u4eba\u4e0d\u7ea6\u800c\u540c\uff0c\u90fd\u6b62\u4f4f\u4e86\u811a\u6b65\uff0c\u9759\u5f97\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0a\u90fd\u542c\u5f97\u89c1\u54cd\uff0c\u4eba\u4eba \u5fc3\u4e2d\u90fd\u5728\u8d5e\u53f9\uff0c\u5ffd\u5730\u91cc\u201c\u4e07\u5c81\u201d\u4e4b\u58f0\u6709\u5982\u5c71\u5d29\u5730\u88c2\uff01\u51b0\u5ddd\u5929\u5973\u5fae\u7b11\u6325\u624b\uff0c\u773c\u89d2\u91cc\u6709\u6676\u83b9\u7684\u6cea \u73e0\uff0c \u3000\u3000\u4e1c\u65b9\u897f\u65b9\uff0c\u90fd\u6709\u76f8\u4f3c\u7684\u6210\u8bed\uff0c\u8bf4\u662f\u7f8e\u4eba\u4e00\u7b11\uff0c\u8db3\u4ee5\u503e\u56fd\u503e\u57ce\uff1b\u4f46\u51b0\u5ddd\u5929\u5973\u80fd\u4ee4\u4e07\u4f17\u503e \u5fc3\uff0c\u5374\u5e76\u975e\u5f92\u6043\u7f8e\u8272\u3002\u5c3c\u6cca\u5c14\u4eba\u4eba\u77e5\u9053\uff0c\u51b0\u5ddd\u5929\u5973\u4e43\u662f\u534e\u7389\u516c\u4e3b\u7684\u5973\u513f\uff0c\u5f53\u5e74\u82e5\u975e\u534e\u7389\u516c\u4e3b \u5f03\u56fd\u8fdc\u8d70\uff0c\u6309\u7167\u738b\u4f4d\u7684\u7ee7\u627f\u6cd5\uff0c\u73b0\u4efb\u7684\u56fd\u738b\u5c31\u5e94\u662f\u51b0\u5ddd\u5929\u5973\u800c\u975e\u8fd9\u4e2a\u66b4\u541b\u3002\u8fd9\u5c71\u547c\u201c\u4e07\u5c81\u201d \u4e4b\u58f0\uff0c\u5176\u5b9e\u662f\u4ee3\u8868\u4e86\u4e00\u4e2a\u613f\u671b\uff0c\u4eba\u4eba\u90fd\u613f\u5f97\u8fd9\u6837\u4e00\u4f4d\u53ef\u7231\u7684\u5973\u738b\u5f53\u56fd\uff01\u8fd9\u613f\u671b\u6f5c\u4f0f\u5728\u6bcf\u4e2a\u4eba \u7684\u5fc3\u5e95\uff0c\u8fd9\u65f6\u89c1\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u7edd\u4e16\u5bb9\u989c\uff0c\u66f4\u662f\u4eba\u4eba\u96be\u4ee5\u6291\u5236\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u7206\u53d1\u51fa\u6765\uff01 \u3000\u3000\u5ffd\u89c1\u738b\u65d7\u62db\u5c55\uff0c\u4e2d\u592e\u5927\u8425\u9ec4\u8272\u7684\u5e10\u5e55\u6253\u5f00\uff0c\u5c3c\u6cca\u5c14\u738b\u9a91\u7740\u767d\u8c61\uff0c\u5728\u738b\u516c\u5927\u81e3\u7684\u65cf\u62e5\u4e4b\u4e0b \u8d70\u51fa\u5e10\u5e55\uff0c\u970e\u65f6\u95f4\u53c8\u662f\u8bf8\u58f0\u4ff1\u5bc2\uff0c\u5510\u7ecf\u5929\u966a\u5728\u51b0\u5ddd\u5929\u5973\u7684\u8eab\u8fb9\uff0c\u51b7\u773c\u671b\u53bb\uff0c\u4f46\u89c1\u5c3c\u6cca\u5c14\u738b\u9762 \u8272\u7070\u8d25\uff0c\u5728\u767d\u8c61\u4e0a\u6447\u6447\u6b32\u5760\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u7adf\u662f\u60e7\u6015\u591a\u4e8e\u559c\u60a6\uff0c\u5c3c\u6cca\u5c14\u738b\u7ed9\u8fd9\u7a81\u5982\u5176\u6765\u7684\u201c\u4e07 \u5c81\u201d\u4e4b\u58f0\u5413\u7740\u4e86\u3002 \u3000\u3000\u8fd9\u786e\u662f\u5927\u51fa\u5c3c\u6cca\u5c14\u738b\u610f\u6599\u4e4b\u5916\u7684\u4e8b\uff0c\u4ed6\u65e5\u601d\u591c\u60f3\uff0c\u53ea\u662f\u60f3\u5f97\u8fd9\u4f4d\u7f8e\u82e5\u5929\u4ed9\u7684\u8868\u59b9\u4e3a\u59bb\uff0c \u5982\u4eca\u4e00\u542c\u8fd9\u201c\u4e07\u5c81\u201d\u4e4b\u58f0\uff0c\u5b9b\u5982\u53d7\u4e86\u5f53\u5934\u68d2\u559d\uff0c\u9661\u7136\u60f3\u8d77\u4e86\u51b0\u5ddd\u5929\u5973\u4e5f\u662f\u738b\u4f4d\u7684\u7ee7\u627f\u4eba\uff0c\u5fc3 \u4e2d\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u4e45\u5df2\u671f\u671b\u8fd9\u6837\u7684\u4e00\u6b21\u4f1a\u9762\uff0c\u65e9\u5df2\u5ff5\u719f\u4e86\u89c1\u9762\u4e4b\u65f6\u8981\u8bf4\u7684\u503e\u6155\u8a00\u8bcd\uff0c\u5982\u4eca\u7adf\u662f\u4e00 \u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u5012\u662f\u51b0\u5ddd\u5929\u5973\u843d\u843d\u5927\u65b9\uff0c\u542b\u7b11\u548c\u4ed6\u65bd\u793c\u3002\u5c3c\u6cca\u5c14\u738b\u6025\u5fd9\u8df3\u4e0b\u767d\u8c61\uff0c\u8ba9\u5979\u4e58 \u5750\uff0c\u4f46\u89c9\u5979\u5bb9\u5149\u8feb\u4eba\uff0c\u4e0d\u53ef\u4ef0\u89c6\uff1b\u6c14\u5ea6\u9ad8\u534e\uff0c\u4ee4\u4eba\u6151\u670d\u3002\u5634\u89d2\u7684\u5fae\u7b11\u5982\u540c\u5e7d\u8c37\u767e\u5408\uff0c\u6e05\u96c5\u7edd \u4fd7\uff0c\u4ee4\u4eba\u4e0d\u6562\u8d77\u4e1d\u6beb\u4eb5\u8bfb\u4e4b\u5ff5\uff01 \u3000\u3000\u8fdb\u4e86\u8425\u5e10\uff0c\u5c3c\u6cca\u5c14\u738b\u66ff\u5979\u6446\u6d12\u6d17\u5c18\uff0c\u51b0\u5ddd\u5929\u5973\u53eb\u5510\u7ecf\u5929\u5750\u5728\u5979\u7684\u4fa7\u8fb9\uff0c\u5c3c\u6cca\u5c14\u738b\u5927\u4e3a\u4e0d \u60a6\uff0c\u4f46\u90a3\u662f\u51b0\u5ddd\u5929\u5973\u5429\u5490\u7684\uff0c\u8fde\u5c3c\u6cca\u5c14\u738b\u4e5f\u4e0d\u6562\u9053\u534a\u4e2a\u201c\u4e0d\u201d\u5b50\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u5c3c\u6cca\u5c14\u738b\u5fc3\u795e\u7a0d\u5b9a\uff0c\u521a\u521a\u60f3\u5411\u51b0\u5ddd\u5929\u5973\u503e\u5410\u4ef0\u6155\u4e4b\u5ff1\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u5148\u5f00\u53e3\u95ee \u9053\uff1a\u201c\u8bf7\u95ee\u4f60\u5e26\u503e\u56fd\u4e4b\u5175\uff0c\u5230\u672a\u4f55\u4e8b\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u6b63\u662f\u4e3a\u4e86\u8fce\u63a5\u8868\u59b9\u56de\u56fd\u3002\u201d\u51b0\u5ddd\u5929 \u5973\u9762\u8272\u4e00\u7aef\uff0c\u51b7\u51b7\u8bf4\u9053\uff0c\u201c\u6211\u867d\u7136\u5728\u4e2d\u56fd\u51fa\u751f\uff0c\u672a\u66fe\u8e0f\u8fc7\u672c\u56fd\u571f\u5730\uff0c\u4f46\u4e5f\u66fe\u542c\u6bcd\u4eb2\u63d0\u8fc7\u672c\u56fd \u524d\u738b\u7684\u9057\u8bad\uff0c\u8868\u5144\u65e2\u7136\u7ee7\u4f4d\u4e3a\u541b\uff0c\u96be\u9053\u5217\u7956\u5217\u5b97\u7684\u9057\u8bad\u4e5f\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5ea7\u5931 \u8272\uff01\u5c3c\u6cca\u5c14\u738b\u676f\u4e2d\u7684\u7f8e\u9152\u4e5f\u6e85\u4e86\u51fa\u6765\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e0d\u6012\u800c\u5a01\uff0c\u90a3\u4e24\u9053\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u7d27\u7d27\u7684\u76ef\u7740\u5c3c\u6cca\u5c14\u738b\uff0c\u5c3c\u6cca\u5c14\u738b\u53ea\u89c9\u5f97\u51b0 \u5ddd\u5929\u5973\u53c8\u662f\u53ef\u7231\uff0c\u53c8\u662f\u53ef\u6015\uff0c\u52c9\u5f3a\u9547\u6444\u5fc3\u795e\uff0c\u907f\u5f00\u51b0\u5ddd\u5929\u5973\u7684\u76ee\u5149\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u9057 \u8bad\uff1f\u5012\u8981\u8bf7\u6559\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u56fd\u5c0f\u56fd\u5be1\u6c11\uff0c\u6837\u6837\u90fd\u8981\u9760\u4e2d\u534e\u5927\u56fd\u6276\u6301\uff0c\u6240\u4ee5\u81ea\u7acb\u56fd\u4ee5 \u6765\uff0c\u5c31\u4e0e\u4e2d\u56fd\u6c38\u6566\u4e16\u597d\uff0c\u7956\u5b97\u7684\u9057\u8bad\uff0c\u8981\u5949\u4e2d\u56fd\u4e3a\u5929\u671d\uff0c\u4e0d\u53ef\u8f7b\u542f\u8fb9\u8845\uff0c\u4f60\u600e\u4e48\u5e26\u5175\u8d8a \u5883\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6311\u8845\uff0c\u6211\u662f\u4e0d\u613f\u4f60\u6d41\u6d6a\u5f02\u4e61\uff0c\u60f3\u63a5\u4f60\u56de\u56fd\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u201c\u6211\u5728\u897f\u85cf\u4f4f\u5f97\u597d\u597d\u7684\uff0c\u6211\u82e5\u8981\u56de\u6765\u6211\u81ea\u5df1\u4f1a\u8d70\u3002\u518d\u8bf4\u4f60\u8981\u63a5\u6211\uff0c\u4e5f\u4e0d\u5fc5\u53d1\u4e86\u503e\u56fd\u4e4b\u5175 \u5475\uff01\u201d\u5c3c\u6cca\u5c14\u54d1\u53e3\u65e0\u8a00\u3002\u51b0\u5ddd\u5929\u5973\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u53d1\u4e86\u503e\u56fd\u4e4b\u5175\uff0c\u4e5f\u586b\u4e0d\u6ee1\u559c\u9a6c\u62c9\u96c5\u5c71\u7684 \u4e00\u4e2a\u5c71\u8c37\uff0c\u4e2d\u56fd\u4e4b\u5927\uff0c\u5c82\u662f\u4f60\u80fd\u60f3\u50cf\uff01\u201d\u5c3c\u6cca\u5c14\u738b\u8001\u7f9e\u6210\u6012\uff0c\u60f3\u8981\u53d1\u4f5c\uff0c\u53ef\u662f\u5bf9\u7740\u8fd9\u6837\u4e00\u4f4d \u7edd\u4e16\u5bb9\u989c\u53c8\u662f\u516c\u4e3b\u8eab\u4efd\u7684\u51b0\u5ddd\u5929\u5973\uff0c\u4ed6\u53c8\u600e\u6562\u53d1\u4f5c\u51fa\u6765\u3002 \u3000\u3000\u51b0\u5ddd\u5927\u5973\u76ee\u5149\u4e00\u626b\uff0c\u9053\uff1a\u201c\u56fd\u738b\u505a\u4e86\u9519\u4e8b\uff0c\u76d1\u56fd\u91cd\u81e3\u4e5f\u6709\u8d23\u4efb\u5475\uff01\u201d\u90a3\u4e9b\u738b\u516c\u5927\u81e3\u4e2a\u4e2a \u5782\u4e0b\u4e86\u5934\u3002\u51b0\u5ddd\u5929\u5973\u9762\u5bf9\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u867d\u7136\u79bb\u5f00\u6545\u56fd\uff0c\u4f46\u5979\u8fd8\u4fdd\u5b58\u6709\u5148\u738b\u7956\u7ed9\u5979\u7684 \u94c1\u5238\u4e39\u4e66\uff0c\u53ef\u4ee5\u987e\u95ee\u56fd\u4e8b\uff0c\u8fd9\u94c1\u5238\u4e39\u4e66\u5982\u4eca\u5c31\u5728\u6211\u7684\u8eab\u4e0a\u3002\u4e3a\u4e86\u4e2d\u5c3c\u4e24\u56fd\u7684\u4e16\u4ee3\u4ea4\u8c0a\uff0c\u6211\u529d \u4f60\u7acb\u5373\u64a4\u5175\u3002\u4f60\u82e5\u662f\u4e0d\u80af\u4f9d\u4ece\uff0c\u54b1\u4eec\u5c31\u62db\u96c6\u5168\u519b\uff0c\u5404\u81ea\u628a\u4e3b\u5f20\u8bf4\u51fa\u6765\uff0c\u8bc9\u4e4b\u516c\u51b3\u597d\u4e86\u3002\u201d\u5c3c \u6cca\u5c14\u738b\u51b7\u900f\u5fc3\u5934\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u662f\u62db\u96c6\u5168\u519b\uff0c\u8bc9\u4e4b\u516c\u51b3\uff0c\u519b\u961f\u5341\u4e5d\u4f1a\u62e5\u62a4\u5979\uff0c\u8fd9\u5c82\u4e0d\u662f\u8981\u7acb\u5373 \u5f15\u8d77\u9635\u524d\u53db\u4e71\uff01\u201d\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u7eb5\u8bb8\u51b0\u5ddd\u5929\u5973\u518d\u7f8e\u5341\u5206\uff0c\u4ed6\u4e5f\u4e0d\u6562\u62db\u60f9\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u51b0\u5ddd\u5929\u5973\u8fd9\u6837\u65a9\u9489\u622a\u94c1\u7684\u8bf4\u8bdd\uff0c\u5927\u4e3a\u51c9\u5947\uff0c\u5fc3\u4e2d\u53c8\u89c9\u5341\u5206\u75db\u5feb\u3002\u4ed6 \u8fd8\u672a\u80fd\u5b8c\u5168\u9886\u4f1a\uff0c\u90a3\u662f\u51b0\u5ddd\u5929\u5973\u51fa\u4e8e\u7231\u4e2d\u56fd\u4e0e\u7231\u5c3c\u6cca\u5c14\u7684\u6fc0\u60c5\uff0c\u4ee5\u81f3\u4ee4\u4e00\u4e2a\u67d4\u60c5\u4f3c\u6c34\u7684\u59d1 \u5a18\uff0c\u53d8\u53cb\u4e86\u6177\u6168\u6fc0\u6602\u3001\u5927\u4ec1\u5927\u52c7\u7684\u4fa0\u58eb\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u975e\u5e38\u4e0d\u5b89\uff0c\u652f\u543e\u8bf4\u9053\uff1a\u201c\u8981\u64a4\u5175\u4e5f\u5f97\u8fc7\u4e24\u5929\u624d\u884c\uff0c\u5916\u9762\u51b0\u96ea\u5c01\u5c71\uff0c\u4e5f\u5f97\u6d3e\u4eba\u5148 \u626b\u6e05\u9053\u8def\u5475\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9762\u8272\u7a0d\u7a0d\u7f13\uff0c\u9053\uff1a\u201c\u76ee\u4e0b\u6625\u6696\u82b1\u5f00\uff0c\u51b0\u96ea\u5c31\u5c06\u878d\u89e3\uff0c\u90a3\u4e48\u4f60\u5c31\u8d81\u65e9 \u6d3e\u4eba\u6e05\u9053\u5427\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u8f6c\u8fc7\u8bdd\u9898\uff0c\u642d\u8baa\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u516c\u4e3b\u4f4f\u5728\u51b0\u5bab\uff0c\u4eba\u8ff9\u7f55\u5230\uff0c\u4e0d\u5bc2\u5bde \u4e48\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e5f\u4f4f\u60ef\u4e86\u3002\u4f55\u51b5\u6211\u8fd8\u6709\u8bb8\u591a\u5bab\u5973\u966a\u4f34\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u7b11\u9053\uff1a\u201c\u4f60\u5728\u4e2d\u56fd \u957f\u5927\uff0c\u5f53\u77e5\u4e2d\u56fd\u53e4\u8bed\u7537\u5a5a\u5973\u5ac1\uff0c\u4eba\u4e4b\u5927\u4f26\uff0c\u957f\u4f4f\u51b0\u5cf0\uff0c\u600e\u751f\u6311\u9009\u9a78\u9a6c\uff1f\u6240\u4ee5\u6211\u6b64\u6b21\u60f3\u63a5\u4f60\u56de \u53bb\uff0c\u66ff\u4f60\u7b79\u529e\u5927\u5a5a\u3002\u201d\u51b0\u5ddd\u5929\u5973\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u4f60\u591a\u5c11\u6b63\u4e8b\u8981\u7406\u2026\u2026\u201d\u5c3c\u6cca\u5c14\u738b\u622a\u7740\u51b0\u5ddd\u5929\u5973 \u7684\u8bdd\u5934\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u5b8c\u5a5a\u96be\u9053\u4e0d\u662f\u6b63\u4e8b\u4e48\uff1f\u6211\u53ea\u6709\u4f60\u4e00\u4f4d\u8fd1\u4eb2\uff0c\u80fd\u4e0d\u5173\u5fc3\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9762\u8272 \u4e00\u7aef\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u4e5f\u4e0d\u52b3\u738b\u5144\u62c5\u5fc3\uff01\u201d\u5c3c\u6cca\u5c14\u738b\u5fc3\u5934\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4f60\u9009\u4e86\u9a78\u9a6c\u4e48\uff1f\u201d \u51b0\u5ddd\u5929\u5973\u542b\u7b11\u4e0d\u7b54\uff0c\u7f13\u7f13\u62ac\u8d77\u5934\u6765\uff0c\u5ffd\u89c1\u5510\u7ecf\u5929\u542b\u60c5\u8109\u8109\u7684\u6ce8\u89c6\u7740\u5979\uff0c\u51b0\u5ddd\u5929\u5973\u6ee1\u9762\u901a\u7ea2\uff0c \u53c8\u5782\u4e0b\u5934\u6765\u3002\uff0c \u3000\u3000\u77a7\u90a3\u795e\u60c5\uff0c\u8c01\u90fd\u53ef\u4ee5\u731c\u60f3\u5230\u4ed6\u4eec\u662f\u4e00\u5bf9\u7231\u4fa3\u3002\u5c3c\u6cca\u5c14\u738b\u5992\u6068\u4ea4\u5e76\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f \u8c01\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e2d\u56fd\u6700\u6709\u540d\u7684\u5c11\u5e74\u4fa0\u5ba2\uff0c\u6587\u624d\u6b66\u529f\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\u3002\u201d\u5510\u7ecf\u5929 \u9053\uff1a\u201c\u516c\u4e3b\u592a\u5938\u5956\u4e86\uff0c\u4e2d\u56fd\u50cf\u6211\u8fd9\u6837\u7684\u4eba\u8f66\u8f7d\u6597\u91cf\u3002\u201d\u8bdd\u4f3c\u8c26\u865a\uff0c\u5176\u5b9e\u662f\u6b63\u544a\u5c3c\u6cca\u5c14\u738b\uff0c\u4e2d \u56fd\u4e0d\u53ef\u8f7b\u4fae\u3002\u5c3c\u6cca\u5c14\u738b\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4e45\u4e45\u59cb\u9053\uff1a\u201c\u5931\u656c\u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ed6\u8fd8\u6709\u51e0\u4f4d \u670b\u53cb\u5728\u5c71\u811a\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u597d\uff0c\u51e1\u662f\u6c49\u4eba\uff0c\u6211\u90fd\u8bf7\u4ed6\u4eec\u8fdb\u6765\u3002\u201d\u58f0\u97f3\u548c\u9762\u8272\u4e00\u6837\u9634\u6c89\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u5c3c\u6cca\u5c14\u738b\u5f7b\u591c\u65e0\u7720\uff0c\u51b0\u5ddd\u5929\u5973\u5728\u4ed6\u5fc3\u76ee\u4e2d\u5c31\u50cf\u4e00\u6735\u6709\u523a\u7684\u73ab\u7470\uff0c\u660e\u660e\u77e5\u9053\u4e0d\u597d\u6cbe \u60f9\uff0c\u5374\u53c8\u820d\u4e0d\u5f97\u653e\u5f00\uff0c\u4e00\u95ed\u773c\u775b\uff0c\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u4eb2\u6635\u7684\u795e\u60c5\uff0c\u53c8\u5728\u4ed6\u8111\u6d77\u4e2d\u6d6e\u73b0\u3002\u5c3c\u6cca \u5c14\u738b\u6068\u6068\u60f3\u9053\uff1a\u201c\u6211\u5c31\u662f\u64a4\u5175\u4e5f\u5f97\u628a\u8fd9\u5c0f\u5b50\u6740\u6389\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5c3c\u6cca\u5c14\u738b\u53c8\u6d3e\u4eba\u8bf7\u51b0\u5ddd\u5929\u5973\u4e0e\u5510\u7ecf\u5929\u8d74\u5bb4\uff0c\u7b75\u5e2d\u4ecd\u662f\u8bbe\u5728\u4ed6\u7684\u5e10\u5e55\u4e2d\uff0c\u53ea \u662f\u5374\u591a\u4e86\u597d\u51e0\u4e2a\u4eba\uff0c\u539f\u6765\u6768\u67f3\u9752\u6bcd\u5973\u548c\u5510\u8d5b\u82b1\u59d1\u4f84\u4e0e\u53ca\u90a3\u4e2a\u5c0f\u4e66\u7ae5\u6c5f\u5357\uff0c\u90fd\u7ed9\u5c3c\u6cca\u5c14\u738b\u6d3e\u4eba \u515c\u622a\uff0c\u8bf4\u662f\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u7684\u610f\u601d\uff0c\u628a\u4ed6\u4eec\u90fd\u8bf7\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6b22\u559c\u65e0\u9650\uff0c\u8bf7\u5510\u8d5b\u82b1\u5750\u5728\u5979\u7684\u8eab\u8fb9\uff0c\u6084\u6084\u95ee\u9053\uff1a\u201c\u6211\u4eec\u627e\u5f97\u4f60\u597d\u82e6\uff0c\u4f60\u4e0d\u662f\u548c \u91d1\u4e16\u9057\u5728\u4e00\u58f0\u513f\u5417\uff1f\u4ed6\u5230\u90a3\u513f\u53bb\u4e86\u3002\u201d\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u4e00\u5230\u5c71\u811a\uff0c\u4ed6\u5c31\u4e22\u5f00\u6211\u72ec\u81ea\u767b\u5c71\u53bb\u4e86\u3002 \u5440\uff0c\u6211\u82e5\u662f\u5e74\u8f7b\u4e09\u5341\u5e74\uff0c\u6216\u8bb8\u8fd8\u80fd\u8ffd\u8d76\u5f97\u4e0a\u3002\u91d1\u4e16\u9057\u8fd9\u4e2a\u4eba\u771f\u662f\u53e4\u602a\u900f\u4e86\uff0c\u54b3\uff0c\u6211\u6cbe\u4ed6\u7684\u6069 \u60e0\uff0c\u4eca\u751f\u662f\u65e0\u6cd5\u62a5\u7b54\u4e86\u3002\u4f60\u6709\u6ca1\u6709\u89c1\u5230\u7075\u77eb\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6b63\u60f3\u7b54\u8bdd\uff0c\u5e10\u5e55\u5f00\u5904\uff0c\u4e00\u7fa4\u6b66\u58eb\u8d70\u4e86\u8fdb\u6765\uff0c\u597d\u50cf\u5f00\u4e86\u4e00\u4e2a\u4eba\u79cd\u5c55\u89c8\u4f1a\uff0c\u6b27\u6d32 \u4eba\u3001\u963f\u62c9\u4f2f\u4eba\u3001\u5370\u5ea6\u4eba\u3001\u6ce2\u65af\u4eba\u90fd\u6709\u3002\u4ee5\u5c3c\u6cca\u5c14\u4e00\u4e2a\u5c0f\u56fd\uff0c\u5c45\u7136\u8058\u8bf7\u5230\u6b27\u4e9a\u5404\u56fd\u7684\u6b66\u58eb\uff0c\u5c3c \u6cca\u5c14\u738b\u4e5f\u5927\u8db3\u4ee5\u81ea\u8c6a\u4e86\u3002\u90a3\u4e2a\u80e1\u50e7\u6cf0\u5409\u63d0\u4e5f\u5728\u5176\u4e2d\uff0c\u770b\u4e86\u5510\u8d5b\u82b1\u4e00\u773c\uff0c\u82e5\u65e0\u5176\u4e8b\u7684\u5750\u4e0b\uff0c\u5510 \u8d5b\u82b1\u771f\u60f3\u63ea\u7740\u4ed6\u8ffd\u95ee\u9f99\u7075\u77eb\u7684\u4e0b\u843d\uff0c\u53ef\u662f\u5728\u5c3c\u6cca\u5c14\u738b\u7684\u56fd\u5bb4\u4e0a\uff0c\u4efb\u5979\u5982\u4f55\u751f\u6c14\uff0c\u5374\u4e5f\u53ea\u597d\u5fcd \u7740\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5c3c\u6cca\u5c14\u738b\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u4e2d\u534e\u4e0a\u56fd\u4eba\u624d\u4f17\u591a\uff0c\u6628\u65e5\u542c\u516c\u4e3b\u79f0\u8d5e\u5f97\u8fd9\u4f4d\u5510\u5927\u4fa0\u5929\u4e0a\u6709 \u5730\u4e0b\u65e0\uff0c\u66f4\u662f\u4ee4\u672c\u738b\u94a6\u4ef0\uff0c\u96be\u5f97\u6709\u4eca\u65e5\u7684\u76db\u4f1a\uff0c\u5404\u56fd\u6b66\u58eb\u805a\u4f1a\u4e00\u5802\uff0c\u8fd8\u8bf7\u5510\u5927\u4fa0\u4e0d\u541d\u6307\u6559\uff0c \u597d\u8ba9\u6211\u4eec\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fae\u7b11\u9053\uff1a\u201c\u5207\u78cb\u6b66\u529f\uff0c\u4e0d\u5206\u56fd\u57df\uff0c\u738b\u5144\u6b64\u8a00\uff0c\u597d\u50cf\u628a\u5728\u5ea7\u8bf8\u4eba\u5212\u5206\u8fb9\u4e86\u3002\u201d\u5c3c \u6cca\u5c14\u738b\u9053\uff1a\u201c\u516c\u4e3b\u8a00\u91cd\u4e86\uff0c\u5c0f\u738b\u5e76\u65e0\u4ed6\u610f\uff0c\u53ea\u56e0\u5510\u5927\u4fa0\u662f\u521d\u6b21\u89c1\u9762\u7684\u8d35\u5ba2\uff0c\u53c8\u662f\u516c\u4e3b\u8d4f\u8bc6\u7684 \u4eba\uff0c\u624d\u60f3\u5148\u89c1\u8bc6\u4ed6\u7684\u672c\u9886\u3002\u597d\uff0c\u6211\u5148\u656c\u5510\u5927\u4fa0\u4e00\u676f\uff01\u201d\u51b0\u5ddd\u5929\u5973\u89c1\u4ed6\u76ee\u5149\u6709\u5f02\uff0c\u5fc3\u4e2d\u4e00\u8c06\uff0c \u6b63\u60f3\u8bf4\u8bdd\uff0c\u5510\u7ecf\u5927\u5df2\u5766\u7136\u7684\u5c06\u90a3\u676f\u9152\u63a5\u8fc7\u53bb\u559d\u4e86\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u8c01\u4eba\u613f\u548c\u5510\u5927\u4fa0\u5408\u6f14\u6b66\u529f\uff1f\u201d\u90a3\u80e1\u50e7\u6cf0\u5409\u63d0\u5e94\u58f0\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u6211 \u5df2\u89c1\u8bc6\u8fc7\u5510\u5927\u4fa0\u7684\u9ad8\u62db\uff0c\u53ef\u60dc\u672a\u80fd\u5c3d\u5174\uff0c\u4eca\u65e5\u8fd8\u8981\u7eed\u8bf7\u6307\u6559\u3002\u201d\u4ed6\u65e9\u5df2\u6362\u4e86\u88c5\u675f\uff0c\u5de6\u624b\u63d0\u7740 \u5927\u7ea2\u8888\u88df\uff0c\u53f3\u624b\u62ff\u4e00\u4e2a\u5927\u94c1\u9524\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u56fd\u5e08\u8d50\u6559\uff0c\u4f55\u5e78\u5982\u4e4b\uff01\u201d\u62d4\u5251\u4e0b\u5750\uff0c\u5c3c\u6cca\u5c14\u738b\u547d\u64a4\u5f00\u5e10\u5e55\uff0c\u817e\u51fa\u4e00\u5927\u7247\u7a7a \u5730\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u626c\u8d77\u8888\u88df\uff0c\u5b9b\u5982\u4e00\u7247\u7ea2\u4e91\uff0c\u5f53\u5934\u7f69\u4e0b\uff0c\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8888\u88df\u7ec7\u8865\u5f97\u597d\u5feb \u5475\uff01\u201d\u4e3e\u5251\u4e00\u523a\uff0c\u4f46\u542c\u5f97\u54e8\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u6cf0\u5409\u63d0\u53f3\u624b\u7684\u5927\u94c1\u9524\u731b\u5730\u649e\u53bb\uff0c\u5510\u7ecf\u5929\u8e09\u8e09\u8dc4\u8dc4\u7684 \u5012\u9000\u51e0\u6b65\u3002\u5c3c\u6cca\u5c14\u738b\u4fa7\u76ee\u7b11\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u6562\u60c5\u4f60\u771f\u662f\u8a00\u8fc7\u5176\u5b9e\uff0c\u5bf9\u8fd9\u4f4d\u5510\u5927\u4fa0\u5938\u5956\u5f97\u592a\u751a \u4e86\uff01\u201d\u51b0\u5ddd\u5929\u5973\u5927\u662f\u8d77\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u80e1\u50e7\u6c14\u529b\u867d\u5927\uff0c\u4f46\u4ee5\u5510\u7ecf\u5929\u6240\u4fee\u4e60\u7684\u5929\u5c71\u6b63\u5b97\u5185 \u529f\uff0c\u5c82\u6709\u6321\u4e0d\u4f4f\u4ed6\u4e00\u51fb\u4e4b\u7406\uff1f\u8fd9\u4e2d\u95f4\u4e00\u5b9a\u662f\u6709\u4ec0\u4e48\u53e4\u602a\u3002\u201d \u3000\u3000\u6cf0\u5409\u63d0\u4e00\u51fb\u5f97\u624b\uff0c\u731b\u5982\u6012\u72ee\uff0c\u8888\u88df\u4e00\u5c55\uff0c\u5927\u94c1\u9524\u53c8\u662f\u547c\u7684\u4e00\u58f0\u6253\u4e0b\u3002\u4ed6\u8fd9\u6253\u6cd5\u4f3c\u662f\u6b3a\u8d1f \u5510\u7ecf\u5929\u6ca1\u6709\u6c14\u529b\u4f3c\u7684\uff0c\u786c\u6253\u786c\u649e\uff0c\u5de6\u80c1\u9732\u51fa\u7a7a\u95e8\uff0c\u4ed6\u4ea6\u4f3c\u6beb\u65e0\u987e\u5fcc\u3002\u5ffd\u89c1\u5510\u7ecf\u5929\u4e00\u4e2a\u201c\u56de\u98ce \u6298\u67f3\u201d\uff0c\u8eab\u5f62\u75be\u95ea\uff0c\u5251\u5149\u75be\u8d77\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u7ad9\u7a33\u4e86\uff01\u201d\u5237\u7684\u4e00\u5251\uff0c\u6cf0\u5409\u63d0\u817e\u8eab\u8df3\u8d77\uff0c\u8888\u88df \u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002\u5510\u7ecf\u5929\u8fde\u903c\u4e24\u5251\uff0c\u6cf0\u5409\u63d0\u6536\u52bf\u4e0d\u53ca\uff0c\u4e00\u9524\u6253\u4e0b\uff0c\u628a\u575a\u786c\u7684\u77f3\u5730\u6253\u4e86\u4e00\u4e2a\u51f9 \u69fd\uff0c\u51e0\u4e4e\u6251\u5012\u3002\u5510\u7ecf\u5929\u4e00\u7b11\u6536\u5251\uff0c\u9053\uff1a\u201c\u518d\u6765\uff0c\u518d\u6765\uff01\u6211\u4eec\u4e2d\u56fd\u7684\u53e4\u8bad\uff0c\u4e0d\u6253\u843d\u6c34\u4e4b\u72d7\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8212\u4e86\u53e3\u6c14\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u738b\u5144\u8bf7\u770b\u3002\u5510\u5927\u4fa0\u82e5\u662f\u4e58\u52bf\u8fdb\u62db\uff0c\u518d\u8865\u4e00\u5251\uff0c\u4f60\u7684\u7b2c \u4e00\u56fd\u5e08\u53ea\u6050\u9a6c\u4e0a\u5c31\u8981\u8840\u6e85\u9ec4\u6c99\uff01\u201d\u5c3c\u6cca\u5c14\u738b\u5927\u60ca\u5931\u8272\uff0c\u8fd9\u56de\u662f\u8f6e\u5230\u4ed6\u6697\u6697\u5947\u602a\u4e86\u3002 \u3000\u3000\u539f\u6765\u5c3c\u6cca\u5c14\u738b\u84c4\u610f\u8981\u628a\u5510\u7ecf\u5929\u7f6e\u4e8e\u6b7b\u5730\uff0c\u5728\u58f6\u4e2d\u6697\u85cf\u6bd2\u9152\uff0c\u90a3\u9152\u58f6\u5206\u6210\u4e24\u683c\uff0c\u5185\u6709\u673a \u5173\uff0c\u659f\u7ed9\u5510\u7ecf\u5929\u5403\u7684\u90a3\u676f\uff0c\u662f\u7528\u559c\u9a6c\u62c9\u8222\u7279\u4ea7\u7684\u4e00\u79cd\u53eb\u505a\u201c\u767e\u65e5\u9189\uff0c\uff0c\u7684\u6bd2\u8349\u6240\u6ce1\u5236\u7684\uff1b\u800c \u659f\u7ed9\u81ea\u5df1\u548c\u6cf0\u5409\u63d0\u5403\u7684\u5374\u662f\u5e73\u5e38\u7684\u8461\u8404\u9152\u3002\u201c\u767e\u65e5\u9189\u201d\u987e\u540d\u601d\u4e49\uff0c\u4e43\u662f\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u9ebb\u9189 \u836f\u3002\u54ea\u77e5\u5510\u7ecf\u5929\u80c6\u5927\u5fc3\u7ec6\uff0c\u65e9\u5df2\u770b\u51fa\u6cca\u5c14\u738b\u795e\u8272\u4e0d\u5bf9\uff0c\u6697\u4e2d\u670d\u4e0b\u4e86\u4e00\u9897\u7528\u5929\u5c71\u96ea\u83b2\u5236\u70bc\u7684 \u201c\u78a7\u7075\u4e39\u201d\uff0c\u5929\u5c71\u96ea\u83b2\u80fd\u89e3\u767e\u6bd2\uff0c\u5373\u7b97\u662f\u6700\u5389\u5bb3\u7684\u201c\u5b54\u96c0\u80c6\u201d\u548c\u201c\u9e64\u9876\u7ea2\u201d\u5c1a\u4e14\u4e0d\u6015\uff0c\u201c\u767e \u65e5\u9189\u201d\u4f55\u8db3\u9053\u54c9\uff1f \u3000\u3000\u6cf0\u5409\u63d0\u6ee1\u5fc3\u4ee5\u4e3a\u5510\u7ecf\u5929\u5403\u4e86\u6bd2\u9152\u4e4b\u540e\uff0c\u7b4b\u9165\u9aa8\u8f6f\uff0c\u771f\u529b\u5fc5\u7136\u53d1\u4f5c\u4e0d\u51fa\u6765\uff0c\u6240\u4ee5\u624d\u5927\u80c6\u62a2 \u653b\uff0c\u6beb\u65e0\u987e\u5fcc\u3002\u54ea\u77e5\u5510\u7ecf\u5929\u5c06\u8ba1\u5c31\u8ba1\uff0c\u5047\u610f\u88c5\u4f5c\u4e0d\u80dc\u9152\u529b\uff0c\u8ba9\u4e86\u4e00\u62db\uff0c\u8fd9\u624d\u5b9e\u65bd\u53cd\u51fb\u3002\u8981\u4e0d \u7136\u5510\u7ecf\u5929\u548c\u90a3\u80e1\u50e7\u7684\u672c\u4e8b\uff0c\u5b9e\u5728\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5510\u7ecf\u5929\u4e5f\u65ad\u4e0d\u80fd\u4e00\u5251\u5c06\u4ed6\u6740\u8d25\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u4e00\u632b\u590d\u4e0a\uff0c\u8fd9\u56de\u4ed6\u53ef\u4e0d\u6562\u518d\u8f7b\u654c\u4e86\uff0c\u4e24\u4eba\u5404\u5c55\u51fa\u5e73\u751f\u7edd\u5b66\uff0c\u6253\u5f97\u7802\u98de\u77f3\u8d70\uff0c\u5730\u60e8 \u5929\u6101\uff0c\u4e0d\u8fc7\u534a\u65e5\u65f6\u8fb0\uff0c\u5c31\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5140\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u4f46\u89c1\u90a3\u80e1\u50e7\u7684\u8888\u88df\u6709\u5982\u4e00\u7247\u7ea2\u4e91\uff0c \u800c\u5510\u7ecf\u5929\u7684\u5251\u5149\uff0c\u5219\u5982\u94f6\u8679\u73af\u7ed5\uff0c\u4e2d\u95f4\u4e0d\u65f6\u6742\u4ee5\u201c\u54e8\u54e8\u201d\u7684\u94c1\u9524\u4e0e\u5b9d\u5251\u4ea4\u51fb\u7684\u91d1\u94c1\u4e4b\u58f0\uff0c\u52a8 \u4eba\u5fc3\u9b44\u3002\u8fd9\u4e00\u6218\u628a\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\u90fd\u770b\u5f97\u76ee\u5b9a\u795e\u5446\uff0c\u8fde\u5c3c\u6cca\u5c14\u738b\u4ea6\u662f\u60ca\u5fc3\u5931\u8272\uff01 \u3000\u3000\u90a3\u80e1\u50e7\u6628\u65e5\u4e0e\u5510\u7ecf\u5929\u7b2c\u4e00\u6b21\u4ea4\u624b\u4e4b\u540e\uff0c\u77e5\u9053\u4ed6\u7684\u6e38\u9f99\u5b9d\u5251\u950b\u5229\u5f02\u5e38\uff0c\u53ea\u51ed\u7740\u4e00\u4ef6\u8888\u88df\uff0c \u5b9e\u5728\u96be\u4ee5\u62b5\u654c\uff0c\u56e0\u6b64\u53c8\u591a\u7528\u4e86\u4e00\u67c4\u91cd\u8fbe\u4e03\u516b\u5341\u65a4\u7684\u5927\u94c1\u9524\u4f5c\u4e3a\u8f85\u52a9\u5175\u5668\u3002\u5b9d\u5251\u867d\u5229\uff0c\u603b\u4e0d\u80fd \u524a\u65ad\u94c1\u9524\uff0c\u6cf0\u5409\u63d0\u7684\u5185\u529b\u53c8\u6bd4\u5510\u7ecf\u5929\u5927\u5f97\u591a\uff0c\u56e0\u6b64\u5510\u7ecf\u5929\u867d\u7136\u5c55\u5f00\u4e86\u7edd\u5999\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u4e5f\u4e0d \u8fc7\u582a\u582a\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u6fc0\u6597\u6b63\u9163\uff0c\u731b\u7684\u91cc\u72c2\u98ce\u9aa4\u8d77\uff0c\u559c\u9a6c\u62c9\u96c5\u5c71\u533a\u98ce\u529b\u4e4b\u731b\uff0c\u4e3e\u4e16\u65e0\u5339\uff0c\u5c24\u5176\u662f\u5728\u5317\u5c71\u5cf0\u62d7\u7684 \u4e00\u4e2a\u201c\u53f0\u9636\u201d\uff0c\u66f4\u6709\u4e16\u754c\u201c\u98ce\u7a9d\u201d\u4e4b\u79f0\uff0c\u636e\u8fd1\u4e16\u82f1\u4eba\u63a2\u9669\u5bb6\u6240\u6d4b\uff0c\u7ecf\u5e38\u8fbe\u5230\u5341\u7ea7\u53f0\u98ce\u4ee5\u4e0a\uff0c \u767b\u5c71\u8005\u82e5\u4e0d\u662f\u7528\u7ef3\u7d22\u76f8\u8fde\uff0c\u5f80\u5f80\u8fde\u4eba\u4e5f\u88ab\u5439\u8d70\u3002\u5c3c\u6cca\u5c14\u519b\u961f\u9a7b\u5c6f\u5c71\u8c37\u4e4b\u4e2d\uff0c\u4e00\u4e3a\u907f\u5bd2\uff0c\u4e8c\u6765 \u4e5f\u662f\u4e3a\u4e86\u907f\u98ce\uff0c\u867d\u7136\u5982\u6b64\uff0c\u5927\u98ce\u522e\u8fc7\u5c71\u8c37\uff0c\u58f0\u52bf\u4ea6\u8db3\u9a87\u4eba\u3002\u90a3\u80e1\u50e7\u7684\u5927\u7ea2\u8888\u88df\u5f97\u98ce\u529b\u4e4b\u52a9\uff0c \u6296\u5f00\u6765\u6709\u5982\u5927\u9e4f\u5c55\u7fc5\uff0c\u6bcf\u4e00\u6251\u529b\u903e\u5e72\u65a4\uff0c\u628a\u5510\u7ecf\u5929\u6574\u4e2a\u8eab\u5f62\u90fd\u7b3c\u7f69\u5728\u4ed6\u7684\u8888\u88df\u4e4b\u4e0b\u3002\u5510\u7ecf\u5929 \u60f3\u7528\u5b9d\u5251\u518d\u523a\u7a7f\u4ed6\u7684\u8888\u88df\uff0c\u51fa\u624b\u867d\u5feb\uff0c\u5374\u603b\u662f\u88ab\u4ed6\u7684\u5927\u94c1\u9524\u6321\u4f4f\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u4e00\u5360\u4e0a\u98ce\uff0c\u5c3c\u6cca\u5c14\u4e3b\u53c8\u662f\u6d0b\u6d0b\u5f97\u610f\uff0c\u56de\u987e\u51b0\u5ddd\u5929\u5973\uff0c\u5510\u8d5b\u82b1\u5750\u5728\u51b0\u5ddd\u5929\u5973\u53f3\u4fa7\uff0c \u8511\u5634\u8bf4\u9053\uff1a\u201c\u5f97\u98ce\u529b\u4e4b\u52a9\uff0c\u867d\u80dc\u4e0d\u6b66\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5927\u4e3a\u626b\u5174\uff0c\u51b0\u5ddd\u5929\u5973\u542c\u5f97\u7ecf\u9a8c\u6700\u4e30\u5bcc\u7684\u6b66 \u6797\u524d\u8f88\u5510\u8001\u592a\u5a46\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u5374\u7981\u4e0d\u4f4f\u4e3a\u5510\u7ecf\u5927\u62c5\u5fc3\u3002 \u3000\u3000\u5c71\u98ce\u8d8a\u522e\u8d8a\u731b\uff0c\u4e0d\u4f46\u5510\u8d5b\u82b1\u4ee5\u4e3a\u5510\u7ecf\u5929\u53ef\u80fd\u843d\u8d25\uff0c\u90a3\u80e1\u618e\u4e5f\u4ee5\u4e3a\u80dc\u5238\u53ef\u64cd\uff0c\u987a\u7740\u98ce\u52bf\uff0c \u8888\u88df\u821e\u5f97\u547c\u547c\u4f5c\u54cd\u3002\u5510\u7ecf\u5929\u7ed9\u4ed6\u903c\u5f97\u4e00\u8fde\u9000\u540e\u51e0\u6b65\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u53cc\u624b\u90fd\u6709\u5175\u5668\uff0c\u6211\u5374\u53ea \u6709\u4e00\u628a\u5251\uff0c\u8fd9\u4e0d\u516c\u5e73\u3002\u201d\u6cf0\u5409\u63d0\u51b7\u7b11\u9053\uff1a\u201c\u53ef\u6ca1\u6709\u8c01\u7981\u6b62\u4f60\u7528\u4e24\u79cd\u5175\u5668\u5440\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a \u201c\u90a3\u4e48\uff0c\u6211\u53ef\u8981\u5f97\u7f6a\u4e86\u3002\u201d\u731b\u7136\u95ee\u53ea\u89c1\u4ed6\u628a\u624b\u4e00\u626c\uff0c\u51e0\u9053\u6697\u8d64\u8272\u7684\u5149\u7275\u5728\u4ed6\u6307\u95f4\u53d1\u51fa\uff0c\u90a3\u4ef6 \u5927\u7ea2\u8888\u88df\u767b\u65f6\u50cf\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\uff0c\u7a7f\u4e86\u51e0\u5341\u4e2a\u5c0f\u5b54\u3002\u6cf0\u5409\u63d0\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u731b\u5730\u60f3\u8d77\u8fd9\u662f\u5929 \u4e0b\u6700\u5389\u5bb3\u7684\u6697\u5668\u5929\u5c71\u795e\u8292\uff0c\u7eb5\u6709\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u7684\u529f\u592b\u4e5f\u4e0d\u80fd\u62b5\u654c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5510\u7ecf\u5929 \u4e94\u6307\u75be\u5f39\uff0c\u5927\u5c0f\u58f0\uff1a\u201c\u6492\u624b\uff01\u201d\u4ed6\u6307\u95f4\u5939\u7740\u51e0\u652f\u5929\u5c71\u795e\u8292\uff0c\u4e00\u6325\u624b\u95f4\u5df2\u628a\u8888\u88df\u523a\u4e86\u65e0\u6570\u5c0f \u5b54\uff0c\u624b\u6cd5\u4e4b\u5feb\uff0c\u5b9e\u5728\u96be\u4ee5\u5f62\u5bb9\uff0c\u795e\u8292\u900f\u8fc7\u8888\u88df\uff0c\u76f4\u524a\u80e1\u50e7\u624b\u8155\u3002\u80e1\u50e7\u5927\u53eb\u4e00\u58f0\uff0c\u4e0d\u7531\u5f97\u4ed6\u4e0d \u6025\u5fd9\u6492\u624b\uff0c\u53ea\u89c1\u90a3\u4ef6\u8888\u88df\u88ab\u5927\u98ce\u4e00\u522e\uff0c\u767b\u65f6\u98de\u51fa\u4e86\u5c71\u8c37\uff0c\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u5782\u5934\u4e27\u6c14\uff0c\u9000\u5165\u519b\u8425\uff0c\u7adf\u4e0d\u6562\u8ddf\u5510\u7ecf\u5929\u56de\u5230\u5e2d\u95f4\u3002\u5510\u7ecf\u5929\u5bf9\u5c3c\u6cca\u5c14\u738b\u7b11\u9053\uff1a\u201c\u8d35 \u56fd\u7684\u7b2c\u4e00\u56fd\u5e08\uff0c\u6b66\u827a\u4e5f\u786e\u7b97\u5f97\u662f\u4e0d\u9519\u7684\u4e86\u3002\u201d\u4f3c\u8d5e\u4f3c\u8bbd\uff0c\u5c3c\u6cca\u5c14\u738b\u542c\u5f97\u523a\u8033\u94bb\u5fc3\uff0c\u4f46\u4ed6\u6240\u7b49 \u5019\u7684\u7b2c\u4e00\u9ad8\u624b\u8fd8\u672a\u6765\uff0c\u53ea\u5f97\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u56fd\u7ec3\u5175\u6ce8\u91cd\u5f13\u9a6c\uff0c\u6bcf\u4e2a\u58eb\u5175\u90fd\u80fd\u9a70\u9a6c\u5c04\u7bad\uff0c\u767e\u53d1 \u767e\u4e2d\u7684\u4e5f\u5f88\u666e\u901a\uff0c\u5e76\u975e\u53ea\u6ce8\u65f1\u4e00\u4e24\u4e2a\u51fa\u7c7b\u62d4\u9769\u7684\u6b66\u58eb\u3002\u201d\u5510\u8d5b\u82b1\u5ffd\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u8bf7 \u56fd\u738b\u53eb\u51e0\u4f4d\u8d35\u56fd\u7684\u795e\u7bad\u624b\u51fa\u6765\uff0c\u8ba9\u6211\u8fd9\u4e2a\u8001\u592a\u5a46\u4e5f\u89c1\u8bc6\u89c1\u8bc6\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u96d5\u866b\u5c0f\u6280\u771f\u582a\u7b11\uff0c\u8bf7\u770b\u4e2d\u539f\u7b2c\u4e00\u5bb6\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516d\u56de\u3000\u795e\u529f\u65e0\u654c\u3000\u8f83\u6280\u670d\u4e09\u519b\u3000\u6ed1\u96ea\u5947\u80fd\u3000\u632f\u8863\u51cc\u7edd\u9876 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u516d\u56de \u795e\u529f\u65e0\u654c\u3000\u8f83\u6280\u670d\u4e09\u519b \u6ed1\u96ea\u5947\u80fd\u3000\u632f\u8863\u51cc\u7edd\u9876 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u9762\u8272\u4e00\u6c89\uff0c\u628a\u624b\u4e00\u6325\uff0c\u4f20\u4e0b\u4ee4\u53bb\uff0c\u767b\u65f6\u5728\u519b\u4e2d\u6311\u9009\u51fa\u56db\u4e2a\u4eba\u6765\uff0c\u6bcf\u4e2a\u4eba\u90fd\u62b1\u7740 \u4e00\u5f20\u5927\u94c1\u5f13\uff0c\u770b\u90a3\u5927\u5f13\u4e24\u81c2\u975e\u6709\u4e94\u4e03\u767e\u65a4\u6c14\u529b\uff0c\u68da\u62c9\u5f97\u5b83\u52a8\uff0c\u8fd9\u56db\u4e2a\u4eba\u90fd\u662f\u519b\u4e2d\u7684\u5f13\u7bad\u6559 \u5934\uff0c\u5c3c\u6cca\u5c14\u738b\u5374\u6545\u610f\u9690\u7792\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u6307\u7740\u4ed6\u4eec\u5bf9\u5510\u7ecf\u5929\u8bf4\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u58eb\u5175\u90fd\u662f\u519b\u4e2d\u7684\u795e \u7bad\u624b\uff0c\u767e\u53d1\u767e\u4e2d\uff0c\u5510\u5927\u4fa0\u53ef\u80af\u548c\u5c0f\u5175\u6bd4\u6bd4\u5f13\u7bad\u5417\uff1f\u201d\u5728\u5c3c\u6cca\u5c14\u738b\u7684\u7528\u610f\uff0c\u4ee5\u5510\u7ecf\u5929\u7684\u8eab\u4efd\uff0c \u80dc\u4e86\u51e0\u4e2a\u5c0f\u5175\u4e0d\u8db3\u4e3a\u8363\uff0c\u4f46\u82e5\u8f93\u4e86\uff0c\u90a3\u81ea\u662f\u5927\u5931\u9762\u5b50\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u5c1a\u672a\u5f00\u8a00\uff0c\u5510\u8d5b\u82b1\u5df2\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u6bd4\u5f13\u7bad\u8fd9\u6837\u7684\u5c0f\u73a9\u610f\u4f55\u52b3\u5510\u5927\u4fa0\u51fa \u624b\uff1f\u4e2d\u56fd\u7684\u5987\u5b7a\u90fd\u80fd\u633d\u5f13\u5c04\u7bad\uff0c\u4f55\u8db3\u4e3a\u5947\u3002\u8fd9\u91cc\u5730\u6e7f\u98ce\u5bd2\uff0c\u8001\u8eab\u6b63\u60f3\u8212\u5c55\u8212\u5c55\u7b4b\u9aa8\uff0c\u8fd9\u4e00\u573a \u5f85\u6211\u6765\u5427\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c31\u98a4\u5dcd\u5dcd\u5730\u7ad9\u4e86\u60f3\u6765\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5927\u4e3a\u607c\u6012\uff0c\u91cd\u91cd\u7684\u5c06\u9152\u676f\u4e00\u987f\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u56fd\u867d\u7136\u56fd\u5c0f\u5175\u5fae\uff0c\u968f\u6211\u51fa\u5f81\u7684 \u90fd\u662f\u80fd\u5f81\u60ef\u6218\u4e4b\u58eb\u3002\u8d73\u8d73\u6b66\u592b\uff0c\u5c82\u80fd\u6b3a\u8d1f\u4e00\u4e2a\u8001\u5987\uff1f\u201d\u5510\u8d5b\u82b1\u4e5f\u628a\u9152\u676f\u91cd\u91cd\u7684\u4e00\u987f\uff0c\u7528\u66f4\u51b7 \u5ced\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8001\u8eab\u867d\u7136\u5e74\u8fc7\u516d\u65ec\uff0c\u53eb\u6211\u7a7f\u9488\u5f15\u7ebf\uff0c\u6211\u53ef\u80fd\u8001\u773c\u660f\u82b1\uff1b\u5f20\u5f13\u5c04\u7bad\uff0c\u563f\uff0c \u563f\uff0c\u90a3\u53ef\u662f\u6700\u5e73\u5e38\u4e0d\u8fc7\u4e4b\u4e8b\u3002\u82e5\u975e\u56fd\u738b\u8bf4\u4ed6\u4eec\u662f\u795e\u7bad\u624b\uff0c\u6211\u8fd8\u4e0d\u5c51\u6b3a\u8d1f\u540e\u751f\u5c0f\u5b50\u5462\uff01\uff0c\uff0c\u8fd9 \u51e0\u53e5\u8bf4\u8bdd\u9488\u950b\u76f8\u5bf9\uff0c\u628a\u5c3c\u6cca\u5c14\u738b\u8bf4\u7684\u4e0b\u4e0d\u4e86\u53f0\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u8ba8\u538c\u7684\u8001\u4e5e\u5a46\uff0c\u8fd9\u53ef\u662f \u4f60\u81ea\u5df1\u627e\u6b7b\uff01\u201d\u4fbf\u9053\uff1a\u201c\u597d\u5427\uff0c\u8fd9\u51e0\u4e2a\u58eb\u5175\u7528\u7684\u662f\u7b2c\u4e00\u53f7\u5f3a\u5f13\uff0c\u4f60\u8981\u7528\u7b2c\u51e0\u53f7\uff1f\u201d\u8fd9\u79cd\u7b2c\u4e00 \u53f7\u7684\u94c1\u80ce\u5f13\uff0c\u91cd\u8fbe\u767e\u65a4\u3002\u5c3c\u6cca\u5c14\u738b\u770b\u5510\u8d5b\u82b1\u8001\u6001\u9f99\u949f\u7684\u6a21\u6837\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u76f8\u4fe1\u4f60\u80fd\u4f7f\u7528 \u94c1\u80ce\u5f13\uff0c\u53ea\u6015\u4f60\u62ff\u4e5f\u62ff\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u5510\u8d5b\u82b1\u6545\u610f\u4e0d\u7b54\uff0c\u9053\uff1a\u201c\u4f60\u5f85\u6211\u518d\u559d\u4e00\u676f\u9152\u63d0\u63d0\u795e\u3002\u201d\u8fd9\u65f6\u95f4\u5c3c\u6cca\u5c14\u7684\u5175\u5352\u5df2\u628a\u5404\u53f7\u5f13 \u7bad\u6367\u51fa\u6765\uff0c\u7b2c\u4e00\u53f7\u7b2c\u4e8c\u53f7\u7684\u94c1\u80ce\u5f13\u7528\u4e24\u4e2a\u4eba\u62ac\uff0c\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u6700\u5c0f\u7684\u90a3\u4e00\u53f7\u94c1\u5f13\u5200\u4e5f\u6709\u4e8c \u5341\u6765\u65a4\uff0c\u8001\u592a\u592a\u4f60\u5c0f\u5fc3\u70b9\u513f\uff0c\u522b\u95ea\u4e86\u624b\u3002\u201d \u3000\u3000\u5510\u8d5b\u82b1\u4e00\u58f0\u957f\u7b11\uff0c\u9053\uff1a\u201c\u8001\u8eab\u4e0d\u7528\u5f13\u7bad\uff01\u201d\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e0d\u7528\u5f13\u7bad\uff0c\u5982\u4f55\u6bd4 \u6cd5\uff1f\u201d\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u5584\u5c04\u8005\u4f55\u987b\u81ea\u5df1\u6467\u5f13\u5e26\u7bad\uff0c\u563f\uff0c\u563f\uff0c\u4fbf\u4ee5\u5176\u4eba\u5c04\u6765\u4e4b\u7bad\u53cd\u5176\u4eba\u4e4b\u8eab\u5c31\u884c \u5566\uff01\u4f60\u4eec\u5c3c\u6cca\u5c14\u7684\u795e\u7bad\u624b\u8fde\u8fd9\u70b9\u672c\u9886\u90fd\u6ca1\u5b66\u8fc7\u5417\uff1f\u201d\u6bd4\u5c04\u7bad\u800c\u53ef\u4ee5\u81ea\u5df1\u4e0d\u7528\u6467\u5f13\u5e26\u7bad\uff0c\u5c3c\u6cca \u5c14\u738b\u786e\u662f\u6ca1\u6709\u542c\u8fc7\uff0c\u90a3\u80af\u76f8\u4fe1\u53ea\u5f53\u662f\u5510\u8d5b\u82b1\u56e0\u4e3a\u81ea\u5df1\u62ff\u4e0d\u8d77\u94c1\u5f13\uff0c\u6545\u4f5c\u5927\u8a00\uff0c\u5176\u5b9e\u662f\u60f3\u9003 \u907f\u3002\u5510\u7ecf\u5929\u53ef\u662f\u6697\u6674\u597d\u7b11\uff0c\u5510\u5bb6\u7d20\u6709\u201c\u5929\u4e0b\u6697\u5668\u7b2c\u4e00\u5bb6\u201d\u4e4b\u79f0\uff0c\u5510\u8d5b\u82b1\u662f\u5510\u5bb6\u7855\u679c\u4ec5\u5b58\u7684\u957f \u8f88\uff0c\u5979\u548c\u8fd9\u51e0\u4e2a\u4eba\u6bd4\u7bad\uff0c\u90a3\u7b80\u76f4\u662f\u7b49\u4e8e\u732b\u548c\u8001\u9f20\u620f\u800d\u4e00\u822c\u3002 \u3000\u3000\u53ea\u89c1\u5510\u8d5b\u82b1\u4e00\u6b65\u4e00\u6b65\uff0c\u6c14\u5598\u5401\u5401\u7684\u8d70\u5165\u573a\u5fc3\uff0c\u5ffd\u5730\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u53eb\u9053\uff1a \u201c\u4f60\u4eec\u628a\u5229\u7bad\u5c04\u6765\u5427\uff01\u201d\u90a3\u56db\u4e2a\u5f13\u7bad\u6559\u5934\u89c1\u4e00\u4e2a\u8001\u5987\u4eba\u8d70\u51fa\u6765\uff0c\u53c8\u662f\u5982\u6b64\u8fd9\u822c\u6a21\u6837\uff0c\u53cd\u800c\u7ed9 \u5979\u5f04\u7cca\u6d82\u4e86\uff0c\u4ed6\u4eec\u5f00\u59cb\u4ee5\u4e3a\u662f\u5979\u8d70\u5f97\u7d2f\u4e86\uff0c\u5750\u5728\u5730\u4e0a\u6b47\u606f\uff0c\u54ea\u77e5\u5979\u5374\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u6765\u3002\u8fd9\u56db \u4e2a\u5c3c\u6cca\u5c14\u6559\u5934\u5728\u519b\u4e2d\u7d20\u8d1f\u76db\u540d\uff0c\u5c82\u80af\u5c04\u4e00\u4e2a\u624b\u65e0\u5bf8\u94c1\u7684\u8001\u5987\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u56b7\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4eec\u4e0d\u6562\u548c\u6211\u6bd4\u7bad\u5417\uff1f\u54c8\uff01\u54c8\uff01\u5c3c\u6cca\u5c14\u7684\u795e\u7bad\u624b\u610f\u662f\u865a\u6709\u5176 \u540d\uff01\u201d\u5c3c\u6cca\u5c14\u53d7\u6c49\u5316\u751a\u6df1\uff0c\u8bb8\u591a\u4eba\u61c2\u5f97\u6c49\u8bed\u3002\u8fd9\u56db\u4e2a\u6559\u5934\u4e2d\u6709\u4e24\u4e2a\u4fbf\u80fd\u542c\u80fd\u8bf4\u3002\u5176\u4e2d\u4e00\u4eba\u5fcd \u4e0d\u4f4f\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u9a82\u6211\u4e8b\u5c0f\uff0c\u635f\u53ca\u5c3c\u6cca\u5c14\u5c04\u624b\u7684\u5a01\u540d\uff0c\u53ef\u4e0d\u6210\uff01\u201d\u7acb\u523b\u5f20\u5f13\u642d\u7bad\uff0c\u53eb\u9053\uff1a \u201c\u6211\u8fd9\u4e00\u7bad\u5c04\u4f60\u5934\u4e0a\u7684\u7389\u7c2a\uff0c\u4f60\u4e0d\u8981\u52a8\uff0c\u514d\u5f97\u8bef\u4f24\uff01\u201d\u4ed6\u7684\u7bad\u6cd5\u767e\u4e0d\u5931\u4e00\uff0c\u5509\u7684\u4e00\u7bad\uff0c\u5bf9\u51c6 \u5510\u8d5b\u82b1\u7684\u5934\u5c04\u53bb\u3002 \u3000\u3000\u8fd9\u4e2a\u6559\u5934\u8fd8\u771f\u7684\u4e0d\u5fcd\u5c04\u4f24\u4e00\u4e2a\u8001\u5987\uff0c\u6240\u4ee5\u9884\u5148\u51fa\u8a00\u63d0\u9192\u3002\u54ea\u77e5\u5510\u8d5b\u82b1\u53ef\u5168\u4e0d\u9886\u4ed6\u8fd9\u4e2a \u60c5\uff0c\u53ea\u89c1\u5f13\u5982\u9739\u96f3\uff0c\u7bad\u4f3c\u6d41\u661f\uff0c\u500f\u7684\u5c04\u5230\u5510\u8d5b\u82b1\u5934\u4e0a\uff0c\u5510\u8d5b\u82b1\u628a\u624b\u4e00\u62db\uff0c\u82e5\u65e0\u5176\u4e8b\u7684\u5c06\u90a3\u652f \u5229\u7bad\u63a5\u4e86\u4e0b\u6765\uff0c\u5728\u5730\u4e0a\u4e00\u63d2\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5176\u4ed6\u7684\u4eba\u600e\u4e48\u4e0d\u5c04\uff1f\u201d\u90a3\u4e2a\u6559\u5934\u5927\u5403\u4e00\u60ca\uff0c\u53c8\u662f \u5506\u7684\u4e00\u7bad\uff0c\u5bf9\u51c6\u5979\u7684\u624b\u8155\u5c04\u53bb\uff0c\u5510\u8d5b\u82b1\u4f38\u6307\u4e00\u5f39\uff0c\u90a3\u652f\u5229\u7bad\u53c8\u63d2\u5230\u6ce5\u4e2d\u3002\u53e6\u4e00\u4e2a\u6559\u5934\u5fc3\u72e0\u624b \u8fa3\uff0c\u4e00\u7bad\u5c04\u5411\u5979\u7684\u54bd\u5589\uff0c\u5510\u8d5b\u82b1\u53eb\u58f0\uff1a\u201c\u54ce\u54df\uff0c\u4e0d\u597d\u4e86\uff01\u5634\u5df4\u4e00\u5f20\uff0c\u5229\u7bad\u63d2\u5165\u53e3\u4e2d\u3002\u7b2c\u4e00\u4e2a \u6559\u5934\u6028\u540c\u4f34\u9053\uff1a\u201c\u4f60\u600e\u4e48\u771f\u7684\u8981\u5c04\u6b7b\u5979\uff1f\u201d\u5ffd\u89c1\u5510\u8d5b\u82b1\u5f20\u53e3\u4e00\u5410\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u7684\u7259\u9f7f\u8fd8 \u884c\uff01\u201d\u90a3\u652f\u7bad\u53c8\u63d2\u5728\u5730\u4e0a\u3002\u8fd9\u6b63\u662f\u5510\u95e8\u7684\u7edd\u6280\u4e00\u201c\u556e\u7c07\u6cd5\u201d\u3002\u5510\u8d5b\u82b1\u56b7\u9053\uff1a\u201c\u4f60\u4eec\u662f\u600e\u4e48\u5c04 \u7684\uff1f\u8fd9\u4e00\u4f1a\u5b50\u529f\u592b\u624d\u5c04\u51fa\u4e09\u679d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u628a\u90a3\u56db\u4e2a\u6559\u5934\u5168\u90fd\u6fc0\u6012\uff0c\u56db\u5f13\u9f50\u5f20\uff0c\u56db\u7bad\u9f50\u53d1\u3002\u5510\u8d5b\u82b1\u5750\u5728\u5730\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u7bad \u5230\u4fbf\u63a5\uff0c\u970e\u65f6\u95f4\u5728\u5979\u5468\u56f4\u90fd\u63d2\u6ee1\u4e86\u7bad\u6746\uff0c\u597d\u8c61\u5e73\u5730\u7b51\u8d77\u4e86\u4e2a\u7bf1\u7b06\u56f4\u7740\u5979\u4e00\u6837\u3002\u5510\u8d5b\u82b1\u8fb9\u63a5\u7bad \u8fb9\u56b7\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff01\u8fd8\u8981\u5c04\u5feb\u4e00\u4e9b\uff01\u201d\u56db\u4e2a\u6559\u5934\u54ac\u4e00\u54ac\u7259\uff0c\u8fd9\u65f6\u5df2\u4e0d\u662f\u6015\u5c06\u5979\u5c04\u6b7b\uff0c\u800c \u662f\u6015\u635f\u4e86\u4ed6\u4eec\u519b\u4e2d\u795e\u7bad\u624b\u7684\u5a01\u540d\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u90fd\u65bd\u5c55\u51fa\u201c\u8fde\u73e0\u7bad\u201d\u7684\u7edd\u6280\uff0c\u4f46\u89c1\u98de\u77e2\u5982\u8757\uff0c \u7eb7\u7eb7\u6512\u5c04\uff0c\u5510\u8d5b\u82b1\u624b\u6cd5\u4e00\u53d8\uff0c\u968f\u63a5\u968f\u7529\uff0c\u6bcf\u7529\u4e00\u679d\u7bad\uff0c\u5c31\u5c06\u9635\u679d\u7bad\u78b0\u843d\u3002\u5979\u867d\u5e74\u8fc8\uff0c\u5374\u662f\u5185 \u529f\u6709\u706b\u5019\u7684\u4eba\uff0c\u4ee5\u624b\u7529\u7bad\u7684\u52b2\u9053\u6bd4\u90a3\u56db\u4e2a\u6559\u5934\u7528\u94c1\u80ce\u5f13\u5c04\u51fa\u7684\u52b2\u9053\u8fd8\u8981\u51cc\u5389\u5f97\u591a\uff0c\u4f46\u89c1\u6ee1\u7a7a \u7bad\u96e8\uff0c\u7eb7\u7eb7\u81ea\u90a3\u56db\u4e2a\u6559\u5934\u53cd\u5c04\u56de\u53bb\u3002\u5979\u4e5f\u662f\u6709\u610f\u4e0d\u4f24\u90a3\u56db\u4e2a\u6559\u5934\uff0c\u5229\u7bad\u5c04\u56de\uff0c\u90fd\u63d2\u5728\u56db\u4e2a\u6559 \u5934\u8eab\u8fb9\u7684\u5730\u4e0a\u3002\u970e\u65f6\u95f4\u8c61\u5e73\u5730\u6d8c\u8d77\u4e86\u4e00\u5ea7\u7bad\u6797\uff0c\u5c06\u90a3\u56db\u4e2a\u6559\u5934\u90fd\u56f4\u5728\u91cc\u9762\u3002 \u3000\u3000\u56db\u4e2a\u6559\u5934\u5927\u60ca\u5931\u8272\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4ed6\u4eec\u7bad\u56ca\u4e2d\u7684\u5229\u7bad\u5b8c\u4e86\u3002\u5510\u8d5b\u82b1\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u7559\u5fc3\uff0c\u6211 \u8fd8\u656c\u4e86\uff0c\u6211\u8981\u628a\u4f60\u4eec\u7684\u56db\u5f20\u5f13\u5f26\u5168\u90fd\u5c04\u65ad\uff01\u201d\u5979\u53cc\u624b\u9f50\u53d1\uff0c\u5c06\u6700\u540e\u6240\u63a5\u7684\u56db\u679d\u7bad\u90fd\u7529\u51fa\u53bb\uff0c \u7bad\u631f\u98ce\u58f0\uff0c\u63a0\u8fc7\u7a7a\u4e2d\uff0c\u53d1\u51fa\u9e23\u545c\u7684\u5578\u58f0\u3002\u90a3\u56db\u4e2a\u6559\u5934\u65e0\u6cd5\u53ef\u6321\uff0c\u53ea\u597d\u4e0d\u7ea6\u800c\u540c\u7684\u63d0\u8d77\u94c1\u5f13\u62db \u67b6\uff0c\u4f46\u542c\u5f97\u4e00\u9635\u50fb\u556a\u7684\u8fde\u73e0\u5bc6\u54cd\uff0c\u56db\u5f20\u94c1\u5f13\u7684\u5f26\u679c\u7136\u90fd\u7ed9\u5979\uff0c\u4e00\u4e3e\u5c04\u65ad\uff01 \u3000\u3000\u56db\u4e2a\u6559\u5934\u63b7\u5f13\u4e8e\u5730\uff0c\u6c14\u6cae\u795e\u4e27\u3002\u5510\u8d5b\u82b1\u62cd\u62cd\u8863\u670d\uff0c\u6296\u4e00\u6296\u8eab\u4e0a\u7684\u5c18\u7802\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u5982 \u4f55\uff1f\u6211\u4e2d\u534e\u5987\u5b7a\u4e4b\u8f88\uff0c\u4ea6\u5584\u9a91\u5c04\uff0c\u8fd9\u8bdd\u53ef\u4e0d\u662f\u8bf4\u5047\u7684\u5427\uff1f\u201d\u90a3\u56db\u4e2a\u6559\u5934\u8de8\u51fa\u7bad\u6746\u6240\u56f4\u6210\u7684\u5708 \u5b50\uff0c\u9762\u8272\u60e8\u767d\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c\u610f\u6b8a\u4e3a\u4e0d\u4fe1\uff0c\u62f1\u624b\u9f50\u9053\uff1a\u201c\u8001\u592a\u592a\u795e\u6280\u60ca\u4eba\uff0c\u53ea\u6015\u5929\u4e0b\u518d\u4e5f\u627e\u4e0d \u5230\u7b2c\u4e8c\u4e2a\u3002\u201d\u5510\u8d5b\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u62db\u624b\u8bf4\u9053\uff1a\u201c\u67f3\u9752\uff0c\u4f60\u4e5f\u6765\u9732\u4e00\u624b\u3002\u201d \u3000\u3000\u5176\u65f6\u72c2\u98ce\u5df2\u606f\uff0c\u5c71\u4e0a\u7684\u98de\u9e1f\uff0c\u7eb7\u7eb7\u98de\u8fdb\u8c37\u4e2d\u8eb2\u907f\u5916\u9762\u5377\u8d77\u7684\u6f2b\u5929\u96ea\u7247\uff0c\u6768\u67f3\u9752\u53d6\u51fa\u4e86\u5f39 \u5f13\uff0c\u6307\u7740\u5929\u4e0a\u7684\u4e24\u884c\u96c1\u9053\uff1a\u201c\u6211\u7b2c\u4e00\u6392\u5f39\u5f13\uff0c\u8981\u6253\u5de6\u8fb9\u8fd9\u884c\u96c1\u7684\u5de6\u773c\uff0c\u7b2c\u4e8c\u6392\u5f39\u5f13\u8981\u6253\u53f3\u8fb9 \u8fd9\u884c\u96c1\u7684\u53f3\u773c\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e0d\u4f46\u90a3\u56db\u4e2a\u6559\u5934\u5403\u60ca\uff0c\u6240\u6709\u542c\u5f97\u61c2\u6c49\u8bed\u7684\u5c3c\u6cca\u5c14\u6b66\u58eb\u90fd\u9732\u51fa\u4e0d \u76f8\u4fe1\u7684\u795e\u6c14\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u6768\u67f3\u9752\u5f39\u5f13\u4e00\u66f3\uff0c\u560e\u54e9\u8fde\u58f0\uff0c\u5de6\u8fb9\u90a3\u4e00\u884c\u96c1\u5e94\u58f0\u5760\u5730\uff1b\u6768\u67f3\u9752\u811a \u8ddf\u75be\u8f6c\uff0c\u67f3\u8170\u4e00\u6298\uff0c\u5f39\u5f13\u518d\u66f3\uff0c\u53f3\u8fb9\u90a3\u4e00\u884c\u96c1\u4e5f\u90fd\u5760\u5728\u5730\u4e0a\uff0c\u4e5f\u76f8\u8ddd\u4e09\u4e08\u6709\u591a\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u6559\u5934\u5206\u6210\u4e24\u7ec4\uff0c\u4e0a\u524d\u9a8c\u770b\uff0c\u679c\u7136\u662f\u5de6\u8fb9\u90a3\u4e00\u884c\u96c1\u90fd\u778e\u4e86\u5de6\u773c\uff0c\u53f3\u8fb9\u90a3\u4e00\u884c\u96c1\u90fd\u778e \u4e86\u53f3\u773c\uff0c\u773c\u4e2d\u90fd\u5d4c\u7740\u4e00\u9897\u5c0f\u5c0f\u7684\u5f39\u5b50\u3002\u4e00\u6392\u5f39\u5f13\u80fd\u6253\u778e\u4e00\u884c\u5929\u7a7a\u98de\u96c1\u7684\u773c\uff0c\u800c\u4e14\u8981\u5de6\u4e2d\u5de6\uff0c \u8981\u53f3\u4e2d\u53f3\u3002\u8fd9\u624b\u529f\u592b\u4e0e\u521a\u624d\u5510\u8d5b\u82b1\u7684\u63a5\u7bad\u7529\u7bad\uff0c\u5404\u6709\u80dc\u573a\uff0c\u90fd\u662f\u8db3\u4ee5\u9707\u4e16\u9a87\u4fd7\u7684\u7edd\u6280\uff01\u56db\u4e2a \u5c3c\u6cca\u5c14\u6559\u5934\u5fc3\u671b\u53e3\u670d\uff0c\u518d\u4e5f\u4e0d\u6562\u591a\u8bf4\u534a\u53e5\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u4e0e\u6768\u67f3\u9752\u56de\u5230\u5e2d\u4e0a\uff0c\u6c5f\u5357\u7b11\u563b\u563b\u9053\uff1a\u201c\u90b9\u4f2f\u6bcd\uff0c\u4f60\u8fd9\u624b\u7edd\u6280\u6559\u6211\u884c\u4e0d\u884c\uff1f\u201d\u6768\u67f3 \u9752\u7b11\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u78d5\u5934\uff0c\u53eb\u6211\u5988\u5988\uff0c\u6211\u4e5f\u8bb8\u4f1a\u6559\u4f60\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u597d\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u6211\u8fd9\u5c31\u7ed9 \u4f60\u78d5\u5934\u3002\u201d\u6768\u67f3\u9752\u53c8\u6c14\u53c8\u607c\uff0c\u9053\uff1a\u201c\u522b\u80e1\u95f9\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u90b9\u7edb\u971e\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u6559\u7ed9 \u4ed6\u3002\u201d\u6768\u67f3\u9752\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u971e\u513f\u770b\u4e0a\u4e86\u8fd9\u4e2a\u4e66\u7ae5\uff1f\u201d\u5c82\u77e5\u90b9\u7edb\u971e\u65e9\u4e0e\u6c5f\u5357\u7ea6\u5b9a\uff0c \u60f3\u8981\u5b66\u6c5f\u5357\u90a3\u624b\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u8bf4\u597d\u4e86\u5c06\u6768\u5bb6\u7684\u795e\u5f39\u7edd\u6280\u4f5c\u4e3a\u4ea4\u6362\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5fc3\u70e6\u610f\u4e71\uff0c\u4ed6\u4e00\u8fde\u770b\u4e86\u4e09\u573a\u7edd\u6280\uff0c\u7531\u4e0d\u5f97\u4ed6\u4e0d\u60ca\u60f6\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e9b\u6c49\u4eba\u96be \u9053\u90fd\u662f\u795e\u4ed9\u4e0b\u51e1\uff1f\u6bd2\u9152\u4e0d\u4e2d\u7528\uff0c\u8fde\u4e00\u4e2a\u8001\u592a\u5a46\u4e5f\u80fd\u5c04\u65ad\u94c1\u80ce\u5f13\u3002\u201d\u4ed6\u6240\u7b49\u7684\u4e00\u4e2a\u4eba\u8fd8\u6ca1\u6709 \u6765\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u4ec0\u4e48\u529e\u6cd5\u6765\u6298\u5510\u7ecf\u5929\u7684\u5a01\u98ce\u3002 \u3000\u3000\u5ffd\u89c1\u4e00\u4e2a\u9ec4\u53d1\u78a7\u773c\u7684\u897f\u6d0b\u6b66\u58eb\u7ad9\u4e86\u51fa\u6765\u51e0\u54e9\u5495\u565c\u7684\u8bf4\u4e86\u4e00\u5927\u904d\uff0c\u901a\u8bd1\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u53f2 \u5bc6\u592b\u5148\u751f\u8bf4\uff0c\u4ed6\u66fe\u542c\u8bf4\u4e2d\u56fd\u6709\u4e00\u79cd\u5947\u5999\u7684\u70b9\u7a74\u529f\u592b\uff0c\u53ef\u4ee5\u5236\u4eba\u4e8e\u6b7b\uff0c\u4ed6\u8bf4\u5728\u6b27\u6d32\u4e5f\u6709\u4e00\u79cd\u53eb \u505a\u201c\u5b50\u5348\u6d41\u201d\u7684\u529f\u592b\uff0c\u53ef\u4ee5\u968f\u65f6\u4ee4\u4eba\u7684\u8840\u6db2\u505c\u6b62\u5faa\u73af\uff0c\u770b\u6765\u5927\u7ea6\u4e0e\u4e2d\u56fd\u7684\u70b9\u7a74\u529f\u592b\u76f8\u8fd1\uff0c\u4ed6 \u60f3\u4e0e\u4e2d\u56fd\u7684\u70b9\u7a74\u540d\u5bb6\u5f7c\u6b64\u89c2\u6469\u5370\u8bc1\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u542c\u8bf4\u6b27\u6d32\u5c45\u7136\u4e5f\u6709\u8fd9\u79cd\u4e0e\u201c\u70b9\u7a74\u201d\u76f8\u540c\u7684\u529f\u592b\uff0c\u5927\u611f\u5174\u8da3\uff0c\u6b63\u60f3\u5e94\u6218\uff0c\u5ffd\u89c1\u6c5f\u5357 \u7b11\u563b\u563b\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6c5f\u5357\u624b\u75d2\u5f97\u7d27\uff0c\u5510\u5927\u4fa0\uff0c\u8fd9\u4e00\u573a\u5c31\u8ba9\u6211\u73a9\u73a9\u5427\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u6211\u51e0\u4e4e\u628a\u4f60\u8fd9\u4f4d\u70b9\u7a74\u540d\u5bb6\u5fd8\u8bb0\u5566\uff01\u201d\u6c5f\u5357\u4e50\u4e0d\u53ef\u652f\uff0c\u5bf9\u90b9 \u7edb\u971e\u9053\uff1a\u201c\u4f60\u542c\u5230\u6ca1\u6709\uff1f\u5510\u5927\u4fa0\u4e5f\u5938\u5956\u6211\uff0c\u4f60\u8fd8\u6562\u8bf4\u6211\u7684\u529f\u592b\u4e0d\u884c\uff1f\u201d\u5495\u565c\u5495\u565c\u8fde\u559d\u4e86\u4e09\u5927 \u76cf\u8461\u8404\u7f8e\u9152\uff0c\u8fde\u7b11\u5e26\u8df3\u7684\u8dd1\u5230\u573a\u5fc3\uff0c\u6d3b\u8c61\u4e00\u4e2a\u987d\u76ae\u7684\u5b69\u5b50\uff0c\u4e89\u4e0d\u53ca\u5f85\u7684\u53bb\u53c2\u52a0\u4ec0\u4e48\u6709\u8da3\u7684\u73a9 \u610f\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u521a\u624d\u90a3\u4e2a\u8001\u592a\u5a46\u4e5f\u6709\u8fd9\u822c\u60ca\u4eba\u7684\u672c\u9886\uff0c\u53ea\u6015\u8fd9\u5c0f\u5b69\u5b50 \u771f\u4f1a\u70b9\u7a74\uff01\u5bf9\u6c5f\u5357\u5012\u662f\u4e0d\u6562\u5c0f\u89d1\u3002\u90a3\u897f\u6d0b\u6b66\u58eb\u5374\u662f\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u6307\u7740\u9f3b\u5b50\u9053\uff1a\u201c\u54fc\uff0c\u54fc\uff01 \u53eb\u8fd9\u4e2a\u5c0f\u5b69\u4e8e\u548c\u6211\u6bd4\u8d5b\u70b9\u7a74\uff1f\u201d\u6c5f\u5357\u542c\u4e0d\u61c2\u7684\u8bf4\u8bdd\uff0c\u4f46\u89c1\u4ed6\u54c7\u54e9\u54c7\u5566\u7684\u6307\u624b\u753b\u811a\u5927\u56b7\u4e00\u901a\uff0c \u5f62\u72b6\u751a\u662f\u6ed1\u7a3d\uff0c\u4e5f\u5b66\u7740\u4ed6\u7684\u6837\u5b50\u548c\u8154\u8c03\u6307\u7740\u9f3b\u5b50\u80e1\u53eb\u4e00\u901a\u3002\u90a3\u897f\u6d0b\u6b66\u58eb\u95ee\u901a\u8bd1\u9053\uff1a\u201c\u8fd9\u5c0f\u628a \u620f\u8bf4\u4ec0\u4e48\uff1f\u201d\u90a3\u901a\u8bd1\u5176\u5b9e\u4e5f\u4e0d\u77e5\u9053\u6c5f\u5357\u662f\u8bf4\u4ec0\u4e48\uff0c\u4f46\u4ed6\u542c\u5f97\u5c3c\u6cca\u5c14\u738b\u4f20\u8bdd\u4e0b\u6765\uff0c\u8bf4\u8fd9\u4e2a\u5c0f\u5b69 \u5b50\u662f\u70b9\u7a74\u540d\u5bb6\uff0c\u4fbf\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u7684\u70b9\u7a74\u529f\u592b\u5f88\u5386\u5bb3\uff0c\u95ee\u4f60\u6562\u4e0d\u6562\u548c\u4ed6\u6bd4\u8bd5\u3002\u201d\u8f6c\u8fc7\u5934\u95ee\u6c5f\u5357 \u9053\u201c\u662f\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff1f\u201d\u6c5f\u5357\u5fcd\u7740\u4e00\u809a\u76ae\u7684\u7b11\uff0c\u6ee1\u8138\u6b63\u7ecf\u7684\u70b9\u5934\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9\u6781\uff01\u4f60\u8bd1\u5f97 \u4e00\u70b9\u4e0d\u9519\uff0c\u6b63\u662f\u8fd9\u4e2a\u610f\u601d\uff01\u6b27\u90fd\u7531\u90fd\uff0c\u827e\u8a79\u54c7\u54e9\u54c7\u55dc\u3002\u201d\u521a\u624d\u8fd9\u4e2a\u897f\u6d0b\u6b66\u58eb\u51fa\u573a\u65f6\u66fe\u5411\u51b0\u5ddd \u5927\u5973\u95ee\u5019\uff1a\u201c\u6b27\u90fd\u7531\u90fd\u201d\u662f\uff1a\u201c\u4f60\u597d\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u7ecf\u8fc7\u901a\u8bd1\u4f20\u8bdd\uff0c\u4e5f\u95ee\u4ed6\uff1a\u201c\u4f60\u597d\u3002\u201d\u4ed6 \u8bf4\uff1a\u201c\u4e49\u8a79\u54c7\u54e9\u54c7\u55dc\u3002\u201d\u5373\u662f\u56de\u7b54\uff1a\u201c\u6211\u5f88\u597d\u3002\u201d\u8fd9\u662f\u897f\u65b9\u5e94\u916c\u7684\u5ba2\u5957\u8bed\u3002\u6c5f\u5357\u5c31\u5b66\u4f1a\u4e86\u8fd9 \u4e24\u53e5\uff0c\u6a21\u4eff\u897f\u6d0b\u6b66\u58eb\u7684\u53e3\u543b\uff0c\u4e71\u56b7\u4e00\u901a\uff0c\u4f46\u8bf4\u51fa\u6765\u5f53\u7136\u662f\u8352\u8154\u8d70\u8c03\u3002 \u3000\u3000\u90a3\u897f\u6d0b\u6b66\u58eb\u521d\u65f6\u52c3\u7136\u5927\u6012\uff0c\u542c\u4e86\u6c5f\u5357\u4e71\u56b7\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c\u5fc3\uff1a\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u600e\u4e48\u5411\u6211\u95ee\u597d \u53c8\u81ea\u95ee\u81ea\u7b54\u5462\uff1f\u201d\u7ee7\u800c\u81ea\u4f5c\u806a\u660e\u7684\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8fd9\u4e2a\u5c0f\u5b69\u5b50\u6015\u6211\u5f04\u6b7b\u4ed6\uff0c\u6240\u4ee5\u5148\u5411\u6211\u5957\u5957 \u4ea4\u60c5\u3002\u201d\u4fbf\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u653e\u5fc3\uff0c\u6211\u4e0d\u8981\u4f60\u7684\u6027\u547d\uff0c\u53ea\u5c06\u4f60\u70b9\u5f97\u6655\u5012\u5c31\u7b97\u5566\u3002\u201d\u6c5f\u5357\u51dd\u795e\u542c\u4ed6 \u8bf4\u8bdd\uff0c\u8ddf\u7740\u53c8\u5b66\u4ed6\u7684\u6307\u7740\u90a3\u897f\u6d0b\u6b66\u58eb\u7684\u9f3b\u5b50\u5927\u56b7\u4e00\u901a\uff0c\u8fd9\u51e0\u53e5\u8bdd\u751a\u957f\uff0c\u4ed6\u5b66\u8bb2\u4e5f\u8bb2\u5f97\u4e0d\u5168\uff0c \u4f46\u201c\u6211\u4e0d\u8981\u4f60\u7684\u6027\u547d\u201d\u8fd9\u4e00\u53e5\u5374\u8bb2\u5f97\u76f8\u5f53\u7eaf\u719f\u3002\u90a3\u897f\u6d0b\u6b66\u58eb\u521a\u521a\u5bf9\u4ed6\u6709\u70b9\u597d\u611f\uff0c\u4e00\u542c\u4e4b\u4e0b\uff0c \u6012\u706b\u53c8\u53d1\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u5f20\u624b\u5c31\u5411\u6c5f\u5357\u4e00\u6251\u3002 \u3000\u3000\u90a3\u6b66\u58eb\u53ea\u5f53\u6c5f\u5357\u662f\u548c\u4ed6\u80e1\u95f9\uff0c\u5e76\u4e0d\u771f\u60f3\u7528\u201c\u4e8e\u5348\u6d41\u201d\u7684\u95ed\u8840\u6cd5\u6765\u5bf9\u4ed8\u4ed6\uff0c\u800c\u662f\u60f3\u5c06\u4ed6\u6454 \u5012\u4fbf\u7b97\u3002\u5c82\u77e5\u6c5f\u5357\u5728\u77f3\u6797\u4e2d\uff0c\u5b66\u8fc7\u201c\u7a7a\u82b1\u7ed5\u6811\u201d\u7684\u8eab\u6cd5\uff0c\u5728\u5ca9\u77f3\u6797\u4e2d\u4e5f\u53ef\u4ee5\u7a7a\u63d2\u81ea\u5982\uff0c\u897f\u6d0b \u6b66\u58eb\u8981\u6349\u4ed6\uff0c\u4ed6\u53ea\u5f53\u662f\u6349\u8ff7\u85cf\uff0c\u7ed5\u7740\u90a3\u6b66\u58eb\u7684\u8eab\u5b50\u8f6c\u6765\u8f6c\u53bb\u3002\u90a3\u6b66\u58eb\u624b\u957f\u811a\u957f\uff0c\u6349\u6765\u6349\u53bb\u90fd \u6349\u4e0d\u7740\u6c5f\u5357\uff0c\u6c5f\u5357\u65f6\u800c\u4ece\u4ed6\u80ef\u4e0b\u8e09\u8fc7\uff0c\u65f6\u800c\u4ece\u4ed6\u80a9\u5934\u8df3\u8fc7\uff0c\u95f9\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u65c1\u8fb9\u4eba\u770b\u53bb\uff0c\u5c31 \u4f3c\u4e4e\u90a3\u957f\u624b\u957f\u811a\u7684\u897f\u6d0b\u6b66\u58eb\u5728\u548c\u8fd9\u4e2a\u5c0f\u5b69\u5b50\u95f9\u7740\u8f6c\u5708\u7684\u73a9\u513f\uff0c\u90fd\u5fcd\u4fca\u4e0d\u7981\uff0c\u563b\u563b\u54c8\u54c8\u7684\u54d7\u7b11 \u8d77\u6765\u3002 \u3000\u3000\u90a3\u897f\u6d0b\u6b66\u58eb\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u80e1\u95f9\uff0c\u6211\u53ef\u4e0d\u7559\u60c5\u5566\uff01\u201d\u6c5f\u5357\u4e5f\u5b66\u7740\u4ed6\u559d\u9053\uff1a\u201c\u6211\u53ef\u4e0d \u7559\u60c5\u5566\uff01\u201d\u53ea\u542c\u5f97\u6323\u7684\u4e00\u58f0\uff0c\u90a3\u897f\u6d0b\u6b66\u58eb\u63a3\u51fa\u4e00\u4ef6\u5947\u5f62\u602a\u72b6\u7684\u5175\u5668\uff0c\u4f3c\u4e00\u4e2a\u94f6\u5236\u7684\u7b14\u7ba1\uff0c\u7ea6 \u6709\u516d\u4e03\u5bf8\u957f\uff0c\u4e24\u5934\u90fd\u662f\u5c16\u7684\uff0c\u94f6\u5149\u95ea\u95ea\uff0c\u5411\u7740\u6c5f\u5357\u7684\u80f8\u819b\u4e00\u523a\u3002\u6c5f\u5357\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u70b9\u7684\u662f\u4ec0 \u4e48\u7a74\uff1f\u201d\u8eab\u5f62\u4e00\u4ef0\uff0c\u4fbf\u5f85\u907f\u5f00\uff0c\u54ea\u77e5\u201c\u5f97\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u652f\u7b14\u7ba1\u5ffd\u7136\u957f\u4e86\u51e0\u5bf8\uff0c\u5728\u6c5f\u5357\u7684\u80f8\u812f \u4e0a\u91cd\u91cd\u70b9\u4e86\u4e00\u4e0b\uff0c\u539f\u6765\u8fd9\u679d\u7b14\u7ba1\uff0c\u88c5\u6709\u673a\u62ec\uff0c\u53ef\u4ee5\u968f\u610f\u4f38\u957f\uff0c\u9ad8\u624b\u6bd4\u6597\uff0c\u53ea\u5dee\u6beb\u5398\uff0c\u4f55\u51b5\u6c5f \u5357\u8fd8\u5e76\u4e0d\u662f\u9ad8\u624b\uff0c\u4e00\u4e0b\u4fbf\u7ed9\u4ed6\u70b9\u4e2d\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u53ea\u89c9\u4e00\u9635\u9178\u9ebb\uff0c\u7acb\u5373\u53c8\u8df3\u8d77\u6765\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u4ef6\u4e1c\u897f\u5012\u662f\u4ef6\u597d\u73a9\u610f\uff0c\u9001\u7ed9\u6211\u884c\u4e0d \u884c\uff1f\u201d\u90a3\u897f\u6d0b\u6b66\u58eb\u7684\u201c\u5b50\u5348\u6d41\u201d\u95ed\u8840\u6cd5\u548c\u4e2d\u56fd\u7684\u201c\u70b9\u7a74\u6cd5\u201d\u540c\u4e00\u539f\u7406\uff0c\u4e0d\u8fc7\u5374\u6ca1\u6709\u4e2d\u56fd\u70b9\u7a74 \u6cd5\u7684\u6df1\u5965\uff0c\u4e2d\u56fd\u7684\u70b9\u7a74\u6cd5\u662f\u8ba4\u660e\u4eba\u8eab\u4e0a\u7684\u5404\u79cd\u7a74\u9053\uff0c\u6240\u51fb\u4e4b\u5904\u53ea\u5728\uff1d\u70b9\uff1b\u800c\u201c\u5b50\u5348\u6d41\u201d\u95ed\u8840 \u6cd5\u5219\u662f\u6309\u7740\u65f6\u8fb0\uff0c\u5c06\u8eab\u4f53\u67d0\u4e00\u90e8\u5206\u7684\u8840\u6db2\u5faa\u73af\u73af\u963b\u904f\u3002\u6c5f\u5357\u8ddf\u9ec4\u77f3\u9053\u4eba\u4e03\u5929\uff0c\u5c31\u53ea\u5b66\u5f97\u4ed6\u4e00 \u6837\u201c\u98a0\u5012\u7a74\u201d\u7684\u529f\u592b\uff0c\u7a74\u9053\u98a0\u5012\uff0c\u8840\u6db2\u7684\u5faa\u73af\u81ea\u7136\u4e5f\u4e0d\u662f\u4f9d\u7167\u6b63\u8f68\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u201c\u5b50\u5348\u6d41\u201d\u95ed \u7a74\u6cd5\u89e6\u53ca\u7684\u90e8\u4f4d\u8f83\u5e7f\uff0c\u56e0\u6b64\u4ea6\u611f\u5230\u4e00\u9635\u9178\u9ebb\uff0c\u4f46\u5374\u65e0\u4f24\u5bb3\u3002 \u3000\u3000\u90a3\u897f\u6d0b\u6b66\u58eb\u70b9\u4e0d\u5012\u6c5f\u5357\uff0c\u6c5f\u5357\u53cd\u800c\u563b\u563b\u54c8\u54c8\u5730\u6765\u62a2\u4ed6\u7684\u7b14\u7ba1\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u6309\u673a \u62ec\uff0c\u201c\u5f97\u201d\u7684\u4e00\u58f0\uff0c\u957f\u4edf\u6587\u5728\u6c5f\u5357\u7684\u624b\u8155\u4e0a\u523a\u4e86\u4e00\u4e0b\uff0c\u6c5f\u5357\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5bb6\u4f19\uff0c\u4f60\u4e0d\u7ed9\u6211\uff0c \u6211\u504f\u8981\u53d6\uff01\u201d\u4f7f\u51fa\u4e00\u62db\u9648\u5929\u5b87\u6559\u4ed6\u7684\u201c\u987a\u624b\u7275\u7f8a\u201d\uff0c\u5c06\u90a3\u897f\u6d0b\u6b66\u58eb\u4e00\u626f\uff0c\u4e00\u53ea\u624b\u6258\u7740\u4ed6\u7684\u8098 \u5c16\uff0c\u53e6\u4e00\u53ea\u624b\u4fbf\u6765\u786c\u62a2\u4ed6\u624b\u4e2d\u7684\u7b14\u7ba1\uff1a\u5c82\u77e5\u90a3\u897f\u6d0b\u6b66\u58eb\u9887\u6709\u51e0\u65a4\u86ee\u529b\uff0c\u624b\u8155\u4e00\u5f2f\uff0c\u4fbf\u662f\u4e00\u8bb0 \u52fe\u62f3\uff0c\u6c5f\u5357\u9669\u9669\u907f\u5f00\uff0c\u4ed6\u90a3\u652f\u7b14\u7ba1\u5411\u524d\u4e00\u9001\uff0c\u94f6\u9488\u9661\u7684\u957f\u51fa\u4e00\u5c3a\u6709\u591a\uff0c\u9488\u7aef\u950b\u5229\uff0c\u5728\u6c5f\u5357\u817f \u4e0a\u91cd\u91cd\u523a\u4e86\u4e00\u4e0b\u3002\u8fd9\u4e00\u4e0b\u5374\u4e0d\u662f\u201c\u5b50\u5348\u6d41\u201d\u95ed\u8840\u6cd5\uff0c\u800c\u662f\u628a\u94f6\u9488\u5f53\u6210\u4f24\u4eba\u7684\u5229\u5668\u3002\u539f\u6765\u4ed6\u8fd9 \u652f\u7b14\u7ba1\uff0c\u5171\u6709\u4e09\u622a\uff0c\u7b2c\u4e8c\u622a\u7684\u94f6\u9488\u662f\u949d\u5934\u7684\uff0c\u7528\u4ee5\u95ed\u8840\uff0c\u7b2c\u4e09\u622a\u7684\u9488\u5c16\u5374\u662f\u950b\u5229\u7684\uff0c\u5185\u8d2e\u6bd2 \u6db2\uff0c\u53ef\u4ee5\u4f24\u4eba\u3002\u6c5f\u5357\u7ed9\u4ed6\u4e00\u9488\uff0c\u75db\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\u5927\u53eb\u8d77\u6765\uff0c\u5ffd\u89c9\u4e00\u817f\u9ebb\u6728\u4e0d\u4ec1\u53ea\u9053\u662f\u88ab\u4ed6 \u70b9\u4e86\u7a74\u9053\uff0c\u5927\u6012\u53eb\u9053\uff1a\u201c\u54fc\uff0c\u5c31\u53ea\u4f60\u4f1a\u70b9\u7a74\u4e48\uff1f\u770b\u6211\u7684\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u4ece\u90a3\u897f\u6d0b\u6b66\u58eb\u84b2\u6247\u822c \u7684\u5927\u624b\u5e95\u4e0b\u8fc7\uff0c\u9a84\u6307\u4e00\u70b9\uff0c\u6b63\u6b63\u70b9\u4e2d\u4ed6\u8151\u4e0b\u7684\u6655\u7a74\uff0c\u90a3\u897f\u6d0b\u6b66\u58eb\u54fc\u4e86\u5730\u58f0\uff0c\u7acb\u523b\u8dcc\u5012\u3002 \u3000\u3000\u6c5f\u5357\u4e00\u8df7\u4e00\u62d0\u7684\u8dd1\u4e86\u56de\u6765\uff0c\u5bf9\u5510\u7ecf\u5929\u9053\uff1a\u201c\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\u4e0d\u9876\u7528\uff0c\u5582\uff0c\u4f60\u7ed9\u6211\u89e3 \u7a74\u3002\u201d\u5510\u7ecf\u5927\u4e00\u770b\uff0c\u89c1\u4ed6\u5c0f\u817f\u7ea2\u80bf\uff0c\u7b11\u9053\uff1a\u2018\u8fd9\u4e0d\u662f\u70b9\u7a74\uff0c\u4f60\u559d\u4e00\u676f\u9152\u5c31\u597d\u5566\uff01\u201d\u6697\u628a\u4e00\u9897 \u78a7\u7075\u4e39\u4e22\u5165\u9152\u676f\uff0c\u5de5\u5357\u63a5\u8fc7\u8fd9\u676f\u8461\u8404\u9152\u4e00\u559d\u800c\u5c3d\uff0c\u75db\u695a\u82e5\u5931\uff0c\u563b\u563b\u54c8\u54c8\u5730\u5bf9\u5c3c\u6cca\u5c14\u738b\u7b11\u9053\uff1a \u201c\u8fd9\u5927\u4e2a\u5b50\u8bf4\u8981\u548c\u6211\u6bd4\u8d5b\u70b9\u7a74\uff0c\u54c8\uff0c\u6211\u7528\u70b9\u7a74\u6cd5\u70b9\u5012\u4ed6\uff0c\u4ed6\u5374\u7528\u6bd2\u9488\u6574\u6cbb\u6211\uff0c\u771f\u4e0d\u8981\u8138\u3002\u4e0d \u8fc7\u4ed6\u65e2\u7136\u5728\u70b9\u7a74\u7684\u6bd4\u8d5b\u4e0a\u8f93\u4e86\uff0c\u5f53\u7136\u7b97\u6211\u5168\u80dc\u5566\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u505a\u58f0\u4e0d\u5f97\uff0c\u90a3\u897f\u6d0b\u6b66\u58eb\u7684\u4f19\u4f34 \u5374\u5ffd\u7136\u54d7\u53eb\u8d77\u6765\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u89c1\u540c\u4f34\u660f\u8ff7\u4e0d\u9192\uff0c\u4ed6\u4eec\u4ee5\u4e3a\u4e2d\u56fd\u7684\u70b9\u7a74\u65e2\u4e0e\u201c\u5b50\u5348\u6d41\u201d\u95ed\u7a74\u6cd5\u76f8\u540c\uff0c\u4fbf\u5c3d\u4ed6\u4eec \u6240\u77e5\uff0c\u7528\u89e3\u201c\u5b50\u5348\u6d41\u201d\u95ed\u8840\u7684\u624b\u672f\u65bd\u6551\uff0c\u5c82\u77e5\u4e2d\u56fd\u7684\u70b9\u7a74\u6cd5\u5965\u5999\u975e\u5e38\uff0c\u5404\u5bb6\u5404\u6d3e\u7684\u70b9\u7a74\u6cd5\u90fd \u662f\u4e0d\u5c3d\u76f8\u540c\u7684\uff0c\u4ed6\u4eec\u4e0d\u52a8\u624b\u672f\u4e5f\u7f62\u4e86\uff0c\u4e00\u52a8\u624b\u672f\uff0c\u5272\u65ad\u9759\u8109\uff0c\u653e\u51fa\u8840\u6765\uff0c\u6478\u4e00\u6478\u540c\u4f34\u7684\u9f3b \u7aef\uff0c\u53cd\u800c\u6ca1\u4e86\u6c14\u606f\u3002\u56e0\u6b64\u7fa4\u60c5\u6c79\u6d8c\uff0c\u8bf4\u662f\u6c5f\u5357\u7528\u5deb\u6cd5\u6cbb\u6b7b\u4e86\u4ed6\u4eec\u7684\u540c\u4f34\uff0c\u8981\u5411\u6c5f\u5357\u7d22\u547d\u3002 \u3000\u3000\u901a\u8bd1\u4f20\u8bdd\u8fc7\u6765\uff0c\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u5475\u5440\uff0c\u6211\u65e9\u8bf4\u8fc7\u6211\u7684\u70b9\u7a74\u975e\u5e38\u5386\u5bb3\uff0c\u95ee\u8fc7\u4ed6\u6562\u4e0d\u6562\u4e0e\u6211\u6bd4 \u8bd5\u7684\uff0c\u662f\u4e48\uff1f\u201d\u901a\u8bd1\u70b9\u70b9\u5934\u201c\u4e0d\u9519\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u662f\u548e\u7531\u81ea\u53d6\uff0c\u600e\u80fd\u8981\u6211\u8d54\u547d\u201d\u5c3c\u6cca \u5c14\u738b\u4e00\u60f3\uff0c\u65e2\u7136\u6bd4\u6b66\uff0c\u90a3\u5c31\u96be\u4fdd\u4e0d\u4f24\u6027\u547d\uff0c\u786e\u662f\u6ca1\u7406\u7531\u8981\u6c5f\u5357\u8d54\u547d\u3002\u4e0d\u8fc7\u6b66\u58eb\u4eec\u7fa4\u60c5\u6c79\u6d8c\uff0c \u5374\u662f\u4ee4\u4ed6\u96be\u4ee5\u5904\u7f6e\uff0c\u4fbf\u9053\uff1a\u201c\u8bf7\u95ee\u5c0f\u4fa0\uff0c\u4f60\u65e2\u4f1a\u70b9\u7a74\uff0c\u662f\u4e0d\u662f\u80fd\u591f\u89e3\u6551\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b2c\u4e00\u6b21\u542c\u5f97\u4eba\u79f0\u4ed6\u505a\u201c\u5c0f\u4fa0\u201d\uff0c\u4e50\u5f97\u7709\u5f00\u773c\u7b11\uff0c\u88c5\u6a21\u4f5c\u6837\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5417\uff1f\u8fd9\u4e2a \u2014\u2014\u201d\u5c3c\u6cca\u5c14\u738b\u6025\u9053\uff1a\u201c\u600e\u6837\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u6211\u5e08\u7236\u53ea\u6559\u6211\u70b9\u7a74\uff0c\u89e3\u7a74\u5374\u672a\u6559\u8fc7\u3002\u66f4\u4e14\uff0c\u8c01\u6559 \u4ed6\u4eec\u80e1\u5f04\uff0c\u5200\u5440\u53c9\u5440\u7684\u4e71\u5272\u4e00\u901a\uff0c\u4ed6\u4eec\u628a\u540c\u4f34\u5f04\u6b7b\u4e86\uff0c\u5374\u63a8\u7ed9\u6211\u533b\uff0c\u54ea\u6709\u8fd9\u4e2a\u9053\u7406\uff1f\u201d\u5c3c\u6cca \u5c14\u738b\u5927\u4e3a\u5931\u671b\uff0c\u9053\uff1a\u201c\u8fd9\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d\u6c5f\u5357\u6162\u541e\u541e\u5730\u9053\uff1a\u201c\u5c0f\u4fa0\u4e0d\u4f1a\uff0c\u5927\u4fa0\u53ef\u4f1a\u3002\u5510\u5927\u4fa0 \u4e0d\u4f46\u4f1a\u89e3\u7a74\uff0c\u800c\u4e14\u6b7b\u4e86\u7684\u4ed6\u4e5f\u53ef\u4ee5\u533b\u6d3b\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5927\u559c\uff0c\u6025\u5fd9\u5411\u5510\u7ecf\u5929\u6c42\u6551\uff0c\u5510\u7ecf\u5929\u6697\u6697 \u597d\u7b11\uff0c\u4e0d\u60f3\u6c5f\u5357\u518d\u80e1\u95f9\u4e0b\u53bb\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u4e14\u5f85\u6211\u8bd5\u4e00\u8bd5\u770b\uff0c\u6211\u53ef\u4e0d\u6562\u62c5\u4fdd\u51c6\u6210\u3002\u8bf7\u90a3\u4e9b\u4eba\u4e0d \u8981\u56f4\u5728\u65c1\u8fb9\uff0c\u6211\u597d\u65bd\u672f\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u8bf7\u901a\u8bd1\u4f20\u8bdd\uff0c\u90a3\u7fa4\u897f\u6d0b\u6b66\u58eb\u542c\u8bf4\u5510\u7ecf\u5929\u53ef\u4ee5\u628a\u6b7b\u4eba\u533b\u6d3b\uff0c\u7acb\u523b\u8ba9\u51fa\u8def\u6765\uff0c\u606d\u8bf7 \u5510\u7ecf\u5929\u6765\u65bd\u672f\u3002\u5510\u7ecf\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7684\u624b\u672f\uff0c\u662f\u4e0d\u5fc5\u4e34\u5e8a\u7684\u3002\u201d\u968f\u624b\u5728\u5730\u4e0a\u62fe\u8d77\u4e00\u7c92 \u77f3\u5b50\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c\u7b75\u5e2d\u4e0e\u573a\u5fc3\u76f8\u8ddd\u6570\u5341\u4e08\uff1a\u8fd9\u7c92\u5c0f\u77f3\u547c\u7684\u4e00\u58f0\uff0c\u7aef\u7aef\u6b63\u6b63\u7684\u6253\u4e2d\u4e86\u8eba\u5728\u5730\u4e0a \u90a3\u4e2a\u897f\u6d0b\u6b66\u58eb\u7684\u7709\u5fc3\uff0c\u65c1\u8fb9\u7684\u540c\u4f34\u54d7\u7136\u5927\u53eb\uff0c\u6b63\u6b32\u8d23\u95ee\u5510\u7ecf\u5929\u4f55\u4ee5\u5bf9\u6b7b\u4e86\u7684\u6b66\u58eb\u5c1a\u52a0\u4fae\u8fb1\uff1f \u5ffd\u89c1\u90a3\u897f\u6d0b\u6b66\u58eb\u201c\u54ce\u54ce\u201d\u7684\u53eb\u4e86\u4e00\u58f0\uff0c\u624b\u811a\u98a4\u52a8\uff0c\u4e00\u970e\u773c\u4fbf\u7ad9\u4e86\u8d77\u6765\u3002\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u884c \u5566\uff0c\u4ed6\u4eec\u81ea\u5df1\u5272\u7834\u7684\u4f24\u53e3\uff0c\u90a3\u6211\u53ef\u4e0d\u8d1f\u8d23\u4e86\u3002\u201d\u624b\u672f\u5272\u7834\u7684\u5916\u4f24\uff0c\u6781\u4e3a\u8f7b\u5fae\uff0c\u8fb9\u65c1\u7684\u4eba\u66ff\u4ed6 \u88f9\u4f24\u5305\u624e\uff0c\u7acb\u523b\u884c\u52a8\u5982\u5e38\u3002 \u3000\u3000\u8fd9\u7fa4\u897f\u6d0b\u6b66\u58eb\u89c1\u4e2d\u56fd\u7684\u70b9\u7a74\u6cd5\u5982\u6b64\u795e\u5947\uff0c\u90fd\u662f\u5fc3\u670d\u53e3\u670d\uff0c\u4e00\u81f4\u5411\u5510\u7ecf\u5929\u9053\u8c22\u3002\u90a3\u4e2a\u4e0e\u6c5f \u5357\u52a8\u624b\u7684\u897f\u6d0b\u6b66\u58eb\u957f\u53f9\u4e00\u58f0\uff0c\u5c06\u95ed\u8840\u7684\u7b14\u7ba1\u53eb\u4eba\u9001\u7ed9\u6c5f\u5357\u3002\u897f\u65b9\u6b66\u58eb\u7684\u89c4\u77e9\uff0c\u6bd4\u8bd5\u8f93\u4e86\uff0c\u5c31 \u5f97\u5c06\u4f69\u5251\u732e\u7ed9\u5bf9\u65b9\uff0c\u8fd9\u4e2a\u897f\u6d0b\u6b66\u58eb\u6b63\u662f\u4f9d\u7167\u4ed6\u4eec\u7684\u89c4\u77e9\uff0c\u4f55\u51b5\u6c5f\u5357\u66fe\u5411\u4ed6\u7d22\u53d6\u8fc7\u8fd9\u679d\u7b14\u7ba1\u3002 \u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4f60\u656c\u6211\u4e00\u5c3a\uff0c\u6211\u4e5f\u656c\u4f60\u4e00\u4e08\uff0c\u8fd9\u679d\u7b14\u7ba1\u6211\u4e0d\u8981\u5566\u3002\u201d\u90a3\u897f\u6d0b\u6b66\u58eb\u66f4\u662f\u611f\u6fc0\uff0c\u5927 \u5927\u7684\u606d\u7ef4\u4e86\u6c5f\u5357\u4e00\u901a\uff0c\u79f0\u8d5e\u4ed6\u7684\u70b9\u7a74\u786e\u662f\u4e16\u95f4\u5c11\u6709\uff0c\u6c5f\u5357\u7b11\u5f97\u773c\u775b\u772f\u6210\u4e86\u4e00\u6761\u7f1d\uff0c\u5176\u5b9e\u4ed6\u7684 \u201c\u98a0\u5012\u7a74\u9053\u201d\u529f\u592b\u8fd8\u53ef\u7b97\u5f97\u662f\u72ec\u95e8\u7edd\u6280\uff0c\u81f3\u4e8e\u8bba\u5230\u70b9\u7a74\u7684\u529f\u592b\uff0c\u7b2c\u4e09\u6d41\u8fd8\u591f\u4e0d\u4e0a\u3002 \u3000\u3000\u6c5f\u5357\u6b63\u5728\u563b\u563b\u54c8\u54c8\uff0c\u5ffd\u89c9\u56db\u56f4\u7684\u4eba\u7a81\u7136\u9759\u5bc2\uff0c\u6c14\u6c1b\u6709\u5f02\uff01 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u7a81\u7136\u53d1\u51fa\u4e00\u58f0\u6b22\u547c\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u53ea\u89c1\u4e24\u4e2a\u6b8b\u4e86\u53cc\u8db3\u7684\u602a\u4eba\uff0c\u624b\u633d\u7740\u624b\uff0c\u4e00\u8df7\u4e00 \u62d0\u7684\u8df3\u8dc3\u800c\u6765\uff0c\u5f62\u72b6\u8be1\u79d8\u4e4b\u6781\uff0c\u8fd9\u6b63\u662f\u5728\u70fd\u706b\u53f0\u4e2d\u6240\u53ee\u8a00\u8981\u6253\u65ad\u6c5f\u5357\u53cc\u8db3\u7684\u90a3\u4e24\u4e2a\u602a\u4eba\uff0c\u6c5f \u5357\u4e00\u89c1\uff0c\u5413\u5f97\u4e0d\u6562\u4f5c\u58f0\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u80a9\u4e0a\u642d\u7740\u4e00\u4ef6\u5927\u7ea2\u8888\u88df\uff0c\u6b63\u662f\u80e1\u50e7\u6cf0\u5409\u63d0\u7528\u4f5c\u5175\u5668\u7684\u90a3\u4ef6\u8888\u88df\uff0c\u521a\u624d\u522e\u5927\u98ce \u4e4b\u65f6\uff0c\u8888\u88df\u88ab\u5439\u5230\u8c37\u5916\uff0c\u60f3\u662f\u521a\u597d\u88ab\u8fd9\u4e24\u4eba\u62fe\u83b7\uff0c\u5c31\u62ab\u4e86\u8fdb\u6765\u3002\u6c5f\u5357\u5f88\u6015\u8fd9\u4e24\u4e2a\u602a\u4eba\uff0c\u8fd9\u4e24 \u4e2a\u602a\u4eba\u5374\u4e0d\u7406\u4f1a\u6c5f\u5357\uff0c\u773c\u775b\u5411\u5e2d\u4e0a\u4e00\u626b\uff0c\u5ffd\u5730\u4ece\u8888\u88df\u4e0a\u53d6\u4e0b\u4e00\u652f\u5929\u5c71\u795e\u8292\uff0c\u95f4\u9053\uff1a\u201c\u8fd9\u662f\u8c01 \u7684\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u6025\u5fd9\u7ed9\u4ed6\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e2d\u56fd\u6700\u51fa\u540d\u7684\u5927\u4fa0\u3002\u8fd9\u4e24\u4f4d\u662f\u963f\u62c9\u4f2f\u6700\u51fa\u540d\u7684\u4fee \u58eb\uff0c\u5de6\u8fb9\u8fd9\u4f4d\u662f\u5085\u53e4\u62c9\uff0c\u53f3\u8fb9\u8fd9\u4f4d\u662f\u963f\u65af\u7f57\u3002\u4ed6\u4eec\u7684\u5e08\u7236\u662f\u4e1c\u6b27\u548c\u963f\u62c9\u4f2f\u6700\u6709\u672c\u9886\u7684\u5f02 \u4eba\u3002\u201d\u5510\u7ecf\u5929\u62b1\u62f3\u9053\uff1a\u201c\u9886\u6559\u4e86\uff0c\u8fd9\u652f\u795e\u8292\u6b63\u662f\u6211\u7684\u3002\u201d\u90a3\u4e24\u4e2a\u602a\u4eba\u6253\u91cf\u4e86\u5510\u7ecf\u5929\u4e00\u4f1a\uff0c\u8bf4 \u9053\uff1a\u201c\u5e78\u5f97\u5728\u8fd9\u91cc\u91cd\u9022\uff0c\u771f\u662f\u597d\u6781\u4e86\uff0c\u771f\u662f\u597d\u6781\u4e86\u3002\u6211\u4eec\u8fd8\u8981\u548c\u5510\u5927\u4fa0\u9886\u6559\u9886\u8f83\uff01\u201d\u5c3c\u6cca\u5c14 \u738b\u542c\u8bf4\u4ed6\u4eec\u66fe\u7ecf\u89c1\u8fc7\u9887\u4e3a\u5947\u602a\u3002 \u3000\u3000\u90a3\u4e00\u665a\u5728\u70fd\u706b\u53f0\u5185\uff0c\u5085\u53e4\u62c9\u548c\u963f\u7f57\u65af\u5176\u5b9e\u8fd8\u6ca1\u6709\u89c1\u7740\u5510\u7ecf\u5929\u7684\u9762\uff0c\u4ed6\u4eec\u662f\u7ed9\u5510\u7ecf\u5929\u7684\u5929 \u5c71\u795e\u8292\u5413\u8dd1\u4e86\u7684\u3002\u521a\u624d\u4ed6\u4eec\u5728\u8c37\u5916\u62fe\u83b7\u80e1\u50e7\u7684\u5927\u7ea2\u8888\u88df\uff0c\u770b\u5230\u63d2\u5728\u8888\u88df\u4e0a\u7684\u5929\u5c71\u795e\u8292\uff0c\u8fd8\u4ee5 \u4e3a\u662f\u5510\u6653\u6f9c\u5728\u6b64\uff08\u4ed6\u4eec\u7684\u53cc\u817f\u6b63\u662f\u5510\u6653\u6f9c\u7528\u5929\u5c71\u795e\u8292\u5c04\u6b8b\u7684\u3002\uff09\u786c\u7740\u5934\u76ae\uff0c\u5fc3\u60ca\u80c6\u98a4\u7684\u8fdb \u6765\u3002\u5982\u4eca\u4e00\u89c1\u4e0d\u662f\u5510\u6653\u6f9c\u5fc3\u4e2d\u90fd\u662f\u53c8\u7f9e\u53c8\u6012\uff0c\u7acb\u610f\u8981\u548c\u5510\u7ecf\u5929\u518d\u51b3\u96cc\u96c4\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u8bf7\u4e24\u4f4d\u5212\u51fa\u9053\u6765\u3002\u201d\u5fc3\u4e2d\u6b63\u5728\u76d8\u7b97\u5982\u4f55\u89e3\u4ed6\u4eec\u7684\u9634\u9633\u638c\u529b\uff0c\u4fae\u53e4\u62c9\u548c\u963f\u65af \u7f57\u6084\u6084\u8033\u8bed\uff0c\u5546\u91cf\u4e86\u597d\u4e00\u4f1a\uff0c\u7531\u5085\u53e4\u62c9\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e24\u4eba\u662f\u4e00\u5e08\u6240\u6388\uff0c\u78b0\u5230\u4e00\u4e2a\u662f\u4e24\u4eba\u9f50 \u4e0a\uff0c\u78b0\u5230\u4e00\u5343\u4e2a\u4e5f\u662f\u4e24\u4eba\u9f50\u4e0a\u3002\u8981\u6bd4\u8bd5\u5c31\u662f\u6211\u4eec\u5144\u5f1f\u540c\u5510\u5927\u4fa0\u4e00\u9f50\u6bd4\u8bd5\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00 \u51db\uff0c\u60f3\uff1a\u201c\u82e5\u662f\u4e00\u4e2a\uff0c\u6211\u6709\u628a\u63e1\u53d6\u80dc\uff0c\u82e5\u662f\u4e24\u4eba\uff0c\u4ed6\u4eec\u90a3\u602a\u5f02\u7684\u9634\u9633\u638c\u529b\uff0c\u5374\u975e\u6211\u4e00\u4eba\u6240\u80fd \u7834\u89e3\u3002\u201d\u4f46\u5728\u56fd\u738b\u7b75\u524d\uff0c\u5c82\u80fd\u793a\u5f31\uff0c\u4fbf\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u90a3\u5c31\u8ba9\u6211\u4e00\u4eba\u63a5\u4e24\u4f4d\u7684\u9ad8\u62db\uff01\u201d \u3000\u3000\u5085\u53e4\u62c9\u9053\uff1a\u201c\u5510\u5927\u4fa0\u662f\u56fd\u738b\u8d35\u5bbe\uff0c\u54b1\u4eec\u82e5\u7136\u6b66\u6bd4\uff0c\u53ea\u6015\u4f24\u4e86\u548c\u6c14\u3002\u201d\u5510\u7ecf\u5929\u5fc3\u4e2d\u4e00\u559c\uff0c \u9053\uff0c\u201c\u90a3\u4e48\u6587\u6bd4\u4e5f\u884c\uff0c\u8bf7\u95ee\u4e24\u4f4d\u8981\u5982\u4f55\u6bd4\u6cd5\uff1f\u201d\u5085\u53e4\u62c9\u9053\uff1a\u201c\u6211\u4eec\u4e8c\u4eba\u60f3\u4e0e\u5510\u5927\u4fa0\u6bd4\u8bd5\u8f7b \u529f\u3002\u201d\u539f\u6765\u4ed6\u4eec\u4e8c\u4eba\u88ab\u5510\u6653\u6f9c\u6253\u5f97\u6015\u4e86\uff0c\u542c\u8bf4\u5510\u7ecf\u5929\u4e5f\u59d3\u201c\u5510\u201d\u53c8\u4f1a\u7528\u5929\u5c71\u795e\u8292\uff0c\u65e9\u5df2\u731c\u5230 \u5510\u7ecf\u5929\u662f\u5510\u6653\u6f9c\u7684\u513f\u5b50\uff0c\u867d\u7136\u89c1\u5510\u7ecf\u5929\u5982\u6b64\u5e74\u8f7b\uff0c\u529f\u529b\u6599\u60f3\u8fdc\u8fdc\u4e0d\u5982\u4ed6\u7684\u7236\u4eb2\uff0c\u4f46\u5fc3\u6709\u987e \u5fcc\uff0c\u672a\u6709\u5341\u5206\u628a\u63e1\uff0c\u7ec8\u662f\u4e0d\u6562\u6b66\u6bd4\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u8fd9\u53e5\u8bdd\u4e00\u8bf4\u51fa\u6765\uff0c\u53ef\u4ee4\u5f97\u5168\u573a\u9707\u52a8\uff0c\u8fde\u5510\u7ecf\u5929\u4e5f\u6697\u6697\u5403\u60ca\u3002\u8fd9\u4e24\u4e2a\u602a \u4eba\u7684\u819d\u76d6\u5df2\u788e\uff0c\u867d\u7136\u7ecf\u8fc7\u591a\u65e5\u6cbb\u7597\uff0c\u4e0d\u5fc5\u50cf\u5728\u70fd\u706b\u53f0\u7684\u65f6\u5019\uff0c\u7528\u624b\u4ee3\u8db3\u8d70\u8def\uff0c\u4f46\u4e24\u53cc\u811a\u597d\u8c61 \u540a\u5728\u5927\u817f\u4e0a\u4e00\u6837\uff0c\u4e00\u8df7\u4e00\u62d0\uff0c\u8d70\u4e00\u6b65\u90fd\u5341\u5206\u5403\u529b\uff0c\u8fd9\u4e2a\u6837\u5b50\uff0c\u5374\u5c45\u7136\u8981\u4e0e\u5510\u7ecf\u5929\u6bd4\u8bd5\u8f7b\u529f\uff0c \u800c\u4e14\u770b\u4ed6\u4eec\u7684\u795e\u6c14\uff0c\u7adf\u4f3c\u6781\u6709\u628a\u63e1\uff01 \u3000\u3000\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u5085\u53e4\u62c9\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u4ee5\u5357\u9762\u8fd9\u5ea7\u5c71\u5cf0\uff0c\u4f5c\u4e3a\u6bd4\u8bd5\u8f7b\u529f\u7684\u5730 \u70b9\uff0c\u8c01\u5148\u4e0a\u5230\u5cf0\u9876\uff0c\u8c01\u4fbf\u7b97\u8d62\u3002\u201d\u5510\u8d5b\u82b1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4eec\u662f\u4e24\u4e2a\u4eba\u5462\uff01\u82e5\u7136\u4e00\u4e2a\u6bd4\u5510 \u5927\u4fa0\u5148\u5230\uff0c\u4e00\u4e2a\u6bd4\u5510\u5927\u4fa0\u540e\u5230\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u4ffa\u53e4\u62c9\u9053\uff0c\u201c\u8981\u8d62\u6211\u4eec\u4e24\u4e2a\u5c31\u4e00\u9f50\u8d62\uff0c\u8981\u8f93\u6211 \u4eec\u4e24\u4e2a\u5c31\u4e00\u9f50\u8f93\u3002\u6211\u4eec\u53ea\u8981\u4e00\u4e2a\u843d\u5728\u5510\u5927\u4fa0\u4e4b\u540e\uff0c\u90a3\u5c31\u7b97\u6211\u4eec\u8f93\u4e86\u3002\u201d\u8fd9\u529e\u6cd5\u770b\u6765\u597d\u4f3c\u662f\u5510 \u7ecf\u5929\u5927\u5360\u4fbf\u5b9c\uff0c\uff0c\u5510\u8d5b\u82b1\u4e5f\u65e0\u8bdd\u53ef\u8bf4\u3002\u5085\u53e4\u62c9\u53c8\u559d\u4e86\u4e00\u5927\u676f\u9152\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u9152\u676f\u6454 \u6389\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8d81\u73b0\u5728\u5929\u8272\u597d\uff0c\u54b1\u4eec\u8fd9\u5c31\u6bd4\u5427\uff0c\u4e00\u522e\u5927\u98ce\uff0c\u8fd9\u5c71\u5cf0\u5c31\u66f4\u96be\u4e0a\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u62ac\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u90a3\u5ea7\u5c71\u5cf0\u5ced\u58c1\u5343\u4e08\uff0c\u79ef\u96ea\u7691\u7691\uff0c\u6709\u5982\u4e00\u5ea7\u767d\u7389\u5c4f\u98ce\u5728\u9633 \u5149\u4e0b\u95ea\u51fa\u971e\u8f89\u4e3d\u5f69\uff0c\u770b\u8fd9\u5149\u666f\uff0c\u53ea\u6015\u82cd\u8747\u722c\u4e0a\u53bb\u4e5f\u4f1a\u6ed1\u4e0b\u6765\uff0c\u4eba\u54ea\u5f97\u7acb\u8db3\uff1f\u5373\u7b97\u662f\u7528\u58c1\u864e\u6e38 \u5899\u7684\u529f\u592b\uff0c\u4e5f\u652f\u6301\u4e0d\u4e86\u591a\u4e45\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u60f3\u7b54\u8bdd\uff0c\u5ffd\u89c1\u51b0\u5ddd\u5929\u5973\u76c8\u76c8\u8d77\u7acb\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5510\u5927\u4fa0\u9002\u624d\u4e0e\u6211\u56fd\u7684\u7b2c\u4e00\u56fd\u5e08 \u6bd4\u4e86\u4e00\u573a\uff0c\u54b1\u4eec\u4e0d\u8be5\u8ba9\u5ba2\u4eba\u592a\u8fc7\u52b3\u7d2f\uff0c\u8bf7\u8ba9\u6211\u4e0e\u4e24\u4f4d\u5927\u5e08\u6bd4\u4e00\u573a\u5427\u3002\u5f7c\u6b64\u89c2\u6469\u5370\u8bc1\uff0c\u539f\u4e0d\u5fc5 \u6709\u56fd\u57df\u4e4b\u5206\uff0c\u5c3d\u6311\u7740\u8981\u4e0e\u5510\u5927\u4fa0\u6bd4\uff0c\u90a3\u5c82\u4e0d\u662f\u4ee4\u5ba2\u4eba\u611f\u5230\u89c1\u5916\u4e86\uff1f\u201d\u5979\u8fd9\u8bdd\u8bf4\u5f97\u51a0\u5195\u5802\u7687\uff0c \u5c3c\u6cca\u5c14\u738b\u65e0\u8bdd\u53ef\u9a73\uff0c\u5085\u53e4\u62c9\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u4e07\u91d1\u4e4b\u4f53\uff0c\u600e\u597d\u8f7b\u8bd5\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u5728 \u51b0\u5cf0\u4e0a\uff0c\u4e5f\u5df2\u60ef\u4e86\uff0c\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d\u5085\u53e4\u62c9\u7ea6\u7565\u542c\u8fc7\u5173\u4e8e\u51b0\u5ddd\u5929\u5973\u7684\u6545\u4e8b\uff0c\u5fc3\u5185\u5600\u5495\u3002\u51b0\u5ddd\u5929 \u5973\u7b11\u9053\uff1a\u201c\u82e5\u662f\u6211\u8f93\u7ed9\u4e8c\u4f4d\uff0c\u518d\u7531\u5510\u5927\u4fa0\u6765\u6bd4\uff0c\u90a3\u4e48\u53cc\u65b9\u90fd\u6bd4\u4e86\u4e00\u573a\uff0c\u5c31\u6ca1\u6709\u8c01\u5360\u4fbf\u5b9c \u4e86\u3002\u201d\u5085\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\uff0c\u5728\u963f\u62c9\u4f2f\u4e45\u4eab\u80dc\u540d\uff0c\u81ea\u7136\u8981\u4fdd\u6301\u8eab\u4efd\uff0c\u542c\u51b0\u5ddd\u5929\u5973\u7684\u53e3\u6c14\uff0c\u610f\u662f\u53e3 \u53e3\u58f0\u58f0\u6697\u6307\u4ed6\u4eec\u60f3\u5360\u5510\u7ecf\u5929\u7684\u4fbf\u5b9c\uff0c\u5fc3\u4e2d\u5927\u662f\u6c14\u6124\uff0c\u60f3\u9053\uff1a\u201c\u597d\uff0c\u5f85\u6211\u4eec\u8d62\u4e86\u4f60\u4e4b\u540e\uff0c\u518d\u4e0e \u4ed6\u6bd4\uff0c\u90a3\u4e5f\u51c6\u8d62\u3002\u8fd9\u4e0d\u8fc7\u662f\u8fdf\u65e9\u7684\u95ee\u9898\u800c\u5df2\u3002\u201d\u4fbf\u9053\uff1a\u201c\u516c\u4e3b\u5373\u5982\u6b64\u8bf4\uff0c\u90a3\u6211\u4eec\u53ea\u597d\u5949\u966a \u4e86\uff0c\u8bf7\u56fd\u738b\u6055\u6211\u4eec\u7c2a\u8d8a\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u6301\u676f\u6c89\u541f\uff0c\u826f\u4e45\u59cb\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\uff0c\u8bf7\u516c\u4e3b\u73cd\u91cd\u7389\u4f53\uff0c\u4e0d\u8981\u5f3a\u529b\u800c\u4e3a\u3002\u201d\u4ed6\u770b \u8fd9\u5ced\u58c1\u5343\u4e08\uff0c\u79ef\u96ea\u7691\u7691\u7684\u5c71\u5cf0\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u53d1\u6bdb\uff0c\u751a\u6015\u51b0\u5ddd\u5929\u5973\u4e00\u4e2a\u5931\u8db3\uff0c\u90a3\u4fbf\u8981\u7acb\u523b\u7389\u6b92 \u9999\u9500\uff01\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u6b32\u8ba8\u51b0\u5ddd\u5929\u5973\u4e3a\u59bb\uff0c\u90a3\u662f\u5341\u4e5d\u4e0d\u80fd\u5982\u613f\uff0c\u82e5\u7136\u51b0\u5ddd\u5929\u5973\u5931\u8db3\u800c\u6b7b\uff0c\u90a3 \u6700\u591a\u662f\u81ea\u5df1\u4e0e\u5510\u7ecf\u5929\u90fd\u65e0\u6240\u5f97\uff0c\u81ea\u5df1\u7684\u7687\u4f4d\u4e5f\u4e0d\u6015\u6709\u4eba\u5a01\u80c1\u4e86\u3002\u6240\u4ee5\u4ed6\u51e0\u6b21\u8f6c\u5ff5\uff0c\u6b32\u963b\u8fd8 \u4f11\uff0c\u7ec8\u4e8e\u8fd8\u662f\u5141\u51c6\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u6bd4\u8bd5\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u7684\u519b\u961f\u542c\u8bf4\u516c\u4e3b\u8981\u4eb2\u81ea\u6bd4\u8bd5\uff0c\u90fd\u662f\u53c8\u559c\u53c8\u60ca\uff0c\u559c\u8005\u662f\u6709\u673a\u4f1a\u5f97\u518d\u7779\u51b0\u5ddd\u5929\u5973\u7684\u4ed9 \u5bb9\uff0c\u60ca\u8005\u662f\u6015\u5979\u4e07\u4e00\u5931\u8db3\u3002\u4f46\u738b\u547d\u5df2\u4e0b\uff0c\u519b\u58eb\u53c8\u6709\u8c01\u6562\u4e0a\u53bb\u529d\u6b62\uff1f \u3000\u3000\u51e0\u5341\u8425\u5175\u4e01\u90fd\u6d8c\u51fa\u5e10\u5916\uff0c\u4f46\u5374\u662f\u4e07\u4f17\u65e0\u58f0\uff0c\u5927\u5bb6\u90fd\u5c4f\u4f4f\u4e86\u547c\u5438\u6765\u770b\u8fd9\u4e00\u573a\u6bd4\u8bd5\uff0c\u51b0\u5ddd\u5929 \u5973\u7f13\u7f13\u8d70\u5230\u5c71\u5cf0\u4e0b\u9762\uff0c\u548c\u5085\u53e4\u62c9\u3001\u963f\u65af\u7f57\u4e8c\u4eba\u5e76\u6392\u7ad9\u7acb\uff0c\u9759\u5f85\u5c3c\u6cca\u5c14\u738b\u53d1\u4ee4\u3002\u963f\u65af\u7f57\u5ffd\u9053\uff1a \u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u963f\u65af\u7f57\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u573a\u6bd4\u8bd5\uff0c\u540d\u662f\u4e00\u573a\uff0c\u5b9e\u662f\u4e24\u573a\u3002\u4e0a\u5c71\u4e4b \u540e\uff0c\u8fd8\u8981\u4e0b\u5c71\u3002\u518d\u56de\u6765\u65f6\uff0c\u8c01\u5148\u843d\u5730\uff0c\u90a3\u4fbf\u7b97\u8d62\uff0c\u8fd8\u662f\u4f9d\u7167\u4e0a\u5c71\u7684\u89c4\u77e9\u3002\u201d\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a \u201c\u8fd9\u4e2a\u4f55\u987b\u518d\u8bf4\u3002\u4e0a\u4e86\u5c71\u5f53\u7136\u8fd8\u5f97\u4e0b\u5c71\u3002\u597d\u5427\uff0c\u73b0\u5728\u53ef\u4ee5\u5f00\u59cb\u4e86\u5427\u3002\u201d\u6325\u4e00\u6325\u624b\uff0c\u53eb\u4e00\u4e2a\u5728 \u65c1\u4f8d\u5019\u7684\u5c3c\u6cca\u5c14\u6b66\u58eb\u544a\u8bc9\u56fd\u738b\u3002\u963f\u65af\u7f57\u6bd4\u5085\u53e4\u62c9\u7ec6\u5fc3\uff0c\u672a\u83b7\u80dc\uff0c\u5148\u9632\u654c\uff0c\u5fc3\u4e2d\u6697\u601d\uff1a\u201c\u516c\u4e3b \u80fd\u79f0\u51b0\u5ddd\u5929\u5973\uff0c\u53ea\u6015\u4e0a\u51b0\u5cf0\u786e\u6709\u975e\u5e38\u672c\u9886\uff0c\u4f46\u4e0b\u5c71\u4e4b\u65f6\uff0c\u4ee5\u6211\u4eec\u7ecf\u5e38\u7ec3\u4e4b\u6709\u7d20\u7684\u795e\u6280\uff0c\u5219\u5b9a \u662f\u80fd\u51c6\u80dc\u65e0\u7591\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u4e00\u58f0\u53f7\u4ee4\uff0c\u4ed6\u7684\u5fa1\u524d\u4f8d\u536b\u7acb\u523b\u53d1\u51fa\u4e00\u652f\u54cd\u7bad\uff0c\u53ea\u89c1\u5085\u53e4\u62c9\u624b\u4e00\u6309\u5730\uff0c\u817e\u7a7a\u98de\u8d77 \u4e09\u4e08\u6765\u9ad8\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u5411\u7740\u51b0\u5cf0\u731b\u884c\uff0c\u8eab\u4f53\u4e00\u6cbe\u7740\u51b0\u58c1\uff0c\u4fbf\u597d\u4f3c\u9489\u5728\u4e0a\u9762\u4f3c\u7684\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3 \u65f6\u5feb\uff0c\u963f\u65af\u7f57\u4e5f\u7167\u6837\u7684\u817e\u7a7a\u800c\u8d77\uff0c\u4f46\u5374\u62ff\u5085\u53e4\u62c9\u7684\u8eab\u4f53\u4f5c\u4e3a\u6309\u624b\u4e4b\u5904\uff0c\u4e00\u6309\u4ed6\u7684\u8eab\u4f53\uff0c\u7acb\u523b \u501f\u529b\u518d\u5ea6\u98de\u8d77\uff0c\u8fd9\u4e00\u4e0b\u4e24\u80a1\u529b\u91cf\u76f8\u5408\uff0c\u8eab\u5b50\u817e\u7a7a\uff0c\u98de\u5f97\u66f4\u9ad8\uff0c\u76f4\u98de\u4e0a\u56da\u4e94\u4e08\u9ad8\uff0c\u59cb\u884c\u51b2\u4e0b\uff0c \u4ecd\u662f\u50cf\u4f36\u53e4\u62c9\u4e00\u6837\u7684\u9644\u7740\u51b0\u58c1\uff0c\u518d\u8ba9\u5085\u53e4\u62c9\u501f\u4ed6\u7684\u8eab\u4f53\u4f5c\u4e3a\u6309\u624b\u4e4b\u5904\uff0c\u53d1\u529b\u518d\u98de\uff0c\u5982\u662f\u8005\u6b64 \u8d77\u5f7c\u843d\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5df2\u5347\u4e86\u6570\u5341\u4e08\u3002\u6ee1\u5c71\u8c37\u58eb\u5175\uff0c\u90fd\u4e0d\u7981\u5927\u58f0\u559d\u91c7\u3002\u5374\u4e0d\u77e5\u4ed6\u4eec\u662f\u7528\u4ec0\u4e48\u65b9 \u6cd5\uff0c\u5982\u6b64\u795e\u5947\uff0c\u7adf\u7136\u4ee4\u8eab\u4f53\u9489\u5728\u51b0\u58c1\u4e4b\u4e0a\u3002 \u3000\u3000\u539f\u6765\u5085\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u65ad\u817f\u4e4b\u540e\uff0c\u5f7c\u6b64\u76f8\u4f9d\uff0c\u5728\u5404\u79cd\u6b66\u529f\u4e0a\u90fd\u7ec3\u597d\u4e86\u4e92\u76f8\u914d\u5408\u4e4b\u6cd5\uff0c\u4ed6\u4eec \u5bf9\u8fd9\u573a\u6bd4\u8bd5\uff0c\u66f4\u662f\u65e9\u6709\u51c6\u5907\uff0c\u5341\u6307\u4e0a\u90fd\u6234\u6709\u94c1\u6307\u5957\uff0c\u786c\u7528\u6307\u529b\u63d2\u5165\u51b0\u58c1\u3002\u6240\u4ee5\u4ed6\u4eec\u575a\u6301\u8981\u4e24 \u4eba\u4e00\u540c\u6bd4\u8bd5\uff0c\u770b\u4f3c\u7ed9\u5bf9\u65b9\u4fbf\u5b9c\uff0c\u5176\u5b9e\u5374\u662f\u4ed6\u4eec\u7edd\u5999\u7684\u53d6\u80dc\u4e4b\u6cd5\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8ba9\u4ed6\u4eec\u5148\u8d77\u6b65\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u8ddf\u7740\u817e\u7a7a\u98de\u8d77\uff0c\u4f46\u89c1\u5979\u53cc\u8db3\u4e00\u6cbe\u51b0\u58c1\uff0c\u4fbf\u518d\u4e0d\u8d77 \u6b65\uff0c\u7adf\u4f3c\u5728\u51b0\u58c1\u4e0a\u6ed1\u884c\u4f3c\u7684\uff0c\u501f\u90a3\u51b0\u96ea\u4e4b\u529b\uff0c\u98ce\u9a70\u7535\u63a3\u822c\u7684\u5411\u4e0a\u75be\u9a76\u3002\u5c3c\u6cca\u5c14\u662f\u51b0\u96ea\u4e4b\u56fd\uff0c \u6e9c\u51b0\u6ed1\u96ea\u8fd9\u79cd\u73a9\u610f\u4e09\u5c81\u513f\u7ae5\u4e5f\u4f1a\uff0c\u4f46\u8db3\u4e0b\u5fc5\u5b9a\u88c5\u4e0a\u6ed1\u51b0\u7684\u978b\u5b50\uff0c\u800c\u4e14\u662f\u987a\u7740\u4e0b\u6613\uff0c\u5411\u4e0a\u6ed1 \u96be\uff0c\u50cf\u51b0\u5ddd\u5929\u5973\u8fd9\u6837\u65e0\u6240\u51ed\u4f9d\uff0c\u5728\u51b0\u58c1\u4e0a\u5411\u4e0a\u6ed1\u884c\uff0c\u90a3\u662f\u95fb\u6240\u672a\u95fb\uff0c\u89c1\u6240\u672a\u89c1\u3002\u6ee1\u5c71\u8c37\u7684\u58eb \u5175\u53d1\u51fa\u8f70\u5929\u822c\u7684\u559d\u91c7\u58f0\uff01\u8fde\u5510\u7ecf\u5929\u4e0e\u5979\u76f8\u8bc6\u4e86\u51e0\u5e74\uff0c\u4e5f\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u5979\u5728\u51b0\u5cf0\u4e0a\u7684\u8f7b\u529f\u672c \u9886\uff0c\u4e0d\u7981\u770b\u5f97\u5446\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u51b0\u5ddd\u5929\u5973\u4e0e\u4ffa\u53e4\u62c9\u3001\u963f\u65af\u7f57\u4e8c\u4eba\uff0c\u65f6\u800c\u4f60\u62a2\u5728\u6211\u7684\u524d\u9762\uff0c\u65f6\u800c\u6211\u62a2\u5728\u4f60\u7684\u524d\u9762\uff0c\u5085 \u963f\u4e8c\u4eba\u4e00\u98de\u5c31\u662f\u56db\u4e94\u4e08\u9ad8\uff0c\u4f46\u4ed6\u4eec\u8981\u6307\u63d2\u51b0\u58c1\uff0c\u65b9\u80fd\u501f\u529b\u518d\u98de\uff0c\u5f80\u5f80\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u51b0\u5ddd \u5929\u5973\u4fbf\u5373\u6ed1\u884c\u7a7a\u8d8a\u4ed6\u4eec\uff1b\u968f\u5373\u4ed6\u4eec\u4e8c\u4eba\u53c8\u662f\u817e\u7a7a\u63a0\u8fc7\uff0c\u51b0\u5ddd\u5929\u5973\u53c8\u8ffd\u4e0a\uff1b\u4e8e\u662f\u8005\u5154\u8d77\u9e58\u843d\uff0c \u7aef\u7684\u4ee4\u4eba\u773c\u82b1\u7f2d\u4e71\u3002\u6e10\u6e10\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u4f46\u89c1\u51b0\u5ddd\u5929\u5973\u8863\u8882\u98d8\u98d8\uff1a\u4e25\u5982\u5728\u5343\u4e08\u7684\u51b0\u58c1\u4e0a\u8e48\u7a7a\u98de \u7fd4\uff0c\u7f8e\u5999\u4e4b\u5904\uff0c\u96be\u4ee5\u8a00\u5ba3\u3002\u5c71\u8c37\u4e0b\u9762\u7684\u6570\u4e07\u5927\u519b\uff0c\u4e2a\u4e2a\u76ee\u4e0d\u8f6c\u775b\u7684\u4ef0\u5934\u4e0a\u671b\uff0c\u9759\u5f97\u8fde\u4e00\u6839\u9488 \u6389\u5728\u5730\u4e0a\u90fd\u542c\u5f97\u89c1\u54cd\u3002\u5982\u6b64\u5947\u666f\uff0c\u518d\u4e16\u96be\u9022\uff0c\u4eba\u4eba\u5fc3\u4e2d\u8d5e\u597d\uff0c\u8fde\u559d\u91c7\u4e5f\u65e0\u6687\u4e86\u3002\u518d\u8fc7\u7247\u65f6\uff0c \u53ea\u89c1\u8fd9\u4e09\u4eba\u597d\u50cf\u661f\u4e38\u98de\u8dc3\uff0c\u5373\u5c06\u5230\u8fbe\u5c71\u9876\u3002 \u3000\u3000\u9664\u4e86\u5510\u7ecf\u5929\u5510\u8d5b\u82b1\u7b49\u6709\u9650\u51e0\u4eba\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u7b49\u5df2\u77a7\u4e0d\u6e05\u695a\u8c01\u5728\u8c01\u7684\u524d\u9762\u3002\u6c5f\u5357\u7d27\u5f20\u4e4b \u6781\uff0c\u9891\u9891\u95ee\u5510\u7ecf\u5929\u9053\uff1a\u201c\u5582\uff0c\u73b0\u5728\u662f\u8c01\u5360\u5148\u4e86\uff1f\u201d\u5510\u7ecf\u5929\u7741\u5706\u53cc\u773c\uff0c\u4ef0\u5934\u4e0a\u671b\uff0c\u4e0d\u776c\u6c5f\u5357\uff0c \u6c5f\u5357\u6025\u5f97\u6413\u7740\u53cc\u624b\uff0c\u6ee1\u5934\u5927\u5e76\u3002\u5ffd\u542c\u5f97\u5510\u7ecf\u5929\u4e00\u58f0\u6b22\u547c\uff0c\u624b\u4e2d\u7684\u9152\u676f\u201c\u4ed3\u5577\u201d\u4e00\u58f0\u8dcc\u843d\u5730 \u4e0a\uff0c\u6c5f\u5357\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u5510\u7ecf\u5929\u900f\u4e86\u53e3\u6c14\uff0c\u8fd9\u624d\u53eb\u9053\uff1a\u201c\u516c\u4e3b\u8d62\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u5728\u63a5\u8fd1\u5cf0\u9876\u7684\u4e00\u5239\u90a3\uff0c\u5085\u53e4\u62c9\u4f7f\u5c3d\u5e73\u751f\u6c14\u529b\uff0c\u5411\u4e0a\u4e00\u51b2\uff0c\u521a\u521a\u6cbe\u5730\uff0c\u51b0\u5ddd\u5973\u7acb\u523b\u4fbf \u8ddf\u4e0a\u6765\uff0c\u800c\u963f\u65af\u7f57\u867d\u7136\u4e5f\u7acb\u5373\u98de\u4e0a\uff0c\u4f46\u5df2\u662f\u843d\u5728\u51b0\u5ddd\u5929\u5973\u540e\u9762\u3002\u7167\u4ed6\u4eec\u81ea\u5df1\u5b9a\u7684\u89c4\u77e9\uff0c\u53ea\u8981 \u6709\u4e00\u4eba\u843d\u540e\uff0c\u4fbf\u5f97\u7b97\u8f93\uff0c\u5510\u7ecf\u5929\u77a7\u5f97\u6e05\u695a\uff0c\u6240\u4ee5\u8bf4\u662f\u51b0\u5ddd\u5929\u5973\u8d62\u4e86\u3002 \u3000\u3000\u4f46\u5728\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u81ea\u5df1\u613f\u5f53\u4f5c\u548c\u8bba\u3002\u5085\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u6b63\u81ea\u6c14\u6cae\u795e\u4f24\uff0c\u51b0\u5ddd\u5929\u5973 \u5374\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u6211\u8d62\u4e86\u963f\u65af\u7f57\uff0c\u8f93\u7ed9\u4ffa\u53e4\u62c9\uff0c\u82e5\u7136\u7167\u4f60\u4eec\u5b9a\u7684\u89c4\u77e9\uff0c\u7b97\u6211\u8d62\u4e86\uff0c\u6211\u81ea\u5df1\u4e5f\u5fc3 \u96be\u81ea\u5b89\u3002\u597d\u8fd9\u4e00\u573a\u5c31\u7b97\u626f\u6210\u5e73\u624b\uff0c\u516c\u4e0d\u516c\u9053\uff1f\u201d\u5085\u53e4\u62c9\u5401\u4e86\u53e3\u6c14\uff0c\u4e0d\u597d\u610f\u601d\u56de\u7b54\uff0c\u963f\u65af\u7f57\u9053 \u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u591a\u8c22\u516c\u4e3b\u3002\u597d\u5427\uff0c\u54b1\u4eec\u518d\u6bd4\u8d5b\u4e0b\u5c71\u3002\u201d\u5085\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u5f97\u51b0\u5ddd\u5929\u5973\u5f53\u4f5c\u548c \u8bba\uff0c\u90fd\u4e0d\u7981\u7cbe\u795e\u4e00\u632f\uff0c\u5728\u5c71\u5cf0\u4e0a\u4e0e\u51b0\u5ddd\u5929\u5973\u5e76\u6392\u7ad9\u597d\uff0c\u5c3c\u6cca\u5c14\u738b\u7684\u5fa1\u524d\u4f8d\u536b\u5728\u5730\u4e0a\u5c04\u51fa\u4e00\u652f \u54cd\u7bad\uff0c\u54cd\u7bad\u5e26\u7740\u4e00\u6e9c\u84dd\u706b\u5347\u7a7a\uff0c\u5c71\u5cf0\u4e0a\u7684\u4e09\u4eba\u7acb\u523b\u53c8\u98de\u9a70\u800c\u4e0b\u3002 \u3000\u3000\u5085\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u4ecd\u4f9d\u524d\u6cd5\uff0c\u4ee5\u4e00\u4eba\u6307\u63d2\u5165\u51b0\u58c1\uff0c\u5b9a\u7740\u8eab\u5f62\uff0c\u7b2c\u4e8c\u4eba\u518d\u501f\u529b\u98de\u817e\uff0c\u4e0d\u8fc7\u6bd4 \u4e0a\u5c71\u4e4b\u65f6\uff0c\u5374\u5feb\u5f97\u591a\uff0c\u4e25\u5982\u4e24\u53ea\u5927\u9e1f\u4fef\u51b2\u98de\u4e0b\uff0c\u6bcf\u4e00\u817e\u8d77\u8dc3\u843d\u5c31\u662f\u5341\u4e08\u6709\u591a\uff01 \u3000\u3000\u4ed6\u4eec\u5feb\u51b0\u5ddd\u5929\u5973\u66f4\u5feb\uff0c\u5979\u987a\u7740\u51b0\u58c1\u6e9c\u4e0b\uff0c\u6beb\u4e0d\u8d39\u529b\uff0c\u5f53\u771f\u5982\u51b0\u6cb3\u5012\u6cfb\uff0c\u98de\u661f\u6025\u9a76\uff0c\u8f6c\u77ac \u4e4b\u95f4\uff0c\u5df2\u5230\u5c71\u8170\u3002\u4ffa\u53e4\u62c9\u6025\u6781\uff0c\u4f7f\u5c3d\u6c14\u529b\u98de\u964d\uff0c\u4f46\u89c1\u4ed6\u5f20\u5f00\u53cc\u81c2\uff0c\u8eab\u4e0a\u7684\u6597\u7bf7\u88ab\u5c71\u98ce\u5439\u5f97\u597d \u50cf\u6da8\u6ee1\u7684\u98ce\u5e06\uff0c\u501f\u7740\u98ce\u529b\uff0c\u4e0b\u201c\u98de\u201d\u66f4\u901f\u3002\u51b0\u5ddd\u5929\u5973\u53cc\u8db3\u4ea4\u9519\u6ed1\u884c\uff0c\u5728\u5979\u9644\u7740\u77f3\u58c1\u7684\u65f6\u95f4\uff0c \u9a76\u8fc7\u4ed6\u7684\u9762\u524d\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\u4e3a\u597d\uff01\u201d\u5085\u53e4\u62c9\u5168\u795e\u8d2f\u6ce8\uff0c\u54ea\u6562\u56de\u7b54\uff0c\u9661\u7136\u95f4\u5c71\u4e0a\u522e\u4e0b \u5927\u98ce\uff0c\u5085\u53e4\u62c9\u4e00\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e58\u98ce\u98de\u817e\u800c\u843d\uff0c\u600e\u4e48\u6837\u4e5f\u6bd4\u4f60\u6ed1\u884c\u8981\u5feb\u5f97\u591a\uff01\u201d\u8fd9\u65f6\u963f\u65af\u7f57 \u5df2\u63a0\u8fc7\u4ed6\u4eec\u9762\u524d\uff0c\u624b\u6307\u521a\u521a\u63d2\u5165\u51b0\u58c1\uff0c\u5085\u53e4\u62c9\u6025\u4e0d\u53ca\u5f85\uff0c\u7528\u529b\u5728\u5730\u80a9\u4e0a\u4e00\u6309\uff0c\u54ea\u77e5\u51b0\u96ea\u7ed9\u98ce \u5439\u5f97\u5265\u843d\uff0c\u5085\u53e4\u62c9\u8fd9\u4e00\u4e0b\u7528\u529b\uff0c\u4e24\u4e2a\u4eba\u90fd\u7acb\u8db3\u4e0d\u7a33\uff0c\u88ab\u98ce\u4e00\u522e\uff0c\u5934\u4e0b\u811a\u4e0a\u7684\u51b2\u4e0b\u6765\uff0c\u8dcc\u5f97\u5934 \u6655\u773c\u82b1\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u6cbe\u7740\u51b0\u58c1\uff0c\u4f46\u51b0\u58c1\u6ed1\u4e0d\u7559\u624b\uff0c\u4ed6\u4eec\u987a\u7740\u51b0\u58c1\u6eda\u4e0b\uff0c\u5931\u4e86\u90a3\u4fef\u51b2\u4e4b\u52bf\uff0c\u624b \u6307\u4f7f\u4e0d\u51fa\u52b2\u6765\uff0c\u773c\u775b\u53c8\u88ab\u98ce\u522e\u5f97\u5f20\u4e0d\u5f00\u6765\uff0c\u4f46\u89c9\u8eab\u4f53\u865a\u865a\u6d6e\u6d6e\uff0c\u597d\u4f3c\u5411\u65e0\u5e95\u7684\u6d77\u6d0b\u98de\u5760\uff0c\u5fc3 \u4e2d\u90fd\u5728\u53eb\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5c31\u6b64\u5b8c\u4e86\uff01\u201d \u3000\u3000\u8c37\u5e95\u7684\u58eb\u5175\u4e0d\u77e5\u5c31\u91cc\uff0c\u53ea\u89c1\u5085\u53e4\u62c9\u4e8c\u4eba\u5728\u51b0\u58c1\u4e0a\u98de\u6eda\u800c\u4e0b\uff0c\u662f\u51b0\u5ddd\u5929\u5973\u7adf\u843d\u540e\u6570\u5341\u4e08\u4e4b \u591a\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4ffa\u53e4\u62c9\u7528\u4ec0\u4e48\u5999\u6cd5\uff0c\u90fd\u5728\u66ff\u51b0\u5ddd\u5929\u5973\u6697\u6697\u53f9\u606f\uff0c\u60cb\u501f\u5979\u8fd9\u4e16\u4e0a\u65e0\u53cc\u7684\u6ed1\u96ea\u529f \u592b\uff0c\u7adf\u4f1a\u8d25\u5728\u5085\u53e4\u62c9\u7b49\u4e8c\u4eba\u624b\u91cc\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5085\u53e4\u62c9\u89e6\u7740\u4e00\u5757\u51f8\u51fa\u6765\u7684\u5927\u51b0\u5757\uff0c\u649e\u5f97\u5934\u7834\u8840\u6d41\uff0c\u767b\u65f6\u6655\u53a5\u3002\u4f46 \u4e5f\u5e78\u800c\u6709\u8fd9\u5757\u51b0\u5757\uff0c\u963b\u6b62\u4e86\u4ed6\uff0c\u8fd9\u624d\u4e0d\u81f3\u4ece\u5343\u4e08\u51b0\u6daf\u5760\u4e0b\uff0c\u9001\u4e86\u6027\u547d\u3002\u963f\u65af\u7f57\u7ed9\u4ed6\u4e00\u963b\uff0c\u624b \u811a\u4e5f\u7ed9\u5c16\u51b0\u5272\u4f24\u3002\u51b0\u5ddd\u5929\u5973\u4e00\u770b\u4e0d\u597d\uff0c\u52a0\u901f\u6ed1\u4e0b\uff0c\u89e3\u4e0b\u8170\u5e26\uff0c\u7f1a\u4f4f\u4e86\u5085\u53e4\u62c9\u7684\u8170\uff0c\u53eb\u963f\u65af\u7f57 \u62c9\u7740\u4e2d\u95f4\uff0c\u5979\u6267\u7740\u8170\u5e26\u7684\u4e00\u5934\uff0c\u5c0f\u5fc3\u8c28\u614e\u7684\u5c06\u4ed6\u4eec\u62d6\u4e0b\u51b0\u58c1\u3002 \u3000\u3000\u8c37\u5e95\u7684\u58eb\u5175\u89e6\u76ee\u60ca\u5fc3\uff0c\u51b0\u5ddd\u5929\u5973\u4e00\u4e0b\u6765\uff0c\u5468\u56f4\u7684\u6b66\u58eb\u4fbf\u7eb7\u7eb7\u6d8c\u4e0a\u53bb\u770b\uff0c\u6025\u5fd9\u65bd\u6551\uff0c\u5e78\u559c \u4e8c\u4eba\u7684\u5185\u529f\u751a\u6709\u6839\uff0c\u5085\u53e4\u62c9\u4f24\u52bf\u8f83\u91cd\uff0c\u5934\u4e0a\u7a7f\u4e86\u4e00\u4e2a\u7a9f\u7abf\uff0c\u7ecf\u8fc7\u88f9\u4f24\u5305\u624e\uff0c\u8840\u4e5f\u6b62\u4e86\u3002\u5c3c\u6cca \u5c14\u738b\u9762\u65e0\u4eba\u8272\uff0c\u5fd9\u53eb\u4eba\u8d3c\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u62ac\u5230\u5e10\u540e\u7597\u6cbb\u3002\u8fd9\u4e8c\u4eba\u8fd8\u80fd\u8bf4\u8bdd\uff0c\u8eba\u5728\u62c5\u67b6\u4e0a\u9891\u9891\u5411 \u51b0\u5ddd\u5929\u5973\u70b9\u9996\u9053\u8c22\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u56de\u5230\u5e2d\u4e0a\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6599\u4e0d\u5230\u6211\u4e00\u65f6\u597d\u80dc\uff0c\u5374\u7d2f\u5f97\u8fd9\u4e8c\u4eba\u8dcc\u4f24\uff01\u201d\u5c3c\u6cca\u5c14\u738b \u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u4ec1\u4eba\u4e4b\u5fc3\uff0c\u5728\u7edd\u9669\u7684\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u6551\u4e86\u8fd9\u4e8c\u4eba\u7684\u6027\u547d\uff0c\u5c0f\u738b\u656c\u4f69\u65e0\u9650\uff01\u201d\u4eb2 \u81ea\u656c\u4e86\u51b0\u5ddd\u5929\u5973\u4e09\u676f\u7f8e\u9152\uff0c\u5fc3\u4e2d\u5374\u4e00\u76f4\u6253\u9f13\uff0c\u81ea\u601d\u81ea\u60f3\u9053\uff1a\u201c\u51b0\u5ddd\u5929\u5973\u8fd9\u6837\u672c\u4e8b\uff0c\u4e07\u4e00\u5979\u80af \u5ac1\u6211\uff0c\u6211\u4e5f\u5236\u670d\u4e0d\u4e86\u5979\uff01\u201d\u5728\u5c3c\u6cca\u5c14\u738b\u7684\u773c\u4e2d\uff0c\u6b64\u65f6\u7684\u51b0\u5ddd\u5929\u5973\u5df2\u4e0d\u6b62\u662f\u4e00\u6735\u6709\u523a\u7684\u73ab\u7470\uff0c \u800c\u662f\u4ed6\u738b\u4f4d\u7684\u514b\u661f\u4e86\u3002\u5c3c\u6cca\u5c14\u738b\u6068\u4e0d\u5f97\u65e9\u65e9\u9001\u8d70\u4e86\u5979\uff0c\u4f46\u4ed6\u4e00\u6765\u5c31\u8bf4\u8fc7\u8981\u9080\u8bf7\u51b0\u5ddd\u5929\u5973\u56de \u56fd\uff0c\u5374\u53c8\u600e\u751f\u63aa\u8f9e\u5c06\u5979\u9001\u8d70\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u8c37\u5916\u6572\u8d77\u5580\u54c6\u7684\u5927\u9f13\uff0c\u4e00\u8fde\u6572\u4e86\u4e09\u5341\u516d\u4e0b\uff0c\u51b0\u5ddd\u5929\u5973\u77e5\u9053\u8fd9\u662f\u5c3c\u6cca\u5c14\u7687\u5ba4\u63a5\u5f85\u6700 \u73cd\u8d35\u7684\u5916\u56fd\u8d35\u5bbe\u7684\u656c\u793c\uff0c\u5fc3\u4e2d\u5927\u8be7\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u662f\u54ea\u4e00\u56fd\u7684\u7687\u5b50\u5230\u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u5c3c\u6cca\u5c14\u738b\u559c\u5f62\u4e8e\u8272\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u5510\u5927\u4fa0\uff0c\u6211\u7ed9\u4f60\u4eec\u5f15\u89c1\u4e00\u4f4d\u5f53\u4e16\u7684\u5f02\u4eba\uff0c\u4ed6\u662f\u4e1c \u6b27\u548c\u963f\u62c9\u4f2f\u8bf8\u56fd\u516c\u63a8\u4e3a\u6700\u6709\u672c\u9886\u7684\u4e00\u4f4d\u9ad8\u4eba\uff0c\u63d0\u6469\u8fbe\u591a\u5927\u6cd5\u5e08\uff01\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u4ee5\u738b\u8005\u4e4b\u793c\u8fce\u63a5\u63d0\u6469\u8fbe\u591a\uff0c\u4f46\u89c1\u524d\u9762\u738b\u65d7\u5f15\u8def\uff0c\u63d0\u6469\u8fbe\u591a\u9a91\u5728\u4e00\u5339\u767d\u8c61\u4e4b\u4e0b\uff0c \u5728\u4f17\u6b66\u58eb\u4e0e\u5f1f\u5b50\u7c07\u62e5\u4e4b\u4e0b\uff0c\u8d70\u8fdb\u5c71\u8c37\u8425\u5730\u3002\u5510\u7ecf\u5929\u5b9a\u775b\u4e00\u770b\uff0c\u4f46\u89c1\u4ed6\u94f6\u53d1\u62ab\u80a9\uff0c\u9762\u8272\u5374\u662f\u975e \u5e38\u7ea2\u6da6\uff0c\u592a\u9633\u7a74\u5fae\u5fae\u9f13\u8d77\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u5185\u529f\u6df1\u6e5b\u7684\u9ad8\u4eba\u3002\u5510\u7ecf\u5929\u5fc3\u9053\uff1a\u201c\u4e45\u95fb\u963f\u62c9\u4f2f\u8bf8\u56fd\u4e5f \u662f\u6587\u660e\u53e4\u56fd\uff0c\u4ed6\u4eec\u7684\u6b66\u672f\u50cf\u4e2d\u534e\u4e00\u6837\uff0c\u4e5f\u662f\u6e90\u8fdc\u6d41\u957f\uff0c\u8fd9\u4e2a\u4eba\u5012\u662f\u4e0d\u53ef\u5c0f\u89d1\u3002\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u89c1\u56fd\u738b\u8fce\u63a5\uff0c\u7565\u4e00\u6b20\u8eab\uff0c\u4fbf\u4e0b\u4e86\u8c61\u80cc\uff0c\u4f17\u4eba\u50cf\u6367\u51e4\u51f0\u4f3c\u7684\uff0c\u966a\u4ed6\u8d70\u5230\u7b75\u5e2d\uff0c\u5c3c \u6cca\u5c14\u738b\u606d\u8bf7\u4ed6\u5750\u5728\u4e0a\u4f4d\uff0c\u81ea\u5df1\u5728\u4e0b\u9996\u76f8\u966a\u3002\u5510\u7ecf\u5929\u6697\u6697\u7559\u5fc3\uff0c\u53ea\u89c1\u4ed6\u8d70\u8fc7\u7684\u5730\u65b9\uff0c\u5730\u4e0a\u7684\u51b0 \u96ea\u7acb\u523b\u878d\u5316\uff0c\u867d\u8bf4\u8c37\u4e2d\u5730\u6c14\u6696\u548c\uff0c\u5730\u4e0a\u7684\u79ef\u96ea\u4e0d\u539a\uff0c\u4f46\u8fd9\u4efd\u529f\u529b\uff0c\u5373\u5728\u4e2d\u56fd\u7684\u6b66\u6797\uff0c\u4e5f\u6ca1\u6709 \u51e0\u4eba\u80fd\u4e0e\u6297\u8861\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u6a2a\u773c\u73af\u626b\u5e2d\u4e0a\u8bf8\u4eba\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u6b64\u6765\u662f\u60f3\u767b\u4e0a\u4e16\u754c\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u521b\u9020\u4eba\u7c7b\u5947 \u8ff9\uff0c\u60f3\u4e0d\u5230\u78b0\u4e0a\u56fd\u738b\u7684\u76db\u5bb4\uff0c\u771f\u662f\u5e78\u4f55\u5982\u4e4b\u3002\u201d\u4ed6\u7684\u8bdd\u81ea\u6709\u4eba\u8bd1\u4e2d\u5c3c\u4e8c\u56fd\u8bed\u8a00\uff0c\u5510\u7ecf\u5929\u542c \u4e86\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e0e\u91d1\u4e16\u9057\u7adf\u662f\u62b1\u7740\u540c\u6837\u7684\u5fc3\u601d\uff01\u201d\u968f\u5373\u53c8\u611a\uff1a\u201c\u559c\u739b\u62c9\u96c5\u5c71 \u662f\u4e2d\u5c3c\u4e24\u56fd\u5171\u6709\u7684\u540d\u5c71\uff0c\u82e5\u7ed9\u4ed6\u6500\u4e0a\u8fd9\u4e16\u754c\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u5c82\u4e0d\u4ee4\u6211\u4eec\u6127\u6b7b\uff1f\u201d\u5fc3\u4e2d\u4e0d\u671f\u7136\u8d77\u4e86 \u4e89\u96c4\u4e4b\u5ff5\u3002\u4f46\u60f3\u5230\u73e0\u5cf0\u4e98\u53e4\u65e0\u4eba\u80fd\u4e0a\uff0c\u63d0\u6469\u8fbe\u591a\u7684\u6b66\u529f\u518d\u9ad8\uff0c\u53ea\u6015\u4e5f\u662f\u4e00\u573a\u5984\u60f3\u800c\u5df2\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u6500\u767b\u73e0\u7a46\u6717\u739b\u5cf0\uff0c\u7a0d\u7f13\u4e00\u4e24\u65e5\uff0c\u5f85\u5929\u6c14\u8f6c\u6696\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u76ee\u4e0b\u5404\u56fd\u6b66\u58eb\u8f83 \u6280\uff0c\u76db\u4f1a\u96be\u9022\uff0c\u6b63\u8981\u8bf7\u5927\u6cd5\u5e08\u6307\u6559\u3002\u201d\u51b0\u5ddd\u5929\u5973\u770b\u4e86\u63d0\u6469\u8fbe\u591a\u4e00\u773c\uff0c\u89c1\u4ed6\u4ef0\u671b\u73e0\u5cf0\uff0c\u6d0b\u6d0b\u81ea \u5f97\uff1b\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u751f\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7ed9\u8fd9\u53ae\u6500\u4e0a\u73e0\u5cf0\uff0c\u5c3c\u6cca\u5c14\u4eba\u4e5f\u5931\u4e86\u9762\u5b50\u3002\u53ef\u7b11\u56fd\u738b\u8fd8\u8fd9\u6837 \u5949\u627f\u4ed6\u3002\u201d\u8fd9\u65f6\u5979\u4e5f\u660e\u767d\u4e86\uff0c\u63d0\u6469\u8fbe\u591a\u4f5c\u5c3c\u6cca\u5c14\u738b\u56fd\u5bbe\u7684\u7406\u7531\uff0c\u539f\u6765\u4ed6\u662f\u60f3\u6500\u767b\u73e0\u5cf0\uff0c\u559c\u9a6c \u62c9\u96c5\u5c71\u4e3b\u6743\u5c5e\u4e8e\u4e2d\u5c3c\u4e24\u56fd\uff0c\u4ed6\u662f\u8981\u53d6\u5f97\u5c3c\u6cca\u5c14\u738b\u7684\u5141\u8bb8\uff0c\u624d\u80fd\u767b\u5c71\u3002\u4e0d\u8fc7\u4e25\u683c\u8bf4\u6765\uff0c\u5c71\u90a3\u8fb9 \u662f\u4e2d\u56fd\u6240\u6709\uff0c\u4ed6\u82e5\u4ece\u5317\u8fb9\u767b\u5c71\uff0c\u6309\u7406\u81f3\u5c11\u8fd8\u5e94\u5f97\u5230\u897f\u85cf\u5f53\u5c40\u7684\u5141\u53ef\uff1b\u8fd9\u65f6\u6e05\u5ef7\u5728\u897f\u85cf\u7684\u5f53 \u5c40\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u4e5f\u96be\u4ee5\u7406\u5230\u8fd9\u4e9b\u4e8b\u60c5\u4e86\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u76ee\u5149\u4e0e\u51b0\u5ddd\u5929\u5973\u4e00\u89e6\uff0c\u500f\u7684\u9762\u8272\u4e00\u53d8\uff0c\u968f\u5373\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u83e9\u8428\uff0c\u5c31\u662f\u8d35 \u56fd\u7684\u516c\u4e3b\u5417\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u6b63\u662f\u524d\u738b\u7684\u516c\u4e3b\uff0c\u6d41\u843d\u4e2d\u56fd\uff0c\u5b64\u738b\u6b64\u6b21\u4fbf\u662f\u8981\u63a5\u5979 \u56de\u6765\u3002\u201d\u63d0\u6469\u8fbe\u591a\u4e00\u5230\uff0c\u4fbf\u542c\u5f97\u81ea\u5df1\u7684\u4e24\u4e2a\u5f92\u5f1f\u4e0e\u51b0\u5ddd\u5929\u5973\u6bd4\u8bd5\u8f7b\u529f\uff0c\u51e0\u4e4e\u8dcc\u81f3\u6454\u6b7b\uff0c\u5fc3\u4e2d \u6b63\u81ea\u4e0d\u5fff\uff0c\u5982\u4eca\u89c1\u5230\u51b0\u5ddd\u5929\u5973\u7edd\u4e16\u5bb9\u989c\uff0c\u800c\u4e14\u9ad8\u8d35\u5e84\u4e25\uff0c\u4ee4\u4eba\u4e0d\u6562\u8feb\u89c6\uff0c\u8154\u4e2d\u7684\u6012\u706b\u600e\u4e48\u4e5f \u53d1\u4f5c\u4e0d\u51fa\u6765\uff0c\u66f4\u517c\u5979\u662f\u534a\u4e2a\u4e3b\u4eba\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u65b9\u4fbf\u5411\u5979\u6311\u6218\uff0c\u8f6c\u8fc7\u76ee\u5149\uff0c\u5bf9\u5510\u7ecf\u5927\u770b\u4e86\u4e00 \u773c\u3002\u5c3c\u6cca\u5c14\u738b\u5fd9\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e2d\u56fd\u6700\u8d1f\u76db\u540d\u7684\u5927\u4fa0\uff0c\u4ee4\u5e08\u4f84\u6cf0\u5409\u63d0\u4fbf\u662f\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u4ed6\u7684 \u6b66\u529f\u795e\u5947\u4e4b\u6781\uff0c\u53ea\u6015\u9664\u4e86\u6cd5\u5e08\u5916\uff0c\u65e0\u4eba\u80fd\u4e0e\u4ed6\u76f8\u6297\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u662f\u6545\u610f\u8981\u6311\u8d77\u63d0\u6469\u8fbe\u591a\u7684\u654c\u5ffe\uff0c\u63d0\u6469\u8fbe\u591a\u542c\u4e86\u901a\u8bd1\u7684\u8bdd\uff0c\u679c\u7136\u4e0d\u6c11\u54fc\u4e86\u58f0\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u4e45\u95fb\u4e2d\u56fd\u6b66\u529f\u7684\u5965\u5999\uff0c\u53ef\u60dc\u65e0\u7f18\u6765\u4e0e\u4e2d\u56fd\u9ad8\u624b\u5207\u78cb\uff0c\u4eca\u65e5\u5f97\u9047\u5510\u5927\u4fa0\uff0c\u90a3\u662f\u5b9a\u8981\u9886 \u6559\u7684\u4e86\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u600e\u6562\u5f53\u5927\u4fa0\u4e4b\u540d\uff0c\u6cd5\u5e08\u82e5\u60f3\u4e0e\u6211\u56fd\u9ad8\u624b\u5207\u78cb\uff0c\u4ea6\u975e\u96be\u4e8b\uff0c\u5728\u4e00\u6708\u4e4b\u5185\uff0c \u6211\u5b9a\u5f53\u5bfb\u5f97\u672c\u9886\u6bd4\u6211\u9ad8\u5341\u500d\u4e4b\u4eba\uff0c\u5411\u5927\u5e08\u9886\u6559\u3002\u201d\u5510\u7ecf\u5927\u77e5\u9053\u81ea\u5df1\u7684\u7236\u6bcd\u5df2\u5230\u6b64\u5730\uff0c\u51af\u7433\u548c \u5415\u56db\u5a18\u4e5f\u4f1a\u5230\u897f\u85cf\u6765\uff0c\u5fc3\u60f3\u968f\u4fbf\u4e00\u4eba\uff0c\u4fbf\u81f3\u5c11\u53ef\u4e0e\u63d0\u6469\u8fbe\u591a\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u542c\u4e86\u901a\u8bd1\u7684\u4f20\u8bdd\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u4ef0\u5929\u8bf4\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u5de5\u592b\u7b49\u4e00\u4e2a\u6708\uff0c\u54b1\u4eec\u53c8\u4e0d \u662f\u5b69\u5b50\u6253\u67b6\uff0c\u8981\u7b49\u5927\u4eba\u6765\u5e2e\u624b\u561b\uff0c\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u8c01\u80dc\u8c01\u8d25\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u5510\u5927\u4fa0\u53ef\u4e0d\u5fc5 \u7740\u5fd9\u8981\u6302\u514d\u6218\u724c\u3002\u5510\u5927\u4fa0\u82e5\u662f\u6015\u8f93\uff0c\u90a3\u4e48\u8ba9\u5728\u5ea7\u6240\u6709\u7684\u4e2d\u56fd\u4eba\u5728\u4e00\u8fb9\uff0c\u533a\u533a\u4e0d\u624d\uff0c\u53ea\u51ed\u8fd9\u53cc \u8089\u638c\uff0c\u613f\u4e0e\u6240\u6709\u4e2d\u56fd\u9ad8\u624b\u8f83\u91cf\u3002\u201d\u542c\u4e86\u8fd9\u8bdd\uff0c\u6ce5\u4eba\u4e5f\u81ea\u6709\u6c14\uff0c\u5510\u8d5b\u82b1\u5fcd\u8010\u4e0d\u4f4f\uff0c\u9053\uff1a\u201c\u7ecf \u5929\uff0c\u4f60\u4e0d\u51fa\u573a\uff0c\u8ba9\u6211\u8fd9\u8001\u592a\u5a46\u5411\u4ed6\u9886\u6559\u3002\u201d\u5510\u7ecf\u5929\u6025\u5fd9\u5c06\u5979\u6309\u4f4f\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5927\u6cd5\u5e08\u65e2\u7136 \u5982\u6b64\u6324\u5151\uff0c\u6211\u867d\u7136\u4e0d\u8db3\u4ee5\u4ee3\u8868\u4e2d\u56fd\u6b66\u58eb\uff0c\u4e5f\u53ea\u597d\u4e0d\u81ea\u91cf\u529b\uff0c\u5411\u4f60\u8ba8\u6559\u4e86\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5802\u5802\u4e2d\u56fd\u5947\u7537\u5b50\uff0c\u5c82\u80af\u4f4e\u5934\u670d\u5916\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u5510\u7ecf\u5929\u4e0e\u63d0\u6469\u8fbe\u591a\u8f83\u6280\uff0c\u80dc\u8d25\u5982\u4f55\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e03\u56de\u3000\u5251\u5f71\u5200\u5149\u3000\u7fa4\u82f1\u901e\u7edd\u6280\u3000\u5e72\u6208\u7389\u5e1b\u3000\u6740\u6c14\u5316\u7965\u4e91 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u4e03\u56de \u5251\u5f71\u5200\u5149\u3000\u7fa4\u82f1\u901e\u7edd\u6280 \u5e72\u6208\u7389\u5e1b\u3000\u6740\u6c14\u5316\u7965\u4e91 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u8fd8\u662f\u723d\u5feb\u4e9b\u597d\uff01\u566b\uff0c\u8fd8\u6709\u90a3\u4f4d\u8981\u4e00\u540c\u4e0a\u5417\uff1f\u7701\u5f97\u6211\u4e00 \u4e2a\u4e00\u4e2a\u7684\u6bd4\u8bd5\u3002\u201d\u5510\u8d5b\u82b1\u8001\u800c\u5f25\u8fa3\uff0c\u542c\u4e86\u901a\u8bd1\u7684\u4f20\u8bdd\uff0c\u201c\u54c8\uff0c\u54c8\uff0c\u54c8\uff01\u201d\u7684\u4e5f\u5927\u7b11\u4e86\u4e09\u58f0\uff0c \u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u8bf4\uff0c\u6211\u5750\u7740\u4e0d\u52a8\uff0c\u4e5f\u8981\u5c06\u4ed6\u6253\u8d25\uff01\u201d\u5510\u7ecf\u5929\u4e00\u542c\uff0c\u4fbf\u77e5\u9053\u5510\u8d5b\u82b1\u53c8\u662f\u60f3\u65bd\u5c55\u5979 \u7684\u6697\u5668\u529f\u592b\uff0c\u4f46\u63d0\u6469\u8fbe\u591a\u5c82\u662f\u90a3\u51e0\u4e2a\u5f13\u7bad\u6559\u5934\u53ef\u6bd4\uff1f\u4ed6\u65e2\u5728\u4e1c\u6b27\u897f\u4e9a\u53f7\u79f0\u7b2c\u4e00\u9ad8\u624b\uff0c\u60f3\u5fc5\u6709 \u6781\u5176\u5389\u5bb3\u7684\u72ec\u95e8\u529f\u592b\uff0c\u5510\u5bd2\u82b1\u5e74\u8fc8\u529b\u8870\uff0c\u7eb5\u7136\u6697\u5668\u7cbe\u7edd\uff0c\u53ea\u6050\u4e5f\u96be\u4e0e\u76f8\u6297\u3002\u5510\u7ecf\u5927\u4e0d\u5f85\u901a\u8bd1 \u4f20\u77e5\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u592a\u592a\u662f\u95f9\u7740\u73a9\u7684\uff0c\u5f53\u7136\u7531\u6211\u6bd4\u8bd5\u3002\u201d\u90a3\u901a\u8bd1\u7684\u8bf4\u4e86\uff0c\u63d0\u6469\u8fbe\u591a\u6bd7 \u7259\u88c2\u5634\u7684\u51b2\u7740\u5510\u8d5b\u82b1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592a\u5927\u4f60\u5750\u7740\u77a7\u597d\u4e86\uff0c\u4f60\u5e74\u7eaa\u5927\u5566\uff0c\u5c31\u662f\u6253\u6211\uff0c\u6211\u4e5f\u4e0d\u80fd \u8fd8\u624b\u3002\u201d\u5510\u5bd2\u82b1\u6700\u6068\u522b\u4eba\u6b3a\u5979\u5e74\u8001\uff0c\u542c\u4e86\u901a\u8bd1\u7684\u4f20\u8bdd\uff0c\u6c14\u5f97\u534a\u6b7b\uff0c\u63d0\u6469\u8fbe\u90f4\u7ecf\u5929\u5df2\u7ecf\u8d70\u5165\u573a \u5fc3\u4e86\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u6c14\u7130\u51cc\u4eba\uff0c\u5510\u7ecf\u5929\u5fc3\u4e2d\u81ea\u662f\u4e0d\u60a6\uff0c\u4f46\u4ecd\u662f\u5f85\u4ed6\u4ee5\u524d\u8f88\u4e4b\u793c\uff0c\u62f1\u624b\u8bf4\u9053\uff1a \u201c\u8bf7\uff01\u201d\u63d0\u6469\u8fbe\u591a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8170\u95f4\u60ac\u7740\u5b9d\u5251\uff0c\u6211\u5c31\u8ba9\u4f60\u5148\u523a\u4e09\u62db\uff01\u201d\u5510\u7ecf\u5929\u53c8\u6012\u53c8\u60ca\uff0c \u5fc3\u9053\uff1a\u201c\u8fd9\u53ae\u597d\u773c\u529b\uff0c\u5251\u672a\u51fa\u9798\uff0c\u4ed6\u5c45\u7136\u770b\u51fa\u6211\u7684\u6e38\u9f99\u5251\u4e43\u662f\u5b9d\u7269\u3002\u201d\u5510\u7ecf\u5929\u5982\u4f55\u80af\u5360\u8fd9\u4e2a \u201c\u4fbf\u5b9c\u201d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e2d\u56fd\u6b66\u58eb\u4ece\u4e0d\u6b3a\u8d1f\u624b\u65e0\u5bf8\u94c1\u4e4b\u4eba\uff0c\u4f60\u4eae\u51fa\u5175\u5668\u6765\uff0c\u6211\u8ba9\u4f60\u5148\u8fdb\u4e09 \u62db\uff01\u201d\u63d0\u6469\u8fbe\u591a\u53cc\u638c\u4e00\u62cd\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u591a\u5e74\u4e0d\u7528\u5175\u5668\u5bf9\u654c\uff0c\u65e9\u5df2\u5fd8\u6389\u5175\u5668\u662f\u600e\u4e48\u7528\u7684 \u5566\uff1a\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u8f83\u91cf\u8f83\u91cf\u62f3\u811a\u4e0a\u7684\u529f\u592b\u3002\u201d\u6c5f\u5357\u6025\u5fd9\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5510 \u5927\u4fa0\u4e0d\u8981\u4e0a\u4ed6\u7684\u5f53\uff0c\u6709\u5b9d\u5251\u4e3a\u4f55\u4e0d\u7528\uff1f\u201d\u8981\u77e5\u5510\u7ecf\u5929\u7684\u5b9d\u5251\u795e\u8292\uff0c\u4e43\u662f\u514b\u654c\u81f4\u80dc\u7684\u4e24\u5927\u201c\u6cd5 \u5b9d\u201d\uff0c\u53ea\u8d5b\u62f3\u811a\uff0c\u90a3\u5c31\u662f\u820d\u957f\u7528\u77ed\u4e86\u3002\u6309\u4e2d\u56fd\u6b66\u6797\u7684\u89c4\u77e9\uff0c\u5404\u4eba\u6709\u5404\u4eba\u7684\u7edd\u6280\uff0c\u6709\u7684\u7cbe\u4e8e\u5251 \u6cd5\uff0c\u6709\u7684\u96c4\u4e8e\u638c\u529b\uff0c\u4ee5\u5251\u5bf9\u638c\uff0c\u4e5f\u5e76\u4e0d\u662f\u4ec0\u4e48\u6709\u5931\u9762\u5b50\u7684\u4e8b\u60c5\u3002\u4f46\u7ecf\u591a\u5634\u7684\u6c5f\u5357\u8fd9\u6837\u4e00\u56b7\uff0c \u5c3c\u6cca\u5c14\u6b66\u58eb\u4eec\u90fd\u6ce8\u610f\u5510\u7ecf\u5929\u8170\u95f4\u9690\u9690\u900f\u51fa\u5149\u8292\u7684\u5b9d\u5251\u3002\u901a\u8bd1\u7684\u53c8\u6545\u610f\u5c06\u6c5f\u5357\u7684\u8bdd\u4f20\u8bd1\u51fa\u6765\uff0c \u63d0\u6469\u8fbe\u591a\u66f4\u662f\u6d0b\u6d0b\u5f97\u610f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6709\u5b9d\u5251\u4e3a\u4f55\u4e0d\u7528\uff1f\u8981\u4e0d\u7136\u4f60\u8f93\u4e86\u4e5f\u4e0d\u5fc3\u670d\uff01\u201d \u3000\u3000\u5904\u6b64\u60c5\u5f62\uff0c\u5510\u7ecf\u5929\u66f4\u4e0d\u597d\u81ea\u98df\u524d\u8a00\uff0c\u5f03\u638c\u7528\u5251\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u505a\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u8a00\uff0c\u8bf7 \u5148\u8d50\u62db\uff01\u6211\u82e5\u8f93\u4e86\uff0c\u81ea\u7136\u7518\u62dc\u4e0b\u98ce\uff01\u201d\u63d0\u6469\u8fbe\u591a\u5fc3\u4e2d\u4e5f\u4f69\u670d\u5510\u7ecf\u5929\u7684\u5014\u5f3a\uff0c\u77e5\u4ed6\u4e0d\u80af\u5148\u884c\u52a8 \u624b\uff0c\u4fbf\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u7ad9\u7a33\u4e86\uff01\u201d\u8ddd\u79bb\u4e09\u4e08\u4e4b\u5916\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u4f0f\u8eab\u4f5c\u52bf\uff0c\u4fbf\u82e5\u65e0\u5176\u4e8b\u4f3c\u7684\uff0c\u8f7b \u98d8\u98d8\u7684\u62cd\u51fa\u4e00\u638c\uff0c\u5510\u7ecf\u5929\u5c1a\u672a\u7559\u795e\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u4e00\u80a1\u6781\u5927\u7684\u6f5c\u529b\u6392\u5c71\u5012\u6d77\u800c\u81f3\uff0c\u6025\u5fd9\u65bd\u5c55 \u201c\u5343\u65a4\u5760\u201d\u7684\u529f\u592b\uff0c\u53cc\u811a\u7262\u7262\u9489\u5728\u5730\u4e0a\uff0c\u4e0a\u8eab\u5df2\u662f\u6643\u4e86\u4e24\u6643\uff0c\u63d0\u6469\u8fbe\u591a\u89c1\u4e00\u638c\u63a8\u4ed6\u4e0d\u52a8\uff0c\u5fae \u5fae\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u53f3\u638c\u6536\u56de\uff0c\u5de6\u624b\u8f7b\u8f7b\u4e00\u62db\uff0c\u5510\u7ecf\u5929\u53ea\u89c9\u9661\u7136\u95f4\u53c8\u6709\u4e00\u80a1\u76f8\u53cd\u7684\u6f5c\u529b\uff0c\u5c06\u4ed6 \u7275\u5f15\uff01 \u3000\u3000\u4e24\u80a1\u529b\u91cf\uff0c\u76f8\u63a8\u76f8\u5f15\uff0c\u5510\u7ecf\u5929\u518d\u4e5f\u7ad9\u7acb\u4e0d\u7a33\uff0c\u6025\u5fd9\u8d81\u52bf\u4e00\u8dc3\u800c\u8d77\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u51cc\u7a7a\u75be \u51fb\uff0c\u4e00\u7167\u9762\u4fbf\u7528\u5929\u5c71\u638c\u6cd5\u4e2d\u7684\u8ffd\u98ce\u638c\u5f0f\u201c\u6392\u4e91\u9a76\u7535\u201d\uff0c\u7acb\u4e0b\u6740\u624b\u3002\u5c3c\u6cca\u5c14\u6b66\u58eb\u4eec\u4e0d\u77e5\u5c31\u91cc\uff0c \u89c1\u5510\u7ecf\u5929\u8eab\u6cd5\u4fca\u7f8e\uff0c\u638c\u6cd5\u51cc\u5389\uff0c\u90fd\u559d\u8d77\u5f69\u6765\u3002\u5c82\u77e5\u5510\u7ecf\u5929\u662f\u88ab\u8feb\u5982\u6b64\uff0c\u5b9e\u5728\u5df2\u88ab\u654c\u4eba\u5360\u4e86\u4e3b \u52a8\u3002\u53ea\u662f\u63d0\u6469\u8fbe\u591a\u5728\u559d\u5f69\u58f0\u4e2d\uff0c\u53cc\u638c\u9f50\u626c\uff0c\u5510\u7ecf\u5929\u5728\u534a\u7a7a\u4e2d\u8fde\u7ffb\u4e24\u4e2a\u7b4b\u6597\uff0c\u659c\u98de\u51fa\u4e09\u4e08\u4e4b \u5916\uff0c\u843d\u5728\u5730\u4e0a\u3002\u5c3c\u6cca\u5c14\u7684\u6570\u4e07\u5927\u519b\uff0c\u89c1\u4e24\u4eba\u624b\u6307\u90fd\u672a\u6cbe\u5230\uff0c\u4fbf\u7acb\u5373\u5206\u5f00\uff0c\u90fd\u662f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u89c1\u53cc\u638c\u9f50\u51fa\uff0c\u4ecd\u662f\u672a\u80fd\u5c06\u5510\u7ecf\u5929\u51fb\u5012\uff0c\u5fc3\u4e2d\u6697\u6697\u79f0\u5947\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5c31\u7b97\u5728 \u5a18\u80ce\u91cc\u4fbf\u5b66\u6b66\u529f\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u591a\u5e74\u529f\u529b\uff0c\u5c45\u7136\u80fd\u6321\u5f97\u6211\u7684\u9634\u9633\u638c\u529b\uff01\u770b\u6765\u4e2d\u56fd\u6b66\u529f\u7684\u5965 \u5999\uff0c\u786e\u662f\u540d\u4e0d\u865a\u4f20\uff01\u201d\u5fc3\u4e2d\u4e00\u51db\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u8d81\u7740\u5510\u7ecf\u5929\u5598\u606f\u672a\u5b9a\uff0c\u75be\u884c\u6251\u4e0a\uff0c\u5de6\u4e00\u638c\u53f3\u4e00 \u638c\uff0c\u6709\u5982\u72c2\u98ce\u9aa4\u96e8\uff0c\u6253\u5f97\u5510\u7ecf\u5929\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\uff01 \u3000\u3000\u5510\u7ecf\u5929\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u7528\u8ffd\u98ce\u638c\u6cd5\u5bf9\u4ed8\uff0c\u653b\u4e2d\u5e26\u5b88\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\uff0c\u4e0d\u8fc7\u4e00\u4f1a\u5b50\u529f \u592b\uff0c\u4f46\u89c9\u654c\u4eba\u7684\u4e24\u80a1\u638c\u529b\uff0c\u5de6\u53f3\u7275\u5f15\uff0c\u8d8a\u6765\u8d8a\u89c1\u5389\u5bb3\uff0c\u987f\u7136\u95f4\u597d\u50cf\u8eab\u5904\u5728\u4e00\u4e2a\u6781\u5927\u7684\u6f29\u6da1\u4e2d \u5fc3\uff0c\u8fdb\u65e2\u4e0d\u80fd\uff0c\u9000\u4ea6\u4e0d\u5f97\uff01\u539f\u6765\u63d0\u6469\u8fbe\u591a\u7528\u7684\u4e43\u662f\u201c\u9634\u9633\u4e94\u884c\u638c\u529b\u201d\uff0c\u662f\u89c2\u5bdf\u5929\u4f53\u661f\u8fb0\u7684\u8fd0 \u884c\u6cd5\u5219\uff0c\u4ece\u201c\u4e07\u6709\u5f15\u529b\u201d\u4e2d\u6240\u53c2\u609f\u51fa\u6765\u7684\u4e00\u95e8\u5947\u529f\u3002\u8981\u77e5\u7528\u4efb\u4f55\u4e00\u79cd\u529b\u91cf\u6253\u51fb\u5bf9\u65b9\uff0c\u6709\u6b63\u4f5c \u7528\u5fc5\u6709\u53cd\u4f5c\u7528\uff0c\u63d0\u6469\u8fbe\u591a\u7ec3\u5230\u4e24\u80a1\u638c\u529b\u4e92\u76f8\u6fc0\u649e\uff0c\u518d\u4e0e\u654c\u4eba\u6240\u53d1\u7684\u529b\u91cf\u6c47\u5408\uff0c\u654c\u4eba\u7684\u529b\u91cf\u5c31 \u53cd\u800c\u4e3a\u6211\u6240\u7528\uff0c\u548c\u51e0\u80a1\u6d6a\u6f6e\u76f8\u78b0\u4e4b\u65f6\uff0c\u5377\u8d77\u6f29\u6da1\u7684\u9053\u7406\uff0c\u6b63\u590d\u76f8\u540c\u3002 \u3000\u3000\u5510\u7ecf\u5929\u867d\u7136\u4e0d\u8bc6\u8fd9\u79cd\u5965\u5999\u7684\u5947\u529f\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e00\u4ee3\u5b97\u5e08\u7684\u5ae1\u7cfb\u4f20\u4eba\uff0c\u4e00\u89c9\u8eab\u5b50\u4f3c\u6295\u5165\u6f29 \u6da1\u7684\u4e2d\u5fc3\uff0c\u4e0d\u4e45\u4fbf\u609f\u5230\u5185\u529b\u6fc0\u649e\u7684\u6d88\u957f\u4e4b\u7406\uff0c\u5f53\u4e0b\u7acb\u5373\u51dd\u795e\u8fd0\u6c14\uff0c\u62b1\u5143\u5b88\u4e00\uff0c\u5140\u7acb\u5728\u6f29\u6da1\u7684 \u4e2d\u5fc3\uff0c\u65bd\u5c55\u51fa\u5927\u5c71\u638c\u6cd5\u4e2d\u6700\u7cbe\u5999\u7684\u201c\u987b\u5f25\u638c\u6cd5\u201d\u3002\u987b\u5f25\u638c\u6cd5\u662f\u5929\u4e0b\u7b2c\u4e00\u7b49\u7684\u9632\u8eab\u529f\u592b\uff0c\u5168\u7528 \u9634\u67d4\u4e4b\u529b\uff0c\u968f\u52bf\u5c48\u4f38\uff0c\u6d88\u89e3\u654c\u4eba\u653b\u6765\u7684\u52b2\u9053\u3002\u4e0d\u8fc7\u63d0\u6469\u8fbe\u591a\u7684\u638c\u529b\u5e76\u975e\u76f4\u63a5\u6253\u5230\u5510\u7ecf\u5929\u7684\u8eab \u4e0a\uff0c\u4ed6\u7684\u4e24\u80a1\u638c\u529b\u6210\u4e3a\u5706\u5708\u5f62\u7684\u7275\u5f15\uff0c\u5510\u7ecf\u5929\u867d\u7136\u5c3d\u529b\u5316\u89e3\uff0c\u4ecd\u7136\u662f\u8eab\u4e0d\u7531\u5df1\u7684\u8ddf\u7740\u4ed6\u7684\u638c \u529b\u76f4\u6253\u5708\u5708\u3002\u4e0d\u8fc7\u6bd4\u8d77\u521d\u9047\u8fd9\u79cd\u638c\u529b\u4e4b\u65f6\u7684\u72fc\u72c8\uff0c\u90a3\u662f\u5e94\u4ed8\u6709\u65b9\u4e86\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u6b66\u58eb\u4eec\u4e0d\u660e\u5176\u7406\uff0c\u4f46\u89c1\u5510\u7ecf\u5929\u4e0d\u4f4f\u7684\u7ed5\u7740\u63d0\u6469\u8fbe\u591a\u75be\u8d70\uff0c\u63d0\u6469\u8fbe\u591a\u5219\u6709\u65f6\u8fc8\u524d\u4e00 \u6b65\uff0c\u6709\u65f6\u9000\u540e\u4e00\u6b65\uff0c\u603b\u662f\u5c06\u81ea\u5df1\u4fdd\u6301\u5728\u5510\u7ecf\u5929\u6240\u7ed5\u5708\u5b50\u7684\u4e2d\u5fc3\uff0c\u540c\u65f6\u4e0d\u505c\u7684\u5c06\u4e24\u624b\u63c9\u6413\uff0c\u5747 \u662f\u5927\u611f\u8be7\u5f02\uff0c\u4e0d\u77e5\u8005\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u662f\u5f04\u4ec0\u4e48\u628a\u620f\u3002\u5510\u8d5b\u82b1\u53ef\u662f\u89e6\u76ee\u60ca\u5fc3\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u8d8a\u8f6c\u8d8a \u75be\uff0c\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u5fc3\u4e2d\u6697\u53eb\u4e0d\u5999\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u957f\u8896\u4e00\u6325\uff0c\u6697\u4e2d\u53d1\u51fa\u51e0\u679a\u4e09\u68f1\u900f\u9aa8 \u9489\uff0c\u5206\u6253\u63d0\u6469\u8fbe\u591a\u4e0a\u4e2d\u4e0b\u4e09\u5904\u6b7b\u7a74\uff01 \u3000\u3000\u5510\u8d5b\u82b1\u53d1\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u8fd9\u4e00\u4e0b\u8896\u5e95\u98de\u9489\u3001\u6beb\u65e0\u58f0\u606f\uff0c\u4f17\u4eba\u53c8\u6b63\u5728\u770b\u5f97\u773c\u82b1\u7f00 \u4e71\uff0c\u8c01\u4e5f\u6ca1\u6709\u7559\u610f\u5979\u3002\u5510\u8d5b\u82b1\u6b63\u81ea\u5f97\u610f\uff0c\u5ffd\u542c\u5f97\u53ee\u53ee\u53ee\u51e0\u58f0\u8fde\u54cd\uff0c\u6709\u5982\u94f6\u74f6\u4e4d\u88c2\uff0c\u91d1\u94c1\u4ea4 \u9e23\u3002\u5510\u8d5b\u82b1\u5403\u4e86\u4e00\u60ca\uff0c\u7acb\u523b\u6697\u547c\u4e0d\u5999\u3002\u63d0\u6469\u8fbe\u591a\u624b\u4e0a\u6ca1\u6709\u5175\u5668\uff0c\u8eab\u4e0a\u6ca1\u6709\u7532\u80c4\uff0c\u5510\u8d5b\u82b1\u6240\u53d1 \u7684\u6697\u5668\u540d\u53eb\u201c\u900f\u9aa8\u9489\u201d\uff0c\u4e00\u6cbe\u4eba\u4f53\uff0c\u7acb\u53ef\u900f\u9aa8\u800c\u5165\uff0c\u4ed6\u8eab\u4e0a\u65e2\u65e0\u7532\u80c4\u963b\u9694\uff0c\u600e\u4f1a\u53d1\u51fa\u8fd9\u79cd\u53ee \u53ee\u53ee\u4e4b\u58f0\uff1f \u3000\u3000\u53ea\u89c1\u5510\u7ecf\u5929\u4eba\u9640\u87ba\u822c\u5730\u75be\u8f6c\u4e00\u5708\uff0c\u8eab\u5f62\u5ffd\u7136\u505c\u6ede\u4e0b\u6765\u3002\u63d0\u6469\u8fbe\u591a\u7eb5\u58f0\u5927\u7b11\uff0c\u539f\u6765\u90a3\u51e0\u679a \u900f\u9aa8\u9489\u90fd\u7ed9\u4ed6\u7528\u638c\u529b\u786c\u8feb\u5230\u5510\u7ecf\u5929\u8eab\u4e0a\u3002\u63d0\u6469\u8fbe\u591a\u6b63\u60f3\u51fa\u8bed\u51b7\u5632\uff0c\u5ffd\u89c1\u706b\u661f\u70b9\u70b9\uff0c\u4ece\u5510\u7ecf\u5929 \u8eab\u4e0a\u6e85\u8d77\uff0c\u90a3\u51e0\u679a\u900f\u9aa8\u9489\u7ed9\u9707\u5230\u534a\u7a7a\uff0c\u9664\u4e86\u662f\u4ed6\uff0c\u5bfb\u5e38\u8089\u773c\uff0c\u5df2\u662f\u4e0d\u80fd\u770b\u89c1\u3002\u63d0\u6469\u8fbe\u591a\u8fd9\u4e00 \u60ca\u4e0d\u5728\u5510\u8d5b\u82b1\u4e4b\u4e0b\uff0c\u8981\u77e5\u8fd9\u51e0\u679a\u900f\u9aa8\u9489\u950b\u5229\u975e\u5e38\uff0c\u7ecf\u4ed6\u7684\u638c\u529b\u4e00\u8feb\uff0c\u90a3\u5c31\u7b49\u4e8e\u4ece\u67aa\u53e3\u6240\u53d1\u51fa \u7684\u94c5\u5f39\u4e00\u6837\uff0c\u5373\u7b97\u8eab\u4e0a\u62ab\u7740\u91cd\u7532\uff0c\u4e5f\u96be\u62b5\u5fa1\uff0c\u7136\u800c\u7adf\u7136\u5c04\u4e0d\u8fdb\u5510\u7ecf\u5929\u7684\u8eab\u4f53\uff01 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\u5510\u7ecf\u5929\u8eab\u4e0a\u62ab\u7740\u4e00\u4ef6\u5f02\u5b9d\uff0c\u90a3\u662f\u6614\u5e74\u949f\u4e07\u5802\u9001\u7ed9\u4ed6\u6bcd\u4eb2\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u4e0d\u8981\u8bf4 \u51e0\u679a\u900f\u9aa8\u9489\uff0c\u5373\u7b97\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\u4e5f\u523a\u4e0d\u8fdb\u53bb\u3002\u4e0d\u8fc7\u56e0\u4e3a\u63d0\u6469\u8fbe\u591a\u7684\u5185\u529b\u5927\u731b\uff0c\u6240\u4ee5\u4ed6\u624d\u4f3c \u7a81\u7136\u7ed9\u4eba\u63a8\u4e86\u4e00\u628a\u4f3c\u7684\uff0c\u8f6c\u4e2a\u4e0d\u4f11\uff0c\u597d\u4e0d\u5bb9\u6613\u7528\u201c\u5343\u65a4\u5760\u201d\u7684\u529f\u592b\uff0c\u624d\u80fd\u628a\u8eab\u5f62\u5b9a\u4f4f\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5927\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u5077\u7528\u6697\u5668\uff0c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4f60\u4e5f\u63a5\u4e00\u63a5\u6211\u8fd9\u5929\u5c71\u795e\u8292\u3002\u201d \u970e\u7136\u95f4\u4e24\u9053\u4e4c\u91d1\u5149\u8292\u7535\u5c04\u800c\u81f3\u3002\u63d0\u6469\u8fbe\u591a\u957f\u8896\u4e00\u6325\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u4e24\u58f0\u3002\u90a3\u4e24\u652f\u5929\u5c71\u795e\u8292\u867d\u7136 \u7ed9\u4ed6\u62c2\u843d\u5730\u4e0a\uff0c\u4f46\u4ed6\u7684\u8863\u8896\u4e5f\u88ab\u5c04\u7a7f\u4e86\u4e24\u4e2a\u5c0f\u5b54\u3002\u63d0\u6469\u8fbe\u591a\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u4e16\u95ee\u6709\u8fd9\u79cd\u5f3a\u52b2 \u5a01\u731b\u7684\u6697\u5668\uff0c\u5fc3\u5934\u4e5f\u4e0d\u7981\u5fae\u5fae\u4e00\u9707\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5510\u7ecf\u5929\u53c8\u63a5\u7eed\u53d1\u51fa\u4e24\u652f\uff0c\u63d0\u6469\u8fbe\u591a\u4e0d \u6562\u6020\u6162\uff0c\u51dd\u795e\u8fd0\u638c\uff0c\u5c06\u4e24\u652f\u5929\u5c71\u795e\u8292\u5728\u79bb\u8eab\u4e08\u8bb8\u4e4b\u5730\u5288\u843d\u3002\u8fd9\u65f6\u901a\u8bd1\u624d\u6765\u5f97\u53ca\u5c06\u5510\u7ecf\u5929\u9002\u624d \u6240\u9a82\u7684\u8bf4\u8bdd\u4f20\u8bd1\u8fc7\u6765\u3002\u63d0\u6469\u8fbe\u591a\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u7684\u4eba\u5077\u65bd\u6697\u7b97\uff0c\u5374\u8d56\u5728 \u6211\u7684\u8eab\u4e0a\uff0c\u54fc\uff0c\u54fc\uff0c\u7b97\u54ea\u95e8\u5b50\u7684\u597d\u6c49\uff01\u8d4c\uff01\u5c31\u662f\u2014\u2014\u201d\u5ffd\u5730\u60f3\u8d77\u81ea\u5df1\u9002\u624d\u8bf4\u8bdd\u592a\u6ee1\uff0c\u8bf4\u8fc7\u53ea \u51ed\u4e00\u53cc\u8089\u638c\u4fbf\u53ef\u4e0e\u6240\u6709\u7684\u6c49\u4eba\u5468\u65cb\uff0c\u90a3\u53c8\u600e\u602a\u5f97\u65c1\u4eba\u51fa\u624b\u76f8\u52a9\uff1f\u4f55\u51b5\u53d1\u6697\u5668\u7684\u53c8\u662f\u4ed6\u6240\u8ba5\u7b11 \u8fc7\u7684\u201c\u8001\u592a\u5a46\u201d\uff1f\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u96be\u9053\u8fd8\u8981\u4e0e\u4e00\u4e2a\u8001\u592a\u5a46\u9a82\u6218\uff1f\u6240\u4ee5\u4ed6\u672c\u6765\u60f3\u6307\u51fa\u5510\u8d5b\u82b1\uff0c\u8bdd \u5230\u53e3\u8fb9\uff0c\u5374\u53c8\u5fcd\u7740\u3002\u5c3c\u6cca\u5c14\u6b66\u58eb\u542c\u4e86\u901a\u8bd1\u7684\u4f20\u8bdd\uff0c\u5fc3\u4e2d\u90fd\u5728\u60f3\u9053\uff1a\u201c\u660e\u660e\u662f\u4f60\u7528\u6697\u5668\u5148\u6253\u4eba \u5bb6\uff0c\u82e5\u7136\u662f\u4e2d\u56fd\u4eba\u53d1\u7684\uff0c\u600e\u4e48\u4f1a\u6253\u5230\u4ed6\u4eec\u540c\u4f34\u7684\u8eab\u4e0a\uff1f\u201d\u5bf9\u63d0\u6469\u8fbe\u591a\u7684\u8bdd\u53cd\u800c\u4e0d\u4fe1\uff0c\u5618\u58f0\u56db \u8d77\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5510\u7ecf\u5929\u53c8\u63a5\u7eed\u53d1\u4e86\u51fa\u4e24\u652f\u5929\u5c71\u795e\u8292\uff0c\u63d0\u6469\u8fbe\u591a\u4e00\u52a8\u4e86\u6c14\uff0c\u771f\u529b\u7a0d\u51cf\uff0c \u4e24\u652f\u795e\u8292\u76f4\u5230\u79bb\u8eab\u4e09\u5c3a\u4e4b\u5730\uff0c\u624d\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u843d\uff0c\u8981\u662f\u638c\u529b\u518d\u5f31\u4e00\u4e9b\uff0c\u53ea\u6015\u5c31\u8981\u7ed9\u795e\u8292\u900f\u5fc3 \u7a7f\u8fc7\uff01\u63d0\u6469\u8fbe\u591a\u5fc3\u4e2d\u4e00\u51db\uff0c\u6b63\u5728\u51dd\u795e\u8fd0\u6c14\uff0c\u5ffd\u89c9\u81c2\u4e0a\u7684\u7a74\u9053\u4e00\u9635\u9178\u9ebb\uff0c\u968f\u5373\u542c\u5230\u5973\u5b50\u5403\u5403\u7684 \u7b11\u58f0\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u5761\u4e0a\u7684\u51b0\u5ca9\u8f6c\u80f8\u4e4b\u5904\uff0c\u7a81\u7136\u95ea\u51fa\u4e24\u4e2a\u5973\u5b50\uff0c\u4e00\u4e2a\u662f\u4e2d\u5e74\u5987\u4eba\uff0c\u4e00\u4e2a\u662f\u5982\u82b1\u4f3c\u7389\u7684 \u5c11\u5973\uff0c\u770b\u60c5\u5f62\u662f\u4e24\u6bcd\u5973\uff0c\u5374\u662f\u4e00\u822c\u6253\u626e\uff0c\u5934\u4e0a\u7ed3\u7740\u4e24\u4e2a\u8774\u8776\u7ed3\uff0c\u663e\u51fa\u4e00\u526f\u6dd8\u6c14\u7684\u795e\u60c5\u3002\u5510\u7ecf \u5927\u5927\u559c\u53eb\u9053\uff1a\u201c\u59e8\u5988\uff01\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u8eab\u5f62\u4e00\u8d77\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u98d8\u7136\u843d\u5730\uff0c\u8fd9\u7b49\u8f7b\u529f\u6bd4\u521a \u624d\u7684\u5085\u53e4\u62c9\u963f\u65af\u7f57\u7b49\u4eba\uff0c\u53c8\u4e0d\u77e5\u9ad8\u660e\u4e86\u51e0\u500d\uff0c\u5c71\u8c37\u4e2d\u7684\u51e0\u4e07\u5927\u519b\u4e0d\u7981\u53d1\u51fa\u5982\u96f7\u91c7\u58f0\uff01 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u4fef\u9996\u4e00\u770b\uff0c\u53ea\u89c1\u81c2\u4e0a\u6cbe\u7740\u4e00\u7247\u65b0\u7eff\u7684\u6811\u53f6\uff0c\u4e00\u62ac\u5934\u4f46\u89c1\u51af\u7433\u5bf9\u7740\u4ed6\u563b\u563b\u5730\u7b11\u3002 \u8fd9\u7247\u6811\u53f6\u6b63\u662f\u51af\u7433\u7528\u201c\u98de\u82b1\u6458\u53f6\u201d\u7684\u6700\u4e0a\u4e58\u7684\u5185\u529f\u53d1\u51fa\u6765\u7684\uff01\u672c\u6765\u63d0\u6469\u8fbe\u591a\u7684\u5185\u529f\u4e0e\u51af\u7433\u4e0d \u76f8\u4e0a\u4e0b\uff0c\u53a2\u4ed6\u5168\u795e\u5bf9\u4ed8\u5929\u5c71\u795e\u8292\uff0c\u6545\u6b64\u7adf\u7ed9\u51af\u7433\u7684\u4e00\u7247\u6811\u53f6\uff0c\u5c06\u4ed6\u7684\u81c2\u818a\u6253\u5f97\u9690\u9690\u53d1\u9ebb\uff01\u4e5f \u5e78\u4e8f\u51af\u7433\u53ca\u65f6\u51fa\u624b\uff0c\u8981\u4e0d\u7136\u4ed6\u7684\u638c\u529b\u4e00\u53d1\uff0c\u5510\u7ecf\u5929\u5c31\u8981\u91cd\u9677\u6f29\u6da1\uff0c\u867d\u6709\u5929\u5c71\u795e\u8292\uff0c\u4e5f\u65e0\u4f59\u529b \u53d1\u51fa\u4e86\u3002 \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u7ecf\u5929\uff0c\u91d1\u4e16\u9057\u5462\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u55ef\uff0c\u8fd8\u672a\u89c1\u5230\uff0c\u770b\u8ff9\u8c61\u53ef\u80fd\u4e5f\u5230\u8fd9\u513f\u6765 \u4e86\u3002\u201d\u51af\u7433\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u548c\u8868\u59b9\u8bf4\u53bb\uff0c\u6211\u6765\u5bf9\u4ed8\u8fd9\u4e2a\u756a\u50e7\u3002\u201d\u4e00\u62db\u624b\u53eb\u901a\u8bd1\u8fc7 \u6765\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u6700\u559c\u6b22\u770b\u4eba\u800d\u628a\u620f\uff0c\u6211\u77a7\u8fd9\u4f4d\u5927\u6cd5\u5e08\u6413\u624b\u8f6c\u5708\uff0c\u602a\u6709\u8da3\u7684\uff0c\u4f60\u5bf9\u4ed6\u8bf4\uff0c \u6211\u60f3\u9017\u4ed6\u73a9\u73a9\u3002\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u51e0\u66fe\u7ed9\u4eba\u8fd9\u6837\u5632\u5f04\u8fc7\uff0c\u4f46\u4ed6\u89c1\u4e86\u51af\u7433\u7684\u6b66\u529f\uff0c\u786e\u662f\u4e0d\u5bb9\u5c0f\u89c6\uff0c\u9ad8\u624b\u6bd4\u62fc\uff0c\u54ea\u6562 \u52a8\u6c14\uff1f\u53ea\u597d\u5f3a\u6291\u6012\u706b\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eca\u65e5\u5c31\u518d\u4f1a\u4e00\u4f1a\u4e2d\u56fd\u7684\u5973\u82f1\u96c4\uff0c\u53eb\u5979\u4eae\u51fa\u5175\u5668 \u6765\uff01\u201d\u51af\u3002\u7433\u542c\u4e86\u901a\u8bd1\u7684\u8bdd\uff0c\u7b11\u563b\u563b\u7684\u89e3\u4e0b\u5934\u4e0a\u7684\u4e00\u4e2a\u8774\u8776\u7ed3\uff0c\u628a\u7f20\u7740\u8774\u8776\u7ed3\u7684\u5f69\u8272\u5934\u7ef3\u4e00 \u6296\uff0c\u7b11\u9053\uff1a\u201c\u6211\u65e2\u4e0d\u662f\u5973\u82f1\u96c4\uff0c\u4e5f\u4e0d\u4f1a\u62ff\u5200\u5f04\u5251\uff0c\u6211\u6700\u62ff\u624b\u7684\u5c31\u662f\u7528\u7ef3\u5b50\u7f1a\u7334\u513f\uff0c\u597d\u5440\uff0c\u4f60 \u5bf9\u4ed6\u8bf4\u53bb\uff01\u201d \u3000\u3000\u901a\u8bd1\u7684\u6d3b\u672a\u8bf4\u5b8c\uff0c\u4f46\u542c\u5f97\u63d0\u6469\u8fbe\u591a\u4e00\u58f0\u6012\u543c\uff0c\u53cc\u638c\u4e00\u62cd\uff0c\u72c2\u8d31\u9aa4\u8d77\uff0c\u51af\u7433\u8eab\u4f3c\u82b1\u679d\u4e71 \u98a4\uff0c\u5728\u98ce\u4e2d\u6447\u6447\u6643\u6643\u3002\u5510\u8d5b\u82b1\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u674e\u6c81\u6885\u7b11\u9053\uff1a\u201c\u6211\u5988\u5988\u548c\u4ed6\u620f\u800d\u54621\u201d\u53ea\u89c1 \u51af\u7433\u5de6\u4e00\u6643\uff0c\u53f3\u4e00\u6643\uff0c\u6709\u5982\u8fce\u98ce\u8d77\u821e\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u90a3\u6839\u5f69\u7ef3\u4e25\u4f3c\u4e00\u6761\u91d1\u86c7\uff0c\u5ffd\u5c48\u5ffd\u4f38\uff0c\u5ffd\u5730 \u5506\u7684\u4e00\u58f0\uff0c\u6296\u5f97\u7b14\u76f4\uff0c\u76f4\u94bb\u63d0\u6469\u8fbe\u591a\u7684\u9f3b\u5b54\u3002\u8fd9\u4e00\u4e0b\u602a\u62db\uff0c\u5927\u51fa\u63d0\u6469\u514b\u591a\u610f\uff0c\u5f69\u7ef3\u5168\u4e0d\u53d7 \u529b\uff0c\u638c\u98ce\u53ca\u8fdc\u4e0d\u80fd\u53ca\u8fd1\uff0c\u7adf\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u9976\u662f\u4ed6\u95ea\u907f\u5f97\u5feb\uff0c\u4e5f\u88ab\u5f69\u7ef3\u8f7b\u8f7b\u7684\u6cbe\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6 \u6253\u4e86\u4e00\u4e2a\u55b7\u568f\u3002 \u3000\u3000\u6c5f\u5357\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5999\u554a\uff01\u5999\u554a\uff01\u201d\u8fde\u7d27\u7ef7\u7740\u8138\u5b54\u7684\u5c3c\u6cca\u5c14\u738b\u4e5f\u4e0d\u7981\u7b11\u4e86\u8d77\u6765\uff1b\u4f46\u89c1\u51af\u7433 \u5201\u94bb\u4e4b\u6781\uff0c\u53e3\u4e2d\u4e0d\u4efb\u53eb\u9053\uff1a\u201c\u523a\u4f60\u773c\u775b\uff01\u201d\u201c\u7a7f\u4f60\u8033\u6735\uff01\u201d\u90a3\u6761\u5f69\u7ef3\u88ab\u5979\u7528\u4e0a\u4e58\u7684\u5185\u529f\u4f7f \u52a8\uff0c\u7adf\u4f3c\u4e00\u6761\u94a2\u7ebf\uff0c\u4e0d\u4f46\u7a7f\u773c\u523a\u9f3b\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u800c\u4e14\u4e13\u94bb\u4eba\u8eab\u5404\u5904\u7a74\u9053\u3002\u63d0\u6469\u8fbe\u591a\u7684\u9634\u9633\u638c \u529b\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u4e5f\u5f97\u5229\u7528\u654c\u4eba\u7684\u53cd\u51fb\u4e4b\u529b\uff0c\u51af\u7433\u7684\u5f69\u7ef3\u8f7b\u98d8\u7684\uff0c\u6253\u53c8\u6253\u4e0d\u65ad\uff1b\u8361\u6587\u8361\u4e0d\u5f00\uff0c \u770b\u4f3c\u6700\u67d4\uff0c\u5b9e\u662f\u6700\u521a\u3002\u51af\u7433\u628a\u771f\u6c14\u9632\u62a4\u5168\u8eab\uff0c\u5979\u4e0e\u63d0\u6469\u8fbe\u591a\u529f\u529b\u6089\u654c\uff0c\u63d0\u6469\u8fbe\u591a\u7684\u5288\u7a7a\u638c\u529b \u53c8\u4f24\u5979\u4e0d\u5f97\uff0c\u5979\u7528\u5f69\u7ef3\u523a\u7a74\uff0c\u7b49\u4e8e\u7528\u5175\u5668\u4ee5\u5236\u7a7a\u62f3\uff0c\u63d0\u6469\u8fbe\u591a\u7b80\u76f4\u65e0\u6cd5\u5e94\u4ed8\u3002 \u3000\u3000\u5510\u7ecf\u5929\u76f4\u770b\u5f97\u5165\u795e\uff0c\u674e\u6c81\u6885\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8868\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u5f88\u8ba8\u538c\u91d1\u4e16\u9057\uff1f\u201d\u5510 \u7ecf\u5929\u968f\u53e3\u5e94\u9053\uff1a\u201c\u55ef\uff0c\u6709\u4e00\u70b9\u3002\u201d\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u674e\u6c81\u6885\u795e\u8272\u751a\u662f\u8ba4\u771f\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8f6c\u53e3\u8bf4 \u9053\uff1a\u201c\u6ca1\uff0c\u6ca1\u6709\u5440\uff01\u5440\uff0c\u5feb\u770b\uff01\u8fd9\u4e00\u62db\u597d\u6781\u4e86\uff01\u201d\u674e\u6c81\u6885\u55c5\u9053\uff1a\u201c\u5582\uff0c\u4f60\u600e\u4e48\u65e0\u5fc3\u7b54\u6211\u7684 \u6d3b\uff1f\u6211\u5988\u51c6\u8d62\u8fd9\u4e2a\u756a\u50e7\uff0c\u4e0d\u770b\u4e5f\u7f62\u3002\u4f60\u771f\u5fc3\u7b54\u6211\uff0c\u4f60\u5230\u5e95\u662f\u4e0d\u662f\u8ba8\u538c\u91d1\u4e16\u9057\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a \u201c\u6211\u662f\u8bf4\u771f\u7684\u3002\u4ee5\u524d\u662f\u6709\u70b9\u8ba8\u538c\uff0c\u73b0\u5728\u5417\uff1f\u6ca1\u6709\u4e86\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u55ef\uff0c\u73b0\u5728\u4e16\u9057\u54e5\u53ea\u6709\u4e03 \u5929\u6027\u547d\u4e86\uff0c\u4f60\u77e5\u4e5f\u4e0d\u77e5\uff1f\u201d\u5510\u7ecf\u5929\u6014\u4e86\u4e00\u6014\uff1b\u600e\u7684\u674e\u6c81\u6885\u8bb0\u5f97\u5982\u6b64\u6e05\u695a\uff1f\u5ffd\u5730\u604d\u7136\u5927\u609f\uff0c\u5fae \u7b11\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u548c\u59e8\u5988\u5230\u6b64\uff0c\u662f\u6765\u8ffd\u91d1\u4e16\u9057\u7684\u3002\u201d\u674e\u6c81\u6094\u9053\uff1a\u201c\u4f60\u613f\u4e0d\u613f\u6551\u4ed6\uff1f\u6211\u5988\u8bf4\u53ea \u6709\u4f60\u548c\u59e8\u7236\u7528\u5929\u5c71\u6d3e\u7684\u5185\u529f\u5fc3\u6cd5\u53ef\u4ee5\u6551\u4ed6\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u548c\u51b0\u5ddd\u5929\u5973\u6765\u6b64\uff0c\u672c\u6765\u5c31\u662f\u51c6 \u5907\u6551\u4ed6\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u8d76\u5feb\u4e0a\u5c71\u4eba\u5bfb\u4ed6\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u5f97\u7b49\u4f60\u5988\u5988\u6253\u5b8c \u8fd9\u4e00\u573a\u54b1\u4eec\u624d\u597d\u53bb\u5440\u3002\u201d\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u91d1\u4e16\u9057\u8fd9\u6837\u4e0d\u8fd1\u4eba\u60c5\uff0c\u5c45\u7136\u4e5f\u6709\u4eba\u6b22\u559c\u4ed6\u3002\u201d \u4f46\u7acb\u5373\u88ab\u8868\u59b9\u6d41\u9732\u7684\u771f\u631a\u611f\u60c5\u6240\u611f\u52a8\uff0c\u60f3\u8d77\u8981\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u627e\u4e00\u4e2a\u4eba\uff0c\u65e0\u5f02\u5927\u6d77\u635e\u9488\uff0c\u6b8a\u65e0 \u628a\u63e1\uff0c\u4e0d\u7981\u9eef\u7136\u795e\u4f24\u3002 \u3000\u3000\u674e\u6c81\u6885\u626c\u58f0\u53eb\u9053\uff1a\u201c\u8868\u54e5\u5df2\u7b54\u5e94\u6551\u4ed6\u5566\uff1b\u5988\uff0c\u4f60\u8d76\u5feb\u6253\u8d25\u8fd9\u4e2a\u756a\u50e7\uff1b\u54b1\u4eec\u597d\u540c\u4e0a\u5c71 \u53bb\uff01\u201d\u5ffd\u542c\u5f97\u201c\u54d7\u5566\u5566\u201d\u4e00\u7247\u58f0\u54cd\uff0c\u5730\u4e0a\u672c\u6765\u51dd\u7ed3\u7740\u5f88\u539a\u7684\u575a\u51b0\uff0c\u8fd9\u65f6\u51af\u7433\u811a\u4e0b\u7684\u51b0\u96ea\u7a81\u7136 \u5d29\u89e3\uff0c\u53ea\u89c1\u51af\u7433\u51cc\u7a7a\u98de\u8d77\uff0c\u5f69\u7ef3\u75be\u7ed5\uff0c\u540c\u65f6\u5c48\u6307\u5982\u94a9\uff0c\u5411\u7740\u63d0\u6469\u8fbe\u591a\u7684\u5934\u9876\u51ff\u4e0b\u3002\u5510\u7ecf\u5929\u559d \u5f69\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u732b\u9e70\u6251\u51fb\u7684\u529f\u592b\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4f46\u89c1\u63d0\u6469\u8fbe\u591a\u7684\u6ee1\u5934\u4e71\u53d1\u6839\u6839\u4e0a\u7ad6\uff0c\u51af\u7433\u7a81 \u7136\u5728\u534a\u7a7a\u4e2d\u8f6c\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u5f69\u7ef3\u500f\u7684\u98d8\u5f00\uff0c\u4eba\u4e5f\u659c\u98de\u98d8\u4e0b\u3002\u63d0\u6469\u8fbe\u591a\u8eab\u6cd5\u4e5f\u662f\u5feb\u5230\u6c72\u70b9\uff0c\u51e0 \u4e4e\u662f\u540e\u811a\u8ddf\u7740\u524d\u811a\u7684\u4e00\u6251\u5373\u81f3\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u628a\u51af\u7433\u5168\u8eab\u90fd\u7f69\u5728\u4ed6\u7684\u638c\u529b\u4e4b\u4e0b\u3002 \u3000\u3000\u8981\u77e5\u63d0\u6469\u8fbe\u591a\u80fd\u591f\u79f0\u96c4\u4e1c\u6b27\u897f\u4e9a\uff0c\u5b9e\u975e\u5e78\u81f3\uff0c\u4ed6\u89c1\u96be\u4ee5\u53d6\u80dc\uff0c\u7a81\u65bd\u8be1\u8ba1\uff0c\u865a\u5288\u6570\u638c\uff0c\u8ff7 \u60d1\u51af\u7433\uff0c\u5374\u628a\u5185\u5bb6\u771f\u529b\uff0c\u8fd0\u5230\u811a\u8ddf\uff0c\u7a81\u7136\u5728\u5730\u4e0a\u91cd\u91cd\u4e00\u8e0f\uff0c\u5c06\u575a\u51b0\u9707\u88c2\u3002\u6b63\u5de7\u51af\u7433\u53c8\u88ab\u5973\u513f \u50ac\u4fc3\uff0c\u5ffd\u89c9\u5730\u4e0b\u6447\u52a8\uff0c\u4fbf\u8d81\u52bf\u98de\u8d77\uff0c\u7528\u529b\u4e0b\u6251\u3002\u63d0\u6469\u8fbe\u591a\u6b63\u8981\u501f\u7528\u654c\u4eba\u53cd\u51fb\u4e4b\u529b\uff0c\u51af\u7433\u7684\u529b \u91cf\u5206\u89e3\u4e3a\u4e8c\uff0c\u4e00\u80a1\u529b\u91cf\u7528\u4ee5\u538b\u4f4f\u5730\u4e0b\u7684\u575a\u51b0\uff0c\u624d\u80fd\u501f\u529b\u98de\u8d77\uff1b\u4e00\u80a1\u529b\u91cf\u7528\u4ee5\u53cd\u6251\u654c\u4eba\uff1b\u8fd9\u4e00 \u6765\uff0c\u6070\u597d\u4e2d\u8ba1\uff0c\u5373\u5728\u5185\u529f\u7684\u6bd4\u5bf9\u4e0a\uff0c\u4e5f\u5df2\u53ca\u4e0d\u4e0a\u63d0\u6469\u8fbe\u591a\u4e86\u3002\u63d0\u6469\u8fbe\u591a\u7684\u9634\u9633\u4e94\u884c\u638c\u529b\u7acb\u751f \u5999\u7528\uff0c\u51af\u7433\u51e0\u4e4e\u88ab\u4ed6\u7684\u638c\u529b\u5377\u5165\u6f29\u6da1\uff0c\u5e78\u800c\u5979\u7684\u8f7b\u529f\u5999\u6280\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u80fd\u5728\u7a7a\u4e2d\u8f6c\u6298\uff0c\u8fd9\u624d \u9003\u51fa\u4e86\u63d0\u6469\u8fbe\u591a\u7684\u6bd2\u624b\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u8fdb\u4e00\u9000\u4e4b\u95f4\uff0c\u63d0\u6469\u8fbe\u591a\u5df2\u662f\u62a2\u4e86\u5148\u624b\uff0c\u51af\u7433\u6025\u5fd9\u51dd\u795e\u8fd0\u6c14\uff0c\u4ecd\u7528\u524d\u6cd5\uff0c\u4ee5\u5f69\u7ef3\u523a \u4ed6\u7684\u7a74\u9053\u3002\u4f46\u63d0\u6469\u8fbe\u591a\u7684\u638c\u6cd5\u4ea6\u5df2\u8ddf\u7740\u6539\u53d8\u3002 \u3000\u3000\u4f46\u89c1\u63d0\u6469\u8fbe\u591a\u4e94\u6307\u75be\u5f39\uff0c\u53e6\u4e00\u53ea\u624b\u5219\u4e0d\u505c\u7684\u6253\u7740\u5708\u5708\uff0c\u51af\u7433\u7684\u5f69\u7ef3\u6709\u5982\u957f\u86c7\u5c48\u4f38\uff0c\u500f\u8fdb \u500f\u9000\uff0c\u5374\u603b\u662f\u7a7f\u4e0d\u8fdb\u5708\u5b50\uff0c\u8fd1\u4e0d\u4e86\u654c\u4eba\u7684\u8eab\u8eaf\uff0c\u539f\u6765\u63d0\u6469\u8fbe\u591a\u7684\u806a\u660e\u624d\u667a\u5e76\u4e0d\u4e9a\u4e8e\u51af\u7433\uff0c\u4ea4 \u624b\u4e86\u6570\u5341\u56de\u5408\u4e4b\u540e\uff0c\u4ed6\u5df2\u770b\u51fa\u51af\u7433\u7684\u529f\u529b\u4e0e\u4ed6\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4e5f\u770b\u51fa\u4e86\u51af\u7433\u9632\u4ed6\u9634\u9633\u638c\u529b\u7684\u65b9 \u6cd5\u3002\u4e8e\u662f\u6539\u53d8\u6218\u672f\uff0c\u53ea\u7528\u4e00\u624b\u53d1\u52a8\u9634\u9633\u638c\u529b\uff0c\u53e6\u4e00\u53ea\u624b\u5219\u6539\u638c\u4e3a\u6307\uff0c\u628a\u5185\u529b\u51dd\u4e8e\u6307\u5c16\uff1b\u638c\u529b \u7684\u5206\u5e03\u9762\u5e7f\uff0c\u9762\u5e7f\u5219\u529b\u8584\uff0c\u96be\u4ee5\u4ee4\u5f69\u7ef3\u53d7\u529b\uff1b\u6307\u529b\u51dd\u4e8e\u4e00\u70b9\uff0c\u5f69\u7ef3\u4e00\u8fd1\u5c31\u88ab\u4ed6\u5f39\u5f00\u3002\u8fd9\u4e00 \u6765\uff0c\u51af\u7433\u7684\u5f69\u7ef3\u523a\u7a74\u4e4b\u6cd5\u53d7\u4e86\u514b\u5236\uff0c\u96be\u4ee5\u53d1\u6325\uff0c\u53cc\u65b9\u7b49\u4e8e\u5404\u4ee5\u5185\u529b\u76f8\u640f\uff0c\u6253\u6210\u4e86\u4e00\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6697\u6697\u987f\u8db3\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u518d\u50ac\u4f60\u7684\u5988\u5988\u5566\uff01\u201d\u674e\u6c81\u6885\u5927\u662f\u7126\u6025\uff0c\u5374\u65e0\u53ef\u5948\u4f55\u3002\u6c5f\u5357 \u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5510\u8001\u592a\u5a46\uff0c\u518d\u53d1\u6697\u5668\u3002\u201d\u4ed6\u673a\u4f36\u4e4b\u6781\uff0c\u521a\u624d\u5510\u8d5b\u82b1\u5077\u53d1\u6697\u5668\uff0c\u4ed6\u5750\u5728\u5510\u8d5b\u82b1\u8eab \u8fb9\uff0c\u53ea\u6709\u4ed6\u77a7\u5728\u773c\u5185\u3002\u4e0d\u8fc7\u4ed6\u5374\u770b\u4e0d\u51fa\u51af\u7433\u5077\u53d1\u7684\u90a3\u7247\u6811\u53f6\uff0c\u53ea\u9053\u521a\u624d\u4e00\u63d0\u6469\u8fbe\u591a\u7684\u53d7\u632b\uff0c \u662f\u5510\u8d5b\u82b1\u7684\u6697\u5668\u4e4b\u529f\u3002\u5510\u8d5b\u82b1\u82e6\u7b11\u9053\uff1a\u201c\u51af\u7433\u7684\u6697\u5668\u529f\u592b\u6bd4\u6211\u5389\u5bb3\u5f97\u591a\uff0c\u5979\u72b9\u81ea\u4e0d\u80fd\u5236\u80dc\uff0c \u6211\u518d\u51fa\u624b\uff0c\u90a3\u7ba1\u4fdd\u662f\u8d8a\u5e2e\u8d8a\u7cdf\uff01\u201d\u5510\u7ecf\u5929\u542c\u4e86\u8fd9\u624d\u77e5\u9053\u521a\u624d\u7684\u6697\u5668\u7adf\u662f\u5510\u8d5b\u82b1\u6240\u53d1\uff0c\u81ea\u5df1\u9519 \u602a\u63d0\u6469\u8fbe\u591a\u4e86\u3002 \u3000\u3000\u4e0d\u8bf4\u5510\u7ecf\u5929\u7b49\u4e00\u5e72\u4eba\u4e3a\u51af\u7433\u6697\u6697\u7740\u6025\uff0c\u5c3c\u6cca\u5c14\u738b\u66f4\u662f\u89e6\u76ee\u60ca\u5fc3\uff0c\u4ed6\u628a\u63d0\u6469\u8fbe\u591a\u501a\u4e3a\u9760 \u5c71\uff0c\u53ea\u9053\u63d0\u6469\u8fbe\u591a\u4e00\u5230\uff0c\u4fbf\u53ef\u65e0\u654c\u4e8e\u5929\u4e0b\uff0c\u54ea\u77e5\u5374\u88ab\u51af\u7433\u7f20\u6218\uff0c\u62a2\u4e0d\u5230\u534a\u70b9\u4e0a\u98ce\u3002\u4e00\u4e2a\u4e2d\u56fd \u5987\u4eba\uff0c\u4e5f\u6709\u5982\u6b64\u795e\u5947\u7684\u672c\u9886\uff0c\u4e2d\u56fd\u4eba\u624d\u4e4b\u76db\uff0c\u771f\u662f\u96be\u4ee5\u7aa5\u6d4b\uff0c\u770b\u6765\u6211\u771f\u662f\u4e95\u5e95\u4e4b\u86d9\u4e86\uff01\u201d\u5fc3 \u4e2d\u4e0d\u7981\u51db\u7136\u751f\u60e7\uff01 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u82e6\u6597\u51af\u7433\uff0c\u5730\u4e0b\u7684\u51b0\u96ea\u4e0d\u4f4f\u878d\u89e3\uff0c\u53cc\u65b9\u90fd\u5360\u4e0d\u5230\u4fbf\u5b9c\u3002\u51af\u7433\u9762\u4e0a\u7684\u7b11\u5bb9\u4e5f\u5c3d\u5df2 \u6536\u655b\uff0c\u5979\u6b63\u60f3\u522b\u51fa\u65b0\u6cd5\u7834\u654c\uff0c\u5ffd\u5730\u5c71\u98ce\u53c8\u8d77\uff0c\u5377\u7740\u6c99\u77f3\u51b0\u5757\uff0c\u4ece\u4e0a\u9762\u76f4\u522e\u4e0b\u6765\uff0c\u84e6\u5730\u91cc\u5ffd\u542c \u5f97\u4e00\u58f0\u602a\u5578\uff0c\u968f\u7740\u5c71\u98ce\u5439\u9001\u4e0b\u6765\uff0c\u90a3\u5578\u58f0\u604d\u5982\u6d77\u6cab\u5377\u7a7a\uff0c\u63a5\u7eed\u4e0d\u65ad\uff0c\u63a5\u7740\u7687\u4e00\u9635\u6781\u5947\u7279\u7684\u545c \u545c\u4e4b\u58f0\u3002 \u3000\u3000\u51af\u7433\u5ffd\u5730\u8df3\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u662f\u91d1\u4e16\u9057\uff01\u201d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u75be\u5411\u5c71\u4e0a\u5954\u53bb\u3002\u63d0\u6469\u8fbe\u591a \u6014\u4e86\u4e00\u6014\uff0c\u5495\u5495\u565c\u565c\u7684\u5927\u56b7\u4e00\u901a\uff0c\u4e5f\u8ddf\u7740\u5411\u5c71\u9876\u5954\u53bb\uff0c\u51af\u7433\u7684\u5f71\u5b50\uff0c\u8f6c\u77ac\u4e4b\u95f4\u4e0d\u89c1\uff0c\u63d0\u6469\u8fbe \u591a\u5411\u7740\u53e6\u4e00\u4e2a\u65b9\u5411\u767b\u5c71\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8eab\u5f62\u4e5f\u88ab\u55df\u5ce8\u7684\u602a\u77f3\u906e\u853d\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u662f\u4e00\u5446\uff0c\u901a\u8bd1\u7684\u7980\u544a\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u63d0\u6469\u8fbe\u591a\u5927\u6cd5\u5e08\u8bf4\uff0c\u4ed6\u7684\u5f1f\u5b50\u5728\u4e0a\u9762\u547c\u5524 \u4ed6\uff0c\u4ed6\u8981\u6500\u767b\u4e16\u754c\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u5148\u544a\u8f9e\u4e86\u3002\u201d\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u660e\u660e\u662f\u91d1\u4e16\u9057\uff0c\u600e\u4e48\u662f\u4ed6 \u7684\u5f1f\u5b50\uff1f\u201d\u674e\u6c81\u6885\u626f\u7740\u5510\u7ecf\u5929\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u53bb\u3002\u201d\u8fd9\u65f6\u7fa4\u60c5\u8038\u52a8\uff0c\u51b0\u5ddd\u5929\u5973\u548c\u5510\u8d5b\u82b1\u7b49\u4eba\u90fd \u7eb7\u7eb7\u8d77\u7acb\uff0c\u5ffd\u53c8\u95fb\u5f97\u545c\u545c\u7684\u53f7\u89d2\u4e4b\u58f0\uff0c\u5b88\u5728\u5c71\u8c37\u7684\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\u8dd1\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u4e2d\u56fd\u7684\u5927\u519b \u5230\u4e86\uff01\u201d\u4f46\u542c\u5f97\u8c37\u5916\u4e07\u9a6c\u5954\u817e\u4e4b\u58f0\uff0c\u5c3c\u6cca\u5c14\u738b\u5927\u60ca\u5931\u8272\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54b1\u4eec\u7684\u519b\u961f\u5148\u884c\u8d8a\u754c\uff0c\u602a\u4e0d\u5f97\u4eba\u5bb6\u524d\u6765\u95ee\u7f6a\u3002\u5e78\u5728\u5c1a\u672a\u8d8a\u51fa\u5c71\u533a\uff0c\u8fd8\u6709 \u5f97\u8bf4\u3002\u76ee\u4e0b\u4e4b\u8ba1\uff0c\u53ea\u6709\u8bbe\u6cd5\u6d88\u9975\u4e89\u7aef\u3002\u65b9\u4e3a\u4e0a\u7b56\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u9053\uff1a\u201c\u4ed6\u4eec\u80af\u4e48\uff1f\u201d\u5510\u7ecf\u5929 \u9053\uff1a\u201c\u4e2d\u56fd\u662f\u4ec1\u4e49\u4e4b\u5e08\uff0c\u4eba\u4e0d\u72af\u6211\uff0c\u6211\u4e0d\u72af\u4eba\u3002\u73b0\u5728\u6218\u7aef\u672a\u542f\uff0c\u56fd\u738b\u4eb2\u53bb\u966a\u7f6a\uff0c\u65b9\u53ef\u5316\u5e72\u6208 \u800c\u4e3a\u7389\u5e1b\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u6ca1\u4e86\u4e3b\u610f\uff0c\u6073\u6c42\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4e00\u5207\u4ef0\u4ed7\u5510\u5927\u4fa0\u4ee3\u4e3a\u8bf4\u8f9e\u3002\u201d\u5c3c\u6cca\u5c14\u738b \u672c\u6765\u89ce\u89ca\u897f\u85cf\uff0c\u7ecf\u8fc7\u4e86\u4eca\u65e5\u7684\u4e00\u573a\u6bd4\u6b66\uff0c\u59cb\u77e5\u4e2d\u56fd\u80fd\u4eba\u4e4b\u591a\uff0c\u800c\u4eca\u53c8\u88ab\u4e2d\u56fd\u7684\u519b\u961f\u5236\u4f4f\u673a \u5148\uff0c\u5835\u4e86\u8c37\u53e3\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u6709\u91ce\u5fc3\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6392\u96be\u89e3\u7eb7\uff0c\u4e43\u662f\u6211\u8f88\u4efd\u6240\u5f53\u4e3a\uff0c\u4e0d\u6562\u63a8\u8f9e\uff01\u201d\u5c3c\u6cca\u5c14\u738b\u4fbf\u8bf7\u5510\u7ecf\u5929\u4e0e\u51b0\u5ddd \u5929\u5973\u540c\u4e58\u767d\u8c61\uff0c\u6446\u8d77\u4eea\u4ed7\uff0c\u5230\u8c37\u53e3\u53bb\u8fce\u63a5\u5927\u519b\u3002\u674e\u6c81\u6885\u6025\u9053\uff1a\u201c\u8868\u54e5\uff0c\u4f60\u4e0d\u53bb\u6551\u91d1\u4e16\u9057 \u4e48\uff1f\u201d\u5510\u7ecf\u5929\u201c\u9053\uff1a\u201c\u5f85\u8fd9\u91cc\u4e8b\u60c5\u7a0d\u544a\u6bb5\u843d\uff0c\u6211\u4fbf\u7acb\u5373\u4e0a\u5c71\u3002\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5148\u8d70 \u4e86\u3002\u201d\u795e\u8272\u4e4b\u95f4\uff0c\u9887\u4e3a\u4e0d\u60a6\u3002\u5510\u7ecf\u5929\u53d6\u51fa\u4e00\u4e2a\u94f6\u74f6\uff0c\u74f6\u4e2d\u85cf\u6709\u4e09\u7c92\u78a7\u7075\u4e39\uff0c\u9012\u8fc7\u53bb\u7ed9\u674e\u6c81\u6885 \u9053\uff1a\u201c\u78a7\u7075\u4e39\u867d\u7136\u4e0d\u80fd\u6cbb\u672c\uff0c\u4f46\u8ba9\u4ed6\u591a\u6d3b\u51e0\u5929\uff0c\u60f3\u8fd8\u80fd\u591f\u3002\u4f60\u4e00\u8def\u4e0a\u7559\u4e0b\u6807\u5fd7\uff0c\u6211\u81ea\u4f1a\u8ddf\u8e2a \u524d\u5f80\u3002\u201d\u674e\u6c81\u6885\u63a5\u8fc7\u94f6\u74f6\uff0c\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u82e5\u7136\u6551\u4e0d\u56de\u4e16\u9057\u54e5\u54e5\uff0c\u6211\u4e00\u751f\u90fd\u4f1a\u96be \u8fc7\u3002\u201d\u5510\u7ecf\u5929\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u8fd9\u4e2a\u987d\u76ae\u7684\u8868\u59b9\u53f9\u6c14\uff0c\u5fc3\u4e2d\u751a\u611f\u6b49\u759a\uff0c\u4f46\u4e2d\u5c3c\u4e24\u56fd\u7684\u53cb\u597d\uff0c\u6bd4\u8d77 \u91d1\u4e16\u9057\u7684\u751f\u6b7b\u91cd\u8981\u5f97\u591a\uff0c\u4ed6\u53c8\u600e\u80fd\u62bd\u8eab\u966a\u674e\u6c81\u6885\uff1f \u3000\u3000\u8d70\u51fa\u846b\u82a6\u5f62\u7684\u5ce1\u8c37\uff0c\u53ea\u89c1\u4e2d\u56fd\u7684\u519b\u961f\u6392\u6210\u6247\u5f62\u7684\u9635\u52bf\uff0c\u5835\u4f4f\u8c37\u53e3\uff0c\u6208\u77db\u6620\u65e5\uff0c\u65bd\u65d7\u62db \u5c55\uff0c\u519b\u5bb9\u751a\u58ee\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u770b\u90a3\u4e0d\u662f\u9648\u5929\u5b87\u548c\u5e7d\u840d\u5417\uff1f\u201d\u53ea\u89c1\u201c\u5e05\u201d\u5b57\u65d7\u4e0b\uff0c\u4e00 \u4e2a\u96c4\u8d73\u8d73\u7684\u5c06\u519b\uff0c\u633a\u7740\u72fc\u7259\u68d2\uff0c\u5728\u9a6c\u80cc\u4e0a\u987e\u76fc\u81ea\u96c4\uff0c\u4fa7\u8fb9\u7acb\u7740\u4e00\u4e2a\u5c11\u5e74\u516c\u4e8e\uff0c\u4e00\u4e2a\u5982\u82b1\u5c11 \u5973\uff0c\u5510\u7ecf\u5929\u8ba4\u5f97\u8fd9\u5c06\u519b\u4e43\u662f\u7126\u6625\u96f7\uff0c\u65c1\u8fb9\u7ad9\u7acb\u7684\u516c\u5b50\u548c\u5c11\u5973\u6b63\u662f\u9648\u5929\u5b87\u548c\u5e7d\u840d\u3002\u539f\u6765\u798f\u5eb7\u5b89 \u8d4f\u8bc6\u9648\u5929\u5b87\u7684\u624d\u5177\uff0c\u53eb\u4ed6\u6765\u8944\u8d5e\u519b\u52a1\uff0c\u5e7d\u840d\u6000\u5ff5\u4e3b\u4eba\uff0c\u5f53\u7136\u8ddf\u7740\u6765\u4e86\uff1b \u3000\u3000\u5510\u7ecf\u5929\u5f97\u89c1\u9648\u5929\u5b87\uff0c\u51b0\u5ddd\u5929\u5973\u5f97\u89c1\u5e7d\u840d\uff0c\u81ea\u662f\u559c\u4e4b\u4e0d\u80dc\u3002\u7126\u6625\u96f7\u867d\u662f\u4e3b\u5e05\uff0c\u4f46\u62d9\u4e8e\u8a00 \u8f9e\uff0c\u4ea4\u6d89\u4e8b\u5b9c\uff0c\u90fd\u59d4\u6258\u7ed9\u9648\u5929\u5b87\u529e\u7406\u3002\u9648\u5929\u5b87\u9996\u5148\u4fbf\u95ee\u5c3c\u6cca\u5c14\u738b\u7684\u6765\u610f\uff0c\u5c3c\u6cca\u5c14\u738b\u8bf4\u662f\u56e0\u4e3a \u51ac\u5929\u5929\u5bd2\u51b7\uff0c\u7279\u5730\u5230\u5c71\u8c37\u4e2d\u907f\u5bd2\u7ec3\u519b\uff0c\u559c\u9a6c\u62c9\u96c5\u5c71\u592a\u5927\uff0c\u4e00\u65f6\u6ca1\u6709\u67e5\u6e05\u695a\uff0c\u4ee5\u81f3\u8d8a\u8fc7\u7586\u754c\u3002 \u8bf4\u8bdd\u4e4b\u95f4\uff0c\u9891\u9891\u9053\u6b49\u3002\u9648\u5929\u5b57\u60f3\u4e0d\u5230\u4e8b\u60c5\u5982\u6b64\u5bb9\u6613\u89e3\u51b3\uff0c\u4e5f\u4fbf\u4e0d\u4e3a\u5df1\u751a\uff0c\u544a\u8beb\u4e86\u51e0\u53e5\uff0c\u7ea6\u597d \u5728\u7b2c\u4e8c\u65e5\u518d\u8be6\u7ec6\u5546\u8c08\u4e24\u56fd\u53cb\u597d\u901a\u5546\u7684\u5177\u4f53\u6761\u6587\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u65e2\u5df2\u9053\u6b49\uff0c\u4e2d\u56fd\u519b\u961f\u5f53\u7136\u4ea6\u4ee5\u56fd\u541b\u4e4b\u793c\u76f8\u5f85\uff0c\u7acb\u5373\u5728\u519b\u8425\u4e2d\u8bbe\u5bb4\uff0c\u5e76\u9988\u8d60\u4e00\u4e07 \u5957\u5bd2\u8863\u7ed9\u5c3c\u6cca\u5c14\u7684\u58eb\u5175\u3002\u5c3c\u6cca\u5c14\u519b\u6b22\u58f0\u96f7\u52a8\uff0c\u4eba\u4eba\u611f\u8c22\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u7684\u76f8\u52a9\uff0c\u6d88\u6d31\u4e86\u8fd9 \u573a\u6218\u7978\u3002\u5bf9\u4e2d\u56fd\u7684\u5bbd\u5bb9\uff0c\u5f53\u7136\u66f4\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002 \u3000\u3000\u4e8b\u60c5\u544a\u4e00\u6bb5\u843d\uff0c\u8d81\u7740\u7b75\u5e2d\u672a\u5f00\uff0c\u9648\u592d\u5b57\u5fd9\u4e0e\u5510\u7ecf\u5929\u4ea4\u8c08\u522b\u540e\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u9648\u5929\u5b87\u542c\u8bf4\u91d1\u4e16\u9057\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u800c\u4eca\u72ec\u4e0a\u9ad8\u5c71\uff0c\u53ea\u6015\u96be\u4ee5\u5bfb\u89c5\uff0c\u5fc3\u5ff5\u4ed6\u4ee5\u5f80\u76f8\u6551\u4e4b\u60c5\uff0c \u751a\u662f\u96be\u8fc7\uff0c\u4e5f\u613f\u966a\u5510\u7ecf\u5929\u7b49\u4e0a\u5c71\u5bfb\u627e\u3002\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6211\u4eec\u5df2\u6709\u591a\u4eba\u524d\u5f80\uff0c\u4f60\u5c1a\u6709\u5927\u4e8b\u8981\u529e\uff0c \u4e0d\u5fc5\u53bb\u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u4e45\u4e5f\u6015\u8981\u5206\u624b\u4e86\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u662f\u5426\u4ee4\u5c0a\u5df2\u63a5\u4e86\u5fa1\u65e8\uff0c \u6709\u4e86\u5357\u5f52\u4e4b\u8baf\u4e48\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4eac\u4e2d\u5df2\u6765\u4e86\u9a7f\u62a5\uff0c\u5bb6\u7236\u5949\u8c03\u56de\u4eac\uff0c\u91cd\u4efb\u5fa1\u53f2\u3002\u5bb6\u7236\u60f3\u56de\u4eac \u4e4b\u540e\uff0c\u4fbf\u5373\u8f9e\u5b98\uff0c\u56de\u6545\u4e61\u517b\u8001\u3002\u201d \u3000\u3000\u6c5f\u5357\u63d2\u53e3\u7b11\u9053\uff1a\u201c\u5e26\u4e0d\u5e26\u6211\u56de\u53bb\uff1f\u53ef\u601c\u6211\u540d\u53eb\u6c5f\u5357\uff0c\u5929\u5929\u542c\u4f60\u4eec\u8bf4\u6c5f\u5357\u7684\u7f8e\u666f\uff0c\u6c5f\u5357\u5230 \u5e95\u662f\u600e\u4e2a\u597d\u6cd5\uff1f\u6211\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u6c5f\u5357\u5c31\u50cf\u4f60\u4e00\u6837\uff0c\u987d\u76ae\u6d3b\u6cfc\uff0c\u751f\u6c14\u52c3 \u52c3\uff0c\u60f9\u4eba\u559c\u7231\u3002\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u6211\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5f97\u6709\u4eba\u8bf4\u6211\u4e0d\u60f9\u4eba\u538c\uff0c\u5510\u5927\u4fa0\uff0c\u4f60\u771f\u662f \u6211\u7684\u77e5\u5df1\u3002\u201d\u9648\u5929\u5b87\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u5982\u4eca\u548c\u6211\u4eec\u90fd\u662f\u4e00\u6837\u7684\u8eab\uff0c\u4f60\u6b22\u559c\u53bb\u54ea\u513f\u5c31\u53bb\u54ea\u513f\u3002\u4f60 \u613f\u548c\u6211\u4eec\u540c\u56de\u6c5f\u5357\uff0c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u6211\u4e5f\u820d\u4e0d\u5f97\u4f60\u5462\uff01\u201d \u3000\u3000\u90a3\u8fb9\u53a2\uff0c\u5e7d\u840d\u4e5f\u5728\u548c\u4e3b\u4eba\u4e92\u8c08\u5fc3\u4e8b\u3002\u5e7d\u840d\u95f4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u56de\u4e0d\u56de\u5c3c\u6cca\u5c14\uff1f\u201d\u51b0\u5ddd\u5929\u5973 \u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u60f3\u56de\u53bb\uff0c\u53ea\u6015\u56fd\u738b\u4e5f\u4e0d\u6b22\u8fce\u6211\u5462\uff01\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u60f3\u5a36\u4f60\u505a\u7687\u540e \u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u8c05\u4ed6\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80c6\u5b50\u3002\u6211\u770b\u4ed6\u73b0\u5728\u5c31\u662f\u60f3\u7b49\u6211\u81ea\u5df1\u8bf4\u51fa\u4e0d\u613f\u610f\u56de\u5c3c \u6cca\u5c14\u7684\u8bf4\u8bdd\u3002\u201d\u5c06\u4e24\u65e5\u6765\u7684\u4e8b\u60c5\uff0c\u544a\u8bc9\u5e7d\u840d\uff0c\u5e7d\u840d\u542c\u8bf4\u5c3c\u6cca\u5c14\u738b\u5c34\u5c2c\u4e4b\u4e8b\uff0c\u51e0\u4e4e\u7b11\u7834\u809a\u76ae\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5e7d\u840d\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u56de\u4e0d\u56de\u51b0\u5bab\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5e7d\u840d\u9053\uff1a \u201c\u6211\u60f3\u90a3\u51b0\u5bab\u51b7\u51b7\u6e05\u6e05\uff0c\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u56de\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u504f\u504f\u5c31\u662f\u559c\u6b22\u51b0\u5bab\uff01\u201d \u5e7d\u840d\u9eef\u7136\u4e0d\u8bed\uff0c\u8138\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u5931\u671b\u7684\u795e\u8272\u3002\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u5b66\u9648\u5929\u5b87\u5bf9\u5f85\u6c5f\u5357\u7684\u699c \u6837\uff0c\u4ece\u4eca\u4ee5\u540e\uff0c\u4f60\u6211\u59d0\u59b9\u76f8\u79f0\uff0c\u4f60\u613f\u610f\u53bb\u54ea\u513f\uff0c\u5c31\u53bb\u54ea\u513f\u3002\u201d\u5e7d\u840d\u5fd9\u9053\uff1a\u201c\u6211\u6ca1\u6709\u79bb\u5f00\u516c\u4e3b \u7684\u610f\u601d\u3002\u201d\u51b0\u5ddd\u592d\u5973\u7b11\u9053\uff1a\u201c\u5404\u4eba\u6709\u5404\u4eba\u7684\u7f18\u4efd\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u613f\u518d\u56de\u51b0\u5bab\uff0c\u4f60\u60f3\u8ddf\u9648\u516c\u5b50\u540c \u53bb\u6c5f\u5357\uff0c\u5929\u5b87\u4e3a\u4eba\u4e0d\u9519\uff0c\u4f60\u8ddf\u4ed6\u6211\u5f88\u653e\u5fc3\uff01\u201d\u5e7d\u840d\u7ed9\u4e3b\u4eba\u4e00\u8a00\u8bf4\u7834\u5fc3\u4e8b\uff0c\u65e2\u662f\u6b22\u559c\uff0c\u53c8\u662f\u5bb3 \u7f9e\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ea\u662f\u563b\u563b\u7684\u7b11\u3002 \u3000\u3000\u5e2d\u6563\u4e4b\u540e\uff0c\u5df2\u662f\u9ec4\u660f\uff0c\u5510\u7ecf\u5929\u51b0\u5ddd\u5929\u5973\u7b49\u90fd\u7559\u5728\u6e05\u519b\u5927\u8425\uff0c\u5c3c\u6cca\u5c14\u738b\u81ea\u548c\u4ed6\u7684\u5927\u81e3\u56de \u53bb\uff0c\u5546\u8bae\u660e\u65e5\u4ea4\u6362\u6587\u4e66\uff0c\u52d8\u5b9a\u7586\u754c\u7b49\u5927\u4e8b\u3002\u5510\u8d5b\u82b1\u77e5\u9053\u9f99\u7075\u77eb\u5df2\u9003\u4eba\u6df1\u5c71\uff0c\u4e0d\u5f85\u5e2d\u6563\uff0c\u4fbf\u5148 \u548c\u4f84\u513f\u4e0a\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u559c\u9a6c\u62c9\u96c5\u5c71\u4f2a\u591c\u666f\u5947\u7279\u4e4b\u6781\uff0c\u4e00\u671b\u65e0\u5c3d\u5343\u4e07\u5ea7\u5c71\u5cf0\uff0c\u90fd\u662f\u767d\u96ea\u7691\u7691\uff0c\u597d\u50cf\u795e\u8bdd\u4e2d\u7684\u7409\u7483 \u4e16\u754c\u3002\u5510\u7ecf\u5929\u8feb\u4e0d\u53ca\u5f85\uff0c\u4e0e\u51b0\u5ddd\u5929\u5973\u8fde\u591c\u767b\u5c71\u3002\u5348\u591c\u65f6\u5206\uff0c\u91cd\u5230\u91d1\u4e16\u9057\u7559\u4e0b\u8bd7\u53e5\u7684\u5730\u65b9\uff0c\u5510 \u7ecf\u5929\u65e0\u9650\u611f\u6168\uff0c\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u5f53\u521d\u90a3\u4e48\u618e\u6068\u4ed6\uff0c\u800c\u4eca\u5374\u4ece\u5fc3\u5e95\u91cc\u76fc\u671b\u4ed6\u4e0d\u8981\u6b7b\u3002\u201d\u51b0\u5ddd \u5929\u5973\u7b11\u9053\uff1a\u201c\u4eba\u4e16\u4e4b\u4e8b\uff0c\u672c\u6765\u96be\u6d4b\u3002\u8fd9\u4e0d\u662f\u4f60\u5e38\u8bf4\u7684\u5417\uff1f\u201d\u8c08\u7b11\u4e4b\u95f4\uff0c\u5ffd\u53c8\u542c\u7684\u5f97\u5c71\u9876\u6709\u602a \u5578\u4e4b\u58f0\uff0c\u4e0d\u662f\u91d1\u4e16\u9057\u662f\u8c01\uff0c\u53ea\u662f\u5c71\u5cf0\u63d2\u4e91\uff0c\u867d\u95fb\u5578\u8038\uff0c\u5374\u4e0d\u77e5\u4ed6\u4eba\u5728\u4f55\u5904\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u98d8\u96f6\u6e56\u6d77\u8c6a\u60c5\u5728\uff0c\u6b32\u4e0a\u4ecd\u95f4\u7b2c\u4e00\u5cf0\uff01 \u3000\u3000\u6b32\u77e5\u91d1\u4e16\u9057\u6027\u547d\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516b\u56de\u3000\u6069\u6028\u5168\u6d88\u3000\u7ecf\u5e74\u6000\u65e7\u6068\u3000\u6b7b\u751f\u5ea6\u5916\u3000\u4e00\u9189\u89e3\u5343\u6101 \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u516b\u56de \u6069\u6028\u5168\u6d88\u3000\u7ecf\u5e74\u6000\u65e7\u6068 \u6b7b\u751f\u5ea6\u5916\u3000\u4e00\u9189\u89e3\u5343\u6101 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5728\u4e3a\u91d1\u4e16\u9057\u62c5\u5fc3\uff0c\u91d1\u4e16\u9057\u5374\u6b63\u5728\u4e3a\u51b0\u5ddd\u5929\u5973\u7948\u7977\u3002\u91d1\u4e16\u9057\u65e9\u5c31\u770b\u89c1\u4ed6\u4eec\u4e86\uff0c\u5510 \u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u5374\u6ca1\u6709\u770b\u89c1\u4ed6\u3002 \u3000\u3000\u90a3\u662f\u5728\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u51fa\u624b\u62e6\u963b\u7ea2\u8863\u756a\u50e7\uff0c\u8ba9\u9f99\u7075\u77eb\u6500\u4e0a\u5c71\u5cf0\u9003\u8d70\u7684\u65f6\u5019\uff0c\u91d1\u4e16\u9057\u6b63 \u4f0f\u5728\u5bf9\u9762\u5c71\u5cf0\u3002\u5c06\u4e00\u5207\u60c5\u5f62\u90fd\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u8fd9\u65f6\u53ea\u8981\u91d1\u4e16\u9057\u4e00\u58f0\u558a\uff0c\u4ed6\u7acb\u523b\u53ef\u4ee5\u5c06\u81ea\u5df1\u7684\u751f\u547d\u4ece\u6b7b\u4ea1\u7684\u8fb9\u7f18\u633d\u6551\u56de\u6765\uff0c\u53ef\u662f\u4ed6\u5374\u4e0d \u613f\u610f\u5411\u5510\u7ecf\u5929\u4e5e\u6c42\uff0c\u4ed6\u4e00\u58f0\u4e0d\u54cd\u5730\u76f4\u5230\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u8d70\u4e86\u4e4b\u540e\uff0c\u624d\u62ac\u8d77\u5934\u6765\uff0c\u6df1\u6df1\u5730\u53f9 \u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5c71\u98ce\u5377\u7740\u96ea\u82b1\uff0c\u96ea\u82b1\u98d8\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u6b7b\u6c34\u4e00\u6837\u7684\u5fc3\u6e56\uff0c\u5374\u5ffd\u7136\u6cdb\u8d77\u4e86\u6ce2\u6f9c\uff0c\u8bb0\u8d77\u4e86\u4eba \u4e16\u7684\u51b7\u9177\uff0c\u4e5f\u8bb0\u8d77\u4e86\u4eba\u4e16\u7684\u6e29\u6696\u3002\u4ed6\u60f3\u8d77\u51b0\u5ddd\u5929\u5973\u5bf9\u4ed6\u7684\u53cb\u60c5\u548c\u671f\u5f85\uff0c\u4ed6\u4e5f\u60f3\u8d77\u4e86\u674e\u6c81\u6885\u5bf9 \u4ed6\u7684\u7231\u610f\u4e0e\u5173\u6000\u3002\u7136\u800c\u8fd9\u4e00\u4e9b\u6742\u4e71\u65e0\u7ae0\u3001\u7247\u7247\u6bb5\u6bb5\u7684\u56de\u5fc6\uff0c\u90fd\u4f3c\u90a3\u6ee1\u5929\u98de\u821e\u7684\u96ea\u82b1\uff0c\u5239\u90a3\u4e4b \u95f4\uff0c\u4fbf\u53c8\u968f\u98ce\u800c\u901d\u3002 \u3000\u3000\u4ed6\u6df1\u6df1\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u4ece\u6765\u4e0d\u61c2\u5f97\u5173\u5fc3\u522b\u4eba\u7684\u4ed6\uff0c\u8fd9\u65f6\u5374\u5ffd\u7136\u4e3a\u51b0\u5ddd\u5929\u5973\u7948\u7977\u8d77\u6765\uff0c\u4ed6\u751f \u5e73\u4e00\u4e0d\u4fe1\u795e\uff0c\u4e8c\u4e0d\u4fe1\u4f5b\uff0c\u53ef\u4ee5\u8bf4\u4ece\u6765\u6ca1\u6709\u4fe1\u4ef0\u8fc7\u4ec0\u4e48\u4e1c\u897f\uff0c\u7136\u800c\u4ed6\u8fd9\u6b21\u5374\u662f\u8877\u5fc3\u7684\u4e3a\u51b0\u5ddd\u5929 \u5973\u800c\u7948\u7977\uff0c\u4f46\u613f\u5929\u4e0a\u771f\u6709\u4e00\u4e2a\u201c\u5168\u80fd\u201d\u7684\u795e\uff0c\u80fd\u591f\u964d\u798f\u7ed9\u51b0\u5ddd\u5929\u5973\uff0c\u8ba9\u5979\u548c\u5510\u7ecf\u5929\u4e00\u751f\u5e78 \u798f\u3002\u8fd9\u65f6\u4ed6\u5bf9\u5510\u7ecf\u5929\u7684\u6068\u610f\u4e5f\u50cf\u96ea\u82b1\u5728\u9633\u5149\u4e4b\u4e0b\u4e00\u6837\u7684\u878d\u89e3\u4e86\uff0c\u867d\u7136\u8c08\u4e0d\u4e0a\u597d\u611f\uff0c\u4f46\u4ed6\u5df2\u77e5 \u9053\u51b0\u5ddd\u5929\u5973\u662f\u771f\u5fc3\u559c\u7231\u5510\u7ecf\u5929\uff0c\u4ed6\u4e3a\u4e86\u51b0\u5ddd\u5929\u5973\u7684\u5e78\u798f\uff0c\u4e5f\u5c31\u613f\u610f\u5510\u7ecf\u5929\u5f97\u5e78\u798f\uff0c\u4e00\u5207\u5992\u5fcc \u8d2a\u55d4\uff0c\u5c3d\u90fd\u5347\u534e\uff0c\u5c3d\u90fd\u51c0\u5316\u3002 \u3000\u3000\u4ed6\u832b\u7136\u5730\u72ec\u81ea\u767b\u5c71\uff0c\u4f46\u89c1\u9f99\u7075\u77eb\u6b63\u5728\u4e0a\u9762\u75be\u884c\uff0c\u9f99\u7075\u77eb\u4f3c\u4e4e\u4e5f\u6000\u7740\u91cd\u91cd\u7684\u5fc3\u4e8b\uff0c\u811a\u6b65\u4e0d \u505c\u5730\u6500\u4e0a\u4e00\u5ea7\u5c71\u5cf0\u53c8\u4e00\u5ea7\u5c71\u5cf0\uff0c\u6839\u672c\u6ca1\u6709\u60f3\u5230\u4f1a\u6709\u4eba\u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u91d1\u4e16\u9057\u5ffd\u7136\u89c9\u5f97\u975e\u5e38\u5bc2 \u5bde\uff0c\u60f3\u51fa\u58f0\u547c\u558a\uff0c\u60f3\u627e\u4e00\u4e2a\u4eba\u503e\u8c08\uff0c\u7136\u800c\u4ed6\u7ec8\u4e8e\u8fd8\u662f\u5fcd\u4f4f\u4e86\u3002\u9f99\u7075\u77eb\u4e3a\u4ec0\u4e48\u9003\u4e0a\u5c71\u5462\uff1f\u4ed6\u5230 \u5e95\u662f\u600e\u6837\u4e00\u4e2a\u4eba\uff1f\u6000\u7740\u80f6\u539a\u7684\u597d\u5947\u5fc3\uff0c\u91d1\u4e16\u9057\u6084\u6084\u5730\u8ddf\u5728\u9f99\u7075\u77eb\u540e\u9762\u3002\u5ffd\u7136\u53c8\u662f\u4e00\u9635\u5927\u98ce\uff0c \u4e0a\u9762\u6709\u4e00\u5757\u78e8\u76d8\u5927\u7684\u51b0\u5757\u6447\u6447\u6b32\u5760\uff0c\u9f99\u7075\u77eb\u5374\u4f3c\u4e4e\u8fd8\u6ca1\u6709\u7559\u610f\uff0c\u770b\u4ed6\u8eab\u5f62\u8dc3\u843d\uff0c\u52bf\u5c06\u8e0f\u7740\u90a3 \u5757\u51b0\u5757\uff0c\u91d1\u4e16\u9057\u6361\u8d77\u4e24\u5757\u77f3\u5b50\uff0c\u500f\u5730\u63b7\u51fa\uff0c\u4e00\u5757\u63b7\u5728\u9f99\u7075\u77eb\u7684\u9762\u524d\uff0c\u5c06\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u53e6\u4e00\u5757 \u63b7\u5728\u90a3\u51b0\u5757\u4e0a\uff0c\u90a3\u51b0\u5757\u672c\u5c31\u6447\u6447\u6b32\u5760\uff0c\u7ed9\u77f3\u5934\u4e00\u649e\uff0c\u767b\u65f6\u201c\u8f70\u9686\u9686\u201d\u7684\u98de\u6eda\u4e0b\u6765\u3002\u4f46\u662f\u9f99\u7075 \u77eb\u832b\u7136\u56db\u987e\uff0c\u4e0d\u4e45\u53c8\u5411\u524d\u8d70\u4e86\u3002 \u3000\u3000\u9f99\u7075\u77eb\u56db\u987e\u65e0\u4eba\uff0c\u8fd8\u4ee5\u4e3a\u90a3\u662f\u5c71\u5cf0\u5076\u7136\u522e\u6765\u7684\u4e24\u5757\u77f3\u5b50\u3002\u4ed6\u8fd9\u65f6\u4e5f\u6b63\u662f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u53f9\u4e86 \u53e3\u6c14\u9053\uff1a\u201c\u8981\u662f\u8fd9\u6837\u8dcc\u6b7b\u4e86\uff0c\u5012\u4e5f\u5e72\u51c0\u3002\u201d\u4ed6\u5fc3\u4e2d\u6b63\u5728\u4eba\u5929\u4ea4\u6218\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u8fd9\u6b21\u4ece\u5c3c\u6cca\u5c14 \u519b\u8425\u4e2d\u9003\u8d70\uff0c\u5c3c\u6cca\u5c14\u738b\u5fc5\u5b9a\u8981\u8ffd\u6355\u4ed6\uff1b\u4ed6\u82e5\u662f\u56de\u5230\u62c9\u8428\uff0c\u6e05\u5ef7\u4e5f\u5fc5\u7136\u4e0d\u80af\u653e\u8fc7\u4ed6\u3002 \u3000\u3000\u9f99\u7075\u77eb\u6296\u4e00\u6296\u8eab\u4e0a\u7684\u96ea\u82b1\uff0c\u81ea\u601d\u81ea\u60f3\uff1a\u201c\u6211\u5373\u7b97\u6b7b\u5728\u798f\u5eb7\u5b89\u624b\u4e2d\uff0c\u4e5f\u80dc\u4e8e\u7ed9\u5c3c\u6cca\u5c14\u738b\u4f5c \u5080\u5121\u3002\u6211\u65e2\u5df2\u77e5\u9053\u5c3c\u6cca\u5c14\u738b\u8981\u8fdb\u5175\u897f\u85cf\u7684\u9634\u8c0b\uff0c\u5c82\u53ef\u4e0d\u56de\u53bb\u62a5\u544a\u3002\u54fc\uff0c\u54fc\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u5c45\u7136 \u60f3\u8981\u6211\u505a\u5f15\u72fc\u5165\u5ba4\u7684\u5de8\u5978\u5927\u6076\uff0c\u8fd9\u7b80\u76f4\u662f\u5bf9\u6211\u6700\u5927\u7684\u4fae\u8fb1\uff01\u201d\u5fc3\u4e2d\u6253\u5b9a\u4e3b\u610f\uff0c\u5728\u5c71\u4e0a\u8eb2\u8fc7\u8ffd \u5175\u4e4b\u540e\uff0c\u5c31\u4ece\u53e6\u4e00\u9762\u7ffb\u4e0b\u5c71\u5761\uff0c\u7ed5\u8fc7\u5580\u4ec0\u4f26\u8349\u539f\u56de\u62c9\u8428\u3002 \u3000\u3000\u96ea\u8d8a\u4e0b\u8d8a\u5927\uff0c\u5929\u8272\u6e10\u8fd1\u9ec4\u660f\uff0c\u7d2b\u8272\u7684\u665a\u971e\u62b9\u5728\u6ee1\u5c71\u4ea4\u9519\u7684\u51b0\u5ddd\u4e0a\uff0c\u851a\u6210\u4e03\u5f69\uff0c\u5947\u4e3d\u65e0 \u4fe6\uff0c\u9f99\u7075\u77eb\u65e0\u5fc3\u89c2\u8d4f\uff0c\u53ea\u662f\u60f3\u627e\u4e00\u4e2a\u5ca9\u7a74\uff0c\u4eca\u665a\u53ef\u4ee5\u6816\u8eab\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c9\u51b7\u98ce\u4e4b\u4e2d\uff0c\u6709\u4e00 \u80a1\u6e29\u6696\u6e7f\u6da6\u7684\u7a7a\u6c14\u6251\u9762\u800c\u6765\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u539f\u6765\u524d\u9762\u6709\u4e00\u80a1\u55b7\u6cc9\uff0c\u707c\u70ed\u7684\u6c34\u82b1\u88ab\u98ce\u5439\u6563\uff0c\u6620\u7740 \u9633\u5149\uff0c\u5f62\u6210\u4e00\u5708\u5708\u6a59\u8272\u7684\u3001\u6de1\u7d2b\u548c\u6d45\u7ea2\u7684\u82b1\u6735\uff0c\u5c31\u50cf\u62c9\u8428\u5e03\u8fbe\u62c9\u5bab\u5728\u8282\u65e5\u4e4b\u591c\u6240\u653e\u7684\u70df\u82b1\u3002 \u897f\u85cf\u5404\u5730\u672c\u591a\u6e29\u6cc9\uff0c\u4f46\u5728\u8fd9\u9ad8\u63d2\u5165\u4e91\u3001\u51b0\u5ddd\u904d\u5e03\u7684\u559c\u9a6c\u62c9\u96c5\u5c71\u5c71\u5cf0\u4e0a\u89c1\u5230\u707c\u70ed\u7684\u55b7\u6cc9\uff0c\u5374\u662f \u4e00\u5927\u5947\u666f\u3002 \u3000\u3000\u9f99\u7075\u77eb\u5fc3\u4e2d\u5927\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u6e29\u6cc9\u7684\u65c1\u8fb9\u8fc7\u591c\uff0c\u5012\u4e5f\u4e0d\u9519\u3002\u53ef\u60dc\u603b\u78b0\u4e0d\u7740\u9ec4\u7f8a\u548c\u5c71 \u9e21\uff0c\u8981\u4e0d\u7136\u8fde\u5f00\u6c34\u4e5f\u4e0d\u7528\u70e7\u3002\u201d\u8d70\u8fd1\u6e29\u6cc9\uff0c\u5ffd\u53c8\u95fb\u5f97\u98ce\u4e2d\u9001\u6765\u7684\u82b1\u9999\uff0c\u9f99\u7075\u77eb\u5927\u4e3a\u5947\u602a\uff0c\u5faa \u7740\u9999\u98ce\u6765\u5904\u8d70\u53bb\uff0c\u53ea\u89c1\u5c71\u5761\u4e0a\u6709\u4e00\u5bb6\u4eba\u5bb6\uff0c\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u82b1\u5703\uff0c\u56f4\u5899\u53ea\u6709\u4eba\u9ad8\uff1b\u82b1\u679d\u4f4e\u626d\uff0c \u7eff\u53f6\u7ea2\u82b1\u9690\u7ea6\u53ef\u89c1\u3002\u9f99\u7075\u77eb\u5fc3\u9053\uff1a\u201c\u6b64\u5904\u5730\u6c14\u6e29\u6696\uff0c\u6709\u82b1\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u6709\u8fd9\u6837\u7684\u4e00\u5bb6\u5b64\u96f6\u96f6 \u7684\u4eba\u5bb6\uff0c\u5374\u662f\u5947\u4e86\u3002\u201d\u8981\u77e5\u8fd9\u5730\u65b9\u867d\u7136\u8fd8\u672a\u5230\u534a\u5c71\uff0c\u4f46\u6bd4\u4e2d\u539f\u7684\u5927\u5c71\u5df2\u4e0d\u77e5\u8981\u9ad8\u51fa\u591a\u5c11\uff0c\u4e0d \u8981\u8bf4\u5c71\u9876\u7684\u51b0\u96ea\u4e98\u53e4\u4e0d\u5316\uff0c\u5c71\u8170\u4e5f\u662f\u7ec8\u5e74\u79ef\u96ea\uff0c\u7b49\u95f2\u4eba\u5bb6\uff0c\u600e\u80fd\u5728\u6b64\u5b89\u8eab\uff1f \u3000\u3000\u9f99\u7075\u77eb\u8d70\u8fd1\u524d\u53bb\uff0c\u53ea\u89c1\u56ed\u95e8\u865a\u63a9\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u95e8\u5c31\u5f00\u4e86\u3002\u5ffd\u542c\u5f97\u91cc\u9762\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u5a07\u58f0 \u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u770b\u6211\u79cd\u7684\u73ab\u7470\u5df2\u7ecf\u5f00\u4e86\u3002\u201d\u62ac\u5934\u4e00\u770b\uff0c\u4e24\u4e2a\u4eba\u90fd\u4e0d\u7981\u201c\u5475\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5a07\u5c0f\u73b2\u62e2\u7684\u5c11\u5973\uff0c\u7acb\u5728\u73ab\u7470\u4e1b\u4e2d\uff0c\u624b\u62c8\u4e00\u628a\u526a\u5200\uff0c\u6307\u7532\u4e0a\u8fd8\u6709\u6c61\u6ce5\uff0c\u4f3c\u4e4e\u662f\u521a \u521a\u7ed9\u82b1\u6811\u683d\u679d\u526a\u53f6\u3002\u90a3\u5c11\u5973\u9053\uff1a\u201d\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6211\u662f\u8ff7\u4e86\u8def\u7684\u730e\u4eba\u3002\u201d\u90a3 \u5c11\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u7684\u5927\u96ea\u5929\uff0c\u4f60\u4e0a\u5c71\u6253\u730e\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6211\u60f3\u730e\u4e00\u53ea\u91ce\u7266\u725b\u3002\u201d\u897f\u85cf\u7684\u91ce \u7266\u725b\u6709\u201c\u51b0\u6cb3\u4e4b\u821f\u201d\u7684\u79f0\u53f7\uff0c\u8089\u53ef\u98df\uff0c\u4e73\u53ef\u996e\uff0c\u76ae\u53ef\u5236\u9769\uff0c\u6bdb\u53ef\u5fa1\u5bd2\uff0c\u897f\u85cf\u7684\u730e\u4eba\u89c6\u4e3a\u5b9d \u8d1d\uff0c\u8fd9\u79cd\u7266\u725b\u6d12\u606f\u5728\u96ea\u5c71\u4e4b\u4e0a\uff0c\u9f99\u7075\u77eb\u7684\u8bf4\u8bdd\u5012\u53ef\u4ee5\u81ea\u5706\u5176\u8c0e\uff0c\u4f46\u4ed6\u65e2\u6ca1\u6709\u730e\u4eba\u7684\u88c5\u5907\uff0c\u800c \u4e14\u6700\u5927\u80c6\u7684\u730e\u4eba\u4e5f\u53ea\u6562\u5728\u4e0b\u9762\u7684\u7fa4\u5cf0\u4e4b\u95f4\u6253\u730e\uff0c\u4ece\u6765\u65e0\u4eba\u6562\u4e0a\u5230\u8fd9\u6837\u9ad8\u7684\u3002\u90a3\u5c11\u5973\u534a\u4fe1\u534a \u7591\uff0c\u4f46\u80fd\u89c1\u5230\u4e00\u4e2a\u5916\u4eba\uff0c\u5fc3\u4e2d\u5374\u53c8\u9ad8\u5174\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u5f85\u6211\u548c\u7239\u7239\u8bf4\u53bb\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4f60 \u5bb6\u4e2d\u6709\u591a\u5c11\u4eba\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5c31\u53ea\u6709\u6211\u548c\u7239\u7239\u3002\u55ef\uff0c\u4f60\u5728\u8fd9\u91cc\u5f85\u4e00\u4f1a\u513f\u3002\u201d\u9f99\u7075\u77eb\u5fc3\u4e2d\u7591 \u8651\uff0c\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u5df2\u5230\u4e86\u82b1\u5703\u5916\u8fb9\u3002 \u3000\u3000\u4e00\u4e2a\u8001\u5934\u7684\u58f0\u97f3\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u5426\u771f\u6b63\u7684\u730e\u4eba\uff0c\u65e2\u7136\u662f\u5c71\u4e0b\u7684\u8fdc\u5ba2\u5230\u6765\uff0c\u54b1\u4eec\u5c31 \u8be5\u6b3e\u5f85\u3002\u4f60\u4e5f\u4e0d\u5fc5\u95ee\u4ed6\u7684\u6765\u5386\u3002\u201d\u8bed\u58f0\u6781\u4f4e\uff0c\u4f3c\u4e4e\u662f\u51d1\u7740\u8033\u6735\u8bf4\u7684\u3002\u4f46\u9f99\u7075\u77eb\u662f\u6697\u5668\u5927\u540d\u5bb6 \u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u8033\u97f3\u6781\u597d\uff0c\u8fd9\u8001\u5934\u7684\u8bf4\u8bdd\u5374\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\u3002 \u3000\u3000\u56ed\u95e8\u63a8\u5f00\uff0c\u53ea\u89c1\u8fd9\u8001\u5934\u9aef\u7709\u5982\u96ea\uff0c\u8001\u6001\u9f99\u949f\uff0c\u80cc\u4e5f\u5fae\u5fae\u8be2\u697c\u4e86\u3002\u4f46\u5e72\u7626\u7684\u9762\u4e0a\u5374\u9690\u6cdb\u7ea2 \u5149\u3002\u9f99\u7075\u77eb\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u5c31\u662f\u9041\u8ff9\u5c71\u6797\u7684\u4e00\u4f4d\u4e16\u5916\u9ad8\u4eba\u3002\u201d\u606d\u606d\u656c\u656c\u7684\u4e0a\u524d \u884c\u793c\uff0c\u8bf7\u95ee\u59d3\u540d\u3002\u90a3\u8001\u5934\u9053\uff1a\u201c\u8001\u673d\u59d3\u65b9\uff0c\u5c45\u4f4f\u6b64\u95ee\uff0c\u4e09\u5341\u5e74\u4e86\uff0c\u540d\u5b57\u4e00\u5411\u6ca1\u4eba\u63d0\u8d77\uff0c\u65e9\u5df2 \u5fd8\u4e86\u3002\u201d\u9f99\u7075\u77eb\u81ea\u62a5\u59d3\u540d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0a\u5c71\u730e\u7266\u725b\uff0c\u4e0d\u60f3\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u95ef\u5230\u4ed9\u5c45\uff0c\u5b9e\u5728\u65e0 \u793c\u3002\u201d\u90a3\u65b9\u8001\u5934\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u58ee\u58eb\u82e5\u4e0d\u5acc\u7b80\u6162\uff0c\u5c31\u8bf7\u5728\u6b64\u6b47\u5bbf\u4e00\u5bb5\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u81ea\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u968f\u4e24\u7236\u5973\u767b\u5802\u5165\u5ba4\uff0c\u4f46\u89c1\u77f3\u5ba4\u91cc\u7a7a\u65e0\u6240\u6709\uff0c\u53ea\u662f\u5899\u58c1\u4e0a\u6302\u7740\u51e0\u5f20 \u517d\u76ae\uff0c\u5c4b\u89d2\u5806\u6709\u4e00\u4e9b\u8349\u836f\u3002\u90a3\u5c11\u5973\u6367\u51fa\u4e00\u5927\u76c6\u8089\u548c\u4e00\u5927\u76c6\u725b\u4e73\uff0c\u90a3\u8001\u8005\u7b11\u9053\uff1a\u201c\u4f60\u4e0a\u5c71\u6765\u8fd8 \u6ca1\u78b0\u5230\u7266\u725b\u5427\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u7266\u725b\u8981\u5728\u5927\u96ea\u521d\u6b62\u7684\u65f6\u5019\u51fa\u6765\uff0c\u5f88\u6709 \u8010\u5fc3\u7684\u730e\u4eba\u624d\u80fd\u5b88\u5230\u3002\u5c0f\u5973\u524d\u51e0\u5929\u5012\u5f88\u5e78\u8fd0\uff0c\u730e\u5230\u4e86\u4e00\u53ea\u7266\u725b\uff0c\u591f\u6211\u4eec\u5403\u51e0\u4e2a\u6708\u4e86\u3002\u4f60\u5c1d\u5c1d \u8fd9\u7266\u725b\u5976\uff0c\u8d81\u70ed\u559d\u6700\u597d\u3002\u201d\u9f99\u7075\u77eb\u5927\u5403\u4e00\u60ca\uff0c\u8981\u77e5\u897f\u85cf\u7684\u7266\u725b\u6bd4\u731b\u864e\u8fd8\u51f6\uff0c\u6700\u5c11\u8981\u96c6\u5408\u5341\u6570 \u730e\u4eba\u624d\u6562\u6355\u5b83\uff0c\u800c\u8fd9\u5c11\u5973\u5c45\u7136\u80fd\u730e\u7266\u725b\uff01\u9f99\u7075\u77eb\u867d\u7136\u65e9\u5c31\u6599\u5230\u8fd9\u4e24\u7236\u5973\u662f\u6709\u672c\u4e8b\u7684\u4eba\uff0c\u542c\u4ed6 \u4eec\u8bf4\u5f97\u5982\u6b64\u8f7b\u677e\uff0c\u5fc3\u4e2d\u8fd8\u662f\u4e0d\u514d\u9a87\u5f02\u3002\u9f99\u7075\u77eb\u6df1\u77e5\u6c5f\u6e56\u5fcc\u8bb3\uff0c\u867d\u6709\u6240\u7591\uff0c\u5374\u4e5f\u4e0d\u6562\u52a8\u95f4\u4ed6\u4eec \u7684\u6765\u5386\u3002 \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u58ee\u58eb\u6562\u72ec\u81ea\u4e0a\u5c71\u6355\u725b\uff0c\u52c7\u6c14\u53ef\u5609\u3002\u8170\u95f4\u957f\u5251\u4ea6\u975e\u51e1\u54c1\uff0c\u60f3\u6765\u5728\u6b66\u529f\u4e0a\u5b9a\u6709 \u6781\u6df1\u7684\u9063\u8be3\u4e86\u3002\u201d\u9f99\u7075\u77eb\u5fc3\u60f3\u4e0d\u8ba4\u4e5f\u4e0d\u884c\uff0c\u8c26\u8f9e\u5bf9\u9053\uff1a\u201c\u5b66\u662f\u5b66\u8fc7\u51e0\u5e74\uff0c\u54ea\u8bf4\u5f97\u4e0a\u4ec0\u4e48\u9020 \u8be3\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d\u8001\u5934\u5b50\u671b\u4e86\u5973\u513f\u4e00\u773c\uff0c\u90a3\u5c11\u5973\u60f3\u8d77\u7236\u4eb2\u4e0d\u8bb8\u5979\u76d8\u95ee\u5ba2 \u4eba\u6765\u5386\u7684\u5429\u5490\uff0c\u6c55\u6c55\u7684\u602a\u4e0d\u597d\u610f\u601d\u3002\u9f99\u7075\u77eb\u9053\uff1a\u201c\u662f\u56db\u5ddd\u4e00\u4f4d\u59d3\u5510\u7684\u5e08\u7236\u3002\u201d\u4ed6\u6ca1\u8bf4\u51fa\u5929\u4e0b \u6697\u5668\u7b2c\u4e00\u5bb6\u7684\u540d\u5934\uff0c\u90a3\u8001\u5934\u542c\u540e\uff0c\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u6ca1\u8ffd\u95ee\u3002 \u3000\u3000\u7266\u725b\u8089\u5fae\u5e26\u8165\u5473\uff0c\u9f99\u7075\u77eb\u5f88\u4e0d\u4e60\u60ef\uff0c\u628a\u56bc\u788e\u7684\u8089\u5410\u51fa\u6765\uff0c\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u9f99\u5148\u751f\u5403\u4e0d\u60ef \u5417\uff1f\u5510\u5927\u4fa0\u5012\u5f88\u559c\u6b22\uff01\u201d\u90a3\u8001\u5934\u6025\u5fd9\u53c8\u77aa\u4e86\u5973\u513f\u4e00\u773c\uff0c\u9f99\u7075\u77eb\u5927\u4e3a\u5403\u60ca\uff0c\u9053\uff1a\u201c\u54ea\u4f4d\u5510\u5927 \u4fa0\uff1f\u201d\u90a3\u8001\u5934\u5fae\u7b11\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u61c2\u5f97\u5251\u672f\u7684\u670b\u53cb\uff0c\u5c0f\u5973\u5c11\u89c1\u4e16\u9762\uff0c\u51e1\u662f\u672c\u4e8b\u6bd4\u5979\u597d\u7684\u4eba\uff0c\u4ed6 \u90fd\u5c0a\u4e3a\u5927\u4fa0\u7684\u3002\u201d\u9f99\u7075\u77eb\u5fc3\u9053\uff1a\u201c\u4e16\u95f4\u8db3\u5f53\u5f97\u4e0a\u5510\u5927\u4fa0\u79f0\u547c\u7684\uff0c\u53ea\u6709\u5510\u7ecf\u5929\u7236\u5b50\uff0c\u5510\u6653\u6f9c\u8fdc \u5728\u5929\u5c71\uff0c\u5510\u7ecf\u5929\u5c1a\u5728\u5c71\u5cf0\u5e95\u4e0b\uff0c\u4ed6\u4eec\u600e\u80fd\u89c1\u5230\uff1f\u201d\u5fc3\u4e2d\u7591\u4e91\u66f4\u91cd\u4e86\u3002 \u3000\u3000\u7266\u725b\u5976\u5012\u5f88\u53ef\u53e3\uff0c\u53ea\u662f\u6eda\u70ed\u70eb\u53e3\uff0c\u9f99\u7075\u77eb\u559d\u4e86\u4e00\u5927\u7897\uff0c\u989d\u4e0a\u6c81\u51fa\u6c57\u73e0\uff0c\u90a3\u8001\u5934\u9053\uff1a\u201c\u8d35 \u5ba2\u8bf7\u5bbd\u8863\u3002\u201d\u9f99\u7075\u77eb\u8131\u4e0b\u5916\u9762\u7684\u72d0\u76ae\u7f69\u888d\uff0c\u5ffd\u89c1\u90a3\u8001\u8005\u76ee\u5149\u6709\u5f02\uff0c\u7d27\u7d27\u7684\u76ef\u7740\u81ea\u5df1\uff0c\u795e\u60c5\u8be1 \u79d8\u4e4b\u6781\u3002\u9f99\u7075\u77eb\u7ecf\u5c3d\u5927\u98ce\u5927\u6d6a\uff0c\u5bf9\u7740\u8fd9\u6837\u7684\u76ee\u5149\uff0c\u4e5f\u4e0d\u7981\u5fae\u5fae\u53d1\u6296\u3002 \u3000\u3000\u9f99\u7075\u77eb\u611f\u89c9\u90a3\u8001\u8005\u7684\u76ee\u5149\uff0c\u7684\u89c6\u7740\u4ed6\u8170\u95f4\u7684\u4e00\u4ef6\u7269\u9970\uff0c\u90a3\u662f\u7528\u4e00\u5757\u901a\u4f53\u6676\u83b9\u7684\u767d\u7389\u96d5\u6210 \u7684\u7389\u72ee\u5b50\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u6837\u4e00\u4f4d\u4e16\u5916\u7684\u9ad8\u4eba\uff0c\u4e5f\u5782\u6d8e\u4e16\u95f4\u7684\u91d1\u7389\uff1f\u4f55\u51b5\u8fd9 \u7389\u72ee\u5b50\u4e5f\u5e76\u4e0d\u662f\u4ec0\u4e48\u5b9d\u7269\u3002\u53ef\u60dc\u8fd9\u662f\u6211\u7236\u4eb2\u4ec5\u5269\u4e0b\u6765\u7684\u9057\u7269\uff0c\u8981\u4e0d\u7136\u6211\u5012\u53ef\u4ee5\u9001\u7ed9\u4ed6\u3002\u201d\u90a3 \u5c11\u5973\u4e5f\u611f\u5230\u7236\u4eb2\u7684\u76ee\u5149\u6709\u5f02\uff0c\u8f7b\u8f7b\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u7266\u725b\u5976\u51c9\u5566\u3002\u201d\u76ee\u5149\u4e5f\u4e0d\u81ea\u7981\u7684\u8f6c\u5230\u4e86\u9f99 \u7075\u77eb\u7684\u9970\u7269\u4e0a\u3002 \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u627f\u8499\u8001\u4f2f\u6b3e\u5f85\uff0c\u65e0\u4ee5\u4e3a\u62a5\uff0c\u8fd9\u4e00\u4e32\u73cd\u73e0\u9001\u7ed9\u4ee4\u5a9b\uff0c\u4e0d\u6210\u656c\u610f\uff0c\u804a\u8868\u5bf8 \u5fc3\u3002\u201d\u4ed6\u820d\u4e0d\u5f97\u9001\u90a3\u7389\u72ee\u5b50\uff0c\u53e6\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u4e32\u73cd\u73e0\u3002\u90a3\u8001\u8005\u8be1\u5f02\u7684\u76ee\u5149\u4e00\u77ac\u5373\u901d\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5c71\u91ce\u4e2b\u5934\uff0c\u8981\u8fd9\u73cd\u73e0\u6709\u4f55\u7528\u5904\uff1f\u6234\u7ed9\u6591\u8c79\u548c\u7266\u725b\u770b\u5417\uff1f\u201d\u90a3\u5c11\u5973\u4ece\u672a\u89c1\u8fc7\u73cd\u73e0\uff0c\u95ea\u7740 \u597d\u5947\u7684\u76ee\u5149\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u600e\u4e48\u5149\u95ea\u95ea\u7684\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5b9d\u5251\u8d60\u4fa0\u5ba2\uff0c\u73cd\u73e0\u8d60\u7f8e \u4eba\u3002\u59d1\u5a18\u4f60\u6234\u4e0a\u8fd9\u4e32\u73cd\u73e0\uff0c\u4e00\u5b9a\u66f4\u597d\u770b\u5566\u3002\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u4e00\u4e9b\u753b\u4e0a\u7684\u7f8e\u4eba\uff0c\u54c8\uff0c \u626d\u626d\u634f\u634f\u5f31\u4e0d\u7981\u98ce\u7684\u6837\u5b50\uff0c\u6211\u624d\u4e0d\u613f\u50cf\u5979\u3002\u201d\u8fd9\u5c11\u5973\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u957f\u5927\uff0c\u538b\u6839\u513f\u5c31\u6ca1\u6709\u89c1\u8fc7 \u51e0\u4e2a\u5916\u4eba\uff0c\u4e1d\u6beb\u4e0d\u61c2\u4eba\u4e16\u4e4b\u4e8b\uff0c\u89c9\u5f97\u90a3\u4e32\u73cd\u73e0\u597d\u73a9\uff0c\u6839\u672c\u5c31\u4e0d\u8003\u8651\u5230\u4e16\u4fd7\u4e4b\u89c1\u2014\u2014\u4e0d\u597d\u4e71\u8981 \u522b\u4eba\u7684\u4e1c\u897f\u3002\u90a3\u8001\u8005\u76b1\u76b1\u7709\u5934\uff0c\u5ffd\u9053\uff1a\u201c\u96ea\u513f\uff0c\u4f60\u65e2\u7136\u6b22\u559c\uff0c\u5c31\u8c22\u8fc7\u8fd9\u4f4d\u5ba2\u4eba\u5427\u3002\u201d\u90a3\u5c11\u5973 \u5f53\u771f\u88e3\u887d\u4e00\u793c\uff0c\u9f99\u7075\u77eb\u6025\u5fd9\u8fd8\u793c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5230\u5e95\u8fd8\u662f\u8981\u4e86\u3002\u201d\u4f46\u5bf9\u90a3\u5c11\u5973\uff0c\u53ea\u611f\u5230\u5929\u771f \u65e0\u90aa\uff0c\u5374\u4e5f\u4e0d\u6562\u5b58\u534a\u70b9\u8f7b\u89c6\u4e4b\u5ff5\u3002 \u3000\u3000\u90a3\u8001\u8005\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5728\u897f\u85cf\u7684\u730e\u6237\uff0c\u8981\u4e70\u5357\u6d77\u7684\u73cd\u73e0\uff0c\u6211\u770b\u603b\u5f97\u5341\u53ea\u7266\u725b\u624d\u6362\u5f97\u8fd9\u4e48\u6837 \u7684\u4e00\u4e32\u73cd\u73e0\u5462\u3002\u201d\u9f99\u7075\u77eb\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u7b11\u81ea\u5df1\u6cc4\u9732\u4e86\u8eab\u4efd\uff0c\u4f46\u968f\u5373\u60f3\u5230\uff0c\u8fd9\u8001\u8005\u7edd\u975e\u5e38\u4eba\uff0c \u5b9a\u7136\u65e9\u5df2\u770b\u7a7f\u81ea\u5df1\u4e0d\u662f\u730e\u6237\uff0c\u90a3\u4e5f\u5c31\u968f\u4ed6\u53bb\u5427\u3002 \u3000\u3000\u90a3\u8001\u8005\u8ba9\u9f99\u7075\u77eb\u4f4f\u5728\u5916\u9762\u7684\u4e00\u95f4\u77f3\u5ba4\uff0c\u9760\u8fd1\u82b1\u5703\u3002\u9f99\u7075\u77eb\u8fd9\u4e00\u665a\u7ffb\u6765\u8986\u53bb\uff0c\u54ea\u7761\u5f97\u7740\uff0c \u4ed6\u5fc3\u4e2d\u601d\u5982\u6f6e\u6d8c\uff0c\u9996\u5148\u60f3\u5230\u8fd9\u4e24\u7236\u5973\u5947\u602a\u7684\u884c\u5f84\uff1b\u90a3\u8001\u8005\u8be1\u79d8\u7684\u76ee\u5149\u4f3c\u4e4e\u5728\u9ed1\u6697\u4e2d\u76ef\u7740\u4ed6\uff0c \u9f99\u7075\u77eb\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u5524\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u6446\u8131\u5f00\u8fd9\u8001\u8005\u7684\u5f71\u5b50\uff1b\u624b\u89e6\u8170\u95f4\u7684\u7389\u72ee\u5b50\uff0c\u5ffd\u7684\u53c8\u60f3\u8d77 \u4e86\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u60f3\u8d77\u4ed6\u7387\u9886\u767e\u4e07\u5927\u519b\u7684\u5a01\u98ce\uff0c\u60f3\u8d77\u4ed6\u88ab\u6e05\u5ef7\u6740\u622e\u7684\u4ec7\u6068\u3002\u9f99\u7075\u77eb\u53f9\u4e86\u53e3\u6c14\uff0c \u5fc3\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5f53\u5e74\u672c\u6765\u53ef\u4ee5\u81ea\u7acb\u79f0\u738b\uff0c\u53ef\u60dc\u4ed6\u6ca1\u8fd9\u4efd\u80c6\u6c14\u3002\u201d\u60f3\u8d77\u81ea\u5df1\u591a\u5e74\u7684\u82e6\u5fc3\u7b56\u5212\uff0c \u58ee\u5fd7\u96c4\u5fc3\uff0c\u5230\u800c\u4eca\u90fd\u4ed8\u4e4b\u6d41\u6c34\u3002\u601d\u6f6e\u63a5\u8fde\u4e0d\u65ad\uff0c\u5c71\u98ce\u9001\u6765\u7f15\u7f15\u82b1\u9999\uff0c\u9f99\u7075\u77eb\u7761\u4e0d\u7740\u89c9\uff0c\u7d20\u6027 \u62ab\u8863\u51fa\u6237\uff0c\u5230\u4e86\u82b1\u5703\u4e2d\u6f2b\u6b65\u3002 \u3000\u3000\u7a7f\u8fc7\u82b1\u4e1b\uff0c\u5ffd\u89c1\u6709\u4e00\u9053\u77ee\u5c0f\u7684\u7bf1\u7b06\u56f4\u7740\u56ed\u5b50\u7684\u4e00\u89d2\uff0c\u9f99\u7075\u77eb\u4e00\u65f6\u597d\u5947\uff0c\u63a2\u5934\u8fdb\u53bb\u4e00\u770b\uff0c \u8fd9\u4e00\u770b\u767b\u65f6\u4ee4\u4ed6\u5413\u5f97\u5446\u4e86\uff0c\u8fd9\u65f6\u4ed6\u518d\u4e5f\u65e0\u6687\u987e\u53ca\u90a3\u4e24\u7236\u5973\u662f\u4ec0\u4e48\u4eba\uff0c\u7acb\u5373\u5c31\u628a\u7bf1\u7b06\u5b8c\u5168\u62c6 \u6bc1\uff0c\u6708\u5149\u4e0b\u4e24\u5c0a\u77f3\u50cf\u663e\u9732\u51fa\u6765\uff0c\u4e00\u5c0a\u77f3\u50cf\u4f3c\u662f\u4e00\u4e2a\u6ee1\u65cf\u7684\u8d35\u4eba\uff0c\u53e6\u4e00\u5c0a\u77f3\u50cf\u7adf\u662f\u4ed6\u7684\u7236\u4eb2\u4e00 --\u5e74\u7fb9\u5c27\uff0c\u66f4\u5947\u602a\u7684\u662f\u4ed6\u7236\u4eb2\u90a3\u5c0a\u77f3\u50cf\u4e0a\u63d2\u7740\u4e24\u628a\u5c16\u5200\u3002 \u3000\u3000\u9f99\u7075\u77eb\u51e0\u4e4e\u6000\u7591\u81ea\u5df1\u662f\u8eab\u5728\u6076\u68a6\u4e4b\u4e2d\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u65e2\u662f\u6124\u6012\uff0c\u53c8\u662f\u60ca\u6050\uff0c\u5ffd\u89c9\u80cc\u540e\u8863\u895f \u5e26\u98ce\u4e4b\u58f0\uff0c\u9f99\u7075\u77eb\u5927\u543c\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u62f3\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u4f55\u6545\u4fae\u8fb1\u6211\u7684\u7236\u4eb2\uff01\u201d \u3000\u3000\u4e00\u62f3\u6253\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u5982\u4e2d\u8d25\u9769\uff0c\u9f99\u7075\u77eb\u88ab\u90a3\u8001\u5934\u8f7b\u8f7b\u4e00\u63a8\uff0c\u9000\u51fa\u6570\u6b65\uff0c\u56de \u5934\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u8001\u8005\u8eab\u8eaf\u6447\u6643\uff0c\u53e3\u89d2\u6c81\u51fa\u8840\u4e1d\uff0c\u5728\u51b7\u6708\u5bd2\u51b0\u7684\u6620\u7167\u4e4b\u4e0b\uff0c\u9762\u8272\u8d8a\u53d1\u663e\u5f97\u60e8\u767d \u53ef\u6015\u3002\u9f99\u7075\u77eb\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c1\u90a3\u8001\u8005\u7f13\u7f13\u4e3e\u8d77\u8863\u8896\uff0c\u62ed\u6389\u5634\u89d2\u7684\u8840\u4e1d\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u6599 \u5230\u5e74\u516c\u5b50\u6709\u6b64\u4e00\u95ee\uff0c\u8bf7\u4f60\u628a\u90a3\u67c4\u5c16\u5200\u62d4\u51fa\u6765\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u7565\u4e00\u8e0c\u8e87\uff0c\u7ec8\u4e8e\u53bb\u62d4\u90a3\u4e24\u67c4\u5c16\u5200\uff0c\u53ea\u89c1\u5200\u67c4\u89e6\u624b\u5373\u843d\uff0c\u539f\u6765\u5e74\u6df1\u65e5\u4e45\uff0c\u6728\u5934\u65e9\u5df2 \u8150\u673d\u4e86\u3002\u9f99\u7075\u77eb\u529b\u900f\u6307\u5c16\uff0c\u786c\u628a\u5c16\u5200\u62d4\u51fa\uff0c\u53ea\u89c1\u4e0a\u9762\u534a\u622a\u751f\u6ee1\u94c1\u9508\uff0c\u4e0b\u9762\u534a\u622a\u56e0\u63d2\u5728\u77f3\u50cf \u4e2d\uff0c\u5200\u53e3\u4ecd\u7136\u95ea\u7740\u5149\u8292\u3002\u90a3\u8001\u8005\u9053\uff1a\u201c\u8fd9\u4e24\u628a\u5200\u662f\u4e09\u5341\u5e74\u524d\uff0c\u63d2\u8fdb\u53bb\u7684\u3002\u90a3\u65f6\uff0c\u6211\u5bf9\u4ee4\u5c0a\u786e \u662f\u6028\u6bd2\u751a\u6df1\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u6211\u7236\u4eb2\u4e0e\u4f60\u4f55\u51a4\u4f55\u4ec7\uff0c\u4f60\u5982\u6b64\u51a4\u6bd2\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u5929\u4e0b\u7684\u4ec1 \u4eba\u4e49\u58eb\uff0c\u4e2a\u4e2a\u90fd\u662f\u4f60\u7236\u4eb2\u7684\u4ec7\u4eba\uff01\u6211\u5462\uff0c\u6211\u867d\u7136\u4e5f\u6068\u4f60\u7684\u7236\u4eb2\uff0c\u53ef\u662f\u8fd9\u4ec7\u6068\u53c8\u4e0e\u4e00\u822c\u4eba\u4e0d \u540c\uff0c\u8bf4\u8d77\u6765\u60ed\u6127\u5f97\u5f88\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u56e0\u4f55\u6068\u6211\u7236\u4eb2\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u65b9\u4eca\u660e\u8fd9\u4e2a\u540d\u5b57 \u4e48\uff1f\u201d\u9f99\u7075\u77eb\u4f3c\u4e4e\u542c\u5e08\u7236\u63d0\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff0c\u5374\u60f3\u4e0d\u8d77\u4ed6\u662f\u8c01\u4eba\u3002\u90a3\u8001\u8005\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e09 \u5341\u5e74\u4e16\u4e8b\u6ca7\u6851\uff0c\u73b0\u5728\u6211\u7684\u540d\u5b57\u4e5f\u6ca1\u4eba\u77e5\u9053\u4e86\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u7684\u7687\u5e1d\u662f\u4e7e \u9686\uff0c\u56db\u5341\u4e94\u5e74\u4e4b\u524d\uff0c\u4e7e\u9686\u7684\u7236\u4eb2\u96cd\u6b63\u8fd8\u662f\u56db\u7687\u5b50\u5141\u796f\uff0c\u90a3\u65f6\u8bf8\u7687\u5b50\u4e89\u4f4d\uff0c\u5141\u796f\u6700\u5927\u7684\u5f3a\u654c\u5c31 \u662f\u5341\u56db\u7687\u5b50\u5141\u63d0\u3002\u8fd9\u6545\u4e8b\u4f60\u542c\u8bf4\u8fc7\u5417\uff1f\u201d\u9f99\u7075\u77eb\u70b9\u70b9\u5934\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u6545\u4e8b\u6211\u542c\u8bf4\u8fc7\u3002\u201d\u65b9\u4eca \u660e\u9053\uff1a\u201c\u4e7e\u9686\u7684\u7956\u7236\u5eb7\u7199\u672c\u6765\u662f\u5199\u597d\u9057\u8bcf\u4f20\u4f4d\u7ed9\u5341\u56db\u7687\u5b50\u7684\uff0c\u540e\u6765\u96cd\u6b63\u5f97\u4f60\u7684\u7236\u4eb2\u548c\u56fd\u8205\u79d1 \u9686\u591a\u4e4b\u52a9\uff0c\u64c5\u6539\u9057\u8bcf\uff0c\u5c06\u2018\u4f20\u4f4d\u5341\u56db\u7687\u5b50\u2019\u8fd9\u51e0\u4e2a\u5b57\uff0c\u6539\u4e3a\u2018\u4f20\u4f4d\u4e8e\u56db\u7687\u5b50\uff0c\u96cd\u6b63\u624d\u5f97\u767b\u5927 \u5b9d\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4ed6\u4eec\u6ee1\u6d32\u4eba\u8c01\u505a\u7687\u5e1d\uff0c\u8fd8\u4e0d\u4e00\u6837\u3002\u4e0e\u8001\u767e\u59d3\u4f55\u5e72\uff1f\u201d \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4e0d\uff0c\u6700\u5c11\u4e0e\u4f60\u6211\u6709\u5173\u3002\u82e5\u4e0d\u662f\u96cd\u6b63\u505a\u7687\u5e1d\uff0c\u4f60\u7236\u4eb2\u4e0d\u4f1a\u8fd9\u6837\u5feb\u4fbf\u88ab\u6740\u5934\uff0c \u6211\u4e5f\u4e0d\u4f1a\u9003\u5230\u8fd9\u5c71\u4e0a\u6765\u3002\u201d\u9f99\u7075\u77eb\u9ed8\u7136\u4e0d\u8bed\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u597d\u5728\u96cd\u6b63\u4e5f\u7ed9\u4ed6\u7684\u4ec7\u4eba\u6740\u4e86\u3002\u201d \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u56db\u5341\u591a\u5e74\u4e4b\u524d\uff0c\u90a3\u65f6\u5341\u56db\u7687\u5b50\u624b\u4e0b\u6709\u4e24\u4e2a\u6700\u51fa\u540d\u7684\u6b66\u58eb\uff0c\u79f0\u4e3a\u519b\u4e2d\u4e8c\u5b9d\uff0c \u4e00\u4e2a\u53eb\u505a\u8f66\u8f9f\u90aa\uff0c\u540e\u6765\u6539\u4e8b\u65b0\u541b\uff0c\u6295\u987a\u4e86\u96cd\u6b63\uff0c\u53e6\u4e00\u4e2a\u5462\uff0c\u5bf9\u5341\u56db\u7687\u5b50\u59cb\u7ec8\u5fe0\u5fc3\u803f\u803f\u3002\u201d\u9f99 \u7075\u77eb\u9aa4\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u53eb\u505a\u795e\u62f3\u65b9\u4eca\u660e\u3002\u201d\u90a3\u8001\u8005\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3 \u5c31\u6b63\u662f\u8001\u673d\u4e86\u3002\u201d\u8bf4\u81f3\u8fd9\u91cc\uff0c\u90a3\u5c11\u5973\u5206\u82b1\u62c2\u53f6\uff0c\u7a7f\u5165\u82b1\u4e1b\uff0c\u9053\uff0c\u201c\u7239\u7239\uff0c\u8fd9\u4e48\u591c\u4e86\uff0c\u4f60\u8fd8\u8981 \u5ba2\u4eba\u966a\u4f60\u8bf4\u8bdd\u5417\uff1f\u54a6\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u65b9\u4eca\u660e\u518d\u62ed\u5e72\u51c0\u5634\u89d2\u6c81\u51fa\u6765\u7684\u8840\u4e1d\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff1f\u96ea\u513f\uff0c\u4f60\u4e5f\u542c\u542c\u3002\u201d\u987f\u4e86\u4e00 \u987f\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u96cd\u6b63\u64c5\u6539\u9057\u8bcf\uff0c\u6f5c\u767b\u5927\u5b9d\uff0c\u8fc7\u4e86\u51e0\u5e74\uff0c\u53c8\u8d81\u7740\u5341\u56db\u7687\u5b50\u897f\u5f81\u4e4b\u65f6\uff0c\u5c06\u4ed6\u5bb3 \u4e86\u3002\u5bb3\u5341\u56db\u7687\u5b50\u4e4b\u4e8b\uff0c\u6b63\u662f\u4f60\u7236\u4eb2\u66ff\u96cd\u6b63\u7b56\u5212\u7684\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u4f60\u7236\u4eb2\u593a\u4e86\u5341\u56db\u7687\u5b50\u7684\u5175\u6743\uff0c \u624d\u5f97\u4ee5\u6210\u4e3a\u5e74\u5927\u5c06\u519b\u3002\u201d\uff08\u6309\uff1a\u8bf8\u4e8b\u8be6\u89c1\u62d9\u8457\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\uff09\u9f99\u7075\u77eb\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u4f60\u5c31\u6068 \u96cd\u6b63\u4e0e\u6211\u7684\u7236\u4eb2\u4e86\u3002\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e0d\u80af\u6295\u987a\uff0c\u96cd\u6b63\u4e5f\u6068\u6781\u4e86\u6211\uff0c\u6211\u624d\u9003\u5230\u897f\u85cf\u3002 \u9003\u5230\u897f\u85cf\u4e4b\u540e\uff0c\u6211\u8fd8\u77e2\u5fd7\u62a5\u4ec7\uff0c\u5a36\u4e86\u5979\u7684\u6bcd\u4eb2\uff0c\u5e0c\u671b\u751f\u4e0b\u4e00\u4e2a\u513f\u5b50\uff0c\u6740\u4f60\u7684\u7236\u4eb2\u548c\u96cd\u6b63\u3002\u201d \u90a3\u5c11\u5973\u60ca\u53eb\u8d77\u6765\uff0c\u65b9\u4eca\u660e\u7b11\u9053\uff1a\u201c\u96ea\u513f\uff0c\u4e0d\u5fc5\u9a87\u6015\uff0c\u8fd9\u4e24\u4e2a\u4ec7\u4eba\u90fd\u6b7b\u4e86\u4e09\uff0b\u591a\u5e74\u4e86\uff0c\u90a3\u65f6\u6211 \u6d88\u606f\u9694\u9602\uff0c\u5c1a\u81ea\u5ff5\u5ff5\u590d\u4ec7\uff0c\u8fd8\u672a\u5a36\u4f60\u7684\u6bcd\u4eb2\u5462\u3002\u201d\u505c\u4e86\u4e00\u4e0b\uff0c\u7eed\u9053\uff1a\u201c\u96cd\u6b63\u6b7b\u540e\u51e0\u5e74\uff0c\u5510\u5927 \u4fa0\u6765\u63a2\u671b\u6211\uff0c\u6211\u624d\u77e5\u9053\u6d88\u606f\u3002\u4f46\u6211\u7684\u540d\u5b57\uff0c\u8fd8\u662f\u88ab\u671d\u5ef7\u5217\u4e3a\u94a6\u72af\u3002\u6211\u4e5f\u65e9\u5fc3\u7070\u610f\u51b7\uff0c\u4f60\u6bcd\u4eb2 \u5bf9\u6211\u5f88\u597d\uff0c\u6211\u4e5f\u5c31\u628a\u897f\u85cf\u5f53\u6210\u6211\u7684\u5bb6\u4e61\u5566\u3002\u6211\u521d\u6765\u81f3\u8fd9\u91cc\u9690\u5c45\u65f6\uff0c\u5bf9\u5e74\u7fb9\u5c27\u7684\u6068\u5c1a\u672a\u5168\u6d88\uff0c \u56e0\u6b64\u523b\u4e86\u4ed6\u7684\u77f3\u50cf\uff0c\u7ec3\u4e60\u98de\u5200\u3002\u5176\u5b9e\u4eba\u6b7b\u4ec7\u706d\uff0c\u5728\u6b7b\u4eba\u8eab\u4e0a\u53d1\u6c14\uff0c\u5b9e\u662f\u65e0\u804a\u5f97\u5f88\uff0c\u5510\u5927\u4fa0\u4e5f \u66fe\u529d\u544a\u8fc7\u6211\u3002\u5e74\u516c\u5b50\uff0c\u4eca\u665a\u6211\u628a\u4e8b\u60c5\u8bf4\u660e\uff0c\u6211\u662f\u8bda\u5fc3\u8ba9\u4f60\u6253\u4e00\u62f3\u6d88\u6c14\u7684\u3002\u201d\u90a3\u5c11\u5973\u8bf7\u9f99\u7075\u77eb \u5750\u4e0b\uff0c\u8fd9\u65f6\u9f99\u7075\u77eb\u624d\u77e5\u9053\u5979\u7684\u540d\u5b57\u53eb\u505a\u65b9\u96ea\u541b\u3002 \u3000\u3000\u9f99\u7075\u77eb\u6068\u610f\u6d88\u4e86\u4e00\u534a\uff0c\u4ecd\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u56e0\u6b64\u6068\u6211\u7236\u4eb2\u3002\u4f60\u6548\u5fe0\u5341\u56db\u7687\u5b50\uff0c\u6211\u7236\u4eb2\u6548\u5fe0 \u56db\u7687\u5b50\uff0c\u53ea\u80fd\u8bf4\u662f\u5404\u4e3a\u5176\u4e3b\uff0c\u4f60\u4f55\u4ee5\u6028\u6bd2\u6df1\u539a\u5982\u65af\uff1f\u201d \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5f53\u5e74\u6548\u5fe0\u5341\u56db\u7687\u5b50\uff0c\u8bf4\u8d77\u6765\u4e5f\u8be5\u4e3a\u4eba\u8d23\u9a82\u3002\u4f46\u6bd4\u8d77\u4f60\u7684\u7236\u4eb2\u5374\u5927 \u4e0d\u76f8\u540c\u3002\u6211\u4ec5\u662f\u5341\u56db\u7687\u5b50\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u4f60\u7236\u4eb2\u5374\u662f\u4e2a\u5927\u5c06\u519b\u3002\u4ed6\u7ed9\u96cd\u6b63\u51fa\u4e86\u8bb8\u591a\u574f\u4e3b\u610f\uff0c\u6740 \u6233\u5929\u4e0b\u4e49\u58eb\u3002\u538b\u5f97\u8001\u767e\u59d3\u62ac\u4e0d\u8d77\u5934\u6765\uff0c\u4ed6\u53c8\u80cc\u53db\u5e08\u95e8\uff0c\u706b\u70e7\u5c11\u6797\u5bfa\uff0c\u5c61\u5174\u5927\u72f1\uff0c\u6b8b\u5bb3\u65e0\u8f9c\uff0c \u8fd9\u79cd\u79cd\u4e8b\u60c5\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u9f99\u7075\u77eb\u81ea\u5e7c\u53d7\u5510\u5bb6\u629a\u517b\uff0c\u5510\u5bb6\u6015\u4f24\u4ed6\u7684\u5fc3\uff0c\u4ece\u6ca1\u548c\u4ed6\u8bf4\u8d77\u4ed6\u7236\u4eb2 \u7684\u4e8b\u3002\u8fd8\u662f\u9f99\u7075\u77eb\u957f\u5927\u6210\u4eba\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u81ea\u5df1\u7684\u7236\u4eb2\u662f\u5e74\u7fb9\u5c27\uff0c\u4f46\u4ea6\u4ec5\u4ec5\u77e5\u9053\u81ea\u5df1\u7684\u7236\u4eb2\u662f \u4e2a\u624b\u63e1\u767e\u4e07\u519b\u7b26\u7684\u5927\u5c06\u519b\u548c\u4ed6\u88ab\u96cd\u6b63\u60e8\u6740\u8fd9\u4e24\u4ef6\u4e8b\u800c\u5df2\uff0c\u81f3\u4e8e\u4ed6\u7236\u4eb2\u505a\u8fc7\u7684\u8bb8\u591a\u574f\u4e8b\uff0c\u56e0\u6ca1 \u4eba\u5bf9\u4ed6\u8bf4\uff0c\u4ed6\u81ea\u7136\u4e5f\u4e0d\u77e5\u9053\u3002\u8fd9\u65f6\u542c\u5f97\u65b9\u4eca\u660e\u4e00\u6869\u6869\u63d0\u8d77\uff0c\u6709\u5982\u4e07\u7bad\u7a7f\u5fc3\uff0c\u60f3\u8d77\u81ea\u5df1\u4e00\u5411\u5d07 \u62dc\u7684\u7236\u4eb2\uff0c\u7adf\u662f\u4e2a\u56fd\u4eba\u7686\u66f0\u53ef\u6740\u7684\u56fd\u8d3c\uff0c\u60b2\u6124\u7f9e\u60ed\uff0c\u987f\u662f\u5145\u6ee1\u80f8\u81c6\uff0c\u6068\u4e0d\u5f97\u6398\u4e2a\u5730\u6d1e\u94bb\u4e86\u4e0b \u53bb\u3002\u65b9\u4eca\u660e\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7236\u4eb2\u7684\u7f6a\u8fc7\uff0c\u4e0d\u5173\u513f\u5b50\u7684\u4e8b\u3002\u4f55\u51b5\u4f60\u7236\u4eb2\u6b7b\u65f6\uff0c\u4f60\u8fd8\u662f\u4e2a\u672a\u6ee1\u5468\u5c81 \u7684\u5a74\u5b69\u3002\u524d\u4e9b\u65f6\u5510\u5927\u4fa0\u81f3\u6b64\uff0c\u4e5f\u66fe\u63d0\u8d77\u4f60\uff0c\u4ed6\u4ece\u5510\u5c11\u4fa0\u6253\u542c\u5230\u7684\u6d88\u606f\u77e5\u9053\u4f60\u5df2\u6539\u540d\u6362\u59d3\uff0c\u5728 \u897f\u85cf\u6709\u6240\u56fe\u8c0b\uff0c\u7b97\u5f97\u662f\u4e00\u4e2a\u4eba\u624d\u3002\u4ed6\u8fd8\u66ff\u4f60\u9ad8\u5174\u5462\u3002\u53ea\u662f\u4ed6\u542c\u8bf4\u4f60\u60f3\u5728\u897f\u85cf\u8d77\u4e8b\uff0c\u4ed6\u5f88\u4e0d\u8d5e \u6210\u3002\u201d\u9f99\u7075\u77eb\u6709\u5982\u6ce5\u5851\u6728\u96d5\uff0c\u80f8\u4e2d\u767e\u611f\u4ea4\u96c6\uff0c\u60f3\u7684\u53ea\u662f\u600e\u6837\u66ff\u7236\u4eb2\u8d4e\u7f6a\uff0c\u54ea\u8fd8\u6709\u4e89\u593a\u6c5f\u5c71\u7684 \u58ee\u5fd7\u96c4\u5fc3\uff1f\u597d\u534a\u664c\u624d\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u3001\u6211\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\uff1f\u201d\u597d\u8270\u96be\u624d\u8bf4\u5f97\u51fa\u4ed6\u7236\u4eb2 \u7684\u540d\u5b57\u3002\u4f46\u89c9\u8fd9\u4e09\u4e2a\u5b57\u5bf9\u4ed6\u4e43\u662f\u662f\u4e00\u79cd\u803b\u8fb1\u3002 \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u6211\u66fe\u89c1\u8fc7\u4f60\u7236\u4eb2\u4f69\u6234\u8fc7\u8fd9\u4e2a\u7389\u72ee\u5b50\u3002\u55ef\uff0c\u6211\u4eca\u665a\u82e5\u8981\u5bb3\u4f60\uff0c\u90a3\u662f\u6613\u5982\u53cd \u638c\u3002\u73b0\u5728\u4f60\u7684\u6c14\u6d88\u4e86\u5427\uff1f\u201d\u9f99\u7075\u77eb\u6f78\u7136\u6cea\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u8001\u4e08\uff01\u201d\u6781\u4e3a\u6094\u6068\u6253\u4ed6\u90a3\u62f3\u3002 \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u73b0\u5728\u6211\u5f97\u542c\u4f60\u8bf4\u4e86\uff0c\u4f60\u53c8\u662f\u56e0\u4f55\u9003\u4e0a\u6b64\u5c71\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5c3c\u6cca\u5c14\u7684\u5927\u519b \u5c31\u9a7b\u5c6f\u5728\u4e0b\u9762\u7684\u5c71\u8c37\uff0c\u6211\u5bf9\u671d\u5ef7\u5e76\u65e0\u597d\u611f\uff0c\u4f46\u603b\u4e0d\u80fd\u89c1\u5f02\u56fd\u5165\u4fb5\u3002\u201d\u731b\u7684\u60f3\u8d77\u7236\u4eb2\u5f53\u5e74\u66fe\u5e26 \u5927\u519b\u7ed9\u6e05\u5ef7\u56db\u5904\u201c\u5e73\u4e71\u201d\uff0c\u8ba9\u6ee1\u6d32\u7687\u5e1d\u53ef\u4ee5\u5750\u7a33\u9f99\u5ef7\uff0c\u65e0\u5f02\u4e3a\u864e\u4f5c\u6005\u3002\u4e0d\u7981\u6697\u602a\u81ea\u5df1\u7cca\u6d82\uff0c \u591a\u5c11\u5e74\u6765\uff0c\u4f55\u4ee5\u603b\u6ca1\u60f3\u5230\u8fd9\u7b49\u6c11\u65cf\u7684\u5927\u4e49\u3002 \u3000\u3000\u65b9\u4eca\u660e\u773c\u775b\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u5510\u5927\u4fa0\u6ca1\u770b\u9519\uff0c\u4f60\u679c\u7136\u4e0d\u50cf\u4f60\u7684\u7236\u4eb2\uff01\u201d\u90a3\u5c11\u5973\u66ff\u9f99\u7075\u77eb\u96be \u8fc7\uff0c\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u7239\u7239\uff0c\u4f60\u5c3d\u63d0\u4eba\u5bb6\u7684\u7236\u4eb2\u505a\u4ec0\u4e48\uff1f\u201d\u65b9\u4eca\u660e\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0a\u4ee3 \u51a4\u4ec7\u4eca\u4ee3\u89e3\uff0c\u9f99\u751f\u4e5d\u79cd\u5404\u4e0d\u540c\u3002\u4f60\u4eec\u62c9\u62c9\u624b\u5427\u3002\u201d\u90a3\u5c11\u5973\u5929\u771f\u65e0\u90aa\uff0c\u5766\u7136\u7684\u4f38\u624b\u548c\u9f99\u7075\u77eb\u4e00 \u63e1\u3002\u65b9\u4eca\u660e\u4eca\u665a\u7acb\u610f\u548c\u9f99\u7075\u77eb\u5316\u89e3\uff0c\u5176\u5b9e\u8fd8\u53e6\u6709\u7528\u5fc3\u3002\u4ed6\u548c\u5973\u513f\u9690\u5c45\u6df1\u5c71\uff0c\u96be\u9009\u4f73\u5a7f\uff0c\u542c\u5510 \u6653\u6f9c\u8bf4\u8d77\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\u4e0e\u7236\u4e0d\u540c\uff0c\u5fc3\u4e2d\u65e9\u6709\u5370\u8c61\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u679c\u662f\u4e00\u8868\u4eba\u624d\uff0c\u867d\u7136\u4ed6\u6bd4\u5973\u513f \u5927\u4e0a\u5341\u591a\u5e74\uff0c\u4e5f\u8fd8\u5339\u914d\u3002\u53ea\u662f\u81ea\u5df1\u521a\u521a\u88ab\u4ed6\u6253\u4e86\u4e00\u62f3\uff0c\u5a5a\u4e8b\u53c8\u600e\u597d\u610f\u601d\u51fa\u53e3\u3002\u53ea\u597d\u7b49\u5f85\u5c06\u6765 \u518d\u8bf7\u5510\u6653\u6f9c\u64ae\u5408\u4e86\u3002 \u3000\u3000\u9f99\u7075\u77eb\u5fc3\u795e\u7a0d\u5b9a\uff0c\u95ee\u9053\uff1a\u201c\u8001\u4e08\u6240\u8bf4\u7684\u5510\u5927\u4fa0\u662f\u5426\u5373\u5929\u5c71\u6d3e\u7684\u638c\u95e8\u5510\u6653\u6f9c\uff1f\u201d\u65b9\u4eca\u660e \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u662f\u5c06\u8fd1\u56db\u5341\u5e74\u7684\u8001\u670b\u53cb\u4e86\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4ed6\u4e5f\u5230\u4e86\u8fd9\u91cc\u5417\uff1f\u201d\u65b9\u4eca\u660e \u9053\uff1a\u201c\u4e0d\u4e45\u4e4b\u524d\u624d\u6765\u8fc7\u3002\u201d\u6b63\u60f3\u518d\u8bf4\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\uff0c\u65b9\u4eca\u660e\u9053\uff1a\u201c\u6765\u4eba\u8e0f\u96ea \u65e0\u75d5\u7684\u529f\u592b\u8fd8\u672a\u5230\u5bb6\uff0c\u4f46\u4e5f\u7b97\u4e0d\u5f31\u4e86\u3002\u201d\u9f99\u7075\u77eb\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u8fd9\u5fc5\u7136\u662f\u5c3c\u6cca\u5c14\u738b\u6d3e\u6b66\u58eb \u6765\u8ffd\u6355\u6211\uff01\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u9f99\u5148\u751f\uff0c\u54c8\uff0c\u6211\u8fd8\u662f\u53eb\u4f60\u9f99\u5148\u751f\u7684\u597d\uff0c\u6709\u6211\u4eec\u7236\u5973\u5728\u8fd9\u513f\uff0c\u7edd\u4e0d \u80fd\u8ba9\u4f60\u88ab\u6355\uff0c\u53ea\u6050\u672a\u5fc5\u5c31\u662f\u4f60\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u811a\u6b65\u58f0\u5df2\u5230\u5916\u9762\uff0c\u6709\u4eba\u6253\u77f3\u5c4b\u7684\u5927\u95e8\uff0c\u65b9\u4eca\u660e\u6c89\u58f0\u559d\u9053\uff1a\u201c\u6211\u5728\u8fd9\u513f\uff01\u201d\u53ea \u542c\u5f97\u6709\u4eba\u7528\u897f\u85cf\u8bdd\u9a82\u9053\uff1a\u201c\u8001\u5934\u513f\uff0c\u4f60\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff0c\u80c6\u6562\u6253\u4f24\u63d0\u6469\u8fbe\u591a\u7684\u95e8\u4e0b\uff0c\u5feb\u5feb\u51fa \u6765\u9886\u6b7b\uff01\u201d\u9f99\u7075\u77eb\u4e00\u6014\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u627e\u4f60\u7684\u3002\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4e0d\u5173\u4f60\u4e8b\uff0c\u5f85\u6211\u53bb\u4f1a\u4ed6 \u4eec\u3002\u201d\u63d0\u9ad8\u58f0\u97f3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u6b63\u60f3\u627e\u4eba\u677e\u677e\u5462\u3002\u201d\u4e00\u7a9c\u8eab\uff0c\u6253\u5f00\u56ed\u95e8\uff0c\u51b2 \u4e86\u51fa\u53bb\uff0c\u9f99\u7075\u77eb\u5c82\u80af\u8ba9\u4ed6\u5b64\u8eab\u5bf9\u654c\uff0c\u4e0e\u90a3\u5c11\u5973\u4e5f\u7acb\u5373\u8ddf\u5728\u65b9\u4eca\u660e\u8eab\u540e\uff0c\u98de\u51fa\u56f4\u5899\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u5761\u4e0a\u9ad8\u9ad8\u77ee\u77ee\u7684\u7ad9\u7740\u56db\u4e94\u4e2a\u4eba\uff0c\u9664\u4e86\u4e00\u4e2a\u8bf4\u897f\u85cf\u8bdd\u7684\u4e4b\u5916\uff0c\u5176\u4ed6\u90fd\u662f\u5947\u5f62\u602a\u72b6\u7684 \u5f02\u90a6\u4eba\uff0c\u4e00\u89c1\u65b9\u4eca\u660e\u51fa\u6765\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u7acb\u523b\u6251\u4e0a\uff0c\u9f99\u7075\u77eb\u5927\u6012\uff0c\u957f\u5251\u51fa\u9798\uff0c\u62a2\u5148\u52a8\u624b\uff0c\u5ffd\u89c9 \u4e24\u80a1\u638c\u529b\uff0c\u5de6\u53f3\u56de\u65cb\uff0c\u957f\u5251\u51e0\u4e4e\u62ff\u634f\u4e0d\u5b9a\u3002\u9f99\u7075\u77eb\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u201d\u53ea \u89c1\u65b9\u4eca\u660e\u201c\u547c\u201d\u7684\u4e00\u62f3\u6253\u51fa\uff0c\u76f8\u8ddd\u5341\u6b65\uff0c\u62a2\u5148\u6251\u4e0a\u7684\u90a3\u4e24\u4e2a\u756a\u50e7\u8fd8\u662f\u7ed9\u62f3\u98ce\u51b2\u5f97\u6447\u6447\u6643\u6643\uff01 \u3000\u3000\u9f99\u7075\u77eb\u5fc3\u4e2d\u8d5e\u9053\uff1a\u201c\u795e\u62f3\u4e4b\u540d\uff0c\u786e\u4e0d\u865a\u4f20\uff01\u201d\u53e6\u4e24\u4e2a\u4eba\u53c8\u4ece\u4fa7\u7ffc\u6284\u4e0a\uff0c\u56db\u80a1\u638c\u529b\u4e00\u5408\uff0c \u65b9\u4eca\u660e\u5e94\u4ed8\u6e10\u89c1\u8270\u96be\uff0c\u9f99\u7075\u77eb\u4e0e\u90a3\u5c11\u5973\u4e0a\u524d\u52a9\u6218\uff0c\u9f99\u7075\u77eb\u5185\u529f\u6df1\u6e5b\uff0c\u867d\u7136\u8fd8\u6bd4\u4e0d\u4e0a\u9876\u513f\u5c16\u513f \u7684\u6b66\u6797\u540d\u5bbf\uff0c\u4f46\u4ea6\u4e0d\u8fc7\u7565\u900a\u4e8e\u5510\u7ecf\u5929\u7b49\u4eba\u800c\u5df2\uff0c\u63d0\u6469\u8fbe\u591a\u95e8\u4e0b\u7684\u9634\u9633\u638c\u529b\uff0c\u867d\u7136\u5389\u5bb3\uff0c\u8fc7\u62db \u4e0d\u4e45\uff0c\u4ed6\u5df2\u5999\u609f\u5176\u7406\uff0c\u987a\u7740\u90a3\u80a1\u638c\u529b\u7684\u56de\u65cb\u4e4b\u52bf\uff0c\u8fd0\u5251\u51fb\u523a\uff0c\u4e5f\u4e0d\u89c1\u600e\u6837\u5403\u529b\u3002\u90a3\u5c11\u5973\u4f7f\u7684 \u662f\u4e00\u6839\u91d1\u4e1d\u8f6f\u97ad\uff0c\u529f\u592b\u867d\u7136\u8f83\u5f31\uff0c\u4f46\u97ad\u6cd5\u7075\u6d3b\u5201\u94bb\uff0c\u4e00\u4e08\u4e4b\u5185\uff0c\u654c\u4eba\u8fd1\u4e0d\u4e86\u8eab\uff0c\u4e5f\u662f\u4e2a\u5f97\u529b \u7684\u52a9\u624b\u3002 \u3000\u3000\u6218\u5230\u5206\u9645\uff0c\u5ffd\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u597d\u50cf\u4e00\u4e2a\u6781\u5927\u7684\u6c14\u7403\u7206\u88c2\u4e00\u822c\uff0c\u5de6\u7ffc\u4e24\u4e2a\u654c\u4eba\u671d\u5929\u8dcc \u4e0b\uff0c\u9f99\u7075\u77eb\u957f\u5251\u659c\u523a\uff0c\u5374\u88ab\u53f3\u7ffc\u90a3\u4e24\u4e2a\u654c\u4eba\u6321\u56de\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u8dcc\u5012\u7684\u53e6\u4e24\u4eba\u5df2\u6eda\u4e0b\u5c71\u5761\uff0c\u53f3 \u7ffc\u90a3\u4e24\u4e2a\u654c\u4eba\u4ee5\u9000\u4e3a\u8fdb\uff0c\u731b\u53d1\u4e09\u638c\uff0c\u5c06\u9f99\u7075\u77eb\u8feb\u9000\u6570\u6b65\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e5f\u6025\u5fd9\u8d70\u4e86\u3002 \u3000\u3000\u4f46\u542c\u5f97\u65b9\u4eca\u660e\u6c14\u5598\u5401\u5401\uff0c\u6447\u5934\u53f9\u9053\uff1a\u201c\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\uff01\u201d\u539f\u6765\u4ed6\u4ee5\u5185\u5bb6\u771f\u529b\uff0c\u7834\u4e86\u654c \u4eba\u7684\u9634\u9633\u638c\u529b\uff0c\u867d\u7136\u5f97\u80dc\uff0c\u5143\u6c14\u5df2\u662f\u5927\u4f24\uff0c\u9f99\u7075\u77eb\u548c\u90a3\u5c11\u5973\u6276\u4ed6\u56de\u8f6c\u77f3\u5ba4\uff0c\u65b9\u4eca\u660e\u9759\u5750\u8fd0 \u529f\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u6c14\u606f\u624d\u6e10\u6e10\u8c03\u5300\u3002 \u3000\u3000\u9f99\u7075\u77eb\u95ee\u9053\uff1a\u201c\u8fd9\u5e72\u4eba\u662f\u751a\u6765\u5934\uff1f\u600e\u7684\u8981\u548c\u8001\u4e08\u4f5c\u5bf9\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u8c01\u77e5\u9053\u5462\uff1f\u4ed6\u4eec \u53bb\u4e86\u4e00\u6279\uff0c\u53c8\u6765\u4e00\u6279\uff0c\u5148\u540e\u5df1\u6709\u4e09\u6b21\u4e86\u3002\u7b2c\u4e00\u6b21\u662f\u4e00\u4e2a\u7ea2\u53d1\u7684\u756a\u50e7\u5e26\u540c\u4e00\u4e2a\u897f\u85cf\u7684\u901a\u8bd1\u6765\uff0c \u8bf4\u4ed6\u7684\u5e08\u7236\u8981\u8fd9\u4e2a\u5730\u65b9\uff0c\u53eb\u6211\u4eec\u5c06\u77f3\u5ba4\u548c\u82b1\u5703\u90fd\u8ba9\u7ed9\u4ed6\uff0c\u8fd8\u8981\u8001\u673d\u548c\u5c0f\u5973\u90fd\u505a\u4ed6\u4eec\u7684\u5974\u5a62\uff0c \u54fc\uff0c\u54fc\uff0c\u8001\u673d\u6d3b\u4e86\u516d\u5341\u591a\u5c81\uff0c\u8fd8\u6ca1\u89c1\u8fc7\u8fd9\u6837\u9738\u9053\u7684\u4eba\uff0c\u6ca1\u8bf4\u7684\uff0c\u53ea\u6709\u7ed9\u4ed6\u4eec\u4e00\u987f\u597d\u6253\uff0c\u5c06\u4ed6 \u4eec\u6253\u8dd1\u4e86\u3002\u7b2c\u4e8c\u6b21\u6709\u4e09\u4e2a\u4eba\u6765\uff0c\u5176\u4e2d\u4e24\u4e2a\u529f\u751a\u9ad8\uff0c\u8001\u673d\u7236\u5973\u4e24\u4eba\u548c\u4ed6\u4eec\u6253\u4e86\u534a\u5929\uff0c\u62b5\u6321\u4e0d \u4f4f\uff0c\u5e78\u597d\u5510\u5927\u4fa0\u6070\u5de7\u4e0a\u5c71\u627e\u6211\uff0c\u7528\u4e24\u652f\u5929\u5c71\u795e\u8292\uff0c\u5c06\u529f\u529b\u6700\u9ad8\u7684\u4e24\u4eba\u6253\u4f24\uff0c\u76f4\u5c06\u4ed6\u4eec\u8d76\u5230\u5c71 \u811a\u3002\u8fd9\u4e00\u6b21\u53c8\u591a\u6765\u4e86\u4e00\u4e2a\uff0c\u5e78\u4e8f\u6709\u9f99\u5148\u751f\u76f8\u52a9\uff0c\u8981\u4e0d\u7136\u8001\u673d\u7ecf\u8425\u4e86\u6570\u5341\u7684\u7684\u5bb6\u56ed\uff0c\u5c31\u53ea\u597d\u773c \u7741\u7741\u7684\u8ba9\u4ed6\u4eec\u9738\u5360\u4e86\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u5fc3\u4e2d\u5947\u602a\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e9b\u5916\u56fd\u4eba\u770b\u6765\u4e0d\u4f3c\u662f\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\uff0c\u4ed6\u4eec\u4e07\u91cc\u8fe2\u8fe2\uff0c \u5230\u4e2d\u56fd\u6765\uff0c\u8981\u9738\u5360\u8352\u5c71\u7684\u4e00\u95f4\u77f3\u5ba4\uff0c\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u4e8b\u7406\u53cd\u5e38\uff0c\u600e\u6837\u4e5f\u731c\u60f3\u4e0d\u900f\u3002\u539f\u6765\u8fd9\u4e9b\u4eba \u90fd\u662f\u63d0\u6469\u8fbe\u591a\u7684\u95e8\u4e0b\u3002\u63d0\u6469\u8fbe\u591a\u60f3\u6500\u767b\u4e16\u754c\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u7b79\u5212\u5df2\u4e45\uff0c\u6d3e\u4e86\u95e8\u4e0b\u5f1f\u5b50\u63a2\u8def\uff0c\u89c1\u534a \u5c71\u4e0a\u6709\u65b9\u4eca\u660e\u8fd9\u4e00\u5bb6\u4eba\uff0c\u751a\u662f\u5947\u5f02\u3002\u52a0\u4ee5\u65b9\u4eca\u660e\u6240\u5c45\u4e4b\u5904\uff0c\u5730\u6c14\u6e29\u6696\uff0c\u6700\u9002\u5408\u505a\u4e2d\u9014\u7684\u9a7b\u811a \u4e4b\u6240\uff0c\u6545\u6b64\u4ed6\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u4e24\u6b21\u4e09\u756a\uff0c\u524d\u6765\u8981\u7d22\uff0c\u82e5\u662f\u4ed6\u4eec\u8bf4\u660e\u539f\u7531\uff0c\u65b9\u4eca\u660e\u670d\u8f6f\u4e0d\u670d\u786c\uff0c \u6216\u8bb8\u7b54\u5141\uff0c\u504f\u504f\u63d0\u6469\u8fbe\u591a\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u4e00\u5411\u6a2a\u884c\u6b27\u4e9a\uff0c\u6043\u5f3a\u60ef\u4e86\uff0c\u6545\u6b64\u624d\u7206\u51fa\u4e86\u8fd9\u51e0\u573a\u7684\u6076 \u6218\u3002\u7b2c\u4e8c\u6b21\u4e0a\u5c71\uff0c\u88ab\u5510\u6653\u6f9c\u7528\u5929\u5c71\u795e\u8292\u6253\u6298\u4e86\u817f\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u6b63\u662f\u602a\u53e4\u62c9\u548c\u963f\u65af\u7f57\u3002 \u3000\u3000\u6708\u5149\u4ece\u96ea\u5cf0\u4e0a\u6cfb\u4e0b\u6765\uff0c\u4ee4\u4eba\u611f\u5230\u4e00\u80a1\u5bd2\u610f\uff0c\u65b9\u96ea\u541b\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8be5\u7761\u5566\uff01\u201d\u65b9\u4eca\u660e\u4fa7 \u8033\u51dd\u795e\uff0c\u597d\u4f3c\u5728\u8046\u542c\u4ec0\u4e48\u58f0\u97f3\uff0c\u5ffd\u9053\uff1a\u201c\u53ea\u6015\u654c\u4eba\u8fd8\u4e0d\u80af\u8ba9\u6211\u4eec\u7761\u89c9\u3002\u201d\u65b9\u96ea\u541b\u9053\uff1a\u201c\u4ec0 \u4e48\uff0c\u4ed6\u4eec\u53c8\u6765\u4e86\u5417\uff1f\u201d\u9f99\u7075\u77eb\u957f\u5251\u4e00\u632f\uff0c\u6012\u9053\uff1a\u201c\u8fd9\u5e72\u4eba\u7f20\u7ea0\u4e0d\u6e05\uff0c\u786e\u662f\u4ee4\u4eba\u53ef\u607c\u3002\u201d\u4ed6\u4e5f \u542c\u5230\u5916\u9762\u654c\u4eba\u7684\u58f0\u606f\u4e86\u3002 \u3000\u3000\u84e6\u5730\u91cc\u8f70\u9686\u4e00\u58f0\u5de8\u54cd\uff0c\u82b1\u5703\u7684\u56f4\u5899\u5d29\u4e86\u4e00\u5835\uff0c\u6c99\u77f3\u7eb7\u98de\u4e2d\uff0c\u4e00\u4f19\u4eba\u4ece\u7f3a\u53e3\u6d8c\u5165\uff0c\u53ea\u89c1\u5f53 \u524d\u7684\u90a3\u6b63\u662f\u5c3c\u6cca\u5c14\u7684\u7b2c\u4e00\u56fd\u5e08\u6cf0\u5409\u63d0\uff0c\u521a\u624d\u88ab\u6253\u8d70\u7684\u90a3\u56db\u4e2a\u63d0\u6469\u8fbe\u591a\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e5f\u53bb\u800c\u590d \u56de\uff0c\u53e6\u5916\u8fd8\u6709\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u8ddf\u5728\u540e\u9762\u3002\u539f\u6765\u6cf0\u5409\u63d0\u88ab\u5510\u7ecf\u5929\u6253\u8d25\u4e4b\u540e\uff0c\u65e0\u9762\u76ee\u518d\u89c1\u56fd\u738b\uff0c \u56e0\u6b64\u9080\u4e86\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u518d\u4e0a\u5c71\u6765\u8ffd\u62ff\u9f99\u7075\u77eb\uff0c\u5e0c\u671b\u53ef\u4ee5\u5c06\u529f\u8d4e\u7f6a\u3002\u4ed6\u7684\u8888\u88df\u5df2\u88ab\u5929\u5c71\u795e \u8292\u5c04\u7a7f\uff0c\u4e0d\u80fd\u518d\u7528\uff0c\u6539\u7528\u4e00\u9762\u94c1\u76fe\uff0c\u914d\u5408\u53f3\u624b\u7684\u94c1\u9524\u3002\u4e0a\u5230\u534a\u5c71\uff0c\u6070\u597d\u78b0\u5230\u90a3\u56db\u4e2a\u63d0\u6469\u8fbe\u591a \u7684\u5f1f\u5b50\uff0c\u6cf0\u5409\u63d0\u61c2\u5f97\u963f\u62c9\u4f2f\u8bdd\uff0c\u4e00\u95ee\u60c5\u5f62\uff0c\u77e5\u9053\u9f99\u7075\u77eb\u4e5f\u5728\u4e0a\u9762\uff0c\u4e8e\u662f\u4e24\u4f19\u4eba\u5408\u6210\u4e00\u4f19\uff0c\u53c8 \u6765\u5bfb\u8845\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u4e00\u9524\u51fb\u574d\u56f4\u5899\uff0c\u6ee1\u56ed\u82b1\u6811\u90fd\u53d7\u707e\u6b83\uff0c\u65b9\u96ea\u541b\u7231\u82b1\u82e5\u547d\uff0c\u5fc3\u75db\u5982\u5272\uff0c\u5927\u6012\u65a5\u9053\uff1a \u201c\u65e0\u793c\u756a\u50e7\uff0c\u80c6\u6562\u7cdf\u584c\u6211\u7684\u82b1\u679d\uff0c\u770b\u5251\uff01\u201d\u65b9\u4eca\u660e\u5fd9\u53eb\u9053\uff1a\u201c\u96ea\u513f\u9000\u4e0b\u3002\u201d\u65b9\u96ea\u541b\u53f3\u624b\u6325\u52a8 \u957f\u97ad\uff0c\u5de6\u624b\u98de\u51fa\u4e00\u628a\u77ed\u5251\uff0c\u53ea\u542c\u5f97\u5417\u7684\u4e00\u58f0\uff0c\u77ed\u5251\u78b0\u5728\u94c1\u76fe\u4e0a\uff0c\u767b\u65f6\u6298\u65ad\uff0c\u957f\u97ad\u50fb\u556a\u4e00\u58f0\uff0c \u5374\u7f20\u4e0a\u4e86\u6cf0\u5409\u63d0\u7684\u624b\u8155\u3002\u6cf0\u5409\u63d0\u7adf\u4f3c\u6beb\u4e0d\u5728\u610f\uff0c\u4ecd\u7136\u8fc8\u6b65\u524d\u884c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5e74\u516c\u5b50\uff0c\u6211\u56fd \u56fd\u738b\u5f85\u4f60\u4e0d\u8584\uff0c\u56e0\u4f55\u79c1\u9003\uff1f\u201d\u6bcf\u884c\u4e00\u6b65\uff0c\u90a3\u957f\u97ad\u4fbf\u5728\u4ed6\u624b\u81c2\u4e0a\u591a\u7ed5\u4e0a\u4e00\u531d\uff0c\u65b9\u96ea\u541b\u4f7f\u5c3d\u6c14 \u529b\uff0c\u6709\u5982\u9756\u8712\u4e4b\u64bc\u77f3\u67f1\uff0c\u773c\u770b\u957f\u97ad\u8d8a\u7f29\u8d8a\u77ed\u3002\u9f99\u7075\u77eb\u559d\u9053\uff1a\u201c\u653e\u5f00\u518d\u8bf4\uff01\u201d\u957f\u5251\u4e00\u633d\uff0c\u4f5c\u52bf \u523a\u4ed6\u8155\u4e0a\u7684\u5173\u5143\u7a74\uff0c\u6cf0\u5409\u63d0\u624b\u81c2\u4e00\u632f\uff0c\u5c06\u65b9\u96ea\u541b\u63a8\u4e0a\u4e24\u6b65\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u523a\uff01\u5e74\u5148\u751f\uff0c\u54b1 \u4eec\u8fd8\u662f\u5148\u793c\u540e\u5175\u7684\u597d\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u6377\u5982\u98de\u9e1f\uff0c\u500f\u5730\u6251\u6765\uff0c\u53ea\u542c\u5f97\u53c8 \u662f\u201c\u88c6\u201d\u7684\u4e00\u58f0\uff0c\u6cf0\u5409\u63d0\u7684\u94c1\u76fe\u767b\u65f6\u8131\u624b\u98de\u4e0a\u534a\u7a7a\uff0c\u968f\u5373\u542c\u5f97\u201c\u535c\u52d2\u201d\u201c\u535c\u52d2\u201d\u7684\u4e00\u4e32\u6025 \u54cd\uff0c\u65b9\u96ea\u541b\u7684\u957f\u97ad\u5bf8\u5bf8\u788e\u88c2\uff0c\u4e08\u4f59\u7684\u957f\u97ad\uff0c\u53ea\u5269\u4e0b\u56db\u5c3a\u6765\u957f\u3002\u539f\u6765\u662f\u65b9\u4eca\u660e\u65bd\u7528\u795e\u62f3\u771f\u529b\uff0c \u786c\u6253\u4e86\u6cf0\u5409\u63d0\u4e00\u62f3\uff0c\u89e3\u4e86\u5973\u513f\u4e4b\u56f4\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u9762\u8272\u7070\u767d\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff1b\u65b9\u4eca\u660e\u7684\u8eab\u5b50\u4e5f\u6447\u6643\u4e0d\u5b9a\uff0c\u6709\u4f3c\u98ce\u4e2d\u4e4b \u70db\u3002\u65b9\u4eca\u660e\u521a\u624d\u90a3\u4e00\u62f3\u662f\u4ee5\u5185\u5bb6\u771f\u529b\u4e0e\u6cf0\u5409\u63d0\u786c\u78b0\uff0c\u82e5\u5728\u4ed6\u58ee\u5e74\u4e4b\u65f6\uff0c\u8fd9\u4e00\u62f3\u5c31\u8db3\u4ee5\u88c2\u6cf0\u5409 \u63d0\u7684\u4e94\u810f\uff0c\u800c\u4eca\u4e00\u8005\u5403\u4e8f\u5728\u5e74\u7eaa\u8001\u4e86\uff0c\u4e8c\u8005\u5403\u4e8f\u5728\u66fe\u5403\u4e86\u9f99\u7075\u77eb\u4e00\u62f3\uff0c\u4e09\u8005\u5403\u4e8f\u5728\u521a\u521a\u6fc0\u6218 \u8fc7\u6765\uff0c\u4ee5\u81f3\u95f9\u5f97\u4e2a\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u9f99\u7075\u77eb\u53eb\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u6276\u4f60\u7239\u7239\u56de\u53bb\u3002\u201d\u4e00\u6296\u624b\u53d1\u51fa\u51e0\u679a\u84ba\u85dc\u548c\u8896\u7bad\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u7684\u6697 \u5668\u7834\u98ce\u4e4b\u58f0\uff0c\u5374\u90fd\u4ece\u6cf0\u5409\u63d0\u7684\u8eab\u8fb9\u64e6\u8fc7\uff0c\u539f\u6765\u662f\u88ab\u90a3\u56db\u4e2a\u63d0\u6469\u8fbe\u591a\u7684\u5f1f\u5e72\u7528\u9634\u9633\u638c\u529b\u9707\u6b6a\u4e86 \u51c6\u5934\u3002\u9f99\u7075\u77eb\u5927\u6012\uff0c\u594b\u4e0d\u987e\u8eab\uff0c\u633d\u5251\u51b2\u5165\u654c\u4eba\u7684\u6838\u5fc3\u3002 \u3000\u3000\u6cf0\u6746\u63d0\u987d\u52c7\u4e4b\u6781\uff0c\u53d7\u4e86\u5185\u4f24\uff0c\u5c45\u7136\u80fd\u591f\u633a\u6ce8\uff0c\u62fe\u56de\u94c1\u76fe\uff0c\u6325\u52a8\u94c1\u9524\uff0c\u4ecd\u7136\u62a2\u6765\u52a9\u6218\uff0c\u8fd9 \u4e00\u6765\u53d8\u6210\u4e86\u4ee5\u4e00\u654c\u4e03\u4e4b\u52bf\u3002\u9f99\u7075\u77eb\u88ab\u90a3\u56db\u4e2a\u63d0\u964d\u8fbe\u591a\u7684\u5f1f\u5b50\u4ee5\u53ca\u5c3c\u6cca\u5c14\u7684\u4e24\u4e2a\u6b66\u571f\u56f0\u5728\u6838 \u5fc3\u3002\u53e6\u5916\u8fd8\u8981\u62b5\u6321\u6cf0\u5409\u63d0\u7684\u94c1\u9524\u538b\u9876\u4e4b\u52bf\uff0c\u5e78\u800c\u6cf0\u5409\u63d0\u53d7\u4e86\u5185\u4f24\uff0c\u90a3\u56db\u4e2a\u63d0\u6469\u8fbe\u591a\u7684\u5f1f\u5b50\u521a \u521a\u7ecf\u8fc7\u4e00\u573a\u6fc0\u6218\uff0c\u5176\u4e2d\u4e24\u4e2a\u8fd8\u88ab\u65b9\u4eca\u660e\u7528\u767e\u6b65\u795e\u62f3\u4e4b\u529b\u6253\u4e0b\u5c71\u5761\uff0c\u5185\u529b\u4ff1\u90fd\u53d7\u4e86\u635f\u8017\uff0c\u9f99\u7075 \u77eb\u8fd9\u624d\u80fd\u591f\u52c9\u5f3a\u652f\u6301\u3002\u7136\u800c\u4e5f\u4e0d\u8fc7\u5341\u591a\u4e8c\u5341\u62db\uff0c\u9f99\u7075\u77eb\u4fbf\u88ab\u5377\u8fdb\u9634\u9633\u638c\u529b\u7684\u6f29\u6da1\u4e4b\u4e2d\uff0c\u957f\u5251 \u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00\u3002\u6cf0\u5409\u63d0\u4e00\u89c1\u65f6\u673a\u5df2\u5230\uff0c\u8fd0\u4e86\u5168\u529b\uff0c\u4e00\u9524\u51fb\u4e0b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u5757\u602a\u5578\uff0c\u54cd\u5f7b\u6797\u8c37\uff0c\u7a81\u7136\u4e00\u5757\u78e8\u76d8\u5927\u7684\u5de8\u77f3\u5411\u7740\u4f17\u4eba\u98de\u4e0b\uff0c\u8fd9\u4e00\u6765\u9635\u52bf\u5927\u4e71\uff0c \u5404\u4eba\u7eb7\u7eb7\u8d70\u907f\uff0c\u53ea\u89c1\u968f\u7740\u90a3\u5927\u77f3\u7684\u8f70\u9686\u64bc\u5730\u4e4b\u58f0\uff0c\u4e00\u4e2a\u94a8\u8863\u767e\u7ed3\u7684\u5c11\u5e74\u8df3\u4e86\u51fa\u6765\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u6211\u751f\u5e73\u6700\u770b\u4e0d\u8fc7\u773c\u4ee5\u591a\u6b3a\u5c11\u4e4b\u4e8b\uff0c\u54c8\u54c8\uff0c\u4f60\u5403\u6211\u4e00\u62d0\uff0c\u54c8\u54c8\uff01\u4f60\u4e5f\u5403\u6211\u4e00\u62d0\uff01\u201d\u94c1\u62d0 \u4e00\u6325\uff0c\u7a81\u7136\u5728\u5730\u4e0a\u8fde\u6253\u4e86\u4e09\u4e2a\u7b4b\u6597\uff0c\u75be\u4f3c\u60ca\u96f7\u95ea\u7535\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5df2\u8fde\u88ad\u4e86\u4e03\u4e2a\u654c\u4eba\uff0c\u8eab\u6cd5\u602a \u5f02\uff0c\u4e16\u7f55\u5176\u4f26\uff01\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u91d1\u4e16\u9057\u6765\u4e86\uff01 \u3000\u3000\u9f99\u7075\u77eb\u4e0d\u8ba4\u5f97\u91d1\u4e16\u9057\uff0c\u60ca\u8be7\u4ea4\u96c6\uff0c\u987e\u4e0d\u5f97\u95ee\u4ed6\u59d3\u540d\uff0c\u957f\u5251\u4e00\u632f\uff0c\u4e0a\u6765\u52a9\u6218\u3002\u91d1\u4e16\u9057\u4ed7\u7740 \u8be1\u5f02\u7edd\u4f26\u7684\u8eab\u6cd5\uff0c\u628a\u90a3\u56db\u4e2a\u63d0\u6469\u3002\u8fbe\u591a\u7684\u5f1f\u5b50\u6253\u5f97\u9694\u5728\u56db\u5904\uff0c\u9634\u9633\u638c\u529b\u6c47\u4e0d\u5230\u4e00\u5904\uff0c\u5148\u5360\u4e0a \u98ce\uff0c\u6cf0\u5409\u63d0\u9f13\u52c7\u6321\u4e86\u4e09\u62db\uff0c\u9635\u52bf\u91cd\u6574\uff0c\u91d1\u4e16\u9057\u88ab\u90a3\u56db\u80a1\u638c\u529b\u7275\u5f15\uff0c\u53ea\u89c9\u6709\u5982\u8eab\u9677\u6f29\u6da1\uff0c\u5927\u6012 \u559d\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u90aa\u95e8\u529f\u592b\uff1f\u201d\u4e00\u62d0\u8361\u5f00\u6cf0\u5409\u63d0\u7684\u5927\u94c1\u9524\uff0c\u62bd\u51fa\u62d0\u4e2d\u94c1\u5251\uff0c\u5de6\u62d0\u53f3\u5251\uff0c\u5de6\u51b2 \u53f3\u7a81\uff0c\u9f99\u7075\u77eb\u53eb\u9053\uff1a\u201c\u5144\u53f0\u4e0d\u53ef\u52a8\u6c14\uff0c\u987a\u7740\u5176\u52bf\uff0c\u5148\u5b88\u540e\u653b\uff01\u201d\u91d1\u4e16\u9057\u201c\u5478\u201d\u4e86\u4e00\u53e3\u9053\uff1a \u201c\u731b\u864e\u6012\u543c\uff0c\u9707\u6151\u9f20\u8f88\uff0c\u5927\u4e08\u592b\u5f53\u6012\u5219\u6012\uff0c\u5c82\u53ef\u6ca1\u6709\u813e\u6c14\uff1f\u201d\u9f99\u7075\u77eb\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u9053\uff1a\u201c\u6211 \u597d\u5fc3\u529d\u4f60\uff0c\u600e\u7684\u4f60\u8fde\u6211\u4e5f\u9a82\u8d77\u6765\u4e86\uff1f\u201d\u90a3\u56db\u4e2a\u63d0\u6469\u8fbe\u591a\u7684\u5f1f\u5b50\u867d\u7136\u542c\u4e0d\u61c2\u4e2d\u56fd\u8bdd\uff0c\u4f46\u89c1\u91d1\u4e16 \u9057\u5f3a\u653b\u731b\u6253\uff0c\u5fc3\u4e2d\u6b63\u81ea\u6697\u559c\uff0c\u6b63\u5f85\u52a0\u5f3a\u638c\u529b\uff0c\u4f7f\u4ed6\u4e0d\u80fd\u8131\u8eab\uff0c\u5ffd\u542c\u5f97\u6cf0\u5409\u63d0\u5927\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3 \u4e86\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u4e16\u9057\u5478\u7684\u4e00\u53e3\u6d53\u6d8e\uff0c\u5df1\u7136\u5410\u51fa\uff0c\u9996\u5f53\u5176\u51b2\u7684\u4e00\u540d\u63d0\u6469\u8fbe\u591a\u95e8\u4e0b\uff0c \u7709\u5c16\u4e0a\u5ffd\u4f3c\u7ed9\u4e00\u53ea\u6bd2\u8682\u8681\u53ee\u4e86\u4e00\u53e3\uff0c\u773c\u775b\u987f\u65f6\u7741\u4e0d\u5f00\u6765\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u201c\u55e4\u55e4\u201d\u58f0\u54cd\uff0c\u90a3\u4e24\u540d \u5c3c\u6cca\u5c14\u6b66\u58eb\u4e5f\u4ec6\u5730\u4e0d\u8d77\u3002 \u3000\u3000\u5269\u4e0b\u7684\u90a3\u4e09\u4e2a\u63d0\u6469\u8fbe\u591a\u5f1f\u5b50\u60ca\u9a87\u83ab\u660e\uff0c\u6025\u5fd9\u64a4\u56de\u638c\u529b\u81ea\u4fdd\uff0c\u53ea\u89c1\u6cf0\u5409\u63d0\u4e5f\u628a\u94c1\u76fe\u821e\u5f97\u65cb \u98ce\u75be\u8f6c\uff0c\u6cfc\u6c34\u96be\u8fdb\u3002\u539f\u6765\u8fd9\u6b63\u662f\u91d1\u4e16\u9057\u7684\u62ff\u624b\u7edd\u6280\uff0c\u5047\u4f5c\u52a8\u6012\uff0c\u55b7\u51fa\u53e3\u4e2d\u7684\u6bd2\u9488\u3002\u9f99\u7075\u77eb\u8fd9 \u624d\u604d\u7136\u5927\u609f\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u6bd2\u624b\u75af\u4e10\uff01\u201d\u91d1\u4e16\u9057\u54c8\u54c8\u5927\u7b11\uff0c\u5e94\u9053\uff1a\u201c\u4e0d\u9519\u5440\u4e0d\u9519\uff01\u6bd2\u624b \u75af\u4e10\u662f\u6211\uff0c\u6211\u662f\u6bd2\u624b\u75af\u4e10\uff01\uff0c\u4e16\u4eba\u90fd\u8bf4\u6211\u6bd2\uff0c\u4e16\u4eba\u90fd\u8bf4\u6211\u75af\uff01\u54c8\u54c8\uff0c\u4f60\u6015\u4e86\u6211\u4e48\uff1f\u201d\u9f99\u7075\u77eb \u4e00\u58f0\u558a\u51fa\uff0c\u7acb\u523b\u9192\u89c9\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u597d\u751f\u5c34\u5c2c\uff0c\u5fd9\u9053\uff1a\u201c\u5144\u53f0\u4fa0\u4e49\u5fc3\u80a0\uff0c\u5c0f\u5f1f\u5931\u8a00\u4e86\u3002\u201d\u91d1 \u4e16\u9057\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u6bd2\u624b\u75af\u4e10\uff0c\u54c8\u54c8\uff0c\u4f60\u518d\u6765\u770b\u6211\u7684\u6bd2\u624b\uff01\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53c8\u662f\u5478\u7684\u4e00\u53e3\u6d53\u75f0\u98de\u51fa\uff0c\u94c1\u5251\u4e00\u632f\uff0c\u628a\u6cf0\u5409\u63d0\u7684\u6709\u81c2\u5272\u4e86\u4e00\u9053\u957f\u957f\u7684\u4f24\u53e3\uff0c\u6cf0\u5409 \u63d0\u72c2\u821e\u94c1\u76fe\uff0c\u62fc\u547d\u62b5\u6321\uff0c\u91d1\u4e16\u9057\u5de6\u4e00\u62d0\uff0c\u53f3\u4e00\u5251\uff0c\u771f\u5982\u75af\u864e\u4e0b\u5c71\uff0c\u62db\u62db\u90fd\u662f\u6bd2\u624b\uff01 \u3000\u3000\u4f46\u5728\u8fd9\u8f6c\u77ac\u4e4b\u95f4\uff0c\u90a3\u4e09\u4e2a\u63d0\u6469\u8fbe\u591a\u7684\u95e8\u4e0b\uff0c\u53c8\u5df2\u5360\u597d\u65b9\u4f4d\uff0c\u4e09\u80a1\u638c\u529b\u5408\u5728\u4e00\u8d77\uff0c\u4ee5\u56db\u654c \u4e8c\uff0c\u582a\u582a\u6253\u4e2a\u5e73\u624b\uff0c\u91d1\u4e16\u9057\u62d0\u5251\u517c\u65bd\uff0c\u7834\u4e0d\u4e86\u4ed6\u4eec\u7684\u638c\u529b\uff0c\u4ed6\u4eec\u5bb3\u6015\u91d1\u4e16\u9057\u7684\u6697\u5668\uff0c\u4e5f\u53ea\u80fd \u534a\u653b\u534a\u5b88\uff0c\u4e0d\u6562\u5168\u529b\u65bd\u4e3a\u3002 \u3000\u3000\u6fc0\u6218\u79fb\u65f6\uff0c\u53ea\u542c\u5f97\u90a3\u4e09\u4e2a\u63d0\u6469\u8fbe\u591a\u95e8\u4e0b\u53d1\u51fa\u545c\u545c\u7684\u53e3\u54e8\u58f0\u3002\u4ee4\u4eba\u5fc3\u70e6\u610f\u4e71\uff0c\u91d1\u4e16\u9057\u559d \u9053\uff1a\u201c\u9b3c\u568e\u4ec0\u4e48\uff1f\u4f60\u4e5f\u542c\u6211\u7684\u9f99\u541f\u864e\u5578\uff01\u201d\u53d1\u58f0\u957f\u5578\uff0c\u628a\u4ed6\u4eec\u7684\u53e3\u54e8\u58f0\u90fd\u538b\u4e86\u4e0b\u53bb\u3002\u5c71\u98ce\u547c \u53f7\uff0c\u5578\u58f0\u54e8\u58f0\u5728\u98ce\u4e2d\u56de\u65cb\uff0c\u66f4\u4ee4\u4eba\u60ca\u5fc3\u52a8\u9b44\u3002 \u3000\u3000\u518d\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6cf0\u5409\u63d0\u53c8\u88ab\u4ed6\u6572\u4e86\u4e00\u62d0\uff0c\u773c\u89c1\u4e0d\u652f\uff0c\u91d1\u4e16\u9057\u5ffd\u9053\uff1a\u201c\u6211\u809a\u4e86\u997f\u5566\uff01\u5403 \u9971\u4e86\u518d\u548c\u4f60\u6253\u3002\u201d\u6cf0\u5409\u63d0\u6c42\u4e4b\u4e0d\u5f97\uff0c\u6025\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u4f60\u4eec\u591a\u6d3b\u4e00\u5929\uff01\u201d\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d \u77e5\u662f\u8c01\u8ba9\u8c01\u5462\uff1f\u201d\u201c\u5478\u201d\u7684\u53c8\u662f\u4e00\u53e3\u6d53\u75f0\uff0c\u6cf0\u5409\u63d0\u6025\u5fd9\u7a9c\u5f00\uff0c\u4e0d\u6562\u518d\u8bf4\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6478\u51fa\u534a\u8fb9\u70e7\u91ce\u9e21\uff0c\u54ac\u4e86\u4e24\u53e3\uff0c\u9053\uff1a\u201c\u51bb\u5f97\u786c\u4e86\uff0c\u4e00\u70b9\u4e5f\u4e0d\u597d\u5403\uff0c\u5582\uff0c\u6211\u5e2e\u4f60\u6253 \u67b6\uff0c\u4f60\u5c31\u4e0d\u62db\u5f85\u6211\u4e48\uff1f\u201d\u9f99\u7075\u77eb\u773c\u89c1\u5c06\u8981\u5f97\u80dc\uff0c\u751a\u662f\u53ef\u60dc\uff0c\u4f46\u4e0d\u597d\u8fdd\u62d7\u91d1\u4e16\u9057\uff0c\u53ea\u5f97\u8bf4\u9053\uff1a \u201c\u5c4b\u5b50\u91cc\u6709\u9152\u6709\u8089\uff0c\u54b1\u4eec\u56de\u53bb\u5403\u9971\u4e86\u518d\u6253\u4e5f\u597d\u3002\u201d\u4ed6\u5374\u4e0d\u77e5\u539f\u6765\u91d1\u4e16\u9057\u731b\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6c14 \u529b\u4e5f\u5dee\u4e0d\u591a\u5c3d\u4e86\u3002\u91d1\u4e16\u9057\u8fd9\u65f6\u5df2\u609f\u51fa\u4e86\u9634\u9633\u638c\u529b\u7684\u8bc0\u7a8d\uff0c\u77e5\u9053\u5728\u6025\u8feb\u4e4b\u95f4\uff0c\u7834\u4ed6\u4e0d\u5f97\uff0c\u6b63\u51c6 \u5907\u517b\u597d\u6c14\u529b\uff0c\u518d\u7528\u5999\u6cd5\u7834\u4ed6\u3002 \u3000\u3000\u9f99\u7075\u77eb\u8bb0\u6302\u65b9\u4eca\u660e\u7684\u4f24\u52bf\uff0c\u5fc3\u9053\uff1a\u201c\u56de\u53bb\u5148\u628a\u4ed6\u533b\u597d\u4e5f\u662f\u6b63\u7406\u3002\u201d\u4e0e\u91d1\u4e16\u9057\u8e0f\u5165\u77f3\u5c4b\uff0c \u53ea\u89c1\u65b9\u4eca\u660e\u8eba\u5728\u5730\u4e0a\uff0c\u9762\u5982\u91d1\u7eb8\u3002\u9f99\u7075\u77eb\u60ca\u9053\uff1a\u201c\u8001\u4e08\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u65b9\u4eca\u660e\u5fae\u7b11\u9053\uff1a\u201c\u8fd8 \u597d\uff0c\u4eca\u665a\u6211\u6b7b\u4e0d\u4e86\uff01\u201d\u9f99\u7075\u77eb\u662f\u4e2a\u884c\u5bb6\uff0c\u6025\u5fd9\u66ff\u4ed6\u628a\u8109\uff0c\u5fc3\u5934\u4e0d\u89c9\u4e00\u6c89\uff0c\u539f\u6765\u65b9\u4eca\u660e\u7684\u5e26\u8109 \u5df2\u7ed9\u9707\u65ad\uff0c\u6700\u591a\u4e5f\u6d3b\u4e0d\u8fc7\u4e03\u5929\uff0c\u5fc3\u4e2d\u4fc3\u4e3a\u96be\u8fc7\uff0c\u773c\u6cea\u51e0\u4e4e\u8981\u6eda\u51fa\u6765\uff0c\u4e3a\u6015\u4ee4\u4ed6\u5973\u513f\u4f24\u5fc3\uff0c\u5f3a \u884c\u5fcd\u7740\uff0c\u4e0d\u6562\u628a\u771f\u60c5\u8bf4\u51fa\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u53c8\u662f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9\u6781\uff01\u6d3b\u4e00\u5929\u5c31\u7b97\u4e00\u5929\uff0c\u53ea\u8981\u4eca\u665a\u6b7b\u4e0d\u4e86\u5c31\u597d\uff1b \u8c01\u77e5\u9053\u81ea\u5df1\u660e\u5929\u8fd8\u5728\u4e0d\u5728\u8fd9\u4e16\u754c\u4e0a\uff1f\u201d\u9f99\u7075\u77eb\u5fc3\u4e2d\u751f\u6c14\uff0c\u6697\u9053\uff1a\u201c\u6bd2\u624b\u75af\u4e10\u679c\u7136\u662f\u75af\u75af\u766b \u766b\uff0c\u8bf4\u8bdd\u4e0d\u8fd1\u4eba\u60c5\u3002\u8001\u4eba\u5bb6\u4f24\u5f97\u8fd9\u4e48\u91cd\uff0c\u4ed6\u8fd8\u5728\u8bf4\u98ce\u51c9\u8bdd\u513f\uff01\u201d\u5411\u4ed6\u767d\u4e86\u4e00\u773c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a \u201c\u91cc\u9762\u6709\u9152\u6709\u8089\uff0c\u4f60\u81ea\u5df1\u7aef\u51fa\u6765\u559d\u5427\uff01\u201d\u91d1\u4e16\u9057\u94c1\u62d0\u4e00\u987f\uff0c\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5999\u6781\u5999\u6781\uff01 \u5403\u9971\u4e86\u660e\u5929\u4fbf\u6b7b\u4e5f\u597d\u505a\u4e2a\u9971\u9b3c\uff01\u8001\u4e08\u5475\uff0c\u54b1\u4eec\u540c\u75c5\u76f8\u601c\uff0c\u6211\u548c\u4f60\u75db\u996e\u4e09\u676f\uff01\u201d\u9f99\u7075\u77eb\u6c14\u5f97\u8bf4 \u4e0d\u51fa\u8bdd\uff0c\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u91d1\u4e16\u9057\u7684\u751f\u547d\u4e5f\u53ea\u6709\u4e03\u5929\uff0c\u96be\u602a\u4ed6\u6709\u5982\u65af\u611f\u89e6\uff01 \u3000\u3000\u65b9\u4eca\u660e\u671b\u4e86\u91d1\u4e16\u9057\u4e00\u773c\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u8fd9\u4f4d\u5c0f\u54e5\u5feb\u4eba\u5feb\u8bed\uff0c\u6211\u4e0e\u4f60\u75db \u996e\u4e09\u676f\uff01\u96ea\u513f\uff0c\u5feb\u53bb\u53d6\u9152\u98df\u6765\u6b3e\u5f85\u5ba2\u4eba\u3002\u201d\u7b11\u58f0\u6e10\u6e10\u51c4\u51c9\uff0c\u65b9\u96ea\u513f\u4ece\u672a\u89c1\u8fc7\u7236\u4eb2\u8fd9\u526f\u795e\u6c14\uff0c \u4e0d\u89c9\u5446\u4e86\uff01 \u3000\u3000\u65b9\u4eca\u660e\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u77a7\u4e86\u4e00\u773c\uff0c\u5df2\u770b\u51fa\u91d1\u4e16\u9057\u5185\u529f\u8d70\u706b\u5165\u9b54\uff0c\u6027\u547d\u4e5f\u4e0d\u8fc7\u4e03\u5929\uff0c\u4efb \u4f55\u5999\u836f\u7075\u4e39\uff0c\u65e0\u53ef\u6551\u6cbb\uff0c\u4ed6\u9971\u7ecf\u5fe7\u60a3\uff0c\u5386\u5c3d\u6ca7\u6851\uff0c\u5bf9\u6b7b\u751f\u4e4b\u4e8b\u672c\u5c31\u8c41\u8fbe\uff0c\u4f55\u51b5\u91d1\u4e16\u9057\u53c8\u662f\u4e0e \u4ed6\u540c\u75c5\u76f8\u601c\u7684\u4eba\uff0c\u56e0\u800c\u5bf9\u91d1\u4e16\u9057\u7684\u8bdd\uff0c\u4e5f\u5c31\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u65b9\u96ea\u541b\u70eb\u597d\u70ed\u9152\uff0c\u7aef\u4e86\u51fa\u6765\uff0c\u7ed9\u91d1\u4e16\u9057\u659f\u4e86\u4e00\u676f\uff0c\u6309\u7740\u9152\u58f6\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u559d\u9152\u4e0d\u59a8\u4e8b \u4e48\uff1f\u201d\u65b9\u4eca\u660e\u4ef0\u5929\u4e00\u7b11\uff0c\u5728\u5973\u513f\u624b\u4e0a\u63a5\u8fc7\u9152\u58f6\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u5e78\u9047\u654c\u4eba\u4e4b\u5b50\uff0c\u53c8\u65b0\u4ea4\u4e0a\u4e86\u8fd9\u6837 \u4e00\u4f4d\u8c41\u8fbe\u8c6a\u8fc8\u7684\u5c0f\u53cb\uff0c\u6211\u5fc3\u4e2d\u75db\u5feb\u5df2\u6781\uff0c\u4ec0\u4e48\u59a8\u4e8b\u4e0d\u59a8\u4e8b\uff1f\u5982\u6b64\u76db\u4f1a\uff0c\u5c82\u53ef\u4e0d\u75db\u996e\u4e00\u573a\u3002\u201d \u63d0\u8d77\u9152\u58f6\u81ea\u659f\u81ea\u996e\uff0c\u53c8\u7ed9\u91d1\u4e16\u9057\u9891\u9891\u6dfb\u9152\uff0c\u4e00\u8001\u4e00\u5c11\uff0c\u7aef\u7684\u662f\u8131\u7565\u5f62\u9ab8\uff0c\u653e\u6000\u5927\u996e\uff0c\u628a\u751f\u751f \u6b7b\u6b7b\uff0c\u6069\u6069\u6028\u6028\uff0c\u5168\u90fd\u7f6e\u4e4b\u5ea6\u5916\u3002 \u3000\u3000\u9f99\u7075\u77eb\u60f3\u8d77\u662f\u81ea\u5df1\u7684\u7236\u4eb2\u5bb3\u5f97\u4ed6\u4eec\u4e24\u7236\u5973\u9690\u5c45\u8352\u5c71\uff0c\u800c\u4ed6\u53c8\u662f\u4e3a\u81ea\u5df1\u800c\u53d7\u91cd\u4f24\uff0c\u4e0d\u89c9\u5fc3 \u75db\u5982\u5272\uff0c\u660e\u660e\u77e5\u9053\u4ed6\u662f\u501f\u9152\u6d47\u6101\u3002\u5374\u53c8\u600e\u5fcd\u6b62\u4ed6\u6b7b\u524d\u7684\u6b22\u4e50\uff1f \u3000\u3000\u65b9\u4eca\u660e\u9152\u9163\u8033\u70ed\uff0c\u5ffd\u5730\u628a\u9152\u676f\u91cd\u91cd\u4e00\u987f\uff0c\u9762\u5411\u9f99\u7075\u77eb\u8bf4\u9053\uff1a\u201c\u9f99\u5148\u751f\uff0c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u4f55\u5e78 \u5982\u4e4b\uff0c\u6211\u7684\u672a\u4e86\u4e4b\u4e8b\uff0c\u8981\u62dc\u6258\u4f60\u4e86\u3002\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8001\u4e08\u6709\u547d\uff0c\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d\u65b9\u4eca\u660e\u9053\uff1a \u201c\u6211\u8fd9\u4f4d\u5c0f\u5973\uff0c\u603b\u4e0d\u80fd\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u6e21\u8fc7\u4e00\u751f\uff0c\u5c06\u6765\u4e0b\u5c71\uff0c\u8fd8\u671b\u4f60\u591a\u591a\u7167\u987e\u3002\u201d \u3000\u3000\u9f99\u7075\u77eb\u542c\u4ed6\u8bdd\u4e2d\u4f3c\u6709\u6df1\u610f\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u65b9\u4eca\u660e\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u8fd9\u662f\u7406\u6240\u5f53 \u7136\u3002\u201d\u65b9\u96ea\u541b\u5341\u5206\u4e0d\u89e3\uff0c\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u82e5\u4e0b\u5c71\uff0c\u4f60\u81ea\u7136\u4e5f\u5f97\u4e0b\u5c71\uff0c\u54b1\u4eec\u76f8\u4f9d\u4e3a\u547d\uff0c\u96be\u9053\u4f60 \u5c31\u4e0d\u7167\u987e\u6211\u4e86\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u7239\u7239\u80fd\u7167\u987e\u4f60\u4e00\u4e16\u4e48\uff1f\u9f99\u5148\u751f\u8d60\u4f60\u73e0\u4e32\uff0c\u4f60\u5411\u5979\u62dc \u8c22\u3002\u201d\u65b9\u96ea\u541b\u5fc3\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8c22\u8fc7\u4e86\u4e48\uff1f\u54a6\uff0c\u7239\u7239\u600e\u7684\u4eca\u665a\u5927\u5931\u5e38\u6001\uff0c\u8bf4\u8bdd\u98a0\u5012\u3002\u201d\u4f46\u8fd8\u662f \u4f9d\u7740\u7236\u4eb2\u7684\u5429\u5490\uff0c\u5411\u9f99\u7075\u77eb\u518d\u8c22\u4e00\u6b21\u3002\u9f99\u7075\u77eb\u662f\u4e2a\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u8fd9\u65f6\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u65b9\u4eca \u660e\u9002\u624d\u51c6\u8bb8\u5973\u513f\u63a5\u53d7\u4ed6\u7684\u793c\u7269\uff0c\u6562\u60c5\u65e9\u5df2\u6709\u4e86\u4ee5\u5973\u513f\u7ec8\u8eab\u76f8\u6258\u4e4b\u610f\uff0c\u628a\u73cd\u73e0\u4e32\u5f53\u4f5c\u8058\u793c\u770b\u5f85 \u4e86\u3002 \u3000\u3000\u9f99\u7075\u77eb\u591a\u5e74\u6765\u9041\u8ff9\u98ce\u5c18\uff0c\u80f8\u6000\u5927\u5fd7\uff0c\u6d3b\u5230\u4e09\u5341\u591a\u5c81\uff0c\u4ece\u6765\u672a\u5174\u8fc7\u5bb6\u5ba4\u4e4b\u5ff5\uff0c\u8fd9\u65f6\u5ffd\u5728\u559c \u9a6c\u62c9\u96c5\u5c71\u4e2d\u6709\u6b64\u5947\u9047\uff0c\u773c\u89c1\u65b9\u96ea\u541b\u5a07\u7f8e\u53ef\u7231\uff0c\u5929\u771f\u65e0\u90aa\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6026\u7136\u800c\u52a8\uff0c\u6025\u5fd9\u5411\u65b9\u96ea \u541b\u7b54\u62dc\uff0c\u53c8\u5411\u65b9\u4eca\u660e\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u9053\uff1a\u201c\u5c0f\u4f84\u5fc5\u4e0d\u8d1f\u8001\u4e08\u6240\u6258\u3002\u201d\u65b9\u4eca\u660e\u71c3\u987b\u5927\u7b11\uff0c\u53c8\u996e \u4e86\u6ee1\u6ee1\u4e00\u676f\u3002\u65b9\u96ea\u541b\u4ecd\u662f\u83ab\u540d\u5176\u5999\uff0c\u6014\u6014\u5730\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u4e5f\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u628a\u58f6\u4e2d\u4f59\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4ed6\u82e5\u8d1f\u4f60\u6240\u6258\uff0c\u6211\u5c31\u7ed9 \u4f60\u6253\u4ed6\u4e09\u5341\u94c1\u62d0\uff01\u54c8\u54c8\uff0c\u60f3\u4e0d\u5230\u6211\u4eca\u665a\u5012\u505a\u4e86\u4e16\u5916\u5947\u7f18\u7684\u89c1\u8bc1\u4e4b\u4eba\uff01\u201d \u3000\u3000\u9f99\u7075\u77eb\u9053\uff1a\u201c\u5144\u53f0\u9189\u4e86\uff01\u201d\u91d1\u4e16\u9057\u5927\u7b11\u9053\uff1a\u201c\u7aef\u7684\u9189\u4e86\uff0c\u6211\u53ea\u6709\u7f18\u4f5c\u8bc1\uff0c\u65e0\u7f18\u518d\u996e\u4f60\u7684 \u9152\u4e86\uff01\u201d\u628a\u9152\u58f6\u201c\u7830\u201d\u7684\u4e00\u58f0\u63b7\u51fa\u95e8\u5916\uff0c\u7acb\u523b\u5012\u5728\u5730\u4e0a\uff0c\u547c\u547c\u719f\u7761\u3002 \u3000\u3000\u9f99\u7075\u77eb\u5374\u662f\u6ee1\u6000\u5fc3\u4e8b\uff0c\u54ea\u7761\u5f97\u7740\uff0c\u597d\u5bb9\u6613\u71ac\u5230\u5929\u660e\uff0c\u53ea\u89c1\u91d1\u4e16\u9057\u4e00\u4e2a\u7ffb\u8eab\u8df3\u8d77\uff0c\u63c9\u63c9\u773c \u775b\uff0c\u8fce\u7740\u5c04\u5165\u6765\u7684\u6668\u66e6\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u53c8\u662f\u4e00\u5929\u5566\uff01\u201d\u62fe\u8d77\u94c1\u62d0\uff0c\u8e22\u5f00\u5927\u95e8\uff0c\u5927\u53eb\u9053\uff1a \u201c\u6765\uff0c\u6765\uff0c\u6765\uff01\u4f60\u4e14\u770b\u6211\u7ed9\u4f60\u6253\u53d1\u90a3\u51e0\u4e2a\u5c0f\u8d3c\uff01\u201d \u3000\u3000\u5927\u8e0f\u6b65\u8d70\u51fa\u95e8\u5916\uff0c\u53ea\u89c1\u90a3\u51e0\u4e2a\u654c\u4eba\u90fd\u805a\u5728\u4e00\u5806\uff0c\u5374\u591a\u4e86\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u3001\u957f\u53d1\u62ab\u80a9\u3001\u78a7\u773c \u9ec4\u987b\u7684\u5916\u56fd\u4eba\uff0c\u6b63\u4fef\u4e0b\u8eab\u8eaf\u66ff\u90a3\u4e2a\u4e2d\u4e86\u6bd2\u9488\u7684\u654c\u4eba\u6309\u6469\u3002\u8fd9\u4e2a\u4eba\u6b63\u662f\u63d0\u6469\u8fbe\u591a\uff0c\u4ed6\u662f\u542c\u5230\u5f1f \u5b50\u5439\u7684\u53e3\u54e8\u58f0\u8d76\u4e0a\u6765\u7684\uff0c\u521a\u5230\u4e0d\u4e45\uff0c\u8fd9\u65f6\u6b63\u7528\u6df1\u6e5b\u7684\u5185\u529f\uff0c\u66ff\u5f1f\u5b50\u5438\u51fa\u4f53\u5185\u7684\u6bd2\u9488\u3002 \u3000\u3000\u53ea\u89c1\u63d0\u6469\u8fbe\u591a\u7684\u638c\u5fc3\u5728\u90a3\u5f1f\u5b50\u7684\u80cc\u5fc3\u8f6c\u4e86\u51e0\u8f6c\uff0c\u5ffd\u5730\u53eb\u4e86\u4e00\u58f0\uff0c\u624b\u638c\u4e00\u8d77\uff0c\u53cc\u6307\u62c8\u7740\u4e00 \u6839\u4eae\u6676\u6676\u7684\u94f6\u9488\uff0c\u5495\u5495\u55dc\u55dc\u7684\u76f4\u9a82\u3002\u91d1\u4e16\u9057\u542c\u4e0d\u61c2\u4ed6\u7684\u8bdd\uff0c\u4e5f\u731c\u5f97\u5230\u4ed6\u662f\u9a82\u81ea\u5df1\u7684\u6697\u5668\u72e0 \u6bd2\u3002\u6cf0\u5409\u63d0\u53d7\u4e86\u91cd\u4f24\uff0c\u65e0\u6cd5\u8fd0\u6c14\uff0c\u517b\u4e86\u4e00\u591c\uff0c\u8d8a\u53d1\u91cd\u4e86\uff0c\u8fd9\u65f6\u5750\u5728\u5730\u4e0a\uff0c\u4e0d\u6562\u52a8\u5f39\uff0c\u89c1\u91d1\u4e16 \u9057\u73b0\u8eab\uff0c\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u5411\u91d1\u4e16\u9057\u6307\u4e86\u4e00\u6307\uff0c\u7528\u963f\u62c9\u4f2f\u8bdd\u53eb\u9053\uff1a\u201c\u5c31\u662f\u4ed6\uff01\u201d\u53c8\u7528\u4e2d\u56fd\u8bdd\u5411 \u91d1\u4e16\u9057\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u63d0\u6469\u8fbe\u591a\u5927\u6cd5\u5e08\u6765\u4e86\uff0c\u7ba1\u53eb\u4f60\u4eec\u4e00\u4e2a\u4e2a\u90fd\u96be\u9003\u6d3b\u547d\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u7684\u6bd2\u9488\u662f\u7528\u86c7\u5c9b\u6700\u6bd2\u7684\u91d1\u7ebf\u86c7\u7684\u53e3\u6d8e\u6240\u70bc\uff0c\u4f24\u4eba\u4e4b\u540e\uff0c\u4e8c\u5341\u56db\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u6bd2\u6c14 \u5373\u653b\u4eba\u5fc3\u5934\uff0c\u65e0\u836f\u53ef\u6551\uff0c\u800c\u4eca\u7ade\u88ab\u63d0\u6469\u8fbe\u591a\u7528\u638c\u5fc3\u5438\u51fa\uff0c\u8fd9\u4efd\u5185\u529f\uff0c\u786e\u662f\u4e0d\u53ef\u601d\u8bae\u3002\u91d1\u4e16\u9057 \u4e5f\u4e0d\u7981\u5fc3\u4e2d\u4e00\u51db\uff0c\u4f46\u4ed6\u81ea\u77e5\u6b7b\u671f\u5c06\u81f3\uff0c\u5bf9\u4efb\u4f55\u5f3a\u654c\uff0c\u4e5f\u4e86\u65e0\u754f\u60e7\uff0c\u542c\u4e86\u6cf0\u5409\u63d0\u7684\u6307\u65a5\uff0c\u53cd\u800c \u54c8\u54c8\u5927\u7b11\uff0c\u8fce\u4e0a\u524d\u53bb\uff0c\u201c\u5478\u201d\u7684\u5550\u4e86\u4e00\u53e3\uff0c\u53eb\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u6bd2\u9488\u662f\u6211\u53d1\u7684\uff0c\u4ec0\u4e48\u5927\u6cd5\u5e08\uff0c\u4f60 \u61c2\u4e0d\u61c2\u5f97\u8d85\u5e7d\u5ea6\u9b3c\uff01\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u8863\u8896\u4e00\u62c2\uff0c\u5c06\u91d1\u4e16\u9057\u6742\u5728\u53e3\u6d8e\u4e2d\u7684\u51e0\u53e3\u6bd2\u9488\u3002\u62c2\u5f97\u65e0\u8e2a\u65e0\u5f71\uff0c\u731b\u7684\u5927\u543c\u4e00\u58f0\uff0c \u4e00\u638c\u5411\u91d1\u4e16\u9057\u62cd\u4e0b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u94c1\u62d0\u4e00\u4e3e\uff0c\u4e00\u62db\u201c\u98de\u9f99\u5728\u5929\u201d\uff0c\u75be\u8d77\u800c\u8fce\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u90a3\u94c1\u62d0\u5f2f\u4e86\u8fc7\u6765\uff0c \u63d0\u6469\u8fbe\u591a\u7684\u864e\u53e3\u4e5f\u9707\u5f97\u5927\u75db\u3002\u6bd4\u5bf9\u4e4b\u4e0b\uff0c\u867d\u7136\u662f\u91d1\u4e16\u9057\u5403\u4e86\u4e8f\uff0c\u63d0\u6469\u8fbe\u591a\u5374\u4e5f\u4e0d\u6562\u8f7b\u89c6\uff0c\u5de6 \u638c\u8fde\u73af\u51fb\u5230\uff0c\u91d1\u4e16\u9057\u65e9\u5df2\u62d4\u51fa\u62d0\u4e2d\u94c1\u5251\uff0c\u63d0\u6469\u8fbe\u591a\u90a3\u4e00\u638c\u62cd\u4e0b\uff0c\u6b63\u6b63\u8fce\u7740\u5251\u5c16\uff0c\u91d1\u4e16\u9057\u4e00\u5251 \u6233\u53bb\uff0c\u5fc3\u9053\uff1a\u8fd9\u4e00\u5251\u8fd8\u4e0d\u628a\u4f60\u7684\u624b\u638c\u6233\u7a7f\uff1f \u3000\u3000\u90a3\u6599\u63d0\u6469\u8fbe\u591a\u638c\u52bf\u500f\u7136\u800c\u6b62\uff0c\u91d1\u4e16\u9057\u9aa4\u89c9\u4e24\u80a1\u529b\u9053\uff0c\u4e00\u9f50\u653b\u5230\uff0c\u4e00\u63a8\u4e00\u62c9\uff0c\u7adf\u662f\u7acb\u8db3\u4e0d \u7a33\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u6ef4\u6e9c\u6e9c\u7684\u8f6c\u4e86\u51e0\u4e2a\u5708\u3002\u63d0\u6469\u8fbe\u591a\u6840\u6840\u602a\u7b11\uff0c\u5de6\u4e00\u638c\uff0c\u53f3\u4e00\u638c\uff0c\u638c\u638c\u62cd\u5411\u91d1\u4e16 \u9057\u547d\u95e8\u8981\u5bb3\uff0c\u91d1\u4e16\u9057\u867d\u8d25\u4e0d\u4e71\uff0c\u5ffd\u7136\u987a\u7740\u8eab\u5b50\u65cb\u8f6c\u4e4b\u52bf\uff0c\u4e00\u4e2a\u201c\u7075\u7334\u5012\u7eb5\u201d\u6253\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c \u94c1\u62d0\u970d\u5730\u4e00\u626b\uff0c\u5c45\u7136\u5316\u89e3\u4e86\u63d0\u6469\u8fbe\u591a\u6253\u4ed6\u7684\u81f4\u547d\u7684\u4e00\u62db\u3002\u63d0\u6469\u8fbe\u591a\u5927\u4e3a\u9640\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u4e2d\u56fd \u7684\u6b66\u672f\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u7adf\u4e5f\u4e0d\u5728\u90a3\u59d3\u5510\u7684\u4e4b\u4e0b\u3002\u201d\u6218\u672f\u4e00\u6539\uff0c\u7531\u6025\u653b\u6539 \u4e3a\u7f13\u53d6\uff0c\u8fd0\u7528\u9634\u9633\u638c\u529b\uff0c\u5c06\u91d1\u4e16\u9057\u56f0\u4f4f\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u4e00\u638c\u63a5\u7740\u4e00\u638c\u7f13\u7f13\u62cd\u51fa\uff0c\u770b\u4f3c\u8f7b\u63cf\u6de1\u5199\uff0c\u5b9e\u5df2\u7528\u4e86\u5168\u529b\uff0c\u91d1\u4e16\u9057\u4f46\u89c9\u654c\u4eba\u7684\u529b \u9053\u4ece\u56db\u65b9\u516b\u9762\u63a8\u6324\u8feb\u6765\uff0c\u6709\u5982\u7f6e\u8eab\u5728\u6f29\u6da1\u4e4b\u4e2d\uff0c\u8fdb\u9000\u4e0d\u5f97\u3002 \u3000\u3000\u65b9\u4eca\u660e\u6276\u7740\u5973\u513f\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u76d8\u819d\u5750\u5728\u95e8\u524d\uff0c\u51dd\u76ee\u6ce8\u89c6\uff0c\u6447\u5934\u53f9\u606f\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef \u60dc\uff01\u201d\u65b9\u96ea\u541b\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u529f\u529b\u4e4b\u9ad8\uff0c\u9664\u4e86\u6709\u9650\u51e0\u4f4d\u524d \u8f88\u9ad8\u4eba\u4e4b\u5916\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u6050\u6015\u65e0\u4eba\u80fd\u4e0e\u5339\u654c\uff0c\u82f1\u5e74\u56fd\u624b\uff0c\u65e9\u5f52\u9ec4\u571f\uff0c\u5c82\u4e0d\u4ee4\u4eba\u6168\u53f9\uff1f\u201d\u9f99\u7075 \u77eb\u4e0d\u77e5\u9053\u91d1\u4e16\u9057\u7684\u751f\u547d\u53ea\u6709\u516d\u65e5\u671f\u9650\uff0c\u53ea\u9053\u65b9\u4eca\u660e\u662f\u6307\u76ee\u524d\u4e4b\u6218\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u75af\u4e10\u6628\u665a\u66fe\u7ecf \u6551\u6211\uff0c\u6211\u5c82\u53ef\u8ba9\u4ed6\u72ec\u6297\u5f3a\u654c\uff1f\u201d\u62d4\u5251\u6b32\u51fa\uff0c\u4f46\u89c1\u63d0\u6469\u8fbe\u591a\u7684\u90a3\u56db\u4e2a\u5f1f\u5b50\uff0c\u6392\u6210\u534a\u4e2a\u5f27\u5f62\uff0c\u6b63 \u662f\u864e\u89c6\u7708\u7708\uff0c\u9f99\u7075\u77eb\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u65b9\u8001\u4f2f\u8eab\u53d7\u91cd\u4f24\uff0c\u654c\u4eba\u82e5\u653b\u8fc7\u6765\uff0c\u51ed\u96ea\u59b9\u4e00\u4eba\uff0c\u600e \u80fd\u9632\u62a4\uff1f\u201d\u624b\u6309\u5251\u67c4\uff0c\u8e0c\u8e87\u96be\u51b3\u3002\u5ffd\u542c\u5f97\u65b9\u4eca\u660e\u4e00\u58f0\u6b22\u547c\uff0c\u53eb\u9053\uff1a\u201c\u5510\uff0c\u5510\u3001\u5510\u5927\u4fa0\u592b\u5987\u6765 \u5566\uff01\u201d\u6b22\u559c\u8fc7\u5ea6\uff0c\u58f0\u97f3\u98a4\u6296\u5636\u54d1\uff01 \u3000\u3000\u91d1\u4e16\u9057\u6b63\u81ea\u5168\u795e\u8d2f\u6ce8\uff0c\u5bf9\u4ed8\u63d0\u6469\u8fbe\u591a\u7684\u9634\u9633\u638c\u529b\uff0c\u5934\u660f\u8111\u80c0\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u542c\u5230\u65b9\u4eca\u660e\u53eb \u4e9b\u4ec0\u4e48\u3002\u5ffd\u89c9\u8eab\u4e0a\u4e00\u8f7b\uff0c\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u4e00\u6761\u957f\u8896\u8fce\u9762\u62c2\u6765\uff0c\u91d1\u4e16\u9057\u5927\u5403\u4e00\u60ca\uff0c\u6b32\u5f85\u95ea\u907f\uff0c \u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u7adf\u4f3c\u88ab\u4eba\u5e73\u7a7a\u6258\u8d77\uff0c\u91d1\u4e16\u9057\u987a\u7740\u8fd9\u80a1\u529b\u9053\uff0c\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u51fa\u53bb\uff0c\u4f46\u89c1\u63d0\u6469\u8fbe \u591a\u4e5f\u8e09\u8e09\u8dc4\u8dc4\u7684\u5411\u540e\u8fde\u9000\u4e86\u5341\u51e0\u6b65\u3002 \u3000\u3000\u5510\u6653\u6f9c\u6765\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u8981\u4e0d\u662f\u4ed6\u53cc\u8896\u9f50\u62c2\uff0c\u4e00\u4e3e\u62c2\u5f00\u4e86\u63d0\u6469\u8fbe\u591a\u4e0e\u91d1\u4e16\u9057\u4e8c\u4eba\uff0c\u518d\u8fc7\u7247 \u523b\uff0c\u91d1\u4e16\u9057\u5185\u529b\u652f\u6301\u4e0d\u4f4f\uff0c\u5fc5\u88ab\u63d0\u6469\u8fbe\u591a\u7684\u9633\u9633\u638c\u529b\u538b\u5f97\u7a92\u606f\u95ed\u6c14\u3002\u6b64\u65f6\u4ed6\u867d\u8131\u8eab\uff0c\u4f46\u9634\u9633 \u638c\u529b\u7684\u540e\u52b2\u5c1a\u672a\u6d88\u89e3\uff0c\u5140\u81ea\u5728\u5730\u4e0a\u65cb\u8f6c\u4e0d\u4f11\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u7eb5\u6a2a\u6b27\u6d32\u4e0e\u963f\u62c9\u4f2f\u8bf8\u56fd\uff0c\u4ece\u65e0\u5bf9\u624b\uff0c\u4e00\u7167\u9762\u5c31\u7ed9\u6765\u4eba \u6325\u8896\u62c2\u5f00\uff0c\u4e0d\u89c9\u88ab\u5510\u6653\u6f9c\u7684\u795e\u5a01\u9707\u6151\uff0c\u867d\u7136\u7acb\u5373\u6251\u4e86\u4e0a\u6765\uff0c\u5374\u4e0d\u6562\u52a8\u624b\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u662f \u4f55\u4eba\uff1f\u600e\u7684\u5728\u6211\u8001\u53cb\u7684\u95e8\u524d\u80e1\u95f9\uff1f\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u542c\u4e0d\u61c2\u5510\u6653\u6f9c\u7684\u8bdd\uff0c\u4f46\u89c9\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u867d\u7136\u4e0d\u9ad8\uff0c\u8033\u9f13\u5374\u7ed9\u9707\u5f97\u55e1\u55e1\u4f5c\u54cd\uff0c\u63d0 \u6469\u8fbe\u591a\u6025\u5fd9\u8fd0\u6c14\u6258\u5fa1\uff0c\u6cf0\u5409\u63d0\u5c1a\u81ea\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u4ee3\u4e3a\u7b54\u9053\uff1a\u201c\u7eb5\u6a2a\u6b27\u4e9a\uff0c\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u5927\u6cd5 \u5e08\u63d0\u6469\u8fbe\u591a\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u4ef0\u5929\u5927\u7b11\uff0c\u626c\u8896\u4e00\u62c2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u89c1\u8fc7\u6562\u81ea\u79f0\u5929\u4e0b\u7b2c\u4e00\u7684\u4eba\u3002\u4eca\u65e5\u5012\u8981\u89c1 \u8bc6\u89c1\u8bc6\u5916\u56fd\u7684\u6b66\u529f\u3002\u597d\u5440\uff0c\u4f60\u7684\u638c\u529b\u662f\u6709\u70b9\u90aa\u95e8\uff0c\u6211\u5c31\u5148\u8ba9\u4f60\u6253\u6211\u5341\u638c\u3002\u201d\u4ed6\u8fd9\u4e00\u62c2\uff0c\u529b\u9053 \u5206\u88ad\u63d0\u6469\u8fbe\u591a\u4e0e\u6cf0\u5409\u63d0\u4e8c\u4eba\uff0c\u63d0\u6469\u8fbe\u591a\u5168\u529b\u62b5\u5fa1\uff0c\u8eab\u8eaf\u4e0d\u8fc7\u6643\u4e86\u4e00\u6643\uff0c\u6cf0\u5409\u63d0\u8ddd\u79bb\u4e8c\u4e09\u5341\u6b65 \u4e4b\u5916\uff0c\u5374\u88ab\u5510\u6653\u6f9c\u6325\u8896\u7684\u52b2\u98ce\u4e00\u62c2\uff0c\u5495\u54af\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u8981\u4e0d\u662f\u540c\u95e8\u62a2\u6551\u5f97 \u5feb\uff0c\u8d76\u7d27\u5c06\u4ed6\u6276\u8d77\uff0c\u51e0\u4e4e\u5c31\u8981\u6eda\u4e0b\u5c71\u5761\u3002 \u3000\u3000\u6cf0\u5409\u63d0\u5636\u58f0\u53eb\u9053\uff0c\u201c\u6cd5\u5e08\u4e0d\u5fc5\u548c\u4ed6\u5ba2\u6c14\uff0c\u4ed6\u8bf4\u4ed6\u8ba9\u4f60\u5148\u6253\u5341\u638c\uff0c\u53ea\u8981\u9664\u6b64\u5f3a\u654c\uff0c\u4e2d\u56fd\u5c31 \u65e0\u4eba\u518d\u6562\u4e0e\u4f60\u76f8\u6297\u3002\u201d\u6cf0\u5409\u6377\u7ecf\u5e38\u5728\u5c3c\u6cca\u5c14\u4e0e\u897f\u85cf\u4e4b\u95f4\u6765\u5f80\uff0c\u5bf9\u4e2d\u56fd\u7684\u6b66\u6797\u540d\u624b\uff0c\u867d\u672a\u8ba4 \u8bc6\uff0c\u4e5f\u6709\u8033\u95fb\uff0c\u542c\u5230\u65b9\u4eca\u660e\u7684\u547c\u558a\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u4e5f\u6599\u5230\u662f\u5929\u5c71\u6d3e\u7684\u638c\u95e8\u5510\u6653\u6f9c\u5230\u4e86\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u54ea\u66fe\u53d7\u8fc7\u5982\u6b64\u8f7b\u8511\uff0c\u6c89\u4f4f\u4e86\u6c14\uff0c\u53cc\u638c\u63a5\u8fde\u62cd\u51fa\uff0c\u53ea\u89c1\u5510\u6653\u6f9c\u8db3\u8ddf\u7262\u7262\u9489\u5728\u5730 \u4e0a\uff0c\u72b9\u5982\u6253\u4e86\u6869\u4f3c\u7684\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u3002\u63d0\u6469\u8fbe\u591a\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u638c\u7d27\u4f3c\u4e00\u638c\uff0c\u53ea\u89c1\u5510\u6653\u6f9c\u6e56\u6c34\u8272\u7684 \u957f\u886b\u968f\u7740\u638c\u98ce\u98d8\u52a8\uff0c\u4ed6\u7684\u811a\u6b65\u5374\u59cb\u7ec8\u672a\u66fe\u79fb\u52a8\u5206\u6beb\u3002\u63d0\u6469\u8fbe\u591a\u7528\u5c3d\u5168\u529b\uff0c\u731b\u7684\u5927\u543c\u4e00\u58f0\uff0c\u53cc \u638c\u9f50\u51fa\uff0c\u9634\u9633\u638c\u529b\uff0c\u5de6\u63a8\u53f3\u5f15\uff01\u5510\u6653\u6f9c\u8eab\u8eaf\u7565\u6643\uff0c\u63d0\u8d77\u5de6\u8db3\uff0c\u5212\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u8e0f\u4e0b\u8db3\u6765\uff0c\u4ecd \u7136\u7ad9\u5728\u539f\u4f4d\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5341\u62db\u5df2\u6ee1\uff0c\u4f60\u80fd\u4f7f\u6211\u8eab\u5f62\u6643\u52a8\uff0c\u4ea6\u7b97\u96be\u5f97\u4e86\uff01\u597d\uff0c\u4f60\u4e5f\u63a5\u6211\u6570 \u62db\uff01\u201d\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u52b2\u98ce\u9aa4\u8d77\uff0c\u5929\u5c71\u795e\u638c\uff0c\u5b9e\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u63d0\u6469\u8fbe\u591a\u54ea\u6562\u5b66\u5510\u6653\u6f9c \u7684\u6837\u5b50\uff0c\u7eaf\u7528\u5185\u529f\u62b5\u5fa1\uff0c\u5f53\u4e0b\u53cc\u638c\u62a4\u80f8\uff0c\u62fc\u529b\u5f80\u5916\u4e00\u63a8\uff0c\u8eab\u8eaf\u4ecd\u662f\u4e0d\u7531\u81ea\u5df1\u7684\u5411\u540e\u8fde\u9000\u4e09 \u6b65\u3002\u5510\u6653\u6f9c\u4e00\u58f0\u957f\u5578\uff0c\u8e0f\u4e0a\u4e00\u6b65\uff0c\u547c\u7684\u53c8\u662f\u4e00\u638c\u62cd\u51fa\uff0c\u63d0\u6469\u8fbe\u591a\u53cc\u638c\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u659c\u8d70\u75be \u907f\uff0c\u4ecd\u7136\u88ab\u5510\u6653\u6f9c\u7684\u638c\u529b\u8feb\u5f97\u7acb\u8db3\u4e0d\u7a33\uff0c\u6709\u5982\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6643\u6643\uff0c\u51e0\u4e4e\u683d\u5012\uff01\u5510\u6653\u6f9c\u518d\u8e0f \u524d\u4e00\u6b65\uff0c\u7b2c\u4e09\u638c\u53c8\u5f85\u8fde\u73af\u8feb\u51fa\uff0c\u63d0\u6469\u8fbe\u591a\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u4e14\u4f4f\uff0c\u4e14\u4f4f\uff01\u201d\u5510\u6653\u6f9c\u6014\u4e86\u4e00\u6014\uff0c\u56de \u987e\u6cf0\u5409\u63d0\u9053\uff1a\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u5495\u5495\u55dc\u55dc\u7684\u8bf4\u4e86\u4e00\u901a\uff0c\u6cf0\u5409\u63d0\u65ad\u65ad\u7eed\u7eed\u7684\u4ee3\u4e3a\u7ffb\u8bd1\u9053\uff1a\u201c\u5927\u3001\u5927\u3001\u5927\u6cd5\u5e08\u8bf4\uff0c \u8bf4\u3001\u8bf4\u4ed6\u3001\u4ed6\u548c\u4f60\uff0c\u90fd\u3001\u90fd\u662f\u5e76\u4e16\u9ad8\u624b\uff0c\u786c\u6253\u786c\u62fc\uff0c\u6709\u5931\u8eab\u4efd\uff0c\u4ed6\u3001\u4ed6\u3001\u4ed6\u8981\u4e0e\u4f60\u53e6\u3001\u53e6\u6362 \u4e00\u4e2a\u65b9\u6cd5\uff0c\u8d4c\u3001\u8d4c\u8d5b\u2026\u2026\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u600e\u6837\u8d4c\u8d5b\uff1f\u201d\u6cf0\u5409\u63d0\u9053\uff1a\u201c\u8d4c\u3001\u8d4c\u8d5b\u6500\u3001\u6500\u5c71\uff0c\u770b \u8c01\u80fd\u6500\u4e0a\u4e16\u754c\u7b2c\u4e00\u9ad8\u5cf0\uff1f\u201d\u628a\u8bdd\u8bf4\u5b8c\uff0c\u58f0\u5636\u529b\u7aed\uff0c\u767b\u65f6\u6655\u6b7b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u73e0\u7a46\u6717\u739b\u5cf0\u662f\u4e2d\u56fd\u7684\uff0c\u5c31\u662f\u4e0d\u63d0\u8d4c\u8d5b\uff0c\u4e2d\u56fd\u4eba\u4e5f\u8981\u4e0a\u6b64\u9ad8 \u5cf0\uff01\u201d\u65b9\u4eca\u660e\u53eb\u9053\uff1a\u201c\u5510\u5927\u4fa0\uff0c\u4e0d\uff0c\u4e0d\u2026\u2026\u201d\u6c14\u529b\u5fae\u5f31\uff0c\u5362\u97f3\u5636\u54d1\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c \u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u8fd9\u65f6\u5df2\u6b62\u4e86\u65cb\u8f6c\u4e4b\u52bf\uff0c\u65b9\u4eca\u660e\u7684\u8bdd\uff0c\u4f20\u5165\u8033\u4e2d\uff0c\u91d1\u4e16\u9057\u5446\u82e5\u6728\u9e21\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765 \u662f\u5510\u7ecf\u5929\u7684\u7236\u4eb2\u3002\u201d\u5934\u8111\u660f\u4e71\uff0c\u60f3\u8d77\u5f53\u4eca\u4e4b\u4e16\uff0c\u53ea\u6709\u6b64\u4eba\u80fd\u6551\u81ea\u5df1\u7684\u6027\u547d\uff0c\u51e0\u4e4e\u558a\u51fa\u58f0\u6765\uff0c \u5ffd\u5730\u53c8\u60f3\u8d77\u4ed6\u662f\u5510\u7ecf\u5929\u7684\u7236\u4eb2\uff0c\u60f3\u8d77\u8463\u592a\u6e05\u7684\u8c17\u8a00\uff0c\u8bf4\u662f\u5510\u6653\u6f9c\u5992\u5fcc\u4ed6\u8fd9\u4e00\u6d3e\u7684\u6b66\u529f\uff0c\u81ea\u5df1 \u82e5\u53bb\u6c42\u4ed6\uff0c\u4ee5\u540e\u5c31\u6c38\u8fdc\u62ac\u4e0d\u8d77\u5934\u6765\u3002\u970e\u65f6\u95f4\u601d\u6f6e\u8f6c\u4e86\u6570\u5341\u767e\u904d\uff0c\u7a81\u7136\u56de\u8eab\u4fbf\u8d70\uff0c\u731b\u4e00\u62ac\u5934\uff0c \u5ffd\u89c1\u4e00\u4e2a\u4e2d\u5e74\u7f8e\u5987\uff0c\u4ece\u5c71\u5cf0\u4e0a\u98d8\u7136\u800c\u4e0b\uff0c\u91d1\u4e16\u9057\u597d\u4f3c\u88ab\u4eba\u5b9a\u7740\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u4e00\u5b9a\u8981 \u8feb\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6b32\u4e0a\u73e0\u5cf0\u6458\u661f\u6597\uff0c\u751f\u6765\u72c2\u50b2\u4e0d\u6c42\u601c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u5927\u96ea\u5bd2\u98ce\u3000\u9ad8\u5c71\u6d88\u9738\u6c14\u3000\u8f7b\u601c\u5bc6\u7231\u3000\u51b0\u5854\u6551\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u4e09\u5341\u4e5d\u56de \u5927\u96ea\u5bd2\u98ce\u3000\u9ad8\u5c71\u6d88\u9738\u6c14 \u8f7b\u601c\u5bc6\u7231\u3000\u51b0\u5854\u6551\u4f73\u4eba \u3000\u3000\u8fd9\u5c11\u5987\u6b63\u662f\u5510\u6653\u6f9c\u7684\u59bb\u5b50\u51af\u745b\uff0c\u91d1\u4e16\u9057\u9519\u628a\u5979\u5f53\u6210\u4e86\u51af\u7433\u3002\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff1a\u201c\u8fd9\u56de\u5979\u5fc5 \u5b9a\u4e0d\u80af\u653e\u6211\u8d70\u5f00\uff0c\u8981\u5f3a\u8feb\u6211\u63a5\u53d7\u5510\u6653\u6f9c\u7684\u6069\u60e0\u4e86\u3002\u201d \u3000\u3000\u51af\u73bb\u4e00\u542c\u91d1\u4e16\u9057\u7684\u6d3b\uff0c\u5982\u5760\u4e94\u670d\u96fe\u4e2d\uff0c\u6478\u4e0d\u7740\u5934\u8111\uff0c\u8be7\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u89c1\u5979 \u4e00\u526f\u51b7\u50b2\u7684\u795e\u6c14\uff0c\u5fc3\u4e2d\u6012\u706b\u7a81\u53d1\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4f60\u4ee5\u524d\u5bf9\u6211\u597d\uff0c\u90fd\u662f\u5047\u4ec1\u5047\u4e49\uff0c\u89c1\u6211\u6b7b\u671f\u5728 \u5373\uff0c\u5374\u53c8\u6362L\u4e86\u8fd9\u6837\u7684\u4e00\u526f\u51b7\u9762\u5b54\u4e86\u3002\u5440\uff0c\u4eba\u60c5\u51b7\u6de1\uff0c\u4e16\u6001\u708e\u51c9\uff0c\u8fd9\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\uff01\u201d\u91d1\u4e16 \u9057\u5c31\u662f\u8fd9\u6837\u7684\u602a\u813e\u6c14\uff0c\u4ed6\u4e0d\u5e0c\u671b\u6cbe\u522b\u4eba\u7684\u6069\u60e0\uff0c\u5374\u53c8\u70ed\u76fc\u6709\u4eba\u5173\u6000\u4ed6\u3002\u4ed6\u65e2\u6015\u51af\u7433\u7f20\u4ed6\uff0c\u4f46 \u4e00\u65e6\u611f\u5230\u53d7\u5979\u51b7\u843d\u4e4b\u65f6\uff0c\u5374\u53c8\u66f4\u589e\u6012\u6c14\u3002 \u3000\u3000\u51af\u745b\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u53c8\u662f\u6211\u59b9\u59b9\u60f9\u6765\u7684\u4e8b\u60c5\uff1f\u201d\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u51c6\uff1a\u4ec0\u4e48\u4e8b \u60c5\u3001\u597d\u597d\u7684\u5bf9\u6211\u8bf4\u5427\uff01\u201d\u91d1\u4e16\u9057\u7a81\u7136\u4e00\u58f0\u602a\u53eb\uff0c\u558a\u9053\uff1a\u201c\u597d\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u53ea\u5f53\u4f60\u6211\u672a\u66fe\u76f8 \u8bc6\uff0c\u653e\u6211\u8d70\u5f00\u3002\u201d\u4ed6\u53ea\u6015\u51af\u745b\u51fa\u624b\u62e6\u963b\uff0c\u4e0d\u987e\u4e00\u5207\uff0c\u98de\u8eab\u8dc3\u8d77\uff0c\u4e00\u62d0\u626b\u53bb\u3002\u4ee5\u89c1\u51af\u745b\u8f7b\u8212\u7389 \u81c2\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u51c6\u8981\u7559\u4f60\uff1f\u201d\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u4e16\u9057\u7684\u94c1\u62d0\u88ab\u5979\u4e00\u5f39\uff0c \u767b\u65f6\u4e00\u80a1\u529b\u9053\u4f20\u4e86\u8fc7\u6765\uff0c\u91d1\u4e16\u9057\u7ade\u7834\u8fd9\u80a1\u529b\u9053\u63a8\u5f97\u5728\u7a7a\u4e2d\u8fde\u7ffb\u4e86\u4e09\u4e2a\u7b4b\u6597\u3002\u91d1\u4e16\u9057\u843d\u4e0b\u5c71 \u5761\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u4ee5\u524d\u66fe\u89c1\u51af\u7433\u7684\u672c\u9886\uff0c\u867d\u7136\u6781\u4e4b\u4f69\u670d\uff0c\u5374\u4e5f\u60f3\u4e0d\u5230\u5982\u6b64\u795e\u901a\uff0c\u5fc3 \u9053\uff1a\u201c\u5e78\u4e8f\u5979\u65e0\u610f\u4f5c\u5f04\u6211\u3002\u8981\u4e0d\u7136\u6211\u53ea\u6709\u542c\u5979\u6446\u5e03\u7684\u4efd\u513f\u4e86\u3002\u201d\u5fc3\u4e2d\u51db\u60e7\uff0c\u6025\u5fd9\u6500\u4e0a\u5bf9\u9762\u7684 \u5c71\u5cf0\uff0c\u4e0d\u6562\u518d\u56de\u5934\u671b\u51af\u745b\u4e00\u773c\u3002\u4ed6\u54ea\u77e5\u9053\u51af\u745b\u7684\u6b66\u529f\u8fdc\u5728\u51af\u7433\u4e4b\u4e0a\uff0c\u51e0\u4e4e\u4e0e\u5415\u56db\u5a18\u5e76\u9a7e\u9f50 \u9a71\uff0c\u8fd9\u4e00\u5f39\u82e5\u662f\u6362\u4e86\u51af\u7433\uff0c\u81f3\u591a\u53ea\u80fd\u53eb\u91d1\u4e16\u9057\u7ffb\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8fd9\u65f6\u5df2\u770b\u6e05\u695a\u4e86\u65b9\u4eca\u660e\u7684\u4f24\u52bf\uff0c\u7ed9\u4ed6\u670d\u4e86\u4e24\u7c92\u78a7\u7075\u4e39\uff0c\u53c8\u7528\u6700\u4e0a\u4e58\u7684\u5185\u529f\u66ff\u4ed6\u6253 \u901a\u7ecf\u8109\uff0c\u51af\u73ab\u8d70\u4e86\u8fc7\u6765\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5510\u6653\u6f9c\u62cd\u62cd\u624b\u638c\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u4f60\u660e\u65e5\u8d77\u5728\u9759 \u5ba4\u9759\u5750\u5341\u5929\uff0c\u8fd9\u4f24\u52bf\u6599\u60f3\u65e0\u59a8\u3002\u201d\u65b9\u4eca\u660e\u82e6\u7b11\u9053\uff1a\u201c\u5510\u5927\u4fa0\uff0c\u4f60\u4f55\u82e6\u591a\u4e8b\uff0c\u53c8\u8981\u6211\u591a\u6d3b\u51e0 \u5e74\uff1f\u201d\u539f\u6765\u65b9\u4eca\u660e\u5e74\u7eaa\u8001\u8fc8\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u867d\u5f97\u7597\u6cbb\uff0c\u6b66\u529f\u6700\u5c11\u4e5f\u8981\u635f\u5931\u4e00\u534a\uff0c\u4f30\u91cf\u4e5f\u4e0d\u80fd\u6d3b \u591a\u5c11\u5e74\u4e86\u3002 \u3000\u3000\u65b9\u4eca\u660e\u6162\u6162\u62ac\u8d77\u5934\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5510\u5927\u4fa0\uff0c\u6211\u7ed9\u4f60\u4eec\u5f15\u89c1\u4e24\u4f4d\u540e\u8f88\u82f1\u8c6a\u3002\u54a6\uff0c\u90a3\u4f4d\u5c0f \u54e5\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u521a\u624d\u4ed6\u95ed\u76ee\u8fd0\u6c14\uff0c\u63a5\u53d7\u5510\u6653\u6f9c\u7684\u6cbb\u7597\uff0c\u8fd8\u4e0d\u77e5\u9053\u91d1\u4e16\u9057\u5df2\u7ecf\u9003\u8d70\u3002\u51af\u745b\u9053\uff1a \u201c\u90a3\u4eba\u662f\u8c01\uff1f\u600e\u7684\u884c\u5f84\u5982\u6b64\u5947\u602a\uff1f\u201d\u9f99\u7075\u77eb\u9053\uff1a\u201c\u4ed6\u662f\u6c5f\u6e56\u4e0a\u4eba\u79f0\u6bd2\u624b\u75af\u4e10\u7684\u91d1\u4e16\u9057\u3002\u201d\u5510 \u6653\u6f9c\u6ca1\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u54a6\uff0c\u521a\u624d\u6211\u89c1\u4ed6\u7684\u6b66\u529f\u8def\u9053\uff0c\u56de\u60f3\u8d77\u4e00\u4f4d\u8001\u670b \u53cb\u6765\u4e86\u3002\u201d\u51af\u745b\u53eb\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u770b\u4ed6\u7684\u6b66\u529f\u662f\u4e0d\u662f\u6bd2\u9f99\u5c0a\u8005\u7684 \u8def\u5b50\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u5c82\u53ea\u8def\u9053\u76f8\u540c\uff0c\u8fde\u90a3\u5947\u95e8\u5185\u529f\u4e5f\u662f\u4e00\u6837\u7684\u8def\u5b50\u3002\u5440\uff0c\u7cdf\u4e86\uff0c\u53ef\u60dc\u6211\u6ca1\u6709 \u628a\u4ed6\u7559\u4e0b\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u51af\u75ea\u9053\uff1a\u201c\u521a\u624d\u6211\u7528\u4e00\u6307\u7985\u7684\u529f\u592b\uff0c\u5c06\u91d1\u4e16\u9057\u9001\u8d70\uff0c\u4ed6\u4e0d\u77e5\u9053\u6211 \u7684\u597d\u610f\uff0c\u7adf\u7136\u8fd0\u529b\u53cd\u51fb\uff0c\u6309\u8bf4\u662f\u975e\u7acb\u5373\u53d7\u4f24\u4e0d\u53ef\uff0c\u4f46\u4ed6\u7684\u5185\u529f\u602a\u5f02\u975e\u5e38\uff0c\u5c45\u7136\u628a\u56e0\u4ed6\u53cd\u51fb\u800c \u5f15\u8d77\u7684\u6211\u7684\u4e00\u6307\u7985\u7684\u6f5c\u529b\u5316\u89e3\u4e86\u3002\u5929\u4e0b\u53ea\u6709\u6bd2\u9f99\u5c0a\u8005\u6709\u8fd9\u95e8\u81ea\u751f\u81ea\u706d\u7684\u5185\u529f\uff0c\u4f46\u4ed6\u4ece\u94c1\u62d0\u4f20 \u6765\u7684\u5185\u529b\uff0c\u6beb\u65e0\u540e\u52b2\uff0c\u770b\u6765\u5df2\u662f\u8d70\u706b\u4eba\u9b54\u4e4b\u8c61\uff0c\u53ea\u6015\u6b7b\u671f\u5c31\u5728\u8fd9\u51e0\u5929\u4e86\uff01\u201d\u9f99\u7075\u77eb\u542c\u4e86\u5927 \u9a87\uff0c\u8fd9\u624d\u9192\u609f\u91d1\u4e16\u9057\u8bf4\u8bdd\u75af\u75af\u766b\u766b\uff0c\u539f\u6765\u662f\u5c06\u6b7b\u7684\u72c2\u50b2\u54c0\u6124\u7684\u5fc3\u58f0\u3002 \u3000\u3000\u65b9\u4eca\u660e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6628\u80f0\u6211\u4ed4\u7ec6\u5bdf\u770b\u4ed6\u7684\u6c14\u8272\uff0c\u63a8\u6d4b\u4ed6\u6b7b\u671f\u4e0d\u8fc7\u516d\u5929\uff0c\u5510\u592b\u4eba\u4e5f\u8fd9\u4e48 \u8bf4\uff0c\u60f3\u6765\u4e0d\u4f1a\u9519\u4e86\u3002\u201d\u51af\u745b\u53f9\u9053\uff1a\u201c\u82e5\u662f\u6211\u65e9\u77e5\u9053\u4ed6\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u5f1f\u5b50\uff0c\u5b9a\u7136\u628a\u4ed6\u7559\u4e0b\u3002\u6bd2 \u9f99\u5c0a\u8005\u7684\u6b66\u529f\u81ea\u6210\u4e00\u6d3e\uff0c\u82e5\u56e0\u6b64\u800c\u6210\u7edd\u54cd\uff0c\u8fd9\u5012\u662f\u6b66\u5b66\u4e0a\u7684\u5927\u635f\u5931\u5475\uff01\u201d \u3000\u3000\u65b9\u4eca\u660e\u9759\u9ed8\u534a\u664c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e16\u4e0a\u65b0\u4eba\u6362\u65e7\u4eba\u3002\u770b\u6765\u8fd9\u5341\u6570\u5e74\u95f4\uff0c \u6b66\u6797\u4e2d\u7684\u540e\u8f88\u82f1\u8c6a\u5012\u51fa\u4e86\u4e0d\u5c11\u3002\u5510\u5927\u4fa0\uff0c\u6211\u518d\u7ed9\u4f60\u5f15\u89c1\u4e00\u4f4d\u540e\u8f88\u82f1\u8c6a\u3002\u201d\u9f99\u7075\u77eb\u4e0a\u524d\u65bd\u793c\uff0c \u5510\u6653\u6f9c\u4e00\u773c\u77a5\u89c1\u4ed6\u4f69\u5251\u4e0a\u6302\u7740\u7684\u90a3\u4ef6\u9970\u7269\u2014\u2014\u7389\u72ee\u5b50\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f \u6545\u4eba\u4e4b\u5b50\u3002\u4e45\u4ef0\u4e86\uff01\u201d\u9f99\u7075\u77eb\u6ee1\u9762\u7f9e\u60ed\uff0c\u9053\uff1a\u201c\u7f6a\u4eba\u4e4b\u5b50\uff0c\u5c1a\u7948\u6055\u7f6a\u3002\u201d\u5510\u6653\u6f9c\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u5e74\u7fb9\u5c27\u4e4b\u7f6a\u4e0e\u4f60\u4f55\u4e8e\uff1f\u4f60\u7236\u4eb2\u672c\u662f\u4e00\u4ee3\u5c06\u624d\uff0c\u53ef\u60dc\u4e0d\u8d70\u6b63\u8def\u3002\u4f46\u671b\u4f60\u719f\u8bfb\u5175\u4e66\uff0c\u4e3a\u6c11\u6548 \u529b\u3002\u201d\u9f99\u7075\u77eb\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u8c28\u9886\u6559\u8a00\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7ed9\u6211\u4fdd\u5b58\u90a3\u5757\u6c49\u7389\uff0c\u6211\u65e9\u4ece\u7ecf \u5929\u53e3\u4e2d\u4e2d\u77e5\u9053\u4f60\u7684\u4e3a\u4eba\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u540c\u8fdb\u77f3\u5c4b\u53d9\u8bdd\uff0c\u5510\u6653\u6f9c\u542c\u513f\u5b50\u548c\u51b0\u5ddd\u5929\u5973\u4e5f\u90fd\u6765\u4e86\uff0c\u6b22\u559c\u65e0\u9650\uff0c\u5bf9\u51af\u745b\u7b11\u9053\uff1a\u201c\u6211 \u4e0e\u90a3\u5927\u6cd5\u5e08\u6253\u8d4c\u6500\u5c71\uff0c\u4f60\u4e0b\u53bb\u63a2\u8bbf\u4ed6\u4eec\u5427\u3002\u201d\u8bf4\u5c06\u8d77\u6765\uff0c\u539f\u6765\u5510\u6653\u6f9c\u4e5f\u77e5\u9053\u5c3c\u6cca\u5c14\u7684\u5927\u519b\u5c6f \u5728\u4e0b\u9762\u7684\u5c71\u8c37\uff0c\u6015\u6709\u4eba\u4e0a\u6765\u9a9a\u6270\u65b9\u5bb6\uff0c\u6545\u6b64\u7279\u5730\u4e0a\u5c71\u63a2\u95ee\u8001\u53cb\u7684\u3002 \u3000\u3000\u51af\u73bb\u60f3\u8d77\u90a3\u6b21\u5728\u9a7c\u5cf0\u4e4b\u4e0a\uff0c\u51b0\u5ddd\u5929\u5973\u8bef\u4f1a\u5979\u662f\u51af\u7433\u4e8b\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e2a\u672a\u6765\u5ab3\u5987\uff0c\u89c1 \u4e86\u6211\u53ea\u6015\u6c14\u8fd8\u6ca1\u6d88\u5462\u3002\u7433\u59b9\u603b\u662f\u5b69\u5b50\u813e\u6c14\uff0c\u770b\u6765\u8fd9\u4e2a\u6bd2\u624b\u75af\u4e10\u91d1\u4e16\u9057\u4e5f\u662f\u88ab\u5979\u6349\u5f04\u8fc7\u7684\uff0c\u8981 \u4e0d\u7136\u4e0d\u4f1a\u4e00\u89c1\u6211\u5c31\u5413\u5f97\u8981\u9003\u3002\u54a6\uff0c\u8fd9\u662f\u8c01\u6765\u4e86\uff1f\u201d \u3000\u3000\u4f17\u4eba\u968f\u7740\u51af\u745b\u8d70\u51fa\u77f3\u5c4b\uff0c\u53ea\u542c\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u563b\u563b\u7b11\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u53c8\u5728\u80cc\u540e\u9a82\u6211\u4e86\u3002 \u4f60\u95ee\u7ecf\u5929\u53bb\uff0c\u6211\u5f97\u7f6a\u4e86\u4f60\u7684\u5ab3\u5987\uff0c\u53ef\u4e5f\u5e2e\u4e86\u5979\u4e0d\u5c11\u5fd9\u5440\uff01\u201d\u6765\u7684\u6b63\u662f\u51af\u7433\u3002\u5979\u8f7b\u529f\u672c\u6765\u6bd4\u63d0 \u6469\u8fbe\u591a\u9ad8\u5f3a\uff0c\u53ea\u56e0\u4e0d\u719f\u5c71\u8def\uff0c\u53cd\u800c\u843d\u5728\u63d0\u6469\u8fbe\u591a\u4e4b\u540e\uff0c\u800c\u4eca\u624d\u5230\u3002 \u3000\u3000\u51af\u745b\u6b63\u5f85\u8bf4\u8bdd\uff0c\u51af\u7433\u5ffd\u5730\u95ea\u4e86\u8fc7\u6765\uff0c\u5c06\u5979\u63fd\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u662f\u4e0d \u662f\u4f60\u5df2\u7ecf\u89c1\u5230\u91d1\u4e16\u9057\u4e86\uff1f\u201d \u3000\u3000\u51af\u73bb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd9\u6837\u7740\u6025\u505a\u4ec0\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ed6\u521a\u521a\u8d70\u4e86\u3002\u201d\u51af\u7433\u53eb\u9053\uff1a\u201c\u5440\uff0c \u4f60\u77e5\u9053\u4e0d\u77e5\u9053\u4ed6\u7684\u751f\u547d\u671f\u9650\u53ea\u6709\u516d\u5929\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d\u51af\u7433\u5927\u53eb\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u89c1 \u6b7b\u4e0d\u6551\uff1f\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u8c01\u53eb\u4ed6\u4e00\u89c1\u9762\u5c31\u6253\u6211\u4e00\u62d0\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u522b\u518d\u6fc0\u607c\u4f60\u7684\u7433\u59b9\u5566\u3002 \u6ca1\u6709\u5c06\u91d1\u4e16\u9057\u7559\u4e0b\uff0c\u6211\u4e5f\u9057\u61be\u5f97\u5f88\u3002\u201d\u5f53\u4e0b\u5c06\u9002\u624d\u7684\u60c5\u5f62\u8bf4\u4e86\u3002\u51af\u7433\u6025\u5f97\u8df3\u811a\uff0c\u4e00\u628a\u626d\u7740\u59d0 \u59d0\uff0c\u53eb\u9053\uff1a\u201c\u597d\u3002\u4f60\u4eec\u628a\u4ed6\u653e\u8d70\uff0c\u4f60\u4eec\u5c31\u5f97\u66ff\u6211\u628a\u4ed6\u627e\u56de\u6765\u3002\u201d \u3000\u3000\u51af\u745b\u719f\u77e5\u59b9\u59b9\u7684\u813e\u6c14\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5728\u59b9\u59b9\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u600e\u7684\u5982\u6b64\u8ba4\u771f\u3002\u54c8\uff0c \u662f\u4e0d\u662f\u66ff\u963f\u6885\u770b\u4e2d\u4e86\u8fd9\u4e2a\u6bd2\u624b\u75af\u4e10\uff1f\u201d\u51af\u7433\u674f\u773c\u7741\u5706\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4ed6\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u4f60\u4eec\u8bf4 \u4ed6\u662f\u6bd2\u624b\u75af\u4e10\uff0c\u6211\u5374\u8981\u8bf4\u4ed6\u662f\u4e2a\u81f3\u60c5\u81f3\u6027\u7684\u5c11\u5e74\u3002\u4f60\u8ba8\u538c\u4ed6\uff0c\u6211\u504f\u504f\u6b22\u559c\u4ed6\u3002\u201d\u51af\u745b\u5657\u55e4\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u8c01\u8ba8\u538c\u4ed6\u4e86\uff1f\u4f60\u66ff\u6211\u64ae\u5408\u7ecf\u5929\u7684\u59fb\u7f18\uff0c\u6211\u4e5f\u66ff\u4f60\u627e\u56de\u4e00\u4e2a\u5973\u5a7f\u4fbf\u662f\u3002\u201d \u3000\u3000\u53ea\u89c1\u5c71\u62d7\u5904\u53c8\u8f6c\u51fa\u4e00\u4eba\uff0c\u5374\u662f\u5510\u8001\u592a\u5a46\uff0c\u5979\u4e00\u89c1\u5ca9\u77f3\u4e0a\u6709\u91d1\u4e16\u9057\u7684\u62d0\u5370\u4fbf\u5927\u58f0\u53eb\u4e86\u8d77 \u6765\uff0c\u51af\u7433\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u77a7\uff0c\u53c8\u662f\u4e00\u4e2a\u8bf4\u91d1\u4e16\u9057\u597d\u7684\u4eba\u6765\u4e86\u3002\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u5e78\u4e8f\u8fd9\u4e2a\u5510\u8001 \u592a\u5a46\u6ca1\u6709\u5973\u513f\u3002\u201d \u3000\u3000\u5510\u8d5b\u82b1\u542c\u8bf4\u91d1\u4e16\u9057\u5df2\u8d70\uff0c\u5374\u89c1\u4e86\u9f99\u7075\u77eb\uff0c\u6b63\u662f\u4e00\u559c\u4e00\u6101\uff0c\u62d6\u7740\u9f99\u7075\u77eb\u8bf4\u9053\uff1a\u201c\u513f\u5475\uff0c\u6599 \u4e0d\u5230\u8fd8\u80fd\u89c1\u4f60\uff0c\u5a18\u5c31\u662f\u73b0\u5728\u4fbf\u6b7b\uff0c\u4e5f\u7791\u76ee\u4e86\uff0c\u7075\u77eb\uff0c\u4f9d\u6211\u8bf4\uff0c\u4f60\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\u4e86\uff0c\u597d\u597d\u7ed9\u6211\u8ba8 \u4e00\u95e8\u5ab3\u5987\u6b63\u7ecf\u3002\u5f85\u6211\u6b7b\u540e\uff0c\u4f60\u518d\u53bb\u4e89\u738b\u593a\u9738\u5427\uff0c\u514d\u5f97\u6211\u5728\u751f\u4e4b\u65e5\uff0c\u603b\u4e3a\u4f60\u62c5\u5fc3\u3002\u201d\u5510\u8d5b\u82b1\u5e74 \u9752\u5b88\u5be1\uff0c\u5c06\u9f99\u7075\u77eb\u629a\u517b\u6210\u4eba\uff0c\u7aef\u7684\u662f\u89c6\u540c\u5df1\u51fa\uff0c\u9f99\u7075\u77eb\u800c\u4eca\u5df2\u662f\u4e09\u5341\u591a\u5c81\u7684\u4eba\uff0c\u5979\u8fd8\u662f\u5c06\u4ed6 \u5f53\u4f5c\u5b69\u5b50\u770b\u5f85\u3002\u9f99\u7075\u77eb\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u53ea\u76fc\u80fd\u8ddf\u968f\u5510\u5927\u4fa0\u7b49\u8bf8\u5148\u8f88\u4e4b\u540e\uff0c \u884c\u4fa0\u4ed7\u4e49\uff0c\u518d\u4e5f\u522b\u63d0\u4ec0\u4e48\u4e89\u738b\u593a\u9738\u5566\u3002\u5a18\uff0c\u4f60\u8001\u5f53\u76ca\u58ee\uff0c\u5c3d\u8bf4\u90a3\u4e9b\u4e27\u6c14\u7684\u8bdd\u505a\u4ec0\u4e48\uff1f\u201d\u5510\u8d5b \u82b1\u9053\uff1a\u201c\u8981\u4e0d\u662f\u91d1\u4e16\u9057\uff0c\u6211\u53ea\u6015\u65e9\u5df2\u6b7b\u5566\u3002\u4f60\u53ef\u5f97\u66ff\u6211\u627e\u4ed6\u3002\u6653\u6f9c\uff0c\u73b0\u5728\u53ea\u6709\u4f60\u662f\u4ed6\u7684\u6551 \u661f\uff0c\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u8bf7\u4f60\u4eec\u592b\u5987\u4e5f\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u4f60\u4ece\u4e0b\u9762\u4e0a\u6765\uff0c\u53ef\u77e5\u9053\u7ecf\u5929\u7684\u6d88\u606f\u4e48\uff1f\u201d\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u4e5f\u8981 \u4e0a\u6765\u7684\uff0c\u6211\u8001\u5a46\u5b50\u5fc3\u6025\u5148\u8d70\uff0c\u6240\u4ee5\u6ca1\u6709\u548c\u4ed6\u4eec\u4e00\u9053\u3002\u201d\u5510\u6653\u6f9c\u8be7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5c3c\u6cca\u5c14\u7684\u5927\u519b \u9000\u8d70\u4e86\u5417\uff1f\u201d\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u4e5f\u4e0d\u8fdc\u4e86\u3002\u201d\u9f99\u7075\u77eb\u4e0e\u5510\u6653\u6f9c\u592b\u5987\u5f97\u77e5\u4e2d\u56fd\u519b\u961f\u5df2\u5230\uff0c\u8fd9\u624d\u653e\u4e0b \u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\u3002 \u3000\u3000\u5f53\u4e0b\u5546\u8bae\uff0c\u5206\u5934\u53bb\u627e\u91d1\u4e16\u9057\u3002\u5510\u6653\u6f9c\u3001\u51af\u745b\u3001\u51af\u7433\u5404\u8d70\u4e00\u8def\uff0c\u9f99\u7075\u77eb\u4e0e\u5510\u8001\u592a\u5a46\u540c\u4e00 \u8def\uff0c\u867d\u7136\u5206\u6210\u56db\u8def\uff0c\u4f46\u4e00\u60f3\u559c\u739b\u62c9\u96c5\u5c71\u5343\u5cf0\u4e07\u58d1\uff0c\u7ef5\u5ef6\u6570\u5343\u91cc\uff0c\u5bfb\u89c5\u4e00\u4e2a\u4eba\u7b49\u5982\u6d77\u5e95\u635e\u9488\uff0c \u771f\u662f\u6e3a\u832b\u5f97\u5f88\uff0c\u90a3\u53ea\u6709\u542c\u5929\u7531\u547d\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u5728\u65b9\u4eca\u660e\u5bb6\u4e2d\u7565\u4e8b\u6b47\u606f\uff0c\u5e76\u51c6\u5907\u767b\u5c71\u7684\u5e72\u7cae\u3002\u51af\u745b\u548c\u5510\u6653\u6f9c\u5c06\u51af\u7433\u62c9\u8fc7\u4e00\u8fb9\uff0c\u67e5\u95ee \u5979\u6bcd\u5973\u7ed3\u8bc6\u91d1\u4e16\u9057\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u51af\u7433\u5c06\u7ed3\u8bc6\u91d1\u4e16\u9057\u7684\u7ecf\u8fc7\uff0c\u4e00\u4e00\u8bf4\u4e0e\u59d0\u59d0\u77e5\u9053\u3002\u51af\u745b\u542c\u5230\u5979\u5728\u5ce8\u5d4b\u5c71\u620f\u5f04\u91d1\u4e16\u9057\u7684\u60c5 \u5f62\uff0c\u4e5f\u4e0d\u7981\u7b11\u4e86\u8d77\u6765\uff0c\u542c\u5230\u91d1\u4e16\u9057\u7684\u51c4\u51c9\u8eab\u4e16\uff0c\u53c8\u4e0d\u7981\u6f78\u7136\u6cea\u4e0b\uff0c\u6005\u7136\u53f9\u9053\uff1a\u201c\u539f\u6765\u4ed6\u7684\u72c2 \u50b2\u602a\u50fb\uff0c\u5927\u6709\u6765\u7531\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u59d0\u59b9\u4e00\u89c1\u9762\uff0c\u603b\u662f\u8bdd\u8bf4\u4e0d\u5b8c\uff0c\u54b1\u4eec\u8be5\u767b\u5c71\u5566\u3002\u201d\u51af\u7433\u5ffd\u7136\u60f3\u8d77\u4e00 \u4e8b\uff0c\u53d6\u51fa\u6bd2\u9f99\u5c0a\u8005\u90a3\u672c\u65e5\u8bb0\uff0c\u4ea4\u7ed9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u672c\u4e1c\u897f\u4ea4\u7ed9\u4f60\u4fdd\u7ba1\uff0c\u8fd9\u662f\u6bd2\u9f99\u5c0a\u8005\u5728\u86c7\u5c9b \u51e0\u5341\u5e74\u6240\u5199\u4e0b\u7684\u3002\u4f46\u613f\u4f60\u80fd\u4eb2\u624b\u4ea4\u4e0e\u91d1\u4e16\u9057\u3002\u201d\u91d1\u4e16\u9057\u4e0e\u5510\u7ecf\u5929\u4e0d\u548c\uff0c\u51af\u7433\u7ea6\u7565\u77e5\u9053\u4e00\u4e9b\uff0c \u6545\u6b64\u5c06\u8fd9\u672c\u65e5\u8bb0\u4ea4\u4e0e\u5510\u6653\u6f9c\uff0c\u5e0c\u671b\u4e3a\u4ed6\u4eec\u7684\u548c\u89e3\u52a0\u591a\u4e00\u91cd\u52a9\u529b\u3002\u5510\u6653\u6f9c\u65e0\u6687\u7ec6\u95ee\uff0c\u66f4\u65e0\u6687\u7ffb \u770b\uff0c\u53ea\u9053\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u6b66\u529f\u79d8\u7c4d\uff0c\u4fbf\u73cd\u91cd\u7684\u6536\u85cf\u4e86\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u80fd\u6551\u6d3b\u91d1\u4e16\u9057\uff0c\u90a3\u56fa\u7136\u662f \u6700\u597d\u4e0d\u8fc7\u3002\u4e07\u4e00\u91d1\u4e16\u9057\u4e0d\u5e78\u800c\u6b7b\uff0c\u6211\u4e5f\u5fc5\u5b9a\u8981\u66ff\u6bd2\u9f99\u5c0a\u8005\u5bfb\u89c5\u4f20\u4eba\uff0c\u514d\u5f97\u4ed6\u8fd9\u4e00\u6d3e\u65f7\u4e16\u6b66\u529f \u6210\u4e3a\u7edd\u54cd\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u907f\u5f00\u4e86\u5510\u6653\u6f9c\u592b\u5987\u4e4b\u540e\uff0c\u72ec\u81ea\u767b\u5c71\uff0c\u6b64\u65f6\u4ed6\u6700\u540e\u6c42\u751f\u7684\u4e00\u70b9\u673a\u4f1a\u4ea6\u5df2\u6d88\u706d\uff0c\u81ea\u4efd \u5fc5\u6b7b\uff0c\u5fc3\u4e2d\u6240\u60f3\u7684\uff0c\u53ea\u662f\u80fd\u591f\u5728\u6b7b\u524d\u767b\u4e0a\u73e0\u7a46\u6717\u739b\u5cf0\u3002\u7b2c\u4e00\u7b2c\u4e8c\u4e24\u65e5\u8fd8\u6ca1\u89c9\u5f97\u4ec0\u4e48\uff0c\u5230\u4e86\u7b2c \u4e09\u65e5\uff0c\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u4f46\u89c9\u547c\u5438\u6e10\u6e10\u56f0\u96be\u3002\u91d1\u4e16\u9057\u6ca1\u6709\u73b0\u4ee3\u4eba\u7684\u5e38\u8bc6\uff0c\u5f53\u7136\u4e0d\u77e5\u9053\u8fd9\u662f\u56e0\u4e3a\u9ad8\u5c71 \u7f3a\u6c27\u7684\u539f\u6545\u3002\u8981\u77e5\u672c\u4e16\u7eaa\u521d\uff0c\u6b27\u6d32\u7684\u722c\u5c71\u5bb6\u8fd8\u8ba4\u4e3a\u516b\u5343\u7c73\u662f\u767b\u5c71\u7684\u201c\u6781\u9650\u201d\uff0c\u559c\u9a6c\u62c9\u96c5\u5c71\u9ad8 \u8fbe\u516b\u516b\u516b\u4e8c\u7c73\uff0c\u4ea6\u662f\u5730\u7403\u7684\u6700\u9ad8\u70b9\uff0c\u91d1\u4e16\u9057\u8fd9\u65f6\u6500\u767b\u7684\u9ad8\u5ea6\uff0c\u5df2\u662f\u63a5\u8fd1\u4e03\u5343\u7c73\u4e86\uff0c\u9ad8\u5c71\u7f3a\u6c27 \u7684\u7ed3\u679c\uff0c\u5f53\u7136\u5728\u751f\u7406\u4e0a\u5f15\u8d77\u53cd\u5e94\uff0c\u91d1\u4e16\u9057\u4e0d\u660e\u5176\u7406\uff0c\u53ea\u9053\u662f\u81ea\u5df1\u7684\u201c\u8d70\u706b\u4eba\u9b54\u201d\u63d0\u524d\u53d1\u4f5c\uff0c \u5fc3\u4e2d\u7126\u6025\uff0c\u53ea\u597d\u62fc\u547d\u52a0\u5feb\u811a\u6b65\uff0c\u9f13\u52c7\u524d\u884c\u3002 \u3000\u3000\u53ef\u662f\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u90a3\u5c31\u8d8a\u53d1\u96be\u8d70\uff0c\u4efb\u662f\u91d1\u4e16\u903c\u5982\u4f55\u4f7f\u5c3d\u6c14\u529b\uff0c\u901f\u5ea6\u5df2\u662f\u5927\u4e0d\u5982\u524d\u3002\u8fd8\u6709\u4e00 \u6837\u56f0\u96be\u7684\u662f\uff0c\u9ad8\u5c71\u4e0a\u7684\u5bd2\u98ce\uff0c\u8d8a\u81f3\u9ad8\u5904\uff0c\u98ce\u529b\u8d8a\u5927\uff0c\u5f80\u5f80\u9aa4\u7136\u4e00\u9635\u72c2\u98ce\uff0c\u5c06\u4eba\u522e\u5f97\u540e\u9000\u6570\u5341 \u6b65\uff0c\u5f85\u5f97\u98ce\u6b62\u4e4b\u540e\uff0c\u53c8\u8981\u8017\u6389\u8bb8\u591a\u6c14\u529b\uff0c\u65b9\u80fd\u722c\u81f3\u539f\u5904\u3002\u91d1\u4e16\u9057\u9065\u671b\u9ad8\u8038\u5165\u4e91\u7684\u73e0\u7a46\u6717\u739b \u5cf0\uff0c\u73e0\u7a46\u6717\u739b\u5cf0\u5c31\u50cf\u4e00\u4e2a\u7855\u5927\u65e0\u670b\u7684\u5b9d\u77f3\uff0c\u5728\u84dd\u5929\u767d\u4e91\u4e4b\u4e2d\u6676\u83b9\u8000\u76ee\uff0c\u662f\u90a3\u6837\u7684\u8bf1\u4eba\uff0c\u5374\u53c8 \u662f\u90a3\u6837\u7684\u53ef\u671b\u800c\u4e0d\u53ef\u5373\uff01\u91d1\u4e16\u9057\u6253\u904d\u5929\u4e0b\u82f1\u96c4\uff0c\u6b64\u65f6\u9065\u671b\u73e0\u5cf0\uff0c\u4e5f\u4e0d\u7981\u611f\u5230\u6709\u4e9b\u6c14\u9981\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u9f13\u52c7\u524d\u884c\u3002 \u3000\u3000\u5947\u666f\u9aa4\u7136\u5728\u773c\u524d\u51fa\u73b0\uff0c\u4f46\u89c1\u51b0\u5ddd\u4ea4\u9519\uff0c\u904d\u5e03\u5728\u96ea\u767d\u7684\u5c71\u5761\u4e0a\uff0c\u851a\u84dd\u5f97\u50cf\u7fe1\u7fe0\u4e00\u822c\uff0c\u65e0\u6570 \u51b0\u5ddd\u6c47\u5230\u4e00\u5904\uff0c\u7a81\u7136\u597d\u4f3c\u5e73\u5730\u4e0a\u6d8c\u8d77\u8bb8\u591a\u5b9d\u5854\uff0c\u90a3\u662f\u50cf\u851a\u84dd\u8272\u6c34\u6676\u7684\u201c\u51b0\u5854\u7fa4\u201d\uff01\u201c\u6210\u7fa4\u7ed3 \u961f\u201d\u7684\u8fde\u6210\u4e00\u5927\u7247\uff0c\u5728\u9633\u5149\u4e4b\u4e0b\u95ea\u7740\u5bd2\u5149\uff01\u91d1\u4e16\u9057\u4e00\u58f0\u6b22\u547c\uff0c\u4ef0\u5929\u957f\u5578\uff0c\u53eb\u9053\uff1a\u201c\u7eb5\u7b97\u4e0d\u80fd \u6500\u4e0a\u73e0\u5cf0\uff0c\u5f97\u89c1\u6b64\u4eba\u95f4\u4ed9\u5883\uff0c\u6b7b\u4ea6\u7791\u76ee\u4e86\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u4f7f\u52b2\u7684\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5411\u7740\u201c\u51b0\u5854\u7fa4\u201d\u5954\u53bb\uff0c\u811a\u6b65\u4e00\u62ac\uff0c\u8e0f\u788e\u51b0\u5757\uff0c\u5ffd\u7136\u89e6\u7740\u4e00 \u6837\u4e1c\u897f\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e00\u4e2a\u5916\u56fd\u4eba\u7684\u5c38\u4f53\uff0c\u5728\u79ef\u96ea\u91cc\u4e0d\u77e5\u57cb\u4e86\u591a\u5c11\u5e74\uff0c\u5c38\u4f53\u65c1\u8fb9\u6709\u8bb8 \u591a\u767b\u5c71\u7684\u7528\u5177\uff0c\u7ef3\u7d22\u8863\u88f3\u90fd\u5df2\u98ce\u5316\u8150\u70c2\u4e86\uff0c\u89e6\u624b\u5373\u6210\u788e\u7c89\uff0c\u9762\u76ee\u4ecd\u662f\u6829\u6829\u5982\u751f\u3002\u8d70\u4e0d\u591a\u8fdc\uff0c \u53c8\u53d1\u73b0\u4e00\u4e2a\u5c38\u4f53\uff0c\u91d1\u4e16\u9057\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5343\u767e\u5e74\u6765\uff0c\u4e0d\u77e5\u591a\u5c11\u4eba\u56e0\u4e3a\u6500\u767b\u8fd9\u5929\u4e0b\u7b2c\u4e00\u9ad8\u5cf0\u800c\u57cb \u5c38\u96ea\u5730\uff0c\u4e09\u4e24\u65e5\u540e\uff0c\u5927\u7ea6\u6211\u4e5f\u8981\u6b65\u4ed6\u4eec\u7684\u540e\u5c18\uff0c\u4e0e\u4ed6\u4eec\u4f5c\u4f34\u4e86\uff01\u201d \u3000\u3000\u201c\u51b0\u5854\u7fa4\u201d\u770b\u6765\u4e0d\u8fdc\uff0c\u8d70\u4e86\u5927\u534a\u5929\u4ecd\u672a\u8d70\u5230\uff0c\u91d1\u4e16\u9057\u5e26\u6765\u7684\u4e8e\u7cae\u4e5f\u5df2\u5403\u5b8c\u4e86\uff0c\u5e78\u559c\u9ad8\u5c71 \u4e0a\u4e5f\u6709\u4e9b\u52a8\u7269\uff0c\u800c\u4e14\u90fd\u662f\u522b\u5904\u89c1\u4e0d\u5230\u7684\u73cd\u79bd\u5f02\u517d\uff0c\u5c0f\u718a\u732b\u5728\u96ea\u5730\u4e0a\u8df3\u8dc3\uff0c\u89c1\u4e86\u4eba\u4e5f\u4e0d\u77e5\u9053\u8eb2 \u907f\uff0c\u53ef\u7231\u6781\u4e86\uff0c\u6d3b\u50cf\u4e00\u4e2a\u6dd8\u6c14\u7684\u5a03\u5a03\uff0c\u91d1\u4e16\u9057\u820d\u4e0d\u5f97\u6253\u5b83\uff0c\u7528\u77f3\u5b50\u6253\u4e0b\u4e86\u51e0\u5934\u9ec4\u5634\u5c71\u9e26\uff0c\u53c8 \u730e\u4e86\u4e00\u53ea\u96ea\u9e21\u3002\u4ed6\u968f\u8eab\u5e26\u6709\u706b\u77f3\uff0c\u64e6\u4e86\u8bb8\u4e45\uff0c\u624d\u64e6\u51fa\u706b\u661f\uff0c\u9ad8\u5c71\u4e0a\u6709\u7684\u662f\u67af\u679d\u8d25\u53f6\uff0c\u53ef\u4f5c\u71c3 \u6599\uff0c\u4f46\u716e\u4e1c\u897f\u5374\u6bd4\u5e73\u5730\u82b1\u591a\u4e86\u4e0d\u6b62\u4e09\u500d\u7684\u65f6\u95f4\uff0c\u91d1\u4e16\u9057\u5728\u90a3\u4e24\u4e2a\u6b7b\u4e86\u7684\u201c\u722c\u5c71\u5bb6\u201d\u7684\u9057\u7269 \u4e2d\uff0c\u6361\u51fa\u4e86\u4e2a\u76db\u6c34\u7684\u9521\u5668\uff0c\u628a\u51b0\u5757\u653e\u5728\u91cc\u9762\uff0c\u70e7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6c34\u8fd8\u672a\u6eda\u3002\u91d1\u4e16\u9057\u5403\u4e86\u4e24\u5934\u5c71 \u9e26\uff0c\u534a\u8fb9\u96ea\u9e21\uff0c\u559d\u9971\u4e86\u534a\u5f00\u7684\u6e29\u6c34\uff0c\u6c14\u529b\u7a0d\u7a0d\u6062\u590d\uff0c\u53c8\u5411\u524d\u884c\u3002 \u3000\u3000\u8fce\u9762\u662f\u4e00\u6761\u5927\u51b0\u5ddd\uff0c\u51b0\u5ddd\u4e0a\u6709\u4e00\u5757\u5de8\u5927\u7684\u82b1\u5c97\u77f3\uff0c\u88ab\u4e00\u5ea7\u5c0f\u5c71\u822c\u7684\u5927\u51b0\u5757\u652f\u6491\u7740\uff0c\u5f62\u72b6 \u9177\u8096\u4e00\u4e2a\u5de8\u578b\u7684\u201c\u8611\u82d1\u201d\u3002\u91d1\u4e16\u9057\u6b63\u60f3\u6539\u9053\u7ed5\u8fc7\uff0c\u5ffd\u542c\u5f97\u201c\u51b0\u8611\u82d1\u201d\u540e\u9762\u9690\u7ea6\u6709\u547b\u541f\u4e4b\u58f0\u3002 \u91d1\u4e16\u9057\u5413\u4e86\u4e00\u8df3\uff0c\u6500\u4e0a\u201c\u51b0\u8611\u82d1\u201d\uff0c\u5411\u4e0b\u4e00\u770b\uff0c\u53ea\u89c1\u4e24\u4e2a\u50f5\u5c38\u822c\u7684\u602a\u4eba\uff0c\u8eba\u5728\u51b0\u5757\u4e0a\uff0c\u9762\u4e0a \u4e00\u6761\u6761\u7684\u8840\u75d5\uff0c\u8d8a\u53d1\u663e\u5f97\u72f0\u72de\u53ef\u6015\u3002\u8fd9\u4e24\u4e2a\u4eba\u4e43\u662f\u8d64\u795e\u5b50\u4e0e\u8463\u592a\u6e05\uff0c\u4ed6\u4eec\u60f3\u4e0a\u5c71\u6765\u5bfb\u7ee6\u73e0\u4ed9 \u8349\uff0c\u54ea\u77e5\u521a\u671b\u89c1\u201c\u51b0\u5854\u7fa4\u201d\u5c31\u51bb\u50f5\u4e86\u3002 \u3000\u3000\u82e5\u7136\u662f\u5728\u5e73\u5730\uff0c\u91d1\u4e16\u9057\u5bf9\u8fd9\u4e24\u4e2a\u4eba\u51b3\u4e0d\u4f1a\u8d77\u534a\u70b9\u540c\u60c5\u4e4b\u5fc3\uff0c\u6b64\u9645\u5728\u9ad8\u5c71\u4e4b\u4e0a\uff0c\u5f97\u89c1\u4eba \u7c7b\uff0c\u90a3\u6015\u4ed6\u662f\u654c\u4eba\uff0c\u4e5f\u6709\u4e00\u79cd\u4eb2\u70ed\u4e4b\u611f\u3002\u91d1\u4e16\u9057\u63d0\u4e00\u53e3\u6c14\uff0c\u8dc3\u4e0b\u51b0\u5ddd\uff0c\u811a\u5e95\u4e0b\u9690\u9690\u53ef\u89c9\u51b0\u5757 \u6d6e\u52a8\uff0c\u91d1\u4e16\u9057\u5148\u6478\u4e00\u6478\u8d64\u795e\u5b50\u7684\u9f3b\u89c2\uff0c\u89e6\u624b\u51b0\u51b7\uff0c\u6c14\u606f\u5df2\u7edd\u3002\u8463\u592a\u6e05\u5374\u5c1a\u6709\u4e00\u4e1d\u6c14\u606f\u3002\u539f\u6765 \u8d64\u795e\u5b50\u662f\u88ab\u51b0\u5ddd\u5929\u5973\u6253\u4e86\u4e03\u679a\u51b0\u9b44\u795e\u5f39\u4e4b\u540e\uff0c\u5143\u6c14\u5927\u4f24\uff0c\u52a0\u4ee5\u4ed6\u6240\u7ec3\u7684\u5185\u529f\u66f4\u662f\u90aa\u95e8\uff0c\u53cd\u800c \u6bd4\u4e0d\u4e0a\u8463\u592a\u6e05\u80fd\u591f\u6301\u4e45\u3002 \u3000\u3000\u91d1\u4e16\u9057\u66ff\u8463\u592a\u6e05\u63c9\u6413\u624b\u8db3\uff0c\u53c8\u5582\u4ed6\u559d\u4e86\u534a\u53e3\u6c34\uff0c\u8463\u592a\u6e05\u5fae\u5fae\u5f20\u5f00\u773c\u775b\uff0c\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u662f \u4f60\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u522b\u52a8\uff0c\u6211\u52a9\u4f60\u8fd0\u529f\u3002\u201d\u8463\u592a\u6e05\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u6210\u5566\uff0c\u4f60\u5feb\u79bb \u6b64\u9669\u5730\uff01\u201d\u91d1\u4e16\u9057\u542c\u4ed6\u8109\u606f\u6563\u4e71\uff0c\u4f53\u786c\u5982\u51b0\uff0c\u4ea6\u5df2\u77e5\u9053\u96be\u4ee5\u6551\u6cbb\uff0c\u4f46\u4ecd\u72b9\u7591\u4e0d\u51b3\uff0c\u672a\u5fcd\u79bb \u5f00\u3002\u8463\u592a\u6e05\u6323\u624e\u4e86\u4e00\u4e0b\uff0c\u5ffd\u9053\uff1a\u201c\u4e16\u9057\u5144\uff0c\u662f\u6211\u54c4\u9a97\u4e86\u4f60\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6069\u6069\u6028\u6028\uff0c\u662f\u662f\u975e\u975e\uff0c\u5230\u4e86\u6b64\u65f6\uff0c\u8fd8\u7528\u5f97\u7740\u8ba1\u8f83\u4e48\uff1f\u6211\u54ea\u6709\u5fc3\u601d\u7406\u4f1a\u4f60\u8bf4 \u7684\u4ec0\u4e48\u662f\u8c0e\u8a00\uff0c\u4ec0\u4e48\u662f\u771f\u8bdd\uff1f\u201d\u8463\u592a\u6e05\u53c8\u6323\u624e\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u518d\u4e0d\u8bf4\u4ee5\u540e\u5c31\u4e0d\u80fd \u8bf4\u4e86\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u8981\u8bf4\u51fa\u624d\u80fd\u5fc3\u5b89\uff0c\u90a3\u4f60\u5c31\u8bf4\u3002\u201d \u3000\u3000\u8463\u592a\u6e05\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u7684\u4e66\uff0c\u5728\u51af\u7433\u624b\u4e2d\u3002\u6211\u4ee5\u524d\u6240\u8bf4\u88ab\u5510\u6653\u6f9c\u62a2\u53bb\u4e43\u662f\u54c4\u9a97\u4f60 \u7684\u3002\u201d\u91d1\u4e16\u9057\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7ba1\u5b83\u5728\u8c01\u624b\u91cc\uff0c\u5582\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u8463\u592a\u6e05\u5ffd\u5730\u628a\u811a\u4e0a\u8e6c\uff0c\u4f7f\u5c3d\u6700\u540e\u7684\u6c14\u529b\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u91d1\u4e16\u9057\u53ea\u89c9\u811a\u4e0b\u6d41\u51b0\u6d6e\u52a8\uff0c\u773c \u89c1\u4e00\u80a1\u72c2\u98ce\u522e\u6765\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u6025\u5fd9\u8dc3\u4e0a\u201c\u51b0\u8611\u82d1\u201d\uff0c\u518d\u8df3\u56de\u5730\u4e0a\u3002\u53ea\u542c\u5f97\u5728\u547c\u547c\u7684\u72c2\u98ce\u58f0 \u4e2d\uff0c\u90a3\u5757\u201c\u51b0\u8611\u82d1\u201d\u6643\u4e86\u51e0\u6643\uff0c\u201c\u8611\u82d1\u201d\u4e0b\u9762\u7684\u6d6e\u51b0\u54d7\u5566\u5566\u7684\u54cd\uff0c\u9aa4\u7136\u88c2\u5f00\u4e86\u4e00\u6761\u5927\u7f1d\uff0c\u8463 \u592a\u6e05\u548c\u8d64\u795e\u5b50\u7684\u5c38\u4f53\u88ab\u6d6e\u51b0\u4e00\u6324\uff0c\u6c89\u6ca1\u5165\u88c2\u7f1d\u4e4b\u4e2d\uff0c\u57cb\u5929\u51b0\u5ddd\u5e95\u4e0b\uff01 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u5e95\u4e00\u9635\u60b2\u51c9\uff0c\u4e0d\u81ea\u7981\u7684\u6d12\u4e0b\u51e0\u70b9\u82f1\u96c4\u773c\u6cea\uff0c\u4e5f\u4e0d\u77e5\u662f\u4e3a\u4e86\u8463\u592a\u6e05\u4f24\u611f\uff0c\u8fd8\u662f\u4e3a \u81ea\u5df1\u7684\u547d\u8fd0\u8f9b\u9178\uff1f\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u9644\u8fd1\u7684\u4e00\u5757\u51b0\u5ca9\u4e0a\u523b\u6709\u4e00\u6735\u6885\u82b1\uff0c\u91d1\u4e16\u9057\u5403\u4e86\u4e00\u60ca\uff0c\u987f\u65f6\u95f4 \u53ea\u89c9\u70ed\u8840\u4e0a\u6d8c\uff0c\u795e\u601d\u6005\u60d8\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u5f53\u771f\u662f\u597d\uff0c\u5979\u4e5f\u6765\u4e86\uff1f\u201d\u72c2\u98ce\u5df2\u6b62\uff0c\u9633\u5149\u88ab\u51b0\u5ddd \u53cd\u5c04\uff0c\u6cdb\u51fa\u5343\u767e\u9053\u971e\u8f89\u4e3d\u5f69\uff0c\u91d1\u4e16\u9057\u4e00\u7247\u832b\u7136\uff0c\u6cbf\u7740\u51b0\u5ca9\u8d70\u53bb\uff0c\u8d70\u4e0d\u591a\u4e45\u3001\u53c8\u89c1\u4e00\u6735\u6885\u82b1\u6807 \u5fd7\uff0c\u6562\u60c5\u90a3\u662f\u7528\u5229\u5251\u5728\u51b0\u58c1\u4e0a\u523b\u5212\u51fa\u6765\u7684\uff0c\u51b0\u5c42\u900f\u660e\uff0c\u82b1\u74e3\u5728\u51b0\u5c42\u4e2d\u6620\u5f97\u73b2\u73ab\u6d6e\u51f8\uff0c\u771f\u6bd4\u5f00 \u5728\u679d\u5934\u7684\u6885\u82b1\u66f4\u8981\u5996\u8273\u3002\u91d1\u4e16\u9057\u8eab\u8eaf\u98a4\u6296\uff0c\u501a\u7740\u51b0\u58c1\uff0c\u51e0\u4e4e\u8fc8\u4e0d\u52a8\u811a\u6b65\u3002 \u3000\u3000\u8fd9\u6885\u82b1\u6b63\u662f\u674e\u6c81\u6885\u7684\u6807\u5fd7\uff0c\u56e0\u5979\u7684\u540d\u5b57\u4e2d\u6709\u4e00\u4e2a\u201c\u6885\u201d\u5b57\u3002\u91d1\u4e16\u9057\u4ee5\u524d\u548c\u5979\u540c\u8def\uff0c\u4ece\u56db \u5ddd\u5ce8\u5d4b\u5c71\u8d70\u4e0b\uff0c\u4e00\u8def\u76f4\u5230\u85cf\u8fb9\uff0c\u6cbf\u9014\u5c31\u66fe\u89c1\u5979\u7559\u4e0b\u4e0d\u5c11\u6885\u82b1\u8bb0\u53f7\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u91d1\u4e16\u9057\u4f46\u89c9\u88ab\u51bb\u5f97\u9ebb\u6728\u4e86\u7684\u8eab\u4f53\u5ffd\u7136\u5982\u6709\u6696\u6d41\u901a\u8fc7\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e16\u754c\u4e0a\u8fd8\u6709\u4e00 \u4e2a\u5982\u6b64\u6302\u5ff5\u4ed6\u7684\u4eba\uff0c\u4e0d\u8f9e\u5192\u96ea\u51b2\u5bd2\uff0c\u5230\u6b64\u4e98\u53e4\u65e0\u4eba\u7684\u51b0\u5cf0\uff0c\u8ffd\u8e2a\u89c5\u8ff9\uff01\u4f46\u60f3\u5230\u81ea\u5df1\u6b7b\u671f\u5c06 \u81f3\uff0c\u53c8\u600e\u5fcd\u548c\u5979\u518d\u89c1\u6700\u540e\u4e00\u9762\uff0c\u4ee4\u5979\u4f24\u5fc3\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6b63\u81ea\u8e0c\u8e87\u96be\u51b3\uff0c\u5ffd\u542c\u5f97\u51b0\u5854\u7fa4\u4e2d\u9690\u9690\u6709\u53ae\u6740\u4e4b\u58f0\uff0c\u91d1\u4e16\u9057\u7a81\u7136\u8840\u8109\u6124\u5f20\uff0c\u63d0\u4e86\u53e3 \u6c14\uff0c\u98de\u5954\u8fc7\u53bb\uff0c\u7a7f\u5165\u201c\u5854\u201d\u7fa4\uff0c\u8fdc\u8fdc\u5c31\u89c1\u51b0\u58c1\u4e0a\u6620\u51fa\u674e\u6c81\u6885\u7684\u5f71\u5b50\uff0c\u65e0\u6570\u5927\u5927\u4e0a\u5c0f\u7684\u51b0\u5854\uff0c \u5c31\u50cf\u5343\u767e\u9762\u660e\u955c\uff0c\u5c42\u5c42\u53cd\u5c04\uff0c\u8d70\u5230\u5854\u7fa4\u7684\u4e2d\u592e\uff0c\u65e5\u4e4b\u6240\u81f3\uff0c\u6240\u89c1\u7684\u90fd\u662f\u674e\u6c81\u6885\u7684\u5f71\u5b50\u3002\u53e6\u5916 \u8fd8\u6709\u4e24\u4e2a\u602a\u4eba\u7684\u5f71\u5b50\uff0c\u56f4\u7740\u674e\u6c81\u6885\u624b\u821e\u8db3\u8e48\u7684\uff0c\u5728\u5343\u767e\u9762\u51b0\u58c1\u4e0a\u53cd\u5c04\u51fa\u6765\uff0c\u4ee4\u4eba\u773c\u82b1\u7ee9\u4e71\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5b9a\u4e00\u5b9a\u795e\uff0c\u9760\u7740\u8033\u6735\u7684\u611f\u89c9\uff0c\u8fa8\u522b\u58f0\u97f3\u7684\u6765\u8def\uff0c\u5728\u201c\u51b0\u5854\u7fa4\uff1a\u2019\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u773c \u524d\u5ffd\u7136\u5f00\u6717\uff0c\u4f46\u89c1\u5728\u51e0\u5ea7\u51b0\u5854\u56f4\u62f1\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u5c0f\u6e56\uff0c\u5c0f\u6e56\u4e4b\u6ee8\uff0c\u674e\u6c81\u6885\u6b63\u5728\u548c\u90a3\u4e24\u4e2a\u602a\u4eba \u53ae\u6740\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u90fd\u662f\u53cc\u8db3\u5df2\u80a2\uff0c\u4ee5\u624b\u652f\u5730\uff0c\u9891\u9891\u6362\u638c\uff0c\u56f4\u7740\u674e\u6c81\u6885\u9640\u87ba\u822c\u7684\u65cb\u8f6c\uff0c\u4ea4\u66ff\u53d1 \u638c\u3002\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u4f36\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u3002\u4ed6\u4eec\u90a3\u65e5\u4e0e\u51b0\u5ddd\u5929\u5973\u6bd4\u8d5b\u8f7b\u529f\uff0c\u4ece\u51b0\u5cf0\u4e0a\u8dcc\u4e0b\u6765\uff0c\u5e78\u800c \u51b0\u5ddd\u5929\u5973\u76f8\u6551\uff0c\u5f97\u4ee5\u4e0d\u6b7b\u3002\u6240\u53d7\u7684\u8f7b\u4f24\uff0c\u517b\u4e86\u4e00\u4e24\u65e5\u4ea6\u5df2\u65e0\u4e8b\u3002\u4ed6\u4eec\u95fb\u77e5\u5e08\u7236\u63d0\u6469\u8fbe\u591a\u767b \u5c71\uff0c\u4fbf\u8d76\u4e0a\u6765\uff0c\u4e0d\u60f3\u5728\u6b64\u5904\u9047\u89c1\u674e\u6c81\u6885\u3002\u4ed6\u4eec\u4e00\u6765\u7f3a\u4e86\u5e72\u7cae\u3002\u4e8c\u6765\u4ea6\u611f\u6c14\u529b\u67af\u7aed\uff0c\u89c1\u5230\u674e\u6c81 \u6885\uff0c\u5ffd\u5730\u8d77\u4e86\u574f\u5fc3\uff0c\u60f3\u628a\u674e\u6c81\u6885\u52ab\u8d70\uff0c\u4ece\u5357\u9762\u4e0b\u5c71\uff0c\u5077\u56de\u6545\u56fd\u3002\u8bf4\u662f\u52ab\u5230\u4e2d\u56fd\u7684\u7f8e\u4eba\uff0c\u4e5f\u597d \u5728\u6b27\u6d32\u70ab\u8000\u3002\u5728\u5f53\u65f6\u6b27\u6d32\u7684\u98ce\u6c14\uff0c\u201c\u9a91\u58eb\u201d\u8fdc\u5f81\uff0c\u62a2\u52ab\u5973\u4eba\u4f5c\u4e3a\u80dc\u5229\u54c1\uff0c\u90a3\u662f\u53f8\u7a7a\u89c1\u60ef\u4e4b \u4e8b\u3002\u4f55\u51b5\u4ffa\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u6b64\u6b21\u6765\u534e\uff0c\u4e00\u518d\u632b\u8d25\uff0c\u8fde\u53cc\u817f\u90fd\u88ab\u5510\u6653\u6f9c\u6253\u5f97\u51e0\u4e4e\u65ad\u6298\uff0c\u4e00\u8154\u6012 \u6c14\uff0c\u65e0\u5904\u53d1\u6cc4\uff0c\u52ab\u4e00\u4e2a\u4e2d\u56fd\u7f8e\u4eba\u56de\u53bb\uff0c\u6b63\u597d\u6cc4\u6124\u3002 \u3000\u3000\u674e\u6c81\u6885\u6b64\u65f6\u4e5f\u662f\u6c14\u8870\u529b\u7aed\uff0c\u4f46\u5979\u7684\u5251\u6cd5\u662f\u5929\u5c71\u5251\u6cd5\u7684\u53e6\u4e00\u652f\uff0c\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u7684\u5ae1\u4f20\uff0c \u5947\u8be1\u53d8\u5e7b\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u4f36\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u7684\u9634\u9633\u638c\u529b\uff0c\u867d\u7136\u5389\u5bb3\uff0c\u5374\u4e5f\u53ea\u80fd\u5c06\u5979\u56f0\u4f4f\uff0c\u8fd1\u4e0d\u4e86 \u8eab\u3002 \u3000\u3000\u9ad8\u5c71\u7f3a\u6c27\uff0c\u5728\u6b64\u6253\u6597\uff0c\u6bd4\u5728\u5e73\u5730\u4e0a\u5403\u529b\u767e\u500d\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e09\u4e2a\u4eba\u90fd\u662f\u5934\u660f\u76ee\u7729\uff0c\u6c14 \u5c3d\u529b\u7aed\uff0c\u53ea\u662f\u672c\u80fd\u7684\u53d1\u62db\u76f8\u6297\u4e86\u3002\u91d1\u4e16\u9057\u81ea\u662f\u884c\u5bb6\uff0c\u4e00\u89c1\u674e\u6c81\u6885\u7684\u5251\u5c16\u4e1c\u6307\u897f\u5212\uff0c\u6beb\u65e0\u52b2 \u98ce\uff0c\u7acb\u77e5\u4e0d\u5999\uff0c\u63d0\u8d77\u94c1\u62d0\uff0c\u6b63\u5f85\u76f8\u52a9\uff0c\u674e\u6c81\u6885\u4ece\u51b0\u58c1\u7684\u53cd\u6620\u4e2d\uff0c\u5df2\u770b\u89c1\u91d1\u4e16\u9057\u7684\u5f71\u5b50\uff0c\u7aef\u7684 \u4f3c\u5927\u6f20\u4e2d\u7edd\u91cd\u7684\u65c5\u4eba\uff0c\u6479\u7136\u5929\u964d\u7518\u9716\uff0c\u72c2\u559c\u800c\u81f4\u660f\u8ff7\u3002\u53ea\u542c\u5f97\u5979\u5c16\u53eb\u4e00\u58f0\uff0c\u957f\u5251\u4e00\u629b\uff0c\u8e09\u8e09 \u8dc4\u8dc4\u7684\u8fce\u7740\u91d1\u4e16\u9057\u5954\u8dd1\uff0c\u8dd1\u5f97\u5341\u6765\u6b65\uff0c\u4fbf\u6655\u5012\u5730\u4e0a\u3002 \u3000\u3000\u5085\u53e4\u62c9\u4e0e\u963f\u65af\u7f57\u5140\u81ea\u5728\u5730\u4e0a\u6253\u8f6c\uff0c\u4ed6\u4eec\u4ea6\u5df2\u795e\u667a\u660f\u8ff7\uff0c\u91d1\u4e16\u9057\u4e00\u5230\u6e56\u6ee8\uff0c\u4ed6\u4eec\u7adf\u4f3c\u89c6\u800c \u4e0d\u89c1\u3002\u91d1\u4e16\u9057\u54ea\u6709\u5fc3\u601d\u53bb\u7406\u4ed6\u4eec\uff0c\u614c\u5fd9\u62a2\u4e0a\u524d\u53bb\u5c06\u674e\u6c81\u6885\u4e00\u628a\u62b1\u8d77\uff0c\u4f46\u89c9\u5979\u8eab\u5b50\u8f6f\u7ef5\u7ef5\u7684\uff0c \u9999\u5598\u5401\u5401\uff0c\u661f\u7738\u534a\u95ed\uff0c\u91d1\u4e16\u9057\u60c5\u4e0d\u81ea\u7981\u7684\u62e8\u5f00\u5979\u9762\u4e0a\u7684\u4e71\u53d1\uff0c\u8f7b\u8f7b\u7684\u5f39\u4e86\u4e00\u4e0b\u5979\u7684\u7709\u5c16\uff0c\u4f4e \u58f0\u5524\u9053\uff1a\u201c\u6885\u59b9\u59b9\uff0c\u4f60\u7741\u5f00\u773c\u775b\u770b\u770b\u3002\u201d \u3000\u3000\u674e\u6c81\u6885\u5634\u89d2\u6302\u7740\u51c4\u51c9\u7684\u5fae\u7b11\uff0c\u773c\u775b\u6162\u6162\u5f20\u5f00\uff0c\u5598\u6c14\u8bf4\u9053\uff1a\u201c\u4e16\u9057\u54e5\u54e5\uff0c\u6211\u77e5\u9053\u4f60\u4f1a\u6765 \u7684\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u8c03\u5300\u547c\u5438\uff0c\u6211\u52a9\u4f60\u8fd0\u529f\u3002\u201d\u674e\u6c81\u6885\u5728\u4ed6\u6000\u4e2d\u5fae\u5fae\u98a4\u52a8\uff0c\u5ffd\u5730\u638f\u51fa\u4e00\u4e2a \u94f6\u74f6\uff0c\u9053\uff1a\u201c\u4f60\u5feb\u670d\u4e0b\uff01\u201d\u91d1\u4e16\u9057\u6b63\u81ea\u83ab\u660e\u6240\u4ee5\uff0c\u5ffd\u89c1\u674e\u6c81\u6885\u53c8\u6162\u6162\u95ed\u4e86\u773c\u775b\uff0c\u9762\u8272\u975e\u5e38\u5b81 \u9759\uff0c\u5634\u89d2\u7684\u7b11\u5bb9\u6e10\u6e10\u6536\u7f29\u3002\u597d\u50cf\u4e00\u6735\u84d3\u857e\uff0c\u91d1\u4e16\u9057\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u89c9\u5979\u624b\u811a\u6e10\u6e10\u50f5\u786c\u3002 \u3000\u3000\u91d1\u4e16\u9057\u66ff\u5979\u6309\u6469\u4e86\u4e00\u4f1a\uff0c\u6beb\u65e0\u6548\u679c\uff0c\u9664\u4e86\u4e9b\u5fae\u6c14\u606f\u4e4b\u5916\uff0c\u4fbf\u548c\u6b7b\u53bb\u4e00\u822c\u3002\u91d1\u4e16\u9057\u4ed4\u7ec6\u5bdf \u89c6\uff0c\u77e5\u5979\u5e76\u6ca1\u4f24\uff0c\u4f46\u6c14\u529b\u6d88\u8017\u8fc7\u751a\uff0c\u5374\u662f\u96be\u4ee5\u6062\u590d\u3002\u82e5\u5728\u5e73\u5730\uff0c\u559d\u4e24\u7897\u53c2\u6c64\uff0c\u7761\u4e00\u4e2a\u5927\u89c9\uff0c \u81ea\u7136\u65e0\u4e8b\u3002\u4f46\u8fd9\u91cc\u662f\u9ad8\u8038\u5165\u4e91\u7684\u96ea\u5cf0\uff0c\u547c\u5438\u5c1a\u4e14\u56f0\u96be\u3002\u98df\u7269\u4ea6\u6781\u96be\u627e\uff0c\u54ea\u6709\u4ec0\u4e48\u7075\u836f\u53ef\u4ee5\u52a9 \u5979\u6062\u590d\u5143\u795e\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u75db\u5982\u5272\uff0c\u5782\u6cea\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u90fd\u662f\u6211\u7d2f\u4e86\u4f60\u3002\u201d\u8fd9\u662f\u4ed6\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u5927\u52a8\u771f \u60c5\u3002\u53ef\u60dc\u4ed6\u5145\u6ee1\u611f\u60c5\u7684\u8a00\u8bed\uff0c\u674e\u6c81\u6885\u5374\u4e00\u70b9\u4e5f\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5782\u4e0b\u4e86\u5934\uff0c\u832b\u7136\u65e0\u63aa\uff0c\u5ffd\u7136\u773c\u5149\u78b0\u5230\u4e86\u5730\u4e0a\u7684\u94f6\u74f6\uff0c\u91d1\u4e16\u9057\u5fc3\u5934\u4e00\u8df3\uff0c\u5c06\u94f6\u74f6\u6293 \u4e86\u8d77\u6765\uff0c\u53ea\u89c1\u74f6\u4e2d\u6709\u4e09\u7c92\u78a7\u7eff\u8272\u7684\u4e38\u4e39\uff0c\u6b63\u662f\u7528\u5929\u5c71\u96ea\u83b2\u914d\u5236\u7684\u78a7\u7075\u4e39\uff0c\u4ee5\u524d\u5510\u7ecf\u5929\u66fe\u8981\u628a \u8fd9\u4e09\u7c92\u7075\u4e39\u8fde\u540c\u94f6\u74f6\u9001\u7ed9\u91d1\u4e16\u9057\uff0c\u88ab\u91d1\u4e16\u9057\u62d2\u7edd\u4e86\u7684\u3002\u5982\u4eca\u91d1\u4e16\u9057\u53ea\u6709\u4e09\u5927\u7684\u6027\u547d\u4e86\uff0c\u5374\u53c8 \u5728\u674e\u6c81\u6885\u7684\u8eab\u8fb9\u53d1\u73b0\u8fd9\u4e2a\u94f6\u74f6\u3002 \u3000\u3000\u5982\u679c\u91d1\u4e16\u9057\u73b0\u5728\u541e\u4e0b\u8fd9\u4e09\u7c92\u7075\u4e39\uff0c\u4ed6\u7684\u6027\u547d\u6700\u5c11\u53c8\u53ef\u4ee5\u5ef6\u957f\u4e09\u5341\u516d\u5929\uff0c\u4f46\u91d1\u4e16\u9057\u54ea\u4f1a\u5982 \u6b64\u53bb\u60f3\uff0c\u8fd9\u65f6\u4ed6\u6367\u8d77\u94f6\u74f6\uff0c\u5c31\u50cf\u6367\u7740\u4ece\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u5b9d\u8d1d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5929\u5c71\u96ea\u83b2\u53ef\u89e3\u8bf8\u822c \u90aa\u6bd2\uff0c\u800c\u4e14\u80fd\u52a9\u957f\u5143\u6c14\uff0c\u529f\u529b\u6bd4\u8d77\u5343\u5e74\u8001\u53c2\uff0c\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3001\u5440\uff0c\u7075\u836f\u5c31\u5728\u8eab\u8fb9\uff0c\u6211\u521a\u624d \u600e\u4e48\u89c6\u800c\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u6025\u6025\u6253\u5f00\u94f6\u74f6\uff0c\u5c06\u4e09\u7c92\u78a7\u7075\u4e39\u503e\u5012\u624b\u5fc3\uff0c\u64ac\u5f00\u674e\u6c81\u6885\u7684\u7259\u5173\uff0c\u5c06\u4e09\u7c92\u7075\u4e39\u9001\u8fdb\u5979 \u7684\u53e3\u4e2d\uff0c\u5c06\u5979\u7684\u8eab\u5b50\u6447\u4e86\u4e24\u6447\uff0c\u53c8\u7ed9\u5979\u63a8\u8840\u8fc7\u5bab\uff0c\u5fd9\u4e86\u4e00\u9635\uff0c\u4f46\u89c9\u5979\u6c14\u606f\u6e10\u6e10\u8f6c\u7c97\uff0c\u4f46\u4ecd\u672a \u82cf\u9192\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e00\u9635\u72c2\u559c\uff0c\u968f\u5373\u53c8\u662f\u611f\u5230\u4e00\u7247\u60b2\u51c9\uff0c\u81ea\u5df1\u53ea\u6709\u4e0d\u591f\u4e09\u5929\u7684\u6027\u547d\u4e86\uff0c\u96be\u9053\u8fd8\u8981\u7559\u5728 \u5979\u7684\u8eab\u8fb9\uff0c\u8ba9\u5979\u82cf\u9192\u4e4b\u540e\uff0c\u66ff\u81ea\u5df1\u9001\u7ec8\uff1f\u5440\uff0c\u5440\uff0c\u4e16\u754c\u4e0a\u53ea\u6709\u5979\u8fd9\u6837\u5173\u5fc3\u81ea\u5df1\uff0c\u96be\u9053\u53c8\u5fcd\u5fc3 \u72ec\u81ea\u79bb\u53bb\uff0c\u8ba9\u5979\u5b64\u96f6\u96f6\u7684\u5728\u8fd9\u91cc\u6000\u7740\u75f4\u5fc3\uff0c\u7b49\u5019\u4e00\u4e2a\u6c38\u4e0d\u4f1a\u518d\u56de\u6765\u7684\u4eba\uff1f \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u4e71\u5982\u9ebb\uff0c\u6084\u6084\u7684\u79bb\u5f00\u4e86\u674e\u6c81\u6885\uff0c\u5728\u51b0\u5854\u7fa4\u4e2d\u5f98\u5f8a\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u5ffd\u89c1\u90a3\u4e24\u4e2a\u602a\u4eba \u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u5b9b\u5982\u77f3\u50cf\u3002\u91d1\u4e16\u9057\u8fd9\u624d\u8bb0\u8d77\u4ed6\u4eec\uff0c\u8d70\u4e0a\u53bb\u4e00\u63a2\uff0c\u6c14\u606f\u6beb\u65e0\uff0c\u7adf\u662f\u6b7b\u4e86\u3002\u500f\u53e4 \u62c9\u4e0e\u963f\u65af\u7f57\u8fd9\u4e24\u4e2a\u4eba\uff0c\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u8bba\u5230\u5185\u529f\u7684\u7cbe\u7eaf\uff0c\u5374\u4e0d\u5982\u674e\u6c81\u6885\u4f20\u81ea\u5927\u5c71\u7684\u6b63\u5b97\u5185\u529f\uff0c \u56e0\u800c\u80fd\u591f\u652f\u6301\u7684\u65f6\u95f4\uff0c\u6bd4\u674e\u6c81\u6885\u66f4\u77ed\u3002 \u3000\u3000\u91d1\u4e16\u9057\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u56db\u4e2a\u5728\u559c\u739b\u62c9\u96c5\u5c71\u4e0a\u9001\u547d\u7684\u4eba\u3002\u201d\u60f3\u5230\u4e0d\u8be5\u8ba9\u674e\u6c81\u6885\u82cf\u9192\u4e4b \u540e\u770b\u5230\u6b7b\u5c38\u7684\u60e8\u72b6\uff0c\u4e8e\u662f\u6316\u5f00\u5730\u4e0a\u7684\u79ef\u96ea\uff0c\u5c06\u8fd9\u4e24\u4e2a\u602a\u4eba\u7684\u5c38\u4f53\u63a9\u57cb\u3002\u5ffd\u7136\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a \u4eba\u6b7b\u4e86\u8fd8\u6709\u6211\u7ed9\u4ed6\u4eec\u63a9\u57cb\uff0c\u6211\u6b7b\u4e86\u53c8\u6709\u8c01\u6765\u57cb\u6211\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u56de\u8f6c\u5934\u6765\uff0c\u5ffd\u89c1\u674e\u6c81\u6885\u5728\u5979\u4e0a\u52a8\u4e86\u4e24\u4e0b\uff0c\u773c\u76ae\u4e5f\u597d\u4f3c\u5c31\u8981\u5f20\u5f00\u3002\u8fd9\u4e00\u77ac\u95f4\uff0c\u91d1\u4e16 \u9057\u5fc3\u60b8\u4e0d\u4f11\uff0c\u7a81\u7136\u4f5c\u4e86\u51b3\u5b9a\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u4e0d\u5e94\u8ba9\u5979\u773c\u7741\u7741\u77a7\u6211\u6b7b\u53bb\uff01\u6211\u4e00\u751f\u51b7\u9177\u5bf9\u5f85\u4e16 \u4eba\uff0c\u6211\u4e5f\u4e0d\u914d\u63a5\u53d7\u5979\u7684\u7231\u610f\u3002\u201d\u5fc3\u610f\u867d\u51b3\uff0c\u811a\u6b65\u8fd8\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u3002\u53ea\u89c1\u674e\u6c81\u6885\u5728\u5730\u4e0a\u8f6c\u4e86\u4e2a \u8eab\uff0c\u624b\u811a\u6162\u6162\u8212\u5c55\u3002\u91d1\u4e16\u9057\u54ac\u4e86\u54ac\u7259\uff0c\u5ffd\u7136\u8df3\u4e0a\u524d\u53bb\uff0c\u5728\u5979\u989d\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u4e22\u4e0b\u53f1\u5269\u7684\u534a\u8fb9 \u96ea\u9e21\uff0c\u9f13\u8d77\u5168\u8eab\u6c14\u529b\uff0c\u8dd1\u51fa\u4e86\u201c\u51b0\u5854\u7fa4\u201d\uff0c\u518d\u4e5f\u4e0d\u6562\u56de\u5934\u3002 \u3000\u3000\u80cc\u540e\u4f20\u6765\u5fae\u5f31\u7684\u547c\u58f0\uff0c\u90a3\u662f\u674e\u6c81\u6885\u7684\u58f0\u97f3\uff0c\u9690\u9690\u7ea6\u7ea6\u8fd8\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\uff0c\u5979\u662f\u5728\u53eb\uff1a\u201c\u4e16 \u9057\u54e5\u54e5\uff0c\u8ba9\u9057\u54e5\u54e5\uff01\u201d\u91d1\u4e16\u9057\u611f\u5230\u65e0\u9650\u6b23\u60a6\uff1a\u674e\u6c81\u6885\u6bd5\u7adf\u82cf\u9192\u4e86\uff1b\u53c8\u611f\u5230\u65e0\u9650\u8f9b\u9178\uff0c\u4e16\u754c\u4e0a \u7adf\u6709\u4e00\u4e2a\u8fd9\u4e48\u5173\u5fc3\u81ea\u5df1\u7684\u4eba\uff0c\u7136\u800c\u81ea\u5df1\u7adf\u4e0d\u80fd\u548c\u5979\u8bc0\u522b\uff1a\u53c8\u611f\u5230\u83ab\u540d\u5176\u5999\u7684\u6050\u60e7\uff0c\u597d\u50cf\u795e\u8bdd \u4e2d\u7684\u5de8\u4eba\u9003\u907f\u81ea\u5df1\u7684\u5f71\u5b50\u8ffd\u9010\u4e00\u6837\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u9003\u51fa\u4e86\u51b0\u5854\u7fa4\u3002 \u3000\u3000\u592a\u9633\u65e9\u5df2\u843d\u5c71\u53bb\u4e86\uff0c\u4e00\u94a9\u65b0\u6708\u5728\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u6cfb\u4e0b\u5e7d\u51b7\u7684\u6e05\u5149\uff0c\u7fa4\u5cf0\u96ea\u76d6\uff0c\u559c\u739b\u62c9\u96c5\u5c71 \u7684\u591c\u665a\uff0c\u6c89\u6d78\u5728\u96ea\u5149\u6708\u666f\u4e4b\u4e2d\uff0c\u5468\u56f4\u6570\u91cc\u7684\u666f\u7269\uff0c\u8fd8\u662f\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7fe1\u7fe0\u822c\u7684\u51b0\u5ddd\uff0c\u5b9d\u77f3 \u822c\u7684\u51b0\u5854\uff0c\u6784\u6210\u4e86\u7edd\u5999\u7684\u56fe\u753b\uff0c\u5947\u4e3d\u592b\u6d9b\uff01\u90a3\u662f\u5929\u516c\u7684\u5927\u624b\u7b14\uff0c\u5e7b\u51fa\u4e86\u8fd9\u4eba\u4e16\u95f4\u7684\u795e\u4ed9\u5883 \u754c\uff01\u7136\u800c\u8fd9\u795e\u4ed9\u7684\u5883\u754c\uff0c\u5374\u53c8\u662f\u4f55\u5176\u51c4\u5bc2\uff0c\u4f55\u5176\u6e05\u51b7\uff01\u91d1\u4e16\u9057\u9664\u4e86\u9759\u542c\u81ea\u5df1\u7684\u547c\u5438\u4e4b\u5916\uff0c\u773c \u524d\u767d\u832b\u832b\u4e00\u7247\uff0c\u5b8c\u5168\u770b\u4e0d\u5230\u6709\u751f\u547d\u7684\u4e1c\u897f\uff0c\u91d1\u4e16\u9057\u53ea\u611f\u5230\u81ea\u5df1\u4e5f\u5feb\u8981\u7a92\u606f\u4e86\u3002 \u3000\u3000\u7136\u800c\u91d1\u4e16\u9057\u8fd8\u662f\u9f13\u52c7\u524d\u884c\u3002\u4ed6\u6296\u4e00\u6296\u8eab\u4e0a\u7684\u51b0\u96ea\uff0c\u50cf\u662f\u4e0b\u4e86\u6781\u5927\u7684\u51b3\u5fc3\uff0c\u6296\u843d\u4e86\u4e00\u5207\u5bf9 \u4e8e\u4eba\u4e16\u7684\u4f9d\u604b\u548c\u8bb0\u5fc6\uff0c\u5c06\u4e0b\u9762\u7684\u4e16\u754c\u8fde\u540c\u674e\u6c81\u6885\u5728\u5185\u90e8\u629b\u5728\u540e\u9762\u3002 \u3000\u3000\u8fce\u9762\u662f\u4e00\u9053\u7eb5\u76f4\u7684\u51b0\u88c2\u7f1d\uff0c\u963b\u7740\u53bb\u8def\uff0c\u88c2\u7f1d\u6df1\u9677\u800c\u72ed\u7a84\uff0c\u5c31\u50cf\u4e00\u6761\u7ad6\u7740\u7684\u201c\u51b0\u80e1\u540c\u201d\u3002 \u91d1\u4e16\u9057\u627e\u4e0d\u5230\u51fa\u8def\uff0c\u53ea\u597d\u94bb\u5165\u4e86\u201c\u51b0\u80e1\u540c\u201d\u3002\u201c\u80e1\u540c\u201d\u5e7d\u6df1\u6697\u9edd\uff0c\u867d\u6709\u4e0a\u9762\u900f\u4e0b\u6765\u7684\u51b0\u96ea\u5bd2 \u5149\uff0c\u773c\u524d\u9053\u8def\u5df2\u770b\u4e0d\u6e05\u695a\u4e86\u3002\u91d1\u4e16\u9057\u4f46\u89c9\u7b4b\u75b2\u529b\u7aed\uff0c\u56db\u80a2\u9ebb\u6728\uff0c\u53ea\u597d\u5728\u201c\u51b0\u80e1\u540c\u201d\u4e2d\u76d8\u819d\u9759 \u5750\uff0c\u9ed8\u8fd0\u7384\u529f\u3002\u867d\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u8fd0\u529f\uff0c\u4f46\u5df1\u4e0d\u80fd\u50cf\u5e73\u65f6\u4e00\u6837\u5410\u7eb3\u547c\u5438\u3002\u5750\u4e86\u8bb8\u4e45\uff0c\u771f\u6c14\u5140\u662f\u4e0d \u80fd\u900f\u8fc7\u5341\u4e8c\u91cd\u5173\u3002\u91d1\u4e16\u9057\u5728\u534a\u7761\u534a\u9192\u4e4b\u4e2d\uff0c\u6e21\u8fc7\u4e86\u4e8c\u4e2a\u6f2b\u957f\u7684\u591c\u665a\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u9633\u5149\u900f\u4e0b\u4e86\u51b0\u80e1\u540c\uff0c\u91d1\u4e16\u9057\u7cbe\u529b\u7a0d\u7a0d\u6062\u590d\uff0c\u53c8\u5411\u524d\u884c\uff0c\u884c\u4e86\u8bb8\u4e45\uff0c\u624d\u5230\u51b0\u80e1\u540c \u7684\u5c3d\u5934\uff0c\u53c8\u5f97\u5411\u4e0a\u9762\u722c\u4e86\u3002\u8fd9\u51b0\u80e1\u540c\u867d\u7136\u53ea\u6709\u4e8c\u5341\u6765\u4e08\u9ad8\uff0c\u4f46\u5374\u722c\u5f97\u975e\u5e38\u5403\u529b\uff0c\u5bd2\u98ce\u524a\u4f53\u5982 \u5200\uff0c\u6c41\u6c34\u4ecd\u662f\u4e0d\u505c\u7684\u4ece\u989d\u89d2\u4e0a\u6dcc\u4e0b\uff0c\u91d1\u4e16\u9057\u63a5\u8fde\u51e0\u6b21\u4ece\u4e2d\u9014\u8dcc\u843d\u4e0b\u6765\uff0c\u597d\u4e0d\u5bb9\u6613\u722c\u5230\u4e86\u80e1\u540c \u7684\u9876\u7aef\u4f46\u89c1\u65e5\u5934\u5df2\u8fc7\u4e2d\u5929\uff0c\u91d1\u4e16\u9057\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u7684\u751f\u547d\u671f\u9650\uff0c\u5df2\u7ecf\u4e0d\u591f\u4e24\u5929\u4e86\uff01 \u3000\u3000\u91d1\u4e16\u9057\u7a0d\u7a0d\u6b47\u606f\u4e86\u4e00\u4f1a\uff0c\u5403\u5b8c\u4e86\u6700\u540e\u4e00\u4efd\u5e72\u7cae\uff0c\u8179\u4e2d\u8fd8\u89c9\u7a7a\u865a\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u89c1\u4e00\u53ea\u96ea\u7f8a \u4ece\u8eab\u65c1\u7ecf\u8fc7\uff0c\u91d1\u4e16\u9057\u6025\u5fd9\u8dd1\u53bb\u8ffd\u9010\u96ea\u7f8a\uff0c\u54ea\u77e5\u96ea\u7f8a\u662f\u6700\u80c6\u602f\u7684\u52a8\u7269\uff0c\u4e0d\u8ffd\u81ea\u53ef\uff0c\u4e00\u8ffd\u5b83\uff0c\u5b83 \u672a\u66fe\u89c1\u8fc7\u4eba\uff0c\u53ea\u5f53\u662f\u4ec0\u4e48\u51f6\u6076\u7684\u91ce\u517d\uff0c\u653e\u5f00\u56db\u8e44\u75be\u8dd1\uff0c\u91d1\u4e16\u9057\u54ea\u8ffd\u5f97\u53ca\uff0c\u8fd9\u624d\u53d1\u73b0\uff0c\u81ea\u5df1\u7684 \u8f7b\u529f\u4e5f\u5df2\u5927\u4e0d\u5982\u524d\u4e86\u3002\u5176\u5b9e\u4e0d\u662f\u91d1\u4e16\u9057\u7684\u6b66\u529f\u51cf\u9000\uff0c\u5728\u8fd9\u9ad8\u5c71\u4e4b\u4e0a\uff0c\u6c27\u6c14\u7f3a\u4e4f\uff0c\u4efb\u662f\u76d6\u4e16\u82f1 \u96c4\uff0c\u4e5f\u8981\u53d7\u751f\u7406\u7684\u5f71\u54cd\uff0c\u54ea\u80fd\u50cf\u5e73\u5730\u4e00\u6837\u6765\u53bb\u81ea\u5982\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u6253\u4e0b\u4e24\u5934\u9ec4\u5634\u4e4c\u9e26\uff0c\u751f\u4e86\u534a\u5929\u7684\u706b\uff0c\u628a\u4e4c\u9e26\u70e4\u719f\uff0c\u9e26\u8089\u7c97\u7cd9\uff0c\u800c\u4e14\u5e26\u6709\u5e7f\u80a1\u64c5 \u5473\uff0c\u4f46\u5728\u91d1\u4e16\u9057\u5df2\u89c9\u5f97\u662f\u6700\u7f8e\u5473\u7684\u73cd\u8e0c\u3002\u518d\u884c\u4e86\u534a\u5929\uff0c\u773c\u524d\u666f\u8272\u7a81\u53d8\u3002 \u3000\u3000\u8fd9\u662f\u51f8\u51fa\u6765\u7684\u5c71\u62d7\u5730\u533a\uff0c\u53d7\u7684\u98ce\u529b\u6700\u5927\uff0c\u72c2\u98ce\u5377\u7740\u79ef\u96ea\uff0c\u5439\u5f97\u4eba\u96be\u4ee5\u524d\u8fdb\uff0c\u559c\u739b\u62c9\u96c5\u5c71 \u8bf8\u5cf0\uff0c\u90fd\u662f\u7ec8\u5e74\u96ea\u76d6\uff0c\u53ea\u6709\u8fd9\u4e00\u5904\u4e0a\u9762\u7684\u5c71\u5cf0\uff0c\u56e0\u4e3a\u7ecf\u5e38\u88ab\u72c2\u98ce\u5439\u522e\uff0c\u5c71\u5cf0\u5317\u5364\uff0c\u4e5f\u5373\u662f\u6b63 \u5411\u7740\u91d1\u4e16\u9057\u7684\u8fd9\u4e00\u9762\u5c71\u5761\uff0c\u79ef\u96ea\u88ab\u98ce\u5439\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u9732\u51fa\u50a8\u8272\u7684\u5ca9\u77f3\uff0c\u4e0e\u5468\u56f4\u666f\u8272\u5927\u4e0d\u8c03 \u548c\uff0c\u66f4\u589e\u8352\u51b7\u5bc2\u5bde\u4e4b\u611f\uff0c\u4ee4\u4eba\u60ca\u7136\u751f\u60e7\uff01 \u3000\u3000\u91d1\u4e16\u9057\u5728\u72c2\u98ce\u4e2d\u530d\u5310\u524d\u8fdb\uff0c\u722c\u5230\u5929\u9ed1\uff0c\u624d\u901a\u8fc7\u8fd9\u51f8\u51fa\u6765\u7684\u5c71\u62d7\u5730\u533a\uff0c\u53ef\u601c\u91d1\u4e16\u9057\u7684\u624b\u8db3 \u90fd\u5df2\u78e8\u5f97\u4f24\u635f\u6d41\u8840\uff0c\u5c31\u5728\u5c71\u5761\u4e0a\u751f\u8d77\u91ce\u706b\uff0c\u7761\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u8eab\uff0c\u83b7\u5f97\u4e24\u53ea\u91ce\u514d\uff0c\u679c \u8179\u4e4b\u540e\uff0c\u53c8\u5411\u524d\u884c\u3002 \u3000\u3000\u8fd9\u5df2\u7ecf\u662f\u91d1\u4e16\u9057\u751f\u547d\u671f\u9650\u7684\u6700\u540e\u4e00\u5929\u4e86\u3002\u73e0\u7a46\u6717\u739b\u5cf0\u5c31\u5728\u9762\u524d\uff0c\u770b\u6765\u5e76\u4e0d\u8fdc\u4e86\u3002\u53ef\u662f\u73e0 \u7a46\u6717\u739b\u5cf0\u9ad8\u8038\u5165\u4e91\uff0c\u5373\u7b97\u6500\u4e0a\u4e86\u73e0\u5cf0\uff0c\u8fd8\u5f97\u591a\u5c11\u65f6\u65e5\u624d\u80fd\u5230\u8fbe\u5cf0\u9876\uff1f\u800c\u4eca\u53ea\u6709\u77ed\u77ed\u7684\u4e00\u5929\u671f \u9650\uff0c\u91d1\u4e16\u9057\u60f3\u5f81\u670d\u73e0\u5cf0\u7684\u613f\u671b\u770b\u6765\u662f\u7edd\u671b\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u6b64\u9645\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\uff0c\u8981\u5230\u8fbe\u73e0\u5cf0\uff0c\u8981\u521b\u9020\u4eba\u7c7b\u7684\u5947\u8ff9\uff01\u4e0d\u7ba1\u662f\u5426\u7edd\u671b\uff0c\u4ed6\u4ecd\u662f\u9f13\u52c7 \u524d\u884c\u3002 \u3000\u3000\u8d8a\u5230\u540e\u6765\uff0c\u8270\u96be\u8d8a\u751a\uff0c\u91d1\u4e16\u9057\u5f20\u5927\u4e86\u5634\u62fc\u547d\u5730\u5438\u6c14\uff0c\u4ecd\u7136\u611f\u5230\u80f8\u812f\u95ed\u585e\uff0c\u5598\u4e0d\u8fc7\u6c14\u6765\uff0c \u731b\u70c8\u7684\u897f\u5317\u98ce\u51b2\u51fb\u7740\u5317\u5cf0\u548c\u4e3b\u5cf0\u7684\u5ca9\u58c1\uff0c\u5e26\u7740\u66b4\u96e8\u4e00\u6837\u7684\u51b0\u6e23\u548c\u96ea\u7c92\uff0c\u5636\u5578\u7740\uff0c\u7ffb\u6eda\u7740\uff0c\u5f62 \u6210\u4e00\u80a1\u5f3a\u70c8\u7684\u65cb\u98ce\uff0c\u91d1\u4e16\u9057\u8d70\u4e0d\u52a8\u4e86\uff01\u5728\u5730\u4e0a\u51e0\u4e4e\u662f\u4e00\u5bf8\u4e00\u5bf8\u7684\u722c\u884c\u3002 \u3000\u3000\u624b\u89e6\u7740\u73e0\u7a46\u6717\u739b\u5cf0\u7684\u5ca9\u77f3\u4e86\uff0c\u91d1\u4e16\u9057\u7684\u624b\u8db3\u65e9\u5df2\u9ebb\u6728\u4e86\uff0c\u8fd9\u65f6\u5374\u7a81\u611f\u5230\u4e00\u80a1\u6e05\u51b7\u4e4b\u6c14\uff0c \u7cbe\u795e\u9661\u7684\u632f\u4f5c\u8d77\u6765\uff0c\u7ec8\u4e8e\u89e6\u5230\u73e0\u7a46\u6717\u739b\u5cf0\u7684\u5ca9\u77f3\u4e86\uff01\u597d\u50cf\u56de\u5149\u53cd\u7167\u7684\u75c5\u4eba\uff0c\u53d7\u5230\u4e86\u5f3a\u5fc3\u5242\u7684 \u523a\u6fc0\uff0c\u91d1\u4e16\u9057\u53c8\u62fc\u547d\u7684\u5411\u4e0a\u6500\u767b\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u773c\u524d\u91d1\u661f\u95ea\u70c1\u3002\u5934\u660f\u8111\u6da8\uff0c\u9664\u4e86\u4e00\u56e2\u56e2\u7684\u5e7b\u5f71\u4e4b\u5916\uff0c\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u4e86\u3002\u6700\u540e\u7684 \u65f6\u523b\u5230\u4e86\uff0c\u91d1\u4e16\u9057\u7684\u6c14\u529b\u5df2\u662f\u5b8c\u5168\u6d88\u5931\uff0c\u8d70\u706b\u5165\u9b54\u7684\u8ff9\u8c61\u4e5f\u5f00\u59cb\u51fa\u73b0\u4e86\uff01 \u3000\u3000\u5e7b\u5f71\u6e10\u6e10\u6269\u5927\uff0c\u6709\u674e\u6c81\u6885\u7684\u5f71\u5b50\uff0c\u6709\u51b0\u5ddd\u5929\u5973\u7684\u5f71\u5b50\uff0c\u6709\u4ed6\u5e08\u7236\u6bd2\u9f99\u5c0a\u8005\u7684\u5f71\u5b50\u3002\u8fd9\u4e9b \u5f71\u5b50\u90fd\u5728\u6ce8\u89c6\u4ed6\uff0c\u8033\u8fb9\u597d\u50cf\u542c\u5f97\u4eba\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u53ef\u601c\u7684\u5b69\u5b50\uff01\u201d\u8fd9\u662f\u8c01\u8bf4\u7684\u5462\uff1f\u91d1\u4e16\u9057\u6323 \u624e\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4eba\u53ef\u601c\uff01\u201d\u4f46\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u767b\u65f6\u8dcc\u5012\u73e0\u5cf0\u811a\u4e0b\uff0c\u4ed6\u6ca1\u6709\u5f81\u670d \u73e0\u5cf0\uff0c\u5374\u7ed9\u73e0\u5cf0\u5f81\u670d\u4e86\uff01 \u3000\u3000\u8ff7\u832b\u4e2d\uff0c\u91d1\u4e16\u9057\u5ffd\u7136\u611f\u5230\u4eba\u4e16\u7684\u53ef\u604b\uff0c\u4ed6\u4ece\u5fc3\u5e95\u91cc\u53eb\u558a\u51fa\u6765\u9053\uff1a\u201c\u6211\u8fd8\u8981\u6d3b\uff01\u201d\u4e00\u80a1\u72c2 \u98ce\u6253\u6765\uff0c\u72c2\u98ce\u631f\u7740\u51b0\u78b4\u548c\u96ea\u7c92\uff0c\u6492\u5728\u4ed6\u7684\u9762\u4e0a\uff0c\u6492\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u6e10\u6e10\u7684\u5c06\u4ed6\u63a9\u76d6\u4e86\uff01 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u91d1\u4e16\u9057\u597d\u50cf\u5728\u6c89\u7761\u4e2d\u7a81\u7136\u88ab\u4eba\u60ca\u9192\uff0c\u50f5\u786c\u7684\u8eab\u4f53\u4e0a\u53c8\u7ade\u597d\u4f3c\u6709\u4e86\u77e5 \u89c9\uff0c\u89c9\u5f97\u75bc\u75db\u4e86\uff0c\u773c\u524d\u53c8\u662f\u4e00\u56e2\u56e2\u7684\u5e7b\u5f71\uff0c\u53c8\u597d\u4f3c\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u7684\u5c42\u4e91\u4e00\u5c42\u5c42\u7684\u5411\u81ea\u5df1\u538b\u4e0b \u6765\uff0c\u91d1\u4e16\u9057\u60f3\u53eb\uff0c\u53eb\u4e0d\u51fa\u58f0\uff0c\u4f9d\u7a00\u542c\u5f97\u4e00\u4e2a\u4eba\u5728\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u53ef\u601c\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u8fd9\u7684\u786e\u662f\u4eba\u7c7b\u8bf4\u8bdd\u7684\u58f0\u97f3\u3002\u201c\u54a6\uff0c\u6211\u5e76\u6ca1\u6709\u6b7b\uff1f\u8fd9\u4e5f\u4e0d\u662f\u68a6\uff1f\u201d\u91d1\u4e16\u9057\u60f3\u9053\u3002\u4f46\u773c\u775b\u8fd8 \u662f\u7741\u4e0d\u5f00\u6765\uff0c\u8bf8\u822c\u9b54\u76f8\uff0c\u8bf8\u822c\u5e7b\u5f71\u90fd\u6e10\u6e10\u6d88\u6563\u4e86\u3002\u9aa4\u7136\u95ee\uff0c\u91d1\u4e16\u9057\u611f\u5230\u4e00\u80a1\u5de8\u5927\u7684\u6696\u6d41\u4ece\u8eab \u4f53\u6d41\u8fc7\uff0c\u51b2\u51fb\u81ea\u5df1\u5404\u5904\u5927\u7a74\uff0c\u9aa8\u8282\u597d\u50cf\u88ab\u5229\u5200\u652f\u89e3\u4f3c\u7684\uff0c\u75bc\u75db\u4e4b\u4e2d\uff0c\u5374\u53c8\u6709\u4e00\u79cd\u8f7b\u677e\u4e4b\u611f\u3002 \u518d\u8fc7\u4e00\u4f1a\uff0c\u75bc\u75db\u7684\u611f\u89c9\u4e5f\u6e10\u6e10\u51cf\u5f31\u4e86\uff0c\u4f46\u89c9\u90a3\u80a1\u5de8\u5927\u7684\u6696\u6d41\uff0c\u5728\u4f53\u5185\u6d41\u8f6c\uff0c\u7adf\u4f3c\u5316\u6210\u4e86\u4e00\u56e2 \u706b\u7130\uff0c\u5728\u4f53\u5185\u71c3\u70e7\u8d77\u6765\uff0c\u91d1\u4e16\u9057\u4f46\u89c9\u5185\u5916\u7126\u6e34\u4e4b\u6781\uff0c\u60f3\u5f20\u53e3\u5450\u558a\uff0c\u5374\u558a\u4e0d\u51fa\u58f0\uff1b\u60f3\u5f20\u5f00\u773c \u775b\uff0c\u773c\u76ae\u4e0a\u5374\u4f3c\u538b\u7740\u5343\u65a4\u91cd\u7269\u3002\u5ffd\u7136\u95f4\uff0c\u4e00\u80a1\u6e05\u51c9\u4e4b\u6c14\uff0c\u76f4\u900f\u5fc3\u7530\uff0c\u6709\u5982\u996e\u4e86\u7389\u6db2\u743c\u6d46\uff0c\u5c06 \u4f53\u4e2d\u7684\u70e6\u8e81\u706b\u70ed\u4e4b\u6c14\u6d88\u9664\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u90a3\u80a1\u6696\u6d41\u4ecd\u7136\u5728\u4f53\u5185\u6d41\u8f6c\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6162\u6162\u6062\u590d\u4e86\u77e5\u89c9\uff0c\u6162\u6162\u7741\u5f00\u4e86\u773c\u775b\uff0c\u9996\u5148\u770b\u5230\u7684\u662f\u4e24\u53ea\u70af\u70af\u53d1\u5149\u7684\u773c\u775b\uff0c\u6e10\u6e10\u770b \u6e05\u695a\u4e86\u9762\u5bb9\u7684\u8f6e\u5ed3\uff0c\u91d1\u4e16\u9057\u51e0\u4e4e\u8981\u558a\u51fa\u58f0\u6765\uff0c\u53ef\u60dc\u6c14\u529b\u6beb\u65e0\u3002\u60f3\u6323\u624e\u4e5f\u52a8\u5f39\u4e0b\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u91d1\u4e16\u9057\u4e0d\u613f\u5411\u4ed6\u6c42\u6551\u3001\u60f3\u8eb2\u907f\u4ed6\u7684\u5510\u6653\u6f9c\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e00\u6765\u4e3a\u4e86\u5bfb\u89c5\u91d1\u4e16\u9057\uff0c\u4e8c\u6765\u4e3a\u4e86\u4e0e\u63d0\u6469\u8fbe\u591a\u6253\u8d4c\u6500\u5c71\uff0c\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u4ed6\u4ece\u53e6\u4e00\u6761\u8def \u767b\u5c71\uff0c\u7ed5\u8fc7\u4e86\u51b0\u5854\u7fa4\uff0c\u76f4\u62b5\u73e0\u7a46\u6717\u739b\u5cf0\u7684\u811a\u4e0b\u3002\u9976\u662f\u4ed6\u7684\u5185\u529f\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u9976\u662f\u4ed6 \u957f\u4f4f\u5929\u5c71\uff0c\u80fd\u591f\u9002\u5e94\u9ad8\u5c71\u7684\u73af\u5883\uff0c\u8fd9\u65f6\u4e5f\u611f\u5230\u547c\u5438\u56f0\u96be\uff0c\u53ea\u80fd\u4e00\u6b65\u4e00\u6b65\u7684\u5411\u4e0a\u6500\u767b\u4e86\u3002\u5c31\u5728 \u4ed6\u5f00\u59cb\u6500\u767b\u73e0\u5cf0\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u4e86\u8fd8\u6ca1\u6709\u88ab\u79ef\u96ea\u5b8c\u5168\u63a9\u76d6\u7684\u91d1\u4e16\u9057\u3002\u5510\u6653\u6f9c\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c \u6316\u5f00\u79ef\u96ea\uff0c\u6478\u4e00\u6478\u91d1\u4e16\u9057\u7684\u5fc3\u5934\uff0c\u8fd8\u6709\u4e9b\u5fae\u6c14\u606f\uff0c\u5e78\u4e8f\u4ed6\u6765\u5f97\u53ca\u65f6\uff0c\u5c06\u91d1\u4e16\u9057\u4ece\u6b7b\u4ea1\u7684\u8fb9\u7f18 \u4e0a\u62c9\u4e86\u56de\u6765\uff01 \u3000\u3000\u91d1\u4e16\u9057\u5f20\u5f00\u773c\u775b\uff0c\u4f46\u89c1\u5510\u6653\u6f9c\u5934\u4e0a\u767d\u6c14\u817e\u817e\uff0c\u6c57\u6c34\u4ece\u989d\u89d2\u4e0a\u4e0d\u505c\u7684\u6dcc\u4e0b\uff0c\u77e5\u9053\u4ed6\u6b63\u5728\u7528 \u6df1\u6e5b\u7684\u5185\u529f\u66ff\u81ea\u5df1\u51b2\u5173\u89e3\u7a74\uff0c\u6d88\u9664\u90a3\u201c\u8d70\u706b\u4eba\u9b54\u201d\u7684\u90aa\u6bd2\uff0c\u5fc3\u4e2d\u65e2\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u4ed6\u4e00 \u751f\u4e0d\u613f\u5411\u4eba\u4e5e\u601c\uff0c\u4e0d\u613f\u53d7\u4eba\u6069\u60e0\uff0c\u7136\u800c\u8fd9\u4e00\u6b21\u5374\u4e0d\u7531\u5f97\u4ed6\u4e0d\u63a5\u53d7\u4e86\u3002\u4ed6\u8fd8\u4e0d\u77e5\u9053\uff0c\u5510\u6653\u6f9c\u4e3a \u4e86\u6551\u4ed6\uff0c\u4e3a\u4e86\u4f7f\u4ed6\u80fd\u5c3d\u5feb\u7684\u6062\u590d\uff0c\u9664\u4e86\u8017\u8d39\u7cbe\u529b\uff0c\u7528\u5185\u529f\u7ed9\u4ed6\u7597\u6cbb\u4e4b\u5916\uff0c\u8fd8\u628a\u8eab\u4e0a\u4ec5\u5b58\u7684\u4e94 \u7c92\u78a7\u7075\u4e39\u5168\u90fd\u7ed9\u4ed6\u670d\u4e0b\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u89c1\u91d1\u4e16\u9057\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u7ec8\u4e8e\u9192\u4e86\uff01\u201d\u91d1\u4e16\u9057\u5589\u5934\u5495\u5495\u4f5c \u54cd\uff0c\u8fd9\u65f6\u4ed6\u672c\u6765\u53ef\u4ee5\u8bf4\u8bdd\u4e86\uff0c\u4f46\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4e24\u9897\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u4ece\u4ed6\u7684\u773c\u89d2\u6d41\u51fa\u3002\u5510\u6653\u6f9c \u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd8\u662f\u611f\u5230\u75db\u82e6\u5417\uff1f\u54ac\u7740\u7259\u5173\u518d\u5fcd\u4e00\u4f1a\u513f\u3002\u201d\u4ed6\u4e0d\u77e5\u9053\u91d1\u4e16\u9057\u5fc3\u4e2d\u7684\u5343\u822c\u611f\u89e6\uff0c \u53ea\u5f53\u81ea\u5df1\u529f\u529b\u672a\u5230\uff0c\u6025\u5fd9\u51dd\u795e\u8fd0\u6c14\uff0c\u5c06\u771f\u529b\u4f20\u5165\u91d1\u4e16\u9057\u4f53\u5185\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u91d1\u4e16\u9057\u4f46\u89c9\u6c14\u673a\u7545 \u901a\uff0c\u867d\u7136\u4f53\u529b\u5c1a\u672a\u6062\u590d\uff0c\u4f46\u5df2\u77e5\u9053\u7ecf\u6b64\u4e00\u6765\uff1b\u81ea\u5df1\u4e0d\u4f46\u4fdd\u4f4f\u4e86\u6027\u547d\uff0c\u800c\u4e14\u5185\u529f\u4e0a\u4e5f\u5927\u6709\u7a17 \u76ca\u3002 \u3000\u3000\u6b63\u5728\u5510\u6653\u6f9c\u5168\u529b\u65bd\u4e3a\u4e4b\u9645\uff0c\u96ea\u5730\u4e0a\u5ffd\u7136\u4f20\u6765\u4e86\u6781\u8f7b\u7684\u811a\u6b65\u58f0\u97f3\u3002 \u3000\u3000\u8981\u4e0d\u662f\u5510\u6653\u6f9c\u8fd9\u6837\u4e00\u4f4d\u6b66\u5b66\u5927\u5b97\u5e08\uff0c\u8fd9\u6837\u8f7b\u5fae\u7684\u58f0\u97f3\uff0c\u5b9a\u7136\u5f53\u4f5c\u662f\u6d6e\u51b0\u7684\u788e\u54cd\uff0c\u5510\u6653\u6f9c \u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u745b\u59b9\u6765\u4e86\uff1f\u201d\u5ffd\u542c\u5f97\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u654c\u4eba\uff01\u201d\u4ed6\u4ef0\u5367\u5730\u4e0a\uff0c\u5df2\u770b\u5230 \u5510\u6653\u6f9c\u80cc\u540e\u7684\u51b0\u58c1\u73b0\u51fa\u4e86\u63d0\u6469\u8fbe\u591a\u7684\u5f71\u5b50\u3002\u8bdd\u72b9\u672a\u4e86\uff0c\u63d0\u6469\u8fbe\u591a\u7a81\u7136\u4ece\u51b0\u58c1\u8dc3\u4e0b\uff0c\u547c\u7684\u4e00\u638c \u62cd\u5230\u5510\u6653\u6f9c\u7684\u80a9\u5934\u3002 \u3000\u3000\u5e78\u800c\u6709\u91d1\u4e16\u9057\u63d0\u9192\uff0c\u5510\u6653\u6f9c\u8eab\u624b\u4f55\u7b49\u5feb\u6377\uff0c\u5de6\u624b\u62b1\u8d77\u91d1\u4e16\u9057\uff0c\u53f3\u624b\u53cd\u638c\u4e00\u6325\uff0c\u53cc\u638c\u76f8 \u4ea4\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5510\u6653\u6f9c\u8dc4\u8dc4\u8e09\u8e09\u540e\u9000\u51e0\u6b65\uff0c\u51e0\u4e4e\u6ed1\u5761\u3002\u672c\u6765\u5510\u6653\u6f9c\u7684\u529f\u529b\u6bd4\u63d0\u6469 \u8fbe\u591a\u8981\u9ad8\u51fa\u8bb8\u591a\uff0c\u4f46\u56e0\u4ed6\u8017\u4e86\u4e0d\u5c11\u7cbe\u529b\u6551\u6cbb\u91d1\u4e16\u9057\uff0c\u52a0\u4ee5\u53ea\u662f\u7528\u4e00\u638c\u4e4b\u529b\uff0c\u6545\u6b64\u521a\u521a\u548c\u63d0\u6469 \u8fbe\u591a\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8f6c\u8fc7\u5934\u6765\uff0c\u63d0\u6469\u8fbe\u591a\u7684\u72de\u5bd2\u521a\u521a\u6536\u655b\u3002\u5510\u6653\u6f9c\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u5f7c\u6b64\u8d4c\u8d5b\u6500 \u5c71\uff0c\u4f60\u600e\u7684\u6697\u4e2d\u5077\u88ad\uff01\u201d\u63d0\u6469\u8fbe\u591a\u7684\u72de\u7b11\u53d8\u4e3a\u6b22\u7b11\uff0c\u4f5c\u51fa\u4e86\u4e00\u4e2a\u4eb2\u70ed\u7684\u59ff\u6001\uff0c\u62cd\u62cd\u81ea\u5df1\u7684\u80a9 \u5934\uff0c\u5411\u4e0a\u9762\u4e00\u6307\uff0c\u53eb\u9053\uff1a\u201c\u54c8\u5477\uff0c\u54c8\u5435\uff0c\u9ad8\uff0c\u9ad8\uff01\u4e7e\uff0c\u4e7e\uff01\u201d\u610f\u601d\u662f\u62db\u547c\u5510\u6653\u6f9c\u5feb\u53bb\u722c\u5c71\uff0c \u5510\u6653\u6f9c\u542c\u4e0d\u61c2\u4ed6\u7684\u8bdd\uff0c\u770b\u4ed6\u7684\u624b\u52bf\uff0c\u542c\u4ed6\u7684\u8bed\u8c03\uff0c\u4ea6\u5df2\u660e\u767d\uff0c\u8fd9\u63d0\u6469\u8fbe\u591a\u6562\u60c5\u662f\u5077\u88ad\u4e0d\u6210\uff0c \u6545\u610f\u4f5c\u72b6\u62db\u547c\u7684\u3002\u53ea\u89c1\u63d0\u6469\u8fbe\u591a\u4e00\u9762\u80e1\u53eb\uff1b\u4e00\u9762\u722c\u5c71\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u722c\u4e0a\u4e86\u5341\u591a\u4e08\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u7fdf\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4e14\u4e0d\u7ba1\u4ed6\u662f\u6076\u610f\u5077\u88ad\u8fd8\u662f\u597d\u610f\u62db\u547c\uff0c\u6211\u603b\u4e0d\u80fd\u8ba9\u4ed6\u5148\u6211\u767b\u4e0a \u73e0\u5cf0\u3002\u201d\u4f4e\u5934\u4e00\u770b\u91d1\u4e16\u9057\uff0c\u89c1\u91d1\u4e16\u9057\u9762\u8272\u4e5f\u6e10\u8f6c\u7ea2\u6da6\uff0c\u770b\u6b64\u60c5\u5f62\uff0c\u91d1\u4e16\u9057\u5df2\u662f\u8131\u4e86\u5371\u9669\uff0c\u4f53 \u529b\u548c\u6b66\u529f\u7684\u6062\u590d\u4e5f\u662f\u65e6\u5915\u95f4\u4e8b\u4e86\u3002\u5510\u6653\u6f9c\u5c06\u91d1\u4e16\u9057\u8f7b\u8f7b\u653e\u4e0b\uff0c\u540c\u65f6\u4e5f\u7b49\u4e8e\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3 \u5934\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u51af\u7433\u548c\u5979\u7684\u5973\u513f\u4e5f\u4e0a\u6765\u4e86\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49\u5019\u5979\u4eec\uff0c\u6216\u8005\u7977\u4f60\u4f53\u529b\u6062\u590d\u4e4b\u540e\uff0c \u5f84\u81ea\u4e0b\u5c71\uff0c\u5230\u65b9\u4eca\u660e\u5bb6\u4e2d\u53bb\u7b49\u5019\u5979\u4eec\u3002\u201d\u91d1\u4e16\u9057\u6233\u7136\u4e0d\u8bed\uff0c\u9650\u8272\u53c8\u6c81\u51fa\u4e24\u9897\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u201c\u5510\u6653\u6f9c\u5ffd\u7136\u8d77\u4e86\u5f02\u6837\u7684\u611f\u89c9\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5c11\u5e74\u4eba\u600e\u7684\u5982\u6b64\u5947\u602a\uff0c\u5c06\u4ed6\u6551\u9192 \u4e86\uff0c\u4ed6\u9053\u8c22\u4e5f\u4e0d\u8bf4\u4e00\u58f0\u3002\u201d\u5510\u6653\u6f9c\u5e76\u4e0d\u662f\u5e0c\u7f55\u4ed6\u7684\u9053\u8c22\uff0c\u53ea\u662f\u89c9\u5f97\u6b64\u4e8b\u5927\u51fa\u60c5\u7406\u4e4b\u5e38\uff0c\u968f\u60f3 \u9053\uff1a\u201c\u662f\u4e86\uff0c\u60f3\u662f\u4ed6\u5f97\u4ee5\u91cd\u751f\uff0c\u611f\u6781\u800c\u4f4d\uff0c\u795e\u667a\u5c1a\u672a\u6e05\u660e\u54e9\u3002\u201d\u4ed6\u54ea\u77e5\u91d1\u4e16\u9063\u6b64\u523b\u6b63\u662f\u5fc3\u4e8b \u5982\u6f6e\u3002\u662f\u4ecd\u65e7\u50cf\u4ee5\u524d\u4e00\u6837\uff0c\u72ec\u5f80\u72ec\u6765\uff0c\u5bc2\u5bde\u7ec8\u8001\uff1f\u8fd8\u662f\u56f1\u5230\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u83b7\u5f97\u53cb\u8c0a\u7684\u6e29\u6696\uff1f\u6b64 \u4e8b\u6b63\u5728\u91d1\u4e16\u9057\u7684\u5fc3\u5934\u59d4\u51b3\u4e0d\u4e0b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u63d0\u6469\u8fbe\u591a\u53c8\u5df2\u6500\u4e0a\u4e86\u5341\u591a\u4e08\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u65e0\u6687\u518d\u63a8\u6572\u63e3\u6d4b\u91d1\u4e16\u9057 \u7684\u5fc3\u4e8b\u3001\u4e22\u4e0b\u534a\u888b\u5e72\u7cae\uff0c\u4fbf\u53bb\u8ffd\u8d76\u3002\u8d70\u4e86\u51e0\u6b65\uff0c\u9661\u7136\u60f3\u8d77\u4e86\u4e00\u4ef6\u4e8b\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u638f\u51fa\u4e86\u51af\u7433 \u4ea4\u7ed9\u4ed6\u7684\u90a3\u672c\u4e66\u3001\u7b11\u9053\uff1a\u201c\u6211\u51e0\u4e4e\u5fd8\u8bb0\u4e86\uff0c\u8fd9\u662f\u4f60\u5e08\u7236\u7684\u9057\u4e66\u3002\u201d\u8f7b\u8f7b\u4e00\u63b7\uff0c\u5c06\u6bd2\u9f99\u5c0a\u8005\u5728 \u86c7\u5c9b\u6240\u5199\u7684\u90a3\u672c\u65e5\u8bb0\uff0c\u63b7\u5728\u91d1\u4e16\u9057\u7684\u8eab\u65c1\u3002\u4f46\u542c\u5f97\u91d1\u4e16\u9057\u5fae\u5fae\u53f9\u606f\uff0c\u53f9\u606f\u4e2d\u53cd\u663e\u73b0\u5f97\u65e0\u9650\u8be7 \u5f02\uff0c\u65e0\u9650\u51c4\u51c9\uff01 \u3000\u3000\u5510\u6653\u6f9c\u5df2\u5728\u5ced\u58c1\u4e0a\u6500\u767b\u4e86\u51e0\u4e08\u9ad8\uff0c\u56de\u5934\u4e0b\u671b\uff0c\u53ea\u89c1\u91d1\u4e16\u9057\u5df2\u5750\u5728\u5730\u4e0a\uff0c\u7ffb\u9605\u90a3\u672c\u65e5\u8bb0\u3002 \u5510\u6653\u6f9c\u89c1\u63d0\u6469\u8fbe\u591a\u7684\u80cc\u5f71\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u4ed6\u867d\u7136\u89c9\u5f97\u91d1\u4e16\u9057\u7684\u795e\u6001\u6709\u5f02\uff0c\u7ec8\u4e8e\u8fd8\u662f\u629b\u4e0b\u4e86\u91d1\u4e16 \u9057\uff0c\u7d27\u8ddf\u7740\u63d0\u6469\u8fbe\u591a\u7684\u8db3\u5370\u524d\u8fdb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u53ea\u89c9\u547c\u5438\u8d8a\u6765\u8d8a\u662f\u56f0\u96be\uff0c\u5728\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u6500\u767b\uff0c\u90a3\u771f\u662f\u4e16\u4e0a\u65e0\u53ef\u6bd4\u62df\u7684\u5947\u9669\u3002\u53ea \u89c1\u4e0a\u9762\u9664\u4e86\u9661\u5ced\u7684\u957f\u957f\u7684\u51b0\u5761\u4e4b\u5916\uff0c\u8fd8\u6a2a\u5367\u7740\u4e24\u9053\u767e\u4e08\u60ac\u5ca9\uff0c\u73e0\u5cf0\u94f6\u8272\u7684\u5c71\u5ce6\u95f4\u5c3d\u662f\u6d53\u5bc6\u7684 \u767d\u8272\u4e91\u96fe\uff0c\u98de\u7d6e\u4e00\u6837\u7684\u4e91\u6c14\uff0c\u89e6\u624b\u5373\u6563\uff0c\u6709\u51e0\u53ea\u77eb\u5065\u7684\u5c71\u9e70\u5728\u60ac\u5ca9\u4e0a\u7a7a\u76d8\u65cb\uff0c\u7a81\u7136\u95ee\u4e00\u53ea\u5c71 \u9e70\u4ece\u4e91\u96fe\u4e2d\u8dcc\u4e86\u4e0b\u6765\uff0c\u770b\u6765\u5b83\u662f\u56e0\u4e3a\u96fe\u906e\u7740\u89c6\u7ebf\uff0c\u89e6\u7740\u60ac\u5ca9\u7684\u5229\u77f3\u800c\u8dcc\u4e0b\u6765\u7684\u3002\u5510\u6653\u6f9c\u4e0d\u7981 \u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u5140\u9e70\u5c1a\u81ea\u98de\u4e0d\u5230\u73e0\u5cf0\u3002\u201d\u4f46\u4e0d\u7ba1\u5982\u4f55\uff0c\u4ed6\u603b\u4e0d\u80fd\u8ba9\u4e00\u4e2a\u5916\u56fd\u4eba\u6bd4\u4ed6\u5148\u722c \u4e0a\u8fd9\u4e2a\u77a9\u4e8e\u4e2d\u56fd\u7684\u4e16\u754c\u7b2c\u4e00\u5cf0\u3002 \u3000\u3000\u4e0e\u63d0\u6469\u8fbe\u591a\u7684\u8ddd\u79bb\u9010\u6e10\u8fd1\u4e86\uff0c\u5510\u6653\u6f9c\u4f46\u89c9\u7b4b\u75b2\u529b\u7aed\uff0c\u624b\u8db3\u4e95\u7528\uff0c\u4e5f\u53ea\u80fd\u4e00\u5bf8\u4e00\u5bf8\u7684\u5411\u4e0a \u722c\u884c\uff0c\u5fc3\u4e2d\u6b63\u81ea\u5947\u602a\uff0c\u63d0\u6469\u8fbe\u591a\u5374\u600e\u7684\u8fd8\u80fd\u591f\u652f\u6301\u3002\u518d\u63a5\u8fd1\u4e00\u4e9b\uff0c\u4f46\u542c\u53ee\u53ee\u53ee\u4e4b\u58f0\uff0c\u539f\u6765\u63d0 \u6469\u8fbe\u591a\u7684\u80cc\u7fb9\u4e2d\u51c6\u5907\u6709\u5404\u79cd\u767b\u5c71\u5de5\u5177\uff0c\u8fd9\u65f6\u6b63\u5728\u51b0\u5761\u4e0a\u7528\u51b0\u9550\u6316\u9636\uff0c\u5728\u5ca9\u77f3\u4e0a\u9489\u4e0a\u4e00\u53e3\u53e3\u7684 \u94c1\u9489\u3002\u4f46\u4ed6\u6bcf\u4e0a\u4e00\u6b65\uff0c\u5c31\u7528\u5c0f\u94c1\u5e62\u628a\u9489\u5b50\u4e00\u6572\uff0c\u5c06\u94c1\u9489\u6572\u5f97\u6ca1\u5165\u5ca9\u77f3\u4e4b\u4e2d\uff0c\u4f7f\u5510\u6653\u6f9c\u65e0\u6cd5\u5229 \u7528\u3002\u518d\u770b\u4ed6\u8e0f\u8fc7\u7684\u8db3\u5370\uff0c\u53c8\u53d1\u73b0\u4ed6\u662f\u7a7f\u7740\u9576\u6709\u94a2\u9489\u7684\u7279\u5236\u7684\u767b\u5c71\u978b\u5b50\uff0c\u4e0d\u6015\u96ea\u6ed1\u3002\u4ed6\u9760\u7740\u5404 \u79cd\u767b\u5c71\u5de5\u5177\u7684\u5e2e\u52a9\uff0c\u81ea\u662f\u7701\u529b\u5f97\u591a\u3002 \u3000\u3000\u5510\u6653\u6f9c\u96c4\u5fc3\u52c3\u53d1\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u662f\u53ea\u624b\u7a7a\u62f3\u4e5f\u8981\u8d62\u4f60\uff01\u201d\u65bd\u5c55\u5e73\u751f\u7edd\u5b66\uff0c\u4ee5\u5927\u529b\u9e70 \u722a\u529f\uff0c\u6293\u7d27\u5ca9\u77f3\uff0c\u5b9a\u4f4f\u8eab\u5f62\u4e00\u6b65\u6b65\u5411\u4e0a\u6500\u767b\uff0c\u78b0\u5230\u5ca9\u77f3\u5e73\u6ed1\u4e4b\u5904\uff0c\u53c8\u7528\u58c1\u864e\u6e38\u5899\u529f\u52a0\u5feb\u4e0a\u5347 \u7684\u901f\u5ea6\uff0c\u867d\u7136\u5403\u529b\u975e\u5e38\uff0c\u6709\u597d\u51e0\u6b21\u8fd8\u51e0\u4e4e\u6ed1\u4e0b\u6765\uff0c\u4f46\u7ec8\u4e8e\u8fd8\u662f\u652f\u6301\u4f4f\u4e86\uff0c\u4e0e\u63d0\u6469\u8fbe\u591a\u7684\u8ddd\u79bb \u4e5f\u7f29\u77ed\u5230\u53ea\u6709\u4e94\u516d\u4e08\u4e86\u3002 \u3000\u3000\u7b2c\u4e00\u9053\u60ac\u5ca9\u5df2\u6a2a\u5728\u9762\u524d\uff0c\u53ea\u89c1\u63d0\u6469\u8fbe\u591a\u8eab\u4f53\u8d34\u7740\u51b0\u9762\uff0c\u8fdb\u884c\u6500\u767b\uff0c\u90a3\u6c14\u547c\u547c\u7684\u5598\u606f\u58f0\u5439 \u5f97\u51b0\u6e23\u7eb7\u843d\u3002\u4ed6\u5df2\u662f\u7b4b\u75b2\u529b\u7aed\u4e86\u3002\u8981\u4e0d\u662f\u5510\u6653\u6f9c\u8ddf\u5728\u540e\u9762\uff0c\u4ed6\u6015\u5510\u6653\u6f9c\u803b\u7b11\uff0c\u66f4\u6015\u5510\u6653\u6f9c\u5728 \u4ed6\u4e0b\u6765\u4e4b\u65f6\u52a0\u5bb3\uff0c\u4ed6\u65e9\u5df2\u5851\u7ef3\u6e9c\u4e0b\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5b66\u63d0\u6469\u8fbe\u591a\u7684\u65b9\u6cd5\uff0c\u8d34\u7740\u51b0\u9762\uff0c\u8fdb\u884c\u6500\u767b\u3002\u4ed6\u56db\u80a2\u90fd\u5df2\u9ebb\u6728\uff0c\u6c14\u529b\u5c31\u50cf\u8981\u7528\u77f3\u78e8 \u7d27\u69a8\u624d\u4e00\u70b9\u4e00\u70b9\u7684\u69a8\u51fa\u6765\u3002\u8fd9\u65f6\u592a\u9633\u5df2\u7ecf\u504f\u897f\uff0c\u9635\u9635\u5bd2\u98ce\u4ece\u5c71\u5ce6\u95f4\u522e\u8fc7\uff0c\u53d1\u51fa\u9635\u9635\u5578\u9e23\u3002 \u3000\u3000\u7a81\u7136\u98d8\u6765\u4e00\u9635\u4e4c\u4e91\uff0c\u906e\u4f4f\u4e86\u6674\u7a7a\uff0c\u5927\u98ce\u9aa4\u8d77\uff0c\u5439\u5f97\u4eba\u5bf8\u6b65\u96be\u884c\u3002\u5510\u6653\u6f9c\u7d27\u7d27\u6293\u7740\u4e00\u5757\u51f8 \u51fa\u6765\u7684\u77f3\u7b0b\uff0c\u5ffd\u542c\u5f97\u8f70\u9686\u4e4b\u58f0\uff0c\u6574\u4e2a\u5c71\u8c37\u90fd\u597d\u50cf\u8981\u9707\u52a8\u8d77\u6765\uff0c\u539f\u6765\u662f\u78b0\u5230\u73e0\u7a46\u6717\u739b\u5cf0\u9876\u7684 \u201c\u96ea\u5d29\u201d\uff01 \u3000\u3000\u5c71\u5761\u4e0a\u7eb5\u6a2a\u4ea4\u9519\u7684\u51b0\u5ddd\u7a81\u7136\u95f4\u5192\u51fa\u65e0\u6570\u6c14\u6ce1\uff0c\u90a3\u662f\u5c42\u51b0\u9707\u88c2\u4e4b\u540e\u6240\u53d1\u751f\u7684\u73b0\u8c61\uff0c\u6574\u4e2a\u73e0 \u7a46\u6717\u739b\u5cf0\u597d\u50cf\u62ab\u4e0a\u4e86\u8584\u96fe\u8f7b\u7ee2\uff0c\u9633\u5149\u900f\u5c04\u4e0b\u6765\uff0c\u773c\u524d\u4e00\u7247\u767d\u6f3e\u6f3e\u7684\u666f\u8c61\uff0c\u53ea\u542c\u5f97\u51b0\u5757\u70b8\u88c2\u7684 \u58f0\u97f3\u4e0d\u7edd\u4e8e\u8033\uff0c\u5e78\u4e8f\u6709\u5de8\u5927\u7684\u60ac\u5ca9\u6a2a\u5728\u524d\u9762\uff0c\u51b0\u5757\u78b0\u7740\u60ac\u5ca9\uff0c\u4f53\u79ef\u91cd\u7684\u5c31\u50cf\u6eda\u73e0\u4e00\u6837\uff0c\u9047\u5230 \u963b\u788d\u4fbf\u98de\u817e\u8d77\u6765\uff0c\u4f5c\u5f27\u5f62\u7684\u629b\u7269\u7ebf\u5411\u5c71\u8c37\u629b\u4e0b\uff0c\u4f53\u79ef\u8f7b\u7684\u70b8\u6210\u65e0\u6570\u788e\u88c2\u7684\u51b0\u5757\uff0c\u6709\u5982\u6b92\u661f\uff0c \u7eb7\u843d\u5982\u96e8\u3002 \u3000\u3000\u5510\u6653\u6f9c\u7d27\u7d27\u6293\u7740\u51f8\u51fa\u6765\u7684\u77f3\u7b0b\uff0c\u5c06\u8eab\u4f53\u5012\u6302\u5728\u60ac\u7a7a\u7684\u5ca9\u77f3\u4e0b\u9762\uff0c\u4f46\u89c9\u65e0\u6570\u5de8\u5927\u7684\u51b0\u5757\uff0c \u5728\u72c2\u98ce\u4e2d\u547c\u5578\u3001\u70b8\u88c2\uff0c\u4ece\u5934\u9876\u4e0a\u6eda\u8fc7\uff0c\u4ece\u8eab\u8fb9\u98de\u8fc7\u2026\u2026\u8fd9\u771f\u662f\u4eba\u4e16\u4e0a\u96be\u9022\u7684\u5947\u666f\uff0c\u662f\u90a3\u6837\u7684 \u53ef\u6015\uff0c\u53c8\u662f\u90a3\u6837\u7684\u58ee\u4e3d\u65e0\u4f26\uff01\u5510\u6653\u6f9c\u9976\u662f\u76d6\u4e16\u82f1\u96c4\uff0c\u4e5f\u89c9\u5fc3\u5934\u98a4\u9707\u3002 \u3000\u3000\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u5806\u79ef\u7740\u6df1\u4e0d\u53ef\u6d4b\u7684\u4e07\u5e74\u51b0\u96ea\uff0c\u5c24\u5176\u5728\u5510\u6653\u6f9c\u73b0\u5728\u6240\u6500\u767b\u7684\u201c\u5317\u5773\u201d\u9669\u9661\u7684 \u5761\u58c1\u4e0a\uff0c\u66f4\u6f5c\u4f0f\u7740\u65e0\u6570\u51b0\u5d29\u548c\u96ea\u5d29\u7684\u201c\u69fd\u5370\u201d\uff0c\u6210\u4e3a\u73e0\u7a46\u6717\u739b\u5c71\u5cf0\u95f4\u6700\u5371\u9669\u7684\u5730\u533a\uff0c\u51e0\u4e4e\u6bcf \u5e74\u90fd\u8981\u53d1\u751f\u5de8\u5927\u7684\u51b0\u5d29\u548c\u96ea\u5d29\uff0c\u5510\u6653\u6f9c\u8fd9\u6b21\u78b0\u5230\u7684\uff0c\u5176\u5b9e\u53ea\u662f\u5fae\u4e0d\u8db3\u9053\u7684\u4e00\u6b21\u96ea\u5d29\u800c\u5df2\uff01\u5728 \u5de8\u5927\u7684\u96ea\u5d29\u65f6\uff0c\u5343\u767e\u5428\u91cd\u7684\u51b0\u5ca9\u548c\u96ea\u5757\u90fd\u50cf\u706b\u5c71\u4e00\u6837\u55b7\u6cfb\u800c\u4e0b\uff0c\u767e\u91cc\u4e4b\u5916\u90fd\u53ef\u4ee5\u542c\u5230\u5b83\u7684\u8f70 \u9686\u58f0\uff0c\u5728\u96ea\u5d29\u4e09\u6570\u91cc\u4e4b\u5185\u7684\u8303\u56f4\uff0c\u751f\u7269\u4f53\u60f3\u6d3b\u547d\uff01\uff08\u4f5c\u8005\u6309\uff1a\u8fd1\u4ee3\u6500\u5c71\u5bb6\u8ba4\u4e3a\u73e0\u5cf0\u7684\u5317\u52d2\u662f \u201c\u4e0d\u53ef\u903e\u8d8a\u7684\u5929\u9669\u201d\uff0c\u5176\u4e2d\u7684\u4e00\u4e2a\u539f\u56e0\u5c31\u662f\u56e0\u4e3a\u8fd9\u4e2a\u5730\u533a\u7ecf\u5e38\u53d1\u751f\u96ea\u5d29\u3002\u6700\u8fd1\u4e00\u6b21\u4eba\u7c7b\u5728\u5317 \u62d7\u6240\u9047\u5230\u7684\u96ea\u5d29\u662f\u4e00\u4e5d\u4e8c\u4e09\u5e74\u82f1\u56fd\u7684\u63a2\u9669\u961f\u9047\u5230\u7684\uff0c\u5728\u5317\u58a9\u7ea6\u516b\u5343\u7c73\u9ad8\u5ea6\u4e4b\u5904\uff0c\u4e03\u540d\u63a2\u9669\u961f \u5458\u90fd\u88ab\u57cb\u5230\u51b0\u96ea\u7684\u5e95\u5c42\u3002\u6b64\u4e8b\u5927\u82f1\u767e\u79d1\u5168\u4e66\u4ea6\u6709\u8bb0\u8f7d\u3002\uff09 \u3000\u3000\u5510\u6653\u6f9c\u8fd9\u6b21\u78b0\u5230\u7684\u96ea\u5d29\uff0c\u5176\u5b9e\u53ea\u662f\u5fae\u4e0d\u8db3\u9053\u7684\u4e00\u6b21\u800c\u5df2\u3002\u4f46\u5c31\u662f\u8fd9\u6837\u4e00\u6b21\u8f7b\u5fae\u7684\u96ea\u5d29\uff0c \u5df2\u663e\u793a\u51fa\u4e86\u5927\u81ea\u7136\u5de8\u5927\u7684\u5a01\u529b\uff01\u4ee4\u5510\u6653\u6f9c\u8fd9\u6837\u7684\u82f1\u96c4\uff0c\u4e5f\u611f\u5230\u4e2a\u4eba\u529b\u91cf\u7684\u6e3a\u5c0f\uff01 \u3000\u3000\u773c\u524d\u767d\u8499\u8499\u4e00\u7247\uff0c\u5510\u6653\u6f9c\u5b9a\u775b\u6ce8\u89c6\uff0c\u6570\u4e08\u4e4b\u5916\uff0c\u9690\u7ea6\u53ef\u89c1\u5230\u63d0\u6469\u8fbe\u591a\u7684\u666f\u51b5\u3002\u4f46\u89c1\u4ed6\u53cc \u624b\u7d27\u7d27\u6293\u7740\u4e00\u6761\u94c1\u94fe\uff0c\u4ed6\u65e9\u5c31\u5728\u5ca9\u77f3\u4e0a\u51ff\u4e86\u4e00\u53e3\u94c1\u9489\uff0c\u5728\u94c1\u9489\u4e0a\u6302\u4e0a\u94c1\u94fe\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u6574 \u4e2a\u8eab\u5b50\u90fd\u60ac\u5728\u6a2a\u7a7a\u7684\u5927\u5ca9\u77f3\u5e95\u4e0b\uff0c\u6709\u5927\u5ca9\u77f3\u6321\u7740\uff0c\u51b0\u5757\u4f24\u5bb3\u4e0d\u5230\u4ed6\uff0c\u90a3\u662f\u6bd4\u5510\u6653\u6f9c\u5b89\u5168\u5f97 \u591a\u3002\u4ed6\u6bd5\u751f\u5904\u5fc3\u79ef\u8651\u3001\u68a6\u60f3\u6500\u767b\u8fd9\u4e16\u754c\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u66fe\u6d3e\u95e8\u4e0b\u5f1f\u5b50\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u52d8\u67e5\u8fc7\u65e0\u6570 \u6b21\uff0c\u770b\u6765\u4ed6\u5bf9\u53ef\u80fd\u53d1\u751f\u7684\u96ea\u5d29\uff0c\u4e5f\u65e9\u5df2\u4f30\u8ba1\u5728\u5185\uff0c\u6240\u4ee5\u767b\u5c71\u5de5\u5177\u5e26\u5f97\u751a\u4e3a\u9f50\u5168\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u79cd\u4ee4\u4eba\u65e0\u53ef\u6297\u62d2\u7684\u81ea\u7136\u707e\u7978\u4e2d\uff0c\u6700\u91cd\u8981\u7684\u8fd8\u662f\u8d85\u4eba\u7684\u52c7\u6c14\u3002\u5510\u6653\u6f9c\u54ac\u5b9e\u7259\u6839\uff0c \u7528\u4e86\u5168\u8eab\u7684\u529b\u91cf\uff0c\u7d27\u7d27\u6293\u7740\u77f3\u7b0b\uff0c\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u7ec8\u4e8e\u652f\u6301\u4e0b\u6765\u4e86\u3002\u63d0\u6469\u8fbe\u591a\u6293\u7740\u94c1\u94fe\uff0c \u6302\u5728\u60ac\u5ca9\u4e0b\u9762\uff0c\u751f\u547d\u672c\u6765\u5df2\u6709\u4e86\u4fdd\u969c\uff0c\u53cd\u800c\u663e\u5f97\u60f6\u6050\u4e0d\u5b89\uff0c\u53ea\u89c1\u4ed6\u8eab\u4f53\u5267\u70c8\u6447\u6446\uff0c\u53ef\u4ee5\u770b\u51fa \u4ed6\u98a4\u6296\u5f97\u591a\u4e48\u5389\u5bb3\uff01\u6479\u7136\u95f4\u60ac\u5ca9\u4e0a\u8f70\u9686\u4e00\u58f0\u5de8\u54cd\uff0c\u4e00\u5757\u5de8\u5927\u7684\u51b0\u5757\u5760\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u5757\u51b0\u5757\u5927\u5f97\u60ca\u4eba\uff0c\u50cf\u4e00\u5ea7\u5c0f\u5c71\u4f3c\u7684\u9aa4\u7136\u4ece\u5929\u5916\u98de\u6765\uff0c\u538b\u5728\u60ac\u5ca9\u4e0a\u9762\uff0c\u60ca\u5929\u52a8\u5730\u7684\u4e00\u58f0 \u5de8\u54cd\uff0c\u70b8\u88c2\u6210\u65e0\u6570\u788e\u5757\uff0c\u9707\u64bc\u5f97\u90a3\u6a2a\u51f8\u51fa\u6765\u7684\u767e\u4e08\u60ac\u5ca9\u4e5f\u6447\u52a8\u8d77\u6765\uff0c\u5510\u6653\u6f9c\u62fc\u547d\u6293\u7d27\u5ca9\u5c71\uff0c \u773c\u775b\u4e5f\u88ab\u72c2\u98ce\u522e\u5f97\u4e0d\u80fd\u5f20\u5f00\uff0c\u4f46\u89c9\u51b0\u5757\u98d5\u98d5\u7684\u4ece\u56db\u8fb9\u98de\u8fc7\uff0c\u89e6\u4f53\u5982\u5200\uff0c\u5510\u6653\u6f9c\u4e00\u751f\u4e4b\u4e2d\uff0c\u4e0d \u77e5\u7ecf\u8fc7\u591a\u6b21\u5927\u9635\u4ed7\uff0c\u5374\u4ece\u65e0\u4e00\u6b21\u50cf\u73b0\u5728\u7684\u5947\u9669\uff01\u751f\u547d\u7cfb\u4e8e\u4e00\u7ebf\uff0c\u5c31\u50cf\u5230\u4e86\u60ac\u5ca9\u7684\u8fb9\u6cbf\uff0c\u53ea\u8981 \u7a0d\u4e00\u677e\u52b2\uff0c\u4fbf\u4f1a\u4ece\u4e07\u4e08\u9ad8\u5cf0\u8dcc\u4e0b\uff01 \u3000\u3000\u9661\u7136\u95f4\u53ea\u542c\u5f97\u4e00\u58f0\u5389\u53eb\uff0c\u5728\u98ce\u58f0\u4e4b\u4e2d\u63a0\u8fc7\uff0c\u66f4\u663e\u5f97\u523a\u8033\u975e\u5e38\uff0c\u60ca\u5fc3\u8361\u9b44\uff01\u5510\u6653\u6f9c\u52aa\u529b\u7741 \u5f00\u773c\u775b\uff0c\u53ea\u89c1\u63d0\u6469\u8fbe\u591a\u90a3\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u4ece\u9ad8\u7a7a\u98de\u5760\uff0c\u51c4\u5389\u7684\u53eb\u58f0\u6447\u66f3\u7a7a\u9645\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u63d0\u6469 \u8fbe\u591a\u7684\u8eab\u5f62\u5c31\u88ab\u98ce\u96ea\u5377\u6ca1\u4e86\uff01\u672c\u6765\u63d0\u6469\u8fbe\u591a\u6293\u7d27\u94c1\u94fe\uff0c\u6302\u5728\u60ac\u5ca9\u4e0b\u9762\uff0c\u539f\u53ef\u4e0d\u53d7\u4f24\u5bb3\uff0c\u4f46\u4ed6 \u88ab\u8fd9\u5927\u81ea\u7136\u7684\u5a01\u529b\u5413\u7740\u4e86\uff0c\u610f\u5fd7\u652f\u6301\u4e0d\u4e86\u8eab\u4f53\uff0c\u624b\u6307\u4e00\u677e\uff0c\u767b\u65f6\u4e27\u547d\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e5f\u88ab\u8fd9\u4e00\u60e8\u5389\u7684\u666f\u8c61\u5413\u5f97\u5fc3\u60b8\u8eab\u98a4\uff0c\u5e78\u800c\u8fd9\u6b21\u96ea\u5d29\uff0c\u53ea\u662f\u73e0\u5cf0\u4e0a\u4e00\u6b21\u8f7b\u5fae\u7684\u96ea \u5d29\uff0c\u4e0d\u4e45\u98ce\u529b\u4fbf\u6e10\u6e10\u51cf\u8f7b\uff0c\u96ea\u5d29\u4e5f\u505c\u6b62\u4e86\u3002\u5510\u6653\u6f9c\u5411\u524d\u722c\u884c\u4e86\u51e0\u4e08\u4e4b\u5730\uff0c\u5230\u4e86\u63d0\u6469\u8fbe\u591a\u521a\u624d \u8eb2\u907f\u7684\u5730\u65b9\uff0c\u4f46\u89c1\u90a3\u6761\u94c1\u94fe\u5c1a\u81ea\u6302\u5728\u60ac\u5ca9\u4e0b\u9762\uff0c\u5f80\u6765\u6447\u6446\uff0c\u94c1\u94fe\u4e0a\u8840\u8ff9\u6bb7\u7ea2\uff0c\u60f3\u662f\u63d0\u6469\u8fbe\u591a \u7684\u624b\u6307\u88ab\u78e8\u635f\u6240\u81f4\u3002\u5510\u6653\u6f9c\u5fc3\u5934\u98a4\u6817\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u540d\u9707\u4e1c\u6b27\u4e0e\u963f\u523a\u4f2f\u8bf8\u56fd\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u7adf\u662f \u5982\u6b64\u6536\u573a\uff01 \u3000\u3000\u6b64\u65f6\u6b64\u9645\uff0c\u9976\u662f\u5510\u6653\u6f9c\u7edd\u4e16\u6b66\u529f\uff0c\u4ea6\u5df2\u7b4b\u75b2\u529b\u7aed\uff0c\u5bf8\u6b65\u96be\u884c\u3002\u4fef\u9996\u4e0b\u671b\uff0c\u4f46\u89c1\u5ced\u58c1\u51b0 \u5ca9\uff0c\u811a\u4e0b\u4e91\u6c14\u5f25\u6f2b\uff0c\u770b\u6765\u4e0b\u5c71\u4ea6\u5927\u4e0d\u6613\u3002\u5510\u6653\u6f9c\u5367\u5728\u60ac\u5ca9\u4e4b\u4e0a\uff0c\u8c03\u5300\u547c\u5438\uff0c\u8fd0\u6c14\u5fa1\u5bd2\uff0c\u4f46\u89c9 \u547c\u5438\u4ea6\u6781\u8270\u96be\uff0c\u773c\u524d\u4e0d\u505c\u7684\u8ff8\u53d1\u201c\u91d1\u661f\u201d\uff0c\u80f8\u53e3\u75bc\u75db\u80c0\u585e\uff0c\u90a3\u81ea\u662f\u9ad8\u5c71\u7f3a\u6c27\u4e4b\u6545\uff0c\u5e78\u800c\u5510\u6653 \u6f9c\u7684\u5185\u529f\u6df1\u6e5b\uff0c\u5728\u6b66\u6797\u4e2d\u662f\u9876\u513f\u5c16\u513f\u7684\u4eba\u7269\uff0c\u5373\u7b97\u5b8c\u5168\u95ed\u4e86\u547c\u5438\uff0c\u4e5f\u53ef\u52c9\u5f3a\u652f\u6301\u4e00\u65f6\u4e09\u523b\uff0c \u8981\u662f\u6362\u4e86\u7a0d\u5dee\u4e00\u70b9\u7684\uff0c\u5230\u4e86\u8fd9\u4e2a\u9ad8\u5ea6\uff0c\u65e9\u5df2\u7a92\u606f\u800c\u6b7b\uff01 \u3000\u3000\u5510\u6653\u6f9c\u6b47\u4e86\u4e00\u4f1a\uff0c\u6c14\u529b\u7a0d\u7a0d\u6062\u590d\uff0c\u8fd9\u65f6\u98ce\u96ea\u5df2\u6b62\uff0c\u5929\u6717\u6c14\u6e05\uff0c\u7fd8\u9996\u671b\u4e0a\u53bb\uff0c\u73e0\u7a46\u6717\u739b\u5cf0 \u7684\u9876\u5cf0\u4ea6\u6e05\u6670\u53ef\u89c1\uff0c\u7136\u800c\u4ed6\u8fd8\u6ca1\u6709\u4e0a\u5230\u4e00\u534a\uff0c\u4e0a\u9762\u8fd8\u6709\u4e00\u9053\u66f4\u9ad8\u66f4\u9661\u7684\u60ac\u5ca9\u3002\u800c\u4e14\u5728\u957f\u957f\u7684 \u51b0\u96ea\u7684\u659c\u5761\u4e0a\uff0c\u767d\u96ea\u70b9\u7f00\u7740\u72ed\u7a84\u7684\u88c2\u7f1d\uff0c\u5c31\u50cf\u6811\u53f6\u7684\u8109\u7edc\u4e00\u6837\uff0c\u904d\u5e03\u5728\u51b0\u5761\u4e0a\uff0c\u8981\u662f\u5728\u8fd9\u51b0 \u5761\u4e0a\u722c\u884c\uff0c\u7a0d\u4e00\u758f\u795e\uff0c\u5c31\u4f1a\u5815\u4e0b\u88c2\u7f1d\uff0c\u6c38\u57cb\u51b0\u5e95\u3002\u4e0d\u8981\u8bf4\u5510\u6653\u6f9c\u73b0\u5728\u5df2\u662f\u7cbe\u75b2\u529b\u7aed\uff0c\u5373\u7b97\u5728 \u4e00\u5e73\u65f6\uff0c\u8981\u5728\u8fd9\u51b0\u5761\u4e0a\u722c\u884c\uff0c\u4e5f\u662f\u5947\u9669\u4e07\u5206\uff01\u5510\u6653\u6f9c\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u7531\u5f97\u4ed6\u4e0d\u5411\u73e0\u7a46\u6717\u739b\u5cf0\u4f4e \u5934\uff0c\u653e\u5f03\u4e86\u5f81\u670d\u73e0\u5cf0\u7684\u68a6\u60f3\u3002 \u3000\u3000\u5510\u6653\u6f9c\u89e3\u4e0b\u4e86\u63d0\u6469\u8fbe\u591a\u90a3\u6761\u957f\u53ef\u4e08\u8bb8\u7684\u94c1\u94fe\uff0c\u6b63\u5728\u7b79\u601d\u4e0b\u5c71\u4e4b\u6cd5\uff0c\u5ffd\u542c\u5f97\u4e0a\u9762\u9690\u9690\u6709\u4eba \u547c\u5524\u3002\u4ed4\u7ec6\u4e00\u542c\uff0c\u7adf\u50cf\u662f\u53eb\u5524\u4ed6\u7684\u540d\u5b57\uff01 \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u5934\u4e00\u9707\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u745b\u59b9\uff01\u201d\u7cbe\u795e\u9661\u632f\uff0c\u53c8\u5411\u4e0a\u9762\u722c\u884c\u4e86\u5341\u591a\u4e08\uff0c\u62ac \u5934\u4e00\u671b\uff0c\u679c\u7136\u662f\u51af\u745b\u5750\u5728\u4e0a\u9762\uff0c\u4f46\u89c1\u5979\u4e91\u9b13\u677e\u4e71\uff0c\u8863\u88f3\u4e0a\u4e00\u70b9\u70b9\u7684\u8840\u8ff9\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u90a3\u4e5f\u662f \u88ab\u51b0\u96ea\u522e\u635f\u4e86\u8eab\u4f53\u6240\u81f4\u7684\u4e86\u3002\u51af\u73bb\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u662f\u4f60\u5417\uff0c\u5feb\u6765\u6551\u6211\uff01\u201d\u51af\u745b\u7684\u5185\u529f\u5df2 \u5f97\u5929\u5c71\u524d\u8f88\u5251\u5ba2\u6613\u5170\u73e0\u7684\u8863\u94b5\u771f\u4f20\uff0c\u6bd4\u5510\u6653\u6f9c\u8fd8\u7a0d\u80dc\u4e00\u5206\uff0c\u5e73\u65f6\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u767e \u4e08\u4e4b\u5916\uff0c\u4ea6\u53ef\u4e0e\u5510\u6653\u6f9c\u8c08\u8bdd\uff0c\u6709\u5982\u9762\u5bf9\uff0c\u5982\u4ee4\u4e24\u4eba\u7684\u8ddd\u79bb\u4e0d\u8fc7\u5341\u6765\u4e08\uff0c\u58f0\u97f3\u542c\u6765\u5df2\u662f\u5fae\u5f31\u4e4b \u6781\uff0c\u663e\u7136\u4e5f\u5df2\u662f\u7cbe\u75b2\u529b\u7aed\u7684\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u51fa\u5c3d\u5e73\u751f\u6c14\u529b\uff0c\u518d\u5411\u4e0a\u6500\u767b\u6570\u4e08\u3002\u4e24\u4eba\u7684\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\uff0c\u7136\u800c\u5510\u6653\u6f9c\u518d\u4e5f\u65e0\u529b\u5411 \u4e0a\u6500\u767b\u4e86\uff0c\u5ffd\u7684\u8111\u7b4b\u4e00\u52a8\uff0c\u5c06\u90a3\u6761\u94c1\u94fe\u5411\u4e0a\u629b\u51fa\uff0c\u51af\u745b\u4e00\u624b\u6293\u7740\u94c1\u94fe\uff0c\u5c06\u5510\u6653\u6f9c\u62c9\u52a8\u51e0\u6b65\uff0c \u5510\u6653\u6f9c\u4e5f\u7528\u529b\u652f\u6491\u7740\u51b0\u5757\uff0c\u597d\u4e0d\u5bb9\u6613\u7ffb\u4e0a\u60ac\u5ca9\uff0c\u548c\u51af\u745b\u5750\u5728\u4e00\u8d77\uff0c\u6b47\u4e86\u534a\u5929\uff0c\u624d\u8bf4\u5f97\u51fa\u8bdd\u3002 \u3000\u3000\u51af\u745b\u5fae\u7b11\u9053\uff1a\u201c\u548c\u4f60\u5728\u4e00\u8d77\uff0c\u5373\u7b97\u6b7b\u5728\u73e0\u5cf0\uff0c\u4ea6\u53ef\u7791\u76ee\u3002\u201d\u5510\u6653\u6f9c\u60ca\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u600e \u4e48\u5566\uff1f\u662f\u521a\u624d\u7684\u96ea\u5d29\u4f24\u4e86\u4f60\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u8eb2\u5728\u5ca9\u77f3\u7f1d\u4e2d\uff0c\u603b\u7b97\u907f\u8fc7\u4e86\u8fd9\u58f0\u707e \u96be\u3002\u521a\u624d\u6211\u542c\u5f97\u6709\u4eba\u60e8\u53eb\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4f60\u5462\uff01\u6211\u53ea\u88ab\u51b0\u96ea\u522e\u4f24\u4e86\u4e00\u70b9\u76ae\u8089\uff0c\u53ef\u662f\u6211\u7684\u6c14\u529b\u5df2\u7ecf \u5b8c\u5168\u6ca1\u6709\u5566\uff0c\u770b\u6765\u662f\u4e0b\u4e0d\u53bb\u4e86\u3002\u201d\u5510\u6653\u6f9c\u82e6\u7b11\u60f3\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u662f\u5982\u6b64\uff01\u201d\u5176\u5b9e\u4ed6\u56e0\u4e3a\u66fe\u6551 \u6cbb\u91d1\u4e16\u9057\uff0c\u8d39\u4e86\u8bb8\u591a\u7cbe\u795e\u6c14\u529b\uff0c\u722c\u81f3\u6b64\u5904\uff0c\u7cbe\u75b2\u529b\u7aed\u7684\u7a0b\u5ea6\uff0c\u5df2\u662f\u6bd4\u51af\u745b\u66f4\u751a\u4e86\u3002\u4f46\u4e3a\u4e86\u5b89 \u6170\u51af\u6f20\uff0c\u53ea\u597d\u5728\u65e0\u529e\u6cd5\u4e4b\u4e2d\u60f3\u529e\u6cd5\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u82e5\u662f\u5404\u81ea\u4e0b\u5c71\uff0c\u81ea\u662f\u5947\u9669\u4e07\u72b6\uff0c\u4e24\u4eba\u76f8\u4e92 \u6276\u6301\uff0c\u6216\u8bb8\u80fd\u5e73\u5b89\u4e0b\u53bb\u3002\u8fd9\u6761\u94c1\u94fe\u5012\u662f\u53ef\u4ee5\u5927\u6d3e\u7528\u573a\u3002\u201d \u3000\u3000\u4e24\u4eba\u53c8\u6b47\u4e86\u4e00\u4f1a\uff0c\u5403\u4e86\u4e00\u70b9\u5e72\u7cae\uff0c\u8d81\u7740\u5929\u8272\u672a\u665a\uff0c\u6b63\u60f3\u5192\u9669\u4e0b\u5c71\uff0c\u5ffd\u542c\u5f97\u9ad8\u5904\u6709\u4eba\u957f \u5578\uff0c\u5510\u6653\u6f9c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u54a6\uff0c\u662f\u5415\u56db\u5a18\uff01\u201d\u56de\u58f0\u60f3\u5e94\uff0c\u6015\u58f0\u97f3\u4e0d\u80fd\u4f20\u81f3\u9ad8\u5904\uff0c\u53c8\u5c04\u51fa\u4e24\u679d\u5929 \u5c71\u795e\u8292\uff0c\u7834\u7a7a\u76f4\u4e0a\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u4e0a\u9762\u5c71\u5761\u73b0\u51fa\u5415\u56db\u5a18\u7684\u8eab\u5f71\uff0c\u62db\u624b\u53eb\u9053\uff1a\u201c\u5feb\u6765\uff0c\u5feb \u6765\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u51af\u745b\u4e8c\u4eba\u672c\u60f3\u4fdd\u7559\u6c14\u529b\u4f5c\u4e0b\u5c71\u4e4b\u7528\uff0c\u4f46\u542c\u5f97\u5415\u56db\u5a18\u62db\u5524\uff0c\u4ecd\u7136\u6323\u624e\u7740\u5411\u4e0a\u722c\u53bb\uff0c \u4e24\u4eba\u76f8\u4e92\u6276\u6301\uff0c\u624b\u7275\u7740\u624b\uff0c\u4e24\u80a1\u5185\u5bb6\u771f\u529b\u5408\u5728\u4e00\u5904\uff0c\u679c\u7136\u6bd4\u4e00\u4eba\u722c\u5c71\u7701\u529b\u5f97\u591a\uff0c\u7136\u800c\u722c\u5230\u4e0a \u9762\uff0c\u4ea6\u5df2\u624b\u8db3\u9178\u8f6f\uff0c\u56db\u80a2\u65e0\u529b\u3002 \u3000\u3000\u4f46\u89c1\u5415\u56db\u5a18\u4ea6\u662f\u9762\u8272\u60e8\u767d\uff0c\u6c14\u5598\u5401\u5401\uff0c\u663e\u7136\u7cbe\u529b\u5c1a\u672a\u6062\u590d\u3002\u4f46\u5979\u72ec\u81ea\u4e00\u4eba\uff0c\u6bd4\u5510\u6653\u6f9c\u592b \u5987\u8fd8\u6500\u767b\u5f97\u9ad8\uff0c\u5510\u6653\u6f9c\u4ece\u5fc3\u5e95\u4f69\u670d\u3002\u53ea\u89c1\u5415\u56db\u5a18\u5fae\u7b11\u95ee\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60\u7684\u8d4c\u8d5b\u8d62\u4e86\u5417\uff1f\u201d\u539f \u6765\u5415\u56db\u5a18\u5728\u5ce8\u5d4b\u5c71\u91d1\u5149\u5bfa\u9001\u5192\u5ddd\u751f\u4eba\u571f\u4e4b\u540e\uff0c\u4fbf\u5373\u8d76\u6765\u627e\u5510\u6653\u6f9c\uff0c\u8d76\u5230\u559c\u9a6c\u62c9\u96c5\u5c71\u811a\uff0c\u9047\u5230 \u5728\u6e05\u519b\u5927\u8425\u4e2d\u7559\u5b88\u7684\u9648\u5929\u5b87\u7b49\u4eba\uff0c\u624d\u77e5\u9053\u5510\u7ecf\u5929\u7b49\u4f17\u4eba\u90fd\u5df2\u4e0a\u5c71\u627e\u91d1\u4e16\u9057\uff0c\u4e8e\u662f\u5415\u56db\u5a18\u4e5f\u72ec \u81ea\u4e0a\u5c71\uff0c\u5728\u534a\u5c71\u65b9\u4eca\u660e\u5bb6\u4e2d\u4f4f\u4e86\u4e00\u665a\uff0c\u77e5\u6089\u5404\u4e8b\uff0c\u56e0\u800c\u517c\u7a0b\u8ffd\u8d76\uff0c\u5bfb\u89c5\u5510\u6653\u6f9c\u592b\u5987\u7b49\u4eba\u3002 \u3000\u3000\u5415\u56db\u5a18\u7684\u8f7b\u529f\u672c\u9886\u5929\u4e0b\u65e0\u53cc\uff0c\u6cbf\u9014\u53c8\u6ca1\u803d\u6401\uff0c\u6240\u4ee5\u767b\u5c71\u867d\u5728\u5510\u6653\u6f9c\u4e4b\u540e\uff0c\u5374\u6bd4\u5510\u6653\u6f9c\u5148 \u5230\u6b64\u95f4\u3002\u4f46\u5230\u4e86\u8fd9\u4e2a\u9ad8\u5ea6\uff0c\u4ea6\u5df2\u611f\u5230\u547c\u5438\u56f0\u96be\uff0c\u7cbe\u75b2\u529b\u7aed\u7684\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u542c\u5979\u95ee\u8d77\u8d4c\u8d5b\u4e4b\u4e8b\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u8d62\u4e86\uff0c\u4e5f\u8f93\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d \u5510\u6653\u6f9c\u9053\uff1a\u201c\u63d0\u6469\u8fbe\u591a\u8dcc\u6b7b\uff0c\u6211\u548c\u4ed6\u7684\u8d4c\u8d5b\u7b97\u662f\u8d62\u4e86\uff0c\u4f46\u5230\u5e95\u4e0a\u4e0d\u4e86\u73e0\u5cf0\uff0c\u90a3\u8fd8\u662f\u8f93\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5230\u4e86\u6b64\u5904\uff0c\u4f60\u4e5f\u53ef\u4ee5\u5fc3\u8db3\u4e86\u3002\u6211\u5e26\u4f60\u53bb\u770b\u4e00\u4ef6\u7269\u4e8b\u3002\u201d\u4e09\u4eba\u76f8 \u4e92\u6276\u6301\uff0c\u53c8\u722c\u4e86\u597d\u534a\u5929\uff0c\u597d\u5bb9\u6613\u518d\u722c\u4e0a\u4e8c\u4e09\u5341\u4e08\uff0c\u5230\u4e86\u7b2c\u4e8c\u9053\u60ac\u5ca9\u7684\u4e0b\u9762\uff0c\u53ea\u89c1\u51b0\u58c1\u4e00\u5757\u5e73 \u6ed1\u7684\u5927\u77f3\u4e0a\uff0c\u523b\u6709\u201c\u4eba\u5929\u7edd\u754c\u201d\u56db\u4e2a\u5927\u5b57\uff0c\u4e0b\u9762\u8fd8\u6709\u9898\u8bb0\uff0c\u6587\u9053\uff1a \u3000\u3000\u201c\u7532\u7533\u4e4b\u79cb\uff0c\u4f59\u4e09\u8d74\u85cf\u8fb9\uff0c\u6b32\u7a77\u73e0\u5cf0\u4e4b\u9669\uff0c\u81f3\u6b64\u53d7\u963b\uff0c\u529b\u7aed\u7cbe\u75b2\uff0c\u5bf8\u6b65\u96be\u8fdb\uff0c\u51e0\u4e27\u6211 \u751f\uff0c\u55df\u547c\uff0c\u4eca\u59cb\u77e5\u4eba\u529b\u6709\u65f6\u800c\u7a77\uff0c\u5929\u9669\u7edd\u96be\u98de\u5ea6\u4e5f\uff01\u4f59\u867d\u51fa\u5e08\u95e8\u4ee5\u6765\uff0c\u631f\u5251\u6f2b\u6e38\uff0c\u5929\u4e0b\u65e0\u6240 \u6297\u624b\uff0c\u81ea\u4ee5\u4e3a\u4e16\u95f4\u65e0\u8270\u96be\u9669\u963b\u4e4b\u4e8b\uff0c\u5b70\u77e5\u5750\u4e95\u89c2\u5929\uff0c\u4eca\u4e43\u4fef\u9996\u73e0\u5cf0\uff0c\u4e3a\u5cad\u4e0a\u767d\u4e91\u6240\u7b11\u77e3\uff01\u545c \u547c\uff0c\u80dc\u4eba\u6613\uff0c\u80dc\u5929\u96be\uff0c\u6b64\u4e8b\u8bda\u8db3\u4ee4\u5929\u4e0b\u82f1\u96c4\u629a\u5251\u957f\u53f9\u8005\u4e5f\uff01\u201d \u3000\u3000\u6587\u540e\u7684\u7f72\u540d\u662f\u201c\u51cc\u672a\u98ce\u201d\uff0c\u4ed6\u52a9\u6666\u660e\u7985\u5e08\u521b\u7acb\u5929\u5c71\u6d3e\u7684\u6b66\u529f\uff0c\u4e5f\u5373\u662f\u5929\u5c71\u6d3e\u7684\u7b2c\u4e00\u4ee3\u638c \u95e8\uff0c\u5510\u6653\u6f9c\u548c\u51af\u745b\u7684\u5e08\u7956\u3002\u5415\u56db\u5a18\u6307\u7740\u7891\u6587\u7b11\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u5f53\u5e74\u4ea6\u4e0d\u8fc7\u53ea\u5230\u6b64\u5904\uff0c\u4fbf\u5373\u56de \u5934\uff0c\u54b1\u4eec\u73b0\u5728\u4e5f\u5230\u4e86\u6b64\u5904\uff0c\u8fd8\u4e0d\u6ee1\u8db3\u5417\uff1f\u201d\u5510\u6653\u6f9c\u770b\u4e86\u90a3\u201c\u4eba\u5929\u7edd\u754c\u201d\u56db\u5b57\uff0c\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c \u6005\u7136\u53f9\u9053\uff1a\u201c\u51cc\u5e08\u7956\u8bf4\u7684\u4e0d\u9519\uff0c\u518d\u60f3\u4e0a\u53bb\uff0c\u90a3\u771f\u662f\u96be\u4e8e\u767b\u5929\u4e86\u3002\u54b1\u4eec\u90fd\u662f\u8840\u8089\u51e1\u4eba\uff0c\u5230\u4e86\u6b64 \u5904\u4eba\u5929\u4ea4\u754c\u4e4b\u5904\uff0c\u5df2\u662f\u5c3d\u5934\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u6c89\u601d\u6709\u503e\uff0c\u5ffd\u7136\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u662f\u4e0d\u80fd\u518d\u4e0a\u53bb\u4e86\uff0c\u4f46\u51cc\u5927\u4fa0\u6240\u9898\u7684\u2018\u4eba\u5929\u7edd \u754c\u2019\u56db\u5b57\uff0c\u8fd9\u6d3b\u4e5f\u6015\u8bf4\u5f97\u592a\u6ee1\uff0c\u7109\u77e5\u540e\u8005\u4e4b\u4e0d\u5982\u4eca\uff1f\u201d\u5510\u6653\u6f9c\u6709\u70b9\u4e0d\u670d\uff0c\u9053\uff1a\u201c\u4ee5\u51cc\u5e08\u7956\u90a3 \u6837\u7684\u7edd\u4e16\u6b66\u529f\uff0c\u8fd8\u6709\u8c01\u80fd\u8d76\u5f97\u4e0a\u4ed6\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u5438\u4e86\u53e3\u6c14\uff0c\u5de6\u624b\u62c9\u7740\u5510\u6653\u6f9c\uff0c\u53f3\u624b\u62c9\u7740\u51af\u745b\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u518d\u524d\u884c\u4e09\u6b65\uff01\u201d\u5510\u3001 \u51af\u4e8c\u4eba\u4e0d\u660e\u5176\u610f\uff0c\u4f46\u4ed6\u4eec\u4e00\u5411\u90fd\u628a\u5415\u56db\u5a18\u5f53\u6210\u5927\u59d0\u59d0\u4e00\u6837\u5c0a\u656c\uff0c\u4f9d\u8a00\u5411\u524d\u8e0f\u51fa\u4e09\u6b65\uff0c\u8fd9\u4e09\u6b65 \u5728\u60ac\u5ca9\u5ced\u58c1\u4e0a\u8e0f\u8fdb\uff0c\u7aef\u7684\u96be\u5982\u767b\u5929\uff0c\u8981\u4e0d\u662f\u5404\u4ee5\u7edd\u9876\u7684\u5185\u529f\u76f8\u4e92\u6276\u6301\uff0c\u51b3\u8ba1\u79fb\u4e0d\u52a8\u811a\u6b65\u3002\u5415 \u56db\u5a18\u5636\u58f0\u4e00\u7b11\uff0c\u62c9\u7740\u4e24\u4eba\u8df3\u4e86\u4e0b\u6765\uff0c\u5728\u60ac\u5ca9\u4e0a\u6b47\u4e86\u4e00\u4f1a\uff0c\u5598\u6c14\u8bf4\u9053\uff1a\u201c\u540e\u4eba\u5fc5\u80dc\u524d\u4eba\uff0c\u8fd9\u662f \u4eca\u53e4\u4e0d\u6613\u4e4b\u7406\u3002\u54b1\u4eec\u4eca\u5929\u4e0d\u5c31\u662f\u6bd4\u51cc\u5927\u4fa0\u591a\u8d70\u4e86\u4e09\u6b65\u5417\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u5934\u4e00\u52a8\uff0c\u4f46\u89c9\u5415\u56db\u5a18\u4e4b\u8a00\u5927\u6709\u54f2\u7406\uff0c\u4f46\u4ef0\u671b\u73e0\u5cf0\uff0c\u4e91\u6c14\u5f25\u6f2b\uff0c\u4e0d\u77e5\u8fd8\u8981\u51e0\u5343\u51e0 \u4e07\u4e2a\u201c\u4e09\u6b65\u201d\u624d\u80fd\u8e0f\u4e0a\u5cf0\u9876\uff0c\u53c8\u4e0d\u7981\u9eef\u7136\u795e\u4f24\u3002\u53ef\u60dc\u90a3\u65f6\u5019\u8fd8\u6ca1\u6709\u767b\u5c71\u7684\u6d4b\u91cf\u4eea\u5668\uff0c\u8981\u4e0d\u7136 \u4ed6\u4eec\u5f53\u53ef\u53d1\u73b0\uff0c\u4ed6\u4eec\u5df2\u5728\u516b\u5343\u4e8c\u767e\u4e94\u5341\u7c73\u7684\u9ad8\u5904\uff0c\u65e9\u5df2\u8d85\u8fc7\u4e86\u8fd1\u4ee3\u6b27\u6d32\u722c\u5c71\u5bb6\u6240\u8bf4\u7684\u201c\u767b\u5c71 \u6781\u9650\u201d\uff0c\u5927\u8db3\u81ea\u8c6a\u4e86\uff01 \u3000\u3000\u6b47\u4e86\u4e00\u4f1a\uff0c\u51af\u73bb\u95ee\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u4f60\u4e0a\u6765\u7684\u65f6\u5019\uff0c\u53ef\u6709\u89c1\u5230\u7ecf\u5929\u4e48\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u7ecf \u5929\u548c\u4f60\u4eec\u7684\u672a\u6765\u513f\u5ab3\u90fd\u5df2\u4e0a\u5c71\u6765\u4e86\u3002\u542c\u8bf4\u4e5f\u662f\u4e3a\u4e86\u627e\u91d1\u4e16\u9057\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e48\u4ed6\u4eec \u4e5f\u8bb8\u5728\u73e0\u5cf0\u4e0b\u9762\u89c1\u7740\u4e86\u3002\u201d\u5510\u6653\u6f9c\u5c06\u5728\u73e0\u5cf0\u811a\u4e0b\u6551\u6cbb\u91d1\u4e16\u9057\u7684\u4e8b\u544a\u8bc9\u4e86\u5415\u56db\u5a18\uff0c\u5415\u56db\u5a18\u9053\uff1a \u201c\u6bd2\u9f99\u5c0a\u8005\u6709\u4e86\u8863\u94b5\u4f20\u4eba\uff0c\u6211\u4e5f\u653e\u4e0b\u4e00\u91cd\u5fc3\u4e8b\u4e86\u3002\u8d81\u7740\u5929\u8272\u8fd8\u65e9\uff0c\u54b1\u4eec\u4e5f\u8be5\u4e0b\u53bb\u5566\u3002\u201d\u51af\u745b \u9053\uff1a\u201c\u5e78\u800c\u78b0\u5230\u5415\u59d0\u59d0\uff0c\u8981\u4e0d\u7136\u771f\u4e0d\u77e5\u9053\u600e\u4e48\u4e0b\u5c71\u5462\uff01\u201d\u4e09\u4eba\u7275\u7740\u94c1\u94fe\uff0c\u4e92\u76f8\u7167\u987e\uff0c\u6ed1\u4e0b\u51b0 \u5761\uff0c\u867d\u7136\u9669\u72b6\u767e\u51fa\uff0c\u5230\u5e95\u6bd4\u4e0a\u5c71\u4e4b\u65f6\u7701\u529b\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u4eec\u4ee5\u4e3a\u4e00\u4e0b\u73e0\u5cf0\uff0c\u5c31\u53ef\u4ee5\u89c1\u5230\u91d1\u4e16\u9057\uff0c\u8c01\u77e5\u53c8\u6709\u4e86\u610f\u60f3\u4e0d\u5230\u7684\u53d8\u5316\u3002 \u3000\u3000\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\uff0c\u5728\u5c3c\u6cca\u5c14\u738b\u7684\u7b75\u5e2d\u6563\u4e86\u4e4b\u540e\uff0c\u5c31\u8fde\u591c\u4e0a\u5c71\u3002\u5c3c\u6cca\u5c14\u738b\u5df2\u7b54\u5e94\u5728\u51e1\u65e5 \u4e4b\u5185\u4fbf\u64a4\u5175\uff0c\u4ed6\u4eec\u51e0\u6708\u6765\u6240\u62c5\u5fc3\u7684\u4e8b\u60c5\uff0c\u7ec8\u4e8e\u5f97\u5230\u4e86\u5706\u6ee1\u7684\u89e3\u51b3\uff0c\u5fc3\u60c5\u81ea\u662f\u6109\u5feb\u4e4b\u6781\uff0c\u4f46\u60ac \u5ff5\u91d1\u4e16\u9057\u7684\u547d\u8fd0\uff0c\u5374\u53c8\u4e0d\u514d\u8499\u4e0a\u4e00\u5c42\u9634\u5f71\u3002\u4ed6\u4eec\u4e5f\u6709\u542c\u5230\u91d1\u4e16\u9057\u7684\u5578\u58f0\uff0c\u5374\u56e0\u6240\u8d70\u7684\u9053\u8def\u4e0d \u5bf9\uff0c\u65e2\u6ca1\u6709\u7ecf\u8fc7\u65b9\u4eca\u660e\u7684\u5bb6\u56ed\uff1a\u4e5f\u6ca1\u6709\u53d1\u73b0\u91d1\u4e16\u9057\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u8d70\u4e86\u4e09\u65e5\uff0c\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u51b0\u5ddd\u5929\u5973\u957f\u4f4f\u51b0\u5bab\uff0c\u8fd8\u6ca1\u611f\u89c9\u4ec0\u4e48\uff0c\u5510\u7ecf\u5929\u5219\u6e10\u6e10\u611f\u5230\u547c\u5438\u6709\u4e9b \u4e0d\u7545\uff0c\u4f46\u4ed6\u4ecd\u662f\u7ed9\u773c\u524d\u58ee\u4e3d\u7684\u666f\u8272\u6240\u5438\u5f15\u4f4f\u4e86\u3002\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u51b0\u5ddd\u6bd4\u4e4b\u51b0\u5ddd\u5929\u5973\u6240\u4f4f\u7684\u5ff5\u9752 \u5510\u53e4\u62c9\u5c71\uff0c\u4e0d\u77e5\u9ad8\u51fa\u591a\u5c11\u500d\uff01\u4f46\u89c1\u5929\u84dd\u8272\u7684\u51b0\u5ddd\uff0c\u50cf\u5f69\u7f0e\u4e00\u6837\uff0c\u4ece\u5cf0\u9876\u5411\u56db\u9762\u516b\u65b9\u6492\u4e0b\u6765\uff0c \u9576\u5d4c\u5728\u6d01\u767d\u7684\u5c71\u5761\u4e0a\uff0c\u663e\u5f97\u5206\u5916\u7684\u6676\u83b9\u707f\u70c2\uff0c\u51b0\u5ddd\u5929\u5973\u5567\u5567\u79f0\u8d4f\uff0c\u597d\u50cf\u6e38\u5b50\u770b\u5230\u4e86\u4e0e\u6545\u4e61\u76f8 \u4f3c\u7684\u666f\u7269\u4e00\u6837\uff0c\u65f6\u4e0d\u65f6\u505c\u4e0b\u6b65\u6765\uff0c\u9a7b\u8db3\u800c\u89c2\u3002\u5510\u7ecf\u5929\u548c\u5979\u76f8\u5904\u4ee5\u6765\uff0c\u8fd8\u5f88\u5c11\u89c1\u5230\u5979\u6709\u8fd9\u6837\u7684 \u5174\u81f4\uff0c\u4f46\u89c9\u51b0\u96ea\u4e16\u754c\uff0c\u90fd\u5316\u6210\u4e86\u65d6\u65ce\u98ce\u5149\uff01\u5510\u7ecf\u5927\u56de\u60f3\u8d77\u4e09\u4e0a\u51b0\u5cf0\uff0c\u9080\u8bf7\u5979\u4e0b\u5c71\u7684\u5f80\u4e8b\uff0c\u56de \u60f3\u8d77\u4e07\u91cc\u8ffd\u8e2a\uff0c\u597d\u4e8b\u591a\u78e8\u7684\u7ecf\u8fc7\uff0c\u800c\u4eca\u8fd9\u4e00\u5207\u5168\u90fd\u8fc7\u53bb\u4e86\uff0c\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u7684\u9669\u963b\u867d\u591a\uff0c\u4f46\u4ed6 \u4eec\u594e\u60c5\u7684\u9053\u8def\u4e0a\u5df2\u6ca1\u6709\u9669\u963b\u4e86\u3002\u5510\u7ecf\u5929\u5fc3\u4e2d\u751c\u4e1d\u4e1d\u7684\uff0c\u867d\u7136\u4ed6\u4e0d\u5927\u4e60\u60ef\u9ad8\u5c71\u7684\u6c14\u5019\uff0c\u4f46\u6709\u51b0 \u5ddd\u5929\u5973\u5728\u65c1\uff0c\u5374\u662f\u7cbe\u795e\u7115\u53d1\uff0c\u6bd4\u8d77\u91d1\u4e16\u9057\u4e0a\u5c71\u4e4b\u65f6\u7684\u90a3\u79cd\u51c4\u82e6\u5fc3\u60c5\uff0c\u90a3\u81ea\u662f\u4eba\u6e0a\u4e4b\u522b\u4e86\u3002 \u3000\u3000\u518d\u8d70\u4e86\u4e24\u5929\uff0c\u8fdc\u8fdc\u7684\u770b\u5230\u51b0\u5854\u7fa4\uff0c\u5b9d\u5854\u6d41\u8f89\uff0c\u51b0\u5149\u6620\u65e5\uff0c\u7aef\u7684\u4f3c\u51b0\u5cf0\u4e0a\u7a81\u7136\u6d8c\u73b0\u7684\u84ec\u83b1 \u4ed9\u5883\uff0c\u51b0\u5ddd\u5929\u5973\u559c\u6781\u800c\u547c\uff0c\u8fd9\u65f6\uff0c\u56e0\u4e3a\u9ad8\u5c71\u7f3a\u6c27\u7684\u539f\u6545\uff0c\u5979\u672c\u6765\u4e5f\u611f\u5230\u547c\u5438\u6709\u4e9b\u56f0\u96be\u4e86\uff0c\u4f46 \u89c1\u6b64\u4eba\u95f4\u4ed9\u5883\uff0c\u4ecd\u7981\u4e0d\u4f4f\u98de\u5954\u8fc7\u53bb\uff0c\u53ea\u53ef\u601c\u5510\u7ecf\u5929\u7528\u5c3d\u6c14\u529b\uff0c\u90fd\u8ddf\u4e0d\u4e0a\u5979\u3002 \u3000\u3000\u9762\u524d\u4e00\u9053\u51b0\u5ddd\u963b\u6b62\u53bb\u8def\uff0c\u604d\u60da\u542c\u5230\u5e95\u4e0b\u6d41\u51b0\u7684\u5636\u54cd\uff0c\u51b0\u5ddd\u4e0a\u6709\u4e00\u4e2a\u5de8\u5927\u7684\u51b0\u5757\uff0c\u72b6\u4f3c\u8611 \u83c7\uff0c\u51b0\u5ddd\u5929\u5973\u521a\u60f3\u7ed5\u8fc7\u8fd9\u9053\u51b0\u5ddd\uff0c\u5ffd\u542c\u5f97\u51b0\u8611\u83c7\u80cc\u540e\uff0c\u6709\u4eba\u4f4e\u58f0\u54ed\u6ce3\uff0c\u751a\u662f\u51c4\u51c9\uff0c\u51b0\u5ddd\u5929\u5973 \u5fc3\u5934\u4e00\u9707\uff0c\u62db\u624b\u7b49\u5510\u7ecf\u5929\u8fc7\u6765\uff0c\u4e24\u4eba\u7ed5\u8fc7\u51b0\u5ddd\u4e00\u770b\uff0c\u53ea\u89c1\u51b0\u8611\u83c7\u80cc\u4e0a\uff0c\u6709\u4eba\u5750\u5728\u6c99\u5ddd\u7684\u65c1 \u8fb9\uff0c\u62b1\u7740\u4e00\u6761\u9ed1\u6f06\u53d1\u5149\u7684\u4eba\u81c2\u3002 \u3000\u3000\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u662f\u9ec4\u77f3\u9053\u4eba\uff01\u201d\u4ed6\u62b1\u7684\u5374\u662f\u8463\u592a\u6e05\u7684\u90a3\u6761\u94c1\u81c2\u3002\u53ea\u89c1\u4ed6\u9762\u4e0a\u4e00\u6761 \u6761\u7684\u8840\u75d5\uff0c\u6c81\u51fa\u7684\u8840\u4e1d\u90fd\u5df2\u51dd\u7ed3\u6210\u51b0\uff0c\u5f62\u72b6\u5341\u5206\u53ef\u6015\uff0c\u4e00\u89c1\u51b0\u5ddd\u5929\u5973\u5230\u6765\uff0c\u5ffd\u5730\u6325\u52a8\u90a3\u6761\u94c1 \u81c2\uff0c\u5939\u5934\u5939\u8111\u7684\u6253\u6765\uff0c\u5927\u53eb\u5927\u56b7\u9053\uff1a\u201c\u662f\u4f60\u5bb3\u6b7b\u4e86\u4ed6\uff0c\u662f\u4f60\u5bb3\u6b7b\u4e86\u4ed6\uff01\u201d\u51b0\u5ddd\u5929\u5973\u5947\u9053\uff1a \u201c\u6211\u5bb3\u4e86\u8c01\u4e86\uff1f\u201d\u968f\u624b\u7528\u51b0\u9b44\u5bd2\u5149\u5251\u4e00\u62e8\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u9ec4\u77f3\u9053\u4eba\u7684\u9053\u888d\u5272\u88c2\u6570\u5bf8\uff0c\u9ec4 \u77f3\u9053\u4eba\u53cc\u773c\u4e00\u77aa\uff0c\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u5c06\u94c1\u81c2\u629b\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u662f\u6211\u5bb3\u6b7b\u4e86\u4ed6\uff0c\u662f\u6211\u5bb3\u6b7b\u4e86\u4ed6\uff01\u201d \u72b6\u82e5\u75af\u72c2\u3002\u51b0\u5ddd\u5929\u5973\u6709\u70b9\u5bb3\u6015\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u4f46\u89c1\u9ec4\u77f3\u9053\u4eba\u4e00\u58f0\u5389\u53eb\uff0c\u4ec6\u5012\u5730\u4e0a\uff0c\u9c9c\u9c7c\u6d8c\u51fa\uff0c \u67d3\u7ea2\u8863\u88f3\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u53c8\u5df2\u51dd\u7ed3\u6210\u51b0\u3002 \u3000\u3000\u51b0\u5ddd\u5927\u5973\u90a3\u4e00\u5251\u6839\u672c\u6ca1\u6709\u89e6\u53ca\u4ed6\u7684\u8eab\u4f53\uff0c\u7a81\u7136\u89c1\u4ed6\u6d41\u8840\u6655\u5012\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u4e0a\u524d\u5bdf\u770b\uff0c\u539f \u6765\u662f\u4ed6\u53d7\u4e0d\u4e86\u5c71\u4e0a\u7684\u4e25\u5bd2\uff0c\u52a0\u4ee5\u9ad8\u5c71\u4e0a\u547c\u5438\u56f0\u96be\uff0c\u529f\u529b\u65e9\u5df2\u5927\u51cf\uff0c\u51b0\u5ddd\u5927\u5973\u7684\u51b0\u5251\u53c8\u662f\u5947\u51b7 \u65e0\u6bd4\uff0c\u5185\u5916\u4e24\u80a1\u5bd2\u6c14\u5939\u653b\uff0c\u4ee5\u81f3\u8840\u7ba1\u7206\u88c2\u3002\u8981\u4e0d\u7136\u82e5\u662f\u5728\u5e73\u5730\u4e4b\u4e0a\uff0c\u51b0\u5ddd\u5929\u5973\u8fd8\u4e0d\u662f\u4ed6\u7684\u654c \u624b\uff0c\u8fd9\u4e00\u5251\u7edd\u4e0d\u80fd\u53eb\u4ed6\u53d7\u4f24\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u5b58\u607b\u9690\uff0c\u638f\u51fa\u4e13\u89e3\u5bd2\u6c14\u7684\u9633\u548c\u4e38\u7ed9\u4ed6\u670d\u4e0b\uff0c\u8fd9\u662f\u51b0\u5bab\u4e2d\u7edd\u5999\u7684\u7075\u4e39\uff0c\u5373\u7b97\u53d7 \u4e86\u51b0\u9b44\u795e\u5f39\u7684\u5947\u5bd2\u4e4b\u6c14\u4ea6\u53ef\u89e3\u6551\u3002\u9ec4\u77f3\u9053\u4eba\u670d\u540e\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u679c\u7136\u82cf\u9192\u3002\u5510\u7ecf\u5929\u7ed9\u4ed6\u63a8\u8840\u8fc7 \u5bab\uff0c\u518d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u9ec4\u77f3\u9053\u4eba\u795e\u667a\u6e10\u6e10\u6062\u590d\u6b63\u5e38\uff0c\u773c\u5149\u4e2d\u6d41\u9732\u51fa\u611f\u6fc0\u7684\u795e\u6c14\uff0c\u5ffd\u7136\u53c8\u54fa\u54fa\u8bf4 \u9053\uff1a\u201c\u662f\u6211\u5bb3\u6b7b\u4e86\u4ed6\u4eec\uff0c\u662f\u6211\u5bb3\u6b7b\u4e86\u4ed6\u4eec\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u4f60\u5bb3\u4e86\u8c01\u4e86\uff1f\u201d\u9ec4\u77f3\u9053\u4eba\u5ffd\u53c8\u53eb\u9053\uff1a\u201c\u6ca1\u6709\u7edb\u73e0\u4ed9\u8349\uff0c\u6ca1\u6709\u7edb\u73e0\u4ed9\u8349\uff0c\u4f60 \u4eec\u8d76\u5feb\u4e0b\u53bb\u5427\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u7ee6\u73e0\u4ed9\u8349\uff1f\u201d\u9ec4\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u60f3\u4e0a\u73e0\u7a46\u6717\u739b \u5cf0\u5bfb\u89c5\u7ee6\u73e0\u4ed9\u8349\u7684\u5417\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fde\u8fd9\u540d\u5b57\u6211\u90fd\u6ca1\u6709\u542c\u8fc7\u3002\u201d\u9ec4\u77f3\u9053\u4eba\u5401 \u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5440\uff0c\u90a3\u5c31\u53ea\u662f\u6211\u5bb3\u4e86\u8d64\u795e\u5b50\u548c\u8463\u592a\u6e05\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9ec4\u77f3\u9053 \u4eba\u4e00\u6307\u90a3\u6761\u94c1\u81c2\uff0c\u53c8\u53d6\u51fa\u4e00\u7f15\u9ec4\u8910\u8272\u7684\u4e71\u8349\u822c\u7684\u957f\u53d1\uff0c\u90a3\u662f\u8d64\u795e\u5b50\u7684\u5934\u53d1\u3002\u9ec4\u77f3\u9053\u4eba\u53f9\u4e86\u53e3 \u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5df2\u57cb\u5230\u51b0\u5ddd\u5e95\u4e0b\u53bb\u4e86\u3002\u6211\u53ea\u5728\u51b0\u88c2\u7f1d\u4e2d\u6293\u8d77\u8fd9\u6761\u94c1\u81c2\u548c\u626f\u65ad\u8fd9\u7f15\u5934\u53d1\uff0c \u8fde\u4ed6\u4eec\u7684\u5c38\u8eab\u4e5f\u627e\u4e0d\u51fa\u6765\uff0c\u51b0\u7f1d\u4fbf\u91cd\u5408\u4e86\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u9ec4\u77f3\u9053\u4eba\u9053\uff1a\u201c\u8d64\u795e\u5b50\u4e2d\u4e86\u4f60\u7684\u4e03\u679a\u51b0\u9b44\u795e\u5f39\u540e\uff0c\u5143 \u6c14\u5927\u4f24\uff0c\u4ed6\u4e00\u5fc3\u60f3\u6062\u590d\u6b66\u529f\uff0c\u5df2\u5230\u75f4\u8ff7\u7684\u7a0b\u5ea6\uff0c\u4ed6\u4e00\u751f\u53ea\u4ea4\u6211\u8fd9\u4e2a\u670b\u53cb\uff0c\u6211\u4e0d\u5fcd\u8ba9\u4ed6\u90c1\u90c1\u800c \u6b7b\uff0c\u4e3a\u4e86\u89e3\u5f00\u4ed6\u5fc3\u5934\u7684\u6b7b\u7ed3\uff0c\u4e8e\u662f\u9a97\u4ed6\u8bf4\uff0c\u73e0\u5cf0\u4e0a\u6709\u4e00\u79cd\u7edb\u73e0\u4ed9\u8349\uff0c\u670d\u4e0b\u4e00\u682a\uff0c\u53ef\u4ee5\u5f53\u5f97\u4e09 \u5341\u5e74\u529f\u529b\uff0c\u6211\u53ea\u662f\u60f3\u8ba9\u4ed6\u5fc3\u5934\u6709\u4e00\u4e2a\u5e0c\u671b\uff0c\u6216\u8005\u5373\u7b97\u4e0a\u5c71\uff0c\u4e5f\u4f1a\u77e5\u96be\u800c\u9000\uff0c\u90a3\u65f6\u5c31\u606f\u4e86\u5fc3 \u4e86\u3002\u5c82\u77e5\u4ed6\u548c\u8463\u592a\u6e05\u7adf\u7136\u5192\u9669\u6765\u5230\u6b64\u5904\uff0c\u8fd9\u4e0d\u662f\u6211\u5bb3\u4e86\u4ed6\u4eec\u5417\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u4e2d\u4fa7\u7136\uff0c\u60f3\u9053\uff1a\u201c\u8d64\u795e\u5b50\u65e0\u6076\u4e0d\u4f5c\uff0c\u6b7b\u4e0d\u8db3\u60dc\u3002\u4f46\u8fd9\u9ec4\u77f3\u9053\u4eba\u7b03\u4e8e\u53cb\u60c5\uff0c\u867d \u8bf4\u662f\u975e\u4e0d\u5206\uff0c\u5012\u8fd8\u503c\u5f97\u540c\u60c5\u3002\u539f\u6765\u4ed6\u521a\u624d\u662f\u56e0\u4e3a\u597d\u53cb\u4e4b\u6b7b\uff0c\u4ee5\u81f3\u795e\u667a\u8ff7\u4e71\u3002\u201d\u4fbf\u9053\uff1a\u201c\u65e2\u7136 \u5982\u6b64\uff0c\u4f60\u8d76\u5feb\u4e0b\u5c71\u53bb\u5427\u3002\u4f60\u670d\u4e86\u6211\u7684\u9633\u548c\u4e38\uff0c\u4e0d\u754f\u5bd2\u6c14\u6240\u4fb5\uff0c\u4e0b\u5c71\u6599\u53ef\u65e0\u59a8\u3002\u201d \u3000\u3000\u9ec4\u77f3\u9053\u4eba\u62fe\u8d77\u90a3\u6761\u94c1\u81c2\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u4eec\u6240\u8981\u5bfb\u89c5\u7684\u4e1c\u897f\u6bd4\u7edb\u73e0\u4ed9 \u8349\u8fd8\u8981\u73cd\u8d35\u3002\u201d\u9ec4\u77f3\u9053\u4eba\u6447\u4e86\u6447\u5934\uff0c\u89c1\u51b0\u5ddd\u5929\u5973\u610f\u5fd7\u575a\u51b3\uff0c\u53ea\u597d\u72ec\u81ea\u4e0b\u5c71\u800c\u53bb\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u6709\u70b9\u6005\u60c6\uff0c\u4f46\u51b0\u5854\u7fa4\u5947\u4e3d\u65e0\u4f8d\u7684\u666f\u8272\u5c06\u5979\u5438\u5f15\u4f4f\u4e86\uff0c\u5979\u548c\u5510\u7ecf\u5929\u8f7b\u8f7b\u643a\u624b \u524d\u884c\uff0c\u7a7f\u5165\u51b0\u5854\u7fa4\u4e2d\uff0c\u4f46\u89c1\u51b0\u5149\u5854\u5f71\uff0c\u4e92\u76f8\u8f89\u6620\uff0c\u5343\u95e8\u4e07\u6237\uff0c\u5bd2\u6c14\u68ee\u68ee\uff0c\u51b0\u5ddd\u5929\u5973\u6b22\u559c\u8d5e \u53f9\uff0c\u7b11\u9053\uff1a\u201c\u7b80\u76f4\u6bd4\u6211\u7684\u51b0\u5bab\u8fd8\u8981\u80dc\u8fc7\u4e07\u5206\u3002\u201d\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u51b0\u5bab\u6709\u4f60\u8fd9\u6837\u4e00\u4f4d\u4ed9\u5973\uff0c\u8fd9 \u91cc\u867d\u7136\u5947\u4e3d\uff0c\u5374\u6beb\u65e0\u4e00\u70b9\u751f\u6c14\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u4f60\u7109\u77e5\u8fd9\u91cc\u4e0d\u662f\u5973\u795e\u6240\u5c45\uff1f\u55ef\uff0c\u4f60\u53ef\u77e5\u9053\u73e0\u7a46\u6717\u739b\u8fd9\u51e0\u4e2a\u5b57\u7684\u610f\u601d \u5417\uff1f\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u6b63\u8981\u8bf7\u6559\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5b83\u662f\u5973\u795e\u7684\u540d\u5b57\uff0c\u85cf\u4eba\u79f0\u73e0\u7a46\u6717\u739b\u4e3a\u2018\u5723 \u6bcd\u4e4b\u5730\u2019\uff0c\u6709\u7684\u79f0\u4f5c\u2018\u7b2c\u4e09\u5723\u6bcd\u2019\uff0c\u5728\u897f\u85cf\u548c\u5c3c\u6cca\u5c14\uff0c\u6d41\u4f20\u7740\u4e00\u4e2a\u975e\u5e38\u7f8e\u4e3d\u7684\u4f20\u8bf4\u3002 \u3000\u3000\u201c\u636e\u8bf4\u73e0\u7a46\u6717\u739b\u662f\u4e00\u4f4d\u8170\u8eab\u7ea4\u7ec6\u3001\u56db\u80a2\u4fee\u957f\u7684\u5973\u795e\uff0c\u5979\u7684\u76f8\u8c8c\u633a\u79c0\uff0c\u6027\u683c\u6e29\u67d4\u3002\u767b\u4e34\u5cf0 \u5dc5\uff0c\u80fd\u770b\u5230\u5168\u4e16\u754c\u7684\u666f\u8272\u3002\u4eba\u4eec\u770b\u5230\u5979\u7684\u5bb9\u8c8c\uff0c\u6ca1\u6709\u4e0d\u611f\u5230\u7fa1\u6155\u548c\u666f\u4ef0\u7684\u3002\u548c\u5979\u540c\u4f4f\u7684\u6709\u5927 \u59d0\u73e0\u7a46\u7b56\u4ec1\u739b\u3001\u4e8c\u59d0\u73e0\u7a46\u4e01\u7ed3\u6c99\u6851\u739b\uff0c\u5979\u662f\u4e09\u59d0\u73e0\u7a46\u6717\u739b\uff0c\u8fd8\u6709\u56da\u59b9\u7a46\u89c9\u672c\u73e0\u6851\u739b\u3001\u4e94\u59b9\u7a46 \u5fb7\u683c\u65e5\u5353\u6851\u739b\uff0c\u5408\u79f0\u73e0\u7a46\u89c9\u5cb8\uff08\u73e0\u7a46\u4e94\u59ca\u59b9\uff09\u4e00\u5bb6\u3002\u8fd9\u4e16\u754c\u7b2c\u4e00\u5cf0\u672c\u6765\u662f\u4e09\u59d0\u73e0\u7a46\u6717\u739b\u4f4f \u7684\uff0c\u540e\u6765\u5176\u4ed6\u56db\u59d0\u59b9\u56e0\u611f\u5230\u4e16\u754c\u4e0a\u7684\u4eba\u6ca1\u6709\u6bd4\u73e0\u7a46\u6717\u739b\u518d\u6e29\u67d4\u53ef\u7231\u7684\u4e86\uff0c\u4e5f\u6ca1\u6709\u5730\u65b9\u6bd4\u5979\u6240 \u5c45\u4f4f\u7684\u4ed9\u5cf0\u518d\u7f8e\u597d\u7684\u4e86\uff0c\u6240\u4ee5\u90fd\u4ece\u5404\u5730\u8fc1\u6765\uff0c\u73af\u7ed5\u73e0\u7a46\u6717\u739b\u800c\u5c45\u4f4f\u3002\u4f60\u77a7\uff0c\u90a3\u5c31\u662f\u73af\u62f1\u7740\u73e0 \u7a46\u6717\u739b\u90a3\u56db\u5ea7\u5c71\u5cf0\u4e86\u3002\u5979\u4eec\u5728\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u4fee\u5efa\u5bab\u6bbf\u3001\u6e56\u6cca\u548c\u4ead\u53f0\uff0c\u4f3a\u517b\u7740\u91d1\u8272\u7684\u9e33\u9e2f\u548c\u767d \u8272\u7684\u72ee\u5b50\uff0c\u4f7f\u8fd9\u5ea7\u9ad8\u5cf0\u6210\u4e3a\u4e16\u754c\u4e0a\u6700\u7f8e\u597d\u3001\u6700\u5e78\u798f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u795e\u8bdd\u4ece\u51b0\u5ddd\u5929\u5973\u7684\u53e3\u4e2d\u8bf4\u51fa\u6765\uff0c\u542c\u5f97\u5510\u7ecf\u5929\u5982\u9189\u5982\u75f4\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60 \u5c31\u662f\u73e0\u7a46\u6717\u739b\uff0c\u4e16\u754c\u4e0a\u518d\u6ca1\u6709\u4eba\u6bd4\u4f60\u66f4\u6e29\u67d4\u53ef\u7231\u7684\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u55c5\u9053\uff0c\u201c\u4f60\u51e0\u65f6\u5b66\u5f97\u8fd9\u6837 \u6cb9\u5634\u6ed1\u820c\uff1f\u54b1\u4eec\u8fde\u73e0\u7a46\u6717\u739b\u5cf0\u90fd\u4e0a\u4e0d\u4e86\u5462\u3002\u201d\u5510\u7ecf\u5929\u5b66\u7740\u51b0\u5ddd\u5929\u5973\u7684\u8bed\u8c03\u8bf4\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u4f4f \u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u90a3\u5c31\u662f\u4e16\u754c\u4e0a\u6700\u7f8e\u597d\u3001\u6700\u5e78\u798f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8f7b\u8f7b\u7684\u6253\u4e86\u4ed6\u4e00\u4e0b\uff0c\u5510\u7ecf\u5929\u6028\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u91cc\u6562\u60c5\u771f\u6709\u5973\u795e\uff1f\u4f60\u542c\uff01\u201d\u53ea\u542c\u5f97 \u51b0\u5854\u7fa4\u4e2d\u679c\u7136\u6709\u4eba\u7684\u58f0\u606f\uff0c\u542c\u6e05\u695a\u4e86\uff0c\u7adf\u7136\u53c8\u662f\u4f4e\u4f4e\u7684\u555c\u6ce3\u4e4b\u58f0\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4eba\u95f4\u51e0\u8bb8\u4f24\u5fc3\u4e8b\uff0c\u72ec\u4e0a\u73e0\u5cf0\u628a\u6cea\u5f39\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u2014\u2014\u7b2c\u56db\u5341\u56de\u3000\u5929\u5973\u6563\u82b1\u3000\u73e0\u5cf0\u52b3\u6005\u671b\u3000\u51b0\u5ddd\u6620\u6708\u3000\u4e91\u6d77\u5bc4\u9050\u601d \u6881\u7fbd\u751f\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b \u7b2c\u56db\u5341\u56de \u5929\u5973\u6563\u82b1\u3000\u73e0\u5cf0\u52b3\u6005\u671b \u51b0\u5ddd\u6620\u6708\u3000\u4e91\u6d77\u5bc4\u9050\u601d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u5973\u795e\u662f\u4e0d\u4f1a\u54ed\u6ce3\u7684\u3002\u201d\u5510\u7ecf\u5929\u773c\u775b\u4e00\u4eae\uff0c\u9053\u3002\u201c\u8fd9\u54ed\u58f0\u597d\u719f\u6089\uff01\u201d\u671d \u7740\u58f0\u97f3\u7684\u65b9\u5411\u8dd1\u53bb\uff0c\u5ffd\u7136\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6c81\u6885\u8868\u59b9\uff01\u201d\u53ea\u89c1\u51b0\u5854\u7fa4\u4e2d\u4e00\u4e2a\u5c0f\u6e56\u4e4b\u6ee8\uff0c\u674e\u6c81\u6885\u6b63 \u5728\u90a3\u91cc\u54ed\u6ce3\u3002 \u3000\u3000\u5510\u7ecf\u5929\u8f7b\u8f7b\u5730\u8d70\u8fc7\u53bb\uff0c\u5fae\u5fae\u8bf4\u9053\uff1a\u201c\u963f\u6885\uff0c\u8ff7\u4e86\u8def\u5417\uff1f\u201d\u4ed6\u548c\u674e\u6c81\u6885\u5c0f\u65f6\u5019\u5e38\u5e38\u4e00\u9f50\u73a9 \u800d\uff0c\u53ea\u9053\u5979\u8fd8\u662f\u5c0f\u65f6\u90a3\u6837\u813e\u6c14\uff0c\u4f46\u542c\u5979\u54ed\u5f97\u5341\u5206\u51c4\u51c9\u3002\u51b3\u4e0d\u662f\u4ec5\u4ec5\u4e3a\u4e86\u8ff7\u8def\u3002 \u3000\u3000\u674e\u6c81\u6885\u7f13\u7f13\u5730\u62ac\u8d77\u5934\u6765\uff0c\u9053\uff1a\u201c\u4ed6\u8d70\u5566\uff01\u201d\u51b0\u5ddd\u5929\u5973\u8d70\u5230\u4e86\u5979\u7684\u8eab\u8fb9\uff0c\u9053\uff1a\u201c\u4f60\u89c1\u7740\u4ed6 \u4e86\uff0c\u5440\uff0c\u4f60\u600e\u4e48\u4e0d\u7559\u7740\u4ed6\uff1f\u201d\u5510\u7ecf\u5929\u7684\u7b11\u5bb9\u7acb\u5373\u6536\u655b\uff0c\u8fd9\u65f6\u4ed6\u5df2\u660e\u767d\uff0c\u539f\u6765\u662f\u91d1\u4e16\u9057\u5230\u8fc7\u8fd9 \u513f\uff0c\u674e\u6c81\u6885\u90fd\u7559\u4e0d\u4f4f\u4ed6\uff0c\u90a3\u4e48\u8fd8\u6709\u8c01\u80fd\u529d\u4ed6\u56de\u6765\u3002 \u3000\u3000\u674e\u6c81\u6885\u6307\u4e00\u6307\u5730\u4e0a\u7684\u94f6\u74f6\uff0c\u9053\uff1a\u201c\u4ed6\u628a\u78a7\u7075\u4e39\u90fd\u7559\u7ed9\u6211\u5403\u5566\u3002\u4ed6\u7684\u5fc3\u80a0\u592a\u597d\u4e86\uff0c\u4e5f\u592a\u72e0 \u4e86\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u771f\u50cf\u505a\u4e00\u573a\u68a6\u4f3c\u7684\uff0c\u68a6\u9192\u4e86\u4ed6\u5c31\u4e0d\u89c1\u4e86\uff01\u201d\u54fd\u54bd \u7740\u628a\u9047\u5230\u91d1\u4e16\u9057\u7684\u7ecf\u8fc7\u8bf4\u4e86\uff0c\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u90fd\u89c9\u5f97\u5fc3\u5934\u6c89\u91cd\uff0c\u60f3\u4e0d\u51fa\u7528\u4ec0\u4e48\u8bdd\u6765\u5b89\u6170\u674e \u6c81\u6094\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4f4e\u5934\u9ed8\u60f3\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6c81\u6885\u59b9\u59b9\uff0c\u4f60\u522b\u54ed\u5566\u3002\u6211\u4eec\u966a\u4f60\u4e0a\u73e0\u7a46\u6717 \u739b\u5cf0\u53bb\u3002\u201d\u674e\u6c81\u6094\u62ac\u8d77\u4e86\u7591\u60d1\u7684\u773c\u775b\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f9d\u4ed6\u7684\u6027\u683c\uff0c\u6211\u770b\u4ed6\u65e2\u7136\u5230\u4e86\u8fd9\u513f\uff0c \u5c31\u4e00\u5b9a\u4f1a\u53bb\u6500\u767b\u73e0\u5cf0\u3002\u201d \u3000\u3000\u674e\u6c81\u6885\u773c\u5149\u4e2d\u9732\u51fa\u4e00\u70b9\u5e0c\u671b\uff0c\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\uff0c\u4f60\u771f\u597d\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd8\u6253\u4e86 \u96ea\u9e21\uff0c\u54c8\uff0c\u8fd8\u662f\u70e4\u719f\u4e86\u7684\u3002\u4f60\u600e\u4e48\u4e0d\u5403\uff1f\u201d\u674e\u6c81\u6885\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u7559\u7ed9\u6211\u7684\uff0c\u6211\u820d\u4e0d\u5f97\u5403\u3002\u201d \u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4e0d\u5403\u4e1c\u897f\uff0c\u54ea\u6709\u6c14\u529b\u5462\uff1f\u201d\u5979\u6478\u6478\u674e\u6c81\u6885\u7684\u5e72\u7cae\u888b\uff0c\u5e72\u7cae\u888b\u65e9\u5df2 \u7a7a\u4e86\uff0c\u539f\u6765\u674e\u6c81\u6885\u6574\u6574\u4e00\u5929\uff0c\u7adf\u6ca1\u6709\u5403\u4e1c\u897f\u3002\u5e78\u800c\u5510\u7ecf\u5929\u7684\u4e8e\u7cae\u5e26\u5f97\u591a\uff0c\u8fd8\u5e26\u6709\u4e00\u652f\u957f\u767d\u4eba \u53c2\uff0c\u6700\u9002\u5b9c\u722c\u5c71\u4e4b\u7528\u3002\u674e\u6c81\u6885\u5403\u4e86\u4e00\u4e9b\u5e72\u7cae\uff0c\u56bc\u4e86\u534a\u652f\u4eba\u53c2\uff0c\u90a3\u534a\u53ea\u96ea\u9e21\uff0c\u5374\u8fd8\u662f\u820d\u4e0d\u5f97 \u5403\u3002 \u3000\u3000\u4e09\u4eba\u7a7f\u8fc7\u4e86\u51b0\u5854\u7fa4\uff0c\u4f46\u89c1\u51b0\u5761\u4e0a\u8fd8\u7559\u6709\u91d1\u4e16\u9057\u7684\u8db3\u5370\uff0c\u4ed6\u4eec\u8ddf\u7740\u91d1\u4e16\u9057\u7684\u8db3\u5370\u524d\u884c\uff0c\u518d \u8d70\u8fc7\u4e86\u51b0\u80e1\u540c\uff0c\u7b2c\u4e8c\u65e5\u5230\u4e86\u98ce\u7a9d\u7684\u5317\u62d7\u5730\u533a\uff0c\u5927\u98ce\u96ea\u65e9\u5df2\u628a\u91d1\u4e16\u9057\u7684\u8db3\u5370\u57cb\u6389\uff0c\u4e09\u4eba\u7528\u5c3d\u6c14 \u529b\u901a\u8fc7\u4e86\u8fd9\u4e2a\u5730\u533a\uff0c\u518d\u8d70\u4e00\u5929\uff0c\u73e0\u7a46\u6717\u739b\u5cf0\u5df2\u7ecf\u5728\u671b\u3002\u53ef\u662f\u4ed6\u4eec\u4e5f\u90fd\u7cbe\u75b2\u529b\u7aed\u4e86\u3002\u51b0\u5ddd\u5929\u5973 \u867d\u7136\u4e0d\u6015\u5bd2\u51b7\uff0c\u4f46\u5230\u4e86\u8fd9\u6837\u7684\u9ad8\u5ea6\uff0c\u7531\u4e8e\u7f3a\u4e4f\u6c27\u6c14\uff0c\u4e00\u6837\u4ee4\u5979\u89c9\u5f97\u80f8\u53e3\u75bc\u75db\u800c\u80c0\u585e\uff0c\u547c\u5438\u5341 \u5206\u56f0\u96be\u3002\u5510\u7ecf\u5929\u5185\u529f\u6839\u57fa\u6700\u539a\uff0c\u7a0d\u597d\u4e00\u4e9b\uff0c\u674e\u6c81\u6885\u5219\u66f4\u662f\u652f\u6301\u4e0d\u4f4f\uff0c\u4f46\u662f\u4e3a\u4e86\u4e00\u4e2a\u5e0c\u671b\uff0c\u5979 \u4ecd\u7136\u575a\u6301\u7740\uff0c\u5728\u51b0\u5ddd\u5929\u5973\u548c\u5510\u7ecf\u5929\u7684\u6276\u6301\u4e0b\uff0c\u4e00\u6b65\u6b65\u8d70\u8fd1\u73e0\u5cf0\u3002 \u3000\u3000\u90a3\u6b63\u662f\u96ea\u5d29\u8fc7\u540e\uff0c\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u98ce\u96ea\u547c\u5578\uff0c\u4ece\u4e0b\u9762\u671b\u4e0a\u53bb\uff0c\u4f46\u89c1\u96ea\u5cf0\u63d2\u4e91\uff0c\u7b80\u76f4\u662f\u5140\u9e70 \u4e5f\u98de\u4e0d\u4e0a\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u548c\u674e\u6c81\u6885\u4ef0\u671b\u73e0\u5cf0\uff0c\u5fc3\u810f\u90fd\u51e0\u4e4e\u8981\u505c\u6b62\u4e86\u8df3\u52a8\u4e86\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u60f3\u9053\uff1a\u201c\u91d1\u4e16\u9057 \u600e\u80fd\u6500\u4e0a\u8fd9\u5ea7\u9ad8\u5cf0\u3002\u5440\uff0c\u90a3\u5b9a\u662f\u51f6\u591a\u5409\u5c11\u7684\u4e86\uff01\u201d\u4f46\u8fd9\u7edd\u671b\u7684\u8bed\u8a00\uff0c\u8c01\u4e5f\u4e0d\u80af\u5148\u8bf4\u51fa\u6765\u3002\u674e \u6c81\u6885\u5ffd\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u51e0\u5929\u4e86\uff1f\u201d\u5979\u5728\u51b0\u5854\u7fa4\u4e2d\u7ecf\u8fc7\u4e00\u5ea6\u660f\u8ff7\uff0c\u65e5\u5b50\u8bb0\u5f97\u4e0d\u5927\u6e05\u695a\uff0c \u4f46\u89c9\u5f97\u597d\u4f3c\u5df1\u8fc7\u4e86\u91d1\u4e16\u9057\u751f\u547d\u7684\u671f\u9650\u3002\u51b0\u5ddd\u5929\u5973\u5530\u7684\u4e00\u4e0b\u9762\u8272\u53d8\u5f97\u7070\u767d\uff0c\u5979\u731b\u7684\u8bb0\u4e86\u8d77\u6765\uff0c \u5979\u4eec\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u5df2\u8fc7\u4e86\u4e03\u4e2a\u767d\u5929\u548c\u9ed1\u591c\uff0c\u90a3\u5c31\u662f\u8bf4\u65e9\u5df2\u8fc7\u4e86\u671f\u9650\u4e00\u5929\u4e00\u591c\u4e86\uff01 \u3000\u3000\u970e\u65f6\u95f4\u7a7a\u6c14\u90fd\u597d\u4f3c\u51b7\u5f97\u51dd\u7ed3\u4e86\uff0c\u4f17\u4eba\u672c\u6765\u90fd\u5df1\u7cbe\u75b2\u529b\u7aed\uff0c\u8fd9\u65f6\u66f4\u89c9\u624b\u8db3\u9178\u8f6f\uff0c\u4e1d\u6beb\u4e5f\u4e0d \u80fd\u79fb\u52a8\u3002\u767d\u5929\u53c8\u8fc7\u53bb\u4e86\uff0c\u4f46\u89c1\u82cd\u767d\u65e0\u529b\u7684\u6708\u4eae\uff0c\u4ece\u73e0\u7a46\u6717\u739b\u5cf0\u4e0a\u60a0\u60a0\u5347\u8d77\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u5510 \u7ecf\u5929\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u56de\u53bb\u5566\uff01\u201d\u674e\u6c81\u6885\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u56de\u53bb\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51c4\u7136\u5730\u770b\u7740\u674e\u6c81\u6885\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u51b0\u5761\u4e0a\u6709\u4eba\u53eb\u9053\uff1a\u201c\u963f\u6885\uff0c\u662f\u4f60\u6765\u4e86 \u5417\uff1f\u201d\u674e\u6c81\u6885\u8df3\u8d77\u6765\u9053\uff1a\u201c\u5988\u5988\uff01\u201d\u62ac\u8d77\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u51af\u7433\u7b11\u559c\u559c\u5730\u5728\u51b0\u5761\u4e0a\u62db\u624b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5927\u559c\u53eb\u9053\uff1a\u201c\u59e8\u5988\uff0c\u4f60\u627e\u5230\u4ed6\u4e86\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u627e\u5230\u5566\uff01\u201d\u674e\u6c81\u6885\u4e00\u4e0b\u5b50\u7cbe\u795e \u6296\u64de\uff0c\u7adf\u7136\u8dd1\u5f97\u6bd4\u51b0\u5ddd\u5929\u5973\u8fd8\u5feb\uff0c\u5148\u5230\u4e86\u6bcd\u4eb2\u7684\u8ddf\u524d\uff0c\u5ffd\u5730\u53c8\u5760\u8fdb\u4e86\u5931\u671b\u7684\u6df1\u6e0a\u3002\u5931\u58f0\u53eb \u9053\uff1a\u201c\u4ed6\u5728\u54ea\u513f\uff1f\u201d\u51af\u7433\u4f38\u624b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u770b\uff01\u201d \u3000\u3000\u53ea\u89c1\u524d\u9762\u7684\u51b0\u58c1\u4e0a\u523b\u6709\u51e0\u884c\u5b57\u8ff9\uff0c\u90a3\u662f\u4e00\u9996\u8bd7\uff0c\u8bd7\u9053\uff1a\u201c\u4e0d\u662f\u5e73\u751f\u60ef\u8d1f\u6069\uff0c\u73e0\u5cf0\u9065\u671b\u81ea \u6c89\u541f\uff0c\u6b64\u8eab\u53ea\u5408\u6c5f\u6e56\u8001\uff0c\u6127\u5bf9\u5ae6\u5a25\u4e00\u7247\u5fc3\u3002\u201d\u51b0\u58c1\u4e0b\u9762\u8fd8\u5269\u4e0b\u51e0\u4e2a\u672a\u88ab\u98ce\u96ea\u57cb\u6389\u7684\u62d0\u5370\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u5934\u6c89\u91cd\uff0c\u53ea\u6709\u5979\u80fd\u7a0d\u7a0d\u7406\u89e3\u91d1\u4e16\u9057\u9898\u8bd7\u7684\u5fc3\u60c5\uff0c\u90a3\u662f\u4e00\u79cd\u6781\u5ea6\u81ea\u5c0a\u800c\u53c8\u6781\u5ea6 \u81ea\u5351\u7684\u9519\u7efc\u590d\u6742\u7684\u5fc3\u60c5\uff0c\u4ed6\u7ec8\u4e8e\u820d\u6389\u4e86\u6e34\u671b\u5df2\u4e45\u7684\u7684\u4eba\u95f4\u6e29\u6696\uff0c\u5728\u8fd9\u51b0\u96ea\u7684\u4e16\u754c\u4e2d\u53c8\u6084\u6084\u5730 \u72ec\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u674e\u6c81\u6885\u4f46\u89c9\u4e00\u7247\u832b\u7136\uff0c\u5341\u5206\u4e0d\u89e3\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e48\uff0c\u4ed6\u8fd8\u662f\u8d70\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a \u201c\u4f60\u77a7\uff0c\u8fd9\u51e0\u884c\u5b57\u662f\u4ed6\u7528\u94c1\u5251\u523b\u51fa\u6765\u7684\uff0c\u5982\u679c\u4ed6\u4e34\u6b7b\u5782\u5371\uff0c\u54ea\u8fd8\u6709\u8fd9\u4efd\u529f\u529b\uff1f\u201d\u674e\u6c81\u6885\u5fc3\u4e2d \u7a0d\u7a0d\u5b89\u6170\uff0c\u4ecd\u662f\u6005\u7136\u4ed6\u8bf4\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ed6\u8fd8\u662f\u8d70\u4e86\uff01\u201d \u3000\u3000\u73e0\u7a46\u6717\u739b\u5cf0\u9876\u7684\u6708\u5149\uff0c\u900f\u8fc7\u6f2b\u5929\u98ce\u5377\u7684\u51b0\u96ea\uff0c\u6d12\u5230\u4f17\u4eba\u8eab\u4e0a\uff0c\u51b0\u5ddd\u6620\u6708\uff0c\u610f\u5883\u5206\u5916\u51c4 \u6e05\uff0c\u4f17\u4eba\u90fd\u89c9\u5fc3\u5934\u4e00\u7247\u5bd2\u51b7\u3002\u51af\u7433\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u771f\u662f\u5c82\u6709\u6b64\u7406\uff01\u201d\u5ffd\u53c8\u5657\u55e4\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u4f60\u6101\u4ec0\u4e48\uff1f\u53ea\u8981\u4ed6\u4e0d\u6b7b\uff0c\u5988\u603b\u80fd\u7ed9\u4f60\u628a\u4ed6\u6293\u56de\u6765\uff0c\u8ba9\u4f60\u6253\u4ed6\u4e00\u987f\u6d88\u6c14\u3002\u201d\u8fd9\u8bf4\u8bdd\u5f53\u7136 \u662f\u6545\u610f\u9017\u5973\u513f\u7b11\u7684\uff0c\u51af\u7433\u770b\u4e86\u8fd9\u9996\u8bd7\uff0c\u4e5f\u65e9\u5df2\u660e\u767d\uff0c\u91d1\u4e16\u9057\u4e43\u662f\u4e0b\u51b3\u5fc3\u907f\u5f00\u5979\u4eec\uff0c\u518d\u8981\u627e \u4ed6\uff0c\u90a3\u662f\u66f4\u4e0d\u5bb9\u6613\u7684\u4e86\u3002 \u3000\u3000\u98ce\u96ea\u6e10\u6e10\u51cf\u5f31\uff0c\u674e\u6c81\u6885\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e86\u4e2a\u96ea\u7403\u600e\u4e48\u5982\u6b64\u5947\u602a\uff1f\u201d\u53ea\u89c1\u51b0\u5761\u4e0a\u6eda\u4e0b\u4e09\u56e2 \u767d\u8272\u7684\u4e1c\u897f\uff0c\u51af\u7433\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u96ea\u7403\uff0c\u90a3\u662f\u4f60\u7684\u59e8\u7236\u3001\u59e8\u6bcd\uff0c\u54a6\uff0c\u8fd8\u6709\u4e00\u4e2a \u4eba\u4f3c\u662f\u5415\u56db\u5a18\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u4e09\u4e2a\u201c\u96ea\u4eba\u201d\u5df2\u662f\u4ece\u51b0\u5761\u4e0a\u6ed1\u4e86\u4e0b\u6765\uff0c\u5230\u4e86\u73e0\u7a46\u6717\u739b\u5cf0\u811a\uff0c \u7eb5\u58f0\u957f\u7b11\uff0c\u62cd\u6389\u8eab\u4e0a\u539a\u539a\u7684\u79ef\u96ea\uff0c\u679c\u7136\u662f\u5510\u6653\u6f9c\u51af\u745b\u548c\u5415\u56db\u5a18\u3002\u5728\u73e0\u5cf0\u811a\u4e0b\u547c\u5438\u5f53\u7136\u6bd4\u4e0a\u9762 \u8212\u7545\u5f97\u591a\uff0c\u8fd9\u4e09\u4e2a\u4eba\u4e43\u662f\u5f53\u4e16\u6b66\u529f\u6700\u9ad8\u7684\u4eba\u7269\uff0c\u5230\u4e86\u4e0b\u9762\uff0c\u7cbe\u795e\u6062\u590d\uff0c\u8c01\u4e5f\u60f3\u50cf\u4e0d\u5230\uff0c\u4e0d\u4e45 \u4e4b\u524d\uff0c\u4ed6\u4eec\u662f\u90a3\u6837\u7684\u56f0\u987f\u75b2\u52b3\uff0c\u5728\u73e0\u5cf0\u4e0a\u9762\uff0c\u51e0\u4e4e\u4e27\u6389\u4e86\u6027\u547d\u3002 \u3000\u3000\u51af\u745b\u4e00\u89c1\u513f\u5b50\uff0c\u5fc3\u82b1\u6012\u653e\uff0c\u63fd\u7740\u51b0\u5ddd\u5929\u5973\uff0c\u8f7b\u8f7b\u6478\u629a\u5979\u7684\u79c0\u53d1\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5bf9\u6211\u4e0d \u751f\u6c14\u4e86\u5427\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u6211\u7b54\u5e94\u8fc7\u7ed9\u4f60\u627e\u4e00\u4e2a\u597d\u5ab3\u5987\u513f\uff0c\u77a7\uff0c\u4f60\u73b0\u5728\u8be5\u79f0\u5fc3\u6ee1\u610f\u4e86\u5427\uff1f\u201d \u51b0\u5ddd\u5929\u5973\u7f9e\u5f97\u4f4e\u4e0b\u4e86\u5934\uff0c\u60f3\u8d77\u4ee5\u524d\u5c06\u5510\u7ecf\u5929\u7684\u6bcd\u4eb2\u8bef\u5f53\u4ed6\u7684\u59e8\u5988\u4e4b\u4e8b\uff0c\u4e0d\u7981\u6697\u7b11\u3002\u771f\u60f3\u4e0d\u5230 \u5929\u4e0b\u7adf\u6709\u8fd9\u6837\u76f8\u4f3c\u7684\u4eba\u3002\u8bb0\u8d77\u5510\u7ecf\u5929\u7684\u8bdd\uff0c\u6697\u4e2d\u7559\u610f\uff0c\u8fd9\u624d\u5206\u8fa8\u51fa\u5979\u4eec\u7b11\u65f6\u679c\u4e0d\u76f8\u540c\uff0c\u4e00\u4e2a \u5728\u5de6\u8fb9\u9762\u988a\u73b0\u51fa\u68a8\u6da1\uff0c\u4e00\u4e2a\u5374\u5728\u53f3\u8fb9\u3002 \u3000\u3000\u51af\u7433\u53c8\u9053\uff1a\u201c\u6211\u7b54\u5e94\u4f60\u4eec\u7684\u4e8b\u5df2\u529e\u5230\u4e86\uff0c\u4f60\u4eec\u7b54\u5e94\u6211\u7684\u4e8b\u5462\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60 \u4eec\u8fd8\u6ca1\u6709\u89c1\u7740\u91d1\u4e16\u9057\u5417\uff1f\u6211\u53eb\u4ed6\u5728\u8fd9\u91cc\u7b49\u4f60\u4eec\u7684\u5440\uff01\u8981\u4e0d\uff0c\u4ed6\u5c31\u662f\u5230\u65b9\u4eca\u660e\u7684\u5bb6\u4e2d\u7b49\u5019\u4f60\u4eec \u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4ed6\u624d\u4e0d\u4f1a\u5462\uff0c\u4f60\u77a7\uff0c\u4ed6\u9898\u7684\u8fd9\u9996\u8bd7\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u770b\u4e86\u9898\u8bd7\uff0c\u9eef\u7136\u4e0d\u8bed\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u6709\u5176\u5e08\u5fc5\u6709\u5176\u5f92\uff0c\u4ed6\u7684\u884c\u5f84\u6bd4\u6bd2\u9f99\u5c0a\u8005 \u5f53\u5e74\u8fd8\u8981\u53e4\u602a\u3002\u201d\u5c06\u4ed6\u6551\u6cbb\u91d1\u4e16\u9057\u7684\u7ecf\u8fc7\u544a\u8bc9\u4e86\u4f17\u4eba\u3002\u674e\u6c81\u6885\u542c\u4e86\u4e00\u559c\u4e00\u5fe7\uff0c\u559c\u8005\u662f\u91d1\u4e16\u9057 \u7684\u6027\u547d\u5f97\u4ee5\u4fdd\u5b58\uff0c\u800c\u4e14\u56e0\u7978\u53cd\u800c\u5f97\u798f\uff0c\u5f02\u65e5\u5fc5\u80fd\u6210\u4e3a\u6b66\u5b66\u7684\u5927\u5e08\uff1b\u5fe7\u8005\u662f\u4ed6\u5eb7\u590d\u4e4b\u540e\uff0c\u8fd8\u8981 \u9003\u8d70\uff0c\u90a3\u5b9a\u662f\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4e0d\u518d\u56de\u6765\u7684\u4e86\u3002 \u3000\u3000\u51af\u7433\u4e00\u5411\u6e38\u620f\u98ce\u5c18\uff0c\u5bf9\u4ec0\u4e48\u4e8b\u60c5\u90fd\u662f\u6ee1\u4e0d\u5728\u4e4e\u7684\u6837\u5b50\uff0c\u8fd9\u4e00\u6b21\u8868\u9762\u4e0a\u867d\u7136\u4e5f\u6ca1\u6709\u663e\u9732\u5f97 \u600e\u6837\u7d27\u5f20\uff0c\u5176\u5b9e\u5374\u662f\u4f24\u5fc3\u4e4b\u6781\u3002\u5979\u597d\u4e0d\u5bb9\u6613\u624d\u627e\u5230\u4e00\u4e2a\u5408\u4e4e\u81ea\u5df1\u5fc3\u610f\u3001\u4e5f\u5408\u5973\u513f\u5fc3\u610f\u7684\u4eba\uff0c \u7136\u800c\u8fd9\u4e2a\u4eba\u5374\u53c8\u83ab\u540d\u5176\u5999\u5730\u907f\u5f00\u4e86\u5979\uff0c\u907f\u5f00\u4e86\u6240\u6709\u5173\u5fc3\u4ed6\u7684\u4eba\u3002\u51af\u7433\u5fc3\u4e2d\u70e6\u4e71\u4e4b\u6781\uff0c\u542c\u5f97\u5510 \u6653\u6f9c\u63d0\u8d77\u6bd2\u9f99\u5c0a\u8005\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u6bd2\u9f99\u5c0a\u8005\u90a3\u672c\u65e5\u8bb0\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u672c\u65e5\u8bb0\u4f60\u4ea4\u7ed9\u4e86\u91d1\u4e16\u9057\u4e86 \u5417\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ea4\u7ed9\u4ed6\u4e86\u3002\u4ec0\u4e48\uff0c\u90a3\u4e0d\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u6b66\u529f\u79d8\u7c4d\uff0c\u800c\u662f\u4ed6\u6240\u5199\u7684 \u65e5\u8bb0\u5417\uff1f\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u7ffb\u770b\u5417\uff1f\u201d\u5510\u6653\u6f9c\u6120\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u7ffb\u770b\u522b\u4eba\u7684\u4e1c\u897f\uff1f\u201d\u5415\u56db\u5a18\u4e00\u76f4 \u5728\u9ed8\u9ed8\u5730\u542c\u4ed6\u4eec\u8c08\u8bdd\uff0c\u8fd9\u65f6\u773c\u775b\u4e2d\u5ffd\u7136\u73b0\u51fa\u5149\u8292\uff0c\u9053\uff1a\u201d\u8fd9\u65e5\u8bb0\u91cc\u8bb0\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\u5417\uff1f\u201d \u51af\u7433\u9053\uff1a\u201c\u600e\u4e48\u6ca1\u6709\uff1f\u8fd9\u65e5\u8bb0\u7684\u8bb0\u8f7d\uff0c\u6709\u5173\u6cbf\u6d77\u7684\u751f\u7075\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u86c7\u5c9b\u4e0b\u9762\uff0c\u539f\u6765\u57cb\u6709\u706b\u5c71\uff0c\u4f9d\u6bd2\u9f99\u5c0a \u8005\u7684\u63a8\u7b97\uff0c\u8fd9\u706b\u5c71\u7684\u7206\u53d1\u53ef\u80fd\u5728\u5341\u5e74\u4e4b\u540e\uff0c\u53ea\u6050\u6574\u4e2a\u86c7\u5c9b\u90fd\u8981\u5316\u6210\u98de\u7070\uff0c\u4e0d\u4f46\u6d77\u4e2d\u7684\u751f\u7269\u906d \u9022\u6d69\u52ab\uff0c\u9ec4\u6d77\u8fb9\u6cbf\u7684\u9646\u5730\uff0c\u4e5f\u53ef\u80fd\u6ce2\u53ca\uff0c\u53ea\u6709\u719f\u6089\u86c7\u5c9b\u5730\u5f62\u800c\u53c8\u4e0d\u754f\u86c7\u6bd2\u7684\u4eba\uff0c\u5728\u706b\u5c71\u7206\u53d1 \u4e4b\u524d\u7684\u51e0\u4e2a\u6708\uff0c\u6df1\u5165\u706b\u5c71\u53e3\uff0c\u51ff\u5f00\u901a\u8def\uff0c\u5f15\u6765\u6d77\u6c34\uff0c\u8ba9\u6bd2\u706b\u6162\u6162\u6e32\u6cc4\uff0c\u6216\u8005\u53ef\u4ee5\u633d\u6551\u8fd9\u573a\u6d69 \u52ab\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u8272\u7136\u800c\u559c\uff0c\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4eec\u4e0d\u5fc5\u8d39\u529b\u53bb\u627e\u91d1\u4e16\u9057\u5566\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u600e \u4e48\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u770b\u4e86\u8fd9\u672c\u65e5\u8bb0\u96be\u9053\u4ed6\u8fd8\u4e0d\u660e\u767d\uff0c\u4ed6\u81ea\u5df1\u5c31\u662f\u6700\u9002\u5b9c\u4e8e\u633d\u6551\u8fd9\u573a\u6d69\u52ab\u7684 \u4eba\uff01\u201d \u3000\u3000\u674e\u6c81\u6885\u9053\uff1a\u201c\u90a3\u6211\u5b81\u613f\u4ed6\u4e0d\u518d\u56de\u6765\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6551\u56f0\u6276\u5371\uff0c\u4fa0\u8005\u672c\u8272\u3002\u4f55\u51b5\u662f\u633d\u6551 \u8fd9\u6837\u7684\u4e00\u573a\u6d69\u52ab\uff01\u800c\u4e14\u6bd2\u9f99\u5c0a\u8005\u5bf9\u6d88\u9975\u7978\u80ce\u4e4b\u4e8b\uff0c\u65e2\u6709\u9884\u89c1\uff0c\u6599\u60f3\u91d1\u4e16\u9057\u5c31\u662f\u6df1\u5165\u706b\u7a9f\uff0c\u4e5f \u672a\u5fc5\u5c31\u6709\u6027\u547d\u4e4b\u5fe7\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u7684\u6027\u547d\u4e5f\u662f\u62fe\u56de\u6765\u7684\uff0c\u5c31\u8ba9\u4ed6\u505a\u8fd9\u4e00\u573a\u5927\u529f\u5fb7\uff0c\u4e5f \u53ef\u5f97\u4eba\u666f\u4ef0\u3002\u201d \u3000\u3000\u674e\u6c81\u6885\u7d27\u8e69\u7740\u7684\u53cc\u7709\u6e10\u6e10\u5f00\u5c55\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4e5f\u613f\u4ed6\u56de\u6765\u4e86\uff0c\u53ea\u662f\u4ed6\u80af\u4e0d\u80af\u56de\u6765\u5462\uff1f\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u7684\u5fc3\u60c5\u6b63\u81ea\u6127\u5bf9\u4e16\u4eba\uff0c\u6211\u77a7\u4ed6\u4e00\u5b9a\u4f1a\u56de\u53bb\u633d\u6551\u8fd9\u573a\u6d69\u52ab\u3002\u201d\u674e\u6c81\u6885\u542c\u5979\u8bf4\u5f97 \u5982\u6b64\u80af\u5b9a\uff0c\u5fc3\u60c5\u77db\u76fe\u4e4b\u6781\uff0c\u4f46\u4e00\u60f3\u8d77\u706b\u5c71\u7206\u53d1\u4e4b\u671f\u81f3\u5c11\u8fd8\u6709\u5341\u5e74\uff0c\u82e5\u679c\u662f\u91d1\u5410\u9057\u5341\u5e74\u4e4b\u540e\u4e0d \u518d\u91cd\u56de\u4e2d\u539f\uff0c\u81ea\u5df1\u867d\u7136\u53ef\u4ee5\u5230\u86c7\u5c9b\u53bb\u5b88\u5019\u4ed6\uff0c\u8fd9\u5341\u5e74\u6f2b\u957f\u7684\u65f6\u95f4\uff0c\u53c8\u600e\u751f\u6328\u8fc7\u3002\u4f46\u4e8b\u65e2\u5982 \u65af\uff0c\u7a7a\u81ea\u7126\u6025\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u529e\u6cd5\u3002 \u3000\u3000\u4e00\u884c\u4eba\u7b49\uff0c\u9ed8\u9ed8\u4e0b\u5c71\u3002\u4e0b\u5c71\u6bd4\u4e0a\u5c71\u5bb9\u6613\u5f97\u591a\uff0c\u53ef\u662f\u4e3a\u4e86\u91d1\u4e16\u9057\u7684\u4e8b\u60c5\uff0c\u5fc3\u5934\u90fd\u8499\u4e0a\u4e00\u5c42 \u9634\u5f71\u3002\u8d70\u4e86\u4e09\u5929\uff0c\u56de\u5230\u65b9\u4eca\u660e\u7684\u5bb6\u4e2d\uff0c\u9f99\u7075\u77eb\u3001\u5510\u8001\u592a\u5a46\u7b49\u4eba\u65e9\u5df2\u56de\u6765\u4e86\uff0c\u4ed6\u4eec\u6839\u672c\u8fd8\u672a\u4e0a \u5230\u51b0\u5854\u7fa4\u90a3\u5904\u7684\u9ad8\u5ea6\uff0c\u7a7a\u81ea\u6ee1\u5c71\u641c\u7d22\uff0c\u5f53\u7136\u6ca1\u6709\u53d1\u73b0\u91d1\u4e16\u9057\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u65b9\u4eca\u660e\u542c\u5510\u6653\u6f9c\u4e4b\u529d\uff0c\u4e5f\u968f\u540c\u4f17\u4eba\u4e0b\u5c71\uff0c\u4ed6\u79bb\u5f00\u6570\u5341\u5e74\u9690\u5c45\u7684\u5bb6\u56ed\uff0c\u5fc3\u4e2d\u81ea\u6709\u65e0\u9650\u6005 \u60c6\uff0c\u4f46\u60f3\u5230\u5973\u513f\u7684\u5c06\u6765\uff0c\u4ed6\u4ecd\u662f\u6109\u5feb\u5730\u79bb\u5f00\u4e86\u6545\u5c45\u3002 \u3000\u3000\u4f17\u4eba\u4e0a\u5c71\u4e0b\u5c71\uff0c\u7ecf\u8fc7\u7684\u65f6\u95f4\u4e0d\u8fc7\u5341\u591a\u5929\uff0c\u5c71\u4e0b\u7684\u666f\u8272\u65e9\u5df2\u53d8\u4e86\uff0c\u8fd9\u65f6\u5df2\u662f\u66ae\u6625\u4e09\u6708\u7684\u65f6 \u8282\uff0c\u5c71\u4e0b\u7684\u51b0\u96ea\u5df2\u6e10\u6e10\u6eb6\u89e3\uff0c\u5c71\u5761\u4e0a\u62ab\u76d6\u7740\u6d53\u7eff\u7684\u68ee\u6797\uff0c\u5230\u5904\u76db\u5f00\u7740\u767d\u8272\u7684\u91ce\u8537\u8587\uff0c\u8fd8\u6709\u8273 \u7ea2\u7684\u73ab\u7470\u548c\u4e94\u8272\u7f24\u7eb7\u7684\u675c\u9e43\uff0c\u51b0\u5ddd\u5929\u5973\u968f\u624b\u6458\u4e86\u51e0\u6735\u91ce\u82b1\uff0c\u53c8\u8ba9\u5b83\u968f\u98ce\u98d8\u6563\uff0c\u4e0b\u65f6\u5730\u56de\u671b\u73e0 \u5cf0\uff0c\u53ea\u6709\u5510\u7ecf\u5929\u80fd\u7a0d\u7a0d\u7406\u89e3\u5230\u5979\u5fc3\u4e2d\u7684\u6005\u60d8\u3002 \u3000\u3000\u518d\u8d70\u4e86\u4e24\u5929\uff0c\u5faa\u7740\u6765\u65f6\u7684\u8def\uff0c\u56de\u5230\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0b\u9762\u7684\u5e7d\u8c37\uff0c\u4f46\u89c1\u8c37\u4e2d\u91ce\u7f8a\u5954\u8d70\uff0c\u5c3c\u6cca\u5c14 \u7684\u5927\u519b\u65e9\u5df2\u64a4\u8d70\u4e86\uff0c\u6e05\u519b\u4e5f\u5df2\u64a4\u8d70\u4e86\uff0c\u5c71\u8c37\u4e2d\u4e00\u7247\u5b81\u9759\uff0c\u8c01\u6599\u5f97\u5230\u4e0d\u4e45\u4e4b\u524d\uff0c\u8fd9\u548c\u5e73\u5b81\u9759\u7684 \u5c71\u8c37\u4e2d\u66fe\u5f25\u6f2b\u6218\u4e91\uff1f \u3000\u3000\u6e05\u519b\u8fd8\u662f\u524d\u51e0\u5927\u64a4\u8d70\u7684\uff0c\u9648\u5929\u5b87\u548c\u5e7d\u840d\u5374\u8fd8\u7559\u5728\u5c71\u8c37\u4e4b\u4e2d\u7b49\u5019\u4f17\u4eba\uff0c\u89c1\u4f17\u4eba\u5e73\u5b89\u56de\u6765\uff0c \u81ea\u662f\u6b22\u559c\uff0c\u4f46\u542c\u5f97\u91d1\u4e16\u9057\u5931\u8e2a\u7684\u6d88\u606f\uff0c\u60f3\u8d77\u4ed6\u66fe\u6551\u8fc7\u81ea\u5df1\u7684\u6027\u547d\uff0c\u4e5f\u4e0d\u7981\u9eef\u7136\u3002 \u3000\u3000\u4f17\u4eba\u8d70\u51fa\u5c71\u8c37\uff0c\u53c8\u56de\u5230\u9633\u5149\u660e\u5a9a\u7684\u8349\u539f\u4e0a\uff0c\u8349\u539f\u4e0a\u5df2\u5f00\u59cb\u6709\u7b2c\u4e00\u6279\u65c5\u4eba\uff0c\u90a3\u662f\u4e00\u7fa4\u8d29\u5417 \u7684\u201c\u6d41\u6d6a\u4eba\u201d\uff0c\u6765\u5230\u8fb9\u5883\u505a\u751f\u610f\u7684\u3002\u5728\u8349\u539f\u4e0a\u4ed6\u4eec\u5531\u8d77\u4e86\u201c\u6d41\u6d6a\u8005\u4e4b\u6b4c\u201d\uff1a \u3000\u3000\u201c\u5723\u5cf0\u7684\u51b0\u5ddd\u50cf\u5927\u6cb3\u7684\u5012\u6302\uff0c \u3000\u3000\u4f60\u542c\u90a3\u6d41\u51b0\u6d6e\u52a8\u3002\u8f7b\u8f7b\u7684\u54cd\u2014\u2014 \u3000\u3000\u50cf\u662f\u59d1\u5a18\u7684\u5de7\u624b\u5f39\u8d77\u4e86\u4e1c\u4e0d\u62c9\u3002 \u3000\u3000\u5979\u5728\u95ee\u90a3\u6d41\u6d6a\u7684\u65c5\u4eba\uff1a \u3000\u3000\u4f60\u8fd8\u8981\u6500\u8fc7\u51e0\u5ea7\u51b0\u5c71\uff1f \u3000\u3000\u7ecf\u5386\u51e0\u8bb8\u98ce\u7802\uff1f \u3000\u3000\u54bf\u5566\u2014\u2014 \u3000\u3000\u6d41\u6d6a\u7684\u65c5\u4eba\u5440\uff0c \u3000\u3000\u8349\u539f\u7684\u5140\u9e70\u4e5f\u4e0d\u80fd\u7ec8\u65e5\u76d8\u65cb\u4e0d\u4e0b\uff0c \u3000\u3000\u4f60\u4eec\u5c3d\u662f\u8d70\u5440\uff0c\u8d70\u5440\uff0c\u8d70\u5440\u2014\u2014 \u3000\u3000\u8981\u8d70\u5230\u54ea\u5e74\u54ea\u6708\uff0c\u624d\u80af\u505c\u4e0b\u4f60\u4eec\u7684\u9a6c\uff1f \u3000\u3000\u59d1\u5a18\u5440\uff0c\u591a\u8c22\u4f60\u7684\u597d\u5fc3\u597d\u610f\uff0c \u3000\u3000\u53ea\u662f\u6211\u4eec\u6ca1\u6709\u529e\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u4f60\u53ef\u66fe\u89c1\u8fc7\u8352\u6f20\u5f00\u82b1\uff1f \u3000\u3000\u4f60\u53ef\u66fe\u89c1\u8fc7\u51b0\u5ddd\u878d\u5316\uff1f \u3000\u3000\uff08\u4f60\u6ca1\u6709\u89c1\u8fc7\uff1f\u6ca1\u6709\u89c1\u8fc7\uff01\u5440\uff01\uff09 \u3000\u3000\u90a3\u4e48\u6d41\u6d6a\u7684\u65c5\u4eba\u54ea\uff0c \u3000\u3000\u4ed6\u4e5f\u6c38\u4e0d\u4f1a\u505c\u4e0b\uff01\u201d \u3000\u3000\u8fd9\u300a\u6d41\u6d6a\u8005\u4e4b\u6b4c\u300b\u662f\u9648\u5929\u5b87\u4e09\u5e74\u4e4b\u524d\u66fe\u542c\u8fc7\u7684\uff0c\u90a3\u65f6\u4ed6\u521d\u4f1a\u829d\u5a1c\uff0c\u542c\u4e86\u8fd9\u9996\u6b4c\uff0c\u4e0d\u7981\u5fc3 \u4e2d\u7ede\u75db\uff0c\u56de\u5934\u4e00\u77a5\uff0c\u5e7d\u840d\u6b63\u7528\u6df1\u60c5\u7684\u773c\u5149\u6ce8\u89c6\u7740\u4ed6\uff0c\u8fd9\u773c\u5149\u8db3\u4ee5\u7597\u6cbb\u4ed6\u5fc3\u5934\u7684\u521b\u4f24\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e5f\u66fe\u542c\u8fc7\u8fd9\u9996\u6b4c\uff0c\u5979\u7981\u4e0d\u4f4f\u5fc3\u5934\u98a4\u6817\uff0c\u60f3\u8d77\u4e86\u91d1\u4e16\u9057\u7684\u547d\u8fd0\uff0c\u96be\u9053\u91d1\u4e16\u9057\u7684\u547d \u8fd0\u7adf\u4f3c\u8fd9\u6b4c\u4e2d\u6d41\u6d6a\u7684\u65c5\u4eba\u3002\u56de\u5934\u4e00\u77a5\uff0c\u5510\u7ecf\u5929\u4e5f\u6b63\u7528\u6df1\u60c5\u7684\u773c\u5149\u6ce8\u89c6\u7740\u5979\uff0c\u5979\u867d\u7136\u4ecd\u662f\u5fc3\u5934 \u98a4\u6817\uff0c\u5374\u611f\u5230\u81ea\u5df1\u7684\u5e78\u798f\u4e86\u3002 \u3000\u3000\u674e\u6c81\u6885\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u9996\u6b4c\uff0c\u7136\u800c\u5374\u6ca1\u6709\u4eba\u7528\u6df1\u60c5\u7684\u773c\u5149\u6ce8\u89c6\u7740\u5979\u3002\u91d1\u4e16\u9057\u56de\u4e0d\u56de\u6765\uff0c \u8fd9\u8fd8\u662f\u4e00\u4e2a\u8c1c\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u50cf\u6d41\u6d6a\u7684\u65c5\u4eba\uff0c\u8981\u7b49\u8352\u6f20\u5f00\u82b1\u3001\u51b0\u5ddd\u878d\u5316\u624d\u80af\u505c\u4e0b\u4ed6\u7684\u9a6c\uff1f\u674e\u6c81\u6885 \u773c\u89d2\u6c81\u51fa\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u4e0d\u6562\u56de\u671b\u73e0\u5cf0\uff0c\u4f46\u542c\u5f97\u90a3\u300a\u6d41\u6d6a\u8005\u4e4b\u6b4c\u300b\uff0c\u8fd8\u662f\u5728\u8349\u539f\u4e0a\u4f59\u97f3\u7f2d\u7ed5\u3002"}),showOnHomepage:!0}]}}]);