"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[26741],{78462:(e,n,t)=>{t.r(n),t.d(n,{default:()=>s});var a=t(74848);const s=[{name:"\u7b2c\u4e00\u7ae0\u3000\u98df\u5c38\u9e70",content:(0,a.jsx)(a.Fragment,{children:"\u9e70\u5728\u76d8\u65cb\uff0c\u76d8\u65cb\u5728\u8273\u84dd\u7684\u82cd\u7a79\u4e0b\uff0c\u5728\u7b49\u7740\u98df\u4ed6\u7684\u6b7b\u5c38\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u4e5f\u60f3\u5403\u8fd9\u53ea\u9e70\u3002 \u3000\u3000\u4ed6\u540c\u6837\u9965\u997f\uff0c\u997f\u5f97\u8981\u547d\u3002 \u3000\u3000\u5728\u751f\u5b58\u5df2\u53d7\u5230\u5a01\u80c1\u65f6\uff0c\u5728\u8fd9\u79cd\u5a01\u80c1\u5df2\u5230\u8fbe\u67d0\u79cd\u6781\u9650\u65f6\uff0c\u4e00\u4e2a\u4eba\u548c\u4e00\u53ea\u9e70\u5e76\u6ca1\u6709\u4ec0\u4e48\u5206\u522b\uff0c\u540c\u6837\u90fd\u4f1a\u4e3a\u4e86\u4fdd\u5168\u81ea\u5df1\u800c\u4f24\u5bb3\u5bf9\u65b9\u3002 \u3000\u3000\u4ed6\u5f88\u60f3\u8dc3\u8d77\u6765\u53bb\u6293\u8fd9\u53ea\u9e70\uff0c\u5f88\u60f3\u627e\u4e2a\u77f3\u5757\u5c06\u8fd9\u53ea\u9e70\u51fb\u843d\uff0c\u5e73\u65f6\u90fd\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5df2\u7cbe\u75b2\u529b\u7aed\uff0c\u8fde\u624b\u90fd\u5f88\u96be\u62ac\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u5feb\u6b7b\u4e86\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u7684\u670b\u53cb\u5982\u679c\u77e5\u9053\u4ed6\u5df2\u7ecf\u5feb\u6b7b\u4e86\uff0c\u4e00\u5b9a\u4f1a\u6709\u5f88\u591a\u4eba\u4e3a\u6b64\u800c\u5f88\u60ca\u5947\uff0c\u5f88\u60b2\u4f24\uff0c\u5f88\u60cb\u60dc\uff0c\u4e00\u5b9a\u4e5f\u6709\u5f88\u591a\u4eba\u4f1a\u5f88\u6109\u5feb\u3002 \u3000\u3000\u4ed6\u59d3\u65b9\uff0c\u53eb\u65b9\u4f1f\uff0c\u5927\u5bb6\u901a\u5e38\u90fd\u53eb\u4ed6\u201c\u5c0f\u65b9\u201d\uff0c\u8981\u547d\u7684\u5c0f\u65b9\u3002 \u3000\u3000\u6709\u65f6\u8fde\u4ed6\u81ea\u5df1\u90fd\u89c9\u5f97\u81ea\u5df1\u5b9e\u5728\u662f\u4e2a\u5f88\u8981\u547d\u7684\u4eba\uff0c\u5947\u602a\u5f97\u8981\u547d\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u5728\u4e00\u5757\u6ca1\u6709\u6c34\u3001\u6ca1\u6709\u751f\u547d\u7684\u5e72\u65f1\u5927\u5730\u4e0a\u6323\u624e\u7740\u884c\u8d70\u4e86\u5341\u51e0\u5929\uff0c\u4ed6\u7684\u7cae\u98df\u548c\u6c34\u90fd\u5df2\u5728\u90a3\u6b21\u98ce\u66b4\u4e2d\u9057\u5931\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u8eab\u4e0a\u53ea\u5269\u4e0b\u4e86\u4e00\u67c4\u4e09\u5c3a\u4e03\u5bf8\u957f\u7684\u5251\u548c\u4e00\u6761\u4e09\u5bf8\u4e03\u5206\u957f\u7684\u4f24\u53e3\uff0c\u552f\u4e00\u966a\u4f34\u5728\u4ed6\u8eab\u65c1\u7684\uff0c\u53ea\u6709\u201c\u8d64\u72ac\u201d\u3002 \u3000\u3000\u201c\u8d64\u72ac\u201d\u662f\u4e00\u5339\u9a6c\uff0c\u662f\u9a6c\u5578\u5cf0\u9001\u7ed9\u4ed6\u7684\u3002 \u3000\u3000\u9a6c\u5578\u5cf0\u662f\u5173\u4e1c\u843d\u65e5\u9a6c\u573a\u7684\u4e3b\u4eba\uff0c\u5bf9\u4e8e\u9a6c\uff0c\u8fdc\u6bd4\u6d6a\u5b50\u5bf9\u5973\u4eba\u8fd8\u6709\u7814\u7a76\uff0c\u5c31\u7b97\u662f\u4e00\u5339\u6700\u987d\u52a3\u7684\u91ce\u9a6c\uff0c\u5230\u4e86\u4ed6\u624b\u91cc\uff0c\u4e5f\u4f1a\u88ab\u8bad\u7ec3\u6210\u826f\u9a79\u3002 \u3000\u3000\u4ed6\u9001\u7ed9\u670b\u53cb\u7684\u90fd\u662f\u597d\u9a6c\uff0c\u53ef\u662f\u73b0\u5728\u8fde\u8fd9\u5339\u4e07\u4e2d\u9009\u4e00\u7684\u597d\u9a6c\u90fd\u5df2\u7ecf\u5feb\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u8f7b\u8f7b\u62cd\u7740\u5b83\u7684\u80cc\uff0c\u5e72\u88c2\u7684\u5634\u89d2\u5c45\u7136\u4eff\u4f5b\u8fd8\u5e26\u7740\u5fae\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u6211\u4e5f\u4e0d\u80fd\u6b7b\uff0c\u6211\u4eec\u8fde\u8001\u5a46\u90fd\u6ca1\u6709\u5a36\u5230\uff0c\u600e\u4e48\u80fd\u6b7b\uff1f\u201d \u3000\u3000\u70c8\u65e5\u5982\u706b\u7130\uff0c\u5927\u5730\u5982\u70d8\u7089\uff0c\u6240\u6709\u7684\u751f\u547d\u90fd\u5df2\u88ab\u70e4\u7126\u4e86\uff0c\u51e0\u767e\u91cc\u4e4b\u5185\uff0c\u90fd\u770b\u4e0d\u89c1\u4eba\u8e2a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5ffd\u7136\u53d1\u73b0\u6709\u4e2a\u4eba\u5728\u540e\u9762\u8ddf\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u6ca1\u6709\u542c\u5230\u8fd9\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u4f46\u662f\u4ed6\u53ef\u4ee5\u611f\u89c9\u5f97\u5230\uff0c\u4e00\u79cd\u91ce\u517d\u822c\u5947\u5f02\u800c\u7075\u654f\u7684\u611f\u89c9\u3002 \u3000\u3000\u6709\u65f6\u4ed6\u51e0\u4e4e\u5df2\u611f\u89c9\u5230\u8fd9\u4e2a\u4eba\u8ddd\u79bb\u4ed6\u5df2\u7ecf\u5f88\u8fd1\uff0c\u4ed6\u5c31\u505c\u4e0b\u6765\u7b49\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u6709\u591a\u4e48\u6e34\u671b\u80fd\u89c1\u5230\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u53ef\u60dc\u4ed6\u7b49\u4e0d\u5230\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u4e00\u505c\u4e0b\u6765\uff0c\u8fd9\u4e2a\u4eba\u4eff\u4f5b\u7acb\u523b\u4e5f\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u6c5f\u6e56\u4eba\uff0c\u6709\u670b\u53cb\uff0c\u4e5f\u6709\u4ec7\u654c\uff0c\u5e0c\u671b\u80fd\u5c06\u4ed6\u5934\u9885\u5272\u4e0b\u6765\u7684\u4eba\u4e00\u5b9a\u4e0d\u5c11\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u8ddf\u7740\u4ed6\uff1f\u662f\u4e0d\u662f\u8981\u7b49\u4ed6\u65e0\u529b\u62b5\u6297\u65f6\u6765\u5272\u4ed6\u7684\u5934\u9885\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u624b\uff1f\u662f\u4e0d\u662f\u8fd8\u5728\u63d0\u9632\u7740\u4ed6\u8170\u9645\u7684\u8fd9\u67c4\u5251\uff1f \u3000\u3000\u4ed6\u6ca1\u6709\u4ed4\u7ec6\u53bb\u60f3\u3002 \u3000\u3000\u6709\u65f6\u9965\u997f\u867d\u7136\u80fd\u4f7f\u4eba\u601d\u60f3\u7075\u6d3b\uff0c\u73b0\u5728\u4ed6\u5374\u5df2\u997f\u5f97\u8fde\u96c6\u4e2d\u601d\u60f3\u7684\u529b\u91cf\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u53c8\u6323\u624e\u7740\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u4ed6\u603b\u7b97\u627e\u5230\u4e86\u4e00\u4e2a\u53ef\u4ee5\u906e\u6321\u9633\u5149\u7684\u6c99\u4e18\u3002 \u3000\u3000\u4ed6\u5728\u6c99\u4e18\u540e\u7684\u9634\u5f71\u4e2d\u8eba\u4e86\u4e0b\u6765\uff0c\u90a3\u53ea\u9e70\u98de\u5f97\u66f4\u4f4e\u4e86\uff0c\u597d\u8c61\u5df2\u628a\u4ed6\u5f53\u4f5c\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u4ed6\u8fd8\u4e0d\u60f3\u6b7b\uff0c\u4ed6\u8fd8\u8981\u8ddf\u8fd9\u53ea\u9e70\u62fc\u4e00\u62fc\uff0c\u6597\u4e00\u6597\uff0c\u53ef\u60dc\u4ed6\u7684\u773c\u775b\u5df2\u7ecf\u6e10\u6e10\u5f20\u4e0d\u5f00\u4e86\uff0c\u8fde\u773c\u524d\u7684\u4e8b\u90fd\u5df2\u53d8\u5f97\u819d\u6726\u80e7\u9647\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u770b\u5230\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u636e\u8bf4\u6c99\u6f20\u4e2d\u5e38\u5e38\u4f1a\u51fa\u73b0\u6d77\u5e02\u8703\u697c\uff0c\u4e00\u4e2a\u4eba\u5feb\u6b7b\u7684\u65f6\u5019\uff0c\u4e5f\u5e38\u5e38\u4f1a\u6709\u5e7b\u89c9\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u4ed6\u7684\u5e7b\u89c9\uff0c\u4ed6\u771f\u7684\u770b\u89c1\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5f88\u7626\u5c0f\u7684\u4eba\uff0c\u7a7f\u7740\u4ef6\u6781\u5bbd\u5927\u7684\u767d\u8272\u888d\u5b50\uff0c\u5934\u4e0a\u7f20\u7740\u767d\u5e03\uff0c\u8fd8\u6234\u7740\u9876\u5f88\u5927\u7684\u7b20\u5e3d\uff0c\u5e3d\u6a90\u7684\u9634\u5f71\u4e0b\uff0c\u9732\u51fa\u4e86\u4e00\u5f20\u5c16\u524a\u7684\u8138\uff0c\u4e00\u5f20\u5bbd\u9614\u7684\u5634\u548c\u4e00\u53cc\u79c3\u9e70\u822c\u7684\u773c\u775b\u3002 \u3000\u3000\u5c0f\u65b9\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u786e\u5b9a\u81ea\u5df1\u7edd\u6ca1\u6709\u770b\u9519\u3002\u5728\u8fd9\u7247\u51b7\u9177\u65e0\u60c5\u7684\u6c99\u6f20\u4e0a\uff0c\u80fd\u770b\u5230\u4e00\u4e2a\u540c\u7c7b\u7684\u751f\u547d\uff0c\u5b9e\u5728\u662f\u4ef6\u4ee4\u4eba\u559c\u6b22\u632f\u594b\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u5750\u4e86\u8d77\u6765\uff0c\u5e72\u88c2\u7684\u5634\u53c8\u9732\u51fa\u4e86\u5fae\u7b11\uff0c\u8fd9\u4eba\u5374\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u663e\u5f97\u5f88\u5931\u671b\u3002 \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u5fc3\u91cc\u6709\u4ec0\u4e48\u96be\u8fc7\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u53f9\u6c14\uff1f\u201d \u3000\u3000\u7a7f\u767d\u888d\u7684\u4eba\u53f9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u80fd\u7b11\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u5f88\u5c11\u6709\u4eba\u4f1a\u4e3a\u4e86\u8fd9\u79cd\u7406\u7531\u800c\u53f9\u6c14\u7684\uff0c\u5c0f\u65b9\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u8fd8\u80fd\u7b11\u5f97\u51fa\u6765\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u4e00\u70b9\u4e0d\u597d\u3002\u201d\u8fd9\u4eba\u9053\uff1a\u201c\u8fd8\u80fd\u7b11\u5f97\u51fa\u7684\u4eba\uff0c\u5c31\u4e0d\u4f1a\u6b7b\u5f97\u592a\u5feb\uff01\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u5e0c\u671b\u6211\u5feb\u70b9\u6b7b\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u90fd\u5728\u8ddf\u7740\u6211\uff0c\u5c31\u662f\u5e0c\u671b\u6211\u5feb\u70b9\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u63a5\u7740\u53c8\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6211\u8fde\u4e00\u70b9\u529b\u6c14\u6ca1\u6709\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u6740\u4e86\u6211\uff01\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e3a\u4ec0\u4e48\u5e0c\u671b\u6211\u5feb\u70b9\u6b7b\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u770b\u8d77\u6765\u8fdf\u65e9\u90fd\u8981\u6b7b\u7684\uff0c\u4e0d\u4f46\u6211\u5e0c\u671b\u4f60\u5feb\u70b9\u6b7b\uff0c\u8fd9\u53ea\u9e70\u4e00\u5b9a\u4e5f\u5e0c\u671b\u4f60\u5feb\u70b9\u6b7b\u3002\u201d \u3000\u3000\u9e70\u4ecd\u5728\u4ed6\u4eec\u7684\u5934\u9876\u4e0a\u76d8\u65cb\u3002 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e5f\u8ddf\u8fd9\u53ea\u9e70\u4e00\u6837\uff0c\u5728\u7b49\u7740\u5403\u6211\u7684\u5c38\u4f53\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u65e2\u7136\u4f60\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f60\u7684\u5c38\u4f53\u8fdf\u65e9\u603b\u8981\u8150\u70c2\u7684\uff0c\u8fd9\u53ea\u9e70\u6765\u5403\u4f60\u7684\u5c38\u4f53\uff0c\u5bf9\u4f60\u8fde\u4e00\u70b9\u5bb3\u5904\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u5403\u4f60\uff0c\u6211\u53ea\u60f3\u8981\u4f60\u8eab\u4e0a\u8fd9\u628a\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53cd\u6b63\u6211\u6b7b\u4e86\u4e4b\u540e\u4e5f\u6ca1\u6cd5\u5b50\u628a\u8fd9\u67c4\u5251\u5e26\u8d70\uff0c\u4f60\u5e26\u8d70\u4e86\uff0c\u5bf9\u6211\u4e5f\u6ca1\u4ec0\u4e48\u5bb3\u5904\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u867d\u7136\u5e0c\u671b\u6211\u5feb\u70b9\u6b7b\uff0c\u4f46\u5374\u7edd\u4e0d\u4f1a\u51fa\u624b\u6740\u6211\u3002 \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u4ece\u4e0d\u6740\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53ef\u662f\u522b\u4eba\u5982\u679c\u4e00\u5b9a\u8981\u6b7b\uff0c\u4e5f\u662f\u6ca1\u6cd5\u5b50\u7684\u4e8b\uff0c\u4f60\u7b49\u4ed6\u6b7b\u4e86\u4e4b\u540e\uff0c\u62ff\u4ed6\u4e00\u70b9\u4e1c\u897f\uff0c\u65e0\u8bba\u5bf9\u4efb\u4f55\u4eba\u90fd\u8fde\u4e00\u70b9\u5bb3\u5904\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u8fd9\u4eba\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9053\u7406\u4e00\u5411\u5f88\u5c11\u6709\u4eba\u80fd\u60f3\u5f97\u901a\uff0c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u60f3\u901a\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fae\u7b11\u9053\uff1a\u201c\u6709\u5f88\u591a\u522b\u4eba\u60f3\u4e0d\u901a\u7684\u9053\u7406\uff0c\u6211\u90fd\u80fd\u60f3\u5f97\u901a\uff0c\u6240\u4ee5\u6211\u6d3b\u5f97\u4e00\u5411\u5f88\u5feb\u4e50\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u89e3\u4e0b\u4e86\u8170\u95f4\u7684\u5251\uff0c\u7528\u529b\u629b\u7ed9\u4e86\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u5f88\u610f\u5916\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u8981\u628a\u8fd9\u67c4\u5251\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u8fd9\u662f\u67c4\u5f88\u540d\u8d35\u7684\u5251\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u7684\u773c\u5149\u5b9e\u5728\u4e0d\u9519\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u6b7b\uff1a\u4e3a\u4ec0\u4e48\u5c31\u5148\u628a\u5b83\u9001\u7ed9\u6211\uff1f\u2026 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u81ea\u5df1\u6d3b\u7740\u65f6\u5f88\u6109\u5feb\uff0c\u6211\u4e5f\u5e0c\u671b\u522b\u4eba\u6109\u5feb\u3002 \u3000\u3000\u4ed6\u7b11\u548c\u7684\u786e\u50cf\u662f\u5f88\u6109\u5feb\uff1a\u201c\u6211\u53cd\u6b63\u90fd\u8981\u6b7b\u4e86\uff0c\u8fd9\u628a\u5251\u8fdf\u65e9\u662f\u4f60\u7684\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u70b9\u9001\u7ed9\u4f60\uff0c\u8ba9\u4f60\u4e5f\u6109\u5feb\u4e9b\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u7b49\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u7b49\u6b7b\u7edd\u4e0d\u662f\u4ef6\u6109\u5feb\u7684\u4e8b\uff1b\u4e0d\u7ba1\u662f\u7b49\u81ea\u5df1\u6b7b\uff0c\u8fd8\u662f\u7b49\u522b\u4eba\u6b7b\uff0c\u90fd\u5f88\u4e0d\u6109\u5feb\u3002\u6211\u4ece\u6765\u90fd\u4e0d\u505a\u4e0d\u6109\u5feb\u7684\u4e8b\uff0c\u4e5f\u4e0d\u60f3\u522b\u4eba\u505a\u3002\u201d \u3000\u3000\u8fd9\u4eba\u7528\u4e00\u53cc\u79c3\u9e70\u822c\u7684\u773c\u775b\u77aa\u7740\u4ed6\uff0c\u5ffd\u7136\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u5947\u602a\uff0c\u602a\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5bf9\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u53ef\u662f\u5982\u679c\u4f60\u60f3\u7528\u8fd9\u6cd5\u5b50\u6765\u6253\u52a8\u6211\uff0c\u8ba9\u6211\u6551\u4f60\uff0c\u4f60\u5c31\u9519\u4e86\uff0c\u6211\u8fd9\u4e00\u8f88\u5b50\u4ece\u6765\u4e5f\u6ca1\u6709\u88ab\u4eba\u6253\u52a8\u8fc7\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u8fd9\u4eba\u53c8\u77aa\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u518d\u89c1\u3002\u201d \u3000\u3000\u201c\u518d\u89c1\u201d\u7684\u610f\u601d\uff0c\u901a\u5e38\u90fd\u4e0d\u662f\u771f\u7684\u8fd8\u60f3\u8981\u518d\u89c1\uff0c\u800c\u662f\u6c38\u4e0d\u518d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5e76\u4e0d\u5feb\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u5728\u6ca1\u6709\u5fc5\u8981\u7684\u65f6\u5019\u6d6a\u8d39\u4e00\u5206\u4f53\u529b\u3002 \u3000\u3000\u5251\u8fd8\u7559\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u4f60\u7684\u5251\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5fd8\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u8fd9\u67c4\u5251\u5e26\u8d70\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u82e5\u6b7b\u4e86\uff0c\u6211\u4e00\u5b9a\u4f1a\u628a\u8fd9\u67c4\u5251\u5e26\u8d70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u9001\u7ed9\u4f60\uff0c\u4f60\u53cd\u800c\u4e0d\u8981\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u8f88\u5b50\u4ece\u672a\u8981\u8fc7\u6d3b\u4eba\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u8fd9\u4eba\u53c8\u63a5\u7740\u9053\uff0c\u201c\u4f60\u73b0\u5728\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6d3b\u4eba\u7684\u4e1c\u897f\u4f60\u90fd\u4e0d\u8981\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u7edd\u4e0d\u8981\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53ef\u662f\u6709\u4e9b\u4e1c\u897f\u5374\u662f\u6b7b\u4eba\u7edd\u4e0d\u4f1a\u6709\u7684\uff0c\u8b6c\u5982\u8bf4\uff0c\u53cb\u60c5\u3002\u201d \u3000\u3000\u8fd9\u4eba\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u597d\u8c61\u4ece\u6765\u4e5f\u6ca1\u6709\u542c\u8bf4\u8fc7\u201c\u53cb\u60c5\u201d\u8fd9\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u4ece\u6765\u6ca1\u6709\u670b\u53cb\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7684\u56de\u7b54\u7b80\u77ed\u800c\u5e72\u8106\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5979\u53c8\u5f00\u59cb\u5f80\u524d\u8d70\uff0c\u53ea\u8d70\u51fa\u4e00\u6b65\uff0c\u53c8\u505c\u4e0b\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u542c\u5230\u8fdc\u65b9\u4f20\u6765\u4e86\u4e00\u9635\u9a6c\u8e44\u58f0\uff0c\u542c\u6765\u5c31\u50cf\u662f\u6218\u9f13\u96f7\u9e23\uff0c\u6740\u6c14\u68ee\u68ee\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u770b\u89c1\u6c99\u4e18\u540e\u5c18\u5934\u5927\u8d77\uff0c\u6765\u7684\u663e\u7136\u4e0d\u6b62\u4e00\u5339\u9a6c\u3001\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u5c16\u524a\u51b7\u6f20\u7684\u8138\u4e0a\u7acb\u523b\u9732\u51fa\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u4e5f\u8eba\u4e86\u4e0b\u53bb\uff0c\u8eba\u5728\u6c99\u4e18\u7684\u9634\u5f71\u4e0b\uff0c\u770b\u7740\u90a3\u53ea\u76d8\u65cb\u4f4e\u98de\u7684\u98df\u5c38\u9e70\u3002 \u3000\u3000\u8e44\u58f0\u6e10\u8feb\uff0c\u4eba\u9a6c\u5374\u4ecd\u8ddd\u79bb\u5f88\u8fdc\u3002\u5ffd\u7136\u95f4\uff0c\u4e00\u9635\u5c16\u9510\u7684\u98ce\u58f0\u7834\u7a7a\u547c\u5578\u800c\u6765\u3002 \u3000\u3000\u9e70\u4e5f\u6709\u79cd\u5947\u5f02\u7684\u672c\u80fd\uff0c\u4eff\u4f5b\u4e5f\u5df2\u89c9\u5bdf\u51fa\u4e00\u79cd\u4e0d\u7965\u7684\u51f6\u5146\uff0c\u5df2\u51c6\u5907\u51b2\u5929\u98de\u8d77\u3002 \u3000\u3000\u53ef\u60dc\u5b83\u8fd8\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u98ce\u58f0\u5212\u7a7a\u800c\u8fc7\uff0c\u5b83\u7684\u8eab\u5b50\u7a81\u7136\u5728\u7a7a\u4e2d\u4e00\u6296\uff0c\u659c\u659c\u5730\u843d\u4e86\u4e0b\u6765\uff0c\u5e26\u7740\u4e00\u6839\u7bad\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u6839\u4e09\u5c3a\u957f\u7684\u96d5\u7fce\u7bad\uff0c\u4ece\u5b83\u7684\u5de6\u7ffc\u4e0b\u5c04\u8fdb\u53bb\uff0c\u53f3\u80cc\u4e0a\u7a7f\u51fa\u6765\uff0c\u5b83\u7684\u8eab\u5b50\u4e00\u8dcc\u4e0b\uff0c\u5c31\u518d\u4e5f\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u4eba\u9a6c\u8fdc\u5728\u4e09\u5341\u4e08\u5916\uff0c\u5c04\u51fa\u6765\u7684\u4e00\u7bad\uff0c\u7adf\u80fd\u5c06\u4e00\u53ea\u79c3\u9e70\u5c04\u4e2a\u5bf9\u7a7f\u3002 \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4e0d\u7ba1\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u6211\u90fd\u5e0c\u671b\u4ed6\u6765\u627e\u7684\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u8273\u84dd\u7684\u82cd\u7a79\u4e0b\u4e00\u7247\u6b7b\u5bc2\uff0c\u8e44\u58f0\u8fdc\u8fdc\u505c\u4f4f\uff0c\u626c\u8d77\u7684\u5c18\u6c99\u4e5f\u843d\u4e0b\u3002\u90a3\u53ea\u7b49\u7740\u8981\u5403\u522b\u4eba\u5c38\u4f53\u7684\u79c3\u9e70\uff0c\u5df2\u53ea\u6709\u7b49\u7740\u522b\u4eba\u53bb\u98df\u5b83\u7684\u5c38\u8eab\u3002 \u3000\u3000\u751f\u547d\u4e2d\u6240\u6709\u7684\u8282\u594f\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4eff\u4f5b\u90fd\u5df2\u505c\u987f\uff0c\u53ef\u662f\u751f\u547d\u5fc5\u987b\u7ee7\u7eed\uff0c\u8fd9\u79cd\u505c\u987f\u7edd\u4e0d\u4f1a\u5927\u957f\u3002 \u3000\u3000\u7247\u523b\u540e\u8e44\u58f0\u53c8\u54cd\u8d77\uff0c\u4e09\u5339\u9a6c\u5982\u7bad\u822c\u8f6c\u8fc7\u6c99\u4e18\u76f4\u9a70\u800c\u6765\uff0c\u9996\u5148\u4e00\u9a91\u9a6c\u4e0a\u7684\u4eba\u9ed1\u62ab\u98ce\uff0c\u7ea2\u8170\u5e26\uff0c\u978d\u65c1\u6709\u7bad\uff0c\u624b\u4e2d\u6709\u5f13\uff0c\u8170\u95f4\u6709\u5200\u3002 \u3000\u3000\u5065\u9a6c\u521a\u505c\u4e0b\uff0c\u4ed6\u7684\u4eba\u5df2\u7ad9\u5728\u9a6c\u9996\u524d\uff0c\u4eba\u4e0e\u9a6c\u52a8\u4f5c\u7684\u77eb\u5065\uff0c\u90fd\u8ba9\u4eba\u5f88\u96be\u60f3\u8c61\u5f97\u5230\uff0c\u4ed6\u773c\u795e\u7684\u9510\u5229\u4e5f\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u3002 \u3000\u3000\u201c\u6211\u53eb\u536b\u5929\u9e4f\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4f4e\u6c89\uff0c\u5145\u6ee1\u4e86\u5a01\u4e25\u4e0e\u9a84\u50b2\u3002\u4ed6\u53ea\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u597d\u8c61\u5c31\u5df2\u8db3\u591f\u8bf4\u660e\u4e00\u5207\uff0c\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u5e94\u8be5\u542c\u8bf4\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u65e0\u8bba\u8c01\u542c\u5230\u8fd9\u4e2a\u540d\u5b57\u540e\uff0c\u90fd\u5e94\u8be5\u5bf9\u4ed6\u670d\u4ece\u5c0a\u656c\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u8eba\u5728\u4ed6\u9762\u524d\u7684\u4e24\u4e2a\u4eba\u5374\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5200\u950b\u822c\u7684\u76ee\u5149\u6b63\u5728\u77aa\u7740\u5c0f\u65b9\uff1a\u201c\u770b\u6765\u4f60\u4e00\u5b9a\u5df2\u7ecf\u5728\u6c99\u6f20\u4e2d\u884c\u8d70\u4e86\u5f88\u591a\u5929\uff0c\u4e00\u5b9a\u4e5f\u9047\u4e0a\u4e86\u90a3\u573a\u98ce\u66b4\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u3002 \u3000\u3000\u5bf9\u4ed6\u6765\u8bf4\uff0c\u90a3\u573a\u98ce\u66b4\u7b80\u76f4\u5c31\u50cf\u662f\u573a\u5669\u68a6\u3002 \u3000\u3000\u536b\u5929\u9e4f\u95ee\uff1a\u201c\u8fd9\u4e24\u5929\u4f60\u6709\u6ca1\u6709\u770b\u5230\u8fc7\u4ec0\u4e48\u53ef\u7591\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u770b\u5230\u8fc7\u4e00\u4e2a\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u95ee\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u6c89\u4e86\u4e0b\u53bb\uff0c\u4ed6\u4e0d\u559c\u6b22\u8fd9\u79cd\u73a9\u7b11\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u9047\u5230\u53ef\u7591\u7684\u4eba\uff0c\u6211\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\u5bf9\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u9047\u5230\u53ef\u7591\u7684\u4eba\uff0c\u4f60\u4e00\u5b9a\u4f1a\u5148\u5272\u6389\u4ed6\u4e00\u53ea\u9f3b\u5b50\uff0c\u524a\u6389\u4ed6\u4e00\u53ea\u8033\u6735\uff0c\u903c\u95ee\u4ed6\u7684\u6765\u5386\uff0c\u7136\u540e\u518d\u4e00\u5200\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8fd8\u8981\u8bf4\u81ea\u5df1\u662f\u4e2a\u53ef\u7591\u7684\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u4e0d\u8bf4\u90fd\u4e00\u6837\uff0c\u50cf\u6211\u8fd9\u6837\u7684\u4eba\u5982\u679c\u8fd8\u4e0d\u53ef\u7591\uff0c\u8fd8\u6709\u8c01\u53ef\u7591\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u5389\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u8981\u6211\u7528\u8fd9\u79cd\u6cd5\u5b50\u5bf9\u4ed8\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53cd\u6b63\u6211\u5df2\u7ecf\u5feb\u6b7b\u4e86\uff0c\u968f\u4fbf\u4f60\u7528\u4ec0\u4e48\u6cd5\u5b50\u5bf9\u4ed8\u6211\u90fd\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f46\u662f\u4f60\u53ef\u4ee5\u4e0d\u5fc5\u6b7b\u7684\uff0c\u53ea\u8981\u6709\u4e00\u58f6\u6c34\u3001\u4e00\u5757\u8089\uff0c\u808c\u80fd\u6551\u6d3b\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u6709\u6c34\uff0c\u4e5f\u6709\u8089\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6c42\u6211\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6c42\u4f60\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u53ef\u4ee5\u6551\u4f60\u7684\u547d\uff01\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u82e5\u80af\u6551\u6211\uff0c\u7528\u4e0d\u7740\u6211\u6c42\u4f60\uff1b\u4f60\u82e5\u4e0d\u80af\uff0c\u6211\u6c42\u4f60\u4e5f\u6ca1\u7528\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u76ef\u7740\u4ed6\uff0c\u5168\u8eab\u4e0a\u4e0b\u597d\u8c61\u8fde\u4e00\u70b9\u52a8\u4f5c\u90fd\u6ca1\u6709\uff0c\u4f46\u662f\u5ffd\u7136\u95f4\u4ed6\u7684\u5f13\u5df2\u5f15\u6ee1\uff0c\u7bad\u5df2\u5728\u5f26\uff0c\u201c\u98d3\u201d\u7684\uff0c\u4e00\u679d\u7bad\u5c04\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u52a8\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u8fd9\u4e00\u7bad\u7684\u76ee\u6807\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u8fd9\u4e00\u7bad\u5c04\u7684\u662f\u90a3\u5c16\u8138\u9e70\u773c\u7684\u767d\u888d\u4eba\uff0c\u5c04\u7684\u662f\u4ed6\u81f4\u547d\u7684\u8981\u5bb3\u3002 \u3000\u3000\u536b\u5929\u9e4f\u597d\u8c61\u59cb\u7ec8\u90fd\u6ca1\u6709\u770b\u8fc7\u4ed6\u4e00\u773c\uff0c\u4f46\u5374\u8981\u4e00\u7bad\u5c04\u7a7f\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u536b\u5929\u9e4f\u201c\u6012\u7bad\u795e\u5f13\u201d\uff0c\u767e\u53d1\u767e\u4e2d\uff0c\u4ece\u6765\u6ca1\u6709\u5931\u8fc7\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5374\u662f\u4f8b\u5916\u3002 \u3000\u3000\u767d\u888d\u4eba\u53ea\u4f38\u51fa\u4e24\u6839\u624b\u6307\uff0c\u5c31\u5c06\u8fd9\u53ef\u4ee5\u5728\u56db\u5341\u4e08\u5916\u5c04\u7a7f\u98de\u9e70\u7684\u4e00\u7bad\u5939\u4f4f\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7684\u77b3\u5b54\u805a\u7136\u6536\u7f29\uff0c\u77b3\u5b54\u91cc\u5ffd\u7136\u95ea\u51fa\u4e86\u5200\u5149\u3002 \u3000\u3000\u8ddf\u7740\u4ed6\u6765\u7684\u4e24\u9a91\u52b2\u88c5\u5c11\u5e74\u8170\u7554\u7684\u65cb\u98ce\u5200\u5df2\u51fa\u9798\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5ffd\u7136\u6325\u624b\uff0c\u7adf\u4ee5\u638c\u4e2d\u7684\u94c1\u80cc\u5f13\u51fb\u843d\u4e86\u4ed6\u4eec\u624b\u91cc\u7684\u5200\u3002 \u3000\u3000\u5c11\u5e74\u6014\u4f4f\u3002 \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u51ed\u4f60\u4eec\u4e5f\u6562\u5728\u4ed6\u9762\u524d\u62d4\u5200\uff1f\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u767d\u888d\u4eba\uff0c\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a\u201c\u4f46\u662f\u4f60\u82e5\u4ee5\u4e3a\u4f60\u8eba\u5728\u5730\u4e0a\u88c5\u6b7b\u5c31\u53ef\u4ee5\u8ba9\u6211\u8ba4\u4e0d\u51fa\u4f60\uff0c\u4f60\u4e5f\u9519\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u535c\u9e70\uff01\u201d \u3000\u3000\u535c\u9e70\uff01 \u3000\u3000\u5c0f\u65b9\u7684\u773c\u775b\u7741\u5927\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u773c\u775b\u90fd\u4f1a\u7741\u5927\u7684\uff0c\u56e0\u4e3a\u6c5f\u6e56\u4e2d\u51e0\u4e4e\u5df2\u6ca1\u6709\u6bd4\u4ed6\u66f4\u795e\u79d8\u7684\u4eba\u3002 \u3000\u3000\u5728\u4ed6\u591a\u59ff\u591a\u91c7\u7684\u4e00\u751f\u4e2d\u6709\u8bb8\u591a\u6545\u4e8b\uff0c\u6bcf\u4e00\u4e2a\u6545\u4e8b\u90fd\u5145\u6ee1\u4e86\u795e\u79d8\u7684\u4f20\u5947\u3002 \u3000\u3000\u5c0f\u65b9\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4eca\u5929\u6211\u603b\u7b97\u89c1\u5230\u4e86\u535c\u9e70\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u8ddf\u4ed6\u6709\u4ec7\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u8981\u8bd5\u8bd5\u4ed6\u7a76\u7adf\u662f\u4e0d\u662f\u535c\u9e70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5982\u679c\u4ed6\u662f\u535c\u9e70\uff0c\u5c31\u7edd\u4e0d\u4f1a\u6b7b\u5728\u4f60\u7684\u7bad\u4e0b\uff1b\u5982\u679c\u4ed6\u6b7b\u4e86\uff0c\u5c31\u7edd\u4e0d\u4f1a\u662f\u535c\u9e70\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5982\u679c\u4ed6\u6b7b\u4e86\uff0c\u6b7b\u7684\u53ea\u4e0d\u8fc7\u662f\u4e2a\u65e0\u8db3\u8f7b\u91cd\u7684\u4eba\u3002\u2018\u6012\u7bad\u795e\u5f13\u65a9\u9b3c\u5200\u2019\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u6740\u9519\u4e2a\u628a\u4eba\u6709\u4ec0\u4e48\u5173\u7cfb\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u4e3a\u4e86\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\uff0c\u5c31\u7b97\u6740\u9519\u4e09\u4e94\u767e\u4e2a\u4eba\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8038\u7136\u9053\uff1a\u201c\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\uff1f\u54ea\u91cc\u6765\u7684\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u77e5\u9053\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff0c\u5374\u4e0d\u77e5\u9053\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u662f\u4e5d\u6708\u5341\u516d\uff0c\u8ddd\u79bb\u94c1\u7ffc\u60e8\u6b7b\uff0c\u9ec4\u91d1\u5931\u52ab\u7684\u65f6\u5019\u624d\u4e09\u56db\u5929\uff0c\u8fd9\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u5de8\u6848\uff0c\u6c5f\u6e56\u4e2d\u8fd8\u6ca1\u6709\u4eba\u77e5\u9053\u3002 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8ba4\u4e3a\u4ed6\u77e5\u9053\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u535c\u5927\u516c\u5b50\u662f\u5343\u91d1\u4e4b\u4f53\uff0c\u82e5\u4e0d\u662f\u4e3a\u4e86\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\uff0c\u600e\u4e48\u4f1a\u5230\u8fd9\u65e2\u65e0\u9187\u9152\u3001\u4e5f\u6ca1\u6709\u7f8e\u4eba\u7684\u7a77\u8352\u4e4b\u5730\u6765\uff1f\u201d \u7b2c\u4e8c\u7ae0\u3000\u6012\u7bad \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5bf9\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u535c\u5927\u516c\u5b50\u6325\u624b\u5343\u91d1\uff0c\u89c6\u94b1\u8d22\u5982\u7caa\u571f\uff0c\u82e5\u4e0d\u662f\u56e0\u4e3a\u5e38\u5e38\u6709\u8fd9\u79cd\u5916\u5feb\uff0c\u54ea\u91cc\u6765\u7684\u90a3\u4e48\u591a\u9ec4\u91d1\u8ba9\u4ed6\u6325\u624b\u6563\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5bf9\u3002\u201d\u4ed6\u60f3\u4e86\u60f3\uff0c\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u53ea\u6709\u4e00\u70b9\u4e0d\u5927\u5bf9\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u7a76\u7adf\u6709\u591a\u5c11\uff1f\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u6211\u4ece\u6765\u90fd\u6ca1\u6709\u770b\u8fc7\u8fd9\u4e48\u591a\u91d1\u5b50\uff0c\u6211\u53ea\u77e5\u9053\u5c31\u7b97\u6709\u4eba\u628a\u8fd9\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u9001\u7ed9\u6211\uff0c\u6211\u4e5f\u7edd\u5bf9\u642c\u4e0d\u8d70\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fd9\u4f4d\u535c\u5927\u516c\u4e8e\u4e00\u4e2a\u4eba\u5c31\u80fd\u628a\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u642c\u8d70\u53f6 \u3000\u3000\u536b\u5929\u9e4f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u662f\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u6765\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u77b3\u5b54\u53c8\u5f00\u59cb\u6536\u7f29\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6211\u7684\u5f00\u9500\u4e00\u5411\u5f88\u5927\uff0c\u8fd9\u70b9\u91d1\u5b50\u6211\u6b63\u597d\u7528\u5f97\u7740\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u8bf4\u9053\uff1a\u201c\u662f\u4e09\u5341\u4e07\u4e24\uff0c\u4e0d\u662f\u4e00\u70b9\u3002\u201d \u3000\u3000\u535c\u9e70\u5c45\u7136\u4e5f\u627f\u8ba4\uff0c\u9053\uff1a\u201c\u7684\u786e\u4e0d\u662f\u4e00\u70b9\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u6279\u9ec4\u91d1\u65e0\u8bba\u843d\u5728\u8c01\u624b\u91cc\uff0c\u8981\u628a\u5b83\u85cf\u8d77\u6765\u90fd\u5f88\u96be\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u7684\u786e\u5f88\u96be\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6cd5\u5b50\u85cf\u8d77\u6765\uff0c\u5c31\u7edd\u5bf9\u6ca1\u6cd5\u5b50\u8fd0\u8d70\u3002\u201d \u3000\u3000\u52ab\u6848\u53d1\u751f\u7684\u7b2c\u4e09\u5929\u65e9\u4e0a\uff0c\u8fd9\u5730\u533a\u5df2\u4fa6\u9a91\u5bc6\u5e03\uff0c\u5c31\u7b97\u662f\u8981\u8fd0\u4e09\u767e\u4e24\u9ec4\u91d1\u51fa\u53bb\u90fd\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u536b\u5929\u9e4f\u76ef\u7740\u535c\u9e70\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6240\u4ee5\u6211\u770b\u4f60\u8fd8\u662f\u628a\u5b83\u4ea4\u51fa\u6765\u7684\u597d\u3002\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u7528\u5e3d\u5b50\u76d6\u4f4f\u4e86\u8138\uff0c\u4e0d\u7406\u4f1a\u4ed6\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u600e\u80fd\u77e5\u9053\u8fd9\u6279\u9ec4\u91d1\u5728\u4ed6\u624b\u91cc\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u62a4\u9001\u8fd9\u6279\u9ec4\u91d1\u7684\u4eba\u662f\u94c1\u7ffc\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u94c1\u80c6\u795e\u67aa\u94c1\u7ffc\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u70b9\u70b9\u5934\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u80fd\u6740\u4ed6\u7684\u4eba\u6709\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u9ec4\u91d1\u5931\u52ab\uff0c\u94c1\u7ffc\u548c\u4ed6\u7684\u94c1\u8840\u4e09\u5341\u516d\u9a91\u90fd\u5df2\u60e8\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u8fd9\u4f4d\u535c\u5927\u516c\u5b50\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e00\u53ea\u624b\u63e1\u5f13\uff0c\u53e6\u4e00\u53ea\u624b\u5df2\u63e1\u4f4f\u4e86\u4ed6\u8170\u7554\u7684\u5200\u67c4\u3002 \u3000\u3000\u4ed6\u7684\u5200\u8fd8\u672a\u51fa\u9798\uff0c\u53ef\u662f\u4ed6\u7684\u77b3\u5b54\u4e2d\u5df2\u7ecf\u9732\u51fa\u4e86\u6bd4\u5200\u950b\u66f4\u53ef\u6015\u7684\u6740\u673a\u3002 \u3000\u3000\u5c0f\u65b9\u5b9e\u5728\u5f88\u60f3\u628a\u535c\u9e70\u8138\u4e0a\u76d6\u7740\u7684\u5e3d\u5b50\u6380\u8d77\u6765\uff0c\u8ba9\u4ed6\u770b\u770b\u8fd9\u53cc\u773c\u775b\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e00\u5200\u51fa\u624b\uff0c\u8fde\u9b3c\u90fd\u80fd\u65a9\uff0c\u4f55\u51b5\u662f\u4e00\u4e2a\u8138\u4e0a\u76d6\u7740\u9876\u5e3d\u5b50\u7684\u4eba\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u58f6\u4e2d\u8fd8\u6709\u7bad\uff1b\u6bd4\u96f7\u9706\u66f4\u5a01\uff0c\u6bd4\u95ea\u7535\u66f4\u5feb\u7684\u6012\u7bad\u3002 \u3000\u3000\u5e3d\u5b50\u8fd8\u5728\u8138\u4e0a\uff0c\u5200\u4ecd\u5728\u9798\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u6c99\u4e18\u540e\u4f20\u6765\u4e00\u58f0\u51c4\u5389\u7684\u60e8\u53eb\u58f0\u3002 \u3000\u3000\u201c\u77f3\u7c73\uff0c\u67ef\u62c9\u67ef\u7f57\uff01\u201d \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u542c\u4e0d\u61c2\u8fd9\u516d\u4e2a\u5b57\u7684\u610f\u601d\uff0c\u53ef\u662f\u4ed6\u80fd\u542c\u5f97\u51fa\u547c\u58f0\u5145\u6ee1\u4e86\u6050\u60e7\uff0c\u4e00\u79cd\u53ef\u4ee5\u5c06\u4eba\u7684\u9b42\u9b44\u90fd\u6495\u88c2\u7684\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u542c\u5230\u8fd9\u58f0\u60e8\u53eb\u65f6\uff0c\u536b\u5929\u9e4f\u5df2\u7bad\u4e00\u822c\u7a9c\u4e86\u51fa\u53bb\uff0c\u8f6c\u8fc7\u4e86\u6c99\u4e18\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5df2\u7ecf\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\u4e86\uff0c\u4f46\u662f\u4ed6\u4e00\u5411\u5f88\u597d\u5947\uff0c\u201c\u597d\u5947\u201d\u4e5f\u662f\u6709\u9650\u51e0\u6837\u80fd\u6fc0\u52a8\u4eba\u5fc3\u7684\u529b\u91cf\u4e4b\u4e00\uff0c\u4e5f\u80fd\u6fc0\u53d1\u4eba\u7c7b\u6700\u539f\u59cb\u7684\u6f5c\u529b\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u4e5f\u8df3\u4e86\u8d77\u6765\uff0c\u8ddf\u7740\u535c\u9e70\u8f6c\u8fc7\u6c99\u4e18\u3002 \u3000\u3000\u4e00\u8f6c\u6c99\u4e18\uff0c\u4ed6\u5c31\u770b\u5230\u4e86\u4e00\u5e55\u4ed6\u8fd9\u4e00\u751f\u6c38\u8fdc\u90fd\u5fd8\u4e0d\u4e86\u7684\u666f\u8c61\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u4ed6\u7684\u80c3\u5df2\u7ecf\u7a7a\u4e86\uff0c\u4ed6\u5f88\u53ef\u80fd\u4f1a\u5455\u5410\u3002 \u3000\u3000\u9a6c\u5728\u72c2\u5954\uff0c\u4eba\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7684\u65cb\u98ce\u4e09\u5341\u516d\u628a\u5feb\u5200\uff0c\u5df2\u5012\u4e0b\u4e09\u5341\u56db\u4e2a\uff0c\u5012\u5728\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5200\u8fd8\u672a\u51fa\u9798\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u6c5f\u6e56\u4e2d\u6781\u6709\u540d\u7684\u5feb\u5200\u624b\uff0c\u53ef\u662f\u4ed6\u4eec\u6765\u4e0d\u53ca\u62d4\u5200\uff0c\u5c31\u5df2\u60e8\u6b7b\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u6765\u7adf\u4e0d\u50cf\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u7684\uff0c\u800c\u662f\u6b7b\u5728\u4e00\u53ea\u732b\u7684\u722a\u4e0b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u8138\u4e0a\uff0c\u90fd\u6709\u4e09\u6761\u4eff\u4f5b\u662f\u732b\u722a\u6293\u51fa\u6765\u7684\u8840\u75d5\u3002 \u3000\u3000\u4e00\u4e2a\u88c5\u675f\u5947\u5f02\u7684\u85cf\u4eba\uff0c\u4e00\u5f20\u4e45\u5df2\u88ab\u98ce\u971c\u4fb5\u8680\u5f97\u5982\u540c\u8d25\u9769\u822c\u7684\u8138\u5df2\u56e0\u6050\u60e7\u800c\u626d\u66f2\uff0c\u6b63\u8dea\u5728\u5730\u4e0a\uff0c\u9ad8\u4e3e\u7740\u53cc\u624b\uff0c\u5411\u5929\u60e8\u547c\u3002 \u3000\u3000\u201c\u77f3\u7c73\uff0c\u67ef\u62c9\u67ef\u7f57\uff01\u201d \u3000\u3000\u82cf\u739b\u4eca\u5e74\u4e94\u5341\u4e00\u5c81\uff0c\u4ece\u4e09\u5341\u56db\u5c81\u5c31\u5df2\u5f00\u59cb\u505a\u6c49\u4eba\u7684\u5411\u5bfc\uff0c\u9664\u4e86\u4ed6\u7684\u65cf\u5144\u9a6c\u9c81\u5916\uff0c\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4\u4ed6\u66f4\u719f\u6089\u8fd9\u7247\u5927\u6c99\u6f20\u3002 \u3000\u3000\u65e0\u60c5\u7684\u6c99\u6f20\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u8352\u5510\u7684\u5669\u68a6\uff0c\u6709\u65f6\u867d\u7136\u4e5f\u4f1a\u51fa\u73b0\u4e9b\u7f8e\u4e3d\u7684\u5e7b\u666f\u548c\u4ee4\u4eba\u75af\u72c2\u7684\u6d77\u5e02\u8703\u697c\uff0c\u4f46\u662f\u6700\u540e\u7684\u7ec8\u7ed3\u8fd8\u662f\u6b7b\u3002 \u3000\u3000\u5bf9\u4ed6\u6765\u8bf4\uff0c\u6b7b\u5df2\u7ecf\u4e0d\u80fd\u7b97\u662f\u4ef6\u53ef\u6015\u7684\u4e8b\uff0c\u4ed6\u5df2\u89c1\u8fc7\u65e0\u6570\u6b7b\u4eba\u767d\u9aa8\u3002 \u3000\u3000\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u8fc7\u4ed6\u5982\u6b64\u6050\u60e7\uff0c\u4ed6\u6015\u5f97\u5168\u8eab\u90fd\u5728\u62bd\u7b4b\u3002 \u3000\u3000\u6050\u60e7\u4e5f\u662f\u79cd\u4f1a\u4f20\u67d3\u7684\u75be\u75c5\uff0c\u5c31\u50cf\u662f\u761f\u75ab\uff0c\u770b\u89c1\u522b\u4eba\u5bb3\u6015\uff0c\u81ea\u5df1\u4e5f\u4f1a\u83ab\u540d\u5176\u5999\u5730\u5bb3\u6015\u8d77\u6765\u3002 \u3000\u3000\u4f55\u51b5\u540d\u9707\u6c5f\u6e56\u7684\u65cb\u98ce\u4e09\u5341\u516d\u5200\uff0c\u7adf\u5728\u7247\u523b\u95f4\u51e0\u4e4e\u5168\u90fd\u60e8\u6b7b\uff0c\u8fd9\u4ef6\u4e8b\u672c\u8eab\u5c31\u5f88\u53ef\u6015\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u7684\u624b\u811a\u90fd\u5df2\u51b0\u51b7\uff0c\u51b7\u6c57\u5df2\u7ecf\u4ece\u9f3b\u5c16\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u8df3\u8d77\u6765\u7684\u65f6\u5019\uff0c\u535c\u9e70\u8fd8\u8eba\u7740\uff0c\u8138\u4e0a\u8fd8\u76d6\u7740\u9876\u5e3d\u5b50\uff0c\u7b49\u4ed6\u8f6c\u8fc7\u6c99\u4e18\u65f6\uff0c\u535c\u9e70\u5df2\u7ecf\u5728\u8fd9\u91cc\u4e86\u3002 \u3000\u3000\u535c\u9e70\u7684\u8138\u4e0a\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u535c\u9e70\u8eab\u4e0a\u6bcf\u6839\u8840\u7ba1\u91cc\u6d41\u7740\u7684\u597d\u8c61\u90fd\u4e0d\u662f\u8840\uff0c\u662f\u51b0\u6c34\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u65b9\u5374\u542c\u89c1\u4ed6\u5634\u91cc\u4e5f\u5728\u5583\u5583\u4f4e\u8bed\uff0c\u8bf4\u7684\u4e5f\u662f\u90a3\u9b54\u5492\u822c\u7684\u516d\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u77f3\u7c73\uff0c\u67ef\u62c9\u67ef\u7f57\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\uff1a\u201c\u4f60\u61c2\u4e0d\u61c2\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6211\u61c2\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u80fd\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u77f3\u7c73\u7684\u610f\u601d\uff0c\u662f\u4e0d\u662f\u7528\u77f3\u5934\u505a\u6210\u7684\u7c73\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u77f3\u5934\u4e0d\u662f\u7c73\uff0c\u77f3\u5934\u4e0d\u80fd\u505a\u7c73\uff0c\u77f3\u5934\u4e0d\u80fd\u5403\uff0c\u77f3\u5934\u5982\u679c\u80fd\u5403\uff0c\u4e16\u4e0a\u5c31\u4e0d\u4f1a\u997f\u6b7b\u4eba\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53ef\u662f\u6211\u542c\u89c1\u4ed6\u521a\u624d\u8bf4\u7684\u660e\u660e\u662f\u2018\u77f3\u7c73\u2019\uff0c\u4f60\u521a\u624d\u4e5f\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u90a3\u662f\u85cf\u8bed\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5728\u85cf\u8bed\u91cc\u77f3\u7c73\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u662f\u732b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u732b\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u732b\uff01\u201d \u3000\u3000\u732b\u662f\u79cd\u5f88\u67d4\u987a\u3001\u5f88\u5e38\u89c1\u7684\u52a8\u7269\uff0c\u8fde\u516d\u4e03\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u90fd\u6562\u628a\u732b\u62b1\u5728\u6000\u91cc\u3002 \u3000\u3000\u732b\u5403\u9c7c\u3002 \u3000\u3000\u4eba\u4e5f\u5403\u9c7c\uff0c\u5403\u5f97\u6bd4\u732b\u8fd8\u591a\u3002 \u3000\u3000\u732b\u5403\u8001\u9f20\u3002 \u3000\u3000\u53ef\u662f\u6709\u5f88\u591a\u4eba\u90fd\u6015\u8001\u9f20\uff0c\u5374\u5f88\u5c11\u6709\u4eba\u6015\u732b\u3002 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u732b\u6709\u4ec0\u4e48\u53ef\u6015\uff1f\u8fde\u9c7c\u90fd\u4e0d\u6015\u732b\uff0c\u9c7c\u6015\u662f\u4eba\uff0c\u6293\u9c7c\u7684\u4eba\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u5bf9\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53ea\u6709\u8001\u9f20\u624d\u6015\u732b\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u9519\u3002\u201d \u3000\u3000\u4ed6\u79c3\u9e70\u822c\u7684\u9510\u773c\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u5947\u602a\u7684\u5149\u8292\uff0c\u4eff\u4f5b\u5728\u8df3\u671b\u7740\u8fdc\u65b9\u67d0\u4e00\u5904\u5145\u6ee1\u4e86\u795e\u3001\u5996\u5f02\u800c\u90aa\u6076\u7684\u5730\u65b9\u3002 \u3000\u3000\u5c0f\u65b9\u4eff\u4f5b\u4e5f\u88ab\u4ed6\u8fd9\u79cd\u795e\u60c5\u6240\u8ff7\u60d1\uff0c\u7adf\u6ca1\u6709\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u536b\u5929\u9e4f\u8fd8\u5728\u60f3\u6cd5\u5b50\u4f7f\u82cf\u739b\u6062\u590d\u5e73\u9759\uff0c\u8ba9\u4ed6\u8bf4\u51fa\u521a\u624d\u7684\u7ecf\u8fc7\uff0c\u4f46\u662f\u5c31\u8fde\u85cf\u4eba\u6700\u559c\u7231\u7684\u9752\u88f8\u9152\uff0c\u90fd\u65e0\u6cd5\u4f7f\u4ed6\u5e73\u9759\u4e0b\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u535c\u9e70\u624d\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6545\u8001\u76f8\u4f20\uff0c\u5728\u5927\u5730\u7684\u8fb9\u7f18\u6709\u4e00\u5904\u6bd4\u5929\u8fd8\u9ad8\u7684\u5c71\u5cf0\uff0c\u5c71\u4e0a\u4e0d\u4f46\u6709\u4e07\u53e4\u4e0d\u5316\u7684\u51b0\u96ea\uff0c\u800c\u4e14\u8fd8\u6709\u79cd\u6bd4\u6076\u9b3c\u66f4\u53ef\u6015\u7684\u5996\u9b54\u3002\u2026 \u3000\u3000\u5c0f\u65b9\u9053\uff0c\u201c\u4f60\u8bf4\u7684\uff0c\u662f\u4e0d\u662f\u5723\u6bcd\u4e4b\u6c34\u5cf0\uff1f\u201d \u3000\u3000\u535c\u9e70\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8fd9\u79cd\u5996\u9b54\u5c31\u662f\u732b\uff0c\u867d\u7136\u5b83\u8eab\u5b50\u5df2\u70bc\u6210\u4eba\u5f62\uff0c\u5b83\u7684\u5934\u8fd8\u662f\u732b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u67ef\u62c9\u67ef\u7f57\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u535c\u9e70\u8bf4\u9053\uff1a\u201c\u662f\u5f3a\u76d7\uff0c\u4e00\u79cd\u6700\u51f6\u6076\u7684\u5f3a\u76d7\uff0c\u4e0d\u4f46\u8981\u52ab\u4eba\u7684\u94b1\u8d22\uff0c\u8fd8\u8981\u5403\u4eba\u7684\u8840\u8089\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u4eec\u5927\u90e8\u5206\u90fd\u662f\u85cf\u8fb9\u6df1\u5c71\u4e2d\u7684\u2018\u679c\u5c14\u6d1b\u4eba\u2019\uff0c\u4ed6\u4eec\u7684\u751f\u6d3b\u548c\u8bed\u8a00\u90fd\u8ddf\u522b\u4eba\u4e0d\u540c\uff0c\u800c\u4e14\u51f6\u608d\u91ce\u86ee\uff0c\u6bd4\u54c8\u8428\u514b\u4eba\u66f4\u6b8b\u9177\u3002\u201d \u3000\u3000\u6700\u540e\u4ed6\u53c8\u8865\u5145\u9053\uff1a\u201c\u679c\u5c14\u6d1b\u5728\u68b5\u6587\u4e2d\u8fd8\u53e6\u5916\u6709\u79cd\u610f\u601d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u602a\u5934\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u732b\u5934\u4eba\u8eab\u7684\u5996\u9b54\uff0c\u6b8b\u9177\u91ce\u86ee\u7684\u602a\u5934\u5f3a\u76d7\u3002\u201d \u3000\u3000\u4ed6\u770b\u770b\u82cf\u739b\uff1a\u201c\u96be\u602a\u8fd9\u4e2a\u4eba\u6015\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u73b0\u5728\u8fde\u6211\u90fd\u6709\u70b9\u5bb3\u6015\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5ffd\u7136\u62c9\u8d77\u82cf\u739b\u4e00\u53ea\u4e0d\u505c\u5728\u62bd\u7b4b\u7684\u624b\uff0c\u628a\u4ed6\u7684\u624b\u6307\u4e00\u6839\u6839\u6273\u5f00\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u7d27\u7d27\u63e1\u7740\u4e00\u9762\u5c0f\u65d7\uff0c\u4e0a\u9762\u7ee3\u7740\u7684\u8d6b\u7136\u6b63\u662f\u4e00\u4e2a\u732b\u9996\u4eba\u8eab\u7684\u5996\u9b54\u3002 \u3000\u3000\u82cf\u739b\u53c8\u8dea\u4e0b\u6765\uff0c\u4e94\u4f53\u6295\u5730\uff0c\u5411\u8fd9\u9762\u65d7\u819c\u62dc\uff0c\u5634\u91cc\u5ff5\u5ff5\u6709\u8bcd\uff0c\u6bcf\u4e00\u53e5\u8bdd\u4e2d\u90fd\u6709\u540c\u6837\u516d\u4e2a\u5b57\uff1a\u201c\u77f3\u7c73\uff0c\u67ef\u62c9\u67ef\u7f57\uff0f \u3000\u3000\u73b0\u5728\uff0c\u5c0f\u65b9\u603b\u7b97\u5df2\u660e\u767d\u8fd9\u516d\u4e2a\u5b57\u7684\u610f\u601d\u2014\u2014\u732b\u76d7\uff01 \u3000\u3000\u73b0\u5728\u82cf\u739b\u603b\u7b97\u9547\u9759\u4e0b\u6765\uff0c\u8bf4\u51fa\u4e86\u4ed6\u521a\u624d\u4eb2\u773c\u770b\u89c1\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u4e09\u5341\u56db\u540d\u65cb\u98ce\u5feb\u5200\u624b\uff0c\u5c31\u662f\u6b7b\u5728\u201c\u732b\u76d7\u201d\u624b\u91cc\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u5c31\u50cf\u662f\u9b3c\u9b42\u822c\u5ffd\u7136\u51fa\u73b0\uff0c\u4ed6\u4eec\u7684\u8eab\u4e8e\u662f\u4eba\uff0c\u5934\u662f\u732b\uff0c\u989d\u4e0a\u957f\u7740\u732b\u8033\u822c\u7684\u89d2\u3002 \u3000\u3000\u4ed6\u4eec\u771f\u7684\u6709\u79cd\u5996\u5f02\u800c\u90aa\u6076\u7684\u9b54\u529b\uff0c\u6240\u4ee5\u4e45\u7ecf\u8bad\u7ec3\u7684\u5feb\u5200\u624b\u4eec\uff0c\u8fd8\u6765\u4e0d\u53ca\u62d4\u5200\uff0c\u5c31\u5df2\u60e8\u6b7b\u5728\u4ed6\u4eec\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u4eec\u7559\u4e0b\u82cf\u739b\u8fd9\u6761\u547d\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4eec\u8981\u4ed6\u4f20\u544a\u4e00\u53e5\u8bdd\u7ed9\u536b\u5929\u9e4f\u3002 \u3000\u3000\u2014\u2014\u6740\u4eba\u52ab\u91d1\u7684\u90fd\u662f\u4ed6\u4eec\uff0c\u65e0\u8bba\u8c01\u518d\u8ffd\u67e5\u8fd9\u4ef6\u4e8b\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u6b7b\u4e86\u540e\u8fd8\u8981\u5c06\u4ed6\u7684\u9b42\u9b44\u62d8\u5728\u5723\u6bcd\u4e4b\u6c34\u5c71\u6839\u4e0b\u7684\u51b0\u96ea\u5730\u72f1\u91cc\uff0c\u53d7\u4e07\u5e74\u5bd2\u98ce\u523a\u9aa8\u4e4b\u82e6\uff0c\u6c38\u4e16\u4e0d\u5f97\u8d85\u751f\u3002 \u3000\u3000\u5929\u8272\u5df2\u6e10\u6e10\u6697\u4e86\uff0c\u5929\u5730\u95f4\u4eff\u4f5b\u5ffd\u7136\u5145\u6ee1\u4e86\u4e00\u79cd\u90aa\u6076\u8083\u6740\u7684\u5bd2\u610f\u3002 \u3000\u3000\u5c0f\u65b9\u5f88\u60f3\u627e\u70b9\u9752\u7a1e\u9152\u559d\u3002 \u3000\u3000\u65cb\u98ce\u5feb\u5200\u624b\u7684\u8eab\u4e0a\uff0c\u5c31\u7b97\u6ca1\u6709\u9152\uff0c\u81f3\u5c11\u603b\u5e26\u7740\u6c34\uff0c\u73b0\u5728\u5bf9\u4ed6\u4eec\u5df2\u6ca1\u6709\u7528\u3002 \u3000\u3000\u53ef\u662f\u732b\u76d7\u4e0d\u4f46\u593a\u8d70\u4e86\u4ed6\u4eec\u7684\u6027\u547d\uff0c\u8fde\u4ed6\u4eec\u7684\u7f8a\u76ae\u6c34\u888b\u90fd\u5df2\u88ab\u52ab\u8d70\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9759\u9759\u5730\u542c\u82cf\u739b\u8bf4\u5b8c\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u76ef\u7740\u535c\u9e70\u9053\uff1a\u201c\u4f60\u76f8\u4fe1\u4ed6\u8bf4\u7684\u8bdd\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u51fa\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\uff0c\u9053\u201c\u4f60\u76f8\u4fe1\u4e16\u4e0a\u771f\u6709\u90a3\u79cd\u732b\u5934\u4eba\u8eab\u7684\u602a\u7269\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4fe1\uff0c\u53ef\u662f\u6211\u76f8\u4fe1\u90a3\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\uff0c\u4e00\u5b9a\u662f\u88ab\u732b\u76d7\u52ab\u8d70\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u8bf4\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u4eba\u53ea\u8981\u6234\u4e0a\u4e00\u4e2a\u5f62\u5f0f\u50cf\u732b\u5934\u7684\u9762\u5177\uff0c\u5c31\u53ef\u4ee5\u81ea\u79f0\u4e3a\u732b\u76d7\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u4eba\u90fd\u53ef\u4ee5\uff1f\u65e0\u8bba\u4ec0\u4e48\u4eba\u90fd\u53ef\u4ee5\u5728\u4e00\u77ac\u95f4\u6740\u6b7b\u4f60\u4e09\u5341\u56db\u4e2a\u65cb\u98ce\u5feb\u5200\u624b\uff1f\u65e0\u8bba\u4ec0\u4e48\u4eba\u90fd\u53ef\u4ee5\u6740\u6b7b\u94c1\u80c6\u795e\u67aa\u548c\u4ed6\u7684\u94c1\u8840\u4e09\u5341\u516d\u9a91\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5c31\u7b97\u8fd9\u7fa4\u732b\u76d7\u4e0d\u662f\u5996\u9b54\uff0c\u662f\u4eba\uff0c\u4e00\u5b9a\u4e5f\u662f\u4e9b\u6781\u53ef\u6015\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u4f46\u884c\u8e2a\u98d8\u5ffd\uff0c\u800c\u4e14\u4e00\u5b9a\u662f\u6709\u79cd\u8be1\u79d8\u800c\u90aa\u5f02\u7684\u6b66\u529f\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u9053\uff1a\u201c\u6211\u53ea\u76f8\u4fe1\u4e00\u70b9\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4eec\u8981\u6740\u4e00\u4e2a\u4eba\uff0c\u7edd\u4e0d\u662f\u4ef6\u56f0\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u535c\u9e70\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\u4f60\u4e5f\u5e94\u8be5\u660e\u767d\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u5982\u679c\u6211\u662f\u732b\u76d7\uff0c\u73b0\u5728\u4f60\u5c31\u5df2\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u8d70\u4e86\u3002 \u3000\u3000\u6b63\u5728\u4e34\u8d70\u524d\u7684\u90a3\u7247\u523b\u95f4\uff0c\u5c0f\u65b9\u672c\u6765\u4ee5\u4e3a\u4ed6\u4f1a\u51fa\u624b\u7684\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u63e1\u4f4f\u4e86\u4ed6\u7684\u5200\uff0c\u6bcf\u4e00\u4e2a\u6307\u8282\u90fd\u5df2\u56e0\u7528\u529b\u800c\u53d1\u767d\u3002 \u3000\u3000\u4ed6\u7684\u5200\u6cd5\uff0c\u7edd\u5bf9\u53ef\u4ee5\u540d\u5217\u5929\u4e0b\u6240\u6709\u5200\u6cd5\u540d\u5bb6\u7684\u524d\u5341\u4f4d\uff0c\u4ed6\u7684\u65a9\u9b3c\u5200\uff0c\u950b\u5229\u6c89\u91cd\uff0c\u800c\u4e14\u7279\u522b\u52a0\u957f\uff0c\u4ed6\u7684\u4eba\uff0c\u4e5f\u8fdc\u6bd4\u535c\u9e70\u9ad8\u5927\u96c4\u58ee\u3002 \u3000\u3000\u535c\u9e70\u5374\u5f88\u7ea4\u5f31\uff0c\u9664\u4e86\u90a3\u53cc\u79c3\u9e70\u822c\u7684\u9510\u773c\u5916\uff0c\u5176\u4ed6\u7684\u90e8\u5206\u770b\u6765\u90fd\u5f88\u7ea4\u5f31\uff0c\u5c24\u5176\u662f\u4ed6\u7684\u4e00\u53cc\u624b\uff0c\u66f4\u7ea4\u5f31\u5982\u5973\u5b50\u3002 \u3000\u3000\u51e0\u4e4e\u8fde\u5c0f\u65b9\u90fd\u4e0d\u4fe1\u4ed6\u80fd\u63a5\u5f97\u4f4f\u540d\u9707\u5929\u4e0b\u7684\u6012\u7bad\u795e\u5f13\u65a9\u9b3c\u5200\u3002 \u3000\u3000\u4f46\u662f\u536b\u5929\u9e4f\u81ea\u5df1\u7684\u60f3\u6cd5\u5374\u4e0d\u540c\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u8d70\u4e86\uff0c\u5e26\u7740\u4ed6\u201c\u65cb\u98ce\u4e09\u5341\u516d\u5200\u201d\u4e2d\u4ec5\u5b58\u7684\u4e24\u4e2a\u4eba\u8d70\u4e86\uff0c\u8fde\u4e00\u53e5\u8bdd\u90fd\u4e0d\u518d\u8bf4\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u536b\u5929\u9e4f\u65e0\u7591\u662f\u4e2a\u6781\u8c28\u614e\u7684\u4eba\uff0c\u800c\u4e14\u6781\u51b7\u9177\u3002 \u3000\u3000\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u8fde\u770b\u90fd\u6ca1\u6709\u518d\u53bb\u770b\u5730\u4e0a\u7684\u90a3\u4e9b\u5c38\u4f53\uff0c\u4ed6\u4eec\u867d\u7136\u662f\u4ed6\u5b50\u5f1f\uff0c\u53ef\u662f\u5bf9\u4ed6\u5df2\u6ca1\u6709\u7528\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u5fcd\u4e0d\u4f4f\u95ee\u4ed6\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5c06\u4ed6\u4eec\u57cb\u846c\u4e86\u518d\u8d70\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u56de\u7b54\u5c31\u50cf\u4ed6\u505a\u522b\u7684\u4e8b\u4e00\u6837\uff0c\u90fd\u4ee4\u4eba\u65e0\u53ef\u975e\u8bae\u3002 \u3000\u3000\u201c\u6211\u5df2\u7ecf\u57cb\u846c\u5382\u4ed6\u4eec\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u5929\u846c\u3002\u201d \u3000\u3000\u535c\u9e70\u8fd8\u6ca1\u8d70\u3002 \u3000\u3000\u4ed6\u53c8\u8eba\u4e86\u4e0b\u53bb\uff0c\u8eba\u5728\u6c99\u4e18\u540e\u7684\u907f\u98ce\u5904\uff0c\u7528\u90a3\u4ef6\u5bbd\u5927\u7684\u767d\u888d\u5c06\u5168\u8eab\u7d27\u7d27\u88f9\u4f4f\u3002 \u3000\u3000\u6c99\u6f20\u5c31\u50cf\u662f\u4e2a\u6700\u591a\u53d8\u7684\u5973\u4eba\uff0c\u70ed\u7684\u65f6\u5019\u53ef\u4ee5\u4f7f\u4eba\u71c3\u70e7\uff0c\u51b7\u7684\u65f6\u5019\u5374\u53ef\u4ee5\u4f7f\u4eba\u8fde\u8840\u90fd\u7ed3\u51b0\u3002 \u3000\u3000\u4e00\u5230\u4e86\u665a\u4e0a\uff0c\u8fd9\u7247\u9177\u70ed\u5982\u70d8\u7089\u7684\u5927\u6c99\u6f20\u5c31\u4f1a\u53d8\u5f97\u5176\u5bd2\u5f7b\u9aa8\uff0c\u518d\u52a0\u4e0a\u90a3\u79cd\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff0c\u5728\u65e0\u58f0\u65e0\u606f\u4e2d\u5c31\u80fd\u627c\u6740\u5929\u5730\u95ee\u6240\u6709\u7684\u751f\u547d\u3002\u6ca1\u6709\u4eba\u613f\u610f\u5192\u8fd9\u79cd\u9669\u3002 \u3000\u3000\u73b0\u5728\u5929\u8272\u521a\u521a\u6697\u4e0b\uff0c\u535c\u9e70\u663e\u7136\u5df2\u51c6\u5907\u7559\u5728\u8fd9\u91cc\u5ea6\u8fc7\u65e0\u60c5\u7684\u957f\u591c\u3002 \u3000\u3000\u5c0f\u65b9\u5728\u4ed6\u65c1\u8fb9\u5750\u4e0b\u6765\uff0c\u5ffd\u7136\u5bf9\u4ed6\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u62b1\u6b49\u5f97\u5f88\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u62b1\u6b49\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u56e0\u4e3a\u660e\u5929\u65e9\u4e0a\u9192\u6765\u65f6\uff0c\u6211\u4e00\u5b9a\u8fd8\u662f\u6d3b\u7740\u7684\uff0c\u4f60\u8981\u7b49\u6211\u6b7b\uff0c\u4e00\u5b9a\u8fd8\u8981\u7b49\u5f88\u4e45\u3002\u201d \u3000\u3000\u4ed6\u5df2\u7ecf\u627e\u5230\u4e86\u90a3\u53ea\u66fe\u7ecf\u60f3\u98df\u4ed6\u5c38\u4f53\u7684\u9e70\uff0c\u73b0\u5728\u4ed6\u5df2\u51c6\u5907\u5403\u5b83\u7684\u5c38\u4f53\u3002 \u3000\u3000\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u5230\u4e86\u4e0d\u5f97\u5df2\u7684\u65f6\u5019\uff0c\u4e00\u4e2a\u4eba\u548c\u4e00\u53ea\u98df\u5c38\u9e70\u5c31\u4f1a\u53d8\u5f97\u6ca1\u4ec0\u4e48\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u5e73\u5e38\u7684\u65f6\u5019\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u540c\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u5e73\u5e38\u5403\u4e0d\u5403\u725b\u8089\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5403\u3002\u201d \u3000\u3000\u535c\u9e70\u7b11\u9053\uff1a\u201c\u4f60\u5403\u7684\u725b\u8089\uff0c\u4e5f\u662f\u725b\u7684\u5c38\u4f53\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u82e6\u7b11\uff0c\u535c\u9e70\u8bf4\u7684\u8bdd\u867d\u7136\u5c16\u9510\u51b7\u9177\uff0c\u5374\u4ee4\u4eba\u65e0\u6cd5\u53cd\u9a73\u3002 \u3000\u3000\u201c\u8d64\u5927\u201d\u8fd8\u6ca1\u6709\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u5b83\u80fd\u652f\u6301\u5230\u73b0\u5728\uff0c\u56e0\u4e3a\u5c0f\u65b9\u5c06\u6700\u540e\u7684\u4e00\u70b9\u6c34\u7ed9\u4e86\u5b83\uff0c\u56e0\u4e3a\u9a6c\u867d\u7136\u662f\u517d\uff0c\u53ef\u662f\u9a6c\u7684\u517d\u6027\u5374\u6bd4\u4eba\u5c11\uff0c\u81f3\u5c11\u5b83\u4e0d\u6cbe\u8840\u8165\u3002 \u3000\u3000\u5b83\u4e0d\u98df\u5c38\u4f53\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u4f46\u6709\u628a\u597d\u5251\uff0c\u8fd8\u6709\u5339\u597d\u9a6c\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u8fd9\u4e2a\u4eba\u5374\u4e0d\u80fd\u7b97\u662f\u4e2a\u597d\u4eba\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6240\u4ee5\u522b\u4eba\u624d\u4f1a\u53eb\u4f60\u8981\u547d\u7684\u5c0f\u65b9\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u73b0\u5728\u5929\u8272\u5df2\u7ecf\u5f88\u6697\uff0c\u5df2\u7ecf\u770b\u4e0d\u89c1\u4ed6\u7684\u8138\u8272\uff0c\u4ed6\u7684\u58f0\u97f3\u4e2d\u5145\u6ee1\u60ca\u8bb6\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u7684\u4e8b\u5f88\u5c11\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u8fd8\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u7684\u786e\u662f\u4e2a\u5f88\u8981\u547d\u7684\u4eba\uff0c\u813e\u6c14\u8bd1\u5f97\u8981\u547d\uff0c\u9aa8\u5934\u786c\u5f97\u8981\u547d\uff0c\u6709\u65f6\u9614\u5f97\u8981\u547d\uff0c\u6709\u65f6\u7a77\u5f97\u8981\u547d\uff0c\u6709\u65f6\u8981\u522b\u4eba\u7684\u547d\uff0c\u6709\u65f6\u522b\u4eba\u4e5f\u60f3\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u81f3\u5c11\u5c31\u6709\u5341\u4e8c\u4e2a\u4eba\u5728\u8ffd\u8e2a\u4f60\uff0c\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6709\u5341\u4e09\u4e2a\uff1f\u6211\u672c\u6765\u4ee5\u4e3a\u6765\u7684\u8fd8\u8981\u591a\u4e9b\u3002\u2026 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u5176\u5b9e\u6839\u672c\u7528\u4e0d\u7740\u5341\u4e09\u4e2a\uff0c\u53ea\u8981\u5176\u4e2d\u7684\u4e24\u4e2a\u4eba\u6765\u4e86\u5c31\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u54ea\u4e24\u4e2a\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u641c\u9b42\u624b\u548c\u6c34\u94f6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6c34\u94f6\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6c34\u94f6\u662f\u4e2a\u4eba\uff1f\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u751a\u81f3\u8fde\u4ed6\u662f\u7537\u662f\u5973\u90fd\u4e0d\u77e5\u9053\u3002\u6211\u77e5\u9053\u4ed6\u662f\u4e2a\u6740\u4eba\u7684\u4eba\uff0c\u4ee5\u6740\u4eba\u4e3a\u751f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u4e0d\u6b62\u4ed6\u4e00\u4e2a\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f46\u662f\u4ed6\u8981\u7684\u4ef7\u94b1\u81f3\u5c11\u6bd4\u522b\u4eba\u8d35\u5341\u500d\uff0c\u56e0\u4ed6\u6740\u4eba\u4ece\u6765\u6ca1\u6709\u5931\u8fc7\u624b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4ed6\u662f\u4e2a\u5973\u7684\uff0c\u662f\u4e2a\u5f88\u597d\u770b\u7684\u5c0f\u59d1\u5a18\uff0c\u5982\u679c\u6211\u4e00\u5b9a\u8981\u6b7b\uff0c\u80fd\u591f\u6b7b\u5728\u4e00\u4e2a\u7f8e\u5973\u624b\u91cc\u603b\u6bd4\u8f83\u6109\u5feb\u4e9b\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u53ef\u80fd\u662f\u4e2a\u5973\u7684\uff0c\u53ef\u80fd\u662f\u4e2a\u5f88\u6f02\u4eae\u7684\u5c0f\u59d1\u5a18\uff0c\u4e5f\u53ef\u80fd\u662f\u4e2a\u8001\u5934\u5b50\uff0c\u8001\u592a\u5a46\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4e5f\u53ef\u80fd\u662f\u4f60\u3002\u201d \u3000\u3000\u535c\u9e70\u6c89\u9ed8\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4e5f\u53ef\u80fd\u662f\u6211\u3002\u201d \u3000\u3000\u98ce\u66f4\u51b7\uff0c\u9ed1\u6697\u5df2\u7b3c\u7f69\u5927\u5730\uff0c\u4e24\u4e2a\u4eba\u90fd\u9759\u9759\u5730\u8eba\u5728\u9ed1\u6697\u4e2d\uff0c\u4e92\u76f8\u90fd\u770b\u4e0d\u89c1\u5bf9\u65b9\u7684\u8138\u3002\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u5c0f\u65b9\u5ffd\u7136\u53c8\u7b11\u4e86\uff1a\u201c\u6211\u5b9e\u5728\u4e0d\u8be5\u6000\u7591\u4f60\u7684\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5982\u679c\u662f\u4f60\uff0c\u73b0\u5728\u6211\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u535c\u9e70\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u6740\u4f60\uff0c\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u6211\u6839\u672c\u4e0d\u5fc5\u7740\u6025\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4e5f\u8bb8\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u53ea\u8981\u4e00\u6709\u673a\u4f1a\uff0c\u5c31\u5e94\u8be5\u5148\u4e0b\u624b\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5982\u679c\u4f60\u4e0d\u662f\u6c34\u94f6\u5462\uff1f\u201d \u3000\u3000\u535c\u9e70\u8bf4\u9053\uff1a\u201c\u6740\u9519\u4eba\uff0c\u603b\u6bd4\u88ab\u4eba\u6740\u9519\u597d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u6740\u8fc7\u4eba\uff0c\u53ef\u662f\u6211\u4ece\u6765\u6ca1\u6709\u6740\u9519\u8fc7\u4eba\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u6740\u7684\u4eba\u90fd\u8be5\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u7edd\u5bf9\u662f\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u4f60\u81f3\u5c11\u6740\u9519\u4e86\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u5415\u5929\u5b9d\u3002\u201d \u3000\u3000\u4ed6\u53c8\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u4ed6\u662f\u2018\u5bcc\u8d35\u795e\u4ed9\u2019\u7684\u72ec\u751f\u5b50\uff0c\u4f60\u660e\u660e\u77e5\u9053\u4f60\u6740\u4e86\u5415\u5929\u5b9d\u540e\uff0c\u4ed6\u662f\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u7684\u3002\u4f60\u5f53\u7136\u77e5\u9053\u6c5f\u6e56\u4e2d\u6709\u591a\u5c11\u4eba\u80af\u4e3a\u4ed6\u5356\u547d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u8be5\u6b7b\uff0c\u8be5\u6740\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6740\u4e86\u4ed6\u4e4b\u540e\uff0c\u4f60\u81ea\u5df1\u4e5f\u6d3b\u4e0d\u957f\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5c31\u7b97\u6211\u6740\u4e86\u4ed6\u4e4b\u540e\u9a6c\u4e0a\u5c31\u4f1a\u6b7b\uff0c\u6211\u4e5f\u8981\u6740\u4ed6\u3002 \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u5ffd\u7136\u5145\u6ee1\u6124\u6012\uff1a\u201c\u5c31\u7b97\u6211\u4f1a\u88ab\u4eba\u5343\u5200\u4e07\u5250\u6253\u4e0b\u5341\u516b\u5c42\u5730\u72f1\u53bb\uff0c\u6211\u4e5f\u8981\u6740\u4e86\u4ed6\uff0c\u975e\u6740\u4e0d\u53ef\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u53ea\u8981\u4f60\u8ba4\u4e3a\u662f\u8be5\u6740\u7684\u4eba\uff0c\u4f60\u5c31\u4f1a\u53bb\u6740\u4ed6\uff0c\u4e0d\u7ba1\u4ed6\u662f\u8c01\uff0c\u90fd\u4e00\u6837\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u662f\u5929\u7687\u8001\u5b50\uff0c\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u535c\u9e70\u5c45\u7136\u4e5f\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u4f60\u53ea\u6709\u7b49\u7740\u522b\u4eba\u6765\u8981\u4f60\u7684\u547d\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u4e00\u76f4\u90fd\u5728\u7b49\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u5728\u7b49\u3002\u201d \u3000\u3000\u535c\u9e70\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u7edd\u5bf9\u4e0d\u4f1a\u7b49\u5f97\u5927\u4e45\u7684\u3002\u201d \u3000\u3000\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff0c\u6b7b\u4e00\u822c\u7684\u9759\u5bc2\uff0c\u6ca1\u6709\u5149\uff0c\u6ca1\u6709\u58f0\u97f3\uff0c\u6ca1\u6709\u751f\u547d\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u77e5\u9053\u81ea\u5df1\u4e0d\u4f1a\u7b49\u5f97\u592a\u4e45\uff0c\u4ed6\u5fc3\u91cc\u5df2\u7ecf\u6709\u4e86\u79cd\u4e0d\u7965\u7684\u9884\u5146\u3002 \u3000\u3000\u6c34\u94f6\u662f\u65e0\u5b54\u4e0d\u5165\u7684\uff0c\u7edd\u4e0d\u4f1a\u9519\u8fc7\u4e00\u70b9\u673a\u4f1a\u3002 \u3000\u3000\u6c34\u94f6\u6d41\u52a8\u65f6\u7edd\u6ca1\u6709\u4e00\u70b9\u58f0\u97f3\u3002 \u3000\u3000\u4f60\u53ea\u8981\u8ba9\u4e00\u70b9\u6c34\u94f6\u6d41\u5165\u4f60\u7684\u76ae\u8089\u91cc\uff0c\u5b83\u5c31\u4f1a\u628a\u4f60\u5168\u8eab\u7684\u76ae\u90fd\u5265\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5982\u679c\u53eb\u505a\u201c\u6c34\u94f6\u201d\uff0c\u5f53\u7136\u6709\u4ed6\u7684\u539f\u56e0\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u77e5\u9053\u4ed6\u7edd\u5bf9\u662f\u4e2a\u6781\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u53d7\u7684\u4f24\u5f88\u4e0d\u8f7b\uff0c\u4f24\u53e3\u5df2\u6e83\u70c2\uff0c\u4e00\u53ea\u9e70\u7684\u8840\u8089\uff0c\u5e76\u6ca1\u6709\u4f7f\u4ed6\u7684\u4f53\u529b\u6062\u590d\uff0c\u5728\u4ed6\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u597d\u8c61\u53ea\u6709\u7b49\u6b7b\u3002 \u3000\u3000\u7b49\u6b7b\u5b9e\u5728\u662f\u4ef6\u5f88\u53ef\u6015\u7684\u4e8b\uff0c\u751a\u81f3\u6bd4\u201c\u6b7b\u4ea1\u201d\u672c\u8eab\u66f4\u53ef\u6015\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u53c8\u5728\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u641c\u9b42\u624b\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u641c\u9b42\u624b\u59d3\u97e9\uff0c\u53eb\u97e9\u7ae0\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u65f6\u5e38\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u4f46\u662f\u4ed6\u7684\u540d\u6c14\u5374\u5f88\u5927\uff0c\u56e0\u4e3a\u4ed6\u662f\u201c\u5bcc\u8d35\u795e\u4ed9\u201d\u4f9b\u517b\u7684\u56db\u5927\u9ad8\u624b\u4e4b\u4e00\uff0c\u4ed6\u7528\u7684\u72ec\u95e8\u5175\u5203\u5c31\u53eb\u505a\u201c\u641c\u9b42\u624b\u201d\uff0c\u5728\u6d77\u5185\u7edd\u4f20\u5df2\u4e45\uff0c\u62db\u5f0f\u5947\u7279\u6bd2\u8fa3\uff0c\u5df2\u4e0d\u77e5\u641c\u53bb\u8fc7\u591a\u5c11\u4eba\u7684\u9b42\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f46\u662f\u8fd8\u6709\u4ef6\u4e8b\u4f60\u4e00\u5b9a\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u53e6\u5916\u8fd8\u6709\u4e2a\u540d\u5b57\uff0c\u4ed6\u7684\u670b\u53cb\u90fd\u53eb\u4ed6\u8fd9\u4e2a\u540d\u5b57\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53eb\u4ed6\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u778e\u5b50\u3002\u201d \u3000\u3000\u778e\u5b50\u5e76\u4e0d\u53ef\u6015\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u65b9\u542c\u89c1\u8fd9\u4e24\u4e2a\u5b57\uff0c\u5fc3\u5c31\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u778e\u5b50\u770b\u4e0d\u89c1\uff0c\u778e\u5b50\u8981\u6740\u4eba\u65f6\uff0c\u7528\u4e0d\u7740\u770b\u89c1\u90a3\u4e2a\u4eba\uff0c\u4e5f\u4e00\u6837\u53ef\u4ee5\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u778e\u4e8e\u5728\u9ed1\u6697\u4e2d\u4e5f\u4e00\u6837\u53ef\u4ee5\u6740\u4eba\u3002 \u3000\u3000\u6ca1\u6709\u661f\u5149\uff0c\u6ca1\u6709\u6708\u8272\uff0c\u5728\u8fd9\u79cd\u4ee4\u4eba\u7edd\u671b\u7684\u9ed1\u6697\u4e2d\uff0c\u778e\u5b50\u8fdc\u6bd4\u773c\u775b\u6700\u9510\u5229\u7684\u66f4\u53ef\u6015\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u5e76\u6ca1\u6709\u5b8c\u5168\u778e\uff0c\u4f46\u662f\u4e5f\u8ddf\u778e\u5b50\u5dee\u4e0d\u591a\u4e86\uff0c\u4ed6\u7684\u773c\u775b\u591a\u5e74\u524d\u53d7\u8fc7\u4f24\uff0c\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u8fd9\u53e5\u8bdd\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u4e00\u628a\u5feb\u5200\u5272\u65ad\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5168\u8eab\u4e0a\u4e0b\u7684\u5bd2\u6bdb\u5728\u8fd9\u4e00\u77ac\u95f4\u5ffd\u7136\u4e00\u6839\u6839\u7ad6\u8d77\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u535c\u9e70\u4e3a\u4ec0\u4e48\u95ed\u4e0a\u4e86\u5634\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u542c\u89c1\u4e86\u4e00\u79cd\u5947\u602a\u7684\u58f0\u97f3\uff0c\u65e2\u7136\u4e0d\u662f\u811a\u6b65\u58f0\uff0c\u4e5f\u4e0d\u662f\u547c\u5438\u58f0\uff0c\u800c\u662f\u53e6\u5916\u4e00\u79cd\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u79cd\u4e0d\u80fd\u7528\u8033\u6735\u53bb\u542c\uff0c\u8033\u6735\u4e5f\u542c\u4e0d\u89c1\u7684\u58f0\u97f3\uff0c\u4e00\u79cd\u53ea\u6709\u7528\u91ce\u517d\u822c\u7075\u654f\u7684\u89e6\u89c9\u624d\u80fd\u542c\u89c1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6709\u4eba\u6765\u4e86\uff01 \u3000\u3000\u60f3\u8981\u4ed6\u547d\u7684\u4eba\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u770b\u4e0d\u89c1\u8fd9\u4e2a\u4eba\uff0c\u8fde\u5f71\u5b50\u90fd\u770b\u4e0d\u89c1\uff0c\u4f46\u662f\uff0c\u4ed6\u80fd\u611f\u89c9\u5230\u8fd9\u4e2a\u4eba\uff0c\u8ddd\u79bb\u4ed6\u5df2\u8d8a\u6765\u8d8a\u8fd1\u3002 \u3000\u3000\u51b0\u51b7\u7684\u5927\u5730\uff0c\u51b0\u51b7\u7684\u6c99\u7c92\uff0c\u51b0\u51b7\u7684\u957f\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u63e1\u4f4f\u4e86\u4ed6\u7684\u5251\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u770b\u4e0d\u89c1\u8fd9\u4e2a\u4eba\uff0c\u8fde\u5f71\u5b50\u90fd\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u4f46\u4ed6\u5df2\u611f\u89c9\u5230\u4e00\u79cd\u593a\u4eba\u9b42\u9b44\u7684\u6740\u6c14\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5f80\u535c\u9e70\u90a3\u8fb9\u6eda\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u535c\u9e70\u521a\u624d\u660e\u660e\u662f\u8eba\u5728\u90a3\u91cc\u7684\uff0c\u8ddd\u79bb\u4ed6\u5e76\u4e0d\u8fdc\uff0c\u73b0\u5728\u5374\u5df2\u4e0d\u5728\u4e86\u3002 \u3000\u3000\u4f46\u662f\u53e6\u5916\u4e00\u5b9a\u6709\u4e2a\u4eba\u5728\uff0c\u5c31\u5728\u4ed6\u9644\u8fd1\uff0c\u5728\u7b49\u7740\u8981\u4ed6\u7684\u547d\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u518d\u52a8\uff0c\u4e0d\u6562\u518d\u53d1\u51fa\u4e00\u70b9\u58f0\u97f3\uff0c\u4ed6\u7684\u8eab\u5b50\u4eff\u4f5b\u5728\u9010\u6e10\u50f5\u786c\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u53c8\u542c\u89c1\u4e86\u4e00\u9635\u6025\u800c\u5c16\u9510\u7684\u98ce\u58f0\u3002 \u3000\u3000\u4ed6\u4ece\u5341\u56db\u5c81\u65f6\u5c31\u5f00\u59cb\u95ef\u8361\u6c5f\u6e56\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u91ce\u72fc\u822c\u5728\u6c5f\u6e56\u4e2d\u6d41\u6d6a\u3002 \u3000\u3000\u4ed6\u6328\u8fc7\u62f3\u5934\uff0c\u6328\u8fc7\u5df4\u638c\uff0c\u6328\u8fc7\u5200\uff0c\u6328\u8fc7\u5251\uff0c\u6328\u8fc7\u5404\u5f0f\u5404\u6837\u7684\u6b66\u5668\u548c\u6697\u5668\u3002 \u3000\u3000\u4ed6\u542c\u5f97\u51fa\u8fd9\u79cd\u6697\u5668\u7834\u7a7a\u7684\u98ce\u58f0\uff0c\u4e00\u79cd\u6781\u7ec6\u5c0f\u3002\u6781\u5c16\u9510\u7684\u6697\u5668\uff0c\u8fd9\u79cd\u6697\u5668\u901a\u5e38\u90fd\u662f\u7528\u673a\u7c27\u6253\u51fa\u6765\u7684\uff0c\u800c\u4e14\u901a\u5e38\u90fd\u6709\u6bd2\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u95ea\u907f\uff0c\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u4e00\u52a8\u5c31\u6b7b\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u6697\u5668\u5df2\u7ecf\u6253\u4e0b\u6765\uff0c\u6253\u5728\u4ed6\u8eab\u65c1\u7684\u6c99\u7c92\u4e0a\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7b97\u51c6\u4ed6\u4e00\u5b9a\u4f1a\u95ea\u907f\uff0c\u4e00\u5b9a\u4f1a\u52a8\u7684\uff0c\u6240\u4ee5\uff0c\u6697\u5668\u6253\u7684\u4e0d\u662f\u4ed6\u7684\u4eba\uff0c\u800c\u662f\u4ed6\u7684\u9000\u8def\uff0c\u4e0d\u8bba\u4ed6\u4ece\u54ea\u8fb9\u95ea\u907f\uff0c\u53ea\u8981\u4e00\u52a8\u5c31\u6b7b\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u542c\u51fa\u98ce\u58f0\u4e0d\u662f\u76f4\u63a5\u5f80\u4ed6\u8eab\u4e0a\u6253\u8fc7\u6765\u7684\uff0c\u4ed6\u4e5f\u7b97\u51c6\u4e86\u8fd9\u4e2a\u4eba\u51fa\u624b\u7684\u610f\u5411\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u5341\u6210\u628a\u63e1\uff0c\u8fd9\u79cd\u4e8b\u65e0\u8bba\u8c01\u90fd\u7edd\u4e0d\u53ef\u80fd\u6709\u5341\u6210\u628a\u63e1\u3002 \u3000\u3000\u5728\u8fd9\u95ee\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u4e5f\u6ca1\u6cd5\u5b50\u591a\u8003\u8651\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e00\u5b9a\u8981\u8d4c\u4e00\u8d4c\uff0c\u7528\u81ea\u5df1\u7684\u6027\u547d\u4f5c\u8d4c\u6ce8\uff0c\u7528\u81ea\u5df1\u7684\u5224\u65ad\u6765\u4e0b\u6ce8\u3002 \u3000\u3000\u8fd9\u4e00\u6ce8\u4ed6\u4e0b\u5f97\u597d\u9669\uff0c\u8d62\u5f97\u597d\u9669\u3002 \u7b2c\u4e09\u7ae0\u3000\u778e\u5b50 \u3000\u3000\u4f46\u662f\u8fd9\u573a\u8d4c\u8fd8\u6ca1\u6709\u5b8c\uff0c\u4ed6\u4e00\u5b9a\u8fd8\u8981\u8d4c\u4e0b\u53bb\uff0c\u4ed6\u7684\u5bf9\u624b\u7edd\u4e0d\u80af\u653e\u8fc7\u4ed6\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u4ed6\u867d\u7136\u8d62\u4e86\uff0c\u4e0b\u4e00\u624b\u5f88\u53ef\u80fd\u5c31\u4f1a\u8f93\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u4f1a\u8f93\uff0c\u8f93\u7684\u5c31\u662f\u4ed6\u7684\u547d\u3002\u5f88\u53ef\u80fd\u4ed6\u8fde\u5bf9\u624b\u7684\u4eba\u90fd\u6ca1\u6709\u770b\u89c1\uff0c\u5c31\u5df2\u628a\u547d\u8f93\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u5df2\u51c6\u5907\u8981\u6b7b\u7684\uff0c\u53ef\u662f\u8fd9\u4e48\u6837\u6b7b\u6cd5\uff0c\u4ed6\u6b7b\u5f97\u5b9e\u5728\u4e0d\u7518\u5fc3\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5f00\u59cb\u54b3\u55fd\u3002 \u3000\u3000\u54b3\u55fd\u5f53\u7136\u6709\u58f0\u97f3\uff0c\u6709\u58f0\u97f3\u5c31\u6709\u76ee\u6807\uff0c\u4ed6\u5df2\u5c06\u81ea\u5df1\u5b8c\u5168\u66b4\u9732\u7ed9\u5bf9\u65b9\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u53c8\u542c\u5230\u4e86\u4e00\u9635\u98ce\u58f0\uff0c\u4e00\u9635\u4eff\u4f5b\u8981\u5c06\u4ed6\u6574\u4e2a\u4eba\u6495\u88c2\u7684\u98ce\u58f0\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5374\u5df2\u7a9c\u4e86\u51fa\u53bb\uff0c\u7528\u5c3d\u4ed6\u6240\u6709\u7684\u6f5c\u529b\u7a9c\u4e86\u51fa\u53bb\uff0c\u4ece\u98ce\u58f0\u4e0b\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u5ffd\u7136\u95ea\u8d77\u4e86\u5251\u5149\u3002 \u3000\u3000\u5728\u4ed6\u54b3\u55fd\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u62bd\u51fa\u4e86\u4ed6\u7684\u5251\uff0c\u5929\u4e0b\u6700\u950b\u5229\u7684\u4e03\u628a\u5251\u4e4b\u4e00\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u53d1\u51fa\u4e86\u201c\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u7136\u540e\u5c31\u662f\u4e00\u58f0\u94c1\u5668\u843d\u5728\u5730\u4e0a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u54cd\u8fc7\uff0c\u53c8\u662f\u4e00\u7247\u6b7b\u5bc2\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u4e0d\u518d\u52a8\uff0c\u8fde\u547c\u5438\u90fd\u5df2\u505c\u6b62\uff0c\u552f\u4e00\u80fd\u611f\u89c9\u5230\u7684\uff0c\u5c31\u662f\u51b7\u6c57\u6b63\u4ece\u9f3b\u5c16\u5f80\u4e0b\u6ef4\u843d\uff0c\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5c31\u50cf\u6c38\u6052\u822c\u90a3\u4e48\u957f\u4e45\uff0c\u4ed6\u624d\u542c\u5230\u53e6\u5916\u4e00\u79cd\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u7b49\u5f85\u7740\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u542c\u89c1\u8fd9\u79cd\u58f0\u97f3\uff0c\u4ed6\u6574\u4e2a\u4eba\u5c31\u7acb\u523b\u865a\u8131\uff0c\u6162\u6162\u5730\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u542c\u5230\u7684\u662f\u4e00\u58f0\u6781\u8f7b\u5f31\u7684\u547b\u541f\u548c\u4e00\u9635\u6781\u6025\u4fc3\u7684\u5598\u606f\u3002 \u3000\u3000\u4eba\u4eec\u53ea\u6709\u5728\u75db\u82e6\u5df2\u8fbe\u5230\u6781\u9650\u3001\u5df2\u5b8c\u5168\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u65f6\uff0c\u624d\u4f1a\u53d1\u51fa\u8fd9\u79cd\u58f0\u97f3\u6765\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u4e00\u6218\u4ed6\u53c8\u80dc\u4e86\uff0c\u80dc\u5f97\u867d\u7136\u51c4\u51c9\u800c\u8270\u82e6\uff0c\u53ef\u662f\u4ed6\u603b\u7b97\u80dc\u4e86\u3002 \u3000\u3000\u4ed6\u80dc\u8fc7\uff0c\u5e38\u80dc\uff0c\u6240\u4ee5\u4ed6\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u4ed6\u603b\u8ba4\u4e3a\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u80dc\u5229\u548c\u751f\u5b58\uff0c\u81f3\u5c11\u603b\u6bd4\u5931\u8d25\u597d\uff0c\u603b\u6bd4\u6b7b\u597d\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u6b21\u4ed6\u51e0\u4e4e\u8fde\u80dc\u5229\u7684\u6ecb\u5473\u90fd\u65e0\u6cd5\u5206\u8fa8\uff0c\u4ed6\u6574\u4e2a\u4eba\u5ffd\u7136\u95f4\u5c31\u5df2\u865a\u8131\uff0c\u4e00\u79cd\u56e0\u5b8c\u5168\u677e\u5f1b\u800c\u4ea7\u751f\u7684\u865a\u8131\u3002 \u3000\u3000\u56db\u5468\u8fd8\u662f\u4e00\u7247\u9ed1\u6697\uff0c\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff0c\u4ee4\u4eba\u7edd\u671b\u7684\u9ed1\u6697\u3002 \u3000\u3000\u80dc\u5229\u548c\u5931\u8d25\u597d\u8c61\u5df2\u6ca1\u4ec0\u4e48\u5206\u522b\uff0c\u7741\u7740\u773c\u775b\u548c\u95ed\u4e0a\u773c\u775b\u66f4\u6ca1\u6709\u5206\u522b\u3002 \u3000\u3000\u4ed6\u7684\u773c\u5e18\u6e10\u6e10\u960e\u8d77\uff0c\u5df2\u4e0d\u60f3\u518d\u652f\u6301\u4e0b\u53bb\uff0c\u56e0\u4e3a\u751f\u4e0e\u6b7b\u597d\u8c61\u4e5f\u6ca1\u4ec0\u4e48\u5206\u522b\u4e86\u3002 \u3000\u3000\u4e00\u4e00\u4f60\u4e0d\u80fd\u6b7b\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u8fd8\u6709\u4e00\u5206\u751f\u5b58\u7684\u673a\u4f1a\uff0c\u4f60\u5c31\u4e0d\u80fd\u653e\u5f03\u3002 \u3000\u3000\u2014\u2014\u53ea\u6709\u61e6\u592b\u624d\u4f1a\u653e\u5f03\u751f\u5b58\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5c0f\u65b9\u9aa4\u7136\u60ca\u9192\uff0c\u8dc3\u8d77\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u9ed1\u6697\u4e2d\u5df2\u6709\u4e86\u5149\u3002 \u3000\u3000\u5149\u660e\u4e5f\u6b63\u5982\u9ed1\u6697\u4e00\u6837\uff0c\u603b\u662f\u5ffd\u7136\u800c\u6765\uff0c\u4e5f\u8bb8\u4e0d\u77e5\u9053\u5b83\u4ec0\u4e48\u65f6\u5019\u4f1a\u6765\uff0c\u4f46\u4f60\u4e00\u5b9a\u8981\u6709\u4fe1\u5fc3\uff0c\u4e00\u5b9a\u8981\u76f8\u4fe1\u5b83\u8fdf\u65e9\u603b\u4f1a\u6765\u7684\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4e00\u5fc3\u60f3\u8981\u4ed6\u547d\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e5f\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u8fd8\u5728\u6323\u624e\uff0c\u8fd8\u5728\u52a8\uff0c\u52a8\u5f97\u8270\u82e6\u800c\u7f13\u6162\uff0c\u5c31\u50cf\u662f\u4e00\u5c3e\u88ab\u56f0\u5728\u6c99\u783e\u4e2d\u5782\u6b7b\u7684\u9c7c\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u521a\u62ff\u8d77\u4e86\u4e00\u6837\u4e1c\u897f\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u6251\u4e86\u8fc7\u53bb\uff0c\u7528\u5c3d\u5168\u8eab\u7684\u529b\u6c14\u6251\u4e86\u8fc7\u53bb\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u5230\u8fd9\u4e2a\u4eba\u624b\u91cc\u62ff\u7740\u7684\u8fd9\u6837\u4e1c\u897f\u662f\u4e2a\u7528\u7f8a\u76ae\u505a\u7684\u6c34\u888b\u3002 \u3000\u3000\u5728\u8fd9\u91cc\uff0c\u6c34\u5c31\u662f\u547d\uff0c\u6bcf\u4e2a\u4eba\u90fd\u53ea\u6709\u4e00\u6761\u547d\u3002\u5c0f\u65b9\u7684\u624b\u5df2\u56e0\u5174\u594b\u800c\u53d1\u6296\uff0c\u91ce\u517d\u822c\u6251\u8fc7\u53bb\uff0c\u7528\u91ce\u517d\u822c\u7684\u52a8\u4f5c\u593a\u4e0b\u4e86\u6c34\u888b\u3002 \u3000\u3000\u888b\u4e2d\u7684\u6c34\u5df2\u6240\u5269\u4e0d\u591a\uff0c\u53ef\u662f\u53ea\u8981\u8fd8\u6709\u4e00\u6ef4\u6c34\uff0c\u4e5f\u8bb8\u5c31\u80fd\u4f7f\u751f\u547d\u5ef6\u7eed\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u53ea\u6709\u4e00\u6761\u547d\uff0c\u591a\u4e48\u53ef\u8d35\u7684\u751f\u547d\uff0c\u591a\u4e48\u503c\u5f97\u73cd\u60dc\u3002 \u3000\u3000\u5c0f\u65b9\u7528\u98a4\u6296\u7684\u624b\u62d4\u5f00\u6c34\u888b\u7684\u6728\u585e\uff0c\u5e72\u88c2\u7684\u5634\u5507\u5df2\u611f\u89c9\u5230\u6c34\u7684\u82ac\u82b3\u3001\u751f\u547d\u7684\u82ac\u82b3\uff0c\u4ed6\u51c6\u5907\u5c06\u888b\u91cc\u7684\u8fd9\u70b9\u6c34\u4e00\u53e3\u53e3\uff0c\u6162\u6162\u5730\u559d\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u8981\u6162\u6162\u5730\u4eab\u53d7\uff0c\u4eab\u53d7\u6c34\u7684\u6ecb\u6da6\uff0c\u4eab\u53d7\u751f\u547d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u770b\u89c1\u8fd9\u4e2a\u4eba\u7684\u773c\u775b\u3002 \u3000\u3000\u4e00\u4e00\u53cc\u5145\u6ee1\u4e86\u75db\u82e6\u3001\u7edd\u671b\u548c\u54c0\u6c42\u7684\u773c\u775b\uff0c\u4e00\u53cc\u5782\u6b7b\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u53d7\u7684\u4f24\u6bd4\u4ed6\u66f4\u91cd\uff0c\u6bd4\u4ed6\u66f4\u9700\u8981\u8fd9\u70b9\u6c34\u3002\u6ca1\u6709\u6c34\uff0c\u8fd9\u4e2a\u4eba\u5fc5\u5c06\u6b7b\u5f97\u66f4\u4fa0\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u867d\u7136\u662f\u6765\u6740\u4ed6\u7684\uff0c\u53ef\u662f\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u7adf\u5fd8\u8bb0\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u662f\u4eba\uff0c\u4e0d\u662f\u91ce\u517d\uff0c\u4e5f\u4e0d\u662f\u98df\u5c38\u9e70\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u4e00\u4e2a\u4eba\u548c\u4e00\u53ea\u98df\u5c38\u9e70\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u60c5\u51b5\u4e0b\uff0c\u90fd\u662f\u6709\u5206\u522b\u7684\u3002 \u3000\u3000\u4eba\u7684\u5c0a\u4e25\u3001\u4eba\u7684\u826f\u77e5\u548c\u540c\u60c5\uff0c\u90fd\u662f\u4ed6\u629b\u4e0d\u5f00\u3001\u4e5f\u5fd8\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u4ed6\u5c06\u8fd9\u888b\u6c34\u8fd8\u7ed9\u4e86\u8fd9\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4e00\u5fc3\u60f3\u8981\u4ed6\u547d\u7684\u4eba\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4e5f\u66fe\u7ecf\u60f3\u8981\u8fd9\u4e2a\u4eba\u7684\u547d\uff0c\u4f46\u662f\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5728\u4eba\u6027\u53d7\u5230\u5982\u6b64\u65e0\u60c5\u7684\u8003\u9a8c\u65f6\uff0c\u4ed6\u53ea\u6709\u8fd9\u4e48\u505a\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u4ece\u4e00\u4e2a\u5782\u6b7b\u7684\u4eba\u624b\u91cc\u63a0\u593a\uff0c\u4e0d\u7ba1\u8fd9\u4e2a\u4eba\u662f\u8c01\u90fd\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5c45\u7136\u662f\u4e2a\u5973\u4eba\uff0c\u7b49\u5979\u63ed\u8d77\u8499\u9762\u7684\u9ed1\u4e2d\u559d\u6c34\u65f6\uff0c\u5c0f\u65b9\u624d\u53d1\u73b0\u5979\u662f\u4e2a\u5973\u4eba\uff0c\u6781\u7f8e\u7684\u5973\u4eba\uff0c\u867d\u7136\u770b\u6765\u663e\u5f97\u82cd\u767d\u800c\u6194\u60b4\uff0c\u53cd\u800c\u66f4\u589e\u52a0\u4e86\u5979\u7684\u5a07\u5f31\u548c\u7f8e\u4e3d\u3002 \u3000\u3000\u4e00\u4e00\u4e2a\u50cf\u5979\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u600e\u4e48\u4f1a\u5728\u5982\u6b64\u53ef\u6015\u7684\u5927\u6f20\u4e4b\u591c\u91cc\uff0c\u72ec\u81ea\u6765\u6740\u4eba\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u559d\u5b8c\u4e86\u7f8a\u76ae\u888b\u4e2d\u7684\u6c34\uff0c\u4e5f\u6b63\u5077\u6109\u5730\u6253\u91cf\u7740\u5c0f\u65b9\uff0c\u773c\u775b\u91cc\u4eff\u4f5b\u5e26\u7740\u6b49\u610f\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u5e94\u8be5\u7559\u4e00\u534a\u7ed9\u4f60\u7684\u3002\u201d\u5979\u629b\u4e0b\u7a7a\u6c34\u888b\uff0c\u8f7b\u8f7b\u53f9\u606f\uff0c\u201c\u53ef\u60dc\u8fd9\u91cc\u9762\u7684\u6c34\u5b9e\u5728\u592a\u5c11\u4e86\uff0f \u3000\u3000\u5c0f\u65b9\u7b11\u7b11\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u5bf9\u5979\u7b11\u7b11\uff0c\u7136\u540e\u624d\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u662f\u778e\u5b50\uff0c\u8fd8\u662f\u6c34\u94f6\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6211\u4e0d\u662f\u778e\u5b50\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u6c34\u7684\u6ecb\u6da6\u540e\uff0c\u5979\u672c\u6765\u5df2\u7ecf\u5f88\u7f8e\u4e3d\u7684\u773c\u775b\u770b\u6765\u66f4\u660e\u5a9a\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u662f\u6c34\u94f6\uff1f\u201d\u5c0f\u65b9\u8ffd\u95ee\u3002 \u3000\u3000\u201c\u6211\u53ea\u542c\u8bf4\u8fc7\u8fd9\u540d\u5b57\uff0c\u5374\u4e00\u76f4\u4e0d\u77e5\u9053\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u5979\u53c8\u5728\u53f9\u606f\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u672c\u6765\u4e5f\u4e0d\u77e5\u9053\u4f60\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u53ea\u77e5\u9053\u4f60\u59d3\u65b9\uff0c\u53eb\u65b9\u4f1f\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u4f60\u5374\u8981\u6740\u6211\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u5b9a\u8981\u6765\u6740\u4f60\uff0c\u4f60\u6b7b\u4e86\uff0c\u6211\u624d\u80fd\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6c34\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u6ca1\u6709\u6c34\u8c01\u4e5f\u6d3b\u4e0d\u4e86\u4e09\u5929\u3002\u201d \u3000\u3000\u5979\u770b\u7740\u5730\u4e0a\u7684\u7a7a\u6c34\u888b\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u6740\u4e86\u4f60\uff0c\u4ed6\u4eec\u624d\u7ed9\u6211\u6c34\u559d\uff0c\u5426\u5219\u8fd9\u5c31\u662f\u6211\u6700\u540e\u4e00\u6b21\u559d\u6c34\u4e86\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u91cc\u5145\u6ee1\u6050\u60e7\uff1a\u201c\u6709\u4e00\u6b21\u6211\u5c31\u51e0\u4e4e\u88ab\u4ed6\u4eec\u6d3b\u6d3b\u6e34\u6b7b\uff0c\u90a3\u79cd\u6ecb\u5473\u6211\u6b7b\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u8fd9\u4e00\u6b21\u6211\u5c31\u7b97\u80fd\u6d3b\u7740\u56de\u53bb\uff0c\u53ea\u8981\u4ed6\u4eec\u77e5\u9053\u4f60\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5c31\u7edd\u4e0d\u4f1a\u518d\u7ed9\u6211\u4e00\u6ef4\u6c34\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u5bf9\u5979\u7b11\u7b11\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u8ba9\u4f60\u5272\u4e0b\u6211\u7684\u5934\u9885\u6765\uff0c\u8ba9\u4f60\u5e26\u56de\u53bb\u6362\u6c34\u559d\uff1f\u201d \u3000\u3000\u5979\u5c45\u7136\u4e5f\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u6e29\u67d4\u800c\u51c4\u51c9\uff1a\u201c\u6211\u4e5f\u662f\u4e2a\u4eba\uff0c\u4e0d\u662f\u755c\u7272\uff0c\u4f60\u8fd9\u4e48\u5bf9\u6211\uff0c\u6211\u5b81\u6b7b\u4e5f\u4e0d\u4f1a\u518d\u5bb3\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u518d\u8bf4\uff0c\u4e5f\u6ca1\u6709\u95ee\u5979\uff1a\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u5fc5\u95ee\u3002 \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u5c31\u662f\u5bcc\u8d35\u795e\u4ed9\u6d3e\u6765\u8ffd\u6740\u4ed6\u7684\u4eba\uff0c\u73b0\u5728\u5f88\u53ef\u80fd\u5c31\u5728\u9644\u8fd1\u3002 \u3000\u3000\u535c\u9e70\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5c31\u50cf\u662f\u5927\u6f20\u4e2d\u7684\u98ce\u66b4\uff0c\u4ed6\u8981\u6765\u7684\u65f6\u5019\uff0c\u8c01\u4e5f\u6321\u4e0d\u4f4f\uff0c\u8981\u8d70\u7684\u65f6\u5019\uff0c\u8c01\u4e5f\u62e6\u4e0d\u4f4f\uff0c\u4f60\u6c38\u8fdc\u731c\u4e0d\u51fa\u4ed6\u4ec0\u4e48\u65f6\u5019\u4f1a\u6765\uff0c\u66f4\u731c\u4e0d\u51fa\u4ed6\u4ec0\u4e48\u4f1a\u8d70\u3002 \u3000\u3000\u53ef\u662f\u201c\u8d64\u72ac\u201d\u4ecd\u5728\u3002 \u3000\u3000\u65ed\u65e5\u5df2\u5c06\u5347\u8d77\uff0c\u5c0f\u65b9\u7ec8\u4e8e\u5f00\u53e3\u3002\u201c\u4f60\u4e0d\u80fd\u7559\u5728\u8fd9\u91cc\u3002\u201d\u4ed6\u5ffd\u7136\u8bf4\uff0c\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u90fd\u8981\u56de\u5230\u4ed6\u4eec\u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u53ea\u8981\u592a\u9633\u4e00\u5347\u8d77\uff0c\u9644\u8fd1\u5343\u91cc\u4e4b\u5185\uff0c\u90fd\u4f1a\u53d8\u6210\u70d8\u7089\uff0c\u4f60\u559d\u4e0b\u7684\u90a3\u70b9\u6c34\uff0c\u5f88\u5feb\u5c31\u4f1a\u88ab\u70e4\u5e72\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u7559\u5728\u8fd9\u91cc\uff0c\u6211\u4e5f\u662f\u4e00\u6837\u4f1a\u88ab\u6e34\u6b7b\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u5c0f\u65b9\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff1a\u201c\u53ef\u662f\u6211\u4e0d\u60f3\u770b\u7740\u4f60\u6b7b\uff0c\u4e5f\u4e0d\u60f3\u8ba9\u4f60\u770b\u7740\u6211\u6b7b\u3002\u201d\u5979\u9ed8\u9ed8\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9ed8\u9ed8\u5730\u7ad9\u8d77\u6765\uff0c\u521a\u7ad9\u8d77\u6765\uff0c\u53c8\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u53d7\u7684\u4f24\u4e0d\u8f7b\u3002 \u3000\u3000\u5c0f\u65b9\u521a\u624d\u90a3\u4e00\u5251\uff0c\u6b63\u523a\u5728\u5979\u7684\u80f8\u819b\u4e0a\uff0c\u8ddd\u79bb\u5979\u5fc3\u810f\u6700\u591a\u53ea\u6709\u4e24\u5bf8\u3002 \u3000\u3000\u73b0\u5728\u5979\u5df2\u5bf8\u6b65\u96be\u884c\uff0c\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\uff0c\u600e\u4e48\u80fd\u56de\u5f97\u53bb\uff1f \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u6211\u6709\u4e2a\u670b\u53cb\u53ef\u4ee5\u9001\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u5979\u6ca1\u6709\u770b\u89c1\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u597d\u8c61\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u670b\u53cb\u5e76\u4e0d\u4e00\u5b9a\u662f\u4eba\uff0c\u6211\u77e5\u9053\u6709\u5f88\u591a\u4eba\u90fd\u4e0d\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u8d70\u8fc7\u53bb\uff0c\u8f7b\u629a\u201c\u8d64\u5927\u201d\u7684\u67d4\u9b03\uff1a\u201c\u6211\u4e5f\u89c1\u8fc7\u6709\u5f88\u591a\u4f60\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\u7684\u4eba\uff0c\u90fd\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u670b\u53cb\u5c31\u662f\u8fd9\u5339\u9a6c\uff1f\u201d\u5979\u663e\u5f97\u5f88\u60ca\u5f02\uff0c\u201c\u4f60\u628a\u4e00\u5339\u9a6c\u5f53\u4f5c\u670b\u53cb\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u628a\u4e00\u5339\u9a6c\u5f53\u4f5c\u670b\u53cb\uff1f\u201d \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u5fae\u5e26\u82e6\u6da9\uff1a\u201c\u6211\u6d6a\u8ff9\u5929\u6daf\uff0c\u65e0\u4eb2\u65e0\u6545\uff0c\u53ea\u6709\u5b83\u59cb\u7ec8\u8ddf\u7740\u6211\uff0c\u751f\u6b7b\u4e0e\u5171\uff0c\u81f3\u6b7b\u4e0d\u5f03\uff0c\u8fd9\u6837\u7684\u670b\u53cb\u4f60\u6709\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u5979\u5782\u4e0b\u4e86\u5934\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u5730\u95ee\uff1a\u201c\u73b0\u5728\u4f60\u4e3a\u4ec0\u4e48\u8981\u8ddf\u5b83\u5206\u624b\uff1f\u8981\u5b83\u9001\u6211\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e5f\u4e0d\u60f3\u8981\u5b83\u966a\u6211\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u62cd\u201c\u8d64\u72ac\u201d\uff1a\u201c\u5b83\u662f\u5339\u597d\u9a6c\uff0c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u8ba9\u5b83\u6b7b\u7684\u3002\u4f60\u662f\u4e2a\u5f88\u597d\u770b\u7684\u5973\u4eba\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u771f\u7684\u628a\u4f60\u6e34\u6b7b\u3002\u6211\u8ba9\u5b83\u9001\u4f60\u56de\u53bb\uff0c\u624d\u662f\u4f60\u4eec\u552f\u4e00\u7684\u751f\u8def\u3002\u201d \u3000\u3000\u5979\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u5b83\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u53c8\u8f7b\u8f7b\u5730\u95ee\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u66ff\u4f60\u81ea\u5df1\u60f3\u8fc7\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u60f3\u60f3\u4f60\u81ea\u5df1\u8981\u600e\u4e48\u6837\u624d\u80fd\u6d3b\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53ea\u5bf9\u5979\u7b11\u7b11\u3002 \u3000\u3000\u6709\u4e9b\u95ee\u9898\u662f\u4e0d\u80fd\u56de\u7b54\u4e5f\u4e0d\u5fc5\u56de\u7b54\u7684\u3002 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u606f\uff0c\u8bf4\u51fa\u4e86\u5979\u5bf9\u4ed6\u7684\u60f3\u6cd5\uff1a\u201c\u4f60\u771f\u662f\u602a\u4eba\uff0c\u602a\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u592a\u9633\u5df2\u5347\u8d77\u3002 \u3000\u3000\u5927\u5730\u65e0\u60c5\uff0c\u53c8\u53d8\u4e3a\u70d8\u7089\uff0c\u6240\u6709\u7684\u751f\u547d\u90fd\u5df2\u88ab\u71c3\u70e7\uff0c\u71c3\u70e7\u7684\u7ec8\u6781\u5c31\u662f\u706d\u4ea1\uff0c\u5c31\u662f\u6b7b\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u8d64\u72ac\u201d\u4e5f\u8d70\u4e86\uff0c\u80cc\u8d1f\u7740\u90a3\u4e2a\u88ab\u8feb\u6765\u6740\u4eba\u7684\u5973\u4eba\u8d70\u4e86\u3002\u4e5f\u8bb8\u5b83\u5e76\u4e0d\u60f3\u8ddf\u5c0f\u65b9\u5206\u624b\uff0c\u53ef\u662f\u5b83\u4e5f\u4e0d\u80fd\u8fdd\u6297\u4ed6\uff0c\u5b83\u6bd5\u7adf\u53ea\u4e0d\u8fc7\u662f\u4e00\u5339\u9a6c\u800c\u5df2\u3002 \u3000\u3000\u9644\u8fd1\u5df2\u770b\u4e0d\u89c1\u522b\u7684\u751f\u547d\uff0c\u5c0f\u65b9\u5012\u5728\u706b\u70ed\u7684\u6c99\u783e\u4e0a\uff0c\u52c9\u5f3a\u652f\u6301\u7740\u4e0d\u8ba9\u773c\u775b\u95ed\u4e0a\u3002 \u3000\u3000\u53ef\u662f\u5927\u5730\u82cd\u7a79\u5728\u4ed6\u773c\u4e2d\u770b\u6765\uff0c\u4eff\u4f5b\u90fd\u5df2\u53d8\u6210\u4e86\u4e00\u56e2\u706b\u7130\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u8fd9\u4e00\u6b21\u662f\u771f\u7684\u8981\u6b7b\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u89c1\u4e86\u4e00\u79cd\u53ea\u6709\u5782\u6b7b\u8005\u624d\u80fd\u770b\u5f97\u89c1\u7684\u5e7b\u8c61\uff0c\u4ed6\u5ffd\u7136\u770b\u89c1\u4e86\u4e00\u884c\u4eea\u4ece\u4e30\u90fd\u7684\u8f7f\u9a6c\uff0c\u51fa\u73b0\u5728\u91d1\u9ec4\u8272\u7684\u9633\u5149\u4e0b\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u90fd\u4eff\u4f5b\u5728\u95ea\u52a8\u7740\u9ec4\u91d1\u822c\u7684\u5149\u8292\uff0c\u624b\u91cc\u90fd\u62ff\u7740\u91d1\u8272\u7684\u6c34\u888b\uff0c\u888b\u4e2d\u76db\u6ee1\u4e86\u871c\u6c41\u822c\u7684\u751c\u6c34\u548c\u7f8e\u9152\u3002 \u3000\u3000\u5982\u679c\u8fd9\u4e0d\u662f\u4ed6\u7684\u5e7b\u89c9\uff0c\u4e0d\u662f\u82cd\u5929\u7528\u6765\u5b89\u629a\u4e00\u4e2a\u5782\u6b7b\u8005\u7684\u5e7b\u89c9\uff0c\u5c31\u4e00\u5b9a\u662f\u9634\u51a5\u4e2d\u6d3e\u6765\u8fce\u63a5\u4ed6\u7684\u4f7f\u8005\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u7ec8\u4e8e\u95ed\u4e86\u8d77\u6765\uff0c\u4ed6\u5df2\u6b7b\u5f97\u95ee\u5fc3\u65e0\u6127\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u5df2\u7ecf\u662f\u4e5d\u6708\u5341\u516d\u3002 \u3000\u3000\u5c0f\u65b9\u9192\u6765\u65f6\uff0c\u7acb\u523b\u5c31\u786e\u5b9a\u4e86\u4e24\u4ef6\u4e8b\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u662f\u5b8c\u5168\u8d64\u88f8\u7684\u3002 \u3000\u3000\u8d64\u88f8\u88f8\u5730\u8eba\u5728\u4e00\u5f20\u94fa\u7740\u8c79\u76ae\u7684\u8f6f\u69bb\u4e0a\u3002\u8fd9\u5f20\u8f6f\u69bb\u6446\u5728\u4e00\u4e2a\u5de8\u5927\u800c\u534e\u4e3d\u7684\u5e10\u7bf7\u89d2\u843d\u91cc\uff0c\u65c1\u8fb9\u7684\u6728\u51e0\u4e0a\u6709\u4e2a\u91d1\u76c6\uff0c\u76c6\u4e2d\u76db\u6ee1\u4e86\u6bd4\u9ec4\u91d1\u66f4\u73cd\u8d35\u7684\u6c34\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u6750\u6781\u82d7\u6761\u3001\u7a7f\u7740\u6c49\u4eba\u88c5\u675f\u3001\u8138\u4e0a\u8499\u7740\u7eb1\u5dfe\u7684\u5973\u4eba\uff0c\u6b63\u5728\u7528\u4e00\u5757\u6781\u67d4\u8f6f\u7684\u4e1d\u4e2d\uff0c\u8638\u7740\u91d1\u76c6\u91cc\u7684\u6c34\uff0c\u64e6\u6d17\u4ed6\u7684\u8eab\u5b50\u3002 \u3000\u3000\u5979\u7684\u624b\u7ea4\u957f\u67d4\u7f8e\uff0c\u5979\u7684\u52a8\u4f5c\u8f7b\u67d4\u800c\u4ed4\u7ec6\uff0c\u5c31\u50cf\u662f\u6536\u85cf\u5bb6\u5728\u64e6\u6d17\u4e00\u4ef6\u521a\u51fa\u571f\u7684\u53e4\u7389\uff0c\u4ece\u4ed6\u7684\u7709\u3001\u773c\u3001\u8138\u3001\u5507\uff0c\u4e00\u76f4\u64e6\u5230\u7684\u811a\u8dbe\uff0c\u751a\u81f3\u628a\u4ed6\u6307\u7532\u91cc\u7684\u5c18\u57a2\u90fd\u64e6\u6d17\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7ecf\u5386\u4e8e\u65e0\u6570\u707e\u96be\uff0c\u51fa\u751f\u5165\u6b7b\u540e\uff0c\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u7f6e\u8eab\u5728\u8fd9\u4e48\u6837\u4e00\u4e00\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u7684\u611f\u89c9\u662f\u60ca\u5947\uff0c\u8fd8\u662f\u6b22\u559c\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u7b2c\u4e00\u79cd\u611f\u89c9\uff0c\u5374\u597d\u8c61\u72af\u4e86\u7f6a\u3002 \u3000\u3000\u5728\u6c99\u6f20\u4e2d\uff0c\u5c45\u7136\u6709\u4eba\u7528\u6bd4\u9ec4\u91d1\u66f4\u73cd\u8d35\u7684\u6c34\u66ff\u4ed6\u6d17\u6fa1\uff0c\u8fd9\u5df1\u4e0d\u4ec5\u662f\u5962\u4f88\uff0c\u7b80\u76f4\u662f\u7f6a\u6076\u3002 \u3000\u3000\u2014\u2014\u8fd9\u91cc\u7684\u4e3b\u4eba\u662f\u8c01\uff1f\u662f\u51c6\u6551\u4e86\u4ed6\uff1f \u3000\u3000\u4ed6\u60f3\u95ee\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5168\u8eab\u4ecd\u7136\u8f6f\u5f31\u65e0\u529b\uff0c\u5589\u5499\u91cc\u4ecd\u7136\u5e72\u6e34\u6b32\u88c2\uff0c\u5634\u91cc\u4ecd\u7136\u82e6\u6da9\uff0c\u8fde\u820c\u5934\u90fd\u4f3c\u5c06\u88c2\u5f00\u3002 \u3000\u3000\u8fd9\u4e2a\u964c\u751f\u7684\u8499\u9762\u5973\u5b50\u867d\u7136\u7528\u6e05\u6c34\u64e6\u904d\u4e86\u4ed6\u5168\u8eab\uff0c\u5374\u6ca1\u6709\u7ed9\u4ed6\u4e00\u6ef4\u6c34\u559d\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u7684\u7b2c\u4e8c\u79cd\u611f\u89c9\u4e5f\u4e0d\u662f\u60ca\u559c\uff0c\u800c\u662f\u6124\u6012\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u6012\u6c14\u5e76\u6ca1\u6709\u53d1\u4f5c\uff0c\u56e0\u4e3a\u4ed6\u53c8\u5ffd\u7136\u53d1\u73b0\u8fd9\u5e10\u7bf7\u91cc\u5e76\u4e0d\u662f\u53ea\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff0c\u53e6\u5916\u8fd8\u6709\u4e2a\u4eba\u6b63\u9759\u9759\u5730\u7ad9\u5728\u5bf9\u9762\u7684\u89d2\u843d\u91cc\uff0c\u9759\u9759\u5730\u770b\u7740\u4ed6\u3002 \u3000\u3000\u4e00\u4e2a\u6709\u81ea\u5c0a\u7684\u7537\u4eba\uff0c\u5728\u522b\u4eba\u7684\u6ce8\u89c6\u4e0b\uff0c\u7adf\u5b8c\u5168\u8d64\u88f8\u7740\uff0c\u50cf\u5a74\u513f\u822c\u88ab\u4e00\u4e2a\u964c\u751f\u7684\u5973\u4eba\u6d17\u64e6\u3002 \u3000\u3000\u8fd9\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u6709\u8c01\u80fd\u53d7\u5f97\u4e86\uff1f \u3000\u3000\u73b0\u5728\u8fd9\u5973\u4eba\u5c45\u7136\u5f00\u59cb\u5728\u64e6\u6d17\u4ed6\u8eab\u4e0a\u6700\u654f\u611f\u7684\u90e8\u5206\u3002\u5982\u679c\u4ed6\u4e0d\u662f\u592a\u7d2f\u3001\u592a\u6e34\u3001\u592a\u997f\uff0c\u4ed6\u7684\u60c5\u6b32\u5f88\u53ef\u80fd\u5df2\u7ecf\u88ab\u6311\u5f15\u8d77\u6765\u3002 \u3000\u3000\u90a3\u79cd\u60c5\u51b5\u66f4\u8ba9\u4eba\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u7528\u529b\u63a8\u5f00\u8fd9\u5973\u4eba\u7684\u624b\uff0c\u6323\u624e\u7740\u5750\u8d77\u6765\uff0c\u60f3\u53bb\u559d\u91d1\u76c6\u91cc\u7684\u6c34\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5148\u559d\u70b9\u6c34\uff0c\u559d\u4e86\u6c34\u624d\u6709\u4f53\u529b\uff0c\u5c31\u7b97\u662f\u6709\u522b\u4eba\u5728\u8fd9\u76c6\u6c34\u4e2d\u6d17\u8fc7\u81ed\u811a\uff0c\u4ed6\u4e5f\u8981\u559d\u4e0b\u53bb\u3002 \u3000\u3000\u53ef\u60dc\u8fd9\u5973\u4eba\u7684\u52a8\u4f5c\u8fdc\u6bd4\u4ed6\u5feb\u5f97\u591a\uff0c\u5ffd\u7136\u5c31\u6367\u8d77\u4e86\u8fd9\u76c6\u6c34\uff0c\u5403\u5403\u5730\u7b11\u7740\uff0c\u94bb\u51fa\u4e86\u5e10\u7bf7\u3002 \u3000\u3000\u5c0f\u65b9\u7adf\u6ca1\u6709\u529b\u91cf\u8ffd\u51fa\u53bb\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u8ffd\u51fa\u53bb\u3002\u4ed6\u8fd8\u662f\u5b8c\u5168\u8d64\u88f8\u7684\uff0c\u5bf9\u9762\u90a3\u4e2a\u964c\u751f\u7684\u7537\u4eba\u8fd8\u5728\u770b\u7740\u4ed6\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u770b\u6e05\u695a\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4ee5\u524d\u4ed6\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u7684\u4eba\uff0c\u4ee5\u540e\u6050\u6015\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u518d\u89c1\u5230\u3002 \u3000\u3000\u5bf9\u9762\u90a3\u4e2a\u89d2\u843d\u91cc\uff0c\u6709\u5f20\u5f88\u5bbd\u5927\u3001\u5f88\u8212\u670d\u7684\u4ea4\u6905\uff0c\u8fd9\u4e2a\u4eba\u5c31\u7ad9\u5728\u501a\u5b50\u524d\u9762\uff0c\u5374\u4e00\u76f4\u6ca1\u6709\u5750\u4e0b\u53bb\u3002 \u3000\u3000\u7b2c\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u4ed6\u7ad9\u5728\u90a3\u91cc\u7684\u6837\u5b50\u8ddf\u522b\u4eba\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u540c\u3002 \u3000\u3000\u53ef\u662f\u4f60\u5982\u679c\u518d\u591a\u770b\u51e0\u773c\uff0c\u5c31\u4f1a\u53d1\u73b0\u4ed6\u7ad9\u7acb\u7684\u59ff\u52bf\u8ddf\u4efb\u4f55\u4eba\u90fd\u4e0d\u540c\u3002 \u3000\u3000\u7a76\u7adf\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u8c01\u4e5f\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u660e\u660e\u7ad9\u5728\u90a3\u91cc\uff0c\u5374\u8ba9\u4eba\u5f88\u96be\u53d1\u73b0\u4ed6\u7684\u5b58\u5728\uff0c\u56e0\u4e3a\u4ed6\u8fd9\u4e2a\u4eba\u597d\u50cf\u5df2\u7ecf\u8ddf\u4ed6\u8eab\u540e\u7684\u6905\u5b50\u3001\u5934\u9876\u7684\u5e10\u7bf7\u3001\u811a\u4e0b\u7684\u5927\u5730\u6eb6\u4e3a\u4e00\u4f53\uff1a \u3000\u3000\u4e0d\u7ba1\u4ed6\u7ad9\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u597d\u50cf\u90fd\u53ef\u4ee5\u8ddf\u90a3\u91cc\u7684\u4e8b\u7269\u5b8c\u5168\u914d\u5408\u3002 \u3000\u3000\u7b2c\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u4ed6\u662f\u7edd\u5bf9\u9759\u6b62\u7684\uff0c\u624b\u8db3\u56db\u80a2\u3001\u8eab\u4f53\u6bdb\u53d1\u3001\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e2a\u5730\u65b9\u90fd\u6ca1\u6709\u52a8\uff0c\u751a\u81f3\u8fde\u5fc3\u8df3\u90fd\u4eff\u4f5b\u5df2\u505c\u6b62\u3002 \u3000\u3000\u53ef\u662f\u4f60\u5982\u679c\u518d\u591a\u770b\u51e0\u773c\uff0c\u5c31\u4f1a\u53d1\u73b0\u4ed6\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e00\u4e2a\u5730\u65b9\u90fd\u4eff\u4f5b\u5728\u52a8\uff0c\u4e00\u76f4\u4e0d\u505c\u5730\u5728\u52a8\u3002\u5982\u679c\u4f60\u4e00\u62f3\u6253\u8fc7\u53bb\uff0c\u4e0d\u7ba1\u4f60\u8981\u6253\u4ed6\u8eab\u4e0a\u4ec0\u4e48\u5730\u65b9\uff0c\u90fd\u53ef\u80fd\u7acb\u523b\u4f1a\u53d7\u5230\u6781\u53ef\u6015\u7684\u53cd\u51fb\u3002 \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u5374\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u660e\u660e\u662f\u770b\u7740\u4f60\uff0c\u773c\u775b\u91cc\u4e5f\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u4ec0\u4e48\u4e1c\u897f\u90fd\u6ca1\u6709\u770b\u89c1\u4e00\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u638c\u4e2d\u6709\u5251\uff0c\u4e00\u67c4\u5f88\u72ed\u3001\u5f88\u957f\u3001\u5f88\u8f7b\u7684\u4e4c\u9798\u5251\u3002 \u3000\u3000\u4ed6\u7684\u5251\u4ecd\u5728\u9798\u91cc\u3002 \u3000\u3000\u53ef\u662f\u4f60\u53ea\u8981\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u5c31\u4f1a\u611f\u89c9\u5230\u4e00\u79cd\u903c\u4eba\u7684\u5251\u6c14\u3002\u4ed6\u624b\u4e0a\u90a3\u67c4\u8fd8\u6ca1\u6709\u62d4\u51fa\u9798\u7684\u5251\uff0c\u4eff\u4f5b\u5df2\u7ecf\u5728\u4f60\u7684\u7709\u776b\u54bd\u5589\u95f4\u3002 \u3000\u3000\u5c0f\u65b9\u5b9e\u5728\u4e0d\u60f3\u518d\u53bb\u591a\u770b\u8fd9\u4e2a\u4eba\uff0c\u5374\u53c8\u504f\u504f\u5fcd\u4e0d\u4f4f\u8981\u53bb\u770b\u3002\u8fd9\u4e2a\u4eba\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u5728\u770b\u522b\u4eba\u7684\u65f6\u5019\uff0c\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u611f\u89c9\u3002\u522b\u4eba\u53bb\u770b\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u597d\u50cf\u5b8c\u5168\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5929\u4e0a\u5730\u4e0b\u7684\u4e07\u4e8b\u4e07\u7269\uff0c\u4ed6\u597d\u50cf\u6839\u672c\u5c31\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u522b\u4eba\u5bf9\u4ed6\u7684\u770b\u6cd5\uff0c\u4ed6\u66f4\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5173\u5fc3\u7684\u53ea\u6709\u4e00\u4ef6\u4e8b\u2014\u2014 \u3000\u3000\u4ed6\u7684\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u624b\u5fc3\u6e7f\u4e86\u3002 \u3000\u3000\u53ea\u6709\u5728\u52bf\u96be\u4e24\u5b58\u7684\u751f\u6b7b\u640f\u6740\u4e4b\u524d\uff0c\u4ed6\u7684\u624b\u5fc3\u624d\u4f1a\u53d1\u6e7f\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u53ea\u4e0d\u8fc7\u770b\u4e86\u8fd9\u4e2a\u4eba\u51e0\u773c\uff0c\u8fd9\u4e2a\u4eba\u65e2\u6ca1\u6709\u52a8\uff0c\u5bf9\u4ed6\u4e5f\u6ca1\u6709\u654c\u610f\uff0c\u4ed6\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u53cd\u5e94\uff1f \u3000\u3000\u96be\u9053\u4ed6\u4eec\u5929\u751f\u5c31\u662f\u5bf9\u5934\uff1f\u8fdf\u65e9\u603b\u8981\u6709\u4e00\u4e2a\u4eba\u6b7b\u5728\u5bf9\u65b9\u624b\u91cc\uff1f \u3000\u3000\u8fd9\u79cd\u4e8b\u5f53\u7136\u6700\u597d\u4e0d\u8981\u53d1\u751f\u3002\u4ed6\u4eec\u4e4b\u95f4\u5e76\u6ca1\u6709\u6069\u6028\uff0c\u66f4\u6ca1\u6709\u4ec7\u6068\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6210\u4e3a\u4ec7\u654c\uff1f \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5c0f\u65b9\u5fc3\u91cc\u5374\u4f3c\u4e4e\u5df2\u6709\u4e86\u79cd\u4e0d\u7965\u7684\u9884\u5146\uff0c\u4eff\u4f5b\u5df2\u770b\u89c1\u4ed6\u4eec\u4e4b\u95f4\u6709\u4e2a\u4eba\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u5bf9\u65b9\u7684\u5251\u4e0b\uff0c\u5012\u5728\u81ea\u5df1\u7684\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u4ed6\u770b\u4e0d\u89c1\u5012\u4e0b\u53bb\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002 \u3000\u3000\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u53c8\u54cd\u8d77\u3002 \u3000\u3000\u90a3\u4e2a\u8499\u9762\u7684\u5973\u4eba\u53c8\u4ece\u5e10\u7bf7\u5916\u94bb\u4e86\u8fdb\u6765\uff0c\u624b\u91cc\u8fd8\u6367\u7740\u90a3\u4e2a\u91d1\u76c6\u3002 \u3000\u3000\u5979\u7684\u7b11\u58f0\u6e05\u60a6\u751c\u7f8e\uff0c\u4e0d\u4f46\u663e\u51fa\u5979\u81ea\u5df1\u7684\u6b22\u60a6\uff0c\u4e5f\u53ef\u4ee5\u4ee4\u522b\u4eba\u6109\u5feb\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u5341\u5206\u4e0d\u6109\u5feb\u3002\u4e5f\u60f3\u4e0d\u901a\u5979\u4e3a\u4ec0\u4e48\u4f1a\u7b11\u5f97\u5982\u6b64\u6109\u5feb\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u7ed9\u6211\u559d\u70b9\u6c34\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u3002\u201d\u5979\u5e26\u7740\u7b11\u6447\u5934\uff0c\u201c\u8fd9\u76c6\u6c34\u5df2\u7ecf\u810f\u4e86\uff0c\u4e0d\u80fd\u559d\u3002\u201d \u3000\u3000\u201c\u810f\u6c34\u4e5f\u662f\u6c34\uff0c\u53ea\u8981\u662f\u6c34\uff0c\u5c31\u80fd\u591f\u89e3\u6e34\u3002\u201d \u3000\u3000\u201c\u6211\u8fd8\u662f\u4e0d\u80fd\u7ed9\u4f60\u559d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u76c6\u6c34\u672c\u6765\u5c31\u4e0d\u662f\u7ed9\u4f60\u559d\u7684\u3002\u201d \u3000\u3000\u5979\u8fd8\u5728\u7b11\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u5728\u6c99\u6f20\u91cc\u6c34\u6709\u591a\u4e48\u73cd\u8d35\uff0c\u8fd9\u662f\u6211\u7684\u6c34\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u7ed9\u4f60\u559d\uff1f\u201d \u3000\u3000\u201c\u4f60\u5b81\u53ef\u7528\u76c6\u6c34\u66ff\u6211\u6d17\u6fa1\uff0c\u5374\u4e0d\u80af\u7ed9\u6211\u559d\uff1f\u201d \u3000\u3000\u201c\u90a3\u5b8c\u5168\u662f\u4e24\u56de\u4e8b\u3002\u201d \u3000\u3000\u4e3a\u4ec0\u4e48\u662f\u4e24\u56de\u4e8b\uff1f\u5c0f\u65b9\u5b8c\u5168\u4e0d\u61c2\uff0c\u5979\u8bf4\u7684\u8bdd\u5b9e\u5728\u8ba9\u4eba\u5f88\u96be\u542c\u5f97\u61c2\u3002 \u3000\u3000\u5e78\u597d\u5979\u5df2\u7ecf\u5728\u89e3\u91ca\u3002 \u3000\u3000\u201c\u66ff\u4f60\u6d17\u6fa1\uff0c\u662f\u6211\u7684\u4eab\u53d7\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u4eab\u53d7\uff1f\u4ec0\u4e48\u4eab\u53d7\uff1f\u201d\u5c0f\u65b9\u66f4\u4e0d\u61c2\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e2a\u8eab\u6750\u5f88\u597d\u7684\u5e74\u9752\u7537\u4eba\uff0c\u4ece\u5934\u5230\u811a\u90fd\u53d1\u80b2\u5f97\u5f88\u597d\uff0c\u66ff\u4f60\u6d17\u6fa1\uff0c\u6211\u89c9\u5f97\u5f88\u6109\u5feb\uff0c\u5982\u679c\u8ba9\u4f60\u559d\u4e0b\u53bb\uff0c\u5c31\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u66f4\u751c\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u660e\u767d\u4e86\u6211\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u60f3\u5bf9\u5979\u7b11\u7b11\uff0c\u5374\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u867d\u7136\u5df2\u7ecf\u542c\u61c2\u4e86\u5979\u7684\u8bdd\uff0c\u5374\u4e0d\u61c2\u5979\u600e\u4e48\u8bf4\u5f97\u51fa\u8fd9\u79cd\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u7b80\u76f4\u4e0d\u50cf\u4eba\u8bdd\u3002 \u3000\u3000\u5979\u81ea\u5df1\u5374\u597d\u50cf\u89c9\u5f97\u5f88\u6709\u7406\uff1a\u201c\u8fd9\u662f\u6211\u7684\u6c34\uff0c\u968f\u4fbf\u6211\u600e\u4e48\u7528\u5b83\uff0c\u90fd\u8ddf\u4f60\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u3002\u5982\u679c\u4f60\u8981\u559d\u6c34\uff0c\u5c31\u5f97\u81ea\u5df1\u53bb\u60f3\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5979\u7b11\u8d77\u6765\u7684\u65f6\u5019\uff0c\u773c\u775b\u90fd\u5f2f\u5f2f\u5730\u772f\u4e86\u8d77\u6765\uff0c\u50cf\u4e00\u94a9\u65b0\u6708\uff0c\u53c8\u50cf\u662f\u4e2a\u9c7c\u94a9\uff0c\u53ea\u4e0d\u8fc7\u65e0\u8bba\u8c01\u90fd\u80fd\u770b\u5f97\u51fa\u5979\u60f3\u9493\u7684\u4e0d\u662f\u9c7c\uff0c\u800c\u662f\u4eba\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u60f3\u4e0d\u51fa\u6cd5\u5b50\u6765\uff0c\u6211\u4eec\u53ef\u4ee5\u6307\u70b9\u4f60\u4e00\u6761\u660e\u8def\u3002\u201d \u3000\u3000\u8fd9\u662f\u53e5\u4eba\u8bdd\u3002 \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\uff1a\u201c\u6211\u7528\u4ec0\u4e48\u6cd5\u5b50\u624d\u80fd\u627e\u5230\u6c34\uff0c\u5230\u54ea\u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u5979\u5ffd\u7136\u4f38\u51fa\u4e00\u53ea\u67d4\u767d\u7684\u624b\uff0c\u5411\u5c0f\u65b9\u80cc\u540e\u6307\u4e86\u6307\uff1a\u201c\u4f60\u53ea\u8981\u56de\u8fc7\u5934\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u56de\u8fc7\u4e86\u5934\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u3001\u5df2\u7ecf\u6709\u4e2a\u4eba\u4ece\u540e\u9762\u8d70\u4eba\u4e86\u5e10\u7bf7\u3002 \u3000\u3000\u5e73\u65f6\u5c31\u7b97\u662f\u6709\u53ea\u732b\u6f5c\u4e86\u8fdb\u6765\uff0c\u4e5f\u4e00\u5b9a\u65e9\u5df2\u88ab\u4ed6\u53d1\u89c9\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u592a\u7d2f\u3001\u592a\u6e34\u3001\u592a\u60f3\u559d\u6c34\uff0c\u53ea\u7b49\u5230\u4ed6\u56de\u8fc7\u5934\uff0c\u624d\u770b\u89c1\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u7684\u662f\u536b\u5929\u9e4f\u3002 \u3000\u3000\u536b\u5929\u9e4f\u8eab\u6750\u9ad8\u5927\uff0c\u6001\u5ea6\u4e25\u8083\uff0c\u6c14\u52bf\u6c89\u731b\uff0c\u5341\u5206\u8bb2\u7a76\u8863\u7740\uff0c\u8138\u4e0a\u7ec8\u5e74\u96be\u5f97\u9732\u51fa\u7b11\u5bb9\uff0c\u4e00\u53cc\u68f1\u68f1\u6709\u5a01\u7684\u773c\u775b\u91cc\uff0c\u5145\u6ee1\u4e86\u767e\u6298\u4e0d\u56de\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4efb\u4f55\u65f6\u5019\uff0c\u4efb\u4f55\u5730\u65b9\uff0c\u4ed6\u90fd\u80fd\u8ba9\u522b\u4eba\u4fdd\u6301\u5bf9\u4ed6\u7684\u5c0a\u656c\u3002 \u3000\u3000\u4ed6\u505a\u7684\u4e8b\u901a\u5e38\u4e5f\u90fd\u503c\u5f97\u522b\u4eba\u5c0a\u656c\u3002 \u3000\u3000\u4eca\u5e74\u4ed6\u4e94\u5341\u4e09\u5c81\u3002\u4e8c\u5341\u4e00\u5c81\u65f6\uff0c\u4ed6\u5c31\u5df2\u7ecf\u662f\u5173\u4e2d\u6700\u5927\u4e00\u5bb6\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u8fd9\u4e09\u5341\u5e74\u6765\uff0c\u59cb\u7ec8\u4e00\u5e06\u98ce\u987a\uff0c\u4ece\u672a\u9047\u5230\u8fc7\u592a\u5927\u7684\u632b\u6298\u3002 \u3000\u3000\u76f4\u5230\u6628\u5929\u4ed6\u624d\u9047\u5230\u3002 \u3000\u3000\u9ec4\u91d1\u5931\u52ab\uff0c\u4ed6\u4e5f\u6709\u8d23\u4efb\uff0c\u4ed6\u7684\u4eb2\u4fe1\u5f1f\u5b50\uff0c\u51e0\u4e4e\u5168\u90fd\u5ffd\u7136\u60e8\u6b7b\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u770b\u6765\u4ecd\u7136\u540c\u6837\u5a01\u4e25\u5c0a\u8d35\uff0c\u90a3\u79cd\u53ef\u6015\u7684\u6253\u51fb\u7adf\u672a\u80fd\u8ba9\u4ed6\u6709\u4e1d\u6beb\u6539\u53d8\u3002 \u3000\u3000\u5c0f\u65b9\u7528\u8f6f\u69bb\u4e0a\u7684\u8c79\u76ae\u56f4\u4f4f\u4e86\u8170\uff0c\u624d\u62ac\u8d77\u5934\u9762\u5bf9\u536b\u5929\u9e4f\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u662f\u4f60\u6551\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u6551\u4f60\u3002\u201d\u536b\u5929\u9e4f\u9053\uff1a\u201c\u8c01\u4e5f\u6551\u4e0d\u4e86\u4f60\uff0c\u53ea\u6709\u4f60\u81ea\u5df1\u624d\u80fd\u6551\u81ea\u5df1\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u4e00\u5411\u7b80\u77ed\u76f4\u63a5\uff1a\u201c\u4f60\u6740\u4e86\u5bcc\u8d35\u795e\u4ed9\u7684\u72ec\u751f\u513f\u5b50\uff0c\u672c\u6765\u4e00\u5b9a\u662f\u8981\u4e3a\u4ed6\u507f\u547d\u7684\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u5e94\u8be5\u5df2\u7ecf\u6b7b\u5728\u6c99\u6f20\u4e2d\uff0c\u6b7b\u5728\u5979\u7684\u624b\u91cc\u3002\u201d \u3000\u3000\u4ed6\u6240\u8bf4\u7684\u201c\u5979\u201d\uff0c\u7adf\u662f\u90a3\u4e2a\u8499\u9762\u7684\u5973\u4eba\u3002 \u3000\u3000\u536b\u5927\u9e4f\u5ffd\u7136\u53c8\u95ee\uff1a\u201c\u4f60\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u5c45\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u5979\u4e00\u5b9a\u8ba4\u4e3a\u6211\u5df1\u8ba4\u4e0d\u51fa\u5979\u4e86\uff0c\u56e0\u4e3a\u4eca\u5929\u65e9\u4e0a\u6211\u770b\u89c1\u5979\u7684\u65f6\u5019\uff0c\u5979\u8fd8\u662f\u4e2a\u5feb\u8981\u6b7b\u4e86\u7684\u53ef\u601c\u5973\u4eba\uff0c\u88ab\u4eba\u903c\u7740\u53bb\u6740\u6211\uff0c\u53cd\u800c\u4e2d\u4e86\u6211\u4e00\u5251\uff0c\u6c34\u888b\u91cc\u53ea\u5269\u4e0b\u4e24\u53e3\u6c34\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u56e0\u4e3a\u5979\u5df2\u77e5\u9053\u672a\u5fc5\u80fd\u6740\u5f97\u6b7b\u6211\uff0c\u6240\u4ee5\u65e9\u5c31\u7559\u597d\u9000\u8def\uff0c\u6c34\u888b\u91cc\u7684\u6c34\u5f53\u7136\u4e0d\u80fd\u5e26\u5f97\u5927\u591a\uff0c\u514d\u5f97\u88ab\u6211\u62a2\u8d70\uff0c\u6837\u5b50\u4e00\u5b9a\u8981\u88c5\u5f97\u5341\u5206\u53ef\u601c\uff0c\u624d\u80fd\u6253\u52a8\u6211\u3002\u201d \u3000\u3000\u5979\u4e00\u76f4\u5728\u542c\uff0c\u4e00\u76f4\u5728\u7b11\uff0c\u7b11\u5f97\u5f53\u7136\u6bd4\u521a\u624d\u66f4\u6109\u5feb\uff1a\u201c\u90a3\u65f6\u4f60\u5c31\u4e0d\u8be5\u76f8\u4fe1\u6211\u7684\uff0c\u53ea\u53ef\u60dc\u4f60\u7684\u5fc3\u592a\u8f6f\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5ffd\u53c8\u5f00\u53e3\uff1a\u201c\u53ef\u662f\u5979\u7684\u5fc3\u5374\u7edd\u4e0d\u8f6f\uff0c\u2018\u6c34\u94f6\u2019\u6740\u4eba\u65f6\uff0c\u5fc3\u7edd\u4e0d\u4f1a\u8f6f\uff0c\u624b\u4e5f\u7edd\u4e0d\u4f1a\u8f6f\u3002\u201d \u3000\u3000\u8fd9\u5973\u4eba\u5c31\u662f\u6c34\u94f6\uff0c\u65e0\u5b54\u4e0d\u5165\u7684\u6c34\u94f6\uff01 \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u597d\u8c61\u5e76\u4e0d\u89c9\u5f97\u610f\u5916\u3002 \u3000\u3000\u536b\u5929\u9e4f\u53c8\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6709\u6740\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6447\u5934\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u56e0\u4e3a\u5415\u5929\u5b9d\u5df2\u7ecf\u6b7b\u4e86\uff0c\u90a3\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u5374\u4ecd\u5728\u3002\u201d \u3000\u3000\u201c\u5415\u5929\u5b9d\u8ddf\u90a3\u6279\u9ec4\u91d1\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u4e00\u70b9\u5173\u7cfb\u3002\u201d\u536b\u5929\u9e4f\u9053\uff1a\u201c\u90a3\u6279\u9ec4\u91d1\u4e5f\u662f\u2018\u5bcc\u8d35\u795e\u4ed9\u2019\u5415\u4e09\u7237\u7684\u3002\u201d \u3000\u3000\u6c34\u94f6\u9053\uff1a\u201c\u65e0\u8bba\u51c6\u6b7b\u4e86\u4e4b\u540e\uff0c\u90fd\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6b7b\u4eba\u800c\u5df2\uff0c\u5728\u5415\u4e09\u7237\u773c\u4e2d\u770b\u6765\uff0c\u4e00\u4e2a\u6b7b\u4eba\u5f53\u7136\u6bd4\u4e0d\u4e0a\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u3002\u201d\u5979\u5403\u5403\u5730\u7b11\u7740\uff1a\u201c\u5426\u5219\u4ed6\u600e\u4e48\u4f1a\u53d1\u8d22\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u53ea\u8981\u80fd\u5e2e\u6211\u627e\u51fa\u90a3\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u7684\u4e0b\u843d\uff0c\u6211\u4fdd\u8bc1\u4ed6\u7edd\u4e0d\u4f1a\u518d\u627e\u4f60\u590d\u4ec7\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u542c\u8d77\u6765\u8fd9\u5012\u662f\u4e2a\u5f88\u597d\u7684\u4ea4\u6613\u3002\u201d \u3000\u3000\u6c34\u94f6\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u76f4\u6000\u7591\u9ec4\u91d1\u662f\u88ab\u535c\u9e70\u52ab\u8d70\u7684\uff0c\u6211\u6b63\u597d\u8ba4\u5f97\u4ed6\uff0c\u6b63\u597d\u53bb\u66ff\u4f60\u4eec\u8c03\u67e5\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6c34\u94f6\u7b11\u9053\uff1a\u201c\u4f60\u5b9e\u5728\u4e0d\u7b28\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u53ea\u8981\u4f60\u80af\u7b54\u5e94\uff0c\u4e0d\u7ba1\u4f60\u9700\u8981\u4ec0\u4e48\uff0c\u6211\u4eec\u90fd\u53ef\u4ee5\u4f9b\u7ed9\u4f60\u3002\u201d \u3000\u3000\uff0e\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\u535c\u9e70\u7684\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u4eec\u53ef\u5e2e\u4f60\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u541f\u7740\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u535c\u9e70\u5e76\u6ca1\u6709\u628a\u6211\u5f53\u4f5c\u670b\u53cb\u3002\u66ff\u4fdd\u9556\u7684\u4eba\u53bb\u6293\u5f3a\u76d7\uff0c\u4e5f\u4e0d\u80fd\u7b97\u4e22\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u7b54\u5e94\uff0c\u4f60\u4eec\u5c31\u7b97\u4e0d\u6740\u6211\uff0c\u6211\u4e5f\u4f1a\u88ab\u6d3b\u6d3b\u5730\u6e34\u6b7b\u3002\u201d \u3000\u3000\u6c34\u94f6\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u79cd\u6ecb\u5473\u53ef\u771f\u7684\u4e0d\u597d\u53d7\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u597d\u50cf\u975e\u7b54\u5e94\u4f60\u4eec\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u6c34\u94f6\u67d4\u58f0\u9053\uff1a\u201c\u6062\u786e\u5b9e\u5df2\u7ecf\u6ca1\u6709\u522b\u7684\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u8d77\u6765\u597d\u50cf\u786e\u5b9e\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u6c34\u94f6\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5df2\u7ecf\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u6c34\u94f6\u9053\uff1a\u201c\u4f60\u8fd8\u5728\u8003\u8651\u4ec0\u4e48\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u56db\u7ae0\u3000\u751f\u6b7b\u4e4b\u95f4 \u7b2c\u56db\u7ae0\u3000\u751f\u6b7b\u4e4b\u95f4 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u90fd\u6ca1\u6709\u8003\u8651\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u7b54\u5e94\uff0c\u8fd8\u662f\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4e0d\u7b54\u5e94\u3002\u201d \u3000\u3000\u4ed6\u7684\u56de\u7b54\u76f4\u63a5\u800c\u7b80\u5355\uff0c\u7b80\u5355\u5f97\u8981\u547d\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u6ca1\u6709\u53d8\uff0c\u53ef\u662f\u773c\u89d2\u7684\u808c\u8089\u5df1\u62bd\u7d27\uff0c\u77b3\u5b54\u5df2\u6536\u7f29\u3002 \u3000\u3000\u6c34\u94f6\u773c\u775b\u91cc\u9732\u51fa\u79cd\u590d\u6742\u800c\u5947\u602a\u7684\u8868\u60c5\uff0c\u4eff\u4f5b\u89c9\u5f97\u5f88\u60ca\u8bb6\uff0c\u53c8\u4eff\u4f5b\u89c9\u5f97\u5f88\u6b23\u8d4f\u3001\u5f88\u6709\u8da3\u3002 \u3000\u3000\u5979\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u7b11\u4e86\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u9ad8\u5174\u3002\u201d \u3000\u3000\u8fd9\u7406\u7531\u975e\u4f46\u4e0d\u591f\u597d\uff0c\u6839\u672c\u5c31\u4e0d\u80fd\u6210\u4e3a\u7406\u7531\u3002\u771f\u6b63\u7684\u7406\u7531\u662f\u4ec0\u4e48\uff1f\u5c0f\u65b9\u4e0d\u60f3\u8bf4\u51fa\u6765\uff0c\u4ed6\u505a\u4e8b\u4e00\u5411\u6709\u4ed6\u7684\u539f\u5219\uff0c\u522b\u4eba\u4e00\u5411\u5f88\u96be\u4e86\u89e3\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u522b\u4eba\u4e86\u89e3\u3002 \u3000\u3000\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u89c9\u5f97\u53ea\u8981\u80fd\u8ba9\u81ea\u5df1\u95f4\u5fc3\u65e0\u6127\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u6c34\u94f6\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u536b\u5929\u9e4f\u662f\u4e0d\u4f1a\u6740\u4f60\u7684\uff0c\u4ed6\u4ece\u4e0d\u52c9\u5f3a\u522b\u4eba\u505a\u4efb\u4f55\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u79cd\u597d\u4e60\u60ef\uff0c\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u6709\u8fd9\u79cd\u597d\u4e60\u60ef\u3002\u201d \u3000\u3000\u6c34\u94f6\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4f1a\u6740\u4f60\uff0c\u56e0\u4e3a\u6211\u5df2\u7ecf\u7b54\u5e94\u8fc7\u4f60\uff0c\u7edd\u4e0d\u518d\u5bb3\u4f60\u3002\u201d \u3000\u3000\u5979\u4e5f\u5bf9\u5c0f\u65b9\u7b11\u7b11\uff1a\u201c\u5b88\u4fe1\u4e5f\u662f\u79cd\u597d\u4e60\u60ef\uff0c\u4f60\u4e00\u5b9a\u4e5f\u60f3\u4e0d\u5230\u6211\u4f1a\u6709\u79cd\u597d\u4e60\u60ef\u5c38 \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\uff1a\u201c\u5973\u4eba\u80fd\u6709\u8fd9\u79cd\u597d\u4e60\u60ef\u7684\u786e\u5b9e\u4e0d\u591a\u3002\u201d \u3000\u3000\u6c34\u94f6\u9053\uff1a\u201c\u6211\u4eec\u53ea\u4e0d\u8fc7\u60f3\u628a\u4f60\u9001\u56de\u53bb\uff0c\u8ba9\u4f60\u4e00\u4e2a\u4eba\u5b89\u5b89\u9759\u9759\u8eba\u5728\u90a3\u91cc\u7b49\u6b7b\u3002\u201d \u3000\u3000\u7b49\u6b7b\u6bd4\u6b7b\u66f4\u75db\u82e6\uff0c\u66f4\u96be\u5fcd\u53d7\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u5728\u7b49\u6b7b\uff0c\u518d\u53bb\u7b49\u7b49\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u8fd8\u662f\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u56de\u7b54\u8fd8\u662f\u5982\u6b64\u7b80\u5355\uff0c\u7b80\u5355\u5f97\u8981\u547d\u3002 \u3000\u3000\u5e10\u7bf7\u5916\u53c8\u522e\u8d77\u98ce\uff0c\u5439\u8d77\u6ee1\u5929\u9ec4\u6c99\uff0c\u767d\u663c\u5f88\u5feb\u5c31\u5c06\u8fc7\u53bb\uff0c\u9ed1\u6697\u5f88\u5feb\u5c31\u5c06\u5e26\u7740\u6b7b\u4ea1\u6765\u4e34\u3002 \u3000\u3000\u5728\u8fd9\u7247\u65e0\u60c5\u7684\u5927\u5730\u4e0a\uff0c\u751f\u547d\u7684\u4ef7\u503c\u672c\u5c31\u5df2\u53d8\u5f97\u5341\u5206\u5351\u8d31\u6e3a\u5c0f\uff0c\u80fd\u6d3b\u4e0b\u53bb\u56fa\u7136\u8981\u6d3b\u4e0b\u53bb\uff0c\u4e0d\u80fd\u6d3b\u4e0b\u53bb\uff0c\u6b7b\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u5c0f\u65b9\u53c8\u8eba\u4e86\u4e0b\u53bb\uff0c\u597d\u50cf\u5df2\u7ecf\u51c6\u5907\u8ba9\u4ed6\u4eec\u9001\u56de\u98ce\u6c99\u4e2d\u53bb\u7b49\u6b7b\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u521a\u60f3\u95ed\u4e0a\u773c\u775b\u65f6\uff0c\u4ed6\u5ffd\u7136\u542c\u89c1\u4e00\u4e2a\u4eba\u7528\u5947\u7279\u800c\u751f\u51b7\u7684\u58f0\u97f3\u5728\u95ee\u4ed6\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u6015\u6b7b\uff1f\u201d \u3000\u3000\u4ed6\u7528\u4e0d\u7740\u5f20\u5f00\u773c\u775b\u770b\uff0c\u5c31\u5df2\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e00\u76f4\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u9759\u9759\u5730\u770b\u7740\u4ed6\u3002\u76ee\u5149\u4ece\u672a\u79fb\u52a8\u8fc7\u7247\u523b\uff0c\u773c\u775b\u5374\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5728\u770b\u7740\u5c0f\u65b9\u65f6\uff0c\u5c31\u597d\u8c61\u4e00\u53ea\u732b\u5728\u770b\u7740\u4e00\u53ea\u5df2\u7ecf\u843d\u5165\u4e86\u86db\u7f51\u7684\u6606\u866b\u3002 \u3000\u3000\u5b83\u4eec\u672c\u5c31\u4e0d\u662f\u540c\u7c7b\u7684\u3002 \u3000\u3000\u751f\u547d\u672c\u5c31\u5982\u6b64\u5351\u8d31\uff0c\u751f\u6b7b\u95f4\u7684\u6323\u624e\u5f53\u7136\u4e5f\u53d8\u5f97\u5341\u5206\u611a\u8822\u53ef\u7b11\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u52a8\u5fc3\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u5374\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u6015\u6b7b\uff1f\u201d\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4ece\u672a\u89c1\u8fc7\u771f\u4e0d\u6015\u6b7b\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u62d2\u7edd\u56de\u7b54\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u80fd\u786e\u5b9e\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5df2\u7ecf\u8fd9\u6837\u505a\u4e86\uff0c\u5df2\u7ecf\u73b0\u51fa\u4e00\u79cd\u4eba\u7c7b\u5728\u9762\u4e34\u751f\u6b7b\u6289\u62e9\u65f6\u7684\u5c0a\u4e25\u4e0e\u52c7\u6c14\u3002 \u3000\u3000\u6709\u4e9b\u95ee\u9898\u6839\u672c\u5c31\u7528\u4e0d\u7740\u8a00\u8bed\u6765\u56de\u7b54\uff0c\u4e5f\u4e0d\u662f\u8a00\u8bed\u6240\u80fd\u56de\u7b54\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5c45\u7136\u80fd\u4e86\u89e3\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u6ca1\u6709\u518d\u95ee\uff0c\u5374\u6162\u6162\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u4ed6\u8d70\u8def\u59ff\u6001\u4e5f\u8ddf\u4ed6\u7ad9\u7acb\u4e4b\u65f6\u540c\u6837\u5947\u7279\u3002 \u3000\u3000\u522b\u4eba\u6839\u672c\u6ca1\u6709\u770b\u89c1\u4ed6\u79fb\u52a8\uff0c\u53ef\u662f\u4ed6\u5ffd\u7136\u5df2\u5230\u4e86\u5c0f\u65b9\u8eba\u7740\u7684\u90a3\u5f20\u8f6f\u69bb\u524d\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5251\u5c31\u6446\u5728\u8f6f\u69bb\u65c1\u90a3\u6728\u51e0\u4e0a\uff0c\u4ed6\u5ffd\u7136\u53c8\u95ee\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u4e0d\u96be\u56de\u7b54\uff0c\u4e5f\u4e0d\u5fc5\u62d2\u7edd\u56de\u7b54\u3002 \u3000\u3000\u201c\u662f\uff0c\u662f\u6211\u7684\u5251\u3002\u201d \u3000\u3000\u201c\u4f60\u4f7f\u5251\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u5982\u60ca\u8679\u95ea\u7535\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u4eba\u4f38\u624b\u53bb\u62ff\u5251\uff0c\u62d4\u5251\uff0c\u53ef\u662f\uff0c\u6728\u51e0\u4e0a\u7684\u5251\u5ffd\u7136\u5c31\u5df2\u5230\u4e86\u4ed6\u624b\u91cc\u3002 \u3000\u3000\u5251\u5df2\u51fa\u9798\u3002 \u3000\u3000\u4e00\u67c4\u51fa\u4e86\u9798\u7684\u5251\u5230\u4e86\u4ed6\u624b\u91cc\uff0c\u4ed6\u8fd9\u4e2a\u4eba\u7acb\u523b\u53d8\u4e86\uff0c\u53d8\u5f97\u4f3c\u4e4e\u5df2\u8ddf\u4ed6\u624b\u91cc\u7684\u5251\u4e00\u6837\uff0c\u4e5f\u53d1\u51fa\u4e86\u60ca\u8679\u95ea\u7535\u822c\u7684\u593a\u76ee\u5149\u8292\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u79cd\u5149\u8292\u8f6c\u77ac\u5c31\u5df2\u6d88\u5931\uff0c\u56e0\u4e3a\u4ed6\u638c\u4e2d\u7684\u5251\u5ffd\u7136\u53c8\u5df2\u4eba\u9798\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u7acb\u523b\u53c8\u53d8\u5f97\u7edd\u5bf9\u9759\u6b62\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u4e00\u4e2a\u4e00\u4e2a\u5b57\u8bf4\uff1a\u201c\u4e16\u4eba\u94f8\u5251\u5343\u4e07\uff0c\u80fd\u79f0\u4e3a\u5229\u5668\u5374\u53ea\u4e0d\u8fc7\u5176\u4e2d\u4e8c\u4e09\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u5b9d\u5251\u540d\u9a79\uff0c\u672c\u6765\u5c31\u53ef\u9047\u4e0d\u53ef\u6c42\uff0c\u4e07\u4e2d\u80fd\u5f97\u5176\u4e00\uff0c\u5df2\u7ecf\u4e0d\u80fd\u7b97\u5c11\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5251\u662f\u5229\u5668\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fae\u7b11\uff1a\u201c\u4f60\u7684\u773c\u4e5f\u5f88\u5229\u3002\u201d \u3000\u3000\u8fd9\u4eba\u53c8\u95ee\uff1a\u201c\u4f60\u7528\u5b83\u6740\u8fc7\u4eba\uff1f\u201d \u3000\u3000\u201c\u5076\u4e00\u4e3a\u4e4b\uff0c\u53ea\u6740\u8be5\u6740\u7684\u3002\u201d \u3000\u3000\u201c\u5584\u7528\u5229\u5668\u8005\uff0c\u624d\u80fd\u6740\u4eba\u8010\u672a\u88ab\u6740\uff0c\u4f60\u7684\u5251\u6cd5\u60f3\u5fc5\u4e0d\u5dee\u3002\u201d \u3000\u3000\u201c\u8fd8\u7b97\u8fc7\u5f97\u53bb\uff0c\u201d \u3000\u3000\u8fd9\u4eba\u53c8\u6c89\u9ed8\u826f\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u53e6\u5916\u8fd8\u6709\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u54ea\u6761\u8def\uff1f\u600e\u4e48\u8d70\u5e7f \u3000\u3000\u201c\u7528\u4f60\u7684\u5251\u6740\u4e86\u6211\u3002\u201d\u4ed6\u58f0\u97f3\u91cc\u5168\u65e0\u60c5\u611f\uff0c\u201c\u4f60\u80fd\u6740\u6211\uff0c\u4f60\u5c31\u53ef\u4ee5\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u201c\u5426\u5219\uff0c\u6211\u662f\u4e0d\u662f\u5c31\u8981\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6709\u8d44\u683c\u6b7b\u5728\u6211\u5251\u4e0b\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u4f60\u80fd\u6b7b\u5728\u6211\u5251\u4e0b\uff0c\u5df2\u53ef\u7b97\u6b7b\u800c\u65e0\u61be\u3002\u201d\u2026 \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5b9e\u5728\u592a\u72c2\uff0c\u5982\u679c\u662f\u522b\u4eba\u8bf4\u51fa\u7684\uff0c\u5c0f\u65b9\u5f88\u53ef\u80fd\u4f1a\u7b11\u51fa\u6765\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u7b11\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u4e0d\u80fd\u7b11\uff0c\u56e0\u4e3a\u4ed6\u770b\u5f97\u51fa\u8fd9\u4e2a\u4eba\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u7b80\u7b80\u5355\u5355\u7684\u4e00\u53e5\u771f\u8bdd\uff0c\u65e2\u6ca1\u6709\u70ab\u8000\uff0c\u4e5f\u4e0d\u662f\u606b\u5413\u3002\u4ed6\u8bf4\u8fd9\u53e5\u8bdd\u65f6\uff0c\u53ea\u4e0d\u8fc7\u8bf4\u51fa\u4e86\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u6837\uff0c\u80fd\u6b7b\u5728\u8fd9\u4eba\u7684\u5251\u4e0b\uff0c\u603b\u6bd4\u8eba\u5728\u90a3\u91cc\u7b49\u6b7b\u597d\u3002 \u3000\u3000\u80fd\u4e0e\u8fd9\u6837\u7684\u9ad8\u624b\u51b3\u4e00\u751f\u6b7b\u80dc\u8d1f\uff0c\u5c82\u975e\u4e5f\u6b63\u662f\u5b66\u5251\u8005\u7684\u751f\u5e73\u7094\u4e8b\uff1f \u3000\u3000\u5c0f\u65b9\u751f\u547d\u4e2d\u7684\u6f5c\u529b\u88ab\u6fc0\u53d1\u2014\u2014\u4e5f\u8bb8\u8fd9\u5df2\u662f\u6700\u540e\u4e00\u6b21\uff0c\u5df2\u662f\u4ed6\u6700\u540e\u2026\uff0c\u5206\u6f5c\u529b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\xb7\u8dc3\u800c\u8d77\uff0c\u6293\u4f4f\u4e86\u4ed6\u7684\u5251\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u65f6\u5019\uff1f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u5c31\u5728\u6b64\u5730\uff0c\u5c31\u662f\u6b64\u523b\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u4eba\u5728\u6b64\uff0c\u5251\u4e5f\u5728\u6b64\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u884c\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u4eba\u5251\u867d\u5728\uff0c\u7cbe\u6c14\u5374\u5df2\u4e0d\u5728\u3002\u201d\u8fd9\u4eba\u7684\u58f0\u97f3\u8fd8\u662f\u5168\u65e0\u60c5\u611f\uff0c\u201c\u6211\u82e5\u5728\u6b64\u65f6\u6b64\u5730\u6740\u4e86\u4f60\uff0c\u6211\u5c31\u5bf9\u4e0d\u8d77\u6211\u7684\u5251\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u4f60\u6839\u672c\u4e0d\u914d\u8ba9\u6211\u51fa\u624b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u770b\u7740\u4ed6\uff0c\u5fc3\u91cc\u5ffd\u7136\u5bf9\u4ed6\u6709\u4e86\u79cd\u4ece\u5fc3\u5e95\u751f\u51fa\u7684\u5c0a\u656c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5c0a\u656c\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u79cd\u5c0a\u656c\u5df2\u7ecf\u8d85\u8d8a\u4e86\u751f\u6b7b\uff0c\u8d85\u8d8a\u4e86\u4e00\u5207\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u8bf4\u51fa\u4ef6\u522b\u4eba\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u5f88\u8352\u8c2c\u7684\u8981\u6c42\uff0c\u4ed6\u8bf4\uff1a\u201c\u4f60\u7ed9\u6211\uff0c\u201c\u4e00\u888b\u6c34\u3001\u4e00\u888b\u9152\u3001\u4e00\u888b\u8089\u3001\u4e00\u888b\u997c\u3001\u4e00\u5957\u5e03\u8863\u3001\u4e00\u5f20\u6bdb\u6be1\uff0c\u4e09\u5929\u540e\u6211\u518d\u6765\u3002\u201d \u3000\u3000\u8fd9\u4eba\u5c45\u7136\u7acb\u523b\u7b54\u5e94\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u6ca1\u6709\u53cd\u5e94\uff0c\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u6709\u542c\u89c1\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u6c34\u94f6\u5374\u597d\u50cf\u8981\u8df3\u4e86\u8d77\u6765\u8bf4\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u9759\u9759\u5730\u770b\u7740\u5979\uff0c\u5168\u8eab\u4e0a\u4e0b\u90fd\u6ca1\u6709\u4efb\u4f55\u52a8\u4f5c\u548c\u8868\u60c5\uff0c\u53ea\u662f\u5f88\u5e73\u9759\u5730\u95ee\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\u4f60\u6ca1\u6709\u542c\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u6211\u542c\u6e05\u695a\u4e86\u3002\u201d\u6c34\u94f6\u4e0d\u4f46\u7acb\u523b\u5b89\u9759\u4e0b\u6765\uff0c\u800c\u4e14\u5782\u4e0b\u4e86\u5934\u9053\uff1a\u201c\u6211\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4f60\u6ca1\u6709\u610f\u89c1\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u3002\u201d \u3000\u3000\u6c34\u3001\u9152\u3001\u8089\u3001\u997c\u3001\u8863\u670d\u3001\u6bdb\u6be1\uff0c\u5bf9\u4e00\u4e2a\u88ab\u56f0\u5728\u6c99\u6f20\u4e0a\u7684\u4eba\u6765\u8bf4\uff0c\u5df2\u4e0d\u4ec5\u662f\u4e00\u7b14\u8d22\u5bcc\uff0c\u5b83\u7684\u610f\u4e49\u5df2\u7edd\u975e\u4efb\u4f55\u8a00\u8bed\u6587\u5b57\u6240\u80fd\u5f62\u5bb9\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u5e26\u7740\u8fd9\u4e9b\u4e1c\u897f\u79bb\u5f00\u4ed6\u4eec\u7684\u5e10\u7bf7\u5f88\u4e45\uff0c\u60c5\u7eea\u4ecd\u672a\u5e73\u9759\uff0c\u592a\u957f\u4e45\u7684\u9965\u6e34\u5df2\u7ecf\u4f7f\u4ed6\u53d8\u5f97\u8fdc\u6bd4\u4ee5\u524d\u8f6f\u5f31\uff0c\u8f6f\u5f31\u7684\u4eba\u60c5\u7eea\u603b\u662f\u5bb9\u6613\u88ab\u6fc0\u52a8\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5411\u6c34\u94f6\u8981\u56de\u4ed6\u7684\u201c\u8d64\u72ac\u201d\uff0c\u56e0\u4e3a\u4ed6\u5e76\u4e0d\u60f3\u8d70\u5f97\u592a\u8fdc\uff0c\u514d\u5f97\u8ff7\u5931\u65b9\u5411\uff0c\u627e\u4e0d\u5230\u8fd9\u5e10\u7bf7\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u60f3\u8ba9\u522b\u4eba\u8ba4\u4e3a\u4ed6\u8981\u8d70\u8fdc\uff0c\u56e0\u4e3a\u4ed6\u51b3\u5fc3\u8981\u56de\u6765\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7edd\u4e0d\u80fd\u7559\u5728\u90a3\u91cc\u7b49\u5230\u4f53\u529b\u590d\u539f\u3002\u53ea\u8981\u4ed6\u770b\u89c1\u90a3\u4e2a\u4eba\uff0c\u4ed6\u5c31\u4f1a\u53d7\u5230\u4e00\u79cd\u65e0\u6cd5\u6297\u62d2\u7684\u5a01\u80c1\uff0c\u6c38\u8fdc\u90fd\u65e0\u6cd5\u653e\u677e\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5728\u8fd9\u4e09\u5929\u5185\u4f7f\u81ea\u5df1\u7684\u7cbe\u6c14\u4f53\u529b\u5168\u90fd\u6062\u590d\u5230\u5dc5\u5cf0\u72b6\u6001\uff0c\u624d\u6709\u5e0c\u671b\u8ddf\u90a3\u4e2a\u4eba\u51b3\u4e00\u80dc\u8d1f\uff0c\u5982\u679c\u4ed6\u65e0\u6cd5\u653e\u677e\u81ea\u5df1\u5c31\u5fc5\u8d25\u3002 \u3000\u3000\u5728\u4e00\u4e2a\u65e0\u60c5\u5251\u5ba2\u7684\u65e0\u60c5\u5251\u4e0b\uff0c\u8d25\u5c31\u662f\u6b7b\u3002 \u3000\u3000\u51b7\u98ce\uff0c\u9ec4\u6c99\uff0c\u5bd2\u591c\u3002 \u3000\u3000\u4ed6\u603b\u7b97\u5728\u4e00\u7247\u98ce\u5316\u4e86\u7684\u5ca9\u77f3\u95f4\u627e\u5230\u4e2a\u907f\u98ce\u5904\uff0c\u559d\u4e86\u51e0\u53e3\u6c34\u3001\u51e0\u53e3\u9152\uff0c\u5403\u4e86\u4e00\u5757\u9ea6\u997c\u3001\u4e00\u7247\u8089\u812f\uff0c\u7528\u6bdb\u6be1\u5377\u4f4f\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u7761\u7740\u4e86\u3002 \u3000\u3000\u7b49\u4ed6\u9192\u6765\u65f6\uff0c\u7b2c\u4e00\u773c\u770b\u89c1\u7684\uff0c\u5c31\u662f\u535c\u9e70\u3002 \u3000\u3000\u5bd2\u591c\u53c8\u5df2\u8fc7\u53bb\uff0c\u535c\u9e70\u7684\u767d\u8863\u5728\u6653\u8272\u4e2d\u770b\u6765\u5c31\u50cf\u662f\u5e7d\u7075\u7684\u957f\u888d\uff0c\u5df2\u7ecf\u8fc7\u9b54\u5492\u7684\u6cd5\u70bc\uff0c\u6c38\u8fdc\u90fd\u80fd\u4fdd\u6301\u96ea\u767d\u3001\u5e72\u51c0\u3001\u7b14\u633a\u3002 \u3000\u3000\u5c0f\u65b9\u5e76\u4e0d\u60ca\uff0c\u53ea\u5bf9\u4ed6\u7b11\u7b11\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u53c8\u6765\u4e86\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2a\u4eba\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u51fa\u73b0\uff0c\u4ed6\u90e8\u4e0d\u4f1a\u89c9\u5f97\u610f\u5916\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u95ee\u4e86\u53e5\u5f88\u5947\u602a\u7684\u8bdd\u3002 \u3000\u3000\u201c\u6211\u770b\u8d77\u6765\u8ddf\u4f60\u7b2c\u4e00\u6b21\u770b\u89c1\u6211\u65f6\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d\u4ed6\u95ee\u3002 \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u5374\u53d8\u5f97\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u535c\u9e70\u7684\u58f0\u97f3\u4e2d\u5e26\u7740\u8ba5\u6084\uff1a\u201c\u4f60\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u66b4\u53d1\u6237\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\uff0c\u4ed6\u8eab\u65c1\u7684\u7f8a\u76ae\u888b\uff0c\u535c\u9e70\u7684\u9510\u773c\u5f53\u7136\u4e0d\u4f1a\u9519\u8fc7\u3002 \u3000\u3000\u5728\u8fd9\u5757\u65e0\u60c5\u7684\u5927\u5730\u4e0a\uff0c\u5982\u679c\u6709\u4eba\u80af\u7ed9\u4f60\u8fd9\u4e9b\u4e1c\u897f\uff0c\u5f53\u7136\u4f1a\u8981\u4f60\u5148\u4ed8\u51fa\u4ee3\u4ef7\uff0c\u73b0\u5728\u4ed6\u552f\u4e00\u80fd\u4ed8\u51fa\u7684\u5c31\u662f\u4ed6\u7684\u826f\u77e5\u548c\u826f\u5fc3\u3002 \u3000\u3000\u535c\u9e70\u662f\u4e0d\u662f\u5df2\u7ecf\u5728\u6000\u7591\u4ed6\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u89e3\u91ca\u3002 \u3000\u3000\u5728\u535c\u9e70\u8fd9\u79cd\u4eba\u9762\u524d\uff0c\u4efb\u4f55\u4e8b\u90fd\u4e0d\u5fc5\u89e3\u91ca\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u5bf9\u4ed6\u7b11\u4e86\u7b11\uff0f\u53ef\u662f\u4f60\u8fd9\u4e2a\u66b4\u53d1\u6237\u597d\u50cf\u5e76\u6ca1\u505a\u8fc7\u4ec0\u4e48\u89c1\u4e0d\u5f97\u4eba\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u6709\u65f6\u4e0d\u89e3\u91ca\u5c31\u662f\u79cd\u6700\u597d\u7684\u89e3\u91ca\u3002 \u3000\u3000\u201c\u6211\u53ea\u4e0d\u8fc7\u9047\u89c1\u4e86\u4e00\u4e2a\u4eba\u800c\u5df2\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u4ed6\u6682\u65f6\u8fd8\u4e0d\u60f3\u8ba9\u6211\u88ab\u6e34\u6b7b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u51c6\u5907\u5728\u4e09\u5929\u540e\u518d\u4eb2\u624b\u6740\u6b7b\u6211\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u51c6\u5907\u7528\u4ec0\u4e48\u6740\u4f60\uff1f\u201d \u3000\u3000\u201c\u7528\u4ed6\u7684\u5251\u3002\u201d \u3000\u3000\u535c\u9e70\u7684\u76ee\u5149\u626b\u8fc7\u5c0f\u65b9\u7684\u5251\u3002\u201c\u4f60\u4e5f\u6709\u5251\uff0c\u88ab\u6740\u7684\u5f88\u53ef\u80fd\u4e0d\u662f\u4f60\uff0c\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u6709\u53ef\u80fd\uff0c\u5374\u4e0d\u592a\u53ef\u80fd\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u628a\u597d\u5251\uff0c\u4f60\u7684\u5251\u6cd5\u5f88\u4e0d\u5dee\uff0c\u51fa\u624b\u4e5f\u4e0d\u6162\uff0c\u80fd\u80dc\u8fc7\u4f60\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5251\u6cd5\u5982\u4f55\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u8fc7\u6211\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u89c1\u8fc7\uff0c\u6211\u542c\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u542c\u8fc7\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u61c2\uff0c\u5251\u6cd5\u7684\u5f3a\u5f31\u600e\u80fd\u542c\u5f97\u51fa\u3002 \u3000\u3000\u201c\u6628\u5929\u665a\u4e0a\uff0c\u6211\u542c\u89c1\u4f60\u90a3\u4e00\u5251\u51fa\u624b\u65f6\u7684\u98ce\u58f0\uff0c\u5c31\u77e5\u9053\u6765\u523a\u6740\u4f60\u7684\u90a3\u4e2a\u4eba\u5fc5\u5c06\u4f24\u5728\u4f60\u7684\u5251\u4e0b\u3002\u201d\u535c\u9e70\u6de1\u6de1\u4ed6\u8bf4\uff0c\u201c\u80fd\u907f\u5f00\u4f60\u90a3\u4e00\u5251\u7684\u4eba\u4e5f\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u6682\u65f6\u8fd8\u4e0d\u4f1a\u6b7b\uff0c\u6211\u53ea\u6709\u8d70\u3002\u201d\u535c\u9e70\u7684\u58f0\u97f3\u51b7\u5982\u5200\u524a\uff0c\u201c\u81ea\u5df1\u7b49\u6b7b\u548c\u7b49\u522b\u4eba\u6b7b\u90fd\u540c\u6837\u4e0d\u662f\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u7684\u5fc3\u662f\u4e0d\u662f\u4e5f\u548c\u4ed6\u58f0\u97f3\u540c\u6837\u51b7\u9177\uff1f\u4ed6\u8d70\u4e86\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u77e5\u9053\u5c0f\u65b9\u5df2\u8131\u79bb\u9669\u5883\uff1f \u3000\u3000\u5c0f\u65b9\u5148\u559d\u4e86\u53e3\u9152\uff0c\u542b\u5728\u5634\u91cc\uff0c\u518d\u559d\u4e00\u53e3\u6c34\u628a\u9152\u9001\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5f88\u60f3\u8ba9\u535c\u9e70\u4e5f\u8fd9\u4e48\u6837\u559d\u4e00\u53e3\uff0c\u8fd9\u4e48\u6837\u559d\u6cd5\u4e0d\u4f46\u98ce\u5473\u6781\u4f73\uff0c\u800c\u4e14\u5bf9\u7cbe\u795e\u8eab\u4f53\u90fd\u5f88\u6709\u76ca\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8ba9\u535c\u9e70\u559d\uff0c\u5c31\u6b63\u5982\u4ed6\u4e0d\u4f1a\u5411\u4e00\u4e2a\u6e05\u5ec9\u7684\u5b98\u540f\u65bd\u8d3f\u8d42\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u6177\u6168\u65bd\u4e88\uff0c\u5bf9\u53e6\u4e00\u4e2a\u4eba\u6765\u8bf4\uff0c\u6709\u65f6\u53cd\u800c\u662f\u4fae\u8fb1\u3002 \u3000\u3000\u535c\u9e70\u65e0\u7591\u4e5f\u770b\u51fa\u4e86\u8fd9\u4e00\u70b9\uff0c\u5140\u9e70\u822c\u7684\u51b7\u773c\u4e2d\u5c45\u7136\u9732\u51fa\u6e29\u6696\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u6ca1\u6709\u89c1\u8fc7\u90a3\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6447\u5934\u3002 \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u4ed6\u6c89\u601d\u7740\u9053\uff1a\u201c\u5f53\u4eca\u5929\u4e0b\u7684\u5251\u6cd5\u540d\u5bb6\uff0c\u6211\u5dee\u4e0d\u591a\u5168\u90fd\u77e5\u9053\uff0c\u5374\u59cb\u7ec8\u60f3\u4e0d\u51fa\u6709\u4ed6\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u60f3\u4e0d\u51fa\u3002\u201d\u535c\u9e70\u773c\u4e2d\u9732\u51fa\u6df1\u601d\u7684\u8868\u60c5\uff0c\u4e00\u79cd\u5df2\u63a5\u8fd1\u201c\u7985\u201d\u7684\u6df1\u601d\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u6162\u6162\u5730\u63a5\u7740\u8bf4\uff1a\u201c\u56e0\u4e3a\u771f\u6b63\u7684\u5251\u5ba2\u90fd\u662f\u65e0\u540d\u7684\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u540c\u6837\u5df2\u63a5\u8fd1\u201c\u7985\u201d\u7684\u610f\u5883\uff0c\u5c0f\u65b9\u8fd8\u5e74\u9752\uff0c\u8fd8\u4e0d\u80fd\u5b8c\u5168\u9886\u609f\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u535c\u9e70\u4e5f\u8981\u601d\u7d22\u5f88\u4e45\u624d\u80fd\u89e3\u91ca\uff1a\u201c\u56e0\u4e3a\u771f\u6b63\u7684\u5251\u5ba2\uff0c\u6240\u6c42\u7684\u53ea\u662f\u5251\u6cd5\u4e2d\u7684\u7cbe\u4e49\uff0c\u6240\u60f3\u5230\u8fbe\u7684\u53ea\u662f\u5251\u5883\u4e2d\u81f3\u9ad8\u81f3\u6df1\u3001\u4ece\u6765\u6ca1\u6709\u4eba\u80fd\u8fbe\u5230\u7684\u5883\u754c\u3002\u4ed6\u7684\u5fc3\u5df2\u75f4\u4e8e\u5251\uff0c\u4ed6\u7684\u4eba\u5df2\u4e0e\u4ed6\u7684\u5251\u8054\u4e3a\u4e00\u4f53\uff0c\u4ed6\u6240\u627e\u7684\u5bf9\u624b\uff0c\u4e00\u5b9a\u662f\u80fd\u5e2e\u52a9\u4ed6\u8fbe\u5230\u8fd9\u79cd\u5883\u754c\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u81ea\u89c9\u4ed6\u7684\u89e3\u91ca\u8fd8\u4e0d\u80fd\u4ee4\u4eba\u6ee1\u610f\uff0c\u6240\u4ee5\u53c8\u8865\u5145\uff1a\u201c\u8fd9\u79cd\u4eba\u4e0d\u4ec5\u4e0d\u4f1a\u5230\u6c5f\u6e56\u4e2d\u53bb\u6c42\u540d\uff0c\u751a\u81f3\u4f1a\u5c06\u81ea\u5df1\u7684\u540d\u5b57\u90fd\u6d51\u7136\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u5c0f\u65b9\u66ff\u4ed6\u8865\u5145\uff1a\u201c\u6700\u4e3b\u8981\u7684\u662f\uff0c\u4ed6\u4eec\u6839\u672c\u4e0d\u5e0c\u671b\u522b\u4eba\u77e5\u9053\u4ed6\u4eec\u7684\u540d\u5b57\uff0c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u5982\u679c\u5927\u6709\u540d\uff0c\u5c31\u4e0d\u80fd\u4e13\u5fc3\u505a\u4ed6\u81ea\u5df1\u559c\u6b22\u505a\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff1a\u201c\u4f60\u5b9e\u5728\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u7edd\u9876\u806a\u660e\uff0c\u53ea\u53ef\u60dc \u3000\u3000\u5c0f\u65b9\u66ff\u4ed6\u8bf4\u4e86\u4e0b\u53bb\uff1a\u201c\u53ea\u53ef\u60dc\u806a\u660e\u4eba\u901a\u5e38\u90fd\u77ed\u547d\u3002\u201d \u3000\u3000\u535c\u9e70\u7684\u58f0\u97f3\u53c8\u53d8\u5f97\u51b7\u5982\u5200\u524a\uff1a\u201c\u6240\u4ee5\u4e09\u5929\u540e\u6211\u4e00\u5b9a\u4f1a\u53bb\u66ff\u4f60\u6536\u5c38\u3002\u201d \u3000\u3000\u8fd9\u4e00\u5929\u5df2\u7ecf\u662f\u4e5d\u6708\u5341\u516b\u3002 \u3000\u3000\u4e5d\u6708\u4e8c\u5341\u65e5\uff0c\u6674\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u767d\u663c\u4f9d\u7136\u9177\u70ed\uff0c\u591c\u665a\u4f9d\u7136\u5bd2\u51b7\uff0c\u5c0f\u65b9\u7684\u4f53\u529b\u867d\u7136\u5df2\u6e10\u6062\u590d\uff0c\u60c5\u7eea\u5374\u53cd\u800c\u53d8\u5f97\u66f4\u7d27\u5f20\u3001\u66f4\u6025\u8e81\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u5bf9\u8fd9\u6b21\u751f\u6b7b\u51b3\u6218\u7684\u5fe7\u90c1\u548c\u6050\u60e7\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u592a\u5bc2\u5bde\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5f88\u60f3\u627e\u4e2a\u4eba\u804a\u804a\uff0c\u535c\u9e70\u5374\u5df2\u8d70\u4e86\uff0c\u5343\u91cc\u4e4b\u5185\u4e0d\u89c1\u4eba\u8ff9\u3002 \u3000\u3000\u7d27\u5f20\u3001\u9177\u70ed\u3001\u4f9b\u5e94\u65e0\u7f3a\u7684\u8089\u4e0e\u9152\uff0c\u4f7f\u5f97\u4ed6\u7684\u60c5\u6b32\u5ffd\u7136\u53d8\u5f97\u6781\u4ea2\u594b\u3002 \u3000\u3000\u4ed6\u5df2\u6709\u5f88\u4e45\u5f88\u4e45\u672a\u66fe\u63a5\u8fd1\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u65f6\u5e38\u5fcd\u4e0d\u4f4f\u4f1a\u60f3\u5230\u90a3\u53ea\u624b\uff0c\u90a3\u53ea\u7ea4\u79c0\u67d4\u7f8e\u3001\u5c06\u4ed6\u5168\u8eab\u6bcf\u4e00\u5bf8\u5730\u65b9\u90fd\u629a\u6478\u64e6\u6d17\u8fc7\u7684\u624b\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u81ea\u5df1\u4eff\u4f5b\u5df2\u5c06\u7206\u88c2\u3002 \u3000\u3000\u6240\u4ee5\u4e5d\u6708\u5341\u4e5d\u7684\u6df1\u591c\uff0c\u4ed6\u5c31\u4ee5\u661f\u8fb0\u8fa8\u522b\u65b9\u5411\uff0c\u5f00\u59cb\u5f80\u90a3\u5e10\u7bf7\u6240\u5728\u5730\u8d70\u56de\u53bb\u4e00 \u3000\u3000\u73b0\u5728\u5df2\u662f\u4e5d\u6708\u4e8c\u5341\u7684\u51cc\u6668\uff0c\u4ed6\u5df2\u770b\u5230\u4e86\u90a3\u5e10\u7bf7\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u81ea\u5df1\u73b0\u5728\u7684\u60c5\u51b5\u7edd\u5bf9\u4e0d\u9002\u4e8e\u8ddf\u90a3\u6837\u7684\u5bf9\u624b\u4ea4\u950b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u7edd\u4e0d\u80af\u9003\u907f\uff0c\u4e5f\u4e0d\u4f1a\u9000\u7f29\u3002 \u3000\u3000\u6709\u5f88\u591a\u4eba\u90fd\u76f8\u4fe1\u547d\u8fd0\uff0c\u90fd\u8ba4\u4e3a\u547d\u8fd0\u53ef\u4ee5\u51b3\u5b9a\u4e00\u4e2a\u4eba\u7684\u4e00\u751f\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u9053\u51b3\u5b9a\u4e00\u4e2a\u4eba\u4e00\u751f\u547d\u8fd0\u7684\uff0c\u5f80\u5f80\u5c31\u662f\u4ed6\u81ea\u5df1\u7684\u6027\u683c\u3002 \u3000\u3000\u5c0f\u65b9\u5c31\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u8d70\u4e0a\u8fd9\u6761\u8def\u3002 \u3000\u3000\u4ed6\u5927\u6b65\u8d70\u5411\u90a3\u5e10\u7bf7\u3002 \u3000\u3000\u5de8\u5927\u800c\u575a\u56fa\u7684\u725b\u76ae\u5e10\u7bf7\uff0c\u652f\u7acb\u5728\u4e00\u9053\u98ce\u77f3\u65ad\u5d16\u4e0b\u3002 \u3000\u3000\u5c0f\u65b9\u4e09\u5929\u524d\u79bb\u5f00\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u5e10\u7bf7\u5916\u4e0d\u4f46\u6709\u4eba\uff0c\u8fd8\u6709\u9a7c\u9a6c\uff0c\u73b0\u5728\u5374\u5df1\u5168\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u90a3\u4e9b\u4e3a\u4eba\u4eec\u80cc\u8d1f\u98df\u7269\u548c\u6c34\uff0c\u7ef4\u6301\u4eba\u7684\u751f\u547d\uff0c\u5374\u7ec8\u65e5\u8981\u5fcd\u53d7\u4eba\u4eec\u65e0\u60c5\u97ad\u7b56\u7684\u9a7c\u9a6c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u8fd9\u5e10\u7bf7\u91cc\u662f\u4e0d\u662f\u5df2\u7ecf\u53ea\u5269\u4e0b\u90a3\u65e0\u60c5\u53c8\u65e0\u540d\u7684\u5251\u5ba2\u4e00\u4e2a\u4eba\u5728\u7b49\u7740\u4ed6\uff1f \u3000\u3000\u7b49\u7740\u8981\u4ed6\u7684\u547d\uff01 \u3000\u3000\u70c8\u65e5\u5df2\u5347\u8d77\u3002 \u3000\u3000\u5c0f\u65b9\u4efb\u51ed\u6c57\u73e0\u6d41\u4e0b\uff0c\u6d41\u5230\u5634\u89d2\u3002\u53c8\u54b8\u53c8\u82e6\u7684\u6c57\u73e0\uff0c\u7528\u820c\u5934\u8214\u8d77\u6765\uff0c\u5c31\u50cf\u662f\u8840\u3002 \u3000\u3000\u4ed6\u5f88\u5feb\u5c31\u4f1a\u5c1d\u5230\u771f\u6b63\u8840\u7684\u6ecb\u5473\u4e86\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u7684\u8840\u3002 \u3000\u3000\u4ed6\u629b\u4e0b\u4e86\u4ed6\u7684\u6bdb\u6be1\u3001\u76ae\u888b\u3001\u90a3\u4e9b\u5f88\u53ef\u80fd\u4f1a\u5f71\u54cd\u4ed6\u52a8\u4f5c\u901f\u5ea6\u7684\u4e1c\u897f\uff0c\u7d27\u63e1\u4f4f\u4ed6\u7684\u5251\uff0c\u8d70\u5165\u4e86\u5e10\u7bf7\uff0c\u51c6\u5907\u9762\u5bf9\u4ed6\u8fd9\u4e00\u751f\u4e2d\u6700\u53ef\u6015\u7684\u5bf9\u624b\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u8fd9\u5e10\u7bf7\u91cc\u7adf\u8fde\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5251\u5ba2\u65e0\u540d\uff0c\u62d4\u5251\u65e0\u60c5\uff0c\u4e00\u51fa\u624b\u5c31\u8981\u7f6e\u4eba\u4e8e\u6b7b\u5730\uff0c\u8fd9\u4e00\u5251\u4e0d\u4f46\u662f\u4ed6\u5251\u6cd5\u4e2d\u7684\u7cbe\u534e\uff0c\u4e5f\u662f\u4ed6\u7684\u79d8\u5bc6\uff0c\u4ed6\u51fa\u624b\u65f6\u5f53\u7136\u4e0d\u613f\u6709\u522b\u4eba\u5728\u65c1\u8fb9\u770b\u7740\u3002 \u3000\u3000\u80fd\u770b\u5230\u4ed6\u8fd9\u4e00\u5251\u7684\u4eba\u5c31\u5fc5\u5c06\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\u3002 \u3000\u3000\u6240\u4ee5\u5c0f\u65b9\u66fe\u7ecf\u60f3\u5230\u536b\u5929\u9e4f\u548c\u6c34\u94f6\u90fd\u5df2\u88ab\u8feb\u79bb\u5f00\u8fd9\u91cc\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4ece\u672a\u60f3\u5230\u90a3\u65e0\u540d\u7684\u5251\u5ba2\u4e5f\u4f1a\u8d70\uff0c\u66f4\u60f3\u4e0d\u901a\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8d70\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u540c\u4e00\u7c7b\u7684\u4eba\uff0c\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u90fd\u7edd\u4e0d\u4f1a\u4e34\u9635\u8131\u9003\u7684\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u4e0d\u662f\u53d1\u751f\u8fc7\u4ec0\u4e48\u60ca\u4eba\u7684\u53d8\u5316\uff1f\u53d1\u751f\u8fc7\u4ec0\u4e48\u8ba9\u4ed6\u975e\u8d70\u4e0d\u53ef\u7684\u4e8b\uff1f \u3000\u3000\u5c0f\u65b9\u770b\u4e0d\u51fa\u3002 \u3000\u3000\u5e10\u7bf7\u91cc\u6240\u6709\u7684\u4e00\u5207\uff0c\u90fd\u8ddf\u4ed6\u4e09\u5929\u524d\u79bb\u5f00\u65f6\u5b8c\u5168\u4e00\u6837\uff0c\u91d1\u76c6\u4ecd\u5728\u6728\u51e0\u4e0a\uff0c\u90a3\u5757\u8c79\u76ae\u4e5f\u4ecd\u5728\u2026\u2026 \u3000\u3000\u5c0f\u65b9\u5168\u8eab\u7684\u808c\u8089\u5ffd\u7136\u62bd\u7d27\uff0c\u5ffd\u7136\u4e00\u4e2a\u7bad\u6b65\u7a9c\u5230\u8f6f\u69bb\u524d\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u8c79\u76ae\u5728\u52a8\u3002 \u3000\u3000\u4ed6\u4e00\u53ea\u624b\u63e1\u5251\uff0c\u53e6\u4e00\u53ea\u624b\u6162\u6162\u5730\u4f38\u51fa\uff0c\u5f88\u6162\u5f88\u6162\uff0c\u7136\u540e\u5ffd\u7136\u7528\u6700\u5feb\u7684\u901f\u5ea6\u5c06\u8c79\u76ae\u6380\u8d77\u3002 \u3000\u3000\u8c79\u76ae\u4e0b\u679c\u7136\u6709\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u662f\u6c34\u94f6\uff0c\u4e0d\u662f\u536b\u5929\u9e4f\uff0c\u66f4\u4e0d\u662f\u90a3\u65e0\u540d\u7684\u5251\u5ba2\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u5b8c\u5168\u8d64\u88f8\u7684\u5973\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u4e00\u773c\u5c31\u53ef\u4ee5\u786e\u5b9a\u4ed6\u4ee5\u524d\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e2a\u5973\u4eba\uff0c\u8fd9\u4e2a\u5973\u4eba\u548c\u4ed6\u4ee5\u524d\u6240\u89c1\u8fc7\u7684\u4efb\u4f55\u5973\u4eba\u90fd\u4e0d\u540c\u3002 \u3000\u3000\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f \u3000\u3000\u5c0f\u65b9\u867d\u7136\u8bf4\u4e0d\u51fa\uff0c\u5374\u5df2\u611f\u89c9\u5230\uff0c\u4e00\u79cd\u6781\u6df1\u5165\u3001\u6781\u5f3a\u7684\u611f\u89c9\uff0c\u51e0\u4e4e\u5df2\u6df1\u5165\u5230\u4ed6\u7684\u5c0f\u8179\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u6d6a\u5b50\u3002 \u3000\u3000\u4ed6\u89c1\u8fc7\u65e0\u6570\u5973\u4eba\uff0c\u4e5f\u89c1\u8fc7\u65e0\u6570\u5973\u4eba\u5728\u4ed6\u9762\u524d\u5c06\u81ea\u5df1\u8d64\u88f8\u3002 \u3000\u3000\u5979\u4eec\u7684\u80ed\u4f53\u90fd\u8fdc\u6bd4\u8fd9\u4e2a\u5973\u4eba\u66f4\u7ed3\u5b9e\u3001\u66f4\u8bf1\u60d1\u3002 \u3000\u3000\u5979\u770b\u6765\u4e0d\u4f46\u82cd\u767d\u800c\u7626\u5f31\uff0c\u800c\u4e14\u53d1\u80b2\u5f97\u5e76\u4e0d\u597d\uff0c\u4f46\u662f\u5979\u7ed9\u4eba\u7684\u611f\u89c9\uff0c\u5374\u53ef\u4ee5\u6df1\u5165\u5230\u4eba\u7c7b\u6700\u539f\u59cb\u7684\u60c5\u6b32\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u662f\u5b8c\u5168\u65e0\u52a9\u7684\uff0c\u5b8c\u5168\u6ca1\u6709\u62b5\u6297\u529b\uff0c\u751a\u81f3\u8fde\u62b5\u6297\u7684\u610f\u8bc6\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u592a\u8f6f\u5f31\uff0c\u65e0\u8bba\u522b\u4eba\u8981\u600e\u4e48\u5bf9\u4ed8\u5979\uff0c\u5979\u90fd\u53ea\u6709\u627f\u53d7\u3002 \u3000\u3000\u2014\u2014\u4efb\u4f55\u4e00\u4e2a\u7537\u4eba\uff0c\u90fd\u53ef\u4ee5\u5bf9\u5979\u505a\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u5982\u679c\u7ed9\u4e86\u7537\u4eba\u8fd9\u79cd\u611f\u89c9\uff0c\u65e0\u8bba\u5bf9\u5979\u81ea\u5df1\uff0c\u6291\u6216\u5bf9\u522b\u4eba\u90fd\u662f\u4ef6\u5f88\u4e0d\u5e78\u7684\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u79cd\u611f\u89c9\u672c\u8eab\u5c31\u662f\u79cd\u5f15\u4eba\u72af\u7f6a\u7684\u8bf1\u60d1\u3002 \u3000\u3000\u5c0f\u65b9\u51b2\u4e86\u51fa\u53bb\uff0c\u51b2\u51fa\u4e86\u5e10\u7bf7\uff0c\u5e10\u7bf7\u5916\u70c8\u65e5\u5982\u706b\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u70c8\u65e5\u4e0b\uff0c\u5fc3\u91cc\u4e5f\u4eff\u4f5b\u6709\u706b\u7130\u5728\u71c3\u70e7\u3002 \u3000\u3000\u4ed6\u5df2\u5c06\u60c5\u611f\u514b\u5236\u5f97\u592a\u4e45\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u72af\u7f6a\u3002 \u3000\u3000\u6c57\u73e0\u53c8\u5f00\u59cb\u5f80\u4e0b\u6d41\uff0c\u514b\u5236\u60c5\u6b32\u6709\u65f6\u6bd4\u514b\u5236\u4efb\u4f55\u4e00\u79cd\u51b2\u52a8\u90fd\u56f0\u96be\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8d70\u8fdc\uff0c\u56e0\u4e3a\u6709\u4e9b\u4e8b\u4e00\u5b9a\u8981\u5f04\u6e05\u695a\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u5973\u4eba\u662f\u600e\u4e48\u6765\u7684\uff1f\u536b\u5929\u9e4f\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u4ed6\u518d\u6b21\u8d70\u4eba\u5e10\u7bf7\u65f6\uff0c\u5979\u5df2\u7ecf\u5750\u8d77\u6765\u4e86\uff0c\u7528\u8c79\u76ae\u88f9\u4f4f\u4e86\u81ea\u5df1\uff0c\u7528\u4e00\uff0c\u53cc\u5145\u6ee1\u60ca\u60e7\u7684\u773c\u775b\u770b\u7740\u4ed6\u3002 \u3000\u3000\u5c0f\u65b9\u5c3d\u91cf\u907f\u514d\u53bb\u770b\u5979\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u5fd8\u8bb0\u521a\u624d\u90a3\u79cd\u611f\u89c9\uff0c\u4e5f\u4e0d\u80fd\u5fd8\u8bb0\u5979\u5728\u8c79\u76ae\u4e0b\u8fd8\u662f\u8d64\u88f8\u7684\u3002 \u3000\u3000\u53ef\u662f\u6709\u4e9b\u8bdd\u4ed6\u4e00\u5b9a\u8981\u95ee\uff0c\u9996\u5148\u4ed6\u4e00\u5b9a\u8981\u5f04\u6e05\u695a\u5979\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u4ed6\u95ee\u4e00\u53e5\uff0c\u5979\u5c31\u56de\u7b54\u4e00\u53e5\u3002 \u3000\u3000\u5979\u4ece\u4e0d\u53cd\u6297\uff0c\u56e0\u4e3a\u5979\u65e2\u6ca1\u6709\u53cd\u6297\u7684\u529b\u91cf\uff0c\u4e5f\u6ca1\u6709\u53cd\u6297\u7684\u610f\u5fd7\u3002 \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u6ce2\u5a03\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u67d4\u602f\uff0c\u8bf4\u7684\u867d\u7136\u662f\u4e2d\u539f\u5e38\u7528\u7684\u8bed\u8a00\uff0c\u5374\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8154\u8c03\u3002 \u3000\u3000\u5979\u770b\u6765\u867d\u7136\u662f\u6c49\u4eba\uff0c\u5374\u65e0\u7591\u662f\u5728\u5927\u6f20\u4e2d\u751f\u957f\u7684\uff0c\u5979\u7684\u540d\u5b57\u4e5f\u662f\u85cf\u8bed\u3002 \u3000\u3000\u201c\u4f60\u662f\u536b\u5929\u9e4f\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u6765\u7b49\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u7b49\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\u59d3\u65b9\uff0c\u662f\u4e2a\u7537\u4eba\uff0c\u662f\u4e2a\u5f88\u597d\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5e76\u4e0d\u5927\u60ca\u5f02\uff0c\u6240\u4ee5\u7acb\u523b\u63a5\u7740\u95ee\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u53eb\u4f60\u6765\u7b49\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u6211\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u4e3b\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u662f\u4e2a\u7537\u4eba\u3002\u201d\u63d0\u5230\u5979\u7684\u4e3b\u4eba\uff0c\u5979\u773c\u775b\u91cc\u7acb\u523b\u9732\u51fa\u79cd\u51e0\u4e4e\u5df2\u63a5\u8fd1\u51e0\u4eba\u5bf9\u795e\u4e00\u6837\u7684\u5d07\u62dc\u548c\u5c0a\u656c\uff1a\u201c\u53ef\u662f\u4ed6\u6bd4\u4e16\u4e0a\u6240\u6709\u7684\u7537\u4eba\u90fd\u5a01\u6b66\u5f3a\u58ee\uff0c\u53ea\u8981\u4ed6\u60f3\u505a\u7684\u4e8b\uff0c\u6ca1\u6709\u505a\u4e0d\u5230\u7684\uff0c\u53ea\u8981\u4ed6\u613f\u610f\uff0c\u4ed6\u5c31\u4f1a\u98de\u4e0a\u9752\u5929\uff0c\u98de\u4e0a\u5723\u6bcd\u5cf0\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u9e70\u3002\u201d \u3000\u3000\u201c\u4e00\u53ea\u9e70\uff1f\u201d\u5c0f\u65b9\u7ec8\u4e8e\u660e\u767d\uff1a\u201c\u4ed6\u7684\u540d\u5b57\u662f\u4e0d\u662f\u53eb\u535c\u9e70\uff1f\u201d \u3000\u3000\u5979\u6765\u8fd9\u91cc\uff0c\u662f\u535c\u9e70\u53eb\u5979\u6765\u7684\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4ed6\u4eec\u4e0d\u5728\u8fd9\u91cc\uff0c\u5f53\u7136\u4e5f\u662f\u88ab\u535c\u9e70\u903c\u8d70\u7684\u3002 \u3000\u3000\u4ed6\u66ff\u5c0f\u65b9\u903c\u8d70\u4e86\u536b\u5929\u9e4f\u548c\u6c34\u94f6\uff0c\u66ff\u5c0f\u65b9\u51fb\u8d25\u4e86\u90a3\u53ef\u6015\u7684\u7684\u65e0\u540d\u5251\u5ba2\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u613f\u610f\uff0c\u4ec0\u4e48\u4e8b\u90fd\u80fd\u505a\u5f97\u5230\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u5f88\u6124\u6012\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5e94\u8be5\u611f\u6fc0\u624d\u5bf9\uff0c\u4f46\u662f\u4ed6\u7684\u6124\u6012\u5374\u8fdc\u6bd4\u611f\u6fc0\u66f4\u5f3a\u70c8\u3002 \u3000\u3000\u90a3\u4e2a\u6740\u4eba\u7684\u5251\u5ba2\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4ed6\u4eec\u95f4\u7684\u751f\u6b7b\u51b3\u6218\u8ddf\u522b\u4eba\u5168\u65e0\u5173\u7cfb\uff0c\u5c31\u7b97\u4ed6\u6218\u8d25\u3001\u6218\u6b7b\uff0c\u4e5f\u662f\u4ed6\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u51b2\u51fa\u53bb\uff0c\u53bb\u627e\u535c\u9e70\uff0c\u53bb\u544a\u8bc9\u8fd9\u4e2a\u81ea\u547d\u4e0d\u51e1\u7684\u4eba\uff0c\u6709\u4e9b\u4e8b\u66b4\u4e00\u5b9a\u8981\u81ea\u5df1\u505a\u7684\u2014\u2014\u81ea\u5df1\u7684\u6218\u6597\u8981\u81ea\u5df1\u53bb\u6253\uff0c\u81ea\u5df1\u7684\u5c0a\u4e25\u8981\u81ea\u5df1\u6765\u7ef4\u62a4\uff0c\u81ea\u5df1\u7684\u547d\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u8fd8\u6709\u6c57\u53ef\u6d41\uff0c\u8fd8\u6709\u8840\u53ef\u6d41\uff0c\u90a3\u4e2a\u81ea\u5927\u7684\u4eba\u51ed\u4ec0\u4e48\u8981\u6765\u7ba1\u4ed6\u7684\u95f2\u4e8b\uff01 \u3000\u3000\u5979\u4e00\u76f4\u5728\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u5df2\u4e0d\u518d\u6709\u754f\u60e7\uff0c\u5ffd\u7136\u8f7b\u8f7b\u4ed6\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u5c31\u662f\u6211\u5728\u7b49\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\u4f60\u662f\u4e2a\u597d\u4eba\u3002\u201d\u5979\u5782\u4e0b\u5934\uff1a\u201c\u56e0\u4e3a\u4f60\u6ca1\u6709\u6b3a\u8d1f\u6211\u3002\u201d \u3000\u3000\u4eba\u7c7b\u5e73\u7b49\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u201c\u4e0d\u53d7\u6b3a\u4fae\u201d\u7684\u6743\u5229\uff0c\u53ef\u662f\u5bf9\u5979\u6765\u8bf4\uff0c\u80fd\u591f\u4e0d\u53d7\u6b3a\u4fae\uff0c\u5df2\u7ecf\u662f\u5f88\u96be\u5f97\u7684\u5e78\u8fd0\u3002 \u3000\u3000\u5979\u66fe\u7ecf\u5fcd\u53d7\u8fc7\u591a\u5c11\u4eba\u7684\u6b3a\u538b\u51cc\u4fae\uff1f\u5728\u5979\u8bf4\u7684\u8fd9\u53e5\u8bdd\u4e2d\uff0c\u9690\u85cf\u7740\u591a\u5c11\u8f9b\u9178\u4e0d\u5e78\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u6124\u6012\u5ffd\u6d88\u5931\uff0c\u53d8\u4e3a\u601c\u60af\u540c\u60c5\u3002 \u3000\u3000\u5979\u53c8\u62ac\u8d77\u5934\uff0c\u76f4\u89c6\u7740\u4ed6\uff1a\u201c\u6211\u4e5f\u770b\u5f97\u51fa\u4f60\u9700\u8981\u4ec0\u4e48\uff0c\u4f60\u8981\u7684\uff0c\u6211\u90fd\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u8df3\u52a0\u5feb\u65f6\uff0c\u5979\u53c8\u7ad9\u8d77\u6765\uff0c\u8d64\u88f8\u88f8\u5730\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u60f3\u9003\u907f\u65f6\uff0c\u5979\u5df2\u5728\u4ed6\u7684\u6000\u91cc\u3002 \u3000\u3000\u5979\u7b11\u5f97\u771f\u662f\u6109\u5feb\u6781\u4e86\uff0c\u8fdc\u6bd4\u4e00\u4e2a\u9493\u9c7c\u7684\u4eba\u5c06\u4eb2\u624b\u9493\u6765\u7684\u9c7c\u653e\u4e0b\u6cb9\u9505\u66f4\u6109\u5feb\u3002 \u3000\u3000\u9c7c\u662f\u4ec0\u4e48\u611f\u89c9\uff1f \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e94\u7ae0\u3000\u7f51\u91cc\u7684\u9c7c \u7b2c\u4e94\u7ae0\u3000\u7f51\u91cc\u7684\u9c7c \u3000\u3000\u5c0f\u65b9\u7b2c\u4e00\u4e2a\u611f\u89c9\u662f\u201c\u4e0d\u76f8\u4fe1\u201d\uff0c\u4ed6\u7edd\u4e0d\u76f8\u4fe1\u6ce2\u5a03\u4f1a\u51fa\u5356\u4ed6\u3002 \u3000\u3000\u4e0d\u5e78\u8fd9\u662f\u4e8b\u5b9e\uff0c\u4e8b\u5b9e\u5f80\u5f80\u4f1a\u6bd4\u5669\u68a6\u66f4\u53ef\u6015\u3001\u66f4\u6b8b\u9177\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7ec8\u4e8e\u660e\u767d\u4e86\u3002 \u3000\u3000\u6ce2\u5a03\u5728\u90a3\u5e10\u7bf7\u91cc\u7b49\u4ed6\uff0c\u5e76\u4e0d\u662f\u535c\u9e70\u53eb\u5979\u53bb\u7684\u3002 \u3000\u3000\u5979\u7684\u4e3b\u4eba\u5e76\u4e0d\u662f\u535c\u9e70\uff0c\u662f\u6c34\u94f6\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u4e00\u5b9a\u5df2\u7ecf\u660e\u767d\u8fd9\u662f\u4e2a\u5708\u5957\uff0c\u8fd9\u4f4d\u96ea\u59d1\u5a18\u5bf9\u4f60\u8bf4\u7684\u6839\u672c\u6ca1\u6709\u4e00\u53e5\u662f\u771f\u8bdd\uff0c\u5979\u7684\u58f0\u97f3\u867d\u7136\u751c\u5982\u871c\uff0c\u7b11\u91cc\u5374\u85cf\u6709\u5200\uff0c\u6740\u4eba\u4e0d\u89c1\u8840\u7684\u5200\uff01\u201d \u3000\u3000\u6ce2\u5a03\u5c31\u5728\u5979\u8eab\u65c1\uff0c\u4e0d\u7ba1\u5979\u8bf4\u4ec0\u4e48\uff0c\u6ce2\u5a03\u90fd\u4e00\u76f4\u9759\u9759\u5730\u542c\u7740\u3002 \u3000\u3000\u5979\u5ffd\u7136\u4e00\u628a\u63ea\u4f4f\u6ce2\u5a03\u7684\u5934\u53d1\uff0c\u628a\u5979\u82cd\u767d\u7684\u8138\uff0c\u6309\u5728\u5c0f\u65b9\u9762\u524d\u3002 \u3000\u3000\u201c\u4f60\u7741\u5f00\u773c\u775b\u770b\u7740\u5979\uff0c\u6211\u6562\u6253\u8d4c\uff0c\u76f4\u5230\u73b0\u5728\u4f60\u4e00\u5b9a\u8fd8\u4e0d\u76f8\u4fe1\u5979\u4f1a\u662f\u4e2a\u8fd9\u6837\u7684\u5973\u4eba\uff01\u201d \u3000\u3000\u5c0f\u65b9\u7741\u5f00\u4e86\u773c\uff0c\u5979\u7684\u5934\u66ff\u4ed6\u6321\u4f4f\u4e86\u9633\u5149\uff0c\u5979\u7684\u957f\u53d1\u843d\u5728\u4ed6\u8138\u4e0a\uff0c\u5979\u7684\u773c\u775b\u91cc\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u4eff\u4f5b\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u60f3\u3002 \u3000\u3000\u5979\u8fd9\u4e2a\u4eba\u4eff\u4f5b\u53ea\u5269\u4e0b\u4e00\u526f\u8eaf\u58f3\uff0c\u65e2\u6ca1\u6709\u601d\u60f3\u60c5\u611f\uff0c\u4e5f\u6ca1\u6709\u7075\u9b42\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5c0f\u65b9\u5df2\u7ecf\u539f\u8c05\u4e86\u5979\uff0c\u4e0d\u7ba1\u5979\u66fe\u7ecf\u5bf9\u4ed6\u505a\u51fa\u8fc7\u591a\u5c11\u53ef\u6015\u7684\u4e8b\uff0c\u4ed6\u90fd\u53ef\u4ee5\u539f\u8c05\u5979\u3002 \u3000\u3000\u6c34\u94f6\u9053\uff1a\u201c\u7ea6\u4f60\u7684\u4eba\u5df2\u8d70\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5df2\u53d1\u73b0\u4f60\u6839\u672c\u4e0d\u914d\u8ba9\u4ed6\u51fa\u624b\u3002\u536b\u5929\u9e4f\u60f3\u8981\u4f60\u66ff\u4ed6\u627e\u56de\u9ec4\u91d1\uff0c\u6211\u5374\u53ea\u60f3\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u5979\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u6562\u6253\u8d4c\uff0c\u8fd9\u6b21\u7edd\u5bf9\u6ca1\u6709\u4eba\u6765\u6551\u4f60\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u8d4c\u4ec0\u4e48\uff0c\u8d4c\u4f60\u7684\u547d\uff1f\u201d \u3000\u3000\u6c34\u94f6\u4e5f\u5bf9\u4ed6\u770b\u7740\uff1a\u201c\u53ea\u8981\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5979\u7684\u7b11\u5bb9\u5ffd\u7136\u51bb\u7ed3\uff0c\u56e0\u4e3a\u5979\u5df2\u53d1\u73b0\u5730\u4e0a\u591a\u4e86\u6761\u5f71\u5b50\u3002 \u3000\u3000\u9633\u5149\u4ece\u5979\u80cc\u540e\u7167\u8fc7\u6765\uff0c\u8fd9\u6761\u5f71\u5b50\u5c31\u5728\u5979\u8eab\u540e\uff0c\u662f\u4e2a\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff1f\u5979\u5b8c\u5168\u6ca1\u6709\u53d1\u89c9\u3002 \u3000\u3000\u5f71\u5b50\u5c31\u8d34\u5728\u5979\u8eab\u540e\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u5979\u4e5f\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u5979\u7684\u624b\u8db3\u51b0\u51b7\uff0c\u989d\u4e0a\u5374\u5192\u51fa\u4e86\u4e00\u7c92\u7c92\u6bd4\u9ec4\u8c46\u8fd8\u5927\u7684\u6c57\u73e0\u3002 \u3000\u3000\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5979\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u3002 \u3000\u3000\u5f71\u4e8e\u6ca1\u6709\u56de\u7b54\uff0c\u5c0f\u65b9\u66ff\u4ed6\u8bf4\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u56de\u5934\u770b\u770b\uff1f\u201d \u3000\u3000\u5979\u4e0d\u6562\u56de\u5934\u3002 \u3000\u3000\u5979\u53ea\u8981\u4e00\u56de\u5934\uff0c\u5f88\u53ef\u80fd\u5c31\u4f1a\u6709\u628a\u5229\u5203\u5272\u65ad\u5979\u7684\u54bd\u5589\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u5439\u8d77\u4e86\u5f71\u4e8e\u7684\u957f\u888d\uff0c\u5979\u770b\u89c1\u4ece\u5979\u8eab\u540e\u5439\u8fc7\u6765\u7684\u4e00\u5757\u767d\u8272\u8863\u89d2\uff0c\u6bd4\u8fdc\u65b9\u9ad8\u5c71\u4e0a\u7684\u79ef\u96ea\u8fd8\u767d\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u5728\u95ee\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u8fd8\u8981\u8ddf\u6211\u8d4c\uff1f\u201d \u3000\u3000\u6c34\u94f6\u60f3\u5f00\u53e3\uff0c\u53ef\u662f\u5634\u5507\u53d1\u6296\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u8bf4\u4e0d\u51fa\uff0c\u5c31\u5728\u522b\u4eba\u90fd\u8ba4\u4e3a\u5979\u5df2\u5c06\u56e0\u6050\u60e7\u800c\u5d29\u6e83\u65f6\uff0c\u5979\u5df2\u4ece\u6ce2\u5a03\u8eab\u4e0a\u7ffb\u51fa\uff0c\u8e29\u4f4f\u6ce2\u5a03\u7684\u5934\u63a0\u51fa\u4e86\u4e09\u4e08\uff0c\u4e0d\u505c\u5730\u5411\u524d\u98de\u63a0\u3002 \u3000\u3000\u5979\u59cb\u7ec8\u4e0d\u6562\u56de\u5934\u53bb\u770b\u80cc\u540e\u8fd9\u5f71\u5b50\u4e00\u773c\uff0c\u56e0\u4e3a\u5979\u5df2\u731c\u51fa\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5728\u8fdc\u65b9\u79ef\u96ea\u7684\u5723\u5cf0\u4e0a\uff0c\u6709\u4e00\u53ea\u5b64\u9e70\uff0c\u5728\u8fd9\u7247\u65e0\u60c5\u7684\u571f\u5730\u4e0a\uff0c\u6709\u4e00\u4e2a\u5b64\u72ec\u7684\u4eba\uff0c\u636e\u8bf4\u8fd9\u4e2a\u4eba\u5c31\u662f\u9e70\u7684\u7cbe\u9b42\u5316\u8eab\uff0c\u662f\u6c38\u8fdc\u4e0d\u4f1a\u88ab\u6bc1\u706d\u7684\u3002 \u3000\u3000\u751f\u5b58\u5728\u5927\u6f20\u4e2d\u7684\u4eba\u51e0\u4e4e\u90fd\u542c\u8fc7\u8fd9\u4f20\u8bf4\uff0c\u5979\u4e5f\u542c\u8fc7\u3002 \u3000\u3000\u535c\u9e70\u6ca1\u6709\u8ffd\u5979\uff0c\u8fd8\u662f\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u7528\u4e00\u53cc\u9e70\u822c\u7684\u773c\u775b\u770b\u7740\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u4f60\u8f93\u4e86\u3002\u201d\u4ed6\u5ffd\u7136\u8bf4\uff1a\u201c\u5982\u679c\u5979\u771f\u7684\u8ddf\u4f60\u8d4c\uff0c\u4f60\u5c31\u8f93\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u8bf4\u7684\u4e0d\u9519\uff0c\u8fd9\u6b21\u7684\u786e\u6ca1\u6709\u4eba\u4f1a\u6765\u6551\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u662f\u6765\u6551\u4f60\u7684\uff0c\u6211\u53ea\u4e0d\u8fc7\u51d1\u5de7\u8d70\u5230\u8fd9\u91cc\uff0c\u51d1\u5de7\u7ad9\u5728\u5979\u8eab\u540e\u800c\u5df2\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6c38\u8fdc\u90fd\u4e0d\u8981\u522b\u4eba\u611f\u6fc0\u4f60\uff1f\u201d \u3000\u3000\u4ed6\u77e5\u9053\u535c\u9e70\u7edd\u4e0d\u4f1a\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u6240\u4ee5\u7acb\u523b\u53c8\u63a5\u9053\uff1a\u201c\u5982\u679c\u4f60\u51d1\u5de7\u9700\u8981\u4e94\u6839\u725b\u76ae\u5e26\uff0c\u6211\u5374\u51d1\u5de7\u6b63\u597d\u6709\u4e94\u6839\uff0c\u53ef\u4ee5\u9001\u7ed9\u4f60\uff0c\u6211\u4e5f\u4e0d\u8981\u4f60\u611f\u6fc0\u6211\uff01\u201d \u3000\u3000\u535c\u9e70\u773c\u775b\u91cc\u53c8\u6709\u4e86\u7b11\u610f\uff1a\u201c\u8fd9\u6837\u7684\u725b\u76ae\u5e26\uff0c\u6211\u51d1\u5de7\u6b63\u597d\u7528\u5f97\u7740\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5410\u51fa\u53e3\u6c14\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u5728\u5c0f\u65b9\u624b\u8db3\u7684\u56db\u80a2\u548c\u54bd\u5589\u4e0a\u7684\u725b\u76ae\u5e26\u90fd\u5df2\u89e3\u4e0b\uff0c\u535c\u9e70\u5c06\u4e94\u6839\u76ae\u5e26\u7ed3\u6210\u4e00\u6761\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u77e5\u9053\u6211\u51c6\u5907\u7528\u5b83\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6211\u51c6\u5907\u628a\u5b83\u9001\u7ed9\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u9001\u7ed9\u8c01\uff1f\u201d \u3000\u3000\u201c\u9001\u7ed9\u4e00\u4e2a\u968f\u65f6\u90fd\u53ef\u80fd\u4f1a\u4e0a\u540a\u7684\u4eba\uff0c\u7528\u8fd9\u79cd\u725b\u76ae\u5e26\u4e0a\u540a\u7edd\u5bf9\u6b64\u7ef3\u5e72\u597d\u3002\u201d\u535c\u9e70\u6de1\u6de1\u5730\uff0c\u201c\u6211\u4e0d\u6740\u4eba\uff0c\u53ef\u662f\u4e00\u4e2a\u4eba\u5982\u679c\u81ea\u5df1\u8981\u4e0a\u540a\uff0c\u6211\u4e5f\u4e0d\u53cd\u5bf9\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u518d\u95ee\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u5341\u5206\u6ce8\u610f\u542c\u535c\u9e70\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u4e00\u4e00\u76f4\u5728\u770b\u7740\u6ce2\u5a03\u3002 \u3000\u3000\u6ce2\u5a03\u5df2\u88ab\u90a3\u4e00\u201c\u811a\u8e29\u5728\u5730\u4e0a\uff0c\u6ee1\u5934\u67d4\u53d1\u5728\u98ce\u4e2d\u4e1d\u4e1d\u98d8\u62c2\uff0c\u8138\u5374\u57cb\u5728\u6c99\u5b50\u91cc\u3002 \u3000\u3000\u5979\u4e00\u76f4\u90fd\u8fd9\u4e48\u6837\u8eba\u7740\uff0c\u6ca1\u6709\u52a8\uff0c\u4e5f\u6ca1\u6709\u62ac\u5934\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u5979\u4e0d\u6562\u62ac\u5934\u9762\u5bf9\u5c0f\u65b9\uff1f \u3000\u3000\u5c0f\u65b9\u5f88\u60f3\u5c31\u8fd9\u6837\u8d70\u5f00\uff0c\u4e0d\u518d\u7406\u5979\uff0c\u53ef\u662f\u4ed6\u7684\u5fc3\u5374\u5728\u523a\u75db\u3002 \u3000\u3000\u535c\u9e70\u53c8\u5728\u95ee\u4ed6\uff1a\u201c\u4f60\u7684\u5251\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d\u5251\u5df2\u4e0d\u5728\u4ed6\u8eab\u65c1\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u60f3\u627e\u56de\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u3002\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u51b7\u7b11\uff1a\u201c\u4f60\u4e0d\u60f3\uff0c\u9664\u4e86\u8fd9\u4e2a\u5973\u4eba\u5916\uff0c\u4f60\u4ec0\u4e48\u90fd\u6ca1\u6709\u60f3\u3002\u2026 \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u6ca1\u6709\u5426\u8ba4\uff0c\u5c45\u7136\u4f38\u51fa\u4e86\u624b\uff0c\u8f7b\u629a\u6ce2\u5a03\u88ab\u98ce\u5439\u4e71\u4e86\u7684\u5934\u53d1\u3002 \u3000\u3000\u5728\u535c\u9e70\u9762\u524d\uff0c\u4ed6\u672c\u6765\u4e0d\u60f3\u8fd9\u4e48\u505a\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5df2\u7ecf\u505a\u51fa\u6765\u4e86\uff0c\u5df2\u4e0d\u662f\u51fa\u81ea\u540c\u60c5\u601c\u60af\uff0c\u4e5f\u4e0d\u662f\u56e0\u4e3a\u4e00\u65f6\u51b2\u52a8\uff0c\u800c\u662f\u56e0\u4e3a\u4e00\u79cd\u65e0\u6cd5\u63cf\u8ff0\u3001\u4e0d\u53ef\u89e3\u91ca\u7684\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u79cd\u611f\u60c5\u5e76\u4e0d\u662f\u535c\u9e70\u80fd\u591f\u4e86\u89e3\u7684\uff0c\u4ed6\u542c\u89c1\u535c\u9e70\u7684\u51b7\u7b11\u58f0\u5ffd\u7136\u8fdc\u53bb\u3002 \u3000\u3000\u5929\u5730\u95f4\u4eff\u4f5b\u53ea\u5269\u4e0b\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff0c\u53ef\u662f\u4ed6\u5df2\u4e0d\u518d\u5b64\u72ec\u3002 \u3000\u3000\u4ed6\u6276\u8d77\u5979\uff0c\u7528\u53cc\u624b\u6367\u8d77\u5979\u7684\u8138\uff0c\u5979\u773c\u4e2d\u4ecd\u662f\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u6ca1\u6709\u8868\u60c5\uff0c\u5374\u6709\u4e86\u6cea\u3002 \u3000\u3000\u6cea\u75d5\u5e03\u6ee1\u5728\u5979\u5df1\u88ab\u6c99\u7c92\u64e6\u4f24\u7684\u8138\u4e0a\uff0c\u4ed6\u5ffd\u7136\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u4e00\u5b9a\u8981\u8ba9\u5979\u660e\u767d\u4ed6\u7684\u5fc3\u610f\u3002 \u3000\u3000\u201c\u8fd9\u4e0d\u662f\u4f60\u7684\u9519\uff0c\u6211\u4e0d\u6068\u4f60\u3002\u4e0d\u7ba1\u4f60\u4ee5\u524d\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u6211\u90fd\u4e0d\u5728\u4e4e\uff0c\u53ea\u8981\u6211\u8fd8\u80fd\u6d3b\u4e00\u5929\uff0c\u6211\u5c31\u8981\u7167\u987e\u4f60\u4e00\u5929\uff0c\u7edd\u4e0d\u8ba9\u4f60\u518d\u53d7\u4eba\u6446\u5e03\u3001\u88ab\u4eba\u6b3a\u8d1f\u3002\u201d \u3000\u3000\u5979\u9ed8\u9ed8\u5730\u542c\u7740\uff0c\u9ed8\u9ed8\u5730\u6d41\u7740\u6cea\uff0c\u65e2\u7136\u6ca1\u6709\u89e3\u91ca\u5979\u7684\u8fc7\u9519\uff0c\u4e5f\u6ca1\u6709\u62d2\u7edd\u4ed6\u7684\u67d4\u60c5\u3002\u4e0d\u7ba1\u4ed6\u600e\u4e48\u505a\uff0c\u5979\u90fd\u613f\u610f\u627f\u53d7\u4f9d\u987a\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u62b1\u8d77\u4e86\u5979\uff0c\u5927\u6b65\u5f80\u524d\u8d70\u3002\u80fd\u53bb\u591a\u8fdc\uff1f\u80fd\u6d3b\u591a\u4e45\uff1f\u4ed6\u65e2\u4e0d\u77e5\uff0c\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u8d70\u51fa\u591a\u8fdc\uff0c\u5c31\u542c\u89c1\u4e00\u9635\u9a7c\u94c3\u58f0\uff0c\u6bd4\u4ed9\u4e50\u8fd8\u60a6\u8033\u3001\u6bd4\u6218\u9f13\u66f4\u4ee4\u4eba\u632f\u594b\u7684\u9a7c\u94c3\u58f0\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u770b\u89c1\u4e86\u4e00\u961f\u4ed6\u4ece\u672a\u89c1\u8fc7\u7684\u5982\u6b64\u5e9e\u5927\u7684\u9a7c\u5546\u3002 \u3000\u3000\u65e0\u6570\u5339\u9a86\u9a7c\uff0c\u65e0\u6570\u4ef6\u8d27\u7269\uff0c\u65e0\u6570\u4e2a\u4eba\u3002\u4ed6\u7b2c\u4e00\u4e2a\u770b\u89c1\u7684\u662f\u4e2a\u9a7c\u5b50\u3001\u8ddb\u8db3\u3001\u65ad\u6307\u3001\u79c3\u9876\u3001\u778e\u4e86\u4e00\u53ea\u773c\u7684\u9a7c\u5b50\uff0c\u770b\u6765\u5374\u4ecd\u7136\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u9ad8\u5927\u51f6\u608d\u3002 \u3000\u3000\u5bf9\u8fd9\u79cd\u4eba\u8bf4\u8bdd\u662f\u7528\u4e0d\u7740\u515c\u5708\u5b50\u7684\u3002 \u3000\u3000\u201c\u6211\u59d3\u65b9\u3002\u201d\u4ed6\u76f4\u622a\u4e86\u5f53\u4ed6\u8bf4\uff0c\u201c\u6211\u6ca1\u6709\u6c34\uff0c\u6ca1\u6709\u98df\u7269\uff0c\u6ca1\u6709\u94f6\u94b1\uff0c\u6211\u5df2\u7ecf\u8ff7\u4e86\u8def\uff0c\u6240\u4ee5\u6211\u5e0c\u671b\u4f60\u4eec\u80fd\u6536\u5bb9\u6211\uff0c\u628a\u6211\u5e26\u51fa\u6c99\u6f20\u53bb\uff01\u201d \u3000\u3000\u9a7c\u5b50\u7528\u4e00\u53ea\u95ea\u95ea\u53d1\u4eae\u7684\u72ec\u773c\u76ef\u7740\u4ed6\uff0c\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u65e2\u7136\u4f60\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u6536\u5bb9\u4f60\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u662f\u4e2a\u4eba\uff0c\u4f60\u4eec\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u5c31\u56e0\u4e3a\u8fd9\u53e5\u8bdd\uff0c\u6240\u4ee5\u4ed6\u4eec\u6536\u5bb9\u4e86\u4ed6\u3002 \u3000\u3000\u9a7c\u961f\u4e2d\u7684\u5546\u65c5\u6765\u81ea\u5404\u65b9\uff0c\u6709\u88c5\u675f\u5947\u5f02\u800c\u534e\u4e3d\u7684\u85cf\u4eba\uff0c\u6709\u96c4\u58ee\u575a\u97e7\u7684\u8499\u4eba\uff0c\u6709\u559c\u7a7f\u7d2b\u886b\u7684\u4e0d\u4e39\u4eba\uff0c\u4e5f\u6709\u6ee1\u9762\u98ce\u5c18\u3001\u8fdc\u79bb\u6545\u4e61\u7684\u6c49\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u8d29\u5356\u7684\u8d27\u7269\u662f\u7f8a\u6bdb\u3001\u76ae\u9769\u3001\u787c\u7802\u3001\u7816\u8336\u3001\u6c60\u76d0\u3001\u836f\u6750\u548c\u9e9d\u9999\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u76ee\u7684\u5730\u662f\u5510\u65f6\u7684\u5410\u9c81\u756a\u56fd\uff0c\u90fd\u66b9\u5a11\u57ce\uff0c\u4e5f\u5c31\u662f\u85cf\u4eba\u5fc3\u76ee\u4e2d\u5723\u5730\u201c\u62c9\u8428\u201d\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u7ec4\u6210\u867d\u590d\u6742\uff0c\u5374\u90fd\u662f\u5c5e\u4e8e\u540c\u4e00\u5546\u5bb6\u7684\uff0c\u6240\u4ee5\u5927\u5bb6\u5206\u5de5\u5408\u4f5c\uff0c\u76f8\u5904\u6781\u878d\u6d3d\u3002\u6709\u7684\u4eba\u7167\u6599\u9a7c\u9a6c\uff0c\u6709\u7684\u4eba\u6599\u7406\u996e\u98df\uff0c\u6709\u7684\u4eba\u533b\u6cbb\u75c5\u60a3\uff0c\u8fd8\u6709\u4e00\u7ec4\u6700\u5f3a\u58ee\u51f6\u608d\u7684\u4eba\uff0c\u8d1f\u8d23\u9632\u536b\u3001\u4e86\u671b\u3001\u5bf9\u62b5\u76d7\u532a\u3002 \u3000\u3000\u6536\u5bb9\u5c0f\u65b9\u7684\u9a7c\u5b50\uff0c\u5c31\u662f\u8fd9\u7ec4\u4eba\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u542c\u8bf4\u4ed6\u4eec\u7684\u9996\u9886\uff0c\u662f\u4e2a\u7ef0\u53f7\u540d\u53eb\u201c\u73ed\u5bdf\u5df4\u90a3\u201d\u7684\u85cf\u4eba\uff0c\u5374\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u901a\u5e38\u90fd\u5728\u56db\u65b9\u6e38\u6208\u3002 \u3000\u3000\u4ed6\u4e0d\u5728\u7684\u65f6\u5019\uff0c\u8fd9\u4e00\u7ec4\u4eba\u5c31\u7531\u90a3\u9a7c\u4e8e\u548c\u4e00\u4e2a\u53eb\u5510\u9e9f\u7684\u8700\u4eba\u8d1f\u8d23\u7ba1\u8f96\u3002 \u3000\u3000\u8981\u7ba1\u8f96\u8fd9\u6279\u4eba\u5e76\u4e0d\u5bb9\u6613\uff01 \u3000\u3000\u90a3\u9a7c\u4e8e\u867d\u7136\u662f\u4e2a\u6b8b\u5e9f\uff0c\u4f46\u662f\u884c\u52a8\u8f7b\u6377\u77eb\u5065\uff0c\u800c\u4e14\u795e\u529b\u60ca\u4eba\uff0c\u6570\u767e\u65a4\u91cd\u7684\u8d27\u7269\u5305\u88f9\uff0c\u4ed6\u7528\u4e00\u53ea\u624b\u5c31\u80fd\u8f7b\u6613\u63d0\u8d77\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u770b\u51fa\u4ed6\u65e0\u7591\u662f\u4e2a\u8eab\u6000\u7edd\u6280\u7684\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u5510\u9e9f\u6df1\u6c89\u7a33\u91cd\uff0c\u624b\u6307\u957f\u800c\u6709\u529b\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4ee5\u6bd2\u836f\u6697\u5668\u5a01\u9707\u5929\u4e0b\u7684\u8700\u4e2d\u5510\u95e8\u4e8e\u5f1f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u63d0\u8d77\u201c\u73ed\u5bdf\u5df4\u90a3\u201d\u65f6\uff0c\u6001\u5ea6\u90fd\u5341\u5206\u5c0a\u656c\u3002 \u3000\u3000\u5c0f\u65b9\u867d\u7136\u8fd8\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u5374\u5df2\u80fd\u60f3\u50cf\u5230\u4ed6\u7edd\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u3002 \u3000\u3000\u961f\u4f0d\u884c\u8d70\u5f97\u5e76\u4e0d\u5feb\u3002\u9a86\u9a7c\u672c\u6765\u5c31\u4e0d\u559c\u6b22\u5954\u8dd1\uff0c\u4eba\u4e5f\u6ca1\u6709\u8981\u6025\u7740\u8d76\u8def\u3002 \u3000\u3000\u592a\u9633\u4e00\u843d\u5c71\uff0c\u4ed6\u4eec\u5c31\u5c06\u9a86\u9a7c\u56f4\u6210\u4e00\u5708\uff0c\u5728\u5708\u5b50\u91cc\u7684\u7a7a\u5730\u4e0a\u642d\u8d77\u8f7b\u4fbf\u7684\u5e10\u7bf7\uff0c\u5c0f\u65b9\u548c\u6ce2\u5a03\u4e5f\u5206\u914d\u5230\u4e00\u4e2a\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u665a\u4e0a\u5c0f\u65b9\u7761\u5f97\u5f88\u719f\u3002 \u3000\u3000\u5728\u8fd9\u4e48\u6837\u4e00\u4e2a\u7ec4\u7ec7\u5b88\u62a4\u90fd\u975e\u5e38\u4e25\u5bc6\u7684\u961f\u4f0d\u91cc\uff0c\u4ed6\u5df2\u7ecf\u53ef\u4ee5\u5b89\u5fc3\u719f\u7761\u3002 \u3000\u3000\u4ed6\u5e0c\u671b\u6ce2\u5a03\u4e5f\u80fd\u597d\u597d\u5730\u7761\u4e00\u89c9\uff0c\u53ef\u662f\u76f4\u5230\u4ed6\u7b2c\u4e8c\u5929\u9192\u6765\u65f6\uff0c\u5979\u8fd8\u662f\u75f4\u75f4\u5730\u5750\u5728\u90a3\u91cc\uff0c\u773c\u4e2d\u867d\u5df2\u65e0\u6cea\uff0c\u5374\u6709\u4e86\u8868\u60c5\u3002 \u3000\u3000\u5979\u773c\u4e2d\u7684\u8868\u60c5\u4ee4\u4eba\u5fc3\u788e\u3002 \u3000\u3000\u867d\u7136\u5979\u4e00\u76f4\u90fd\u6ca1\u6709\u8bf4\u8fc7\u4e00\u53e5\u6094\u6068\u81ea\u759a\u7684\u8bdd\uff0c\u53ef\u662f\u5979\u7684\u773c\u8272\u5df2\u6bd4\u4efb\u4f55\u8a00\u8bed\u6240\u80fd\u8868\u8fbe\u7684\u90fd\u591a\u3002 \u3000\u3000\u5c0f\u65b9\u867d\u7136\u5df2\u539f\u8c05\u5979\uff0c\u5979\u5374\u4e0d\u80fd\u539f\u8c05\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u65f6\u95f4\u80fd\u4f7f\u5979\u5fc3\u91cc\u7684\u521b\u75a4\u5e73\u590d\u3002 \u3000\u3000\u4ed6\u9192\u6765\u65f6\u5929\u8fd8\u6ca1\u6709\u5b8c\u5168\u4eae\uff0c\u9a7c\u961f\u5374\u5df2\u51c6\u5907\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u8d70\u5e10\u7bf7\u65f6\uff0c\u9a7c\u5b50\u5df2\u7ecf\u5728\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u201c\u6628\u5929\u6211\u5df2\u5c06\u8fd9\u91cc\u7684\u60c5\u51b5\u544a\u8bc9\u4f60\uff0c\u4f60\u5df2\u7ecf\u5e94\u8be5\u660e\u767d\uff0c\u8fd9\u91cc\u6bcf\u4e2a\u4eba\u90fd\u8981\u505a\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u5c31\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u9a7c\u5b50\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u72ec\u773c\u4e2d\u7cbe\u5149\u95ea\u52a8\uff0c\u5ffd\u7136\u95ea\u7535\u822c\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u7684\u5de6\u624b\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e24\u6839\u624b\u6307\uff0c\u4ed6\u51fa\u624b\u65f6\uff0c\u8fd9\u4e24\u6839\u624b\u6307\u597d\u50cf\u5ffd\u7136\u53d8\u6210\u4e86\u4e00\u628a\u5251\u3001\u4e00\u67c4\u9525\u5b50\u3001\u4e00\u6761\u6bd2\u86c7\uff0c\u4e00\u4e0b\u5b50\u5c31\u60f3\u54ac\u4f4f\u5c0f\u65b9\u7684\u54bd\u5589\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u52a8\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\uff0c\u76f4\u5230\u8fd9\u4e24\u6839\u624b\u6307\u8ddd\u79bb\u4ed6\u54bd\u5589\u5df2\u4e0d\u53ca\u4e94\u5bf8\u65f6\uff0c\u4ed6\u7684\u8eab\u5b50\u624d\u5f00\u59cb\u79fb\u52a8\uff0c\u5ffd\u7136\u5c31\u5df2\u5230\u4e86\u9a7c\u5b50\u7684\u5de6\u4fa7\u3002 \u3000\u3000\u8fd9\u65f6\u9a7c\u5b50\u7684\u53f3\u638c\u5df2\u51fb\u51fa\uff0c\u8fd9\u4e00\u638c\u624d\u662f\u4ed6\u653b\u51fb\u7684\u4e3b\u529b\uff0c\u4ed6\u6325\u821e\u65f6\u5e26\u8d77\u7684\u98ce\u58f0\uff0c\u5df2\u5c06\u5e10\u7bf7\u9707\u52a8\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u653b\u51fb\u7684\u76ee\u6807\u5df2\u7ecf\u4e0d\u5728\u4ed6\u8ba1\u7b97\u4e2d\u7684\u65b9\u4f4d\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u770b\u51fa\u4ed6\u7684\u6307\u5251\u662f\u865a\u62db\uff0c\u5c0f\u65b9\u52a8\u5f97\u867d\u7136\u8fdf\uff0c\u5374\u6781\u5feb\uff0c\u5c0f\u65b9\u79fb\u52a8\u7684\u65b9\u5411\uff0c\u6b63\u662f\u4ed6\u8fd9\u4e00\u62f3\u5a01\u529b\u96be\u53ca\u7684\u5730\u65b9\uff0c\u4e5f\u6b63\u662f\u4ed6\u9632\u5b88\u6700\u7a7a\u865a\u4e4b\u5904\uff0c\u53ea\u8981\u4e00\u51fa\u624b\uff0c\u5c31\u53ef\u80fd\u5c06\u4ed6\u51fb\u5012\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u8ba9\u5bf9\u65b9\u77e5\u9053\u4ed6\u662f\u4e0d\u5bb9\u8f7b\u4fae\u7684\uff0c\u4ed6\u5df2\u5c06\u201c\u4ee5\u9759\u5236\u52a8\u3001\u4ee5\u6162\u6253\u5feb\u3001\u540e\u53d1\u5148\u81f3\u3001\u540e\u53d1\u5236\u4eba\u201d\u8fd9\u5341\u516d\u4e2a\u5b57\u7684\u7cbe\u4e49\u8868\u73b0\u51fa\u6765\u3002 \u3000\u3000\u9a7c\u5b50\u4e5f\u4e0d\u518d\u51fa\u624b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u9762\u5bf9\u9762\u5730\u7ad9\u7740\uff0c\u4e92\u76f8\u51dd\u89c6\u4e86\u5f88\u4e45\uff0c\u9a7c\u5b50\u624d\u6162\u6162\u8bf4\uff1a\u201c\u73b0\u5728\u6211\u5df2\u77e5\u9053\u4f60\u80fd\u505a\u4ec0\u4e48\u4e86\u3002\u201d\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u73b0\u5728\u5c0f\u65b9\u5f53\u7136\u4e5f\u77e5\u9053\u9a7c\u5b50\u8981\u4ed6\u505a\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u4e3a\u4e86\u751f\u5b58\uff0c\u4e3a\u4e86\u8981\u6d3b\u7740\u8d70\u51fa\u8fd9\u7247\u6c99\u6f20\uff0c\u4ed6\u53ea\u6709\u53bb\u505a\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5c3d\u529b\u4e3a\u81ea\u5df1\u548c\u6ce2\u5a03\u4e89\u53d6\u5230\u751f\u5b58\u7684\u6743\u5229\u3002\u4e0d\u80fd\u4e0d\u6b7b\u7684\u65f6\u5019\uff0c\u4ed6\u4e00\u5b9a\u5168\u5fc3\u5168\u610f\u5730\u53bb\u6c42\u6b7b\uff1b\u80fd\u591f\u6d3b\u4e0b\u53bb\u65f6\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u5168\u5fc3\u5168\u610f\u5730\u53bb\u4e89\u53d6\u3002 \u3000\u3000\u5510\u9e9f\u8eab\u957f\u4e0d\u53ca\u4e94\u5c3a\uff0c\u4f53\u91cd\u53ea\u6709\u4e94\u5341\u4e00\u65a4\uff0c\u53ef\u662f\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e00\u5bf8\u5730\u65b9\u90fd\u5145\u6ee1\u4e86\u53ef\u6015\u7684\u52b2\u529b\uff0c\u6bcf\u4e00\u5757\u808c\u8089\uff0c\u6bcf\u4e00\u6839\u9aa8\u9abc\uff0c\u6bcf\u4e00\u6839\u795e\u7ecf\uff0c\u90fd\u968f\u65f6\u4fdd\u6301\u7740\u6700\u5065\u5168\u7684\u72b6\u51b5\uff0c\u968f\u65f6\u53ef\u4ee5\u53d1\u51fa\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u4ed6\u5c5e\u4e0b\u7684\u4eba\u867d\u7136\u90fd\u6bd4\u4ed6\u9ad8\u5f88\u591a\uff0c\u53ef\u662f\u7ad9\u5728\u4ed6\u9762\u524d\u65f6\uff0c\u7edd\u4e0d\u6562\u5bf9\u4ed6\u6709\u4e00\u70b9\u8f7b\u89c6\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u4e00\u7ec4\u7684\u4eba\uff0c\u5176\u4e2d\u4e0d\u4f46\u6709\u6765\u81ea\u5173\u5185\u7684\u6b66\u6797\u8c6a\u6770\uff0c\u4e5f\u6709\u5173\u5916\u7684\u529b\u58eb\u3001\u5f02\u65cf\u7684\u5065\u513f\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u53c8\u591a\u4e86\u4e00\u4e2a\u540c\u4f34\u3002 \u3000\u3000\u201c\u4ed6\u59d3\u65b9\u3002\u201d\u9a7c\u5e72\u5c06\u5c0f\u65b9\u5e26\u5230\u4ed6\u4eec\u6bcf\u65e5\u51cc\u6668\u7684\u805a\u4f1a\u5730\uff1a\u201c\u6211\u60f3\u7528\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u6709\u7528\uff1f\u201d\u5510\u78f7\u95ee\uff0c\u53ea\u95ee\u4e86\u8fd9\u53e5\u3002 \u3000\u3000\u201c\u6709\u3002\u201d \u3000\u3000\u5510\u9e9f\u4e0d\u518d\u5f00\u53e3\uff0c\u4ed6\u4efb\u8fd9\u4e2a\u9a7c\u5b50\uff0c\u4ed6\u4e00\u5411\u4e0d\u591a\u8bdd\u3002 \u3000\u3000\u53ef\u60dc\u522b\u4eba\u5e76\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u7ec4\u4eba\u98de\u626c\u8dcb\u6248\u3001\u91ce\u6027\u672a\u9a6f\uff0c\u8c01\u4e5f\u6ca1\u6709\u628a\u522b\u4eba\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u51e0\u4e2a\u4eba\u4ea4\u6362\u4e86\u773c\u8272\uff0c\u7b2c\u4e00\u4e2a\u51fa\u5934\u7684\u662f\u9a6c\u6c99\u3002 \u3000\u3000\u9a6c\u6c99\u9ad8\u5927\u7c97\u58ee\uff0c\u4e00\u8eab\u86ee\u529b\uff0c\u662f\u8499\u85cf\u4e00\u5e26\u51fa\u540d\u7684\u52c7\u58eb\uff0c\u4e5f\u662f\u6570\u4e00\u6570\u4e8c\u7684\u6454\u8de4\u597d\u624b\u3002\u8981\u627e\u522b\u4eba\u7684\u9ebb\u70e6\uff0c\u7b2c\u4e00\u4e2a\u51fa\u5934\u7684\u603b\u662f\u4ed6\u3002 \u3000\u3000\u201c\u6211\u6765\u8bd5\u8bd5\u4ed6\u6709\u591a\u5927\u7684\u672c\u9886\uff01\u201d \u3000\u3000\u559d\u58f0\u51fa\u53e3\uff0c\u4ed6\u4e00\u53cc\u8fde\u86ee\u725b\u90fd\u80fd\u6454\u5012\u7684\u5927\u624b\uff0c\u5df2\u642d\u4e0a\u5c0f\u65b9\u7684\u80a9\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u4eba\u7acb\u523b\u88ab\u6454\u5f97\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9a6c\u6c99\u5927\u7b11\uff0c\u3002\u521a\u521a\u7b11\u51fa\u6765\uff0c\u5ffd\u7136\u5c31\u7b11\u4e0d\u51fa\u4e86\u3002\u521a\u521a\u660e\u660e\u5df2\u7ecf\u88ab\u4ed6\u6454\u51fa\u53bb\u7684\u4eba\uff0c\u5ffd\u7136\u95f4\u53c8\u5df2\u56de\u5230\u4ed6\u9762\u524d\uff0c\u8fd8\u662f\u7ad9\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u8fd8\u662f\u539f\u6765\u7684\u6837\u5b50\uff0c\u597d\u50cf\u6839\u672c\u6ca1\u52a8\u8fc7\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u679c\u7136\u6709\u4e24\u624b\u3002\u201d \u3000\u3000\u9a6c\u6c99\u5927\u543c\uff0c\u4f7f\u51fa\u4e86\u6454\u8de4\u4e2d\u6700\u5389\u5bb3\u7684\u4e00\u62db\uff0c\u636e\u8bf4\u4ed6\u66fe\u7ecf\u7528\u8fd9\u4e00\u62db\u6454\u6b7b\u8fc7\u4e00\u5934\u725b\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u6b21\u5c0f\u65b9\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u4e24\u6761\u817f\u5c31\u50cf\u751f\u5728\u5730\u4e0a\u4f3c\u7684\u3002 \u3000\u3000\u9a6c\u6c99\u5410\u6c14\u5f00\u58f0\uff0c\u91ce\u517d\u822c\u4f4e\u543c\uff0c\u5c06\u5168\u8eab\u6c14\u529b\u5168\u90fd\u4f7f\u51fa\u3002 \u3000\u3000\u8fd9\u6b21\u5c0f\u65b9\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u80a9\u8f6f\u8f6f\u4e00\u5378\uff0c\u9a6c\u6c99\u86ee\u725b\u822c\u7684\u8eab\u5b50\u5ffd\u7136\u51cc\u7a7a\u7ffb\u4e86\u4e2a\u8ddf\u6597\uff0c\u4ef0\u5929\u8dcc\u5012\uff0c\u51e0\u4e4e\u628a\u6c99\u5730\u7838\u51fa\u4e00\u4e2a\u5751\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u89e3\u8155\u5c16\u5200\u51fa\u9798\uff0c\u4e00\u5200\u523a\u5411\u5c0f\u65b9\u7684\u8170\u3002 \u3000\u3000\u201c\u4f60\u518d\u8bd5\u8bd5\u8fd9\u4e00\u5200\uff01\u201d \u3000\u3000\u8fd9\u4eba\u5148\u51fa\u624b\uff0c\u518d\u51fa\u58f0\uff0c\u679c\u5c14\u6d1b\u65cf\u7684\u6218\u58eb\u8981\u6740\u4eba\u65f6\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u201c\u52a0\u7b54\u201d\u5c31\u662f\u4ed6\u4eec\u4e4b\u4e2d\u6700\u51f6\u608d\u7684\u6218\u58eb\u4e4b\u4e00\u3002 \u3000\u3000\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0c\u6740\u4eba\u5c31\u662f\u6740\u4eba\uff0c\u53ea\u8981\u80fd\u6740\u5f97\u6b7b\u4eba\uff0c\u4e0d\u7ba1\u7528\u4ec0\u4e48\u6cd5\u5b50\u90fd\u540c\u6837\u5149\u8363\u3002 \u3000\u3000\u559d\u58f0\u51fa\u53e3\uff0c\u4ed6\u7684\u5200\u950b\u51e0\u4e4e\u5df2\u523a\u5165\u5c0f\u65b9\u7684\u8170\uff0c\u53ef\u60dc\u4ed6\u7684\u624b\u8155\u4e5f\u5df2\u88ab\u5c0f\u65b9\u62e7\u4f4f\uff0c\u7136\u540e\u4ed6\u7684\u5200\u5c31\u5230\u4e86\u5c0f\u65b9\u53e6\u4e00\u53ea\u624b\u91cc\u3002 \u3000\u3000\u5c0f\u65b9\u6c89\u6c89\u4ed6\u8bf4\uff1a\u201c\u4f60\u8981\u6740\u6211\uff0c\u6211\u5c31\u8be5\u6740\u4f60\uff0c\u4f60\u6740\u4e0d\u6b7b\u6211\uff0c\u5c31\u8be5\u6b7b\u5728\u6211\u624b\u91cc\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5411\u52a0\u7b54\u95ee\u9053\uff1a\u201c\u8fd9\u6837\u5b50\u662f\u4e0d\u662f\u5f88\u516c\u5e73\uff1f\u201d \u3000\u3000\u52a0\u7b54\u5934\u4e0a\u5df2\u75db\u5f97\u5192\u51fa\u4e86\u6c57\uff0c\u624b\u8155\u51e0\u4e4e\u5df2\u88ab\u62e7\u65ad\uff0c\u5374\u8fd8\u662f\u54ac\u7740\u7259\u8bf4\uff1a\u201c\u516c\u5e73\uff01\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\uff0c\u5ffd\u7136\u677e\u5f00\u4e86\u4ed6\u7684\u624b\uff0c\u628a\u4ed6\u7684\u5200\u63d2\u56de\u4ed6\u6d82\u4e86\u6cb9\u7684\u725b\u76ae\u5200\u9798\u91cc\u3002 \u3000\u3000\u201c\u6211\u4e0d\u80fd\u6740\u4f60\uff0c\u56e0\u4e3a\u4f60\u662f\u4e2a\u52c7\u58eb\uff0c\u4e0d\u6015\u6b7b\u7684\u52c7\u58eb\u3002\u201d \u3000\u3000\u52a0\u7b54\u77aa\u7740\u4ed6\uff0c\u5ffd\u7136\u5bf9\u7740\u4ed6\u4f38\u51fa\u4e86\u820c\u5934\uff0c\u4f38\u5f97\u5f88\u957f\u5f88\u957f\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u662f\u5728\u505a\u9b3c\u8138\uff0c\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u4e25\u8083\u800c\u606d\u656c\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4ece\u6000\u4e2d\u62ff\u51fa\u4e00\u5757\u6708\u767d\u8272\u7684\u4e1d\u4e2d\uff0c\u7528\u53cc\u624b\u6367\u4e0a\uff0c\u653e\u5728\u5c0f\u65b9\u8db3\u4e0b\u3002 \u3000\u3000\u5e78\u597d\u5c0f\u65b9\u5df2\u5728\u8fd9\u4e00\u5e26\u8d70\u8fc7\u5f88\u591a\u5730\u65b9\uff0c\u603b\u7b97\u6ca1\u6709\u8bef\u89e3\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u5411\u4eba\u5410\u820c\u5934\uff0c\u5c31\u662f\u85cf\u4eba\u6700\u9ad8\u7684\u793c\u8282\uff0c\u8868\u793a\u4ed6\u4eec\u5bf9\u4f60\u7684\u5c0a\u656c\u3002 \u3000\u3000\u90a3\u5757\u767d\u8272\u7684\u4e1d\u4e2d\uff0c\u5c31\u662f\u85cf\u4eba\u6700\u91cd\u89c6\u7684\u201c\u54c8\u8fbe\u201d\uff0c\u5982\u679c\u4e00\u4e2a\u4eba\u5411\u4f60\u732e\u51fa\u54c8\u8fbe\uff0c\u5c31\u8868\u793a\u4ed6\u5df2\u5c06\u4f60\u5f53\u4f5c\u4ed6\u6700\u5c0a\u8d35\u7684\u670b\u53cb\u3002 \u3000\u3000\u6240\u4ee5\u5c0f\u65b9\u5728\u8fd9\u91cc\u81f3\u5c11\u5df2\u7ecf\u6709\u4e86\u4e00\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u6ca1\u6709\u522b\u7684\u4eba\u518d\u51fa\u624b\uff0c\u6bcf\u4e2a\u4eba\u770b\u7740\u5c0f\u65b9\u65f6\uff0c\u773c\u8272\u90fd\u5df1\u8ddf\u521a\u624d\u4e0d\u540c\u3002 \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u4ed6\u4eec\u5df2\u63a5\u7eb3\u4e86\u4ed6\u3002 \u3000\u3000\u9a7c\u5b50\u4e00\u76f4\u51b7\u773c\u65c1\u89c2\uff0c\u8fd9\u65f6\u624d\u5f00\u53e3\uff1a\u201c\u6211\u4eec\u8fd9\u4e00\u7ec4\u7684\u4ee3\u53f7\u662f\u2018\u7bad\u2019\uff0c\u73b0\u5728\u4f60\u5df2\u662f\u2018\u7bad\u7ec4\u2019\u7684\u4eba\uff0c\u4e5f\u5f97\u50cf\u522b\u4eba\u4e00\u6837\uff0c\u6bcf\u5929\u8f6e\u73ed\u4e00\u6b21\u3002\u6211\u4eec\u8fd9\u4e00\u6b21\u5e26\u56de\u53bb\u7684\u8d27\u7269\u5f88\u8d35\u91cd\uff0c\u53ea\u8981\u6709\u53ef\u7591\u7684\u4eba\u60f3\u6765\u52a8\u6211\u4eec\u7684\u8d27\u7269\uff0c\u4f60\u5c31\u53ef\u4ee5\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a\u201c\u4f60\u751a\u81f3\u53ef\u4ee5\u7528\u521a\u624d\u52a0\u7b54\u8981\u6740\u4f60\u7684\u65b9\u6cd5\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5510\u9cde\u9053\uff1a\u201c\u4eca\u5929\u4f60\u662f\u5728\u9ec4\u660f\u65f6\u5f53\u73ed\uff0c\u6211\u6d3e\u52a0\u7b54\u8ddf\u4f60\u4e00\u73ed\uff0c\u5230\u65f6\uff0c\u4ed6\u4f1a\u53bb\u8ddf\u4f60\u8054\u7edc\u3002\u201d \u3000\u3000\u9a7c\u5b50\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53ef\u4ee5\u56de\u53bb\u7167\u987e\u4f60\u7684\u5973\u4eba\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u72ec\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u7b11\u610f\uff1a\u201c\u90a3\u4e2a\u5973\u4eba\u770b\u8d77\u6765\u662f\u4e2a\u597d\u5973\u4eba\uff0c\u8fd9\u91cc\u7684\u5973\u4eba\u5927\u5c11\uff0c\u7537\u4eba\u5927\u591a\uff0c\u4f60\u8981\u7279\u522b\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9ed8\u9ed8\u5730\u542c\u7740\uff0c\u9ed8\u9ed8\u5730\u8d70\u5f00\uff0c\u8d70\u51fa\u6ca1\u591a\u8fdc\uff0c\u5c31\u542c\u89c1\u5510\u9e9f\u5728\u95ee\u9a7c\u5b50\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u59d3\u65b9\u7684\u6b66\u529f\u5f88\u4e0d\u9519\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u7684\u6b66\u529f\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u95ee\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u542c\u89c1\u4ed6\u4eec\u4e0b\u9762\u8bf4\u7684\u8bdd\uff0c\u56e0\u4e3a\u9a7c\u5b50\u7684\u58f0\u97f3\u5ffd\u7136\u538b\u5f97\u5f88\u4f4e\uff0c\u4ed6\u4e5f\u8d70\u8fdc\u4e86\u3002 \u3000\u3000\u961f\u4f0d\u5a49\u8712\u524d\u884c\uff0c\u8d70\u5f97\u5f88\u6162\u3002 \u3000\u3000\u6709\u7684\u4eba\u4e3a\u4e86\u8868\u793a\u5bf9\u5723\u5730\u7684\u5411\u5f80\u3001\u8654\u8bda\uff0c\u4e09\u6b65\u4e00\u62dc\uff0c\u4e94\u6b65\u4e09\u53e9\u3002 \u3000\u3000\u6ce2\u5a03\u5374\u5206\u914d\u5230\u4e00\u5339\u9a86\u9a7c\uff0c\u5979\u75f4\u75f4\u5730\u5750\u5728\u9a86\u9a7c\u4e0a\uff0c\u773c\u4e2d\u8fd8\u662f\u4e00\u7247\u7a7a\u6d1e\u8ff7\u60c6\uff0c\u4eff\u4f5b\u4ec0\u4e48\u90fd\u6ca1\u6709\u60f3\uff0c\u53c8\u4eff\u4f5b\u60f3\u5f97\u5927\u591a\u3002 \u3000\u3000\u5c0f\u65b9\u91cc\u5fc3\u5374\u4e00\u76f4\u5728\u60f3\u7740\u9a7c\u5b50\u521a\u624d\u8bf4\u7684\u90a3\u53e5\u8bdd\u3002 \u3000\u3000\u2014\u2014\u6211\u4eec\u8fd9\u6b21\u5e26\u56de\u53bb\u7684\u8d27\u7269\u5f88\u8d35\u91cd\uff0c\u53ea\u8981\u6709\u53ef\u7591\u7684\u4eba\u63a5\u8fd1\uff0c\u4f60\u5c31\u8981\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u6000\u7591\u3002 \u3000\u3000\u96be\u9053\u4ed6\u4eec\u8fd9\u6b21\u5e26\u56de\u53bb\u7684\u8fd9\u6279\u8d27\u7269\uff0c\u5c31\u662f\u90a3\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\uff1f \u3000\u3000\u96be\u9053\u8fd9\u4e9b\u4eba\u5c31\u662f\u732b\u76d7\uff1f \u3000\u3000\u7528\u8fd9\u79cd\u65b9\u6cd5\u6765\u63a9\u9970\u4ed6\u4eec\u7684\u8eab\u4efd\u867d\u7136\u4e0d\u80fd\u7b97\u592a\u597d\uff0c\u53ef\u662f\u8981\u5c06\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u8fd0\u51fa\u6c99\u6f20\uff0c\u9664\u4e86\u8fd9\u6cd5\u5b50\u5916\u4e5f\u6ca1\u6709\u518d\u597d\u7684\u6cd5\u5b50\u4e86\u3002 \u3000\u3000\u201c\u7bad\u7ec4\u201d\u4e2d\u90a3\u4e9b\u6765\u81ea\u5404\u65b9\u7684\u6597\u58eb\uff0c\u5982\u679c\u6234\u4e0a\u6709\u732b\u8033\u7684\u9762\u5177\uff0c\u5c82\u975e\u7acb\u523b\u5c31\u53ef\u4ee5\u53d8\u6210\u732b\u76d7\uff1f \u3000\u3000\u4ed6\u4eec\u7684\u884c\u8e2a\u867d\u7136\u53ef\u7591\uff0c\u4f46\u662f\u5176\u4e2d\u4e5f\u6709\u95ee\u9898\u3002\u8fd9\u4e48\u5e9e\u5927\u7684\u961f\u4f0d\u8d70\u5728\u6c99\u6f20\u4e0a\uff0c\u536b\u5929\u9e4f\u7edd\u4e0d\u4f1a\u6ca1\u6709\u6ce8\u610f\u5230\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e3a\u4ec0\u4e48\u6ca1\u6709\u5bf9\u4ed6\u4eec\u91c7\u53d6\u884c\u52a8\uff1f \u3000\u3000\u5982\u679c\u4ed6\u4eec\u771f\u7684\u662f\u732b\u76d7\uff0c\u4e3a\u4ec0\u4e48\u8981\u63a5\u7eb3\u5c0f\u65b9\u8fd9\u4e48\u6837\u4e00\u4e2a\u6765\u5386\u4e0d\u660e\uff0c\u7684\u964c\u751f\u4eba\uff1f \u3000\u3000\u5c0f\u65b9\u51b3\u5b9a\u4e0d\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u4e9b\u4eba\u603b\u7b97\u5bf9\u4ed6\u4e0d\u9519\uff0c\u5982\u679c\u4e0d\u662f\u4ed6\u4eec\u6536\u5bb9\u4e86\uff0c\u73b0\u5728\u4ed6\u5f88\u53ef\u80fd\u5df2\u7ecf\u5728\u5140\u9e70\u7684\u809a\u5b50\u91cc\u3002 \u3000\u3000\u98df\u6c34\u662f\u88ab\u4e25\u683c\u7ba1\u5236\u7740\u7684\u3002 \u3000\u3000\u8d1f\u8d23\u8fd9\u4ef6\u4e8b\u7684\u4eba\u59d3\u4e25\uff0c\u53eb\u4e25\u6b63\u521a\uff0c\u4ed6\u7684\u4eba\u5982\u5176\u540d\uff0c\u521a\u6b63\u516c\u76f4\u3001\u4e00\u4e1d\u4e0d\u82df\u3002 \u3000\u3000\u5728\u65c5\u9014\u4e2d\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u6709\u75c5\u75db\u3002 \u3000\u3000\u8d1f\u8d23\u7167\u6599\u75c5\u60a3\u7684\uff0c\u662f\u4e2a\u4ece\u5173\u4e2d\u6d41\u6d6a\u5230\u8fd9\u91cc\u7684\u843d\u7b2c\u79c0\u624d\uff0c\u7626\u5f31\u4f5d\u507b\uff0c\u6ee1\u9762\u75c5\u5bb9\uff0c\u867d\u7136\u4ed6\u81ea\u5df1\u7684\u75c5\u90fd\u6cbb\u4e0d\u597d\uff0c\u5927\u5bb6\u5374\u5168\u90fd\u5bf9\u4ed6\u5341\u5206\u5c0a\u656c\uff0c\u90fd\u79f0\u4ed6\u4e3a\u5b8b\u8001\u592b\u5b50\u3002 \u3000\u3000\u5c0f\u65b9\u5f88\u5feb\u5c31\u8ba4\u5f97\u4e86\u4ed6\u4eec\uff0c\u5374\u4e00\u76f4\u6ca1\u6709\u89c1\u5230\u90a3\u4f4d\u884c\u8e2a\u98d8\u5ffd\u7684\u201c\u73ed\u5bdf\u5df4\u90a3\u201d\uff0c\u4e5f\u6ca1\u6709\u518d\u89c1\u5230\u536b\u5929\u9e4f\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7adf\u4f3c\u5b8c\u5168\u6ca1\u6709\u6ce8\u610f\u5230\u6c99\u6f20\u4e2d\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u5e9e\u5927\u7684\u961f\u4f0d\u3002 \u3000\u3000\u9ec4\u660f\u3002 \u3000\u3000\u9a86\u9a7c\u53c8\u88ab\u56f4\u6210\u4e00\u5708\uff0c\u5e10\u7bf7\u53c8\u67b6\u8d77\u3002 \u3000\u3000\u6ce2\u5a03\u663e\u5f97\u66f4\u6194\u6dec\u3001\u66f4\u5a07\u5f31\uff0c\u6709\u65f6\u867d\u7136\u4f1a\u5077\u5077\u5730\u770b\u5c0f\u65b9\u4e00\u773c\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u5f00\u8fc7\u53e3\u3002 \u3000\u3000\u5e78\u597d\u5979\u8fd8\u662f\u90a3\u4e48\u987a\u4ece\uff0c\u5c0f\u65b9\u8981\u5979\u5403\u559d\uff0c\u5979\u5c31\u5403\u559d\uff0c\u8981\u5979\u7761\u4e0b\uff0c\u5979\u5c31\u7761\u4e0b\u3002 \u3000\u3000\u8fd9\u79cd\u6001\u5ea6\u66f4\u4ee4\u4eba\u5fc3\u9178\u3002 \u3000\u3000\u4ed6\u672c\u6765\u60f3\u591a\u966a\u966a\u5979\u7684\uff0c\u53ef\u662f\u52a0\u7b54\u5df2\u7ecf\u6765\u53eb\u4ed6\u53bb\u5f53\u73ed\u4e86\u3002 \u3000\u3000\u8d27\u7269\u90fd\u5df2\u4ece\u9a7c\u80cc\u4e0a\u5378\u4e0b\uff0c\u96c6\u4e2d\u5728\u4e00\u4e2a\u5730\u65b9\uff0c\u5806\u5f97\u50cf\u662f\u4e2a\u6c99\u4e18\u3002 \u3000\u3000\u4ece\u9ec4\u660f\u5230\u5348\u591c\uff0c\u6709\u5341\u4e8c\u4e2a\u4eba\u5206\u6210\u516d\u73ed\u5de1\u903b\uff0c\u5c0f\u65b9\u548c\u52a0\u7b54\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u65e0\u8bba\u8c01\u60f3\u8981\u62c6\u5f00\u4e00\u5305\u8d27\u7269\u6765\u770b\uff0c\u90fd\u5f88\u96be\u4e0d\u88ab\u53d1\u73b0\u3002 \u3000\u3000\u5c0f\u65b9\u6839\u672c\u5df2\u62d2\u7edd\u53bb\u60f3\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u201c\u5bcc\u8d35\u795e\u4ed9\u201d\u7684\u9ec4\u91d1\u5df2\u7ecf\u5927\u591a\u4e86\uff0c\u672c\u6765\u5c31\u5e94\u8be5\u5206\u51fa\u4e00\u70b9\u7ed9\u522b\u4eba\u3002 \u3000\u3000\u5929\u8272\u6e10\u6697\uff0c\u4ed6\u4eec\u5728\u8d27\u7269\u9644\u8fd1\u5de1\u6208\u3002\u52a0\u7b54\u59cb\u7ec8\u6545\u610f\u843d\u540e\u4e00\u6b65\uff0c\u8868\u793a\u4ed6\u5bf9\u5c0f\u65b9\u7684\u5c0a\u656c\uff0c\u5c0f\u65b9\u4e0d\u8bf4\u8bdd\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u5148\u5f00\u53e3\u7684\u5f53\u7136\u662f\u5c0f\u65b9\uff1a\u201c\u6211\u770b\u5f97\u51fa\u9a6c\u6c99\u662f\u4e2a\u52c7\u58eb\uff0c\u4ed6\u662f\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u52a0\u7b54\u7684\u8138\u8272\u5f88\u6c89\u91cd\uff0c\u201c\u4f46\u662f\uff0c\u6211\u4ee5\u540e\u6050\u6015\u6c38\u8fdc\u770b\u4e0d\u89c1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u5f88\u60ca\u5f02\u3002 \u3000\u3000\u201c\u592a\u9633\u8fd8\u5728\u5929\u6b63\u4e2d\u7684\u65f6\u5019\uff0c\u4ed6\u8981\u6211\u966a\u4ed6\u53bb\u653e\u7caa\uff0c\u6211\u6ca1\u6709\u7caa\uff0c\u6211\u6ca1\u6709\u53bb\uff0c\u4ed6\u72ec\u81ea\u53bb\u4e86\u3002\u201d \u3000\u3000\u52a0\u7b54\u773c\u4e2d\u9732\u51fa\u4e86\u60b2\u4f24\uff1a\u201c\u4ed6\u53bb\u4e86\u540e\u5c31\u6ca1\u6709\u518d\u56de\u6765\u8fc7\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e86\u89e3\u4ed6\u7684\u60b2\u4f24\u3002 \u3000\u3000\u5728\u6c99\u6f20\u4e2d\uff0c\u9020\u6210\u6b7b\u4ea1\u7684\u539f\u56e0\u5b9e\u5728\u5927\u591a\uff0c\u4efb\u4f55\u4eba\u968f\u65f6\u90fd\u53ef\u80fd\u5ffd\u7136\u50cf\u91ce\u72d7\u822c\u6b7b\u5728\u6c99\u783e\u4e0a\uff0c\u9664\u4e86\u4ed6\u771f\u6b63\u7684\u670b\u53cb\u5916\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u5173\u5fc3\u4ed6\uff0c\u66f4\u4e0d\u4f1a\u4e3a\u4ed6\u60b2\u4f24\u3002 \u3000\u3000\u5929\u8272\u66f4\u6697\uff0c\u8fdc\u5904\u5ffd\u7136\u54cd\u8d77\u4e00\u9635\u80e1\u54e8\uff0c\u4e24\u5339\u5feb\u9a6c\u98de\u9a70\u800c\u6765\u3002\u8fd9\u961f\u4f0d\u4e2d\u4e5f\u6709\u9a6c\u5339\u3002 \u3000\u3000\u201c\u8fd9\u662f\u5510\u9e9f\u6d3e\u51fa\u53bb\u627e\u9a6c\u6c99\u7684\u4eba\u56de\u6765\u4e86\u3002\u201d\u52a0\u7b54\u7cbe\u795e\u4e00\u632f\uff0c\u201c\u9a6c\u6c99\u4e00\u5b9a\u4e5f\u5df2\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u5feb\u9a6c\u5954\u6765\uff0c\u4ed6\u5df2\u8fce\u4e0a\u53bb\u3002\u9a6c\u6c99\u679c\u7136\u4e5f\u56de\u6765\u4e86\uff0c\u56de\u6765\u7684\u5374\u4e0d\u662f\u6d3b\u9a6c\u6c99\u3002 \u3000\u3000\u8fd9\u4e2a\u795e\u529b\u60ca\u4eba\u7684\u52c7\u58eb\uff0c\u6570\u4e00\u6570\u4e8c\u7684\u6454\u8de4\u597d\u624b\uff0c\u5934\u9888\u5df2\u88ab\u62d7\u65ad\uff0c\u7adf\u662f\u88ab\u4eba\u7528\u6454\u8de4\u7684\u624b\u6cd5\u6d3b\u6d3b\u627c\u6b7b\u7684\uff01 \u3000\u3000\u662f\u8c01\u6740\u4e86\u4ed6\uff1f\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u6ca1\u6709\u4eba\u77e5\u9053\u3002 \u3000\u3000\u795e\u79d8\u800c\u53ef\u6015\u7684\u6b7b\u4ea1\u9634\u5f71\uff0c\u5df2\u7ecf\u50cf\u9ed1\u6697\u672c\u8eab\u4e00\u6837\uff0c\u7b3c\u7f69\u4e86\u8fd9\u961f\u4f0d\u3002 \u3000\u3000\u9a6c\u6c99\u53ea\u4e0d\u8fc7\u662f\u7b2c\u4e00\u4e2a\u66b4\u6b7b\u7684\u4eba\uff0c\u4ed6\u4eec\u56de\u5230\u5de1\u903b\u7684\u5730\u65b9\u65f6\uff0c\u5c31\u53d1\u73b0\u4e86\u7b2c\u4e8c\u4e2a\u3002 \u3000\u3000\u7bad\u7ec4\u4e2d\u7684\u597d\u624b\u5982\u4e91\uff0c\u6709\u7684\u5584\u7528\u5200\uff0c\u6709\u7684\u5584\u7528\u5251\uff0c\u6709\u7684\u7cbe\u5e72\u89d2\u529b\u6454\u8de4\uff0c\u7528\u957f\u97ad\u7684\u5374\u53ea\u6709\u4e00\u4e2a\u3002 \u3000\u3000\u5b59\u4eae\u7528\u7684\u957f\u97ad\u662f\u4e00\u4e08\u4e09\u5c3a\u957f\u7684\u86c7\u97ad\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u66b4\u6b7b\u7684\u4eba\u5c31\u662f\u4ed6\uff0c\u5c31\u88ab\u4ed6\u81ea\u5df1\u7684\u86c7\u97ad\u6d3b\u6d3b\u7ede\u6b7b\u7684\u3002 \u3000\u3000\u8ddf\u4ed6\u540c\u73ed\u5de1\u903b\u7684\u51af\u6d69\u4e5f\u5931\u8e2a\u4e86\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929\u51cc\u6668\uff0c\u624d\u627e\u5230\u4ed6\u7684\u5c38\u8eab\u3002 \u3000\u3000\u51af\u6d69\u662f\u91d1\u5200\u95e8\u7684\u5f1f\u5b50\uff0c\u4e3a\u4e86\u4e00\u4ef6\u547d\u6848\uff0c\u9003\u4ea1\u51fa\u5173\u3002 \u3000\u3000\u4ed6\u7528\u7684\u662f\u4e00\u67c4\u91d1\u80cc\u780d\u5c71\u5200\u3002 \u3000\u3000\u4ed6\u7684\u5200\u8fd8\u5728\uff0c\u5934\u9885\u5374\u5df2\u4e0d\u5728\uff0c\u4ed6\u7684\u5934\u9885\u5c31\u662f\u88ab\u4ed6\u81ea\u5df1\u90a3\u67c4\u91d1\u5200\u780d\u4e0b\u6765\u7684\u3002 \u3000\u3000\u4e00\u591c\u4e2d\u5c31\u5df2\u6709\u4e09\u4e2a\u4eba\u79bb\u5947\u66b4\u6b7b\uff0c\u53ef\u662f\u795e\u79d8\u7684\u6b7b\u4ea1\u8fd8\u53ea\u4e0d\u8fc7\u662f\u521a\u5f00\u59cb\u3002 \u3000\u3000\u5348\u591c\u3002 \u3000\u3000\u5c0f\u65b9\u56de\u5230\u4ed6\u7684\u5e10\u7bf7\u65f6\uff0c\u4e0d\u4f46\u75b2\u5026\uff0c\u800c\u4e14\u6cae\u4e27\u3002 \u3000\u3000\u66b4\u6b7b\u7684\u4e09\u4e2a\u4eba\uff0c\u867d\u7136\u8ddf\u4ed6\u5168\u65e0\u5173\u7cfb\uff0c\u4f46\u662f\uff0c\u5154\u6b7b\u72d0\u60b2\uff0c\u4ed6\u5fc3\u91cc\u4e5f\u96be\u514d\u89c9\u5f97\u5f88\u4e0d\u597d\u53d7\u3002 \u3000\u3000\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u6240\u906d\u9047\u5230\u7684\u6bcf\u4ef6\u4e8b\u90fd\u4ee4\u4ed6\u5931\u671b\u3002\u795e\u79d8\u7684\u52ab\u6848\uff0c\u4e0d\u5e78\u7684\u707e\u96be\uff0c\u66b4\u623e\u7684\u6b7b\u4ea1\uff0c\u4eff\u4f5b\u603b\u662f\u8ddf\u968f\u7740\u4ed6\u3002 \u3000\u3000\u51a5\u51a5\u4e2d\u4eff\u4f5b\u5df2\u6709\u79cd\u90aa\u6076\u7684\u529b\u91cf\uff0c\u5c06\u4ed6\u548c\u8fd9\u4e9b\u4e0d\u7965\u7684\u4e8b\u8054\u7ed3\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5e10\u7bf7\u91cc\u9759\u5bc2\u800c\u9ed1\u6697\uff0c\u867d\u7136\u4ed6\u5e0c\u671b\u6ce2\u5a03\u80fd\u591f\u5b89\u6170\u4ed6\uff0c\u4f46\u662f\u4ed6\u4e5f\u4e86\u89e3\u5979\u7684\u5fc3\u60c5\uff0c\u4e0d\u7ba1\u5979\u662f\u4e0d\u662f\u5df2\u7761\u7740\uff0c\u4ed6\u90fd\u4e0d\u6562\u518d\u6253\u6270\u5979\u3002 \u3000\u3000\u6478\u7d22\u7740\u627e\u5230\u4e00\u5f20\u6bdb\u6be1\uff0c\u4ed6\u9759\u9759\u5730\u8eba\u4e86\u4e0b\u53bb\uff0c\u53ea\u5e0c\u671b\u80fd\u591f\u5f88\u5feb\u7761\u7740\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u7761\u7740\u3002 \u3000\u3000\u6ce2\u5a03\u5149\u6ed1\u67d4\u8f6f\u7684\u8eab\u5b50\u5df2\u8d34\u8fd1\u4ed6\uff0c\u4ed6\u4e0d\u4f46\u80fd\u611f\u89c9\u5230\u5979\u7684\u6e29\u6696\uff0c\u4e5f\u80fd\u611f\u89c9\u5230\u5979\u4e00\u76f4\u5728\u4e0d\u505c\u5730\u98a4\u6296\uff0c\u4e5f\u4e0d\u77e5\u662f\u56e0\u4e3a\u7d27\u5f20\uff0c\u8fd8\u662f\u56e0\u4e3a\u60b2\u4f24\uff1f \u3000\u3000\u5979\u770b\u5f97\u51fa\uff0c\u4ed6\u9700\u8981\u5b89\u6170\uff0c\u6240\u4ee5\u5979\u7ed9\u4e86\u4ed6\u3002\u4e0d\u7ba1\u5979\u81ea\u5df1\u7684\u5fc3\u60c5\u600e\u4e48\u6837\uff0c\u53ea\u8981\u5979\u80fd\u591f\u7ed9\u4ed6\u7684\uff0c\u7528\u4e0d\u7740\u4ed6\u8981\u6c42\uff0c\u5979\u4e5f\u4f1a\u7ed9\u4ed6\u3002 \u3000\u3000\u8fd9\u4e16\u754c\u4e0a\u4ece\u672a\u6709\u4e00\u4e2a\u5973\u4eba\u8fd9\u4e48\u6837\u5bf9\u5f85\u4ed6\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u4e5f\u53d1\u73b0\u81ea\u5df1\u4e5f\u5f00\u59cb\u5728\u98a4\u6296\u3002 \u3000\u3000\u4ed6\u4eec\u4e92\u76f8\u63a5\u7eb3\u65f6\uff0c\u5df2\u4e0d\u4ec5\u662f\u60c5\u6b32\u7684\u53d1\u6cc4\uff0c\u60c5\u6b32\u5df2\u5347\u534e\u3002\u4ed6\u4ece\u672a\u60f3\u5230\u8fd9\u79cd\u4e8b\u4e5f\u4f1a\u53d8\u5f97\u8fd9\u4e48\u7f8e\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u516d\u7ae0\u3000\u4e00\u5251\u7a7f\u5fc3 \u7b2c\u516d\u7ae0\u3000\u4e00\u5251\u7a7f\u5fc3 \u3000\u3000\u7b49\u5230\u4e00\u5207\u90fd\u8fc7\u53bb\u540e\uff0c\u4ed6\u5fc3\u91cc\u4ecd\u7136\u5145\u6ee1\u4e86\u751c\u871c\u4e0e\u6e29\u67d4\u3002 \u3000\u3000\u4ed6\u6709\u8fc7\u5973\u4eba\uff0c\u53ef\u662f\u4ed6\u4ece\u672a\u5230\u8fbe\u8fc7\u8fd9\u4e48\u7f8e\u7684\u5883\u754c\u3002 \u3000\u3000\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5979\u5ffd\u7136\u8f7b\u8f7b\u4ed6\u8bf4\uff1a\u201c\u5979\u662f\u6211\u7684\u59d0\u59d0\u3002\u201d \u3000\u3000\u6ce2\u5a03\u5c45\u7136\u5f00\u53e3\u8bf4\u8bdd\u4e86\uff0c\u53ef\u662f\u8fd9\u53e5\u8bdd\u5374\u8bf4\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u201c\u8c01\u662f\u4f60\u7684\u59d0\u59d0\uff1f\u201d\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u96be\u9053\u90a3\u4e2a\u6076\u6bd2\u7684\u5973\u4eba\u5c31\u662f\u4f60\u59d0\u59d0\uff1f\u201d \u3000\u3000\u6ce2\u5a03\u8f7b\u8f7b\u70b9\u5934\uff1a\u201c\u6211\u4ece\u5c0f\u5c31\u662f\u8ddf\u7740\u5979\u7684\uff0c\u5979\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u5c31\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u4f60\u4ece\u6765\u4e0d\u53cd\u6297\uff1f\u201d \u3000\u3000\u201c\u6211\u4ece\u6765\u6ca1\u6709\u60f3\u5230\u8fc7\u3002\u201d \u3000\u3000\u5979\u975e\u4f46\u4e0d\u6562\u53cd\u6297\uff0c\u751a\u81f3\u8fde\u60f3\u4e0d\u6562\u60f3\uff0c\u6240\u4ee5\u5979\u624d\u4f1a\u5bf9\u4ed6\u505a\u90a3\u79cd\u4e8b\uff0c\u5979\u7ec8\u4e8e\u5411\u4ed6\u8bf4\u51fa\u4e86\u5979\u7684\u82e6\u8877\u3002 \u3000\u3000\u4ec0\u4e48\u4e8b\u90fd\u7528\u4e0d\u7740\u89e3\u91ca\uff0c\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u5fc5\u518d\u8bf4\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u5fc3\u91cc\u7684\u6cae\u4e27\u548c\u82e6\u95f7\u90fd\u5df2\u50cf\u8f7b\u70df\u822c\u6563\u53bb\u4e86\uff0c\u4e16\u4e0a\u5df2\u4e0d\u518d\u6709\u4ec0\u4e48\u80fd\u503c\u5f97\u4ed6\u70e6\u607c\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u4ed6\u7d27\u7d27\u62e5\u62b1\u7740\u5979\u3002 \u3000\u3000\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u53ea\u8981\u6211\u6d3b\u7740\uff0c\u5c31\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u518d\u88ab\u4eba\u6b3a\u8d1f\u3002\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u867d\u7136\u662f\u8fd9\u4e48\u8bf4\uff0c\u53ef\u662f\uff0c\u5c06\u6765\u5462\uff1f\u201d \u3000\u3000\u592a\u957f\u4e45\u7684\u82e6\u96be\uff0c\u5df2\u4f7f\u5979\u5bf9\u4eba\u5931\u53bb\u4fe1\u5fc3\uff1a\u201c\u8c01\u77e5\u9053\u5c06\u6765\u4f1a\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u4e5f\u4f1a\u53d8\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5c06\u6765\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u6211\u90fd\u4e0d\u4f1a\u53d8\uff0c\u4f60\u4e00\u5b9a\u8981\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4fe1\u3002\u201d\u5979\u7684\u8138\u8d34\u7740\u4ed6\u7684\u8138\uff0c\u8138\u4e0a\u5df1\u6709\u51b0\u51c9\u7684\u6cea\u73e0\uff0c\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u957f\u591c\u4ecd\u672a\u8fc7\u53bb\u3002 \u3000\u3000\u6700\u5927\u7684\u4e00\u4e2a\u5e10\u7bf7\u91cc\u706f\u706b\u901a\u660e\uff0c\u5510\u9e9f\u5df2\u5c06\u4ed6\u8fd9\u4e00\u7ec4\u6240\u6709\u7684\u4eba\u90fd\u53ec\u96c6\u5230\u8fd9\u91cc\u6765\uff0c\u5c0f\u65b9\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u8fd9\u65f6\u8ddd\u79bb\u51af\u6d69\u7684\u66b4\u6b7b\u5df2\u6709\u56db\u4e2a\u591a\u65f6\u8fb0\u3002\u5c0f\u65b9\u5df2\u7761\u8fc7\u4e00\u89c9\uff0c\u522b\u7684\u4eba\u5374\u663e\u5f97\u6ca1\u6709\u4ed6\u5e78\u8fd0\uff0c\u6bcf\u4e2a\u4eba\u770b\u6765\u90fd\u5f88\u52b3\u7d2f\u75b2\u5026\u3002 \u3000\u3000\u5510\u9e9f\u7684\u773c\u4e2d\u5e03\u6ee1\u8840\u4e1d\uff0c\u795e\u60c5\u5374\u8fd8\u662f\u5f88\u9547\u9759\u3002 \u3000\u3000\u201c\u6211\u4eec\u5df2\u5206\u6279\u51fa\u53bb\u641c\u7d22\u8fc7\uff0c\u9644\u8fd1\u4e09\u5341\u91cc\u4e4b\u5185\uff0c\u7edd\u65e0\u4eba\u8ff9\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u6781\u6709\u81ea\u4fe1\uff0c\u4ed6\u6d3e\u51fa\u53bb\u7684\u6bcf\u4e2a\u4eba\uff0c\u5728\u8fd9\u65b9\u9762\u90fd\u662f\u4e13\u5bb6\uff0c\u5982\u679c\u4ed6\u4eec\u8bf4\u8fd9\u9644\u8fd1\u4e09\u5341\u91cc\u4e2d\u6ca1\u6709\u4eba\u8ff9\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u627e\u51fa\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6740\u6b7b\u51af\u6d69\u4ed6\u4eec\u7684\u51f6\u624b\uff0c\u5fc5\u5b9a\u5c31\u662f\u6211\u4eec\u8fd9\u961f\u4f0d\u7684\u4eba\uff0c\u73b0\u5728\u4e00\u5b9a\u8fd8\u7559\u5728\u961f\u4f0d\u91cc\u3002\u201d \u3000\u3000\u5510\u9e9f\u7684\u58f0\u97f3\u51b0\u51b7\uff1a\u201c\u8fd9\u961f\u4f0d\u4e2d\u80fd\u6740\u6b7b\u4ed6\u4eec\u4e94\u4e2a\u4eba\u7684\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u4e94\u4e2a\u4eba\uff1f\u201d\u5c0f\u65b9\u8131\u53e3\u95ee\u3002 \u3000\u3000\u201c\u662f\u4e94\u4e2a\u4eba\u3002\u201d\u5510\u9e9f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7761\u89c9\u7684\u65f6\u5019\uff0c\u53c8\u6b7b\u4e86\u4e24\u4e2a\uff0c\u4f60\u4e00\u5b9a\u7761\u5f97\u5f88\u719f\uff0c\u6240\u4ee5\u8fde\u4ed6\u4eec\u6b7b\u524d\u7684\u60e8\u53eb\u90fd\u6ca1\u6709\u542c\u89c1\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4e5f\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u5510\u9e9f\u9053\uff1a\u201c\u4ed6\u4eec\u4e94\u4e2a\u4eba\u7684\u6765\u5386\u4e0d\u540c\uff0c\u6b66\u529f\u95e8\u6237\u4e5f\u4e0d\u540c\uff0c\u66f4\u6ca1\u6709\u540c\u65f6\u4e0e\u4eba\u7ed3\u4ec7\uff0c\u6240\u4ee5\u4ed6\u4eec\u7684\u6b7b\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ec7\u6740\u3002\u201d \u3000\u3000\u53ef\u662f\u6740\u4eba\u4e00\u5b9a\u6709\u539f\u56e0\uff0c\u6709\u52a8\u673a\u3002 \u3000\u3000\u6740\u4eba\u7684\u52a8\u673a\uff0c\u901a\u5e38\u53ea\u6709\u4e24\u79cd\u2014\u2014\u8d22\u3001\u8272\u3002 \u3000\u3000\u5510\u9e9f\u9053\uff1a\u201c\u4ed6\u4eec\u88ab\u6740\uff0c\u4e00\u5b9a\u662f\u56e0\u4e3a\u6709\u4eba\u60f3\u52a8\u6211\u4eec\u8fd9\u6279\u8d27\u3002\u201d \u3000\u3000\u9a7c\u5b50\u76f4\u5230\u8fd9\u65f6\u624d\u5f00\u53e3\uff1a\u201c\u8d27\u7269\u5df2\u7ecf\u88ab\u4eba\u52a8\u8fc7\uff0c\u800c\u4e14\u6709\u5341\u51e0\u5305\u8d27\u90fd\u5df2\u88ab\u4eba\u5272\u5f00\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u90a3\u4e2a\u4eba\u5148\u8981\u770b\u770b\u8fd9\u4e9b\u8d27\u662f\u4e0d\u662f\u503c\u5f97\u4ed6\u52a8\u624b\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u4f60\uff0c\u4f60\u8ba4\u4e3a\u662f\u5426\u503c\u5f97\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u503c\u5f97\u3002\u201d \u3000\u3000\u201c\u8fd9\u6279\u8d27\u4e00\u4e2a\u4eba\u867d\u7136\u642c\u4e0d\u8d70\uff0c\u4f46\u662f\u4ed6\u5982\u679c\u80fd\u5c06\u6211\u4eec\u4e00\u4e2a\u4e2a\u5168\u90e8\u6697\u6740\uff0c\u8d27\u5c31\u662f\u4ed6\u7684\u4e86\u3002\u201d \u3000\u3000\u5510\u9e9f\u7684\u76ee\u5149\u59cb\u7ec8\u6ca1\u6709\u6b63\u89c6\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u6211\u4eec\u867d\u7136\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u4f46\u6211\u4eec\u4e00\u5b9a\u80fd\u67e5\u51fa\u6765\uff0c\u56e0\u4e3a\u8fd9\u961f\u4f0d\u4e2d\u6bcf\u4e2a\u4eba\u7684\u6765\u5386\u6211\u4eec\u90fd\u5df2\u8c03\u67e5\u5f97\u6e05\u695a\u3002\u201d \u3000\u3000\u5176\u5b9e\u5e76\u4e0d\u662f\u6bcf\u4e2a\u4eba\uff0c\u8fd8\u6709\u4eba\u662f\u4f8b\u5916\u3002 \u3000\u3000\u5c0f\u65b9\u5c31\u662f\u552f\u4e00\u7684\u4f8b\u5916\u3002 \u3000\u3000\u5510\u9e9f\u9053\uff1a\u201c\u5728\u51f6\u624b\u8fd8\u672a\u67e5\u51fa\u4e4b\u524d\uff0c\u6211\u4eec\u6682\u65f6\u7559\u5728\u6b64\u5904\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u79bb\u5f00\u961f\u4f0d\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8fc7\u5934\uff0c\u7528\u4e00\u53cc\u6ee1\u5e03\u8840\u4e1d\u7684\u773c\u775b\u76ef\u7740\u5c0f\u65b9\uff1a\u201c\u5c24\u5176\u662f\u4f60\uff0c\u4f60\u6682\u65f6\u6700\u597d\u4e0d\u8981\u79bb\u5f00\u4f60\u7684\u5e10\u7bf7\u4e00\u6b65\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8fd8\u662f\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u90fd\u662f\u5728\u4ed6\u5230\u540e\u624d\u53d1\u751f\u7684\uff0c\u65e0\u8bba\u8c01\u90fd\u96be\u514d\u8981\u5bf9\u4ed6\u6000\u7591\u3002 \u3000\u3000\u5510\u9e9f\u4e5f\u5df2\u4e0d\u518d\u63a9\u9970\u8fd9\u4e00\u70b9\uff1a\u201c\u4f60\u6700\u597d\u73b0\u5728\u5c31\u56de\u5230\u4f60\u7684\u5e10\u7bf7\u91cc\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u521a\u51c6\u5907\u8d70\uff0c\u60f3\u4e0d\u5230\u5c45\u7136\u6709\u4eba\u66ff\u4ed6\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u52a0\u7b54\u4e00\u76f4\u60f3\u8bf4\u7684\uff0c\u60f3\u8bf4\uff0c\u53c8\u4e0d\u6562\u8bf4\uff0c\u73b0\u5728\u624d\u58ee\u8d77\u80c6\u5b50\u3002 \u3000\u3000\u201c\u4e0d\u662f\u4ed6\uff0c\u4ed6\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4f60\u4eec\u8bf4\u90a3\u4e2a\u4eba\uff0c\u6211\u4e0d\u662f\u778e\u5b50\uff0c\u4ed6\u6740\u4e86\u4eba\uff0c\u6211\u770b\u5f97\u89c1\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u5f97\u89c1\u3002\u201d \u3000\u3000\u201c\u6211\u8ddf\u4ed6\uff0c\u4ed6\u8ddf\u6211\uff0c\u5c31\u597d\u8c61\u4e00\u4e2a\u4eba\u8ddf\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\uff0c\u4e00\u76f4\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5510\u9e9f\u51b7\u7b11\uff1a\u201c\u4f60\u62b1\u7740\u9a6c\u6c99\u7684\u5c38\u4f53\u75db\u54ed\u6d41\u6d95\u65f6\uff0c\u4f60\u4e5f\u770b\u89c1\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u52a0\u7b54\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u4e00\u6839\u80a0\u5b50\uff0c\u4e00\u5f88\u4ece\u5634\u5df4\u901a\u5230\u5e95\u7684\u80a0\u5b50\uff0c\u770b\u89c1\u4e86\u5c31\u662f\u770b\u89c1\u4e86\uff0c\u6ca1\u770b\u89c1\u5c31\u662f\u6ca1\u770b\u89c1\u3002 \u3000\u3000\u5510\u9e9f\u7528\u4e00\u53ea\u9752\u7b4b\u5df2\u66b4\u51fa\u7684\u624b\u63c9\u4e86\u63c9\u4ed6\u90a3\u53cc\u53d1\u7ea2\u7684\u773c\u775b\uff1a\u201c\u6211\u7684\u8bdd\u5df2\u7ecf\u8bf4\u5b8c\u4e86\u3002\u6211\u7684\u610f\u601d\u4f60\u4eec\u4e00\u5b9a\u5168\u90fd\u660e\u767d\u3002\u201d\u4ed6\u6325\u4e86\u6325\u624b\uff1a\u201c\u4f60\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u8d70\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u8d70\u5f97\u6700\u5feb\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6709\u4eba\u5728\u7b49\u4ed6\uff0c\u53ef\u4ee5\u7ed9\u4ed6\u5b89\u6170\u3002 \u3000\u3000\u4ed6\u521a\u8d70\u5165\u4ed6\u7684\u5e10\u7bf7\uff0c\u521a\u770b\u89c1\u8737\u4f0f\u5728\u6bdb\u6be1\u4e2d\u7684\u6ce2\u5a03\uff0c\u5c31\u542c\u89c1\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u6ca1\u6709\u7761\u7740\uff0c\u8fd9\u6b21\u4ed6\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u60e8\u547c\u58f0\u5c31\u662f\u4ece\u4ed6\u521a\u624d\u79bb\u5f00\u7684\u90a3\u5e10\u7bf7\u4e2d\u4f20\u51fa\u6765\u7684\uff0c\u800c\u4e14\u5c31\u662f\u5510\u9e9f\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5510\u9e9f\u5df2\u7ecf\u6b7b\u4e86\uff0c\u7b49\u4ed6\u4eec\u8d76\u56de\u90a3\u5e10\u7bf7\u65f6\uff0c\u5510\u9e9f\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u4e00\u67c4\u96ea\u4eae\u7684\u5251\uff0c\u4ece\u4ed6\u7684\u524d\u80f8\u523a\u5165\uff0c\u80cc\u540e\u7a7f\u51fa\u3002 \u3000\u3000\u4e00\u5251\u7a7f\u5fc3\u800c\u8fc7\u3002 \u3000\u3000\u5e10\u7bf7\u91cc\u4f9d\u65e7\u706f\u706b\u901a\u660e\u3002 \u3000\u3000\u4e00\u51fb\u81f4\u547d\u3001\u4e00\u523a\u7a7f\u5fc3\u7684\u90a3\u67c4\u5251\uff0c\u4f9d\u65e7\u7559\u5728\u5510\u9e9f\u7684\u5c38\u4f53\u4e0a\u3002 \u3000\u3000\u96ea\u4eae\u7684\u5251\uff0c\u4eae\u5f97\u5c31\u50cf\u662f\u773c\u775b\u3002 \u3000\u3000\u2014\u2014\u521d\u604b\u65f6\u5c11\u5973\u7684\u68a6\u773c\uff0c\u9ed1\u591c\u4e2d\u7b49\u7740\u6355\u9f20\u7684\u732b\u773c\uff0c\u9965\u997f\u65f6\u7b49\u7740\u62e9\u4eba\u800c\u566c\u7684\u864e\u773c\uff0c\u51c6\u5907\u652b\u9e21\u65f6\u7684\u9e70\u773c\uff0c\u5669\u68a6\u4e2d\u7684\u9b3c\u773c\u3002 \u3000\u3000\u5982\u679c\u4f60\u80fd\u60f3\u8c61\u5230\u8fd9\u51e0\u79cd\u773c\u5149\u6df7\u5408\u5728\u4e00\u8d77\u65f6\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u5149\u8292\uff0c\u4f60\u624d\u80fd\u60f3\u8c61\u5230\u8fd9\u67c4\u5251\u7684\u5149\u8292\u3002 \u3000\u3000\u5730\u4e0a\u4e5f\u95ea\u7740\u5149\u3002 \u3000\u3000\u4e0d\u662f\u8fd9\u67c4\u5251\u7684\u4eae\u5149\uff0c\u800c\u662f\u4e00\u79cd\u6696\u6627\u7684\u3001\u9634\u68ee\u7684\u3001\u6349\u6478\u4e0d\u5b9a\u3001\u95ea\u52a8\u4e0d\u505c\u7684\u5bd2\u5149\u3002 \u3000\u3000\u53d1\u51fa\u8fd9\u79cd\u95ea\u5149\u7684\uff0c\u662f\u5341\u4e09\u679a\u6697\u9ed1\u5149\u7684\u94c1\u5668\u3002\u521a\u624d\u88ab\u53ec\u96c6\u7684\u4eba\u73b0\u5728\u5927\u534a\u90fd\u5df2\u56de\u6765\uff0c\u5176\u4e2d\u6709\u5f88\u591a\u4eba\u7684\u773c\u775b\u5374\u5f88\u5229\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u867d\u7136\u80fd\u770b\u5f97\u51fa\u53d1\u5149\u7684\u662f\u4ec0\u4e48\uff0c\u5374\u770b\u4e0d\u51fa\u5b83\u7684\u5f62\u72b6\u3002 \u3000\u3000\u5176\u4e2d\u96be\u514d\u6709\u4eba\u60f3\u6361\u8d77\u4e00\u679a\u6765\u770b\u770b\uff0c\u770b\u6e05\u695a\u4e9b\u3002 \u3000\u3000\u9a7c\u5b50\u5ffd\u5927\u559d\uff1a\u201c\u4e0d\u80fd\u78b0\uff0c\u78b0\u4e0d\u5f97\uff01\u201d \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u8bf4\u5f97\u5df2\u7ecf\u6162\u4e86\u4e9b\uff0c\u5df2\u7ecf\u6709\u4eba\u6361\u8d77\u4e86\u4e00\u679a\u3002 \u3000\u3000\u4ed6\u521a\u6361\u8d77\u6765\uff0c\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u4ed6\u7684\u77b3\u5b54\u5c31\u5df2\u7a81\u7136\u6da3\u6563\u3002\u4ed6\u7684\u8138\u5c31\u5df2\u5f00\u59cb\u53d8\u8272\uff0c\u53d8\u6210\u4e00\u79cd\u66a7\u6627\u7684\u3001\u9634\u68ee\u7684\u6b7b\u7070\u8272\uff0c\u5634\u89d2\u540c\u65f6\u9732\u51fa\u4e00\u79cd\u8be1\u79d8\u800c\u5947\u5f02\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u5728\u5403\u60ca\u5730\u770b\u7740\u4ed6\u8fd9\u79cd\u53d8\u5316\uff0c\u4ed6\u81ea\u5df1\u5374\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u611f\u89c9\u5230\u3002 \u3000\u3000\u4ed6\u8fd8\u5728\u95ee\uff1a\u201c\u4f60\u4eec\u770b\u6211\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u53ea\u6709\u4e03\u4e2a\u5b57\uff0c\u8bf4\u51fa\u4e86\u8fd9\u4e03\u4e2a\u5b57\uff0c\u4ed6\u7684\u8138\u5c31\u5df2\u5b8c\u5168\u626d\u66f2\u53d8\u5f62\uff0c\u4ed6\u7684\u4eba\u5c31\u597d\u50cf\u4e00\u4e2a\u5ffd\u7136\u88ab\u62bd\u7a7a\u4e86\u6c14\u7684\u76ae\u7403\uff0c\u5ffd\u7136\u840e\u7f29\u3001\u5012\u4e0b\u3002 \u3000\u3000\u4ed6\u5012\u4e0b\u65f6\u8138\u5df2\u53d1\u9ed1\uff0c\u6b7b\u9ed1\uff0c\u53ef\u662f\u90a3\u79cd\u8be1\u5f02\u7684\u7b11\u5bb9\u5374\u8fd8\u7559\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u597d\u50cf\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u8fd8\u89c9\u5f97\u5f88\u6109\u5feb\u3002 \u3000\u3000\u522b\u7684\u4eba\u5374\u5df2\u5168\u8eab\u53d1\u51b7\uff0c\u4ece\u9f3b\u5c16\u4e00\u76f4\u51b7\u5230\u5fc3\u91cc\uff0c\u4ece\u5fc3\u91cc\u4e00\u76f4\u51b7\u5230\u8db3\u5e95\u3002 \u3000\u3000\u6709\u4e9b\u89c1\u95fb\u6bd4\u8f83\u5e7f\u7684\u4eba\u5df2\u7ecf\u770b\u51fa\u6765\u4ed6\u662f\u4e2d\u4e86\u6bd2\uff0c\u5374\u8fd8\u662f\u60f3\u4e0d\u5230\u4ed6\u53ea\u4e0d\u8fc7\u7528\u624b\u6361\u8d77\u4e00\u6837\u4e1c\u897f\u6765\u5c31\u4f1a\u4e2d\u6bd2\uff0c\u6bd2\u6027\u7adf\u53d1\u4f5c\u5f97\u8fd9\u4e48\u5feb\u3002 \u3000\u3000\u53ea\u6709\u51e0\u4e2a\u4eba\u77e5\u9053\u4ed6\u6361\u8d77\u7684\u8fd9\u6837\u4e1c\u897f\uff0c\u5c31\u662f\u8700\u4e2d\u5510\u95e8\u5a01\u9707\u5929\u4e0b\u3001\u4ee4\u5929\u4e0b\u82f1\u96c4\u8c6a\u6770\u95fb\u540d\u4e27\u80c6\u7684\u6bd2\u836f\u6697\u5668\u3002 \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u7684\u6bd4\u4efb\u4f55\u4eba\u90fd\u591a\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u77e5\u9053\u8fd9\u79cd\u6697\u5668\u7684\u53ef\u6015\uff0c\u4e5f\u77e5\u9053\u8fd9\u67c4\u5251\u7684\u6765\u5386\u3002 \u3000\u3000\u201c\u8fd9\u662f\u9b54\u773c\u3002\u201d \u3000\u3000\u9a7c\u5b50\u62d4\u51fa\u4e86\u5c38\u4f53\u4e0a\u7684\u5251\uff0c\u5251\u950b\u4e0a\u6ca1\u6709\u7559\u4e0b\u4e00\u6ef4\u8840\uff0c\u660e\u4eae\u5982\u79cb\u6c34\u822c\u7684\u5251\u950b\u4e0a\uff0c\u53ea\u6709\u4e00\u70b9\u6687\u75b5\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u53ea\u773c\u775b\u3002 \u3000\u3000\u201c\u9b54\u773c\uff01\u201d\u6709\u4eba\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ec0\u4e48\u662f\u9b54\u773c\uff1f\u201d \u3000\u3000\u201c\u8fd9\u67c4\u5251\u7684\u540d\u5b57\u5c31\u53eb\u505a\u9b54\u773c\uff0c\u662f\u5f53\u4eca\u5929\u4e0b\u6700\u950b\u5229\u7684\u4e03\u67c4\u5251\u4e4b\u4e00\u3002\u201d \u3000\u3000\u540d\u5251\u5c31\u50cf\u662f\u5b9d\u7389\uff0c\u672c\u6765\u662f\u4e0d\u5e94\u8be5\u6709\u6687\u75b5\u7684\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u5374\u662f\u4f8b\u5916\uff0c\u8fd9\u4e00\u70b9\u6687\u75b5\u53cd\u800c\u589e\u52a0\u4e86\u8fd9\u67c4\u5251\u7684\u53ef\u6015\u4e0e\u795e\u79d8\u3002 \u3000\u3000\u9a7c\u5b50\u8f7b\u629a\u5251\u950b\uff1b\u773c\u4e2d\u4e5f\u6709\u5149\u8292\u95ea\u52a8\u3002 \u3000\u3000\u201c\u5510\u9e9f\u867d\u7136\u662f\u8700\u4e2d\u5510\u95e8\u7684\u65c1\u652f\u5b50\u5f1f\uff0c\u5374\u662f\u5510\u5bb6\u53ef\u4ee5\u6570\u5f97\u51fa\u7684\u51e0\u4f4d\u9ad8\u624b\u4e4b\u4e00\uff0c\u4ed6\u7684\u51fa\u624b\u4e0d\u4f46\u5feb\u800c\u51c6\uff0c\u800c\u4e14\u8fd8\u7ec3\u8fc7\u5ce8\u5d4b\u7684\u4ed9\u733f\u5251\u3002\u201d \u3000\u3000\u5510\u9e9f\u7528\u7684\u662f\u67c4\u8f6f\u5251\uff0c\u5e73\u65f6\u76ae\u5e26\u822c\u56f4\u5728\u8170\u4e0a\uff0c\u4ed6\u62d4\u5251\u901f\u5ea6\u4e5f\u548c\u4ed6\u7684\u6697\u5668\u540c\u6837\u5feb\u3002 \u3000\u3000\u4ed6\u7684\u624b\u7ecf\u5e38\u5782\u5728\u8170\u7554\uff0c\u53ea\u8981\u624b\u4e00\u52a8\uff0c\u8170\u4e0a\u7684\u8f6f\u5251\u5c31\u4f1a\u6bd2\u86c7\u822c\u523a\u51fa\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u6b21\u4ed6\u8fde\u5251\u90fd\u6ca1\u6709\u62d4\u51fa\u6765\uff0c\u5bf9\u65b9\u7684\u5251\u5c31\u5df2\u7a7f\u5fc3\u800c\u8fc7\u3002 \u3000\u3000\u8fd9\u5251\u5b9e\u5728\u592a\u72e0\u3001\u592a\u5feb\u3002 \u3000\u3000\u4ed6\u4eec\u5f7c\u6b64\u4e86\u89e3\uff0c\u90fd\u77e5\u9053\u8fd9\u961f\u4f0d\u4e2d\u7684\u4eba\u8c01\u4e5f\u4f7f\u4e0d\u51fa\u5982\u6b64\u7280\u5229\u8fc5\u901f\u7684\u5251\u6cd5\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4ee5\u524d\u4e5f\u4ece\u672a\u89c1\u8fc7\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u51f6\u624b\u662f\u8c01\uff1f\u5251\u662f\u8c01\u7684\uff1f \u3000\u3000\u9a7c\u5b50\u5ffd\u7136\u8f6c\u8fc7\u5934\uff0c\u76ef\u7740\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u6211\u60f3\uff0c\u4f60\u4e00\u5b9a\u4e5f\u542c\u8bf4\u8fc7\u8fd9\u67c4\u5251\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u6211\u542c\u8bf4\u8fc7\u3002\u201d\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u201c\u8fd9\u67c4\u5251\u662f\u4e0d\u662f\u5df2\u7ecf\u843d\u5165\u4e00\u4e2a\u59d3\u65b9\u7684\u5e74\u9752\u5251\u5ba2\u624b\u91cc\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u59d3\u65b9\u7684\u4eba\u662f\u4e0d\u662f\u65b9\u4f1f\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u9a7c\u5b50\u72ec\u773c\u7684\u5149\u8292\u5ffd\u7136\u6536\u7f29\uff0c\u53d8\u5f97\u50cf\u662f\u4e00\u6839\u9488\u3001\u4e00\u6839\u523a\uff0c\u4ed6\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u95ee\uff1a\u201c\u4f60\u5c31\u662f\u65b9\u4f1f\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u5c31\u662f\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\uff0c\u6bcf\u4e2a\u4eba\u7684\u77b3\u5b54\u90fd\u5df2\u6536\u7f29\uff0c\u5fc3\u8df3\u90fd\u5df2\u52a0\u5feb\uff0c\u638c\u5fc3\u90fd\u5df2\u6c81\u51fa\u51b7\u6c57\u3002 \u3000\u3000\u5e10\u7bf7\u91cc\u7acb\u523b\u5145\u6ee1\u6740\u6c14\u3002 \u3000\u3000\u5c0f\u65b9\u4ecd\u7136\u4fdd\u6301\u9547\u9759\u3002 \u3000\u3000\u201c\u8fd9\u67c4\u5251\u662f\u6211\u7684\uff0c\u6211\u7684\u51fa\u624b\u4e00\u5411\u4e0d\u6162\uff0c\u8981\u6740\u5510\u9e9f\u4e5f\u4e0d\u96be\u3002\u201d \u3000\u3000\u5fc3\u8df3\u5f97\u66f4\u5feb\uff0c\u6709\u51e0\u53ea\u5e26\u7740\u51b7\u6c57\u7684\u624b\uff0c\u5df2\u7ecf\u6084\u6084\u5730\u63e1\u8d77\u5175\u5203\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u50cf\u662f\u6ca1\u770b\u89c1\uff0c\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u8fd9\u6b21\u5982\u679c\u771f\u662f\u6211\u6740\u4e86\u5510\u9e9f\uff0c\u6211\u4e3a\u4ec0\u4e48\u5c06\u8fd9\u67c4\u5251\u7559\u4e0b\u6765\uff1f\u96be\u9053\u6211\u662f\u4e2a\u75af\u5b50\uff1f\u96be\u9053\u6211\u751f\u6015\u522b\u4eba\u4e0d\u77e5\u9053\u662f\u6211\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8fd9\u67c4\u5251\u6211\u5f97\u6765\u5e76\u4e0d\u5bb9\u6613\uff0c\u6211\u7edd\u4e0d\u4f1a\u628a\u5b83\u7559\u7ed9\u522b\u4eba\u7684\uff0c\u4e0d\u7ba1\u90a3\u4e2a\u4eba\u662f\u6b7b\u662f\u6d3b\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u9a7c\u5b50\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6709\u7406\u3002\u201d \u3000\u3000\u4ed6\u7684\u76ee\u5149\u5df2\u4ece\u5c0f\u65b9\u8138\u4e0a\u79fb\u5f00\uff0c\u4ece\u4ed6\u5c5e\u4e0b\u7684\u8138\u4e0a\u6162\u6162\u5730\u626b\u89c6\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u4eec\u6709\u8fd9\u4e48\u6837\u4e00\u628a\u5251\uff0c\u4f60\u4eec\u6740\u4eba\u540e\u4f1a\u4e0d\u4f1a\u628a\u5b83\u7559\u4e0b\u6765\uff1f\u201d \u3000\u3000\u6ca1\u6709\u4eba\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff0c\u5c31\u7b97\u662f\u7b2c\u4e00\u6b21\u6740\u4eba\u7684\u51f6\u624b\uff0c\u4e5f\u4e0d\u4f1a\u5982\u6b64\u758f\u5ffd\u611a\u8822\u5927\u610f\u3002 \u3000\u3000\u672c\u6765\u5df2\u63e1\u7d27\u5175\u5203\u7684\u624b\u53c8\u653e\u677e\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u4e0d\u7981\u677e\u4e86\u53e3\u6c14\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u8fd9\u9a7c\u5b50\u4e0d\u4f46\u660e\u7406\uff0c\u800c\u4e14\u597d\u50cf\u4e00\u76f4\u90fd\u662f\u7ad9\u5728\u4ed6\u8fd9\u4e00\u8fb9\u7684\uff0c\u4e00\u76f4\u90fd\u5728\u6697\u6697\u4fdd\u62a4\u4ed6\u3002 \u3000\u3000\u9a7c\u5b50\u53c8\u9053\uff1a\u201c\u4f46\u662f\u51f6\u624b\u4e5f\u7edd\u4e0d\u4f1a\u662f\u6211\u4eec\u8fd9\u961f\u4f0d\u4e2d\u7684\u4eba\uff0c\u8fd9\u91cc\u6ca1\u6709\u4eba\u80fd\u4e00\u5251\u6740\u6b7b\u5510\u9e9f\uff0c\u4e5f\u6ca1\u4eba\u80fd\u4ece\u4f60\u624b\u4e2d\u593a\u8d70\u8fd9\u67c4\u5251\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u6709\u4e24\u4e09\u5929\u6ca1\u6709\u770b\u5230\u8fc7\u8fd9\u67c4\u5251\u4e86\uff0c\u4f60\u5e94\u8be5\u8bb0\u5f97\uff0c\u4f60\u7b2c\u4e00\u6b21\u89c1\u5230\u6211\u7684\u65f6\u5019\u8fd9\u67c4\u5251\u5e76\u4e0d\u5728\u6211\u624b\u91cc\u3002\u201d \u3000\u3000\u9a7c\u5b50\u7acb\u523b\u95ee\uff1a\u201c\u600e\u4e48\u4f1a\u4e0d\u5728\u4f60\u624b\u91cc\uff1f\u5728\u8c01\u7684\u624b\u91cc\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u536b\u5929\u9e4f\uff0c\u60f3\u5230\u4e86\u6c34\u94f6\uff0c\u60f3\u5230\u4e86\u90a3\u53ef\u6015\u7684\u65e0\u540d\u5251\u5ba2\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u60f3\u5230\u4e86\u535c\u9e70\u3002 \u3000\u3000\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u53ef\u80fd\u662f\u6740\u6b7b\u5510\u9e9f\u7684\u51f6\u624b\uff0c\u5374\u53c8\u4e0d\u592a\u53ef\u80fd\u3002 \u3000\u3000\u5728\u8fd9\u7247\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u63a9\u62a4\u7269\u7684\u7a7a\u65f7\u6c99\u6f20\u4e0a\uff0c\u65e0\u8bba\u8c01\u60f3\u8981\u5077\u5077\u5730\u4fb5\u5165\u8fd9\u5e10\u7bf7\uff0c\u6740\u4e86\u4eba\u540e\u518d\u5077\u5077\u5730\u6e9c\u8d70\uff0c\u662f\u4e0d\u53ef\u80fd\u7684\u3002 \u3000\u3000\u4ed6\u4e5f\u76f8\u4fe1\u8fd9\u4e00\u7ec4\u4eba\u7684\u80fd\u529b\uff0c\u5982\u679c\u9644\u8fd1\u6709\u4eba\u8d70\u52a8\uff0c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u67e5\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u9664\u975e\u51f6\u624b\u5df2\u6df7\u5165\u4e86\u8fd9\u961f\u4f0d\uff0c\u800c\u4e14\u5b8c\u5168\u6ca1\u6709\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u961f\u4f0d\u4e2d\u6bcf\u4e2a\u4eba\u5f7c\u6b64\u90fd\u5f88\u719f\u6089\uff0c\u522b\u7684\u4eba\u8981\u6df7\u8fdb\u6765\uff0c\u597d\u50cf\u4e5f\u7edd\u65e0\u53ef\u80fd\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u5c0f\u65b9\u90fd\u4e0d\u80fd\u89e3\u91ca\uff0c\u6240\u4ee5\u4ed6\u53ea\u6709\u95ed\u7740\u5634\u3002 \u3000\u3000\u9a7c\u5b50\u5c45\u7136\u4e5f\u6ca1\u6709\u8ffd\u95ee\uff0c\u53ea\u544a\u8bc9\u4ed6\uff1a\u201c\u5728\u51f6\u624b\u67e5\u51fa\u6765\u4e4b\u524d\uff0c\u4f60\u8fd8\u662f\u4e0d\u80fd\u79bb\u5f00\uff0c\u8fd9\u67c4\u5251\u4f60\u4e5f\u4e0d\u80fd\u5e26\u8d70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5728\u51f6\u624b\u67e5\u51fa\u6765\u4e4b\u524d\uff0c\u5c31\u7b97\u6709\u4eba\u8d76\u6211\u8d70\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8d70\u7684\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u771f\u5fc3\u6d3b\u3002 \u3000\u3000\u8fde\u4ed6\u81ea\u5df1\u90fd\u89c9\u5f97\uff0c\u8fd9\u4e9b\u4eba\u7684\u66b4\u6b7b\uff0c\u8ddf\u4ed6\u591a\u5c11\u603b\u6709\u70b9\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u4e5f\u60f3\u67e5\u51fa\u51f6\u624b\u662f\u8c01\u3002 \u3000\u3000\u9a7c\u5b50\u53c8\u5728\u5429\u5490\uff1a\u201c\u660e\u5929\u6211\u4eec\u4e0d\u8d70\uff0c\u8c01\u4e5f\u4e0d\u80fd\u79bb\u5f00\u961f\u4f0d\u3002\u4e09\u5341\u4e94\u5c81\u4ee5\u4e0b\u7684\u7537\u4eba\uff0c\u4e0d\u7ba1\u6709\u6ca1\u6709\u7ec3\u8fc7\u6b66\uff0c\u90fd\u8981\u52a0\u5165\u8b66\u536b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e5f\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5e78\u597d\u73ed\u5bdf\u5df4\u90a3\u660e\u5929\u4e00\u5b9a\u4f1a\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u957f\u591c\u5c06\u5c3d\u3002\u5e10\u7bf7\u91cc\u5df2\u7ecf\u6709\u4e86\u6726\u80e7\u7684\u66d9\u5149\u3002 \u3000\u3000\u6ce2\u5a03\u8fd8\u662f\u50cf\u521a\u624d\u4e00\u6837\u86e4\u4f0f\u5728\u90a3\u91cc\uff0c\u7528\u6bdb\u6be1\u76d6\u4f4f\u4e86\u5934\u3002 \u3000\u3000\u8fd9\u6b21\u5979\u662f\u771f\u7684\u7761\u7740\u4e86\uff0c\u7761\u5f97\u5f88\u719f\u3002 \u3000\u3000\u4e00\u4e2a\u7537\u4eba\u65e0\u8bba\u5728\u7ecf\u5386\u8fc7\u591a\u4e48\u53ef\u6015\u7684\u4e8b\u4ef6\u4e4b\u540e\uff0c\u56de\u6765\u65f6\u80fd\u591f\u770b\u89c1\u4e00\u4e2a\u8fd9\u4e48\u6837\u7684\u5973\u4eba\u5728\u7b49\u7740\u4ed6\uff0c\u5fc3\u91cc\u603b\u4f1a\u5145\u6ee1\u67d4\u60c5\u4e0e\u5b89\u6170\u3002 \u3000\u3000\u5c0f\u65b9\u5750\u4e0b\u6765\uff0c\u60f3\u6380\u8d77\u6bdb\u6be1\u770b\u770b\u5979\uff0c\u53c8\u6015\u5c06\u5979\u60ca\u9192\uff0c\u5374\u53c8\u504f\u504f\u5fcd\u4e0d\u4f4f\u4f38\u51fa\u4e86\u624b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u52a0\u7b54\u5ffd\u7136\u50cf\u4e00\u53ea\u5730\u9f20\u822c\u6e9c\u8fdb\u4e86\u4ed6\u7684\u5e10\u7bf7\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u53cc\u5f0f\u6837\u5947\u7279\u3001\u624b\u5de5\u7cbe\u81f4\u7684\u5c0f\u76ae\u9774\u3002 \u3000\u3000\u4ed6\u7684\u795e\u8272\u770b\u6765\u7d27\u5f20\u800c\u614e\u91cd\uff0c\u4ed6\u5ffd\u7136\u8dea\u4e0b\u6765\uff0c\u7528\u53cc\u624b\u5c06\u8fd9\u53cc\u76ae\u9774\u732e\u7ed9\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u8fd9\u662f\u5580\u5df4\u6c99\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u53ea\u6709\u8fd9\u4e00\u53cc\u5580\u5df4\u6c99\uff0c\u5c31\u597d\u50cf\u4f60\u53ea\u6709\u4e00\u628a\u2018\u9b54\u773c\u2019\u3002\u201d \u3000\u3000\u5c0f\u65b9\u867d\u7136\u542c\u4e0d\u61c2\u201c\u5580\u5df4\u6c99\u201d\u4e09\u4e2a\u5b57\uff0c\u5374\u731c\u5f97\u51fa\u52a0\u7b54\u8bf4\u7684\u5c31\u662f\u8fd9\u53cc\u9774\u4e8e\u3002 \u3000\u3000\u4ed6\u867d\u7136\u4e0d\u592a\u4e86\u89e3\u85cf\u4eba\u7684\u6c11\u4fd7\uff0c\u5374\u77e5\u9053\u85cf\u4eba\u6700\u770b\u91cd\u81ea\u5df1\u7684\u4e00\u53cc\u811a\u3002 \u3000\u3000\u5982\u679c\u4f60\u60f3\u4ece\u85cf\u4eba\u7684\u88c5\u675f\u4e0a\u770b\u51fa\u4ed6\u4eec\u7684\u8d2b\u5bcc\uff0c\u6700\u5bb9\u6613\u7684\u65b9\u6cd5\u5c31\u662f\u770b\u4ed6\u4eec\u811a\u4e0a\u7a7f\u7684\u9774\u5b50\uff0c\u5176\u8d35\u8d31\u7684\u60ac\u6b8a\uff0c\u7edd\u4e0d\u662f\u5916\u4eba\u6240\u80fd\u60f3\u8c61\u5f97\u5230\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u867d\u7136\u4e0d\u77e5\u9053\u201c\u5580\u5df4\u6c99\u201d\u5c31\u662f\u85cf\u4eba\u4eec\u7a7f\u7684\u9774\u5b50\u91cc\u6700\u534e\u8d35\u7684\u4e00\u79cd\uff0c\u751a\u81f3\u5728\u6ce2\u65af\u90fd\u5f15\u4ee5\u4e3a\u8d35\uff0c\u4f46\u5374\u770b\u5f97\u51fa\u52a0\u7b54\u5bf9\u8fd9\u53cc\u9774\u5b50\u7684\u91cd\u89c6\uff0c\u751a\u81f3\u5df2\u5c06\u8fd9\u53cc\u9774\u5b50\u4e0e\u90a3\u67c4\u5a01\u6151\u6c5f\u6e56\u7684\u540d\u5251\u76f8\u63d0\u5e76\u8bba\u3002 \u3000\u3000\u52a0\u7b54\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u6211\u6ca1\u6709\u7a7f\u8fc7\u8fd9\u53cc\u5580\u5df4\u6c99\uff0c\u6211\u7684\u811a\u6709\u811a\u6c57\uff0c\u6211\u4e0d\u914d\u7a7f\uff0c\u53ef\u662f\u6211\u672c\u6765\u4e5f\u7edd\u4e0d\u4f1a\u628a\u5b83\u7559\u7ed9\u522b\u4eba\uff0c\u53ef\u662f\u6211\u73b0\u5728\u732e\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u5f53\u7136\u8981\u95ee\uff1a\u201c\u6211\u4e0d\u4f1a\u628a\u2018\u9b54\u773c\u2019\u732e\u7ed9\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u628a\u8fd9\u53cc\u5580\u5df4\u6c99\u732e\u7ed9\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8981\u8d70\u4e86\uff0c\u8981\u8d70\u5f88\u8fdc\u5f88\u8fdc\u7684\u8def\uff0c\u8981\u8d70\u5f97\u5f88\u5feb\u5f88\u5feb\uff0c\u4f60\u9700\u8981\u4e00\u53cc\u597d\u9774\u4e8e\u4fdd\u62a4\u4f60\u7684\u811a\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u73ed\u5bdf\u5df4\u90a3\u5c31\u8981\u56de\u6765\u4e86\u3002\u201d\u52a0\u7b54\u8bf4\uff1a\u201c\u522b\u4eba\u6000\u7591\u4f60\uff0c\u53ef\u662f\u522b\u4eba\u4e0d\u6562\u52a8\u4f60\uff0c\u522b\u4eba\u90fd\u6015\u4f60\uff0c\u6015\u4f60\u6015\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u52a0\u7b54\u7528\u8863\u8896\u5728\u64e6\u6c57\uff1a\u201c\u53ef\u662f\u73ed\u5bdf\u5df4\u90a3\u4e0d\u6015\uff0c\u73ed\u5bdf\u5df4\u90a3\u8c01\u90fd\u4e0d\u6015\uff0c\u4ec0\u4e48\u4eba\u90fd\u4e0d\u6015\u3002\u73ed\u5bdf\u5df4\u90a3\u4e00\u56de\u6765\uff0c\u4f60\u5c31\u4f1a\u50cf\u9a6c\u6c99\u4e00\u6837\u6b7b\u6389\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5df2\u56e0\u6050\u60e7\u800c\u53d1\u6296\uff0c\u50cf\u4ed6\u8fd9\u6837\u7684\u6218\u58eb\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u4e00\u4e2a\u4eba\u5982\u6b64\u5bb3\u6015\uff1f \u3000\u3000\u5c0f\u65b9\u53c8\u5fcd\u4e0d\u4f4f\u8981\u95ee\u9053\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\u4ed6\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u6ce2\u5a03\u5ffd\u7136\u60ca\u9192\uff0c\u5ffd\u7136\u4ece\u6bdb\u6be1\u91cc\u94bb\u51fa\u6765\uff0c\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u4e86\u56db\u4e2a\u5b57\uff0c\u4f60\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u6b63\u60f3\u95ee\u6211\u7684\u670b\u53cb\uff0c\u73ed\u5bdf\u5df4\u90a3\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u6ce2\u5a03\u7684\u8eab\u5b50\u5ffd\u7136\u4e5f\u5f00\u59cb\u53d1\u6296\uff0c\u770b\u6765\u751a\u81f3\u6bd4\u52a0\u7b54\u66f4\u5bb3\u6015\u3002 \u3000\u3000\u5979\u5ffd\u7136\u7d27\u7d27\u62b1\u4f4f\u5c0f\u65b9\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\u8981\u6765\u4e86\uff0c\u4f60\u4e00\u5b9a\u8981\u5feb\u8d70\uff0c\u5feb\u8d70\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4e0d\u77e5\u9053\u5723\u6bcd\u5cf0\u4e0b\u7b2c\u4e00\u4f4d\u52c7\u58eb\u662f\u8c01\uff1f\u4f60\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\u4e94\u82b1\u7bad\u795e\uff1f\u201d\u6ce2\u5a03\u8fde\u58f0\u97f3\u90fd\u5df2\u5636\u54d1\uff0c\u201c\u73ed\u5bdf\u5df4\u90a3\u5c31\u662f\u4e94\u82b1\u7bad\u795e\u3002\u201d \u3000\u3000\u5728\u9177\u70ed\u5982\u70d8\u7089\u7684\u6c99\u6f20\u4e2d\uff0c\u5728\u70ed\u5f97\u4ee4\u4eba\u8fde\u6c14\u90fd\u900f\u4e0d\u51fa\u7684\u5c4b\u5b50\u91cc\uff0c\u4f60\u4f9d\u7136\u53ef\u4ee5\u770b\u5230\u8fdc\u5904\u9ad8\u5c71\u4e0a\u7684\u7691\u7691\u767d\u96ea\u3002 \u3000\u3000\u5728\u4f60\u5df2\u7ecf\u5feb\u70ed\u6b7b\u7684\u65f6\u5019\uff0c\u8fdc\u5904\u7684\u96ea\u5cf0\u4f9d\u7136\u5728\u671b\u3002 \u3000\u3000\u53ea\u6709\u5728\u8fd9\u91cc\uff0c\u4f60\u624d\u80fd\u770b\u89c1\u8fd9\u6837\u7684\u5947\u666f\uff0c\u90a3\u4e48\u5c31\u7b97\u4f60\u4e0d\u662f\u85cf\u4eba\uff0c\u4f60\u4e5f\u5e94\u8be5\u4e86\u89e3\uff0c\u85cf\u4eba\u7684\u601d\u60f3\u4e3a\u4ec0\u4e48\u4f1a\u5982\u6b64\u6d6a\u6f2b\uff1f\u5982\u6b64\u795e\u79d8\uff1f\u5982\u6b64\u7a7a\u5e7b\uff1f \u3000\u3000\u8fd9\u79cd\u601d\u60f3\u7edd\u4e0d\u662f\u4e00\u671d\u4e00\u5915\u6240\u80fd\u5f62\u6210\u7684\uff0c\u7ecf\u8fc7\u4e86\u5343\u767e\u4ee3\u6d6a\u6f2b\u3001\u795e\u79d8\u800c\u7f8e\u4e3d\u7684\u751f\u6d3b\u540e\uff0c\u5176\u4e2d\u5f53\u7136\u4f1a\u4ea7\u751f\u8bb8\u591a\u795e\u8bdd\u3002 \u3000\u3000\u5176\u4e2d\u6700\u6d6a\u6f2b\u3001\u6700\u795e\u79d8\u3001\u6700\u7f8e\u4e3d\u7684\u4e00\u79cd\u795e\u8bdd\uff0c\u5c31\u662f\u4e94\u82b1\u7bad\u795e\u3002 \u3000\u3000\u4e94\u82b1\u7bad\u795e\uff0c\u7528\u85cf\u8bed\u6765\u8bf4\uff0c\u5c31\u662f\u73ed\u5bdf\u5df4\u90a3\u3002 \u3000\u3000\u5728\u85cf\u4eba\u6700\u539f\u59cb\u53e4\u8001\u7684\u7ecf\u5178\u6587\u5b57\u4e2d\u8bb0\u8f7d\uff0c\u73ed\u5bdf\u5df4\u90a3\u7684\u7bad\uff0c\u662f\u2014\u2014 \u3000\u3000\u201c\u767e\u53d1\u767e\u4e2d\u7684\uff0c\u950b\u5229\u65e0\u6bd4\u7684\uff0c\u7bad\u7fbd\u4e0a\u6709\u75db\u82e6\u7684\u5fc3\uff0c\u7bad\u7c07\u4e0a\u6709\u76f8\u601d\u7684\u5fc3\uff0c\u76f4\u5c04\u4eba\u5fc3\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u638c\u7ba1\u7740\u4eba\u4e16\u95f4\u6700\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\uff1a\u60c5\u4e0e\u6b32\u3002 \u3000\u3000\u4ed6\u7684\u5251\u4e0a\u9970\u6ee1\u9c9c\u82b1\uff0c\u4ed6\u7684\u5f13\u5f26\u662f\u871c\u7684\u4e1d\u3002 \u3000\u3000\u4ed6\u662f\u6c38\u8fdc\u5e74\u8f7b\u7684\u3002 \u3000\u3000\u4ed6\u662f\u5929\u4e0a\u5730\u4e0b\uff0c\u8bf8\u795e\u4e2d\u6700\u7f8e\u7684\u4e00\u4e2a\u5c11\u5e74\u90ce\u3002 \u3000\u3000\u4ed6\u6709\u4e94\u679d\u9510\u7bad\uff0c\u4e00\u679d\u575a\u5f3a\u5982\u91d1\uff0c\u4e00\u679d\u6e29\u67d4\u5982\u6625\uff0c\u4e00\u6280\u5a07\u5a9a\u5982\u82b1\uff0c\u4e00\u679d\u70ed\u70c8\u5982\u706b\uff0c\u4e00\u6839\u5c16\u9510\u5982\u9525\u3002 \u3000\u3000\u4ed6\u7684\u529b\u91cf\u6ca1\u6709\u4eba\u80fd\u6297\u62d2\u3002 \u3000\u3000\u6ce2\u5a03\u548c\u52a0\u7b54\u8bf4\u7684\u8fd9\u4e2a\u73ed\u5bdf\u5df4\u90a3\u4e0d\u662f\u795e\uff0c\u662f\u4eba\uff0c\u662f\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u7684\u7b2c\u4e00\u4f4d\u6218\u58eb\u3001\u7b2c\u4e00\u4f4d\u52c7\u58eb\uff0c\u4ed6\u7684\u529b\u91cf\u5c31\u50cf\u795e\u4e00\u6837\u4e0d\u53ef\u6297\u62d2\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u5c0f\u65b9\u5c31\u7b97\u4f1a\u542c\u4ece\u4ed6\u4eec\u7684\u529d\u544a\u8981\u8d70\u65f6\uff0c\u4e5f\u5df2\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u5e10\u7bf7\u5916\u5df2\u4f20\u6765\u70ed\u70c8\u7684\u6b22\u547c\u58f0\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\u56de\u6765\u4e86\uff0c\u73ed\u5bdf\u5df4\u90a3\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7275\u7740\u4ed6\u90a3\u5339\u9ad8\u5927\u795e\u9a8f\u7684\u767d\u9a6c\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u63a5\u53d7\u4ed6\u7684\u65cf\u4eba\u4eec\u6b22\u547c\u3002 \u3000\u3000\u4ed6\u5df2\u79bb\u5f00\u4ed6\u4eec\u4e09\u5929\uff0c\u5728\u8fd9\u5757\u65e0\u60c5\u7684\u5927\u5730\u4e0a\uff0c\u8fc7\u4e86\u4e09\u5929\u7edd\u5bf9\u5b64\u5bc2\u8270\u82e6\u7684\u751f\u6d3b\uff0c\u53ef\u662f\u70c8\u65e5\u3001\u98ce\u6c99\u3001\u52b3\u7d2f\u90fd\u4e0d\u80fd\u8ba9\u4ed6\u6709\u4e1d\u6beb\u6539\u53d8\u3002 \u3000\u3000\u4ed6\u7684\u8863\u7740\u4f9d\u65e7\u9c9c\u660e\u534e\u4e3d\uff0c\u770b\u6765\u4f9d\uff1b\u65e5\u50cf\u5929\u795e\u822c\u82f1\u4fca\u5a01\u6b66\u3002 \u3000\u3000\u2014\u2014\u6ca1\u6709\u4efb\u4f55\u4eba\u3001\u4efb\u4f55\u4e8b\u80fd\u51fb\u5012\u73ed\u5bdf\u5df4\u90a3\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u5371\u9669\u56f0\u96be\u662f\u4ed6\u4e0d\u80fd\u514b\u670d\u7684\u3002 \u3000\u3000\u6c38\u8fdc\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5e10\u7bf7\u91cc\u9ed1\u6697\u800c\u5b89\u9759\uff0c\u5916\u9762\u7684\u6b22\u547c\u58f0\u5df2\u505c\u6b62\uff0c\u751a\u81f3\u8fde\u9a7c\u9a6c\u90fd\u4e0d\u518d\u5636\u545c\u3002 \u3000\u3000\u56e0\u4e3a\u73ed\u5bdf\u5df4\u90a3\u9700\u8981\u4f11\u606f\uff0c\u9700\u8981\u5b89\u9759\u3002 \u3000\u3000\u867d\u7136\u4ed6\u7ecf\u5e38\u90fd\u5728\u63a5\u53d7\u522b\u4eba\u7684\u6b22\u547c\uff0c\u4f46\u662f\u4ed6\u5374\u5b81\u613f\u4e00\u4e2a\u4eba\u9759\u9759\u5730\u8eba\u5728\u9ed1\u6697\u91cc\u3002 \u3000\u3000\u4ed6\u5929\u751f\u5c31\u662f\u4e2a\u5b64\u72ec\u7684\u4eba\uff0c\u4ed6\u559c\u7231\u5b64\u72ec\uff0c\u5c31\u597d\u50cf\u522b\u4eba\u559c\u7231\u8363\u8000\u548c\u8d22\u5bcc\u3002 \u3000\u3000\u4ed6\u9759\u9759\u5730\u5728\u9ed1\u6697\u4e2d\u8eba\u4e0b\u6765\uff0c\u73b0\u5728\u5df2\u7ecf\u6ca1\u6709\u522b\u4eba\u80fd\u770b\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u82f1\u4fca\u53d1\u5149\u7684\u8138\u5ffd\u7136\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u82cd\u767d\u75b2\u5026\u3002 \u3000\u3000\u53ef\u662f\u53ea\u8981\u6709\u4e00\u4e2a\u964c\u751f\u4eba\uff0c\u4ed6\u7684\u5149\u5f69\u7acb\u523b\u5c31\u4f1a\u50cf\u706b\u7130\u822c\u71c3\u70e7\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u7684\u65cf\u4eba\u5bf9\u4ed6\u5931\u671b\u3002 \u3000\u3000\u4ed6\u662f\u85cf\u4eba\u3002 \u3000\u3000\u867d\u7136\u4ed6\u66fe\u7ecf\u5165\u5173\u65e0\u6570\u6b21\uff0c\u5728\u4e2d\u539f\uff0c\u5728\u6dee\u9634\uff0c\u90fd\u66fe\u7ecf\u751f\u6d3b\u4e86\u5f88\u4e45\uff0c\u751a\u81f3\u8fde\u5927\u6c5f\u5357\u5317\u90fd\u66fe\u6709\u8fc7\u4ed6\u7684\u8db3\u8ff9\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u662f\u85cf\u4eba\uff0c\u7a7f\u85cf\u4eba\u4f20\u7edf\u7684\u670d\u88c5\uff0c\u5403\u85cf\u4eba\u4f20\u7edf\u7684\u996e\u98df\uff0c\u559c\u7231\u5916\u5730\u4eba\u4e0d\u80fd\u8fdb\u53e3\u7684\u201c\u8471\u6ce5\u201d\uff0c\u559d\u989c\u8272\u6f06\u9ed1\u5982\u58a8\u6c41\u7684\u9165\u6cb9\u8336\u548c\u9752\u68f5\u9152\u3002 \u3000\u3000\u4ed6\u751f\u800c\u4e3a\u85cf\u4eba\uff0c\u4ed6\u4ee5\u6b64\u4e3a\u8363\u3002 \u3000\u3000\u4ed6\u7684\u65cf\u4eba\u4e5f\u4ee5\u4ed6\u4e3a\u8363\u3002 \u3000\u3000\u4ed6\u5728\u7b49\u5c0f\u65b9\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u53d1\u751f\u7684\u4e8b\u4ed6\u5df2\u77e5\u9053\u4e86\uff0c\u9a7c\u5b50\u5df2\u7ecf\u7b80\u5355\u627c\u8981\u5730\u5411\u4ed6\u62a5\u544a\u3002 \u3000\u3000\u4ed6\u7684\u5224\u65ad\u4e5f\u8ddf\u522b\u4eba\u4e00\u6837\uff0c\u552f\u4e00\u53ef\u7591\u7684\u4eba\u5c31\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u9b54\u773c\u201d\u5c31\u5728\u4ed6\u624b\u8fb9\uff0c\u4ed6\u62d4\u51fa\u6765\uff0c\u8f7b\u629a\u5251\u950b\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u5251\uff0c\u4f60\u5c31\u662f\u90a3\u4e2a\u8981\u547d\u7684\u5c0f\u65b9\uff1f\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u770b\u89c1\u5c0f\u65b9\uff0c\u53ef\u662f\u4ed6\u77e5\u9053\u5df2\u7ecf\u6709\u4eba\u5230\u4e86\u4ed6\u7684\u5e10\u7bf7\u5916\uff0c\u6765\u7684\u4e00\u5b9a\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u7ecf\u5e74\u751f\u6d3b\u5728\u5371\u9669\u4e2d\u7684\u4eba\uff0c\u867d\u7136\u901a\u5e38\u90fd\u6709\u79cd\u91ce\u517d\u822c\u7684\u5947\u5f02\u53cd\u5e94\uff0c\u53ef\u662f\u4ed6\u8fd9\u79cd\u53cd\u5e94\u65e0\u7591\u6bd4\u522b\u4eba\u66f4\u7075\u654f\u3002 \u3000\u3000\u201c\u8fd9\u662f\u6211\u7684\u5251\u3002\u201d\u5c0f\u65b9\u5df2\u8fdb\u6765\uff0c\u201c\u6211\u5c31\u662f\u90a3\u4e2a\u8981\u547d\u7684\u5c0f\u65b9\u3002\u201d \u3000\u3000\u672c\u6765\u9759\u5367\u7740\u7684\u73ed\u5bdf\u5df4\u90a3\uff0c\u5ffd\u7136\u5df2\u6807\u67aa\u822c\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u51b7\u773c\u5728\u9ed1\u6697\u4e2d\u53d1\u5149\u3002 \u3000\u3000\u201c\u6211\u542c\u8bf4\u8fc7\u4f60\uff0c\u522b\u4eba\u8fd8\u5728\u6d41\u9f3b\u6d95\u65f6\uff0c\u4f60\u5df2\u5728\u6d41\u8840\u3002\u201d \u3000\u3000\u201c\u6d41\u7684\u901a\u5e38\u90fd\u4e0d\u662f\u6211\u7684\u8840\u3002\u201d \u3000\u3000\u201c\u80fd\u8ba9\u522b\u4eba\u6d41\u8840\u7684\u4eba\uff0c\u81ea\u5df1\u5c31\u5f97\u5148\u6d41\u8840\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u7684\u58f0\u97f3\u542c\u6765\u5c45\u7136\u5f02\u5e38\u6e29\u67d4\uff0c\u201c\u73b0\u5728\u5510\u9e9f\u7684\u8840\u5df2\u51b7\u4e86\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u8840\u4ecd\u5728\uff0c\u968f\u65f6\u968f\u5730\u90fd\u5728\u51c6\u5907\u6d41\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u7684\u58f0\u97f3\u66f4\u6e29\u67d4\u3002\u201c\u6740\u4eba\u8005\u6b7b\uff0c\u4ee5\u8840\u8fd8\u8840\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u6e29\u67d4\u5982\u6625\u6c34\uff0c\u5c0f\u65b9\u7684\u58f0\u97f3\u4e5f\u5f88\u5e73\u9759\u3002 \u3000\u3000\u201c\u53ea\u53ef\u60dc\u6ca1\u6709\u6740\u4eba\u7684\u4eba\u6709\u65f6\u4e5f\u4f1a\u6b7b\uff0c\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u82e5\u6b7b\u4e86\uff0c\u771f\u6b63\u7684\u6740\u4eba\u8005\u5c31\u5c06\u6c38\u8fdc\u900d\u9065\u6cd5\u5916\u3002\u201d \u3000\u3000\u201c\u6740\u4eba\u7684\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u8fd9\u6b21\u4e0d\u662f\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9759\u9759\u5730\u770b\u4e86\u4ed6\u5f88\u4e45\uff0c\u201c\u4f60\u8fd8\u6ca1\u6709\u9003\u8d70\uff0c\u4e5f\u4e0d\u60f3\u9003\u8d70\uff0c\u4f60\u7684\u6001\u5ea6\u5f88\u9547\u5b9a\uff0c\u547c\u5438\u4e5f\u5f88\u5747\u5300\uff0c\u7684\u786e\u4e0d\u50cf\u662f\u4e2a\u72af\u4e86\u7f6a\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4eff\u4f5b\u5728\u53f9\u606f\uff1a\u201c\u53ea\u53ef\u60dc\u5c31\u51ed\u8fd9\u4e00\u70b9\uff0c\u8fd8\u662f\u4e0d\u80fd\u8bc1\u660e\u4f60\u65e0\u7f6a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\uff1a\u201c\u8981\u600e\u6837\u624d\u80fd\u8bc1\u660e\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e03\u7ae0\u3000\u7bad\u795e\u7684\u795e\u7bad \u7b2c\u4e03\u7ae0\u3000\u7bad\u795e\u7684\u795e\u7bad \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6c89\u601d\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u4ed6\u8bf4\uff1a\u201c\u6211\u662f\u85cf\u4eba\uff0c\u85cf\u4eba\u4eec\u90fd\u5f88\u8ff7\u4fe1\uff0c\u6211\u4eec\u90fd\u76f8\u4fe1\uff0c\u6ca1\u6709\u7f6a\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u88ab\u51a4\u6740\u7684\u3002\u201d \u3000\u3000\u73b0\u5728\u5df2\u662f\u9ece\u660e\uff0c\u5e10\u7bf7\u4e2d\u5df2\u6709\u4e86\u5149\uff0c\u5df2\u7ecf\u53ef\u4ee5\u770b\u89c1\u4ed6\u7684\u4e00\u5f20\u5f13\u548c\u4e00\u58f6\u7bad\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u63d0\u8d77\u4e86\u5f13\u7bad\uff0c\u8d70\u51fa\u53bb\uff1a\u201c\u4f60\u4e5f\u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8d70\u51fa\u5e10\u7bf7\u65f6\uff0c\u624d\u53d1\u73b0\u5916\u9762\u5df2\u805a\u4e86\u5f88\u591a\u4eba\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u50cf\u77f3\u50cf\u822c\u9759\u9759\u5730\u7ad9\u7740\uff0c\u7b49\u7740\u4ed6\u4eec\u7684\u82f1\u96c4\u6765\u88c1\u51b3\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5c06\u5f13\u5f26\u6307\u7740\u4e94\u4e08\u5916\u4e00\u4e2a\u5e10\u7bf7\u3002 \u3000\u3000\u201c\u4f60\u5148\u7ad9\u5230\u90a3\u91cc\u53bb\uff0c\u6211\u518d\u5f00\u59cb\u6570\uff0c\u6570\u5230\u2018\u4e94\u2019\u5b57\uff0c\u6211\u624d\u4f1a\u51fa\u624b\uff0c\u6211\u6570\u5f97\u7edd\u4e0d\u4f1a\u592a\u5feb\uff0c\u4ee5\u4f60\u7684\u8f7b\u529f\uff0c\u7b49\u6211\u6570\u5230\u2018\u4e94\u2019\u65f6\uff0c\u4f60\u5df2\u53ef\u8d70\u51fa\u5f88\u8fdc\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u62cd\u8170\u7554\u7684\u7bad\u58f6\uff1a\u201c\u6211\u53ea\u6709\u4e94\u6839\u7bad\uff0c\u5982\u679c\u4f60\u771f\u662f\u65e0\u8f9c\u7684\uff0c\u6211\u7684\u7bad\u4e00\u5b9a\u5c04\u4e0d\u4e2d\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u201c\u767e\u53d1\u767e\u4e2d\u7684\u4e94\u82b1\u7bad\u795e\uff0c\u8981\u7528\u8fd9\u79cd\u6cd5\u5b50\u6765\u8bc1\u660e\u4e00\u4e2a\u4eba\u662f\u4e0d\u662f\u65e0\u8f9c\uff0c\u8fd9\u771f\u662f\u4e2a\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u7b11\u3002\u201c\u5982\u679c\u4f60\u8ba4\u4e3a\u8fd9\u6cd5\u5b50\u4e0d\u597d\uff0c\u53e6\u5916\u8fd8\u6709\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5c0f\u65b9\u95ee\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53e6\u4e00\u53ea\u624b\u4e0a\uff0c\u8fd8\u63d0\u7740\u5c0f\u65b9\u7684\u201c\u9b54\u773c\u201d\uff0c\u4ed6\u5ffd\u7136\u628a\u8fd9\u67c4\u5251\u63d2\u5728\u5c0f\u65b9\u9762\u524d\u7684\u6c99\u5730\u4e0a\u3002 \u3000\u3000\u201c\u7528\u8fd9\u67c4\u5251\u6740\u4e86\u6211\u3002\u201d\u4ed6\u6de1\u6de1\u4ed6\u8bf4\uff0c\u201c\u53ea\u8981\u4f60\u80fd\u6740\u4e86\u6211\uff0c\u5c31\u4e0d\u5fc5\u518d\u8bc1\u660e\u4f60\u662f\u5426\u65e0\u8f9c\u4e86\u3002\u53ea\u8981\u4f60\u80fd\u6740\u6211\uff0c\u4e0d\u7ba1\u4f60\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u90fd\u7edd\u5bf9\u6ca1\u6709\u4eba\u518d\u95ee\u3002\u201d \u3000\u3000\u51cc\u6668\uff0c\u9633\u5149\u521d\u9732\u3002 \u3000\u3000\u5251\u950b\u5728\u65ed\u65e5\u4e0b\u95ea\u7740\u5149\uff0c\u73ed\u5bdf\u5df4\u90a3\u7684\u773c\u775b\u91cc\u4e5f\u5728\u95ea\u7740\u5149\u3002 \u3000\u3000\u4ed6\u662f\u4eba\uff0c\u4e0d\u662f\u9752\u6625\u6c38\u9a7b\u7684\u795e\uff0c\u4ed6\u7684\u773c\u89d2\u5df2\u7ecf\u6709\u4e86\u76b1\u7eb9\u3002 \u3000\u3000\u4f46\u662f\u5728\u8fd9\u521d\u5347\u7684\u9633\u5149\u4e0b\uff0c\u4ed6\u770b\u6765\u8fd8\u662f\u795e\u3002 \u3000\u3000\u5c0f\u65b9\u76f8\u4fe1\u4ed6\u8bf4\u7684\u6d3b\u3002 \u3000\u3000\u4ed6\u7684\u65cf\u4eba\u548c\u5c5e\u4e0b\u8fd8\u662f\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4e0d\u7ba1\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u4eec\u90fd\u4f1a\u670d\u4ece\u7684\u3002 \u3000\u3000\u62d4\u5251\u6740\u4eba\u5e76\u4e0d\u96be\u3002 \u3000\u3000\u5c0f\u65b9\u5bf9\u81ea\u5df1\u7684\u5251\u6cd5\u4e00\u5411\u6709\u81ea\u4fe1\uff0c\u5e94\u8be5\u62d4\u5251\u7684\u65f6\u5019\uff0c\u4ed6\u4ece\u4e0d\u9000\u7f29\u9003\u907f\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u5728\u95ee\uff1a\u201c\u4e24\u79cd\u6cd5\u4e8e\uff0c\u4f60\u9009\u54ea\u4e00\u79cd\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u56de\u7b54\uff0c\u9ed8\u9ed8\u5730\u5f00\u59cb\u5f80\u524d\u8d70\uff0c\u8d70\u5230\u4e94\u4e08\u5916\u7684\u5e10\u7bf7\u524d\u505c\u4e0b\u3002 \u3000\u3000\u4ed6\u5df2\u7528\u884c\u52a8\u4ee3\u66ff\u4e86\u56de\u7b54\uff1b \u3000\u3000\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u73ed\u5bdf\u5df4\u90a3\uff1a\u201c\u4f60\u5df2\u7ecf\u53ef\u4ee5\u5f00\u59cb\u6570\u4e86\uff0c\u6700\u597d\u6570\u5f97\u5feb\u4e00\u70b9\uff0c\u6211\u6700\u6015\u4e45\u7b49\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53ea\u8bf4\u4e86\u4e00\u4e2a\u5b57\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u6240\u6709\u7684\u4eba\u90fd\u5df2\u6563\u5f00\uff0c\u5728\u4ed6\u4eec\u4e4b\u95f4\u7559\u4e0b\u5757\u7a7a\u5730\u3002 \u3000\u3000\u201c\u4e00\u3001\u4e00\u3001\u4e8c\u3001\u56db\u2026\u2026\u201d \u3000\u3000\u4e94\u82b1\u7bad\u795e\u6162\u6162\u5730\u62bd\u51fa\u4e86\u4ed6\u7684\u7b2c\u4e00\u679d\u795e\u7bad\uff0c\u9ec4\u91d1\u8272\u7684\u7bad\u6746\uff0c\u9ec4\u91d1\u8272\u7684\u7bad\u955e\u3002 \u3000\u3000\u767e\u53d1\u767e\u4e2d\u3001\u76f4\u5c04\u4eba\u5fc3\u7684\u795e\u7bad\uff0c\u6e29\u67d4\u5982\u6625\u3001\u5a07\u5a9a\u5982\u82b1\u3001\u70ed\u70c8\u5982\u706b\u3001\u5c16\u5229\u5982\u9525\u3001\u575a\u5f3a\u5982\u91d1\u3002 \u3000\u3000\u4ed6\u6570\u5f97\u5e76\u4e0d\u7094\uff0c\u53ef\u662f\u7ec8\u4e8e\u5df2\u6570\u5230\u201c\u4e94\u201d\u5b57\u3002 \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u7ad9\u5728\u90a3\u91cc\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u8f7b\u529f\uff0c\u4e0d\u7ba1\u73ed\u5bdf\u5df4\u90a3\u6570\u5f97\u591a\u5feb\uff0c\u6570\u5168\u201c\u4e94\u201d\u5b57\u65f6\uff0c\u4ed6\u81f3\u5c11\u5df2\u5728\u6570\u4e08\u5916\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fde\u4e00\u5bf8\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u201c\u4e94\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u5b57\u8bf4\u51fa\u53e3\uff0c\u6bcf\u4e2a\u4eba\u90fd\u542c\u89c1\u4e86\u4e00\u9635\u5c16\u9510\u7684\u98ce\u58f0\u54cd\u8d77\uff0c\u5c16\u9510\u5f97\u5c31\u50cf\u662f\u7fa4\u9b54\u7684\u547c\u5578\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u770b\u89c1\u73ed\u5bdf\u5df4\u90a3\u62bd\u51fa\u4ed6\u7684\u7b2c\u4e00\u6839\u7bad\uff0c\u53ef\u662f\u7bad\u58f6\u5ffd\u7136\u5df2\u7a7a\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u4e94\u679d\u7bad\u51e0\u4e4e\u662f\u5728\u540c\u4e00\u5239\u90a3\u95f4\u53d1\u51fa\u53bb\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u8fd8\u662f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u6025\u7bad\u7834\u7a7a\u7684\u98ce\u58f0\u5df2\u505c\u6b62\uff0c\u4e94\u679d\u9ec4\u91d1\u822c\u7684\u7bad\uff0c\u5e76\u6392\u63d2\u5728\u4ed6\u7684\u811a\u4e0b\u3002 \u3000\u3000\u4ed6\u6839\u672c\u6ca1\u6709\u95ea\u907f\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u662f\u56e0\u4e3a\u4ed6\u7b97\u51c6\u73ed\u5bdf\u5df4\u90a3\u53ea\u4e0d\u8fc7\u662f\u5728\u8bd5\u63a2\u4ed6\uff0c\u6240\u4ee5\u6839\u672c\u4e0d\u5fc5\u95ea\u907f\uff0c\u8fd8\u662f\u56e0\u4e3a\u4ed6\u77e5\u9053\u5982\u679c\u95ea\u907f\uff0c\u53cd\u800c\u907f\u4e0d\u5f00\u4e86\u3002 \u3000\u3000\u4e0d\u7ba1\u4ed6\u5fc3\u91cc\u662f\u600e\u4e48\u60f3\u7684\uff0c\u8fd9\u6b21\u4ed6\u53c8\u662f\u5728\u7528\u4ed6\u7684\u547d\u505a\u8d4c\u6ce8\u3002 \u3000\u3000\u8fd9\u4e00\u6ce8\u4ed6\u53c8\u62bc\u5bf9\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4e00\u4e2a\u4eba\u5982\u679c\u6ca1\u6709\u94a2\u94c1\u822c\u7684\u610f\u5fd7\u529b\uff0c\u600e\u4e48\u6562\u50cf\u4ed6\u8fd9\u6837\u4e0b\u6ce8\uff1f \u3000\u3000\u4eba\u7fa4\u4e2d\u5ffd\u7136\u7206\u8d77\u5439\u547c\uff0c\u52a0\u7b54\u5ffd\u7136\u51b2\u51fa\u6765\uff0c\u8dea\u4e0b\u53bb\u543b\u4ed6\u7684\u811a\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u90a3\u5b64\u72ec\u7684\u51b7\u773c\u91cc\u4e5f\u9732\u51fa\u7b11\u610f\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u603b\u8be5\u76f8\u4fe1\u4e86\uff0c\u4e00\u4e2a\u65e0\u8f9c\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u88ab\u6740\u7684\u3002\u53ea\u8981\u4f60\u65e0\u8f9c\uff0c\u8fd9\u4e94\u679d\u7bad\u5c31\u7edd\u5bf9\u5c04\u4e0d\u5230\u4f60\u8eab\u4e0a\uff0c\u4e0d\u7ba1\u6211\u662f\u4e0d\u662f\u4e94\u82b1\u7bad\u795e\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u8ff7\u4fe1\uff0c\u8fd9\u662f\u79cd\u777f\u667a\u7684\u8bd5\u63a2\uff0c\u53ea\u6709\u65e0\u7f6a\u7684\u4eba\uff0c\u624d\u6562\u63a5\u53d7\u8fd9\u79cd\u8003\u9a8c\u3002 \u3000\u3000\u53ea\u6709\u5c0f\u65b9\u81ea\u5df1\u77e5\u9053\uff0c\u4ed6\u5168\u8eab\u8863\u670d\u51e0\u4e4e\u90fd\u5df2\u6e7f\u900f\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u4e0d\u505c\u5730\u5728\u5192\u51b7\u6c57\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8d70\u8fc7\u53bb\u62cd\u4ed6\u7684\u80a9\uff0c\u624b\u4e0a\u7acb\u523b\u6cbe\u5230\u4ed6\u7684\u51b7\u6c57\u3002 \u3000\u3000\u201c\u539f\u6765\u4f60\u4e5f\u6709\u70b9\u5bb3\u6015\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u6709\u4e00\u70b9\u5bb3\u6015\u3002\u201d\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u6015\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7b11\u4e86\uff0c\u4ed6\u7684\u65cf\u4eba\u548c\u5c5e\u4e0b\u4e5f\u7b11\u4e86\uff0c\u5927\u5bb6\u90fd\u5df2\u6709\u5f88\u4e45\u672a\u66fe\u770b\u8fc7\u4ed6\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u7b11\u5f97\u6700\u6109\u5feb\u65f6\uff0c\u5ffd\u7136\u53c8\u542c\u89c1\u4e00\u58f0\u60e8\u547c\uff0c\u6bcf\u4e2a\u4eba\u90fd\u542c\u5f97\u51fa\u60e8\u547c\u58f0\u8d6b\u7136\u7adf\u662f\u90a3\u9a7c\u5b50\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u672c\u6765\u5806\u5f97\u5f88\u6574\u9f50\u7684\u8d27\u7269\u5305\u88f9\uff0c\u73b0\u5728\u5df2\u53d8\u5f97\u5f88\u51cc\u4e71\uff0c\u6709\u5f88\u591a\u5305\u88f9\u90fd\u5df2\u88ab\u5272\u5f00\uff0c\u9732\u51fa\u4e86\u5404\u79cd\u8d27\u7269\u548c\u73cd\u8d35\u7684\u836f\u6750\u3002 \u3000\u3000\u2014\u2014\u53ea\u6709\u8d27\u7269\u548c\u836f\u6750\uff0c\u6ca1\u6709\u9ec4\u91d1\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u6ce8\u610f\u5230\u8fd9\u4e00\u70b9\uff0c\u5272\u5f00\u8fd9\u4e9b\u5305\u88f9\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u4e5f\u4e3a\u4e86\u8981\u67e5\u660e\u8fd9\u4e00\u70b9\uff1f \u3000\u3000\u536b\u5929\u9e4f\u4ed6\u4eec\u662f\u4e0d\u662f\u5df2\u7ecf\u6765\u4e86\uff1f \u3000\u3000\u9a7c\u4e8e\u5c31\u5012\u5728\u4e00\u5305\u9e9d\u9999\u65c1\uff0c\u8863\u670d\u5df2\u88ab\u9c9c\u8840\u67d3\u7ea2\uff0c\u4ed6\u81ea\u5df1\u7684\u8840\uff0c\u4ed6\u540c\u4f34\u7684\u8840\u3002 \u3000\u3000\u81f4\u547d\u7684\u4e00\u51fb\u662f\u523a\u5728\u4ed6\u80f8\u819b\u4e0a\u7684\uff0c\u7528\u7684\u662f\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u60f3\u5230\u90a3\u65e0\u60c5\u53c8\u65e0\u540d\u7684\u5251\u5ba2\u3002 \u3000\u3000\u9a7c\u5b50\u4e0d\u4f46\u6b66\u529f\u6781\u9ad8\uff0c\u4ece\u4ed6\u8eab\u4e0a\u7684\u65e0\u6570\u4f24\u75d5\uff0c\u4e5f\u53ef\u770b\u51fa\u4ed6\u5fc5\u5b9a\u8eab\u7ecf\u767e\u6218\uff0c\u80fd\u591f\u4e00\u5251\u523a\u5165\u4ed6\u81f4\u547d\u8981\u5bb3\u7684\u4eba\uff0c\u9664\u4e86\u90a3\u65e0\u540d\u7684\u5251\u5ba2\u8fd8\u6709\u8c01\uff1f \u3000\u3000\u8fd9\u4e00\u5251\u867d\u7136\u5fc5\u5b9a\u81f4\u547d\uff0c\u9a7c\u4e8e\u5374\u8fd8\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u6709\u79cd\u4eba\u4e0d\u4f46\u751f\u547d\u529b\u6bd4\u522b\u4eba\u5f3a\uff0c\u6c42\u751f\u7684\u610f\u5fd7\u4e5f\u6bd4\u522b\u4eba\u5f3a\u3002 \u3000\u3000\u9a7c\u5b50\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4ed6\u8fd8\u5728\u5598\u606f\u3001\u6323\u624e\uff0c\u4e3a\u751f\u547d\u800c\u6323\u624e\uff0c\u4ed6\u7684\u8138\u5df2\u56e0\u75db\u82e6\u6050\u60e7\u800c\u626d\u66f2\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u773c\u775b\u91cc\u5374\u662f\u53e6\u5916\u4e00\u79cd\u8868\u60c5\uff0c\u4e00\u79cd\u6df7\u5408\u4e86\u60ca\u8bb6\u548c\u6000\u7591\u7684\u8868\u60c5\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ea\u6709\u5728\u770b\u89c1\u81ea\u5df1\u8ba4\u4e3a\u7edd\u5bf9\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u5374\u53d1\u751f\u4e86\u7684\u65f6\u5019\uff0c\u773c\u775b\u91cc\u624d\u4f1a\u6709\u79cd\u8868\u60c5\u3002 \u3000\u3000\u2014\u2014\u4ed6\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4fef\u4e0b\u8eab\uff0c\u5c06\u4e00\u5757\u85cf\u4eba\u8ba4\u4e3a\u53ef\u6cbb\u767e\u75c5\u7684\u81ed\u9165\u6cb9\u585e\u5165\u4ed6\u5634\u91cc\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u6709\u8bdd\u8981\u544a\u8bc9\u6211\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8f7b\u62cd\u4ed6\u7684\u8138\uff0c\u60f3\u632f\u8d77\u4ed6\u7684\u751f\u547d\u529b\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9a7c\u5b50\u7684\u773c\u89d2\u8df3\u52a8\uff0c\u7ec8\u4e8e\uff0c\u8bf4\u51fa\u4e86\u51e0\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u2026\u2026\u60f3\u4e0d\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4ec0\u4e48\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u53c8\u95ee\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u6740\u4eba\u7684\u7adf\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9a7c\u5b50\u7684\u547c\u5438\u5df2\u6025\u4fc3\uff0c\u5df2\u7ecf\u6ca1\u6cd5\u5b50\u518d\u53d1\u51fa\u58f0\u97f3\uff0c\u6ca1\u6cd5\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd8\u6709\u4e00\u53ea\u773c\u775b\uff0c\u6709\u65f6\u773c\u775b\u4e5f\u53ef\u4ee5\u8bf4\u8bdd\u7684\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u5728\u770b\u7740\u6700\u8fdc\u7684\u4e00\u4e2a\u5e10\u7bf7\u3002 \u3000\u3000\u4e00\u4e2a\u9876\u4e0a\u6302\u7740\u9ed1\u8272\u9e70\u7fbd\u7684\u5e10\u7bf7\u2014\u2014\u9ed1\u8272\u7684\u9e70\u7fbd\uff0c\u8c61\u5f81\u7684\u662f\u75be\u75c5\u3002\u707e\u96be\u548c\u6b7b\u4ea1\u3002 \u3000\u3000\u8fd9\u4e2a\u5e10\u7bf7\u91cc\u7684\u4eba\uff0c\u90fd\u662f\u4f24\u75c5\u5df2\u6781\u91cd\u3001\u5df2\u7ecf\u5feb\u6b7b\u4e86\u7684\u4eba\u3002 \u3000\u3000\u9664\u4e86\u8d1f\u8d23\u6551\u6cbb\u4ed6\u4eec\u7684\u90a3\u4f4d\u592b\u5b50\u5148\u751f\u5916\uff0c\u8c01\u4e5f\u4e0d\u613f\u8fdb\u5165\u90a3\u5e10\u7bf7\u3002 \u3000\u3000\u2014\u2014\u51f6\u624b\u662f\u4e0d\u662f\u5df2\u9003\u4eba\u90a3\u5e10\u7bf7\u53bb\u4e86\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u518d\u95ee\uff0c\u4e5f\u4e0d\u5fc5\u518d\u95ee\uff0c\u4ed6\u7684\u4eba\u5df2\u50cf\u4ed6\u7684\u7bad\u4e00\u822c\u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u51e0\u4e4e\u662f\u540c\u65f6\u7a9c\u5165\u8fd9\u5e10\u7bf7\u7684\uff0c\u6240\u4ee5\u540c\u65f6\u770b\u89c1\u4e86\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u8fde\u505a\u68a6\u90fd\u6ca1\u6709\u60f3\u5230\uff0c\u4f1a\u5728\u8fd9\u4e2a\u5e10\u7bf7\u91cc\uff0c\u770b\u89c1\u8fd9\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u4e2a\u770b\u89c1\u7684\u4eba\u7adf\u662f\u6ce2\u5a03\uff0c\u672c\u6765\u5e94\u8be5\u5728\u4ed6\u7684\u5e10\u7bf7\u91cc\u7b49\u5019\u4ed6\u7684\u6ce2\u5a03\u3002 \u3000\u3000\u4ed6\u7b2c\u4e8c\u4e2a\u770b\u89c1\u7684\u8d6b\u7136\u7adf\u662f\u535c\u9e70\uff01 \u3000\u3000\u535c\u9e70\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4f9d\u7136\u51b7\u9177\u9547\u5b9a\uff0c\u4f9d\u7136\u767d\u8863\u5982\u96ea\u3002 \u3000\u3000\u6ce2\u5a03\u8737\u4f0f\u5728\u4ed6\u9762\u524d\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u60ca\u9a87\u4e0e\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u4e0d\u8be5\u5728\u8fd9\u5e10\u7bf7\u91cc\u7684\uff0c\u53ef\u662f\u4ed6\u4eec\u90fd\u5728\u3002 \u3000\u3000\u51f6\u624b\u5df2\u9003\u5165\u8fd9\u5e10\u7bf7\uff0c\u5e10\u7bf7\u91cc\u522b\u65e0\u9000\u8def\uff0c\u4ed6\u4eec\u4e4b\u95f4\uff0c\u5fc5\u5b9a\u6709\u4e2a\u4eba\u662f\u51f6\u624b\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u4e4b\u95f4\uff0c\u8c01\u4f1a\u6740\u4eba\uff1f \u3000\u3000\u5c0f\u65b9\u51b7\u51b7\u5730\u770b\u7740\u535c\u9e70\uff0c\u6c89\u91cd\u53f9\u606f\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\u662f\u4f60\uff0c\u6211\u4e00\u76f4\u90fd\u8ba4\u4e3a\u4f60\u771f\u7684\u4ece\u4e0d\u6740\u4eba\u3002\u201d \u3000\u3000\u535c\u9e70\u7684\u8138\u4e0a\u5168\u65e0\u8868\u60c5\uff1a\u201c\u4e16\u4e0a\u672c\u6765\u5c31\u6709\u5f88\u591a\u4ee4\u4eba\u60f3\u4e0d\u5230\u7684\u4e8b\uff0c\u91d1\u5b50\u53ef\u4ee5\u8ba9\u4eba\u505a\u51fa\u5f88\u591a\u5f88\u591a\u8fde\u4ed6\u81ea\u5df1\u90fd\u60f3\u4e0d\u5230\u7684\u4e8b\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e5f\u5728\u627e\u90a3\u6279\u91d1\u5b50\uff0c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u6ce2\u5a03\u5df2\u6295\u5165\u4ed6\u7684\u6000\u62b1\uff0c\u773c\u775b\u91cc\u5df2\u6709\u6cea\u6c34\u6d8c\u51fa\uff1a\u201c\u5e26\u6211\u8d70\uff0c\u6c42\u6c42\u4f60\u5e26\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u5c0f\u65b9\u8f7b\u629a\u5979\u7684\u67d4\u53d1\uff1a\u201c\u6211\u4e00\u5b9a\u4f1a\u5e26\u4f60\u8d70\uff0c\u4f60\u672c\u5c31\u4e0d\u8be5\u6765\u7684\u3002\u201d \u3000\u3000\u53ef\u662f\u5979\u5df2\u7ecf\u6765\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u6765\u7684\uff1f\u201d \u3000\u3000\u6ce2\u5a03\u542b\u7740\u6cea\u6447\u5934\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u771f\u7684\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u60f3\u8d76\u5feb\u8d70\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u5f00\u53e3\u3002 \u3000\u3000\u201c\u5979\u4e0d\u80fd\u8d70\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u4e0d\u518d\u6e29\u67d4\uff0c\u201c\u8c01\u4e5f\u4e0d\u80fd\u5e26\u5979\u8d70\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u8981\u522b\u4eba\u6d41\u8840\u7684\u4eba\uff0c\u81ea\u5df1\u4e5f\u5f97\u6d41\u8840\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u53c8\u5c06\u4ed6\u81ea\u5df1\u8bf4\u8fc7\u7684\u8bdd\u91cd\u590d\u4e00\u904d\uff0c\u201c\u6740\u4eba\u8005\u6b7b\uff0c\u4ee5\u8840\u8fd8\u8840\u3002\u201d \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u7684\u771f\u7406\uff0c\u65e0\u8bba\u5728\u4e2d\u539f\u3001\u5728\u6c5f\u5357\u3001\u5728\u6c99\u6f20\u90fd\u540c\u6837\u9002\u7528\u3002 \u3000\u3000\u5c0f\u65b9\u7d27\u7d27\u63e1\u4f4f\u6ce2\u5a03\u7684\u624b\uff1a\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6740\u4eba\u7684\u4e0d\u662f\u5979\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f60\u770b\u5f97\u51fa\uff1f\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u6539\u53d8\u8bdd\u9898\uff1a\u201c\u6211\u4eec\u8fd9\u4e9b\u4eba\uff0c\u8fd9\u4e9b\u8d27\u7269\uff0c\u90fd\u662f\u5c5e\u4e8e\u4e00\u4e2a\u5546\u5bb6\u7684\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u4e2a\u5546\u5bb6\uff1f\u201d \u3000\u3000\u201c\u9e70\u8bb0\u3002\u201d \u3000\u3000\u201c\u9e70\u8bb0\uff1f\u201d\u5c0f\u65b9\u7684\u624b\u5df2\u53d1\u51b7\uff0c\u201c\u98de\u9e70\u7684\u9e70\uff1f\u201d \u3000\u3000\u98de\u9e70\u7684\u9e70\uff0c\u5c31\u662f\u535c\u9e70\u7684\u9e70\uff0c\u4ed6\u5403\u60ca\u5730\u770b\u7740\u535c\u9e70\uff1a\u201c\u4f60\u5c31\u662f\u4ed6\u4eec\u7684\u4e1c\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5c31\u662f\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u4eec\u6536\u5bb9\u4f60\uff0c\u5c31\u56e0\u4e3a\u4ed6\u662f\u6211\u4eec\u7684\u4e1c\u4e3b\uff1b\u6211\u4eec\u4fe1\u4efb\u4f60\uff0c\u4e5f\u662f\u56e0\u4e3a\u4ed6\uff0c\u5426\u5219\uff0c\u4f60\u521a\u624d\u5f88\u53ef\u80fd\u5df2\u6b7b\u5728\u6211\u7684\u7bad\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5168\u8eab\u90fd\u5df2\u51b0\u51b7\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u8981\u641c\u7d22\u90a3\u6279\u9ec4\u91d1\uff0c\u4e5f\u4e0d\u4f1a\u641c\u5230\u4ed6\u81ea\u5df1\u7684\u961f\u4f0d\u4e2d\u6765\uff0c\u5c31\u7b97\u4ed6\u8981\u641c\u67e5\u8fd9\u6279\u8d27\uff0c\u4e5f\u7528\u4e0d\u7740\u6740\u4eba\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u5e94\u8be5\u77e5\u9053\u6740\u4eba\u7684\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u6ce2\u5a03\u7684\u624b\u6bd4\u5c0f\u65b9\u66f4\u51b7\uff0c\u6cea\u6bd4\u624b\u66f4\u51b7\u3002 \u3000\u3000\u5979\u7d27\u7d27\u62e5\u62b1\u4f4f\u5c0f\u65b9\uff0c\u5979\u5168\u8eab\u90fd\u5728\u98a4\u6296\uff0c\u50cf\u5979\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u600e\u4e48\u4f1a\u662f\u4e2a\u51b7\u8840\u7684\u51f6\u624b\uff1f \u3000\u3000\u5c0f\u65b9\u4e0d\u4fe1\u3002 \u3000\u3000\u5c0f\u65b9\u5b81\u6b7b\u4e5f\u4e0d\u613f\u76f8\u4fe1\u3002 \u3000\u3000\u201c\u6211\u53ea\u77e5\u9053\u6740\u4eba\u7684\u7edd\u4e0d\u662f\u5979\u3002\u201d\u4ed6\u628a\u5979\u62b1\u5f97\u66f4\u7d27\uff1a\u201c\u8c01\u4e5f\u6ca1\u6709\u770b\u89c1\u6740\u4eba\u7684\u662f\u8c01\u3002\u201d \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u8981\u4eb2\u773c\u770b\u89c1\u624d\u76f8\u4fe1\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u95ee\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5c31\u7b97\u4ed6\u771f\u7684\u4eb2\u773c\u770b\u89c1\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u5982\u679c\u5c0f\u65b9\u662f\u4e2a\u5f88\u7406\u667a\u3001\u5f88\u6709\u5206\u6790\u529b\u7684\u4eba\uff0c\u73b0\u5728\u5df2\u7ecf\u5e94\u8be5\u660e\u767d\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u5df2\u7ecf\u5f88\u660e\u663e\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4ed6\u4eec\u65e9\u5df2\u77e5\u9053\u535c\u9e70\u662f\u8fd9\u961f\u5546\u65c5\u7684\u4e1c\u4e3b\uff0c\u4e00\u76f4\u90fd\u5728\u6000\u7591\u535c\u9e70\u7528\u8fd9\u961f\u5546\u65c5\u505a\u63a9\u62a4\uff0c\u6765\u8fd0\u9001\u90a3\u4e09\u5341\u4e07\u4e24\u5931\u52ab\u7684\u9ec4\u91d1\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u4e0d\u6562\u52a8\u8fd9\u4e2a\u961f\u4f0d\u3002 \u3000\u3000\u535c\u9e70\u7684\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u6c5f\u6e56\u4e2d\u4eba\u90fd\u77e5\u9053\u4ed6\u4ece\u672a\u8d25\u8fc7\u3002 \u3000\u3000\u201c\u4e94\u82b1\u7bad\u795e\u201d\u73ed\u5bdf\u5df4\u90a3\u540d\u9707\u5173\u5916\uff0c\u662f\u85cf\u4eba\u4e2d\u7684\u7b2c\u4e00\u4f4d\u52c7\u58eb\u3001\u7b2c\u4e00\u9ad8\u624b\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e0d\u4f46\u5bf9\u8fd9\u4e24\u4e2a\u4eba\u5fc3\u5b58\u754f\u60e7\uff0c\u5bf9\u8fd9\u961f\u4f0d\u4e2d\u7684\u6bcf\u4e2a\u4eba\u90fd\u4e0d\u80fd\u4e0d\u63d0\u9632\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u961f\u4f0d\u4e2d\u6bcf\u4e2a\u4eba\u90fd\u53ef\u80fd\u662f\u732b\u76d7\uff0c\u5982\u679c\u771f\u7684\u706b\u5e76\u8d77\u6765\uff0c\u4ed6\u4eec\u7edd\u5bf9\u6ca1\u6709\u81f4\u80dc\u7684\u628a\u63e1\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u6709\u5728\u6697\u4e2d\u6765\u4fa6\u5bdf\uff0c\u9ec4\u91d1\u662f\u4e0d\u662f\u5728\u8fd9\u961f\u4f0d\u7684\u8d27\u7269\u5305\u88f9\u91cc\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u60f3\u5229\u7528\u5c0f\u65b9\u6765\u505a\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u8fd9\u4e2a\u8981\u547d\u7684\u5c0f\u65b9\u504f\u504f\u662f\u4e2a\u4e0d\u8981\u547d\u7684\u4eba\uff0c\u4ed6\u4eec\u53ea\u6709\u60f3\u522b\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u8981\u67e5\u51fa\u9ec4\u91d1\u662f\u5426\u5728\u8fd9\u4e9b\u8d27\u7269\u5305\u88f9\u91cc\uff0c\u4e00\u5b9a\u8981\u5148\u6d3e\u4e2a\u4eba\u6df7\u5165\u8fd9\u961f\u4f0d\u4e2d\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u8981\u662f\u4e2a\u7edd\u5bf9\u4e0d\u5f15\u4eba\u6ce8\u610f\u3001\u7edd\u4e0d\u4f1a\u88ab\u6000\u7591\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u8981\u50cf\u5c3a\u8816\u866b\u822c\u5584\u4e8e\u4f2a\u88c5\uff0c\u4e00\u5b9a\u8981\u6709\u732b\u4e00\u822c\u7075\u5de7\u8f7b\u5de7\u7684\u52a8\u4f5c\u3001\u86c7\u4e00\u822c\u51c6\u786e\u6bd2\u8fa3\u7684\u653b\u51fb\u3001\u5de8\u8c61\u822c\u7684\u9547\u5b9a\u6c89\u7740\uff0c\u8fd8\u8981\u6709\u871c\u4e00\u822c\u7684\u751c\u7f8e\u3001\u6c34\u4e00\u822c\u7684\u6e29\u67d4\uff0c\u624d\u80fd\u5148\u5f81\u670d\u5c0f\u65b9\u3002 \u3000\u3000\u56e0\u4e3a\u5c0f\u65b9\u662f\u552f\u4e00\u80fd\u8ba9\u8fd9\u4e2a\u4eba\u6df7\u5165\u8fd9\u961f\u4f0d\u6765\u7684\u6865\u6881\u3002 \u3000\u3000\u4ed6\u4eec\u5c45\u7136\u627e\u5230\u4e86\u4e00\u4e2a\u8fd9\u6837\u7684\u4eba\u3002 \u3000\u3000\u6ce2\u5a03\u3002 \u3000\u3000\u5982\u679c\u5c0f\u65b9\u8fd8\u6709\u4e00\u70b9\u7406\u667a\uff0c\u73b0\u5728\u5c31\u5e94\u8be5\u770b\u51fa\u8fd9\u4ef6\u4e8b\u7684\u771f\u76f8\u3002 \u3000\u3000\u53ef\u60dc\u5c0f\u65b9\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u6ca1\u6709\u7406\u667a\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u7684\u7406\u667a\u65f6\u5e38\u90fd\u4f1a\u88ab\u60c5\u611f\u6df9\u6ca1\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u60f3\u4e0d\u5230\u8fd9\u4e9b\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u6839\u672c\u62d2\u7edd\u53bb\u60f3\u3002 \u3000\u3000\u4ed6\u6839\u672c\u62d2\u7edd\u627f\u8ba4\u6ce2\u5a03\u662f\u51f6\u624b\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5f53\u7136\u4e5f\u770b\u51fa\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u201c\u6ca1\u6709\u4eba\u770b\u89c1\u5979\u6740\u4eba\uff0c\u6ca1\u6709\u4eba\u80fd\u8bc1\u660e\u5979\u6740\u8fc7\u4eba\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff0c\u201c\u53ef\u662f\u4f60\u4e5f\u540c\u6837\u4e0d\u80fd\u8bc1\u660e\u5979\u662f\u65e0\u8f9c\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u660e\u767d\u4ed6\u7684\u610f\u601d\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u53c8\u60f3\u7528\u521a\u624d\u90a3\u6cd5\u5b50\u8bc1\u660e\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4e94\u82b1\u795e\u7684\u7bad\uff0c\u7edd\u4e0d\u4f1a\u4f24\u53ca\u65e0\u8f9c\u7684\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u51b7\u7b11\u3002 \u3000\u3000\u201c\u53ea\u53ef\u60dc\u4f60\u5e76\u4e0d\u662f\u771f\u7684\u4e94\u82b1\u7bad\u795e\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u4eba\uff0c\u4f60\u5fc3\u91cc\u5df2\u8ba4\u5b9a\u4e86\u5979\u6709\u7f6a\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u662f\u4e0d\u662f\u8fd8\u6709\u4ec0\u4e48\u66f4\u597d\u7684\u6cd5\u4e8e\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u66f4\u597d\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u4e16\u4e0a\u5df2\u6ca1\u6709\u4efb\u4f55\u4eba\uff0c\u80fd\u60f3\u51fa\u4efb\u4f55\u65b9\u6cd5\u6765\u8bc1\u660e\u5979\u662f\u65e0\u8f9c\u7684\u3002 \u3000\u3000\u6ce2\u5a03\u5ffd\u7136\u6323\u8131\u5c0f\u65b9\u7684\u6000\u62b1\uff0c\u6d41\u7740\u6cea\u9053\uff1a\u201c\u4f60\u867d\u7136\u8bf4\u8fc7\uff0c\u53ea\u8981\u4f60\u6d3b\u7740\uff0c\u5c31\u4e0d\u8ba9\u522b\u4eba\u6b3a\u8d1f\u6211\uff0c\u53ef\u662f\u6211\u65e9\u5c31\u77e5\u9053\u8fd9\u662f\u505a\u4e0d\u5230\u7684\uff0c\u6bcf\u4ef6\u4e8b\u90fd\u4f1a\u6539\u53d8\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u6539\u53d8\u3002\u201d \u3000\u3000\u5979\u7684\u6cea\u73e0\u6676\u83b9\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u4f60\u5df2\u7ecf\u53ef\u4ee5\u5fd8\u8bb0\u8fd9\u4e9b\u8bdd\uff0c\u5c31\u8ba9\u4ed6\u4eec\u6740\u4e86\u6211\uff0c\u5c31\u8ba9\u6211\u6b7b\u5427\uff01\u201d \u3000\u3000\u5979\u8fd8\u662f\u90a3\u67d4\u5f31\uff0c\u8fd9\u4e48\u6e29\u987a\uff0c\u5979\u8fd8\u662f\u5b8c\u5168\u4f9d\u8d56\u7740\u5c0f\u65b9\u7684\u3002 \u3000\u3000\u5979\u5df2\u5c06\u5979\u7684\u751f\u547d\u3001\u5979\u7684\u6574\u4e2a\u4eba\u90fd\u4ea4\u7ed9\u4e86\u5c0f\u65b9\uff0c\u5979\u5b81\u613f\u6b7b\uff0c\u53ea\u56e0\u4e3a\u5979\u4e0d\u613f\u8fde\u7d2f\u5c0f\u65b9\u3002\u8c01\u4e5f\u6ca1\u6709\u770b\u89c1\u5979\u6740\u4eba\uff0c\u53ef\u662f\u8fd9\u4e00\u70b9\u6bcf\u4e2a\u4eba\u90fd\u770b\u5f97\u6e05\u695a\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8ba9\u5979\u8d70\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5f88\u60ca\u8bb6\u9053\uff1a\u201c\u5c31\u8fd9\u4e48\u6837\u653e\u5979\u8d70\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u8fd9\u4e48\u6837\u653e\u5979\u8d70\u3002\u201d\u535c\u9e70\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u8fd8\u5f97\u7ed9\u5979\u4e00\u888b\u6c34\u3001\u4e00\u888b\u7cae\u98df\u3001\u4e00\u5339\u9a6c\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6700\u5feb\u7684\u4e00\u5339\u9a6c\uff0c\u6211\u8981\u8ba9\u5979\u8d70\u5f97\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u5bf9\u535c\u9e70\u7684\u670d\u4ece\uff0c\u5c31\u597d\u50cf\u522b\u4eba\u5bf9\u4ed6\u4e00\u6837\u3002\u5c0f\u65b9\u4e5f\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u535c\u9e70\u505a\u7684\u4e8b\uff0c\u6bcf\u6b21\u90fd\u8ba9\u4ed6\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u4ed6\u9ed8\u9ed8\u5730\u62c9\u7740\u6ce2\u5a03\u7684\u624b\uff0c\u8f6c\u8fc7\u8eab\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u53c8\u8bf4\uff1a\u201c\u5979\u8d70\uff0c\u4f60\u7559\u4e0b\u3002\u201d \u3000\u3000\u201c\u6211\u7559\u4e0b\uff1f\u201d\u5c0f\u65b9\u56de\u5934\uff1a\u201c\u4f60\u8981\u6211\u7559\u4e0b\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u653e\u5979\u8d70\uff0c\u4f60\u5c31\u5f97\u7559\u4e0b\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u535c\u9e70\u7684\u56de\u7b54\u7b80\u77ed\u800c\u575a\u51b3\uff0c\u8fd9\u5df2\u662f\u4ed6\u6700\u540e\u7684\u51b3\u5b9a\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u6539\u53d8\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u5c0f\u65b9\u660e\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u653e\u5f00\u4e86\u6ce2\u5a03\u7684\u624b\u3002 \u3000\u3000\u201c\u53ea\u8981\u6211\u4e0d\u6b7b\uff0c\u6211\u4e00\u5b9a\u4f1a\u53bb\u627e\u4f60\uff0c\u4e00\u5b9a\u80fd\u627e\u5230\u4f60\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u5bf9\u6ce2\u5a03\u6700\u540e\u8bf4\u7684\u8bdd\uff0c\u9664\u6b64\u4e4b\u5916\u4ed6\u8fd8\u80fd\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u6ce2\u5a03\u9ed8\u9ed8\u5730\u8d70\u4e86\u3002 \u3000\u3000\u5979\u4e5f\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002\u5c0f\u65b9\u76ee\u9001\u5979\u8d70\u51fa\u53bb\uff0c\u770b\u7740\u5979\u67d4\u5f31\u7ea4\u79c0\u7684\u80cc\u5f71\u3002 \u3000\u3000\u4ed6\u5e0c\u671b\u5979\u518d\u56de\u5934\u770b\u4ed6\uff0c\u53c8\u6015\u5979\u56de\u5934\u3002 \u3000\u3000\u5982\u679c\u5979\u518d\u56de\u5934\uff0c\u4ed6\u8bf4\u4e0d\u5b9a\u4f1a\u5c31\u4e0d\u987e\u4e00\u5207\uff0c\u8ddf\u7740\u5979\u95ef\u51fa\u53bb\u3002 \u3000\u3000\u5979\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e5f\u8d70\u4e86\uff0c\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u5bf9\u5c0f\u65b9\u8bf4\u4e86\u53e5\u5f88\u6709\u6df1\u610f\u7684\u8bdd\u3002\u201c\u5982\u679c\u6211\u662f\u4f60\uff0c\u6211\u4e5f\u4f1a\u50cf\u4f60\u4e00\u6837\u8fd9\u4e48\u505a\u7684\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u4e2d\u7edd\u6ca1\u6709\u8ba5\u7b11\u4e4b\u610f\uff0c\u201c\u50cf\u5979\u8fd9\u6837\u7684\u5973\u4eba\u5b9e\u5728\u4e0d\u591a\u3002\u201d \u3000\u3000\u5feb\u8d70\u5230\u5e10\u7bf7\u5916\u65f6\uff0c\u4ed6\u53c8\u56de\u8fc7\u5934\uff1a\u201c\u53ef\u662f\u5982\u679c\u6211\u662f\u4f60\uff0c\u4ee5\u540e\u6211\u7edd\u4e0d\u4f1a\u518d\u89c1\u5979\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7d27\u63e1\u53cc\u62f3\uff0c\u53c8\u6162\u6162\u677e\u5f00\uff0c\u7136\u540e\u518d\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u535c\u9e70\u3002 \u3000\u3000\u4ed6\u60f3\u95ee\u535c\u9e70\uff1a\u201c\u4f60\u65e2\u7136\u80af\u653e\u5979\u8d70\uff0c\u4e3a\u4ec0\u4e48\u8981\u6211\u7559\u4e0b\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u95ee\u51fa\u6765\u3002 \u3000\u3000\u6ce2\u5a03\u548c\u73ed\u5bdf\u5df4\u90a3\u4e00\u8d70\u51fa\u53bb\uff0c\u535c\u9e70\u7684\u6837\u5b50\u5c31\u53d8\u4e86\u3002\u5c0f\u65b9\u9762\u5bf9\u4ed6\u65f6\uff0c\u4ed6\u5df2\u7ecf\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u7528\u517d\u76ae\u5806\u6210\u7684\u8f6f\u57ab\u4e0a\u3002\u5c0f\u65b9\u4ece\u672a\u89c1\u8fc7\u4ed6\u5982\u6b64\u75b2\u5026\u8870\u5f31\u3002 \u3000\u3000\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\u5168\u65e0\u8840\u8272\uff0c\u53ef\u662f\u4ed6\u96ea\u767d\u7684\u8863\u670d\u5df2\u6709\u9c9c\u8840\u6e17\u51fa\u3002\u8840\u8ff9\u5c31\u5728\u4ed6\u80f8\u819b\u4e0a\uff0c\u8ddd\u79bb\u4ed6\u7684\u5fc3\u53e3\u5f88\u8fd1\u3002\u201c\u4f60\u53d7\u4e86\u4f24\uff1f\u201d\u5c0f\u65b9\u5931\u58f0\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u53d7\u4f24\uff1f\u201d \u3000\u3000\u535c\u9e70\u82e6\u7b11\uff1a\u201c\u53ea\u8981\u662f\u4eba\uff0c\u5c31\u4f1a\u53d7\u4f24\uff0c\u5229\u5251\u523a\u4eba\u80f8\u819b\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u53d7\u4f24\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u66f4\u5403\u60ca\u3002 \u3000\u3000\u201c\u6c5f\u6e56\u4e2d\u4eba\u90fd\u8bf4\u4f60\u662f\u4ece\u6765\u4e0d\u8d25\u7684\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u8eab\u7ecf\u6570\u5341\u6218\uff0c\u4ece\u672a\u8d25\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u6bcf\u4ef6\u4e8b\u90fd\u6709\u7b2c\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u523a\u4f24\u4e86\u4f60\uff1f\u201d \u3000\u3000\u535c\u9e70\u8fd8\u6ca1\u6709\u56de\u7b54\uff0c\u5c0f\u65b9\u5df2\u7ecf\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\uff0c\u5982\u679c\u6709\u4eba\u80fd\u523a\u4f24\u535c\u9e70\uff0c\u4e00\u5b9a\u5c31\u662f\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u65e0\u540d\u7684\u5251\u5ba2\uff0c\u65e0\u60c5\u7684\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u8ddf\u4ed6\u4ea4\u8fc7\u624b\u4e86\uff1f\u201d \u3000\u3000\u535c\u9e70\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u4ed6\u8bf4\uff1a\u201c\u5f53\u4ee3\u7684\u4e03\u5927\u5251\u5ba2\uff0c\u6211\u90fd\u89c1\u8fc7\uff0c\u867d\u7136\u6211\u5e76\u6ca1\u6709\u8ddf\u4ed6\u4ea4\u624b\uff0c\u4f46\u662f\u4ed6\u4eec\u7684\u5251\u6cd5\u6211\u90fd\u89c1\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u5728\u53f9\u606f\uff1a\u201c\u4ed6\u4eec\u4e4b\u4e2d\uff0c\u6709\u7684\u4eba\u5df2\u8001\uff0c\u6709\u7684\u4eba\u751f\u547d\u592a\u5962\u534e\uff0c\u6709\u7684\u4eba\u5251\u6cd5\u5927\u62d8\u8c28\uff0c\u5f53\u5e74\u88ab\u6c5f\u6e56\u516c\u8ba4\u7684\u5f53\u4ee3\u4e03\u5927\u5251\u5ba2\uff0c\u5982\u4eca\u90fd\u5df2\u8fc7\u53bb\uff0c\u6240\u4ee5\u6211\u6ca1\u6709\u8ddf\u4ed6\u4eec\u4ea4\u624b\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u6211\u4e00\u5b9a\u80fd\u80dc\u8fc7\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u56de\u7b54\uff0c\u6240\u4ee5\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u4ed6\u5462\uff1f\u201d \u3000\u3000\u535c\u9e70\u5f53\u7136\u4e5f\u77e5\u9053\u5c0f\u65b9\u8bf4\u7684\u201c\u4ed6\u201d\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u201c\u6211\u5df2\u7ecf\u8ddf\u4ed6\u4ea4\u8fc7\u624b\u3002\u201d\u535c\u9e70\u7ec8\u4e8e\u56de\u7b54\uff0c\u201c\u6211\u6562\u4fdd\u8bc1\uff0c\u4e03\u5927\u5251\u5ba2\u4e2d\uff0c\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u63a5\u5f97\u4f4f\u4ed6\u8fd9\u4e00\u5251\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e00\u5251\u3002\u201d\u65e0\u7591\u5c31\u662f\u523a\u4f24\u535c\u9e70\u7684\u8fd9\u4e00\u5251\u2026\u2026 \u3000\u3000\u201c\u6211\u4ece\u672a\u89c1\u8fc7\u90a3\u6837\u7684\u5251\u6cd5\uff0c\u6211\u751a\u81f3\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\u5230\u8fc7\u3002\u201d\u535c\u9e70\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u53ea\u80fd\u7528\u516d\u4e2a\u5b57\u6765\u5f62\u5bb9\u8fd9\u4e00\u5251\u3002\u201d \u3000\u3000\u201c\u54ea\u516d\u4e2a\u5b57\uff1f\u201d \u3000\u3000\u201c\u5fc5\u6740\uff01\u5fc5\u80dc\uff01\u5fc5\u6b7b\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d\u5c0f\u65b9\u4eff\u4f5b\u5728\u5b89\u6170\u4ed6\uff0c\u53c8\u4eff\u4f5b\u5728\u5b89\u6170\u81ea\u5df1\uff0c\u201c\u6211\u770b\u5f97\u51fa\u4f60\u7edd\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u600e\u7684\u770b\u5f97\u51fa\u6211\u4e0d\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u4e2d\u5e26\u8ba5\u4fcf\uff1a\u201c\u6211\u7559\u4e0b\u4f60\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u4e3a\u4e86\u8981\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\u6b7b\uff0c\u56e0\u4e3a\u6211\u4e5f\u66fe\u7559\u5728\u4f60\u8eab\u8fb9\uff0c\u7b49\u7740\u4f60\u6b7b\u3002\u201d \u3000\u3000\u8ba5\u6d88\u6709\u65f6\u4e5f\u662f\u79cd\u60b2\u4f24\uff0c\u60b2\u4f24\u6709\u65f6\u5f80\u5f80\u4f1a\u7528\u8ba5\u6d88\u7684\u65b9\u5f0f\u8868\u8fbe\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u4e86\u89e3\u3002 \u3000\u3000\u9664\u4e86\u5bf9\u81ea\u5df1\u7684\u611f\u60c5\u5916\uff0c\u5bf9\u522b\u7684\u4e8b\u4ed6\u901a\u5e38\u90fd\u80fd\u4e86\u89e3\u3002 \u3000\u3000\u4ed6\u6162\u6162\u5730\u5750\u4e0b\u6765\uff0c\u5750\u5728\u535c\u9e70\u8eab\u65c1\u3002\u201c\u6211\u7b49\u4f60\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u4e0d\u662f\u7b49\u4f60\u6b7b\uff0c\u662f\u7b49\u4f60\u7ad9\u8d77\u6765\u3002\u201d \u3000\u3000\u70c8\u65e5\u53c8\u5347\u8d77\uff0c\u5e10\u7bf7\u91cc\u5374\u663e\u5f97\u5206\u5916\u9634\u6697\u5bd2\u51b7\u3002 \u3000\u3000\u535c\u9e70\u5df2\u95ed\u7740\u773c\u775b\u8eba\u4e86\u8bb8\u4e45\uff0c\u4e5f\u4e0d\u77e5\u662f\u4e0d\u662f\u7761\u7740\u4e86\uff0c\u8fd9\u65f6\u5ffd\u7136\u53c8\u5f20\u5f00\u773c\uff0c\u770b\u7740\u5c0f\u65b9\uff1a\u201c\u6709\u4e24\u4ef6\u4e8b\uff0c\u4e00\u5b9a\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u90a3\u4e2a\u65e0\u540d\u7684\u5251\u5ba2\u5e76\u4e0d\u662f\u771f\u7684\u6ca1\u6709\u540d\u5b57\uff0c\u4ed6\u59d3\u72ec\u5b64\uff0c\u53eb\u72ec\u5b64\u75f4\uff0c\u4e0d\u662f\u75f4\u4e8e\u60c5\uff0c\u662f\u75f4\u4e8e\u5251\u3002\u201d \u3000\u3000\u535c\u9e70\u53f9\u606f\u7740\uff1a\u201c\u6240\u4ee5\u4f60\u5343\u4e07\u4e0d\u80fd\u4e0e\u4ed6\u4ea4\u624b\u3002\u75f4\u4e8e\u60c5\u7684\u4eba\uff0c\u4e00\u5b9a\u4f1a\u6b7b\u5728\u75f4\u4e8e\u5251\u7684\u4eba\u4e4b\u5251\u4e0b\uff0c\u8fd9\u4e00\u70b9\u4f60\u7edd\u5bf9\u4e0d\u80fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53ea\u95ee\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\u5462\uff1f\u201d \u3000\u3000\u535c\u9e70\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e2a\u6d6a\u5b50\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6709\u7684\u6d6a\u5b50\u591a\u91d1\uff0c\u6709\u7684\u6d6a\u5b50\u591a\u60c5\uff0c\u6709\u7684\u6d6a\u5b50\u7231\u7b11\uff0c\u6709\u7684\u6d6a\u5b50\u7231\u54ed\uff0c\u4e0d\u8fc7\u6240\u6709\u7684\u6d6a\u5b50\u90fd\u6709\u4e00\u70b9\u76f8\u540c\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u7a7a\u865a\u3002\u201d\u535c\u9e70\u5f3a\u8c03\uff1a\u201c\u5b64\u72ec\u3001\u5bc2\u5bde\u3001\u7a7a\u865a\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u6d6a\u5b50\u4eec\u5982\u679c\u627e\u5230\u4e00\u4e2a\u53ef\u4ee5\u8ba9\u81ea\u5df1\u89c9\u5f97\u4e0d\u518d\u5b64\u72ec\u7684\u4eba\uff0c\u5c31\u4f1a\u50cf\u4e00\u4e2a\u6eba\u6c34\u8005\u6293\u5230\u4e00\u6839\u6728\u5934\uff0c\u6b7b\u4e5f\u4e0d\u80af\u653e\u624b\u4e86\u3002\u81f3\u4e8e\u8fd9\u6839\u6728\u5934\u662f\u4e0d\u662f\u80fd\u8f7d\u4ed6\u5230\u5cb8\uff0c\u4ed6\u5e76\u4e0d\u5728\u4e4e\uff0c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u5df2\u7ecf\u6709\u4e86\u79cd\u5b89\u5168\u7684\u611f\u89c9\uff0c\u5bf9\u6d6a\u5b50\u4eec\u6765\u8bf4\uff0c\u8fd9\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u6b63\u662f\u5c0f\u65b9\u4e00\u76f4\u9690\u85cf\u5728\u5fc3\u5e95\uff0c\u8fde\u78b0\u90fd\u4e0d\u6562\u53bb\u78b0\u7684\u75db\u82e6\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\uff0c\u4e00\u67c4\u5251\uff0c\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u5feb\u610f\u601d\u4ec7\uff0c\u6d6a\u5b50\u7684\u8c6a\u60c5\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u7fa1\u6155\u3002 \u3000\u3000\u56e0\u4e3a\u522b\u4eba\u6c38\u8fdc\u4e0d\u4f1a\u77e5\u9053\u4ed6\u4eec\u5fc3\u5e95\u7684\u7a7a\u865a\u548c\u75db\u82e6\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6293\u5230\u7684\u90a3\u6839\u6728\u5934\uff0c\u6709\u65f6\u975e\u4f46\u4e0d\u80fd\u8f7d\u4f60\u5230\u5cb8\uff0c\u53cd\u800c\u4f1a\u8ba9\u4f60\u6c89\u5f97\u66f4\u5feb\uff0c\u6240\u4ee5\u4f60\u5e94\u8be5\u653e\u624b\u65f6\uff0c\u5c31\u4e00\u5b9a\u8981\u653e\u624b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u63e1\u7d27\u53cc\u62f3\uff0c\u53c8\u6162\u6162\u677e\u5f00\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5bf9\u6211\u8bf4\u8fd9\u4e9b\u8bdd\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u670b\u53cb\u3002 \u3000\u3000\u542c\u5230\u8fd9\u4e24\u4e2a\u5b57\u4ece\u535c\u9e70\u5634\u91cc\u8bf4\u51fa\u6765\uff0c\u5c0f\u65b9\u771f\u7684\u5403\u4e86\u4e00\u60ca\uff0c\u751a\u81f3\u6bd4\u770b\u89c1\u4ed6\u767d\u8863\u4e0a\u7684\u8840\u8ff9\u65f6\u66f4\u5403\u60ca\uff0c\u53ea\u89c9\u5f97\u5fc3\u91cc\u5ffd\u7136\u6709\u4e00\u80a1\u70ed\u8840\u4e0a\u6d8c\uff0c\u585e\u4f4f\u4e86\u54bd\u5589\u3002 \u3000\u3000\u535c\u9e70\u5750\u8d77\uff0c\u4ece\u8eab\u65c1\u62ff\u8d77\u4e00\u4e2a\u7f8a\u76ae\u888b\uff0c\u888b\u91cc\u4e0d\u662f\u90a3\u79cd\u6de1\u800c\u5fae\u9178\u7684\u9752\u68f5\u9152\u3002 \u3000\u3000\u201c\u8fd9\u662f\u5929\u5c71\u5317\u8def\u7684\u53e4\u57ce\u70e7\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u8fd9\u79cd\u9152\u6bd4\u5927\u9ea6\u8fd8\u70c8\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u81ea\u5df1\u5148\u559d\u4e86\u4e00\u53e3\uff0c\u5c06\u7f8a\u76ae\u888b\u4ea4\u7ed9\u5c0f\u65b9\u3002 \u3000\u3000\u8f9b\u8fa3\u7684\u70c8\u9152\uff0c\u559d\u4e0b\u53bb\u5c31\u50cf\u662f\u70ed\u8840\u4e00\u6837\u3002 \u3000\u3000\u201c\u4f60\u6015\u4e0d\u6015\u9189\uff1f\u201d \u3000\u3000\u201c\u8fde\u6b7b\u90fd\u4e0d\u6015\uff0c\u4e3a\u4ec0\u4e48\u8981\u6015\u9189\uff1f\u201d \u3000\u3000\u535c\u9e70\u9510\u773c\u4e2d\u53c8\u6709\u4e86\u7b11\u610f\uff0c\u5ffd\u7136\u6f2b\u58f0\u800c\u6b4c\u3002 \u3000\u3000\u2014\u2014\u513f\u987b\u6709\u540d\u3002 \u3000\u3000\u9152\u987b\u9189\uff0c \u3000\u3000\u9189\u540e\u7545\u8c08\uff0c \u3000\u3000\u89c1\u5fc3\u8a00\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u516b\u7ae0\u3000\u7edd\u9876\u9ad8\u624b \u7b2c\u516b\u7ae0\u3000\u7edd\u9876\u9ad8\u624b \u3000\u3000\u8fd9\u662f\u897f\u85cf\u8bd7\u4eba\u5bc6\u62c9\u52d2\u65af\u5df4\u7684\u540d\u53e5\uff0c\u7b80\u7b80\u5355\u5355\u3001\u666e\u666e\u901a\u901a\u7684\u5341\u56db\u4e2a\u5b57\u91cc\uff0c\u5374\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u4e5f\u50cf\u662f\u7537\u513f\u4eec\u7684\u70ed\u8840\u4e00\u6837\u3002 \u3000\u3000\u535c\u9e70\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5c0f\u65b9\u4e5f\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u961f\u4f0d\u53c8\u5f00\u59cb\u524d\u884c\uff0c\u7ec8\u4e8e\u5c06\u5230\u8ddd\u5927\u5409\u5cad\u4e8c\u767e\u4e94\u5341\u91cc\u7684\u201c\u5723\u5730\u201d\u62c9\u8428\u3002 \u3000\u3000\u6674\u7a7a\u4e07\u91cc\uff0c\u4e91\u6de1\u5929\u9752\uff0c\u8fdc\u5904\u96ea\u5cf0\u5728\u671b\uff0c\u5c0f\u65b9\u7684\u5fc3\u60c5\u4eff\u4f5b\u4e5f\u5f00\u6717\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u5fd8\u8bb0\u6ce2\u5a03\u3002 \u3000\u3000\u535c\u9e70\u770b\u5f97\u51fa\u8fd9\u4e00\u70b9\uff0c\u201c\u8fd8\u6709\u4ef6\u4e8b\u6211\u4e00\u5b9a\u8981\u544a\u8bc9\u4f60\u3002\u201d\u6709\u4e00\u5929\u4ed6\u5bf9\u5c0f\u65b9\u8bf4\uff0c\u201c\u4e0d\u7ba1\u4f60\u4fe1\u4e0d\u4fe1\u6211\uff0c\u90fd\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6ce2\u5a03\u7684\u610f\u601d\u662f\u96ea\uff0c\u96ea\u662f\u51b0\u7ed3\u6210\u7684\uff0c\u96ea\u7684\u989c\u8272\u6d01\u767d\u5982\u94f6\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6ce2\u5a03\u624d\u662f\u771f\u6b63\u7684\u6c34\u94f6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u773a\u671b\u8fdc\u5904\u9ad8\u5cf0\u4e0a\u7684\u79ef\u96ea\uff0c\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u542c\u89c1\u535c\u9e70\u5728\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u535c\u9e70\u53c8\u9053\uff1a\u201c\u5931\u52ab\u7684\u9ec4\u91d1\u8fd8\u6ca1\u6709\u627e\u5230\uff0c\u536b\u5929\u9e4f\u8fd8\u662f\u4e0d\u4f1a\u653e\u8fc7\u6211\u3002\u6b7b\u53bb\u7684\u513f\u5b50\u6c38\u8fdc\u4e0d\u80fd\u590d\u751f\uff0c\u5415\u4e09\u4e5f\u4e00\u5b9a\u4e0d\u4f1a\u653e\u8fc7\u4f60\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u2018\u7bad\u7ec4\u2019\u4e2d\u7684\u4eba\u5df2\u4f24\u4ea1\u5927\u534a\uff0c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u8ba9\u6211\u4eec\u5e73\u5b89\u56de\u5230\u62c9\u8428\u53bb\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e24\u5929\u665a\u4e0a\uff0c\u961f\u4f0d\u6b47\u4e0b\u65f6\uff0c\u5c0f\u65b9\u4e5f\u4eff\u4f5b\u542c\u89c1\u8fdc\u5904\u9690\u9690\u6709\u9a6c\u8e44\u5954\u817e\u7684\u58f0\u97f3\u3002 \u3000\u3000\u536b\u5929\u9e4f\u662f\u4e0d\u662f\u5df2\u8c03\u96c6\u4e86\u4eba\u624b\uff0c\u51c6\u5907\u8ddf\u4ed6\u4eec\u4f5c\u6700\u540e\u4e00\u6218\uff1f \u3000\u3000\u201c\u524d\u9762\u6709\u4e2a\u9698\u53e3\uff0c\u85cf\u4eba\u4eec\u90fd\u79f0\u4e4b\u4e3a\u2018\u6b7b\u9888\u2019\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u5982\u679c\u6211\u7b97\u5f97\u4e0d\u9519\uff0c\u4ed6\u4eec\u6b64\u523b\u4e00\u5b9a\u5df2\u7ecf\u5728\u90a3\u91cc\u7b49\u7740\u6211\uff01\u201d \u3000\u3000\u6b7b\u9888\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u4e24\u4e2a\u5b57\uff0c\u5c0f\u65b9\u5df2\u53ef\u60f3\u8c61\u5230\u90a3\u9698\u53e3\u5730\u52bf\u7684\u9669\u5cfb\u3002\u56db\u5c71\u73af\u63d2\uff0c\u58c1\u7acb\u5343\u4ede\uff0c\u5982\u679c\u6709\u4eba\u5728\u90a3\u91cc\u57cb\u4f0f\u7a81\u51fb\uff0c\u8fd9\u961f\u4f0d\u4e2d\u80fd\u6d3b\u7740\u8fc7\u53bb\u7684\u4eba\u7edd\u4e0d\u4f1a\u591a\uff0c\u4f55\u51b5\u57cb\u4f0f\u5728\u90a3\u91cc\u7684\uff0c\u5fc5\u5b9a\u90fd\u662f\u536b\u5929\u9e4f\u90a3\u7ec4\u7ec7\u4e2d\u7684\u7cbe\u9510\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u4e0d\u7981\u62c5\u5fc3\uff1a\u201c\u4f60\u51c6\u5907\u95ef\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u535c\u9e70\u51b7\u7b11\uff1a\u201c\u4ed6\u4eec\u5c31\u60f3\u6211\u95ef\u8fc7\u53bb\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8ba9\u4ed6\u4eec\u7684\u79f0\u5fc3\u5982\u613f\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u9664\u4e86\u90a3\u9698\u53e3\u5916\uff0c\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u8def\u53ef\u8d70\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4f46\u662f\u6211\u4eec\u5e76\u4e0d\u662f\u4e00\u5b9a\u975e\u8fc7\u53bb\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u53bb\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7b49\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u53ef\u4ee5\u7b49\uff0c\u7b49\u4ed6\u4eec\u6765\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4f1a\u6765\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b9a\u4f1a\u6765\uff0c\u800c\u4e14\u5f88\u5feb\u5c31\u4f1a\u6765\uff0c\u56e0\u4e3a\u6211\u4eec\u80fd\u7b49\uff0c\u4ed6\u4eec\u4e0d\u80fd\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u7684\u4eba\u624b\u5df2\u96c6\u4e2d\uff0c\u6b63\u662f\u58eb\u9971\u9a6c\u817e\u3001\u6597\u5fd7\u6700\u65fa\u76db\u7684\u65f6\u5019\u3002\u4ed6\u4eec\u7b97\u51c6\u4e86\u8fd9\u4e00\u6218\u5fc5\u80dc\uff0c\u4e00\u51fb\u5f97\u624b\u540e\uff0c\u5c31\u53ef\u4ee5\u5f00\u5bb4\u5e86\u529f\u4e86\uff0c\u6240\u4ee5\u4ed6\u4eec\u8eab\u4e0a\u7edd\u4e0d\u4f1a\u5e26\u7740\u5927\u591a\u7cae\u98df\u548c\u6c34\uff0c\u56e0\u4e3a\u4e00\u6218\u8fc7\u540e\uff0c\u6211\u4eec\u7684\u7cae\u98df\u548c\u6c34\u5c31\u5168\u90fd\u662f\u4ed6\u4eec\u7684\u4e86\u3002\u201d \u3000\u3000\u535c\u9e70\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec\u4e0d\u80fd\u7b49\uff0c\u6211\u4eec\u4e0d\u8fc7\u53bb\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5df2\u5429\u5490\u8fc7\uff0c\u5728\u90a3\u9698\u53e3\u4e09\u5341\u91cc\u4e4b\u5916\u624e\u8425\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u4eec\u7b49\u4e0d\u5230\u6211\u4eec\uff0c\u6597\u5fd7\u5df2\u8870\uff0c\u518d\u5954\u9a70\u4e09\u5341\u91cc\u6765\u627e\u6211\u4eec\uff0c\u529b\u6c14\u4e5f\u5df2\u5f31\uff0c\u6211\u4eec\u5c31\u5728\u90a3\u91cc\u4ee5\u9038\u5f85\u52b3\uff0c\u7b49\u4ed6\u4eec\u6765\u9001\u6b7b\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e0d\u4ec5\u770b\u5f97\u51c6\uff0c\u800c\u4e14\u7b97\u65e0\u9057\u7b56\uff0c\u4e0d\u4ec5\u53ef\u4ee5\u62d4\u5251\u4f24\u4eba\u4e8e\u4e94\u6b65\u4e4b\u5185\uff0c\u800c\u4e14\u53ef\u4ee5\u8fd0\u7b79\u5e37\u5e44\uff0c\u51b3\u80dc\u4e8e\u5343\u91cc\u4e4b\u5916\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4ed6\u7684\u786e\u662f\u6c5f\u6e56\u5c11\u89c1\u7684\u5947\u624d\uff0c\u53ea\u4e0d\u8fc7\u5c0f\u65b9\u8fd8\u662f\u5728\u62c5\u5fc3\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u5c31\u7b97\u6765\u4e86\uff0c\u4e5f\u672a\u5fc5\u662f\u6765\u9001\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u536b\u5929\u9e4f\u65e2\u7136\u5df2\u51b3\u5fc3\u8981\u80dc\u8fd9\u4e00\u6218\uff0c\u8fd9\u4e00\u6b21\u5fc5\u5b9a\u7cbe\u9510\u5c3d\u51fa\uff0c\u518d\u52a0\u4e0a\u72ec\u5b64\u548c\u641c\u9b42\u624b\uff0c\u6211\u4eec\u8fd9\u8fb9\u80fd\u8ddf\u4ed6\u4eec\u4e00\u51b3\u80dc\u8d1f\u7684\u4eba\u6709\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u535c\u9e70\u7684\u767d\u8863\u4e0a\u53c8\u6709\u9c9c\u8840\u6ea2\u51fa\uff0c\u8fd9\u4e00\u6218\u4e4b\u540e\uff0c\u4ed6\u7684\u767d\u8863\u5fc5\u5c06\u88ab\u9c9c\u8840\u67d3\u7ea2\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u795e\u60c5\u5374\u4ecd\u7136\u6781\u9547\u9759\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e0d\u7ba1\u8fd9\u4e00\u6218\u6211\u4eec\u6709\u591a\u5927\u673a\u4f1a\uff0c\u4f60\u90fd\u7edd\u4e0d\u4f1a\u8d70\u7684\uff0c\u5426\u5219\u4f60\u4e5f\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u80f8\u53e3\u53c8\u70ed\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u670b\u53cb\u7684\u4e86\u89e3\uff0c\u603b\u662f\u6bd4\u4efb\u4f55\u4e8b\u90fd\u4ee4\u4ed6\u611f\u52a8\u3002 \u3000\u3000\u535c\u9e70\u770b\u7740\u4ed6\uff0c\u51b7\u9177\u9510\u5229\u7684\u76ee\u5149\u5ffd\u7136\u53d8\u5f97\u5f88\u67d4\u548c\uff1a\u201c\u6211\u53d7\u4e86\u4f24\uff0c\u6211\u4eec\u7684\u4eba\u624b\u7684\u786e\u4e0d\u591f\uff0c\u4f46\u662f\u6211\u4eec\u5e76\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u673a\u4f1a\uff0c\u56e0\u4e3a\u6211\u4eec\u6709\u4e00\u6837\u4e1c\u897f\u662f\u536b\u5929\u9e4f\u4ed6\u4eec\u7edd\u5bf9\u6ca1\u6709\u7684\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u4eec\u6709\u751f\u6b7b\u4e0e\u5171\uff0c\u6b7b\u4e5f\u4e0d\u4f1a\u4e34\u9635\u8131\u9003\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u6b21\u4f60\u4e00\u5b9a\u8981\u5c06\u72ec\u5b64\u75f4\u7559\u7ed9\u6211\uff01\u201d \u3000\u3000\u535c\u9e70\u53c8\u9759\u9759\u5730\u770b\u4e86\u4ed6\u5f88\u4e45\uff0c\u76ee\u4e2d\u53c8\u6709\u4e86\u7b11\u610f\u3002 \u3000\u3000\u201c\u8fd9\u6b21\u72ec\u5b64\u75f4\u6050\u6015\u4e0d\u4f1a\u6765\uff0c\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u4e5f\u542c\u8fc7\u73ed\u5bdf\u5df4\u90a3\u6700\u559c\u6b22\u8bf4\u7684\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u662f\u54ea\u53e5\u8bdd\u3002 \u3000\u3000\u2014\u2014\u8981\u8ba9\u522b\u4eba\u6d41\u8840\uff0c\u81ea\u5df1\u4e5f\u5f97\u6d41\u8840\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u6211\u627f\u8ba4\u72ec\u5b64\u75f4\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u5251\u5ba2\uff0c\u53ef\u662f\u4ed6\u8981\u8ba9\u6211\u6d41\u8840\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5f97\u4ed8\u51fa\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\uff1a\u201c\u4ed6\u4e5f\u53d7\u4e86\u4f24\uff1f\u201d \u3000\u3000\u535c\u9e70\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u53ea\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5982\u679c\u4ed6\u6765\u4e86\uff0c\u6211\u4e00\u5b9a\u628a\u4ed6\u7559\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8fd8\u672a\u5230\u9ec4\u660f\uff0c\u961f\u4f0d\u5c31\u5df2\u505c\u4e0b\u3002 \u3000\u3000\u6839\u636e\u52a0\u7b54\u7684\u62a5\u544a\uff0c\u8fd9\u91cc\u4e0e\u201c\u6b7b\u9888\u201d\u4e4b\u95f4\u7684\u6b63\u786e\u8ddd\u79bb\u662f\u4e8c\u5341\u4e5d\u91cc\u3002 \u3000\u3000\u9a86\u9a7c\u56f4\u6210\u4e86\u4e00\u5708\uff0c\u5e10\u7bf7\u624e\u8d77\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4f9d\uff01\u65e5\u5728\u505a\u4ed6\u4eec\u5e94\u8be5\u505a\u7684\u4e8b\uff0c\u548c\u5e73\u65f6\u5b8c\u5168\u6ca1\u6709\u4e0d\u540c\uff0c\u4eff\u4f5b\u6839\u672c\u4e0d\u77e5\u9053\u6709\u5927\u654c\u5c06\u4e34\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u6709\u4e00\u6574\u5929\u6ca1\u6709\u89c1\u5230\u73ed\u5bdf\u5df4\u90a3\u4e86\uff0c\u8fd9\u4e24\u5929\u4ed6\u4e5f\u6ca1\u6709\u88ab\u6d3e\u51fa\u53bb\u503c\u52e4\u5de1\u5927\uff0c\u4e00\u76f4\u90fd\u966a\u7740\u535c\u9e70\u7559\u5728\u90a3\u9876\u4e0a\u60ac\u6302\u7740\u9ed1\u8272\u9e70\u7fbd\u7684\u5e10\u7bf7\u91cc\u3002 \u3000\u3000\u8d1f\u8d23\u7ba1\u5236\u98df\u6c34\u7684\u4e25\u6b63\u521a\u548c\u5b8b\u8001\u592b\u5b50\u4e5f\u6765\u4e86\uff0c\u662f\u535c\u9e70\u8bf7\u4ed6\u4eec\u6765\u7684\uff0c\u8bf7\u4ed6\u4eec\u6765\u559d\u9152\u3002 \u3000\u3000\u4eca\u5929\u535c\u9e70\u7684\u5174\u8da3\u5c45\u7136\u5f88\u597d\u3002 \u3000\u3000\u4ed6\u4eec\u559d\u7684\u4e0d\u662f\u53e4\u57ce\u70e7\uff0c\u662f\u201c\u545b\u201d\u2014\u2014\u9752\u68f5\u917f\u9152\uff0c\u540d\u66f0\u545b\u3002 \u3000\u3000\u8fd9\u79cd\u9152\u867d\u7136\u4e0d\u6613\u9189\uff0c\u9189\u4e86\u5374\u4e0d\u6613\u9192\u3002 \u3000\u3000\u9ec4\u660f\u540e\u5916\u9762\u5c31\u54cd\u8d77\u4e86\u6b4c\u58f0\uff0c\u5bf9\u85cf\u4eba\u4eec\u6765\u8bf4\uff0c\u6b4c\u4e0e\u9152\u662f\u5206\u4e0d\u5f00\u7684\u3002 \u3000\u3000\u56db\u4e0b\u8425\u706b\u5904\u5904\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5728\u6b4c\uff0c\u90fd\u5728\u996e\uff0c\u597d\u50cf\u6545\u610f\u8981\u8ba9\u522b\u4eba\u8ba4\u4e3a\u4ed6\u4eec\u5b8c\u5168\u6ca1\u6709\u6212\u5907\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u4eec\u6709\u6240\u6212\u5907\u53c8\u5982\u4f55\uff1f\u7bad\u7ec4\u4e2d\u7684\u52c7\u58eb\uff0c\u5269\u4e0b\u7684\u5df2\u4e0d\u5230\u5341\u4e2a\u4eba\u3002 \u3000\u3000\u6839\u636e\u5c0f\u65b9\u6240\u542c\u5230\u7684\u9a6c\u8e44\u58f0\uff0c\u535c\u9e70\u8c03\u96c6\u6765\u7684\u4eba\u624b\u81f3\u5c11\u6709\u4ed6\u4eec\u7684\u5341\u500d\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u56de\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8bc1\u5b9e\u4e86\u5c0f\u65b9\u7684\u60f3\u6cd5\uff0c\u4ed6\u5df2\u5230\u201c\u6b7b\u9888\u201d\u53bb\u8fc7\uff1a\u201c\u6b64\u523b\u5df2\u5230\u4e86\u90a3\u91cc\u7684\uff0c\u5927\u7ea6\u6709\u4e03\u5341\u5339\u9a6c\u3002\u201d \u3000\u3000\u4e03\u5341\u5339\u9a6c\uff0c\u5c31\u662f\u4e03\u5341\u4e2a\u4eba\uff0c\u5c31\u662f\u4e03\u5341\u4ef6\u5175\u5203\uff0c\u6bcf\u4e00\u4ef6\u90fd\u5fc5\u5b9a\u662f\u6740\u4eba\u7684\u5229\u5668\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u8bf4\uff1a\u201c\u90a3\u4e9b\u4eba\u6bcf\u4e00\u4e2a\u90fd\u662f\u9a91\u672f\u7cbe\u7edd\u7684\u58ee\u58eb\uff0c\u5176\u4e2d\u6709\u4e00\u90e8\u5206\u7528\u7684\u662f\u957f\u67aa\u5927\u621f\uff0c\u6709\u4e00\u90e8\u5206\u914d\u7740\u5f13\u5f29\uff0c\u8fd8\u6709\u4e03\u516b\u4e2a\u7528\u7684\u662f\u5916\u95e8\u5175\u5203\u3002\u201d \u3000\u3000\u80fd\u7528\u5916\u95e8\u5175\u5203\u7684\u4eba\uff0c\u6b66\u529f\u7edd\u4e0d\u4f1a\u592a\u5dee\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5374\u8bf4\uff1a\u201c\u53ef\u662f\u771f\u6b63\u53ef\u6015\u7684\u4e0d\u662f\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u771f\u6b63\u53ef\u6015\u7684\u662f\u8c01\uff1f\u201d\u5c0f\u65b9\u5728\u95ee\u3002 \u3000\u3000\u201c\u9664\u4e86\u4e03\u5341\u5339\u9a6c\u5916\uff0c\u8fd8\u6709\u4e09\u9876\u8f7f\u5b50\u4e5f\u5230\u4e86\u90a3\u91cc\u3002\u201d \u3000\u3000\u6c99\u6f20\u4e2d\u5c45\u7136\u6709\u4eba\u5750\u8f7f\u4e8e\uff0c\u5728\u51c6\u5907\u7a81\u88ad\u5f3a\u654c\u65f6\uff0c\u5c45\u7136\u6709\u4eba\u8981\u5750\u8f7f\u5b50\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u66f4\u60ca\u5f02\uff1a\u201c\u8f7f\u5b50\u91cc\u6709\u4eba\uff1f\u201d \u3000\u3000\u201c\u6709\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4e00\u9876\u8f7f\u5b50\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u662f\u4e9b\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u80fd\u591f\u8ba9\u536b\u5929\u9e4f\u6d3e\u8f7f\u5b50\u53bb\u63a5\u6765\u7684\uff0c\u5f53\u7136\u90fd\u662f\u4e86\u4e0d\u8d77\u7684\u4eba\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8fdf\u7591\u4e86\u7247\u523b\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u6211\u53ea\u8ba4\u5f97\u51fa\u5176\u4e2d\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u5f97\u51fa\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u4f60\u8ba4\u4e3a\u7edd\u4e0d\u4f1a\u6740\u4eba\u7684\u90a3\u4e2a\u5973\u4eba\uff0c\u201d \u3000\u3000\u5c0f\u65b9\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u2014\u2014\u6ce2\u5a03\u771f\u7684\u662f\u4e2a\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u624b\uff1f\u771f\u7684\u80fd\u5728\u7728\u773c\u95f4\u6740\u4eba\uff1f \u3000\u3000\u4ed6\u770b\u4e0d\u51fa\uff0c\u771f\u7684\u770b\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u4e5f\u8bb8\u5df2\u7ecf\u4e0d\u662f\u4e0d\u80fd\u76f8\u4fe1\uff0c\u800c\u662f\u4e0d\u613f\u76f8\u4fe1\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u53e6\u5916\u4e00\u4e2a\u662f\u72ec\u81c2\u72ec\u817f\u7684\u6b8b\u5e9f\uff0c\u5de6\u817f\u4e0a\u88c5\u7740\u6839\u6728\u811a\uff0c\u53f3\u624b\u4e0a\u63d0\u7740\u4e2a\u9ec4\u5e03\u5305\u88b1\uff0c\u4efd\u91cf\u770b\u6765\u5f88\u91cd\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\uff1a\u201c\u4ed6\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4e0d\u51fa\u4ed6\u7684\u5e74\u7eaa\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4ed6\u7684\u5934\u53d1\u6bcf\u4e00\u6839\u90fd\u767d\u4e86\uff0c\u4eae\u5982\u94f6\u4e1d\uff0c\u4f46\u662f\u4e00\u5f20\u8138\u5374\u8fd8\u662f\u767d\u91cc\u900f\u7ea2\uff0c\u770b\u6765\u7b80\u76f4\u662f\u4e2a\u5c0f\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u5c0f\u59d1\u5a18\uff1f\u201d\u5c0f\u65b9\u53c8\u95f4\uff0c\u201c\u4f60\u8bf4\u7684\u8fd9\u4e2a\u4eba\uff0c\u662f\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\uff0c\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u8138\u8272\u4eff\u4f5b\u5df2\u53d8\u4e86\u3002 \u3000\u3000\u201c\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e2a\u4eba\u597d\u8c61\u662f\u4e2a\u778e\u5b50\uff0c\u4e0b\u8f7f\u65f6\u5374\u8981\u4eba\u6400\u6276\uff0c\u4f46\u662f\u552f\u4e00\u53d1\u73b0\u6211\u8eb2\u5728\u9644\u8fd1\u7684\u4eba\u5c31\u662f\u4ed6\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u82e6\u7b11\uff0c\u201c\u6211\u5dee\u4e00\u70b9\u5c31\u56de\u4e0d\u6765\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u4ed6\u5df2\u731c\u51fa\u8fd9\u4e24\u4e2a\u4eba\u662f\u8c01\uff0c\u5728\u5f53\u4e16\u7684\u7edd\u9876\u9ad8\u624b\u4e2d\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7edd\u5bf9\u53ef\u4ee5\u540d\u5217\u5728\u524d\u5341\u4f4d\u3002 \u3000\u3000\u535c\u9e70\u4e5f\u5e94\u8be5\u77e5\u9053\u4ed6\u4eec\u7684\uff0c\u4f46\u662f\u535c\u9e70\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u53ea\u6de1\u6de1\u4ed6\u8bf4\u4e86\u53e5\uff1a\u201c\u4f60\u7d2f\u4e86\uff0c\u6765\u559d\u676f\u9152\u3002\u201d \u3000\u3000\u4e0d\u6613\u9189\u7684\u9152\uff0c\u9189\u4e86\u5c31\u4e0d\u6613\u9192\u3002\u6700\u53ef\u7231\u7684\u4eba\uff0c\u5f80\u5f80\u5c31\u662f\u6700\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u5929\u8272\u5df2\u6697\u4e86\uff0c\u4eba\u4e5f\u5c06\u9189\u4e86\uff0c\u8425\u706b\u5374\u66f4\u4eae\uff0c\u6b4c\u58f0\u4e5f\u66f4\u4eae\u3002 \u3000\u3000\u535c\u9e70\u7684\u9510\u773c\u4e5f\u66f4\u4eae\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u5982\u6b64\u9547\u9759\uff1f\u96be\u9053\u4ed6\u5df2\u6709\u65b9\u6cd5\u5bf9\u4ed8\u5373\u5c06\u6765\u7684\u90a3\u4e9b\u4eba\uff1f \u3000\u3000\u5c0f\u65b9\u60f3\u4e0d\u51fa\u4ed6\u80fd\u6709\u4ec0\u4e48\u6cd5\u5b50\u3002 \u3000\u3000\u90a3\u778e\u5b50\u65e0\u7591\u5c31\u662f\u641c\u9b42\u624b\u3002 \u3000\u3000\u201c\u6bd2\u624b\u641c\u9b42\uff0c\u6027\u547d\u65e0\u5b58\u3002\u201d\u5982\u679c\u4ed6\u8981\u53bb\u627e\u4e00\u4e2a\u4eba\uff0c\u90a3\u4e2a\u4eba\u4e0d\u662f\u8d76\u5feb\u9003\u8d70\uff0c\u5c31\u662f\u8d76\u5feb\u4e3a\u81ea\u5df1\u6599\u7406\u540e\u4e8b\u3002 \u3000\u3000\u80fd\u591f\u4ece\u4ed6\u624b\u4e0b\u9003\u8d70\u7684\u4eba\u81f3\u4eca\u8fd8\u6ca1\u6709\u51e0\u4e2a\u3002 \u3000\u3000\u90a3\u4e2a\u72ec\u81c2\u72ec\u817f\u3001\u7ea2\u989c\u767d\u53d1\u7684\u5973\u4eba\u6bd4\u4ed6\u66f4\u53ef\u6015\uff0c\u56e0\u4e3a\u5979\u53ea\u6709\u4e00\u534a\u662f\u4eba\u3002 \u3000\u3000\u5979\u7684\u53e6\u4e00\u534a\u7136\u4e0d\u662f\u795e\uff0c\u4e5f\u4e0d\u662f\u9b3c\uff0c\u66f4\u4e0d\u662f\u4eba\u3002 \u3000\u3000\u5979\u7684\u53e6\u5916\u4e00\u534a\u662f\u201c\u9b54\u201d\u3002 \u3000\u3000\u5979\u8fd9\u4e2a\u4eba\u4eff\u4f5b\u5df2\u88ab\u4e00\u79cd\u53ef\u6015\u7684\u9b54\u6cd5\u5206\u6210\u4e86\u4e24\u534a\uff0c\u4e00\u534a\u662f\u7389\u5973\uff0c\u4e00\u534a\u662f\u5929\u9b54\u3002 \u3000\u3000\u201c\u7389\u5973\u5929\u9b54\u201d\u67f3\u5206\u5206\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u5979\u7a76\u7adf\u6709\u591a\u9ad8\u6b66\u529f\uff0c\u591a\u5927\u5e74\u7eaa\u3002 \u3000\u3000\u53ef\u662f\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\uff0c\u5979\u4e5f\u968f\u65f6\u90fd\u53ef\u4ee5\u628a\u4f60\u4e00\u4e2a\u4eba\u5206\u6210\u4e24\u534a\u3002 \u3000\u3000\u4e25\u6b63\u521a\u4e00\u5411\u6ef4\u9152\u4e0d\u6cbe\u3002\u5b8b\u8001\u592b\u5b50\u559d\u5f97\u5374\u4e0d\u5c11\u3002\u4e0d\u559d\u9152\u7684\u4e00\u4e2a\u65b9\u6b63\u4e25\u8083\uff0c\u559d\u9152\u7684\u4e00\u4e2a\u4e5f\u662f\u541b\u5b50\uff0c\u5728\u4e00\u822c\u60c5\u51b5\u4e0b\uff0c\u4ed6\u4eec\u90fd\u662f\u503c\u5f97\u5c0a\u656c\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u5230\u4e86\u62d4\u5200\u76f8\u5bf9\u3001\u767d\u5203\u52a0\u9888\u65f6\uff0c\u4ed6\u4eec\u7684\u4ef7\u503c\u4e5f\u8bb8\u8fd8\u6bd4\u4e0d\u4e0a\u52a0\u7b54\u3002 \u3000\u3000\u52a0\u7b54\u662f\u6218\u58eb\u3001\u4e5f\u662f\u52c7\u58eb\uff0c\u53ef\u662f\u5728\u9762\u5bf9\u641c\u9b42\u624b\u548c\u67f3\u5206\u5206\u8fd9\u6837\u7684\u9ad8\u624b\u65f6\uff0c\u4ed6\u552f\u4e00\u80fd\u505a\u5230\u7684\uff0c\u5c31\u662f\u6b7b\u3002 \u3000\u3000\u201c\u6b7b\u201d\u867d\u7136\u662f\u6240\u6709\u4e00\u5207\u7684\u7ec8\u7ed3\uff0c\u5374\u4e0d\u80fd\u89e3\u51b3\u4efb\u4f55\u95ee\u9898\u3002 \u3000\u3000\u5c31\u771f\u80fd\u89e3\u51b3\uff0c\u4e5f\u6ca1\u6709\u4eba\u613f\u610f\u7528\u8fd9\u79cd\u65b9\u5f0f\u89e3\u51b3\u3002 \u3000\u3000\u535c\u9e70\u5df2\u91cd\u4f24\uff0c\u73ed\u5bdf\u5df4\u90a3\u6bd5\u7adf\u4e0d\u662f\u795e\uff0c\u4ed6\u4eec\u80fd\u6709\u4ec0\u4e48\u6cd5\u5b50\u53bb\u5bf9\u4ed8\u5373\u5c06\u5230\u6765\u7684\u5f3a\u654c\uff1f \u3000\u3000\u5c0f\u65b9\u60f3\u7684\u5f88\u591a\uff0c\u53ea\u6709\u4e00\u4ef6\u4e8b\u6ca1\u6709\u60f3\u3002 \u3000\u3000\u2014\u2014\u6ce2\u5a03\u662f\u4e0d\u662f\u4f1a\u6765\uff1f\u6765\u4e86\u4e4b\u540e\uff0c\u4f1a\u7528\u4ec0\u4e48\u6837\u7684\u6001\u5ea6\u5bf9\u5f85\u4ed6\uff1f \u3000\u3000\u4ed6\u53c8\u80fd\u7528\u4ec0\u4e48\u6837\u7684\u6001\u5ea6\u5bf9\u5f85\u5979\uff1f \u3000\u3000\u62b5\u6b7b\u7f20\u7ef5\u7684\u60c5\u4eba\uff0c\u5ffd\u7136\u53d8\u6210\u751f\u6b7b\u76f8\u640f\u7684\u4ec7\u654c\uff0c\u4ed6\u5c06\u5982\u4f55\u81ea\u5904\u3001 \u3000\u3000\u8fd9\u79cd\u60c5\u51b5\u6709\u8c01\u80fd\u5e94\u4ed8\uff1f\u8fd9\u79cd\u75db\u82e6\u6709\u8c01\u80fd\u4e86\u89e3\uff1f \u3000\u3000\u535c\u9e70\u4e00\u76f4\u5728\u770b\u7740\u4ed6\uff0c\u4eff\u4f5b\u5df2\u770b\u51fa\u4e86\u4ed6\u5fc3\u91cc\u7684\u75db\u82e6\uff0c\u9ed8\u9ed8\u5730\u5411\u4ed6\u4e3e\u8d77\u4e86\u9152\u676f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u3002\u8fdc\u5904\u5ffd\u7136\u6709\u9a6c\u8e44\u5954\u817e\u58f0\u54cd\u8d77\u3002 \u3000\u3000\u4e03\u5341\u5339\u5feb\u9a6c\u98de\u9a70\u5954\u817e\uff0c\u8e44\u58f0\u5982\u6218\u9f13\u96f7\u9e23\uff0c\u5929\u5730\u95f4\u7acb\u523b\u5145\u6ee1\u4e86\u6740\u6c14\u3002 \u3000\u3000\u53ef\u662f\u5916\u9762\u7684\u6b22\u5531\u5e76\u6ca1\u6709\u505c\u6b62\uff0c\u535c\u9e70\u4e5f\u4ecd\u7136\u5b89\u5750\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u7684\u676f\u4e2d\u4ecd\u6709\u9152\uff0c\u6ee1\u6ee1\u7684\u4e00\u676f\u9152\uff0c\u8fde\u4e00\u6ef4\u90fd\u6ca1\u6709\u6e85\u51fa\u6765\uff0c\u4ed6\u53ea\u6de1\u6de1\u5730\u5bf9\u5c0f\u65b9\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6700\u6015\u7b49\uff0c\u4ed6\u4eec\u679c\u7136\u6ca1\u6709\u8ba9\u6211\u4eec\u7b49\u5f97\u592a\u4e45\u3002\u201d\u4ed6\u53c8\u4e3e\u676f\uff0c\u201c\u4e3a\u4e86\u8fd9\u4e00\u70b9\uff0c\u6211\u4eec\u4e5f\u8be5\u559d\u676f\u9152\u3002\u201d \u3000\u3000\u8e44\u58f0\u81ea\u8fdc\u800c\u8fd1\uff0c\u4eff\u4f5b\u5728\u56f4\u7740\u8fd9\u961f\u4f0d\u7684\u8425\u5730\u5954\u9a70\uff0c\u5e76\u6ca1\u6709\u51b2\u8fc7\u6765\u3002 \u3000\u3000\u8425\u706b\u65c1\u7684\u4eba\u4ecd\u5728\u9ad8\u6b4c\u6b22\u5531\uff0c\u4eff\u4f5b\u6839\u672c\u4e0d\u77e5\u9053\u5f3a\u654c\u5df2\u6765\uff0c\u751f\u6b7b\u5df2\u5728\u547c\u5438\u95f4\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u7edd\u5bf9\u4fe1\u4efb\u535c\u9e70\uff0c\u7edd\u4e0d\u4f1a\u5c06\u4ed6\u4eec\u5e26\u4e0a\u6b7b\u8def\uff0c\u6240\u4ee5\u624d\u80fd\u5982\u6b64\u9547\u5b9a\uff1f \u3000\u3000\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u4eec\u8fd9\u79cd\u8d85\u4eba\u7684\u9547\u5b9a\uff0c\u624d\u4f7f\u5f97\u5f3a\u654c\u4e0d\u6562\u8f7b\u72af\uff01 \u3000\u3000\u5ffd\u7136\u95ee\uff0c\u4e00\u58f0\u5c16\u9510\u7684\u80e1\u54e8\u54cd\u8d77\uff0c\u54cd\u5f7b\u4e91\u9704\u3002 \u3000\u3000\u56f4\u7ed5\u7740\u8425\u5730\u5954\u9a70\u7684\u5065\u9a6c\uff0c\u5ffd\u7136\u5168\u90e8\u90fd\u505c\u4e0b\uff0c\u8e44\u58f0\u9aa4\u6b62\uff0c\u5927\u5730\u9759\u5bc2\u5982\u6b7b\u3002 \u3000\u3000\u6740\u6c14\u5374\u66f4\u91cd\u4e86\u3002 \u3000\u3000\u4e03\u5341\u5339\u5feb\u9a6c\u4e0a\u7684\u4e03\u5341\u540d\u6218\u58eb\uff0c\u60f3\u5fc5\u90fd\u5df2\u62bd\u7bad\u4e0a\u5f26\uff0c\u62d4\u5200\u51fa\u9798\u3002 \u3000\u3000\u535c\u9e70\u4ecd\u7136\u6beb\u65e0\u4e3e\u52a8\u3002 \u3000\u3000\u5bf9\u65b9\u4e0d\u52a8\uff0c\u4ed6\u4e5f\u4e0d\u52a8\uff0c\u4ed6\u6bd4\u4ed6\u4eec\u66f4\u80fd\u7b49\uff0c\u66f4\u80fd\u5fcd\u3002 \u3000\u3000\u5c0f\u65b9\u5f88\u60f3\u51fa\u53bb\u770b\u770b\u5916\u9762\u7684\u60c5\u51b5\uff0c\u535c\u9e70\u5374\u53c8\u5411\u4ed6\u4e3e\u8d77\u4e86\u9152\u676f\u3002 \u3000\u3000\u201c\u6211\u4fdd\u8bc1\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u51b2\u8fc7\u6765\u7684\uff0c\u60c5\u51b5\u672a\u660e\uff0c\u4ed6\u4eec\u7edd\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002\u201d \u3000\u3000\u4ed6\u53c8\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff1a\u201c\u6211\u4eec\u81f3\u5c11\u8fd8\u6709\u65f6\u95f4\u518d\u559d\u4e09\u4e94\u676f\u3002\u201d \u3000\u3000\u4ed6\u53ea\u559d\u4e86\u4e00\u8fd9\u4e00\u676f\uff0c\u53c8\u662f\u4e00\u58f0\u80e1\u54e8\u54cd\u8d77\uff0c\u52a0\u7b54\u5ffd\u7136\u51b2\u5165\u4e86\u5e10\u7bf7\uff0c\u5636\u58f0\u8bf4\uff1a\u201c\u6765\u4e86\uff01\u201d \u3000\u3000\u535c\u9e70\u7684\u676f\u4e2d\u9152\u53c8\u5df2\u659f\u6ee1\uff0c\u6ef4\u9152\u4e0d\u6e85\uff0c\u53ea\u51b7\u51b7\u5730\u95ee\uff1a\u4e00\u8c01\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u536b\u5929\u9e4f\u6765\u4e86\u3002\u201d\u52a0\u7b54\u663e\u5f97\u6709\u70b9\u7d27\u5f20\uff0c\u201c\u8fd8\u6709\u516d\u4e2a\u4eba\u62ac\u7740\u4e09\u9876\u8f7f\u5b50\u8ddf\u7740\u4ed6\u4e00\u8d77\u6765\u4e86\uff0c\u5df2\u7ecf\u4ece\u897f\u9762\u8fdb\u5165\u4e86\u8425\u5730\u3002\u201d \u3000\u3000\u201c\u6765\u7684\u53ea\u6709\u8fd9\u51e0\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u5176\u4f59\u7684\u4eba\u9a6c\u5df2\u7ecf\u628a\u6211\u4eec\u5305\u56f4\u4f4f\uff0c\u6765\u7684\u5374\u53ea\u6709\u8fd9\u51e0\u4e2a\u4eba\u3002\u201d\u52a0\u7b54\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u8981\u6765\u89c1\u4f60\u3002\u201d \u3000\u3000\u535c\u9e70\u6d45\u6d45\u5730\u555c\u4e86\u4e00\u53e3\u9152\uff1a\u201c\u65e2\u7136\u6709\u8d35\u5ba2\u5149\u4e34\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8bf7\u4ed6\u4eec\u8fdb\u6765\uff1f\u201d \u3000\u3000\u5e10\u7bf7\u5916\u5ffd\u7136\u6709\u4eba\u51b7\u7b11\uff01 \u3000\u3000\u201c\u65e2\u7136\u77e5\u9053\u6709\u8d35\u5ba2\u5149\u4e34\uff0c\u4e3b\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u6765\u8fce\u63a5\uff1f\u201d\u8bf4\u8bdd\u7684\u8fd9\u4e2a\u4eba\u58f0\u97f3\u5c16\u7ec6\uff0c\u5c31\u50cf\u662f\u4e00\u6839\u6839\u5c16\u9488\u523a\u5165\u8033\u91cc\uff0c\u201c\u535c\u5927\u8001\u677f\u7684\u67b6\u5b50\u4e5f\u672a\u514d\u592a\u5927\u4e9b\u3002\u201d \u3000\u3000\u535c\u9e70\u51b7\u51b7\u9053\uff1a\u201c\u6211\u7684\u67b6\u5b50\u672c\u6765\u5c31\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u4ed6\u6325\u4e86\u6325\u624b\uff0c\u52a0\u7b54\u7acb\u523b\u5c06\u5927\u5e10\u6380\u8d77\uff0c\u5e10\u5916\u706f\u706b\u4eae\u5982\u767d\u663c\uff0c\u8fdc\u5904\u95ea\u52a8\u7740\u5200\u67aa\u5251\u621f\u7684\u5bd2\u5149\uff0c\u6b22\u5531\u58f0\u7ec8\u4e8e\u505c\u6b62\uff0c\u9a7c\u9a6c\u4e0d\u65f6\u60ca\u5636\uff0c\u5bd2\u98ce\u9635\u9635\u5439\u6765\uff0c\u51b7\u5982\u523a\u9aa8\u94a2\u5200\u3002 \u3000\u3000\u4e00\u5339\u9ad8\u5934\u5927\u9a6c\u3001\u4e09\u9876\u7eff\u7ed2\u5c0f\u8f7f\u5df2\u5230\u4e86\u5e10\u5916\u3002\u536b\u5929\u9e4f\u9ad8\u5750\u9a6c\u4e0a\uff0c\u8170\u7554\u6709\u5200\uff0c\u978d\u65c1\u6709\u7bad\uff0c\u7bad\u4ecd\u5728\u58f6\uff0c\u5200\u4ecd\u5728\u9798\uff0c\u6740\u6c14\u5374\u5df2\u5c3d\u51fa\u3002 \u3000\u3000\u521a\u624d\u8bf4\u8bdd\u7684\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u521a\u624d\u8bf4\u8bdd\u7684\u58f0\u97f3\u662f\u4ece\u7b2c\u4e00\u9876\u8f7f\u5b50\u91cc\u53d1\u51fa\u6765\u7684\uff0c\u73b0\u5728\u4eba\u5df2\u4e0b\u8f7f\u3002 \u3000\u3000\u4e00\u4e2a\u72ec\u81c2\u72ec\u817f\u7684\u5973\u4eba\uff0c\u5934\u53d1\u767d\u5982\u94f6\u4e1d\uff0c\u9762\u8c8c\u5b9b\u5982\u5c11\u5973\uff0c\u5de6\u817f\u4e0a\u88c5\u7740\u4e11\u964b\u800c\u7b28\u62d9\u7684\u6728\u811a\uff0c\u53f3\u817f\u4e0a\u5374\u7a7f\u7740\u6761\u7eff\u82b1\u88e4\uff0c\u9732\u51fa\u4e86\u5149\u6ed1\u7ea4\u7ec6\u67d4\u7f8e\u7684\u8db3\u8e1d\uff0c\u8e1d\u4e0a\u6234\u7740\u4e03\u516b\u679a\u95ea\u95ea\u53d1\u5149\u7684\u91d1\u94dc\u3002 \u3000\u3000\u5979\u7684\u5de6\u81c2\u5df2\u9f50\u65f6\u65ad\u53bb\uff0c\u53f3\u624b\u5374\u7f8e\u5982\u6625\u8471\uff0c\u624b\u4e0a\u63d0\u7740\u4e2a\u770b\u6765\u4efd\u91cf\u6781\u6c89\u91cd\u7684\u9ec4\u5e03\u5305\u88b1\u3002 \u3000\u3000\u5979\u7684\u6728\u811a\u7740\u5730\uff0c\u59ff\u52bf\u4e11\u964b\u800c\u7b28\u6398\uff0c\u53f3\u817f\u843d\u4e0b\u540e\uff0c\u7acb\u523b\u53d8\u5f97\u98ce\u59ff\u7ef0\u7ea6\uff0c\u7f8e\u5982\u4ed9\u5b50\u3002 \u3000\u3000\u5979\u8fd9\u4e2a\u4eba\u5c31\u50cf\u662f\u5730\u4e0b\u8bf8\u9b54\u7528\u4e24\u4e2a\u5b8c\u5168\u4e0d\u540c\u7684\u4eba\u62fc\u51d1\u8d77\u6765\u7684\uff0c\u62fc\u5f97\u867d\u7136\u5f88\u5de7\u5999\uff0c\u5374\u4ee4\u4eba\u4e00\u770b\u89c1\u5c31\u4f1a\u4ece\u5fc3\u5e95\u53d1\u51b7\u3002 \u3000\u3000\u5c0f\u65b9\u672c\u6765\u5c31\u542c\u8bf4\u8fc7\u201c\u5929\u9b54\u7389\u5973\u201d\u67f3\u5206\u5206\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u7b49\u4ed6\u4eb2\u773c\u770b\u89c1\u65f6\uff0c\u4ed6\u624d\u77e5\u9053\u6240\u6709\u7684\u4f20\u8bf4\u90fd\u4e0d\u80fd\u5f62\u5bb9\u51fa\u5979\u7684\u90aa\u5f02\u548c\u8be1\u79d8\u3002 \u3000\u3000\u7b2c\u4e8c\u9876\u8f7f\u5b50\u4e0a\u7684\u4eba\u4e5f\u4e0b\u6765\u4e86\uff0c\u7626\u800c\u9edd\u9ed1\uff0c\u957f\u5982\u7af9\u7aff\uff0c\u8eab\u4e0a\u7a7f\u7740\u4ef6\u9ed1\u5e03\u957f\u886b\uff0c\u4e00\u53cc\u773c\u775b\u91cc\u660f\u6697\u65e0\u5149\uff0c\u4e00\u53cc\u624b\u59cb\u7ec8\u85cf\u5728\u8896\u5b50\u91cc\uff0c\u4e0d\u613f\u8ba9\u4eba\u770b\u89c1\u3002 \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u4ed6\u5c31\u662f\u6c5f\u6e56\u4e2d\u4eba\u95fb\u540d\u4e27\u80c6\u7684\u201c\u6740\u624b\u641c\u9b42\u201d\uff0c\u53ef\u662f\u5e76\u6ca1\u6709\u5341\u5206\u6ce8\u610f\u4ed6\u3002 \u3000\u3000\u5c0f\u65b9\u4e00\u76f4\u5728\u6ce8\u610f\u7740\u7b2c\u4e09\u9876\u8f7f\u5b50\u3002 \u3000\u3000\u2014\u2014\u6ce2\u5a03\u662f\u4e0d\u662f\u9a6c\u4e0a\u5c31\u8981\u4ece\u8fd9\u9876\u8f7f\u5b50\u91cc\u8d70\u51fa\u6765\u4e86\uff1f \u3000\u3000\u4ed6\u7684\u5fc3\u5728\u8df3\u52a8\uff0c\u5728\u523a\u75db\uff0c\u8df3\u5f97\u5f88\u5feb\uff0c\u75db\u4eba\u9aa8\u9ad3\u3002 \u3000\u3000\u4ed6\u5728\u5c3d\u529b\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u4e0d\u8ba9\u8138\u4e0a\u9732\u51fa\u4e00\u70b9\u75db\u82e6\u7684\u8868\u60c5\u6765\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u7b2c\u4e09\u9876\u8f7f\u4e8e\u91cc\u4e00\u76f4\u90fd\u6ca1\u6709\u4eba\u8d70\u51fa\u6765\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e00\u8dc3\u4e0b\u9a6c\uff0c\u8ddf\u7740\u641c\u9b42\u624b\u548c\u67f3\u5206\u5206\u8d70\u5165\u4e86\u5e10\u7bf7\u3002 \u3000\u3000\u5e10\u7bf7\u4e0a\u7684\u9ed1\u8272\u9e70\u7fbd\u5728\u98ce\u4e2d\u6447\u52a8\uff0c\u4eff\u4f5b\u6b63\u5728\u5411\u4eba\u4eec\u5ba3\u793a\u5b83\u6240\u8c61\u5f81\u7684\u4e0d\u7965\u542b\u4e49\uff1a\u75be\u75c5\uff0c\u707e\u7978\uff0c\u6b7b\u4ea1\uff01 \u3000\u3000\u4f46\u662f\u8fd9\u4e9b\u4e8b\u5c0f\u65b9\u5e76\u4e0d\u5728\u4e4e\uff0c\u75be\u75c5\u3001\u707e\u7978\u3001\u6b7b\u4ea1\uff0c\u4ed6\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4ed6\u5728\u4e4e\u7684\u53ea\u6709\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u2014\u2014\u7b2c\u4e09\u9876\u8f7f\u5b50\u91cc\u7a76\u7adf\u6709\u6ca1\u6709\u4eba\uff1f\u5982\u679c\u6709\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u6765\uff1f\u5982\u679c\u6ca1\u6709\u4eba\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u628a\u4e00\u9876\u7a7a\u8f7f\u5b50\u62ac\u6765\uff1f \u3000\u3000\u535c\u9e70\u4ecd\u7136\u7aef\u5750\u4e0d\u52a8\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u3002 \u3000\u3000\u201c\u535c\u5927\u8001\u677f\u7684\u67b6\u5b50\u679c\u7136\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d\u67f3\u5206\u5206\u4e5f\u5728\u7b11\uff0c\u201c\u73b0\u5728\u6211\u5df2\u7ecf\u770b\u51fa\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u67b6\u5b50\u5927\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5ffd\u7136\u53d8\u4e86\uff0c\u53d8\u5f97\u5c11\u5973\u822c\u6e29\u67d4\u5a07\u5a9a\uff1a\u201c\u4ed6\u6ca1\u6709\u7ad9\u8d77\u6765\u8fce\u63a5\u6211\u4eec\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u53d7\u4e86\u4f24\uff0c\u6211\u4eec\u600e\u4e48\u80fd\u602a\u4ed6\uff1f\u201d \u3000\u3000\u535c\u9e70\u7adf\u7136\u627f\u8ba4\u3002 \u3000\u3000\u201c\u6211\u4e0d\u4f46\u53d7\u4e86\u4f24\uff0c\u800c\u4e14\u4f24\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e5f\u4e0d\u5fc5\u592a\u96be\u53d7\u3002\u201d\u67f3\u5206\u5206\u7684\u58f0\u97f3\u66f4\u6e29\u67d4\uff0c\u201c\u80fd\u591f\u5728\u72ec\u5b64\u5251\u4e0b\u4fdd\u4f4f\u6027\u547d\u7684\u4eba\uff0c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u597d\u50cf\u8fd8\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u70b9\u90fd\u4e0d\u96be\u53d7\uff0c\u201d\u535c\u9e70\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u72ec\u5b64\u73b0\u5728\u4e5f\u672a\u5fc5\u5f88\u597d\u53d7\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u5c45\u7136\u540c\u610f\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u90a3\u4e00\u6218\u4e5f\u4e0d\u80fd\u7b97\u662f\u4f60\u8d25\u4e86\uff0c\u6240\u4ee5\u535c\u5927\u8001\u677f\u8fd8\u662f\u6c38\u8fdc\u4e0d\u8d25\u7684\uff01\u201d \u3000\u3000\u5979\u67d4\u58f0\u63a5\u7740\u9053\uff1a\u201c\u81f3\u5c11\u76f4\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u8fd8\u6ca1\u6709\u8d25\u8fc7\uff0c\u8fde\u4e00\u6b21\u90fd\u6ca1\u6709\u8d25\u8fc7\u3002\u201d \u3000\u3000\u641c\u9b42\u624b\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u4e0b\u4e00\u6b21\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0b\u4e00\u6b21\u4ed6\u4e5f\u4e0d\u4f1a\u8d25\u3002\u201d\u67f3\u5206\u5206\u5403\u5403\u5730\u7b11\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e00\u6b21\u4ed6\u82e5\u4e0d\u80af\u7b54\u5e94\u6211\u4eec\u7684\u8981\u6c42\uff0c\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u4e0b\u4e00\u6b21\u4e86\u3002\u3002 \u3000\u3000\u535c\u9e70\u95ee\uff1a\u201c\u4f60\u4eec\u8981\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u8981\u7684\u662f\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u548c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u5df2\u7ecf\u6d3e\u4eba\u6765\u641c\u67e5\u8fc7\uff0c\u5df2\u7ecf\u5e94\u8be5\u77e5\u9053\u9ec4\u91d1\u4e0d\u5728\u8fd9\u91cc\u3002\u3002 \u3000\u3000\u536b\u5929\u9e4f\u53c8\u5728\u51b7\u7b11\uff1a\u201c\u4e0d\u5728\u8fd9\u91cc\u5728\u54ea\u91cc\uff1f\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5df2\u5c06\u8fd9\u5730\u533a\u5b8c\u5168\u641c\u67e5\u8fc7\u3002\u201d\u536b\u5929\u9e4f\u9053\u3001\u201c\u9664\u4e86\u4f60\u4eec\u5916\uff0c\u7edd\u6ca1\u6709\u522b\u4eba\u80fd\u4ece\u94c1\u7ffc\u624b\u4e0a\u52ab\u8d70\u90a3\u6279\u9ec4\u91d1\uff0c\u6240\u4ee5\u9ec4\u91d1\u5c31\u7b97\u4e0d\u5728\u4f60\u4eec\u8981\u5e26\u8d70\u7684\u8fd9\u6279\u8d27\u7269\u91cc\uff0c\u4e5f\u4e00\u5b9a\u662f\u88ab\u4f60\u4eec\u85cf\u8d77\u6765\u4e86\u3002\u3002 \u3000\u3000\u67f3\u5206\u5206\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u6837\u51f6\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u627f\u8ba4\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u6709\u6cd5\u5b50\u8ba9\u4ed6\u627f\u8ba4\uff1f\u201d \u3000\u3000\u67f3\u5206\u5206\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u901a\u5e38\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\u89e3\u51b3\uff0c\u8fd9\u79cd\u6cd5\u5b50\u867d\u7136\u5f88\u4fd7\u6c14\uff0c\u5374\u662f\u6700\u53e4\u8001\u3001\u6700\u6709\u6548\u7684\u4e00\u79cd\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5ffd\u7136\u53c8\u53d8\u4e86\u2026\u2026\u53d8\u5f97\u5c16\u9510\u800c\u51b7\u9177\uff1a\u201c\u80dc\u8005\u4e3a\u5f3a\uff0c\u8d25\u8005\u906d\u6b83\u3002\u5982\u679c\u4ed6\u4eec\u8d25\u5728\u6211\u4eec\u624b\u91cc\uff0c\u5c31\u7b97\u9ec4\u91d1\u4e0d\u662f\u88ab\u4ed6\u4eec\u52ab\u8d70\u7684\uff0c\u4ed6\u4eec\u4e5f\u5f97\u60f3\u522b\u7684\u6cd5\u5b50\u628a\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u4ea4\u51fa\u6765\u3002\u201d \u3000\u3000\u641c\u9b42\u624b\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u542c\u6765\u597d\u50cf\u5f88\u4e0d\u9519\uff0c\u8981\u535c\u5927\u8001\u677f\u4ea4\u51fa\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u6765\uff0c\u597d\u50cf\u5e76\u4e0d\u96be\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u4ed6\u4e00\u5b9a\u80fd\u4ea4\u5f97\u51fa\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u5e76\u4e0d\u60f3\u591a\u4f24\u65e0\u8f9c\uff0c\u6240\u4ee5\u6211\u4eec\u53ea\u6765\u4e86\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u641c\u9b42\u624b\u9053\uff1a\u201c\u6211\u4eec\u4e09\u9635\u8d4c\u8f93\u8d62\uff0c\u5c31\u8d4c\u90a3\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u548c\u90a3\u4e2a\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4eec\u80fd\u5c06\u6211\u4eec\u4e09\u4e2a\u4eba\u5168\u90fd\u51fb\u8d25\uff0c\u6211\u4eec\u4ece\u6b64\u4e0d\u518d\u95ee\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u641c\u9b42\u624b\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u4eec\u8981\u627e\u7684\u5bf9\u624b\u662f\u8c01\uff0c\u5c0f\u65b9\u603b\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u8f6c\u8fc7\u8eab\u3002 \u3000\u3000\u5728\u521a\u624d\u90a3\u7247\u523b\uff0c\u4ed6\u6709\u51e0\u6b21\u90fd\u60f3\u51b2\u8fc7\u53bb\uff0c\u770b\u770b\u90a3\u9876\u8f7f\u5b50\u91cc\u662f\u4e0d\u662f\u6709\u4eba\uff0c\u770b\u770b\u6ce2\u5a03\u662f\u4e0d\u662f\u5728\u90a3\u8f7f\u5b50\u91cc\u3002 \u3000\u3000\u4ed6\u51e0\u6b21\u90fd\u5fcd\u4f4f\u3002 \u3000\u3000\u770b\u89c1\u4e86\u53c8\u5982\u4f55\uff1f\u53c8\u80fd\u8bc1\u660e\u4ec0\u4e48\uff1f\u6539\u53d8\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u8f6c\u8eab\u9762\u5bf9\u641c\u9b42\u624b\uff1a\u201c\u6211\u5c31\u662f\u5c0f\u65b9\uff0c\u5c31\u662f\u4f60\u8981\u627e\u7684\u4eba\u3002\u4f60\u662f\u4e0d\u662f\u73b0\u5728\u5c31\u60f3\u51fa\u624b\uff1f\u201d \u3000\u3000\u641c\u9b42\u624b\u6ca1\u6709\u5f00\u53e3\uff0c\u535c\u9e70\u5374\u66ff\u4ed6\u56de\u7b54\uff1a\u201c\u4ed6\u4e0d\u60f3\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u6839\u672c\u5c31\u4e0d\u662f\u771f\u7684\u60f3\u627e\u4f60\u8fd9\u4e2a\u5bf9\u624b\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u5341\u62db\u4e4b\u5185\uff0c\u4f60\u5c31\u53ef\u4ee5\u5c06\u4ed6\u523a\u6740\u5728\u5251\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u660e\u660e\u5df2\u627e\u4e0a\u4e86\u6211\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u90a3\u53ea\u4e0d\u8fc7\u662f\u4ed6\u4eec\u7684\u6218\u7565\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u61c2\u3002 \u3000\u3000\u201c\u6218\u7565\uff1f\u4ec0\u4e48\u6218\u7565\uff1f\u201d \u3000\u3000\u201c\u6211\u53d7\u4e86\u4f24\uff0c\u73ed\u5bdf\u5df4\u90a3\u662f\u85cf\u4eba\uff0c\u4ed6\u4eec\u4e00\u5411\u8ba4\u4e3a\u85cf\u4eba\u4e2d\u6ca1\u6709\u771f\u6b63\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u535c\u9e70\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u4eec\u771f\u6b63\u63d0\u9632\u7684\u4eba\u53ea\u6709\u4f60\uff0c\u6240\u4ee5\u4ed6\u4eec\u8981\u641c\u9b42\u624b\u5148\u9009\u4f60\u505a\u5bf9\u624b\uff0c\u56e0\u4e3a\u4ed6\u7684\u6b66\u529f\u6700\u5f31\uff0c\u4ee5\u6700\u5f31\u7684\u4eba\u5bf9\u6700\u5f3a\u7684\uff0c\u4ee5\u4e0b\u9a77\u5bf9\u4e0a\u9a77\uff0c\u5269\u4e0b\u7684\u4e24\u9635\uff0c\u4ed6\u4eec\u5c31\u5fc5\u80dc\u65e0\u7591\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u6625\u79cb\u65f6\u5175\u6cd5\u5bb6\u7684\u6218\u7565\uff0c\u53ea\u8981\u8fd0\u7528\u5f97\u5f53\uff0c\u901a\u5e38\u90fd\u5341\u5206\u6709\u6548\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u53c8\u51b7\u7b11\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u4e00\u6b21\u4ed6\u4eec\u7684\u6218\u7565\u7528\u9519\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u9519\u5728\u4f60\u4eec\u6839\u672c\u5c31\u6ca1\u770b\u51fa\u8fd9\u91cc\u8c01\u624d\u662f\u771f\u6b63\u7684\u7edd\u9876\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u8fd9\u91cc\u8fd8\u6709\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4e2a\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u613f\u610f\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u593a\u4e0b\u4f60\u7684\u5200\uff0c\u62d7\u65ad\u4f60\u7684\u5f13\u7bad\uff0c\u518d\u987a\u624b\u6253\u4f60\u4e03\u516b\u4e2a\u8033\u5149\uff0c\u628a\u4f60\u4e00\u811a\u8e22\u51fa\u53bb\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u7b11\u4e86\uff0c\u5927\u7b11\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u535c\u5927\u8001\u677f\u8bf4\u7684\u8bdd\uff0c\u6211\u600e\u4e48\u6562\u4e0d\u4fe1\uff1f\u53ea\u4e0d\u8fc7\u50cf\u535c\u5927\u8001\u677f\u8bf4\u7684\u8fd9\u79cd\u4eba\u6211\u975e\u4f46\u6ca1\u6709\u89c1\u8fc7\uff0c\u8fde\u542c\u90fd\u6ca1\u6709\u542c\u8fc7\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u542c\u8fc7\u4e86\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u89c1\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5f88\u60f3\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e0d\u59a8\u8d76\u5feb\u62d4\u5200\uff0c\u53ea\u8981\u4f60\u4e00\u62d4\u5200\uff0c\u5c31\u53ef\u4ee5\u89c1\u5230\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u6ca1\u6709\u62d4\u5200\u3002 \u3000\u3000\u4ed6\u7684\u5200\u5728\u8170\uff0c\u540d\u9707\u6c5f\u6e56\u7684\u65a9\u9b3c\u5200\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u63e1\u4f4f\u5200\u67c4\u3002 \u3000\u3000\u4ed6\u62d4\u5200\u7684\u59ff\u6001\u65e0\u61c8\u53ef\u51fb\uff0c\u62d4\u5200\u7684\u52a8\u4f5c\u4e5f\u540c\u6837\u6b63\u786e\u8fc5\u901f\uff0c\u6c5f\u6e56\u4e2d\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4\u5f97\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u5200\u4e00\u62d4\u51fa\u6765\uff0c\u5fc5\u5b9a\u89c1\u8840\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u62d4\u5200\u3002 \u3000\u3000\u5e10\u7bf7\u91cc\u9664\u4e86\u4ed6\u4eec\u81ea\u5df1\u4e09\u4e2a\u4eba\u548c\u5c0f\u65b9\u3001\u535c\u9e70\u3001\u73ed\u5bdf\u5df4\u90a3\uff0c\u53ea\u6709\u4e24\u4f4d\u8001\u5148\u751f\u3002 \u3000\u3000\u4e25\u6b63\u521a\u523b\u677f\u65b9\u6b63\uff0c\u6ca1\u6709\u4e00\u70b9\u6b66\u6797\u9ad8\u624b\u7684\u7075\u6c14\u548c\u6740\u6c14\u3002 \u3000\u3000\u5b8b\u8001\u592b\u5b50\u770b\u6765\u66f4\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8001\u773c\u660f\u82b1\u3001\u8001\u6001\u9f99\u949f\u7684\u8001\u5b66\u7a76\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u770b\u6765\u90fd\u7edd\u4e0d\u50cf\u662f\u9ad8\u624b\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4eec\u8fd8\u6709\u8c01\uff0c \u3000\u3000\u536b\u5929\u9e4f\u770b\u4e0d\u51fa\uff0c\u6240\u4ee5\u4ed6\u6ca1\u6709\u62d4\u5200\u3002\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u4ece\u672a\u505a\u8fc7\u6ca1\u628a\u63e1\u7684\u4e8b\u3002 \u3000\u3000\u67f3\u5206\u5206\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u535c\u5927\u8001\u677f\u4e5f\u5e94\u8be5\u4e86\u89e3\u4ed6\u8fd9\u4e2a\u4eba\uff0c\u8981\u4ed6\u62d4\u5200\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u6211\u5c31\u4e0d\u540c\u4e86\uff0c\u8981\u6211\u51fa\u624b\u5f88\u5bb9\u6613\u3002\u201d \u3000\u3000\u5979\u5c11\u5973\u822c\u7684\u8138\u4e0a\u53c8\u9732\u51fa\u751c\u7f8e\u7684\u7b11\u5bb9\uff1a\u201c\u6211\u51fa\u624b\u662f\u4e0d\u662f\u4e5f\u4e00\u6837\u80fd\u89c1\u5230\uff1f\u201d \u3000\u3000\u535c\u9e70\u7684\u56de\u7b54\u660e\u786e\uff1a\u201c\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u5fae\u7b11\uff1a\u201c\u90a3\u5c31\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u5e10\u7bf7\u91cc\u6709\u4e24\u5f20\u4f4e\u51e0\u548c\u51e0\u4e2a\u517d\u76ae\u7f1d\u6210\u7684\u5750\u57ab\uff0c\u67f3\u5206\u5206\u6162\u6162\u5730\u5750\u4e0b\uff0c\u5c06\u624b\u91cc\u7684\u9ec4\u5e03\u5305\u88b1\u653e\u5728\u51e0\u4e0a\uff0c\u7528\u90a3\u53ea\u6625\u8471\u822c\u7684\u7389\u624b\u53bb\u89e3\u5305\u88b1\u4e0a\u7684\u7ed3\u3002 \u3000\u3000\u5979\u5df2\u51c6\u5907\u51fa\u624b\uff0c\u5305\u88b1\u91cc\u65e0\u7591\u5c31\u662f\u5979\u6740\u4eba\u7684\u5229\u5668\uff0c\u4e00\u79cd\u7edd\u4e0d\u662f\u5c5e\u4e8e\u5979\u201c\u4eba\u201d\u90a3\u4e00\u534a\u7684\u6740\u4eba\u5229\u5668\uff01 \u3000\u3000\u4e00\u79cd\u5df2\u63a5\u8fd1\u201c\u9b54\u201d\u7684\u6740\u4eba\u5229\u5668\uff01 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e5d\u7ae0\u3000\u53e6\u5916\u4e00\u53ea\u624b \u7b2c\u4e5d\u7ae0\u3000\u53e6\u5916\u4e00\u53ea\u624b \u3000\u3000\u5305\u88b1\u5df2\u89e3\u5f00\uff0c\u5305\u88b1\u91cc\u53ea\u6709\u5341\u4e09\u4ef6\u95ea\u52a8\u7740\u6697\u9ed1\u5149\u8292\u7684\u94c1\u5668\u3002\u6bcf\u4e00\u4ef6\u7684\u5f62\u72b6\u90fd\u5f88\u602a\u5f02\uff0c\u6709\u7684\u770b\u6765\u5982\u73af\u6263\uff0c\u6709\u7684\u770b\u6765\u5982\u9aa8\u8282\u3002 \u3000\u3000\u8c01\u4e5f\u770b\u4e0d\u51fa\u8fd9\u662f\u4ec0\u4e48\u5175\u5203\uff0c\u4e16\u4e0a\u6839\u672c\u6ca1\u6709\u8fd9\u6837\u7684\u5175\u5203\u3002 \u3000\u3000\u67f3\u5206\u5206\u89e3\u91ca\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u7684\u53e6\u5916\u4e00\u53ea\u624b\u3002\u201d \u3000\u3000\u5979\u4f38\u51fa\u4e86\u5979\u90a3\u53ea\u7ea4\u67d4\u7f8e\u4e3d\u7684\u624b\uff1a\u201c\u6211\u7684\u8fd9\u53ea\u624b\u8ddf\u522b\u4eba\u7684\u5b8c\u5168\u6ca1\u4ec0\u4e48\u4e0d\u540c\uff0c\u6211\u7a7f\u8863\u3001\u5403\u996d\u3001\u6d17\u8138\u3001\u6f31\u53e3\uff0c\u90fd\u662f\u7528\u8fd9\u53ea\u624b\uff0c\u5076\u5c14\u6211\u4e5f\u4f1a\u7528\u8fd9\u53ea\u624b\u53bb\u629a\u6478\u6211\u559c\u6b22\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u53e6\u5916\u8fd9\u53ea\u624b\u5462\uff1f\u201d\u535c\u9e70\u95ee\u3002 \u3000\u3000\u67f3\u5206\u5206\u7b11\u4e86\uff0c\u7b11\u5bb9\u5ffd\u7136\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u90aa\u6076\u8be1\u79d8\uff1a\u201c\u4f60\u4eec\u90fd\u5e94\u8be5\u770b\u5f97\u51fa\uff0c\u8fd9\u7edd\u4e0d\u662f\u4e00\u53ea\u4eba\u7684\u624b\u3002\u201d\u5979\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u662f\u9b54\u624b\uff0c\u662f\u7528\u5341\u516b\u5c42\u5730\u72f1\u4e0b\u7684\u9b54\u706b\u70bc\u6210\u7684\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u5377\u8d77\u8863\u8896\uff0c\u4ece\u90a3\u6761\u5df2\u88ab\u9f50\u65f6\u780d\u65ad\u7684\u624b\u81c2\u9aa8\u8282\u91cc\uff0c\u62bd\u51fa\u4e86\u4e00\u6839\u4e4c\u9ed1\u7684\u94a2\u4e1d\u3002 \u3000\u3000\u7136\u540e\u518d\u628a\u5176\u4f59\u8fd9\u5341\u4e09\u4ef6\u94c1\u5668\u5168\u90fd\u63a5\u8fde\u5728\u780d\u65ad\u7684\u624b\u81c2\u4e0a\uff0c\u63a5\u6210\u4e00\u6761\u602a\u5f02\u800c\u5947\u7279\u7684\u94c1\u81c2\u3002 \u3000\u3000\u6700\u540e\u4e00\u4e00\u8282\u662f\u4e2a\u94a2\u722a\u3002 \u3000\u3000\u5979\u628a\u65ad\u81c2\u4e2d\u62bd\u51fa\u7684\u90a3\u6761\u94a2\u4e1d\uff0c\u7ed3\u4e0a\u8fd9\u6700\u540e\u4e00\u8282\u94a2\u722a\u7684\u673a\u7c27\u73af\u6263\u3002 \u3000\u3000\u8fd9\u6761\u672c\u6765\u660e\u660e\u662f\u7528\u9ed1\u94c1\u70bc\u6210\u3001\u6ca1\u6709\u8840\u3001\u6ca1\u6709\u8089\u3001\u6ca1\u6709\u751f\u547d\u7684\u94c1\u81c2\uff0c\u5ffd\u7136\u53d8\u5f97\u6709\u4e86\u751f\u547d\uff0c\u5ffd\u7136\u5f00\u59cb\u5f2f\u66f2\u3001\u626d\u52a8\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u4ece\u4efb\u4f55\u4e00\u4e2a\u90e8\u4f4d\uff0c\u5411\u4efb\u4f55\u4e00\u4e2a\u65b9\u5411\u5f2f\u66f2\u626d\u52a8\u3002 \u3000\u3000\u6700\u540e\u4e00\u8282\u94a2\u722a\uff0c\u4e5f\u914d\u5408\u7740\u94c1\u81c2\u7684\u52a8\u4f5c\uff0c\u5ffd\u7136\u5f2f\u8f6c\uff0c\u6293\u4f4f\u4e86\u5979\u81ea\u5df1\u8fd9\u6761\u624b\u81c2\u7684\u540e\u65f6\u3002 \u3000\u3000\u8fd9\u79cd\u52a8\u4f5c\u662f\u4efb\u4f55\u4eba\u90fd\u7edd\u5bf9\u505a\u4e0d\u5230\u7684\uff0c\u53ef\u662f\u5979\u80fd\u505a\u5f97\u5230\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u8fd9\u53ea\u624b\uff0c\u6839\u672c\u4e0d\u662f\u4eba\u7684\u624b\u3002 \u3000\u3000\u5979\u5ffd\u7136\u8f6c\u8eab\u770b\u7740\u5c0f\u65b9\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u628a\u4f60\u7684\u624b\u4f38\u51fa\u6765\u7ed9\u6211\u770b\u770b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4f38\u51fa\u4e86\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u638c\u5bbd\u5927\u3001\u575a\u5b9e\u3001\u5e72\u71e5\uff0c\u4ed6\u7684\u624b\u6307\u957f\u800c\u6709\u529b\u3002 \u3000\u3000\u67f3\u5206\u5206\u5fae\u7b11\uff1a\u201c\u4f60\u6709\u53cc\u5f88\u597d\u770b\u7684\u624b\uff0c\u800c\u4e14\u5f88\u6709\u7528\uff0c\u4f60\u7528\u8fd9\u53cc\u624b\u63e1\u5251\u7684\u65f6\u5019\uff0c\u4efb\u4f55\u4eba\u90fd\u5f88\u96be\u5c06\u4f60\u7684\u5251\u51fb\u843d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u624b\u91cc\u7684\u5251\u4ece\u672a\u88ab\u4eba\u51fb\u843d\u8fc7\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u624b\u91cc\u6ca1\u6709\u5251\u7684\u65f6\u5019\u5462\uff1f\u201d\u67f3\u5206\u5206\u95ee\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u51ed\u7a7a\u53d8\u51fa\u4e00\u628a\u5251\u6765\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u3002 \u3000\u3000\u201c\u6211\u80fd\u3002\u201d\u67f3\u5206\u5206\u8bf4\u3002 \u3000\u3000\u5979\u7684\u94c1\u81c2\u4e00\u626d\uff0c\u94a2\u722a\u5f39\u51fa\uff1a\u201c\u8fd9\u5c31\u662f\u4e00\u628a\u5251\uff0c\u6211\u5df2\u7528\u8fd9\u628a\u5251\u523a\u7a7f\u8fc7\u4e8c\u5341\u4e03\u4e2a\u4eba\u7684\u54bd\u5589\u3002\u201d \u3000\u3000\u5c0f\u65b9\u51b7\u51b7\u9053\uff1a\u201c\u4e8c\u5341\u4e03\u4e2a\u4eba\u4e5f\u4e0d\u80fd\u7b97\u591a\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u683c\u683c\u5730\u7b11\u9053\uff1a\u201c\u6211\u6740\u7684\u4eba\u5f53\u7136\u4e0d\u6b62\u4e8c\u5341\u516d\u4e2a\uff0c\u56e0\u4e3a\u6211\u8fd9\u53ea\u624b\u91cc\u8fd8\u85cf\u7740\u8ff7\u9999\u3001\u6bd2\u6c41\u548c\u53e6\u5916\u5341\u4e09\u79cd\u6697\u5668\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u5c04\u51fa\u6765\uff0c\u8981\u4eba\u7684\u547d\uff01\u4f46\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u5b83\u4f1a\u5728\u4ec0\u4e48\u65f6\u5019\u5c04\u51fa\u6765\uff0c\u4ece\u4ec0\u4e48\u5730\u65b9\u5c04\u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u5979\u8fd9\u53ea\u624b\u5b9e\u5728\u662f\u79cd\u53ef\u6015\u7684\u6b66\u5668\u3002 \u3000\u3000\u67f3\u5206\u5206\u7684\u94c1\u81c2\u53c8\u4e00\u626d\uff0c\u94a2\u722a\u518d\u6b21\u5f39\u51fa\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e09\u5bf8\u539a\u7684\u6728\u51e0\uff0c\u5df2\u88ab\u523a\u7a7f\u4e86\u4e00\u4e2a\u6d1e\uff0c\u4e00\u7f15\u9752\u70df\u8885\u8885\u6563\u51fa\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u4eec\u60f3\u5fc5\u4e5f\u5df2\u770b\u51fa\uff0c\u6211\u8fd9\u628a\u5251\u4e0a\u8fd8\u6dec\u4e86\u6bd2\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u7edd\u5bf9\u6ca1\u6551\u3002\u201d \u3000\u3000\u5979\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u6728\u51e0\u4e0a\u90a3\u7834\u6d1e\u7684\u56db\u5468\uff0c\u7adf\u5df2\u5b8c\u5168\u7126\u88c2\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u5df2\u7ecf\u51c6\u5907\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u5979\u5a9a\u773c\u4e2d\u5149\u8292\u5982\u86c7\u874e\uff0c\u6162\u6162\u5730\u4ece\u5c0f\u65b9\u3001\u535c\u9e70\u3001\u73ed\u5bdf\u5df4\u90a3\u4e09\u4e2a\u4eba\u8138\u4e0a\u626b\u8fc7\u3002 \u3000\u3000\u7136\u540e\u5979\u624d\u8f7b\u8f7b\u5730\u95ee\uff1a\u201c\u4f60\u4eec\u8981\u6211\u5bf9\u8c01\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u201d\u4e00\u4e2a\u4eba\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u65e9\u5df2\u5728\u7b49\u7740\u4f60\u51fa\u624b\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u8fd9\u4e2a\u4eba\u7adf\u4e0d\u662f\u5979\u770b\u7740\u7684\u4e09\u4e2a\u4eba\uff0c\u800c\u662f\u770b\u6765\u6700\u4e0d\u53ef\u80fd\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u7684\u5b8b\u8001\u592b\u5b50\u3002 \u3000\u3000\u201c\u4f60\uff1f\u201d\u67f3\u5206\u5206\u4e5f\u663e\u5f97\u5f88\u60ca\u8bb6\uff0c\u201c\u662f\u4f60\uff1f\u201d \u3000\u3000\u5b8b\u8001\u592b\u5b50\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u6709\u70b9\u6015\u4f60\u8fd9\u53ea\u624b\uff0c\u66f4\u4e0d\u60f3\u8981\u4f60\u7528\u8fd9\u53ea\u624b\u6765\u5bf9\u4ed8\u6211\uff0c\u53ea\u53ef\u60dc\u8fd9\u91cc\u504f\u504f\u53ea\u6709\u6211\u4e00\u4e2a\u4eba\u80fd\u5bf9\u4ed8\u4f60\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u201c\u53ea\u6709\u4f60\u80fd\u5bf9\u4ed8\u6211\uff1f\u201d\u5979\u7684\u7b11\u5bb9\u53c8\u53d8\u5f97\u5341\u5206\u6e29\u67d4\uff0c\u201c\u4f60\u51c6\u5907\u7528\u4ec0\u4e48\u5bf9\u4ed8\u6211\uff1f\u201d \u3000\u3000\u201c\u7528\u6211\u7684\u53e6\u5916\u4e00\u53ea\u624b\u3002\u201d\u5b8b\u8001\u592b\u5b50\u9053\uff0c\u201c\u4f60\u6709\u53e6\u5916\u4e00\u53ea\u624b\uff0c\u6211\u4e5f\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u6709\uff1f\u201d \u3000\u3000\u67f3\u5206\u5206\u770b\u7740\u4ed6\u6446\u5728\u684c\u4e0a\u7684\u4e00\u53cc\u67af\u7626\u7684\u624b\uff1a\u201c\u4f60\u7684\u4e24\u53ea\u624b\u597d\u50cf\u90fd\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5b8b\u8001\u592b\u5b50\u5fae\u7b11\uff1a\u201c\u4f60\u7684\u53e6\u5916\u4e00\u53ea\u624b\uff0c\u662f\u7b2c\u4e8c\u53ea\u624b\uff0c\u6211\u53e6\u5916\u90a3\u53ea\u624b\uff0c\u662f\u7b2c\u4e09\u53ea\u624b\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5f97\u5f88\u6109\u5feb\uff1a\u201c\u6211\u7684\u8fd9\u53cc\u624b\uff0c\u4e5f\u8ddf\u522b\u7684\u4eba\u6ca1\u4ec0\u4e48\u4e0d\u540c\uff0c\u6211\u7a7f\u8863\u3001\u5403\u996d\u3001\u6d17\u8138\u3001\u6f31\u53e3\uff0c\u90fd\u7528\u8fd9\u53cc\u624b\uff0c\u5076\u5c14\u6211\u4e5f\u4f1a\u7528\u8fd9\u53cc\u624b\u53bb\u629a\u6478\u5973\u4eba\u7684\u2026\u2026\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\uff01 \u3000\u3000\u201c\u4f60\u901a\u5e38\u6478\u7684\u90fd\u662f\u5973\u4eba\u8eab\u4e0a\u7684\u54ea\u4e9b\u5730\u65b9\uff0c\u7528\u4e0d\u7740\u8bf4\u51fa\u6765\u522b\u4eba\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u5b8b\u8001\u592b\u5b50\u9053\uff1a\u201c\u53ef\u662f\u6211\u53e6\u5916\u90a3\u53ea\u624b\uff0c\u7528\u5904\u5c31\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u5ffd\u7136\u4e5f\u53d8\u5f97\u5f88\u8be1\u79d8\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u770b\u770b\u6211\u90a3\u53ea\u624b\uff1f\u201d \u3000\u3000\u67f3\u5206\u5206\u5a9a\u7b11\uff1a\u201c\u6211\u60f3\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u201c\u597d\u3002\u201d\u5b8b\u8001\u592b\u5b50\u9053\uff1a\u201c\u4f60\u770b\u7740\u3002\u201d \u3000\u3000\u4ed6\u7684\u4e00\u53cc\u624b\u672c\u6765\u5c31\u6446\u5728\u51e0\u4e0a\uff0c\u5341\u6839\u624b\u6307\u5e73\u5e73\u5730\u4f38\u5c55\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u5728\u770b\u7740\u81ea\u5df1\u7684\u8fd9\u53cc\u624b\u3002 \u3000\u3000\u67f3\u5206\u5206\u5f53\u7136\u66f4\u4e0d\u80fd\u4e0d\u770b\uff0c\u536b\u5929\u9e4f\u548c\u641c\u9b42\u624b\u4e5f\u6ca1\u6cd5\u5b50\u4e0d\u53bb\u770b\u3002 \u3000\u3000\u7f8a\u89d2\u706f\u5728\u98ce\u4e2d\u6447\u66f3\uff0c\u706f\u5149\u95ea\u52a8\u4e0d\u505c\u3002 \u3000\u3000\u4ed6\u7684\u4e00\u53cc\u67af\u5e72\u7684\u624b\u5ffd\u7136\u53d8\u4e86\uff0c\u4e0d\u4f46\u989c\u8272\u53d8\u4e86\uff0c\u5f62\u72b6\u4e5f\u53d8\u4e86\u3002\u672c\u6765\u6beb\u65e0\u8840\u8272\u7684\u624b\uff0c\u5ffd\u7136\u53d8\u5f97\u8840\u7ea2\uff0c\u672c\u6765\u67af\u7626\u65e0\u8089\u7684\u624b\uff0c\u5ffd\u7136\u53d8\u5f97\u5065\u58ee\u6709\u529b\uff0c\u5c31\u597d\u50cf\u4e00\u5bf9\u7a7a\u76ae\u7fb9\u4e2d\uff0c\u5ffd\u7136\u88ab\u585e\u5165\u4e86\u8840\u8089\u3002 \u3000\u3000\u770b\u7740\u4ed6\u8fd9\u53cc\u624b\u7684\u4eba\u8138\u8272\u4e5f\u53d8\u4e86\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u7136\u6709\u53e6\u4e00\u53ea\u624b\u95ea\u7535\u822c\u4f38\u51fa\uff0c\u201c\u683c\u201d\u7684\u4e00\u54cd\uff0c\u67f3\u5206\u5206\u65ad\u81c2\u4e0a\u7684\u94c1\u624b\u5df2\u88ab\u5378\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f \u3000\u3000\u8fd9\u53ea\u624b\u672c\u6765\u5c31\u5728\uff0c\u5728\u4e25\u6b63\u521a\u8eab\u4e0a\uff0c\u6bcf\u4e2a\u4eba\u90fd\u770b\u89c1\u4e86\u8fd9\u53ea\u624b\uff0c\u53ef\u662f\u6ca1\u6709\u4eba\u60f3\u5230\u8fd9\u5c31\u662f\u5b8b\u8001\u592b\u5b50\u7684\u201c\u53e6\u5916\u4e00\u53ea\u624b\u201d\u3002 \u3000\u3000\u73b0\u5728\u67f3\u5206\u5206\u7684\u94c1\u81c2\u5df2\u7ecf\u5230\u4e25\u6b63\u521a\u624b\u91cc\u3002 \u3000\u3000\u67f3\u5206\u5206\u8138\u8272\u60e8\u53d8\u3002 \u3000\u3000\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b97\u4f60\u8d25\u4e86\u3002\u201d\u5b8b\u8001\u592b\u5b50\u772f\u7740\u773c\u7b11\uff0c\u201c\u4e09\u9635\u8d4c\u8f93\u8d62\uff0c\u7b2c\u4e00\u9635\u4f60\u4eec\u5df2\u8d25\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u80fd\u7b97\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u7b97\uff1f\u201d \u3000\u3000\u5b8b\u8001\u592b\u5b50\u9053\uff1a\u201c\u4f60\u7684\u53e6\u5916\u4e00\u53ea\u624b\u5728\u5305\u88b1\u91cc\uff0c\u6211\u7684\u53e6\u5916\u4e00\u53ea\u624b\u5728\u522b\u4eba\u90a3\u91cc\uff0c\u6211\u4eec\u4e24\u53ea\u624b\u672c\u6765\u90fd\u540c\u6837\u4e0d\u5728\u81ea\u5df1\u8eab\u624b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u4eec\u4e24\u4e2a\u4eba\u5bf9\u4ed8\u6211\u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u8c01\u8bf4\u6211\u4eec\u662f\u4e24\u4e2a\u4eba\uff1f\u51fa\u624b\u7684\u662f\u4ed6\uff0c\u6211\u7684\u624b\u6839\u672c\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u8fc7\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u5c11\u5973\u822c\u7684\u8138\uff0c\u597d\u50cf\u5ffd\u7136\u5c31\u8001\u4e86\u4e8c\u4e09\u5341\u5c81\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u662f\u4e2a\u5708\u5957\uff0c\u53ef\u662f\u73b0\u5728\u5979\u5df2\u7ecf\u6389\u4e86\u8fdb\u53bb\uff0c\u5979\u8fd8\u80fd\u600e\u4e48\u6837\uff1f \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u94c1\u9752\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u4f69\u670d\u3002\u201d \u3000\u3000\u201c\u4f60\u4f69\u670d\u6211\uff1f\u201d\u5b8b\u8001\u592b\u5b50\u7b11\u5f97\u66f4\u6109\u5feb\u3002 \u3000\u3000\u201c\u9601\u4e0b\u7684\u638c\u529b\u5185\u529f\uff0c\u6211\u5f53\u7136\u4f69\u670d\u3002\u201d\u536b\u5929\u9e4f\u8f6c\u5411\u4e25\u6b63\u521a\uff1a\u201c\u9601\u4e0b\u51fa\u624b\u4e4b\u5feb\uff0c\u6211\u66f4\u4f69\u670d\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u51b7\u7b11\uff0c\u770b\u7740\u535c\u9e70\u51b7\u7b11\u3002 \u3000\u3000\u201c\u4f46\u662f\u6211\u6700\u4f69\u670d\u7684\uff0c\u8fd8\u662f\u4f60\uff01\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u82e5\u4e0d\u662f\u9601\u4e0b\u5148\u8bf4\u4e86\u90a3\u4e9b\u8bdd\uff0c\u8ba9\u6211\u4eec\u8ba4\u5b9a\u8fd9\u91cc\u6709\u4f4d\u968f\u65f6\u90fd\u53ef\u4ee5\u593a\u4e0b\u6211\u7684\u5200\uff0c\u628a\u6211\u4e00\u811a\u8e22\u51fa\u53bb\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u67f3\u592b\u4eba\u53ea\u6015\u8fd8\u672a\u5fc5\u4f1a\u4e2d\u4ed6\u4eec\u7684\u8ba1\u3002\u201d \u3000\u3000\u535c\u9e70\u4e5f\u51b7\u51b7\u5730\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u4f60\u8fd8\u662f\u4e0d\u4fe1\u4e16\u4e0a\u6709\u8fd9\u6837\u7684\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7684\u4eba\u5728\u54ea\u91cc\uff1f\u201d\u536b\u5929\u9e4f\u3002 \u3000\u3000\u201c\u5c31\u5728\u8fd9\u91cc\u8fb9\uff01\u201d \u3000\u3000\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u8bf4\u8fc7\uff0c\u53ea\u8981\u4f60\u4e00\u62d4\u5200\uff0c\u5c31\u4f1a\u77e5\u9053\u4ed6\u662f\u8c01\u4e86\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u7edd\u4e0d\u8ba9\u4f60\u5931\u671b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u4e00\u5411\u51b7\u9759\u8c28\u614e\uff0c\u4e00\u5411\u6700\u80fd\u6c89\u5f97\u4f4f\u6c14\uff0c\u4ece\u4e0d\u8f7b\u6613\u51fa\u624b\uff0c\u4ece\u4e0d\u505a\u6ca1\u628a\u63e1\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u7834\u4f8b\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u62d4\u5200\uff01 \u3000\u3000\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u5200\u51fa\u9798\u3002 \u3000\u3000\u5200\u5149\u5982\u96ea\u5982\u971c\uff0c\u5982\u5954\u96f7\u95ea\u52a8\uff0c\u4e09\u5c3a\u4e5d\u5bf8\u957f\u7684\u5200\u950b\uff0c\u5e26\u7740\u523a\u8033\u7684\u98ce\u58f0\uff0c\u4e00\u5200\u5411\u535c\u9e70\u780d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4ece\u4e0d\u8f7b\u6613\u51fa\u624b\uff0c\u53ea\u8981\u51fa\u624b\uff0c\u5c31\u5f88\u5c11\u5931\u624b\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u8fd9\u4e00\u5200\u7684\u901f\u5ea6\u548c\u5a01\u529b\uff0c\u5feb\u3001\u51c6\u3001\u72e0\u90fd\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5200\u5df2\u4f7f\u5c3d\u5168\u529b\uff0c\u65e2\u6ca1\u6709\u66ff\u81ea\u5df1\u7559\u9000\u8def\uff0c\u4e5f\u4e0d\u60f3\u518d\u7559\u4e0b\u5bf9\u65b9\u8fd9\u6761\u547d\uff01 \u3000\u3000\u9ad8\u624b\u51fa\u62db\uff0c\u901a\u5e38\u90fd\u4e0d\u4f1a\u5c3d\u5168\u529b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e00\u5b9a\u8981\u5148\u4e3a\u81ea\u5df1\u7559\u4e0b\u9000\u8def\uff0c\u5148\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7edd\u5bf9\u662f\u9ad8\u624b\uff0c\u4ed6\u8fd9\u4e00\u5200\u672a\u7559\u9000\u8def\uff0c\u53ea\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u6839\u672c\u4e0d\u5fc5\u7559\u9000\u8def\u3002 \u3000\u3000\u535c\u9e70\u4e0d\u4f46\u53d7\u4e86\u4f24\uff0c\u800c\u4e14\u7a7a\u62f3\u8d64\u624b\uff0c\u7528\u4ec0\u4e48\u6765\u63a5\u8fd9\u4e00\u5200\uff1f \u3000\u3000\u5c31\u7b97\u8fd8\u80fd\u95ea\u907f\uff0c\u4e5f\u7edd\u5bf9\u65e0\u529b\u53cd\u51fb\u3002 \u3000\u3000\u5bf9\u65b9\u65e2\u7136\u65e0\u529b\u53cd\u51fb\uff0c\u4ed6\u53c8\u4f55\u5fc5\u8981\u4e3a\u81ea\u5df1\u7559\u9000\u8def\uff1f\u80fd\u591f\u6709\u4e00\u4efd\u529b\u91cf\u4f7f\u51fa\u6765\uff0c\u5c31\u5c06\u8fd9\u4e00\u4efd\u529b\u91cf\u4f7f\u51fa\u6765\uff0c\u5200\u4e0b\u7edd\u4e0d\u7559\u60c5\u3002 \u3000\u3000\u4ed6\u5e0c\u671b\u8fd9\u4e00\u5200\u5c31\u80fd\u81f4\u547d\uff01 \u3000\u3000\u536b\u5929\u9e4f\u8001\u8c0b\u6df1\u7b97\uff0c\u8eab\u7ecf\u767e\u6218\uff0c\u4e00\u5411\u770b\u5f97\u6781\u51c6\u3001\u7b97\u5f97\u6781\u51c6\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u8fd9\u4e00\u6b21\u7b97\u9519\u4e86\u3002 \u3000\u3000\u535c\u9e70\u63a5\u4f4f\u4e86\u8fd9\u4e00\u5200\uff0c\u7528\u4e00\u53cc\u7a7a\u624b\u63a5\u4f4f\u4e86\u8fd9\u4e00\u5200\u3002 \u3000\u3000\u4ed6\u7684\u53cc\u624b\u4e00\u62cd\uff0c\u5c31\u5df1\u5c06\u5200\u950b\u5939\u4f4f\uff0c\u4ed6\u7684\u8eab\u5b50\u5df2\u98de\u8d77\uff0c\u53cc\u811a\u8fde\u73af\u8e22\u51fa\uff0c\u7b2c\u4e00\u811a\u8e22\u536b\u5929\u9e4f\u63e1\u5200\u7684\u624b\uff0c\u7b2c\u4e8c\u811a\u8e22\u4ed6\u53cc\u817f\u7684\u8981\u5bb3\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e0d\u80fd\u4e0d\u95ea\u907f\u3001\u540e\u9000\u3002 \u3000\u3000\u7b2c\u4e00\u811a\u8e22\u6765\u65f6\uff0c\u4ed6\u7684\u5200\u5df2\u6492\u624b\uff0c\u7b2c\u4e8c\u811a\u8e22\u6765\uff0c\u4ed6\u53ea\u6709\u51cc\u7a7a\u7ffb\u8eab\uff0c\u624d\u80fd\u8eb2\u5f97\u5f00\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u843d\u4e0b\u65f6\uff0c\u5df1\u5728\u5e10\u7bf7\u5916\u3002 \u3000\u3000\u4ed6\u7684\u5200\u5df2\u5728\u535c\u9e70\u624b\u91cc\u3002 \u3000\u3000\u535c\u9e70\u8f7b\u629a\u5200\u950b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e00\u5200\u8fd8\u4e0d\u591f\u5feb\uff0c\u8fd9\u628a\u5200\u4e5f\u4e0d\u591f\u5feb\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u62c7\u6307\u6263\u4e2d\u6307\uff0c\u4ee5\u4e2d\u6307\u5f39\u5200\u950b\uff0c\u201c\u5d29\u201d\u7684\u4e00\u54cd\uff0c\u5200\u950b\u5df2\u7f3a\u53e3\u3002 \u3000\u3000\u4ed6\u53f3\u624b\u63e1\u5200\u67c4\uff0c\u518d\u7528\u5de6\u624b\u4e24\u6307\u634f\u4f4f\u5200\u5c16\uff0c\u53c8\u662f\u201c\u5d29\u201d\u7684\u4e00\u54cd\uff0c\u957f\u5200\u5df2\u88ab\u62d7\u65ad\uff0c\u4ece\u5200\u950b\u7f3a\u53e3\u5904\u65ad\u6210\u4e24\u622a\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u60e8\u53d8\uff0c\u53d8\u5f97\u6bd4\u67f3\u5206\u5206\u66f4\u60e8\u3002 \u3000\u3000\u535c\u9e70\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u867d\u7136\u5df2\u8d1f\u4f24\uff0c\u53ef\u662f\u4f60\u4eec\u4e5f\u4e0d\u8be5\u62b5\u4f30\u6211\u7684\uff0c\u56e0\u4e3a\u6211\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u63e1\u7d27\u53cc\u62f3\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u6b7b\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u51fb\u8d25\u4f60\uff1f\u201d \u3000\u3000\u535c\u9e70\u7684\u56de\u7b54\u548c\u4ee5\u524d\u540c\u6837\u660e\u786e\uff1a\u201c\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u8fde\u770b\u90fd\u4e0d\u518d\u53bb\u770b\u536b\u5929\u9e4f\uff0c\u4ed6\u4e00\u53cc\u5140\u9e70\u822c\u7684\u9510\u773c\u5df2\u76ef\u5728\u641c\u9b42\u624b\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u73b0\u5728\uff0c\u53ea\u5269\u4e0b\u4f60\u4e86\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4e09\u9635\u8d4c\u8f93\u8d62\uff0c\u4f60\u4eec\u5df2\u8d25\u4e86\u4e24\u9635\uff0c\u4f60\u662f\u4e0d\u662f\u8fd8\u60f3\u62fc\u4e00\u62fc\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u6211\u7684\u3002\u201d\u5c0f\u65b9\u7684\u58f0\u97f3\u867d\u7136\u5f88\u5e73\u9759\uff0c\u60c5\u7eea\u5374\u5f88\u4e0d\u5e73\u9759\u3002 \u3000\u3000\u521a\u624d\u90a3\u4e24\u9635\u5bf9\u51b3\uff0c\u5b9e\u5728\u4ee4\u4eba\u8840\u8109\u6cb8\u817e\uff0c\u52a8\u9b44\u60ca\u5fc3\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u5f53\u7136\u662f\u4f60\u7684\uff0c\u8fde\u547d\u90fd\u662f\u4f60\u7684\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u51fa\u624b\uff0c\u4e09\u62db\u4e4b\u5185\uff0c\u5fc5\u5c06\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u4f60\u662f\u8bf4\u5341\u62db\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u4e0d\u540c\u4e86\u3002\u201d\u535c\u9e70\u51b7\u51b7\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u7684\u80c6\u5df2\u5bd2\uff0c\u6c14\u5df2\u9981\uff0c\u4f60\u8981\u6740\u4ed6\uff0c\u5df2\u7ecf\u7528\u4e0d\u7740\u5341\u62db\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u4e5f\u51b7\u7b11\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u7edd\u4e0d\u6562\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u5f53\u7136\u4e0d\u6562\u3002\u201d \u3000\u3000\u641c\u9b42\u624b\u7ad9\u5728\u90a3\u91cc\uff0c\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u4ed6\u4eec\u8bf4\u7684\u8bdd\uff0c\u4ed6\u597d\u50cf\u6839\u672c\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4e0d\u4f46\u662f\u3001\u201c\u778e\u5b50\u201d\uff0c\u800c\u4e14\u53d8\u6210\u4e86\u804b\u5b50\u3002 \u3000\u3000\u67f3\u5206\u5206\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u5f00\u53e3\uff0c\u5ffd\u7136\u8f7b\u8f7b\u5730\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u65e0\u8bba\u6597\u667a\u6597\u529f\uff0c\u535c\u5927\u8001\u677f\u90fd\u65e0\u4eba\u80fd\u53ca\u3002\u201d \u3000\u3000\u535c\u9e70\u63a5\u53d7\u4e86\u5979\u7684\u606d\u7ef4\u3002 \u3000\u3000\u67f3\u5206\u5206\u53c8\u9053\uff1a\u201c\u4f46\u662f\u667a\u8005\u5343\u8651\uff0c\u4e5f\u96be\u514d\u4f1a\u6709\u6240\u5931\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u867d\u7136\u8d25\u4e86\uff0c\u4f46\u662f\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u7ad9\u8d77\u6765\uff0c\u773a\u671b\u7740\u8fdc\u5904\u5251\u621f\u4e0a\u95ea\u52a8\u7684\u5bd2\u5149\uff1a\u201c\u5c31\u5728\u4f60\u4eec\u7684\u8425\u5730\u5916\uff0c\u6211\u4eec\u8fd8\u6709\u4e03\u5341\u4f4d\u4e45\u7ecf\u8bad\u7ec3\u3001\u767e\u6218\u4e0d\u6b7b\u7684\u6218\u58eb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u63a5\u7740\u9053\uff1a\u201c\u53ea\u8981\u6211\u4e00\u58f0\u4ee4\u4e0b\uff0c\u4ed6\u4eec\u5c31\u4f1a\u51b2\u8fc7\u6765\uff0c\u7247\u523b\u95ee\u8fd9\u91cc\u5c31\u5c06\u6a2a\u5c38\u904d\u5730\u3001\u8840\u6d41\u6210\u6e20\u3002\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4eec\u5916\u9762\u8fd8\u6709\u9876\u8f7f\u5b50\uff0c\u8f7f\u5b50\u5f53\u7136\u4e0d\u4f1a\u662f\u7a7a\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d\u67f3\u5206\u5206\u9053\uff1a\u201c\u6211\u4eec\u5f53\u7136\u4e0d\u4f1a\u62ac\u4e00\u9876\u7a7a\u8f7f\u5b50\u6765\u3002\u201d \u3000\u3000\u5979\u76ee\u4e2d\u53c8\u95ea\u51fa\u6076\u6bd2\u8be1\u6a58\u7684\u7b11\u610f\uff1a\u201c\u8f7f\u5b50\u91cc\u5f88\u53ef\u80fd\u5750\u7740\u4f4d\u4ece\u672a\u8d25\u8fc7\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u4e5f\u53ef\u80fd\u85cf\u7740\u53ef\u4ee5\u5c06\u8fd9\u65b9\u5706\u4e94\u91cc\u5185\u7684\u4eba\u755c\u5168\u90fd\u70b8\u6210\u98de\u7070\u7684\u706b\u836f\u3002\u201d \u3000\u3000\u5979\u7528\u7b11\u773c\u770b\u7740\u5c0f\u65b9\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u76f4\u60f3\u770b\u770b\u8f7f\u5b50\u91cc\u7a76\u7adf\u6709\u4ec0\u4e48\uff0c\u4f46\u662f\u4e0d\u5230\u6700\u540e\u5173\u5934\u65f6\uff0c\u6211\u4eec\u662f\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u770b\u5230\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u67f3\u5206\u5206\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u4e0d\u5230\u6700\u540e\u5173\u5934\uff0c\u56e0\u4e3a\u6211\u4eec\u8fd8\u6709\u8d4c\u6ce8\uff0c\u8fd8\u53ef\u4ee5\u8ddf\u4f60\u4eec\u8d4c\u4e00\u8d4c\u3002\u201d \u3000\u3000\u5979\u8f6c\u8eab\u9762\u5bf9\u535c\u9e70\uff1a\u201c\u53ea\u770b\u535c\u5927\u8001\u677f\u662f\u4e0d\u662f\u613f\u610f\u7528\u4f60\u8fd9\u4e48\u591a\u5b50\u5f1f\u4f19\u4f34\u7684\u6027\u547d\u6765\u8ddf\u6211\u4eec\u8d4c\u3002\u201d \u3000\u3000\u535c\u9e70\u4e5f\u6c89\u9ed8\u3002 \u3000\u3000\u8fd9\u662f\uff1a\u4e00\u573a\u8c6a\u8d4c\uff0c\u8d4c\u6ce8\u5b9e\u5728\u592a\u5927\uff0c\u8d25\u7684\u4e00\u65b9\u56fa\u7136\u4f1a\u8d25\u5f97\u6781\u60e8\uff0c\u80dc\u7684\u4e00\u65b9\u4e5f\u662f\u60e8\u80dc\u3002 \u3000\u3000\u65e0\u8bba\u662f\u60e8\u80dc\u8fd8\u662f\u60e8\u8d25\uff0c\u90fd\u540c\u6837\u75db\u82e6\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u5f88\u96be\u4e0b\u51b3\u5b9a\u3002\u201d\u67f3\u5206\u5206\u9053\uff1a\u201c\u4e0d\u5012\u6700\u540e\u5173\u5934\uff0c\u6211\u4eec\u4e5f\u540c\u6837\u4e0d\u613f\u8ddf\u4f60\u8d4c\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u6211\u4eec\u4e24\u70b9\u5c0f\u5c0f\u7684\u8981\u6c42\uff0c\u6211\u4eec\u7acb\u523b\u5c31\u8d70\u3002\u201d \u3000\u3000\u535c\u9e70\u4ecd\u7136\u6c89\u9ed8\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u4eec\u60f3\u770b\u770b\u4f60\u7684\u8d27\uff0c\u6bcf\u4e00\u5305\u8d27\u90fd\u8981\u770b\u3002\u201d \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u7b2c\u4e00\u70b9\u8981\u6c42\uff1a\u201c\u9ec4\u91d1\u65e2\u7136\u4e0d\u5728\u4f60\u8fd9\u91cc\uff0c\u4f60\u5c31\u8ba9\u6211\u4eec\u770b\u770b\u53c8\u4f55\u59a8\uff1f\u201d \u3000\u3000\u67f3\u5206\u5206\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u60f3\u628a\u8fd9\u4e2a\u4eba\u5e26\u8d70\u3002\u201d \u3000\u3000\u5979\u6307\u7740\u5c0f\u65b9\uff1a\u201c\u4ed6\u8ddf\u4f60\u975e\u4eb2\u975e\u6545\uff0c\u4f60\u4f55\u5fc5\u4e3a\u4ed6\u8ddf\u6211\u4eec\u62fc\u547d\uff1f\u201d \u3000\u3000\u535c\u9e70\u7ec8\u4e8e\u5f00\u53e3\uff1a\u201c\u4f60\u4eec\u7684\u8981\u6c42\u542c\u6765\u597d\u50cf\u5e76\u4e0d\u8fc7\u5206\u3002\u201d \u3000\u3000\u201c\u975e\u4f46\u4e0d\u8fc7\u5206\uff0c\u800c\u4e14\u5f88\u5408\u7406\u3002\u201d\u67f3\u5206\u5206\u5a9a\u7b11\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u7b54\u5e94\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u4e5f\u5f00\u4e86\u53e3\uff1a\u201c\u6211\u613f\u610f\u8ddf\u4f60\u4eec\u8d70\u3002\u201d\u4ed6\u7684\u8bed\u6c14\u575a\u51b3\uff0c\u6beb\u65e0\u72b9\u7591\uff1a\u201c\u968f\u65f6\u90fd\u53ef\u4ee5\u8d70\u3002\u201d \u3000\u3000\u535c\u9e70\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u4f60\u4e00\u5411\u4e0d\u613f\u8fde\u7d2f\u522b\u4eba\uff0c\u66f4\u4e0d\u613f\u65e0\u8f9c\u8005\u4e3a\u4f60\u800c\u6b7b\u3002\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u4e0d\u8be5\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u5fd8\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u4f60\u7559\u4e0b\u6765\uff0c\u662f\u6211\u8981\u4f60\u7559\u4e0b\u6765\u7684\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u6211\u65e2\u7136\u8981\u4f60\u7559\u4e0b\u6765\uff0c\u51c6\u4e5f\u4e0d\u80fd\u5e26\u4f60\u8d70\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u6162\uff0c\u53ef\u662f\u6bcf\u4e2a\u5b57\u90fd\u50cf\u662f\u6839\u9489\u5b50\u3002\u4ed6\u6bcf\u8bf4\u4e00\u4e2a\u5b57\uff0c\u5c31\u50cf\u5df2\u5c06\u4e00\u4e00\u6839\u9489\u5b50\u9489\u5165\u77f3\u5934\u91cc\u3002 \u3000\u3000\u9489\u5b50\u5df2\u9489\u4e86\u4e0b\u53bb\uff0c\u8bdd\u5df2\u8bf4\u51fa\u53e3\uff0c\u5c0f\u65b9\u80f8\u4e2d\u7684\u70ed\u8840\u53c8\u6d8c\u8d77\u3002 \u3000\u3000\u67f3\u5206\u5206\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u771f\u7684\u8981\u8ddf\u6211\u4eec\u8d4c\u4e00\u8d4c\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d\u535c\u9e70\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u5df2\u7ecf\u53ef\u4ee5\u4e0b\u4ee4\uff0c\u8981\u4f60\u4eec\u90a3\u4e03\u5341\u4f4d\u4e45\u7ecf\u8bad\u7ec3\u3001\u767e\u6218\u4e0d\u6b7b\u7684\u6218\u58eb\u51b2\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u53d1\u9752\uff0c\u638c\u5fc3\u5192\u6c57\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u540e\u6094\uff1f\u201d \u3000\u3000\u535c\u9e70\u62d2\u7edd\u56de\u7b54\u3002 \u3000\u3000\u62d2\u7edd\u56de\u7b54\uff0c\u5df2\u7ecf\u662f\u4e00\u79cd\u56de\u7b54\uff0c\u7edd\u4e0d\u5bb9\u522b\u4eba\u8bef\u89e3\uff0c\u4e5f\u4e0d\u4f1a\u88ab\u4eba\u8bef\u89e3\u7684\u56de\u7b54\u3002 \u3000\u3000\u201c\u597d\u3002\u201d\u536b\u5929\u9e4f\u54ac\u7259\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u6015\u6d41\u8840\uff0c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u6015\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u64ae\u53e3\u957f\u5578\uff0c\u58f0\u97f3\u5c16\u9510\u51c4\u5389\uff0c\u5982\u8352\u5c71\u9b3c\u547c\uff0c\u96ea\u5730\u72fc\u66dd\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u4eec\u7ea6\u5b9a\u7684\u4fe1\u53f7\u3002 \u3000\u3000\u653b\u51fb\u7684\u4fe1\u53f7\u3002 \u3000\u3000\u591c\u5bd2\u5982\u5200\u3002 \u3000\u3000\u8fdc\u5904\u5251\u621f\u68ee\u68ee\uff0c\u5728\u8df3\u52a8\u7684\u706b\u7130\u7167\u8000\u4e0b\uff0c\u95ea\u70c1\u7740\u6151\u4eba\u7684\u5bd2\u5149\u3002 \u3000\u3000\u4eba\u5934\u5728\u9888\u5b50\u4e0a\uff0c\u70ed\u8840\u5728\u80f8\u819b\uff0c\u7bad\u5728\u5f26\u4e0a\uff0c\u5200\u5728\u624b\u3002 \u3000\u3000\u653b\u51fb\u7684\u547d\u4ee4\u5df2\u53d1\u51fa\u4e86\u3002 \u3000\u3000\u5c16\u9510\u7684\u5578\u58f0\u54cd\u5f7b\u591c\u7a7a\u3002 \u3000\u3000\u535c\u9e70\u5c45\u7136\u8fd8\u662f\u5b89\u5750\u4e0d\u52a8\uff0c\u9664\u4e86\u5fc3\u810f\u4e0e\u8840\u8109\u5916\uff0c\u5168\u8eab\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u8fdc\u5904\u68ee\u7136\u73af\u5217\u7684\u5251\u4e7e\u4e5f\u6ca1\u6709\u52a8\uff0c\u4eba\u9a6c\u5e76\u6ca1\u6709\u51b2\u8fc7\u6765\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u7ec4\u7ec7\u4e25\u5bc6\uff0c\u53f7\u4ee4\u4e25\u660e\uff0c\u7eaa\u5f8b\u4e25\u8083\u3002 \u3000\u3000\u4ed6\u53d1\u51fa\u7684\u547d\u4ee4\u4ece\u672a\u5931\u6548\u3002 \u3000\u3000\u5b8b\u8001\u592b\u5b50\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4f60\u8fd9\u6b21\u5e26\u6765\u7684\u4eba\u8033\u6735\u90fd\u4e0d\u592a\u597d\uff0c\u90fd\u6ca1\u6709\u542c\u89c1\u4f60\u5728\u53eb\u4ed6\u4eec\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u4e0d\u7406\u4ed6\uff0c\u518d\u6b21\u957f\u5578\uff0c\u5578\u58f0\u66f4\u5c16\u9510\uff0c\u66f4\u54cd\u4eae\u3002 \u3000\u3000\u5b8b\u8001\u592b\u5b50\u63a9\u8d77\u4e86\u8033\u6735\uff0c\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8fd9\u4e00\u6b21\u8fde\u804b\u5b50\u90fd\u5e94\u8be5\u542c\u5f97\u89c1\u4e86\u3002\u201d \u3000\u3000\u4f46\u662f\u8fdc\u5904\u7684\u4eba\u9a6c\u4ecd\u7136\u6ca1\u6709\u52a8\u3002\u536b\u5929\u9e4f\u9f3b\u5c16\u4e0a\u5df2\u5192\u51fa\u51b7\u6c57\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u53c8\u5f00\u53e3\uff0c\u58f0\u97f3\u51b7\u5982\u9488\u523a\u3001\u5251\u51fb\u5200\u9798\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u662f\u804b\u5b50\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u804b\u5b50\u4e3a\u4ec0\u4e48\u542c\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u542c\u5f97\u89c1\u3002\u201d \u3000\u3000\u201c\u542c\u5f97\u89c1\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51b2\u8fc7\u6765\uff1f\u201d\u5b8b\u8001\u592b\u5b50\u53c8\u772f\u8d77\u773c\uff0c\u201c\u5200\u67aa\u5251\u4e7e\u9f50\u4e0b\uff0c\u628a\u6211\u4eec\u4e00\u4e2a\u4e2a\u5241\u6210\u8089\u6ce5\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8fd8\u6ca1\u6709\u8981\u4ed6\u4eec\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u4ed6\u4eec\u8fc7\u6765\uff0c\u4ed6\u4eec\u5c31\u4f1a\u8fc7\u6765\uff1f\u201d\u5b8b\u8001\u592b\u5b50\u53c8\u95ee\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u53ea\u6709\u6211\u8981\u4ed6\u4eec\u8fc7\u6765\uff0c\u4ed6\u4eec\u624d\u4f1a\u8fc7\u6765\u3002\u201d \u3000\u3000\u5b8b\u8001\u592b\u5b50\u6447\u5934\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u201c\u4f60\u9a6c\u4e0a\u5c31\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u6325\u624b\uff0c\u8bf4\u51fa\u4e86\u4e24\u4e2a\u5b57\uff1a\u201c\u8fc7\u6765\uff01\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u65e2\u4e0d\u5c16\u9510\uff0c\u4e5f\u4e0d\u54cd\u4eae\uff0c\u53ef\u662f\u8fd9\u4e24\u4e2a\u5b57\u4e00\u8bf4\u51fa\uff0c\u8fdc\u5904\u7684\u4eba\u9a6c\u5c31\u52a8\u4e86\u3002 \u3000\u3000\u52a8\u5f97\u5f88\u6162\u3002 \u3000\u3000\u4e03\u5341\u5339\u5065\u9a6c\uff0c\u8f7d\u7740\u4e00\u767e\u56db\u5341\u4e2a\u4eba\uff0c\u6162\u6162\u5730\u8d70\u5165\u706b\u5149\u7167\u8000\u7684\u8425\u5730\u3002 \u3000\u3000\u6bcf\u5339\u9a6c\u4e0a\u90fd\u6709\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u524d\u9762\u7684\u4e00\u4e2a\u4eba\uff0c\u6025\u88c5\u52b2\u670d\uff0c\u624b\u6301\u5f13\u7bad\u5200\u621f\uff0c\u6b63\u662f\u536b\u5929\u9e4f\u5c5e\u4e0b\u7684\u6218\u58eb\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u786e\u90fd\u5df2\u4e45\u7ecf\u8bad\u7ec3\uff0c\u4f46\u662f\u73b0\u5728\u6bcf\u4e2a\u4eba\u90fd\u597d\u50cf\u6728\u5934\u4eba\u4e00\u6837\u5750\u5728\u9a6c\u978d\u4e0a\uff0c\u8eab\u5b50\u90fd\u5df2\u50f5\u786c\uff0c\u8138\u4e0a\u90fd\u5e26\u7740\u6050\u60e7\u81f3\u6781\u7684\u8868\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u540e\u9762\u8fd8\u6709\u4e2a\u4eba\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u8eab\u540e\uff0c\u90fd\u6709\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u7528\u4e00\u628a\u5c16\u5200\uff0c\u62b5\u5728\u4ed6\u4eec\u7684\u8170\u773c\u4e0a\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53d1\u73b0\u521a\u624d\u8fd8\u5728\u8425\u706b\u65c1\u9ad8\u6b4c\u6b22\u5531\u75db\u996e\u7684\u90a3\u4e9b\u6d6a\u5b50\u884c\u5546\u65c5\u5ba2\uff0c\u73b0\u5728\u5df2\u5c11\u4e86\u5f88\u591a\uff0c\u672c\u6765\u6709\u4e00\u767e\u591a\u4e2a\u4eba\uff0c\u73b0\u5728\u5df2\u5c11\u4e86\u4e00\u534a\u3002 \u3000\u3000\u8fd9\u4e00\u534a\u4eba\u90fd\u5df2\u5230\u4e86\u9a6c\u4e0a\uff0c\u5230\u4e86\u536b\u5929\u9e4f\u5c5e\u4e0b\u6218\u58eb\u7684\u5065\u9a6c\u4e0a\uff0c\u50cf\u5f71\u5b50\u822c\u8d34\u5728\u8fd9\u4e9b\u6218\u58eb\u7684\u80cc\u540e\uff0c\u7528\u4e00\u628a\u5c16\u5200\u62b5\u4f4f\u4e86\u8fd9\u4e9b\u6218\u58eb\u7684\u8170\u773c\u3002 \u3000\u3000\u4ed6\u4eec\u624d\u662f\u771f\u6b63\u7684\u6218\u58eb\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u884c\u52a8\u8f7b\u6377\u5982\u72f8\u732b\uff0c\u8fc5\u6025\u5982\u6bd2\u86c7\uff0c\u51c6\u786e\u5982\u4e94\u82b1\u7bad\u795e\u7684\u7bad\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5c5e\u4e0b\u6b63\u5728\u7b49\u5f85\u7740\u653b\u51fb\u4ee4\u4e0b\u65f6\uff0c\u6b63\u5728\u5168\u795e\u8d2f\u6ce8\uff0c\u51c6\u5907\u51fa\u51fb\uff0c\u5168\u90e8\u6ce8\u610f\u529b\u90fd\u96c6\u4e2d\u5728\u8fd9\u4e2a\u9876\u4e0a\u60ac\u6302\u7740\u9ed1\u8272\u9e70\u7fbd\u7684\u5e10\u7bf7\u65f6\u2026\u2026 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u6bcf\u4e2a\u4eba\u90fd\u53d1\u73b0\u81ea\u5df1\u80cc\u540e\u591a\u4e86\u4e00\u4e2a\u4eba\uff0c\u6bcf\u4e2a\u4eba\u8170\u773c\u4e0a\u90fd\u5df2\u611f\u89c9\u5230\u5c16\u5200\u7684\u523a\u9aa8\u5bd2\u610f\uff0c\u6bcf\u4e2a\u4eba\u90fd\u542c\u89c1\u8eab\u540e\u6709\u4eba\u5728\u8bf4\uff1a\u201c\u4e0d\u8bb8\u52a8\uff0c\u4e00\u52a8\u5c31\u6b7b\uff01\u201d \u3000\u3000\u8fd8\u6ca1\u6709\u5f00\u59cb\u8d4c\uff0c\u4ed6\u4eec\u5c31\u5df2\u8d25\u4e86\u3002 \u3000\u3000\u60e8\u8d25\uff01 \u3000\u3000\u6709\u4eba\u66fe\u7ecf\u6709\u516b\u4e2a\u5b57\u5f62\u5bb9\u536b\u5929\u9e4f\u2014\u2014\u9759\u5982\u5c71\u5cb3\uff0c\u7a33\u5982\u78d0\u77f3\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u73b0\u5728\u6574\u4e2a\u4eba\u90fd\u5df2\u5d29\u6e83\uff0c\u5f7b\u5e95\u5d29\u6e83\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u7ecf\u5386\u8fc7\u8fd9\u6837\u7684\u60e8\u8d25\u3002 \u3000\u3000\u67f3\u5206\u5206\u5c11\u5973\u822c\u7684\u7ea2\u989c\u7b11\u9765\uff0c\u73b0\u5728\u4e5f\u5315\u53d8\u5f97\u65b0\u4e27\u7684\u5be1\u5987\u822c\u8870\u8001\u82cd\u767d\u60df\u6dec\u3002 \u3000\u3000\u73b0\u5728\u5979\u5df2\u7ecf\u4e0d\u662f\u4e00\u534a\u4eba\uff0c\u800c\u662f\u4e00\u4e2a\u4eba\u4e86\uff0c\u5979\u5c5e\u4e8e\u201c\u9b54\u201d\u7684\u90a3\u4e00\u534a\uff0c\u5df2\u7ecf\u5728\u8fd9\u79cd\u65e0\u60c5\u7684\u60e8\u75db\u6253\u51fb\u4e0b\u88ab\u6d88\u706d\uff0c\u5f7b\u5e95\u6d88\u706d\u3002 \u3000\u3000\u535c\u9e70\u51b7\u51b7\u5730\u770b\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u201c\u4f60\u4eec\u867d\u7136\u8d25\u4e86\uff0c\u5374\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4f60\u4eec\u5916\u9762\u4e03\u5341\u4f4d\u4e45\u7ecf\u8bad\u7ec3\u3001\u767e\u6218\u4e0d\u6b7b\u7684\u6218\u58eb\u4e5f\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u95ee\uff1a\u201c\u4f60\u4eec\u60f3\u4e0d\u60f3\u6b7b\uff1f\u60f3\u4e0d\u60f3\u8981\u90a3\u4e03\u5341\u4f4d\u6218\u58eb\u966a\u4f60\u4eec\u4e00\u8d77\u6b7b\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u6839\u672c\u4e0d\u5fc5\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u4eba\u613f\u610f\u56de\u7b54\uff0c\u4f46\u662f\u4ece\u6765\u4e0d\u5f00\u53e3\u7684\u641c\u9b42\u624b\u5374\u56de\u7b54\u4e86\uff1a\u201c\u6211\u4eec\u4e0d\u60f3\u6b7b\u3002\u201d \u3000\u3000\u6bd2\u624b\u641c\u9b42\uff0c\u6027\u547d\u65e0\u5b58\u3002 \u3000\u3000\u4f46\u662f\u6740\u4eba\u7684\u4eba\uff0c\u5374\u5f80\u5f80\u6bd4\u88ab\u4ed6\u6740\u7684\u4eba\u66f4\u6015\u6b7b\uff0c\u6740\u4eba\u8005\u5f80\u5f80\u5c31\u662f\u56e0\u4e3a\u6015\u6b7b\u624d\u6740\u4eba\u3002 \u3000\u3000\u535c\u9e70\u51b7\u7b11\uff1a\u201c\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u7ecf\u5230\u4e86\u6700\u540e\u5173\u5934\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u4eec\u8fd8\u6709\u4e00\u9876\u8f7f\u5b50\uff0c\u8f7f\u5b50\u91cc\u53ef\u80fd\u6709\u4f4d\u7edd\u9876\u9ad8\u624b\uff0c\u4e5f\u53ef\u80fd\u6709\u8db3\u591f\u628a\u6211\u4eec\u90fd\u70b8\u6210\u98de\u7070\u7684\u706b\u836f\u3002\u201d \u3000\u3000\u535c\u9e70\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u8fd8\u60f3\u8d4c\u4e00\u8d4c\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u60f3\u3002\u201d\u641c\u9b42\u624b\u62a2\u7740\u9053\uff1a\u201c\u8f7f\u5b50\u91cc\u6ca1\u6709\u9ad8\u624b\uff0c\u4e5f\u6ca1\u6709\u706b\u836f\uff0c\u53ea\u6709\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u6325\u62f3\uff0c\u75db\u51fb\u5728\u4ed6\u8138\u4e0a\uff0c\u5c01\u4f4f\u4e86\u4ed6\u7684\u5634\u3002 \u3000\u3000\u540d\u6ee1\u6c5f\u6e56\u7684\u641c\u9b42\u624b\u7adf\u907f\u4e0d\u5f00\u8fd9\u4e00\u62f3\uff0c\u4e16\u4e0a\u6050\u6015\u5df2\u5f88\u5c11\u6709\u4eba\u80fd\u907f\u5f00\u8fd9\u4e00\u62f3\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u5341\u7ae0\u3000\u60e8\u8d25 \u7b2c\u5341\u7ae0\u3000\u60e8\u8d25 \u3000\u3000\u8fd9\u4e00\u62f3\u65e2\u6ca1\u6709\u82b1\u4fcf\u7684\u62db\u5f0f\uff0c\u4e5f\u6ca1\u6709\u590d\u6742\u7684\u53d8\u5316\uff0c\u53ea\u6709\u901f\u5ea6\u3002 \u3000\u3000\u60ca\u4eba\u7684\u901f\u5ea6\uff0c\u5feb\u5f97\u4ee4\u4eba\u65e0\u6cd5\u601d\u8bae\uff0c\u5feb\u5f97\u53ef\u6015\u3002 \u3000\u3000\u641c\u9b42\u624b\u5012\u4e0b\u53bb\u65f6\uff0c\u5634\u91cc\u5f88\u53ef\u80fd\u5df2\u6ca1\u6709\u4e00\u9897\u5b8c\u6574\u7684\u7259\u9f7f\uff0c\u788e\u88c2\u7684\u9f3b\u6881\u5df2\u79fb\u52a8\u4e86\u4f4d\u7f6e\uff0c\u9c9c\u8840\u4ece\u7834\u88c2\u7684\u5634\u5507\u4e2d\u6d8c\u51fa\uff0c\u5c31\u50cf\u662f\u88ab\u5c60\u5200\u5272\u5f00\u7684\u4e00\u6837\u3002 \u3000\u3000\u901f\u5ea6\u5c31\u662f\u529b\u91cf\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u8138\u4e0a\u90fd\u53d8\u4e86\u989c\u8272\u3002\u76f4\u5230\u6b64\u523b\uff0c\u5927\u5bb6\u624d\u770b\u51fa\u73ed\u5bdf\u5df4\u90a3\u7684\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u51b7\u51b7\u5730\u770b\u7740\u641c\u9b42\u624b\u5012\u4e0b\u53bb\u540e\u624d\u5f00\u53e3\uff1a\u201c\u6211\u4e0d\u662f\u540d\u5bb6\u5f1f\u5b50\uff0c\u4e5f\u6ca1\u6709\u5b66\u8fc7\u4f60\u4eec\u90a3\u4e9b\u9ad8\u5999\u7684\u6b66\u529f\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u7c97\u91ce\u65e0\u77e5\u7684\u85cf\u4eba\uff0c\u5728\u4f60\u4eec\u773c\u4e2d\uff0c\u5f88\u53ef\u80fd\u8ddf\u91ce\u517d\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ef\u662f\u6211\u8bf4\u51fa\u6765\u7684\u8bdd\u4e00\u5411\u7b97\u6570\u3002\u2026 \u3000\u3000\u8c01\u90fd\u4e0d\u77e5\u9053\u4ed6\u8981\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u641c\u9b42\u624b\u8bf4\u51fa\u90a3\u9876\u8f7f\u5b50\u91cc\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u53ea\u6709\u535c\u9e70\u77e5\u9053\u3002 \u3000\u3000\u201c\u4ed6\u8981\u8bf4\u7684\uff0c\u5c31\u662f\u6211\u8981\u8bf4\u7684\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u8bdd\u8ddf\u6211\u540c\u6837\u6709\u6548\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e92\u76f8\u51dd\u671b\u4e00\u773c\uff0c\u4e24\u4e2a\u4eba\u7684\u773c\u8272\u5df2\u8bf4\u51fa\u4ed6\u4eec\u5f7c\u6b64\u95f4\u7684\u4fe1\u4efb\u4e0e\u5c0a\u656c\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8bf4\u51fa\u7684\u8bdd\u8ba9\u6bcf\u4e2a\u4eba\u90fd\u5f88\u60ca\u8bb6\u3002 \u3000\u3000\u201c\u6211\u4eec\u4e0d\u60f3\u77e5\u9053\u90a3\u9876\u8f7f\u5b50\u91cc\u6709\u4ec0\u4e48\uff0c\u4e0d\u60f3\u542c\uff0c\u4e5f\u4e0d\u60f3\u770b\uff01\u201d\u4ed6\u7684\u58f0\u97f3\u51b0\u51b7\uff0c\u201c\u5982\u679c\u6709\u4eba\u8bf4\u51fa\u4e86\u90a3\u9876\u8f7f\u5b50\u91cc\u662f\u4ec0\u4e48\uff0c\u5982\u679c\u6709\u4eba\u8ba9\u6211\u770b\u89c1\u4e86\u90a3\u9876\u8f7f\u5b50\u91cc\u662f\u4ec0\u4e48\uff0c\u4e0d\u7ba1\u4ed6\u662f\u8c01\uff0c\u6211\u90fd\u4f1a\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u5c0f\u65b9\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u60f3\u5f00\u53e3\uff0c\u53c8\u5fcd\u4f4f\uff0c\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u901a\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8f6c\u8eab\u9762\u5bf9\u536b\u5929\u9e4f\uff1a\u201c\u73b0\u5728\u6211\u4eec\u4e4b\u95f4\u7684\u6218\u4e89\u5df2\u7ed3\u675f\uff0c\u4f60\u4eec\u5df2\u60e8\u8d25\u3002\u6211\u4eec\u7684\u6761\u4ef6\uff0c\u4f60\u90fd\u5f97\u63a5\u53d7\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5df2\u4e0d\u518d\u7a33\u5982\u76d8\u77f3\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u7ecf\u5728\u53d1\u6296\uff0c\u5634\u5507\u4e5f\u5728\u53d1\u6296\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u80fd\u95f4\u51fa\u4e00\u53e5\u8bdd\uff1a\u201c\u4f60\u4eec\u6709\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5374\u5df2\u95ed\u4e0a\u5634\uff0c\u9000\u5230\u535c\u9e70\u8eab\u540e\u3002 \u3000\u3000\u4ed6\u6709\u529b\u91cf\uff0c\u4f46\u5374\u4ece\u4e0d\u8f7b\u9732\uff0c\u4ed6\u6709\u6743\u529b\uff0c\u4f46\u5374\u7edd\u4e0d\u6ee5\u7528\u3002 \u3000\u3000\u5230\u4e86\u5e94\u8be5\u95ed\u4e0a\u5634\u65f6\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u5f00\u53e3\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4efb\u4f55\u5730\u65b9\u3001\u4efb\u4f55\u7ec4\u7ec7\u91cc\uff0c\u53d1\u53f7\u65bd\u4ee4\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df1\u8bf4\u51fa\u4e86\u4ed6\u8981\u8bf4\u7684\uff0c\u4ed6\u4e5f\u50cf\u522b\u4eba\u4e00\u6837\u7b49\u7740\u535c\u9e70\u4e0b\u4ee4 \u3000\u3000\u535c\u9e70\u7ec8\u4e8e\u5f00\u53e3\uff1a\u201c\u4f60\u4eec\u53ef\u4ee5\u628a\u90a3\u9876\u8f7f\u5b50\u5e26\u8d70\uff0c\u4f46\u662f\u4f60\u4eec\u4e0d\u80fd\u8fd9\u4e48\u6837\u8d70\u3002\u2026 \u3000\u3000\u4ed6\u8bf4\u51fa\u4e86\u4ed6\u7684\u6761\u4ef6\uff1a\u201c\u4f60\u4eec\u6bcf\u4e2a\u4eba\u90fd\u5f97\u7559\u4e0b\u70b9\u4e1c\u897f\u6765\u624d\u80fd\u8d70\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u4eec\u7559\u4e0b\u4ec0\u4e48\uff1f\u201d\u536b\u5929\u9e4f\u95f4\u51fa\u8fd9\u53e5\u8bdd\u65f6\uff0c\u58f0\u97f3\u5df2\u5636\u54d1\u3002 \u3000\u3000\u201c\u7559\u4e0b\u4e00\u6837\u80fd\u8ba9\u4f60\u4eec\u6c38\u8fdc\u8bb0\u4f4f\u8fd9\u6b21\u6559\u8bad\u7684\u4e1c\u897f\u3002\u201d\u535c\u9e70\u5ffd\u7136\u8f6c\u5411\u67f3\u5206\u5206\uff1a\u201c\u4f60\u8bf4\u4f60\u4eec\u5e94\u8be5\u7559\u4e0b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u662f\u53d1\u4ee4\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u8bf4\u51fa\u7684\u8bdd\u5c31\u662f\u547d\u4ee4\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u6562\u8fdd\u6297\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u95f4\u67f3\u5206\u5206\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u522b\u4eba\uff1f\u53ea\u95ee\u67f3\u5206\u5206\uff1f \u3000\u3000\u67f3\u5206\u5206\u4e5f\u5f88\u60ca\u8bb6\uff0c\u53ef\u662f\u5ffd\u7136\u95f4\u5979\u7684\u773c\u775b\u91cc\u5c31\u53d1\u51fa\u4e86\u5149\u3002 \u3000\u3000\u5979\u5ffd\u7136\u660e\u767d\u4e86\u535c\u9e70\u7684\u610f\u601d\u3002 \u3000\u3000\u5979\u770b\u7740\u535c\u9e70\u65f6\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u72e1\u72d0\u770b\u7740\u4e00\u53ea\u6355\u72d0\u7684\u9e70\uff0c\u867d\u7136\u6050\u60e7\u656c\u754f\uff0c\u5374\u53c8\u5e26\u7740\u4e00\u79cd\u9664\u4e86\u4ed6\u4eec\u81ea\u5df1\u5916\uff0c\u522b\u4eba\u7edd\u5bf9\u65e0\u6cd5\u4e86\u89e3\u7684\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u7adf\u4f3c\u5df1\u4e92\u76f8\u4e86\u89e3\u3002 \u3000\u3000\u535c\u9e70\u4e5f\u77e5\u9053\u5979\u5df2\u5b8c\u5168\u4e86\u89e3\u4ed6\u7684\u7528\u610f\uff0c\u624d\u653e\u8fc7\u4e86\u5979\u7684\u76ee\u5149\uff0c\u6de1\u6de1\u4ed6\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u4f60\u8bf4\u51fa\u6765\uff0c\u6211\u5c31\u7b54\u5e94\u3002\u201d \u3000\u3000\u67f3\u5206\u5206\u4eff\u4f5b\u5e26\u5728\u72b9\u7591\uff0c\u773c\u4e2d\u5374\u95ea\u51fa\u4e86\u72e1\u9ee0\u6076\u6bd2\u7684\u7b11\u610f\uff1a\u201c\u6211\u4eec\u662f\u4e00\u8d77\u6765\u7684\uff0c\u6211\u7559\u4e0b\u4e86\u4ec0\u4e48\uff0c\u4ed6\u4eec\u4e5f\u8be5\u7559\u4e0b\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5979\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u7559\u4e0b\u4e86\u4e00\u53ea\u624b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u6709\u624b\uff0c\u4ed6\u7684\u624b\u51b0\u51b7\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4e5f\u660e\u767d\u4e86\u535c\u9e70\u7684\u610f\u601d\u3002 \u3000\u3000\u535c\u9e70\u65e9\u5df2\u7b97\u51c6\u5979\u4f1a\u8fd9\u4e48\u8bf4\u7684\uff0c\u6240\u4ee5\u624d\u95f4\u5979\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u5979\u4e3a\u4e86\u4fdd\u62a4\u81ea\u5df1\u65f6\uff0c\u7edd\u5bf9\u4e0d\u60dc\u51fa\u5356\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u535c\u9e70\u8138\u4e0a\u5168\u65e0\u8868\u60c5\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4f60\u8bf4\u7684\u3002\u201d\u4ed6\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8ba4\u4e3a\u8fd9\u6837\u505a\u5f88\u516c\u5e73\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u67f3\u5206\u5206\u7acb\u523b\u56de\u7b54\uff1a\u201c\u7edd\u5bf9\u516c\u5e73\uff0c\u201d \u3000\u3000\u535c\u9e70\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4e5f\u4e0d\u518d\u770b\u5979\u3002 \u3000\u3000\u7528\u4e24\u6839\u624b\u6307\u634f\u4f4f\u5200\u950b\uff0c\u5c06\u521a\u624d\u4ece\u536b\u5929\u9e4f\u624b\u91cc\u593a\u8fc7\u6765\u7684\uff0c\u6162\u6162\u5730\u9001\u5230\u536b\u5929\u9e4f\u9762\u524d\u3002 \u3000\u3000\u4ed6\u4e0d\u5fc5\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u536b\u5929\u9e4f\u8fd8\u80fd\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u5df2\u60e8\u8d25\u3002 \u3000\u3000\u4e00\u4e2a\u60e8\u8d25\u4e86\u7684\u4eba\uff0c\u9664\u4e86\u6d41\u6cea\u5916\uff0c\u53ea\u6709\u6d41\u8840\u3002 \u3000\u3000\u6d41\u4e0d\u5b8c\u7684\u8840\uff01 \u3000\u3000\u5200\u950b\u51b0\u51b7\uff0c\u5200\u67c4\u4e5f\u540c\u6837\u51b7\u3002 \u3000\u3000\u624b\u66f4\u51b7\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7528\u51b0\u51b7\u7684\u624b\u63a5\u8fc7\u51b0\u51b7\u7684\u5200\uff0c\u51dd\u89c6\u7740\u5bd2\u5149\u95ea\u52a8\u7684\u5200\u950b\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u5200\u3002 \u3000\u3000\u4ed6\u7528\u8fd9\u628a\u5200\u780d\u4e0b\u8fc7\u522b\u4eba\u7684\u5934\u9885\uff0c\u5272\u65ad\u8fc7\u522b\u4eba\u7684\u54bd\u5589\uff0c\u4ed6\u4e5f\u7528\u8fd9\u628a\u5200\u780d\u65ad\u8fc7\u522b\u4eba\u7684\u624b\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u7684\u795e\u60c5\u53c8\u6062\u590d\u9547\u5b9a\uff0c\u5df2\u51c6\u5907\u63a5\u53d7\u8fd9\u4ef6\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u5df2\u4e0d\u80fd\u9003\u907f\u3002 \u3000\u3000\u4e8b\u5b9e\u672c\u5c31\u662f\u6b8b\u9177\u7684\uff0c\u7edd\u4e0d\u5bb9\u4eba\u9003\u907f\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u8981\u6211\u54ea\u53ea\u624b\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8fd9\u95ee\u9898\u535c\u9e70\u5fc5\u5b9a\u62d2\u7edd\u56de\u7b54\uff0c\u4ed6\u7528\u5de6\u624b\u63e1\u5200\uff0c\u5c06\u53f3\u624b\u4f38\u51fa\u3002 \u3000\u3000\u201c\u8fd9\u662f\u6211\u63e1\u5200\u6740\u4eba\u7684\u624b\uff0c\u6211\u628a\u8fd9\u53ea\u624b\u7ed9\u4f60\uff0c\u4eca\u751f\u6211\u7edd\u4e0d\u518d\u7528\u5200\u3002\u201d \u3000\u3000\u662f\u4e0d\u518d\u7528\u5200\uff0c\u4e0d\u662f\u4e0d\u518d\u6740\u4eba\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u4f46\u662f\u53ea\u8981\u6211\u4e0d\u6b7b\uff0c\u6211\u4e00\u5b9a\u8981\u6740\u4e86\u4f60\uff0c\u4e0d\u7ba1\u7528\u4ec0\u4e48\u6cd5\u4e86\uff0c\u90fd\u8981\u6740\u4e86\u4f60\u3002\u5c31\u7b97\u4f60\u780d\u65ad\u6211\u4e24\u53ea\u624b\uff0c\u53ea\u8981\u6211\u8fd8\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u6211\u4e5f\u8981\u7528\u5634\u54ac\u65ad\u4f60\u7684\u54bd\u5589\uff0c\u5c1d\u5c1d\u4f60\u7684\u8840\u662f\u4ec0\u4e48\u6ecb\u5473\uff01\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u6781\u5e73\u9759\uff0c\u53ef\u662f\u6bcf\u53e5\u8bdd\uff0c\u6bcf\u4e2a\u5b57\u91cc\uff0c\u90fd\u5e26\u7740\u79cd\u4ee4\u4eba\u51b7\u5165\u9aa8\u9ad3\u7684\u5bd2\u610f\uff0c\u5c31\u50cf\u662f\u6765\u81ea\u5730\u72f1\u7fa4\u9b3c\u7684\u6bd2\u5492\u3002 \u3000\u3000\u535c\u9e70\u8138\u4e0a\u8fd8\u662f\u5168\u65e0\u8868\u60c5\u3002 \u3000\u3000\u201c\u5f88\u597d\u3002\u201d\u4ed6\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u4f1a\u7ed9\u4f60\u6700\u597d\u7684\u4f24\u836f\uff0c\u8ba9\u4f60\u597d\u597d\u5730\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u63e1\u5200\u7684\u624b\u4e0a\u9752\u7b4b\u66b4\u8d77\uff0c\u5df2\u51c6\u5907\u63e1\u5200\u780d\u4e0b\u53bb\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u53c8\u559d\u6b62\uff1a\u201c\u7b49\u4e00\u7b49\uff01\u201d \u3000\u3000\u201c\u8fd8\u8981\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd8\u8981\u8ba9\u4f60\u770b\u4e00\u4ef6\u4e8b\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4f60\u770b\u8fc7\u4e4b\u540e\uff0c\u624d\u4f1a\u77e5\u9053\u4f60\u81ea\u5df1\u8fd9\u4e00\u6b21\u6765\u5f97\u591a\u4e48\u611a\u8822\uff01\u201d \u3000\u3000\u535c\u9e70\u6325\u624b\u4e0b\u4ee4\uff0c\u6240\u6709\u7684\u8d27\u7269\u7acb\u523b\u5168\u90fd\u5806\u79ef\u5230\u5e10\u7bf7\u524d\uff0c\u6bcf\u4e00\u5305\u8d27\u7269\u90fd\u6253\u5f00\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u9ec4\u91d1\u3002 \u3000\u3000\u201c\u9ec4\u91d1\u6839\u672c\u4e0d\u5728\u8fd9\u91cc\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4f60\u6839\u672c\u4e0d\u8be5\u6765\u7684\u3002\u8fd9\u4ef6\u4e8b\u4f60\u505a\u5f97\u4e0d\u4f46\u611a\u8822\uff0c\u800c\u4e14\u65e0\u77e5\uff0c\u4f60\u81ea\u5df1\u4e5f\u5fc5\u5c06\u540e\u6094\u7ec8\u751f\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u9759\u9759\u5730\u542c\u7740\uff0c\u5168\u65e0\u53cd\u5e94\uff0c\u7b49\u4ed6\u8bf4\u5b8c\u4e86\uff0c\u624d\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\u3002\u201d\u536b\u5929\u9e4f\u5ffd\u7136\u51b7\u7b11\uff0c\u201c\u5176\u5b9e\u8fde\u8fd9\u4e9b\u8bdd\u4f60\u90fd\u4e0d\u5fc5\u8bf4\u7684\u3002\u201d \u3000\u3000\u4ed6\u6325\u5200\u3002 \u3000\u3000\u5200\u950b\u843d\u4e0b\u65f6\uff0c\u5916\u9762\u9a6c\u80cc\u4e0a\u7684\u4e03\u5341\u6218\u58eb\u5ffd\u7136\u540c\u58f0\u60e8\u547c\u3002 \u3000\u3000\u4e03\u5341\u4e2a\u4eba\uff0c\u4e03\u5341\u6761\u624b\u81c2\uff0c\u90fd\u5df2\u88ab\u4ed6\u4eec\u80cc\u540e\u7684\u4eba\u62e7\u65ad\u3002 \u3000\u3000\u7528\u6700\u6709\u6548\u7684\u624b\u6cd5\u62e7\u65ad\uff0c\u4e00\u62e7\u5c31\u65ad\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u7684\u786e\u90fd\u662f\u4e45\u7ecf\u8bad\u7ec3\u3002\u767e\u6218\u4e0d\u6b7b\u7684\u5065\u513f\uff0c\u53ef\u662f\u8fd9\u4e00\u6b21\u4ed6\u4eec\u7adf\u8fde\u8fd8\u624b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6218\u9a6c\u60ca\u5636\uff0c\u5954\u51fa\u8425\u5730\uff0c\u8f7f\u5b50\u4e5f\u5df2\u88ab\u62ac\u8d70\uff0c\u4e09\u9876\u8f7f\u5b50\u90fd\u88ab\u62ac\u8d70\u3002 \u3000\u3000\u8e44\u58f0\u6e10\u8fdc\u3001\u6e10\u65e0\uff0c\u6b22\u996e\u9ad8\u6b4c\u4e5f\u4e0d\u590d\u518d\u6709\uff0c\u8fde\u71c3\u70e7\u7684\u8425\u706b\u90fd\u5df2\u5c06\u7184\u706d\u3002 \u3000\u3000\u5929\u5df2\u5feb\u4eae\u4e86\u3002 \u3000\u3000\u9ece\u660e\u524d\u603b\u6709\u6bb5\u6700\u9ed1\u6697\u7684\u65f6\u5019\uff0c\u5e10\u7bf7\u91cc\u7684\u7f8a\u89d2\u706f\u4ecd\u7136\u70b9\u5f97\u5f88\u4eae\u3002 \u3000\u3000\u5b8b\u8001\u592b\u5b50\u201c\u9189\u4e86\u201d\uff0c\u4e25\u8001\u5148\u751f\u201c\u7d2f\u4e86\u201d\uff0c\u8be5\u8d70\u7684\u4eba\u90fd\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u8fd8\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e5f\u6ca1\u6709\u5750\u4e0b\u6765\uff0c\u4ed6\u4e00\u76f4\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u522b\u4eba\u7684\u6765\u53bb\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u535c\u9e70\u548c\u73ed\u5bdf\u5df4\u90a3\u7684\u5b58\u5728\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u660e\u660e\u5728\u8fd9\u91cc\uff0c\u5374\u53c8\u4eff\u4f5b\u5230\u4e86\u8fdc\u65b9\uff0c\u5230\u4e86\u8fdc\u65b9\u4e00\u4e2a\u548c\u5e73\u5b81\u9759\u3001\u65e0\u6069\u65e0\u6028\u3001\u65e0\u60c5\u65e0\u7231\u7684\u5730\u65b9\u3002 \u3000\u3000\u535c\u9e70\u51dd\u89c6\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8ba4\u4e3a\u6211\u4e0d\u8be5\u505a\u5f97\u8fd9\u4e48\u7edd\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u201c\u6211\u4e0d\u7ba1\u4f60\u600e\u4e48\u60f3\uff0c\u53ea\u8981\u4f60\u660e\u767d\u4e00\u70b9\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u654c\u6211\u4e4b\u95f4\uff0c\u5c31\u50cf\u662f\u5200\u950b\u4e00\u6837\uff0c\u65e2\u65e0\u4f59\u60c5\uff0c\u4e5f\u65e0\u4f59\u5730\uff0c\u6211\u82e5\u8d25\u4e86\uff0c\u6211\u7684\u4e0b\u573a\u4e00\u5b9a\u66f4\u60e8\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u4f55\u51b5\u8fd9\u4e00\u6b21\u672c\u6765\u5c31\u662f\u4ed6\u4eec\u6765\u627e\u6211\u7684\uff0c\u6211\u4eec\u65e2\u7136\u4e0d\u80fd\u4e0d\u6218\uff0c\u8981\u6218\uff0c\u5c31\u4e00\u5b9a\u8981\u80dc\uff0c\u5bf9\u654c\u4eba\u5c31\u7edd\u4e0d\u80fd\u7559\u60c5\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e0d\u53d8\u7684\u771f\u7406\uff0c\u6ca1\u6709\u4eba\u80fd\u53cd\u9a73\u3002 \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u8fd9\u9053\u7406\u4f60\u4e00\u5b9a\u4e5f\u660e\u767d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u61c2\uff01\u201d \u3000\u3000\u4ed6\u770b\u6765\u5c31\u50cf\u662f\u5ffd\u7136\u81ea\u5669\u68a6\u4e2d\u60ca\u9192\uff1a\u201c\u4f60\u4eec\u505a\u7684\u4e8b\uff0c\u6211\u5168\u90fd\u4e0d\u61c2\uff01\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u82cd\u767d\u82f1\u4fca\u7684\u8138\u4e0a\u5df2\u6709\u5f88\u4e45\u672a\u89c1\u7684\u7b11\u5bb9\uff1a\u201c\u4f60\u4e0d\u61c2\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u4ed6\u4eec\u5c06\u90a3\u7b2c\u4e09\u9876\u8f7f\u5b50\u62ac\u8d70\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u65e9\u5df2\u60f3\u95ee\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u61c2\uff0c\u53ea\u56e0\u4e3a\u6709\u5f88\u591a\u4e8b\u4f60\u90fd\u542c\u4e0d\u89c1\uff0c\u6709\u5f88\u591a\u4e8b\u4f60\u90fd\u770b\u4e0d\u89c1\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u5c0f\u65b9\u5f00\u53e3\uff0c\u56e0\u4e3a\u4ed6\u4e00\u5b9a\u8981\u5148\u5c06\u81ea\u5df1\u5e94\u8be5\u8bf4\u7684\u8bdd\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u61c2\uff0c\u53ea\u56e0\u4e3a\u4f60\u5e74\u8f7b\uff0c\u8fd8\u6ca1\u6709\u7ecf\u8fc7\u6211\u4eec\u8fd9\u4e48\u591a\u60e8\u75db\u7684\u7ecf\u9a8c\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u6001\u5ea6\u4e25\u8083\u800c\u8bda\u6073\uff1a\u201c\u5982\u679c\u4f60\u4e5f\u8ddf\u6211\u4eec\u4e00\u6837\uff0c\u4e5f\u66fe\u5728\u8fd9\u5757\u5927\u5730\u4e0a\u751f\u6d3b\u4e86\u5341\u5e74\uff0c\u51e0\u4e4e\u6b7b\u8fc7\u4e8c\u5341\u6b21\uff0c\u90a3\u4e48\u4f60\u4e5f\u4f1a\u542c\u89c1\u4e00\u4e9b\u522b\u4eba\u542c\u4e0d\u89c1\u7684\u4e8b\uff0c\u4e5f\u4f1a\u770b\u89c1\u4e00\u4e9b\u522b\u4eba\u770b\u4e0d\u89c1\u7684\u4e8b\u4e86\u3002\u201d\u4ed6\u7684\u6001\u5ea6\u4f7f\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u51b7\u9759\u4e0b\u6765\u3002 \u3000\u3000\u201c\u6211\u542c\u4e0d\u89c1\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u4f60\u4eec\u53c8\u542c\u89c1\u4e86\u4ec0\u4e48\uff1f\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u9876\u8f7f\u5b50\u6bd4\u5176\u4ed6\u4e24\u9876\u90fd\u91cd\u4e00\u70b9\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u800c\u4e14\u8f7f\u5b50\u91cc\u6709\u4e24\u4e2a\u4eba\u7684\u547c\u5438\u58f0\u3002\u201d \u3000\u3000\u535c\u9e70\u66ff\u4ed6\u63a5\u4e0b\u53bb\u8bf4\uff1a\u201c\u662f\u4e24\u4e2a\u5973\u4eba\u7684\u547c\u5438\u58f0\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u7684\u547c\u5438\u5df2\u7ecf\u5f88\u5fae\u5f31\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u53d1\u73b0\u81ea\u5df1\u5e94\u8be5\u5b66\u4e60\u7684\u4e8b\u8fd8\u6709\u5f88\u591a\uff0c\u8fdc\u6bd4\u4ed6\u81ea\u5df1\u672c\u6765\u7684\u60f3\u8c61\u4e2d\u591a\u5f97\u591a\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u8981\u95ee\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u77e5\u9053\u8f7f\u5b50\u91cc\u662f\u4e24\u4e2a\u5973\u4eba\uff1f\u5973\u4eba\u7684\u547c\u5438\u96be\u9053\u4e5f\u8ddf\u7537\u4eba\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u540c\u3002\u201d\u535c\u9e70\u56de\u7b54\u3002 \u3000\u3000\u201c\u6211\u4eec\u77e5\u9053\u8f7f\u5b50\u91cc\u662f\u4e24\u4e2a\u5973\u4eba\uff0c\u53ea\u56e0\u4e3a\u90a3\u9876\u8f7f\u5b50\u53ea\u6bd4\u641c\u9b42\u624b\u5750\u7684\u90a3\u9876\u91cd\u4e00\u70b9\u3002\u201d \u3000\u3000\u535c\u9e70\u53c8\u9053\uff1a\u201c\u6211\u4eec\u662f\u4ece\u62ac\u8f7f\u5b50\u7684\u4eba\u811a\u4e0b\u5e26\u8d77\u7684\u5c18\u6c99\u4e0a\u770b\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u8fd9\u6b21\u662f\u73ed\u5bdf\u5df4\u90a3\u66ff\u4ed6\u63a5\u7740\u8bf4\u4e86\u4e0b\u53bb\uff1a\u201c\u8f7f\u5b50\u7684\u8d28\u6599\u548c\u91cd\u91cf\u90fd\u662f\u4e00\u6837\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u641c\u9b42\u624b\u7ec3\u7684\u662f\u5916\u529f\uff0c\u4eba\u867d\u7136\u7626\uff0c\u9aa8\u5934\u5374\u91cd\uff0c\u800c\u4e14\u5f88\u9ad8\uff0c\u5927\u6982\u6709\u4e00\u767e\u4e8c\u5341\u65a4\u5de6\u53f3\u3002\u201d \u3000\u3000\u201c\u90a3\u4e24\u4e2a\u4eba\u52a0\u8d77\u6765\u6700\u591a\u53ea\u6bd4\u4ed6\u4e00\u4e2a\u4eba\u91cd\u4e8c\u4e09\u5341\u65a4\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e0b\u4e86\u4e2a\u5f88\u5947\u602a\u7684\u7ed3\u8bba\uff1a\u201c\u8fd9\u4e2a\u91cd\u91cf\u521a\u597d\u662f\u5979\u4eec\u4e24\u4e2a\u4eba\u52a0\u8d77\u6765\u7684\u91cd\u91cf\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u7acb\u523b\u5c31\u95ee\uff1a\u201c\u5979\u4eec\u4e24\u4e2a\u4eba\uff0c\u54ea\u4e24\u4e2a\u4eba\uff1f\u4f60\u77e5\u9053\u662f\u54ea\u4e24\u4e2a\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u5a07\u96c5\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5176\u4e2d\u4e00\u5b9a\u6709\u4e00\u4e2a\u662f\u5a07\u96c5\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u542c\u8fc7\u8fd9\u540d\u5b57\uff1a\u201c\u5a07\u96c5\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u8868\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u60b2\u4f24\uff01 \u3000\u3000\u201c\u5982\u679c\u4f60\u8981\u4e86\u89e3\u5a07\u96c5\u8fd9\u4e2a\u4eba\uff0c\u5c31\u4e00\u5b9a\u8981\u5148\u542c\u4e00\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u4e2a\u60b2\u4f24\u7684\u6545\u4e8b\uff01 \u3000\u3000\u5a07\u96c5\u662f\u4e2a\u5973\u4eba\uff0c\u662f\u5343\u767e\u5e74\u524d\u751f\u957f\u5728\u5723\u6bcd\u4e4b\u6c34\u5cf0\u5317\u9e93\u3001\u53e4\u4ee3\u7684\u5ed3\u5c14\u5580\u65cf\u4e2d\u4e00\u4e2a\u4f1f\u5927\u800c\u5723\u6d01\u7684\u5973\u4eba\uff0c\u4e3a\u4e86\u5979\u7684\u65cf\u4eba\uff0c\u800c\u727a\u7272\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u5728\u51f6\u6076\u6b79\u6bd2\u5f3a\u608d\u65e0\u803b\u7684\u5c3c\u514b\u65cf\u4eba\u56f4\u653b\u5ed3\u5c14\u5580\u90e8\u843d\u65f6\uff0c\u5979\u7684\u65cf\u4eba\u88ab\u51fb\u8d25\u4e86\u3002 \u3000\u3000\u5c3c\u514b\u65cf\u7684\u6807\u5fd7\u662f\u201c\u7ea2\u201d\uff0c\u5e26\u7740\u8840\u8165\u7684\u201c\u7ea2\u201d\uff0c\u4ed6\u4eec\u559c\u6b22\u8165\u7ea2\u548c\u8840\u6c61\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u914b\u957f\u6d3b\u6349\u4e86\u5a07\u96c5\uff0c\u73b7\u6c61\u4e86\u5979\u3002 \u3000\u3000\u5979\u5fcd\u53d7\uff0c\u56e0\u4e3a\u5979\u8981\u590d\u4ec7\u3002 \u3000\u3000\u4ee5\u7259\u8fd8\u7259\uff0c\u4ee5\u8840\u8fd8\u8840\uff0c\u5979\u7ec8\u4e8e\u7b49\u5230\u673a\u4f1a\uff0c\u6551\u4e86\u540c\u65cf\u90a3\u4e2a\u88ab\u4fd8\u7684\u914b\u957f\uff0c\u6551\u4e86\u5979\u7684\u65cf\u4eba\u3002 \u3000\u3000\u5979\u81ea\u5df1\u4e5f\u4e0d\u5f97\u4e0d\u727a\u7272\u3002 \u3000\u3000\u7b49\u5230\u5979\u7684\u6c11\u65cf\u590d\u4ec7\u5927\u519b\u653b\u5165\u5c3c\u514b\u65cf\u914b\u957f\u7684\u5927\u5e10\u4e0b\u65f6\uff0c\u5979\u5df2\u5316\u4f5c\u82b3\u9b42\u3002 \u3000\u3000\u662f\u82b3\u9b42\uff0c\u4e5f\u662f\u5fe0\u9b42\u3002 \u3000\u3000\u5979\u624b\u91cc\u8fd8\u7d27\u63e1\u7740\u5979\u5728\u4e34\u6b7b\u524d\u5199\u7ed9\u5979\u60c5\u4eba\u201c\u679c\u987f\u201d\u7684\u4e00\u9996\u60c5\u66f2\u3002 \u3000\u3000\u662f\u60c5\u66f2\uff0c\u4e5f\u662f\u53f2\u8bd7\u3002 \u3000\u3000\u8bf7\u62fe\u5f97\u8fd9\u652f\u6b4c\u66f2\u7684\u4eba\u3002 \u3000\u3000\u59a5\u4ea4\u7ed9\u6211\u90a3\u4f4f\u5728\u67af\u6eaa\u4e0b\u7684\u679c\u987f\u3002 \u3000\u3000\u6211\u7231\u7684\u679c\u987f\uff0c\u4f60\u4e00\u5b9a\u8981\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u4f60\u8981\u751f\u5b58\uff0c\u5c31\u8be5\u8b66\u60d5\u3002 \u3000\u3000\u65f6\u523b\u8b66\u60d5\uff0c\u6c38\u8fdc\u8bb0\u4f4f\uff0c\u8bb0\u4f4f\u90a3\u4e9b\u559c\u6b22\u6c61\u8165\u8840\u7ea2\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u597d\u6740\u7684\u3002 \u3000\u3000\u4f60\u9047\u5230\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u5fc5\u7559\u60c5\u3002 \u3000\u3000\u4f60\u8981\u5c06\u4ed6\u4eec\u8d76\u5165\u7a77\u6d77\uff0c\u8d76\u5165\u8352\u585e\uff0c\u91cd\u5efa\u4f60\u7f8e\u4e3d\u7684\u6545\u56fd\u7530\u56ed\u3002 \u3000\u3000\u6545\u56fd\u867d\u5df2\u6c89\u6ca6\uff0c \u3000\u3000\u7530\u56ed\u867d\u5df2\u8352\u829c\uff0c \u3000\u3000\u53ef\u662f\u53ea\u8981\u4f60\u52e4\u52c9\u52aa\u529b\uff0c\u6211\u4eec\u7684\u6545\u56fd\u5fc5\u5c06\u590d\u5174\uff0c\u7530\u56ed\u5fc5\u5c06\u91cd\u5efa\u3002 \u3000\u3000\u5979\u7684\u60c5\u4eba\u6ca1\u6709\u8f9c\u8d1f\u5979\uff0c\u5979\u7684\u65cf\u4eba\u4e5f\u6ca1\u6709\u8f9c\u8d1f\u5979\u3002 \u3000\u3000\u5979\u7684\u6545\u56fd\u5df1\u590d\u5174\uff0c\u6545\u56fd\u5df2\u91cd\u5efa\u3002 \u3000\u3000\u5979\u7684\u767d\u9aa8\u548c\u5979\u7684\u8bd7\uff0c\u90fd\u5df2\u88ab\u846c\u5728\u4e3a\u5979\u800c\u5efa\u7684\u5a07\u96c5\u5bfa\u767d\u5854\u4e0b\uff0c\u6c38\u8fdc\u53d7\u4eba\u5c0a\u656c\u5d07\u62dc\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u60b2\u60e8\u7684\u6545\u4e8b\uff0c\u4e0d\u662f\u4e2a\u58ee\u70c8\u7684\u6545\u4e8b\uff0c\u6c38\u8fdc\u503c\u5f97\u540e\u4eba\u8bb0\u5fc6\u8b66\u60d5\u3002 \u3000\u3000\u5343\u5343\u4e07\u4e07\u5e74\u4e4b\u540e\u7684\u4eba\uff0c\u90fd\u5e94\u8be5\u4e3a\u6b64\u8b66\u60d5\u3002 \u3000\u3000\u56e0\u4e3a\u771f\u7406\u867d\u7136\u5e38\u5728\uff0c\u6b63\u4e49\u867d\u7136\u6c38\u5b58\uff0c\u4eba\u4e16\u95f4\u5374\u8fd8\u662f\u96be\u514d\u6709\u4e9b\u8840\u8165\u7684\u4eba\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5e94\u8be5\u50cf\u5a07\u96c5\u4e00\u6837\uff0c\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\u53bb\u6d88\u706d\u4ed6\u4eec\u3002 \u3000\u3000\u73b0\u5728\u73ed\u5bdf\u5df4\u90a3\u5df2\u8bf4\u5b8c\u4e86\u8fd9\u4e2a\u6545\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u4e0a\u4e2a\u4eba\u5982\u679c\u80f8\u4e2d\u5df2\u6709\u70ed\u8840\u6cb8\u817e\uff0c\u600e\u4e48\u4f1a\u6d41\u6cea\uff1f \u3000\u3000\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u4e0d\u80fd\u4e0d\u95ee\uff1a\u201c\u5979\u7684\u767d\u9aa8\u65e2\u7136\u5df2\u57cb\u5728\u767d\u5854\u4e0b\uff0c\u4f60\u4eec\u8bf4\u7684\u8fd9\u4e2a\u5a07\u96c5\u662f\u8c01\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u56de\u7b54\u53c8\u8ba9\u4ed6\u60ca\u8bb6\u3002 \u3000\u3000\u201c\u6211\u4eec\u8bf4\u7684\u8fd9\u4e2a\u5a07\u96c5\uff0c\u5c31\u662f\u4f60\u4e00\u76f4\u8ba4\u4e3a\u5979\u5c31\u662f\u6c34\u94f6\u7684\u90a3\u4e2a\u5973\u4eba\uff0c\u201d \u3000\u3000\u5c0f\u65b9\u6014\u4f4f\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u663e\u5f97\u66f4\u60b2\u4f24\uff1a\u201c\u5979\u662f\u6211\u4eec\u7684\u65cf\u4eba\uff0c\u5979\u77e5\u9053\u5415\u4e09\u4e00\u76f4\u5728\u538b\u69a8\u6211\u4eec\uff0c\u5c31\u50cf\u662f\u90a3\u4e9b\u8840\u8165\u7684\u6076\u6c49\u4e00\u76f4\u5728\u538b\u69a8\u5a07\u96c5\u7684\u65cf\u4eba\u4e00\u6837\uff0c\u6240\u4ee5\u5979\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\u3002\u201d \u3000\u3000\u535c\u9e70\u5ffd\u7136\u63d2\u53e3\uff1a\u201c\u56e0\u4e3a\u5979\u4e0d\u4f46\u662f\u4ed6\u7684\u65cf\u4eba\u4e5f\u662f\u4ed6\u7684\u60c5\u4eba\uff0c\u5979\u727a\u7272\u4e86\u81ea\u5df1\u5230\u5979\u7684\u654c\u4eba\u90a3\u91cc\u53bb\u5367\u5e95\uff0c\u53bb\u523a\u63a2\u4ed6\u4eec\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u63e1\u4f4f\u4e86\u5c0f\u65b9\u7684\u624b\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u5979\u5bf9\u4f60\u505a\u8fc7\u7684\u90a3\u4e9b\u4e8b\uff0c\u53ef\u662f\u6211\u4fdd\u8bc1\uff0c\u5979\u4e00\u5b9a\u662f\u88ab\u903c\u51fa\u6765\u7684\uff0c\u4e3a\u4e86\u6211\uff0c\u4e3a\u4e86\u6211\u4eec\u7684\u65cf\u4eba\uff0c\u5979\u4e0d\u80fd\u4e0d\u8fd9\u4e48\u505a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e86\u89e3\u3002 \u3000\u3000\u4ed6\u4e5f\u7d27\u63e1\u4f4f\u73ed\u5bdf\u5df4\u90a3\u7684\u624b\uff1a\u201c\u6211\u4e0d\u602a\u5979\uff0c\u5982\u679c\u6211\u662f\u5979\uff0c\u6211\u4e5f\u4f1a\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u624b\u51b0\u51b7\uff1a\u201c\u4f46\u662f\u73b0\u5728\u5979\u7684\u79d8\u5bc6\u5df2\u7ecf\u88ab\u63ed\u7a7f\u4e86\uff0c\u5bf9\u65b9\u5df2\u7ecf\u77e5\u9053\u5979\u662f\u6211\u4eec\u6d3e\u53bb\u7684\u4eba\u3002\u201d \u3000\u3000\u535c\u9e70\u53c8\u63a5\u7740\u8bf4\u4e0b\u53bb\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec\u6d3e\u4e86\u4e00\u4e2a\u4eba\u628a\u5979\u62bc\u5230\u8fd9\u91cc\u6765\uff0c\u8ddf\u5979\u5750\u5728\u4e00\u9876\u8f7f\u5b50\u91cc\uff0c\u5230\u4e86\u6700\u540e\u5173\u5934\uff0c\u5c31\u53ef\u4ee5\u7528\u5979\u6765\u8981\u631f\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u4ed6\u4eec\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4eec\u5c45\u7136\u4f1a\u8d25\u5f97\u8fd9\u4e48\u5feb\u3001\u90a3\u4e48\u60e8\uff0c\u6240\u6709\u7684\u53d8\u5316\u5b8c\u5168\u8ba9\u4ed6\u4eec\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6c89\u75db\u800c\u6fc0\u52a8\uff1a\u201c\u53ea\u4e0d\u8fc7\u5979\u8fd8\u662f\u4ed6\u4eec\u6700\u540e\u7684\u4e00\u4ef6\u6b66\u5668\uff0c\u6240\u4ee5\u6211\u8fd8\u662f\u4e0d\u80fd\u770b\u89c1\u5979\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u5229\u7528\u5979\u6765\u8981\u631f\u6211\u3002\u201d \u3000\u3000\u6240\u4ee5\u4ed6\u53ea\u6709\u5148\u53d1\u5236\u4eba\uff01 \u3000\u3000\u2014\u2014\u5982\u679c\u6709\u4eba\u8ba9\u4ed6\u770b\u89c1\u5979\uff0c\u4ed6\u5c31\u4e00\u5b9a\u4f1a\u6740\u4e86\u90a3\u4e2a\u4eba\uff01\u8fd9\u4e00\u70b9\u4e5f\u5df2\u4ee4\u4ed6\u4eec\u786e\u4fe1\u4e0d\u7591\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4ee5\u540e\u8bf4\u4e0d\u5b9a\u8fd8\u80fd\u5229\u7528\u5979\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u8ba9\u5979\u6d3b\u4e0b\u53bb\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e5f\u53ea\u6709\u8ba9\u4ed6\u4eec\u628a\u90a3\u9876\u8f7f\u5b50\u539f\u5c01\u4e0d\u52a8\u62ac\u8d70\u3002\u201d \u3000\u3000\u201c\u8f7f\u5b50\u91cc\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u5c31\u662f\u552f\u4e00\u80fd\u63ed\u7a7f\u8fd9\u79d8\u5bc6\u7684\u4eba\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u5979\u4e5f\u5750\u5728\u8f7f\u5b50\u91cc\uff0c\u5979\u77e5\u9053\u81ea\u5df1\u7edd\u5bf9\u5b89\u5168\uff0c\u6240\u4ee5\u5979\u66f4\u4e0d\u4f1a\u5984\u52a8\u3002\u201d \u3000\u3000\u201c\u6211\u65e9\u5c31\u8ba4\u5f97\u5979\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u4f46\u662f\u6211\u4e5f\u4ece\u672a\u60f3\u5230\u5979\u662f\u4e2a\u8fd9\u4e48\u6837\u53ef\u6015\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4eec\u90fd\u6ca1\u6709\u8bf4\u51fa\u201c\u5979\u201d\u662f\u8c01\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u95ee\uff0c\u4e0d\u6562\u95ee\uff0c\u4e5f\u4e0d\u5fc5\u95ee\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4ed6\u4eec\u4e0d\u8bf4\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u80fd\u8bf4\uff0c\u4e0d\u5fcd\u8bf4\uff0c\u4e5f\u4e0d\u5fc5\u8bf4\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u4e0d\u613f\u4f24\u5c0f\u65b9\u7684\u5fc3\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u6709\u4e2a\u201c\u6b7b\u9888\u201d\uff0c\u4e00\u4e2a\u5f88\u96be\u7a7f\u8fc7\u53bb\u7684\u6b7b\u9888\u3002 \u3000\u3000\u5982\u679c\u4f60\u4e00\u5b9a\u8981\u7a7f\u8fc7\u53bb\uff0c\u5c31\u4e00\u201c\u5b9a\u4f1a\u4f24\u5230\u8fd9\u4e2a\u4eba\u7684\u5fc3\u3002 \u3000\u3000\u6ce2\u5a03\uff0c\u4f60\u771f\u662f\u4e2a\u8fd9\u6837\u7684\u4eba\uff1f \u3000\u3000\u5a07\u96c5\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u727a\u7272\uff1f \u3000\u3000\u5979\u4ed8\u51fa\u4e86\u8fd9\u4e48\u5927\u7684\u4ee3\u4ef7\uff0c\u6362\u56de\u6765\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u523a\u63a2\u5230\u4ec0\u4e48\u79d8\u5bc6\uff1f\u662f\u4e0d\u662f\u548c\u90a3\u6279\u5931\u52ab\u7684\u9ec4\u91d1\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u8fd9\u4e2a\u961f\u4f0d\u4e2d\u672c\u6765\u90fd\u662f\u5e73\u51e1\u7684\u5546\u65c5\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u663e\u9732\u51fa\u4e00\u70b9\u6b66\u529f\uff0c\u600e\u4e48\u80fd\u5728\u7247\u523b\u95f4\u5236\u4f4f\u4e03\u5341\u4e2a\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218\u58eb\uff1f \u3000\u3000\u5b8b\u8001\u592b\u5b50\u548c\u4e25\u6b63\u521a\u66f4\u662f\u8eab\u6000\u7edd\u6280\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u9690\u85cf\u81ea\u5df1\u7684\u6b66\u529f\uff1f \u3000\u3000\u4ed6\u4eec\u7a76\u7adf\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u6709\u4ec0\u4e48\u79d8\u5bc6\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u5c0f\u65b9\u90fd\u6ca1\u6709\u518d\u95f4\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u77e5\u9053\u7684\u5df2\u591f\u591a\u3002 \u3000\u3000\u9ec4\u91d1\u4e0d\u662f\u5728\u4ed6\u4eec\u7684\u8d27\u7269\u5305\u88f9\u91cc\u3002 \u3000\u3000\u535c\u9e70\u662f\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u9ec4\u91d1\u7684\u4e0b\u843d\u5c0f\u65b9\u6839\u672c\u5c31\u4e0d\u5173\u5fc3\uff0c\u4ed6\u53ea\u8981\u77e5\u9053\u6709\u4eba\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u5bf9\u4e00\u4e2a\u50cf\u4ed6\u8fd9\u6837\u7684\u6d6a\u5b50\u6765\u8bf4\uff0c\u4e00\u4e2a\u771f\u6b63\u670b\u53cb\u7684\u4ef7\u503c\u7edd\u4e0d\u662f\u4efb\u4f55\u4e8b\u80fd\u6bd4\u5f97\u4e0a\u7684\u3002 \u3000\u3000\u9ece\u660e\u3002 \u3000\u3000\u65ed\u65e5\u5347\u8d77\uff0c\u5927\u5730\u4e00\u671b\u65e0\u9645\uff0c\u7802\u783e\u95ea\u8000\u5982\u91d1\u3002 \u3000\u3000\u5927\u5730\u65e0\u60c5\u3001\u8352\u829c\u3001\u51b7\u9177\uff0c\u9177\u5bd2\u3001\u9177\u70ed\uff0c\u53ef\u662f\u8fd9\u4e00\u7247\u65e0\u60c5\u7684\u5927\u5730\uff0c\u4e5f\u6709\u5b83\u7684\u53ef\u7231\u4e4b\u5904\uff0c\u5c31\u50cf\u662f\u4eba\u751f\u4e00\u6837\u3002 \u3000\u3000\u4eba\u751f\u4e2d\u867d\u7136\u6709\u8bb8\u8bb8\u591a\u591a\u4e0d\u5982\u610f\u7684\u4e8b\u3001\u8bb8\u8bb8\u591a\u591a\u4e0d\u80fd\u89e3\u91ca\u7684\u95ee\u9898\uff0c\u4f46\u662f\u4eba\u751f\u6bd5\u7adf\u8fd8\u662f\u53ef\u7231\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u535c\u9e70\u5e76\u80a9\u7ad9\u5728\u5e10\u7bf7\u524d\uff0c\u773a\u671b\u7740\u9633\u5149\u7167\u8000\u7684\u5927\u5730\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\u8981\u53bb\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5c0f\u65b9\u56de\u7b54\uff0c\u201c\u4ec0\u4e48\u5730\u65b9\u6211\u90fd\u53ef\u4ee5\u4e0d\u53bb\uff0c\u4ec0\u4e48\u5730\u65b9\u6211\u90fd\u53ef\u4ee5\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u53bb\u671d\u62dc\u8fc7\u85cf\u4eba\u7684\u5723\u5730\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u60f3\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u56de\u7b54\u4f7f\u535c\u9e70\u7684\u9510\u773c\u4e2d\u53c8\u6709\u4e86\u7b11\u610f\u3002\u201c\u6211\u60f3\u53bb\u7684\u5730\u65b9\u4e5f\u53ef\u4ee5\u4e0d\u53bb\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6211\u4e0d\u60f3\u53bb\u7684\u5730\u65b9\u4e5f\u53ef\u4ee5\u53bb\u3002\u201d \u3000\u3000\u535c\u9e70\u53c8\u95ee\uff1a\u201c\u5982\u679c\u6211\u8981\u4f60\u53bb\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u53bb\u3002\u201d \u3000\u3000\u961f\u4f0d\u53c8\u5f00\u59cb\u524d\u884c\uff0c\u80fd\u5728\u7247\u523b\u5236\u4f0f\u6218\u58eb\u7684\u4eba\uff0c\u53c8\u53d8\u6210\u4e86\u5e73\u51e1\u7684\u5546\u65c5\u3002 \u3000\u3000\u53cc\u5cf0\u9a86\u9a7c\u7684\u9a7c\u5cf0\u95f4\uff0c\u6446\u7740\u4e2a\u5c0f\u725b\u76ae\u7684\u978d\u6905\uff0c\u535c\u9e70\u5750\u5728\u6905\u4e0a\uff0c\u770b\u7740\u53e6\u4e00\u5339\u9a86\u9a7c\u4e0a\u7684\u5c0f\u65b9\uff1a\u201c\u518d\u8d70\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u5230\u90a3\u4e2a\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u6b7b\u9888\u3002\u201d \u3000\u3000\u7fa4\u5c71\u73af\u63d2\uff0c\u58c1\u7acb\u5343\u4ede\uff0c\u9752\u5929\u5982\u4e00\u7ebf\uff0c\u9053\u8def\u5982\u7f8a\u80a0\u3002 \u3000\u3000\u4e00\u7ebf\u9752\u5929\u5728\u5371\u5ca9\u602a\u77f3\u7684\u72fc\u7259\u822c\u9510\u89d2\u95f4\uff0c\u7f8a\u80a0\u66f2\u8def\u4e5f\u5d0e\u5c96\u9669\u6076\u5982\u72fc\u7259\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u5230\u4e86\u6b7b\u9888\u3002 \u3000\u3000\u961f\u4f0d\u8d70\u5f97\u66f4\u6162\uff0c\u65e0\u6cd5\u4e0d\u6162\u4e0b\u6765\uff0c\u63d2\u5929\u800c\u7acb\u7684\u5c71\u5ca9\u5371\u77f3\uff0c\u4e5f\u50cf\u662f\u7fa4\u72fc\u5728\u7b49\u7740\u62e9\u4eba\u800c\u566c\u3002\u65e0\u8bba\u8c01\u8d70\u5230\u8fd9\u91cc\uff0c\u90fd\u96be\u514d\u4f1a\u60ca\u5fc3\u52a8\u9b44\u3001\u5fc3\u8df3\u52a0\u5feb\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u8df3\u5f97\u4e5f\u4eff\u4f5b\u6bd4\u5e73\u5e38\u52a0\u5feb\u4e86\u5f88\u591a\u3002 \u3000\u3000\u535c\u9e70\u4eff\u4f5b\u5df2\u542c\u89c1\u4ed6\u7684\u5fc3\u8df3\u58f0\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u603b\u8be5\u660e\u767d\u6211\u4e3a\u4ec0\u4e48\u8981\u505a\u5f97\u90a3\u4e48\u7edd\u4e86\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u5982\u679c\u6211\u4e0d\u80fd\u7559\u4e0b\u4ed6\u4eec\u4e00\u53ea\u624b\uff0c\u5982\u679c\u4ed6\u4eec\u53c8\u56de\u5230\u8fd9\u91cc\u6765\u7b49\u7740\u6211\uff0c\u8fd9\u6761\u8def\u5c31\u662f\u6211\u4eec\u7684\u6b7b\u8def\uff0c\u8fd9\u5730\u65b9\u5c31\u662f\u6211\u4eec\u7684\u6b7b\u5730\uff01\u201d \u3000\u3000\u6b7b\u9888\uff0c\u6b7b\u5730\uff0c\u6b7b\u8def\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u624b\u5fc3\u91cc\u5192\u51fa\u51b7\u6c57\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u6ca1\u6709\u522b\u7684\u4eba\u57cb\u4f0f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u53ef\u80fd\u8fd8\u6709\u522b\u7684\u4eba\u624b\u3002\u5728\u6c99\u6f20\u8c03\u96c6\u4eba\u624b\u5e76\u4e0d\u5bb9\u6613\uff0c\u73ed\u5bdf\u5df4\u90a3\u5df2\u5c06\u4ed6\u4eec\u4eba\u9a6c\u8c03\u52a8\u7684\u60c5\u51b5\u67e5\u5f97\u5f88\u6e05\u695a\uff0c\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u7684\u638c\u5fc3\u91cc\u5ffd\u7136\u4e5f\u5192\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u53d1\u89c9\u8fd9\u4e2a\u6b7b\u9888\u3001\u8fd9\u6761\u6b7b\u8def\u3001\u8fd9\u5757\u6b7b\u5730\u4e0a\u6709\u4eba\u57cb\u4f0f\u3002 \u3000\u3000\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u6709\u65f6\u4e5f\u53ef\u80fd\u4f1a\u53d1\u751f\u7684\u3002 \u3000\u3000\u5fc3\u4e2d\u6709\u6b7b\u9888\uff0c\u4eba\u4f24\u5fc3\u3002 \u3000\u3000\u4eba\u5728\u6b7b\u9888\u4e2d\uff0c\u5c31\u4e0d\u4f1a\u4f24\u5fc3\u4e86\u3002 \u3000\u3000\u4f24\u5fc3\u7684\u4eba\u6709\u65f6\u4f1a\u60f3\u6b7b\uff0c\u53ef\u662f\u4eba\u6b7b\u4e86\u5c31\u4e0d\u4f1a\u518d\u4f24\u5fc3\uff0c\u53ea\u6709\u6b7b\u4eba\u624d\u4e0d\u4f1a\u4f24\u5fc3\u3002 \u3000\u3000\u5982\u679c\u8fd9\u91cc\u6709\u4eba\u57cb\u4f0f\uff0c\u4ed6\u4eec\u8fd9\u961f\u4f0d\u5c31\u50cf\u662f\u4e00\u4e2a\u4eba\u7684\u9888\u5b50\u5df2\u88ab\u4e00\u6761\u6253\u4e86\u6b7b\u7ed3\u7684\u7ef3\u7d22\u5957\u4f4f\u3002\u53ea\u8981\u57cb\u4f0f\u7684\u4eba\u4e00\u51fa\u51fb\uff0c\u4ed6\u4eec\u5c31\u8981\u88ab\u540a\u8d77\u3002 \u3000\u3000\u9888\u65ad\uff0c\u6c14\u7edd\uff0c\u4eba\u6b7b\uff0c\u6b7b\u9888\u3002 \u3000\u3000\u6b7b\u9888\u4e2d\u7edd\u5bf9\u6709\u4eba\u57cb\u4f0f\uff0c\u4ed6\u4eec\u65e0\u7591\u5df2\u8d70\u4e0a\u6b7b\u8def\uff0c\u8d70\u5165\u6b7b\u5730\u3002 \u3000\u3000\u535c\u9e70\u786e\u4fe1\u81ea\u5df1\u4e0d\u4f1a\u542c\u9519\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e5f\u540c\u6837\u542c\u89c1\u4e86\u4ed6\u6240\u542c\u89c1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u2014\u2014\u4eba\u7684\u547c\u5438\u58f0\u3002\u5fc3\u8df3\u58f0\u3001\u5598\u606f\u58f0\uff0c\u9a6c\u7684\u547c\u5438\u58f0\u3001\u5fc3\u8df3\u58f0\u3001\u8f7b\u5636\u58f0\u3002 \u3000\u3000\u58f0\u97f3\u8fd8\u5728\u8fdc\u5904\u3002 \u3000\u3000\u522b\u4eba\u8fd8\u542c\u4e0d\u89c1\uff0c\u53ef\u662f\u4ed6\u4eec\u542c\u5f97\u89c1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5df2\u5728\u8fd9\u4e00\u7247\u6ca1\u6709\u540c\u60c5\u3001\u6ca1\u6709\u601c\u60af\u3001\u6ca1\u6709\u6c34\u3001\u6ca1\u6709\u751f\u547d\uff0c\u5374\u968f\u65f6\u53ef\u4ee5\u593a\u53bb\u4e00\u5207\u751f\u547d\u7684\u5927\u6c99\u6f20\u4e0a\u4e3a\u4e86\u81ea\u5df1\u7684\u751f\u5b58\u594b\u6597\u4e86\u4e8c\u5341\u5e74\u3002 \u3000\u3000\u5982\u679c\u4ed6\u4eec\u4e5f\u542c\u4e0d\u89c1\u522b\u4eba\u65e0\u6cd5\u542c\u89c1\u7684\u58f0\u97f3\uff0c\u4ed6\u4eec\u6700\u5c11\u5df2\u6b7b\u4e86\u4e8c\u5341\u6b21\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u6b7b\u4e8c\u5341\u6b21\uff0c\u7edd\u5bf9\u6ca1\u6709\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u8fde\u4e00\u6b21\u90fd\u4e0d\u80fd\u6b7b\u3002 \u3000\u3000\u5982\u679c\u6709\u4eba\u8bf4\uff0c\u771f\u6b63\u7684\u7231\u60c5\u53ea\u6709\u4e00\u6b21\uff0c\u6ca1\u6709\u7b2c\u4e8c\u6b21\uff0c\u90a3\u4e48\u4ed6\u8bf4\u7684\u5c31\u7b97\u662f\u53e5\u540d\u8a00\uff0c\u4e5f\u4e0d\u662f\u771f\u7406\u3002 \u3000\u3000\u56e0\u4e3a\u7231\u60c5\u662f\u4f1a\u53d8\u8d28\u7684\uff0c\u53d8\u4e3a\u53cb\u60c5\uff0c\u53d8\u4e3a\u4eb2\u60c5\uff0c\u53d8\u4e3a\u4f9d\u8d56\uff0c\u751a\u81f3\u4f1a\u53d8\u4e3a\u4ec7\u6068\u3002 \u3000\u3000\u4f1a\u53d8\u7684\uff0c\u5c31\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u7b49\u5230\u4e00\u6b21\u7231\u60c5\u53d8\u8d28\u6de1\u5fd8\u540e\uff0c\u5f80\u5f80\u5c31\u4f1a\u6709\u7b2c\u4e8c\u6b21\uff0c\u7b2c\u4e8c\u6b21\u5f80\u5f80\u4e5f\u4f1a\u53d8\u5f97\u548c\u7b2c\u4e00\u6b21\u540c\u6837\u771f\uff0c\u540c\u6837\u6df1\uff0c\u540c\u6837\u751c\u871c\uff0c\u540c\u6837\u75db\u82e6\u3002 \u3000\u3000\u53ef\u662f\u6b7b\u53ea\u6709\u4e00\u6b21\uff0c\u7edd\u4e0d\u4f1a\u6709\u7b2c\u4e8c\u6b21\u3002 \u3000\u3000\u4eba\u751f\u4e2d\u6240\u6709\u7684\u4e8b\uff0c\u53ea\u6709\u6b7b\uff0c\u624d\u662f\u771f\u6b63\u7edd\u5bf9\u4e0d\u4f1a\u6709\u7b2c\u4e8c\u6b21\u7684\u3002 \u3000\u3000\u4eba\u3001\u9a6c\u3001\u9a86\u9a7c\uff0c\u672c\u6765\u90fd\u662f\u6210\u5355\u7ebf\u884c\u8d70\u7684\uff0c\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\uff0c\u5a49\u8712\u5982\u957f\u86c7\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5728\u8fd9\u4e2a\u961f\u4f0d\u4e2d\u884c\u8d70\u7684\u4f4d\u7f6e\uff0c\u5c31\u6b63\u5982\u5728\u4e00\u6761\u86c7\u7684\u4e03\u5bf8\u4e0a\u3002 \u3000\u3000\u535c\u9e70\u4e0e\u5c0f\u65b9\u6bbf\u540e\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u4e00\u7ae0\u3000\u84dd\u8272\u7684\u9633\u5149 \u7b2c\u5341\u4e00\u7ae0\u3000\u84dd\u8272\u7684\u9633\u5149 \u3000\u3000\u4ed6\u4eec\u5df2\u7ecf\u770b\u89c1\u73ed\u5bdf\u5df4\u90a3\u6253\u9a6c\u9a70\u6765\uff0c\u9a6c\u6025\u8e44\u8f7b\uff0c\u4ed6\u82f1\u4fca\u9547\u9759\u7684\u8138\u4e0a\uff0c\u5df2\u7ecf\u9732\u51fa\u65e0\u6cd5\u63a9\u9970\u7684\u60ca\u60f6\u4e4b\u8272\u3002 \u3000\u3000\u201c\u6709\u4eba\u3002\u201d\u4ed6\u538b\u4f4e\u4e86\u58f0\u97f3\uff0c\u201c\u524d\u9762\u7684\u51fa\u53e3\u3001\u4e24\u8fb9\u5c71\u5ca9\u4e0a\u90fd\u6709\u4eba\u3002\u201d \u3000\u3000\u90a3\u91cc\u662f\u6b7b\u7ed3\u4e0a\u7684\u5589\u7ed3\uff0c\u4e00\u51fb\u5c31\u53ef\u4ee5\u81f4\u547d\u3002 \u3000\u3000\u4e0b\u51b3\u5b9a\u7684\u4eba\u8fd8\u662f\u535c\u9e70\uff0c\u6240\u4ee5\u73ed\u5bdf\u5df4\u90a3\u53c8\u95ee\uff1a\u201c\u6211\u4eec\u662f\u9000\u8d70\uff0c\u8fd8\u662f\u51b2\u4e0a\u53bb\uff1f\u201d \u3000\u3000\u535c\u9e70\u989d\u89d2\u4e0a\u5ffd\u7136\u8ff8\u8d77\u4e00\u6839\u9752\u7b4b\uff0c\u9752\u7b4b\u5728\u4e0d\u505c\u5730\u8df3\u52a8\u3002 \u3000\u3000\u6bcf\u5230\u771f\u6b63\u7d27\u5f20\u65f6\uff0c\u4ed6\u8fd9\u6839\u7b4b\u624d\u4f1a\u8df3\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u4e0b\u51b3\u5b9a\uff0c\u524d\u9762\u7684\u5c71\u5ca9\u4e0a\u4e00\u5757\u5371\u77f3\u540e\uff0c\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\uff0c\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\uff0c\u6bd4\u84dd\u5927\u66f4\u84dd\uff0c\u6bd4\u6d77\u6c34\u66f4\u84dd\u3002 \u3000\u3000\u5979\u71d5\u5b50\u822c\u8dc3\u8d77\uff0c\u7ad9\u5728\u5371\u77f3\u4e0a\uff0c\u7ad9\u5728\u9633\u5149\u4e0b\uff0c\u5411\u4ed6\u4eec\u6325\u624b\uff1a\u201c\u535c\u9e70\uff0c\u6211\u60f3\u4f60\uff0c\u73ed\u5bdf\u5df4\u90a3\uff0c\u6211\u60f3\u4f60\uff0c\u5b8b\u8001\u5934\uff0c\u6211\u4e5f\u60f3\u4f60\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u660e\u6717\u6109\u5feb\uff0c\u5979\u9ad8\u547c\uff1a\u201c\u6211\u597d\u60f3\u4f60\u4eec\u3002\u201d \u3000\u3000\u770b\u89c1\u5979\uff0c\u535c\u9e70\u7684\u773c\u91cc\uff0c\u4eff\u4f5b\u4e5f\u6709\u4e86\u9633\u5149\u3002 \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u89c1\u5230\u4ed6\u773c\u775b\u8fd9\u4e48\u4eae\uff0c\u4e5f\u4ece\u672a\u89c1\u5230\u4ed6\u8fd9\u4e48\u6109\u5feb\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u672c\u8eab\u5c31\u50cf\u662f\u9633\u5149\uff0c\u603b\u662f\u80fd\u5e26\u7ed9\u4eba\u6e29\u6696\u5e78\u798f\u6109\u5feb\u3002 \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u535c\u9e70\u5fae\u7b11\uff0c\u73ed\u5bdf\u5df4\u90a3\u4e5f\u5728\u7b11\uff0c\u521a\u624d\u7684\u60ca\u8651\u90fd\u5df2\u53d8\u4e3a\u6b22\u60a6\u3002 \u3000\u3000\u201c\u5979\u59d3\u84dd\u3002\u201d\u535c\u9e70\u8bf4\uff0c\u201c\u5979\u7684\u540d\u5b57\u5c31\u53eb\u505a\u9633\u5149\u3002\u201d\u8fc7\u4e86\u6b7b\u9888\uff0c\u5c31\u662f\u4e00\u7247\u6c83\u91ce\u5e73\u539f\uff0c\u8ddd\u79bb\u5723\u5730\u62c9\u8428\u5df2\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u961f\u4f0d\u5df2\u505c\u4e0b\u6765\uff0c\u624e\u8d77\u4e86\u8425\u5e10\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u663e\u5f97\u5f88\u6109\u5feb\uff0c\u662f\u9633\u5149\u4e3a\u4ed6\u4eec\u5e26\u6765\u7684\u6109\u5feb\uff0c\u4ed6\u4eec\u90fd\u7528\u85cf\u8bed\u5728\u4e3a\u5979\u6b22\u547c\uff0c\u4ed6\u4eec\u90fd\u79f0\u5979\u4e3a\u201c\u84dd\u8272\u7684\u9633\u5149\u201d\u3002 \u3000\u3000\u5979\u662f\u6765\u63a5\u5e94\u4ed6\u4eec\u7684\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u53c8\u60f3\u5413\u552c\u4f60\u4eec\u3002\u201d\u5979\u7684\u58f0\u97f3\u4e5f\u5982\u9633\u5149\u822c\u660e\u6717\uff0c\u201c\u53ef\u662f\u6211\u53c8\u4e0d\u60f3\u628a\u4f60\u4eec\u5413\u6b7b\u3002\u201d \u3000\u3000\u5979\u62b1\u4f4f\u4e86\u535c\u9e70\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\u5929\u4e0b\u518d\u4e5f\u627e\u4e0d\u5230\u7b2c\u4e8c\u4e2a\uff0c\u4e07\u4e00\u628a\u4f60\u5413\u6b7b\u4e86\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u4e5f\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u660e\u6717\uff0c\u5982\u6b64\u4ee4\u4eba\u6109\u5feb\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u80fd\u7b97\u662f\u4e2a\u5b8c\u7f8e\u65e0\u7f3a\u7684\u7edd\u8272\u7f8e\u4eba\uff0c\u5979\u7684\u9f3b\u5b50\u6709\u4e00\u70b9\u5f2f\u66f2\uff0c\u8ddf\u535c\u9e70\u7684\u9f3b\u5b50\u6709\u4e00\u70b9\u76f8\u50cf\u3002 \u3000\u3000\u4f46\u662f\u5979\u7684\u773c\u6ce2\u660e\u5a9a\uff0c\u96ea\u767d\u7684\u76ae\u80a4\u5149\u6ed1\u67d4\u8f6f\u5982\u4e1d\u7f0e\u3002 \u3000\u3000\u5979\u7b11\u8d77\u6765\u7684\u65f6\u5019\uff0c\u5fae\u5fae\u5f2f\u66f2\u7684\u9f3b\u5b50\u5fae\u5fae\u76b1\u8d77\uff0c\u8fd9\u4e00\u70b9\u5c0f\u5c0f\u7684\u7f3a\u9677\uff0c\u53cd\u800c\u53d8\u6210\u4e86\u5979\u7279\u6b8a\u7684\u7f8e\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53d1\u73b0\u535c\u9e70\u5f88\u559c\u6b22\u634f\u5979\u7684\u9f3b\u5b50\uff0c\u73b0\u5728\u4ed6\u5c31\u6b63\u5728\u634f\u5979\u7684\u9f3b\u5b50\uff1a\u201c\u4f60\u7b54\u5e94\u8fc7\u6211\uff0c\u8fd9\u4e00\u6b21\u7edd\u4e0d\u51fa\u6765\u4e71\u8dd1\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8dd1\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u9633\u5149\u8f7b\u5de7\u5730\u907f\u5f00\u4e86\u8fd9\u95ee\u9898\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u603b\u662f\u559c\u6b22\u634f\u6211\u7684\u9f3b\u5b50\uff1f\u201d\u5979\u53c8\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u60f3\u628a\u6211\u7684\u9f3b\u5b50\u634f\u6210\u50cf\u4f60\u4e00\u6837\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u3002 \u3000\u3000\u9633\u5149\u56de\u8fc7\u5934\uff0c\u7728\u4e86\u4ed6\u4e00\u773c\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53eb\u5c0f\u65b9\u3002\u201d\u535c\u9e70\u8bf4\uff1a\u201c\u8981\u547d\u7684\u5c0f\u65b9\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u53eb\u4ed6\u8981\u547d\u7684\u5c0f\u65b9\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6709\u65f6\u5019\u4ed6\u4e5f\u8ddf\u4f60\u4e00\u6837\u8981\u547d\uff0c\u6709\u65f6\u5019\u8981\u628a\u4eba\u6c14\u6b7b\uff0c\u6709\u65f6\u5019\u60f3\u628a\u4eba\u5413\u6b7b\u3002\u201d \u3000\u3000\u535c\u9e70\u773c\u4e2d\u5145\u6ee1\u7b11\u610f\uff1a\u201c\u4ed6\u81ea\u5df1\u5374\u53c8\u504f\u504f\u662f\u4e2a\u4e0d\u8981\u547d\u7684\u4eba\u3002\u201d \u3000\u3000\u9633\u5149\u53c8\u76ef\u7740\u5c0f\u65b9\u770b\u4e86\u534a\u5929\uff1a\u201c\u6211\u6700\u559c\u6b22\u4e0d\u8981\u547d\u7684\u7537\u4eba\u3002\u201d\uff0e\u5979\u53c8\u5f00\u59cb\u7b11\u4e86\uff0c\u201c\u73b0\u5728\uff0c\u6211\u5df2\u7ecf\u5f00\u59cb\u6709\u70b9\u559c\u6b22\u4f60\u4e86\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u4e5f\u50cf\u521a\u624d\u62b1\u4f4f\u535c\u9e70\u90a3\u6837\u62b1\u4f4f\u4e86\u5c0f\u65b9\uff0c\u5728\u5c0f\u65b9\u7684\u989d\u4e0a\u4eb2\u4e86\u4eb2\uff1a\u201c\u6211\u5927\u54e5\u7684\u670b\u53cb\u5c31\u662f\u6211\u7684\u670b\u53cb\u3002\u201d\u5979\u8bf4\uff0c\u201c\u4ed6\u559c\u6b22\u7684\u4eba\u6211\u90fd\u559c\u6b22\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u8138\u5c45\u7136\u6ca1\u6709\u7ea2\uff0c\u56e0\u4e3a\u5979\u7684\u8138\u4e5f\u6ca1\u6709\u7ea2\u3002 \u3000\u3000\u5979\u62b1\u4f4f\u4ed6\u65f6\uff0c\u5c31\u50cf\u662f\u9633\u5149\u666e\u7167\u5728\u5927\u5730\u4e00\u6837\uff0c\u660e\u6717\u800c\u81ea\u7136\u3002 \u3000\u3000\u5c0f\u65b9\u7edd\u4e0d\u662f\u4e2a\u626d\u626d\u634f\u634f\u7684\u7537\u4eba\uff0c\u5f88\u5c11\u80fd\u628a\u5fc3\u91cc\u60f3\u8bf4\u7684\u8bdd\u5fcd\u4f4f\u4e0d\u8bf4\u3002\u201c\u6211\u4e5f\u559c\u6b22\u4f60\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u771f\u7684\u5f88\u559c\u6b22\u3002\u201d \u3000\u3000\u5929\u8272\u5df2\u6697\u4e86\u3002 \u3000\u3000\u8425\u5730\u4e2d\u53c8\u54cd\u8d77\u4e86\u6b22\u996e\u9ad8\u6b4c\uff0c\u6b4c\u58f0\u6bd4\u5f80\u6614\u66f4\u6b22\u6109\u5639\u4eae\u3002 \u3000\u3000\u56e0\u4e3a\u5176\u4e2d\u53c8\u589e\u52a0\u4e86\u5341\u591a\u4e2a\u5c11\u5973\u6e05\u4eae\u7684\u6b4c\u58f0\u3002 \u3000\u3000\u5979\u4eec\u90fd\u662f\u9633\u5149\u5e26\u6765\u7684\uff0c\u90fd\u662f\u50cf\u9633\u5149\u4e00\u6837\u660e\u6717\u6d3b\u6cfc\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u5979\u4eec\u4e5f\u50cf\u5979\u4eec\u7684\u5144\u5f1f\u3001\u60c5\u4eba\u4e00\u6837\uff0c\u9a91\u7740\u9a6c\uff0c\u559d\u70c8\u9152\uff0c\u7528\u5feb\u5200\u3002 \u3000\u3000\u559d\u9189\u4e86\uff0c\u559d\u7d2f\u4e86\uff0c\u5979\u4eec\u5c31\u8ddf\u4ed6\u4eec\u7684\u60c5\u4eba\u5144\u5f1f\u8eba\u5728\u4e00\u8d77\uff0c\u6570\u5929\u4e0a\u7684\u661f\u661f\u3002 \u3000\u3000\u5bf9\u4e00\u4e2a\u5fc3\u4e2d\u672c\u65e0\u90aa\u5ff5\u7684\u4eba\u6765\u8bf4\uff0c\u4e16\u4e0a\u6709\u4ec0\u4e48\u90aa\u6076\u7684\u4e8b\uff1f \u3000\u3000\u5e73\u5e38\u5f88\u5c11\u559d\u9152\u7684\u73ed\u5bdf\u5df4\u90a3\uff0c\u4eca\u5929\u4e5f\u559d\u5f97\u4e0d\u5c11\u3002 \u3000\u3000\u4ed6\u914d\u5408\u7740\u535c\u9e70\uff0c\u62cd\u624b\u4f4e\u5531\uff1a\u2014\u2014\u513f\u987b\u6709\u540d\uff0c\u9152\u987b\u9189\u3002 \u3000\u3000\u9189\u540e\u7545\u8c08\uff0c\u662f\u5fc3\u8a00\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u6b4c\u58f0\u4e2d\uff0c\u7adf\u4f3c\u5e26\u7740\u79cd\u6de1\u6de1\u7684\u60b2\u4f24\u3001\u6de1\u6de1\u7684\u79bb\u6101\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u63a8\u676f\u800c\u8d77\uff0c\u201c\u4f60\u5df2\u7ecf\u5feb\u5230\u5bb6\u4e86\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4e5f\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u535c\u9e70\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u4ed6\u7684\u795e\u8272\u9eef\u7136\uff0c\u201c\u6211\u56de\u53bb\uff0c\u4f60\u8d70\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4ec0\u4e48\u90fd\u6ca1\u6709\u518d\u8bf4\uff0c\u53ea\u7528\u529b\u63e1\u4e86\u63e1\u4ed6\u7684\u624b\uff0c\u5c31\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\u3002 \u3000\u3000\u5e10\u5916\u5df2\u5907\u597d\u4e24\u5339\u9a6c\uff0c\u4e00\u5339\u9a6c\u662f\u4ed6\u7684\u767d\u9a6c\uff0c\u53e6\u4e00\u5339\u9a6c\u4e0a\u5df2\u88c5\u914d\u597d\u4ed6\u4eec\u9700\u8981\u7684\u4e00\u5207\u884c\u88c5\u3002 \u3000\u3000\u4ed6\u4e00\u8dc3\u4e0a\u9a6c\uff0c\u4fbf\u6253\u9a6c\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u6ca1\u6709\u518d\u56de\u5934\u3002 \u3000\u3000\u5929\u8fd8\u6ca1\u6709\u4eae\uff0c\u53ea\u9732\u51fa\u4e86\u4e00\u70b9\u66d9\u5149\u3002 \u3000\u3000\u5927\u5730\u4f9d\u7136\u5bd2\u51b7\u5bc2\u5bde\u3002 \u3000\u3000\u4ed6\u8fce\u98ce\u8d70\u5411\u8fdc\u65b9\u90a3\u65e0\u8fb9\u65e0\u9645\u7684\u65e0\u60c5\u5927\u5730\uff0c\u90a3\u91cc\u4ecd\u7136\u6709\u65e0\u9650\u65e0\u6b62\u7684\u5bd2\u51b7\u5bc2\u5bde\u82e6\u96be\u5728\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u80f8\u4e2d\u4e5f\u6d8c\u8d77\u4e86\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u8427\u7d22\u51c4\u51c9\uff0c\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u4f60\u56de\u53bb\uff1f\u4e3a\u4ec0\u4e48\u8981\u4e00\u4e2a\u4eba\u8d70\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u535c\u9e70\u624d\u56de\u7b54\uff1a\u201c\u56e0\u4e3a\u4ed6\u5929\u751f\u5c31\u662f\u4e2a\u5b64\u72ec\u7684\u4eba\uff0c\u5929\u751f\u5c31\u559c\u6b22\u5b64\u72ec\u3002\u201d\u535c\u9e70\u6162\u6162\u4ed6\u8bf4\uff1a\u201c\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u5927\u90e8\u5206\u5c81\u6708\u90fd\u662f\u5728\u5b64\u72ec\u4e2d\u5ea6\u8fc7\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4ed6\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d\u535c\u9e70\u56de\u7b54\uff0c\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5929\u7ec8\u4e8e\u4eae\u4e86\uff0c\u65ed\u65e5\u7ec8\u4e8e\u5347\u8d77\uff0c\u7b2c\u4e00\u7ebf\u9633\u5149\u6b63\u7167\u5728\u84dd\u8272\u7684\u9633\u5149\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u6211\u4e0d\u559c\u6b22\u5b64\u72ec\u3002\u201d\u5979\u62c9\u7d27\u535c\u9e70\u7684\u624b\uff0c\u201c\u6211\u4eec\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u60f3\u5230\u535c\u9e70\u4e5f\u6709\u5bb6\u3002 \u3000\u3000\u535c\u9e70\u6709\u5bb6\u3002 \u3000\u3000\u535c\u9e70\u7684\u5bb6\u5c31\u5728\u85cf\u4eba\u5fc3\u76ee\u4e2d\u7684\u5723\u5730\u201c\u62c9\u8428\u201d\uff0c\u4ed6\u7684\u5bb6\u4e5f\u662f\u4ed6\u7684\u4f19\u4f34\u4ea7\u5f1f\u5fc3\u76ee\u4e2d\u7684\u5723\u5730\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u6709\u5bb6\uff0c\u800c\u4e14\u8fdc\u6bd4\u5927\u591a\u6570\u7684\u5bb6\u90fd\u5bbd\u5927\u5e7d\u7f8e\u534e\u4e3d\u3002 \u3000\u3000\u8fc7\u4e86\u8fbe\u8d56\u6d3b\u4f5b\u7684\u5e03\u9762\u8fbe\u62c9\u5bab\uff0c\u6709\u4e00\u5ea7\u9752\u8272\u7684\u5c71\u5c97\u3001\u4e00\u7247\u7eff\u8272\u7684\u6e56\u6cca\u3002 \u3000\u3000\u4ed6\u7684\u5bb6\u5c31\u5728\u5c71\u811a\u4e0b\uff0c\u9752\u5c71\u5728\u62b1\uff0c\u7eff\u6c34\u5728\u6000\uff0c\u8fdc\u5904\u7684\u5bab\u6bbf\u548c\u57ce\u581e\u9690\u7ea6\u5728\u671b\uff0c\u6674\u7a7a\u5982\u6d17\uff0c\u4e07\u91cc\u65e0\u4e91\uff0c\u767d\u8272\u7684\u5e03\u8fbe\u62c9\u5bab\u5728\u9a84\u9633\u4e0b\u770b\u6765\u4eae\u5982\u7eaf\u94f6\uff0c\u5230\u4e86\u5915\u9633\u897f\u4e0b\u65f6\uff0c\u53c8\u53d8\u5f97\u707f\u70c2\u5982\u9ec4\u91d1\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u4ece\u672a\u60f3\u5230\uff0c\u5728\u585e\u5916\u7684\u8fb9\u9672\u4e4b\u5730\uff0c\u7adf\u6709\u5982\u6b64\u7f8e\u5999\u7684\u5730\u65b9\uff0c\u7f8e\u5f97\u8f89\u714c\u800c\u795e\u79d8\uff0c\u7f8e\u5f97\u4ee4\u5165\u8ff7\u60d1\uff0c\u7f8e\u5f97\u4ee4\u4eba\u90fd\u9189\u4e86\u3002 \u3000\u3000\u8d27\u7269\u9700\u8981\u6e05\u70b9\uff0c\u76c8\u5229\u5fc5\u987b\u7b97\u6e05\uff0c\u5c3d\u5feb\u5206\u7ed9\u6bcf\u4e00\u4e2a\u5e94\u5f97\u7684\u4eba\uff0c\u8ba9\u4ed6\u4eec\u53bb\u4eab\u53d7\u5e94\u5f97\u7684\u6b22\u4e50\u3002 \u3000\u3000\u4f3c\u4e4e\u535c\u9e70\u5c06\u5c0f\u65b9\u4ea4\u7ed9\u4e86\u9633\u5149\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u5e74\u8f7b\uff0c\u4ed6\u4eec\u5f7c\u6b64\u76f8\u60a6\uff0c\u535c\u9e70\u5e0c\u671b\u9633\u5149\u80fd\u591f\u7167\u4eae\u5c0f\u65b9\u5fc3\u91cc\u7684\u9634\u5f71\u3002 \u3000\u3000\u6ce2\u5a03\u7684\u9634\u5f71\u3002 \u3000\u3000\u65e5\u51fa\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u6f2b\u6b65\u5728\u5c71\u5c97\u4e0a\uff0c\u535c\u9e70\u7684\u5b85\u7b2c\u56ed\u6797\u6e56\u6cca\u5728\u4ed6\u4eec\u7684\u811a\u4e0b\uff0c\u8fdc\u5904\u7684\u5bab\u6bbf\u4eff\u4f5b\u8fd1\u5728\u773c\u524d\u3002 \u3000\u3000\u9633\u5149\u95ee\u5c0f\u65b9\u8bf4\uff1a\u201c\u4f60\u559c\u4e0d\u559c\u6b22\u8fd9\u4e2a\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u70b9\u5934\uff0c\u4ed6\u53ea\u80fd\u70b9\u5934\u201d\u6ca1\u6709\u4eba\u80fd\u591f\u4e0d\u559c\u6b22\u8fd9\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u9633\u5149\u53c8\u95ee\u9053\uff1a\u201c\u6062\u4ee5\u524d\u6765\u8fc7\u8fd9\u4e2a\u5730\u65b9\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6447\u5934\u3002 \u3000\u3000\u4ed6\u4ee5\u524d\u6ca1\u6709\u6765\u8fc7\uff0c\u5982\u679c\u4ed6\u6765\u8fc7\uff0c\u5f88\u53ef\u80fd\u5c31\u4e0d\u4f1a\u8d70\u4e86\u3002 \u3000\u3000\u9633\u5149\u62c9\u8d77\u5c0f\u65b9\u7684\u624b\uff0c\u5c31\u597d\u8c61\u5979\u62c9\u7740\u535c\u9e70\u7684\u624b\u65f6\u4e00\u6837\u3002 \u3000\u3000\u201c\u6211\u5e26\u4f60\u51fa\u53bb\u73a9\u3002\u201d\u5979\u8bf4\uff0c\u201c\u4ed6\u4eec\u5728\u505a\u751f\u610f\uff0c\u6211\u4eec\u53bb\u73a9\u3002\u201d \u3000\u3000\u201c\u5230\u54ea\u91cc\u53bb\u73a9\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5148\u5230\u5e03\u8fbe\u62c9\u5bab\u53bb\u3002\u201d \u3000\u3000\u77f3\u780c\u7684\u57ce\u57a3\u6a2a\u4e98\u5728\u5e03\u8fbe\u62c9\u5bab\u548c\u6070\u514b\u535c\u91cc\u5c71\u4e4b\u95f4\uff0c\u57ce\u95e8\u5728\u4e00\u5ea7\u820d\u5229\u5854\u4e0b\uff0c\u5854\u91cc\u85cf\u7740\u53e4\u4ee3\u9ad8\u50e7\u7684\u4f5b\u9aa8\u548c\u65e0\u6570\u795e\u79d8\u7f8e\u4e3d\u7684\u4f20\u8bf4\u4e0e\u795e\u8bdd\u3002 \u3000\u3000\u901a\u8fc7\u5706\u5f62\u7684\u62f1\u95e8\uff0c\u6c14\u52bf\u8feb\u4eba\u7684\u5bab\u6bbf\u8d6b\u7136\u51fa\u73b0\u5728\u4ed6\u4eec\u7684\u53f3\u65b9\u3002 \u3000\u3000\u5bab\u6bbf\u9ad8\u56db\u5341\u4e08\uff0c\u5bbd\u4e00\u767e\u4e8c\u5341\u4e08\uff0c\u8fde\u7ef5\u5a49\u8712\u7684\u96c9\u579b\uff0c\u9ad8\u8038\u5728\u5c71\u5ca9\u4e0a\u7684\u57ce\u5821\uff0c\u53e4\u8001\u7684\u5bfa\u9662\uff0c\u7985\u623f\uff0c\u7891\u78a3\u3001\u697c\u9601\uff0c\u7b97\u4e0d\u6e05\u7684\u7a97\u7256\u5e37\u5e18\uff0c\u770b\u6765\u7470\u4e3d\u800c\u8c03\u548c\uff0c\u5c31\u50cf\u662f\u68a6\u5883\uff0c\u5c31\u50cf\u662f\u795e\u8bdd\u3002 \u3000\u3000\u5c0f\u65b9\u4eff\u4f5b\u5df2\u770b\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u2014\u2014\u6ce2\u5a03\u5462\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u4ed6\u8eab\u8fb9\u7684\u4eba\u662f\u6ce2\u5a03\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u4e00\u4e2a\u4eba\u5728\u88ab\u201c\u7f8e\u201d\u6240\u611f\u52a8\u65f6\uff0c\u53cd\u800c\u66f4\u4e0d\u80fd\u5fd8\u8bb0\u4ed6\u4e00\u5fc3\u60f3\u5fd8\u8bb0\u7684\u4eba\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u4eba\u4eec\u603b\u662f\u5f88\u96be\u5fd8\u8bb0\u4e00\u4e9b\u81ea\u5df1\u5e94\u8be5\u5fd8\u8bb0\u7684\u4e8b\uff1f \u3000\u3000\u592a\u9633\u7167\u5728\u4ed6\u8eab\u4e0a\uff0c\u9633\u5149\u5728\u770b\u7740\u4ed6\uff0c\u9633\u5149\u7f8e\u4e3d\u800c\u660e\u6717\u3002 \u3000\u3000\u2014\u2014\u4e00\u6ce2\u5a03\u5462\uff1f \u3000\u3000\u2014\u2014\u6ce2\u5a03\u5e76\u4e0d\u50cf\u96ea\uff0c\u6ce2\u5a03\u5c31\u50cf\u662f\u96e8\uff0c\u7ef5\u7ef5\u7684\u6625\u96e8\uff0c\u526a\u4e0d\u65ad\u7684\u79bb\u6101\uff0c\u526a\u4e0d\u65ad\u7684\u96e8\u4e1d\uff0c\u5c0f\u65b9\u5ffd\u7136\u8bf4\uff1a\u201c\u6211\u4eec\u5230\u5927\u62db\u5bfa\u53bb\u3002\u3001 \u3000\u3000\u4ed6\u77e5\u9053\u5927\u62db\u5bfa\u5916\uff0c\u56f4\u7ed5\u7740\u5bfa\u9662\u7684\u516b\u89d2\u8857\uff0c\u662f\u57ce\u91cc\u6700\u7e41\u534e\u70ed\u95f9\u7684\u5730\u65b9\uff0c\u6240\u6709\u6700\u5927\u7684\u5546\u6709\u884c\u53f7\uff0c\u90fd\u5728\u90a3\u6761\u8857\u4e0a\u3002 \u3000\u3000\u535c\u9e70\u7684\u201c\u9e70\u8bb0\u201d\u5546\u53f7\u4e5f\u5728\u90a3\u6761\u8857\u4e0a\u3002 \u3000\u3000\u5c0f\u65b9\u5e0c\u671b\u201c\u70ed\u95f9\u201d\u80fd\u591f\u8ba9\u4ed6\u201c\u5fd8\u8bb0\u201d\uff0c\u54ea\u6015\u53ea\u4e0d\u8fc7\u662f\u6682\u65f6\u5fd8\u8bb0\u4e5f\u597d\u3002 \u3000\u3000\u5927\u62db\u5bfa\u662f\u5510\u4ee3\u7684\u6587\u6210\u516c\u4e3b\u6240\u5efa\u3002 \u3000\u3000\u5728\u90a3\u4e2a\u65f6\u5019\uff0c\u897f\u85cf\u8fd8\u662f\u201c\u5410\u8543\u201d\uff0c\u62c9\u8428\u8fd8\u662f\u201c\u66b9\u5a11\u57ce\u201d\u3002\u5927\u5510\u8d1e\u89c2\u5341\u56db\u5e74\uff0c\u5410\u8543\u7684\u5bb0\u76f8\u201c\u4e1c\u8d5e\u201d\u5e26\u7740\u73cd\u5b9d\u65e0\u6570\u3001\u9ec4\u91d1\u4e94\u5343\u4e24\u5230\u4e86\u957f\u5b89\uff0c\u628a\u5929\u53ef\u6c57\u7684\u4f84\u5973\uff0c\u9762\u8c8c\u6167\u79c0\u3001\u5999\u76f8\u5177\u8db3\u3001\u7aef\u5e84\u7f8e\u4e3d\u3001\u4f53\u51c0\u65e0\u6687\u3001\u53e3\u5410\u2018\u54c8\u91cc\u79f0\u6a80\u9999\u7c92\u2019\uff0c\u800c\u4e14\u8654\u8bda\u4e8b\u4f5b\u7684\u6587\u6210\u516c\u4e3b\u5e26\u56de\u4e86\u66b9\u5a11\u57ce\uff0c\u5ac1\u7ed9\u4e86\u4ed6\u4eec\u7684\u7b2c\u4e03\u4e16\u201c\u8d5e\u666e\u201d\uff0c\u96c4\u59ff\u82f1\u53d1\u3001\u60ca\u624d\u7edd\u8273\u7684\u201c\u5f03\u5b97\u5f04\u8d5e\u201d\u3002 \u3000\u3000\u4e3a\u4e86\u5979\u7684\u8654\u8bda\uff0c\u4e3a\u4e86\u5979\u7684\u7f8e\u4e3d\uff0c\u4ed6\u4e3a\u5979\u5efa\u9020\u4e86\u8fd9\u5ea7\u96c4\u58ee\u5b8f\u4e3d\u7684\u5bfa\u9662\u3002 \u3000\u3000\u4f46\u662f\u5bfa\u9662\u5916\u7684\u8857\u5e02\uff0c\u5374\u662f\u8fd9\u57ce\u5e02\u7684\u53e6\u4e00\u9762\u3002 \u3000\u3000\u57ce\u5e02\u8d64\u5982\u76ae\u9769\uff0c\u6709\u5149\u6ed1\u7f8e\u4e3d\u7684\u4e00\u9762\uff0c\u4e5f\u6709\u7c97\u7cd9\u4e11\u964b\u7684\u4e00\u9762\u3002 \u3000\u3000\u6709\u4e9b\u8857\u5934\u4e0a\u5783\u573e\u7caa\u4fbf\u72fc\u85c9\uff0c\u6210\u7fa4\u7ed3\u961f\u7684\u5e74\u8001\u4e5e\u4e10\uff0c\u7a7f\u7740\u7834\uff1b\u65e5\u8934\u891b\u7684\u8863\u670d\uff0c\u5243\u5149\u5934\u6253\u8d64\u8db3\uff0c\u530d\u5310\u5728\u5c18\u571f\u4e2d\uff0c\u5634\u91cc\u5583\u5583\u4e0d\u505c\u5730\u5ff5\u7740\u4ed6\u4eec\u7684\u516d\u5b57\u771f\u8a00\u201c\u5535\u5427\u5462\u53ed\u7c73\u543d\u201d\uff0c\u7b49\u5f85\u7740\u884c\u4eba\u9999\u5ba2\u7684\u65bd\u820d\u3002 \u3000\u3000\u5728\u6c99\u6f20\u4e2d\uff0c\u5728\u90a3\u573a\u5927\u98ce\u66b4\u91cc\uff0c\u5c0f\u65b9\u5931\u53bb\u4ed6\u7684\u98df\u6c34\u7cae\u98df\uff0c\u5374\u6ca1\u6709\u5931\u53bb\u4ed6\u7684\u94f6\u94b1\u3002 \u3000\u3000\u4ed6\u5c06\u8eab\u4e0a\u6240\u6709\u7684\u5168\u90fd\u65bd\u820d\u7ed9\u4ed6\u4eec\uff0c\u4e0d\u4ec5\u662f\u56e0\u4e3a\u540c\u60c5\u548c\u601c\u60af\uff0c\u8fd8\u50cf\u662f\u88ab\u4e00\u79cd\u5947\u5f02\u7684\u529b\u91cf\u6240\u5506\u4f7f\u7684\u611f\u53ec\u3002 \u3000\u3000\u201c\u6211\u4e0d\u5e94\u5230\u5927\u62db\u5bfa\u53bb\u4e86\u3002\u201d\u5c0f\u65b9\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u5fc3\u91cc\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u79cd\u5947\u5f02\u7684\u53d8\u5316\uff0c\u201c\u6211\u4eec\u80fd\u4e0d\u80fd\u5230\u4f60\u4eec\u7684\u5546\u53f7\u53bb\u770b\u770b\uff1f\u201d \u3000\u3000\u201c\u4f60\u80fd\u53bb\u3002\u201d\u9633\u5149\u8bf4\uff0c\u201c\u4f60\u662f\u5927\u54e5\u7684\u670b\u53cb\uff0c\u4f60\u60f3\u5230\u54ea\u91cc\u53bb\uff0c\u6211\u90fd\u5e26\u4f60\u53bb\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u53c8\u9732\u51fa\u9633\u5149\u822c\u7f8e\u4e3d\u660e\u6717\u7684\u7b11\uff1a\u201c\u5230\u4e86\u90a3\u91cc\uff0c\u6211\u8fd8\u8981\u5e26\u4f60\u53bb\u89c1\u4e00\u4e2a\u4eba\uff0c\u4f60\u4e00\u5b9a\u4e5f\u4f1a\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\u7684\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u8fd9\u4e2a\u4eba\u53eb\u6731\u4e91\u3002 \u3000\u3000\u6731\u4e91\u5c31\u662f\u201c\u9e70\u8bb0\u201d\u7684\u5927\u638c\u67dc\u3002\u5927\u638c\u67dc\u7684\u610f\u601d\uff0c\u5c31\u662f\u603b\u7ba1\u3002 \u3000\u3000\u6731\u4e91\u4eca\u5e74\u4e8c\u5341\u516b\u5c81\uff0c\u4e09\u5e74\u524d\u535c\u9e70\u5c31\u5df2\u5c06\u201c\u9e70\u8bb0\u201d\u7684\u5546\u52a1\u4ea4\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u4e00\u4e2a\u4e8c\u5341\u4e94\u5c81\u7684\u4eba\u5c31\u80fd\u5347\u8d77\u5982\u6b64\u9ad8\u4f4d\uff0c\u5e76\u4e0d\u662f\u5bb9\u6613\u4e8b\uff0c\u4e5f\u5e76\u975e\u4fa5\u5e78\u3002 \u3000\u3000\u4ed6\u5e74\u8f7b\uff0c\u8bda\u5b9e\uff0c\u751f\u6d3b\u7b80\u6734\uff0c\u505a\u4eba\u672c\u4efd\uff0c\u8bf4\u8bdd\u4e2d\u80af\u627c\u8981\uff0c\u867d\u7136\u81f3\u4eca\u4ecd\u662f\u72ec\u8eab\uff0c\u5374\u4ece\u6765\u4e0d\u8fd1\u9152\u8272\u3002 \u3000\u3000\u535c\u9e70\u4fe1\u4efb\u4ed6\uff0c\u4ed6\u7684\u4f19\u8ba1\u5c0a\u91cd\u4ed6\uff0c\u4ed6\u4e5f\u4ece\u672a\u8ba9\u522b\u4eba\u5931\u671b\u8fc7\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u8ba9\u5c0f\u65b9\u5931\u671b\u3002 \u3000\u3000\u4ed6\u7528\u8bda\u6073\u7684\u6001\u5ea6\u548c\u6eda\u70eb\u7684\u9165\u6cb9\u8336\u62db\u5f85\u5c0f\u65b9\uff0c\u4ed6\u7ecf\u8425\u7684\u5546\u53f7\u7b80\u6734\u89c4\u77e9\u5e72\u51c0\u5927\u65b9\u3002 \u3000\u3000\u4ed6\u544a\u8bc9\u5c0f\u65b9\uff1a\u201c\u6211\u5c31\u4f4f\u5728\u540e\u9762\uff0c\u53ea\u8981\u4f60\u6ca1\u4e8b\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u6765\u627e\u6211\u3002\u201d\u6731\u4e91\u8bf4\uff0c\u201c\u6211\u6bcf\u5929\u90fd\u5728\uff0c\u65e5\u591c\u90fd\u5728\u3002\u201d \u3000\u3000\u9633\u5149\u62c9\u7740\u4ed6\u7684\u624b\uff0c\u5c31\u597d\u50cf\u5979\u62c9\u7740\u535c\u9e70\u3001\u5c0f\u65b9\u7684\u624b\u4e00\u6837\u3002 \u3000\u3000\u201c\u4ed6\u5e73\u65f6\u4e0d\u559d\u9152\uff0c\u53ef\u662f\u5982\u679c\u4f60\u4e00\u5b9a\u8981\u4ed6\u559d\uff0c\u4ed6\u4e0d\u4f1a\u6bd4\u4f60\u5148\u9189\u3002\u201d\u5979\u7684\u7b11\u5bb9\u5982\u9633\u5149\uff0c\u201c\u53ea\u4e0d\u8fc7\u4f60\u8981\u627e\u5973\u4eba\uff0c\u4ed6\u5c31\u6ca1\u6cd5\u5b50\u4e86\u3002\u201d \u3000\u3000\u5979\u5e76\u6ca1\u6709\u628a\u201c\u627e\u5973\u4eba\u201d\u5f53\u4f5c\u4e00\u4ef6\u4e22\u4eba\u7684\u4e8b\uff0c\u5979\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u6307\u7740\u5979\u90a3\u4e2a\u867d\u7136\u6709\u70b9\u5f2f\u66f2\uff0c\u770b\u8d77\u6765\u8fd8\u662f\u5f88\u6f02\u4eae\u7684\u9f3b\u5b50\u8bf4\uff1a\u201c\u4f60\u8981\u627e\u5973\u4eba\uff0c\u5c31\u6765\u6c42\u6211\uff0c\u6211\u66ff\u4f60\u627e\u7684\u5973\u5b69\u5b50\u4fdd\u8bc1\u6bd4\u4f60\u4ee5\u524d\u89c1\u8fc7\u7684\u90fd\u6e29\u67d4\u597d\u770b\u3002\u201d \u3000\u3000\u5979\u4e0d\u662f\u5973\u4eba\uff0c\u4e0d\u662f\u5c5e\u4e8e\u67d0\u4e00\u4e2a\u4eba\u7684\u5973\u4eba\u3002 \u3000\u3000\u5979\u662f\u9633\u5149\u3002 \u3000\u3000\u9633\u5149\u662f\u5c5e\u4e8e\u5927\u5bb6\u7684\uff0c\u8c01\u4e5f\u4e0d\u80fd\u72ec\u5360\u3002 \u3000\u3000\u2014\u2014\u6ce2\u5a03\u5462\uff1f \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u7ad9\u8d77\u6765\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u73b0\u5728\u5c31\u5e26\u6211\u53bb\u627e\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\uff1f\u201d\u9633\u5149\u663e\u5f97\u6709\u70b9\u60ca\u8bb6\uff0c\u201c\u73b0\u5728\u4f60\u5c31\u8981\u53bb\u627e\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f46\u8981\u627e\u5973\u4eba\uff0c\u8fd8\u8981\u559d\u9152\u3002\u201d \u3000\u3000\u8fd9\u91cc\u662f\u5723\u5730\uff0c\u5723\u5730\u4e5f\u50cf\u522b\u7684\u5730\u65b9\u4e00\u6837\uff0c\u4e5f\u6709\u7981\u5730\uff0c\u4e5f\u6709\u9ed1\u6697\u7684\u5730\u65b9\uff0c\u6709\u9152\uff0c\u4e5f\u6709\u5973\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53d1\u73b0\u4e86\u4e2a\u5973\u5b69\u5b50\u5f88\u50cf\u6ce2\u5a03\uff0c\u4e00\u4e2a\u7626\u7626\u7684\u3001\u5f31\u5f31\u7684\u3001\u9759\u9759\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u4ed6\u5df2\u7ecf\u9189\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u9189\u5728\u5723\u5730\uff0c\u8ddf\u9189\u5728\u522b\u7684\u5730\u65b9\u6ca1\u6709\u4ec0\u4e48\u4e24\u6837\u3002 \u3000\u3000\u51cc\u6668\u3002 \u3000\u3000\u5c0f\u65b9\u4ece\u90a3\u6761\u6ca1\u6709\u67f3\u7684\u67f3\u5df7\u4e2d\u8d70\u51fa\u6765\uff0c\u53ea\u89c9\u5f97\u5934\u75db\u3001\u5e72\u6e34\u3001\u6cae\u4e27\u3002\u8fd9\u79cd\u611f\u89c9\u4e5f\u8ddf\u4ed6\u5728\u522b\u7684\u5730\u65b9\u9189\u540e\u9192\u6765\u65f6\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002 \u3000\u3000\u9633\u5149\u6b63\u7167\u4e0a\u4e00\u5757\u659c\u5899\uff0c\u662f\u91d1\u9ec4\u8272\u7684\u9633\u5149\uff0c\u4e0d\u662f\u84dd\u8272\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u8863\u7740\u8934\u891b\u3001\u84ec\u5934\u57a2\u9762\u7684\u5c0f\u5b69\uff0c\u624b\u91cc\u6367\u7740\u4e2a\u94c1\u7f50\u5b50\uff0c\u8e72\u5728\u659c\u5899\u4e0b\uff0c\u4f4e\u5934\u770b\u7740\u4ed6\u7684\u7f50\u5b50\uff0c\u770b\u5f97\u805a\u7cbe\u4f1a\u795e\uff0c\u5c31\u597d\u50cf\u4e16\u754c\u4e0a\u518d\u6ca1\u6709\u4ec0\u4e48\u6bd4\u8fd9\u7f50\u5b50\u91cc\u7684\u4e1c\u897f\u66f4\u6709\u8da3\u4e86\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u672c\u6765\u5c31\u5145\u6ee1\u4e86\u8bb8\u8bb8\u591a\u591a\u5f88\u65e0\u804a\u7684\u4e8b\uff0c\u73b0\u5728\u7684\u5c0f\u65b9\u5fc3\u91cc\u4e5f\u89c9\u5f97\u5f88\u65e0\u804a\u3002 \u3000\u3000\u4e00\u4e2a\u65e0\u804a\u7684\u4eba\uff0c\u505a\u4e86\u4e00\u591c\u65e0\u804a\u7684\u4e8b\uff0c\u5fc3\u60c5\u603b\u662f\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u53bb\u770b\u770b\u8fd9\u5c0f\u5b69\u7f50\u5b50\u91cc\u88c5\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u7f50\u5b50\u91cc\u88c5\u7684\u662f\u866b\uff0c\u88c5\u6ee1\u4e86\u5404\u79cd\u626d\u66f2\u8815\u52a8\u7684\u5c0f\u866b\u3002 \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u95ee\u4ed6\uff1a\u201c\u8fd9\u4e9b\u662f\u4ec0\u4e48\u866b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u866b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6709\u70b9\u60ca\u5947\uff1a\u201c\u4e0d\u662f\u866b\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5728\u4f60\u773c\u4e2d\u770b\u6765\uff0c\u770b\u6765\u867d\u7136\u662f\u866b\uff0c\u53ef\u662f\u5728\u6211\u670b\u53cb\u773c\u4e2d\u5374\u662f\u987f\u4e30\u5bcc\u7684\u5927\u9910\u3002\u201d \u3000\u3000\u4ed6\u62ac\u8d77\u5934\u6765\uff0c\u770b\u7740\u5c0f\u65b9\uff0c\u8138\u4e0a\u867d\u7136\u810f\u5f97\u8981\u547d\uff0c\u4f46\u4e00\u53cc\u9ed1\u767d\u5206\u660e\u7684\u5927\u773c\u775b\uff0c\u663e\u5f97\u975e\u5e38\u673a\u4f36\u5de7\u9ee0\uff1a\u201c\u56e0\u4e3a\u6211\u7684\u670b\u53cb\u4e0d\u662f\u4eba\uff0c\u662f\u9e1f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u8fd9\u5c0f\u5b69\u5f88\u6709\u610f\u601d\uff0c\u8bf4\u7684\u8bdd\u4e5f\u5f88\u6709\u610f\u601d\uff0c\u4ed6\u6545\u610f\u95ee\uff1a\u201c\u4f60\u660e\u660e\u662f\u4e2a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u8ddf\u9e1f\u4ea4\u670b\u53cb\u53f6 \u3000\u3000\u201c\u56e0\u4e3a\u6ca1\u6709\u4eba\u80af\u8ddf\u6211\u4ea4\u670b\u53cb\uff0c\u53ea\u6709\u4e4c\u80af\u8ddf\u6211\u4ea4\u670b\u53cb\u3002\u201d\u5c0f\u5b69\u8bf4\uff1a\u201c\u6709\u670b\u53cb\u603b\u6bd4\u6ca1\u6709\u670b\u53cb\u597d\u3002\u201d \u3000\u3000\u4ed6\u660e\u660e\u662f\u4e2a\u5c0f\u5b69\uff0c\u53ef\u662f\u4ed6\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u4e0d\u50cf\u662f\u5c0f\u5b69\u8bf4\u7684\u3002 \u3000\u3000\u4ed6\u7684\u8bdd\u7adf\u5f15\u8d77\u4e86\u5c0f\u65b9\u5f88\u591a\u611f\u89e6\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6709\u670b\u53cb\u7684\u786e\u6bd4\u6ca1\u6709\u670b\u53cb\u597d\u3002\u201d\u5c0f\u65b9\u8f7b\u8f7b\u53f9\u606f\uff0c\u201c\u9e1f\u670b\u53cb\u6709\u65f6\u5019\u4e5f\u6bd4\u4eba\u670b\u53cb\u597d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4eba\u4f1a\u9a97\u4eba\u3001\u5bb3\u4eba\uff0c\u9e1f\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u51c6\u5907\u8d70\u4e86\uff0c\u4ed6\u4e0d\u60f3\u8ba9\u8fd9\u5929\u771f\u7684\u5c0f\u5b69\u77e5\u9053\u5927\u591a\u4eba\u5fc3\u7684\u8be1\u8ba1\u3002 \u3000\u3000\u5c0f\u5b69\u5374\u53c8\u95ee\u4ed6\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u5bf9\u670b\u53cb\u597d\u4e0d\u597d\uff1f\u201d\u4ed6\u95ee\u7684\u8bdd\u5f88\u5947\u602a\uff1a\u201c\u5982\u679c\u4f60\u6709\u4e2a\u670b\u53cb\u9700\u8981\u4f60\u5e2e\u52a9\uff0c\u60f3\u8981\u4f60\u53bb\u770b\u770b\u4ed6\uff0c\u4f60\u80af\u4e0d\u80af\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u56de\u8fc7\u5934\uff0c\u770b\u770b\u4ed6\uff1a\u201c\u5982\u679c\u6211\u80af\u53bb\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4f60\u80af\u53bb\uff0c\u73b0\u5728\u5c31\u8ddf\u6211\u8d70\u3002\u201d \u3000\u3000\u201c\u8ddf\u4f60\u8d70\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u8ddf\u4f60\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u662f\u4f60\u90a3\u4e2a\u670b\u53cb\u53eb\u6211\u6765\u627e\u4f60\u7684\u3002\u201d\u5c0f\u5b69\u8bf4\uff0c\u201c\u6211\u5df2\u7ecf\u5728\u8fd9\u7b49\u4f60\u4e00\u591c\u3002\u201d \u3000\u3000\u5c0f\u65b9\u66f4\u60ca\u8bb6\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d\u5c0f\u5b69\u9053\uff1a\u201c\u4f60\u59d3\u65b9\uff0c\u522b\u4eba\u90fd\u53eb\u4f60\u8981\u547d\u7684\u5c0f\u65b9\u3002\u201d \u3000\u3000\u201c\u6211\u90a3\u4e2a\u670b\u53cb\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u8981\u6211\u66ff\u4ed6\u4fdd\u5b88\u79d8\u5bc6\u3002\u6211\u5df2\u7ecf\u7b54\u5e94\u4e86\u4ed6\u3002\u5c31\u7b97\u4f60\u6740\u4e86\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8bf4\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u597d\u5947\u5fc3\u65e0\u7591\u88ab\u5f15\u8d77\u3002 \u3000\u3000\u4e00\u7f50\u5c0f\u866b\uff0c\u4e00\u4e2a\u5c0f\u5b69\uff0c\u4e00\u4e2a\u9700\u8981\u4ed6\u5e2e\u52a9\u7684\u670b\u53cb\uff0c\u4e00\u4ef6\u5b81\u6b7b\u4e5f\u4e0d\u80fd\u8bf4\u51fa\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u60f3\u5230\u8fd9\u4e9b\u4e8b\u5c45\u7136\u80fd\u8054\u5728\u4e00\u8d77\uff0c\u4ed6\u60f3\u4e0d\u901a\u8fd9\u5176\u4e2d\u6709\u4ec0\u4e48\u8054\u7cfb\u3002 \u3000\u3000\u201c\u597d\u3002\u201d\u5c0f\u65b9\u5ffd\u7136\u4e0b\u4e86\u51b3\u5fc3\uff0c\u201c\u6211\u8ddf\u4f60\u53bb\uff0c\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u5c0f\u5b69\u5374\u53c8\u7528\u90a3\u53cc\u9ed1\u767d\u5206\u660e\u7684\u5927\u773c\u775b\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\u3002 \u3000\u3000\u201c\u6211\u80fd\u66ff\u4f60\u7684\u670b\u53cb\u5b88\u79d8\u5bc6\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u66ff\u670b\u53cb\u4fdd\u5b88\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u70b9\u5934\u3002 \u3000\u3000\u5c0f\u5b69\u5ffd\u7136\u722c\u8d77\u6765\uff0c\u7528\u4e00\u53ea\u810f\u5f97\u51fa\u5947\u7684\u5c0f\u624b\uff0c\u62c9\u8d77\u5c0f\u65b9\u7684\u624b\uff1a\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u8fdc\u5904\u949f\u58f0\u9f50\u545c\uff0c\u4e00\u58f0\u58f0\u68b5\u5531\u968f\u98ce\u98d8\u6765\uff0c\u5b9d\u5854\u7684\u5c16\u9876\u5728\u592a\u9633\u4e0b\u95ea\u7740\u91d1\u5149\u3002 \u3000\u3000\u592a\u9633\u6f84\u84dd\uff0c\u9633\u5149\u8273\u4e3d\uff0c\u5145\u6ee1\u4e86\u795e\u5723\u5e84\u4e25\u8083\u7a46\u7684\u666f\u8c61\u3002 \u3000\u3000\u80ae\u810f\u7684\u5c0f\u5df7\u91cc\uff0c\u5374\u6324\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u5351\u8d31\u5e73\u51e1\u7a77\u56f0\u9f8c\u9f8a\u7684\u4eba\uff0c\u4ed6\u4eec\u7684\u795e\u4f5b\u597d\u50cf\u5e76\u6ca1\u6709\u542c\u5230\u4ed6\u4eec\u7684\u7948\u6c42\u7977\u544a\uff0c\u5e76\u6ca1\u6709\u597d\u597d\u5730\u7167\u987e\u4ed6\u4eec\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u4ece\u4e0d\u57cb\u6028\u3002 \u3000\u3000\u5c0f\u5b69\u62c9\u7740\u5c0f\u65b9\u7684\u624b\uff0c\u7a7f\u7740\u4eba\u7fa4\uff0c\u7a7f\u8fc7\u5c0f\u5df7\uff0c\u6765\u5230\u4e00\u5ea7\u5b8f\u5927\u58ee\u4e3d\u7684\u5bfa\u9662\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u662f\u5927\u62db\u5bfa\u3002\u201d \u3000\u3000\u5230\u5927\u62db\u5bfa\u6765\u5e72\u4ec0\u4e48\uff1f\u90a3\u4e2a\u795e\u79d8\u7684\u670b\u53cb\u662f\u4e0d\u662f\u5728\u5927\u62db\u5bfa\u7b49\u4ed6\uff1f \u3000\u3000\u5c0f\u5b69\u5b50\u50cf\u6545\u610f\u4e0d\u8ba9\u5c0f\u65b9\u518d\u95ee\uff0c\u5f88\u5feb\u5730\u62c9\u7740\u4ed6\uff0c\u4ece\u65e0\u6570\u8654\u8bda\u7684\u9999\u5ba2\u4e2d\u6324\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u660e\u660e\u662f\u4e2a\u5b69\u5b50\uff0c\u53ef\u662f\u505a\u51fa\u6765\u7684\u4e8b\u4e5f\u4e0d\u50cf\u5c0f\u5b69\u505a\u7684\u3002 \u3000\u3000\u58ee\u4e3d\u7684\u5bfa\u9662\uff0c\u5149\u7ebf\u5374\u5341\u5206\u9634\u68ee\u5e7d\u6697\uff0c\u6570\u5343\u652f\u5de8\u70db\u548c\u7528\u725b\u6cb9\u505a\u71c3\u6599\u7684\u9752\u94dc\u706f\uff0c\u5728\u98ce\u4e2d\u95ea\u52a8\u7740\u795e\u79d8\u7684\u706b\u7130\u3002 \u3000\u3000\u9ad8\u8038\u7684\u5bfa\u5899\u4e0a\uff0c\u6709\u65e0\u6570\u795e\u50cf\uff0c\u4f9b\u5949\u7740\u9762\u76ee\u72f0\u72de\u7684\u5de8\u5927\u4e03\u8272\u795e\u50cf\uff0c\u5728\u95ea\u52a8\u7684\u70db\u706b\u4e2d\uff0c\u66f4\u663e\u5f97\u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u4e5f\u8bb8\u5c31\u662f\u8fd9\u79cd\u529b\u91cf\uff0c\u624d\u80fd\u4f7f\u4eba\u7684\u5fc3\u795e\u5b8c\u5168\u88ab\u62d8\u6444\uff0c\u5b8c\u5168\u5fd8\u8bb0\u81ea\u6211\uff0c\u6709\u7684\u9999\u5ba2\u811a\u4e0a\u751a\u81f3\u62d6\u7740\u6c89\u91cd\u7684\u94c1\u9563\uff0c\u5728\u4f5b\u5802\u91cc\u722c\u884c\u3002 \u3000\u3000\u5c0f\u65b9\u4e86\u89e3\u4ed6\u4eec\u8fd9\u79cd\u884c\u4e3a\uff0c\u4e16\u4e0a\u6709\u5f88\u591a\u4eba\u90fd\u5e0c\u671b\u80fd\u501f\u8089\u4f53\u4e0a\u7684\u82e6\u75db\uff0c\u6d88\u9664\u5fc3\u4e0a\u7684\u6127\u759a\u7f6a\u6106\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u4eff\u4f5b\u6c89\u6d78\u5165\u8fd9\u79cd\u4f3c\u771f\u4f3c\u5e7b\u3001\u865a\u65e0\u7384\u79d8\u7684\u611f\u89c9\u4e2d\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4e86\u89e3\u5230\u5b97\u6559\u529b\u91cf\u7684\u795e\u5947\u4f1f\u5927\u3002 \u3000\u3000\u7a7a\u6c14\u4e2d\u6c24\u6c32\u7740\u9178\u5976\u548c\u9999\u70db\u7684\u6c14\u5473\uff0c\u98ce\u4e2d\u56de\u8361\u7740\u949f\u9f13\u94dc\u94b9\u58f0\uff0c\u6c89\u90c1\u7684\u9634\u5f71\u4e2d\u706f\u706b\u6447\u66f3\uff0c\u4f4e\u6c89\u5feb\u901f\u7684\u7ecf\u5492\u58f0\u968f\u7740\u4f5b\u524d\u7684\u7948\u7977\u58f0\u54cd\u52a8\u3002 \u3000\u3000\u5c0f\u5b69\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u505c\u5728\u53f3\u58c1\u4e0a\u4e00\u4e2a\u7a79\u5f62\u7684\u77f3\u7a9f\u524d\u3002 \u3000\u3000\u77f3\u7a9f\u91cc\u6709\u4e00\u5e45\u8272\u5f69\u9c9c\u8273\uff0c\u4f46\u5374\u6050\u6016\u4e4b\u6781\u7684\u58c1\u753b\uff0c\u753b\u7684\u662f\u4e00\u4e2a\u72f0\u72de\u5a07\u5f02\u7684\u7f57\u5239\u9b3c\u5973\uff0c\u6b63\u5728\u542e\u5438\u7740\u4e00\u4e2a\u51e1\u4eba\u7684\u8111\u9ad3\u3002 \u3000\u3000\u7cbe\u5bc6\u7ec6\u81f4\u7684\u753b\u4e0a\uff0c\u770b\u6765\u8981\u6829\u6829\u5982\u751f\uff0c\u5c0f\u65b9\u867d\u7136\u77e5\u9053\u8fd9\u53ea\u4e0d\u8fc7\u662f\u5e45\u753b\uff0c\u5fc3\u91cc\u8fd8\u662f\u89c9\u5f97\u5f88\u4e0d\u8212\u670d\u3002 \u3000\u3000\u5c0f\u5b69\u5ffd\u53c8\u95f4\u4ed6\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u8fd9\u4e2a\u7f57\u5239\u9b3c\u5973\u4e3a\u4ec0\u4e48\u8981\u5438\u4ed6\u7684\u8111\u9ad3\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u56e0\u4ed6\u662f\u4e2a\u4e0d\u5b88\u4fe1\u7684\u4eba\u3002\u201d\u5c0f\u5b69\u8bf4\uff0c\u201c\u4ed6\u7b54\u5e94\u4e3a\u4ed6\u670b\u53cb\u4fdd\u5b88\u79d8\u5bc6\u5374\u6ca1\u505a\u5230\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\uff1a\u201c\u4f60\u597d\u50cf\u4e0d\u5927\u4fe1\u4efb\u6211\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u8fd8\u4e0d\u662f\u670b\u53cb\uff0c\u6211\u4e0d\u80fd\u4fe1\u4efb\u4f60\u3002\u201d \u3000\u3000\u5c0f\u5b69\u7684\u5927\u773c\u775b\u91cc\u95ea\u52a8\u7740\u72e1\u9ee0\u7684\u5149\uff1a\u201c\u4f60\u8981\u6211\u5e26\u4f60\u53bb\uff0c\u4e00\u5b9a\u8981\u5728\u8fd9\u91cc\u5148\u7acb\u4e2a\u8a93\uff0c\u5982\u679c\u4f60\u8fdd\u80cc\u4e86\u8a93\u8a00\uff0c\u7ec8\u751f\u90fd\u8981\u50cf\u8fd9\u4e2a\u4eba\u4e00\u6837\uff0c\u53d7\u7f57\u5239\u9b3c\u5973\u6076\u6bd2\u7684\u6298\u78e8\u3002\u201d \u3000\u3000\u90a3\u4e2a\u670b\u53cb\u7a76\u7adf\u662f\u8c01\uff0c\u884c\u8e2a\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u8be1\u79d8\uff1f \u3000\u3000\u5c0f\u65b9\u7acb\u4e0b\u4e86\u8fd9\u4e2a\u6bd2\u8a93\u3002 \u3000\u3000\u4ed6\u4e0d\u6015\u795e\u9b3c\u7684\u62a5\u5e94\uff0c\u4ed6\u4ece\u672a\u51fa\u5356\u8fc7\u522b\u4eba\uff0c\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u552f\u4e00\u5bf9\u4e0d\u8d77\u7684\u4eba\uff0c\u5c31\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u5c0f\u5b69\u7b11\u4e86\uff0c\u771f\u5fc3\u7684\u7b11\u4e86\u3002 \u3000\u3000\u201c\u4f60\u679c\u7136\u662f\u4e2a\u597d\u4eba\u3002\u201d\u4ed6\u53c8\u62c9\u8d77\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u6211\u771f\u7684\u5e26\u4f60\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u5230\u9e1f\u5c4b\u53bb\u3002\u201d \u3000\u3000\u5c0f\u5b69\u8bf4\uff1a\u201c\u4f60\u7684\u670b\u53cb\u548c\u6211\u670b\u53cb\u90fd\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u9e1f\u5c4b\u662f\u680b\u5947\u602a\u7684\u6728\u5c4b\uff0c\u5efa\u9020\u5728\u4e00\u7247\u51f8\u8d77\u7684\u5c71\u5ca9\u4e0a\uff0c\u51e0\u68f5\u5de8\u5927\u7684\u6811\u6728\u95ee\u3002 \u3000\u3000\u6728\u5c4b\u7684\u56db\u5468\u90fd\u6709\u680f\u6746\uff0c\u5c4b\u6a90\u9e1f\u7fc5\u822c\u5411\u5916\u4f38\u51fa\uff0c\u6a90\u4e0b\u6302\u6ee1\u4e86\u9e1f\u7b3c\u3002 \u3000\u3000\u624b\u5de5\u7cbe\u7ec6\u7684\u9e1f\u7b3c\u91cc\uff0c\u9e1f\u8bed\u5541\u557e\uff0c\u6709\u7684\u9e1f\u5c0f\u65b9\u975e\u4f46\u4e0d\u77e5\u540d\uff0c\u8fde\u770b\u90fd\u6ca1\u770b\u89c1\u8fc7\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u9e1f\u7b3c\u90fd\u662f\u6211\u505a\u7684\u3002\u201d \u3000\u3000\u5c0f\u5b69\u7684\u773c\u4e2d\u95ea\u7740\u5149\uff0c\u663e\u7136\u5728\u4e3a\u81ea\u5df1\u800c\u9a84\u50b2\uff1a\u201c\u4f60\u770b\u4e0d\u770b\u5f97\u51fa\u5b83\u4eec\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u770b\u51fa\u6765\uff0c\u8fd9\u4e9b\u9e1f\u7b3c\u867d\u7136\u4e5f\u6709\u201c\u95e8\u201d\uff0c\u5374\u90fd\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u201c\u6211\u4e0d\u613f\u628a\u5b83\u4eec\u5f53\u56da\u72af\u822c\u5173\u5728\u7b3c\u5b50\u91cc\uff0c\u53ea\u8981\u4ed6\u4eec\u9ad8\u5174\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u98de\u51fa\u53bb\u3002\u201d\u5c0f\u5b69\u8bf4\uff1a\u201c\u53ef\u662f\u98de\u8d70\u7684\u5f80\u5f80\u53c8\u4f1a\u98de\u56de\u6765\u3002\u201d \u3000\u3000\u4ed6\u80ae\u810f\u7684\u8138\u4e0a\u9732\u51fa\u5149\u8f89\u7684\u7b11\u5bb9\uff1a\u201c\u56e0\u4e3a\u5b83\u4eec\u4e5f\u77e5\u9053\u6211\u662f\u5b83\u4eec\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u6211\u90a3\u4e2a\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u5c0f\u5b69\u6307\u7740\u4e00\u6247\u5f88\u7a84\u5f88\u7a84\u7684\u6728\u95e8\uff1a\u201c\u4f60\u7684\u670b\u53cb\u5c31\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u6728\u5c4b\u91cc\u5bbd\u5927\u7a7a\u9614\uff0c\u4e2d\u58c1\u7684\u6728\u677f\u90fd\u5df2\u5f88\u9648\u65e7\uff0c\u6709\u7684\u751a\u81f3\u5df2\u5e72\u88c2\uff0c\u65e0\u7591\u5df2\u662f\u680b\u591a\u5e74\u7684\u8001\u5c4b\uff0c\u8fdc\u5728\u8fd9\u5c0f\u5b69\u51fa\u4e16\u524d\u5c31\u5df1\u5efa\u8d77\u3002 \u3000\u3000\u5bbd\u5927\u7684\u6728\u5c4b\u91cc\uff0c\u53ea\u6709\u4e00\u5f20\u4f4e\u77ee\u7684\u6728\u684c\u3001\u4e00\u4e2a\u5de8\u5927\u7684\u706b\u76c6\u548c\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u706b\u76c6\u4e0a\u652f\u7740\u70e7\u70e4\u98df\u7269\u7684\u94c1\u67b6\uff0c\u4eba\u5c31\u5750\u5728\u5730\u4e0a\uff0c\u80cc\u5bf9\u7740\u95e8\u3002 \u3000\u3000\u5c0f\u65b9\u8fdb\u6765\u65f6\uff0c\u4ed6\u6ca1\u6709\u56de\u5934\uff0c\u4e5f\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u7684\u80cc\u666f\u5f88\u7626\uff0c\u53cc\u80a9\u659c\u659c\u4e0b\u524a\uff0c\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u843d\u5bde\u8427\u7d22\uff0c\u4e16\u4e0a\u4eff\u4f5b\u5df2\u5f88\u5c11\u6709\u4eba\u80fd\u60ca\u52a8\u4ed6\uff0c\u5f15\u8d77\u4ed6\u7684\u6ce8\u610f\u3002 \u3000\u3000\u5982\u679c\u4f60\u4e5f\u662f\u4e2a\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6c5f\u6e56\u4eba\uff0c\u4f60\u4ece\u4e00\u4e2a\u4eba\u7684\u80cc\u5f71\uff0c\u4e5f\u80fd\u770b\u51fa\u5f88\u591a\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u7ecf\u9a8c\u867d\u7136\u5e76\u4e0d\u5341\u5206\u591a\uff0c\u53ef\u662f\u4ed6\u4e00\u3002\u770b\u89c1\u8fd9\u4e2a\u4eba\u7684\u80cc\u5c31\u7acb\u523b\u786e\u5b9a\u4e86\u4e00\u4ef6\u4e8b\u2014\u2014 \u3000\u3000\u4ed6\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u66f4\u4e0d\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u3002\u53ea\u8981\u662f\u4ed6\u8ba4\u5f97\u7684\u4eba\uff0c\u4ed6\u53ea\u8981\u770b\u89c1\u80cc\u5f71\uff0c\u5c31\u4e00\u5b9a\u80fd\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u4e2a\u4eba\u7edd\u5bf9\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u51c6\u4e5f\u4e0d\u4f1a\u8ddf\u4e00\u4e2a\u81ea\u5df1\u4ece\u672a\u89c1\u8fc7\u7684\u4eba\u4ea4\u4e0a\u670b\u53cb\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u51c6\uff1f\u4e3a\u4ec0\u4e48\u8981\u5192\u79f0\u5c0f\u65b9\u7684\u670b\u53cb\uff1f\u4e3a\u4ec0\u4e48\u8981\u4e2a\u5c0f\u5b69\u5e26\u5c0f\u65b9\u6765\u89c1\u4ed6\uff1f \u3000\u3000\u5c0f\u65b9\u7ad9\u4f4f\u3002 \u3000\u3000\u4ed6\u8d70\u52a8\u65f6\u8f7b\u6377\u7075\u654f\uff0c\u4e00\u7ad9\u4f4f\u5c31\u5f97\u5f88\u7a33\uff0c\u5c31\u50cf\u662f\u4e00\u6839\u77f3\u6869\u9489\u5165\u5927\u5730\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u4e8c\u7ae0\u3000\u9e1f\u5c4b\u7591\u4e91 \u7b2c\u5341\u4e8c\u7ae0\u3000\u9e1f\u5c4b\u7591\u4e91 \u3000\u3000\u4ed6\u5df2\u7ecf\u6709\u4e86\u51c6\u5907\uff0c\u51c6\u5907\u5e94\u4ed8\u4efb\u4f55\u4e00\u79cd\u7a81\u53d1\u7684\u5371\u673a\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5148\u53d1\u52a8\uff0c\u53ea\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u770b\u6765\u5e76\u4e0d\u662f\u4e2a\u5371\u9669\u7684\u4eba\uff0c\u4ed6\u53ea\u8bf4\uff1a\u201c\u6211\u5c31\u662f\u5c0f\u65b9\uff0c\u6211\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u8fd8\u662f\u6ca1\u6709\u56de\u5934\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u5730\u62ac\u8d77\u4ed6\u7684\u53f3\u624b\uff0c\u6307\u7740\u684c\u5b50\u5bf9\u9762\uff0c\u8f7b\u8f7b\u4ed6\u8bf4\u4e86\u4e00\u4e00\u4e2a\u5b57\uff1a\u201c\u5750\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u663e\u7136\u5f88\u8870\u5f31\uff0c\u4ed6\u7684\u624b\u4e0a\u7f20\u7740\u767d\u5e03\uff0c\u9690\u9690\u6709\u8840\u6e0d\u6e17\u51fa\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u65e0\u7591\u53d7\u4e86\u4f24\uff0c\u4f24\u5f97\u4e0d\u8f7b\u3002 \u3000\u3000\u5c0f\u65b9\u66f4\u786e\u4fe1\u81ea\u5df1\u7edd\u4e0d\u8ba4\u5f97\u8fd9\u4e2a\u4eba\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7edd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4ed6\u7684\u6212\u5907\u8b66\u60d5\u90fd\u5df2\u653e\u677e\u3002 \u3000\u3000\u4ed6\u7ed5\u8fc7\u4f4e\u77ee\u7684\u6728\u684c\u8d70\u5230\u8fd9\u4e2a\u4eba\u9762\u524d\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u770b\u89c1\u8fd9\u4e2a\u4eba\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u5fc3\u5ffd\u7136\u6c89\u4e86\u4e0b\u53bb\uff0c\u6c89\u5230\u51b0\u51b7\u7684\u811a\u5e95\u3002 \u3000\u3000\u5c0f\u65b9\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u867d\u7136\u662f\u5c0f\u65b9\u7684\u4ec7\u654c\uff0c\u4f46\u4ed6\u5982\u679c\u8981\u5c06\u5c0f\u65b9\u5f53\u4f5c\u670b\u53cb\uff0c\u5c0f\u65b9\u4e5f\u7edd\u4e0d\u4f1a\u62d2\u7edd\u3002 \u3000\u3000\u6709\u79cd\u4eba\u672c\u6765\u5c31\u662f\u4ecb\u4e8e\u670b\u53cb\u4e0e\u4ec7\u654c\u4e4b\u95f4\u7684\uff0c\u4e00\u4e2a\u503c\u5f97\u5c0a\u656c\u7684\u4ec7\u654c\uff0c\u6709\u65f6\u751a\u81f3\u6bd4\u771f\u5fc3\u7684\u670b\u53cb\u66f4\u96be\u6c42\u3002 \u3000\u3000\u5c0f\u65b9\u4e00\u76f4\u5c0a\u91cd\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u521a\u624d\u6ca1\u6709\u8ba4\u51fa\u8fd9\u4e2a\u4eba\uff0c\u53ea\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u5b8c\u5168\u53d8\u4e86\uff0c\u53d8\u5f97\u60b2\u60e8\u800c\u53ef\u6015\u3002 \u3000\u3000\u7edd\u4ee3\u7684\u4f73\u4eba\u5ffd\u7136\u53d8\u4e3a\u819c\u6bcd\uff0c\u7edd\u4e16\u7684\u5229\u5668\u5ffd\u7136\u53d8\u4e3a\u987d\u94c1\u3002 \u3000\u3000\u867d\u7136\u5929\u610f\u96be\u6d4b\uff0c\u4e16\u4e8b\u591a\u53d8\uff0c\u53ef\u662f\u8fd9\u79cd\u53d8\u5316\u4ecd\u7136\u4ee4\u4eba\u96be\u514d\u4f24\u60b2\u3002 \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u60f3\u5230\u4e00\u4f4d\u7edd\u4ee3\u7684\u5251\u5ba2\u7adf\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7adf\u662f\u72ec\u5b64\u75f4\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u75f4\u3002 \u3000\u3000\u975e\u75f4\u4e8e\u5251\u3002\u4e43\u75f4\u4e8e\u60c5\u3002 \u3000\u3000\u5251\u75f4\u6c38\u8fdc\u4e0d\u80fd\u4e86\u89e3\u4e00\u4e2a\u75f4\u60c5\u4eba\u7684\u6d88\u6c89\u4e0e\u60b2\u4f24\uff0c\u4f46\u662f\u771f\u6b63\u75f4\u60c5\u7684\u4eba\uff0c\u5374\u7edd\u5bf9\u53ef\u4ee5\u4e86\u89e3\u4e00\u4e2a\u5251\u75f4\u7684\u5b64\u72ec\u3001\u5bc2\u5bde\u548c\u75db\u82e6\u3002 \u3000\u3000\u5251\u5ba2\u65e0\u540d\uff0c\u56e0\u4e3a\u4ed6\u5df2\u75f4\u4e8e\u5251\uff0c\u5982\u679c\u4ed6\u5931\u53bb\u4e86\u4ed6\u7684\u5251\uff0c\u5fc3\u4e2d\u662f\u4ec0\u4e48\u611f\u53d7\uff1f \u3000\u3000\u5982\u679c\u4ed6\u5df2\u5931\u53bb\u4e86\u4ed6\u63e1\u5251\u7684\u624b\uff0c\u5fc3\u4e2d\u53c8\u662f\u4ec0\u4e48\u611f\u53d7\uff1f \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u5750\u4e0b\u3002 \u3000\u3000\u201c\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u6211\u3002\u201d\u72ec\u5b64\u75f4\u7684\u58f0\u97f3\u5e73\u9759\u800c\u8870\u5f31\uff0c\u201c\u4f60\u4e00\u5b9a\u60f3\u4e0d\u5230\u662f\u6211\u627e\u4f60\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u6211\u627e\u4f60\u6765\uff0c\u53ea\u56e0\u4e3a\u6211\u6ca1\u6709\u670b\u53cb\uff0c\u4f60\u867d\u7136\u4e5f\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f46\u662f\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u6709\u5f88\u591a\u4e8b\u60c5\u90fd\u53ef\u4ee5\u5fcd\u4f4f\u4e0d\u95ee\uff0c\u5374\u5fcd\u4e0d\u4f4f\u8981\u53bb\u770b\u90a3\u53ea\u624b\u3002 \u3000\u3000\u90a3\u53ea\u63e1\u5251\u7684\u624b\uff0c\u90a3\u53ea\u73b0\u5728\u5df2\u88ab\u81ea\u5e03\u5305\u7f20\u7740\u7684\u624b\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u4e5f\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u5ffd\u7136\u89e3\u5f00\u4e86\u624b\u4e0a\u5305\u7f20\u7740\u7684\u767d\u5e03\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u788e\u88c2\u53d8\u5f62\uff0c\u6bcf\u4e00\u6839\u9aa8\u5934\u90fd\u51e0\u4e4e\u5df2\u788e\u88c2\u3002 \u3000\u3000\u5251\u5c31\u662f\u4ed6\u7684\u751f\u547d\uff0c\u73b0\u5728\u4ed6\u5df2\u5931\u53bb\u4e86\u4ed6\u63e1\u5251\u7684\u624b\u2014\u2014\u624d\u4eba\u5df2\u65e0\u4f73\u53e5\uff0c\u7ea2\u7c89\u5df2\u5316\u9ab7\u9ac5\uff0c\u767e\u6218\u529f\u6210\u7684\u82f1\u96c4\u5df2\u53bb\u6e29\u67d4\u4e61\u4f4f\uff0c\u826f\u9a79\u5df2\u4f0f\u677f\uff0c\u91d1\u5251\u5df2\u6c89\u57cb\u3002 \u3000\u3000\u5c0f\u65b9\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u9178\u695a\uff0c\u4e00\u79cd\u5c16\u9488\u523a\u5165\u9aa8\u9ad3\u822c\u7684\u9178\u695a\u3002 \u3000\u3000\u72ec\u6444\u5b64\u75f4\u5df2\u7ecf\u53d8\u4e86\uff0c\u53d8\u5f97\u8870\u5f31\u60df\u7cb9\uff0c\u53d8\u5f97\u5149\u8292\u5c3d\u5931\uff0c\u53d8\u5f97\u4ee4\u4eba\u5fc3\u788e\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u4e00\u70b9\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u5f88\u9759\uff0c\u5e73\u9759\u3001\u5b89\u9759\u3001\u51b7\u9759\uff0c\u9759\u5982\u78d0\u77f3\uff0c\u9759\u5982\u5927\u5730\u3002 \u3000\u3000\u5251\u5ba2\u65e0\u60c5\uff0c\u5251\u5ba2\u65e0\u540d\uff0c\u5251\u5ba2\u4e5f\u65e0\u6cea\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u773c\u775b\u91cc\u751a\u81f3\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u53ea\u662f\u9759\u9759\u5730\u770b\u7740\u4ed6\u90a3\u53ea\u788e\u88c2\u7684\u624b\u3002 \u3000\u3000\u201c\u4f60\u8be5\u770b\u5f97\u51fa\u6211\u8fd9\u53ea\u624b\u662f\u88ab\u634f\u788e\u7684\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u634f\u788e\u6211\u7684\u624b\u3002\u201d \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u7edd\u5bf9\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u5c0f\u65b9\u76f8\u4fe1\uff0c\u5c0f\u65b9\u4e5f\u77e5\u9053\u4ed6\u8bf4\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u77e5\u9053\u4ed6\u77e5\u9053\u3002 \u3000\u3000\u201c\u535c\u9e70\u4e0d\u662f\u5251\u5ba2\uff0c\u4e0d\u662f\u4fa0\u5ba2\uff0c\u4e5f\u4e0d\u662f\u82f1\u96c4\uff0c\u7edd\u5bf9\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95f4\u3002 \u3000\u3000\u201c\u535c\u9e70\u662f\u4eba\u6770\uff01\u201d\u72ec\u5b64\u75f4\u4ecd\u7136\u5f88\u5e73\u9759\uff0c\u201c\u4ed6\u7684\u5fc3\u4e2d\u53ea\u6709\u80dc\uff0c\u6ca1\u6709\u8d25\uff0c\u53ea\u8bb8\u80dc\uff0c\u4e0d\u8bb8\u8d25\u3002\u4e3a\u4e86\u6c42\u80dc\uff0c\u4ed6\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u8fd9\u4e00\u70b9\uff0c\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u201c\u4ed6\u77e5\u9053\u81ea\u5df1\u4e0d\u662f\u6211\u7684\u654c\u624b\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u4ed6\u6765\u627e\u6211\u6c42\u6218\u65f6\uff0c\u6211\u4e5f\u77e5\u9053\u4ed6\u5fc5\u8d25\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u4ed6\u6ca1\u6709\u8d25\u3002\u201d \u3000\u3000\u201c\u4ed6\u6ca1\u6709\u8d25\uff0c\u867d\u7136\u6ca1\u6709\u80dc\uff0c\u4e5f\u6ca1\u6709\u8d25\uff0c\u4ed6\u8fd9\u79cd\u4eba\u662f\u6c38\u8fdc\u4e0d\u4f1a\u8d25\u7684\u3002\u201d\u72ec\u5b64\u75f4\u53c8\u91cd\u590d\u4e00\u904d\uff0c\u201c\u56e0\u4e3a\u4ed6\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u3002\u201d \u3000\u3000\u201c\u4ed6\u727a\u7272\u4e86\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u95f4\uff0c\u201c\u4ed6\u600e\u4e48\u727a\u7272\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6545\u610f\u8ba9\u6211\u4e00\u5251\u523a\u5165\u4ed6\u80f8\u819b\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff1a\u201c\u5c31\u5728\u6211\u5251\u950b\u523a\u5165\u4ed6\u80f8\u819b\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u5ffd\u7136\u634f\u4f4f\u6211\u7684\u624b\uff0c\u634f\u788e\u4e86\u6211\u7684\u8fd9\u53ea\u624b\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5c45\u7136\u8fd8\u662f\u5f88\u5e73\u9759\uff1a\u201c\u90a3\u65f6\u6211\u81ea\u77e5\u5fc5\u80dc\uff0c\u800c\u4e14\u786e\u5b9e\u5df2\u7ecf\u80dc\u4e86\u3002\u90a3\u65f6\u6211\u7684\u7cbe\u795e\u3001\u5251\u950b\u90fd\u5df2\u4e0e\u4ed6\u7684\u8840\u8089\u4ea4\u4f1a\uff0c\u6211\u7684\u5251\u6c14\u5df2\u8870\uff0c\u6211\u7684\u5251\u5df2\u88ab\u4ed6\u7684\u8840\u8089\u6240\u963b\uff0c\u6b63\u662f\u6211\u6700\u5f31\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9759\u9759\u5730\u542c\u7740\uff0c\u4e0d\u80fd\u4e0d\u542c\uff0c\u4e5f\u4e0d\u60f3\u4e0d\u542c\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u4e00\u5411\u5f88\u5c11\u8bf4\u8bdd\uff0c\u53ef\u662f\u542c\u4ed6\u8bf4\u7684\u8bdd\uff0c\u5c31\u50cf\u662f\u542c\u540d\u5993\u8c08\u60c5\u3001\u9ad8\u50e7\u8bf4\u7985\u3002 \u3000\u3000\u201c\u90a3\u53ea\u4e0d\u8fc7\u662f\u5239\u90a3\u95f4\u7684\u4e8b\u3002\u201d\u72ec\u5b64\u75f4\u5ffd\u7136\u95ee\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4e00\u5239\u90a3\u662f\u591a\u4e45\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u201c\u4e00\u5239\u90a3\u201d\u975e\u5e38\u77ed\u6682\uff0c\u6bd4\u201c\u767d\u9a79\u8fc7\u9699\u201d\u90a3\u4e00\u77ac\u8fd8\u77ed\u6682\u3002 \u3000\u3000\u201c\u4e00\u5239\u90a3\u662f\u4f5b\u5bb6\u8bdd\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u4e00\u5f39\u6307\u95f4\uff0c\u5c31\u5df2\u662f\u516d\u5341\u5239\u90a3\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u5f53\u65f6\u751f\u6b7b\u80dc\u8d1f\u4e4b\u95f4\uff0c\u7684\u786e\u53ea\u6709\u2018\u4e00\u5239\u90a3\u2019\u4e09\u5b57\u6240\u80fd\u5f62\u5bb9\uff0c\u535c\u9e70\u6293\u4f4f\u4e86\u90a3\u4e00\u5239\u90a3\uff0c\u6240\u4ee5\u4ed6\u80fd\u4e0d\u8d25\u3002\u201d \u3000\u3000\u4e00\u5239\u90a3\u95f4\u5c31\u5df2\u51b3\u5b9a\u751f\u6b7b\u80dc\u8d1f\uff0c\u4e00\u201c\u5239\u90a3\u95f4\u5c31\u5df2\u6539\u53d8\u4e00\u4e2a\u4eba\u7ec8\u751f\u7684\u547d\u8fd0\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u662f\u591a\u4e48\u52a8\u9b44\u60ca\u5fc3\uff01 \u3000\u3000\u4f46\u662f\u72ec\u5b64\u75f4\u5728\u8c08\u53ca\u8fd9\u4e00\u5239\u90a3\u65f6\uff0c\u58f0\u97f3\u6001\u5ea6\u90fd\u4ecd\u7136\u4fdd\u6301\u51b7\u9759\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u4f69\u670d\u4ed6\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u4e0d\u662f\u540d\u5993\uff0c\u4e0d\u662f\u9ad8\u50e7\uff0c\u8bf4\u7684\u4e0d\u662f\u60c5\uff0c\u4e5f\u4e0d\u662f\u7985\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u5251\uff0c\u662f\u5251\u7406\u3002 \u3000\u3000\u5c0f\u65b9\u4f69\u670d\u7684\u4e0d\u662f\u8fd9\u4e00\u70b9\uff0c\u72ec\u5b64\u75f4\u5e94\u8be5\u80fd\u8bf4\u5251\uff0c\u4ed6\u5df2\u75f4\u4e8e\u5251\u3002\u5c0f\u65b9\u4f69\u670d\u7684\uff0c\u662f\u4ed6\u7684\u51b7\u9759\u3002 \u3000\u3000\u5f88\u5c11\u6709\u4eba\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u8fd8\u80fd\u4fdd\u6301\u51b7\u9759\uff0c\u5c0f\u65b9\u81ea\u5df1\u5c31\u4e0d\u80fd\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u4eff\u4f5b\u5df2\u770b\u7a7f\u4ed6\u7684\u5fc3\u610f\u3002 \u3000\u3000\u201c\u6211\u5df2\u5c06\u6211\u7684\u4e00\u751f\u732e\u4e8e\u5251\uff0c\u73b0\u5728\u6211\u8bf4\u4e0d\u5b9a\u5df2\u7ec8\u751f\u4e0d\u80fd\u518d\u63e1\u5251\uff0c\u4f46\u662f\u6211\u5e76\u6ca1\u6709\u53d1\u75af\uff0c\u4e5f\u6ca1\u6709\u5d29\u6e83\u3002\u201d\u4ed6\u95ee\u5c0f\u65b9\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u5f88\u5947\u602a\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u53c8\u95ee\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6709\u5012\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u5df1\u8bf4\u51fa\u4e86\u7b54\u6848\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u535c\u9e70\u867d\u7136\u634f\u788e\u4e86\u63e1\u5251\u7684\u624b\uff0c\u5374\u634f\u4e0d\u788e\u6211\u5fc3\u4e2d\u7684\u5251\u610f\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u6211\u7684\u624b\u7eb5\u7136\u5df2\u4e0d\u80fd\u518d\u63e1\u5251\uff0c\u53ef\u662f\u6211\u5fc3\u4e2d\u8fd8\u6709\u4e00\u67c4\u5251\u3002\u201d \u3000\u3000\u201c\u5fc3\u5251\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff1a\u2018\u201c\u5fc3\u5251\u5e76\u4e0d\u662f\u7a7a\u65e0\u865a\u5e7b\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u6001\u5ea6\u771f\u8bda\u800c\u4e25\u8083\uff1a\u201c\u4f60\u638c\u4e2d\u7eb5\u7136\u63e1\u6709\u5439\u6bdb\u65ad\u53d1\u7684\u5229\u5668\uff0c\u4f46\u4f60\u5fc3\u4e2d\u82e5\u662f\u65e0\u5251\uff0c\u4f60\u638c\u4e2d\u7684\u5251\u4e5f\u53ea\u4e0d\u8fc7\u662f\u5757\u5e9f\u94c1\u800c\u5df2\uff0c\u4f60\u8fd9\u4e2a\u4eba\u4e5f\u7ec8\u751f\u4e0d\u80fd\u6210\u4e3a\u771f\u6b63\u7684\u5251\u5ba2\u3002\u201d \u3000\u3000\u201c\u4ee5\u5fc3\u52a8\u5251\uff0c\u4ee5\u610f\u4f24\u654c\u3002\u201d \u3000\u3000\u8fd9\u79cd\u5251\u672f\u4e2d\u81f3\u9ad8\u81f3\u6df1\u7684\u5883\u754c\uff0c\u5c0f\u65b9\u867d\u7136\u8fd8\u4e0d\u80fd\u5b8c\u5168\u4e86\u89e3\uff0c\u4f46\u662f\u4ed6\u4e5f\u77e5\u9053\uff0c\u4e00\u4e2a\u771f\u6b63\u7684\u5251\u5ba2\uff0c\u5fc3\u4e0e\u5251\u5fc5\u5b9a\u5df2\u6eb6\u4e3a\u4e00\u8bfd\u6fc9* \u3000\u3000\u4eba\u5251\u5408\u4e00\uff0c\u9a6d\u6c14\u5fa1\u5251\uff0c\u4e5f\u8bb8\u53ea\u4e2a\u8fc7\u662f\u865a\u65e0\u7684\u795e\u8bdd\u800c\u5df2\u3002 \u3000\u3000\u5fc3\u5251\u5408\u4e00\uff0c\u5374\u662f\u5251\u5ba2\u4eec\u5fc5\u987b\u8fbe\u5230\u7684\u5883\u754c\uff0c\u5426\u5219\u4ed6\u6839\u672c\u4e0d\u80fd\u6210\u529b\u5251\u5ba2\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u53c8\u9053\uff1a\u201c\u535c\u9e70\u867d\u7136\u6ca1\u6709\u8d25\uff0c\u4f46\u662f\u4ed6\u4e5f\u6ca1\u6709\u80dc\uff0c\u5c31\u5728\u6211\u8fd9\u53ea\u624b\u88ab\u4ed6\u634f\u788e\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u6211\u8fd8\u662f\u53ef\u4ee5\u628a\u4ed6\u523a\u6740\u201c\u4e8e\u6211\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u6ca1\u6709\u523a\u6740\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u7684\u5fc3\u4e2d\u4ecd\u6709\u5251\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u6211\u4e5f\u8ddf\u4ed6\u4e00\u6837\uff0c\u6211\u4eec\u7684\u5fc3\u4e2d\u5e76\u6ca1\u6709\u751f\u6b7b\uff0c\u53ea\u6709\u80dc\u8d1f\u3002\u6211\u4eec\u6c42\u7684\u4e0d\u662f\u751f\uff0c\u800c\u662f\u80dc\uff0c\u627e\u5e76\u4e0d\u60f3\u8981\u4ed6\u6b7b\uff0c\u53ea\u60f3\u51fb\u8d25\u4ed6\uff0c\u771f\u6b63\u51fb\u8d25\u4ed6\uff0c\u5f7b\u5e95\u51fb\u8d25\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u770b\u770b\u4ed6\u7684\u624b\uff1a\u201c\u4f60\u8fd8\u6709\u673a\u4f1a\u80fd\u51fb\u8d25\u4ed6\uff1f\u201d \u3000\u3000\u72ec\u5b64\u75f4\u7684\u56de\u7b54\u5145\u6ee1\u51b3\u5fc3\u4e0e\u81ea\u4fe1\u3002 \u3000\u3000\u201c\u6211\u4e00\u5b9a\u8981\u51fb\u8d25\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u660e\u767d\uff0c\u5c31\u56e0\u4e3a\u4ed6\u8fd8\u6709\u8fd9\u79cd\u51b3\u5fc3\u4e0e\u81ea\u4fe1\uff0c\u6240\u4ee5\u8fd8\u80fd\u4fdd\u6301\u51b7\u9759\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u53c8\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u4e00\u5b9a\u8981\u51fb\u8d25\u4ed6\uff0c\u6240\u4ee5\u624d\u627e\u4f60\u6765\u3002\u6211\u6ca1\u6709\u522b\u5165\u53ef\u627e\uff0c\u53ea\u6709\u627e\u4f60\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u5c0f\u65b9\uff1a\u201c\u8fd9\u662f\u4f60\u6211\u4e4b\u95f4\u7684\u79d8\u5bc6\uff0c\u4f60\u7edd\u4e0d\u80fd\u6cc4\u9732\u6211\u7684\u79d8\u5bc6\uff0c\u5360\u5219\u6211\u5fc5\u6b7b\u3002\u201d \u3000\u3000\u201c\u4f60\u5fc5\u6b7b\uff1f\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u4f60\u8ba4\u4e3a\u535c\u9e70\u4f1a\u6765\u6740\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u535c\u9e70\uff0c\u662f\u536b\u5927\u9e4f\u4ed6\u4eec\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u770b\u770b\u81ea\u5df1\u7684\u624b\uff1a\u201c\u4ed6\u4eec\u90fd\u8ba4\u4e3a\u6211\u662f\u4e2a\u65e0\u7528\u7684\u5e9f\u4eba\uff0c\u53ea\u8981\u77e5\u9053\u6211\u7684\u4e0b\u843d\uff0c\u5c31\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6211\u7684\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u7684\u79d8\u5bc6\u5927\u591a\u4e86\uff0c\u800c\u4e14\u4ece\u672a\u5c06\u4ed6\u4eec\u770b\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u4eec\u6068\u4f60\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u6211\u770b\u5f97\u51fa\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u6068\u4f60\uff0c\u53c8\u6068\u53c8\u6015\uff0c\u73b0\u5728\u4f60\u5df2\u7ecf\u6ca1\u6709\u8ba9\u4ed6\u4eec\u5bb3\u6015\u7684\u5730\u65b9\uff0c\u4ed6\u4eec\u5f53\u7136\u8981\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u627e\u4f60\u6765\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u6211\u5e0c\u671b\u4f60\u80fd\u66ff\u6211\u505a\u4e24\u4ef6\u4e8b\uff0c\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u6211\u9700\u8981\u7528\u94b1\uff0c\u6211\u8981\u4f60\u6bcf\u9694\u5341\u5929\u66ff\u6211\u9001\u4e8c\u767e\u4e24\u94f6\u5b50\u6765\uff0c\u6765\u7684\u65f6\u5019\u7edd\u4e0d\u80fd\u88ab\u4efb\u4f55\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u5e76\u6ca1\u6709\u8bf4\u51fa\u4ed6\u4e3a\u4ec0\u4e48\u7528\u8fd9\u4e48\u591a\u94f6\u5b50\uff0c\u5c0f\u65b9\u4e5f\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u201c\u6211\u8fd8\u8981\u4f60\u53bb\u66ff\u6211\u6740\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u8981\u5c0f\u65b9\u53bb\u66ff\u4ed6\u6740\u4eba\uff01 \u3000\u3000\u201c\u6211\u4eec\u4e0d\u662f\u670b\u53cb\u3002\u8eab\u4e3a\u5251\u5ba2\uff0c\u4e0d\u4f46\u65e0\u60c5\u65e0\u540d\u65e0\u6cea\uff0c\u4e5f\u6ca1\u6709\u670b\u53cb\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u6211\u4eec\u5929\u751f\u5c31\u662f\u4ec7\u654c\uff0c\u56e0\u4e3a\u4f60\u4e5f\u5b66\u5251\uff0c\u6211\u4e5f\u60f3\u51fb\u8d25\u4f60\uff0c\u4e0d\u7ba1\u4f60\u66ff\u6211\u505a\u8fc7\u4ec0\u4e48\u4e8b\u3002\u6211\u8fd8\u662f\u8981\u51fb\u8d25\u4f60\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\uff0c\u5728\u6211\u7684\u5251\u4e0b\uff0c\u8d25\u5c31\u662f\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4f60\u53ef\u4ee5\u62d2\u7edd\u6211\uff0c\u6211\u7edd\u4e0d\u6068\u4f60\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u6211\u8981\u4f60\u505a\u7684\u4e8b\u5e76\u4e0d\u6613\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4ef6\u4e8b\u7684\u786e\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u6bcf\u9694\u5341\u5929\u9001\u4e09\u767e\u4e24\u94f6\u5b50\uff0c\u8fd9\u6570\u76ee\u5e76\u4e0d\u5c0f\uff0c\u5c0f\u65b9\u5e76\u4e0d\u662f\u6709\u94b1\u4eba\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u73b0\u5728\u4ed6\u6839\u672c\u5df2\u56ca\u7a7a\u5982\u6d17\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u4e0d\u662f\u4e2a\u613f\u610f\u6740\u4eba\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u5e94\u8be5\u62d2\u7edd\u72ec\u5b64\u75f4\u7684\uff0c\u4ed6\u4eec\u6839\u672c\u4e0d\u662f\u670b\u53cb\uff0c\u662f\u4ec7\u654c\u3002 \u3000\u3000\u4ed6\u5f88\u53ef\u80fd\u4f1a\u6b7b\u5728\u72ec\u5b64\u75f4\u7684\u5251\u4e0b\u3002\u4ed6\u4eec\u521d\u89c1\u65f6\u4ed6\u5c31\u5df2\u6709\u8fc7\u8fd9\u79cd\u4e0d\u8be6\u7684\u9884\u611f\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u65e0\u6cd5\u62d2\u7edd\u4ed6\u3002 \u3000\u3000\u4ed6\u65e0\u6cd5\u62d2\u7edd\u4e00\u4e2a\u5728\u771f\u6b63\u5371\u96be\u65f6\u8fd8\u80fd\u5b8c\u5168\u4fe1\u4efb\u4ed6\u7684\u4ec7\u654c\u3002 \u3000\u3000\u201c\u6211\u53ef\u4ee5\u7b54\u5e94\u4f60\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u53ea\u4e0d\u8fc7\u6709\u4e24\u4ef6\u4e8b\u6211\u4e00\u5b9a\u8981\u5148\u95ee\u6e05\u695a\u3002\u201d \u3000\u3000\u4ed6\u8981\u95ee\u7684\u7b2c\u4e00\u4ef6\u4e8b\u662f\uff1a\u201c\u4f60\u786e\u4fe1\u522b\u4eba\u4e0d\u4f1a\u627e\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u8fd9\u5730\u65b9\u867d\u7136\u9690\u79d8\uff0c\u5e76\u4e0d\u662f\u4eba\u8ff9\u96be\u81f3\u7684\u5730\u65b9\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u56de\u7b54\u5374\u5f88\u80af\u5b9a\uff1a\u201c\u8fd9\u5730\u65b9\u4ee5\u524d\u7684\u4e3b\u4eba\u662f\u4f4d\u9690\u58eb\uff0c\u4e5f\u662f\u4f4d\u5251\u5ba2\uff0c\u4ed6\u7684\u65cf\u4eba\u4eec\u90fd\u5341\u5206\u5c0a\u656c\u4ed6\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u6765\u6253\u6270\u8fc7\u4ed6\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff1a\u201c\u66f4\u6ca1\u6709\u4eba\u60f3\u5f97\u5230\u6211\u4f1a\u627e\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u90a3\u4f4d\u9690\u58eb\u5251\u5ba2\u5c31\u662f\u6b7b\u5728\u6211\u5251\u4e0b\u7684\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u4e24\u4e2a\u6708\u524d\uff0c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u5c06\u4ed6\u523a\u6740\u4e8e\u5916\u9762\u7684\u53e4\u6811\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u6162\u6162\u5410\u51fa\uff0c\u7136\u540e\u624d\u95ee\uff1a\u201c\u90a3\u4e2a\u5b69\u5b50\u662f\u4e0d\u662f\u4ed6\u7684\u513f\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u6740\u4e86\u4ed6\u7236\u4eb2\uff0c\u5374\u8eb2\u5230\u8fd9\u91cc\u6765\uff0c\u8981\u4ed6\u6536\u5bb9\u4f60\uff0c\u4e3a\u4f60\u4fdd\u5b88\u79d8\u5bc6\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4ed6\u4e00\u5b9a\u4f1a\u4e3a\u6211\u4fdd\u5b88\u79d8\u5bc6\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u56e0\u4e3a\u4ed6\u8981\u590d\u4ec7\uff0c\u5c31\u7edd\u4e0d\u80fd\u8ba9\u6211\u6b7b\u5728\u522b\u4eba\u7684\u624b\u91cc\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4e5f\u53ea\u6709\u6211\u80fd\u4f20\u6388\u4ed6\u53ef\u4ee5\u51fb\u8d25\u6211\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u201c\u4f60\u80af\u5c06\u8fd9\u79cd\u5251\u6cd5\u4f20\u6388\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u7b54\u5e94\u4e86\u4ed6\u3002\u201d\u72ec\u5b64\u75f4\u6de1\u6de1\u4ed6\u8bf4\uff0c\u201c\u6211\u5e0c\u671b\u4ed6\u80fd\u4e3a\u4ed6\u7684\u7236\u4eb2\u590d\u4ec7\uff0c\u4e5f\u5c06\u6211\u540c\u6837\u523a\u6740\u4e8e\u4ed6\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u6307\u5c16\u51b0\u51b7\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u4e0d\u80fd\u4e86\u89e3\u8fd9\u79cd\u60c5\u611f\uff0c\u4eba\u6027\u4e2d\u672c\u6765\u5c31\u5145\u6ee1\u4e86\u5f88\u591a\u79cd\u5c16\u9510\u75db\u82e6\u7684\u77db\u76fe\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4e86\u89e3\uff0c\u6240\u4ee5\u624d\u89c9\u5f97\u53ef\u6015\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u4e00\u5b9a\u4f1a\u9075\u5b88\u8bfa\u8a00\uff0c\u90a3\u4e2a\u5b69\u5b50\u5c06\u6765\u5f88\u53ef\u80fd\u53d8\u6210\u6bd4\u4ed6\u66f4\u65e0\u60c5\u7684\u5251\u5ba2\uff0c\u8fdf\u65e9\u603b\u6709\u4e00\u5929\u4f1a\u6740\u4e86\u72ec\u5b64\u75f4\uff0c\u7136\u540e\u518d\u7b49\u7740\u53e6\u4e00\u4e2a\u65e0\u60c5\u7684\u5251\u5ba2\u6765\u523a\u6740\u4ed6\u3002 \u3000\u3000\u5bf9\u4ed6\u4eec\u8fd9\u79cd\u4eba\u6765\u8bf4\uff0c\u751f\u547d\u7edd\u4e0d\u662f\u6700\u91cd\u8981\u7684\uff0c\u65e0\u8bba\u662f\u522b\u4eba\u7684\u751f\u547d\u8fd8\u662f\u4ed6\u4eec\u81ea\u5df1\u7684\u90fd\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u6d3b\u8fc7\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u5b8c\u6210\u4e00\u4ef6\u4e8b\uff0c\u8fbe\u5230\u4e00\u4e2a\u76ee\u5730\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4efb\u4f55\u4e8b\u4ed6\u4eec\u90fd\u7edd\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u95e8\u5916\u9633\u5149\u904d\u5730\uff0c\u5c4b\u6a90\u4e0b\u9e1f\u8bed\u5541\u557e\u3002\u751f\u547d\u672c\u6765\u5982\u6b64\u7f8e\u597d\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u6709\u4eba\u8981\u5bf9\u5b83\u5982\u6b64\u8f7b\u8d31\uff1f \u3000\u3000\u5c0f\u65b9\u6162\u6162\u5730\u7ad9\u8d77\u6765\uff0c\u73b0\u5728\u4ed6\u53ea\u6709\u6700\u540e\u4e00\u4ef6\u4e8b\u8981\u95ee\u4e86\uff1a\u4e00\u4ef6\u4e8b\uff0c\u4e24\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6211\u66ff\u4f60\u53bb\u6740\u4eba\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u4f60\u8981\u6211\u53bb\u6740\u8c01\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u82e5\u4e0d\u5148\u6b7b\uff0c\u6211\u5c31\u6c38\u8fdc\u65e0\u6cd5\u505a\u5230\u6211\u60f3\u505a\u5230\u7684\u4e8b\u3002\u201d\u72ec\u5b64\u75f4\u5148\u56de\u7b54\u524d\u9762\u4e00\u4e2a\u95ee\u9898\uff0c\u201c\u53ea\u6709\u535c\u9e70\u80fd\u634f\u788e\u6211\u63e1\u5251\u7684\u624b\uff0c\u8fd9\u4e2a\u4eba\u5374\u80fd\u6298\u65ad\u6211\u5fc3\u4e2d\u7684\u5251\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u672c\u65e0\u5251\uff0c\u5982\u679c\u5251\u5df2\u5728\u5fc3\u4e2d\uff0c\u8fd8\u6709\u8c01\u80fd\u6298\u65ad\uff1f \u3000\u3000\u8981\u6298\u65ad\u4eba\u7684\u5fc3\u5251\uff0c\u5fc5\u5b9a\u5148\u8981\u8ba9\u90a3\u4e2a\u4eba\u5fc3\u788e\uff0c\u65e0\u60c5\u65e0\u540d\u65e0\u6cea\u7684\u5251\u5ba2\u5fc3\u600e\u4e48\u4f1a\u5fc3\u788e\uff1f \u3000\u3000\u72ec\u5b64\u75f4\u51b7\u6f20\u7684\u53cc\u773c\u4e2d\uff0c\u5ffd\u7136\u8d77\u4e86\u79cd\u6781\u5947\u5f02\u7684\u53d8\u5316\uff0c\u5c31\u50cf\u662f\u4e00\u67c4\u5df2\u6740\u4eba\u65e0\u7b97\u7684\u5229\u5668\uff0c\u5ffd\u7136\u53c8\u88ab\u6295\u5165\u94f8\u9020\u5b83\u7684\u6d2a\u7089\u4e2d\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u773c\u4e2d\u4f1a\u73b0\u51fa\u5982\u6b64\u5f3a\u70c8\u75db\u82e6\u70bd\u70c8\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u5979\u662f\u4e2a\u5973\u4eba\uff0c\u662f\u4e2a\u9b54\u5973\uff0c\u6211\u53ea\u8981\u4e00\u89c1\u5230\u5979\u5c31\u5b8c\u5168\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u3002\u867d\u7136\u6211\u660e\u77e5\u5979\u662f\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u5374\u8fd8\u662f\u65e0\u6cd5\u6446\u8131\u5979\u3002\u5979\u82e5\u4e0d\u6b7b\uff0c\u6211\u7ec8\u751f\u90e8\u8981\u53d7\u5979\u7684\u6298\u78e8\u5974\u5f79\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u95ee\u8fd9\u4e2a\u5973\u4eba\u662f\u8c01\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u95ee\u3002\u4ed6\u5185\u5fc3\u6df1\u5904\u5ffd\u7136\u6709\u4e86\u79cd\u4ee4\u4ed6\u81ea\u5df1\u90fd\u6015\u5f97\u8981\u547d\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u53e4\u5bfa\u5e7d\u706b\u95ea\u52a8\u7167\u8000\u4e0b\u7684\u90a3\u5e45\u58c1\u753b\u4e0a\uff0c\u90a3\u4e2a\u542e\u5438\u4eba\u8111\u7684\u7f57\u5239\u9b3c\u5973\uff0c\u90a3\u5f20\u72f0\u72de\u4e11\u6076\u7684\u8138\uff0c\u4eff\u4f5b\u5ffd\u7136\u53d8\u6210\u4e86\u53e6\u4e00\u4e2a\u5973\u4eba\u7684\u8138\u3002 \u3000\u3000\u4e00\u5f20\u7eaf\u6d01\u7f8e\u4e3d\u7684\u8138\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u53c8\u5f00\u59cb\u63a5\u7740\u8bf4\u4e86\u4e0b\u53bb\uff1a\u201c\u6211\u77e5\u9053\u5979\u4e00\u5b9a\u4e5f\u53c8\u5230\u4e86\u62c9\u8428\uff0c\u56e0\u4e3a\u5979\u7edd\u4e0d\u4f1a\u653e\u8fc7\u535c\u9e70\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u542c\u5f97\u89c1\u81ea\u5df1\u7684\u58f0\u97f3\u5728\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u535c\u9e70\u5c31\u662f\u732b\u76d7\uff0c\u7edd\u5bf9\u662f\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u4f1a\u8ddf\u7740\u535c\u9e70\u5230\u62c9\u8428\u6765\uff0c\u5979\u5728\u62c9\u8428\u4e5f\u6709\u4e2a\u79d8\u5bc6\u7684\u5730\u65b9\u85cf\u8eab\u3002\u201d \u3000\u3000\u201c\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u201c\u5c31\u5728\u5e03\u8fbe\u62c9\u5bab\u7684\u4e2d\u5fc3\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u907f\u5bd2\u7684\u2018\u7ea2\u5bab\u2019\u65c1\uff0c\u4e00\u95f4\u5c0f\u5c0f\u7684\u7985\u623f\u91cc\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u9053\uff1a\u201c\u53ea\u6709\u5979\u80fd\u6df1\u5165\u5e03\u8fbe\u62c9\u5bab\u7684\u4e2d\u5fc3\uff0c\u56e0\u4e3a\u5587\u561b\u4eec\u4e5f\u662f\u7537\u4eba\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u7537\u4eba\u80fd\u62d2\u7edd\u5979\u7684\u8981\u6c42\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u518d\u542c\uff0c\u4e0d\u60f3\u542c\u72ec\u5b64\u75f4\u8bf4\u51fa\u8fd9\u4e2a\u5973\u4eba\u7684\u540d\u5b57\u3002 \u3000\u3000\u53ef\u662f\u72ec\u5b64\u75f4\u5df2\u7ecf\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u5979\u7684\u540d\u5b57\u53eb\u6ce2\u5a03\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u4e2d\u5145\u6ee1\u75db\u82e6\uff0c\u201c\u4f60\u65e2\u7136\u5df1\u7b54\u5e94\u4e86\u6211\uff0c\u73b0\u5728\u5c31\u5f97\u53bb\u66ff\u6211\u6740\u4e86\u5979\u3002\u201d \u3000\u3000\u95e8\u5916\u4f9d\u65e7\u6709\u9633\u5149\u904d\u5730\uff0c\u5c4b\u6a90\u4e0b\u4f9d\u65e7\u6709\u9e1f\u8bed\u5541\u557e\uff0c\u53ef\u662f\u751f\u547d\u5462\uff1f \u3000\u3000\u751f\u547d\u662f\u5426\u771f\u7684\u5982\u6b64\u7f8e\u597d\uff1f\u751f\u547d\u4e2d\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u6709\u8fd9\u4e48\u591a\u8c01\u90e8\u65e0\u6cd5\u907f\u514d\u7684\u75db\u82e6\u4e0e\u77db\u76fe\uff1f \u3000\u3000\u5c0f\u65b9\u6162\u6162\u5730\u8d70\u51fa\u6765\u3002\u90a3\u5b69\u5b50\u4ecd\u7136\u7ad9\u5728\u5c4b\u6a90\u4e0b\uff0c\u75f4\u75f4\u5730\u770b\u80b2\u4e00\u4e2a\u9e1f\u7b3c\u3001\u4e00\u53ea\u9e1f\uff0c\u4e5f\u4e0d\u77e5\u662f\u5c71\u96c0\uff0c\u8fd8\u662f\u753b\u7709\uff1f \u3000\u3000\u201c\u5b83\u662f\u6211\u7684\u670b\u53cb\u3002\u201d\u5b69\u5b50\u6ca1\u6709\u56de\u5934\u770b\u5c0f\u65b9\uff0c\u8fd9\u53e5\u8bdd\u5374\u65e0\u7591\u662f\u5bf9\u5c0f\u65b9\u8bf4\u7684\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u5b83\u4eec\u90fd\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5c0f\u5b69\u5ffd\u7136\u53f9\u606f\uff0c\u4e00\u53cc\u9ed1\u767d\u5206\u660e\u7684\u773c\u775b\u91cc\u5ffd\u7136\u5145\u6ee1\u6210\u4eba\u7684\u5fe7\u90c1\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u5bf9\u4e0d\u8d77\u5b83\u4eec\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u5b83\u4eec\u8fdf\u65e9\u603b\u6709\u4e00\u5929\u4f1a\u5168\u90fd\u6b7b\u5728\u72ec\u5b64\u75f4\u7684\u5251\u4e0b\u3002\u201d\u5c0f\u5b69\u8f7b\u8f7b\u4ed6\u8bf4\uff1a\u201c\u53ea\u8981\u7b49\u5230\u4ed6\u7684\u624b\u53ef\u4ee5\u63e1\u5251\u65f6\uff0c\u5c31\u4e00\u5b9a\u4f1a\u7528\u5b83\u4eec\u6765\u8bd5\u5251\u7684\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u6211\u7236\u4eb2\u8981\u6211\u517b\u8fd9\u4e9b\u9e1f\uff0c\u4e5f\u662f\u4e3a\u4e86\u8981\u7528\u5b83\u4eec\u6765\u8bd5\u5251\u7684\u3002\u201d\u5c0f\u5b69\u9053\uff0c\u201c\u6709\u4e00\u6b21\u4ed6\u66fe\u7ecf\u4e00\u5251\u65a9\u6740\u4e86\u5341\u4e09\u53ea\u98de\u9e1f\u3002\u90a3\u4e00\u4e00\u5929\u7684\u665a\u4e0a\uff0c\u4ed6\u5c31\u6b7b\u5728\u72ec\u5b64\u75f4\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u662f\u4e2a\u5b69\u5b50\uff0c\u53ef\u662f\u4ed6\u7684\u58f0\u97f3\u91cc\u5374\u5df2\u6709\u4e86\u4e00\u79cd\u65e0\u53ef\u5948\u4f55\u7684\u60b2\u4f24\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u5df2\u4e86\u89e3\uff0c\u6b7b\uff0c\u672c\u6765\u5c31\u662f\u6240\u6709\u4e00\u5207\u4e8b\u7684\u7ec8\u7ed3\uff1f \u3000\u3000\u5dc5\u5cf0\u5f80\u5f80\u5c31\u662f\u7ec8\u70b9\uff0c\u4e00\u4e2a\u5251\u5ba2\u5230\u4e86\u4ed6\u7684\u5dc5\u5cf0\u65f6\uff0c\u4ed6\u7684\u751f\u547d\u5f80\u5f80\u4e5f\u5230\u4e86\u7ec8\u7ed3\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u5e78\u8fd0\uff0c\u8fd8\u662f\u4ed6\u7684\u4e0d\u5e78\uff1f \u3000\u3000\u98ce\u5728\u6811\u68a2\uff0c\u4eba\u5728\u6811\u4e0b\u3002 \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u4ed6\u8bf4\uff1a\u201c\u5b83\u4eec\u867d\u7136\u662f\u4f60\u7684\u670b\u53cb\uff0c\u53ef\u662f\u4f60\u8bf4\u4e0d\u5b9a\u4e5f\u6709\u4e00\u5929\u4f1a\u7528\u5b83\u4eec\u6765\u8bd5\u5251\u7684\u3002\u201d \u3000\u3000\u5c0f\u5b69\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5c45\u7136\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff1a\u201c\u4e0d\u9519\uff0c\u8bf4\u4e0d\u5b9a\u6211\u4e5f\u4f1a\u7528\u5b83\u4eec\u6765\u8bd5\u5251\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u4eb2\u773c\u770b\u89c1\u4ed6\u6740\u4e86\u4f60\u7236\u4eb2\uff0c\u660e\u77e5\u4ed6\u8981\u6740\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u5374\u8fd8\u662f\u6536\u5bb9\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u5b69\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e5f\u60f3\u505a\u4ed6\u4eec\u90a3\u6837\u7684\u5251\u5ba2\u3002\u3001 \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u603b\u6709\u4e00\u5929\uff0c\u4f60\u4e00\u5b9a\u4e5f\u4f1a\u6210\u4e3a\u4ed6\u4eec\u90a3\u6837\u7684\u5251\u5ba2\u3002\u201d \u3000\u3000\u5c0f\u5b69\u5ffd\u7136\u56de\u8fc7\u5934\uff0c\u76ef\u7740\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u5df2\u8d70\u51fa\u4e86\u53e4\u6811\u7684\u6d53\u836b\uff0c\u8d70\u5230\u9633\u5149\u4e0b\uff0c\u4ed6\u4e00\u4e00\u76f4\u5f80\u524d\u8d70\uff0c\u4e00\u76f4\u6ca1\u6709\u56de\u5934\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u65e0\u6cd5\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u5927\u62db\u5bfa\u5916\u7684\u516b\u89d2\u8857\u4e0a\uff0c\u6709\u5404\u5f0f\u5404\u6837\u7684\u5e97\u94fa\u3002 \u3000\u3000\u4e45\u5df2\u88ab\u6cb9\u70df\u718f\u9ed1\u7684\u9634\u9ed1\u7684\u5e97\u94fa\u91cc\uff0c\u6709\u6765\u81ea\u56db\u65b9\u3001\u5404\u5f0f\u5404\u6837\u7684\u8d27\u7269\u3002 \u3000\u3000\u8c79\u76ae\u3001\u864e\u76ae\u3001\u9ed1\u8c82\u76ae\u3001\u5c71\u732b\u76ae\uff0c\u5404\u79cd\u989c\u8272\u7684\u201c\u5361\u5951\u201d\u548c\u4e1d\u7f0e\uff0c\u9ad8\u6302\u5728\u8d27\u67b6\u4e0a\uff0c\u6765\u81ea\u6ce2\u65af\u3001\u5929\u7afa\u7684\u5e03\u5339\u548c\u5730\u6bef\uff0c\u94fa\u6ee1\u67dc\u53f0\u3002 \u3000\u3000\u4ece\u6253\u7bad\u7089\u6765\u7684\u8336\u7816\u5806\u79ef\u5982\u5c71\uff0c\u4ece\u85cf\u4e1c\u6765\u7684\u9e9d\u9999\uff0c\u4ece\u5c3c\u6cca\u5c14\u6765\u7684\u9999\u6599\u3001\u84dd\u975b\u3001\u73ca\u745a\u3001\u73cd\u73e0\u3001\u94dc\u5668\uff0c\u4ece\u4e2d\u4e0a\u6765\u7684\u74f7\u5668\u3001\u73ca\u745a\u3001\u739b\u76fe\u3001\u523a\u7ee3\u3001\u5927\u7c73\uff0c\u4ece\u8499\u53e4\u6765\u7684\u76ae\u8d27\u548c\u978d\u8d27\uff0c\u6362\u8d70\u4e86\u5404\u79cd\u6b64\u5730\u7684\u540d\u4ea7\uff0c\u6362\u6765\u4e86\u85cf\u4eba\u7684\u5bcc\u8db3\u3002 \u3000\u3000\u201c\u9e70\u8bb0\u201d\u65e0\u7591\u662f\u6240\u6709\u5546\u53f7\u4e2d\u6700\u5927\u7684\u4e00\u5bb6\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\u5c31\u662f\u732b\u76d7\uff1f\u7edd\u5bf9\u662f\u3002 \u3000\u3000\u6ce2\u5a03\u662f\u4e2a\u9b54\u5973\uff01\u4ece\u6ca1\u6709\u4efb\u4f55\u7537\u4eba\u80fd\u62d2\u7edd\u5979\uff01 \u3000\u3000\u2014\u2014\u4f60\u65e2\u7136\u5df2\u7b54\u5e94\u6211\uff0c\u73b0\u5728\u5e94\u8be5\u53bb\u66ff\u6211\u6740\u4e86\u5979\uff01 \u3000\u3000\u5c0f\u65b9\u4ec0\u4e48\u90fd\u6ca1\u6709\u60f3\u3002 \u3000\u3000\u4ed6\u65e2\u4e0d\u80fd\u53bb\u95ee\u535c\u9e70\uff0c\u4e5f\u4e0d\u77e5\u9053\u5e94\u8be5\u7528\u4ec0\u4e48\u65b9\u6cd5\u624d\u80fd\u63a5\u8fd1\u5e03\u8fbe\u62c9\u5bab\u7684\u4e2d\u5fc3\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u90a3\u6240\u907f\u5bd2\u7684\u7ea2\u5bab\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u5148\u56de\u5230\u201c\u9e70\u8bb0\u201d\uff0c\u4ed6\u60f3\u95ee\u6731\u4e91\u501f\u4e09\u767e\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u6731\u4e91\u4e00\u5b9a\u4e0d\u4f1a\u62d2\u7edd\u3002 \u3000\u3000\u4f46\u662f\u6731\u4e91\u8fd8\u6ca1\u6709\u7b49\u5230\u4ed6\u5f00\u53e3\uff0c\u5c31\u5148\u544a\u8bc9\u4ed6\uff1a\u201c\u6709\u4eba\u5728\u7b49\u4f60\uff0c\u5df2\u7ecf\u7b49\u4e86\u5f88\u4e45\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u5c31\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5f88\u5e74\u8f7b\u7684\u4eba\uff0c\u8138\u8272\u770b\u6765\u867d\u7136\u6709\u4e9b\u6194\u6dec\uff0c\u53ef\u662f\u670d\u9970\u534e\u4e3d\u5c0a\u8d35\uff0c\u6001\u5ea6\u5e84\u91cd\u6c89\u7740\uff0c\u5728\u4ed6\u7684\u65cf\u4eba\u4e2d\uff0c\u4ed6\u7684\u5730\u4f4d\u65e0\u7591\u8981\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u9ad8\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u662f\u85cf\u4eba\uff0c\u8bf4\u7684\u6c49\u8bed\u8270\u6da9\u800c\u751f\u786c\uff0c\u5c0f\u65b9\u8bf4\u4e00\u53e5\uff0c\u4ed6\u624d\u8bf4\u4e00\u53e5\u3002 \u3000\u3000\u201c\u6211\u59d3\u65b9\uff0c\u6211\u5c31\u662f\u5c0f\u65b9\u3002\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u6765\u627e\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4e0d\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u8ba4\u5f97\u4f60\u3002\u201d\u8fd9\u4eba\u76ef\u7740\u5c0f\u65b9\uff0c\u201c\u4f60\u4e5f\u4e0d\u8ba4\u5f97\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u4f60\u6765\u627e\u6211\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u5ffd\u7136\u7ad9\u8d77\uff0c\u8d70\u51fa\u4e86\u201c\u9e70\u8bb0\u201d\uff0c\u8d70\u51fa\u540e\u95e8\u624d\u56de\u5934\uff1a\u201c\u4f60\u60f3\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u627e\u4f60\uff0c\u4f60\u5c31\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u4ed6\u7ad9\u8d77\u6765\u4e4b\u540e\uff0c\u5c0f\u65b9\u624d\u53d1\u89c9\u4ed6\u7684\u8eab\u6750\u5f88\u9ad8\u5927\uff0c\u6bd4\u4e00\u822c\u4eba\u90fd\u9ad8\u5f97\u591a\u3002 \u3000\u3000\u5916\u9762\u5c31\u662f\u62c9\u8428\u6700\u7e41\u8363\u7684\u8857\u9053\uff0c\u6324\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u884c\u4eba\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u8857\u9053\u4eba\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u4ed9\u9e64\u8d70\u5165\u4e86\u9e21\u7fa4\u3002\u6709\u5f88\u591a\u4eba\u770b\u89c1\u4e86\u4ed6\uff0c\u8138\u4e0a\u90fd\u7acb\u523b\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u5411\u4ed6\u606d\u656c\u884c\u793c\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u751a\u81f3\u7acb\u523b\u5c31\u8dea\u4e0b\u53bb\u543b\u4ed6\u7684\u811a\u3002 \u3000\u3000\u4ed6\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\uff0c\u663e\u7136\u4e45\u5df2\u4e60\u60ef\u63a5\u53d7\u522b\u4eba\u5bf9\u4ed6\u7684\u5d07\u62dc\u5c0a\u656c\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\uff1f \u3000\u3000\u5c0f\u65b9\u8ddf\u7740\u4ed6\u8d70\u4e86\u51fa\u6765\uff0c\u521a\u8d70\u5230\u4e00\u5bb6\u8d29\u5356\u201c\u9165\u6cb9\u201d\u548c\u201c\u8471\u6ce5\u201d\u7684\u98df\u7269\u5e97\u94fa\u5916\uff0c\u521a\u55c5\u5230\u90a3\u79cd\u4e5f\u4e0d\u77e5\u662f\u9999\u662f\u81ed\uff0c\u5374\u7edd\u5bf9\u80fd\u5f15\u8d77\u4eba\u4eec\u98df\u6b32\u7684\u5f02\u5473\u65f6\uff0c\u5c31\u5df2\u7ecf\u6709\u4e8c\u4e09\u5341\u4ef6\u81f4\u547d\u7684\u6697\u5668\u6253\u5411\u4ed6\u7684\u8981\u5bb3\uff01 \u3000\u3000\u662f\u4e8c\u5341\u516d\u4ef6\u6697\u5668\uff0c\u542c\u8d77\u6765\u5374\u53ea\u6709\u4e00\u9053\u98ce\u58f0\uff0c\u770b\u8d77\u6765\u4e5f\u53ea\u6709\u4e09\u9053\u5149\u8292\u3002 \u3000\u3000\u4e8c\u5341\u516d\u4ef6\u6697\u5668\uff0c\u5206\u522b\u6253\u5411\u5c0f\u65b9\u4e09\u5904\u8981\u5bb3\u2014\u2014\u54bd\u5589\uff0c\u5fc3\u53e3\uff0c\u80be\u56ca\u3002 \u3000\u3000\u6697\u5668\u6b79\u6bd2\uff0c\u51fa\u624b\u66f4\u6b79\u6bd2\u3002 \u3000\u3000\u4e8c\u5341\u4e03\u4ef6\u6697\u5668\uff0c\u7edd\u5bf9\u662f\u4ece\u540c\u4e00\u4e2a\u65b9\u5411\u6253\u8fc7\u6765\u7684\uff0c\u5c31\u662f\u4ece\u8d70\u5728\u5c0f\u65b9\u9762\u524d\uff0c\u90a3\u4e2a\u88c5\u9970\u534e\u8d35\u800c\u4e14\u975e\u5e38\u53d7\u4eba\u5c0a\u656c\u7684\u5e74\u8f7b\u4eba\u624b\u91cc\u6253\u51fa\u6765\u7684\u3002 \u3000\u3000\u8fd9\u4e48\u6837\u9ad8\u5c1a\u5c0a\u8d35\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u7528\u5982\u6b64\u9634\u72e0\u6b79\u6bd2\u7684\u65b9\u6cd5\u6697\u7b97\u4e00\u4e2a\u7d20\u4e0d\u76f8\u8bc6\u7684\u964c\u751f\u4eba\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u95ee\uff0c\u4e5f\u6ca1\u6709\u88ab\u6253\u5012\u3002 \u3000\u3000\u4ed6\u7ecf\u5386\u8fc7\u7684\u51f6\u9669\u3001\u6697\u5668\u5df2\u591f\u591a\uff0c\u4ed6\u968f\u65f6\u90fd\u5728\u4fdd\u6301\u7740\u8b66\u89c9\u3002 \u3000\u3000\u6697\u5668\u6253\u6765\u65f6\uff0c\u4ed6\u5df2\u626f\u4e0b\u521a\u624d\u8d70\u8fc7\u7684\u4e00\u5bb6\u5e97\u94fa\u95e8\u5916\u6302\u7740\u7684\u4e00\u6761\u6ce2\u65af\u6bdb\u6be1\u3002 \u3000\u3000\u4e8c\u5341\u516d\u4ef6\u6697\u5668\uff0c\u5168\u90fd\u6253\u5728\u8fd9\u6761\u624b\u5de5\u7cbe\u7ec6\u3001\u7ec7\u6cd5\u7d27\u5bc6\u7684\u6bdb\u6be1\u4e0a\uff0c\u6ca1\u6709\u4e00\u4ef6\u6697\u5668\u80fd\u7a7f\u900f\u6bdb\u6be1\u3002 \u3000\u3000\u8d70\u5728\u5c0f\u65b9\u524d\u9762\u7684\u8fd9\u4e2a\u5e74\u8f7b\u4eba\uff0c\u65e2\u6ca1\u6709\u56de\u5934\uff0c\u4e5f\u6ca1\u6709\u505c\u6b65\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u4e09\u7ae0\u3000\u9ad8\u50e7\u7684\u8d4c\u7ea6 \u7b2c\u5341\u4e09\u7ae0\u3000\u9ad8\u50e7\u7684\u8d4c\u7ea6 \u3000\u3000\u5c0f\u65b9\u4e5f\u4ecd\u7136\u4e0d\u52a8\u58f0\u8272\uff0c\u56de\u8eab\u5c06\u6bdb\u6be1\u6302\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u53c8\u8ddf\u7740\u8fd9\u4e2a\u4eba\u5f80\u524d\u8d70\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7ee7\u7eed\u5f80\u524d\u8d70\uff0c\u597d\u50cf\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u65b9\u5fc3\u91cc\u5e76\u6ca1\u6709\u4ed6\u5916\u8868\u770b\u6765\u90a3\u4e48\u5e73\u9759\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u8fd9\u4e2a\u4eba\u662f\u9ad8\u624b\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4ed6\u5165\u85cf\u4ee5\u6765\u9047\u89c1\u7684\u6700\u53ef\u6015\u7684\u4e00\u4e2a\u5bf9\u624b\uff0c\u751a\u81f3\u6bd4\u536b\u5929\u9e4f\u66f4\u53ef\u6015\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7684\u5200\u867d\u7136\u53ef\u6015\uff0c\u62d4\u5200\u7684\u52a8\u4f5c\u867d\u7136\u8fc5\u901f\u6b63\u786e\uff0c\u53ef\u662f\u4ed6\u5728\u62d4\u5200\u524d\uff0c\u53f3\u80a9\u603b\u662f\u96be\u514d\u8981\u5148\u8038\u8d77\u3002 \u3000\u3000\u4ed6\u7684\u7bad\u867d\u7136\u53ef\u6015\uff0c\u53ef\u662f\u4ed6\u5728\u53d1\u7bad\u4ee5\u524d\uff0c\u4e00\u5b9a\u8981\u5148\u633d\u5f13\u3002 \u3000\u3000\u7eb5\u7136\u662f\u6b66\u6797\u4e2d\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u5728\u4ed6\u4eec\u53d1\u51fa\u81f4\u547d\u7684\u4e00\u51fb\u524d\uff0c\u901a\u5e38\u90fd\u96be\u514d\u4f1a\u6709\u4e9b\u88ab\u4eba\u770b\u51fa\u6765\u7684\u51c6\u5907\u52a8\u4f5c\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5374\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u53d1\u51fa\u90a3\u4e8c\u5341\u516d\u4ef6\u81f4\u547d\u7684\u6697\u5668\u65f6\uff0c\u4ed6\u7684\u5934\u6ca1\u6709\u56de\u8fc7\u6765\uff0c\u80a9\u4e5f\u6ca1\u6709\u52a8\uff0c\u751a\u81f3\u8fde\u624b\u90fd\u6ca1\u6709\u626c\u8d77\u3002 \u3000\u3000\u4ed6\u624b\u81c2\u4e0a\u7684\u9aa8\u8282\u3001\u624b\u8155\u6b62\u7684\u5173\u8282\uff0c\u597d\u50cf\u90fd\u80fd\u591f\u968f\u610f\u5f2f\u66f2\u626d\u52a8\uff0c\u4ece\u4efb\u4f55\u4eba\u90fd\u5f88\u96be\u60f3\u5230\u7684\u90e8\u4f4d\uff0c\u8fd0\u7528\u4efb\u4f55\u4eba\u90fd\u5f88\u96be\u8fd0\u7528\u529b\u91cf\uff0c\u53d1\u51fa\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u5929\u7a7a\u6f84\u84dd\uff0c\u8fdc\u5904\u79ef\u96ea\u7684\u5c71\u5dc5\u5728\u84dd\u5929\u4e0b\u9690\u7ea6\u53ef\u89c1\uff0c\u4ed6\u4eec\u5df2\u8d70\u8fc7\u7e41\u8363\u7684\u8857\u5e02\uff0c\u8d70\u5165\u4e86\u8352\u90ca\u3002 \u3000\u3000\u4ece\u5c0f\u65b9\u73b0\u5728\u7ad9\u7740\u7684\u5730\u65b9\u770b\u8fc7\u53bb\uff0c\u770b\u4e0d\u89c1\u522b\u7684\u4eba\uff0c\u4e5f\u542c\u4e0d\u89c1\u4e00\u70b9\u58f0\u97f3\u3002 \u3000\u3000\u5c0f\u65b9\u552f\u4e00\u80fd\u770b\u89c1\u7684\u4eba\uff0c\u5c31\u662f\u73b0\u5728\u5df2\u505c\u4e0b\u6765\uff0c\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u7740\u4ed6\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u6b63\u5728\u7528\u4e00\u53cc\u5145\u6ee1\u4ec7\u6068\u6028\u6bd2\u7684\u773c\u775b\u76ef\u7740\u4ed6\uff0c\u5bf9\u4e00\u4e2a\u4e92\u76f8\u90fd\u4e0d\u8ba4\u5f97\u7684\u964c\u751f\u4eba\uff0c\u672c\u6765\u7edd\u5bf9\u4e0d\u5e94\u8be5\u6709\u8fd9\u79cd\u773c\u8272\u3002 \u3000\u3000\u201c\u6211\u53eb\u666e\u677e\u3002\u201d\u8fd9\u4e2a\u4eba\u5ffd\u7136\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u5c0f\u65b9\u4ece\u672a\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002 \u3000\u3000\u666e\u677e\u8bf4\u51fa\u7684\u7b2c\u4e8c\u53e5\u8bdd\u66f4\u60ca\u4eba\u3002 \u3000\u3000\u201c\u6211\u6765\u627e\u4f60\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u6b7b\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u6c49\u8bed\u751f\u786c\u51b7\u6da9\uff0c\u53ef\u662f\u8fd9\u4e2a\u201c\u6b7b\u201d\u5b57\u7528\u8fd9\u79cd\u53e3\u97f3\u8bf4\u51fa\u6765\uff0c\u5374\u663e\u5f97\u66f4\u6709\u51b3\u5fc3\uff0c\u66f4\u6709\u529b\u91cf\uff0c\u66f4\u4ee4\u4eba\u60ca\u5fc3\uff0c\u4e5f\u66f4\u53ef\u6015\u3002 \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u77e5\u9053\u4f60\u8981\u6211\u6b7b\uff0c\u521a\u624d\u6211\u5dee\u4e00\u70b9\u5c31\u6b7b\u5728\u4f60\u5343\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u5c31\u662f\u5251\u5ba2\uff0c\u4f60\u5e94\u8be5\u660e\u767d\u3002\u201d\u666e\u677e\u9053\uff0c\u201c\u5251\u5ba2\u8981\u6740\u4eba\uff0c\u53ea\u8981\u80fd\u624b\u6b7b\u90a3\u4e2a\u4eba\u5c31\u597d\uff0c\u968f\u4fbf\u7528\u4ec0\u4e48\u624b\u6bb5\u90fd\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bcd\u53e5\u8bed\u8c03\u90fd\u5f88\u5947\u602a\uff1a\u201c\u4f60\u662f\u5251\u5ba2\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u6740\u4eba\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u88ab\u4eba\u6740\u3002\u4f60\u6740\u4e86\u4eba\uff0c\u4f60\u4e0d\u4f1a\u602a\u4f60\u81ea\u5df1\uff0c\u4f60\u88ab\u4eba\u6740\uff0c\u4e5f\u4e0d\u5e94\u8be5\u602a\u522b\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u5251\u5ba2\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4f46\u662f\u6211\u542c\u4eba\u8bf4\u8fc7\u4f60\uff0c\u4f60\u662f\u4e2d\u571f\u6709\u540d\u7684\u5251\u5ba2\u3002\u201d\u666e\u677e\u7684\u6001\u5ea6\u4e25\u8083\u5e84\u91cd\uff0c\u7edd\u6ca1\u6709\u4e1d\u6beb\u8f7b\u8511\u8ba5\u4fcf\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u8bf4\uff1a\u201c\u4f60\u662f\u5251\u5ba2\uff0c\u5251\u5ba2\u7684\u5251\uff0c\u5c31\u50cf\u662f\u4eba\u7684\u624b\u3002\u6bcf\u4e2a\u4eba\u7684\u624b\u90fd\u5e94\u8be5\u5728\u624b\u4e0a\uff0c\u6bcf\u4e2a\u5251\u5ba2\u7684\u5251\u90e8\u5e94\u8be5\u5728\u8eab\u4e0a\uff0c\u53ef\u662f\u4f60\u6ca1\u6709\u3002\u201d \u3000\u3000\u5251\u5ba2\u7684\u5251\uff0c\u5c31\u50cf\u662f\u4eba\u7684\u624b\u3002 \u3000\u3000\u666e\u677e\u7684\u8bdd\u867d\u7136\u8270\u6da9\u96be\u61c2\uff0c\u4f46\u662f\u8c01\u4e5f\u4e2a\u80fd\u4e0d\u627f\u8ba4\u4ed6\u8bf4\u7684\u5f88\u6709\u9053\u7406\u3002 \u3000\u3000\u201c\u4f60\u7ec3\u7684\u662f\u5251\uff0c\u4f60\u6740\u4eba\u7528\u5251\u3002\u201d\u666e\u677e\u9053\uff0c\u201c\u6211\u4e0d\u7ec3\u5251\uff0c\u6211\u6740\u4eba\u4e0d\u7528\u5251\uff0c\u6211\u7528\u624b\u5c31\u80fd\u6740\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4f38\u51fa\u4e86\u4ed6\u7684\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4f38\u51fa\u6765\u65f6\uff0c\u8fd8\u662f\u4e00\u53ea\u5f88\u666e\u901a\u7684\u624b\uff0c\u5ffd\u7136\u95f4\u4ed6\u7684\u624b\u5fc3\u5c31\u5df2\u53d8\u4e3a\u8d64\u7ea2\uff0c\u7ea2\u5982\u5915\u9633\uff0c\u7ea2\u5982\u9c9c\u8840\uff0c\u7ea2\u5982\u706b\u7130\u3002 \u3000\u3000\u666e\u677e\u6162\u6162\u5730\u63a5\u7740\u8bf4\uff1a\u201c\u6211\u8fd8\u6709\u624b\uff0c\u4f60\u5374\u6ca1\u6709\u5251\uff0c\u6240\u4ee5\u6211\u4e0d\u4f1a\u6b7b\uff0c\u6211\u8981\u4f60\u6b7b\uff01\u201d \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u542c\u8fc7\u4efb\u4f55\u4eba\u80fd\u5c06\u8fd9\u4e2a\u201c\u6b7b\u201d\u5b57\u8bf4\u5f97\u5982\u6b64\u5c16\u9177\u6c89\u90c1\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4ed6\u81ea\u5df1\u5fc3\u91cc\u5df2\u611f\u89c9\u5230\u6b7b\u7684\u9634\u5f71\uff1f \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6740\u5c0f\u65b9\uff1f \u3000\u3000\u662f\u4ed6\u81ea\u5df1\u8981\u6740\u5c0f\u65b9\uff0c\u8fd8\u662f\u522b\u4eba\u6d3e\u6765\u7684\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u6b66\u529f\u548c\u6c14\u8d28\uff0c\u7edd\u4e0d\u53ef\u80fd\u505a\u536b\u5929\u9e4f\u90a3\u4e9b\u4eba\u7684\u5c5e\u4e0b\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u6839\u672c\u672a\u89c1\u8fc7\u5c0f\u65b9\uff0c\u4e5f\u4e0d\u53ef\u80fd\u548c\u5c0f\u65b9\u6709\u4ec0\u4e48\u52bf\u5fc5\u8981\u7528\u201c\u6b7b\u201d\u6765\u89e3\u51b3\u7684\u6069\u6028\u4ec7\u6068\u3002 \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u5c0f\u65b9\u90fd\u60f3\u4e0d\u901a\uff0c\u5c0f\u65b9\u53ea\u770b\u51fa\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u638c\u529b\u96c4\u539a\u90aa\u5f02\uff0c\u5982\u679c\u4e0d\u662f\u4f20\u8bf4\u4e2d\u201c\u5bc6\u5b97\u5927\u624b\u5370\u201d\u90a3\u4e00\u7c7b\u529f\u592b\uff0c\u60f3\u5fc5\u4e5f\u5f88\u63a5\u8fd1\u3002 \u3000\u3000\u8fd9\u79cd\u638c\u529b\u7edd\u4e0d\u662f\u5c0f\u65b9\u80fd\u591f\u7528\u8089\u638c\u62b5\u6297\u7684\u3002 \u3000\u3000\u4ed6\u7684\u5251\u4e0d\u5728\u4ed6\u8eab\u8fb9\uff0c\u56e0\u4e3a\u4ed6\u4ece\u672a\u60f3\u5230\u5728\u8fd9\u964c\u751f\u7684\u5730\u65b9\u4e5f\u6709\u5fc5\u987b\u7528\u5251\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u80fd\u7528\u4ec0\u4e48\u5bf9\u4ed8\u666e\u677e\u8fd9\u4e00\u53cc\u8840\u638c\uff1f \u3000\u3000\u9633\u5149\u666e\u7167\u7684\u5927\u5730\uff0c\u5ffd\u7136\u5145\u6ee1\u6740\u673a\u3002\u5728\u6b7b\u4ea1\u9634\u5f71\u4e0b\uff0c\u8fde\u9633\u5149\u90fd\u53d8\u5f97\u9634\u68ee\u9eef\u6de1\u4e86\u3002 \u3000\u3000\u666e\u677e\u5411\u5c0f\u65b9\u8fdb\u903c\u3002 \u3000\u3000\u4ed6\u7684\u811a\u6b65\u7f13\u6162\u800c\u6c89\u7a33\u3002 \u3000\u3000\u6709\u79cd\u4eba\u53ea\u8981\u4e00\u4e0b\u5b9a\u51b3\u5fc3\u5f00\u59cb\u884c\u52a8\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u8ba9\u4ed6\u505c\u4e0b\u6765\u3002 \u3000\u3000\u666e\u677e\u65e0\u7591\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4ed6\u5df2\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u51b3\u5fc3\u8981\u5c0f\u65b9\u6b7b\u5728\u4ed6\u638c\u4e0b\uff0c\u4ed6\u5fc3\u4e2d\u7684\u9634\u5f71\u53ea\u6709\u201c\u6b7b\u201d\u624d\u80fd\u9a71\u6563\u3002 \u3000\u3000\u5c0f\u65b9\u4e00\u6b65\u6b65\u5411\u540e\u9000\u3002 \u3000\u3000\u4ed6\u65e0\u6cd5\u5bf9\u4ed8\u666e\u677e\u7684\u8fd9\u4e00\u53cc\u624b\u638c\uff0c\u4ed6\u53ea\u6709\u9000\uff0c\u9000\u5230\u65e0\u8def\u53ef\u9000\u65f6\u4e3a\u4e0a\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u65e0\u8def\u53ef\u9000\u3002 \u3000\u3000\u4ed6\u5df2\u9000\u5230\u4e00\u682a\u67af\u6811\u4e0b\uff0c\u67af\u6811\u963b\u65ad\u4e86\u4ed6\u7684\u9000\u8def\u3002\u6811\u5df2\u67af\u6b7b\uff0c\u4eba\u4e5f\u5c06\u6b7b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u5fc3\u91cc\u5ffd\u7136\u95ea\u51fa\u4e86\u4e00\u4e1d\u7075\u673a\u2014\u2014\u5728\u751f\u6b7b\u5c06\u5206\u7684\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u672c\u5c31\u662f\u4eba\u7c7b\u601d\u60f3\u6700\u654f\u9510\u7684\u65f6\u5019\u3002 \u3000\u3000\u5fc3\u5251\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u72ec\u5b64\u75f4\u7684\u8bdd\u3002 \u3000\u3000\u2014\u2014\u4f60\u638c\u4e2d\u7eb5\u7136\u63e1\u6709\u5439\u6bdb\u65ad\u53d1\u7684\u5229\u5668\uff0c\u4f46\u662f\u4f60\u7684\u5fc3\u4e2d\u82e5\u662f\u65e0\u5251\uff0c\u4f60\u638c\u4e2d\u7684\u5229\u5251\u4e5f\u53ea\u4e0d\u8fc7\u662f\u5757\u5e9f\u94c1\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u662f\u5251\u672f\u4e2d\u81f3\u9ad8\u81f3\u6df1\u7684\u9053\u7406\uff0c\u8fd9\u9053\u7406\u5982\u679c\u7528\u53e6\u4e00\u79cd\u65b9\u6cd5\u89e3\u91ca\uff0c\u4e5f\u540c\u6837\u53ef\u5b58\u5728\u3002 \u3000\u3000\u2014\u2014\u4f60\u638c\u4e2d\u867d\u7136\u65e0\u5251\uff0c\u4f46\u662f\u4f60\u7684\u5fc3\u4e2d\u5982\u679c\u6709\u5251\uff0c\u7eb5\u7136\u662f\u4e00\u5757\u5e9f\u94c1\uff0c\u4e5f\u53ef\u4ee5\u53d8\u6210\u6740\u4eba\u7684\u5229\u5668\u3002 \u3000\u3000\u4eba\u5df2\u903c\u8fd1\u3002 \u3000\u3000\u666e\u677e\u5ffd\u7136\u53d1\u51fa\u4f4e\u543c\u5982\u72ee\uff0c\u5168\u8eab\u7684\u8863\u886b\u5ffd\u7136\u65e0\u98ce\u800c\u52a8\uff0c\u9707\u8361\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u5df2\u632f\u8d77\u4e86\u5168\u529b\uff0c\u4f5c\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u4ed6\u7684\u8840\u638c\u5df2\u51fb\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5c0f\u65b9\u5ffd\u7136\u53cd\u624b\u62d7\u65ad\u4e86\u4e00\u6839\u67af\u679d\uff0c\u659c\u659c\u5730\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u8fd9\u6839\u67af\u679d\u5df2\u4e0d\u662f\u67af\u679d\uff0c\u5df2\u7ecf\u53d8\u6210\u4e86\u4e00\u67c4\u5251\u3002 \u3000\u3000\u65e0\u575a\u4e0d\u6467\u7684\u6740\u4eba\u5229\u5251\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u4e5f\u6ca1\u6709\u5c06\u8fd9\u6839\u67af\u679d\u5f53\u4f5c\u67af\u679d\uff0c\u4e5f\u5df2\u5c06\u5b83\u5f53\u4f5c\u4e86\u4e00\u67c4\u5251\uff0c\u5168\u5fc3\u5168\u610f\u5730\u5c06\u5b83\u5f53\u4f5c\u4e86\u4e00\u67c4\u5251\uff0c\u4ed6\u7684\u5168\u8eab\u7cbe\u6c14\u90fd\u5df2\u6ce8\u5728\u8fd9\u67c4\u201c\u5251\u201d\u4e0a\u3002 \u3000\u3000\u8fd9\u201c\u5251\u201d\u770b\u6765\u867d\u7136\u7a7a\u7075\u7f25\u7f08\u865a\u65e0\uff0c\u53ef\u662f\u4ed6\u4e00\u201c\u5251\u201d\u523a\u51fa\uff0c\u666e\u677e\u7684\u8840\u638c\u7adf\u5df1\u88ab\u6d1e\u7a7f\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4e58\u52bf\u5f80\u524d\u4e00\u9006\uff0c\u4ed6\u7684\u201c\u5251\u201d\u53c8\u523a\u5165\u4e86\u666e\u677e\u7684\u773c\u3002 \u3000\u3000\u666e\u677e\u7684\u8840\u638c\u7adf\u88ab\u8fd9\u4e00\u6839\u67af\u679d\u9489\u5728\u81ea\u5df1\u7684\u773c\u775b\u4e0a\uff01 \u3000\u3000\u9c9c\u8840\u98de\u6e85\uff0c\u4eba\u5df2\u5012\u4e0b\uff0c\u4e00\u5012\u4e0b\u5c31\u4e0d\u518d\u52a8\u3002 \u3000\u3000\u7b49\u5230\u6709\u98ce\u5439\u8fc7\u7684\u65f6\u5019\uff0c\u5c0f\u65b9\u624d\u53d1\u89c9\u81ea\u5df1\u7684\u8863\u886b\u90fd\u5df2\u6e7f\u900f\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u60f3\u4e0d\u5230\u4ed6\u8fd9\u4e00\u67c4\u201c\u5251\u201d\u6709\u8fd9\u6837\u7684\u5a01\u529b\uff0c\u56e0\u4e3a\u8fd9\u4e00\u201c\u5251\u201d\u5e76\u4e0d\u662f\u7528\u4ed6\u7684\u624b\u523a\u51fa\u7684\uff0c\u800c\u662f\u7528\u5fc3\u523a\u51fa\u7684\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u201c\u5251\u201d\u523a\u51fa\u7684\u90a3\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u7684\u5fc3\uff0c\u4ed6\u7684\u624b\uff0c\u4ed6\u7684\u4eba\uff0c\u5df2\u5b8c\u5168\u548c\u4ed6\u7684\u201c\u5251\u201d\u878d\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u7684\u7cbe\u6c14\u8d2f\u901a\uff0c\u4eba\u795e\u4ea4\u4f1a\uff0c\u4ed6\u628a\u63e1\u4f4f\u8fd9\u4e00\u5239\u90a3\uff0c\u523a\u51fa\u4e86\u5fc5\u6740\u5fc5\u80dc\u7684\u4e00\u4e00\u201c\u5251\u201d\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u201c\u5fc3\u5251\u201d\u7684\u7cbe\u4e49\u3002 \u3000\u3000\u4f46\u662f\u666e\u677e\u5e76\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u542c\u89c1\u4ed6\u5728\u5583\u5583\u81ea\u8bed\uff0c\u4eff\u4f5b\u5728\u547c\u5524\u7740\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\uff1a\u201c\u6ce2\u5a03\u2026\u2026\u6ce2\u5a03\u2026\u2026\u201d \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u62bd\u7d27\uff0c\u7acb\u523b\u4fef\u4e0b\u8eab\u6765\uff0c\u7528\u529b\u6293\u8d77\u4e86\u666e\u677e\u7684\u8863\u895f\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6ce2\u5a03\u8981\u4f60\u6765\u6740\u6211\u7684\uff1f\u201d\u4ed6\u7684\u58f0\u97f3\u5636\u54d1\uff0c\u201c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u666e\u677e\u773c\u775b\u91cc\u4e00\u7247\u865a\u7a7a\uff0c\u5583\u5583\u5730\u8bf4\uff1a\u201c\u5979\u8981\u6211\u5e26\u4f60\u53bb\u89c1\u5979\u3002\u6211\u4e0d\u80fd\u5e26\u4f60\u53bb\u89c1\u5979\uff0c\u6211\u5b81\u53ef\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u7528\u7684\u8bcd\u53e5\u672c\u6765\u5c31\u5f88\u8270\u6da9\u96be\u89e3\uff1a\u201c\u6211\u4e0d\u80fd\u8981\u4f60\u6b7b\uff0c\u6211\u81ea\u5df1\u6b7b\u3002\u7b49\u6211\u6b7b\u4e86\uff0c\u4f60\u624d\u80fd\u53bb\u89c1\u5979\u3002\u6211\u6d3b\u7740\u7684\u65f6\u5019\uff0c\u8c01\u4e5f\u4e0d\u80fd\u628a\u5979\u62a2\u8d70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u624b\u653e\u677e\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4e86\u89e3\u666e\u677e\u5fc3\u91cc\u7684\u9634\u5f71\u662f\u600e\u4e48\u4f1a\u5b58\u5728\u7684\u3002 \u3000\u3000\u53ea\u6709\u6700\u5f3a\u70c8\u75db\u82e6\u7684\u7231\uff0c\u624d\u80fd\u5e26\u6765\u5982\u6b64\u6c89\u90c1\u7684\u9634\u5f71\u3002 \u3000\u3000\u540c\u6837\u7684\u75db\u82e6\uff0c\u540c\u6837\u7684\u7231\uff0c\u540c\u6837\u7684\u5f3a\u70c8\uff0c\u4f7f\u5f97\u5c0f\u65b9\u5ffd\u7136\u5bf9\u8fd9\u4e2a\u4eba\u751f\u51fa\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u601c\u4f24\u3002 \u3000\u3000\u666e\u677e\u5ffd\u7136\u4ece\u5fc3\u7684\u6700\u6df1\u5904\u5410\u51fa\u53e3\u6c14\uff1a\u201c\u6211\u5df2\u5c06\u6b7b\uff0c\u4f60\u53ef\u4ee5\u53bb\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6323\u624e\u7740\uff0c\u62c9\u5f00\u521a\u624d\u5df2\u7ecf\u88ab\u5c0f\u65b9\u6293\u677e\u4e86\u7684\u8863\u895f\uff0c\u9732\u51fa\u4e86\u91cc\u9762\u7684\u9ec4\u8272\u8888\u88df\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u5c0f\u65b9\u624d\u770b\u51fa\u4ed6\u662f\u4e2a\u50e7\u4eba\u3002 \u3000\u3000\u770b\u4ed6\u7684\u6c14\u5ea6\u548c\u522b\u4eba\u5bf9\u4ed6\u7684\u5c0a\u656c\uff0c\u4ed6\u65e0\u7591\u662f\u4e2a\u6743\u4f4d\u6781\u91cd\u7684\u5587\u561b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e5f\u50cf\u5176\u4ed6\u51e1\u4fd7\u7684\u4eba\u4e00\u6837\uff0c\u4e5f\u5b81\u613f\u4e3a\u4e00\u4e2a\u5973\u4eba\u800c\u6b7b\u3002 \u3000\u3000\u4e00\u4e00\u5979\u4e0d\u662f\u5973\u4eba\uff0c\u5979\u662f\u4e2a\u9b54\u5973\uff0c\u6ca1\u6709\u4efb\u4f55\u7537\u4eba\u80fd\u62d2\u7edd\u5979\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5728\u523a\u75db\u3002 \u3000\u3000\u201c\u4f60\u8981\u6211\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u666e\u677e\u4ece\u8d34\u8eab\u7684\u8888\u88df\u91cc\uff0c\u62ff\u51fa\u4e2a\u91d1\u4f5b\uff1a\u201c\u4f60\u5230\u5e03\u8fbe\u62c9\u5bab\u53bb\uff0c\u5e26\u7740\u6211\u7684\u62a4\u8eab\u4f5b\u53bb\uff0c\u53bb\u6c42\u89c1\u2018\u5676\u4f26\u5587\u561b\u2019\u5c31\u8bf4\u6211\u2026\u2026\u6211\u5df2\u7ecf\u89e3\u8131\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u8bf4\u7684\u6700\u540e\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u9634\u5f71\u53ea\u6709\u6b7b\u624d\u80fd\u9a71\u6563\uff0c\u4ed6\u5fc3\u4e2d\u7684\u75db\u82e6\u53ea\u6709\u6b7b\u624d\u80fd\u89e3\u8131\u3002 \u3000\u3000\u2014\u2014\u4ed6\u662f\u4e0d\u662f\u771f\u7684\u5df2\u89e3\u8131\u4e86\uff1f\u4ed6\u6b7b\u65f6\u5fc3\u4e2d\u662f\u5426\u5df2\u771f\u7684\u6062\u590d\u4e86\u6614\u65e5\u7684\u5b81\u9759\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u6709\u8c01\u80fd\u56de\u7b54\uff1f \u3000\u3000\u4ed6\u628a\u8fd9\u95ee\u9898\u7559\u7ed9\u4e86\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u5676\u4f26\u5587\u561b\u201d\u662f\u5728\u96c4\u5947\u7470\u4e3d\u7684\u5e03\u8fbe\u62c9\u5bab\u4e2d\uff0c\u4e00\u4e2a\u9634\u6697\u7684\u7985\u623f\u4e2d\u63a5\u89c1\u5c0f\u65b9\u7684\u3002 \u3000\u3000\u5728\u8fd9\u5360\u8001\u800c\u795e\u79d8\u7684\u5b97\u6559\u4f20\u7edf\u4e2d\uff0c\u5676\u4f26\u5587\u561b\u4e0d\u4ec5\u662f\u4f4d\u6df1\u901a\u4f5b\u7406\u7684\u9ad8\u50e7\uff0c\u4e5f\u662f\u6cbb\u7406\u4e07\u6c11\u7684\u5927\u540f\uff0c\u4ed6\u7684\u5730\u4f4d\u4ec5\u6b21\u4e8e\u4ed6\u4eec\u7684\u6d3b\u4f5b\u8fbe\u8d56\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u5165\u5374\u50cf\u662f\u8fd9\u95f4\u7985\u623f\u4e00\u6837\uff0c\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u9634\u6697\u8870\u8001\u3001\u66ae\u6c14\u6c89\u6c89\u3002 \u3000\u3000\u5c0f\u65b9\u60f3\u4e0d\u5230\u8fd9\u4e48\u5bb9\u6613\u5c31\u80fd\u89c1\u5230\u4ed6\uff0c\u66f4\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u662f\u4e2a\u8fd9\u4e48\u6837\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u76d8\u819d\u5750\u5728\u4e00\u5f20\u53e4\u8001\u7834\u65e7\u7684\u7985\u5e8a\u4e0a\uff0c\u9ed8\u9ed8\u5730\u63a5\u8fc7\u4e86\u5c0f\u65b9\u4ea4\u7ed9\u4ed6\u7684\u91d1\u4f5b\uff0c\u9ed8\u9ed8\u5730\u542c\u5c0f\u65b9\u8bf4\u51fa\u4e86\u6765\u610f\uff0c\u6ee1\u5e03\u76b1\u7eb9\u7684\u7626\u8138\u4e0a\uff0c\u59cb\u7ec8\u5e26\u7740\u79cd\u6b63\u5728\u6df1\u601d\u7684\u8868\u60c5\uff0c\u5374\u53c8\u4eff\u4f5b\u5168\u65e0\u8868\u60c5\uff0c\u56e0\u4e3a\u4ed6\u7684\u601d\u60f3\u5df2\u4e0d\u80fd\u6253\u52a8\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d\u7b49\u5c0f\u65b9\u8bf4\u5b8c\u540e\uff0c\u5676\u4f26\u5587\u561b\u624d\u5f00\u53e3\uff0c\u201c\u6211\u4e5f\u77e5\u9053\u666e\u677e\u7684\u75db\u82e6\u53ea\u6709\u6b7b\u624d\u80fd\u89e3\u8131\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u8870\u5f31\u7f13\u6162\u8fdf\u949d\uff0c\u8bf4\u51fa\u7684\u6c49\u8bed\u5374\u6781\u6d41\u5229\u51c6\u786e\uff1a\u201c\u6211\u53ea\u95ee\u4f60\uff0c\u662f\u4e0d\u662f\u4f60\u6740\u4e86\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u6211\u4e0d\u80fd\u4e0d\u6740\u4ed6\uff0c\u5f53\u65f6\u6211\u6839\u672c\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\uff0c\u4ed6\u4e0d\u6b7b\uff0c\u6211\u5c31\u8981\u6b7b\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\uff0c\u6211\u770b\u5f97\u51fa\u4f60\u662f\u4e2a\u8bda\u5b9e\u7684\u4eba\u3002\u201d\u5676\u6ca6\u5587\u561b\u9053\uff0c\u201c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u4f60\u5f53\u7136\u4e0d\u60f3\u6b7b\uff0f \u3000\u3000\u4ed6\u7528\u4e00\u53cc\u6697\u6de1\u7684\u773c\u775b\u51dd\u89c6\u7740\u5c0f\u65b9\uff0c\u201c\u6240\u4ee5\u4f60\u4e5f\u4e0d\u8be5\u6765\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u666e\u677e\u4e3a\u4ec0\u4e48\u8981\u4f60\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8981\u6211\u6765\u89c1\u6ce2\u5a03\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d\u5676\u4f26\u5587\u561b\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u4eec\u7684\u6559\u4e49\u548c\u4e2d\u571f\u4e0d\u540c\uff0c\u6211\u4eec\u4e0d\u6212\u6740\u751f\uff0c\u56e0\u4e3a\u4e0d\u6740\u751f\u5c31\u4e0d\u80fd\u964d\u9b54\u3002\u6211\u4eec\u5bf9\u4ed8\u5996\u9b54\u7f6a\u4eba\u53db\u5f92\u4ec7\u654c\u7684\u65b9\u6cd5\u5c31\u53ea\u6709\u4e00\u79cd\uff0c\u540c\u6837\u7684\u4e00\u79cd\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u79cd\uff1f\u201d \u3000\u3000\u201c\u4ee5\u773c\u8fd8\u773c\uff0c\u4ee5\u7259\u8fd8\u7259\uff01\u201d\u5676\u4f26\u5587\u561b\u7684\u6001\u5ea6\u8fd8\u662f\u5f88\u5e73\u9759\uff0c\u201c\u6211\u4eec\u76f8\u4fe1\u8fd9\u662f\u552f\u4e00\u6709\u6548\u7684\u4e00\u79cd\u65b9\u6cd5\uff0c\u81ea\u53e4\u4ee5\u6765\u5c31\u53ea\u6709\u8fd9\u4e00\u79cd\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u4f60\u5df2\u7ecf\u5e94\u8be5\u660e\u767d\uff0c\u666e\u677e\u8981\u4f60\u6765\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u77e5\u9053\u6211\u4e00\u5b9a\u4f1a\u6740\u4e86\u4f60\u66ff\u4ed6\u590d\u4ec7\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\uff0c\u666e\u677e\u65e0\u8bba\u662f\u6b7b\u662f\u6d3b\uff0c\u90fd\u4e0d\u613f\u8ba9\u4ed6\u89c1\u5230\u6ce2\u5a03\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u4ecd\u5728\u51dd\u89c6\u7740\u4ed6\uff0c\u773c\u8272\u8fd8\u662f\u90a3\u4e48\u6e29\u548c\uff0c\u4f46\u5374\u5ffd\u7136\u8bf4\u51fa\u4e86\u4e00\u53e5\u6bd4\u5200\u950b\u66f4\u5c16\u9510\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u4fe1\u4e0d\u4fe1\u6211\u5728\u4e3e\u624b\u95f4\u5c31\u80fd\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u62d2\u7edd\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u4e0d\u4fe1\uff0c\u4f46\u662f\u4ed6\u5df2\u7ecf\u5386\u8fc7\u5927\u591a\u4ee4\u4eba\u65e0\u6cd5\u7f6e\u4fe1\u7684\u4e8b\u3002 \u3000\u3000\u5728\u8fd9\u795e\u79d8\u800c\u964c\u751f\u7684\u56fd\u571f\u4e0a\uff0c\u5728\u8fd9\u795e\u79d8\u800c\u5e84\u4e25\u7684\u5bab\u6bbf\u91cc\uff0c\u9762\u5bf9\u7740\u8fd9\u6837\u4e00\u4f4d\u795e\u79d8\u7684\u9ad8\u50e7\uff0c\u6709\u5f88\u591a\u4ed6\u672c\u6765\u7edd\u4e0d\u76f8\u4fe1\u7684\u4e8b\u73b0\u5728\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u53c8\u9053\uff1a\u201c\u5899\u4e0a\u6709\u5251\uff0c\u4f60\u4e0d\u59a8\u89e3\u4e0b\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u56de\u8fc7\u5934\uff0c\u5c31\u770b\u5230\u5899\u4e0a\u60ac\u6302\u7740\u4e00\u67c4\u5c18\u5c01\u5df2\u4e45\u7684\u53e4\u5251\u3002 \u3000\u3000\u4ed6\u89e3\u4e0b\u4e86\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u5f62\u5f0f\u5947\u53e4\u7684\u957f\u5251\uff0c\u4efd\u91cf\u6781\u6c89\u91cd\uff0c\u9752\u94dc\u5251\u9537\u548c\u5251\u9798\u541e\u53e3\u4e0a\u5df2\u751f\u7eff\u9508\uff0c\u770b\u6765\u5e76\u4e0d\u50cf\u662f\u67c4\u5229\u5668\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u62d4\u51fa\u6765\u770b\u770b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u62d4\u5251\u3002 \u3000\u3000\u5251\u8eab\u4eff\u4f5b\u4e5f\u5df2\u9508\u4f4f\uff0c\u7b2c\u4e00\u6b21\u4ed6\u7adf\u6ca1\u6709\u62d4\u51fa\u6765\u3002\u7b2c\u4e8c\u6b21\u4ed6\u518d\u7528\u529b\uff0c\u5ffd\u7136\u95f4\uff0c\u201c\u545b\u537f\u201d\u4e00\u58f0\u9f99\u541f\uff0c\u957f\u5251\u8131\u9798\u800c\u51fa\uff0c\u9634\u6697\u7684\u7985\u623f\u91cc\u7acb\u523b\u5e03\u6ee1\u68ee\u68ee\u5251\u6c14\uff0c\u8fde\u5676\u4f26\u5587\u561b\u7684\u987b\u7709\u90fd\u88ab\u6620\u7eff\u3002 \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u8131\u53e3\u800c\u547c\uff1a\u201c\u597d\u5251\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u662f\u67c4\u597d\u5251\u3002\u201d\u5676\u4f26\u5587\u561b\u9053\uff0c\u201c\u4f60\u80fd\u6740\u666e\u677e\uff0c\u7ec3\u5251\u81f3\u5c11\u5df2\u6709\u5341\u5e74\uff0c\u5e94\u8be5\u80fd\u770b\u51fa\u8fd9\u662f\u4ec0\u4e48\u5251\u3002\u201d \u3000\u3000\u8fd9\u662f\u67c4\u5f88\u5947\u602a\u7684\u5251\uff0c\u4efd\u91cf\u672c\u6765\u6781\u91cd\uff0c\u53ef\u662f\u5251\u950b\u51fa\u9798\u540e\uff0c\u63e1\u5728\u624b\u91cc\uff0c\u53c8\u4eff\u4f5b\u5ffd\u7136\u53d8\u5f97\u6781\u8f7b\uff0c\u5251\u950b\u672c\u6765\u8272\u5982\u53e4\u677e\u7684\u6811\u5e72\uff0c\u5251\u5149\u5374\u662f\u78a7\u7eff\u8272\u7684\uff0c\u5c31\u50cf\u662f\u9752\u7fe0\u7684\u677e\u9488\u3002 \u3000\u3000\u5c0f\u65b9\u8bd5\u63a2\u7740\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u6625\u79cb\u6218\u56fd\u65f6\u7b2c\u4e00\u9ad8\u4eba\u8d64\u677e\u5b50\u7684\u4f69\u5251\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u8fd9\u67c4\u5251\u5c31\u662f\u2018\u8d64\u677e\u2019\u3002\u201d\u5676\u4f26\u5587\u561b\u9053\uff0c\u201c\u867d\u7136\u6ca1\u6709\u5217\u5165\u5f53\u4e16\u4e03\u67c4\u540d\u5251\u4e2d\uff0c\u53ea\u56e0\u4e3a\u4e16\u4eba\u591a\u534a\u4ee5\u4e3a\u5b83\u5df2\u88ab\u6c89\u57cb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u53e4\u8001\u76f8\u4f20\uff0c\u2018\u8d64\u677e\u2019\u7684\u5149\u8292\u672c\u8be5\u7ea2\u5982\u5915\u9633\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u662f\u78a7\u7eff\u8272\u7684\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u5df2\u6709\u5341\u4e5d\u5e74\u672a\u996e\u4eba\u8840\u4e86\u3002\u201d \u3000\u3000\u5676\u4f26\u5587\u561b\u9053\uff1a\u201c\u6740\u4eba\u65e0\u7b97\u7684\u5229\u5668\u795e\u5175\uff0c\u82e5\u662f\u591a\u5e74\u6765\u672a\u996e\u4eba\u8840\uff0c\u4e0d\u4f46\u5149\u8292\u4f1a\u53d8\u8272\uff0c\u800c\u4e14\u4f1a\u6e10\u6e10\u5931\u53bb\u5b83\u7684\u950b\u8292\uff0c\u751a\u81f3\u4f1a\u6e10\u6e10\u53d8\u4e3a\u51e1\u94c1\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5b83\u662f\u4e0d\u662f\u5df2\u7ecf\u5230\u4e86\u8981\u559d\u996e\u4eba\u8840\u7684\u65f6\u5019\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u996e\u8c01\u7684\u8840\uff1f\u201d\u5c0f\u65b9\u63e1\u7d27\u5251\u67c4\u3002 \u3000\u3000\u201c\u6211\u7684\u8840\u3002\u201d\u5676\u4f26\u5587\u561b\u9053\uff0c\u201c\u4f5b\u7956\u80fd\u820d\u8eab\u5582\u9e70\uff0c\u4e3a\u4e86\u8fd9\u79cd\u795e\u5175\u5229\u5668\uff0c\u6211\u4e3a\u4f55\u4e0d\u80fd\u820d\u5f03\u8fd9\u526f\u81ed\u76ae\u56ca\uff1f\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u548c\u6001\u5ea6\u90fd\u5b8c\u5168\u6ca1\u6709\u53d8\u5316\uff0c\u770b\u6765\u8fd8\u662f\u90a3\u4e48\u8870\u5f31\u6e29\u548c\u5e73\u9759\u3002 \u3000\u3000\u5c0f\u65b9\u63e1\u5251\u7684\u624b\u653e\u677e\u4e86\uff1a\u201c\u4f60\u8981\u6211\u7528\u8fd9\u67c4\u5251\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u672c\u6765\u8981\u6740\u6211\u7684\u3002\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u73b0\u5728\u4e3a\u4ec0\u4e48\u8981\u6211\u6740\u4f60\uff1f\u201d \u3000\u3000\u5676\u4f26\u5587\u561b\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u4e45\u5df2\u5c06\u751f\u6b7b\u770b\u5f97\u5f88\u6de1\u3002\u6211\u82e5\u6740\u4e86\u4f60\uff0c\u7edd\u4e0d\u4f1a\u4e3a\u4f60\u60b2\u4f24\u3002\u4f60\u82e5\u6740\u4e86\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u602a\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u8bdd\u4e2d\u4eff\u4f5b\u53e6\u6709\u6df1\u610f\uff1a\u201c\u6240\u4ee5\u6211\u4e0d\u59a8\u6740\u4e86\u4f60\uff0c\u4f60\u4e5f\u4e0d\u59a8\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\uff0c\u6211\u80fd\u6740\u4f60\uff0c\u5c31\u4e0d\u59a8\u6740\u4e86\u4f60\uff0c\u6740\u4e0d\u4e86\u4f60\uff0c\u5c31\u5f97\u6b7b\u5728\u4f60\u7684\u624b\u91cc\uff1f\u201d \u3000\u3000\u5676\u4f26\u5587\u561b\u4e0d\u518d\u56de\u7b54\uff0c\u8fd9\u95ee\u9898\u6839\u672c\u4e0d\u5fc5\u56de\u7b54\u3002 \u3000\u3000\u5c0f\u65b9\u63e1\u5251\u7684\u624b\u53c8\u63e1\u7d27\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u826f\u673a\u4e00\u5931\uff0c\u6c38\u4e0d\u518d\u6765\uff0c\u518d\u60f3\u56de\u5934\uff0c\u5c31\u5df2\u4e07\u52ab\u4e0d\u590d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5c31\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u5c0f\u65b9\u4e00\u773c\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u4e0d\u80fd\u4e0d\u770b\u4ed6\u3002 \u3000\u3000\u4ed6\u7684\u786e\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u7684\u786e\u5df2\u4e0d\u518d\u5c06\u751f\u6b7b\u653e\u5728\u5fc3\u4e0a\u3002\u5bf9\u4ed6\u6765\u8bf4\uff0c\u6b7b\u5df2\u4e0d\u518d\u662f\u4e2a\u60b2\u5267\uff0c\u56e0\u4e3a\u4e16\u4e0a\u5df2\u6ca1\u6709\u4efb\u4f55\u4e8b\u4f24\u5bb3\u4ed6\uff0c\u8fde\u6b7b\u90fd\u4e0d\u80fd\u3002 \u3000\u3000\u5c0f\u65b9\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u4e00\u5251\u523a\u4e86\u51fa\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u5251\u523a\u7684\u662f\u5fc3\u810f\u3002 \u3000\u3000\u5c0f\u65b9\u786e\u4fe1\u81ea\u5df1\u7684\u51fa\u624b\u7edd\u5bf9\u51c6\u786e\uff0c\u523a\u7684\u7edd\u5bf9\u662f\u5728\u4e00\u5239\u90a3\u95f4\u5c31\u53ef\u4ee5\u5236\u4eba\u4e8e\u6b7b\u7684\u90e8\u5206\uff0c\u4ed6\u4e0d\u60f3\u8ba9\u8fd9\u4f4d\u9ad8\u50e7\u4e34\u6b7b\u524d\u518d\u53d7\u75db\u82e6\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u4ed6\u8fd9\u4e00\u5251\u7adf\u523a\u7a7a\u4e86\u3002 \u3000\u3000\u4ed6\u660e\u660e\u770b\u89c1\u5676\u4f26\u5587\u561b\u4e00\u76f4\u90fd\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u660e\u660e\u5df2\u907f\u4e0d\u5f00\u4ed6\u8fd9\u4e00\u4e00\u5251\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd9\u4e00\u5251\u504f\u504f\u523a\u7a7a\u4e86\uff01 \u3000\u3000\u5676\u4f26\u5587\u561b\u786e\u5b9e\u6ca1\u6709\u52a8\uff0c\u7edd\u5bf9\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u8fd8\u662f\u5750\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u4e24\u6761\u817f\u8fd8\u662f\u76d8\u7740\u819d\uff0c\u4ed6\u7684\u8138\u8fd8\u662f\u5728\u90a3\u4e00\u7247\u9634\u5f71\u91cc\uff0c\u773c\u775b\u8fd8\u662f\u95ed\u7740\u7684\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u5251\u950b\u523a\u6765\u7684\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u7684\u5fc3\u810f\u7684\u90e8\u4f4d\u5ffd\u7136\u79fb\u5f00\u4e86\u4e5d\u5bf8\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u90fd\u6ca1\u6709\u52a8\uff0c\u5c31\u53ea\u8fd9\u4e00\u4e2a\u90e8\u4f4d\u5ffd\u7136\u79fb\u5f00\u4e86\u4e5d\u5bf8\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u8eab\u4e0a\u8fd9\u4e00\u90e8\u4efd\u5c31\u50cf\u662f\u5ffd\u7136\u8ddf\u4ed6\u7684\u8eab\u4e8e\u8131\u79bb\u4e86\u3002 \u3000\u3000\u5251\u950b\u53ea\u5dee\u534a\u5bf8\u5c31\u53ef\u4ee5\u523a\u5165\u4ed6\u7684\u5fc3\u810f\uff0c\u53ef\u662f\u8fd9\u534a\u5bf8\u5c31\u5df2\u8fdc\u9694\u4eba\u5929\uff0c\u8fdc\u9694\u751f\u6b7b\uff0c\u867d\u7136\u53ea\u5dee\u534a\u5bf8\uff0c\u5374\u5df2\u8fdc\u5982\u5343\u5343\u4e07\u4e07\u91cc\u4e4b\u5916\uff0c\u53ef\u671b\u800c\u4e0d\u53ef\u5373\u7684\u82b1\u6811\u4e91\u5c71\u3002 \u3000\u3000\u4e00\u5251\u523a\u7a7a\uff0c\u5c0f\u65b9\u7684\u5fc3\u4e5f\u597d\u50cf\u5ffd\u7136\u4e00\u811a\u8e0f\u7a7a\uff0c\u843d\u5165\u4e86\u4e07\u52ab\u4e0d\u590d\u7684\u6df1\u6e0a\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u5df2\u4f38\u51fa\u624b\uff0c\u4ee5\u62c7\u6307\u6263\u4e2d\u6307\uff0c\u4ee5\u4e2d\u6307\u8f7b\u5f39\u5251\u950b\u3002 \u3000\u3000\u201c\u6323\u201d\u7684\u3001\u58f0\uff0c\u706b\u661f\u56db\u6e85\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u89c9\u5f97\u864e\u53e3\u4e00\u9635\u5267\u9707\uff0c\u957f\u5251\u5df2\u8131\u624b\u98de\u51fa\uff0c\u201c\u593a\u201d\u7684\u4e00\u58f0\uff0c\u9489\u5165\u4e86\u5c4b\u9876\u3002 \u3000\u3000\u5c4b\u9876\u4e0a\u6709\u5c18\u57c3\u843d\u4e0b\uff0c\u843d\u5728\u4ed6\u8eab\u4e0a\uff0c\u4e00\u7c92\u7c92\u5fae\u5c18\uff0c\u5c31\u50cf\u662f\u4e00\u67c4\u67c4\u94c1\u9524\u3002 \u3000\u3000\u4ed6\u5df2\u88ab\u6253\u5f97\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u7ec8\u4e8e\u53c8\u5f20\u5f00\u773c\uff0c\u770b\u7740\u4ed6\uff0c\u773c\u8272\u8fd8\u662f\u540c\u6837\u6e29\u548c\u9634\u6697\u3002 \u3000\u3000\u4ed6\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u76f8\u4fe1\u6211\u5728\u4e3e\u624b\u95f4\u5c31\u80fd\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u4ed6\u5df2\u53d1\u73b0\u8fd9\u8870\u8001\u7684\u50e7\u4eba\uff0c\u624d\u662f\u4ed6\u8fd9\u4e00\u751f\u4e2d\u6240\u9047\u89c1\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e0d\u4f46\u80fd\u968f\u610f\u63a7\u5236\u81ea\u5df1\u7684\u7cbe\u6c14\u529b\u91cf\uff0c\u8fde\u6bcf\u4e00\u5bf8\u808c\u8089\u3001\u6bcf\u4e00\u5904\u5173\u8282\u90fd\u80fd\u968f\u610f\u53d8\u5316\u63a7\u5236\u3002 \u3000\u3000\u5c0f\u65b9\u7adf\u5b8c\u5168\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u88ab\u4e00\u79cd\u4ec0\u4e48\u6837\u7684\u6b66\u529f\u6240\u51fb\u8d25\u7684\u3002 \u3000\u3000\u795e\u79d8\u7684\u6c11\u65cf\u3001\u795e\u79d8\u7684\u5b97\u6559\u3001\u795e\u79d8\u7684\u6b66\u529f\uff0c\u5c0f\u65b9\u8fd8\u80fd\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u53ea\u80fd\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u5676\u4f26\u5587\u561b\u7684\u56de\u7b54\u4e5f\u548c\u4ed6\u7684\u6b66\u529f\u540c\u6837\u7384\u79d8\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5df2\u7ecf\u77e5\u9053\u4f60\u7684\u6765\u610f\u3002\u201d\u5676\u4f26\u5587\u561b\u9053\uff0c\u201c\u4f60\u4e0d\u662f\u6765\u770b\u90a3\u4e2a\u5973\u4eba\u7684\uff0c\u4f60\u662f\u6765\u6740\u5979\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u6709\u6740\u6c14\u3002\u201d\u5676\u4f26\u5587\u561b\u9053\uff0c\u201c\u53ea\u6709\u5df2\u51b3\u5fc3\u8981\u6740\u4eba\u7684\u4eba\uff0c\u624d\u6709\u8fd9\u79cd\u6740\u6c14\uff0c\u4f60\u81ea\u5df1\u867d\u7136\u770b\u4e0d\u89c1\uff0c\u53ef\u662f\u4f60\u4e00\u8d70\u5165\u6b64\u95e8\uff0c\u6211\u5c31\u5df2\u611f\u89c9\u5230\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u518d\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u90fd\u5df2\u88ab\u9707\u60ca\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u53c8\u63a5\u7740\u8bf4\u4e0b\u53bb\uff1a\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u53ea\u56e0\u4e3a\u6211\u8981\u4f60\u53bb\u6740\u4e86\u5979\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u5f88\u6c89\u91cd\uff0c\u201c\u53ea\u6709\u5979\u6b7b\uff0c\u4f60\u624d\u80fd\u751f\u3002\u53ea\u6709\u5979\u6b7b\uff0c\u666e\u677e\u7684\u6b7b\u624d\u6709\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u4ed6\u8870\u8001\u7684\u53cc\u773c\u4e2d\u5ffd\u7136\u5c04\u51fa\u7cbe\u5149\uff0c\u5ffd\u7136\u5389\u58f0\u4f5c\u72ee\u5b50\u543c\uff1a\u201c\u62d4\u4e0b\u8fd9\u67c4\u5251\uff0c\u7528\u8fd9\u67c4\u5251\u53bb\u6740\u4e86\u5979\uff01\u7528\u90a3\u9b54\u5973\u7684\u8840\u6765\u996e\u9971\u6b64\u5251\uff01\u201d \u3000\u3000\u5676\u4f26\u5587\u561b\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u5207\u5207\u7262\u8bb0\uff0c\u8fd9\u6b21\u826f\u673a\u518d\u5931\uff0c\u5c31\u771f\u7684\u8981\u6c38\u6ca6\u82e6\u72f1\u3001\u4e07\u52ab\u4e0d\u590d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u8981\u6c42\uff0c\u4e5f\u4e0d\u662f\u547d\u4ee4\u3002\u8fd9\u662f\u4e2a\u8d4c\u7ea6\u3002 \u3000\u3000\u9ad8\u50e7\u7684\u8d4c\u7ea6\u3002 \u3000\u3000\u2014\u2014\u4f60\u80fd\u6740\u5979\uff0c\u4f60\u624d\u80fd\u751f\uff0c\u5426\u5219\u4f60\u7eb5\u7136\u6d3b\u7740\uff0c\u4e5f\u4e0e\u6b7b\u65e0\u5f02\u3002 \u3000\u3000\u8fd9\u4f4d\u795e\u79d8\u7684\u9ad8\u50e7\u975e\u4f46\u770b\u51fa\u4e86\u5c0f\u65b9\u7684\u6740\u6c14\uff0c\u4e5f\u770b\u900f\u4e86\u5c0f\u65b9\u7684\u5fc3\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e0e\u5c0f\u65b9\u8ba2\u4e0b\u8fd9\u4e2a\u8d4c\u7ea6\uff0c\u53ea\u6709\u9ad8\u50e7\u624d\u80fd\u8ba2\u7684\u8d4c\u7ea6\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u4f4d\u9ad8\u50e7\u7684\u82e6\u5fc3\u3002 \u3000\u3000\u5c0f\u65b9\u662f\u4e0d\u662f\u771f\u7684\u6709\u51b3\u5fc3\u53bb\u6740\u6ce2\u5a03\uff1f\u80fd\u4e0d\u80fd\u5fcd\u5fc3\u4e0b\u5f97\u4e86\u624b\uff1f \u3000\u3000\u5c0f\u65b9\u662f\u771f\u7684\u5df2\u4e0b\u4e86\u51b3\u5fc3\u8981\u6765\u6740\u6ce2\u5a03\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u548c\u666e\u677e\u90fd\u7edd\u5bf9\u4e0d\u662f\u4f1a\u8bf4\u8c0e\u7684\u4eba\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\u7edd\u4e0d\u4f1a\u4e1d\u6beb\u865a\u5047\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u7ecf\u8bc1\u5b9e\u4e86\u6ce2\u5a03\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5973\u4eba\uff0c\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u4fe1\uff0c\u6240\u4ee5\u4e5f\u4e0d\u80fd\u518d\u8ba9\u5979\u6d3b\u4e0b\u53bb\uff0c\u5426\u5219\u53c8\u4e0d\u77e5\u6709\u591a\u5c11\u7537\u4eba\u6bc1\u5728\u5979\u624b\u91cc\uff01 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u9762\u5bf9\u6ce2\u5a03\u3002 \u3000\u3000\u4ed6\u7684\u638c\u4e2d\u6709\u5251\uff0c\u5251\u950b\u8ddd\u79bb\u5979\u7684\u5fc3\u810f\u5e76\u4e0d\u8fdc\uff0c\u53ea\u8981\u4ed6\u4e00\u5251\u523a\u51fa\uff0c\u6240\u6709\u7684\u7231\u618e\u6069\u6028\u70e6\u607c\u75db\u82e6\u5c31\u4f1a\u5168\u90fd\u7ed3\u675f\u4e86\u3002\u5c31\u7b97\u4ed6\u8fd8\u662f\u5fd8\u4e0d\u4e86\u5979\uff0c\u65e5\u5b50\u4e45\u4e86\uff0c\u4e5f\u5fc5\u5c06\u6e10\u6e10\u53d8\u5f97\u6de1\u5982\u70df\u4e91\uff0c\u65e0\u8ff9\u53ef\u5bfb\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e00\u5251\u4ed6\u504f\u504f\u523a\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u65e5\u8272\u5df2\u6e10\u6e10\u897f\u6c89\u3002 \u3000\u3000\u6ce2\u5a03\u4e5f\u50cf\u90a3\u795e\u79d8\u7684\u9ad8\u50e7\u4e00\u6837\uff0c\u9759\u9759\u5730\u5750\u5728\u4e00\u7247\u60e8\u6de1\u7684\u9634\u5f71\u91cc\u3002 \u3000\u3000\u5979\u770b\u89c1\u5c0f\u65b9\u8fdb\u6765\uff0c\u770b\u89c1\u4ed6\u624b\u91cc\u63d0\u7740\u5251\uff0c\u5979\u5f53\u7136\u4e5f\u80fd\u770b\u5f97\u51fa\u4ed6\u7684\u6765\u610f\u3002 \u3000\u3000\u6740\u6c14\u867d\u7136\u65e0\u58f0\u65e0\u5f71\u65e0\u5f62\uff0c\u5374\u662f\u7edd\u5bf9\u6ca1\u6cd5\u5b50\u53ef\u4ee5\u9690\u85cf\u7684\u3002 \u3000\u3000\u5982\u679c\u5979\u8fd8\u60f3\u5206\u8fa8\u89e3\u8bf4\uff0c\u8fd8\u60f3\u7528\u90a3\u79cd\u5a07\u695a\u67d4\u5f31\u7684\u6001\u5ea6\u6765\u6311\u8d77\u5c0f\u65b9\u7684\u65e7\u60c5\uff0c\u5c0f\u65b9\u8fd9\u4e00\u5251\u5fc5\u5b9a\u65e9\u5df2\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5982\u679c\u5979\u4e00\u89c1\u5c0f\u65b9\u5c31\u6295\u6000\u9001\u62b1\uff0c\u5b9b\u8f6c\u627f\u6b22\uff0c\u5c0f\u65b9\u4e5f\u5fc5\u5b9a\u5df2\u7ecf\u6740\u4e86\u5979\u3002 \u3000\u3000\u53ef\u662f\u5979\u6ca1\u6709\u8fd9\u4e48\u505a\u3002 \u3000\u3000\u5979\u53ea\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u51dd\u89c6\u7740\u5c0f\u65b9\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u5979\u7b2c\u4e00\u53e5\u8bf4\u7684\u5c31\u662f\u771f\u8bdd\uff0c\u201c\u6211\u8981\u666e\u677e\u53bb\u627e\u4f60\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u4f60\u6765\u770b\u6211\uff0c\u800c\u662f\u4e3a\u4e86\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8e31\u7740\uff0c\u7b49\u7740\u5979\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u771f\u8bdd\u867d\u7136\u4f24\u4eba\uff0c\u5374\u6ca1\u6709\u88ab\u4eba\u6b3a\u9a97\u65f6\u7684\u90a3\u79cd\u75db\u82e6\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u666e\u677e\u4e00\u5b9a\u4e0d\u4f1a\u8ba9\u4f60\u6765\u89c1\u6211\uff0c\u4e00\u5b9a\u4f1a\u6740\u4f60\u3002\u201d\u6ce2\u5a03\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4e0d\u80fd\u6740\u4f60\uff0c\u5c31\u5fc5\u5c06\u6b7b\u5728\u4f60\u624b\u91cc\u3002\u201d \u3000\u3000\u5979\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u6b7b\u4e86\u4e4b\u540e\uff0c\u4f60\u4e00\u5b9a\u4f1a\u6765\uff0c\u5676\u4f26\u5587\u561b\u4e00\u5b9a\u4f1a\u6740\u4e86\u4f60\u66ff\u4ed6\u62a5\u4ec7\u7684\uff0c\u4ed6\u4eec\u7684\u5173\u7cfb\uff0c\u5c31\u50cf\u662f\u7236\u5b50\u4e00\u822c\u7684\u4eb2\u5bc6\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u771f\u8bdd\u3002 \u3000\u3000\u5979\u5df2\u5c06\u6bcf\u4e00\u79cd\u53ef\u80fd\u90fd\u8ba1\u7b97\u8fc7\uff0c\u5979\u7684\u8ba1\u5212\u672c\u6765\u662f\u4f1a\u6210\u529f\u7684\u3002 \u3000\u3000\u6ce2\u5a03\u53c8\u54bd\u4e86\u53e3\u6c14\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u6211\u8fd8\u662f\u7b97\u9519\u4e86\u4e00\u70b9\u3002\u201d\u6ce2\u5a03\u8bf4\uff1a\u201c\u5676\u4f26\u5587\u561b\u8fdc\u6bd4\u6211\u60f3\u50cf\u4e2d\u66f4\u7cbe\u660e\uff0c\u66f4\u5389\u5bb3\uff0c\u5c45\u7136\u80fd\u770b\u7a7f\u6211\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u5979\u53c8\u89e3\u91ca\uff1a\u201c\u4ed6\u5e73\u65f6\u4ece\u6765\u6ca1\u6709\u7406\u4f1a\u6211\u548c\u666e\u677e\u7684\u4e8b\uff0c\u6240\u4ee5\u6211\u624d\u4f1a\u4f4e\u4f30\u4ed6\uff0c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u4ed6\u4e00\u76f4\u90fd\u75db\u6068\u5728\u5fc3\uff0c\u5b81\u5bf9\u653e\u8fc7\u4f60\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u8ba9\u6211\u79f0\u5fc3\u5982\u613f\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u6211\u8fd9\u4e9b\u4e8b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e0d\u60f3\u518d\u9a97\u4f60\u4e86\u3002\u201d \u3000\u3000\u5979\u58f0\u97f3\u5ffd\u7136\u9732\u51fa\u4e86\u4e00\u70b9\u6de1\u6de1\u7684\u54c0\u4f24\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u518d\u95ee\u6211\u5bf9\u4f60\u7a76\u7adf\u662f\u771f\u662f\u5047\uff0c\u56e0\u4e3a\u4f60\u662f\u6211\u7684\u4ec7\u654c\uff0c\u6211\u53ea\u6709\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u8bb0\u5f97\u5979\u8bf4\u8fc7\u540c\u6837\u7684\u8bdd\u3002 \u3000\u3000\u654c\u53cb\u4e4b\u95f4\uff0c\u7edd\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\uff0c\u4e0d\u662f\u670b\u53cb\uff0c\u5c31\u662f\u654c\u4eba\uff0c\u4e0d\u662f\u4f60\u6b7b\uff0c\u5c31\u662f\u6211\u6b7b\uff01 \u3000\u3000\u5f7c\u5a03\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u968f\u65f6\u90fd\u53ef\u4ee5\u6740\u4e86\u6211\uff0c\u6211\u7edd\u4e0d\u602a\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0b\u4e0d\u4e86\u624b\u3002 \u3000\u3000\u4e0d\u662f\u4e0d\u5fcd\u4e0b\u624b\uff0c\u662f\u6839\u672c\u4e0d\u80fd\u4e0b\u624b\uff01 \u3000\u3000\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7a76\u7adf\u662f\u8c01\u5bf9\u8c01\u9519\uff0c\u8c01\u662f\u8c01\u975e\u3002 \u3000\u3000\u5982\u679c\u535c\u9e70\u771f\u7684\u662f\u732b\u76d7\uff0c\u5982\u679c\u6ce2\u5a03\u662f\u4e3a\u4e86\u6355\u76d7\u800c\u505a\u8fd9\u4e9b\u4e8b\u7684\uff0c\u6709\u8c01\u80fd\u8bf4\u5979\u9519\uff1f \u3000\u3000\u4e3a\u4e86\u8fbe\u5230\u76ee\u7684\uff0c\u535c\u9e70\u5c82\u975e\u4e5f\u540c\u6837\u505a\u8fc7\u4e00\u4e9b\u4e0d\u62e9\u624b\u6bb5\u7684\u4e8b\uff1f \u3000\u3000\u72ec\u5b64\u75f4\u662f\u5251\u5ba2\uff0c\u5251\u5ba2\u672c\u65e0\u60c5\uff0c\u666e\u677e\u5df2\u51fa\u5bb6\u4e3a\u50e7\uff0c\u66f4\u4e0d\u5e94\u8be5\u60f9\u4e0a\u4e86\u60c5\u5b7d\uff0c\u5c31\u7b97\u4ed6\u4eec\u662f\u88ab\u5979\u6b3a\u9a97\u4e86\uff0c\u4e5f\u53ea\u80fd\u8bf4\u4ed6\u4eec\u662f\u548e\u7531\u81ea\u53d6\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u60f3\u5230\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u6bcf\u5230\u8fd9\u79cd\u751f\u4e0e\u6b7b\u3001\u662f\u4e0e\u975e\u7684\u91cd\u8981\u5206\u9645\uff0c\u4ed6\u5e38\u5e38\u90fd\u4f1a\u5fd8\u8bb0\u4ed6\u81ea\u5df1\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u56db\u7ae0\u3000\u7231\u6068\u751f\u6b7b\u4e00\u7ebf \u7b2c\u5341\u56db\u7ae0\u3000\u7231\u6068\u751f\u6b7b\u4e00\u7ebf \u3000\u3000\u6ce2\u5a03\u51dd\u89c6\u7740\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u6740\u6211\u4e5f\u597d\uff0c\u4e0d\u6740\u6211\u4e5f\u597d\uff0c\u6211\u90fd\u4e0d\u52c9\u5f3a\u4f60\u3002\u201d\u6ce2\u5a03\u9053\uff1a\u201c\u4f46\u662f\u6709\u4e00\u4ef6\u4e8b\u6211\u4e00\u5b9a\u8981\u63d0\u9192\u4f60\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u6740\u6211\uff0c\u6709\u4eba\u5c31\u8981\u6740\u4f60\u3002\u201d\u6ce2\u5a03\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u6b7b\uff0c\u4f60\u4e00\u8d70\u51fa\u8fd9\u95f4\u7985\u623f\uff0c\u5c31\u5fc5\u5b9a\u6b7b\u5728\u5676\u4f26\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u8bf4\u51fa\u4e86\u8fd9\u4e09\u4e2a\u5b57\uff0c\u4ed6\u5c31\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7231\u4e0e\u6068\uff0c\u662f\u4e0e\u975e\uff0c\u751f\u4e0e\u6b7b\uff0c\u672c\u6765\u5c31\u50cf\u662f\u5200\u950b\u5251\u5203\uff0c\u5728\u5206\u522b\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u5728\u4e00\u7ebf\u95f4\u800c\u5df2\u3002 \u3000\u3000\u5c0f\u65b9\u8d70\u51fa\u4e86\u7985\u623f\uff0c\u5c31\u770b\u89c1\u5676\u4f26\u5587\u561b\u5df2\u7ecf\u5728\u5916\u9762\u7684\u5c0f\u9662\u4e2d\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u65e5\u8272\u6e10\u6697\uff0c\u98ce\u6e10\u51b7\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u5c31\u7ad9\u5728\u4e00\u68f5\u53e4\u6811\u4e0b\uff0c\u98ce\u52a8\u53e4\u6811\uff0c\u5927\u5730\u4e0d\u52a8\u3002 \u3000\u3000\u8fd9\u4f4d\u9ad8\u618e\u4e5f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u770b\u6765\u867d\u7136\u8fd8\u662f\u90a3\u4e48\u67af\u7626\u8870\u5f31\uff0c\u4f46\u662f\u4ed6\u7684\u5b89\u5fcd\u5df2\u5230\u9759\u5982\u5927\u5730\u3002 \u3000\u3000\u552f\u4e00\u7684\u4e00\u70b9\u53d8\u5316\u662f\uff0c\u5f53\u4ed6\u770b\u5230\u5c0f\u65b9\u65f6\uff0c\u773c\u775b\u91cc\u4eff\u4f5b\u4e5f\u9732\u51fa\u4e00\u62b9\u4f36\u60af\u548c\u54c0\u4f24\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u65e9\u5df2\u7b97\u51c6\u5c0f\u65b9\u662f\u7edd\u5bf9\u4e0b\u4e0d\u4e86\u624b\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u638c\u4e2d\u4ecd\u6709\u5251\uff0c\u5251\u5149\u4ecd\u7136\u662f\u78a7\u7eff\u8272\u7684\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u770b\u7740\u4ed6\u624b\u91cc\u7684\u5251\uff0c\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u540d\u5251\u5982\u826f\u9a79\uff0c\u826f\u9a79\u62e9\u4e3b\uff0c\u5251\u4e5f\u4e00\u6837\uff0c\u4f60\u4e0d\u80fd\u5584\u7528\u5b83\uff0c\u5b83\u5c31\u4e0d\u662f\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u67c4\u5251\u672c\u6765\u5c31\u4e0d\u662f\u6211\u7684\uff0c\u662f\u4f60\u7684\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u6162\u6162\u5730\u4f38\u51fa\u624b\uff1a\u201c\u4e0d\u662f\u4f60\u7684\uff0c\u4f60\u5c31\u8be5\u8fd8\u7ed9\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e1d\u6beb\u6ca1\u6709\u72b9\u7591\uff0c\u5c31\u5c06\u8fd9\u67c4\u5251\u8fd8\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u7684\u950b\u5229\uff0c\u7edd\u4e0d\u5728\u4ed6\u7684\u610f\u6599\u4e4b\u4e0b\uff0c\u5982\u679c\u4ed6\u638c\u4e2d\u63e1\u6709\u8fd9\u6837\u7684\u5668\uff0c\u672a\u5fc5\u7edd\u5bf9\u4e0d\u662f\u5676\u4f26\u7684\u654c\u624b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4eff\u4f5b\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u5676\u4f26\u8981\u4ed6\u4ea4\u8fd8\u8fd9\u67c4\u5251\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u7528\u8fd9\u67c4\u5251\u6740\u4ed6\u7684\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u2026\u2026 \u3000\u3000\u5915\u9633\u5df2\u9690\u6ca1\u5728\u9ad8\u8038\u7684\u57ce\u5821\u4e0e\u8fde\u7ef5\u7684\u96c9\u581e\u540e\uff0c\u53ea\u5269\u4e0b\u60e8\u78a7\u8272\u7684\u5251\u5149\u5728\u66ae\u8272\u82cd\u832b\u4e2d\u95ea\u52a8\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff1a\u201c\u4f60\u672c\u6765\u4e5f\u662f\u4e2a\u4f18\u79c0\u7684\u5e74\u9752\u4eba\uff0c\u5c31\u597d\u50cf\u666e\u677e\u4e00\u6837\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u4f60\u4e5f\u6b7b\u4e86\u3002\u6211\u7eb5\u7136\u4e0d\u6740\u4f60\uff0c\u4f60\u4e5f\u5df2\u548c\u6b7b\u4eba\u5168\u65e0\u5206\u522b\u3002\u201d \u3000\u3000\u4ed6\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u9053\uff1a\u201c\u6709\uff0c\u6211\u8fd8\u6709\u8bdd\u8bf4\uff0c\u8fd8\u6709\u4e8b\u8981\u95ee\u4f60\u3002\u201d \u3000\u3000\u5676\u4f26\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u903c\u89c6\u7740\u4ed6\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u4ed6\u8bf4\uff1a\u201c\u4f60\u6068\u6ce2\u5a03\uff0c\u6068\u5979\u6bc1\u4e86\u4f60\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u4f60\u4e5f\u6068\u4f60\u81ea\u5df1\uff0c\u5c31\u56e0\u4e3a\u4f60\u5b8c\u5168\u4e0d\u80fd\u963b\u6b62\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u63d0\u9ad8\u58f0\u97f3\uff0c\u5389\u58f0\u5730\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u963b\u6b62\u4ed6\u4eec\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u628a\u5979\u7559\u5728\u8fd9\u91cc\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u4eb2\u624b\u6740\u4e86\u5979\uff1f\u4f60\u7a76\u7adf\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5676\u4f26\u5587\u561b\u6ca1\u6709\u56de\u7b54\uff0c\u6ca1\u6709\u5f00\u53e3\uff0c\u638c\u4e2d\u7684\u5251\u5149\u5374\u95ea\u52a8\u5f97\u66f4\u5267\u70c8\u3002 \u3000\u3000\u96be\u9053\u4ed6\u7684\u624b\u5728\u6296\uff1f\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u4e8b\u53ef\u4ee5\u4f7f\u8fd9\u4f4d\u9ad8\u618e\u60ca\u9707\u98a4\u6296\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u8bdd\u950b\u66f4\u903c\u4eba\uff1a\u201c\u4f60\u660e\u660e\u53ef\u4ee5\u963b\u6b62\u8fd9\u4ef6\u4e8b\u53d1\u751f\u7684\uff0c\u90a3\u4e48\u666e\u677e\u6839\u672c\u5c31\u4e0d\u4f1a\u6b7b\uff0c\u4f60\u5fc3\u91cc\u4e00\u5b9a\u9690\u85cf\u7740\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u975e\u4f46\u4e0d\u6562\u53bb\u6740\u6ce2\u5a03\uff0c\u751a\u81f3\u8fde\u89c1\u90fd\u4e0d\u6562\u53bb\u89c1\u5979\u3002\u201d \u3000\u3000\u5676\u4f26\u5ffd\u7136\u5f00\u53e3\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u53bb\u6740\u4e86\u5979\uff1f\u201d\u4ed6\u95ee\u5c0f\u65b9\uff0c\u201c\u5982\u679c\u6211\u8981\u6740\u4f60\uff0c\u662f\u4e0d\u662f\u5e94\u8be5\u53bb\u6740\u4e86\u5979\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5c0f\u65b9\u7684\u56de\u7b54\u76f4\u63a5\u660e\u786e\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u60f3\u8981\u6ce2\u5a03\u6b7b\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u60f3\u6b7b\uff0c\u4ed6\u51fa\u4e86\u4e2a\u96be\u9898\u7ed9\u5676\u4f26\u3002 \u3000\u3000\u4ed6\u786e\u4fe1\u5676\u4f26\u4e5f\u8ddf\u4ed6\u4e00\u6837\uff0c\u7edd\u4e0d\u4f1a\u5bf9\u6ce2\u5a03\u4e0b\u624b\u7684\uff0c\u5426\u5219\u6ce2\u5a03\u65e9\u5df2\u6b7b\u4e86\u65e0\u6570\u6b21\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u6b21\u4ed6\u53c8\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u521a\u8bf4\u4e86\u90a3\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u5676\u4f26\u7626\u5f31\u7684\u8eab\u5b50\u5df2\u50cf\u662f\u4e00\u9635\u6e05\u98ce\u822c\u4ece\u4ed6\u9762\u524d\u63a0\u8fc7\u53bb\uff0c\u63a0\u5165\u4e86\u90a3\u95ee\u7985\u623f\u3002 \u3000\u3000\u7b49\u4ed6\u8ddf\u8fdb\u53bb\u65f6\uff0c\u5676\u4f26\u638c\u4e2d\u90a3\u67c4\u60e8\u78a7\u8272\u7684\u957f\u5251\uff0c\u5251\u950b\u5df2\u5728\u6ce2\u5a03\u54bd\u5589\u4e0a\u3002 \u3000\u3000\u5251\u5149\u7167\u7eff\u4e86\u6ce2\u5a03\u7684\u8138\uff0c\u5979\u7684\u8138\u4e0a\u5e76\u6ca1\u6709\u4e00\u70b9\u60ca\u614c\u6050\u60e7\u7684\u8868\u60c5\u3002 \u3000\u3000\u5979\u4e0d\u4fe1\u5676\u4f26\u4f1a\u4e0b\u624b\u3002 \u3000\u3000\u201c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d\u6ce2\u5a03\u6de1\u6de1\u5730\u95ee\uff0c\u201c\u96be\u9053\u4f60\u60f3\u6765\u6740\u6211\uff1f\u96be\u9053\u4f60\u5fd8\u8bb0\u4e86\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u5fd8\u8bb0\u4e86\u6211\u4eec\u4e4b\u95f4\u7684\u5bc6\u7ea6\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u5fd8\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5c31\u8be5\u77e5\u9053\uff0c\u4f60\u82e5\u6740\u4e86\u6211\uff0c\u4e0d\u4f46\u5fc5\u5c06\u540e\u6094\u7ec8\u751f\uff0c\u4f60\u7684\u7f6a\u5b7d\u4e5f\u6c38\u8fdc\u6ca1\u6cd5\u5b50\u6d17\u5f97\u6e05\u4e86\u3002\u201d \u3000\u3000\u6ce2\u5a03\u8bf4\u5f97\u5f88\u80af\u5b9a\uff0c\u80af\u5b9a\u5f97\u4ee4\u4eba\u4e0d\u80fd\u4e0d\u5403\u60ca\u3002 \u3000\u3000\u5979\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u4e00\u4e2a\u9b54\u5973\u548c\u4e00\u4e2a\u9ad8\u50e7\u95f4\uff0c\u4f1a\u6709\u4ec0\u4e48\u79d8\u5bc6\u7684\u7ea6\u5b9a\uff1f\u7ea6\u5b9a\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u5c0f\u65b9\u60f3\u4e0d\u901a\uff0c\u4e5f\u4e0d\u80fd\u76f8\u4fe1\u3002 \u3000\u3000\u53ef\u662f\u5676\u4f26\u5587\u561b\u81ea\u5df1\u5e76\u6ca1\u6709\u5426\u8ba4\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u6211\u4e0d\u80fd\u6740\u4f60\u7684\uff0c\u4f46\u662f\u6211\u5b81\u53ef\u6c38\u6ca6\u9b54\u52ab\uff0c\u4e5f\u8981\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u666e\u677e\u662f\u6211\u7684\u513f\u5b50\u3002\u201d\u5676\u4f26\u9053\uff1a\u201c\u6211\u4e8c\u5341\u516b\u5e74\u524d\uff0c\u4e5f\u9047\u5230\u8fc7\u4e00\u4e2a\u50cf\u4f60\u8fd9\u6837\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u6ce2\u5a03\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u662f\u56e0\u4e3a\u542c\u89c1\u4e86\u8fd9\u79d8\u5bc6\u800c\u5403\u60ca\uff0c\u800c\u662f\u56e0\u4e3a\u5979\u77e5\u9053\u5676\u4f26\u5587\u561b\u65e2\u7136\u80af\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u5979\uff0c\u5c31\u4e00\u5b9a\u5df2\u7ecf\u4e0b\u4e86\u51b3\u5fc3\u8981\u7f6e\u5979\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u770b\u51fa\u4e86\u8fd9\u4e00\u70b9\u3002\u4ed6\u4e0d\u4f46\u60ca\u8bb6\uff0c\u800c\u4e14\u6094\u6068\uff0c\u56e0\u4e3a\u5676\u4f26\u7684\u6740\u673a\uff0c\u662f\u88ab\u4ed6\u903c\u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u773c\u770b\u7740\u6ce2\u5a03\u56e0\u4ed6\u800c\u6b7b\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u8fd8\u672a\u523a\u4e0b\uff0c\u5c0f\u65b9\u5df2\u6251\u8fc7\u53bb\uff0c\u53f3\u624b\u731b\u5207\u5676\u4f26\u7684\u540e\u9888\uff0c\u5de6\u624b\u6025\u6263\u4ed6\u63e1\u5251\u7684\u624b\u8155\u8109\u95e8\u3002 \u3000\u3000\u5676\u4f26\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u4ed6\u4ee5\u5de6\u624b\u63e1\u5251\uff0c\u4ed6\u7684\u53f3\u81c2\u5173\u8282\u5ffd\u7136\u626d\u66f2\u53cd\u8f6c\uff0c\u53cd\u624b\u6253\u5c0f\u65b9\u7684\u8170\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u7edd\u5bf9\u4e0d\u80fd\u60f3\u5230\u4e00\u4e2a\u4eba\u7684\u624b\u81c2\u7adf\u80fd\u5728\u8fd9\u79cd\u90e8\u4f4d\u626d\u8f6c\uff0c\u4ece\u8fd9\u79cd\u65b9\u5411\u6253\u8fc7\u6765\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u5676\u4f26\u7684\u624b\u81c2\u626d\u8f6c\u65f6\uff0c\u4ed6\u7684\u4eba\u5df2\u88ab\u51fb\u5012\u3002 \u3000\u3000\u5251\u950b\u8ddd\u79bb\u6ce2\u5a03\u7684\u54bd\u5589\u5df2\u4e0d\u53ca\u4e24\u5bf8\u3002 \u3000\u3000\u5676\u4f26\u8fd9\u4e00\u5251\u523a\u5f97\u5f88\u6162\uff0c\u6291\u5236\u591a\u5e74\u7684\u60c5\u611f\u548c\u7231\u5fc3\u5ffd\u7136\u6d8c\u53d1\uff0c\u4ed6\u5bf9\u6ce2\u5a03\u7684\u4ec7\u6068\u4e5f\u8fdc\u6bd4\u522b\u4eba\u66f4\u6df1\u3002 \u3000\u3000\u4ed6\u8981\u770b\u7740\u8fd9\u4e2a\u6bc1\u4e86\u4ed6\u513f\u5b50\u7684\u9b54\u5973\u6162\u6162\u5730\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\u3002 \u3000\u3000\u73b0\u5728\u5df2\u7ecf\u518d\u6ca1\u6709\u4eba\u80fd\u633d\u56de\u6ce2\u5a03\u7684\u6027\u547d\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u51e0\u4e4e\u5df2\u4e0d\u5fcd\u518d\u770b\u3002\u60f3\u4e0d\u5230\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u5ffd\u7136\u53c8\u770b\u89c1\u4e86\u4e00\u9053\u5251\u5149\u95ea\u7535\u822c\u98de\u6765\uff0c\u76f4\u523a\u5676\u4f26\u540e\u9888\u4e0a\u7684\u5927\u8840\u7ba1\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u6765\u5f97\u592a\u5feb\uff0c\u523a\u5f97\u592a\u51c6\u3002 \u3000\u3000\u5676\u4f26\u4e0d\u5f97\u4e0d\u6551\u3002 \u3000\u3000\u4ed6\u7684\u5251\u53cd\u624b\u6325\u53bb\uff0c\u8fce\u4e0a\u4e86\u8fd9\u9053\u51cc\u7a7a\u98de\u51fb\u7684\u5251\u5149\u3002\u53cc\u5251\u76f8\u51fb\uff0c\u58f0\u5982\u9f99\u541f\uff0c\u98de\u6fc0\u51fa\u7684\u706b\u661f\uff0c\u5c31\u50cf\u662f\u9ed1\u591c\u65f6\u653e\u51fa\u7684\u70df\u82b1\u3002 \u3000\u3000\u63a5\u7740\uff0c\u53c8\u662f\u201c\u593a\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e00\u67c4\u5251\u659c\u659c\u5730\u9489\u5165\u4e86\u6a2a\u6881\u3002 \u3000\u3000\u53ea\u6709\u5251\uff0c\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u7adf\u662f\u88ab\u4eba\u8131\u624b\u98de\u63b7\u51fa\u6765\u7684\uff0c\u4eba\u8fd8\u5728\u7985\u623f\u5916\uff0c\u8131\u624b\u63b7\u51fa\u4e00\u5251\uff0c\u7adf\u6709\u8fd9\u79cd\u58f0\u52bf\u3001\u8fd9\u79cd\u901f\u5ea6\uff01\u5676\u4f26\u867d\u7136\u8fd8\u672a\u89c1\u5230\u8fd9\u4e2a\u4eba\uff0c\u5df2\u7ecf\u77e5\u9053\u4ed6\u7684\u53ef\u6015\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u5df2\u731c\u51fa\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\u3002\u867d\u7136\u4ed6\u4ece\u672a\u60f3\u5230\u8fd9\u4e2a\u4eba\u4f1a\u6765\u6551\u6ce2\u5a03\uff0c\u4f46\u662f\u4ed6\u8ba4\u5f97\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u659c\u63d2\u5728\u6a2a\u6881\u4e0a\u7684\u5251\uff0c\u8d6b\u7136\u7adf\u662f\u4ed6\u7684\u201c\u9b54\u773c\u201d\u3002 \u3000\u3000\u9634\u6697\u7684\u7985\u623f\uff0c\u96ea\u767d\u7684\u7a97\u7eb8\uff0c\u7a97\u6237\u534a\u5f00\uff0c\u5251\u81ea\u7a97\u5916\u98de\u6765\uff0c\u4eba\u5462\uff1f \u3000\u3000\u201c\u9b54\u773c\u201d\u9489\u5165\u6a2a\u6881\u65f6\uff0c\u5676\u4f26\u5587\u561b\u5df2\u7a7f\u7a97\u800c\u51fa\u3002\u5c0f\u65b9\u53ea\u770b\u89c1\u4e00\u9053\u78a7\u7eff\u7684\u5251\u5149\u98de\u8679\u822c\u7a7f\u51fa\u7a97\u6237\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u67af\u7626\u7684\u8eab\u5b50\u5df2\u6eb6\u4eba\u5251\u5149\u4e2d\uff0c\u4ed6\u7684\u4eba\u5df2\u4e0e\u5251\u76f8\u5408\uff0c\u51e0\u4e4e\u5df2\u5230\u8fbe\u4f20\u8bf4\u4e2d\u201c\u8eab\u5251\u5408\u4e00\u201d\u7684\u65e0\u4e0a\u5999\u5883\u3002 \u3000\u3000\u4ed6\u7684\u201c\u8d64\u677e\u201d\u4e5f\u662f\u5251\u4e2d\u7684\u795e\u54c1\u3002 \u3000\u3000\u535c\u9e70\u5982\u679c\u8fd8\u5728\u7985\u623f\u5916\uff0c\u7528\u4ec0\u4e48\u6765\u62b5\u6321\u8fd9\u4e00\u5251\uff1f \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u8dc3\u8d77\uff0c\u53bb\u6458\u6881\u4e0a\u7684\u5251\uff0c\u5e0c\u671b\u80fd\u53ca\u65f6\u5c06\u8fd9\u67c4\u5251\u4ea4\u7ed9\u535c\u9e70\u3002 \u3000\u3000\u4ed6\u7684\u624b\u8fd8\u6ca1\u6709\u4f38\u51fa\u53bb\uff0c\u6a2a\u6881\u4e0a\u7684\u5c4b\u74e6\u5ffd\u7136\u788e\u88c2\uff0c\u4e00\u53ea\u624b\u4ece\u7834\u6d1e\u4e2d\u4f38\u4e0b\u6765\uff0c\u652b\u53bb\u4e86\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u4e00\u53ea\u7626\u524a\u800c\u6709\u529b\u7684\u624b\uff0c\u6307\u7532\u4fee\u526a\u5f97\u975e\u5e38\u6574\u9f50\u5e72\u51c0\u3002 \u3000\u3000\u5c0f\u65b9\u8ba4\u5f97\u8fd9\u53ea\u624b\uff0c\u4ed6\u4e5f\u66fe\u7ecf\u63e1\u8fc7\u8fd9\u53ea\u624b\u3002 \u3000\u3000\u6765\u7684\u4eba\u679c\u7136\u662f\u535c\u9e70\u3002 \u3000\u3000\u535c\u9e70\u4e3a\u4ec0\u4e48\u8981\u6765\u6551\u6ce2\u5a03\uff1f\u662f\u4e3a\u4e86\u5c0f\u65b9\uff0c\u8fd8\u662f\u4e3a\u4e86\u53e6\u4e00\u79cd\u81f3\u4eca\u8fd8\u6ca1\u6709\u4eba\u77e5\u9053\u7684\u539f\u56e0\uff1f \u3000\u3000\u5c0f\u65b9\u8fd8\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u5916\u9762\u53c8\u54cd\u8d77\u4e86\u4e00\u58f0\u9f99\u541f\u3002 \u3000\u3000\u201c\u8d64\u677e\u201d\u4e0e\u201c\u9b54\u773c\u201d\u53cc\u5251\u518d\u6b21\u76f8\u51fb\uff0c\u9f99\u541f\u58f0\u8fd8\u672a\u505c\u6b47\uff0c\u5c0f\u65b9\u4e5f\u5df2\u5230\u4e86\u7985\u623f\u5916\u3002 \u3000\u3000\u66ae\u8272\u5df2\u6df1\u6c89\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u4e0d\u89c1\u535c\u9e70\u7684\u4eba\uff0c\u4e5f\u770b\u4e0d\u89c1\u5676\u4f26\uff0c\u53ea\u770b\u89c1\u4e24\u9053\u5251\u5149\u6e38\u9f99\u822c\u76d8\u65cb\u98de\u821e\uff0c\u68ee\u68ee\u7684\u5251\u6c14\u4e2d\uff0c\u53e4\u6811\u4e0a\u7684\u6728\u53f6\u8427\u8427\u800c\u843d\uff0c\u5c0f\u65b9\u7684\u8863\u8882\u4e5f\u5df2\u88ab\u632f\u8d77\u3002 \u3000\u3000\u8fd9\u662f\u5c0f\u65b9\u7b2c\u4e00\u6b21\u770b\u89c1\u535c\u9e70\u7684\u5251\u672f\u3002 \u3000\u3000\u4ed6\u7ec3\u5251\u5341\u4f59\u5e74\uff0c\u81f3\u4eca\u624d\u77e5\u9053\u5251\u672f\u7684\u9886\u57df\u7adf\u662f\u5982\u6b64\u535a\u5927\u3002 \u3000\u3000\u4ed6\u75f4\u75f4\u5730\u770b\u7740\uff0c\u4e5f\u89c9\u5f97\u624b\u8db3\u51b0\u51b7\uff0c\u5fc3\u4e5f\u5f00\u59cb\u53d1\u51b7\uff0c\u76f4\u51b7\u5230\u8dbe\u5c16\u8db3\u5e95\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u8c01\u80fd\u80dc\uff1f \u3000\u3000\u78a7\u7eff\u7684\u5251\u6c14\u770b\u6765\u4eff\u4f5b\u66f4\u76db\u4e8e\u201c\u9b54\u773c\u201d\u7684\u5bd2\u5149\uff0c\u98de\u65cb\u8f6c\u6298\u95f4\u4eff\u4f5b\u4e5f\u66f4\u77eb\u6377\u7075\u654f\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u65b9\u5374\u5ffd\u7136\u53d1\u89c9\u80dc\u7684\u5fc5\u5c06\u662f\u535c\u9e70\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u8d64\u677e\u201d\u7684\u5251\u6c14\u867d\u76db\uff0c\u5374\u663e\u5f97\u6709\u70b9\u7126\u8e81\u6025\u8fdb\u3002 \u3000\u3000\u6025\u8fdb\u8005\u5fc5\u4e0d\u80fd\u6301\u4e45\u3002 \u3000\u3000\u4ed6\u679c\u7136\u6ca1\u6709\u770b\u9519\uff0c\u201c\u8d64\u677e\u201d\u5251\u4e0a\u7684\u5149\u534e\u867d\u7136\u66f4\u9c9c\u8273\u7fe0\u7eff\uff0c\u5251\u98ce\u4e2d\u5374\u5df2\u6ca1\u6709\u90a3\u79cd\u51cc\u5389\u7684\u6740\u6c14\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u53c8\u662f\u201c\u545b\u201d\u7684\u4e00\u58f0\u9f99\u541f\uff0c\u53cc\u5251\u4e09\u6b21\u62cd\u51fb\u3002 \u3000\u3000\u9f99\u541f\u58f0\u6b47\uff0c\u6f2b\u5929\u5251\u5149\u4e5f\u5ffd\u7136\u6d88\u5931\uff0c\u53e4\u6811\u6728\u53f6\u5df2\u79c3\uff0c\u7985\u9662\u4e2d\u5ffd\u53c8\u53d8\u6210\u4e00\u7247\u6b7b\u5bc2\u3002 \u3000\u3000\u5676\u4f26\u5587\u561b\u4e0d\u77e5\u4f55\u65f6\u5df2\u5750\u4e0b\uff0c\u76d8\u819d\u5728\u843d\u53f6\u4e0a\uff0c\u66ae\u8272\u4e2d\uff0c\u53c8\u53d8\u5f97\u548c\u5c0f\u65b9\u7b2c\u4e00\u773c\u770b\u89c1\u4ed6\u65f6\u90a3\u4e48\u5e73\u9759\u9634\u6697\u8870\u5f31\u3002 \u3000\u3000\u201c\u8d64\u677e\u201d\u5df2\u4e0d\u5728\u4ed6\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u638c\u4e2d\u65e0\u5251\uff0c\u5fc3\u4e2d\u4e5f\u5df2\u65e0\u5251\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u4e0d\u662f\u521a\u624d\u90a3\u4f4d\u80fd\u4ee5\u6c14\u9a6d\u5251\u6740\u4eba\u4e8e\u7728\u773c\u95f4\u7684\u5251\u5ba2\u3002 \u3000\u3000\u4ed6\u653e\u4e0b\u4ed6\u7684\u5251\u65f6\uff0c\u5c31\u5df2\u91cd\u5165\u7985\u9662\uff0c\u53c8\u53d8\u4e3a\u4e00\u4f4d\u5fc3\u5982\u6b62\u6c34\u7684\u9ad8\u50e7\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u7684\u623e\u6c14\u548c\u6740\u673a\uff0c\u60c5\u4e0e\u4ec7\uff0c\u7231\u4e0e\u6068\uff0c\u90fd\u5df2\u968f\u7740\u4ed6\u7684\u5251\u6c14\u4e00\u6cc4\u800c\u51fa\uff0c\u5c31\u5728\u5c0f\u65b9\u89c9\u5f97\u4ed6\u5251\u98ce\u4e2d\u5df2\u65e0\u6740\u6c14\u65f6\uff0c\u4ed6\u5fc3\u4e2d\u7684\u7985\u5883\u53c8\u8fdb\u4e86\u4e00\u5c42\u3002 \u3000\u3000\u535c\u9e70\u9759\u9759\u5730\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u9759\u9759\u5730\u770b\u7740\u4ed6\uff0c\u795e\u8272\u4e25\u8083\u606d\u8c28\uff0c\u773c\u4e2d\u5145\u6ee1\u5c0a\u656c\uff0c\u5ffd\u7136\u5408\u4ec0\u9876\u793c\uff1a \u3000\u3000\u201c\u606d\u559c\u5927\u5e08\u3002\u201d \u3000\u3000\u201c\u4e3a\u4f55\u606d\u559c\uff1f\u4f55\u559c\u4e4b\u6709\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u5df2\u5728\u5251\u4e2d\u609f\u9053\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u606d\u559c\u5927\u5e08\u7684\u4fee\u4e3a\u53c8\u6709\u7cbe\u8fdb\u3002\u201d \u3000\u3000\u5676\u4f26\u5587\u561b\u5fae\u7b11\uff0c\u6162\u6162\u5730\u5408\u4e0a\u773c\u775b\u3002 \u3000\u3000\u201c\u4f60\u597d\u3002\u201d\u4ed6\u4ece\u5bb9\u6325\u624b\uff0c\u201c\u4f60\u53bb\u3002\u201d \u3000\u3000\u535c\u9e70\u8fd8\u6ca1\u6709\u8d70\uff0c\u5676\u4f26\u5587\u561b\u5ffd\u53c8\u5f20\u5f00\u773c\uff0c\u5927\u58f0\u4f5c\u72ee\u5b50\u543c\uff01 \u3000\u3000\u201c\u4e3a\u4f55\u8981\u4f60\u53bb\uff1f\u4e3a\u4ec0\u4e48\u6211\u4e0d\u80fd\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u51fa\uff0c\u4ed6\u9634\u6697\u7684\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e00\u5c42\u7965\u548c\u7684\u795e\u8272\u3002 \u3000\u3000\u535c\u9e70\u518d\u6b21\u5408\u4ec0\u9876\u793c\uff0c\u5676\u4f26\u5587\u561b\u5df2\u8e0f\u7740\u843d\u53f6\uff0c\u8d70\u5165\u6df1\u6c89\u7684\u66ae\u8272\u91cc\u3002 \u3000\u3000\u591c\u7a7a\u4e2d\u5ffd\u7136\u6709\u661f\u5347\u8d77\u3002 \u3000\u3000\u201c\u8d64\u677e\u201d\u8fd8\u7559\u5728\u5730\u4e0a\uff0c\u5149\u534e\u78a7\u7eff\u7684\u5251\u950b\uff0c\u5df2\u53d8\u5f97\u9eef\u6de1\u65e0\u5149\u3002 \u3000\u3000\u540d\u5251\u6b63\u5982\u5251\u5ba2\uff0c\u4e5f\u662f\u4e0d\u80fd\u8d25\u7684\u3002 \u3000\u3000\u535c\u9e70\u76ee\u9001\u5676\u4f26\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u5ffd\u7136\u8f7b\u8f7b\u53f9\u606f\u3002 \u3000\u3000\u201c\u4ed6\u6ca1\u6709\u8d25\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u5c31\u7b97\u8d25\u4e86\uff0c\u4e5f\u4e0d\u662f\u8d25\u5728\u6211\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u4e0d\u662f\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u8d25\uff0c\u53ea\u56e0\u4e3a\u4ed6\u6839\u672c\u6ca1\u6709\u6740\u6211\u7684\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u60f3\u7528\u6211\u6fc0\u53d1\u4ed6\u7684\u5251\u6c14\uff0c\u6cc4\u51fa\u4ed6\u5fc3\u4e2d\u7684\u623e\u6c14\u4e0e\u6740\u673a\u3002\u201d \u3000\u3000\u535c\u9e70\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u6839\u672c\u6ca1\u6709\u80dc\u6211\u4e4b\u610f\uff0c\u53c8\u600e\u4e48\u80fd\u7b97\u662f\u8d25\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u5b89\u5fcd\u591a\u5e74\u7684\u9ad8\u50e7\uff0c\u5ffd\u7136\u53d1\u89c9\u5fc3\u4e2d\u7adf\u6709\u6fc0\u60c5\u65e0\u6cd5\u6291\u5236\u65f6\uff0c\u5f80\u5f80\u5728\u4e00\u77ac\u95f4\u5c31\u4f1a\u5815\u5165\u9b54\u52ab\u3002 \u3000\u3000\u201c\u9b54\u201d\u4e0e\u201c\u9053\u201d\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u4e5f\u6b63\u5982\u7231\u4e0e\u6068\u4e00\u6837\uff0c\u4ec5\u5728\u4e00\u7ebf\u95f4\u3002 \u3000\u3000\u73b0\u5728\u5251\u5ba2\u5df2\u8d25\uff0c\u9ad8\u50e7\u5374\u5df2\u609f\u9053\u4e86\u3002 \u3000\u3000\u535c\u9e70\u51dd\u89c6\u7740\u5c0f\u65b9\uff0c\u773c\u4e2d\u53c8\u9732\u51fa\u6b23\u6170\u4e4b\u8272\uff0c\u4ed6\u770b\u5f97\u51fa\u5c0f\u65b9\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5374\u5f88\u4e71\u3002 \u3000\u3000\u4ed6\u6709\u5f88\u591a\u8bdd\u8981\u95ee\u535c\u9e70\u535c\u4ed6\u5df2\u89c9\u5bdf\u5230\u6ce2\u5a03\u4e0e\u535c\u9e70\u4e4b\u95f4\uff0c\u4e5f\u6709\u79cd\u81f3\u4eca\u8fd8\u6ca1\u6709\u4eba\u77e5\u9053\u7684\u795e\u79d8\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u95ee\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4e0d\u77e5\u9053\u5e94\u8be5\u5982\u4f55\u95ee\u3002 \u3000\u3000\u535c\u9e70\u6ca1\u6709\u8bf4\uff0c\u662f\u4e0d\u662f\u4e5f\u56e0\u4e3a\u4e0d\u77e5\u8be5\u5982\u4f55\u8bf4\uff1f \u3000\u3000\u534a\u5f00\u7684\u7a97\u6237\u5df2\u960e\u8d77\uff0c\u7985\u623f\u91cc\u6ca1\u6709\u71c3\u706f\uff0c\u4e5f\u6ca1\u6709\u52a8\u9759\uff0c\u53ea\u6709\u6ce2\u5a03\u4e00\u4e2a\u4eba\u9759\u5750\u5728\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u5979\u4e3a\u4ec0\u4e48\u8fd8\u8981\u7559\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u535c\u9e70\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u591c\u7a7a\u4e2d\u7b2c\u4e00\u9897\u5347\u8d77\u7684\u661f\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u4ed6\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u8fd8\u6709\u4e00\u4e2a\u6253\u4e0d\u5f00\u7684\u7ed3\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u4e2a\u9e70\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff1a\u201c\u5982\u679c\u4f60\u771f\u60f3\u77e5\u9053\u8fd9\u5176\u4e2d\u7684\u79d8\u5bc6\uff0c\u5c31\u8ddf\u6211\u8d70\uff0c\u53ef\u662f\u6211\u529d\u4f60\uff0c\u6709\u4e9b\u4e8b\u8fd8\u662f\u4e0d\u77e5\u9053\u7684\u597d\u3002\u201d \u3000\u3000\u8fd9\u6b21\u5c0f\u65b9\u6ca1\u6709\u63a5\u53d7\u535c\u9e70\u7684\u529d\u544a\u3002 \u3000\u3000\u4ed6\u8ddf\u7740\u535c\u9e70\u8d70\u4e86\uff0c\u8d70\u5411\u4e1c\u65b9\u7684\u5c0f\u5c4b\u3002 \u3000\u3000\u661f\u5149\u5728\u6c99\u6f20\u4e2d\u770b\u6765\u4eff\u4f5b\u66f4\u660e\u4eae\uff0c\u4ed6\u4eec\u5df2\u7ecf\u5728\u6c99\u6f20\u4e2d\u5954\u9a70\u4e86\u4e09\u5929\u3002 \u3000\u3000\u5c0f\u65b9\u60f3\u4e0d\u5230\u535c\u9e70\u4e3a\u4ec0\u4e48\u53c8\u5c06\u4ed6\u5e26\u5165\u6c99\u6f20\u6765\uff0c\u4ed6\u4e5f\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u535c\u9e70\u8fd9\u6b21\u4e00\u5b9a\u4f1a\u7ed9\u4ed6\u4e00\u4e2a\u660e\u786e\u5b8c\u6574\u7684\u7b54\u6848\uff0c\u8ba9\u4ed6\u80fd\u89e3\u5f00\u5fc3\u91cc\u8fd9\u4e2a\u7ed3\u3002 \u3000\u3000\u4ed6\u4eec\u5feb\u9a6c\u5954\u9a70\uff0c\u4f11\u606f\u7684\u65f6\u5019\u5f88\u5c11\u3002\u8fd9\u4e09\u5929\u4e2d\u4ed6\u4eec\u8d70\u7684\u8def\uff0c\u5df2\u7ecf\u6bd4\u4e0a\u4e00\u6b21\u5341\u5929\u4e2d\u8d70\u5f97\u66f4\u591a\u3002 \u3000\u3000\u65e0\u60c5\u7684\u6c99\u6f20\u8fd8\u662f\u540c\u6837\u65e0\u60c5\uff0c\u7b2c\u4e09\u5929\u9ec4\u660f\uff0c\u4ed6\u4eec\u53c8\u56de\u5230\u90a3\u4e00\u7247\u98ce\u5316\u7684\u5ca9\u77f3\u95f4\u3002 \u3000\u3000\u5c0f\u65b9\u6c38\u8fdc\u5fd8\u4e0d\u4e86\u8fd9\u5730\u65b9\uff0c\u56e0\u4e3a\u8fd9\u91cc\u6b63\u662f\u4ed6\u521d\u9047\u6ce2\u5a03\u7684\u5730\u65b9\uff0c\u4e5f\u6b63\u662f\u536b\u5929\u9e4f\u4ed6\u4eec\u7684\u9a7b\u624e\u5730\u3002\u73b0\u5728\u90a3\u5e10\u7bf7\u867d\u7136\u5df2\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u5728\u90a3\u5e10\u7bf7\u4e2d\u53d1\u751f\u7684\u4e8b\uff0c\u5374\u662f\u5c0f\u65b9\u8fd9\u4e00\u751f\u6c38\u96be\u5fd8\u6000\u7684\u3002 \u3000\u3000\u535c\u9e70\u5df2\u4e0b\u9a6c\uff0c\u548c\u5c0f\u65b9\u5206\u4eab\u4e86\u4e00\u5757\u5341\u725b\u8089\u548c\u4e00\u888b\u4e73\u9177\u9152\u3002 \u3000\u3000\u8fd9\u4e09\u5929\u4ed6\u4e00\u76f4\u5f88\u5c11\u5f00\u53e3\uff0c\u4f46\u662f\u6bcf\u5f53\u9152\u540e\uff0c\u5c0f\u65b9\u5c31\u4f1a\u542c\u89c1\u4ed6\u53c8\u5728\u4f4e\u5531\u90a3\u66f2\u60b2\u6b4c\u3002\u90a3\u79cd\u7537\u5b50\u6c49\u7684\u60c5\u6000\uff0c\u90a3\u79cd\u82cd\u51c9\u4e2d\u5e26\u7740\u8c6a\u8fc8\u7684\u610f\u5883\uff0c\u603b\u662f\u6bd4\u9152\u66f4\u4ee4\u4eba\u9189\u3002 \u3000\u3000\u201c\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u518d\u5f80\u524d\u8d70\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u518d\u5f80\u524d\u8d70\u4e86\u3002\u201d\u535c\u9e70\u56de\u7b54\uff0c\u201c\u8fd9\u91cc\u5c31\u662f\u6211\u4eec\u7684\u5730\u5934\u3002\u201d \u3000\u3000\u201c\u4f60\u5e26\u6211\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\u3002 \u3000\u3000\u8fd9\u91cc\u65e2\u7136\u662f\u4ed6\u4eec\u7684\u76ee\u7684\u5730\uff0c\u96be\u9053\u6240\u6709\u95ee\u9898\u7684\u7b54\u6848\u90fd\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u535c\u9e70\u8fd8\u6ca1\u6709\u628a\u7b54\u6848\u7ed9\u4ed6\uff0c\u5374\u4ece\u9a6c\u978d\u65c1\u7684\u4e00\u4e2a\u9769\u56ca\u91cc\u62ff\u51fa\u4e86\u4e24\u628a\u94c1\u9504\uff0c\u629b\u4e86\u4e00\u628a\u7ed9\u5c0f\u65b9\u3002 \u3000\u3000\u4ed6\u8981\u5c0f\u65b9\u8ddf\u4ed6\u4e86\u8d77\u6316\u5730\u3002 \u3000\u3000\u96be\u9053\u4ed6\u5df2\u5c06\u95ee\u9898\u7684\u7b54\u6848\u57cb\u85cf\u5728\u5730\u4e0b\uff1f \u3000\u3000\u591c\u6e10\u6df1\u3002 \u3000\u3000\u4ed6\u4eec\u6316\u5f97\u4e5f\u6e10\u6df1\uff0c\u5df2\u7ecf\u6316\u8fc7\u4e86\u4e00\u5c42\u677e\u8f6f\u7684\u6c99\u783e\uff0c\u53c8\u6316\u8fc7\u4e86\u4e00\u5c42\u98ce\u5316\u7684\u5ca9\u77f3\u3002\u5ffd\u7136\u95f4\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5c0f\u65b9\u611f\u89c9\u5230\u81ea\u5df1\u624b\u91cc\u7684\u9504\u5934\u6316\u5230\u4e86\u4e00\u5c42\u575a\u786c\u7684\u91d1\u5c5e\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u770b\u89c1\u4e86\u5ca9\u77f3\u4e4b\u4e2d\u6709\u91d1\u5149\u5728\u95ea\u52a8\u3002 \u3000\u3000\u662f\u9ec4\u91d1\uff01 \u3000\u3000\u8fd9\u4e00\u7247\u5ca9\u77f3\u95f4\uff0c\u5730\u4e0b\u5168\u90fd\u662f\u9ec4\u91d1\u3002 \u3000\u3000\u535c\u9e70\u629b\u4e0b\u9504\u5934\uff0c\u9762\u5bf9\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u4f60\u603b\u8be5\u660e\u767d\u6211\u4e3a\u4ec0\u4e48\u8981\u5e26\u4f60\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u8fd8\u5f88\u5e73\u9759\uff0c\u201c\u5bcc\u8d35\u795e\u4ed9\u5415\u4e09\u5931\u52ab\u7684\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\uff0c\u5168\u90fd\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u662f\u4f60\u57cb\u5728\u8fd9\u91cc\u7684\uff1f\u201d \u3000\u3000\u535c\u9e70\uff1a\u201c\u662f\u6211\uff0c\u6211\u5c31\u662f\u732b\u76d7\u3002\u201d \u3000\u3000\u5c0f\u65b9\u867d\u7136\u65e9\u5df2\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u4e0d\u5403\u60ca\u3002 \u3000\u3000\u535c\u9e70\u51dd\u89c6\u7740\u4ed6\uff0c\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u4eec\u90a3\u961f\u4f0d\u91cc\uff0c\u6bcf\u4e2a\u4eba\u90fd\u662f\u732b\u76d7\uff0c\u4ed6\u4eec\u624d\u771f\u6b63\u662f\u4e45\u7ecf\u8bad\u7ec3\u3001\u767e\u6218\u4e0d\u6b7b\u7684\u6218\u58eb\uff0c\u536b\u5929\u9e4f\u5c5e\u4e0b\u90a3\u4e9b\u4eba\u8ddf\u4ed6\u4eec\u6bd4\u8d77\u6765\uff0c\u53ea\u80fd\u7b97\u662f\u521d\u5b66\u5200\u5251\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u4e2d\u5e76\u6ca1\u6709\u8ba5\u6d88\u4e4b\u610f\uff0c\u56e0\u4e3a\u4ed6\u8bf4\u7684\u662f\u4e8b\u5b9e\uff1a\u201c\u536b\u5929\u9e4f\u60f3\u4e0d\u5230\u6211\u4eec\u6839\u672c\u4e0d\u60f3\u628a\u8fd9\u6279\u9ec4\u91d1\u8fd0\u51fa\u6c99\u6f20\u3002\u201d \u3000\u3000\u201c\u6c38\u8fdc\u90fd\u4e0d\u60f3\u8fd0\u51fa\u53bb\uff1f\u201d \u3000\u3000\u201c\u6c38\u8fdc\uff01\u201d \u3000\u3000\u535c\u9e70\u7684\u56de\u7b54\u6781\u4e3a\u80af\u5b9a\uff0c\u5c0f\u65b9\u5374\u60f3\u4e0d\u901a\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u8d39\u5c3d\u82e6\u5fc3\u76d7\u52ab\u8fd9\u6279\u9ec4\u91d1\uff0c\u5f53\u7136\u662f\u4e3a\u4e86\u9ec4\u91d1\u7684\u4ef7\u503c\u3002 \u3000\u3000\u5982\u679c\u628a\u9ec4\u91d1\u6c38\u8fdc\u57cb\u5728\u5730\u4e0b\uff0c\u9ec4\u91d1\u5c82\u975e\u4e5f\u53d8\u5f97\u548c\u6c99\u77f3\u5c18\u571f\u65e0\u5f02\uff1f \u3000\u3000\u535c\u9e70\u4e0d\u7b49\u5c0f\u65b9\u95ee\u51fa\u6765\uff0c\u5df2\u7ecf\u5148\u56de\u7b54\u4e86\u8fd9\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u201c\u6211\u4eec\u5e76\u4e0d\u60f3\u8981\u8fd9\u6279\u9ec4\u91d1\u3002\u201d\u535c\u9e70\u9053\uff0c\u201c\u6211\u4eec\u52ab\u8d70\u8fd9\u6279\u9ec4\u91d1\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u4eec\u4e5f\u4e0d\u80fd\u8ba9\u5415\u4e09\u4ed6\u4eec\u5229\u7528\u8fd9\u6279\u9ec4\u91d1\u53bb\u5bf9\u4ed8\u522b\u4eba\u3002\u201d \u3000\u3000\u201c\u522b\u4eba\uff1f\u201d\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff0c\u201c\u522b\u4eba\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u8fd9\u4e24\u5929\u4f60\u5929\u5929\u90fd\u80fd\u770b\u5f97\u89c1\u7684\u90a3\u4e9b\u4eba\u3002\u201d\u535c\u9e70\u9053\uff0c\u201c\u4e5f\u5c31\u662f\u6ce2\u5a03\u3001\u73ed\u5bdf\u5df4\u90a3\u4ed6\u4eec\u7684\u65cf\u4eba\u548c\u59d0\u59b9\u5144\u5f1f\u3002\u201d \u3000\u3000\u201c\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4ed8\u4ed6\u4eec\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\uff0c\u201c\u51c6\u5907\u600e\u6837\u53bb\u5bf9\u4ed8\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u535c\u9e70\u5148\u8981\u5c0f\u65b9\u5c06\u6316\u6398\u51fa\u7684\u6c99\u77f3\u91cd\u65b0\u57cb\u597d\uff0c\u624d\u5f00\u59cb\u53d9\u8bf4\u8fd9\u4ef6\u4e8b\uff1a\u201c\u4ed6\u8981\u63a8\u7ffb\u85cf\u4eba\u4eec\u5df2\u4fe1\u5949\u767e\u5e74\u7684\u5b97\u6559\uff0c\u8981\u523a\u6740\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u7684\u6d3b\u4f5b\uff0c\u8981\u5728\u8fd9\u91cc\u5efa\u7acb\u4ed6\u81ea\u5df1\u7684\u5b97\u6559\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u6781\u5e9e\u5927\u60ca\u4eba\u7684\u8ba1\u5212\uff0c\u5415\u4e09\u4e0d\u62e9\u624b\u6bb5\u6765\u505a\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u56e0\u4e3a \u3000\u3000\u201c\u4ed6\u4fe1\u5949\u7684\u662f\u62dc\u706b\u6559\uff0c\u4ed6\u7684\u7236\u4eb2\u662f\u6ce2\u65af\u4eba\uff0c\u662f\u4e2a\u72c2\u70ed\u7684\u62dc\u706b\u6559\u5f92\u3002\u201d\u535c\u9e70\u9053\uff0c\u201c\u6240\u4ee5\u4ed6\u8981\u7528\u62dc\u706b\u6559\u53bb\u53d6\u4ee3\u5587\u561b\u5728\u897f\u85cf\u4e2d\u7684\u5730\u4f4d\u3002\u201d \u3000\u3000\u4ed6\u7684\u6001\u5ea6\u6781\u4e25\u8083\uff1a\u201c\u4f46\u662f\u8fd9\u79cd\u5b97\u6559\u4fe1\u4ef0\u5df2\u5728\u85cf\u4eba\u5fc3\u4e2d\u6839\u6df1\u8482\u56fa\uff0c\u6240\u4ee5\u5415\u4e09\u8ba1\u5212\u5982\u679c\u5b9e\u73b0\u4e86\uff0c\u897f\u85cf\u5883\u4e2d\u5fc5\u5c06\u6c38\u65e0\u5b81\u65e5\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4eec\u4e0d\u80fd\u8ba9\u4ed6\u7684\u8ba1\u5212\u5b9e\u73b0\u3002\u201d \u3000\u3000\u201c\u7edd\u4e0d\u80fd\u3002\u201d\u535c\u9e70\u8bf4\u5f97\u66f4\u575a\u51b3\uff0c\u201c\u4e3a\u4e86\u963b\u6320\u4ed6\uff0c\u6211\u4eec\u4e5f\u4e0d\u62e9\u624b\u6bb5\uff0c\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\uff0c\u535c\u9e70\u53c8\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u727a\u7272\u7684\u5c31\u662f\u6ce2\u5a03\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u727a\u7272\u6700\u5927\u7684\u5c31\u662f\u5979\u3002\u201d \u3000\u3000\u201c\u5979\u624d\u662f\u73ed\u5bdf\u5df4\u90a3\u8bf4\u7684\u90a3\u4e2a\u4e3a\u4e86\u65cf\u4eba\u800c\u727a\u7272\u81ea\u5df1\u7684\u5973\u4eba\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u6f5c\u4f0f\u5230\u5415\u4e09\u7ec4\u7ec7\u5185\u90e8\u53bb\u505a\u5978\u7ec6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5979\u662f\u7684\u3002\u201d \u3000\u3000\u535c\u9e70\u9053\uff1a\u201c\u8fd9\u79d8\u5bc6\u6211\u4eec\u7edd\u4e0d\u80fd\u8ba9\u522b\u4eba\u77e5\u9053\uff0c\u6240\u4ee5\u5728\u90a3\u4e0d\u7965\u7684\u2018\u9ed1\u7fbd\u4e4b\u5e10\u2019\u4e2d\uff0c\u6211\u53ea\u6709\u8ba9\u4f60\u8bef\u4f1a\u5979\uff0c\u5728\u2018\u6b7b\u9888\u2019\u5916\u90a3\u4e00\u6218\u4e2d\u6211\u4eec\u4e5f\u7edd\u4e0d\u80fd\u8ba9\u5979\u8d70\u51fa\u7b2c\u4e09\u9876\u8f7f\u5b50\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u5df2\u6e10\u6e10\u660e\u767d\u3002 \u3000\u3000\u201c\u6240\u4ee5\u5676\u4f26\u624d\u80af\u5979\u4f4f\u5728\u5e03\u8fbe\u62c9\u5bab\u91cc\uff0c\u6240\u4ee5\u4f60\u624d\u4f1a\u53bb\u6551\u5979\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u7edd\u4e0d\u80fd\u8ba9\u5979\u6b7b\u5728\u5676\u4f26\u624b\u91cc\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5676\u4f26\u62b1\u61be\u7ec8\u751f\u3002\u201d\u535c\u9e70\u9053\uff0c\u201c\u4e3a\u4e86\u5676\u4f26\u7684\u5b97\u6559\uff0c\u5979\u7684\u727a\u7272\u5df2\u592a\u5927\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u4e2d\u5ffd\u7136\u5145\u6ee1\u60b2\u4f24\uff1a\u201c\u5979\u975e\u4f46\u4e0d\u60dc\u81ea\u5df1\uff0c\u751a\u81f3\u4e0d\u60dc\u727a\u7272\u5979\u6240\u7231\u7684\u4eba\u3002\u201d \u3000\u3000\u2014\u2014\u6ce2\u5a03\u6700\u7231\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u95ee\uff0c\u4e5f\u4e0d\u5fc5\u518d\u95ee\u3002 \u3000\u3000\u5415\u4e09\u5f53\u7136\u8981\u4e3a\u81ea\u5df1\u7684\u72ec\u751f\u5b50\u62a5\u4ec7\u3002\u4e3a\u4e86\u53d6\u5f97\u5415\u4e09\u7684\u4fe1\u4efb\uff0c\u6ce2\u5a03\u53ea\u6709\u727a\u7272\u5c0f\u65b9\uff0c\u5979\u81ea\u5df1\u4e0d\u5fcd\u4e0b\u624b\uff0c\u53ea\u6709\u8981\u666e\u677e\u53bb\u66ff\u5979\u505a\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\uff0c\u4e3a\u4e86\u4e00\u79cd\u66f4\u4f1f\u5927\u7684\u7231\u548c\u4fe1\u4ef0\uff0c\u7adf\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\u5fc3\u7231\u7684\u7537\u4eba\uff0c\u867d\u7136\u8fd9\u4e2a\u7537\u4eba\u662f\u5b8c\u5168\u65e0\u8f9c\u7684\uff0c\u5979\u4e5f\u7f6e\u4e4b\u4e0d\u987e\u3002 \u3000\u3000\u5979\u8fd9\u6837\u505a\uff0c\u6709\u8c01\u80fd\u8bf4\u5979\u9519\uff1f \u3000\u3000\u5c0f\u65b9\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u518d\u8bf4\uff0c\u53ea\u6709\u6162\u6162\u5730\u8eba\u4e0b\u53bb\uff0c\u9759\u9759\u5730\u8eba\u5728\u661f\u5149\u4e0b\u3002 \u3000\u3000\u9065\u8fdc\u7684\u661f\u5149\uff0c\u5bd2\u51b7\u65e0\u60c5\u7684\u5927\u6f20\u4e4b\u591c\uff0c\u5982\u679c\u4ed6\u6709\u6cea\u6d41\u51fa\uff0c\u4e5f\u4e00\u5b9a\u7ed3\u6210\u4e86\u51b0\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u6d41\u6cea\uff0c\u7ecf\u8fc7\u8fd9\u4ef6\u4e8b\u4e4b\u540e\uff0c\u4ed6\u8fd9\u4e00\u751f\u6050\u6015\u90fd\u4e0d\u4f1a\u518d\u6d41\u6cea\u3002 \u3000\u3000\u535c\u9e70\u5e76\u6ca1\u6709\u89e3\u91ca\u4e3a\u4ec0\u4e48\u8981\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u4ed6\uff0c\u201c\u56e0\u4e3a\u4f60\u662f\u6211\u7684\u597d\u670b\u53cb\u201d\uff0c\u8fd9\u79cd\u8bdd\u662f\u7528\u4e0d\u7740\u518d\u8bf4\u7b2c\u4e8c\u6b21\u7684\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u5df2\u5c06\u6211\u7684\u4e8b\u5168\u90fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u535c\u9e70\u53ea\u7b80\u5355\u4ed6\u8bf4\u660e\u4e86\u4e00\u70b9\uff1a\u201c\u4f60\u53ef\u8003\u8651\uff0c\u662f\u7559\u4e0b\u6765\u8ddf\u6211\u4eec\u5728\u4e00\u8d77\uff0c\u8fd8\u662f\u8d70\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u8003\u8651\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u201c\u968f\u4fbf\u4f60\u8981\u8003\u8651\u591a\u4e45\uff0c\u4f46\u662f\u4f60\u51b3\u5b9a\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u8981\u5148\u6765\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b54\u5e94\u3002 \u3000\u3000\u661f\u5149\u9065\u8fdc\u9eef\u6de1\uff0c\u591c\u8272\u5bd2\u51b7\u51c4\u6e05\uff0c\u4ed6\u4eec\u5f7c\u6b64\u90fd\u770b\u4e0d\u89c1\u5bf9\u65b9\u8138\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5c0f\u65b9\u624d\u8bf4\uff1a\u201c\u4f60\u505a\u4e8b\u4e00\u5411\u6781\u8c28\u614e\uff0c\u53ef\u662f\u8fd9\u6b21\u5374\u505a\u662f\u592a\u5192\u9669\u4e86\u3002\u201d \u3000\u3000\u201c\u5192\u9669\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u6015\u6709\u4eba\u8ddf\u8e2a\u6211\u4eec\u5230\u8fd9\u91cc\u6765\uff1f\u4e0d\u6015\u522b\u4eba\u53d1\u73b0\u8fd9\u91cc\u7684\u85cf\u91d1\uff1f\u201d \u3000\u3000\u535c\u9e70\u6ca1\u6709\u8bf4\u8bdd\uff0c\u9ed1\u6697\u4e2d\u5374\u4f20\u6765\u4e00\u9635\u7b11\u58f0\uff1a\u201c\u4ed6\u4e0d\u6015\u522b\u4eba\u8ddf\u8e2a\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e00\u8def\u4e0a\u6211\u90fd\u5728\u4f60\u4eec\u7684\u9644\u8fd1\uff0c\u5c31\u7b97\u6709\u6761\u72d0\u72f8\u60f3\u8ddf\u8e2a\u4f60\u4eec\uff0c\u6211\u4e5f\u5df2\u6293\u4f4f\u4e86\u5b83\uff0c\u5265\u4e0b\u4e86\u5b83\u7684\u76ae\u3002\u201d \u3000\u3000\u8fd9\u662f\u73ed\u5bdf\u5df4\u90a3\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5c0f\u65b9\u8dc3\u8d77\u65f6\uff0c\u73ed\u5bdf\u5df4\u90a3\u5df2\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u8ddd\u79bb\u4ed6\u5df2\u4e0d\u53ca\u4e94\u5c3a\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u884c\u52a8\u8fdc\u6bd4\u6c99\u6f20\u4e0a\u6700\u5de7\u9ee0\u7684\u72d0\u72f8\u66f4\u96be\u88ab\u4eba\u53d1\u73b0\uff0c\u4ed6\u7684\u52a8\u4f5c\u6bd4\u98ce\u66f4\u9aa4\uff0c\u4ed6\u7684\u773c\u775b\u6bd4\u591c\u8272\u66f4\u6df1\u6c89\uff0c\u4ed6\u51dd\u89c6\u7740\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u6015\u4f60\u4f1a\u6cc4\u9732\u4ed6\u7684\u79d8\u5bc6\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u4ece\u6765\u6ca1\u6709\u4eba\u80fd\u6cc4\u9732\u6211\u4eec\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u4ed6\u5728\u7b11\uff0c\u4f46\u4ed6\u7684\u7b11\u5bb9\u5374\u50cf\u662f\u8fd9\u51c4\u60ca\u7684\u5927\u6f20\u4e4b\u591c\u4e00\u6837\u795e\u79d8\u3001\u51b7\u9177\u65e0\u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u53c8\u56de\u5230\u4e86\u62c9\u8428\uff0c\u707f\u70c2\u7684\u6674\u5929\u3001\u8dc3\u52a8\u7684\u751f\u547d\u548c\u90a3\u7f8e\u4e3d\u5f00\u6717\u7684\u201c\u84dd\u8272\u9633\u5149\u201d\u90fd\u5728\u7b49\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u535c\u9e70\u53c8\u5c06\u5c0f\u65b9\u4ea4\u7ed9\u4e86\u5979\u3002 \u3000\u3000\u201c\u4ed6\u8981\u5230\u54ea\u91cc\u53bb\uff0c\u4f60\u5c31\u5e26\u4ed6\u5230\u54ea\u91cc\u53bb\u3002\u201d\u535c\u9e70\u5429\u5490\uff1a\u201c\u4ed6\u8981\u4ec0\u4e48\uff0c\u4f60\u5c31\u7ed9\u4ed6\u4ec0\u4e48\u3002\u201d \u3000\u3000\u542c\u5230\u4ed6\u8bf4\u7684\u8bdd\uff0c\u60f3\u5230\u73ed\u5bdf\u5df4\u90a3\u51b7\u9177\u7684\u7b11\u5bb9\uff0c\u4f7f\u4eba\u5f88\u5bb9\u6613\u8054\u60f3\u5230\u4e00\u4e2a\u6b7b\u5211\u72af\u5728\u4e34\u5211\u524d\uff0c\u65e0\u8bba\u63d0\u4ec0\u4e48\u8981\u6c42\u90fd\u4f1a\u88ab\u7b54\u5e94\u7684\u3002 \u3000\u3000\u4ed6\u5c06\u8fd9\u7edd\u4e0d\u5bb9\u4efb\u4f55\u4eba\u6cc4\u9732\u7684\u79d8\u5bc6\u544a\u8bc9\u4e86\u5c0f\u65b9\uff0c\u5728\u67d0\u65b9\u9762\u8bf4\u4e5f\u662f\u65e0\u5f02\u5ba3\u5224\u4e86\u5c0f\u65b9\u7684\u6b7b\u5211\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u8fd9\u4e48\u60f3\uff0c\u4ed6\u597d\u50cf\u4ec0\u4e48\u90fd\u6ca1\u6709\u60f3\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u8fd8\u662f\u7b11\u5f97\u90a3\u4e48\u6109\u5feb\u5f00\u6717\uff0c\u5979\u7edd\u4e0d\u95ee\u4ed6\u8fd9\u51e0\u5929\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u53ea\u95ee\u4ed6\uff1a\u201c\u4f60\u60f3\u8981\u4ec0\u4e48\uff1f\u60f3\u8981\u6211\u966a\u7740\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4e09\u5929\u4e4b\u540e\uff0c\u5c0f\u65b9\u624d\u56de\u7b54\u4ed6\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u201c\u6211\u8981\u4e00\u4e07\u4e24\u94f6\u5b50\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6211\u8981\u5230\u4e00\u4e2a\u4f60\u7edd\u4e0d\u80fd\u966a\u6211\u5230\u7684\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u8fd9\u4e09\u5929\u91cc\uff0c\u4ed6\u4eec\u51e0\u4e4e\u671d\u5915\u90fd\u5728\u4e00\u8d77\uff0c\u5979\u966a\u7740\u5c0f\u65b9\u53bb\u505a\u4e00\u5207\u522b\u7684\u5973\u4eba\u4e0d\u80af\u966a\u7537\u4eba\u505a\u7684\u4e8b\u3002 \u3000\u3000\u5979\u966a\u4ed6\u8c6a\u8d4c\uff0c\u966a\u4ed6\u75db\u996e\uff0c\u6709\u65f6\u559d\u9189\u4e86\uff0c\u4ed6\u4eec\u751a\u81f3\u7761\u5728\u4e00\u8d77\u3002 \u3000\u3000\u6709\u4e00\u5929\u5c0f\u65b9\u9152\u9189\u65f6\uff0c\u53d1\u73b0\u5979\u7adf\u7761\u5728\u4ed6\u8eab\u65c1\u3002 \u3000\u3000\u5979\u7761\u7740\u7684\u65f6\u5019\u8fdc\u6bd4\u9192\u65f6\u66f4\u6e29\u67d4\uff0c\u66f4\u7f8e\u4e3d\uff0c\u66f4\u50cf\u4e00\u4e2a\u5973\u4eba\u3002\u5979\u7684\u8eab\u6750\u67d4\u7f8e\u3001\u76ae\u80a4\u96ea\u767d\u3001\u6c14\u5473\u82b3\u9999\u3002 \u3000\u3000\u5bbf\u9189\u521d\u9192\u65f6\u90a3\u79cd\u70c8\u706b\u711a\u70e7\u822c\u7684\u5f3a\u70c8\u6b32\u671b\uff0c\u4f7f\u5f97\u5c0f\u65b9\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5360\u6709\u5979\u3002 \u3000\u3000\u4ed6\u5fcd\u4f4f\u4e86\uff0c\u4ed6\u7528\u51b7\u6c34\u51b2\u6dcb\u4e86\u5c06\u8fd1\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u4e4b\u95f4\u8fd8\u662f\u6e05\u767d\u7684\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4eec\u7684\u6e05\u767d\u975e\u4f46\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u53ef\u80fd\u6ca1\u6709\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7adf\u5168\u4e0d\u5728\u4e4e\uff0c\u4e0d\u7ba1\u522b\u4eba\u5bf9\u4ed6\u4eec\u600e\u4e48\u60f3\uff0c\u5979\u5374\u4e0d\u5728\u4e4e\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u4e94\u7ae0\u3000\u6289\u62e9 \u7b2c\u5341\u4e94\u7ae0\u3000\u6289\u62e9 \u3000\u3000\u8fd9\u79cd\u4e8b\u672c\u6765\u662f\u4e00\u4e2a\u5973\u5b69\u5b50\u6700\u5728\u4e4e\u7684\u4e8b\uff0c\u9664\u975e\u5979\u5df2\u51c6\u5907\u63a5\u53d7\u90a3\u4e2a\u7537\u4eba\u3002\u201c\u9633\u5149\u201d\u4e0d\u5728\u4e4e\uff0c\u662f\u4e0d\u662f\u5979\u5df2\u51c6\u5907\u63a5\u53d7\u4ed6\uff1f \u3000\u3000\u4f46\u662f\u4e09\u5929\u540e\uff0c\u5c0f\u65b9\u5374\u5ffd\u7136\u63d0\u51fa\u8fd9\u8981\u6c42\uff0c\u800c\u4e14\u8fd8\u8981\u5979\u7b54\u5e94\uff1a\u201c\u4f60\u7edd\u4e0d\u80fd\u95f4\u6211\u8981\u5230\u54ea\u91cc\u53bb\uff0c\u66f4\u4e0d\u80fd\u5728\u6697\u4e2d\u8ddf\u8e2a\u6211\uff0c\u5426\u5219\u6211\u8bf4\u4e0d\u5b9a\u4f1a\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u8fd9\u8981\u6c42\u591a\u4e48\u4e0d\u8fd1\u4eba\u60c5\uff0c\u4ed6\u8bf4\u7684\u8bdd\u591a\u4e48\u7edd\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u8ba4\u4e3a\u201c\u9633\u5149\u201d\u4f1a\u751f\u6c14\u7684\u3002 \u3000\u3000\u5979\u6ca1\u6709\u751f\u6c14\uff0c\u5979\u7acb\u523b\u5c31\u7b54\u5e94\u4e86\uff1a\u201c\u4f60\u53bb\uff0c\u6211\u7231\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8981\u7684\u8fd9\u4e00\u4e07\u4e24\u94f6\u5b50\uff0c\u7adf\u7136\u662f\u51c6\u5907\u7ed9\u72ec\u5b64\u75f4\u7684\u3002 \u3000\u3000\u4ed6\u7edd\u6ca1\u4ed6\u5fd8\u8bb0\u4ed6\u7684\u8bfa\u8a00\uff0c\u4ed6\u53c8\u56de\u5230\u90a3\u5b69\u5b50\u5e26\u4ed6\u53bb\u8fc7\u7684\u9e1f\u5c4b\u3002 \u3000\u3000\u9e1f\u5c4b\u4ecd\u5728\uff0c\u5c4b\u6a90\u4e0b\u7684\u9e1f\u7b3c\u4e5f\u4ecd\u5728\uff0c\u4f46\u662f\u4e4c\u7b3c\u5374\u5df2\u7a7a\u4e86\u3002 \u3000\u3000\u7b3c\u4e2d\u7684\u98de\u9e1f\u5df2\u88ab\u65a9\u843d\u5728\u5730\u4e0a\uff0c\u6bcf\u4e00\u53ea\u90fd\u88ab\u4e00\u5251\u65a9\u6210\u4e86\u4e24\u534a\u3002 \u3000\u3000\u5730\u4e0a\u7684\u8840\u8ff9\u5df2\u5e72\uff0c\u5c4b\u91cc\u5bc2\u65e0\u4eba\u58f0\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u624b\u8db3\u51b0\u51b7\u3002 \u3000\u3000\u4e0a\u4e00\u6b21\u4ed6\u6765\u7684\u65f6\u5019\uff0c\u96be\u9053\u5df2\u7ecf\u6709\u4eba\u8ddf\u8e2a\u4ed6\u5230\u8fd9\u91cc\uff1f \u3000\u3000\u4ed6\u672c\u6765\u4e00\u5411\u81ea\u4fe1\u8033\u76ee\u90fd\u6781\u7075\u654f\uff0c\u65e0\u8bba\u8c01\u8981\u8ddf\u8e2a\u4ed6\u90fd\u5f88\u96be\uff0c\u4f46\u7ecf\u8fc7\u90a3\u5927\u6f20\u4e4b\u591c\u91cc\uff0c\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u51fa\u73b0\u5728\u4ed6\u773c\u524d\u4e4b\u540e\uff0c\u4ed6\u7684\u4fe1\u5fc3\u5df2\u52a8\u6447\u3002 \u3000\u3000\u2014\u2014\u662f\u8c01\u8ddf\u8e2a\u4ed6\u5230\u8fd9\u91cc\u6765\u8fc7\uff1f\u662f\u8c01\u4ee5\u8fd9\u79cd\u72e0\u6bd2\u7684\u5251\u6cd5\u65a9\u6740\u4e86\u8fd9\u4e9b\u65e0\u8f9c\u7684\u98de\u9e1f\uff1f\u72ec\u5b64\u75f4\u548c\u90a3\u4e2a\u5b69\u5b50\u662f\u4e0d\u662f\u4e5f\u5df2\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\uff1f \u3000\u3000\u9648\u65e7\u7684\u9e1f\u5c4b\uff0c\u4e00\u8d70\u4e0a\u53bb\uff0c\u6728\u677f\u5c31\u4f1a\u8e29\u5f97\u201c\u5431\u5431\u201d\u4f5c\u54cd\u3002 \u3000\u3000\u5c0f\u65b9\u8d70\u4e0a\u53bb\uff0c\u63a8\u5f00\u95e8\u3002 \u3000\u3000\u5c4b\u91cc\u6ca1\u6709\u4eba\uff0c\u4e5f\u6ca1\u6709\u5c38\u4f53\uff0c\u53ea\u6709\u4e00\u5e45\u56fe\uff0c\u4eff\u4f5b\u662f\u7528\u9c9c\u8840\u753b\u6210\u7684\u56fe\u753b\uff0c\u753b\u5728\u8fce\u95e8\u7684\u6728\u677f\u5899\u4e0a\uff0c\u753b\u7684\u662f\u4e00\u4e2a\u9b54\u5973\uff0c\u5728\u542e\u5438\u7740\u4e00\u4e2a\u7537\u4eba\u7684\u8111\u9ad3\u3002 \u3000\u3000\u9b54\u5973\u7684\u5bb9\u8c8c\u662f\u6ce2\u5a03\u3002 \u3000\u3000\u88ab\u5979\u542e\u5438\u7740\u8111\u9ad3\u7684\u7537\u4eba\u8d6b\u7136\u5c31\u662f\u5c0f\u65b9\u81ea\u5df1\u3002 \u3000\u3000\u53ea\u6709\u8fd9\u5e45\u753b\uff0c\u6ca1\u6709\u522b\u7684\u5b57\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u65b9\u5374\u5df2\u5b8c\u5168\u660e\u767d\u5b83\u7684\u610f\u601d\uff0c\u4eff\u4f5b\u5ffd\u7136\u53c8\u56de\u5230\u90a3\u9634\u68ee\u6c89\u90c1\u7684\u5e99\u5b87\u4e2d\uff0c\u53c8\u56de\u5230\u90a3\u5f2f\u5f62\u77f3\u9f9b\u7684\u58c1\u753b\u524d\u3002 \u3000\u3000\u4ed6\u8033\u7554\u4eff\u4f5b\u53c8\u542c\u5230\u90a3\u5b69\u5b50\u7684\u58f0\u97f3\uff1a\u201c\u2026\u2026\u5982\u679c\u4f60\u8fdd\u80cc\u4e86\u8a93\u8a00\uff0c\u7ec8\u751f\u90fd\u8981\u50cf\u8fd9\u4e2a\u4eba\u4e00\u6837\uff0c\u53d7\u5c3d\u4e86\u7f57\u5239\u9b3c\u5973\u6076\u6bd2\u7684\u6298\u78e8\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5e76\u6ca1\u6709\u8fdd\u80cc\u4ed6\u7684\u8a93\u8a00\uff0c\u4e5f\u6ca1\u6709\u6cc4\u9732\u8fc7\u4efb\u4f55\u4eba\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e5f\u6ca1\u6709\u6740\u6b7b\u6ce2\u5a03\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u4e00\u5b9a\u5df2\u67e5\u51fa\u4e86\u6ce2\u5a03\u6ca1\u6709\u6b7b\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u5c0f\u65b9\u5c06\u4ed6\u51fa\u6765\u5356\u4e86\uff0c\u6240\u4ee5\u7acb\u523b\u5e26\u7740\u90a3\u5b69\u5b50\u79bb\u5f00\u4e86\u8fd9\u4e4c\u5c4b\u3002\u88ab\u65a9\u6740\u7684\u98de\u9e1f\u3001\u58c1\u4e0a\u7684\u56fe\u753b\u90fd\u662f\u4ed6\u7279\u5730\u7559\u4e0b\u6765\u7ed9\u5c0f\u65b9\u770b\u7684\uff0c\u7279\u5730\u8981\u8ba9\u5c0f\u65b9\u77e5\u9053\u4ed6\u7684\u4ec7\u6068\u548c\u6028\u6bd2\u3002\u2014\u2014\u4ed6\u8fd8\u6709\u4e00\u53ea\u624b\uff0c\u8fd8\u53ef\u4ee5\u63e1\u5251\uff0c\u8fd8\u6709\u65a9\u6740\u98de\u9e1f\u7684\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u8fd9\u4e2a\u4eba\u672c\u6765\u5c31\u5145\u6ee1\u4e86\u4e00\u79cd\u4ee4\u4eba\u6c38\u8fdc\u65e0\u6cd5\u9884\u6d4b\u7684\u53ef\u6015\u6f5c\u529b\uff0c\u4f55\u51b5\u201c\u4ec7\u6068\u201d\u672c\u8eab\u4e5f\u662f\u79cd\u53ef\u6015\u7684\u529b\u91cf\uff01 \u3000\u3000\u73b0\u5728\u4ed6\u7b2c\u4e00\u4e2a\u8981\u6740\u7684\u4eba\u5df2\u7ecf\u7edd\u5bf9\u4e0d\u662f\u535c\u9e70\u4e86\uff0c\u800c\u662f\u5c0f\u65b9\uff01 \u3000\u3000\u5c0f\u65b9\u9759\u9759\u5730\u7ad9\u5728\u8fd9\u5e45\u58c1\u753b\u524d\uff0c\u7ad9\u4e86\u5f88\u4e45\uff0c\u6162\u6162\u5730\u5c06\u4ed6\u5e26\u6765\u7684\u4e00\u4e07\u4e24\u94f6\u7968\u653e\u5728\u5730\u4e0a\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\uff1b\u8d70\u5230\u84dd\u5929\u4e4b\u4e0b\u3002 \u3000\u3000\u5929\u6c14\u867d\u7136\u8fd8\u662f\u540c\u6837\u6674\u6717\uff0c\u53ef\u662f\u4ed6\u5fc3\u91cc\u5374\u5df2\u6709\u4e86\u4e2a\u9a71\u4e0d\u6563\u7684\u9634\u5f71\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u72ec\u5b64\u75f4\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002 \u3000\u3000\u4ece\u4eca\u4ee5\u540e\uff0c\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u8981\u63d0\u9632\u7740\u90a3\u81f4\u547d\u7684\u4e00\u5251\u523a\u6765\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u6b21\u89c1\u5230\u72ec\u5b64\u75f4\u65f6\u5c31\u77e5\u9053\u4e86\uff0c\u4ed6\u4eec\u5f7c\u6b64\u95f4\uff0c\u8fdf\u65e9\u603b\u6709\u4e00\u4e2a\u8981\u6b7b\u5728\u5bf9\u65b9\u624b\u91cc\u7684\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u679c\u7136\u8fd8\u5728\u7b49\u7740\u4ed6\u3002\u4ed6\u770b\u5230\u5979\u4e4b\u540e\uff0c\u7b2c\u4e00\u53e5\u8bdd\u5c31\u8bf4\uff1a\u201c\u535c\u9e70\u73b0\u5728\u54ea\u91cc\uff1f\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u8981\u53bb\u89c1\u4ed6\uff0c\u73b0\u5728\u5c31\u8981\u53bb\u89c1\u4ed6\uff01\u201d \u3000\u3000\u5bbd\u5927\u6d01\u51c0\u7684\u53a2\u623f\uff0c\u65b0\u9c9c\u5145\u8db3\u7684\u9633\u5149\uff0c\u6bcf\u4e00\u6837\u4e1c\u897f\u90fd\u662f\u7cbe\u9009\u7684\uff0c\u65e2\u4e0d\u4f1a\u6709\u591a\u4f59\uff0c\u4e5f\u4e0d\u4f1a\u7f3a\u5c11\u4ec0\u4e48\u3002 \u3000\u3000\u9152\u662f\u751c\u7f8e\u9187\u539a\u7684\u6ce2\u65af\u8461\u8404\u9152\uff0c\u76db\u5728\u900f\u660e\u7684\u6c34\u6676\u676f\u91cc\uff0c\u95ea\u52a8\u7740\u7425\u73c0\u8272\u7684\u5149\u3002 \u3000\u3000\u535c\u9e70\u5012\u4e86\u4e00\u676f\u7ed9\u5c0f\u65b9\uff0c\u81ea\u5df1\u4f4e\u659f\u6d45\u914c\uff0c\u559d\u5b8c\u4e86\u5c0f\u534a\u676f\uff0c\u7136\u540e\u624d\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5df2\u51b3\u5b9a\u8981\u8d70\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u5c0f\u65b9\u7684\u56de\u7b54\u8fd8\u662f\u548c\u4ee5\u524d\u4ed6\u56de\u7b54\u95ee\u9898\u65f6\u540c\u6837\u7b80\u5355\u660e\u786e\uff0c\u597d\u50cf\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u95ee\u9898\u6bd4\u4ed6\u4ee5\u524d\u56de\u7b54\u8fc7\u7684\u4efb\u4f55\u95ee\u9898\u90fd\u4e25\u91cd\u5f97\u591a\u3002 \u3000\u3000\u535c\u9e70\u6ca1\u6709\u518d\u95ee\uff0c\u4e5f\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u4eec\u90fd\u6ca1\u6709\u518d\u5f00\u53e3\u3002 \u3000\u3000\u8fdc\u5904\u7684\u767d\u4e91\u5728\u5929\uff0c\u98ce\u5728\u6811\u68a2\uff0c\u79ef\u96ea\u7684\u5c71\u5dc5\u5728\u6674\u6717\u7684\u84dd\u5929\u4e0b\uff0c\u5e73\u51e1\u7684\u4eba\u5728\u4e3a\u81ea\u5df1\u7684\u751f\u6d3b\u6323\u624e\uff0c\u4e0d\u5e73\u51e1\u7684\u4eba\u5728\u4e3a\u81ea\u5df1\u7684\u751f\u547d\u594b\u6597\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e9b\u4e8b\u90fd\u8ddd\u79bb\u4ed6\u4eec\u5f88\u8fdc\uff0c\u5c4b\u5b50\u91cc\u5b89\u9759\u5f97\u5c31\u50cf\u662f\u4e00\u4e2a\u6b7b\u4eba\u7684\u5fc3\u810f\u3002 \u3000\u3000\u7136\u540e\u66ae\u8272\u5c31\u6e10\u6e10\u6765\u4e34\u4e86\u3002\u5c31\u50cf\u662f\u4e00\u77ac\u95f4\u7684\u4e8b\uff0c\u591c\u8272\u5ffd\u7136\u5c31\u5df2\u7b3c\u7f69\u5927\u5730\u3002* \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u706f\uff0c\u53ef\u662f\u8c01\u4e5f\u6ca1\u6709\u53bb\u70b9\u71c3\u5b83\u3002\u4e24\u4e2a\u4eba\u9759\u9759\u5730\u5750\u5728\u9ed1\u6697\u4e2d\uff0c\u7a97\u5916\u6709\u661f\u5347\u8d77\u3002\u6709\u6708\u5347\u8d77\uff0c\u76f4\u5230\u661f\u5149\u3001\u6708\u8272\u7167\u5165\u7a97\u6237\uff0c\u535c\u9e70\u624d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u6211\u5f88\u4e86\u89e3\u4f60\uff0c\u4f60\u5df2\u7ecf\u51b3\u5b9a\u4e86\u7684\u4e8b\uff0c\u5c31\u7edd\u5bf9\u4e0d\u4f1a\u66f4\u6539\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u51b3\u5b9a\u4e86\u3002\u201d\u5c0f\u65b9\u663e\u5f97\u51fa\u5947\u7684\u5e73\u9759\uff0c\u201c\u6211\u975e\u8d70\u4e0d\u53ef\u3002\u201d \u3000\u3000\u535c\u9e70\u5e76\u6ca1\u6709\u95ee\u4ed6\u201c\u4e3a\u4ec0\u4e48\u201d\uff0c\u5374\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u73ed\u5bdf\u5df2\u90a3\u8bf4\u8fc7\u7684\u90a3\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u201c\u6211\u8bb0\u5f97\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u4ed6\u8bf4\uff0c\u4ece\u6765\u90fd\u6ca1\u6709\u4eba\u80fd\u6cc4\u9732\u4f60\u4eec\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u7edd\u4e0d\u4f1a\u6cc4\u9732\u522b\u4eba\u7684\u79d8\u5bc6\uff0c\u4f46\u4ed6\u4e0d\u540c\uff0c\u4ed6\u4ece\u4e0d\u76f8\u4fe1\u4efb\u4f55\u4eba\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u603b\u8ba4\u4e3a\u53ea\u6709\u6b7b\u4eba\u624d\u80fd\u4fdd\u5b88\u79d8\u5bc6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u624b\u63e1\u7d27\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u535c\u9e70\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u53ea\u544a\u8bc9\u5c0f\u65b9\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u6211\u4e5f\u4e0d\u80fd\u505a\u4e3b\u7684\u3002\u201d\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff0c\u201c\u8b6c\u5982\u8bf4\uff0c\u4f60\u8981\u8d70\uff0c\u6211\u4e5f\u6ca1\u6cd5\u5b50\u7559\u4f4f\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u660e\u767d\u535c\u9e70\u7684\u610f\u601d\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u535c\u9e70\u8bf4\u8fc7\u7684\u4e24\u53e5\u8bdd\uff1a \u3000\u3000\u2014\u2014\u4e0d\u662f\u670b\u53cb\uff0c\u5c31\u662f\u4ec7\u654c\u3002 \u3000\u3000\u2014\u2014\u5bf9\u4ed8\u4ec7\u654c\uff0c\u7edd\u4e0d\u80fd\u7559\u60c5\u3002 \u3000\u3000\u670b\u53cb\u53d8\u4e3a\u4ec7\u654c\uff0c\u62e5\u62b1\u53d8\u4e3a\u640f\u51fb\uff0c\u9c9c\u8840\u50cf\u91d1\u640f\u4e2d\u7684\u7f8e\u9152\u822c\u6d41\u51fa\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\u5c0f\u65b9\u6240\u60f3\u7684\u5e76\u4e0d\u662f\u8fd9\u4e9b\uff0c\u4e0d\u662f\u6740\u622e\u4e0d\u662f\u6b7b\u4ea1\u4e0d\u662f\u6bc1\u706d\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5ffd\u7136\u60f3\u5230\u4e86\u4ed6\u7684\u6545\u4e61\u6c5f\u5357\uff0c\u5b81\u9759\u7f8e\u4e3d\u7684\u6c5f\u5357\uff0c\u674f\u82b1\u70df\u96e8\u4e2d\u7684\u6c5f\u5357\uff0c\u67d4\u6a79\u58f0\u91cc\u591a\u6865\u591a\u6c34\u591a\u6101\u7684\u6c5f\u5357\u3002 \u3000\u3000\u535c\u9e70\u7684\u58f0\u97f3\u4e5f\u53d8\u6210\u5728\u6c5f\u5357\u822c\u9065\u8fdc\u3002 \u3000\u3000\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u8981\u8d70\u7684\u3002\u201d\u535c\u9e70\u8bf4\uff0c\u201c\u4f60\u56de\u5230\u62c9\u8428\uff0c\u6ca1\u6709\u518d\u53bb\u770b\u6ce2\u5a03\uff0c\u6211\u5c31\u5df2\u77e5\u9053\u4f60\u51b3\u5fc3\u8981\u79bb\u5f00\u6211\u4eec\uff0c\u56e0\u4e3a\u4f60\u81ea\u5df1\u77e5\u9053\u4f60\u6c38\u8fdc\u65e0\u6cd5\u4e86\u89e3\u6211\u4eec\uff0c\u4e5f\u65e0\u6cd5\u4e86\u89e3\u6211\u4eec\u6240\u505a\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u6253\u65ad\u4ed6\u81ea\u5df1\u8fd8\u5728\u8bf4\u7684\u8bdd\uff0c\u5ffd\u7136\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5357\u3002\u201d\u5c0f\u65b9\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u5728\u60f3\u6c5f\u5357\u3002\u201d \u3000\u3000\u201c\u4f60\u5728\u60f3\u6c5f\u5357\uff1f\u6b64\u65f6\u6b64\u523b\uff0c\u4f60\u5c45\u7136\u5728\u60f3\u6c5f\u5357\uff1f\u201d \u3000\u3000\u535c\u9e70\u7684\u58f0\u97f3\u91cc\u6ca1\u6709\u8ba5\u6d88\u60ca\u5f02\uff0c\u53ea\u6709\u4e00\u70b9\u6de1\u6de1\u7684\u4f24\u611f\uff1a\u201c\u4f60\u6839\u672c\u4e0d\u662f\u6211\u4eec\u8fd9\u4e00\u7c7b\u7684\uff0c\u4f60\u662f\u4e2a\u8bd7\u4eba\uff0c\u4e0d\u662f\u6218\u58eb\uff0c\u4e5f\u4e0d\u662f\u5251\u5ba2\uff0c\u6240\u4ee5\u4f60\u624d\u8981\u8d70\uff0c\u56e0\u4e3a\u73b0\u5728\u4f60\u5c45\u7136\u8fd8\u5728\u60f3\u7740\u6c5f\u5357\u3002\u201d \u3000\u3000\u5c0f\u65b9\u62ac\u8d77\u5934\uff0c\u770b\u7740\u4ed6\uff1a\u201c\u73b0\u5728\u6211\u5e94\u8be5\u600e\u4e48\u60f3\uff1f\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u60f3\u60f3\u4e25\u6b63\u521a\uff0c\u60f3\u60f3\u5b8b\u8001\u592b\u5b50\uff0c\u60f3\u60f3\u6731\u4e91\uff0c\u60f3\u60f3\u4ed6\u4eec\u662f\u4e9b\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u60f3\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u8d70\u7684\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u5982\u679c\u4e16\u4e0a\u53ea\u6709\u4e00\u4e2a\u6cd5\u5b50\u80fd\u7559\u4f4f\u4f60\uff0c\u4ed6\u4eec\u4e00\u5b9a\u5c31\u4f1a\u7528\u90a3\u4e2a\u6cd5\u5b50\u5bf9\u4ed8\u4f60\u3002\u5982\u679c\u4ed6\u4eec\u8ba4\u4e3a\u4e00\u5b9a\u8981\u5272\u65ad\u4f60\u7684\u54bd\u5589\u624d\u80fd\u7559\u4f4f\u4f60\uff0c\u4ed6\u4eec\u7684\u5200\u7edd\u4e0d\u4f1a\u843d\u5728\u522b\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\u7684\u3002\u201d\u535c\u9e70\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u4f46\u80fd\u628a\u4eba\u7684\u54bd\u5589\u50cf\u5272\u8349\u822c\u5272\u65ad\uff0c\u4e5f\u80fd\u628a\u5200\u950b\u4e0a\u7684\u8840\u5f53\u505a\u6c34\u4e00\u6837\u64e6\u5e72\u3002\u201d \u3000\u3000\u5c0f\u65b9\u51dd\u89c6\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u6162\u6162\u4ed6\u8bf4\uff1a\u201c\u4f60\u8be5\u77e5\u9053\u6709\u65f6\u5019\u6211\u4e5f\u4f1a\u8fd9\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u535c\u9e70\u7684\u9510\u773c\u4e2d\u5ffd\u7136\u900f\u51fa\u201c\u9b54\u773c\u201d\u822c\u7684\u5bd2\u5149\uff0c\u638c\u4e2d\u7684\u6c34\u6676\u676f\u5ffd\u7136\u788e\u88c2\uff0c\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u63a8\u5f00\u7a97\u6237\uff1a\u201c\u4f60\u770b\u90a3\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ece\u7a97\u5b50\u91cc\u770b\u51fa\u53bb\uff0c\u53ef\u4ee5\u770b\u5230\u4e00\u6839\u5f88\u9ad8\u7684\u65d7\u6266\uff0c\u65d7\u6746\u4e0a\u5df2\u6302\u8d77\u4e00\u76cf\u706f\u3002 \u3000\u3000\u201c\u90a3\u662f\u4e00\u76cf\u706f\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u535c\u9e70\u9065\u671b\u7740\u8fdc\u5904\u9ad8\u6302\u7684\u7ea2\u706f\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u4e00\u79cd\u4ece\u672a\u6709\u7684\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u201c\u90a3\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u4ed6\u4eec\u4e5f\u77e5\u9053\u4f60\u8981\u8d70\u4e86\uff0c\u5df2\u51c6\u5907\u4e3a\u4f60\u996f\u884c\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4f38\u624b\uff0c\u5f39\u6307\uff0c\u5f39\u51fa\u4e86\u4e00\u7247\u6c34\u6676\u676f\u7684\u788e\u7247\uff0c\u6025\u98ce\u7834\u7a7a\u58f0\u5c16\u9510\u5982\u9e70\u5578\u3002 \u3000\u3000\u4e8c\u5341\u4e08\u5916\u7684\u7ea2\u706f\u5ffd\u7136\u7184\u706d\uff0c\u535c\u9e70\u773c\u4e2d\u7684\u5bd2\u5149\u4e5f\u5df2\u6d88\u706d\u3002 \u3000\u3000\u201c\u6240\u4ee5\u73b0\u5728\u4f60\u5df2\u7ecf\u53ef\u4ee5\u8d70\u4e86\u3002\u201d\u4ed6\u6ca1\u6709\u56de\u5934\u518d\u770b\u5c0f\u65b9\uff0c\u53ea\u6325\u4e86\u6325\u624b\uff0c\u201c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8d70\u51fa\u95e8\u65f6\uff0c\u5c31\u770b\u89c1\u4e86\u201c\u9633\u5149\u201d\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u6b63\u7ad9\u5728\u9662\u5b50\u91cc\u4e00\u68da\u7d2b\u817e\u7684\u9634\u5f71\u4e0b\uff0c\u8138\u4e0a\u90a3\u79cd\u9633\u5149\u822c\u5f00\u6717\u6109\u5feb\u7684\u7b11\u5bb9\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5979\u867d\u7136\u8fd8\u5728\u7b11\uff0c\u7b11\u5bb9\u770b\u6765\u5374\u5df1\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u9634\u90c1\u54c0\u4f24\u3002 \u3000\u3000\u5c0f\u65b9\u8d70\u8fc7\u53bb\uff0c\u8d70\u5230\u5979\u9762\u524d\uff1a\u201c\u4f60\u4e5f\u662f\u6765\u4e3a\u6211\u996f\u884c\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u3002\u201d\u5979\u5ffd\u7136\u63e1\u4f4f\u5c0f\u65b9\u7684\u624b\uff0c\u5979\u7684\u624b\u51b0\u51b7\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec\u51c6\u5907\u7528\u4ec0\u4e48\u6765\u4e3a\u4f60\u996f\u884c\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u7b11\uff1a\u201c\u7528\u6211\u7684\u4eba\u5934\uff0c\u8fd8\u662f\u7528\u6211\u7684\u8840\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u63e1\u4f4f\u201c\u9633\u5149\u201d\u7684\u624b\uff1a\u201c\u4f60\u8981\u8bf4\u7684\u6211\u90fd\u77e5\u9053\uff0c\u53ef\u662f\u968f\u4fbf\u4ed6\u4eec\u8981\u7528\u4ec0\u4e48\uff0c\u6211\u90fd\u4e0d\u5728\u4e4e\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff1a\u201c\u4f60\u4e0d\u5728\u4e4e\uff1f\u771f\u7684\u4e0d\u5728\u4e4e\uff1f\u201d \u3000\u3000\u201c\u53cd\u6b63\u6211\u5df2\u51b3\u5fc3\u8981\u8d70\u4e86\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u968f\u4fbf\u7528\u4ec0\u4e48\u6cd5\u5b50\u8d70\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u6d3b\u7740\u4e5f\u662f\u8d70\uff0c\u6b7b\u4e86\u4e5f\u662f\u8d70\uff0c\u65e2\u7136\u5df2\u51b3\u5fc3\u8981\u8d70\uff0c\u5c31\u5df2\u6ca1\u6709\u628a\u6b7b\u6d3b\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7ec8\u4e8e\u653e\u5f00\u4e86\u4ed6\u7684\u624b\uff0c\u8f6c\u8fc7\u5934\u53bb\u770b\u82b1\u68da\u9634\u5f71\u4e0b\u4e00\u679d\u67af\u840e\u7684\u7d2b\u6ed5\u3002 \u3000\u3000\u201c\u597d\uff0c\u4f60\u8d70\u5427\uff01\u201d\u5979\u6307\u7740\u89d2\u843d\u91cc\u4e00\u4e2a\u5c0f\u95e8\uff0c\u201c\u4f60\u4ece\u8fd9\u9053\u95e8\u8d70\uff0c\u7b2c\u4e00\u4e2a\u8981\u4e3a\u4f60\u996f\u884c\u7684\u662f\u4e25\u6b63\u521a\uff0c\u4f60\u8981\u7279\u522b\u6ce8\u610f\u4ed6\u7684\u624b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u770b\u89c1\u8fc7\u4e25\u6b63\u521a\u51fa\u624b\u3002 \u3000\u3000\u5728\u90a3\u60ac\u6302\u7740\u9ed1\u8272\u9e70\u7fbd\u7684\u5e10\u7bf7\u4e2d\uff0c\u5728\u90a3\u5feb\u5982\u7535\u5149\u77f3\u706b\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u5c31\u5df2\u5378\u4e0b\u4e86\u67f3\u5206\u5206\u7684\u9b54\u81c2\u3002 \u3000\u3000\u4ed6\u7528\u7684\u662f\u5de6\u624b\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u6211\u4f1a\u7279\u522b\u6ce8\u610f\u4ed6\u7684\u5de6\u624b\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u7684\u58f0\u97f3\u5ffd\u7136\u538b\u5f97\u5f88\u4f4e\uff1a\u201c\u4e0d\u4f46\u8981\u6ce8\u610f\u4ed6\u7684\u5de6\u624b\uff0c\u8fd8\u8981\u6ce8\u610f\u4ed6\u7684\u53e6\u5916\u4e00\u53ea\u624b\u3002\u201d \u3000\u3000\u201c\u53e6\u5916\u4e00\u53ea\u624b\uff1f\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u53f3\u624b\uff1f\u6216\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\u53f3\u624b\uff01\u201d \u3000\u3000\u96be\u9053\u4e25\u6b63\u521a\u4e5f\u6709\u53e6\u5916\u4e00\u53ea\u624b\uff0c\u7b2c\u4e09\u53ea\u624b\uff1f \u3000\u3000\u5c0f\u65b9\u8fd8\u60f3\u518d\u95f4\u65f6\uff0c\u5979\u5df2\u7ecf\u6084\u6084\u5730\u8d70\u4e86\uff0c\u5c31\u50cf\u662f\u65e5\u8584\u5d26\u5d6b\u65f6\u9633\u5149\u5ffd\u7136\u6d88\u5931\u5728\u897f\u5c71\u540e\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u592a\u9633\u660e\u65e5\u8fd8\u4f1a\u5347\u8d77\uff0c\u5c0f\u65b9\u8fd9\u4e00\u751f\u53ef\u80fd\u6c38\u8fdc\u89c1\u4e0d\u5230\u5979\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u4f60\u5728\u4ec0\u4e48\u65f6\u5019\u3001\u4ec0\u4e48\u5730\u65b9\u770b\u89c1\u4e25\u6b63\u521a\uff0c\u4ed6\u770b\u6765\u90fd\u597d\u50cf\u662f\u5728\u5e99\u5802\u4e2d\u884c\u5927\u5178\u4e00\u6837\uff0c\u8863\u7740\u6574\u9f50\u6d01\u51c0\uff0c\u6001\u5ea6\u4e25\u8083\u606d\u8c28\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u770b\u6765\u4e5f\u662f\u8fd9\u6837\u4e8e\u7684\uff0c\u5f53\u4ed6\u4e00\u5200\u5272\u65ad\u522b\u4eba\u54bd\u5589\u65f6\uff0c\u6001\u5ea6\u4e5f\u4e0d\u4f1a\u6539\u53d8\u3002 \u3000\u3000\u5c0f\u65b9\u8d70\u8fc7\u53bb\uff0c\u8fde\u4e00\u53e5\u4e0d\u5fc5\u8981\u8bf4\u7684\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u4e00\u5f00\u53e3\u5c31\u95ee\uff1a\u201c\u4f60\u51c6\u5907\u7528\u4ec0\u4e48\u66ff\u6211\u996f\u884c\uff1f\u201d \u3000\u3000\u201c\u7528\u6211\u7684\u5de6\u624b\u3002\u201d \u3000\u3000\u4e25\u6b63\u521a\u7684\u56de\u7b54\u4e5f\u540c\u6837\u76f4\u63a5\u5e72\u8106\uff0c\u201c\u8fd9\u91cc\u662f\u76d7\u7a9f\uff0c\u4eba\u4e86\u76d7\u7a9f\uff0c\u5c31\u50cf\u662f\u5165\u4e86\u5730\u72f1\uff0c\u60f3\u79bb\u5f00\u53ea\u6709\u518d\u4e16\u4e3a\u4eba\u3002\u4f60\u8981\u8d70\uff0c\u6211\u5c31\u53ea\u6709\u6740\u4e86\u4f60\uff0c\u7528\u6211\u7684\u5de6\u624b\u6740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e00\u76f4\u5c06\u4ed6\u7684\u5de6\u624b\u85cf\u5728\u8863\u8896\u91cc\u3002 \u3000\u3000\u201c\u6211\u4ece\u6765\u4e0d\u7528\u6b66\u5668\uff0c\u6211\u8fd9\u53ea\u624b\u5c31\u662f\u6740\u4eba\u7684\u6b66\u5668\u3002\u201d\u4e25\u6b63\u521a\u9053\uff0c\u201c\u6c5f\u6e56\u4e2d\u5584\u7528\u5de6\u624b\u7684\u4eba\uff0c\u51fa\u624b\u7edd\u5bf9\u6ca1\u6709\u6bd4\u6211\u66f4\u5feb\u7684\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u7279\u522b\u6ce8\u610f\uff01\u201d \u3000\u3000\u201c\u6211\u89c1\u8fc7\u4f60\u51fa\u624b\uff0c\u6211\u5f53\u7136\u4f1a\u6ce8\u610f\u7684\u3002\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u53ef\u662f\u6211\u4e0d\u61c2\uff0c\u4f60\u65e2\u7136\u8981\u6740\u6211\uff0c\u4e3a\u4ec0\u4e48\u8981\u63d0\u9192\u6211\u6ce8\u610f\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u6b7b\u5f97\u5fc3\u670d\u53e3\u670d\u3002\u201d\u4e25\u6b63\u521a\u9053\uff0c\u201c\u6211\u8981\u4f60\u6b7b\u800c\u65e0\u6028\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4e25\u6b63\u521a\u679c\u7136\u4eba\u5982\u5176\u540d\uff0c\u516c\u6b63\u521a\u76f4\uff0c\u7edd\u4e0d\u80af\u505a\u6b3a\u4eba\u7684\u4e8b\uff0c\u6240\u4ee5\u4f60\u5982\u679c\u5076\u5c14\u505a\u4e00\u6b21\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6000\u7591\u7684\u3002\u201d \u3000\u3000\u4e25\u6b63\u521a\u7684\u8138\u8272\u8fd8\u6ca1\u6709\u53d8\uff0c\u773c\u795e\u5374\u5df1\u53d8\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u5982\u679c\u6211\u771f\u7684\u5168\u795e\u8d39\u6ce8\uff0c\u6ce8\u610f\u4f60\u7684\u5de6\u624b\uff0c\u4eca\u5929\u6211\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95f4\u7b11\u4e86\u7b11\uff0c\u201c\u5e78\u597d\u6211\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u67f3\u5206\u5206\u3002\u201d \u3000\u3000\u201c\u67f3\u5206\u5206\uff1f\u5979\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u8fde\u5979\u90fd\u6ca1\u6709\u6000\u7591\u4f60\uff0c\u8fde\u5979\u90fd\u4e0a\u4e86\u4f60\u7684\u5f53\uff0c\u4f55\u51b5\u6211\u8fd9\u4e2a\u521d\u51fa\u9053\u7684\u5c0f\u4f19\u5b50\uff1f\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u4f60\u80fd\u505a\u5b8b\u8001\u592b\u5b50\u7684\u7b2c\u4e09\u53ea\u624b\uff0c\u5f53\u7136\u4e5f\u53ef\u4ee5\u7528\u4ed6\u7684\u624b\u505a\u4f60\u7684\u7b2c\u4e09\u53ea\u624b\uff0c\u7528\u7b2c\u4e09\u53ea\u624b\u6765\u6740\u6211\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u90a3\u65f6\u6211\u6b7b\u5f97\u867d\u7136\u5fc3\u4e0d\u670d\u53e3\u4e0d\u670d\uff0c\u5fc3\u91cc\u5c31\u7b97\u6709\u4e00\u809a\u5b50\u6028\u6c14\uff0c\u4e5f\u53d1\u4e0d\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u4e25\u6b63\u521a\u7684\u8138\u8272\u4e5f\u5df2\u6539\u53d8\u4e86\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u4e0d\u592a\u7b28\u3002\u201d \u3000\u3000\u4ed6\u5df2\u51c6\u5907\u51fa\u624b\uff0c\u4ed6\u7684\u773c\u775b\u5374\u5728\u770b\u7740\u5c0f\u65b9\u8eab\u540e\u7684\u90a3\u9053\u5c0f\u95e8\uff0c\u5b8b\u8001\u592b\u5b50\u65e0\u7591\u5c31\u5728\u5c0f\u95e8\u540e\uff0c\u53ea\u8981\u4ed6\u4e00\u51fa\u624b\uff0c\u4e24\u4eba\u524d\u540e\u5939\u51fb\uff0c\u5c0f\u65b9\u8fd8\u662f\u5fc5\u6b7b\u65e0\u7591\uff0c\u6c5f\u6e56\u4e2d\u51e0\u4e4e\u5df2\u6ca1\u6709\u4eba\u80fd\u907f\u5f97\u5f00\u4ed6\u4eec\u7684\u5408\u529b\u4e00\u51fb\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u8fd8\u6709\u4ef6\u4e8b\u4f60\u4e00\u5b9a\u4e5f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u53e6\u5916\u4e5f\u6709\u53ea\u624b\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u7b2c\u4e09\u53ea\u624b\u3002\u201d \u3000\u3000\u4e25\u6b63\u521a\u51b7\u7b11\uff1a\u201c\u4f60\u4e5f\u6709\u7b2c\u4e09\u53ea\u624b\uff1f\u6211\u600e\u4e48\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u770b\u4e0d\u89c1\uff0c\u4f60\u6c38\u8fdc\u90fd\u770b\u4e0d\u89c1\u7684\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u4f46\u662f\u4f60\u7edd\u5bf9\u4e0d\u80fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u7b2c\u4e09\u53ea\u624b\uff0c\u73b0\u5728\u5df2\u7ecf\u88ab\u6211\u7684\u7b2c\u4e09\u53ea\u624b\u7ed1\u8d77\u6765\u4e86\u3002\u201d\u5c0f\u65b9\u7a81\u7136\u9053\uff1a\u201c\u5982\u679c\u4f60\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u81ea\u5df1\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u4e25\u6b63\u521a\u5f53\u7136\u4e0d\u4f1a\u53bb\u770b\u7684\uff0c\u4ed6\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5f88\u5c11\u7b11\uff0c\u6709\u65f6\u7ec8\u6708\u96be\u5f97\u4e00\u7b11\uff0c\u53ef\u662f\u8fd9\u6b21\u4ed6\u771f\u7684\u7b11\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\u771f\u7684\u5f88\u597d\u7b11\uff0c\u4ed6\u4ece\u6765\u90fd\u6ca1\u6709\u9047\u5230\u8fc7\u8fd9\u4e48\u53ef\u7b11\u7684\u4e8b\u3002 \u3000\u3000\u4e00\u4e2a\u521d\u51fa\u9053\u7684\u5e74\u8f7b\u5c0f\u4f19\u5b50\uff0c\u5c45\u7136\u60f3\u7528\u8fd9\u79cd\u6cd5\u5b50\u6765\u9a97\u4e00\u4e2a\u50cf\u4ed6\u8fd9\u6837\u7684\u8001\u6c5f\u6e56\u3002 \u3000\u3000\u4ed6\u5c11\u5e74\u65f6\u5c31\u5df2\u6210\u540d\uff0c\u58ee\u5e74\u65f6\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u6740\u4eba\u65e0\u7b97\uff0c\u4e2d\u5e74\u540e\u867d\u7136\u88ab\u4ec7\u5bb6\u903c\u5f97\u6539\u540d\u6362\u59d3\uff0c\u4ea1\u547d\u5929\u6daf\uff0c\u667a\u6167\u5374\u66f4\u6210\u719f\uff0c\u7ecf\u9a8c\u4e5f\u66f4\u4e30\u5bcc\uff0c\u4ed6\u600e\u4e48\u4f1a\u4e0a\u8fd9\u79cd\u5f53\uff01 \u3000\u3000\u5c31\u5728\u4ed6\u5f00\u59cb\u7b11\u7684\u65f6\u5019\uff0c\u4ed6\u85cf\u5728\u8863\u8896\u91cc\u7684\u624b\u5df2\u95ea\u7535\u822c\u51fb\u51fa\u3002 \u3000\u3000\u4ed6\u51fa\u624b\u65f6\uff0c\u5b8b\u8001\u592b\u5b50\u4e5f\u4e00\u5b9a\u4f1a\u914d\u5408\u4ed6\u51fa\u624b\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u80a9\u4f5c\u6218\u591a\u5e74\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u8eab\u7ecf\u767e\u6218\uff0c\u4ed6\u4eec\u7684\u914d\u5408\u4ece\u6765\u672a\u6709\u4e00\u6b21\u51fa\u8fc7\u610f\u5916\uff0c\u4ece\u672a\u6709\u4e00\u6b21\u5931\u8fc7\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5374\u662f\u4f8b\u5916\u3002 \u3000\u3000\u4e25\u6b63\u521a\u5df2\u51fa\u624b\uff0c\u95e8\u5916\u7684\u5b8b\u8001\u5934\u5b50\u5374\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u4e00\u51fb\u4e0d\u4e2d\uff0c\u518d\u51fa\u624b\u3002 \u3000\u3000\u95e8\u5916\u8fd8\u662f\u5b8c\u5168\u6ca1\u6709\u52a8\u9759\u3002 \u3000\u3000\u4e25\u6b63\u521a\u4e0d\u518d\u53d1\u51fa\u7b2c\u4e09\u51fb\uff0c\u7adf\u7136\u51cc\u7a7a\u8dc3\u8d77\uff0c\u63a0\u51fa\u90a3\u9053\u5c0f\u95e8\u3002 \u3000\u3000\u5b8b\u8001\u592b\u5b50\u679c\u7136\u5728\u95e8\u5916\uff0c\u5374\u5df2\u5012\u5728\u5899\u89d2\u4e0b\uff0c\u53ea\u80fd\u770b\u7740\u4ed6\u82e6\u7b11\u3002 \u3000\u3000\u4e25\u6b63\u521a\u7b11\u4e0d\u51fa\u6765\u4e86\uff0c\u4ed6\u7ec8\u4e8e\u53d1\u89c9\u8fd9\u4ef6\u4e8b\u4e00\u70b9\u90fd\u4e0d\u53ef\u7b11\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u786e\u4fe1\u4e25\u6b63\u521a\u7edd\u4e0d\u4f1a\u518d\u8ffd\uff0c\u51fb\u5012\u4e86\u5b8b\u8001\u592b\u5b50\uff0c\u5c31\u65e0\u5f02\u4e5f\u51fb\u5012\u4e86\u4e25\u6b63\u521a\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u662f\u7528\u4ed6\u7684\u201c\u7b2c\u4e09\u53ea\u624b\u201d\u51fb\u5012\u5b8b\u8001\u592b\u5b50\uff0c\u4ed6\u6ca1\u6709\u7b2c\u4e09\u53ea\u624b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6709\u7b2c\u4e8c\u53cc\u773c\u775b\u2014\u2014\u201c\u9633\u5149\u201d\u5c31\u662f\u4ed6\u7684\u7b2c\u4e8c\u53cc\u773c\u775b\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u201c\u9633\u5149\u201d\u7684\u6697\u793a\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u60f3\u5230\u5b8b\u8001\u592b\u5b50\u4f1a\u8eb2\u5728\u6697\u5904\u7b49\u7740\u548c\u4e25\u6b63\u521a\u524d\u540e\u5939\u51fb\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u8bf4\u7684\u867d\u7136\u5e76\u4e0d\u592a\u660e\u663e\uff0c\u5374\u5df2\u4f7f\u4ed6\u60f3\u8d77\u4e86\u4ed6\u4eec\u8054\u624b\u5bf9\u4ed8\u67f3\u5206\u5206\u65f6\u6240\u7528\u7684\u8be1\u8ba1\u3002 \u3000\u3000\u4ed6\u5148\u627e\u5230\u4e86\u5b8b\u8001\u592b\u5b50\uff0c\u5148\u7528\u5ba2\u6c14\u7684\u5fae\u7b11\u3001\u6709\u793c\u7684\u6001\u5ea6\u7a33\u4f4f\u4e86\u5b8b\u8001\u592b\u5b50\uff0c\u5c31\u5728\u5b8b\u8001\u592b\u5b50\u5df2\u7ecf\u8ba4\u4e3a\u4ed6\u5df2\u5b8c\u5168\u4e27\u5931\u6597\u5fd7\u65f6\uff0c\u4ed6\u5ffd\u7136\u51fa\u624b\u4e86\uff0c\u4ee5\u6700\u5feb\u7684\u624b\u6cd5\uff0c\u70b9\u4f4f\u4e86\u5b8b\u8001\u592b\u5b50\u4e09\u5904\u7a74\u9053\u3002 \u3000\u3000\u5b8b\u8001\u592b\u5b50\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u662f\u4ed6\u7684\u4ec7\u654c\uff0c\u5bf9\u4ed8\u4ec7\u654c\u662f\u53ef\u4ee5\u4e0d\u62e9\u624b\u6bb5\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u5bf9\u81ea\u5df1\u8fd9\u6b21\u884c\u52a8\u89c9\u5f97\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u4e0b\u4e00\u4e2a\u8981\u4e3a\u4ed6\u201c\u996f\u884c\u201d\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u4ed6\u8bb0\u5f97\u535c\u9e70\u66fe\u7ecf\u5bf9\u4ed6\u63d0\u8fc7\u201c\u6731\u4e91\u201d\u7684\u540d\u5b57\uff0c\u4e5f\u8bb0\u5f97\u6731\u4e91\u5c31\u662f\u201c\u9e70\u8bb0\u201d\u5546\u53f7\u7684\u603b\u7ba1\uff0c\u662f\u4e2a\u975e\u5e38\u8bda\u6073\u3001\u975e\u5e38\u89c4\u77e9\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u60f3\u5230\u4ed6\u4e5f\u662f\u4e2a\u8eab\u6000\u7edd\u6280\u3001\u6df1\u85cf\u4e0d\u9732\u7684\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u4f46\u662f\u535c\u9e70\u63d0\u5230\u4ed6\u540d\u5b57\u65f6\uff0c\u5374\u597d\u8c61\u628a\u4ed6\u7684\u4efd\u91cf\u770b\u5f97\u6bd4\u4e25\u6b63\u521a\u8fd8\u91cd\uff0c\u8981\u638c\u7ba1\u201c\u9e70\u8bb0\u201d\u5546\u53f7\u4e5f\u7edd\u4e0d\u662f\u4e00\u4e2a\u666e\u901a\u4eba\u6240\u80fd\u505a\u5f97\u5230\u7684\uff0c\u5982\u679c\u4ed6\u6ca1\u6709\u7279\u522b\u7684\u6b66\u529f\u548c\u624d\u80fd\uff0c\u535c\u9e70\u4e5f\u7edd\u4e0d\u4f1a\u5c06\u8fd9\u4e48\u91cd\u8981\u7684\u804c\u4f4d\u4ea4\u7ed9\u4ed6\u3002 \u3000\u3000\u5c0f\u65b9\u76f8\u4fe1\u535c\u9e70\u7edd\u4e0d\u4f1a\u770b\u9519\u4eba\uff0c\u4ed6\u5bf9\u6731\u4e91\u5df2\u7ecf\u6709\u4e86\u6212\u5fc3\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u770b\u89c1\u4e86\u6731\u4e91\u3002 \u3000\u3000\u6731\u4e91\u770b\u6765\u8fd8\u662f\u548c\u5e73\u65f6\u4e00\u6837\u8001\u5b9e\u89c4\u77e9\uff0c\u552f\u4e00\u4e0d\u540c\u7684\u5730\u65b9\u662f\uff0c\u4ed6\u624b\u4e0a\u591a\u4e86\u4e00\u67c4\u5251\u3002 \u3000\u3000\u4e00\u67c4\u5f88\u666e\u901a\u7684\u9752\u94a2\u5251\uff0c\u5251\u5df2\u51fa\u9798\u3002 \u3000\u3000\u6731\u4e91\u53cc\u624b\u62b1\u5251\uff0c\u5251\u5c16\u4e0b\u5782\uff0c\u5411\u5c0f\u65b9\u606d\u656c\u884c\u793c\u3002 \u3000\u3000\u201c\u665a\u8f88\u6731\u4e91\uff0c\u606d\u8bf7\u65b9\u5927\u4fa0\u8d50\u62db\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u4e0d\u662f\u5927\u4fa0\uff0c\u4f60\u4e5f\u4e0d\u662f\u6211\u7684\u665a\u8f88\uff0c\u4f60\u4e0d\u5fc5\u592a\u5ba2\u6c14\u3002\u201d \u3000\u3000\u4ed6\u521a\u624d\u5bf9\u5b8b\u8001\u592b\u5b50\u7684\u6001\u5ea6\u548c\u6731\u4e91\u5bf9\u4ed6\u540c\u6837\u5ba2\u6c14\uff0c\u73b0\u5728\u5b8b\u8001\u592b\u5b50\u5df1\u5012\u5728\u5899\u89d2\u91cc\u3002 \u3000\u3000\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u53c8\u5b66\u4f1a\u4e86\u5f88\u591a\u4e8b\u3002 \u3000\u3000\u4ed6\u4e5f\u660e\u767d\u6731\u4e91\u7684\u610f\u601d\u2014\u2014\u665a\u8f88\u6c42\u524d\u8f88\u8d50\u62db\uff0c\u5c31\u4e0d\u5fc5\u5927\u516c\u5e73\u4e86\uff0c\u524d\u8f88\u7684\u624b\u91cc\u6ca1\u6709\u5251\uff0c\u665a\u8f88\u4e5f\u4e00\u6837\u53ef\u4ee5\u51fa\u624b\u7684\u3002 \u3000\u3000\u6731\u4e91\u679c\u7136\u5df2\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u7684\u51fa\u624b\u5e76\u4e0d\u5feb\uff0c\u62db\u5f0f\u95f4\u7684\u53d8\u5316\u4e5f\u4e0d\u5feb\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u7684\u62db\u5f0f\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u7cbe\u5999\u590d\u6742\u7684\u53d8\u5316\uff0c\u53ea\u4e0d\u8fc7\u6bcf\u4e00\u62db\u90fd\u7528\u5f97\u5f88\u5b9e\u9645\uff0c\u5f88\u6709\u6548\u3002 \u3000\u3000\u8fd9\u79cd\u5251\u672f\u867d\u7136\u4e5f\u6709\u5b83\u7684\u4f18\u70b9\uff0c\u53ef\u662f\u7528\u6765\u5bf9\u4ed8\u5c0f\u65b9\u5c31\u4e0d\u884c\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u867d\u7136\u8d64\u624b\u7a7a\u62f3\uff0c\u53ef\u662f\u65bd\u5c55\u5f00\u6bcf\u4e2a\u7ec3\u6b66\u8005\u90fd\u5fc5\u5b66\u7684\u201c\u7a7a\u624b\u4eba\u767d\u5203\u7684\u529f\u592b\uff0c\u5e94\u4ed8\u8fd9\u67c4\u4e0d\u5df2\u6e38\u5203\u6709\u4f59\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5df2\u7ecf\u5728\u6000\u7591\uff0c\u535c\u9e70\u5bf9\u6731\u4e91\u662f\u4e0d\u662f\u4f30\u8ba1\u5f97\u592a\u4ea2\u4e9b\uff0c\u6731\u4e91\u662f\u4e0d\u662f\u8fd8\u6ca1\u6709\u5c06\u771f\u529f\u592b\u4f7f\u51fa\u6765\u3002 \u3000\u3000\u5c0f\u65b9\u6b63\u60f3\u589e\u52a0\u538b\u529b\uff0c\u903c\u4ed6\u4f7f\u51fa\u5168\u529b\uff0c\u6731\u4e91\u5374\u5df2\u7ecf\u540e\u9000\u5341\u6b65\uff0c\u518d\u6b21\u7528\u53cc\u624b\u62b1\u5251\uff0c\u5251\u5c16\u4e0b\u5782\uff0c\u5411\u5c0f\u65b9\u606d\u656c\u884c\u793c\uff1a\u201c\u665a\u8f88\u4e0d\u662f\u65b9\u5927\u4fa0\u5bf9\u624b\uff0c\u665a\u8f88\u5df2\u7ecf\u8d25\u4e86\u3002\u201d \u3000\u3000\u73b0\u5728\u5c31\u8ba4\u8f93\u672a\u514d\u8fd8\u592a\u65e9\uff0c\u535c\u9e70\u5c5e\u4e0b\uff0c\u672c\u4e0d\u8be5\u6709\u8fd9\u79cd\u4eba\u7684\u3002 \u3000\u3000\u535c\u9e70\u5c5e\u4e0b\u90fd\u662f\u6218\u58eb\uff0c\u4e0d\u594b\u6218\u5230\u6700\u540e\u5173\u5934\uff0c\u7edd\u4e0d\u4f1a\u8f7b\u6613\u653e\u5f03\u3002 \u3000\u3000\u6731\u4e91\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u65b9\u5927\u4fa0\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u665a\u8f88\u8fd8\u672a\u5c3d\u5168\u529b\uff0c\u8fd8\u4e0d\u8be5\u653e\u624b\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u8fd9\u4e00\u70b9\uff0c\u6731\u4e91\u5fae\u7b11\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u613f\u518d\u6218\uff0c\u53ea\u56e0\u4e3a\u665a\u8f88\u5df2\u4e0d\u5fcd\u4e0e\u65b9\u5927\u4fa0\u7f20\u6597\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u4e0d\u5fcd\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u5fcd\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u65b9\u5927\u4fa0\u5df2\u4e2d\u4e86\u5947\u6bd2\uff0c\u5df2\u7ecf\u7edd\u5bf9\u6d3b\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\u4e86\u3002\u201d\u6731\u4e91\u9053\uff1a\u201c\u5982\u679c\u665a\u8f88\u518d\u7f20\u6597\u4e8c\u5341\u62db\uff0c\u65b9\u5927\u5251\u7684\u6bd2\u6027\u4e00\u53d1\u4f5c\uff0c\u5c31\u5fc5\u6b7b\u65e0\u6551\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u5728\u7b11\u3002 \u3000\u3000\u6731\u4e91\u8bf4\u7684\u8bdd\uff0c\u4ed6\u6839\u672c\u5c31\u4e0d\u4fe1\uff0c\u8fde\u4e00\u53e5\u4e5f\u4e0d\u4fe1\u3002 \u3000\u3000\u201c\u6211\u4e2d\u4e86\u6bd2\uff1f\u4f60\u770b\u5f97\u6211\u4e2d\u4e86\u6bd2\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6545\u610f\u95ee\uff1a\u201c\u662f\u4ec0\u4e48\u65f6\u5019\u4e2d\u7684\u6bd2\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u7247\u523b\u4e4b\u524d\u3002\u201d \u3000\u3000\u201c\u535c\u9e70\u7ed9\u6211\u559d\u7684\u9152\u4e2d\u6709\u6bd2\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u9152\u91cc\u7edd\u5bf9\u6ca1\u6709\u6bd2\u3002\u201d\u6731\u4e91\u9053\uff0c\u201c\u4ed6\u8981\u6740\u4f60\uff0c\u4e5f\u4e0d\u5fc5\u7528\u6bd2\u9152\u3002\u201d \u3000\u3000\u201c\u6bd2\u4e0d\u5728\u9152\u91cc\uff0c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u624b\u4e0a\uff1f\u201d \u3000\u3000\u201c\u8c01\u7684\u624b\uff1f\u201d \u3000\u3000\u6731\u4e91\u53cd\u95ee\uff1a\u201c\u4f60\u521a\u624d\u63e1\u8fc7\u8c01\u7684\u624b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u521a\u624d\u53ea\u63e1\u8fc7\u201c\u9633\u5149\u201d\u7684\u624b\uff0c\u4ed6\u7edd\u4e0d\u76f8\u4fe1\u201c\u9633\u5149\u201d\u4f1a\u6697\u7b97\u4ed6\u3002 \u3000\u3000\u6731\u4e91\u5374\u5728\u53f9\u606f\uff1a\u201c\u5176\u5b9e\u4f60\u5e94\u8be5\u60f3\u5f97\u5230\u7684\u3002\u5979\u4e5f\u662f\u4e3a\u4f60\u996f\u884c\u7684\u4eba\uff0c\u7b2c\u4e00\u4e2a\u4e3a\u4f60\u996f\u884c\u7684\u5c31\u662f\u5979\uff1b\u53ea\u4e0d\u8fc7\u5979\u7528\u7684\u624b\u6cd5\u548c\u6211\u4eec\u4e0d\u540c\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u201c\u5979\u7528\u7684\u65b9\u6cd5\u6bd4\u6211\u4eec\u6e29\u548c\u3002\u201d\u6731\u4e91\u9053\uff1a\u201c\u4f46\u662f\u4e5f\u8fdc\u6bd4\u6211\u4eec\u6709\u6548\u3002\u201d \u3000\u3000\u201c\u5979\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u6700\u8fd1\u5e38\u5728\u4e00\u8d77\uff0c\u4f60\u5e94\u8be5\u770b\u89c1\u5979\u624b\u4e0a\u4e00\u76f4\u6234\u7740\u4e2a\u6212\u6307\u3002\u201d \u3000\u3000\u5c0f\u65b9\u770b\u89c1\u8fc7\u90a3\u4e2a\u6212\u6307\uff0c\u7eaf\u91d1\u7684\u6212\u6307\uff0c\u5f0f\u6837\u4eff\u4f5b\u5f88\u597d\uff0c\u624b\u5de5\u4e5f\u5f88\u597d\u3002 \u3000\u3000\u7a76\u7adf\u662f\u4ec0\u4e48\u5f0f\u6837\uff0c\u5c0f\u65b9\u90fd\u5df1\u8bb0\u4e0d\u6e05\u4e86\u3002\u5728\u62c9\u8428\uff0c\u6bcf\u4e2a\u5973\u4eba\u90fd\u6234\u7740\u91d1\u9970\uff0c\u5728\u6bcf\u4e00\u6761\u6cb3\u6d41\u6ee9\u5934\uff0c\u90fd\u53ef\u4ee5\u770b\u89c1\u4eba\u4eec\u7528\u6700\u53e4\u8001\u539f\u59cb\u7684\u65b9\u6cd5\u5c31\u80fd\u635e\u53d6\u5230\u5927\u91cf\u7684\u91d1\u6c99\u3002 \u3000\u3000\u624b\u4e0a\u6234\u7740\u4e00\u4e2a\u7eaf\u91d1\u7684\u6212\u6307\uff0c\u5728\u8fd9\u91cc\u7edd\u4e0d\u662f\u4ef6\u80fd\u591f\u5f15\u4eba\u6ce8\u610f\u7684\u4e8b\u3002 \u3000\u3000\u201c\u53ef\u662f\u5979\u6234\u7684\u90a3\u4e2a\u6212\u6307\u4e0d\u540c\u3002\u201d\u6731\u4e91\u9053\uff0c\u201c\u90a3\u4e2a\u6212\u6307\u867d\u7136\u53ea\u6709\u51e0\u94b1\u91cd\uff0c\u5374\u8fdc\u6bd4\u51e0\u767e\u4e24\u9ec4\u91d1\u66f4\u73cd\u8d35\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u5b83\u7684\u624b\u5de5\u7279\u522b\u7cbe\u7ec6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53bf\u201d \u3000\u3000\u201c\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u56e0\u4e3a\u6212\u6307\u4e0a\u7684\u6bd2\u3002\u201d\u6731\u4e91\u9053\uff0c\u201c\u662f\u7528\u4e09\u5341\u4e09\u79cd\u6bd2\u6dec\u6210\u7684\u3002\u5148\u5c06\u8fd9\u4e09\u5341\u4e09\u79cd\u5267\u6bd2\u6dec\u5165\u9ec4\u91d1\uff0c\u518d\u6253\u6210\u8fd9\u4e48\u6837\u4e00\u4e2a\u6212\u6307\u3002\u6212\u6307\u4e0a\u6709\u4e00\u6839\u523a\uff0c\u6bd4\u9488\u5c16\u8fd8\u7ec6\u7684\u523a\uff0c\u523a\u5165\u4f60\u7684\u76ae\u80a4\u65f6\uff0c\u4f60\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\uff0c\u53ef\u662f\u534a\u4e2a\u65f6\u8fb0\u5185\uff0c\u4f60\u5df2\u5fc5\u6b7b\u65e0\u6551\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u4e0d\u7b11\u4e86\uff0c\u4f46\u662f\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u53cd\u5e94\u3002\u6731\u4e91\u5374\u4eff\u4f5b\u5728\u4e3a\u4ed6\u60cb\u60dc\uff1a\u201c\u672c\u6765\u6211\u4eec\u90fd\u5df2\u7ecf\u628a\u4f60\u5f53\u4f5c\u670b\u53cb\uff0c\u5982\u679c\u4f60\u4e0d\u8d70\uff0c\u8fd9\u91cc\u7edd\u5bf9\u6ca1\u6709\u4eba\u4f1a\u4f24\u5bb3\u4f60\uff0c\u2018\u9633\u5149\u2019\u66f4\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\u8bf4\uff1a\u201c\u4e0d\u5e78\u73b0\u5728\u6211\u4eec\u5df2\u7ecf\u4e0d\u662f\u670b\u53cb\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u6253\u65ad\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u60f3\u8bf4\u4ec0\u4e48\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u4e0d\u662f\u670b\u53cb\uff0c\u5c31\u662f\u4ec7\u654c\uff0c\u6240\u4ee5\u5979\u624d\u4f1a\u7528\u8fd9\u79cd\u65b9\u6cd5\u5bf9\u4ed8\u6211\uff0c\u4f60\u4eec\u5bf9\u4ed8\u4ec7\u654c\u672c\u6765\u5c31\u662f\u4e0d\u62e9\u624b\u6bb5\u7684\u3002\u201d \u3000\u3000\u6731\u4e91\u5e76\u4e0d\u5426\u8ba4\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u9053\uff1a\u201c\u5979\u5148\u628a\u4e25\u6b63\u521a\u548c\u5b8b\u8001\u592b\u5b50\u7684\u6740\u7740\u544a\u8bc9\u6211\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u7a33\u4f4f\u6211\uff0c\u8981\u6211\u5bf9\u5979\u5b8c\u5168\u4fe1\u4efb\uff0c\u5979\u624d\u80fd\u5728\u6211\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u628a\u6bd2\u523a\u523a\u5165\u6211\u7684\u638c\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\uff1a\u201c\u53ef\u662f\u4f60\u4e3a\u4ec0\u4e48\u8981\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u6211\u5462\uff1f\u201d \u3000\u3000\u6731\u4e91\u8fd8\u6ca1\u6709\u56de\u7b54\uff0c\u5c0f\u65b9\u53c8\u95ee\u4ed6\uff1a\u201c\u6bd2\u86c7\u566c\u624b\uff0c\u58ee\u58eb\u65ad\u8155\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u65a9\u65ad\u81ea\u5df1\u8fd9\u53ea\u624b\uff1f\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u516d\u7ae0\u3000\u65ad\u9b42\u5251\u65ad\u80a0\u4eba",content:(0,a.jsx)(a.Fragment,{children:"\u201c\u4e0d\u662f\u3002\u201d\u6731\u4e91\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u542c\u51fa\u4ed6\u8bdd\u4e2d\u7684\u8ba5\u6d88\u4e4b\u610f\uff0c\u201c\u4f46\u662f\u4f60\u4e0d\u59a8\u5148\u770b\u770b\u4f60\u81ea\u5df1\u8fd9\u53ea\u624b\uff0c\u770b\u770b\u4f60\u624b\u4e0a\u662f\u4e0d\u662f\u5df2\u7ecf\u6709\u4e86\u4e2a\u597d\u50cf\u88ab\u6bd2\u8702\u87ab\u8fc7\u7684\u4f24\u53e3\u3002\u5982\u679c\u4f24\u53e3\u8fd8\u6ca1\u6709\u53d1\u751f\u53d8\u5316\uff0c\u4e5f\u8bb8\u4f60\u8fd8\u6709\u5f97\u6551\u3002\u201d \u3000\u3000\u201c\u6211\u8fd8\u6709\u6551\uff1f\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u8c01\u4f1a\u6765\u6551\u6211\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u80af\u7559\u4e0b\u6765\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u6551\u4f60\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5bf9\u201c\u9633\u5149\u201d\u7684\u4fe1\u5fc3\u65e0\u7591\u5df2\u7ecf\u5f00\u59cb\u52a8\u6447\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u521a\u521a\u5347\u8d77\u7684\u660e\u6708\uff0c\u4f38\u51fa\u4e86\u90a3\u53ea\u66fe\u7ecf\u88ab\u201c\u9633\u5149\u201d\u63e1\u4f4f\u7684\u624b\u3002\u4ed6\u7684\u8eab\u5b50\u521a\u521a\u8f6c\u8fc7\u53bb\uff0c\u6731\u4e91\u7684\u5de6\u624b\u91cc\u5df2\u7ecf\u6709\u4e03\u70b9\u5bd2\u661f\u66b4\u5c04\u800c\u51fa\uff0c\u4e0d\u662f\u7528\u8155\u529b\u53d1\u51fa\u7684\uff0c\u662f\u7528\u4e00\u79cd\u529b\u91cf\u6781\u5f3a\u7684\u673a\u7c27\u7b52\u5c04\u51fa\u6765\u7684\u3002\u6c5f\u6e56\u4e2d\u4eba\u7528\u6697\u5668\u7684\u79cd\u7c7b\u867d\u7136\u591a\uff0c\u201c\u593a\u547d\u4e03\u661f\u9488\u201d\u6c38\u8fdc\u90fd\u662f\u5176\u4e2d\u6700\u53ef\u6015\u7684\u4e00\u79cd\u3002 \u3000\u3000\u673a\u7c27\u201c\u5d29\u201d\u7684\u4e00\u54cd\uff0c\u6731\u4e91\u53f3\u638c\u4e2d\u7684\u9752\u94a2\u5251\u4e5f\u5df2\u95ea\u7535\u822c\u523a\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u7ecf\u4e0d\u50cf\u521a\u624d\u90a3\u4ee5\u6162\u4e86\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u95ea\u52a8\u7684\u5251\u5149\u5c31\u5df1\u5c06\u5c0f\u65b9\u6240\u6709\u7684\u9000\u8def\u5168\u90fd\u5c01\u6b7b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u7247\u523b\uff0c\u4ed6\u597d\u50cf\u5c31\u5df2\u7ecf\u53d8\u6210\u4e86\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u4ece\u4e00\u4e2a\u5e73\u5eb8\u7684\u5251\u624b\uff0c\u53d8\u6210\u4e86\u4e2a\u975e\u51e1\u7684\u5251\u5ba2\u3002\u5982\u679c\u4ed6\u4e00\u5f00\u59cb\u5c31\u4f7f\u51fa\u8fd9\u79cd\u5251\u672f\uff0c\u5c0f\u65b9\u7edd\u4e0d\u4f1a\u8eb2\u4e0d\u5f00\u7684\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u5df2\u5c06\u5c0f\u65b9\u7684\u4fe1\u5fc3\u6467\u6bc1\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u53d1\u73b0\u81ea\u5df1\u88ab\u4e00\u4e2a\u81ea\u5df1\u7edd\u5bf9\u4fe1\u4efb\u7684\u670b\u53cb\u51fa\u5356\u4e86\u65f6\uff0c\u60c5\u7eea\u90fd\u4f1a\u53d8\u5f97\u5341\u5206\u4f4e\u843d\u3001\u6cae\u4e27\uff0c\u4f55\u51b5\u5c0f\u65b9\u6b63\u5728\u770b\u4ed6\u624b\u4e0a\u7684\u4f24\u53e3\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u8981\u5728\u6708\u5149\u4e0b\u67e5\u770b\u4e00\u4e2a\u6bd4\u9488\u8fd8\u5c0f\u7684\u4f24\u53e3\uff0c\u90fd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u5c06\u5168\u526f\u7cbe\u795e\u90fd\u96c6\u4e2d\u5728\u4ed6\u81ea\u5df1\u7684\u624b\u4e0a\uff0c\u4ed6\u7684\u4fe1\u5fc3\u5df2\u7ecf\u88ab\u6467\u6bc1\uff0c\u60c5\u7eea\u4e5f\u5df1\u6cae\u4e27\uff0c\u4ed6\u600e\u4e48\u80fd\u907f\u5f97\u5f00\u8fd9\u4e00\u5251\uff1f \u3000\u3000\u6731\u4e91\u4e00\u5251\u523a\u51fa\uff0c\u5c31\u7b97\u51c6\u5c0f\u65b9\u5df2\u7ecf\u6b7b\u5b9a\u4e86\u3002 \u3000\u3000\u5982\u679c\u5c0f\u65b9\u771f\u7684\u76f8\u4fe1\u4e86\u6731\u4e91\u7684\u8bdd\uff0c\u771f\u7684\u53bb\u770b\u624b\u4e0a\u662f\u4e0d\u662f\u6709\u4e2a\u4f24\u53e3\uff0c\u4ed6\u5c31\u771f\u7684\u6b7b\u5b9a\u4e86\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5bf9\u201c\u9633\u5149\u201d\u6709\u4fe1\u5fc3\uff0c\u5bf9\u4eba\u7c7b\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u4fe1\u5fc3\u7edd\u4e0d\u662f\u522b\u4eba\u51e0\u53e5\u8bdd\u5c31\u53ef\u4ee5\u6467\u6bc1\u7684\uff0c\u6240\u4ee5\u4ed6\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u6731\u4e91\u5bf9\u81ea\u5df1\u8fd9\u4e00\u5251\u5927\u6709\u628a\u63e1\u4e86\uff0c\u5bf9\u4ed6\u7684\u4e03\u661f\u9488\u4e5f\u5927\u6709\u628a\u63e1\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e00\u5251\u523a\u51fa\uff0c\u5df2\u5c3d\u5168\u529b\uff0c\u53ea\u8bb0\u5f97\u201c\u653b\u201d\u800c\u5fd8\u4e86\u201c\u5b88\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u7684\u653b\u52bf\u867d\u7136\u51cc\u5389\u9738\u9053\uff0c\u5374\u6709\u7a7a\u95e8\uff0c\u4e5f\u6709\u7834\u7efd\u3002\u4ed6\u4ee5\u4e3a\u5c0f\u65b9\u7684\u9000\u8def\u5168\u90fd\u5df2\u88ab\u5c01\u6b7b\uff0c\u5374\u5fd8\u4e86\u5c0f\u65b9\u8fd8\u6709\u4e00\u6761\u8def\u53ef\u8d70\uff0c\u8fd8\u53ef\u4ee5\u201c\u4ee5\u653b\u4e3a\u5b88\u201d\uff0c\u4ece\u4ed6\u7684\u7a7a\u95e8\u7834\u7efd\u4e2d\u653b\u51fa\u53bb\uff0c\u653b\u4ed6\u7684\u5fc3\u810f\uff0c\u653b\u4ed6\u7684\u547d\u8109\uff0c\u653b\u4ed6\u7684\u5fc5\u6551\u5904\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u6740\u6b7b\u6731\u4e91\u3002 \u3000\u3000\u4ed6\u5148\u4ee5\u5de6\u638c\u659c\u5207\u6731\u4e91\u63e1\u5251\u7684\u8155\uff0c\u6a2a\u6b65\u8eb2\u5165\u6731\u4e91\u7684\u7a7a\u95e8\uff0c\u66f2\u65f6\u6253\u6731\u4e91\u7684\u808b\u90e8\uff0c\u5e76\u4e2d\u6307\u98df\u6307\u65e0\u540d\u6307\u4f5c\u6307\u950b\uff0c\u731b\u6233\u6731\u4e91\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u653b\u7684\u90fd\u662f\u8981\u5bb3\uff0c\u6731\u4e91\u4e0d\u80fd\u4e0d\u95ea\u907f\u81ea\u6551\u3002\u5c0f\u65b9\u53f3\u624b\u4e94\u6307\u5ffd\u7136\u5316\u9e70\u722a\uff0c\u6293\u6731\u4e91\u7684\u9762\u95e8\uff0c\u4e71\u6731\u4e91\u7684\u773c\u795e\uff0c\u5de6\u638c\u5df2\u659c\u5207\u5728\u6731\u4e91\u53f3\u80a9\u4e0a\u3002 \u3000\u3000\u53f3\u80a9\u88ab\u51fb\uff0c\u9752\u94a2\u5251\u5fc5\u7136\u8131\u624b\u3002 \u3000\u3000\u5c0f\u65b9\u5269\u673a\u593a\u5251\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u5251\u950b\u5df2\u5728\u6731\u4e91\u54bd\u5589\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u6740\u6731\u4e91\u3002 \u3000\u3000\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u53ea\u56e0\u4e3a\u4f60\u867d\u7136\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u4e5f\u4e0d\u662f\u6211\u7684\u4ec7\u654c\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u8981\u6740\u6211\uff0c\u53ea\u4e0d\u8fc7\u662f\u5728\u505a\u4e00\u4ef6\u4f60\u8ba4\u4e3a\u5e94\u8be5\u505a\u7684\u4e8b\u3002\u201d \u3000\u3000\u5251\u950b\u4e0b\u7684\u6731\u4e91\u5c45\u7136\u8fd8\u80fd\u4fdd\u6301\u9547\u9759\uff0c\u5374\u5fcd\u4e0d\u4f4f\u8981\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u771f\u7684\u76f8\u4fe1\u2018\u9633\u5149\u2019\u7edd\u4e0d\u4f1a\u5bb3\u4f60\uff1f\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u5982\u6b64\u4fe1\u4efb\u5979\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u56de\u7b54\u5f88\u7b80\u5355\uff1a\u201c\u56e0\u4e3a\u6211\u4ece\u672a\u6b3a\u9a97\u8fc7\u5979\u3002\u201d \u3000\u3000\u6731\u4e91\u5ffd\u7136\u957f\u53f9\uff1a\u201c\u6211\u4f69\u670d\u4f60\uff0c\u4f60\u7684\u786e\u662f\u4e2a\u597d\u670b\u53cb\u3002\u201d\u6731\u4e91\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u7684\u670b\u53cb\u5012\u672a\u5fc5\u90fd\u662f\u597d\u670b\u53cb\uff0c\u6240\u4ee5\u6211\u529d\u4f60\u6700\u597d\u5c06\u6211\u7684\u5251\u5e26\u8d70\u3002\u201d \u3000\u3000\u201c\u6211\u65e2\u7136\u4e0d\u8981\u4f60\u7684\u547d\uff0c\u4e3a\u4ec0\u4e48\u8981\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u5f88\u5feb\u5c31\u4f1a\u7528\u5f97\u7740\u7684\u3002\u201d\u6731\u4e91\u9053\uff1a\u201c\u4e5f\u8bb8\u5e76\u4e0d\u662f\u7528\u6765\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u7528\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6731\u4e91\u770b\u7740\u5c0f\u65b9\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u8bf4\uff1a\u201c\u8fd9\u67c4\u5251\u4e5f\u8ddf\u522b\u7684\u5251\u4e00\u6837\uff0c\u9664\u4e86\u6740\u4eba\u5916\uff0c\u53e6\u5916\u8fd8\u6709\u79cd\u7528\u5904\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u7528\u5904\uff1f\u201d \u3000\u3000\u201c\u81ea\u520e\u3002\u201d\u6731\u4e91\u53c8\u53f9\u53e3\u6c14\uff0c\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u81ea\u520e\u81f3\u5c11\u6bd4\u6b7b\u5728\u522b\u4eba\u5251\u4e0b\u597d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u53c8\u6709\u4e2a\u4eba\u51b7\u51b7\u5730\u8bf4\uff1a\u201c\u5c31\u7b97\u4ed6\u8981\u81ea\u520e\uff0c\u4e5f\u4e0d\u5fc5\u7528\u4f60\u7684\u5251\uff0c\u4ed6\u81ea\u5df1\u4e5f\u6709\u5251\uff0c\u4ed6\u7684\u5251\u8fdc\u6bd4\u4f60\u7684\u5251\u950b\u5229\u3002\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u5ffd\u7136\u6709\u5251\u5149\u4e00\u95ea\uff0c\u4e00\u67c4\u5251\u4eff\u4f5b\u5ffd\u7136\u81ea\u5927\u5916\u98de\u6765\uff0c\u659c\u63d2\u5728\u5c0f\u65b9\u8db3\u4e0b\u3002 \u3000\u3000\u68ee\u5bd2\u7684\u5251\u5149\uff0c\u5251\u950b\u4e0a\u4eff\u4f5b\u6709\u4e00\u53ea\u90aa\u6076\u7684\u9e70\u773c\u5728\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u8fd9\u6b63\u662f\u4ed6\u7684\u201c\u9b54\u773c\u3002\u201d \u3000\u3000\u8fd9\u67c4\u5251\u4e00\u76f4\u5728\u535c\u9e70\u90a3\u91cc\uff0c\u5c0f\u65b9\u4ece\u672a\u63d0\u8d77\u8fc7\uff0c\u5c31\u597d\u50cf\u5df2\u7ecf\u5fd8\u4e86\u8fd9\u67c4\u5251\u7684\u5b58\u5728\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u7684\u5251\u53c8\u98de\u56de\u6765\u4e86\uff0c\u5f53\u7136\u4e0d\u662f\u4ece\u5929\u5916\u98de\u6765\u7684\u3002 \u3000\u3000\u662f\u4ece\u4e00\u4e2a\u4eba\u624b\u91cc\u98de\u51fa\u6765\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u56de\u8fc7\u5934\uff0c\u5c31\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\uff0c\u5140\u9e70\u822c\u7684\u9510\u773c\uff0c\u5e7d\u7075\u822c\u7684\u767d\u8863\uff0c\u5200\u950b\u6bb7\u51b7\u9177\uff0c\u5c71\u5cb3\u822c\u9547\u5b9a\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u535c\u9e70\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6700\u540e\u4e00\u4e2a\u8981\u4e3a\u4ed6\u996f\u884c\u7684\uff0c\u7adf\u662f\u535c\u9e70\u3002 \u3000\u3000\u6731\u4e91\u4ea4\u7ed9\u4ed6\u8fd9\u67c4\u94a2\u5251\uff0c\u7684\u786e\u4e0d\u662f\u8981\u7528\u4ed6\u6765\u6740\u4eba\u7684\uff0c\u5728\u535c\u9e70\u5251\u4e0b\uff0c\u4ed6\u6839\u672c\u5168\u65e0\u673a\u4f1a\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u5df2\u7ecf\u53ef\u4ee5\u5f88\u4eb2\u8fd1\u7684\u670b\u53cb\uff0c\u73b0\u5728\u5374\u5df2\u597d\u50cf\u662f\u4e24\u4e2a\u4e16\u754c\u7684\u4eba\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u4ed6\u8fd9\u4e00\u751f\u4ece\u672a\u7b11\u5f97\u5982\u6b64\u6c89\u75db\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u4e5f\u4f1a\u6765\u4e3a\u6211\u996f\u884c\u3002\u201d\u5c0f\u65b9\u9053\u3002\u201c\u4f60\u65e2\u7136\u6765\u4e3a\u6211\u996f\u884c\uff0c\u53c8\u4f55\u5fc5\u628a\u8fd9\u67c4\u5251\u8fd8\u7ed9\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u672c\u6765\u5c31\u662f\u4f60\u7684\u5251\u3002\u201d \u3000\u3000\u535c\u9e70\u7684\u58f0\u97f3\u91cc\u5168\u65e0\u611f\u60c5\uff1a\u201c\u4f60\u5e94\u8be5\u8bb0\u5f97\u6211\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u6211\u4ece\u6765\u4e0d\u8981\u6d3b\u4eba\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u8bb0\u5f97\u3002\u4e5f\u8bb8\u535c\u9e70\u6839\u672c\u5c31\u6ca1\u6709\u63a5\u53d7\u8fc7\u4ed6\u4efb\u4f55\u4e00\u6837\u4e1c\u897f\u2014\u2014\u4ed6\u7684\u5251\u3001\u4ed6\u7684\u53cb\u60c5\uff0c\u90fd\u6ca1\u6709\u63a5\u53d7\u8fc7\u3002 \u3000\u3000\u535c\u9e70\u53c8\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u6709\u4e86\u81ea\u5df1\u7684\u5251\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u5c06\u4f60\u624b\u91cc\u7684\u5251\u8fd8\u7ed9\u6731\u4e91\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5c06\u5251\u8fd8\u7ed9\u4e86\u6731\u4e91\uff0c\u5251\u67c4\u7f20\u7740\u7684\u9752\u7ede\u5df2\u7ecf\u88ab\u4ed6\u638c\u5fc3\u7684\u51b7\u6c57\u6e7f\u900f\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u53c8\u51b7\u7b11\uff1a\u201c\u73b0\u5728\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8d70\uff1f\u662f\u4e0d\u662f\u8fd8\u60f3\u4eb2\u773c\u770b\u7740\u6211\u6740\u4ed6\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u662f\u5bf9\u6731\u4e91\u8bf4\u7684\u3002 \u3000\u3000\u6731\u4e91\u53ea\u6709\u8d70\uff0c\u867d\u7136\u4e0d\u60f3\u8d70\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u8d70\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u4e5f\u51b7\u7b11\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u4ed6\u8d70\uff1f\u201d\u5c0f\u65b9\u95ee\u535c\u9e70\uff0c\u201c\u4f60\u6740\u4eba\u65f6\u4e3a\u4ec0\u4e48\u6015\u88ab\u4eba\u770b\u89c1\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u7b49\u535c\u9e70\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u77e5\u9053\u535c\u9e70\u4e00\u5b9a\u4e0d\u4f1a\u56de\u7b54\u7684\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u62d4\u8d77\u4e86\u4ed6\u7684\u5251\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u8ddf\u968f\u5c0f\u65b9\u5df2\u591a\u5e74\uff0c\u6bcf\u6b21\u4ed6\u63e1\u8d77\u5b83\u7684\u5251\u67c4\u65f6\uff0c\u5fc3\u91cc\u90fd\u4f1a\u6709\u79cd\u5145\u5b9e\u7684\u611f\u89c9\uff0c\u5c31\u597d\u50cf\u63e1\u4f4f\u4e86\u4e00\u4e2a\u597d\u670b\u53cb\u7684\u624b\u4e00\u6837\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u6b21\u4ed6\u63e1\u5251\u65f6\uff0c\u5374\u597d\u50cf\u63e1\u4f4f\u4e86\u4e00\u4e2a\u6b7b\u4eba\u7684\u624b\uff0c\u51b0\u51b7\u50f5\u786c\u7684\u6b7b\u4eba\u7684\u624b\uff0c\u5c31\u597d\u50cf\u5728\u8ddf\u4e00\u4e2a\u6b7b\u53bb\u7684\u670b\u53cb\u6700\u540e\u4e00\u6b21\u63e1\u624b\u8bc0\u522b\u3002 \u3000\u3000\u2014\u2014\u8fd9\u5c31\u662f\u4e00\u4e2a\u5b66\u5251\u7684\u4eba\u6700\u540e\u4e00\u6b21\u63e1\u5251\u65f6\u7684\u611f\u89c9\u3002 \u3000\u3000\u5982\u679c\u4ed6\u80af\u7559\u5728\u8fd9\u91cc\uff0c\u5982\u679c\u4ed6\u80af\u5c06\u8fd9\u67c4\u5251\u7559\u5728\u5730\u4e0a\uff0c\u535c\u9e70\u7edd\u4e0d\u4f1a\u51fa\u624b\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e0d\u80af\u3002 \u3000\u3000\u4ed6\u4ece\u5730\u4e0a\u62d4\u8d77\u8fd9\u67c4\u5251\u65f6\uff0c\u5c31\u7b49\u4e8e\u5df2\u7ecf\u5c06\u81ea\u5df1\u57cb\u5165\u5730\u4e0b\u3002 \u3000\u3000\u535c\u9e70\u8fd8\u662f\u5e7d\u7075\u822c\u7ad9\u5728\u90a3\u91cc\uff0c\u51b7\u51b7\u5730\u770b\u7740\u4ed6\u3002 \u3000\u3000\u535c\u9e70\u7684\u624b\u91cc\u6ca1\u6709\u5251\u3002 \u3000\u3000\u535c\u9e70\u4e0d\u7528\u5251\u4e5f\u4e00\u6837\u53ef\u4ee5\u6740\u4eba\u3002 \u3000\u3000\u4ed6\u7528\u4e00\u53ea\u7a7a\u624b\u5c31\u80fd\u63a5\u4f4f\u536b\u5929\u9e4f\u95ea\u7535\u822c\u5288\u6740\u8fc7\u6765\u7684\u5feb\u5200\uff0c\u73b0\u5728\u4ed6\u5f53\u7136\u4e5f\u540c\u6837\u80fd\u7528\u8fd9\u53cc\u624b\u63a5\u4f4f\u5c0f\u65b9\u7684\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5251\u5df2\u523a\u51fa\u3002\u8fd9\u4e00\u5251\u523a\u7684\u662f\u535c\u9e70\u5fc3\u810f\uff0c\u201d\u4e5f\u662f\u5c0f\u65b9\u81ea\u5df1\u7684\u5fc3\u810f\u3002\u4ed6\u4e00\u5251\u523a\u51fa\u65f6\uff0c\u5c31\u7b49\u4e8e\u5df2\u7ecf\u5c06\u81ea\u5df1\u523a\u6740\u4e8e\u5251\u4e0b\uff01 \u3000\u3000\u4ed6\u81ea\u5df1\u5df2\u7ecf\u4ece\u95ea\u52a8\u7684\u5251\u5149\u4e4b\u4e2d\u770b\u5230\u4e86\u201c\u6b7b\u201d\uff01 \u3000\u3000\u95ea\u52a8\u7684\u5251\u5149\u5ffd\u7136\u505c\u987f\uff0c\u505c\u987f\u5728\u535c\u9e70\u7684\u5fc3\u810f\u4e4b\u524d\uff0c\u5251\u950b\u5df2\u7ecf\u523a\u7a7f\u535c\u9e70\u7684\u767d\u8863\u3002 \u3000\u3000\u535c\u9e70\u6839\u672c\u6ca1\u6709\u51fa\u624b\uff0c\u6839\u672c\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5c0f\u65b9\u5728\u6700\u540e\u4e00\u5239\u90a3\u95f4\u624d\u52d2\u4f4f\u8fd9\u4e00\u5251\uff0c\u5c0f\u65b9\u81ea\u5df1\u4e5f\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\u535c\u9e70\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u535c\u9e70\u65f6\uff0c\u535c\u9e70\u4e5f\u5728\u95f4\u4ed6\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u5f79\u6709\u56de\u7b54\u5bf9\u65b9\u7684\u95ee\u9898\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5f7c\u6b64\u90fd\u5df2\u77e5\u9053\u7b54\u6848\u3002 \u3000\u3000\u670b\u53cb\uff01 \u3000\u3000\u8fd9\u5c31\u662f\u552f\u4e00\u7684\u4e00\u4e2a\u7b54\u6848\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4e0d\u4f46\u5251\u950b\u505c\u987f\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u4e00\u5207\u53d8\u52a8\u4eff\u4f5b\u90fd\u5df2\u505c\u987f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u90fd\u5df2\u53d1\u73b0\uff0c\u4e0d\u7ba1\u522b\u4eba\u7684\u4e8b\u5728\u600e\u4e48\u53d8\uff0c\u4ed6\u4eec\u8fd8\u662f\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u662f\u670b\u53cb\u3002 \u3000\u3000\u771f\u6b63\u7684\u670b\u53cb\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d8\u4e3a\u4ec7\u654c\u3002 \u3000\u3000\u9ad8\u7aff\u4e0a\u7684\u706f\u7b3c\u53c8\u4eae\u8d77\u3002 \u3000\u3000\u535c\u9e70\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u770b\u7740\u8fd9\u4e00\u70b9\u9065\u8fdc\u5982\u661f\u8fb0\u7684\u706f\u5149\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u4ed6\u8bf4\uff1a\u201c\u4f60\u53bb\u5427\uff0c\u5230\u90a3\u76cf\u706f\u4e0b\u53bb\uff0c\u90a3\u91cc\u6709\u4e2a\u4eba\u5728\u7b49\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u535c\u9e70\u4e5f\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u6709\u4e9b\u4e8b\u662f\u7528\u4e0d\u7740\u8bf4\u51fa\u6765\u7684\uff0c\u4e16\u4e0a\u6240\u6709\u6700\u7f8e\u7684\u4e8b\u90fd\u7528\u4e0d\u7740\u8bf4\u51fa\u6765\u7684 \u3000\u3000\u4ed6\u7684\u68a6\u5728\u6c5f\u5357\u3002 \u3000\u3000\u6c5f\u5357\u5728\u4ed6\u7684\u68a6\u91cc\u3002 \u3000\u3000\u706f\u5149\u4e5f\u9065\u8fdc\u5982\u6c5f\u5357\uff0c\u5728\u706f\u4e0b\u7b49\u7740\u4ed6\u7684\u6709\u4e00\u4e2a\u4eba\u3001\u4e24\u5339\u9a6c\u3002 \u3000\u3000\u4eba\u662f\u201c\u9633\u5149\u201d\uff0c\u9a6c\u662f\u201c\u8d64\u72ac\u201d\uff0c\u4eba\u548c\u9a6c\u90fd\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u6c38\u8fdc\u4e0d\u53d8\u7684\u670b\u53cb\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53ea\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u4e09\u4e2a\u5b57\uff1a\u201c\u6211\u4eec\u8d70\u3002\u201d \u3000\u3000\u661f\u5149\u6bd4\u6c5f\u5357\u66f4\u8fdc\uff0c\u53ef\u662f\u661f\u5149\u80fd\u591f\u770b\u5f97\u89c1\uff0c\u6c5f\u5357\u5462\uff1f \u3000\u3000\u4ed6\u7684\u68a6\u5728\u6c5f\u5357\uff0c\u4ed6\u7684\u68a6\u4e2d\u5145\u6ee1\u4e86\u6d6a\u5b50\u7684\u60b2\u4f24\u548c\u6e38\u5b50\u7684\u79bb\u6101\u3002 \u3000\u3000\u4ed6\u6c38\u8fdc\u5fd8\u4e0d\u4e86\u4ed6\u6325\u624b\u79bb\u522b\u6c5f\u5357\u65f6\u7684\u60c6\u6005\u60b2\u4f24\u75db\u82e6\u3002\u73b0\u5728\u4ed6\u5c31\u8981\u56de\u5230\u6c5f\u5357\u4e86\uff0c\u4ed6\u5fc3\u91cc\u4e3a\u4ec0\u4e48\u4e5f\u6709\u540c\u6837\u7684\u75db\u82e6\u60b2\u4f24\u60c6\u6005\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u4e00\u76f4\u5728\u4ed6\u8eab\u7554\uff0c\u5ffd\u7136\u95ee\u4ed6\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5357\u3002\u201d \u3000\u3000\u6c5f\u5357\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e24\u4e2a\u5b57\u800c\u5df2\uff0c\u53ef\u662f\u542c\u5230\u8fd9\u4e24\u4e2a\u5b57\uff0c\u201c\u9633\u5149\u201d\u773c\u91cc\u4e5f\u9732\u51fa\u79cd\u68a6\u4e00\u6837\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u66fc\u58f0\u4f4e\u5531\uff1a\u201c\u91cd\u6e56\u53e0\u7fe0\u6e05\u5609\uff0c\u6709\u4e09\u79cb\u6842\u5b50\uff0c\u5341\u91cc\u8377\u82b1\u3002\u7f8c\u7ba1\u5f04\u6674\uff0c\u83f1\u6b4c\u6cdb\u591c\uff0c\u563b\u563b\u9493\u53df\u83b2\u5a03\u3002\u5343\u9a91\u62e5\u9ad8\u7259\uff0c\u4e58\u9189\u542c\u8427\u9f13\uff0c\u541f\u8d4f\u70df\u971e\u3002\u5f02\u65e5\u56fe\u5c06\u597d\u666f\uff0c\u5f52\u53bb\u51e4\u6c60\u5938\u3002\u201d \u3000\u3000\u8fd9\u662f\u67f3\u6c38\u67f3\u5c6f\u7530\u7684\u8bcd\uff0c\u636e\u300a\u94b1\u5858\u9057\u4e8b\u300b\u4e0a\u8bf4\uff0c\u5b59\u4f55\u7763\u5e05\u94b1\u5858\u65f6\uff0c\u67f3\u5c6f\u7530\u4f5c\u8fd9\u9996\u300a\u671b\u6d77\u6f6e\u300b\u8d60\u4e4b\uff0c\u5374\u88ab\u91d1\u4e3b\u5b8c\u989c\u4eae\u5728\u65e0\u610f\u4e2d\u770b\u89c1\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u5b8c\u989c\u4eae\u7279\u5730\u4ee4\u753b\u5de5\u81f3\u6c5f\u5357\u7ed8\u300a\u98ce\u7269\u56fe\u300b\u8fdb\u5448\uff0c\u800c\u4e14\u5728\u4e0a\u9762\u9898\u4e86\u4e24\u9996\u8bd7\u3002 \u3000\u3000\u201c\u79fb\u5175\u767e\u4e07\u897f\u6e56\u4e0a\uff0c\u7acb\u9a6c\u5434\u5c71\u7b2c\u4e00\u5cf0\u3002\u201d \u3000\u3000\u636e\u8bf4\u8fd9\u5c31\u662f\u91d1\u5175\u5165\u4fb5\u6c5f\u5357\u6765\u7684\u4e3b\u8981\u539f\u56e0\u3002 \u3000\u3000\u8fd9\u662f\u9996\u7f8e\u4e3d\u7684\u8bcd\uff0c\u542c\u7684\u4eba\u4e0d\u89c9\u9189\u4e86\uff0c\u5531\u7684\u4eba\u81ea\u5df1\u4e5f\u4eff\u4f5b\u9189\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5c0f\u65b9\u624d\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6ca1\u6709\u5230\u8fc7\u6c5f\u5357\u7684\u4eba\uff0c\u90fd\u60f3\u5230\u6c5f\u5357\u53bb\uff0c\u53ef\u662f\u5982\u679c\u4f60\u5230\u4e86\u6c5f\u5357\uff0c\u4f60\u5c31\u4f1a\u6000\u5ff5\u62c9\u8428\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u6211\u56de\u5230\u6c5f\u5357\u540e\uff0c\u5982\u679c\u77e5\u9053\u6709\u4eba\u8981\u5230\u62c9\u8428\u6765\uff0c\u6211\u4e00\u5b9a\u4f1a\u6258\u4ed6\u5e26\u6765\u4e00\u70b9\u6c5f\u5357\u7684\u6842\u82b1\u7cd5\u548c\u8377\u53f6\u7cd6\u7ed9\u4f60\u3002\u201d\u5c0f\u65b9\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u867d\u7136\u770b\u4e0d\u89c1\u6c5f\u5357\u7684\u4e09\u79cb\u6842\u5b50\u548c\u5341\u91cc\u8377\u82b1\uff0c\u5403\u4e00\u70b9\u6842\u82b1\u7cd5\u548c\u8377\u53f6\u7cd6\uff0c\u4e5f\u804a\u80dc\u4e8e\u65e0\u4e86\uff0c\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u6258\u4eba\u5e26\u4fe1\u7ed9\u6211\u3002\u201d\u5979\u7b11\u5f97\u5f88\u5947\u602a\uff0c\u201c\u6211\u4f1a\u81ea\u5df1\u53bb\u4e70\u3002\u201d \u3000\u3000\u201c\u4f60\u81ea\u5df1\u53bb\u4e70\uff1f\u201d\u5c0f\u65b9\u6ca1\u6709\u542c\u61c2\u5979\u7684\u8bdd\uff0c\u201c\u5230\u54ea\u91cc\u53bb\u4e70\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5230\u6c5f\u5357\u53bb\u4e70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u201c\u5230\u6c5f\u5357\u53bb\u4e70\uff1f\u4f60\u4e5f\u8981\u5230\u6c5f\u5357\u53bb\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u773c\u4e2d\u663e\u7136\u5df2\u6709\u4e86\u6c5f\u5357\u7684\u68a6\uff0c\u4e5f\u6709\u4e86\u526a\u4e0d\u65ad\u7684\u79bb\u6101\u3002 \u3000\u3000\u5c0f\u65b9\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u4f1a\u53bb\u7684\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u4f60\u7edd\u5bf9\u820d\u4e0d\u5f97\u79bb\u5f00\u62c9\u8428\uff0c\u66f4\u820d\u4e0d\u5f97\u79bb\u5f00\u90a3\u4e9b\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u4ed6\u4eec\u3002\u201d\u201c\u9633\u5149\u201d\u9053\uff0c\u201c\u53ef\u662f\u6211\u4e00\u5b9a\u8981\u5230\u6c5f\u5357\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9e70\u54e5\u8981\u6211\u9001\u4f60\uff0c\u8981\u6211\u628a\u4f60\u9001\u5230\u6c5f\u5357\uff0c\u201d\u201c\u9633\u5149\u201d\u60a0\u60a0\u4ed6\u8bf4\uff0c\u201c\u4f60\u5e94\u8be5\u77e5\u9053\uff0c\u4e0d\u7ba1\u4ed6\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u90fd\u4f1a\u542c\u4ed6\u7684\u8bdd\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u52c9\u5f3a\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u4f60\u9001\u5f97\u90a3\u4e48\u8fdc\uff1f\u96be\u9053\u4ed6\u4ee5\u4e3a\u6211\u5df2\u7ecf\u5fd8\u4e86\u56de\u5bb6\u7684\u8def\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6211\u9001\u4f60\u3002\u201d\u201c\u9633\u5149\u201d\u9053\uff0c\u201c\u53ef\u662f\u4ed6\u65e2\u7136\u8981\u6211\u9001\u4f60\uff0c\u6211\u5c31\u8981\u628a\u4f60\u9001\u5230\u6c5f\u5357\uff0c\u4f60\u7528\u97ad\u5b50\u8d76\u6211\u90fd\u8d76\u4e0d\u8d70\u7684\u3002\u201d \u3000\u3000\u5979\u4e5f\u5728\u7b11\uff0c\u7b11\u5f97\u4e5f\u5f88\u52c9\u5f3a\uff0c\u56e0\u4e3a\u5979\u4e5f\u548c\u5c0f\u65b9\u4e00\u6837\uff0c\u4e5f\u660e\u767d\u535c\u9e70\u7684\u610f\u601d\u3002 \u3000\u3000\u535c\u9e70\u8981\u5979\u9001\u5c0f\u65b9\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u60f3\u6210\u5168\u4ed6\u4eec\uff0c\u6bcf\u4e2a\u90fd\u8ba4\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u662f\u4e00\u53cc\u4e24\u60c5\u76f8\u60a6\u7684\u60c5\u4fa3\u3002 \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u4e86\u5f88\u4e45\uff1b\u5ffd\u7136\u53c8\u95ee\uff1a\u201c\u5230\u4e86\u6c5f\u5357\uff0c\u4f60\u8fd8\u4f1a\u4e0d\u4f1a\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u4f1a\u3002\u201d\u201c\u9633\u5149\u201d\u6beb\u4e0d\u8003\u8651\u5c31\u56de\u7b54\uff0c\u201c\u4e0d\u7ba1\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u90fd\u4e00\u5b9a\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u535c\u9e70\u662f\u6211\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u4f60\u7684\u5927\u54e5\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u6211\u7684\u5927\u54e5\uff0c\u4ed6\u5f53\u7136\u662f\u6211\u7684\u5927\u54e5\u3002\u201d\u201c\u9633\u5149\u201d\u8f7b\u8f7b\u5730\u53f9\u606f\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u5374\u4e0d\u662f\u4ed6\u7684\u59b9\u59b9\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\uff1f\u201d\u5c0f\u65b9\u5f88\u610f\u5916\uff0c\u201c\u4f60\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4ed6\u672a\u5a5a\u7684\u59bb\u5b50\u3002\u201d\u201c\u9633\u5149\u201d\u9053\uff0c\u201c\u6211\u4eec\u5df2\u7ecf\u6709\u4e86\u5a5a\u7ea6\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6014\u4f4f\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u8bf4\uff1a\u201c\u4ed6\u4e00\u76f4\u4e0d\u8ba9\u4f60\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u4f60\u5f88\u559c\u6b22\u6211\uff0c\u4ed6\u4e0d\u613f\u8ba9\u4f60\u518d\u53d7\u523a\u6fc0\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u9053\uff1a\u201c\u800c\u4e14\u4ed6\u4e00\u76f4\u89c9\u5f97\u81ea\u5df1\u8001\u4e86\uff0c\u89c9\u5f97\u81ea\u5df1\u914d\u4e0d\u4e0a\u6211\uff0c\u4e00\u76f4\u5e0c\u671b\u6211\u80fd\u627e\u4e2a\u66f4\u597d\u7684\u5f52\u5bbf\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u5c0f\u65b9\u66ff\u5979\u8bf4\u4e86\u4e0b\u53bb\uff1a\u201c\u6240\u4ee5\u4ed6\u624d\u8981\u4f60\u9001\u6211\uff0c\u9001\u5230\u6c5f\u5357\u3002\u201d \u3000\u3000\u201c\u4ed6\u5c31\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u603b\u662f\u5148\u66ff\u522b\u4eba\u7740\u60f3\uff0c\u4ece\u6765\u4e0d\u80af\u66ff\u81ea\u5df1\u60f3\u60f3\u3002\u201d\u201c\u9633\u5149\u201d\u4e5f\u82e6\u7b11\uff0c\u201c\u53ef\u662f\u4ed6\u7684\u5916\u8868\u5374\u504f\u504f\u51b7\u5f97\u50cf\u51b0\u4e00\u6837\u3002\u201d \u3000\u3000\u5979\u7684\u7b11\u5bb9\u867d\u7136\u9eef\u6de1\uff0c\u5374\u53c8\u5145\u6ee1\u9a84\u50b2\uff0c\u4e3a\u535c\u9e70\u800c\u9a84\u50b2\u3002 \u3000\u3000\u201c\u4ed6\u4e3a\u4e86\u4f60\uff0c\u4e0d\u60dc\u8ddf\u4ed6\u7684\u4f19\u4f34\u4e89\u5435\uff0c\u751a\u81f3\u4e0d\u6070\u4ee5\u4ed6\u81ea\u5df1\u7684\u6027\u547d\u6765\u4fdd\u8bc1\u4f60\u7edd\u4e0d\u4f1a\u6cc4\u9732\u4ed6\u4eec\u7684\u79d8\u5bc6\u3002\u201d\u9633\u5149\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u53ef\u662f\u8fd9\u4e9b\u4e8b\u4ed6\u5b81\u6b7b\u4e5f\u4e0d\u4f1a\u5bf9\u4f60\u8bf4\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u8ba9\u4f60\u5fc3\u91cc\u6709\u8d1f\u62c5\uff0c\u4e0d\u613f\u8ba9\u4f60\u611f\u6fc0\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u751f\u6015\u81ea\u5df1\u773c\u4e2d\u7684\u70ed\u6cea\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u6cea\u7edd\u4e0d\u8f7b\u6d41\uff0c\u4ed6\u5fc3\u91cc\u7684\u611f\u6fc0\u4e5f\u4ece\u4e0d\u8f7b\u6613\u5411\u4eba\u53d9\u8bf4\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u201c\u9633\u5149\u201d\u624d\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u600e\u4e48\u5bf9\u6211\uff0c\u6211\u5bf9\u4ed6\u5374\u4e0d\u4f1a\u53d8\u7684\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4e0d\u7ba1\u4f60\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\uff0c\u90fd\u4e00\u5b9a\u4f1a\u56de\u6765\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u770b\u7740\u4ed6\uff0c\u8f7b\u8f7b\u5730\u95ee\uff1a\u201c\u4f60\u660e\u767d\u6211\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u7b11\u4e86\uff0c\u771f\u7684\u7b11\u4e86\uff0c\u7b11\u5bb9\u53c8\u53d8\u5f97\u50cf\u9633\u5149\u822c\u707f\u70c2\u8f89\u714c\u3002 \u3000\u3000\u5979\u53c8\u63e1\u4f4f\u4e86\u5c0f\u65b9\u7684\u624b\uff0c\u63e1\u5f97\u6bd4\u4ee5\u524d\u66f4\u7d27\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u660e\u767d\u7684\u3002\u201d\u5979\u8bf4\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4ed6\u6ca1\u6709\u770b\u9519\u4f60\uff0c\u4f60\u7684\u786e\u662f\u4ed6\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u7b11\u5f97\u6700\u5f00\u6717\u3001\u6700\u6109\u5feb\u65f6\uff0c\u4ed6\u4eec\u5ffd\u7136\u542c\u5230\u4e00\u79cd\u75db\u82e6\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e0d\u662f\u547b\u541f\uff0c\u4e5f\u4e0d\u662f\u5598\u606f\uff0c\u800c\u662f\u4e00\u4e2a\u4eba\u53ea\u6709\u5728\u75db\u82e6\u5df2\u5230\u6781\u9650\u65f6\u624d\u4f1a\u53d1\u51fa\u7684\u58f0\u97f3\u3002 \u3000\u3000\u58f0\u97f3\u5f88\u4f4e\u3001\u5f88\u8fdc\uff0c\u5982\u679c\u4e0d\u662f\u5728\u8fd9\u6b7b\u5bc2\u7684\u5927\u6f20\u4e4b\u591c\u4e2d\uff0c\u4ed6\u4eec\u5f88\u53ef\u80fd\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u542c\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u91cc\u8fd8\u662f\u6c99\u6f20\u7684\u8fb9\u7f18\uff0c\u662f\u4e2a\u5df2\u5e72\u6db8\u4e86\u7684\u7eff\u6d32\u3002 \u3000\u3000\u7eff\u6d32\u5df2\u5e72\u6db8\uff0c\u6b63\u5982\u7f8e\u4eba\u5df2\u8fdf\u66ae\uff0c\u518d\u4e5f\u65e0\u6cd5\u7559\u4f4f\u4efb\u4f55\u4eba\u7684\u811a\u6b65\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5e26\u5c0f\u65b9\u8d70\u8fd9\u6761\u8def\uff0c\u201d\u4e0d\u4f46\u56e0\u4e3a\u8fd9\u91cc\u884c\u4eba\u5df2\u5c11\uff0c\u4e5f\u56e0\u4e3a\u522b\u4eba\u60f3\u4e0d\u5230\u4e00\u4e2a\u50cf\u5979\u90a3\u6837\u5bf9\u6c99\u6f20\u5982\u6b64\u719f\u6089\u7684\u4eba\uff0c\u4f1a\u5230\u4e00\u4e2a\u6ca1\u6709\u6c34\u7684\u7eff\u6d32\u6765\u3002 \u3000\u3000\u6ca1\u6709\u6c34\uff0c\u5c31\u6ca1\u6709\u751f\u547d\uff0c\u65c5\u4eba\u8fdc\u907f\uff0c\u7eff\u6811\u67af\u6b7b\uff0c\u53ea\u5269\u4e0b\u4e00\u5ea7\u571f\u4e18\u4ecd\u7136\u987d\u5f3a\u5982\u6614\uff0c\u51b7\u773c\u5750\u89c6\u4eba\u95f4\u7684\u6ca7\u6851\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u4eec\u542c\u5230\u7684\u58f0\u97f3\uff0c\u5c31\u662f\u4ece\u8fd9\u5ea7\u571f\u4e18\u540e\u9762\u4f20\u6765\u7684\u3002 \u3000\u3000\u571f\u4e18\u540e\u6709\u68f5\u67af\u6811\uff0c\u6811\u4e0a\u540a\u7740\u4e00\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u672c\u6765\u65e9\u5c31\u5df2\u7ecf\u8be5\u6b7b\u4e86\u7684\u4eba\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u53d7\u8fc7\u5979\u8fd9\u4e48\u591a\u6298\u78e8\u9177\u5211\u4e4b\u540e\uff0c\u90fd\u5f88\u96be\u6d3b\u5230\u73b0\u5728\u3002\u5979\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u5979\u53ea\u6709\u4e00\u534a\u662f\u4eba\uff0c\u53e6\u4e00\u534a\u662f\u9b54\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8d6b\u7136\u7adf\u662f\u201c\u5929\u9b54\u7389\u5973\u201d\u67f3\u5206\u5206\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u5979\u7684\u8863\u670d\uff0c\u8fde\u5c0f\u65b9\u90fd\u51e0\u4e4e\u8ba4\u4e0d\u51fa\u5979\u5c31\u662f\u67f3\u5206\u5206\u3002 \u3000\u3000\u5979\u5df2\u88ab\u6298\u78e8\u5f97\u4e0d\u6210\u4eba\u5f62\uff0c\u8fde\u547b\u541f\u58f0\u90fd\u53d1\u4e0d\u51fa\uff0c\u53ea\u80fd\u7528\u4e00\u53cc\u5e03\u6ee1\u8840\u4e1d\u7684\u773c\u775b\uff0c\u4e5e\u601c\u5730\u770b\u7740\u5c0f\u65b9\u3002 \u3000\u3000\u5979\u4e0d\u662f\u8981\u5c0f\u65b9\u6551\u5979\uff0c\u5979\u81ea\u5df1\u4e5f\u77e5\u9053\u81ea\u5df1\u662f\u7edd\u5bf9\u6d3b\u4e0d\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u5979\u53ea\u6c42\u901f\u6b7b\u3002 \u3000\u3000\u5c0f\u65b9\u660e\u767d\u5979\u7684\u610f\u601d\uff0c\u5c0f\u65b9\u4e5f\u77e5\u9053\uff0c\u5982\u679c\u7ed9\u5979\u4e00\u5200\uff0c\u5bf9\u5979\u53cd\u800c\u662f\u79cd\u4ec1\u6148\u7684\u884c\u4e3a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u51fa\u624b\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u505a\u624d\u662f\u5bf9\u7684\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u4e2a\u4eba\u6bd5\u7adf\u8fd8\u6ca1\u6709\u6b7b\uff0c\u8c01\u4e5f\u6ca1\u6709\u6743\u529b\u51b3\u5b9a\u5979\u7684\u6b7b\u6d3b\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5df2\u7ecf\u626d\u8fc7\u5934\uff0c\u4e0d\u5fcd\u518d\u770b\u5979\u3002 \u3000\u3000\u201c\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80af\u8d70\u3002\u201c\u9633\u5149\u201d\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u65e2\u7136\u6551\u4e0d\u5979a\uff0c\u53c8\u4e0d\u5fcd\u6740\u5979\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u80af\u8d70\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u81ea\u5df1\u4e5f\u8bf4\u4e0d\u51fa\u7406\u7531\u3002 \u3000\u3000\u4eba\u6027\u4e2d\u672c\u6765\u5c31\u6709\u5f88\u591a\u79cd\u60c5\u611f\u662f\u65e0\u6cd5\u89e3\u91ca\u7684\uff0c\u6240\u4ee5\u6bcf\u4e2a\u4eba\u90fd\u5e38\u5e38\u4f1a\u505a\u51fa\u4e9b\u8fde\u81ea\u5df1\u90fd\u8bf4\u4e0d\u51fa\u7406\u7531\u6765\u7684\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u60f3\u5148\u628a\u5979\u4ece\u6811\u4e0a\u89e3\u4e0b\u6765\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5374\u62c9\u4f4f\u4e86\u4ed6\u7684\u624b\uff1a\u201c\u4f60\u7edd\u5bf9\u4e0d\u80fd\u52a8\u5979\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u53ea\u8981\u4e00\u52a8\u5979\uff0c\u522b\u4eba\u5c31\u77e5\u9053\u6211\u4eec\u5230\u8fd9\u91cc\u6765\u8fc7\uff0c\u5c31\u77e5\u9053\u6211\u4eec\u8d70\u7684\u662f\u8fd9\u6761\u8def\u4e86\u3002\u201d \u3000\u3000\u201c\u522b\u4eba\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\u9053\uff0c\u201c\u522b\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6ca1\u6709\u56de\u7b54\uff0c\u56e0\u4e3a\u201c\u522b\u4eba\u201d\u5df2\u7ecf\u66ff\u5979\u56de\u7b54\u4e86\uff1a\u201c\u522b\u4eba\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u58f0\u97f3\u662f\u4ece\u5c0f\u65b9\u8eab\u540e\u4f20\u6765\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\uff0c\u8fd9\u4e2a\u4eba\u5c31\u5df2\u5e7d\u7075\u822c\u5230\u4e86\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u2014\u2014\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4ec0\u4e48\u65f6\u5019\u4f1a\u6765\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4ec0\u4e48\u65f6\u5019\u8981\u8d70\u3002 \u3000\u3000\u5c0f\u65b9\u63e1\u7d27\u53cc\u62f3\uff0c\u8fde\u6307\u5c16\u90fd\u5df2\u51b0\u51b7\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5e76\u4e0d\u89c9\u5f97\u610f\u5916\uff0c\u56e0\u4e3a\u4ed6\u65e9\u5df2\u77e5\u9053\u73ed\u5bdf\u5df4\u90a3\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\uff01 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8138\u4e0a\u5df1\u6ca1\u6709\u6e29\u67d4\u5982\u6625\u7684\u5fae\u7b11\uff0c\u795e\u5fd7\u5374\u4ecd\u7136\u575a\u5f3a\u5982\u91d1\uff0c\u773c\u795e\u4e5f\u4ecd\u7136\u5c16\u5229\u5982\u9525\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4e0a\u4ecd\u6709\u5f13\uff0c\u8170\u7554\u4ecd\u6709\u7bad\u3002 \u3000\u3000\u2014\u2014\u7bad\u7fbd\u4e0a\u6709\u75db\u82e6\u4e4b\u5fc3\uff0c\u5012\u94a9\u4e0a\u6709\u76f8\u601d\u4e4b\u60c5\uff0c\u5145\u6ee1\u6b32\u671b\u76f4\u5c04\u4eba\u5fc3\uff0c\u767e\u53d1\u767e\u4e2d\u7684\u4e94\u82b1\u795e\u7bad\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u5728\u53f9\u606f\uff1a\u201c\u6211\u4ee5\u4e3a\u4f60\u60f3\u4e0d\u5230\u6211\u4f1a\u5e26\u4ed6\u8d70\u8fd9\u6761\u8def\u7684\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u662f\u627e\u5230\u4e86\u3002\u201d \u3000\u3000\u5979\u82e6\u7b11\uff1a\u201c\u96be\u602a\u6bcf\u4e2a\u4eba\u90fd\u8bf4\uff0c\u5982\u679c\u73ed\u5bdf\u5df4\u90a3\u8ffd\u8e2a\u90a3\u4e00\u4e2a\u4eba\uff0c\u5c31\u597d\u50cf\u730e\u72ac\u8981\u8ffd\u4e00\u53ea\u9e21\uff0c\u4ece\u6765\u90fd\u6ca1\u6709\u4e00\u6b21\u8feb\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u542c\u89c1\u5979\u5728\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4e00\u76f4\u90fd\u5728\u770b\u7740\u540a\u5728\u6811\u4e0a\u7684\u67f3\u5206\u5206\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u8c01\u5bf9\u5979\u4e0b\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u201d\u201c\u9633\u5149\u201d\u95ee\uff0c\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u8bf4\u51fa\u4e00\u4e2a\u540d\u5b57\uff1a\u201c\u662f\u91d1\u624b\u3002\u201d \u3000\u3000\u201c\u91d1\u624b\uff1f\u91d1\u624b\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u91d1\u624b\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u662f\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u662f\u5415\u4e09\u7528\u9ec4\u91d1\u6536\u4e70\u7684\u7ec4\u7ec7\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u91d1\u624b\u5c31\u662f\u4ed6\u4eec\u7528\u7684\u4ee3\u53f7\u3002\u201d \u3000\u3000\u201c\u4ee5\u524d\u6211\u4eec\u4e3a\u4ec0\u4e48\u6ca1\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u201c\u8fde\u6211\u4e5f\u662f\u6700\u8fd1\u624d\u77e5\u9053\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u94c1\u7ffc\u3001\u536b\u5929\u9e4f\u3001\u67f3\u5206\u5206\uff0c\u90fd\u662f\u8fd9\u7ec4\u7ec7\u4e2d\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u67f3\u5206\u5206\u65e2\u7136\u4e5f\u662f\u8fd9\u7ec4\u7ec7\u4e2d\u7684\u4eba\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5bf9\u4ed8\u5979\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u4e0d\u77e5\u9053\u5176\u4e2d\u7684\u539f\u56e0\uff0c\u5c0f\u65b9\u5374\u77e5\u9053\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u5979\u66fe\u7ecf\u51fa\u5356\u8fc7\u4ed6\u4eec\uff01\u201d \u3000\u3000\u5728\u90a3\u6302\u7740\u9ed1\u8272\u9e70\u7fbd\u7684\u5e10\u7bf7\u4e2d\uff0c\u5979\u8981\u5979\u7684\u540c\u4f19\u6bcf\u4e2a\u4eba\u90fd\u7559\u4e0b\u4e86\u4e00\u53ea\u624b\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u65b9\u624d\u660e\u767d\u3002\u90a3\u6b21\u535c\u9e70\u4e3a\u4ec0\u4e48\u4f1a\u8f7b\u6613\u653e\u8fc7\u67f3\u5206\u5206\u4e86\u3002 \u3000\u3000\u4ed6\u7b97\u51c6\u5979\u7684\u540c\u4f19\u4e00\u5b9a\u4f1a\u5bf9\u4ed8\u5979\u7684\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u77b3\u5b54\u5728\u6536\u7f29\uff0c\u773c\u795e\u66f4\u9510\u5229\uff0c\u5ffd\u7136\u51b7\u7b11\uff1a\u201c\u60f3\u4e0d\u5230\u4ed6\u4eec\u5c45\u7136\u8fd8\u7559\u5728\u8fd9\u91cc\u6ca1\u6709\u8d70\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u95ee\uff1a\u201c\u4ed6\u4eec\u6545\u610f\u628a\u67f3\u5206\u5206\u540a\u5728\u8fd9\u91cc\uff0c\u662f\u4e0d\u662f\u6545\u610f\u5411\u6211\u4eec\u793a\u5a01\uff1f\u201d \u3000\u3000\u5979\u81ea\u5df1\u66ff\u81ea\u5df1\u56de\u7b54\uff1a\u201c\u4e00\u5b9a\u662f\u7684\uff0c\u6240\u4ee5\u4f60\u5e94\u8be5\u8d76\u5feb\u53bb\u627e\u4ed6\u4eec\uff0c\u7ed9\u4ed6\u4eec\u4e00\u70b9\u989c\u8272\u770b\u3002\u201d \u3000\u3000\u5979\u53c8\u62c9\u4f4f\u5c0f\u65b9\u7684\u624b\uff0c\u62c9\u7740\u5c0f\u65b9\u5f80\u4ed6\u4eec\u6b47\u9a6c\u7684\u5730\u65b9\u8d70\u3002 \u3000\u3000\u201c\u6211\u4eec\u4e5f\u5e94\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5374\u5df2\u6a2a\u51fa\u91d1\u5f13\uff0c\u62e6\u4f4f\u4e86\u4ed6\u4eec\u7684\u53bb\u8def\u3002 \u3000\u3000\u201c\u4f60\u8d70\uff0c\u4ed6\u7559\u4e0b\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u4ed6\u7559\u4e0b\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u9633\u5149\u6545\u610f\u88c5\u4f5c\u4e0d\u61c2\uff0c\u201c\u662f\u4e0d\u662f\u8981\u4ed6\u966a\u4f60\u559d\u9152\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u8fd9\u95ee\u9898\u672c\u6765\u662f\u4e0d\u5fc5\u56de\u7b54\u7684\uff0c\u73ed\u5bdf\u5df4\u90a3\u5374\u56de\u7b54\u4e86\uff0c\u56de\u7b54\u5f97\u4e25\u8083\u800c\u614e\u91cd\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u5f53\u7136\u4e0d\u662f\u8981\u4ed6\u966a\u4f60\u559d\u9152\uff0c\u4f60\u8981\u6740\u4eba\u65f6\u4ece\u4e0d\u559d\u9152\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u627f\u8ba4\u4e86\uff0c\u4ed6\u7684\u773c\u4e2d\u5df1\u9732\u51fa\u6740\u673a\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u95ee\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5e0c\u671b\u4f60\u53ea\u4e0d\u8fc7\u662f\u8981\u4ed6\u966a\u4f60\u559d\u676f\u9152\u800c\u5df2\u3002\u201d\u201c\u9633\u5149\u201d\u7684\u6001\u5ea6\u4e5f\u53d8\u5f97\u540c\u6837\u4e25\u8083\u614e\u91cd\uff0c\u201c\u56e0\u4e3a\u4f60\u662f\u7edd\u5bf9\u6740\u4e0d\u4e86\u4ed6\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u51b7\u7b11\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d\u4ed6\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4eba\u4e0d\u59a8\u4e00\u8d77\u51fa\u624b\uff0c\u53ea\u8981\u80fd\u6740\u4e86\u6211\uff0c\u4f60\u5c31\u53ef\u4ee5\u5e26\u4ed6\u8d70\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u53ea\u6709\u6740\u4e86\u6211\uff0c\u4f60\u624d\u80fd\u5e26\u4ed6\u8d70\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u4f60\u6839\u672c\u4e0d\u660e\u767d\u6211\u7684\u610f\u601d\uff0c\u6211\u6839\u672c\u4e0d\u60f3\u6740\u4f60\uff0c\u4f46\u662f\u4f60\u4e5f\u7edd\u4e0d\u80fd\u6740\u4ed6\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u201c\u5426\u5219\u600e\u4e48\u6837\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u4ed6\u8981\u8d70\u65f6\uff0c\u8c01\u4e5f\u62e6\u4e0d\u4f4f\u4ed6\uff1b\u6211\u8981\u6740\u4eba\u65f6\uff0c\u4e5f\u540c\u6837\u6ca1\u6709\u4eba\u80fd\u62e6\u4f4f\u6211\u3002\u201d \u3000\u3000\u4ed6\u53f3\u624b\u63e1\u91d1\u5f13\uff0c\u7528\u5de6\u624b\u98df\u4e2d\u4e24\u6307\u62c8\u8d77\u4e00\u6839\u7fbd\u7bad\uff1a\u201c\u9664\u975e\u4ed6\u8fd9\u6b21\u8fd8\u80fd\u907f\u5f00\u6211\u8fd9\u4e94\u679d\u7bad\u3002\u201d \u3000\u3000\u4ed6\u7684\u91d1\u5f13\u5df2\u5f15\u6ee1\uff0c\u7bad\u5df2\u5728\u5f26\uff0c\u767e\u53d1\u767e\u4e2d\u7684\u4e94\u82b1\u795e\u7bad\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u80fd\u4e0d\u80fd\u907f\u5f00\u4f60\u7684\u7bad\uff0c\u4f46\u662f\u6211\u77e5\u9053\uff0c\u4f60\u8fd9\u4e00\u7bad\u5c04\u51fa\uff0c\u5c04\u6b7b\u7684\u7edd\u5bf9\u4e0d\u6b62\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u966a\u4ed6\u6b7b\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u4e03\u7ae0\u3000\u8dea\u7740\u6b7b\u7684\u4eba \u7b2c\u5341\u4e03\u7ae0\u3000\u8dea\u7740\u6b7b\u7684\u4eba \u3000\u3000\u201c\u9633\u5149\u201d\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u3002\u201d \u3000\u3000\u5979\u5c45\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u4f46\u6211\u53ea\u77e5\u9053\uff0c\u4f60\u82e5\u6740\u4e86\u4ed6\uff0c\u53e6\u5916\u6709\u4e2a\u4eba\u4e00\u5b9a\u4f1a\u966a\u4ed6\u6b7b\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e0d\u80fd\u4e0d\u95ee\uff1a\u201c\u8c01\uff1f\u53e6\u5916\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u6ce2\u5a03\u3002\u201d \u3000\u3000\u5979\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u535c\u9e70\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u82e5\u6740\u4e86\u5c0f\u65b9\uff0c\u6ce2\u5a03\u4e5f\u5f97\u6b7b\uff0c\u4f60\u4eca\u5929\u6740\u4e86\u4ed6\uff0c\u6ce2\u5a03\u7edd\u5bf9\u6d3b\u4e0d\u5230\u660e\u5929\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u91d1\u5f13\u5728\u624b\uff0c\u7fbd\u7bad\u4ecd\u5728\u5f26\uff0c\u4f46\u662f\u4ed6\u5168\u8eab\u90fd\u5df2\u50f5\u786c\uff0c\u8fde\u6263\u7bad\u7684\u624b\u6307\u90fd\u5df2\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u4e86\u89e3\u535c\u9e70\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6bd4\u4ed6\u66f4\u4e86\u89e3\u535c\u9e70\u3002 \u3000\u3000\u535c\u9e70\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u5c31\u50cf\u662f\u4ed6\u5c04\u51fa\u53bb\u7684\u7bad\uff0c\u535c\u9e70\u7684\u8bdd\u5df2\u51fa\u53e3\uff0c\u4ed6\u7684\u7bad\u8fd8\u672a\u79bb\u5f26\u3002 \u3000\u3000\u4f46\u662f\u7bad\u5df2\u5728\u5f26\uff0c\u53c8\u600e\u4e48\u80fd\u4e0d\u53d1\uff1f \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u201c\u5d29\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u91d1\u5f13\u5f39\u8d77\uff0c\u5f13\u5f26\u7adf\u5df2\u88ab\u4ed6\u62c9\u65ad\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u6740\u6c14\u4e5f\u5df2\u968f\u7740\u65ad\u5f26\u800c\u6cc4\u3002 \u3000\u3000\u201c\u4f60\u4eec\u679c\u7136\u662f\u597d\u670b\u53cb\u3002\u201d\u4ed6\u53f9\u606f\uff0c\u201c\u6211\u4ece\u672a\u60f3\u5230\u4f60\u4eec\u7adf\u662f\u8fd9\u4e48\u597d\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u591c\u6df1\uff0c\u66f4\u6df1\u3002 \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u73ed\u5bdf\u5df4\u90a3\u5c31\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u8d70\u5411\u9ed1\u6697\u3002 \u3000\u3000\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff0c\u6c38\u65e0\u5c3d\u671f\u7684\u5bc2\u5bde\u3002 \u3000\u3000\u770b\u7740\u4ed6\u80cc\u5f71\uff0c\u201c\u9633\u5149\u201d\u4e5f\u5fcd\u4e0d\u4f4f\u53f9\u606f\uff1a\u201c\u4f60\u4ece\u672a\u60f3\u5230\u4ed6\u4eec\u662f\u8fd9\u4e48\u597d\u7684\u670b\u53cb\uff0c\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4f60\u81ea\u5df1\u4ece\u6765\u6ca1\u6709\u670b\u53cb\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u7684\u2026\u2026\u201d\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4ed6\u7684\u8eab\u5b50\u5ffd\u7136\u5982\u5f13\u5f26\u822c\u7ef7\u7d27\uff0c\u5ffd\u7136\u4f0f\u5367\u5728\u5730\u6b62\uff0c\u7528\u5de6\u8033\u8d34\u5730\u3002\u661f\u5149\u7167\u5728\u4ed6\u8138\u4e0a\uff0c\u4ed6\u8138\u4e0a\u9732\u51fa\u6781\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u53c8\u542c\u89c1\u4e86\u4e00\u4e9b\u522b\u4eba\u542c\u4e0d\u89c1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5fcd\u4e0d\u4f4f\u6084\u6084\u5730\u5411\uff1a\u201c\u4f60\u542c\u89c1\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4eba\u3002\u201d \u3000\u3000\u201c\u4eba\uff1f\u201d\u201c\u9633\u5149\u201d\u53c8\u95ee\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u662f\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u6765\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u7528\u4e0d\u7740\u518d\u56de\u7b54\uff0c\u56e0\u4e3a\u8fd9\u65f6\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u4e00\u5b9a\u4e5f\u80fd\u542c\u5230\u4ed6\u521a\u624d\u542c\u89c1\u7684\u58f0\u97f3\u4e86\u3002 \u3000\u3000\u4e00\u9635\u975e\u5e38\u8f7b\u7684\u9a6c\u8e44\u58f0\uff0c\u6765\u5f97\u6781\u5feb\uff0c\u7728\u773c\u95f4\u4ed6\u4eec\u5c31\u5df2\u80fd\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u4eba\u9a6c\u6b63\u662f\u5f80\u4ed6\u4eec\u8fd9\u65b9\u5411\u6765\u7684\uff0c\u6765\u7684\u6700\u5c11\u6709\u4e09\u56db\u5341\u4e2a\u4eba\uff0c\u4e09\u56db\u5341\u5339\u9a6c\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8eab\u5b50\u5df2\u8dc3\u8d77\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u201c\u8d64\u72ac\u201d\u548c\u201c\u9633\u5149\u201d\u7684\u9a6c\uff0c\u90fd\u8eb2\u5728\u5e72\u6db8\u7684\u6c34\u6c60\u65c1\u4e00\u68f5\u67af\u6811\u4e0b\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u98de\u63a0\u8fc7\u53bb\uff0c\u8f7b\u62cd\u9a6c\u5934\uff0c\u89e3\u5f00\u9a6c\u7f30\uff0c\u5e26\u7740\u4e24\u5339\u9a6c\u8f6c\u5165\u53e6\u4e00\u5ea7\u6bd4\u8f83\u4f4e\u77ee\u7684\u6c99\u4e18\u540e\uff0c\u5ffd\u7136\u5c06\u201c\u8d64\u72ac\u201d\u7eca\u5012\uff0c\u7528\u81ea\u5df1\u7684\u80f8\u819b\uff0c\u538b\u4f4f\u201c\u8d64\u72ac\u201d\u7684\u5934\u3002 \u3000\u3000\u4e00\u5411\u8363\u9a9b\u4e0d\u8bad\u7684\u201c\u8d64\u72ac\u201d\uff0c\u5728\u4ed6\u7684\u624b\u4e0b\uff0c\u7adf\u5b8c\u5168\u6ca1\u6709\u6323\u624e\u53cd\u6297\u4e4b\u529b\u3002 \u3000\u3000\u4ed6\u51fa\u624b\u65f6\u5df2\u7ecf\u5411\u201c\u9633\u5149\u201d\u793a\u610f\uff0c\u5979\u7acb\u523b\u4e5f\u7528\u540c\u6837\u7684\u65b9\u6cd5\u5236\u4f4f\u4e86\u53e6\u5916\u4e00\u5339\u9a6c\u3002 \u3000\u3000\u4ed6\u4eec\u7528\u7684\u6cd5\u5b50\u8fc5\u901f\u800c\u4e14\u786e\u5b9e\u6709\u6548\uff0c\u751a\u81f3\u6bd4\u6d6a\u5b50\u5bf9\u4ed8\u5973\u4eba\u7684\u65b9\u6cd5\u66f4\u6709\u6548\u3002 \u3000\u3000\u8fd9\u65f6\u8fdc\u5904\u7684\u8e44\u58f0\u6e10\u8fd1\uff0c\u7136\u540e\u5c31\u53ef\u4ee5\u770b\u89c1\u4e00\u884c\u4eba\u9a6c\u9a70\u5165\u8fd9\u4e2a\u5df2\u7ecf\u5e72\u6db8\u4e86\u7684\u7eff\u6d32\u3002 \u3000\u3000\u4e00\u884c\u4e09\u5341\u4e03\u4e2a\u4eba\u3001\u4e09\u5341\u516d\u5339\u9a6c\uff0c\u6700\u540e\u4e00\u4e2a\u4eba\u9a91\u7684\u4e0d\u662f\u9a6c\uff0c\u662f\u9a74\u5b50\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u9ad8\u5927\u800c\u80a5\u80d6\uff0c\u9a91\u7684\u5374\u504f\u504f\u662f\u5339\u53c8\u7626\u53c8\u5c0f\u7684\u9a74\u5b50\u3002 \u3000\u3000\u9a74\u5b50\u867d\u7136\u7626\u5c0f\uff0c\u770b\u6765\u5374\u6781\u77eb\u5065\uff0c\u8f7d\u7740\u8fd9\u4e48\u91cd\u7684\u4e00\u4e2a\u4eba\uff0c\u5c45\u7136\u8fd8\u80fd\u8d76\u5f97\u4e0a\u524d\u9762\u4e09\u5341\u516d\u5339\u5065\u9a6c\u3002 \u3000\u3000\u4eba\u867d\u9ad8\u5927\u80a5\u80d6\uff0c\u5374\u6ca1\u6709\u4e00\u70b9\u5a01\u6b66\u96c4\u58ee\u7684\u6c14\u6982\uff0c\u7a7f\u5f97\u4e5f\u5f88\u968f\u4fbf\uff0c\u8ddf\u5728\u4e09\u5341\u516d\u4e2a\u7740\u9c9c\u8863\u3001\u97ad\u5feb\u9a6c\u3001\u4f69\u957f\u5200\u7684\u9a91\u58eb\u540e\uff0c\u5c31\u50cf\u662f\u4e2a\u6742\u5f79\u8ddf\u73ed\uff01 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e9b\u9a91\u58eb\u4eec\u5bf9\u4ed6\u7684\u6001\u5ea6\u5374\u6781\u5c0a\u656c\uff0c\u751a\u81f3\u8fd8\u663e\u5f97\u6709\u4e9b\u754f\u60e7\u3002 \u3000\u3000\u4e09\u5341\u516d\u4e2a\u4eba\u504f\u8eab\u4e0b\u9a6c\u540e\uff0c\u7acb\u523b\u606d\u606d\u656c\u656c\u5730\u5782\u624b\u8083\u7acb\u5728\u4e24\u65c1\uff0c\u8fde\u5927\u6c14\u90fd\u4e0d\u6562\u5598\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u9a91\u5728\u9a74\u5b50\u4e0a\uff0c\u4e1c\u5f20\u897f\u671b\u5730\u770b\u4e86\u534a\u5929\uff0c\u624d\u6162\u541e\u541e\u5730\u4e0b\u4e86\u978d\uff0c\u4e00\u5f20\u7ea2\u901a\u901a\u7684\u8138\uff0c\u770b\u6765\u53c8\u8001\u5b9e\u53c8\u5fe0\u539a\uff0c\u8138\u4e0a\u8fd8\u5e26\u7740\u79cd\u8ff7\u60c6\u7684\u8868\u60c5\uff0c\u53c8\u4e1c\u5f20\u897f\u671b\u770b\u4e86\u534a\u5929\uff0c\u624d\u5411\u4e00\u4e2a\u9e2f\u80a9\u8702\u8170\u5927\u6c49\u62db\u4e86\u62db\u624b\uff0c\u6162\u541e\u541e\u5730\u95ee\uff1a\u201c\u4f60\u8bf4\u7684\u5c31\u662f\u8fd9\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u6211\u8bb0\u5f97\u4f60\u597d\u50cf\u662f\u8bf4\u8fc7\u8fd9\u5730\u65b9\u662f\u4e2a\u7eff\u6d32\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u7eff\u6d32\u662f\u4e0d\u662f\u90fd\u6709\u6c34\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u6c34\u5728\u54ea\u91cc\uff1f\u201d\u8fd9\u4e2a\u4eba\u53f9\u7740\u6c14\uff0c\u201c\u6211\u600e\u4e48\u8fde\u4e00\u6ef4\u6c34\u90fd\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u5927\u6c49\u5782\u4e0b\u5934\uff0c\u989d\u89d2\u9f3b\u5c16\u4e0a\u90fd\u5df2\u5192\u51fa\u6bd4\u9ec4\u8c46\u8fd8\u8981\u5927\u7684\u6c57\u73e0\u5b50\uff0c\u4e24\u6761\u817f\u4e5f\u597d\u50cf\u5728\u53d1\u6296\uff0c\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3\u90fd\u5df2\u7ecf\u5f00\u59cb\u53d1\u6296\u3002 \u3000\u3000\u201c\u4e09\u5e74\u524d\u6211\u5230\u8fd9\u91cc\u6765\u8fc7\uff0c\u8fd9\u91cc\u7684\u786e\u662f\u4e2a\u7eff\u6d32\uff0c\u7684\u786e\u6709\u6c34\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u5c45\u7136\u5e72\u6db8\u4e86\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\uff0c\u771f\u662f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u9a91\u9a74\u7684\u80d6\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u53c8\u95ee\u8fd9\u5927\u6c49\uff1a\u201c\u6700\u8fd1\u4f60\u8eab\u4f53\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u8fd8\u597d\u3002\u201d \u3000\u3000\u201c\u6709\u6ca1\u6709\u751f\u8fc7\u4ec0\u4e48\u75c5\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u9a91\u9a74\u7684\u80d6\u5b50\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u90a3\u4e48\u6211\u731c\u4f60\u4e00\u5b9a\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u5927\u6c49\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u8138\u4e0a\u672c\u6765\u5df2\u5145\u6ee1\u6050\u60e7\u4e4b\u6781\u7684\u8868\u60c5\uff0c\u73b0\u5728\u5374\u5ffd\u7136\u9732\u51fa\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5c45\u7136\u8fd8\u80fd\u7b11\u5f97\u51fa\uff0c\u4e5f\u662f\u4ef6\u4ee4\u4eba\u7edd\u5bf9\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u9a91\u9a74\u7684\u80d6\u5b50\u4e5f\u89c9\u5f97\u5f88\u610f\u5916\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u5f88\u597d\u7b11\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5927\u6c49\u8fd8\u5728\u7b11\uff0c\u7b11\u5bb9\u770b\u6765\u53c8\u6109\u5feb\u53c8\u795e\u79d8\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5374\u5145\u6ee1\u6050\u60e7\uff0c\u5ffd\u7136\u6162\u6162\u5730\u8dea\u4e86\u4e0b\u53bb\uff0c\u8dea\u4e0b\u53bb\u7684\u65f6\u5019\u4eff\u4f5b\u7b11\u5f97\u66f4\u6109\u5feb\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e5f\u770b\u51fa\u4e86\u8fd9\u80d6\u5b50\u7684\u6740\u673a\uff0c\u660e\u660e\u6015\u5f97\u8981\u547d\uff0c\u5c45\u7136\u8fd8\u80fd\u7b11\u5f97\u51fa\uff0c\u660e\u660e\u7b11\u5f97\u5f88\u6109\u5feb\uff0c\u5374\u53c8\u504f\u504f\u6015\u5f97\u8981\u547d\u3002 \u3000\u3000\u4e00\u4e2a\u6b63\u5e38\u7684\u4eba\u7edd\u4e0d\u4f1a\u50cf\u8fd9\u6837\u5b50\u7684\uff0c\u8fd9\u4e2a\u4eba\u662f\u4e0d\u662f\u5df2\u7ecf\u88ab\u5413\u75af\u4e86\uff1f \u3000\u3000\u4ed6\u7684\u540c\u4f34\u4eec\u90fd\u5728\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u672c\u6765\u663e\u5f97\u5f88\u60ca\u8bb6\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u4e5f\u5168\u90fd\u9732\u51fa\u4e86\u7b11\u5bb9\uff0c\u53c8\u6109\u5feb\u53c8\u795e\u79d8\u7684\u7b11\u5bb9\uff0c\u8ddf\u4ed6\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u7136\u540e\u8fd9\u4e09\u5341\u4e94\u4e2a\u4eba\u4e5f\u5168\u90fd\u8dea\u4e86\u4e0b\u53bb\uff0c\u8dea\u4e0b\u53bb\u7684\u65f6\u5019\u4e5f\u4eff\u4f5b\u7b11\u5f97\u66f4\u6109\u5feb\u3002 \u3000\u3000\u9a91\u9a74\u7684\u80d6\u5b50\u8138\u8272\u53d8\u4e86\uff0c\u4e5f\u53d8\u5f97\u60ca\u8bb6\u800c\u6050\u60e7\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u8138\u8272\u521a\u5f00\u59cb\u53d8\u7684\u65f6\u5019\uff0c\u4ed6\u8138\u4e0a\u5ffd\u7136\u4e5f\u9732\u51fa\u4e86\u7b11\u5bb9\uff0c\u53c8\u6109\u5feb\u53c8\u795e\u79d8\u7684\u7b11\u5bb9\uff0c\u548c\u53e6\u5916\u4e09\u5341\u516d\u4e2a\u4eba\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4e5f\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e09\u5341\u4e03\u4e2a\u4eba\u4e00\u8dea\u4e0b\u53bb\u5c31\u4e0d\u518d\u52a8\uff0c\u4e0d\u4f46\u8eab\u5b50\u4fdd\u6301\u539f\u6765\u7684\u59ff\u52bf\uff0c\u8138\u4e0a\u4e5f\u4fdd\u6301\u7740\u540c\u6837\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4e09\u5341\u516d\u4e2a\u4eba\u4e00\u76f4\u5728\u7b11\uff0c\u5c31\u597d\u50cf\u540c\u65f6\u770b\u5230\u4e00\u4ef6\u4ee4\u4ed6\u4eec\u6109\u5feb\u6781\u4e86\u7684\u4e8b\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5ffd\u7136\u63e1\u4f4f\u4e86\u5c0f\u65b9\u7684\u624b\uff0c\u5979\u7684\u624b\u51b0\u51b7\u800c\u6f6e\u6e7f\uff0c\u5c0f\u65b9\u7684\u624b\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u770b\u89c1\u8fd9\u4e09\u5341\u4e03\u4e2a\u4eba\u5982\u6b64\u6109\u5feb\u7684\u7b11\u5bb9\uff0c\u4ed6\u4eec\u8fde\u4e00\u70b9\u6109\u5feb\u7684\u611f\u89c9\u90fd\u6ca1\u6709\uff0c\u53ea\u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f46\u662f\u4ed6\u4eec\u5fc3\u91cc\u5ffd\u7136\u4e5f\u6709\u4e86\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\u3002 \u3000\u3000\u6f2b\u6f2b\u7684\u957f\u591c\u8fd8\u672a\u8fc7\u53bb\uff0c\u5927\u5730\u4e00\u7247\u9ed1\u6697\u6b7b\u5bc2\uff0c\u4e09\u5341\u516d\u4e2a\u4eba\u8fd8\u662f\u52a8\u4e5f\u4e0d\u52a8\u5730\u8dea\u5728\u90a3\u91cc\uff0c\u8138\u4e0a\u8fd8\u662f\u4fdd\u6301\u7740\u540c\u6837\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u8fde\u4ed6\u4eec\u7684\u7b11\u5bb9\u770b\u6765\u90fd\u4e0d\u4ee4\u4eba\u6109\u5feb\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7b11\u5bb9\u5df2\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u4eec\u5168\u8eab\u4e0a\u4e0b\u90fd\u5df2\u50f5\u786c\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u8dea\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4e00\u8dea\u4e0b\u53bb\u5c31\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6b7b\u7684\u65f6\u5019\uff0c\u5c31\u662f\u4ed6\u4eec\u8dea\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u4e5f\u5c31\u662f\u4ed6\u4eec\u7b11\u5f97\u6700\u6109\u5feb\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u4eec\u6b7b\u7684\u65f6\u5019\u4e3a\u4ec0\u4e48\u8981\u7b11\uff1f \u3000\u3000\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u8dea\u7740\u6b7b\uff1f \u3000\u3000\u5c0f\u65b9\u60f3\u95ee\u73ed\u5bdf\u5df4\u90a3\uff0c\u201c\u9633\u5149\u201d\u4e5f\u60f3\u95ee\uff0c\u6709\u5f88\u591a\u4e8b\u90fd\u60f3\u95ee\u3002 \u3000\u3000\u5728\u8fd9\u7247\u795e\u79d8\u800c\u65e0\u60c5\u7684\u5927\u5730\u4e0a\uff0c\u5982\u679c\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u89e3\u91ca\u8fd9\u79cd\u795e\u79d8\u800c\u53ef\u6015\u7684\u4e8b\uff0c\u8fd9\u4e2a\u4eba\u65e0\u7591\u5c31\u662f\u73ed\u5bdf\u5df4\u90a3\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5374\u4e0d\u8ba9\u4ed6\u4eec\u95f4\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4ece\u8eab\u4e0a\u62ff\u51fa\u6f06\u9ed1\u7684\u4e4c\u6728\u74f6\uff0c\u7528\u5c0f\u6307\u548c\u65e0\u540d\u6307\u634f\u4f4f\u74f6\u5b50\uff0c\u7528\u62c7\u6307\u548c\u98df\u6307\u62d4\u5f00\u74f6\u585e\uff0c\u4ece\u74f6\u5b50\u91cc\u5012\u51fa\u4e00\u70b9\u7c89\u672a\u62b9\u5728\u4e24\u5339\u9a6c\u7684\u9f3b\u5b50\u4e0a\u3002 \u3000\u3000\u672c\u6765\u5df2\u6e10\u6e10\u5f00\u59cb\u8981\u52a8\u7684\u9a6c\uff0c\u7acb\u523b\u4e0d\u518d\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u4e0d\u8ba9\u4eba\u51fa\u58f0\uff0c\u4e5f\u4e0d\u8ba9\u9a6c\u51fa\u58f0\u3002 \u3000\u3000\u6c99\u4e18\u524d\u4e09\u5341\u516d\u4e2a\u4eba\u5168\u90e8\u6b7b\u4e86\uff0c\u6b7b\u4eba\u662f\u4ec0\u4e48\u90fd\u542c\u4e0d\u5230\u7684\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u6562\u51fa\u58f0\uff1f \u3000\u3000\u4ed6\u6015\u8c01\u542c\u89c1\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e0d\u4f46\u51b7\u9759\u9547\u5b9a\uff0c\u800c\u4e14\u975e\u5e38\u9a84\u50b2\uff0c\u5bf9\u81ea\u5df1\u603b\u662f\u5145\u6ee1\u4fe1\u5fc3\uff0c\u5bf9\u522b\u4eba\u4e00\u65e0\u6240\u60e7\uff0c\u5927\u5bb6\u90fd\u627f\u8ba4\u8fd9\u4e16\u754c\u4e0a\u5df2\u7ecf\u5f88\u5c11\u6709\u80fd\u591f\u8ba9\u4ed6\u5bb3\u6015\u7684\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u7684\u8138\u8272\u5374\u53d8\u4e86\uff0c\u770b\u6765\u751a\u81f3\u6bd4\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u66f4\u5bb3\u6015\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u7684\u4e8b\u8fdc\u6bd4\u4ed6\u4eec\u591a\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u77e5\u9053\u8fd9\u4e9b\u4eba\u90fd\u4e2d\u4e86\u6bd2\u3002\u800c\u4e14\u8fd8\u77e5\u9053\u4ed6\u4eec\u4e2d\u7684\u5c31\u662f\u4f20\u8bf4\u4e2d\u6700\u53ef\u6015\u7684\u201c\u9634\u7075\u201d\u4e4b\u6bd2\u3002 \u3000\u3000\u4e00\u6bd2\u6027\u65e0\u8272\u65e0\u5473\uff0c\u6765\u5f97\u65e0\u5f71\u65e0\u5f62\uff0c\u4e0b\u6bd2\u7684\u4eba\u4e5f\u50cf\u9634\u9b42\u5e7d\u7075\u822c\u98d8\u5ffd\u8be1\u79d8\u3001\u6765\u53bb\u65e0\u8e2a\u3002 \u3000\u3000\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u4e0b\u6bd2\u7684\u4eba\u662f\u8c01\uff0c\u7528\u4ec0\u4e48\u65b9\u6cd5\u4e0b\u7684\u6bd2\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u81ea\u5df1\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u4e2d\u7684\u6bd2\uff0c\u7b49\u4ed6\u4eec\u77e5\u9053\u81ea\u5df1\u4e2d\u6bd2\u65f6\uff0c\u6bd2\u5df2\u65e0\u6551\u4e86\u3002\u4ed6\u4eec\u7684\u8138\u5df2\u56e0\u6bd2\u6027\u53d1\u4f5c\u800c\u626d\u66f2\u53d8\u5f62\uff0c\u4ed6\u4eec\u7684\u8eab\u5b50\u5df2\u56e0\u808c\u8089\u75c9\u631b\u800c\u8dea\u4e0b\u53bb\u3002 \u3000\u3000\u6bd2\u6740\u4ed6\u4eec\u7684\u201c\u9634\u7075\u201d\u4e5f\u8bb8\u8fd8\u5728\u5343\u91cc\u5916\uff0c\u4e5f\u8bb8\u5c31\u5728\u4ed6\u4eec\u9644\u8fd1\u3002 \u3000\u3000\u4e0d\u7ba1\u4ed6\u5728\u54ea\u91cc\uff0c\u4ed6\u8fdf\u65e9\u603b\u4f1a\u6765\u770b\u770b\u8fd9\u4e9b\u6b7b\u5728\u4ed6\u6bd2\u624b\u4e0b\u7684\u4eba\uff0c\u5c31\u597d\u50cf\u4e00\u4f4d\u540d\u5320\u5927\u5e08\u5b8c\u6210\u4e00\u4ef6\u7cbe\u54c1\u540e\uff0c\u603b\u5fcd\u4e0d\u4f4f\u8981\u6765\u6b23\u8d4f\u81ea\u5df1\u7684\u6770\u4f5c\uff0c\u53ef\u662f\u4ece\u6765\u90fd\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u6d3b\u7740\u770b\u5230\u4ed6\u7684\u771f\u9762\u76ee\uff0c\u56e0\u4e3a\u4ed6\u4e00\u5b9a\u8981\u7b49\u5230\u4ed6\u7684\u5bf9\u8c61\u5168\u90fd\u6b7b\u4e86\u4e4b\u540e\u624d\u5168\u6765\uff0c\u4ed6\u603b\u662f\u4f1a\u5b89\u6392\u4ed6\u4eec\u6b7b\u5728\u4e00\u4e2a\u5bc2\u9759\u8352\u51c9\u3001\u5f88\u5c11\u6709\u522b\u4eba\u4f1a\u53bb\u7684\u5730\u65b9\u3002 \u3000\u3000\u8fd9\u4e2a\u5e72\u6db8\u7684\u7eff\u6d32\u672c\u6765\u5df2\u5f88\u5c11\u6709\u4eba\u8ff9\uff0c\u73b0\u5728\u8fd9\u4e9b\u4eba\u90fd\u6b7b\u5149\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u201c\u9634\u7075\u201d\u4e5f\u5f88\u5feb\u5c31\u4f1a\u6765\u4e86\u3002 \u3000\u3000\u2014\u2014\u4ed6\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u662f\u7537\u662f\u5973\uff1f\u662f\u8001\u662f\u5c11\uff1f \u3000\u3000\u2014\u2014\u4ed6\u7a76\u7adf\u662f\u4eba\uff0c\u8fd8\u662f\u4e2a\u5e7d\u7075\u9b3c\u9b42\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u5fc3\u8df3\u5df2\u52a0\u5feb\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5982\u679c\u201c\u9634\u7075\u201d\u53d1\u73b0\u8fd9\u91cc\u8fd8\u6709\u6d3b\u4eba\uff0c\u8fd9\u4e2a\u6d3b\u4eba\u8fd8\u60f3\u518d\u6d3b\u4e0b\u53bb\u5c31\u5f88\u96be\u4e86\u3002 \u3000\u3000\u6f2b\u6f2b\u7684\u957f\u591c\u5df2\u5c06\u8fc7\u53bb\uff0c\u88ab\u6c57\u6e7f\u900f\u7684\u8863\u670d\u5df2\u88ab\u523a\u9aa8\u5bd2\u98ce\u5439\u5e72\u3002 \u3000\u3000\u9ed1\u6697\u7684\u82cd\u7a79\u5df2\u53d8\u6210\u4e86\u4e00\u79cd\u6bd4\u9ed1\u6697\u66f4\u9ed1\u6697\u7684\u6b7b\u7070\u8272\u3002 \u3000\u3000\u4e09\u5341\u4e03\u4e2a\u8dea\u7740\u6b7b\u7684\u4eba\u8fd8\u662f\u76f4\u633a\u633a\u5730\u8dea\u5728\u6b7b\u7070\u8272\u7684\u82cd\u7a79\u4e0b\uff0c\u7b49\u7740\u6bd2\u6740\u4ed6\u4eec\u7684\u201c\u9634\u7075\u201d\u6765\u770b\u4ed6\u4eec\u6700\u540e\u4e00\u773c\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u6765\u7684\u5374\u4e0d\u662f\u9634\u7075\uff0c\u662f\u4e00\u53ea\u9e70\u3002 \u3000\u3000\u98df\u5c38\u9e70\u3002 \u3000\u3000\u9e70\u5728\u76d8\u65cb\u3002 \u3000\u3000\u6b7b\u7070\u8272\u7684\u82cd\u7a79\u6e10\u6e10\u53d1\u767d\uff0c\u6e10\u6e10\u53d8\u6210\u4e86\u6b7b\u4eba\u773c\u767d\u4e00\u6837\u7684\u989c\u8272\u3002 \u3000\u3000\u76d8\u65cb\u4f4e\u98de\u7684\u98df\u5c38\u9e70\u5ffd\u7136\u843d\u4e0b\uff0c\u843d\u5728\u4e00\u4e2a\u8dea\u7740\u6b7b\u7684\u4eba\u8eab\u4e0a\uff0c\u7528\u94a2\u9525\u822c\u7684\u9e70\u693d\u5544\u53bb\u4e86\u8fd9\u4e2a\u4eba\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u662f\u5b83\u7684\u7b2c\u4e00\u53e3\u3002 \u3000\u3000\u5c31\u5728\u5b83\u51c6\u5907\u7ee7\u7eed\u4eab\u53d7\u5b83\u8fd9\u987f\u4e30\u7f8e\u7684\u65e9\u9910\u65f6\uff0c\u5b83\u7684\u53cc\u7fc5\u4e5f\u5ffd\u7136\u62bd\u7d27\u626d\u66f2\u3002 \u3000\u3000\u5b83\u4e0d\u662f\u8dea\u7740\u6b7b\u7684\u3002 \u3000\u3000\u9e70\u4e0d\u4f1a\u8dea\u4e0b\uff0c\u53ef\u662f\u9e70\u4e5f\u4f1a\u6b7b\u3002 \u3000\u3000\u201c\u9634\u7075\u201d\u7684\u6bd2\u5df2\u5e03\u6ee1\u4e86\u8fd9\u4e9b\u6b7b\u4eba\u7684\u6bcf\u4e00\u5206\u8840\u8089\uff0c\u8fd9\u53ea\u9e70\u5544\u98df\u4e86\u6b7b\u4eba\u7684\u8840\uff0c\u9e70\u4e5f\u88ab\u6bd2\u6740\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u89c9\u5f97\u80f8\u53e3\u5f88\u95f7\uff0c\u95f7\u5f97\u8fde\u6c14\u90fd\u900f\u4e0d\u51fa\uff0c\u80c3\u90e8\u4e5f\u5728\u6536\u7f29\uff0c\u4eff\u4f5b\u8fde\u82e6\u6c34\u90fd\u8981\u5410\u51fa\u6765\u3002\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u542c\u89c1\u4e86\u4e00\u58f0\u5f88\u5947\u602a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u542c\u89c1\u4e00\u58f0\u72ac\u5420\u3002 \u3000\u3000\u72ac\u5420\u58f0\u5e76\u4e0d\u5947\u602a\u3002\u5728\u6c5f\u5357\u8f6f\u7ea2\u5341\u4e08\u7684\u57ce\u5e02\u91cc\uff0c\u5728\u90a3\u4e9b\u5c71\u660e\u6c34\u79c0\u7684\u4e61\u6751\u4e2d\uff0c\u9e21\u72ac\u76f8\u95fb\uff0c\u4ed6\u6bcf\u5929\u90fd\u80fd\u542c\u89c1\u72ac\u5420\u58f0\uff0c\u60f3\u4e0d\u53bb\u542c\u90fd\u5f88\u96be\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u79cd\u8fb9\u9672\u8352\u5bd2\u4e4b\u5730\uff0c\u5728\u8fd9\u4e48\u6837\u4e00\u4e2a\u9634\u68ee\u5bd2\u51b7\u7684\u65e9\u4e0a\uff0c\u65e0\u8bba\u8c01\u90fd\u60f3\u4e0d\u81f3\u201c\u81ea\u5df1\u4f1a\u542c\u89c1\u72ac\u5420\u58f0\u7684\uff0c\u60f3\u4e0d\u53bb\u542c\u90fd\u5f88\u96be\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u79cd\u8fb9\u9672\u8352\u5bd2\u4e4b\u5730\uff0c\u5728\u8fd9\u4e48\u6837\u4e00\u4e2a\u9634\u68ee\u5bd2\u51b7\u7684\u65e9\u4e0a\uff0c\u65e0\u8bba\u8c01\u90fd\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u542c\u89c1\u5927\u5420\u58f0\u7684\uff0c\u5f53\u7136\u66f4\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u770b\u89c1\u4e00\u6761\u72d7\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u89c1\u4e86\u4e00\u6761\u72d7\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u6765\u7684\u4e5f\u4e0d\u662f\u201c\u9634\u7075\u201d\uff0c\u662f\u4e00\u6761\u72d7\u3002 \u3000\u3000\u4e00\u6761\u96ea\u767d\u53ef\u7231\u7684\u72ee\u5b50\u72d7\u3002 \u3000\u3000\uff0c \u3000\u3000\u5929\u8272\u51e0\u4e4e\u5df2\u7ecf\u5f88\u4eae\u4e86\uff0c\u5df2\u6e10\u6e10\u53d8\u6210\u4e86\u6b7b\u4eba\u9f3b\u5c16\u4e0a\u7684\u989c\u8272\u3002 \u3000\u3000\u8fd9\u6761\u96ea\u767d\u53ef\u7231\u7684\u72ee\u5b50\u72d7\u201c\u6c6a\u6c6a\u201d\u5730\u53eb\u7740\uff0c\u7528\u4e00\u79cd\u975e\u5e38\u751f\u52a8\u6d3b\u6cfc\u53ef\u7231\u7684\u59ff\u6001\u8dd1\u4e86\u8fc7\u6765\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u975e\u5e38\u53d7\u5ba0\u7684\u5c0f\u72d7\uff0c\u8dd1\u8fdb\u4e86\u5b83\u4e3b\u4eba\u7684\u95fa\u623f\u3002 \u3000\u3000\u5b83\u77e5\u9053\u5b83\u8fd9\u813e\u6c14\u6e29\u67d4\u7684\u4e3b\u4eba\u7edd\u4e0d\u4f1a\u8d23\u7f5a\u5b83\u7684\uff0c\u6240\u4ee5\u5b83\u770b\u89c1\u6bcf\u6837\u4e1c\u897f\u90fd\u8981\u54ac\u4e00\u53e3\uff0c\u770b\u89c1\u4e3b\u4eba\u7684\u7ee3\u82b1\u978b\u4e5f\u8981\u54ac\u4e00\u53e3\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u8fd9\u91cc\u4e0d\u662f\u5343\u91d1\u5c0f\u59d0\u7684\u95fa\u623f\uff0c\u8fd9\u91cc\u65e2\u6ca1\u6709\u813e\u6c14\u6e29\u67d4\u7684\u5927\u5c0f\u59d0\uff0c\u4e5f\u6ca1\u6709\u7ee3\u82b1\u978b\u3002 \u3000\u3000\u8fd9\u91cc\u53ea\u6709\u6b7b\u4eba\uff0c\u6b7b\u4eba\u811a\u4e0a\u7a7f\u7740\u7684\u662f\u76ae\u9774\u3002 \u3000\u3000\u8fd9\u6761\u96ea\u767d\u53ef\u7231\u7684\u72ee\u5b50\u72d7\u8fd8\u662f\u4e00\u53e3\u54ac\u4e86\u4e0b\u53bb\uff0c\u54ac\u7684\u4e0d\u662f\u6b7b\u4eba\u811a\u4e0a\u7684\u76ae\u9774\uff0c\u54ac\u7684\u662f\u6b7b\u4eba\u7684\u811a\u8e1d\u3002 \u3000\u3000\u8fd9\u6761\u96ea\u767d\u53ef\u7231\u7684\u72ee\u5b50\u72d7\u5c45\u7136\u5728\u6bcf\u4e2a\u6b7b\u4eba\u7684\u811a\u8e1d\u4e0a\u90fd\u54ac\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u6b7b\u4eba\u5df2\u4e0d\u4f1a\u75db\u4e86\uff0c\u6b7b\u4eba\u5df2\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5374\u6709\u70b9\u5fc3\u75db\u3002 \u3000\u3000\u5c31\u50cf\u662f\u5176\u4ed6\u90a3\u4e9b\u5341\u516b\u4e5d\u5c81\u7684\u5973\u5b69\u5b50\u4e00\u6837\uff0c\u5979\u4e5f\u5f88\u559c\u6b22\u8fd9\u79cd\u96ea\u767d\u53ef\u7231\u7684\u5c0f\u72d7\u3002 \u3000\u3000\u5979\u4e0d\u5fcd\u770b\u89c1\u8fd9\u4e48\u53ef\u7231\u7684\u4e00\u6761\u5c0f\u72d7\u4e5f\u50cf\u90a3\u53ea\u98df\u5c38\u9e70\u4e00\u6837\u88ab\u6bd2\u6740\u3002 \u3000\u3000\u5979\u4e0d\u5fcd\u770b\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u8981\u770b\u3002 \u3000\u3000\u6240\u4ee5\u5979\u770b\u89c1\u4e86\u8fd9\u4ef6\u602a\u4e8b\u3002 \u3000\u3000\u8fd9\u6761\u5c0f\u72d7\u975e\u4f46\u6ca1\u6709\u88ab\u6bd2\u6740\uff0c\u53cd\u800c\u53d8\u5f97\u66f4\u6d3b\u6cfc\u66f4\u597d\u73a9\u66f4\u53ef\u7231\u4e86\uff0c\u5c31\u597d\u50cf\u521a\u5403\u8fc7\u5b83\u7684\u4e3b\u4eba\u4eb2\u624b\u9012\u7ed9\u5b83\u7684\u7f8e\u98df\uff0c\u4e5f\u60f3\u7528\u6700\u53ef\u7231\u7684\u6837\u5b50\u6765\u56de\u62a5\uff0c\u6765\u535a\u53d6\u5b83\u4e3b\u4eba\u7684\u6b22\u5fc3\uff0c\u6240\u4ee5\u4e00\u76f4\u5728\u4e0d\u505c\u5730\u53eb\uff0c\u4e0d\u505c\u5730\u6447\u5c3e\u5df4\u3002 \u3000\u3000\u5b83\u5df2\u7ecf\u542c\u89c1\u5b83\u4e3b\u4eba\u5728\u53eb\u5b83\u3002 \u3000\u3000\u201c\u5c0f\u8001\u864e\uff0c\u5feb\u5feb\u5feb\uff0c\u8ba9\u5988\u5988\u4eb2\u4eb2\u4f60\uff0c\u62b1\u62b1\u4f60\u3002\u201d \u3000\u3000\u5b83\u662f\u6761\u5c0f\u72d7\uff0c\u4e0d\u662f\u5c0f\u8001\u864e\uff0c\u5b83\u7684\u201c\u5988\u5988\u201d\u4e5f\u4e0d\u662f\u72d7\uff0c\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u662f\u4e2a\u975e\u5e38\u53ef\u7231\u7684\u4eba\uff0c\u96ea\u767d\u7684\u76ae\u80a4\uff0c\u7075\u6d3b\u7684\u773c\u775b\uff0c\u4e4c\u9ed1\u7684\u5934\u53d1\u68b3\u6210\u4e86\u5341\u4e03\u516b\u6839\u5c0f\u8fab\u5b50\uff0c\u6bcf\u6839\u8fab\u5b50\u90fd\u7528\u7ea2\u4e1d\u7ebf\u7ed3\u4e86\u4e2a\u8774\u8776\u7ed3\u3002 \u3000\u3000\u5728\u5c71\u660e\u6c34\u79c0\u7684\u6c5f\u5357\uff0c\u5728\u6625\u5149\u660e\u5a9a\u3001\u9e2f\u98de\u8349\u957f\u7684\u4e09\u6708\uff0c\u5728\u897f\u5b50\u66fe\u7ecf\u70f7\u7eb1\u7684\u5c0f\u6eaa\u65c1\uff0c\u4f60\u4e5f\u8bb8\u5076\u7136\u4f1a\u770b\u89c1\u8fd9\u4e48\u6837\u4e00\u4e2a\u53ef\u7231\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u53ef\u662f\u5728\u6b64\u65f6\u6b64\u523b\u6b64\u5730\uff0c\u65e0\u8bba\u8c01\u90fd\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u770b\u89c1\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u5979\u5f53\u7136\u4e0d\u4f1a\u662f\u201c\u9634\u7075\u201d\uff0c\u7edd\u4e0d\u662f\u3002 \u3000\u3000\u2014\u2014\u5979\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u4f1a\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff0c\u800c\u4e14\u8fd8\u5e26\u4e86\u6761\u5c0f\u72d7\u6765\uff1f \u3000\u3000\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u8fd8\u6709\u4e09\u5341\u516d\u4e2a\u4eba\u6b7b\u4eba\u8dea\u5728\u90a3\u91cc\uff0c\u201c\u9633\u5149\u201d\u4e00\u5b9a\u4f1a\u8dd1\u8fc7\u6c99\u4e18\u53bb\u95f4\u5979\uff0c\u4ece\u81ea\u5df1\u7684\u884c\u56ca\u4e2d\u5206\u7ed9\u5979\u4e00\u7897\u9178\u9178\u751c\u751c\u7684\u7f8a\u5976\uff0c\u518d\u95f4\u5979\u6709\u6ca1\u6709\u5a46\u5bb6\uff0c\u613f\u4e0d\u613f\u610f\u8ddf\u5c0f\u65b9\u4ea4\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u5979\u8fd9\u4e3b\u610f\u5f88\u5feb\u5c31\u88ab\u5979\u81ea\u5df1\u6253\u6d88\u4e86\uff0c\u5c31\u7b97\u6ca1\u6709\u6b7b\u4eba\u5979\u4e5f\u4e0d\u4f1a\u8dd1\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5ffd\u7136\u770b\u89c1\u4e86\u4e00\u4e2a\u6bd4\u6b7b\u4eba\u66f4\u53ef\u6015\u7684\u4eba\uff0c\u7a7f\u7740\u96ea\u767d\u7684\u8863\u670d\uff0c\u5c31\u50cf\u662f\u9b3c\u9b42\u822c\u5ffd\u7136\u51fa\u73b0\u5728\u8fd9\u4e2a\u68b3\u7740\u5341\u4e03\u516b\u6839\u5c0f\u8fab\u5b50\u7684\u5c0f\u59d1\u5a18\u8eab\u540e\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u7edd\u5bf9\u4e0d\u80fd\u7b97\u662f\u4e2a\u4e11\u964b\u7684\u4eba\uff0c\u9ad8\u9ad8\u7684\u8eab\u6750\u4fee\u957f\u7b14\u633a\uff0c\u96ea\u767d\u7684\u8863\u670d\u6574\u6d01\u5408\u8eab\uff0c\u800c\u4e14\u4e94\u5b98\u4e5f\u957f\u5f97\u975e\u5e38\u82f1\u4fca\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u6bd4\u5927\u591a\u6570\u7537\u4eba\u90fd\u597d\u770b\u5f97\u591a\uff0c\u4f46\u662f\u65e0\u8bba\u8c01\u770b\u89c1\u4ed6\u90fd\u4f1a\u88ab\u5413\u51fa\u4e00\u8eab\u6c57\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u770b\u6765\u4eff\u4f5b\u662f\u900f\u660e\u7684\uff0c\u9732\u5728\u8863\u88f3\u5916\u9762\u7684\u5730\u65b9\u90fd\u662f\u900f\u660e\u7684\uff0c\u6bcf\u4e00\u6839\u8840\u7ba1\u6bcf\u4e00\u6839\u7b4b\uff0c\u751a\u81f3\u8fde\u6bcf\u4e00\u6839\u9aa8\u5934\u90fd\u80fd\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5168\u8eab\u4e0a\u4e0b\u7684\u76ae\u80a4\u5c31\u50cf\u662f\u4e00\u5c42\u6c34\u6676\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u53eb\u4e86\u51fa\u6765\uff0c\u53eb\u8fd9\u4e2a\u53ef\u7231\u7684\u5c0f\u59d1\u5a18\u5feb\u8dd1\uff0c\u8dd1\u5f97\u8d8a\u5feb\u8d8a\u597d\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u4e0d\u66ff\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u62c5\u5fc3\u3002 \u3000\u3000\u8fd9\u4e2a\u6c34\u6676\u4eba\u662f\u4e0d\u662f\u4e3a\u4e86\u5979\u6765\u7684\uff1f\u4f1a\u600e\u4e48\u6837\u5bf9\u4ed8\u5979\uff1f \u3000\u3000\u5c31\u7b97\u4ed6\u4e0d\u53bb\u52a8\u5979\uff0c\u7b49\u5979\u770b\u89c1\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u5c31\u7ad9\u5728\u81ea\u5df1\u80cc\u540e\u65f6\uff0c\u4e5f\u4f1a\u88ab\u6d3b\u6d3b\u5413\u6b7b\u7684\u3002 \u3000\u3000\u73b0\u5728\u5979\u5df2\u7ecf\u770b\u89c1\u4ed6\u4e86\u3002 \u3000\u3000\u5979\u975e\u4f46\u8fde\u4e00\u70b9\u5bb3\u6015\u7684\u6837\u5b50\u90fd\u6ca1\u6709\uff0c\u53cd\u800c\u9ad8\u5174\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u62b1\u4f4f\u4e86\u4ed6\u7684\u8116\u5b50\uff0c\u5728\u4ed6\u900f\u660e\u7684\u8138\u4e0a\u4eb2\u4e86\u4eb2\u3002 \u3000\u3000\u8fd9\u4e2a\u6c34\u6676\u4eba\u5c45\u7136\u4e5f\u4f1a\u7b11\uff0c\u800c\u4e14\u8fd8\u4f1a\u8bf4\u8bdd\uff0c\u58f0\u97f3\u91cc\u5c45\u7136\u5145\u6ee1\u67d4\u60c5\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u53c8\u8ba9\u4eba\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u201c\u662f\u4e0d\u662f\u5168\u90e8\u6b7b\u4e86\uff1f\u201d\u4ed6\u8f7b\u629a\u7740\u8fd9\u5c0f\u59d1\u5a18\u7684\u67d4\u53d1\u67d4\u58f0\u95ee\uff0c\u201c\u662f\u4e0d\u662f\u5df2\u7ecf\u6b7b\u5f97\u5e72\u5e72\u51c0\u51c0\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5168\u90fd\u6b7b\u4e86\u3002\u201d\u5c0f\u59d1\u5a18\u7b54\u9053\uff0c\u201c\u4f60\u8981\u4e0d\u8981\u53eb\u5c0f\u8001\u864e\u518d\u53bb\u54ac\u4ed6\u4eec\u4e00\u53e3\u8bd5\u8bd5\u770b\uff1f\u201d \u3000\u3000\u5979\u772f\u7740\u773c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bb8\u4ed6\u4eec\u770b\u89c1\u4eca\u5929\u7684\u592a\u9633\uff0c\u4ed6\u4eec\u600e\u4e48\u80fd\u6d3b\u5230\u592a\u9633\u5347\u8d77\u6765\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u5fcd\u4e0d\u4f4f\u53c8\u6084\u6084\u63e1\u4f4f\u5c0f\u65b9\u7684\u624b\uff0c\u4e24\u4e2a\u4eba\u7684\u624b\u90fd\u6bd4\u521a\u624d\u66f4\u51b7\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u201c\u6c34\u6676\u4eba\u201d\u5c31\u662f\u201c\u9634\u7075\u201d\u3002 \u3000\u3000\u2014\u2014\u8fd9\u6761\u5c0f\u72d7\u521a\u624d\u53bb\u54ac\u90a3\u4e9b\u6b7b\u4eba\u7684\u811a\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u53bb\u8bd5\u8bd5\u4ed6\u4eec\u662f\u4e0d\u662f\u5df2\u7ecf\u771f\u7684\u6b7b\u4eba\uff0c\u53ea\u6709\u6b7b\u4eba\u624d\u4e0d\u4f1a\u75db\u3002 \u3000\u3000\u2014\u2014\u4e00\u5b9a\u8981\u7b49\u5230\u6bcf\u4e2a\u4eba\u5168\u90fd\u6b7b\u5149\uff0c\u201c\u9634\u7075\u201d\u624d\u4f1a\u51fa\u73b0\u3002 \u3000\u3000\u4f46\u662f\u201c\u9633\u5149\u201d\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5c0f\u65b9\u548c\u73ed\u5bdf\u5df4\u90a3\u4e5f\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u4eec\u7ec8\u4e8e\u6d3b\u7740\u770b\u5230\u4e86\u201c\u9634\u7075\u201d\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u80fd\u6d3b\u591a\u4e45\uff1f \u3000\u3000\u201c\u9634\u7075\u201d\u5f88\u53ef\u80fd\u5df2\u7ecf\u53d1\u73b0\u4e86\u4ed6\u4eec\uff0c\u5df2\u7ecf\u65bd\u653e\u51fa\u4ed6\u90a3\u65e0\u8272\u65e0\u5473\u65e0\u5f71\u65e0\u5f62\u7684\u6bd2\uff0c\u53d1\u5728\u98ce\u91cc\uff0c\u53d1\u5728\u7a7a\u6c14\u91cc\uff0c\u7b49\u4ed6\u4eec\u53d1\u73b0\u81ea\u5df1\u4e2d\u6bd2\u65f6\uff0c\u5df2\u7ecf\u8dea\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u8dea\u4e0b\u53bb\u6b7b\uff01 \u3000\u3000\u4e00\u4e2a\u4eba\u5c31\u7b97\u8981\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u8dea\u7740\u6b7b\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u51fa\u53bb\u8ddf\u4ed6\u62fc\u4e00\u62fc\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u51b2\u51fa\u53bb\u4e86\uff0c\u53ef\u662f\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5979\u53c8\u770b\u89c1\u4e86\u4e00\u4ef6\u53ef\u6015\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4e09\u5341\u516d\u4e2a\u8dea\u5728\u5730\u4e0a\u7684\u6b7b\u4eba\u4e2d\uff0c\u7adf\u6709\u4e00\u4e2a\u5ffd\u7136\u590d\u6d3b\u4e86\u3002 \u3000\u3000\u590d\u6d3b\u4e86\u7684\u6b7b\u4eba\u5c31\u662f\u90a3\u4e2a\u9a91\u9a74\u7684\u80d6\u5b50\uff01 \u3000\u3000\u4ed6\u9ad8\u5927\u80a5\u80d6\u7684\u8eab\u5b50\u5ffd\u7136\u50cf\u662f\u6761\u9ec4\u6cb3\u9ca4\u9c7c\u822c\u51cc\u7a7a\u8dc3\u8d77\uff0c\u6eda\u51fa\u4e86\u4e00\u67f1\u94f6\u5149\u3002 \u3000\u3000\u94f6\u5149\u4e00\u95ea\uff0c\u843d\u5728\u90a3\u6c34\u6676\u4eba\u8eab\u4e0a\uff0c\u7adf\u662f\u4e00\u9762\u7f51\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u5728\u7a7a\u4e2d\u4e00\u633a\uff0c\u7ffb\u8eab\u843d\u5728\u4e00\u68f5\u67af\u6811\u4e0a\uff0c\u63d0\u8d77\u4e86\u8fd9\u9762\u94f6\u7f51\u3002 \u3000\u3000\u8fd9\u4e2a\u6c34\u6676\u4eba\u7acb\u523b\u53d8\u6210\u4e86\u7f51\u4e2d\u7684\u9c7c\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5982\u679c\u771f\u7684\u6b7b\u4e86\uff0c\u5c31\u7edd\u4e0d\u4f1a\u590d\u6d3b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u53ea\u6709\u4e00\u6761\u547d\uff0c\u53ea\u80fd\u6b7b\u4e00\u6b21\u3002 \u3000\u3000\u8fd9\u4e2a\u80d6\u5b50\u5f53\u7136\u4e5f\u4e0d\u80fd\u4f8b\u5916\u3002 \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u6211\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d\u4ed6\u5927\u7b11\uff0c\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u4e16\u4e0a\u8fd8\u6709\u4f60\u6bd2\u4e0d\u6b7b\u7684\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u7b11\u5f97\u6109\u5feb\u6781\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u4ed6\u5b9e\u5728\u505a\u5f97\u5f88\u5f97\u610f\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u7b11\u5c31\u8981\u7ed3\u675f\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u770b\u89c1\u4e86\u4e00\u4ef6\u8fde\u4ed6\u90fd\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u4e5f\u5728\u7b11\u3002 \u3000\u3000\u521a\u624d\u5979\u62b1\u7740\u90a3\u6c34\u6676\u4eba\u4eb2\u4e86\u53c8\u4eb2\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u5f53\u7136\u5f88\u4eb2\u5bc6\uff0c\u73b0\u5728\u5979\u7684\u4eb2\u4eba\u5ffd\u7136\u88ab\u540a\u4e86\u8d77\u6765\uff0c\u5979\u5e94\u8be5\u89c9\u5f97\u5f88\u5403\u60ca\u3001\u5f88\u6124\u6012\u3001\u5f88\u96be\u53d7\u624d\u5bf9\uff0c\u5982\u679c\u5979\u4e0d\u6562\u8ddf\u8fd9\u4e2a\u80d6\u5b50\u62fc\u547d\uff0c\u5c31\u8be5\u8d76\u5feb\u9003\u547d\u7684\u3002 \u3000\u3000\u53ef\u662f\u5979\u504f\u504f\u8fd8\u5728\u7b11\uff0c\u4e0d\u4f46\u5728\u7b11\uff0c\u800c\u4e14\u8fd8\u5728\u62cd\u624b\uff0c\u4e0d\u4f46\u7b11\u5f97\u6bd4\u8c01\u90fd\u5f00\u5fc3\uff0c\u62cd\u624b\u4e5f\u6bd4\u8c01\u90fd\u62cd\u5f97\u8d77\u52b2\u3002 \u3000\u3000\u201c\u597d\u529f\u592b\uff01\u597d\u672c\u4e8b\uff01\u201d\u5979\u62cd\u7740\u624b\u7b11\u9053\uff0c\u201c\u5c31\u7b97\u4f60\u522b\u7684\u672c\u4e8b\u90fd\u4e0d\u600e\u4e48\u6837\uff0c\u88c5\u6b7b\u7684\u672c\u4e8b\u7edd\u5bf9\u53ef\u4ee5\u7b97\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u5979\u53c8\u95ee\uff1a\u201c\u521a\u624d\u5c0f\u8001\u864e\u54ac\u4f60\u7684\u65f6\u5019\uff0c\u4f60\u96be\u9053\u4e00\u70b9\u90fd\u4e0d\u75db\uff1f\u201d \u3000\u3000\u80d6\u5b50\u53c8\u7b11\u4e86\uff1a\u201c\u8c01\u8bf4\u6211\u4e0d\u75db\uff0c\u6211\u75db\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u80fd\u5fcd\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u201c\u60f3\u5230\u8fd9\u4f4d\u6a2a\u884c\u5929\u4e0b\uff0c\u65e0\u8bba\u8c01\u4e00\u542c\u89c1\u90fd\u4f1a\u5413\u4e00\u8df3\u7684\u2018\u9634\u7075\u2019\uff0c\u9634\u5148\u751f\u9a6c\u4e0a\u5c31\u8981\u88ab\u6211\u7528\u7f51\u5b50\u540a\u8d77\u6765\u7684\u65f6\u5019\uff0c\u518d\u75db\u6211\u90fd\u80fd\u5fcd\u5f97\u4f4f\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u7406\uff0c\u975e\u5e38\u6709\u7406\u3002\u201d\u5c0f\u59d1\u5a18\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff0c\u201c\u80e1\u5927\u638c\u67dc\u8bf4\u7684\u8bdd\uff0c\u597d\u50cf\u603b\u662f\u6709\u9053\u7406\u7684\u3002\u201d \u3000\u3000\u73b0\u5728\u201c\u9633\u5149\u201d\u624d\u77e5\u9053\u8fd9\u4e2a\u80d6\u5b50\u59d3\u80e1\uff0c\u800c\u4e14\u662f\u4f4d\u5927\u638c\u67dc\u3002 \u3000\u3000\u5728\u5317\u65b9\uff0c\u5927\u638c\u67dc\u5c31\u662f\u5927\u8001\u677f\uff0c\u4ed6\u770b\u6765\u786e\u5b9e\u4e5f\u6709\u51e0\u5206\u50cf\u662f\u4f4d\u5927\u8001\u677f\u7684\u6837\u5b50\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u60f3\u4e0d\u5230\u80e1\u5927\u638c\u67dc\u4eca\u5929\u5c45\u7136\u8bf4\u9519\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u88ab\u4f60\u7528\u7f51\u5b50\u540a\u8d77\u6765\u7684\u8fd9\u4e2a\u4eba\u5e76\u4e0d\u662f\u9634\u5148\u751f\u3002\u201d\u5c0f\u59d1\u5a18\u9053\uff0c\u201c\u4f60\u6839\u672c\u4e0d\u8be5\u628a\u90a3\u4f4d\u4eba\u4eba\u542c\u89c1\u90fd\u4f1a\u5413\u4e00\u8df3\u7684\u2018\u9634\u7075\u2019\u79f0\u4e3a\u9634\u5148\u751f\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5e94\u8be5\u79f0\u547c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u53eb\u4e00\u58f0\u9634\u5927\u5c0f\u59d0\u3002\u201d\u5979\u53c8\u5f00\u59cb\u7b11\uff0c\u201c\u6700\u5c11\u4e5f\u5e94\u8be5\u53eb\u4e00\u58f0\u9634\u5927\u59d1\u5a18\uff01\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u5f53\u7136\u8981\u95ee\uff1a\u201c\u8fd9\u4f4d\u9634\u5927\u5c0f\u59d0\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u8fd9\u91cc\uff0c\u5c31\u5728\u4f60\u9762\u524d\u3002\u201d\u5979\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u201c\u6211\u5c31\u662f\u9634\u5927\u5c0f\u59d0\uff0c\u9634\u5927\u5c0f\u59d0\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u53c8\u7b11\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e2a\u5934\u4e0a\u68b3\u7740\u5341\u516d\u516b\u6761\u8fab\u5b50\uff0c\u624b\u91cc\u62b1\u7740\u6761\u5c0f\u72d7\uff0c\u7b11\u8d77\u6765\u597d\u50cf\u662f\u4f60\u81ea\u5df1\u5916\u5b59\u5973\u90a3\u4e48\u53ef\u7231\u7684\u4e00\u4e2a\u5c0f\u59d1\u5a18\u662f\u201c\u9634\u7075\u201d\u3002 \u3000\u3000\u5979\u53c8\u62b1\u8d77\u4e86\u5979\u7684\u5c0f\u72d7\u3002\u5979\u5ffd\u7136\u95f4\u8fd9\u4f4d\u5df2\u7ecf\u7b11\u4e0d\u51fa\u7684\u5927\u638c\u67dc\uff1a\u201c\u6211\u5531\u4e2a\u6b4c\u7ed9\u4f60\u542c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u65f6\u5019\u5979\u5c45\u7136\u8981\u5531\u6b4c\uff0c\u5979\u5c45\u7136\u771f\u7684\u5531\u4e86\u8d77\u6765 \u3000\u3000\u201c\u71d5\u5317\u6709\u4e2a\u4e09\u5b9d\u5802\uff0c \u3000\u3000\u540d\u6c14\u8bf4\u6765\u54cd\u5f53\u5f53\u3002 \u3000\u3000\u4e09\u5b9d\u5802\u91cc\u6709\u4e09\u5b9d\uff0c \u3000\u3000\u8c01\u89c1\u8c01\u906d\u6b83\uff0c\u4e24\u773c\u6cea\u6c6a\u6c6a\u3002 \u3000\u3000\u7239\u89c1\u6ca1\u6709\u7239\uff0c\u5a18\u89c1\u6ca1\u6709\u5a18\uff0c\u8c01\u89c1\u8c01\u906d\u6b83\uff0c\u773c\u6cea\u5982\u7c73\u6c64\u3002\u201d \u3000\u3000\u5979\u5531\u7684\u6839\u672c\u4e0d\u80fd\u7b97\u662f\u4e00\u9996\u6b4c\uff0c\u8bcd\u53e5\u66f4\u4e0d\u80fd\u7b97\u4f18\u7f8e\uff0c\u53ea\u4e0d\u8fc7\u6bcf\u4e00\u53e5\u90fd\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u4e09\u5b9d\u5802\u96c4\u8e1e\u71d5\u5317\uff0c\u540d\u6c14\u7684\u786e\u975e\u5e38\u54cd\u4eae\u3002\u4e09\u5b9d\u5802\u4e2d\u7684\u786e\u6709\u4e09\u5b9d\uff0c\u6c5f\u6e56\u4e2d\u4eba\u5982\u679c\u9047\u5230\u8fd9\u4e09\u5b9d\uff0c\u4e0d\u906d\u6b83\u7684\u786e\u5b9e\u5f88\u5c11\u3002 \u3000\u3000\u7b49\u5979\u5531\u5b8c\u4e86\uff0c\u80e1\u5927\u638c\u67dc\u4e5f\u4e3a\u5979\u62cd\u624b\u3002 \u3000\u3000\u201c\u4f60\u51ed\u826f\u5fc3\u8bf4\uff0c\u6211\u5531\u7684\u8fd9\u652f\u6b4c\u597d\u542c\u4e0d\u597d\u542c\uff1f\u201d \u3000\u3000\u201c\u597d\u542c\u3002\u201d\u80e1\u5927\u638c\u67dc\u7b11\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u4ece\u6765\u90fd\u6ca1\u6709\u4eba\u6bd4\u4f60\u5531\u5f97\u66f4\u597d\u542c\u3002\u201d \u3000\u3000\u9634\u5927\u5c0f\u59d0\u5403\u5403\u5730\u7b11\u9053\uff1a\u201c\u5343\u7a7f\u4e07\u7a7f\uff0c\u9a6c\u5c41\u4e0d\u7a7f\u3002\u4f60\u8fd9\u4e48\u606d\u7ef4\u6211\uff0c\u6211\u5f53\u7136\u4e5f\u8981\u79f0\u8d5e\u4f60\u4e24\u53e5\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u3001\u5f53\u7136\u3002\u201d \u3000\u3000\u201c\u522b\u4eba\u542c\u6211\u79f0\u4f60\u4e3a\u5927\u638c\u67dc\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u4f60\u6700\u591a\u53ea\u4e0d\u8fc7\u662f\u5bb6\u5c0f\u996d\u9986\u5927\u638c\u67dc\u800c\u5df2\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u4e5f\u60c5\u613f\u5982\u6b64\uff0c\u90a3\u4e9b\u5c0f\u996d\u9986\u7684\u5927\u638c\u67dc\uff0c\u9ebb\u70e6\u4e00\u5b9a\u6bd4\u6211\u5c0f\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u4f60\u504f\u504f\u5c31\u662f\u4e09\u5b9d\u5802\u7684\u5927\u638c\u67dc\uff0c\u60f3\u8d56\u90fd\u8d56\u4e0d\u6389\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4f60\u7684\u4e09\u5b9d\u5802\u91cc\u7a76\u7adf\u6709\u54ea\u4e09\u5b9d\uff1f\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u5fae\u7b11\uff1a\u201c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u9634\u5927\u5c0f\u59d0\u773c\u73e0\u5b50\u76f4\u8f6c\uff1a\u201c\u8fd9\u4e2a\u4f1a\u540a\u4eba\u7684\u7f51\u5b50\u5f53\u7136\u662f\u4e00\u5b9d\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u4f60\u8fd8\u6709\u79cd\u53eb\u2018\u98ce\u51f0\u5c55\u7fc5\u2019\u7684\u6697\u5668\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u6614\u5e74\u5b54\u96c0\u5c71\u5e84\u7684\u5b54\u96c0\u7fce\uff0c\u4e5f\u5dee\u4e0d\u4e86\u592a\u591a\u3002\u201d\u9634\u5927\u5c0f\u59d0\u9053\uff1a\u201c\u90a3\u5f53\u7136\u4e5f\u5e94\u8be5\u7b97\u4e00\u5b9d\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u5e94\u8be5\u3002\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u516b\u7ae0\u3000\u80e1\u5927\u638c\u67dc \u7b2c\u5341\u516b\u7ae0\u3000\u80e1\u5927\u638c\u67dc \u3000\u3000\u201c\u8fd8\u6709\u4e00\u5b9d\u7528\u4e0d\u7740\u4f60\u8bf4\u6211\u4e5f\u731c\u5f97\u51fa\u4e86\u3002\u201d \u3000\u3000\u9634\u5927\u5c0f\u59d0\u7b11\u9053\uff0c\u201c\u4e09\u5b9d\u5802\u4e2d\u6700\u5b9d\u8d35\u7684\u4e00\u5b9d\u5f53\u7136\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u5927\u7b11\uff1a\u201c\u5bf9\uff0c\u5b8c\u5168\u5bf9\uff0c\u6211\u82e5\u4e0d\u662f\u5b9d\uff0c\u600e\u4e48\u6bd2\u4e0d\u6b7b\uff1f\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u6c5f\u6e56\u4e2d\u90fd\u8bf4\u4f60\u6bd2\u4e0d\u6b7b\uff0c\u6240\u4ee5\u6211\u624d\u60f3\u8bd5\u8bd5\u4f60\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u8bd5\u8fc7\u4e86\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u9053\uff1a\u201c\u597d\u50cf\u5df2\u7ecf\u5e94\u8be5\u8f6e\u5230\u6211\u6765\u8bd5\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u8bd5\u4ec0\u4e48\uff1f\u600e\u4e48\u8bd5\uff1f\u201d \u3000\u3000\u201c\u8bd5\u8bd5\u4f60\u80fd\u4e0d\u80fd\u907f\u5f97\u8fc7\u6211\u7684\u2018\u51e4\u51f0\u5c55\u7fc5\u2019\uff01\u201d \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u867d\u7136\u8fd8\u5728\u7b11\uff0c\u773c\u775b\u91cc\u5374\u5df2\u9732\u51fa\u6740\u673a\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u867d\u7136\u6ca1\u6709\u52a8\uff0c\u4e24\u53ea\u624b\u7684\u624b\u80cc\u4e0a\u5374\u5df2\u6709\u9752\u7b4b\u51f8\u8d77\u3002 \u3000\u3000\u9634\u5927\u5c0f\u59d0\u773c\u73e0\u5b50\u53c8\u8f6c\u4e86\u8f6c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u771f\u7684\u76f8\u4fe1\u6211\u5c31\u662f\u2018\u9634\u7075\u2019\uff1f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5148\u95f4\u95ee\u6211\uff0c\u88ab\u4f60\u540a\u8d77\u6765\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u76ef\u7740\u5979\uff0c\u773c\u775b\u8fde\u7728\u90fd\u4e0d\u7728\uff0c\u597d\u50cf\u5df2\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u7edd\u4e0d\u56de\u5934\u53bb\u770b\u90a3\u4e2a\u6c34\u6676\u4eba\u3002 \u3000\u3000\u4ed6\u7528\u4e0d\u7740\u518d\u4e3a\u4e00\u4e2a\u5df2\u7ecf\u88ab\u540a\u5728\u7f51\u5b50\u91cc\u7684\u4eba\u5206\u5fc3\uff0c\u4e0d\u7ba1\u8fd9\u4e2a\u4eba\u662f\u8c01\u90fd\u4e00\u6837\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u95ee\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\u4ed6\u6839\u672c\u4e0d\u80fd\u7b97\u662f\u4e00\u4e2a\u4eba\u3002\u201d\u9634\u5927\u5c0f\u59d0\u9053\uff1a\u201c\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u74f6\u5b50\u3002\u201d \u3000\u3000\u201c\u74f6\u5b50\uff1f\u4ec0\u4e48\u74f6\u5b50\uff1f\u201d \u3000\u3000\u201c\u88c5\u6bd2\u836f\u7684\u74f6\u5b50\uff0c\u91cc\u9762\u5404\u5f0f\u5404\u6837\u7684\u6bd2\u836f\u90fd\u6709\u3002\u201d \u3000\u3000\u9634\u5927\u5c0f\u59d0\u9053\uff1a\u201c\u6240\u4ee5\u53ea\u8981\u4f60\u7684\u624b\u6562\u52a8\u4e00\u52a8\uff0c\u5c31\u6b7b\u5b9a\u4e86\uff01\u201d \u3000\u3000\u201c\u8c01\u6b7b\u5b9a\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\uff01\u5f53\u7136\u662f\u4f60\u3002\u201d \u3000\u3000\u9634\u5927\u5c0f\u59d0\u67d4\u58f0\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u5bf9\u4f60\u5439\u4e00\u53e3\u6c14\u4f60\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u5927\u7b11\uff1a\u201c\u4e0d\u7ba1\u4f60\u8bf4\u4ec0\u4e48\u90fd\u9a97\u4e0d\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u9053\uff0c\u201c\u6211\u8fd9\u4eba\u957f\u5f97\u867d\u7136\u50cf\u5934\u732a\uff0c\u5176\u5b9e\u5374\u662f\u6761\u8001\u72d0\u72f8\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u7684\u624b\u4e00\u52a8\uff0c\u4f60\u5c31\u7acb\u5373\u662f\u6761\u6b7b\u72d0\u72f8\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u7684\u7b11\u58f0\u5ffd\u7136\u505c\u987f\u3002 \u3000\u3000\u8fd9\u6b21\u8bf4\u8bdd\u7684\u4eba\u4e0d\u662f\u9634\u5927\u5c0f\u59d0\uff0c\u5f53\u7136\u4e5f\u4e0d\u662f\u4ed6\u81ea\u5df1\uff0c\u8bf4\u8bdd\u7684\u4eba\u5c31\u5728\u4ed6\u80cc\u540e\uff0c\u79bb\u5f00\u4ed6\u7edd\u5bf9\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u5c3a\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u7a81\u7136\u62d4\u8d77\uff0c\u51cc\u7a7a\u7ffb\u8eab\uff0c\u7acb\u523b\u5c31\u53d1\u73b0\u672c\u6765\u540a\u5728\u7f51\u5b50\u91cc\u7684\u4eba\u5df2\u4e0d\u5728\u7f51\u5b50\u91cc\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4e0b\u51b3\u5fc3\u7edd\u4e0d\u4e0a\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u7684\u5f53\uff0c\u7edd\u4e0d\u56de\u5934\u53bb\u770b\u7684\u65f6\u5019\uff0c\u8fd9\u4e2a\u6c34\u6676\u4eba\u5df2\u7ecf\u4ece\u4ed6\u7684\u7f51\u5b50\u91cc\u8131\u8eab\u800c\u51fa\uff0c\u5230\u4e86\u4ed6\u7684\u80cc\u540e\uff0c\u4ed6\u7684\u7f51\u5b50\u5df2\u7ecf\u5230\u4e86\u8fd9\u4e2a\u4eba\u624b\u91cc\u3002 \u3000\u3000\u80e1\u5927\u638c\u67dc\u8fd8\u662f\u4e0a\u5f53\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u6c34\u6676\u4eba\uff0c\u867d\u7136\u4e0d\u662f\u4eba\uff0c\u4e5f\u4e0d\u662f\u74f6\u5b50\u3002 \u3000\u3000\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u53c8\u8bf4\u53c8\u7b11\u53c8\u5531\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u8ba9\u4ed6\u4ece\u7f51\u5b50\u91cc\u8131\u8eab\u3002 \u3000\u3000\u5982\u679c\u5929\u4e0b\u53ea\u6709\u4e24\u4e2a\u4eba\u80fd\u4ece\u8fd9\u9762\u94f6\u7f51\u4e2d\u8131\u8eab\uff0c\u4ed6\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u5982\u679c\u5929\u4e0b\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u4ece\u8fd9\u9762\u94f6\u7f51\u4e2d\u8131\u8eab\uff0c\u4ed6\u5c31\u662f\u552f\u4e00\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u4ed6\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u662f\u900f\u660e\u7684\uff0c\u800c\u4e14\u597d\u50cf\u8fde\u4e00\u6839\u9aa8\u5934\u90fd\u6709\u3002 \u3000\u3000\u68b3\u8fab\u5b50\u7684\u5c0f\u59d1\u5a18\u7b11\u5f97\u66f4\u751c\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u603b\u8be5\u77e5\u9053\u8c01\u662f\u2018\u9634\u7075\u2019\u4e86\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u5df2\u7ecf\u8fdf\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u8fdf\u4e86\u4e00\u70b9\u3002\u201d\u80e1\u5927\u638c\u67dc\u53c8\u63a0\u4e0a\u67af\u6811\uff0c\u201c\u5e78\u597d\u8fd8\u4e0d\u592a\u8fdf\u3002\u53ea\u8981\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5c31\u4e0d\u7b97\u592a\u8fdf\uff01\u5c31\u7b97\u6211\u8981\u6b7b\uff0c\u4f60\u4eec\u4e5f\u5f97\u966a\u7740\u6211\u53bb\uff01\u201d \u3000\u3000\u4ed6\u7684\u4e00\u53cc\u624b\u5df2\u5982\u51e4\u51f0\u7684\u53cc\u7fc5\u822c\u5c55\u8d77\uff1a\u201c\u5c31\u7b97\u6211\u8981\u4e0b\u5730\u72f1\uff0c\u4f60\u4eec\u4e5f\u5f97\u966a\u6211\u53bb\uff01\u201d \u3000\u3000\u5c31\u597d\u50cf\u201c\u98de\u4e91\u4e94\u82b1\u9526\u201d\u3001\u201c\u5b54\u96c0\u7fce\u201d\u3001\u201c\u5929\u7edd\u5730\u706d\u4eba\u4ea1\uff0c\u65e0\u60c5\u593a\u547d\u4e09\u624d\u9489\u201d\u8fd9\u4e9b\u5728\u4f20\u8bf4\u4e2d\u5df2\u8ff9\u8fd1\u795e\u5947\u7684\u6697\u5668\u4e00\u6837\uff0c\u6c5f\u6e56\u4e2d\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4e09\u5b9d\u638c\u7684\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\u7a76\u7adf\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u6697\u5668\uff0c\u7a76\u7adf\u662f\u7528\u4ec0\u4e48\u624b\u6cd5\u6253\u51fa\u6765\u7684\uff0c\u6709\u591a\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u56e0\u4e3a\u770b\u8fc7\u8fd9\u79cd\u6697\u5668\u5a01\u529b\u7684\u4eba\uff0c\u901a\u5e38\u90fd\u5df2\u6b7b\u5728\u8fd9\u79cd\u6697\u5668\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u4e5f\u6ca1\u4eba\u80fd\u6000\u7591\u80e1\u5927\u638c\u67dc\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u8bf4\u4ed6\u8981\u4ed6\u4eec\u966a\u4ed6\u4e0b\u5730\u72f1\u65f6\uff0c\u4ed6\u7684\u610f\u601d\u5c31\u771f\u662f\u8981\u4ed6\u4eec\u966a\u4ed6\u4e0b\u5730\u72f1\uff01 \u3000\u3000\u4ed6\u5bf9\u4ed6\u81ea\u5df1\u548c\u4ed6\u7684\u6697\u5668\u90fd\u7edd\u5bf9\u6709\u4fe1\u5fc3\uff0c\u7edd\u5bf9\u6709\u628a\u63e1\u3002 \u3000\u3000\u4ed6\u7684\u53cc\u81c2\u5c55\u8d77\uff0c\u59ff\u52bf\u5947\u79d8\u800c\u602a\u5f02\u3002 \u3000\u3000\u6c34\u6676\u4eba\u90a3\u5f20\u672c\u6765\u5b8c\u5168\u900f\u660e\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u6cdb\u8d77\u4e86\u4e00\u5c42\u6697\u7d2b\u8272\u7684\u70df\u96fe\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u8138\u4e0a\u7684\u7b11\u5bb9\u4e5f\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u53ea\u8981\u6709\u4e00\u4e2a\u4eba\u51fa\u624b\uff0c\u4e09\u4e2a\u4eba\u90fd\u8981\u540c\u4e0b\u5730\u72f1\u2014\u2014\u53ea\u6709\u4e0b\u5730\u72f1\uff0c\u7edd\u65e0\u522b\u5904\u53ef\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u6bd4\u8f83\u5927\u7684\u4e00\u5ea7\u6c99\u4e18\u540e\u5ffd\u7136\u4f20\u6765\u4e86\u4e00\u9635\u60a0\u626c\u7684\u7b1b\u58f0\u3002 \u3000\u3000\u7b1b\u58f0\u67d4\u7f8e\u60a0\u626c\uff0c\u66f2\u8c03\u7f20\u7ef5\u6392\u607b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u5439\u6563\u4e86\u4eba\u4eec\u5fc3\u91cc\u7684\u6740\u673a\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u968f\u7740\u7b1b\u58f0\u4ece\u6c99\u4e18\u4e4b\u540e\u8f6c\u51fa\u6765\uff0c\u4e24\u4e2a\u5c0f\u5c0f\u7684\u4eba\u3002 \u3000\u3000\u4e00\uff0c\u4e2a\u5c0f\u5c0f\u5c0f\u5c0f\u7684\u5c0f\u8001\u5934\uff0c\u7275\u7740\u5339\u9752\u9aa1\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u5c0f\u5c0f\u7684\u5c0f\u8001\u592a\u592a\uff0c\u6a2a\u5750\u5728\u9aa1\u80cc\u4e0a\u5439\u7b1b\uff0c\u5c0f\u5c0f\u7684\u8138\uff0c\u5c0f\u5c0f\u7684\u9f3b\u5b50\uff0c\u5c0f\u5c0f\u7684\u5634\uff0c\u5c0f\u5c0f\u7684\u4e00\uff1a\u5f88\u767d\u7389\u7b1b\u3002 \u3000\u3000\u5c0f\u65b9\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u8fd9\u4e48\u5c0f\u7684\u4eba\uff0c\u65e0\u8bba\u4ec0\u4e48\u5730\u65b9\u201c\u90fd\u8981\u6bd4\u5e73\u5e38\u4eba\u5c0f\u4e00\u534a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u7684\u8eab\u6750\u5374\u5f88\u5300\u79f0\uff0c\u7edd\u6ca1\u6709\u4e00\u70b9\u7578\u5f62\u4e11\u964b\u7684\u6837\u5b50\u3002 \u3000\u3000\u5c0f\u8001\u5934\u5934\u53d1\u82b1\u767d\uff0c\u9762\u8c8c\u6148\u7965\uff0c\u5c0f\u8001\u592a\u592a\u7709\u6e05\u76ee\u79c0\uff0c\u6e29\u67d4\u5a34\u9759\uff0c\u62ff\u7740\u7b1b\u5b50\u7684\u4e00\u53cc\u624b\uff0c\u5c31\u597d\u50cf\u5979\u624b\u91cc\u767d\u7389\u7b1b\u4e00\u6837\u6676\u5b9d\u6da6\u5706\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9\u4e24\u4e2a\u4eba\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\uff0c\u914d\u5f97\u771f\u662f\u597d\u6781\u4e86\u3002 \u3000\u3000\u80e1\u5927\u638c\u67dc\u6ca1\u6709\u51fa\u624b\uff0c\u201c\u9634\u7075\u201d\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u542c\u89c1\u4e86\u8fd9\u6837\u7684\u7b1b\u58f0\uff0c\u770b\u89c1\u4e86\u8fd9\u4e48\u6837\u4e24\u4e2a\u4eba\uff0c\u90fd\u6ca1\u6cd5\u5b50\u518d\u4e0b\u6bd2\u624b\u7684\u3002 \u3000\u3000\u9634\u5927\u5c0f\u59d0\u8138\u4e0a\u53c8\u9732\u51fa\u82b1\u4e00\u822c\u7684\u7b11\u989c\u3002 \u3000\u3000\u201c\u8001\u5148\u751f\uff0c\u8001\u592a\u592a\uff0c\u4f60\u4eec\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u8981\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff1f\u201d \u3000\u3000\u770b\u89c1\u8fd9\u4e48\u6837\u4e00\u4e2a\u53ef\u7231\u7684\u59d1\u5a18\uff0c\u5c0f\u8001\u5934\u8138\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u5fae\u7b11\u3002 \u3000\u3000\u201c\u6211\u4eec\u5c31\u662f\u4ece\u4f60\u4eec\u6765\u7684\u5730\u65b9\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u4f46\u662f\u6211\u4eec\u5374\u4e0d\u60f3\u5230\u4f60\u4eec\u53bb\u7684\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u6148\u7965\u548c\u853c\uff0c\u8bf4\u8bdd\u8f7b\u8a00\u8f6f\u8bed\uff1a\u201c\u5929\u4e0b\u8fd9\u4e48\u5927\uff0c\u6709\u8fd9\u4e48\u591a\u597d\u73a9\u7684\u5730\u65b9\u53ef\u4ee5\u53bb\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u4e0b\u5730\u72f1\uff1f\u201d \u3000\u3000\u7b1b\u58f0\u66f4\u6e29\u67d4\u7f20\u7ef5\uff0c\u6c34\u6676\u4eba\u8138\u4e0a\u7684\u70df\u96fe\u5df2\u6d88\u6563\u3002 \u3000\u3000\u80e1\u5927\u638c\u67dc\u5ffd\u7136\u63a0\u4e0b\u6811\u68a2\uff0c\u606d\u606d\u656c\u656c\u5730\u5411\u8fd9\u4e2a\u5c0f\u8001\u5934\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u5c0f\u8001\u5934\u4eff\u4f5b\u5f88\u60ca\u5f02\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u660f\u5eb8\u8001\u673d\u7684\u8001\u5934\u5b50\u800c\u5df2\uff0c\u9601\u4e0b\u4e3a\u4f55\u5982\u6b64\u591a\u793c\uff1f\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u7684\u8138\u8272\u5374\u66f4\u606d\u656c\uff1a\u201c\u770b\u89c1\u98ce\u8001\u524d\u8f88\uff0c\u8c01\u6562\u65e0\u793c\uff1f\u201d \u3000\u3000\u9634\u5927\u5c0f\u59d0\u7684\u773c\u775b\u5ffd\u7136\u4eae\u4e86\uff0c\u5403\u60ca\u5730\u770b\u7740\u8fd9\u5c0f\u8001\u5934\uff1a\u201c\u98ce\u8001\u524d\u8f88\uff1f\u201d\u5979\u7684\u58f0\u97f3\u4e5f\u663e\u5f97\u5f88\u60ca\u8bb6\uff0c\u201c\u4f60\u5c31\u662f\u90a3\u2018\u5343\u91cc\u98de\u4e91\u3001\u4e07\u91cc\u6349\u6708\u3001\u795e\u884c\u65e0\u5f71\u8ffd\u98ce\u671b\u2019\u98ce\u8001\u7237\u5b50\uff1f\u201d \u3000\u3000\u5c0f\u8001\u5934\u5fae\u7b11\u70b9\u5934\u3002 \u3000\u3000\u9634\u5927\u5c0f\u59d0\u770b\u7740\u9a74\u80cc\u4e0a\u7684\u5c0f\u8001\u592a\u592a\uff1a\u201c\u98ce\u53df\u6708\u5a46\uff0c\u5f62\u5f71\u4e0d\u79bb\uff0c\u8fd9\u4f4d\u5f53\u7136\u5c31\u662f\u6708\u5a46\u5a46\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u7b11\u5bb9\u66f4\u6148\u7965\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c31\u5df2\u6709\u4e86\u8fd9\u6837\u7684\u89c1\u8bc6\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u5e72\u54b3\u4e24\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u98ce\u8001\u524d\u8f88\u4e0d\u5728\u4f34\u6708\u5c71\u5e84\u7eb3\u798f\uff0c\u5230\u8fd9\u79cd\u7a77\u8352\u4e4b\u5730\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u671b\u770b\u7740\u4ed6\u76f4\u7b11\uff1a\u201c\u80e1\u5927\u638c\u67dc\u4e0d\u5728\u4e09\u5b9d\u5802\u7eb3\u798f\uff0c\u5374\u6765\u5230\u8fd9\u79cd\u7a77\u8352\u4e4b\u5730\u4e3a\u7684\u53c8\u662f\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u5176\u5b9e\u80e1\u5927\u638c\u67dc\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u201d\u80e1\u5927\u638c\u67dc\u4eff\u4f5b\u5403\u4e00\u60ca\uff1a\u201c\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u672c\u6765\u5c31\u662f\u4e3a\u4e86\u540c\u4e00\xb7\u4ef6\u4e8b\u800c\u6765\u7684\uff0c\u6211\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u66f4\u5403\u60ca\uff0c\u6545\u610f\u95ee\uff1a\u201c\u98ce\u8001\u524d\u8f88\u8bf4\u7684\u662f\u54ea\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u6162\u6162\u5730\u4ece\u8eab\u4e0a\u62ff\u51fa\u4e86\u4e00\u53ea\u624b\u3002 \u3000\u3000\u4e00\u53ea\u91d1\u5149\u707f\u70c2\u7684\u201c\u91d1\u624b\u201d\uff01 \u3000\u3000\u201c\u65e2\u7136\u5927\u5bb6\u90fd\u662f\u4e3a\u6b64\u800c\u6765\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u4e00\u8d77\u8d70\u4e0b\u5730\u72f1\uff1f\u201d\u8ffd\u98ce\u53df\u7b11\u7b11\u9053\uff0c\u201c\u65e2\u7136\u6211\u4eec\u90fd\u5df2\u6765\u4e86\uff0c\u5e94\u8be5\u4e0b\u5730\u72f1\u7684\u5c31\u662f\u522b\u4eba\u4e86\u3002\u201d \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u5df2\u7ecf\u6765\u4e86\uff0c\u5e94\u8be5\u4e0b\u5730\u72f1\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u60a0\u626c\u7684\u7b1b\u58f0\u8fdc\u53bb\uff0c\u4eba\u4e5f\u5df2\u8fdc\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u4e3a\u4e86\u201c\u91d1\u624b\u201d\u800c\u6765\u7684\u3002 \u3000\u3000\u5728\u201c\u91d1\u624b\u201d\u7684\u53f7\u4ee4\u4e0b\uff0c\u7edd\u4e0d\u5bb9\u8bb8\u79c1\u4eba\u7684\u6069\u6028\u8fc7\u8282\u5b58\u5728\uff0c\u4e0d\u7ba1\u4f60\u662f\u201c\u9634\u7075\u201d\u4e5f\u597d\uff0c\u662f\u80e1\u5927\u638c\u67dc\u4e5f\u597d\uff0c\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u90fd\u4e00\u6837\u3002 \u3000\u3000\u201c\u91d1\u624b\u201d\u4e00\u73b0\uff0c\u5c31\u5df2\u6709\u8fd9\u4e48\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7ffb\u8eab\u8dc3\u8d77\uff0c\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u773c\u8272\u76ef\u7740\u5c0f\u65b9\uff0c\u5ffd\u7136\u8bf4\u51fa\u53e5\u5f88\u5947\u602a\u7684\u8bdd\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u535c\u9e70\u4e3a\u4ec0\u4e48\u80af\u8ba9\u4f60\u8d70\u4e86\u3002\u201d\u4ed6\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u4f60\u8d70\u5427\uff0c\u5feb\u8d70\uff01\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u61c2\uff0c\u6b63\u60f3\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u8bf4\uff0c\u662f\u4ec0\u4e48\u610f\u601d\u3002 \u3000\u3000\u53ef\u662f\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u73ed\u5bdf\u5df4\u90a3\u4e5f\u8d70\u4e86\uff0c\u5c31\u50cf\u662f\u4e00\u9635\u98ce\u4e00\u6837\u98d8\u7136\u8fdc\u53bb\u3002 \u3000\u3000\u4ed6\u8981\u8d70\u7684\u65f6\u5019\uff0c\u4ece\u6765\u90fd\u6ca1\u6709\u4eba\u80fd\u7559\u5f97\u4f4f\u4ed6\u3002 \u3000\u3000\u660f\u6697\u7684\u6cb9\u706f\uff0c\u6df7\u6d4a\u7684\u9762\u6c64\uff0c\u6c64\u91cc\u6709\u6c99\u5b50\uff0c\u9762\u91cc\u4e5f\u6709\u6c99\u5b50\uff0c\u5403\u4e00\u53e3\u5c31\u6709\u4e00\u5634\u6c99\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u603b\u7b97\u6765\u5230\u4e00\u4e2a\u6709\u4eba\u70df\u7684\u5730\u65b9\uff0c\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u90fd\u628a\u8fd9\u7897\u9762\u5403\u5149\u4e86\uff0c\u8fde\u9762\u6c64\u90fd\u559d\u5149\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u8fb9\u9672\u4e0a\u7684\u7a77\u4e61\u50fb\u9547\u91cc\uff0c\u770b\u5230\u90a3\u4e9b\u8863\u4e0d\u853d\u4f53\u3001\u6ee1\u8857\u4e89\u62fe\u9a6c\u7caa\u4fbf\u7684\u5b69\u5b50\uff0c\u8c01\u90fd\u4e0d\u6562\u518d\u66b4\u8bca\u5929\u7269\u4e86\u3002 \u3000\u3000\u5403\u5b8c\u4e86\u8fd9\u7897\u9762\uff0c\u4ed6\u4eec\u5c31\u9759\u9759\u5730\u5750\u5728\u660f\u706f\u4e0b\uff0c\u5fc3\u91cc\u4eff\u4f5b\u6709\u5f88\u591a\u8bdd\u8981\u8bf4\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u5e94\u8be5\u4ece\u4f55\u5904\u8bf4\u8d77\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5c0f\u65b9\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u8bf4\u8fc7\u8ffd\u98ce\u53df\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u542c\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u201c\u9633\u5149\u201d\u8bf4\uff0c\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u4ed6\u5c31\u5df2\u53f7\u79f0\u2018\u8f7b\u529f\u5929\u4e0b\u7b2c\u4e00\u2019\u3002\u8fd9\u4e8c\u5341\u5e74\u6765\u6c5f\u6e56\u4e2d\u867d\u7136\u4eba\u624d\u8f88\u51fa\uff0c\u80fd\u8d85\u8fc7\u4ed6\u7684\u4eba\u8fd8\u662f\u4e0d\u591a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\u624d\u5f00\u53e3\uff1a\u201c\u6211\u5728\u6c5f\u6e56\u7684\u65f6\u5019\uff0c\u6709\u4e2a\u5e74\u7eaa\u6bd4\u6211\u5927\u5f88\u591a\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u7684\u6b66\u529f\u867d\u7136\u4e0d\u592a\u9ad8\uff0c\u53ef\u662f\u6c5f\u6e56\u4e2d\u7684\u4e8b\u8c01\u4e5f\u6ca1\u6709\u4ed6\u77e5\u9053\u7684\u591a\u3002\u201d \u3000\u3000\u9633\u5149\u542c\u7740\uff0c\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u9053\uff1a\u201c\u4ed6\u66fe\u7ecf\u628a\u5f53\u4ee3\u6b66\u6797\u4e2d\u6700\u53ef\u6015\u7684\u51e0\u4e2a\u4eba\u7684\u540d\u5b57\u90fd\u544a\u8bc9\u8fc7\u6211\u3002\u201d \u3000\u3000\u201c\u5176\u4e2d\u5c31\u6709\u4e00\u4e2a\u8ffd\u98ce\u81fe\uff1f\u201d\u201d \u3000\u3000\u201c\u6709\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u6709\u8ffd\u98ce\u81fe\uff0c\u4e5f\u6709\u80e1\u5927\u638c\u67dc\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u63d0\u8d77\u201c\u9634\u7075\u201d\uff0c\u5728\u5927\u591a\u6570\u6c5f\u6e56\u4eba\u7684\u5fc3\u76ee\u4e2d\uff0c\u201c\u9634\u7075\u201d\u6839\u672c\u4e0d\u7b97\u662f\u4e00\u4e2a\u4eba\uff0c\u56e0\u4e3a\u8c01\u4e5f\u4e0d\u80fd\u786e\u5b9e\u4ed6\u662f\u5426\u771f\u7684\u5b58\u5728\u3002 \u3000\u3000\u201c\u73b0\u5728\u4ed6\u4eec\u90fd\u6765\u4e86\uff0c\u90fd\u662f\u4e3a\u4e86\u2018\u91d1\u624b\u2019\u800c\u6765\u7684\u3002\u201d\u5c0f\u65b9\u63a5\u7740\u95ee\u9053\uff1a\u201c\u2018\u91d1\u624b\uff0c\u8981\u4ed6\u4eec\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u542c\u73ed\u5bdf\u5df4\u90a3\u8bf4\u8fc7\uff0c\u201c\u91d1\u624b\u201d\u5c31\u662f\u5bcc\u8d35\u795e\u4ed9\u5415\u4e09\u5efa\u7acb\u7684\u4e00\u4e2a\u79d8\u5bc6\u7ec4\u7ec7\uff0c\u76ee\u7684\u662f\u8981\u5728\u85cf\u4eba\u95f4\u9020\u6210\u6df7\u4e71\uff0c\u593a\u53d6\u6743\u529b\u3002 \u3000\u3000\u5931\u91d1\u88ab\u6740\u7684\u94c1\u7ffc\uff0c\u5bfb\u91d1\u65ad\u81c2\u7684\u536b\u5929\u9e4f\uff0c\u8ffd\u6740\u5c0f\u65b9\u7684\u52fe\u9b42\u624b\uff0c\u88ab\u540a\u6b7b\u5728\u6811\u4e0a\u7684\u67f3\u5206\u5206\uff0c\u90fd\u662f\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\u7684\u4eba\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u5df2\u5c06\u7ec4\u7ec7\u4e2d\u7684\u9876\u5c16\u9ad8\u624b\u90fd\u8c03\u96c6\u5230\u8fd9\u91cc\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u662f\u6765\u5e72\u4ec0\u4e48\u7684\uff1f\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u90fd\u5e94\u8be5\u80fd\u60f3\u5f97\u5230\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u7740\u9762\u524d\u7684\u7a7a\u7897\uff0c\u5c31\u597d\u50cf\u8fd9\u4e2a\u7c97\u74f7\u7834\u7897\u91cc\uff0c\u4f1a\u5ffd\u7136\u8dc3\u51fa\u4e2a\u7cbe\u7075\u6765\u89e3\u51b3\u4ed6\u7684\u96be\u9898\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u5f88\u4e45\u5f88\u4e45\u624d\u8bf4\uff1a\u201c\u4ed6\u4eec\u4e5f\u4e0d\u4e00\u5b9a\u662f\u6765\u627e\u535c\u9e70\u7684\u3002\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u4ed6\u4eec\u662f\u6765\u627e\u4ed6\u7684\uff0c\u4ed6\u4e5f\u6709\u6ca1\u5b50\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u624b\u4e0b\u9ad8\u624b\u6218\u58eb\u5982\u4e91\uff0c\u4ed6\u81ea\u5df1\u66f4\u662f\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u5982\u679c\u8fde\u4ed6\u90fd\u4e0d\u80fd\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u522b\u4eba\u53bb\u4e5f\u6ca1\u6709\u7528\uff0c\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u4e9b\u4e8b\u53cd\u6b63\u90fd\u5df2\u7ecf\u8ddf\u6211\u5b8c\u5168\u6ca1\u5173\u7cfb\u4e86\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u53cd\u6b63\u6211\u5df2\u7ecf\u5b8c\u5168\u8131\u79bb\u4e86\u4ed6\u4eec\u3002\u518d\u8fc7\u4e00\u4e2a\u591a\u6708\uff0c\u6211\u5c31\u53ef\u4ee5\u56de\u5230\u6c5f\u5357\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u8fd9\u4e9b\u8bdd\u5c31\u597d\u50cf\u662f\u8bf4\u7ed9\u81ea\u5df1\u542c\u7684\uff1a\u201c\u4f60\u6ca1\u6709\u5230\u8fc7\u6c5f\u5357\uff0c\u6240\u4ee5\u4f60\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u60f3\u5230\u6c5f\u5357\u662f\u4e2a\u591a\u4e48\u7f8e\u4e3d\u7684\u5730\u65b9\uff0c\u90a3\u4e9b\u6865\uff0c\u90a3\u4e9b\u6c34\uff0c\u90a3\u4e9b\u8239\uff0c\u90a3\u4e9b\u6570\u4e0d\u5c3d\u7684\u9752\u5c71\u2026\u2026\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u9759\u9759\u5730\u770b\u7740\u4ed6\uff0c\u4e0d\u7ba1\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u5979\u90fd\u5e94\u58f0\u9644\u548c\u3002 \u3000\u3000\u53ef\u662f\u8bf4\u5230\u8fd9\u91cc\uff0c\u5c0f\u65b9\u5ffd\u7136\u6253\u65ad\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u8981\u559d\u9152\u3002\u201d \u3000\u3000\u4ed6\u559d\u4e86\u5f88\u591a\u9152\u3002 \u3000\u3000\u53c8\u51f6\u53c8\u8fa3\u7684\u571f\u57ce\u70e7\uff0c\u559d\u5230\u809a\u5b50\u91cc\uff0c\u5c31\u50cf\u662f\u4e00\u56e2\u70c8\u706b\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u535c\u9e70\u66fe\u7ecf\u966a\u4ed6\u559d\u8fc7\u8fd9\u79cd\u9152\uff0c\u559d\u8fc7\u5f88\u591a\u6b21\uff0c\u6bcf\u4e00\u6b21\u9152\u540e\u5fae\u9189\u65f6\uff0c\u535c\u9e70\u5c31\u4f1a\u4f4e\u5531\u90a3\u9996\u60b2\u6b4c\uff0c\u90a3\u79cd\u82cd\u51c9\u7684\u610f\u5883\uff0c\u90a3\u79cd\u7537\u513f\u7684\u60c5\u6000\uff0c\u4f7f\u4eba\u6c38\u8fdc\u90fd\u5fd8\u4e0d\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u5916\u8868\u6bd4\u94c1\u77f3\u8fd8\u51b7\u9177\u7684\u4eba\uff0c\u5fc3\u91cc\u7a76\u7adf\u85cf\u6709\u591a\u6df1\u7684\u75db\u82e6\uff1f \u3000\u3000\u5c0f\u65b9\u4e00\u7897\u53c8\u4e00\u7897\u5730\u559d\u7740\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4e5f\u5f00\u59cb\u51fb\u638c\u4f4e\u5531\uff1a \u3000\u3000\u513f\u987b\u6210\u540d\uff0c \u3000\u3000\u9152\u987b\u9189\uff0c\u9152\u987b\u9189\u2026\u2026 \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u5531\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5df2\u5636\u54d1\uff0c\u773c\u775b\u5df2\u53d1\u7ea2\uff0c\u5ffd\u7136\u7528\u529b\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u58f0\u8bf4\uff1a\u201c\u6211\u4eec\u56de\u53bb\uff01\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u8fd8\u662f\u5f88\u5b89\u7965\u5730\u770b\u7740\u4ed6\u3002 \u3000\u3000\u201c\u56de\u53bb\uff1f\u201d\u5979\u95ee\u5c0f\u65b9\uff0c\u201c\u4f60\u8bf4\u56de\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u56de\u62c9\u8428\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u5df2\u7ecf\u8d70\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8981\u56de\u5230\u90a3\u91cc\u53bb\uff1f\u201d\u201c\u9633\u5149\u201d\u6de1\u6de1\u5730\u95ee\uff0c\u201c\u96be\u9053\u4f60\u5df2\u7ecf\u5fd8\u4e86\uff0c\u518d\u8fc7\u4e00\u4e2a\u6708\uff0c\u4f60\u5c31\u53ef\u4ee5\u56de\u5230\u6c5f\u5357\u4e86\uff0c\u90a3\u662f\u4f60\u7684\u6545\u4e61\uff0c\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u7684\u68a6\uff0c\u5168\u90fd\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u5979\u51b7\u51b7\u5730\u76ef\u7740\u5c0f\u65b9\uff0c\u53c8\u95ee\u4e00\u904d\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u8981\u56de\u5230\u62c9\u8428\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u62ac\u8d77\u5934\uff0c\u72e0\u72e0\u5730\u76ef\u7740\u5979\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u6211\u662f\u4e3a\u4e86\u4ec0\u4e48\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u95ee\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u7684\u773c\u775b\u5c31\u50cf\u662f\u6625\u96ea\u822c\u6eb6\u5316\u4e86\uff0c\u5316\u4e3a\u4e86\u6625\u6c34\uff0c\u6bd4\u6625\u6c34\u66f4\u6e29\u67d4\u3002 \u3000\u3000\u201c\u6211\u5f53\u7136\u77e5\u9053\u4f60\u4e3a\u7684\u662f\u4ec0\u4e48\u3002\u201d\u5979\u5e7d\u5e7d\u4ed6\u8bf4\uff0c\u201c\u4f60\u8ddf\u6211\u4e00\u6837\uff0c\u90fd\u77e5\u9053\u90a3\u4e9b\u4eba\u662f\u6765\u5e72\u4ec0\u4e48\u7684\uff0c\u4f60\u4e5f\u8ddf\u6211\u4e00\u6837\uff0c\u90fd\u4e0d\u80fd\u5fd8\u8bb0\u535c\u9e70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u4e0d\u80fd\u518d\u5426\u8ba4\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u80fd\u5fd8\u8bb0\u73ed\u5bdf\u5df4\u90a3\u8bf4\u7684\u90a3\u53e5\u8bdd\u3002 \u3000\u3000\u2014\u2014\u73b0\u5728\u6211\u624d\u660e\u767d\u535c\u9e70\u4e3a\u4ec0\u4e48\u80af\u8ba9\u4f60\u8d70\u4e86\u3002 \u3000\u3000\u535c\u9e70\u5f88\u53ef\u80fd\u5df2\u7ecf\u6709\u9884\u611f\uff0c\u5df2\u77e5\u9053\u6709\u5f3a\u654c\u5c06\u6765\uff0c\u6240\u4ee5\u4e0d\u4f46\u8ba9\u4ed6\u8d70\uff0c\u800c\u5df2\u8fd8\u8981\u4ed6\u5e26\u7740\u201c\u9633\u5149\u201d\u4e00\u8d77\u8d70\u3002 \u3000\u3000\u4e0d\u7ba1\u4ed6\u81ea\u5df1\u906d\u9047\u5230\u4ec0\u4e48\u4e8b\uff0c\u535c\u9e70\u90fd\u7edd\u4e0d\u80af\u8ba9\u4ed6\u4eec\u53d7\u5230\u8fde\u7d2f\u4f24\u5bb3\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u81ea\u5df1\u4e5f\u8bf4\u8fc7\uff0c\u5982\u679c\u8fde\u535c\u9e70\u90fd\u4e0d\u80fd\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u522b\u4eba\u53bb\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u9633\u5149\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u5b8c\u5168\u8131\u79bb\u4e86\u6211\u4eec\uff0c\u8c01\u4e5f\u4e0d\u80fd\u518d\u52c9\u5f3a\u4f60\u56de\u53bb\u9001\u6b7b\uff0c\u5982\u679c\u4f60\u4e0d\u60f3\u56de\u53bb\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u602a\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u77e5\u9053\u8c01\u90fd\u4e0d\u4f1a\u602a\u6211\u7684\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\xb7\u201c\u53ef\u662f\u6211\u81ea\u5df1\u4e00\u5b9a\u4f1a\u602a\u81ea\u5df1\u3002\u201d \u3000\u3000\u201c\u4f60\u5b81\u613f\u56de\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u63e1\u7d27\u53cc\u62f3\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u4ed6\u8bf4\uff1a\u201c\u5c31\u7b97\u90a3\u91cc\u5df2\u7ecf\u53d8\u6210\u4e2a\u5730\u72f1\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u4e0b\u53bb\uff01\u201d \u3000\u3000\u62c9\u8428\u8fd8\u662f\u62c9\u8428\uff0c\u8fd8\u662f\u8ddf\u4ed6\u4eec\u79bb\u5f00\u7684\u65f6\u5019\u4e00\u6837\uff0c\u5929\u7a7a\u6674\u6717\uff0c\u9633\u5149\u707f\u70c2\u3002 \u3000\u3000\u5e03\u8fbe\u62c9\u5bab\u7684\u5706\u9876\u4f9d\u65e7\u5728\u84dd\u5929\u4e0b\u95ea\u95ea\u53d1\u5149\uff0c\u6240\u6709\u7684\u4e00\u5207\u597d\u50cf\u90fd\u6ca1\u6709\u4e1d\u6beb\u53d8\u5316\u3002 \u3000\u3000\u8fd9\u53e4\u8001\u7684\u5723\u57ce\u5c31\u50cf\u662f\u4ed6\u4eec\u7684\u53cb\u60c5\u4e00\u6837\uff0c\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d8\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u56de\u5230\u4e86\u62c9\u8428\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7684\u7b11\u5bb9\u53c8\u53d8\u5f97\u597d\u50cf\u8fd9\u91cc\u7684\u5929\u6c14\u4e00\u6837\u660e\u6717\uff0c\u5c0f\u65b9\u7684\u8138\u8272\u5374\u66f4\u9634\u6697\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u597d\u50cf\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u90a3\u4e9b\u4eba\u5df2\u7ecf\u6765\u4e86\uff0c\u5df2\u7ecf\u6709\u4e86\u884c\u52a8\uff0c\u8fd9\u91cc\u4e00\u5b9a\u53d8\u5f97\u5f88\u4e71\u4e86\u3002\u201d\u201c\u9633\u5149\u201d\u8bf4\uff0c\u201c\u6bcf\u6b21\u6709\u4e8b\u53d1\u751f\u65f6\uff0c\u535c\u9e70\u90fd\u4f1a\u6d3e\u4eba\u5728\u57ce\u5916\u5de1\u903b\u793a\u8b66\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u66f4\u6109\u5feb\uff1a\u201c\u53ef\u662f\u73b0\u5728\u8fd9\u9644\u8fd1\u8fde\u4e00\u4e2a\u6211\u4eec\u7684\u4eba\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8fd8\u6ca1\u6709\u8fdb\u5165\u62c9\u8428\u5723\u5730\uff0c\u8def\u4e0a\u53ea\u80fd\u770b\u89c1\u4e09\u4e2a\u4eba\uff0c\u90fd\u662f\u6d3b\u4f5b\u7684\u8654\u8bda\u4fe1\u5f92\uff0c\u4e0d\u8fdc\u5343\u91cc\u5230\u8fd9\u91cc\u6765\u671d\u5723\u7684\uff0c\u4e09\u6b65\u4e00\u62dc\uff0c\u4e94\u6b65\u4e00\u53e9\uff0c\u7528\u6700\u8270\u82e6\u7684\u65b9\u6cd5\u6765\u8868\u793a\u4ed6\u4eec\u7684\u8654\u8bda\u548c\u5c0a\u656c\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u7cbe\u795e\u548c\u8089\u4f53\u90fd\u5df2\u8fdb\u5165\u4e00\u79cd\u534a\u865a\u8131\u7684\u72b6\u6001\uff0c\u5bf9\u6240\u6709\u80fd\u591f\u770b\u5f97\u89c1\u7684\u90fd\u89c6\u800c\u4e0d\u89c1\uff0c\u5bf9\u6240\u6709\u80fd\u591f\u542c\u5f97\u89c1\u7684\u90fd\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u7ecf\u5c06\u81ea\u5df1\u5b8c\u5168\u6295\u5165\u4e86\u4e00\u79cd\u542c\u4e0d\u89c1\u4e5f\u770b\u4e0d\u89c1\u7684\u865a\u65e0\u7384\u79d8\u4e2d\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u6539\u53d8\u4e86\u8bdd\u9898\uff1a\u201c\u6709\u4e9b\u4e8b\u4f60\u867d\u7136\u770b\u4e0d\u89c1\u4e5f\u542c\u4e0d\u89c1\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u5426\u5b9a\u5b83\u7684\u5b58\u5728\u3002\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u5e26\u7740\u6df1\u601d\u4e4b\u8272\uff0c\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6709\u65f6\u5b83\u751a\u81f3\u8fdc\u6bd4\u80fd\u591f\u770b\u5f97\u89c1\u4e5f\u542c\u5f97\u89c1\u7684\u66f4\u771f\u5b9e\uff0c\u5b58\u5728\u5f97\u66f4\u4e45\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u65e2\u4e0d\u80fd\u5b8c\u5168\u4e86\u89e3\u4ed6\u7684\u610f\u601d\uff0c\u4e5f\u4e0d\u61c2\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u5ffd\u7136\u8bf4\u51fa\u8fd9\u4e9b\u8bdd\u6765\u3002 \u3000\u3000\u4f46\u662f\u5979\u6ca1\u6709\u95f4\uff0c\u56e0\u4e3a\u5979\u5ffd\u7136\u53d1\u73b0\u6709\u4e9b\u4e8b\u53d8\u4e86\uff0c\u53d8\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u4ed6\u4eec\u51b3\u5b9a\u5148\u5230\u516b\u89d2\u8857\u4e0a\u7684\u201c\u9e70\u8bb0\u201d\u5546\u53f7\u53bb\u770b\u770b\u52a8\u9759\uff0c\u518d\u56de\u53bb\u770b\u535c\u9e70\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u6ca1\u6709\u7ecf\u5e03\u8fbe\u62c9\u5bab\u65c1\u8fb9\u7684\u90a3\u6761\u8857\u9053\u8d70\uff0c\u76f4\u63a5\u5c31\u4ece\u5927\u8def\u8fdb\u5165\u5e02\u533a\u3002 \u3000\u3000\u8857\u9053\u4e0a\u7684\u884c\u4eba\u5df2\u6e10\u6e10\u591a\u4e86\uff0c\u6709\u5f88\u591a\u4eba\u90fd\u8ba4\u5f97\u51fa\u201c\u9633\u5149\u201d\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u5979\u751f\u957f\u7684\u5730\u65b9\uff0c\u5979\u4ece\u5c0f\u5c31\u662f\u4e2a\u660e\u6717\u70ed\u60c5\u6177\u6168\u7684\u4eba\uff0c\u4ece\u5c0f\u5c31\u975e\u5e38\u8ba8\u4eba\u6b22\u559c\u3001\u53d7\u4eba\u6b22\u8fce\uff0c\u5c24\u5176\u662f\u90a3\u4e9b\u530d\u5310\u5728\u6ce5\u571f\u4e2d\u6c42\u4e5e\u7684\u4e5e\u4e10\u4eec\uff0c\u6bcf\u6b21\u770b\u5979\uff0c\u90fd\u4f1a\u50cf\u82cd\u8747\u770b\u89c1\u871c\u7cd6\u4e00\u6837\u6d8c\u8fc7\u6765\u3002 \u3000\u3000\u53ef\u662f\u4eca\u5929\u4ed6\u4eec\u4e00\u770b\u89c1\u5979\u5c31\u8fdc\u8fdc\u5730\u907f\u5f00\u4e86\uff0c\u597d\u50cf\u8fde\u770b\u90fd\u4e0d\u6562\u770b\u5979\u4e00\u773c\uff0c\u5c31\u7b97\u6709\u4e9b\u4eba\u5077\u5077\u5730\u5728\u770b\u5979\uff0c\u773c\u775b\u91cc\u7684\u8868\u60c5\u4e5f\u5f88\u66a7\u6627\u8be1\u79d8\uff0c\u751a\u81f3\u663e\u5f97\u5f88\u5bb3\u6015\uff0c\u5c31\u597d\u50cf\u751f\u6015\u5979\u4f1a\u4e3a\u4ed6\u4eec\u5e26\u6765\u4ec0\u4e48\u761f\u75ab\u707e\u7978\u4e00\u6837\u3002 \u3000\u3000\u5979\u81ea\u5df1\u77e5\u9053\u5979\u8fd8\u662f\u4ee5\u524d\u90a3\u4e2a\u4eba\uff0c\u8fde\u4e00\u70b9\u90fd\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u600e\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4eec\u90fd\u77e5\u9053\u5c0f\u65b9\u5df2\u7ecf\u4e0d\u518d\u662f\u201c\u9e70\u8bb0\u201d\u7684\u4eba\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u535c\u9e70\u5df2\u7ecf\u8b66\u544a\u8fc7\u4ed6\u4eec\uff0c\u4e0d\u8bb8\u4ed6\u4eec\u518d\u8ddf\u5c0f\u65b9\u63a5\u8fd1\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u90fd\u53ea\u6709\u7b49\u4ed6\u4eec\u5230\u4e86\u201c\u9e70\u8bb0\u201d\u4e4b\u540e\u624d\u80fd\u5f97\u5230\u89e3\u7b54\u3002 \u3000\u3000\u4ed6\u4eec\u7275\u7740\u9a6c\uff0c\u5f88\u5feb\u5730\u8d70\u8fc7\u62e5\u6ee1\u4eba\u7fa4\u3001\u5806\u6ee1\u8d27\u7269\u7684\u8857\u9053\uff0c\u7ec8\u4e8e\u770b\u89c1\u4e86\u201c\u9e70\u8bb0\u201d\u7684\u91d1\u5b57\u62db\u724c\u3002 \u3000\u3000\u201c\u9e70\u8bb0\u201d\u7684\u62db\u724c\u4e5f\u8fd8\u662f\u548c\u4ee5\u524d\u4e00\u6837\u5728\u592a\u9633\u4e0b\u95ea\u95ea\u53d1\u5149\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u603b\u7b97\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u6731\u4e91\u770b\u89c1\u4f60\u7684\u65f6\u5019\uff0c\u6837\u5b50\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u70b9\u602a\u602a\u7684\u3002\u201d\u5979\u529d\u5c0f\u65b9\uff0c\u201c\u4f60\u4e0d\u8981\u7406\u4ed6\u5c31\u597d\u4e86\uff0c\u4e0d\u7ba1\u4ed6\u600e\u4e48\u6837\u5bf9\u4f60\uff0c\u4f60\u6700\u597d\u90fd\u5047\u88c5\u6ca1\u770b\u89c1\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6839\u672c\u5c31\u4e0d\u66fe\u201c\u5047\u88c5\u201d\u6ca1\u770b\u89c1\uff0c\u5e73\u65f6\u7ec8\u65e5\u90fd\u7559\u5b88\u5728\u201c\u9e70\u8bb0\u201d\u7684\u6731\u4e91\uff0c\u4eca\u5929\u5c45\u7136\u4e0d\u5728\uff0c\u90a3\u4e9b\u5df2\u7ecf\u4e3a\u201c\u9e70\u8bb0\u201d\u670d\u52a1\u591a\u5e74\u7684\u4f19\u8ba1\u4e5f\u4e0d\u5728\u3002 \u3000\u3000\u201c\u9e70\u8bb0\u201d\u7684\u62db\u724c\u5e97\u9762\u867d\u7136\u5168\u90fd\u6ca1\u6709\u53d8\uff0c\u53ef\u662f\u91cc\u9762\u7684\u4f19\u8ba1\u5374\u5df2\u5168\u90e8\u6362\u4e86\uff0c\u201c\u9633\u5149\u201d\u5c45\u7136\u8fde\u4e00\u4e2a\u8ba4\u5f97\u7684\u4eba\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u4eec\u5c45\u7136\u4e5f\u4e0d\u8ba4\u5f97\u201c\u9633\u5149\u201d\uff0c\u5c45\u7136\u628a\u5979\u5f53\u505a\u4e86\u4e3b\u987e\u3002\u4e24\u4e2a\u4f19\u8ba1\u540c\u65f6\u8fce\u4e0a\u6765\uff0c\u5148\u540e\u7528\u6c49\u8bed\u548c\u85cf\u8bed\u95ee\u5979\u548c\u5c0f\u65b9\u8981\u4e70\u4ec0\u4e48\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u89c9\u5f97\u5f88\u7edd\u3002 \u3000\u3000\u8fd9\u4e9b\u65b0\u6765\u7684\u4f19\u8ba1\u5c31\u7b97\u4e0d\u8ba4\u5f97\u5979\uff0c\u4e5f\u5e94\u8be5\u77e5\u9053\u201c\u9e70\u8bb0\u201d\u5546\u53f7\u91cc\u6709\u5979\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u5c31\u50cf\u662f\u201c\u84dd\u8272\u9633\u5149\u201d\u4e00\u6837\u7684\u4eba\u3002 \u3000\u3000\u201c\u6211\u4ec0\u4e48\u90fd\u4e0d\u4e70\u3002\u201d\u9633\u5149\u8bf4\uff1a\u201c\u6211\u662f\u6765\u627e\u4eba\u7684\u3002\u201d \u3000\u3000\u201c\u627e\u54ea\u4f4d\uff1f\u201d\u8bf4\u6c49\u8bed\u7684\u4f19\u8ba1\u8138\u5706\u5934\u5c16\uff0c\u957f\u5f97\u5f88\u6ed1\u7a3d\uff0c\u8bf4\u7684\u662f\u6781\u9053\u5730\u7684\u4eac\u7247\u5b50\u3002 \u3000\u3000\u201c\u6211\u627e\u6731\u4e91\u3002\u201d \u3000\u3000\u6731\u4e91\u662f\u8fd9\u91cc\u7684\u5927\u7ba1\u4e8b\uff0c\u53ef\u662f\u8fd9\u4e24\u4e2a\u4f19\u8ba1\u5374\u597d\u50cf\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u8fd9\u540d\u5b57\u3002\u4e24\u4e2a\u4eba\u4f60\u770b\u770b\u6211\uff0c\u6211\u770b\u770b\u4f60\uff0c\u540c\u65f6\u6447\u4e86\u6447\u5934\uff1a\u201c\u6211\u4eec\u8fd9\u513f\u6ca1\u542c\u8bf4\u6709\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u89c9\u5f97\u66f4\u7edd\u4e86\u3002 \u3000\u3000\u201c\u6211\u770b\u4f60\u4e00\u5b9a\u662f\u65b0\u6765\u7684\u3002\u201d\u5979\u95ee\u8fd9\u4e2a\u4f19\u8ba1\uff0c\u201c\u4f60\u6765\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u624d\u4e09\u5929\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u91cc\u7684\u8001\u677f\u662f\u8c01\uff1f\u201d \u3000\u3000\u8bf4\u4eac\u7247\u5b50\u7684\u4f19\u8ba1\u7b11\u4e86\u3002 \u3000\u3000\u201c\u505a\u4f19\u8ba1\u7684\u4eba\uff0c\u5982\u679c\u8fde\u8001\u677f\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\uff0c\u5c82\u975e\u662f\u4e2a\u7cca\u6d82\u86cb\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7cca\u6d82\uff0c\u6240\u4ee5\u4ed6\u8bf4\uff1a\u201c\u8fd9\u91cc\u7684\u8001\u677f\u59d3\u536b\uff0c\u4e0d\u662f\u71d5\u8d75\u97e9\u9b4f\u7684\u9b4f\uff0c\u662f\u5929\u6d25\u536b\u7684\u536b\uff0c\u53eb\u536b\u5929\u9e4f\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6253\u9a6c\uff0c\u9a6c\u98de\u5954\u3002 \u3000\u3000\u4e00\u4e00\u535c\u9e70\u4e00\u624b\u521b\u7acb\u7684\u201c\u9e70\u8bb0\u201d\u5546\u53f7\uff0c\u8001\u677f\u600e\u4e48\u4f1a\u53d8\u6210\u4e86\u536b\u5929\u9e4f\uff1f \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6240\u6709\u7684\u4f19\u8ba1\u90fd\u662f\u65b0\u6765\u7684\uff0c\u90fd\u662f\u4ece\u5916\u5730\u6765\u7684\uff0c\u8fd9\u4e9b\u4e8b\u4ed6\u4eec\u5b8c\u5168\u4e0d\u77e5\u9053\uff0c\u751a\u81f3\u8fde\u535c\u9e70\u7684\u540d\u5b57\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u76f8\u4fe1\u4ed6\u4eec\u662f\u771f\u7684\u4e0d\u77e5\u9053\uff0c\u5c31\u7b97\u6740\u4e86\u4ed6\u4eec\uff0c\u4e5f\u4e00\u6837\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u536b\u5929\u9e4f\u5728\u54ea\u91cc\uff0c\u8001\u677f\u7684\u884c\u8e2a\uff0c\u505a\u4f19\u8ba1\u7684\u4eba\u672c\u6765\u5c31\u65e0\u6743\u8fc7\u95ee\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\u5462\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u6253\u9a6c\uff0c\u9a6c\u98de\u5954\uff0c\u5954\u5411\u535c\u9e70\u7684\u5e84\u9662\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u786e\u5b9a\u535c\u9e70\u662f\u4e0d\u662f\u8fd8\u5728\u90a3\u91cc\u3002 \u3000\u3000\u60f3\u5230\u90a3\u4e9b\u4eba\u770b\u89c1\u5979\u65f6\u7684\u5947\u602a\u8868\u60c5\uff0c\u60f3\u5230\u90a3\u4e9b\u4eba\u773c\u91cc\u90a3\u79cd\u66a7\u6627\u8be1\u79d8\u7684\u795e\u8272\uff0c\u5979\u5fc3\u91cc\u5df2\u6709\u4e86\u79cd\u8fde\u60f3\u4e0d\u6562\u53bb\u60f3\u7684\u4e0d\u7965\u9884\u5146\u3002 \u3000\u3000\u4f46\u662f\u5979\u4e00\u5b9a\u8981\u53bb\u627e\u3002 \u3000\u3000\u5728\u4ed6\u4eec\u79bb\u5f00\u62c9\u8428\u7684\u8fd9\u6bb5\u65e5\u5b50\u91cc\uff0c\u8fd9\u91cc\u7a76\u7adf\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u53ef\u6015\u7684\u53d8\u5316\uff1f\u6240\u6709\u7684\u95ee\u9898\u90fd\u4e00\u5b9a\u8981\u5148\u627e\u5230\u535c\u9e70\u624d\u80fd\u5f97\u5230\u89e3\u7b54\u3002 \u3000\u3000\u4f46\u662f\u5979\u5df2\u7ecf\u627e\u4e0d\u5230\u535c\u9e70\u4e86\u3002 \u3000\u3000\u5979\u548c\u5c0f\u65b9\u8d76\u5230\u535c\u9e70\u7684\u5e84\u9662\u65f6\uff0c\u90a3\u5730\u65b9\u7adf\u5df2\u53d8\u6210\u4e86\u4e00\u7247\u74e6\u783e\uff0c\u6240\u6709\u7684\u4ead\u53f0\u697c\u9601\u3001\u6811\u6728\u82b1\u8349\u90fd\u5df2\u88ab\u4e00\u628a\u5927\u706b\u70e7\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u201c\u597d\u5927\u7684\u4e00\u573a\u706b\u3002\u201d \u3000\u3000\u591a\u5e74\u540e\u4eba\u4eec\u63d0\u8d77\u8fd9\u6b21\u5927\u706b\u65f6\uff0c\u5fc3\u4e2d\u4ecd\u6709\u4f59\u60b8\uff1a\u201c\u706b\u5934\u81f3\u5c11\u6709\u4e09\u56db\u5341\u4e2a\uff0c\u4e00\u5f00\u59cb\u5c31\u6709\u4e09\u56db\u5341\u4e2a\u5730\u65b9\u540c\u65f6\u70e7\u8d77\u6765\uff0c\u6574\u6574\u70e7\u4e86\u4e09\u5929\u4e09\u591c\u3002\u201d \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u8ba4\u4e3a\u90a3\u662f\u573a\u201c\u5929\u706b\u201d\uff0c\u662f\u4e0a\u82cd\u964d\u7ed9\u8fd9\u5bb6\u4eba\u7684\u707e\u7978\u3002 \u3000\u3000\u8d77\u706b\u7684\u771f\u6b63\u539f\u56e0\u4ece\u6765\u90fd\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u4e5f\u6ca1\u6709\u4eba\u60f3\u77e5\u9053\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7ad9\u5728\u74e6\u783e\u95f4\u3002 \u3000\u3000\u5979\u4f9d\uff1b\u65e5\u8fd8\u80fd\u5206\u8fa8\u51fa\u8fd9\u5730\u65b9\u672c\u6765\u662f\u4e2a\u516b\u89d2\u4ead\uff0c\u56db\u9762\u662f\u4e00\u7247\u82b1\u6d77\u3002\u6bcf\u5f53\u6625\u79cb\u4f73\u65e5\uff0c\u535c\u9e70\u7a7a\u95f2\u7684\u65f6\u5019\uff0c\u5979\u603b\u662f\u4f1a\u966a\u4ed6\u5230\u8fd9\u91cc\u559d\u4e24\u676f\u9152\uff0c\u4e0b\u4e00\u4e00\u5c40\u68cb\u3002 \u3000\u3000\u6cbf\u7740\u82b1\u4e1b\u95f4\u4e00\u6761\u7528\u91c7\u77f3\u94fa\u6210\u7684\u5c0f\u5f84\u5f80\u4e1c\u8d70\uff0c\u5c31\u662f\u5979\u5c45\u4f4f\u7684\u5c0f\u9662\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u5728\u90a3\u91cc\u4f4f\u4e86\u5341\u5e74\uff0c\u5979\u6240\u6709\u7684\u68a6\u60f3\u90fd\u662f\u5728\u90a3\u91cc\u7f16\u7ec7\u6210\u7684\uff0c\u6240\u6709\u7684\u56de\u5fc6\u4e5f\u5168\u90fd\u7559\u5728\u90a3\u91cc\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5979\u75f4\u75f4\u5730\u7ad9\u7740\uff0c\u75f4\u75f4\u5730\u770b\u7740\uff0c\u770b\u7740\u8fd9\u4e00\u7247\u4ee4\u4eba\u5fc3\u788e\u7684\u5e9f\u589f\u3002 \u3000\u3000\u5979\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u4e3a\u4e86\u4e00\u4e00\u4e2a\u5fc3\u7231\u7684\u74f7\u5a03\u88ab\u4eba\u7838\u7834\uff0c\u5979\u4f1a\u6d41\u6cea\uff0c\u4e3a\u4e86\u4e00\u53ea\u5c0f\u732b\u7684\u6b7b\u5979\u4f1a\u54ed\u4e0a\u534a\u5929\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u5979\u53cd\u800c\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u65e7\u68a6\u4f9d\u7a00\uff0c\u6ee1\u76ee\u75ae\u75cd\uff0c\u6ca1\u6709\u4eba\uff0c\u6ca1\u6709\u58f0\u97f3\uff0c\u6240\u6709\u7684\u4e00\u5207\u90fd\u5df2\u5316\u4e3a\u98de\u7070\u3002 \u3000\u3000\u4e00\u4e00\u535c\u9e70\u5462\uff1f \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u8fd8\u6d3b\u7740\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u5979\u4e00\u76f4\u4e0d\u505c\u5730\u5583\u5583\u4f4e\u8bed\uff0c\u53cd\u6765\u590d\u53bb\u4ed6\u8bf4\u7740\u8fd9\u4e24\u53e5\u8bdd\uff0c\u4e5f\u4e0d\u77e5\u662f\u8bf4\u7ed9\u5c0f\u65b9\u542c\u5462\uff0c\u8fd8\u662f\u5728\u5b89\u6170\u81ea\u5df1\u3002 \u3000\u3000\u5c0f\u65b9\u8fde\u4e00\u53e5\u8bdd\u3001\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u8fd8\u80fd\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u8fd9\u91cc\u4e0d\u662f\u4ed6\u7684\u6545\u4e61\uff0c\u4e0d\u662f\u6c5f\u5357\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u7684\u4f24\u75db\u7edd\u4e0d\u6bd4\u5979\u8f7b\u3002 \u3000\u3000\u4ed6\u4e86\u89e3\u5979\u5bf9\u535c\u9e70\u7684\u611f\u60c5\u3002 \u3000\u3000\u5bb6\u56ed\u88ab\u6bc1\uff0c\u8fd8\u53ef\u91cd\u5efa\uff0c\u4eba\u6b7b\u5374\u4e0d\u80fd\u590d\u751f\uff0c\u53ea\u8981\u535c\u9e70\u8fd8\u6d3b\u7740\uff0c\u522b\u7684\u4e8b\u90fd\u6ca1\u6709\u5173\u7cfb\u3002 \u3000\u3000\u2014\u2014\u4ed6\u662f\u4e0d\u662f\u8fd8\u6d3b\u7740\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4ed6\u7684\u4eba\u5982\u4eca\u5728\u54ea\u91cc\uff1f \u3000\u3000\u74e6\u783e\u95f4\u4f20\u6765\u4e00\u9635\u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u4e00\u4e2a\u9ad8\u5927\u7684\u5587\u561b\u8e0f\u7740\u7070\u70ec\u5927\u6b65\u800c\u6765\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u56de\u8fc7\u5934\uff0c\u770b\u7740\u4ed6\u3002 \u3000\u3000\u201c\u6211\u8ba4\u5f97\u4f60\u3002\u201d\u5979\u7684\u58f0\u97f3\u867d\u5df2\u5636\u54d1\uff0c\u5c45\u7136\u8fd8\u80fd\u4fdd\u6301\u9547\u9759\uff0c\u201c\u4f60\u662f\u5676\u4f26\u5927\u5587\u561b\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u8fd9\u5587\u561b\u8bf4\uff0c\u201c\u6211\u53eb\u963f\u82cf\u3002\u201d \u3000\u3000\u201c\u662f\u4ed6\u53eb\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u963f\u82cf\u7684\u795e\u60c5\u4e5f\u5f88\u6c89\u75db\uff1a\u201c\u4e09\u5929\u524d\u6211\u5c31\u5df2\u6765\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e00\u4e5d\u7ae0\u3000\u5728\u5c71\u6df1\u5904 \u7b2c\u5341\u4e5d\u7ae0\u3000\u5728\u5c71\u6df1\u5904 \u3000\u3000\u201c\u90a3\u65f6\u706b\u5df2\u7184\u4e86\uff0c\u6211\u6765\u6e05\u7406\u706b\u573a\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u7684\u624b\u7acb\u523b\u5c31\u56e0\u6fc0\u52a8\u800c\u98a4\u6296\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u80fd\u95ee\uff1a\u201c\u4f60\u627e\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u82cf\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7b49\u5230\u60c5\u7eea\u5e73\u9759\u624d\u80fd\u56de\u7b54\u3002 \u3000\u3000\u201c\u5728\u52ab\u96be\u9003\uff0c\u5929\u610f\u96be\u6d4b\uff0c\u6211\u6765\u65f6\u8fd9\u91cc\u5df2\u7ecf\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\uff0c\u4ec0\u4e48\u90fd\u88ab\u70e7\u5149\uff0c\u6211\u53ea\u627e\u5230\u4e86\u4e00\u70b9\u9aa8\u7070\u3002\u201d \u3000\u3000\u4ed6\u627e\u5230\u7684\u4e0d\u662f\u201c\u4e00\u70b9\u201d\u9aa8\u7070\uff0c\u4ed6\u627e\u5230\u7684\u9aa8\u7070\u88c5\u6ee1\u5341\u4e09\u4e2a\u74e6\u7f50\u3002 \u3000\u3000\u201c\u9aa8\u7070\uff1f\u201d\u9633\u5149\u5c3d\u529b\u63a7\u5236\u81ea\u5df1\uff0c\u201c\u662f\u8c01\u7684\u9aa8\u7070\uff1f\u201d \u3000\u3000\u201c\u662f\u8c01\u7684\u9aa8\u7070\uff1f\u662f\u8c01\u7684\u9aa8\u7070\uff1f\u2026\u2026\u201d \u3000\u3000\u963f\u82cf\u9eef\u7136\u9053\uff1a\u201c\u8fd9\u91cc\u4e5f\u6709\u6211\u7684\u65cf\u4eba\u3001\u6211\u7684\u670b\u53cb\uff0c\u8fd9\u4e09\u5929\u91cc\u6211\u65e5\u65e5\u591c\u591c\u90fd\u5728\u627e\uff0c\u6211\u4e5f\u60f3\u77e5\u9053\u90a3\u662f\u8c01\u7684\u9aa8\u7070\uff0c\u53ea\u53ef\u60dc\u6bcf\u4e2a\u4eba\u7684\u5c38\u9aa8\u90fd\u5df2\u6210\u7070\uff0c\u8fd8\u6709\u8c01\u80fd\u5206\u8fa8\u5f97\u51fa\uff1f\u201d \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\uff1f\u201d\u201c\u9633\u5149\u201d\u95ee\uff0c\u201c\u6bcf\u4e2a\u4eba\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u963f\u82cf\u957f\u957f\u53f9\u606f\uff0c\u9eef\u7136\u65e0\u8bed\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7528\u529b\u626f\u4f4f\u4ed6\u7684\u8888\u88df\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u91cc\u672c\u6765\u4e00\u5171\u6709\u591a\u5c11\u4eba\uff1f\u4f60\u8bf4\u6bcf\u4e2a\u4eba\uff0c\u96be\u9053\u662f\u8bf4\u4ed6\u4eec\u5168\u90fd\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5ffd\u7136\u505c\u987f\uff0c\u597d\u50cf\u8fde\u5979\u81ea\u5df1\u90fd\u88ab\u8fd9\u79cd\u60f3\u6cd5\u6240\u9707\u60ca\u3002 \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u7edd\u4e0d\u4f1a\u3002\u201d\u5979\u653e\u5f00\u4e86\u624b\u8bf4\u9053\uff0c\u201c\u8fd9\u91cc\u4e00\u5b9a\u8fd8\u6709\u4eba\u6d3b\u7740\uff0c\u4e00\u5b9a\u8fd8\u6709\u3002\u4f60\u53ea\u8981\u627e\u5230\u4e00\u4e2a\uff0c\u5c31\u53ef\u4ee5\u95ee\u51fa\u522b\u7684\u4eba\u5728\u54ea\u91cc\u4e86\u3002\u201d \u3000\u3000\u963f\u82cf\u9ed8\u9ed8\u5730\u6447\u5934\u3002 \u3000\u3000\u201c\u96be\u9053\u4f60\u8fde\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\u627e\u5230\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u963f\u82cf\u9053\uff0c\u201c\u6211\u8fde\u4e00\u4e2a\u6d3b\u7740\u7684\u4eba\u90fd\u6ca1\u6709\u627e\u5230\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8d77\u706b\u7684\u90a3\u5929\u665a\u4e0a\uff0c\u8fd9\u91cc\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u7a76\u7adf\u662f\u8c01\u653e\u7684\u706b\uff0c\u6050\u6015\u6c38\u8fdc\u90fd\u6ca1\u6709\u4eba\u80fd\u591f\u8bf4\u51fa\u771f\u8c61\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u4eba\u80fd\u8bf4\u51fa\u771f\u8c61\uff1f\u201d\u201c\u9633\u5149\u201d\u6e10\u6e10\u5931\u53bb\u63a7\u5236\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u731c\u4e0d\u5230\u8c01\u662f\u51f6\u624b\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u51f6\u624b\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d\u201c\u9633\u5149\u201d\u63e1\u7d27\u53cc\u62f3\u8bf4\u51fa\u4e86\u51e0\u4e2a\u540d\u5b57\uff1a\u201c\u536b\u5929\u9e4f\u3001\u80e1\u5927\u638c\u67dc\u3001\u98ce\u53f2\u6708\u5a46\u3001\u2018\u9634\u7075\u2019\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u51ed\u8fd9\u4e9b\u4eba\uff0c\u5c31\u80fd\u5c06\u535c\u9e70\u3001\u6731\u4e91\u3001\u4e25\u6b63\u521a\u3001\u5b8b\u8001\u592b\u5b50\u548c\u8fd9\u91cc\u7684\u6570\u767e\u540d\u6218\u58eb\u5728\u4e00\u591c\u4e4b\u95f4\u4e00\u7f51\u6253\u5c3d\uff0c\u800c\u4e14\u4e0d\u7559\u4e00\u4e2a\u6d3b\u53e3\uff1f\u201d \u3000\u3000\u963f\u82cf\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u95ee\u9898\uff1a\u201c\u5c31\u51ed\u8fd9\u4e9b\u4eba\uff0c\u6050\u6015\u8fd8\u529e\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u5185\u5e94\u3002\u201d \u3000\u3000\u201c\u5185\u5e94\uff1f\u201d\u201c\u9633\u5149\u201d\u95ee\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fd9\u91cc\u4e5f\u6709\u4ed6\u4eec\u57cb\u4f0f\u7684\u5978\u7ec6\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u80fd\u591f\u6d3e\u5978\u7ec6\u57cb\u4f0f\u5728\u4ed6\u4eec\u7684\u7ec4\u7ec7\u91cc\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6c89\u9ed8\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u53c8\u95ee\u9053\uff1a\u201c\u6ce2\u5a03\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u6ce2\u5a03\u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d \u3000\u3000\u963f\u82cf\u9053\uff1a\u201c\u5979\u8bf4\u5979\u4e00\u5b9a\u8981\u6765\u89c1\u535c\u9e70\u3002\u201d \u3000\u3000\u201c\u5931\u706b\u7684\u65f6\u5019\uff0c\u5979\u4e5f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5979\u7684\u4eba\u5462\uff1f\u662f\u6b7b\u662f\u6d3b\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u53c8\u662f\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u56de\u7b54\u7684\uff0c\u963f\u82cf\u53cd\u95ee\uff1a\u201c\u96be\u9053\u4f60\u6000\u7591\u5979\u5df2\u7ecf\u505a\u4e86\u5bf9\u65b9\u7684\u5978\u7ec6\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u62d2\u7edd\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u53ef\u662f\u5979\u7684\u6001\u5ea6\u5df2\u7ecf\u5f88\u660e\u663e\u3002 \u3000\u3000\u5979\u4e00\u5411\u4e0d\u4fe1\u4efb\u6ce2\u5a03\uff01 \u3000\u3000\u5973\u4eba\u5bf9\u5973\u4eba\u672c\u6765\u5c31\u6709\u79cd\u5929\u751f\u7684\u654c\u610f\uff0c\u5f88\u5c11\u6709\u5973\u4eba\u80fd\u591f\u5b8c\u5168\u4fe1\u4efb\u53e6\u4e00\u4e2a\u5973\u4eba\uff0c\u5c24\u5176\u662f\u5728\u7f8e\u4e3d\u7684\u5973\u4eba\u4e4b\u95f4\uff0c\u8fd9\u79cd\u60c5\u51b5\u5c31\u66f4\u660e\u663e\u3002 \u3000\u3000\u201c\u8fd9\u6b21\u4f60\u9519\u4e86\u3002\u201d\u963f\u82cf\u65ad\u7136\u9053\uff0c\u201c\u5978\u7ec6\u7edd\u4e0d\u662f\u6ce2\u5a03\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u80fd\u786e\u5b9a\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u2026\u2026\u201d\u963f\u82cf\u8fdf\u7591\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u4e0b\u5b9a\u51b3\u5fc3\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u5728\u65e0\u610f\u95f4\u53d1\u73b0\u4e86\u4e00\u4e2a\u79d8\u5bc6\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u201c\u6709\u5173\u535c\u9e70\u3001\u73ed\u5bdf\u5df4\u90a3\u548c\u6ce2\u5a03\u4e09\u4e2a\u4eba\u4e4b\u95f4\u7684\u79d8\u5bc6\uff0c\u6709\u5173\u4ed6\u4eec\u7684\u8eab\u4e16\u548c\u2026\u2026\u201d \u3000\u3000\u963f\u82cf\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u4e25\u8083\u6c89\u91cd\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u9732\u51fa\u79cd\u8be1\u79d8\u4e4b\u6781\u3001\u53c8\u6109\u5feb\u4e4b\u6781\u7684\u7b11\u5bb9\uff0c\u5ffd\u7136\u6162\u6162\u5730\u8dea\u4e86\u4e0b\u53bb\uff0c\u4e00\u8dea\u4e0b\u53bb\uff0c\u5c31\u52a8\u4e5f\u4e0d\u518d\u52a8\u4e86\u3002 \u3000\u3000\u6674\u7a7a\u4e07\u91cc\uff0c\u56db\u91ce\u6e3a\u65e0\u4eba\u8ff9\uff0c\u770b\u4e0d\u89c1\u90a3\u4e2a\u900f\u660e\u5982\u6c34\u6676\u7684\u201c\u9634\u7075\u201d\uff0c\u770b\u4e0d\u89c1\u90a3\u4e2a\u68b3\u7740\u4e00\u5934\u5c0f\u8fab\u5b50\u7684\u5c0f\u59d1\u5a18\uff0c\u4e5f\u770b\u4e0d\u89c1\u90a3\u6761\u96ea\u767d\u53ef\u7231\u7684\u72ee\u5b50\u72d7\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u6bd2\u6740\u4e86\u963f\u82cf\u7684\uff1f\u963f\u82cf\u77e5\u9053\u7684\u662f\u4ec0\u4e48\u79d8\u5bc6\uff1f \u3000\u3000\u201c\u9634\u7075\u201d\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u4ed6\u8bf4\u51fa\u8fd9\u79d8\u5bc6\u6765\uff1f \u3000\u3000\u4e00\u4e2a\u6709\u5173\u535c\u9e70\u3001\u73ed\u5bdf\u5df4\u90a3\u548c\u6ce2\u5a03\u4e09\u4e2a\u4eba\u4e4b\u95f4\u7684\u79d8\u5bc6\uff0c\u548c\u201c\u9634\u7075\u201d\u4ed6\u4eec\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u5ffd\u7136\u53c8\u62c9\u4f4f\u4e86\u5c0f\u65b9\u7684\u624b\u3002 \u3000\u3000\u201c\u6211\u4eec\u8d70\u3002\u201d\u5979\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u53bb\u627e\u535c\u9e70\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u627e\u5f97\u5230\u4ed6\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4ed6\u4e0d\u6b7b\uff0c\u6211\u5c31\u80fd\u627e\u5f97\u5230\u3002\u201d\u201c\u9633\u5149\u201d\u4f9d\uff1b\u65e5\u5145\u6ee1\u4fe1\u5fc3\uff0c\u201c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u600e\u4e48\u80fd\u629b\u5f97\u4e0b\u8fd9\u4e9b\u4e8b\uff0c\u81ea\u5df1\u4e00\u8d70\u4e86\u4e4b\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u5cfb\u86c7\u87ab\u624b\uff0c\u58ee\u58eb\u65ad\u8155\u3002\u201d\u201c\u9633\u5149\u201d\u8bf4\uff0c\u201c\u5230\u4e86\u5fc5\u8981\u65f6\uff0c\u4ec0\u4e48\u4e8b\u4ed6\u90fd\u80fd\u629b\u5f97\u4e0b\uff0c\u4ec0\u4e48\u4e8b\u4ed6\u90fd\u53ef\u4ee5\u727a\u7272\u3002\u201d \u3000\u3000\u5979\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u8981\u6d3b\u4e0b\u53bb\uff0c\u65e0\u8bba\u6d3b\u5f97\u591a\u8270\u82e6\uff0c\u4ed6\u90fd\u8981\u6d3b\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u8981\u91cd\u5efa\u4ed6\u7684\u5bb6\u56ed\uff0c\u8fd8\u8981\u6d88\u706d\u4ed6\u7684\u4ec7\u654c\uff0c\u6240\u4ee5\u4ed6\u80fd\u8d70\uff0c\uff0e\u4e0d\u80fd\u6b7b\uff01\u201d \u3000\u3000\u5979\u51dd\u89c6\u7740\u5c0f\u65b9\uff1a\u201c\u4f60\u5e94\u8be5\u660e\u767d\uff0c\u6b7b\u6709\u65f6\u8fdc\u6bd4\u6d3b\u5bb9\u6613\u5f97\u591a\uff0c\u6709\u4eba\u867d\u7136\u5b81\u53ef\u9009\u62e9\u6bd4\u8f83\u5bb9\u6613\u7684\u4e00\u6761\u8def\u8d70\uff0c\u5b81\u53ef\u4e00\u6b7b\u4e86\u4e4b\uff0c\u4ed6\u7edd\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6211\u660e\u767d\u4e86\u3002\u201d\u5c0f\u65b9\u5ffd\u7136\u95ee\u4e5f\u6709\u4e86\u4fe1\u5fc3\uff0c\u201c\u4ed6\u4e00\u5b9a\u8fd8\u6d3b\u7740\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u6b7b\u7684\uff01\u201d \u3000\u3000\u5728\u5c71\u6df1\u5904\uff0c\u5728\u6c34\u4e4b\u6ee8\uff0c\u5728\u4e00\u4e2a\u8fdc\u79bb\u7ea2\u5c18\u7684\u7eff\u6811\u6797\u91cc\uff0c\u642d\u7740\u4e00\u95f4\u5c0f\u5c0f\u7684\u6728\u5c4b\u3002 \u3000\u3000\u5728\u4f60\u9971\u7ecf\u5fe7\u60a3\uff0c\u5386\u5c3d\u8270\u82e6\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u767e\u6218\u5f52\u6765\u7684\u65f6\u5019\uff0c\u5077\u534a\u65e5\u95f2\uff0c\u5e26\u4e00\u4e2a\u4f60\u559c\u6b22\u5979\u3001\u5979\u4e5f\u559c\u6b22\u4f60\u7684\u5973\u5b69\u5b50\uff0c\u5230\u8fd9\u6728\u5c4b\u6765\uff0c\u505a\u4e00\u70b9\u4f60\u559c\u6b22\u505a\u5979\u4e5f\u559c\u6b22\u505a\u7684\u4e8b\uff0c\u6216\u8005\u4ec0\u4e48\u90fd\u4e0d\u505a\u3002 \u3000\u3000\u5982\u679c\u4f60\u6709\u8fd9\u4e48\u4e00\u95f4\u6728\u5c4b\uff0c\u5982\u679c\u4f60\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u5b69\uff0c\u4f60\u5f53\u7136\u4e0d\u613f\u610f\u522b\u4eba\u6765\u6253\u6270\u3002 \u3000\u3000\u6240\u4ee5\u4f60\u6709\u4e86\u5371\u9669\u65f6\uff0c\u4e5f\u53ef\u4ee5\u8eb2\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u535c\u9e70\u6709\u8fd9\u4e48\u6837\u4e00\u95f4\u6728\u5c4b\uff0c\u5728\u5c71\u6df1\u5904\uff0c\u5728\u6c34\u4e4b\u6ee8\uff0c\u5728\u4e00\u4e2a\u8fdc\u79bb\u7ea2\u5c18\u7684\u7eff\u6811\u6797\u91cc\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5c31\u662f\u4ed6\u7684\u5973\u5b69\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u4eec\u7684\u79d8\u5bc6\uff0c\u672c\u6765\u53ea\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\u77e5\u9053\uff0c\u73b0\u5728\u5979\u628a\u5c0f\u65b9\u5e26\u6765\u4e86\u3002 \u3000\u3000\u6728\u5c4b\u91cc\u6709\u56db\u6247\u5927\u5927\u7684\u7a97\u5b50\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u7684\u706b\u7089\u3002 \u3000\u3000\u5982\u679c\u662f\u590f\u5929\uff0c\u4ed6\u4eec\u5c31\u4f1a\u6253\u5f00\u7a97\u5b50\uff0c\u8ba9\u6765\u81ea\u8fdc\u5c71\u3001\u6765\u81ea\u6c34\u6ee8\u7684\u98ce\u5439\u8fdb\u7a97\u6237\u6765\uff0c\u9759\u9759\u5730\u547c\u5438\u98ce\u4e2d\u4ece\u8fdc\u5c71\u5e26\u6765\u7684\u6728\u53f6\u82ac\u82b3\u3002 \u3000\u3000\u5982\u679c\u662f\u51ac\u5929\uff0c\u4ed6\u4eec\u5c31\u4f1a\u5728\u5c0f\u5c0f\u7684\u706b\u7089\u91cc\u751f\u4e00\u5806\u65fa\u65fa\u7684\u706b\uff0c\u5728\u706b\u4e0a\u67b6\u4e00\u4e2a\u5c0f\u5c0f\u7684\u94c1\u9505\uff0c\u6e29\u4e00\u89d2\u9152\uff0c\u9759\u9759\u5730\u770b\u7740\u706b\u7130\u95ea\u52a8\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u4eec\u7684\u4e16\u754c\uff0c\u5b81\u9759\u7684\u4e16\u754c\u3002 \u3000\u3000\u201c\u5982\u679c\u535c\u9e70\u8fd8\u6d3b\u7740\uff0c\u4e00\u5b9a\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d\u201c\u9633\u5149\u201d\u8bf4\uff0c\u201c\u4ed6\u4e00\u5b9a\u77e5\u9053\u6211\u4e00\u5b9a\u4f1a\u6765\u627e\u4ed6\u3002\u201d \u3000\u3000\u535c\u9e70\u6ca1\u6709\u6765\u3002 \u3000\u3000\u95e8\u6ca1\u6709\u9501\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u5916\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u5730\u65b9\uff0c\u95e8\u4e0d\u5fc5\u9501\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u63a8\u5f00\u95e8\uff0c\u8138\u4e0a\u7684\u8840\u8272\u5c31\u892a\u5c3d\u4e86\u3002 \u3000\u3000\u4e00\u95f4\u7a7a\u5c4b\uff0c\u6ee1\u5c4b\u76f8\u601d\uff0c\u6ee1\u5c4b\u6d53\u6101\u2014\u2014\u4ed6\u4e3a\u4ec0\u4e48\u6ca1\u6709\u6765\uff1f \u3000\u3000\u5979\u7684\u8eab\u5b50\u5ffd\u7136\u53d1\u6296\uff0c\u8840\u8272\u5df2\u892a\u5c3d\u7684\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u8d77\u4e86\u79cd\u5947\u5f02\u7684\u7ea2\u6655\u3002 \u3000\u3000\u5979\u7684\u8eab\u5b50\u6296\u5f97\u597d\u53ef\u6015\u597d\u53ef\u6015\uff0c\u5979\u7684\u8138\u7ea2\u5f97\u597d\u5947\u602a\u597d\u5947\u602a\u3002 \u3000\u3000\u5979\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f\u5979\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u7a97\u4e0b\u6709\u5f20\u5c0f\u684c\uff0c\u5979\u7684\u773c\u775b\u5c31\u5728\u76ef\u7740\u8fd9\u5f20\u5c0f\u684c\u5b50\u770b\uff0c\u53ef\u662f\u684c\u4e0a\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u5728\u770b\u7740\u4e00\u5f20\u7a7a\u684c\u5b50\u65f6\uff0c\u8138\u4e0a\u90fd\u7edd\u4e0d\u4f1a\u9732\u51fa\u5979\u8fd9\u6837\u7684\u8868\u60c5\u3002 \u3000\u3000\u5979\u4e3a\u4ec0\u4e48\u5374\u5ffd\u7136\u53d8\u5f97\u5982\u6b64\u5174\u594b\u6fc0\u52a8\uff1f \u3000\u3000\u96be\u9053\u5979\u80fd\u770b\u5f97\u89c1\u4e00\u4e9b\u522b\u4eba\u770b\u4e0d\u89c1\u7684\u4e1c\u897f\u3001 \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u8981\u95ee\u5979\uff0c\u201c\u9633\u5149\u201d\u7528\u529b\u54ac\u4f4f\u5634\u5507\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u80fd\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4ed6\u5df2\u7ecf\u5230\u8fd9\u91cc\u6765\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u6765\u8fc7\uff1f\u201d \u3000\u3000\u201c\u8fd9\u684c\u5b50\u4e0a\u672c\u6765\u6709\u4e2a\u6ce5\u5a03\u5a03\uff0c\u662f\u4ed6\u7279\u5730\u4ece\u65e0\u9521\u5e26\u56de\u6765\u7684\u6ce5\u5a03\u5a03\u3002\u201d \u3000\u3000\u9633\u5149\u201d\u8f7b\u8f7b\u4ed6\u8bf4\uff0c\u201c\u4ed6\u4e00\u76f4\u89c9\u5f97\u8fd9\u6ce5\u5a03\u5a03\u5f88\u50cf\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u660e\u767d\uff1a\u201c\u4f60\u4eec\u4e0a\u6b21\u8d70\u7684\u65f6\u5019\uff0c\u6ce5\u5a03\u5a03\u662f\u4e0d\u662f\u8fd8\u662f\u5728\u8fd9\u5f20\u684c\u4e0a\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u70b9\u5934\uff1a\u201c\u6211\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7edd\u4e0d\u4f1a\u9519\u3002\u201d\u5979\u8bf4\uff0c\u201c\u6211\u4eec\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u6211\u8fd8\u4eb2\u4e86\u5b83\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u4f60\u4eec\u8fd8\u6709\u6ca1\u6709\u6765\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u9664\u4e86\u4f60\u4eec\u4e4b\u5916\uff0c\u8fd8\u6709\u6ca1\u6709\u4eba\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\u3002 \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u201c\u9633\u5149\u201d\u5f3a\u8c03\u4ed6\u8bf4\uff1a\u201c\u7edd\u5bf9\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u8ba4\u4e3a\u535c\u9e70\u4e00\u5b9a\u5df2\u7ecf\u5230\u8fd9\u91cc\u6765\u8fc7\uff0c\u6ce5\u5a03\u5a03\u4e00\u5b9a\u662f\u4ed6\u5e26\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b9a\u662f\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5df2\u54fd\u54bd\uff0c\u6709\u4e9b\u95ee\u9898\u5979\u60f3\u95ee\uff0c\u53c8\u4e0d\u6562\u95f4\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u8fd9\u4e9b\u95ee\u9898\u4e00\u5b9a\u4f1a\u523a\u4f24\u5979\u81ea\u5df1\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\u65e2\u7136\u5df2\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u8d70\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u7559\u5728\u8fd9\u91cc\u7b49\u5979\uff1f\u4e3a\u4ec0\u4e48\u6ca1\u6709\u7559\u4e0b\u4e00\u70b9\u6d88\u606f\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u5979\u5c31\u7b97\u95ee\u51fa\u6765\uff0c\u5c0f\u65b9\u4e5f\u65e0\u6cd5\u56de\u7b54\u7684\u3002 \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u5979\u6ca1\u6709\u95ee\u51fa\u6765\uff0c\u53cd\u800c\u6709\u4eba\u4e3a\u5979\u56de\u7b54\u4e86\u2014\u2014\u662f\u7528\u4e00\u79cd\u5f88\u5947\u602a\u5f88\u60ca\u4eba\u5f88\u53ef\u6015\u7684\u65b9\u6cd5\u56de\u7b54\u7684\u3002 \u3000\u3000\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u53ea\u542c\u89c1\u5c4b\u9876\u4e0a\u6709\u201c\u7b03\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u63a5\u7740\uff0c\u8fd9\u5c0f\u6728\u5c4b\u7684\u56db\u9762\u516b\u65b9\u90fd\u6709\u540c\u6837\u7684\u54cd\u58f0\uff0c\u201c\u7b03\u3001\u7b03\u3001\u7b03\u2026\u2026\u201d\u4e00\u8fde\u4e32\u54cd\u4e2a\u4e0d\u505c\uff0c\u5c31\u597d\u50cf\u6709\u65e0\u6570\u611a\u8822\u7684\u730e\u4eba\uff0c\u5c06\u8fd9\u5c0f\u6728\u5c4b\u9519\u8ba4\u4e3a\u662f\u4e00\u4e2a\u6d2a\u8352\u5de8\u517d\uff0c\u5c04\u51fa\u4e86\u65e0\u6570\u5f2f\u7bad\uff0c\u9489\u5728\u6728\u5c4b\u4e0a\uff0c\u60f3\u6d3b\u6d3b\u628a\u5b83\u5c04\u6b7b\u3002 \u3000\u3000\u6728\u5c4b\u4e0d\u4f1a\u6b7b\uff0c\u4e16\u4e0a\u4e5f\u6ca1\u6709\u5982\u6b64\u611a\u8822\u7684\u730e\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u4ed6\u4eec\u5f88\u5feb\u5c31\u660e\u767d\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u5c31\u5728\u4e00\u77ac\u95f4\uff0c\u6728\u5c4b\u5ffd\u7136\u98de\u8d77\uff0c\u6bcf\u4e00\u5757\u6728\u677f\u90fd\u5ffd\u7136\u8131\u79bb\u4e86\u539f\u6765\u7684\u7ed3\u6784\uff0c\u4e00\u5757\u5757\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6bcf\u4e00\u5757\u6728\u677f\u4e0a\u90fd\u9489\u7740\u4e2a\u94a2\u94a9\uff0c\u6bcf\u4e00\u4e2a\u94a2\u94a9\u4e0a\u90fd\u5e26\u7740\u6761\u957f\u7d22\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u770b\u89c1\u4e00\u6761\u6761\u957f\u7d22\u5e26\u7740\u4e00\u5757\u5757\u6728\u677f\u6ee1\u5929\u98de\u821e\uff0c\u4e00\u8f6c\u773c\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6728\u5c4b\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u90a3\u5f20\u5c0f\u5c0f\u7684\u7a7a\u684c\u5b50\u8fd8\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u90a3\u4e2a\u5c0f\u5c0f\u7684\u706b\u7089\u4e5f\u8fd8\u5728\u539f\u6765\u7684\u5730\u65b9\u3002 \u3000\u3000\u6728\u5c4b\u91cc\u6bcf\u6837\u4e1c\u897f\u90fd\u4f9d\uff1b\u65e5\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u53ef\u662f\u6728\u5c4b\u5df2\u7ecf\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u6df1\u5c71\uff0c\u662f\u5728\u5927\u5c71\u6700\u6df1\u5904\u4e00\u4e2a\u8fdc\u79bb\u7ea2\u5c18\u7684\u7eff\u8272\u4e1b\u6797\u6700\u6df1\u5904\u3002 \u3000\u3000\u957f\u7d22\u98de\u6765\u98de\u53bb\u3002 \u3000\u3000\u6728\u5c4b\u5df2\u98de\u53bb\u3002 \u3000\u3000\u5927\u5c71\u5374\u4ecd\u4f9d\uff1b\u65e5\uff0c\u4e1b\u6797\u4e5f\u4f9d\u65e7\uff0c\u98ce\u4f9d\uff1b\u65e5\u5728\u5439\uff0c\u98ce\u4e2d\u4f9d\u65e7\u5145\u6ee1\u4e86\u4ece\u8fdc\u5c71\u5e26\u6765\u7684\u6728\u53f6\u82ac\u82b3\u3002 \u3000\u3000\u867d\u7136\u662f\u767d\u5929\uff0c\u9633\u5149\u5374\u7167\u4e0d\u8fdb\u8fd9\u5757\u6d53\u5bc6\u7684\u539f\u59cb\u4e1b\u6797\uff0c\u56db\u4e0b\u4e00\u7247\u6d53\u7eff\uff0c\u6d53\u5f97\u5316\u4e5f\u5316\u4e0d\u5f00\uff0c\u7eff\u5f97\u5c31\u50cf\u662f\u6c5f\u5357\u7684\u6625\u6c34\u3002 \u3000\u3000\u9664\u4e86\u8fd9\u4e00\u7247\u6d53\u7eff\u548c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u5916\uff0c\u5929\u5730\u95f4\u4eff\u4f5b\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u522b\u7684\u4eba\uff0c\u6ca1\u6709\u58f0\u97f3\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u770b\u770b\u5c0f\u65b9\uff0c\u5c0f\u65b9\u770b\u770b\u5979\uff0c\u5b64\u96f6\u96f6\u7684\u4e24\u4e2a\u4eba\uff0c\u4e24\u4e2a\u4eba\u7684\u624b\u811a\u90fd\u5df2\u51b0\u51b7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u90fd\u77e5\u9053\uff0c\u73b0\u5728\u4ed6\u4eec\u867d\u7136\u770b\u4e0d\u89c1\u4efb\u4f55\u4eba\uff0c\u4e5f\u542c\u4e0d\u89c1\u4efb\u4f55\u58f0\u97f3\uff0c\u53ef\u662f\u5728\u6bcf\u4e00\u682a\u7eff\u6811\u540e\u3001\u6bcf\u4e00\u4e2a\u9634\u5f71\u91cc\uff0c\u90fd\u5df2\u7ecf\u5e03\u6ee1\u4e86\u4ed6\u4eec\u770b\u4e0d\u89c1\u4e5f\u542c\u4e0d\u89c1\u7684\u6740\u673a\u3002 \u3000\u3000\u957f\u7d22\u4e0d\u4f1a\u65e0\u6545\u98de\u6765\uff0c\u6728\u5c4b\u4e5f\u4e0d\u4f1a\u65e0\u6545\u5730\u98de\u53bb\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u7684\u4ec7\u654c\u5df2\u7ecf\u6765\u4e86\uff0c\u8ddf\u7740\u4ed6\u4eec\u6765\u7684\uff0c\u5728\u62c9\u8428\uff0c\u5728\u90a3\u706b\u573a\u91cc\uff0c\u5c31\u5df2\u7ecf\u76ef\u4e0a\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u535c\u9e70\u8fd8\u6ca1\u6709\u8d70\uff0c\u73b0\u5728\u5f53\u7136\u5df2\u843d\u5165\u4e86\u8fd9\u4e9b\u4eba\u7684\u638c\u63e1\u4e2d\u3002 \u3000\u3000\u2014\u2014\u6240\u4ee5\u535c\u9e70\u8d70\u4e86\uff0c\u800c\u4e14\u6ca1\u6709\u7559\u4e0b\u4e00\u70b9\u6d88\u606f\u3002 \u3000\u3000\u2014\u2014\u56e0\u4e3a\u4ed6\u7b97\u51c6\u4e86\u201c\u9633\u5149\u201d\u8fdf\u65e9\u4e00\u5b9a\u4f1a\u6765\u627e\u4ed6\uff0c\u4e5f\u7b97\u51c6\u4e86\u4ed6\u7684\u5bf9\u5934\u4e00\u5b9a\u4f1a\u8ddf\u7740\u5979\u6765\u7684\u3002 \u3000\u3000\u5f3a\u654c\u73af\u4f3a\uff0c\u6740\u673a\u56db\u4f0f\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u5e94\u8be5\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u770b\u7740\u5c0f\u65b9\uff0c\u5c0f\u65b9\u4e5f\u770b\u7740\u5979\uff0c\u4e24\u4e2a\u4eba\u5c45\u7136\u5168\u90fd\u7b11\u4e86\uff0c\u5c31\u597d\u50cf\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u3002 \u3000\u3000\u5c31\u597d\u50cf\u6728\u5c4b\u8fd8\u5728\u539f\u6765\u7684\u5730\u65b9\u3002 \u3000\u3000\u201c\u8fd9\u5730\u65b9\u771f\u4e0d\u9519\u3002\u201d\u5c0f\u65b9\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u5e94\u8be5\u5e26\u6211\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5c31\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u559c\u6b22\u8fd9\u5730\u65b9\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627e\u4e86\u4e2a\u6905\u5b50\u5750\u4e0b\u6765\uff0c\u5ffd\u7136\u8bf4\uff1a\u201c\u6211\u6562\u8ddf\u4f60\u6253\u8d4c\u3002\u201d \u3000\u3000\u201c\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u6562\u8d4c\u8fd9\u91cc\u4e00\u5b9a\u6709\u9152\u3002\u201d \u3000\u3000\u201c\u4f60\u8d62\u4e86\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u7b11\u5f97\u4eff\u4f5b\u771f\u7684\u5f88\u6109\u5feb\uff0c\u771f\u7684\u4ece\u4e00\u4e2a\u5c0f\u5c0f\u7684\u67dc\u5b50\u91cc\u62ff\u51fa\u4e86\u4e00\u7f50\u9152\u548c\u4e24\u4e2a\u9152\u676f\uff5e \u3000\u3000\u5979\u5728\u5c0f\u65b9\u5bf9\u9762\u5750\u4e0b\u6765\uff0c\u5c0f\u65b9\u62cd\u5f00\u5382\u9152\u7f50\u7684\u6ce5\u5c01\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u597d\u9152\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u4ed6\u5012\u4e86\u4e24\u676f\uff0c\u4e00\u676f\u7ed9\u81ea\u5df1\uff0c\u4e00\u676f\u7ed9\u201c\u9633\u5149\u201d\u3002\u201c\u6211\u656c\u4f60\u3002\u201d\u4ed6\u4e3e\u676f\uff0c\u201c\u795d\u4f60\u4e07\u4e8b\u5982\u610f\uff0c\u957f\u547d\u767e\u5c81\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u656c\u4f60\u3002\u201d\u201c\u9633\u5149\u201d\u8bf4\uff0c\u201c\u4e5f\u795d\u4f60\u4e07\u4e8b\u5982\u610f\u3002\u201d \u3000\u3000\u4ed6\u4eec\u540c\u65f6\u4e3e\u676f\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u6ca1\u6709\u628a\u676f\u4e2d\u7684\u9152\u559d\u4e0b\u53bb\uff0c\u5ffd\u7136\u95f4\uff0c\u98ce\u58f0\u7834\u7a7a\uff0c\u201c\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u4e24\u4e2a\u9152\u676f\u90fd\u788e\u4e86\u3002 \u3000\u3000\u9152\u676f\u662f\u88ab\u4e24\u679a\u94dc\u94b1\u51fb\u788e\u7684\uff0c\u94dc\u94b1\u81ea\u6d53\u836b\u6df1\u5904\u98de\u6765\uff0c\u8ddd\u79bb\u4ed6\u4eec\u6700\u5c11\u5728\u5341\u51e0\u4e08\u5916\u3002 \u3000\u3000\u8981\u7528\u4e00\u679a\u94dc\u94b1\u6253\u788e\u4e00\u4e2a\u9152\u676f\u5e76\u4e0d\u96be\uff0c\u8981\u7528\u4e00\u679a\u94dc\u94b1\u4ece\u5341\u51e0\u4e08\u5916\u6253\u788e\u4e00\u4e2a\u9152\u676f\uff0c\u5c31\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u4f46\u662f\u201c\u9633\u5149\u201d\u548c\u5c0f\u65b9\u90fd\u597d\u50cf\u6839\u672c\u6ca1\u6709\u628a\u5b83\u5f53\u4f5c\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5c45\u7136\u8fd8\u662f\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u5c31\u597d\u50cf\u624b\u91cc\u6839\u672c\u6ca1\u6709\u62ff\u8fc7\u9152\u676f\uff0c\u53c8\u597d\u50cf\u9152\u676f\u5728\u624b\u4e2d\uff0c\u6839\u672c\u6ca1\u6709\u88ab\u6253\u788e\u3002 \u3000\u3000\u5982\u679c\u8fd9\u65f6\u5019\u6709\u4eba\u5728\u770b\u7740\u4ed6\u4eec\uff0c\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u767d\u75f4\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u5f53\u7136\u6709\u4eba\u5728\u770b\u7740\u4ed6\u4eec\uff0c\u8fd9\u6728\u5c4b\u56db\u9762\u7684\u5bc6\u6797\u4e2d\u90fd\u6709\u4eba\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4eec\u867d\u7136\u62c6\u4e86\u6728\u5c4b\uff0c\u51fb\u788e\u9152\u676f\uff0c\u5374\u6ca1\u6709\u522b\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u5982\u679c\u8bf4\u201c\u9633\u5149\u201d\u548c\u5c0f\u65b9\u662f\u5728\u6f14\u620f\uff0c\u4ed6\u4eec\u5c31\u5728\u770b\u620f\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u96be\u9053\u662f\u7279\u5730\u6765\u770b\u620f\u7684\uff1f \u3000\u3000\u5929\u8272\u5df2\u6e10\u6e10\u6697\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u7ad9\u8d77\u6765\uff0c\u5728\u8fd9\u4e2a\u5df2\u7ecf\u4e0d\u89c1\u4e86\u7684\u6728\u5c4b\u91cc\uff0c\u6cbf\u7740\u56db\u9762\u5df2\u7ecf\u4e0d\u89c1\u5382\u7684\u6728\u58c1\uff0c\u8f6c\u4e86\u4e24\u4e2a\u5708\u5b50\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u7684\u5929\u6c14\u771f\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u60f3\u51fa\u53bb\u8d70\u4e00\u8d70\uff1f\u201d\u5c0f\u65b9\u95ee\u201c\u9633\u5149\u201d\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u770b\u7740\u4ed6\uff0c\u770b\u4e86\u534a\u5929\uff0c\u624d\u6162\u6162\u5730\u6447\u4e86\u6447\u5934\uff1a\u201c\u6211\u4e0d\u60f3\u53bb\u3002\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u53bb\uff0c\u6211\u5728\u8fd9\u91cc\u7b49\u4f60\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u4e00\u4e2a\u4eba\u53bb\u3002\u201d\u5c0f\u65b9\u5411\u5979\u4fdd\u8bc1\uff0c\u201c\u6211\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u56db\u9762\u7684\u6728\u58c1\u95e8\u7a97\u867d\u7136\u5df2\u5168\u90fd\u4e0d\u5b58\u5728\u4e86\uff0c\u4ed6\u5374\u8fd8\u662f\u4ece\u539f\u6765\u6709\u95e8\u7684\u5730\u65b9\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5f88\u6162\uff0c\u6001\u5ea6\u5f88\u60a0\u95f2\uff0c\u5c31\u597d\u50cf\u771f\u7684\u662f\u5403\u9971\u4e86\u996d\u51fa\u53bb\u6563\u6b65\u7684\u6837\u5b50\u3002 \u3000\u3000\u6728\u5c4b\u5efa\u9020\u5728\u6811\u6797\u91cc\u7279\u5730\u5f00\u907f\u51fa\u6765\u7684\u4e00\u5757\u7a7a\u5730\u4e0a\u3002\u4ed6\u521a\u521a\u8d70\u5230\u7a7a\u5730\u7684\u8fb9\u7f18\uff0c\u6797\u6728\u540e\u9762\u5ffd\u7136\u6709\u6761\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u4e2a\u4eba\u8f7b\u53f1\uff1a\u201c\u56de\u53bb\uff01\u201d \u3000\u3000\u53f1\u58f0\u4e2d\uff0c\u5341\u4e8c\u70b9\u5bd2\u661f\u66b4\u5c04\u800c\u51fa\uff0c\u6253\u7684\u65e2\u4e0d\u662f\u5c0f\u65b9\u7a74\u9053\uff0c\u4e5f\u4e0d\u662f\u4ed6\u7684\u8981\u5bb3\uff0c\u5374\u5c06\u4ed6\u6240\u6709\u7684\u53bb\u8def\u5168\u90fd\u5c01\u6b7b\u3002 \u3000\u3000\u8fce\u9762\u6253\u6765\u7684\u4e09\u70b9\u5bd2\u661f\u6765\u52bf\u6700\u5feb\uff0c\u5c0f\u65b9\u65e2\u4e0d\u80fd\u518d\u5411\u524d\u8d70\uff0c\u4e5f\u4e0d\u80fd\u5de6\u53f3\u95ea\u907f\uff0c\u53ea\u6709\u968f\u7740\u8fce\u9762\u6253\u6765\u7684\u8fd9\u4e09\u4ef6\u6697\u5668\u7684\u6765\u52bf\u5411\u540e\u9000\uff0c\u4e00\u8def\u9000\u56de\u4e86\u6728\u5c4b\uff0c\u9000\u56de\u4e86\u4ed6\u539f\u6765\u5750\u7684\u90a3\u5f20\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u4ed6\u521a\u5750\u4e0b\uff0c\u8fd9\u4e09\u4ef6\u6697\u5668\u4e5f\u843d\u4e86\u4e0b\u53bb\uff0c\u843d\u5728\u4ed6\u9762\u524d\uff0c\u5374\u4e0d\u662f\u521a\u624d\u51fb\u788e\u4ed6\u9152\u676f\u7684\u90a3\u79cd\u94dc\u94b1\uff0c\u800c\u662f\u4e09\u679a\u7cbe\u94c1\u6253\u9020\u7684\u94c1\u83b2\u5b50\u3002 \u3000\u3000\u94c1\u83b2\u5b50\u672c\u6765\u662f\u79cd\u6781\u666e\u901a\u7684\u6697\u5668\uff0c\u53ef\u662f\u8fd9\u4e2a\u4eba\u53d1\u6697\u5668\u7684\u624b\u6cd5\u5374\u6781\u4e0d\u666e\u901a\uff0c\u4e0d\u4f46\u624b\u6cd5\u6781\u5de7\u5999\uff0c\u529b\u91cf\u66f4\u7b97\u5f97\u51c6\u6781\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u770b\u7740\u5c0f\u65b9\uff0c\u8138\u4e0a\u867d\u7136\u8fd8\u662f\u5168\u65e0\u8868\u60c5\uff0c\u773c\u4e2d\u5374\u5df2\u6709\u4e86\u5fe7\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u73b0\u5728\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\uff0c\u8fd9\u6b21\u6765\u7684\u90fd\u662f\u4e00\u7b49\u7684\u9ad8\u624b\u3002 \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u53c8\u5411\u201c\u9633\u5149\u201d\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u6211\u56de\u6765\u5f97\u5feb\u4e0d\u5feb\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u5c45\u7136\u4e5f\u5bf9\u4ed6\u7b11\u4e86\u7b11\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u771f\u662f\u5feb\u6781\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u8bf4\u5b8c\uff0c\u5c0f\u65b9\u5df2\u7ecf\u4ece\u6905\u5b50\u4e0a\u98de\u8eab\u800c\u53bb\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u201c\u71d5\u5b50\u4e09\u6284\u6c34\u201d\uff0c\u5f2f\u7bad\u822c\u6251\u5411\u53e6\u4e00\u8fb9\u6797\u6728\u7684\u6d53\u836b\u6df1\u5904\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u521a\u6251\u4eba\u6811\u836b\uff0c\u6811\u836b\u4e2d\u4e5f\u54cd\u8d77\u4e00\u58f0\u8f7b\u53f1\uff0c\u4eff\u4f5b\u8fd8\u6709\u5251\u5149\u4e00\u95ea\uff1a\u201c\u8fd9\u6761\u8def\u4e5f\u662f\u8d70\u4e0d\u901a\uff0c\u4f60\u8fd8\u662f\u5f97\u56de\u53bb\uff01\u201d \u3000\u3000\u4e00\u53e5\u8bdd\uff0c\u5341\u56db\u4e2a\u5b57\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u5c0f\u65b9\u7684\u8eab\u5b50\u5df2\u7ecf\u4ece\u6811\u836b\u4e2d\u98de\u51fa\uff0c\u51cc\u7a7a\u7ffb\u4e86\u4e09\u4e2a\u7b4b\u6597\uff0c\u4ece\u534a\u7a7a\u4e2d\u843d\u4e0b\u6765\uff0c\u53c8\u843d\u5728\u6728\u5c4b\u91cc\uff0c\u843d\u5728\u4ed6\u539f\u6765\u5750\u7740\u7684\u90a3\u5f20\u6905\u5b50\u4e0a\uff0c\u8863\u895f\u5df2\u88ab\u5251\u950b\u5212\u7834\u4e86\u4e24\u6761\u88c2\u53e3\uff0c\u5750\u4e0b\u53bb\u5f88\u4e45\u4e4b\u540e\uff0c\u8fd8\u5728\u4e0d\u505c\u5598\u606f\u3002 \u3000\u3000\u8fd9\u8fb9\u6811\u836b\u4e2d\u65e0\u7591\u4e5f\u4f0f\u7740\u7edd\u9876\u9ad8\u624b\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u867d\u7136\u51fb\u9000\u4e86\u5c0f\u65b9\uff0c\u5374\u6ca1\u6709\u518d\u4e58\u80dc\u8feb\u51fb\u3002 \u3000\u3000\u53ea\u8981\u5c0f\u65b9\u4e00\u9000\u56de\u6728\u5c4b\uff0c\u4ed6\u4eec\u7684\u653b\u51fb\u5c31\u7acb\u523b\u505c\u6b62\uff0c\u770b\u6765\u4ed6\u4eec\u53ea\u4e0d\u8fc7\u60f3\u8981\u5c0f\u65b9\u7559\u5728\u6728\u5c4b\u91cc\uff0c\u5e76\u4e0d\u60f3\u53d6\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u6765\u7684\u7a76\u7adf\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u7a76\u7adf\u662f\u60f3\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u5929\u8272\u66f4\u6697\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u8fd8\u662f\u9762\u5bf9\u9762\u5750\u5728\u90a3\u91cc\uff0c\u6811\u836b\u4e2d\u7684\u4eba\u5df2\u7ecf\u770b\u4e0d\u89c1\u4ed6\u4eec\u7684\u8138\u8272\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u81ea\u5df1\u77e5\u9053\u81ea\u5df1\u7684\u8138\u8272\u4e00\u5b9a\u5f88\u4e0d\u597d\u770b\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u65f6\u5019\u5df2\u7ecf\u4e0d\u65e9\u4e86\uff0c\u4e00\u5929\u8fc7\u5f97\u771f\u5feb\u3002\u201d\u5979\u95ee\u5c0f\u65b9\uff0c\u201c\u4f60\u8fd8\u60f3\u4e0d\u60f3\u51fa\u53bb\u5c38 \u3000\u3000\u5c0f\u65b9\u6447\u5934\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u90a3\u4e48\u6211\u4eec\u4e0d\u5982\u8fd8\u662f\u65e9\u70b9\u7761\u5427\uff01\u201d \u3000\u3000\u201c\u597d\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u4f60\u7761\u5e8a\uff0c\u6211\u7761\u5730\u677f\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff1a\u201c\u6211\u7761\u5e8a\uff0c\u4f60\u4e5f\u7761\u5e8a\u3002\u201d \u3000\u3000\u5979\u7684\u53e3\u6c14\u5f88\u575a\u51b3\uff0c\u800c\u4e14\u5df2\u7ecf\u8d70\u4e86\u8fc7\u53bb\uff0c\u628a\u5c0f\u65b9\u4ece\u6905\u5b50\u4e0a\u62c9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u7684\u624b\u51b0\u51b7\uff0c\u800c\u4e14\u5728\u53d1\u6296\u3002 \u3000\u3000\u5979\u662f\u4ed6\u751f\u6b7b\u4e4b\u4ea4\u7684\u672a\u6765\u59bb\u5b50\uff0c\u6697\u4e2d\u4e5f\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u53cc\u773c\u775b\u5728\u770b\u7740\u4ed6\u4eec\uff0c\u5982\u679c\u662f\u522b\u4eba\uff0c\u4e00\u5b9a\u4f1a\u907f\u5acc\uff0c\u4e00\u5b9a\u4f1a\u575a\u6301\u8981\u7761\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u662f\u522b\u4eba\uff0c\u5c0f\u65b9\u5c31\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u597d\u5427\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u4f60\u7761\u5e8a\uff0c\u6211\u4e5f\u7761\u5e8a\u3002\u201d \u3000\u3000\u6728\u5c4b\u91cc\u53ea\u6709\u4e00\u5f20\u5e8a\uff0c\u5f88\u5927\u7684\u4e00\u5f20\u5e8a\uff0c\u4ed6\u4eec\u7761\u4e0b\u53bb\uff0c\u8fd8\u662f\u597d\u50cf\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\uff0c\u4ed6\u4eec\u8fd8\u662f\u5728\u4e00\u4e2a\u5c0f\u800c\u6e29\u6696\u7684\u6728\u5c4b\u91cc\uff0c\u95e8\u7a97\u90fd\u662f\u5f00\u7740\u7684\uff0c\u7edd\u4e0d\u4f1a\u6709\u4eba\u6765\u4fb5\u72af\u9a9a\u6270\u4ed6\u4eec\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u5fc3\u91cc\u90fd\u77e5\u9053\uff0c\u6240\u6709\u7684\u4e8b\u90fd\u5df2\u7ecf\u4e0d\u4e00\u6837\u4e86\uff0c\u4ed6\u4eec\u7684\u6027\u547d\u968f\u65f6\u90fd\u53ef\u80fd\u50cf\u9152\u676f\u4e00\u6837\u88ab\u51fb\u788e\uff0c\u4ed6\u4eec\u80fd\u6d3b\u5230\u4ec0\u4e48\u65f6\u5019\u8fde\u4ed6\u4eec\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u8737\u66f2\u5728\u4e00\u5e8a\u7528\u5927\u5e03\u7f1d\u6210\u7684\u8584\u88ab\u91cc\uff0c\u4ed6\u4eec\u7684\u8eab\u5b50\u8ddd\u79bb\u5f88\u8fdc\uff0c\u5934\u5374\u9760\u5f97\u5f88\u8fd1\uff0c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u77e5\u9053\u5bf9\u65b9\u4e00\u5b9a\u6709\u5f88\u591a\u8bdd\u8981\u8bf4\u3002 \u3000\u3000\u5148\u5f00\u53e3\u7684\u662f\u201c\u9633\u5149\u201d\uff0c\u5979\u538b\u4f4e\u58f0\u97f3\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u53d7\u4f24\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5c0f\u65b9\u8033\u8bed\uff0c\u201c\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e0d\u60f3\u8981\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u4eec\u60f3\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u73b0\u5728\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4ece\u6765\u90fd\u4e0d\u4f1a\u6cc4\u6c14\u7684\uff0c\u4ed6\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u5c31\u8868\u793a\u4ed6\u4eec\u5df2\u5b8c\u5168\u6ca1\u6709\u673a\u4f1a\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u52c9\u5f3a\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u53cd\u6b63\u4ed6\u4eec\u6682\u65f6\u8fd8\u4e0d\u4f1a\u51fa\u624b\u7684\uff0c\u6211\u4eec\u4e0d\u59a8\u5148\u7761\u4e00\u4e0b\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u80fd\u7761\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4eec\u4e0d\u80fd\u7559\u5728\u8fd9\u91cc\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u80fd\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u51b2\u51fa\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u4e00\u5b9a\u8981\u51b2\u51fa\u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u5df2\u7ecf\u8bd5\u8fc7\u3002\u201d\u9633\u5149\u9053\uff0c\u201c\u4f60\u81ea\u5df1\u4e5f\u77e5\u9053\u6211\u4eec\u7684\u673a\u4f1a\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u5f88\u53ef\u80fd\u8fde\u4e00\u6210\u673a\u4f1a\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u4eec\u5c82\u975e\u662f\u9001\u6b7b\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u8981\u6b7b\uff0c\u6211\u4eec\u4e5f\u5f97\u8981\u51b2\u51fa\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5c31\u7b97\u8981\u6b7b\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4eec\u7edd\u4e0d\u80fd\u8fde\u7d2f\u535c\u9e70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u53e3\u6c14\u575a\u51b3\uff0c\u201c\u4ed6\u5f88\u53ef\u80fd\u8fd8\u7559\u5728\u9644\u8fd1\u3002\u8fd9\u4e9b\u4eba\u65e2\u4e0d\u51fa\u624b\uff0c\u53c8\u4e0d\u653e\u6211\u4eec\u8d70\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u5229\u7528\u6211\u4eec\u8bf1\u535c\u9e70\u4eba\u4f0f\u3002\u5982\u679c\u535c\u9e70\u8fd8\u5728\u9644\u8fd1\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u8ba9\u6211\u4eec\u88ab\u56f0\u6b7b\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6c89\u9ed8\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u8f7b\u8f7b\u5730\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4ed6\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u76ef\u7740\u5979\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u95ee\uff0c\u201c\u6211\u4eec\u80fd\u4e0d\u80fd\u8ba9\u4ed6\u6765\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6c89\u9ed8\u3002 \u3000\u3000\u8fd9\u95ee\u9898\u53c8\u662f\u4e2a\u6839\u672c\u4e0d\u5fc5\u56de\u7b54\u7684\u95ee\u9898\u3002\u5979\u51dd\u89c6\u7740\u5c0f\u65b9\uff0c\u773c\u775b\u91cc\u5df2\u7ecf\u6709\u4e86\u6cea\u5149\u3002 \u3000\u3000\u5979\u7edd\u4e0d\u4f1a\u4e3a\u81ea\u5df1\u4f24\u5fc3\uff0c\u53ef\u662f\u4e3a\u4e86\u4e00\u4e2a\u5b81\u6b7b\u4e5f\u4e0d\u613f\u670b\u53cb\u88ab\u4f24\u5bb3\u7684\u4eba\uff0c\u5979\u7684\u5fc3\u5df2\u788e\u4e86\u3002 \u3000\u3000\u2014\u2014\u5c0f\u65b9\u4e0d\u80fd\u6b7b\uff0c\u7edd\u4e0d\u80fd\u6b7b\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u535c\u9e70\u5462\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u95ed\u4e0a\u773c\u775b\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u5ffd\u7136\u4f38\u51fa\u624b\uff0c\u7d27\u7d27\u5730\u62b1\u4f4f\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u51b3\u5fc3\u8981\u8fd9\u6837\u505a\uff0c\u6211\u4eec\u5c31\u8fd9\u4e48\u505a\u3002\u201d\u5979\u8bf4\uff0c\u201c\u4e0d\u7ba1\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff0c\u6211\u90fd\u8ddf\u7740\u4f60\u3002\u4f60\u8981\u4e0b\u5730\u72f1\uff0c\u6211\u4e5f\u4e0b\u5730\u72f1\u3002\u201d \u3000\u3000\u591c\u8272\u6e10\u6df1\u3002 \u3000\u3000\u5c0f\u65b9\u9759\u9759\u5730\u8eba\u7740\uff0c\u8ba9\u201c\u9633\u5149\u201d\u7d27\u7d27\u5730\u62e5\u62b1\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u52a8\uff0c\u4e5f\u6ca1\u6709\u8d1f\u759a\u7684\u611f\u89c9\uff0c\u56e0\u4e3a\u4ed6\u4e86\u89e3\u201c\u9633\u5149\u201d\u7684\u611f\u60c5\uff0c\u4e5f\u4e86\u89e3\u4ed6\u81ea\u5df1\u7684\u3002\u4ed6\u4eec\u867d\u7136\u5728\u62e5\u62b1\uff0c\u53ef\u662f\u5fc3\u91cc\u60f3\u7740\u7684\u5374\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u968f\u65f6\u90fd\u53ef\u4ee5\u4e3a\u4ed6\u4eec\u53bb\u6b7b\uff0c\u4e5f\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u53bb\u6b7b\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec\u5bf9\u4f60\u7684\u611f\u60c5\uff1f\u5ffd\u7136\u95f4\uff0c\u4e00\u6761\u4eba\u5f71\u81ea\u6697\u5904\u4e2d\u98de\u51fa\uff0c\u6a2a\u7a7a\u98de\u8fc7\u5341\u4f59\u4e08\uff0c\u53c8\u5ffd\u7136\u843d\u4e0b\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u843d\u5728\u8fd9\u4e2a\u5df2\u7ecf\u4e0d\u5b58\u5728\u7684\u6728\u5c4b\u91cc\uff0c\u843d\u5728\u4ed6\u4eec\u7684\u5e8a\u8fb9\uff0c\u4e00\u843d\u4e0b\u4e4b\u540e\uff0c\u5c45\u7136\u5c31\u4e0d\u518d\u6709\u52a8\u9759\u3002\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u6765\u5e72\u4ec0\u4e48\uff1f\u96be\u9053\u4ed6\u4eec\u7684\u4ec7\u654c\u5df2\u51b3\u5b9a\u4e0d\u518d\u7b49\u5f85\uff0c\u5df2\u51b3\u5b9a\u8981\u5bf9\u4ed6\u4eec\u51fa\u624b\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u770b\u7740\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u6211\u4eec\u597d\u50cf\u6709\u5ba2\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e0d\u7406\u4ed6\u884c\u4e0d\u884c\uff1f\u201d\u201c\u9633\u5149\u201d\u6545\u610f\u95ee\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7406\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8fde\u95e8\u90fd\u4e0d\u6572\u5c31\u95ef\u8fdb\u6765\uff0c\u8fde\u4e00\u70b9\u793c\u8c8c\u90fd\u6ca1\u6709\uff0c\u8fd9\u79cd\u4eba\u7406\u4ed6\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5f00\u59cb\u7b11\u7684\u65f6\u5019\uff0c\u201c\u9633\u5149\u201d\u7684\u624b\u4e5f\u5df2\u677e\u5f00\uff0c\u4ed6\u7684\u8eab\u5b50\u5df2\u63a0\u8d77\uff0c\u51c6\u5907\u51cc\u7a7a\u4e0b\u51fb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u51fa\u624b\uff0c\u56e0\u4e3a\u4ed6\u770b\u6e05\u4e86\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u6839\u672c\u6ca1\u6709\u95e8\uff0c\u5c31\u7b97\u6709\u95e8\uff0c\u8fd9\u4e2a\u4eba\u4e5f\u4e0d\u4f1a\u6572\u95e8\u7684\u3002 \u3000\u3000\u6b7b\u4eba\u4e0d\u4f1a\u6572\u95e8\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u5934\u9885\u5df2\u5782\u4e0b\uff0c\u8f6f\u8f6f\u5730\u6302\u5728\u8116\u5b50\u4e0a\uff0c\u5c31\u50cf\u662f\u4e2a\u88ab\u987d\u7ae5\u62d7\u65ad\u4e86\u8116\u5b50\u7684\u6ce5\u5a03\u5a03\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u91cc\u867d\u7136\u65e0\u706f\u65e0\u6708\uff0c\u5c0f\u65b9\u8fd8\u662f\u4e00\u773c\u5c31\u770b\u51fa\u4ed6\u662f\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u2014\u2014\u662f\u8c01\u62d7\u65ad\u4e86\u4ed6\u7684\u8116\u5b50\uff1f\u4e3a\u4ec0\u4e48\u8981\u628a\u4ed6\u629b\u5230\u8fd9\u91cc\u6765\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u8df3\u5ffd\u7136\u52a0\u5feb\uff0c\u5df2\u7ecf\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u5341\u7ae0\u3000\u6740\u673a\u56db\u4f0f \u7b2c\u4e8c\u5341\u7ae0\u3000\u6740\u673a\u56db\u4f0f \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u53e6\u5916\u4e00\u4e2a\u65b9\u5411\u7684\u6697\u6797\u4e2d\uff0c\u5ffd\u7136\u6709\u4e00\u6761\u4eba\u5f71\u98de\u51fa\uff0c\u6a2a\u7a7a\u98de\u8fc7\u5341\u4f59\u4e08\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u843d\u5728\u8fd9\u4e2a\u5df2\u7ecf\u4e0d\u5b58\u5728\u7684\u6728\u5c4b\u91cc\uff0c\u5934\u9885\u4e5f\u540c\u6837\u8f6f\u8f6f\u5730\u6302\u5728\u8116\u5b50\u4e0a\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u4e00\u9aa8\u788c\u7ffb\u8eab\u8dc3\u8d77\uff0c\u4e00\u628a\u63e1\u7d27\u5c0f\u65b9\u7684\u624b\u3002\u4e24\u4e2a\u4eba\u7684\u5fc3\u90fd\u8df3\u5f97\u5f88\u5feb\uff0c\u773c\u775b\u91cc\u90fd\u53d1\u51fa\u4e86\u5149\u3002 \u3000\u3000\u6697\u6797\u4e2d\u5df2\u4f20\u51fa\u51b7\u7b11\u3002 \u3000\u3000\u201c\u679c\u7136\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u9601\u4e0b\u65e2\u7136\u5df2\u7ecf\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u6765\u8ddf\u5927\u5bb6\u89c1\u89c1\u9762\uff1f\u201d \u3000\u3000\u51b7\u7b11\u58f0\u4e2d\uff0c\u5939\u6742\u7740\u8863\u8882\u5e26\u98ce\u58f0\u3001\u6728\u53f6\u6298\u65ad\u58f0\uff0c\u9690\u7ea6\u8fd8\u53ef\u4ee5\u89c1\u5230\u4eba\u5f71\u95ea\u52a8\u3002 \u3000\u3000\u8fdc\u5904\u53c8\u6709\u4eba\u8f7b\u53f1\uff1a\u201c\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u53f1\u58f0\u521a\u54cd\uff0c\u6697\u6797\u4e2d\u5c31\u6709\u4e09\u6761\u4eba\u5f71\u51b2\u5929\u98de\u8d77\uff0c\u5411\u90a3\u8fb9\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u548c\u5c0f\u65b9\u7684\u5fc3\u8df3\u5f97\u66f4\u5feb\uff0c\u4ed6\u4eec\u5f53\u7136\u5df1\u731c\u51fa\u6765\u7684\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u6697\u6797\u4e2d\u4eba\u5f71\u5154\u8d77\u9e43\u843d\uff0c\u5168\u90e8\u5f80\u90a3\u4e2a\u65b9\u5411\u6251\u8fc7\u53bb\uff0c\u8863\u8882\u5e26\u98ce\u58f0\u4e2d\u5939\u6742\u7740\u4e00\u58f0\u58f0\u53f1\u559d\u3002 \u3000\u3000\u201c\u59d3\u535c\u7684\uff0c\u4f60\u8fd8\u60f3\u5f80\u54ea\u91cc\u8d70\uff1f\u201d \u3000\u3000\u201c\u4f60\u5c31\u7559\u4e0b\u547d\u6765\u5427\uff01\u201d \u3000\u3000\u6765\u7684\u65e0\u7591\u662f\u535c\u9e70\u3002 \u3000\u3000\u4ed6\u6545\u610f\u663e\u9732\u8eab\u5f62\uff0c\u5c06\u6697\u6797\u4e2d\u7684\u57cb\u4f0f\u8bf1\u5f00\uff0c\u8ba9\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u4e58\u673a\u8131\u8d70\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u5728\u770b\u7740\u5c0f\u65b9\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u4e8b\u5979\u90fd\u8981\u5c0f\u65b9\u505a\u51b3\u5b9a\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff0c\u6211\u5c31\u5230\u54ea\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u8fde\u4e00\u53e5\u8bdd\u90fd\u4e0d\u518d\u8bf4\uff0c\u4e24\u4e2a\u4eba\u540c\u65f6\u79fb\u52a8\u8eab\u5f62\uff0c\u4e5f\u5f80\u90a3\u4e2a\u65b9\u5411\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u77e5\u9053\u6697\u6797\u4e2d\u6b65\u6b65\u90fd\u6709\u6740\u673a\uff0c\u53ef\u662f\u4ed6\u4eec\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u7e41\u661f\u6ee1\u5929\uff0c\u661f\u5149\u90fd\u7167\u4e0d\u8fdb\uff0c\u8302\u5bc6\u7684\u6728\u53f6\uff0c\u6728\u53f6\u867d\u7136\u5df2\u67af\u9ec4\uff0c\u5374\u8fd8\u6ca1\u6709\u51cb\u843d\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u662f\u770b\u4e0d\u89c1\u4eba\uff0c\u8fde\u8fdc\u5904\u7684\u547c\u559d\u58f0\u90fd\u5df2\u6e10\u6e10\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u6811\u6797\u662f\u5728\u7fa4\u5c71\u5408\u62b1\u7684\u4e00\u4e2a\u5c71\u8c37\u76c6\u5730\u91cc\uff0c\u5c71\u52bf\u5230\u4e86\u8fd9\u91cc\u7a81\u7136\u4f4e\u9677\uff0c\u5730\u6c14\u6781\u6696\u61ca\uff0c\u8fde\u98ce\u90fd\u662f\u6696\u7684\uff0c\u6240\u4ee5\u73b0\u5728\u867d\u7136\u5df2\u7ecf\u662f\u521d\u51ac\uff0c\u6728\u53f6\u4ecd\u672a\u51cb\u843d\u3002 \u3000\u3000\u53ef\u662f\u5730\u4e0a\u4ecd\u7136\u6709\u843d\u53f6\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u4eba\u5f80\u5f80\u4f1a\u56e0\u4e3a\u5f88\u591a\u79cd\u539f\u56e0\u8981\u79bb\u5f00\u4ed6\u7684\u5bb6\u4e00\u6837\uff0c\u53f6\u5b50\u4e5f\u5f80\u5f80\u4f1a\u56e0\u4e3a\u5f88\u591a\u79cd\u539f\u56e0\u800c\u79bb\u5f00\u5b83\u7684\u679d\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u542c\u89c1\u843d\u53f6\u4e0a\u6709\u4efb\u4f55\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u201c\u9633\u5149\u201d\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u542c\u89c1\u4e86\u4e00\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u4eec\u542c\u89c1\u4e00\u4e2a\u4eba\u5728\u54ed\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u54ed\uff0c\u5728\u751f\u7684\u65f6\u5019\u4f1a\u54ed\uff0c\u5728\u6b7b\u7684\u65f6\u5019\u4e5f\u4f1a\u54ed\uff0c\u5728\u751f\u4e0e\u6b7b\u4e4b\u95f4\u90a3\u4e2a\u9636\u6bb5\u66f4\u4f1a\u5e38\u5e38\u54ed\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u53ea\u6709\u5728\u60b2\u4f24\u75db\u82e6\u5931\u610f\u65f6\u624d\u4f1a\u54ed\uff0c\u6709\u4e9b\u4eba\u5728\u5174\u594b\u6fc0\u52a8\u6b22\u4e50\u65f6\u4e5f\u4f1a\u54ed\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff0c\u4e00\u4e2a\u4eba\u5728\u4ed6\u4e00\u751f\u4e2d\u6700\u65e0\u6cd5\u907f\u514d\u53bb\u542c\u7684\u4e24\u79cd\u58f0\u97f3\uff0c\u9664\u4e86\u7b11\u58f0\u5916\uff0c\u5c31\u662f\u54ed\u58f0\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u54ed\u58f0\u7edd\u4e0d\u80fd\u7b97\u662f\u4e00\u79cd\u5947\u602a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u8fd9\u79cd\u65f6\u5019\uff0c\u65e0\u8bba\u8c01\u542c\u89c1\u6709\u4eba\u5728\u54ed\uff0c\u90fd\u4f1a\u89c9\u5f97\u5947\u602a\u6781\u4e86\u3002 \u3000\u3000\u6700\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e2a\u6b63\u5728\u54ed\u7684\u4eba\uff0c\u53c8\u662f\u4e2a\u8c01\u90fd\u60f3\u4e0d\u5230\u4ed6\u4f1a\u54ed\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u542c\u89c1\u54ed\u58f0\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u770b\u5230\u4e86\u8fd9\u4e2a\u6b63\u5728\u54ed\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8d6b\u7136\u7adf\u662f\u80e1\u5927\u638c\u67dc\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u89c1\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u6b63\u5750\u5728\u4e00\u68f5\u5f88\u9ad8\u5927\u7684\u53e4\u6811\u4e0b\uff0c\u54ed\u5f97\u5c31\u50cf\u662f\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u5982\u679c\u4ed6\u4eec\u6ca1\u6709\u4eb2\u773c\u770b\u89c1\uff0c\u4ed6\u4eec\u7edd\u5bf9\u60f3\u4e0d\u5230\u540d\u9707\u6c5f\u6e56\u7684\u201c\u4e09\u5b9d\u5802\u201d\u4e3b\u4eba\u5c45\u7136\u4f1a\u5728\u8fd9\u79cd\u5730\u65b9\u3001\u8fd9\u4e2a\u65f6\u5019\uff0c\u5750\u5728\u4e00\u68f5\u6811\u4e0b\u9762\u50cf\u5b69\u5b50\u4e00\u6837\u62b1\u5934\u75db\u54ed\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u4eb2\u773c\u770b\u89c1\u4e86\u3002 \u3000\u3000\u80e1\u5927\u638c\u67dc\u597d\u50cf\u6ca1\u6709\u770b\u89c1\u4ed6\u4eec\u3002 \u3000\u3000\u4ed6\u54ed\u5f97\u771f\u4f24\u5fc3\uff0c\u597d\u50cf\u5df2\u7ecf\u4f24\u5fc3\u5f97\u6ca1\u6cd5\u5b50\u518d\u53bb\u6ce8\u610f\u522b\u4eba\uff0c\u53ef\u60dc\u4ed6\u4eec\u5374\u6ca1\u6cd5\u5b50\u4e0d\u53bb\u6ce8\u610f\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u89c1\u8fc7\u4ed6\uff0c\u90fd\u8ba4\u5f97\u4ed6\uff0c\u90fd\u77e5\u9053\u4ed6\u662f\u8c01\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u4eec\u5047\u88c5\u6ca1\u6709\u6ce8\u610f\u4ed6\uff0c\u5047\u88c5\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u4ed6\u4eec\u51b3\u5b9a\u5c31\u8fd9\u6837\u4ece\u4ed6\u7684\u9762\u524d\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u6709\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u5927\u638c\u67dc\u5ffd\u7136\u4ece\u6811\u4e0b\u4e00\u8dc3\u800c\u8d77\uff0c\u6321\u4f4f\u4e86\u4ed6\u4eec\u7684\u53bb\u8def\uff0c\u8138\u4e0a\u867d\u7136\u8fd8\u6709\u6cea\u75d5\uff0c\u5374\u5df2\u7ecf\u4e0d\u518d\u54ed\u4e86\uff0c\u773c\u775b\u867d\u7136\u8fd8\u662f\u7ea2\u7ea2\u7684\uff0c\u5374\u5df2\u7ecf\u53d1\u51fa\u4e86\u72e1\u72d0\u822c\u7684\u5149\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u4ed6\u4eec\uff1a\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u770b\u770b\u201c\u9633\u5149\u201d\uff0c\u201c\u9633\u5149\u201d\u770b\u770b\u5c0f\u65b9\uff0c\u6545\u610f\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u662f\u3002\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u51b7\u7b11\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u4eba\uff0c\u53ef\u662f\u4f60\u4eec\u770b\u89c1\u6709\u4eba\u54ed\u5f97\u8fd9\u4e48\u4f24\u5fc3\uff0c\u5c45\u7136\u80fd\u5047\u88c5\u6ca1\u770b\u89c1\uff01\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u4e5f\u51b7\u7b11\u3002 \u3000\u3000\u201c\u5c31\u7b97\u6211\u4eec\u770b\u89c1\u4e86\u53c8\u600e\u6837\uff1f\u96be\u9053\u4f60\u8981\u6211\u4eec\u5750\u4e0b\u6765\u966a\u4f60\u54ed\uff1f\u201d\u5979\u8bf4\u5f97\u7406\u76f4\u6c14\u58ee\uff0c\u201c\u4f60\u5728\u8fd9\u91cc\u54ed\uff0c\u8ddf\u6211\u4eec\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u5173\u7cfb\u3002\u201d\u80e1\u5927\u638c\u67dc\u5c45\u7136\u4e5f\u7406\u76f4\u6c14\u58ee\u4ed6\u8bf4\uff0c\u201c\u5c31\u662f\u4e3a\u4e86\u4f60\u4eec\uff0c\u6211\u624d\u4f1a\u54ed\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4e86\u6211\u4eec\u53f6\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u4f60\u600e\u4e48\u4f1a\u4e3a\u4e86\u6211\u4eec\u54ed\uff1f\u201d \u3000\u3000\u80e1\u5927\u638c\u67dc\u7684\u6837\u5b50\u770b\u6765\u66f4\u4f24\u5fc3\u3002 \u3000\u3000\u201c\u6211\u8fd9\u4e00\u8f88\u5b50\uff0c\u53ea\u559c\u6b22\u8fc7\u4e00\u4e2a\u5973\u4eba\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u627e\u4e86\u5979\u5f88\u4e45\uff0c\u7b49\u6211\u627e\u5230\u5979\u7684\u65f6\u5019\uff0c\u5979\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u88ab\u4f60\u4eec\u6d3b\u6d3b\u540a\u6b7b\u7684\uff01\u201d\u80e1\u5927\u638c\u67dc\u60b2\u4f24\u53eb\u9053\uff0c\u201c\u88ab\u4f60\u4eec\u540a\u5728\u4e00\u68f5\u6811\u4e0a\uff0c\u6d3b\u6d3b\u540a\u6b7b\u7684\uff01\u201d \u3000\u3000\u4ed6\u72fc\u72e0\u5730\u76ef\u7740\u5c0f\u65b9\u597d\u4e00\u4f1a\uff1a\u201c\u6211\u77e5\u9053\u4f60\u59d3\u65b9\uff0c\u53eb\u505a\u8981\u547d\u7684\u5c0f\u65b9\uff0c\u4f60\u60f3\u8d56\u4e5f\u8d56\u4e0d\u6389\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u6709\u70b9\u660e\u767d\u4e86\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u7684\u90a3\u4e2a\u5973\u4eba\u662f\u4e0d\u662f\u67f3\u5206\u5206\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u771f\u4ee5\u4e3a\u662f\u6211\u6740\u4e86\u5979\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5982\u679c\u6211\u8bf4\u4e0d\u662f\u6211\uff0c\u4f60\u5f53\u7136\u4e00\u5b9a\u4e0d\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u770b\u51fa\u80e1\u5927\u638c\u67dc\u5df2\u7ecf\u51b3\u5fc3\u8981\u4ed6\u7684\u547d\uff0c\u65e0\u8bba\u8c01\u90fd\u5e94\u8be5\u80fd\u591f\u770b\u51fa\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u2014\u2014\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\u3002 \u3000\u3000\u80e1\u5927\u638c\u67dc\u7684\u53cc\u81c2\u5df2\u5c55\uff0c\u59ff\u52bf\u5947\u79d8\u800c\u602a\u5f02\uff0c\u867d\u7136\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u6697\u5668\u662f\u7528\u4ec0\u4e48\u624b\u6cd5\u6253\u51fa\u6765\u7684\uff0c\u4f46\u662f\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\uff0c\u53ea\u8981\u4ed6\u7684\u6697\u5668\u4e00\u6253\u51fa\u6765\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u591f\u7b11\u5f97\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5ffd\u7136\u7b11\u4e86\u51fa\u6765\uff0c\u4e0d\u4f46\u7b11\u4e86\u51fa\u6765\uff0c\u800c\u4e14\u5531\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u5531\u7684\u5c31\u662f\u90a3\u5929\u5979\u5728\u90a3\u5e72\u67af\u7684\u7eff\u6d32\u4e2d\u6c99\u4e18\u540e\u542c\u5230\u7684\u90a3\u9996\u6b4c\u8c23\uff1a \u3000\u3000\u201c\u71d5\u5317\u6709\u4e2a\u4e09\u5b9d\u5802\uff0c \u3000\u3000\u540d\u6c14\u8bf4\u6765\u54cd\u5f53\u5f53\u3002 \u3000\u3000\u4e09\u5b9d\u5802\u91cc\u6709\u4e09\u5b9d\uff0c \u3000\u3000\u8c01\u89c1\u8c01\u906d\u6b83\uff0c\u4e24\u773c\u6cea\u6c6a\u6c6a\u3002\u201d \u3000\u3000\u5979\u7684\u8bb0\u5fc6\u529b\u5b9e\u5728\u597d\u6781\u4e86\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u5531\u9519\uff0c\u800c\u4e14\u5531\u5f97\u5c31\u50cf\u90a3\u5c0f\u5973\u5b69\u4e00\u6837\u3002\u5979\u8fd8\u6ca1\u6709\u5531\u5b8c\uff0c\u80e1\u5927\u638c\u67dc\u8138\u8272\u5df2\u6539\u53d8\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f\u6211\u4e0d\u77e5\u9053\u8c01\u77e5\u9053\uff1f\u201d\u201c\u9633\u5149\u201d\u751c\u7b11\uff0c\u201c\u5176\u5b9e\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u6211\u662f\u8c01\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5e94\u8be5\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4f60\u518d\u4ed4\u7ec6\u770b\u770b\u6211\u662f\u8c01\uff1f\u201d\u5979\u7b11\u5f97\u597d\u50cf\u4e5f\u6709\u70b9\u50cf\u90a3\u68b3\u7740\u5341\u516d\u516b\u6761\u5c0f\u8fab\u5b50\u7684\u5c0f\u5973\u5b69\u5b50\uff0c\u53ea\u5dee\u624b\u91cc\u5c11\u4e86\u4e00\u6761\u96ea\u767d\u53ef\u7231\u7684\u72ee\u5b50\u72d7\u3002 \u3000\u3000\u80e1\u5927\u638c\u67dc\u5403\u60ca\u5730\u770b\u7740\u5979\uff0c\u4e00\u6b65\u6b65\u5411\u540e\u9000\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u2018\u9634\u7075\uff0c\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u9053\uff1a\u201c\u4f60\u771f\u7684\u4ee5\u4e3a\u662f\u90a3\u4e2a\u74f6\u5b50\uff0c\u8fd8\u662f\u90a3\u4e2a\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5c0f\u65b9\u5df2\u62d4\u5251\u3002 \u3000\u3000\u4e00\u68f5\u5927\u6811\u7684\u6839\u90e8\uff0c\u5ffd\u7136\u95f4\u9732\u51fa\u4e86\u4e00\u4e2a\u95e8\u3002 \u3000\u3000\u90a3\u5f53\u7136\u4e0d\u80fd\u7b97\u662f\u4e00\u9053\u771f\u6b63\u7684\u95e8\uff0c\u53ea\u80fd\u7b97\u4e00\u4e2a\u6d1e\uff0c\u201c\u9633\u5149\u201d\u8ba4\u4e3a\u90a3\u662f\u95e8\uff0c\u53ea\u56e0\u4e3a\u91cc\u9762\u771f\u7684\u6709\u4e2a\u4eba\u94bb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u867d\u7136\u4e0d\u662f\u535c\u9e70\uff0c\u5374\u662f\u4ed6\u4eec\u7684\u670b\u53cb\u3002 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\uff01\u201d\u201c\u9633\u5149\u201d\u5fcd\u4e0d\u4f4f\u5927\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u770b\u89c1\u4ed6\uff0c\u4ed6\u4eec\u4e5f\u540c\u6837\u5174\u594b\u3002 \u3000\u3000\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4ec0\u4e48\u65f6\u5019\u4f1a\u51fa\u73b0\uff0c\u53ef\u662f\u4ed6\u6bcf\u6b21\u51fa\u73b0\u65f6\u90fd\u540c\u6837\u4ee4\u4eba\u5174\u594b\u3002 \u3000\u3000\u201c\u521a\u624d\u51fa\u624b\u7684\u4eba\u662f\u4f60\uff01\u201d \u3000\u3000\u201c\u662f\u6211\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u7b80\u5355\u5730\u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u4e00\u79cd\u5728\u4e00\u77ac\u95f4\u5c31\u53ef\u4ee5\u5c06\u4eba\u8116\u5b50\u62d7\u65ad\u7684\u624b\u52bf\uff0c\u867d\u7136\u975e\u5e38\u7b80\u5355\uff0c\u5374\u7edd\u5bf9\u6709\u6548\u3002 \u3000\u3000\u201c\u535c\u9e70\u5462\uff1f\u201d\u201c\u9633\u5149\u201d\u53c8\u95ee\u3002 \u3000\u3000\u201c\u6211\u6ca1\u6709\u770b\u89c1\u4ed6\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u4e5f\u5728\u627e\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8bf4\u5f97\u4e5f\u5f88\u6709\u628a\u63e1\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u4ed6\u7edd\u5bf9\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u7684\u7406\u7531\u662f\uff1a\u201c\u56e0\u4e3a\u90a3\u4e9b\u4eba\u4e5f\u5728\u627e\u4ed6\uff0c\u53ef\u89c1\u4ed6\u4eec\u4e5f\u77e5\u9053\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\uff1a\u201c\u65e0\u8bba\u8c01\u60f3\u8981\u535c\u9e70\u7684\u547d\u90fd\u5f88\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u4e5f\u7b11\u4e86\uff1a\u201c\u5982\u679c\u6709\u4eba\u60f3\u8981\u4f60\u7684\u547d\uff0c\u6050\u6015\u66f4\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u5979\u5bf9\u73ed\u5bdf\u5df4\u90a3\u4e5f\u540c\u6837\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u3001\u4ec0\u4e48\u5730\u65b9\uff0c\u4ed6\u90fd\u53ef\u4ee5\u4e3a\u81ea\u5df1\u627e\u5230\u4e00\u4e2a\u8eb2\u85cf\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e00\u4e2a\u522b\u4eba\u7edd\u5bf9\u627e\u4e0d\u5230\u7684\u5730\u65b9\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4ec0\u4e48\u60c5\u51b5\u4e0b\uff0c\u4ed6\u90fd\u4f1a\u5148\u4e3a\u81ea\u5df1\u7559\u4e0b\u4e00\u6761\u9000\u8def\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u4ee5\u4e3a\u4f60\u5df2\u7ecf\u9003\u51fa\u4e86\u6811\u6797\uff0c\u60f3\u4e0d\u5230\u4f60\u5374\u5728\u8fd9\u68f5\u6811\u5e95\u4e0b\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u96be\u602a\u535c\u9e70\u5e38\u8bf4\uff0c\u5982\u679c\u4f60\u60f3\u8eb2\u8d77\u6765\uff0c\u5929\u4e0b\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u627e\u5f97\u5230\u4f60\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5fae\u7b11\uff1a \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4f60\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8bf4\u6211\u662f\u6761\u8001\u72d0\u72f8\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u8001\u72d0\u72f8\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u7b11\u9053\uff0c\u201c\u4e24\u767e\u6761\u8001\u72d0\u72f8\u52a0\u8d77\u6765\u4e5f\u6bd4\u4e0d\u4e0a\u4f60\u3002\u201d \u3000\u3000\u521a\u624d\u5df2\u542c\u4e0d\u89c1\u7684\u4eba\u58f0\uff0c\u73b0\u5728\u53c8\u53ef\u4ee5\u542c\u89c1\u4e86\u3002\u521a\u624d\u5df2\u9000\u51fa\u6811\u6797\u7684\u5165\uff0c\u73b0\u5728\u4eff\u4f5b\u53c8\u9000\u4e86\u56de\u6765\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u76b1\u4e86\u76b1\u7709\u3002 \u3000\u3000\u201c\u4f60\u4eec\u5feb\u8eb2\u8fdb\u53bb\u3002\u201d\u4ed6\u6307\u7740\u6811\u4e0b\u7684\u5730\u6d1e\u8bf4\uff1a\u201c\u8fd9\u4e2a\u6d1e\u7edd\u5bf9\u53ef\u4ee5\u5bb9\u7eb3\u4e0b\u4f60\u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u7528\u4e0d\u7740\u66ff\u6211\u62c5\u5fc3\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u6709\u6cd5\u5b50\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f46\u662f\u4f60\u4eec\u4e00\u5b9a\u8981\u7b49\u6211\u56de\u6765\u4e4b\u540e\u624d\u80fd\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u5df2\u7ecf\u51c6\u5907\u8d70\u4e86\uff0c\u5ffd\u7136\u53c8\u8f6c\u8fc7\u8eab\uff1a\u201c\u6211\u8fd8\u8981\u4f60\u4eec\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u628a\u4f60\u4eec\u7a7f\u7684\u8863\u670d\u548c\u978b\u5b50\u90fd\u8131\u4e0b\u6765\u7ed9\u6211\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df2\u90a3\u6ca1\u6709\u89e3\u91ca\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff0c\u201c\u9633\u5149\u201d\u4e5f\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u80cc\u8f6c\u8eab\uff0c\u5f88\u5feb\u5730\u8131\u4e0b\u4e86\u5979\u7684\u84dd\u8272\u5916\u8863\u548c\u9774\u5b50\u3002\u5982\u679c\u73ed\u5bdf\u5df4\u90a3\u8fd8\u8981\u5979\u8131\u4e0b\u53bb\uff0c\u5979\u4e5f\u4e0d\u4f1a\u62d2\u7edd\u3002 \u3000\u3000\u5979\u4e0d\u662f\u90a3\u79cd\u626d\u626d\u634f\u634f\u7684\u5973\u4eba\u3002 \u3000\u3000\u5979\u76f8\u4fe1\u73ed\u5bdf\u5df4\u90a3\u8fd9\u6837\u505a\u4e00\u5b9a\u662f\u6709\u7406\u7531\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u5c06\u5916\u8863\u8131\u4e0b\u3002 \u3000\u3000\u201c\u8fd9\u6837\u591f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u201c\u591f\u4e86\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4f60\u8fd8\u5f97\u628a\u4f60\u7684\u5251\u4ea4\u7ed9\u6211\uff01\u201d \u3000\u3000\u5bf9\u4e00\u4e2a\u5b66\u5251\u7684\u4eba\u6765\u8bf4\uff0c\u4e16\u4e0a\u53ea\u6709\u4e24\u6837\u4e1c\u897f\u7edd\u4e0d\u80fd\u8f7b\u6613\u4ea4\u7ed9\u522b\u4eba\u7684\u3002 \u3000\u3000\u2014\u2014\u4ed6\u7684\u5251\uff0c\u4ed6\u7684\u59bb\u5b50\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u6beb\u4e0d\u72b9\u8c6b\u5c31\u5c06\u81ea\u5df1\u7684\u5251\u4ea4\u7ed9\u4e86\u73ed\u5bdf\u5df4\u90a3\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u548c\u201c\u9633\u5149\u201d\u4e00\u6837\u4fe1\u4efb\u4ed6\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7528\u529b\u62cd\u4e86\u62cd\u5c0f\u65b9\u7684\u80a9\uff1a\u201c\u4f60\u4fe1\u4efb\u6211\uff0c\u4f60\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u624d\u628a\u5c0f\u65b9\u5f53\u4f5c\u670b\u53cb\uff1a\u201c\u6211\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\u7684\u3002\u201d \u3000\u3000\u8fd9\u5730\u6d1e\u7684\u786e\u53ef\u4ee5\u5bb9\u7eb3\u4e0b\u4e24\u4e2a\u4eba\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e24\u4e2a\u4eba\u5982\u679c\u8fd8\u60f3\u4fdd\u6301\u8ddd\u79bb\uff0c\u4e0d\u53bb\u63a5\u89e6\u5230\u5bf9\u65b9\u7684\u8eab\u5b50\uff0c\u5c31\u4e0d\u592a\u5bb9\u6613\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5c3d\u91cf\u628a\u81ea\u5df1\u7684\u8eab\u5b50\u5f80\u540e\u7f29\u3002 \u3000\u3000\u4ed6\u4eec\u8eab\u4e0a\u867d\u7136\u8fd8\u7a7f\u7740\u8863\u670d\uff0c\u53ef\u662f\u4e24\u4e2a\u4eba\u7684\u8863\u670d\u90fd\u5df2\u7ecf\u5f88\u5355\u8584\u3002 \u3000\u3000\u4e00\u4e2a\u50cf\u201c\u9633\u5149\u201d\u8fd9\u6837\u7684\u5973\u5b69\u5b50\uff0c\u8eab\u4e0a\u53ea\u7a7f\u7740\u8fd9\u4e48\u6837\u4e00\u4ef6\u5355\u8584\u7684\u8863\u670d\uff0c\u4e24\u4e2a\u4eba\u7684\u8ddd\u79bb\u4e4b\u8fd1\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u201c\u53cc\u9ec4\u86cb\u201d\u91cc\u7684\u4e24\u4e2a\u86cb\u9ec4\u3002 \u3000\u3000\u53ea\u8981\u7a0d\u5fae\u6709\u4e00\u70b9\u60f3\u50cf\u529b\u7684\u4eba\uff0c\u90fd\u5e94\u8be5\u80fd\u60f3\u5230\u4ed6\u4eec\u73b0\u5728\u7684\u60c5\u51b5\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u6709\u5c3d\u91cf\u628a\u8eab\u5b50\u5f80\u540e\u7f29\uff0c\u53ea\u53ef\u60dc\u540e\u9762\u80fd\u591f\u8ba9\u4ed6\u9000\u7f29\u7684\u5730\u65b9\u5df2\u4e0d\u591a\u3002 \u3000\u3000\u5730\u6d1e\u91cc\u867d\u7136\u6f6e\u6e7f\u9634\u6697\uff0c\u201c\u9633\u5149\u201d\u7684\u547c\u5438\u5374\u82ac\u82b3\u6e29\u67d4\u5982\u6625\u98ce\u3002 \u3000\u3000\u5bf9\u4e00\u4e2a\u8840\u6c14\u65b9\u521a\u7684\u5e74\u9752\u7537\u4eba\u6765\u8bf4\uff0c\u8fd9\u79cd\u60c5\u51b5\u5b9e\u5728\u6709\u70b9\u8981\u547d\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u76ef\u7740\u5979\uff0c\u5ffd\u7136\u95ee\u5979\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u559c\u6b22\u7b11\uff0c\u5e38\u5e38\u7b11\uff0c\u53ef\u662f\u4f60\u4ee5\u524d\u597d\u50cf\u4ece\u6765\u4e5f\u6ca1\u6709\u95f4\u8fc7\u6211\u5728\u7b11\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u4ee5\u524d\u662f\u4ee5\u524d\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4e3a\u4ec0\u4e48\u8981\u95ee\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u2026\u2026\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u56e0\u4e3a\u6211\u8981\u63d0\u9192\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4e2a\u7537\u4eba\u3002\u201d\u5c0f\u65b9\u7684\u8868\u60c5\u5f88\u662f\u4e25\u8083\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u5929\u4e0b\u6240\u6709\u7684\u7537\u4eba\u90fd\u662f\u5dee\u4e0d\u591a\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5982\u679c\u518d\u7b11\u4e00\u7b11\uff0c\u6211\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5c31\u600e\u4e48\u6837\uff1f\u201d\u201c\u9633\u5149\u201d\u6545\u610f\u95ee\u5c0f\u65b9\uff0c\u201c\u662f\u4e0d\u662f\u60f3\u6253\u6211\u7684\u5c41\u80a1\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u76ef\u7740\u5979\u770b\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u81ea\u5df1\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u7b11\u4e86\u3002 \u3000\u3000\u521a\u624d\u597d\u50cf\u5df2\u7ecf\u4e0d\u80fd\u5fcd\u53d7\u7684\u4e8b\uff0c\u5728\u7b11\u58f0\u4e2d\u5ffd\u7136\u5c31\u53d8\u5f97\u53ef\u4ee5\u5fcd\u53d7\u4e86\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u56de\u6765\u65f6\uff0c\u6f2b\u6f2b\u7684\u957f\u591c\u5df2\u8fc7\u53bb\uff0c\u8fd9\u6d53\u5bc6\u7684\u6811\u6797\u53c8\u6062\u590d\u4e86\u539f\u6765\u7684\u5149\u660e\u548c\u5e73\u5b81\u9759\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u548c\u5c0f\u65b9\u7684\u8138\u8272\u4e5f\u540c\u6837\u660e\u6717\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6ca1\u6709\u5bf9\u4e0d\u8d77\u522b\u4eba\uff0c\u4e5f\u6ca1\u6709\u5bf9\u4e0d\u8d77\u81ea\u5df1\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u770b\u7740\u4ed6\u4eec\uff0c\u5ffd\u7136\u53c8\u7528\u529b\u62cd\u4e86\u62cd\u5c0f\u65b9\u7684\u80a9\u3002 \u3000\u3000\u201c\u4f60\u679c\u7136\u662f\u535c\u9e70\u7684\u597d\u670b\u53cb\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u535c\u9e70\u679c\u7136\u6ca1\u6709\u770b\u9519\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u6837\u5b50\u4eff\u4f5b\u5f88\u795e\u79d8\uff0c\u8bf4\u7684\u8bdd\u4e5f\u5f88\u5947\u602a\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5bf9\u5c0f\u65b9\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u521a\u624d\u3002\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\u3002 \u3000\u3000\u201c\u4ece\u4e00\u4e2a\u5371\u5d16\u4e0a\u6454\u4e86\u4e0b\u53bb\u6454\u6b7b\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f60\u7684\u5934\u9885\u867d\u7136\u5df2\u7ecf\u50cf\u5357\u74dc\u822c\u6454\u788e\uff0c\u53ef\u662f\u522b\u4eba\u4e00\u5b9a\u8fd8\u80fd\u8ba4\u5f97\u51fa\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8eab\u4e0a\u8fd8\u7a7f\u7740\u4ed6\u4eec\u770b\u89c1\u8fc7\u4f60\u8fc7\u53bb\u7a7f\u7684\u8863\u670d\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u4f60\u7684\u5251\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5982\u679c\u4f60\u6ca1\u6709\u6b7b\uff0c\u5f53\u7136\u7edd\u4e0d\u80af\u5c06\u90a3\u4e48\u6837\u4e00\u67c4\u597d\u5251\u4ea4\u7ed9\u522b\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u5b8c\u5168\u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u4ed6\u663e\u7136\u5df2\u7ecf\u66ff\u5c0f\u65b9\u627e\u4e86\u4e2a\u66ff\u6b7b\u7684\u4eba\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5374\u8fd8\u8981\u95ee\uff1a\u201c\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u4e5f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4eba\u5168\u90fd\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4f60\u4eec\u662f\u4e3a\u4e86\u535c\u9e70\uff0c\u4e5f\u8bb8\u4f60\u4eec\u662f\u5931\u8db3\u843d\u4e0b\u53bb\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u5f88\u591a\u79cd\u539f\u56e0\u8981\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\uff1a\u201c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u4eba\u4f1a\u8ba4\u4e3a\u4f60\u4eec\u662f\u4e3a\u4e86\u6015\u79c1\u60c5\u88ab\u535c\u9e70\u53d1\u73b0\uff0c\u6240\u4ee5\u624d\u81ea\u6740\u6b89\u60c5\u7684\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u548c\u5c0f\u65b9\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5fc3\u91cc\u6beb\u65e0\u6127\u759a\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7edd\u5bf9\u6ca1\u6709\u79c1\u60c5\uff0c\u6240\u4ee5\u4ed6\u4eec\u8fd8\u80fd\u7b11\u5f97\u51fa\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5982\u679c\u968f\u65f6\u90fd\u80fd\u7b11\u5f97\u51fa\uff0c\u4e5f\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u4f60\u4eec\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6447\u5934\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u4e0d\u662f\u4e2a\u591a\u8bdd\u7684\u4eba\uff0c\u8fd1\u6765\u66f4\u6c89\u9ed8\u3002\u5982\u679c\u4ed6\u77e5\u9053\u522b\u4eba\u4e5f\u80fd\u56de\u7b54\u540c\u6837\u7684\u4e00\u4e2a\u95ee\u9898\uff0c\u4ed6\u5b81\u613f\u95ed\u7740\u5634\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u679c\u7136\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u4eec\u53bb\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u4e00\u4ef6\u7edd\u4e0d\u80fd\u8ba9\u522b\u4eba\u77e5\u9053\u4f60\u4eec\u8981\u53bb\u505a\u7684\u4e8b\uff0c\u53ea\u6709\u6b7b\u4eba\u624d\u4e0d\u4f1a\u88ab\u522b\u4eba\u6ce8\u610f\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u201c\u522b\u4eba\u201d\uff0c\u5f53\u7136\u5c31\u662f\u4ed6\u4eec\u7684\u5bf9\u5934\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u8fd8\u662f\u8981\u95ee\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5979\u95ee\uff1a\u201c\u4f60\u8981\u6211\u4eec\u53bb\u505a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u53bb\u627e\u535c\u9e70\u3002\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u5c31\u7b97\u4e0d\u8981\u4ed6\u4eec\u53bb\u505a\uff0c\u4ed6\u4eec\u4e5f\u4e00\u6837\u4f1a\u53bb\u505a\u7684\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u4e00\u5b9a\u8981\u62a5\u590d\uff0c\u8bf4\u4e0d\u5b9a\u73b0\u5728\u5c31\u60f3\u53bb\u627e\u536b\u5929\u9e4f\uff0c\u53bb\u627e\u5415\u4e09\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7684\u786e\u6709\u8fd9\u79cd\u60f3\u6cd5\u3002 \u3000\u3000\u201c\u53ef\u662f\u73b0\u5728\u6211\u4eec\u4e00\u5b9a\u8981\u5fcd\u8010\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4e0d\u7ba1\u6211\u4eec\u8981\u505a\u4ec0\u4e48\uff0c\u90fd\u4e00\u5b9a\u8981\u7b49\u627e\u5230\u535c\u9e70\u518d\u8bf4\u3002\u201d \u3000\u3000\u4eba\u6d77\u832b\u832b\uff0c\u8981\u627e\u4e00\u4e2a\u4eba\uff0c\u5e76\u4e0d\u6bd4\u5927\u6d77\u635e\u9488\u5bb9\u6613\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5e76\u4e0d\u5bb9\u6613\uff0c\u4f46\u662f\u53ea\u8981\u6211\u4eec\u6709\u4fe1\u5fc3\uff0c\u4e5f\u4e0d\u662f\u505a\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff1a\u201c\u4f60\u4eec\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u4ed6\u5e26\u7740\u4ed6\u4eec\u627e\u5230\u4e00\u68f5\u4e0d\u77e5\u540d\u7684\u91ce\u6811\uff0c\u4ece\u9774\u7b52\u91cc\u62d4\u51fa\u4e00\u628a\u5315\u9996\uff0c\u7528\u5315\u9996\u5272\u5f00\u6811\u76ae\u6811\u5e72\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u6811\u5e72\u4e2d\u5df2\u6709\u79cd\u4e73\u767d\u8272\u7684\u6c41\u6db2\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8981\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u7528\u53cc\u624b\u63a5\u4f4f\uff0c\u6162\u6162\u5730\uff0c\u5f88\u5747\u5300\u5730\u62b9\u5728\u8138\u4e0a\u548c\u624b\u4e0a\u3002 \u3000\u3000\u4ed6\u4eec\u8138\u4e0a\u7684\u76ae\u80a4\u7acb\u523b\u5c31\u89c9\u5f97\u5f88\u75d2\uff0c\u7136\u540e\u5c31\u8d77\u4e86\u79cd\u5f88\u5947\u602a\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u76ae\u80a4\u5ffd\u7136\u53d8\u9ed1\u4e86\uff0c\u800c\u4e14\u8d77\u4e86\u76b1\u7eb9\uff0c\u770b\u8d77\u6765\u5c31\u597d\u50cf\u5ffd\u7136\u8001\u4e86\u5341\u5c81\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u544a\u8bc9\u5c0f\u65b9\uff1a \u3000\u3000\u201c\u6211\u4eec\u7684\u65cf\u4eba\u66ff\u8fd9\u79cd\u6811\u8d77\u4e86\u4e2a\u5f88\u7279\u522b\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u5149\u9634\u3002\u201d \u3000\u3000\u201c\u5149\u9634\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u7684\u65cf\u4eba\u90fd\u53eb\u8fd9\u79cd\u6811\u53eb\u5149\u9634\u6811\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5b83\u7684\u6548\u7528\u81f3\u5c11\u53ef\u4ee5\u4fdd\u6301\u4e00\u4e00\u5e74\u3002\u4e00\u5e74\u4e4b\u5185\u4f60\u4eec\u90fd\u4f1a\u4fdd\u6301\u73b0\u5728\u7684\u6837\u5b50\uff0c\u5927\u6982\u4e0d\u4f1a\u6709\u4eba\u80fd\u8ba4\u51fa\u4f60\u4eec\u7684\u672c\u6765\u9762\u76ee\u3002\u201d \u3000\u3000\u4f46\u8bf4\u7684\u662f\u201c\u5927\u6982\u4e0d\u4f1a\u201d\uff0c\u4e0d\u662f\u201c\u7edd\u5bf9\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4eec\u8fd8\u8981\u7279\u522b\u6ce8\u610f\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u8fd8\u8981\u66ff\u4f60\u4eec\u627e\u522b\u7684\u63a9\u62a4\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u63a9\u62a4\uff1f\u201d\u201c\u9633\u5149\u201d\u95ee\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u4e0d\u662f\u2018\u84dd\u8272\u7684\u9633\u5149\u2019\uff0c\u4ed6\u4e5f\u4e0d\u662f\u8981\u547d\u7684\u5c0f\u65b9\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u201c\u9633\u5149\u201d\u8bf4\uff0c\u201c\u8fd9\u4e24\u4e2a\u4eba\u73b0\u5728\u90fd\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u73b0\u5728\u4f60\u4eec\u5df2\u662f\u53e6\u5916\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f60\u4eec\u662f\u5bf9\u592b\u59bb\uff0c\u5f88\u8d2b\u7a77\u7684\u592b\u59bb\uff0c\u4e00\u5b9a\u8981\u5954\u6ce2\u52b3\u82e6\u624d\u80fd\u751f\u5b58\u3002\u201d \u3000\u3000\u4e16\u754c\u4e0a\u672c\u6765\u5c31\u6709\u5f88\u591a\u50cf\u8fd9\u6837\u7684\u592b\u59bb\uff0c\u4e3a\u4e86\u6d3b\u4e0b\u53bb\uff0c\u4e0d\u5f97\u4e0d\u65e5\u591c\u52b3\u82e6\u5954\u6ce2\u4e0d\u606f\u3002 \u3000\u3000\u201c\u4f60\u4eec\u662f\u505a\u751f\u610f\u7684\uff0c\u628a\u85cf\u8fb9\u7684\u7279\u4ea7\u8fd0\u5230\u5173\u5185\u53bb\u8d29\u5356\uff0c\u535a\u4e00\u70b9\u8747\u5934\u5fae\u5229\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4eec\u6ca1\u6709\u7236\u6bcd\u5b50\u5973\uff0c\u5bb6\u91cc\u4e5f\u6ca1\u6709\u522b\u7684\u4eba\uff0c\u4e5f\u56e0\u4e3a\u4f60\u4eec\u592b\u59bb\u611f\u60c5\u4e0d\u9519\uff0c\u6240\u4ee5\u4f60\u4eec\u4e0d\u7ba1\u5230\u54ea\u91cc\u53bb\uff0c\u603b\u662f\u4e24\u4e2a\u4eba\u540c\u884c\u3002\u201d \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u90fd\u5728\u9759\u542c\u3002 \u3000\u3000\u73ed\u5bdf\u5df2\u90a3\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u5f53\u7136\u8bf7\u4e0d\u8d77\u9556\u5e08\u62a4\u9001\u3002\u4e3a\u4e86\u884c\u8def\u5b89\u5168\uff0c\u4f60\u4eec\u53ea\u6709\u52a0\u5165\u5546\u961f\u3002\u201d \u3000\u3000\u201c\u5546\u961f\uff1f\u201d\u5c0f\u65b9\u4e0d\u61c2\u3002 \u3000\u3000\u201c\u5546\u961f\u5c31\u662f\u5f88\u591a\u50cf\u4f60\u4eec\u8fd9\u6837\u7684\u4eba\u7ed3\u4f34\u540c\u884c\u7684\u961f\u4f0d\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u89e3\u91ca\uff1a\u201c\u51e0\u4e4e\u6bcf\u4e2a\u6708\u90fd\u6709\u8fd9\u4e48\u6837\u4e00\u961f\u4eba\u5165\u5173\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u5df2\u66ff\u4f60\u4eec\u627e\u5230\u4e86\u4e00\u4e2a\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u505a\u4e8b\u7684\u5468\u5bc6\u4ed4\u7ec6\uff0c\u5b9e\u5728\u4ee4\u4eba\u4e0d\u80fd\u4e0d\u4f69\u670d\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u5546\u961f\u5e76\u4e0d\u5927\uff0c\u5927\u6982\u6709\u4e09\u56db\u5341\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u9886\u5bfc\u8fd9\u4e2a\u961f\u4f0d\u7684\u4eba\u53eb\u2018\u82b1\u4e0d\u62c9\u2019\uff0c\u7cbe\u660e\u8001\u7ec3\uff0c\u5bf9\u5730\u5f62\u4e5f\u6781\u719f\u6089\uff0c\u5c11\u5e74\u65f6\u636e\u8bf4\u5c5e\u4e8e\u9774\u977c\u7684\u94c1\u9a91\u5175\uff0c\u66fe\u7ecf\u8fdc\u5f81\u8fc7\u7a81\u53a5\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u5230\u54ea\u91cc\u53bb\u624d\u80fd\u627e\u5f97\u5230\u4ed6\uff1f\u201d \u3000\u3000\u201c\u864e\u53e3\u96c6\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4ed6\u4eec\u9884\u5b9a\u662f\u5728\u864e\u53e3\u96c6\u4f1a\u5408\u7684\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8865\u5145\uff1a\u201c\u4f60\u4eec\u5230\u4e86\u90a3\u91cc\uff0c\u5148\u53bb\u627e\u4e00\u4e2a\u53eb\u2018\u5927\u70df\u888b\u2019\u7684\u4eba\uff0c\u628a\u4f60\u4eec\u7684\u540d\u5b57\u544a\u8bc9\u4ed6\u4eec\uff0c\u518d\u4ed8\u4e8c\u5341\u4e94\u4e24\u94f6\u5b50\u7684\u8def\u8d39\u7ed9\u4ed6\uff0c\u4ed6\u81ea\u7136\u4f1a\u5e26\u4f60\u4eec\u53bb\u89c1\u82b1\u4e0d\u62c9\u3002\u201d \u3000\u3000\u73b0\u5728\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u4e2a\u95ee\u9898\u4e86\u3002 \u3000\u3000\u201c\u6211\u4eec\u7684\u540d\u5b57\u53eb\u4ec0\u4e48\uff1f\u201d\u201c\u9633\u5149\u201d\u95ee\u3002 \u3000\u3000\u201c\u4f60\u662f\u85cf\u4eba\uff0c\u540d\u53eb\u7f8e\u96c5\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4f60\u7684\u4e08\u592b\u662f\u4e2a\u6c49\u4eba\uff0c\u540d\u5b57\u53eb\u505a\u82d7\u660c\u3002\u201d \u3000\u3000\u4ed6\u5c06\u4ed6\u7684\u53cc\u624b\u642d\u4e0a\u4ed6\u4eec\u7684\u80a9\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u4eec\u80fd\u5728\u4e00\u5e74\u4e4b\u5185\u627e\u5230\u535c\u9e70\u3002\u201d \u3000\u3000\u5728\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u60f3\u50cf\u4e2d\uff0c\u82b1\u4e0d\u62c9\u5f53\u7136\u5e94\u8be5\u662f\u4e2a\u9ad8\u5927\u5065\u58ee\u516c\u6b63\u4e25\u8083\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u60f3\u9519\u4e86\u3002 \u3000\u3000\u82b1\u4e0d\u62c9\u662f\u4e2a\u77ee\u5b50\uff0c\u672c\u6765\u4e5f\u8bb8\u8fd8\u4e0d\u592a\u77ee\uff0c\u53ef\u662f\u591a\u5e74\u6765\u9a6c\u978d\u4e0a\u7684\u751f\u547d\uff0c\u4f7f\u5f97\u4ed6\u4e24\u6761\u817f\u53d8\u5f97\u975e\u5e38\u5f2f\u66f2\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u5706\u5708\uff0c\u8d70\u8d77\u8def\u6765\u603b\u662f\u6447\u6447\u6446\u6446\u7684\uff0c\u6837\u5b50\u663e\u5f97\u5f88\u6ed1\u7a3d\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u603b\u662f\u5750\u5728\u4e00\u5f20\u5f88\u9ad8\u7684\u6905\u5b50\u4e0a\uff0c\u7528\u4e00\u53cc\u659c\u773c\u770b\u4eba\u7684\u65f6\u5019\uff0c\u773c\u775b\u91cc\u603b\u662f\u5e26\u7740\u79cd\u6b8b\u9177\u800c\u8ba5\u4fcf\u7684\u8868\u60c5\uff0c\u5c31\u50cf\u662f\u4e2a\u987d\u7ae5\u5728\u770b\u7740\u5df2\u7ecf\u88ab\u4ed6\u7528\u7ee2\u5b50\u7ed1\u4f4f\u7684\u732b\uff0c\u53c8\u50cf\u662f\u4e00\u53ea\u732b\u5728\u770b\u7740\u722a\u4e0b\u7684\u9f20\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u8fd8\u6709\u4e00\u53cc\u5927\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u53c8\u5bbd\u53c8\u5927\u53c8\u7c97\u53c8\u786c\uff0c\u6446\u5728\u684c\u4e0a\u65f6\uff0c\u5c31\u50cf\u662f\u4e24\u628a\u65a7\u5934\uff0c\u4e00\u4e0b\u5b50\u5c31\u53ef\u4ee5\u628a\u684c\u5b50\u780d\u6210\u4e24\u534a\u3002 \u3000\u3000\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fd9\u53cc\u624b\uff0c\u624d\u4f7f\u4eba\u4e0d\u80fd\u4e0d\u5bf9\u4ed6\u754f\u60e7\u5c0a\u656c\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u53e6\u4e00\u4e2a\u4f18\u70b9\u662f\uff0c\u4ed6\u5f88\u5c11\u8bf4\u8bdd\uff0c\u4ed6\u8981\u8bf4\u7684\u8bdd\u90fd\u7531\u201c\u5927\u70df\u888b\u201d\u66ff\u4ed6\u8bf4\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u770b\u89c1\u82b1\u4e0d\u62c9\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u6709\u4e00\u5bf9\u592b\u59bb\u5728\u4ed6\u7684\u5ba2\u623f\u91cc\u4e86\u3002 \u3000\u3000\u4e00\u5bf9\u548c\u5c0f\u65b9\u4ed6\u4eec\u4e00\u6837\u7684\u592b\u59bb\uff0c\u4e3a\u4e86\u8981\u6d3b\u4e0b\u53bb\uff0c\u5c31\u4e0d\u5f97\u4e0d\u65e5\u591c\u52b3\u82e6\u5954\u6ce2\u4e0d\u606f\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5e74\u7eaa\u90fd\u5df2\u7ecf\u4e0d\u5c0f\u4e86\uff0c\u4e08\u592b\u81f3\u5c11\u5df2\u7ecf\u6709\u4e09\u56db\u5341\u5c81\uff0c\u59bb\u5b50\u4e5f\u6709\u4e8c\u5341\u4e03\u516b\uff0c\u4e08\u592b\u7684\u8138\u4e0a\u5df2\u7ecf\u523b\u6ee1\u98ce\u971c\u52b3\u82e6\u7684\u75d5\u8ff9\uff0c\u59bb\u5b50\u603b\u662f\u4f4e\u7740\u5934\u4e0d\u6562\u89c1\u4eba\u3002 \u3000\u3000\u4e08\u592b\u628a\u4e8c\u5341\u4e94\u4e24\u94f6\u5b50\u8def\u8d39\u4ea4\u51fa\u6765\u7684\u65f6\u5019\uff0c\u59bb\u5b50\u7d27\u5f20\u5f97\u8fde\u6307\u5c16\u90fd\u5728\u53d1\u6296\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8fd9\u4e00\u751f\u4e2d\u4ece\u672a\u4ed8\u51fa\u8fc7\u6570\u76ee\u5982\u6b64\u5e9e\u5927\u7684\u4e00\u7b14\u94f6\u5b50\u3002 \u3000\u3000\u5728\u4ed6\u4eec\u773c\u4e2d\u770b\u6765\uff0c\u8fd9\u4e8c\u5341\u4e94\u4e24\u94f6\u5b50\u7684\u4ef7\u503c\u7edd\u5bf9\u6bd4\u5415\u4e09\u773c\u4e2d\u7684\u4e09\u5341\u4e07\u9ec4\u91d1\u8fd8\u5927\u5f97\u591a\u3002 \u3000\u3000\u5c0f\u65b9\u7b2c\u4e8c\u5929\u624d\u77e5\u9053\u4ed6\u4eec\u7684\u540d\u5b57\u3002\u2014\u2014\u4e08\u592b\u7684\u540d\u5b57\u53eb\u8d75\u7fa4\uff0c\u59bb\u5b50\u59d3\u80e1\uff0c\u5c31\u53eb\u505a\u8d75\u80e1\u6c0f\u3002 \u3000\u3000\u4e00\u4e2a\u5e73\u51e1\u89c4\u77e9\u5bb3\u7f9e\u7684\u5973\u4eba\u51fa\u5ac1\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u540d\u5b57\u4e86\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u4e00\u7ae0\u3000\u53c8\u89c1\u91d1\u624b \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u53c8\u89c1\u91d1\u624b \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u60f3\u5230\u8fd9\u4e00\u5bf9\u5e73\u51e1\u89c4\u77e9\u7684\u592b\u59bb\uff0c\u7adf\u662f\u5bf9\u4ed6\u548c\u201c\u9633\u5149\u201d\u8fd9\u4e00\u751f\u5f71\u54cd\u6700\u5927\u7684\u4eba\uff0c\u4ece\u67d0\u4e00\u65b9\u9762\u8bf4\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u6539\u53d8\u4e86\u4ed6\u4eec\u7684\u4e00\u751f\u3002 \u3000\u3000\u82b1\u4e0d\u62c9\u663e\u5f97\u5f88\u4e0d\u8010\u70e6\u3002 \u3000\u3000\u5bf9\u4ed6\u6765\u8bf4\uff0c\u4e0d\u7ba1\u5750\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u90fd\u8fdc\u4e0d\u53ca\u5750\u5728\u9a6c\u978d\u4e0a\u8212\u670d\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u201c\u5927\u70df\u888b\u201d\u66ff\u4ed6\u95f4\u8fc7\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u51e0\u4e2a\u7b80\u5355\u7684\u95ee\u9898\u4e4b\u540e\uff0c\u53eb\u4ed6\u4eec\u56de\u623f\u53bb\u7684\u65f6\u5019\uff0c\u82b1\u4e0d\u62c9\u5374\u8981\u4ed6\u4eec\u201c\u7b49\u4e00\u7b49\u201d\u3002\u4ed6\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u7ec3\u8fc7\u6b66\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5c0f\u65b9\u7acb\u523b\u56de\u7b54\uff0c\u201c\u867d\u7136\u7ec3\u8fc7\u51e0\u5929\u5e84\u7a3c\u628a\u5f0f\uff0c\u4e5f\u4e0d\u80fd\u7b97\u7ec3\u6b66\u3002\u201d \u3000\u3000\u201c\u4f60\u8eab\u4e0a\u6709\u6ca1\u6709\u5e26\u5bb6\u4f19\uff1f\u201d\u82b1\u4e0d\u62c9\u53c8\u95f4\u3002 \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u8fde\u4e00\u628a\u5200\u90fd\u6ca1\u6709\u5e26\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u82b1\u4e0d\u62c9\u770b\u7740\u5c0f\u65b9\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u6696\u6627\u800c\u8be1\u5f02\u7684\u7b11\u610f\uff0c\u5ffd\u7136\u4ece\u8eab\u4e0a\u62bd\u51fa\u628a\u5315\u9996\u3002 \u3000\u3000\u201c\u4f60\u6700\u597d\u628a\u8fd9\u5bb6\u4f19\u5e26\u5728\u8eab\u4e0a\u3002\u201d\u4ed6\u5c06\u5315\u9996\u4ea4\u7ed9\u5c0f\u65b9\uff1a\u201c\u4f60\u7684\u8001\u5a46\u5e74\u7eaa\u8fd8\u4e0d\u7b97\u592a\u5927\uff0c\u6211\u4eec\u8fd9\u961f\u4f0d\u91cc\u4ec0\u4e48\u6837\u7684\u4eba\u90fd\u6709\uff0c\u8d70\u5728\u8def\u4e0a\uff0c\u80fd\u5c0f\u5fc3\u8fd8\u662f\u5c0f\u5fc3\u4e9b\u597d\uff01\u201d \u3000\u3000\u201c\u90a3\u4e2a\u4eba\u4e0d\u662f\u597d\u4eba\uff0c\u201d \u3000\u3000\u4e00\u56de\u5230\u623f\u91cc\uff0c\u201c\u9633\u5149\u201d\u5c31\u6084\u6084\u5730\u5bf9\u5c0f\u65b9\u8bf4\uff1a\u201c\u7edd\u5bf9\u4e0d\u662f\u597d\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u82b1\u4e0d\u62c9\u7b11\u7684\u65f6\u5019\u7684\u786e\u6709\u70b9\u4e0d\u6000\u597d\u610f\u7684\u6837\u5b50\u3002 \u3000\u3000\u5e78\u597d\u201c\u9633\u5149\u201d\u5df2\u7ecf\u4e0d\u662f\u672c\u6765\u90a3\u4e2a\u660e\u6717\u7f8e\u4e3d\u7684\u201c\u84dd\u8272\u9633\u5149\u201d\u4e86\uff0c\u8fde\u8d75\u80e1\u6c0f\u770b\u8d77\u6765\u90fd\u6bd4\u5979\u987a\u773c\u5f97\u591a\u3002 \u3000\u3000\u90a3\u5bf9\u592b\u59bb\u5c31\u4f4f\u5728\u4ed6\u4eec\u7684\u9694\u58c1\u3002 \u3000\u3000\u4ed6\u4eec\u4f4f\u7684\u662f\u4e00\u5bb6\u6700\u4fbf\u5b9c\u7684\u5c0f\u5ba2\u6808\uff0c\u623f\u91cc\u9664\u4e86\u4e00\u5f20\u571f\u7095\u548c\u4e00\u7fa4\u81ed\u866b\u5916\u51e0\u4e4e\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4e8c\u5341\u4e94\u4e24\u94f6\u5b50\u8def\u8d39\u4e2d\u8fd8\u5305\u62ec\u98df\u5bbf\uff0c\u4ed6\u4eec\u5f53\u7136\u4e0d\u80fd\u8981\u6c42\u5927\u591a\u3002 \u3000\u3000\u4f55\u51b5\u7095\u603b\u7b97\u8fd8\u662f\u70ed\u7684\uff0c\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u80fd\u591f\u6709\u70ed\u7095\u53ef\u7761\u5df2\u7ecf\u5f88\u4e0d\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u5e0c\u671b\u80fd\u5feb\u70b9\u7761\u7740\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u6ca1\u6709\u7761\u7740\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u5f00\u59cb\u8981\u7761\u7684\u65f6\u5019\uff0c\u9694\u58c1\u623f\u91cc\u5ffd\u7136\u54cd\u8d77\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u8fd8\u5206\u4e0d\u51fa\u90a3\u662f\u4ec0\u4e48\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u662f\u58f0\u97f3\u8d8a\u6765\u8d8a\u5927\uff0c\u800c\u4e14\u6301\u7eed\u5f97\u5f88\u4e45\uff0c\u4e24\u95f4\u623f\u53c8\u53ea\u9694\u7740\u4e00\u5c42\u8584\u5899\u3002 \u3000\u3000\u5982\u679c\u4ed6\u4eec\u8fd8\u662f\u5c0f\u5b69\u5b50\uff0c\u4e5f\u8bb8\u8fd8\u662f\u5206\u4e0d\u51fa\u90a3\u662f\u4ec0\u4e48\u58f0\u97f3\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4eec\u5df2\u7ecf\u4e0d\u662f\u5c0f\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u5168\u8eab\u53d1\u70ed\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u60f3\u5230\u4e00\u4e2a\u90a3\u4e48\u89c4\u77e9\u3001\u90a3\u4e48\u5bb3\u7f9e\u7684\u5973\u4eba\uff0c\u5728\u8ddf\u5979\u7684\u4e08\u592b\u505a\u8fd9\u4ef6\u4e8b\u7684\u65f6\u5019\uff0c\u5c45\u7136\u4f1a\u51fa\u8fd9\u4e48\u6837\u7684\u58f0\u97f3\u6765\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u4eec\u5e73\u65e5\u7684\u751f\u6d3b\u592a\u5355\u8c03\uff0c\u5ffd\u7136\u6362\u4e86\u4e2a\u65b0\u7684\u73af\u5883\uff0c\u5230\u4e86\u4e2a\u964c\u751f\u7684\u5730\u65b9\uff0c\u603b\u662f\u96be\u514d\u4f1a\u653e\u8086\u4e00\xb7\u70b9\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u65f6\u5019\uff0c\u53ef\u662f\u6709\u4e9b\u4eba\u5c31\u7b97\u5728\u8fd9\u79cd\u65f6\u5019\u4e5f\u4e00\u5b9a\u8981\u63a7\u5236\u81ea\u5df1\u3002 \u3000\u3000\u5c0f\u65b9\u95ed\u7740\u773c\u775b\uff0c\u5168\u8eab\u4e0a\u4e0b\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u4ed6\u5e0c\u671b\u201c\u9633\u5149\u201d\u8ba4\u4e3a\u4ed6\u5df2\u7761\u7740\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u4e5f\u6ca1\u6709\u52a8\uff0c\u5979\u662f\u4e0d\u662f\u4e5f\u5e0c\u671b\u5c0f\u65b9\u8ba4\u4e3a\u5979\u4e5f\u5df2\u7761\u7740\uff1f \u3000\u3000\u6e05\u6668\uff0c\u9633\u5149\u6ee1\u5730\u3002 \u3000\u3000\u5929\u8fd8\u6ca1\u6709\u4eae\u5c0f\u65b9\u5c31\u8d77\u6765\u4e86\uff0c\u7528\u4e00\u6876\u5df2\u7ecf\u7ed3\u4e86\u51b0\u78b4\u5b50\u7684\u51b7\u6c34\u6d17\u4e86\u4e2a\u51b7\u6c34\u6d74\uff0c\u6cbf\u7740\u5c0f\u5ba2\u6808\u5916\u7684\u5c71\u5761\u4e0a\u8dd1\u4e86\u5341\u516d\u516b\u4e2a\u5708\u5b50\u3002 \u3000\u3000\u4ed6\u56de\u6765\u7684\u65f6\u5019\uff0c\u201c\u9633\u5149\u201d\u5df2\u6536\u62fe\u597d\u884c\u674e\u3002\u4ed6\u770b\u7740\u201c\u9633\u5149\u201d\u7b11\u7b11\uff0c\u201c\u9633\u5149\u201d\u4e5f\u770b\u7740\u4ed6\u7b11\u7b11\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u5bf9\u65b9\u6628\u5929\u665a\u4e0a\u7761\u7740\u4e86\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u4e00\u591c\u4e0d\u7ba1\u600e\u4e48\u6837\u96be\u6371\uff0c\u4ed6\u4eec\u603b\u7b97\u5df2\u7ecf\u6371\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u4e00\u5bf9\u592b\u59bb\u53c8\u6062\u590d\u4e86\u90a3\u79cd\u53c8\u89c4\u77e9\u53c8\u8001\u5b9e\u7684\u6837\u5b50\uff0c\u5bb3\u7f9e\u7684\u59bb\u5b50\u8fd8\u662f\u4f4e\u7740\u5934\u4e0d\u6562\u89c1\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u4e5f\u4e0d\u6562\u53bb\u770b\u7740\u5979\uff0c\u751f\u6015\u4e00\u770b\u5230\u5979\u5c31\u4f1a\u8054\u60f3\u5230\u6628\u5929\u665a\u4e0a\u7684\u58f0\u97f3\uff0c\u5c31\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u6765\u3002 \u3000\u3000\u8981\u547d\u7684\u662f\uff0c\u4ed6\u4eec\u56db\u4e2a\u4eba\u504f\u504f\u88ab\u5206\u6d3e\u5230\u4e00\u8f86\u9a74\u8f66\u4e0a\uff0c\u8f66\u4e86\u53c8\u5c0f\u53c8\u7a84\uff0c\u56db\u4e2a\u4eba\u9f3b\u5b50\u5bf9\u9f3b\u5b50\uff0c\u773c\u775b\u5bf9\u773c\u775b\uff0c\u60f3\u4e0d\u770b\u90fd\u4e0d\u884c\u3002 \u3000\u3000\u4e2d\u5348\u5403\u996d\u7684\u65f6\u5019\uff0c\u8fd9\u5bf9\u592b\u59bb\u5c45\u7136\u8fd8\u628a\u4ed6\u4eec\u505a\u7684\u8def\u83dc\u5206\u4e86\u4e00\u70b9\u7ed9\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\uff0c\u9664\u4e86\u8fa3\u6912\u7092\u8089\u5b50\u4e4b\u5916\uff0c\u5c45\u7136\u8fd8\u6709\u4e00\u70b9\u85cf\u4eba\u6700\u559c\u6b22\u5403\u7684\u201c\u8471\u6ce5\u201d\u3002 \u3000\u3000\u8fd9\u79cd\u7528\u5723\u6bcd\u5cf0\u5c71\u9e93\u4e0a\u7279\u4ea7\u7684\u91ce\u8471\u3001\u9614\u53f6\u97ed\u548c\u7ea2\u849c\u505a\u6210\u7684\u201c\u8471\u6ce5\u201d\uff0c\u5bf9\u85cf\u8fb9\u4e00\u5e26\u7684\u4eba\u6765\u8bf4\uff0c\u7b80\u76f4\u5c31\u662f\u65e0\u4e0a\u7684\u73cd\u6413\uff0c\u662f\u7edd\u4e0d\u80af\u8f7b\u6613\u62ff\u51fa\u6765\u5f85\u5ba2\u7684\u3002 \u3000\u3000\u8fd9\u5bf9\u592b\u59bb\u597d\u50cf\u4e3a\u4e86\u8981\u8865\u507f\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u6628\u5929\u665a\u4e0a\u635f\u5931\u7684\u7761\u7720\uff0c\u7279\u5730\u6765\u8868\u793a\u4ed6\u4eec\u7684\u6b49\u610f\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u53ea\u5e0c\u671b\u4eca\u5929\u665a\u4e0a\u6295\u5bbf\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u80fd\u5b89\u5b89\u9759\u9759\u5730\u7761\u4e00\u5bb5\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u5931\u671b\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u591c\u4ed6\u548c\u201c\u9633\u5149\u201d\u53c8\u88ab\u5206\u914d\u5230\u4ed6\u4eec\u9694\u58c1\uff0c\u53c8\u88ab\u6574\u5f97\u5f88\u60e8\u3002 \u3000\u3000\u8fd9\u5bf9\u592b\u59bb\u7684\u7cbe\u529b\u8fdc\u6bd4\u4ed6\u4eec\u5916\u8868\u770b\u8d77\u6765\u65fa\u76db\u5f97\u591a\u3002 \u3000\u3000\u5982\u679c\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u4e5f\u662f\u5bf9\u592b\u59bb\uff0c\u8fd9\u95ee\u9898\u5f88\u5bb9\u6613\u5c31\u53ef\u4ee5\u89e3\u51b3\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4eec\u4e0d\u662f\u3002 \u3000\u3000\u4ed6\u4eec\u4ece\u672a\u60f3\u5230\u8fd9\u4ef6\u4e8b\u7adf\u662f\u4ed6\u4eec\u8fd9\u4e00\u8def\u4e0a\u6700\u5927\u7684\u70e6\u607c\uff0c\u66f4\u60f3\u4e0d\u5230\u8fd9\u4e48\u4e00\u4e2a\u8001\u5b9e\u5bb3\u7f9e\u7684\u5973\u4eba\uff0c\u4e00\u5230\u665a\u4e0a\u5c31\u53d8\u6210\u4e86\u4e2a\u8981\u547d\u7684\u5c24\u7269\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u4e09\u5929\u7684\u665a\u4e0a\uff0c\u5c0f\u65b9\u5ffd\u7136\u62ff\u51fa\u4e86\u4e09\u7c92\u9ab0\u5b50\uff0c\u5bf9\u201c\u9633\u5149\u201d\u8bf4\uff1a\u201c\u6211\u4eec\u6765\u63b7\u9ab0\u5b50\u3002\u201d \u3000\u3000\u201c\u63b7\u9ab0\u5b50\uff1f\u201d\u201c\u9633\u5149\u201d\u95ee\uff1a\u201c\u4f60\u8981\u8ddf\u6211\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u51c6\u8f93\u4e86\u4eca\u5929\u665a\u4e0a\u8c01\u5c31\u7761\u5230\u5916\u9762\u7684\u8f66\u5b50\u4e0a\u53bb\u3002\u201d \u3000\u3000\u8f93\u7684\u5f53\u7136\u662f\u5c0f\u65b9\uff0c\u4ed6\u5728\u9ab0\u5b50\u4e0a\u505a\u4e86\u624b\u811a\uff0c\u4ed6\u60c5\u613f\u7761\u5728\u8f66\u4e0a\u3002 \u3000\u3000\u4ed6\u7761\u7740\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5374\u8fd8\u662f\u7761\u4e0d\u7740\u3002 \u3000\u3000\u9694\u58c1\u7684\u58f0\u97f3\u867d\u7136\u5df2\u6682\u65f6\u9759\u4e0b\u6765\uff0c\u5979\u5374\u60f3\u8d77\u4e86\u5f88\u591a\u4e8b\uff0c\u5f88\u591a\u672c\u6765\u4e0d\u8be5\u60f3\u7684\u4e8b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5979\u542c\u89c1\u6709\u4eba\u5728\u63a8\u95e8\u3002 \u3000\u3000\u5979\u7684\u5fc3\u8df3\u7acb\u523b\u52a0\u51b3\u3002 \u3000\u3000\u2014\u2014\u662f\u4e0d\u662f\u5c0f\u65b9\u56de\u6765\u4e86\uff1f \u3000\u3000\u4e0d\u662f\u3002 \u3000\u3000\u6765\u7684\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u5979\u770b\u4e0d\u6e05\u8fd9\u4e2a\u4eba\u7684\u8138\uff0c\u53ef\u662f\u53ea\u8981\u770b\u89c1\u90a3\u53cc\u7f57\u5708\u817f\uff0c\u5c31\u77e5\u9053\u6765\u7684\u662f\u8c01\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u8df3\u8d77\uff1a\u201c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6765\u966a\u4f60\u3002\u201d\u82b1\u4e0d\u62c9\u76ef\u7740\u5979\uff0c\u773c\u4e2d\u9732\u51fa\u6deb\u90aa\u7684\u7b11\u610f\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u8001\u516c\u4e0d\u4e2d\u7528\uff0c\u7279\u5730\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6293\u7d27\u88ab\u89d2\u3002 \u3000\u3000\u201c\u6211\u4e0d\u8981\u4f60\u966a\u3002\u201d\u5979\u771f\u7684\u5f88\u7d27\u5f20\uff0c\u201c\u4f60\u518d\u4e0d\u8d70\u6211\u5c31\u8981\u53eb\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u53eb\uff0c\u53eb\u8c01\uff1f\u53eb\u4f60\u7684\u8001\u516c\uff1f\u201d\u82b1\u4e0d\u62c9\u72de\u7b11\uff1a\u201c\u4f60\u5c31\u7b97\u628a\u4ed6\u53eb\u6765\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u4ed6\u4f38\u51fa\u4e00\u53cc\u94c1\u6761\u822c\u7684\u624b\uff0c\u6293\u8d77\u4e2a\u8336\u676f\uff0c\u8f7b\u8f7b\u4e00\u634f\uff0c\u5c31\u634f\u5f97\u7c89\u788e\u3002 \u3000\u3000\u201c\u4f60\u8001\u516c\u6709\u6ca1\u6709\u6211\u8fd9\u6837\u7684\u529f\u592b\uff1f\u201d\u82b1\u4e0d\u62c9\u5e26\u7740\u72de\u7b11\u95ee\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53ea\u6709\u6447\u5934\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u53ea\u4e0d\u8fc7\u662f\u4e00\u5bf9\u5e73\u51e1\u7684\u592b\u59bb\uff0c\u5f53\u7136\u6ca1\u6709\u8fd9\u6837\u7684\u529f\u592b\u3002 \u3000\u3000\u5979\u7edd\u4e0d\u80fd\u66b4\u9732\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u53ef\u662f\u82b1\u4e0d\u62c9\u5df2\u7ecf\u4e00\u6b65\u6b65\u8d70\u8fc7\u6765\uff0c\u5df2\u7ecf\u5feb\u8d70\u5230\u5979\u7684\u5e8a\u5934\u4e86\u3002 \u3000\u3000\u201c\u4f60\u6562\u53eb\uff0c\u6211\u5c31\u585e\u4f4f\u4f60\u7684\u5634\uff0c\u4f60\u7684\u8001\u516c\u6765\u4e86\uff0c\u6211\u5c31\u628a\u4ed6\u6d3b\u6d3b\u634f\u6b7b\u3002\u201d \u3000\u3000\u770b\u6765\u4ed6\u5df2\u7ecf\u51b3\u5fc3\u4e0d\u80af\u653e\u8fc7\u5979\u4e86\u3002 \u3000\u3000\u73b0\u5728\u5979\u5df2\u7ecf\u4e0d\u662f\u201c\u84dd\u8272\u7684\u9633\u5149\u201d\uff0c\u73b0\u5728\u5979\u53ea\u4e0d\u8fc7\u662f\u4e2a\u53c8\u9ed1\u53c8\u4e11\u7684\u5973\u4eba\uff0c\u82b1\u4e0d\u62c9\u600e\u4e48\u4f1a\u504f\u504f\u770b\u4e0a\u4e86\u5979\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u6025\u53c8\u6c14\u53c8\u5947\u602a\uff0c\u82b1\u4e0d\u62c9\u5df2\u7ecf\u7eb5\u8eab\u6251\u4e86\u8fc7\u6765\uff0c\u4e00\u53cc\u5927\u624b\u5df2\u7ecf\u4f38\u51fa\u6765\u51c6\u5907\u5265\u5979\u7684\u8863\u670d\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u6293\u4f4f\u5979\uff0c\u5374\u6293\u4f4f\u4e86\u4e2a\u5305\u88b1\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5f80\u5e8a\u91cc\u8fb9\u4e00\u8ba9\uff0c\u987a\u624b\u6293\u8d77\u4e2a\u5305\u88b1\uff0c\u7528\u529b\u63b7\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u7684\u8863\u670d\u6ca1\u6709\u88ab\u6293\u7834\uff0c\u5305\u88b1\u5374\u88ab\u6293\u7834\u4e86\uff0c\u4e00\u6837\u4e1c\u897f\u4ece\u5305\u88b1\u91cc\u843d\u4e0b\uff0c\u6389\u5728\u5730\u4e0a\u3002 \u3000\u3000\u82b1\u4e0d\u62c9\u5ffd\u7136\u8138\u4e0a\u9732\u51fa\u79cd\u6050\u60e7\u4e4b\u6781\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u8f6c\u8eab\u98de\u5954\u51fa\u53bb\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u89c1\u4e86\u9b3c\u4e00\u6837\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u7acb\u523b\u5c31\u9003\u5f97\u8e2a\u5f71\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7684\u5fc3\u8fd8\u5728\u8df3\uff0c\u624b\u811a\u8fd8\u662f\u51b0\u51b7\u7684\u3002 \u3000\u3000\u2014\u2014\u82b1\u4e0d\u62c9\u4e3a\u4ec0\u4e48\u4f1a\u5ffd\u7136\u9003\u8d70\uff0c\u4ed6\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u521a\u624d\u4ece\u5305\u88b1\u91cc\u6389\u4e0b\u6765\u7684\u4e1c\u897f\u8fd8\u5728\u5730\u4e0a\uff0c\u90a3\u4e2a\u5305\u88b1\u662f\u5979\u4eca\u5929\u65e9\u4e0a\u4eb2\u624b\u5305\u8d77\u6765\u7684\uff0c\u91cc\u9762\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u4ef6\u53ef\u4ee5\u8ba9\u4eba\u4e00\u770b\u89c1\u5c31\u6015\u5f97\u8981\u9003\u8d70\u7684\u4e1c\u897f\u3002 \u3000\u3000\u95e8\u53c8\u88ab\u63a8\u5f00\u4e86\uff0c\u8fd9\u6b21\u8fdb\u6765\u7684\u603b\u7b97\u4e0d\u662f\u522b\u4eba\uff0c\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u4ed6\u7761\u5f97\u5e76\u4e0d\u719f\uff0c\u65e0\u8bba\u8c01\u90fd\u6ca1\u6cd5\u5b50\u80fd\u5728\u90a3\u53c8\u51b7\u53c8\u786c\u53c8\u900f\u98ce\u7684\u8f66\u5b50\u4e0a\u7761\u5f97\u5f88\u719f\u7684\u3002 \u3000\u3000\u4ed6\u7684\u8033\u6735\u4e00\u5411\u5f88\u7075\u3002 \u3000\u3000\u770b\u89c1\u5c0f\u65b9\uff0c\u201c\u9633\u5149\u201d\u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u4f60\u770b\u770b\u5e8a\u4e0b\u9762\u662f\u4e0d\u662f\u6709\u6837\u4e1c\u897f\uff1f\u201d\u5979\u95ee\u5c0f\u65b9\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u66f4\u7740\u6025\uff0c\u66f4\u5947\u602a\uff1a\u201c\u4f60\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6162\u6162\u5730\u4fef\u4e0b\u8eab\uff0c\u4ece\u5e8a\u4e0b\u6361\u8d77\u4e00\u6837\u4e1c\u897f\u3002\u201d \u3000\u3000\u4ed6\u6361\u8d77\u6765\u7684\u7adf\u7136\u662f\u4e00\u53ea\u624b\u3002 \u3000\u3000\u91d1\u624b\uff01 \u3000\u3000\u201c\u8fd9\u5305\u88b1\u771f\u7684\u662f\u4f60\u4eca\u5929\u65e9\u4e0a\u4eb2\u624b\u5305\u597d\u7684\uff1f\u201d\u5c0f\u65b9\u95ee\u9633\u5149\u3002 \u3000\u3000\u201c\u7edd\u5bf9\u662f\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u5019\u8fd9\u53ea\u91d1\u624b\u5728\u4e0d\u5728\u8fd9\u4e2a\u5305\u88b1\u91cc\u9762\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5728\u3002\u201d\u9633\u5149\u8bf4\u5f97\u975e\u5e38\u80af\u5b9a\uff0c\u201c\u7edd\u5bf9\u4e0d\u5728\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u4f60\u771f\u7684\u4eb2\u773c\u770b\u89c1\u5b83\u662f\u4ece\u5305\u88b1\u91cc\u6389\u4e0b\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u8fd9\u53ea\u91d1\u624b\u600e\u4e48\u4f1a\u5230\u4f60\u5305\u88b1\u91cc\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u771f\u7684\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u53ea\u91d1\u624b\u662f\u201c\u5bcc\u8d35\u795e\u4ed9\u201d\u5415\u4e09\u7528\u6765\u8054\u7edc\u53f7\u4ee4\u7fa4\u8c6a\u7684\u4fe1\u7269\uff0c\u672c\u6765\u662f\u7edd\u4e0d\u53ef\u80fd\u5728\u5979\u5305\u88b1\u91cc\u51fa\u73b0\u7684\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u8fd9\u4ef6\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u5374\u504f\u504f\u53d1\u751f\u4e86\u3002 \u3000\u3000\u957f\u591c\u8fd8\u672a\u8fc7\u53bb\uff0c\u9694\u58c1\u7684\u5c4b\u5b50\u5c45\u7136\u5df2\u7ecf\u5b89\u9759\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53c8\u95ee\uff1a\u201c\u4eca\u5929\u6709\u8c01\u78b0\u8fc7\u8fd9\u4e2a\u5305\u88b1\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u9633\u5149\u7684\u53e3\u6c14\u5df2\u7ecf\u6ca1\u6709\u521a\u624d\u90a3\u4e48\u80af\u5b9a\u4e86\uff1a\u201c\u597d\u50cf\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u662f\u597d\u50cf\u6ca1\u6709\uff0c\u8fd8\u662f\u7edd\u5bf9\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u5728\u72b9\u8c6b\uff0c\u8fd9\u95ee\u9898\u5979\u5b9e\u5728\u6ca1\u628a\u63e1\u786e\u5b9a\u56de\u7b54\uff0c\u5979\u53ea\u8bb0\u5f97\u8fd9\u5305\u88b1\u4e00\u76f4\u90fd\u662f\u5728\u5979\u624b\u8fb9\u7684\uff0c\u51e0\u4e4e\u6ca1\u6709\u79bb\u5f00\u8fc7\u5979\u7684\u89c6\u7ebf\u3002 \u3000\u3000\u2014\u2014\u662f\u201c\u51e0\u4e4e\u201d\uff0c\u4e0d\u662f\u201c\u7edd\u5bf9\u201d\u3002 \u3000\u3000\u5c0f\u65b9\u518d\u95ee\uff1a \u3000\u3000\u201c\u6709\u6ca1\u6709\u4eba\u80fd\u591f\u627e\u4e2a\u673a\u4f1a\u628a\u8fd9\u53ea\u91d1\u624b\u585e\u5230\u4f60\u5305\u88b1\u91cc\u53bb\uff1f\u201d \u3000\u3000\u8981\u5728\u5979\u8eab\u65c1\u5c06\u8fd9\u4e2a\u5305\u88b1\u5077\u8d70\u4e5f\u8bb8\u4e0d\u53ef\u80fd\uff0c\u4f46\u662f\u8981\u585e\u6837\u4e1c\u897f\u5230\u5979\u5305\u88b1\u91cc\u53bb\u5c31\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u7acb\u523b\u56de\u7b54\uff1a\u201c\u6709\u3002\u201d\u5979\u7684\u773c\u775b\u5ffd\u7136\u53d1\u5149\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6307\u4e86\u6307\u9694\u58c1\u7684\u5c4b\u5b50\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u5435\u5f97\u6211\u4eec\u6574\u665a\u7761\u4e0d\u7740\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u65e9\u5c31\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u3002\u4ed6\u4eec\u4e00\u8def\u540c\u8f66\uff0c\u73b0\u5728\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u670b\u53cb\u3002\u5728\u8f66\u4e0a\uff0c\u90a3\u4f4d\u8d75\u80e1\u6c0f\u603b\u662f\u5750\u5728\u201c\u9633\u5149\u201d\u65c1\u8fb9\u3002\u201c\u9633\u5149\u201d\u603b\u662f\u5fcd\u4e0d\u4f4f\u6253\u778c\u7761\uff0c\u8d75\u80e1\u6c0f\u8981\u5077\u5077\u585e\u6837\u4e1c\u897f\u5230\u5979\u5305\u88b1\u91cc\u53bb\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ef6\u56f0\u96be\u7684\u4e8b\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u73ed\u5bdf\u5df4\u90a3\u6839\u672c\u5c31\u6ca1\u6709\u9a97\u8fc7\u5415\u4e09\uff0c\u6211\u4eec\u7684\u884c\u52a8\u65e9\u5c31\u88ab\u53d1\u73b0\u3002\u201d\u201c\u9633\u5149\u201d\u9053\uff0c\u201c\u6240\u4ee5\u4ed6\u65e9\u5c31\u6d3e\u4eba\u6765\u8ddf\u8e2a\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u90a3\u5bf9\u592b\u5987\u5c31\u662f\u5415\u4e09\u6d3e\u6765\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u54ac\u7740\u5634\u5507\uff1a\u201c\u6211\u65e9\u5c31\u5bf9\u4ed6\u4eec\u6709\u70b9\u7591\u5fc3\u4e86\uff0c\u4e00\u4e2a\u6b63\u6b63\u7ecf\u7ecf\u7684\u826f\u5bb6\u5987\u5973\uff0c\u660e\u660e\u77e5\u9053\u9694\u58c1\u6709\u4eba\uff0c\u665a\u4e0a\u600e\u4e48\u4f1a\u50cf\u5979\u90a3\u4e48\u9b3c\u53eb\uff1f\u201d \u3000\u3000\u5979\u7684\u8138\u597d\u50cf\u5df2\u7ecf\u6709\u70b9\u7ea2\u4e86\uff1a\u201c\u4e5f\u8bb8\u5979\u6839\u672c\u5c31\u662f\u6545\u610f\u8981\u5435\u5f97\u6211\u4eec\u7761\u4e0d\u7740\uff0c\u8ba9\u6211\u4eec\u767d\u5929\u6ca1\u7cbe\u795e\uff0c\u5979\u624d\u6709\u673a\u4f1a\u4e0b\u624b\u3002\u201d \u3000\u3000\u8fd9\u867d\u7136\u53ea\u4e0d\u8fc7\u662f\u5979\u7684\u731c\u6d4b\uff0c\u53ef\u662f\u8fd9\u79cd\u731c\u6d4b\u5e76\u4e0d\u662f\u6ca1\u6709\u9053\u7406\u3002 \u3000\u3000\u552f\u4e00\u4e0d\u5408\u7406\u7684\u662f\uff1a\u201c\u5982\u679c\u5415\u4e09\u771f\u7684\u5df2\u7ecf\u67e5\u51fa\u6211\u4eec\u7684\u884c\u52a8\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u6740\u4e86\u6211\u4eec\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u8fd8\u60f3\u4ece\u6211\u4eec\u7684\u8eab\u4e0a\u627e\u51fa\u535c\u9e70\u7684\u4e0b\u843d\uff0c\u6240\u4ee5\u53ea\u6709\u6d3e\u4eba\u6697\u4e2d\u8ddf\u8e2a\uff0c\u800c\u4e14\u7edd\u4e0d\u80fd\u8ba9\u6211\u4eec\u53d1\u73b0\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u90a3\u5bf9\u592b\u59bb\u771f\u662f\u5415\u4e09\u6d3e\u6765\u6697\u4e2d\u8ddf\u8e2a\u6211\u4eec\u7684\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u628a\u4e00\u53ea\u91d1\u624b\u585e\u5728\u6211\u4eec\u7684\u5305\u88b1\u91cc\uff1f\u201d\u5c0f\u65b9\u95f4\uff0c\u201c\u4ed6\u4eec\u8fd9\u4e48\u505a\u5c82\u975e\u4e5f\u66b4\u9732\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u5979\u4e5f\u60f3\u4e0d\u901a\uff0c\u8fd9\u4ef6\u4e8b\u5176\u4e2d\u7684\u786e\u6709\u5f88\u591a\u77db\u76fe\u4e4b\u5904\u3002 \u3000\u3000\u9694\u58c1\u90a3\u95f4\u5c4b\u5b50\u672c\u6765\u5df2\u7ecf\u5b89\u9759\u4e86\u5f88\u4e45\uff0c\u73b0\u5728\u5ffd\u7136\u53c8\u6709\u4e86\u58f0\u97f3\u3002 \u3000\u3000\u2014\u2014\u7537\u4eba\u54b3\u55fd\u7684\u58f0\u97f3\uff0c\u5973\u4eba\u53f9\u6c14\u7684\u58f0\u97f3\uff0c\u6709\u4eba\u8d77\u5e8a\u7684\u58f0\u97f3\uff0c\u5f00\u95e8\u7684\u58f0\u97f3\uff0c\u62d6\u7740\u978b\u5b50\u5728\u5730\u4e0a\u8d70\u52a8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u90a3\u5bf9\u592b\u59bb\u4e2d\u65e0\u7591\u6709\u4e2a\u4eba\u8d77\u5e8a\u5f00\u95e8\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e09\u66f4\u534a\u591c\uff0c\u51fa\u53bb\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u5c0f\u65b9\u628a\u58f0\u97f3\u538b\u5f97\u6bd4\u521a\u624d\u66f4\u4f4e\uff1a \u3000\u3000\u201c\u6211\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u53bb\u3002\u201d\u201c\u9633\u5149\u201d\u4e00\u4e0b\u5b50\u5c31\u4ece\u5e8a\u4e0a\u8df3\u8d77\u6765\uff1a\u201c\u8fd9\u6b21\u4f60\u53ef\u4e0d\u80fd\u518d\u628a\u6211\u4e00\u4e2a\u4eba\u7559\u5728\u5c4b\u91cc\u3002\u201d \u3000\u3000\u521a\u624d\u7684\u811a\u6b65\u58f0\u597d\u50cf\u662f\u5f80\u53a8\u623f\u90a3\u8fb9\u53bb\u7684\uff0c\u73b0\u5728\u53a8\u623f\u91cc\u5df2\u7ecf\u5e94\u8be5\u6ca1\u6709\u4eba\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5927\u7076\u91cc\u8fd8\u7559\u7740\u706b\u79cd\uff0c\u7076\u4e0a\u8fd8\u6e29\u7740\u4e00\u9505\u6c34\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u6084\u6084\u5730\u8ddf\u8fc7\u53bb\uff0c\u679c\u7136\u770b\u89c1\u6709\u4e2a\u4eba\u5728\u53a8\u623f\u91cc\u3002 \u3000\u3000\u6240\u6709\u7684\u706f\u5149\u90fd\u5df2\u7184\u706d\uff0c\u8fd9\u79cd\u6700\u5ec9\u4ef7\u7684\u5c0f\u5ba2\u6808\uff0c\u662f\u7edd\u4e0d\u80af\u6d6a\u8d39\u4e00\uff0c\u70b9\u706f\u6cb9\u7684\uff0c\u66f4\u4e0d\u4f1a\u6709\u5de1\u591c\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u5929\u4e0a\u8fd8\u6709\u661f\u5149\uff0c\u7076\u91cc\u4ecd\u6709\u4f59\u5149\uff0c\u4ed6\u4eec\u8fd8\u53ef\u4ee5\u770b\u5f97\u89c1\u8fd9\u4e2a\u4eba\u5c31\u662f\u90a3\u4f4d\u8d75\u80e1\u6c0f\u3002 \u3000\u3000\u8d75\u80e1\u6c0f\u6b63\u5728\u8200\u6c34\uff0c\u628a\u5927\u9505\u91cc\u7684\u70ed\u6c34\uff0c\u4e00\u52fa\u4e00\u52fa\u8200\u5165\u4e00\u4e2a\u6728\u6876\u91cc\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u867d\u7136\u62ab\u7740\u5979\u4e08\u592b\u7684\u5927\u68c9\u888d\uff0c\u770b\u8d77\u6765\u5374\u8fd8\u662f\u50cf\u5f88\u51b7\u7684\u6837\u4e86\uff0c\u597d\u50cf\u9664\u4e86\u8fd9\u4ef6\u68c9\u888d\u4e4b\u5916\uff0c\u5979\u8eab\u4e0a\u5c31\u8fde\u4e00\u5bf8\u5e03\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u8df3\u5ffd\u7136\u52a0\u5feb\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u8bc1\u5b9e\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u68c9\u888d\u4e0b\u9762\u679c\u7136\u662f\u7a7a\u7684\u3002 \u3000\u3000\u5979\u521a\u628a\u6ee1\u6ee1\u7684\u4e00\u52fa\u6c34\u8200\u8d77\u6765\uff0c\u5ffd\u7136\u4e00\u4e2a\u4e0d\u5c0f\u5fc3\uff0c\u628a\u6728\u52fa\u91cc\u7684\u6c34\u6253\u7ffb\u4e86\uff0c\u6e85\u5728\u68c9\u888d\u4e0a\u3002\u5979\u8d76\u7d27\u653e\u4e0b\u6728\u52fa\uff0c\u63d0\u8d77\u68c9\u888d\u6765\u6296\u6c34\uff0c\u4e8e\u662f\u5979\u68c9\u888d\u4e0b\u9762\u8d64\u88f8\u5f97\u5c31\u50cf\u662f\u521d\u751f\u5a74\u513f\u4e00\u6837\u7684\u8eab\u4f53\u5c31\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u7684\u8eab\u5b50\u770b\u6765\u5f53\u7136\u7edd\u4e0d\u662f\u4e2a\u521d\u751f\u7684\u5a74\u513f\uff0c\u5979\u7684\u76ae\u80a4\u96ea\u767d\uff0c\u8170\u80a2\u7ea4\u7ec6\uff0c\u53cc\u817f\u4fee\u957f\u7ed3\u5b9e\u3002\u5c0f\u65b9\u89c1\u8fc7\u5404\u5f0f\u5404\u6837\u7684\u5973\u4eba\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u8bf1\u4eba\u7684\u80f4\u4f53\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u5fc3\u51e0\u4e4e\u8981\u4ece\u8154\u5b50\u91cc\u8df3\u51fa\u6765\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u65f6\u5019\u8d75\u80e1\u6c0f\u5df2\u7ecf\u6253\u597d\u4e86\u6c34\uff0c\u63d0\u7740\u6c34\u6876\u8d70\u4e86\u3002\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u8eb2\u5728\u5899\u89d2\u540e\uff0c\u770b\u7740\u5979\u8d70\u8fdc\uff0c\u624d\u957f\u957f\u5410\u51fa\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5ffd\u7136\u95ee\u4ed6\u9053\uff1a\u201c\u4f60\u770b\u89c1\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u6545\u610f\u88c5\u7cca\u6d82\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5fcd\u4e0d\u4f4f\u8981\u7b11\uff1a\u201c\u4f60\u81ea\u5df1\u8be5\u77e5\u9053\u770b\u89c1\u4e86\u4ec0\u4e48\uff0c\u4f60\u770b\u5f97\u6bd4\u6211\u6e05\u695a\u5f97\u591a\u3002\u201d \u3000\u3000\u78b0\u5230\u8fd9\u79cd\u4e8b\u65f6\uff0c\u7537\u4eba\u7684\u773c\u775b\u603b\u662f\u8981\u6bd4\u5973\u4eba\u5c16\u5f97\u591a\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u6709\u627f\u8ba4\u3002\u201c\u9633\u5149\u201d\u7b11\u4e86\u7b11\uff1a \u3000\u3000\u201c\u4f60\u5f53\u7136\u4e5f\u770b\u8fc7\u5979\u7684\u8138\u548c\u624b\uff1f\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u5979\u8138\u4e0a\u548c\u624b\u4e0a\u7684\u76ae\u80a4\u50cf\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u50cf\u6a58\u5b50\u76ae\u3002\u201d\u5c0f\u65b9\u5f62\u5bb9\u5f97\u867d\u7136\u4e0d\u592a\u597d\uff0c\u53ef\u662f\u4e5f\u4e0d\u7b97\u592a\u79bb\u8c31\u3002 \u3000\u3000\u201c\u5979\u8eab\u4e0a\u7684\u76ae\u80a4\u5462\uff1f\u201d\u9633\u5149\u53cd\u95ee\u3002 \u3000\u3000\u5979\u77e5\u9053\u5c0f\u65b9\u5927\u6982\u662f\u4e0d\u80af\u56de\u7b54\u8fd9\u95ee\u9898\u7684\uff0c\u6240\u4ee5\u81ea\u5df1\u63a5\u7740\u8bf4\uff1a\u201c\u5979\u8eab\u4e0a\u7684\u76ae\u80a4\u7b80\u5c31\u50cf\u662f\u7f0e\u5b50\uff0c\u50cf\u7f8a\u5976\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u8fc7\u76ae\u80a4\u50cf\u5979\u8fd9\u4e48\u597d\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u5c0f\u65b9\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u53ef\u662f\u4e00\u4e2a\u5973\u4eba\u8eab\u4e0a\u548c\u8138\u4e0a\u7684\u76ae\u80a4\u662f\u7edd\u4e0d\u5e94\u8be5\u6709\u8fd9\u4e48\u5927\u5dee\u522b\u7684\u3002 \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u66ff\u5c0f\u65b9\u63a5\u4e0b\u53bb\u8bf4\uff1a\u201c\u9664\u975e\u5979\u4e5f\u8ddf\u6211\u4e00\u6837\uff0c\u4e5f\u7528\u4e00\u79cd\u50cf\u2018\u5149\u9634\u6811\u6c41\u2019\u90a3\u6837\u7684\u836f\u7269\uff0c\u628a\u81ea\u5df1\u7684\u8138\u548c\u624b\u90fd\u6539\u53d8\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65e0\u7591\u662f\u552f\u4e00\u7684\u4e00\u79cd\u5408\u7406\u89e3\u91ca\u3002 \u3000\u3000\u8fd9\u5bf9\u592b\u59bb\u6613\u5bb9\u6539\u626e\uff0c\u53c2\u52a0\u8fd9\u5546\u961f\uff0c\u5f53\u7136\u662f\u4e3a\u4e86\u8981\u8ddf\u8e2a\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u3002 \u3000\u3000\u5c31\u7b97\u8fd9\u4ef6\u4e8b\u4e4b\u4e2d\u8fd8\u6709\u4e9b\u65e0\u6cd5\u89e3\u91ca\u7684\u4e8b\uff0c\u8fd9\u4e00\u70b9\u4e5f\u662f\u6beb\u65e0\u7591\u95ee\u7684\u4e86\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5e94\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u6c89\u541f\uff0c\u201c\u770b\u6837\u5b50\u6211\u4eec\u597d\u50cf\u53ea\u6709\u88c5\u7cca\u6d82\uff0c\u53ea\u6709\u7b49\u3002 \u3000\u3000\u201c\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b49\u7740\u770b\u4ed6\u4eec\u7684\u52a8\u9759\uff0c\u7b49\u4ed6\u4eec\u81ea\u5df1\u5148\u6c89\u4e0d\u4f4f\u6c14\uff0c\u7b49\u673a\u4f1a\u51fa\u624b\u3002\u201d \u3000\u3000\u8fd9\u65e0\u7591\u4e5f\u662f\u4ed6\u4eec\u552f\u4e00\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u80fd\u8d70\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u884c\u8e2a\u65e2\u7136\u5df2\u8d25\u9732\uff0c\u65e0\u8bba\u8d70\u5230\u4ec0\u4e48\u5730\u65b9\u90fd\u662f\u4e00\u6837\u7684\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u7b49\u7684\u6ecb\u5473\u5b9e\u5728\u5f88\u4e0d\u597d\u53d7\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u8fd8\u662f\u548c\u524d\u4e00\u5929\u4e00\u6837\uff0c\u592a\u9633\u8fd8\u662f\u4ece\u4e1c\u65b9\u5347\u8d77\uff0c\u961f\u4f0d\u8fd8\u662f\u5f88\u65e9\u5c31\u542f\u7a0b\u3002 \u3000\u3000\u4e0d\u540c\u7684\u662f\uff0c\u6bcf\u5929\u65e9\u4e0a\u90fd\u8981\u9ad8\u8e1e\u5728\u9a6c\u978d\u4e0a\u5c06\u961f\u4f0d\u5de1\u89c6\u4e00\u904d\u7684\u82b1\u4e0d\u62c9\uff0c\u4eca\u5929\u5374\u56e0\u4e3a\u201c\u8eab\u4f53\u4e0d\u9002\u201d\u800c\u6ca1\u6709\u9732\u9762\uff0c\u4ee3\u66ff\u4ed6\u9886\u961f\u7684\u5f53\u7136\u662f\u201c\u5927\u70df\u888b\u201d\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u8fd8\u662f\u548c\u8d75\u7fa4\u592b\u59bb\u540c\u8f66\uff0c\u4e08\u592b\u8fd8\u662f\u90a3\u4e48\u89c4\u77e9\u8001\u5b9e\uff0c\u59bb\u5b50\u8fd8\u662f\u90a3\u4e48\u817c\u8146\u5bb3\u7f9e\uff0c\u603b\u662f\u4e0d\u6562\u62ac\u8d77\u5934\u6765\u89c1\u4eba\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u548c\u5c0f\u65b9\u4e5f\u88c5\u5f97\u597d\u50cf\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\u3001\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u77e5\u9053\u4e00\u3002\u6837\u3002 \u3000\u3000\u5c0f\u65b9\u751a\u81f3\u8fde\u770b\u90fd\u4e0d\u6562\u518d\u53bb\u591a\u770b\u90a3\u4f4d\u8d75\u80e1\u6c0f\uff0c\u56e0\u4e3a\u53ea\u8981\u4e00\u770b\u5230\u5979\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u4f1a\u60f3\u5230\u6628\u5929\u665a\u4e0a\u5728\u90a3\u660f\u9eef\u7684\u53a8\u623f\u91cc\uff0c\u95ea\u52a8\u7684\u7076\u706b\u524d\u7684\u90a3\u4e00\u5e55\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u4f1a\u60f3\u5230\u90a3\u7ea4\u7ec6\u7684\u8170\u80a2\u3001\u96ea\u767d\u4fee\u957f\u7684\u817f\u3002 \u3000\u3000\u90a3\u79cd\u5e7d\u79d8\u90aa\u8273\uff0c\u5145\u6ee1\u4e86\u60c5\u6b32\u6311\u9017\u7684\u666f\u8c61\uff0c\u53eb\u4e00\u4e2a\u7537\u4eba\u4e0d\u53bb\u60f3\u5b83\uff0c\u65e0\u7591\u662f\u975e\u5e38\u56f0\u96be\u7684\u3002 \u3000\u3000\u5e78\u597d\u7b49\u5230\u4e2d\u5348\u6253\u5c16\u8fc7\u540e\uff0c\u201c\u5927\u70df\u888b\u201d\u5c31\u8981\u4ed6\u4eec\u6362\u5230\u53e6\u5916\u4e00\u8f86\u8f66\u5b50\u4e0a\u53bb\u4e86\u3002\u8f66\u884c\u7684\u6b21\u5e8f\uff0c\u597d\u50cf\u4e5f\u6709\u4e86\u5f88\u5927\u7684\u8c03\u52a8\u3002 \u3000\u3000\u6bcf\u8f86\u8f66\u4e0a\u8fd8\u662f\u5750\u56db\u4e2a\u4eba\uff0c\u8fd9\u6b21\u6765\u8ddf\u5c0f\u65b9\u540c\u8f66\u7684\u662f\u4e00\u5bf9\u7236\u5b50\uff0c\u7236\u4eb2\u82cd\u8001\u75b2\u5026\uff0c\u513f\u5b50\u8138\u4e0a\u4e5f\u6709\u75c5\u5bb9\uff0c\u7236\u5b50\u4e24\u4eba\u90fd\u540c\u6837\u6c89\u9ed8\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u770b\u201c\u9633\u5149\u201d\uff0c\u201c\u9633\u5149\u201d\u770b\u770b\u5c0f\u65b9\uff0c\u4e24\u4e2a\u4eba\u5fc3\u91cc\u90fd\u660e\u767d\uff0c\u8981\u60f3\u5e73\u5e73\u5b89\u5b89\u8d70\u5b8c\u8fd9\u4e00\u5929\u7684\u8def\uff0c\u5df2\u7ecf\u4e0d\u592a\u5bb9\u6613\u4e86\u3002 \u3000\u3000\u5348\u65f6\u8fc7\u540e\u961f\u4f0d\u5c31\u8fdb\u5165\u5c71\u533a\u3002 \u3000\u3000\u5c71\u8def\u5f2f\u66f2\u9669\u5cfb\uff0c\u8d77\u4f0f\u7684\u5c71\u4e18\u8fde\u7ef5\u4e0d\u7edd\u7684\u5411\u8fdc\u65b9\u4f38\u5c55\uff0c\u6700\u540e\u624d\u6d88\u5931\u5728\u5929\u8fb9\u7684\u8273\u7ea2\u4e0e\u91d1\u9ec4\u91cc\u3002\u63a5\u8fd1\u8def\u8fb9\u7684\u5c71\u811a\u4e0b\uff0c\u5e03\u6ee1\u5de8\u5927\u7684\u9ed1\u8272\u5ca9\u77f3\uff0c\u4e00\u5ea7\u5dcd\u5ce8\u7684\u9ed1\u8272\u5927\u5c71\uff0c\u5c31\u50cf\u662f\u795e\u8bdd\u4e2d\u7684\u5927\u9e4f\u822c\u51cc\u7a7a\u4fef\u89c6\u7740\u4eba\u7fa4\uff0c\u7ed9\u4eba\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u5de8\u5927\u538b\u529b\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u5750\u5f97\u66f4\u8fd1\u4e86\u4e9b\u3002 \u3000\u3000\u5982\u679c\u6709\u4eba\u8981\u5728\u534a\u8def\u4f0f\u51fb\uff0c\u5c06\u4ed6\u4eec\u51fb\u6740\u5728\u8def\u9014\u4e2d\uff0c\u8fd9\u91cc\u65e0\u7591\u662f\u6700\u597d\u7684\u5730\u70b9\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u60f3\u5728\u640f\u51fb\u4e2d\u5931\u8d25\uff0c\u4ed6\u4eec\u7684\u8eab\u5b50\u9760\u5f97\u5f88\u7d27\uff0c\u5fc3\u91cc\u90fd\u5df2\u6709\u4e86\u51c6\u5907\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u4eec\u542c\u89c1\u4e86\u201c\u683c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u770b\u89c1\u4e86\u4e00\u4e2a\u8f66\u8f6e\u5411\u524d\u98de\u6eda\u51fa\u53bb\uff0c\u649e\u4e0a\u4e86\u8def\u65c1\u7684\u9ed1\u8272\u5ca9\u77f3\uff0c\u649e\u5f97\u7c89\u788e\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u95f4\uff0c\u5c0f\u65b9\u5df2\u62c9\u7740\u201c\u9633\u5149\u201d\u8dc3\u51fa\u4e86\u8f66\u53a2\u3002 \u3000\u3000\u62c9\u8f66\u7684\u9a6c\u8fd8\u5728\u60ca\u5636\u6323\u624e\uff0c\u8f66\u8f6e\u8fd8\u5728\u4e0d\u505c\u6eda\u52a8\uff0c\u5374\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e09\u4e2a\u8f66\u8f6e\u4e86\u3002 \u3000\u3000\u5de6\u9762\u7684\u540e\u8f6e\u8f66\u8f74\u5df2\u65ad\uff0c\u524d\u9762\u7684\u8f66\u9a6c\u961f\u4f0d\u5df2\u4e0d\u89c1\u8e2a\u5f71\u3002 \u3000\u3000\u7fa4\u5c71\u540e\u7684\u8273\u7ea2\u4e0e\u91d1\u9ec4\u5df2\u6e10\u6e10\u53d8\u4e3a\u4e00\u79cd\u867d\u7136\u66f4\u8273\u4e3d\u3001\u5374\u663e\u5f97\u65e0\u9650\u60b2\u6ca7\u7684\u6697\u8d64\u8272\u3002 \u3000\u3000\u9ec4\u660f\u5df2\u5c06\u81f3\uff0c\u9ed1\u591c\u5df2\u5c06\u4e34\u3002 \u3000\u3000\u90a3\u7236\u5b50\u4e24\u4e2a\u4eba\u5c45\u7136\u8fd8\u7559\u5728\u8f66\u53a2\u91cc\uff0c\u4e5f\u4e0d\u77e5\u662f\u4e0d\u662f\u5df2\u7ecf\u6655\u4e86\u8fc7\u53bb\uff0c\u8fd8\u662f\u60f3\u7559\u5728\u8f66\u53a2\u91cc\u7b49\u7740\u5bf9\u4ed6\u4eec\u4f0f\u51fb\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u8bf4\uff1a\u201c\u4f60\u53bb\u770b\u770b\uff0c\u770b\u770b\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u53bb\u770b\u8f66\u53a2\u91cc\u7684\u4eba\uff0c\u53ea\u53bb\u770b\u4e86\u770b\u90a3\u6839\u7a81\u7136\u6298\u65ad\u7684\u8f66\u8f74\u3002 \u3000\u3000\u8f66\u8f74\u65ad\u5f97\u5f88\u6574\u9f50\uff0c\u53ea\u8981\u7565\u6709\u7ecf\u9a8c\u7684\u4eba\uff0c\u90fd\u53ef\u4ee5\u770b\u51fa\u5df2\u7ecf\u5148\u88ab\u4eba\u952f\u65ad\u4e86\u4e00\u534a\u3002 \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u4e5f\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u201c\u6765\u4e86\u3002\u201d\u4ed6\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff1a\u201c\u603b\u7b97\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u4e5f\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4ed6\u4eec\u603b\u7b97\u6ca1\u6709\u8ba9\u6211\u4eec\u7b49\u5f97\u592a\u4e45\u3002\u201d \u3000\u3000\u8f66\u53a2\u91cc\u7684\u7236\u5b50\u4e24\u4e2a\u4eba\u8fd8\u662f\u5168\u65e0\u52a8\u9759\uff0c\u5c31\u7b97\u4ed6\u4eec\u662f\u60f3\u7b49\u673a\u4f1a\u5728\u8f66\u53a2\u4e2d\u6697\u7b97\u4f0f\u51fb\uff0c\u73b0\u5728\u4e5f\u5e94\u8be5\u662f\u65f6\u5019\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u51b7\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u4ed6\u8f7b\u8e22\u8f66\u95e8\u4e00\u4e0b\uff1a\u201c\u4e24\u4f4d\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000\u8f66\u53a2\u4e2d\u4ecd\u7136\u6ca1\u6709\u53cd\u5e94\uff0c\u9669\u5cfb\u66f2\u6298\u7684\u5c71\u8def\u4e24\u7aef\u4e5f\u4ecd\u7136\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u8e22\u8d77\u4e00\u811a\uff0c\u8e22\u788e\u4e86\u7528\u6728\u6761\u8349\u5e2d\u642d\u8d77\u7684\u7b80\u964b\u8f66\u53a2\u3002 \u3000\u3000\u90a3\u7236\u5b50\u4e24\u4e2a\u4eba\u5f53\u7136\u8fd8\u5728\u91cc\u9762\uff0c\u4e24\u4e2a\u4eba\u624b\u91cc\u90fd\u63e1\u7740\u7528\u9ec4\u94dc\u6253\u6210\u7684\u673a\u7c27\u6697\u5668\u7b52\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u7b52\u4e2d\u7684\u6697\u5668\u5e76\u6ca1\u6709\u53d1\u51fa\u6765\uff0c\u7236\u5b50\u4e24\u4eba\u7684\u8eab\u5b50\u7adf\u5df2\u50f5\u786c\uff0c\u8138\u8272\u5df2\u53d1\u9ed1\uff0c\u56db\u53ea\u773c\u775b\u51f8\u51fa\u5982\u6b7b\u9c7c\uff0c\u773c\u91cc\u5145\u6ee1\u60ca\u5413\u6050\u60e7\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u679c\u7136\u662f\u5bf9\u65b9\u7279\u5730\u57cb\u4f0f\u5728\u8f66\u91cc\u7b49\u7740\u5bf9\u4ed8\u4ed6\u4eec\u7684\u6740\u624b\uff0c\u7b49\u7740\u5728\u8f66\u8eab\u503e\u8986\u7684\u90a3\u4e00\u77ac\u95f4\u51fa\u624b\u3002 \u3000\u3000\u90a3\u65f6\u65e0\u7591\u662f\u6700\u597d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4e24\u4e2a\u4eba\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5c31\u5728\u4ed6\u4eec\u51c6\u5907\u51fa\u624b\u65f6\u5c31\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u552f\u4e00\u7684\u7b54\u6848\u662f\u2014\u2014 \u3000\u3000\u201c\u9633\u5149\u201d\u5df2\u7ecf\u770b\u51fa\u4e86\u4ed6\u4eec\u7684\u9634\u8c0b\uff0c\u6240\u4ee5\u5148\u53d1\u5236\u4eba\uff0c\u5148\u4e0b\u4e86\u6bd2\u624b\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u7740\u201c\u9633\u5149\u201d\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u4f60\u771f\u884c\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u51fa\u624b\u5b9e\u5728\u6bd4\u6211\u60f3\u50cf\u4e2d\u5feb\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u9633\u5149\u597d\u50cf\u4e0d\u61c2\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u4e0d\u4f1a\u90a3\u4e48\u5feb\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u8fd8\u4e0d\u80fd\u8bc1\u660e\u4ed6\u4eec\u771f\u7684\u662f\u5bf9\u65b9\u7684\u4eba\uff0c\u4e07\u4e00\u6740\u9519\u4e86\u4eba\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u770b\u7740\u4ed6\uff0c\u663e\u5f97\u5f88\u5403\u60ca\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u6211\u6740\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u4e8c\u7ae0\u3000\u513f\u987b\u6210\u540d\xb7\u9152\u987b\u9189 \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u513f\u987b\u6210\u540d\xb7\u9152\u987b\u9189 \u3000\u3000\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d\u201c\u9633\u5149\u201d\u8bf4\uff0c\u201c\u6211\u672c\u6765\u8fd8\u4ee5\u4e3a\u662f\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u66f4\u5403\u60ca\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5f53\u7136\u77e5\u9053\u8fd9\u4e24\u4e2a\u4eba\u7edd\u4e0d\u662f\u6b7b\u5728\u4ed6\u7684\u624b\u91cc\u7684\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u53c8\u95ee\uff1a\u201c\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4e0d\u662f\u4f60\uff0c\u4e5f\u4e0d\u662f\u6211\uff0c\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u5c31\u4e0d\u662f\u4ed6\u4eec\u6240\u80fd\u7b54\u590d\u7684\u4e86\u3002 \u3000\u3000\u6b7b\u4eba\u7684\u8138\u8272\u5df2\u53d1\u9ed1\uff0c\u770b\u6765\u597d\u50cf\u662f\u4e2d\u4e86\u6bd2\u2014\u2014\u8c01\u4e0b\u7684\u6bd2\uff1f\u4ec0\u4e48\u65f6\u5019\u4e0b\u7684\u6bd2\uff1f\u4e3a\u4ec0\u4e48\u8981\u6bd2\u6b7b\u4ed6\u4eec\uff1f\u662f\u4e0d\u662f\u4e3a\u4e86\u5e2e\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u89e3\u9664\u8fd9\u4e00\u6b21\u5371\u673a\uff1f\u8fd9\u961f\u4f0d\u91cc\u600e\u4e48\u4f1a\u6709\u4ed6\u4eec\u7684\u5e2e\u624b\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\uff0c\u5f53\u7136\u4e5f\u4e0d\u662f\u4ed6\u4eec\u6240\u80fd\u7b54\u590d\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u6b63\u5728\u60ca\u5f02\uff0c\u8def\u65c1\u7684\u9ed1\u77f3\u540e\u5df1\u51fa\u73b0\u4e86\u56db\u4e94\u5341\u4e2a\u4eba\u3002 \u3000\u3000\u56db\u4e94\u5341\u4e2a\u5e26\u7740\u7bad\u7684\u4eba\u3002 \u3000\u3000\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u6709\u6c49\u4eba\uff0c\u6709\u85cf\u4eba\uff0c\u6709\u82d7\u4eba\uff0c\u5e26\u7740\u5404\u5f0f\u5404\u6837\u7684\u7bad\uff0c\u6709\u957f\u5f13\u5927\u7bad\uff0c\u6709\u673a\u7c27\u786c\u5f2f\uff0c\u8fd8\u6709\u82d7\u4eba\u730e\u517d\u7528\u7684\u5439\u7bad\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u4e00\u773c\u5c31\u80fd\u5c06\u8fd9\u4e9b\u7bad\u7684\u79cd\u7c7b\u5206\u8fa8\u51fa\u6765\uff0c\u4f46\u662f\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u6bcf\u79cd\u7bad\u90fd\u80fd\u5236\u4eba\u6b7b\u547d\uff01 \u3000\u3000\u8fd9\u91cc\u662f\u5c71\u8def\u6700\u9669\u7684\u4e00\u73af\uff0c\u5982\u679c\u6709\u4eba\u4e00\u58f0\u4ee4\u4e0b\uff0c\u4e71\u7bad\u9f50\u53d1\uff0c\u7eb5\u7136\u662f\u535c\u9e70\u90a3\u6837\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u4e5f\u5f88\u96be\u95ef\u5f97\u8fc7\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u4ed6\u770b\u5f97\u51fa\u8fd9\u4e00\u70b9\uff0c\u8fd9\u4e00\u6b21\u4ed6\u548c\u201c\u9633\u5149\u201d\u7684\u673a\u4f1a\u5b9e\u5728\u4e0d\u5927\u3002 \u3000\u3000\u56db\u5c71\u6c89\u5bc2\uff0c\u9ed1\u77f3\u65e0\u58f0\uff0c\u7bad\u65e0\u58f0\uff0c\u4eba\u4e5f\u65e0\u58f0\uff0c\u4ed6\u4eec\u597d\u50cf\u4e5f\u5728\u7b49\uff0c\u7b49\u4ec0\u4e48\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u7684\u7b54\u6848\u5c0f\u65b9\u5f88\u5feb\u5c31\u77e5\u9053\u4e86\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u662f\u5728\u7b49\u82b1\u4e0d\u62c9\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u770b\u89c1\u4e86\u82b1\u4e0d\u62c9\u3002 \u3000\u3000\u82b1\u4e0d\u62c9\u9ad8\u8e1e\u5728\u6700\u9ad8\u7684\u4e00\u5757\u5ca9\u77f3\u4e0a\uff0c\u7528\u90a3\u53cc\u5145\u6ee1\u8ba5\u6d88\u7684\u773c\u775b\u51b7\u51b7\u5730\u770b\u7740\u4ed6\u4eec\u2014\u2014\u5c31\u50cf\u662f\u4e00\u53ea\u732b\u770b\u7740\u722a\u4e0b\u7684\u9f20\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8fd9\u6b21\u4ed6\u4eec\u662f\u7edd\u5bf9\u9003\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u60f3\u5230\u82b1\u4e0d\u62c9\u4e5f\u662f\u5415\u4e09\u5c5e\u4e0b\u7684\u4eba\uff0c\u73ed\u5bdf\u5df4\u90a3\u505a\u4e8b\u4e00\u5411\u7cbe\u5bc6\u8c28\u614e\uff0c\u600e\u4e48\u4f1a\u5728\u8fd8\u6ca1\u6709\u67e5\u51fa\u8fd9\u4e2a\u4eba\u7684\u8eab\u4efd\u65f6\uff0c\u5c31\u628a\u4ed6\u4eec\u9001\u5230\u4ed6\u7684\u961f\u4f0d\u53bb\uff1f \u3000\u3000\u82b1\u4e0d\u62c9\u5ffd\u7136\u5f00\u53e3\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4eec\u5c31\u4e0d\u5982\u4e56\u4e56\u5730\u8ddf\u6211\u56de\u5bb6\u53bb\u5427\u3002\u201d \u3000\u3000\u201c\u56de\u5bb6\uff1f\u201d\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u56de\u8c01\u7684\u5bb6\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u4f60\u4eec\u81ea\u5df1\u7684\u5bb6\u3002\u201d \u3000\u3000\u82b1\u4e0d\u62c9\u5f97\u610f\u5730\u7b11\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u603b\u7b97\u77e5\u9053\uff0c\u51fa\u5916\u5bf8\u6b65\u96be\uff0c\u8fd8\u662f\u56de\u5bb6\u7684\u597d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u66f4\u60ca\u8bb6\u3002 \u3000\u3000\u4ed6\u6839\u672c\u542c\u4e0d\u61c2\u82b1\u4e0d\u62c9\u5728\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u4eec\u73b0\u5728\u6839\u672c\u5df2\u7ecf\u6ca1\u6709\u5bb6\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u61c2\uff0c\u201c\u9633\u5149\u201d\u4e5f\u4e0d\u61c2\u3002\u4e24\u4e2a\u4eba\u90fd\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u56de\u7b54\uff0c\u53ea\u6709\u4fdd\u6301\u6c89\u9ed8\u3002 \u3000\u3000\u6709\u65f6\u201c\u6c89\u9ed8\u201d\u5c31\u662f\u201c\u9ed8\u8ba4\u201d\uff0c\u5c31\u662f\u201c\u7b54\u5e94\u201d\uff0c\u6240\u4ee5\u82b1\u4e0d\u62c9\u7b11\u5f97\u5f88\u6109\u5feb\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4eec\u4e00\u5b9a\u4e0d\u4f1a\u4e0d\u542c\u8bdd\u7684\uff0c\u53ea\u4e0d\u8fc7\u6211\u8fd9\u4eba\u505a\u4e8b\u4e00\u5411\u7279\u522b\u5c0f\u5fc3\uff0c\u5bf9\u4f60\u4eec\u6709\u4e00\u70b9\u4e0d\u592a\u653e\u5fc3\u3002\u201d \u3000\u3000\u82b1\u4e0d\u62c9\u6545\u610f\u60f3\u4e86\u60f3\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u5982\u679c\u4f60\u4eec\u80af\u5148\u7528\u7ef3\u5b50\u628a\u81ea\u5df1\u7684\u624b\u811a\u7ed1\u8d77\u6765\uff0c\u6253\u4e0a\u4e09\u4e2a\u6b7b\u7ed3\uff0c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5f3a\u8c03\uff1a\u201c\u4e00\u5b9a\u8981\u6253\u6b7b\u7ed3\u3002\u6211\u7684\u773c\u775b\u7279\u522b\u597d\uff0c\u4f60\u4eec\u7792\u4e0d\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u7136\u540e\u5462\uff1f\u201d\u5c0f\u65b9\u6545\u610f\u95ee\u3002 \u3000\u3000\u201c\u7136\u540e\u6211\u5f53\u7136\u5c31\u4f1a\u597d\u597d\u5730\u9001\u4f60\u4eec\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u82b1\u4e0d\u62c9\u5ffd\u7136\u6c89\u4e0b\u8138\uff1a\u201c\u5982\u679c\u6211\u6570\u5230\u2018\u4e09\u2019\u5b57\u4f60\u4eec\u8fd8\u4e0d\u52a8\u624b\uff0c\u6211\u5c31\u53ea\u597d\u628a\u4f60\u4eec\u7684\u6b7b\u5c38\u9001\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u82b1\u4e0d\u62c9\u771f\u7684\u7acb\u523b\u5c31\u5f00\u59cb\u5728\u6570\u3002 \u3000\u3000\u4ed6\u867d\u7136\u677f\u7740\u8138\uff0c\u773c\u91cc\u5374\u5145\u6ee1\u4e86\u90a3\u79cd\u6b8b\u9177\u800c\u8ba5\u6d88\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u5f97\u51fa\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u8981\u4ed6\u4eec\u81ea\u5df1\u52a8\u624b\uff0c\u66f4\u4e0d\u662f\u771f\u7684\u60f3\u628a\u4ed6\u4eec\u597d\u597d\u5730\u9001\u8d70\u3002 \u3000\u3000\uff0c\u4ed6\u8fd9\u4e48\u6837\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u662f\u8981\u5bf9\u67d0\u4e00\u4e2a\u4eba\u4f5c\u67d0\u79cd\u4ea4\u4ee3\u800c\u5df2\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5fc3\u91cc\u771f\u6b63\u5e0c\u671b\u7684\u662f\u770b\u7740\u4e71\u7bad\u9f50\u53d1\u3001\u8840\u8089\u6a2a\u98de\uff0c\u770b\u7740\u4e00\u6839\u6839\u5404\u5f0f\u5404\u6837\u7684\u5f29\u7bad\u6253\u8fdb\u4ed6\u4eec\u7684\u9762\u76ee\u8840\u8089\u9aa8\u8282\u91cc\uff0c\u518d\u628a\u4ed6\u4eec\u7684\u6b7b\u5c38\u9001\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u6570\u5f97\u5f88\u6162\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u80af\u81ea\u5df1\u628a\u81ea\u5df1\u7684\u624b\u811a\u7ed1\u8d77\u6765\u7684\u3002 \u3000\u3000\u53ea\u6570\u5230\u201c\u4e8c\u201d\u5b57\uff0c\u53ea\u542c\u201c\u683c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5df2\u7ecf\u6709\u4e00\u6392\u5f2f\u7bad\u5c04\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e00\u6392\u8fde\u73af\u80be\uff0c\u4e09\u679d\u7bad\u540c\u65f6\u53d1\u51fa\uff0c\u6253\u7684\u7adf\u4e0d\u662f\u201c\u9633\u5149\u201d\u548c\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u4e09\u679d\u7bad\u540c\u65f6\u6253\u5728\u5bf9\u9762\u7684\u5ca9\u77f3\u4e0a\uff0c\u706b\u661f\u56db\u6e85\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5ffd\u7136\u4ece\u534a\u7a7a\u4e2d\u843d\u4e0b\uff0c\u8dcc\u5728\u5c71\u8def\u4e0a\uff0c\u5934\u9885\u88ab\u6454\u5f97\u7c89\u788e\uff0c\u5374\u6ca1\u6709\u60e8\u547c\u58f0\u53d1\u51fa\uff0c\u56e0\u4e3a\u4ed6\u8dcc\u4e0b\u6765\u4e4b\u524d\u5c31\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u602a\u547c\u58f0\u662f\u5728\u8dcc\u4e0b\u4e4b\u540e\u53d1\u51fa\u6765\u7684\uff0c\u662f\u522b\u4eba\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u5ca9\u77f3\u4e0a\u5ffd\u7136\u95ea\u8d77\u4e86\u4e00\u9053\u96ea\u4eae\u7684\u5251\u5149\u3002 \u3000\u3000\u5251\u5149\u98de\u52a8\u5982\u95ea\u7535\uff0c\u602a\u547c\u58f0\u8fde\u7ef5\u4e0d\u7edd\uff0c\u57cb\u4f0f\u5728\u5ca9\u77f3\u4e0a\u7684\u7bad\u624b\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u5012\u4e0b\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5931\u58f0\u800c\u547c\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\uff01\u201d \u3000\u3000\u6765\u6551\u4ed6\u4eec\u7684\u5f53\u7136\u662f\u73ed\u5bdf\u5df4\u90a3\uff0c\u9664\u4e86\u73ed\u5bdf\u5df4\u90a3\u8fd8\u6709\u8c01\uff1f \u3000\u3000\u82b1\u4e0d\u62c9\u8138\u8272\u60e8\u53d8\uff0c\u5c0f\u65b9\u5df2\u5982\u75be\u98ce\u822c\u6251\u4e0a\u53bb\uff0c\u82b1\u4e0d\u62c9\u5927\u559d\u4e00\u58f0\uff0c\u7528\u5de8\u65a7\u822c\u7684\u5927\u624b\uff0c\u62bd\u51fa\u4e00\u6761\u6c89\u91cd\u7684\u94c1\u97ad\uff0c\u631f\u5e26\u52b2\u98ce\u6325\u4e0b\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u6709\u6682\u65f6\u540e\u9000\u95ea\u907f\u3002\u82b1\u4e0d\u62c9\u638c\u4e2d\u94c1\u97ad\u8fde\u73af\u98de\u821e\u540e\uff0c\u4e0d\u4f46\u5360\u5c3d\u5730\u5229\uff0c\u4e5f\u62a2\u4e86\u5148\u673a\u3002 \u3000\u3000\u5ca9\u77f3\u4e0a\u7684\u7bad\u624b\u8fd8\u6ca1\u6709\u6b7b\u5149\uff0c\u8fd8\u6709\u5f29\u7bad\u5c04\u51fa\uff0c\u3002\u2018\u9633\u5149\u201d\u597d\u50cf\u4e2d\u4e86\u4e00\u7bad\u3002 \u3000\u3000\u5c0f\u65b9\u7b2c\u56db\u6b21\u5f80\u4e0a\u6251\u65f6\uff0c\u82b1\u4e0d\u62c9\u624b\u91cc\u98de\u821e\u7684\u94c1\u97ad\u5ffd\u7136\u5782\u4e0b\uff0c\u5c31\u50cf\u6761\u6b7b\u86c7\u822c\u5782\u4e0b\u3002 \u3000\u3000\u82b1\u4e0d\u62c9\u7684\u8138\u8272\u5ffd\u7136\u626d\u66f2\uff0c\u53d1\u4eae\u7684\u773c\u775b\u5ffd\u7136\u53d8\u6210\u6b7b\u7070\u8272\uff0c\u4e5f\u50cf\u662f\u6761\u6bd2\u86c7\u5ffd\u7136\u88ab\u4eba\u65a9\u65ad\u4e86\u4e03\u5bf8\u3002 \u3000\u3000\u4ed6\u5782\u4e0b\u5934\uff0c\u770b\u7740\u81ea\u5df1\u7684\u80f8\u819b\uff0c\u6b7b\u7070\u8272\u7684\u773c\u775b\u91cc\u5145\u6ee1\u6050\u60e7\u60ca\u8bb6\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u5728\u770b\u7740\u4ed6\u7684\u80f8\u819b\uff0c\u773c\u4e2d\u4e5f\u5145\u6ee1\u60ca\u8bb6\uff0c\u56e0\u4e3a\u4ed6\u7684\u80f8\u819b\u91cc\u7adf\u5ffd\u7136\u6709\u6837\u4e1c\u897f\u7a7f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e00\u6837\u53d1\u4eae\u7684\u4e1c\u897f\uff0c\u4e00\u622a\u53d1\u4eae\u7684\u5251\u5c16\u3002 \u3000\u3000\u4e00\u67c4\u5251\u4ece\u4ed6\u80cc\u540e\u523a\u5165\uff0c\u524d\u80f8\u7a7f\u51fa\uff0c\u4e00\u5251\u7a7f\u900f\u4e86\u4ed6\u7684\u5fc3\u810f\u3002 \u3000\u3000\u5251\u5c16\u8fd8\u5728\u6ef4\u8840\u65f6\u5c31\u5df2\u62bd\u51fa\u3002 \u3000\u3000\u82b1\u4e0d\u62c9\u5012\u4e0b\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7ad9\u5728\u82b1\u4e0d\u62c9\u8eab\u540e\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u67c4\u5251\uff0c\u5c31\u662f\u521a\u624d\u5728\u7247\u523b\u95f4\u523a\u6740\u6570\u5341\u7bad\u624b\u7684\u5251\uff0c\u4e5f\u5c31\u662f\u4e00\u5251\u7a7f\u900f\u82b1\u4e0d\u62c9\u5fc3\u810f\u7684\u5251\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7adf\u4e0d\u662f\u73ed\u5bdf\u5df4\u90a3\uff01\u4ed6\u624b\u91cc\u63d0\u7740\u5251\uff0c\u7adf\u8d6b\u7136\u662f\u5c0f\u65b9\u7684\u201c\u9b54\u773c\u201d\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f \u3000\u3000\u9664\u4e86\u73ed\u5bdf\u5df4\u90a3\u5916\uff0c\u8fd8\u6709\u8c01\u4f1a\u6765\u6551\u5c0f\u65b9\u548c\uff0c\u201c\u9633\u5149\u201d\uff1f \u3000\u3000\u4ed6\u624b\u91cc\u600e\u4e48\u4f1a\u6709\u5c0f\u65b9\u7684\u201c\u9b54\u773c\u201d\u3002 \u3000\u3000\u535c\u9e70\uff1f \u3000\u3000\u662f\u4e0d\u662f\u535c\u9e70\u7ec8\u4e8e\u51fa\u73b0\u4e86\uff1f \u3000\u3000\u8fd8\u6ca1\u6709\u770b\u6e05\u8fd9\u4e2a\u4eba\u7684\u8138\u65f6\uff0c\u5c0f\u65b9\u7684\u786e\u8fd9\u4e48\u6837\u60f3\u8fc7\uff0c\u8fd9\u60f3\u6cd5\u4f7f\u4ed6\u6fc0\u52a8\u5f97\u5168\u8eab\u90fd\u5728\u98a4\u6296\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u53c8\u60f3\u9519\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u65e2\u4e0d\u662f\u73ed\u5bdf\u5df4\u90a3\uff0c\u4e5f\u4e0d\u662f\u535c\u9e70\uff0c\u800c\u662f\u4e2a\u4ed6\u4ece\u672a\u60f3\u5230\u4f1a\u6765\u6551\u4ed6\u4eec\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8d6b\u7136\u7adf\u662f\u8d75\u7fa4\uff0c\u90a3\u4e2a\u89c4\u89c4\u77e9\u77e9\u8001\u8001\u5b9e\u5b9e\u3001\u8fde\u4ed8\u51fa\u4e8c\u5341\u4e94\u4e24\u94f6\u5b50\u65f6\u4e00\u53cc\u624b\u90fd\u4f1a\u7d27\u5f20\u5f97\u53d1\u6296\u7684\u4eba\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u624b\u5374\u6bd4\u78d0\u77f3\u8fd8\u7a33\u5b9a\u3002 \u3000\u3000\u4ed6\u7684\u624b\u91cc\u63e1\u7740\u5251\uff0c\u63e1\u7740\u7684\u662f\u5c0f\u65b9\u7684\u201c\u9b54\u773c\u201d\u3002 \u3000\u3000\u201c\u9b54\u773c\u201d\u5728\u95ea\u52a8\u7740\u795e\u79d8\u800c\u5996\u5f02\u7684\u5bd2\u5149\uff0c\u4ed6\u7684\u773c\u775b\u91cc\u4e5f\u5728\u95ea\u7740\u5149\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u4e0d\u518d\u662f\u90a3\u4e2a\u89c4\u77e9\u8001\u5b9e\u7684\u4eba\u4e86\uff0c\u4ed6\u8eab\u4e0a\u6563\u53d1\u51fa\u7684\u6740\u6c14\u751a\u81f3\u6bd4\u201c\u9b54\u773c\u201d\u7684\u5251\u6c14\u66f4\u53ef\u6015\u3002 \u3000\u3000\u201c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u662f\u4e2a\u6740\u4eba\u7684\u4eba\uff0c\u4e5f\u662f\u4e2a\u6551\u4eba\u7684\u4eba\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u6740\u7684\u662f\u522b\u4eba\uff0c\u6551\u7684\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6765\u6551\u6211\uff1f\u201d \u3000\u3000\u3001\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8981\u6740\u7684\u5e76\u4e0d\u662f\u4f60\u3002\u201d\u8d75\u7fa4\u9053\uff0c\u201c\u56e0\u4e3a\u4f60\u672c\u6765\u5c31\u4e0d\u8be5\u6b7b\u7684\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u4ed6\u4eec\u8981\u6740\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u8d75\u7fa4\u7684\u56de\u7b54\u4ee4\u4eba\u4e0d\u80fd\u4e0d\u60ca\u8bb6\uff0c\u201c\u4ed6\u4eec\u672c\u6765\u8981\u6740\u7684\u4eba\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u8fd8\u6709\u5f88\u591a\u95ee\u9898\u60f3\u95ee\uff0c\u4f46\u662f\u8d75\u7fa4\u5df2\u8f6c\u8fc7\u8eab\u3002 \u3000\u3000\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff0c\u201c\u6211\u5e26\u4f60\u559d\u9152\u53bb\uff0c\u6211\u77e5\u9053\u9644\u8fd1\u6709\u4e2a\u5730\u65b9\u7684\u9152\u5f88\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c0f\u65b9\u867d\u7136\u4e5f\u89c9\u5f97\u5f88\u9700\u8981\u559d\u4e00\u676f\uff1a\u201c\u4f46\u662f\u73b0\u5728\u597d\u50cf\u8fd8\u4e0d\u5230\u5e94\u8be5\u559d\u9152\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u7ecf\u5230\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u6709\u8bdd\u8981\u95ee\u6211\uff0c\u6211\u4e5f\u6709\u8bdd\u8981\u8bf4\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u4f46\u662f\u6211\u6709\u5f88\u591a\u8bdd\u90fd\u8981\u7b49\u5230\u559d\u4e86\u9152\u4e4b\u540e\u624d\u80fd\u8bf4\u5f97\u51fa\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u524d\u9762\u7684\u5c71\u5773\uff0c\u8c37\u5730\u91cc\u6709\u4e2a\u5c0f\u5c0f\u7684\u5c71\u6751\u3002\u5c71\u6c11\u6df3\u6734\u6e29\u539a\uff0c\u53ef\u662f\u4ed6\u4eec\u7528\u9ea6\u6746\u917f\u7684\u9152\u559d\u5230\u5634\u91cc\u65f6\u5374\u50cf\u662f\u4e00\u56e2\u70c8\u706b\u3002 \u3000\u3000\u4ed6\u4eec\u559d\u9152\u7684\u5730\u65b9\u5e76\u4e0d\u662f\u7267\u7ae5\u53ef\u4ee5\u9065\u6307\u7684\u674f\u82b1\u6751\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8d2b\u82e6\u7684\u6a35\u6237\u4eba\u5bb6\u800c\u5df2\uff0c\u5982\u679c\u6709\u8fc7\u8def\u7684\u65c5\u4eba\u6765\u4e70\u9152\u559d\uff0c\u4ed6\u4eec\u7684\u5b69\u5b50\u5728\u8fc7\u5e74\u65f6\u5c31\u53ef\u4ee5\u7a7f\u4e0a\u6761\u65b0\u68c9\u88e4\u4e86\u3002 \u3000\u3000\u4e3b\u4eba\u7528\u4e00\u53cc\u751f\u6ee1\u8001\u8327\u7684\u624b\u6367\u51fa\u4e2a\u74e6\u7f50\uff0c\u7528\u5c0f\u65b9\u542c\u4e0d\u61c2\u7684\u8bed\u8a00\u5bf9\u8d75\u7fa4\u8bf4\u4e86\u4e9b\u8bdd\uff0c\u5c31\u5e26\u7740\u59bb\u513f\u8d70\u4e86\uff0c\u5c06\u4e09\u95f4\u5c0f\u5c0f\u7684\u77f3\u5c4b\u7559\u7ed9\u4ed6\u4eec\u7684\u8d35\u5ba2\u3002 \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u521a\u624d\uff0c\u4ed6\u5728\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u8fd9\u79cd\u9152\u53eb\u2018\u65a7\u5934\u2019\uff0c\u53ea\u6709\u7537\u5b50\u6c49\u624d\u80fd\u559d\u3002\u201d \u3000\u3000\u8d75\u7fa4\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u770b\u5f97\u51fa\u6211\u4eec\u662f\u7537\u5b50\u6c49\uff0c\u6240\u4ee5\u624d\u62ff\u8fd9\u79cd\u9152\u7ed9\u6211\u4eec\u559d\u3002\u201d \u3000\u3000\u4ed6\u5e26\u7740\u7b11\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u660e\u767d\u4ed6\u7684\u610f\u601d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u660e\u767d\uff1a\u201c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u5927\u6982\u662f\u5e0c\u671b\u6211\u4eec\u4ed8\u94b1\u65f6\u4e5f\u50cf\u4e2a\u7537\u5b50\u6c49\u3002\u201d \u3000\u3000\u5c4b\u5b50\u7684\u56db\u58c1\u90fd\u662f\u7528\u77f3\u5757\u780c\u6210\u7684\uff0c\u4e00\u4e2a\u5f88\u5927\u5f88\u5927\u7684\u77f3\u5934\u706b\u7089\u4e0a\u70e7\u7740\u4e00\u9505\u5154\u8089\uff0c\u4e00\u5927\u5757\u6728\u67f4\u6b63\u70e7\u5f97\u5288\u62cd\u53d1\u54cd\uff0c\u5c4b\u5b50\u91cc\u5145\u6ee1\u4e86\u8089\u9999\u548c\u677e\u9999\u3002 \u3000\u3000\u5973\u4eba\u4e0d\u5728\u8fd9\u95f4\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u4e2d\u4e86\u7bad\uff0c\u4e2d\u7bad\u7684\u5730\u65b9\u662f\u5728\u7537\u4eba\u4e0d\u80fd\u770b\u89c1\u7684\u5730\u65b9\u3002 \u3000\u3000\u8d75\u80e1\u6c0f\u5e26\u5979\u5230\u540e\u9762\u4e00\u95f4\u5c0f\u5c4b\u91cc\uff0c\u7528\u7537\u4eba\u559d\u7684\u70c8\u9152\u66ff\u5979\u6d17\u6da4\u4f24\u53e3\uff0c\u75bc\u5f97\u5979\u5168\u8eab\u90fd\u88ab\u51b7\u6c57\u6e7f\u900f\uff0c\u4f46\u662f\u5979\u5e76\u6ca1\u6709\u6f0f\u6389\u5916\u9762\u90a3\u95ee\u5c4b\u91cc\u7684\u7537\u4eba\u4eec\u8bf4\u7684\u6bcf\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4e09\u7897\u201c\u65a7\u5934\u201d\u4e0b\u809a\uff0c\u9152\u610f\u5df2\u51b2\u4e0a\u4e86\u5934\u9876\u3002 \u3000\u3000\u5148\u5f00\u53e3\u7684\u662f\u5c0f\u65b9\uff0c\u4ed6\u95ee\u8d75\u7fa4\uff1a\u201c\u4f60\u8bf4\u4ed6\u4eec\u672c\u6765\u8981\u6740\u7684\u662f\u4f60\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6709\u4e9b\u662f\u5415\u4e09\u7684\u4eba\u3002\u201d \u3000\u3000\u8d75\u7fa4\u7acb\u523b\u56de\u7b54\uff0c\u201c\u82b1\u4e0d\u62c9\u4e5f\u6536\u4e86\u5415\u4e09\u7684\u94f6\u5b50\uff0c\u6240\u4ee5\u4eca\u5929\u4e00\u65e9\u5c31\u53bb\u62a5\u8baf\uff0c\u5e26\u4e86\u5415\u4e09\u7684\u4eba\u6765\u3002\u201d \u3000\u3000\u201c\u6765\u6740\u4f60\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u6765\u6551\u6211\uff1f\u201d \u3000\u3000\u8d75\u7fa4\u56de\u7b54\u5f97\u975e\u5e38\u8f7b\u677e\uff0c\u65e0\u8bba\u8c01\u559d\u4e86\u8fd9\u79cd\u9152\u4e4b\u540e\u8bf4\u8bdd\u90fd\u4e0d\u4f1a\u518d\u6709\u987e\u5fcc\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u672c\u6765\u4e5f\u662f\u4ed6\u7684\u4eba\uff0c\u800c\u4e14\u662f\u4ed6\u975e\u5e38\u4fe1\u4efb\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u4f46\u662f\u6211\u5374\u5e26\u7740\u4ed6\u6700\u5ba0\u7231\u7684\u4e00\u4e2a\u5973\u4eba\u79c1\u5954\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u6e10\u6e10\u660e\u767d\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u5973\u4eba\u201d\uff0c\u5f53\u7136\u5c31\u662f\u8d75\u80e1\u6c0f\uff0c\u5979\u672c\u6765\u5c31\u662f\u4e2a\u5c11\u89c1\u7684\u5c24\u7269\uff0c\u5c0f\u65b9\u968f\u65f6\u90fd\u53ef\u4ee5\u60f3\u51fa\u5f88\u591a\u5415\u4e09\u4e3a\u4ec0\u4e48\u820d\u4e0d\u5f97\u653e\u5979\u8d70\u7684\u7406\u7531\u6765\u3002 \u3000\u3000\u8d75\u7fa4\u80af\u4e0d\u987e\u4e00\u5207\u5192\u9669\u5e26\u5979\u79c1\u5954\uff0c\u7406\u7531\u4e5f\u540c\u6837\u5145\u5206\uff0c\u5c0f\u65b9\u76f8\u4fe1\u6709\u5f88\u591a\u7537\u4eba\u90fd\u4f1a\u4e3a\u5979\u8fd9\u4e48\u505a\u7684\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u4eec\u672c\u6765\u5c31\u6bd4\u8f83\u76f8\u914d\uff0c\u81f3\u5c11\u6bd4\u5979\u8ddf\u5415\u4e09\u76f8\u914d\u5f97\u591a\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u5c0f\u65b9\u53ef\u4ee5\u539f\u8c05\u4ed6\u4eec\u3002 \u3000\u3000\u8d75\u7fa4\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u5374\u6709\u6b49\u610f\uff1a\u201c\u6211\u672c\u6765\u5e76\u4e0d\u60f3\u8fde\u7d2f\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u8bda\u6073\uff1a\u201c\u4f46\u662f\u6211\u77e5\u9053\u5415\u4e09\u5df2\u7ecf\u4e70\u901a\u82b1\u4e0d\u62c9\uff0c\u5df2\u7ecf\u6000\u7591\u6211\u4eec\u5f88\u53ef\u80fd\u6df7\u5728\u8fd9\u4e2a\u5546\u961f\u91cc\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u6545\u610f\u5c06\u90a3\u53ea\u91d1\u624b\u585e\u8fdb\u6211\u4eec\u7684\u5305\u88b1\u91cc\uff0c\u8ba9\u82b1\u4e0d\u62c9\u6000\u7591\u6211\u4eec\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u53ef\u662f\u6211\u5e76\u4e0d\u662f\u60f3\u5bb3\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u6211\u8fd9\u4e48\u505a\uff0c\u53ea\u4e0d\u8fc7\u60f3\u8f6c\u79fb\u4ed6\u4eec\u7684\u76ee\u6807\uff0c\u8ba9\u4ed6\u4eec\u96c6\u4e2d\u529b\u91cf\u5bf9\u4ed8\u4f60\u4eec\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u8fd9\u6837\u6211\u624d\u6709\u6bd4\u8f83\u597d\u7684\u673a\u4f1a\u51fa\u624b\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u5c0f\u65b9\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u8d75\u7fa4\u8fd9\u79cd\u505a\u6cd5\u7684\u786e\u5f88\u806a\u660e\u3002 \u3000\u3000\u8d75\u7fa4\u53c8\u89e3\u91ca\uff1a\u201c\u4ece\u4e00\u5f00\u59cb\u6211\u5c31\u4e0d\u60f3\u4f60\u4eec\u53d7\u5bb3\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u4f1a\u66ff\u4f60\u6740\u4e86\u94b1\u901a\u548c\u94b1\u660e\u3002\u201d \u3000\u3000\u201c\u94b1\u901a\uff1f\u94b1\u660e\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u95ee\uff1a\u201c\u4ed6\u4eec\u5c31\u662f\u4eca\u5929\u4e0b\u5348\u8ddf\u6211\u4eec\u540c\u8f66\u7684\u90a3\u5bf9\u7236\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8d75\u7fa4\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u4e09\u5b9d\u5802\u5c5e\u4e0b\u7684\u4eba\uff0c\u7236\u5b50\u4e24\u4eba\u90fd\u7cbe\u901a\u4e8e\u6697\u5668\uff0c\u800c\u4e14\u662f\u6bd2\u836f\u6697\u5668\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u4e5f\u7528\u540c\u6837\u7684\u65b9\u6cd5\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u540c\u6837\u65b9\u6cd5\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u95ee\uff0c\u201c\u4e0b\u6bd2\uff1f\u201d \u3000\u3000\u201c\u4ee5\u7259\u8fd8\u7259\uff0c\u4ee5\u6bd2\u653b\u6bd2\u3002\u201d \u3000\u3000\u8d75\u7fa4\u8bf4\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u662f\u8fd9\u79cd\u4eba\uff0c\u6240\u4ee5\u82cf\u82cf\u624d\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u82cf\u82cf\u201d\u5f53\u7136\u5c31\u662f\u8d75\u80e1\u6c0f\uff0c\u5c0f\u65b9\u4ece\u672a\u60f3\u5230\u4e0b\u6bd2\u7684\u7adf\u662f\u5979\u3002 \u3000\u3000\u80fd\u591f\u8ba9\u4e24\u4e2a\u7cbe\u4e8e\u6bd2\u836f\u6697\u5668\u7684\u8001\u6c5f\u6e56\uff0c\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4e2d\u6bd2\u800c\u6b7b\uff0c\u90a3\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u4ec0\u4e48\u65f6\u5019\u4e0b\u7684\u6bd2\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u5728\u4e2d\u5348\u6211\u4eec\u8ddf\u4ed6\u4eec\u6362\u8f66\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u5206\u4e86\u4e00\u70b9\u8def\u83dc\u7ed9\u4ed6\u4eec\uff0c\u770b\u7740\u4ed6\u4eec\u5403\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\uff1a\u201c\u6211\u4eec\u6240\u51c6\u5907\u7684\u8def\u83dc\u6709\u5f88\u591a\u79cd\u3002\u201d \u3000\u3000\u6bd2\u5c31\u5728\u8def\u83dc\u91cc\uff0c\u94b1\u901a\u7236\u5b50\u5728\u4e2d\u5348\u65f6\u5c31\u5df2\u5403\u4e86\u6709\u6bd2\u7684\u8def\u83dc\uff0c\u76f4\u5230\u9ec4\u660f\u524d\u6bd2\u6027\u624d\u53d1\u4f5c\u3002 \u3000\u3000\u201c\u5979\u65e9\u5df2\u7b97\u597d\u4e86\u4ed6\u4eec\u4e00\u5b9a\u8981\u7b49\u5230\u5165\u5c71\u4e4b\u540e\u624d\u51fa\u624b\uff0c\u6240\u4ee5\u4e5f\u65e9\u5c31\u7b97\u597d\u6bd2\u6027\u53d1\u4f5c\u7684\u65f6\u523b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u53f9\u606f\u9053\uff1a\u201c\u5979\u7b97\u5f97\u771f\u51c6\u3002\u201d \u3000\u3000\u201c\u5728\u8fd9\u65b9\u9762\uff0c\u5979\u7684\u786e\u53ef\u4ee5\u7b97\u662f\u9ad8\u624b\u3002\u201d \u3000\u3000\u8d75\u7fa4\u7684\u58f0\u97f3\u91cc\u5145\u6ee1\u9a84\u50b2\uff0c\u201c\u5176\u5b9e\u65e0\u8bba\u5728\u54ea\u4e00\u65b9\u9762\u5979\u90fd\u53ef\u4ee5\u7b97\u662f\u9ad8\u624b\u3002\u201d \u3000\u3000\u4ed6\u5728\u4e3a\u4ed6\u7684\u5973\u4eba\u9a84\u50b2\uff0c\u5979\u4e5f\u7684\u786e\u662f\u4e2a\u503c\u5f97\u522b\u4eba\u4e3a\u5979\u9a84\u50b2\u7684\u5973\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4e00\u4e2a\u7537\u4eba\u6709\u4e86\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u4eba\uff0c\u662f\u4e0d\u662f\u771f\u7684\u5e78\u798f\uff1f \u3000\u3000\u5c0f\u65b9\u5e0c\u671b\u4ed6\u4eec\u80fd\u5f97\u5230\u5e78\u798f\u3002 \u3000\u3000\u8fd9\u4e16\u754c\u4e0a\u60b2\u60e8\u7684\u4e8b\u5df2\u591f\u591a\uff0c\u4f55\u51b5\u4ed6\u4eec\u90fd\u662f\u5f88\u5584\u826f\u7684\u4eba\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4ecd\u4e0d\u613f\u522b\u4eba\u53d7\u5230\u4f24\u5bb3\u3002 \u3000\u3000\u5c0f\u65b9\u5f88\u60f3\u95ee\u4ed6\u4eec\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff1f \u3000\u3000\u4ed6\u6ca1\u6709\u95f4\u3002 \u3000\u3000\u4ed6\u7684\u201c\u9b54\u773c\u201d\u5c31\u60ac\u6302\u5728\u8d75\u7fa4\u8170\u7554\uff0c\u4ed6\u4e5f\u6ca1\u6709\u95ee\u8d75\u7fa4\u662f\u4ece\u54ea\u91cc\u5f97\u6765\u7684\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709\u53bb\u770b\u4e00\u773c\u3002 \u3000\u3000\u591a\u5e74\u524d\u4ed6\u5f97\u5230\u8fd9\u67c4\u5251\u65f6\uff0c\u4ed6\u4e5f\u50cf\u5176\u4ed6\u5b66\u5251\u7684\u5c11\u5e74\u4e00\u6837\uff0c\u5c06\u8fd9\u67c4\u5251\u770b\u5f97\u6bd4\u521d\u604b\u7684\u60c5\u4eba\u66f4\u73cd\u8d35\uff0c\u751a\u81f3\u8fd8\u60f3\u5728\u5251\u67c4\u4e0a\u523b\u5b57\u4e3a\u94ed\uff1a \u3000\u3000\u201c\u5251\u5728\u4eba\u5728\uff0c\u5251\u4ea1\u4eba\u4ea1\u3002\u201d \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u7684\u5fc3\u60c5\u5df2\u53d8\u4e86\uff0c\u5df2\u6e10\u6e10\u53d1\u73b0\uff0c\u751f\u547d\u4e2d\u8fd8\u6709\u8bb8\u8bb8\u591a\u591a\u66f4\u91cd\u8981\u7684\u4e8b\uff0c\u8fdc\u6bd4\u4e00\u67c4\u5251\u66f4\u503c\u5f97\u73cd\u60dc\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u518d\u662f\u201c\u4e3a\u8d4b\u65b0\u8bcd\u5f3a\u8bf4\u6101\u201d\u7684\u5c11\u5e74\uff0c\u4e5f\u5df2\u4e0d\u518d\u6709\u201c\u76f8\u9022\u5148\u95ee\u6709\u4ec7\u65e0\u201d\u7684\u8c6a\u60c5\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u80fd\u627e\u5230\u535c\u9e70\uff0c\u53ea\u5e0c\u671b\u80fd\u505a\u4e00\u4e2a\u6069\u4ec7\u4e86\u4e86\uff0c\u95ee\u5fc3\u65e0\u6127\u7684\u5e73\u51e1\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u9b13\u8fb9\u867d\u7136\u8fd8\u6ca1\u6709\u767d\u53d1\uff0c\u53ef\u662f\u5fc3\u5883\u5df2\u5fae\u8feb\u4e2d\u5e74\u4e86\u3002 \u3000\u3000\u8d75\u7fa4\u7684\u773c\u4e2d\u5df2\u6709\u9152\u610f\uff0c\u5374\u8fd8\u662f\u4e00\u76f4\u90fd\u5728\u773c\u5149\u7684\u7684\u5730\u76ef\u7740\u5c0f\u65b9\uff1a\u201c\u6211\u77e5\u9053\u4f60\u672c\u6765\u7684\u540d\u5b57\u4e00\u5b9a\u4e0d\u662f\u82d7\u660c\uff0c\u5c31\u597d\u50cf\u4f60\u4e00\u5b9a\u4e5f\u77e5\u9053\u6211\u672c\u6765\u7edd\u4e0d\u53eb\u8d75\u7fa4\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u4e00\u76f4\u6ca1\u6709\u95ee\u4f60\u662f\u8c01\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u6ca1\u6709\u95ee\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u4eec\u5929\u6daf\u6ca6\u843d\uff0c\u840d\u6c34\u76f8\u9022\uff0c\u5230\u660e\u65e5\u5c31\u8981\u5404\u5206\u4e1c\u897f\uff0c\u5f7c\u6b64\u53c8\u4f55\u5fc5\u77e5\u9053\u5f97\u5927\u591a\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4f60\u5fc3\u91cc\u4e5f\u6709\u5f88\u591a\u4e0d\u613f\u522b\u4eba\u77e5\u9053\u7684\u9690\u75db\u548c\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u62d2\u7edd\u56de\u7b54\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u8d75\u7fa4\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u77e5\u9053\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u6709\u4e9b\u4e8b\u8fd8\u662f\u4e0d\u77e5\u9053\u7684\u597d\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u5df2\u9690\u7ea6\u6709\u4e00\u70b9\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5728\u90a3\u5c71\u9053\u4e0a\u5bf9\u4f60\u7a81\u88ad\u3001\u903c\u7740\u8981\u4f60\u56de\u5bb6\u53bb\u7684\u65f6\u5019\uff0c\u4f60\u5c31\u5e94\u8be5\u77e5\u9053\u60f3\u5230\u4ed6\u4eec\u662f\u627e\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u8d75\u7fa4\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5bf9\u4ed6\u4eec\u8bf4\uff1f\u201d \u3000\u3000\u4ed6\u66ff\u5c0f\u65b9\u56de\u7b54\u4e86\u8fd9\u95ee\u9898\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u53ea\u56e0\u4e3a\u4f60\u4e5f\u662f\u4ed6\u4eec\u8981\u627e\u7684\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u676f\u4e2d\u4ecd\u6709\u9152\uff0c\u8d75\u7fa4\u559d\u5e72\u4e86\u676f\u4e2d\u9152\uff0c\u6162\u6162\u5730\u653e\u4e0b\u9152\u676f\uff0c\u5ffd\u7136\u62d4\u5251\u3002 \u3000\u3000\u5251\u5149\u68ee\u5bd2\uff0c\u90a3\u4e00\u53ea\u201c\u9b54\u773c\u201d\u4eff\u4f5b\u4e0d\u505c\u5730\u5728\u7728\u52a8\uff0c\u4eff\u4f5b\u5df2\u8ba4\u51fa\u4e86\u5b83\u7684\u65e7\u4e3b\u4eba\u3002 \u3000\u3000\u8d75\u7fa4\u8f7b\u629a\u5251\u950b\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u7ec3\u5251\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u638c\u4e2d\u5251\uff0c\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u8fd9\u662f\u67c4\u597d\u5251\u3002\u201d \u3000\u3000\u201c\u662f\u597d\u5251\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f46\u662f\u597d\u5251\uff0c\u800c\u4e14\u662f\u540d\u5251\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff0c\u201c\u5b83\u7684\u540d\u5b57\u53eb\u2018\u9b54\u773c\u2019\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u8fd9\u67c4\u5251\u672c\u6765\u4e0d\u662f\u6211\u7684\uff0c\u4e94\u5929\u524d\u8fd8\u4e0d\u662f\u3002\u201d \u3000\u3000\u8d75\u7fa4\u5ffd\u53c8\u62ac\u5934\uff0c\u76ef\u7740\u5c0f\u65b9\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u6211\uff0c\u8fd9\u67c4\u5251\u662f\u600e\u4e48\u5f97\u6765\u7684\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5c31\u95ee\uff1a\u201c\u8fd9\u67c4\u5251\u662f\u600e\u4e48\u5f97\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u4ece\u4e00\u4e2a\u6b7b\u4eba\u8eab\u4e0a\u5f97\u6765\u7684\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u90a3\u4e2a\u6b7b\u4eba\u5c31\u662f\u5251\u7684\u65e7\u4e3b\uff0c\u59d3\u65b9\uff0c\u662f\u5415\u4e09\u7684\u6b7b\u654c\uff0c\u6211\u4e5f\u662f\u5415\u4e09\u6d3e\u53bb\u56f4\u6355\u4ed6\u7684\u90a3\u4e9b\u4eba\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5df2\u8ddf\u82cf\u82cf\u5546\u8bae\u597d\uff0c\u4e58\u90a3\u6b21\u884c\u52a8\u7684\u673a\u4f1a\uff0c\u8131\u79bb\u5415\u4e09\uff0c\u6240\u4ee5\u6211\u5c31\u5e26\u8d70\u4e86\u8fd9\u67c4\u5251\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9759\u9759\u5730\u542c\u7740\uff0c\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\uff0c\u8fd9\u4ef6\u4e8b\u597d\u50cf\u8ddf\u4ed6\u5168\u65e0\u5173\u7cfb\u3002 \u3000\u3000\u8d75\u7fa4\u5374\u8fd8\u662f\u76ef\u7740\u4ed6\uff0c\u4e00\u53cc\u672c\u6765\u5df2\u6709\u8840\u4e1d\u7684\u9189\u773c\u4eff\u4f5b\u5ffd\u7136\u53d8\u5f97\u5f88\u6e05\u9192\uff0c\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u8981\u6211\u628a\u8fd9\u67c4\u5251\u8fd8\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u201c\u8fd8\u7ed9\u6211\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53cd\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u8fd8\u7ed9\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u8fd9\u67c4\u5251\u7684\u65e7\u4e3b\u4eba\u5c0f\u65b9\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u8dcc\u6b7b\u5728\u5371\u5d16\u4e0b\u7684\u90a3\u4e2a\u4eba\u5e76\u4e0d\u662f\u5c0f\u65b9\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u90a3\u4e2a\u4eba\u7684\u624b\u4e0a\u5e76\u6ca1\u6709\u7ec3\u8fc7\u5251\u7684\u75d5\u8ff9\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u4e0d\u4f46\u6211\u770b\u51fa\u4e00\u8fd9\u4e86\u70b9\uff0c\u522b\u4eba\u4e5f\u770b\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u8d75\u7fa4\u5ffd\u7136\u6325\u5251\uff0c\u7528\u5251\u950b\u903c\u4f4f\u5c0f\u65b9\u7684\u54bd\u5589\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5c0f\u65b9\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u5c31\u662f\u5c0f\u65b9\uff01\u201dJ \u3000\u3000\u5251\u950b\u5c31\u5728\u5589\u7ed3\u524d\u4e00\u5bf8\uff0c\u5251\u6c14\u523a\u4eba\u6bdb\u5b54\u5982\u5c16\u9488\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u8fd8\u662f\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u7684\u808c\u80a4\u5df2\u88ab\u201c\u5149\u9634\u201d\u4fb5\u8680\uff0c\u672c\u6765\u5c31\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u8868\u60c5\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\u3002 \u3000\u3000\u8d75\u7fa4\u5ffd\u7136\u5927\u7b11\uff1a\u201c\u679c\u7136\u662f\u597d\u6c49\uff01\u201d \u3000\u3000\u4ed6\u7684\u624b\u8155\u4e00\u7ffb\uff0c\u5251\u950b\u56de\u8f6c\uff0c\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5df2\u5165\u9798\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u4ece\u8170\u7554\u6458\u4e0b\u4e86\u8fd9\u67c4\u5229\u5251\u7684\u9798\uff0c\u7528\u53cc\u624b\u9001\u5230\u5c0f\u65b9\u9762\u524d\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u5c0f\u65b9\u4e5f\u597d\uff0c\u4e0d\u662f\u5c0f\u65b9\u4e5f\u597d\uff0c\u6211\u90fd\u628a\u8fd9\u67c4\u5251\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u7ec8\u4e8e\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u662f\u6761\u597d\u6c49\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u53ea\u6709\u4f60\u8fd9\u6837\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u624d\u914d\u7528\u8fd9\u628a\u5251\u3002\u201d \u3000\u3000\u4ed6\u7684\u6001\u5ea6\u771f\u8bda\u5766\u7387\uff0c\u4ed6\u662f\u771f\u5fc3\u8981\u628a\u8fd9\u67c4\u5251\u9001\u7ed9\u5c0f\u65b9\uff0c\u5c0f\u65b9\u5374\u6ca1\u6709\u4f38\u624b\u53bb\u63a5\u3002 \u3000\u3000\u867d\u7136\u4ed6\u5df2\u7ecf\u88ab\u8fd9\u4e2a\u4eba\u7684\u4e49\u6c14\u6240\u611f\u52a8\uff0c\u5374\u8fd8\u662f\u4e0d\u80af\u4f38\u624b\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u6211\u662f\u5c0f\u65b9\u4e5f\u597d\uff0c\u4e0d\u662f\u5c0f\u65b9\u4e5f\u597d\uff0c\u90fd\u4e0d\u80fd\u8981\u4f60\u8fd9\u67c4\u5251\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u7406\u7531\u5f88\u7edd\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u6211\u82e5\u662f\u5c0f\u65b9\uff0c\u6211\u4e00\u5b9a\u4f1a\u628a\u8fd9\u67c4\u5251\u9001\u7ed9\u4f60\u7684\uff0c\u5c31\u7b97\u4f60\u8fd8\u7ed9\u4e86\u6211\uff0c\u6211\u4e5f\u4e00\u6837\u4f1a\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u4eec\u53c8\u4f55\u5fc5\u9001\u6765\u9001\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f60\u82e5\u4e0d\u662f\u5c0f\u65b9\u5462\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u82e5\u4e0d\u662f\u5c0f\u65b9\uff0c\u6211\u51ed\u4ec0\u4e48\u8981\u4f60\u9001\u6211\u8fd9\u4e48\u6837\u4e00\u67c4\u5229\u5668\uff1f\u201d \u3000\u3000\u8d75\u7fa4\u4e5f\u7b11\u4e86\uff1a\u201c\u4f60\u771f\u662f\u4e2a\u602a\u4eba\uff0c\u602a\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u638c\u4e2d\u5251\uff0c\u4e3e\u8d77\u676f\u4e2d\u9152\uff1a\u201c\u6211\u656c\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8fd8\u6ca1\u6709\u4e3e\u676f\uff0c\u8138\u8272\u7a81\u7136\u53d8\u4e86\u3002 \u3000\u3000\u521a\u624d\u5251\u950b\u5df2\u5728\u4ed6\u54bd\u5589\uff0c\u4ed6\u8fde\u773c\u90fd\u6ca1\u6709\u7728\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u8fde\u90a3\u5f20\u5df2\u88ab\u201c\u5149\u9634\u201d\u4fb5\u8680\u7684\u8138\u90fd\u5df2\u626d\u66f2\u53d8\u5f62\uff0c\u5c31\u597d\u50cf\u6709\u4e00\u67c4\u867d\u7136\u770b\u4e0d\u89c1\uff0c\u5374\u6bd4\u201c\u9b54\u773c\u201d\u66f4\u950b\u5229\u7684\u5229\u5251\uff0c\u5df2\u523a\u5165\u4e86\u4ed6\u7684\u54bd\u5589\uff0c\u523a\u5165\u4ed6\u7684\u5fc3\u810f\u91cc\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u542c\u89c1\u4e86\u4e00\u9635\u6b4c\u58f0\uff0c\u4e00\u9635\u4ed6\u5df2\u4e0d\u77e5\u542c\u8fc7\u591a\u5c11\u904d\u7684\u6b4c\u58f0\uff1a \u3000\u3000\u2014\u2014\u513f\u987b\u6210\u540d\uff0c \u3000\u3000\u9152\u987b\u9189\u3002 \u3000\u3000\u9152\u540e\u503e\u8bc9\uff0c \u3000\u3000\u662f\u5fc3\u8a00\u3002 \u3000\u3000\u6b4c\u58f0\u4e2d\u5145\u6ee1\u4e86\u65e0\u53ef\u5948\u4f55\u7684\u7537\u5b50\u6c49\u7684\u60b2\u6006\uff0c\u5374\u53c8\u5145\u6ee1\u4e86\u4ee4\u4eba\u8840\u8109\u55b7\u5f20\u7684\u8c6a\u6c14\uff0c\u5728\u8fd9\u8fdc\u79bb\u7ea2\u5c18\u7684\u5c71\u6751\u91cc\uff0c\u5728\u8fd9\u9152\u5df2\u5fae\u9189\u7684\u5bd2\u591c\u4e2d\uff0c\u542c\u6765\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u629b\u4e0b\u9152\u676f\u8dc3\u8d77\uff0c\u7bad\u4e00\u822c\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e0d\u7ba1\u662f\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u4ec0\u4e48\u5730\u65b9\uff0c\u4e0d\u7ba1\u4ed6\u5728\u5e72\u4ec0\u4e48\uff0c\u53ea\u8981\u4ed6\u542c\u89c1\u8fd9\u6b4c\u58f0\uff0c\u4ed6\u90fd\u4f1a\u629b\u5f00\u4e00\u5207\u51b2\u51fa\u53bb\u7684\u3002 \u3000\u3000\u8352\u5bd2\u7684\u5c71\u8c37\uff0c\u5bc2\u5bde\u7684\u5c71\u6751\uff0c\u7528\u77f3\u5757\u780c\u6210\u7684\uff0c\u5f62\u72b6\u53e4\u6734\u7684\u5c4b\u5b50\u53ea\u6709\u4e8c\u4e09\u5341\u6237\uff0c\u706f\u706b\u90fd\u5df2\u7184\u706d\uff0c\u8fdc\u5904\u7684\u5c71\u5761\u4e0a\uff0c\u5374\u4eff\u4f5b\u6709\u706b\u5149\u5728\u95ea\u52a8\u3002 \u3000\u3000\u6b4c\u58f0\u5c31\u662f\u4ece\u90a3\u8fb9\u5c71\u5761\u4e0a\u4f20\u6765\u7684\u3002 \u3000\u3000\u5c71\u5761\u4e0a\u6709\u4e00\u5757\u5de8\u5927\u7684\u5ca9\u77f3\uff0c\u5ca9\u77f3\u4e0a\u751f\u7740\u4e00\u5806\u706b\uff0c\u5e72\u71e5\u7684\u677e\u6728\u5728\u706b\u7130\u4e2d\u5288\u556a\u53d1\u54cd\uff0c\u914d\u5408\u7740\u60b2\u4f26\u7684\u6b4c\u58f0\uff0c\u5c31\u597d\u50cf\u662f\u4e00\u4e2a\u4eba\u5fc3\u788e\u65f6\u7684\u58f0\u7acb\u66f0\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u72ec\u5750\u5728\u706b\u5806\u65c1\uff0c\u624b\u91cc\u7684\u7f8a\u76ae\u888b\u9152\u5df2\u5c06\u7a7a\uff0c\u6b4c\u58f0\u4e5f\u6e10\u6e10\u6d88\u6c89\u3002 \u3000\u3000\u770b\u89c1\u8fd9\u5806\u706b\uff0c\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u5c0f\u65b9\u7684\u5fc3\u4e5f\u53d8\u5f97\u5c31\u50cf\u662f\u706b\u7130\u4e2d\u7684\u677e\u6728\u3002 \u3000\u3000\u4eba\u72b9\u672a\u9189\uff0c\u9152\u5df2\u5c06\u5c3d\uff0c\u6f2b\u6f2b\u957f\u591c\uff0c\u5982\u4f55\u5ea6\u8fc7\uff1f \u3000\u3000\u5c0f\u65b9\u5df2\u6709\u591a\u5e74\u672a\u66fe\u6d41\u6cea\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u773c\u4e2d\u7684\u70ed\u6cea\u5374\u5df2\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u4e5f\u8ffd\u4e0a\u6765\uff0c\u7d27\u63e1\u4f4f\u4ed6\u7684\u624b\u3002 \u3000\u3000\u201c\u662f\u4ed6\uff1f\u201d\u5979\u7684\u58f0\u97f3\u5728\u98a4\u6296\uff0c\u201c\u771f\u7684\u662f\u4ed6\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u4e09\u7ae0\u3000\u627e\u7684\u4e0d\u662f\u4f60 \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u627e\u7684\u4e0d\u662f\u4f60 \u3000\u3000\u6b4c\u58f0\u5ffd\u7136\u505c\u987f\u3002 \u3000\u3000\u706b\u5806\u65c1\u7684\u6b4c\u8005\u5ffd\u7136\u7528\u4e0e\u6b4c\u58f0\u540c\u6837\u60b2\u67aa\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u4e0d\u662f\u4ed6\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u6b4c\u8005\u5df2\u56de\u8fc7\u5934\uff0c\u95ea\u52a8\u7684\u706b\u5149\u7167\u4eae\u4e86\u4ed6\u7684\u8138\uff0c\u5c16\u524a\u7684\u8138\uff0c\u5c16\u524a\u7684\u773c\uff0c\u8138\u4e0a\u5e03\u6ee1\u5c81\u6708\u98ce\u971c\u548c\u75db\u82e6\u7ecf\u9a8c\u7559\u4e0b\u7684\u75d5\u8ff9\uff0c\u773c\u4e2d\u4e5f\u5145\u6ee1\u75db\u82e6\uff1a \u3000\u3000\u201c\u4f60\u4eec\u8981\u627e\u7684\u662f\u4ed6\uff0c\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u540c\u6837\u60b2\u67aa\u7684\u6b4c\u58f0\uff0c\u5374\u4e0d\u662f\u540c\u6837\u7684\u4eba\uff0c\u4e0d\u662f\u535c\u9e70\uff0c\u4e0d\u662f\u3002 \u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u4eec\u8981\u627e\u7684\u662f\u4ed6\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u5927\u58f0\u95ee\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u6b4c\u8005\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u559d\u5e72\u4e86\u7f8a\u76ae\u888b\u7684\u9152\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u5f53\u7136\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u662f\u4ed6\u8981\u6211\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u773c\u4e2d\u53c8\u6709\u4e86\u5149\uff0c\u5fc3\u91cc\u53c8\u6709\u4e86\u5e0c\u671b\uff1a\u201c\u4ed6\u8981\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6b4c\u8005\u6ca1\u6709\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u5374\u4ece\u8d34\u8eab\u7684\u8863\u888b\u91cc\u53d6\u51fa\u4e2a\u5c0f\u5c0f\u7684\u9526\u56ca\u3002 \u3000\u3000\u9526\u56ca\u4e0a\u7ee3\u7684\u662f\u4e00\u53ea\u9e70\uff0c\u7528\u91d1\u8272\u7684\u4e1d\u7ee3\u5728\u84dd\u8272\u7684\u7f0e\u5b50\u4e0a\u3002 \u3000\u3000\u9526\u56ca\u91cc\u88c5\u7684\u662f\u4e00\u7c92\u660e\u73e0\u3002 \u3000\u3000\u6b4c\u8005\u53cd\u95f4\u201c\u9633\u5149\u201d\uff1a\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u5f53\u7136\u8bb0\u5f97\u3002 \u3000\u3000\u7eb5\u7136\u6ca7\u6d77\u5df2\u67af\u3001\u5927\u5730\u5df2\u6c89\u3001\u65e5\u6708\u65e0\u5149\uff0c\u5979\u4e5f\u7edd\u4e0d\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u8fd9\u9526\u56ca\u5c31\u662f\u5979\u4eb2\u624b\u7f1d\u6210\u7684\uff0c\u5c31\u662f\u5979\u548c\u535c\u9e70\u8ba2\u4eb2\u65f6\u7684\u6587\u5b9a\u4e4b\u793c\uff0c\u73b0\u5728\u600e\u4e48\u4f1a\u5230\u4e86\u522b\u4eba\u624b\u91cc\uff1f\u201d \u3000\u3000\u6b4c\u8005\u544a\u8bc9\u201c\u9633\u5149\u201d \u3000\u3000\u201c\u8fd9\u662f\u4ed6\u4ea4\u7ed9\u6211\u7684\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u4eb2\u624b\u4ea4\u7ed9\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u4ea4\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u8981\u6211\u66ff\u4ed6\u628a\u8fd9\u6837\u4e1c\u897f\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u6b4c\u8005\u7684\u58f0\u97f3\u4e2d\u4e5f\u5e26\u7740\u75db\u82e6\uff0c\u201c\u4ed6\u8bf4\u4ed6\u672c\u6765\u5e94\u8be5\u4eb2\u624b\u8fd8\u7ed9\u4f60\u7684\uff0c\u4f46\u662f\u4ed6\u5df2\u4e0d\u613f\u518d\u89c1\u4f60\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6162\u6162\u5730\u4f38\u51fa\u624b\uff0c\u63a5\u8fc7\u9526\u56ca\u548c\u660e\u73e0\u3002 \u3000\u3000\u5979\u7684\u624b\u5728\u6296\uff0c\u6296\u7684\u53ef\u6015\uff0c\u6296\u5f97\u8fde\u5c0f\u5c0f\u4e00\u4e2a\u9526\u56ca\u90fd\u62ff\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u9526\u56ca\u6389\u4e0b\u53bb\uff0c\u660e\u73e0\u4e5f\u6389\u4e86\u4e0b\u53bb\uff0c\u6389\u5165\u706b\u5806\u91cc\u3002 \u3000\u3000\u706b\u5806\u91cc\u7acb\u523b\u95ea\u8d77\u4e86\u4e00\u9635\u6de1\u84dd\u8272\u7684\u706b\u7130\uff0c\u9526\u56ca\u548c\u660e\u73e0\u90fd\u5df2\u5316\u4f5c\u4e86\u65e0\u60c5\u7684\u706b\u7130\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u6276\u8d77\u4e86\u5979\uff0c\u5389\u58f0\u95ee\u6b4c\u8005\uff1a\u201c\u4ed6\u8bf4\u4ed6\u4e0d\u613f\u89c1\u5979\uff0c\u771f\u662f\u4ed6\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8fd8\u8bf4\u4e86\u53e6\u5916\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u4ed6\u8bf4\u4ed6\u4e5f\u4e0d\u613f\u518d\u89c1\u4f60\uff0c\u201d \u3000\u3000\u6b4c\u8005\u51b7\u51b7\u5730\u56de\u7b54\uff0c\u201c\u4f60\u5df2\u7ecf\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u4ed6\u548c\u4f60\u4eec\u4e4b\u95f4\u5df2\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5636\u58f0\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u81ea\u5df1\u5e94\u8be5\u77e5\u9053\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6b4c\u8005\u51b7\u7b11\u53cd\u95ee\uff1a\u201c\u4f60\u81ea\u5df1\u613f\u4e0d\u613f\u610f\u8ddf\u4e00\u4e2a\u5929\u5929\u62b1\u4f4f\u4f60\u59bb\u5b50\u7761\u89c9\u7684\u4eba\u4ea4\u670b\u53cb\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5c31\u50cf\u662f\u4e00\u6839\u9488\u3001\u4e00\u628a\u5200\u3001\u4e00\u6761\u97ad\u5b50\uff0c\u5c31\u50cf\u662f\u4e00\u67c4\u5bc6\u5e03\u72fc\u7259\u7684\u94a2\u952f\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u8df3\u8d77\u6765\uff1a \u3000\u3000\u201c\u6211\u4e0d\u4fe1\uff0c\u6211\u6b7b\u4e5f\u4e0d\u4fe1\u4ed6\u4f1a\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u3002\u201d \u3000\u3000\u5979\u8df3\u8fc7\u53bb\uff0c\u7528\u529b\u63ea\u4f4f\u6b4c\u8005\u7684\u8863\u895f\uff1a\u201c\u4e00\u5b9a\u662f\u4f60\u6740\u4e86\u4ed6\uff0c\u518d\u7528\u8fd9\u79cd\u8bdd\u6765\u6b3a\u9a97\u6211\u3002\u201d \u3000\u3000\u6b4c\u8005\u51b7\u51b7\u5730\u770b\u7740\u5979\uff1a \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a97\u4f60\uff1f\u5982\u679c\u4e0d\u662f\u4ed6\u544a\u8bc9\u6211\u7684\uff0c\u4f60\u4eec\u7684\u4e8b\u6211\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u867d\u7136\u5e76\u4e0d\u80fd\u8fa9\uff0c\u5374\u8fd8\u662f\u4e0d\u80af\u653e\u8fc7\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u4e00\u5b9a\u8981\u542c\u4ed6\u81ea\u5df1\u4eb2\u53e3\u544a\u8bc9\u6211\uff0c\u6211\u624d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4e5f\u5df2\u5636\u54d1\uff1a\u201c\u4f60\u4e00\u5b9a\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff0c\u4e00\u5b9a\u8981\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u544a\u8bc9\u4f60\u3002\u201d\u6b4c\u8005\u8bf4\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u8fd9\u4e48\u75db\u5feb\u5c31\u7b54\u5e94\u4e86\uff0c\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\u53cd\u800c\u5f88\u60ca\u5947\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u867d\u7136\u4e0d\u80fd\u544a\u8bc9\u4f60\u4ed6\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f46\u6211\u5374\u53ef\u4ee5\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u6b4c\u8005\u7684\u76ee\u5149\u9065\u671b\u8fdc\u65b9\uff0c\u773c\u91cc\u5e26\u7740\u79cd\u6ca1\u6709\u4eba\u80fd\u4e86\u89e3\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u5341\u4e09\u5e74\u524d\uff0c\u6211\u5c31\u5df2\u7ecf\u5e94\u8be5\u6b7b\u4e86\uff0c\u6b7b\u5f97\u5f88\u60e8\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u53ea\u56e0\u4e3a\u535c\u9e70\u6551\u4e86\u6211\uff0c\u4e0d\u4f46\u6551\u4e86\u6211\u7684\u547d\uff0c\u4e5f\u6551\u4e86\u6211\u7684\u540d\u58f0\u3002\u201d \u3000\u3000\u5728\u67d0\u4e9b\u4eba\u773c\u4e2d\u770b\u6765\uff0c\u540d\u58f0\u6709\u65f6\u8fdc\u6bd4\u751f\u547d\u66f4\u53ef\u8d35\u3001\u66f4\u91cd\u8981\u3002 \u3000\u3000\u8fd9\u4e2a\u795e\u79d8\u7684\u6b4c\u8005\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u8fd9\u6761\u547d\u5df2\u7ecf\u662f\u4ed6\u7684\u3002\u201d \u3000\u3000\u6b4c\u8005\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u968f\u65f6\u90fd\u53ef\u4ee5\u4e3a\u4ed6\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u73b0\u5728\u7edd\u5bf9\u4e0d\u662f\u5e94\u8be5\u7b11\u7684\u65f6\u5019\uff0c\u4ed6\u5374\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4eec\u4e00\u5b9a\u4f1a\u903c\u6211\u8bf4\u51fa\u4ed6\u7684\u4e0b\u843d\uff0c\u9664\u4e86\u4f60\u4eec\u4e4b\u5916\uff0c\u4e00\u5b9a\u8fd8\u6709\u5f88\u591a\u4eba\u4f1a\u903c\u6211\uff0c\u5e78\u597d\u6211\u4e5f\u5df2\u7ecf\u6709\u6cd5\u5b50\u8ba9\u4f60\u4eec\u903c\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u5927\u558a\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u6211\u7edd\u4e0d\u903c\u4f60\uff01\u201d \u3000\u3000\u6b4c\u8005\u53c8\u5bf9\u5c0f\u65b9\u7b11\u4e86\u7b11\uff0c\u8fd9\u4e2a\u7b11\u5bb9\u5c31\u4e00\u76f4\u7559\u5728\u4ed6\u8138\u4e0a\u4e86\uff0c\u6c38\u8fdc\u90fd\u7559\u5728\u4ed6\u8138\u4e0a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u8138\u5df2\u7a81\u7136\u50f5\u786c\uff0c\u8138\u4e0a\u6bcf\u4e00\u5757\u808c\u8089\u90fd\u5df2\u50f5\u786c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u8896\u4e2d\u85cf\u7740\u4e00\u628a\u5200\uff0c\u4e00\u628a\u53c8\u8584\u53c8\u5229\u7684\u77ed\u5200\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5f00\u59cb\u7b11\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u628a\u8fd9\u67c4\u5200\u523a\u5165\u4e86\u4ed6\u81ea\u5df1\u7684\u5fc3\u810f\uff01 \u3000\u3000\u5929\u8272\u5df2\u6e10\u6e10\u4eae\u4e86\uff0c\u5bd2\u5c71\u5728\u6de1\u6de1\u7684\u66d9\u8272\u4e2d\u770b\u6765\u3001\u5c31\u50cf\u662f\u4e00\u5e45\u6de1\u6de1\u7684\u6c34\u58a8\u753b\u3002 \u3000\u3000\u5c0f\u65b9\u7ad9\u5728\u5c71\u5761\u4e0a\uff0c\u9065\u671b\u7740\u66d9\u8272\u4e2d\u7684\u5bd2\u5c71\uff0c\u8138\u8272\u4e5f\u50cf\u662f\u5c71\u8272\u4e00\u6837\u3002 \u3000\u3000\u662f\u8d75\u7fa4\u7ea6\u4ed6\u5230\u8fd9\u91cc\u6765\u7684\u3002 \u3000\u3000\u6b4c\u8005\u7684\u5c38\u4f53\u5df2\u57cb\u846c\uff0c\u201c\u9633\u5149\u201d\u7684\u521b\u53e3\u5d29\u88c2\uff0c\u82cf\u82cf\u5c31\u7559\u5728\u5c4b\u91cc\u966a\u5979\u3002 \u3000\u3000\u4e0d\u77e5\u540d\u7684\u6b4c\u8005\uff0c\u6ca1\u6709\u7891\u7684\u575f\u5893\uff0c\u5374\u5df2\u8db3\u591f\u4ee4\u4eba\u6c38\u96be\u5fd8\u6000\u3002 \u3000\u3000\u8d75\u7fa4\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u5f00\u53e3\uff1a\u201c\u6211\u77e5\u9053\u535c\u9e70\u8fd9\u4e2a\u4eba\uff0c\u6211\u89c1\u8fc7\u4ed6\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u8981\u4e00\u4e2a\u4eba\u5fc3\u7518\u60c5\u613f\u5730\u4e3a\u53e6\u4e00\u4e2a\u4eba\u53bb\u6b7b\uff0c\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u8d75\u7fa4\u53f9\u606f\uff1a\u201c\u535c\u9e70\u7684\u786e\u4e0d\u6127\u4e3a\u4eba\u6770\u3002\u201d \u3000\u3000\u4ed6\u4fa7\u8fc7\u8138\uff0c\u51dd\u89c6\u5c0f\u65b9\uff1a\u201c\u4f46\u662f\u4e0d\u7ba1\u591a\u4e48\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u4e5f\u6709\u505a\u9519\u4e8b\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u8fd9\u6b21\u4ed6\u4e00\xb7\u5b9a\u51a4\u6789\u4e86\u4f60\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff0c\u201c\u6211\u770b\u5f97\u51fa\u4f60\u8ddf\u90a3\u4f4d\u59d1\u5a18\u90fd\u7edd\u4e0d\u662f\u4ed6\u8bf4\u7684\u90a3\u79cd\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff1a\u201c\u4ed6\u6ca1\u6709\u9519\uff0c\u9519\u7684\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u6211\uff1f\u201d \u3000\u3000\u8d75\u7fa4\u53cd\u95ee\u9053\uff1a\u201c\u6211\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u9519\u5728\u4f60\u6839\u672c\u4e0d\u4e86\u89e3\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9eef\u7136\u9053\uff0c\u201c\u8fd9\u4e16\u754c\u4e0a\u672c\u6765\u5c31\u5f88\u5c11\u6709\u4eba\u80fd\u4e86\u89e3\u4ed6\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u8c61\u4e00\u70b9\u90fd\u4e0d\u6068\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u6068\u4ed6\uff1f\u6211\u4e3a\u4ec0\u4e48\u8981\u6068\u4ed6\uff1f\u2026 \u3000\u3000\u5c0f\u65b9\u95ee\uff1a\u201c\u96be\u9053\u4f60\u771f\u7684\u4ee5\u4e3a\u4ed6\u662f\u5728\u6000\u7591\u6211\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4e0d\u662f\u53f6 \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4ed6\u8fd9\u4e48\u6837\u505a\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4e0d\u613f\u518d\u8fde\u7d2f\u6211\u4eec\uff0c\u6240\u4ee5\u624d\u6545\u610f\u523a\u4f24\u6211\u4eec\uff0c\u8981\u6211\u4eec\u6c38\u8fdc\u4e0d\u60f3\u518d\u89c1\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u9065\u671b\u8fdc\u65b9\uff0c\u773c\u4e2d\u5145\u6ee1\u5c0a\u656c\u611f\u6fc0\uff1a\u201c\u4ed6\u8fd9\u4e48\u505a\uff0c\u53ea\u4e0d\u8fc7\u8981\u6211\u4eec\u81ea\u7531\u81ea\u5728\u5730\u53bb\u8fc7\u6211\u4eec\u81ea\u5df1\u7684\u65e5\u5b50\u3002\u201d \u3000\u3000\u8d75\u7fa4\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u53f9\u606f\uff1a \u3000\u3000\u201c\u4f60\u786e\u5b9e\u4e86\u89e3\u4ed6\uff0c\u4e00\u4e2a\u4eba\u80fd\u6709\u4f60\u8fd9\u4e48\u4e00\u4e2a\u77e5\u5df2\u670b\u53cb\uff0c\u5df2\u7ecf\u53ef\u4ee5\u6b7b\u800c\u65e0\u61be\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u63e1\u4f4f\u5c0f\u65b9\u7684\u624b\u8bf4\uff1a\u201c\u6709\u4e9b\u4e8b\u6211\u672c\u6765\u4e0d\u60f3\u5bf9\u4f60\u8bf4\u7684\uff0c\u53ef\u662f\u73b0\u5728\u4e5f\u4e0d\u80fd\u4e0d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u662f\u4e2a\u79d8\u5bc6\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u4eba\u77e5\u9053\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u4ed6\u7684\u6001\u5ea6\u8bda\u6073\u800c\u4e25\u8083\uff1a\u201c\u6211\u4fdd\u8bc1\u4f60\u542c\u5230\u4e4b\u540e\u4e00\u5b9a\u4f1a\u5927\u5403\u4e00\u60ca\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u79d8\u5bc6\u65e0\u7591\u662f\u4e2a\u5f88\u60ca\u4eba\u7684\u79d8\u5bc6\u3002\u5982\u679c\u5c0f\u65b9\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\u8ddf\u4ed6\u7684\u5173\u7cfb\u6709\u591a\u4e48\u5bc6\u5207\u3001\u5bf9\u4ed6\u7684\u5f71\u54cd\u6709\u591a\u4e48\u5927\uff0c\u5c31\u7b97\u8981\u4ed6\u7528\u5200\u5b50\u53bb\u903c\u8d75\u7fa4\u8bf4\u51fa\u6765\uff0c\u4ed6\u4e5f\u4f1a\u53bb\u505a\u7684\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53ea\u4e0d\u8fc7\u6de1\u6de1\u5730\u95ee\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u8bf4\uff1f\u6211\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u542c\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u8bf4\uff0c\u6211\u542c\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u542c\u5230\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u5c31\u542c\u89c1\u4e86\u4e00\u58f0\u60ca\u547c\uff0c\u547c\u58f0\u4e2d\u5145\u6ee1\u4e86\u60ca\u6016\u4e0e\u6050\u60e7\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u56e0\u4e3a\u201c\u65a7\u5934\u201d\u8fd9\u79cd\u9152\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5c71\u5c45\u7684\u5973\u4eba\u5927\u591a\u5065\u5eb7\u5f3a\u58ee\u7f8e\u4e3d\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u8f9b\u8fa3\u7684\u98df\u7269\u603b\u662f\u4f7f\u4eba\u6027\u6b32\u65fa\u76db\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u73b0\u5728\u5df2\u5230\u4e86\u51ac\u5b63\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5176\u4ed6\u67d0\u79cd\u5916\u4eba\u65e0\u6cd5\u4e86\u89e3\u7684\u539f\u56e0\u2014\u2014 \u3000\u3000\u8fd9\u5c71\u6751\u4e2d\u7684\u5c45\u6c11\u8d77\u8eab\u5e76\u4e0d\u65e9\u3002 \u3000\u3000\u6240\u4ee5\u73b0\u5728\u5c45\u7136\u5929\u5df2\u4eae\u4e86\uff0c\u8fd9\u5c71\u6751\u5374\u8fd8\u5728\u6c89\u7761\u4e2d\uff0c\u6bcf\u4e00\u680b\u7070\u77f3\u5c4b\u5b50\u91cc\u90fd\u662f\u9759\u6084\u6084\u7684\uff0c\u6240\u4ee5\u8fd9\u4e00\u58f0\u60ca\u547c\u542c\u6765\u66f4\u523a\u8033\u3002 \u3000\u3000\u5c0f\u65b9\u542c\u4e0d\u51fa\u8fd9\u662f\u8c01\u7684\u58f0\u97f3\uff0c\u53ef\u662f\u8d75\u7fa4\u542c\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u4e5f\u5931\u58f0\u60ca\u547c\uff1a\u201c\u82cf\u82cf\uff01\u201d \u3000\u3000\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u4e00\u4e2a\u50cf\u82cf\u82cf\u90a3\u6837\u7684\u5c24\u7269\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u90fd\u968f\u65f6\u53ef\u80fd\u4f1a\u906d\u9047\u5230\u4e0d\u5e78\u548c\u66b4\u529b\u3002 \u3000\u3000\u8d75\u7fa4\u7684\u8eab\u5b50\u8dc3\u8d77\uff0c\u5411\u5c71\u4e0b\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u7d27\u968f\u7740\u4ed6\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u5df2\u7ecf\u662f\u5171\u8fc7\u60a3\u96be\u7684\u670b\u53cb\uff0c\u73b0\u5728\u201c\u9633\u5149\u201d\u6b63\u548c\u82cf\u82cf\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4ee4\u4eba\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u7b49\u5230\u4ed6\u4eec\u8d76\u56de\u90a3\u77f3\u5c4b\u65f6\uff0c\u201c\u9633\u5149\u201d\u5e76\u6ca1\u6709\u8ddf\u82cf\u82cf\u5728\u4e00\u8d77\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u5df2\u7ecf\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u82cf\u82cf\u5728\u54ed\uff0c\u7f29\u5728\u4e00\u4e2a\u89d2\u843d\u91cc\u5931\u58f0\u75db\u54ed\u3002 \u3000\u3000\u5979\u7684\u8863\u88f3\u5df2\u7ecf\u6495\u88c2\uff0c\u5979\u90a3\u4e30\u6ee1\u7684\u80f8\u3001\u7ea4\u7ec6\u7684\u8170\u3001\u4fee\u957f\u7ed3\u5b9e\u7684\u817f\u7f0e\u5b50\u822c\u5149\u6ed1\u67d4\u6da6\u7684\u76ae\u80a4\uff0c\u4ece\u88ab\u6495\u88c2\u7684\u8863\u886b\u4e2d\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8d75\u7fa4\u770b\u89c1\u5979\uff0c\u7b2c\u4e00\u53e5\u8bdd\u95ee\u7684\u662f\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u8c01\u6b3a\u8d1f\u4e86\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7b2c\u4e00\u53e5\u95ee\u7684\u5374\u662f\uff1a\u201c\u2018\u9633\u5149\u2019\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u662f\u540c\u65f6\u95ee\u51fa\u6765\u7684\uff0c\u82cf\u82cf\u90fd\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u5979\u5168\u8eab\u90fd\u5728\u98a4\u6296\uff0c\u6296\u5f97\u5c31\u50cf\u662f\u5bd2\u98ce\u4e2d\u4e00\u7247\u5c06\u843d\u672a\u843d\u7684\u53f6\u5b50\u3002 \u3000\u3000\u76f4\u5230\u8d75\u7fa4\u7528\u4e00\u5e8a\u88ab\u5355\u5305\u4f4f\u5979\uff0c\u5c06\u5269\u4e0b\u7684\u534a\u7897\u2018\u65a7\u5934\u2019\u704c\u5979\u559d\u4e0b\u53bb\u4e4b\u540e\uff0c\u5979\u624d\u80fd\u5f00\u53e3\u3002 \u3000\u3000\u5979\u53ea\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u540c\u6837\u7684\u4e09\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u4e94\u4e2a\u4eba\u3002\u201d\u5979\u8bf4\uff0c\u201c\u4e94\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u660e\u767d\u5979\u7684\u610f\u601d\u2014\u2014 \u3000\u3000\u8fd9\u91cc\u6709\u4e94\u4e2a\u4eba\u6765\u8fc7\uff0c\u5bf9\u5979\u505a\u4e86\u4e00\u4e9b\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u2014\u2014\u662f\u4e94\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f \u3000\u3000\u2014\u2014\u201c\u9633\u5149\u201d\u5462\uff1f \u3000\u3000\u4e0d\u7ba1\u8fd9\u4e94\u4e2a\u4eba\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u90fd\u5df2\u4e0d\u91cd\u8981\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u8d70\u4e86\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff1a\u201c\u2018\u9633\u5149\uff0c\u662f\u4e0d\u662f\u88ab\u4ed6\u4eec\u5e26\u8d70\u7684\uff1f\u201d \u3000\u3000\u82cf\u82cf\u70b9\u5934\uff0c\u6d41\u7740\u6cea\u70b9\u5934\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u662f\u5f80\u54ea\u91cc\u8d70\u7684\uff1f\u201d \u3000\u3000\u82cf\u82cf\u6447\u5934\uff0c\u6d41\u7740\u6cea\u6447\u5934\uff0c\u5979\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u5f80\u54ea\u91cc\u8d70\u7684\u3002 \u3000\u3000\u8d75\u7fa4\u4f4e\u53f1\uff1a\u201c\u8ffd\uff01\u201d \u3000\u3000\u5f53\u7136\u8981\u8ffd\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\u90fd\u8981\u53bb\u8ffd\uff0c\u5c31\u7b97\u8981\u8ffd\u4e0b\u5730\u72f1\u3001\u8ffd\u4e0a\u5200\u5c71\u8feb\u5165\u6cb9\u9505\uff0c\u4e5f\u4e00\u6837\u8981\u53bb\u8ffd\u3002 \u3000\u3000\u53ef\u662f\u5f80\u54ea\u91cc\u53bb\u8ffd\u5462\uff1f \u3000\u3000\u201c\u6211\u4eec\u5206\u5934\u53bb\u8ffd\u3002\u201d \u3000\u3000\u8d75\u7fa4\u9053\uff1a\u201c\u4f60\u5f80\u4e1c\u8ffd\uff0c\u6211\u5f80\u897f\u3002\u201d \u3000\u3000\u4ed6\u4ea4\u7ed9\u5c0f\u65b9\u4e00\u652f\u65d7\u82b1\u706b\u70ae\uff1a\u201c\u8c01\u627e\u5230\u4e86\uff0c\u5c31\u53ef\u4ee5\u6b64\u4e3a\u8baf\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u80fd\u7b97\u662f\u4e00\u4e2a\u597d\u6cd5\u5b50\uff0c\u5374\u662f\u552f\u4e00\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u6ca1\u6709\u75d5\u8ff9\uff0c\u6ca1\u6709\u7ebf\u7d22\uff0c\u6ca1\u6709\u76ee\u51fb\u8005\u3002 \u3000\u3000\u5929\u8272\u53c8\u6e10\u6e10\u6697\u4e86\uff0c\u6697\u6de1\u7684\u5929\u7a7a\u4e2d\uff0c\u6ca1\u6709\u51fa\u73b0\u8fc7\u95ea\u4eae\u7684\u65d7\u82b1\uff0c\u751a\u81f3\u8fde\u8d75\u7fa4\u90fd\u6ca1\u6709\u6d88\u606f\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u627e\u5230\u201c\u9633\u5149\u201d\uff0c\u4e5f\u6ca1\u6709\u627e\u5230\u90a3\u4e94\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u627e\u4e86\u4e00\u5929\uff0c\u6ca1\u6709\u5403\u8fc7\u4e00\u70b9\u4e1c\u897f\uff0c\u6ca1\u6709\u559d\u8fc7\u4e00\u6ef4\u6c34\u3002 \u3000\u3000\u4ed6\u7684\u5634\u5507\u5df2\u5e72\u88c2\uff0c\u978b\u5e95\u5df2\u88ab\u5c16\u77f3\u523a\u7a7f\uff0c\u5c0f\u817f\u809a\u4e0a\u6bcf\u4e00\u5757\u808c\u8089\u90fd\u5728\u523a\u75db\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd8\u5728\u627e\u3002 \u3000\u3000\u5c31\u597d\u50cf\u6708\u5bab\u4e2d\u7684\u5434\u521a\u5728\u780d\u90a3\u68f5\u6c38\u8fdc\u780d\u4e0d\u5012\u7684\u6842\u6811\u4e00\u6837\uff0c\u867d\u7136\u660e\u77e5\u627e\u4e0d\u5230\uff0c\u4e5f\u8981\u627e\u4e0b\u53bb\uff0c\u76f4\u5230\u5012\u4e0b\u53bb\u4e3a\u6b62\u3002 \u3000\u3000\u780d\u4e0d\u5012\u7684\u6811\uff0c\u627e\u4e0d\u5230\u7684\u4eba\uff0c\u4e16\u754c\u4e0a\u672c\u6765\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684 \u3000\u3000\u5c71\u6751\u4e2d\u5df2\u4eae\u8d77\u4e86\u706f\u706b\u3002 \u3000\u3000\u4ece\u5c0f\u65b9\u73b0\u5728\u7ad9\u7740\u7684\u5730\u65b9\u770b\u4e0b\u53bb\uff0c\u5f88\u5bb9\u6613\u5c31\u53ef\u4ee5\u627e\u5230\u4ed6\u4eec\u6628\u591c\u7559\u5bbf\u7684\u90a3\u6a35\u592b\u7684\u77f3\u5c4b\uff0c\u5728\u4ed6\u770b\u5f97\u89c1\u7684\u4e24\u6247\u7a97\u6237\u91cc\uff0c\u73b0\u5728\u4e5f\u5df2\u6709\u706f\u5149\u900f\u51fa\u3002 \u3000\u3000\u2014\u2014\u8d75\u7fa4\u662f\u4e0d\u662f\u5df2\u7ecf\u56de\u53bb\u4e86\uff0c\u6709\u6ca1\u6709\u627e\u5230\u4ec0\u4e48\u7ebf\u7d22\uff1f \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u7528\u6700\u5feb\u7684\u901f\u5ea6\u51b2\u8fc7\u53bb\uff0c\u8ddd\u79bb\u77f3\u5c4b\u8fd8\u6709\u51e1\u5341\u4e08\u65f6\uff0c\u5c31\u542c\u89c1\u4e86\u77f3\u5c4b\u91cc\u4f20\u51fa\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u79cd\u65e0\u8bba\u8c01\u53ea\u8981\u542c\u89c1\u8fc7\u4e00\u6b21\u5c31\u6c38\u96be\u5fd8\u8bb0\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u79cd\u6df7\u5408\u7740\u54ed\u3001\u7b11\u3001\u5598\u606f\u3001\u547b\u541f\u7684\u58f0\u97f3\uff0c\u5145\u6ee1\u4e86\u90aa\u6076\u4e0e\u6fc0\u60c5\u3002 \u3000\u3000\u4e00\u79cd\u5c31\u7b97\u662f\u6700\u51b7\u9759\u7684\u4eba\u542c\u89c1\u4e5f\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u8840\u8109\u55b7\u5f20\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5c0f\u65b9\u51b2\u8fc7\u53bb\uff0c\u4e00\u811a\u8e22\u5f00\u4e86\u95e8\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u7acb\u523b\u6c89\u4e86\u4e0b\u53bb\uff0c\u6012\u706b\u5374\u51b2\u4e0a\u4e86\u5934\u9876\u2014\u2014\u8fd9\u7b80\u6734\u7684\u77f3\u5c4b\u5df2\u7ecf\u53d8\u6210\u4e86\u5730\u72f1\u3002 \u3000\u3000\u82cf\u82cf\u6b63\u5728\u5730\u72f1\u4e2d\u53d7\u7740\u714e\u71ac\u3002 \u3000\u3000\u4e00\u6761\u91ce\u517d\u822c\u7684\u58ee\u6c49\uff0c\u6309\u4f4f\u5979\u7684\u8eab\u5b50\uff0c\u9a91\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u6273\u5f00\u5979\u7684\u5634\uff0c\u5c06\u6ee1\u6ee1\u4e00\u888b\u9152\u5f80\u5979\u5634\u91cc\u704c\u3002 \u3000\u3000\u9c9c\u8840\u822c\u7684\u9152\u6c41\u6d41\u904d\u4e86\u5979\u6d01\u767d\u65e0\u6687\u7684\u55e3\u4f53\u3002 \u3000\u3000\u8fd9\u91ce\u517d\u822c\u7684\u58ee\u6c49\u770b\u89c1\u5c0f\u65b9\u65f6\uff0c\u5c0f\u65b9\u5df2\u80be\u7bad\u822c\u7a9c\u8fc7\u53bb\uff0c\u6325\u638c\u731b\u5207\u4ed6\u7684\u540e\u9888\u3002 \u3000\u3000\u8fd9\u662f\u7edd\u5bf9\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u6124\u6012\u4f7f\u5f97\u5c0f\u65b9\u4f7f\u51fa\u4e86\u5168\u529b\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u58ee\u6c49\u5ffd\u7136\u50cf\u53ea\u7a7a\u9ebb\u888b\u822c\u5012\u4e0b\u53bb\u65f6\uff0c\u4ed6\u7684\u6124\u6012\u72b9\u672a\u5e73\u606f\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u63d0\u8d77\u8fd9\u58ee\u6c49\u7684\u811a\uff0c\u7528\u529b\u629b\u51fa\u53bb\uff0c\u7528\u529b\u5173\u4e0a\u95e8\uff0c\u4ed6\u624d\u60f3\u8d77\u81ea\u5df1\u5e94\u8be5\u7559\u4e0b\u8fd9\u4e2a\u4eba\u4e00\u6761\u547d\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5f88\u53ef\u80fd\u5c31\u662f\u90a3\u4e94\u4e2a\u4eba\u5176\u4e2d\u4e4b\u4e00\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4ed6\u552f\u4e00\u80fd\u627e\u5230\u7684\u7ebf\u7d22\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u8fd9\u6761\u7ebf\u7d22\u5df2\u548c\u8fd9\u4e2a\u4eba\u7684\u9888\u5b50\u4e00\u8d77\u88ab\u6253\u65ad\u4e86\u3002 \u3000\u3000\u9020\u6210\u9519\u8bef\u7684\u539f\u56e0\u6709\u5f88\u591a\u79cd\uff0c\u6124\u6012\u65e0\u7591\u662f\u5176\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u79cd\u3002 \u3000\u3000\u73b0\u5728\u9519\u8bef\u5df2\u9020\u6210\uff0c\u5df2\u7ecf\u6c38\u8fdc\u65e0\u6cd5\u6362\u56de\u4e86\u3002 \u3000\u3000\u7a97\u5b50\u662f\u5f00\u7740\u7684\uff0c\u5c4b\u5b50\u91cc\u5145\u6ee1\u4e86\u9152\u6c14\u3002 \u3000\u3000\u4e0d\u662f\u201c\u65a7\u5934\u201d\u90a3\u79cd\u8f9b\u8fa3\u7684\u6c14\u5473\uff0c\u5374\u6709\u70b9\u50cf\u662f\u80ed\u8102\u7684\u5473\u9053\u3002 \u3000\u3000\u82cf\u82cf\u8fd8\u8eba\u5728\u90a3\u5f20\u94fa\u7740\u517d\u76ae\u7684\u77f3\u5e8a\u4e0a\u3002 \u3000\u3000\u5979\u662f\u8d64\u88f8\u7684\u3002 \u3000\u3000\u5979\u7684\u6574\u4e2a\u4eba\u90fd\u5df2\u5b8c\u5168\u865a\u8131\uff0c\u773c\u767d\u4e0a\u7ffb\uff0c\u5634\u91cc\u6d41\u7740\u767d\u6cab\uff0c\u5168\u8eab\u6bcf\u4e00\u5757\u808c\u8089\u90fd\u5728\u4e0d\u505c\u5730\u62bd\u6410\u98a4\u6296\uff0c\u7f0e\u5b50\u822c\u5149\u6ed1\u67d4\u8f6f\u7684\u76ae\u80a4\u6bcf\u4e00\u5bf8\u90fd\u8d77\u4e86\u6218\u6817\u3002 \u3000\u3000\u5979\u4e0d\u662f\u201c\u9633\u5149\u201d\uff0c\u4e0d\u662f\u5c0f\u65b9\u7684\u5973\u4eba\uff0c\u4e5f\u4e0d\u662f\u5c0f\u65b9\u7684\u670b\u53cb\u3002 \u3000\u3000\u53ef\u662f\u770b\u89c1\u5979\u8fd9\u6837\u5b50\uff0c\u5c0f\u65b9\u7684\u5fc3\u4e5f\u540c\u6837\u5728\u523a\u75db\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5fd8\u4e86\u5979\u662f\u5973\u4eba\uff0c\u5fd8\u4e86\u5979\u662f\u8d64\u88f8\u7684\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5728\u5c0f\u65b9\u5fc3\u76ee\u4e2d\uff0c\u5979\u53ea\u4e0d\u8fc7\u662f\u4e2a\u53d7\u5c3d\u6467\u6b8b\u6298\u78e8\u7684\u53ef\u601c\u4eba\u3002 \u3000\u3000\u3002 \u3000\u3000\u5c4b\u91cc\u6709\u4e00\u76c6\u6c34\uff0c\u4e00\u6761\u6bdb\u5dfe\u3002 \u3000\u3000\u5c0f\u65b9\u7528\u6bdb\u5dfe\u6e29\u6c34\uff0c\u8f7b\u62ed\u5979\u7684\u8138\uff0c\u5979\u8138\u4e0a\u7684\u76b1\u7eb9\u4e0e\u9ed1\u75a4\u5ffd\u7136\u5947\u8ff9\u822c\u6d88\u892a\u4e86\uff0c\u9732\u51fa\u4e86\u4e00\u5f20\u4efb\u4f55\u7537\u4eba\u770b\u89c1\u90fd\u65e0\u6cd5\u4e0d\u52a8\u5fc3\u7684\u8138\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5979\u5589\u5499\u91cc\u5ffd\u7136\u53d1\u51fa\u79cd\u5947\u5f02\u800c\u9500\u9b42\u7684\u547b\u541f\u3002 \u3000\u3000\u5979\u7684\u8eab\u5b50\u4e5f\u5f00\u59cb\u626d\u52a8\uff0c\u7ea4\u7ec6\u7684\u8170\u5728\u626d\u52a8\uff0c\u4fee\u957f\u7ed3\u5b9e\u7684\u817f\u4e5f\u5f00\u59cb\u626d\u52a8\u3002 \u3000\u3000\u80fd\u5fcd\u53d7\u8fd9\u79cd\u626d\u52a8\u7684\u7537\u4eba\u7edd\u5bf9\u4e0d\u591a\uff0c\u5e78\u597d\u5c0f\u65b9\u662f\u5c11\u6570\u51e0\u4e2a\u4eba\u4e2d\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u4ed6\u5c3d\u91cf\u4e0d\u53bb\u770b\u5979\u3002 \u3000\u3000\u4ed6\u51c6\u5907\u627e\u6837\u4e1c\u897f\u76d6\u4f4f\u5979\u7684\u8eab\u5b50\u3002 \u3000\u3000\u4f46\u662f\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5979\u5ffd\u7136\u4f38\u51fa\u4e86\u624b\uff0c\u5c06\u5c0f\u65b9\u7d27\u7d27\u62b1\u4f4f\u3002 \u3000\u3000\u5979\u62b1\u5f97\u597d\u7d27\u597d\u7d27\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u5feb\u8981\u6df9\u6b7b\u7684\u4eba\u62b1\u4f4f\u4e86\u4e00\u5757\u6d6e\u6728\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u5fcd\u7528\u529b\u53bb\u63a8\u5979\uff0c\u53c8\u4e0d\u80fd\u4e0d\u63a8\u5f00\u5979\u3002 \u3000\u3000\u4ed6\u4f38\u624b\u53bb\u63a8\uff0c\u53c8\u7acb\u523b\u7f29\u56de\u4e86\u624b\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u4f60\u4e5f\u66fe\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u53bb\u63a8\u8fc7\u4e00\u4e2a\u5973\u4eba\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u8981\u7f29\u56de\u624b\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5973\u4eba\u8eab\u4e0a\u4e0d\u80fd\u88ab\u7537\u4eba\u63a8\u7684\u5730\u65b9\u5f88\u591a\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4f60\u53bb\u63a8\u7684\u4e00\u5b9a\u662f\u8fd9\u79cd\u5730\u65b9\u3002 \u3000\u3000\u5979\u7684\u8eab\u5b50\u662f\u6eda\u70eb\u7684\u3002 \u3000\u3000\u5979\u7684\u5fc3\u8df3\u5f97\u597d\u5feb\u597d\u5feb\u597d\u5feb\u3002 \u3000\u3000\u5979\u7684\u547c\u5438\u4e2d\u4e5f\u5e26\u7740\u90a3\u79cd\u50cf\u80ed\u8102\u822c\u7684\u9152\u6c14\uff0c\u4e00\u53e3\u53e3\u547c\u5438\u90fd\u4f20\u5165\u5c0f\u65b9\u547c\u5438\u91cc\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u660e\u767d\u4e86\uff0c\u660e\u767d\u90a3\u4e2a\u91ce\u517d\u4e3a\u4ec0\u4e48\u8981\u7528\u8fd9\u79cd\u9152\u6765\u704c\u5979\u4e86\u2014\u2014\u90a3\u662f\u50ac\u60c5\u7684\u9152\u3002 \u3000\u3000\u53ef\u60dc\u5c31\u5728\u4ed6\u660e\u767d\u8fd9\u4e00\u70b9\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u540c\u6837\u88ab\u8ff7\u9189\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u4f53\u5df2\u7ecf\u5ffd\u7136\u8d77\u4e86\u79cd\u4efb\u4f55\u4eba\u81ea\u5df1\u90fd\u65e0\u6cd5\u63a7\u5236\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u7684\u7406\u667a\u5df2\u5d29\u6e83\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u7528\u5979\u7684\u626d\u52a8\u7684\u8eab\u5b50\u7f20\u4f4f\u4e86\u4ed6\uff0c\u7ede\u4f4f\u4e86\u4ed6\uff0c\u5c06\u4ed6\u7684\u8eab\u4f53\u5f15\u5bfc\u5165\u7f6a\u6076\u3002 \u3000\u3000\u50ac\u60c5\u7684\u9152\uff0c\u5df2\u7ecf\u6fc0\u53d1\u4e86\u4ed6\u4eec\u8eab\u4f53\u91cc\u6700\u53e4\u8001\u3001\u6700\u4e0d\u53ef\u6297\u62d2\u7684\u4e00\u79cd\u6b32\u671b\u3002 \u3000\u3000\u81ea\u4ece\u6709\u4eba\u7c7b\u4ee5\u6765\uff0c\u5c31\u6709\u4e86\u8fd9\u79cd\u6b32\u671b\u3002 \u3000\u3000\u9020\u6210\u9519\u8bef\u7684\u539f\u56e0\u6709\u5f88\u591a\u79cd\u3002\u8fd9\u79cd\u6b32\u671b\u65e0\u7591\u4e5f\u662f\u5176\u4e2d\u7684\u4e00\u79cd\u3002 \u3000\u3000\u73b0\u5728\u9519\u8bef\u5df2\u9020\u6210\uff0c\u5df2\u7ecf\u6c38\u8fdc\u65e0\u6cd5\u633d\u56de\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u51e1\u4eba\uff0c\u5728\u4e00\u79cd\u65e0\u6cd5\u6297\u62d2\u7684\u60c5\u51b5\u4e0b\u3001\u9020\u6210\u4e86\u4e00\u4e2a\u9519\u8bef\u3002 \u3000\u3000\u8fd9\u79cd\u201c\u9519\u8bef\u201d\u80fd\u4e0d\u80fd\u7b97\u662f\u9519\u8bef\uff0c\u662f\u4e0d\u662f\u53ef\u4ee5\u539f\u8c05\uff1f \u3000\u3000\u9519\u8bef\u5df2\u9020\u6210\uff0c\u6fc0\u60c5\u5df2\u5e73\u9759\uff0c\u6b32\u671b\u5df2\u6b7b\uff0c\u6f2b\u6f2b\u957f\u591c\u5df2\u5c06\u5c3d\u3002 \u3000\u3000\u8fd9\u4e00\u523b\u6b63\u662f\u75db\u82e6\u4e0e\u6b22\u4e50\u4ea4\u66ff\u7684\u65f6\u5019\u3002 \u3000\u3000\u8fd9\u4e00\u523b\uff0c\u4e5f\u6b63\u662f\u4eba\u7c7b\u826f\u77e5\u590d\u82cf\u3001\u6094\u6068\u521d\u751f\u7684\u65f6\u5019\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u523b\uff0c\u5c0f\u65b9\u5df2\u5b8c\u5168\u6e05\u9192\u3002 \u3000\u3000\u70db\u6cea\u5df2\u5e72\uff0c\u706f\u5df2\u706d\uff0c\u7528\u677e\u679d\u7c97\u7eb8\u7cca\u6210\u7684\u7a97\u6237\u5df2\u6e10\u6e10\u53d1\u767d\uff0c\u82cd\u767d\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u4e5f\u662f\u82cd\u767d\u7684\u3002 \u3000\u3000\u2014\u2014\u8d75\u7fa4\u662f\u6761\u597d\u6c49\uff0c\u751a\u81f3\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u2014\u2014\u82cf\u82cf\u662f\u8d75\u7fa4\u7684\u5973\u4eba\uff0c\u662f\u8d75\u7fa4\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u90fd\u8981\u5f97\u5230\u7684\u5973\u4eba\u3002 \u3000\u3000\u73b0\u5728\u82cf\u82cf\u5374\u5728\u4ed6\u8eab\u7554\uff0c\u4ed6\u4ecd\u53ef\u611f\u89c9\u5230\u5979\u7684\u547c\u5438\u3001\u5979\u7684\u5fc3\u8df3\uff0c\u5979\u7684\u4f53\u6e29\u4ee5\u53ca\u5979\u6fc0\u60c5\u5e73\u590d\u540e\u90a3\u79cd\u6e29\u67d4\u6ee1\u8db3\u7684\u5b81\u9759\u3002 \u3000\u3000\u90a3\u79cd\u672c\u6765\u603b\u662f\u80fd\u4ee4\u4e00\u4e2a\u7537\u4eba\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u53bb\u6362\u53d6\u7684\u6109\u5feb\u548c\u5b81\u9759\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u65b9\u5374\u53ea\u5e0c\u671b\u80fd\u6bc1\u6389\u8fd9\u4e00\u5207\u3002\u4ed6\u4e0d\u80fd\u3002\u8fd9\u662f\u4ed6\u81ea\u5df1\u9020\u6210\u7684\uff0c\u4ed6\u4e0d\u80fd\u9003\u907f\uff0c\u4e5f\u4e0d\u80fd\u63a8\u62d2\u3002 \u3000\u3000\u662f\u81ea\u5df1\u9020\u6210\u7684\uff0c\u81ea\u5df1\u5c31\u5f97\u63a5\u53d7\uff0c\u4e0d\u7ba1\u81ea\u5df1\u9020\u6210\u7684\u662f\u4ec0\u4e48\u90fd\u5f97\u63a5\u53d7\u3002 \u3000\u3000\u7a97\u7eb8\u53d1\u767d\uff0c\u56db\u4e0b\u4ecd\u7136\u5bc2\u65e0\u4eba\u58f0\u3002 \u3000\u3000\u2014\u2014\u8d75\u7fa4\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6709\u56de\u6765\uff1f \u3000\u3000\u2014\u2014\u8d75\u7fa4\u56de\u6765\u4e86\u600e\u4e48\u529e\uff1f \u3000\u3000\u8fd9\u4e24\u4e2a\u95ee\u9898\u540c\u6837\u90fd\u662f\u6ca1\u6709\u4eba\u80fd\u591f\u89e3\u7b54\u7684\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u8d75\u7fa4\u56de\u6765\u4e86\uff0c\u662f\u5e94\u8be5\u7792\u4f4f\u4ed6\uff0c\u8fd8\u662f\u5e94\u8be5\u5411\u4ed6\u5766\u767d\uff1f \u3000\u3000\u806a\u660e\u4eba\u4e00\u5b9a\u4f1a\u8bf4\uff1a \u3000\u3000\u2014\u2014\u7792\u4f4f\u4ed6\uff0c\u5982\u679c\u4ed6\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u5927\u5bb6\u7684\u5fc3\u91cc\u90fd\u4f1a\u6bd4\u8f83\u597d\u53d7\u4e9b\uff0c\u4ed6\u4ecd\u7136\u53ef\u4ee5\u548c\u82cf\u82cf\u5728\u4e00\u8d77\u751f\u6d3b\uff0c\u4e5f\u8bb8\u8fd8\u662f\u80fd\u751f\u6d3b\u5f97\u5f88\u6109\u5feb\u3002 \u3000\u3000\u5982\u679c\u5c0f\u65b9\u4e5f\u662f\u4e2a\u806a\u660e\u7684\u4eba\uff0c\u90a3\u4ed6\u5c31\u4f1a\u8fd9\u6837\u8bf4\uff0c\u4f46\u662f\u4ed6\u4ece\u6765\u90fd\u4e0d\u60f3\u505a\u806a\u660e\u4eba\u3002\u6709\u65f6\u4ed6\u60c5\u613f\u7b28\u4e00\u70b9\uff0c\u4e5f\u4e0d\u613f\u592a\u806a\u660e\u3002 \u3000\u3000\u82cf\u82cf\u4e5f\u9192\u4e86\uff0c\u6b63\u5728\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u7684\u8868\u60c5\u4e5f\u4e0d\u77e5\u662f\u75db\u82e6\uff0c\u662f\u6094\u6068\uff0c\u662f\u8ff7\u60c6\uff0c\u8fd8\u662f\u6b49\u759a\uff1f \u3000\u3000\u201c\u8fd9\u4e0d\u80fd\u602a\u4f60\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u8bf4\uff1a\u201c\u4ed6\u903c\u6211\u559d\u7684\u662f\u9500\u9b42\u80ed\u8102\u9152\uff0c\u5415\u4e09\u4e5f\u4e0d\u77e5\u7528\u8fd9\u79cd\u9152\u6bc1\u6389\u4e86\u591a\u5c11\u4e2a\u5973\u5b69\u5b50\u7684\u6e05\u767d\u3002\u201d \u3000\u3000\u201c\u5415\u4e09\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u95ee\uff1a\u201c\u90a3\u4e2a\u4eba\u4e5f\u662f\u5415\u4e09\u7684\u5c5e\u4e0b\u53f6 \u3000\u3000\u82cf\u82cf\u70b9\u5934\uff0c\u4f38\u624b\u5165\u6795\u4e0b\uff0c\u6478\u51fa\u6837\u4e1c\u897f\uff0c\u7d27\u7d27\u6293\u5728\u624b\u91cc\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u644a\u5f00\u624b\u638c\u3002 \u3000\u3000\u5979\u624b\u91cc\u6293\u4f4f\u7684\u662f\u4e00\u53ea\u91d1\u624b\uff0c\u4e00\u53ea\u5f88\u5c0f\u5f88\u5c0f\u7684\u91d1\u624b\uff0c\u8fdc\u6bd4\u5c0f\u65b9\u4ee5\u524d\u770b\u8fc7\u7684\u5c0f\u5f97\u591a\u3002\u5415\u4e09\u7684\u5c5e\u4e0b\uff0c\u65e0\u7591\u662f\u7528\u91d1\u624b\u7684\u5927\u5c0f\u6765\u5206\u9636\u7ea7\u7684\uff0c\u91d1\u624b\u8d8a\u5c0f\uff0c\u9636\u7ea7\u8d8a\u4f4e\u3002 \u3000\u3000\u90a3\u4e2a\u91ce\u517d\u822c\u7684\u5927\u6c49\u53ea\u4e0d\u8fc7\u662f\u5415\u4e09\u5c5e\u4e0b\u4e00\u4e2a\u5c0f\u5352\u800c\u5df2\u3002 \u3000\u3000\u201c\u4ed6\u4e5f\u662f\u90a3\u4e94\u4e2a\u4eba\u5176\u4e2d\u4e4b\u4e00\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\uff1a\u201c\u2018\u9633\u5149\u2019\u5c31\u662f\u88ab\u4ed6\u4eec\u63b3\u8d70\u7684\uff1f\u201d \u3000\u3000\u82cf\u82cf\u70b9\u5934\u53f9\u606f\uff1a\u201c\u6211\u59cb\u7ec8\u4e0d\u660e\u767d\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u7ed1\u8d70\u5979\uff1f\u5374\u6ca1\u6709\u7ed1\u8d70\u6211\uff1f\u201d \u3000\u3000\u5979\u81ea\u5df1\u89e3\u7b54\u4e86\u8fd9\u95ee\u9898\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4eec\u53c8\u628a\u5979\u5f53\u505a\u4e86\u6211\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u8981\u627e\u7684\u672c\u662f\u5979\u3002\u53cd\u6b63\u5415\u4e09\u6240\u505a\u7684\u4e8b\uff0c\u603b\u662f\u8ba9\u4eba\u6478\u4e0d\u900f\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u82cf\u82cf\u5ffd\u7136\u6539\u53d8\u8bdd\u9898\uff0c\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u8981\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4ecd\u7136\u6c89\u9ed8\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u771f\u7684\u8981\u8d70\uff0c\u8981\u53bb\u627e\u5415\u4e09\uff0c\u4f60\u7528\u4e0d\u7740\u987e\u5fcc\u6211\u3002\u201d \u3000\u3000\u82cf\u82cf\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u4ee4\u4eba\u5fc3\u788e\uff1a \u3000\u3000\u201c\u6211\u4eec\u672c\u6765\u5c31\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f60\u8981\u8d70\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u8d70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u662f\u771f\u7684\u8981\u8d70\u4e86\uff0c\u4f46\u662f\u4ed6\u53c8\u600e\u4e48\u80fd\u628a\u5979\u4e00\u4e2a\u4eba\u7559\u5728\u8fd9\u91cc\uff1f\u4e0d\u7ba1\u8fd9\u4ef6\u4e8b\u662f\u8c01\u7684\u9519\uff0c\u4e0d\u7ba1\u4ed6\u4eec\u4e4b\u95f4\u4ee5\u540e\u600e\u4e48\u6837\uff0c\u5979\u90fd\u5df1\u53d8\u6210\u4ed6\u751f\u547d\u4e2d\u7684\u4e00\u90e8\u5206\uff0c\u4ed6\u5df2\u65e0\u6cd5\u63a8\u62d2\u9003\u907f\u3002 \u3000\u3000\u82cf\u82cf\u5ffd\u53c8\u53f9\u606f\uff1a\u201c\u4e0d\u7ba1\u4f60\u80fd\u4e0d\u80fd\u627e\u5230\u5415\u4e09\uff0c\u4f60\u90fd\u4e00\u5b9a\u8981\u8d70\uff0c\u975e\u8d70\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u73b0\u5728\u5415\u4e09\u624b\u4e0b\u5df2\u7ecf\u6709\u5f88\u591a\u4eba\u90fd\u80fd\u8ba4\u5f97\u51fa\u6211\u4e86\u3002\u201d \u3000\u3000\u56e0\u4e3a\u73b0\u5728\u5979\u8138\u4e0a\u7684\u836f\u7269\u5df2\u88ab\u9152\u6d17\u6389\uff0c\u5df2\u7ecf\u6062\u590d\u4e86\u5979\u672c\u6765\u7684\u9762\u76ee\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u79bb\u5f00\u6211\u3002\u201d \u3000\u3000\u82cf\u82cf\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u90fd\u4e0d\u613f\u8fde\u7d2f\u4f60\u3002\u201d \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5979\u987e\u8651\u7684\u5c45\u7136\u8fd8\u4e0d\u662f\u5979\u81ea\u5df1\u3002\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u5fc3\u91cc\u6709\u70b9\u9178\u9178\u7684\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u624d\u80fd\u5f00\u53e3\u3002\u201c\u6211\u4eec\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u4f60\u5e26\u6211\u53bb\u627e\u5415\u4e09\uff0c\u4f60\u4e00\u5b9a\u80fd\u627e\u5f97\u5230\u4ed6\u3002\u201d \u3000\u3000\u201c\u80fd\u627e\u5230\u4ed6\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u82cf\u82cf\u82e6\u7b11\uff0c\u201c\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u5979\u53c8\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5415\u4e09\u5c5e\u4e0b\u6709\u591a\u5c11\u9ad8\u624b\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u3002\u4ed6\u4e0d\u6015\u6b7b\uff0c\u53ef\u662f\u4ed6\u65e0\u6743\u8981\u82cf\u82cf\u966a\u4ed6\u53bb\u9001\u6b7b\uff0c\u8c01\u90fd\u65e0\u6743\u4e3b\u5bb0\u522b\u4eba\u7684\u751f\u6b7b\u547d\u8fd0\u3002 \u3000\u3000\u4f46\u662f\u82cf\u82cf\u5374\u5ffd\u7136\u6349\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u5ffd\u7136\u8bf4\uff1a\u201c\u6211\u4eec\u8d70\u5427\uff0c\u73b0\u5728\u5c31\u8d70\u3002\u201d \u3000\u3000\u201c\u8d70\uff1f\u201d\u5c0f\u65b9\u832b\u7136\u95ee\u9053\uff1a\u201c\u8d70\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u968f\u4fbf\u5230\u54ea\u91cc\u53bb\uff01\u201d \u3000\u3000\u82cf\u82cf\u53c8\u5f00\u59cb\u6fc0\u52a8\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u53bb\u627e\u4e2a\u6ca1\u6709\u4eba\u80fd\u627e\u5f97\u5230\u7684\u5730\u65b9\u8eb2\u8d77\u6765\uff0c\u5fd8\u8bb0\u6240\u6709\u7684\u4eba\u3001\u6240\u6709\u7684\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u95ed\u7740\u5634\u3002 \u3000\u3000\u82cf\u82cf\u5ffd\u53c8\u53f9\u606f\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u60f3\u95ee\u6211\u662f\u4e0d\u662f\u4e5f\u80fd\u5fd8\u8bb0\u8d75\u7fa4\u3002\u201d \u3000\u3000\u5979\u53cd\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u73b0\u5728\u8fd8\u6709\u8138\u89c1\u8d75\u7fa4\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u56db\u7ae0\u3000\u6709\u4e86\u4f60\u7684\u5b69\u5b50 \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u6709\u4e86\u4f60\u7684\u5b69\u5b50 \u3000\u3000\u5c0f\u65b9\u7684\u624b\u662f\u51b7\u7684\uff0c\u5fc3\u4e5f\u662f\u51b7\u7684\u3002 \u3000\u3000\u4e00\u4ef6\u6c38\u8fdc\u65e0\u6cd5\u633d\u56de\u7684\u9519\u8bef\uff0c\u4e24\u4e2a\u6ca1\u6709\u8138\u89c1\u4eba\u7684\u4eba\u3002 \u3000\u3000\u5982\u679c\u4f60\u662f\u5c0f\u65b9\uff0c\u4f60\u4f1a\u600e\u4e48\u505a\uff1f \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5c0f\u65b9\u624d\u5f00\u53e3\uff0c\u65e0\u7591\u5df2\u4e0b\u5b9a\u51b3\u5fc3\u624d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u6211\u4eec\u518d\u7b49\u4e00\u5929\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff0c\u201c\u4e0d\u7ba1\u6211\u4eec\u8981\u600e\u4e48\u505a\uff0c\u90fd\u8981\u518d\u7b49\u4e00\u5929\u3002\u201d \u3000\u3000\u201c\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b49\u8d75\u7fa4\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u8ba9\u4ed6\u77e5\u9053\uff0c\u867d\u7136\u6211\u4e5f\u6ca1\u6709\u8138\u89c1\u4ed6\uff0c\u5374\u8fd8\u662f\u8981\u7b49\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u82cf\u82cf\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u5df2\u9732\u51fa\u4e86\u5979\u4ece\u672a\u5411\u522b\u7684\u7537\u4eba\u8868\u793a\u8fc7\u7684\u7231\u6155\u4e0e\u5c0a\u656c\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\u5979\u624d\u95ee\uff1a\u201c\u5982\u679c\u4ed6\u6ca1\u6709\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u56de\u7b54\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4e0d\u56de\u6765\uff0c\u6211\u5c31\u8d70\u3002\u201d \u3000\u3000\u8fd9\u6b21\u82cf\u82cf\u95ee\u4ed6\uff1a\u201c\u4f60\u6253\u7b97\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u53bb\u627e\u5415\u4e09\uff0c\u53bb\u6b7b\uff01\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u5230\u90a3\u65f6\u4e0d\u7ba1\u4f60\u8981\u600e\u4e48\u6837\uff0c\u6211\u90fd\u53ea\u6709\u8fd9\u4e00\u6761\u8def\u53ef\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u966a\u6211\u5230\u522b\u7684\u5730\u65b9\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u3002\u201d\u5c0f\u65b9\u7684\u56de\u7b54\u8868\u73b0\u5f97\u575a\u51b3\u5e72\u8106\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5fd8\u4e0d\u4e86\u8fd9\u4e9b\u4eba\u8fd9\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4e0d\u7ba1\u6211\u4eec\u8eb2\u5230\u54ea\u91cc\u53bb\uff0c\u5c31\u7b97\u80fd\u8eb2\u5f00\u522b\u4eba\uff0c\u5374\u8fd8\u662f\u6709\u4e00\u4e2a\u4eba\u662f\u6211\u6c38\u8fdc\u8eb2\u4e0d\u4e86\u7684\u3002\u201d\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u9003\u907f\u522b\u4eba\u7684\u65f6\u5019\uff0c\u53ef\u662f\u6c38\u8fdc\u90fd\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u9003\u907f\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u4eec\u7b49\u4e86\u4e00\u5929\u3002 \u3000\u3000\u8d75\u7fa4\u6ca1\u6709\u56de\u6765\uff5e\u4e00\u975e\u4f46\u6ca1\u6709\u56de\u6765\uff0c\u8fde\u4e00\u70b9\u6d88\u606f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5929\u8272\u53c8\u6e10\u6e10\u6697\u4e86\uff0c\u53c8\u5230\u4e86\u5feb\u5403\u665a\u996d\u7684\u65f6\u5019\uff0c\u82cf\u82cf\u5df2\u7ecf\u6709\u5f88\u4e45\u6ca1\u6709\u5f00\u53e3\uff0c\u5c0f\u65b9\u4e5f\u6ca1\u6709\u3002\u4ed6\u4eec\u5df2\u7ecf\u6709\u5f88\u4e45\u5f88\u4e45\u90fd\u6ca1\u6709\u53bb\u770b\u8fc7\u5bf9\u65b9\uff0c\u4eff\u4f5b\u751f\u6015\u5bf9\u65b9\u773c\u4e2d\u7684\u8868\u60c5\u4f1a\u523a\u4f24\u81ea\u5df1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u90fd\u65e0\u6cd5\u5fd8\u8bb0\u6628\u591c\u7684\u4e8b\u60c5\u3002\u90a3\u79cd\u6fc0\u60c5\u3001\u90a3\u79cd\u7f20\u7ef5\uff0c\u672c\u6765\u5c31\u662f\u5f88\u96be\u5fd8\u5f97\u4e86\u7684\u3002 \u3000\u3000\u2014\u2014\u4ee5\u540e\u600e\u4e48\u529e\uff1f \u3000\u3000\u2014\u2014\u4e24\u4e2a\u6ca1\u6709\u6839\u7684\u4eba\uff0c\u4e00\u6b21\u65e0\u6cd5\u5fd8\u6000\u7684\u7ed3\u5408\uff0c\u4ee5\u540e\u662f\u4e0d\u662f\u5c31\u5e94\u8be5\u7ed3\u5408\u5728\u4e00\u8d77\uff0c\u8fd8\u662f\u5e94\u8be5\u4ece\u6b64\u5404\u5c31\u4e1c\u897f\u3001\u8ba9\u5bf9\u65b9\u4e00\u4e2a\u4eba\u5355\u72ec\u5730\u53bb\u627f\u53d7\u56e0\u4e3a\u9519\u8bef\u800c\u9020\u6210\u7684\u75db\u82e6\u548c\u5185\u759a\uff1f \u3000\u3000\u2014\u2014\u8fd9\u4e9b\u95ee\u9898\u6709\u8c01\u80fd\u7b54\u590d\uff1f\u6709\u8c01\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u505a\u624d\u662f\u5bf9\u7684\uff1f \u3000\u3000\u7a97\u6237\u5f00\u7740\uff0c\u5c0f\u65b9\u7ad9\u5728\u7a97\u53e3\u3002 \u3000\u3000\u7a97\u5916\u66ae\u8272\u6e10\u4e34\uff0c\u5b81\u9759\u7684\u5929\u7a7a\uff0c\u5b81\u9759\u7684\u5c71\u8c37\uff0c\u5b81\u9759\u7684\u9ec4\u660f\uff0c\u5929\u5730\u95f4\u662f\u4e00\u7247\u82cd\u832b\u5b81\u9759\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5ffd\u7136\u62bd\u7d27\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u53d1\u73b0\u6709\u4ef6\u4e8b\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u8981\u5403\u996d\uff0c\u6bcf\u5bb6\u4eba\u53a8\u623f\u91cc\u90fd\u6709\u7089\u7076\uff0c\u5c4b\u9876\u4e0a\u90fd\u6709\u70df\u56f1\u3002 \u3000\u3000\u5230\u4e86\u5feb\u8981\u5403\u665a\u996d\u7684\u65f6\u5019\uff0c\u5bb6\u5bb6\u6237\u6237\u5c4b\u9876\u4e0a\u7684\u70df\u56f1\u91cc\u90fd\u4f1a\u6709\u708a\u70df\u5192\u51fa\u3002 \u3000\u3000\u5915\u9633\u897f\u4e0b\uff0c\u665a\u971e\u6ee1\u5929\uff0c\u708a\u70df\u5904\u5904\uff0c\u4e00\u76f4\u90fd\u662f\u4eba\u95f4\u6700\u80fd\u4ee4\u6e38\u5b50\u601d\u5f52\u7684\u7f8e\u666f\u4e4b\u4e00\u3002 \u3000\u3000\u8fd9\u91cc\u6709\u4eba\u5bb6\uff0c\u6709\u70df\u56f1\uff0c\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u5feb\u8981\u5403\u665a\u996d\u7684\u65f6\u5019\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u91cc\u6ca1\u6709\u708a\u70df\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u4f4f\u5728\u8fd9\u5c71\u6751\u91cc\u7684\uff0c\u90fd\u662f\u4e0d\u98df\u4eba\u95f4\u70df\u706b\u7684\u795e\u4ed9\uff1f \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u95ee\u82cf\u82cf\uff1a\u201c\u4f60\u4ee5\u524d\u5230\u8fd9\u91cc\u6765\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6211\u6765\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u91cc\u7684\u4eba\u5e73\u5e38\u90fd\u5403\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82cf\u82cf\u8bf4\uff1a\u201c\u522b\u4eba\u5403\u4ec0\u4e48\uff0c\u8fd9\u91cc\u7684\u4eba\u4e5f\u5403\u4ec0\u4e48\u3002\u201d\u5979\u5f53\u7136\u4e5f\u53d1\u89c9\u5c0f\u65b9\u95ee\u7684\u8bdd\u5f88\u5947\u602a\uff0c\u6240\u4ee5\u53cd\u95ee\u4ed6\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u770b\u89c1\u4e86\u4ec0\u4e48\u5947\u602a\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u770b\u89c1\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\u3002\u201d\u5c0f\u65b9\u5df2\u7ecf\u60f3\u5230\uff0c\u9664\u4e86\u90a3\u6a35\u592b\u592b\u59bb\u5b50\u5973\u5916\uff0c\u4ed6\u5230\u8fd9\u91cc\u6765\u8fd8\u6ca1\u6709\u770b\u89c1\u8fc7\u522b\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u8981\u51fa\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u4ed6\u65e9\u5c31\u5e94\u8be5\u53bb\u770b\u7684\uff0c\u5982\u679c\u662f\u535c\u9e70\u548c\u73ed\u5bdf\u5df4\u90a3\uff0c\u4e00\u5b9a\u65e9\u5df2\u5c06\u8fd9\u91cc\u6bcf\u6237\u4eba\u5bb6\u90fd\u68c0\u67e5\u8fc7\u4e00\u904d\u3002 \u3000\u3000\u90a3\u201c\u4e94\u4e2a\u4eba\u201d\u8bf4\u4e0d\u5b9a\u4e00\u76f4\u90fd\u8eb2\u5728\u8fd9\u5c71\u6751\u91cc\uff0c\u201c\u9633\u5149\u201d\u5f88\u53ef\u80fd\u4e5f\u6ca1\u6709\u79bb\u5f00\u8fc7\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u8fd9\u5b9e\u5728\u662f\u4ed6\u7684\u758f\u5ffd\u3002 \u3000\u3000\u9020\u6210\u9519\u8bef\u7684\u539f\u56e0\u6709\u5f88\u591a\u79cd\uff0c\u758f\u5ffd\u7edd\u5bf9\u662f\u5176\u4e2d\u6700\u4e0d\u53ef\u539f\u8c05\u7684\u4e00\u79cd\uff0c\u800c\u4e14\u4e5f\u540c\u6837\u6c38\u8fdc\u65e0\u6cd5\u5f25\u8865\u3002 \u3000\u3000\u4ed6\u4eec\u501f\u4f4f\u7684\u8fd9\u4e2a\u6a35\u6237\u77f3\u5c4b\u5c31\u5728\u5c71\u6751\u7684\u8fb9\u7f18\uff0c\u5165\u5c71\u540e\u7b2c\u4e00\u4e2a\u770b\u5230\u7684\u5c31\u662f\u8fd9\u4e00\u5bb6\uff0c\u77f3\u5c4b\u524d\u6709\u6761\u5c0f\u8def\uff0c\u6cbf\u7740\u8fd9\u6761\u5c0f\u8def\u518d\u8d70\u767e\u5341\u6b65\uff0c\u624d\u6709\u7b2c\u4e8c\u5bb6\u4eba\u3002 \u3000\u3000\u8fd9\u5bb6\u4eba\u7684\u5c4b\u5b50\u4e5f\u662f\u7528\u77f3\u5757\u780c\u6210\u7684\uff0c\u540c\u6837\u7528\u677e\u679d\u7c97\u7eb8\u7cca\u6210\u7684\u7a97\u6237\u91cc\uff0c\u73b0\u5728\u5df2\u6709\u4e86\u706f\u5149\uff0c\u521a\u71c3\u8d77\u7684\u706f\u5149\u3002 \u3000\u3000\u7a97\u5173\u7740\uff0c\u95e8\u4e5f\u5173\u7740\u3002\u5c0f\u65b9\u6572\u95e8\u3002 \u3000\u3000\u4ed6\u6572\u4e86\u5f88\u4e45\u90fd\u6ca1\u6709\u4eba\u6765\u5e94\u95e8\u3002 \u3000\u3000\u2014\u2014\u5c4b\u91cc\u6709\u706f\uff0c\u5c31\u5e94\u8be5\u6709\u4eba\u3002 \u3000\u3000\u2014\u2014\u4ed6\u5f00\u59cb\u6572\u95e8\u7684\u65f6\u5019\uff0c\u82cf\u82cf\u5c31\u8ddf\u7740\u6765\u4e86\uff0c\u8eab\u4e0a\u7a7f\u7740\u90a3\u6a35\u592b\u59bb\u5b50\u7684\u7c97\u5e03\u8863\u670d\uff0c\u88e4\u7ba1\u8863\u8896\u90fd\u5377\u5f97\u9ad8\u9ad8\u7684\uff0c\u9732\u51fa\u4e00\u6bb5\u96ea\u767d\u7684\u5c0f\u817f\u3002 \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u95ee\u5979\uff1a\u201c\u4ee5\u524d\u4f60\u6709\u6ca1\u6709\u5230\u8fd9\u4e00\u5bb6\u6765\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u82cf\u82cf\u53c8\u60f3\u4e86\u60f3\u518d\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u8fd9\u4e00\u5bb6\u4f4f\u7684\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u5bb6\u4f4f\u7684\u5c31\u662f\u90a3\u6a35\u592b\u7684\u8868\u54e5\u3002\u201d \u3000\u3000\u82cf\u82cf\u8bf4\uff1a\u201c\u6211\u4eec\u5230\u8fd9\u6a35\u592b\u5bb6\u91cc\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u4e00\u5bb6\u5927\u5c0f\u5c31\u5168\u90fd\u4f4f\u5230\u4ed6\u7684\u8868\u54e5\u5bb6\u91cc\u6765\u4e86\u3002\u2026 \u3000\u3000\u5979\u8ddf\u8d75\u7fa4\u4ee5\u524d\u4e00\u5b9a\u5e38\u6765\uff0c\u8fd9\u91cc\u4e00\u5b9a\u5c31\u662f\u4ed6\u4eec\u7684\u79d8\u5bc6\u5e7d\u4f1a\u4e4b\u5904\u3002 \u3000\u3000\u5982\u679c\u8bf4\u5c0f\u65b9\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u90a3\u662f\u5047\u7684\u3002\u5982\u679c\u8bf4\u5c0f\u65b9\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u4e4b\u540e\uff0c\u5fc3\u91cc\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\uff0c\u90a3\u4e5f\u662f\u5047\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u6572\u95e8\u3002 \u3000\u3000\u4ed6\u53c8\u6572\u4e86\u5f88\u4e45\uff0c\u8fde\u95e8\u677f\u90fd\u8d77\u4e86\u9707\u52a8\uff0c\u5c31\u7b97\u5c4b\u91cc\u7684\u4eba\u90fd\u662f\u804b\u5b50\uff0c\u4e5f\u5e94\u8be5\u77e5\u9053\u91cc\u9762\u6709\u4eba\u5728\u6572\u95e8\u4e86\u3002 \u3000\u3000\u91cc\u9762\u5374\u8fd8\u662f\u6ca1\u6709\u4eba\u6765\u5e94\u95e8\uff0c\u56e0\u4e3a\u5c4b\u91cc\u6839\u672c\u6ca1\u6709\u4eba\uff0c\u8fde\u4e2a\u4eba\u5f71\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u8bc1\u5b9e\u4e86\u8fd9\u4e00\u70b9\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u7528\u80a9\u8180\u628a\u8fd9\u6247\u95e8\u649e\u5f00\u4e86\u3002 \u3000\u3000\u5c4b\u91cc\u867d\u7136\u6ca1\u6709\u4eba\uff0c\u5374\u70b9\u7740\u706f\u3002 \u3000\u3000\u4e00\u76cf\u666e\u666e\u901a\u901a\u7684\u6cb9\u706f\uff0c\u4e00\u95f4\u666e\u666e\u901a\u901a\u7684\u5c4b\u5b50\uff0c\u4e00\u4e9b\u666e\u666e\u901a\u901a\u7684\u5bb6\u5177\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u4e00\u8d70\u8fdb\u8fd9\u5c4b\u5b50\uff0c\u8138\u8272\u5c31\u53d8\u4e86\uff0c\u53d8\u5f97\u5c31\u597d\u50cf\u5ffd\u7136\u770b\u89c1\u9b3c\u90a3\u4e48\u53ef\u6015\u3002 \u3000\u3000\u9b3c\u5e76\u4e0d\u53ef\u6015\uff0c\u6709\u5f88\u591a\u4eba\u90fd\u4e0d\u6015\u9b3c\uff0c\u5c0f\u65b9\u4e5f\u4e0d\u6015\uff0c\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u66f4\u4e0d\u6015\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u91cc\u6839\u672c\u5c31\u6ca1\u6709\u9b3c\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u91cc\u7684\u6bcf\u6837\u4e1c\u897f\uff0c\u90fd\u662f\u4e00\u4e2a\u666e\u901a\u4eba\u5bb6\u5c4b\u5b50\u91cc\u5e94\u8be5\u6709\u7684\uff0c\u751a\u81f3\u6bd4\u522b\u7684\u666e\u901a\u4eba\u5bb6\u91cc\u6240\u6709\u7684\u66f4\u7b80\u6734\u3002 \u3000\u3000\u82cf\u82cf\u5e76\u4e0d\u5927\u4e86\u89e3\u5c0f\u65b9\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e24\u5929\u5979\u80fd\u770b\u5f97\u51fa\u5c0f\u65b9\u7edd\u4e0d\u662f\u8f7b\u6613\u5c31\u4f1a\u88ab\u60ca\u5413\u7684\u4eba\u3002 \u3000\u3000\u73b0\u5728\u5979\u4e5f\u770b\u5f97\u51fa\u5c0f\u65b9\u786e\u5b9e\u88ab\u5413\u5446\u4e86\u3002 \u3000\u3000\u5979\u6ca1\u6709\u518d\u95ee\u5c0f\u65b9\u201c\u4f60\u770b\u89c1\u4ec0\u4e48\u201d\u3002 \u3000\u3000\u56e0\u4e3a\u5c0f\u65b9\u770b\u5f97\u89c1\u7684\uff0c\u5979\u4e5f\u4e00\u6837\u80fd\u770b\u5f97\u89c1\uff0c\u5979\u6240\u770b\u89c1\u7684\u4e1c\u897f\uff0c\u6ca1\u6709\u4e00\u6837\u80fd\u8ba9\u5979\u5bb3\u6015\u7684\u3002 \u3000\u3000\u5979\u770b\u89c1\u53ea\u4e0d\u8fc7\u662f\u4e00\u5f20\u5e8a\u3001\u4e00\u5f20\u684c\u5b50\u3001\u51e0\u5f20\u6905\u5b50\u3001\u4e00\u4e2a\u5986\u53f0\u3001\u4e00\u4e2a\u8863\u67dc\u3001\u4e00\u76cf\u6cb9\u706f\uff0c\u6bcf\u6837\u4e1c\u897f\u90fd\u5f88\u7b80\u964b\uff0c\u5f88\u9648\uff1b\u65e5\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u89c1\u7684\u4e5f\u540c\u6837\u662f\u8fd9\u4e9b\uff0c\u8c01\u4e5f\u60f3\u4e0d\u51fa\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u6015\u5f97\u8fd9\u4e48\u5389\u5bb3\u3002 \u3000\u3000\u6cb9\u706f\u7684\u706f\u82af\uff0c\u662f\u7528\u68c9\u82b1\u6413\u6210\u7684\uff0c\u521a\u70b9\u7740\u8fd8\u6ca1\u6709\u591a\u4e45\u3002 \u3000\u3000\u5c0f\u65b9\u521a\u624d\u7ad9\u5728\u90a3\u680b\u5c4b\u5b50\u7a97\u53e3\u7684\u65f6\u5019\uff0c\u8fd9\u680b\u5c4b\u5b50\u91cc\u8fd8\u6ca1\u6709\u70b9\u706f\u3002 \u3000\u3000\u4ed6\u8d70\u51fa\u6765\u7684\u65f6\u5019\uff0c\u706f\u624d\u70b9\u8d77\u6765\u3002 \u3000\u3000\u70b9\u706f\u7684\u4eba\u5462\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u518d\u53bb\u627e\u70b9\u706f\u7684\u4eba\uff0c\u4e5f\u6ca1\u6709\u518d\u5230\u522b\u7684\u90a3\u4e9b\u4eba\u5bb6\u53bb\u3002 \u3000\u3000\u4ed6\u5750\u4e86\u4e0b\u6765\u4e86\uff0c\u5750\u5728\u706f\u4e0b\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u770b\u6765\u5df2\u7ecf\u662f\u89c1\u5230\u9b3c\u4e86\uff0c\u73b0\u5728\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u770b\u6765\u5c31\u50cf\u662f\u9b3c\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u8fd9\u623f\u5b50\u662f\u680b\u9b3c\u5c4b\uff0c\u5230\u5904\u90fd\u9690\u85cf\u7740\u51e1\u4eba\u8089\u773c\u770b\u4e0d\u89c1\u7684\u5996\u9b54\u9b3c\u602a\u5e7d\u7075\u9634\u9b42\uff0c\u65e0\u8bba\u4ec0\u4e48\u4eba\u53ea\u8981\u4e00\u8d70\u8fdb\u8fd9\u5c4b\u5b50\uff0c\u90fd\u8981\u53d7\u4ed6\u4eec\u7684\u6446\u5f04\uff1f \u3000\u3000\u2014\u2014\u90a3\u4e48\u82cf\u82cf\u4e3a\u4ec0\u4e48\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\uff1f \u3000\u3000\u2014\u2014\u96be\u9053\u8fd9\u5c4b\u91cc\u7684\u5996\u9b54\u9b3c\u602a\u5e7d\u7075\u9634\u9b42\u8981\u6211\u7684\u53ea\u662f\u5c0f\u65b9\u4e00\u4e2a\uff1f\u82cf\u82cf\u5b9e\u5728\u5f88\u60f3\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff0c\u53ef\u662f\u5979\u4e0d\u6562\u95ee\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u6837\u5b50\u5b9e\u5728\u592a\u8ba9\u4eba\u5bb3\u6015\u3002 \u3000\u3000\u5c0f\u65b9\u5750\u4e0b\u6765\uff0c\u5750\u5728\u9760\u5899\u7684\u90a3\u5f20\u6728\u684c\u65c1\u4e00\u628a\u7834\uff1b\u65e5\u7684\u7af9\u6905\u4e0a\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u53d8\u5f97\u66f4\u590d\u6742\uff0c\u9664\u4e86\u6050\u60e7\u6124\u6012\u5916\uff0c\u4eff\u4f5b\u8fd8\u5e26\u7740\u79cd\u6c38\u8fdc\u7406\u4e0d\u6e05\u4e5f\u526a\u4e0d\u65ad\u7684\u67d4\u60c5\u548c\u601d\u5ff5\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u7b80\u964b\u7684\u5c4b\u5b50\uff0c\u600e\u4e48\u4f1a\u8ba9\u4ed6\u5728\u4e00\u77ac\u95f4\u540c\u65f6\u751f\u51fa\u8fd9\u4e24\u79cd\u6781\u7aef\u4e0d\u540c\u7684\u60c5\u611f\uff1f \u3000\u3000\u82cf\u82cf\u53c8\u60f3\u95ee\uff0c\u8fd8\u662f\u4e0d\u6562\u95ee\uff0c\u5c0f\u65b9\u5374\u5ffd\u7136\u5f00\u53e3\uff1a\u201c\u6211\u4e5f\u8ddf\u522b\u4eba\u4e00\u6837\uff0c\u6211\u4e5f\u6709\u7236\u6bcd\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u7684\u7236\u4eb2\u662f\u4e2a\u9556\u5e08\uff0c\u5341\u4e94\u5e74\u524d\u5728\u6c5f\u5357\u4e5f\u6709\u70b9\u540d\u671b\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4f4e\u6c89\u7f13\u6162\u5636\u54d1\u4ed6\u8bf4\uff1a\u201c\u6211\u7684\u6bcd\u4eb2\u6e29\u67d4\u8d24\u6167\uff0c\u80c6\u5b50\u53c8\u5c0f\uff0c\u6bcf\u6b21\u6211\u7236\u4eb2\u51fa\u53bb\u8d70\u5ad6\u7684\u65f6\u5019\uff0c\u5979\u90fd\u6ca1\u6709\u4e00\u5929\u665a\u4e0a\u80fd\u7761\u5f97\u7740\u89c9\u3002\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u5931\u8e2a\uff0c\u8d75\u7fa4\u672a\u8fd4\uff0c\u51f6\u5146\u5df2\u751f\uff0c\u201c\u91d1\u624b\u201d\u5df2\u73b0\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5c0f\u65b9\u600e\u4e48\u4f1a\u5ffd\u7136\u8c08\u8d77\u4ed6\u7684\u7236\u6bcd\u6765\uff1f \u3000\u3000\u82cf\u82cf\u53c8\u60f3\u95ee\uff0c\u8fd8\u662f\u4e0d\u6562\u95ee\uff0c\u53c8\u8fc7\u4e86\u534a\u664c\uff0c\u5c0f\u65b9\u624d\u63a5\u7740\u8bf4\uff1a\u201c\u5728\u6211\u4e94\u5c81\u7684\u90a3\u4e00\u5e74\uff0c\u6211\u6bcd\u4eb2\u62c5\u5fc3\u7684\u4e8b\u7ec8\u4e8e\u8fd8\u662f\u53d1\u751f\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u90a3\u4e00\u5e74\u7684\u4e09\u6708\uff0c\u6211\u7236\u4eb2\u62a4\u9556\u5230\u4e2d\u539f\uff0c\u9556\u8f66\u5728\u4e2d\u6761\u5c71\u9047\u76d7\u88ab\u52ab\uff0c\u6211\u7236\u4eb2\u4e5f\u6ca1\u6709\u56de\u6765\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u66f4\u4f4e\u6c89\u5636\u54d1\uff1a\u201c\u9556\u5e08\u7684\u6536\u5165\u5e76\u4e0d\u591a\uff0c\u6211\u7236\u4eb2\u7684\u51fa\u624b\u4e00\u5411\u5f88\u5927\u65b9\uff0c\u6211\u4eec\u5bb6\u91cc\u65e5\u5b50\u867d\u7136\u8fd8\u8fc7\u5f97\u53bb\uff0c\u4f46\u662f\u8fde\u4e00\u70b9\u79ef\u84c4\u90fd\u6ca1\u6709\uff0c\u4ed6\u9047\u96be\u4e4b\u540e\uff0c\u6211\u4eec\u6bcd\u5b50\u5c31\u8fde\u65e5\u5b50\u90fd\u8fc7\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u82cf\u82cf\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u90a3\u5bb6\u9556\u5c40\u5462\uff1f\u4f60\u7236\u4eb2\u4e3a\u4ed6\u4eec\u62fc\u547d\u6b89\u804c\uff0c\u4ed6\u4eec\u96be\u9053\u4e0d\u7167\u987e\u4f60\u4eec\u6bcd\u5b50\u7684\u751f\u6d3b\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u8d54\u90a3\u8d9f\u9556\uff0c\u90a3\u5bb6\u9556\u5c40\u4e5f\u57ae\u4e86\uff0c\u9556\u5c40\u7684\u4e3b\u4eba\u4e5f\u4e0a\u4e86\u540a\u3002\u201d \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u4eba\u7684\u60b2\u5267\uff0c\u6c5f\u6e56\u4e2d\u65f6\u65f6\u523b\u523b\u90fd\u4f1a\u6709\u8fd9\u79cd\u60b2\u5267\u53d1\u751f\u3002 \u3000\u3000\u5200\u5c16\u8210\u8840\u7684\u6c5f\u6e56\u4eba\uff0c\u5feb\u610f\u6069\u4ec7\uff0c\u6709\u51e0\u4eba\u80fd\u4e86\u89e3\u4ed6\u4eec\u60b2\u60e8\u9ed1\u6697\u7684\u4e00\u9762\uff1f \u3000\u3000\u82cf\u82cf\u9eef\u7136\uff1a \u3000\u3000\u201c\u4f46\u662f\u4f60\u4eec\u8fd8\u5f97\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5979\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u4eec\u662f\u600e\u4e48\u6d3b\u4e0b\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u600e\u4e48\u6d3b\u4e0b\u53bb\u7684\uff1f\u662f\u600e\u4e48\u6d3b\u4e0b\u53bb\u7684\uff1f\u2026\u2026\u201d \u3000\u3000\u5c0f\u65b9\u63e1\u7d27\u53cc\u62f3\uff0c\u773c\u4e2d\u7684\u795e\u60c5\u5c31\u597d\u50cf\u88ab\u4eba\u523a\u4e86\u4e00\u5200\uff0c\u523a\u5728\u5fc3\u53e3\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u65e0\u4eb2\u65e0\u6545\u65e0\u4f9d\u65e0\u9760\u7684\u5973\u4eba\uff0c\u5e26\u7740\u4e00\u4e2a\u4e94\u5c81\u5927\u7684\u5b69\u5b50\uff0c\u8981\u600e\u4e48\u6837\u624d\u80fd\u6d3b\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u82cf\u82cf\u662f\u4e2a\u5973\u4eba\uff0c\u5979\u5f53\u7136\u80fd\u660e\u767d\u5c0f\u65b9\u7684\u610f\u601d\u3002 \u3000\u3000\u4e00\u4e2a\u65e0\u4eb2\u65e0\u6545\u65e0\u4f9d\u65e0\u9760\u7684\u5973\u4eba\uff0c\u4e3a\u4e86\u517b\u80b2\u5979\u7684\u5b69\u5b50\uff0c\u662f\u4ec0\u4e48\u4e8b\u90fd\u53ef\u4ee5\u727a\u7272\u7684\u3002 \u3000\u3000\u5728\u9752\u697c\u4e2d\uff0c\u5728\u706b\u5751\u91cc\uff0c\u4ece\u8fdc\u53e4\u76f4\u5230\u73b0\u5728\uff0c\u8fd9\u6837\u7684\u5973\u4eba\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u3002 \u3000\u3000\u82cf\u82cf\u7684\u773c\u6cea\u5df2\u7ecf\u5feb\u8981\u6389\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5979\u66f4\u4e0d\u61c2\uff0c\u5979\u4e0d\u61c2\u5c0f\u65b9\u4e3a\u4ec0\u4e48\u5728\u6b64\u65f6\u6b64\u523b\uff0c\u8981\u5728\u5979\u9762\u524d\u63d0\u8d77\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u672c\u6765\u662f\u4e00\u4e2a\u7537\u5b50\u6c49\u5b81\u6b7b\u4e5f\u4e0d\u613f\u5728\u522b\u4eba\u9762\u524d\u63d0\u8d77\u7684\u3002\u5c0f\u65b9\u63a5\u8bf4\u51fa\u6765\u7684\u4e00\u53e5\u8bdd\uff0c\u66f4\u8ba9\u5979\u5403\u60ca\u3002 \u3000\u3000\u201c\u4f46\u662f\u6211\u7684\u7236\u4eb2\u5e76\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8bf4\uff1a\u201c\u4e09\u5e74\u4e4b\u540e\u4ed6\u53c8\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u82cf\u82cf\u7684\u624b\u4e5f\u6293\u7d27\uff0c\u8fde\u6307\u7532\u90fd\u5df2\u523a\u5165\u8089\u91cc\u3002 \u3000\u3000\u201c\u4f60\u7236\u4eb2\u53c8\u56de\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5979\u7d27\u5f20\u75db\u82e6\u5f97\u8fde\u58f0\u97f3\u90fd\u5728\u98a4\u6296\uff1a\u201c\u4ed6\u77e5\u4e0d\u77e5\u9053\u4f60\u6bcd\u4eb2\u5728\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d \u3000\u3000\u82cf\u82cf\u7528\u529b\u54ac\u5634\u5507\uff0c\u201c\u4ed6\u600e\u4e48\u6837\u5bf9\u4f60\u7684\u6bcd\u4eb2\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u5f00\u5364\uff0c\u82cf\u82cf\u53c8\u62a2\u7740\u95ee\uff1a\u201c\u5982\u679c\u6211\u662f\u4ed6\uff0c\u5b9a\u4f1a\u5bf9\u4f60\u6bcd\u4eb2\u66f4\u5c0a\u656c\u66f4\u611f\u6fc0\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u58f0\u97f3\u51b0\u51b7\uff0c\u201c\u4f60\u4e0d\u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u2026\u2026\u96be\u9053\u4ed6\u4e0d\u8981\u4f60\u6bcd\u4eb2\u4e86\uff1f\u201d\u82cf\u82cf\u53c8\u95ee\u3002 \u3000\u3000\u5979\u95ee\u51fa\u6765\u4e4b\u540e\uff0c\u77e5\u9053\u8fd9\u95ee\u9898\u662f\u4e0d\u8be5\u95ee\u7684\uff0c\u770b\u5230\u5c0f\u65b9\u773c\u4e2d\u7684\u75db\u82e6\uff0c\u5979\u5df2\u7ecf\u5e94\u8be5\u77e5\u9053\u8fd9\u95ee\u9898\u7684\u7b54\u6848\u3002 \u3000\u3000\u2014\u2014\u2014\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u5b69\u5b50\uff0c\u4e00\u79cd\u4eba\u751f\uff0c\u4eba\u751f\u4e2d\u6709\u591a\u5c11\u8fd9\u79cd\u60b2\u5267\uff1f \u3000\u3000\u2014\u2014\u6709\u591a\u5c11\u4eba\u80fd\u4e86\u89e3\u8fd9\u79cd\u60b2\u5267\u4e2d\u6240\u5305\u542b\u7684\u90a3\u79cd\u65e0\u53ef\u5948\u4f55\u7684\u4eba\u751f\uff1f \u3000\u3000\u5c0f\u65b9\u53c8\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u7a97\u53e3\uff0c\u63a8\u5f00\u7a97\u6237\u3002\u7a97\u5916\u591c\u8272\u5df2\u6d53\u3002 \u3000\u3000\u9762\u5bf9\u7740\u661f\u6708\u4ecd\u672a\u5347\u8d77\u7684\u9ed1\u6697\u82cd\u7a79\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\u5c0f\u65b9\u624d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u6211\u544a\u8bc9\u4f60\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u56e0\u4e3a\u6211\u8981\u4f60\u77e5\u9053\uff0c\u6211\u6709\u4e2a\u8fd9\u4e48\u6837\u7684\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u201c\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u82cf\u82cf\u95ee\uff1a\u201c\u5979\u662f\u4e0d\u662f\u8fd8\u6d3b\u7740\uff1f\u201d \u3000\u3000\u201c\u5979\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8f7b\u8f7b\u4ed6\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6211\u8fd8\u5c0f\uff0c\u5979\u4e0d\u80fd\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u8f7b\u5982\u6cea\uff1a\u201c\u90a3\u65f6\u6211\u867d\u7136\u8fd8\u5c0f\uff0c\u53ef\u662f\u5df2\u7ecf\u77e5\u9053\u5979\u4e3a\u6211\u727a\u7272\u4e86\u4ec0\u4e48\uff0c\u6240\u4ee5\u6211\u544a\u8bc9\u5979\uff0c\u5982\u679c\u5979\u6b7b\uff0c\u6211\u4e5f\u6b7b\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u957f\u5927\u4e86\u3002\u201d \u3000\u3000\u82cf\u82cf\u53c8\u95ee\uff1a\u201c\u73b0\u5728\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u4e00\u4e2a\u6ca1\u6709\u4eba\u8ba4\u5f97\u5979\u3001\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u5f80\u4e8b\u7684\u5730\u65b9\uff0c\u5728\u4e00\u680b\u5c0f\u5c0f\u7684\u6728\u5c4b\u91cc\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8bf4\uff1a\u201c\u5979\u4e0d\u8ba9\u6211\u5e38\u53bb\u89c1\u5979\uff0c\u751a\u81f3\u4e0d\u8981\u522b\u4eba\u77e5\u9053\u5979\u662f\u6211\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u6cea\u5df2\u5c06\u6d41\u4e0b\uff0c\u5374\u672a\u6d41\u4e0b\uff0c\u53ea\u6709\u81f3\u6df1\u81f3\u5267\u7684\u75db\u82e6\u624d\u80fd\u4f7f\u4eba\u65e0\u6cea\u53ef\u6d41\u3002 \u3000\u3000\u201c\u5979\u90a3\u6728\u5c4b\u91cc\u53ea\u6709\u4e00\u5f20\u5e8a\u3001\u4e00\u5f20\u684c\u5b50\u3001\u51e0\u5f20\u6905\u5b50\u3001\u4e00\u4e2a\u8863\u67dc\u3001\u4e00\u76cf\u6cb9\u706f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8bf4\uff0c\u201c\u5979\u867d\u7136\u4e0d\u8ba9\u6211\u5e38\u53bb\uff0c\u6211\u8fd8\u662f\u5e38\u5e38\u53bb\uff0c\u5979\u90a3\u91cc\u7684\u6bcf\u6837\u4e1c\u897f\u6211\u90fd\u5f88\u719f\u6089\uff0c\u201d \u3000\u3000\u4ed6\u77aa\u7740\u773c\u775b\uff0c\u77aa\u7740\u9ed1\u6697\u7684\u82cd\u7a79\uff0c\u773c\u4e2d\u5ffd\u7136\u83b7\u5f97\u4e00\u7247\u7a7a\u767d\uff1a\u201c\u8fd9\u5c4b\u5b50\u91cc\u7684\u8fd9\u4e9b\u4e1c\u897f\uff0c\u5c31\u662f\u4ece\u5979\u90a3\u91cc\u642c\u6765\u7684\u3002\u201d \u3000\u3000\u82cf\u82cf\u7ec8\u4e8e\u660e\u767d\u5c0f\u65b9\u4e3a\u4ec0\u4e48\u4e00\u8d70\u8fdb\u5c4b\u5b50\u5c31\u53d8\u6210\u90a3\u6837\u5b50\u3002 \u3000\u3000\u2014\u2014\u8fd9\u5c4b\u91cc\u7684\u6bcf\u6837\u4e1c\u897f\uff0c\u90fd\u662f\u4ece\u4ed6\u6bcd\u4eb2\u90a3\u91cc\u642c\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u662f\u8c01\u642c\u6765\u7684\uff1f \u3000\u3000\u2014\u2014\u5f53\u7136\u662f\u5415\u4e09\u3002 \u3000\u3000\u2014\u2014\u5415\u4e09\u65e0\u7591\u5df2\u627e\u5230\u4e86\u4ed6\u7684\u6bcd\u4eb2\uff0c\u73b0\u5728\u5979\u65e0\u7591\u4e5f\u548c\u201c\u9633\u5149\u201d\u4e00\u6837\u843d\u5165\u4e86\u5415\u4e09\u7684\u638c\u63e1\u4e2d\u3002 \u3000\u3000\u82cf\u82cf\u770b\u770b\u5c0f\u65b9\uff0c\u5c0f\u65b9\u65e0\u6cea\uff0c\u82cf\u82cf\u6709\uff0c\u56e0\u4e3a\u5979\u5df2\u4e86\u89e3\u4ed6\u4eec\u6bcd\u5b50\u4e4b\u95f4\u7684\u611f\u60c5\u3002 \u3000\u3000\u201c\u6211\u5e26\u4f60\u53bb\u3002\u201d \u3000\u3000\u82cf\u82cf\u7ec8\u4e8e\u4e0b\u4e86\u51b3\u5fc3\uff0c\u201c\u6211\u5e26\u4f60\u53bb\u627e\u5415\u4e09\u3002\u201d \u3000\u3000\u5c31\u7b97\u5979\u660e\u77e5\u9053\u4ed6\u662f\u53bb\u9001\u6b7b\uff0c\u5979\u4e5f\u5e26\u4ed6\u53bb\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u4ed6\u5df2\u6ca1\u6709\u522b\u7684\u8def\u53ef\u8d70\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u6447\u5934\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u5fc5\u5e26\u6211\u53bb\uff0c\u4e0d\u5fc5\u966a\u6211\u9001\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e0d\u59a8\u544a\u8bc9\u6211\uff0c\u4ed6\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u82cf\u82cf\u4e5f\u6447\u5934\uff1a\u201c\u6211\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5979\u8bf4\uff1a\u201c\u6211\u53ef\u4e0d\u80fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u82cf\u82cf\u8bf4\uff1a\u201c\u6211\u53ea\u80fd\u5e26\u4f60\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u61c2\uff0c\u82cf\u82cf\u89e3\u91ca\uff1a\u201c\u4ed6\u662f\u4e2a\u8c1c\u4e00\u6837\u7684\u4eba\uff0c\u6bcf\u4e2a\u5e02\u9547\u4e61\u6751\u90fd\u6709\u4ed6\u843d\u811a\u5904\uff0c\u5374\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u4ed6\u843d\u811a\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u5979\u53c8\u8865\u5145\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ef\u662f\u6211\u80fd\u627e\u5f97\u5230\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4ec0\u4e48\u90fd\u6ca1\u6709\u518d\u95ee\uff0c\u4ed6\u5df2\u7ecf\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u5c31\u53bb\u627e\u3002\u201d \u3000\u3000\u82cf\u82cf\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u4eec\u8981\u627e\u5f88\u4e45\uff0c\u4ed6\u7684\u843d\u811a\u5904\u5b9e\u5728\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u53ea\u8981\u80fd\u627e\u5f97\u5230\uff0c\u4e0d\u7ba1\u8981\u627e\u591a\u4e45\u90fd\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u4eec\u627e\u4e86\u5f88\u4e45\uff0c\u5f88\u4e45\u5f88\u4e45\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u6709\u627e\u5230\u3002\u6ca1\u6709\u627e\u5230\u201c\u9633\u5149\u201d\uff0c\u6ca1\u6709\u627e\u5230\u8d75\u7fa4\uff0c\u4e5f\u6ca1\u6709\u627e\u5230\u5415\u4e09\u3002 \u3000\u3000\u7ea2\u6885\uff0c\u767d\u96ea\uff0c\u7eff\u7a97\u3002 \u3000\u3000\u98ce\u9e21\uff0c\u54b8\u9c7c\uff0c\u814a\u8089\u3002 \u3000\u3000\u5b69\u5b50\u7684\u65b0\u8863\uff0c\u7a77\u4eba\u7684\u503a\uff0c\u5c11\u5973\u4eec\u7684\u4e1d\u7ebf\uff0c\u8001\u5a46\u5a46\u7684\u538b\u5c81\u94b1\u3002 \u3000\u3000\u6025\u666f\u6b8b\u5e74\u3002 \u3000\u3000\u5feb\u8981\u8fc7\u5e74\u4e86\u3002 \u3000\u3000\u4e0d\u7ba1\u4f60\u662f\u6c49\u4eba\u3001\u662f\u82d7\u4eba\u3001\u662f\u85cf\u4eba\u3001\u8fd8\u662f\u8499\u4eba\uff0c\u4e0d\u7ba1\u4f60\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u8fc7\u5e74\u5c31\u662f\u8fc7\u5e74\uff0c\u56e0\u4e3a\u5927\u5bb6\u90fd\u662f\u5c5e\u4e8e\u540c\u4e00\u6c11\u65cf\u7684\u4eba\uff0c\u90fd\u662f\u9ec4\u5e1d\u7684\u5b50\u5b59\uff0c\u800c\u4e14\u90fd\u4ee5\u6b64\u4e3a\u8363\u3002 \u3000\u3000\u8fd9\u4e2a\u5730\u65b9\u7684\u4eba\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e2a\u5730\u65b9\u7684\u4eba\u4e5f\u8981\u8fc7\u5e74\uff0c\u4e0d\u7ba1\u4f60\u662f\u8d2b\u662f\u5bcc\u662f\u8001\u662f\u5c11\u662f\u7537\u662f\u5973\uff0c\u8fc7\u5e74\u5c31\u662f\u8fc7\u5e74\u3002 \u3000\u3000\u5e74\u5e74\u96be\u8fc7\u5e74\u5e74\u8fc7\uff0c\u6bcf\u4e2a\u4eba\u90fd\u8981\u8fc7\u5e74\uff0c\u5c0f\u65b9\u548c\u82cf\u82cf\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u627e\u8fc7\u5f88\u591a\u5730\u65b9\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u5230\u4e86\u8fd9\u91cc\uff0c\u73b0\u5728\u6b63\u662f\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u6240\u4ee5\u4ed6\u4eec\u7559\u5728\u8fd9\u91cc\u8fc7\u5e74\u3002 \u3000\u3000\u8d76\u7740\u56de\u5bb6\u8fc7\u5e74\u7684\u65c5\u5ba2\u5927\u591a\u5df2\u5230\u4e86\u5bb6\uff0c\u5ba2\u6808\u91cc\u7684\u5ba2\u623f\u95f4\u4e2d\u7a7a\u4e86\u4e5d\u95f4\uff0c\u63a8\u5f00\u7a97\u5b50\u671b\u51fa\u53bb\uff0c\u79ef\u96ea\u7684\u9662\u5b50\u91cc\u53ea\u5269\u4e0b\u4e00\u4e9b\u8f66\u8f99\u9a6c\u8e44\u7684\u8db3\u8ff9\u3002\u4e00\u5f20\u6cb9\u6f06\u5df2\u892a\u8272\u7684\u516b\u4ed9\u684c\u4e0a\uff0c\u6709\u4e00\u58f6\u9152\u548c\u5806\u5f97\u6ee1\u6ee1\u7684\u56db\u7897\u5e74\u83dc\uff0c\u662f\u5e97\u4e1c\u7279\u5730\u9001\u6765\u7684\uff0c\u83dc\u7897\u4e0a\u8fd8\u76d6\u7740\u5f20\u5199\u7740\u201c\u5409\u7965\u5982\u610f\uff0c\u606d\u559c\u53d1\u8d22\u201d\u7684\u7ea2\u7eb8\u3002 \u3000\u3000\u4eba\u95f4\u672c\u6765\u5c31\u5230\u5904\u6709\u6e29\u60c5\uff0c\u5c24\u5176\u662f\u5728\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4e50\u4e8e\u5c06\u81ea\u5df1\u7684\u798f\u6c14\u548c\u559c\u6c14\u5206\u4e00\u70b9\u7ed9\u90a3\u4e9b\u5b64\u72ec\u5bc2\u5bde\u4e0d\u5e78\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4e2d\u56fd\u4eba\u201c\u8fc7\u5e74\u201d\u7684\u7cbe\u795e\uff0c\u4e5f\u662f\u201c\u8fc7\u5e74\u201d\u7684\u6700\u5927\u610f\u4e49\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6240\u4ee5\u8fc7\u5e74\u7684\u4e60\u4fd7\u624d\u80fd\u6c38\u8fdc\u6d41\u4f20\u4e0b\u53bb\u3002 \u3000\u3000\u82cf\u82cf\u5df2\u6446\u597d\u4e24\u526f\u7897\u7b77\uff0c\u8fd8\u66ff\u5c0f\u65b9\u659f\u6ee1\u4e86\u4e00\u676f\u9152\u3002 \u3000\u3000\u5979\u662f\u4e2a\u597d\u5973\u4eba\uff0c\u5979\u5bf9\u5c0f\u65b9\u5df2\u505a\u5230\u4e86\u4e00\u4e2a\u5973\u4eba\u80fd\u5bf9\u7537\u4eba\u505a\u7684\u6bcf\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u7740\u5979\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u603b\u662f\u89c9\u5f97\u6709\u70b9\u9178\u9178\u7684\uff0c\u603b\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\u81ea\u5df1\uff1a\u201c\u6211\u4e3a\u5979\u505a\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u5929\u5979\u8eab\u5b50\u4eff\u4f5b\u5f88\u4e0d\u5b89\u9002\uff0c\u89c9\u7761\u4e0d\u7740\uff0c\u4e1c\u897f\u4e5f\u5403\u5f97\u4e0d\u591a\uff0c\u6709\u65f6\u8fd8\u4f1a\u80cc\u7740\u5c0f\u65b9\u6084\u6084\u5730\u53bb\u5455\u5410\u3002 \u3000\u3000\u5c0f\u65b9\u631f\u4e86\u4e2a\u86cb\u9ec4\u5230\u5979\u7897\u91cc\uff0c\u5979\u52c9\u5f3a\u5403\u4e0b\u53bb\uff0c\u7acb\u523b\u53c8\u5410\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5982\u679c\u5c0f\u65b9\u662f\u4e2a\u6709\u7ecf\u9a8c\u7684\u7537\u4eba\uff0c\u65e9\u5c31\u5e94\u8be5\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u53d8\u6210\u8fd9\u6837\u5b50\u4e86\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4e0d\u662f\uff0c\u6240\u4ee5\u4ed6\u95ee\u5979\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u75c5\u4e86\uff1f\u201d \u3000\u3000\u82cf\u82cf\u6447\u5934\uff0c\u4f46\u662f\u5979\u770b\u8d77\u6765\u7684\u786e\u50cf\u662f\u6709\u75c5\u7684\u6837\u5b50\uff0c\u6240\u4ee5\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6709\u70b9\u4e0d\u8212\u670d\uff1f\u4ec0\u4e48\u5730\u65b9\u4e0d\u8212\u670d\u5462\uff1f\u201d \u3000\u3000\u82cf\u82cf\u4f4e\u7740\u5934\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u5ffd\u7136\u8d77\u4e86\u9635\u7ea2\u6655\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u624d\u9f13\u8d77\u52c7\u6c14\u6765\u8bf4\uff1a\u201c\u6211\u597d\u50cf\u5df2\u7ecf\u6709\u4e86\u5b55\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6014\u4f4f\uff0c\u5b8c\u5168\u6014\u4f4f\u3002 \u3000\u3000\u82cf\u82cf\u6b63\u5728\u5077\u6109\u5730\u770b\u4ed6\uff0c\u770b\u5230\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u5979\u773c\u4e2d\u7acb\u523b\u5145\u6ee1\u75db\u82e6\u4e4b\u8272\uff0c\u7528\u529b\u54ac\u7740\u5634\u5507\uff0c\u50cf\u751f\u6015\u81ea\u5df1\u4f1a\u8bf4\u51fa\u4e0d\u8be5\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u4f46\u662f\u5979\u7ec8\u4e8e\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8bf4\u4e86\u51fa\u6765\uff1a \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u95ee\u6211\uff0c\u6211\u809a\u91cc\u7684\u5b69\u5b50\u662f\u4f60\u7684\u8fd8\u662f\u8d75\u7fa4\u7684\uff1f\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5df2\u56e0\u6fc0\u52a8\u800c\u98a4\u6296\uff1a\u201c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u5b69\u5b50\u662f\u4f60\u7684\uff0c\u56e0\u4e3a\u8d75\u7fa4\u4e0d\u4f1a\u6709\u5b69\u5b50\u3002\u201d \u3000\u3000\u5979\u5c3d\u529b\u63a7\u5236\u81ea\u5df1\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5728\u82b1\u4e0d\u62c9\u7684\u5546\u961f\u91cc\uff0c\u6211\u4eec\u4f4f\u5728\u4f60\u4eec\u9694\u58c1\u7684\u65f6\u5019\uff0c\u6211\u4eec\u6bcf\u5929\u665a\u4e0a\u90fd\u53d1\u51fa\u90a3\u4e9b\u58f0\u97f3\u6765\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u6211\u4eec\u559c\u6b22\u505a\u90a3\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u6545\u610f\u7684\u3002\u201d \u3000\u3000\u82cf\u82cf\u9053\uff1a\u201c\u6211\u4eec\u6545\u610f\u90a3\u4e48\u505a\uff0c\u522b\u4eba\u624d\u4e0d\u4f1a\u6000\u7591\u6211\u4eec\u5c31\u662f\u5415\u4e09\u8981\u8ffd\u6355\u7684\u4eba\uff0c\u6240\u4ee5\u522b\u4eba\u624d\u4f1a\u6000\u7591\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u5415\u4e09\u7684\u5c5e\u4e0b\u90fd\u662f\u8d75\u7fa4\u7684\u670b\u53cb\uff0c\u90fd\u77e5\u9053\u8d75\u7fa4\u6839\u672c\u4e0d\u80fd\u505a\u90a3\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u82cf\u82cf\u7684\u58f0\u97f3\u66f4\u75db\u82e6\uff1a\u201c\u56e0\u4e3a\u4ed6\u662f\u4e2a\u5929\u9609\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u6014\u4f4f\uff0c\u5b8c\u5168\u6014\u4f4f\u3002 \u3000\u3000\u201c\u522b\u4eba\u90fd\u5728\u5947\u602a\uff0c\u6211\u4e3a\u4ec0\u4e48\u4f1a\u559c\u6b22\u4e00\u4e2a\u6839\u672c\u4e0d\u662f\u7537\u4eba\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u82cf\u82cf\u773c\u4e2d\u5df2\u6709\u6cea\u5149\uff0c\u201c\u90a3\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u522b\u4eba\u90fd\u4e0d\u4e86\u89e3\u6211\u8ddf\u4ed6\u4e4b\u95f4\u7684\u611f\u60c5\u7f62\u4e86\u3002\u201d \u3000\u3000\u5979\u63a5\u7740\u9053\uff1a\u201c\u6211\u559c\u6b22\u4ed6\uff0c\u5c31\u56e0\u4e3a\u4ed6\u7684\u7f3a\u9677\uff0c\u5c31\u56e0\u4e3a\u4ed6\u662f\u6211\u8fd9\u4e00\u751f\u6240\u9047\u5230\u7684\u7537\u4eba\u4e2d\uff0c\u552f\u4e00\u4e0d\u662f\u56e0\u4e3a\u6211\u7684\u8eab\u4f53\u624d\u5bf9\u6211\u597d\u7684\u7537\u4eba\u201d \u3000\u3000\u2014\u2014\u5973\u4eba\u7684\u611f\u60c5\uff0c\u5973\u4eba\u7684\u5fc3\u4e8b\uff0c\u6709\u8c01\u80fd\u5b8c\u5168\u4e86\u89e3\uff1f \u3000\u3000\u5c0f\u65b9\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u82cf\u82cf\u76f4\u89c6\u7740\u4ed6\uff1a\u201c\u6211\u544a\u8bc9\u4f60\u8fd9\u4e9b\u4e8b\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u8981\u4f60\u627f\u8ba4\u8fd9\u5b69\u5b50\u662f\u4f60\u7684\uff0c\u4f60\u8fd8\u662f\u53ef\u4ee5\u4e0d\u8981\u4ed6\uff0c\u8fd8\u662f\u968f\u65f6\u90fd\u53ef\u4ee5\u8d70\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5f00\u59cb\u559d\u9152\uff0c\u4f4e\u7740\u5934\u559d\u9152\uff0c\u56e0\u4e3a\u4ed6\u5df2\u4e0d\u6562\u53bb\u770b\u5979\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u627f\u8ba4\u5b69\u5b50\u662f\u4ed6\u7684\uff0c\u4e5f\u4e0d\u4f1a\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u662f\u90a3\u79cd\u4e0d\u8d1f\u8d23\u4efb\u7684\u7537\u4eba\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u5bf9\u4ed6\u8fd9\u4e48\u6837\u4e00\u4e2a\u6ca1\u6709\u6839\u7684\u6d6a\u5b50\u6765\u8bf4\uff0c\u8fd9\u4ef6\u4e8b\u6765\u5f97\u5b9e\u5728\u592a\u7a81\u7136\uff0c\u7a81\u7136\u5f97\u4ee4\u4ed6\u5b8c\u5168\u65e0\u6cd5\u9002\u5e94\u3002 \u3000\u3000\u2014\u2014\u4ed6\u5c45\u7136\u6709\u4e86\u5b69\u5b50\uff0c\u8ddf\u4e00\u4e2a\u672c\u6765\u5c5e\u4e8e\u522b\u4eba\u7684\u5973\u4eba\u6709\u4e86\u5b69\u5b50\u3002 \u3000\u3000\u6709\u8c01\u80fd\u60f3\u5f97\u5230\u8fd9\u79cd\u4e8b\uff1f \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u4eec\u4ee5\u540e\u8fd8\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u82cf\u82cf\u64e6\u5e72\u773c\u6cea\uff0c\u4e3e\u8d77\u9152\u676f\uff1a\u201c\u6211\u656c\u4f60\u4e00\u676f\uff0c\u4f60\u559d\u4e0d\u559d\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u8981\u559d\u3002\u7b49\u5230\u4ed6\u5f00\u59cb\u60f3\u53bb\u627e\u7b2c\u4e8c\u58f6\u6765\u559d\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u77e5\u9053\u4eca\u5929\u8981\u9189\u4e86\u3002 \u3000\u3000\u4ed6\u771f\u7684\u9189\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5916\u9762\u5df2\u54cd\u8d77\u4e00\u4e32\u7206\u7af9\u58f0\u3002\u65e7\u7684\u4e00\u5e74\u5df2\u8fc7\u53bb\uff0c\u65b0\u7684\u4e00\u5e74\u5df2\u5f00\u59cb\u3002 \u3000\u3000\u5927\u5e74\u521d\u4e00\u3001\u6668\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u4e94\u7ae0\u3000\u6709\u5b50\u4e07\u4e8b\u8db3 \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u6709\u5b50\u4e07\u4e8b\u8db3 \u3000\u3000\u7a7f\u7740\u65b0\u8863\u7684\u5b69\u5b50\u5728\u96ea\u5730\u4e0a\u5954\u8dd1\u8df3\u8dc3\uff0c\u6ee1\u8033\u90fd\u662f\u201c\u606d\u559c\u53d1\u8d22\u201d\u58f0\u3002\u5356\u73a9\u5177\u7206\u7af9\u7684\u5c0f\u8d29\uff0c\u5df2\u7ecf\u6446\u8d77\u644a\u5b50\uff0c\u51c6\u5907\u8d5a\u5916\u5a46\u7ed9\u5b69\u5b50\u7684\u538b\u5c81\u94b1\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u7684\u5927\u5e74\u521d\u4e00\u662f\u4e2a\u5927\u6674\u5929\u3002 \u3000\u3000\u8fd9\u65f6\u5c0f\u65b9\u5df2\u7ecf\u5728\u8def\u4e0a\u901b\u4e86\u5f88\u4e45\uff0c\u773c\u4e2d\u7684\u7ea2\u4e1d\u5df2\u6d88\u892a\uff0c\u6628\u591c\u7684\u9189\u610f\u5df2\u6e10\u6e10\u6e05\u9192\u3002 \u3000\u3000\u8fd9\u91cc\u6ca1\u6709\u6768\u67f3\u5cb8\uff0c\u4e5f\u6ca1\u6709\u6653\u98ce\u6b8b\u6708\u3002 \u3000\u3000\u4ed6\u6e05\u9192\u65f6\uff0c\u53d1\u73b0\u81ea\u5df1\u7ad9\u5728\u4e00\u4e2a\u5356\u73a9\u5076\u7684\u644a\u5b50\u524d\u9762\uff0c\u770b\u7740\u4e00\u4e2a\u77ee\u77ee\u7626\u7626\u7684\u7238\u7238\uff0c\u5e26\u7740\u4e09\u4e2a\u77ee\u77ee\u80d6\u80d6\u7684\u5c0f\u5b69\u5b50\u5728\u4e70\u6ce5\u5a03\u5a03\u3002 \u3000\u3000\u770b\u89c1\u5b69\u5b50\u4eec\u8138\u4e0a\u7684\u6b22\u7b11\uff0c\u7ec8\u5e74\u7701\u5403\u4fed\u7528\u7684\u7236\u4eb2\u4e5f\u53d8\u5f97\u5927\u65b9\u8d77\u6765\uff0c\u7f3a\u4e4f\u8425\u517b\u7684\u7626\u8138\u4e0a\u4e5f\u9732\u51fa\u5b69\u5b50\u822c\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u201c\u6709\u4e8e\u4e07\u4e8b\u8db3\u201d\uff0c\u8fd9\u662f\u4e2d\u56fd\u4eba\u7684\u5927\u6027\uff0c\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u4e2d\u56fd\u4eba\u5c31\u80fd\u6c38\u8fdc\u5b58\u5728\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u773c\u775b\u6709\u70b9\u6e7f\u6e7f\u7684\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e5f\u6709\u4e86\u5b69\u5b50\uff0c\u4ed6\u4e5f\u50cf\u522b\u7684\u4eba\u4e00\u6837\u5feb\u505a\u7238\u7238\u4e86\u3002 \u3000\u3000\u521a\u542c\u5230\u8fd9\u6d88\u606f\u65f6\u7684\u60ca\u9707\u5df2\u8fc7\u53bb\uff0c\u73b0\u5728\u4ed6\u5df2\u6e10\u6e10\u80fd\u611f\u89c9\u5230\u8fd9\u662f\u4ef6\u591a\u4e48\u5947\u5999\u7684\u4e8b\u2014\u2014 \u3000\u3000\u4ed6\u611f\u89c9\u5230\u8fd9\u4e00\u70b9\uff0c\u522b\u7684\u4e8b\u5c31\u53d8\u5f97\u5b8c\u5168\u4e0d\u91cd\u8981\u3002 \u3000\u3000\u4ed6\u4e5f\u4e70\u4e86\u4e2a\u6ce5\u5a03\u5a03\uff0c\u7a7f\u7740\u7ea2\u8863\u670d\uff0c\u7b11\u5f97\u50cf\u5f25\u9640\u4f5b\u4e00\u6837\u7684\u5a03\u5a03\u3002 \u3000\u3000\u7b49\u4ed6\u60f3\u5230\u5b69\u5b50\u8fd8\u6ca1\u6709\u51fa\u751f\uff0c\u8fd8\u4e0d\u77e5\u8981\u8fc7\u591a\u4e45\u624d\u80fd\u73a9\u8fd9\u6ce5\u5a03\u5a03\uff0c\u4ed6\u81ea\u5df1\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u56de\u53bb\u544a\u8bc9\u82cf\u82cf\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4ed6\u90fd\u4f1a\u597d\u597d\u7167\u987e\u5979\u548c\u4ed6\u4eec\u7684\u5b69\u5b50\u3002 \u3000\u3000\u4e00\u4e00\u5b69\u4e8e\u4e00\u5b9a\u8981\u751f\u4e0b\u6765\uff0c\u751f\u547d\u5fc5\u987b\u5ef6\u7eed\uff0c\u4eba\u7c7b\u5fc5\u80fd\u6c38\u5b58\u3002 \u3000\u3000\u8d70\u5728\u56de\u53bb\u7684\u8def\u4e0a\uff0c\u624b\u91cc\u6367\u7740\u65b0\u4e70\u6765\u7684\u6ce5\u5a03\u5a03\uff0c\u5c0f\u65b9\u53ea\u89c9\u5f97\u81ea\u5df1\u7684\u5fc3\u60c5\u4ece\u672a\u5982\u6b64\u5f00\u6717\u8fc7\uff0c\u4f46\u662f\u7b49\u4ed6\u56de\u5230\u90a3\u5ba2\u6808\u7684\u5c0f\u5c4b\u65f6\uff0c\u82cf\u82cf\u5df2\u7ecf\u4e0d\u5728\u4e86\u3002 \u3000\u3000\u5c4b\u91cc\u4e00\u7247\u51cc\u4e71\uff0c\u9152\u58f6\u83dc\u7897\u90fd\u5df2\u88ab\u6454\u5f97\u7834\u788e\uff0c\u788e\u7247\u548c\u5269\u83dc\u56db\u4e0b\u98de\u6e85\uff0c\u7ea2\u70e7\u8089\u7684\u5364\u6c41\u6e85\u5728\u7c89\u5899\u4e0a\uff0c\u5c31\u50cf\u662f\u521a\u5e72\u900f\u7684\u9c9c\u8840\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u91cc\u4e5f\u5728\u6ef4\u8840\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u8fd8\u5728\u7d27\u7d27\u6367\u7740\u90a3\u4e2a\u6ce5\u5a03\u5a03\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u6bcd\u4eb2\u5728\u62b1\u7740\u81ea\u5df1\u7684\u521d\u751f\u5a74\u513f\u3002 \u3000\u3000\u201c\u535c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4ed6\u624b\u91cc\u7684\u6ce5\u5a03\u5a03\u4e5f\u788e\u4e86\u3002 \u3000\u3000\u5e0c\u671b\u3001\u7406\u60f3\u3001\u610f\u5fd7\uff0c\u6240\u6709\u7684\u4e00\u5207\uff0c\u4e5f\u90fd\u50cf\u8fd9\u6ce5\u5a03\u5a03\u4e00\u6837\u788e\u4e86\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u65b9\u5e94\u8be5\u600e\u4e48\u529e\uff1f \u3000\u3000\u53bb\u627e\u5415\u4e09\uff1f\u5230\u54ea\u91cc\u53bb\u627e\uff1f \u3000\u3000\u4ed6\u7684\u6bcd\u4eb2\u3001\u4ed6\u7684\u670b\u53cb\u3001\u4ed6\u7684\u60c5\u4eba\u3001\u4ed6\u7684\u5b69\u5b50\uff0c\u73b0\u5728\u90fd\u5df2\u843d\u5165\u5415\u4e09\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u5c31\u7b97\u627e\u5230\u5415\u4e09\u53c8\u80fd\u600e\u6837\uff1f \u3000\u3000\u5c0f\u65b9\u6162\u6162\u5730\u3001\u6162\u6162\u5730\u5750\u4e86\u4e0b\u53bb\uff0c\u5c31\u5750\u5728\u4ed6\u672c\u6765\u7ad9\u7740\u7684\u90a3\u5757\u5730\u4e0a\uff0c\u5c31\u5750\u5728\u90a3\u7897\u8089\u7684\u6b8b\u6c41\u548c\u7834\u7897\u7684\u788e\u7247\u4e0a\u3002 \u3000\u3000\u5200\u950b\u822c\u7684\u788e\u7247\u523a\u5165\u4e86\u4ed6\u7684\u8089\u3002 \u3000\u3000\u4ed6\u5b8c\u5168\u6ca1\u6709\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u4e24\u6761\u817f\u5ffd\u7136\u53d8\u5f97\u5f88\u8f6f\u5f88\u8f6f\uff0c\u817f\u91cc\u7684\u8840\u8089\u7cbe\u6c14\u529b\u91cf\u90fd\u597d\u50cf\u4e00\u4e0b\u5b50\u5c31\u88ab\u62bd\u7a7a\u4e86\uff0c\u597d\u50cf\u6c38\u8fdc\u518d\u4e5f\u6ca1\u6cd5\u5b50\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u542c\u89c1\u90a3\u597d\u5fc3\u7684\u5e97\u4e3b\u5728\u7a97\u5916\u5411\u4ed6\u62dc\u5e74\uff0c\u795d\u4ed6\uff1a\uff1a\u2018\u5e74\u5e74\u5e73\u5b89\uff0c\u4e8b\u4e8b\u5982\u610f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\uff0c\u5c31\u50cf\u4e00\u4e2a\u767d\u75f4\u4e00\u6837\u7b11\u4e86\u8d77\u6765\u3002\u5e97\u4e1c\u5374\u5df2\u7b11\u4e0d\u51fa\uff0c\u770b\u89c1\u4e86\u8fd9\u5c4b\u91cc\u7684\u60c5\u51b5\uff0c\u770b\u89c1\u4e86\u4ed6\u7684\u8fd9\u79cd\u6837\u5b50\uff0c\u8fd8\u6709\u8c01\u80fd\u7b11\u5f97\u51fa\uff1f\u4ed6\u597d\u50cf\u8fd8\u5bf9\u5c0f\u65b9\u8bf4\u4e86\u4e9b\u5b89\u6170\u529d\u89e3\u7684\u8bdd\uff0c\u53ef\u662f\u5c0f\u65b9\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u5c0f\u65b9\u6b63\u5728\u5bf9\u81ea\u5df1\u8bf4\uff0c\u4e00\u76f4\u4e0d\u505c\u5730\u544a\u8bc9\u81ea\u5df1\uff1a \u3000\u3000\u2014\u2014\u4e00\u5b9a\u8981\u4fdd\u6301\u6e05\u9192\uff0c\u4e00\u5b9a\u8981\u5fcd\u8010\u3002 \u3000\u3000\u53ef\u662f\u4e0d\u77e5\u9053\u4ece\u4ec0\u4e48\u65f6\u5019\u5f00\u59cb\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u5df2\u7ecf\u5728\u559d\u9152\uff0c\u4e00\u76f4\u4e0d\u505c\u5730\u559d\u3002 \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u5df2\u7ecf\u5b8c\u5168\u88ab\u6467\u6bc1\u4e86\u7684\u4eba\uff0c\u624d\u77e5\u9053\u201c\u6e05\u9192\u201d\u662f\u79cd\u591a\u4e48\u53ef\u6015\u7684\u75db\u82e6\u3002\u4ed6\u77e5\u9053\u559d\u9152\u7edd\u5bf9\u4e0d\u80fd\u89e3\u51b3\u4efb\u4f55\u95ee\u9898\uff0c\u4e5f\u4e0d\u80fd\u89e3\u9664\u4ed6\u7684\u75db\u82e6\u3002 \u3000\u3000\u53ef\u662f\u6e05\u9192\u65f6\u4ed6\u66f4\u662f\u75db\u82e6\uff0c\u75db\u82e6\u5f97\u968f\u65f6\u90fd\u4f1a\u53d1\u75af\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u4e0d\u613f\u9003\u907f\uff0c\u65e0\u8bba\u906d\u9047\u5230\u591a\u5927\u7684\u6253\u51fb\uff0c\u90fd\u4e0d\u613f\u9003\u907f\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5df2\u65e0\u8def\u53ef\u8d70\u3002 \u3000\u3000\u2014\u2014\u9189\u4e61\u8def\u9690\u5b9c\u9891\u5230\uff0c\u6b64\u5916\u4e0d\u582a\u884c\u3002 \u3000\u3000\u6b64\u5916\u9189\u4e86\u53c8\u9189\uff0c\u9189\u4e86\u53c8\u9189\uff0c\u76f4\u5230\u4ed6\u559d\u5f97\u70c2\u9189\u5982\u6ce5\uff0c\u65e0\u94b1\u4ed8\u8d26\uff0c\u88ab\u4e00\u5bb6\u5c0f\u9152\u5e97\u7684\u7c97\u66b4\u4e3b\u4eba\u6253\u65ad\u4e86\u4e24\u6839\u808b\u9aa8\uff0c\u8e22\u8fdb\u4e00\u6761\u9634\u6c9f\u7684\u65f6\u5019\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u9192\u6765\u65f6\u5e76\u4e0d\u5728\u9634\u6c9f\u91cc\u3002 \u3000\u3000\u5c0f\u65b9\u9192\u6765\u65f6\u5df2\u7ecf\u8eba\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u5bbd\u5927\u67d4\u8f6f\u8212\u670d\u7684\u5e8a\uff0c\u5d2d\u65b0\u7684\u5e72\u51c0\u88ab\u5355\uff0c\u5149\u6ed1\u5982\u5c11\u5973\u76ae\u80a4\u822c\u7684\u4e1d\u88ab\u3002 \u3000\u3000\u4e00\u4e2a\u76ae\u80a4\u5149\u6ed1\u5982\u4e1d\u68c9\u7684\u5c11\u5973\uff0c\u6b63\u8eba\u5728\u4ed6\u7684\u8eab\u65c1\uff0c\u7528\u4e00\u4e2a\u5973\u4eba\u80fd\u591f\u6311\u9017\u7537\u4eba\u7684\u6240\u6709\u7684\u65b9\u6cd5\u6311\u9017\u4ed6\u3002 \u3000\u3000\u5bbf\u9152\u5c06\u9192\u672a\u9192\uff0c\u6b63\u662f\u60c5\u6b32\u6700\u4ea2\u594b\u7684\u65f6\u5019\uff0c\u4ec0\u4e48\u4eba\u80fd\u5fcd\u53d7\u8fd9\u79cd\u6311\u9017\uff1f \u3000\u3000\u5c0f\u65b9\u662f\u4eba\uff0c\u5c0f\u65b9\u4e5f\u4e0d\u80fd\u5fcd\u53d7\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u505a\u51fa\u8fde\u81ea\u5df1\u90fd\u4e0d\u80fd\u539f\u8c05\u81ea\u5df1\u7684\u4e8b\uff0c\u4ed6\u751a\u81f3\u8fde\u8fd9\u4e2a\u5973\u4eba\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u521a\u5f00\u505a\u4e86\u6ca1\u591a\u4e45\uff0c\u5c31\u5df2\u7ecf\u5f00\u59cb\u5455\u5410\u4e86\u3002 \u3000\u3000\u7b49\u4ed6\u5410\u5b8c\u4e86\uff0c\u4ed6\u624d\u60f3\u5230\u5e94\u8be5\u95ee\u5979\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u600e\u4e48\u4f1a\u7761\u5728\u6211\u65c1\u8fb9\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u6587\u96c0\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5973\u4eba\u5e76\u4e0d\u5728\u4e4e\u4ed6\u5455\u5410\uff0c\u6001\u5ea6\u4ecd\u7136\u540c\u6837\u7f20\u7ef5\u6e29\u67d4\uff0c\u201c\u662f\u4f60\u7684\u670b\u53cb\u8981\u6211\u6765\u966a\u4f60\u7684\u3002\u201d \u3000\u3000\u2014\u2014\u4ed6\u7684\u670b\u53cb\uff1f \u3000\u3000\u2014\u2014\u73b0\u5728\u4ed6\u8fd8\u6709\u670b\u53cb\uff1f \u3000\u3000\u201c\u6211\u90a3\u670b\u53cb\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u5415\u4e09\u7237\u3002\u201d \u3000\u3000\u5c0f\u65b9\u51e0\u4e4e\u53c8\u5fcd\u4e0d\u4f4f\u8981\u5f00\u59cb\u5455\u5410\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5410\uff0c\u53ea\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6ca1\u6709\u4e1c\u897f\u53ef\u5410\u3002 \u3000\u3000\u6587\u96c0\u53c8\u5f00\u59cb\u5979\u7684\u52a8\u4f5c\uff0c\u53ea\u6709\u4e00\u4e2a\u8001\u7ec3\u7684\u5993\u5973\u624d\u80fd\u505a\u5f97\u51fa\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u662f\u6211\u7684\u5730\u65b9\u3002\u201d \u7b2c\u4e8c\u516d\u7ae0\u3000\u795e\u9c7c \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u795e\u9c7c \u3000\u3000\u73b0\u5728\u5e74\u867d\u7136\u5df2\u7ecf\u8fc7\u4e86\uff0c\u5143\u5bb5\u8fd8\u6ca1\u6709\u8fc7\uff0c\u8857\u4e0a\u8fd8\u662f\u5145\u6ee1\u4e86\u8fc7\u5e74\u7684\u6c14\u8c61\uff0c\u70ed\u70ed\u95f9\u95f9\uff0c\u9ad8\u9ad8\u5174\u5174\u7684\uff0c\u4e0d\u7ba1\u6709\u94b1\u6ca1\u94b1\u7684\u4eba\u90fd\u4e00\u6837\uff0c\u8fd9\u4e16\u754c\u4e0a\u597d\u50cf\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u4f18\u6101\u70e6\u607c\u75db\u82e6\u5b58\u5728\u3002 \u3000\u3000\u2014\u2014\u5c0f\u65b9\u5462\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u4f60\u662f\u5c0f\u65b9\uff0c\u4f60\u6b63\u7ad9\u5728\u8fd9\u4e2a\u7a97\u53e3\uff0c\u7ad9\u5728\u4e00\u4e2a\u628a\u4f60\u6bcd\u4eb2\u3001\u670b\u53cb\u3001\u60c5\u4eba\u3001\u5b69\u5b50\u548c\u540d\u8a89\u90fd\u593a\u8d70\u4e86\u7684\u4ec7\u4eba\u8eab\u65c1\uff0c\u770b\u7740\u8fd9\u6761\u70ed\u70ed\u95f9\u95f9\u7684\u8857\u9053\uff0c\u770b\u7740\u8fd9\u4e9b\u9ad8\u9ad8\u5174\u5174\u7684\u4eba\uff0c\u4f60\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\u7684\u3002\u201d\u201c\u9ebb\u96c0\u201d\u5ffd\u7136\u8bf4\u3002 \u3000\u3000\u4ed6\u6307\u7740\u6447\u94c3\u7684\u8d27\u90ce\u3001\u7cd5\u997c\u5e97\u7684\u5e74\u9752\u4f19\u8ba1\u3001\u5e74\u8d27\u5e97\u91cc\u6253\u778c\u7761\u7684\u638c\u67dc\u548c\u7ef8\u7f0e\u5e97\u91cc\u653e\u7206\u7af9\u7684\u638c\u67dc\uff0c\u5356\u82b1\u7684\u8001\u5934\u5b50\u548c\u5c0f\u4f19\u5b50\uff0c\u9152\u94fa\u95e8\u5916\u7684\u9189\u6c49\u548c\u4e5e\u4e10\uff0c\u9001\u8d22\u795e\u548c\u821e\u72ee\u7684\u5927\u6c49\uff0c\u4ee5\u53ca\u90a3\u4e9b\u7ad9\u5728\u8857\u89d2\u770b\u7740\u5973\u4eba\u4eec\u54c1\u5934\u8bba\u8db3\u7684\u5e74\u9752\u4eba\u3002 \u3000\u3000\u201c\u9ebb\u96c0\u201d\u6307\u7740\u8fd9\u4e9b\u4eba\u5bf9\u5415\u4e09\u8bf4\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6211\u5b89\u6392\u5728\u8fd9\u91cc\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\uff1f\u201d \u3000\u3000\u201c\u6bcf\u4e00\u4e2a\u90fd\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u4e00\u5171\u5b89\u6392\u4e86\u591a\u5c11\u4eba\uff1f\u201d\u5415\u4e09\u95ee\u3002 \u3000\u3000\u201c\u672c\u6765\u5e94\u8be5\u662f\u56db\u5341\u516b\u4e2a\u3002\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u56de\u7b54\uff0c\u201c\u53ef\u662f\u73b0\u5728\u6211\u53ea\u770b\u89c1\u56db\u5341\u4e03\u4e2a\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e2a\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u9053\uff0c\u201c\u53ef\u662f\u6211\u4e00\u5b9a\u4f1a\u67e5\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u53c8\u8bf4\uff1a\u201c\u67e5\u51fa\u6765\u4e4b\u540e\uff0c\u4eca\u5929\u6ca1\u6709\u6765\u7684\u90a3\u4e2a\u4eba\u4ee5\u540e\u5c31\u4ec0\u4e48\u5730\u65b9\u90fd\u4e0d\u5fc5\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u4e00\u4e2a\u6b7b\u4eba\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u53ef\u53bb\uff1f \u3000\u3000\u5415\u4e09\u53c8\u5728\u95ee\u201c\u9ebb\u96c0\u201d\uff1a\u201c\u4f60\u5b89\u6392\u7684\u8fd9\u4e9b\u4eba\uff0c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ebb\u96c0\u4e00\u53e3\u6c14\u5c31\u8bf4\u51fa\u4e86\u56db\u5341\u516b\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u5176\u4e2d\u81f3\u5c11\u6709\u4e09\u5341\u591a\u4e2a\u4eba\u7684\u540d\u5b57\u662f\u5c0f\u65b9\u4ee5\u524d\u5c31\u542c\u4eba\u8bf4\u8fc7\u7684\uff0c\u6bcf\u4e2a\u4eba\u7684\u540d\u5b57\u90fd\u53ef\u4ee5\u8ba9\u4eba\u5403\u4e00\u60ca\u3002 \u3000\u3000\u2014\u2014\u53ea\u6709\u4f1a\u6740\u4eba\u800c\u4e14\u6740\u8fc7\u4e0d\u5c11\u4eba\u7684\u4eba\uff0c\u540d\u5b57\u8bf4\u51fa\u6765\u624d\u80fd\u8ba9\u4eba\u5403\u60ca\u3002 \u3000\u3000\u5415\u4e09\u5374\u8fd8\u662f\u8981\u95ee\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fd9\u4e9b\u4eba\u5df2\u7ecf\u591f\u4e86\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u8bf4\uff1a\u201c\u53ea\u8981\u6211\u4e00\u58f0\u4ee4\u4e0b\uff0c\u4ed6\u4eec\u5728\u6211\u6570\u5230\u2018\u4e8c\u5341\uff0c\u7684\u65f6\u5019\uff0c\u5c31\u53ef\u4ee5\u5c06\u8fd9\u6761\u8857\u4e0a\u6240\u6709\u7684\u7537\u5973\u8001\u5c11\u755c\u7272\u732b\u72d7\u5168\u90fd\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u201d \u3000\u3000\u5415\u4e09\u7528\u4e00\u79cd\u5f88\u660e\u663e\u662f\u6545\u610f\u5236\u4f5c\u51fa\u7684\u60ca\u6115\u4e4b\u6001\u770b\u7740\u201c\u9ebb\u96c0\u201d\uff0c\u6545\u610f\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u6761\u8857\u4e0a\u6709\u591a\u5c11\u4eba\uff1f\u201d\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u201c\u9ebb\u96c0\u201d\u8138\u4e0a\u4ecd\u7136\u5e26\u7740\u79cd\u597d\u50cf\u88ab\u70e4\u7126\u4e86\u7684\u8868\u60c5\uff0c\u201c\u6211\u53ea\u77e5\u9053\u968f\u4fbf\u6709\u591a\u5c11\u4eba\u90fd\u4e00\u6837\u3002\u201d\u201c\u8fd8\u6709\u522b\u7684\u4eba\u518d\u6765\u4e5f\u4e00\u6837\uff1f\u201d\u201c\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u56de\u7b54\uff0c\u201c\u800c\u4e14\u4e0d\u7ba1\u6765\u7684\u662f\u4ec0\u4e48\u4eba\u90fd\u4e00\u6837\uff0c\u5c31\u7b97\u535c\u9e70\u548c\u73ed\u5bdf\u5df4\u90a3\u6765\u4e86\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4f60\u53ea\u8981\u6570\u5230\u2018\u4e8c\u5341\u2019\u5c31\u53ef\u4ee5\u628a\u4ed6\u4eec\u5168\u90fd\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\uff1f\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u4f60\u6570\u5f97\u5feb\u4e0d\u5feb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5feb\u3002\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u9053\uff0c\u201c\u53ef\u662f\u4e5f\u5e76\u4e0d\u592a\u6162\u3002\u201d \u3000\u3000\u5415\u4e09\u7b11\u4e86\uff0c\u5fae\u7b11\u7740\u6447\u5934\uff1a\u201c\u6709\u8c01\u4f1a\u76f8\u4fe1\u4f60\u8bf4\u7684\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u51b7\u7b11\u53cd\u95ee\uff1a\u201c\u6709\u8c01\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u6709\u4eba\u4e0d\u4fe1\uff0c\u4f60\u662f\u4e0d\u662f\u968f\u65f6\u90fd\u53ef\u4ee5\u505a\u51fa\u6765\u7ed9\u4ed6\u770b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u56de\u7b54\u9053\uff0c\u201c\u968f\u65f6\u90fd\u53ef\u4ee5\u3002\u201d \u3000\u3000\u5415\u4e09\u53c8\u7b11\u4e86\uff0c\u5fae\u7b11\u7740\u56de\u8fc7\u5934\uff0c\u51dd\u89c6\u7740\u5c0f\u65b9\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u95ee\u4ed6\u9053\uff1a\u201c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u95ed\u7740\u5634\u3002 \u3000\u3000\u4ed6\u5634\u5e72\u5507\u88c2\uff0c\u6307\u5c16\u51b0\u51b7\uff0c\u4ed6\u4e0d\u80fd\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u4e5f\u4e0d\u6562\u56de\u7b54\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u65e0\u8bba\u4ed6\u7684\u7b54\u6848\u662f\u201c\u76f8\u4fe1\u201d\u8fd8\u662f\u201c\u4e0d\u4fe1\u201d\uff0c\u540e\u679c\u90fd\u540c\u6837\u53ef\u6015\u3002 \u3000\u3000\u5415\u4e09\u9759\u9759\u5730\u770b\u7740\u5c0f\u65b9\uff0c\u9759\u9759\u5730\u7b49\u4e86\u5f88\u4e45\u624d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u5176\u5b9e\u4f60\u6839\u672c\u7528\u4e0d\u7740\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u6211\u6839\u672c\u4e5f\u7528\u4e0d\u7740\u95ee\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5f97\u5c31\u50cf\u662f\u53ea\u5df2\u7ecf\u5c06\u72e1\u5154\u6293\u4f4f\u4e86\u7684\u72d0\u72f8\uff0c\u201c\u6211\u8fd9\u4e48\u6837\u95ee\u4f60\uff0c\u53ea\u4e0d\u8fc7\u8981\u8ba9\u4f60\u77e5\u9053\uff0c\u4f60\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u673a\u4f1a\u3001\u5b8c\u5168\u6ca1\u6709\u5e0c\u671b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u5ffd\u7136\u6d88\u901d\uff0c\u773c\u8272\u5ffd\u7136\u53d8\u5f97\u51b7\u9177\u5982\u72fc\uff1a\u201c\u5176\u5b9e\u6211\u771f\u6b63\u60f3\u95ee\u4f60\u7684\u662f\u53e6\u5916\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u535c\u9e70\u628a\u90a3\u6279\u9ec4\u91d1\u85cf\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5415\u4e09\u95ee\uff0c\u201c\u5c31\u662f\u4ed6\u6700\u540e\u4e00\u6b21\u4ece\u94c1\u7ffc\u624b\u91cc\u52ab\u8d70\u7684\u90a3\u4e00\u6279\uff1f\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5c0f\u65b9\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u77e5\u9053\u8fd9\u79d8\u5bc6\uff0c\u9664\u4e86\u535c\u9e70\u81ea\u5df1\u548c\u73ed\u5bdf\u5df4\u90a3\u5916\uff0c\u53ea\u6709\u4f60\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u95ee\u9898\u5c0f\u65b9\u66f4\u4e0d\u80fd\u56de\u7b54\uff0c\u6b7b\u4e5f\u4e0d\u80fd\uff0c\u4f46\u662f\u4ed6\u5374\u5ffd\u7136\u53cd\u95ee\uff1a\u201c\u5982\u679c\u6211\u80af\u8bf4\u51fa\u6765\uff0c\u4f60\u662f\u4e0d\u662f\u5c31\u80af\u653e\u4e86\u6211\uff0c\u800c\u4e14\u653e\u8fc7\u6211\u7684\u6bcd\u4eb2\u548c\u5b69\u5b50\uff1f\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u8003\u8651\u3002\u201d\u5415\u4e09\u9053\u3002 \u3000\u3000\u201c\u6211\u4e5f\u53ef\u4ee5\u7b49\uff0c\u7b49\u4f60\u51b3\u5b9a\u4e4b\u540e\u518d\u8bf4\u3002\u201d\u5c0f\u65b9\u9053\u3002 \u3000\u3000\u5415\u4e09\u76ee\u5149\u95ea\u52a8\uff1a\u201c\u5982\u679c\u6211\u80af\u5462\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u80af\uff0c\u6211\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u660e\u767d\u4f60\u8d39\u4e86\u8fd9\u4e48\u591a\u5fc3\u673a\uff0c\u8fd9\u4e48\u6837\u5bf9\u6211\uff0c\u539f\u6765\u5e76\u4e0d\u662f\u4e3a\u4e86\u62a5\u590d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u6837\u505a\uff0c\u539f\u6765\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8981\u903c\u6211\u8bf4\u51fa\u90a3\u6279\u9ec4\u91d1\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u5415\u4e09\u5c45\u7136\u6ca1\u6709\u5426\u8ba4\uff0c\u73b0\u5728\u4ed6\u5df2\u4e0d\u5fc5\u5426\u8ba4\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u53c8\u8bf4\u51fa\u53e5\u5f88\u5947\u602a\u7684\u8bdd\uff1a \u3000\u3000\u201c\u65e2\u7136\u4f60\u4e0d\u5426\u8ba4\uff0c\u6211\u53c8\u4e0d\u660e\u767d\u4e86\u3002\u201d\u201c\u4ec0\u4e48\u4e8b\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u660e\u767d\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff0c\u201c\u5bf9\u4f60\u6765\u8bf4\uff0c\u4e09\u5341\u4e07\u9ec4\u91d1\u5e76\u4e0d\u80fd\u7b97\u5927\u591a\uff0c\u4f60\u4e3a\u5b83\u4ed8\u51fa\u4ee3\u4ef7\u5374\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u6211\u4e0d\u60f3\u7792\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u8981\u6211\u8bf4\u771f\u8bdd\uff0c\u6700\u597d\u5c31\u4e0d\u8981\u7792\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\u6211\u6765\u8bf4\uff0c\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u7684\u786e\u4e0d\u80fd\u7b97\u5927\u591a\u3002\u201d \u3000\u3000\u5415\u4e09\u9053\uff0c\u201c\u6211\u8fd9\u4e48\u505a\uff0c\u7684\u786e\u4e0d\u662f\u4e3a\u4e86\u8fd9\u6279\u9ec4\u91d1\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u662f\u4e3a\u4e86\u4e00\u6761\u9c7c\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\u9053\uff0c\u201c\u4e00\u6761\u91d1\u9c7c\u3002\u201d \u3000\u3000\u201c\u91d1\u9c7c\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u60ca\u8bb6\u7edd\u4e0d\u662f\u6545\u610f\u88c5\u51fa\u6765\u7684\uff0c\u201c\u4f60\u8d39\u4e86\u8fd9\u4e48\u5927\u7684\u82e6\u5fc3\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u4e00\u6761\u91d1\u9c7c\uff1f\u201d \u3000\u3000\u5415\u4e09\u4e0d\u518d\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u5374\u5ffd\u7136\u53cd\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u85cf\u8fb9\u6709\u4e2a\u57ce\u5e02\u53eb\u2018\u5676\u5c14\u6e21\u2019\uff1f\u4f60\u6709\u6ca1\u6709\u5230\u90a3\u91cc\u53bb\u8fc7\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u53bb\u8fc7\uff0c\u4f46\u662f\u4ed6\u77e5\u9053\u3002 \u3000\u3000\u201c\u5676\u5c14\u6e21\u201d\u5728\u5929\u7afa\u5723\u6cb3\u4e0a\u6e90\u8c61\u6cc9\u6cb3\u897f\u5c3d\u5934\uff0c\u5730\u52bf\u6781\u9ad8\uff0c\u5165\u51ac\u540e\u5947\u5bd2\u5f7b\u9aa8\u3001\u51b0\u96ea\u5c01\u6237\uff0c\u5165\u590f\u5219\u4e07\u5546\u4e91\u96c6\u3002 \u3000\u3000\u5415\u4e09\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5c31\u5728\u9760\u8fd1\u90a3\u5730\u65b9\u7684\u8c61\u6cc9\u6cb3\u91cc\uff0c\u6709\u4e00\u79cd\u9c7c\uff1f\u201d \u3000\u3000\u5415\u4e09\u9053\uff1a\u201c\u662f\u4e00\u79cd\u91d1\u8272\u7684\u9cde\u9c7c\uff0c\u6709\u9cde\u6709\u9aa8\u6709\u8840\u6709\u8089\uff0c\u672c\u6765\u662f\u53ef\u5403\u7684\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u7ecf\u6ca1\u6709\u4eba\u6562\u5403\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u73b0\u5728\u4eba\u4eec\u5df2\u7ecf\u5c06\u90a3\u79cd\u9c7c\u770b\u6210\u795e\u9c7c\uff0c\u5403\u4e86\u5fc5\u906d\u6a2a\u7978\u3002\u201d \u3000\u3000\u5415\u4e09\u9053\uff0c\u201c\u6240\u4ee5\u73b0\u5728\u5df2\u7ecf\u6ca1\u6709\u4eba\u6562\u5403\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u79cd\u9c7c\u548c\u4f60\u90a3\u6279\u9ec4\u91d1\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u4e00\u70b9\u3002\u201d \u3000\u3000\u5415\u4e09\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u5947\u5f02\u800c\u70bd\u70ed\u7684\u8868\u60c5\uff0c\u201c\u90a3\u6279\u9ec4\u91d1\u4e2d\uff0c\u5c31\u6709\u4e00\u6761\u662f\u5676\u5c14\u6e21\u91d1\u9c7c\u3002\u201d\u4ed6\u7684\u773c\u8272\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u521d\u604b\u4e2d\u7684\u5c11\u5973\uff0c\u751a\u81f3\u8fde\u547c\u5438\u90fd\u5df2\u56e0\u5174\u594b\u70ed\u60c5\u800c\u53d8\u7c97\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u95ee\u4ed6\u9ec4\u91d1\u91cc\u600e\u4e48\u4f1a\u6709\u9c7c\uff0c\u9c7c\u600e\u4e48\u80fd\u5728\u9ec4\u91d1\u91cc\u751f\u5b58\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5415\u4e09\u81ea\u5df1\u4e00\u5b9a\u4f1a\u89e3\u91ca\u7684\u3002 \u3000\u3000\u5415\u4e09\u679c\u7136\u5df2\u63a5\u7740\u8bf4\u4e0b\u53bb\uff1a\u201c\u4f60\u6ca1\u6709\u770b\u89c1\u8fc7\u90a3\u6761\u9c7c\uff0c\u6240\u4ee5\u4f60\u7edd\u5bf9\u60f3\u4e0d\u5230\u90a3\u6761\u9c7c\u662f\u591a\u4e48\u795e\u5947\u3001\u591a\u4e48\u7f8e\u4e3d\u3002\u201d \u3000\u3000\u201c\u795e\u5947\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u542c\u8fc7\u4efb\u4f55\u4eba\u7528\u201c\u795e\u5947\u201d\u8fd9\u4e24\u4e2a\u5b57\u6765\u5f62\u5bb9\u4e00\u6761\u9c7c\u3002 \u3000\u3000\u6240\u4ee5\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u90a3\u6761\u9c7c\u6709\u4ec0\u4e48\u795e\u5947\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u6614\u5e74\u5177\u6709\u65e0\u8fb9\u5927\u795e\u901a\u5927\u667a\u6167\u5927\u6cd5\u529b\u7684\u2018\u963f\u91cc\u738b\u2019\u5728\u6210\u795e\u4e4b\u524d\u4eb2\u81ea\u4ece\u8c61\u6cc9\u6cb3\u91cc\u9493\u8d77\u6765\u7684\uff0c\u51fa\u6c34\u4e4b\u540e\uff0c\u5b83\u7684\u8840\u8089\u9cde\u9aa8\u5c31\u5168\u90fd\u53d8\u6210\u4e86\u7eaf\u91d1\u3002\u201d \u3000\u3000\u5415\u4e09\u9053\uff0c\u201c\u5341\u8db3\u5341\u7684\u7eaf\u91d1\uff0c\u5929\u4e0a\u5730\u4e0b\uff0c\u518d\u4e5f\u627e\u4e0d\u51fa\u90a3\u4e48\u7eaf\u90a3\u4e48\u7f8e\u7684\u7eaf\u91d1\uff0c\u53ef\u662f\u5b83\u770b\u8d77\u6765\u4ecd\u7136\u597d\u50cf\u662f\u6d3b\u7740\uff0c\u5c31\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u4ee5\u5316\u4e3a\u795e\u9f99\u98de\u4e0a\u5929\u53bb\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5f00\u59cb\u5598\u606f\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u80fd\u63a5\u7740\u8bf4\uff1a\u201c\u56e0\u4e3a\u5b83\u8981\u4fdd\u62a4\u81ea\u5df1\uff0c\u4e0d\u80fd\u8ba9\u81ea\u5df1\u7684\u6cd5\u8eab\u53bb\u9971\u4fd7\u4eba\u7684\u53e3\u8179\u4e4b\u6b32\uff0c\u6240\u4ee5\u624d\u628a\u81ea\u5df1\u7684\u8840\u8089\u9cde\u9aa8\u90fd\u5316\u4e3a\u7eaf\u91d1\u3002\u201d \u3000\u3000\u5415\u4e09\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u4e00\u6b21\u4e4b\u540e\uff0c\u5b83\u7684\u540c\u7c7b\u4e5f\u5c31\u88ab\u4eba\u4eec\u5949\u4e3a\u795e\u660e\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u8352\u8bde\u7684\u6545\u610f\uff0c\u5374\u53c8\u5145\u6ee1\u4e86\u9b45\u529b\uff0c\u4e00\u79cd\u81ea\u4ece\u8fdc\u53e4\u4ee5\u6765\u5c31\u80fd\u6253\u52a8\u4eba\u5fc3\u7684\u795e\u5947\u9b45\u529b\u3002 \u3000\u3000\u8fd9\u4e2a\u6545\u4e8b\u7684\u7ed3\u5c40\u662f \u3000\u3000\u9493\u9c7c\u7684\u963f\u91cc\u738b\u5f97\u9053\u6210\u795e\u4e86\uff0c\u5316\u4e3a\u7eaf\u91d1\u7684\u9c7c\u5374\u843d\u5165\u4e86\u5415\u4e09\u624b\u91cc\u3002 \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u4e2a\u6545\u4e8b\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\u4e4b\u540e\uff0c\u5415\u4e09\u7684\u6fc0\u52a8\u624d\u6e10\u6e10\u5e73\u606f\uff0c\u773c\u4e2d\u5374\u53c8\u9732\u51fa\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u201c\u5929\u4e0a\u5730\u4e0b\uff0c\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u6761\u90a3\u6837\u7684\u9c7c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5583\u5583\u4ed6\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u4e00\u5b9a\u8981\u628a\u5b83\u627e\u56de\u6765\u3002\u968f\u4fbf\u8981\u6211\u5e72\u4ec0\u4e48\uff0c\u6211\u90fd\u8981\u628a\u5b83\u627e\u56de\u6765\u3002\u201d \u3000\u3000\u2014\u2014\u4e00\u4e2a\u50cf\u5415\u4e09\u8fd9\u6837\u7684\u4eba\uff0c\u600e\u7684\u4f1a\u76f8\u4fe1\u8fd9\u79cd\u8352\u8bde\u7684\u4f20\u8bf4\uff1f \u3000\u3000\u2014\u2014\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u90a3\u6761\u91d1\u9c7c\u4e2d\u53e6\u6709\u79d8\u5bc6\uff0c\u7edd\u4e0d\u80fd\u544a\u8bc9\u522b\u4eba\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u4ed6\u624d\u7528\u8fd9\u4e2a\u6545\u4e8b\u6765\u8ba9\u5c0f\u65b9\u8ff7\u60d1\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u968f\u4fbf\u4ed6\u600e\u4e48\u95ee\uff0c\u5415\u4e09\u90fd\u4e0d\u4f1a\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u5415\u4e09\u5df2\u7ecf\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff1a\u201c\u73b0\u5728\u6211\u5df2\u7ecf\u628a\u6211\u7684\u79d8\u5bc6\u8bf4\u51fa\u6765\u4e86\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u76ef\u7740\u5415\u4e09\u770b\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u4ed6\u8bf4\u51fa\u4e86\u4e09\u4e2a\u5b57\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5415\u4e09\u7acb\u523b\u95ee\uff0c\u201c\u4f60\u4e0d\u4fe1\u8fd9\u4e2a\u6545\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u8fd9\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u4fe1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u4e09\u53c8\u95ee\uff0c\u201c\u4e0d\u4fe1\u6211\u8bf4\u7684\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\u4f60\u8bf4\u7684\u8bdd\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6307\u4e86\u6307\u201c\u9ebb\u96c0\u201d\uff1a\u201c\u662f\u4ed6\u8bf4\u7684\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u8138\uff0c\u9762\u5bf9\u201c\u9ebb\u96c0\u201d\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u7684\u90a3\u4e9b\u8bdd\uff0c\u6211\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5415\u4e09\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u201c\u9ebb\u96c0\u201d\u7684\u8138\u770b\u6765\u66f4\u50cf\u662f\u5df2\u88ab\u70e4\u5f97\u5b8c\u5168\u719f\u900f\u7126\u900f\u7684\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u4fe1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u4e09\u560e\u58f0\u95ee\uff0c\u201c\u4f60\u518d\u8bf4\u4e00\u904d\uff0c\u4f60\u4e0d\u4fe1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u51b7\u51b7\u5730\u53cd\u95ee\u9053\uff1a\u201c\u521a\u624d\u4ed6\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u4ed6\u53ea\u8981\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5728\u4ed6\u6570\u5230\u2018\u4e8c\u5341\u2019\u4e4b\u524d\uff0c\u5c31\u80fd\u5c06\u8fd9\u6761\u8857\u4e0a\u6240\u6709\u7684\u7537\u5973\u755c\u7272\u732b\u72d7\uff0c\u5168\u90fd\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5c0f\u65b9\u51b7\u51b7\u5730\u8bf4\uff0c\u201c\u8fd9\u4e9b\u8bdd\u6211\u6839\u672c\u8fde\u4e00\u53e5\u90fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5415\u4e09\u5403\u60ca\u5730\u770b\u7740\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u6562\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u65b9\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u8fd9\u4e48\u8bf4\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u540e\u679c\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u65b9\u8138\u4e0a\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u201c\u6211\u5b8c\u5168\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u4ed6\u4e0d\u6562\u6740\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6562\uff0c\u6211\u76f8\u4fe1\u4ed6\u6562\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff0c\u201c\u53ea\u4e0d\u8fc7\u6562\u6740\u4eba\u7684\u5e76\u4e0d\u4e00\u5b9a\u80fd\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u4ed6\u771f\u7684\u505a\u51fa\u6765\u624d\u80af\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u5c0f\u65b9\u9053\u3002 \u3000\u3000\u201c\u9ebb\u96c0\u201d\u7684\u773c\u89d2\u5728\u8df3\uff0c\u5634\u89d2\u4e5f\u5728\u8df3\uff0c\u6709\u5f88\u591a\u4eba\u5728\u6740\u4eba\u4e4b\u524d\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u5415\u4e09\u95ee\u4ed6\u9053\uff1a\u201c\u4f60\u4eec\u7ea6\u5b9a\u7684\u5bc6\u4ee4\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u2014\u2014\u5bc6\u4ee4\u53ea\u6709\u4e24\u4e2a\u5b57\uff0c\u53ea\u8981\u5bc6\u4ee4\u4e00\u4e0b\uff0c\u8fd9\u6761\u8857\u5c31\u5c06\u88ab\u8840\u6d17\u3002 \u3000\u3000\u201c\u9ebb\u96c0\u201d\u6162\u6162\u5730\u8d70\u5230\u7a97\u53e3\uff0c\u4fef\u89c6\u8857\u4e0a\u7684\u4eba\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u6740\u673a\uff01 \u3000\u3000\u4ed6\u7ec8\u4e8e\u628a\u8fd9\u4e24\u4e2a\u5b57\u8bf4\u4e86\u51fa\u6765\uff0c\u7528\u4e00\u79cd\u65e0\u8bba\u8c01\u542c\u89c1\u90fd\u4f1a\u5bb3\u6015\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u91d1\u9c7c\uff01\u201d \u3000\u3000\u5c0f\u65b9\u4e3a\u4ec0\u4e48\u8981\u505a\u8fd9\u79cd\u4e8b\uff1f\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u903c\u4ed6\u4eec\u53bb\u6740\u4eba\uff0c\u6740\u90a3\u4e9b\u65e0\u8f9c\u7684\u4eba\uff1f \u3000\u3000\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u8981\u522b\u4eba\u4e5f\u6765\u5c1d\u4e00\u5c1d\u4ed6\u4eec\u53d7\u5230\u7684\u60b2\u4f24\u548c\u75db\u82e6\uff1f\u8981\u770b\u4e00\u770b\u522b\u4eba\u7684\u6bcd\u4eb2\u3001\u670b\u53cb\u3001\u60c5\u4eba\u3001\u513f\u5b50\u4e5f\u65e0\u8f9c\u60e8\u6b7b\u5728\u5415\u4e09\u624b\u4e0b\uff1f \u3000\u3000\u4e0d\u7ba1\u4ed6\u4e3a\u7684\u662f\u4ec0\u4e48\uff0c\u73b0\u5728\u5bc6\u4ee4\u5df2\u4e0b\uff0c\u5df2\u7ecf\u6ca1\u6709\u4eba\u80fd\u6536\u56de\u4e86\u3002 \u3000\u3000\u201c\u91d1\u9c7c\uff01\u201d \u3000\u3000\u201c\u9ebb\u96c0\u201d\u53c8\u7528\u7740\u540c\u6837\u53ef\u6015\u7684\u58f0\u97f3\u5c06\u8fd9\u4e24\u4e2a\u53ef\u6015\u7684\u5b57\u53c8\u91cd\u590d\u4e86\u4e00\u904d\uff1a\u201c\u91d1\u9c7c\uff01\u201d \u3000\u3000\u7a97\u5916\u7684\u957f\u8857\u8fd8\u662f\u8ddf\u521a\u624d\u540c\u6837\u70ed\u95f9\uff0c\u4f9d\u65e7\u6324\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u5c0f\u8d29\u548c\u884c\u4eba\u3002 \u3000\u3000\u5927\u5bb6\u8fd8\u662f\u9ad8\u9ad8\u5174\u5174\u7684\u6837\u5b50\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4f1a\u6709\u6a2a\u7978\u5c06\u4e34\u3002 \u3000\u3000\u6447\u94c3\u7684\u8d27\u90ce\u63a8\u8f66\uff0c\u4ecd\u505c\u5728\u90a3\u5bb6\u7cd5\u997c\u5e97\u524d\u9762\u3002\u81ea\u53d1\u82cd\u82cd\u7684\u8001\u592a\u592a\uff0c\u7ec8\u4e8e\u51b3\u5b9a\u4e86\u81ea\u5df1\u8981\u4e70\u4ec0\u4e48\u989c\u8272\u7684\u7ebf\uff0c\u6b63\u51c6\u5907\u4ed8\u94b1\u3002 \u3000\u3000\u68b3\u7740\u5927\u8fab\u5b50\u7684\u5c0f\u59d1\u5a18\u6ca1\u6709\u4e70\u80ed\u8102\u82b1\u7c89\u9999\u6cb9\uff0c\u5374\u8d70\u8fdb\u4e86\u7cd5\u997c\u5e97\uff0c\u8ddf\u90a3\u4e2a\u5e74\u9752\u7684\u4f19\u8ba1\u8bf4\u8bdd\uff0c\u8c01\u4e5f\u542c\u4e0d\u89c1\u5979\u8bf4\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u751f\u610f\u6e05\u6de1\u7684\u5e74\u8d27\u94fa\u91cc\u5c45\u7136\u4e5f\u6709\u751f\u610f\u4e0a\u95e8\u4e86\uff0c\u638c\u67dc\u7684\u5f53\u7136\u4e0d\u518d\u751f\u6c14\uff0c\u6b63\u6253\u8d77\u4e86\u7cbe\u795e\uff0c\u8ddf\u521a\u4e0a\u95e8\u7684\u80d6\u592a\u592a\u4eec\u505a\u751f\u610f\u3002 \u3000\u3000\u5356\u82b1\u7684\u8001\u5934\u5b50\u548c\u5c0f\u4f19\u5b50\u4e0d\u518d\u4e89\u5435\uff0c\u56e0\u4e3a\u4e70\u82b1\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u5927\u5bb6\u90fd\u6709\u751f\u610f\u4e86\u3002 \u3000\u3000\u9152\u94fa\u95e8\u5916\u7684\u9189\u6c49\u5df2\u7761\u7740\uff0c\u8981\u996d\u7684\u4e5e\u4e10\u653e\u8fc7\u4e86\u53bb\u4e70\u7ef8\u7f0e\u548c\u5e74\u8d27\u7684\u80d6\u592a\u592a\u4eec\uff0c\u5374\u56f4\u4f4f\u4e86\u51e0\u4e2a\u5df2\u7ecf\u7565\u6709\u9152\u610f\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u6709\u4e86\u4e00\u70b9\u9152\u610f\u7684\u4eba\uff0c\u51fa\u624b\u603b\u662f\u7279\u522b\u5927\u65b9\u4e9b\uff0c\u4ed6\u4eec\u5f53\u7136\u4e5f\u8ddf\u90a3\u8001\u592a\u592a\u3001\u80d6\u592a\u5927\u548c\u5c0f\u59d1\u5a18\u4e00\u6837\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4eec\u65bd\u820d\u7684\u5bf9\u8c61\uff0c\u5c31\u662f\u4ed6\u4eec\u7684\u715e\u661f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u957f\u8857\u4e0a\u6bcf\u4e2a\u4eba\u90fd\u542c\u89c1\u697c\u4e0a\u6709\u4e2a\u4eba\u7528\u4e00\u79cd\u975e\u5e38\u53ef\u6015\u7684\u58f0\u97f3\u8bf4\u51fa\u4e86\u4e24\u4e2a\u5b57\uff0c\u800c\u4e14\u8bf4\u4e86\u4e24\u904d\u3002 \u3000\u3000\u201c\u91d1\u9c7c\u3002\u201d \u3000\u3000\u201c\u91d1\u9c7c\u3002\u201d\u522b\u7684\u4eba\u5f53\u7136\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u5b57\u5c31\u662f\u6740\u4eba\u7684\u5bc6\u4ee4\uff0c\u5c31\u662f\u4ed6\u4eec\u7684\u50ac\u547d\u7b26\u3002 \u3000\u3000\u4f46\u662f\u6709\u4eba\u77e5\u9053\uff0c\u81f3\u5c11\u6709\u56db\u5341\u516d\u4e2a\u4eba\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u4ee4\u4e0b\uff0c\u90a3\u6447\u94c3\u7684\u8d27\u90ce\u5df2\u4ece\u63a8\u8f66\u7684\u628a\u624b\u91cc\u62bd\u51fa\u4e00\u67c4\u5c16\u5200\uff0c\u51c6\u5907\u51fa\u624b\u5c31\u5148\u628a\u90a3\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u8001\u592a\u592a\u523a\u6740\u5728\u4ed6\u7684\u5200\u4e0b\u3002 \u3000\u3000\u7cd5\u997c\u5e97\u7684\u5e74\u9752\u4f19\u8ba1\u672c\u6765\u6b63\u76ef\u7740\u7b11\u773c\u542c\u90a3\u5c0f\u59d1\u5a18\u8bf4\u8bdd\uff0c\u73b0\u5728\u5374\u5df2\u51c6\u5907\u627c\u6b7b\u5979\u3002 \u3000\u3000\u5e74\u8d27\u5e97\u548c\u7ef8\u7f0e\u5e84\u7684\u638c\u67dc\u5175\u5203\u6697\u5668\u4e5f\u90fd\u5728\u624b\uff0c\u4ed6\u4eec\u7edd\u5bf9\u6709\u628a\u63e1\u80fd\u5728\u9ebb\u96c0\u6570\u5230\u201c\u4e8c\u5341\u201d\u65f6\u5c31\u5c06\u8fd9\u4e9b\u80d6\u592a\u592a\u7f6e\u4e4b\u6b7b\u5730\u3002 \u3000\u3000\u5c24\u5176\u662f\u521a\u624d\u653e\u7206\u7af9\u7684\u7ef8\u7f0e\u5e84\u638c\u67dc\uff0c\u4ed6\u7684\u706b\u836f\u6697\u5668\u5f97\u81ea\u6c5f\u5357\u201c\u9739\u96f3\u201d\u7684\u4eb2\u4f20\uff0c\u6740\u4f24\u529b\u4e4b\u5f3a\uff0c\u7edd\u5bf9\u662f\u5176\u4ed6\u540c\u4f34\u6bd4\u4e0d\u4e0a\u7684\u3002 \u3000\u3000\u9189\u6c49\u5df2\u8dc3\u8d77\uff0c\u4e5e\u4e10\u4eec\u51c6\u5907\u6740\u521a\u624d\u8fd8\u5bf9\u4ed6\u4eec\u975e\u5e38\u6177\u6168\u65bd\u820d\u8fc7\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u9001\u8d22\u795e\u7684\u73b0\u5728\u51c6\u5907\u8981\u9001\u7684\u5df2\u4e0d\u662f\u8d22\u795e\uff0c\u800c\u662f\u6b7b\u795e\u3002 \u3000\u3000\u821e\u72ee\u7684\u5927\u6c49\u548c\u7ad9\u5728\u8857\u89d2\u770b\u5973\u4eba\u8bc4\u5934\u8bba\u8db3\u7684\u5e74\u9752\u4eba\uff0c\u4e5f\u5df2\u62d4\u51fa\u4e86\u4ed6\u4eec\u7684\u5175\u5203\u3002 \u3000\u3000\u6bcf\u4e00\u4ef6\u5175\u5203\u90fd\u662f\u4e00\u51fb\u5c31\u53ef\u4ee5\u81f4\u547d\u7684\u6b66\u5668\uff0c\u6bcf\uff5e\u4e2a\u4eba\u90fd\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6740\u624b\u3002 \u3000\u3000\u201c\u9ebb\u96c0\u201d\u4e0d\u4f46\u6709\u5934\u8111\uff0c\u800c\u4e14\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u4ed6\u4eec\u5b89\u6392\u7684\u8fd9\u4e9b\u4eba\uff0c\u7edd\u5bf9\u53ef\u4ee5\u5728\u6570\u5230\u201c\u4e8c\u5341\u201d\u4e4b\u524d\uff0c\u5c31\u5b8c\u6210\u4ed6\u4eec\u7684\u4efb\u52a1\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4e5f\u6709\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u521a\u5f00\u59cb\u6570\u5230\u201c\u4e00\u201d\u65f6\uff0c\u4ed6\u5df2\u7ecf\u770b\u5230\u4ed6\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u7684\u4e8b\u53d1\u751f\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u90a3\u4e2a\u6148\u7965\u548c\u853c\u7684\u81ea\u53d1\u8001\u592a\u592a\uff0c\u5ffd\u7136\u7528\u5979\u521a\u4e70\u6765\u7684\u9488\uff0c\u523a\u778e\u4e86\u6447\u94c3\u8d27\u90ce\u7684\u53cc\u773c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u90a3\u4e2a\u5bb3\u7f9e\u7684\u59d1\u5a18\u5ffd\u7136\u51cc\u7a7a\u98de\u8d77\uff0c\u4e00\u811a\u8e22\u788e\u4e86\u5e74\u8d27\u4f19\u8ba1\u7684\u5589\u7ed3\u3002 \u3000\u3000\u5356\u82b1\u7684\u8001\u5934\u5b50\u548c\u5c0f\u4f19\u5b50\u521a\u4ece\u82b1\u6735\u82b1\u675f\u4e2d\u62bd\u51fa\u4e00\u67c4\u96c1\u7fce\u5200\u548c\u4e00\u53cc\u5ce8\u5d4b\u523a\uff0c\u4e24\u4e2a\u4eba\u7684\u54bd\u5589\u5c31\u5168\u90fd\u88ab\u4eba\u7528\u94a2\u7d22\u5957\u4f4f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u9001\u8d22\u795e\u548c\u821e\u72ee\u7684\u5927\u6c49\u5ffd\u7136\u53d1\u73b0\u4eba\u6f6e\u62e5\u6765\uff0c\u7b49\u5230\u4eba\u6f6e\u518d\u6563\u65f6\uff0c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u7684\u54bd\u5589\u4e5f\u90fd\u5df2\u88ab\u5272\u65ad\u3002 \u3000\u3000\u8981\u996d\u7684\u4e5e\u4e10\u5df2\u6b7b\u5728\u90a3\u4e9b\u7565\u6709\u9152\u610f\u7684\u8c6a\u5ba2\u4eec\u624b\u4e0b\uff0c\u6bcf\u4e2a\u4eba\u7684\u8981\u5bb3\u90fd\u88ab\u6253\u5165\u51e0\u679a\u8fb9\u7f18\u5df2\u88ab\u78e8\u5149\u78e8\u9510\u4e86\u7684\u94dc\u94b1\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u5c31\u662f\u8981\u522b\u4eba\u65bd\u820d\u4e00\u70b9\u94dc\u94b1\u7ed9\u4ed6\u4eec\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u5f97\u5230\u7684\uff0c\u5c82\u975e\u6b63\u662f\u4ed6\u4eec\u6240\u8981\u7684\uff1f \u3000\u3000\u4ed6\u4eec\u672c\u6765\u60f3\u8981\u522b\u4eba\u7684\u547d\uff0c\u73b0\u5728\u4ed6\u4eec\u7684\u547d\u5374\u53cd\u800c\u88ab\u4eba\u8981\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u5931\u53bb\u7684\uff0c\u5c82\u975e\u4e5f\u6b63\u662f\u4ed6\u4eec\u6240\u8981\u7684\uff1f \u3000\u3000\u6700\u5403\u60ca\u7684\u5f53\u7136\u8fd8\u662f\u90a3\u5e74\u8d27\u5e97\u548c\u7ef8\u7f0e\u5e84\u7684\u638c\u67dc\uff0c\u4ed6\u4eec\u7684\u6bd2\u836f\u6697\u5668\u548c\u706b\u836f\u6674\u5668\u672c\u6765\u90fd\u662f\u8fd9\u6b21\u653b\u51fb\u7684\u4e3b\u529b\uff0c\u60f3\u4e0d\u5230\u90a3\u4e9b\u80d6\u592a\u592a\u4eec\u7684\u884c\u52a8\u7adf\u8fdc\u6bd4\u4efb\u4f55\u4eba\u60f3\u8c61\u4e2d\u90fd\u5feb\u5341\u500d\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u6697\u5668\u8fd8\u672a\u51fa\u624b\uff0c\u624b\u8155\u5df2\u88ab\u634f\u788e\uff0c\u4ed6\u4eec\u7684\u8eab\u5b50\u521a\u8dc3\u8d77\uff0c\u4e24\u6761\u817f\u5c31\u5df2\u88ab\u6253\u65ad\u3002\u4ed6\u4eec\u751a\u81f3\u8fde\u5bf9\u65b9\u7684\u51fa\u624b\u8fd8\u6ca1\u6709\u770b\u6e05\u695a\uff0c\u6574\u4e2a\u4eba\u5df2\u7ecf\u50cf\u4e00\u6ee9\u6ce5\u4e00\u6837\u5012\u5728\u5730\u4e0a\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u4e86\u3002\u8fd9\u4e9b\u770b\u6765\u5c31\u50cf\u662f\u6cb3\u9a6c\u822c\u884c\u52a8\u8fdf\u949d\u7684\u80d6\u592a\u592a\u4eec\uff0c\u8eab\u624b\u7adf\u8fdc\u6bd4\u8c79\u5b50\u66f4\u51f6\u608d\u654f\u6377\u77eb\u5065\u3002 \u3000\u3000\u8fd9\u65f6\u9ebb\u96c0\u521a\u6570\u5230\u201c\u5341\u4e09\u201d\u3002 \u3000\u3000\u6570\u5230\u201c\u4e94\u201d\u65f6\uff0c\u4ed6\u7684\u58f0\u97f3\u5df2\u5636\u54d1\u3002\u6570\u5230\u201c\u5341\u4e09\u201d\u65f6\uff0c\u4ed6\u5b89\u6392\u5728\u957f\u8857\u4e0a\u7684\u56db\u5341\u4e03\u4e2a\u4eba\u5df2\u7ecf\u5168\u90fd\u5012\u4e86\u4e0b\u53bb\uff0c\u5c31\u7b97\u8fd8\u6d3b\u7740\uff0c\u4e5f\u53ea\u80fd\u8eba\u5728\u5730\u4e0a\u6323\u624e\u547b\u541f\u3002 \u3000\u3000\u5415\u4e09\u548c\u201c\u9ebb\u96c0\u201d\u597d\u50cf\u4e5f\u4e0d\u80fd\u52a8\u4e86\uff0c\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e00\u5757\u808c\u8089\u6bcf\u4e00\u4e2a\u9aa8\u8282\u597d\u50cf\u90fd\u5df1\u9ebb\u6728\u50f5\u786c\u3002 \u3000\u3000\u90a3\u4e9b\u770b\u6765\u5df2\u7ecf\u7565\u6709\u9189\u610f\u7684\u9152\u5ba2\u4e4b\u4e2d\uff0c\u5ffd\u7136\u6709\u4e2a\u4eba\u8131\u4e0b\u5e3d\u5b50\u6765\u5411\u5c0f\u65b9\u5fae\u7b11\u884c\u793c\uff0c\u9732\u51fa\u4e00\u5f20\u9971\u7ecf\u98ce\u971c\u7684\u9ed1\u8138\u548c\u4e00\u53e3\u96ea\u767d\u7684\u7259\u9f7f\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u5411\u4ed6\u5fae\u7b11\u7b54\u793c\u3002 \u3000\u3000\u5415\u4e09\u6162\u6162\u5730\u4ece\u80f8\u53e3\u91cc\u5410\u51fa\u4e00\u53e3\u6c14\uff0c\u8f6c\u8138\u95ee\u5c0f\u65b9\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u4e2a\u672c\u6765\u5df2\u7ecf\u5e94\u8be5\u6b7b\u4e86\u7684\u4eba\u3002\u201d \u7b2c\u4e8c\u4e03\u7ae0\u3000\u4e3a\u4ec0\u4e48\u4e0d\u56de\u53bb \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u4e3a\u4ec0\u4e48\u4e0d\u56de\u53bb \u3000\u3000\u4ed6\u5f88\u5feb\u5c31\u5c06\u79d8\u9053\u7684\u4eba\u53e3\u627e\u5230\uff0c\u53ef\u60dc\u5c31\u5728\u4ed6\u627e\u5230\u7684\u65f6\u5019\uff0c\u5c31\u542c\u89c1\u201c\u8f70\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u785d\u77f3\u7802\u571f\u56db\u6563\uff0c\u5730\u9053\u5df2\u88ab\u95ed\u6b7b\u4e86\u3002 \u3000\u3000\u7247\u523b\u95f4\u6240\u6709\u7684\u4eba\u90fd\u5df2\u64a4\u79bb\u8fd9\u5730\u533a\uff0c\u5230\u8fbe\u4e00\u4e2a\u4eba\u70df\u7a00\u5c11\u7684\u4e61\u6751\u3002 \u3000\u3000\u8fd9\u4e9b\u7247\u523b\u524d\u8fd8\u80fd\u5728\u7728\u773c\u95f4\u6740\u4eba\u5982\u9664\u8349\u7684\u6740\u624b\uff0c\u7acb\u523b\u5c31\u5168\u90e8\u53d8\u6210\u4e86\u7edd\u5bf9\u4e0d\u4f1a\u5f15\u4eba\u6ce8\u76ee\u7684\u826f\u6c11\uff0c\u5230\u4e86\u66ae\u8272\u5c06\u4e34\u65f6\u5c31\u7eb7\u7eb7\u6563\u53bb\uff0c\u5c31\u50cf\u662f\u4e00\u628a\u5c18\u57c3\u843d\u4eba\u7070\u571f\u4e2d\uff0c\u5ffd\u7136\u5c31\u795e\u79d8\u5730\u6d88\u5931\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ee5\u540e\u8fd8\u4f1a\u4e0d\u4f1a\u89c1\u5230\u4ed6\u4eec\uff0c\u8c01\u4e5f\u4e0d\u77e5\u4ee5\u540e\u89c1\u5230\u4ed6\u4eec\u65f6\u8fd8\u4f1a\u4e0d\u4f1a\u8ba4\u5f97\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u5c31\u662f\u6ca1\u6709\u201c\u4ee5\u540e\u201d\u7684\u4eba\uff0c\u6ca1\u6709\u201c\u672a\u6765\u201d\uff0c\u4e5f\u6ca1\u6709\u201c\u8fc7\u53bb\u201d\u3002 \u3000\u3000\u6709\u98ce\uff0c\u98ce\u5728\u7a97\u5916\u3002 \u3000\u3000\u9ec4\u5c18\u98de\u5377\uff0c\u98ce\u6c99\u5439\u6253\u5728\u7528\u539a\u68c9\u7eb8\u7cca\u6210\u7684\u7a97\u6237\u4e0a\uff0c\u5c31\u597d\u50cf\u5bc6\u96e8\u6572\u6253\u82ad\u8549\u3002 \u3000\u3000\u6709\u9152\uff0c\u9152\u5728\u6a3d\u4e2d\uff0c\u4eba\u5728\u6a3d\u524d\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u6ca1\u6709\u559d\uff0c\u8fde\u4e00\u6ef4\u90fd\u6ca1\u6709\u559d\uff0c\u73ed\u5bdf\u5df4\u90a3\u4e5f\u6ca1\u6709\u559d\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u5fc5\u987b\u4fdd\u6301\u6e05\u9192\uff0c\u800c\u4e14\u5e0c\u671b\u5bf9\u65b9\u6e05\u9192\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e4b\u4e2d\u4e00\u4e2a\u6709\u8bb8\u591a\u8bdd\u8981\u8bf4\uff0c\u6709\u8bb8\u591a\u4e8b\u8981\u89e3\u91ca\uff0c\u53e6\u4e00\u4e2a\u5fc5\u987b\u4ed4\u7ec6\u7684\u542c\u3002 \u3000\u3000\u8bf4\u7684\u4eba\u662f\u73ed\u5bdf\u5df4\u90a3\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u82b1\u4e0d\u62c9\u548c\u2018\u5927\u70df\u888b\uff0c\u90fd\u5df2\u88ab\u5415\u4e09\u4e70\u901a\uff0c\u6240\u4ee5\u6211\u624d\u8981\u4f60\u5230\u90a3\u5546\u961f\u53bb\u3002\u201d \u3000\u3000\u6709\u4e9b\u4eba\u8bf4\u8bdd\u4ece\u4e0d\u8f6c\u5f2f\u62b9\u89d2\uff0c\u4e00\u5f00\u53e3\u5c31\u76f4\u4eba\u672c\u9898\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e5f\u8ddf\u4f60\u4e00\u6837\uff0c\u6211\u4e5f\u627e\u4e0d\u5230\u5415\u4e09\uff0c\u4f46\u662f\u6211\u4e00\u5b9a\u8981\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u6240\u4ee5\u6211\u53ea\u6709\u5229\u7528\u4f60\u628a\u4ed6\u5f15\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u548c\u5c0f\u65b9\u53ef\u7b97\u662f\u670b\u53cb\uff0c\u4f46\u662f\u4ed6\u8bf4\u51fa\u201c\u5229\u7528\u201d\u4e24\u4e2a\u5b57\u65f6\uff0c\u7edd\u6ca1\u6709\u4e00\u70b9\u60ed\u6127\u4e4b\u610f\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u6ca1\u6709\u8868\u73b0\u51fa\u4e00\u70b9\u75db\u82e6\u548c\u6124\u6012\uff0c\u53ea\u662f\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u4ed6\u7684\u786e\u88ab\u6211\u5f15\u51fa\u6765\u4e86\uff0c\u8fd9\u4e00\u70b9\u4f60\u786e\u5b9e\u6ca1\u6709\u7b97\u9519\u3002\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u6211\u5f88\u5c11\u4f1a\u7b97\u9519\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4f38\u51fa\u624b\uff0c\u63e1\u7d27\u9152\u676f\uff0c\u53c8\u653e\u5f00\uff0c\u4e00\u5b57\u5b57\u5730\u95ee\uff1a\u201c\u73b0\u5728\u4ed6\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u95ee\u5f97\u5f88\u5403\u529b\uff0c\u56e0\u4e3a\u4ed6\u672c\u6765\u5e76\u4e0d\u60f3\u8fd9\u4e48\u95ee\u7684\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5374\u53ea\u662f\u6de1\u6de1\u5730\u56de\u7b54\uff1a \u3000\u3000\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u9003\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5229\u7528\u6211\u627e\u5230\u4ed6\u4e00\u6b21\u4e4b\u540e\uff0c\u4ee5\u540e\u662f\u4e0d\u662f\u5c31\u80fd\u627e\u5230\u4ed6\u4e86\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\u3002 \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4ee5\u540e\u6211\u8fd8\u662f\u4e00\u6837\u627e\u4e0d\u5230\u4ed6\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u8fd9\u4ef6\u4e8b\u53ef\u8bf4\u505a\u5f97\u6839\u672c\u8fde\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u4f38\u51fa\u624b\u63e1\u4f4f\u9152\u676f\uff1a\u201c\u5bf9\u4f60\u6765\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u505a\u4e86\u4ef6\u6ca1\u6709\u7528\u7684\u4e8b\u800c\u5df2\uff0c\u53ef\u662f\u6211\u5462\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e3a\u8fd9\u4e8b\u4ed8\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u5f97\u66f4\u5403\u529b\uff0c\u597d\u50cf\u5df2\u7ecf\u7528\u51fa\u6240\u6709\u529b\u6c14\uff0c\u624d\u80fd\u95ee\u51fa\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u56de\u7b54\u5374\u53ea\u6709\u4e09\u4e2a\u5b57\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6ce2\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u9152\u676f\u788e\u4e86\uff0c\u7c89\u788e\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8fd8\u662f\u7528\u521a\u624d\u540c\u6837\u51b7\u6de1\u7684\u773c\u8272\u770b\u7740\u5c0f\u65b9\uff0c\u8fd8\u662f\u8fde\u4e00\u70b9\u7f9e\u6127\u5185\u759a\u7684\u610f\u601d\u90fd\u6ca1\u6709\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6068\u6211\u7684\u3002\u4e3a\u4e86\u6211\u8981\u505a\u4e00\u4ef6\u8fde\u6211\u81ea\u5df1\u90fd\u6ca1\u6709\u628a\u63e1\u80fd\u505a\u5230\u7684\u4e8b\uff0c\u4e0d\u4f46\u5bb3\u4f60\u5403\u8db3\u4e86\u82e6\uff0c\u800c\u4e14\u8fd8\u8fde\u7d2f\u5230\u4f60\u7684\u6bcd\u4eb2\u548c\u2018\u9633\u5149\u2019\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u6de1\u6de1\u5730\u63a5\u7740\u8bf4\uff1a\u201c\u4f46\u662f\u4f60\u82e5\u8ba4\u4e3a\u6211\u4f1a\u540e\u6094\uff0c\u4f60\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u63e1\u7d27\u9152\u676f\u7684\u788e\u7247\uff0c\u9c9c\u8840\u4ece\u638c\u5fc3\u6e17\u51fa\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u540e\u6094\uff1f\u201d \u3000\u3000\u201c\u6211\u4e00\u70b9\u90fd\u4e0d\u540e\u6094\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u4ee5\u540e\u5982\u679c\u8fd8\u6709\u8fd9\u6837\u7684\u673a\u4f1a\uff0c\u6211\u8fd8\u662f\u4f1a\u8fd9\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u53ea\u8981\u80fd\u627e\u5230\u5415\u4e09\uff0c\u4e0d\u7ba1\u8981\u6211\u505a\u4ec0\u4e48\u4e8b\uff0c\u6211\u90fd\u4f1a\u53bb\u505a\u3002\u5c31\u7b97\u8981\u628a\u6211\u6253\u4e0b\u5341\u516b\u5c42\u5730\u72f1\uff0c\u6211\u4e5f\u4e0d\u4f1a\u76b1\u7709\u5934\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u770b\u7740\u4ed6\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u80fd\u660e\u767d\u6211\u7684\u610f\u601d\uff0c\u56e0\u4e3a\u4f60\u81ea\u5df1\u4e00\u5b9a\u4e5f\u6709\u8fc7\u4e0d\u501f\u4e0b\u5730\u72f1\u7684\u65f6\u5019\uff0c\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u4ed6\u5b8c\u5168\u4e0d\u80fd\u4e86\u89e3\u73ed\u5bdf\u5df4\u90a3\u8fd9\u4e2a\u4eba\u548c\u8fd9\u4e2a\u4eba\u6240\u505a\u7684\u4e8b\uff0c\u4f46\u662f\u4ed6\u4e5f\u4e0d\u80fd\u5426\u8ba4\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u80fd\u5426\u8ba4\u8fd9\u4e00\u70b9\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u7518\u5fc3\u4e0b\u5730\u72f1\u7684\u65f6\u5019\u3002 \u3000\u3000\u638c\u4e2d\u7684\u9152\u676f\u5df2\u788e\uff0c\u684c\u4e0a\u4ecd\u6709\u676f\u6709\u9152\uff0c\u5c31\u6b63\u5982\u4f60\u7684\u4eb2\u4eba\u60c5\u4eba\u867d\u5df2\u8fdc\u901d\uff0c\u4e16\u4e0a\u5374\u4ecd\u6709\u65e0\u6570\u522b\u4eba\u7684\u4eb2\u4eba\u60c5\u4eba\u3002 \u3000\u3000\u67d0\u5929\u8bf4\u4e0d\u5b9a\u4e5f\u4f1a\u50cf\u4f60\u6614\u65e5\u7684\u4eb2\u4eba\u60c5\u4eba\u5bf9\u4f60\u540c\u6837\u4eb2\u8fd1\u4eb2\u5bc6\u3002 \u3000\u3000\u2014\u2014\u6240\u4ee5\u4e00\u4e2a\u4eba\u53ea\u8981\u80fd\u6d3b\u7740\uff0c\u5c31\u5e94\u8be5\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u65e2\u7136\u8981\u6d3b\u4e0b\u53bb\uff0c\u5c31\u4e0d\u5fc5\u6028\u5929\u5c24\u4eba\u3002 \u3000\u3000\u684c\u4e0a\u65e2\u7136\u8fd8\u6709\u676f\u6709\u9152\uff0c\u6240\u4ee5\u73ed\u5bdf\u5df4\u90a3\u5c31\u4e3a\u5c0f\u65b9\u91cd\u65b0\u659f\u6ee1\u4e00\u676f\uff1a \u3000\u3000\u201c\u4f60\u5148\u559d\u4e00\u676f\uff0c\u6211\u8fd8\u6709\u8bdd\u5bf9\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u8fd8\u6709\u4ec0\u4e48\u8bdd\u53ef\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6709\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u559d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u773c\u8272\u6df1\u6c89\u5982\u767e\u4e08\u5bd2\u6f6d\u4e0b\u7684\u6c89\u6c34\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u5b8c\u5168\u660e\u767d\u6211\u7684\u610f\u601d\u4e86\uff1f\u201d\u4ed6\u95ee\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u56de\u7b54\u662f\u7edd\u5bf9\u80af\u5b9a\u7684\uff0c\u73ed\u5bdf\u5df4\u90a3\u5374\u6447\u5934\uff1a\u201c\u4f60\u4e0d\u660e\u767d\uff0c\u6700\u5c11\u8fd8\u6709\u4e00\u70b9\u4f60\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u6211\u65e2\u7136\u8981\u5229\u7528\u4f60\u628a\u5415\u4e09\u5f15\u51fa\u6765\uff0c\u6211\u5f53\u7136\u5c31\u8981\u76ef\u7740\u4f60\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u4e0d\u7ba1\u5415\u4e09\u5728\u54ea\u91cc\uff0c\u4e5f\u4e0d\u7ba1\u4f60\u5728\u54ea\u91cc\uff0c\u6211\u90fd\u76ef\u5f97\u7262\u7262\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u76f8\u4fe1\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u73ed\u5bdf\u5df4\u90a3\u4e00\u76f4\u76ef\u5f97\u5f88\u7d27\uff0c\u4eca\u65e5\u5415\u4e09\u600e\u4e48\u4f1a\u60e8\u8d25\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u795e\u8272\u4ecd\u7136\u540c\u6837\u51b7\u9177\u51b7\u6de1\u3002 \u3000\u3000\u201c\u65e2\u7136\u6211\u4e00\u76f4\u90fd\u628a\u4f60\u76ef\u5f97\u5f88\u7d27\uff0c\u6211\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\u4f60\u8eab\u65c1\u6700\u4eb2\u8fd1\u7684\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u6de1\u6de1\u5730\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u8bf4\u6211\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e00\u76f4\u5e0c\u671b\u81ea\u5df1\u4e5f\u80fd\u50cf\u535c\u9e70\u548c\u73ed\u5bdf\u5df4\u90a3\u4e00\u6837\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u60c5\u51b5\u4e0b\u90fd\u80fd\u4fdd\u6301\u51b7\u9759\u9547\u5b9a\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u5df2\u5b8c\u5168\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\uff0c\u4ed6\u8df3\u8d77\u6765\uff0c\u51e0\u4e4e\u649e\u7ffb\u4e86\u684c\u5b50\uff0c\u4ed6\u7528\u529b\u63e1\u4f4f\u73ed\u5bdf\u5df4\u90a3\u7684\u81c2\uff1a \u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u4f60\u77e5\u9053\u4ed6\u4eec\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff1a\u201c\u73b0\u5728\u4ed6\u4eec\u90fd\u5df2\u5230\u4e86\u4e00\u4e2a\u7edd\u5bf9\u5b89\u5168\u7684\u5730\u65b9\uff0c\u7edd\u4e0d\u4f1a\u518d\u53d7\u5230\u4efb\u4f55\u60ca\u6270\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u8ffd\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u53bb\u89c1\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u770b\u7740\u5c0f\u65b9\u63e1\u7d27\u4ed6\u53f3\u81c2\u7684\u624b\uff0c\u76f4\u5230\u5c0f\u65b9\u653e\u5f00\u4ed6\u624d\u56de\u7b54\uff1a \u3000\u3000\u201c\u2018\u9633\u5149\u2019\u53d7\u4e86\u6781\u5927\u7684\u60ca\u5413\uff0c\u9700\u8981\u597d\u597d\u4f11\u517b\uff0c\u4f60\u6682\u65f6\u6700\u597d\u4e0d\u8981\u89c1\u5979\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u5979\u7684\u610f\u601d\uff0c\u8fd8\u662f\u4f60\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u5f00\u59cb\u6fc0\u52a8\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u662f\u8c01\u7684\u610f\u601d\u90fd\u4e00\u6837\uff0c\u5927\u5bb6\u90fd\u662f\u4e3a\u4e86\u5979\u597d\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5979\u82e5\u89c1\u5230\u4f60\uff0c\u96be\u514d\u4f1a\u5f15\u8d77\u4e00\u4e9b\u60b2\u75db\u7684\u56de\u5fc6\uff0c\u60c5\u7eea\u5c31\u5f88\u4e0d\u5bb9\u6613\u6062\u590d\u5e73\u9759\u4e86\u3002\u201d \u3000\u3000\u2014\u2014\u5415\u4e09\u662f\u7528\u4ec0\u4e48\u6cd5\u5b50\u6298\u78e8\u5979\u7684\uff1f\u7adf\u8ba9\u5979\u53d7\u5230\u8fd9\u4e48\u5927\u7684\u521b\u4f24\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5728\u523a\u75db\u3002 \u3000\u3000\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff0c\u201c\u662f\u6211\u5bb3\u4e86\u5979\uff0c\u5982\u679c\u5979\u6c38\u4e0d\u518d\u89c1\u5230\u6211\uff0c\u5bf9\u5979\u53ea\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5c45\u7136\u540c\u610f\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u672c\u6765\u5c31\u662f\u4e8b\u5b9e\uff0c\u6bd4\u9488\u5c16\u7bad\u94fe\u5200\u950b\u66f4\u4f24\u4eba\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u5c0f\u65b9\u63e1\u7d27\u53cc\u624b\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u95ee\uff1a\u201c\u53ef\u662f\u6211\u6bcd\u4eb2\u5462\uff1f\u96be\u9053\u6211\u4e5f\u4e0d\u8be5\u53bb\u89c1\u5979\uff1f\u201d \u3000\u3000\u4ed6\u5636\u58f0\u95ee\uff1a\u201c\u96be\u9053\u4f60\u4e5f\u6015\u6211\u4f24\u5bb3\u5230\u5979\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u53bb\u89c1\u4f60\u7684\u6bcd\u4eb2\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7ad9\u8d77\u6765\uff0c\u9762\u5bf9\u98ce\u6c99\u5439\u6253\u7684\u7a97\u6237\uff0c\u201c\u53ea\u4e0d\u8fc7\u4f60\u6c38\u8fdc\u518d\u4e5f\u89c1\u4e0d\u5230\u5979\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4eff\u4f5b\u53c8\u60f3\u8df3\u8d77\u6765\uff0c\u53ef\u662f\u4ed6\u5168\u8eab\u4e0a\u4e0b\u6240\u6709\u7684\u808c\u8089\u9aa8\u8282\u90fd\u5df2\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\u51b0\u51b7\u50f5\u786c\uff1a \u3000\u3000\u201c\u662f\u5415\u4e09\u6740\u4e86\u5979\uff1f\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u542c\u6765\u5982\u5e03\u5e1b\u88ab\u6495\u88c2\uff1a\u201c\u662f\u4e0d\u662f\u5415\u4e09\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5415\u4e09\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u4f1a\u4e00\u6b7b\uff0c\u5bf9\u4e00\u4e2a\u53d7\u5c3d\u6298\u78e8\u7684\u4eba\u6765\u8bf4\uff0c\u53ea\u6709\u6b7b\u624d\u662f\u771f\u6b63\u7684\u5b89\u606f\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u4e5f\u662f\u4e8b\u5b9e\uff0c\u53ef\u662f\u4ed6\u8bf4\u5f97\u5b9e\u5728\u592a\u6b8b\u9177\u3002 \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u8981\u6251\u8fc7\u53bb\uff0c\u6325\u62f3\u75db\u51fb\u4ed6\u90a3\u5f20\u4ece\u65e0\u8868\u60c5\u7684\u8138\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5b9e\u5728\u6ca1\u6709\u9517\uff0c\u5c0f\u65b9\u4e5f\u77e5\u9053\u4ed6\u6ca1\u6709\u9519\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u8fd8\u60f3\u89c1\u4e00\u4e2a\u4eba\uff0c\u4f46\u662f\u4f60\u4e5f\u4e0d\u80fd\u518d\u89c1\u5230\u5979\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5f53\u7136\u662f\u82cf\u82cf\u3002 \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u518d\u89c1\u5979\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u96be\u9053\u5979\u4e5f\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u5979\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u5982\u679c\u5979\u6b7b\u4e86\uff0c\u5bf9\u4f60\u53cd\u800c\u597d\u4e9b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u662f\u5415\u4e09\u7684\u5973\u4eba\uff0c\u5979\u90a3\u6837\u5bf9\u4f60\uff0c\u53ea\u4e0d\u8fc7\u8981\u66ff\u5415\u4e09\u8ba8\u56de\u4e00\u4e2a\u513f\u5b50\u3002\u201d \u3000\u3000\u9152\u5728\u6a3d\u4e2d\uff0c\u6cea\u5462\uff1f \u3000\u3000\u6ca1\u6709\u6cea\u3002 \u3000\u3000\u8fde\u8840\u90fd\u5df2\u51b7\u900f\u5e72\u900f\uff0c\u54ea\u91cc\u8fd8\u6709\u6cea\uff1f \u3000\u3000\u5c0f\u65b9\u770b\u7740\u9152\u5df2\u88ab\u559d\u5e72\u7684\u7a7a\u676f\uff0c\u53ea\u89c9\u5f97\u81ea\u5df1\u8fd9\u4e2a\u4eba\u4e5f\u50cf\u662f\u8fd9\u4e2a\u7a7a\u676f\u4e00\u6837\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8bf4\u7684\u7edd\u5bf9\u90fd\u662f\u4e8b\u5b9e\uff0c\u867d\u7136\u4ed6\u8bf4\u7684\u4e00\u6b21\u6bd4\u4e00\u6b21\u6b8b\u9177\uff0c\u4f46\u4e8b\u5b9e\u5374\u662f\u6c38\u8fdc\u65e0\u6cd5\u6539\u53d8\u7684\u3002 \u3000\u3000\u201c\u8fd9\u4e16\u754c\u4e0a\u5927\u591a\u6570\u4eba\u90fd\u8ddf\u4f60\u4e00\u6837\uff0c\u90fd\u4e3a\u7236\u6bcd\u59bb\u5b50\u670b\u53cb\u4eb2\u4eba\uff0c\u90fd\u8981\u5fcd\u53d7\u751f\u79bb\u6b7b\u522b\u7684\u75db\u82e6\uff01\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6709\u4e9b\u4eba\u80fd\u6491\u5f97\u4e0b\u53bb\uff0c\u6709\u4e9b\u4eba\u6491\u4e0d\u4e0b\u53bb\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u5c0f\u65b9\uff0c\u773c\u4e2d\u5ffd\u7136\u4e5f\u9732\u51fa\u548c\u5415\u4e09\u63d0\u8d77\u201c\u5676\u5c14\u6e21\u91d1\u9c7c\u201d\u65f6\u540c\u6837\u70bd\u70ed\u7684\u8868\u60c5\uff01 \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u5982\u679c\u8981\u8fbe\u5230\u67d0\u4e00\u4e2a\u76ee\u6807\uff0c\u60f3\u505a\u5230\u4ed6\u60f3\u505a\u7684\u4e8b\uff0c\u5c31\u5f97\u6491\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff0c\u201c\u4e0d\u7ba1\u8981\u4ed6\u5fcd\u53d7\u591a\u5927\u7684\u75db\u82e6\uff0c\u4e0d\u7ba1\u8981\u4ed6\u727a\u7272\u4ec0\u4e48\uff0c\u4ed6\u90fd\u5f97\u6491\u4e0b\u53bb\u7684\u3002\u201d \u3000\u3000\u2014\u2014\u4ed6\u7684\u76ee\u6807\u662f\u4ec0\u4e48\uff1f\u4ed6\u60f3\u505a\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u95ee\u8fd9\u4e9b\uff0c\u4ed6\u53ea\u95ee\u73ed\u5bdf\u5df4\u90a3\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u6491\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u80fd\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\u8bdd\u7684\u53e3\u6c14\uff0c\u5c31\u50cf\u662f\u7528\u5229\u5203\u622a\u65ad\u94a2\u9489\u3002 \u3000\u3000\u201c\u6211\u4e00\u5b9a\u8981\u6491\u4e0b\u53bb\uff01\u201d \u3000\u3000\u4ed6\u8bf4\uff0c\u201c\u8ddf\u7740\u6211\u7684\u90a3\u4e9b\u4eba\uff0c\u4e5f\u4e00\u5b9a\u8981\u966a\u6211\u6491\u4e0b\u53bb\uff0c\u4f46\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u56de\u6c5f\u5357\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u53c8\u5f00\u59cb\u523a\u75db\uff0c\u8fd9\u6b21\u662f\u88ab\u73ed\u5bdf\u5df4\u90a3\u523a\u4f24\u7684\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6211\u56de\u6c5f\u5357\uff1f\u201d \u3000\u3000\u4ed6\u53cd\u95ee\uff0c\u201c\u4f60\u8ba4\u4e3a\u6211\u6ca1\u6709\u6cd5\u5b50\u966a\u4f60\u6491\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\uff0c\u53ea\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u4f60\u662f\u4e2a\u597d\u4eba\uff0c\u6240\u4ee5\u4f60\u5e94\u8be5\u56de\u6c5f\u5357\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u5c0f\u65b9\u518d\u95ee\u201c\u4e3a\u4ec0\u4e48\u201d\u3002 \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u51b7\u6de1\u5982\u51b0\u96ea\u6eb6\u5316\u6210\u7684\u6cc9\u6c34\uff1a \u3000\u3000\u201d\u56e0\u4e3a\u6c5f\u5357\u4e5f\u662f\u4e2a\u597d\u5730\u65b9\uff0c\u4e00\u4e2a\u4eba\u751f\u957f\u5728\u591a\u6c34\u591a\u60c5\u7684\u6c5f\u5357\uff0c\u603b\u662f\u6bd4\u8f83\u6e29\u67d4\u591a\u60c5\u4e9b\uff01\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u5730\u8bf4\uff1a\u201c\u8fd9\u91cc\u5374\u662f\u4e00\u7247\u65e0\u60c5\u7684\u5927\u5730\uff0c\u8fd9\u91cc\u7684\u4eba\u8fd8\u6bd4\u4f60\u60f3\u8c61\u4e2d\u66f4\u51b7\u9177\u65e0\u60c5\u3002\u8fd9\u91cc\u7684\u751f\u6d3b\u4f60\u6c38\u8fdc\u90fd\u65e0\u6cd5\u9002\u5e94\uff0c\u8fd9\u91cc\u4e5f\u4e0d\u518d\u6709\u4f60\u503c\u5f97\u7559\u604b\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4ed6\u53c8\u95ee\u5c0f\u65b9\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u56de\u53bb\uff1f\u201d \u3000\u3000\u7a97\u5916\u98ce\u58f0\u547c\u5578\u3002 \u3000\u3000\u6c5f\u5357\u6ca1\u6709\u8fd9\u6837\u7684\u98ce\uff0c\u8fd9\u79cd\u98ce\u522e\u5728\u8eab\u4e0a\uff0c\u5c31\u597d\u50cf\u662f\u5200\u522e\u4e00\u6837\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8bf4\u7684\u8bdd\uff0c\u4e5f\u50cf\u662f\u8fd9\u79cd\u98ce\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u773c\u775b\u4eff\u4f5b\u88ab\u98ce\u6c99\u5439\u5f97\u5f20\u4e0d\u5f00\u4e86\uff0c\u4f46\u662f\u4ed6\u5374\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5c3d\u91cf\u8ba9\u81ea\u5df1\u7ad9\u5f97\u7b14\u76f4\u3002 \u3000\u3000\u201c\u6211\u56de\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u5f53\u7136\u662f\u8981\u56de\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4f69\u5251\u8d70\u51fa\u53bb\u65f6\uff0c\u52a0\u7b54\u5df2\u5907\u597d\u9a6c\u5728\u7b49\u4ed6\uff0c\u5251\u662f\u4ed6\u81ea\u5df1\u7684\u201c\u9b54\u773c\u201d\uff0c\u9a6c\u662f\u4ed6\u81ea\u5df1\u7684\u201c\u8d64\u72ac\u201d\u3002 \u3000\u3000\u4ed6\u6240\u5931\u53bb\u7684\uff0c\u73b0\u5728\u53c8\u5df2\u91cd\u65b0\u5f97\u56de\u3002 \u3000\u3000\u4ed6\u5e26\u7740\u8fd9\u67c4\u5251\uff0c\u9a91\u7740\u8fd9\u5339\u9a6c\uff0c\u6765\u5230\u8fd9\u5730\u65b9\uff0c\u73b0\u5728\u4ed6\u53c8\u5c06\u4f69\u5251\u7b56\u9a6c\u800c\u8fd4\u3002 \u3000\u3000\u8fd9\u4e00\u7247\u5927\u5730\u867d\u7136\u51b7\u9177\u65e0\u60c5\uff0c\u4f46\u662f\u4ed6\u8fd8\u6d3b\u7740\u3002\u4ed6\u662f\u4e0d\u662f\u5df2\u7ecf\u5e94\u8be5\u5f88\u6109\u5feb\u6ee1\u8db3\uff1f\u662f\u4e0d\u662f\u771f\u7684\u5df2\u5f97\u56de\u4ed6\u6240\u5931\u53bb\u7684\u4e00\u5207\uff1f \u3000\u3000\u53c8\u6709\u8c01\u77e5\u9053\u4ed6\u771f\u6b63\u5931\u53bb\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u52a0\u7b54\u5c06\u7f30\u7ef3\u4ea4\u5728\u4ed6\u624b\u91cc\uff0c\u9ed8\u9ed8\u5730\u770b\u7740\u4ed6\uff0c\u4eff\u4f5b\u6709\u5f88\u591a\u8bdd\u8981\u8bf4\uff0c\u5374\u53ea\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u4e09\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u4f60\u7626\u4e86\u3002\u201d\u4ed6\u8bf4\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u56de\u7b54\u9053\uff1a\u201c\u662f\u7684\uff0c\u6211\u7626\u4e86\uff01\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u8c01\u4e5f\u6ca1\u6709\u518d\u5f00\u53e3\uff0c\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5c0f\u65b9\u5c31\u8dc3\u4e0a\u4e86\u9a6c\u978d\u3002 \u3000\u3000\u591c\u8272\u5df2\u4e34\uff0c\u98ce\u66f4\u6025\uff0c\u5927\u5730\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u4ed6\u8dc3\u4e0a\u9a6c\u978d\u65f6\uff0c\u52a0\u7b54\u7684\u4eba\u5df2\u7ecf\u6d88\u5931\u5728\u9ed1\u6697\u91cc\uff0c\u53ea\u5269\u4e0b\u4e86\u4e00\u4e2a\u6de1\u6de1\u7684\u80cc\u5f71\uff0c\u770b\u6765\u4eff\u4f5b\u53c8\u8870\u5f31\u53c8\u75b2\u5026\u3002 \u3000\u3000\u4ed6\u5f88\u60f3\u544a\u8bc9\u52a0\u7b54\uff1a\u201c\u4f60\u4e5f\u7626\u4e86\u3002\u201d \u3000\u3000\u4f46\u662f\u8fd9\u65f6\u5019\u201c\u8d64\u5927\u201d\u5df2\u957f\u5636\u626c\u8e44\uff0c\u51b2\u5165\u4e86\u65e0\u8fb9\u65e0\u9645\u7684\u6025\u98ce\u548c\u591c\u8272\u91cc\u3002 \u3000\u3000\u5b83\u7684\u5636\u58f0\u4e2d\u4eff\u4f5b\u5145\u6ee1\u4e86\u6b22\u6109\uff0c\u56e0\u4e3a\u5b83\u867d\u7136\u662f\u5339\u597d\u9a6c\uff0c\u6bd5\u7adf\u662f\u4e00\u4e00\u5339\u9a6c\uff0c\u8fd8\u4e0d\u80fd\u4e86\u89e3\u4eba\u95f4\u5bc2\u5bde\u5b64\u72ec\u60b2\u4f24\u6101\u82e6\u3002 \u3000\u3000\u4e5f\u56e0\u4e3a\u5b83\u867d\u7136\u53ea\u4e0d\u8fc7\u662f\u4e00\u5339\u9a6c\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u5fd8\u8bb0\uff1b\u65e5\u4e3b\u5bf9\u5b83\u7684\u6069\u60c5\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u8ba4\u5f97\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4f0f\u4e0b\u8eab\uff0c\u7d27\u7d27\u62b1\u4f4f\u4e86\u9a6c\u5934\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4ed6\u5728\u8fd9\u4e16\u754c\u4e0a\u6bd5\u7adf\u8fd8\u6709\u4e00\u4e2a\u670b\u53cb\uff0c\u6c38\u4e0d\u76f8\u5f03\u7684\u670b\u53cb\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u662f\u771f\u6b63\u7684\u670b\u53cb\uff0c\u5c31\u7b97\u662f\u4e00\u5339\u9a6c\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u6c5f\u5357\u4ecd\u9065\u8fdc\uff0c\u9065\u8fdc\u5982\u68a6\uff0c\u6f2b\u6f2b\u7684\u957f\u591c\u521a\u5f00\u59cb\u3002\u8fd9\u65f6\u8fde\u90a3\u4e00\xb7\u70b9\u6de1\u6de1\u7684\u80cc\u5f71\u90fd\u5df2\u6d88\u5931\uff0c\u53ef\u662f\u8fdc\u65b9\u5374\u5df2\u6709\u4e00\u70b9\u661f\u5149\u4eae\u8d77\u3002 \u3000\u3000\u5927\u5730\u867d\u65e0\u60c5\uff0c\u661f\u5149\u5374\u6e29\u67d4\u800c\u660e\u4eae\u3002 \u3000\u3000\u6c5f\u5357\u7684\u661f\u5149\u4e5f\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u2014\u2014\u4f60\u662f\u4e2a\u597d\u4eba\uff0c\u4f46\u662f\u4f60\u592a\u8f6f\u5f31\uff0c\u50cf\u4f60\u8fd9\u79cd\u4eba\uff0c\u5bf9\u6211\u6839\u672c\u6ca1\u6709\u7528\u3002 \u3000\u3000\u2014\u2014\u73b0\u5728\u4f60\u5bf9\u5415\u4e09\u90fd\u6ca1\u6709\u7528\u4e86\uff0c\u4ed6\u968f\u65f6\u90fd\u53ef\u4ee5\u9664\u53bb\u4f60\uff0c\u6211\u4e5f\u4e0d\u5fc5\u518d\u8d39\u529b\u4fdd\u62a4\u4e00\u4e2a\u6ca1\u6709\u7528\u7684\u4eba\uff0c\u6240\u4ee5\u4f60\u6700\u597d\u8d70\u3002 \u3000\u3000\u8fd9\u4e9b\u8bdd\uff0c\u73ed\u5bdf\u5df4\u54ea\u5e76\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u4e5f\u4e0d\u5fc5\u8bf4\u51fa\u6765\u3002\u5c0f\u65b9\u81ea\u5df1\u5f88\u6e05\u695a\u81ea\u5df1\u5728\u522b\u4eba\u5fc3\u76ee\u4e2d\u662f\u4ec0\u4e48\u4efd\u91cf\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e00\u76f4\u5bf9\u4ed6\u4e0d\u9519\uff0c\u53ef\u662f\u4ece\u4ed6\u4eec\u7b2c\u4e00\u6b21\u89c1\u9762\u5f00\u59cb\uff0c\u4ed6\u5c31\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u6210\u4e3a\u670b\u53cb\uff0c\u73ed\u5bdf\u5df4\u90a3\u4ece\u672a\u5c06\u4ed6\u5f53\u4f5c\u670b\u53cb\u3002 \u3000\u3000\u56e0\u4e3a\u73ed\u5bdf\u5df4\u90a3\u6839\u672c\u5c31\u770b\u4e0d\u8d77\u4ed6\u3002 \u3000\u3000\u9664\u4e86\u535c\u9e70\u5916\uff0c\u73ed\u5bdf\u5df4\u90a3\u8fd9\u4e00\u751f\u4e2d\u5f88\u53ef\u80fd\u4ece\u672a\u5c06\u522b\u4eba\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\uff0c\u4f60\u5728\u54ea\u91cc\uff1f \u3000\u3000\u957f\u4ead\u590d\u77ed\u4ead\uff0c\u4f55\u5904\u662f\u5f52\u7a0b\uff1f \u3000\u3000\u6c5f\u5357\u72b9\u8fdc\u5728\u4e07\u6c34\u5343\u5c71\u4e4b\u5916\uff0c\u4f46\u662f\u5c0f\u65b9\u5e76\u6ca1\u6709\u6025\u7740\u8d76\u8def\uff0c\u4ed6\u5e76\u4e0d\u60f3\u8d76\u5230\u6c5f\u5357\u53bb\u7559\u6625\u5929\u3002 \u3000\u3000\u2014\u2014\u56de\u53bb\u4e86\u53c8\u5982\u4f55\uff1f\u6625\u5929\u53c8\u6709\u8c01\u80fd\u7559\u5f97\u4f4f\uff1f \u3000\u3000\u8fdc\u5c71\u7684\u79ef\u96ea\u4ecd\u672a\u6eb6\u5316\uff0c\u9053\u8def\u4e0a\u5374\u5df1\u6ce5\u6cde\u6ee1\u9014\u3002\u524d\u9762\u867d\u7136\u5df2\u6709\u5e02\u9547\u5728\u671b\uff0c\u5929\u8272\u5374\u5df2\u5f88\u6697\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u770b\u6765\u867d\u4e0d\u5065\u58ee\u5374\u5f88\u6709\u529b\u6c14\u7684\u5e74\u9752\u4eba\uff0c\u63a8\u7740\u8f86\u72ec\u8f6e\u8f66\u5728\u524d\u9762\u8d70\u3002\u8f66\u4e0a\u4e00\u8fb9\u5750\u7740\u4ed6\u7684\u59bb\u5b50\u548c\u5973\u513f\uff0c\u4e00\u8fb9\u5806\u7740\u7834\uff1b\u65e5\u7684\u7bb1\u7b3c\u5305\u88b1\uff0c\u59bb\u5b50\u770b\u7740\u5728\u6ce5\u6cde\u4e2d\u8270\u82e6\u63a8\u8f66\u7684\u4e08\u592b\uff0c\u773c\u4e2d\u5145\u6ee1\u7740\u67d4\u60c5\u4e0e\u601c\u60dc\u3002 \u3000\u3000\u8fd9\u79cd\u72ec\u8f6e\u8f66\u5728\u8fd9\u91cc\u5f88\u5c11\u89c1\uff0c\u8fd9\u5bf9\u592b\u59bb\u65e0\u7591\u662f\u4ece\u8fdc\u65b9\u6765\u7684\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4ece\u6c5f\u5357\u6765\u7684\uff0c\u60f3\u5230\u8fd9\u4e2a\u964c\u751f\u7684\u5730\u65b9\u6765\uff0c\u7528\u81ea\u5df1\u7684\u52b3\u529b\u6362\u53d6\u65b0\u7684\u751f\u6d3b\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u5e74\u9752\uff0c\u4ed6\u4eec\u4e0d\u6015\u5403\u82e6\uff0c\u4ed6\u4eec\u8fd8\u6709\u5e74\u9752\u4eba\u72ec\u6709\u7684\u7406\u60f3\u548c\u62b1\u8d1f\u3002 \u3000\u3000\u5c0f\u65b9\u9a91\u7740\u9a6c\u4ece\u540e\u9762\u8d76\u8fc7\u4ed6\u4eec\u65f6\uff0c\u521a\u5de7\u542c\u89c1\u59bb\u5b50\u5728\u95ee\u4e08\u592b\uff1a\u201c\u4fac\u963f\u8981\u606f\u4e00\u606f\uff1f\u201d \u3000\u3000\u201c\u5514\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u4e08\u592b\u5173\u5fc3\u7684\u5e76\u4e0d\u662f\u81ea\u5df1\uff0c\u53ea\u95ee\u4ed6\u59bb\u5b50\uff1a\u201c\u4fac\u683c\u4ed4\u7740\u4e86\u6664\u6ca1\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u8bf4\u7684\u6b63\u662f\u9053\u5730\u7684\u6c5f\u5357\u4e61\u767d\uff0c\u4e61\u97f3\u5165\u8033\uff0c\u5c0f\u65b9\u5fc3\u91cc\u7acb\u523b\u5145\u6ee1\u4e86\u6e29\u6696\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u505c\u4e0b\u6765\uff0c\u95ee\u95ee\u4ed6\u4eec\u6c5f\u5357\u7684\u6d88\u606f\uff0c\u95ee\u95ee\u4ed6\u4eec\u662f\u4e0d\u662f\u9700\u8981\u5e2e\u52a9\u3002 \u3000\u3000\u4f46\u4ed6\u6ca1\u6709\u505c\u4e0b\u6765\u3002\u4ed6\u5fc3\u91cc\u5ffd\u7136\u6709\u4e00\u79cd\u5947\u602a\u800c\u53ef\u6015\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u2014\u2014\u8fd9\u5bf9\u592b\u59bb\u8bf4\u4e0d\u5b9a\u4e5f\u662f\u5415\u4e09\u5c5e\u4e0b\u7684\u6740\u624b\uff0c\u4e08\u592b\u7684\u72ec\u8f6e\u8f66\u628a\u91cc\u5f88\u53ef\u80fd\u85cf\u7740\u81f4\u547d\u7684\u5175\u5203\uff0c\u59bb\u5b50\u62b1\u7740\u5973\u513f\u7684\u624b\u91cc\u4e5f\u5f88\u53ef\u80fd\u968f\u65f6\u90fd\u6709\u81f4\u547d\u7684\u6697\u5668\u6253\u51fa\u6765\uff0c\u5c06\u4ed6\u5c04\u6740\u5728\u9a6c\u8e44\u524d\u3002 \u3000\u3000\u53ea\u6709\u7591\u5fc3\u75c5\u6700\u91cd\u7684\u4eba\u624d\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u65e0\u8bba\u770b\u89c1\u4ec0\u4e48\u4eba\u90fd\u8981\u63d0\u9632\u4e00\u7740\u3002\u3002 \u3000\u3000\u5c0f\u65b9\u672c\u6765\u7edd\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002\u4f46\u662f\u7ecf\u8fc7\u90a3\u4e48\u591a\u6b21\u53ef\u6015\u7684\u4e8b\u4ef6\u4e4b\u540e\uff0c\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u7279\u522b\u5c0f\u5fc3\u8c28\u614e\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u4e5f\u6ca1\u6709\u56de\u5934\uff0c\u4ed6\u53ea\u60f3\u559d\u4e00\u676f\u53ea\u80fd\u89e3\u6e34\u5374\u4e0d\u4f1a\u9189\u7684\u9752\u68f5\u9152\u3002 \u3000\u3000\u8fd9\u4e2a\u5e02\u9547\u662f\u4e2a\u6781\u7e41\u8363\u7684\u5e02\u9547\uff0c\u5c0f\u65b9\u5230\u8fbe\u8fd9\u5e02\u9547\u65f6\u5df2\u7ecf\u662f\u4e07\u5bb6\u706f\u706b\u3002 \u3000\u3000\u5165\u9547\u7684\u5927\u9053\u65c1\uff0c\u6709\u4e00\u5bb6\u5c0f\u9152\u94fa\uff0c\u662f\u4ed6\u770b\u89c1\u7684\u7b2c\u4e00\u5bb6\u9152\u94fa\uff0c\u4e5f\u662f\u6bcf\u4e2a\u8981\u5165\u9547\u7684\u4eba\u5fc5\u7ecf\u4e4b\u5904\u3002 \u3000\u3000\u4e24\u676f\u6de1\u6de1\u7684\u9752\u68f5\u9152\u559d\u4e0b\u53bb\uff0c\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u521a\u624d\u90a3\u79cd\u53ef\u6015\u7684\u60f3\u6cd5\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u90a3\u5bf9\u592b\u59bb\u771f\u662f\u5415\u4e09\u6d3e\u6765\u523a\u6740\u4ed6\u7684\u4eba\uff0c\u521a\u624d\u5df2\u7ecf\u6709\u5f88\u597d\u7684\u673a\u4f1a\u51fa\u624b\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u89c9\u5f97\u6709\u70b9\u540e\u6094\u4e86\uff0c\u5728\u8fd9\u4e2a\u8fdc\u79bb\u6545\u4e61\u5343\u4e07\u91cc\u7684\u5730\u65b9\uff0c\u80fd\u9047\u89c1\u4e00\u4e2a\u4ece\u6545\u4e61\u6765\u7684\u4eba\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u4ed6\u9009\u62e9\u8fd9\u5bb6\u5c0f\u9152\u94fa\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u60f3\u5728\u8fd9\u91cc\u7b49\u4ed6\u4eec\u6765\uff0c\u7eb5\u7136\u542c\u4e0d\u5230\u6545\u4e61\u7684\u6d88\u606f\uff0c\u80fd\u542c\u4e00\u542c\u4e61\u97f3\u4e5f\u662f\u597d\u7684\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u7b49\u5230\u4ed6\u4eec\u3002 \u3000\u3000\u8fd9\u6761\u8def\u6839\u672c\u6ca1\u6709\u5c94\u8def\uff0c\u90a3\u5bf9\u592b\u59bb\u660e\u660e\u662f\u5f80\u8fd9\u5e02\u9547\u6765\u7684\u3002\u4ed6\u4eec\u8d70\u5f97\u867d\u7136\u5f88\u6162\uff0c\u53ef\u662f\u5c0f\u65b9\u8ba1\u7b97\u811a\u7a0b\uff0c\u4ed6\u4eec\u65e9\u5df2\u8be5\u5165\u9547\u4e86\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u4e00\u76f4\u6ca1\u6709\u6765\u3002 \u3000\u3000\u8eab\u5728\u5f02\u4e61\u4e3a\u5f02\u5ba2\uff0c\u5bf9\u6545\u4e61\u4eba\u603b\u96be\u514d\u6709\u79cd\u9664\u4e86\u6d6a\u5b50\u5916\u522b\u4eba\u7edd\u5bf9\u65e0\u6cd5\u4e86\u89e3\u7684\u5fae\u5999\u611f\u60c5\u3002\u5c0f\u65b9\u867d\u4e0d\u8ba4\u5f97\u90a3\u5bf9\u592b\u59bb\uff0c\u5374\u5df2\u7ecf\u5728\u4e3a\u4ed6\u4eec\u62c5\u5fc3\u4e86\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6709\u5230\uff1f\u662f\u4e0d\u662f\u6709\u4e86\u4ec0\u4e48\u610f\u5916\uff1f \u3000\u3000\u2014\u2014\u662f\u4e0d\u662f\u56e0\u4e3a\u90a3\u4e2a\u5df2\u7ecf\u8dcb\u6d89\u8fc7\u5343\u5c71\u4e07\u6c34\u7684\u4e08\u592b\u7ec8\u4e8e\u4e0d\u652f\u5012\u4e0b\uff0c\u8fd8\u662f\u56e0\u4e3a\u90a3\u4e2a\u53ef\u7231\u7684\u5c0f\u5973\u513f\u6709\u4e86\u6025\u75c5\uff1f\u5c0f\u65b9\u51b3\u5b9a\u518d\u7b49\u7247\u523b\uff0c\u5982\u679c\u4ed6\u4eec\u8fd8\u4e0d\u6765\uff0c\u5c31\u6cbf\u7740\u6765\u8def\u56de\u53bb\u770b\u770b\u7a76\u7adf\u3002 \u3000\u3000\u4ed6\u53c8\u7b49\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u770b\u89c1\u4ed6\u4eec\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8def\u4e0a\u7684\u884c\u4eba\u5df2\u7ecf\u5f88\u5c11\u4e86\uff0c\u56e0\u4e3a\u5e73\u5e38\u4eba\u5728\u8fd9\u79cd\u65f6\u5019\u5df2\u7ecf\u5f88\u96be\u5206\u8fa8\u8def\u9014\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u662f\u5e73\u5e38\u4eba\uff0c\u4ed6\u7684\u773c\u529b\u8fdc\u6bd4\u5e73\u5e38\u4eba\u597d\u5f97\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u770b\u89c1\u90a3\u5bf9\u592b\u59bb\uff0c\u5374\u770b\u89c1\u4e86\u4e00\u4e2a\u5355\u8eab\u7684\u5973\u5b50\uff0c\u9a91\u7740\u5339\u9752\u9aa1\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u5929\u8272\u867d\u7136\u5df2\u6697\uff0c\u4ed6\u8fd8\u662f\u53ef\u4ee5\u770b\u5f97\u51fa\u8fd9\u5973\u4eba\u4e0d\u4f46\u5f88\u5e74\u8f7b\u6f02\u4eae\uff0c\u800c\u4e14\u98ce\u59ff\u6781\u7f8e\u3002 \u3000\u3000\u5979\u770b\u6765\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u5341\u516d\u4e03\u5c81\uff0c\u7a7f\u7740\u4ef6\u9752\u5e03\u77ed\u68c9\u8884\uff0c\u4fa7\u7740\u8eab\u5b50\u5750\u5728\u978d\u4e0a\uff0c\u7528\u4e00\u53ea\u624b\u7275\u7740\u7f30\u7ef3\uff0c\u4e00\u53ea\u624b\u62e2\u4f4f\u5934\u53d1\uff0c\u770b\u89c1\u5c0f\u65b9\u65f6\uff0c\u4eff\u4f5b\u7b11\u4e86\u7b11\uff0c\u53c8\u4eff\u4f5b\u6ca1\u6709\u7b11\u3002 \u3000\u3000\u4e00\u5339\u9a6c\u4e00\u6761\u9aa1\u5f88\u5feb\u5c31\u4ea4\u9519\u800c\u8fc7\uff0c\u5c0f\u65b9\u5e76\u6ca1\u6709\u770b\u5f97\u5341\u5206\u6e05\u695a\uff0c\u5374\u89c9\u5f97\u8fd9\u4e2a\u5973\u5b69\u5b50\u4eff\u4f5b\u89c1\u8fc7\uff0c\u53c8\u504f\u504f\u8bb0\u4e0d\u6e05\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u3002 \u3000\u3000\u2014\u2014\u5979\u4e0d\u662f\u6ce2\u5a03\uff0c\u4e0d\u662f\u82cf\u82cf\uff0c\u4e0d\u662f\u201c\u9633\u5149\u201d\uff0c\u4e5f\u4e0d\u662f\u66fe\u7ecf\u5728\u6c5f\u5357\u548c\u5c0f\u65b9\u6709\u8fc7\u4e00\u6bb5\u65e7\u60c5\u7684\u90a3\u4e9b\u5973\u4eba\u3002 \u3000\u3000\u2014\u2014\u5979\u662f\u8c01\u5462\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u518d\u53bb\u60f3\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u5173\u5fc3\u3002 \u3000\u3000\u4e00\u4e2a\u6ca1\u6709\u6839\u7684\u6d6a\u5b50\uff0c\u672c\u6765\u5c31\u65f6\u5e38\u4f1a\u9047\u5230\u4e00\u4e9b\u4f3c\u66fe\u76f8\u8bc6\u7684\u5973\u4eba\u3002 \u3000\u3000\u5026\u9e1f\u5df2\u5165\u6797\uff0c\u65c5\u4eba\u5df2\u6295\u5bbf\uff0c\u8fd9\u6761\u672c\u6765\u5df2\u7ecf\u5f88\u5b89\u9759\u7684\u9053\u8def\u5374\u5ffd\u7136\u4e0d\u5b89\u9759\u4e86\u3002 \u3000\u3000\u9053\u8def\u7684\u524d\u9762\u5ffd\u7136\u6709\u9a9a\u52a8\u7684\u4eba\u58f0\u4f20\u8fc7\u6765\uff0c\u5176\u4e2d\u4eff\u4f5b\u8fd8\u6709\u5b69\u5b50\u5728\u557c\u54ed\u3002 \u3000\u3000\u518d\u5f80\u524d\u8d70\u4e00\u6bb5\u8def\uff0c\u5c31\u53ef\u4ee5\u770b\u89c1\u8def\u65c1\u6709\u706f\u5149\u95ea\u52a8\uff0c\u4e5f\u53ef\u4ee5\u542c\u89c1\u6709\u4eba\u7528\u5145\u6ee1\u60ca\u614c\u6050\u60e7\u4e0e\u6124\u6012\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8c01\u8fd9\u4e48\u72e0\u5fc3\uff1f\u662f\u8c01\uff1f\u201d \u3000\u3000\u4eba\u58f0\u5608\u6742\uff0c\u8bf4\u8bdd\u7684\u4e0d\u6b62\u4e00\u4e2a\uff0c\u5c0f\u65b9\u5e76\u6ca1\u6709\u542c\u6e05\u695a\u4ed6\u4eec\u8bf4\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5fc3\u91cc\u5df2\u7ecf\u6709\u4e86\u79cd\u4e0d\u7965\u7684\u9884\u611f\uff0c\u4eff\u4f5b\u5df2\u7ecf\u770b\u5230\u90a3\u5bf9\u4ece\u6c5f\u5357\u6765\u7684\u9752\u5e74\u592b\u59bb\u5012\u5728\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u8fd9\u5bf9\u592b\u59bb\u679c\u7136\u5df2\u7ecf\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u8def\u65c1\uff0c\u8eab\u4f53\u56db\u80a2\u867d\u7136\u8fd8\u6ca1\u6709\u5b8c\u5168\u51b7\u900f\uff0c\u547c\u5438\u5fc3\u8df3\u5374\u65e9\u5df2\u505c\u6b62\u4e86\u3002 \u3000\u3000\u8def\u65c1\u505c\u7740\u4e00\u8f86\u9a74\u8f66\u3001\u4e24\u5339\u7626\u9a6c\uff0c\u516d\u4e03\u4e2a\u8fdf\u5f52\u7684\u65c5\u4eba\u56f4\u5728\u4ed6\u4eec\u7684\u5c38\u4f53\u65c1\uff0c\u4ed6\u4eec\u7684\u5c0f\u5973\u513f\u5df2\u7ecf\u88ab\u5176\u4e2d\u4e00\u4e2a\u597d\u5fc3\u4eba\u62b1\u8d77\u6765\uff0c\u7528\u4e00\u5757\u51b0\u7cd6\u6b62\u4f4f\u4e86\u5979\u7684\u557c\u54ed\u3002 \u3000\u3000\u5979\u54ed\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u53d7\u4e86\u60ca\u5413\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u60b2\u4f24\u7684\u7f18\u6545\u3002\u56e0\u4e3a\u5979\u8fd8\u5927\u5c0f\uff0c\u8fd8\u4e0d\u61c2\u5f97\u751f\u79bb\u6b7b\u522b\u7684\u60b2\u75db\uff0c\u8fd8\u4e0d\u77e5\u9053\u5979\u7684\u7236\u6bcd\u5df2\u7ecf\u906d\u4e86\u6bd2\u624b\uff0c\u6240\u4ee5\u73b0\u5728\u53ea\u8981\u7528\u4e00\u5757\u51b0\u7cd6\u5c31\u53ef\u4ee5\u8ba9\u5979\u4e0d\u54ed\u4e86\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u82e5\u5e72\u5e74\u4e4b\u540e\uff0c\u5979\u53ea\u8981\u518d\u60f3\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u534a\u591c\u91cc\u90fd\u4f1a\u54ed\u9192\u7684\u3002 \u3000\u3000\u90a3\u65f6\u5c31\u7b97\u5c06\u4e16\u4e0a\u6240\u6709\u7684\u51b0\u7cd6\u90fd\u5806\u5230\u5979\u9762\u524d\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u8ba9\u5979\u4e0d\u54ed\u670d\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u4eba\u5982\u679c\u201c\u65e0\u77e5\u201d\uff0c\u5c31\u6ca1\u6709\u75db\u82e6\uff0c\u6ca1\u6709\u60b2\u54c0\u3002 \u3000\u3000\u2014\u2014\u4f46\u662f\u201c\u65e0\u77e5\u201d\u7684\u672c\u8eab\u5c82\u975e\u5c31\u662f\u4eba\u7c7b\u6700\u5927\u7684\u75db\u82e6\u4e0e\u60b2\u54c0\uff1f \u3000\u3000\u5730\u4e0a\u6ca1\u6709\u8840\uff0c\u4ed6\u4eec\u7684\u5c38\u4f53\u4e0a\u4e5f\u6ca1\u6709\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u5bf9\u5e74\u9752\u7684\u592b\u5987\u600e\u4e48\u4f1a\u5ffd\u7136\u5012\u6bd9\u5728\u8def\u65c1\u3002 \u3000\u3000\u76f4\u5230\u5c0f\u65b9\u5206\u5f00\u4eba\u4e1b\u8d70\u8fdb\u53bb\uff0c\u501f\u8fc7\u4e00\u4e2a\u4eba\u624b\u91cc\u63d0\u7740\u7684\u706f\u7b3c\uff0c\u624d\u770b\u89c1\u4ed6\u4eec\u80f8\u53e3\u8863\u895f\u4e0a\u7684\u4e00\u70b9\u8840\u8ff9\u3002 \u3000\u3000\u81f4\u547d\u7684\u4f24\u53e3\u5c31\u5728\u4ed6\u4eec\u7684\u5fc3\u53e3\u4e0a\uff0c\u662f\u5251\u950b\u523a\u51fa\u7684\u4f24\u53e3\uff0c\u4e00\u523a\u5c31\u5df2\u81f4\u547d\u3002\u8fd9\u4e00\u5251\u4e0d\u4f46\u523a\u5f97\u5e72\u51c0\u5229\u843d\uff0c\u800c\u4e14\u51c6\u786e\u6709\u6548\u3002 \u3000\u3000\u4f46\u662f\u8840\u6d41\u5f97\u5e76\u4e0d\u591a\uff0c\u4f24\u53e3\u4e5f\u4e0d\u6df1\u3002 \u3000\u3000\u2014\u2014\u4e00\u5251\u523a\u51fa\uff0c\u7b97\u51c6\u4e86\u5fc5\u53ef\u81f4\u547d\uff0c\u5c31\u7edd\u4e0d\u518d\u591a\u7528\u4e00\u5206\u529b\u6c14\u3002 \u3000\u3000\u8fd9\u662f\u591a\u4e48\u7cbe\u786e\u7684\u5251\u6cd5\uff0c\u591a\u4e48\u53ef\u6015\uff01 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u60f3\u8d77\u4e86\u4f20\u8bf4\u4e2d\u7684\u4e24\u4f4d\u5947\u4eba\u2014\u2014\u897f\u95e8\u5439\u96ea\u548c\u201c\u4e2d\u539f\u4e00\u70b9\u7ea2\u201d\u3002 \u3000\u3000\u201c\u4e2d\u539f\u4e00\u70b9\u7ea2\u201d\u662f\u695a\u7559\u9999\u90a3\u4e2a\u65f6\u4ee3\u7684\u4eba\uff0c\u662f\u90a3\u4e2a\u65f6\u4ee3\u6700\u53ef\u6015\u7684\u523a\u5ba2\uff0c\u4e5f\u662f\u90a3\u65f6\u6700\u53ef\u6015\u7684\u5251\u5ba2\uff0c\u201c\u6740\u4eba\u4e0d\u89c1\u8840\uff0c\u5251\u4e0b\u4e00\u70b9\u7ea2\u201d\u3002 \u3000\u3000\u4ed6\u4e00\u5251\u523a\u51fa\u4e5f\u7edd\u4e0d\u80af\u591a\u7528\u4e00\u5206\u529b\u6c14\uff0c\u4f46\u5374\u7edd\u5bf9\u51c6\u786e\u6709\u6548\u3002 \u3000\u3000\u897f\u95e8\u5439\u96ea\u662f\u9646\u5c0f\u98ce\u6700\u5c0a\u656c\u7684\u670b\u53cb\uff0c\u4e5f\u662f\u9646\u5c0f\u51e4\u6700\u754f\u60e7\u7684\u5bf9\u624b \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u516b\u7ae0\u3000\u6597\u667a \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u6597\u667a \u3000\u3000\u80fd\u591f\u8ba9\u9646\u5c0f\u51e4\u5c0a\u656c\u754f\u60e7\u5374\u4e0d\u5bb9\u6613\uff0c\u6709\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u897f\u95e8\u5439\u96ea\u7684\u5251\u672f\u5df2\u7ecf\u8d85\u8d8a\u4e86\u201c\u4e2d\u539f\u4e00\u70b9\u7ea2\u201d\uff0c\u5df2\u7ecf\u5230\u8fbe\u5251\u672f\u7684\u5dc5\u5cf0\uff0c\u5230\u8fbe\u4e86\u201c\u65e0\u4eba\u3001\u65e0\u6211\u3001\u65e0\u60c5\u3001\u65e0\u5251\u201d\u7684\u6700\u9ad8\u5883\u754c\u3002 \u3000\u3000\u53ea\u6709\u5230\u8fbe\u4e86\u8fd9\u79cd\u5883\u754c\u7684\u4eba\uff0c\u624d\u80fd\u5c06\u5251\u4e0a\u7684\u529b\u91cf\u63a7\u5236\u5f97\u5982\u6b64\u7cbe\u786e\u3002 \u3000\u3000\u53ef\u662f\u80fd\u591f\u5230\u8fbe\u8fd9\u79cd\u5883\u754c\u7684\u4eba\u7edd\u5bf9\u4e0d\u591a\uff0c\u5230\u8fbe\u8fd9\u79cd\u5883\u754c\u540e\uff0c\u4e5f\u5c31\u7edd\u5bf9\u4e0d\u80af\u968f\u4fbf\u6740\u4eba\u4e86\u3002 \u3000\u3000\u5982\u679c\u4f60\u4e0d\u914d\u8ba9\u4ed6\u62d4\u5251\uff0c\u5c31\u7b97\u8dea\u4e0b\u53bb\u6c42\u4ed6\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u80af\u4f24\u4f60\u6beb\u53d1\u3002 \u3000\u3000\u8fd9\u6b21\u6740\u4eba\u7684\u662f\u8c01\uff1f \u3000\u3000\u4e00\u4e2a\u5df2\u7ecf\u5230\u8fbe\u5dc5\u5cf0\u7684\u5251\u5ba2\uff0c\u53c8\u600e\u4e48\u4f1a\u5bf9\u4e00\u53cc\u5e73\u51e1\u52b3\u82e6\u7684\u592b\u5987\u51fa\u624b\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u770b\u89c1\u8fd9\u5bf9\u592b\u5987\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\uff0c\u66f4\u6ca1\u6709\u4eba\u80fd\u61c2\u5f97\u81f4\u547d\u7684\u8fd9\u4e00\u5251\u662f\u600e\u6837\u7cbe\u786e\u53ef\u6015\u3002 \u3000\u3000\u6240\u4ee5\u6709\u5f88\u591a\u4eba\u90fd\u5728\u95ee\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u4f60\u662f\u8c01\uff1f\u4f60\u662f\u4e0d\u662f\u8ba4\u5f97\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u672c\u6765\u4e5f\u6709\u5f88\u591a\u4e8b\u60f3\u95ee\u8fd9\u4e9b\u4eba\u7684\uff0c\u5374\u6ca1\u6709\u95ee\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53c8\u53d1\u73b0\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e2a\u672c\u6765\u5750\u5728\u72ec\u8f6e\u8f66\u4e0a\uff0c\u62b1\u7740\u5973\u513f\u7684\u5987\u4eba\uff0c\u4eff\u4f5b\u4e5f\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u4e24\u4e2a\u6ca1\u6709\u6839\u7684\u4eba\uff0c\u5728\u9152\u540e\u5fae\u91ba\u65f6\uff0c\u5728\u5bc2\u5bde\u5931\u610f\u65f6\uff0c\u5728\u5f88\u60f3\u627e\u4e2a\u4eba\u503e\u8bc9\u81ea\u5df1\u7684\u611f\u89e6\u7684\u65f6\u5019\uff0c\u5076\u7136\u95f4\u76f8\u805a\u53c8\u5206\u624b\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u4e4b\u540e\uff0c\u4ed6\u4eec\u53c8\u5728\u5076\u7136\u95f4\u76f8\u9047\uff0c\u5f7c\u6b64\u95f4\u90fd\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u5306\u5306\u4e00\u77a5\uff0c\u4e5f\u8bb8\u4e92\u76f8\u6de1\u6de1\u7684\u4e00\u7b11\uff0c\u7136\u540e\u53c8\u5206\u624b\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4eec\u60c5\u613f\u5c06\u6614\u65e5\u90a3\u4e00\u70b9\u6de1\u6de1\u7684\u60c5\u6000\u7559\u5728\u5fc3\u5e95\u3002 \u3000\u3000\u4e00\u70b9\u6de1\u6de1\u7684\u611f\u60c5\uff0c\u4e00\u70b9\u6de1\u6de1\u7684\u54c0\u4f24\uff0c\u591a\u4e48\u6f47\u6d12\uff0c\u591a\u4e48\u7f8e\u4e3d\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u65b9\u73b0\u5728\u5374\u7edd\u5bf9\u6ca1\u6709\u8fd9\u79cd\u611f\u60c5\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u8fd9\u4e2a\u4ed6\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\u7684\u5973\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u4eec\u4e4b\u95f4\u6839\u672c\u5c31\u6ca1\u6709\u90a3\u79cd\u5fae\u5999\u7684\u60c5\u6177\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u5b8c\u5168\u60f3\u4e0d\u8d77\u8fd9\u4e2a\u5973\u4eba\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u7684\uff0c\u5c31\u6b63\u5982\u4ed6\u4e5f\u60f3\u4e0d\u8d77\u521a\u624d\u90a3\u4e2a\u9a91\u7740\u9752\u9aa1\u8d70\u8fc7\u7684\u5c11\u5973\u662f\u8c01\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u4ed6\u5df2\u51c6\u5907\u4e0d\u518d\u53bb\u60f3\u7684\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u60f3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u770b\u5230\u4e86\u8fd9\u4e2a\u5973\u4eba\u7684\u811a\u3002 \u3000\u3000\u5728\u7537\u5973\u4e4b\u95f4\u7684\u5173\u7cfb\u4e2d\uff0c\u201c\u811a\u201d\u7edd\u4e0d\u80fd\u7b97\u662f\u91cd\u8981\u7684\u4e00\u73af\uff0c\u4f46\u5374\u6709\u5f88\u591a\u7537\u4eba\u90fd\u5f88\u6ce8\u610f\u5973\u4eba\u7684\u811a\u3002 \u3000\u3000\u5176\u5b9e\u5c0f\u65b9\u5e76\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u5973\u4eba\u7684\u811a\uff0c\u53ea\u4e0d\u8fc7\u770b\u89c1\u5979\u811a\u4e0a\u7a7f\u7684\u978b\u5b50\u3002 \u3000\u3000\u5979\u7a7f\u7684\u8863\u88f3\u5f88\u6734\u7d20\u5f88\u5e73\u51e1\uff0c\u4e00\u4ef6\u7528\u5ec9\u4ef7\u82b1\u5e03\u505a\u6210\u7684\u77ed\u8884\uff0c\u4e00\u6761\u521a\u597d\u53ef\u4ee5\u76d6\u4f4f\u811a\u7684\u9752\u5e03\u957f\u88d9\u3002 \u3000\u3000\u73b0\u5728\u5979\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u6240\u4ee5\u5979\u7684\u811a\u624d\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u811a\u4e0a\u7a7f\u7684\u662f\u53ea\u9774\u5b50\uff0c\u5f88\u7cbe\u81f4\u5f88\u5c0f\u5de7\u7684\u9774\u5b50\uff0c\u53ea\u8981\u662f\u7565\u6709\u6c5f\u6e56\u7ecf\u9a8c\u7684\u4eba\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u8fd9\u79cd\u9774\u5b50\u91cc\u6709\u4e00\u5757\u4e09\u89d2\u5f62\u7684\u94a2\u94c1\uff0c\u85cf\u5728\u9774\u5b50\u7684\u5c16\u4e0a\u3002 \u3000\u3000\u8fd9\u79cd\u9774\u4e8e\u5c31\u53eb\u505a\u201c\u5251\u9774\u201d\u3002\u5c31\u597d\u50cf\u85cf\u5728\u8896\u4e2d\u7684\u7bad\u4e00\u6837\uff0c\u8fd9\u79cd\u9774\u5b50\u4e5f\u662f\u79cd\u81f4\u547d\u7684\u6b66\u5668\u3002 \u3000\u3000\u7a7f\u8fd9\u79cd\u9774\u7684\u5973\u4eba\uff0c\u901a\u5e38\u90fd\u7ec3\u8fc7\u8fde\u73af\u9e33\u9e2f\u98de\u811a\u4e00\u7c7b\u6b66\u529f\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u60f3\u8d77\u8fd9\u4e2a\u5973\u4eba\u5c31\u662f\u90a3\u5929\u5728\u7cd5\u997c\u5e97\u91cc\u5ffd\u7136\u98de\u8d77\u4e00\u811a\u8e22\u788e\u90a3\u5e74\u9752\u4f19\u8ba1\u54bd\u5589\u7684\u8fab\u5b50\u59d1\u5a18\u3002 \u3000\u3000\u867d\u7136\u5979\u4eca\u5929\u6ca1\u6709\u68b3\u8fab\u5b50\uff0c\u88c5\u675f\u6253\u626e\u90fd\u6bd4\u90a3\u5929\u770b\u6765\u8001\u6c14\u5f97\u591a\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u8fd8\u662f\u76f8\u4fe1\u81ea\u5df1\u7edd\u5bf9\u6ca1\u6709\u770b\u9519\u3002 \u3000\u3000\u2014\u2014\u6240\u4ee5\u8fd9\u5bf9\u592b\u59bb\u7edd\u5bf9\u4e0d\u662f\u4ece\u6c5f\u5357\u6765\u7684\uff0c\u662f\u73ed\u5bdf\u5df4\u90a3\u6d3e\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u5f53\u7136\u4e0d\u662f\u771f\u7684\u592b\u59bb\uff0c\u53ea\u4e0d\u8fc7\u60f3\u5229\u7528\u8fd9\u79cd\u5f62\u5f0f\u6765\u63a9\u62a4\u81ea\u5df1\u7684\u884c\u52a8\u800c\u5df2\u3002 \u3000\u3000\u2014\u2014\u2014\u5bf9\u4ece\u5f02\u4e61\u6765\u7684\u5e74\u9752\u592b\u59bb\uff0c\u5e26\u7740\u4e2a\u55f7\u55f7\u5f85\u54fa\u7684\u5b69\u5b50\uff0c\u8fd9\u5229\u5f62\u5f0f\u65e0\u7591\u662f\u79cd\u6700\u597d\u7684\u63a9\u62a4\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u8fd9\u79cd\u4eba\u7684\u884c\u52a8\u4efb\u52a1\uff0c\u901a\u5e38\u90fd\u662f\u8981\u6740\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u51e0\u70b9\u90fd\u662f\u65e0\u5eb8\u7f6e\u7591\u7684\uff01\u95ee\u9898\u662f\uff1a \u3000\u3000\u4e00\u4e00\u4ed6\u4eec\u8981\u6740\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u4ed6\u4eec\u8981\u6740\u7684\u662f\u5c0f\u65b9\uff0c\u4ed6\u4eec\u521a\u624d\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u624b\uff1f \u3000\u3000\u2014\u2014\u4ed6\u4eec\u521a\u624d\u660e\u660e\u5df2\u7ecf\u6709\u5f88\u597d\u7684\u673a\u4f1a\uff0c\u50cf\u4ed6\u4eec\u8fd9\u79cd\u53d7\u8fc7\u4e25\u683c\u800c\u826f\u597d\u8bad\u7ec3\u7684\u6740\u624b\uff0c\u4ed6\u4eec\u5e94\u8be5\u77e5\u9053\u826f\u673a\u4e00\u5931\u6c38\u4e0d\u518d\u6765\u3002 \u3000\u3000\u8fd9\u95ee\u9898\u6700\u597d\u7684\u7b54\u6848\u662f\uff1a \u3000\u3000\u2014\u2014\u4ed6\u4eec\u8981\u6740\u7684\u4e0d\u662f\u5c0f\u65b9\uff0c\u5f53\u7136\u7edd\u5bf9\u4e0d\u662f\u5c0f\u65b9\uff0c\u56e0\u4e3a\u73ed\u5bdf\u5df4\u90a3\u867d\u7136\u4e0d\u662f\u5c0f\u65b9\u7684\u670b\u53cb\uff0c\u4e5f\u4e0d\u662f\u5c0f\u65b9\u7684\u4ec7\u654c\uff0c\u7edd\u5bf9\u4e0d\u662f\u3002 \u3000\u3000\u2014\u2014\u90a3\u4e48\u4ed6\u4eec\u8981\u6740\u7684\u662f\u8c01\uff1f\u6740\u4ed6\u4eec\u7684\u662f\u8c01\uff1f \u3000\u3000\u2014\u2014\u4ed6\u4eec\u90fd\u662f\u73ed\u5bdf\u5df4\u90a3\u79d8\u5bc6\u8bad\u7ec3\u51fa\u6765\u7684\u6740\u624b\uff0c\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u73ed\u5bdf\u5df4\u90a3\u7edd\u4e0d\u4f1a\u6d3e\u4ed6\u4eec\u51fa\u6765\u6740\u4eba\u7684\u3002 \u3000\u3000\u2014\u2014\u6240\u4ee5\u4ed6\u4eec\u8fd9\u6b21\u4efb\u52a1\u65e0\u7591\u662f\u7edd\u5bf9\u673a\u5bc6\u7edd\u5bf9\u5fc5\u8981\u7684\uff0c\u4ed6\u4eec\u8981\u6740\u7684\u65e0\u7591\u662f\u73ed\u5bdf\u5df4\u90a3\u4e00\u5b9a\u8981\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u73ed\u5bdf\u5df4\u90a3\u7684\u670b\u53cb\u867d\u7136\u4e0d\u591a\uff0c\u4ec7\u654c\u4e5f\u4e0d\u591a\uff0c\u5728\u8fd9\u4e48\u6837\u4e00\u4e2a\u867d\u7136\u7e41\u8363\u5374\u6781\u5e73\u51e1\u7684\u8fb9\u9672\u5c0f\u9547\uff0c\u600e\u4e48\u4f1a\u6709\u4ed6\u4e0d\u60dc\u4ed8\u51fa\u8fd9\u4e48\u5927\u7684\u4ee3\u4ef7\u6765\u523a\u6740\u7684\u4eba\uff1f\u2014\u2014\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f \u3000\u3000\u66f4\u91cd\u8981\u7684\u4e00\u4e2a\u95ee\u9898\u662f\uff1a \u3000\u3000\u2014\u2014\u5728\u8fd9\u4e2a\u867d\u7136\u7e41\u8363\u5374\u6781\u5e73\u51e1\u7684\u5c0f\u9547\u91cc\uff0c\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u80fd\u5bf9\u73ed\u5bdf\u5df4\u90a3\u5c5e\u4e0b\u4e45\u7ecf\u8bad\u7ec3\u7684\u6740\u624b\u4e00\u5251\u523a\u6740\u4e8e\u9053\u65c1\u7684\u5251\u5ba2\uff1f \u3000\u3000\u5bd2\u591c\uff0c\u9006\u65c5\uff0c\u5b64\u706f\u3002 \u3000\u3000\u706f\u4e0b\u6709\u9152\uff0c\u6d4a\u9152\uff0c\u672a\u996e\u7684\u9152\uff0c\u5c0f\u65b9\u5728\u706f\u4e0b\u3002 \u3000\u3000\u8fd8\u6709\u5f88\u591a\u95ee\u9898\u8981\u53bb\u60f3\uff0c\u5f88\u591a\u4ed6\u5fc5\u987b\u53bb\u60f3\u7684\u95ee\u9898\uff0c\u53ef\u662f\u4ed6\u6ca1\u6709\u53bb\u60f3\u3002 \u3000\u3000\u4ed6\u60f3\u5728\u662f\u4e00\u4ef6\u548c\u8fd9\u95ee\u9898\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u7684\u4e8b\uff0c\u4e00\u4e2a\u548c\u8fd9\u4e9b\u95ee\u9898\u5b8c\u5168\u6ca1\u6709\u5173\u8fde\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u60f3\u7684\u662f\u90a3\u4e2a\u6700\u591a\u53ea\u4e0d\u8fc7\u6709\u5341\u516d\u4e03\u5c81\u3001\u7a7f\u7740\u4ef6\u9752\u5e03\u77ed\u68c9\u8884\u3001\u9a91\u7740\u5339\u9752\u9aa1\u4ece\u4ed6\u5bf9\u9762\u8d70\u8fc7\u53bb\u7684\u5355\u8eab\u5973\u5b69\u5b50\u3002 \u3000\u3000\u90a3\u4e2a\u4ed6\u4eff\u4f5b\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\u5374\u53c8\u597d\u50cf\u4ece\u672a\u89c1\u8fc7\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u786e\u4fe1\u81ea\u5df1\u7edd\u5bf9\u4e0d\u4f1a\u770b\u9519\u3002 \u3000\u3000\u90a3\u4e2a\u5973\u5b69\u5b50\u7edd\u5bf9\u6ca1\u6709\u8ddf\u4ed6\u6709\u8fc7\u4e00\u70b9\u5173\u7cfb\u4e00\u70b9\u65e7\u60c5\uff0c\u4f46\u662f\u4ed6\u504f\u504f\u5ffd\u7136\u60f3\u5230\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5f88\u60f3\u53bb\u60f3\u5176\u4ed6\u4e00\u4e9b\u503c\u5f97\u4ed6\u53bb\u60f3\u7684\u4e8b\uff0c\u4f46\u662f\u4ed6\u60f3\u5230\u7684\u5374\u504f\u504f\u603b\u662f\u90a3\u4e2a\u4fa7\u5750\u5728\u9752\u9aa1\u4e0a\uff0c\u90a3\u4e2a\u98ce\u59ff\u6781\u7f8e\u7684\u4eff\u4f5b\u5728\u7b11\u53c8\u4eff\u4f5b\u6ca1\u6709\u7b11\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u2014\u2014\u4e3a\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u662f\u7b11\u4e86\u8fd8\u662f\u6ca1\u6709\u7b11\uff1f\u5982\u679c\u662f\u7b11\uff0c\u53c8\u4e3a\u4ec0\u4e48\u8981\u7b11\uff1f\u4e00\u4e2a\u7d20\u6627\u5e73\u751f\u7684\u5973\u5b69\u5b50\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4e00\u4e2a\u964c\u751f\u7684\u7537\u4eba\u7b11\uff1f\u5982\u679c\u4e0d\u662f\u7b11\uff0c\u4e00\u4e2a\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\uff0c\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4e00\u4e2a\u964c\u751f\u7684\u7537\u4eba\u4f3c\u7b11\u800c\u975e\u7b11\uff1f \u3000\u3000\u5982\u679c\u4ed6\u4eec\u771f\u7684\u76f8\u8bc6\uff0c\u5979\u4e3a\u4ec0\u4e48\u7b11\u4e86\u53c8\u4e0d\u7b11\uff1f\u4e0d\u7b11\u800c\u53c8\u7b11\uff1f \u3000\u3000\u5bd2\u591c\u5df2\u5c06\u5c3d\uff0c\u660f\u706f\u5df2\u5c06\u6b8b\uff0c\u6d4a\u9152\u5df2\u5c3d\uff0c\u6c89\u7761\u7684\u65c5\u4eba\u5df2\u5c06\u9192\uff0c\u672a\u7761\u7684\u65c5\u4eba\u65e9\u5df2\u8be5\u7761\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u5026\u3002 \u3000\u3000\u201c\u6ce2\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u8f7b\u8f7b\u3001\u8f7b\u8f7b\u7684\u4e00\u58f0\u54cd\uff0c\u706f\u82b1\u6563\uff0c\u706f\u706d\u4e86\u3002 \u3000\u3000\u6cb9\u706f\u8fd8\u6ca1\u6709\u71c3\u8d77\uff0c\u5929\u8fd8\u6ca1\u6709\u4eae\uff0c\u5bd2\u51b7\u5b64\u72ec\u5bc2\u5bde\u7a84\u5c0f\u6c61\u6d4a\u5ec9\u4ef7\u7684\u9006\u65c5\u6597\u5ba4\uff0c\u5ffd\u7136\u53d8\u5f97\u66f4\u5bd2\u51b7\u66f4\u9ed1\u6697\u3002 \u3000\u3000\u5c0f\u65b9\u8eba\u5728\u9ed1\u6697\u5904\uff0c\u8eba\u5728\u51b0\u51b7\u7684\u5e8a\u4e0a\uff0c\u5ffd\u7136\u542c\u5230\u4e86\u4e00\u58f0\u54cd\uff0c\u8f7b\u8f7b\u3001\u8f7b\u8f7b\u7684\u4e00\u58f0\u54cd\uff0c\u5c31\u50cf\u662f\u706f\u6b8b\u706f\u706d\u65f6\u90a3\u4e48\u8f7b\u7684\u4e00\u58f0\u54cd\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u542c\u89c1\u522b\u7684\u58f0\u97f3\uff0c\u4ed6\u751a\u81f3\u90fd\u770b\u4e0d\u89c1\uff0c\u4f46\u662f\uff0c\u4ed6\u8eab\u4e0a\u6bcf\u4e00\u201d\u535c\u6709\u611f\u89c9\u7684\u5730\u65b9\u6bcf\u4e00\u4e2a\u6709\u611f\u89c9\u7684\u808c\u8089\u6bcf\u4e00\u6839\u6709\u611f\u89c9\u7684\u795e\u7ecf\u90fd\u5ffd\u7136\u62bd\u7d27\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u611f\u89c9\u5230\u4e00\u80a1\u6740\u6c14\u3002 \u3000\u3000\u6740\u6c14\u662f\u6293\u4e0d\u4f4f\u6478\u4e0d\u5230\u542c\u4e0d\u89c1\u4e5f\u770b\u4e0d\u89c1\u7684\u3002\u53ea\u6709\u6740\u4eba\u65e0\u7b97\u7684\u4eba\u548c\u6740\u4eba\u65e0\u7b97\u7684\u5229\u5668\u624d\u4f1a\u6709\u8fd9\u79cd\u6740\u6c14\u3002 \u3000\u3000\u53ea\u6709\u6740\u4eba\u65e0\u7b97\u7684\u4eba\u5e26\u7740\u8fd9\u79cd\u6740\u4eba\u65e0\u7b97\u7684\u5229\u5668\u8981\u6740\u4eba\u65f6\u624d\u4f1a\u6709\u8fd9\u79cd\u6740\u6c14\u3002 \u3000\u3000\u53ea\u6709\u5c0f\u65b9\u8fd9\u79cd\u4eba\u624d\u4f1a\u611f\u5230\u8fd9\u79cd\u6740\u6c14\u3002\u4ed6\u5168\u8eab\u7684\u808c\u8089\u867d\u7136\u90fd\u5df2\u62bd\u7d27\uff0c\u4f46\u662f\u4ed6\u4e00\u4e0b\u5b50\u5c31\u4ece\u90a3\u4e00\u5f20\u51b0\u51b7\u575a\u786c\u7684\u6728\u677f\u5e8a\u4e0a\u8dc3\u8d77\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u8eab\u5b50\u5982\u540c\u9ca4\u9c7c\u5728\u9ec4\u6cb3\u9006\u6d41\u4e2d\u6253\u633a\u822c\u8dc3\u8d77\u65f6\uff0c\u4ed6\u624d\u770b\u89c1\u4e86\u90a3\u4e00\u9053\u672c\u6765\u53ef\u5c06\u4ed6\u523a\u6740\u5728\u5e8a\u4e0a\u7684\u5251\u5149\u3002 \u3000\u3000\u5982\u679c\u4ed6\u4e0d\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u5982\u679c\u4ed6\u672a\u66fe\u6709\u8fc7\u90a3\u4e9b\u53ef\u6015\u800c\u53c8\u53ef\u8d35\u7684\u7ecf\u9a8c\u3002 \u3000\u3000\u5982\u679c\u4ed6\u6ca1\u6709\u611f\u89c9\u5230\u90a3\u80a1\u6740\u6c14\u3002 \u3000\u3000\u90a3\u4e48\u4ed6\u4e00\u5b9a\u4e5f\u4f1a\u50cf\u90a3\u5bf9\u88ab\u4eba\u523a\u6740\u5728\u9053\u65c1\u7684\u5e74\u9752\u592b\u59bb\u4e00\u6837\uff0c\u73b0\u5728\u4e5f\u5df2\u88ab\u523a\u6740\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u5251\u58f0\u4e00\u54cd\u3002 \u3000\u3000\u5251\u6ca1\u6709\u58f0\u97f3\uff0c\u5c0f\u65b9\u542c\u5230\u7684\u5251\u58f0\uff0c\u662f\u5251\u950b\u523a\u7a7f\u5e8a\u677f\u7684\u58f0\u97f3\u3002\u4ed6\u542c\u5230\u8fd9\u4e00\u58f0\u54cd\u65f6\uff0c\u5251\u950b\u5df2\u7ecf\u523a\u7a7f\u4e86\u6728\u677f\u3002\u73b0\u5728\u5251\u950b\u523a\u7a7f\u7684\u5730\u65b9\uff0c\u672c\u6765\u5c31\u662f\u4ed6\u7684\u5fc3\u810f\uff0c\u53ef\u662f\u73b0\u5728\u5251\u950b\u523a\u7a7f\u7684\u53ea\u4e0d\u8fc7\u662f\u4e00\u5757\u6728\u677f\u3002 \u3000\u3000\u2014\u2014\u4e0d\u7ba1\u8fd9\u628a\u5251\u662f\u4e00\u628a\u4ec0\u4e48\u6837\u7684\u5251\uff0c\u8fd9\u628a\u5251\u4e00\u5b9a\u5728\u4e00\u4e2a\u4eba\u7684\u624b\u4e0a\u3002 \u3000\u3000\u2014\u2014\u4e0d\u7ba1\u8fd9\u4e2a\u4eba\u662f\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u8fd8\u5728\u5e8a\u8fb9\u3002 \u3000\u3000\u5c0f\u65b9\u8eab\u4e8e\u6709\u5982\u9ca4\u9c7c\u6253\u633a\u822c\u8dc3\u8d77\uff0c\u5168\u8eab\u4e0a\u4e0b\u6bcf\u6839\u808c\u8089\u6bcf\u4e00\u5206\u529b\u6c14\u90fd\u5df2\u88ab\u5145\u5206\u8fd0\u7528\u53d1\u6325\u3002\u4ed6\u7684\u8eab\u5b50\u5ffd\u7136\u53c8\u4e00\u7ffb\uff0c\u7136\u540e\u5c31\u76f4\u6251\u4e0b\u53bb\uff0c\u5411\u4e00\u4e2a\u4ed6\u7b97\u51c6\u8be5\u6709\u4eba\u7684\u5730\u65b9\u6251\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u7b97\u9519\u3002 \u3000\u3000\u4ed6\u6293\u4f4f\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5251\u950b\u8fd8\u5728\u5e8a\u677f\u95f4\uff0c\u5251\u67c4\u8fd8\u5728\u4eba\u624b\u3002 \u3000\u3000\u6240\u4ee5\u5c0f\u65b9\u6293\u4f4f\u4e86\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u88ab\u5c0f\u65b9\u6293\u4f4f\u4e00\u6251\uff0c\u8fd9\u4e2a\u4eba\u5012\u4e0b\uff0c\u5c0f\u65b9\u6293\u4f4f\u8fd9\u4e2a\u4eba\uff0c\u6240\u4ee5\u5c0f\u65b9\u4e5f\u5012\u4e0b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u5012\u5728\u5730\u4e0a\uff0c\u540c\u6837\u90fd\u5012\u5728\u5730\u4e0a\uff0c\u53ef\u662f\u4e24\u4e2a\u4eba\u7684\u611f\u89c9\u7edd\u5bf9\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u88ab\u5c0f\u65b9\u6251\u5012\u7684\u8fd9\u4e2a\u4eba\uff0c\u672c\u6765\u4ee5\u4e3a\u5fc5\u53ef\u4e00\u5251\u5c06\u5c0f\u65b9\u523a\u6740\u7684\u4eba\uff0c\u73b0\u5728\u5374\u53cd\u800c\u88ab\u5c0f\u65b9\u6251\u5012\uff0c\u5fc3\u91cc\u4e00\u5b9a\u4f1a\u89c9\u5f97\u975e\u5e38\u60ca\u8bb6\u6050\u60e7\u548c\u5931\u671b\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u611f\u89c9\u66f4\u60ca\u8bb6\u3002\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53d1\u73b0\u88ab\u4ed6\u6251\u5012\u6293\u4f4f\u62b1\u4f4f\u7684\u4eba\uff0c\u5c45\u7136\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u975e\u5e38\u9999\u975e\u5e38\u8f6f\u975e\u5e38\u5a07\u5c0f\u7684\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u770b\u4e0d\u89c1\u8fd9\u4e2a\u5973\u4eba\uff0c\u770b\u4e0d\u89c1\u8fd9\u4e2a\u5973\u4eba\u7a7f\u7684\u662f\u4ec0\u4e48\u8863\u670d\uff0c\u770b\u4e0d\u89c1\u8fd9\u4e2a\u5973\u4eba\u957f\u7684\u662f\u4ec0\u4e48\u6837\uff0c\u4f46\u662f\u4ed6\u770b\u89c1\u4e86\u8fd9\u4e2a\u5973\u4eba\u7684\u773c\u775b\u3002 \u3000\u3000\u4e00\u53cc\u53d1\u4eae\u7684\u773c\u775b\u3002 \u3000\u3000\u4e00\u53cc\u4ed6\u89c9\u5f97\u4eff\u4f5b\u66fe\u7ecf\u770b\u8fc7\u7684\u773c\u775b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u6709\u773c\u775b\uff0c\u4e24\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u77aa\u5f97\u5f88\u5927\uff0c\u4f60\u77aa\u7740\u6211\uff0c\u6211\u77aa\u7740\u4f60\u3002\u5c0f\u65b9\u786e\u4fe1\u81ea\u5df1\u4e00\u5b9a\u89c1\u8fc7\u8fd9\u4e2a\u5973\u4eba\uff0c\u4e00\u5b9a\u89c1\u8fc7\u8fd9\u53cc\u773c\u775b\uff0c\u5374\u53c8\u504f\u504f\u60f3\u4e0d\u8d77\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u89c1\u8fc7\uff0c\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u7684\u3002 \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u5973\u4eba\u5ffd\u7136\u7b11\u4e86\uff0c\u7b11\u5f97\u5f88\u5947\u602a\uff0c\u7b11\u5f97\u5f88\u751c\u3002 \u3000\u3000\u201c\u4f60\u5c45\u7136\u60f3\u4e0d\u8d77\u6211\u662f\u8c01\uff1f\u201d\u5979\u5403\u5403\u5730\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u771f\u4e0d\u662f\u4eba\uff0c\u4f60\u662f\u4e2a\u738b\u516b\u86cb\u3002\u201d \u3000\u3000\u5c31\u5728\u5979\u7b11\u5f97\u6700\u751c\u7684\u65f6\u5019\uff0c\u5979\u624b\u91cc\u53c8\u6709\u4e00\u4ef6\u81f4\u547d\u7684\u6b66\u5668\u5230\u4e86\u5c0f\u65b9\u7684\u54bd\u5589\u95f4\u3002 \u3000\u3000\u6bcf\u4e2a\u5973\u4eba\u90fd\u6709\u624b\u3002 \u3000\u3000\u5973\u4eba\u6709\u5f88\u591a\u79cd\uff0c\u5973\u4eba\u7684\u624b\u6709\u5f88\u591a\u79cd\u3002\u6709\u4e9b\u5f88\u806a\u660e\u7684\u5973\u4eba\uff0c\u5374\u504f\u504f\u957f\u4e86\u53cc\u7b28\u624b\u3002\u6709\u4e9b\u5973\u4eba\u5f88\u79c0\u6c14\uff0c\u5374\u504f\u504f\u957f\u4e86\u53cc\u7c97\u624b\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u4eba\u4e0d\u4f46\u7f8e\uff0c\u800c\u4e14\u5f88\u5e72\u51c0\uff0c\u7a7f\u7684\u8863\u670d\u5c31\u597d\u50cf\u521a\u4ece\u88c1\u7f1d\u624b\u91cc\u62ff\u56de\u6765\u7684\uff0c\u5934\u53d1\u4e5f\u65e0\u7591\u521a\u7ecf\u8fc7\u7cbe\u5fc3\u68b3\u7406\uff0c\u751a\u81f3\u8fde\u978b\u5e95\u4e0a\u90fd\u770b\u4e0d\u5230\u6ce5\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5979\u6307\u7532\u91cc\u5374\u6709\u6ce5\u3002 \u3000\u3000\u5979\u624b\u91cc\u634f\u4f4f\u7684\u662f\u4e00\u6761\u5c0f\u866b\uff0c\u4e00\u6761\u9ed1\u8272\u7684\u5c0f\u866b\u3002\u5979\u7528\u4e24\u6839\u624b\u6307\u7684\u6307\u5c16\u634f\u4f4f\u8fd9\u6761\u5c0f\u866b\uff0c\u628a\u8fd9\u6761\u5c0f\u866b\u653e\u5728\u5c0f\u65b9\u7684\u5589\u7ed3\u4e0a\u3002 \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e2a\u662f\u4ec0\u4e48\uff1f\u201d\u5979\u95ee\u5c0f\u65b9\u3002 \u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u5c0f\u65b9\u6839\u672c\u4e0d\u5fc5\u56de\u7b54\uff0c\u4e5f\u61d2\u5f97\u56de\u7b54\uff0c\u5c31\u7b97\u53ea\u6709\u4e09\u5c81\u5927\u7684\u5b69\u5b50\u4e5f\u77e5\u9053\u8fd9\u662f\u4e00\u6761\u5c0f\u866b\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5374\u8bf4\u9053\uff1a\u201c\u5982\u679c\u4f60\u4ee5\u4e3a\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4e00\u6761\u866b\uff0c\u4f60\u5c31\u5b8c\u5168\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u8fd9\u96be\u9053\u4e0d\u662f\u4e00\u6761\u866b\uff1f\u201d \u3000\u3000\u6293\u866b\u7684\u5973\u5b69\u5b50\u7b11\u4e86\uff1a\u201c\u8fd9\u5f53\u7136\u662f\u4e00\u6761\u866b\uff0c\u5c31\u7b97\u662f\u7b28\u86cb\u4e5f\u5e94\u8be5\u770b\u5f97\u51fa\u8fd9\u662f\u4e00\u6761\u866b\uff0c\u53ea\u4e0d\u8fc7\u866b\u4e5f\u6709\u5f88\u591a\u79cd\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u6761\u866b\u662f\u54ea\u4e00\uff0c\u79cd\uff1f\u201d \u3000\u3000\u201c\u662f\u4f1a\u5403\u4eba\u7684\u90a3\u4e00\u4e00\u79cd\u3002\u201d\u8fd9\u4e2a\u5973\u5b69\u5b50\u8bf4\uff0c\u201c\u53ea\u8981\u6211\u4e00\u653e\u624b\uff0c\u5b83\u5c31\u4f1a\u94bb\u5165\u4f60\u7684\u54bd\u5589\uff0c\u94bb\u8fdb\u4f60\u7684\u8840\u7ba1\u91cc\uff0c\u94bb\u8fdb\u4f60\u7684\u9aa8\u5934\uff0c\u628a\u4f60\u8fd9\u4e2a\u4eba\u7684\u8111\u6d46\u9aa8\u9ad3\u548c\u8840\u5168\u90e8\u5438\u5e72\u3002\u201d \u3000\u3000\u5979\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u4eba\u5403\u9e1f\uff0c\u9e1f\u5403\u866b\uff0c\u8fd9\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\uff0c\u53ef\u662f\u866b\u6709\u65f6\u5019\u4e5f\u4f1a\u5403\u4eba\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u7b11\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u60f3\u8d77\u8fd9\u4e2a\u5973\u5b69\u5b50\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5728\u62c9\u8428\uff0c\u5728\u90a3\u795e\u79d8\u5e84\u4e25\u7684\u53e4\u5bfa\u4e2d\uff0c\u5728\u90a3\u81ea\u4ece\u8fdc\u53e4\u4ee5\u6765\u5c31\u4e0d\u77e5\u8ff7\u60d1\u4e86\u591a\u5c11\u4eba\u7684\u5e7d\u79d8\u706f\u5149\u4e0b\uff1b\u5728\u90a3\u5df2\u88ab\u4fe1\u5f92\u4eec\u7684\u70df\u706b\u718f\u9ed1\u4e86\u7684\u9752\u77f3\u795e\u9f9b\u524d\uff0c\u5e26\u4ed6\u53bb\u770b\u90a3\u9b54\u5973\u5438\u542e\u4eba\u8111\u7684\u58c1\u753b\u3001\u903c\u4ed6\u5728\u753b\u524d\u7acb\u8a93\u7684\u5c31\u662f\u5979\u3002 \u3000\u3000\u5728\u62c9\u8428\uff0c\u5e26\u4ed6\u53bb\u90a3\u795e\u79d8\u7684\u9e1f\u5c4b\u3001\u53bb\u89c1\u72ec\u5b64\u75f4\u7684\u4e5f\u662f\u5979\u3002 \u3000\u3000\u90a3\u65f6\u5979\u662f\u4e2a\u6ee1\u8eab\u6ce5\u7684\u810f\u7537\u5b69\u3002 \u3000\u3000\u73b0\u5728\u5979\u662f\u4e2a\u53c8\u5e72\u51c0\u53c8\u6f02\u4eae\u3001\u53ea\u4e0d\u8fc7\u6307\u7532\u91cc\u6709\u70b9\u6ce5\u7684\u5c0f\u7f8e\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u672c\u6765\u7edd\u4e0d\u53ef\u80fd\u662f\u4e00\u4e2a\u4eba\uff0c\u53ef\u662f\u5c0f\u65b9\u76f8\u4fe1\u81ea\u5df1\u8fd9\u6b21\u4e5f\u7edd\u5bf9\u4e0d\u4f1a\u770b\u9519\u3002 \u3000\u3000\u201c\u6211\u8ba4\u5f97\u4f60\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u6211\u5df2\u7ecf\u8ba4\u51fa\u4f60\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u5e94\u8be5\u8ba4\u5f97\u6211\u3002\u201d\u8fd9\u4e2a\u5973\u5b69\u5b50\u8fde\u4e00\u70b9\u5426\u8ba4\u7684\u610f\u601d\u4e5f\u6ca1\u6709\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u8ba4\u5f97\u6211\uff0c\u4f60\u4e0d\u4f46\u662f\u4e2a\u738b\u516b\u86cb\uff0c\u7b80\u76f4\u662f\u4e00\u6761\u732a\uff0c\u6b7b\u732a\u3002\u201d \u3000\u3000\u5979\u5728\u7b11\uff0c\u597d\u50cf\u662f\u4e00\u4e2a\u5c0f\u5973\u5b69\u5728\u8ddf\u4e00\u4e2a\u5f88\u8981\u597d\u7684\u5c0f\u7537\u5b69\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u4f46\u662f\u5979\u7684\u773c\u775b\u91cc\u5374\u5b8c\u5168\u6ca1\u6709\u7b11\u610f\uff0c\u8fde\u4e00\u70b9\u5f00\u73a9\u7b11\u7684\u6837\u5b50\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u201c\u521a\u624d\u6211\u8bf4\u8fc7\u53ea\u8981\u6211\u4e00\u653e\u624b\uff0c\u8fd9\u6761\u5c0f\u866b\u7acb\u523b\u5c31\u53ef\u4ee5\u628a\u4f60\u5438\u6210\u4e2a\u4eba\u5e72\u3002\u201d\u5979\u95ee\u5c0f\u65b9\uff0c\u201c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u201c\u6211\u4fe1\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u60f3\u8981\u6211\u653e\u624b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5c31\u5148\u653e\u5f00\u6211\u3002\u201d\u8fd9\u4e2a\u5973\u5b69\u5b50\u7528\u5149\u6ed1\u67d4\u8f6f\u7684\u4e0b\u5df4\u8f7b\u8f7b\u78e8\u64e6\u7740\u5c0f\u65b9\u627c\u7740\u5979\u54bd\u5589\u7684\u624b\uff0c\u201c\u8fd9\u6837\u505a\uff0c\u5f88\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u5728\u7b11\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u5df2\u7ecf\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u5973\u5b69\u5b50\u662f\u8c01\u4e86\uff0c\u800c\u4e14\u6709\u5f88\u591a\u672c\u6765\u60f3\u4e0d\u901a\u7684\u4e8b\u60c5\uff0c\u73b0\u5728\u4e5f\u5df2\u7ecf\u60f3\u901a\u4e86\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u5973\u5b69\u5b50\u5728\u9644\u8fd1\uff0c\u72ec\u5b64\u75f4\u65e0\u7591\u4e5f\u5728\u9644\u8fd1\u3002 \u3000\u3000\u2014\u2014\u72ec\u5b64\u75f4\u662f\u73ed\u5bdf\u5df4\u90a3\u7684\u5bf9\u5934\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u73ed\u5bdf\u5df4\u90a3\u8ba4\u4e3a\u6700\u53ef\u6015\u7684\u5bf9\u5934\u3002 \u3000\u3000\u2014\u2014\u90a3\u4e2a\u7a7f\u5251\u9774\u7684\u5973\u4eba\uff0c\u65e0\u7591\u5c31\u662f\u73ed\u5bdf\u5df4\u90a3\u6d3e\u51fa\u6765\u523a\u63a2\u72ec\u5b64\u75f4\u884c\u8e2a\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u4e0d\u662f\u523a\u6740\uff0c\u662f\u523a\u63a2\uff0c\u56e0\u4e3a\u73ed\u5bdf\u5df4\u90a3\u6d3e\u51fa\u6765\u523a\u63a2\u72ec\u5b64\u75f4\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u2014\u2014\u7eb5\u7136\u53ea\u4e0d\u8fc7\u662f\u523a\u63a2\uff0c\u5374\u88ab\u523a\u6740\u5728\u8fd9\u4e2a\u5973\u5b69\u5b50\u7684\u5251\u4e0b\u3002 \u3000\u3000\u6740\u4eba\u7684\u5229\u5251\u5df2\u88ab\u51fb\u843d\uff0c\u81f4\u547d\u7684\u6bd2\u866b\u5374\u4ecd\u5728\u5979\u624b\u91cc\u3002 \u3000\u3000\u5c0f\u65b9\u4ecd\u5728\u7b11\uff0c\u8fd9\u4e2a\u5973\u5b69\u5b50\u5374\u4e0d\u7b11\u4e86\uff0c\u7528\u4e00\u53cc\u53d1\u4eae\u7684\u5927\u773c\u775b\u77aa\u7740\u5c0f\u65b9\uff1a \u3000\u3000\u201c\u6211\u521a\u624d\u8bf4\u7684\u8bdd\u4f60\u542c\u6e05\u695a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6211\u542c\u6e05\u695a\u4e86\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4f60\u653e\u4e0d\u653e\u5f00\u6211\uff1f\u201d \u3000\u3000\u201c\u4e0d\u653e\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u773c\u775b\u91cc\u9732\u51fa\u4e86\u5c16\u9489\u822c\u7684\u5149\uff0c\u72e0\u72e0\u5730\u76ef\u7740\u5c0f\u65b9\uff0c\u72e0\u72e0\u5730\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u60f3\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u653e\uff1f\u201d\u5973\u5b69\u5b50\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u4e09\u70b9\u539f\u56e0\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u7b2c\u4e00\uff0c\u4f60\u662f\u6765\u6740\u6211\u7684\uff0c\u6211\u4e0d\u653e\u624b\uff0c\u6700\u591a\u4e24\u4e2a\u4eba\u4e00\u8d77\u6b7b\u3002\u5728\u6211\u53d8\u6210\u4eba\u5e72\u4e4b\u524d\uff0c\u4f60\u7684\u8116\u5b50\u4e5f\u65ad\u4e86\u3002\u5982\u679c\u6211\u653e\u624b\uff0c\u4f60\u4e00\u5b9a\u4e5f\u4f1a\u653e\u624b\uff0c\u90a3\u4e48\u4f60\u7684\u8116\u5b50\u4e0d\u4f1a\u65ad\uff0c\u6211\u5374\u53d8\u6210\u4eba\u5e72\u4e86\u3002\u201d \u3000\u3000\u201c\u5408\u7406\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\uff0c\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u73b0\u5728\u4f60\u597d\u50cf\u662f\u5728\u5a01\u80c1\u6211\uff0c\u78b0\u5de7\u6211\u521a\u597d\u662f\u4e0d\u559c\u6b22\u88ab\u4eba\u5a01\u80c1\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e09\u5462\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u7b2c\u4e09\u4e86\u3002\u201d\u5c0f\u65b9\u7b54\u9053\uff0c\u201c\u4e0d\u7ba1\u5bf9\u4ec0\u4e48\u4eba\u8bf4\uff0c\u6709\u8fd9\u4e24\u70b9\u539f\u56e0\u90fd\u5df2\u7ecf\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u201c\u96be\u602a\u522b\u4eba\u90fd\u8bf4\u4f60\u662f\u8981\u547d\u7684\u5c0f\u65b9\u3002\u201d\u5979\u770b\u7740\u5c0f\u65b9\uff0c\u201c\u4f60\u5b9e\u5728\u771f\u662f\u5f88\u8981\u547d\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5979\u5ffd\u7136\u505a\u4e86\u4ef6\u5f88\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u7684\u4e8b\uff0c\u5979\u5ffd\u7136\u628a\u624b\u91cc\u8fd9\u6761\u5c0f\u866b\u634f\u6b7b\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u80fd\u591f\u505a\u51fa\u4ef6\u8ba9\u522b\u4eba\u89c9\u5f97\u51fa\u4e4e\u610f\u6599\u7684\u4e8b\uff0c\u901a\u5e38\u90fd\u4f1a\u89c9\u5f97\u5f88\u6109\u5feb\u5f97\u610f\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u5979\u770b\u7740\u5c0f\u65b9\uff0c\u7b11\u5f97\u6109\u5feb\u6781\u4e86\u3002 \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u60f3\u4e0d\u5230\uff0c\u4e3a\u4ec0\u4e48\u6211\u975e\u4f46\u6ca1\u6709\u628a\u8fd9\u6761\u5c0f\u866b\u653e\u5728\u4f60\u7684\u5589\u7ed3\u4e0a\uff0c\u53cd\u800c\u628a\u5b83\u634f\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u786e\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u4e5f\u6ca1\u6709\u8ba9\u5c0f\u65b9\u8d39\u5fc3\u53bb\u60f3\uff0c\u5979\u81ea\u5df1\u8bf4\u51fa\u4e86\u5979\u4e3a\u7684\u662f\u4ec0\u4e48\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u5c31\u7b97\u6211\u8981\u6740\u4f60\uff0c\u4e5f\u662f\u7528\u6211\u7684\u5251\uff0c\u4e0d\u662f\u7528\u8fd9\u6761\u5c0f\u866b\u3002\u201d\u5979\u633a\u8d77\u80f8\uff0c\u50b2\u7136\u9053\uff1a\u201c\u6211\u662f\u5251\u5ba2\uff0c\u5251\u5ba2\u8981\u6740\u4eba\uff0c\u5c31\u5e94\u8be5\u7528\u4ed6\u7684\u5251\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9\u4e00\u70b9\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u5979\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u5251\u5ba2\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u80fd\u591f\u4f7f\u7528\u51fa\u90a3\u79cd\u7cbe\u786e\u6709\u6548\u7684\u5251\u6cd5\uff0c\u523a\u4eba\u7684\u8981\u5bb3\uff0c\u53d6\u4eba\u7684\u6027\u547d\u4e8e\u5239\u90a3\u95f4\uff0c\u90fd\u5df2\u7ecf\u7edd\u5bf9\u53ef\u4ee5\u7b97\u662f\u4e00\u4f4d\u5251\u5ba2\uff0c\u4e00\u6d41\u7684\u5251\u5ba2\uff0c\u53ef\u662f\u73b0\u5728\u8fd9\u4f4d\u4e00\u6d41\u7684\u5251\u5ba2\u5ffd\u7136\u5c31\u50cf\u662f\u4e2a\u5c0f\u5973\u5b69\u4e00\u6837\u5403\u5403\u5730\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4f55\u51b5\u8fd9\u6761\u5c0f\u866b\u53ea\u4e0d\u8fc7\u662f\u6211\u521a\u4ece\u5730\u4e0a\u6349\u5230\u7684\uff0c\u5982\u679c\u628a\u5b83\u653e\u5728\u4f60\u7684\u5589\u7ed3\u4e0a\uff0c\u6700\u591a\u53ea\u4e0d\u8fc7\u4f1a\u89c9\u5f97\u6709\u70b9\u75d2\uff0c\u6700\u591a\u53ea\u4e0d\u8fc7\u4f1a\u5413\u4e00\u8df3\u800c\u5df2\u3002\u201d \u3000\u3000\u8fd9\u6b21\u5c0f\u65b9\u6ca1\u6709\u60f3\u5230\u3002 \u3000\u3000\u88ab\u4eba\u611a\u5f04\u7edd\u4e0d\u662f\u4ef6\u597d\u7b11\u7684\u4e8b\uff0c\u81f3\u5c11\u4ed6\u81ea\u5df1\u4e0d\u4f1a\u89c9\u5f97\u5f88\u597d\u7b11\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u53c8\u8bf4\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u6740\u4f60\uff0c\u53ea\u4e0d\u8fc7\u60f3\u7528\u4f60\u8bd5\u8bd5\u6211\u7684\u5251\u800c\u5df2\uff0c\u8bd5\u8bd5\u6211\u80fd\u4e0d\u80fd\u6740\u5f97\u4e86\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u51b7\u51b7\u7684\u5730\u770b\u7740\u5979\uff0c\u95ee\u5979\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u8bd5\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u4e0d\u80fd\u6740\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u6740\u4e0d\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u60f3\u8ba9\u6211\u6765\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u201c\u8bd5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8bd5\u8bd5\u6211\u662f\u4e0d\u662f\u80fd\u6740\u5f97\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u60f3\uff01\u201d\u8fd9\u4e2a\u5973\u5b69\u5b50\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u6211\u4e00\u70b9\u90fd\u4e0d\u60f3\uff01\u201d \u3000\u3000\u8fd9\u6b21\u5c0f\u65b9\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u4ed6\u5f00\u59cb\u7b11\u7684\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u4e5f\u505a\u4e86\u4ef6\u5f88\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u653e\u5f00\u4e86\u634f\u4f4f\u5979\u8116\u4e8e\u7684\u624b\uff0c\u7528\u529b\u6253\u4e86\u5979\u4e09\u4e0b\u5c41\u80a1\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u53c8\u53eb\u4e86\u8d77\u6765\uff0c\u53eb\u7684\u58f0\u97f3\u66f4\u5927\uff1a \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6253\u6211\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u6740\u6211\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6253\u4f60\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u80fd\u6253\u6211\u8fd9\u4e2a\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u662f\u4e2a\u6dd1\u5973\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u6253\u4f60\u8fd9\u4e2a\u5730\u65b9\u3002\u5982\u679c\u4f60\u662f\u4f4d\u5251\u5ba2\uff0c\u6211\u5f53\u7136\u66f4\u4e0d\u80fd\u6253\u4f60\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u4f60\u5728\u6211\u773c\u91cc\u770b\u6765\u53ea\u53ef\u60dc\u8fd8\u662f\u90a3\u4e2a\u6ee1\u8eab\u6ce5\u5df4\u3001\u6d41\u7740\u9f3b\u6d95\u73a9\u5c0f\u866b\u7684\u810f\u5c0f\u5b69\u3002\u201d\u4ed6\u53c8\u91cd\u91cd\u7684\u5730\u6253\u4e86\u5979\u4e00\u4e0b\uff1a\u201c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u8fd9\u6b21\u5979\u4e5f\u6ca1\u6709\u7b11\u3002 \u3000\u3000\u4e00\u4e2a\u6210\u719f\u7684\u5973\u5b69\u5b50\uff0c\u4e00\u4f4d\u5df2\u7ecf\u80fd\u62d4\u5251\u6740\u4eba\u4e8e\u5239\u90a3\u95f4\u7684\u5251\u5ba2\uff0c\u5c45\u7136\u8fd8\u88ab\u4eba\u770b\u6210\u4e2a\u6d41\u9f3b\u6d95\u7684\u5c0f\u5b69\uff0c\u8fd9\u79cd\u4e8b\u5c31\u7b97\u6709\u4eba\u89c9\u5f97\u53ef\u7b11\uff0c\u5979\u81ea\u5df1\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u53ef\u662f\u5979\u4e5f\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u5979\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u51cc\u7a7a\u98de\u8dc3\uff0c\u51cc\u7a7a\u7ffb\u8eab\uff0c\u51cc\u7a7a\u51fa\u624b\uff0c\u62d4\u8d77\u4e86\u5e8a\u677f\u95f4\u7684\u5251\u3002 \u3000\u3000\u5979\u843d\u5730\u65f6\u5251\u5df2\u5728\u624b\u3002 \u3000\u3000\u6709\u5251\u5728\u624b\uff0c\u5979\u7684\u795e\u60c5\u6001\u5ea6\u6c14\u52bf\u7b11\u5bb9\u90fd\u5df2\u5b8c\u5168\u6539\u53d8\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53c8\u60f3\u8d77\u4e86\u535c\u9e70\u3002\u5728\u4e00\u4e2a\u66f4\u6df1\u5165\u9759\u7684\u665a\u4e0a\uff0c\u5728\u9152\u540e\u5fae\u9187\u65f6\uff0c\u535c\u9e70\u5ffd\u7136\u5bf9\u4ed6\u8bf4\u4e86\u53e5\u8ba9\u4eba\u5f88\u96be\u542c\u5f97\u61c2\u7684\u8bdd\u3002\u201c\u5251\u5ba2\u7684\u5251\uff0c\u6709\u65f6\u5019\u5c31\u50cf\u662f\u94b1\u4e00\u6837\u3002\u201d\u535c\u9e70\u8bf4\uff1a\u201c\u5728\u67d0\u4e9b\u65b9\u9762\u6765\u8bf4\u51e0\u4e4e\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u50cf\u94b1\uff1f\u201d\u5c0f\u65b9\u4e5f\u4e0d\u61c2\uff0c\u201c\u5251\u5ba2\u7684\u5251\u600e\u4e48\u4f1a\u50cf\u662f\u94b1\u5462\uff1f\u201d \u3000\u3000\u201c\u4e00\u4f4d\u5251\u5ba2\u624b\u91cc\u662f\u4e0d\u662f\u6709\u5251\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u4eba\u624b\u91cc\u662f\u4e0d\u662f\u6709\u94b1\u4e00\u3002\u6837\uff0c\u5f80\u5f80\u53ef\u4ee5\u6539\u53d8\u4ed6\u4eec\u7684\u4e00\u5207\u3002\u201d\u8fd9\u53e5\u8bdd\u8bf4\u7684\u8fd8\u662f\u4e0d\u591f\u900f\u5f7b\uff0c\u6240\u4ee5\u535c\u9e70\u53c8\u89e3\u91ca\u9053\uff0c\u201c\u5982\u679c\u4e00\u4f4d\u5251\u5ba2\u624b\u91cc\u6ca1\u6709\u5251\uff0c\u4e00\u4e2a\u4eba\u8eab\u8fb9\u6ca1\u6709\u94b1\uff0c\u4e00\u53e3\u7a7a\u7c73\u888b\u91cc\u6ca1\u6709\u7c73\uff0c\u90fd\u662f\u4e00\u6837\u7ad9\u4e0d\u8d77\u6765\u7684\u3002\u201d\u5c0f\u65b9\u660e\u767d\u4e86\u535c\u9e70\u7684\u610f\u601d\uff0c\u81f3\u4eca\u6ca1\u6709\u5fd8\u8bb0\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u4e2a\u5973\u5b69\u5df2\u7ecf\u7ad9\u8d77\u6765\uff0c\u5979\u7684\u6001\u5ea6\u5ffd\u7136\u5c31\u5df2\u53d8\u5f97\u975e\u5e38\u6c89\u7a33\u51b7\u9177\u9547\u5b9a\u3002 \u3000\u3000\u201c\u521a\u624d\u4f60\u786e\u5b9e\u6709\u673a\u4f1a\u80fd\u6740\u6211\uff0c\u53ea\u662f\u73b0\u5728\u5df2\u7ecf\u4e0d\u540c\u4e86\u3002\u201d\u5979\u8bf4\uff1a\u201c\u521a\u624d\u6211\u5931\u624b\u5e76\u4e0d\u662f\u56e0\u4e3a\u6211\u7684\u5251\u6cd5\u4e0d\u5982\u4f60\uff0c\u73b0\u5728\u4f60\u8fd8\u60f3\u4e0d\u60f3\u518d\u8bd5\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u5251\u4e0d\u5728\u8eab\u4e0a\uff0c\u5728\u5e8a\u4e0a\uff0c\u53ef\u662f\u4ed6\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u62ff\u51fa\u4ed6\u7684\u5251\u3002\u81ea\u4ece\u4ed6\u518d\u6b21\u5f97\u56de\u8fd9\u67c4\u5251\u4e4b\u540e\uff0c\u4ed6\u5c31\u672a\u5c06\u8fd9\u67c4\u5251\u7559\u5728\u4ed6\u4f38\u624b\u62ff\u4e0d\u5230\u7684\u5730\u65b9\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u76ef\u7740\u4ed6\u7684\u624b\uff1a\u201c\u6211\u7ed9\u4f60\u673a\u4f1a\u8ba9\u4f60\u62d4\u5251\u3002\u201d \u3000\u3000\u662f\u62d4\u5251\uff0c\u8fd8\u662f\u4e0d\u62d4\uff1f\u8fd9\u4e0d\u8fc7\u662f\u8f6c\u5ff5\u4e4b\u95f4\u7684\u4e8b\uff0c\u5728\u4e00\u5239\u90a3\u95f4\u5c31\u8981\u4e0b\u51b3\u5b9a\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5c0f\u65b9\u6ca1\u6709\u4e0b\u51b3\u5b9a\uff0c\u5374\u60f3\u8d77\u4e86\u5f88\u591a\u5947\u602a\u7684\u95ee\u9898\u3002\u4ed6\u95ee\u81ea\u5df1\uff1a \u3000\u3000\u2014\u2014\u5982\u679c\u662f\u535c\u9e70\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4f1a\u4e0d\u4f1a\u62d4\u5251\uff1f \u3000\u3000\u4ed6\u7ed9\u81ea\u5df1\u7684\u56de\u7b54\u662f\uff1a\uff5e\u4e00\u4e0d\u4f1a\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u5973\u5b69\u5b50\u8fd8\u4e0d\u80fd\u8ba9\u535c\u9e70\u62d4\u5251\uff0c\u4e5f\u8fd8\u4e0d\u914d\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u95ee\u81ea\u5df1\uff1a\u2014\u2014\u5982\u679c\u662f\u73ed\u5bdf\u5df4\u90a3\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4f1a\u4e0d\u4f1a\u62d4\u5251\uff1f \u3000\u3000\u4ed6\u7ed9\u81ea\u5df1\u7684\u7b54\u6848\u4e5f\u662f\u5426\u5b9a\u7684\uff1a\u2014\u2014\u4e0d\u4f1a\u3002 \u3000\u3000\u56e0\u4e3a\u5982\u679c\u771f\u7684\u662f\u73ed\u5bdf\u5df4\u90a3\u5728\u8fd9\u91cc\uff0c\u8fd9\u4e2a\u5973\u5b69\u5b50\u65e9\u5c31\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u4e86\uff0c\u73ed\u5bdf\u5df4\u90a3\u6839\u672c\u7528\u4e0d\u7740\u62d4\u5251\uff0c\u5979\u5c31\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u4e86\u3002 \u3000\u3000\u2014\u2014\u73ed\u5bdf\u5df4\u90a3\u6740\u4eba\u65f6\u53c8\u4f55\u5fc5\u7531\u81ea\u5df1\u62d4\u5251\uff1f \u3000\u3000\u5c0f\u65b9\u4e0d\u662f\u73ed\u5bdf\u5df4\u90a3\uff0c\u4e5f\u4e0d\u662f\u535c\u9e70\u3002\u4ed6\u62d4\u5251\uff0c\u6162\u6162\u5730\u4f38\u624b\u62d4\u5251\u3002 \u3000\u3000\u4ed6\u7684\u5bf9\u624b\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u8138\u8272\u770b\u7740\u4ed6\u62d4\u51fa\u4ed6\u7684\u201c\u9e70\u773c\u201d\uff0c\u5c45\u7136\u6ca1\u6709\u51fa\u3002 \u3000\u3000\u53cc\u5251\u76f8\u51fb\uff0c\u5fc5\u6709\u706b\u82b1\u8ff8\u51fa\u3002 \u3000\u3000\u2014\u2014\u4e24\u4e2a\u501a\u5251\u4e3a\u547d\u7684\u4eba\u4ed7\u5251\u76f8\u5bf9\u65f6\uff0c\u5176\u95f4\u5fc5\u6709\u5251\u6c14\u3001\u6740\u6c14\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u4e4b\u95f4\u6ca1\u6709\u3002\u5c0f\u65b9\u6709\u5251\u5728\u624b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u624b\u4e2d\u867d\u7136\u6709\u5251\uff0c\u5fc3\u4e2d\u5374\u65e0\u5251\uff0c\u773c\u4e2d\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u201c\u4f60\u8981\u6211\u62d4\u5251\uff0c\u4f60\u60f3\u7528\u5251\u6765\u8bd5\u6211\u3002\u201d\u4ed6\u95ee\u5979\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e8c\u4e5d\u7ae0\u3000\u4ea4\u6613 \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u4ea4\u6613 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u6001\u5ea6\u770b\u7740\u81ea\u5df1\u624b\u91cc\u7684\u5251\uff0c\u8fc7\u4e86\u534a\u5929\u624d\u8bf4\uff1a\u201c\u6211\u4e03\u5c81\u7684\u65f6\u5019\u5148\u7236\u5c31\u66fe\u7ecf\u544a\u8bc9\u8fc7\u6211\uff0c\u5982\u679c\u6211\u60f3\u5b66\u5251\uff0c\u5c31\u4e00\u5b9a\u8981\u8bb0\u4f4f\uff0c\u5251\u662f\u6740\u4eba\u7684\u5229\u5668\uff0c\u4e5f\u662f\u51f6\u5668\uff0c\u4e0d\u5230\u5fc5\u8981\u65f6\uff0c\u5343\u4e07\u4e0d\u53ef\u8f7b\u6613\u62d4\u5251\u3002\u5982\u679c\u4f60\u624b\u91cc\u7684\u5251\u5df2\u51fa\u9798\uff0c\u5c31\u7b97\u4f60\u4e0d\u60f3\u6740\u4eba\uff0c\u522b\u4eba\u4e5f\u4f1a\u56e0\u6b64\u6740\u4f60\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u7684\u5f88\u6709\u9053\u7406\u3002\u201d\u5c0f\u65b9\u540c\u610f\uff0c\u201c\u4e00\u4e2a\u8f7b\u6613\u62d4\u5251\u7684\u4eba\uff0c\u7edd\u4e0d\u662f\u4e2a\u5584\u4e8e\u7528\u5251\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u638c\u4e2d\u7684\u5251\u5df2\u51fa\u9798\uff0c\u672c\u6765\u5f53\u7136\u662f\u51c6\u5907\u51fa\u624b\u7684\u3002\u201d\u8fd9\u4e2a\u5973\u5b69\u5b50\u8bf4\uff1a\u201c\u53ef\u60dc\u73b0\u5728\u6211\u5374\u504f\u504f\u4e0d\u80fd\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\u5979\u3002 \u3000\u3000\u5979\u8fd8\u662f\u6ca1\u6709\u8bf4\u5979\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u51fa\u624b\uff0c\u4e5f\u4e0d\u5fc5\u518d\u8bf4\uff0c\u56e0\u4e3a\u8fd9\u65f6\u5019\u5979\u5df2\u7ecf\u51fa\u624b\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u751f\u6b7b\u547c\u5438\u95f4\u7684\u4e00\u5239\u90a3\uff0c\u5c0f\u65b9\u5ffd\u7136\u53c8\u60f3\u8d77\u4e86\u4e00\u4e9b\u4ed6\u672c\u6765\u4e0d\u8be5\u53bb\u60f3\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u8d77\u4e86\u535c\u9e70\u3002 \u3000\u3000\u5c31\u5728\u90a3\u4eba\u591c\u6df1\u4eba\u9759\u51c9\u5982\u6c34\u7684\u665a\u4e0a\uff0c\u535c\u9e70\u8fd8\u8bf4\u8fc7\u4e00\u4e9b\u8ba9\u4ed6\u6c38\u96be\u5fd8\u8bb0\u7684\u8bdd\u3002 \u3000\u3000\u201c\u5251\u5ba2\u624b\u91cc\u7684\u5251\uff0c\u6709\u65f6\u4e5f\u50cf\u662f\u8d4c\u5f92\u624b\u91cc\u7684\u8d4c\u6ce8\uff0c\u201d\u535c\u9e70\u8bf4\uff1a\u201c\u4e00\u4e2a\u771f\u6b63\u7684\u8d4c\u5f92\u662f\u7edd\u4e0d\u8f7b\u6613\u4e0b\u6ce8\u7684\uff0c\u5982\u679c\u4ed6\u8981\u4e0b\u6ce8\uff0c\u4e0d\u4f46\u8981\u4e0b\u5f97\u51c6\u3001\u4e0b\u5f97\u72e0\uff0c\u800c\u4e14\u4e00\u5b9a\u8fd8\u8981\u5fcd\u3002\u201d \u3000\u3000\u5fcd\u5c31\u662f\u7b49\uff0c\u7b49\u6700\u597d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u535c\u9e70\u53c8\u8bf4\uff1a\u201c\u522b\u4eba\u8ba4\u4e3a\u4f60\u4e0d\u4f1a\u51fa\u624b\u7684\u65f6\u5019\uff0c\u901a\u5e38\u5c31\u662f\u4f60\u6700\u597d\u7684\u673a\u4f1a\u3002\u201d\u8fd9\u4e2a\u5973\u5b69\u5b50\u65e0\u7591\u4e5f\u542c\u5979\u7236\u4eb2\u8bf4\u8fc7\u540c\u6837\u7684\u8bdd\uff0c\u800c\u4e14\u4e5f\u8ddf\u5c0f\u65b9\u4e00\u6837\u7262\u8bb0\u5728\u5fc3\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u8ba9\u5c0f\u65b9\u8ba4\u4e3a\u5979\u4e0d\u4f1a\u51fa\u624b\u4e86\uff0c\u6240\u4ee5\u5979\u4e00\u76f4\u7b49\u5230\u8fd9\u4e00\u523b\u624d\u51fa\u624b\u3002 \u3000\u3000\u9759\u5982\u6cf0\u5c71\uff0c\u52a8\u5982\u8131\u5154\uff0c\u4e0d\u53d1\u5219\u5df2\uff0c\u4e00\u53d1\u5fc5\u4e2d\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u5251\u5ba2\u7684\u539f\u5219\u3002\u4e00\u5251\u51fa\u624b\uff0c\u5c31\u5e94\u8be5\u662f\u81f4\u547d\u7684\u4e00\u5251\uff0c\u523a\u7684\u5fc5\u5b9a\u662f\u5bf9\u65b9\u7684\u8981\u5bb3\uff0c\u4e00\u5b9a\u5e26\u7740\u79cd\u6781\u9738\u9053\u7684\u6740\u6c14\u3002 \u3000\u3000\u5979\u523a\u51fa\u7684\u8fd9\u4e00\u5251\u5374\u4e0d\u662f\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u5979\u7684\u51fa\u624b\u53c8\u5feb\u53c8\u51c6\uff0c\u5979\u7684\u5251\u6cd5\u4e0d\u4f46\u53d8\u5316\u5947\u8be1\u800c\u4e14\u7edd\u5bf9\u6709\u6548\u3002 \u3000\u3000\u4f46\u662f\u5979\u7684\u51fa\u624b\u5374\u4e0d\u591f\u72e0\uff0c\u5251\u6cd5\u4e5f\u4e0d\u591f\u72e0\u3002 \u3000\u3000\u5c0f\u65b9\u867d\u7136\u4ece\u672a\u89c1\u8fc7\u72ec\u5b64\u75f4\u7684\u5251\u6cd5\uff0c\u4e5f\u4ece\u672a\u89c1\u8fc7\u4ed6\u51fa\u624b\uff0c\u4f46\u662f\u5c0f\u65b9\u4e5f\u53ef\u4ee5\u60f3\u8c61\u5f97\u5230\u3002 \u3000\u3000\u53ea\u8981\u770b\u89c1\u8fc7\u72ec\u5b64\u75f4\u7684\u4eba\uff0c\u5927\u6982\u90fd\u53ef\u4ee5\u60f3\u8c61\u5f97\u5230\u4ed6\u7684\u5251\u6cd5\u548c\u51fa\u624b\u662f\u4ec0\u4e48\u6837\u5b50\u7684\u3002 \u3000\u3000\u2014\u2014\u80fd\u770b\u5230\u4ed6\u51fa\u624b\u7684\u4eba\u5f53\u7136\u4e0d\u591a\uff0c\u56e0\u4e3a\u770b\u89c1\u8fc7\u7684\u4eba\u90fd\u5df2\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b69\u5b50\u65e2\u7136\u80fd\u5c06\u73ed\u5bdf\u5df4\u90a3\u5c5e\u4e0b\u7684\u6740\u624b\u4e00\u5251\u523a\u6740\uff0c\u5979\u7684\u5251\u6cd5\u65e0\u7591\u5df2\u5f97\u5230\u72ec\u5b64\u75f4\u5251\u6cd5\u4e2d\u7684\u7cbe\u9ad3\uff0c\u53ef\u662f\u5979\u8fd9\u4e00\u5251\u523a\u51fa\u5374\u4e00\u70b9\u90fd\u4e0d\u50cf\u662f\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u89c9\u5f97\u6709\u70b9\u5947\u602a\u4e86\u3002 \u3000\u3000\u66f4\u5947\u602a\u7684\u662f\uff0c\u5979\u4e00\u5251\u523a\u51fa\u4e4b\u540e\uff0c\u5ffd\u7136\u53c8\u4f4f\u624b\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u770b\u51fa\u6765\u521a\u624d\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u51fa\u624b\uff1f\u201d\u5979\u95ee\u5c0f\u65b9\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u5979\u53c8\u8bf4\uff1a\u201c\u6211\u5b66\u7684\u5251\u6cd5\u662f\u6740\u4eba\u7684\u5251\u6cd5\uff0c\u5982\u679c\u6211\u8981\u6740\u4f60\uff0c\u6211\u7684\u5251\u6cd5\u624d\u6709\u6548\u679c\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53cd\u95ee\u5979\uff1a \u3000\u3000\u201c\u521a\u624d\u4f60\u4e0d\u60f3\u6740\u6211\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u662f\u60f3\u6740\u4f60\uff0c\u7528\u4f60\u7684\u547d\u6765\u796d\u6211\u7684\u5251\u3002\u201d\u5979\u8bf4\uff1a\u201c\u53ef\u662f\u521a\u624d\u6211\u5df2\u7ecf\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d\u3002\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u60f3\u8ddf\u4f60\u505a\u4e2a\u4ea4\u6613\u3002\u201d \u3000\u3000\u201c\u4ea4\u6613\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u4ec0\u4e48\u4ea4\u6613\uff1f\u201d \u7b2c\u4e09\u5341\u7ae0\u3000\u8bd5\u5251 \u7b2c\u4e09\u5341\u7ae0\u3000\u8bd5\u5251 \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5927\u5e74\u8bf4\uff0c\u201c\u8fd9\u4e2a\u4eba\u6700\u8fd1\u597d\u50cf\u5ffd\u7136\u53d8\u5f97\u7279\u522b\u559c\u6b22\u5e72\u51c0\uff0c\u6bcf\u5929\u90fd\u8981\u6d17\u597d\u51e0\u6b21\u51b7\u6c34\u6fa1\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u4eff\u4f5b\u6709\u70b9\u795e\u79d8\uff1a\u201c\u7537\u4eba\u6d17\u51b7\u6c34\u6fa1\u4e0d\u4e00\u5b9a\u662f\u4e3a\u4e86\u7231\u5e72\u51c0\u3002\u201d \u3000\u3000\u5927\u5e74\u77aa\u7740\u773c\u95ee\uff1a\u201c\u4e0d\u662f\u4e3a\u4e86\u7231\u5e72\u51c0\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd8\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4f60\u4e0d\u4f1a\u61c2\u7684\u3002\u201d\u5c0f\u71d5\u8bf4\uff0c\u201c\u5927\u4eba\u7684\u4e8b\uff0c\u4f60\u6700\u597d\u4e0d\u8981\u591a\u95ee\u3002\u201d \u3000\u3000\u5979\u634f\u6b7b\u4e86\u624b\u91cc\u7684\u5c0f\u866b\uff0c\u7ad9\u8d77\u6765\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u5ffd\u7136\u95ee\u5927\u5e74\uff1a\u201c\u4f60\u770b\u4ed6\u6700\u8fd1\u6709\u6ca1\u6709\u4ec0\u4e48\u8ddf\u4ee5\u524d\u4e0d\u4e00\u6837\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u6709\u4e00\u70b9\u3002\u201d\u5927\u5e74\u53c8\u7728\u4e86\u7728\u773c\uff0c\u201c\u6700\u8fd1\u4ed6\u813e\u6c14\u597d\u50cf\u53d8\u5f97\u7279\u522b\u66b4\u8e81\uff0c\u7cbe\u795e\u5374\u597d\u50cf\u6bd4\u4ee5\u524d\u5dee\u4e86\uff0c\u773c\u775b\u603b\u662f\u7ea2\u7ea2\u7684\uff0c\u5c31\u597d\u50cf\u665a\u4e0a\u4ece\u6765\u90fd\u4e0d\u7761\u89c9\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4eca\u5929\u4ed6\u6709\u6ca1\u6709\u95ee\u8d77\u6211\uff1f\u201d \u3000\u3000\u201c\u6700\u8fd1\u8fd9\u4e00\u4e2a\u6708\uff0c\u4ed6\u53ea\u8981\u4e00\u89c1\u5230\u6211\uff0c\u7b2c\u4e00\u53e5\u6d3b\u5c31\u4f1a\u95ee\u6211\u89c1\u5230\u4f60\u6ca1\u6709\u3002\u201d\u5927\u5e74\u9053\uff0c\u201c\u4eca\u5929\u4ed6\u8fd8\u8bf4\u4e00\u5b9a\u8981\u4f60\u53bb\u89c1\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u6709\u975e\u5e38\u975e\u5e38\u91cd\u8981\u7684\u4e8b\u8981\u89c1\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u5c31\u597d\u50cf\u5982\u679c\u770b\u4e0d\u89c1\u4f60\u5c31\u9a6c\u4e0a\u4f1a\u6b7b\u6389\u3002\u201d \u3000\u3000\u5c0f\u71d5\u4e5f\u7b11\u4e86\uff0c\u7b11\u5f97\u53c8\u795e\u79d8\u53c8\u6109\u5feb\u3002\u5927\u5e74\u5fcd\u4e0d\u4f4f\u95ee\u5979\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u6709\u4ec0\u4e48\u4e8b\u627e\u4f60\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u71d5\u5fae\u7b11\uff0c\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u4e0d\u53bb\uff0c\u4ed6\u662f\u4e0d\u662f\u771f\u7684\u4f1a\u6b7b\u6389\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u4e0d\u6b7b\uff0c\u4e00\u5b9a\u4e5f\u5f88\u96be\u8fc7\u3002\u201d\u5c0f\u71d5\u7b11\u5f97\u4eff\u4f5b\u66f4\u6109\u5feb\uff0c\u201c\u6211\u60f3\u4ed6\u6700\u8fd1\u7684\u65e5\u5b50\u4e00\u5b9a\u5f88\u96be\u8fc7\uff0c\u4e00\u5929\u6bd4\u4e00\u5929\u96be\u8fc7\uff0c\u96be\u8fc7\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u7684\u786e\u5f88\u6109\u5feb\uff0c\u53ef\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5c31\u5728\u5979\u7b11\u5f97\u6700\u6109\u5feb\u65f6\uff0c\u5979\u7684\u8138\u5374\u7ea2\u4e86\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u5973\u5b69\u5b50\u901a\u5e38\u90fd\u53ea\u6709\u5728\u5fc3\u52a8\u65f6\u624d\u4f1a\u53d8\u5f97\u8fd9\u4e48\u7ea2\u3002 \u3000\u3000\u2014\u2014\u5979\u65e2\u7136\u4ece\u6765\u4e0d\u52a8\u5fc3\uff0c\u5979\u7684\u8138\u4e3a\u4ec0\u4e48\u4f1a\u7ea2\u6210\u8fd9\u6837\u5b50\uff1f \u3000\u3000\u5927\u5e74\u53c8\u5728\u95ee\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u53bb\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u53bb\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u65f6\u5019\u53bb\uff1f\u201d \u3000\u3000\u201c\u4eca\u5929\u5c31\u53bb\u3002\u201d\u5c0f\u71d5\u5ae3\u7ea2\u7684\u8138\u4e0a\u8840\u8272\u5ffd\u7136\u6d88\u892a\uff0c\u201c\u73b0\u5728\u5c31\u53bb\uff01\u201d \u3000\u3000\u5979\u5ffd\u7136\u63a0\u4e0a\u6811\u68a2\uff0c\u4ece\u4e00\u6839\u6a2a\u679d\u4e0a\u6458\u4e0b\u4e00\u67c4\u5251\u3002\u7b49\u5979\u518d\u8dc3\u4e0b\u6765\u65f6\uff0c\u5979\u7684\u8138\u8272\u5df2\u82cd\u767d\u5982\u7eb8\uff0c\u5c31\u597d\u50cf\u4ef6\u4f5c\u4eec\u7528\u6765\u76d6\u5728\u6b7b\u4eba\u8138\u4e0a\u7684\u90a3\u79cd\u6851\u76ae\u7eb8\u3002 \u3000\u3000\u5927\u5e74\u5403\u60ca\u5730\u770b\u7740\u5979\uff0c\u56e0\u4e3a\u4ed6\u4ece\u6765\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\u4e00\u4e2a\u4eba\u7684\u8138\u5728\u77ac\u606f\u95ee\u6709\u90a3\u4e48\u5927\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u7684\u80c6\u5b50\u4e00\u5411\u4e0d\u5c0f\uff0c\u53ef\u662f\u73b0\u5728\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u5f80\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u597d\u50cf\u751f\u6015\u4ed6\u7684\u8001\u5927\u4f1a\u62d4\u51fa\u5251\u6765\uff0c\u4e00\u5251\u523a\u5165\u4ed6\u7684\u80f8\u819b\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u5bb3\u6015\u5e76\u4e0d\u662f\u6ca1\u6709\u539f\u56e0\u7684\u3002 \u3000\u3000\u53ea\u6709\u8981\u6740\u4eba\u7684\u4eba\uff0c\u624d\u4f1a\u6709\u4ed6\u8001\u5927\u73b0\u5728\u8fd9\u6837\u7684\u8138\u8272\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u9003\u8d70\uff0c\u53ea\u56e0\u4e3a\u4ed6\u77e5\u9053\u8001\u5927\u8981\u6740\u7684\u4eba\u4e0d\u662f\u4ed6\uff0c\u4f46\u662f\u4ed6\u4e5f\u60f3\u4e0d\u5230\u4ed6\u7684\u8001\u5927\u4f1a\u6740\u5c0f\u65b9\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u4ed6\u4eec\u662f\u670b\u53cb\uff0c\u5f88\u597d\u7684\u670b\u53cb\u3002 \u3000\u3000\u5c0f\u71d5\u7684\u624b\u7d27\u63e1\u5251\u67c4\uff0c\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u7684\u817f\u4e3a\u4ec0\u4e48\u5728\u53d1\u6296\uff1f\u201d \u3000\u3000\u201c\u6211\u5bb3\u6015\u3002\u201d\u5927\u5e74\u8bf4\uff0c\u5728\u4ed6\u4eec\u7684\u8001\u5927\u9762\u524d\uff0c\u4ed6\u4ece\u6765\u4e0d\u6562\u8bf4\u8c0e\u3002 \u3000\u3000\u201c\u4f60\u6015\u4ec0\u4e48\uff1f\u201d\u5c0f\u71d5\u53c8\u95ee\uff0c\u201c\u6015\u6211\uff1f\u201d \u3000\u3000\u5927\u5e74\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u5426\u8ba4\uff0c\u4e5f\u4e0d\u6562\u5426\u8ba4\u3002 \u3000\u3000\u5c0f\u71d5\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u7b11\u5bb9\u4e2d\u4eff\u4f5b\u4e5f\u5e26\u7740\u79cd\u6740\u6c14\uff1a \u3000\u3000\u201c\u4f60\u51e0\u65f6\u53d8\u5f97\u8fd9\u4e48\u6015\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u521a\u624d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u2026\u2026\u201d\u5927\u5e74\u5403\u5403\u4ed6\u8bf4\uff0c\u201c\u56e0\u4e3a\u4f60\u521a\u624d\u770b\u8d77\u6765\u5c31\u597d\u50cf\u8981\u6740\u4eba\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u5c0f\u71d5\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u73b0\u5728\u6211\u770b\u8d77\u6765\u96be\u9053\u5c31\u4e0d\u50cf\u8981\u6740\u4eba\u7684\u6837\u5b50\u4e86\uff1f\u201d \u3000\u3000\u5927\u5e74\u4e0d\u6562\u518d\u5f00\u53e3\u3002 \u3000\u3000\u5c0f\u71d5\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u8d70\u5427\uff0c\u6700\u597d\u5feb\u8d70\uff0c\u8d70\u5f97\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d \u3000\u3000\u5979\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5927\u5e74\u5df2\u7ecf\u8dd1\u4e86\u3002 \u3000\u3000\u4ed6\u8dd1\u5f97\u5e76\u4e0d\u5feb\uff0c\u56e0\u4e3a\u4ed6\u4e24\u6761\u817f\u90fd\u5df2\u53d1\u8f6f\uff0c\u8fde\u88e4\u88c6\u90fd\u5df2\u6e7f\u900f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u6709\u4e86\u79cd\u53c8\u5947\u602a\u53c8\u53ef\u6015\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u4ed6\u4eec\u7684\u8001\u5927\u5728\u521a\u624d\u90a3\u4e00\u77ac\u95f4\uff0c\u5f88\u53ef\u80fd\u771f\u7684\u4f1a\u62d4\u51fa\u5251\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u76f4\u5230\u5927\u5e74\u8dd1\u51fa\u53bb\u5f88\u8fdc\u4e4b\u540e\uff0c\u5c0f\u71d5\u624d\u6162\u6162\u5730\u653e\u5f00\u5979\u63e1\u5251\u7684\u624b\u3002 \u3000\u3000\u5979\u7684\u624b\u5fc3\u4e5f\u6e7f\u4e86\uff0c\u6e7f\u6dcb\u6dcb\u7684\u634f\u7740\u6ee1\u628a\u51b7\u6c57\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u5728\u521a\u624d\u90a3\u4e00\u77ac\u95f4\uff0c\u65e0\u8bba\u8c01\u7ad9\u5728\u5979\u9762\u524d\uff0c\u90fd\u53ef\u80fd\u88ab\u5979\u523a\u6740\u5728\u5251\u4e0b\u3002 \u3000\u3000\u5979\u7ec3\u7684\u672c\u6765\u5c31\u662f\u6740\u4eba\u7684\u5251\u6cd5\u3002 \u3000\u3000\u6700\u8fd1\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u5979\u603b\u662f\u6709\u79cd\u60f3\u8981\u6740\u4eba\u7684\u51b2\u52a8\uff0c\u5c24\u5176\u5728\u521a\u624d\u90a3\u4e00\u77ac\u95f4\uff0c\u5979\u5fc3\u91cc\u7684\u6740\u673a\u548c\u6740\u6c14\u5df2\u7ecf\u76f4\u900f\u5251\u950b\u3002 \u3000\u3000\u5979\u77e5\u9053\u5979\u7684\u5251\u6cd5\u5df2\u7ecf\u7ec3\u6210\u4e86\uff0c\u5c0f\u65b9\u7684\u5251\u6cd5\u65e0\u7591\u4e5f\u7ec3\u6210\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u7684\u60c5\u7eea\u90fd\u540c\u6837\u7126\u8e81\uff0c\u90fd\u6709\u540c\u6837\u7684\u51b2\u52a8\u3002 \u3000\u3000\u6b63\u5348\u3002 \u3000\u3000\u5c0f\u71d5\u6ca1\u6709\u53bb\u627e\u5c0f\u65b9\u3002 \u3000\u3000\u5979\u7684\u5251\u4ecd\u5728\u9798\uff0c\u5979\u7684\u4eba\u5df2\u5230\u4e86\u5c71\u5dc5\u3002 \u3000\u3000\u8fd9\u662f\u5ea7\u4ece\u6765\u90fd\u6ca1\u6709\u4eba\u6500\u767b\u8fc7\u7684\u8352\u5c71\uff0c\u6839\u672c\u6ca1\u6709\u8def\u53ef\u4ee5\u5230\u8fbe\u5c71\u5dc5\u3002 \u3000\u3000\u5728\u4e00\u7247\u539f\u59cb\u5bc6\u6797\u540e\uff0c\u4e00\u4e2a\u5e7d\u9759\u7684\u5c71\u5761\u91cc\uff0c\u6709\u4e00\u6c60\u6e05\u6cc9\uff0c\u6b63\u662f\u5c0f\u65b9\u5c4b\u540e\u90a3\u9053\u6cc9\u6c34\u7684\u53d1\u6e90\u5904\u3002 \u3000\u3000\u5c0f\u71d5\u5e38\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u53ea\u6709\u8fd9\u5730\u65b9\uff0c\u624d\u662f\u5b8c\u5168\u5c5e\u4e8e\u5979\u7684\u3002\u53ea\u6709\u5728\u8fd9\u91cc\uff0c\u5979\u624d\u80fd\u81ea\u7531\u81ea\u5728\u5730\u884c\u52a8\u601d\u60f3\uff0c\u968f\u4fbf\u5979\u505a\u4ec0\u4e48\u60f3\u505a\u4ec0\u4e48\uff0c\u90fd\u4e0d\u4f1a\u6709\u4eba\u6765\u6253\u6270\u5979\u3002 \u3000\u3000\u5979\u786e\u4fe1\u9664\u4e86\u5979\u4e4b\u5916\u4ece\u6765\u6ca1\u6709\u4eba\u5230\u8fd9\u91cc\u6765\u8fc7\u3002 \u3000\u3000\u5df2\u7ecf\u662f\u79cb\u5929\u4e86\uff0c\u9633\u5149\u7167\u5c04\u4e0b\u7684\u6cc9\u6c34\u867d\u7136\u6709\u70b9\u6696\u610f\uff0c\u5374\u4e0d\u662f\u5f88\u51b7\u3002\u5979\u4e00\u53ea\u811a\u4f38\u4e0b\u53bb\uff0c\u5168\u8eab\u90fd\u4f1a\u51b7\u5f97\u8f7b\u8f7b\u53d1\u6296\uff0c\u4e00\u76f4\u4ece\u811a\u5e95\u6296\u4eba\u5fc3\u5e95\uff0c\u5c31\u597d\u50cf\u88ab\u4e00\u4e2a\u8584\u60c5\u7684\u60c5\u4eba\u7528\u624b\u634f\u4f4f\u3002 \u3000\u3000\u5979\u559c\u6b22\u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u5bc6\u6797\u91cc\u6709\u5757\u5ca9\u77f3\uff0c\u5ca9\u77f3\u4e0b\u85cf\u7740\u4e2a\u5305\u88b1\uff0c\u662f\u5979\u85cf\u5728\u90a3\u91cc\u7684\uff0c\u5df2\u7ecf\u85cf\u4e86\u5f88\u4e45\uff0c\u73b0\u5728\u624d\u62ff\u51fa\u6765\u3002 \u3000\u3000\u5305\u88b1\u91cc\u662f\u5979\u7684\u8863\u670d\uff0c\u4ece\u8d34\u8eab\u7684\u5185\u8863\u5230\u5916\u9762\u7684\u8863\u88e4\u90fd\u5b8c\u5907\u65e0\u7f3a\uff0c\u6bcf\u4e00\u4ef6\u90fd\u662f\u5d2d\u65b0\u7684\uff0c\u90fd\u662f\u7528\u7eaf\u4e1d\u505a\u6210\u7684\uff0c\u6e29\u8f6f\u800c\u8f7b\u67d4\uff0c\u5c31\u597d\u50cf\u5c11\u5973\u7684\u76ae\u80a4\u3002 \u3000\u3000\u5c31\u597d\u50cf\u5979\u81ea\u5df1\u7684\u76ae\u80a4\u3002 \u3000\u3000\u5979\u628a\u5305\u88b1\u91cc\u7684\u8863\u670d\u4e00\u4ef6\u4ef6\u62ff\u51fa\u6765\uff0c\u5728\u6c60\u65c1\u4e00\u5757\u5df2\u7ecf\u7528\u6c60\u6c34\u6d17\u5e72\u51c0\u7684\u77f3\u5934\u4e0a\u4e00\u4ef6\u4ef6\u5c55\u5e73\u644a\u5f00\uff0c\u518d\u7528\u5979\u7684\u5251\u538b\u4f4f\u3002 \u3000\u3000\u7136\u540e\u5979\u5c31\u8131\u4e0b\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u89e3\u5f00\u4e86\u7d27\u675f\u5728\u5979\u524d\u80f8\u7684\u5e03\u4e2d\uff0c\u8d64\u88f8\u88f8\u5730\u8dc3\u5165\u90a3\u4e00\u6c60\u53c8\u6e29\u6696\u53c8\u5bd2\u51b7\u7684\u6cc9\u6c34\u91cc\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u88ab\u4e00\u4e2a\u53c8\u591a\u60c5\u53c8\u65e0\u60c5\u7684\u60c5\u4eba\u7d27\u7d27\u62e5\u62b1\u4f4f\u3002 \u3000\u3000\u5979\u7684\u80f8\u7acb\u523b\u575a\u633a\uff0c\u5979\u7684\u817f\u7acb\u523b\u7ef7\u7d27\u3002 \u3000\u3000\u5979\u559c\u6b22\u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u5979\u95ed\u8d77\u773c\u775b\uff0c\u8f7b\u629a\u81ea\u5df1\uff0c\u53ea\u6709\u5979\u81ea\u5df1\u624d\u77e5\u9053\u5979\u5df2\u7ecf\u662f\u4e2a\u591a\u4e48\u6210\u719f\u7684\u5973\u4eba\u3002 \u3000\u3000\u6cc9\u6c34\u4ece\u8fd9\u91cc\u6d41\u4e0b\u53bb\uff0c\u6d41\u5230\u5c0f\u65b9\u7684\u6728\u5c4b\u540e\u3002 \u3000\u3000\u5979\u5ffd\u7136\u60f3\u5230\u5c0f\u65b9\u73b0\u5728\u5f88\u53ef\u80fd\u4e5f\u5728\u7528\u8fd9\u9053\u6cc9\u6c34\u51b2\u6d17\u81ea\u5df1\u3002 \u3000\u3000\u5979\u5fc3\u91cc\u5ffd\u7136\u53c8\u6709\u4e86\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u611f\u89c9\uff0c\u4ece\u5979\u7684\u5fc3\u5e95\u4e00\u76f4\u523a\u6fc0\u5230\u5979\u7684\u811a\u5e95\u3002 \u3000\u3000\u5348\u540e\u3002 \u3000\u3000\u5c0f\u65b9\u6e7f\u6dcb\u6dcb\u5730\u4ece\u4ed6\u6728\u5c4b\u540e\u7684\u6cc9\u6c34\u4e2d\u8dc3\u8d77\uff0c\u8ba9\u51b7\u98d5\u98d5\u7684\u79cb\u98ce\u628a\u4ed6\u4eba\u8eab\u5439\u5e72\u3002 \u3000\u3000\u5728\u4ed6\u5c11\u5e74\u65f6\u4ed6\u5c31\u5e38\u7528\u8fd9\u79cd\u6cd5\u5b50\u6765\u6291\u5236\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u800c\u4e14\u901a\u5e38\u90fd\u5f88\u6709\u6548\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u7b49\u5230\u4ed6\u5168\u8eab\u90fd\u5df2\u4e8e\u900f\u51b7\u900f\u540e\uff0c\u4ed6\u7684\u5fc3\u4ecd\u662f\u706b\u70ed\u7684\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u7ec3\u6210\u4e86\u72ec\u5b64\u75f4\u7684\u5251\u6cd5\uff0c\u6240\u4ee5\u53d8\u5f97\u4e5f\u50cf\u72ec\u5b64\u75f4\u4e00\u6837\uff0c\u6bcf\u9694\u4e00\u6bb5\u65e5\u5b50\uff0c\u5982\u679c\u4e0d\u6740\u4eba\uff0c\u7cbe\u6c14\u5c31\u65e0\u6cd5\u53d1\u6cc4\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u4ed4\u7ec6\u60f3\u8fc7\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u53bb\u60f3\u3002 \u3000\u3000\u53ea\u7a7f\u4e0a\u6761\u728a\u9f3b\u88e4\uff0c\u4ed6\u5c31\u63d0\u8d77\u4ed6\u7684\u5251\u5954\u5165\u4ed6\u7ec3\u5251\u7684\u67ab\u6797\u3002 \u3000\u3000\u8fd9\u7247\u67ab\u6797\u4e5f\u50cf\u5c71\u524d\u7684\u90a3\u7247\u67ab\u6797\u4e00\u6837\uff0c\u53f6\u5b50\u90fd\u7ea2\u4e86\uff0c\u7ea2\u5982\u706b\u3002 \u3000\u3000\u7ea2\u5982\u8840\u3002 \u3000\u3000\u5c0f\u65b9\u62d4\u5251\uff0c\u5251\u4e0a\u7684\u201c\u9b54\u773c\u201d\u4eff\u4f5b\u6b63\u5728\u77aa\u7740\u4ed6\uff0c\u4eff\u4f5b\u5df2\u770b\u900f\u4e86\u4ed6\u7684\u5fc3\uff0c\u770b\u51fa\u4e86\u4e45\u5df2\u9690\u85cf\u5728\u4ed6\u5fc3\u5e95\u5374\u4e00\u76f4\u88ab\u6291\u5236\u7740\u7684\u90aa\u5ff5\u3002 \u3000\u3000\u4e00\u8fd9\u672c\u6765\u5c31\u662f\u4eba\u7c7b\u6700\u539f\u59cb\u7684\u7f6a\u6076\uff0c\u4f60\u53ef\u4ee5\u63a7\u5236\u5b83\uff0c\u5374\u65e0\u6cd5\u5c06\u5b83\u6d88\u706d\u3002 \u3000\u3000\u5c0f\u65b9\u4e00\u5251\u523a\u4e86\u51fa\u53bb\uff0c\u523a\u7684\u662f\u4e00\u68f5\u6811\u3002 \u3000\u3000\u6811\u4e0a\u5df2\u5c06\u51cb\u843d\u7684\u6728\u53f6\u8fde\u4e00\u7247\u90fd\u6ca1\u6709\u843d\u4e0b\u6765\uff0c\u53ef\u662f\u4ed6\u7684\u5251\u950b\u5df2\u523a\u5165\u4e86\u6811\u5e72\u3002 \u3000\u3000\u5982\u679c\u6811\u4e5f\u6709\u5fc3\uff0c\u65e0\u7591\u5df2\u88ab\u8fd9\u4e00\u5251\u523a\u7a7f\u3002 \u3000\u3000\u5982\u679c\u4ed6\u523a\u7684\u662f\u4eba\uff0c\u8fd9\u4e00\u5251\u65e0\u7591\u662f\u81f4\u547d\u7684\u4e00\u5251\uff01 \u3000\u3000\u4ed6\u7684\u624b\u4ecd\u7136\u7d27\u63e1\u5251\u67c4\uff0c\u624b\u80cc\u4e0a\u9752\u7b4b\u4e00\u6839\u6839\u51f8\u8d77\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u6761\u6bd2\u86c7\u3002 \u3000\u3000\u2014\u2014\u4ed6\u5fc3\u91cc\u662f\u4e0d\u662f\u4e5f\u6709\u6761\u6bd2\u86c7\u76d8\u65cb\u5728\u5fc3\u5e95\uff1f \u3000\u3000\u4ed6\u7684\u5251\u8fd8\u6ca1\u6709\u62d4\u51fa\u6765\uff0c\u5c31\u542c\u89c1\u6709\u4eba\u5728\u4e3a\u4ed6\u62cd\u624b\uff0c\u4ed6\u56de\u8fc7\u5934\uff0c\u5c31\u770b\u89c1\u4e86\u9f50\u5c0f\u71d5\u3002 \u3000\u3000\u5c0f\u71d5\u659c\u501a\u5728\u5979\u8eab\u540e\u7684\u4e00\u68f5\u6811\u4e0b\uff0c\u4ece\u6811\u68a2\u6f0f\u4e0b\u7684\u9633\u5149\uff0c\u521a\u7167\u4e0a\u5979\u7684\u8138\u3002 \u3000\u3000\u201c\u606d\u559c\u4f60\u3002\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u7684\u5251\u6cd5\u5df2\u7ecf\u7ec3\u6210\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u770b\u7740\u5979\u3002 \u3000\u3000\u5979\u7684\u8138\u660e\u8273\u6e05\u723d\uff0c\u8eab\u4e0a\u7a7f\u7740\u7684\u8863\u670d\u5c31\u50cf\u662f\u76ae\u80a4\u822c\u7d27\u8d34\u5728\u5979\u575a\u633a\u7684\u80f8\u819b\u548c\u67d4\u8f6f\u7684\u8170\u80a2\u4e0a\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u8fd9\u4e48\u6837\u770b\u5979\uff0c\u53ef\u662f\u4ed6\u5df2\u7ecf\u770b\u89c1\u4e86\u4e00\u4e9b\u4ed6\u672c\u6765\u4e0d\u8be5\u770b\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u5f02\u6837\u7684\u8868\u60c5\uff0c\u8fde\u547c\u5438\u90fd\u53d8\u7c97\u4e86\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u95ee\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u7684\u5251\u6cd5\u662f\u4e0d\u662f\u4e5f\u7ec3\u6210\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u6ca1\u6709\u9003\u907f\u4ed6\u7684\u76ee\u5149\uff0c\u4e5f\u6ca1\u6709\u9003\u907f\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5979\u8bf4\uff0c\u201c\u6211\u7684\u5251\u6cd5\u4e5f\u53ef\u4ee5\u7b97\u662f\u7ec3\u6210\u4e86\uff0c\u56e0\u4e3a\u4f60\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u53ef\u4ee5\u6559\u7ed9\u6211\u3002\u201d \u3000\u3000\u5979\u7684\u56de\u7b54\u4e0d\u4f46\u76f4\u63a5\u5e72\u8106\uff0c\u800c\u4e14\u8bf4\u5f97\u5f88\u7edd\u3002 \u3000\u3000\u5c0f\u65b9\u5c3d\u91cf\u4e0d\u8ba9\u81ea\u5df1\u518d\u53bb\u770b\u90a3\u4e9b\u4e00\u4e2a\u5973\u4eba\u672c\u6765\u4e0d\u8be5\u8ba9\u7537\u4eba\u770b\u89c1\u7684\u5730\u65b9\u3002 \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d\u4ed6\u8bf4\u3002 \u3000\u3000\u201c\u4f60\u660e\u767d\uff1f\u201d\u5979\u95ee\u4ed6\uff0c\u201c\u4f60\u8bf4\u6211\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u53ef\u4ee5\u6559\u7ed9\u4f60\uff0c\u4f60\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53ef\u4ee5\u6559\u7ed9\u6211\uff0c\u6240\u4ee5\u6211\u4eec\u7684\u4ea4\u6613\u5df2\u7ed3\u675f\u3002\u201d \u3000\u3000\u4ea4\u6613\u7ed3\u675f\uff0c\u8fd9\u79cd\u751f\u6d3b\u4e5f\u5df2\u7ed3\u675f\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u4e5f\u5df2\u65ad\u7edd\u3002 \u3000\u3000\u5c0f\u65b9\u5c3d\u91cf\u63a7\u5236\u81ea\u5df1\uff1a \u3000\u3000\u201c\u6211\u627e\u4f60\u6765\uff0c\u5c31\u4e3a\u4e86\u8981\u544a\u8bc9\u4f60\uff0c\u6211\u5df2\u7ecf\u51c6\u5907\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u8d70\u3002\u201d\u5c0f\u71d5\u9053\uff0c\u201c\u81f3\u5c11\u73b0\u5728\u8fd8\u4e0d\u80fd\u8d70\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4eec\u8fd8\u8981\u53bb\u627e\u72ec\u5b64\u75f4\u3002\u201d \u3000\u3000\u6ca1\u6709\u72ec\u5b64\u75f4\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u4e2a\u4ea4\u6613\uff0c\u73b0\u5728\u4ed6\u4eec\u7684\u4ea4\u6613\u867d\u7136\u5df2\u7ed3\u675f\uff0c\u53ef\u662f\u4ed6\u4eec\u548c\u72ec\u5b64\u75f4\u4e4b\u95f4\u5374\u4ecd\u7136\u6709\u7b14\u5e10\u8981\u7b97\u6e05\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u4eec\u4e24\u4e2a\u4eba\u4e4b\u95f4\u6700\u5c11\u8981\u6709\u4e00\u4e2a\u4eba\u53bb\u627e\u4ed6\u3002\u201d\u5c0f\u71d5\u76ef\u7740\u5c0f\u65b9\uff0c\u201c\u4e5f\u53ea\u80fd\u4e00\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u662f\u6211\uff0c\u4f60\u662f\u4f60\uff0c\u6211\u4eec\u8981\u627e\u4ed6\u7684\u539f\u56e0\u672c\u6765\u5c31\u4e0d\u4e00\u6837\u3002\u201d\u5c0f\u71d5\u8138\u4e0a\u7684\u9633\u5149\u5df2\u7ecf\u7167\u5230\u522b\u7684\u5730\u65b9\u53bb\u4e86\uff0c\u5979\u7684\u8138\u8272\u82cd\u767d\u3001\u58f0\u97f3\u51b0\u51b7\u3002 \u3000\u3000\u5979\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u4eec\u4e4b\u95f4\u672c\u6765\u5c31\u6ca1\u6709\u4e00\u70b9\u5173\u7cfb\uff0c\u6211\u7684\u4e8b\u5f53\u7136\u8981\u6211\u81ea\u5df1\u53bb\u89e3\u51b3\uff0c\u4f60\u4e0d\u80fd\u4ee3\u66ff\u6211\uff0c\u6211\u4e5f\u4e0d\u80fd\u4ee3\u66ff\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u4f60\u53bb\uff0c\u8fd8\u662f\u6211\u53bb\uff1f\u201d \u3000\u3000\u201c\u8c01\u6d3b\u7740\uff0c\u8c01\u5c31\u53bb\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u4e24\u4e2a\u4eba\u597d\u50cf\u8fd8\u5168\u90fd\u6d3b\u7740\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u6211\u4eec\u4e4b\u95f4\u5fc5\u5b9a\u6709\u4e2a\u4eba\u6d3b\u4e0d\u957f\u7684\u3002\u201d\u5c0f\u71d5\u7684\u77b3\u5b54\u5728\u6536\u7f29\uff0c\u201c\u6211\u770b\u5f97\u51fa\u7247\u523b\u540e\u6211\u4eec\u4e4b\u95f4\u5c31\u6709\u4e2a\u4eba\u4f1a\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u6b7b\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8c01\u8d25\u4e86\uff0c\u8c01\u5c31\u8981\u6b7b\u3002\u201d\u5979\u76ef\u7740\u5c0f\u65b9\u63e1\u5251\u7684\u624b\uff1a\u201c\u4f60\u6709\u5251\uff0c\u6211\u4e5f\u6709\u3002\u4f60\u5df2\u7ecf\u7ec3\u6210\u4e86\u6211\u7684\u5251\u6cd5\uff0c\u6211\u4e5f\u7ec3\u6210\u4e86\u4f60\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u7ecf\u5230\u4e86\u6211\u4eec\u8981\u6bd4\u4e00\u6bd4\u7a76\u7adf\u662f\u8c01\u5f3a\u8c01\u5f31\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u8c01\u8d25\u4e86\uff0c\u8c01\u5c31\u6b7b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5c0f\u71d5\u9053\uff0c\u201c\u5f3a\u8005\u751f\uff0c\u5f31\u8005\u6b7b\uff0c\u8fd9\u6837\u662f\u4e0d\u662f\u4e5f\u5f88\u516c\u5e73\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u56de\u7b54\u4e5f\u540c\u6837\u5e72\u8106\uff1a\u201c\u662f\u7684\uff0c\u8fd9\u6837\u5b50\u7684\u786e\u516c\u5e73\u6781\u4e86\u3002\u201d \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u4e24\u67c4\u5251\u90fd\u5df2\u62d4\u51fa\u3002 \u3000\u3000\u4ed6\u4eec\u7ec3\u7684\u867d\u7136\u662f\u540c\u6837\u7684\u5251\u6cd5\uff0c\u53ef\u662f\u4ed6\u4eec\u7684\u6027\u522b\u4e0d\u540c\u3001\u4f53\u8d28\u4e0d\u540c\uff0c\u667a\u6167\u548c\u60f3\u6cd5\u4e5f\u4e0d\u540c\u3002 \u3000\u3000\u4ed6\u4eec\u4f7f\u51fa\u7684\u7eb5\u7136\u662f\u540c\u6837\u7684\u62db\u5f0f\uff0c\u5728\u4ed6\u4eec\u51fa\u624b\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4e5f\u4f1a\u6709\u4e0d\u540c\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u751f\u6b7b\u80dc\u8d1f\uff0c\u5c31\u51b3\u5b9a\u4e8e\u90a3\u4e00\u77ac\u95f4\u3002 \u3000\u3000\u5c0f\u71d5\u5ffd\u7136\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u4ec0\u4e48\u540e\u4e8b\u8981\u4ea4\u4ee3\u7ed9\u6211\uff1f\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u5c0f\u65b9\u53cd\u95ee\u3002 \u3000\u3000\u201c\u6211\u6ca1\u6709\u3002\u201d\u5c0f\u71d5\u5c45\u7136\u7b11\u4e86\u7b11\uff0c\u201c\u56e0\u4e3a\u6211\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u6709\u3002\u201d\u5c0f\u71d5\u5fae\u7b11\uff0c\u201c\u5426\u5219\u6211\u600e\u4e48\u4f1a\u6765\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u60f3\u7b11\u5374\u7b11\u4e0d\u51fa\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u5b9e\u5728\u8fde\u4e00\u70b9\u628a\u63e1\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u7684\u5bf9\u624b\u5374\u5bf9\u81ea\u5df1\u5145\u6ee1\u4fe1\u5fc3\u3002 \u3000\u3000\u5728\u751f\u6b7b\u4e00\u77ac\u7684\u51b3\u6218\u4e2d\uff0c\u4fe1\u5fc3\u65e0\u7591\u4e5f\u662f\u51b3\u5b9a\u80dc\u8d1f\u7684\u4e00\u5927\u56e0\u7d20\u3002 \u3000\u3000\u5c0f\u71d5\u53c8\u5728\u95ee\u4ed6\uff1a\u201c\u4f60\u81ea\u5df1\u77e5\u4e0d\u77e5\u9053\u4f60\u4e3a\u4ec0\u4e48\u5fc5\u8d25\u65e0\u7591\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u8bf4 \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u662f\u7537\u4eba\u3002\u201d\u5c0f\u71d5\u7684\u56de\u7b54\u5f88\u5947\u602a\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u61c2\uff0c\u6240\u4ee5\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u662f\u7537\u4eba\uff0c\u6240\u4ee5\u6211\u5c31\u5fc5\u8d25\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5c0f\u71d5\u8bf4\uff0c\u201c\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u5df2\u7ecf\u7ec3\u8fc7\u72ec\u5b64\u75f4\u7684\u5251\u6cd5\u3002\u201d\u5c0f\u71d5\u9053\uff0c\u201c\u6211\u8bf4\u8fc7\uff0c\u4ed6\u7684\u5251\u6cd5\u5f88\u7edd\uff0c\u4e5f\u5f88\u90aa\uff0c\u6bcf\u9694\u4e00\u6bb5\u65e5\u5b50\uff0c\u4e00\u5b9a\u8981\u5c06\u7cbe\u6c14\u6e32\u6cc4\uff0c\u8eab\u5fc3\u624d\u80fd\u4fdd\u6301\u5e73\u7a33\u7a33\u5b9a\u3002\u201d \u3000\u3000\u5979\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u53ef\u662f\u4f60\u7684\u7cbe\u6c14\u6839\u672c\u5c31\u6ca1\u6709\u53d1\u6cc4\u7684\u5730\u65b9\uff0c\u6240\u4ee5\u4f60\u6700\u8fd1\u5df2\u7ecf\u6e10\u6e10\u53d8\u4e86\uff0c\u53d8\u5f97\u7126\u8e81\u4e0d\u5b89\uff0c\u5c31\u7b97\u4e00\u5929\u51b2\u5341\u6b21\u51b7\u6c34\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u5979\u53c8\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u5982\u679c\u8fde\u81ea\u5df1\u7684\u60c5\u7eea\u90fd\u65e0\u6cd5\u4fdd\u6301\u9547\u5b9a\uff0c\u4ed6\u80fd\u4e0d\u80fd\u7b97\u662f\u4e2a\u53ef\u6015\u7684\u5bf9\u624b\uff1f\u201d\u5c0f\u71d5\u5e26\u7740\u7b11\u95ee\uff0c\u201c\u4ed6\u600e\u4e48\u80fd\u4e0d\u8d25\uff01\u201d \u3000\u3000\u5c0f\u65b9\u63e1\u5251\u7684\u624b\u53c8\u6709\u9752\u7b4b\u66b4\u8d77\uff0c\u638c\u5fc3\u5df2\u5192\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u5df2\u5bdf\u89c9\u5230\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u867d\u7136\u4ed6\u660e\u77e5\u5979\u8fd9\u4e48\u8bf4\u662f\u4e3a\u4e86\u8981\u6467\u6bc1\u4ed6\u7684\u4fe1\u5fc3\uff0c\u5374\u504f\u504f\u65e0\u6cd5\u53cd\u9a73\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u4e00\u4e2a\u4eba\u7684\u4fe1\u5fc3\u5df2\u88ab\u6467\u6bc1\uff0c\u53c8\u600e\u4e48\u5728\u8fd9\u79cd\u751f\u6b7b\u51b3\u6218\u4e2d\u51fb\u8d25\u4ed6\u7684\u5bf9\u624b\uff1f \u3000\u3000\u5c0f\u71d5\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u624d\u95ee\u4f60\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u540e\u4e8b\u8981\u4ea4\u4ee3\uff1f\u8fd8\u6709\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u601d\uff0c\u540e\u6094\u4ed6\u8bf4\uff0c\u201c\u5c31\u7b97\u4f60\u80fd\u51fb\u8d25\u6211\uff0c\u4e5f\u5fc5\u5c06\u6b7b\u5728\u72ec\u5b64\u75f4\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7684\u56de\u7b54\u4e5f\u8ddf\u5979\u521a\u624d\u7684\u8bf4\u6cd5\u540c\u6837\u5947\u602a\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u5973\u4eba\uff01\u201d\u4ed6\u8bf4\uff0c\u201c\u5c31\u56e0\u4e3a\u4f60\u662f\u5973\u4eba\uff0c\u6240\u4ee5\u4f60\u6c38\u8fdc\u6ca1\u6709\u51fb\u8d25\u4ed6\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5c0f\u71d5\u4e5f\u4e0d\u61c2\uff0c\u6240\u4ee5\u4e5f\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u7684\u5251\u6cd5\u786e\u5b9e\u5f88\u7edd\uff0c\u4e5f\u5f88\u90aa\uff0c\u6211\u7ecf\u8fc7\u4e94\u4e2a\u6708\u540e\uff0c\u5c31\u89c9\u5f97\u6709\u4e00\u80a1\u7cbe\u6c14\u90c1\u7ed3\u3002\u201d \u3000\u3000\u4ed6\u76ef\u7740\u4ed6\u7684\u5bf9\u624b\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u6ca1\u6709\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u56e0\u4e3a\u4f60\u662f\u5973\u4eba\uff0c\u6839\u672c\u5c31\u65e0\u6cd5\u5f97\u5230\u4ed6\u5251\u6cd5\u4e2d\u7684\u7cbe\u9ad3\u3002\u201d \u3000\u3000\u5c0f\u71d5\u7684\u624b\u5706\u6da6\u67d4\u7f8e\uff0c\u53ef\u662f\u73b0\u5728\u5979\u63e1\u5251\u7684\u624b\u4e5f\u6709\u9752\u7b4b\u66b4\u8d77\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\u4e5f\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u597d\u6b79\u90fd\u8981\u53bb\u8bd5\u4e00\u8bd5\u3002\u201d\u5979\u638c\u4e2d\u7684\u5251\u5c16\u659c\u659c\u6311\u8d77\uff0c\u201c\u6240\u4ee5\u73b0\u5728\u6211\u5c31\u8981\u5148\u7528\u4f60\u6765\u8bd5\u4e00\u8bd5\u6211\u7684\u5251\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5929\u5149\u5df2\u6e10\u6e10\u6697\u4e86\uff0c\u6697\u6797\u4e2d\u5ffd\u7136\u6709\u4e00\u9053\u5251\u5149\u659c\u659c\u98de\u8d77\u3002 \u3000\u3000\u5251\u98ce\u7834\u7a7a\u4e00\u54cd\uff0c\u6728\u53f6\u8427\u8427\u843d\u4e0b\uff0c\u5251\u6c14\u903c\u4eba\u7709\u776b\u3002 \u3000\u3000\u9ad8\u624b\u95f4\u7684\u51b3\u6218\uff0c\u901a\u5e38\u90fd\u662f\u6700\u80fd\u5438\u5f15\u4eba\u7684\u3002\u5728\u51b3\u6218\u7684\u8fc7\u7a0b\u4e2d\uff0c\u90a3\u79cd\u60ca\u5fc3\u52a8\u9b44\u7684\u53d8\u5316\uff0c\u51fa\u4eba\u610f\u6599\u7684\u62db\u5f0f\uff0c\u603b\u80fd\u4f7f\u4eba\u770b\u5f97\u5fc3\u52a8\u795e\u9a70\uff0c\u5982\u9189\u5982\u75f4\u3002 \u3000\u3000\u6614\u5e74\u897f\u95e8\u5439\u96ea\u4e0e\u201c\u767d\u4e91\u57ce\u4e3b\u201d\u53f6\u5b64\u57ce\u7ea6\u6218\u4e8e\u91cd\u9633\u4e4b\u65e5\u7d2b\u7981\u4e4b\u5dc5\uff0c\u4e09\u4e2a\u6708\u524d\u5c31\u5df2\u4f20\u904d\u6c5f\u6e56\uff0c\u8f70\u52a8\u4e5d\u57ce\u3002 \u3000\u3000\u60f3\u770b\u5230\u8fd9\u4e00\u7c7b\u51b3\u6218\u5374\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u5927\u591a\u6570\u4eba\u90fd\u5f88\u96be\u5f97\u5230\u8fd9\u79cd\u673a\u4f1a\u3002\u5176\u4e2d\u62db\u5f0f\u95f4\u7684\u53d8\u5316\uff0c\u53d8\u5316\u95f4\u7684\u7cbe\u5999\u5904\uff0c\u53ef\u4e0d\u662f\u4efb\u4f55\u8a00\u8bed\u6587\u5b57\u6240\u80fd\u5f62\u5bb9\u5f97\u51fa\u7684\u3002\u9664\u975e\u4f60\u80fd\u4eb2\u4e34\u5176\u5883\uff0c\u81ea\u5df1\u53bb\u4f53\u4f1a\uff0c\u5426\u5219\u4f60\u5c31\u5f88\u96be\u9886\u7565\u5230\u5176\u4e2d\u7684\u53d8\u5316\u548c\u523a\u6fc0\u3002 \u3000\u3000\u6240\u4ee5\u5bf9\u5927\u591a\u6570\u4eba\u6765\u8bf4\uff0c\u771f\u6b63\u5173\u5fc3\u7684\u5e76\u4e0d\u662f\u51b3\u6218\u7684\u8fc7\u7a0b\uff0c\u800c\u662f\u7ed3\u5c40\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u770b\u89c1\u5c0f\u65b9\u548c\u5c0f\u71d5\u8fd9\u4e00\u6218\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u4e00\u6218\u8fc7\u7a0b\u7684\u523a\u6fc0\u4e0e\u53d8\u5316\uff0c\u5f53\u7136\u4e5f\u6ca1\u6709\u4eba\u80fd\u63cf\u8ff0\u5f97\u51fa\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u6218\u7684\u7ed3\u5c40\u5374\u65e0\u7591\u662f\u6bcf\u4e2a\u4eba\u90fd\u5173\u5fc3\u7684\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e00\u6218\u7a76\u7adf\u662f\u8c01\u80dc\u8c01\u8d1f\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u662f\u5c0f\u65b9\u8d25\u4e86\uff0c\u4ed6\u662f\u4e0d\u662f\u7acb\u523b\u5c31\u4f1a\u6b7b\u5728\u90a3\u91cc\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u662f\u5c0f\u65b9\u80dc\u4e86\uff1a\u4ed6\u4f1a\u4e0d\u4f1a\u5f53\u65f6\u5c31\u5c06\u4ed6\u7684\u5bf9\u624b\u523a\u6740\u4e8e\u5251\u4e0b\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u60c5\u7eea\u5f88\u4e0d\u7a33\u5b9a\uff0c\u51fa\u624b\u5f53\u7136\u4e5f\u5f88\u96be\u4fdd\u6301\u7a33\u5b9a\u3002\u4e0d\u4f46\u62db\u5f0f\u95f4\u7684\u53d8\u5316\u5f88\u96be\u628a\u63e1\u5f97\u6070\u5230\u597d\u5904\uff0c\u8fd0\u6c14\u6362\u6c14\u4e5f\u5f88\u96be\u63a7\u5236\u5f97\u81ea\u7136\u6d41\u7545\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u6218\u4ed6\u80dc\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8fdc\u6bd4\u4ed6\u7684\u5bf9\u624b\u66f4\u6709\u7ecf\u9a8c\uff0c\u4e5f\u66f4\u6709\u8010\u529b\u548c\u97e7\u529b\u3002 \u3000\u3000\u5982\u679c\u8fd9\u4e00\u6218\u80fd\u5728\u6570\u5341\u62db\u4e4b\u5185\u5c31\u51b3\u5b9a\u51fa\u80dc\u8d1f\uff0c\u80dc\u7684\u65e0\u7591\u662f\u9f50\u5c0f\u71d5\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u4e4b\u95f4\u5f3a\u5f31\u7684\u8ddd\u79bb\u5e76\u4e0d\u5927\uff0c\u8c01\u4e5f\u4e0d\u80fd\u5728\u6570\u5341\u62db\u4e4b\u95f4\u51fb\u8d25\u5bf9\u65b9\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u4e00\u70b9\u62d6\u5f97\u5f88\u957f\uff0c\u4e00\u767e\u4e94\u5341\u62db\u4e4b\u540e\uff0c\u5c0f\u65b9\u5c31\u77e5\u9053\u81ea\u5df1\u80dc\u4e86\u3002 \u3000\u3000\u4e00\u767e\u4e94\u5341\u62db\u4e4b\u540e\uff0c\u5c0f\u71d5\u5c31\u77e5\u9053\u81ea\u5df1\u8981\u8d25\u4e86\u3002 \u3000\u3000\u5979\u7684\u6c14\u529b\u5df2\u6e10\u6e10\u4e0d\u7ee7\uff0c\u62db\u5f0f\u8fd0\u7528\u53d8\u5316\u95f4\u5df2\u6e10\u6e10\u529b\u4e0d\u4ece\u5fc3\u3002 \u3000\u3000\u66f4\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff0c\u5979\u5fc3\u91cc\u5df2\u7ecf\u6709\u4e86\u4e2a\u9634\u5f71\u3002 \u3000\u3000\u2014\u2014\u5c31\u7b97\u4f60\u80fd\u51fb\u8d25\u6211\uff0c\u4e5f\u5fc5\u5c06\u6b7b\u5728\u72ec\u5b64\u75f4\u5251\u4e0b\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u5979\u771f\u6b63\u8981\u51fb\u8d25\u7684\u5e76\u4e0d\u662f\u5c0f\u65b9\uff0c\u800c\u662f\u72ec\u5b64\u75f4\uff0c\u6240\u4ee5\u5979\u5bf9\u8fd9\u4e00\u6218\u7684\u80dc\u8d1f\u5df2\u7ecf\u6ca1\u6709\u62b1\u592a\u5927\u7684\u70ed\u671b\u3002 \u3000\u3000\u66f4\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff0c\u5728\u8fd9\u79cd\u538b\u529b\u7684\u9634\u5f71\u4e0b\uff0c\u5979\u751a\u81f3\u5df2\u5fd8\u8bb0\u8d25\u5c31\u662f\u6b7b\uff01 \u3000\u3000\u6240\u4ee5\u5979\u8d25\u4e86\u3002 \u3000\u3000\u201c\u632b\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u5251\u76f8\u51fb\u3002 \u3000\u3000\u5251\u82b1\u5982\u706b\u822c\u7684\u56db\u6563\u98de\u6fc0\uff0c\u5c0f\u71d5\u638c\u4e2d\u7684\u5251\u5df2\u8131\u624b\u98de\u4e86\u51fa\u53bb\uff0c\u5c0f\u65b9\u7684\u5251\u5df2\u5230\u4e86\u5979\u54bd\u5589\u95f4\u3002 \u3000\u3000\u76f4\u5230\u5251\u950b\u4e0a\u7684\u5251\u6c14\u548c\u5bd2\u610f\u5df2\u523a\u5165\u5979\u7684\u6bdb\u5b54\u65f6\uff0c\u5979\u624d\u60f3\u8d77\u4ed6\u4eec\u4e4b\u95f4\u7684\u7ea6\u5b9a\u3002 \u3000\u3000\u2014\u2014\u8c01\u8d25\u4e86\uff0c\u8c01\u5c31\u6b7b\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6b7b\u4ea1\u7684\u6050\u60e7\u5ffd\u7136\u50cf\u662f\u53ea\u9b3c\u624b\u822c\u652b\u4f4f\u4e86\u5979\uff0c\u627c\u4f4f\u4e86\u5979\u7684\u54bd\u5589\uff0c\u634f\u4f4f\u4e86\u5979\u7684\u5173\u8282\uff0c\u5360\u636e\u4e86\u5979\u7684\u8089\u4f53\u548c\u7075\u9b42\u3002 \u3000\u3000\u5979\u8fd8\u5e74\u8f7b\u3002 \u3000\u3000\u5979\u4ece\u6765\u90fd\u4e0d\u6015\u6b7b\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u624d\u771f\u6b63\u4e86\u89e3\u5230\u6b7b\u4ea1\u662f\u4ef6\u591a\u4e48\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u4eba\u7c7b\u5bf9\u6b7b\u4ea1\u7684\u6050\u60e7\uff0c\u672c\u6765\u5c31\u662f\u4eba\u7c7b\u6240\u6709\u7684\u6050\u60e7\u4e2d\u6700\u5927\u6700\u6df1\u5207\u7684\u4e00\u79cd \u3000\u3000\u2014\u2014\u56e0\u4e3a\u201c\u6b7b\u201d\u5c31\u662f\u6240\u6709\u4e00\u5207\u4e8b\u7684\u7ec8\u7ed3\uff0c\u5c31\u662f\u4e00\u65e0\u6240\u6709\u3002 \u3000\u3000\u8fd9\u79cd\u5fc3\u7406\u4e0a\u7684\u6050\u60e7\u7adf\u4f7f\u5f97\u9f50\u5c0f\u71d5\u6574\u4e2a\u4eba\u7684\u751f\u7406\u7ec4\u7ec7\u90fd\u8d77\u4e86\u79cd\u5947\u5f02\u7684\u53d8\u5316\u3002 \u3000\u3000\u5979\u7684\u820c\uff0c\u5979\u7684\u5634\u8154\uff0c\u5979\u7684\u54bd\u5589\uff0c\u5ffd\u7136\u53d8\u5f97\u5b8c\u5168\u5e72\u71e5\u3002 \u3000\u3000\u5979\u7684\u808c\u8089\u5173\u8282\u5ffd\u7136\u53d8\u5f97\u50f5\u786c\u9ebb\u6728\u3002 \u3000\u3000\u5979\u7684\u77b3\u5b54\u5728\u6536\u7f29\uff0c\u6bdb\u5b54\u4e5f\u5728\u6536\u7f29\uff0c\u6240\u6709\u63a7\u5236\u5206\u6ccc\u7684\u7ec4\u7ec7\u90fd\u5df2\u5931\u53bb\u63a7\u5236\u3002 \u3000\u3000\u5979\u7684\u5fc3\u8df3\u4e0e\u547c\u5438\u51e0\u4e4e\u5df2\u52a0\u5feb\u4e86\u4e00\u500d\u3002 \u3000\u3000\u66f4\u5947\u602a\u7684\u662f\uff0c\u5c31\u5728\u8fd9\u79cd\u53d8\u5316\u53d1\u751f\u65f6\uff0c\u5979\u5ffd\u7136\u53c8\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u51b2\u52a8\u3002 \u3000\u3000\u5979\u7684\u60c5\u6b32\u5ffd\u7136\u56e0\u4e3a\u808c\u8089\u7684\u6536\u7f29\u78e8\u64e6\u800c\u706b\u7130\u822c\u71c3\u70e7\u8d77\u6765\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u7a7f\u7684\u53ea\u4e0d\u8fc7\u662f\u4ef6\u76ae\u80a4\u822c\u6e29\u8f6f\u67d4\u8584\u7684\u8863\u670d\uff0c\u8fde\u76ae\u80a4\u7684\u6218\u6817\u3001\u808c\u8089\u7684\u98a4\u52a8\u90fd\u53ef\u4ee5\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u5979\u5f88\u60f3\u95f4\u5c0f\u65b9\uff1a \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u5979\u6ca1\u6709\u95f4\uff0c\u56e0\u4e3a\u5979\u5df2\u65e0\u6cd5\u63a7\u5236\u5979\u5589\u5934\u7684\u808c\u8089\u548c\u5979\u7684\u820c\u5934\u3002 \u3000\u3000\u5979\u6ca1\u6709\u95f4\uff0c\u4e5f\u56e0\u4e3a\u5979\u5ffd\u7136\u53d1\u73b0\u5c0f\u65b9\u751f\u7406\u4e0a\u4e5f\u8d77\u4e86\u79cd\u53c8\u5947\u602a\u53c8\u53ef\u6015\u7684\u53d8\u5316\u3002 \u3000\u3000\u8fd9\u79cd\u53d8\u5316\u4f7f\u5f97\u5979\u7684\u5fc3\u8df3\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u5979\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u4e0d\u6562\u518d\u770b\uff1b\u5979\u95ed\u4e0a\u773c\u775b\u65f6\u5979\u7684\u547c\u5438\u5df2\u53d8\u4e3a\u547b\u541f\uff0c\u82cd\u767d\u7684\u8138\u5df2\u7ea2\u5982\u6843\u82b1\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u5979\u5df2\u7ecf\u77e5\u9053\u5c0f\u65b9\u4e0d\u4f1a\u6740\u5979\u4e86\uff0c\u4e5f\u77e5\u9053\u5c0f\u65b9\u8981\u505a\u4ec0\u4e48\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u611f\u89c9\u5230\u5c0f\u65b9\u70bd\u70ed\u7684\u547c\u5438\u548c\u8eab\u5b50\u7684\u538b\u529b\u3002 \u3000\u3000\u5979\u65e0\u6cd5\u63a8\u62d2\uff0c\u4e5f\u4e0d\u60f3\u63a8\u62d2\u3002 \u3000\u3000\u2014\u2014\u4f46\u8fd9\u4e9b\u53ea\u56e0\u4e3a\u5979\u672c\u6765\u5c31\u5df2\u60f3\u5230\u7ed3\u679c\u4e00\u5b9a\u4f1a\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u5979\u5ffd\u7136\u653e\u677e\u4e86\u81ea\u5df1\uff0c\u653e\u677e\u4e86\u5979\u7684\u8eab\u4f53\u56db\u80a2\uff0c\u653e\u677e\u4e86\u6240\u6709\u7684\u4e00\u5207\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u53ea\u6709\u8fd9\u6837\u5b50\u624d\u80fd\u5f97\u5230\u89e3\u8131\uff0c\u4e00\u79cd\u51e0\u4e4e\u548c\u201c\u6b7b\u4ea1\u201d\u540c\u6837\u5f7b\u5e95\u7684\u89e3\u8131\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u662f\u516b\u6708\u5341\u4e94\u65e5\uff0c\u662f\u9f50\u5c0f\u71d5\u7684\u751f\u65e5\u3002 \u3000\u3000\u5979\u662f\u5728\u4e2d\u79cb\u8282\u751f\u7684\uff0c\u53ef\u662f\u76f4\u5230\u5979\u5df2\u5b8c\u5168\u89e3\u8131\u540e\u518d\u5f20\u5f00\u773c\u775b\u65f6\uff0c\u5979\u624d\u60f3\u8d77\u8fd9\u4e00\u5929\u662f\u5979\u7684\u751f\u65e5\uff0c\u624d\u60f3\u8d77\u8fd9\u4e00\u5929\u662f\u4e2d\u79cb\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e00\u5f20\u5f00\u773c\u775b\uff0c\u5c31\u770b\u89c1\u4e86\u4e00\u8f6e\u660e\u6708\uff0c\u4e00\u8f6e\u6bd4\u5979\u5728\u5f80\u6614\u4efb\u4f55\u4e00\u5929\u665a\u4e0a\u6240\u770b\u89c1\u8fc7\u7684\u660e\u6708\u90fd\u66f4\u5706\u66f4\u4eae\u7684\u660e\u6708\u3002 \u3000\u3000\u7136\u540e\u5979\u624d\u770b\u89c1\u5c0f\u65b9\u3002 \u3000\u3000\u5c0f\u65b9\u5728\u6708\u4e0b\u3002 \u3000\u3000\u6708\u5149\u6e05\u6f88\u67d4\u548c\u5e73\u9759\u7a33\u5b9a\uff0c\u4ed6\u7684\u4eba\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5df2\u5b8c\u5168\u6062\u590d\u5e73\u9759\uff0c\u5b8c\u5168\u653e\u677e\u4e86\u81ea\u5df1\uff0c\u4ed6\u7684\u4eba\u4eff\u4f5b\u5df2\u548c\u5927\u5730\u660e\u6708\u878d\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u5927\u5730\u660e\u6708\u662f\u6c38\u6052\u4e0d\u53d8\u7684\uff0c\u4ed6\u8fd9\u4e2a\u4eba\u4eff\u4f5b\u4e5f\u63a5\u8fd1\u6c38\u6052\uff0c\u63a5\u8fd1\u90a3\u79cd\u5e73\u548c\u5b89\u5b9a\u6c38\u6052\u4e0d\u53d8\u7684\u5883\u754c\u3002 \u3000\u3000\u5c0f\u71d5\u5f88\u60f3\u544a\u8bc9\u4ed6\uff1a \u3000\u3000\u201c\u73b0\u5728\u4f60\u7684\u5251\u6cd5\u5df2\u7ecf\u771f\u6b63\u7ec3\u6210\u4e86\u3002\u201d \u3000\u3000\u5979\u6ca1\u6709\u8bf4\uff0c\u56e0\u4e3a\u5979\u5ffd\u7136\u89c9\u5f97\u773c\u4e2d\u6709\u4e00\u80a1\u6cea\u6c34\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u867d\u7136\u8d25\u4e86\uff0c\u867d\u7136\u5df2\u7ecf\u77e5\u9053\u81ea\u5df1\u6c38\u8fdc\u65e0\u6cd5\u51fb\u8d25\u72ec\u5b64\u75f4\uff0c\u6c38\u8fdc\u65e0\u6cd5\u5230\u8fbe\u5251\u672f\u7684\u5dc5\u5cf0\u3002 \u3000\u3000\u53ef\u662f\u5979\u5df2\u5e2e\u52a9\u4e00\u4e2a\u7537\u4eba\u7a81\u7834\u4e86\u56f0\u5883\uff0c\u5230\u8fbe\u4e86\u8fd9\u79cd\u5883\u754c\u3002 \u3000\u3000\u5979\u7684\u8eab\u4f53\u5df2\u7ecf\u6709\u4e86\u8fd9\u4e2a\u7537\u4eba\u7684\u751f\u547d\uff0c\u4ed6\u4eec\u7684\u751f\u547d\u5df2\u7ecf\u878d\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u4ed6\u7684\u80dc\u5229\uff0c\u5c31\u7b49\u4e8e\u662f\u5979\u7684\u3002 \u3000\u3000\u5929\u8272\u6e10\u6e10\u4eae\u4e86\uff0c\u6708\u5149\u6e10\u6e10\u6de1\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u4e45\uff0c\u5979\u624d\u8f7b\u8f7b\u5730\u544a\u8bc9\u5c0f\u65b9\uff1a\u201c\u4f60\u5df2\u7ecf\u53ef\u4ee5\u53bb\u627e\u72ec\u5b64\u75f4\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u5979\u4e5f\u4e0d\u77e5\u9053\u5c0f\u65b9\u6709\u6ca1\u6709\u542c\u89c1\u5979\u7684\u8bdd\uff0c\u53ef\u662f\u5979\u5df2\u7ecf\u542c\u89c1\u4e86\u4e00\u58f0\u9e21\u557c\u3002 \u3000\u3000\u5c31\u50cf\u662f\u4e0a\u6b21\u4e00\u6837\uff0c\u542c\u89c1\u4e86\u8fd9\u58f0\u9e21\u557c\uff0c\u5979\u5c31\u5ffd\u7136\u8dc3\u8d77\uff0c\u5c31\u50cf\u662f\u4e2a\u542c\u4e0d\u5f97\u9e21\u557c\u89c1\u4e0d\u5f97\u9633\u5149\u7684\u5e7d\u7075\u9b3c\u5973\u822c\u5ffd\u7136\u9003\u8d70\uff0c\u6d88\u5931\u5728\u7070\u7070\u6697\u6697\u8ff7\u8ff7\u8499\u8499\u7684\u6653\u96fe\u91cc\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5c0f\u65b9\u6ca1\u6709\u8ba9\u5979\u9003\u8d70\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7b2c\u4e00\u58f0\u9e21\u557c\u54cd\u8d77\u65f6\uff0c\u5c31\u662f\u72ec\u5b64\u75f4\u8d77\u5e8a\u7684\u65f6\u5019\u3002 \u3000\u3000\u7761\u7720\u662f\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u7f3a\u5c11\u7684\uff0c\u4ed6\u4e5f\u662f\u4eba\uff0c\u53ef\u662f\u5373\u4f7f\u5728\u7761\u7720\u4e2d\u4ed6\u4e5f\u8981\u968f\u65f6\u4fdd\u6301\u6e05\u9192\u3002 \u3000\u3000\u4ed6\u7761\u7684\u662f\u5f20\u77f3\u677f\u5e8a\uff0c\u7a84\u5c0f\u51b0\u51b7\u575a\u786c\uff0c\u5403\u7684\u98df\u7269\u7b80\u5355\u7cbe\u6ca5\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u5bb9\u8bb8\u81ea\u5df1\u6709\u7247\u523b\u5b89\u9038\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4e00\u4e2a\u5251\u5ba2\u7684\u751f\u6d3b\uff0c\u8fdc\u6bd4\u4efb\u4f55\u4e00\u4e2a\u82e6\u884c\u50e7\u8fc7\u5f97\u66f4\u82e6\uff0c\u4ed6\u5374\u4e45\u5df2\u4e60\u60ef\u4e86\u3002 \u3000\u3000\u4ed6\u603b\u8ba4\u4e3a\u65e0\u8bba\u4f60\u8981\u83b7\u5f97\u4efb\u4f55\u4e00\u79cd\u8363\u8000\uff0c\u90fd\u5fc5\u987b\u4ed8\u51fa\u75db\u82e6\u7684\u4ee3\u4ef7\uff0c\u5fc5\u987b\u4e0d\u65ad\u5730\u97ad\u631e\u81ea\u5df1\u3002 \u3000\u3000\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u5251\u6cd5\u662f\u600e\u4e48\u6837\u7ec3\u6210\u7684\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4ece\u6765\u4e0d\u613f\u63d0\u8d77\u3002 \u3000\u3000\u90a3\u65e0\u7591\u662f\u6bb5\u8f9b\u9178\u60e8\u75db\u7684\u7ecf\u5386\uff0c\u5176\u4e2d\u4e5f\u4e0d\u77e5\u5305\u542b\u4e86\u591a\u5c11\u8840\u6cea\u6c57\u6c34\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e2\u4e0d\u662f\u540d\u95e8\u5b50\u5f1f\uff0c\u4e5f\u6ca1\u6709\u663e\u8d6b\u7684\u5bb6\u4e16\uff0c\u8840\u6cea\u548c\u6c57\u6c34\u5c31\u662f\u4ed6\u5fc5\u987b\u4ed8\u51fa\u7684\u4ee3\u4ef7\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u5251\u6cd5\u603b\u7b97\u5df2\u7ec3\u6210\u3002 \u3000\u3000\u4ed6\u4e00\u5251\u7eb5\u6a2a\uff0c\u8f6c\u6218\u5357\u5317\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u9047\u89c1\u8fc7\u5bf9\u624b\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u9047\u5230\u4e86\u535c\u9e70\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\u4f60\u5728\u54ea\u91cc\uff1f \u3000\u3000\u4ed6\u8d64\u88f8\u88f8\u5730\u4ece\u5e8a\u4e0a\u5750\u8d77\uff0c\u5c31\u50cf\u662f\u4e2a\u50f5\u5c38\u7a81\u7136\u81ea\u68fa\u4e2d\u590d\u6d3b\u3002 \u3000\u3000\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\u4ece\u65e0\u4efb\u4f55\u8868\u60c5\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u9664\u4e86\u4ed6\u638c\u4e2d\u6709\u5251\u7684\u65f6\u5019\uff0c\u4ed6\u8fd9\u4e2a\u4eba\u5c31\u597d\u50cf\u53c8\u771f\u7684\u53d8\u6210\u4e86\u50f5\u5c38\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u591a\u5e74\u7981\u6b32\u7684\u7ed3\u679c\uff0c\u7edd\u5bf9\u6ca1\u6709\u4eba\u80fd\u6bd4\u4ed6\u66f4\u4e86\u89e3\u8fd9\u662f\u4ef6\u591a\u4e48\u75db\u82e6\u7684\u4e8b\uff0c\u4e5f\u6ca1\u6709\u4eba\u6bd4\u4ed6\u66f4\u4e86\u89e3\u4e00\u4e2a\u4eba\u8981\u4f7f\u51fa\u591a\u5927\u7684\u529b\u91cf\u624d\u80fd\u514b\u5236\u81ea\u5df1\u7684\u60c5\u6b32\u3002 \u3000\u3000\u7a97\u5916\u8fd8\u662f\u4e00\u7247\u9ed1\u6697\uff0c\u5927\u591a\u6570\u4eba\u90fd\u8fd8\u5728\u6c89\u7761\u4e2d\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u77e5\u9053\uff0c\u7b49\u4ed6\u8d70\u51fa\u8fd9\u5c4b\u5b50\u65f6\uff0c\u201c\u5c0f\u866b\u201d\u4e00\u5b9a\u5df2\u7ecf\u5728\u7b49\u7740\u670d\u4f8d\u4ed6\u3002 \u3000\u3000\u6bcf\u5929\u65e9\u4e0a\uff0c\u4ed6\u90fd\u8981\u201c\u5c0f\u866b\u201d\u628a\u4ed6\u7684\u5168\u8eab\u4e0a\u4e0b\u64e6\u6d17\u5e72\u51c0\uff0c\u66ff\u4ed6\u7a7f\u597d\u8863\u670d\u3002\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e2a\u5b69\u5b50\u6700\u5927\u7684\u613f\u671b\u5c31\u662f\u8981\u5c06\u4ed6\u523a\u6740\u4e8e\u5251\u4e0b\uff01\u4ed6\u7edd\u4e0d\u5bb9\u8bb8\u8fd9\u79cd\u4e8b\u60c5\u53d1\u751f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u9700\u8981\u8fd9\u4e2a\u5b69\u5b50\u6765\u97ad\u7b56\u6fc0\u52b1\u4ed6\uff0c\u4ed6\u603b\u8ba4\u4e3a\u5c31\u7b97\u6700\u5feb\u7684\u4e5f\u9700\u8981\u4e00\u6839\u97ad\u5b50\u624d\u80fd\u8dd1\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u8fd9\u4e2a\u5b69\u5b50\u5c31\u662f\u4ed6\u7684\u97ad\u5b50\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u7559\u4e0b\u4e86\u4ed6\uff0c\u5374\u53c8\u4e0d\u65ad\u5730\u6298\u78e8\u4ed6\u3001\u7f9e\u4fae\u4ed6\uff0c\u8ba9\u4ed6\u5728\u4ed6\u9762\u524d\u6c38\u8fdc\u90fd\u62ac\u4e0d\u8d77\u5934\u6765\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u4e00\u7ae0\u3000\u5251\u75f4\u60c5\u7edd \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u5251\u75f4\u60c5\u7edd \u3000\u3000\u2014\u2014\u5982\u679c\u4f60\u6bcf\u5929\u90fd\u50cf\u5974\u96b6\u822c\u53bb\u670d\u4f8d\u4e00\u4e2a\u4eba\uff0c\u90a3\u4e48\u5c31\u8fde\u4f60\u81ea\u5df1\u90fd\u4f1a\u89c9\u5f97\u4f60\u662f\u6c38\u8fdc\u90fd\u80dc\u4e0d\u8fc7\u8fd9\u4e2a\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u72ec\u5b64\u75f4\u7684\u60f3\u6cd5\uff0c\u4e5f\u662f\u4ed6\u7684\u6218\u7565\u3002 \u3000\u3000\u4e00\u76f4\u5230\u4eca\u5929\u4e3a\u6b62\uff0c\u4ed6\u90fd\u8ba4\u4e3a\u81ea\u5df1\u8fd9\u79cd\u6218\u7565\u662f\u6210\u529f\u7684\u3002 \u3000\u3000\u4eca\u5929\u4ed6\u8d70\u51fa\u53bb\u65f6\uff0c\u4ed6\u7684\u5974\u96b6\u5c45\u7136\u6ca1\u6709\u50cf\u5e73\u65e5\u90a3\u6837\u5728\u95e8\u5916\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u8fdc\u5904\u53c8\u6709\u9e21\u557c\u54cd\u8d77\uff0c\u5927\u5730\u4ecd\u7136\u4e00\u7247\u9ed1\u6697\uff0c\u98ce\u5439\u5728\u8d64\u88f8\u7684\u8eab\u5b50\u4e0a\uff0c\u51b7\u5982\u5200\u522e\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u638c\u4e2d\u6709\u5251\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u63e1\u8d77\u4ed6\u7684\u5251\uff0c\u4ed6\u7684\u5251\u603b\u662f\u5728\u4ed6\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u63e1\u8d77\u7684\u5730\u65b9\u3002 \u3000\u3000\u51b7\u98ce\u5982\u5200\u3002\u4ed6\u7ad9\u5728\u51b7\u98ce\u4e2d\uff0c\u76f4\u7b49\u5230\u66d9\u8272\u5df2\u5982\u5c16\u5200\u822c\u5272\u88c2\u9ed1\u6697\u65f6\uff0c\u624d\u770b\u89c1\u4e00\u4e2a\u4eba\u98de\u63a0\u800c\u6765\u3002 \u3000\u3000\u4ed6\u8ba4\u5f97\u51fa\u8fd9\u4e2a\u4eba\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u53ef\u662f\u4ed6\u770b\u89c1\u7684\u5374\u4e0d\u662f\u90a3\u4e2a\u6d41\u9f3b\u6d95\u73a9\u5c0f\u866b\u7684\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u7684\u662f\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u4ed6\u5df2\u7ecf\u6709\u5f88\u4e45\u672a\u66fe\u770b\u89c1\u5230\u8fc7\u7684\u7f8e\u4e3d\u5973\u4eba\u3002 \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u8fd9\u53e5\u8bdd\u4e4b\u540e\uff0c\u5c31\u770b\u51fa\u8fd9\u4e2a\u5973\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5982\u679c\u4f60\u53d1\u73b0\u4e00\u4e2a\u6bcf\u5929\u90fd\u50cf\u5974\u96b6\u822c\u670d\u4f8d\u4f60\u7684\u201c\u5b69\u5b50\u201d\u7adf\u662f\u4e2a\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u800c\u4f60\u53c8\u8fd8\u50cf\u4ee5\u524d\u90a3\u4e48\u6837\u8d64\u88f8\u88f8\u5730\u7ad9\u5728\u5979\u9762\u524d\u65f6\uff0c\u4f60\u5fc3\u91cc\u662f\u4ec0\u4e48\u611f\u89c9\uff1f\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u53cd\u5e94\uff1f \u3000\u3000\u72ec\u5b64\u75f4\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u8138\u4e0a\u8fd8\u662f\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\uff0c\u53ea\u51b7\u51b7\u5730\u8bf4\u4e86\u53e5\uff1a\u201c\u4f60\u6765\u8fdf\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5c0f\u71d5\u7684\u58f0\u97f3\u540c\u6837\u51b7\u6de1\uff1a\u201c\u4eca\u5929\u6211\u662f\u6765\u8fdf\u4e86\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u6bcf\u5929\u4ed6\u90fd\u7528\u4e00\u79cd\u540c\u6837\u7684\u59ff\u52bf\u7ad9\u5728\u90a3\u91cc\u8ba9\u201c\u5979\u201d\u64e6\u6d17\uff0c\u4eca\u5929\u4ed6\u7684\u59ff\u52bf\u4e5f\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u5c0f\u71d5\u4e5f\u548c\u4ee5\u524d\u4e00\u6837\uff0c\u63d0\u8d77\u4e86\u4e00\u6876\u6c34\uff0c\u6162\u6162\u5730\u8d70\u8fc7\u53bb\uff0c\u773c\u775b\u4e5f\u8fd8\u662f\u548c\u4ee5\u524d\u4e00\u6837\u76f4\u89c6\u7740\u4ed6\u3002 \u3000\u3000\u552f\u4e00\u4e0d\u540c\u7684\u662f\uff0c\u4eca\u5929\u4ed6\u4eec\u4e4b\u95f4\u591a\u51fa\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u51b0\u51b7\u7684\u624b\u4f38\u8fdb\u51b0\u51b7\u7684\u6c34\u6876\uff0c\u635e\u51fa\u4e86\u4e00\u5757\u51b7\u51b7\u51b0\u51b0\u7684\u5e03\u4e2d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5c0f\u65b9\u5df2\u7ecf\u6765\u4e86\u3002 \u3000\u3000\u5979\u7684\u624b\u521a\u4ece\u6c34\u6876\u91cc\u62ff\u51fa\u6765\uff0c\u5c31\u88ab\u7d27\u7d27\u63e1\u4f4f\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u624b\u5feb\u5982\u6bd2\u86c7\u98de\u566c\uff0c\u773c\u795e\u5374\u662f\u8fdf\u949d\u7684\uff0c\u56e0\u6124\u6012\u800c\u8fdf\u949d\u3002 \u3000\u3000\u4ed6\u95ee\u5c0f\u71d5\uff1a\u201c\u4f60\u8d76\u56de\u6765\u5c31\u662f\u4e3a\u4e86\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5c0f\u71d5\u8bf4\uff1a\u201c\u6211\u5929\u5929\u90fd\u5728\u66ff\u4ed6\u505a\u8fd9\u79cd\u4e8b\uff0c\u4e00\u5e74\u4e09\u767e\u516d\u5341\u4e94\u5929\uff0c\u6709\u65f6\u5019\u4e00\u5929\u505a\u4e24\u6b21\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u66ff\u4ed6\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u8981\u6211\u66ff\u4ed6\u505a\u3002\u201d\u5c0f\u71d5\u8bf4\uff0c\u201c\u56e0\u4e3a\u4ed6\u6545\u610f\u8981\u6298\u78e8\u6211\u3001\u4fae\u8fb1\u6211 \u3000\u3000\u5979\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u5979\u7684\u58f0\u97f3\u5df2\u5636\u54d1\uff0c\u5df2\u6e10\u6e10\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u770b\u7740\u4ed6\u4eec\uff0c\u8138\u4e0a\u5ffd\u7136\u51fa\u73b0\u4e86\u51e0\u6761\u602a\u5f02\u626d\u66f2\u7684\u76b1\u7eb9\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u51fa\u4e86\u4ed6\u4eec\u7684\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u7684\u8138\u5ffd\u7136\u53d8\u5f97\u50cf\u662f\u4e2a\u7834\u88c2\u7684\u767d\u8272\u9762\u5177\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u81ea\u89c9\u53d7\u4e86\u6b3a\u9a97\uff0c\u6240\u4ee5\u5c06\u81ea\u5df1\u672c\u8be5\u5f97\u5230\u7684\u8ba9\u7ed9\u4e86\u522b\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u6162\u6162\u5730\u8f6c\u8fc7\u5934\uff0c\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u4e4b\u95f4\u672c\u6765\u5b8c\u5168\u6ca1\u6709\u6069\u6028\u4ec7\u6068\uff0c\u53ef\u662f\u73b0\u5728\u5c0f\u65b9\u7684\u773c\u4e2d\u5df2\u6709\u6012\u706b\u5728\u71c3\u70e7\u3002 \u3000\u3000\u201c\u4ece\u6211\u7b2c\u4e00\u773c\u770b\u89c1\u4f60\uff0c\u6211\u5c31\u77e5\u9053\u6211\u4eec\u4e24\u4eba\u4e4b\u95f4\u5fc5\u5c06\u6709\u4e00\u4e2a\u4eba\u8981\u6b7b\u5728\u5bf9\u65b9\u5251\u4e0b\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u5c45\u7136\u540c\u610f\uff1a\u201c\u6211\u4e5f\u60f3\u5230\u8fdf\u65e9\u603b\u4f1a\u6709\u8fd9\u4e00\u5929\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\u662f\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u5f53\u7136\u5c31\u662f\u73b0\u5728\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff0c\u201c\u73b0\u5728\u4f60\u7684\u638c\u4e2d\u6709\u5251\uff0c\u6211\u4e5f\u6709\u3002\u201d \u3000\u3000\u5c31\u56e0\u4e3a\u4ed6\u638c\u4e2d\u6709\u5251\uff0c\u6240\u4ee5\u4ed6\u7684\u8eab\u5b50\u867d\u7136\u5b8c\u5168\u8d64\u88f8\uff0c\u53ef\u662f\u4ed6\u7684\u795e\u6001\u770b\u6765\u5374\u50cf\u662f\u4e2a\u53f7\u89d2\u9f50\u9e23\u65f6\u5df2\u62ab\u6302\u4ff1\u5168\u51c6\u5907\u4e0a\u9635\u7684\u5c06\u519b\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u77b3\u5b54\u5df2\u7ecf\u5f00\u59cb\u6536\u7f29\u3002 \u3000\u3000\u72ec\u5b64\u75be\u5ffd\u7136\u53c8\u95ee\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\u6b7b\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u5c0f\u65b9\u5f00\u53e3\uff0c\u4ed6\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u95ee\u9898\uff1a\u201c\u6b7b\u7684\u662f\u4f60\uff01\u4e00\u5b9a\u662f\u4f60\u3002\u201d \u3000\u3000\u767d\u8272\u9762\u5177\u4e0a\u7684\u88c2\u75d5\u5df2\u7ecf\u6d88\u5931\u4e0d\u89c1\u4e86\uff0c\u4ed6\u7684\u8138\u4e0a\u53c8\u53d8\u5f97\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e0d\u80fd\u6b7b\u3002\u201d\u72ec\u5b64\u75f4\u63a5\u7740\u9053\uff0c\u201c\u4f60\u8fd8\u8981\u53bb\u627e\u201c\u9633\u5149\u201d\uff0c\u53bb\u627e\u535c\u9e70\uff0c\u53bb\u627e\u5415\u4e09\uff0c\u4f60\u7684\u6069\u6028\u7ea0\u7f20\u90fd\u6ca1\u6709\u4e86\u65ad\uff0c\u4f60\u600e\u4e48\u6b7b\uff01\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u51b0\u51b7\uff1a\u201c\u6240\u4ee5\u6211\u65ad\u5b9a\u4f60\uff0c\u4eca\u5929\u4e00\u5b9a\u4e0d\u4f1a\u51fa\u624b\uff0c\u4e5f\u4e0d\u6562\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u9633\u5149\u5df2\u7a7f\u7834\u4e91\u5c42\uff0c\u5c0f\u65b9\u7684\u8138\u5728\u9633\u5149\u4e0b\u770b\u6765\uff0c\u4eff\u4f5b\u4e5f\u53d8\u6210\u4e86\u4e2a\u767d\u8272\u7684\u9762\u5177\u3002 \u3000\u3000\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u4ed6\u4eec\u5fc5\u987b\u51b3\u4e00\u751f\u6b7b\u80dc\u8d1f\u7684\u65f6\u5019\uff0c\u4e34\u9635\u8131\u9003\u8fd9\u79cd\u4e8b\uff0c\u662f\u7537\u5b50\u6c49\u6b7b\u4e5f\u4e0d\u80af\u505a\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u542c\u89c1\u81ea\u5df1\u5728\u8bf4\uff1a\u201c\u662f\u7684\uff0c\u6211\u4e0d\u80fd\u6b7b\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u8fde\u4ed6\u81ea\u5df1\u542c\u6765\u90fd\u4eff\u4f5b\u5f88\u9065\u8fdc\uff1a\u201c\u5982\u679c\u6211\u6ca1\u6709\u628a\u63e1\u6740\u6b7b\u4f60\uff0c\u6211\u5c31\u4e0d\u80fd\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u628a\u63e1\u6740\u6b7b\u6211\uff1f\u201d\u72ec\u5b64\u75f4\u95ee\u3002 \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u6240\u4ee5\u6211\u4eca\u5929\u7684\u786e\u4e0d\u80fd\u51fa\u624b\u3002\u201d \u3000\u3000\u8bf4\u51fa\u4e86\u8fd9\u53e5\u8bdd\uff0c\u8fde\u5c0f\u65b9\u81ea\u5df1\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5728\u4e00\u5e74\u4ee5\u524d\uff0c\u8fd9\u53e5\u6d3b\u4ed6\u662f\u6b7b\u4e5f\u4e0d\u80af\u8bf4\u51fa\u6765\u7684\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5df2\u7ecf\u53d8\u4e86\u3002 \u3000\u3000\u8fde\u4ed6\u81ea\u5df1\u90fd\u53d1\u89c9\u81ea\u5df1\u53d8\u4e86\u3002 \u3000\u3000\u5c0f\u71d5\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u8138\u8272\u4e5f\u53d8\u5f97\u82cd\u767d\u800c\u6124\u6012\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e0d\u80fd\u51fa\u624b\uff0c\u8fd8\u662f\u4e0d\u6562\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\uff0c\u4e5f\u4e0d\u6562\u3002\u201d \u3000\u3000\u5c0f\u71d5\u5ffd\u7136\u51b2\u8fc7\u53bb\uff0c\u628a\u624b\u91cc\u63d0\u7740\u7684\u4e00\u6876\u6c34\u4ece\u4ed6\u7684\u5934\u4e0a\u6dcb\u5230\u811a\u4e0b\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u52a8\uff0c\u5c31\u8ba9\u81ea\u5df1\u8fd9\u6837\u6e7f\u6dcb\u6dcb\u5730\u7ad9\u7740\u3002 \u3000\u3000\u5c0f\u71d5\u72e0\u72e0\u5730\u76ef\u7740\u4ed6\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4eba\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u5c31\u56e0\u4e3a\u6211\u662f\u4eba\uff0c\u6240\u4ee5\u4eca\u5929\u7edd\u4e0d\u80fd\u51fa\u624b\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5c45\u7136\u8fd8\u80fd\u4fdd\u6301\u51b7\u9759\uff1a\u201c\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u53ea\u6709\u4e00\u6761\u547d\uff0c\u6211\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5c0f\u71d5\u5df2\u7ecf\u4e00\u4e2a\u8033\u5149\u6253\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u662f\u63a5\u7740\u8bf4\u4e0b\u53bb\u3002\u7b49\u4ed6\u8bf4\u5b8c\u65f6\uff0c\u5c0f\u71d5\u5df2\u7ecf\u8d70\u4e86\uff0c\u5c31\u50cf\u662f\u53ea\u8d1f\u4e86\u4f24\u7684\u71d5\u5b50\u4e00\u6837\u98de\u8d70\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u8fd8\u662f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u8ffd\uff1f\u201d \u3000\u3000\u201c\u5979\u53cd\u6b63\u8981\u56de\u6765\u7684\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8ffd\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u5979\u4f1a\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u7684\u58f0\u97f3\u4ecd\uff1b\u65e5\u540c\u6837\u51b7\u9759\uff0c\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u5979\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u4f1a\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u7684\uff0c\u5c31\u597d\u50cf\u4f60\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6211\u548c\u535c\u9e70\u4e00\u6837\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a \u3000\u3000\u6bcf\u4e2a\u5b57\u4ed6\u90fd\u8bf4\u5f97\u5f88\u6162\uff0c\u56e0\u4e3a\u4ed6\u4e00\u5b9a\u8981\u5148\u60f3\u4e00\u60f3\u600e\u4e48\u6837\u624d\u80fd\u628a\u7684\u610f\u601d\u8868\u8fbe\u5f97\u66f4\u660e\u767d\u3002 \u3000\u3000\u201c\u547d\u8fd0\u5c31\u50cf\u6761\u9501\u94fe\uff0c\u6709\u65f6\u5f80\u5f80\u4f1a\u5c06\u4e00\u4e9b\u672c\u6765\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u7684\u4eba\u9501\u5728\u4e00\u8d77\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u73b0\u5728\u6211\u4eec\u5df2\u7ecf\u5168\u90fd\u88ab\u9501\u4f4f\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\uff1f\u201d\u72ec\u5b64\u75f4\u95f4\uff0c\u201c\u6211\u4eec\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\u3001\u6211\u3001\u5979\u3001\u535c\u9e70\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u4ece\u73b0\u5728\u8d77\uff0c\u4e0d\u7ba1\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff0c\u6211\u90fd\u4f1a\u5728\u4f60\u9644\u8fd1\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u4e5f\u8ddf\u6211\u4e00\u6837\uff0c\u4e5f\u8981\u53bb\u627e\u535c\u9e70\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u6240\u4ee5\u6211\u76f8\u4fe1\uff0c\u4e0d\u7ba1\u6211\u8d70\u5230\u54ea\u91cc\uff0c\u4f60\u4e00\u5b9a\u4e5f\u4f1a\u5728\u6211\u9644\u8fd1\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8865\u5145\u8bf4\uff1a\u201c\u53ea\u8981\u6211\u4eec\u4e24\u4e2a\u4eba\u4e0d\u6b7b\uff0c\u5979\u4e00\u5b9a\u4f1a\u6765\u627e\u6211\u4eec\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u5ffd\u7136\u51b7\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u6015\u6211\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u6015\u3002\u201d\u5c0f\u65b9\u6de1\u6de1\u4ed6\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4e5f\u4e0d\u4f1a\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u4e5f\u6ca1\u6709\u628a\u63e1\u6740\u6211\uff01\u201d \u3000\u3000\u592a\u9633\u5df2\u5347\u8d77\uff0c\u7167\u4eae\u4e86\u5c0f\u65b9\u7684\u773c\u775b\uff0c\u4e5f\u7167\u4eae\u4e86\u4ed6\u5251\u4e0a\u7684\u9b54\u773c\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u4f60\u53d8\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6211\u53d8\u4e86\u3002\u201d \u3000\u3000\u201c\u4ece\u524d\u6211\u4ece\u672a\u5c06\u4f60\u770b\u6210\u6211\u7684\u5bf9\u624b\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d\u72ec\u5b64\u75f4\u4eff\u4f5b\u53c8\u5728\u53f9\u606f\uff0c\u201c\u73b0\u5728\u6216\u8bb8\u6709\u4eba\u4f1a\u8ba4\u4e3a\u4f60\u5df2\u53d8\u6210\u4e2a\u61e6\u592b\uff0c\u4f46\u662f\u6211\u5374\u8ba4\u4e3a\u4f60\u5df2\u53d8\u6210\u4e2a\u5251\u5ba2\u3002\u201d \u3000\u3000\u2014\u2014\u5251\u5ba2\u65e0\u60c5\uff0c\u4e5f\u65e0\u6cea\u3002 \u3000\u3000\u2014\u2014\u5c0f\u65b9\u662f\u771f\u7684\u65e0\u60c5\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u53c8\u9053\uff1a\u3002\u2018\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u4ece\u73b0\u5728\u5f00\u59cb\u6211\u4eec\u4e5f\u8bb8\u771f\u7684\u5df2\u7ecf\u88ab\u9501\u5728\u4e00\u8d77\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u7279\u522b\u6ce8\u610f\u3002\u201d \u3000\u3000\u201c\u6211\u8981\u7279\u522b\u6ce8\u610f\uff1f\u201d\u5c0f\u65b9\u95ee\uff0c\u201c\u6ce8\u610f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ce8\u610f\u6211\u3002\u201d\u72ec\u5b64\u75f4\u51b7\u51b7\u5730\u8bf4\uff0c\u201c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u6211\u4e00\u6709\u673a\u4f1a\u5c31\u4f1a\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u6050\u5413\uff0c\u4e5f\u4e0d\u662f\u5a01\u80c1\u3002 \u3000\u3000\u5728\u67d0\u65b9\u9762\u6765\u8bf4\uff0c\u8fd9\u51e0\u4e4e\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u4e00\u79cd\u606d\u7ef4\u3001\u4e00\u79cd\u8d5e\u7f8e\u3002 \u3000\u3000\u2014\u2014\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u628a\u5c0f\u65b9\u770b\u6210\u4ed6\u7684\u5bf9\u624b\uff0c\u771f\u6b63\u7684\u5bf9\u624b\uff0c\u80fd\u591f\u88ab\u72ec\u5b64\u75f4\u89c6\u4e3a\u5bf9\u624b\u5e76\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u6240\u4ee5\u5c0f\u65b9\u5ffd\u7136\u8bf4\u4e86\u53e5\u4ed6\u4eec\u81ea\u5df1\u867d\u7136\u4e86\u89e3\u3001\u522b\u4eba\u542c\u4e86\u5374\u4e00\u5b9a\u4f1a\u89c9\u5f97\u5f88\u5947\u602a\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u8bf4\uff1b\u201c\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u5982\u679c\u6709\u4eba\u8981\u6740\u4f60\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u5bf9\u4ed6\u8bf4\u201c\u8c22\u8c22\u4f60\uff1f\u201d \u3000\u3000\u4f60\u5f53\u7136\u4e0d\u4f1a\u3002 \u3000\u3000\u56e0\u4e3a\u4f60\u4e0d\u662f\u72ec\u5b64\u75f4\uff0c\u4e5f\u4e0d\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u505a\u7684\u4e8b\uff0c\u672c\u6765\u5c31\u662f\u522b\u4eba\u65e0\u6cd5\u7406\u89e3\u7684\u3002 \u3000\u3000\u9633\u5149\u5df2\u7167\u8fdb\u7a97\u5b50\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u6162\u6162\u5730\u3001\u4e00\u4ef6\u4ef6\u7a7f\u4e0a\u4e86\u4ed6\u7684\u8863\u670d\u3002 \u3000\u3000\u5c0f\u65b9\u4e00\u76f4\u7ad9\u5728\u95e8\u53e3\u770b\u7740\u4ed6\uff0c\u6bcf\u4e00\u4e2a\u52a8\u4f5c\u90fd\u770b\u5f97\u5f88\u4ed4\u7ec6\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u9a6c\u5e08\u5728\u89c2\u5bdf\u4ed6\u7684\u79cd\u9a6c\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u5374\u5b8c\u5168\u6ca1\u6709\u6ce8\u610f\u4ed6\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u65e0\u8bba\u5728\u505a\u4ec0\u4e48\u4e8b\u7684\u65f6\u5019\uff0c\u90fd\u4f1a\u8868\u73b0\u51fa\u4e00\u79cd\u4e13\u5fc3\u4e00\u81f4\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u6837\u5b50\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u7684\u7cbe\u795e\u4e0d\u662f\u8d2f\u6ce8\u5728\u4ed6\u6b63\u5728\u505a\u7684\u4e8b\u4e0a\uff0c\u4ed6\u5728\u7a7f\u8863\u670d\u65f6\uff0c\u4e5f\u6b63\u5728\u60f3\u7740\u4ed6\u7684\u5251\u6cd5\u3002 \u3000\u3000\u2014\u2014\u4e5f\u8bb8\u5c31\u5728\u4ed6\u7a7f\u8863\u670d\u7684\u67d0\u4e00\u4e2a\u5c0f\u52a8\u4f5c\u4e0a\uff0c\u4f1a\u5ffd\u7136\u9886\u609f\u5230\u4ed6\u5251\u4e2d\u67d0\u4e00\u5904\u7cbe\u5fae\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u7684\u5251\u5c31\u5728\u4ed6\u4f38\u624b\u53ef\u53ca\u7684\u5730\u65b9\u3002 \u3000\u3000\u7a7f\u597d\u4e86\u8863\u670d\uff0c\u72ec\u5b64\u75f4\u624d\u8f6c\u8eab\u9762\u5bf9\u5c0f\u65b9\uff1a \u3000\u3000\u201c\u8fd9\u5730\u65b9\u6211\u5df2\u7559\u4e0b\u4e0d\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u5c31\u8981\u8d70\u3002\u201d \u3000\u3000\u201c\u6211\u8ddf\u7740\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff0c\u201c\u4e0d\u7ba1\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff0c\u6211\u90fd\u8ddf\u7740\u4f60\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u518d\u8bf4\u3002 \u3000\u3000\u4ed6\u8f6c\u8eab\u8d70\u51fa\u4e86\u95e8\uff0c\u8d70\u5230\u9633\u5149\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u9633\u5149\u5df2\u7167\u904d\u5927\u5730\u3002 \u3000\u3000\u2014\u2014\u201c\u9633\u5149\u201d\u5462\uff1f\u535c\u9e70\u5462\uff1f \u3000\u3000\u4e00\u4ed6\u4eec\u8fd8\u80fd\u4e0d\u80fd\u770b\u5230\u4ed6\u4eec\u7684\u9633\u5149\uff0c\u8fd8\u80fd\u4e0d\u80fd\u5728\u9633\u5149\u4e0b\u81ea\u7531\u547c\u5438\uff1f \u3000\u3000\u201c\u6316\u6811\u5e94\u8be5\u4ece\u4ec0\u4e48\u5730\u65b9\u6316\u8d77\uff1f\u201d \u3000\u3000\u201c\u4ece\u5b83\u7684\u6839\u6316\u8d77\u3002\u201d\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u8981\u6316\u4ec0\u4e48\uff0c\u90fd\u8981\u5148\u6316\u5b83\u7684\u6839\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u7684\u6839\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5931\u52ab\u7684\u9ec4\u91d1\u5728\u54ea\u91cc\uff0c\u8fd9\u4ef6\u4e8b\u7684\u6839\u5c31\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u201c\u90a3\u6279\u9ec4\u91d1\u5c31\u662f\u6240\u6709\u79d8\u5bc6\u7684\u6839\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u6240\u4ee5\u5c0f\u65b9\u53c8\u56de\u5230\u4e86\u5927\u6f20\uff0c\u53c8\u56de\u5230\u4e86\u8fd9\u4e00\u7247\u65e0\u60c5\u7684\u5927\u5730\u3002 \u3000\u3000\u70c8\u65e5\u3001\u98ce\u6c99\u3001\u82e6\u5bd2\u3001\u9177\u70ed\uff0c\u53c8\u5f00\u59cb\u50cf\u4ee5\u524d\u90a3\u6837\u6298\u78e8\u4ed6\u3002 \u3000\u3000\u4ed6\u5728\u8fd9\u91cc\u6d41\u8fc7\u6c57\uff0c\u6d41\u8fc7\u8840\uff0c\u51e0\u4e4e\u5c06\u6027\u547d\u90fd\u846c\u9001\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u75db\u6068\u8fd9\u4e2a\u5730\u65b9\uff0c\u4e0d\u4f46\u75db\u6068\uff0c\u800c\u4e14\u754f\u60e7\uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u504f\u504f\u53c8\u5bf9\u8fd9\u5730\u65b9\u6709\u79cd\u8fde\u4ed6\u81ea\u5df1\u90fd\u65e0\u6cd5\u89e3\u91ca\u7684\u6d53\u70c8\u611f\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5730\u65b9\u867d\u7136\u4e11\u964b\u51b7\u9177\u65e0\u60c5\uff0c\u5374\u53c8\u504f\u504f\u7559\u7ed9\u4ed6\u4e00\u4e9b\u53c8\u8f9b\u9178\u53c8\u7f8e\u4e3d\u7684\u56de\u5fc6\uff0c\u4e0d\u4f46\u4ee4\u4ed6\u7ec8\u8eab\u96be\u4ee5\u5fd8\u6000\uff0c\u800c\u4e14\u6539\u53d8\u4e86\u4ed6\u7684\u4e00\u751f\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u59cb\u7ec8\u90fd\u5728\u8ddf\u7740\u4ed6\uff0c\u4e24\u4e2a\u4eba\u59cb\u7ec8\u90fd\u4fdd\u6301\u7740\u53ef\u4ee5\u8ba9\u5bf9\u65b9\u770b\u5f97\u89c1\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u5374\u5f88\u5c11\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u996e\u98df\u90fd\u975e\u5e38\u7684\u7b80\u5355\uff0c\u7761\u7720\u90fd\u5f88\u5c11\uff0c\u6709\u65f6\u4e24\u4e09\u5929\u4e4b\u5185\u8fde\u4e00\u53e5\u8bdd\u90fd\u4e0d\u8bf4\u3002 \u3000\u3000\u8fdb\u5165\u5927\u6f20\u4e4b\u540e\u7684\u7b2c\u4e00\u5929\uff0c\u72ec\u5b64\u75f4\u624d\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u77e5\u9053\u90a3\u6279\u9ec4\u91d1\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u56de\u7b54\u3002 \u3000\u3000\u76f4\u5230\u7b2c\u4e8c\u5929\u7684\u4e0b\u5348\uff0c\u5c0f\u65b9\u624d\u95ee\u72ec\u5b64\u75f4\uff1a\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u6211\u4eec\u7b2c\u4e00\u6b21\u76f8\u89c1\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u6211\u8bb0\u5f97\u3002\u201d \u3000\u3000\u201c\u9ec4\u91d1\u5c31\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4e24\u4e2a\u4eba\u5c31\u4e0d\u518d\u5f00\u53e3\uff0c\u597d\u50cf\u90fd\u89c9\u5f97\u8fd9\u4e00\u5929\u7684\u8bdd\u5df2\u7ecf\u5927\u591a\u4e86\u3002 \u3000\u3000\u53ef\u662f\u7b2c\u4e09\u5929\u7684\u5929\u4e00\u4eae\uff0c\u72ec\u5b64\u75f4\u5c31\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u8fd8\u627e\u4e0d\u627e\u5f97\u5230\u90a3\u5730\u65b9\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u5c0f\u65b9\u4e00\u70b9\u6ca1\u6709\u56de\u7b54\uff0c\u4e00\u76f4\u7b49\u5230\u7b2c\u56db\u5929\uff0c\u7b49\u5230\u4ed6\u4eec\u8d70\u5230\u4e00\u7247\u9ad8\u8038\u7684\u98ce\u5316\u5c71\u5ca9\u4e0b\uff0c\u5c0f\u65b9\u624d\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u6307\u7740\u4e00\u5757\u5c16\u5854\u822c\u51f8\u8d77\u7684\u5ca9\u77f3\u95ee\u72ec\u5b64\u75f4\uff1a\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u8fd9\u5757\u77f3\u5934\uff1f\u201d \u3000\u3000\u201c\u6211\u8bb0\u5f97\u3002\u201d \u3000\u3000\u4e8e\u662f\u5c0f\u65b9\u5c31\u505c\u4e0b\u6765\uff0c\u5728\u5c71\u5ca9\u4e0b\u627e\u4e86\u4e2a\u907f\u98ce\u5904\uff0c\u5f00\u59cb\u5403\u4ed6\u8fd9\u4e00\u5929\u7684\u7b2c\u4e00\u987f\u996d\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\u72ec\u5b64\u75f4\u624d\u95ee\uff1a\u201c\u9ec4\u91d1\u5c31\u5728\u4e0b\u9762\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5728\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u5728\u8fd9\u91cc\u505c\u4e0b\u6765\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6162\u6162\u5730\u5403\u5b8c\u4e86\u4e00\u4e2a\u9752\u68f5\u997c\u4e4b\u540e\u624d\u8bf4\uff1a\u201c\u9ec4\u91d1\u662f\u535c\u9e70\u548c\u73ed\u5bdf\u5df4\u90a3\u57cb\u85cf\u7684\uff0c\u77e5\u9053\u8fd9\u79d8\u5bc6\u7684\u672c\u6765\u5c31\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u73b0\u5728\u4f60\u4e5f\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\uff0c\u201c\u56e0\u4e3a\u535c\u9e70\u4e5f\u628a\u6211\u5e26\u5230\u4e86\u57cb\u85cf\u9ec4\u91d1\u7684\u5730\u65b9\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u4ed6\u5e26\u6211\u53bb\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u6df1\u591c\u3002\u6211\u4eec\u8d70\u7684\u65f6\u5019\uff0c\u5929\u5374\u5df2\u4eae\u4e86\u3002\u201d \u3000\u3000\u4ed6\u62ac\u5934\u4ef0\u671b\u9ad8\u8038\u5165\u4e91\u7684\u5854\u77f3\uff1a\u201c\u90a3\u65f6\u592a\u9633\u521a\u5347\u8d77\uff0c\u521a\u597d\u5c06\u8fd9\u5757\u77f3\u5934\u7684\u5f71\u5b50\uff0c\u7167\u5728\u57cb\u85cf\u9ec4\u91d1\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u6ca1\u6709\u518d\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u77e5\u9053\u5c0f\u65b9\u5728\u8fd9\u91cc\u505c\u4e0b\u6765\u662f\u4e3a\u4e86\u8981\u7b49\u660e\u5929\u7684\u65e5\u51fa\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u7528\u4e0d\u7740\u518d\u95ee\u4ec0\u4e48\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u5fcd\u4e0d\u4f4f\u8981\u95ee\u81ea\u5df1\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u4ed6\uff1f\u201d \u3000\u3000\u8fd9\u672c\u6765\u662f\u4e2a\u5f88\u96be\u89e3\u7b54\u7684\u95ee\u9898\uff0c\u53ef\u662f\u5c0f\u65b9\u5f88\u5feb\u5c31\u66ff\u81ea\u5df1\u627e\u5230\u4e86\u89e3\u91ca\u3002 \u3000\u3000\u4ed6\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u72ec\u5b64\u75f4\uff0c\u4e0d\u4ec5\u56e0\u4e3a\u4ed6\u6df1\u77e5\u72ec\u5b64\u75f4\u7edd\u4e0d\u662f\u4e2a\u4e3a\u9ec4\u91d1\u52a8\u5fc3\u7684\u4eba\u3002 \u3000\u3000\u6700\u5927\u7684\u539f\u56e0\u662f\uff1a\u4ed6\u8ba4\u4e3a\u8fd9\u6279\u9ec4\u91d1\u5df2\u7ecf\u4e0d\u5728\u535c\u9e70\u57cb\u85cf\u7684\u5730\u65b9\u4e86\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u8fd9\u79cd\u60f3\u6cd5\u662f\u600e\u4e48\u6765\u7684\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u5374\u786e\u4fe1\u4e0d\u7591\u3002 \u3000\u3000\u5915\u9633\u897f\u6c89\uff0c\u5bc2\u5bde\u6f2b\u957f\u5bd2\u82e6\u7684\u957f\u591c\uff0c\u53c8\u5c06\u7b3c\u7f69\u8fd9\u4e00\u7247\u65e0\u60c5\u7684\u5927\u5730\u3002 \u3000\u3000\u4ed6\u4eec\u71c3\u8d77\u4e86\u4e00\u5806\u706b\uff0c\u5404\u81ea\u9759\u5750\u5728\u706b\u5806\u7684\u4e00\u8fb9\uff0c\u51dd\u89c6\u7740\u95ea\u52a8\u7684\u706b\u5149\uff0c\u7b49\u7b49\u7740\u592a\u9633\u5347\u8d77\u3002 \u3000\u3000\u8fd9\u4e00\u591c\u65e0\u7591\u8981\u6bd4\u4ed6\u4eec\u4ee5\u5f80\u5728\u5927\u6f20\u4e2d\u5ea6\u8fc7\u7684\u4efb\u4f55\u4e00\u4e2a\u665a\u4e0a\u90fd\u66f4\u957f\u66f4\u51b7\u66f4\u96be\u6328\uff0c\u4ed6\u4eec\u90fd\u5df2\u7ecf\u5f88\u75b2\u5026\u3002 \u3000\u3000\u5c31\u5728\u5c0f\u65b9\u5feb\u8981\u95ed\u8d77\u773c\u775b\u65f6\uff0c\u4ed6\u5ffd\u7136\u542c\u89c1\u4e00\u58f0\u5c16\u9510\u800c\u77ed\u4fc3\u7684\u98ce\u58f0\u5212\u7a7a\u800c\u8fc7\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u770b\u89c1\u706b\u7130\u4e2d\u7206\u8d77\u4e86\u4e00\u9053\u91d1\u9ec4\u8272\u7684\u9633\u5149\uff0c\u7531\u91d1\u9ec4\u53d8\u4e3a\u6697\u8d64\uff0c\u53c8\u7531\u6697\u8d64\u53d8\u4e3a\u60e8\u78a7\u3002 \u3000\u3000\u60e8\u78a7\u8272\u7684\u706b\u5149\u4e2d\uff0c\u4eff\u4f5b\u6709\u51e0\u6761\u60e8\u78a7\u8272\u7684\u5f71\u5b50\u5728\u98de\u817e\u8dc3\u52a8\uff0c\u5ffd\u7136\u53c8\u5316\u4e3a\u8f7b\u70df\u56db\u6563\u3002 \u3000\u3000\u7b49\u5230\u8f7b\u70df\u6d88\u5931\u65f6\uff0c\u706b\u7130\u4e5f\u7184\u706d\u4e86\uff0c\u5929\u5730\u95f4\u53ea\u5269\u4e0b\u4e00\u7247\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff0c\u5c31\u597d\u50cf\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6709\u5149\u660e\u91cd\u73b0\u4e00\u6837\u3002 \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u52a8\uff0c\u72ec\u5b64\u75f4\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u89c1\u624d\u90a3\u79cd\u7a81\u7136\u53d1\u751f\u7684\u60ca\u4eba\u53d8\u5316\uff0c\u5728\u4ed6\u4eec\u7684\u773c\u4e2d\u770b\u6765\uff0c\u5c31\u597d\u50cf\u5929\u5929\u90fd\u4f1a\u53d1\u751f\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u53ef\u4ee5\u770b\u5f97\u89c1\uff0c\u4e00\u70b9\u90fd\u4e0d\u5947\u602a\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u672c\u6765\u5df2\u7184\u706d\u7684\u706b\u5806\u4e2d\uff0c\u5ffd\u7136\u53c8\u7206\u8d77\u4e86\u95ea\u4eae\u7684\u706b\u5149\u3002 \u3000\u3000\u7b49\u5230\u706b\u5149\u7531\u91d1\u9ec4\u8272\u53d8\u4e3a\u60e8\u78a7\u65f6\uff0c\u706b\u7130\u4e2d\u4eff\u4f5b\u53c8\u6709\u4e00\u6761\u4eba\u5f71\u5347\u8d77\uff0c\u5347\u5230\u9ad8\u5904\uff0c\u5316\u4e3a\u8f7b\u70df\u3002 \u3000\u3000\u8f7b\u70df\u56db\u6563\uff0c\u706b\u5149\u7184\u706d\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u54cd\u8d77\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u3002 \u3000\u3000\u7f25\u7f25\u7f08\u7f08\u7684\u58f0\u97f3\uff0c\u82e5\u6709\u82e5\u65e0\uff0c\u4f3c\u4eba\u4f3c\u9b3c\u3002 \u3000\u3000\u201c\u65b9\u4f1f\uff0c\u72ec\u5b64\u75f4\uff0c\u4f60\u4eec\u8d70\u5427\uff01\u201d\u8fd9\u58f0\u97f3\u8bf4\uff0c\u201c\u6700\u597d\u5feb\u8d70\uff0c\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u8fd8\u662f\u6ca1\u6709\u53cd\u5e94\uff0c\u5c0f\u65b9\u5374\u6709\u4e86\u3002 \u3000\u3000\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u6211\u4eec\u8d70\uff1f\u201d \u3000\u3000\u4ed6\u521a\u95ee\u5b8c\uff0c\u7acb\u523b\u5c31\u542c\u89c1\u6709\u4eba\u56de\u7b54\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u7b2c\u4e00\u4e2a\u56de\u7b54\u7684\u58f0\u97f3\u662f\u4ece\u897f\u9762\u4f20\u6765\u7684\u2014\u2014\u7f25\u7f25\u7f08\u7f08\u7684\u58f0\u97f3\uff0c\u4f3c\u4eba\u975e\u4eba\u3002 \u3000\u3000\u7136\u540e\u4e1c\u9762\u53c8\u6709\u540c\u6837\u7684\u58f0\u97f3\u5728\u8bf4\uff1a\u201c\u81ea\u4ece\u86a9\u5c24\u6218\u6b7b\uff0c\u5b9d\u85cf\u88ab\u6398\u540e\uff0c\u4e16\u4e0a\u6bcf\u4e00\u5b97\u5de8\u5927\u7684\u5b9d\u85cf\uff0c\u90fd\u6709\u5e7d\u7075\u9b3c\u9b42\u5728\u770b\u5b88\u3002\u201d \u3000\u3000\u5357\u9762\u4f20\u6765\u7684\u58f0\u97f3\u4eff\u4f5b\u66f4\u9065\u8fdc\u3002 \u3000\u3000\u201c\u6211\u4eec\u5c31\u662f\u66ff\u535c\u9e70\u770b\u5b88\u8fd9\u4e00\u6279\u9ec4\u91d1\u5b9d\u85cf\u7684\u9b3c\u9b42\u3002\u201d \u3000\u3000\u5317\u9762\u7684\u58f0\u97f3\u63a5\u7740\u8bf4\uff1a\u201c\u6211\u4eec\u90fd\u662f\u4e3a\u535c\u9e70\u6218\u6b7b\u7684\u4eba\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u6d3b\u7740\u65f6\u662f\u6218\u58eb\uff0c\u6b7b\u4e86\u4e5f\u662f\u5389\u9b3c\uff0c\u6211\u4eec\u7edd\u4e0d\u5bb9\u4efb\u4f55\u4eba\u4fb5\u72af\u4ed6\u7684\u9ec4\u91d1\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u6de1\u6de1\u5730\u95ee\uff1a\u201c\u5982\u679c\u6211\u4eec\u4e0d\u60f3\u8d70\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4eec\u5c31\u8981\u6b7b\u5728\u8fd9\u91cc\u4e86\u3002\u201d\u897f\u9762\u7684\u58f0\u97f3\u8bf4\uff0c\u201c\u800c\u4e14\u6b7b\u5f97\u5f88\u60e8\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u4e86\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u53ea\u53ef\u60dc\u4f60\u4eec\u8bf4\u7684\u8bdd\u6211\u8fde\u4e00\u53e5\u90fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u56db\u9762\u516b\u65b9\u90fd\u6ca1\u6709\u4eba\u518d\u8bf4\u8bdd\u4e86\u2014\u2014\u4e0d\u7ba1\u8bf4\u8bdd\u7684\u662f\u4eba\u662f\u9b3c\uff0c\u90fd\u4e0d\u518d\u5f00\u53e3\u3002 \u3000\u3000\u672c\u6765\u5df2\u7ecf\u7184\u706d\u7684\u706b\u5806\u4e2d\uff0c\u5374\u53c8\u95ea\u8d77\u4e86\u706b\u5149\u3002 \u3000\u3000\u9ec4\u91d1\u822c\u7684\u706b\u5149\u521a\u95ea\u4eae\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u6709\u5341\u516d\u516b\u6761\u4eba\u5f71\u98de\u6765\u3002 \u3000\u3000\u7b49\u5230\u706b\u5149\u53d8\u4e3a\u6697\u8d64\uff0c\u8fd9\u4e9b\u4eba\u5f71\u5df2\u843d\u5728\u5730\u4e0a\uff0c\u6709\u7684\u5f71\u5b50\u843d\u5728\u5730\u4e0a\u8d77\u53d1\u51fa\u201c\u54af\u201d\u7684\u4e00\u54cd\uff0c\u6709\u7684\u54cd\u58f0\u5374\u597d\u50cf\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\u3002 \u3000\u3000\u56e0\u4e3a\u843d\u4e0b\u6765\u7684\u8fd9\u4e9b\u4eba\u5f71\u672c\u6765\u867d\u7136\u90fd\u662f\u4eba\uff0c\u4f46\u662f\u73b0\u5728\u6709\u4e9b\u5df1\u5b8c\u5168\u51b0\u51b7\u50f5\u786c\uff0c\u6709\u4e9b\u5df2\u53d8\u6210\u4e86\u67af\u9aa8\uff0c\u4e00\u8dcc\u5c31\u788e\u7684\u67af\u9aa8\u3002 \u3000\u3000\u897f\u9762\u90a3\u7f25\u7f08\u9634\u68ee\u7684\u58f0\u97f3\u53c8\u5728\u95ee\uff1a\u201c\u6211\u4eec\u8bf4\u7684\u8bdd\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u4fe1\uff01\u201d\u5c0f\u65b9\u4f9d\u65e7\u540c\u6837\u56de\u7b54\uff0c\u201c\u6211\u8fde\u4e00\u53e5\u90fd\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e0d\u59a8\u5148\u770b\u770b\u8fd9\u4e9b\u4eba\u3002\u201d\u5357\u9762\u6709\u4eba\u8bf4\uff0c\u201c\u56e0\u4e3a\u4f60\u5f88\u5feb\u5c31\u4f1a\u53d8\u5f97\u8ddf\u4ed6\u4eec\u4e00\u6837\u4e86\uff0c\u4ed6\u4eec\u4e5f\u662f\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u6ca1\u6709\u8bf4\u5b8c\uff0c\u56e0\u4e3a\u4e00\u76f4\u6ca1\u6709\u53cd\u5e94\u7684\u72ec\u5b64\u75f4\u6709\u4e86\u53cd\u5e94\u3002 \u3000\u3000\u4e00\u79cd\u65e0\u8bba\u4efb\u4f55\u4eba\u770b\u89c1\u90fd\u4f1a\u5927\u5403\u4e00\u60ca\u7684\u53cd\u5e94\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u8eab\u5b50\u5ffd\u7136\u8e7f\u8d77\uff0c\u5c31\u50cf\u4e00\u6839\u7bad\u4e00\u6837\u5c04\u4e86\u51fa\u53bb\uff0c\u5c04\u51fa\u58f0\u97f3\u4f20\u51fa\u7684\u5730\u65b9\uff0c\u5c04\u5411\u5357\u65b9\u3002 \u3000\u3000\u5357\u65b9\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u4eba\u5f71\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\u65f6\uff0c\u5357\u65b9\u5c31\u4f20\u51fa\u4e00\u58f0\u60e8\u53eb\u3002 \u3000\u3000\u8fd9\u65f6\u5c0f\u65b9\u7684\u4eba\u4e5f\u5df2\u8e7f\u8d77\uff0c\u4e5f\u50cf\u662f\u4e00\u6839\u7bad\u4e00\u6837\u5c04\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5357\u65b9\u7684\u60e8\u547c\u58f0\u53d1\u51fa\u65f6\uff0c\u4ed6\u7684\u4eba\u5df2\u5230\u4e86\u897f\u65b9\u7684\u4e00\u5757\u5ca9\u77f3\u4e0a\u3002 \u3000\u3000\u897f\u65b9\u4e5f\u540c\u6837\u662f\u4e00\u7247\u9ed1\u6697\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u6709\u4e86\u5200\u5149\u4e00\u95ea\uff0c\u95ea\u7535\u822c\u780d\u5411\u5c0f\u65b9\u7684\u817f\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u62db\u67b6\uff0c\u4e0d\u95ea\u907f\uff0c\u957f\u5251\u6025\u6325\uff0c\u5251\u950b\u8d34\u7740\u5200\u950b\u76f4\u5212\u8fc7\u53bb\uff0c\u524a\u65ad\u4e86\u5200\u6115\uff0c\u524a\u65ad\u4e86\u63e1\u5200\u7684\u624b\u3002 \u3000\u3000\u897f\u65b9\u7684\u9ed1\u6697\u4e2d\u7acb\u523b\u4e5f\u4f20\u51fa\u4e00\u58f0\u60e8\u547c\uff0c\u547c\u58f0\u5ffd\u7136\u53c8\u505c\u6b62\u3002 \u3000\u3000\u5251\u950b\u5df2\u523a\u4eba\u5fc3\u810f\u3002 \u3000\u3000\u547c\u58f0\u505c\u6b62\u65f6\uff0c\u5c0f\u65b9\u5c31\u542c\u89c1\u72ec\u5b64\u75f4\u5728\u51b7\u51b7\u5730\u4e3a\u4ed6\u559d\u91c7\uff1a \u3000\u3000\u201c\u597d\u5feb\u7684\u5251\uff1b\u597d\u72e0\u7684\u51fa\u624b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u56de\u7b54\u5f97\u5f88\u5999\uff1a\u201c\u5f7c\u6b64\u5f7c\u6b64\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4e0d\u61c2\u4f60\u4e3a\u4ec0\u4e48\u8981\u4e0b\u6bd2\u624b\uff1f\u201d\u72ec\u5b64\u75f4\u95ee\uff0c\u201c\u4f60\u77e5\u9053\u4ed6\u4e0d\u662f\u535c\u9e70\u7684\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u535c\u9e70\u7684\u5c5e\u4e0b\u4ece\u6765\u6ca1\u6709\u4eba\u6562\u76f4\u547c\u4ed6\u7684\u540d\u5b57\u3002\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u5927\u5bb6\u90fd\u53eb\u4ed6\u9e70\u54e5\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u5f88\u7ec6\u5fc3\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u7684\u58f0\u97f3\u91cc\u5b8c\u5168\u6ca1\u6709\u4e00\u70b9\u8ba5\u523a\u4e4b\u610f\uff0c\u201c\u50cf\u6211\u4eec\u8fd9\u79cd\u4eba\uff0c\u4e00\u5b9a\u8981\u7ec6\u5fc3\uff0c\u624d\u80fd\u6d3b\u5f97\u957f\u4e9b\u3002\u201d \u3000\u3000\u4ed6\u4eec\u90fd\u4e0d\u662f\u559c\u8bf4\u8bdd\u7684\u4eba\uff0c\u8fd9\u4e9b\u8bdd\u4e5f\u4e0d\u662f\u5e94\u8be5\u5728\u8fd9\u79cd\u65f6\u5019\u8bf4\u7684\u3002 \u3000\u3000\u5929\u8272\u5982\u58a8\uff0c\u5f3a\u654c\u73af\u4f3a\uff0c\u4e00\u5f00\u53e3\u8bf4\u8bdd\u5c31\u66b4\u9732\u4e86\u76ee\u6807\uff0c\u5404\u5f0f\u5404\u6837\u4e0d\u540c\u7684\u5175\u5203\u6697\u5668\u5c31\u968f\u65f6\u53ef\u80fd\u4f1a\u4ece\u5404\u79cd\u4e0d\u540c\u7684\u65b9\u5411\u653b\u51fb\u3002 \u3000\u3000\u6bcf\u4e00\u6b21\u653b\u51fb\u90fd\u53ef\u80fd\u662f\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u6709\u7ecf\u9a8c\u7684\u4eba\u90fd\u4f1a\u7d27\u7d27\u5730\u95ed\u7740\u5634\uff0c\u7b49\u5230\u5bf9\u65b9\u6c89\u4e0d\u4f4f\u6c14\u65f6\u624d\u51fa\u624b\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u72ec\u5b64\u75f4\u90fd\u662f\u6709\u7ecf\u9a8c\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u8eab\u7ecf\u767e\u6218\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u8fd9\u79cd\u7ecf\u9a8c\u6bd4\u8c01\u90fd\u4e30\u5bcc\u3002 \u3000\u3000\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u5728\u8fd9\u79cd\u65f6\u5019\u8bf4\u8fd9\u4e9b\u672c\u6765\u5e76\u4e0d\u662f\u4e00\u5b9a\u8981\u8bf4\u7684\u8bdd\uff1f \u3000\u3000\u8fd9\u672c\u6765\u4e5f\u662f\u5f88\u96be\u56de\u7b54\u7684\u95ee\u9898\uff0c\u53ef\u662f\u7b54\u6848\u5374\u7b80\u5355\u6781\u4e86\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u5411\u5bf9\u65b9\u66b4\u9732\u4e86\u81ea\u5df1\u7684\u76ee\u6807\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u5e0c\u671b\u5bf9\u65b9\u51fa\u624b\u3002 \u3000\u3000\u5929\u8272\u5982\u58a8\uff0c\u5f3a\u654c\u73af\u4f3a\uff0c\u53ef\u662f\u5bf9\u65b9\u5982\u679c\u4e0d\u51fa\u624b\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u5bf9\u65b9\u9690\u85cf\u5728\u54ea\u91cc\uff1f \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u79cd\u6218\u7565\uff0c\u4e00\u79cd\u8bf1\u654c\u4e4b\u8ba1\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u4eec\u7684\u6218\u7565\u6210\u529f\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5bf9\u65b9\u7684\u653b\u51fb\u5df2\u5f00\u59cb\u3002 \u3000\u3000\u7b2c\u4e00\u6b21\u653b\u51fb\u6765\u81ea\u5317\u65b9\u3002 \u3000\u3000\u5982\u679c\u5c0f\u65b9\u4e0d\u662f\u5c0f\u65b9\uff0c\u4ed6\u5df2\u7ecf\u6b7b\u5728\u8fd9\u4e00\u6b21\u653b\u51fb\u4e0b\uff01 \u3000\u3000\u4ed6\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u6709\u8fc7\u5341\u4e5d\u6b21\u6fd2\u4e34\u6b7b\u4ea1\u7684\u7ecf\u9a8c\uff0c\u5982\u679c\u4ed6\u7684\u53cd\u5e94\u6162\u4e00\u70b9\uff0c\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u5341\u4e5d\u6b21\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u6240\u4ee5\u4ed6\u542c\u89c1\u4e86\u90a3\u4e00\u9053\u98ce\u58f0\uff0c\u4e00\u9053\u6781\u5c16\u7ec6\u6781\u8f7b\u5fae\u7684\u98ce\u58f0\u3002 \u3000\u3000\u4e00\u9053\u6781\u5feb\u7684\u98ce\u58f0\uff0c\u4ece\u5317\u65b9\u6253\u6765\uff0c\u6253\u4ed6\u7684\u8981\u5bb3\uff0c\u81f4\u547d\u7684\u8981\u5bb3\u3002 \u3000\u3000\u5c0f\u65b9\u6325\u5251\uff0c\u5251\u950b\u4e0a\u7acb\u523b\u7206\u51fa\u4e03\u70b9\u5bd2\u661f\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4e00\u5251\u51fb\u843d\u8fd9\u4e03\u679a\u6697\u5668\u65f6\uff0c\u5df2\u7ecf\u6709\u4e00\u7f15\u9510\u98ce\u523a\u5411\u4ed6\u7684\u8170\u3002 \u3000\u3000\u523a\u6765\u7684\u4e0d\u662f\u6697\u5668\uff0c\u662f\u67aa\uff0c\u6700\u5c11\u6709\u4e09\u56db\u5341\u65a4\u91cd\u7684\u68a8\u82b1\u5927\u67aa\uff0c\u81ea\u9ed1\u6697\u4e2d\u6162\u6162\u5730\u65e0\u58f0\u65e0\u606f\u5730\u523a\u6765\uff0c\u76f4\u5230\u8ddd\u79bb\u5c0f\u65b9\u8170\u95f4\u4e0d\u53ca\u4e00\u5c3a\u65f6\uff0c\u624d\u52a0\u5feb\u901f\u5ea6\u3002 \u3000\u3000\u5c0f\u65b9\u611f\u89c9\u5230\u67aa\u950b\u4e0a\u7684\u9510\u98ce\u65f6\uff0c\u751f\u6b7b\u5df2\u5728\u547c\u5438\u4e4b\u95f4\u3002 \u3000\u3000\u4ed6\u731b\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u8eab\u5b50\u7a81\u7136\u62d4\u8d77\u3002 \u3000\u3000\u67aa\u950b\u523a\u7834\u4e86\u4ed6\u7684\u8863\u670d\uff0c\u4ed6\u51cc\u7a7a\u7ffb\u8eab\uff0c\u957f\u5251\u5212\u8d77\u4e00\u9053\u5149\u5f27\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u4e86\u4e00\u4e2a\u4eba\u7684\u8138\u3002 \u3000\u3000\u68ee\u5bd2\u7684\u5251\u5149\u6b63\u7167\u5728\u8fd9\u4e2a\u4eba\u7684\u8138\u4e0a\uff0c\u4e00\u5f20\u65b9\u65b9\u6b63\u6b63\u957f\u6ee1\u4e86\u8d64\u53d1\u866c\u9aef\u7684\u8138\u5df2\u56e0\u6050\u60e7\u800c\u626d\u66f2\uff0c\u770b\u6765\u5c31\u50cf\u662f\u5f20\u63c9\u76b1\u4e86\u7684\u76b1\u5b98\u56fe\u50cf\u3002 \u3000\u3000\u5251\u5149\u518d\u4e00\u95ea\uff0c\u8fd9\u5f20\u8138\u5c31\u770b\u4e0d\u89c1\u4e86\uff0c\u8fd9\u4e2a\u4eba\u4e5f\u5df2\u4ece\u6b64\u6d88\u5931\u3002 \u3000\u3000\u5728\u67aa\u95f4\u5200\u950b\u5251\u5149\u4e0b\uff0c\u4e00\u4e2a\u4eba\u7684\u751f\u547d\u5c31\u50cf\u811a\u5e95\u4e0b\u624b\u638c\u95f4\u7684\u868a\u8747\uff0c\u5728\u4e00\u5239\u90a3\u95f4\u5c31\u4f1a\u88ab\u6d88\u706d\u3002 \u3000\u3000\u5982\u679c\u4f60\u6ca1\u6709\u7ecf\u9a8c\u8fc7\u8fd9\u79cd\u4e8b\uff0c\u4f60\u6c38\u8fdc\u4e0d\u4f1a\u60f3\u5230\u4eba\u7c7b\u7684\u751f\u547d\u6709\u65f6\u7adf\u4f1a\u53d8\u5f97\u5982\u6b64\u8f7b\u8d31\u3002 \u3000\u3000\u7b2c\u4e00\u6b21\u653b\u51fb\u8fd8\u672a\u7ed3\u675f\uff0c\u7b2c\u4e8c\u6b21\u5df2\u5f00\u59cb\uff0c\u7b2c\u4e8c\u6b21\u653b\u51fb\u5931\u8d25\uff0c\u8fd8\u6709\u7b2c\u4e09\u6b21\u3002 \u3000\u3000\u653b\u51fb\u5c31\u50cf\u662f\u6d77\u6d6a\uff0c\u4e00\u6b21\u63a5\u7740\u4e00\u6b21\uff0c\u4eff\u4f5b\u6c38\u65e0\u4f11\u6b62\u7684\u65f6\u5019\u3002 \u3000\u3000\u6bcf\u4e00\u6b21\u653b\u51fb\u90fd\u53ef\u80fd\u81f4\u547d\uff0c\u6bcf\u4e00\u6b21\u653b\u51fb\u90fd\u53ef\u80fd\u662f\u6700\u540e\u4e00\u6b21\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u4e8c\u7ae0\u3000\u98ce\u66b4 \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u98ce\u66b4 \u3000\u3000\u5c0f\u65b9\u7684\u773c\u89d2\u5df2\u7ecf\u5f00\u59cb\u5728\u523a\u75db\uff0c\u56e0\u4e3a\u6c57\u6c34\u5df2\u7ecf\u6d41\u5165\u4e86\u4ed6\u7684\u773c\u3002 \u3000\u3000\u4ed6\u5f88\u60f3\u4f38\u624b\u53bb\u64e6\u5e72\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u80fd\u3002 \u3000\u3000\u4efb\u4f55\u4e00\u4e2a\u4e0d\u5fc5\u8981\u7684\u52a8\u4f5c\uff0c\u90fd\u53ef\u80fd\u9020\u6210\u81f4\u547d\u7684\u758f\u5ffd\u548c\u9519\u8bef\u3002 \u3000\u3000\u9664\u4e86\u653b\u51fb\u62db\u67b6\u95ea\u907f\u5916\uff0c\u4efb\u4f55\u52a8\u4f5c\u90fd\u662f\u4e0d\u5fc5\u8981\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u8eab\u4e0a\u6bcf\u4e00\u6839\u808c\u8089\u90fd\u5df2\u7ecf\u5728\u5f00\u59cb\u62bd\u75db\uff0c\u5c31\u50cf\u662f\u4e00\u6839\u6839\u7ef7\u5f97\u592a\u7d27\u5df1\u5c06\u7ef7\u65ad\u7684\u5f13\u5f26\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u79cd\u60c5\u51b5\u4e0d\u597d\uff0c\u4ed6\u5f88\u60f3\u653e\u677e\u81ea\u5df1\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u80fd\u3002 \u3000\u3000\u4e00\u77ac\u95f4\u7684\u677e\u9a70\uff0c\u5c31\u53ef\u80fd\u5bfc\u81f4\u6c38\u6052\u7684\u6bc1\u706d\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u7a76\u7adf\u9690\u85cf\u6709\u591a\u5c11\u6740\u4eba\u7684\u6740\u624b\uff1f\u653b\u51fb\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\u624d\u4f1a\u505c\u6b62\u3002 \u3000\u3000\u653b\u51fb\u5ffd\u7136\u95f4\u5c31\u505c\u6b62\u4e86\u3002\u2014\u2014\u867d\u7136\u8c01\u4e5f\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u505c\u6b62\u7684\uff0c\u5c31\u6b63\u5982\u8c01\u4e5f\u6ca1\u6cd5\u4e8e\u786e\u5b9a\u6700\u540e\u4e00\u6ef4\u96e8\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u843d\u4e0b\u7684\u4e00\u6837\u3002 \u3000\u3000\u7a7a\u6c14\u4e2d\u4ecd\u5e26\u7740\u79cd\u4ee4\u4eba\u60ca\u6817\u4f5c\u5455\u7684\u8840\u8165\u6c14\uff0c\u5927\u5730\u5374\u5df2\u6062\u590d\u9759\u5bc2\u3002 \u3000\u3000\u4ee4\u5c0f\u65b9\u89c9\u9192\u7684\u662f\u4ed6\u81ea\u5df1\u7684\u5598\u606f\u58f0\u3002 \u3000\u3000\u4ed6\u62ac\u8d77\u5934\uff0c\u624d\u77e5\u9053\u4e1c\u65b9\u5df2\u73b0\u51fa\u66d9\u8272\uff0c\u4eba\u4e73\u767d\u8272\u7684\u6668\u96fe\u4e2d\u770b\u8fc7\u53bb\uff0c\u4f9d\u7a00\u53ef\u4ee5\u770b\u89c1\u626d\u66f2\u5012\u5367\u5728\u7802\u783e\u5ca9\u77f3\u4e2d\u7684\u5c38\u4f53\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u4e2a\u4e2a\u7834\u788e\u6495\u88c2\u4e86\u7684\u73a9\u5076\u3002 \u3000\u3000\u2014\u2014\u653b\u51fb\u5df2\u7ed3\u675f\uff0c\u5371\u9669\u5df2\u8fc7\u53bb\uff0c\u5929\u5df2\u7ecf\u5feb\u4eae\u4e86\u3002 \u3000\u3000\u4e00\u79cd\u56e0\u5b8c\u5168\u677e\u9a70\u800c\u4ea7\u751f\u7684\u75b2\u5026\uff0c\u5ffd\u7136\u50cf\u53ea\u9b54\u624b\u822c\u652b\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u90fd\u5df2\u51e0\u4e4e\u865a\u8131\u3002\u4ed6\u6ca1\u6709\u5012\u4e0b\u53bb\uff0c\u53ea\u56e0\u4e3a\u4e1c\u65b9\u7684\u4e91\u5806\u4e2d\u5df2\u6709\u9633\u5149\u7167\u5c04\u51fa\u6765\uff0c\u7167\u4e0a\u5c71\u5ca9\uff0c\u7167\u4e0a\u90a3\u9ad8\u8038\u7684\u5854\u77f3\uff0c\u5c06\u90a3\u5c16\u5854\u822c\u7684\u5f71\u5b50\u7167\u5c04\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5c0f\u65b9\u5954\u51fa\u53bb\uff0c\u5c06\u638c\u4e2d\u5251\u7528\u529b\u63b7\u51fa\uff0c\u63b7\u5728\u5854\u5f71\u7684\u5c16\u7aef\u3002 \u3000\u3000\u5251\u950b\u6ca1\u5165\u7802\u77f3\uff0c\u5251\u67c4\u4e0d\u505c\u6447\u6643\u3002 \u3000\u3000\u201c\u5c31\u662f\u8fd9\u91cc\u3002\u201d\u5c0f\u65b9\u7684\u58f0\u97f3\u5df2\u56e0\u5174\u594b\u800c\u5636\u54d1\uff1a\u201c\u9ec4\u91d1\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u2014\u2014\u9ec4\u91d1\u5c31\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u2014\u2014\u8fd9\u91cc\u5c31\u662f\u6240\u6709\u79d8\u5bc6\u7684\u6839\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u8c01\u90fd\u96be\u514d\u4f1a\u5174\u594b\u6fc0\u52a8\u7684\u3002 \u3000\u3000\u4ed6\u7684\u808c\u8089\u5ffd\u7136\u53c8\u62bd\u7d27\uff0c\u638c\u5fc3\u5ffd\u7136\u53c8\u5192\u51fa\u51b7\u6c57\uff0c\u4ed6\u7684\u77b3\u5b54\u5ffd\u7136\u53c8\u56e0\u6050\u60e7\u800c\u6536\u7f29\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u6b63\u7ad9\u5728\u4ed6\u9762\u524d\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u638c\u4e2d\u7684\u5251\u950b\u6b63\u597d\u5728\u4e00\u51fa\u624b\u5c31\u53ef\u4ee5\u523a\u5165\u4ed6\u5fc3\u810f\u7684\u5730\u65b9\u3002 \u3000\u3000\u592a\u9633\u6e10\u6e10\u5347\u8d77\uff0c\u5c0f\u65b9\u7684\u5fc3\u5374\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5fd8\u8bb0\u72ec\u5b64\u75f4\u7684\u8bdd\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u4e00\u6709\u673a\u4f1a\uff0c\u6211\u5c31\u6740\u4e86\u4f60\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u673a\u4f1a\u5df2\u7ecf\u6765\u4e86\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u81ea\u5df1\u5f53\u7136\u77e5\u9053\uff0c\u5c0f\u65b9\u4e5f\u77e5\u9053\u3002 \u3000\u3000\u3002\u53ea\u8981\u72ec\u5b64\u75f4\u4e00\u5251\u523a\u51fa\uff0c\u4ed6\u51e0\u4e4e\u5df2\u5b8c\u5168\u4e0d\u53ef\u80fd\u62b5\u6321\u95ea\u907f\u62db\u67b6\uff01 \u3000\u3000\u72ec\u5b64\u75f4\u638c\u4e2d\u6709\u5251\uff0c\u5251\u950b\u4e0a\u7684\u8840\u8ff9\u4ecd\u672a\u5e72\uff0c\u63e1\u5251\u7684\u624b\u5df2\u6709\u9752\u7b4b\u51f8\u8d77\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5251\u4f1a\u4e0d\u4f1a\u523a\u51fa\u6765\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u5251\u4e5f\u5728\u4ed6\u4f38\u624b\u53ef\u53ca\u4e4b\u5904\uff0c\u4ed6\u6ca1\u6709\u4f38\u624b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u53ea\u8981\u4e00\u4f38\u51fa\u624b\uff0c\u5c31\u5fc5\u5c06\u6b7b\u5728\u72ec\u5b64\u75f4\u5251\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e0d\u4f38\u624b\uff0c\u7ed3\u5c40\u4e5f\u53ef\u80fd\u662f\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u662f\u4f60\uff0c\u73b0\u5728\u4e5f\u4e00\u5b9a\u4f1a\u51fa\u624b\u7684\u3002\u201d\u5c0f\u65b9\u5ffd\u7136\u8bf4\uff1a\u201c\u6240\u4ee5\u4f60\u5982\u679c\u6740\u4e86\u6211\uff0c\u6211\u4e5f\u6b7b\u4e5f\u65e0\u6028\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u6ca1\u6709\u5f00\u53e3\uff0c\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u2014\u2014\u8981\u6740\u4eba\u7684\uff0c\u901a\u5e38\u90fd\u4e0d\u4f1a\u591a\u8bf4\u8bdd\u7684\u3002 \u3000\u3000\u968f\u65f6\u90fd\u53ef\u80fd\u88ab\u6740\u7684\u4eba\u60c5\u51b5\u5c31\u4e0d\u540c\u3002 \u3000\u3000\u5982\u679c\u80fd\u591a\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5c31\u4e00\u5b9a\u8981\u60f3\u6cd5\u5b50\u8bf4\u51fa\u6765\uff0c\u54ea\u6015\u53ea\u80fd\u591a\u6d3b\u7247\u523b\u4e5f\u662f\u597d\u7684\u3002 \u3000\u3000\u201c\u4f46\u662f\u6211\u5e0c\u671b\u4f60\u7b49\u4e00\u7b49\u518d\u51fa\u624b\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u6ca1\u6709\u95ee\u4ed6\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u81ea\u5df1\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u56e0\u4e3a\u6211\u8fd8\u60f3\u77e5\u9053\u4e00\u4ef6\u4e8b\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u5982\u679c\u4f60\u80fd\u8ba9\u6211\u67e5\u51fa\u8fd9\u4ef6\u4e8b\u4e4b\u540e\u518d\u6b7b\uff0c\u6211\u5c31\u6b7b\u800c\u65e0\u61be\u4e86\uff01\u201d \u3000\u3000\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\u4e4b\u540e\u72ec\u5b64\u75f4\u624d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u8981\u6b7b\u800c\u65e0\u6028\uff0c\u5df2\u7ecf\u5f88\u4e0d\u5bb9\u6613\uff0c\u8981\u6b7b\u800c\u65e0\u61be\u66f4\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u53ea\u4e0d\u8fc7\u6709\u8d44\u683c\u505a\u6211\u5bf9\u624b\u7684\u4eba\u4e5f\u4e0d\u591a\uff0c\u201d\u72ec\u5b64\u75f4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u60f3\u77e5\u9053\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u60f3\u77e5\u9053\u90a3\u6279\u9ec4\u91d1\u662f\u4e0d\u662f\u8fd8\u5728\u8fd9\u91cc\uff1f\u201d\u5c0f\u65b9\u56de\u7b54\uff1a\u201c\u5426\u5219\u6211\u5b9e\u5728\u6b7b\u4e0d\u7791\u76ee\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u786e\u5b9a\u9ec4\u91d1\u672c\u6765\u771f\u7684\u662f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u80fd\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6211\u4eb2\u773c\u770b\u89c1\u8fc7\uff0c\u4ece\u8fd9\u91cc\u6316\u4e0b\u53bb\uff0c\u4e00\u5b9a\u53ef\u4ee5\u770b\u5230\u9ec4\u91d1\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u201c\u597d\uff01\u4f60\u6316\uff01\u201d \u3000\u3000\u201c\u6211\u6316\uff01\u201d\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u7528\u4ec0\u4e48\u6316\uff1f\u201d \u3000\u3000\u201c\u7528\u4f60\u7684\u5251\uff01\u201d\u72ec\u5b64\u75f4\u58f0\u97f3\u51b0\u51b7\uff1a\u201c\u5982\u679c\u4f60\u4e0d\u60f3\u7528\u4f60\u7684\u5251\uff0c\u5c31\u7528\u4f60\u7684\u624b\uff01\u201d \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u53c8\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u9ec4\u91d1\u57cb\u5f97\u5f88\u6df1\uff0c\u4e0d\u7ba1\u7528\u624b\u6316\u4e5f\u597d\uff0c\u7528\u5251\u6316\u4e5f\u597d\uff0c\u8981\u6316\u5230\u9ec4\u91d1\u7684\u57cb\u85cf\u5904\uff0c\u90fd\u8981\u6d88\u8017\u5f88\u591a\u6c14\u529b\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u6c14\u529b\u5df2\u5c06\u5c3d\uff0c\u5982\u679c\u518d\u591a\u6d88\u8017\u4e00\u5206\uff0c\u6d3b\u547d\u7684\u673a\u4f1a\u5c31\u66f4\u5c11\u4e00\u5206\u3002 \u3000\u3000\u53ef\u60dc\u73b0\u5728\u4ed6\u5df2\u522b\u65e0\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u5c0f\u65b9\u4f38\u624b\u62d4\u5251\u3002\u72ec\u5b64\u75f4\u5c31\u5728\u4ed6\u9762\u524d\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5982\u679c\u4ed6\u4e00\u5251\u523a\u51fa\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u53ef\u4ee5\u523a\u5165\u72ec\u5b64\u75f4\u7684\u5fc3\u810f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6ca1\u6709\u8fd9\u4e48\u505a\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u4ed6\u523a\u5165\u4e86\u5730\u4e0b\u3002 \u3000\u3000\u5730\u4e0b\u6ca1\u6709\u9ec4\u91d1\uff0c\u8fde\u4e00\u4e24\u9ec4\u91d1\u90fd\u6ca1\u6709\u3002\u5c0f\u65b9\u5c45\u7136\u4e5f\u8fde\u4e00\u70b9\u60ca\u8bb6\u7684\u610f\u601d\u90fd\u6ca1\u6709\uff0c\u8fd9\u4ef6\u4e8b\u597d\u50cf\u672c\u6765\u5c31\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u4f60\u4f1a\u4e0d\u4f1a\u8bb0\u9519\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u3002\u201d\u5c0f\u65b9\u7684\u56de\u7b54\u6781\u80af\u5b9a\uff1a\u201c\u7edd\u5bf9\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u90a3\u6279\u9ec4\u91d1\u672c\u6765\u786e\u5b9e\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u77e5\u9053\u85cf\u91d1\u5904\u7684\u4eba\u6709\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u201c\u4e09\u4e2a\u3002\u201d \u3000\u3000\u201c\u9664\u4e86\u4f60\u548c\u535c\u9e70\u4e4b\u5916\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u73ed\u5bdf\u5df4\u90a3\u3002\u201d \u3000\u3000\u2014\u2014\u73ed\u5bdf\u5df4\u90a3\uff0c\u4e00\u4e2a\u5bc2\u5bde\u7684\u9690\u58eb\uff0c\u4e00\u4f4d\u6700\u53d7\u6b22\u8fce\u7684\u6c11\u65cf\u82f1\u96c4\uff0c\u4e00\u4e2a\u5b64\u72ec\u7684\u6d41\u6d6a\u5ba2\uff0c\u4e00\u4f4d\u6ee1\u8154\u70ed\u8840\u7684\u7231\u56fd\u5fd7\u58eb\uff0c\u4e00\u4e2a\u51b7\u8840\u7684\u6740\u4eba\u8005\uff0c\u4e00\u4e2a\u6c38\u8fdc\u90fd\u6ca1\u6709\u4eba\u80fd\u591f\u4e86\u89e3\u7684\u4eba\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6709\u4ed6\u8fd9\u79cd\u77db\u76fe\u800c\u590d\u6742\u7684\u6027\u683c\u3002 \u3000\u3000\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u4f1a\u4ece\u54ea\u91cc\u6765\uff1f\u4f1a\u5f80\u54ea\u91cc\u53bb\uff1f\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u5728\u60f3\u4ec0\u4e48\uff1f \u3000\u3000\u66f4\u6ca1\u6709\u4eba\u80fd\u9884\u6d4b\u4ed6\u4f1a\u505a\u51fa\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u542c\u89c1\u4ed6\u7684\u540d\u5b57\uff0c\u8fde\u72ec\u5b64\u75f4\u7684\u8138\u90fd\u4eff\u4f5b\u6709\u70b9\u53d8\u4e86\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u65e9\u5c31\u77e5\u9053\u9ec4\u91d1\u85cf\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u9ec4\u91d1\u662f\u4e0d\u662f\u4f60\u76d7\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u4f1a\u4e0d\u4f1a\u81ea\u5df1\u6d88\u5931\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5408\u201d \u3000\u3000\u201c\u90a3\u4e48\u8fd9\u6279\u9ec4\u91d1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u5ffd\u7136\u51b7\u7b11\u3002 \u3000\u3000\u201c\u5176\u5b9e\u4ed6\u5e94\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u80fd\u76d7\u8d70\u8fd9\u6279\u9ec4\u91d1\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\uff0c\u2018\u73ed\u5bdf\u5df4\u90a3\u3002\u201d\u72ec\u5b64\u75f4\u9053\uff1a\u201c\u53ea\u6709\u73ed\u5bdf\u5df4\u90a3\u3002\u201d \u3000\u3000\u8fd9\u63a8\u7406\u672c\u6765\u5f88\u5408\u7406\uff0c\u5c0f\u65b9\u5374\u4e0d\u540c\u610f\u3002 \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u80fd\u8fd0\u8d70\u8fd9\u6279\u9ec4\u91d1\u7684\uff0c\u9664\u4e86\u73ed\u5bdf\u5df4\u90a3\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u535c\u9e70\uff01\u201d\u5c0f\u65b9\u9053\uff0c\u201c\u9664\u4e86\u73ed\u5bdf\u5df4\u90a3\u5916\uff0c\u8fd8\u6709\u535c\u9e70\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u662f\u535c\u9e70\u81ea\u5df1\u76d7\u8d70\u4e86\u8fd9\u6279\u9ec4\u91d1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u76d7\u8d70\uff0c\u662f\u8fd0\u8d70\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd0\u8d70\uff1f\u201d\u72ec\u5b64\u75f4\u53c8\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u8fd9\u6279\u9ec4\u91d1\u843d\u5165\u522b\u4eba\u624b\u91cc\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u8981\u5229\u7528\u8fd9\u6279\u9ec4\u91d1\u6765\u590d\u4ec7\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u9ec4\u91d1\u5df2\u7ecf\u88ab\u8fd0\u8d70\uff0c\u662f\u4e0d\u662f\u5c31\u8868\u793a\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u773c\u775b\u95ea\u7740\u5149\uff1a\u201c\u6211\u65e9\u5df2\u60f3\u5230\u9ec4\u91d1\u4e0d\u4f1a\u5728\u8fd9\u91cc\uff0c\u56e0\u4e3a\u535c\u9e70\u7edd\u4e0d\u4f1a\u6b7b\u7684\uff0c\u65e0\u8bba\u8c01\u60f3\u8981\u4ed6\u7684\u547d\u90fd\u5f88\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u8981\u8fd0\u8d70\u4e09\u5341\u4e07\u4e24\u9ec4\u91d1\u597d\u50cf\u4e5f\u4e0d\u592a\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u5bb9\u6613\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u5e78\u597d\u8fd9\u4e16\u754c\u4e0a\u8fd8\u6709\u4e9b\u4eba\u603b\u662f\u80fd\u505a\u5230\u522b\u4eba\u505a\u4e0d\u5230\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u535c\u9e70\u5c31\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u672c\u6765\u5c31\u662f\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u4ed6\u90fd\u80fd\u627e\u5230\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u4e3a\u4ed6\u6548\u5fe0\u6548\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u72ec\u5b64\u75f4\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u80af\u4e3a\u4ed6\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u5ffd\u7136\u51b7\u7b11\u3002 \u3000\u3000\u201c\u90a3\u4e48\u6211\u5c31\u4e0d\u61c2\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u61c2\uff1f\u201d\u5c0f\u65b9\u53cd\u95ee\uff1a\u201c\u4e0d\u61c2\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u4e00\u70b9\u6211\u4e0d\u61c2\u3002\u201d\u72ec\u5b64\u75f4\u58f0\u97f3\u4e2d\u7684\u8ba5\u4fcf\u4e4b\u610f\u5c31\u5982\u5c16\u9488\uff1a\u201c\u4f60\u65e2\u7136\u4e5f\u80af\u4e3a\u4ed6\u6b7b\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6765\u627e\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5e76\u6ca1\u6709\u88ab\u523a\u4f24\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5df2\u7ecf\u79bb\u5f00\u4ed6\u4e86\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u4ed6\u4e0d\u6765\u627e\u6211\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4e0d\u613f\u518d\u8ba9\u6211\u5377\u5165\u8fd9\u4e2a\u6f29\u6da1\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4e00\u70b9\u90fd\u4e0d\u602a\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u4e0d\u602a\u4ed6\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u518d\u6765\u627e\u4f60\uff0c\u4f60\u662f\u4e0d\u662f\u4e00\u6837\u80af\u4e3a\u4ed6\u6b7b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5c0f\u65b9\u6beb\u4e0d\u8003\u8651\u5c31\u56de\u7b54\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u592a\u9633\u5df2\u5347\u8d77\uff0c\u8d8a\u5347\u8d8a\u9ad8\uff0c\u5854\u77f3\u7684\u5c16\u5f71\u5374\u8d8a\u7f29\u8d8a\u77ed\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u9633\u5149\uff0c\u5c31\u6ca1\u6709\u5f71\u5b50\uff0c\u53ef\u662f\u65e5\u6b63\u4e2d\u5929\u65f6\uff0c\u5f71\u5b50\u53cd\u800c\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff01\u53f9\u606f\u7684\u58f0\u97f3\u5c31\u597d\u50cf\u662f\u81ea\u8fdc\u5c71\u5439\u6765\u7684\u51b7\u98ce\u5439\u8fc7\u6797\u68a2\u3002 \u3000\u3000\u201c\u535c\u9e70\u7684\u786e\u662f\u4eba\u6770\u3002\u201d \u3000\u3000\u201c\u4ed6\u672c\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u8981\u6740\u4ed6\u7684\u786e\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u5ffd\u7136\u95ee\uff1a\u201c\u8981\u6740\u4f60\u5462\uff1f\u201d\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u8981\u6740\u4f60\u5bb9\u4e0d\u5bb9\u6613\uff1f\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5c0f\u65b9\uff0c\u5c0f\u65b9\u4e5f\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u8bf4\uff1a\u201c\u90a3\u5c31\u8981\u770b\u4e86\u3002\u201d \u3000\u3000\u201c\u770b\uff1f\u201d\u72ec\u5b64\u75f4\u95ee\uff1a\u201c\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\u662f\u8c01\u8981\u6740\u6211\uff1f\u4ec0\u4e48\u65f6\u5019\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u6211\u8981\u6740\u4f60\uff0c\u73b0\u5728\u5c31\u6740\u4f60\u3002\u201d\u72ec\u5b64\u75f4\u53c8\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u5f88\u5bb9\u6613\uff1f\u201d \u3000\u3000\u5f88\u5c11\u6709\u4eba\u80af\u56de\u7b54\u8fd9\u79cd\u95ee\u9898\uff0c\u53ef\u662f\u5c0f\u65b9\u5374\u5f88\u5feb\u5c31\u56de\u7b54\uff1a\u201c\u662f\u7684\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u662f\u5f88\u5bb9\u6613\u3002\u201d \u3000\u3000\u592a\u9633\u8d8a\u5347\u8d8a\u9ad8\uff0c\u53ef\u662f\u5728\u8fd9\u4e00\u7247\u65e0\u60c5\u7684\u5927\u5730\u4e0a\uff0c\u5728\u8fd9\u4e00\u5757\u5730\u65b9\uff0c\u5728\u5c0f\u65b9\u548c\u72ec\u5b64\u75f4\u4e4b\u95f4\uff0c\u592a\u9633\u7684\u70ed\u529b\u597d\u50cf\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5c0f\u65b9\u89c9\u5f97\u5f88\u51b7\uff0c\u8d8a\u6765\u8d8a\u51b7\uff0c\u51b7\u5f97\u8fde\u51b7\u6c57\u90fd\u6d41\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u8138\u8272\u4e5f\u51b7\u5f97\u50cf\u51b0\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u4f1a\u6740\u4f60\uff1f\u201d\u4ed6\u5ffd\u7136\u53c8\u95ee\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4f1a\u6740\u6211\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\uff0c\u53ea\u8981\u4e00\u6709\u673a\u4f1a\uff0c\u5c31\u8981\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u8fd9\u53e5\u8bdd\u4f60\u6ca1\u6709\u5fd8\u8bb0\uff1f\u201d \u3000\u3000\u201c\u8fd9\u79cd\u8bdd\u8c01\u4f1a\u5fd8\u8bb0\uff1f\u201d\u5c0f\u65b9\u770b\u7740\u72ec\u5b64\u75f4\u63e1\u5251\u7684\u624b\uff1a\u201c\u4f60\u662f\u5251\u5ba2\uff0c\u73b0\u5728\u4f60\u7684\u638c\u4e2d\u6709\u5251\uff0c\u5251\u65e0\u60c5\uff0c\u5251\u5ba2\u4e5f\u65e0\u60c5\uff0c\u73b0\u5728\u4f60\u82e5\u6740\u4e86\u6211\uff0c\u6211\u975e\u4f46\u6b7b\u800c\u65e0\u6028\uff0c\u4e5f\u6b7b\u800c\u65e0\u61be\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u638c\u4e2d\u4e5f\u6709\u5251\uff0c\u4f46\u662f\u4ed6\u63e1\u5251\u7684\u624b\u5df2\u5b8c\u5168\u653e\u677e\u3002 \u3000\u3000\u592a\u9633\u4ece\u4e1c\u65b9\u5347\u8d77\u6765\uff0c\u72ec\u5b64\u75f4\u662f\u80cc\u5bf9\u4e1c\u65b9\u7ad9\u7740\u7684\uff0c\u4e0a\u4e2a\u6709\u7ecf\u9a8c\u7684\u5251\u5ba2\uff0c\u7edd\u4e0d\u4f1a\u9762\u5bf9\u9633\u5149\u7ad9\u5728\u4ed6\u7684\u5bf9\u624b\u524d\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u5b8c\u5168\u5360\u5c3d\u4f18\u52bf\uff0c\u5df2\u7ecf\u628a\u5c0f\u65b9\u903c\u5728\u4e00\u4e2a\u6700\u574f\u7684\u5730\u4f4d\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u8fd8\u662f\u60f3\u5c3d\u65b9\u6cd5\u4e0d\u8ba9\u81ea\u5df1\u6b63\u9762\u5bf9\u7740\u592a\u9633\uff0c\u6240\u4ee5\u4ed6\u8fd8\u662f\u80fd\u770b\u5230\u72ec\u5b64\u75f4\u7684\u8138\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u8138\u8fd8\u662f\u50cf\u82b1\u5c97\u77f3\u4e00\u6837\uff0c\u53c8\u51b7\u53c8\u786c\uff0c\u4f46\u662f\u4ed6\u8138\u4e0a\u5df2\u7ecf\u6709\u4e86\u8868\u60c5\u3002 \u3000\u3000\u4e00\u79cd\u975e\u5e38\u590d\u6742\u7684\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u7684\u773c\u795e\u663e\u5f97\u5f88\u5174\u594b\u3002 \u3000\u3000\u2014\u2014\u65e0\u8bba\u8c01\u5728\u6740\u4eba\u4e4b\u524d\u90fd\u96be\u514d\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff0c\u4f55\u51b5\u4ed6\u8981\u6740\u7684\u4eba\uff0c\u53c8\u662f\u4ed6\u751f\u5e73\u5c11\u89c1\u7684\u5bf9\u624b\u3002 \u3000\u3000\u4ed6\u7684\u773c\u795e\u867d\u7136\u5df2\u56e0\u5174\u594b\u800c\u70bd\u70ed\u53d1\u5149\uff0c\u7709\u68a2\u773c\u89d2\u5374\u53c8\u5e26\u7740\u79cd\u65e0\u53ef\u5948\u4f55\u7684\u60b2\u4f24\u3002 \u3000\u3000\u2014\u2014\u4e58\u4eba\u4e4b\u5371\uff0c\u6bd5\u7adf\u4e0d\u662f\u4ef6\u5149\u91c7\u6109\u5feb\u7684\u4e8b\uff0c\u53ef\u662f\u4ed6\u4e00\u5b9a\u8981\u5f3a\u8feb\u81ea\u5df1\u8fd9\u4e48\u505a\u3002 \u3000\u3000\u2014\u2014\u826f\u673a\u4e00\u5931\uff0c\u6c38\u4e0d\u518d\u6765\uff0c\u5c31\u7b97\u4ed6\u672c\u6765\u4e0d\u613f\u6740\u5c0f\u65b9\uff0c\u4e5f\u4e0d\u80fd\u5931\u53bb\u8fd9\u6b21\u673a\u4f1a\u3002 \u3000\u3000\u5c0f\u65b9\u660e\u4e86\u4ed6\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u4ed6\u5df2\u7ecf\u51c6\u5907\u51fa\u624b\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u751f\u6b7b\u547c\u5438\uff0c\u95ee\u4e0d\u5bb9\u53d1\u7684\u4e00\u77ac\u95f4\uff0c\u72ec\u5b64\u75f4\u8138\u4e0a\u5ffd\u7136\u53c8\u8d77\u4e86\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u5ffd\u7136\u53c8\u53d8\u5f97\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\u4e86\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5c0f\u65b9\u7684\u5fc3\u5ffd\u7136\u4eff\u4f5b\u5728\u6536\u7f29\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u611f\u89c9\u5230\u6709\u4e2a\u4eba\u5df2\u7ecf\u5230\u4e86\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u2014\u2014\u6765\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u56de\u5934\uff0c\u4e5f\u4e0d\u6562\u56de\u5934\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u76ef\u7740\u72ec\u5b64\u75f4\u7684\u8138\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u773c\u775b\u91cc\u7adf\u4f3c\u5df2\u6709\u4e86\u79cd\u8bf4\u4e0d\u51fa\u7684\u75db\u82e6\u548c\u6124\u6012\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u611f\u89c9\u5230\u6709\u4e00\u53ea\u6e29\u67d4\u5149\u6da6\u7684\u624b\u8f7b\u8f7b\u63e1\u4f4f\u4e86\u4ed6\u51b0\u51b7\u6d41\u6c57\u7684\u624b\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u8c01\u7684\u624b\uff1f \u3000\u3000\u2014\u2014\u8c01\u4f1a\u5728\u4ed6\u6700\u8270\u82e6\u5371\u9669\u7684\u65f6\u5019\u7ad9\u5230\u4ed6\u8eab\u8fb9\u6765\uff0c\u63e1\u4f4f\u4ed6\u7684\u624b\uff1f \u3000\u3000\u4ed6\u60f3\u5230\u4e86\u5f88\u591a\u4eba\u3002\u2014\u2014\u201c\u9633\u5149\u201d\u3001\u6ce2\u5a03\u3001\u82cf\u82cf\u3002 \u3000\u3000\u5979\u4eec\u90fd\u5df2\u7ecf\u8ddf\u4ed6\u6709\u4e86\u611f\u60c5\uff0c\u90fd\u4e0d\u4f1a\u8fdc\u8fdc\u7ad9\u5728\u4e00\u8fb9\u770b\u4ed6\u6b7b\u5728\u522b\u4eba\u7684\u5251\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u77e5\u9053\u6765\u7684\u4e0d\u662f\u5979\u4eec\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u5979\u4eec\u867d\u7136\u90fd\u5bf9\u4ed6\u4e0d\u9519\uff0c\u4f46\u4ed6\u5374\u4e0d\u662f\u5979\u4eec\u5fc3\u76ee\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u201c\u9633\u5149\u201d\u5fc3\u91cc\u8fd8\u6709\u535c\u9e70\uff0c\u6ce2\u5a03\u5fc3\u91cc\u8fd8\u6709\u73ed\u5bdf\u5df4\u90a3\uff0c\u82cf\u82cf\u5fc3\u91cc\u8fd8\u6709\u5415\u4e09\u3002 \u3000\u3000\u4e0d\u7ba1\u5979\u4eec\u5bf9\u4ed6\u591a\u597d\uff0c\u4e0d\u7ba1\u5979\u4eec\u66fe\u7ecf\u4e3a\u4ed6\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u5230\u4e86\u67d0\u4e00\u79cd\u7279\u6b8a\u7684\u60c5\u51b5\u4e0b\uff0c\u5979\u4eec\u8fd8\u662f\u4f1a\u5f03\u4ed6\u800c\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4eec\u672c\u6765\u5c31\u4e0d\u662f\u5c5e\u4e8e\u4ed6\u7684\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u71d5\u5c31\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u4e0d\u7ba1\u5979\u662f\u6068\u4ed6\u4e5f\u597d\uff0c\u662f\u7231\u4ed6\u4e5f\u597d\uff0c\u81f3\u5c11\u5728\u5979\u5fc3\u76ee\u4e2d\u4ece\u672a\u6709\u8fc7\u522b\u7684\u7537\u4eba\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4ece\u4e0d\u91cd\u89c6\u8fd9\u4e00\u70b9\uff0c\u53ef\u662f\u5728\u8fd9\u79cd\u751f\u6b7b\u4e00\u77ac\u3001\u95ee\u4e0d\u5bb9\u53d1\u7684\u65f6\u5019\uff0c\u4ed6\u624d\u53d1\u89c9\u8fd9\u4e00\u70b9\u662f\u8fd9\u4e48\u91cd\u8981\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u5730\u95ee\uff1a\u201c\u662f\u4f60\u6765\u4e86\uff1f\u201d\u3002 \u3000\u3000\u201c\u5f53\u7136\u662f\u6211\u6765\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u58f0\u97f3\u867d\u7136\u4e5f\u5f88\u51b7\uff0c\u4f46\u5374\u5e26\u7740\u4e00\u79cd\u9664\u4e86\u201c\u4ed6\u4eec\u201d\u4e4b\u5916\u8c01\u90fd\u65e0\u6cd5\u76f8\u4fe1\u4e5f\u65e0\u6cd5\u4e86\u89e3\u7684\u611f\u60c5\u3002 \u3000\u3000\u2014\u2014\u201c\u4ed6\u4eec\u201d\u5df2\u4e0d\u662f\u4e24\u4e2a\u4eba\uff0c\u662f\u4e09\u4e2a\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u4e5f\u4e86\u89e3\u8fd9\u79cd\u611f\u60c5\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u4ed6\u95f4\u9f50\u5c0f\u71d5\uff1a\u201c\u662f\u4e0d\u662f\u6765\u966a\u4ed6\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u65e9\uff01\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u51b7\u51b7\u5730\u8bf4\uff1a\u201c\u4ed6\u6839\u672c\u4e0d\u4f1a\u6b7b\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u966a\u4ed6\u6b7b\uff01\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u201c\u7edd\u4e0d\u4f1a\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u73b0\u5728\u5df2\u7ecf\u6709\u4e24\u4e2a\u4eba\u4e86\uff0c\u4f60\u5df2\u7ecf\u6ca1\u6709\u628a\u63e1\u5bf9\u4ed8\u6211\u4eec\uff0c\u6240\u4ee5\u4f60\u6839\u672c\u5df2\u4e0d\u6562\u51fa\u624b\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u6ca1\u6709\u518d\u5f00\u53e3\u3002\u4e5f\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u8bf4\u7684\u662f\u4e8b\u5b9e\uff0c\u50cf\u4ed6\u8fd9\u79cd\u4eba\uff0c\u4ece\u6765\u4e5f\u4e0d\u4f1a\u4e0e\u4e8b\u5b9e\u4e89\u8fa9\uff0c\u66f4\u4e0d\u4f1a\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u653e\u677e\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u4fdd\u6301\u7740\u653b\u51fb\u7684\u59ff\u52bf\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u53d1\u51fa\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e0d\u52a8\uff0c\u5c0f\u65b9\u548c\u5c0f\u71d5\u4e5f\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u624b\u4e92\u76f8\u63e1\u7d27\uff0c\u4ed6\u4eec\u638c\u5fc3\u7684\u6c57\u4e92\u76f8\u6d41\u5165\u5bf9\u65b9\u7684\u638c\u5fc3\u3002\u4e92\u76f8\u4ea4\u878d\uff0c\u5c31\u597d\u50cf\u662f\u8840\u4e00\u6837\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u79cd\u5c40\u9762\u8981\u50f5\u6301\u5230\u4ec0\u4e48\u65f6\u5019\u3002\u592a\u9633\u5347\u5f97\u66f4\u9ad8\uff0c\u5927\u8272\u5374\u5ffd\u7136\u6697\u4e86\uff0c\u6697\u5f97\u4e0d\u5408\u60c5\u7406\uff0c\u6697\u5f97\u53ef\u6015\u3002 \u3000\u3000\u5c0f\u65b9\u638c\u5fc3\u5ffd\u7136\u53c8\u6c81\u51fa\u4e86\u5927\u91cf\u51b7\u6c57\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53d1\u73b0\u98ce\u5439\u5728\u8eab\u4e0a\u7adf\u5df2\u53d8\u5f97\u5f88\u51b7\u3002 \u3000\u3000\u5728\u767d\u663c\u9177\u70ed\u7684\u5927\u6c99\u6f20\u4e0a\uff0c\u672c\u6765\u4e0d\u8be5\u6709\u8fd9\u4e48\u51b7\u7684\u98ce\u3002 \u3000\u3000\u5bf9\u8fd9\u4e00\u7247\u65e0\u60c5\u7684\u5927\u5730\uff0c\u4ed6\u5df2\u7ecf\u5f88\u719f\u6089\uff0c\u5728\u4e00\u5e74\u591a\u4ee5\u524d\u4e00\u4e2a\u540c\u6837\u9177\u70ed\u7684\u767d\u663c\uff0c\u4ed6\u4e5f\u66fe\u6709\u8fc7\u540c\u6837\u7684\u7ecf\u9a8c\u2014\u2014\u5929\u8272\u5ffd\u7136\u53d8\u6697\uff0c\u98ce\u5ffd\u7136\u53d8\u51b7\u3002 \u3000\u3000\u7136\u540e\u5c31\u662f\u4e00\u573a\u53ef\u6015\u7684\u5927\u98ce\u66b4\uff0c\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u907f\u514d\u6297\u62d2\u3002 \u3000\u3000\u73b0\u5728\u65e0\u7591\u53c8\u5c06\u6709\u4e00\u573a\u540c\u6837\u53ef\u6015\u7684\u98ce\u66b4\u5c06\u8981\u6765\u4e34\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u53ea\u8981\u52a8\u4e00\u52a8\u5c31\u53ef\u80fd\u9020\u6210\u81f4\u547d\u7684\u758f\u5ffd\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u5251\uff0c\u8fdc\u6bd4\u5c06\u8981\u6765\u4e34\u7684\u98ce\u66b4\u8ddd\u79bb\u4ed6\u66f4\u8fd1\uff0c\u4e5f\u66f4\u53ef\u6015\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53ea\u6709\u7ad9\u5728\u90a3\u91cc\u7b49\uff0c\u7b49\u98ce\u66b4\u5230\u6765\uff0c\u5c31\u7b97\u4ed6\u660e\u77e5\u98ce\u66b4\u6765\u4e34\u540e\u5927\u5bb6\u90fd\u53ef\u80fd\u6b7b\u5728\u8fd9\u91cc\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e2\u4e0d\u80fd\u9009\u62e9\uff0c\u4e5f\u65e0\u6cd5\u9003\u907f\u3002 \u3000\u3000\u98ce\u66b4\u679c\u7136\u6765\u4e86\u3002 \u3000\u3000\u98ce\u8d8a\u6765\u8d8a\u6025\uff0c\u6025\u98ce\u5439\u8d77\u6ee1\u5929\u9ec4\u7802\uff0c\u6253\u5728\u4eba\u8eab\u4e0a\uff0c\u5b9b\u5982\u7bad\u94fe\u3002 \u3000\u3000\u7b2c\u4e00\u9635\u6025\u98ce\u5e26\u7740\u9ec4\u7802\u5439\u8fc7\u6765\u65f6\uff0c\u5c0f\u65b9\u5c31\u77e5\u9053\u81ea\u5df1\u5b8c\u4e86\uff01 \u3000\u3000\u56e0\u4e3a\u4ed6\u867d\u7136\u628a\u6bcf\u4e00\u70b9\u90fd\u8003\u8651\u5230\uff0c\u5374\u8fd8\u662f\u758f\u5ffd\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u4efb\u4f55\u4e00\u70b9\u758f\u5ffd\uff0c\u90fd\u4f1a\u9020\u6210\u81f4\u547d\u7684\u9519\u8bef\u3002 \u3000\u3000\u4ed6\u5fd8\u4e86\u81ea\u5df1\u662f\u8fce\u98ce\u7ad9\u7740\u7684\uff0c\u98ce\u7802\u5439\u8fc7\u6765\uff0c\u6b63\u597d\u8fce\u9762\u6253\u5728\u4ed6\u7684\u8138\u4e0a\u3002 \u3000\u3000\u7b49\u4ed6\u60f3\u5230\u8fd9\u4e00\u70b9\u65f6\uff0c\u5927\u9519\u5df2\u94f8\u6210\uff0c\u5df2\u65e0\u6cd5\u5f25\u8865\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u5251\u5df2\u7ecf\u50cf\u6bd2\u86c7\u822c\u5411\u4ed6\u523a\u8fc7\u6765\uff0c\u4ed6\u53ea\u770b\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u5c31\u5df2\u7741\u4e0d\u5f00\u773c\u775b\uff0c\u751a\u81f3\u8fde\u8fd9\u4e00\u5251\u523a\u5728\u8eab\u4e0a\u4ec0\u4e48\u5730\u65b9\u90fd\u5df2\u611f\u89c9\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u5012\u4e0b\u53bb\u65f6\uff0c\u8fd8\u542c\u89c1\u9f50\u5c0f\u71d5\u5728\u547c\u559d\uff0c\u7136\u540e\u4ed6\u5c31\u8fde\u58f0\u97f3\u90fd\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u98ce\u5728\u547c\u5578\uff0c\u9ec4\u7802\u98de\u821e\u3002 \u3000\u3000\u5c0f\u65b9\u4eff\u4f5b\u53c8\u542c\u89c1\u4e86\u5c0f\u71d5\u7684\u58f0\u97f3\uff0c\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u75db\u82e6\uff0c\u4e00\u6b63\u5728\u5411\u4ed6\u54c0\u547c\u6c42\u6551\u3002\u53c8\u4eff\u4f5b\u770b\u89c1\u72ec\u5b64\u75f4\u5df2\u7ecf\u6495\u88c2\u4e86\u5979\u7684\u8863\u670d\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4ec0\u4e48\u4e5f\u542c\u4e0d\u89c1\uff0c\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u4ed6\u81ea\u5669\u68a6\u4e2d\u60ca\u9192\u65f6\uff0c\u51b7\u6c57\u5df2\u6e7f\u900f\u8863\u670d\uff0c\u773c\u524d\u8fd8\u662f\u53ea\u6709\u4e00\u7247\u9ec4\u7802\u3002 \u3000\u3000\u2014\u2014\u4ed6\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u2014\u2014\u521a\u624d\u4ed6\u542c\u89c1\u770b\u89c1\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u68a6\u4e2d\u7684\u5e7b\u89c9\u3002 \u3000\u3000\u4f46\u662f\u9f50\u5c0f\u71d5\u7684\u4eba\u5df2\u4e0d\u77e5\u9053\u54ea\u91cc\u53bb\u4e86\uff0c\u72ec\u5b64\u75f4\u4e5f\u4e0d\u77e5\u9053\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u521a\u624d\u5728\u4ed6\u68a6\u4e2d\u53d1\u751f\u7684\u4e8b\uff0c\u5728\u73b0\u5b9e\u4e2d\u4e5f\u53ef\u80fd\u540c\u6837\u53d1\u751f\u8fc7\u3002 \u3000\u3000\u60f3\u5230\u72ec\u5b64\u75f4\u8d64\u88f8\u88f8\u5730\u7ad9\u5728\u5bd2\u98ce\u4e2d\u8ba9\u5c0f\u71d5\u4e3a\u4ed6\u6d17\u64e6\u7684\u60c5\u51b5\uff0c\u5c0f\u65b9\u5fc3\u91cc\u5ffd\u7136\u6709\u4e86\u79cd\u4ece\u6765\u672a\u6709\u7684\u523a\u75db\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e00\u5b9a\u8981\u627e\u5230\u4ed6\u4eec\uff0c\u4e00\u5b9a\u8981\u963b\u6b62\u8fd9\u4ef6\u4e8b\u53d1\u751f\u3002 \u3000\u3000\u4ed6\u60f3\u6323\u624e\u7740\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u52a8\u8170\u4e0b\u5c31\u75db\u5982\u5200\u5272\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u662f\u4ed6\u7684\u5e78\u8fd0\u8fd8\u662f\u4e0d\u5e78\uff1f\u72ec\u5b64\u75f4\u90a3\u4e00\u5251\u5c45\u7136\u6ca1\u6709\u523a\u4e2d\u4ed6\u7684\u8981\u5bb3\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u8fd8\u6d3b\u7740\uff0c\u53ef\u662f\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u81ea\u5df1\u8fd8\u80fd\u6d3b\u591a\u4e45\u3002 \u3000\u3000\u2014\u2014\u98ce\u66b4\u8fd8\u672a\u8fc7\u53bb\uff0c\u4ed6\u7684\u4f24\u53e3\u53c8\u5f00\u59cb\u6d41\u8840\uff0c\u4ed6\u7684\u5634\u5507\u53c8\u5f00\u59cb\u5e72\u88c2\uff0c\u808c\u8089\u8fd8\u5728\u9178\u75db\u3002 \u3000\u3000\u2014\u2014\u4ed6\u7684\u7cae\u98df\u548c\u6c34\u90fd\u5df2\u88ab\u98ce\u5439\u8d70\uff0c\u4e0e\u4ed6\u751f\u6b7b\u76f8\u5171\u7684\u5973\u4eba\u73b0\u5728\u5f88\u53ef\u80fd\u5728\u53d7\u522b\u4eba\u7684\u6467\u6b8b\u4fae\u8fb1\u3002 \u3000\u3000\u4ed6\u7684\u8089\u4f53\u548c\u5fc3\u7075\u90fd\u5728\u53d7\u7740\u4efb\u4f55\u4eba\u90fd\u96be\u4ee5\u5fcd\u53d7\u7684\u714e\u71ac\u3002 \u3000\u3000\u4ed6\u600e\u4e48\u80fd\u6d3b\u5f97\u4e0b\u53bb\uff1f \u3000\u3000\u53ea\u6709\u4eb2\u8eab\u7ecf\u5386\u8fc7\u7684\u4eba\uff0c\u624d\u77e5\u9053\u8981\u5728\u6c99\u6f20\u7684\u98ce\u66b4\u4e2d\u6d3b\u4e0b\u53bb\u662f\u4ef6\u591a\u4e48\u8270\u82e6\u7684\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\u3002 \u3000\u3000\u4e0a\u4e00\u6b21\u4ed6\u51e0\u4e4e\u6b7b\u5728\u8fd9\u91cc\uff0c\u8fd9\u4e00\u6b21\u4ed6\u7684\u60c5\u51b5\u8fdc\u6bd4\u4e0a\u6b21\u66f4\u7cdf\u3002 \u3000\u3000\u5982\u679c\u4ed6\u4e0d\u662f\u5c0f\u65b9\uff0c\u4e5f\u8bb8\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u60f3\u518d\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u4eba\u5982\u679c\u4e27\u5931\u4e86\u4e3a\u751f\u5b58\u594b\u6597\u7684\u610f\u5fd7\u548c\u52c7\u6c14\uff0c\u8fd8\u6709\u8c01\u80fd\u8ba9\u4ed6\u6d3b\u4e0b\u53bb\uff1f \u3000\u3000\u4ed6\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u4ed6\u4e0d\u65ad\u5730\u544a\u8bc9\u81ea\u5df1\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e00\u5b9a\u8981\u6d3b\u4e0b\u53bb\uff0c\u4e00\u5b9a\u8981\u6d3b\u4e0b\u53bb\uff01 \u3000\u3000\u5929\u5730\u95ee\u4e00\u7247\u660f\u9ec4\uff0c\u8c01\u4e5f\u5206\u4e0d\u51fa\u73b0\u5728\u7a76\u7adf\u662f\u767d\u5929\u8fd8\u662f\u665a\u4e0a\uff1f \u3000\u3000\u5c0f\u65b9\u8eba\u5728\u51b0\u51b7\u7684\u7802\u7c92\u4e0a\uff0c\u98ce\u7802\u51e0\u4e4e\u5df2\u5c06\u4ed6\u6574\u4e2a\u4eba\u5b8c\u5168\u63a9\u57cb\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u592a\u75b2\u5026\uff0c\u5931\u53bb\u7684\u8840\u5b9e\u5728\u5927\u591a\uff0c\u5b9e\u5728\u60f3\u95ed\u4e0a\u773c\u775b\u5148\u7761\u4e00\u4e0b\u3002 \u3000\u3000\u2014\u2014\u6e29\u67d4\u9ed1\u6697\u3001\u751c\u871c\u7684\u68a6\u4e61\uff0c\u662f\u4e2a\u591a\u4e48\u7f8e\u4e3d\u7684\u5730\u65b9\uff01 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u7741\u5f00\u773c\u775b\uff0c\u7528\u5c3d\u5168\u8eab\u529b\u6c14\u7ffb\u4e86\u4e2a\u8eab\uff0c\u4ee5\u989d\u89d2\u7528\u529b\u53bb\u78e8\u64e6\u7c97\u7cd9\u7684\u7802\u7c92\uff0c\u8ba9\u75db\u82e6\u4f7f\u4ed6\u6e05\u9192\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u53ea\u8981\u4e00\u7761\u7740\uff0c\u5c31\u53ef\u80fd\u6d3b\u6d3b\u57cb\u6b7b\u5728\u9ec4\u7802\u4e0b\uff01 \u3000\u3000\u4ed6\u6ca1\u6709\u7761\u7740\u3002 \u3000\u3000\u4ed6\u7684\u989d\u89d2\u5728\u6d41\u8840\uff0c\u8170\u4e0a\u7684\u4f24\u53e3\u4e5f\u5728\u6d41\u8840\uff0c\u4f46\u662f\u4ed6\u5df2\u5b8c\u5168\u6e05\u9192\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u6709\u4e00\u70b9\u6c34\uff0c\u4ed6\u5c31\u53ef\u4ee5\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u65e0\u60c5\u7684\u5927\u6f20\u4e0a\uff0c\u72c2\u66b4\u7684\u98ce\u7802\u4e2d\uff0c\u5230\u54ea\u91cc\u624d\u80fd\u627e\u5f97\u5230\u6c34\uff1f \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u8dc3\u8d77\uff0c\u594b\u529b\u5411\u524d\u8d70\u4e86\u51e0\u6b65\uff0c\u7b49\u4ed6\u518d\u5012\u4e0b\u53bb\u65f6\uff0c\u4ed6\u5c31\u50cf\u8725\u8734\u822c\u5f80\u524d\u722c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u53c8\u6709\u4e86\u751f\u5b58\u7684\u5e0c\u671b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u6628\u591c\u6b7b\u5728\u4ed6\u548c\u72ec\u5b64\u75f4\u5251\u4e0b\u7684\u90a3\u4e9b\u4eba\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u5b88\u5019\u5728\u8fd9\u91cc\u5df2\u7ecf\u4e0d\u6b62\u4e00\u5929\u4e86\uff0c\u4ed6\u4eec\u8eab\u4e0a\u5f53\u7136\u6709\u6c34\u548c\u98df\u7cae\u3002 \u3000\u3000\u8fd9\u5ff5\u5934\u5c31\u50cf\u7535\u51fb\u4e00\u6837\u6253\u8fc7\u4ed6\u7684\u5168\u8eab\uff0c\u4f7f\u4ed6\u5ffd\u7136\u6709\u4e86\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u679c\u7136\u5f88\u5feb\u5c31\u6478\u5230\u4e86\u4e00\u4e2a\u4eba\u7684\u5c38\u4f53\uff0c\u6478\u5230\u4e86\u8fd9\u5c38\u4f53\u8170\u5e26\u4e0a\u7cfb\u7740\u9769\u56ca\u3002 \u3000\u3000\u9769\u56ca\u4e2d\u6709\u4e09\u952d\u4efd\u91cf\u5f88\u91cd\u7684\u94f6\u952d\uff0c\u4e00\u4e9b\u6563\u788e\u7684\u94f6\u5b50\u3002 \u3000\u3000\u9769\u56ca\u4e2d\u8fd8\u6709\u53ea\u91d1\u624b\u2014\u2014\u5415\u4e09\u7528\u6765\u53f7\u4ee4\u5c5e\u4e0b\u7684\u91d1\u624b\u3002 \u3000\u3000\u2014\u2014\u5415\u4e09\uff01\u5bcc\u8d35\u795e\u4ed9\u5415\u4e09\uff01\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u8a93\u4e0d\u4e24\u7acb\u7684\u5f3a\u654c\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u73b0\u5728\u4eff\u4f5b\u8fde\u8fd9\u79cd\u4ec7\u6068\u90fd\u5fd8\u8bb0\u4e86\uff0c\u56e0\u4e3a\u4ed6\u7684\u5fc3\u5df2\u7ecf\u5b8c\u5168\u88ab\u4e00\u79cd\u66f4\u5f3a\u70c8\u7684\u60c5\u611f\u6240\u5360\u636e\u3002 \u3000\u3000\u2014\u2014\u751f\u5b58\u7684\u6b32\u671b\uff0c\u6c38\u8fdc\u662f\u4eba\u7c7b\u6240\u6709\u60c5\u611f\u4e2d\u6700\u5f3a\u70c8\u7684\u4e00\u79cd\uff01 \u3000\u3000\u9769\u56ca\u4e2d\u6ca1\u6709\u6c34\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u76db\u6c34\u7684\u76ae\u888b\u5df2\u7ecf\u88ab\u523a\u7834\u4e86\uff0c\u523a\u7834\u8fd9\u6c34\u888b\u7684\u4eba\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u5c0f\u65b9\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u662f\u79cd\u591a\u4e48\u60b2\u54c0\u6c89\u75db\u7684\u8bbd\u523a\uff1f \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u4e5f\u6ca1\u6709\u53bb\u60f3\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u53bb\u60f3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u60f3\u5f97\u5927\u591a\uff0c\u5bf9\u751f\u547d\u7684\u610f\u4e49\u4e5f\u8bb8\u5c31\u4f1a\u91cd\u65b0\u4f30\u4ef7\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u523b\u5bf9\u4ed6\u6765\u8bf4\uff0c\u751f\u547d\u662f\u65e0\u4ef7\u7684\uff0c\u6c38\u8fdc\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u4ee3\u66ff\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53c8\u5f00\u59cb\u5f80\u524d\u722c\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u5ffd\u7136\u72c2\u8df3\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u53c8\u627e\u5230\u4e86\u53e6\u4e00\u4e2a\u6b7b\u4eba\u7684\u5c38\u4f53\uff0c\u800c\u4e14\u8fd8\u6478\u5230\u4e86\u8fd9\u4e2a\u4eba\u8170\u4e0a\u76db\u6c34\u7684\u76ae\u888b\u3002 \u3000\u3000\u6c34\u888b\u662f\u6ee1\u7684\uff0c\u4e30\u5bcc\u9971\u6ee1\u5982\u5904\u5973\u7684\u4e73\u623f\u3002 \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u81ea\u5df1\u5f97\u6551\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u4f38\u51fa\u51b0\u51b7\u98a4\u6296\u7684\u624b\uff0c\u60f3\u53bb\u89e3\u5f00\u8fd9\u76ae\u888b\uff0c\u4f46\u662f\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u53c8\u542c\u89c1\u4e86\u4e00\u4e2a\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u542c\u89c1\u4e86\u4e00\u9635\u5fc3\u8df3\u7684\u58f0\u97f3\u535c \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u5fc3\u8fd8\u5728\u8df3\uff0c\u8fd9\u4e2a\u4eba\u8fd8\u6ca1\u6709\u6b7b\uff01 \u3000\u3000\u5c0f\u65b9\u7684\u624b\u505c\u4e0b\u6765\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u51bb\u7ed3\u3002 \u3000\u3000\u4ece\u4e00\u4e2a\u6b7b\u4eba\u8eab\u4e0a\u62ff\u4e00\u70b9\u6c34\u6765\u6551\u81ea\u5df1\u7684\u547d\uff0c\u7edd\u4e0d\u662f\u4ef6\u53ef\u803b\u7684\u4e8b\u3002 \u3000\u3000\u4ece\u4e00\u4e2a\u5782\u6b7b\u7684\u5b8c\u5168\u6ca1\u6709\u62b5\u6297\u529b\u7684\u6d3b\u4eba\u8eab\u4e0a\uff0c\u63a0\u593a\u4ed6\u7684\u6c34\u888b\uff0c\u5c31\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u8fd8\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u4ed6\u90fd\u662f\u4ed6\u81ea\u5df1\uff0c\u56e0\u4e3a\u4ed6\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5931\u53bb\u4ed6\u81ea\u5df1\u2014\u2014\u4e0d\u4f1a\u5931\u53bb\u81ea\u5df1\u7684\u826f\u5fc3\uff0c\u4e5f\u4e0d\u4f1a\u6539\u53d8\u81ea\u5df1\u7684\u539f\u5219\uff0c\u66f4\u4e0d\u4f1a\u505a\u51fa\u8ba9\u81ea\u5df1\u89c9\u5f97\u5bf9\u4e0d\u8d77\u81ea\u5df1\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u4e2a\u6ca1\u6709\u6b7b\u7684\u201c\u6b7b\u4eba\u201d\uff0c\u5ffd\u7136\u7528\u4e00\u79cd\u5947\u602a\u800c\u8870\u5f31\u7684\u58f0\u97f3\u95ee\u4ed6\uff1a\u201c\u6211\u7684\u76ae\u888b\u91cc\u6709\u6c34\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u62ff\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u4f60\u4e5f\u9700\u8981\u8fd9\u4e9b\u6c34\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4f46\u662f\u4f60\u518d\u7ed9\u6211\u4e00\u5251\uff0c\u6211\u5c31\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u65e2\u7136\u60f3\u8981\u6211\u7684\u6c34\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u4e0d\u80fd\u6740\u4f60\uff0c\u6211\u4e0d\u80fd\u4e3a\u4e86\u8fd9\u79cd\u7406\u7531\u6740\u4eba\uff01\u201d \u3000\u3000\u201c\u4f46\u662f\u4f60\u672c\u6765\u5c31\u8981\u6740\u6211\u7684\u3002\u201d\u8fd9\u4e2a\u4eba\u8bf4\uff0c\u201c\u6211\u672c\u6765\u5e94\u8be5\u5df2\u7ecf\u6b7b\u5728\u4f60\u624b\u91cc\u3002\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u4e09\u7ae0\u3000\u516b\u89d2\u8857\u4e0a\u7684\u5947\u6848 \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u516b\u89d2\u8857\u4e0a\u7684\u5947\u6848 \u3000\u3000\u201c\u90a3\u65f6\u4f60\u8981\u6740\u6211\uff0c\u6211\u5f53\u7136\u8981\u6740\u4f60\u3002\u201d\u5c0f\u65b9\u8bf4\uff0c\u201c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u975e\u4f46\u4e0d\u80fd\u6740\u4f60\uff0c\u8fd8\u8981\u6551\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u5df2\u7ecf\u662f\u4e2a\u5feb\u8981\u6b7b\u7684\u4eba\uff0c\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u5982\u679c\u6211\u6740\u4e86\u4f60\uff0c\u5c31\u7b97\u80fd\u6d3b\u4e0b\u53bb\uff0c\u4e5f\u6d3b\u5f97\u4e0d\u5b89\u5fc3\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u6d3b\u5f97\u5f88\u5b89\u5fc3\uff1f\u201d \u3000\u3000\u201c\u6211\u4e00\u76f4\u90fd\u6d3b\u5f97\u5f88\u5b89\u5fc3\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u95ee\u5fc3\u65e0\u6127\u3002\u201d \u3000\u3000\u201c\u4f60\u5b81\u6b7b\u4e5f\u4e0d\u80af\u505a\u5bf9\u4e0d\u8d77\u522b\u4eba\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5bf9\u4e0d\u8d77\u81ea\u5df1\u7684\u4e8b\uff0c\u6211\u4e5f\u4e00\u6837\u4e0d\u80af\u505a\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5598\u606f\u7740\uff0c\u5ffd\u7136\u53d1\u51fa\u4e86\u4e00\u58f0\u7edd\u671b\u7684\u547b\u541f\uff0c\u5c31\u597d\u50cf\u4e00\u53ea\u91ce\u517d\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u843d\u4e0b\u4e86\u9677\u997c\u3002 \u3000\u3000\u201c\u6211\u9519\u4e86\uff01\u201d\u4ed6\u547b\u541f\u7740\u9053\uff1a\u201c\u6211\u505a\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u518d\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u53ea\u662f\u4e0d\u505c\u5730\u4f4e\u8bed\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u53d8\uff0c\u4f60\u8fd8\u662f\u4ee5\u524d\u90a3\u4e2a\u5c0f\u65b9\uff0c\u6211\u4e0d\u8be5\u2026\u2026\u4e0d\u8be5\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u4f4e\uff0c\u8d8a\u6765\u8d8a\u8870\u5f31\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u5c0f\u65b9\uff1f\u600e\u4e48\u77e5\u9053\u6211\u6ca1\u53d8\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u4f60\u4e0d\u8be5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5df2\u65e0\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u7684\u547c\u5438\u66f4\u5f31\uff0c\u5598\u606f\u5374\u66f4\u5267\u70c8\uff0c\u800c\u4e14\u5f00\u59cb\u4e0d\u505c\u5730\u54b3\u55fd\u3002 \u3000\u3000\u5c0f\u65b9\u89e3\u4e0b\u4ed6\u7684\u6c34\u888b\uff0c\u60f3\u5582\u4e00\u70b9\u6c34\u7ed9\u4ed6\u559d\uff0c\u5598\u606f\u548c\u54b3\u55fd\u5374\u5f97\u4ed6\u8fde\u4e00\u53e3\u6c34\u90fd\u559d\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u5929\u8272\u660f\u6697\uff0c\u5c0f\u65b9\u6478\u7d22\u7740\uff0c\u4ece\u81ea\u5df1\u8eab\u4e0a\u62ff\u51fa\u5757\u5e03\u4e2d\uff0c\u8638\u4e86\u70b9\u6c34\uff0c\u6ef4\u5728\u4ed6\u5634\u5507\u4e0a\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7ec8\u4e8e\u53c8\u80fd\u5f00\u53e3\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u6211\u5bf9\u4e0d\u8d77\u4f60\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u4e5f\u5bf9\u4e0d\u8d77\u9e70\u54e5\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u8bdd\u8ba9\u5c0f\u65b9\u9707\u60ca\u5f97\u5f88\u4e45\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u80fd\u95ee\uff1a\u201c\u4f60\u4e5f\u8ba4\u5f97\u535c\u9e70\uff0c\u4f60\u600e\u4e48\u4f1a\u5bf9\u4e0d\u8d77\u4ed6\uff1f\u201d\u4ed6\u95ee\u8fd9\u4e2a\u4eba\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u5c0f\u65b9\u95ee\u4ed6\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u547c\u5438\u548c\u5fc3\u8df3\u90fd\u5df2\u5b8c\u5168\u505c\u987f\u3002 \u3000\u3000\u5c0f\u65b9\u8f7b\u8f7b\u5730\u628a\u90a3\u5757\u6253\u6e7f\u4e86\u7684\u5e03\u4e2d\uff0c\u76d6\u5728\u8fd9\u4e2a\u4eba\u7684\u8138\u4e0a\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u77e5\u9053\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u548c\u4ed6\u6709\u5f88\u6df1\u7684\u5173\u7cfb\uff0c\u548c\u535c\u9e70\u4e5f\u6709\u5f88\u6df1\u7684\u5173\u7cfb\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u60f3\u4e0d\u8d77\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u72c2\u98ce\u547c\u5578\uff0c\u4ed6\u5df2\u542c\u4e0d\u51fa\u8fd9\u4e2a\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5929\u8272\u66f4\u6697\u3002 \u3000\u3000\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\u624d\u4f1a\u5929\u4eae\uff0c\u98ce\u624d\u4f1a\u505c\uff1f \u3000\u3000\u5c0f\u65b9\u4e3e\u8d77\u624b\u91cc\u7684\u6c34\u888b\uff0c\u559d\u4e86\u4e24\u53e3\u6c34\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u559d\u8fd9\u76ae\u888b\u91cc\u7684\u6c34\uff0c\u4ed6\u559d\u6c34\u7684\u65f6\u5019\uff0c\u7adf\u5168\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u662f\u5728\u505a\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u4ed6\u559d\u8fd9\u76ae\u888b\u7684\u6c34\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u79cd\u672c\u80fd\u7684\u53cd\u5e94\uff0c\u56e0\u4e3a\u4ed6\u60f3\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u4eba\u5f88\u53ef\u80fd\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u800c\u4e14\u521a\u6b7b\u5728\u4ed6\u624b\u91cc\u3002 \u3000\u3000\u5982\u679c\u4ed6\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u5982\u679c\u4ed6\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u90a3\u4e48\u4ed6\u4e5f\u8bb8\u5b81\u6b7b\u4e5f\u4e0d\u80af\u559d\u8fd9\u4e24\u53e3\u6c34\u4e86\u3002 \u3000\u3000\u5929\u8272\u867d\u7136\u66f4\u6697\uff0c\u5929\u4eae\u4e4b\u524d\u5c82\u975e\u603b\u662f\u6700\u9ed1\u6697\u7684\u65f6\u5019\uff1f \u3000\u3000\u5929\u5ffd\u7136\u4eae\u4e86\uff0c\u98ce\u52bf\u4e5f\u5ffd\u7136\u5c0f\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u770b\u89c1\u4e86\u5728\u4ed6\u6000\u91cc\u7684\u8fd9\u4e2a\u4eba\u7684\u8138\uff0c\u76d6\u5728\u4ed6\u8138\u4e0a\u7684\u5e03\u4e2d\u5df2\u88ab\u5439\u8d70\uff0c\u9732\u51fa\u4e86\u4e00\u5f20\u9971\u5386\u98ce\u971c\u82e6\u96be\uff0c\u5145\u6ee1\u75db\u82e6\u6094\u6068\u7684\u8138\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u7acb\u523b\u6c89\u4e86\u4e0b\u53bb\uff0c\u5168\u8eab\u7684\u8840\u90fd\u51b7\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8d6b\u7136\u7adf\u662f\u52a0\u7b54\u3002 \u3000\u3000\u5728\u4ed6\u88ab\u4eba\u6000\u7591\uff0c\u51e0\u4e4e\u65e0\u8def\u53ef\u8d70\u65f6\uff0c\u552f\u4e00\u628a\u4ed6\u5f53\u670b\u53cb\u7684\u5c31\u662f\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u7528\u6765\u76d6\u4f4f\u8fd9\u5f20\u8138\u7684\u5e03\u4e2d\uff0c\u5c31\u662f\u8fd9\u4e2a\u4eba\u8dea\u4e0b\u6765\u53cc\u624b\u732e\u7ed9\u4ed6\u7684\u201c\u54c8\u8fbe\u201d\uff0c\u8c61\u5f81\u7740\u53cb\u8c0a\u548c\u5c0a\u656c\u7684\u201c\u54c8\u8fbe\u201d\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u4e2a\u4eba\u5374\u5df2\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\uff0c\u4ed6\u5c45\u7136\u8fd8\u5728\u8fd9\u4e2a\u4eba\u6b7b\u540e\u559d\u5149\u4e86\u4ed6\u76ae\u888b\u4e2d\u7684\u6c34\u3002 \u3000\u3000\u2014\u2014\u52a0\u7b54\u600e\u4e48\u6ca1\u6709\u6b7b\uff1f\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u600e\u4e48\u4f1a\u548c\u5415\u4e09\u7684\u5c5e\u4e0b\u5728\u4e00\u8d77\uff1f \u3000\u3000\u2014\u2014\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8bf4\u4ed6\u9519\u4e86\uff1f\u4e3a\u4ec0\u4e48\u8981\u8bf4\u4ed6\u5bf9\u4e0d\u8d77\u5c0f\u65b9\u548c\u535c\u9e70\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u5c0f\u65b9\u90fd\u6ca1\u6709\u60f3\u3002 \u3000\u3000\u4ed6\u552f\u4e00\u60f3\u5230\u7684\uff0c\u5c31\u662f\u5728\u90a3\u4e2a\u7a84\u5c0f\u7684\u5e10\u7bf7\uff0c\u52a0\u7b54\u5c06\u81ea\u5df1\u552f\u4e00\u73cd\u60dc\u7684\u76ae\u9774\u9001\u7ed9\u4ed6\uff0c\u8981\u4ed6\u5feb\u9003\u8d70\u65f6\u6240\u6d41\u9732\u51fa\u7684\u90a3\u79cd\u771f\u60c5\u3002 \u3000\u3000\u5982\u679c\u73b0\u5728\u6709\u4eba\u80fd\u770b\u89c1\u5c0f\u65b9\u7684\u8138\uff0c\u4e00\u5b9a\u4f1a\u5f88\u60ca\u5f02\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u8138\u51e0\u4e4e\u5df2\u53d8\u5f97\u548c\u8fd9\u6b7b\u4eba\u4e00\u6837\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u8138\u4e0a\u4e5f\u540c\u6837\u5145\u6ee1\u4e86\u75db\u82e6\u548c\u6094\u6068\u3002 \u3000\u3000\u96be\u9053\u8fd9\u5c31\u662f\u547d\u8fd0\uff1f \u3000\u3000\u547d\u8fd0\u4e3a\u4ec0\u4e48\u603b\u8981\u5c06\u4eba\u903c\u5165\u4e00\u79cd\u65e0\u53ef\u5948\u4f55\u7684\u6b7b\u89d2\u91cc\uff0c\u4e3a\u4ec0\u4e48\u603b\u8981\u62e8\u5f04\u4eba\u4eec\u53bb\u505a\u4e00\u4e9b\u4ed6\u672c\u6765\u6b7b\u4e5f\u4e0d\u80af\u53bb\u505a\u7684\u4e8b\uff1f \u3000\u3000\u98ce\u66b4\u5df2\u5e73\u606f\uff0c\u5c38\u4f53\u5df2\u63a9\u57cb\u3002 \u3000\u3000\u5bf9\u5c0f\u65b9\u6765\u8bf4\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u7b2c\u4e00\u6b21\u7ecf\u9a8c\uff0c\u4ed6\u7ecf\u5386\u8fc7\u98ce\u66b4\uff0c\u4e5f\u63a9\u57cb\u8fc7\u5c38\u4f53\uff0c\u552f\u4e00\u4e0d\u540c\u7684\u662f\uff0c\u8fd9\u4e00\u6b21\u4ed6\u57cb\u846c\u7684\u662f\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u4e00\u4e2a\u6b7b\u5728\u4ed6\u5251\u4e0b\u7684\u670b\u53cb\u3002 \u3000\u3000\u5c0f\u65b9\u4ee5\u5251\u4f5c\u4ed7\uff0c\u6323\u624e\u7740\u5f80\u524d\u8d70\u3002 \u3000\u3000\u4ed6\u6839\u672c\u6ca1\u6709\u5730\u65b9\u53ef\u53bb\uff0c\u4e5f\u4e0d\u77e5\u80fd\u5230\u54ea\u91cc\uff0c\u66f4\u4e0d\u77e5\u9053\u80fd\u652f\u6301\u5230\u4ec0\u4e48\u65f6\u5019\u3002 \u3000\u3000\u6ca1\u6709\u6c34\uff0c\u6ca1\u6709\u7cae\u98df\uff0c\u6ca1\u6709\u4f53\u529b\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\uff0c\u751a\u81f3\u8fde\u90a3\u4e00\u80a1\u6c42\u751f\u7684\u610f\u5fd7\u90fd\u5df2\u56e0\u6094\u6068\u800c\u6d88\u5931\uff0c\u4ed6\u968f\u65f6\u90fd\u53ef\u80fd\u5012\u4e0b\u53bb\uff0c\u4e00\u5012\u5c31\u53ef\u80fd\u6c38\u8fdc\u7ad9\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u8981\u5f80\u524d\u8d70\uff1f \u3000\u3000\u56e0\u4e3a\u5c0f\u71d5\u3002\u4ed6\u4eff\u4f5b\u53c8\u542c\u89c1\u4e86\u5c0f\u71d5\u7684\u58f0\u97f3\uff0c\u5145\u6ee1\u4e86\u75db\u82e6\u60b2\u4f24\u7684\u547b\u541f\u58f0\u3002\u8fd9\u4e00\u6b21\u4ed6\u8fd8\u662f\u4e0d\u80fd\u786e\u5b9a\u4ed6\u542c\u89c1\u7684\u58f0\u97f3\u7a76\u7adf\u662f\u771f\u662f\u5e7b\uff1f\u6240\u4ee5\u4ed6\u53ea\u8981\u8fd8\u6709\u4e00\u5206\u529b\u6c14\uff0c\u8fd8\u80fd\u518d\u5f80\u524d\u8d70\u4e00\u6b65\uff0c\u4ed6\u5c31\u7edd\u4e0d\u80af\u505c\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u627e\u51fa\u89e3\u7b54\u6765\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u627e\u5230\u4e86\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u51e0\u4e4e\u5df2\u7ecf\u5012\u4e0b\u6c38\u8fdc\u65e0\u6cd5\u518d\u7ad9\u8d77\u6765\uff0c\u4ed6\u770b\u89c1\u4e86\u9f50\u5c0f\u71d5\u3002 \u3000\u3000\u592a\u9633\u53c8\u5347\u8d77\uff0c\u5927\u5730\u53c8\u53d8\u5f97\u9177\u70ed\u5219\u6d2a\u7089\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u53d1\u73b0\u5979\u6b63\u5411\u4ed6\u8d70\u8fc7\u6765\uff0c\u8d64\u7740\u811a\u8d70\u5728\u6eda\u70eb\u7684\u7802\u7c92\u4e0a\uff0c\u5168\u8eab\u7684\u8863\u670d\u90fd\u5df2\u88ab\u6495\u88c2\u3002\u6f06\u9ed1\u7684\u5934\u53d1\u62ab\u6563\uff0c\u82cd\u767d\u7f8e\u4e3d\u7684\u8138\u5df2\u88ab\u6253\u80bf\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u6cea\u6c34\u3002 \u3000\u3000\u518d\u5f80\u524d\u770b\uff0c\u5c31\u53ef\u4ee5\u770b\u89c1\u72ec\u5b64\u75f4\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u8d64\u88f8\u7740\uff0c\u8eba\u5728\u9177\u70ed\u7684\u592a\u9633\u4e0b\uff0c\u4ed6\u7684\u5251\u4ecd\u6446\u5728\u4ed6\u4f38\u624b\u53ef\u53ca\u4e4b\u5904\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u770b\u6765\u5374\u4f3c\u5df2\u865a\u8131\uff0c\u56e0\u6ee1\u8db3\u800c\u865a\u8131\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u89c1\u8fd9\u60c5\u51b5\uff0c\u4e00\u5b9a\u90fd\u53ef\u4ee5\u60f3\u50cf\u5230\u521a\u624d\u53d1\u751f\u8fc7\u4ec0\u4e48\u4e8b\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u5728\u5669\u68a6\u4e2d\u770b\u89c1\u7684\u90a3\u4e9b\u4e8b\uff0c\u5728\u73b0\u5b9e\u4e2d\u65e0\u7591\u4e5f\u540c\u6837\u53d1\u751f\u8fc7\u3002\u5f88\u53ef\u80fd\u6bd4\u4ed6\u5728\u5669\u68a6\u4e2d\u89c1\u5230\u7684\u66f4\u60b2\u60e8\u66f4\u53ef\u6015\u66f4\u4ee4\u4eba\u5fc3\u788e\u3002 \u3000\u3000\u2014\u2014\u6709\u8c01\u80fd\u8bf4\u51fa\u4e00\u4e2a\u4eba\u771f\u6b63\u5fc3\u788e\u65f6\u662f\u4ec0\u4e48\u611f\u89c9\uff1f \u3000\u3000\u5c0f\u65b9\u4e5f\u8bf4\u4e0d\u51fa\uff0c\u4f46\u662f\u4ed6\u5df2\u7ecf\u611f\u89c9\u5230\u3002 \u3000\u3000\u5c0f\u71d5\u5df2\u7ecf\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u75f4\u75f4\u5730\u770b\u7740\u4ed6\uff0c\u5145\u6ee1\u6cea\u6c34\u7684\u773c\u775b\u91cc\uff0c\u4e5f\u5e26\u7740\u79cd\u8c01\u90fd\u65e0\u6cd5\u63cf\u5f97\u51fa\uff0c\u4f46\u662f\u65e0\u8bba\u8c01\u770b\u89c1\u90fd\u4f1a\u5fc3\u788e\u7684\u8868\u60c5\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u4f38\u5f00\u53cc\u81c2\u8fce\u63a5\u4ed6\u7684\u62e5\u62b1\uff0c\u4f46\u662f\u5c0f\u65b9\u5374\u5df2\u4ece\u5979\u9762\u524d\u51b2\u8fc7\uff0c\u6251\u5411\u72ec\u5b64\u75f4\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u53bb\u62e5\u62b1\u72ec\u5b64\u75f4\u3002 \u3000\u3000\u4ed6\u6251\u8fc7\u53bb\uff0c\u56e0\u4e3a\u4ed6\u7684\u638c\u4e2d\u4ecd\u6709\u5251\uff0c\u4ed6\u53ea\u60f3\u4e00\u5251\u523a\u7a7f\u72ec\u5b64\u75f4\u8d64\u88f8\u7684\u54bd\u5589\u3002 \u3000\u3000\u75db\u82e6\u548c\u6124\u6012\u5df2\u6fc0\u53d1\u51fa\u4ed6\u6bcf\u4e00\u4efd\u529b\u91cf\uff0c\u6240\u4ee5\u4ed6\u8fd8\u6709\u529b\u91cf\u6325\u5251\u6251\u6740\u3002 \u3000\u3000\u53ef\u89c1\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u81ea\u5df1\u5269\u4e0b\u7684\u529b\u91cf\u4e0d\u591a\u4e86\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u5251\u4ecd\u5728\u4f38\u624b\u53ef\u53ca\u5904\u3002\u4ed6\u8fd9\u4e00\u5251\u8fd8\u6ca1\u6709\u523a\u4e0b\u53bb\u65f6\uff0c\u72ec\u5b64\u75f4\u7684\u5251\u5f88\u53ef\u80fd\u5df2\u523a\u7a7f\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u4f46\u662f\u4ed6\u4e0d\u5728\u4e4e\uff0c\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u5c0f\u65b9\u8fd9\u4e00\u5251\u6ca1\u6709\u523a\u4e0b\u53bb\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u72ec\u5b64\u75f4\u5df2\u4f38\u624b\u53d6\u5251\u5148\u5c06\u4ed6\u523a\u6740\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5251\u6ca1\u6709\u523a\u4e0b\u53bb\uff0c\u53ea\u56e0\u4e3a\u4ed6\u89c9\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u4ed6\u523a\u7684\u662f\u72ec\u5b64\u75f4\u80f8\u819b\uff0c\u662f\u4e00\u6740\u5fc5\u6b7b\u7684\u8981\u5bb3\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e00\u5251\u523a\u4e0b\u65f6\uff0c\u72ec\u5b64\u75f4\u5c45\u7136\u6ca1\u6709\u4f38\u624b\u53d6\u5251\uff0c\u751a\u81f3\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u8138\u8272\u4e5f\u5b8c\u5168\u6ca1\u53d8\u3002 \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u8fd8\u662f\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u602a\u4e8b\uff01 \u3000\u3000\u72ec\u5b64\u75f4\u7684\u8138\u4e0a\u672c\u6765\u5c31\u6ca1\u6709\u8868\u60c5\uff0c\u4e00\u76f4\u90fd\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u73b0\u5728\u4ed6\u8fd9\u5f20\u6ca1\u6709\u8868\u60c5\u7684\u8138\uff0c\u770b\u8d77\u6765\u548c\u4ee5\u524d\u7684\u90a3\u5f20\u6ca1\u6709\u8868\u60c5\u7684\u8138\u5b8c\u5168\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u56e0\u4e3a\u6ca1\u6709\u8868\u60c5\u6709\u65f6\u4e5f\u662f\u79cd\u8868\u60c5\uff0c\u751a\u81f3\u53ef\u4ee5\u7ed9\u4eba\u975e\u5e38\u5f3a\u70c8\u7684\u611f\u53d7\u3002 \u3000\u3000\u4ee5\u524d\u72ec\u5b64\u75f4\u90a3\u5f20\u6ca1\u6709\u8868\u60c5\u7684\u8138\uff0c\u8ba9\u4eba\u4e00\u770b\u89c1\u5c31\u4f1a\u6709\u79cd\u51b7\u9177\u9634\u68ee\u53ef\u6015\u7684\u611f\u60c5\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7ed9\u4eba\u7684\u611f\u53d7\u5374\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u8fd9\u5f20\u6ca1\u6709\u8868\u60c5\u7684\u8138\u53ea\u4f1a\u8ba9\u4eba\u89c9\u5f97\u75db\u82e6\uff0c\u4e00\u79cd\u53ea\u6709\u5728\u4eba\u4eec\u5df2\u7ecf\u89c9\u5f97\u5b8c\u5168\u5931\u8d25\u7edd\u671b\u65f6\u624d\u4f1a\u6709\u7684\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u662f\u5f3a\u8005\uff0c\u662f\u80dc\u8005\uff0c\u5360\u6709\u8005\uff0c\u63a0\u593a\u8005\u3002 \u3000\u3000\u4ed6\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u75db\u82e6\uff1f \u3000\u3000\u5c0f\u65b9\u4e0d\u61c2\uff0c\u6240\u4ee5\u4ed6\u8fd9\u4e00\u5251\u6ca1\u6709\u523a\u4e0b\u53bb\u2014\u2014\u867d\u7136\u6ca1\u6709\u523a\u4e0b\u53bb\uff0c\u5374\u968f\u65f6\u53ef\u4ee5\u523a\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u5251\u950b\u5df2\u5728\u72ec\u5b64\u75f4\u54bd\u5589\u95f4\uff0c\u8ddd\u79bb\u72ec\u5b64\u75f4\u7684\u54bd\u5589\u6700\u591a\u53ea\u6709\u4e00\u5bf8\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u8138\u4e0a\u5374\u8fd8\u662f\u5e26\u7740\u90a3\u79cd\u6ca1\u6709\u8868\u60c5\u7684\u7edd\u671b\u75db\u82e6\u7684\u8868\u60c5\uff0c\u751a\u81f3\u8ba9\u4eba\u89c9\u5f97\u4ed6\u5f88\u5e0c\u671b\u5c0f\u65b9\u8fd9\u4e00\u5251\u80fd\u523a\u7a7f\u4ed6\u7684\u54bd\u5589\uff0c\u5c06\u4ed6\u523a\u6740\u4e8e\u70c8\u65e5\u4e0b\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u4ed6\u60f3\u6b7b\uff1f \u3000\u3000\u2014\u2014\u53ea\u6709\u5931\u8d25\u7684\u4eba\u624d\u60f3\u6b7b\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u60f3\u6b7b\uff1f \u3000\u3000\u5c0f\u71d5\u4e5f\u5728\u770b\u7740\u72ec\u5b64\u75f4\u3002 \u3000\u3000\u5979\u7684\u8863\u88f3\u5df2\u88ab\u6495\u88c2\uff0c\u8138\u4e5f\u88ab\u6253\u80bf\uff0c\u53ef\u662f\u5979\u5728\u770b\u7740\u8fd9\u4e2a\u4eba\u65f6\uff0c\u773c\u4e2d\u5e76\u6ca1\u6709\u6124\u6012\u4ec7\u6068\uff0c\u53cd\u800c\u5145\u6ee1\u8ba5\u523a\u601c\u60af\u3002 \u3000\u3000\u5979\u5ffd\u7136\u8d70\u8fc7\u6765\u62c9\u4f4f\u5c0f\u65b9\u63e1\u5251\u7684\u624b\u8bf4\uff1a\u201c\u6211\u4eec\u8d70\u5427\uff01\u201d\u5979\u8bf4\uff1a\u201c\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u6ca1\u6709\u7528\u4e86\uff0c\u4f60\u5df2\u7ecf\u7528\u4e0d\u7740\u6740\u4ed6\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u7528\uff1f\u201d\u5c0f\u65b9\u4e0d\u61c2\uff1a\u201c\u4e3a\u4ec0\u4e48\u6ca1\u6709\u7528\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u4e0d\u662f\u7537\u4eba\u3002\u201d\u5c0f\u71d5\u7684\u58f0\u97f3\u91cc\u4e5f\u5145\u6ee1\u8ba5\u523a\uff1a\u201c\u4ed6\u60f3\u5360\u6709\u6211\uff0c\u53ef\u60dc\u4ed6\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u72ec\u5b64\u75f4\u8fd8\u8eba\u5728\u90a3\u91cc\uff0c\u8eba\u5728\u6eda\u70eb\u7684\u7802\u7c92\u4e0a\uff0c\u9177\u70ed\u7684\u592a\u9633\u4e0b\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u8d70\u4e86\uff0c\u5c31\u8fd9\u6837\u7559\u4e0b\u4e86\u4ed6\u3002 \u3000\u3000\u2014\u2014\u2014\u4e2a\u5df2\u7ecf\u6ca1\u6709\u7528\u7684\u7537\u4eba\uff0c\u4e00\u4e2a\u5df2\u7ecf\u4e0d\u662f\u7537\u4eba\u7684\u7537\u4eba\uff0c\u6839\u672c\u5df2\u7ecf\u4e0d\u503c\u5f97\u522b\u4eba\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u77e5\u9053\u8ba9\u4ed6\u8fd9\u6837\u5b50\u8eba\u5728\u90a3\u91cc\uff0c\u65e5\u843d\u524d\u4ed6\u5c31\u4f1a\u50cf\u70e4\u7089\u4e0a\u7684\u7099\u8089\u822c\u88ab\u70e4\u7126\u3002 \u3000\u3000\u4ed6\u4eec\u5374\u8fd8\u662f\u8d70\u4e86\uff0c\u56e0\u4e3a\u9664\u4e86\u4ed6\u81ea\u5df1\u4e4b\u5916\u8fd9\u4e16\u754c\u4e0a\u5df2\u7ecf\u6ca1\u6709\u522b\u4eba\u80fd\u6551\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u63a5\u8fc7\u4e86\u4e00\u4ef6\u5c0f\u65b9\u9ed8\u9ed8\u9012\u7ed9\u5979\u7684\u8863\u670d\uff0c\u62ab\u5728\u5979\u51e0\u4e4e\u5df2\u5b8c\u5168\u8d64\u88f8\u7684\u8eab\u5b50\u4e0a\u3002 \u3000\u3000\u5979\u770b\u6765\u867d\u72fc\u72c8\uff0c\u795e\u60c5\u5374\u8fd8\u6bd4\u5c0f\u65b9\u9547\u5b9a\u3002 \u3000\u3000\u5979\u95ee\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u6211\u4eec\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u7740\uff0c\u770b\u770b\u8fd9\u4e00\u7247\u8d64\u70ed\u7684\u5927\u5730\uff0c\u770b\u770b\u81ea\u5df1\u4e00\u53cc\u7a7a\u624b\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u4ed6\u624d\u53cd\u95ee\u5979\uff1a\u201c\u73b0\u5728\u6211\u4eec\u80fd\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u5230\u54ea\u91cc\u53bb\uff0c\u6211\u4eec\u5c31\u5230\u54ea\u91cc\u53bb\u3002\u201d\u5c0f\u71d5\u8bf4\u5f97\u5f88\u8f7b\u677e\uff0c\u5c31\u597d\u50cf\u5b8c\u5168\u4e0d\u77e5\u9053\u73b0\u5728\u4ed6\u4eec\u5df2\u7ecf\u4e00\u65e0\u6240\u6709\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u5012\u4e0b\u3002 \u3000\u3000\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5c0f\u65b9\u624d\u5f00\u53e3\uff1a\u201c\u6211\u60f3\u56de\u62c9\u8428\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u4eec\u5c31\u56de\u62c9\u8428\u3002\u201d\u5c0f\u71d5\u8fd8\u662f\u8bf4\u5f97\u5f88\u8f7b\u677e\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5c31\u56de\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u770b\u7740\u5979\uff0c\u5ffd\u7136\u7b11\u4e86\uff0c\u82e6\u7b11\u3002 \u3000\u3000\u201c\u6211\u4eec\u600e\u4e48\u56de\u53bb\uff1f\u201d\u4ed6\u95ee\uff1a\u201c\u662f\u722c\u56de\u53bb\uff1f\u8fd8\u662f\u88ab\u4eba\u62ac\u56de\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u71d5\u5c45\u7136\u4e5f\u5728\u7b11\uff0c\u7b11\u5f97\u4eff\u4f5b\u5f88\u795e\u79d8\u3002 \u3000\u3000\u5c0f\u65b9\u5b9e\u5728\u60f3\u4e0d\u901a\u5979\u600e\u4e48\u8fd8\u80fd\u7b11\u5f97\u51fa\uff0c\u4f46\u662f\u4ed6\u5f88\u5feb\u5c31\u60f3\u901a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u65f6\u5019\u5979\u5df2\u7ecf\u642c\u5f00\u4e86\u4e00\u5757\u5ca9\u77f3\uff0c\u5c31\u597d\u50cf\u53d8\u620f\u6cd5\u4e00\u6837\u4ece\u5ca9\u77f3\u4e0b\u7684\u4e00\u4e2a\u6d1e\u7a74\u91cc\u62ff\u51fa\u4e86\u4e09\u4e2a\u5f88\u5927\u7684\u76ae\u888b\uff0c\u4e00\u888b\u7cae\u98df\uff0c\u4e00\u888b\u8863\u670d\uff0c\u4e00\u888b\u6c34\u3002 \u3000\u3000\u5c0f\u65b9\u5403\u60ca\u5730\u770b\u7740\u5979\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u606f\u3002 \u3000\u3000\u201c\u6211\u5ffd\u7136\u53d1\u73b0\u4f60\u5f88\u50cf\u4e00\u4e2a\u4eba\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6709\u5f88\u591a\u5730\u65b9\u90fd\u5f88\u50cf\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u6211\u50cf\u8c01\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6c99\u6f20\u4e2d\u7684\u7b2c\u4e00\u53f7\u82f1\u96c4\u597d\u6c49\uff0c\u6c38\u8fdc\u6ca1\u6709\u4eba\u80fd\u6349\u6478\u900f\u7684\u73ed\u5bdf\u5df4\u90a3\u3002\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u4f1a\u50cf\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u4e5f\u8ddf\u4ed6\u4e00\u6837\uff0c\u4e0d\u7ba1\u8d70\u5230\u54ea\u91cc\uff0c\u90fd\u4f1a\u5148\u4e3a\u81ea\u5df1\u7559\u4e0b\u9000\u8def\u3002\u201d \u3000\u3000\u5c0f\u65b9\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u88ab\u4eba\u903c\u5f97\u65e0\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u53c8\u7b11\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u4ece\u4ec0\u4e48\u65f6\u5019\u5f00\u59cb\u7684\uff0c\u5979\u5ffd\u7136\u4e5f\u53d8\u5f97\u50cf\u201c\u9633\u5149\u201d\u4e00\u6837\uff0c\u53d8\u6210\u4e86\u4e2a\u5f88\u7231\u7b11\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u5979\u5e26\u7740\u7b11\u95ee\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u6211\u4eec\u662f\u4e0d\u662f\u5df2\u7ecf\u53ef\u4ee5\u5230\u62c9\u8428\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5df2\u7ecf\u53ef\u4ee5\u53bb\u4e86\u3002\u201d \u3000\u3000\u62c9\u8428\u4f9d\u65e7\u662f\u62c9\u8428\u3002 \u3000\u3000\u5c31\u597d\u50cf\u5176\u4ed6\u90a3\u4e9b\u5386\u53f2\u8f89\u714c\u60a0\u4e45\u7684\u53e4\u57ce\u4e00\u6837\uff0c\u5c81\u6708\u7684\u4fb5\u8680\uff0c\u6218\u4e71\u7684\u6467\u6b8b\uff0c\u4e16\u4e8b\u7684\u8fc1\u79fb\uff0c\u90fd\u4e0d\u80fd\u8ba9\u8fd9\u4e9b\u53e4\u8001\u7684\u5927\u57ce\u6709\u4e1d\u6beb\u6539\u53d8\u3002 \u3000\u3000\u90a3\u6761\u6a2a\u4e98\u4e8e\u5e03\u8fbe\u62c9\u5bab\u4e0e\u6070\u514b\u535c\u91cc\u5c71\u4e4b\u95f4\u7684\u77f3\u780c\u57ce\u57a3\uff0c\u90a3\u4e9b\u5e03\u6ee1\u5728\u5c71\u5934\u4e0a\u7684\u697c\u9601\u3001\u7985\u623f\u3001\u5bfa\u9662\u3001\u7891\u78a3\uff0c\u90a3\u9ad8\u8038\u5728\u5ca9\u77f3\u4e0a\u7684\u5de8\u5927\u57ce\u5821\uff0c\u8fde\u7ef5\u7684\u96c9\u8c0d\uff0c\u53d1\u5149\u7684\u7a97\u77a9\uff0c\u770b\u6765\u4f9d\uff1b\u65e5\u662f\u90a3\u4e48\u7470\u4e3d\uff0c\u90a3\u4e48\u8c03\u548c\u3002 \u3000\u3000\u5e02\u4e2d\u7684\u5df7\u91cc\u4f9d\uff1b\u65e5\u6324\u6ee1\u4e86\u4eba\uff0c\u90a3\u4e9b\u80ae\u810f\u8870\u8001\u7684\u8001\u4e5e\u4e10\u4f9d\uff1b\u65e5\u530d\u5310\u4e8e\u5c18\u571f\u4e2d\uff0c\u5ff5\u7740\u4ed6\u4eec\u5df2\u4e0d\u77e5\u5ff5\u8fc7\u591a\u5c11\u904d\u7684\u516d\u5b57\u771f\u8a00\u201c\u5535\u5427\u5462\u53ed\u5627\u543d\u201d\uff0c\u5411\u8def\u4eba\u548c\u8fdc\u65b9\u6765\u7684\u65c5\u5ba2\u4e5e\u8ba8\uff0c\u8857\u9053\u65c1\u4f9d\u65e7\u5806\u6ee1\u5783\u573e\u548c\u7caa\u4fbf\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u4f1a\u5f71\u54cd\u8fd9\u4e2a\u57ce\u5e02\u7684\u7f8e\u4e3d\u3002 \u3000\u3000\u62c9\u8428\u5c31\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u53c8\u77db\u76fe\u3001\u53c8\u8c03\u548c\u3001\u53c8\u8934\u891b\u3001\u53c8\u7470\u4e3d\uff1b \u3000\u3000\u91cd\u5230\u4e86\u8fd9\u91cc\uff0c\u5c0f\u65b9\u5fc3\u91cc\u7684\u611f\u89c9\u51e0\u4e4e\u5c31\u597d\u50cf\u56de\u5230\u4e86\u4ed6\u7684\u6545\u4e61\u6c5f\u5357\u4e00\u6837\u3002 \u3000\u3000\u5c0f\u71d5\u53c8\u5728\u95ee\u4ed6\uff1a\u201c\u73b0\u5728\u6211\u4eec\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u53bb\u516b\u89d2\u8857\u3002\u201d \u3000\u3000\u90a3\u91cc\u662f\u8fd9\u53e4\u57ce\u7684\u5546\u4e1a\u6c47\u96c6\u533a\uff0c\u9644\u8fd1\u7684\u5927\u5546\u53f7\u51e0\u4e4e\u90fd\u805a\u96c6\u5728\u8fd9\u91cc\uff0c\u4e0d\u7ba1\u4f60\u60f3\u8981\u4e70\u4ec0\u4e48\uff0c\u5728\u90a3\u91cc\u90fd\u53ef\u4ee5\u627e\u5f97\u5230\u3002 \u3000\u3000\u5c0f\u71d5\u53c8\u95ee\uff1a\u201c\u4f60\u8981\u5230\u90a3\u91cc\u53bb\u4e70\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u90fd\u4e0d\u4e70\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u90fd\u4e0d\u4e70\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53bb\u4e00\u5bb6\u5546\u53f7\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u9e70\u8bb0\u5546\u53f7\u3002\u201d \u3000\u3000\u201c\u9e70\u8bb0\uff1f\u662f\u4e0d\u662f\u535c\u9e70\u7684\uff1f\u201d \u3000\u3000\u201c\u4ee5\u524d\u662f\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u4ed6\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u65e2\u7136\u5df2\u7ecf\u4e0d\u662f\u4ed6\u7684\uff0c\u4f60\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d\u5c0f\u71d5\u597d\u50cf\u5df2\u51b3\u5fc3\u8981\u6253\u7834\u7802\u9505\u95ee\u5230\u5e95\u3002 \u3000\u3000\u201c\u53bb\u627e\u4e00\u4e2a\u4eba\u3002\u201d\u5c0f\u65b9\u6162\u6162\u5730\u56de\u7b54\uff1a\u201c\u95ee\u4ed6\u4e00\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5c0f\u71d5\uff1a\u201c\u5982\u679c\u4f60\u4e0d\u53bb\uff0c\u4e0d\u59a8\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5979\u5f53\u7136\u4e0d\u4f1a\u4e0d\u53bb\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u7a7f\u8fc7\u4e86\u7e41\u8363\u7684\u5e02\u96c6\uff0c\u4ece\u4e24\u65c1\u5df2\u88ab\u6cb9\u706f\u718f\u9ed1\u7684\u94fa\u5b50\u91cc\u4f20\u51fa\u7684\u9178\u5976\u916a\u5473\uff0c\u6d53\u5f97\u51e0\u4e4e\u8ba9\u4eba\u8fde\u6c14\u90fd\u900f\u4e0d\u8fc7\u6765\uff0c\u660e\u4eae\u7684\u9633\u5149\u548c\u98d2\u98d2\u7684\u98ce\u7802\u53c8\u51e0\u4e4e\u4f7f\u4eba\u8fde\u773c\u775b\u90fd\u7741\u4e0d\u5f00\u3002 \u3000\u3000\u5e02\u573a\u4e0a\u8d27\u7269\u5145\u6c9b\uff0c\u4ece\u6253\u7bad\u7089\u6765\u7684\u8336\u7816\u5806\u79ef\u5982\u5c71\uff0c\u4ece\u5929\u7afa\u6765\u7684\u6843\u674e\u6851\u6939\u8349\u8393\u4ee4\u4eba\u5782\u6d8e\u6b32\u6ef4\uff0c\u4ece\u85cf\u4e1c\u6765\u7684\u85cf\u9999\uff0c\u7cbe\u5236\u7684\u91d1\u5c5e\u978d\u5177\uff0c\u4ece\u5c3c\u6cca\u5c14\u6765\u7684\u9999\u6599\u3001\u84dd\u975b\u3001\u73ca\u745a\u3001\u73cd\u73e0\u3001\u94dc\u5668\uff0c\u4ece\u5173\u5185\u6765\u7684\u74f7\u5668\u548c\u4e1d\u7f0e\uff0c\u8499\u53e4\u7684\u76ae\u8d27\u4e0e\u552c\u73c0\uff0c\u9521\u91d1\u7684\u7cd6\u679c\u3001\u9e9d\u9999\u548c\u5927\u7c73\uff0c\u2026\u2026\u8fd9\u4e9b\u73cd\u8d35\u7684\u8d27\u7269\u53c8\u8ba9\u4eba\u4e0d\u80fd\u4e0d\u628a\u773c\u775b\u7741\u5927\u4e9b\u3002 \u3000\u3000\u552f\u4e00\u548c\u4ee5\u524d\u4e0d\u540c\u7684\u662f\uff0c\u8fd9\u6761\u8857\u4e0a\u7684\u4eba\u6837\u5b50\u597d\u50cf\u53d8\u4e86\u3002 \u3000\u3000\u8fd9\u6761\u8857\u4e5f\u8ddf\u522b\u7684\u8857\u9053\u4e00\u6837\uff0c\u8857\u4e0a\u7684\u4eba\u5927\u81f4\u53ef\u5206\u4e3a\u4e24\u79cd\uff1a\u4e00\u79cd\u662f\u4f4f\u5728\u8fd9\u91cc\u7684\uff0c\u4e00\u79cd\u662f\u4ece\u522b\u7684\u5730\u65b9\u6765\u7684\u3002 \u3000\u3000\u4ee5\u524d\u5c0f\u65b9\u8d70\u8fc7\u8fd9\u6761\u8857\u65f6\uff0c\u603b\u89c9\u5f97\u6bcf\u4e2a\u4eba\u90fd\u5e26\u7740\u5065\u5eb7\u6109\u5feb\u5bcc\u8db3\u7684\u6837\u5b50\uff0c\u663e\u5f97\u5bf9\u81ea\u5df1\u7684\u751f\u6d3b\u548c\u4e8b\u4e1a\u90fd\u5f88\u6ee1\u610f\uff0c\u5bf9\u672a\u6765\u4e5f\u5145\u6ee1\u4fe1\u5fc3\u3002 \u3000\u3000\u53ef\u662f\u4eca\u5929\u8fd9\u4e9b\u4eba\u7684\u6837\u5b50\u90fd\u53d8\u4e86\uff0c\u53d8\u5f97\u6709\u70b9\u754f\u7f29\uff0c\u6709\u70b9\u9b3c\u795f\uff0c\u770b\u4eba\u7684\u65f6\u5019\u773c\u775b\u91cc\u4eff\u4f5b\u5145\u6ee1\u6000\u7591\u548c\u6212\u5fc3\uff0c\u800c\u4e14\u6bcf\u4e2a\u4eba\u90fd\u663e\u5f97\u5f88\u5bb3\u6015\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u6761\u8857\u4e0a\u90fd\u662f\u6bb7\u5b9e\u7684\u5546\u53f7\uff0c\u8fd9\u4e9b\u4eba\u7684\u751f\u6d3b\u4e00\u5411\u65e0\u5fe7\u65e0\u8651\u3002 \u3000\u3000\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u5bb3\u6015\uff1f\u6015\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u5c0f\u65b9\u611f\u89c9\u5230\u8fd9\u4e00\u70b9\u7684\u65f6\u5019\uff0c\u5c0f\u71d5\u4e5f\u540c\u6837\u611f\u89c9\u5230\u4e86\u3002 \u3000\u3000\u5979\u62c9\u4e86\u62c9\u5c0f\u65b9\u7684\u8863\u89d2\uff0c\u8f7b\u8f7b\u5730\u544a\u8bc9\u4ed6\uff1a\u201c\u8fd9\u6761\u8857\u4e0a\u4e00\u5b9a\u51fa\u4e86\u4e8b\u3002\u201d\u5979\u8bf4\uff1a\u201c\u800c\u4e14\u4e00\u5b9a\u662f\u4ef6\u5f88\u53ef\u6015\u7684\u4e8b\u3002\u201d \u3000\u3000\u5979\u53c8\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u6ce8\u610f\u5230\u522b\u4eba\u770b\u4f60\u7684\u6837\u5b50\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u4e5f\u6ce8\u610f\u5230\u3002 \u3000\u3000\u522b\u4eba\u770b\u4ed6\u65f6\u7684\u6837\u5b50\uff0c\u5c31\u597d\u50cf\u628a\u4ed6\u5f53\u6210\u968f\u65f6\u90fd\u53ef\u80fd\u628a\u761f\u75ab\u9ebb\u75af\u5e26\u6765\u7684\u761f\u795e\u3002 \u3000\u3000\u548c\u6c14\u751f\u8d22\uff0c\u505a\u751f\u610f\u7684\u4eba\u672c\u6765\u662f\u4e0d\u53ef\u4ee5\u7528\u8fd9\u79cd\u773c\u5149\u770b\u4eba\u7684\u3002 \u3000\u3000\u2014\u2014\u8fd9\u5730\u65b9\u53c8\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u96be\u9053\u53c8\u8ddf\u5c0f\u65b9\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e0a\u6b21\u535c\u9e70\u7684\u5c71\u5e84\u88ab\u711a\uff0c\u9e70\u8bb0\u5546\u53f7\u6613\u4e3b\uff0c\u4ed6\u548c\u201c\u9633\u5149\u201d\u8d70\u8fc7\u8fd9\u6761\u8857\u65f6\uff0c\u522b\u4eba\u4e5f\u662f\u7528\u8fd9\u79cd\u773c\u5149\u770b\u4ed6\u4eec\u7684\u3002 \u3000\u3000\u96be\u9053\u8fd9\u6b21\u7684\u53d8\u6545\u53c8\u53d1\u751f\u5728\u9e70\u8bb0\uff1f \u3000\u3000\u96be\u9053\u8fd9\u4e9b\u4eba\u8fd8\u8ba4\u5f97\u4ed6\uff0c\u8fd8\u8bb0\u5f97\u4ed6\u662f\u535c\u9e70\u7684\u670b\u53cb\uff1f \u3000\u3000\u96be\u9053\u535c\u9e70\u5df2\u56de\u5230\u8fd9\u91cc\uff0c\u5bf9\u4ed6\u7684\u4ec7\u654c\u4f5c\u4e86\u516c\u6b63\u800c\u6b8b\u9177\u7684\u62a5\u590d\uff1f \u3000\u3000\u8fd9\u4e0d\u662f\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u3002 \u3000\u3000\u535c\u9e70\u505a\u7684\u4e8b\uff0c\u672c\u6765\u5c31\u662f\u4ee4\u4eba\u6c38\u8fdc\u65e0\u6cd5\u9884\u6599\u5f97\u5230\u7684\u3002 \u3000\u3000\u5047\u5982\u5c0f\u65b9\u56de\u5230\u9e70\u8bb0\u65f6\uff0c\u535c\u9e70\u5df2\u7ecf\u5750\u5728\u67dc\u53f0\u91cc\uff0c\u5c0f\u65b9\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u592a\u5403\u60ca\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u8ba4\u4e3a\u8fd9\u4e16\u754c\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u535c\u9e70\u505a\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u811a\u6b65\u52a0\u5feb\uff0c\u5fc3\u8df3\u4e5f\u52a0\u5feb\u4e86\u3002\u6068\u4e0d\u5f97\u4e00\u6b65\u5c31\u8de8\u8fdb\u9e70\u8bb0\u7684\u5927\u95e8\u3002 \u3000\u3000\u5982\u679c\u4ed6\u77e5\u9053\u9e70\u8bb0\u5546\u53f7\u91cc\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4f60\u5c31\u7b97\u7528\u8f7f\u5b50\u62ac\u4ed6\u3001\u7528\u97ad\u5b50\u62bd\u4ed6\uff0c\u4ed6\u4e5f\u672a\u5fc5\u4f1a\u8fdb\u53bb\u7684\u3002 \u3000\u3000\u9e70\u8bb0\u7684\u5927\u95e8\u662f\u5f00\u7740\u7684\uff0c\u8fdc\u8fdc\u5c31\u53ef\u4ee5\u770b\u5f97\u89c1\u5e97\u91cc\u7684\u60c5\u51b5\u3002 \u3000\u3000\u5e97\u91cc\u6709\u4e94\u4e2a\u4eba\uff0c\u6b63\u5728\u505a\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u9e70\u8bb0\u4e00\u5411\u662f\u5bb6\u4fe1\u7528\u5353\u8457\u3001\u751f\u610f\u9f0e\u76db\u7684\u5546\u53f7\uff0c\u5e97\u91cc\u7684\u4eba\u5f53\u7136\u90fd\u6709\u4e8b\u505a\uff0c\u975e\u505a\u4e8b\u4e0d\u53ef\u3002 \u3000\u3000\u8fd9\u4e94\u4e2a\u4eba\u5728\u505a\u4e8b\uff0c\u7edd\u4e0d\u662f\u4ef6\u5947\u602a\u7684\u4e8b\uff0c\u4ed6\u4eec\u6ca1\u4e8b\u53ef\u505a\u624d\u662f\u5947\u602a\u7684\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u5c45\u7136\u770b\u4e0d\u51fa\u4ed6\u4eec\u5728\u505a\u7684\u662f\u4ec0\u4e48\u4e8b\uff0c\u65e0\u8bba\u8c01\u4e00\u773c\u770b\u8fc7\u53bb\u90fd\u770b\u4e0d\u51fa\u4ed6\u4eec\u5728\u505a\u7684\u662f\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5728\u505a\u7684\u4e8b\u5f88\u5947\u602a\uff0c\u4e0d\u4f46\u662f\u5728\u4e00\u822c\u60c5\u51b5\u4e0b\u4efb\u4f55\u4eba\u90fd\u4e0d\u4f1a\u505a\u7684\u4e8b\uff0c\u800c\u4e14\u53ef\u4ee5\u8bf4\u662f\u4efb\u4f55\u4eba\u4e00\u8f88\u5b50\u90fd\u5f88\u96be\u770b\u5f97\u5230\u7684\u4e8b\u3002 \u3000\u3000\u6240\u4ee5\u4f60\u5c31\u771f\u770b\u89c1\u4e86\u4ed6\u4eec\u6b63\u5728\u505a\u4ec0\u4e48\u4e8b\uff0c\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u4ed6\u4eec\u6b63\u5728\u505a\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u6b63\u5728\u6740\u4eba\uff01 \u3000\u3000\u5c31\u5728\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u5c31\u5728\u4e00\u6761\u4eba\u5f88\u591a\u7684\u8857\u9053\u4e0a\uff0c\u4e00\u5bb6\u5f00\u7740\u5927\u95e8\u7684\u5e97\u94fa\u91cc\u6740\u4eba\u3002 \u3000\u3000\u2014\u2014\u662f\u8c01\u5728\u6740\u8c01\uff1f \u3000\u3000\u6709\u4e24\u4e2a\u4eba\u5728\u6740\u53e6\u5916\u4e24\u4e2a\u4eba\u3002\u8fd8\u6709\u4e00\u4e2a\u4eba\u5728\u65c1\u8fb9\u770b\uff0c\u770b\u7740\u4ed6\u4eec\u4eba\u6740\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u51b2\u8fc7\u53bb\uff0c\u8fd8\u6ca1\u6709\u51b2\u8fdb\u95e8\u5c31\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7b2c\u4e00\u4e2a\u770b\u5230\u7684\u4eba\u5c31\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u9664\u4e86\u7167\u955c\u5b50\u7684\u65f6\u5019\u5916\uff0c\u771f\u7684\u770b\u89c1\u4e86\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u770b\u5230\u4e86\u4ed6\u81ea\u5df1\uff0c\u4e00\u4e2a\u957f\u5f97\u8ddf\u4ed6\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u8fd8\u5728\u9e70\u8bb0\u7684\u5927\u95e8\u5916\u9762\uff0c\u5e97\u91cc\u5c45\u7136\u8fd8\u6709\u4e00\u4e2a\u5c0f\u65b9\u7ad9\u5728\u67dc\u53f0\u524d\u770b\u7740\u522b\u4eba\u6740\u4eba\u3002 \u3000\u3000\u2014\u2014\u5c0f\u65b9\u4e0d\u662f\u5b6a\u751f\u5b50\uff0c\u4e5f\u6ca1\u6709\u5144\u5f1f\uff0c\u53e6\u5916\u8fd9\u4e2a\u5c0f\u65b9\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f \u3000\u3000\u9f50\u5c0f\u71d5\u65e0\u7591\u4e5f\u540c\u6837\u5403\u60ca\u3002 \u3000\u3000\u5c0f\u65b9\u6014\u4f4f\u65f6\uff0c\u5979\u4e5f\u540c\u6837\u6014\u4f4f\uff0c\u5979\u7528\u529b\u62c9\u4f4f\u5c0f\u65b9\u7684\u624b\u8bf4\uff1a\u201c\u6211\u770b\u89c1\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u89c1\u4f60\u5728\u524d\u9762\u90a3\u5bb6\u5546\u5e97\u91cc\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u660e\u660e\u5728\u6211\u65c1\u8fb9\uff0c\u600e\u4e48\u4f1a\u53c8\u5728\u90a3\u5bb6\u5e97\u91cc\uff1f\u201d\u5c0f\u71d5\u95ee\u5c0f\u65b9\uff1a\u201c\u96be\u9053\u4f60\u4e00\u4e2a\u4eba\u4f1a\u53d8\u6210\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\uff0c\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u542c\u89c1\u522b\u4eba\u95f4\u4ed6\u8fd9\u79cd\u95ee\u9898\u90fd\u53ea\u6709\u82e6\u7b11\uff0c\u8fd9\u95ee\u9898\u5b9e\u5728\u592a\u7edd\uff0c\u592a\u8352\u8c2c\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u5c0f\u65b9\u770b\u6e05\u695a\u6740\u4eba\u7684\u4eba\u548c\u88ab\u6740\u7684\u4eba\u65f6\uff0c\u4ed6\u8fde\u82e6\u7b11\u90fd\u7b11\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u5c31\u597d\u50cf\u5ffd\u7136\u88ab\u4eba\u780d\u4e86\u4e00\u5200\uff0c\u6b63\u780d\u5728\u4ed6\u611f\u89c9\u6700\u7075\u654f\u7684\u5173\u8282\u4e0a\u3002 \u3000\u3000\u6740\u4eba\u7684\u4eba\u6709\u4e24\u4e2a\uff0c\u4e00\u4e2a\u7537\u3001\u4e00\u4e2a\u5973\u3002 \u3000\u3000\u88ab\u6740\u7684\u4e5f\u6709\u4e24\u4e2a\uff0c\u4e5f\u662f\u4e00\u4e2a\u7537\u3001\u4e00\u4e2a\u5973\u3002 \u3000\u3000\u6740\u4eba\u7684\u7537\u4eba\u8d6b\u7136\u7adf\u662f\u201c\u535c\u9e70\u201d\u3002 \u3000\u3000\u6740\u4eba\u7684\u5973\u4eba\u8d6b\u7136\u7adf\u662f\u201c\u9633\u5149\u201d\u3002 \u3000\u3000\u535c\u9e70\u6740\u7684\u4eba\u8d6b\u7136\u7adf\u662f\u73ed\u5bdf\u5df4\u90a3\uff01\u201d \u3000\u3000\u201c\u9633\u5149\u201d\u6740\u7684\u4eba\u8d6b\u7136\u7adf\u662f\u6ce2\u5a03\u3002 \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u5c0f\u65b9\u5c45\u7136\u6b63\u5728\u770b\u7740\u535c\u9e70\u548c\u201c\u9633\u5149\u201d\u6740\u73ed\u5bdf\u5df4\u90a3\u548c\u6ce2\u5a03\uff0c\u5c45\u7136\u8fde\u4e00\u70b9\u529d\u963b\u7684\u610f\u601d\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u8c01\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u8fd9\u662f\u4ef6\u5f88\u7b80\u5355\u7684\u4e8b\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u6709\u5f88\u591a\u8868\u9762\u770b\u6765\u5f88\u590d\u6742\u5f88\u795e\u79d8\u7684\u4e8b\uff0c\u5176\u5b9e\u90fd\u5f88\u7b80\u5355\u3002 \u3000\u3000\u6709\u65f6\u751a\u81f3\u7b80\u5355\u5f97\u53ef\u7b11\u3002 \u3000\u3000\u2014\u2014\u4e3a\u4ec0\u4e48\u4f1a\u6709\u4e24\u4e2a\u5c0f\u65b9\uff1f \u3000\u3000\u56e0\u4e3a\u5e97\u91cc\u53e6\u5916\u4e00\u4e2a\u5c0f\u65b9\u662f\u8721\u4eba\uff0c\u662f\u7528\u8721\u505a\u6210\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\u4e3a\u4ec0\u4e48\u4f1a\u6740\u73ed\u5bdf\u5df4\u90a3\uff1f\u201c\u9633\u5149\u201d\u4e3a\u4ec0\u4e48\u4f1a\u6740\u6ce2\u5a03\uff1f \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u4e5f\u662f\u8721\u4eba\u3002 \u3000\u3000\u5e97\u91cc\u7684\u4e94\u4e2a\u4eba\u90fd\u662f\u7528\u8721\u505a\u6210\u7684\u4eba\uff0c\u867d\u7136\u505a\u5f97\u60df\u5999\u690e\u8096\uff0c\u5374\u662f\u5047\u7684\u3002 \u3000\u3000\u6240\u6709\u65e0\u6cd5\u89e3\u91ca\u7684\u4e8b\u90fd\u6709\u4e86\u89e3\u7b54\uff0c\u7b54\u6848\u5f88\u7b80\u5355\uff0c\u53ef\u662f\u5e76\u4e0d\u53ef\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u5c0f\u65b9\u7acb\u523b\u53c8\u60f3\u5230\u4e86\u5f88\u591a\u95ee\u9898\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e9b\u8721\u4eba\u662f\u8c01\u505a\u7684\uff1f\u4e3a\u4ec0\u4e48\u8981\u505a\u8fd9\u79cd\u4e8b\uff1f\u6709\u4ec0\u4e48\u7528\u610f\uff1f \u3000\u3000\u2014\u2014\u9e70\u8bb0\u5546\u53f7\u91cc\u7684\u4eba\u4e00\u5411\u5f88\u591a\uff0c\u73b0\u5728\u600e\u4e48\u4f1a\u53ea\u5269\u4e0b\u4e94\u4e2a\u7528\u8721\u505a\u7684\u5047\u4eba\uff1f\u522b\u7684\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u5c0f\u65b9\u7ee7\u7eed\u5f80\u524d\u8d70\uff0c\u53c8\u770b\u89c1\u4e86\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u7ad9\u5728\u6bd4\u8f83\u8fdc\u7684\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u662f\u4e00\u4e2a\u7537\u4eba\uff0c\u4e00\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u7537\u4eba\u662f\u5415\u4e09\uff0c\u5973\u4eba\u662f\u82cf\u82cf\uff0c\u82cf\u82cf\u624b\u91cc\u8fd8\u62b1\u7740\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u5415\u4e09\u98ce\u8c8c\u4f9d\u65e7\uff0c\u82cf\u82cf\u7f8e\u4e3d\u5982\u6614\uff0c\u5979\u6000\u91cc\u62b1\u7740\u7684\u5b69\u5b50\u7740\u82b1\u8863\uff0c\u6234\u7ea2\u5e3d\uff0c\u867d\u7136\u53ea\u6709\u4e24\u4e09\u4e2a\u6708\u5927\uff0c\u5df2\u7ecf\u957f\u5f97\u80a5\u5934\u5927\u8033\uff0c\u53ef\u7231\u6781\u4e86\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u5f53\u7136\u4e5f\u662f\u8721\u505a\u7684\u5047\u4eba\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u4eec\u4e0d\u662f\u8721\u505a\u7684\uff0c\u5c31\u7b97\u5415\u4e09\u771f\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5c0f\u65b9\u4e5f\u4e0d\u6562\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5e76\u6ca1\u6709\u5fd8\u8bb0\u5c71\u6751\u77f3\u5c4b\u4e2d\u90a3\u4e00\u6bb5\u5f80\u4e8b\u3002 \u3000\u3000\u82cf\u82cf\u6000\u91cc\u62b1\u7740\u7684\u5b69\u5b50\uff0c\u65e0\u7591\u5c31\u662f\u4ed6\u7684\u5b69\u5b50\uff0c\u662f\u4ed6\u4eb2\u751f\u7684\u9aa8\u8089\uff0c\u662f\u4ed6\u8840\u4e2d\u7684\u8840\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u7684\u867d\u7136\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8721\u505a\u7684\u5b69\u5b50\uff0c\u4f46\u662f\u8fd9\u5b69\u5b50\u7684\u5bb9\u8c8c\u60f3\u5fc5\u548c\u4ed6\u90a3\u5b69\u5b50\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u4e00\u4e00\u591a\u4e48\u53ef\u7231\u7684\u5b69\u5b50\uff0c\u5c0f\u65b9\u591a\u4e48\u5e0c\u671b\u81ea\u5df1\u80fd\u591f\u53bb\u62b1\u62b1\u4ed6\u3002 \u3000\u3000\u5982\u679c\u662f\u5728\u4e24\u5e74\u524d\uff0c\u4e0d\u7ba1\u5415\u4e09\u662f\u771f\u662f\u5047\uff0c\u4e5f\u4e0d\u7ba1\u8fd9\u5b69\u5b50\u662f\u771f\u662f\u5047\uff0c\u5c0f\u65b9\u65e9\u5df2\u51b2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u7684\u5c0f\u65b9\u5df2\u7ecf\u4e0d\u662f\u4e24\u5e74\u524d\u7684\u5c0f\u65b9\u4e86\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u5b66\u4f1a\u4e86\u5fcd\u8010\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5fcd\u8010\uff0c\u8981\u51b7\u9759\uff0c\u56e0\u4e3a\u8fd9\u51e0\u4e2a\u8721\u4eba\u4e0d\u4ec5\u662f\u51e0\u4e2a\u8721\u4eba\u800c\u5df2\uff0c\u5176\u4e2d\u5fc5\u5b9a\u8fd8\u9690\u85cf\u7740\u4e00\u4e9b\u53ef\u6015\u7684\u9634\u8c0b\u548c\u79d8\u5bc6\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u4e00\u4e2a\u95ee\u9898\u662f\uff1a \u3000\u3000\u2014\u2014\u8fd9\u4e9b\u8721\u4eba\u7a76\u7adf\u662f\u8c01\u505a\u7684\uff1f\u4e3a\u4ec0\u4e48\u8981\u505a\u8fd9\u4e48\u6837\u51e0\u4e2a\u8721\u4eba\u6446\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u5c0f\u65b9\u5c3d\u91cf\u8ba9\u81ea\u5df1\u51b7\u9759\u9547\u5b9a\u4e0b\u6765\uff0c\u4e8e\u662f\u4ed6\u53c8\u6ce8\u610f\u5230\u51e0\u4ef6\u4e8b\u3002 \u3000\u3000\u9e70\u8bb0\u672c\u6765\u4e5f\u8ddf\u522b\u7684\u5546\u53f7\u4e00\u6837\uff0c\u95e8\u53e3\u4e5f\u805a\u96c6\u7740\u4e00\u4e9b\u6d41\u52a8\u7684\u5c0f\u8d29\u548c\u884c\u4eba\u4e5e\u4e10\uff0c\u518d\u52a0\u4e0a\u5e97\u91cc\u53c8\u6446\u7740\u8fd9\u51e0\u4e2a\u670d\u9970\u9c9c\u660e\u884c\u4e8b\u8be1\u79d8\u7684\u8721\u4eba\uff0c\u672c\u6765\u5e94\u8be5\u80fd\u5438\u5f15\u66f4\u591a\u7684\u4eba\u5728\u95e8\u53e3\u3002 \u3000\u3000\u73b0\u5728\u95e8\u53e3\u65b9\u5706\u51e0\u4e08\u4e4b\u5185\u5374\u8fde\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\uff0c\u6240\u6709\u7684\u4eba\u4e00\u8d70\u5230\u8fd9\u9644\u8fd1\u5c31\u8fdc\u8fdc\u5730\u907f\u5f00\u4e86\uff0c\u4eff\u4f5b\u53ea\u8981\u4e00\u8e0f\u5165\u8fd9\u5757\u4e0d\u7965\u4e4b\u5730\u7acb\u523b\u5c31\u4f1a\u6709\u7978\u4e8b\u964d\u4e34\u3002 \u3000\u3000\u53ef\u662f\u6bcf\u4e2a\u4eba\u90fd\u5728\u8fdc\u8fdc\u5730\u6ce8\u610f\u7740\u8fd9\u5bb6\u5546\u53f7\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4ee5\u4e00\u79cd\u5145\u6ee1\u60ca\u7591\u6050\u60e7\u7684\u773c\u8272\u5077\u5077\u5730\u7aa5\u671b\u7740\u5e97\u91cc\u7684\u8721\u50cf\uff0c\u5c31\u597d\u50cf\u628a\u5b83\u4eec\u5168\u90fd\u5f53\u505a\u6709\u8840\u6709\u8089\u7684\u6d3b\u4eba\u4e00\u6837\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u7528\u5b83\u4eec\u624b\u7684\u8721\u5251\u5272\u65ad\u4eba\u7684\u54bd\u5589\u523a\u7a7f\u4eba\u7684\u5fc3\u810f\uff0c\u53d6\u4eba\u7684\u6027\u547d\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u6084\u6084\u62c9\u4e86\u62c9\u9f50\u5c0f\u71d5\u7684\u8863\u89d2\uff0c\u62c9\u7740\u5979\u5411\u540e\u9000\uff0c\u9000\u5165\u4eba\u7fa4\u3002 \u3000\u3000\u4eba\u7fa4\u53c8\u8fdc\u8fdc\u907f\u5f00\uff0c\u4e0d\u7ba1\u4ed6\u4eec\u8d70\u5230\u54ea\u91cc\uff0c\u4eba\u7fa4\u90fd\u4f1a\u8fdc\u8fdc\u907f\u5f00\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u5ffd\u7136\u95f4\u5c0f\u65b9\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5927\u5bb6\u4e3a\u4ec0\u4e48\u5168\u90fd\u8eb2\u7740\u4f60\uff1f\u201d \u3000\u3000\u5979\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u95ee\u9898\uff1a\u201c\u56e0\u4e3a\u90a3\u5bb6\u5e97\u91cc\u4e5f\u6709\u4e00\u4e2a\u4f60\u7684\u8721\u50cf\u3002\u201d \u3000\u3000\u5979\u7684\u690e\u8bba\u662f\uff1a\u201c\u505a\u8fd9\u4e9b\u8721\u50cf\u7684\u4eba\u65e2\u7136\u80fd\u628a\u4f60\u7684\u50cf\u505a\u5f97\u8fd9\u4e48\u903c\u771f\uff0c\u4e00\u5b9a\u662f\u4e2a\u8ddf\u4f60\u5f88\u719f\u7684\u4eba\u3002\u201d \u3000\u3000\u5979\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u731c\u4e0d\u731c\u5f97\u51fa\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u731c\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u6839\u672c\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4e00\u4e2a\u9762\u76ee\u9edd\u9ed1\uff0c\u7a7f\u7740\u4ef6\u6ce2\u65af\u957f\u888d\uff0c\u5356\u9999\u6599\u7684\u6df7\u79cd\u8001\u4eba\u672c\u6765\u6b63\u5728\u53e6\u4e00\u5bb6\u5546\u53f7\u95e8\u53e3\u515c\u751f\u610f\uff0c\u770b\u89c1\u5c0f\u65b9\u8fc7\u6765\uff0c\u4e5f\u60f3\u8fdc\u8fdc\u5730\u907f\u5f00\u3002 \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u4e00\u628a\u62c9\u4f4f\u4e86\u4ed6\uff0c\u538b\u4f4e\u58f0\u97f3\u8bf4\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\uff0c\u4f60\u8ba4\u4e0d\u8ba4\u5f97\u6211\uff1f\u201d \u3000\u3000\u8001\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u62fc\u547d\u6447\u5934\uff0c\u7528\u534a\u751f\u4e0d\u719f\u7684\u6c49\u8bed\u8bf4\uff1a\u201c\u4e0d\u8ba4\u5f97\uff0c\u5b8c\u5168\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u5c0f\u65b9\u51b7\u7b11\uff1a\u201c\u5c31\u7b97\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e5f\u6ca1\u5173\u7cfb\uff0c\u53ea\u8981\u4f60\u80fd\u542c\u61c2\u6211\u7684\u8bdd\uff0c\u4e0d\u7ba1\u4f60\u8ba4\u4e0d\u8ba4\u5f97\u6211\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u63e1\u7d27\u8001\u4eba\u7684\u81c2\uff1a\u201c\u4f60\u542c\u7740\uff0c\u6211\u6709\u51e0\u53e5\u8bdd\u8981\u95ee\u4f60\uff0c\u4f60\u80af\u8bf4\u6211\u6709\u94f6\u5b50\u7ed9\u4f60\uff0c\u4f60\u4e0d\u80af\u8bf4\uff0c\u6211\u5c31\u634f\u65ad\u4f60\u8fd9\u6761\u624b\u81c2\u3002\u201d \u3000\u3000------------------"}),showOnHomepage:!0},{name:"\u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u8721\u4eba",content:(0,a.jsx)(a.Fragment,{children:"\u4ed6\u7528\u6765\u5bf9\u4ed8\u8fd9\u8001\u4eba\u7684\u4e24\u79cd\u65b9\u6cd5\uff0c\u81ea\u4ece\u8fdc\u53e4\u4ee5\u6765\uff0c\u5c31\u662f\u6700\u6709\u6548\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u8001\u4eba\u7684\u989d\u89d2\u4e0a\u5df2\u7ecf\u75db\u51fa\u4e86\u51b7\u6c57\uff0c\u773c\u775b\u91cc\u5df2\u7ecf\u770b\u5230\u4e86\u94f6\u5149\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5f88\u5c11\u6709\u4eba\u8fd8\u80fd\u95ed\u7740\u5634\u3002 \u3000\u3000\u5c0f\u65b9\u5c06\u8001\u4eba\u62c9\u51fa\u4e86\u4eba\u7fa4\uff0c\u62c9\u5230\u4e00\u4e2a\u6bd4\u8f83\u504f\u50fb\u7684\u89d2\u843d\u91cc\uff0c\u624d\u6c89\u7740\u58f0\u95ee\uff1a\u201c\u9e70\u8bb0\u5546\u53f7\u91cc\u90a3\u4e9b\u8721\u4eba\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u624b\u53ea\u52a0\u4e86\u4e00\u5206\u529b\uff0c\u8001\u4eba\u5c31\u75db\u5f97\u773c\u6cea\u90fd\u51e0\u4e4e\u6d41\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u6211\u771f\u7684\u4e0d\u77e5\u9053\u3002\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u6628\u5929\u65e9\u4e0a\u9e70\u8bb0\u5546\u53f7\u4e00\u5f00\u95e8\uff0c\u90a3\u4e9b\u8721\u4eba\u5c31\u5728\u90a3\u91cc\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u76ef\u7740\u4ed6\uff0c\u76f4\u7b49\u5230\u5224\u65ad\u51fa\u4ed6\u8bf4\u7684\u8bdd\u662f\u771f\u8bdd\u4e4b\u540e\uff0c\u624b\u7684\u529b\u91cf\u624d\u653e\u677e\u3002 \u3000\u3000\u201c\u9e70\u8bb0\u5546\u53f7\u7684\u4f19\u8ba1\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u4ece\u6628\u5929\u65e9\u4e0a\u6211\u5c31\u6ca1\u6709\u770b\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u8fde\u4e00\u4e2a\u90fd\u6ca1\u6709\u770b\u89c1\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e00\u4e2a\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4ece\u6628\u5929\u65e9\u4e0a\u8d77\uff0c\u9e70\u8bb0\u5546\u53f7\u91cc\u5c31\u53ea\u6709\u90a3\u51e0\u4e2a\u8721\u4eba\u5728\u5e97\u91cc\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u8fde\u4e00\u4e2a\u6d3b\u4eba\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u8001\u4eba\u8bf4\u5f97\u5f88\u80af\u5b9a\uff1a\u201c\u7edd\u5bf9\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u9e70\u8bb0\u201d\u7684\u7ec4\u7ec7\u4e25\u5bc6\uff0c\u89c4\u6a21\u5e9e\u5927\uff0c\u9664\u4e86\u90a3\u4e9b\u5b9e\u4e3a\u535c\u9e70\u5c5e\u4e0b\u6218\u58eb\u7684\u4f19\u8ba1\u4e4b\u5916\uff0c\u7ecf\u5e38\u7559\u5b88\u5728\u5e97\u91cc\u771f\u6b63\u505a\u89c4\u77e9\u751f\u610f\u4e70\u5356\u7684\u4eba\uff0c\u81f3\u5c11\u4e5f\u6709\u4e00\u767e\u591a\u4e2a\u3002 \u3000\u3000\u4e00\u767e\u591a\u4e2a\u6709\u8840\u6709\u8089\u7684\u5927\u6d3b\u4eba\uff0c\u5f53\u7136\u4e0d\u53ef\u80fd\u5728\u4e00\u591c\u4e4b\u95f4\u5168\u90e8\u5931\u8e2a\u3002\u5934\u5200\u3002 \u3000\u3000\u7b2c\u56db\u6761\u5927\u6c49\u7528\u7684\u5c45\u7136\u53ea\u4e0d\u8fc7\u662f\u67c4\u5f88\u666e\u901a\u7684\u9752\u94a2\u5251\uff0c\u8eab\u6750\u867d\u7136\u9ad8\u5927\uff0c\u957f\u5f97\u5374\u5f88\u79c0\u6c14\u3002 \u3000\u3000\u7b2c\u4e94\u6761\u5927\u6c49\u7a7a\u7740\u4e00\u53cc\u624b\uff0c\u51e0\u4e4e\u5782\u5230\u819d\u76d6\u4e0a\uff0c\u4e0d\u4f46\u624b\u81c2\u5947\u957f\uff0c\u624b\u638c\u4e5f\u6bd4\u666e\u901a\u4eba\u5927\u4e00\u500d\u3002 \u3000\u3000\u4ed6\u7684\u624b\u867d\u7136\u4e0d\u5e26\u5175\u5203\uff0c\u8170\u5e26\u4e0a\u5374\u6302\u6ee1\u4e86\u96f6\u4ef6\uff0c\u96f6\u96f6\u788e\u788e\u7684\u4e5f\u770b\u4e0d\u51fa\u7a76\u7adf\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u7a76\u7adf\u6709\u591a\u5c11\u79cd\uff1f\u8116\u5b50\u4e0a\u8fd8\u6302\u7740\u4e00\u5708\u957f\u7ef3\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u6d3b\u52a8\u7684\u6742\u8d27\u67b6\u5b50\u3002 \u3000\u3000\u7b2c\u4e94\u6761\u5927\u6c49\u7528\u4e0d\u7740\u5927\u543c\u5927\u53eb\uff0c\u4e5f\u7528\u4e0d\u7740\u51fa\u624b\uff0c\u5c31\u8fd9\u4e48\u6837\u5f80\u90a3\u91cc\u4e00\u7ad9\uff0c\u67b6\u52bf\u5df2\u7ecf\u591f\u552c\u4eba\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u4eae\u76f8\uff0c\u522b\u7684\u4eba\u7acb\u523b\u5b89\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e94\u4e2a\u4eba\u5f7c\u6b64\u671b\u4e86\u4e00\u773c\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u7765\u7768\u81ea\u96c4\uff0c\u6311\u621f\u63d0\u6775\u4f69\u5200\u7684\u62db\u547c\u7b2c\u4e00\u6761\u5927\u6c49\u3002 \u3000\u3000\u201c\u8001\u5927\uff0c\u5c31\u662f\u8fd9\u51e0\u4e2a\u8721\u4eba\u5728\u6363\u9b3c\uff0c\u9752\u8c82\u5cad\u7684\u5144\u5f1f\u5c31\u662f\u6b7b\u5728\u4ed6\u4eec\u624b\u4e0a\u7684\u3002\u201d \u3000\u3000\u201c\u8721\u4eba\u4e5f\u4f1a\u6740\u4eba\uff1f\u201d\u8001\u5927\u51b7\u7b11\uff1a\u201c\u8fd9\u5012\u771f\u4ed6\u5988\u7684\u6d3b\u89c1\u9b3c\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u4ed6\u4eec\u662f\u4ec0\u4e48\u53d8\u7684\uff0c\u54b1\u4eec\u4e0d\u5982\u5148\u628a\u4ed6\u4eec\u6bc1\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u4f69\u5251\u7684\u5927\u6c49\u6837\u5b50\u867d\u7136\u957f\u5f97\u6700\u79c0\u6c14\uff0c\u52a8\u4f5c\u5374\u6700\u5feb\uff0c\u4e00\u53cd\u624b\u62d4\u51fa\u4e86\u9752\u94a2\u5251\uff0c\u5c31\u51c6\u5907\u52a8\u4e86\u3002 \u3000\u3000\u7528\u65a7\u5934\u7684\u5927\u6c49\u5374\u62e6\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5df2\u7ecf\u6765\u4e86\uff0c\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b49\u7740\u770b\u6211\u7684\uff01\u201d \u3000\u3000\u4f69\u5251\u7684\u5927\u6c49\u6ca1\u6709\u4e89\u5148\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u8001\u5927\u4e5f\u540c\u610f\u201c\u597d\uff0c\u54b1\u5c31\u5148\u770b\u8001\u4e8c\u7684\u3002\u201d \u3000\u3000\u4e0d\u4f46\u4ed6\u4eec\u5728\u770b\uff0c\u522b\u7684\u4eba\u4e5f\u5728\u770b\uff0c\u7b49\u7740\u770b\u4ed6\u4eec\u8001\u4e8c\u51fa\u624b\u3002 \u3000\u3000\u8001\u4e8c\u7684\u52a8\u4f5c\u5e76\u4e0d\u5feb\uff0c\u5148\u6162\u541e\u541e\u5730\u5f80\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u4ece\u8170\u5e26\u4e0a\u62bd\u51fa\u4e86\u4e00\u628a\u8fde\u67c4\u53ea\u6709\u4e00\u5c3a\u591a\u957f\u7684\u65a7\u5934\uff0c\u7528\u5927\u62c7\u6307\u8210\u4e86\u8210\u820c\u5934\u4e0a\u7684\u53e3\u6c34\uff0c\u5f80\u65a7\u950b\u4e0a\u62b9\u4e86\u62b9\uff0c\u2026\u2026\u7a81\u7136\u4e00\u5f2f\u8eab\u3001\u4e00\u6325\u624b\u3002 \u3000\u3000\u53ea\u542c\u201c\u5427\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u6025\u98ce\u7834\u7a7a\uff0c\u4ed6\u624b\u91cc\u7684\u65a7\u5934\u5df2\u7ecf\u8131\u624b\u98de\u51fa\uff0c\u5f80\u73ed\u5bdf\u5df4\u90a3\u7684\u5934\u4e0a\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u79cd\u6c5f\u6e56\u4e0a\u5f88\u5c11\u6709\u4eba\u7ec3\u7684\u529f\u592b\uff0c\u4e00\u65a7\u5934\u7684\u529b\u91cf\u8fdc\u6bd4\u4efb\u4f55\u4e00\u79cd\u6697\u5668\u90fd\u5927\u5f97\u591a\u3002 \u3000\u3000\u529b\u91cf\u5927\uff0c\u901f\u5ea6\u5f53\u7136\u4e5f\u5feb\uff0c\u5c31\u7b97\u662f\u72ee\u864e\u731b\u517d\uff0c\u4e5f\u7981\u4e0d\u8d77\u8fd9\u4e48\u6837\u4e00\u65a7\u5934\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u8fd9\u4e2a\u73ed\u5bdf\u5df4\u90a3\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8721\u4eba\uff0c\u6839\u672c\u4e0d\u4f1a\u52a8\uff0c\u53ef\u662f\u8fd9\u4e00\u65a7\u5934\u4e5f\u6ca1\u6709\u5288\u5728\u4ed6\u5934\u4e0a\u3002 \u3000\u3000\u8fd9\u79cd\u529f\u592b\u5c31\u50cf\u662f\u98de\u5200\u4e00\u6837\uff0c\u6700\u96be\u7ec3\u7684\u4e00\u70b9\u5c31\u662f\u51c6\u5934\u3002\u8981\u80fd\u5728\u4e09\u5341\u6b65\u4ee5\u5916\u4ee5\u4e00\u65a7\u5934\u5288\u5f00\u4e00\u4e2a\u6838\u6843\uff0c\u529f\u592b\u624d\u7b97\u7ec3\u6210\u4e86\u3002 \u3000\u3000\u8fd9\u6761\u5927\u6c49\u65e0\u7591\u5df2\u7ecf\u628a\u529f\u592b\u7ec3\u5230\u4e86\u8fd9\u4e00\u6b65\uff0c\u51fa\u624b\u4e0d\u4f46\u5feb\uff0c\u800c\u4e14\u51c6\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u8fd9\u4e00\u65a7\u5934\u5288\u51fa\u53bb\uff0c\u51c6\u53ef\u4ee5\u628a\u90a3\u8721\u4eba\u8111\u888b\u4e00\u4e0b\u5b50\u5288\u6210\u4e24\u534a\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e00\u65a7\u5934\u5374\u504f\u504f\u5288\u7a7a\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u9053\u662f\u56e0\u4e3a\u90a3\u6761\u5927\u6c49\u624b\u4e0a\u7684\u529b\u91cf\u7528\u5f97\u4e0d\u591f\uff0c\u8fd8\u662f\u56e0\u4e3a\u522b\u7684\u53e4\u602a\u7f18\u6545\uff0c\u8fd9\u628a\u53bb\u52bf\u5982\u98ce\u7684\u98de\u65a7\u521a\u5288\u5230\u201c\u73ed\u5bdf\u5df4\u90a3\u201d\u5934\u4e0a\uff0c\u5c31\u5ffd\u7136\u5931\u53bb\u4e86\u51c6\u5934\uff0c\u5ffd\u7136\u53d8\u5f97\u50cf\u662f\u4e2a\u65ad\u4e86\u7ebf\u7684\u98ce\u7b5d\u4e00\u6837\uff0c\u8f7b\u98d8\u98d8\u5730\u5f80\u65c1\u8fb9\u98de\u4e86\u51fa\u53bb\uff0c\u201c\u593a\u201d\u7684\u4e00\u58f0\uff0c\u9489\u5728\u67dc\u53f0\u4e0a\u3002 \u3000\u3000\u8001\u4e8c\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5144\u5f1f\u4eec\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u8001\u5927\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u6545\u610f\u7834\u53e3\u5927\u9a82\u3002 \u3000\u3000\u201c\u76f4\u5a18\u8d3c\uff1a\u53eb\u4f60\u591a\u5403\u4e24\u65a4\u8089\uff0c\u624b\u4e0a\u624d\u6709\u529b\u6c14\uff0c\u4f60\u4ed6\u5988\u7684\u504f\u8981\u53bb\u73a9\u59d1\u5a18\uff0c\u73a9\u5f97\u624b\u53d1\u8f6f\uff0c\u771f\u4ed6\u5988\u7684\u4e22\u4eba\u73b0\u773c\u3002\u201d \u3000\u3000\u8001\u4e8c\u7684\u8138\u8272\u53d1\u9752\uff0c\u4e0d\u7b49\u4ed6\u4eec\u7684\u8001\u5927\u9a82\u5b8c\uff0c\u5df2\u7ecf\u53c8\u662f\u4e00\u65a7\u5934\u5288\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u4ed6\u7684\u51fa\u624b\u66f4\u5feb\u66f4\u51c6\uff0c\u7528\u7684\u529b\u91cf\u4e5f\u66f4\u5927\u3002 \u3000\u3000\u65a7\u5934\u7834\u7a7a\u98de\u51fa\uff0c\u6025\u98ce\u547c\u5578\u800c\u8fc7\uff0c\u5ffd\u7136\u95ee\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u65a7\u5934\u7684\u6728\u67c4\u5ffd\u7136\u51ed\u7a7a\u65ad\u6210\u4e86\u4e24\u622a\uff0c\u65a7\u5934\u5931\u53bb\u5e73\u8861\u4e4b\u529b\uff0c\u4e00\u4e0b\u5b50\u5c31\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8001\u5927\u8fd8\u5728\u9a82\uff0c\u9a82\u5f97\u66f4\u51f6\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u773c\u775b\u5374\u4e00\u76f4\u5728\u56db\u4e0b\u641c\u7d22\uff0c\u56e0\u4e3a\u4ed6\u8ddf\u4ed6\u7684\u5144\u5f1f\u4e00\u6837\u660e\u767d\u4e24\u4ef6\u4e8b\u3002 \u3000\u3000\u2014\u2014\u4e00\u628a\u4ee5\u4e0a\u597d\u6a61\u6728\u4e3a\u67c4\u7684\u65a7\u5934\uff0c\u662f\u7edd\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u4ece\u4e2d\u6298\u65ad\u7684\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u7684\u8001\u4e8c\u624b\u4e0a\u6709\u4ec0\u4e48\u6837\u7684\u529b\u91cf\uff0c\u4ed6\u4eec\u5fc3\u91cc\u5f53\u7136\u66f4\u6e05\u695a\uff0c\u5982\u679c\u8bf4\u4ed6\u4f1a\u5c06\u4e00\u628a\u65a7\u5934\u5288\u6b6a\uff0c\u90a3\u7b80\u76f4\u5c31\u597d\u50cf\u8bf4\u592a\u9633\u662f\u4ece\u897f\u8fb9\u51fa\u6765\u7684\u4e00\u6837\u8352\u8c2c\u3002 \u3000\u3000\u65a7\u67c4\u65e2\u7136\u4e0d\u53ef\u80fd\u65e0\u6545\u6298\u65ad\uff0c\u65a7\u5934\u4e5f\u7edd\u4e0d\u53ef\u80fd\u5288\u6b6a\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f \u3000\u3000\u552f\u4e00\u5408\u7406\u7684\u89e3\u91ca\u662f\u2014\u2014\u6709\u4e00\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u6709\u4e00\u4e2a\u4eba\uff0c\u5728\u4e00\u4e2a\u5f88\u4e0d\u5bb9\u6613\u88ab\u4eba\u770b\u5230\u7684\u89d2\u843d\u91cc\uff0c\u4ee5\u4e00\u79cd\u4e0d\u5bb9\u6613\u88ab\u4eba\u770b\u89c1\u7684\u624b\u6cd5\uff0c\u53d1\u51fa\u4e00\u79cd\u5f88\u4e0d\u5bb9\u6613\u88ab\u4eba\u770b\u51fa\u6765\u7684\u6697\u5668\uff0c\u6253\u6b6a\u4e86\u4ed6\u4eec\u8001\u4e8c\u7b2c\u4e00\u6b21\u5288\u51fa\u7684\u65a7\u5934\uff0c\u6253\u65ad\u4e86\u4ed6\u7b2c\u4e8c\u6b21\u5288\u51fa\u7684\u65a7\u67c4\uff1b \u3000\u3000\u8fd9\u4e2a\u4eba\u65e0\u7591\u662f\u9ad8\u624b\uff0c\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5f88\u53ef\u80fd\u5c31\u662f\u628a\u8721\u50cf\u6446\u5728\u8fd9\u91cc\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u4e94\u5144\u5f1f\u867d\u7136\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u5374\u5b8c\u5168\u4e0d\u52a8\u58f0\u8272\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u6ca1\u6709\u770b\u51fa\u6765\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u6697\u5668\uff1f \u3000\u3000\u4ed6\u4eec\u53ea\u770b\u89c1\u4e86\u5c0f\u65b9\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u5728\u627e\uff0c\u627e\u8fd9\u4e2a\u6253\u6b6a\u65a7\u5934\u6298\u65ad\u65a7\u67c4\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u627e\u5230\u8fd9\u4e2a\u4eba\uff0c\u522b\u4eba\u5df2\u7ecf\u627e\u4e0a\u4ed6\u4e86\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u627e\u4e0a\u6765\u7684\u5c31\u662f\u90a3\u8eab\u6750\u6700\u9ad8\u5927\uff0c\u957f\u5f97\u9ad8\u79c0\u6c14\u7684\u4f69\u5251\u5c11\u5e74\u3002 \u3000\u3000\u4ed6\u76ef\u7740\u5c0f\u65b9\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u597d\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u597d\u50cf\u89c1\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u597d\u50cf\u521a\u624d\u9047\u89c1\u8fc7\u4f60\uff0c\u5728\u53e6\u5916\u4e00\u4e2a\u5730\u65b9\u89c1\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u54e6\u3002\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u5728\u54ea\u91cc\u89c1\u8fc7\u6211\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u90a3\u5bb6\u5546\u53f7\u91cc\u3002\u201d\u4f69\u5251\u7684\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u597d\u50cf\u8ddf\u90a3\u4e2a\u957f\u5f97\u5b8c\u5168\u4e00\u4e00\u6a21\u4e00\u3002\u6837\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\uff0c\u6478\u7740\u81ea\u5df1\u7684\u8138\u7b11\u4e86\u3002 \u3000\u3000\u201c\u6211\u81ea\u5df1\u4e5f\u89c9\u5f97\u6709\u70b9\u50cf\u3002\u201d\u4ed6\u95f4\u8fd9\u5c11\u5e74\uff1a\u201c\u4f60\u8d35\u59d3\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u8001\u56db\u3002\u201d \u3000\u3000\u201c\u8001\u56db\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\uff1b\u201c\u8c01\u7684\u8001\u56db\uff1f\u201d \u3000\u3000\u201c\u662f\u6211\u4eec\u8001\u5927\u7684\u8001\u56db\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u7684\u8001\u5927\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u4ece\u6765\u90fd\u4e0d\u4f1a\u6740\u4eba\u7684\u4eba\u3002\u201d\u8001\u56db\u8bf4\uff1a\u201c\u4ed6\u53ea\u4f1a\u6253\u4eba\uff0c\u5e38\u5e38\u4e00\u4e0b\u5b50\u5c31\u628a\u522b\u4eba\u6253\u6210\u8089\u6ce5\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u4e00\u5b9a\u5f88\u7d2f\u3002\u201d \u3000\u3000\u201c\u5f88\u7d2f\uff1f\u201d \u3000\u3000\u201c\u65e0\u8bba\u8c01\u8981\u628a\u522b\u4eba\u6253\u6210\u8089\u6ce5\u90fd\u662f\u4ef6\u5f88\u8d39\u529b\u6c14\u7684\u4e8b\uff0c\u4ed6\u600e\u4e48\u4f1a\u4e0d\u7d2f\uff1f\u201d \u3000\u3000\u8001\u56db\u51b7\u7b11\uff0c\u5ffd\u7136\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u7684\u6697\u5668\u5462\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6697\u5668\u53f6\u5c0f\u65b9\u53cd\u95ee\u3002 \u3000\u3000\u201c\u6253\u65a7\u5934\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u8fd9\u79cd\u6697\u5668\u3002\u201d\u5c0f\u65b9\u5728\u7b11\uff1a\u201c\u5982\u679c\u6211\u6709\u6697\u5668\uff0c\u4e5f\u4e0d\u6253\u65a7\u5934\u3002\u201d \u3000\u3000\u201c\u4e0d\u6253\u65a7\u5934\u6253\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6253\u5165\u3002\u201d\u5c0f\u65b9\u597d\u50cf\u7b11\u5f97\u5f88\u6109\u5feb\uff1a\u201c\u6253\u4eba\u7edd\u5bf9\u6bd4\u6253\u65a7\u5934\u597d\u73a9\u5f97\u591a\u3002\u201d \u3000\u3000\u8001\u56db\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4e2a\u4eba\u90fd\u5728\u7b11\uff0c\u53ef\u662f\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\u6765\u4ed6\u4eec\u5e76\u4e0d\u662f\u771f\u7684\u89c9\u5f97\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u4ed6\u4eec\u7b11\u7684\u65f6\u5019\uff0c\u773c\u775b\u90fd\u5728\u76ef\u7740\u5bf9\u65b9\u7684\u624b\u3002 \u3000\u3000\u63e1\u5251\u7684\u90a3\u53ea\u624b\u3002 \u3000\u3000\u8001\u56db\u7b11\u5f97\u6bd4\u5c0f\u65b9\u8fdc\u4e0d\u50cf\u662f\u5728\u7b11\uff0c\u4ed6\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u4e5f\u4f1a\u4f7f\u5251\uff1f\u201d \u3000\u3000\u201c\u4f1a\u4e00\u70b9\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u4e00\u70b9\u70b9\u3002\u201d \u3000\u3000\u201c\u90a3\u597d\u6781\u4e86\u3002\u201d\u8001\u56db\u8bf4\uff1a\u201c\u78b0\u5de7\u6211\u4e5f\u4f1a\u4f7f\u5251\uff0c\u4e5f\u53ea\u4f1a\u4e00\u70b9\u70b9\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u6bcf\u4e2a\u4eba\u90fd\u660e\u767d\u4ed6\u7684\u610f\u601d\u4e86\u3002 \u3000\u3000\u8001\u56db\u5df2\u7ecf\u8ba4\u5b9a\u4e86\u5c0f\u65b9\u548c\u9e70\u8bb0\u5546\u53f7\u91cc\u8fd9\u51e0\u4e2a\u8721\u4eba\u6709\u5173\u7cfb\uff0c\u5c31\u7b97\u4ed6\u4e0d\u662f\u6253\u843d\u65a7\u5934\u7684\u9ad8\u624b\uff0c\u4e5f\u4e00\u5b9a\u53ef\u4ee5\u4ece\u4ed6\u8eab\u4e0a\u903c\u51fa\u90a3\u4f4d\u9ad8\u624b\u6765\u3002 \u3000\u3000\u5c0f\u65b9\u5e76\u6ca1\u6709\u5426\u8ba4\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5426\u8ba4\u4e5f\u6ca1\u6709\u7528\u3002 \u3000\u3000\u8001\u56db\u7684\u638c\u4e2d\u6709\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u6709\u3002 \u3000\u3000\u8001\u56db\u6253\u7b97\u8981\u7528\u4ed6\u7684\u5251\u6765\u903c\u5c0f\u65b9\u8bf4\u51fa\u8fd9\u79d8\u5bc6\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u6ca1\u6709\u62d2\u7edd\u9003\u907f\u3002 \u3000\u3000\u8001\u56db\u8eab\u9ad8\u516b\u5c3a\u4e00\u5bf8\uff0c\u624b\u811a\u957f\u5927\uff0c\u52a8\u4f5c\u7075\u6d3b\uff0c\u5168\u8eab\u7684\u808c\u8089\u90fd\u5145\u6ee1\u5f39\u6027\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u6765\u4e0d\u4f46\u82cd\u767d\u6a35\u7cb9\uff0c\u800c\u4e14\u663e\u5f97\u5f88\u865a\u5f31\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5f3a\u5f31\u4e4b\u52bf\u770b\u6765\u5df2\u7ecf\u5f88\u660e\u663e\uff0c\u6bcf\u4e2a\u4eba\u90fd\u8ba4\u5b9a\u5c0f\u65b9\u5fc5\u8d25\u65e0\u7591\u3002 \u3000\u3000\u53ea\u6709\u9f50\u5c0f\u71d5\u662f\u4f8b\u5916\u3002 \u3000\u3000\u53ea\u6709\u5979\u7b97\u51c6\u4e86\u8001\u56db\u7edd\u5bf9\u907f\u4e0d\u5f00\u5c0f\u65b9\u4e09\u62db\u3002 \u3000\u3000\u4e00\u58f0\u8f7b\u53f1\uff0c\u5251\u5149\u95ea\u52a8\uff0c\u8f6c\u77ac\u95f4\u8001\u56db\u5c31\u5df2\u653b\u51fa\u516b\u5251\uff0c\u62db\u4e2d\u5957\u62db\u7ef5\u5ef6\u4e0d\u7edd\u7684\u8fde\u73af\u516b\u5251\uff0c\u88ab\u8fd9\u4e48\u6837\u4e00\u6761\u5927\u6c49\u4f7f\u51fa\u6765\u5f53\u7136\u66f4\u5177\u5a01\u529b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fde\u5c0f\u65b9\u7684\u8863\u89d2\u90fd\u6ca1\u6709\u78b0\u5230\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u523a\u51fa\u4e00\u5251\u3002 \u3000\u3000\u4ed6\u8f6c\u8eab\u3001\u62d4\u5251\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u5230\u4e86\u8001\u56db\u7684\u54bd\u5589\u3002 \u3000\u3000\u8001\u56db\u7528\u5c3d\u5168\u529b\u624d\u907f\u5f00\u8fd9\u4e00\u5251\u3002 \u3000\u3000\u4ed6\u51cc\u7a7a\u540e\u8dc3\uff0c\u51cc\u7a7a\u7ffb\u8eab\uff0c\u867d\u7136\u907f\u5f00\u4e86\u8fd9\u4e00\u5251\uff0c\u5374\u5df2\u65e0\u6cd5\u987e\u53ca\u9000\u8def\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u843d\u4e0b\u65f6\uff0c\u5df2\u7ecf\u5230\u4e86\u9e70\u8bb0\u5546\u53f7\u91cc\u3002 \u3000\u3000\u9e70\u8bb0\u5546\u53f7\u91cc\u53ea\u6709\u51e0\u4e2a\u6ca1\u6709\u751f\u547d\u6ca1\u6709\u77e5\u89c9\u8fde\u52a8\u90fd\u4e0d\u4f1a\u52a8\u7684\u8721\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u8eab\u5b50\u4e00\u843d\u4e0b\u65f6\uff0c\u773c\u775b\u91cc\u5c31\u9732\u51fa\u79cd\u60ca\u8bb6\u6050\u60e7\u4e4b\u6781\u7684\u8868\u60c5\uff0c\u8eab\u4e0a\u6bcf\u4e00\u5757\u808c\u8089\u90fd\u56e0\u6050\u60e7\u800c\u6536\u7f29\uff0c\u5ffd\u7136\u5c31\u5931\u53bb\u4e86\u5f39\u6027\uff0c\u53d8\u5f97\u75c9\u631b\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u7684\u5144\u5f1f\u4eec\u540c\u65f6\u5927\u559d\uff1a\u201c\u8001\u56db\uff0c\u5feb\u9000\uff01\u9000\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u81ea\u5df1\u5f53\u7136\u4e5f\u60f3\u9000\u51fa\u6765\uff0c\u5374\u5df2\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u4ed6\u6323\u624e\u7740\uff0c\u8fd8\u60f3\u6251\u8fc7\u53bb\uff0c\u7528\u4ed6\u624b\u91cc\u7684\u5251\u53bb\u640f\u6740\u90a3\u51e0\u4e2a\u672c\u6765\u5c31\u6ca1\u6709\u751f\u547d\u7684\u8721\u4eba\u3002 \u3000\u3000\u4f46\u662f\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5168\u8eab\u7684\u5173\u8282\u808c\u8089\u7ec4\u7ec7\u90fd\u5df2\u5931\u53bb\u63a7\u5236\uff0c\u773c\u6cea\u9f3b\u6d95\u5927\u5c0f\u4fbf\u5ffd\u7136\u5168\u90e8\u6d41\u4e86\u51fa\u6765\uff0c\u8eab\u5b50\u4e5f\u5df2\u6e10\u6e10\u7f29\u6210\u4e86\u4e00\u56e2\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u8fd8\u5269\u4e0b\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u7528\u5c3d\u5168\u529b\uff0c\u5c06\u638c\u4e2d\u5251\u8131\u624b\u98de\u63b7\u51fa\u53bb\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\u95f4\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e00\u5251\u523a\u4eba\u4e86\u535c\u9e70\u7684\u80f8\u819b\uff0c\u4ece\u524d\u80f8\u523a\u4eba\uff0c\u540e\u80cc\u7a7f\u51fa\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u535c\u9e70\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8721\u4eba\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u65f6\u8001\u56db\u5df2\u7ecf\u5012\u5728\u5730\u4e0a\uff0c\u5168\u8eab\u90fd\u5df2\u6536\u7f29\u50f5\u786c\uff0c\u4e00\u6761\u516b\u5c3a\u4e00\u5bf8\u7684\u5927\u6c49\uff0c\u7adf\u5728\u8f6c\u77ac\u95f4\u53d8\u5f97\u597d\u50cf\u662f\u4e2a\u5df2\u7ecf\u88ab\u62bd\u5e72\u8840\u8089\u7684\u6807\u672c\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5df2\u7ecf\u770b\u4e0d\u89c1\u4ed6\u8fd9\u4e00\u5251\u63b7\u51fa\u540e\u7684\u7ed3\u679c\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u5144\u5f1f\u8fd8\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u4eec\u8138\u4e0a\u5ffd\u7136\u4e5f\u9732\u51fa\u79cd\u60ca\u8bb6\u6050\u60e7\u4e4b\u6781\u7684\u8868\u60c5\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8fd8\u770b\u5f97\u89c1\u3002 \u3000\u3000\u6bcf\u4e2a\u773c\u775b\u90fd\u8fd8\u770b\u5f97\u89c1\u7684\u4eba\uff0c\u8138\u4e0a\u90fd\u9732\u51fa\u4e86\u8ddf\u4ed6\u4eec\u5b8c\u5168\u4e00\u6837\u7684\u8868\u60c5\uff0c\u751a\u81f3\u8fde\u5c0f\u65b9\u90fd\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u8ddf\u4ed6\u4eec\u4e00\u6837\uff0c\u770b\u89c1\u4e86\u4e00\u4ef6\u867d\u7136\u4eb2\u773c\u76ee\u7779\u4e5f\u65e0\u6cd5\u76f8\u4fe1\u7684\u602a\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u89c1\u535c\u9e70\u5728\u6d41\u8840\uff01 \u3000\u3000\u8fd9\u4e2a\u535c\u9e70\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6ca1\u6709\u77e5\u89c9\u6ca1\u6709\u751f\u547d\u7684\u8721\u4eba\u800c\u5df2\uff0c\u600e\u4e48\u4f1a\u6d41\u8840\uff1f \u3000\u3000\u201c\u535c\u9e70\u201d\u7684\u786e\u5728\u6d41\u8840\u3002 \u3000\u3000\u4e00\u6ef4\u6ef4\u9c9c\u8840\u6cbf\u7740\u5251\u950b\u6d41\u8fc7\uff0c\u4ece\u5251\u5c16\u4e0a\u6ef4\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u52a8\uff0c\u4e5f\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6bd5\u7adf\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8721\u4eba\u800c\u5df2\uff0c\u2014\u2014\u81f3\u5c11\u4ece\u5916\u8868\u770b\u6765\u7edd\u5bf9\u662f\u4e2a\u8721\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4ece\u53e6\u4e00\u65b9\u9762\u770b\u53bb\uff0c\u65e0\u8bba\u8c01\u90fd\u77e5\u9053\u4e00\u4e2a\u8721\u4eba\u662f\u4e0d\u4f1a\u6d41\u8840\u7684\u3002 \u3000\u3000\u7edd\u5bf9\u4e0d\u4f1a\u3002 \u3000\u3000\u2014\u2014\u90a3\u4e48\u8840\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f \u3000\u3000\u2014\u2014\u96be\u9053\u8fd9\u4e2a\u8721\u4eba\u53ea\u6709\u4ece\u5916\u8868\u770b\u53bb\u624d\u662f\u8721\u4eba\uff0c\u5176\u5b9e\u5374\u4e0d\u662f\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u8fd9\u4e2a\u8721\u4eba\u5176\u5b9e\u5e76\u4e0d\u662f\u8721\u4eba\uff0c\u4e3a\u4ec0\u4e48\u770b\u8fc7\u53bb\u53c8\u504f\u504f\u662f\u4e2a\u8721\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u5f88\u8352\u8c2c\u7684\u95ee\u9898\uff0c\u4e5f\u662f\u79cd\u5f88\u8352\u8c2c\u7684\u60f3\u6cd5\uff0c\u8352\u8c2c\u800c\u53ef\u6015\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5168\u8eab\u5ffd\u7136\u90fd\u88ab\u51b7\u6c57\u6d78\u900f\uff0c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u5ffd\u7136\u6709\u4e86\u4e2a\u8352\u8c2c\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u60f3\u51b2\u8fdb\u9e70\u8bb0\u5546\u53f7\u53bb\u627e\u8fd9\u95ee\u9898\u7684\u7b54\u6848\u3002 \u3000\u3000\u4ed6\u53ea\u60f3\u627e\u51fa\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u5374\u5fd8\u4e86\u90a3\u8001\u4eba\u5bf9\u4ed6\u8bf4\u8fc7\u7684\u6d3b\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u4e00\u8d70\u8fdb\u9e70\u8bb0\u7684\u5927\u95e8\u5c31\u5fc5\u6b7b\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u4eba\u90fd\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u542c\u8d77\u6765\u5f88\u8352\u8c2c\uff0c\u5f88\u5c11\u6709\u4eba\u4f1a\u76f8\u4fe1\uff0c\u53ef\u662f\u4eb2\u773c\u770b\u89c1\u8001\u56db\u66b4\u6bd9\u540e\uff0c\u8fd8\u6709\u8c01\u80fd\u4e0d\u4fe1\uff0c\u8c01\u6562\u4e0d\u4fe1\uff1f \u3000\u3000\u8001\u56db\u4e34\u6b7b\u524d\u773c\u795e\u4e2d\u90a3\u79cd\u6050\u60e7\u4e4b\u6781\u7684\u8868\u60c5\uff0c\u66f4\u4ee4\u4eba\u96be\u4ee5\u5fd8\u8bb0\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u5fd8\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ec0\u4e48\u4e8b\u4ed6\u5168\u90fd\u5fd8\u4e86\uff0c\u6240\u6709\u90a3\u4e9b\u4ee4\u4eba\u60b2\u75db\u4f24\u611f\u6124\u6012\u6050\u60e7\u7684\u4e8b\uff0c\u90fd\u5df2\u4e0d\u80fd\u5f71\u54cd\u4ed6\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5173\u5fc3\u7684\u53ea\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u535c\u9e70\uff01 \u3000\u3000\u5bc2\u5bde\u5bd2\u51b7\u6f2b\u957f\u7684\u5927\u6f20\u4e4b\u591c\uff0c\u6bd4\u5bd2\u98ce\u66f4\u6d53\u70c8\u7684\u9152\uff0c\u6bd4\u9152\u66f4\u6d53\u70c8\u7684\u53cb\u60c5\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u4ee4\u4eba\u6c38\u96be\u5fd8\u6000\u7684\u3002 \u3000\u3000\u2014\u2014\u513f\u9700\u6210\u540d\uff0c \u3000\u3000\u9152\u9700\u9189\uff0c \u3000\u3000\u9152\u540e\u503e\u8bc9\uff0c \u3000\u3000\u5410\u5fc3\u8a00\u3002 \u3000\u3000\u535c\u9e70\uff0c\u4f60\u7a76\u7adf\u662f\u6b7b\u662f\u6d3b\uff1f\u4f60\u7a76\u7adf\u5728\u54ea\u91cc\uff1f \u3000\u3000\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u6d41\u8840\uff1f \u3000\u3000\u5c0f\u65b9\u4e0d\u662f\u82f1\u96c4\u3002 \u3000\u3000\u5f88\u5c11\u4f1a\u6709\u4eba\u628a\u4ed6\u5f53\u4f5c\u82f1\u96c4\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u60f3\u505a\u82f1\u96c4\u3002 \u3000\u3000\u4ed6\u53ea\u60f3\u505a\u4e00\u4e2a\u5e73\u5e73\u51e1\u51e1\u7684\u4eba\uff0c\u505a\u5e73\u5e73\u51e1\u51e1\u7684\u4e8b\uff0c\u8fc7\u5e73\u5e73\u51e1\u51e1\u7684\u65e5\u5b50\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6709\u4e00\u80a1\u51b2\u52a8\u3002 \u3000\u3000\u6bcf\u5f53\u4ed6\u770b\u89c1\u4e00\u4e9b\u4e0d\u516c\u5e73\u7684\u4e8b\uff0c\u770b\u89c1\u4e00\u4e9b\u5bf9\u4eba\u4e0d\u516c\u5e73\u7684\u4eba\uff0c\u4ed6\u5c31\u4f1a\u51b2\u52a8\uff0c\u5c31\u4f1a\u4e0d\u987e\u4e00\u5207\uff0c\u53bb\u8ba9\u90a3\u4e9b\u4e8b\u505a\u5f97\u516c\u5e73\u4e00\u70b9\uff0c\u53bb\u8ba9\u90a3\u4e9b\u4eba\u53d7\u5230\u5408\u7406\u7684\u5236\u88c1\u3002 \u3000\u3000\u5c0f\u65b9\u8fd8\u6709\u4e00\u80a1\u52b2\uff0c\u4e00\u80a1\u6c38\u8fdc\u4e0d\u80af\u5c48\u670d\u7684\u52b2\u3002 \u3000\u3000\u5982\u679c\u522b\u4eba\u4e0d\u903c\u4ed6\uff0c\u4ed6\u7edd\u5bf9\u662f\u4e2a\u5f88\u5e73\u548c\u7684\u4eba\uff0c\u4e0d\u60f3\u8ddf\u522b\u4eba\u53bb\u4e89\uff0c\u4e5f\u4e0d\u60f3\u4e3a\u4efb\u4f55\u4e8b\u53bb\u4e89\u3002 \u3000\u3000\u5982\u679c\u6709\u4eba\u903c\u4ed6\uff0c\u4ed6\u8fd9\u80a1\u52b2\u5c31\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8fd9\u80a1\u52b2\u6765\u7684\u65f6\u5019\uff0c\u4e0d\u7ba1\u522b\u4eba\u662f\u7528\u5229\u8bf1\u8fd8\u662f\u7528\u5a01\u80c1\uff0c\u4ed6\u90fd\u4e0d\u5728\u4e4e\uff0c\u5c31\u7b97\u7528\u5200\u67b6\u5728\u4ed6\u7684\u8116\u5b50\u4e0a\uff0c\u4ed6\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u5c0f\u65b9\u6700\u8fd1\u5df2\u51b7\u9759\u591a\u4e86\uff0c\u6bcf\u4e2a\u8ba4\u5f97\u4ed6\u7684\u4eba\u90fd\u8ba4\u4e3a\u4ed6\u5df2\u7ecf\u51b7\u9759\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u8ba4\u4e3a\u81ea\u5df1\u51b7\u9759\u591a\u4e86\uff0c\u5df2\u7ecf\u5b66\u4f1a\u4e86\u63a7\u5236\u81ea\u5df1\u3002 \u3000\u3000\u6709\u5f88\u591a\u6b21\u4ed6\u90fd\u66ff\u81ea\u5df1\u8bc1\u5b9e\u4e86\u8fd9\u4e00\u70b9\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5ffd\u7136\u53c8\u51b2\u52a8\u8d77\u6765\u4e86\u3002\u628a\u81ea\u5df1\u4ee5\u524d\u66fe\u7ecf\u518d\u4e09\u544a\u8beb\u8fc7\u81ea\u5df1\u7684\u8bdd\u5168\u90fd\u5fd8\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5982\u679c\u662f\u4e3a\u4e86\u4ed6\u81ea\u5df1\u7684\u4e8b\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u53ef\u662f\u4e3a\u4e86\u4ed6\u7684\u670b\u53cb\uff0c\u4e3a\u4e86\u535c\u9e70\uff0c\u4ed6\u968f\u65f6\u90fd\u53ef\u4ee5\u653e\u5f00\u4e00\u5207\u3002\u968f\u65f6\u90fd\u65f6\u4ee5\u628a\u81ea\u5df1\u7684\u8111\u888b\u5f80\u5899\u4e0a\u649e\u8fc7\u53bb\uff0c\u5c31\u662f\u5899\u4e0a\u6709\u4e09\u767e\u516b\u5341\u6839\u9489\u5b50\uff0c\u4ed6\u4e5f\u4f1a\u649e\u8fc7\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5929\u751f\u5c31\u662f\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u5929\u751f\u5c31\u662f\u8fd9\u79cd\u813e\u6c14\uff0c\u4f60\u8bf4\u8fd9\u79cd\u813e\u6c14\u8981\u547d\u4e0d\u8981\u547d\uff1f\u2014\u2014\u8721\u4eba\u600e\u4e48\u4f1a\u6d41\u8840\uff1f \u3000\u3000\u5408\u7406\u7684\u7b54\u6848\u53ea\u6709\u4e00\u4e2a\u3002 \u3000\u3000\u2014\u2014\u8721\u4eba\u91cc\u9762\u662f\u6709\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u4f1a\u6d41\u8840\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u53ea\u6709\u6d3b\u4eba\u624d\u4f1a\u6d41\u8840\uff1f \u3000\u3000\u5c0f\u65b9\u5f88\u5c0f\u5f88\u5c0f\u7684\u65f6\u5019\u5c31\u542c\u5230\u8fc7\u4e00\u4e2a\u6545\u4e8b\uff0c\u4e00\u4e2a\u53ef\u6015\u6781\u4e86\u7684\u6545\u4e8b\uff1a \u3000\u3000\u2014\u2014\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u5728\u4e00\u4e2a\u795e\u79d8\u9065\u8fdc\u7684\u56fd\u5ea6\u91cc\uff0c\u6709\u4e00\u4f4d\u4e13\u505a\u8721\u4eba\u7684\u5927\u5e08\uff0c\u4ed6\u505a\u51fa\u7684\u8721\u4eba\u6bcf\u4e00\u4e2a\u90fd\u50cf\u6d3b\u7684\u4e00\u6837\uff0c\u5c24\u5176\u662f\u4ed6\u7528\u8721\u505a\u51fa\u6765\u7684\u5973\u5b69\u5b50\uff0c\u6bcf\u4e00\u4e2a\u90fd\u8ba9\u7537\u4eba\u7740\u8ff7\u3002 \u3000\u3000\u2014\u2014\u5c31\u5728\u8fd9\u6bb5\u65f6\u5019\uff0c\u5728\u90a3\u4e2a\u56fd\u5ea6\u4e2d\u4e00\u4e9b\u504f\u50fb\u7684\u4e61\u6751\u91cc\uff0c\u65f6\u5e38\u4f1a\u6709\u4e00\u4e9b\u5973\u5b69\u5b50\u795e\u79d8\u5931\u8e2a\uff0c\u8fde\u6700\u6709\u7ecf\u9a8c\u7684\u6355\u5feb\u4e5f\u67e5\u4e0d\u51fa\u5979\u4eec\u7684\u4e0b\u843d\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4ef6\u5947\u6848\u662f\u88ab\u4e00\u4e2a\u60b2\u4f24\u7684\u6bcd\u4eb2\u5728\u65e0\u610f\u95f4\u63ed\u7a7f\u7684\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4f4d\u6bcd\u4eb2\u56e0\u4e3a\u5973\u513f\u7684\u5931\u8e2a\u60b2\u4f24\u5f97\u51e0\u4e4e\u53d1\u4e86\u75af\uff0c\u4ed6\u7684\u4e08\u592b\u5c31\u5e26\u5979\u5230\u57ce\u91cc\u53bb\u6563\u5fc3\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u5728\u57ce\u91cc\u6709\u4e00\u4f4d\u6709\u94b1\u7684\u4eb2\u621a\uff0c\u521a\u5de7\u8ba4\u5f97\u90a3\u4f4d\u5999\u593a\u5929\u5de5\u7684\u8721\u50cf\u5927\u5e08\uff0c\u5c31\u5e26\u4ed6\u4eec\u53bb\u770b\u90a3\u4e9b\u6d3b\u8272\u751f\u9999\u7684\u8721\u50cf\u3002 \u3000\u3000\u2014\u2014\u90a3\u4f4d\u6bcd\u4eb2\u770b\u89c1\u5176\u4e2d\u4e00\u4e2a\u8721\u50cf\u540e\uff0c\u5ffd\u7136\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u2014\u2014\u56e0\u4e3a\u4ed6\u4eec\u770b\u89c1\u7684\u90a3\u4e2a\u8721\u4eba\uff0c\u5b9e\u5728\u592a\u50cf\u5979\u7684\u5973\u513f\u4e86\uff0c\u5728\u9ec4\u660f\u540e\u6de1\u6de1\u7684\u706f\u5149\u91cc\uff0c\u770b\u6765\u7b80\u76f4\u5c31\u50cf\u5979\u7684\u5973\u513f\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u5979\u9192\u8fc7\u6765\u4e4b\u540e\uff0c\u5c31\u8981\u6c42\u90a3\u4f4d\u5927\u5e08\u5c06\u8fd9\u4e2a\u8721\u50cf\u5356\u7ed9\u5979\uff0c\u4e0d\u7ba1\u591a\u5c11\u94b1\u5979\u90fd\u613f\u610f\u4e70\uff0c\u5c31\u7b97\u8981\u5979\u503e\u5bb6\u8361\u4ea7\u4e5f\u5728\u6240\u4e0d\u60dc\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u5927\u5e08\u62d2\u7edd\u4e86\u3002 \u3000\u3000\u2014\u2014\u5927\u5e08\u7684\u6770\u4f5c\uff0c\u662f\u7edd\u4e0d\u53ef\u80fd\u8f6c\u8ba9\u7ed9\u522b\u4eba\u7684\u3002 \u3000\u3000\u2014\u2014\u60b2\u4f24\u7684\u6bcd\u4eb2\u53c8\u96be\u53d7\u53c8\u5931\u671b\uff0c\u6b63\u51c6\u5907\u8d70\u7684\u65f6\u5019\u3002 \u3000\u3000\u2014\u2014\u53ef\u6015\u7684\u4e8b\u5c31\u5728\u90a3\u4e00\u77ac\u95f4\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u2014\u2014\u90a3\u4e2a\u5973\u5b69\u5b50\u7684\u8721\u50cf\uff0c\u773c\u4e2d\u5ffd\u7136\u6d41\u51fa\u4e86\u6cea\u6765\uff0c\u7ea2\u8272\u7684\u773c\u6cea\uff0c\u8840\u6cea\u3002 \u3000\u3000\u2014\u2014\u60b2\u4f24\u7684\u6bcd\u4eb2\u518d\u4e5f\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\uff0c\u4e0d\u987e\u4e00\u5207\u51b2\u4e86\u8fc7\u53bb\uff0c\u62b1\u4f4f\u4e86\u90a3\u4e2a\u8721\u50cf\u3002 \u3000\u3000\u8721\u50cf\u5ffd\u7136\u788e\u88c2\uff0c\u5916\u9762\u4e00\u5c42\u5ffd\u7136\u88c2\u5f00\uff0c\u91cc\u9762\u8d6b\u7136\u6709\u4e00\u4e2a\u4eba\uff0c\u867d\u7136\u4e0d\u662f\u6d3b\u4eba\uff0c\u5374\u662f\u4e00\u4e2a\u6709\u8840\u6709\u8089\u7684\u4eba\u3002 \u3000\u3000\u4e00\u4e00\u8721\u50cf\u91cc\u7684\u8fd9\u4e2a\u4eba\uff0c\u8d6b\u7136\u5c31\u662f\u90a3\u4f4d\u6bcd\u4eb2\u5931\u8e2a\u4e86\u7684\u5973\u513f\u3002 \u3000\u3000\u2014\u2014\u4e8e\u662f\u5927\u5e08\u7684\u79d8\u5bc6\u88ab\u63ed\u7a7f\u4e86\uff0c\u4ed6\u6240\u6709\u7684\u6770\u4f5c\u90fd\u662f\u7528\u6d3b\u4eba\u6d47\u8721\u505a\u6210\u7684\u3002 \u3000\u3000\u5728\u5c0f\u65b9\u5f88\u5c0f\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u8fd8\u542c\u5230\u4e86\u4e00\u79cd\u4f20\u8bf4\uff0c\u4e00\u79cd\u53c8\u53ef\u6015\u53c8\u795e\u79d8\u7684\u4f20\u8bf4\u3002 \u3000\u3000\u2014\u2014\u53e4\u8001\u76f8\u4f20\uff0c\u5982\u679c\u4e00\u4e2a\u4eba\u6b7b\u5728\u5f02\u4e61\uff0c\u542b\u51a4\u800c\u6b7b\u540e\uff0c\u518d\u89c1\u5230\u4ed6\u7684\u4eb2\u4eba\u65f6\uff0c\u4ed6\u7684\u5c38\u4f53\u8fd8\u4f1a\u6709\u8840\u6d41\u51fa\u6765\uff0c\u4e03\u7a8d\u4e2d\u90fd\u4f1a\u6709\u8840\u6d41\u51fa\u6765\u3002 \u3000\u3000\u2014\u2014\u6240\u4ee5\u6b7b\u4eba\u4e5f\u672a\u5fc5\u662f\u4e00\u5b9a\u4e0d\u4f1a\u518d\u6d41\u8840\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u6545\u4e8b\u548c\u8fd9\u79cd\u4f20\u8bf4\uff0c\u90fd\u5728\u5c0f\u65b9\u5fc3\u91cc\u751f\u4e86\u6839\uff0c\u5c31\u5728\u4ed6\u770b\u89c1\u535c\u9e70\u7684\u8721\u50cf\u91cc\u6709\u8840\u6d41\u51fa\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u53c8\u60f3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u2014\u2014\u535c\u9e70\u7684\u8fd9\u4e2a\u8721\u50cf\u662f\u4e0d\u662f\u4e5f\u7528\u8fd9\u79cd\u65b9\u6cd5\u505a\u6210\u7684\uff1f \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u8721\u50cf\u91cc\u7684\u4eba\u662f\u4e0d\u662f\u535c\u9e70\uff1f \u3000\u3000\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u5c0f\u65b9\u5c31\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u627e\u51fa\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\u90fd\u8981\u627e\u51fa\u6765\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u81ea\u5df1\u7684\u5b89\u5371\u6b7b\u6d3b\uff0c\u4ed6\u6839\u672c\u5c31\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e00\u77ac\u95f4\u4ed6\u5df2\u7ecf\u628a\u6240\u6709\u522b\u7684\u95ee\u9898\u5168\u90fd\u5fd8\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u7ad9\u5728\u9e70\u8bb0\u5546\u53f7\u5916\u7684\u4eba\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u5c0f\u65b9\u4f1a\u5728\u4eb2\u773c\u770b\u89c1\u8001\u56db\u66b4\u6bd9\u540e\u8fd8\u4f1a\u51b2\u8fdb\u53bb\uff0c\u8fde\u9f50\u5c0f\u71d5\u90fd\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5df2\u7ecf\u51b2\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6cd5\u6781\u5feb\uff0c\u6bd4\u5927\u591a\u6570\u4eba\u60f3\u50cf\u4e2d\u90fd\u5feb\u5f97\u591a\uff0c\u53ef\u662f\u4ed6\u4e00\u51b2\u8fdb\u53bb\u4e4b\u540e\uff0c\u5c31\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u9b54\u6cd5\u5b9a\u4f4f\u4e00\u6837\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u6807\u662f\u90a3\u4e2a\u4f1a\u6d41\u8840\u7684\u535c\u9e70\u8721\u50cf\u3002 \u3000\u3000\u53ef\u662f\u5728\u4ed6\u8eab\u5b50\u505c\u4e0b\u6765\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u773c\u775b\u662f\u770b\u7740\u53e6\u5916\u4e00\u4e2a\u8721\u4eba\u7684\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u773c\u775b\u770b\u5230\u8fd9\u4e2a\u8721\u4eba\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u8eab\u5b50\u624d\u5ffd\u7136\u505c\u987f\u3002 \u3000\u3000\u7136\u540e\u4ed6\u8138\u4e0a\u5c31\u9732\u51fa\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u8001\u56db\u4e34\u6b7b\u524d\u9732\u51fa\u7684\u90a3\u79cd\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u91cc\u4e5f\u5ffd\u7136\u5145\u6ee1\u6050\u60e7\uff0c\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u4eff\u4f5b\u4e5f\u5728\u6536\u7f29\u75c9\u631b\u626d\u66f2\u3002 \u3000\u3000\u2014\u2014\u4ed6\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u5c0f\u65b9\u770b\u89c1\u7684\u4e8b\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\u4e4b\u5916\u8c01\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u751a\u81f3\u8fde\u4ed6\u81ea\u5df1\u90fd\u5f88\u96be\u76f8\u4fe1\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u770b\u89c1\u4e86\u4ed6\u81ea\u5df1\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4e5f\u770b\u89c1\u4e86\u4ed6\u81ea\u5df1\u773c\u775b\u91cc\u9732\u51fa\u7684\u90a3\u79cd\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u60f3\u50cf\u7684\u8868\u60c5\u3002 \u3000\u3000\u4e00\u79cd\u5145\u6ee1\u4e86\u8ba5\u5632\u548c\u6028\u6bd2\u7684\u8868\u60c5\u3002 \u3000\u3000\u6709\u8c01\u80fd\u60f3\u8c61\u5230\u4e00\u4e2a\u4eba\u4f1a\u7528\u8fd9\u6837\u7684\u773c\u5149\u6765\u770b\u81ea\u5df1\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u89c1\u7684\u5f53\u7136\u4e0d\u662f\u4ed6\u81ea\u5df1\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u770b\u6765\u51e0\u4e4e\u8ddf\u4ed6\u5b8c\u5168\u4e00\u6837\u7684\u8721\u4eba\u800c\u5df2\u3002 \u3000\u3000\u53ef\u662f\u5728\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u5374\u771f\u7684\u6709\u4e86\u8fd9\u79cd\u611f\u89c9\uff0c\u89c9\u5f97\u771f\u7684\u662f\u4ed6\u81ea\u5df1\u5728\u770b\u7740\u4ed6\u81ea\u5df1\uff0c\u4ed6\u4e00\u4e2a\u4eba\u597d\u50cf\u5df2\u5ffd\u7136\u88c2\u6210\u4e24\u4e2a\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u4e94\u7ae0\u3000\u4e0d\u662f\u4f60\u7684\u513f\u5b50 \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u4e0d\u662f\u4f60\u7684\u513f\u5b50 \u3000\u3000\u2014\u2014\u5c31\u7b97\u662f\u5728\u7167\u955c\u5b50\u7684\u65f6\u5019\uff0c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u955c\u5b50\u91cc\u770b\u7740\u4ed6\u7684\u90a3\u4e2a\u4eba\u5e76\u4e0d\u662f\u4f60\u81ea\u5df1\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u865a\u5e7b\u7684\u5f71\u5b50\u800c\u5df2\u3002 \u3000\u3000\u2014\u2014\u8fd9\u79cd\u4e8b\u53ea\u6709\u5728\u68a6\u4e2d\u624d\u4f1a\u53d1\u751f\uff0c\u800c\u4e14\u901a\u5e38\u662f\u5669\u68a6\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u65b9\u4e0d\u662f\u5728\u505a\u68a6\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u770b\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u8eab\u5b50\u5df2\u7ecf\u505c\u4e0b\u6765\uff0c\u76ee\u5149\u5df2\u7ecf\u88ab\u4ed6\u53e6\u5916\u4e00\u4e2a\u81ea\u5df1\u6240\u5438\u5f15\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\uff0c\u6068\u4e0d\u5f97\u8d76\u5feb\u9003\u8d70\uff0c\u8d76\u5feb\u79bb\u5f00\u8fd9\u91cc\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u8eab\u5b50\u5df2\u7ecf\u4e0d\u80fd\u52a8\u4e86\uff0c\u76ee\u5149\u4e5f\u79fb\u4e0d\u5f00\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u773c\u775b\u5ffd\u7136\u89c9\u5f97\u4e00\u9635\u523a\u75db\uff0c\u5c31\u597d\u50cf\u6709\u4e00\u6839\u9488\u4ece\u4ed6\u773c\u775b\u91cc\u523a\u4e86\u8fdb\u53bb\uff0c\u628a\u4ed6\u6574\u4e2a\u4eba\u90fd\u9489\u6b7b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u7684\u808c\u8089\u4eff\u4f5b\u90fd\u5df2\u7ecf\u75db\u82e6\u800c\u9ebb\u75f9\u50f5\u6728\u626d\u66f2\uff0c\u4ed6\u81ea\u5df1\u4e5f\u80fd\u611f\u89c9\u5230\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5df2\u7ecf\u5b8c\u5168\u65e0\u80fd\u4e3a\u529b\u4e86\u3002 \u3000\u3000\u2014\u2014\u8001\u56db\u4e34\u6b7b\u524d\u7684\u611f\u89c9\uff0c\u662f\u4e0d\u662f\u4e5f\u50cf\u8fd9\u6837\u5b50\uff1f \u3000\u3000\u4ed6\u4eff\u4f5b\u542c\u89c1\u9f50\u5c0f\u71d5\u5728\u547c\u5524\uff0c\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u60ca\u60f6\u7126\u6025\u4e0e\u5173\u5207\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5df2\u7ecf\u542c\u4e0d\u6e05\u695a\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u638c\u4e2d\u867d\u4ecd\u7d27\u63e1\u7740\u4ed6\u7684\u9b54\u773c\uff0c\u5374\u5df2\u65e0\u529b\u523a\u51fa\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u5b8c\u5168\u88ab\u4ed6\u53e6\u5916\u4e00\u4e2a\u81ea\u5df1\u7684\u773c\u775b\u6240\u63a7\u5236\uff0c\u4ed6\u5df2\u7ecf\u4ece\u8fd9\u53cc\u773c\u775b\u91cc\u770b\u5230\u4e86\u5730\u72f1\u3002 \u3000\u3000\u706b\u7130\u5728\u71c3\u70e7\uff0c\u56db\u9762\u516b\u65b9\u90fd\u5728\u71c3\u70e7\u3002 \u3000\u3000\u5929\u5d29\u5730\u88c2\uff0c\u7802\u77f3\u98de\u626c\u3002 \u3000\u3000\u6ca1\u6709\u751f\u547d\u7684\u8721\u4eba\u5ffd\u7136\u5168\u90e8\u90fd\u5728\u706b\u7684\u6d17\u793c\u4e2d\u83b7\u5f97\u4e86\u751f\u547d\uff0c\u5ffd\u7136\u95f4\u5168\u90fd\u98de\u8dc3\u800c\u8d77\uff0c\u9b3c\u9b45\u822c\u6251\u5411\u4eba\u7fa4\u3002 \u3000\u3000\u4eba\u7fa4\u5728\u52a8\u4e71\u4e2d\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u542c\u5230\u4e00\u58f0\u58f0\u51c4\u5389\u7684\u60e8\u547c\u3002 \u3000\u3000\u706b\u7130\u4e2d\u6709\u4e86\u8840\u5149\uff01 \u3000\u3000\u8fd9\u4e0d\u662f\u5730\u72f1\uff0c\u4e5f\u4e0d\u662f\u5730\u72f1\u4e2d\u7684\u5e7b\u8c61\u3002 \u3000\u3000\u5c0f\u65b9\u77e5\u9053\u4e0d\u662f\u7684\uff0c\u7edd\u5bf9\u4e0d\u662f\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u4eb2\u773c\u770b\u89c1\u7684\u3002 \u3000\u3000\u4ed6\u770b\u5230\u8fd9\u4e9b\u53ef\u6015\u7684\u666f\u8c61\u53d1\u751f\u540e\uff0c\u5c31\u6655\u4e86\u8fc7\u53bb\uff0c\u8fd8\u6ca1\u6709\u5f04\u6e05\u695a\u8fd9\u4e9b\u4e8b\u662f\u600e\u4e48\u53d1\u751f\u7684\u5c31\u5df2\u7ecf\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u84dd\u8272\u7684\u6d77\u3002 \u3000\u3000\u84dd\u8272\u7684\u6ce2\u6d6a\u3002 \u3000\u3000\u9633\u5149\u707f\u70c2\uff0c\u6d77\u6c34\u6e5b\u84dd\uff0c\u84dd\u8272\u7684\u6ce2\u6d6a\u5728\u9633\u5149\u4e0b\u770b\u6765\u6e29\u67d4\u5982\u60c5\u4eba\u7684\u773c\u6ce2\u3002 \u3000\u3000\u60c5\u4eba\u7684\u773c\u6ce2\u4e5f\u6e29\u67d4\u5982\u84dd\u8272\u7684\u6ce2\u6d6a\u3002 \u3000\u3000\u8fd9\u4e5f\u4e0d\u662f\u5e7b\u8c61\uff0c\u4e5f\u662f\u5c0f\u65b9\u4eb2\u773c\u770b\u89c1\u7684\u3002 \u3000\u3000\u4ed6\u9192\u6765\u65f6\u5c31\u770b\u89c1\u4e00\u7247\u84dd\uff0c\u90a3\u4e48\u84dd\uff0c\u84dd\u5f97\u90a3\u4e48\u7f8e\uff0c\u90a3\u4e48\u6e29\u67d4\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u91cc\u5e76\u6ca1\u6709\u6d77\uff0c\u4ed6\u770b\u89c1\u7684\u4e5f\u4e0d\u662f\u6ce2\u6d6a\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u7684\u662f\u9633\u5149\u3002 \u3000\u3000\u84dd\u8272\u7684\u9633\u5149\u3002 \u3000\u3000\u5c0f\u65b9\u9192\u6765\u65f6\uff0c\u201c\u9633\u5149\u201d\u6b63\u5728\u770b\u7740\u4ed6\uff0c\u773c\u6ce2\u6e29\u67d4\u5982\u6d77\u6d6a\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u771f\u7684\uff1f\u771f\u7684\u4e0d\u662f\u5e7b\u8c61\uff1f \u3000\u3000\u201c\u9633\u5149\u201d\uff0c\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u5c0f\u65b9\u4e0d\u4fe1\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u8fd9\u5c31\u662f\u5730\u72f1\uff1f\u96be\u9053\u6211\u5df2\u7ecf\u5230\u4e86\u5730\u72f1\uff1f \u3000\u3000\u2014\u2014\u5730\u72f1\u4e2d\u6709\u65f6\u5c82\u975e\u4e5f\u4f1a\u51fa\u73b0\u7f8e\u666f\uff1f\u5c31\u6b63\u5982\u5730\u72f1\u822c\u7684\u6c99\u6f20\u4e2d\u6709\u65f6\u4e5f\u4f1a\u51fa\u73b0\u4ee4\u4eba\u7740\u8ff7\u7684\u6d77\u5e02\u8703\u697c\u4e00\u6837\u3002 \u3000\u3000\u5c0f\u65b9\u60f3\u4f38\u624b\u63c9\u63c9\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u662f\u8f6f\u7684\uff0c\u8f6f\u7ef5\u7ef5\u7684\u5b8c\u5168\u6ca1\u6709\u4e00\u70b9\u529b\u6c14\u3002 \u3000\u3000\u4ed6\u7684\u624b\u80fd\u591f\u62ac\u8d77\u6765\uff0c\u53ea\u56e0\u4e3a\u201c\u9633\u5149\u201d\u5df2\u7ecf\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\u3002 \u3000\u3000\u51b0\u51b7\u7684\u624b\uff0c\u51b0\u51b7\u7684\u6cea\u3002 \u3000\u3000\u773c\u6cea\u5df2\u7ecf\u6d41\u4e0b\u4e86\u201c\u9633\u5149\u201d\u7684\u9762\u988a\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u770b\u6765\u5c31\u597d\u50cf\u6c38\u8fdc\u518d\u4e5f\u4e0d\u4f1a\u628a\u5c0f\u65b9\u7684\u624b\u653e\u5f00\u3002 \u3000\u3000\u4f46\u662f\u5979\u504f\u504f\u5f88\u5feb\u5c31\u653e\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u9664\u4e86\u4ed6\u4eec\u4e4b\u5916\uff0c\u8fd9\u95f4\u5c0f\u800c\u6e29\u6696\u7684\u5c4b\u5b50\u91cc\u8fd8\u6709\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u4e5f\u770b\u89c1\u4e86\u8fd9\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u4e8c\u4e2a\u5927\u4eba\uff0c\u4e00\u4e2a\u5c0f\u5b69\u3002 \u3000\u3000\u7ad9\u5728\u5c0f\u65b9\u5e8a\u5934\u7684\u662f\u9f50\u5c0f\u71d5\u3002 \u3000\u3000\u5979\u4e00\u76f4\u90fd\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u770b\u7740\u5c0f\u65b9\u548c\u201c\u9633\u5149\u201d\uff0c\u770b\u7740\u4ed6\u4eec\u7684\u4e3e\u52a8\u548c\u8868\u60c5\u3002 \u3000\u3000\u5979\u81ea\u5df1\u5374\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u5c31\u597d\u50cf\u5df2\u7ecf\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u2014\u2014\u5979\u80fd\u600e\u4e48\u6837\uff1f\u5979\u80fd\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u8fdc\u8fdc\u5730\u7ad9\u5728\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u624b\u91cc\u62b1\u7740\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u5979\u7a7f\u7740\u4e00\u8eab\u6de1\u7070\u8272\u7684\u8863\u88f3\uff0c\u767d\u751f\u751f\u7684\u4e00\u5f20\u8138\u4e0a\u672a\u65bd\u8102\u7c89\uff0c\u6f06\u9ed1\u7684\u5934\u53d1\u84ec\u84ec\u677e\u677e\u5730\u633d\u4e86\u4e2a\u9afb\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5e26\u7740\u4e00\u62b9\u6de1\u6de1\u7684\u3001\u65e0\u53ef\u5948\u4f55\u7684\u4f24\u611f\u3002 \u3000\u3000\u5979\u624b\u91cc\u62b1\u7740\u4e2a\u7a7f\u7ea2\u8863\u7684\u5a74\u513f\u3002 \u3000\u3000\u2014\u2014\u82cf\u82cf\u3002 \u3000\u3000\u2014\u2014\u82cf\u82cf\u5c45\u7136\u4e5f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u5979\u624b\u91cc\u62b1\u7740\u7684\u5a74\u513f\u65e0\u7591\u5c31\u662f\u5c0f\u65b9\u7684\u5b69\u5b50\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5fc3\u5728\u523a\u75db\u3002 \u3000\u3000\u2014\u2014\u82cf\u82cf\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u2014\u2014\u201c\u9633\u5149\u201d\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u2014\u2014\u8fd9\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u2014\u2014\u4ed6\u81ea\u5df1\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f \u3000\u3000\u2014\u2014\u5728\u201c\u9e70\u8bb0\u201d\u4ed6\u6240\u770b\u5230\u7684\u90a3\u4e9b\u666f\u8c61\u662f\u771f\u662f\u5e7b\uff1f\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u2014\u2014\u90a3\u4e9b\u53c8\u795e\u79d8\u53c8\u53ef\u6015\u7684\u8721\u4eba\u5462\uff1f \u3000\u3000\u5c0f\u65b9\u6700\u5fd8\u4e0d\u4e86\u7684\u5f53\u7136\u8fd8\u662f\u90a3\u53cc\u773c\u775b\uff0c\u90a3\u53cc\u6bd2\u773c\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e9b\u95ee\u9898\u4ed6\u90fd\u6ca1\u6709\u95ee\uff0c\u56e0\u4e3a\u4ed6\u8ddf\u672c\u4e0d\u77e5\u9053\u5e94\u8be5\u95ee\u8c01\u3002 \u3000\u3000\u67d4\u8f6f\u7684\u5e8a\u94fa\uff0c\u5e72\u51c0\u7684\u88ab\u5355\uff0c\u4ed6\u5f88\u60f3\u5c31\u8fd9\u6837\u8eba\u5728\u8fd9\u91cc\uff0c\u8eba\u4e00\u8f88\u5b50\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u80fd\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u6323\u624e\u7740\u7ad9\u8d77\u6765\uff0c\u4f38\u51fa\u53cc\u81c2\uff0c\u4eff\u4f5b\u8981\u53bb\u62e5\u62b1\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u91cc\u6709\u4e09\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u5973\u4eba\u90fd\u66fe\u7ecf\u5f71\u54cd\u8fc7\u4ed6\u7684\u751f\u547d\uff0c\u90fd\u662f\u4ed6\u8fd9\u4e00\u751f\u6c38\u96be\u5fd8\u6000\u7684\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u5973\u4eba\u90fd\u66fe\u7ecf\u8ddf\u4ed6\u6709\u8fc7\u4e00\u6bb5\u53c8\u5947\u602a\u53c8\u590d\u6742\u53c8\u6df1\u539a\u7684\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u8981\u53bb\u62e5\u62b1\u7684\u662f\u8c01\uff1f \u3000\u3000\u5c0f\u71d5\u671f\u5f85\u7740\u5c0f\u65b9\u7684\u62e5\u62b1\u3002 \u3000\u3000\u82cf\u82cf\u4e5f\u671f\u5f85\u7740\u5c0f\u65b9\u7684\u62e5\u62b1\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u65b9\u6251\u5411\u4e86\u82cf\u82cf\u3002 \u3000\u3000\u4ed6\u62e5\u62b1\u7684\u5374\u4e0d\u662f\u82cf\u82cf\uff0c\u800c\u662f\u82cf\u82cf\u6000\u91cc\u62b1\u7740\u7684\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u7d27\u7d27\u5730\u62b1\u7740\u8fd9\u4e2a\u4ece\u672a\u89c1\u8fc7\u7684\u5b69\u5b50\u3002 \u3000\u3000\u773c\u6cea\uff0c\u5ffd\u7136\u81ea\u5c0f\u65b9\u773c\u4e2d\u6d41\u4e0b\u3002 \u3000\u3000\u82f1\u96c4\u6709\u6cea\u4e0d\u8f7b\u5f39\u3002 \u3000\u3000\u5c0f\u65b9\u6d41\u6cea\uff0c\u662f\u56e0\u4e3a\u4ed6\u4e0d\u662f\u82f1\u96c4\uff1f \u3000\u3000\u5c0f\u65b9\u7231\u82cf\u82cf\uff0c\u4f46\u662f\u4ed6\u4eec\u5206\u79bb\u4e86\u5f88\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\u3002 \u3000\u3000\u5c0f\u65b9\u7231\u5c0f\u71d5\uff0c\u4f46\u662f\u4ed6\u5fc3\u5e95\u6709\u53e6\u4e00\u79cd\u611f\u89c9\uff0c\u4ed6\u4eec\u5fc5\u5c06\u5206\u624b\u3002 \u3000\u3000\u4e00\u8109\u76f8\u627f\uff0c\u7ef4\u7cfb\u7740\u5c0f\u65b9\u7684\u8840\u548c\u8089\u7684\uff0c\u53ea\u6709\u4ed6\u81ea\u5df1\u7684\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u548c\u82cf\u82cf\u7684\u5b69\u5b50\u3002 \u3000\u3000\u6000\u4e2d\u7684\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\uff0c\u5bf9\u6000\u4e2d\u5c0f\u5b69\u7684\u611f\u60c5\uff0c\u590d\u6742\u800c\u6df1\u539a\u3002 \u3000\u3000\u7231\u60c5\u5e76\u4e0d\u662f\u5386\u4e45\u4e0d\u8870\u7684\uff0c\u5386\u4e45\u4e0d\u8870\u7684\u7231\u60c5\u5c11\u4e4b\u53c8\u5c11\u3002 \u3000\u3000\u7231\u60c5\u662f\u5f88\u5bb9\u6613\u6d88\u5931\u7684\u3002 \u3000\u3000\u5c71\u9ad8\u6c34\u957f\uff0c\u6cb3\u5ddd\u963b\u9694\uff0c\u4f1a\u4f7f\u7231\u60c5\u6162\u6162\u892a\u8272\uff0c\u6d88\u5931\u4e8e\u65e0\u60c5\u4e4b\u4e2d\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u773c\u5149\uff0c\u6e29\u67d4\u7684\u773c\u5149\uff0c\u73b0\u5728\u843d\u5728\u5c0f\u5b69\u5b50\u7684\u8138\u4e0a\u3002 \u3000\u3000\u5c0f\u5b69\u77aa\u7740\u4e00\u53cc\u9ed1\u767d\u5206\u660e\u7684\u773c\u775b\uff0c\u65e0\u90aa\u5730\u770b\u7740\u4ed6\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5185\u5fc3\u5ffd\u7136\u611f\u5230\u4e00\u9635\u523a\u75db\u3002 \u3000\u3000\u56e0\u4e3a\u5b69\u5b50\u5ffd\u7136\u5411\u4ed6\u88c2\u5634\u4e00\u7b11\uff0c\u90a3\u7b11\u5bb9\uff0c\u5c31\u548c\u82cf\u82cf\u7684\u7b11\u5bb9\u4e00\u6837\u3002 \u3000\u3000\u5c0f\u65b9\u53c8\u7d27\u7d27\u5730\u5c06\u5c0f\u5b69\u62e5\u5728\u6000\u4e2d\u3002 \u3000\u3000\u5c0f\u65b9\u770b\u770b\u5c0f\u71d5\uff0c\u53c8\u770b\u770b\u82cf\u82cf\u3002 \u3000\u3000\u4ed6\u8111\u6d77\u4e2d\uff0c\u6d6e\u73b0\u51fa\u548c\u8fd9\u4e24\u4e2a\u5973\u4eba\u5171\u6e21\u65f6\u7684\u6b22\u4e50\u3002 \u3000\u3000\u8fd9\u4e9b\u6b22\u4e50\uff0c\u4ed6\u5c06\u7ec8\u751f\u96be\u5fd8\u3002 \u3000\u3000\u4ed6\u5bf9\u8fd9\u4e24\u4e2a\u5973\u4eba\u7684\u611f\u60c5\uff0c\u662f\u53c8\u590d\u6742\u53c8\u6df1\u539a\u7684\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u7528\u8be7\u5f02\u7684\u76ee\u5149\u6ce8\u89c6\u7740\u5c0f\u65b9\u3002 \u3000\u3000\u82cf\u82cf\u7684\u76ee\u5149\u5374\u4e0d\u8be7\u5f02\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e86\u89e3\u5c0f\u65b9\u7684\u611f\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u662f\u5b69\u5b50\u7684\u6bcd\u4eb2\uff0c\u5c0f\u65b9\u662f\u5b69\u5b50\u7684\u7236\u4eb2\u3002\u6bcd\u5b50\u60c5\u6df1\uff0c\u7236\u5b50\u60c5\u4e5f\u6df1\u3002 \u3000\u3000\u5728\u5371\u96be\u4e2d\uff0c\u5728\u5386\u52ab\u540e\uff0c\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u6709\u4e86\u5c0f\u5b69\uff0c\u7a81\u7136\u89c1\u5230\u4e86\u8fd9\u4e2a\u5c0f\u5b69\uff0c\u90a3\u4e00\u4efd\u5fc3\u7075\u7684\u9707\u64bc\uff0c\u662f\u7edd\u5bf9\u8fde\u63a5\u5230\u6cea\u817a\u4e0a\u7684\u3002 \u3000\u3000\u82cf\u82cf\u6df1\u60c5\u5730\u770b\u7740\u5c0f\u65b9\u548c\u4ed6\u6000\u4e2d\u7684\u5c0f\u5b69\u3001\u5979\u5ffd\u7136\u611f\u5230\u4e00\u80a1\u6696\u6d41\u5145\u76c8\u5728\u5fc3\u53e3\u3002 \u3000\u3000\u5979\u4ece\u6765\u6ca1\u6709\u60f3\u5230\uff0c\u7236\u7231\uff0c\u4e5f\u662f\u8fd9\u4e48\u6df1\u523b\uff0c\u8fd9\u4e48\u52a8\u4eba\u7684\u3002 \u3000\u3000\u5979\u53ea\u77e5\u9053\u6bcd\u7231\u3002 \u3000\u3000\u6bcd\u7231\u662f\u81ea\u7136\u7684\uff0c\u4ece\u6000\u5b55\u90a3\u5929\u5f00\u59cb\uff0c\u4ece\u5a74\u513f\u5728\u6bcd\u4f53\u6210\u5f62\u90a3\u5929\u5f00\u59cb\uff0c\u6bcd\u4eb2\u5c31\u6709\u4e00\u79cd\u5f88\u7279\u6b8a\u7684\u611f\u89c9\uff0c\u5f88\u5feb\u5c31\u53d8\u6210\u7231\u3002 \u3000\u3000\u5a74\u513f\u8fd8\u6ca1\u6709\u51fa\u751f\uff0c\u5c31\u5df2\u7ecf\u6709\u4e86\u4ed6\u6bcd\u4eb2\u7231\u7684\u5173\u6ce8\u3002 \u3000\u3000\u7236\u7231\u5c31\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u7236\u4eb2\u4e00\u5b9a\u8981\u770b\u5230\u5c0f\u5b69\u8131\u79bb\u6bcd\u4f53\uff0c\u964d\u4e34\u4eba\u95f4\uff0c\u624d\u4f1a\u53bb\u7231\u4ed6\u3002 \u3000\u3000\u4ece\u7b2c\u4e00\u773c\u770b\u5230\u5c0f\u5b69\u8d77\uff0c\u7236\u7231\u624d\u5f00\u59cb\u3002 \u3000\u3000\u6bcd\u7231\u662f\u5929\u751f\u7684\uff0c\u7236\u5b50\u4e4b\u7231\u5374\u662f\u540e\u5929\u6162\u6162\u57f9\u517b\u7684\u3002 \u3000\u3000\u7236\u5b50\u4e4b\u7231\uff0c\u662f\u4e00\u79cd\u5b66\u4e60\u7684\u7231\u3002 \u3000\u3000\u4ee4\u82cf\u82cf\u611f\u52a8\u7684\uff0c\u5c31\u662f\u5979\u53d1\u73b0\u5c0f\u65b9\u7adf\u7136\u7231\u5979\u7684\u5c0f\u5b69\u90a3\u4e48\u6df1\u539a\u3002 \u3000\u3000\u5979\u5ffd\u7136\u51b2\u4e0a\u53bb\uff0c\u5c06\u5c0f\u65b9\u548c\u5c0f\u5b69\u62b1\u7d27\u3002 \u3000\u3000\u5c0f\u65b9\u6e29\u67d4\u5730\u5c06\u89c6\u7ebf\u6295\u843d\u5728\u82cf\u82cf\u7684\u8138\u4e0a\uff0c\u76ee\u5149\u663e\u51fa\u4e00\u4efd\u5f88\u6df1\u6c89\u7684\u611f\u6fc0\u3002 \u3000\u3000\u611f\u6fc0\u5979\u4e3a\u4ed6\u7559\u4e86\u540e\u4ee3\u3002 \u3000\u3000\u6709\u4e86\u540e\u4ee3\uff0c\u4ed6\u5c31\u6b7b\u800c\u65e0\u61be\u4e86\u3002 \u3000\u3000\u6709\u4e86\u540e\u4ee3\uff0c\u4ed6\u5fc3\u60c5\u8c41\u7136\u5f00\u6717\u3002 \u3000\u3000\u4ed6\u4e0d\u518d\u6050\u60e7\u6b7b\u4ea1\uff0c\u4ed6\u4e0d\u518d\u6050\u60e7\u9762\u5bf9\u5371\u96be\u3002 \u3000\u3000\u4ed6\u968f\u65f6\u968f\u5730\u53ef\u4ee5\u6b7b\u53bb\uff0c\u4e3a\u535c\u9e70\uff0c\u4e3a\u82cf\u82cf\uff0c\u4e3a\u201c\u9633\u5149\u201d\uff0c\u4e3a\u9f50\u5c0f\u71d5\u3002 \u3000\u3000\u5c0f\u65b9\u521a\u9192\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u4ee5\u4e3a\u81ea\u5df1\u8eab\u9677\u5730\u72f1\u4e4b\u5185\uff0c\u73b0\u5728\uff0c\u4ed6\u77e5\u9053\u4ed6\u5e76\u6ca1\u6709\u5165\u5730\u72f1\u3002 \u3000\u3000\u5165\u5730\u72f1\u7684\u4eba\u7edd\u5bf9\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u5c31\u7b97\u662f\u5165\u4e86\u5730\u72f1\uff0c\u4ed6\u5165\u7684\u4e5f\u53ea\u4e0d\u8fc7\u662f\u201c\u6211\u4e0d\u5165\u5730\u72f1\u8c01\u5165\u5730\u72f1\u201d\u7684\u5730\u72f1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u6709\u4e86\u6211\u4e0d\u5165\u5730\u72f1\u8c01\u5165\u5730\u72f1\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u4ed6\u51b3\u5fc3\u53bb\u67e5\u660e\u8fd9\u4ef6\u4e8b\u60c5\u7684\u771f\u76f8\u3002 \u3000\u3000\u4e0d\u60dc\u4ee3\u4ef7\uff0c\u4e0d\u60dc\u6b7b\u4ea1\u7684\u727a\u7272\uff0c\u4ed6\u90fd\u8981\u53bb\u67e5\u51fa\u80cc\u540e\u7684\u9634\u8c0b\u8005\u5230\u5e95\u662f\u8c01\uff1f \u3000\u3000\u4ed6\u77e5\u9053\u4ed6\u5fc5\u7136\u67e5\u5f97\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6ca1\u6709\u4e86\u540e\u987e\u4e4b\u5fe7\u3002 \u3000\u3000\u4ed6\u7684\u601d\u8def\uff0c\u4e5f\u5c06\u4e0d\u4f1a\u53d7\u6b7b\u4ea1\u9634\u5f71\u7684\u5a01\u80c1\u800c\u5927\u6253\u6298\u6263\u3002 \u3000\u3000\u4e00\u4e2a\u65e0\u754f\u7684\u4eba\uff0c\u4ed6\u7684\u5251\u672f\u5fc5\u5c06\u767e\u5206\u4e4b\u767e\u53d1\u6325\u5c3d\u81f4\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u8fd9\u662f\u4ed6\u5f00\u59cb\u53d1\u95ee\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u5148\u53bb\u62b1\u8d77\u4e86\u4ed6\u7684\u5b69\u5b50\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u662f\u5723\u4eba\uff0c\u65e2\u4e0d\u80fd\u505a\u5723\u4eba\uff0c\u4e5f\u4e0d\u60f3\u505a\u5723\u4eba\u3002 \u3000\u3000\u5728\u4ed6\u5fc3\u5e95\u67d0\u4e00\u4e2a\u79d8\u5bc6\u7684\u89d2\u843d\u91cc\uff0c\u4e5f\u8bb8\u4ed6\u662f\u60f3\u5148\u53bb\u62e5\u62b1\u9f50\u5c0f\u71d5\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u662f\u5979\u7b2c\u4e00\u4e2a\u7537\u4eba\uff0c\u5979\u5df2\u5c06\u4e00\u4e2a\u5973\u4eba\u4e00\u751f\u4e2d\u6240\u503c\u5f97\u73cd\u60dc\u7684\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u4e0d\u4f46\u662f\u5973\u4eba\u6240\u96be\u5fd8\u6000\u7684\uff0c\u7537\u4eba\u4e5f\u540c\u6837\u5f88\u96be\u5fd8\u8bb0\u3002 \u3000\u3000\u5728\u5c0f\u65b9\u5fc3\u5e95\u6df1\u5904\u53e6\u5916\u4e00\u4e2a\u79d8\u5bc6\u7684\u89d2\u843d\u91cc\uff0c\u4ed6\u60f3\u53bb\u62e5\u62b1\u7684\u4e5f\u8bb8\u662f\u201c\u9633\u5149\u201d\u3002 \u3000\u3000\u201c\u9633\u5149\u201d\u662f\u4e2a\u660e\u6717\u7f8e\u4e3d\u4f46\u5374\u975e\u5e38\u75f4\u60c5\u7684\u5973\u5b69\u5b50\uff0c\u4ed6\u77e5\u9053\u4ed6\u8fd9\u4e00\u751f\u4e2d\u662f\u6c38\u8fdc\u5f97\u4e0d\u5230\u5979\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u559c\u6b22\u5979\uff0c\u4e0d\u4f46\u559c\u6b22\uff0c\u800c\u4e14\u5c0a\u656c\u3002 \u3000\u3000\u4ed6\u5bf9\u201c\u9633\u5149\u201d\u7684\u611f\u60c5\u5df2\u7ecf\u8ddf\u4ed6\u5bf9\u535c\u9e70\u7684\u53cb\u8c0a\u6df7\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u5c0f\u65b9\u662f\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u82cf\u82cf\u662f\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u7edd\u5bf9\u5973\u6027\u5316\u7684\u5973\u4eba\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u5979\u5168\u8eab\u4e0a\u4e0b\u6bcf\u5206\u6bcf\u5bf8\u90fd\u662f\u5973\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u5fd8\u8bb0\u5979\u3002 \u3000\u3000\u5979\u7684\u6fc0\u60c5\uff0c\u5979\u7684\u6e29\u67d4\uff0c\u5979\u7684\u7f20\u7ef5\uff0c\u65e0\u8bba\u4efb\u4f55\u7537\u4eba\u90fd\u96be\u4ee5\u5fd8\u8bb0\u3002 \u3000\u3000\u5728\u5c0f\u65b9\u5fc3\u5e95\u66f4\u6df1\u5904\uff0c\u4ed6\u60f3\u53bb\u62e5\u62b1\u7684\u4e5f\u8bb8\u662f\u5979\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u5148\u53bb\u62b1\u8d77\u4e86\u4ed6\u7684\u5b69\u5b50\u3002 \u3000\u3000\u90a3\u4e0d\u6b62\u662f\u56e0\u4e3a\u7236\u7231\uff0c\u7236\u4e0e\u5b50\u4e4b\u95f4\u7684\u611f\u60c5\u662f\u540e\u5929\u7684\uff0c\u662f\u9700\u8981\u57f9\u517b\u7684\u3002 \u3000\u3000\u4ed6\u5148\u53bb\u62b1\u8d77\u4ed6\u7684\u5b69\u5b50\uff0c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u8981\u6c42\u5e73\u8861\uff0c\u4e00\u79cd\u7231\u7684\u5e73\u8861\uff0c\u4e00\u79cd\u552f\u4e00\u53ef\u4ee5\u4f7f\u4ed6\u618e\u7eea\u7a33\u5b9a\u7684\u5e73\u8861\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4ed6\u8fd8\u662f\u8fd9\u4e48\u505a\u4e86\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u6084\u6084\u5730\u9000\u4e86\u51fa\u53bb\uff0c\u201c\u9633\u5149\u201d\u6162\u6162\u5730\u5750\u4e86\u4e0b\u53bb\uff0c\u5750\u5728\u5e8a\u8fb9\u7684\u4e00\u5f20\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u82cf\u82cf\u5374\u5ffd\u7136\u7b11\u4e86\uff0c\u7b11\u5f97\u975e\u5e38\u5947\u602a\u3002 \u3000\u3000\u5979\u7684\u7b11\u4e2d\u4eff\u4f5b\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u8ba5\u4fcf\u6076\u6bd2\u4e4b\u610f\uff0c\u5979\u7684\u773c\u795e\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u5979\u770b\u7740\u5c0f\u65b9\u5fae\u7b11\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u4ee5\u4e3a\u8fd9\u5b69\u5b50\u662f\u4f60\u7684\u5b69\u5b50\uff1f\u201d \u3000\u3000\u201c\u4ed6\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d\u82cf\u82cf\u8bf4\uff1a\u201c\u4e0d\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u5979\u51b7\u51b7\u5730\u63a5\u7740\u8bf4\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u60f3\u60f3\uff0c\u5415\u4e09\u600e\u4e48\u4f1a\u628a\u4f60\u7684\u5b69\u5b50\u8fd8\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u82cf\u82cf\u4e0d\u662f\u5728\u8bf4\u8c0e\uff0c\u4f46\u662f\u4ed6\u4e5f\u6ca1\u6709\u653e\u4e0b\u624b\u91cc\u7684\u5b69\u5b50\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u6eba\u6c34\u8005\u660e\u77e5\u81ea\u5df1\u6293\u4f4f\u7684\u5e76\u4e0d\u662f\u4e00\u6839\u53ef\u4ee5\u8f7d\u4ed6\u6d6e\u8d77\u6765\u7684\u6728\u5934\uff0c\u5374\u8fd8\u662f\u4e0d\u80af\u653e\u8fc7\u4e00\u6837\u3002 \u3000\u3000\u82cf\u82cf\u7684\u7b11\u5bb9\u770b\u6765\u5c31\u50cf\u5ffd\u7136\u53c8\u53d8\u6210\u4e86\u4e00\u4e2a\u9762\u5177\u3002 \u3000\u3000\u201c\u5415\u4e09\u8981\u6211\u5e26\u8fd9\u4e2a\u5b69\u5b50\u6765\u89c1\u4f60\uff0c\u53ea\u4e0d\u8fc7\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u7684\u5b69\u5b50\u5df2\u7ecf\u957f\u5f97\u6709\u8fd9\u4e48\u5927\u4e86\u3002\u5c31\u597d\u50cf\u8fd9\u4e2a\u5b69\u5b50\u4e00\u6837\u6d3b\u6cfc\u53ef\u7231\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u624b\u51b0\u51b7\u3002 \u3000\u3000\u82cf\u82cf\u5ffd\u7136\u53c8\u51b7\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u524d\u6709\u6ca1\u6709\u60f3\u8fc7\u4f60\u7684\u5b69\u5b50\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u8bda\u5b9e\u7684\u4eba\uff0c\u4e5f\u8bb8\u4e0d\u80fd\u7b97\u662f\u597d\u4eba\uff0c\u5374\u7edd\u5bf9\u8bda\u5b9e\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u6ca1\u6709\u60f3\u8fc7\u4ed6\u7684\u5b69\u5b50\uff0c\u53ea\u56e0\u4e3a\u4ed6\u8fd8\u6ca1\u6709\u89c1\u8fc7\u4ed6\u7684\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u4eec\u7236\u5b50\u4e4b\u95f4\u8fd8\u6ca1\u6709\u7231\u3002 \u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u5df2\u7ecf\u6709\u4e86\u4f60\u7684\u5b69\u5b50\u3002\u201d\u82cf\u82cf\u53c8\u95ee\uff1a\u201c\u4f46\u662f\u4f60\u4ece\u6765\u90fd\u6ca1\u6709\u60f3\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u5df2\u7ecf\u5f00\u59cb\u5728\u60f3\u4ed6\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5bf9\u4ed6\u7684\u5b69\u5b50\u5df2\u7ecf\u6709\u4e86\u4e00\u4e2a\u5177\u4f53\u7684\u5f62\u8c61\u3002 \u3000\u3000\u2014\u2014\u8fd9\u5c31\u662f\u4eba\u6027\u3002 \u3000\u3000\u65e0\u8bba\u4eba\u7684\u672c\u6027\u662f\u5584\u8fd8\u662f\u6076\uff0c\u4eba\u6027\u4e2d\u603b\u662f\u6709\u5f31\u70b9\u7684\u3002 \u3000\u3000\u5415\u4e09\u65e0\u7591\u662f\u6700\u80fd\u628a\u63e1\u8fd9\u79cd\u5f31\u70b9\u7684\u4eba\u3002 \u3000\u3000\u201c\u5415\u4e09\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u201d\u82cf\u82cf\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u8981\u89c1\u4f60\u7684\u5b69\u5b50\uff0c\u5c31\u5f97\u5148\u66ff\u4ed6\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u5c0f\u65b9\u4e0d\u80fd\u4e0d\u95ee\uff01\u201c\u4ed6\u8981\u6211\u66ff\u4ed6\u53bb\u505a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u82cf\u82cf\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u5916\u9762\u5df2\u7ecf\u6709\u4eba\u66ff\u5979\u56de\u7b54\uff1a\u201c\u4ed6\u8981\u4f60\u5148\u66ff\u4ed6\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u8fd9\u662f\u73ed\u5bdf\u5df4\u90a3\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u79cd\u975e\u5e38\u51b7\u9759\uff0c\u53c8\u975e\u5e38\u70ed\u60c5\u7684\u58f0\u97f3\uff0c\u53ea\u8981\u542c\u8fc7\u4e00\u6b21\u5c31\u5f88\u4e0d\u5bb9\u6613\u5fd8\u8bb0\u3002 \u3000\u3000\u2014\u2014\u6c38\u8fdc\u6ca1\u6709\u4eba\u77e5\u9053\u4f1a\u5728\u4ec0\u4e48\u65f6\u5019\u51fa\u73b0\u7684\u73ed\u5bdf\u5df4\u90a3\u53c8\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u770b\u6765\u6c38\u8fdc\u662f\u5e74\u8f7b\u7684\u3002 \u3000\u3000\u2014\u2014\u201c\u5e74\u8f7b\u201d\uff0c\u8fd9\u4e24\u4e2a\u5b57\u6240\u4ee3\u8868\u7684\u5e76\u4e0d\u662f\u5e74\u7eaa\uff0c\u800c\u662f\u4e00\u79cd\u5f62\u8c61\u3002 \u3000\u3000\u4ed6\u770b\u6765\u5e74\u8f7b\uff0c\u56e0\u4e3a\u4ed6\u770b\u6765\u6c38\u8fdc\u90fd\u662f\u90a3\u4e48\u575a\u5f3a\uff0c\u90a3\u4e48\u633a\u62d4\uff0c\u90a3\u4e48\u6709\u751f\u6c14\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u5728\u4ec0\u4e48\u65f6\u5019\u4ec0\u4e48\u5730\u65b9\u51fa\u73b0\u90fd\u4e00\u6837\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u521a\u4ece\u6ce5\u6cbc\u91cc\u8d70\u51fa\u6765\uff0c\u4ed6\u770b\u6765\u8fd8\u662f\u50cf\u4e00\u628a\u521a\u51fa\u7089\u7684\u5251\uff0c\u5e72\u51c0\u3001\u660e\u4eae\u3001\u950b\u5229\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u521a\u4ece\u654c\u4eba\u7684\u5c38\u9aa8\u9c9c\u8840\u4e2d\u8d70\u51fa\u6765\uff0c\u4ed6\u770b\u6765\u8fd8\u662f\u6ca1\u6709\u4e00\u70b9\u8840\u8165\u6c14\u3002 \u3000\u3000\u8fd9\u6b21\u548c\u4ee5\u5f80\u552f\u4e00\u4e0d\u540c\u7684\u5730\u65b9\u662f\uff0c\u8fd9\u6b21\u4ed6\u624b\u91cc\u5c45\u7136\u63d0\u7740\u4e00\u888b\u9152\u3002 \u3000\u3000\u6ee1\u6ee1\u7684\u4e00\u7f8a\u76ae\u888b\u9152\u3002 \u3000\u3000\u4ed6\u8d70\u8fc7\u6765\uff0c\u5750\u5728\u4e00\u5f20\u5c0f\u684c\u65c1\u7684\u4e00\u628a\u6905\u5b50\u4e0a\uff0c\u4ed6\u770b\u7740\u5c0f\u65b9\u8bf4\uff1a\u201c\u5750\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u5750\u4e0b\u3002\u5148\u628a\u5b69\u5b50\u4ea4\u7ed9\u82cf\u82cf\u624d\u5750\u4e0b\uff0c\u5750\u5728\u5bf9\u9762\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5c06\u6ee1\u6ee1\u7684\u4e00\u888b\u9152\u653e\u5728\u5c0f\u684c\u4e0a\u3002 \u3000\u3000\u201c\u8fd9\u79cd\u9152\u53eb\u53e4\u57ce\u70e7\u3002\u201d\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u559d\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6211\u559d\u8fc7\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u559d\u8fc7\uff0c\u535c\u9e70\u6700\u559c\u6b22\u7684\u5c31\u662f\u8fd9\u79cd\u9152\u3002 \u3000\u3000\u8fd9\u79cd\u9152\u559d\u8d77\u6765\u5c31\u50cf\u662f\u7537\u513f\u7684\u70ed\u8840\u3002 \u3000\u3000\u7528\u4e00\u6839\u624b\u6307\u52fe\u8d77\u7f8a\u76ae\u888b\u4e0a\u7684\u67c4\uff0c\u628a\u7f8a\u76ae\u9152\u888b\u7529\u5728\u8116\u5b50\u540e\uff0c\u73ed\u5bdf\u5df4\u90a3\u81ea\u5df1\u5148\u559d\u4e86\u4e00\u5927\u53e3\uff0c\u624d\u628a\u9152\u888b\u9012\u7ed9\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u4f60\u559d\uff01\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u559d\u4e86\u4e00\u5927\u53e3\uff0c\u597d\u5927\u7684\u4e00\u5927\u53e3\uff0c\u7136\u540e\u53c8\u8f6e\u5230\u73ed\u5bdf\u5df4\u90a3\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u6ca1\u6709\u53bb\u770b\u82cf\u82cf\u548c\u201c\u9633\u5149\u201d\uff0c\u5c31\u597d\u50cf\u8fd9\u5c4b\u5b50\u91cc\u6839\u672c\u5c31\u6ca1\u6709\u522b\u4eba\u5b58\u5728\u3002 \u3000\u3000\u201c\u4f60\u559d\u8fc7\u8fd9\u79cd\u9152\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4f60\u5f53\u7136\u4e5f\u8bb0\u5f97\u4e00\u9996\u6b4c\u3002\u201d \u3000\u3000\u201c\u6211\u8bb0\u5f97\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5148\u5531\uff0c\u6211\u6765\u548c\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5c31\u5531\u3002 \u3000\u3000\u201c\u2014\u2014\u513f\u987b\u6210\u540d\uff0c \u3000\u3000\u9152\u987b\u9189\uff0c \u3000\u3000\u9189\u540e\u503e\u8bc9\uff0c \u3000\u3000\u662f\u5fc3\u8a00\u3002 \u3000\u3000\u4ed6\u4eec\u5531\u4e86\u4e00\u904d\u53c8\u4e00\u904d\uff0c\u559d\u4e86\u4e00\u53e3\u53c8\u4e00\u53e3\uff0c\u4ed6\u4eec\u5531\u7684\u6b4c\u6d53\u70c8\u5982\u9152\uff0c\u4ed6\u4eec\u559d\u7684\u9152\u6bd4\u8840\u8fd8\u6d53\u3002 \u3000\u3000\u6b4c\u53ef\u4ee5\u5531\u4e0d\u505c\uff0c\u9152\u5374\u53ef\u4ee5\u559d\u5f97\u5149\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u7528\u529b\u4e00\u62cd\u684c\u5b50\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u201d\u4ed6\u770b\u7740\u5c0f\u65b9\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4ece\u6765\u6ca1\u6709\u628a\u6211\u5f53\u4f5c\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u54e6\uff01\u201d \u3000\u3000\u201c\u4f60\u4e00\u76f4\u90fd\u8ba4\u4e3a\u53ea\u6709\u535c\u9e70\u624d\u662f\u597d\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u4ed6\u672c\u6765\u5c31\u662f\u4e2a\u597d\u670b\u53cb\uff0c\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u4e0d\u4f46\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u4e5f\u662f\u4f60\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u76f4\u90fd\u4e0d\u6765\u627e\u4f60\uff1f\u4e5f\u4e0d\u6765\u627e\u6211\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u76ef\u7740\u5c0f\u65b9\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u4ed6\u65e0\u6cd5\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u9664\u4e86\u535c\u9e70\u81ea\u5df1\u5916\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u540c\u6837\u7684\u95ee\u9898\u4ed6\u4e5f\u4e0d\u77e5\u9053\u95ee\u8fc7\u81ea\u5df1\u591a\u5c11\u6b21\uff0c\u6700\u8fd1\u4ed6\u5df2\u4e0d\u518d\u95ee\u4e86\uff0c\u56e0\u4e3a\u8fd9\u95ee\u9898\u603b\u662f\u4f1a\u523a\u4f24\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e5f\u6ca1\u6709\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e5f\u5728\u559d\u9152\uff0c\u559d\u5f97\u5e76\u4e0d\u6bd4\u5c0f\u65b9\u5c11\u3002 \u3000\u3000\u5c0f\u65b9\u4ece\u672a\u60f3\u5230\u4e00\u5411\u51b7\u9177\u575a\u5b9a\u5982\u5ca9\u77f3\u7684\u73ed\u5bdf\u5df4\u90a3\u4e5f\u4f1a\u559d\u8fd9\u4e48\u591a\u9152\u3002 \u3000\u3000\u4ed6\u63e1\u7d27\u7f8a\u76ae\u9152\u888b\uff0c\u6ca1\u6709\u518d\u9012\u7ed9\u73ed\u5bdf\u5df4\u90a3\uff0c\u6709\u5f88\u591a\u4e8b\u4e00\u5b9a\u8981\u5728\u4ed6\u4eec\u8fd8\u6ca1\u6709\u559d\u9189\u65f6\u540c\u6e05\u695a\u3002 \u3000\u3000\u53ef\u662f\u73ed\u5bdf\u5df4\u90a3\u53c8\u5728\u95ee\u4ed6\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u770b\u6e05\u695a\u9e70\u8bb0\u5546\u53f7\u91cc\u90a3\u51e0\u4e2a\u8721\u50cf\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u201c\u4ee5\u524d\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u8fc7\u94f8\u9020\u5f97\u90a3\u4e48\u7cbe\u7f8e\u751f\u52a8\u7684\u8721\u50cf\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u201c\u4f60\u5f53\u7136\u6ca1\u6709\u770b\u89c1\u8fc7\uff01\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u90a3\u6837\u7684\u8721\u50cf\uff0c\u4ee5\u524d\u6839\u672c\u8fd8\u6ca1\u6709\u5728\u4e2d\u571f\u51fa\u73b0\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u666e\u5929\u4e4b\u4e0b\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u94f8\u9020\u51fa\u90a3\u6837\u7684\u8721\u50cf\u6765\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u7edd\u5bf9\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6717\u4f5b\u70c8\u91d1\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u975e\u5e38\u5947\u7279\u7684\u540d\u5b57\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u542c\u8fc7\u4e00\u6b21\u5c31\u4f1a\u7262\u8bb0\u5728\u5fc3\u3002 \u3000\u3000\u201c\u6717\u4f5b\u70c8\u91d1\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u5c06\u8fd9\u540d\u5b57\u53c8\u91cd\u590d\u4e00\u6b21\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u4ece\u672a\u542c\u8fc7\u8fd9\u540d\u5b57\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u786e\u4ece\u672a\u542c\u8fc7\u3002 \u3000\u3000\u201c\u4ed6\u662f\u4e0d\u662f\u6c49\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\uff01\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4ed6\u662f\u6ce2\u65af\u4eba\uff0c\u4f46\u662f\u4e00\u76f4\u4f4f\u5728\u4e00\u4e2a\u53eb\u82f1\u5409\u5229\u7684\u6d77\u5c9b\u3002\u201d \u3000\u3000\u201c\u82f1\u5409\u5229\uff1f\u201d\u5c0f\u65b9\u4e5f\u4ece\u672a\u542c\u8fc7\u8fd9\u6d77\u5c9b\u7684\u540d\u5b57\uff1b\u201c\u82f1\u5409\u5229\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5728\u5929\u4e4b\u6daf\uff0c\u6d77\u4e4b\u89d2\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5728\u4e00\u4e2a\u6211\u4eec\u90fd\u4ece\u6765\u6ca1\u6709\u53bb\u8fc7\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u94f8\u9020\u7684\u8721\u50cf\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6717\u4f5b\u70c8\u91d1\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\u3002 \u3000\u3000\u201c\u4ed6\u600e\u4e48\u4f1a\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u88ab\u4eba\u8bf7\u6765\u7684\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4ed6\u662f\u4e2a\u5947\u4eba\uff0c\u4ed6\u94f8\u51fa\u7684\u8721\u50cf\u5929\u4e0b\u65e0\u4eba\u80fd\u53ca\uff0c\u53ef\u662f\u4ed6\u4e5f\u8981\u751f\u5b58\u4e5f\u8981\u5403\u996d\uff0c\u53ea\u8981\u6709\u4eba\u80af\u51fa\u91cd\u4ef7\uff0c\u4ec0\u4e48\u5730\u65b9\u4ed6\u90fd\u4f1a\u53bb\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u88ab\u8c01\u8bf7\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u597d\u50cf\u4e5f\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u8bf7\u5f97\u8d77\u4ed6\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4f60\u5e94\u8be5\u80fd\u60f3\u5230\u6211\u8bf4\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u60f3\u5230\u4e86\u3002 \u3000\u3000\u2014\u2014\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u4ed8\u5f97\u51fa\u8fd9\u4e48\u5927\u7684\u4ee3\u4ef7\uff0c\u4e5f\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u505a\u5f97\u51fa\u8fd9\u6837\u7684\u4e8b\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u7684\u662f\u5415\u4e09\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u4ed6\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u201c\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u7279\u5730\u8bf7\u6717\u4f5b\u70c8\u91d1\u5230\u8fd9\u91cc\u6765\u4e25\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u8981\u4ed6\u6765\u505a\u90a3\u51e0\u4e2a\u8721\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u5f88\u591a\u79cd\u539f\u56e0\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6700\u4e3b\u8981\u7684\u4e00\u79cd\uff0c\u5c31\u662f\u4ed6\u8981\u7528\u90a3\u4e9b\u8721\u50cf\u6765\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u6740\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u5176\u5b9e\u4e0d\u8be5\u95ee\u4e5f\u4e0d\u5fc5\u95ee\u7684\uff0c\u53ef\u662f\u73ed\u5bdf\u5df4\u90a3\u8fd8\u662f\u56de\u7b54\uff1a\u201c\u6740\u4f60\uff0c\u6740\u6211\uff0c\u6740\u535c\u9e70\uff01\u201d \u3000\u3000\u51e0\u4e2a\u6ca1\u6709\u751f\u547d\uff0c\u6ca1\u6709\u8840\u8089\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u7684\u8721\u50cf\uff0c\u600e\u4e48\u80fd\u6740\u4eba\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u89e3\u91ca\uff1a\u201c\u90a3\u4e9b\u8721\u50cf\u90fd\u662f\u7a7a\u7684\uff0c\u6bcf\u4e2a\u8721\u50cf\u91cc\u90fd\u85cf\u7740\u4e00\u4e2a\u4eba\uff0c\u5176\u4e2d\u6709\u4f7f\u6bd2\u7684\u9ad8\u624b\uff0c\u4e5f\u6709\u6697\u5668\u540d\u5bb6\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4f7f\u51fa\u6765\u7684\u6bd2\uff0c\u5f53\u7136\u90fd\u662f\u65e0\u8272\u65e0\u5473\u8ba9\u4eba\u5b8c\u5168\u89c9\u5bdf\u4e0d\u51fa\u7684\u5267\u6bd2\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u6697\u5668\uff0c\u5f53\u7136\u90fd\u662f\u4ece\u673a\u7c27\u9488\u7b52\u53d1\u51fa\u6765\u7684\u8ba9\u4eba\u770b\u4e0d\u89c1\u7684\u6697\u5668\u3002 \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4e0d\u7ba1\u4ec0\u4e48\u4eba\u53ea\u8981\u4e00\u8d70\u8fdb\u9e70\u8bb0\u5546\u53f7\u7684\u5927\u95e8\uff0c\u5c31\u4f1a\u7a81\u7136\u66b4\u6b7b\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4e0d\u7ba1\u4ec0\u4e48\u4eba\u53ea\u8981\u4e00\u8d70\u8fdb\u53bb\u90fd\u5fc5\u6b7b\u65e0\u7591\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\uff1a\u201c\u4eba\u6b7b\u7684\u591a\u4e86\uff0c\u6211\u4eec\u5f53\u7136\u5c31\u4f1a\u77e5\u9053\uff0c\u4e0d\u7ba1\u6211\u4eec\u5728\u4ec0\u4e48\u5730\u65b9\u90fd\u4f1a\u542c\u5230\u8fd9\u6d88\u606f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u66ff\u4ed6\u63a5\u7740\u8bf4\u4e0b\u53bb\uff1a\u4eba\u5982\u679c\u6211\u4eec\u77e5\u9053\u4e86\u8fd9\u6d88\u606f\uff0c\u5f53\u7136\u5fcd\u4e0d\u4f4f\u8981\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u4eec\u8fd8\u6ca1\u6709\u770b\u51fa\u90a3\u4e9b\u8721\u50cf\u4e2d\u7684\u79d8\u5bc6\uff0c\u4e00\u8fdb\u53bb\u5f53\u7136\u4e5f\u5fc5\u6b7b\u65e0\u7591\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u5df2\u7ecf\u6b7b\u8fc7\u4e00\u6b21\u3002 \u3000\u3000\u201c\u5e78\u597d\u4f60\u5df2\u7ecf\u770b\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6211\u5df2\u7ecf\u770b\u51fa\u6765\u4e86\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4f60\u4e5f\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u957f\u957f\u5410\u51fa\u4e00\u53e3\u6c14\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u6709\u4e00\u70b9\u6211\u8fd8\u662f\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u90a3\u5bf9\u773c\u775b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53ef\u60f3\u8d77\u4e86\u90a3\u6761\u6bd2\u86c7\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u770b\u4e86\u5b83\u4e00\u773c\uff0c\u597d\u50cf\u5c31\u5df2\u7ecf\u4e2d\u6bd2\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u901a\u90a3\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u90a3\u5e76\u4e0d\u662f\u5f88\u96be\u89e3\u91ca\u7684\u4e8b\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u9047\u5230\u8fc7\u77f3\u773c\u75c5\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u9047\u5230\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u53bb\u770b\u8fc7\u90a3\u4e9b\u4eba\u7684\u773c\u775b\uff1f\u201d \u3000\u3000\u201c\u6709\u65f6\u6211\u96be\u514d\u4e5f\u4f1a\u53bb\u770b\u4e24\u773c\u3002\u201d \u3000\u3000\u201c\u770b\u8fc7\u4e86\u4e4b\u540e\u4f60\u6709\u4ec0\u4e48\u611f\u89c9\uff1f\u201d \u3000\u3000\u201c\u6211\u4f1a\u89c9\u5f97\u6211\u81ea\u5df1\u7684\u773c\u775b\u4e5f\u5f88\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u770b\u5f97\u4e45\u4e9b\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u81ea\u5df1\u4e5f\u4f1a\u88ab\u67d3\u4e0a\u540c\u6837\u7684\u773c\u75c5\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u5982\u679c\u4f60\u4ed4\u7ec6\u60f3\u60f3\uff0c\u4f60\u4e00\u5b9a\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u786e\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff1a\u201c\u53ef\u662f\u6211\u4e0d\u61c2\u90a3\u662f\u56e0\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u4e2d\u4e86\u6bd2\u3002\u201d \u3000\u3000\u201c\u4e2d\u6bd2\uff1f\u201d\u5c0f\u65b9\u5947\u602a\uff1a\u201c\u600e\u4e48\u4f1a\u4e2d\u6bd2\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u90a3\u4e2a\u4eba\u7684\u75c5\u773c\u4e2d\u6709\u4e00\u79cd\u4f1a\u4f20\u7ed9\u522b\u4eba\u7684\u75c5\u6bd2\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u81f3\u5c11\u6709\u4e24\u4e09\u79cd\u773c\u75c5\u90fd\u6709\u8fd9\u79cd\u75c5\u6bd2\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u53ea\u4e0d\u8fc7\u770b\u4e86\u4ed6\u4e24\u773c\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u770b\u4e24\u773c\u5c31\u5df2\u7ecf\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u516d\u7ae0\u3000\u8be5\u4e0b\u5730\u72f1\u7684\u65f6\u5019 \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u8be5\u4e0b\u5730\u72f1\u7684\u65f6\u5019 \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u79cd\u75c5\u6bd2\u672c\u6765\u5c31\u662f\u4ece\u773c\u775b\u4f20\u67d3\u7684\uff0c\u4f60\u53ea\u8981\u770b\u4e00\u773c\u5c31\u53ef\u80fd\u88ab\u67d3\u4e0a\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4e16\u754c\u4e0a\u6709\u5f88\u591a\u79cd\u75c5\u6bd2\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u4f60\u53ea\u8981\u8ddf\u75c5\u60a3\u8005\u540c\u65f6\u5f85\u5728\u4e00\u95f4\u5c4b\u5b50\u91cc\uff0c\u5c31\u53ef\u80fd\u88ab\u67d3\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u89e3\u91ca\u5f97\u8be6\u7ec6\u800c\u6e05\u695a\uff1a\u201c\u5982\u679c\u6709\u4eba\u80fd\u5229\u7528\u8fd9\u4e9b\u75c5\u6bd2\u7684\u7279\u6027\u70bc\u6210\u6bd2\u836f\uff0c\u4f60\u53ea\u8981\u770b\u4ed6\u4e00\u773c\u4e5f\u540c\u6837\u4f1a\u4e2d\u6bd2\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u8bf4\uff1b\u201c\u8fd9\u5f53\u7136\u4e0d\u662f\u5bb9\u6613\u7684\u4e8b\uff0c\u53ef\u662f\u6211\u77e5\u9053\u7684\u786e\u6709\u4eba\u5df2\u7ecf\u70bc\u6210\u4e86\u8fd9\u79cd\u6bd2\u836f\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u660e\u767d\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u8fc7\u90a3\u4e9b\u8dea\u7740\u6b7b\u7684\u4eba\uff0c\u6b7b\u4e86\u4e4b\u540e\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u600e\u4e48\u4e2d\u6bd2\u7684\u3002 \u3000\u3000\u5728\u6ca1\u6709\u542c\u5230\u73ed\u5bdf\u5df4\u90a3\u8fd9\u756a\u8bdd\u4e4b\u524d\uff0c\u4ed6\u4e5f\u540c\u6837\u4ece\u672a\u68a6\u60f3\u5230\u4e16\u4e0a\u7adf\u4f1a\u6709\u8fd9\u4e48\u53ef\u6015\u7684\u6bd2\u836f\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u53c8\u95ee\u4ed6\uff1a\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u90a3\u4e2a\u603b\u662f\u559c\u6b22\u62b1\u7740\u6761\u5c0f\u767d\u72d7\u7684\u5c0f\u5973\u5b69\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u8bb0\u5f97\u3002 \u3000\u3000\u201c\u85cf\u5728\u4f60\u90a3\u4e2a\u8721\u50cf\u91cc\u7684\u4eba\u5c31\u662f\u5979\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u867d\u7136\u53ea\u4e0d\u8fc7\u770b\u4e86\u5979\u4e00\u773c\uff0c\u5c31\u5df2\u7ecf\u4e2d\u4e86\u5979\u7684\u6bd2\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u65e0\u8272\u65e0\u5473\u7684\u65e0\u5f71\u4e4b\u6bd2\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u65e0\u8bba\u4ec0\u4e48\u4eba\u53ea\u8981\u4e00\u8d70\u8fdb\u9e70\u8bb0\u7684\u5927\u95e8\u90fd\u4f1a\u7a81\u7136\u66b4\u6bd9\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u795e\u8272\u51dd\u91cd\uff1a\u201c\u90a3\u4e0d\u662f\u9b54\u6cd5\uff0c\u4e5f\u4e0d\u662f\u5deb\u672f\uff0c\u90a3\u662f\u7ecf\u8fc7\u82e6\u5fc3\u7814\u7a76\u3001\u7cbe\u5fc3\u63d0\u70bc\u51fa\u6765\u7684\u5267\u6bd2\uff0c\u8981\u907f\u514d\u4e2d\u6bd2\u5df2\u7ecf\u5f88\u96be\uff0c\u8981\u7834\u89e3\u66f4\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u53ea\u4e0d\u8fc7\u4f60\u8fd8\u662f\u60f3\u51fa\u4e86\u7834\u89e3\u5b83\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u60f3\u4e86\u5f88\u4e45\uff0c\u8ba1\u5212\u4e86\u5f88\u4e45\u3002\u201d \u3000\u3000\u201c\u4f60\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u201c\u7528\u706b\u653b\uff01\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ea\u6709\u7528\u706b\u653b\uff0c\u624d\u80fd\u628a\u4ed6\u4eec\u5168\u90e8\u6d88\u706d\u3002\u201d\u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u6211\u51fb\u843d\u5e9e\u8001\u4e8c\u7684\u98de\u65a7\uff0c\u5c31\u56e0\u4e3a\u6211\u6df1\u6015\u4ed6\u4eec\u5f71\u54cd\u6211\u7684\u8ba1\u5212\uff0c\u53ef\u662f\u6211\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u4f1a\u4e0d\u987e\u4e00\u5207\u51b2\u8fdb\u53bb\uff1f\u201d\u4ed6\u770b\u7740\u5c0f\u65b9\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u5df2\u7ecf\u662f\u4e2a\u5f88\u51b7\u9759\u3001\u5f88\u6c89\u5f97\u4f4f\u6c14\u7684\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4e5f\u4ee5\u4e3a\u81ea\u5df1\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u65b9\u5f53\u7136\u5df2\u660e\u767d\uff0c\u5730\u72f1\u4e2d\u7684\u706b\u7130\u5e76\u4e0d\u662f\u5e7b\u60f3\u3002 \u3000\u3000\u706b\u7130\u878d\u5316\u4e86\u8721\u50cf\uff0c\u70e7\u6bc1\u4e86\u623f\u5c4b\uff0c\u85cf\u5728\u8721\u50cf\u4e2d\u7684\u4eba\u53ea\u6709\u9003\u51fa\u6765\u3002 \u3000\u3000\u53ea\u8981\u4e00\u9003\u51fa\u6765\uff0c\u6709\u8c01\u80fd\u8eb2\u5f97\u5f00\u201c\u4e94\u82b1\u7bad\u795e\u201d\u7684\u4e94\u82b1\u795e\u7bad\u3002\u5c0f\u65b9\u5ffd\u7136\u53c8\u8bf4\uff1b\u201c\u6211\u8fd8\u662f\u6709\u4ef6\u4e8b\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u5df2\u7ecf\u77e5\u9053\u8721\u50cf\u4e2d\u6709\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u7528\u4f60\u7684\u7bad\u5c04\u6740\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u76ef\u7740\u5c0f\u65b9\uff0c\u773c\u795e\u4e2d\u53c8\u5145\u6ee1\u8ba5\u6d88\uff0c\u51b7\u51b7\u5730\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8721\u50cf\u4e2d\u85cf\u7684\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u6211\u4e0d\u6562\u90a3\u4e48\u505a\uff0c\u201d\u73ed\u5bdf\u5df2\u90a3\u9053\uff1a\u201c\u5982\u679c\u6211\u505a\u4e86\uff0c\u4e0d\u4f46\u6211\u5fc5\u5c06\u540e\u6094\u7ec8\u751f\uff0c\u4f60\u4e5f\u4f1a\u6068\u6211\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u4e0d\u56de\u7b54\u5374\u53cd\u95ee\uff1a\u201c\u82cf\u82cf\u7684\u8721\u50cf\u4e2d\u4e5f\u85cf\u7740\u4e00\u4e2a\u4eba\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d\u201c\u5c31\u662f\u5979\u81ea\u5df1\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff0c\u201c\u5415\u4e09\u5c06\u5979\u548c\u90a3\u4e2a\u5b69\u5b50\u90fd\u85cf\u5728\u4ed6\u4eec\u81ea\u5df1\u7684\u8721\u50cf\u91cc\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u6211\u4eec\u53bb\u51fb\u6740\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4ed6\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u90a3\u65f6\u4f60\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4e2a\u5b69\u5b50\u662f\u4e0d\u662f\u4f60\u7684\u5b69\u5b50\uff0c\u5982\u679c\u6211\u5c06\u4ed6\u4eec\u6bcd\u5b50\u5c04\u6740\u5728\u6211\u7684\u7bad\u4e0b\uff0c\u4f60\u4f1a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6014\u4f4f\uff0c\u624b\u811a\u51b0\u51b7\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4e00\u76f4\u8ba4\u4e3a\u81ea\u5df1\u5df2\u7ecf\u5b66\u4f1a\u4e86\u5f88\u591a\uff0c\u73b0\u5728\u624d\u77e5\u9053\u81ea\u5df1\u8fd8\u5e94\u8be5\u53bb\u5b66\u7684\u5730\u65b9\u66f4\u591a\u3002 \u3000\u3000\u4ed6\u770b\u7740\u5750\u5728\u4ed6\u5bf9\u9762\u8fd9\u4e2a\u53c8\u6e29\u67d4\u53c8\u7c97\u65f7\u53c8\u51b7\u9177\u53c8\u70ed\u60c5\u7684\u4eba\uff0c\u5ffd\u7136\u5bf9\u8fd9\u4e2a\u4eba\u751f\u51fa\u4e86\u4e00\u79cd\u524d\u6240\u672a\u6709\u7684\u4f69\u670d\u4e0e\u5c0a\u656c\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u8bf4\uff1a\u201c\u5415\u4e09\u4e0d\u8fdc\u5343\u91cc\u5c06\u6717\u4f5b\u70c8\u91d1\u8bf7\u6765\u94f8\u4f5c\u90a3\u4e9b\u8721\u50cf\uff0c\u4e0d\u4ec5\u662f\u4e3a\u4e86\u8981\u8bf1\u6740\u6211\u4eec\uff0c\u201d\u4ed6\u51b7\u7b11\uff1a\u201c\u5415\u4e09\u4e5f\u77e5\u9053\u6211\u4eec\u90fd\u4e0d\u662f\u5f88\u5bb9\u6613\u5c31\u4f1a\u4e0a\u5f53\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u8fd8\u53e6\u6709\u76ee\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4ed6\u8fd8\u8981\u5236\u9020\u6211\u4eec\u4e4b\u95f4\u7684\u8bef\u4f1a\u4e0e\u4ec7\u6068\u3002\u201d \u3000\u3000\u5c0f\u65b9\u95ed\u7740\u5634\uff0c\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u535c\u9e70\u662f\u4eba\u6770\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u3001\u673a\u667a\u548c\u7edf\u5fa1\u5c5e\u4e0b\u7684\u80fd\u529b\u90fd\u662f\u524d\u6240\u672a\u6709\u7684\uff0c\u4ed6\u7a81\u7136\u88ab\u88ad\u60e8\u8d25\uff0c\u522b\u4eba\u662f\u4e0d\u662f\u4f1a\u60f3\u5230\u4ed6\u662f\u88ab\u4eba\u51fa\u5356\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u201c\u522b\u4eba\u4e00\u5b9a\u4e5f\u4f1a\u60f3\u5230\uff0c\u80fd\u51fa\u5356\u4ed6\u8fd9\u79cd\u4eba\u7684\uff0c\u4e00\u5b9a\u662f\u4ed6\u6700\u4eb2\u8fd1\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff1a\u201c\u8fd1\u5341\u5e74\u6765\uff0c\u4ed6\u6700\u4eb2\u8fd1\u7684\u670b\u53cb\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u8fde\u4f60\u90fd\u4f1a\u6000\u7591\u662f\u6211\u51fa\u5356\u4e86\u4ed6\u7684\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6709\u5f88\u591a\u8ff9\u8c61\u90fd\u4f1a\u8ba9\u4f60\u8fd9\u4e48\u60f3\uff0c\u6700\u91cd\u8981\u7684\u5f53\u7136\u8fd8\u662f\u90a3\u6279\u9ec4\u91d1\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u4ed6\u786e\u5b9e\u8fd9\u4e48\u60f3\u8fc7\uff0c\u77e5\u9053\u85cf\u91d1\u5904\u7684\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u73b0\u5728\u9ec4\u91d1\u5df2\u5931\u8e2a\uff0c\u4ed6\u81ea\u5df1\u6ca1\u6709\u52a8\u8fc7\u90a3\u6279\u9ec4\u91d1\uff0c\u535c\u9e70\u4e5f\u4e0d\u4f1a\u76d7\u81ea\u5df1\u7684\u85cf\u91d1\uff0c\u5acc\u7591\u6700\u5927\u7684\u5f53\u7136\u662f\u73ed\u5bdf\u5df4\u90a3\u3002 \u3000\u3000\u201c\u5982\u679c\u535c\u9e70\u8fd8\u6d3b\u7740\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u81ea\u5df1\u90fd\u4f1a\u8fd9\u4e48\u60f3\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5982\u679c\u6709\u673a\u4f1a\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4e5f\u4f1a\u5c06\u6211\u523a\u6740\u5728\u4ed6\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u518d\u6b21\u4e3e\u676f\u5411\u5c0f\u65b9\uff1a\u201c\u5c31\u7b97\u4ed6\u76f8\u4fe1\u6211\uff0c\u4f60\u4e5f\u4f1a\u8fd9\u4e48\u60f3\u7684\uff0c\u5728\u4f60\u770b\u5230\u90a3\u4e9b\u8721\u50cf\u65f6\uff0c\u4f60\u4e5f\u8bb8\u5df2\u7ecf\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u770b\u5230\u535c\u9e70\u7684\u8721\u50cf\u523a\u6740\u73ed\u5bdf\u5df4\u90a3\u7684\u8721\u50cf\u65f6\uff0c\u4ed6\u4e0d\u4f46\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u751a\u81f3\u8fd8\u6000\u7591\u90a3\u4e9b\u8721\u50cf\u662f\u535c\u9e70\u7684\u8ba1\u5212\uff0c\u7528\u6765\u8bf1\u6740\u73ed\u5bdf\u5df4\u90a3\u7684\u8ba1\u5212\u3002 \u3000\u3000\u540c\u6837\u4ed6\u4e5f\u66fe\u6000\u7591\u8fd9\u662f\u73ed\u5bdf\u5df4\u90a3\u7528\u6765\u8bf1\u6740\u535c\u9e70\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u5b89\u9759\u5e7d\u7f8e\u7684\u9ec4\u660f\uff0c\u4e00\u95f4\u5b89\u9759\u5e7d\u96c5\u7684\u5c0f\u623f\uff0c\u4e24\u4e2a\u5b89\u9759\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u4e00\u4e2a\u521a\u521a\u7761\u7740\u7684\u5b69\u5b50\uff0c\u4e24\u76cf\u521a\u521a\u70b9\u71c3\u7684\u706f\uff0c\u4e00\u888b\u521a\u521a\u559d\u5b8c\u7684\u9152\uff0c\u4e00\u4ef6\u8be1\u79d8\u60ca\u4eba\u7684\u79d8\u5bc6\uff0c\u5f62\u6210\u4e86\u4e00\u79cd\u5c40\u5916\u4eba\u7edd\u5bf9\u65e0\u6cd5\u4e86\u89e3\u7684\u6c14\u6c1b\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u6c14\u6c1b\u4e0b\uff0c\u5c0f\u65b9\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u9192\u662f\u9189\uff1f\u662f\u9189\u662f\u9192\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u95ee\u4ed6\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u5b8c\u5168\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6447\u5934\uff0c\u4ed6\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u660e\u767d\u73ed\u5bdf\u5df4\u90a3\u7684\u610f\u601d\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u544a\u8bc9\u4ed6\uff1a\u201c\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u5e94\u8be5\u4e0b\u5730\u72f1\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u201c\u4e0b\u5730\u72f1\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u8c01\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f60\uff01\u201d\u73ed\u5bdf\u5df4\u90a3\u5c06\u6700\u540e\u51e0\u6ef4\u9152\u6ef4\u5165\u54bd\u5589\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u8bf4\uff1a\u201c\u4f60\u4e0b\u53bb\uff01\u201d \u3000\u3000\u591c\u8272\u6df1\u4e86\uff0c\u706f\u5149\u4eae\u4e86\uff1b\u591c\u8272\u8d8a\u6df1\uff0c\u706f\u5149\u8d8a\u4eae\u3002 \u3000\u3000\u2014\u2014\u4e16\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53d6\u51fa\u4e00\u5f20\u56fe\u94fa\u5728\u684c\u4e0a\uff0c\u4e00\u5f20\u7528\u8584\u7f8a\u76ae\u7eb8\u63cf\u51fa\u7684\u5730\u56fe\u3002 \u3000\u3000\u201c\u8fd9\u662f\u7389\u95e8\u5173\u5185\u5916\u5305\u62ec\u6208\u58c1\u62c9\u8428\u5723\u5cf0\u90fd\u5728\u5185\u7684\u4e00\u5f20\u5730\u56fe\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u8fd9\u5730\u533a\u4e4b\u5927\uff0c\u5e7f\u53ca\u4e94\u4e07\u4e94\u5343\u91cc\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\uff1a\u201c\u53ef\u662f\u5728\u8fd9\u5e7f\u5927\u7684\u5730\u57df\u4e2d\uff0c\u6709\u4eba\u70df\u7684\u5730\u65b9\u5e76\u4e0d\u5927\u591a\u3002\u201d \u3000\u3000\u5730\u56fe\u753b\u5f97\u5e76\u4e0d\u8be6\u7ec6\uff0c\u5e76\u6ca1\u6709\u753b\u51fa\u5c71\u5ddd\u6cb3\u5cb3\u7684\u5730\u5f62\uff0c\u53ea\u7528\u6731\u7802\u7b14\u70b9\u51fa\u4e86\u4e00\u4e9b\u91cd\u8981\u7684\u5e02\u4e61\u5c71\u6751\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u518d\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u6570\u4e00\u6570\uff0c\u8fd9\u5f20\u56fe\u4e0a\u7528\u6731\u7802\u7b14\u70b9\u8fc7\u7684\u5730\u65b9\u4e00\u5171\u6709\u591a\u5c11\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u6570\u8fc7\uff0c\u6240\u4ee5\u7acb\u523b\u5c31\u56de\u7b54\uff1a\u201c\u4e00\u5171\u6709\u4e00\u767e\u4e5d\u5341\u4e00\u5904\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u70b9\u5934\uff0c\u8868\u793a\u8d5e\u8bb8\u3002\u7136\u540e\u544a\u8bc9\u5c0f\u65b9\uff1a\u201c\u8fd9\u4e00\u767e\u4e5d\u5341\u4e00\u4e2a\u5730\u65b9\uff0c\u90fd\u662f\u5415\u4e09\u7684\u79d8\u5bc6\u5de2\u7a74\u6240\u5728\u5730\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\uff1a\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u4eec\u867d\u7136\u53ea\u67e5\u51fa\u8fd9\u4e48\u591a\uff0c\u53ef\u662f\u6211\u76f8\u4fe1\u4ed6\u5c31\u7b97\u8fd8\u6709\u5176\u4ed6\u5206\u8235\u3001\u79d8\u7a74\u3001\u6697\u5361\uff0c\u4e5f\u4e0d\u4f1a\u5927\u591a\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u4e5f\u76f8\u4fe1\u3002\u201d \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u5b8c\u5168\u4fe1\u4efb\u73ed\u5bdf\u5df4\u90a3\u7684\u624d\u80fd\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u4e00\u5b9a\u8981\u627e\u5230\u5415\u4e09\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u90fd\u4e00\u5b9a\u8981\u627e\u5230\u4ed6\u624d\u80fd\u89e3\u51b3\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u6211\u4eec\u4e00\u5b9a\u53ef\u4ee5\u5728\u8fd9\u4e9b\u5730\u65b9\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u76f8\u4fe1\uff0c\u53ea\u53ef\u60dc\u4ed6\u4eec\u5e94\u8981\u53bb\u627e\u7684\u5730\u65b9\u5b9e\u5728\u5927\u591a\u4e86\u3002 \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u7a76\u7adf\u5728\u54ea\u4e00\u4e2a\u5206\u8235\u79d8\u7a74\u91cc\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u65b9\u82e6\u7b11\u3002 \u3000\u3000\u2014\u2014\u4e00\u767e\u4e5d\u5341\u4e00\u4e2a\u5e02\u9547\u4e61\u6751\uff0c\u5206\u5e03\u5728\u5982\u6b64\u5e7f\u5927\u7684\u4e00\u4e2a\u533a\u57df\u91cc\uff0c\u53eb\u4ed6\u4eec\u5982\u4f55\u53bb\u627e\uff1f \u3000\u3000\u201c\u6211\u4eec\u867d\u7136\u65e9\u5c31\u67e5\u51fa\u4e86\u5415\u4e09\u7684\u7a9d\u5728\u4e9b\u4ec0\u4e48\u5730\u65b9\uff0c\u53ef\u662f\u6211\u4eec\u4e00\u76f4\u90fd\u6ca1\u6709\u52a8\u624b\u53bb\u627e\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4eec\u77e5\u9053\u627e\u4e0d\u5230\u4ed6\u7684\uff01\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u89e3\u91ca\uff1a\u201c\u6211\u4eec\u6ca1\u6709\u8fd9\u4e48\u591a\u7684\u4eba\u529b\uff0c\u53ef\u4ee5\u5206\u6210\u4e00\u767e\u4e5d\u5341\u4e00\u961f\u4eba\uff0c\u5206\u5934\u53bb\u627e\uff0c\u5c31\u7b97\u6211\u4eec\u80fd\u5206\u51fa\u6765\uff0c\u529b\u91cf\u5fc5\u5b9a\u4e5f\u5df1\u5f88\u8584\u5f31\u3002\u201d \u3000\u3000\u5c0f\u65b9\u540c\u610f\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u201c\u5415\u4e09\u7684\u884c\u8e2a\u6240\u5728\u4e4b\u5730\uff0c\u8b66\u536b\u6212\u5907\u4e00\u5b9a\u6781\u68ee\u4e25\uff0c\u5c31\u7b97\u6211\u4eec\u6709\u4eba\u80fd\u627e\u5230\u4ed6\uff0c\u4e5f\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u5206\u6790\u5f97\u5f88\u6e05\u695a\uff1b\u201c\u5982\u679c\u6211\u4eec\u4e00\u51fb\u4e0d\u4e2d\uff0c\u518d\u60f3\u627e\u4ed6\u5c31\u66f4\u96be\u4e86\u3002\u201d \u3000\u3000\u201c\u5b8c\u5168\u6b63\u786e\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u4eec\u7edd\u4e0d\u53ef\u8f7b\u4e3e\u5984\u52a8\uff0c\u7edd\u4e0d\u80fd\u6253\u8349\u60ca\u86c7\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u4eec\u7edd\u4e0d\u80fd\u505a\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u81f3\u5c11\u5df2\u7ecf\u60f3\u51fa\u4e86\u4e00\u201c\u4e2a\u5bf9\u4ed8\u4ed6\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u867d\u7136\u8fd8\u662f\u4e00\u4f6f\u627e\u4e0d\u5230\u4ed6\uff0c\u4f46\u5374\u53ef\u4ee5\u8981\u4ed6\u81ea\u5df1\u628a\u81ea\u5df1\u7684\u884c\u8e2a\u66b4\u9732\u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u65b9\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u771f\u7684\u6709\u628a\u63e1\u80fd\u505a\u5230\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u70b9\u5934\uff0c\u773c\u4e2d\u53c8\u9732\u51fa\u9e70\u96bc\u72e1\u72d0\u822c\u7684\u9510\u5149\uff0c\u6c89\u7740\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u542c\u542c\u6211\u7684\u8ba1\u5212\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u975e\u5e38\u60f3\uff01\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u8ba1\u5212\u662f\u8fd9\u6837\u5b50\u7684\u2014\u2014 \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u6211\u4eec\u4e00\u5b9a\u8981\u5148\u653e\u51fa\u6d88\u606f\uff0c\u8ba9\u5415\u4e09\u77e5\u9053\u6211\u4eec\u5df2\u7ecf\u67e5\u51fa\u4e86\u4ed6\u4e00\u767e\u4e5d\u5341\u4e00\u4e2a\u79d8\u5bc6\u85cf\u8eab\u5904\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6211\u4eec\u751a\u81f3\u4e0d\u59a8\u5c06\u8fd9\u5f20\u79d8\u56fe\u516c\u5f00\uff0c\u8ba9\u4ed6\u786e\u4fe1\u6211\u4eec\u5df2\u7ecf\u6709\u4e86\u8fd9\u79cd\u5b9e\u529b\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u5462\uff1f\u201d \u3000\u3000\u201c\u7ecf\u8fc7\u4e86\u8fd9\u6b21\u632b\u8d25\u4e4b\u540e\uff0c\u4ed6\u5bf9\u6211\u4eec\u7edd\u4e0d\u4f1a\u518d\u5b58\u8f7b\u654c\u4e4b\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4ed6\u4ece\u6765\u90fd\u6ca1\u6709\u8f7b\u89c6\u8fc7\u4f60\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u8c01\u4e5f\u4e0d\u6562\u8f7b\u89c6\u4f60\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u77e5\u9053\u6211\u4eec\u5df2\u7ecf\u5f00\u59cb\u51c6\u5907\u6709\u6240\u884c\u52a8\u4e4b\u540e\uff0c\u4e00\u5b9a\u4f1a\u4e25\u52a0\u6212\u5907\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4e0d\u7ba1\u4ed6\u5728\u54ea\u91cc\u4e00\u5b9a\u4f1a\u7acb\u523b\u8c03\u96c6\u4ed6\u5c5e\u4e0b\u7684\u9ad8\u624b\u5230\u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u660e\u767d\u4ed6\u7684\u610f\u601d\u2014\u2014 \u3000\u3000\u201c\u53ea\u8981\u4ed6\u4e00\u5f00\u59cb\u8c03\u534f\u4ed6\u5c5e\u4e0b\u7684\u9ad8\u624b\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u67e5\u51fa\u4ed6\u5728\u4ec0\u4e48\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u73ed\u5bdf\u5df4\u90a3\u5fae\u7b11\u70b9\u5934\uff1a\u201c\u6211\u7684\u8ba1\u5212\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u5c0f\u65b9\uff1a\u201c\u53ea\u4e0d\u8fc7\u8fd9\u9879\u884c\u52a8\u4ecd\u7136\u5f88\u51f6\u9669\uff0c\u5415\u4e09\u8d22\u96c4\u52bf\u5927\uff0c\u5c5e\u4e0b\u9ad8\u624b\u5982\u6797\uff0c\u6211\u4eec\u8fd8\u662f\u6ca1\u6709\u5fc5\u80dc\u7684\u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u8fd9\u6b21\u673a\u4f1a\u6211\u4eec\u7edd\u4e0d\u53ef\u60dc\u8fc7\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd9\u5df2\u7ecf\u662f\u6211\u4eec\u6700\u540e\u4e00\u6b21\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u5c31\u7b97\u660e\u77e5\u8981\u4e0b\u5730\u72f1\uff0c\u4e5f\u975e\u53bb\u4e0d\u53ef\uff01\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e0d\u80fd\u53bb\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u4f60\u8fd8\u6709\u522b\u7684\u4e8b\u8981\u505a\uff0c\u4f60\u4e0d\u80fd\u5192\u8fd9\u79cd\u9669\uff01\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\u5f97\u5f88\u5766\u767d\uff1b\u201c\u6240\u4ee5\u6211\u53ea\u6709\u8ba9\u4f60\u53bb\u3002\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5c0f\u65b9\uff1a\u201c\u5982\u679c\u6211\u4eec\u4e24\u4e2a\u4eba\u4e4b\u95f4\u4e00\u5b9a\u6709\u4e00\u4e2a\u4eba\u8981\u6b7b\uff0c\u6211\u4e5f\u53ea\u662f\u8ba9\u4f60\u53bb\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u53cd\u5e94\u5f88\u5947\u602a\u3002 \u3000\u3000\u4ed6\u65e2\u6ca1\u6709\u6124\u6012\u6fc0\u52a8\uff0c\u4e5f\u6ca1\u6709\u53cd\u5bf9\u6297\u8bae\uff0c\u53ea\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u597d\uff01\u6211\u53bb\u3002\u201d \u3000\u3000\u9ec4\u91d1\u8272\u7684\u5c4b\u5b50\uff0c\u9ec4\u91d1\u8272\u7684\u5899\uff0c\u9ec4\u91d1\u8272\u7684\u5730\uff0c\u9ec4\u91d1\u8272\u7684\u5c4b\u9876\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6bcf\u6837\u4e1c\u897f\u90fd\u662f\u9ec4\u91d1\u8272\u7684\u3002 \u3000\u3000\u7edd\u5bf9\u662f\u9ec4\u91d1\u8272\u7684\uff0c\u548c\u7eaf\u91d1\u5b8c\u5168\u4e00\u6837\u7684\u989c\u8272\u3002\u7edd\u5bf9\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5c4b\u5b50\u7684\u56db\u58c1\u548c\u9876\u90fd\u9540\u4e0a\u4e86\u4e00\u5c42\u7eaf\u91d1\uff0c\u5730\u4e0a\u94fa\u7684\u662f\u91d1\u7816\u3002\u5c4b\u5b50\u91cc\u6bcf\u4e00\u6837\u4e1c\u897f\u90fd\u662f\u9ec4\u91d1\u6240\u94f8\uff0c\u751a\u81f3\u8fde\u684c\u6905\u90fd\u662f\uff0c\u8fde\u7a97\u6162\u90fd\u662f\u7528\u91d1\u4e1d\u7f16\u6210\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u95f4\u5c4b\u5b50\u7684\u4e3b\u4eba\u559c\u6b22\u9ec4\u91d1\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u559c\u6b22\u9ec4\u91d1\u3002\u53ef\u662f\u4f4f\u5728\u4e00\u95ee\u8fd9\u4e48\u6837\u7684\u5c4b\u5b50\u91cc\uff0c\u5c31\u5f88\u5c11\u6709\u4eba\u80fd\u53d7\u5f97\u4e86\u3002 \u3000\u3000\u9ec4\u91d1\u867d\u7136\u53ef\u7231\uff0c\u4f46\u662f\u592a\u51b7\u3001\u592a\u786c\uff0c\u4e5f\u5927\u65e0\u60c5\u3002 \u3000\u3000\u5927\u591a\u6570\u4eba\u90fd\u5b81\u613f\u5750\u5728\u4e00\u5f20\u6302\u7740\u4e1d\u7ed2\u7a97\u9992\u7684\u5c4b\u5b50\u91cc\uff0c\u5750\u5728\u4e00\u5f20\u6709\u4e1d\u7ed2\u57ab\u5b50\u7684\u8f6f\u69bb\u4e0a\uff0c\u7528\u6c34\u6676\u676f\u559d\u9152\u3002 \u3000\u3000\u8fd9\u95f4\u5c4b\u5b50\u7684\u4e3b\u4eba\u5374\u559c\u6b22\u9ec4\u91d1\u3002 \u3000\u3000\u4ed6\u62e5\u6709\u7684\u9ec4\u91d1\u4e5f\u6bd4\u8fd9\u4e16\u754c\u4efb\u4f55\u4e00\u4e2a\u4eba\u90fd\u591a\u5f97\u591a\u3002 \u3000\u3000\u8fd9\u95ee\u5c4b\u5b50\u7684\u4e3b\u4eba\u5c31\u662f\u5415\u4e09\u3002 \u3000\u3000\u7528\u7eaf\u91d1\u94f8\u6210\u7684\u6905\u5b50\u867d\u7136\u51b0\u51b7\u575a\u786c\uff0c\u5415\u4e09\u5750\u5728\u4e0a\u9762\u5374\u663e\u5f97\u5f88\u8212\u670d\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5750\u5728\u8fd9\u95f4\u5c4b\u5b50\u91cc\uff0c\u9762\u5bf9\u7740\u8fd9\u4e9b\u7528\u7eaf\u91d1\u94f8\u6210\u7684\u4e1c\u897f\uff0c\u770b\u7740\u95ea\u52a8\u7684\u91d1\u5149\uff0c\u901a\u5e38\u5c31\u662f\u4ed6\u6700\u6109\u5feb\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u4e00\u4e2a\u4eba\u5f85\u5728\u8fd9\u5c4b\u5b50\u91cc\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u522b\u4eba\u6765\u5206\u4eab\u4ed6\u7684\u6109\u5feb\uff0c\u5c31\u6b63\u5982\u4ed6\u4e5f\u4e0d\u613f\u522b\u4eba\u6765\u5206\u4eab\u4ed6\u7684\u9ec4\u91d1\u4e00\u6837\u3002 \u3000\u3000\u6240\u4ee5\u5f88\u5c11\u6709\u4eba\u6562\u95ef\u8fdb\u4ed6\u8fd9\u5c4b\u5b50\u91cc\u6765\uff0c\u8fde\u4ed6\u6700\u4eb2\u8fd1\u7684\u4eba\u90fd\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u4eca\u5929\u5374\u6709\u4e86\u4f8b\u5916\u3002 \u3000\u3000\u9ec4\u91d1\u7684\u7eaf\u5ea6\u7edd\u5bf9\u6bd4\u91d1\u676f\u4e2d\u7684\u9187\u9152\u66f4\u7eaf\u3002 \u3000\u3000\u5415\u4e09\u6d45\u6d45\u5730\u5438\u4e86\u4e00\u53e3\u9152\uff0c\u628a\u4e00\u53cc\u4fdd\u517b\u5f97\u5f88\u597d\u7684\u6307\u7532\uff0c\u4fee\u526a\u5f97\u6781\u5e72\u51c0\u6574\u9f50\u7684\u8d64\u8db3\uff0c\u6446\u5728\u5bf9\u9762\u4e00\u5f20\u7528\u7eaf\u91d1\u94f8\u6210\u7684\u684c\u5b50\u4e0a\uff0c\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u653e\u677e\u4e86\u3002 \u3000\u3000\u53ea\u6709\u5728\u8fd9\u91cc\u4ed6\u624d\u4f1a\u559d\u9152\uff0c\u56e0\u4e3a\u53ea\u6709\u4ed6\u6700\u4eb2\u4fe1\u7684\u4eba\u624d\u77e5\u9053\u8fd9\u4e2a\u5730\u65b9\uff0c\u5c24\u5176\u662f\u5728\u4ed6\u559d\u9152\u7684\u65f6\u5019\uff0c\u66f4\u6ca1\u6709\u4eba\u6562\u6765\u6253\u6270\u4ed6\u3002 \u3000\u3000\u53ef\u662f\u4eca\u5929\u5c31\u5728\u4ed6\u6b63\u51c6\u5907\u559d\u7b2c\u4e8c\u676f\u7684\u65f6\u5019\uff0c\u5916\u9762\u5c45\u7136\u6709\u4eba\u5728\u6572\u95e8\uff0c\u800c\u4e14\u4e0d\u7b49\u4ed6\u5141\u8bb8\uff0c\u5c31\u5df2\u7ecf\u63a8\u5f00\u95e8\u95ef\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5415\u4e09\u5f88\u4e0d\u6109\u5feb\uff0c\u4f46\u662f\u4ed6\u8868\u9762\u4e0a\u8fde\u4e00\u70b9\u70b9\u90fd\u6ca1\u6709\u8868\u9732\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u5e76\u975e\u56e0\u4e3a\u6572\u95e8\u95ef\u8fdb\u6765\u7684\u4eba\u662f\u4ed6\u6700\u4eb2\u4fe1\u7684\u5c5e\u4e0b\u82d7\u5ba3\u3002 \u3000\u3000\u4ed6\u8868\u9762\u4e0a\u5b8c\u5168\u4e0d\u52a8\u58f0\u8272\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u672c\u6765\u5c31\u662f\u4e2a\u559c\u6012\u4e0d\u5f62\u5201\u8272\u7684\u4eba\uff0c\u5c31\u8fde\u4ed6\u542c\u5230\u4ed6\u72ec\u751f\u5b50\u6b7b\u5728\u5c0f\u65b9\u624b\u91cc\u7684\u65f6\u5019\uff0c\u4ed6\u8138\u4e0a\u90fd\u6ca1\u6709\u9732\u51fa\u4e00\u70b9\u60b2\u60e8\u6124\u6012\u7684\u795e\u8272\u3002 \u3000\u3000\u4ed6\u4e0d\u50cf\u73ed\u5bdf\u5df4\u90a3\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u8138\u5c31\u50cf\u82b1\u5c97\u77f3\uff0c\u4ece\u6765\u90fd\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u5415\u4e09\u7684\u8138\u4e0a\u6709\u8868\u60c5\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u901a\u5e38\u90fd\u8ddf\u4ed6\u5fc3\u91cc\u7684\u611f\u89c9\u4e0d\u4e00\u6837\u800c\u5df2\u3002\u73b0\u5728\u4ed6\u5fc3\u91cc\u867d\u7136\u5f88\u4e0d\u6109\u5feb\uff0c\u8138\u4e0a\u5374\u5e26\u7740\u5f88\u6109\u5feb\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u95ee\u82d7\u5ba3\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u559d\u676f\u9152\uff1f\u8981\u4e0d\u8981\u5750\u4e0b\u6765\u966a\u6211\u559d\u4e00\u676f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u60f3\u3002\u201d\u82d7\u5ba3\u8bf4\uff1a\u201c\u4e0d\u8981\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u50cf\u4ed6\u7684\u4e3b\u4eba\uff0c\u4ed6\u5fc3\u91cc\u6709\u4e86\u4e8b\u8138\u4e0a\u7acb\u523b\u5c31\u4f1a\u9732\u51fa\u6765\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u770b\u6765\u5c31\u597d\u50cf\u5bb6\u91cc\u521a\u521a\u5931\u4e86\u706b\u3002 \u3000\u3000\u201c\u6211\u4e0d\u60f3\u559d\u9152\uff0c\u4e5f\u4e0d\u8981\u559d\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u4e0d\u662f\u4e3a\u4e86\u559d\u9152\u800c\u6765\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u76f4\u80a0\u76f4\u809a\u76f4\u6027\u5b50\u7684\u4eba\uff0c\u867d\u7136\u4ed6\u81ea\u5df1\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u53ef\u662f\u4ed6\u559c\u6b22\u8fd9\u79cd\u4eba\uff0c\u56e0\u4e3a\u4ed6\u4e00\u5411\u8ba4\u4e3a\u8fd9\u79cd\u4eba\u6700\u597d\u9a7e\u9a6d\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u5c06\u82d7\u5ba3\u5f53\u4f5c\u4eb2\u4fe1\u3002 \u3000\u3000\u4ed6\u95f4\u82d7\u5ba3\uff1a\u201c\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u4e00\u4ef6\u5927\u4e8b\u3002\u201d\u82d7\u5ba3\u8bf4\uff1a\u201c\u4e3a\u4e86\u90a3\u4e2a\u73ed\u5bdf\u5df4\u90a3\u3002\u201d \u3000\u3000\u5415\u4e09\u4ecd\u7136\u5728\u5fae\u7b11\u3002 \u3000\u3000\u201c\u6709\u5173\u73ed\u5bdf\u5df4\u90a3\u7684\u4e8b\uff0c\u5f53\u7136\u90fd\u662f\u5927\u4e8b\u3002\u201d\u4ed6\u6307\u4e86\u6307\u5bf9\u9762\u7684\u6905\u5b50\uff1a\u201c\u4f60\u5750\u4e0b\u6765\u6162\u6162\u8bf4\u3002\u201d \u3000\u3000\u82d7\u5ba3\u8fd9\u6b21\u6ca1\u6709\u542c\u4ed6\u7684\u8bdd\uff0c\u6ca1\u6709\u5750\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u5df2\u7ecf\u628a\u6211\u4eec\u4e00\u767e\u4e5d\u5341\u4e00\u4e2a\u5206\u8235\u90fd\u67e5\u51fa\u6765\u4e86\uff0c\u800c\u4e14\u5df2\u7ecf\u4e0b\u4ee4\u8c03\u96c6\u4eba\u624b\uff0c\u53d1\u52a8\u653b\u51fb\u3002\u201d \u3000\u3000\u5415\u4e09\u975e\u4f46\u8138\u8272\u6ca1\u6709\u53d8\uff0c\u8fde\u5750\u7684\u59ff\u52bf\u90fd\u6ca1\u6709\u53d8\uff0c\u53ea\u662f\u6de1\u6de1\u5730\u95ee\uff1a\u201c\u4ed6\u51c6\u5907\u5728\u4ec0\u4e48\u65f6\u5019\u53d1\u52a8\u653b\u51fb\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df2\u90a3\u4e00\u5411\u4ee4\u51fa\u5982\u98ce\u3002\u201d\u82d7\u5ba3\u8bf4\uff1a\u201c\u73b0\u5728\u4ed6\u65e2\u7136\u5df2\u4e0b\u4ee4\uff0c\u4e0d\u51fa\u5341\u5929\uff0c\u5c31\u4f1a\u89c1\u5206\u6653\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u4e5f\u627f\u8ba4\u8fd9\u4e00\u70b9\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u4ee4\u51fa\u5982\u98ce\uff0c\u800c\u4e14\u4ee4\u51fa\u5982\u5c71\u3002\u201d \u3000\u3000\u4ed6\u53c8\u6d45\u6d45\u8f8d\u4e86\u4e00\u53e3\u9152\uff0c\u7136\u540e\u624d\u95f4\u82d7\u5ba3\u3002 \u3000\u3000\u201c\u4f60\u770b\u6211\u4eec\u73b0\u5728\u5e94\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u82d7\u5ba3\u6beb\u4e0d\u8003\u8651\u5c31\u56de\u7b54\uff1a\u201c\u6211\u4eec\u73b0\u5728\u5e94\u8be5\u7acb\u523b\u628a\u597d\u624b\u90fd\u8c03\u96c6\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u5c5e\u4e0b\u7684\u597d\u624b\uff0c\u867d\u7136\u4e5f\u6709\u4e0d\u5c11\uff0c\u4f46\u5374\u8981\u5206\u5230\u4e00\u767e\u4e5d\u5341\u4e00\u4e2a\u5730\u65b9\u53bb\u3002\u201d\u82d7\u5ba3\u8bf4\uff1a\u201c\u6211\u4eec\u5982\u679c\u80fd\u5c06\u597d\u624b\u90fd\u8c03\u96c6\u5230\u8fd9\u91cc\u6765\uff0c\u4ee5\u9038\u5f85\u52b3\uff0c\u4ee5\u4f17\u51fb\u5be1\uff0c\u8fd9\u4e00\u6b21\u4ed6\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u8138\u4e0a\u5df2\u7ecf\u5fcd\u4e0d\u4f4f\u9732\u51fa\u4e86\u5f97\u610f\u4e4b\u8272\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u8fd9\u662f\u4e2a\u597d\u4e3b\u610f\uff0c\u800c\u4e14\u76f8\u4fe1\u8fd9\u662f\u4e2a\u597d\u4e3b\u610f\u3002 \u3000\u3000\u5927\u591a\u6570\u7684\u60f3\u6cd5\u90fd\u4f1a\u8ddf\u4ed6\u4e00\u6837\uff0c\u90fd\u4f1a\u70ed\u70c8\u8d5e\u6210\u4ed6\u8fd9\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u5415\u4e09\u5374\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u91d1\u5149\u5728\u95ea\u52a8\uff0c\u676f\u4e2d\u7684\u9152\u4e5f\u6709\u91d1\u5149\u5728\u95ea\u52a8\uff0c\u4ed6\u770b\u7740\u676f\u4e2d\u9152\u4e0a\u7684\u95ea\u52a8\u91d1\u5149\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u4e4b\u540e\uff0c\u5ffd\u7136\u95ee\u51fa\u53e5\u5f88\u5947\u602a\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u82d7\u5ba3\uff1a\u201c\u4f60\u8ddf\u6211\u505a\u4e8b\u5df2\u7ecf\u6709\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u5341\u5e74\u3002\u201d\u82d7\u5ba3\u867d\u7136\u4e0d\u61c2\u5415\u4e09\u4e3a\u4ec0\u4e48\u4f1a\u5ffd\u7136\u95ee\u4ed6\u8fd9\u4ef6\u4e8b\uff0c\u4ecd\u7136\u7167\u5b9e\u56de\u7b54\uff1a\u201c\u6574\u6574\u5341\u5e74\u4e86\uff01\u201d \u3000\u3000\u5415\u4e09\u5ffd\u7136\u62ac\u8d77\u5934\u6765\u770b\u4ed6\uff0c\u770b\u7740\u4ed6\u4e11\u964b\u8bda\u5b9e\u800c\u5bcc\u4e8e\u8868\u60c5\u7684\u8138\u3002 \u3000\u3000\u5415\u4e09\u770b\u4e86\u5f88\u4e45\u4e4b\u540e\u624d\u8bf4\uff1a\u201c\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u201c\u4e0d\u5bf9\uff1f\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u5341\u5e74\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u662f\u4e5d\u5e74\u5341\u4e00\u4e2a\u6708\uff0c\u8981\u5230\u4e0b\u4e2a\u6708\u7684\u5341\u4e09\u624d\u6ee1\u5341\u5e74\u3002\u201d \u3000\u3000\u82d7\u5ba3\u5438\u4e86\u53e3\u6c14\uff0c\u8138\u4e0a\u9732\u51fa\u4e86\u4f69\u670d\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5415\u4e09\u7684\u8bb0\u5fc6\u529b\u4e00\u5411\u5f88\u597d\uff0c\u53ef\u662f\u5374\u60f3\u4e0d\u5230\u7adf\u7136\u597d\u5f97\u5982\u6b64\u60ca\u4eba\u3002 \u3000\u3000\u5415\u4e09\u8f7b\u8f7b\u6447\u8361\u7740\u676f\u4e2d\u7684\u9152\uff0c\u8ba9\u95ea\u52a8\u7684\u91d1\u5149\u770b\u6765\u66f4\u8000\u773c\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u8ddf\u7740\u6211\u7684\u65f6\u5019\u5df2\u7ecf\u4e0d\u7b97\u592a\u77ed\u4e86\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u5df2\u7ecf\u5e94\u8be5\u770b\u5f97\u51fa\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u591a\u5c11\u603b\u80fd\u770b\u5f97\u51fa\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u6700\u5927\u7684\u957f\u5904\u662f\u54ea\u4e00\u70b9\uff1f\u201d\u5415\u4e09\u53c8\u95ee\u3002 \u3000\u3000\u82d7\u5ba3\u8fd8\u5728\u8003\u8651\uff0c\u5415\u4e09\u5df2\u7ecf\u5148\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u6211\u6700\u5927\u7684\u957f\u5904\u5c31\u662f\u516c\u6b63\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u4e0d\u80fd\u4e0d\u516c\u6b63\uff0c\u8ddf\u6211\u505a\u4e8b\u7684\u4eba\u6700\u5c11\u65f6\u4e5f\u6709\u516b\u3001\u4e5d\u5343\u4e2a\u5982\u679c\u6211\u4e0d\u516c\u6b63\uff0c\u600e\u4e48\u80fd\u670d\u5f97\u4f4f\u4eba\uff1f\u201d \u3000\u3000\u82d7\u5ba3\u627f\u8ba4\u8fd9\u4e00\u70b9\uff0c\u5415\u4e09\u786e\u5b9e\u662f\u4e2a\u5904\u4e8b\u516c\u6b63\u7684\u4eba\u3002\u800c\u4e14\u7edd\u5bf9\u8d4f\u7f5a\u5206\u660e\u3002 \u3000\u3000\u5415\u4e09\u5ffd\u7136\u53c8\u95ee\u4ed6\uff1a\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u521a\u624d\u6211\u8fdb\u6765\u65f6\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u82d7\u5ba3\u8bb0\u5f97\uff1a\u2018\u4f60\u8bf4\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u51c6\u8d70\u8fdb\u8fd9\u5c4b\u5b50\u7684\u95e8\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u4eba\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u8fdb\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u4e00\u6837\u3002\u201d\u82d7\u5ba3\u5df2\u7ecf\u6709\u70b9\u53d1\u6025\uff1a\u201c\u6211\u6709\u8981\u7d27\u7684\u4e8b\u3002\u201d \u3000\u3000\u5415\u4e09\u6c89\u4e0b\u8138\u3002 \u3000\u3000\u4ed6\u7684\u8138\u5728\u95ea\u52a8\u7684\u91d1\u5149\u4e2d\u770b\u6765\u4e5f\u50cf\u662f\u9ec4\u91d1\u94f8\u6210\u7684\uff1a\u201c\u6211\u53ea\u95ee\u4f60\uff0c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u8fdb\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u82d7\u5ba3\u5fc3\u91cc\u867d\u7136\u4e0d\u670d\uff0c\u53ef\u662f\u518d\u4e5f\u4e0d\u6562\u53cd\u8fa8\u3002 \u3000\u3000\u5415\u4e09\u53c8\u53cd\u95f4\u4ed6\uff1a\u201c\u521a\u624d\u6211\u6709\u6ca1\u6709\u53eb\u4f60\u5750\u4e0b\u6765\u966a\u6211\u559d\u676f\u9152\u3002\u201d \u3000\u3000\u201c\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u5750\u4e0b\u6765\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u966a\u6211\u559d\u9152\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d \u3000\u3000\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u6211\u66fe\u7ecf\u8bf4\u8fc7\u7684\uff0c\u6211\u8bf4\u51fa\u6765\u7684\u8bdd\u5c31\u662f\u547d\u4ee4\u3002\u201d \u3000\u3000\u201c\u6211\u8bb0\u5f97\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5f53\u7136\u4e5f\u5e94\u8be5\u8bb0\u5f97\uff0c\u8fdd\u80cc\u6211\u547d\u4ee4\u7684\u4eba\u5e94\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8bf4\u8fc7\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5415\u4e09\u5c31\u518d\u4e5f\u4e0d\u53bb\u770b\u90a3\u5f20\u8bda\u5b9e\u800c\u4e11\u964b\u7684\u8138\u4e86\uff0c\u5c31\u597d\u50cf\u8fd9\u5c4b\u5b50\u91cc\u5df2\u7ecf\u4e0d\u518d\u6709\u82d7\u5ba3\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u5b58\u5728\u3002 \u3000\u3000\u82d7\u5ba3\u7684\u8138\u8272\u5df2\u7ecf\u53d8\u6210\u50cf\u662f\u5f20\u767d\u7eb8\uff0c\u7d27\u63e1\u7684\u53cc\u62f3\u4e0a\u9752\u7b4b\u4e00\u6839\u6839\u51f8\u8d77\uff0c\u770b\u8d77\u6765\u597d\u50cf\u6068\u4e0d\u5f97\u4e00\u62f3\u5f80\u5415\u4e09\u7684\u9f3b\u5b50\u4e0a\u6253\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8fd9\u4e48\u505a\uff0c\u4ed6\u4e0d\u6562\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u6015\u6b7b\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u53ea\u56e0\u4e3a\u4e09\u5e74\u524d\u5df2\u7ecf\u5a36\u4e86\u59bb\uff0c\u4ed6\u7684\u59bb\u5b50\u5df2\u7ecf\u4e3a\u4ed6\u751f\u4e86\u4e2a\u513f\u5b50\u3002 \u3000\u3000\u4e00\u4e2a\u53c8\u767d\u53c8\u80d6\u53c8\u53ef\u7231\u7684\u513f\u5b50\uff0c\u4eca\u5929\u65e9\u4e0a\u521a\u521a\u5b66\u4f1a\u53eb\u4ed6\u201c\u7238\u7238\u201d\u3002 \u3000\u3000\u4e00\u7c92\u7c92\u6bd4\u9ec4\u8c46\u8fd8\u5927\u7684\u51b7\u6c57\u5df2\u7ecf\u4ece\u82d7\u5ba3\u8138\u4e0a\u6d41\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u7528\u4ed6\u90a3\u53cc\u9752\u7b4b\u51f8\u8d77\u7684\u624b\uff0c\u4ece\u8eab\u4e0a\u62d4\u51fa\u4e00\u628a\u5200\uff0c\u5200\u950b\u8584\u800c\u5229\uff0c\u8f7b\u8f7b\u4e00\u523a\u5c31\u53ef\u4ee5\u523a\u5165\u4eba\u7684\u5fc3\u810f\u3002 \u3000\u3000\u5982\u679c\u662f\u5728\u4e09\u5e74\u524d\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u7528\u8fd9\u628a\u5200\u5f80\u5415\u4e09\u7684\u5fc3\u53e3\u4e0a\u523a\u8fc7\u53bb\uff0c\u4e0d\u7ba1\u6210\u8d25\u4ed6\u90fd\u4f1a\u8bd5\u4e00\u8bd5\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u4e0d\u6562\uff0c\u8fde\u8bd5\u90fd\u4e0d\u6562\u8bd5\u3002 \u3000\u3000\u2014\u2014\u53ef\u7231\u7684\u513f\u5b50\uff0c\u53ef\u7231\u7684\u7b11\u8138\uff0c\u53eb\u8d77\u201c\u7238\u7238\u201d\u6765\u7b11\u5f97\u591a\u4e48\u53ef\u7231\u3002 \u3000\u3000\u82d7\u5ba3\u5ffd\u7136\u4e00\u5200\u523a\u51fa\uff0c\u523a\u5165\u4e86\u81ea\u5df1\u7684\u5fc3\u810f\u3002 \u3000\u3000\u82d7\u5ba3\u5012\u4e0b\u53bb\uff0c\u773c\u524d\u4eff\u4f5b\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u5e45\u7f8e\u4e3d\u7684\u56fe\u753b\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u770b\u89c1\u4ed6\u7684\u513f\u5b50\u5728\u6210\u957f\uff0c\u957f\u6210\u4e3a\u4e00\u4e2a\u5e62\u5eb7\u5f3a\u58ee\u7684\u5c11\u5e74\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u770b\u89c1\u4ed6\u90a3\u867d\u7136\u4e0d\u592a\u7f8e\u4e3d\uff0c\u4f46\u5374\u975e\u5e38\u6e29\u67d4\u7684\u59bb\u5b50\u6b63\u5728\u4e3a\u4ed6\u4eec\u7684\u513f\u5b50\u9003\u9009\u65b0\u5a18\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4e5f\u77e5\u9053\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4ed6\u4e34\u6b7b\u524d\u7684\u5e7b\u8c61\uff0c\u53ef\u662f\u4ed6\u504f\u504f\u53c8\u76f8\u4fe1\u8fd9\u662f\u4e00\u5b9a\u4f1a\u5b9e\u73b0\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u76f8\u4fe1\u201c\u516c\u6b63\u7684\u5415\u4e09\u201d\u4e00\u5b9a\u4f1a\u597d\u597d\u7167\u987e\u4ed6\u4eec\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u4ed6\u7684\u6b7b\u5df2\u7ecf\u6709\u4e86\u4ee3\u4ef7\u3002 \u3000\u3000\u5415\u4e09\u8fd8\u662f\u6ca1\u6709\u62ac\u5934\uff0c\u8fd8\u662f\u8fde\u770b\u90fd\u6ca1\u6709\u53bb\u770b\u4ed6\u8fd9\u4e2a\u5fe0\u5fc3\u7684\u5c5e\u4e0b\u3002 \u3000\u3000\u76f4\u5230\u82d7\u5ba3\u5200\u53e3\u4e0a\u7684\u9c9c\u8840\u5f00\u59cb\u51dd\u7ed3\u65f6\uff0c\u4ed6\u624d\u8f7b\u8f7b\u5730\u53eb\u4e86\u58f0\uff1a\u201c\u6c99\u5e73\u3002\u201d \u3000\u3000\u8fc7\u4e86\u534a\u664c\u95e8\u5916\u624d\u6709\u4eba\u56de\u5e94\uff1a\u201c\u6c99\u5e73\u5728\u3002\u201d \u3000\u3000\u4ed6\u56de\u5e94\u5f97\u867d\u7136\u4e0d\u5feb\uff0c\u4e5f\u4e0d\u7b97\u592a\u6162\uff0c\u95e8\u867d\u7136\u5f00\u7740\uff0c\u53ef\u662f\u4ed6\u7684\u4eba\u5e76\u6ca1\u6709\u8fdb\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u662f\u82d7\u5ba3\u3002 \u3000\u3000\u4ed6\u548c\u82d7\u5ba3\u662f\u7edd\u5bf9\u5b8c\u5168\u4e0d\u540c\u7684\u4e24\u4e2a\u4eba\uff0c\u5415\u4e09\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4ed6\u4ece\u6765\u6ca1\u6709\u5fd8\u8bb0\u8fc7\u4e00\u53e5\uff0c\u4e5f\u6ca1\u6709\u5fd8\u8bb0\u8fc7\u4e00\u6b21\u3002 \u3000\u3000\u5415\u4e09\u8fd8\u6ca1\u6709\u4e0b\u4ee4\u8981\u4ed6\u8fdb\u53bb\uff0c\u4ed6\u5c31\u7edd\u4e0d\u4f1a\u8d70\u8fdb\u8fd9\u5c4b\u5b50\u7684\u95e8\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u8ba4\u4e3a\u4ed6\u7684\u6b66\u529f\u4e0d\u53ca\u82d7\u5ba3\uff0c\u770b\u6765\u4e5f\u6ca1\u6709\u82d7\u5ba3\u806a\u660e\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u82d7\u5ba3\u90a3\u4e48\u5fe0\u8bda\u70ed\u5fc3\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u81ea\u5df1\u4e00\u76f4\u76f8\u4fe1\u4ed6\u4e00\u5b9a\u4f1a\u6bd4\u82d7\u5ba3\u6d3b\u5f97\u957f\u4e9b\u3002 \u3000\u3000\u6c99\u5e73\u4eca\u5e74\u56db\u5341\u516b\u5c81\uff0c\u8eab\u6750\u7626\u5c0f\uff0c\u5bb9\u8c8c\u5e73\u51e1\uff0c\u5728\u6c5f\u6e56\u4e2d\u8fde\u4e00\u70b9\u540d\u6c14\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u60f3\u8981\u6c5f\u6e56\u4e2d\u7684\u865a\u540d\uff0c\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u201c\u540d\u6c14\u201d\u80fd\u5e26\u7ed9\u4eba\u7684\u53ea\u6709\u56f0\u6270\u548c\u9ebb\u70e6\u3002 \u3000\u3000\u4ed6\u4e0d\u559d\u9152\uff0c\u4e0d\u8d4c\u94b1\uff0c\u5403\u5f97\u975e\u5e38\u7b80\u5355\uff0c\u7a7f\u5f97\u975e\u5e38\u7b80\u6734\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5728\u5c71\u897f\u56db\u5927\u94b1\u5e84\u4e2d\u90fd\u5df2\u7ecf\u5b58\u4e86\u4e94\u5341\u4e07\u4e24\u4ee5\u4e0a\u7684\u5b58\u6b3e\u3002 \u3000\u3000\u867d\u7136\u5927\u5bb6\u90fd\u8ba4\u4e3a\u4ed6\u7684\u6b66\u529f\u4e0d\u53ca\u82d7\u5ba3\uff0c\u53ef\u662f\u5415\u4e09\u5374\u77e5\u9053\u4ed6\u7684\u52b2\u6c14\u5185\u529b\u6697\u5668\u638c\u6cd5\u90fd\u4e0d\u5728\u6b66\u6797\u4e2d\u4efb\u4f55\u4e00\u4f4d\u540d\u5bb6\u4e4b\u4e0b\u3002 \u3000\u3000\u4ed6\u81f3\u4eca\u8fd8\u662f\u72ec\u8eab\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u5c31\u7b97\u4e00\u4e2a\u4eba\u6bcf\u5929\u90fd\u8981\u5403\u9e21\u86cb\uff0c\u4e5f\u4e0d\u5fc5\u5728\u5bb6\u91cc\u76d6\u4e2a\u9e21\u68da\u3002 \u3000\u3000\u76f4\u7b49\u5230\u5415\u4e09\u4e0b\u4ee4\u4e4b\u540e\uff0c\u6c99\u5e73\u624d\u8d70\u8fdb\u8fd9\u5c4b\u5b50\uff0c\u8d70\u5f97\u5e76\u4e0d\u592a\u5feb\uff0c\u53ef\u662f\u4e5f\u7edd\u5bf9\u4e0d\u80fd\u7b97\u592a\u6162\u3002 \u3000\u3000\u5415\u4e09\u770b\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u773c\u4e2d\u603b\u662f\u4f1a\u5fcd\u4e0d\u4f4f\u9732\u51fa\u6ee1\u610f\u7684\u8868\u60c5\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u6709\u4e86\u8fd9\u4e48\u6837\u4e00\u4e2a\u90e8\u4e0b\u90fd\u4e0d\u80fd\u4e0d\u6ee1\u610f\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5374\u6ca1\u6709\u63d0\u8d77\u82d7\u5ba3\u7684\u6b7b\uff0c\u5c31\u597d\u50cf\u4e16\u754c\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u751f\u5b58\u8fc7\u3002 \u3000\u3000\u5415\u4e09\u53c8\u95ee\u6c99\u5e73\uff1a \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u73ed\u5bdf\u5df4\u90a3\u5df2\u4e0b\u4ee4\u8981\u6765\u653b\u51fb\u6211\u4eec\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4eec\u73b0\u5728\u5e94\u8be5\u600e\u6837\u505a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5e94\u8be5\u77e5\u9053\u7684\u4e8b\uff0c\u6c99\u5e73\u7edd\u4e0d\u4f1a\u4e0d\u77e5\u9053\uff0c\u4e0d\u8be5\u77e5\u9053\u7684\u4e8b\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u2014\u2014\u5728\u5415\u4e09\u9762\u524d\uff0c\u65e2\u4e0d\u80fd\u663e\u662f\u592a\u7b28\uff0c\u4e5f\u4e0d\u80fd\u8868\u73b0\u5f97\u592a\u806a\u660e\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u662f\u4e0d\u662f\u5e94\u8be5\u5c06\u4eba\u624b\u90fd\u8c03\u96c6\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u5415\u4e09\u53c8\u95f4\u3002 \u3000\u3000\u201c\u4e0d\u5e94\u8be5\u3002\u201d\u6c99\u5e73\u56de\u7b54\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u73ed\u5bdf\u5df4\u90a3\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\u4f60\u5728\u54ea\u91cc\u3002\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u5982\u679c\u6211\u4eec\u4e0d\u544a\u8bc9\u4ed6\uff0c\u4ed6\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\uff1a\u201c\u5982\u679c\u6211\u4eec\u8fd9\u4e48\u6837\u505a\uff0c\u5c31\u7b49\u4e8e\u5df2\u7ecf\u544a\u8bc9\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u5fae\u7b11\u3002 \u3000\u3000\u201c\u4f60\u65e2\u7136\u660e\u767d\u8fd9\u4e00\u70b9\uff0c\u5c31\u5e94\u8be5\u77e5\u9053\u6211\u4eec\u73b0\u5728\u5e94\u8be5\u600e\u4e48\u6837\u505a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u201d\u6c99\u5e73\u8bf4\uff0c\u201c\u6211\u60f3\u8fc7\uff0c\u53ef\u662f\u6211\u4e0d\u77e5\u9053\u8981\u600e\u4e48\u6837\u505a\u624d\u662f\u5bf9\u7684\u3002\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u4e03\u7ae0\u3000\u5236\u9020\u9677\u9636 \u7b2c\u4e09\u5341\u4e03\u7ae0\u3000\u5236\u9020\u9677\u9636 \u3000\u3000\u5415\u4e09\u7b11\u5f97\u771f\u6109\u5feb\uff01 \u3000\u3000\u201c\u770b\u6765\u4f60\u867d\u7136\u6bd4\u82d7\u5ba3\u806a\u660e\u5f97\u591a\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u7b97\u592a\u806a\u660e\u3002\u201d \u3000\u3000\u6c99\u5e73\u5b8c\u5168\u540c\u610f\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4e2d\u4ece\u6765\u5c31\u4e0d\u60f3\u505a\u4e00\u4e2a\u806a\u660e\u4eba\u2014\u2014\u81f3\u5c11\u5728\u5341\u4e09\u5c81\u4ee5\u540e\u5c31\u6ca1\u6709\u518d\u60f3\u8fc7\u3002 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u6545\u610f\u516c\u5f00\u5ba3\u5e03\u8981\u53d1\u52a8\u653b\u51fb\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u6211\u81ea\u5df1\u66b4\u9732\u51fa\u81ea\u5df1\u7684\u884c\u8ff9\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u7edd\u4e0d\u80fd\u8fd9\u4e48\u6837\u505a\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u5982\u613f\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u4e5f\u4e0d\u80fd\u653e\u5f03\u8fd9\u4e2a\u673a\u4f1a\uff0c\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\u662f\u5934\u8001\u72d0\u72f8\uff0c\u6211\u4eec\u8981\u6293\u8fd9\u6761\u8001\u72d0\u72f8\uff0c\u5c31\u4e0d\u80fd\u653e\u8fc7\u8fd9\u6b21\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u4eec\u4e00\u4e00\u5b9a\u8981\u53e6\u5916\u5236\u9020\u4e2a\u9677\u997c\uff0c\u8ba9\u4ed6\u81ea\u5df1\u5f80\u4e0b\u6389\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u676f\u4e2d\u7684\u9152\u5df2\u7a7a\u4e86\uff0c\u5415\u4e09\u81ea\u5df1\u53c8\u659f\u6ee1\u4e00\u676f\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e0d\u8981\u4efb\u4f55\u4eba\u4e3a\u4ed6\u659f\u9152\uff0c\u522b\u4eba\u4e3a\u4ed6\u659f\u7684\u9152\u4ed6\u4ece\u6765\u6ca1\u6709\u559d\u8fc7\u4e00\u53e3\u3002 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u7684\u5c5e\u4e0b\uff0c\u867d\u7136\u5168\u90fd\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218\u58eb\uff0c\u4f46\u662f\u5176\u4e2d\u5e76\u6ca1\u6709\u771f\u6b63\u7684\u9ad8\u624b\uff0c\u201d\u5415\u4e09\u6c89\u541f\u7740\u9053\uff0c\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u5c0f\u65b9\u3002\u201d\u5415\u4e09\u9053\uff1a\u201c\u65b9\u4f1f\uff01\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u672c\u6765\u4e00\u76f4\u4f4e\u4f30\u4e86\u4ed6\uff0c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u8fd9\u4e2a\u4eba\u5c31\u8c61\u662f\u4e2a\u6a61\u76ae\u7403\u4e00\u6837\uff0c\u4f60\u4e0d\u53bb\u52a8\u4ed6\uff0c\u4ed6\u597d\u8c61\u8fde\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\uff0c\u5982\u679c\u4f60\u53bb\u6253\u4ed6\u4e00\u4e0b\uff0c\u4ed6\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u7a81\u7136\u8df3\u8d77\u6765\uff0c\u4f60\u6253\u5f97\u8d8a\u7528\u529b\uff0c\u4ed6\u5c31\u8df3\u5f97\u8d8a\u9ad8\uff0c\u8bf4\u4e0d\u5b9a\u4e00\u4e0b\u5b50\u5c31\u4f1a\u8df3\u5230\u4f60\u7684\u5934\u4e0a\u6765\uff0c\u8981\u4e86\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u770b\u8d77\u6765\u4ed6\u7684\u786e\u50cf\u4e2a\u8fd9\u4e48\u6837\u7684\u4eba\uff0c\u6240\u4ee5\u522b\u4eba\u624d\u4f1a\u79f0\u4ed6\u4e3a\u8981\u547d\u7684\u5c0f\u65b9\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u7684\u884c\u8e2a\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e24\u5929\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u62c9\u8428\u3002\u201d\u6c99\u95f0\u8bf4\uff1a\u201c\u5728\u62c9\u8428\u7684\u98de\u9e70\u697c\uff0c\u4e5f\u5c31\u662f\u4ee5\u524d\u9e70\u8bb0\u5546\u53f7\u63a5\u5f85\u5ba2\u6237\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5415\u4e09\u51dd\u89c6\u7740\u676f\u4e2d\u95ea\u52a8\u7684\u91d1\u5149\uff0c\u8fc7\u4e86\u5f88\u4e45\u53c8\u95ee\u6c99\u5e73\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u2018\u4e09\u53f7\uff0c\u3001\u2018\u5341\u4e09\u53f7\uff0c\u548c\u2018\u4e8c\u5341\u4e09\u53f7\u2019\u8fd9\u51e0\u5929\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u4e0d\u80fd\u627e\u5f97\u5230\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u80fd\uff01\u201d\u6c99\u5e73\u9053\uff1a\u201c\u516d\u4e2a\u65f6\u8fb0\u4e4b\u5185\u6211\u5c31\u53ef\u4ee5\u627e\u5230\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u5c06\u676f\u4e2d\u9152\u4e00\u996e\u800c\u5c3d\uff1a\u201c\u4f60\u4e00\u627e\u5230\u4ed6\u4eec\uff0c\u5c31\u5e26\u4ed6\u4eec\u5230\u71d5\u5b50\u697c\u53bb\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u8981\u4ed6\u4eec\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u53bb\u6740\u5c0f\u65b9\u3002\u201d\u5415\u4e09\u9053\uff1a\u201c\u6211\u8981\u4ed6\u4eec\u53bb\u6740\u5c0f\u65b9\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u8bf4\uff1a\u201c\u53ef\u662f\u6709\u4e00\u70b9\u4f60\u4e00\u5b9a\u8981\u8bb0\u4f4f\uff0c\u4f60\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u4e09\u4e2a\u4eba\u540c\u65f6\u51fa\u624b\u3002\u201d \u3000\u3000\u5415\u4e09\u8981\u6740\u4eba\u662f\u4ece\u6765\u4e0d\u62e9\u624b\u6bb5\u7684\uff0c\u5c0f\u65b9\u7edd\u4e0d\u662f\u4e2a\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u540c\u65f6\u51fa\u624b\uff0c\u529b\u91cf\u65e0\u7591\u8981\u6bd4\u4e00\u4e2a\u4eba\u5927\u5f97\u591a\uff0c\u6210\u529f\u7684\u673a\u4f1a\u4e5f\u5927\u5f97\u591a\u3002 \u3000\u3000\u53ef\u662f\u5415\u4e09\u5374\u4e0d\u8981\u8fd9\u4e48\u505a\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8981\u8fd9\u4e48\u505a\uff1f \u3000\u3000\u6c99\u5e73\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e0d\u95ee\u4e3a\u4ec0\u4e48\uff0c\u4e0d\u7ba1\u5415\u4e09\u53d1\u51fa\u591a\u4e48\u5947\u602a\u7684\u547d\u4ee4\uff0c\u4ed6\u90fd\u53ea\u6709\u670d\u4ece\u63a5\u53d7\u3002 \u3000\u3000\u201c\u4e09\u53f7\u201d\u3001\u201c\u5341\u4e09\u53f7\u201d\u3001\u201c\u4e8c\u5341\u4e09\u53f7\u201d\uff0c\u5f53\u7136\u4e0d\u662f\u4e09\u4e2a\u6570\u5b57\uff0c\u662f\u4e09\u4e2a\u4eba \u3000\u3000\u4e09\u4e2a\u6740\u4eba\u7684\u4eba\uff0c\u968f\u65f6\u90fd\u5728\u7b49\u5f85\u7740\u5415\u4e09\u7684\u547d\u4ee4\u53bb\u6740\u4eba\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u6d3b\u7740\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u66ff\u5415\u4e09\u53bb\u6740\u4eba\u3002 \u3000\u3000\u4ece\u53e6\u5916\u4e00\u79cd\u89c2\u70b9\u53bb\u770b\uff1b \u3000\u3000\u2014\u2014\u4ed6\u4eec\u80fd\u6d3b\u7740\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u80fd\u66ff\u5415\u4e09\u53bb\u6740\u4eba\u3002 \u3000\u3000\u5728\u67d0\u4e00\u4e2a\u975e\u5e38\u975e\u5e38\u79d8\u5bc6\u7684\u5730\u65b9\uff0c\u5728\u4e00\u4e2a\u7528\u82b1\u5c97\u77f3\u7b51\u6210\u7684\u5730\u5ba4\u4e2d\uff0c\u5728\u4e00\u4e2a\u53ea\u6709\u5415\u4e09\u4e00\u4e2a\u4eba\u53ef\u4ee5\u5f00\u542f\u7684\u94c1\u67dc\u91cc\uff0c\u6709\u4e00\u672c\u8bb0\u5f55\u7c3f\u3002 \u3000\u3000\u90a3\u672c\u8bb0\u5f55\u662f\u7edd\u4e0d\u516c\u5f00\u7684\u3002 \u3000\u3000\u5728\u90a3\u672c\u8bb0\u5f55\u4e0a\uff0c\u6709\u5173\u8fd9\u4e09\u4e2a\u4eba\u7684\u8d44\u6599\u662f\u8fd9\u6837\u5b50\u7684\u2014\u2014\u3002 \u3000\u3000\u4e8c\u5341\u4e09\u53f7\u3002 \u3000\u3000\u59d3\u540d\uff1a\u80e1\u5927\u78f7\u3002 \u3000\u3000\u6027\u522b\uff1a\u7537\u3002 \u3000\u3000\u5e74\u9f84\uff1a\u4e8c\u5341\u4e00\u3002 \u3000\u3000\u7c4d\u8d2f\uff1a\u6d59\u6c5f\uff0c\u676d\u5dde\u3002 \u3000\u3000\u5bb6\u4e16\uff1a\u7236\uff1a\u80e1\u7956\u660c\u3002\u6bcd\uff1a\u5b59\u6c38 \u3000\u3000\u5144\u5f1f\u59d0\u59b9\uff1a\u65e0\u3002 \u3000\u3000\u59bb\u5b50\u513f\u5973\uff1a\u65e0\u3002 \u3000\u3000\u5728\u90a3\u4efd\u8d44\u6599\u91cc\uff0c\u6709\u5173\u4e8e\u201c\u4e8c\u5341\u4e09\u53f7\u201d\u80e1\u5927\u78f7\u7684\u8bb0\u5f55\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u66ff\u5415\u4e09\u505a\u4e8b\u7684\u4eba\uff0c\u6c38\u8fdc\u53ea\u6709\u8fd9\u4e48\u6837\u4e00\u4efd\u7b80\u5355\u7684\u8d44\u6599\u3002 \u3000\u3000\u53ef\u662f\u5728\u53e6\u5916\u4e00\u4efd\u53ea\u6709\u5415\u4e09\u4e00\u4e2a\u4eba\u53ef\u4ee5\u770b\u5f97\u5230\u7684\u8bb0\u5f55\u91cc\uff0c\u6709\u5173\u201c\u4e8c\u5341\u4e8c\u53f7\u201d\u80e1\u5927\u78f7\u7684\u8d44\u6599\u53c8\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4efd\u8bb0\u5f55\u91cc\uff0c\u624d\u628a\u201c\u80e1\u5927\u9cde\u201d\u8fd9\u4e2a\u4eba\u662f\u4ec0\u4e48\u6837\u5b50\u7684\u4eba\u6316\u51fa\u6765\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u53e6\u5916\u4e00\u9762\uff0c\u80e1\u5927\u9cde\u7684\u53e6\u5916\u4e00\u9762\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u80e1\u5927\u78f7\uff0c\u7537\uff0c\u4e8c\u5341\u4e09\u5c81\uff0c\u7236\u4e3a\u201c\u6c38\u5229\u9556\u5c40\u201d\u4e4b\u53a8\u5e08\uff0c\u6bcd\u4e3a\u201c\u6c38\u5229\u9556\u5c40\u201d\u4e4b\u5976\u5988\u2014\u2014\u5373\u80e1\u5927\u9cde\u4e4b\u5988\u3002 \u3000\u3000\u6709\u5173\u80e1\u5927\u78f7\u7684\u8d44\u6599\u5c31\u662f\u8fd9\u4e48\u591a\uff0c\u867d\u7136\u4e0d\u5927\u591a\uff0c\u53ef\u662f\u5df2\u7ecf\u591f\u4e86\u3002 \u3000\u3000\u591f\u591a\u7684\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u5982\u679c\u4e00\u4e2a\u4eba\u591f\u806a\u660e\u4e5f\u591f\u7ecf\u9a8c\uff0c\u5c31\u4e0d\u96be\u4ece\u8fd9\u4e9b\u8d44\u6599\u91cc\u6316\u51fa\u5f88\u591a\u4e8b\uff01 \u3000\u3000\u2014\u2014\u5415\u4e09\u7684\u7ec4\u7ec7\u5e9e\u5927\u800c\u4e25\u5bc6\uff0c\u8981\u52a0\u5165\u7ec4\u7ec7\u5e76\u4e0d\u5bb9\u6613\uff0c\u80fd\u591f\u5217\u5165\u8fd9\u4efd\u79d8\u5bc6\u8d44\u6599\u7f16\u53f7\u7684\uff0c\u66f4\u5168\u90fd\u662f\u4e00\u6d41\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4e00\u4e00\u80e1\u5927\u78f7\u5728\u5341\u516d\u5c81\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\u3002\u638c\u4e2d\u4e00\u67c4\u5251\u5df2\u7ecf\u51fb\u8d25\u8fc7\u5f88\u591a\u522b\u4eba\u8ba4\u4e3a\u4ed6\u7edd\u65e0\u53ef\u80fd\u51fb\u8d25\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u53a8\u5e08\u548c\u5976\u5988\u7684\u513f\u5b50\uff0c\u80fd\u591f\u5728\u5341\u516d\u5c81\u7684\u65f6\u5019\uff0c\u7adf\u6210\u4e3a\u6c5f\u6e56\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u4ed6\u5f53\u7136\u5403\u8fc7\u5f88\u591a\u82e6\uff0c\uff1a\u505a\u8fc7\u5f88\u591a\u522b\u4eba\u4e0d\u4f1a\u505a\u4e5f\u505a\u4e0d\u5230\u7684\u4e8b\uff0c\u800c\u4e14\u6709\u4e00\u4efd\u767e\u6298\u4e0d\u56de\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u4e00\u52a0\u5165\u5415\u4e09\u7684\u7ec4\u7ec7\u540e\uff0c\u4ed6\u5c31\u53d8\u6210\u4e00\u4e2a\u53ea\u6709\u7f16\u53f7\u6ca1\u6709\u540d\u59d3\u7684\u4eba\u4e86\u3002 \u3000\u3000\u2014\u2014\u8c01\u4e5f\u4e0d\u613f\u5c06\u81ea\u5df1\u7528\u8840\u6cea\u6362\u6765\u7684\u540d\u58f0\u5730\u4f4d\u653e\u5f03\uff0c\u80e1\u5927\u78f7\u8fd9\u4e48\u505a\uff0c\u5f53\u7136\u6709\u4ed6\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\u3002 \u3000\u3000\u2014\u2014\u4ed6\u6740\u4e86\u5927\u591a\u4e0d\u8be5\u6740\u7684\u4eba\uff0c\u505a\u4e86\u5927\u591a\u4e0d\u8be5\u505a\u7684\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u59cb\u7ec8\u4e0d\u80fd\u5fd8\u8bb0\u81ea\u5df1\u662f\u4e2a\u53a8\u5e08\u548c\u5976\u5988\u7684\u513f\u5b50\u3002 \u3000\u3000\u2014\u2014\u5c31\u56e0\u4e3a\u4ed6\u59cb\u7ec8\u4e0d\u80fd\u5fd8\u8bb0\u81ea\u5df1\u51fa\u8eab\u7684\u5351\u8d31\uff0c\u6240\u4ee5\u624d\u4f1a\u505a\u51fa\u5f88\u591a\u4e0d\u8be5\u505a\u7684\u4e8b\uff0c\u6240\u4ee5\u624d\u4f1a\u52a0\u5165\u5415\u4e09\u7684\u7ec4\u7ec7\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u6709\u524d\u56e0\u624d\u6709\u540e\u679c\uff0c\u6709\u540e\u679c\u5fc5\u6709\u524d\u56e0\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u4ed6\u7684\u8eab\u4e16\u5982\u6b64\uff0c\u6240\u4ee5\u624d\u4f1a\u62fc\u547d\u60f3\u51fa\u4eba\u5934\u5730\uff0c\u65e0\u8bba\u5bf9\u4efb\u4f55\u4eba\u4efb\u4f55\u4e8b\u90fd\u5145\u6ee1\u4e86\u53cd\u53db\u6027\uff0c\u5728\u522b\u4eba\u773c\u5149\u4e2d\uff0c\u4ed6\u5f53\u7136\u662f\u4e2a\u53db\u5f92\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u4e5f\u8ddf\u4ed6\u7684\u4eba\u4e00\u6837\uff0c\u51b2\u52a8\u3001\u504f\u6fc0\uff0c\u5145\u6ee1\u4e86\u53cd\u53db\u6027\u3002 \u3000\u3000\u675c\u6c38\u7684\u5bb6\u4e16\u5c31\u548c\u80e1\u5927\u9cde\u5b8c\u5168\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u4e0d\u7ba1\u6839\u636e\u54ea\u4e00\u4efd\u8d44\u6599\u7684\u8bb0\u8f7d\u4e2d\uff0c\u675c\u6c38\u90fd\u5e94\u8be5\u662f\u4e2a\u975e\u5e38\u6b63\u5e38\u7684\u4eba\uff0c\u5bb6\u4e16\u548c\u6559\u80b2\u90fd\u975e\u5e38\u826f\u597d\u3002 \u3000\u3000\u5341\u4e09\u53f7\u3002 \u3000\u3000\u59d3\u540d\uff1a\u675c\u6c38\u3002 \u3000\u3000\u6027\u522b\uff1a\u7537\u3002 \u3000\u3000\u5e74\u7eaa\uff1a\u4e09\u5341\u3002 \u3000\u3000\u7c4d\u8d2f\uff1a\u6c5f\u82cf\u5f90\u5dde\u3002 \u3000\u3000\u7236\uff1a\u675c\u5b89\u3002 \u3000\u3000\u6bcd\uff1a\u9648\u7d20\u8d1e\u3002\u65e9\u6b81\u3002 \u3000\u3000\u59bb\uff1a\u6731\u8d35\u82ac\u3002 \u3000\u3000\u6709\u5b50\u5973\u5404\u4e00\u4eba\u3002 \u3000\u3000\u675c\u6c38\u7684\u7236\u4eb2\u675c\u5b89\u662f\u6c5f\u5317\u6700\u6210\u529f\u7684\u9556\u5e08\u548c\u751f\u610f\u4eba\uff0c\u767d\u624b\u8d77\u5bb6\uff0c\u4e8c\u5341\u4e03\u5c81\u65f6\u5c31\u5df2\u79ef\u8d44\u5343\u4e07\u3002 \u3000\u3000\u675c\u6c38\u7684\u6bcd\u4eb2\u65e9\u901d\uff0c\u4ed6\u7684\u7236\u4eb2\u4ece\u672a\u7eed\u5f26\uff0c\u800c\u4e14\u4ece\u672a\u653e\u677e\u8fc7\u5bf9\u513f\u5b50\u7684\u6559\u517b\uff0c\u5728\u675c\u6c38\u4e03\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u5df2\u8bf7\u4e86\u4e09\u4f4d\u9971\u5b66\u901a\u5112\u548c\u4e24\u4f4d\u6709\u540d\u7684\u6b66\u5e08\u6765\u548c\u4e00\u4f4d\u6b66\u5f53\u540d\u5bbf\u6559\u5bfc\u4ed6\uff0c\u5e0c\u671b\u4ed6\u6210\u4e3a\u4e00\u4e2a\u6587\u6b66\u5168\u624d\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u675c\u6c38\u5e76\u6ca1\u6709\u8ba9\u4ed6\u7684\u7236\u4eb2\u5931\u671b\uff0c\u65e9\u5e74\u5c31\u5df2\u6587\u91c7\u6590\u7136\uff0c\u5251\u6cd5\u4e5f\u5f97\u5230\u4e86\u6b66\u5f53\u7684\u7cbe\u7cb9\uff0c\u88ab\u6c5f\u6e56\u4e2d\u516c\u8ba4\u4e3a\u6b66\u5f53\u540e\u8d77\u4e00\u8f88\u4e2d\u7684\u4f7c\u4f7c\u8005\u3002 \u3000\u3000\u675c\u6c38\u7684\u59bb\u5b50\u4e5f\u662f\u4e16\u5bb6\u5973\uff0c\u6e29\u67d4\u8d24\u6167\u7f8e\u4e3d\uff0c\u5341\u4e94\u5c81\u7684\u65f6\u5019\u5c31\u5ac1\u7ed9\u4ed6\uff0c\u6240\u6709\u8ba4\u5f97\u4ed6\u7684\u4eba\u90fd\u5728\u7fa1\u6155\u4ed6\u7684\u798f\u6c14\u3002 \u3000\u3000\u675c\u6c38\u7684\u513f\u5b50\u806a\u660e\u5b5d\u987a\uff0c\u8bda\u5b9e\u89c4\u77e9\uff0c\u4ece\u6765\u6ca1\u6709\u505a\u8fc7\u4e00\u4ef6\u8ba9\u7236\u6bcd\u4f24\u5fc3\u8ba8\u538c\u7684\u4e8b\u3002 \u3000\u3000\u50cf\u675c\u6c38\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u600e\u4e48\u4f1a\u653e\u5f03\u6240\u6709\u7684\u4e00\u5207\u52a0\u5165\u5415\u4e09\u7684\u7ec4\u7ec7\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u5f53\u7136\u6709\u4eba\u95ee\u8fc7\u4ed6\uff0c\u6709\u4e00\u6b21\u4ed6\u5728\u5927\u9189\u4e4b\u540e\u624d\u56de\u7b54\uff1a\u201c\u56e0\u4e3a\u6211\u53d7\u4e0d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u6837\u7684\u751f\u6d3b\uff0c\u8fd9\u6837\u7684\u5bb6\u5ead\uff0c\u8fd9\u4e48\u6837\u7684\u73af\u5883\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u53d7\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u5982\u679c\u4f60\u66f4\u6df1\u5165\u4e86\u89e3\u4ed6\u7684\u4e00\u5207\uff0c\u4f60\u5c31\u4f1a\u660e\u767d\u4ed6\u53d7\u4e0d\u4e86\u7684\u662f\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u7236\u4eb2\u592a\u5f3a\uff0c\u592a\u80fd\u5e72\uff0c\u5927\u6709\u94b1\uff0c\u4e5f\u5927\u6709\u540d\uff0c\u5728\u4ed6\u5341\u51e0\u5c81\u7684\u65f6\u5019\u5c31\u5df2\u7ecf\u628a\u4ed6\u4e00\u751f\u90fd\u5b89\u6392\u597d\u4e86\uff0c\u8fd9\u4e16\u754c\u4e0a\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u80fd\u591f\u8ba9\u4ed6\u64cd\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u4ece\u5c0f\u5c31\u88ab\u8bad\u7ec3\u6210\u4e00\u4e2a\u89c4\u89c4\u77e9\u77e9\u7684\u5b69\u5b50\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u505a\u8fc7\u4e00\u4ef6\u8ba9\u4ed6\u7236\u4eb2\u64cd\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u597d\u50cf\u5df2\u7ecf\u6ce8\u5b9a\u662f\u4e2a\u6210\u529f\u5e78\u798f\u7684\u4eba\uff0c\u6709\u5e78\u798f\u7684\u5bb6\u5ead\uff0c\u6709\u6210\u529f\u7684\u4e8b\u4e1a\uff0c\u6709\u5730\u4f4d\uff0c\u6709\u540d\u6c14\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u5207\u90fd\u4e0d\u662f\u9760\u4ed6\u81ea\u5df1\u594b\u6597\u5f97\u6765\u7684\uff0c\u800c\u662f\u4f9d\u9760\u4ed6\u7684\u7236\u4eb2\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u6709\u5f88\u591a\u4eba\u5992\u5ac9\u4ed6\uff0c\u6709\u5f88\u591a\u4eba\u7fa1\u6155\uff0c\u53ef\u662f\u771f\u6b63\u5c0a\u656c\u4ed6\u7684\u4eba\u5374\u4e0d\u591a\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u624d\u60f3\u505a\u51e0\u4ef6\u4ee4\u4eba\u6ce8\u76ee\u7684\u4e8b\uff0c\u8ba9\u5927\u5bb6\u6539\u53d8\u5bf9\u4ed6\u7684\u770b\u6cd5\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u4f60\u6025\u7740\u60f3\u53bb\u505a\u8fd9\u79cd\u4e8b\uff0c\u4f60\u4e00\u5b9a\u4f1a\u505a\u9519\u7684\u3002 \u3000\u3000\u675c\u6c38\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u53bb\u505a\u90a3\u4e9b\u4e8b\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u53bb\u505a\u51fa\u6765\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53ea\u6709\u52a0\u5165\u5415\u4e09\u7684\u7ec4\u7ec7\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u4e5f\u8ddf\u4ed6\u7684\u4eba\u4e00\u6837\uff0c\u51fa\u8eab\u540d\u95e8\uff0c\u5f88\u5c11\u72af\u9519\uff0c\u53ef\u662f\u4e00\u9519\u5c31\u4e0d\u53ef\u6536\u62fe\uff01 \u3000\u3000\u4e09\u5e74\u524d\u4ed6\u624d\u52a0\u5165\u5415\u4e09\u7684\u7ec4\u7ec7\uff0c\u7ecf\u8fc7\u8fd9\u4e09\u5e74\u7684\u78e8\u7ec3\u540e\uff0c\u4ed6\u72af\u9519\u7684\u65f6\u5019\u66f4\u5c11\u4e86\u3002 \u3000\u3000\u80e1\u5927\u9cde\u548c\u675c\u6c38\u65e0\u7591\u662f\u4e24\u79cd\u5178\u578b\u5b8c\u5168\u4e0d\u540c\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u73b0\u5728\u4f1a\u52a0\u5165\u540c\u4e00\u7ec4\u7ec7\uff0c\u505a\u4e00\u79cd\u540c\u6837\u6027\u8d28\u7684\u4e8b\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u7b54\u590d\u3002 \u3000\u3000\u4e5f\u8bb8\u8fd9\u5c31\u662f\u547d\u8fd0\u3002 \u3000\u3000\u547d\u8fd0\u5e38\u5e38\u4f1a\u4f7f\u4eba\u906d\u9047\u5230\u4e00\u4e9b\u5947\u5947\u602a\u602a\u3001\u8c01\u4e5f\u65e0\u6cd5\u9884\u6599\u5230\u7684\u4e8b\u3002 \u3000\u3000\u547d\u8fd0\u4e5f\u5e38\u5e38\u4f1a\u4f7f\u4eba\u843d\u5165\u67d0\u79cd\u53c8\u53ef\u60b2\u53c8\u53ef\u7b11\u7684\u5883\u9047\u4e2d\uff0c\u4f7f\u4eba\u6839\u672c\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u771f\u6b63\u6709\u52c7\u6c14\u7684\u4eba\uff0c\u662f\u6c38\u8fdc\u4e0d\u4f1a\u5411\u547d\u8fd0\u5c48\u670d\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u65e9\u5df2\u5728\u56f0\u5883\u4e2d\u5b66\u4f1a\u5fcd\u8010\uff0c\u5728\u9006\u5883\u4e2d\u5b66\u4f1a\u5fcd\u53d7\uff0c\u53ea\u8981\u4e00\u6709\u673a\u4f1a\uff0c\u4ed6\u4eec\u5c31\u4f1a\u633a\u8d77\u80f8\u819b\uff0c\u7ee7\u7eed\u6323\u624e\u594b\u6597\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u4eec\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4ed6\u4eec\u5c31\u6709\u62ac\u5934\u7684\u65f6\u5019\u3002 \u3000\u3000\u6797\u6b63\u96c4\u65e0\u7591\u53c8\u662f\u53e6\u5916\u4e00\u79cd\u5b8c\u5168\u4e0d\u540c\u5178\u578b\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u662f\u95fd\u4eba\u3002 \u3000\u3000\u5728\u95fd\uff0c\u6797\u59d3\u662f\u5927\u65cf\uff0c\u6797\u6b63\u96c4\u4e5f\u662f\u4e2a\u975e\u5e38\u666e\u901a\u975e\u5e38\u666e\u901a\u7684\u540d\u5b57\uff0c\u6bcf\u4e00\u4e2a\u57ce\uff0c\u6bcf\u4e00\u4e2a\u4e61\uff0c\u6bcf\u4e00\u9547\uff0c\u6bcf\u4e00\u6751\u90fd\u6709\u59d3\u6797\u53eb\u6797\u6b63\u96c4\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u751f\u957f\u5728\u95fd\u5883\u6cbf\u6d77\u4e00\u5e26\u502d\u5bc7\u51fa\u6ca1\u6700\u591a\u7684\u5730\u65b9\uff0c\u636e\u8bf4\u5728\u4ed6\u5341\u516d\u5c81\u65f6\u5019\uff0c\u5c31\u66fe\u4ee5\u4e00\u67c4\u957f\u5200\u523a\u6740\u502d\u5bc7\u7684\u9996\u7ea7\u4e00\u767e\u4e09\u5341\u4f59\u7ea7\u3002 \u3000\u3000\u5728\u502d\u8bed\u4e2d\uff0c\u4ed6\u7684\u540d\u5b57\u88ab\u79f0\u4e3a\u201c\u9a6c\u6c99\u201d\uff0c\u63d0\u8d77\u201c\u9a6c\u6c99\u201d\u6765\uff0c\u502d\u5bc7\u83ab\u4e0d\u5fc3\u60ca\u80c6\u6218\uff0c\u671b\u98ce\u800c\u9003\u3002 \u3000\u3000\u540e\u6765\u502d\u5bc7\u6e10\u88ab\u6b7c\u706d\uff0c\u4ed6\u4e5f\u8fdc\u79bb\u4e86\u5bb6\u4e61\uff0c\u6d6a\u8ff9\u5929\u6daf\uff0c\u53bb\u95ef\u5929\u4e0b\u3002 \u3000\u3000\u5728\u6c5f\u6e56\u4e2d\u4ed6\u6df7\u5f97\u5f88\u4e0d\u5f97\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e2\u6ca1\u6709\u663e\u8d6b\u7684\u5bb6\u4e16\u80cc\u666f\uff0c\u4e5f\u4e0d\u662f\u51fa\u8eab\u4e8e\u540d\u95e8\u6b63\u6d3e\u7684\u5b50\u5f1f\uff0c\u65e0\u8bba\u4ed6\u8d70\u5230\u54ea\u91cc\uff0c\u65e0\u8bba\u4ed6\u505a\u4ec0\u4e48\uff0c\u90fd\u4f1a\u53d7\u5230\u6392\u6324\u3002 \u3000\u3000\u6240\u4ee5\u51e0\u5e74\u4e4b\u540e\u201c\u9a6c\u6c99\u201d\u8fd9\u4e2a\u4eba\u5c31\u4ece\u6c5f\u6e56\u4e2d\u6d88\u5931\u4e86\uff0c\u6797\u6b63\u96c4\u8fd9\u4e2a\u4eba\u4e5f\u6d88\u5931\u4e86\u3002 \u3000\u3000\u7136\u540e\u6c5f\u6e56\u4e2d\u5c31\u51fa\u73b0\u4e86\u4e00\u4e2a\u51b7\u9177\u65e0\u60c5\u7684\u804c\u4e1a\u6740\u624b\uff0c\u867d\u7136\u4ee5\u6740\u4eba\u4e3a\u4e1a\uff0c\u5e76\u4e0d\u4ee5\u6740\u4eba\u4e3a\u4e50\u3002 \u3000\u3000\u5728\u5415\u4e09\u7684\u8bb0\u5f55\u4e2d\uff0c\u662f\u4ee5\u52a0\u5165\u7ec4\u7ec7\u7684\u5148\u540e\u4e3a\u987a\u5e8f\u7684\uff1b\u201c\u4e09\u53f7\u201d\u7684\u5386\u53f2\u65e0\u7591\u5df2\u975e\u5e38\u60a0\u4e45\uff0c\u8bb0\u5f55\u5374\u6700\u77ed\u3002 \u3000\u3000\u4e09\u53f7\u3002 \u3000\u3000\u59d3\u540d\uff1a\u6797\u6b63\u96c4\uff08\u6df7\u53f7\u9a6c\u6c99\uff09\u3002 \u3000\u3000\u6027\u522b\uff1a\u7537\u3002 \u3000\u3000\u5e74\u7eaa\uff1a\u56db\u5341\u4e09\u3002 \u3000\u3000\u7c4d\u8d2f\uff1a\u95fd\u3002 \u3000\u3000\u5bb6\u4e16\u4e0d\u8be6\u3002 \u3000\u3000\u4e8c\u5341\u4e94\u5c81\u4e4b\u540e\uff0c\u6797\u6b63\u96c4\u5c31\u5f00\u59cb\u7528\u5251\u4e86\u3002 \u3000\u3000\u5f53\u65f6\u4ed6\u5df2\u975e\u5c11\u5e74\uff0c\u5df2\u7ecf\u6ca1\u6709\u5b66\u5251\u5c11\u5e74\u4eec\u7684\u70ed\u60c5\u548c\u51b2\u52a8\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e5f\u6ca1\u6709\u675c\u6c38\u90a3\u4e48\u597d\u7684\u5e08\u8d44\u548c\u6559\u517b\uff0c\u5251\u6cd5\u4e2d\u7684\u7cbe\u517b\u4ed6\u5f88\u53ef\u80fd\u5b8c\u5168\u4e00\u7a8d\u4e0d\u901a\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6709\u7ecf\u9a8c\u3002 \u3000\u3000\u4ed6\u7684\u7ecf\u9a8c\u4e5f\u8bb8\u6bd4\u80e1\u5927\u9cde\u548c\u675c\u6c38\u4e24\u4e2a\u4eba\u52a0\u8d77\u6765\u90fd\u591a\u5f97\u591a\uff0c\u4ed6\u8eab\u4e0a\u7684\u5200\u75a4\uff0c\u4e5f\u6bd4\u4ed6\u4eec\u52a0\u8d77\u6765\u591a\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u4ee5\u4ed6\u5c11\u5e74\u65f6\u4e0e\u502d\u5bc7\u8d34\u8eab\u8089\u640f\u7684\u7ecf\u9a8c\uff0c\u521b\u9020\u4e86\u4e00\u79cd\u72ec\u7279\u7684\u5251\u6cd5\uff0c\u4e00\u79cd\u6df7\u5408\u4e86\u4e1c\u6d41\u6b66\u58eb\u5200\u6cd5\u7684\u5251\u6cd5\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u867d\u7136\u5e76\u4e0d\u82b1\u4fcf\uff0c\u53d8\u5316\u4e5f\u4e0d\u591a\uff0c\u4f46\u5374\u7edd\u5bf9\u6709\u6548\u3002 \u3000\u3000\u4e09\u53f7\u3001\u5341\u4e09\u53f7\u3001\u4e8c\u5341\u4e09\u53f7\uff0c\u65e0\u7591\u90fd\u662f\u5415\u4e09\u5c5e\u4e0b\u4e2d\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u4ee3\u8868\u4e86\u4e09\u79cd\u7edd\u5bf9\u4e0d\u540c\u7684\u4eba\u683c\u548c\u5178\u578b\uff0c\u4e09\u4e2a\u4eba\u7684\u6b66\u529f\u548c\u5251\u6cd5\u4e5f\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u5415\u4e09\u4e0b\u4ee4\u6d3e\u4ed6\u4eec\u4e09\u4e2a\u4eba\u53bb\u523a\u6740\u5c0f\u65b9\uff0c\u8fd9\u547d\u4ee4\u7edd\u5bf9\u4e0b\u5f97\u5f88\u6b63\u786e\u3002 \u3000\u3000\u2014\u2014\u5415\u4e09\u4e0b\u7684\u547d\u4ee4\u4e00\u5411\u4e0d\u4f1a\u4e0d\u6b63\u786e\u7684\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u4ed6\u4eec\u4e09\u4e2a\u540c\u65f6\u51fa\u624b\uff1f\u4e09\u4e2a\u4eba\u540c\u65f6\u51fa\u624b\u7684\u673a\u4f1a\u8fdc\u6bd4\u4e00\u4e2a\u4eba\u5927\u5f97\u591a\uff1f \u3000\u3000\u4ed6\u7684\u7528\u610f\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u7528\u610f\u662f\u4ec0\u4e48\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u8ba1\u5212\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u4e5f\u6ca1\u6709\u4eba\u95f4\u3002 \u3000\u3000\u975e\u4f46\u6c99\u5e73\u4e0d\u95ee\uff0c\u80e1\u5927\u9e9f\u3001\u675c\u6c38\u3001\u6797\u6b63\u96c4\u4e5f\u4e0d\u95ee\u3002 \u3000\u3000\u6c99\u5e73\u627e\u5230\u4e86\u4ed6\u4eec\u4e09\u4e2a\u4eba\uff0c\u7528\u6700\u7b80\u5355\u7684\u5b57\u53e5\u5c06\u5415\u4e09\u7684\u547d\u4ee4\u4e0b\u8fbe\u3002 \u3000\u3000\u201c\u8001\u677f\u8981\u4f60\u4eec\u53bb\u6740\u65b9\u4f1f\uff01\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u8981\u4f60\u4eec\u4e09\u4e2a\u4eba\u5355\u72ec\u5206\u522b\u53bb\u6740\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u56de\u7b54\u540c\u6837\u53ea\u6709\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u7136\u540e\u4ed6\u4eec\u5c31\u5728\u6700\u77ed\u7684\u65f6\u95f4\u91cc\u627e\u5230\u4e86\u5c0f\u65b9\u3002 \u3000\u3000\u867d\u7136\u8fd8\u662f\u6ca1\u6709\u4eba\u77e5\u9053\u5415\u4e09\u7684\u8ba1\u5212\uff0c\u53ef\u662f\u884c\u52a8\u5df2\u5c55\u5f00\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7684\u5c5e\u4e0b\u65e0\u7591\u4e5f\u5df2\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u4e8e\u662f\u8ba1\u5212\u7684\u65f6\u671f\u5df2\u7ed3\u675f\uff0c\u884c\u52a8\u7684\u65f6\u671f\u5df2\u5f00\u59cb\u2014\u2014\u5f53\u7136\u662f\u5168\u9762\u884c\u52a8\u3002 \u3000\u3000\u6674\u591c\u3001\u65e0\u661f\u3001\u65e0\u6708\u3001\u65e0\u96e8\u3001\u6709\u98ce\u3002 \u3000\u3000\u6697\u5ba4\u3001\u660f\u706f\u3002 \u3000\u3000\u5ba4\u6697\uff0c\u662f\u56e0\u4e3a\u706f\u660f\u3002 \u3000\u3000\u706f\u660f\uff0c\u662f\u56e0\u4e3a\u5c0f\u65b9\u7279\u610f\u5c06\u706f\u82af\u62e7\u5230\u6700\u5c0f\u5904\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u662f\u4e2a\u660e\u6717\u7684\u4eba\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5374\u5b81\u613f\u5728\u9ed1\u6697\u4e2d\u72ec\u5904\u3002 \u3000\u3000\u8fd9\u4e0d\u4ec5\u662f\u56e0\u4e3a\u4ed6\u6709\u5f88\u591a\u4e8b\u8981\u53bb\u60f3\uff0c\u4e5f\u4e0d\u4ec5\u662f\u56e0\u4e3a\u73b0\u5728\u4ed6\u6709\u4e00\u4ef6\u51b3\u5b9a\u6027\u7684\u8ba1\u5212\u5373\u5c06\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u6709\u4e9b\u5f88\u5f00\u6717\u5f88\u4e0d\u7518\u5bc2\u5bde\u7684\u4eba\uff0c\u5728\u67d0\u79cd\u65f6\u5019\u4e5f\u4f1a\u5ffd\u7136\u53d8\u5f97\u5b81\u613f\u5bc2\u5bde\u5b64\u72ec\u81ea\u5904\u3002 \u3000\u3000\u5c0f\u65b9\u73b0\u5728\u7684\u5fc3\u60c5\u5c31\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u8fd9\u51e0\u5929\u4ed6\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u4ed6\u6709\u5f88\u591a\u8bdd\u8981\u544a\u8bc9\u201c\u9633\u5149\u201d\uff0c\u4e5f\u6709\u5f88\u591a\u4e8b\u8981\u95ee\u82cf\u82cf\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6ca1\u6709\u95ee\uff0c\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u548c\u5979\u4eec\u5355\u72ec\u76f8\u5904\u8fc7\u3002 \u3000\u3000\u2014\u2014\u4e5f\u8bb8\u4ed6\u662f\u5728\u9003\u907f\u3002 \u3000\u3000\u2014\u2014\u9003\u907f\u5e76\u4e0d\u80fd\u89e3\u51b3\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u65e0\u8bba\u4efb\u4f55\u4eba\u4e00\u751f\u4e2d\u603b\u96be\u514d\u6709\u9003\u907f\u7684\u65f6\u5019\u3002 \u3000\u3000\u5728\u67d0\u4e00\u65b9\u9762\u8bf4\uff0c\u9003\u907f\u5c31\u662f\u4f11\u606f\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u9700\u8981\u4f11\u606f\uff0c\u5c24\u5176\u662f\u5728\u4e00\u6b21\u51b3\u5b9a\u6027\u7684\u8ba1\u5212\u5373\u5c06\u5c55\u5f00\u884c\u52a8\u7684\u65f6\u5019\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e2a\u65e0\u661f\u65e0\u6708\u65e0\u96e8\u7684\u6697\u591c\u91cc\uff0c\u98ce\u4e2d\u5ffd\u7136\u4f20\u6765\u4e00\u9635\u547c\u5438\u58f0\uff0c\u5728\u5f80\u8fd9\u91cc\u79fb\u52a8\u3002 \u3000\u3000\u4e00\u79cd\u53ea\u6709\u5c0f\u65b9\u8fd9\u79cd\u4eba\u624d\u80fd\u542c\u5230\u7684\u547c\u5438\u58f0\u4e00\u4e00\u5f53\u7136\u662f\u4eba\u7684\u547c\u5438po\u3002 \u3000\u3000\u7edd\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u547c\u5438\u58f0\uff0c\u5c0f\u65b9\u53ef\u4ee5\u65ad\u5b9a\u6765\u7684\u6700\u5c11\u6709\u4e09\u4e2a\u4eba\uff0c\u6700\u591a\u4e5f\u53ea\u6709\u56db\u4e2a\u3002 \u3000\u3000\u53ea\u6709\u547c\u5438\u58f0\uff0c\u6ca1\u6709\u811a\u6b65\u58f0\u3002 \u3000\u3000\u8fd9\u81f3\u5c11\u8bc1\u660e\u4e86\u4e24\u4ef6\u4e8b\u3002 \u3000\u3000\u2014\u2014\u4e0d\u7ba1\u5c0f\u65b9\u7684\u5fc3\u60c5\u600e\u4e48\u6837\uff0c\u4ed6\u7684\u8033\u6735\u8fd8\u662f\u5f88\u7075\u3002 \u3000\u3000\u2014\u2014\u6765\u7684\u4e0d\u7ba1\u662f\u4e09\u4e2a\u4eba\u8fd8\u662f\u56db\u4e2a\u4eba\uff0c\u90fd\u662f\u8eab\u624b\u6781\u77eb\u5065\u7684\u6b66\u6797\u9ad8\u624b\uff01\u56e0\u4e3a\u4ed6\u4eec\u811a\u6b65\u58f0\u6bd4\u547c\u5438\u58f0\u8fd8\u8f7b\u3002 \u3000\u3000\u5c0f\u65b9\u4f4f\u7684\u662f\u5bb6\u5ba2\u6808\u3002 \u3000\u3000\u81ea\u4ece\u73ed\u5bdf\u5df4\u90a3\u5df2\u7ecf\u5c06\u8ba1\u5212\u51b3\u5b9a\u4e4b\u540e\uff0c\u4ed6\u5c31\u4f4f\u8fdb\u4e86\u8fd9\u5bb6\u5ba2\u6808\u3002 \u3000\u3000\u4e00\u5bb6\u5f88\u50fb\u9759\u7684\u5ba2\u6808\uff0c\u4ed6\u4f4f\u7684\u662f\u8fd9\u5bb6\u5ba2\u6808\u4e2d\u4e00\u4e2a\u5f88\u50fb\u9759\u7684\u540e\u9662\u3002 \u3000\u3000\u5ba2\u6808\u4e2d\u7684\u638c\u67dc\u4f19\u8ba1\u5ba2\u4eba\u5c0f\u53ae\u90fd\u968f\u65f6\u53ef\u4ee5\u5230\u8fd9\u4e2a\u540e\u9662\u91cc\u6765\u3002 \u3000\u3000\u5728\u9644\u8fd1\u4e00\u5e26\u5c71\u91ce\u7530\u90ca\u91cc\u95f2\u901b\u7684\u4eba\uff0c\u4e5f\u968f\u65f6\u53ef\u4ee5\u901b\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u73b0\u5728\u591c\u5df2\u6df1\uff0c\u5927\u591a\u6570\u4eba\u90fd\u5df2\u7ecf\u7761\u7740\u4e86\uff0c\u6ca1\u6709\u7761\u7740\u7684\u4eba\uff0c\u4e00\u5b9a\u6709\u7279\u522b\u7684\u539f\u56e0\u624d\u6ca1\u6709\u7761\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u67d0\u79cd\u7279\u522b\u7684\u539f\u56e0\uff0c\u4e00\u4e2a\u4eba\u8d70\u8def\u7684\u811a\u6b65\u58f0\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u6bd4\u547c\u5438\u58f0\u8fd8\u8f7b\u3002 \u3000\u3000\u8fd9\u81f3\u5c11\u53c8\u8bc1\u660e\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u2014\u2014\u6765\u7684\u8fd9\u51e0\u4e2a\u4eba\uff0c\u4e00\u5b9a\u662f\u81ea\u4e3a\u67d0\u79cd\u7279\u522b\u76ee\u7684\u624d\u4f1a\u6765\u7684\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6765\u627e\u5c0f\u65b9\u559d\u9152\u4e0b\u68cb\uff0c\u804a\u5929\u8c08\u60c5\u3002 \u3000\u3000\u5c31\u7b97\u6709\u4eba\u4f1a\u6765\u627e\u4ed6\u8c08\u60c5\uff0c\u4e5f\u4e0d\u4f1a\u627e\u4e09\u56db\u4e2a\u4eba\u4e00\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u627e\u5c0f\u65b9\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u6700\u6b63\u786e\u7684\u7b54\u6848\u53ea\u6709\u4e00\u79cd\u2014\u2014\u4ed6\u4eec\u90fd\u662f\u6765\u6740\u5c0f\u65b9\u7684\uff0c\u5728\u8fd9\u4e2a\u65e0\u661f\u65e0\u6708\u65e0\u96e8\u6709\u98ce\u7684\u6697\u591c\u4e2d\uff0c\u5c06\u5c0f\u65b9\u523a\u6740\u5728\u4e00\u4e2a\u660f\u9eef\u7684\u6597\u5ba4\u91cc\u3002 \u3000\u3000\u5c0f\u65b9\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u5e94\u8be5\u7acb\u523b\u8df3\u8d77\u6765\uff0c\u63e1\u7d27\u4ed6\u7684\u201c\u9b54\u773c\u201d\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u547c\u5438\u58f0\u6e10\u6e10\u8fd1\u4e86\uff0c\u4ed6\u5df2\u7ecf\u53ef\u4ee5\u542c\u5230\u4ed6\u4eec\u7684\u811a\u6b65\u58f0\uff0c\u4e00\u79cd\u53ea\u6709\u4ed6\u8fd9\u79cd\u4eba\u624d\u80fd\u542c\u5230\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u4e00\u79cd\u53ea\u6709\u66fe\u7ecf\u82e6\u7ec3\u8fc7\u8f7b\u529f\u6216\u5251\u672f\u7684\u4eba\u7279\u6709\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u53ef\u4ee5\u542c\u51fa\u6765\u7684\u6709\u591a\u5c11\u4eba\u4e86\u3002 \u3000\u3000\u6765\u7684\u662f\u56db\u4e2a\u4eba\uff0c\u7edd\u5bf9\u53ea\u56db\u4e2a\u4eba\uff0c\u56db\u4e2a\u66fe\u7ecf\u82e6\u7ec3\u8fc7\u8f7b\u529f\u548c\u5251\u672f\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4ed6\u7684\u638c\u5fc3\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6ca1\u6709\u628a\u63e1\u5bf9\u4ed8\u8fd9\u56db\u4e2a\u4eba\uff0c\u5982\u679c\u4ed6\u4eec\u540c\u65f6\u653b\u51fb\u4ed6\uff0c\u4ed6\u8fde\u4e00\u70b9\u628a\u63e1\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ee4\u4eba\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u811a\u6b65\u5e76\u6ca1\u6709\u4e00\u76f4\u5f80\u8fd9\u91cc\u8d70\u8fc7\u6765\uff0c\u8fdc\u5728\u4e8c\u5341\u4e08\u5916\u5c31\u5df2\u505c\u987f\u3002 \u3000\u3000\u7b49\u5230\u811a\u6b65\u58f0\u518d\u54cd\u8d77\u65f6\uff0c\u6765\u7684\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e00\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u7684\u811a\u6b65\u58f0\u548c\u547c\u5438\u58f0\uff0c\u90fd\u6bd4\u521a\u624d\u91cd\u5f97\u591a\uff0c\u663e\u89c1\u4ed6\u7684\u5fc3\u60c5\u4e5f\u5f88\u7d27\u5f20\uff0c\u751a\u81f3\u6bd4\u5c0f\u65b9\u8fd8\u7d27\u5f20\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u4ed6\u662f\u6765\u6740\u5c0f\u65b9\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u4e00\u4e2a\u4eba\u6765\uff1f \u3000\u3000\u2014\u2014\u4ed6\u7684\u540c\u4f34\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u4ed6\u4e00\u8d77\u51fa\u624b\uff1f \u3000\u3000\u5c0f\u65b9\u60f3\u4e0d\u901a\u2014\u2014\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u65f6\u5019\u53bb\u60f3\u4e86\uff0c\u8fd9\u4e2a\u4eba\u811a\u6b65\u58f0\u5df2\u7ecf\u6765\u5230\u4ed6\u7684\u7a97\u53e3\u3002 \u3000\u3000\u4ece\u9ad8\u539f\u90a3\u8fb9\u5439\u6765\u7684\u98ce\u5439\u8fc7\u8fd9\u4e00\u7247\u5bcc\u9976\u800c\u80a5\u6c83\u7684\u571f\u5730\uff0c\u7a97\u7eb8\u88ab\u5439\u5f97\u7c41\u7c41\u7684\u54cd\uff0c\u5374\u4e0d\u662f\u88ab\u8fd9\u9635\u98ce\u5439\u52a8\u7684\uff0c\u800c\u662f\u88ab\u8fd9\u4e2a\u4eba\u7684\u547c\u5438\u5438\u52a8\u7684\u3002 \u3000\u3000\u4ed6\u7ad9\u5f97\u8ddd\u79bb\u7a97\u6237\u592a\u8fd1\u3002 \u3000\u3000\u5c0f\u65b9\u7acb\u523b\u5224\u65ad\u51fa\u4e00\u4ef6\u4e8b\u2014\u2014\u8fd9\u4e2a\u4eba\u65e0\u7591\u662f\u4e2a\u5f88\u5bb9\u6613\u51b2\u52a8\u7684\u4eba\uff0c\u8eab\u624b\u867d\u7136\u4e0d\u5f31\uff0c\u505a\u8fd9\u79cd\u4e8b\u4e5f\u7edd\u4e0d\u662f\u7b2c\u4e00\u6b21\uff0c\u5374\u8fd8\u662f\u5f88\u5bb9\u6613\u51b2\u52a8\u3002 \u3000\u3000\u4ee5\u9038\u5f85\u52b3\uff0c\u4ee5\u9759\u5236\u52a8\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u65e0\u6570\u6b21\u7684\u51fa\u751f\u5165\u6b7b\u7684\u7ecf\u9a8c\u540e\uff0c\u5c0f\u65b9\u5df2\u7ecf\u975e\u5e38\u660e\u767d\u8fd9\u516b\u5b57\u7684\u8981\u9886\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4ecd\u7136\u4fdd\u6301\u5b89\u9759\uff0c\u7edd\u5bf9\u5b89\u9759\u3002 \u3000\u3000\u5b89\u9759\u4e0d\u662f\u51b7\u9759\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u4e0d\u80fd\u4fdd\u6301\u7edd\u5bf9\u51b7\u9759\uff0c\u56e0\u4e3a\u4ed6\u672c\u6765\u4e5f\u662f\u4e2a\u5f88\u5bb9\u6613\u51b2\u52a8\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u8df3\u4e5f\u5df2\u52a0\u5feb\uff0c\u547c\u5438\u4e5f\u53d8\u5f97\u6bd4\u8f83\u6025\u4fc3\u3002 \u3000\u3000\u7a97\u5916\u7684\u4eba\u5ffd\u7136\u53eb\u4ed6\u7684\u540d\u5b57\uff1a\u201c\u5c0f\u65b9\uff0c\u65b9\u4f1f\uff01\u201d \u3000\u3000\u4ed6\u867d\u7136\u5728\u51b7\u7b11\uff0c\u58f0\u97f3\u5374\u5df2\u56e0\u7d27\u5f20\u800c\u6c99\u54d1\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6ca1\u6709\u7761\u7740\uff0c\u800c\u4e14\u77e5\u9053\u6211\u6765\u4e86\uff0f \u3000\u3000\u5c0f\u65b9\u4fdd\u6301\u5b89\u9759\u3002 \u3000\u3000\u201c\u6211\u662f\u6765\u6740\u4f60\u7684\uff01\u201d\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u6211\u662f\u6765\u6740\u4f60\u7684\uff01\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4ecd\u7136\u4fdd\u6301\u5b89\u9759\u3002 \u3000\u3000\u4e0d\u4ec5\u5b89\u9759\uff0c\u800c\u4e14\u51b7\u9759\uff0c\u4ed6\u5df2\u7ecf\u53d1\u73b0\u8fd9\u4e2a\u4eba\u8fdc\u6bd4\u4ed6\u4ee5\u524d\u66f4\u51b2\u52a8\u3002 \u3000\u3000\u82cd\u767d\u7684\u7a97\u7eb8\u5df2\u7ecf\u88ab\u6253\u6e7f\u4e86\u4e00\u5757\uff0c\u800c\u4e14\u52a8\u5f97\u66f4\u5389\u5bb3\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u7684\u547c\u5438\u66f4\u6025\u4fc3\u3002 \u3000\u3000\u2014\u2014\u4f60\u8981\u6740\u6211\uff0c\u6211\u5f53\u7136\u4e5f\u4e0d\u80fd\u4e0d\u6740\u4f60\u3002 \u3000\u3000\u2014\u2014\u5728\u8fd9\u79cd\u65f6\u5019\u8fd8\u8fd9\u4e48\u51b2\u52a8\uff0c\u5b9e\u5728\u4e0d\u662f\u4ef6\u5f88\u597d\u73a9\u7684\u4e8b\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u7a97\u6237\u7ec8\u4e8e\u88ab\u6253\u5f00\uff0c\u9732\u51fa\u4e86\u4e00\u5f20\u94c1\u9752\u8272\u7684\u8138\uff1a\u975e\u5e38\u82f1\u4fca\uff0c\u975e\u5e38\u5e74\u8f7b\u3002 \u3000\u3000\u201c\u6211\u53eb\u80e1\u5927\u78f7\uff01\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u8981\u6740\u4f60\uff01\u201d \u3000\u3000\u4ed6\u7528\u4e00\u53cc\u660e\u4eae\u9510\u5229\u5374\u5df2\u5145\u6ee1\u8840\u4e1d\u7684\u773c\u775b\u77aa\u7740\u5c0f\u65b9\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\u3002 \u3000\u3000\u201c\u662f\u4f60\u8981\u6765\u6740\u6211\uff0c\u53c8\u4e0d\u662f\u6211\u8981\u6740\u4f60\u3002\u201d\u4ed6\u53cd\u95ee\u8fd9\u4e2a\u5e74\u8f7b\u4eba\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u51fa\u53bb\uff1f\u201d \u3000\u3000\u80e1\u5927\u9cde\u8bf4\u4e0d\u51fa\u8bdd\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u51c6\u5907\u62d4\u5251\uff0c\u5df2\u7ecf\u51c6\u5907\u51b2\u8fdb\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u770b\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u4ed6\u4ece\u672a\u770b\u89c1\u8fc7\u5982\u6b64\u660e\u4eae\u8000\u773c\u8fc5\u75be\u7684\u5251\u5149\u3002 \u3000\u3000\u4ed6\u5f97\u540e\u9000\u3001\u95ea\u907f\uff0c\u540c\u65f6\u4e5f\u62d4\u5251\u53cd\u51fb\u3002 \u3000\u3000\u4ed6\u7684\u52a8\u4f5c\u7edd\u4e0d\u80fd\u7b97\u592a\u6162\uff0c\u53ea\u4e0d\u8fc7\u6162\u4e86\u4e00\u70b9\u800c\u5df2\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u523a\u7684\u662f\u4ed6\u7684\u54bd\u5589\uff0c\u53ef\u662f\u5ffd\u7136\u4e00\u53d8\uff0c\u5c31\u523a\u5165\u4e86\u4ed6\u7684\u5fc3\u810f\u3002 \u3000\u3000\u8fd9\u624d\u662f\u771f\u6b63\u7684\u8981\u5bb3\uff0c\u5fc5\u6b7b\u65e0\u6551\u7684\u8981\u5bb3\u3002 \u3000\u3000\u4f60\u8981\u6740\u6211\uff0c\u6211\u5c31\u4e0d\u80fd\u4e0d\u6740\u4f60\uff01 \u3000\u3000\u80e1\u5927\u9cde\u5fc3\u8df3\u505c\u6b62\u524d\uff0c\u7ec8\u4e8e\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u2014\u2014\u505a\u4e00\u4e2a\u5e73\u51e1\u7684\u4eba\uff0c\u5e76\u4e0d\u53ef\u60b2\u4e5f\u4e0d\u53ef\u803b\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u4e0d\u8be5\u6765\u6740\u4eba\uff0c\u56e0\u4e3a\u4ed6\u672c\u6765\u5c31\u4e0d\u662f\u4e2a\u6740\u4eba\u7684\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u592a\u51b2\u52a8\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u672c\u6765\u5f88\u5e73\u51e1\u7684\u4eba\u4e00\u5b9a\u8981\u53bb\u505a\u4ed6\u4e0d\u8be5\u505a\u7684\u4e8b\uff0c\u624d\u662f\u503c\u5f97\u60b2\u54c0\u3002 \u3000\u3000\u98ce\u8fd8\u5728\u5439\u3002 \u3000\u3000\u8fdc\u65b9\u7684\u9ed1\u6697\u4e2d\uff0c\u8fd8\u6709\u4e09\u4e2a\u4eba\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u548c\u80e1\u5927\u9cde\u4e00\u8d77\u6765\u7684\u3002\u53ef\u662f\u80e1\u5927\u9cde\u7684\u6b7b\uff0c\u5374\u597d\u50cf\u8ddf\u4ed6\u4eec\u8fde\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u4eec\u773c\u76ef\u7740\u5c0f\u65b9\u3002 \u3000\u3000\u521a\u624d\u5c0f\u65b9\u4e00\u5251\u523a\u6740\u80e1\u5927\u78f7\uff0c\u6bcf\u4e00\u4e2a\u52a8\u4f5c\u4ed6\u4eec\u90fd\u6ca1\u6709\u9519\u8fc7\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u516b\u7ae0\u3000\u5168\u9762\u884c\u52a8 \u7b2c\u4e09\u5341\u516b\u7ae0\u3000\u5168\u9762\u884c\u52a8 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u4e4b\u540e\uff0c\u4e09\u4e2a\u4eba\u4e2d\u624d\u6709\u4e00\u4e2a\u4eba\u8d70\u8fc7\u6765\u3002\u8fd9\u4e2a\u4eba\u8d70\u8def\u7684\u59ff\u52bf\u975e\u5e38\u5947\u602a\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u662f\u8981\u6765\u6740\u5c0f\u65b9\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8d70\u8fc7\u6765\u7684\u6837\u5b50\uff0c\u5374\u597d\u50cf\u662f\u4e00\u4e2a\u5b66\u751f\u6765\u89c1\u4ed6\u7684\u5e08\u957f\uff0c\u4e0d\u4f46\u6587\u96c5\u89c4\u77e9\uff0c\u8fd8\u5e26\u7740\u4e00\u70b9\u70b9\u754f\u7f29\u3002 \u3000\u3000\u5c0f\u65b9\u4e00\u773c\u5c31\u770b\u51fa\u4ed6\u662f\u4e2a\u53d7\u8fc7\u826f\u597d\u6559\u517b\u7684\u4eba\uff0c\u800c\u4e14\u4ece\u5c0f\u5c31\u88ab\u7ea6\u675f\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u53ef\u662f\u4ece\u53e6\u4e00\u65b9\u9762\u53bb\u770b\uff0c\u4ed6\u65e0\u7591\u53c8\u662f\u4e2a\u975e\u5e38\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u811a\u6b65\u867d\u7136\u7a33\u91cd\uff0c\u53ef\u662f\u5168\u8eab\u4e0a\u4e0b\u90fd\u5145\u6ee1\u4e86\u6212\u5907\uff0c\u968f\u65f6\u90fd\u4fdd\u6301\u7740\u4e00\u79cd\u6218\u6597\u7684\u59ff\u6001\uff0c\u7edd\u4e0d\u7ed9\u4eba\u4e00\u70b9\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u4ed6\u7684\u624b\u81c2\u867d\u7136\u4e00\u76f4\u662f\u653e\u677e\u7684\uff0c\u53ef\u662f\u4ed6\u7684\u624b\u4e00\u76f4\u90fd\u5728\u4ed6\u7684\u5251\u67c4\u9644\u8fd1\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u4e00\u76f4\u5728\u76ef\u7740\u5c0f\u65b9\u63e1\u5251\u7684\u624b\u3002 \u3000\u3000\u6709\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u9ad8\u624b\u5bf9\u51b3\u65f6\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u603b\u662f\u76ef\u7740\u53e6\u5916\u4e00\u4e2a\u4eba\u7684\u624b\uff0c\u7edd\u4e0d\u662f\u4ef6\u660e\u667a\u4e4b\u4e3e\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e9b\u4eba\u90fd\u8ba4\u4e3a\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u4ece\u53e6\u5916\u4e00\u4e2a\u4eba\u7684\u624b\u4e0a\u770b\u51fa\u4ec0\u4e48\u3002 \u3000\u3000\u90e8\u5206\u4eba\u8ba4\u4e3a\u51b3\u6218\u65f6\u6700\u5e94\u8be5\u6ce8\u610f\u7684\u662f\u5bf9\u65b9\u7684\u773c\u795e\uff0c\u4e5f\u6709\u4e00\u90e8\u5206\u4eba\u8ba4\u4e3a\u6700\u5e94\u8be5\u6ce8\u610f\u7684\u662f\u5bf9\u65b9\u8138\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u7684\u89c2\u5ff5\u5e76\u4e0d\u6b63\u786e\u3002\u56e0\u4e3a\u4ed6\u4eec\u5ffd\u7565\u4e86\u51e0\u70b9\uff1a \u3000\u3000\u2014\u2014\u6740\u4eba\u662f\u8981\u7528\u624b\u7684\u3002 \u3000\u3000\u4e00\u624b\u4e5f\u6709\u8868\u60c5\uff0c\u4e5f\u4f1a\u6cc4\u9732\u51fa\u5f88\u591a\u79d8\u5bc6\u3002 \u3000\u3000\u2014\u2014\u6709\u5f88\u591a\u4eba\u90fd\u53ef\u4ee5\u628a\u81ea\u5df1\u7684\u60c5\u611f\u548c\u79d8\u5bc6\u63a9\u9970\u5f97\u5f88\u597d\uff0c\u751a\u81f3\u628a\u81ea\u5df1\u53d8\u5f97\u50cf\u4e00\u679a\u786c\u679c\u58f3\u4e00\u6837\uff0c\u8ba9\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u4ece\u4ed6\u7684\u8138\u8272\u548c\u773c\u795e\u4e2d\uff0c\u770b\u51fa\u4efb\u4f55\u4e00\u70b9\u4ed6\u4e0d\u613f\u8ba9\u522b\u4eba\u77e5\u9053\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u4f46\u662f\u624b\u5c31\u4e0d\u4e00\u6837\u4e86\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u4f60\u770b\u89c1\u4e00\u4e2a\u4eba\u624b\u4e0a\u7684\u9752\u7b4b\u51f8\u8d77\uff0c\u8840\u7ba1\u66b4\u9732\uff0c\u5c31\u53ef\u4ee5\u77e5\u9053\u4ed6\u7684\u5fc3\u60c5\u4e00\u5b9a\u5f88\u7d27\u5f20\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u4f60\u770b\u89c1\u4e00\u4e2a\u4eba\u7684\u624b\u5728\u53d1\u6296\uff0c\u5c31\u53ef\u4ee5\u77e5\u9053\u4ed6\u4e0d\u4f46\u7d27\u5f20\uff0c\u800c\u4e14\u6050\u60e7\u3001\u6124\u6012\u3001\u6fc0\u52a8\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e9b\u90fd\u662f\u65e0\u6cd5\u63a7\u5236\u63a9\u9970\u7684\uff0c\u56e0\u4e3a\u8fd9\u5b8c\u5168\u662f\u4e00\u79cd\u751f\u7406\u4e0a\u7684\u53cd\u5e94\u3002 \u3000\u3000\u6240\u4ee5\u4e00\u4e2a\u771f\u6b63\u7684\u9ad8\u624b\uff0c\u5728\u751f\u6b7b\u5bf9\u51b3\u65f6\uff0c\u6700\u6ce8\u610f\u7684\u5c31\u662f\u5bf9\u65b9\u7684\u624b\u3002 \u3000\u3000\u6765\u7684\u8fd9\u4e2a\u4eba\u65e0\u7591\u662f\u4e2a\u8eab\u7ecf\u767e\u6218\u3001\u7ecf\u9a8c\u4e30\u5bcc\u7684\u9ad8\u624b\uff0c\u4e0d\u4f46\u52a8\u4f5c\u786e\u5b9e\uff0c\u89c2\u5ff5\u4e5f\u975e\u5e38\u6b63\u786e\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u5728\u76ef\u7740\u4ed6\uff0c\u5374\u6ca1\u6709\u76ef\u7740\u4ed6\u7684\u624b\uff0c\u56e0\u4e3a\u5c0f\u65b9\u77e5\u9053\u8fd9\u79cd\u4eba\u7edd\u4e0d\u4f1a\u5148\u51fa\u624b\u7684\u3002 \u3000\u3000\u5c0f\u65b9\u53ea\u95ee\uff1a\u201c\u4f60\u4e5f\u662f\u6765\u6740\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u5f97\u6211\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u6709\u4ec7\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u4e2a\u597d\u95ee\u9898\uff0c\u6709\u5f88\u591a\u4eba\u6740\u4eba\u90fd\u4e0d\u9700\u8981\u4efb\u4f55\u7406\u7531\u3002 \u3000\u3000\u5c0f\u65b9\u5374\u8fd8\u662f\u8981\u8fd9\u4e48\u95ee\uff0c\u56e0\u4e3a\u4ed6\u9700\u8981\u65f6\u95f4\u6765\u7f13\u548c\u81ea\u5df1\u7684\u60c5\u7eea\uff0c\u4e5f\u9700\u8981\u65f6\u95f4\u6765\u628a\u8fd9\u4e2a\u4eba\u4e86\u89e3\u5f97\u66f4\u591a\u4e00\u70b9\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u65e0\u975e\u56e0\u4e3a\u540c\u6837\u7684\u7406\u7531\u6240\u4ee5\u624d\u56de\u7b54\u2014\u2014 \u3000\u3000\u201c\u6211\u8981\u6740\u4f60\uff0c\u53ea\u56e0\u4e3a\u4f60\u662f\u5c0f\u65b9\uff0c\u8981\u547d\u7684\u5c0f\u65b9\uff0c\u4f60\u53ef\u4ee5\u8981\u522b\u4eba\u7684\u547d\uff0c\u522b\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6765\u8981\u4f60\u547d\uff1f\u201d \u3000\u3000\u4ed6\u53cd\u95ee\u5c0f\u65b9\uff1a\u201c\u8fd9\u7406\u7531\u591f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u201c\u591f\u4e86\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u7edd\u5bf9\u591f\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5c0f\u65b9\u5c31\u5df2\u5148\u51fa\u624b\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u662f\u7edd\u5bf9\u4e0d\u80af\u5148\u51fa\u624b\u7684\uff0c\u4ed6\u7684\u540c\u4f34\u5df2\u7ecf\u7ed9\u4e86\u4ed6\u4e00\u4e2a\u5f88\u597d\u7684\u6559\u8bad\u3002 \u3000\u3000\u4ed6\u4e5f\u60f3\u5b66\u5c0f\u65b9\uff0c\u8981\u4ee5\u9038\u5f85\u52b3\uff0c\u4ee5\u9759\u5236\u52a8\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u8fd8\u662f\u7b97\u9519\u4e86\u4e00\u70b9\u2014\u2014\u5c0f\u65b9\u52a8\u5f97\u5b9e\u5728\u5927\u5feb\u4e86\uff0c\u8fdc\u6bd4\u4ed6\u60f3\u50cf\u7684\u5feb\u5f97\u591a\u3002\u3001 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u9c9c\u8840\u98de\u6e85\u3002\u9b54\u773c\u5df2\u7ecf\u523a\u4eba\u4e86\u8fd9\u4e2a\u4eba\u7684\u54bd\u5589\u3002 \u3000\u3000\u2014\u2014\u5251\u662f\u6b7b\u7684\uff0c\u4eba\u624d\u662f\u6d3b\u7684\uff0c\u5b8c\u5168\u540c\u6837\u7684\u4e00\u5251\u523a\u51fa\u53bb\uff0c\u5f80\u5f80\u4f1a\u6709\u5b8c\u5168\u4e0d\u540c\u7684\u540e\u679c\u3002 \u3000\u3000\u4e0d\u662f\u80f8\u819b\uff0c\u662f\u54bd\u5589\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u5b66\u5251\u7684\u4eba\u5982\u679c\u8981\u60f3\u6d3b\u5f97\u6bd4\u522b\u4eba\u957f\u4e9b\uff0c\u5c31\u8981\u5148\u5b66\u4f1a\u6d3b\u7528\u81ea\u5df1\u638c\u4e2d\u7684\u5251\u3002 \u3000\u3000\u5c0f\u65b9\u65e0\u7591\u5b66\u5230\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u6d3b\u7740\uff0c\u4ed6\u7684\u5bf9\u624b\u5374\u5012\u4e0b\u53bb\uff0c\u8fde\u8fd8\u624b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u5c31\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u770b\u7740\u8fd9\u4e2a\u4eba\u5012\u4e0b\u53bb\uff0c\u5c0f\u65b9\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u7684\u5fc3\u8df3\u5f97\u6bd4\u5e73\u65f6\u5feb\u5f97\u591a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u5bf9\u65b9\u5e76\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\uff0c\u4ece\u672a\u60f3\u5230\u81ea\u5df1\u4e00\u5251\u5c31\u80fd\u5f97\u624b\u3002 \u3000\u3000\u4ed6\u51fa\u624b\u4e4b\u8fc5\u901f\uff0c\u5224\u65ad\u4e4b\u6b63\u786e\uff0c\u7adf\u8fde\u4ed6\u81ea\u5df1\u90fd\u5df2\u7ecf\u60f3\u50cf\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u65e0\u7591\u5df2\u5f80\u524d\u8fc8\u8fdb\u4e86\u4e00\u5927\u6b65\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u4eff\u4f5b\u6709\u4eba\u5728\u53f9\u606f\uff0c\u5c31\u597d\u50cf\u638c\u58f0\u90a3\u6837\u7684\u53f9\u606f\uff0c\u5145\u6ee1\u4e86\u8d5e\u8d4f\u4e4b\u610f\u3002 \u3000\u3000\u201c\u4f60\u4eec\u5f53\u7136\u4e5f\u662f\u6765\u6740\u6211\u7684\u3002\u201d\u5c0f\u65b9\u770b\u7740\u7ad9\u5728\u9ed1\u6697\u4e2d\u7684\u4e24\u4e2a\u4eba\u2026\u2026\uff0c\u201c\u4f60\u4eec\u4e0d\u59a8\u540c\u65f6\u51fa\u624b\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u8fd8\u662f\u7ad9\u7740\u6ca1\u6709\u52a8\uff0c\u53e6\u5916\u4e00\u4e2a\u4eba\u5374\u5df2\u7ecf\u5f00\u59cb\u6162\u9992\u7684\u5f80\u524d\u8d70\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u6bd4\u521a\u624d\u6b7b\u5728\u5c0f\u65b9\u5251\u4e0b\u7684\u90a3\u4e2a\u4eba\u8fd8\u6162\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u76f4\u63a5\u5411\u5c0f\u65b9\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u5c0f\u65b9\u76ef\u7740\u4ed6\uff0c\u76ef\u7740\u4ed6\u7684\u6bcf\u4e00\u4e2a\u52a8\u4f5c\uff0c\u76ef\u7740\u4ed6\u4e00\u53cc\u53d1\u4eae\u7684\u773c\u775b\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5c0f\u65b9\u53d1\u73b0\u81ea\u5df1\u9517\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u662f\u6765\u6740\u4ed6\u7684\uff0c\u53e6\u5916\u4e00\u4e2a\u4eba\u624d\u662f\u653b\u51fb\u7684\u4e3b\u529b\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u53ea\u4e0d\u8fc7\u5728\u8f6c\u79fb\u5c0f\u65b9\u7684\u6ce8\u610f\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5251\uff0c\u4e5f\u6ca1\u6709\u6740\u6c14\u3002 \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u4eba\u5462\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u90a3\u4e2a\u4eba\u5c45\u7136\u5c31\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u6709\u8840\u6709\u8089\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u5ffd\u7136\u6d88\u5931\u7684\uff0c\u53ea\u4e0d\u8fc7\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u5bf9\u9762\u90a3\u4e2a\u4eba\u5df2\u7ecf\u8d70\u5230\u4e00\u682a\u6811\u4e0b\uff0c\u5f88\u60a0\u95f2\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5b8c\u5168\u62b1\u7740\u4e00\u79cd\u65c1\u89c2\u8005\u7684\u6001\u5ea6\u5728\u90a3\u91cc\u89c2\u5bdf\u7740\u5c0f\u65b9\u7684\u53cd\u5e94\uff0c\u4e00\u53cc\u53d1\u4eae\u7684\u773c\u775b\u91cc\u751a\u81f3\u8fd8\u5e26\u7740\u79cd\u6f20\u4e0d\u5173\u5fc3\u7684\u7b11\u610f\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u867d\u7136\u662f\u8ddf\u53e6\u5916\u4e09\u4e2a\u4eba\u4e00\u8d77\u6765\u7684\uff0c\u5374\u597d\u50cf\u6839\u672c\u6ca1\u6709\u628a\u4ed6\u4eec\u7684\u6b7b\u6d3b\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u60f3\u6765\u770b\u770b\u5c0f\u65b9\u600e\u4e48\u6837\u5e94\u4ed8\u4ed6\u4eec\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u662f\u5c0f\u65b9\u7684\u670b\u53cb\uff0c\u4f46\u662f\u4e5f\u4e0d\u50cf\u5c0f\u65b9\u7684\u4ec7\u654c\u3002 \u3000\u3000\u8fd9\u662f\u79cd\u5f88\u5947\u602a\u7684\u6001\u5ea6\uff0c\u5947\u602a\u800c\u6696\u5473\uff0c\u5c31\u597d\u50cf\u4ed6\u8eab\u4e0a\u7a7f\u7740\u7684\u4e00\u8eab\u7070\u8272\u7684\u8863\u670d\u4e00\u6837\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u6001\u5ea6\u4e5f\u5f88\u5947\u602a\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u5728\u6ce8\u610f\u7740\u7ad9\u5728\u5bf9\u9762\u6811\u4e0b\u7684\u8fd9\u4e2a\u4eba\uff0c\u5bf9\u90a3\u4e2a\u5ffd\u7136\u4e0d\u89c1\u4e86\u7684\u53ef\u6015\u5bf9\u624b\uff0c\u53cd\u800c\u597d\u50cf\u5e76\u4e0d\u5728\u610f\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u8fd8\u5bf9\u8fd9\u4e2a\u4eba\u7b11\u4e86\u7b11\uff0c\u8fd9\u4e2a\u7a7f\u7070\u8863\u7684\u4eba\u5c45\u7136\u4e5f\u5bf9\u4ed6\u7b11\u4e86\u7b11\uff0c\u5c45\u7136\u8fd8\u5411\u5c0f\u65b9\u95ee\u597d\uff1a\u201c\u4f60\u597d\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u597d\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6211\u597d\u597d\u5730\u7761\u89c9\u4e86\uff0c\u5374\u6709\u4eba\u65e0\u7f18\u65e0\u6545\u8981\u6765\u6740\u6211\uff0c\u6211\u600e\u4e48\u4f1a\u597d\uff1f\u201d \u3000\u3000\u7070\u8863\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u4f46\u8868\u793a\u540c\u610f\uff0c\u800c\u4e14\u8fd8\u8868\u793a\u540c\u60c5\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u597d\u597d\u5730\u8eba\u5728\u5e8a\u4e0a\uff0c\u5ffd\u7136\u6709\u4e09\u4e2a\u4eba\u8981\u6765\u6740\u6211\uff0c\u6211\u4e5f\u4f1a\u89c9\u5f97\u5f88\u5012\u9709\u7684\u3002\u201d \u3000\u3000\u201c\u53ea\u6709\u4e09\u4e2a\u4eba\u8981\u6765\u6740\u6211\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u4e09\u4e2a\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u4f60\u4e0d\u662f\u6765\u6740\u6211\u7684\uff1f\u201d \u3000\u3000\u7070\u8863\u4eba\u53c8\u5bf9\u5c0f\u65b9\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6211\u4e0d\u662f\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u65e0\u51a4\u65e0\u4ec7\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u548c\u6211\u65e0\u51a4\u65e0\u4ec7\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u6765\u6740\u6211\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u5949\u547d\u800c\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u5949\u8c01\u7684\u547d\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u5415\u4e09\uff1f\u201d \u3000\u3000\u7070\u8863\u4eba\u7528\u5fae\u7b11\u6765\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u73b0\u5728\u4ed6\u4eec\u4e09\u4e2a\u4eba\u91cc\u5df2\u7ecf\u6709\u4e24\u4e2a\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e09\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e09\u4e2a\u4eba\u5f53\u7136\u662f\u6700\u53ef\u6015\u7684\u4e00\u4e2a\u3002\u201d\u7070\u8863\u4eba\u8bf4\uff1a\u201c\u6bd4\u524d\u9762\u4e24\u4e2a\u4eba\u52a0\u8d77\u6765\u90fd\u53ef\u6015\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\u4e2a\u53bb\u6740\u4f60\u7684\u4eba\u53eb\u80e1\u5927\u9cde\uff0c\u7b2c\u4e8c\u4e2a\u53eb\u675c\u6c38\u3002\u201d\u7070\u8863\u4eba\u8bf4\uff1a\u201c\u4ed6\u4eec\u7684\u5251\u6cd5\u90fd\u4e0d\u5f31\uff0c\u6740\u4eba\u7684\u7ecf\u9a8c\u4e5f\u5f88\u4e30\u5bcc\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u4f60\u80fd\u5728\u4e00\u62db\u5185\u5c31\u80fd\u53d6\u4ed6\u4eec\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\uff0c\u53c8\u5fae\u7b11\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u5b9e\u5728\u6bd4\u4ed6\u4eec\u4f30\u8ba1\u7684\u9ad8\u5f97\u591a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u5fae\u7b11\uff01 \u3000\u3000\u201c\u90a3\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4ed6\u4eec\u7684\u5251\u6cd5\u6bd4\u4ed6\u4eec\u81ea\u5df1\u7684\u4f30\u8ba1\u5dee\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u7b2c\u4e09\u4e2a\u4eba\u5c31\u4e0d\u540c\u4e86\uff01\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e09\u4e2a\u4eba\u624d\u662f\u771f\u6b63\u61c2\u5f97\u6740\u4eba\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u524d\u9762\u4e24\u4e2a\u4eba\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u80fd\u77e5\u5df1\u77e5\u5f7c\u3002\u201d\u7070\u8863\u4eba\u8bf4\uff1a\u201c\u4ed6\u4eec\u4e0d\u4f46\u9ad8\u4f30\u4e86\u81ea\u5df1\uff0c\u800c\u4e14\u4f4e\u4f30\u4e86\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u53ef\u662f\u7b2c\u4e09\u4e2a\u4eba\u5bf9\u4f60\u7684\u51fa\u8eab\u5bb6\u4e16\u548c\u6b66\u529f\u7ecf\u9a8c\u90fd\u5df2\u4e86\u5982\u6307\u638c\uff0c\u56e0\u4e3a\u4ed6\u6ca1\u6709\u5230\u8fd9\u91cc\u6765\u6740\u4f60\u4e4b\u524d\uff0c\u5df2\u7ecf\u628a\u4f60\u8fd9\u4e2a\u4eba\u5f7b\u5e95\u7814\u7a76\u8fc7\uff0c\u800c\u4e14\u521a\u624d\u8fd8\u628a\u4f60\u6740\u4eba\u51fa\u624b\u7684\u52a8\u4f5c\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u8fd9\u4e00\u70b9\u3002\u201c\u53ef\u662f\u4f60\u5462\uff1f\u201d\u7070\u8863\u4eba\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u5bf9\u4ed6\u8fd9\u4e2a\u4eba\u77e5\u9053\u591a\u5c11\uff1f\u201d \u3000\u3000\u201d\u6211\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u53f9\u4e86\u53e3\u6c14l\u201c\u6240\u4ee5\u4f60\u5728\u8fd9\u4e00\u65b9\u9762\u5df2\u7ecf\u843d\u4e86\u4e0b\u98ce\uff01\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u627f\u8ba4\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u7ad9\u7740\u7684\u5730\u65b9\uff0c\u662f\u4e2a\u5f88\u7a7a\u65f7\u7684\u5730\u65b9\uff0c\u201d\u7070\u8863\u4eba\u8bf4\uff0c\u201c\u4ece\u56db\u9762\u516b\u65b9\u90fd\u53ef\u4ee5\u770b\u5f97\u5230\u4f60\u3002\u201d \u3000\u3000\u4ed6\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u770b\u4e0d\u770b\u5f97\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4e0d\u89c1\uff0c\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u4e5f\u8bb8\u53ef\u4ee5\u731c\u60f3\u5f97\u5230\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u5df2\u7ecf\u5230\u4e86\u6211\u7684\u8eab\u540e\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u5c31\u5728\u6211\u521a\u624d\u5168\u795e\u8d2f\u6ce8\u5728\u4f60\u8eab\u4e0a\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4ece\u53e6\u4e00\u8fb9\u7ed5\u5230\u6211\u540e\u9762\u53bb\u4e86\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u9732\u51fa\u4e86\u8d5e\u8d4f\u4e4b\u8272\uff1a\u201c\u4f60\u731c\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4ed6\u8bf4\u4e0d\u5b9a\u5c31\u7ad9\u5728\u6211\u540e\u9762\uff0c\u8bf4\u4e0d\u5b9a\u5df2\u7ecf\u8ddd\u79bb\u6211\u5f88\u8fd1\uff0c\u8bf4\u4e0d\u5b9a\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4e00\u76f4\u4e0d\u6562\u56de\u5934\u53bb\u770b\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u4e0d\u6562\u56de\u5934\u3002\u201d\u5c0f\u65b9\u53f9\u606f\uff0c\u201c\u56e0\u4e3a\u6211\u5982\u679c\u56de\u5934\u53bb\u770b\uff0c\u8eab\u6cd5\u4e0a\u4e00\u5b9a\u4f1a\u6709\u7834\u7efd\u9732\u51fa\u6765\uff0c\u4ed6\u5c31\u6709\u673a\u4f1a\u6740\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u60f3\u7ed9\u4ed6\u8fd9\u79cd\u673a\u4f1a\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u4e0d\u60f3\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u5c31\u7b97\u4e0d\u56de\u5934\uff0c\u4ed6\u4e5f\u4e00\u6837\u6709\u673a\u4f1a\u53ef\u4ee5\u6740\u4f60\u7684\uff0c\u201d\u7070\u8863\u4eba\u8bf4\uff1a\u201c\u4ece\u80cc\u540e\u51fa\u624b\u6740\u4eba\u603b\u6bd4\u5f53\u9762\u523a\u6740\u8981\u5bb9\u6613\u4e9b\u3002\u201d \u3000\u3000\u201c\u867d\u7136\u5bb9\u6613\u4e00\u70b9\uff0c\u4e5f\u4e0d\u80fd\u7b97\u592a\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u8fd8\u4e0d\u662f\u6b7b\u4eba\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6211\u8fd8\u6709\u8033\u6735\u53ef\u4ee5\u542c\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u542c\u51fa\u4ed6\u51fa\u624b\u65f6\u7684\u98ce\u58f0\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u7684\u51fa\u624b\u5f88\u6162\uff0c\u6839\u672c\u6ca1\u6709\u98ce\u58f0\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u4ed6\u7684\u51fa\u624b\u591a\u9992\uff0c\u6211\u603b\u4f1a\u6709\u611f\u89c9\u7684\u3002\u201d\u5c0f\u65b9\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u7ec3\u5251\u5341\u4f59\u5e74\uff0c\u8d70\u6c5f\u6e56\u4e5f\u8d70\u4e86\u5341\u4f59\u5e74\uff0c\u5982\u679c\u6211\u8fde\u8fd9\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\uff0c\u6211\u600e\u4e48\u4f1a\u6d3b\u5230\u73b0\u5728\uff1f\u201d \u3000\u3000\u201c\u6709\u7406\u3002\u201d\u7070\u8863\u4eba\u540c\u610f\uff1a\u201c\u7edd\u5bf9\u6709\u7406\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u5982\u679c\u8981\u51fa\u624b\u6740\u6211\uff0c\u5c31\u4e00\u5b9a\u8981\u8003\u8651\u540e\u679c\u3002\u201d \u3000\u3000\u201c\u540e\u679c\uff1f\u201d\u7070\u8863\u4eba\u53c8\u95ee\uff1a\u201c\u4ec0\u4e48\u540e\u679c\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8981\u6211\u7684\u547d\uff0c\u6211\u4e5f\u4f1a\u8981\u4ed6\u7684\u547d\u3002\u201d\u5c0f\u65b9\u7684\u58f0\u97f3\u8fd8\u662f\u5f88\u51b7\u6de1\uff1a\u201c\u5c31\u7b97\u4ed6\u80fd\u628a\u6211\u523a\u6740\u5728\u4ed6\u7684\u5251\u4e0b\uff0c\u6211\u4e5f\u7edd\u4e0d\u4f1a\u8ba9\u4ed6\u6d3b\u7740\u56de\u53bb\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u5730\u95ee\uff1a\u201c\u4f60\u771f\u7684\u6709\u8fd9\u79cd\u628a\u63e1\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u6709\uff01\u201d\u5c0f\u65b9\u8bf4\u201c\u4e0d\u4f46\u6211\u81ea\u5df1\u76f8\u4fe1\u81ea\u5df1\u6709\u628a\u63e1\uff0c\u8fde\u4ed6\u90fd\u4e00\u5b9a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u4e0d\u8ba4\u4e3a\u6211\u6709\u8fd9\u79cd\u628a\u63e1\uff0c\u4e3a\u4ec0\u4e48\u76f4\u7b49\u5230\u73b0\u5728\u8fd8\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u8fd8\u5728\u7b49\u3002\u201d\u7070\u8863\u4eba\u9053\uff1a\u201c\u7b49\u5230\u6709\u66f4\u597d\u7684\u673a\u4f1a\u65f6\u624d\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u4ed6\u7b49\u4e0d\u5230\u7684\uff0c\u201d \u3000\u3000\u201c\u90a3\u4e48\u73b0\u5728\u4f60\u5c31\u4e0d\u8be5\u8ddf\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u65e0\u8bba\u4ec0\u4e48\u4eba\u5728\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u6ce8\u610f\u529b\u90fd\u96be\u514d\u4f1a\u5206\u6563\u3002\u201d\u7070\u8863\u4eba\u9053\uff1a\u201c\u90a3\u65f6\u5019\u4ed6\u5c31\u6709\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fae\u7b11\uff0c\u5ffd\u7136\u95ee\u8fd9\u4e2a\u7070\u8863\u4eba\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u521a\u624d\u9644\u8fd1\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u5c31\u5728\u4f60\u8d70\u5230\u8fd9\u68f5\u6811\u4e0b\u7684\u65f6\u5019\uff0c\u6811\u4e0a\u6709\u4e00\u53ea\u677e\u9f20\u94bb\u8fdb\u4e86\u6d1e\u7a74\uff0c\u9707\u52a8\u4e86\u516d\u7247\u53f6\u5b50\uff0c\u6211\u4eec\u5f00\u59cb\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5de6\u9762\u7684\u8352\u5730\u91cc\u6709\u4e00\u6761\u8757\u86c7\u541e\u4e86\u4e00\u53ea\u7530\u9e21\uff0c\u4e00\u6761\u9ec4\u9f20\u72fc\u521a\u4ece\u524d\u9762\u7684\u5c71\u811a\u4e0b\u8dd1\u8fc7\u53bb\uff0c\u540e\u9762\u5ba2\u6808\u91cc\u6709\u4e00\u5bf9\u592b\u5987\u9192\u4e86\uff0c\u5ba2\u6808\u8001\u677f\u517b\u7684\u4e00\u53ea\u998b\u732b\u6b63\u5728\u53a8\u623f\u91cc\u5077\u9c7c\u5403\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u5403\u60ca\u5730\u770b\u7740\u5c0f\u65b9\uff0c\u5403\u60ca\u5730\u95ee\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u4e0d\u5047\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u4e0d\u7ba1\u6211\u5728\u5e72\u4ec0\u4e48\uff0c\u9644\u8fd1\u4e00\u4e8c\u5341\u4e08\u5185\u7684\u52a8\u9759\uff0c\u90fd\u9003\u4e0d\u8fc7\u6211\u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u5e78\u597d\u6211\u4e0d\u662f\u6765\u6740\u4f60\u7684\u3002\u201d\u4ed6\u82e6\u7b11\uff1a\u201c\u5426\u5219\u73b0\u5728\u6211\u8bf4\u4e0d\u5b9a\u4e5f\u5df2\u7ecf\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5e76\u4e0d\u5426\u8ba4\u3002 \u3000\u3000\u7070\u8863\u4eba\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u65e2\u7136\u660e\u77e5\u4ed6\u8981\u6740\u4f60\uff0c\u65e2\u7136\u660e\u77e5\u4ed6\u5728\u4f60\u7684\u8eab\u540e\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5148\u51fa\u624b\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e0d\u6025\uff0c\u6025\u7684\u662f\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fae\u7b11\uff1a\u201c\u662f\u4ed6\u8981\u6765\u6740\u6211\uff0c\u4e0d\u662f\u6211\u8981\u6740\u4ed6\uff0c\u6211\u5f53\u7136\u6bd4\u4ed6\u6c89\u5f97\u4f4f\u6c14\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u53c8\u53f9\u4e86\u53e3\u6c14\uff01 \u3000\u3000\u201c\u6211\u4f69\u670d\u4f60\uff0c\u771f\u7684\u4f69\u670d\u4f60\u3002\u5982\u679c\u6211\u4eec\u4e0d\u662f\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u76f8\u89c1\uff0c\u6211\u771f\u5e0c\u671b\u4ea4\u4f60\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u4ea4\u670b\u53cb\uff1f\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u662f\u8ddf\u4ed6\u4eec\u4e00\u8d77\u6765\u7684\uff0c\u201d\u7070\u8863\u4eba\u9053\uff1a\u201c\u4f60\u591a\u5c11\u603b\u4e0d\u514d\u5bf9\u6211\u6709\u4e9b\u63d0\u9632\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff01\u201d\u5c0f\u65b9\u6447\u5934\uff1a\u201c\u5982\u679c\u6211\u770b\u4e0d\u51fa\u4f60\u7684\u7528\u5fc3\uff0c\u600e\u4e48\u4f1a\u8ddf\u4f60\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u8fd8\u662f\u53ef\u4ee5\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u201c\u4f46\u662f\u4f60\u6839\u672c\u4e0d\u77e5\u9053\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u3002\u201d\u7070\u8863\u4eba\u8bf4\uff1a\u201c\u4f60\u751a\u81f3\u8fde\u6211\u7684\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u201c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u53c8\u7b11\u4e86\uff0c\u7b11\u5f97\u5f88\u6109\u5feb\uff1a\u201c\u6211\u59d3\u6797\uff0c\u53eb\u6797\u6b63\u96c4\uff0c\u6211\u7684\u670b\u53cb\u90fd\u53eb\u6211\u9a6c\u6c99\u3002\u201d \u3000\u3000\u201c\u9a6c\u6c99\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u540d\u5b57\u5f53\u7136\u4e0d\u4f1a\u5f15\u8d77\u5c0f\u65b9\u7684\u60ca\u8bb6\u548c\u6000\u7591\uff0c\u5c0f\u65b9\u7684\u670b\u53cb\u4e2d\u6709\u5f88\u591a\u4eba\u7684\u540d\u5b57\u90fd\u8fdc\u6bd4\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u66f4\u5947\u602a\u5f97\u591a\u3002 \u3000\u3000\u201c\u6211\u59d3\u65b9\uff0c\u53eb\u65b9\u4f1f\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff01\u201d\u6797\u6b63\u96c4\u8bf4\uff1a\u201c\u6211\u65e9\u5c31\u542c\u89c1\u8fc7\u4f60\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u5411\u5c0f\u65b9\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u7684\u624b\u91cc\u8fd8\u662f\u6ca1\u6709\u5251\uff0c\u5168\u8eab\u4e0a\u4e0b\u8fd8\u662f\u770b\u4e0d\u51fa\u4e00\u70b9\u6740\u6c14\u3002 \u3000\u3000\u4ed6\u5411\u5c0f\u65b9\u8d70\u8fc7\u6765\uff0c\u53ea\u4e0d\u8fc7\u60f3\u8ddf\u5c0f\u65b9\u4eb2\u8fd1\u4eb2\u8fd1\uff0c\u8fd9\u672c\u6765\u5c31\u662f\u4ef6\u5f88\u81ea\u7136\u7684\u4e8b\uff0c\u56e0\u4e3a\u5c0f\u65b9\u5df2\u7ecf\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\u3002 \u3000\u3000\u5c0f\u65b9\u672c\u6765\u5c31\u662f\u5f88\u559c\u6b22\u4ea4\u670b\u53cb\u7684\u4eba\u3002\u672c\u6765\u5c31\u6ca1\u6709\u63d0\u9632\u4ed6\uff0c\u73b0\u5728\u5f53\u7136\u66f4\u4e0d\u4f1a\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5feb\u8981\u8d70\u5230\u5c0f\u65b9\u9762\u524d\u65f6\uff0c\u8138\u8272\u5ffd\u7136\u53d8\u4e86\uff0c\u5ffd\u7136\u5931\u58f0\u4f4e\u547c\uff1a\u201c\u5c0f\u5fc3\uff0c\u5c0f\u5fc3\u540e\u9762\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5fcd\u4e0d\u4f4f\u56de\u5934\u2014\u2014\u65e0\u8bba\u8c01\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u90fd\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u56de\u5934\u7684\u3002 \u3000\u3000\u5c31\u5728\u5c0f\u65b9\u521a\u56de\u8fc7\u5934\u53bb\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u6797\u6b63\u96c4\u5ffd\u7136\u4ece\u8896\u4e2d\u62bd\u51fa\u4e00\u67c4\u5251\u3002 \u3000\u3000\u4e00\u67c4\u767e\u70bc\u7cbe\u94a2\u94f8\u6210\u7684\u8f6f\u5251\uff0c\u8fce\u98ce\u4e00\u6296\uff0c\u6bd2\u86c7\u822c\u523a\u5411\u5c0f\u65b9\u540e\u9888\u3002 \u3000\u3000\u5de6\u540e\u9888\u3002 \u3000\u3000\u5c0f\u65b9\u662f\u4ece\u53f3\u9762\u626d\u8f6c\u5934\u5f80\u540e\u53bb\u770b\u7684\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u7684\u5de6\u540e\u9888\u5f53\u7136\u662f\u4e00\u4e2a\u201c\u7a7a\u95e8\u201d\u3002 \u3000\u3000\u2014\u2014\u201c\u7a7a\u95e8\u201d\u662f\u4e00\u79cd\u6c5f\u6e56\u4eba\u5e38\u7528\u7684\u672f\u8bed\uff0c\u90a3\u610f\u601d\u5c31\u662f\u8bf4\u4ed6\u90a3\u4e2a\u90e8\u4f4d\uff0c\u5c31\u50cf\u662f\u4e00\u6247\u5b8c\u5168\u672a\u8bbe\u9632\u7684\u7a7a\u5c4b\u5927\u95e8\u4e00\u6837\uff0c\u53ea\u8981\u4f60\u9ad8\u5174\uff0c\u4f60\u5c31\u53ef\u4ee5\u8fdb\u53bb\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u5de6\u9888\u540e\u90fd\u6709\u6761\u5927\u8840\u7ba1\uff0c\u662f\u4eba\u8eab\u6700\u4e3b\u8981\u7684\u8840\u8109\u6d41\u52a8\u5904\uff0c\u5982\u679c\u8fd9\u6761\u8840\u7ba1\u88ab\u5272\u65ad\uff0c\u5fc5\u5c06\u6d41\u8840\u4e0d\u6b62\uff0c\u65e0\u6551\u800c\u6b7b\u3002 \u3000\u3000\u4e00\u4e2a\u6709\u7ecf\u9a8c\u7684\u6740\u624b\uff0c\u4e0d\u7b49\u5230\u7edd\u5bf9\u6709\u628a\u63e1\u65f6\u7edd\u4e0d\u51fa\u624b\u3002 \u3000\u3000\u6797\u6b63\u96c4\u65e0\u7591\u5df2\u628a\u63e1\u4f4f\u6700\u597d\u7684\u673a\u4f1a\uff0c\u8fd9\u662f\u4ed6\u81ea\u5df1\u5236\u9020\u7684\u673a\u4f1a\uff0c\u4ed6\u786e\u4fe1\u81ea\u5df1\u8fd9\u4e00\u5251\u7edd\u4e0d\u4f1a\u5931\u624b\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u4ed6\u5bf9\u8fd9\u4e00\u70b9\u786e\u4fe1\u4e0d\u7591\uff0c\u6240\u4ee5\u6839\u672c\u6ca1\u6709\u4e3a\u81ea\u5df1\u7559\u9000\u8def\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u6b7b\u4e86\uff0c\u6b7b\u5728\u5c0f\u65b9\u7684\u5251\u4e0b\uff01 \u3000\u3000\u5c0f\u65b9\u660e\u660e\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u63d0\u9632\u4e4b\u5fc3\uff0c\u800c\u4e14\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u62db\u67b6\u95ea\u907f\u7684\u4f59\u5730\u3002 \u3000\u3000\u6797\u6b63\u96c4\u770b\u51c6\u4e86\u8fd9\u4e00\u70b9\uff0c\u4e5f\u7b97\u51c6\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5251\u523a\u51fa\u65f6\uff0c\u5fc3\u91cc\u7684\u611f\u89c9\u597d\u50cf\u4e00\u4e2a\u9493\u9c7c\u7684\u4eba\u5df2\u7ecf\u611f\u89c9\u5230\u9493\u7aff\u5728\u9707\u52a8\uff0c\u77e5\u9053\u9c7c\u5df2\u4e0a\u94a9\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5c0f\u65b9\u7684\u5251\u5ffd\u7136\u4e5f\u523a\u4e86\u51fa\u6765\uff0c\u4ece\u4e00\u4e2a\u4ed6\u7edd\u5bf9\u60f3\u4e0d\u5230\u7684\u90e8\u4f4d\u523a\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u7684\u5251\u8fd8\u672a\u523a\u5165\u5c0f\u65b9\u7684\u540e\u9888\uff0c\u5c0f\u65b9\u7684\u5251\u5df2\u7ecf\u523a\u5165\u4e86\u4ed6\u7684\u5fc3\u810f\u3002 \u3000\u3000\u5c0f\u65b9\u7684\u5251\u523a\u5165\u4ed6\u7684\u5fc3\u810f\u65f6\uff0c\u4ed6\u7684\u5251\u8ddd\u79bb\u5c0f\u65b9\u540e\u9888\u5df2\u7ecf\u53ea\u6709\u4e00\u5bf8\u3002 \u3000\u3000\u2014\u2014\u4ec5\u4ec5\u53ea\u6709\u4e00\u5bf8\uff0c\u4e00\u5bf8\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u2014\u2014\u751f\u6b7b\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u5f80\u5f80\u6bd4\u4e00\u5bf8\u66f4\u77ed\uff0c\u80dc\u8d1f\u6210\u8d25\u5f97\u5931\u4e4b\u95f4\u5f80\u5f80\u4e5f\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u6240\u4ee5\u4e00\u4e2a\u4eba\u53c8\u4f55\u5fc5\u8ba1\u8f83\u5f97\u5927\u591a\uff1f \u3000\u3000\u51b0\u51b7\u7684\u5251\u950b\u8d34\u7740\u5c0f\u65b9\u7684\u540e\u9888\u6ed1\u8fc7\u53bb\uff0c\u6797\u6b63\u96c4\u63e1\u5251\u7684\u624b\u5df2\u5b8c\u5168\u50f5\u786c\u3002 \u3000\u3000\u5c0f\u65b9\u8eab\u540e\u5ffd\u7136\u53c8\u54cd\u8d77\u4e00\u58f0\u53f9\u606f\uff0c\u4e00\u9635\u638c\u58f0\u3002 \u3000\u3000\u201c\u7cbe\u5f69\u3002\u201d\u4e00\u4e2a\u5f88\u5e73\u51e1\u7684\u58f0\u97f3\u53f9\u606f\u7740\u9053\uff1a\u201c\u7cbe\u5f69\u7edd\u4f26\u3002\u201d \u3000\u3000\u58f0\u97f3\u8ddd\u79bb\u5c0f\u65b9\u5f88\u8fdc\uff0c\u6240\u4ee5\u5c0f\u65b9\u8f6c\u8fc7\u8eab\u3002 \u3000\u3000\u521a\u624d\u4ed6\u626d\u56de\u5934\u65f6\uff0c\u5e76\u6ca1\u6709\u770b\u89c1\u540e\u9762\u6709\u4eba\uff0c\u5f53\u65f6\u4ed6\u773c\u4e2d\u53ea\u6709\u6797\u6b63\u96c4\u548c\u6797\u6b63\u96c4\u7684\u5251\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u770b\u89c1\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u8fdc\u8fdc\u5730\u7ad9\u5728\u9ed1\u6697\u4e2d\uff0c\u548c\u5c0f\u65b9\u4fdd\u6301\u7740\u4e00\u79cd\u4e92\u76f8\u90fd\u5f88\u5b89\u5168\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u56e0\u4e3a\u6c99\u5e73\u4ece\u4e0d\u613f\u8ba9\u4efb\u4f55\u4eba\u5bf9\u4ed6\u6709\u4e00\u70b9\u63d0\u9632\u6000\u7591\u4e4b\u5fc3\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u4e00\u5b9a\u6d3b\u4e0d\u6210\u4e86\u3002\u201d\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6b7b\u7684\u5c45\u7136\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u81ea\u5df1\u4e5f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u624d\u60f3\u5230\u4ed6\u624d\u771f\u6b63\u662f\u7b2c\u4e09\u4e2a\u8981\u6740\u4f60\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8d70\u8fc7\u6765\u7684\u65f6\u5019\u3002\u201d\u5c0f\u65b9\u8bf4\u3002 \u3000\u3000\u201c\u90a3\u65f6\u5019\u8fde\u6211\u90fd\u8ba4\u4e3a\u4f60\u5df2\u7ecf\u613f\u610f\u4ea4\u4ed6\u8fd9\u4e2a\u670b\u53cb\u4e86\uff0c\u4f60\u600e\u4e48\u4f1a\u60f3\u5230\u4ed6\u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u8d70\u8def\u8d70\u5f97\u5927\u5c0f\u5fc3\u4e86\uff0c\u5c31\u597d\u50cf\u6df1\u6015\u4f1a\u8e29\u6b7b\u4e2a\u8682\u8681\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u5c0f\u5fc3\u4e00\u70b9\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u4e00\u70b9\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201d\u50cf\u6211\u4eec\u8fd9\u6837\u7684\u6c5f\u6e56\u4eba\uff0c\u5c31\u7b97\u8e29\u6b7b\u4e03\u767e\u4e2a\u8682\u8681\u4e5f\u4e0d\u5728\u4e4e\u7684\uff0c\u4ed6\u8d70\u8def\u8d70\u5f97\u90a3\u4e48\u5c0f\u5fc3\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u8fd8\u5728\u63d0\u9632\u7740\u6211\u3002\u201d \u3000\u3000\u201c\u6709\u7406\u3002\u201d \u3000\u3000\u201c\u53ea\u6709\u81ea\u5df1\u5fc3\u91cc\u60f3\u53bb\u5bb3\u4eba\u7684\u4eba\uff0c\u624d\u4f1a\u63d0\u9632\u522b\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u2019\u201d\u6211\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff0c\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u5403\u4e8f\u4e0a\u5f53\u7684\uff0c\u901a\u5e38\u90fd\u662f\u4e0d\u60f3\u53bb\u5bb3\u4eba\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u6ca1\u6709\u5bb3\u4eba\u4e4b\u610f\uff0c\u6240\u4ee5\u624d\u6ca1\u6709\u9632\u4eba\u4e4b\u5fc3\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u5982\u679c\u4f60\u4e5f\u66fe\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff0c\u4f60\u5c31\u4f1a\u660e\u767d\u6211\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u53ef\u662f\u6211\u6ca1\u6709\u8fd9\u79cd\u7ecf\u9a8c\u3002\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u4ece\u6765\u90fd\u6ca1\u6709\u76f8\u4fe1\u8fc7\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u5c0f\u65b9\uff0c\u5fae\u7b11\uff1a\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4f60\u66fe\u7ecf\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff0c\u5df2\u7ecf\u53d7\u5230\u8fc7\u60e8\u75db\u7684\u6559\u8bad\uff0c\u6240\u4ee5\u73b0\u5728\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u7684\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u611a\u6211\u4e00\u6b21\uff0c\u5176\u9519\u5728\u4f60\uff0c\u611a\u6211\u4e24\u6b21\uff0c\u5176\u9519\u5728\u6211\u3002\u5982\u679c\u6211\u53d7\u5230\u8fc7\u4e00\u6b21\u6559\u8bad\u540e\uff0c\u8fd8\u4e0d\u77e5\u8b66\u60d5\uff0c\u6211\u5c31\u771f\u7684\u8be5\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u5f97\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6765\u6740\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u5415\u4e09\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u3002 \u3000\u3000\u201c\u662f\u4e0d\u662f\u8ddf\u4ed6\u4eec\u4e00\u8d77\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u6211\u4eec\u90fd\u662f\u5949\u5415\u4e09\u4e4b\u547d\u800c\u6765\u7684\uff0c\u53ea\u4e0d\u8fc7\u6211\u4eec\u5f97\u5230\u7684\u547d\u4ee4\u4e0d\u540c\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e09\u4eba\u662f\u5949\u547d\u6765\u6740\u4f60\uff0c\u6211\u53ea\u4e0d\u8fc7\u5949\u547d\u6765\u770b\u770b\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\u4f60\u4eec\u662f\u600e\u6837\u6740\u4eba\uff1f\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u4e0d\u7ba1\u662f\u4ed6\u4eec\u6740\u4e86\u4f60\uff0c\u8fd8\u662f\u4f60\u6740\u4e86\u4ed6\u4eec\uff0c\u6211\u90fd\u8981\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u770b\u5f97\u5f88\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u5e94\u8be5\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u8fd8\u8981\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u5e26\u4ed6\u4eec\u56de\u53bb\u3002\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u4e0d\u7ba1\u4ed6\u4eec\u662f\u6b7b\u662f\u6d3b\uff0c\u6211\u90fd\u8981\u5e26\u4ed6\u4eec\u56de\u53bb\u3002\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u80af\u4e0d\u80af\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u7b11\u4e86\uff01 \u3000\u3000\u201c\u4ed6\u4eec\u6d3b\u7740\u65f6\u5bf9\u6211\u8fde\u4e00\u70b9\u7528\u5904\u90fd\u6ca1\u6709\uff0c\u6b7b\u4e86\u5bf9\u6211\u8fd8\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u4ed6\u95ee\u6c99\u5e73\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u7559\u4e0b\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u4f60\u80af\u8ba9\u6211\u5e26\u8d70\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u70b9\u5934\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u4e5f\u5e0c\u671b\u4f60\u80fd\u66ff\u6211\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u5e0c\u671b\u4f60\u56de\u53bb\u544a\u8bc9\u5415\u4e09\uff0c\u8bf7\u4ed6\u591a\u591a\u4fdd\u91cd\u81ea\u5df1\uff0c\u597d\u597d\u4fdd\u91cd\u81ea\u5df1\uff0c\u7b49\u6211\u53bb\u89c1\u4ed6\u65f6\uff0c\u5e0c\u671b\u4ed6\u8fd8\u662f\u6d3b\u5f97\u5b89\u7136\u65e0\u6059\u3002\u201d \u3000\u3000\u201c\u4ed6\u4f1a\u7684\uff01\u201d\u6c99\u5e73\u8bf4\uff1a\u201c\u4ed6\u4e00\u5411\u662f\u4e2a\u5f88\u4f1a\u4fdd\u91cd\u81ea\u5df1\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\u6781\u4e86\u3002\u201d\u5c0f\u65b9\u5fae\u7b11\uff1a\u201c\u6211\u771f\u5e0c\u671b\u4ed6\u80fd\u6d3b\u7740\u7b49\u5230\u6211\u53bb\u89c1\u4ed6\u3002\u201d \u3000\u3000\u6c99\u5e73\u4e5f\u540c\u6837\u5fae\u7b11\uff1a\u201c\u6211\u53ef\u4ee5\u4fdd\u8bc1\u4ed6\u6682\u65f6\u8fd8\u4e0d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u5415\u4e09\u5f53\u7136\u4e0d\u4f1a\u6b7b\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u76f8\u4fe1\u4ed6\u7edd\u5bf9\u53ef\u4ee5\u6bd4\u4efb\u4f55\u4e00\u4e2a\u8ddf\u4ed6\u540c\u6837\u5e74\u7eaa\u7684\u4eba\uff0c\u90fd\u6d3b\u5f97\u957f\u4e45\u4e9b\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u76f8\u4fe1\u91d1\u94b1\u662f\u4e07\u80fd\u7684\uff0c\u4e00\u76f4\u8ba4\u4e3a\u4e16\u754c\u4e0a\u6ca1\u6709\u91d1\u94b1\u529e\u4e0d\u5230\u7684\u4e8b\uff0c\u751a\u81f3\u8fde\u5065\u5eb7\u548c\u751f\u547d\u90fd\u5305\u62ec\u5728\u5185\u3002 \u3000\u3000\u4e0d\u7ba1\u4ed6\u60f3\u7684\u662f\u5bf9\u662f\u9519\uff0c\u81f3\u5c11\u4ed6\u76f4\u5230\u73b0\u5728\u4e00\u76f4\u90fd\u6d3b\u5f97\u5f88\u597d\u3002 \u3000\u3000\u4e09\u53f7\u3001\u5341\u4e09\u53f7\u3001\u4e8c\u5341\u4e09\u53f7\u90fd\u6b7b\u4e86\uff0c\u597d\u50cf\u672c\u6765\u5c31\u662f\u4ed6\u610f\u6599\u4e2d\u7684\u4e8b\u3002 \u3000\u3000\u2014\u2014\u4ed6\u660e\u77e5\u4ed6\u4eec\u4e09\u4e2a\u4eba\u5fc5\u6b7b\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u53eb\u4ed6\u4eec\u4e09\u4e2a\u4eba\u53bb\u9001\u6b7b\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u4ed6\u4eec\u540c\u65f6\u51fa\u624b\uff1f \u3000\u3000\u8fd9\u4e00\u70b9\u8fde\u6c99\u5e73\u90fd\u4e0d\u592a\u660e\u767d\u4e86\u3002 \u3000\u3000\u6c99\u5e73\u53ea\u660e\u767d\u7684\u662f\uff1a\u5415\u4e09\u4ea4\u7ed9\u4ed6\u505a\u7684\u4e8b\uff0c\u4ed6\u5c31\u8981\u505a\u5230\uff0c\u65e0\u8bba\u591a\u56f0\u96be\u7684\u4e8b\u4ed6\u90fd\u8981\u505a\u5230\u3002 \u3000\u3000\u2014\u2014\u5415\u4e09\u8981\u4ed6\u5c06\u4ed6\u4eec\u4e09\u4e2a\u4eba\u5e26\u56de\u53bb\uff0c\u4e0d\u7ba1\u6b7b\u6d3b\u90fd\u8981\u5e26\u56de\u53bb\u3002 \u3000\u3000\u6c99\u5e73\u505a\u5230\u4e86\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u4ed6\u4eec\u90fd\u5df2\u6b7b\u5728\u5c0f\u65b9\u5251\u4e0b\uff0c\u5415\u4e09\u4e00\u5b9a\u8981\u5728\u56db\u4e2a\u65f6\u8fb0\u5185\u770b\u5230\u4ed6\u4eec\u7684\u5c38\u4f53\u3002 \u3000\u3000\u8fd9\u662f\u4ef6\u975e\u5e38\u4e0d\u5bb9\u6613\u505a\u5230\u7684\u4e8b\uff0c\u53ef\u662f\u6c99\u5e73\u4e5f\u505a\u5230\u4e86\uff0c\u4ed6\u4eec\u6b7b\u5728\u51cc\u6668\u4e4b\u524d\uff0c\u6b63\u5348\u540e\u5415\u4e09\u5df2\u7ecf\u89c1\u5230\u4e86\u4ed6\u4eec\u7684\u5c38\u4f53\u3002 \u3000\u3000\u2014\u2014\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e4b\u4e0b\u90fd\u4e0d\u80fd\u88ab\u4eba\u8ffd\u67e5\u51fa\u4ed6\u7684\u884c\u8e2a\u3002 \u3000\u3000\u8981\u505a\u5230\u8fd9\u4e00\u70b9\u5f53\u7136\u66f4\u56f0\u96be\uff0c\u73ed\u5bdf\u5df4\u90a3\u548c\u5c0f\u65b9\u5f53\u7136\u7edd\u5bf9\u4e0d\u4f1a\u653e\u8fc7\u4efb\u4f55\u4e00\u4e2a\u53ef\u4ee5\u8ffd\u67e5\u51fa\u5415\u4e09\u85cf\u8eab\u5904\u7684\u673a\u4f1a\uff0c\u4f55\u51b5\u8fd9\u4e2a\u673a\u4f1a\u5f88\u53ef\u80fd\u5df2\u7ecf\u662f\u6700\u540e\u4e00\u6b21\u673a\u4f1a\u3002 \u3000\u3000\u8fde\u8fd9\u4e00\u70b9\u6c99\u5e73\u90fd\u505a\u5230\u4e86\uff0c\u4ed6\u786e\u4fe1\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u4ece\u4ed6\u8fd9\u91cc\u8ffd\u67e5\u51fa\u5415\u4e09\u7684\u4e0b\u843d\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u53ef\u4ee5\u7528\u4ed6\u81ea\u5df1\u7684\u5934\u9885\u6765\u8d4c\u6ce8\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u5982\u6b64\u6709\u628a\u63e1\uff1f \u3000\u3000\u8fd9\u4e09\u4ef6\u4e8b\u4ed6\u662f\u600e\u4e48\u505a\u5230\u7684\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5f53\u7136\u4e0d\u4f1a\u653e\u8fc7\u8fd9\u4e00\u6b21\u673a\u4f1a\uff0c\u5c0f\u65b9\u8fd8\u6ca1\u6709\u5c06\u9a6c\u6c99\u523a\u6740\u5728\u5251\u4e0b\u65f6\uff0c\u73ed\u5bdf\u5df4\u90a3\u5df2\u7ecf\u5c06\u4ed6\u5c5e\u4e0b\u8f7b\u529f\u6700\u4f18\u79c0\u3001\u7ecf\u9a8c\u6700\u4e30\u5bcc\u7684\u8ffd\u8e2a\u597d\u624b\u5168\u90fd\u8c03\u96c6\u6765\u4e86\uff0c\u5728\u6bcf\u4e00\u6761\u8def\u4e0a\u90fd\u5e03\u7f6e\u597d\u57cb\u4f0f\u548c\u773c\u7ebf\u3002 \u3000\u3000\u6c99\u5e73\u5c06\u5c38\u4f53\u5e26\u8d70\u4e4b\u540e\uff0c\u6240\u5230\u8fc7\u6bcf\u4e00\u4e2a\u5730\u65b9\uff0c\u6240\u505a\u8fc7\u7684\u6bcf\u4e00\u4ef6\u4e8b\uff0c\u4ed6\u4eec\u90fd\u8c03\u67e5\u5f97\u5f88\u6e05\u695a\uff0c\u751a\u81f3\u8fde\u4e00\u4e9b\u770b\u6765\u5b8c\u5168\u65e0\u5173\u8981\u7d27\u7684\u5c0f\u5730\u65b9\uff0c\u90fd\u6ca1\u6709\u653e\u8fc7\u3002 \u3000\u3000\u6bcf\u4e00\u70b9\u4ed6\u4eec\u90fd\u4f5c\u4e86\u6781\u8be6\u7ec6\u7684\u62a5\u544a\u3002 \u3000\u3000\u6c99\u5e73\u662f\u7528\u4e00\u8f86\u4ece\u83dc\u573a\u53e3\u96c7\u6765\u7684\u5927\u8f66\uff0c\u5c06\u80e1\u5927\u9cde\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u5c38\u4f53\u5e26\u8d70\u7684\u3002 \u3000\u3000\u5728\u5934\u4e00\u5929\u665a\u4e0a\uff0c\u4ed6\u5c31\u5df2\u96c7\u597d\u4e86\u8fd9\u8f86\u5927\u8f66\uff0c\u4ed8\u4e86\u6bd4\u5e73\u5e38\u4e00\u822c\u60c5\u51b5\u591a\u51fa\u4e94\u500d\u7684\u8f66\u8d44\uff0c\u8981\u8f66\u592b\u901a\u5bb5\u5b88\u5019\u5728\u9644\u8fd1\u3002 \u3000\u3000\u8f66\u592b\u8001\u738b\u5343\u8fd9\u884c\u5df2\u7ecf\u5e72\u4e86\u4e8c\u4e09\u5341\u5e74\uff0c\u8ddf\u4ed6\u4eec\u4e4b\u95f4\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u5173\u7cfb\u3002 \u3000\u3000\u2014\u2014\u4ece\u8fd9\u4e00\u70b9\u770b\u6765\uff0c\u8868\u793a\u4ed6\u5fc3\u91cc\u4e5f\u65e9\u5c31\u6709\u4e86\u51c6\u5907\uff0c\u4e5f\u5df2\u60f3\u5230\u8fd9\u4e09\u4e2a\u4eba\u6050\u6015\u662f\u4e0d\u4f1a\u6d3b\u7740\u56de\u53bb\u7684\u4e86\u3002 \u3000\u3000\u57ce\u91cc\u6700\u5927\u7684\u4e00\u5bb6\u68fa\u6750\u94fa\u53eb\u201c\u67f3\u5dde\u5f20\u8bb0\u201d\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u4e09\u4e5d\u7ae0\u3000\u7b2c\u4e8c\u6b65\u884c\u52a8 \u7b2c\u4e09\u5341\u4e5d\u7ae0\u3000\u7b2c\u4e8c\u6b65\u884c\u52a8 \u3000\u3000\u51cc\u6668\u65f6\uff0c\u6c99\u5e73\u5c31\u5df2\u5c06\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u5c38\u4f53\u5e26\u5230\u4e86\u5f20\u8bb0\uff0c\u51fa\u4e86\u6bd4\u5e73\u5e38\u591a\u4e24\u500d\u7684\u4ef7\u94b1\uff0c\u4e70\u4e0b\u4e86\u4e09\u53e3\u522b\u4eba\u9884\u8ba2\u7684\u4e0a\u597d\u6986\u6728\u68fa\u6750\u3002 \u3000\u3000\u4ed6\u4eb2\u81ea\u76d1\u7763\u201c\u5f20\u8bb0\u201d\u7684\u4f19\u8ba1\uff0c\u5c06\u4e09\u5177\u5c38\u4f53\u5165\u6b93\uff0c\u867d\u7136\u7528\u6700\u597d\u7684\u9999\u6599\u9632\u8150\uff0c\u5374\u4e0d\u51c6\u4efb\u4f55\u4eba\u89e6\u52a8\u4ed6\u4eec\u7684\u5c38\u4f53\uff0c\u751a\u81f3\u8fde\u5bff\u8863\u90fd\u6ca1\u6709\u6362\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53c8\u4eb2\u81ea\u62bc\u8fd0\u8fd9\u4e09\u53e3\u68fa\u6750\u5230\u57ce\u5916\u5c71\u811a\u4e0b\u6700\u5927\u7684\u4e00\u4e2a\u5893\u573a\u53bb\uff0c\u5e26\u7740\u57ce\u91cc\u6700\u6709\u540d\u7684\u4e00\u4f4d\u98ce\u6c34\u5e08\uff0c\u9009\u4e86\u4e00\u5757\u5893\u5730\u3002 \u3000\u3000\u5893\u5730\u5c31\u5728\u5c71\u811a\u4e0b\u7684\u5411\u9633\u5904\uff0c\u6316\u575f\u7684\u4eba\u90fd\u662f\u8fd9\u4e00\u884c\u7684\u8001\u624b\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\u68fa\u6750\u5df2\u4eba\u571f\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u65f6\u8fb0\u4e2d\uff0c\u5893\u7891\u4e5f\u523b\u597d\u4e86\uff0c\u800c\u4e14\u523b\u4e0a\u4e86\u80e1\u5927\u9e9f\u3001\u675c\u6c38\u548c\u6797\u6b63\u96c4\u4e09\u4e2a\u4eba\u7684\u540d\u5b57\u3002 \u3000\u3000\u6c99\u5e73\u53c8\u4eb2\u81ea\u76d1\u7763\u7acb\u7891\u5b89\u539d\uff0c\u8fd8\u66ff\u4ed6\u4eec\u4e0a\u4e86\u9999\u70e7\u4e86\u7eb8\u94b1\u624d\u8d70\u7684\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u8fd8\u7ad9\u5728\u575f\u524d\uff0c\u559d\u4e86\u4e09\u676f\u9152\uff0c\u597d\u50cf\u8fd8\u6389\u4e86\u51e0\u6ef4\u773c\u6cea\u3002 \u3000\u3000\u4ed6\u79bb\u5f00\u90a3\u5893\u573a\u7684\u65f6\u5019\uff0c\u8fd8\u4e0d\u5230\u6b63\u5348\u3002 \u3000\u3000\u4ed6\u505a\u7684\u6bcf\u4ef6\u4e8b\u90fd\u5f88\u6b63\u5e38\uff0c\u90fd\u662f\u4e00\u4e2a\u4eba\u4e3a\u6b7b\u53bb\u7684\u670b\u53cb\u4eec\u505a\u7684\u4e8b\uff0c\u8fde\u4e00\u70b9\u53ef\u7591\u4e4b\u5904\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4f46\u662f\u5348\u65f6\u521a\u8fc7\u4e00\u523b\uff0c\u5415\u4e09\u5c31\u5df2\u7ecf\u89c1\u5230\u80e1\u5927\u78f7\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u5c38\u4f53\u4e86\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9759\u9759\u7684\u542c\u5b8c\u4e86\u4ed6\u5c5e\u4e0b\u7684\u62a5\u544a\uff0c\u6c89\u601d\u4e86\u5f88\u4e45\uff0c\u624d\u62ac\u5934\u95ee\u5750\u5728\u4ed6\u5bf9\u9762\u7684\u5c0f\u65b9\uff1a\u201c\u5415\u4e09\u65e2\u8981\u90a3\u4e09\u4e2a\u4eba\u6765\u6740\u4f60\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u8981\u4ed6\u4eec\u540c\u65f6\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u6211\u4e5f\u60f3\u4e0d\u901a\u8fd9\u4e00\u70b9\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u53ef\u662f\u73b0\u5728\u6211\u5df2\u7ecf\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u5415\u4e09\u5c5e\u4e0b\u9ad8\u624b\u5982\u4e91\uff0c\u90a3\u4e09\u4e2a\u4eba\u5e76\u4e0d\u662f\u4ed6\u653b\u51fb\u7684\u4e3b\u529b\uff0c\u4ed6\u4eec\u7684\u6b7b\u6d3b\uff0c\u5415\u4e09\u5e76\u4e0d\u5728\u4e4e\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\uff0c\u5c31\u7b97\u4ed6\u4eec\u4e09\u4e2a\u4eba\u540c\u65f6\u51fa\u624b\uff0c\u4e5f\u672a\u5fc5\u6740\u5f97\u4e86\u6211\uff0c\u4f55\u51b5\u6211\u4e5f\u53ef\u80fd\u6709\u5e2e\u624b\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u5415\u4e09\u4e00\u5b9a\u4e5f\u770b\u5f97\u5f88\u6e05\u695a\u3002\u4ed6\u4e00\u76f4\u4e0d\u613f\u4e3b\u52a8\u6765\u653b\u51fb\u6211\u4eec\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4e00\u76f4\u4f30\u4e0d\u900f\u6211\u4eec\u7684\u5b9e\u529b\uff0c\u800c\u4e14\u6839\u672c\u627e\u4e0d\u5230\u6211\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8fd9\u4e2a\u4eba\u5c31\u50cf\u662f\u4e00\u9635\u98ce\uff0c\u4ed6\u7684\u884c\u8e2a\u8fdc\u6bd4\u5415\u4e09\u66f4\u96be\u6349\u6478\u3002 \u3000\u3000\u201c\u5415\u4e09\u6700\u4e3b\u8981\u7684\u76ee\u6807\u867d\u7136\u662f\u6211\uff0c\u4e0d\u662f\u4f60\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u53c8\u8bf4\uff0c\u201c\u4f46\u662f\u73b0\u5728\u4ed6\u4e00\u5b9a\u60f3\u5230\u4f60\u662f\u6211\u653b\u51fb\u4ed6\u7684\u4e3b\u8981\u4eba\u624b\uff0c\u6240\u4ee5\u4ed6\u4e00\u5b9a\u8981\u5148\u67e5\u660e\u4f60\u7684\u6b66\u529f\u6df1\u6d45\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d\u5c0f\u65b9\u9053\uff1a\u201c\u4ed6\u6d3e\u90a3\u4e09\u4e2a\u4eba\u6765\uff0c\u4e00\u5b9a\u5c31\u662f\u4e3a\u4e86\u8bd5\u63a2\u6211\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8865\u5145\uff1a\u201c\u90a3\u4e09\u4e2a\u4eba\u7684\u6b66\u529f\u5251\u6cd5\u8def\u6570\u5b8c\u5168\u4e0d\u540c\uff0c\u6740\u4eba\u7684\u65b9\u6cd5\u4e5f\u4e0d\u540c\u3002\u201d \u3000\u3000\u201c\u4ed6\u6d3e\u4ed6\u4eec\u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u770b\u770b\u4f60\u662f\u600e\u4e48\u51fa\u624b\u6740\u4ed6\u4eec\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u518d\u4ece\u4f60\u7684\u51fa\u624b\uff0c\u770b\u4f60\u7684\u5251\u6cd5\u5bb6\u6570\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e00\u76f4\u90fd\u60f3\u4eb2\u624b\u6740\u4e86\u6211\u3002\u201d\u5c0f\u65b9\u82e6\u7b11\uff1a\u201c\u4e3a\u4e86\u8fbe\u5230\u4ed6\u7684\u76ee\u7684\uff0c\u727a\u7272\u4e09\u4e2a\u4eba\u4ed6\u5f53\u7136\u4e0d\u5728\u4e4e\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u771f\u662f\u4e3a\u4e86\u8fd9\u4e2a\u76ee\u7684\u624d\u6d3e\u4ed6\u4eec\u6765\u7684\uff0c\u90a3\u4e48\u4ed6\u4e00\u5b9a\u8981\u5728\u534a\u5929\u5185\u770b\u5230\u4ed6\u4eec\u7684\u5c38\u4f53\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e00\u5b9a\u8981\u770b\u5230\u4ed6\u4eec\u7684\u81f4\u547d\u4f24\u53e3\uff0c\u624d\u80fd\u5b8c\u5168\u660e\u4e86\u4f60\u7684\u51fa\u624b\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u65f6\u95f4\u5982\u679c\u76f8\u9694\u592a\u4e45\uff0c\u4f24\u53e3\u5c31\u4f1a\u6536\u7f29\u53d8\u5f62\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6614\u5e74\u2018\u767d\u4e91\u57ce\u4e3b\uff0c\u53f6\u5b64\u57ce\u7684\u4e00\u5251\u524a\u65ad\u4e86\u4e00\u6bb5\u82b1\u679d\uff0c\u897f\u95e8\u5439\u96ea\u4ece\u82b1\u679d\u7684\u5207\u53e3\u4e0a\uff0c\u5c31\u5df2\u770b\u51fa\u4e86\u4ed6\u7684\u5251\u6cd5\u6df1\u6d45\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u662f\u4f20\u8bf4\uff0c\u4e5f\u4e0d\u662f\u795e\u8bdd\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4e00\u4f4d\u771f\u6b63\u7684\u5251\u6cd5\u9ad8\u624b\uff0c\u7edd\u5bf9\u53ef\u4ee5\u505a\u5230\u8fd9\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u4e0d\u4fe1\u5415\u4e09\u7684\u5251\u6cd5\u5df2\u7ecf\u8fbe\u5230\u4e86\u8fd9\u79cd\u5883\u754c\u3002\u201d \u3000\u3000\u201c\u4f60\u81ea\u5df1\u4e5f\u8bf4\u8fc7\uff0c\u4ed6\u5c5e\u4e0b\u9ad8\u624b\u5982\u4e91\uff0c\u5c31\u7b97\u4ed6\u81ea\u5df1\u505a\u4e0d\u5230\uff0c\u4ed6\u8eab\u8fb9\u4e00\u5b9a\u6709\u4eba\u80fd\u505a\u5230\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u541f\uff1a\u201c\u90a3\u4e48\u6211\u5c31\u66f4\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u95ee\u9053\uff1a\u3001\u201c\u4f60\u4e0d\u61c2\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5415\u4e09\u65e2\u7136\u6025\u7740\u8981\u770b\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u5c38\u4f53\u548c\u4ed6\u4eec\u81f4\u547d\u7684\u4f24\u53e3\uff0c\u4ed6\u5c5e\u4e0b\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u6025\u7740\u8981\u5c06\u4ed6\u4eec\u7684\u5c38\u4f53\u57cb\u846c\uff1f\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u5f88\u4e3b\u8981\u7684\u95ee\u9898\uff0c\u4e5f\u662f\u4e2a\u5f88\u96be\u89e3\u91ca\u56de\u7b54\u7684\u95ee\u9898\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5374\u4eff\u4f5b\u5df2\u7ecf\u77e5\u9053\u4e86\u7b54\u6848\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u95f4\u521a\u624d\u5411\u4ed6\u62a5\u544a\u8fd9\u4ef6\u4e8b\u7ecf\u8fc7\u7684\u4eba\uff1a\u201c\u90a3\u4e09\u4e2a\u4eba\u57cb\u846c\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u57ce\u5916\u5893\u5730\u7684\u5c71\u811a\u4e0b\u5411\u9633\u5904\u3002\u201d \u3000\u3000\u201c\u90a3\u5757\u5730\u662f\u8c01\u9009\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u4e2a\u59d3\u67f3\uff0c\u53eb\u67f3\u4e09\u773c\u7684\u98ce\u6c34\u5e08\u7236\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u5e73\u5e38\u559c\u6b22\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u559c\u6b22\u8d4c\uff0c\u4ed6\u603b\u8ba4\u4e3a\u81ea\u5df1\u4e0d\u4f46\u6562\u8d4c\u5f97\u7cbe\uff0c\u800c\u4e14\u770b\u5f97\u51c6\uff0c\u53ea\u53ef\u60dc\u504f\u504f\u5341\u8d4c\u4e5d\u8f93\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4e0d\u662f\u4e00\u76f4\u5f88\u9700\u8981\u94b1\u7528\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u51b7\u7b11\uff0c\u5ffd\u7136\u56de\u5934\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u613f\u4e0d\u613f\u610f\u8ddf\u6211\u6253\u4e2a\u8d4c\uff1f\u201d \u3000\u3000\u201c\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u6562\u8d4c\u8fd9\u4e2a\u53eb\u67f3\u4e09\u773c\u7684\u4eba\u73b0\u5728\u4e00\u5b9a\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4ece\u672a\u89c1\u8fc7\u67f3\u4e09\u773c\uff0c\u751a\u81f3\u4ece\u6765\u6ca1\u6709\u542c\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u4f46\u8d4c\u8fd9\u4e2a\u4eba\u73b0\u5728\u5df2\u7ecf\u6b7b\u4e86\uff0c\u800c\u4e14\u6562\u8d4c\u8fd9\u4e2a\u4eba\u662f\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u524d\u90a3\u6bb5\u65f6\u5019\u6b7b\u7684\uff0c\u800c\u4e14\u968f\u4fbf\u5c0f\u65b9\u8d4c\u4ec0\u4e48\u90fd\u884c\u3002 \u3000\u3000\u4ed6\u8d4c\u5f97\u5b9e\u5728\u5f88\u8352\u8c2c\u3002 \u3000\u3000\u5c0f\u65b9\u5c45\u7136\u6ca1\u6709\u8d4c\u3002 \u3000\u3000\u5c0f\u65b9\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u600e\u4e48\u786e\u5b9e\u67f3\u4e09\u773c\u5df2\u7ecf\u6b7b\u4e86\uff0c\u53ef\u662f\u5c0f\u65b9\u77e5\u9053\u4ed6\u4ece\u6765\u4e0d\u505a\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u76f8\u4fe1\u5982\u679c\u73ed\u5bdf\u5df4\u90a3\u80af\u8ddf\u522b\u4eba\u6253\u8d4c\uff0c\u5c31\u4e00\u5b9a\u4e0d\u4f1a\u8f93\u7684\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u679c\u7136\u6ca1\u6709\u8f93\u3002 \u3000\u3000\u67f3\u4e09\u773c\u679c\u7136\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6b7b\u5728\u4ed6\u81ea\u5df1\u7684\u5e8a\u4e0a\u3002 \u3000\u3000\u8fd8\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u51fa\u53bb\u8c03\u67e5\u7684\u4eba\u5c31\u5df2\u7ecf\u56de\u6765\u4e86\uff0c\u8bc1\u5b9e\u4e86\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u201c\u67f3\u4e09\u773c\u662f\u88ab\u4eba\u7528\u4e00\u6839\u7af9\u7b77\u523a\u7a7f\u54bd\u5589\u800c\u6b7b\u7684\uff0c\u6740\u6b7b\u4ed6\u7684\u4eba\u624b\u6cd5\u5e72\u51c0\u5229\u843d\uff0c\u6ca1\u6709\u7559\u4e0b\u4e00\u70b9\u75d5\u8ff9\u7ebf\u7d22\uff0c\u9644\u8fd1\u7684\u4eba\u4e5f\u6ca1\u6709\u542c\u89c1\u4e00\u70b9\u52a8\u9759\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e00\u70b9\u90fd\u4e0d\u60ca\u5947\uff0c\u8fd9\u672c\u6765\u5c31\u662f\u4ed6\u9884\u6599\u4e2d\u7684\u4e8b\u3002 \u3000\u3000\u60ca\u5947\u7684\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u95ee\u73ed\u5bdf\u5df4\u90a3\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4e00\u5b9a\u4f1a\u6b7b\u7684\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e0d\u56de\u7b54\uff0c\u53ea\u6de1\u6de1\u5730\u7b11\u4e86\u7b11\uff1a\u201c\u8fd8\u6709\u4ef6\u4e8b\u6211\u4e5f\u53ef\u4ee5\u8ddf\u4f60\u6253\u8d4c\uff0c\u968f\u4fbf\u4f60\u8d4c\u4ec0\u4e48\u90fd\u884c\u3002\u201d \u3000\u3000\u201c\u8fd9\u6b21\u4f60\u8d4c\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u6562\u8d4c\u80e1\u5927\u78f7\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u68fa\u6750\u73b0\u5728\u5df2\u7ecf\u4e0d\u5728\u4ed6\u4eec\u7684\u575f\u5893\u91cc\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u4fe1\u3002 \u3000\u3000\u6b7b\u4eba\u5df2\u7ecf\u5165\u68fa\uff0c\u68fa\u6750\u5df2\u7ecf\u4eba\u58eb\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u4e0d\u89c1\u4e86\u5462\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u51ed\u4ec0\u4e48\u6562\u6253\u8fd9\u79cd\u8d4c\uff1f\u5c0f\u65b9\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u8981\u8ddf\u4ed6\u8d4c\u4e00\u8d4c\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u603b\u7b97\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u82e5\u771f\u7684\u8d4c\u4e86\uff0c\u4ed6\u5c31\u771f\u7684\u8f93\u4e86\uff0c\u8d4c\u591a\u5c11\u5c31\u8f93\u591a\u5c11\u3002 \u3000\u3000\u80e1\u5927\u9cde\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u68fa\u6750\uff0c\u5c45\u7136\u771f\u7684\u5df2\u7ecf\u4e0d\u5728\u4ed6\u4eec\u7684\u575f\u5893\u91cc\u3002 \u3000\u3000\u575f\u5893\u5df2\u7ecf\u662f\u7a7a\u7684\u3002 \u3000\u3000\u4e09\u53e3\u88c5\u7740\u4e09\u4e2a\u6b7b\u4eba\u7684\u4e0a\u597d\u6355\u6728\u68fa\u6750\u5f53\u7136\u4e0d\u4f1a\u5ffd\u7136\u51ed\u7a7a\u6d88\u5931\u3002 \u3000\u3000\u8fd9\u4e09\u53e3\u68fa\u6750\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u4e16\u4e0a\u6709\u5f88\u591a\u770b\u6765\u5f88\u590d\u6742\u7384\u5999\u7684\u4e8b\uff0c\u7b54\u6848\u5f80\u5f80\u90fd\u5f88\u7b80\u5355\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u68fa\u6750\u662f\u5728\u5730\u9053\u4e2d\u88ab\u4eba\u8fd0\u8d70\u7684\u3002 \u3000\u3000\u2014\u2014\u5c71\u811a\u8fb9\u8fd9\u5757\u5411\u9633\u7684\u575f\u5730\u4e0b\u9762\uff0c\u65e9\u5df2\u6316\u597d\u4e86\u4e00\u6761\u5f88\u957f\u7684\u5730\u9053\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u95ee\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u4f60\u603b\u8be5\u5df2\u7ecf\u660e\u767d\uff0c\u6211\u4e3a\u4ec0\u4e48\u80fd\u786e\u5b9a\u67f3\u4e09\u773c\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u5df2\u7ecf\u660e\u767d\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u5f00\u53e3\u3002\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u53d1\u73b0\uff0c\u5728\u73ed\u5bdf\u5df4\u90a3\u9762\u524d\u8fd8\u662f\u95ed\u7740\u5634\u6bd4\u8f83\u597d\u3002 \u3000\u3000\u6240\u4ee5\u73ed\u5bdf\u5df4\u90a3\u53ea\u6709\u81ea\u5df1\u89e3\u91ca\u3002 \u3000\u3000\u201c\u57cb\u846c\u8fd9\u4e09\u53e3\u68fa\u6750\u7684\u4eba\u540d\u53eb\u6c99\u5e73\uff0c\u5728\u6c5f\u6e56\u4e2d\u867d\u7136\u6ca1\u6709\u540d\uff0c\u5374\u662f\u5415\u4e09\u5c5e\u4e0b\u6700\u5f97\u529b\u7684\u52a9\u624b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5df2\u7ecf\u770b\u51fa\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u201c\u4ed6\u65e9\u5df2\u51c6\u5907\u597d\u8fd9\u5757\u5893\u5730\uff0c\u65e9\u5df2\u5728\u4e0b\u9762\u6316\u597d\u4e86\u8fd9\u6761\u5730\u9053\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u53c8\u89e3\u91ca\uff1a\u201c\u4e3a\u4e86\u907f\u514d\u6211\u4eec\u6000\u7591\u6240\u4ee5\u624d\u627e\u67f3\u4e09\u773c\u505a\u5e4c\u5b50\u3002\u201d\u4ed6\u53c8\u8865\u5145\uff1a\u201c\u67f3\u4e09\u773c\u6b63\u9700\u8981\u94b1\u7528\uff0c\u6c99\u5e73\u5c31\u7528\u94b1\u4e70\u901a\u4e86\u4ed6\uff0c\u7b49\u5230\u4e8b\u6210\u540e\uff0c\u5f53\u7136\u5c31\u6740\u4e86\u4ed6\u706d\u53e3\u3002\u201d \u3000\u3000\u7528\u4e00\u8981\u7af9\u7b77\u5c06\u4eba\u523a\u6740\u4e8e\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u6c99\u5e73\u7684\u51fa\u624b\u65e0\u7591\u6bd4\u9a6c\u6c99\u66f4\u5feb\u66f4\u51c6\u66f4\u72e0\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u7684\u667a\u8c0b\u6bd4\u4ed6\u7684\u51fa\u624b\u66f4\u53ef\u6015\uff0c\u56e0\u4e3a\u4ed6\u80fd\u60f3\u5f97\u51fa\u8fd9\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u6cd5\u5b50\u65e0\u7591\u662f\u552f\u4e00\u80fd\u9003\u8fc7\u73ed\u5bdf\u5df4\u90a3\u5c5e\u4e0b\u8feb\u8e2a\u7684\u6cd5\u5b50\u3002\u4e5f\u53ea\u6709\u7528\u8fd9\u4e2a\u6cd5\u5b50\u624d\u80fd\u5c3d\u5feb\u5730\u628a\u4ed6\u4eec\u4e09\u4e2a\u4eba\u7684\u5c38\u4f53\u9001\u5230\u5415\u4e09\u90a3\u91cc\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u7ec8\u4e8e\u5f00\u53e3\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4e09\u53e3\u88c5\u7740\u4e09\u4e2a\u6b7b\u4eba\u7684\u6960\u6728\u68fa\u6750\uff0c\u603b\u4e0d\u4f1a\u51ed\u7a7a\u98de\u8d70\u7684\uff0c\u4e0d\u7ba1\u8fd9\u4e09\u53e3\u68fa\u6750\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u603b\u8981\u6709\u4eba\u53bb\u62ac\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u62ac\u7740\u8fd9\u4e48\u91cd\u7684\u4e09\u53e3\u68fa\u6750\uff0c\u4e0d\u7ba1\u8d70\u5230\u54ea\u91cc\u53bb\uff0c\u591a\u5c11\u603b\u4f1a\u7559\u4e0b\u4e00\u70b9\u75d5\u8ff9\u6765\u3002\u201d \u3000\u3000\u201c\u6309\u7406\u8bf4\u5e94\u8be5\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u8ffd\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u8981\u53bb\u8ffd\uff0c\u6211\u4eec\u5c31\u53bb\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u8fd8\u53ef\u4ee5\u8ddf\u4f60\u518d\u6253\u4e00\u6b21\u8d4c\u3002\u201d \u3000\u3000\u201c\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u6562\u8d4c\u6211\u4eec\u4e00\u5b9a\u8feb\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u5c0f\u65b9\u8fd8\u662f\u6ca1\u6709\u8d4c\u3002 \u3000\u3000\u5730\u9053\u7684\u51fa\u53e3\u5728\u5c71\u9634\u3002 \u3000\u3000\u51fa\u53e3\u5f53\u7136\u6709\u75d5\u8ff9\u7559\u4e0b\u6765\u3002\u65e0\u8bba\u51fa\u53e3\u5916\u9762\u662f\u8349\u5730\u662f\u5e72\u5730\u8fd8\u662f\u6ce5\u5730\uff0c\u8981\u5c06\u4e09\u53e3\u68fa\u6750\u8fd0\u8d70\uff0c\u5730\u4e0a\u90fd\u4e00\u5b9a\u4f1a\u6709\u75d5\u8ff9\u7559\u4e0b\u6765\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u4eec\u662f\u7528\u4eba\u62ac\u8fd8\u662f\u7528\u8f66\u8f7d\u90fd\u4e00\u6837\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u65b9\u8fd9\u4e00\u6b21\u5982\u679c\u548c\u73ed\u5bdf\u5df4\u90a3\u6253\u4e86\u8d4c\uff0c\u8f93\u7684\u8fd8\u662f\u5c0f\u65b9\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5730\u9053\u51fa\u53e3\u5916\u4e0d\u8fdc\u5904\uff0c\u5c31\u6709\u4e00\u6761\u5c0f\u5c0f\u7684\u6cb3\u6d41\uff0c\u6c34\u6d41\u867d\u7136\u6e4d\u6025\uff0c\u8981\u7528\u7f8a\u76ae\u7b4f\u5b50\u8fd0\u8d70\u4e09\u53e3\u68fa\u6750\uff0c\u8fd8\u662f\u53ef\u4ee5\u505a\u5f97\u5230\u7684\u3002 \u3000\u3000\u65e0\u8bba\u662f\u6cb3\u6c34\u662f\u6e56\u6c34\u8fd8\u662f\u6d77\u6c34\uff0c\u6c34\u4e0a\u90fd\u7edd\u4e0d\u4f1a\u6709\u4efb\u4f55\u75d5\u8ff9\u7559\u4e0b\u6765\u3002 \u3000\u3000\u88ab\u8feb\u8e2a\u7684\u4eba\u53ea\u8981\u4e00\u4e0b\u4e86\u6c34\uff0c\u5c31\u7b97\u662f\u54c1\u79cd\u6700\u4f18\u79c0\u3001\u8bad\u7ec3\u6700\u4e25\u683c\u7684\u730e\u72ac\uff0c\u90fd\u8ffd\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u84dd\u8272\u7684\u7a79\u82cd\uff0c\u82cd\u7fe0\u7684\u5c71\u8109\uff0c\u6e4d\u6025\u7684\u6cb3\u6d41\uff0c\u6cb3\u6ee8\u6709\u4e00\u6392\u53f6\u5b50\u5df2\u5f00\u59cb\u51cb\u96f6\u7684\u5927\u6811\u3002 \u3000\u3000\u6811\u4e0b\u6709\u4eba\uff0c\u5f88\u591a\u4eba\u2014\u2014\u53ea\u6709\u4eba\uff0c\u6ca1\u6709\u68fa\u6750\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u73ed\u5bdf\u5df4\u90a3\u4e00\u8d70\u51fa\u5730\u9053\uff0c\u5c31\u6709\u4e00\u4e2a\u4eba\u5411\u4ed6\u4eec\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u975e\u5e38\u6709\u89c4\u77e9\u7684\u4eba\uff0c\u8d70\u8def\u7684\u6837\u5b50\u89c4\u89c4\u77e9\u77e9\uff0c\u7a7f\u7684\u8863\u670d\u89c4\u89c4\u77e9\u77e9\uff0c\u8a00\u8bed\u795e\u6001\u4e5f\u89c4\u89c4\u77e9\u77e9\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u4f1a\u8ba9\u4eba\u89c9\u5f97\u8fc7\u4efd\u3002 \u3000\u3000\u5c0f\u65b9\u4ee5\u524d\u89c1\u8fc7\u8fd9\u79cd\u4eba\uff0c\u4f46\u4ece\u672a\u60f3\u5230\u4f1a\u5728\u8fd9\u79cd\u5730\u65b9\u89c1\u5230\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u2014\u2014\u540d\u95e8\u4e16\u5bb6\u4e2d\u7684\u4ec6\u5f79\u603b\u7ba1\uff0c\u5386\u53f2\u60a0\u4e45\u7684\u9152\u697c\u5e97\u94fa\u4e2d\u7684\u638c\u67dc\uff0c\u901a\u5e38\u90fd\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u901a\u5e38\u90fd\u662f\u5c0f\u53ae\u5b66\u5f92\u51fa\u8eab\uff0c\u4ece\u5c0f\u5c31\u53d7\u5230\u522b\u4eba\u65e0\u6cd5\u60f3\u50cf\u7684\u4e25\u683c\u8bad\u7ec3\uff0c\u5386\u5c3d\u8270\u82e6\u624d\u722c\u5347\u5230\u73b0\u5728\u8fd9\u79cd\u5730\u4f4d\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u505a\u51fa\u4efb\u4f55\u4e00\u4ef6\u903e\u8d8a\u89c4\u77e9\u7684\u4e8b\uff0c\u7edd\u4e0d\u4f1a\u8ba9\u4efb\u4f55\u4eba\u89c9\u5f97\u8ba8\u538c\u3002 \u3000\u3000\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u600e\u4e48\u4f1a\u5728\u8fd9\u79cd\u5730\u65b9\u51fa\u73b0\uff1f \u3000\u3000\u73b0\u5728\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u8d70\u8fc7\u6765\u4e86\uff0c\u5411\u5c0f\u65b9\u548c\u73ed\u5bdf\u5df4\u90a3\u5fae\u7b11\u884c\u793c\u3002 \u3000\u3000\u201c\u5c0f\u4eba\u5415\u606d\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u53cc\u53e3\u5415\uff0c\u606d\u656c\u7684\u606d\u3002\u201d \u3000\u3000\u4ed6\u7684\u5fae\u7b11\u548c\u6001\u5ea6\u867d\u7136\u606d\u8c28\u6709\u793c\uff0c\u5374\u4e0d\u4f1a\u8ba9\u4eba\u89c9\u5f97\u6709\u4e00\u70b9\u8c04\u5a9a\u7684\u611f\u89c9\uff1a\u201c\u4e09\u7237\u7279\u5730\u8981\u5c0f\u4eba\u5728\u8fd9\u91cc\u606d\u5019\u4e24\u4f4d\u7684\u5927\u9a7e\u3002\u201d \u3000\u3000\u201c\u4e09\u7237\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u5415\u4e09\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u8981\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\u4eec\u5e72\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u8981\u4f60\u5e26\u6211\u4eec\u53bb\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7792\u4e24\u4f4d\u8bf4\uff0c\u5c0f\u4eba\u867d\u7136\u5df2\u8ddf\u968f\u4e09\u7237\u591a\u5e74\uff0c\u53ef\u662f\u4e09\u7237\u7684\u884c\u8e2a\uff0c\u8fde\u5c0f\u4eba\u4e5f\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u8bda\u6073\uff0c\u5c31\u7b97\u662f\u7591\u5fc3\u75c5\u6700\u91cd\uff0c\u6700\u4f1a\u731c\u7591\u7684\u5987\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u8ba4\u4e3a\u4ed6\u8bf4\u7684\u662f\u8c0e\u8bdd\u3002 \u3000\u3000\u2014\u2014\u5947\u602a\u7684\u662f\uff0c\u6700\u4f1a\u731c\u7591\u7684\u5987\u4eba\uff0c\u6709\u65f6\u5019\u53cd\u800c\u4f1a\u504f\u504f\u76f8\u4fe1\u4e00\u4e9b\u522b\u4eba\u90fd\u4e0d\u4fe1\u7684\u4e8b\uff0c\u6700\u4e0d\u53ef\u9760\u7684\u4e8b\u3002 \u3000\u3000\u5c0f\u65b9\u548c\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u7591\u5fc3\u75c5\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u4e0d\u662f\u5987\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u90fd\u76f8\u4fe1\u5415\u606d\u8bf4\u7684\u4e0d\u662f\u8c0e\u8bdd\uff0c\u56e0\u4e3a\u8bf4\u8c0e\u7684\u4eba\u5728\u4ed6\u4eec\u9762\u524d\u4e00\u773c\u5c31\u4f1a\u88ab\u770b\u51fa\u6765\u3002 \u3000\u3000\u6240\u4ee5\u5c0f\u65b9\u53c8\u95ee\uff1a\u201c\u5415\u4e09\u8981\u4f60\u6765\u627e\u6211\u4eec\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e09\u7237\u8ddf\u4e24\u4f4d\u795e\u4ea4\u5df2\u4e45\uff0c\u5df2\u7ecf\u6709\u5f88\u4e45\u672a\u66fe\u76f8\u89c1\u3002\u201d\u5415\u606d\u8bf4\uff1a\u201c\u6240\u4ee5\u7279\u5730\u8981\u5c0f\u4eba\u5230\u8fd9\u91cc\u6765\u7b49\u5019\u4e24\u4f4d\uff0c\u66ff\u4ed6\u62db\u5f85\u4e24\u4f4d\u4e00\u987f\u4fbf\u996d\u3002\u201d \u3000\u3000\u201c\u4ed6\u8981\u4f60\u66ff\u4ed6\u8bf7\u6211\u4eec\u5403\u996d\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0f\u5415\u606d\u8bf4\uff1a\u201c\u53ea\u4e0d\u8fc7\u662f\u4e00\u987f\u4e0d\u6210\u656c\u610f\u7684\u5bb6\u5e38\u4fbf\u996d\u3002\u201d \u3000\u3000\u2014\u2014\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u8bf7\u5c0f\u65b9\u548c\u73ed\u5bdf\u5df4\u90a3\u5403\u996d\uff1f \u3000\u3000\u2014\u2014\u96be\u9053\u8fd9\u53c8\u662f\u4e2a\u9677\u997c\uff1f \u3000\u3000\u2014\u2014\u996d\u83dc\u4e2d\u662f\u4e0d\u662f\u53c8\u4e0b\u4e86\u80fd\u6740\u4eba\u4e8e\u65e0\u5f62\u65e0\u5f71\u4e2d\u7684\u5267\u6bd2\uff01 \u3000\u3000\u5c0f\u65b9\u770b\u770b\u73ed\u5bdf\u5df4\u90a3\uff0c\u73ed\u5bdf\u5df4\u90a3\u4e5f\u770b\u770b\u5c0f\u65b9\u3002 \u3000\u3000\u201c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u53bb\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u5403\u8fc7\u5bb6\u5e38\u4fbf\u996d\u4e86\u3002\u201d \u3000\u3000\u5415\u606d\u6ca1\u6709\u8bf4\u8c0e\uff0c\u5415\u4e09\u8bf7\u5c0f\u65b9\u548c\u73ed\u5bdf\u5df4\u90a3\u5403\u7684\u786e\u5b9e\u662f\u987f\u5f88\u666e\u901a\u7684\u5bb6\u5e38\u4fbf\u996d\u3002 \u3000\u3000\u53ef\u662f\u4ece\u53e6\u5916\u4e00\u65b9\u9762\u770b\u6765\uff0c\u8fd9\u987f\u5f88\u666e\u901a\u7684\u5bb6\u5e38\u4fbf\u996d\u53c8\u5f88\u7279\u522b\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u662f\u4e2a\u5f88\u7279\u522b\u7684\u4eba\uff0c\u4ed6\u559c\u6b22\u5b64\u72ec\uff0c\u559c\u6b22\u6d41\u6d6a\u3002 \u3000\u3000\u4ed6\u901a\u5e38\u90fd\u662f\u4e00\u4e2a\u4eba\u72ec\u5904\u5728\u90a3\u4e00\u7247\u5bc2\u5bde\u51b7\u9177\u65e0\u60c5\u7684\u5927\u6f20\u91cc\uff0c\u4ee5\u82cd\u5927\u529b\u88ab\uff0c\u4ee5\u5927\u5730\u4e3a\u5e8a\uff0c\u53ea\u8981\u80fd\u5145\u9965\u7684\u4e1c\u897f\uff0c\u4ed6\u90fd\u80fd\u5403\u5f97\u4e0b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8981\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6700\u559c\u6b22\u5403\u7684\uff0c\u5e76\u4e0d\u662f\u4ed6\u7ecf\u5e38\u5403\u7684\u5e72\u7cae\u8089\u812f\u9752\u68f5\u997c\u3002 \u3000\u3000\u4ed6\u6700\u559c\u6b22\u7684\u662f\u8471\u6ce5\uff0c\u4e00\u79cd\u98ce\u5473\u6781\u7279\u6b8a\u7684\u8471\u6ce5\uff0c\u7528\u8471\u6ce5\u6765\u62cc\u7684\u996d\uff0c\u521a\u51fa\u9505\u7684\u767d\u996d\u3002 \u3000\u3000\u5bf9\u4e00\u4e2a\u7ec8\u5e74\u6d41\u6d6a\u5728\u5927\u6f20\u91cc\u7684\u4eba\u6765\u8bf4\uff0c\u767d\u996d\u8fdc\u6bd4\u4efb\u4f55\u98df\u7269\u90fd\u96be\u6c42\u3002 \u3000\u3000\u5415\u4e09\u8981\u5415\u606d\u4e3a\u4ed6\u4eec\u51c6\u5907\u7684\u5c31\u662f\u8471\u6ce5\u62cc\u767d\u996d\u3002 \u3000\u3000\u5c0f\u65b9\u662f\u4e2a\u6d6a\u5b50\u3002 \u3000\u3000\u2014\u2014\u2014\u4e2a\u6ca1\u6709\u6839\u7684\u6d6a\u5b50\uff0c\u5c31\u50cf\u662f\u98ce\u4e2d\u7684\u843d\u53f6\uff0c\u6c34\u4e2d\u7684\u6d6e\u840d\u3002 \u3000\u3000\u4f46\u662f\u5f53\u4ed6\u5348\u591c\u9152\u9192\uff0c\u4e0d\u80fd\u6210\u7720\u65f6\uff0c\u4ed6\u6700\u60f3\u7684\u5c31\u662f\u4ed6\u7684\u5bb6\uff0c\u4ed6\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u4ed6\u4e5f\u66fe\u6709\u8fc7\u5bb6\u3002\u4ed6\u7684\u5bb6\u7b80\u964b\u6e05\u8d2b\uff0c\u51e0\u4e4e\u5f88\u96be\u5f97\u6709\u5403\u8089\u7684\u65e5\u5b50\u3002 \u3000\u3000\u4f46\u662f\u4e00\u4e2a\u6bcd\u4eb2\u5bf9\u4e00\u4e2a\u72ec\u751f\u5b50\u7684\u7231\u5fc3\uff0c\u5374\u6c38\u8fdc\u4e0d\u4f1a\u56e0\u4e3a\u4efb\u4f55\u539f\u56e0\u800c\u6539\u53d8\u7684\u3002 \u3000\u3000\u4ed6\u7684\u6bcd\u4eb2\u4e5f\u50cf\u522b\u7684\u6bcd\u4eb2\u4e00\u6837\uff0c\u603b\u5e0c\u671b\u81ea\u5df1\u7684\u513f\u5b50\u80fd\u591f\u957f\u5f97\u9ad8\u5927\u5065\u5eb7\u5f3a\u58ee\u3002 \u3000\u3000\u6240\u4ee5\u53ea\u8981\u6709\u673a\u4f1a\uff0c\u4ed6\u7684\u6bcd\u4eb2\u603b\u4f1a\u505a\u4e00\u70b9\u53ef\u53e3\u800c\u6709\u8425\u517b\u7684\u5bb6\u5e38\u5c0f\u83dc\u7ed9\u4ed6\u5403\u3002 \u3000\u3000\u2014\u2014\u97ed\u9ec4\u8001\u5c11\u86cb\uff0c\u70c2\u7cca\u767d\u83dc\u8089\u4e1d\uff0c\u516b\u5b9d\u7092\u8fa3\u9171\uff0c\u7ea2\u70e7\u5708\u5b50\uff0c\u54b8\u86cb\u84b8\u8089\u997c\u7b49\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u5f88\u666e\u901a\u7684\u6c5f\u5357\u5bb6\u5e38\u5c0f\u83dc\uff0c\u4e5f\u662f\u5c0f\u65b9\u5c0f\u65f6\u5019\u6700\u6700\u559c\u6b22\u5403\u7684\u3002 \u3000\u3000\u5415\u4e09\u8981\u5415\u606d\u4e3a\u4ed6\u4eec\u51c6\u5907\u7684\u5c31\u662f\u8fd9\u4e9b\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u5415\u4e09\u5f53\u7136\u8fd8\u4e3a\u4ed6\u4eec\u51c6\u5907\u4e86\u9152\u3002 \u3000\u3000\u867d\u7136\u6bcf\u4e2a\u559d\u9152\u7684\u4eba\u90fd\u6709\u67d0\u79cd\u504f\u55dc\uff0c\u53ef\u662f\u771f\u6b63\u7684\u597d\u9152\uff0c\u8fd8\u662f\u6bcf\u4e2a\u4eba\u90fd\u559c\u6b22\u7684\u3002 \u3000\u3000\u5415\u4e09\u4e3a\u4ed6\u4eec\u51c6\u5907\u7684\u662f\u4e00\u79cd\u771f\u6b63\u7684\u597d\u9152\uff0c\u53ea\u8981\u662f\u559d\u9152\u7684\u4eba\uff0c\u90fd\u4e0d\u4f1a\u4e0d\u559c\u6b22\u7684\u597d\u9152\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5148\u559d\u4e86\u4e00\u676f\uff0c\u624d\u95f4\u4e00\u76f4\u7ad9\u5728\u65c1\u8fb9\u4f8d\u5019\u7684\u5415\u606d\uff01 \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u5f88\u5947\u602a\uff1f\u201d \u3000\u3000\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5947\u602a\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6015\u9152\u4e2d\u6709\u6bd2\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e0d\u5947\u602a\u3002\u201d\u5415\u606d\u8bf4\uff1a\u201c\u5982\u679c\u4e09\u7237\u4f1a\u5728\u9152\u4e2d\u4e0b\u6bd2\u6765\u6697\u7b97\u4e94\u82b1\u7bad\u795e\uff0c\u90a3\u4e48\u4ed6\u5c31\u672a\u514d\u592a\u4f4e\u4f30\u4e86\u81ea\u5df1\u3002\u201d \u3000\u3000\u201c\u5b8c\u5168\u6b63\u786e\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u559d\u4e86\u4e00\u676f\uff1a\u201c\u4f60\u786e\u5b9e\u4e0d\u6127\u5df2\u8ddf\u968f\u5415\u4e09\u591a\u5e74\uff0c\u53ea\u4e0d\u8fc7\u4f60\u8fd8\u662f\u60f3\u9519\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u771f\u7684\u8ba4\u4e3a\u5415\u4e09\u53ea\u4e0d\u8fc7\u60f3\u8ba9\u6211\u4eec\u5403\u987f\u4fbf\u996d\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\uff01\u201d\u73ed\u5bdf\u5df2\u90a3\u9053\uff1a\u201c\u4ed6\u8bf7\u6211\u4eec\u5403\u8fd9\u987f\u996d\uff0c\u53ea\u4e0d\u8fc7\u8981\u6211\u4eec\u660e\u767d\uff0c\u4ed6\u5bf9\u6211\u4eec\u6bcf\u4e00\u70b9\u90fd\u5b8c\u5168\u4e86\u89e3\uff0c\u751a\u81f3\u8fde\u6211\u4eec\u559c\u6b22\u5403\u4ec0\u4e48\u4ed6\u90fd\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u535c\u9e70\u662f\u4eba\u6770\uff0c\u5415\u4e09\u53c8\u4f55\u5c1d\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u95ee\u4ed6\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5982\u679c\u4f60\u8981\u95ee\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4f60\u5c31\u95ee\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u81ea\u5df1\u4ece\u6765\u90fd\u6ca1\u6709\u4e86\u89e3\u8fc7\u81ea\u5df1\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e0d\u8ba9\u5c0f\u65b9\u518d\u95ee\uff0c\u53cd\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u81ea\u5df1\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u5f00\u53e3\uff0c\u73ed\u5bdf\u5df4\u90a3\u5df2\u7ecf\u66ff\u4ed6\u56de\u7b54\uff1a\u201c\u4f60\u662f\u4e2a\u602a\u4eba\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u662f\u4e2a\u975e\u5e38\u5947\u602a\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u4e2a\u6c5f\u6e56\u4eba\uff0c\u662f\u4e2a\u6d6a\u5b50\uff0c\u5e38\u5e38\u4f1a\u4e3a\u4e86\u522b\u4eba\u7684\u4e8b\u53bb\u6d41\u8840\u62fc\u547d\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u201c\u4f60\u597d\u9152\u3001\u597d\u8272\u3001\u70ed\u60c5\u3001\u51b2\u52a8\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ef\u662f\u521a\u624d\u6211\u4e09\u6b21\u8981\u8ddf\u4f60\u6253\u8d4c\uff0c\u4f60\u90fd\u6ca1\u6709\u8d4c\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u559c\u6b22\u8d4c\u3002\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u4f60\u4e0d\u559c\u6b22\u8d4c\uff0c\u6240\u4ee5\u6211\u624d\u5947\u602a\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u4eba\uff0c\u6ca1\u6709\u4e00\u4e2a\u4e0d\u559c\u6b22\u8d4c\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u559c\u6b22\u8d4c\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u4e0d\u8fc7\u6211\u53ea\u548c\u4e00\u79cd\u4eba\u8d4c\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5bf9\uff01\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u6211\u53ea\u548c\u670b\u53cb\u559d\u9152\u3002\u201d \u3000\u3000\u201c\u4f60\u53ea\u548c\u54ea\u79cd\u4eba\u8d4c\uff1f\u201d \u3000\u3000\u201c\u4ec7\u4eba\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u901a\u5e38\u90fd\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8d4c\u547d\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7b11\u4e86\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u5374\u8fd8\u662f\u4e0d\u660e\u767d\u4f60\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5c0f\u65b9\u95ee\u4ed6\uff1a\u201c\u96be\u9053\u6211\u8fd8\u6709\u4ec0\u4e48\u5947\u602a\u7684\u5730\u65b9\uff1f\u201d\xb7 \u3000\u3000\u201c\u5f53\u7136\u6709\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u6709\u5f88\u591a\u7537\u4eba\u90fd\u4f1a\u628a\u5973\u4eba\u770b\u5f97\u6bd4\u670b\u53cb\u91cd\uff0c\u53ef\u662f\u4f60\u4e0d\u540c\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u5bf9\u4f60\u7684\u670b\u53cb\u5b9e\u5728\u4e0d\u9519\uff0c\u53ef\u662f\u4f60\u5bf9\u4f60\u7684\u5973\u4eba\u5c31\u5b9e\u5728\u592a\u9519\u4e86\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4e0d\u7ba1\u662f\u4f60\u559c\u6b22\u7684\u5973\u4eba\uff0c\u8fd8\u662f\u559c\u6b22\u4f60\u7684\u5973\u4eba\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u8b6c\u5982\u8bf4\u2018\u9633\u5149\u2019\u3002\u5979\u5e94\u8be5\u53ef\u4ee5\u7b97\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u201c\u53ef\u662f\u8fd9\u4e24\u5929\u4f60\u4e00\u76f4\u907f\u514d\u548c\u5979\u76f8\u89c1\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u5c31\u56e0\u4e3a\u5979\u662f\u4e2a\u5973\u4eba\uff0c\u800c\u4e14\u4f60\u591a\u591a\u5c11\u5c11\u6709\u4e00\u70b9\u559c\u6b22\u5979\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u5426\u8ba4\u3002 \u3000\u3000\u201c\u8fd8\u6709\u82cf\u82cf\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4e0d\u7ba1\u5979\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5973\u4eba\uff0c\u5979\u603b\u7b97\u4e3a\u4f60\u751f\u4e86\u4e2a\u5b69\u5b50\uff0c\u4e0d\u7ba1\u5979\u662f\u4e3a\u4ec0\u4e48\u6765\u7684\uff0c\u73b0\u5728\u5979\u603b\u7b97\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u53ef\u662f\u4f60\u5bf9\u5979\u600e\u4e48\u6837\uff1f\u4f60\u770b\u89c1\u5979\u7b80\u76f4\u5c31\u597d\u50cf\u770b\u89c1\u6d3b\u9b3c\u4e00\u6837\uff0c\u53ea\u8981\u4f60\u4e00\u770b\u89c1\u5979\u8d70\u8fc7\u6765\uff0c\u4f60\u5c31\u843d\u8352\u800c\u9003\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u95ed\u7740\u5634\uff0c\u56e0\u4e3a\u4ed6\u4e00\u76f4\u5728\u559d\u9152\uff0c\u95ed\u7740\u5634\u5c31\u4e0d\u80fd\u559d\u9152\u4e86\u3002 \u3000\u3000\u201c\u8fd8\u6709\u9f50\u5c0f\u71d5\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u53c8\u8bf4\uff0c\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u770b\u5f97\u51fa\u5979\u5bf9\u4f60\u4e0d\u9519\uff0c\u53ef\u662f\u4f60\u5bf9\u5979\u5462\uff1f\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5979\u8d70\u4e86\u4e4b\u540e\uff0c\u4f60\u8fde\u95ee\u90fd\u6ca1\u6709\u95ee\u8fc7\uff0c\u4f60\u6839\u672c\u5c31\u4e0d\u5173\u5fc3\u5979\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6839\u672c\u5c31\u4e0d\u5173\u5fc3\u5979\u7684\u6b7b\u6d3b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5ffd\u7136\u653e\u4e0b\u9152\u676f\uff0c\u76ef\u7740\u73ed\u5bdf\u5df4\u90a3\uff1a\u4e00\u5c31\u7b97\u6211\u5173\u5fc3\u5979\u4eec\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u4ed6\u95ee\uff1a\u201c\u6211\u80fd\u5bf9\u5979\u4eec\u8bf4\u4ec0\u4e48\uff1f\u6211\u80fd\u4e3a\u5979\u4eec\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u6700\u5c11\u5e94\u8be5\u8868\u793a\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u8868\u793a\u4f60\u5bf9\u4ed6\u4eec\u7684\u5173\u5fc3\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u600e\u4e48\u8868\u793a\uff1f\u201d\u5c0f\u65b9\u53c8\u996e\u4e00\u6ee1\u676f\uff0c\u201c\u4f60\u8981\u6211\u8dea\u4e0b\u6765\uff0c\u8dea\u5728\u5979\u4eec\u9762\u524d\uff0c\u6c42\u5979\u4eec\u539f\u8c05\u6211\uff0c\u8fd8\u662f\u8981\u6211\u7528\u8111\u888b\u53bb\u649e\u5899\uff0c\u649e\u5f97\u5934\u7834\u8840\u6d41\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5c0f\u65b9\u4eff\u4f5b\u5df2\u6709\u4e86\u9152\u610f\uff1a\u201c\u5c31\u7b97\u6211\u8fd9\u4e48\u505a\u4e86\uff0c\u53c8\u80fd\u8868\u793a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u95ee\u73ed\u5bdf\u5df4\u90a3\uff1a \u3000\u3000\u201c\u662f\u4e0d\u662f\u6211\u4e00\u5b9a\u8981\u8fd9\u4e48\u6837\u505a\uff0c\u624d\u80fd\u8868\u793a\u51fa\u6211\u5bf9\u5979\u4eec\u7684\u611f\u60c5\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u65e0\u6cd5\u56de\u7b54\uff0c\u5c0f\u65b9\u53c8\u95ee\u4ed6\uff1a \u3000\u3000\u201c\u5982\u679c\u4f60\u662f\u6211\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u8fd9\u4e48\u6837\u505a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\uff01\u201d\u73ed\u5bdf\u5df4\u90a3\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u4f60\u4f1a\u600e\u4e48\u505a\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4f1a\u8ddf\u4f60\u4e00\u6837\uff0c\u4ec0\u4e48\u90fd\u4e0d\u505a\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u4e5f\u996e\u6ee1\u4e00\u676f\uff1a\u201c\u5230\u4e86\u5fc5\u8981\u65f6\uff0c\u4e5f\u8bb8\u6211\u4eec\u4f1a\u4e3a\u5979\u4eec\u53bb\u6b7b\uff0c\u53ef\u662f\u8fd9\u79cd\u65f6\u5019\uff0c\u6211\u4eec\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u505a\u3002\u201d \u3000\u3000\u4ed6\u7684\u8868\u60c5\u4e5f\u5f88\u6c89\u91cd\uff1a\u201c\u4e00\u4e2a\u7537\u4eba\uff0c\u4e00\u4e2a\u771f\u6b63\u7684\u7537\u5b50\u6c49\uff0c\u6709\u65f6\u65e0\u8bba\u4ec0\u4e48\u4e8b\u90fd\u8981\u53bb\u505a\uff0c\u6709\u65f6\u65e0\u8bba\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u80fd\u505a\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u5c0f\u65b9\u8bf4\uff1a\u201c\u5c31\u662f\u8fd9\u6837\u5b50\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u957f\u957f\u53f9\u606f\uff0c\u4e3e\u676f\u996e\u5c3d\uff1a\u201c\u4e5f\u8bb8\u8fd9\u5c31\u662f\u6211\u4eec\u8fd9\u79cd\u4eba\u7684\u60b2\u54c0\u3002\u201d \u3000\u3000\u4e00\u76f4\u7ad9\u5728\u4ed6\u4eec\u65c1\u8fb9\u4f8d\u5019\u7740\u4ed6\u4eec\u7684\u5415\u606d\u5ffd\u7136\u4e5f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u5176\u5b9e\u6bcf\u79cd\u4eba\u90fd\u6709\u4ed6\u4eec\u81ea\u5df1\u7684\u60b2\u54c0\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u50cf\u5c0f\u4eba\u8fd9\u79cd\u4eba\uff0c\u867d\u7136\u5728\u6df7\u5403\u7b49\u6b7b\uff0c\u8fc7\u4e00\u5929\u7b97\u4e00\u5929\uff0c\u53ef\u662f\u4e5f\u4e00\u6837\u6709\u60b2\u54c0\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e0d\u59a8\u4e5f\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e0d\u80fd\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u50cf\u5c0f\u4eba\u8fd9\u79cd\u4eba\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u90fd\u662f\u8eab\u4e0d\u7531\u4e3b\u7684\uff0c\u5c31\u7b97\u5fc3\u91cc\u6709\u4ec0\u4e48\u96be\u53d7\u7684\u4e8b\uff0c\u4e5f\u53ea\u6709\u95f7\u5728\u5fc3\u91cc\uff0c\u4e0d\u80fd\u8bf4\u51fa\u6765\u3002\u201d\u5415\u606d\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd9\u5c31\u662f\u6211\u4eec\u8fd9\u79cd\u4eba\u6700\u5927\u7684\u60b2\u54c0\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u4eff\u4f5b\u5ffd\u7136\u4e0b\u4e86\u51b3\u5fc3\uff01 \u3000\u3000\u201c\u4f46\u662f\u65e0\u8bba\u54ea\u79cd\u4eba\u5076\u5c14\u90fd\u4f1a\u505a\u51fa\u4e00\u4e24\u4ef6\u8fde\u4ed6\u81ea\u5df1\u90fd\u89c9\u5f97\u83ab\u540d\u5176\u5999\u7684\u4e8b\uff0c\u8bf4\u51fa\u4e00\u4e9b\u8fde\u4ed6\u81ea\u5df1\u90fd\u89c9\u5f97\u83ab\u540d\u5176\u5999\u7684\u8bdd\u6765\uff0c\u5c31\u7b97\u4ed6\u660e\u660e\u77e5\u9053\u8bf4\u51fa\u6765\u4e4b\u540e\u4e00\u5b9a\u4f1a\u540e\u6094\u7684\uff0c\u4ed6\u4e5f\u975e\u8bf4\u51fa\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u8bf4\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u521a\u624d\u662f\u4e0d\u662f\u63d0\u8d77\u4e00\u4f4d\u9f50\u59d1\u5a18\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u8bf4\u7684\u90a3\u4f4d\u9f50\u5c0f\u71d5\u9f50\u59d1\u5a18\uff0c\u4ee5\u524d\u662f\u4e0d\u662f\u5f88\u559c\u6b22\u6253\u626e\u6210\u7537\u5b69\u7684\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4e24\u4f4d\u8bf4\u7684\u662f\u5979\uff0c\u90a3\u4e48\u4e24\u4f4d\u73b0\u5728\u5df2\u7ecf\u53ef\u4ee5\u4e0d\u5fc5\u518d\u4e3a\u5979\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u53c8\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u5979\u73b0\u5728\u6d3b\u5f97\u5f88\u597d\u3002\u201d\u5415\u606d\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u52c9\u5f3a\uff01\u201c\u4e5f\u8bb8\u8fdc\u6bd4\u4e24\u4f4d\u60f3\u50cf\u4e2d\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u5c0f\u65b9\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u95ee\uff1a\u201c\u4f60\u77e5\u9053\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u4e0d\u80fd\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u5415\u606d\u53c8\u6c89\u541f\u4e86\u5f88\u4e45\uff0c\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5c0f\u4eba\u672c\u6765\u4e0d\u60f3\u8bf4\u7684\uff0c\u53ef\u662f\u73b0\u5728\u597d\u50cf\u5df2\u7ecf\u975e\u8bf4\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u90a3\u4f4d\u9f50\u59d1\u5a18\u73b0\u5728\u5df2\u7ecf\u88ab\u4e09\u7237\u6536\u505a\u4e49\u59b9\u4e86\uff0c\u800c\u4e14\u4e09\u7237\u5df2\u7ecf\u505a\u4e3b\u4e3a\u5979\u8ba2\u4e86\u4eb2\u3002\u201d \u3000\u3000\u5c0f\u65b9\u7684\u8138\u8272\u6ca1\u6709\u53d8\uff01\u597d\u50cf\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u53ea\u4e0d\u8fc7\u559d\u4e86\u4e09\u676f\u9152\uff0c\u559d\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u201c\u8ba2\u4eb2\uff1f\u201d\u559d\u4e0b\u4e09\u676f\u9152\u4e4b\u540e\uff0c\u5c0f\u65b9\u624d\u95ee\uff1a\u201c\u5979\u8ddf\u8c01\u8ba2\u4e86\u4eb2\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e5f\u4e0d\u6e05\u695a\u3002\u201d\u5415\u606d\u8bf4\uff1a\u201c\u5c0f\u4eba\u53ea\u77e5\u9053\u9093\u4f4d\u672a\u6765\u7684\u65b0\u59d1\u7237\u662f\u4f4d\u5251\u5ba2\uff0c\u5251\u6cd5\u4e4b\u9ad8\uff0c\u636e\u8bf4\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5c0f\u65b9\u624b\u91cc\u7684\u9152\u676f\u788e\u4e86\u3002 \u3000\u3000\u201c\u72ec\u5b64\u75f4\uff1f\u201d\u4ed6\u95ee\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u4e0d\u662f\u72ec\u5b64\u75f4\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6ca1\u6709\u518d\u95ee\u4e0b\u53bb\uff0c\u4e5f\u6ca1\u6709\u518d\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u7684\u5634\u597d\u50cf\u5ffd\u7136\u88ab\u4e00\u53ea\u770b\u4e0d\u89c1\u7684\u624b\uff0c\u7528\u4e00\u6839\u770b\u4e0d\u89c1\u7684\u9488\u7f1d\u4e86\u8d77\u6765\uff0c\u8fde\u9152\u90fd\u4e0d\u518d\u559d\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5374\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u72ec\u5b64\u75f4\u73b0\u5728\u4e5f\u8ddf\u5415\u4e09\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u672c\u6765\u5c31\u662f\u597d\u670b\u53cb\u3002\u201d\u5415\u606d\u8bf4\uff1a\u201c\u4e09\u7237\u5bf9\u4ed6\u4e00\u5411\u90fd\u656c\u91cd\u5f97\u5f88\u3002\u201d\uff0c\u4ed6\u60f3\u4e86\u60f3\uff0c\u53c8\u8bf4\uff1a\u201c\u8fd9\u4f4d\u72ec\u5b64\u5148\u751f\u4e00\u5411\u662f\u4e2a\u602a\u4eba\uff0c\u8fd9\u6b21\u56de\u6765\u4e4b\u540e\uff0c\u597d\u50cf\u53d8\u5f97\u66f4\u602a\u4e86\uff0c\u4e00\u5929\u5230\u665a\u603b\u662f\u75f4\u75f4\u5446\u5446\u5730\u5750\u5728\u90a3\u91cc\uff0c\u8fde\u4e00\u53e5\u6d3b\u90fd\u4e0d\u8bf4\uff0c\u76f4\u5230\u89c1\u7740\u9f50\u59d1\u5a18\u4e4b\u540e\uff0c\u4ed6\u624d\u597d\u4e86\u4e9b\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u51b7\u7b11\uff0c\u8f6c\u773c\u95f4\u5c0f\u65b9\uff1a\u201c\u73b0\u5728\u6211\u624d\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u56db\u5341\u7ae0\u3000\u6728\u5c4b\u91cc\u7684\u79d8\u5bc6 \u7b2c\u56db\u5341\u7ae0\u3000\u6728\u5c4b\u91cc\u7684\u79d8\u5bc6 \u3000\u3000\u201c\u5415\u4e09\u8981\u80e1\u5927\u9cde\u4ed6\u4eec\u4e09\u4e2a\u4eba\u6765\u8bd5\u4f60\u7684\u5251\uff0c\u5c31\u56e0\u4e3a\u6709\u72ec\u5b64\u75f4\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u8bf4\u4e16\u4e0a\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u4ece\u4ed6\u4eec\u81f4\u547d\u7684\u4f24\u53e3\u4e0a\u770b\u51fa\u4f60\u7684\u5251\u6cd5\u6765\uff0c\u8fd9\u4e2a\u4eba\u65e0\u7591\u5c31\u662f\u72ec\u5b64\u75f4\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u53c8\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u4e0d\u80fd\u53bb\u4e86\uff0c\u7edd\u5bf9\u4e0d\u80fd\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c0f\u65b9\u832b\u7136\u95ee\uff1a\u201c\u4e0d\u80fd\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u5df2\u7ecf\u51b3\u5b9a\uff0c\u53ea\u8981\u6709\u5415\u4e09\u7684\u4e0b\u843d\uff0c\u5c31\u53eb\u4f60\u7387\u9886\u6211\u7684\u5c5e\u4e0b\u53d1\u52a8\u653b\u51fb\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f46\u662f\u73b0\u5728\u4f60\u5df2\u7ecf\u4e0d\u80fd\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u65b9\u95ee\u3002 \u3000\u3000\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6709\u9f50\u5c0f\u71d5\u548c\u72ec\u5b64\u75f4\u5728\u90a3\u91cc\uff0c\u4f60\u53bb\u5c82\u975e\u662f\u9001\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\uff0c\u5ffd\u7136\u95ee\u73ed\u5bdf\u5df4\u90a3\uff1a\u201c\u50cf\u6211\u4eec\u8fd9\u79cd\u4eba\uff0c\u6b7b\u4e86\u4e4b\u540e\u4f1a\u4e0d\u4f1a\u4e0b\u5730\u72f1\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e0d\u80fd\u56de\u7b54\uff0c\u4e5f\u4e0d\u613f\u56de\u7b54\uff0c\u4f46\u662f\u4ed6\u8bf4\uff1a\u201c\u6211\u53ea\u77e5\u9053\u6211\u4eec\u6709\u5f88\u591a\u7684\u670b\u53cb\u4e00\u5b9a\u5728\u5730\u72f1\u91cc\uff0c\u6240\u4ee5\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u6211\u60c5\u613f\u4e0b\u5730\u72f1\u53bb\u3002\u201d \u3000\u3000\u5c0f\u65b9\u5927\u7b11\uff01 \u3000\u3000\u201c\u6211\u4e5f\u4e00\u6837\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u65e2\u7136\u6211\u4eec\u5df2\u7ecf\u51c6\u5907\u4e0b\u5730\u72f1\uff0c\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u4e0d\u80fd\u53bb\uff1f\u201d \u3000\u3000\u5f88\u591a\u4eba\u90fd\u559c\u6b22\u7b11\u3002 \u3000\u3000\u6709\u5f88\u591a\u88ab\u4eba\u559c\u7231\u3001\u53d7\u4eba\u6b22\u8fce\u7684\u4eba\u90fd\u559c\u6b22\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u7b11\u5c31\u50cf\u662f\u6700\u73cd\u8d35\u7684\u80ed\u8102\u82b1\u7c89\u9999\u6599\uff0c\u4e0d\u4f46\u80fd\u4f7f\u81ea\u5df1\u82ac\u82b3\u7f8e\u4e3d\uff0c\u4e5f\u80fd\u4f7f\u522b\u4eba\u6109\u5feb\u3002 \u3000\u3000\u53ef\u662f\u7b11\u4e5f\u6709\u5f88\u591a\u79cd\u3002 \u3000\u3000\u6709\u7684\u4eba\u4ee5\u72c2\u6b4c\u5f53\u54ed\uff0c\u6709\u7684\u4eba\u4ee5\u72c2\u7b11\u5f53\u6b4c\uff0c\u6709\u4e9b\u4eba\u7684\u7b11\u751a\u81f3\u6bd4\u75db\u54ed\u66f4\u60b2\u4f24\uff0c\u6709\u4e9b\u4eba\u7684\u7b11\u4e5f\u8bb8\u6bd4\u6012\u543c\u66f4\u6124\u6012\u3002 \u3000\u3000\u7b49\u5230\u5c0f\u65b9\u7b11\u5b8c\u4e86\uff0c\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u95ee\u5415\u606d\uff1a\u201c\u4f60\u5e73\u5e38\u662f\u4e0d\u662f\u5e38\u5e38\u7b11\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u5e38\u7b11\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5e38\u5e38\u90fd\u7b11\u4e0d\u51fa\uff0c\u201d\u5415\u606d\u8bf4\uff1a\u201c\u5c31\u662f\u6709\u65f6\u6211\u60f3\u7b11\uff0c\u4e5f\u4e0d\u80fd\u7b11\uff0c\u4e0d\u6562\u7b11\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u770b\u7740\u4ed6\uff0c\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u8bf4\u51fa\u53e5\u5f88\u5947\u602a\u7684\u8bdd\uff1a\u201c\u90a3\u4e48\u6211\u5e0c\u671b\u4f60\u73b0\u5728\u8d76\u5feb\u591a\u7b11\u7b11\uff0c\u201d\u4ed6\u8bf4\uff1a\u201c\u5c31\u7b97\u4f60\u4e0d\u60f3\u7b11\uff0c\u4e5f\u5e94\u8be5\u7b11\u4e00\u7b11\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u73b0\u5728\u5982\u679c\u4e0d\u7b11\uff0c\u4ee5\u540e\u5c31\u771f\u60f3\u7b11\uff0c\u6050\u6015\u4e5f\u7b11\u4e0d\u51fa\u4e86\u3002\u201d \u3000\u3000\u5415\u606d\u786e\u5b9e\u60f3\u7b11\u4e00\u7b11\uff0c\u4f46\u662f\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u5df2\u5ffd\u7136\u50f5\u786c\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4ed6\u53c8\u95ee\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53cd\u95ee\u4ed6\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u6b7b\u4eba\u7b11\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u6ca1\u6709\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u7684\u58f0\u97f3\u51b0\u51b7\uff1a\u201c\u56e0\u4e3a\u53ea\u6709\u6b7b\u4eba\u624d\u662f\u771f\u6b63\u7b11\u4e0d\u51fa\u7684\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u73b0\u5728\u6211\u597d\u50cf\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u73b0\u5728\u4f60\u5f53\u7136\u8fd8\u6ca1\u6709\u6b7b\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6709\u6ca1\u6709\u60f3\u8fc7\uff0c\u6211\u8fd8\u4f1a\u8ba9\u4f60\u6d3b\u591a\u4e45\u3002\u201d \u3000\u3000\u5415\u606d\u7684\u8138\u8272\u6ca1\u6709\u53d8\u3001\u56e0\u4e3a\u4ed6\u7684\u8138\u8272\u5df2\u7ecf\u6ca1\u6cd5\u5b50\u53d8\u5f97\u66f4\u96be\u770b\u4e86\u3002 \u3000\u3000\u53d8\u8272\u7684\u662f\u5c0f\u65b9\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u95f4\u73ed\u5bdf\u5df4\u90a3\uff1a\u201c\u4f60\u8981\u4ed6\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u6b7b\u7684\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u8fdf\u4e00\u70b9\u53c8\u6709\u4f55\u76ca\uff1f\u65e9\u4e00\u70b9\u6b7b\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u60f3\u4e0d\u901a\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6709\u4e9b\u4e8b\u6211\u4e5f\u60f3\u4e0d\u901a\u3002\xb7 \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u201c\u6709\u5f88\u591a\u4e8b\u6211\u90fd\u60f3\u4e0d\u901a\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u6700\u4e3b\u8981\u7684\u4e00\u70b9\u662f\uff0c\u6211\u60f3\u4e0d\u901a\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u6d3e\u4ed6\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u6765\u628a\u6211\u4eec\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u662f\u4ed6\u628a\u6211\u4eec\u7559\u4e0b\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ea\u6709\u4ed6\u8fd9\u79cd\u4eba\u624d\u80fd\u628a\u6211\u4eec\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u89c4\u77e9\u6709\u793c\uff0c\u800c\u4e14\u5076\u5c14\u4f1a\u8bf4\u4e9b\u771f\u5fc3\u8bdd\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u53ea\u6709\u771f\u8bda\u7684\u4eba\uff0c\u624d\u80fd\u628a\u6211\u4eec\u7559\u4f4f\u3002\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u4f46\u662f\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u4eec\u7559\u4f4f\u5728\u8fd9\u91cc\u5462\uff1f\u662f\u56e0\u4e3a\u4ed6\u6df1\u6015\u6211\u4eec\u518d\u8ffd\u8e2a\u4e0b\u53bb\uff1f\u8fd8\u662f\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u5728\u8fd9\u91cc\u5e03\u4e0b\u4e86\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u6cb3\u6ee8\u7684\u786e\u6709\u5f88\u591a\u4eba\uff0c\u6709\u7684\u5728\u751f\u706b\uff0c\u6709\u7684\u5728\u70e7\u6c34\uff0c\u6709\u7684\u5728\u6253\u6742\uff0c\u7092\u83dc\u7684\u4eba\u66f4\u591a\uff0c\u56e0\u4e3a\u6bcf\u4e00\u6837\u5bb6\u5e38\u83dc\u90fd\u662f\u7531\u4e00\u4e2a\u7279\u522b\u4f1a\u7092\u8fd9\u6837\u83dc\u7684\u4eba\u7092\u51fa\u6765\u7684\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u73af\u987e\u5de6\u53f3\uff1a\u201c\u6740\u4eba\u5982\u9ebb\u7684\u6b66\u6797\u9ad8\u624b\u5e76\u4e0d\u4e00\u5b9a\u4f1a\u751f\u706b\u6253\u6742\u70e7\u6c34\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u4f1a\u7092\u70c2\u7cca\u7684\u83dc\u8089\u4e1d\uff0c\u53ef\u662f\u4f1a\u751f\u706b\u6253\u6742\u70e7\u6c34\u7092\u8089\u4e1d\u7684\u4eba\uff0c\u4e5f\u672a\u5fc5\u5c31\u4e0d\u662f\u6740\u4eba\u5982\u9ebb\u7684\u6b66\u6797\u9ad8\u624b\u3002\u201d\u4ed6\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u4e0d\u80fd\u8bf4\u4e0d\u5bf9\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u770b\u770b\u4e00\u4e2a\u6b63\u5728\u7528\u706b\u94b3\u5939\u70ad\u7684\u9752\u8863\u79c3\u9876\u4e2d\u5e74\u58ee\u6c49\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u4e5f\u8bb8\u5c31\u662f\u4f4d\u6b66\u6797\u9ad8\u624b\u3002\u4ed6\u624b\u91cc\u7684\u706b\u94b3\u5b50\u8bf4\u4e0d\u5b9a\u5c31\u662f\u79cd\u6781\u5389\u5bb3\u9738\u9053\u7684\u5916\u95e8\u5175\u5668\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u66ff\u6211\u7528\u8471\u6ce5\u70e4\u8089\u7684\u90a3\u4e2a\u4eba\uff0c\u5e73\u65f6\u7ecf\u5e38\u70e4\u7684\u8bf4\u4e0d\u5b9a\u662f\u4eba\u8089\u3002\u201d \u3000\u3000\u5c0f\u65b9\u4e5f\u4e0d\u80fd\u8bf4\u4e0d\u53ef\u80fd\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u8bf4\u4e0d\u5b9a\u968f\u65f6\u90fd\u53ef\u80fd\u5bf9\u6211\u4eec\u53d1\u52a8\u653b\u51fb\uff0c\u8bf4\u4e0d\u5b9a\u968f\u65f6\u90fd\u80fd\u5c06\u6211\u4eec\u5207\u6210\u8089\u4e1d\uff0c\u70e4\u6210\u70e4\u8089\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u53c8\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u600e\u4e48\u80fd\u8bf4\u4e0d\u5bf9\uff1f \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u53ef\u662f\u4ed6\u4eec\u4e5f\u672a\u5fc5\u4e00\u5b9a\u4f1a\u8fd9\u4e48\u505a\u7684\uff0c\u8fd9\u5730\u65b9\u4e5f\u8bb8\u6839\u672c\u4e0d\u662f\u4e2a\u9677\u997c\uff0c\u90a3\u4e09\u53e3\u68fa\u6750\u4e5f\u8bb8\u65e9\u5df2\u8fdc\u53bb\uff0c\u6839\u672c\u4e0d\u6015\u6211\u4eec\u53bb\u8ffd\uff0c\u6240\u4ee5\u6211\u624d\u66f4\u5947\u602a\u3002\u201d \u3000\u3000\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5947\u602a\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u6d3e\u8fd9\u4e48\u6837\u4e00\u4f4d\u89c4\u89c4\u77e9\u77e9\u606d\u606d\u656c\u656c\u800c\u4e14\u8fd8\u4f1a\u8bf4\u771f\u8bdd\u7684\u4eba\u6765\u628a\u6211\u4eec\u7559\u5728\u8fd9\u91cc\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u90fd\u60f3\u95ee\u95ee\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u4ed6\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u5c31\u7b97\u4ed6\u77e5\u9053\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u8bf4\u3002\u201d \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u76f8\u4fe1\uff0c\u5415\u4e09\u7684\u5c5e\u4e0b\uff0c\u7edd\u5bf9\u90fd\u662f\u5b88\u53e3\u5982\u74f6\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u65b9\u4e5f\u76f8\u4fe1\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u53ea\u6709\u6740\u4e86\u4ed6\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4e0d\u7ba1\u4ed6\u77e5\u9053\u4e5f\u597d\uff0c\u4e0d\u77e5\u9053\u4e5f\u597d\uff0c\u53cd\u6b63\u4ed6\u4e0d\u4f1a\u8bf4\uff0c\u6211\u5c31\u4e0d\u80fd\u4e0d\u6740\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\u76ef\u7740\u5415\u606d\uff1a\u201c\u5415\u4e09\u8981\u4f60\u6765\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u3002\u201d \u3000\u3000\u5415\u606d\u5c45\u7136\u627f\u8ba4\uff1a\u201c\u4e09\u7237\u786e\u5b9e\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u90a3\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6d3e\u4f60\u6765\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u4e5f\u6709\u70b9\u60ca\u5947\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u80af\u6765\u53f6 \u3000\u3000\u201c\u4e09\u7237\u8981\u6211\u6765\uff0c\u6211\u5c31\u6765\u3002\u201d\u5415\u606d\u8bf4\uff1a\u201c\u4e09\u7237\u8981\u6211\u53bb\u6b7b\uff0c\u6211\u5c31\u53bb\u6b7b\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u4e3e\u676f\uff1a\u201c\u6211\u4f69\u670d\u4ed6\u3002\u201d\u4ed6\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff1a\u201c\u65e0\u8bba\u8c01\u80fd\u591f\u8ba9\u522b\u4eba\u4e3a\u4ed6\u53bb\u6b7b\uff0c\u6211\u90fd\u4f69\u670d\u3002\u201d \u3000\u3000\u5415\u606d\u5374\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u4ed6\u5e73\u65f6\u672c\u6765\u5e38\u5e38\u7b11\u4e0d\u51fa\u6765\u7684\uff0c\u8fd9\u79cd\u65f6\u5019\u4ed6\u53cd\u800c\u80fd\u7b11\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u53ef\u662f\u4e09\u7237\u7b97\u51c6\u6211\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u597d\u50cf\u66f4\u5947\u602a\u4e86\uff1a\u201c\u4ed6\u771f\u7684\u80fd\u7b97\u51c6\u4f60\u4e0d\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\uff01\u201d \u3000\u3000\u201c\u4ed6\u51ed\u4ec0\u4e48\u5982\u6b64\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4e09\u7237\u7b97\u51c6\uff0c\u50cf\u4e24\u4f4d\u8fd9\u6837\u7684\u5927\u82f1\u96c4\u5927\u8c6a\u6770\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u6740\u6211\u8fd9\u6837\u4e00\u4e2a\u5c0f\u4eba\u3002\u201d\u5415\u606d\u8bf4\uff1a\u201c\u800c\u4e14\u4e24\u4f4d\u5c31\u7b97\u6740\u4e86\u6211\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u201c\u4f60\u6d3b\u7740\u5bf9\u6211\u4eec\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u6ca1\u6709\u7528\u3002\u201d\u5415\u606d\u8bf4\uff1a\u201c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u5415\u606d\u5ffd\u7136\u95ed\u4e0a\u4e86\u5634\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u80af\u8bf4\u4e86\u3002 \u3000\u3000\u2014\u2014\u4ed6\u6d3b\u7740\u4e5f\u8bb8\u5df2\u7ecf\u6ca1\u6709\u7528\u4e86\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u70b9\u3002 \u3000\u3000\u2014\u2014\u73b0\u5728\u4ed6\u867d\u7136\u4e0d\u8bf4\u51fa\u6765\uff0c\u4ee5\u540e\u4e5f\u8bb8\u4f1a\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u73b0\u5728\u4ed6\u5982\u679c\u6b7b\u4e86\uff0c\u4ee5\u540e\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u8bf4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u4e3e\u676f\uff1a\u201c\u6211\u4e5f\u4f69\u670d\u4f60\uff0c\u56e0\u4e3a\u4f60\u5b9e\u5728\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u6211\u4e00\u5411\u5f88\u4f69\u670d\u806a\u660e\u4eba\uff0c\u4ece\u6765\u90fd\u4e0d\u613f\u6740\u806a\u660e\u4eba\u3002\u201d\u4ed6\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u5076\u5c14\u4e5f\u6740\u8fc7\u51e0\u4e2a\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u731c\u6211\u4f1a\u4e0d\u4f1a\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5c31\u5728\u73ed\u5bdf\u5df4\u90a3\u95ee\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u51e0\u4e4e\u5c31\u662f\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u4e5f\u6709\u4e00\u4e2a\u4eba\u7528\u8fd9\u4e2a\u540c\u6837\u7684\u95ee\u9898\u95ee\u53e6\u5916\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u95ee\u8fd9\u4e2a\u95ee\u9898\u7684\u4eba\uff0c\u8fd9\u65f6\u5019\u6b63\u7ad9\u5728\u6cb3\u6d41\u5bf9\u5cb8\u5c71\u5761\u4e0a\uff0c\u5ca9\u77f3\u95f4\uff0c\u6811\u4e1b\u91cc\uff0c\u4e00\u95f4\u5f88\u9690\u79d8\u7684\u5c0f\u5c4b\u91cc\uff0c\u4e00\u6247\u5f88\u9690\u79d8\u7684\u5c0f\u7a97\u524d\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8ddd\u79bb\u73ed\u5bdf\u5df4\u90a3\u5f88\u8fdc\u5f88\u8fdc\u3002 \u3000\u3000\u73ed\u5bdf\u5df2\u90a3\u770b\u4e0d\u89c1\u4ed6\uff0c\u53ef\u662f\u73ed\u5bdf\u5df4\u90a3\u7684\u4e00\u4e3e\u4e00\u52a8\u4ed6\u90fd\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u751a\u81f3\u8fde\u73ed\u5bdf\u5df4\u90a3\u8bf4\u7684\u8bdd\u4ed6\u90fd\u597d\u50cf\u80fd\u542c\u5f97\u89c1\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5c31\u662f\u5415\u4e09\u3002 \u3000\u3000\u6cb3\u6d41\u5bf9\u5cb8\u7684\u5c71\u5761\u4e0a\uff0c\u5ca9\u77f3\u95f4\uff0c\u6811\u4e1b\u91cc\uff0c\u6709\u4e00\u680b\u9690\u79d8\u7684\u5c0f\u5c4b\u3002 \u3000\u3000\u4e00\u680b\u522b\u4eba\u5f88\u96be\u53d1\u73b0\u7684\u5c0f\u6728\u5c4b\u3002 \u3000\u3000\u5c31\u7b97\u6709\u4eba\u53d1\u73b0\u4e86\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u6ce8\u610f\u7684\uff0c\u56e0\u4e3a\u4ece\u5916\u8868\u4e0a\u770b\u6765\uff0c\u8fd9\u680b\u5c0f\u6728\u5c4b\u7edd\u6ca1\u6709\u4e00\u70b9\u80fd\u591f\u8ba9\u4eba\u6ce8\u610f\u7684\u5730\u65b9\u3002 \u3000\u3000\u5c31\u7b97\u6709\u8ff7\u8def\u7684\u65c5\u5ba2\u730e\u4eba\uff0c\u5728\u65e0\u610f\u95f4\u95ef\u4e86\u8fdb\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u53d1\u73b0\u8fd9\u95f4\u5c0f\u6728\u5c4b\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u66f4\u4e0d\u4f1a\u60f3\u5230\u201c\u5bcc\u8d35\u795e\u4ed9\u201d\u5415\u4e09\u4f1a\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u4f46\u662f\u5415\u4e09\u5c31\u5728\u8fd9\u6728\u5c4b\u91cc\u3002 \u3000\u3000\u4e0d\u4f46\u5415\u4e09\u5728\uff0c\u9f50\u5c0f\u71d5\u4e5f\u5728\u3002 \u3000\u3000\u6728\u5c4b\u662f\u7528\u575a\u5b9e\u800c\u5e72\u71e5\u7684\u677e\u6728\u677f\u642d\u6210\u7684\uff0c\u6ca1\u6709\u6f06\uff0c\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7a97\u6237\u3002 \u3000\u3000\u6728\u5c4b\u91cc\u6709\u4e00\u5f20\u6728\u677f\u5e8a\uff0c\u4e00\u5f20\u6728\u677f\u684c\uff0c\u4e09\u5f20\u6728\u677f\u51f3\uff0c\u4e00\u4e2a\u6728\u677f\u67dc\uff0c\u540e\u9762\u8fd8\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u53a8\u623f\u3002 \u3000\u3000\u5982\u679c\u4f60\u5e38\u5e38\u5728\u5c71\u91ce\u4e1b\u6797\u95f4\u8d70\u52a8\uff0c\u4f60\u4e00\u5b9a\u5e38\u5e38\u4f1a\u770b\u5230\u4e00\u4e9b\u8fd9\u6837\u7684\u6728\u5c4b\u3002 \u3000\u3000\u4e00\u4e9b\u6a35\u592b\u730e\u6237\u9690\u58eb\u548c\u88ab\u653e\u9010\u7684\u4eba\uff0c\u4f4f\u7684\u5730\u65b9\u901a\u5e38\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u680b\u6728\u5c4b\u4e0d\u540c\u3002 \u3000\u3000\u8fd9\u95f4\u6728\u5c4b\u4e0d\u662f\u6a35\u592b\u730e\u6237\u7684\u5c45\u6240\uff0c\u4e5f\u4e0d\u662f\u4efb\u4f55\u4eba\u7684\u9690\u5c45\u5904\u3002 \u3000\u3000\u8fd9\u95ee\u6728\u5c4b\u662f\u5415\u4e09\u7684\u79d8\u7a9f\uff0c\u751a\u81f3\u53ef\u4ee5\u7b97\u662f\u5415\u4e09\u6700\u4e3b\u8981\u7684\u79d8\u81ea\u4e4b\u4e00\u3002 \u3000\u3000\u6728\u677f\u684c\u4e5f\u6ca1\u6709\u6f06\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u5750\u5728\u6728\u684c\u65c1\u4e00\u5f20\u6ca1\u6709\u6f06\u7684\u6728\u677f\u51f3\u4e0a\uff0c\u770b\u7740\u5415\u4e09\u3002 \u3000\u3000\u5979\u89c9\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u5979\u4e00\u5411\u8ba4\u4e3a\u81ea\u5df1\u662f\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u8fd9\u4e16\u754c\u4e0a\u5f88\u5c11\u6709\u5979\u4e0d\u61c2\u7684\u4e8b\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u4e5f\u786e\u5b9e\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u53ef\u662f\u5979\u770b\u4e0d\u61c2\u5415\u4e09\u5728\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u5415\u4e09\u6b63\u7ad9\u5728\u8fd9\u95ee\u5c0f\u6728\u5c4b\u552f\u4e00\u7684\u4e00\u4e2a\u5c0f\u7a97\u524d\uff0c\u624b\u91cc\u62ff\u7740\u4e2a\u5c0f\u5706\u7b52\u3002 \u3000\u3000\u4e00\u4e2a\u5927\u7ea6\u6709\u4e24\u5c3a\u957f\u7684\u5c0f\u5706\u7b80\uff0c\u7c97\u7684\u4e00\u5934\u6bd4\u9152\u676f\u7c97\u4e00\u70b9\uff0c\u7ec6\u4e00\u5934\u6bd4\u9152\u676f\u7ec6\u4e00\u70b9\u3002 \u3000\u3000\u8fd9\u4e2a\u5706\u7b80\u662f\u5415\u4e09\u521a\u4ece\u90a3\u4e2a\u6ca1\u6709\u6f06\u7684\u6728\u677f\u67dc\u91cc\u62ff\u51fa\u6765\u7684\u3002 \u3000\u3000\u6728\u67dc\u91cc\u672c\u6765\u53ea\u6709\u51e0\u4ef6\u7c97\u5e03\u8863\u670d\uff0c\u4f46\u662f\u5415\u4e09\u4f38\u624b\u4e5f\u4e0d\u77e5\u5728\u4ec0\u4e48\u5730\u65b9\u4e00\u6309\uff0c\u6728\u67dc\u91cc\u5ffd\u7136\u5f39\u51fa\u4e86\u4e00\u5757\u6728\u677f\uff0c\u6728\u677f\u540e\u5ffd\u7136\u53c8\u51fa\u73b0\u4e86\u4e00\u4e2a\u5c0f\u67dc\u5b50\uff0c\u91d1\u5149\u95ea\u95ea\u7684\u5c0f\u67dc\u5b50\uff0c\u4e0a\u9762\u6709\u4e03\u9053\u9501\u3002 \u3000\u3000\u8fd9\u4e2a\u5c0f\u5706\u7b80\u5c31\u662f\u4ece\u8fd9\u4e2a\u5c0f\u67dc\u5b50\u91cc\u62ff\u51fa\u6765\u7684\u3002 \u3000\u3000\u5415\u4e09\u7ad9\u5728\u7a97\u53e3\uff0c\u95ed\u8d77\u4e86\u5de6\u773c\uff0c\u628a\u8fd9\u4e2a\u5c0f\u5706\u7b80\u6bd4\u8f83\u7ec6\u7684\u4e00\u5934\u5bf9\u5728\u53f3\u773c\u4e0a\uff0c\u628a\u8fd9\u4e2a\u5c0f\u5706\u7b52\u6bd4\u8f83\u7c97\u7684\u4e00\u5934\u5bf9\u4f4f\u5c0f\u7a97\u5916\u3002 \u3000\u3000\u4ed6\u5c31\u8fd9\u4e48\u6837\u7ad9\u5728\u90a3\u91cc\uff0c\u4fdd\u6301\u7740\u8fd9\u79cd\u59ff\u52bf\uff0c\u5df2\u7ecf\u7ad9\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u662f\u4e2a\u559c\u6012\u4e0d\u5f62\u4e8e\u8272\u7684\u4eba\uff0c\u8138\u4e0a\u4e00\u5411\u5f88\u5c11\u6709\u4ec0\u4e48\u8868\u60c5\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u8138\u4e0a\u5374\u6709\u4e86\u5f88\u591a\u79cd\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u80fd\u4ece\u8fd9\u4e2a\u5c0f\u5706\u7b52\u91cc\u770b\u5230\u5f88\u591a\u80fd\u591f\u8ba9\u4ed6\u89c9\u5f97\u975e\u5e38\u6709\u8da3\u7684\u4e8b\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u5c0f\u5b69\u5b50\u5728\u770b\u4e07\u82b1\u7b52\u4e00\u6837\u3002 \u3000\u3000\u5415\u4e09\u5df2\u7ecf\u4e0d\u662f\u5c0f\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u5c0f\u5706\u7b52\u5f53\u7136\u4e5f\u7edd\u4e0d\u4f1a\u662f\u4e07\u82b1\u7b52\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u5b9e\u5728\u770b\u4e0d\u51fa\u4ed6\u5728\u770b\u4ec0\u4e48\uff1f\u4e5f\u60f3\u4e0d\u901a\u4ed6\u5728\u4e8e\u4ec0\u4e48\uff1f \u3000\u3000\u5415\u4e09\u5ffd\u7136\u56de\u5934\u5bf9\u5979\u7b11\u4e86\u7b11\uff0c\u628a\u624b\u91cc\u7684\u5c0f\u5706\u7b80\u9012\u7ed9\u5979\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u6765\u770b\u770b\u3002\u201d \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d\u5c0f\u71d5\u95ee\uff1a\u201c\u770b\u8fd9\u4e2a\u5c0f\u7b52\u5b50\uff1f\u201d \u3000\u3000\u5979\u6447\u5934\u62d2\u7edd\uff1a\u201c\u6211\u4e0d\u770b\u3002\u201d\u5979\u60f3\u4e0d\u51fa\u8fd9\u4e2a\u5c0f\u5706\u7b52\u6709\u4ec0\u4e48\u597d\u770b\u7684\u3002 \u3000\u3000\u4f46\u662f\u5415\u4e09\u5374\u575a\u6301\u3002 \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u8981\u6765\u770b\u770b\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u4fdd\u8bc1\u4f60\u4e00\u5b9a\u53ef\u4ee5\u770b\u5230\u4e00\u4e9b\u5f88\u6709\u8da3\u7684\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u71d5\u4e0d\u76f8\u4fe1\uff0c\u4f46\u662f\u5979\u4e5f\u4e0d\u518d\u575a\u6301\u3002 \u3000\u3000\u5979\u79bb\u5f00\u5c0f\u65b9\u51b3\u5b9a\u6765\u6295\u5954\u5415\u4e09\u65f6\uff0c\u5c31\u5df2\u7ecf\u51b3\u5b9a\u4e0d\u518d\u575a\u6301\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u51b3\u5b9a\u8981\u505a\u4e00\u4e2a\u53c8\u806a\u660e\u53c8\u542c\u8bdd\u7684\u5973\u5b69\u5b50\uff0c\u56e0\u4e3a\u8fd9\u79cd\u4eba\u662f\u7edd\u4e0d\u4f1a\u5403\u4e8f\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u5c0f\u5706\u7b52\u662f\u7528\u91d1\u5c5e\u505a\u6210\u7684\uff0c\u505a\u5f97\u6781\u7cbe\u81f4\uff0c\u4e24\u5934\u90fd\u9576\u7740\u624b\u5de5\u6781\u7cbe\u5999\u7684\u9ec4\u91d1\u82b1\u7eb9\uff0c\u770b\u6765\u65e0\u7591\u662f\u4ef6\u6781\u8d35\u91cd\u7684\u4e1c\u897f\uff0c\u5374\u53c8\u504f\u504f\u770b\u4e0d\u51fa\u5b83\u6709\u4ec0\u4e48\u7528\uff1f \u3000\u3000\u5415\u4e09\u8981\u5c0f\u71d5\u7528\u4ed6\u521a\u624d\u540c\u6837\u7684\u59ff\u52bf\u62ff\u4f4f\u5b83\uff0c\u7528\u4e24\u53ea\u624b\u62ff\u4f4f\u5b83\u7684\u524d\u540e\u4e24\u7aef\uff0c\u4e3e\u5728\u53f3\u773c\u524d\uff0c\u5bf9\u51c6\u7a97\u53e3\uff0c\u95ed\u4e0a\u5de6\u773c\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e2a\u975e\u5e38\u975e\u5e38\u806a\u660e\u7684\u5973\u5b69\u5b50\u3002\u201d\u5415\u4e09\u5fae\u7b11\uff1a\u201c\u53ef\u662f\u6211\u4fdd\u8bc1\u4f60\u4e00\u5b9a\u60f3\u4e0d\u5230\u4f60\u4f1a\u4ece\u8fd9\u4e2a\u5706\u7b52\u91cc\u770b\u5230\u4ec0\u4e48\u4e8b\u7684\u3002\u201d \u3000\u3000\u5c0f\u71d5\u679c\u7136\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5979\u4f1a\u4ece\u8fd9\u4e2a\u5706\u7b80\u91cc\u770b\u5230\u5c0f\u65b9\u3002 \u3000\u3000\u2014\u2014\u5c0f\u65b9\uff0c\u8981\u547d\u7684\u5c0f\u65b9\u3002 \u3000\u3000\u5979\u4e00\u76f4\u8ba4\u4e3a\u81ea\u5df1\u662f\u4e2a\u65e0\u60c5\u7684\u5973\u4eba\uff0c\u7edd\u5bf9\u6bd4\u4efb\u4f55\u4e00\u4e2a\u50cf\u5979\u8fd9\u79cd\u5e74\u7eaa\u7684\u5c11\u5973\u90fd\u65e0\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u786e\u975e\u5e38\u975e\u5e38\u806a\u660e\uff0c\u591a\u5e74\u524d\u5979\u5c31\u5df2\u77e5\u9053\u591a\u60c5\u662f\u4ef6\u591a\u4e48\u4ee4\u4eba\u75db\u82e6\u7684\u4e8b\u3002 \u3000\u3000\u5979\u4e00\u76f4\u60f3\u5fd8\u8bb0\u5c0f\u65b9\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e16\u754c\u53c8\u6709\u54ea\u4e2a\u5c11\u5973\u80fd\u8fd9\u4e48\u5feb\u5c31\u5fd8\u8bb0\u5979\u7684\u7b2c\u4e00\u4e2a\u7537\u4eba\uff1f \u3000\u3000\u81ea\u4ece\u5979\u770b\u89c1\u5c0f\u65b9\u5bf9\u201c\u9633\u5149\u201d\u548c\u82cf\u82cf\u7684\u6001\u5ea6\uff0c\u770b\u5230\u4ed6\u5bf9\u5979\u4eec\u6d41\u9732\u51fa\u7684\u90a3\u79cd\u611f\u60c5\uff0c\u5979\u5c31\u5df2\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u8981\u79bb\u5f00\u8fd9\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u8981\u547d\u7684\u7537\u4eba\uff0c\u4eff\u4f5b\u65e0\u60c5\uff0c\u5374\u53c8\u504f\u504f\u591a\u60c5\uff0c\u4eff\u4f5b\u591a\u60c5\uff0c\u5374\u53c8\u504f\u504f\u65e0\u60c5\u3002 \u3000\u3000\u5979\u6084\u6084\u5730\u9000\u51fa\u4e86\u90a3\u95f4\u5c0f\u5c4b\uff0c\u9000\u51fa\u4e86\u4ed6\u4eec\u90a3\u4e2a\u590d\u6742\u7684\u5708\u5b50\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u5982\u679c\u518d\u7559\u4e0b\u53bb\uff0c\u53ea\u4f1a\u53d8\u5f97\u66f4\u75db\u82e6\u66f4\u70e6\u607c\u66f4\u4f24\u5fc3\u3002 \u3000\u3000\u5979\u4e00\u5411\u4e0d\u613f\u6298\u78e8\u81ea\u5df1\u3002 \u3000\u3000\u4ece\u90a3\u65f6\u5f00\u59cb\uff0c\u5979\u5c31\u4e0d\u60f3\u518d\u89c1\u5230\u5c0f\u65b9\u4e86\u3002 \u3000\u3000\u2014\u2014\u76f8\u89c1\u4e0d\u5982\u4e0d\u89c1\uff0c\u7eb5\u7136\u6709\u60c5\uff0c\u6b64\u60c5\u4e5f\u53ea\u6709\u7559\u5f85\u8ffd\u5fc6\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u5979\u4e3e\u8d77\u4e86\u8fd9\u4e2a\u5c0f\u5706\u7b52\uff0c\u8fd9\u4e2a\u65e2\u591a\u60c5\u53c8\u65e0\u60c5\u7684\u5c0f\u65b9\u5374\u5ffd\u7136\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u5706\u7b52\u7684\u4e2d\u95f4\u662f\u7a7a\u7684\uff0c\u4e24\u5934\u90fd\u5d4c\u7740\u4e00\u79cd\u4eff\u4f5b\u50cf\u662f\u6c34\u6676\u7684\u900f\u660e\u7269\u3002 \u3000\u3000\u5979\u4e3e\u8d77\u8fd9\u4e2a\u5706\u7b52\uff0c\u628a\u8f83\u7ec6\u7684\u4e00\u5934\u5bf9\u4f4f\u81ea\u5df1\u7684\u53f3\u773c\uff0c\u628a\u8f83\u7c97\u7684\u4e00\u5934\u5bf9\u7740\u7a97\u53e3\u3002\u8fd9\u4e2a\u8981\u547d\u7684\u5c0f\u65b9\u5c31\u5ffd\u7136\u51fa\u73b0\u5728\u5979\u773c\u524d\u3002 \u3000\u3000\u5415\u4e09\u4e00\u76f4\u5728\u770b\u7740\u5979\uff0c\u4e5f\u4e0d\u77e5\u662f\u4e0d\u662f\u60f3\u4ece\u5979\u8138\u4e0a\u7684\u8868\u60c5\u548c\u53cd\u5e94\u4e0a\u770b\u51fa\u5979\u5bf9\u5c0f\u65b9\u7684\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u73b0\u5728\u4e00\u5b9a\u5df2\u7ecf\u770b\u89c1\u4e86\u5c0f\u65b9\uff0c\u53ef\u662f\u5979\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5979\u7684\u624b\u8fd8\u662f\u548c\u521a\u624d\u4e00\u6837\u7a33\u5b9a\uff0c\u5979\u7684\u8138\u8272\u4e5f\u5b8c\u5168\u6ca1\u6709\u6539\u53d8\u3002 \u3000\u3000\u2014\u2014\u9f50\u5c0f\u71d5\u4eca\u5e74\u624d\u5341\u516d\u5c81\uff0c\u53ef\u662f\u5979\u5df2\u7ecf\u628a\u81ea\u5df1\u8bad\u7ec3\u5f97\u50cf\u4e03\u5341\u5c81\u4e00\u6837\u3002 \u3000\u3000\u5979\u53ea\u95ee\u5415\u4e09\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u5979\u95ee\u7684\u662f\u5979\u624b\u91cc\u7684\u8fd9\u4e2a\u5c0f\u5706\u7b52\u3002 \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u8fd9\u662f\u4ece\u6bd4\u82f1\u5409\u5229\u56fd\u66f4\u8fdc\u7684\u4e00\u4e2a\u56fd\u5ea6\u5f97\u6765\u7684\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u8fd9\u79cd\u4e1c\u897f\u8fd8\u6ca1\u6709\u540d\u5b57\uff0c\u56e0\u4e3a\u8fd9\u79cd\u4e1c\u897f\u4ee5\u524d\u4ece\u6765\u90fd\u6ca1\u6709\u88ab\u4f20\u5165\u5230\u4e2d\u571f\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u53ea\u6709\u4f60\u770b\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u73b0\u5728\u5b83\u5df2\u7ecf\u6709\u4e00\u4e2a\u540d\u5b57\u4e86\uff0c\u201d\u5415\u4e09\u5f97\u610f\u5fae\u7b11\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u7ecf\u66ff\u5b83\u53d6\u4e86\u4e00\u4e2a\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u51c6\u5907\u53eb\u5b83\u5343\u91cc\u773c\u955c\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u53ef\u662f\u8fd9\u540d\u5b57\u592a\u4fd7\uff0c\u800c\u4e14\u542c\u6765\u597d\u50cf\u662f\u795e\u8bdd\u4e2d\u7684\u6cd5\u5b9d\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff0c\u201c\u8fd9\u4e0d\u662f\u795e\u8bdd\uff0c\u8fd9\u662f\u771f\u771f\u5b9e\u5b9e\u7684\u4e1c\u897f\uff0c\u5b83\u552f\u4e00\u7684\u7528\u5904\uff0c\u5c31\u662f\u80fd\u671b\u8fdc\uff0c\u6240\u4ee5\u6211\u624d\u51b3\u5b9a\u6b63\u5f0f\u4e3a\u5b83\u547d\u540d\u4e3a\u2018\u671b\u8fdc\u955c\u2019\u3002\u201d \u3000\u3000\u201c\u671b\u8fdc\u955c\uff1f\u201d\u5c0f\u71d5\u8bf4\uff1a\u201c\u8fd9\u662f\u4e2a\u597d\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e1c\u897f\u4e5f\u662f\u6837\u597d\u4e1c\u897f\u3002\u201d \u3000\u3000\u5c0f\u71d5\u540c\u610f\uff1a\u201c\u6240\u4ee5\u8fd9\u6837\u4e1c\u897f\u548c\u8fd9\u4e2a\u540d\u5b57\u90fd\u4e00\u5b9a\u53ef\u4ee5\u7559\u4f20\u5343\u53e4\u3002\u201d \u3000\u3000\u5979\u867d\u7136\u5728\u8bf4\u8bdd\uff0c\u53ef\u662f\u5979\u7684\u773c\u775b\u4e00\u76f4\u90fd\u6ca1\u6709\u79bb\u5f00\u5979\u624b\u91cc\u8fd9\u4e2a\u671b\u8fdc\u955c\u3002\u5c0f\u65b9\u7684\u6bcf\u4e00\u4e2a\u52a8\u4f5c\uff0c\u5979\u90fd\u6ca1\u6709\u9519\u8fc7\u3002 \u3000\u3000\u5415\u4e09\u5ffd\u7136\u53c8\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u8fd8\u5b66\u8fc7\u4e00\u6837\u5f88\u5c11\u6709\u4eba\u80fd\u5b66\u5f97\u4f1a\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8bfb\u5507\u8bed\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u4e2a\u975e\u5e38\u65b0\u5947\u7684\u540d\u5b57\uff0c\u5415\u4e09\u89e3\u91ca\uff1a\u201c\u53ea\u8981\u4f60\u80fd\u770b\u89c1\u4e00\u4e2a\u4eba\u5728\u8bf4\u8bdd\u65f6\u7684\u5634\u5f62\uff0c\u4f60\u5c31\u80fd\u77e5\u9053\u4ed6\u5728\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u4f60\u5bf9\u6211\u7684\u4e8b\u597d\u50cf\u77e5\u9053\u5f97\u5f88\u591a\u3002\u201d \u3000\u3000\u8bf4\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u9f50\u5c0f\u71d5\u5e76\u6ca1\u6709\u8868\u73b0\u51fa\u4e00\u70b9\u4e0d\u6109\u5feb\u7684\u6837\u5b50\uff0c\u800c\u4e14\u8fd8\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u5f53\u7136\u5e94\u8be5\u77e5\u9053\u5f97\u5f88\u591a\uff0c\u5426\u5219\u4f60\u600e\u4e48\u4f1a\u6536\u5bb9\u6211\uff1f\u201d \u3000\u3000\u5415\u4e09\u4e5f\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u770b\u6765\u6211\u4eec\u5f7c\u6b64\u90fd\u5f88\u4e86\u89e3\u3002\u6240\u4ee5\u6211\u76f8\u4fe1\u6211\u4eec\u4ee5\u540e\u4e00\u5b9a\u4f1a\u76f8\u5904\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u7136\u540e\u4ed6\u53c8\u95f4\u5979\uff1a\u201c\u73b0\u5728\u662f\u8c01\u5728\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u201c\u662f\u73ed\u5bdf\u5df4\u90a3\u3002\u201d \u3000\u3000\u201c\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5728\u5947\u602a\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4ed6\u60f3\u4e0d\u901a\u4f60\u4e3a\u4ec0\u4e48\u8981\u6d3e\u5415\u606d\u90a3\u4e48\u6837\u4e00\u4e2a\u4eba\u53bb\u628a\u4ed6\u7559\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u5415\u4e09\u5fae\u7b11\uff01 \u3000\u3000\u201c\u4ed6\u8fd8\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u4f60\u6d3e\u53bb\u66ff\u4ed6\u4eec\u7092\u83dc\u70e4\u8089\u7684\u90a3\u4e9b\u4eba\uff0c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u53ef\u80fd\u662f\u6b66\u6797\u9ad8\u624b\u3002\u201d\u5c0f\u71d5\u8bf4\uff1a\u201c\u4ed6\u8fd8\u8bf4\u8fde\u90a3\u4e2a\u6b63\u5728\u6dfb\u706b\u7684\u4eba\u7528\u7684\u90a3\u628a\u706b\u94b3\u5b50\uff0c\u90fd\u53ef\u80fd\u662f\u4ef6\u5f88\u5389\u5bb3\u7684\u5916\u95e8\u5175\u5668\u3002\u201d \u3000\u3000\u5415\u4e09\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u535c\u9e70\u662f\u4eba\u6770\uff0c\u4f9d\u6211\u770b\uff0c\u73ed\u5bdf\u5df2\u90a3\u7edd\u4e0d\u6bd4\u535c\u9e70\u5dee\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u95ee\uff1a\u201c\u4f60\u731c\u4ed6\u4f1a\u4e0d\u4f1a\u6740\u5415\u606d\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u73b0\u5728\u4ed6\u4e5f\u6b63\u5728\u95ee\u5c0f\u65b9\uff0c\u540c\u6837\u4e5f\u662f\u5728\u95ee\u8fd9\u53e5\u8bdd\u3002\u201d \u3000\u3000\u201c\u5c0f\u65b9\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u5c0f\u65b9\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u8ddf\u5c0f\u65b9\u4e00\u6837\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201d\u4f60\u548c\u73ed\u5bdf\u5df4\u90a3\u8fd9\u79cd\u4eba\u505a\u7684\u4e8b\uff0c\u6211\u4eec\u6c38\u8fdc\u90fd\u731c\u4e0d\u900f\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u7528\u4e00\u53cc\u67d4\u8f6f\u7ea4\u957f\uff0c\u4fdd\u517b\u5f97\u975e\u5e38\u597d\u7684\u624b\uff0c\u8f7b\u8f7b\u6162\u6162\u5730\u6574\u7406\u7740\u8170\u4e0a\u7684\u91d1\u8272\u7f0e\u5e26\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u95ee\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u548c\u73ed\u5bdf\u5df4\u90a3\u662f\u540c\u4e00\u79cd\u4eba\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u6ca1\u6709\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u5415\u4e09\u597d\u50cf\u4e5f\u4e0d\u60f3\u8981\u5979\u56de\u7b54\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u8bf4\uff1a\u201c\u5982\u679c\u6211\u662f\u73ed\u5bdf\u5df4\u90a3\uff0c\u6211\u7edd\u4e0d\u4f1a\u6740\u5415\u606d\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u56e0\u4e3a\u5415\u606d\u8fd9\u79cd\u4eba\u6839\u672c\u4e0d\u503c\u5f97\u4ed6\u51fa\u624b\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u7b2c\u4e8c\uff0c\u56e0\u4e3a\u5415\u606d\u4ee5\u540e\u5bf9\u4ed6\u4e5f\u8bb8\u8fd8\u6709\u7528\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u5415\u606d\u81ea\u5df1\u4e5f\u8fd9\u4e48\u8bf4\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u53e6\u5916\u8fd8\u6709\u66f4\u91cd\u8981\u7684\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u2018\u73ed\u5bdf\u5df4\u90a3\u4e0d\u6740\u5415\u606d\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u4e0d\u60f3\u5192\u9669\u3002\u201d \u3000\u3000\u201c\u5192\u9669\uff1f\u201d\u5c0f\u71d5\u95ee\uff1a\u201c\u5192\u4ec0\u4e48\u9669\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u770b\u9519\uff0c\u6211\u6d3e\u53bb\u66ff\u4ed6\u4eec\u7092\u83dc\u70e4\u8089\u6dfb\u706b\u7684\u4eba\uff0c\u786e\u5b9e\u90fd\u662f\u6b66\u6797\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u66ff\u4ed6\u4eec\u6dfb\u67f4\u751f\u706b\u7684\u90a3\u4e2a\u4eba\u5916\u53f7\u53eb\u2018\u8783\u87f9\u2019\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u4ed6\u7528\u6765\u6dfb\u67f4\u751f\u706b\u7684\u90a3\u4e2a\u94c1\u94b3\u5b50\uff0c\u7684\u786e\u662f\u4ef6\u72ec\u521b\u7684\u5916\u95e8\u6b66\u5668\uff0c\u4e0d\u4f46\u53ef\u4ee5\u94b3\u6b7b\u5bf9\u65b9\u7684\u5175\u5203\uff0c\u62a4\u624b\u7684\u628a\u5b50\u4e0a\u8fd8\u53e6\u6709\u5999\u7528\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u7684\u5175\u5203\u4e00\u65e6\u88ab\u4ed6\u94b3\u4f4f\uff0c\u90a3\u94c1\u94b3\u7684\u624b\u628a\u7acb\u523b\u5c31\u4f1a\u5f39\u51fa\u3002\u201d\u5415\u4e09\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u4e00\u53cd\u624b\uff0c\u5c31\u53ef\u4ee5\u523a\u7a7f\u4f60\u7684\u5fc3\u810f\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\uff1a\u201c\u8fd9\u662f\u4ed6\u72ec\u521b\u7684\u6b66\u5668\uff0c\u6c5f\u6e56\u4e2d\u89c1\u5230\u8fc7\u7684\u4eba\u8fd8\u4e0d\u591a\uff0c\u56e0\u4e3a\u4ed6\u51fa\u9053\u8fd8\u4e0d\u53ca\u4e00\u5e74\uff0c\u5c31\u88ab\u6211\u6536\u5bb9\u4e86\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u73ed\u5bdf\u5df4\u90a3\u5c45\u7136\u80fd\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u66ff\u4ed6\u70e4\u8089\u7684\u90a3\u4e2a\u4eba\u5e73\u5e38\u70e4\u7684\u771f\u662f\u4eba\u8089\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e2a\u4eba\u7684\u5916\u53f7\u53eb\u2018\u53c9\u5b50\u2019\uff0c\u65e0\u8bba\u4ec0\u4e48\u4eba\u53ea\u8981\u4e00\u88ab\u4ed6\u770b\u4e0a\uff0c\u5c31\u597d\u50cf\u88ab\u53c9\u5b50\u53c9\u4f4f\u4e86\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u7136\u540e\u4ed6\u662f\u4e0d\u662f\u5c31\u4f1a\u628a\u88ab\u4ed6\u53c9\u4f4f\u7684\u90a3\u4e2a\u4eba\uff0c\u9001\u5230\u706b\u4e0a\u53bb\u70e4\u4e00\u70e4\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u5415\u4e09\u9053\uff1a\u201c\u5982\u679c\u4f60\u88ab\u4ed6\u53c8\u4f4f\u4e86\uff0c\u4e5f\u8bb8\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u4f1a\u628a\u4f60\u9001\u5230\u706b\u4e0a\u53bb\u70e4\uff0c\u53ef\u662f\u4f60\u81ea\u5df1\u7684\u611f\u89c9\u5374\u4e00\u5b9a\u662f\u90a3\u6837\u5b50\u7684\uff0c\u751a\u81f3\u5f88\u53ef\u80fd\u6bd4\u88ab\u706b\u70e4\u8fd8\u96be\u53d7\u3002\u201d \u3000\u3000\u201c\u53e6\u5916\u90a3\u4e9b\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e9b\u4eba\u4e5f\u8ddf\u4ed6\u4eec\u5dee\u4e0d\u591a\uff0c\u201d\u5415\u4e09\u9053\uff1a\u201c\u51e0\u4e4e\u6bcf\u4e00\u4e2a\u90fd\u662f\u5fc3\u72e0\u624b\u8fa3\u3001\u6740\u4eba\u5982\u9ebb\u7684\u89d2\u8272\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u670d\u4f60\uff1f\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u592a\u72e0\uff0c\u6240\u4ee5\u624d\u4f1a\u670d\u6211\uff0c\u201d\u5415\u4e09\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u9664\u4e86\u6765\u6295\u5954\u6211\u4e4b\u5916\uff0c\u6839\u672c\u5df2\u65e0\u5904\u53ef\u53bb\uff0c\u5728\u6c5f\u6e56\u4e2d\u6839\u672c\u5df2\u65e0\u6cd5\u7acb\u8db3\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u8981\u6740\u4eba\u7684\u4eba\uff0c\u522b\u4eba\u5f53\u7136\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u201c\u5b8c\u5168\u6b63\u786e\u3002\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u4e0d\u6740\u5415\u606d\uff0c\u5c31\u56e0\u4e3a\u5728\u987e\u5fcc\u4ed6\u4eec\u8fd9\u4e9b\u4eba\uff1f\u201d\u9f50\u5c0f\u71d5\u95f4\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u7edd\u5bf9\u5f88\u91cd\u8981\uff0c\u201d\u5415\u4e09\u9053\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\u4e00\u5411\u662f\u4e2a\u975e\u5e38\u8c28\u614e\u7684\u4eba\uff0c\u4e0d\u5fc5\u8981\u7684\u4e8b\u4ed6\u7edd\u4e0d\u4f1a\u505a\uff0c\u6ca1\u628a\u63e1\u7684\u4e8b\u4ed6\u66f4\u4e0d\u4f1a\u505a\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5462\uff1f\u201d\u9f50\u5c0f\u71d5\u53c8\u95ee\uff1a\u201c\u4f60\u4e00\u76f4\u60f3\u9664\u53bb\u73ed\u5bdf\u5df4\u90a3\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e58\u8fd9\u4e2a\u673a\u4f1a\u52a8\u624b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u4e2a\u673a\u4f1a\u8fd8\u4e0d\u7b97\u592a\u597d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u5728\u9644\u8fd1\u5f88\u53ef\u80fd\u4e5f\u6709\u57cb\u4f0f\uff0c\u51ed\u2018\u8783\u87f9\u2019\u548c\u2018\u53c9\u5b50\u2019\u90a3\u4e9b\u4eba\uff0c\u4e5f\u672a\u5fc5\u80fd\u5c06\u73ed\u5bdf\u5df4\u90a3\u548c\u5c0f\u65b9\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u5415\u4e09\u53c8\u8865\u5145\uff1a\u201c\u56e0\u4e3a\u90a3\u5730\u65b9\u6839\u672c\u4e0d\u662f\u6b7b\u5730\uff0c\u56db\u9762\u90fd\u6709\u9000\u8def\uff0c\u4ed6\u4eec\u5c31\u7b97\u4e0d\u80fd\u53d6\u80dc\uff0c\u4e5f\u53ef\u4ee5\u9000\u8d70\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u660e\u77e5\u5982\u6b64\uff0c\u4e3a\u4ec0\u4e48\u8981\u9009\u62e9\u8fd9\u4e48\u6837\u4e00\u4e2a\u5730\u65b9\u8bf7\u4ed6\uff1f\u201d \u3000\u3000\u5415\u4e09\u53f9\u4e86\u53e3\u6c14\uff01 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff1f\u201d\u4ed6\u8bf4\uff1a\u201c\u5982\u679c\u4e0d\u662f\u8fd9\u79cd\u5730\u65b9\uff0c\u4ed6\u600e\u4e48\u4f1a\u53bb\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u4e5f\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u90a3\u4e48\u6211\u5c31\u66f4\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u5979\u4e0d\u61c2\u7684\u662f\uff1a\u201c\u4f60\u81ea\u5df1\u6839\u672c\u4e0d\u60f3\u4e58\u8fd9\u4e2a\u673a\u4f1a\u52a8\u624b\u9664\u53bb\u4ed6\uff0c\u53c8\u77e5\u9053\u4ed6\u4e5f\u4e0d\u4f1a\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u6d3e\u5415\u606d\u548c\u90a3\u4e9b\u4eba\uff0c\u53bb\u628a\u73ed\u5bdf\u5df4\u90a3\u548c\u5c0f\u65b9\u7559\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u89c2\u5bdf\u4ed6\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\u7684\u884c\u8e2a\u98d8\u5ffd\uff0c\u795e\u51fa\u9b3c\u6ca1\uff0c\u800c\u4e14\u4e00\u5411\u72ec\u6765\u72ec\u5f80\uff0c\u53ef\u4ee5\u8bf4\u662f\u8fd1\u767e\u5e74\u6765\u6c5f\u6e56\u4e2d\u6700\u795e\u79d8\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u8c01\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u53ea\u6709\u5236\u9020\u8fd9\u4e48\u6837\u4e00\u4e2a\u673a\u4f1a\uff0c\u518d\u52a0\u4e0a\u8fd9\u67b6\u6211\u7528\u4e00\u5bf9\u7eaf\u79cd\u7684\u5927\u5b9b\u6c57\u8840\u9a6c\uff0c\u548c\u4e00\u67c4\u6c49\u672b\u65f6\u66f9\u64cd\u60f3\u7528\u6765\u65a9\u6740\u8463\u684c\u7684\u5b9d\u5200\uff0c\u4ece\u6ce2\u65af\u5927\u8d3e\u2018\u80e1\u585e\u2019\u90a3\u91cc\u6362\u6765\u7684\u671b\u8fdc\u773c\u955c\uff0c\u624d\u80fd\u89c2\u5bdf\u5230\u4ed6\u7684\u8a00\u8bed\u795e\u6001\u884c\u52a8\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u4ed8\u51fa\u8fd9\u4e48\u5927\u7684\u4ee3\u4ef7\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u770b\u770b\u4ed6\u800c\u5df2\uff1f\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u56db\u4e00\u7ae0\u3000\u81f4\u547d\u7684\u4f24\u53e3 \u7b2c\u56db\u5341\u4e00\u7ae0\u3000\u81f4\u547d\u7684\u4f24\u53e3 \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u77e5\u5df1\u77e5\u5f7c\uff0c\u624d\u80fd\u767e\u6218\u767e\u80dc\uff0c\u4ed6\u662f\u6211\u751f\u5e73\u552f\u4e00\u7684\u5bf9\u624b\uff0c\u5982\u679c\u6211\u8fde\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u90fd\u4e0d\u77e5\u9053\uff0c\u600e\u4e48\u80fd\u6218\u80dc\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4f60\u771f\u7684\u8ba4\u4e3a\u4ed6\u662f\u4f60\u751f\u5e73\u552f\u4e00\u7684\u5bf9\u624b\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\uff01\u201d \u3000\u3000\u201c\u535c\u9e70\u5462\uff1f\u201d \u3000\u3000\u201c\u535c\u9e70\uff1f\u201d\u5415\u4e09\u7b11\u4e86\u7b11\uff1a \u3000\u3000\u201c\u535c\u9e70\u4e0d\u8db3\u8651\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u9f50\u5c0f\u71d5\u5fcd\u4e0d\u4f4f\u95ee\uff1a \u3000\u3000\u201c\u522b\u4eba\u90fd\u8bf4\u535c\u9e70\u662f\u5f53\u4e16\u7684\u4eba\u6770\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u5982\u6b64\u770b\u8f7b\u4ed6\uff1f\u201d \u3000\u3000\u5415\u4e09\u6c89\u601d\u4e86\u5f88\u4e45\u4e4b\u540e\u624d\u56de\u7b54\u8fd9\u95ee\u9898\uff1a \u3000\u3000\u201c\u535c\u9e70\u548c\u73ed\u5bdf\u5df4\u90a3\u4e0d\u540c\u3002\u201d\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u535c\u9e70\u867d\u7136\u6709\u67ad\u96c4\u4e4b\u624d\u3002\u5929\u6027\u5374\u662f\u7231\u597d\u548c\u5e73\u7684\uff0c\u4ed6\u6740\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u9632\u6b62\u66f4\u591a\u4eba\u88ab\u6740\uff0c\u4ed6\u6218\u6597\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u8981\u6d88\u706d\u66f4\u5927\u7684\u6218\u4e89\uff0c\u4ed6\u5916\u8868\u770b\u6765\u867d\u7136\u51b7\u9177\u65e0\u60c5\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u5fc3\u80a0\u5f88\u8f6f\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u5462\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u5c31\u4e0d\u540c\u4e86\u3002\u201d\u5415\u4e09\u8bf4\u3002 \u3000\u3000\u201c\u4ed6\u5929\u751f\u5c31\u662f\u4e2a\u6218\u6597\u8005\uff0c\u800c\u4e14\u4e00\u5b9a\u8981\u6218\u80dc\u3002\u4e0d\u60dc\u4efb\u4f55\u4ee3\u4ef7\uff0c\u4e0d\u62e9\u4efb\u4f55\u624b\u6bb5\uff0c\u90fd\u8981\u6218\u80dc\uff0c\u53ea\u8bb8\u80dc\uff0c\u4e0d\u8bb8\u8d25\u3002\u4e0d\u80fd\u80dc\uff0c\u5c31\u662f\u6b7b\uff0c\u5176\u95f4\u7edd\u65e0\u9009\u62e9\u7684\u4f59\u5730\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff1a \u3000\u3000\u201c\u5176\u5b9e\u6211\u4e00\u76f4\u90fd\u5f88\u559c\u6b22\u535c\u9e70\u8fd9\u4e2a\u4eba\uff0c\u800c\u4e14\u4e00\u5411\u90fd\u5bf9\u4ed6\u5341\u5206\u5c0a\u656c\uff0c\u5982\u679c\u4ed6\u4e0d\u6b7b\uff0c\u4ee5\u540e\u6211\u4eec\u8bf4\u4e0d\u5b9a\u4f1a\u53d8\u6210\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u4e0d\u6b7b\uff01\u201d\u9f50\u5c0f\u71d5\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a \u3000\u3000\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5415\u4e09\u70b9\u5934\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u53c8\u95ee\uff1a \u3000\u3000\u201c\u662f\u4f60\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5415\u4e09\u6447\u5934\u3002 \u3000\u3000\u201c\u8981\u6740\u535c\u9e70\u5e76\u975e\u5bb9\u6613\uff0c\u8fde\u6211\u90fd\u505a\u4e0d\u5230\u3002\u201d\u4ed6\u53c8\u5728\u53f9\u606f\u9053\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u6211\u662f\u4ed6\u7684\u4ec7\u654c\uff0c\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u53ea\u6709\u4ed6\u7684\u670b\u53cb\u624d\u80fd\u6740\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\uff01\u201d\u5415\u4e09\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff1a \u3000\u3000\u201c\u53ea\u6709\u73ed\u5bdf\u5df4\u90a3\uff0c\u518d\u65e0\u522b\u4eba\uff01\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u4e48\u60f3\uff1f\u201d\u5c0f\u71d5\u95ee\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4e00\u5411\u662f\u6700\u597d\u7684\u4f19\u4f34\uff0c\u73ed\u5bdf\u5df4\u90a3\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5415\u4e09\u6162\u6162\u5730\u4f38\u51fa\u624b\uff0c\u4ed6\u7684\u624b\u91cc\u63e1\u7740\u7684\u662f\u4e00\u5757\u5341\u8db3\u7eaf\u91d1\u3002 \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u8fd9\u6837\u4e1c\u897f\u3002\u201d \u3000\u3000\u201c\u9ec4\u91d1\uff1f\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u73ed\u5bdf\u5df4\u90a3\u662f\u4e3a\u4e86\u9ec4\u91d1\u800c\u6740\u535c\u9e70\u7684\uff1f\u201d \u3000\u3000\u5415\u4e09\u51dd\u89c6\u7740\u638c\u4e2d\u7684\u9ec4\u91d1\u3002 \u3000\u3000\u201c\u5343\u53e4\u4ee5\u6765\uff0c\u4e3a\u4e86\u8fd9\u6837\u4e1c\u897f\u6740\u4eba\u7684\u4eba\u4e5f\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u3002\u201d\u4ed6\u770b\u7740\u9f50\u5c0f\u71d5\u70b9\u4e86\u70b9\u5934\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u8fd9\u4e2a\u7406\u7531\u8fd8\u4e0d\u591f\uff1f\u201d \u3000\u3000\u8fd9\u7406\u7531\u5f53\u7136\u5df2\u8db3\u591f\uff0c\u9f50\u5c0f\u71d5\u5374\u8fd8\u662f\u4e0d\u61c2\u3002 \u3000\u3000\u5415\u4e09\u53c8\u89e3\u91ca\uff1a \u3000\u3000\u201c\u9ec4\u91d1\u662f\u4ed6\u4eec\u4e24\u4e2a\u4eba\u5171\u540c\u8ba1\u5212\u4ece\u6211\u8fd9\u91cc\u76d7\u8d70\u7684\uff0c\u4f46\u662f\u4ed6\u4eec\u7684\u76ee\u7684\u5374\u4e0d\u540c\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u201c\u535c\u9e70\u76d7\u53bb\u6211\u7684\u9ec4\u91d1\uff0c\u662f\u4e3a\u4e86\u8981\u963b\u6b62\u6211\u5229\u7528\u8fd9\u4e9b\u9ec4\u91d1\u5b9e\u73b0\u6211\u7684\u7406\u60f3\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u53ea\u60f3\u5c06\u90a3\u4e9b\u9ec4\u91d1\u6c38\u8fdc\u57cb\u85cf\u4e8e\u5730\u4e0b\u3002\u53ea\u8981\u4ed6\u6d3b\u7740\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u8ba9\u4efb\u4f55\u4eba\u53bb\u52a8\u7528\u5b83\u3002\u201d \u3000\u3000\u5415\u4e09\u53c8\u8bf4\uff1a\u201c\u4f46\u662f\u73ed\u5bdf\u5df4\u90a3\u5374\u60f3\u5229\u7528\u90a3\u4e9b\u9ec4\u91d1\u6765\u6253\u51fb\u6211\uff0c\u6218\u80dc\u6211\uff0c\u4ed6\u8ba4\u4e3a\u5c06\u9ec4\u91d1\u57cb\u5728\u5730\u4e0b\uff0c\u800c\u4e0d\u52a0\u5229\u7528\u5b9e\u5728\u662f\u4ef6\u611a\u8822\u4e4b\u6781\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u4ed6\u4e5f\u6ca1\u6cd5\u5b50\u8bf4\u670d\u535c\u9e70\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u7ec8\u4e8e\u6e10\u6e10\u660e\u767d\uff1a \u3000\u3000\u201c\u535c\u9e70\u7684\u547d\u4ee4\uff0c\u4ed6\u4e5f\u4e0d\u6562\u53cd\u6297\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u53ea\u6709\u628a\u535c\u9e70\u6740\u4e86\uff0c\u800c\u4e14\u8ba9\u522b\u4eba\u8ba4\u4e3a\u662f\u6211\u6740\u7684\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u535c\u9e70\u4e0d\u662f\u4f60\u6740\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u516c\u5f00\u5426\u8ba4\uff1f\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u5426\u8ba4\uff1f\u201d\u5415\u4e09\u51b7\u7b11\uff1a \u3000\u3000\u201c\u8981\u6740\u535c\u9e70\u5e76\u4e0d\u5bb9\u6613\uff0c\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u80fd\u6740\u5f97\u4e86\u4ed6\u7684\uff0c\u5982\u679c\u522b\u4eba\u8ba4\u4e3a\u662f\u6211\u6740\u4e86\u4ed6\uff0c\u5c82\u975e\u662f\u4ef6\u5f88\u5149\u91c7\u7684\u4e8b\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u5426\u8ba4\uff1f\u201d \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u8427\u7d22\u4e4b\u610f\uff1a \u3000\u3000\u201c\u4f55\u51b5\uff0c\u4e0d\u662f\u6211\u6740\u7684\u4eba\u800c\u7b97\u5728\u6211\u7684\u8d26\u4e0a\u6765\uff1b\u672c\u6765\u5df2\u7ecf\u591f\u591a\u4e86\uff0c\u518d\u589e\u52a0\u4e00\u4e2a\u53c8\u4f55\u59a8\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u7684\u773c\u775b\u672c\u6765\u4e00\u76f4\u6ca1\u6709\u79bb\u5f00\u8fc7\u5979\u624b\u91cc\u7684\u671b\u8fdc\u955c\uff0c\u76f4\u5230\u8fd9\u65f6\u624d\u56de\u5934\uff0c\u76ef\u7740\u5415\u4e09\uff0c\u4eff\u4f5b\u60f3\u4ece\u4ed6\u7684\u8868\u60c5\u4e2d\u770b\u51fa\u4ed6\u8bf4\u7684\u8fd9\u4e9b\u8bdd\uff0c\u7a76\u7adf\u662f\u771f\u662f\u5047\uff1f \u3000\u3000\u4f46\u662f\u5979\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u6240\u4ee5\u5979\u53c8\u95ee\uff1a \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u73ed\u5bdf\u5df4\u90a3\u662f\u4e3a\u4ec0\u4e48\u8981\u6740\u535c\u9e70\u7684\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u7684\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u5f88\u96be\u56de\u7b54\u7684\u95ee\u9898\uff0c\u5f88\u5c11\u6709\u4eba\u613f\u610f\u56de\u7b54\u8fd9\u79cd\u6709\u5173\u4e00\u4e2a\u4eba\u5185\u5fc3\u601d\u60f3\u79d8\u5bc6\u7684\u95ee\u9898\u3002 \u3000\u3000\u5415\u4e09\u5c45\u7136\u613f\u610f\uff0c\u800c\u4e14\u5f88\u5feb\u5c31\u56de\u7b54\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u6211\u548c\u73ed\u5bdf\u5df4\u90a3\u786e\u5b9e\u662f\u540c\u4e00\u7c7b\u7684\u4eba\u3002\u201d\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u672c\u6765\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\uff0c\u76f4\u5230\u6211\u4ed4\u7ec6\u89c2\u5bdf\u8fc7\u4ed6\u4e4b\u540e\u624d\u53d1\u73b0\u7684\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u4f60\u65e9\u5c31\u5e94\u8be5\u77e5\u9053\u4f60\u4eec\u6709\u5f88\u591a\u76f8\u540c\u7684\u5730\u65b9\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u8fde\u6211\u90fd\u65e9\u5c31\u770b\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u90fd\u662f\u4eba\u4e2d\u4e4b\u6770\uff0c\u90fd\u6709\u79f0\u9738\u4e00\u65b9\u7684\u96c4\u5fc3\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u800c\u4e14\u4f60\u4eec\u90fd\u662f\u5b64\u72ec\u7684\u4eba\uff0c\u867d\u7136\u90fd\u80fd\u8ba9\u522b\u4eba\u4e3a\u4f60\u4eec\u53bb\u6b7b\uff0c\u5374\u8fde\u4e00\u4e2a\u670b\u53cb\u4e5f\u6ca1\u6709\u3002\u56e0\u4e3a\u4f60\u4eec\u4ece\u6765\u90fd\u6ca1\u6709\u4fe1\u4efb\u8fc7\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u5415\u4e09\u6de1\u6de1\u5730\u7b11\u4e86\u7b11\uff1a \u3000\u3000\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u80fd\u6d3b\u5230\u73b0\u5728\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u4e5f\u6de1\u6de1\u5730\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6240\u4ee5\u4f60\u4eec\u867d\u7136\u6d3b\u7740\uff0c\u867d\u7136\u62e5\u6709\u4e00\u5207\uff0c\u53ef\u662f\u6d3b\u5f97\u5e76\u4e0d\u5feb\u4e50\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u5415\u4e09\u76ef\u7740\u5979\uff1a \u3000\u3000\u201c\u96be\u9053\u4f60\u4e0d\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u907f\u5f00\u4e86\u8fd9\u95ee\u9898\uff0c\u53cd\u95ee\u5415\u4e09\uff1a \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u89c2\u5bdf\u4ed6\u5f88\u4e45\uff0c\u800c\u4e14\u89c2\u5bdf\u5f97\u5f88\u4ed4\u7ec6\uff0c\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u4e09\u4e5f\u6ca1\u56de\u7b54\u5979\u8fd9\u4e2a\u95ee\u9898\uff0c\u4e5f\u53cd\u95ee\u5979\uff1a \u3000\u3000\u201c\u5982\u679c\u4e00\u4e2a\u4eba\u7ec8\u5e74\u6d41\u6d6a\u5728\u90a3\u4e00\u7247\u65e0\u60c5\u7684\u5927\u6f20\u4e0a\uff0c\u6ca1\u6709\u6c34\uff0c\u4e5f\u6ca1\u6709\u540c\u4f34\u3002\u201d \u3000\u3000\u5415\u4e09\u95ee\uff1a \u3000\u3000\u201c\u4f60\u60f3\u4ed6\u5e94\u8be5\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u5f88\u5b64\u50fb\u7684\u4eba\uff0c\u5c31\u50cf\u662f\u91ce\u517d\u4e00\u6837\uff0c\u770b\u8d77\u6765\u4e00\u5b9a\u5f88\u7626\u5f88\u810f\u3002\u201d \u3000\u3000\u8c01\u90fd\u4f1a\u8fd9\u4e48\u60f3\u7684\u3002 \u3000\u3000\u7cae\u98df\u7684\u7f3a\u4e4f\uff0c\u65e0\u7591\u4f1a\u4f7f\u4eba\u7626\u5f31\uff0c\u8fde\u996e\u7528\u7684\u6c34\u90fd\u89c6\u5982\u73cd\u5b9d\uff0c\u5f53\u7136\u4f1a\u4f7f\u4eba\u810f\u3002 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u770b\u8d77\u6765\u662f\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u4ed6\u770b\u8d77\u6765\u7edd\u5bf9\u4e0d\u662f\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u770b\u8d77\u6765\u82f1\u4fca\u96c4\u4f1f\u800c\u5065\u5eb7\uff0c\u7edd\u5bf9\u6ca1\u6709\u4e00\u70b9\u8425\u517b\u4e0d\u826f\u7684\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u8863\u670d\u6c38\u8fdc\u90fd\u4fdd\u6301\u5149\u6d01\u7b14\u633a\uff0c\u5c31\u8fde\u4eac\u57ce\u91cc\u6700\u8bb2\u7a76\u7a7f\u7740\u7684\u4eba\uff0c\u90fd\u672a\u5fc5\u80fd\u6bd4\u5f97\u4e0a\u4ed6\u3002 \u3000\u3000\u751a\u81f3\u8fde\u5934\u53d1\u548c\u6307\u7532\u90fd\u80fd\u4fee\u5f97\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u201c\u8fd8\u6709\u6700\u5947\u602a\u7684\u4e00\u70b9\uff01\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u521a\u624d\u4f60\u8bf4\u5f97\u4e0d\u9519\u3002\u201d\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u5982\u679c\u7ec8\u5e74\u5355\u72ec\u6d41\u6d6a\uff0c\u4ed6\u7684\u884c\u4e3a\u4e3e\u52a8\u770b\u8d77\u6765\u5c31\u96be\u514d\u4f1a\u548c\u91ce\u517d\u4e00\u6837\uff0c\u53d8\u5f97\u6563\u6f2b\u800c\u7c97\u91ce\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u73ed\u5bdf\u5df4\u90a3\u5374\u4e0d\u540c\u3002\u201d\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u521a\u624d\u6211\u4ed4\u7ec6\u89c2\u5bdf\u4e86\u4ed6\u5f88\u4e45\uff0c\u53d1\u73b0\u4ed6\u7684\u4e00\u4e3e\u4e00\u52a8\u90fd\u6781\u6709\u8282\u5236\uff0c\u8fde\u4e00\u70b9\u5c0f\u8282\u90fd\u4e0d\u758f\u5ffd\uff0c\u5c31\u7b97\u662f\u6700\u6709\u6559\u517b\u7684\u4e16\u5bb6\u5b50\uff0c\u5728\u5403\u996d\u7684\u65f6\u5019\u4e5f\u4e0d\u4f1a\u6bd4\u4ed6\u66f4\u6709\u793c\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u53f9\u4e86\u53e3\u6c14\uff1a \u3000\u3000\u201c\u4f60\u770b\u51fa\u6765\u7684\u4e8b\u5012\u771f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u4e8b\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u4e5f\u770b\u51fa\u6765\u4e86\u3002\u4f60\u4e5f\u4e0d\u5fc5\u5426\u8ba4\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u6ca1\u6709\u5426\u8ba4\uff0c\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u53ea\u95ee\u4f60\uff0c\u201d\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u4ece\u8fd9\u4e9b\u5c0f\u4e8b\u4e0a\u9762\uff0c\u4f60\u80fd\u4e0d\u80fd\u770b\u51fa\u73ed\u5bdf\u5df4\u90a3\u7684\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d\u9f50\u5c0f\u71d5\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\uff1a \u3000\u3000\u201c\u4ece\u8fd9\u4e9b\u4e8b\u4e0a\u80fd\u770b\u51fa\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u5415\u4e09\u76ef\u7740\u5979\uff0c\u76ef\u7740\u5979\u770b\u4e86\u5f88\u4e45\uff0c\u4eff\u4f5b\u4e5f\u60f3\u770b\u770b\u5979\u662f\u4e0d\u662f\u5728\u8bf4\u8c0e\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e5f\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5bf9\u8fd9\u4e00\u70b9\u4ed6\u663e\u7136\u89c9\u5f97\u5f88\u4e0d\u6ee1\u610f\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u7ee7\u7eed\u8bf4\uff1a \u3000\u3000\u201c\u4ed6\u7684\u8863\u7740\u6574\u6d01\uff0c\u8eab\u4f53\u5065\u5eb7\uff0c\u8868\u793a\u4ed6\u867d\u7136\u7ecf\u5e74\u6d41\u6d6a\u5728\u6c99\u6f20\u91cc\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u7f3a\u4e4f\u8fc7\u7cae\u98df\u548c\u6c34\u3002\u201d \u3000\u3000\u2014\u2014\u5728\u90a3\u4e00\u7247\u65e0\u60c5\u7684\u5927\u5730\u4e0a\uff0c\u73ed\u5bdf\u5df4\u90a3\u600e\u4e48\u80fd\u5f97\u5230\u5145\u8db3\u7684\u7cae\u98df\u548c\u6c34\uff1f \u3000\u3000\u8fd9\u65e0\u7591\u662f\u4ef6\u602a\u4e8b\uff0c\u9f50\u5c0f\u71d5\u5374\u6ca1\u6709\u95ee\uff0c\u53ea\u662f\u9759\u9759\u5730\u542c\u5415\u4e09\u63a5\u7740\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u7684\u884c\u4e3a\u4e3e\u52a8\u90fd\u6781\u6709\u8282\u5236\uff0c\u770b\u6765\u4e0d\u4f46\u5f6c\u5f6c\u6709\u793c\uff0c\u800c\u4e14\u5f88\u6709\u5a01\u4e25\u3002\u201d \u3000\u3000\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5c31\u8868\u793a\u4ed6\u5e76\u4e0d\u662f\u50cf\u522b\u4eba\u60f3\u50cf\u4e2d\u90a3\u4e48\u5bc2\u5bde\u5b64\u72ec\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u522b\u7684\u4eba\u90fd\u4ee5\u4e3a\u4ed6\u662f\u4e00\u4e2a\u4eba\u50cf\u4e00\u5339\u91ce\u72fc\u822c\u5728\u6d41\u6d6a\u65f6\uff0c\u4ed6\u8bf4\u4e0d\u5b9a\u6b63\u548c\u53e6\u5916\u4e00\u4e9b\u4eba\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u95ee\uff1a \u3000\u3000\u201c\u53e6\u5916\u4e00\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e9b\u4f69\u670d\u4ed6\uff0c\u4f9d\u9760\u4ed6\uff0c\u968f\u65f6\u90fd\u613f\u610f\u4e3a\u4ed6\u53bb\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u4ed6\u8ddf\u8fd9\u4e9b\u4eba\u5728\u4e00\u8d77\uff0c\u6240\u4ee5\u4ed6\u7684\u4e00\u4e3e\u4e00\u52a8\u90fd\u5fc5\u987b\u8282\u5236\u3002\u201d\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e00\u5b9a\u8981\u4ee5\u81ea\u5df1\u7684\u884c\u4e3a\u4f5c\u8fd9\u4e9b\u4eba\u7684\u8868\u7387\u3002\u201d \u3000\u3000\u201c\u8fd9\u53c8\u8868\u793a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8868\u793a\u4ed6\u5728\u6c99\u6f20\u4e2d\u4e00\u5b9a\u8fd8\u6709\u4e2a\u79d8\u5bc6\u7684\u85cf\u8eab\u4e4b\u5730\u3002\u201d\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u6c99\u6f20\u4e2d\u7684\u5730\u52bf\u60c5\u51b5\uff0c\u5929\u4e0b\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u6bd4\u4ed6\u66f4\u719f\u6089\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u627e\u5230\u90a3\u4e48\u6837\u4e00\u4e2a\u5730\u65b9\uff0c\u4e5f\u53ea\u6709\u4ed6\u77e5\u9053\u8fd9\u79d8\u5bc6\u3002\u201d \u3000\u3000\u201c\u8fde\u535c\u9e70\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u535c\u9e70\u5f53\u7136\u4e0d\u77e5\u9053\u3002\u201d\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u4ed6\u5229\u7528\u90a3\u5730\u65b9\uff0c\u8bad\u7ec3\u4e86\u4e00\u6279\u968f\u65f6\u90fd\u80af\u4e3a\u4ed6\u53bb\u6b7b\u7684\u4eba\uff0c\u535c\u9e70\u5c31\u662f\u6b7b\u5728\u90a3\u4e9b\u4eba\u624b\u91cc\u7684\u3002\u201d \u3000\u3000\u4ed6\u62ac\u5934\u3002 \u3000\u3000\u201c\u73b0\u5728\u4ed6\u4e00\u5b9a\u4e5f\u540c\u6837\u60f3\u8981\u6211\u6b7b\u5728\u90a3\u4e9b\u4eba\u7684\u624b\u91cc\u3002\u201d \u3000\u3000\u6709\u79cd\u4eba\u7684\u611f\u89e6\u60c5\u7eea\u548c\u60f3\u6cd5\uff0c\u597d\u50cf\u65f6\u65f6\u523b\u523b\u5206\u5206\u79d2\u79d2\u90fd\u4f1a\u6539\u53d8\u7684\u3002 \u3000\u3000\u5415\u4e09\u65e0\u7591\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u7b11\u4e86\uff0c\u771f\u7b11\u4e86\u3002 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u867d\u7136\u65f6\u65f6\u523b\u523b\u5206\u5206\u79d2\u79d2\u90fd\u60f3\u6740\u6211\uff0c\u53ef\u662f\u6211\u5e76\u4e0d\u6068\u4ed6\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u4e5f\u60f3\u6740\u4ed6\uff0c\u65f6\u65f6\u523b\u523b\u5206\u5206\u79d2\u79d2\u90fd\u60f3\u6740\u4ed6\u3002\u201d \u3000\u3000\u5415\u4e09\u7b11\u5f97\u4eff\u4f5b\u5f88\u6109\u5feb\uff1a \u3000\u3000\u201c\u4ed6\u60f3\u6740\u6211\uff0c\u6211\u4e5f\u60f3\u6740\u4ed6\uff0c\u4f46\u662f\u6211\u4eec\u4e4b\u95f4\u5e76\u6ca1\u6709\u4ec7\u6068\u3002\u6211\u4e0d\u6068\u4ed6\uff0c\u4ed6\u4e5f\u672a\u5fc5\u6068\u6211\u3002\u201d \u3000\u3000\u6740\u4eba\u672c\u6765\u5c31\u4e0d\u4e00\u5b9a\u662f\u56e0\u4e3a\u4ec7\u6068\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u4e86\u89e3\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u6068\u7684\u4e0d\u662f\u73ed\u5bdf\u5df4\u90a3\uff0c\u4f60\u6068\u7684\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u6068\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u5c0f\u65b9\uff01\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u4e0d\u4f46\u4f60\u6068\u4ed6\uff0c\u72ec\u5b64\u75f4\u4e5f\u6068\u4ed6\uff0c\u751a\u81f3\u8fde\u73ed\u5bdf\u5df4\u90a3\u8bf4\u4e0d\u5b9a\u90fd\u5728\u6068\u4ed6\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u4eec\u90fd\u77e5\u9053\u53e6\u5916\u6709\u4e9b\u4eba\u90fd\u975e\u5e38\u559c\u6b22\u4ed6\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u5927\u5bb6\u90fd\u77e5\u9053\uff0c\u53ef\u601c\u7684\u4eba\u5fc5\u6709\u53ef\u6068\u4e4b\u5904\uff0c\u4ece\u53e6\u4e00\u65b9\u9762\u6765\u8bf4\uff0c\u53ef\u7231\u7684\u4eba\u4e5f\u4e00\u5b9a\u4f1a\u6709\u5f88\u591a\u4eba\u6068\u4ed6\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u5f53\u7136\u4e5f\u4e86\u89e3\u8fd9\u9053\u7406\uff0c\u7231\u4e0e\u6068\u4e4b\u95f4\u7684\u5dee\u522b\u672c\u6765\u5c31\u5f88\u5fae\u5999\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8138\u4e0a\u7684\u7b11\u5bb9\u5ffd\u7136\u95f4\u5c31\u6d88\u5931\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u6068\u7684\u662f\u5c0f\u65b9\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u5f53\u7136\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u54fc\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u8fd9\u4e00\u6b21\u73ed\u5bdf\u5df4\u90a3\u4e0b\u4ee4\u53d1\u52a8\u653b\u51fb\uff0c\u4e00\u5b9a\u8981\u4f60\u77e5\u9053\u4ed6\u4e00\u5b9a\u4f1a\u4ee5\u5c0f\u65b9\u4e3a\u653b\u51fb\u7684\u4e3b\u529b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5e7f \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5c31\u7b97\u4ed6\u660e\u77e5\u4ed6\u8fd9\u6b21\u653b\u51fb\u7684\u76ee\u7684\u662f\u4e3a\u4e86\u8981\u627e\u4f60\u7684\u4e0b\u843d\uff0c\u4f60\u4e5f\u540c\u6837\u4f1a\u4e0a\u5f53\u7684\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u4e5f\u540c\u6837\u60f3\u5229\u7528\u8fd9\u6b21\u673a\u4f1a\u5c06\u5c0f\u65b9\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u5979\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u6240\u4ee5\u8fd9\u4e00\u6b21\u5c0f\u65b9\u5df2\u7ecf\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u662f\u4e2a\u975e\u5e38\u8c28\u614e\u4ed4\u7ec6\u7684\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5982\u679c\u80fd\u4ece\u767d\u624b\u8d77\u5bb6\uff0c\u800c\u53d8\u4e3a\u5bcc\u53ef\u654c\u56fd\uff0c\u90a3\u4e48\u4ed6\u901a\u5e38\u90fd\u4f1a\u662f\u4e2a\u975e\u5e38\u8c28\u614e\u4ed4\u7ec6\u7684\u4eba\u3002 \u3000\u3000\u5bf9\u8eab\u65c1\u7684\u6bcf\u4e2a\u4eba\u6bcf\u6837\u4e8b\u90fd\u4f1a\u89c2\u5bdf\u5f97\u975e\u5e38\u4ed4\u7ec6\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u5374\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u6ce8\u610f\u9f50\u5c0f\u71d5\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u53cd\u5e94\uff0c\u597d\u50cf\u4e5f\u5b8c\u5168\u4e0d\u77e5\u9053\u5979\u548c\u5c0f\u65b9\u4e4b\u95f4\u7684\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u53ea\u4e0d\u8fc7\u5ffd\u7136\u6539\u53d8\u4e86\u8bdd\u9898\u3002 \u3000\u3000\u201c\u73b0\u5728\u5c0f\u65b9\u548c\u73ed\u5bdf\u5df4\u90a3\u662f\u4e0d\u662f\u5df2\u7ecf\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6709\u6ca1\u6709\u6740\u5415\u606d\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u628a\u5415\u606d\u5e26\u8d70\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u6447\u5934\uff1a \u3000\u3000\u201c\u6211\u672c\u6765\u4e5f\u4ee5\u4e3a\u73ed\u5bdf\u5df4\u90a3\u4f1a\u628a\u5415\u606d\u5e26\u8d70\uff0c\u56e0\u4e3a\u5415\u606d\u4ee5\u540e\u5f88\u53ef\u80fd\u8fd8\u6709\u7528\uff0c\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u6ca1\u6709\u8fd9\u4e48\u505a\u3002\u201d \u3000\u3000\u5415\u4e09\u5fae\u7b11\u3002 \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u8fd9\u79cd\u4eba\u505a\u4e8b\uff0c\u901a\u5e38\u90fd\u662f\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u5df2\u7ecf\u60f3\u5230\u4e86\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u4ed6\u505a\u7684\u4e8b\u53ea\u6709\u4f60\u80fd\u60f3\u5f97\u5230\u3002\u201d \u3000\u3000\u5415\u4e09\u7b11\u5f97\u66f4\u795e\u79d8\uff0c\u66f4\u6109\u5feb\uff0c\u4e5f\u66f4\u66a7\u6627\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u9f50\u5c0f\u71d5\uff1a \u3000\u3000\u201c\u6211\u505a\u7684\u4e8b\u4f60\u731c\u4ed6\u662f\u4e0d\u662f\u4e5f\u80fd\u60f3\u5f97\u5230\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u9189\u3002 \u3000\u3000\u4ed6\u5e73\u5e38\u5f88\u5c11\u559d\u9152\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u770b\u8fc7\u4ed6\u559d\u9152\uff0c\u4eca\u5929\u4ed6\u559d\u7684\u9152\u5374\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u591a\u5f97\u591a\uff0c\u5927\u591a\u6570\u4eba\u90fd\u4f1a\u8ba4\u4e3a\u4ed6\u4e00\u5b9a\u4f1a\u9189\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6ca1\u6709\u9189\u3002 \u3000\u3000\u4ed6\u6e05\u9192\u5f97\u5c31\u50cf\u662f\u4e2a\u521a\u4ece\u6811\u4e0a\u6458\u4e0b\u6765\u7684\u786c\u58f3\u679c\u3002 \u3000\u3000\u5c0f\u65b9\u5c31\u6ca1\u6709\u4ed6\u8fd9\u6837\u6e05\u9192\u4e86\uff0c\u5728\u5fae\u9187\u4e2d\u8fd8\u5e26\u7740\u51e0\u5206\u5fe7\u90c1\u3002 \u3000\u3000\u4ed6\u4eec\u8d70\u5728\u4e00\u6761\u5f88\u5e7d\u9759\u5730\u5c71\u5761\u5c0f\u8def\u4e0a\uff0c\u98ce\u4e2d\u5145\u6ee1\u4e86\u9752\u5c71\u7684\u82ac\u82b3\u548c\u5e72\u8349\u7684\u9999\u6c14\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5ffd\u7136\u95ee\u4e86\u5c0f\u65b9\u4e00\u4e2a\u5f88\u7edd\u7684\u95ee\u9898\u3002 \u3000\u3000\u201c\u5415\u4e09\u662f\u4e0d\u662f\u6761\u732a\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\u3002\u201d\u5c0f\u65b9\u8bf4\uff1a \u3000\u3000\u201c\u4ed6\u6bd4\u9b3c\u90fd\u7cbe\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5e73\u767d\u65e0\u6545\u5730\u8d39\u90a3\u4e48\u5927\u7684\u4e8b\uff0c\u8ba9\u6211\u4eec\u5927\u5403\u4e00\u987f\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u672c\u6765\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a \u3000\u3000\u201c\u4f46\u662f\u73b0\u5728\u6211\u5df2\u7ecf\u60f3\u901a\u4e86\uff0c\u4ed6\u628a\u6211\u4eec\u7559\u5728\u90a3\u91cc\uff0c\u4e00\u5b9a\u662f\u56e0\u4e3a\u4ed6\u8981\u597d\u597d\u5730\u770b\u770b\u6211\u3002\u770b\u770b\u6211\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u80fd\u770b\u5f97\u5230\u4f60\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u867d\u7136\u770b\u4e0d\u89c1\u4ed6\uff0c\u53ef\u662f\u6211\u76f8\u4fe1\u4ed6\u4e00\u5b9a\u80fd\u770b\u5f97\u89c1\u6211\u4eec\u3002\u201d\u73ed\u5bdf\u5df2\u90a3\u8bf4\uff1a \u3000\u3000\u201c\u8eb2\u5728\u4e00\u4e2a\u5f88\u8fdc\u7684\u5730\u65b9\uff0c\u5077\u5077\u5730\u770b\u3002\u800c\u4e14\u4e0d\u662f\u7528\u4ed6\u7684\u773c\u775b\u770b\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u773c\u775b\u770b\u7528\u4ec0\u4e48\u770b\u53f6 \u3000\u3000\u201c\u7528\u4e00\u79cd\u5f88\u7279\u522b\u7684\u955c\u5b50\u3002\u201d \u3000\u3000\u201c\u955c\u5b50\uff1f\u201d \u3000\u3000\u201d\u90a3\u5f53\u7136\u4e0d\u662f\u6211\u4eec\u5e73\u5e38\u7528\u7684\u90a3\u79cd\u955c\u5b50\uff0c\u751a\u81f3\u4e0d\u80fd\u7b97\u662f\u4e2a\u955c\u5b50\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a \u3000\u3000\u201c\u53ef\u662f\u6211\u53ea\u80fd\u8fd9\u4e48\u6837\u8bf4\uff0c\u56e0\u4e3a\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u522b\u7684\u540d\u79f0\u3002\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u65b9\uff1a \u3000\u3000\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u5236\u4f5c\u90a3\u4e9b\u8721\u50cf\u7684\u4eba\uff0c\u662f\u4ece\u4ec0\u4e48\u5730\u65b9\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u4ece\u4e00\u4e2a\u975e\u5e38\u975e\u5e38\u9065\u8fdc\u7684\u56fd\u5ea6\u4e2d\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u6562\u8bf4\u5728\u4e00\u4e2a\u66f4\u9065\u8fdc\u7684\u56fd\u5ea6\u91cc\uff0c\u6709\u4e00\u4e2a\u66f4\u806a\u660e\u66f4\u5947\u7279\u7684\u4eba\uff0c\u5df2\u7ecf\u521b\u9020\u51fa\u4e00\u79cd\u795e\u79d8\u7684\u9b54\u955c\uff0c\u80fd\u591f\u5728\u5f88\u8fdc\u7684\u5730\u65b9\u770b\u5230\u4e00\u4e9b\u522b\u4eba\u770b\u4e0d\u89c1\u7684\u4e8b\uff0c\u5c31\u597d\u50cf\u6211\u4eec\u795e\u8bdd\u4e2d\u7684\u5343\u91cc\u773c\u4e00\u6837\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1b \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u5c31\u662f\u7528\u8fd9\u79cd\u955c\u5b50\u5728\u5077\u5077\u5730\u770b\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u770b\u6211\u4eec\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\u6211\u4eec\u7684\u795e\u6001\uff0c\u770b\u6211\u4eec\u7684\u884c\u52a8\uff0c\u770b\u6211\u4eec\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u56e0\u4e3a\u77e5\u5df1\u77e5\u5f7c\uff0c\u624d\u80fd\u767e\u6218\u767e\u80dc\uff0c\u4ed6\u4e00\u5b9a\u5df2\u7ecf\u628a\u6211\u4eec\u5f53\u4f5c\u4ed6\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u5c0f\u65b9\uff1a \u3000\u3000\u201c\u5c24\u5176\u662f\u4f60\uff0c\u56e0\u4e3a\u4ed6\u6068\u4f60\uff01\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u3002 \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u4ed6\u6068\u4f60\uff0c\u4e00\u5b9a\u8981\u4eb2\u624b\u6740\u4f60\uff0c\u6240\u4ee5\u4ed6\u8fd9\u6b21\u4e00\u5b9a\u4f1a\u4e2d\u6211\u4eec\u7684\u8ba1\uff0c\u4e00\u5b9a\u4f1a\u66b4\u9732\u4ed6\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u4ec7\u6068\u5f80\u5f80\u4f1a\u8ba9\u4eba\u9020\u6210\u4e00\u4e9b\u4e0d\u53ef\u539f\u8c05\u7684\u758f\u5ffd\u548c\u9519\u8bef\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u5415\u4e09\u4e0d\u662f\u732a\uff0c\u4ed6\u6bd4\u9b3c\u90fd\u7cbe\uff0c\u6211\u4eec\u6545\u610f\u5ba3\u5e03\u8981\u5168\u9762\u53d1\u52a8\u7684\u547d\u4ee4\uff0c\u4ed6\u5e94\u8be5\u60f3\u5f97\u5230\u6211\u4eec\u662f\u8981\u5229\u7528\u8fd9\u6cd5\u5b50\u627e\u51fa\u4ed6\u7684\u884c\u8e2a\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u8fde\u4f60\u6211\u90fd\u5e94\u8be5\u80fd\u60f3\u5f97\u5230\u3002\u201d \u3000\u3000\u5c0f\u65b9\u627f\u8ba4\u3002 \u3000\u3000\u201c\u4f46\u4ed6\u5374\u8fd8\u662f\u4e00\u6837\u4f1a\u4e2d\u8ba1\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1b \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e5f\u60f3\u5c06\u8ba1\u5c31\u8ba1\uff0c\u5229\u7528\u8fd9\u4e2a\u673a\u4f1a\u4eb2\u624b\u6740\u4f60\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u4e00\u5b9a\u4f1a\u5c06\u624b\u4e0b\u7684\u7cbe\u9510\u5168\u90e8\u8c03\u96c6\u5230\u90a3\u91cc\u53bb\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1b \u3000\u3000\u201c\u4ed6\u60f3\u4ee5\u9038\u5f85\u52b3\uff0c\u628a\u6211\u4eec\u4e00\u7f51\u6253\u5c3d\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u4e5f\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u53ea\u53ef\u60dc\u4ed6\u5bf9\u4f60\u6068\u5f97\u592a\u6df1\uff0c\u6240\u4ee5\u96be\u514d\u8ba1\u7b97\u9519\u8bef\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a \u3000\u3000\u201c\u4ed6\u81f3\u5c11\u7b97\u9519\u4e86\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u54ea\u4e24\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u4f4e\u4f30\u6211\u4eec\u7684\u5b9e\u529b\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\u5f97\u6781\u6709\u628a\u63e1\uff1a \u3000\u3000\u201c\u8fd9\u51e0\u5e74\u6765\u6211\u7cbe\u5fc3\u8bad\u7ec3\u51fa\u7684\u4eba\uff0c\u8fdc\u6bd4\u4ed6\u60f3\u50cf\u4e2d\u5389\u5bb3\u5f97\u591a\u3002\u5982\u679c\u6211\u4eec\u503e\u5de2\u800c\u51fa\uff0c\u548c\u4ed6\u7684\u5c5e\u4e0b\u653e\u624b\u4e00\u640f\uff0c\u6211\u4eec\u5360\u7684\u80dc\u7b97\u8fdc\u6bd4\u4ed6\u4eec\u591a\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u8ba4\u4e3a\u6211\u4e5f\u4f1a\u53bb\u7684\uff0c\u4f46\u662f\u6211\u4e0d\u4f1a\u53bb\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4eec\u5df2\u80dc\u7b97\u5728\u63e1\uff0c\u6211\u6b63\u597d\u4e58\u4ed6\u96c6\u4e2d\u529b\u91cf\u6765\u5bf9\u4ed8\u4f60\u7684\u65f6\u5019\u53bb\u505a\u4e00\u4e9b\u522b\u7684\u4e8b\uff0c\u8ba9\u4ed6\u6218\u8d25\u4e4b\u540e\u8fde\u9000\u8def\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u771f\u7684\u8ba4\u4e3a\u8fd9\u4e00\u6b21\u6211\u4eec\u5df2\u7ecf\u80dc\u7b97\u5728\u63e1\uff1f\u201d\u5c0f\u65b9\u95ee\uff1a \u3000\u3000\u201c\u96be\u9053\u4f60\u5fd8\u4e86\u72ec\u5b64\u75f4\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53cd\u95f4\u5c0f\u65b9\uff1a \u3000\u3000\u201c\u96be\u9053\u4f60\u771f\u76f8\u4fe1\u5415\u4e09\u7684\u8bdd\uff0c\u771f\u7684\u8ba4\u4e3a\u9f50\u5c0f\u71d5\u548c\u72ec\u5b64\u75f4\u90fd\u5df2\u7ecf\u6295\u5954\u4ed6\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u53c8\u95ee\uff1a \u3000\u3000\u201c\u5415\u606d\u662f\u8ddf\u968f\u4ed6\u591a\u5e74\u7684\u5974\u4ec6\uff0c\u4e3a\u4ec0\u4e48\u8981\u628a\u4ed6\u7684\u79d8\u5bc6\u544a\u8bc9\u6211\u4eec\uff1f\u6211\u4eec\u5bf9\u5415\u606d\u6709\u8fc7\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u5c0f\u65b9\u6c89\u9ed8\u4e86\u3002 \u3000\u3000\u201c\u672c\u6765\u6211\u4e5f\u66fe\u7ecf\u60f3\u5230\u8fc7\uff0c\u72ec\u5b64\u75f4\u5f88\u53ef\u80fd\u53c8\u5df2\u7ecf\u6295\u9760\u4ed6\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u6211\u542c\u5415\u606d\u8fd9\u4e48\u6837\u8bf4\u4e86\u4e4b\u540e\uff0c\u6211\u53cd\u800c\u4e0d\u8fd9\u4e48\u60f3\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\uff1a \u3000\u3000\u201c\u6240\u4ee5\u6211\u7b97\u8ba1\u4f60\u8fd9\u6b21\u4e00\u5b9a\u4f1a\u6210\u529f\u7684\uff0c\u6240\u4ee5\u5415\u4e09\u8fd9\u6b21\u5df2\u662f\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u521a\u8d70\u5230\u4e00\u4e2a\u4e09\u53c9\u8def\u53e3\uff0c\u5ffd\u7136\u6709\u8e44\u58f0\u54cd\u8d77\uff0c\u4e00\u5339\u5feb\u9a6c\u81ea\u659c\u8def\u4e0a\u6025\u9a70\u800c\u6765\u3002 \u3000\u3000\u8fdc\u5728\u6570\u4e08\u5916\uff0c\u9a6c\u4e0a\u7684\u9752\u8863\u9a91\u58eb\u5c31\u5df2\u98de\u8eab\u4e0b\u9a6c\u3002 \u3000\u3000\u4e45\u7ecf\u8bad\u7ec3\u7684\u5feb\u9a6c\u9aa4\u7136\u505c\u4e0b\uff0c\u4e45\u7ecf\u8bad\u7ec3\u7684\u9a91\u58eb\u5df2\u62dc\u5012\u5728\u73ed\u5bdf\u5df4\u90a3\u9762\u524d\uff0c\u53cc\u624b\u5949\u4e0a\u4e00\u4e2a\u7eb8\u5377\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u8eab\u624b\u884c\u52a8\u6781\u77eb\u5065\uff0c\u770b\u6765\u5374\u5f88\u80a5\u80d6\u3002 \u3000\u3000\u5c0f\u65b9\u4eff\u4f5b\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u53c8\u597d\u50cf\u6ca1\u6709\u89c1\u8fc7\uff0c\u7b49\u5230\u4ed6\u62ac\u8d77\u5934\u6765\u65f6\uff0c\u5c0f\u65b9\u624d\u60f3\u8d77\u4ed6\u5c31\u662f\u90a3\u5929\u5728\u90a3\u6761\u70ed\u95f9\u7684\u957f\u8857\u4e0a\uff0c\u7528\u6700\u6709\u6548\u7684\u624b\u6cd5\u627c\u6740\u5241\u7f0e\u5e84\u4f19\u8ba1\u7684\u80a5\u80d6\u5987\u4eba\u3002\u53ea\u4e0d\u8fc7\u5979\u4eca\u5929\u7a7f\u7684\u662f\u7537\u88c5\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5f53\u7136\u4e5f\u5c31\u662f\u73ed\u5bdf\u5df4\u90a3\u8fd1\u5e74\u6765\u7cbe\u5fc3\u8bad\u7ec3\u51fa\u6765\u7684\u6740\u624b\u4e4b\u4e00\u3002 \u3000\u3000\u5979\u5e26\u6765\u7684\u7eb8\u5377\u5c31\u8ddf\u73ed\u5bdf\u5df4\u90a3\u7ed9\u5c0f\u65b9\u770b\u8fc7\u7684\u90a3\u7eb8\u7b80\u56fe\u4e00\u6837\uff0c\u4e0a\u9762\u5212\u7740\u5415\u4e09\u6240\u6709\u7684\u79d8\u5bc6\u5de2\u7a74\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u5f20\u56fe\u4e0a\u7528\u6731\u7802\u7279\u522b\u56f5\u51fa\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u8fd8\u7528\u6731\u7802\u5212\u51fa\u4e86\u5f88\u591a\u7bad\u5934\u3002 \u3000\u3000\u6240\u6709\u7684\u7bad\u5934\u90fd\u6307\u5411\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u2014\u2014\u5728\u56fe\u4e0a\u7684\u4e00\u70b9\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u5e02\u96c6\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u4e00\u6761\u6cb3\uff0c\u4e00\u7247\u4e1b\u6797\uff0c\u4e00\u9053\u5c71\u8109\u3002 \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u5c55\u5f00\u7eb8\u5377\uff1a \u3000\u3000\u201c\u5415\u4e09\u662f\u4e0d\u662f\u5df2\u7ecf\u5c06\u4ed6\u5c5e\u4e0b\u6240\u6709\u7684\u7cbe\u9510\u5168\u90e8\u8c03\u96c6\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u56de\u7b54\u662f\u7edd\u5bf9\u80af\u5b9a\u7684\uff01 \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u7acb\u523b\u4e0b\u4ee4\u3002 \u3000\u3000\u201c\u90a3\u4e48\u6211\u4eec\u7684\u4eba\u4e00\u5b9a\u4e5f\u8981\u5728\u540e\u5929\u5b50\u65f6\u524d\u8d76\u5230\u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u201c\u5b50\u65f6\u524d\u4f60\u4eec\u4e00\u5b9a\u8981\u5728\u9547\u5916\u90a3\u7247\u67a3\u6797\u91cc\u96c6\u5408\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a \u3000\u3000\u201c\u7f3a\u4e00\u4e2a\u4eba\uff0c\u6211\u5c31\u53d6\u4f60\u8eab\u4e0a\u4e00\u6837\u4e1c\u897f\u3002\u4e5f\u8bb8\u662f\u773c\uff0c\u4e5f\u8bb8\u662f\u9f3b\uff0c\u4e5f\u8bb8\u662f\u624b\uff0c\u4e5f\u8bb8\u662f\u811a\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u4e5f\u8bb8\u5c31\u662f\u4f60\u7684\u5934\u9885\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u63a5\u5230\u73ed\u5bdf\u5df4\u90a3\u7684\u547d\u4ee4\u540e\uff0c\u8fd9\u4e2a\u4eba\u7acb\u523b\u53c8\u98de\u8eab\u4e0a\u9a6c\uff0c\u626c\u97ad\u6025\u9a70\u800c\u53bb\u3002 \u3000\u3000\u5c0f\u65b9\u5f53\u7136\u8981\u95ee\uff1a \u3000\u3000\u201c\u90a3\u4e2a\u5730\u65b9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u5f88\u70ed\u95f9\u7684\u5c0f\u9547\uff0c\u53eb\u2018\u80e1\u96c6\u2019\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a \u3000\u3000\u201c\u540e\u5929\u7684\u5b50\u65f6\u524d\uff0c\u4f60\u4e00\u5b9a\u4e5f\u8981\u8d76\u5230\u90a3\u91cc\u53bb\uff0c\u5426\u5219\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u5426\u5219\u4f60\u662f\u4e0d\u662f\u4e5f\u8981\u53d6\u6211\u8eab\u4e0a\u4e00\u6837\u4e1c\u897f\uff1f\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u6447\u5934\uff1a \u3000\u3000\u201c\u5982\u679c\u4f60\u4e0d\u53bb\uff0c\u6050\u6015\u6211\u5c31\u8981\u53d6\u4e0b\u6211\u8eab\u4e0a\u4e00\u6837\u4e1c\u897f\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u73ed\u5bdf\u5df4\u90a3\u82e6\u7b11\uff1a \u3000\u3000\u201c\u90a3\u6837\u4e1c\u897f\u4e5f\u8bb8\u5c31\u662f\u6211\u7684\u5934\u9885\u3002\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u56db\u4e8c\u7ae0\u3000\u795e\u79d8\u7684\u901a\u9053 \u7b2c\u56db\u5341\u4e8c\u7ae0\u3000\u795e\u79d8\u7684\u901a\u9053 \u3000\u3000\u5929\u8272\u8fd8\u6ca1\u6709\u6697\uff0c\u53ef\u662f\u8fd9\u7b80\u964b\u7684\u6728\u5c4b\u91cc\u5df2\u7ecf\u663e\u5f97\u5f88\u6697\u3002 \u3000\u3000\u5415\u4e09\u5750\u5728\u9ed1\u6697\u7684\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u6ca1\u6709\u8868\u60c5\u7684\u8138\u4e0a\u9732\u51fa\u6b63\u5728\u6c89\u601d\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u73b0\u5728\u73ed\u5bdf\u5df4\u90a3\u4e00\u5b9a\u5df2\u7ecf\u63a5\u5230\u4e86\u4ed6\u5c5e\u4e0b\u7684\u62a5\u544a\uff0c\u5df2\u7ecf\u77e5\u9053\u6211\u5df2\u7ecf\u5c06\u7cbe\u9510\u5168\u90e8\u8c03\u96c6\u5230\u2018\u80e1\u96c6\u2019\u53bb\u3002\u201d \u3000\u3000\u5415\u4e09\u6162\u6162\u4ed6\u8bf4\uff1b \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u8ba4\u4e3a\u6211\u4e5f\u5728\u2018\u80e1\u96c6\u2019\uff0c\u56e0\u4e3a\u6211\u6015\u5c0f\u65b9\uff0c\u6b63\u597d\u5c06\u8ba1\u5c31\u8ba1\uff0c\u5229\u7528\u8fd9\u6b21\u673a\u4f1a\u4eb2\u81ea\u5c06\u5c0f\u65b9\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff1a \u3000\u3000\u201c\u73ed\u5bdf\u5df4\u90a3\u4e00\u5411\u7b97\u65e0\u9057\u7b56\uff0c\u4f46\u662f\u6211\u4fdd\u8bc1\u4ed6\u8fd9\u6b21\u4e00\u5b9a\u4f1a\u7b97\u9519\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5415\u4e09\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u76f8\u4fe1\u72ec\u5b64\u75f4\u771f\u7684\u5728\u6211\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u72ec\u5b64\u75f4\u771f\u7684\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u4e0d\u7b49\u5415\u4e09\u56de\u7b54\uff0c\u7ee7\u7eed\u53c8\u95ee\uff1a \u3000\u3000\u201c\u4f60\u771f\u7684\u8981\u6211\u5ac1\u7ed9\u4ed6\u3002\uff1f\u201d \u3000\u3000\u201c\u5a5a\u59fb\u662f\u4ef6\u5f88\u5947\u602a\u7684\u4e8b\uff0c\u6709\u65f6\u4e0d\u4ec5\u662f\u7537\u5973\u95f4\u7684\u7ed3\u5408\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u79cd\u624b\u6bb5\u3002\u201d \u3000\u3000\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u8d2b\u7a77\u4eba\u5bb6\u7684\u5b50\u5973\u4ee5\u5a5a\u59fb\u4f5c\u624b\u6bb5\uff0c\u6765\u53d6\u5f97\u4ee5\u540e\u751f\u6d3b\u7684\u4fdd\u969c\uff0c\u5bcc\u8d35\u4eba\u5bb6\u7684\u5b50\u5973\u4e5f\u4f1a\u4ee5\u5a5a\u59fb\u4f5c\u624b\u6bb5\u6765\u589e\u52a0\u81ea\u5df1\u7684\u5730\u4f4d\u548c\u6743\u529b\u3002\u201d \u3000\u3000\u4ed6\u76ef\u7740\u9f50\u5c0f\u71d5\uff0c\u773c\u775b\u91cc\u5e26\u7740\u79cd\u5c16\u9488\u822c\u7684\u7b11\u610f\uff1a \u3000\u3000\u201c\u4f60\u81ea\u5df1\u4e5f\u8be5\u77e5\u9053\uff0c\u6211\u8981\u4f60\u5ac1\u7ed9\u72ec\u5b64\u75f4\uff0c\u5bf9\u4f60\u5bf9\u6211\u90fd\u4e00\u6837\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u4f46\u662f\u6211\u4e00\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u89c1\u5230\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u5415\u4e09\u970d\u7136\u7ad9\u8d77\u6765\uff1a \u3000\u3000\u201c\u597d\uff0c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u7b80\u964b\u7684\u6728\u5c4b\u91cc\u6709\u4e2a\u7b80\u964b\u7684\u6728\u67dc\uff0c\u6253\u5f00\u8fd9\u4e2a\u6728\u67dc\uff0c\u6309\u52a8\u4e00\u4e2a\u79d8\u5bc6\u7684\u94ae\uff0c\u7acb\u523b\u5c31\u4f1a\u73b0\u51fa\u53e6\u4e00\u9053\u95e8\u3002 \u3000\u3000\u8d70\u8fdb\u8fd9\u9053\u79d8\u95e8\uff0c\u5c31\u8d70\u5165\u4e86\u53e6\u5916\u4e00\u4e2a\u4e16\u754c\u3002 \u3000\u3000\u4e00\u4e2a\u8f89\u714c\u5bcc\u4e3d\u7684\u9ec4\u91d1\u4e16\u754c\u3002 \u3000\u3000\u6709\u4e09\u4e2a\u4eba\u5728\u8fd9\u91d1\u5149\u707f\u70c2\u7684\u5c4b\u5b50\u91cc\uff0c\u4e00\u4e2a\u4ecd\u7136\u5e74\u8f7b\uff0c\u4e00\u4e2a\u5e74\u7eaa\u6bd4\u8f83\u5927\u4e9b\uff0c\u4e00\u4e2a\u53cc\u9b13\u6591\u767d\uff0c\u5df2\u8fd1\u4e2d\u5e74\u3002 \u3000\u3000\u5e74\u8f7b\u7684\u8eab\u6750\u4fee\u957f\uff0c\u88c5\u9970\u534e\u4e3d\uff0c\u770b\u6765\u4e0d\u4f46\u975e\u5e38\u82f1\u4fca\uff0c\u800c\u4e14\u975e\u5e38\u9a84\u50b2\u3002 \u3000\u3000\u5e74\u7eaa\u6bd4\u8f83\u5927\u7684\u4e00\u4e2a\u98ce\u5ea6\u7fe9\u7fe9\uff0c\u5f6c\u5f6c\u6709\u793c\uff0c\u65e0\u7591\u662f\u4e2a\u6781\u6709\u6559\u517b\u7684\u4eba \u3000\u3000\u4e24\u9b13\u5df2\u6591\u7684\u4e2d\u5e74\u4eba\uff0c\u5374\u548c\u4f60\u5728\u4efb\u4f55\u4e00\u4e2a\u5e02\u9547\u9053\u8def\u4e0a\u6240\u89c1\u5230\u7684\u4efb\u4f55\u4e00\u4e2a\u4e2d\u5e74\u4eba\u90fd\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u8eab\u6750\u6bd4\u4e00\u822c\u4e2d\u5e74\u4eba\u4fdd\u6301\u5f97\u597d\u4e00\u70b9\uff0c\u8fde\u809a\u5b50\u4e0a\u90fd\u6ca1\u6709\u4e00\u70b9\u591a\u4f59\u7684\u8102\u80aa\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u662f\u7edd\u5bf9\u4e0d\u540c\u7c7b\u578b\u7684\uff0c\u53ea\u4e0d\u8fc7\u6709\u4e00\u70b9\u76f8\u540c\u4e4b\u5904\u3002 \u3000\u3000\u2014\u2014\u4e09\u4e2a\u4eba\u90fd\u6709\u5251\uff0c\u4e09\u4e2a\u4eba\u7684\u4f69\u5251\u90fd\u5728\u4ed6\u4eec\u7684\u624b\u8fb9\uff0c\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u62d4\u51fa\u6765\u3002 \u3000\u3000\u72ec\u5b64\u75f4\u5c45\u7136\u4e0d\u5728\u8fd9\u5c4b\u5b50\u91cc\uff0c\u8fd9\u4e09\u4e2a\u4eba\u9f50\u5c0f\u71d5\u90fd\u6ca1\u6709\u89c1\u8fc7\u3002 \u3000\u3000\u5415\u4e09\u4e3a\u5979\u5f15\u89c1\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\u6211\u7684\u597d\u5e2e\u624b\uff0c\u4e5f\u90fd\u662f\u4e00\u7b49\u7684\u5251\u5ba2\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4 \u3000\u3000\u201c\u53ef\u60dc\u4ed6\u4eec\u5728\u6211\u8fd9\u91cc\u53ea\u6709\u4ee3\u53f7\uff0c\u6ca1\u6709\u540d\u5b57\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7684\u4ee3\u53f7\u662f\uff1a \u3000\u3000\u56db\u53f7\u3001\u5341\u56db\u53f7\u3001\u4e8c\u5341\u56db\u53f7\u3002 \u3000\u3000\u548c\u201c\u4e09\u53f7\u3001\u5341\u4e09\u53f7\u3001\u4e8c\u5341\u4e09\u53f7\u201d\u53ea\u5dee\uff1d\u53f7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u6bcf\u4e00\u4e2a\u4eba\u548c\u5415\u4e09\u6d3e\u53bb\u523a\u6740\u5c0f\u65b9\u7684\u90a3\u4e09\u4e2a\u4eba\u90fd\u5206\u522b\u6709\u5f88\u591a\u76f8\u540c\u4e4b\u5904\uff0c\u4e0d\u4f46\u6027\u683c\u8eab\u4e16\u76f8\u540c\uff0c\u8fde\u5251\u6cd5\u7684\u8def\u5b50\u90fd\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5415\u4e09\u8bf4\uff1b \u3000\u3000\u201c\u6211\u8981\u4ed6\u4eec\u5728\u8fd9\u91cc\u5f85\u547d\uff0c\u53ea\u56e0\u4e3a\u6211\u4e5f\u8981\u4ed6\u4eec\u53bb\u6740\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u9053\uff1a \u3000\u3000\u201c\u6740\u8c01\uff1f\u201d \u3000\u3000\u5415\u4e09\u4e5f\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u4ed6\u53c8\u6309\u52a8\u4e86\u53e6\u4e00\u4e2a\u79d8\u5bc6\u7684\u94ae\uff0c\u5f00\u542f\u4e86\u53e6\u4e00\u4e2a\u79d8\u5bc6\u7684\u95e8\u3002 \u3000\u3000\u95e8\u540e\u662f\u4e00\u6761\u957f\u800c\u9634\u6697\u7684\u901a\u9053\u3002 \u3000\u3000\u201c\u4f60\u4e00\u76f4\u5f80\u524d\u8d70\uff0c\u8d70\u5230\u5c3d\u5934\u5904\uff0c\u4e5f\u6709\u4e00\u9053\u95e8\uff0c\u95e8\u662f\u865a\u63a9\u7740\u7684\uff0c\u6709\u4e2a\u4eba\u5c31\u5750\u5728\u95e8\u540e\uff0c\u53ea\u8981\u4e00\u5f00\u95e8\u5c31\u53ef\u4ee5\u770b\u89c1\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u8981\u4f60\u53bb\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5415\u4e09\u7684\u547d\u4ee4\u76f4\u63a5\u800c\u7b80\u77ed\uff1a \u3000\u3000\u201c\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u56db\u53f7\u4e5f\u548c\u5415\u4e09\u7684\u5c5e\u4e0b\u5176\u4ed6\u90a3\u4e9b\u4eba\u4e00\u6837\uff0c\u53ea\u63a5\u53d7\u547d\u4ee4\uff0c\u4ece\u4e0d\u95ee\u7406\u7531\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u66f4\u4e0d\u4f1a\u95ee\u5415\u4e09\u8981\u4ed6\u53bb\u6740\u7684\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\uff1f \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u53ea\u8bf4\uff1a \u3000\u3000\u201c\u6211\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5c31\u5df2\u7ecf\u50cf\u4e00\u6839\u7bad\u4e00\u6837\u7a9c\u5165\u4e86\u90a3\u6761\u9634\u6697\u7684\u5730\u9053\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u884c\u52a8\u77eb\u5065\u800c\u7075\u654f\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u663e\u5f97\u6709\u4e00\u70b9\u70b9\u6fc0\u52a8\u800c\u5df2\u3002\u8fde\u82cd\u767d\u7684\u8138\u4e0a\u90fd\u5df2\u56e0\u6fc0\u52a8\u800c\u73b0\u51fa\u4e86\u4e00\u70b9\u7ea2\u6655\u3002 \u3000\u3000\u547c\u5438\u597d\u50cf\u53d8\u5f97\u6bd4\u5e73\u5e38\u6025\u4fc3\u4e00\u4e9b\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4eba\u4eec\u6700\u540e\u4e00\u6b21\u770b\u5230\u4ed6\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e00\u7a9c\u5165\u8fd9\u6761\u9634\u6697\u7684\u5730\u9053\uff0c\u4ed6\u5c31\u6ca1\u6709\u56de\u6765\u8fc7\u3002 \u3000\u3000\u73b0\u5728\u6bcf\u4e2a\u4eba\u90fd\u5df2\u7ecf\u77e5\u9053\u4ed6\u4e0d\u4f1a\u6d3b\u7740\u56de\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u53bb\u4e86\u5f88\u4e45\uff0c\u592a\u4e45\u4e86\uff0c\u50cf\u4ed6\u4eec\u8fd9\u6837\u7684\u4eba\uff0c\u65e0\u8bba\u662f\u6740\u4eba\u8fd8\u662f\u88ab\u6740\uff0c\u90fd\u4e0d\u5fc5\u8fd9\u4e48\u4e45\u7684\u3002 \u3000\u3000\u5728\u8fd9\u4e48\u957f\u4e45\u7684\u65f6\u95f4\u91cc\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u90fd\u5df2\u7ecf\u5e94\u8be5\u6709\u4e86\u7ed3\u679c\u3002 \u3000\u3000\u2014\u2014\u6b7b\uff01 \u3000\u3000\u8fd9\u5c31\u662f\u552f\u4e00\u7684\u7ed3\u679c\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u4eba\u7684\u8138\u4e0a\u9732\u51fa\u4e00\u70b9\u5154\u6b7b\u72d0\u60b2\u7684\u4f24\u611f\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u6839\u672c\u5c31\u4e0d\u662f\u4ef6\u503c\u5f97\u60b2\u4f24\u7684\u4e8b\u3002 \u3000\u3000\u2014\u2014\u6bcf\u4e2a\u4eba\u90fd\u8981\u6b7b\u7684\uff0c\u4f55\u51b5\u662f\u4ed6\u4eec\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u2014\u2014\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0c\u201c\u6b7b\u201d\u5c31\u597d\u50cf\u662f\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u4ed6\u4eec\u4e45\u5df2\u538c\u5026\u7684\u5973\u4eba\uff0c\u4e00\u4e2a\u4ed6\u4eec\u867d\u7136\u4e45\u5df2\u538c\u5026\u5374\u53c8\u504f\u504f\u65e0\u6cd5\u820d\u5f03\u7684\u5973\u4eba\uff0c\u6240\u4ee5\u4ed6\u4eec\u5929\u5929\u8981\u7b49\u7740\u5979\u6765\uff0c\u7b49\u5230\u5979\u771f\u7684\u6765\u4e86\u65f6\uff0c\u4ed6\u4eec\u65e2\u4e0d\u4f1a\u89c9\u5f97\u60ca\u5947\uff0c\u66f4\u4e0d\u4f1a\u89c9\u5f97\u5174\u594b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u5979\u8fdf\u65e9\u4e00\u5b9a\u4f1a\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u5bf9\u4e8e\u8fd9\u79cd\u4e8b\uff0c\u4ed6\u4eec\u51e0\u4e4e\u5df2\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u5415\u4e09\u5c45\u7136\u53c8\u7b49\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u662f\u51fa\u4e8e\u4ed6\u5bf9\u4e00\u4e2a\u4eba\u751f\u547d\u7684\u601c\u60af\uff0c\u8fd8\u662f\u56e0\u4e3a\u4ed6\u5bf9\u6b7b\u4ea1\u672c\u8eab\u7684\u754f\u60e7\u548c\u5c0a\u656c\u3002 \u3000\u3000\u5415\u4e09\u7684\u8138\u8272\u8fdc\u6bd4\u9f50\u5c0f\u71d5\u548c\u53e6\u5916\u4e24\u4e2a\u4eba\u90fd\u4e25\u8083\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fd8\u5728\u4e00\u4e2a\u91d1\u76c6\u91cc\u6d17\u4e86\u6d17\u4ed6\u90a3\u53cc\u672c\u6765\u5df2\u7ecf\u975e\u5e38\u6d01\u51c0\u7684\u624b\uff0c\u5728\u4e00\u4e2a\u91d1\u7089\u91cc\u71c3\u4e0a\u4e00\u70b7\u9999\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u8f6c\u5411\u5341\u56db\u53f7\u3002 \u3000\u3000\u201c\u6211\u8981\u505a\u7684\u4e8b\uff0c\u4e00\u5b9a\u8981\u505a\u6210\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u56db\u53f7\u505a\u4e0d\u6210\uff0c\u73b0\u5728\u53ea\u6709\u8ba9\u4f60\u53bb\u505a\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u5341\u56db\u53f7\u7acb\u523b\u63a5\u4e0b\u4e86\u8fd9\u4e2a\u547d\u4ee4\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u5728\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u4e00\u76f4\u63a7\u5236\u5f97\u5f88\u597d\u3002 \u3000\u3000\u53ef\u662f\u5728\u63a5\u4e0b\u4e86\u8fd9\u4e2a\u547d\u4ee4\u4e4b\u540e\uff0c\u4ed6\u7684\u8eab\u4f53\uff0c\u4ed6\u7684\u8138\u8272\uff0c\u8fd8\u662f\u96be\u514d\u56e0\u6fc0\u52a8\u800c\u6709\u4e86\u6539\u53d8\u3002 \u3000\u3000\u4e00\u4e9b\u5f88\u4e0d\u5bb9\u6613\u8ba9\u522b\u4eba\u5bdf\u89c9\u5230\u7684\u6539\u53d8\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u5f00\u59cb\u65f6\u4ed6\u7684\u884c\u52a8\u5f88\u7f13\u6162\uff0c\u8c28\u614e\u800c\u7f13\u6162\u3002 \u3000\u3000\u4ed6\u5148\u5f00\u59cb\u68c0\u67e5\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u2014\u2014\u4ed6\u7684\u8863\u670d\uff0c\u4ed6\u7684\u8170\u5e26\uff0c\u4ed6\u7684\u9774\u5b50\uff0c\u4ed6\u7684\u624b\uff0c\u4ed6\u7684\u5251\u3002 \u3000\u3000\u4ed6\u62d4\u51fa\u4ed6\u7684\u5251\uff0c\u53c8\u653e\u8fdb\u53bb\uff0c\u53c8\u62d4\u51fa\u6765\uff0c\u518d\u653e\u8fdb\u53bb\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u81ea\u5df1\u8ba4\u4e3a\u6bcf\u4e00\u6837\u4e1c\u897f\u90fd\u5b89\u6392\u59a5\u5f53\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u81ea\u5df1\u8ba4\u4e3a\u5df2\u7ecf\u6ee1\u610f\u7684\u65f6\u5019\uff0c\u4ed6\u624d\u7a9c\u5165\u90a3\u6761\u9634\u6697\u7684\u901a\u9053\u3002 \u3000\u3000\u4ed6\u7684\u884c\u52a8\u4e5f\u540c\u6837\u77eb\u5065\u7075\u6d3b\uff0c\u800c\u4e14\u8fdc\u6bd4\u4ed6\u7684\u540c\u4f34\u66f4\u8001\u7ec3\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e5f\u6ca1\u6709\u56de\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u5415\u4e09\u7b49\u5f97\u66f4\u4e45\uff0c\u7136\u540e\u624d\u7528\u91d1\u76c6\u6d17\u624b\uff0c\u5728\u91d1\u7089\u70e7\u9999\u3002 \u3000\u3000\u800c\u4e14\u5c45\u7136\u8fd8\u5728\u53f9\u606f\u3002 \u3000\u3000\u4ed6\u9762\u5bf9\u4e8c\u5341\u56db\u53f7\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u66f4\u4e25\u8083\uff0c\u53d1\u51fa\u7684\u547d\u4ee4\u66f4\u7b80\u77ed\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u5bf9\u4e8c\u5341\u56db\u53f7\u8fd9\u79cd\u4eba\u6765\u8bf4\uff0c\u4efb\u4f55\u4e00\u4e2a\u591a\u4f59\u7684\u5b57\u90fd\u662f\u5e9f\u8bdd\u3002 \u3000\u3000\u4ed6\u53ea\u8bf4\u4e86\u4e24\u4e2a\u5b57\uff1a \u3000\u3000\u201c\u4f60\u53bb\uff01\u201d \u3000\u3000\u4e8c\u5341\u56db\u53f7\u9ed8\u9ed8\u5730\u63a5\u4e0b\u4e86\u8fd9\u9053\u547d\u4ee4\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u50cf\u56db\u53f7\u90a3\u6837\u3002 \u3000\u3000\u4e00\u63a5\u4e0b\u547d\u4ee4\u5c31\u7acb\u523b\u50cf\u706b\u71c3\u5230\u7709\u6bdb\u4e00\u6837\u5f00\u59cb\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u50cf\u5341\u56db\u53f7\u90a3\u6837\u5148\u68c0\u67e5\u4ed6\u7684\u88c5\u5907\u662f\u5426\u5229\u843d\uff1f \u3000\u3000\u518d\u68c0\u67e5\u4ed6\u7684\u5251\u662f\u5426\u987a\u624b\uff1f \u3000\u3000\u5df2\u7ecf\u6709\u4e24\u4e2a\u4eba\u4e00\u8d70\u5165\u8fd9\u6761\u9634\u6697\u7684\u5730\u9053\u540e\uff0c\u5c31\u6c38\u4e0d\u590d\u8fd4\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u6740\u4eba\u7684\u4eba\uff0c\u90fd\u662f\u4f7f\u5251\u7684\u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u4ed6\u7684\u4f19\u4f34\uff0c\u4ed6\u5df2\u7ecf\u8ddf\u4ed6\u4eec\u5171\u540c\u751f\u6d3b\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4ed6\u4eec\u90fd\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u63a5\u4e0b\u8fd9\u4e2a\u8981\u547d\u7684\u547d\u4ee4\u4e4b\u540e\uff0c\u5c31\u597d\u50cf\u63a5\u5230\u4e00\u5f20\u522b\u4eba\u8bf7\u4ed6\u53bb\u5403\u996d\u7684\u5e16\u5b50\u4e00\u6837\u3002\u800c\u4e14\u662f\u4e2a\u5f88\u719f\u7684\u670b\u53cb\u8bf7\u4ed6\u53bb\u5403\u5bb6\u5e38\u4fbf\u996d\u3002 \u3000\u3000\u901a\u9053\u91cc\u8fd8\u662f\u90a3\u4e48\u9634\u68ee\u9ed1\u6697\u3002 \u3000\u3000\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u97f3\uff0c\u770b\u4e0d\u89c1\u4e00\u70b9\u52a8\u9759\u3002 \u3000\u3000\u5c31\u50cf\u662f\u6761\u4e0a\u53e4\u6d2a\u8352\u65f6\u7684\u5de8\u87d2\uff0c\u9759\u9759\u5730\u541e\u566c\u4e86\u4e24\u4e2a\u4eba\uff0c\u8fde\u5480\u56bc\u7684\u58f0\u97f3\u90fd\u6ca1\u6709\u53d1\u51fa\u6765\u3002 \u3000\u3000\u4e8c\u5341\u56db\u53f7\u5df2\u7ecf\u51c6\u5907\u8d70\u8fdb\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u795e\u60c5\u8fd8\u662f\u90a3\u4e48\u9547\u9759\uff0c\u975e\u4f46\u8138\u8272\u6ca1\u6709\u53d8\uff0c\u4e5f\u6ca1\u6709\u4e00\u70b9\u51c6\u5907\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u4e0d\u5feb\u4e5f\u4e0d\u6162\uff0c\u770b\u8d77\u6765\u4e5f\u50cf\u662f\u8981\u5230\u9644\u8fd1\u7684\u8001\u670b\u53cb\u5bb6\u91cc\u53bb\u5403\u4fbf\u996d\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u4ed6\u6709\u6ca1\u6709\u60f3\u5230\u8fd9\u6b21\u8981\u88ab\u4eba\u8fde\u76ae\u5e26\u8089\u4e00\u8d77\u541e\u4e0b\u53bb\u7684\uff0c\u4e5f\u8bb8\u5c31\u662f\u4ed6\u81ea\u5df1\uff1f \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u8d70\u5230\u901a\u9053\u7684\u4eba\u53e3\uff0c\u65e0\u8bba\u8c01\u90fd\u8ba4\u4e3a\u4ed6\u4f1a\u4e00\u76f4\u8d70\u8fdb\u53bb\u7684\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u4ed6\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u5415\u4e09\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u91cc\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\uff0c\u4e5f\u6ca1\u6709\u611f\u60c5\uff0c\u53ef\u662f\u4ed6\u5c45\u7136\u5f00\u53e3\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u6211\u4ece\u4e03\u5c81\u5b66\u5251\uff0c\u5341\u4e09\u5c81\u65f6\u5b66\u5251\u5c1a\u672a\u6210\uff0c\u5c31\u5df2\u5b66\u4f1a\u6740\u4eba\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5e73\u51e1\u5355\u8c03\uff1a \u3000\u3000\u201c\u800c\u4e14\u6211\u771f\u7684\u6740\u4e86\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5415\u4e09\u5fae\u7b11\uff1a \u3000\u3000\u201c\u4f60\u5341\u4e09\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u5df2\u5c06\u4f60\u5bb6\u4e61\u6700\u51f6\u6a2a\u7684\u9646\u5c60\u6237\u523a\u6740\u5728\u5f53\u5730\u6700\u70ed\u95f9\u7684\u83dc\u5e02\u53e3\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u8fd9\u4e00\u751f\u4e2d\u6740\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u4e8c\u5341\u56db\u53f7\u8bf4\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4ece\u4e0d\u613f\u60f9\u4e8b\u751f\u975e\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u8ddf\u522b\u4eba\u7ed3\u4ec7\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6700\u4e3b\u8981\u7684\u662f\uff0c\u6211\u6839\u672c\u5c31\u4e0d\u559c\u6b22\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u4f60\u6740\u4eba\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u8981\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u6740\u4eba\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u8981\u5403\u996d\u800c\u5df2\uff0c\u6bcf\u4e2a\u4eba\u90fd\u8981\u5403\u996d\uff0c\u6211\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u4e8c\u5341\u56db\u53f7\u8bf4\uff1a \u3000\u3000\u201c\u4e3a\u4e86\u5403\u996d\u800c\u6740\u4eba\u867d\u7136\u4e0d\u662f\u4ef6\u6109\u5feb\u7684\u4e8b\uff0c\u4f46\u662f\u53e6\u5916\u8fd8\u6709\u4e00\u4e9b\u4eba\u4e3a\u4e86\u5403\u996d\u800c\u505a\u51fa\u7684\u4e8b\u6bd4\u6211\u505a\u7684\u4e8b\u66f4\u75db\u82e6\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u65e2\u7136\u4e3a\u4e86\u8981\u5403\u996d\u800c\u6740\u4eba\uff0c\u6240\u4ee5\u6211\u6bcf\u6b21\u6740\u4eba\u90fd\u8981\u6709\u4ee3\u4ef7\u7684\u3002\u4ece\u6765\u90fd\u6ca1\u6709\u4e00\u6b21\u4f8b\u5916\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u867d\u7136\u5728\u6211\u8eab\u5206\u66b4\u9732\uff0c\u88ab\u4eba\u8ffd\u6740\u65f6\u6536\u5bb9\u4e86\u6211\uff0c\u53ef\u662f\u4f60\u4e5f\u4e0d\u80fd\u4f8b\u5916\u3002\u201d \u3000\u3000\u4e8c\u5341\u56db\u53f7\u8bf4\uff1a \u3000\u3000\u201c\u4f60\u5f53\u7136\u4e5f\u5e94\u8be5\u77e5\u9053\u6211\u6740\u4eba\u7684\u4ef7\u94b1\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5415\u4e09\u4ecd\u7136\u5728\u5fae\u7b11\uff1a \u3000\u3000\u201c\u6211\u65e9\u5c31\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8d70\u8fc7\u53bb\uff0c\u628a\u90a3\u5757\u4ed6\u4e00\u76f4\u63e1\u5728\u624b\u638c\u91cc\u7684\u5341\u8db3\u7eaf\u91d1\u585e\u5165\u4e8c\u5341\u56db\u53f7\u624b\u91cc\u3002 \u3000\u3000\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u7684\u89c4\u77e9\uff0c\u6740\u4eba\u524d\u53ea\u8981\u5148\u4ed8\u4e00\u534a\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u8fd9\u5757\u9ec4\u91d1\u5df2\u7ecf\u5e94\u8be5\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u5df2\u7ecf\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u56db\u53f7\u8bf4\uff1b \u3000\u3000\u201c\u8fd9\u5757\u91d1\u5b50\u4e0d\u4f46\u6210\u8272\u6781\u7eaf\uff0c\u800c\u4e14\u91d1\u8d28\u6781\u597d\uff0c\u4e00\u822c\u5e02\u9762\u4e0a\u662f\u7edd\u5bf9\u4e70\u4e0d\u5230\u7684\uff0c\u53ea\u4e0d\u8fc7\u4e00\u4e2a\u4eba\u5982\u679c\u6b7b\u4e86\uff0c\u9ec4\u91d1\u5bf9\u4ed6\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u7136\u8fd9\u4e48\u8bf4\uff0c\u8fd8\u662f\u5c06\u9ec4\u91d1\u85cf\u5165\u6000\u91cc\uff0c\u5ffd\u7136\u53c8\u8bf4\uff1a \u3000\u3000\u201c\u6211\u8fd8\u8981\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e8c\u5341\u56db\u53f7\u6de1\u6de1\u4ed6\u8bf4\uff1a \u3000\u3000\u201c\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u6c42\u4f60\u5343\u4e07\u4e0d\u8981\u4e3a\u6211\u6d17\u624b\u4e0a\u9999\uff0c\u56e0\u4e3a\u4f60\u5df2\u7ecf\u4ed8\u51fa\u4e86\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u597d\u50cf\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u53ef\u662f\u4ed6\u5df2\u7ecf\u8f6c\u8eab\u8d70\u5165\u4e86\u90a3\u6761\u9634\u68ee\u9ed1\u6697\u7684\u901a\u9053\u3002 \u3000\u3000\u4ed6\u7684\u80cc\u5f71\u770b\u8d77\u6765\u8fdc\u6bd4\u4ed6\u7684\u6b63\u9762\u633a\u62d4\u5f97\u591a\uff0c\u4f46\u662f\u4e5f\u5f88\u5feb\u5c31\u5df2\u6d88\u5931\u7684\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u4e5f\u4f1a\u540c\u6837\u4e00\u53bb\u4e0d\u8fd4\uff1f \u3000\u3000\u9f50\u5c0f\u71d5\u770b\u7740\u4ed6\uff0c\u76f4\u5230\u4ed6\u7684\u80cc\u5f71\u5b8c\u5168\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\uff0c\u624d\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u771f\u662f\u602a\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u597d\u50cf\u5df2\u7ecf\u660e\u77e5\u8fd9\u4e00\u53bb\u975e\u6b7b\u4e0d\u53ef\uff0c\u800c\u4e14\u4e5f\u660e\u660e\u77e5\u9053\u4e00\u4e2a\u4eba\u6b7b\u4e86\u4e4b\u540e\uff0c\u6210\u4efd\u518d\u7eaf\u7684\u9ec4\u91d1\u5bf9\u4ed6\u90fd\u6ca1\u6709\u7528\u4e86\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u4f46\u4ed6\u5374\u504f\u504f\u8fd8\u662f\u8981\u5148\u6536\u4e0b\u4f60\u8fd1\u5757\u9ec4\u91d1\uff0c\u4ed6\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e3a\u4e86\u4ed6\u7684\u539f\u5219\u3002\u201d \u3000\u3000\u201c\u539f\u5219\uff1f\u201d \u3000\u3000\u201c\u539f\u5219\u5c31\u662f\u89c4\u77e9\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u4ed6\u81ea\u77e5\u5fc5\u6b7b\u4e5f\u8981\u53bb\u505a\u8fd9\u4ef6\u4e8b\uff0c\u65e2\u7136\u8981\u53bb\u505a\u5c31\u5f97\u5148\u6536\u4e0b\u8fd9\u5757\u9ec4\u91d1\uff0c\u56e0\u4e3a\u8fd9\u662f\u4ed6\u7684\u89c4\u77e9\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u7edd\u6ca1\u6709\u4e1d\u6beb\u8ba5\u6d88\u4e4b\u610f\uff1a \u3000\u3000\u201c\u4e00\u4e2a\u6709\u539f\u5219\u7684\u4eba\uff0c\u89c4\u77e9\u662f\u7edd\u4e0d\u53ef\u7834\u7684\uff0c\u4e0d\u7ba1\u4ed6\u662f\u6b7b\u662f\u6d3b\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u4e25\u8083\uff0c\u751a\u81f3\u8fd8\u5e26\u7740\u4e09\u5206\u5c0a\u656c\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u5374\u95ee\u4ed6\uff1a \u3000\u3000\u201c\u4ed6\u89c9\u5f97\u8fd9\u79cd\u4eba\u662f\u7b28\uff1f\u8fd8\u662f\u806a\u660e\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1b \u3000\u3000\u201c\u6211\u53ea\u77e5\u9053\u73b0\u5728\u8fd9\u79cd\u4eba\u5df2\u7ecf\u8d8a\u6765\u8d8a\u5c11\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u5f88\u559c\u6b22\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u4ed6\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u662f\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u5415\u4e09\u53cd\u95ee\uff1a \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6b7b\u7684\u4e0d\u662f\u6211\u8981\u4ed6\u53bb\u6740\u7684\u90a3\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u76ef\u7740\u9f50\u5c0f\u71d5\uff1a \u3000\u3000\u201c\u83ab\u975e\u4f60\u5df2\u7ecf\u77e5\u9053\u6211\u8981\u4ed6\u6740\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u5979\u6c89\u9ed8\u5f97\u5c31\u50cf\u662f\u90a3\u6761\u9634\u68ee\u9ed1\u6697\u7684\u901a\u9053\u4e00\u6837\u3002 \u3000\u3000\u901a\u9053\u91cc\u4ecd\u7136\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u97f3\uff0c\u770b\u4e0d\u89c1\u4e00\u70b9\u52a8\u9759\u3002 \u3000\u3000\u4e8c\u5341\u56db\u53f7\u4e5f\u6ca1\u6709\u56de\u6765\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u5f88\u4e45\u90fd\u6ca1\u6709\u56de\u6765\u3002 \u3000\u3000\u5415\u4e09\u5ffd\u7136\u8bf4\uff1a \u3000\u3000\u201c\u6211\u4eec\u597d\u50cf\u5e94\u8be5\u5403\u996d\u4e86\uff1f\u201d \u3000\u3000\u201c\u5403\u996d\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u597d\u50cf\u5f88\u60ca\u8bb6\uff1a \u3000\u3000\u201c\u4f60\u8981\u5403\u996d\uff1f\u201d \u3000\u3000\u201c\u5403\u996d\u5e76\u4e0d\u662f\u4ef6\u602a\u4e8b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u8981\u5403\u996d\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u5e94\u8be5\u5403\u996d\u7684\u65f6\u5019\u5c31\u8981\u5403\u996d\uff0c\u4e0d\u7ba1\u4e8b\u60c5\u600e\u4e48\u6837\u53d1\u5c55\u90fd\u8981\u5403\u996d\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4f60\u7684\u539f\u5219\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u9152\u662f\u7528\u91d1\u640f\u76db\u6765\u7684\uff0c\u659f\u5728\u91d1\u676f\u91cc\u3002 \u3000\u3000\u4ece\u6ce2\u65af\u6765\u7684\u8461\u8404\u7f8e\u9152\u659f\u5728\u91d1\u676f\u91cc\uff0c\u867d\u7136\u53d1\u4e0d\u51fa\u7425\u73c0\u5149\uff0c\u5374\u4ecd\u7136\u6709\u4e00\u4e00\u79cd\u6de1\u6de1\u7684\u90c1\u91d1\u9999\u6c14\uff0c\u800c\u4e14\u522b\u6709\u4e00\u79cd\u60c5\u8da3\u3002 \u3000\u3000\u2014\u2014\u6709\u8c01\u80fd\u8bf4\u5bcc\u8d35\u4e0d\u662f\u4e00\u79cd\u60c5\u8da3\uff1f \u3000\u3000\u83dc\u80b4\u88c5\u5728\u7eaf\u91d1\u7684\u5668\u76bf\u91cc\u3002 \u3000\u3000\u2014\u2014\u6781\u7cbe\u7f8e\u7684\u624b\u5de5\u5668\u76bf\uff0c\u6781\u7cbe\u7f8e\u7684\u70f9\u4efb\u3002 \u3000\u3000\u4e5f\u8bb8\u8fd8\u4e0d\u4ec5\u662f\u201c\u7cbe\u7f8e\u201d\u800c\u5df2\uff0c\u800c\u662f\u201c\u5b8c\u7f8e\u201d\u3002 \u3000\u3000\u5415\u4e09\u5728\u996e\u98df\u65f6\u7684\u98ce\u5ea6\u4e5f\u4f18\u96c5\u5f97\u51e0\u4e4e\u5230\u8fbe\u201c\u5b8c\u7f8e\u201d\u3002 \u3000\u3000\u80fd\u591f\u548c\u4ed6\u8fd9\u6837\u7684\u4eba\u5171\u4eab\u4e00\u987f\u7cbe\u7f8e\u7684\u665a\u9910\uff0c\u5e94\u8be5\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u5374\u8fde\u4e00\u70b9\u80c3\u53e3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u662f\u5728\u4e3a\u4e8c\u5341\u56db\u53f7\u62c5\u5fc3\u3002 \u3000\u3000\u4e5f\u4e0d\u662f\u4e3a\u4e8c\u5341\u56db\u53f7\u8981\u53bb\u6740\u7684\u90a3\u4e2a\u4eba\u62c5\u5fc3\u3002 \u3000\u3000\u5979\u53ea\u662f\u89c9\u5f97\u5728\u522b\u4eba\u53bb\u6740\u4eba\u7684\u65f6\u5019\u3002 \u3000\u3000\u8fd8\u80fd\u591f\u5750\u4e0b\u6765\u4eab\u53d7\u4f73\u80b4\u7f8e\u9152\uff0c\u5b9e\u5728\u662f\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002 \u3000\u3000\u9634\u68ee\u9ed1\u6697\u7684\u901a\u9053\u91cc\uff0c\u4ecd\u7136\u5168\u65e0\u52a8\u9759\u3002 \u3000\u3000\u5415\u4e09\u7ec8\u4e8e\u7ed3\u675f\u4e86\u4ed6\u7684\u665a\u9910\uff0c\u5728\u4e00\u4e2a\u91d1\u76c6\u91cc\u6d17\u4e86\u6d17\u624b\u3002 \u3000\u3000\u91d1\u76c6\u91cc\u88c5\u7684\u4e0d\u662f\u6c34\uff0c\u800c\u662f\u6e05\u8336\u3002 \u3000\u3000\u5415\u4e09\u89e3\u91ca\uff1a \u3000\u3000\u201c\u4eca\u5929\u6211\u4eec\u5403\u4e86\u867e\u548c\u87f9\uff0c\u53ea\u6709\u81ea\u5df1\u4eb2\u624b\u5265\u867e\u548c\u87f9\uff0c\u624d\u80fd\u771f\u6b63\u9886\u7565\u5230\u5403\u867e\u548c\u87f9\u7684\u4e50\u8da3\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\uff1a \u3000\u3000\u201c\u53ea\u6709\u7528\u6e05\u8336\u6d17\u624b\uff0c\u624d\u80fd\u6d17\u6389\u624b\u4e0a\u7684\u8165\u6c14\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u5ffd\u7136\u95ee\uff1a \u3000\u3000\u201c\u6740\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u6740\u4eba\uff1f\u201d \u3000\u3000\u5415\u4e09\u663e\u7136\u8fd8\u6ca1\u6709\u4e86\u89e3\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u6740\u4eba\u662f\u4e0d\u662f\u4e5f\u8ddf\u5403\u867e\u548c\u87f9\u4e00\u6837\uff1f\u4e5f\u8981\u81ea\u5df1\u4eb2\u624b\u53bb\u6740\uff0c\u624d\u80fd\u9886\u7565\u5230\u5176\u4e2d\u7684\u4e50\u8da3\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5f97\u5f88\u7edd\uff0c\u5415\u4e09\u56de\u7b54\u5f97\u4e5f\u5f88\u5999\u3002 \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u90a3\u5c31\u5f97\u770b\u4e86\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\u4f60\u8981\u6740\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u6709\u4e9b\u4eba\u4f60\u4e0d\u59a8\u8981\u522b\u4eba\u53bb\u6740\uff0c\u6709\u4e9b\u4eba\u5374\u4e00\u5b9a\u975e\u8981\u81ea\u5df1\u4eb2\u624b\u53bb\u6740\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u6740\u5b8c\u4e86\u4e4b\u540e\u5462\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u53c8\u95ee\uff1a \u3000\u3000\u201c\u5982\u679c\u4f60\u4eb2\u624b\u53bb\u6740\uff0c\u6740\u5b8c\u4e86\u4e4b\u540e\u8981\u7528\u4ec0\u4e48\u624d\u80fd\u6d17\u6389\u4f60\u624b\u4e0a\u7684\u8840\u8165\u6c14\uff1f\u201d \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u4e5f\u6ca1\u6709\u4eba\u613f\u610f\u56de\u7b54\u3002\u5415\u4e09\u7528\u4e00\u5757\u7eaf\u6d01\u7684\u767d\u4e2d\u64e6\u5e72\u4e86\u624b\uff0c\u6162\u6162\u5730\u7ad9\u8d77\u6765\uff0c\u4e5f\u8d70\u5165\u4e86\u90a3\u6761\u9634\u68ee\u7684\u901a\u9053\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u62db\u547c\u9f50\u5c0f\u71d5\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u9f50\u5c0f\u71d5\u4e00\u5b9a\u4e5f\u4f1a\u8ddf\u4ed6\u4e00\u8d77\u8fdb\u53bb\u7684\u3002\u901a\u9053\u91cc\u7a76\u7adf\u53d1\u751f\u8fc7\u4ec0\u4e48\u4e8b\uff1f\u901a\u9053\u7684\u5165\u53e3\u95e8\u6237\uff0c\u5efa\u9020\u5f97\u5c31\u50cf\u662f\u4e00\u4e2a\u957f\u5f62\u7684\u7c73\u6597\u3002\u8d8a\u5230\u5e95\u7aef\u8d8a\u5c0f\uff0c\u5230\u4e86\u771f\u6b63\u7684\u4eba\u53e3\u5904\uff0c\u5df2\u7ecf\u6536\u7f29\u6210\u4e00\u4e2a\u4e24\u5c3a\u89c1\u65b9\u7684\u6d1e\u3002 \u3000\u3000\u50cf\u9f50\u5c0f\u71d5\u8fd9\u79cd\u8eab\u6750\u7684\u4eba\uff0c\u8981\u94bb\u8fdb\u53bb\u90fd\u4e0d\u592a\u5bb9\u6613\u3002 \u3000\u3000\u6240\u4ee5\u5916\u9762\u7684\u706f\u5149\u867d\u7136\u8f89\u714c\u660e\u4eae\u3001\u5374\u6839\u672c\u7167\u4e0d\u8fdb\u8fd9\u6761\u901a\u9053\u91cc\u3002 \u3000\u3000\u4e00\u8d70\u8fdb\u53bb\u5c31\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u4e86\uff0c\u751a\u81f3\u8fde\u81ea\u5df1\u7684\u624b\u6307\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u2014\u2014\u5415\u4e09\u4e3a\u4ec0\u4e48\u8981\u628a\u8fd9\u6761\u901a\u9053\u5efa\u9020\u5f97\u5982\u6b64\u795e\u79d8\uff1f \u3000\u3000\u5415\u4e09\u5df2\u7ecf\u9690\u6ca1\u5728\u9ed1\u6697\u91cc\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u6b63\u60f3\u6478\u7d22\u7740\u5f80\u524d\u8d70\u3002 \u3000\u3000\u5ffd\u7136\u542c\u89c1\u4ed6\u7684\u58f0\u97f3\uff1a \u3000\u3000\u201c\u4f60\u6700\u597d\u4e0d\u8981\u4e00\u76f4\u518d\u5f80\u524d\u8d70\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u95ee\uff1a \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u6761\u901a\u9053\u4e0d\u662f\u76f4\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1b \u3000\u3000\u201c\u8fd9\u6761\u901a\u9053\u4e00\u5171\u6709\u4e09\u5341\u4e09\u66f2\uff0c\u5982\u679c\u4f60\u4e00\u76f4\u5f80\u524d\u8d70\uff0c\u4e00\u5b9a\u4f1a\u78b0\u5230\u5899\u4e0a\uff0c\u78b0\u6241\u4f60\u7684\u9f3b\u5b50\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u63a5\u7740\u8bf4\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e5f\u8bb8\u4e0d\u4fe1\uff0c\u4ece\u5916\u800c\u770b\uff0c\u8fd9\u6761\u901a\u9053\u786e\u5b9e\u7cfb\u7b14\u76f4\u901a\u5230\u5e95\u7684\uff0c\u5982\u679c\u4f60\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u8bd5\u4e00\u8bd5\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u6ca1\u6709\u8bd5\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u9ed1\u6697\u603b\u662f\u4f1a\u8ba9\u4eba\u9020\u6210\u5f88\u591a\u9519\u89c9\u3002 \u3000\u3000\u4f1a\u8ba9\u4eba\u8ba4\u4e3a\u201c\u76f4\u201d\u662f\u201c\u66f2\u201d\uff0c\u201c\u66f2\u201d\u662f\u201c\u76f4\u201d\u3002 \u3000\u3000\u4f1a\u8ba9\u4eba\u66f2\u76f4\u4e0d\u5206\uff0c\u4f1a\u8ba9\u4eba\u78b0\u6241\u9f3b\u5b50\u3002 \u3000\u3000\u5979\u867d\u7136\u5e74\u8f7b\uff0c\u53ef\u662f\u5979\u4e5f\u77e5\u9053\u8fd9\u4e16\u754c\u4e0a\u8fd8\u6709\u5f88\u591a\u522b\u7684\u4e8b\u4e5f\u548c\u9ed1\u6697\u4e00\u6837\u3002 \u3000\u3000\u4e5f\u4f1a\u8ba9\u4eba\u9020\u6210\u9519\u89c9\uff0c\u8ba9\u4eba\u4e0d\u5206\u66f2\u76f4\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u56db\u4e09\u7ae0\u3000\u5b9d\u85cf \u7b2c\u56db\u5341\u4e09\u7ae0\u3000\u5b9d\u85cf \u3000\u3000\u8b6c\u5982\u8bf4\uff0c\u4e00\u79cd\u4f3c\u662f\u800c\u975e\u7684\u4f2a\u541b\u5b50\u7684\u9053\u5fb7\u89c2\uff0c\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u5979\u6ca1\u6709\u8fd9\u79cd\u89c2\u5ff5\uff0c\u5979\u4e0d\u60f3\u505a\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u5979\u65e2\u4e0d\u60f3\u8ba9\u4eba\u78b0\u6241\u9f3b\u5b50\uff0c\u4e5f\u4e0d\u60f3\u78b0\u6241\u81ea\u5df1\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u6240\u4ee5\u5979\u4f5c\u4e86\u4e2a\u6700\u806a\u660e\u7684\u9009\u62e9\u3002 \u3000\u3000\u5979\u70b9\u4eae\u4e86\u4e00\u4e2a\u706b\u6298\u5b50\u3002 \u3000\u3000\u706b\u5149\u4eae\u8d77\u65f6\uff0c\u7acb\u523b\u6709\u91d1\u5149\u8000\u773c\u3002 \u3000\u3000\u8fd9\u6761\u901a\u9053\u7684\u4e24\u58c1\uff0c\u7adf\u90fd\u662f\u7528\u5de8\u5927\u7684\u91d1\u7816\u780c\u6210\u3002 \u3000\u3000\u524d\u9762\u4e0d\u8fdc\u5904\u5c31\u6709\u4e2a\u8f6c\u66f2\u3002 \u3000\u3000\u5415\u4e09\u6b63\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u6001\u5ea6\u770b\u7740\u5979\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f60\u8eab\u4e0a\u5c45\u7136\u5e26\u7740\u706b\u6298\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u5fae\u7b11\uff1a \u3000\u3000\u201c\u867d\u7136\u4f60\u5df2\u7ecf\u6d3e\u4eba\u628a\u6211\u5f7b\u5e95\u641c\u67e5\u8fc7\uff0c\u53ef\u60dc\u90a3\u4e9b\u4eba\u8fd8\u662f\u6ca1\u60f3\u5230\u6211\u4f1a\u628a\u4e00\u4e2a\u706b\u6298\u5b50\u85cf\u5728\u4e00\u4e2a\u53d1\u7c2a\u91cc\u3002\u201d \u3000\u3000\u7cbe\u7f8e\u7684\u78a7\u7389\u7ba1\uff0c\u7cbe\u5de7\u7684\u706b\u6298\u5b50\u3002 \u3000\u3000\u8fd9\u4e2a\u706b\u6298\u5b50\u672c\u8eab\u7684\u4ef7\u503c\u4e5f\u8bb8\u5df2\u8fdc\u8d85\u8fc7\u78a7\u7389\u7c2a\u3002 \u3000\u3000\u5415\u4e09\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u4f60\u8eab\u4e0a\u662f\u4e0d\u662f\u8fd8\u85cf\u4e86\u4e9b\u4ec0\u4e48\u522b\u7684\u4e1c\u897f\uff1f\u4e00\u4e9b\u8ba9\u4eba\u60f3\u4e0d\u5230\u7684\u53e4\u602a\u4e1c\u897f\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u60f3\u77e5\u9053\uff0c\u4f60\u6700\u597d\u5c31\u81ea\u5df1\u6765\u5f7b\u5e95\u628a\u6211\u641c\u67e5\u4e00\u904d\u3002\u201d \u3000\u3000\u5979\u76ef\u7740\u5415\u4e09\uff0c\u4f38\u5f00\u53cc\u624b\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u7684\u8863\u670d\u7a7f\u5f97\u5e76\u4e0d\u591a\uff0c\u5979\u7684\u8eab\u6750\u5df2\u6e10\u6e10\u6210\u719f\u3002\u201d \u3000\u3000\u5979\u773c\u775b\u91cc\u9732\u51fa\u7684\u8868\u60c5\u4e5f\u4e0d\u77e5\u662f\u8bf1\u60d1\uff1f \u3000\u3000\u8fd8\u662f\u6311\u6218\uff1f \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u90fd\u53ef\u4ee5\u8ddf\u4f60\u4fdd\u8bc1\u3002\u201d\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u6211\u8eab\u4e0a\u5e26\u7740\u7684\u6700\u53e4\u602a\u6700\u6709\u8da3\u7684\u4e00\u6837\u4e1c\u897f\uff0c\u7edd\u4e0d\u662f\u8fd9\u4e2a\u706b\u6298\u5b50\u3002\u201d \u3000\u3000\u5415\u4e09\u7b11\u4e86\uff0c\u6709\u70b9\u50cf\u662f\u82e6\u7b11\u3002 \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u6211\u7edd\u5bf9\u76f8\u4fe1\u3002\u201d \u3000\u3000\u901a\u9053\u91cc\u7684\u8f6c\u66f2\u5904\u867d\u7136\u5f88\u591a\uff0c\u5415\u4e09\u53c8\u7ee7\u7eed\u5f80\u524d\u8d70\uff0c\u9f50\u5c0f\u71d5\u5728\u540e\u9762\u8ddf\u7740\uff0c\u4e24\u58c1\u7684\u91d1\u7816\u5728\u706b\u5149\u4e0b\u95ea\u8000\u4e0d\u606f\u3002 \u3000\u3000\u8fd9\u6761\u901a\u9053\u65e0\u7591\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u4e16\u4e0a\u4ef7\u503c\u6700\u6602\u8d35\u7684\u4e00\u6761\u3002 \u3000\u3000\u5979\u6ca1\u6709\u95ee\u5415\u4e09\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u8981\u5efa\u9020\u8fd9\u6837\u4e00\u6761\u901a\u9053\uff1f \u3000\u3000\u5979\u77e5\u9053\u8fd9\u6761\u901a\u9053\u4e00\u5b9a\u9690\u85cf\u7740\u4e00\u4e9b\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5982\u679c\u5415\u4e09\u4e0d\u8bf4\uff0c\u8c01\u4e5f\u95f4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6240\u4ee5\u5979\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u95f4\uff0c\u4f46\u662f\u5979\u5ffd\u7136\u89c9\u5f97\u5f88\u4e0d\u8212\u670d\uff0c\u800c\u4e14\u8d8a\u6765\u8d8a\u4e0d\u8212\u670d\u3002 \u3000\u3000\u5979\u4e00\u76f4\u60f3\u4e0d\u901a\u8fd9\u79cd\u4e0d\u8212\u670d\u7684\u611f\u89c9\u662f\u600e\u4e48\u6765\u7684\uff1f \u3000\u3000\u901a\u9053\u91cc\u867d\u7136\u9634\u68ee\u9ed1\u6697\uff0c\u53ef\u662f\u70b9\u7740\u7684\u706b\u6298\u5b50\u5e76\u6ca1\u6709\u7184\u706d\uff0c\u8d70\u5728\u901a\u9053\u91cc\u7684\u547c\u5438\u4e5f\u5f88\u7545\u901a\u3002 \u3000\u3000\u7531\u6b64\u53ef\u89c1\uff0c\u5728\u8fd9\u6761\u901a\u9053\u91cc\u67d0\u4e00\u4e9b\u79d8\u5bc6\u7684\u5730\u65b9\uff0c\u4e00\u5b9a\u7528\u67d0\u79cd\u5f88\u5de7\u5999\u7684\u65b9\u6cd5\u7559\u4e0b\u4e86\u4e00\u4e9b\u901a\u98ce\u5904\u3002 \u3000\u3000\u6240\u4ee5\u901a\u9053\u91cc\u7684\u7a7a\u6c14\u6c38\u8fdc\u90fd\u4fdd\u6301\u5e72\u71e5\u6d41\u7545\uff0c\u800c\u4e14\u975e\u5e38\u5e72\u51c0\u3002 \u3000\u3000\u975e\u5e38\u975e\u5e38\u5e72\u51c0\uff0c\u5e72\u51c0\u5f97\u8ba9\u4eba\u55c5\u8d77\u6765\u5c31\u50cf\u662f\u4e00\u4ef6\u5df2\u7ecf\u5728\u80a5\u7682\u6c34\u91cc\u6ce1\u8fc7\u4e09\u5929\uff0c\u53c8\u6413\u6d17\u8fc7\u5341\u516d\u516b\u904d\u7684\u8863\u670d\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u5ffd\u7136\u53d1\u89c9\u5979\u90a3\u79cd\u4e0d\u8212\u670d\u7684\u611f\u89c9\u5c31\u662f\u8fd9\u4e48\u6837\u6765\u7684\u3002 \u3000\u3000\u201c\u5e72\u51c0\u201d\u662f\u4ef6\u597d\u4e8b\uff0c\u662f\u4ef6\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u672c\u6765\u7edd\u4e0d\u4f1a\u8ba9\u4eba\u4e0d\u8212\u670d\u7684\u3002\u53ef\u89c1\u8fd9\u5730\u65b9\u5b9e\u5728\u5927\u5e72\u51c0\u4e86\u3002 \u3000\u3000\u7b80\u76f4\u5e72\u51c0\u5f97\u8ba9\u4eba\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u9f50\u5c0f\u71d5\u8fd8\u662f\u60f3\u4e0d\u901a\uff1f \u3000\u3000\u5415\u4e09\u5ffd\u7136\u95ee\u5979\uff1a \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u6709\u70b9\u602a\u602a\u7684\uff1f\u662f\u4e0d\u662f\u89c9\u5f97\u6709\u70b9\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u5415\u4e09\u53c8\u95ee\uff1a \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u79cd\u611f\u89c9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u5766\u767d\u627f\u8ba4\uff1a \u3000\u3000\u201c\u6211\u600e\u4e48\u60f3\u90fd\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u5979\u672c\u6765\u4ee5\u4e3a\u5415\u4e09\u4f1a\u89e3\u91ca\u8fd9\u4ef6\u4e8b\u7684\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u5415\u4e09\u53c8\u95ee\u4e86\u53e6\u5916\u4e00\u4e2a\u597d\u50cf\u548c\u8fd9\u4ef6\u4e8b\u5b8c\u5168\u65e0\u5173\u7684\u95ee\u9898\u3002 \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5929\u4e0b\u4e07\u4e8b\u4e07\u7269\u4e2d\uff0c\u6700\u7eaf\u6700\u5e72\u51c0\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u5415\u4e09\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u95ee\u9898\uff1a \u3000\u3000\u201c\u662f\u9ec4\u91d1\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u4e16\u4e0a\u4e07\u7269\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u6bd4\u9ec4\u91d1\u66f4\u7eaf\u66f4\u5e72\u51c0\u3002\u201d \u3000\u3000\u8fd9\u6761\u901a\u9053\u5c31\u662f\u7528\u9ec4\u91d1\u5efa\u6210\u7684\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9\u91cc\u786e\u5b9e\u975e\u5e38\u5e72\u51c0\u3002 \u3000\u3000\u53ef\u662f\u5415\u4e09\u53c8\u63a5\u7740\u95ee\u4e86\u5979\u4e00\u4e2a\u66f4\u7edd\u7684\u95ee\u9898\u3002 \u3000\u3000\u201c\u4e16\u4e0a\u4e5f\u6709\u5f88\u591a\u79cd\u4eba\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6700\u5e72\u51c0\u7684\u662f\u54ea\u4e00\u79cd\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u5df1\u56de\u7b54\uff1a \u3000\u3000\u201c\u662f\u6b7b\u4eba\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1b \u3000\u3000\u201c\u4e16\u4e0a\u6700\u5e72\u51c0\u7684\u4e00\u79cd\u4eba\uff0c\u5c31\u662f\u6b7b\u4eba\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u6240\u6709\u7684\u6b7b\u4eba\u90fd\u8981\u88ab\u6e05\u6d17\u5f97\u5e72\u5e72\u51c0\u51c0\u4e4b\u540e\u624d\u88c5\u8fdb\u68fa\u6750\u3002 \u3000\u3000\u5c31\u7b97\u662f\u6700\u80ae\u810f\u7684\u4eba\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u5979\u627f\u8ba4\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4e5f\u5c31\u60f3\u901a\u4e86\u5979\u521a\u624d\u60f3\u4e0d\u901a\u7684\u90a3\u4ef6\u3002 \u3000\u3000\u201c\u4f60\u89c9\u5f97\u8fd9\u91cc\u6709\u70b9\u602a\u602a\u7684\uff0c\u5c31\u56e0\u4e3a\u8fd9\u91cc\u592a\u5e72\u51c0\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u4e5f\u540c\u65f6\u89e3\u91ca\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u91cc\u901a\u5e38\u90fd\u53ea\u6709\u9ec4\u91d1\u548c\u6b7b\u4eba\u3002\u201d \u3000\u3000\u9ec4\u91d1\u786e\u5b9e\u662f\u4e16\u4e0a\u6742\u8d28\u6700\u5c11\u7684\u4e00\u79cd\u4e1c\u897f\u3002 \u3000\u3000\u6700\u7eaf\u51c0\u7684\u4e00\u79cd\u4e1c\u897f\u3002 \u3000\u3000\u800c\u4e14\u5927\u591a\u6570\u4eba\u90fd\u8ba4\u4e3a\u5b83\u662f\u6700\u53ef\u7231\u7684\u4e00\u79cd\u3002 \u3000\u3000\u6b7b\u4eba\u672c\u6765\u4e5f\u662f\u4eba\u3002 \u3000\u3000\u4e0d\u7ba1\u591a\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u6b7b\u4e86\u4e4b\u540e\u5c31\u6ca1\u6cd5\u5b50\u518d\u4f24\u5bb3\u5230\u4efb\u4f55\u4eba\u4e86\u3002 \u3000\u3000\u4e00\u6761\u7528\u9ec4\u91d1\u5efa\u9020\u6210\u7684\u901a\u9053\u3002 \u3000\u3000\u4e00\u4e9b\u518d\u4e5f\u4e0d\u80fd\u4f24\u5bb3\u5230\u522b\u4eba\u7684\u6b7b\u4eba\u3002 \u3000\u3000\u672c\u6765\u5e76\u6ca1\u6709\u4ec0\u4e48\u8ba9\u4eba\u89c9\u5f97\u5bb3\u6015\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u662f\u9f50\u5c0f\u71d5\u5ffd\u7136\u89c9\u5f97\u8fd9\u79cd\u5730\u65b9\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u53ef\u6016\u4e4b\u5904\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u80fd\u5f00\u53e3\u95ee\uff1a \u3000\u3000\u201c\u8fd9\u5730\u65b9\u662f\u4e2a\u575f\u5893\uff1f\u201d \u3000\u3000\u201c\u575f\u5893\uff1f\u201d \u3000\u3000\u5415\u4e09\u5927\u7b11\uff1a \u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u60f3\u5230\u8fd9\u91cc\u662f\u4e2a\u575f\u5893\uff1f\u4f60\u600e\u4e48\u4f1a\u60f3\u5230\u6211\u80af\u7528\u9ec4\u91d1\u66ff\u522b\u4eba\u5efa\u9020\u575f\u5893\uff1f\u201d \u3000\u3000\u4ed6\u5f88\u5c11\u8fd9\u4e48\u6837\u5927\u7b11\u8fc7\u3002 \u3000\u3000\u8981\u4ed6\u8fd9\u79cd\u4eba\u7528\u9ec4\u91d1\u6765\u66ff\u522b\u4eba\u5efa\u9020\u575f\u5893\uff0c\u786e\u5b9e\u662f\u4ef6\u5f88\u53ef\u7b11\u7684\u4e8b\u3002 \u3000\u3000\u2014\u2014\u4e0d\u7ba1\u8981\u4ec0\u4e48\u4eba\u7528\u9ec4\u91d1\u6765\u66ff\u522b\u4eba\u5efa\u9020\u575f\u5893\uff0c\u90fd\u540c\u6837\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff1a \u3000\u3000\u5982\u679c\u8fd9\u91cc\u4e0d\u662f\u575f\u5893\uff0c\u600e\u4e48\u4f1a\u7ecf\u5e38\u6709\u6b7b\u4eba\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u9f50\u5c0f\u71d5\u53c8\u60f3\u4e0d\u901a\u4e86\uff01 \u3000\u3000\u9f50\u5c0f\u71d5\u95ee\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u662f\u4e2a\u5b9d\u5e93\u3002\u201d \u3000\u3000\u5415\u4e09\u7684\u56de\u7b54\u4f7f\u5f97\u9f50\u5c0f\u71d5\u66f4\u60ca\u5947\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u8fd9\u91cc\u662f\u4e2a\u5b9d\u5e93\uff1f\u201d\u9f50\u5c0f\u71d5\u95ee\uff1a \u3000\u3000\u201c\u662f\u4f60\u85cf\u5b9d\u7684\u5b9d\u5e93\uff1f\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u7528\u6307\u5c16\u8f7b\u629a\u901a\u9053\u4e24\u58c1\u7684\u91d1\u7816\u3002 \u3000\u3000\u5c31\u50cf\u662f\u4e00\u4e2a\u9a84\u50b2\u7684\u6bcd\u4eb2\u5728\u629a\u6478\u5979\u7684\u72ec\u751f\u5b50\u4e00\u6837\u3002 \u3000\u3000\u795e\u60c5\u4e2d\u751a\u81f3\u8fd8\u5e26\u7740\u4e9b\u56e0\u5f97\u610f\u6ee1\u8db3\u800c\u751f\u51fa\u7684\u611f\u89e6\u3002 \u3000\u3000\u201c\u6211\u53ef\u4ee5\u4fdd\u8bc1\u6211\u8fd9\u91cc\u50a8\u5b58\u7684\u9ec4\u91d1\uff0c\u81f3\u5c11\u6bd4\u4e16\u4e0a\u4efb\u4f55\u4e00\u4e2a\u5730\u65b9\u90fd\u591a\u4e09\u500d\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u5982\u679c\u6211\u5c06\u8fd9\u91cc\u7684\u9ec4\u91d1\u629b\u552e\u51fa\u53bb\uff0c\u4e16\u4e0a\u6bcf\u4e00\u4e2a\u56fd\u5ea6\u91cc\u9ec4\u91d1\u7684\u4ef7\u683c\u90fd\u4f1a\u4e0b\u843d\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u4e5f\u5fcd\u4e0d\u4f4f\u7528\u6307\u5c16\u8f7b\u629a\u58c1\u4e0a\u7684\u91d1\u7816\uff1a \u3000\u3000\u201c\u6211\u8fd9\u4e00\u751f\u4e2d\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e48\u591a\u9ec4\u91d1\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u975e\u4f46\u4f60\u6ca1\u6709\u89c1\u8fc7\uff0c\u89c1\u8fc7\u8fd9\u4e9b\u9ec4\u91d1\u7684\u4eba\u6050\u6015\u8fd8\u6ca1\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u91cc\u901a\u5e38\u90fd\u53ea\u6709\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u9664\u4e86\u5f88\u7279\u522b\u7684\u60c5\u51b5\u4e4b\u5916\uff0c\u8fd9\u91cc\u901a\u5e38\u90fd\u53ea\u6709\u6b7b\u4eba\u624d\u80fd\u8fdb\u6765\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u95ee\uff1a \u3000\u3000\u201c\u4f60\u901a\u5e38\u90fd\u7528\u6b7b\u4eba\u6765\u770b\u5b88\u4f60\u7684\u9ec4\u91d1\uff1f\u201d \u3000\u3000\u5415\u4e09\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u95ee\u5f97\u786e\u5b9e\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u81ea\u53e4\u4ee5\u6765\uff0c\u4e16\u4e0a\u53ea\u6709\u4e00\u79cd\u4eba\u4f1a\u7528\u6b7b\u4eba\u6765\u770b\u5b88\u4ed6\u7684\u9ec4\u91d1\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a \u3000\u3000\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u201c\u6b7b\u4eba\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u53ea\u6709\u6b7b\u4eba\u624d\u4f1a\u7528\u6b7b\u4eba\u770b\u5b88\u4ed6\u7684\u9ec4\u91d1\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff0c\u9ec4\u91d1\u662f\u4e0d\u662f\u4f1a\u88ab\u76d7\u8d70\uff0c\u5bf9\u4ed6\u90fd\u5df2\u4e0d\u91cd\u8981\u3002\u201d \u3000\u3000\u4ed6\u7684\u56de\u7b54\u5e76\u4e0d\u53ef\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u6837\u7684\u4f8b\u5b50\u975e\u4f46\u4ee5\u524d\u5c31\u6709\u8fc7\uff0c\u4ee5\u540e\u4e5f\u4e00\u5b9a\u8fd8\u4f1a\u6709\u3002 \u3000\u3000\u2014\u2014\u53e4\u5f80\u4eca\u6765\u7684\u738b\u4faf\u8d35\u65cf\u6b7b\u4e86\u4e4b\u540e\uff0c\u901a\u5e38\u90fd\u4f1a\u4ee5\u9ec4\u91d1\u6b89\u846c\u3002 \u3000\u3000\u518d\u4ee5\u4ed6\u5c5e\u4e0b\u6700\u82f1\u52c7\u5fe0\u5fc3\u7684\u536b\u58eb\u966a\u846c\u3002 \u3000\u3000\u6765\u770b\u5b88\u4ed6\u7684\u9ec4\u91d1\u548c\u7075\u9b42\u3002 \u3000\u3000\u2014\u2014\u4ed6\u81ea\u5df1\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u4ed6\u8fd9\u79cd\u505a\u6cd5\u6709\u591a\u4e48\u611a\u8822\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u6ca1\u6709\u6b7b\uff0c\u81f3\u5c11\u73b0\u5728\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d\u5415\u4e09\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u8fd8\u4e0d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4f46\u5979\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a \u3000\u3000\u201c\u65e2\u7136\u8fd9\u91cc\u662f\u4f60\u7684\u5b9d\u5e93\uff0c\u4f60\u7684\u5b9d\u5e93\u91cc\u600e\u4e48\u4f1a\u7ecf\u5e38\u6709\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u5c31\u4e0d\u662f\u53ef\u7b11\u7684\u95ee\u9898\u4e86\u3002 \u3000\u3000\u5927\u591a\u6570\u4eba\u90fd\u4f1a\u8fd9\u4e48\u95ee\u7684\u3002 \u3000\u3000\u5415\u4e09\u7684\u56de\u7b54\u5374\u662f\u5927\u591a\u6570\u4eba\u90fd\u4e0d\u80fd\u660e\u4e86\u7684\u3002 \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u8fd9\u91cc\u662f\u5b9d\u5e93\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u6240\u4ee5\u8fd9\u91cc\u624d\u4f1a\u6709\u6b7b\u4eba\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6709\u79cd\u6b7b\u4eba\u7684\u4ef7\u503c\u8fdc\u6bd4\u9ec4\u91d1\u8fd8\u5927\u5f97\u591a\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u201c\u6211\u8fd9\u91cc\u7684\u6b7b\u4eba\u90fd\u662f\u8fd9\u4e00\u79cd\u3002\u201d \u3000\u3000\u4eba\u6b7b\u4e86\u4e4b\u540e\u8fd8\u6709\u4ec0\u4e48\u4ef7\u503c\uff1f\u201d \u3000\u3000\u8fd8\u6709\u4ec0\u4e48\u7528\uff1f \u3000\u3000\u5415\u4e09\u81ea\u5df1\u5927\u6982\u4e5f\u77e5\u9053\u8fd9\u79cd\u8bf4\u6cd5\u5f88\u96be\u8ba9\u4eba\u7406\u89e3\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u7b49\u9f50\u5c0f\u71d5\u518d\u95ee\uff0c\u5c31\u5ffd\u7136\u6539\u53d8\u4e86\u8bdd\u9898\u3002 \u3000\u3000\u201c\u5728\u6781\u897f\u7684\u897f\u65b9\uff0c\u4e5f\u6709\u4e00\u4e9b\u5386\u53f2\u6781\u60a0\u4e45\u7684\u53e4\u8001\u56fd\u5bb6\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u5728\u90a3\u4e9b\u56fd\u5bb6\u91cc\uff0c\u4e5f\u6709\u4e00\u4e9b\u5b66\u77e5\u6781\u6e0a\u535a\u7684\u667a\u8005\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0f \u3000\u3000\u9f50\u5c0f\u71d5\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\u4e00\u70b9\u3002\u90a3\u4e9b\u56fd\u5bb6\u4e5f\u8ddf\u6211\u4eec\u4e00\u6837\uff0c\u4e5f\u6709\u6cd5\u5f8b\u548c\u5b97\u6559\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u5728\u4ed6\u4eec\u4fe1\u5949\u7684\u5b97\u6559\u91cc\uff0c\u4e5f\u6709\u5fb7\u9ad8\u671b\u91cd\u7684\u957f\u8001\u3002\u5c31\u597d\u50cf\u6211\u4eec\u5c11\u6797\u6b66\u5b66\u7684\u62a4\u6cd5\u957f\u8001\u4e00\u6837\u3002\u6211\u77e5\u9053\u5176\u4e2d\u6709\u4e00\u4f4d\u2018\u5fb7\u957f\u8001\u2019\u5c31\u662f\u4e2a\u6781\u6709\u667a\u6167\u3001\u6781\u53d7\u4eba\u5c0a\u656c\u7684\u4eba\uff0c\u5c31\u597d\u50cf\u6614\u5e74\u5c11\u6797\u7684\u62a4\u6cd5\u5927\u5e08\u2018\u5fc3\u7709\u2019\u4e00\u6837\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u5f53\u7136\u4e5f\u542c\u8bf4\u8fc7\u5fc3\u7709\u5927\u5e08\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u5415\u4e09\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u7684\u5e08\u5085\u662f\u88ab\u6bd2\u6b7b\u7684\uff0c\u6240\u4ee5\u4ed6\u9664\u4e86\u7cbe\u7814\u4f5b\u5b66\u548c\u6b66\u9053\u5916\uff0c\u5bf9\u6bd2\u836f\u4e5f\u7814\u7a76\u5f97\u6781\u900f\u5f7b\uff0c\u751a\u81f3\u4e0d\u60dc\u4ee5\u8089\u8eab\u904d\u8bd5\u767e\u6bd2\uff0c\u751a\u81f3\u6709\u4eba\u8bf4\u4ed6\u5230\u665a\u5e74\u65f6\u7adf\u5df2\u7ec3\u6210\u767e\u6bd2\u4e0d\u4fb5\u7684\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\u3002\u201d \u3000\u3000\u201c\u5fb7\u957f\u8001\u7684\u60c5\u51b5\u4e5f\u548c\u5fc3\u7709\u5927\u5e08\u4e00\u6837\u3002\u201d\u5415\u4e09\u8bf4\uff1a\u6240\u4ee5\u6211\u624d\u4f1a\u63d0\u8d77\u4ed6\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u66fe\u7ecf\u8bf4\u8fc7\u4e00\u4ef6\u975e\u5e38\u6709\u8da3\u7684\u4e8b\u3002\u201d \u3000\u3000\u5415\u4e09\u4e0d\u7b49\u9f50\u5c0f\u71d5\u518d\u95ee\u4ed6\uff0c\u8fd9\u4ef6\u6709\u8da3\u7684\u4e8b\u548c\u5979\u7684\u95ee\u9898\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u5c31\u5df2\u7ecf\u628a\u8fd9\u4ef6\u4e8b\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u90a3\u4f4d\u5fb7\u957f\u8001\u6709\u4e2a\u975e\u5e38\u597d\u7684\u679c\u56ed\uff0c\u56ed\u91cc\u79cd\u6ee1\u4e86\u5404\u79cd\u82b1\u5349\u3001\u6c34\u679c\u548c\u852c\u83dc\uff0c\u4ed6\u66fe\u7ecf\u5728\u4ed6\u7684\u679c\u56ed\u91cc\u4f5c\u4e86\u4e00\u6b21\u975e\u5e38\u6709\u8da3\u7684\u8bd5\u9a8c\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u4ed6\u5728\u679c\u56ed\u91cc\u9009\u4e86\u4e00\u79cd\u6700\u666e\u901a\u7684\u852c\u83dc\uff09\u8b6c\u5982\u8bf4\u662f\u4e00\u68f5\u5377\u5fc3\u83dc\uff0c\u7136\u540e\u4ed6\u5c31\u7528\u4e00\u79cd\u542b\u6709\u5267\u6bd2\u7684\u84b8\u998f\u6c34\uff0c\u53bb\u6d47\u8fd9\u68f5\u5377\u5fc3\u83dc\uff0c\u4e00\u8fde\u6d47\u4e86\u4e09\u5929\uff0c\u5377\u5fc3\u83dc\u7684\u53f6\u5b50\u5c31\u53d8\u9ec4\u4e86\uff0c\u800c\u4e14\u6e10\u6e10\u67af\u840e\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u7136\u540e\u4ed6\u53c8\u7528\u8fd9\u68f5\u5377\u5fc3\u83dc\uff0c\u53bb\u5582\u4e00\u53ea\u5154\u5b50\uff0c\u4e09\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u8fd9\u53ea\u5154\u5b50\u5c31\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u4ed6\u53eb\u4ed6\u7684\u56ed\u4e01\u628a\u8fd9\u4e2a\u6b7b\u5154\u5b50\u7684\u5185\u810f\uff0c\u638f\u51fa\u6765\u53bb\u5582\u4e00\u53ea\u6bcd\u9e21\uff0c\u7b2c\u4e8c\u5929\u6bcd\u9e21\u5c31\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u5c31\u5728\u8fd9\u53ea\u6bcd\u9e21\u4f5c\u5782\u6b7b\u7684\u6323\u624e\u65f6\uff0c\u6070\u5de7\u6709\u4e00\u53ea\u8001\u9e70\u98de\u8fc7\uff0c\u5728\u5fb7\u957f\u8001\u5c45\u4f4f\u7684\u5730\u65b9\uff0c\u8001\u9e70\u662f\u5f88\u591a\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u8001\u9e70\u628a\u6b7b\u9e21\u6293\u5230\u5ca9\u77f3\u4e0a\uff0c\u5f53\u70b9\u5fc3\u5403\u4e86\u540e\uff0c\u5c31\u89c9\u5f97\u5f88\u4e0d\u8212\u670d\uff0c\u4e09\u5929\u540e\u6b63\u5728\u7a7a\u4e2d\u98de\u7fd4\u65f6\uff0c\u7a81\u7136\u6389\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a \u3000\u3000\u201c\u5fb7\u957f\u8001\u53c8\u8981\u4ed6\u7684\u56ed\u4e01\u627e\u5230\u4e86\u8fd9\u53ea\u8001\u9e70\uff0c\u629b\u5165\u9c7c\u5858\u91cc\uff0c\u5858\u91cc\u7684\u9cd7\u9c7c\u3001\u9ca4\u9c7c\u548c\u68ad\u5b50\u9c7c\uff0c\u90fd\u662f\u5f88\u8d2a\u5403\u7684\uff0c\u5f53\u7136\u4f1a\u628a\u8001\u9e70\u7684\u8089\u5927\u5403\u4e00\u987f\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u8bf4\u7b2c\u4e8c\u5929\u6709\u4e00\u5c3e\u68ad\u5b50\u9c7c\uff0c\u88ab\u9001\u4e0a\u4f60\u7684\u996d\u684c\u53bb\u62db\u5f85\u4f60\u7684\u8d35\u5ba2\uff0c\u90a3\u4e48\u8fd9\u4f4d\u5ba2\u5728\u7b2c\u516b\u5929\u6216\u8005\u7b2c\u5341\u5929\u4e4b\u540e\uff0c\u5c31\u4f1a\u56e0\u80a0\u80c3\u6e83\u70c2\u800c\u6b7b\u3002\u5c31\u7b97\u662f\u6700\u6709\u7ecf\u9a8c\u7684\u540d\u533b\u548c\u4ef6\u4f5c\uff0c\u4e5f\u7edd\u5bf9\u68c0\u67e5\u4e0d\u51fa\u4ed6\u7684\u6b7b\u56e0\uff0c\u66f4\u4e0d\u4f1a\u60f3\u5230\u4ed6\u662f\u88ab\u4ec7\u4eba\u6bd2\u6740\u800c\u6b7b\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u8bf4\uff1a\u201c\u8fd9\u4e2a\u79d8\u5bc6\u4e5f\u8bb8\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u6709\u4eba\u77e5\u9053\uff0c\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5415\u4e09\u5ffd\u7136\u4e0d\u518d\u5f80\u4e0b\u8bf4\u4e86\u3002 \u3000\u3000\u53ef\u662f\u542c\u5230\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u9f50\u5c0f\u71d5\u5df2\u7ecf\u5fcd\u4e0d\u4f4f\u8981\u542c\u4e0b\u53bb\uff0c\u5fcd\u4e0d\u4f4f\u95f4\u201c\u9664\u975e\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5415\u4e09\u5fae\u7b11\u9053\uff1a\u201c\u9664\u975e\u8fd9\u4e2a\u6b7b\u4eba\u88ab\u9001\u5230\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u96be\u9053\u4f60\u80fd\u627e\u51fa\u4ed6\u7684\u6b7b\u56e0\uff1f\u201d \u3000\u3000\u5415\u4e09\u9053\uff1a\u201c\u5982\u679c\u6211\u80fd\u53ca\u65f6\u5256\u5f00\u4ed6\u7684\u5c38\u4f53\uff0c\u627e\u5230\u4ed6\u80a0\u80c3\u4e2d\u6b8b\u5b58\u7684\u68ad\u5b50\u9c7c\uff0c\u90a3\u4e48\u6211\u975e\u4f46\u80fd\u627e\u51fa\u4ed6\u7684\u6b7b\u56e0\uff0c\u800c\u4e14\u8fd8\u80fd\u627e\u51fa\u6bd2\u6740\u4ed6\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u60a0\u7136\u63a5\u7740\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u4e2a\u6b7b\u4eba\u7684\u4ef7\u503c\uff0c\u5c31\u8fdc\u80dc\u4e8e\u9ec4\u91d1\u4e86\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8fd8\u662f\u4e0d\u592a\u61c2\u3002 \u3000\u3000\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u4e09\u9053\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e0d\u4f46\u4ece\u8fd9\u4e2a\u6b7b\u4eba\u8eab\u4e0a\u53d1\u73b0\u4e00\u4ef6\u672c\u6765\u7edd\u4e0d\u4f1a\u6709\u4eba\u77e5\u9053\u7684\u79d8\u5bc6\uff0c\u8fd8\u56e0\u6b64\u800c\u77e5\u9053\u4e86\u4e00\u79cd\u80fd\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5c06\u4eba\u6bd2\u6740\u81f4\u6b7b\u7684\u5de7\u5999\u65b9\u6cd5\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u6bd2\u6740\u4ed6\u7684\u90a3\u4e2a\u4eba\u79d8\u5bc6\u88ab\u4f60\u53d1\u73b0\u540e\uff0c\u5f53\u7136\u4e5f\u4e0d\u80fd\u4e0d\u542c\u4f60\u7684\u8bdd\u4e86\u3002\u201d\u3002 \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5415\u4e09\u7b11\u5f97\u66f4\u6109\u5feb\uff01 \u3000\u3000\u201c\u4e8b\u60c5\u7684\u7ed3\u679c\u4e00\u5b9a\u5c31\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u8bf4\uff1a\u201c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6709\u5f88\u591a\u6b7b\u4eba\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u6709\u7684\u4e2d\u4e86\u79d8\u5bc6\u7684\u6bd2\uff0c\u6709\u7684\u4e2d\u4e86\u79d8\u5bc6\u7684\u6697\u5668\uff0c\u6709\u7684\u88ab\u4eba\u7528\u4e00\u79cd\u79d8\u5bc6\u7684\u624b\u6cd5\u6240\u4f24\uff0c\u53ea\u8981\u4ed6\u4eec\u7684\u5c38\u4f53\u5728\u8fd9\u91cc\uff0c\u6211\u5c31\u80fd\u627e\u51fa\u4ed6\u4eec\u81f4\u6b7b\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u5415\u4e09\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u5bf9\u6211\u6765\u8bf4\uff0c\u6bcf\u4e00\u4ef6\u79d8\u5bc6\u8fdf\u65e9\u90fd\u4f1a\u6709\u7528\u7684\uff0c\u6709\u65f6\u751a\u81f3\u8fdc\u6bd4\u9ec4\u91d1\u6709\u7528\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u5df2\u7ecf\u542c\u5f97\u6123\u4f4f\u3002 \u3000\u3000\u624b\u5fc3\u811a\u5e95\u80cc\u810a\u90fd\u5df2\u6c81\u51fa\u51b7\u6c57\u3002 \u3000\u3000\u5415\u4e09\u5728\u8bf4\u8fd9\u4e9b\u4e8b\u7684\u65f6\u5019\uff0c\u8a00\u8bcd\u6001\u5ea6\u8fd8\u662f\u90a3\u4e48\u65af\u6587\u4f18\u96c5\uff0c\u5c31\u597d\u50cf\u4e00\u4f4d\u4f1f\u5927\u7684\u8bd7\u4eba\u5728\u4f4e\u8bf5\u4e00\u9996\u4ed6\u751f\u5e73\u6700\u4f1f\u5927\u7684\u6770\u4f5c\uff0c\u4e00\u9996\u4efb\u4f55\u4eba\u90fd\u786e\u4fe1\u53ef\u4ee5\u7559\u4f20\u5343\u53e4\u7684\u60c5\u8bd7\u3002 \u3000\u3000\u53ef\u662f\u5728\u9f50\u5c0f\u71d5\u773c\u4e2d\u770b\u6765\uff0c\u8fd9\u4e16\u754c\u4e0a\u7edd\u4e0d\u4f1a\u6709\u6bd4\u4ed6\u66f4\u53ef\u6015\u7684\u4eba\u4e86\u3002 \u3000\u3000\u5415\u4e09\u4e5f\u5728\u770b\u7740\u5979\uff0c\u773c\u4e2d\u8fd8\u662f\u5145\u6ee1\u4e86\u6e29\u67d4\u7684\u7b11\u610f\uff0c\u60a0\u7136\u95ee\uff1a\u201c\u4f60\u613f\u4e0d\u613f\u610f\u53bb\u770b\u770b\u6211\u7684\u5b9d\u85cf\uff1f\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u5ffd\u7136\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u773c\u775b\u91cc\u53c8\u53d1\u51fa\u4e86\u5149\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u96cc\u8c79\u3002 \u3000\u3000\u5728\u63a5\u53d7\u6311\u6218\u65f6\u6240\u53d1\u51fa\u7684\u90a3\u79cd\u5149\u4e00\u6837\u3002 \u3000\u3000\u201c\u6211\u5f53\u7136\u613f\u610f\u3002\u201d \u3000\u3000\u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u6211\u4e0d\u6562\u53bb\uff1f\u201d \u3000\u3000\u65e0\u8bba\u591a\u66f2\u6298\u6f2b\u957f\u7684\u8def\uff0c\u603b\u6709\u8d70\u5b8c\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u4eec\u7ec8\u4e8e\u8d70\u5230\u901a\u9053\u7684\u5c3d\u5934\u3002 \u3000\u3000\u901a\u9053\u7684\u5c3d\u5934\u5904\u662f\u4e00\u6247\u95e8\u3002 \u3000\u3000\u4e00\u6247\u6ca1\u6709\u95e8\u73af\u4e5f\u6ca1\u6709\u624b\u67c4\u7684\u95e8\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u4e00\u8d70\u8fc7\u53bb\uff0c\u95e8\u5c31\u5f00\u4e86\u3002 \u3000\u3000\u9f50\u5c0f\u71d5\u53c8\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\u5979\u6240\u770b\u89c1\u7684\u3002\u7adf\u662f\u5979\u5728\u8fd9\u4e00\u77ac\u95f4\u4e4b\u524d\u4ece\u672a\u68a6\u60f3\u80fd\u89c1\u5230\u7684\u5947\u666f\u3002 \u3000\u3000\u95e8\u540e\u662f\u4e00\u4e2a\u5bbd\u9614\u7684\u5c71\u7a9f\uff0c\u770b\u6765\u4eff\u4f5b\u6709\u4e03\u516b\u5341\u4e08\u5bbd\uff0c\u4e03\u516b\u5341\u4e08\u957f\uff0c\u4e03\u516b\u5341\u4e08\u9ad8\uff0c\u53ef\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u7a76\u7adf\u6709\u591a\u5bbd\u591a\u957f\u591a\u9ad8\u3002 \u3000\u3000\u5c71\u7a9f\u7684\u4e0a\u4e0b\u5de6\u53f3\u56db\u58c1\uff0c\u90fd\u780c\u6ee1\u4e86\u5de8\u5927\u7684\u91d1\u7816\u3002 \u3000\u3000\u5c71\u7a9f\u91cc\u6446\u6ee1\u4e86\u4e00\u53e3\u53e3\u7528\u7eaf\u91d1\u94f8\u6210\u7684\u68fa\u6750\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u4f1a\u5728\u540c\u4e00\u4e2a\u5730\u65b9\u770b\u89c1\u8fd9\u4e48\u591a\u68fa\u6750\uff0c\u800c\u4e14\u662f\u7528\u7eaf\u91d1\u94f8\u6210\u7684\u68fa\u6750\u3002 \u3000\u3000\u2014\u2014\u662f\u4e0d\u662f\u6bcf\u4e00\u53e3\u68fa\u6750\u91cc\u90fd\u6709\u4e00\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u79d8\u5bc6\uff1f \u3000\u3000\u7528\u7eaf\u91d1\u94f8\u6210\u7684\u6cb9\u706f\u91cc\uff0c\u95ea\u52a8\u7740\u91d1\u9ec4\u8272\u7684\u706b\u7130\u3002 \u3000\u3000\u95e8\u4e00\u5f00\uff0c\u9f50\u5c0f\u71d5\u5c31\u8d70\u5165\u4e86\u4e00\u4e2a\u8bf4\u4e0d\u51fa\u6709\u591a\u4e48\u707f\u70c2\u8f89\u714c\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u6709\u591a\u4e48\u795e\u79d8\u8be1\u5f02\u7684\u9ec4\u91d1\u4e16\u754c\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u4e16\u4eba\u68a6\u60f3\u96be\u53ca\u7684\u9ec4\u91d1\u4e16\u754c\u3002 \u3000\u3000\u53c8\u504f\u504f\u662f\u4e2a\u6b7b\u4eba\u7684\u4e16\u754c\u3002 \u3000\u3000\u2014\u2014\u68fa\u6750\u662f\u4eba\u4eba\u538c\u6076\u7684\uff0c\u9ec4\u91d1\u662f\u4eba\u4eba\u559c\u7231\u7684\u3002 \u3000\u3000\u4e00\u53e3\u7528\u9ec4\u91d1\u94f8\u6210\u7684\u68fa\u6750\u7ed9\u4eba\u7684\u611f\u89c9\u662f\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u9f50\u5c0f\u71d5\u597d\u50cf\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u90fd\u4f3c\u5b8c\u5168\u9ebb\u6728\u4e86\u3002 \u3000\u3000\u5415\u4e09\u7684\u8138\u4e0a\u5374\u5728\u53d1\u5149\u3002 \u3000\u3000\u4ed6\u4f38\u5f00\u53cc\u81c2\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5c31\u597d\u50cf\u4e16\u4e0a\u53ea\u6709\u8fd9\u91cc\u7684\u6c14\u606f\u624d\u662f\u4ed6\u6240\u559c\u6b22\u7684\uff0c\u4e5f\u53ea\u6709\u8fd9\u91cc\u624d\u662f\u4ed6\u771f\u6b63\u559c\u7231\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u5e26\u7740\u9f50\u5c0f\u71d5\u8d70\u5230\u6700\u524d\u9762\u4e00\u6392\u3002 \u3000\u3000\u6700\u53f3\u9996\u7684\u4e09\u53e3\u68fa\u6750\u524d\uff0c\u7528\u7eaf\u91d1\u94f8\u6210\u7684\u68fa\u6750\uff0c\u8fd8\u6ca1\u6709\u9616\u8d77\u3002 \u3000\u3000\u521a\u624d\u4ed6\u6d3e\u6765\u6740\u4eba\u7684\u4e09\u4e2a\u4eba\uff0c\u5df2\u7ecf\u6b7b\u5728\u68fa\u6750\u91cc\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u6b7b\u5f97\u4eff\u4f5b\u90fd\u5f88\u5e73\u9759\uff0c\u8138\u4e0a\u65e2\u6ca1\u6709\u72f0\u72de\u60ca\u6016\u7684\u8868\u60c5\uff0c\u8eab\u4e0a\u4e5f\u6ca1\u6709\u9c9c\u8840\u6dcb\u6f13\u7684\u4f24\u53e3\u3002 \u3000\u3000\u751a\u81f3\u8fde\u8863\u670d\u90fd\u597d\u50cf\u4ed6\u4eec\u521a\u8d70\u8fdb\u6765\u65f6\u4e00\u6837\u5b8c\u5b8c\u6574\u5e72\u51c0\uff0c\u4ed6\u4eec\u6b7b\u7684\u65f6\u5019\uff0c\u663e\u7136\u5e76\u6ca1\u6709\u75db\u82e6\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u786e\u5b9e\u90fd\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000------------------ \u3000\u3000 \u7b2c\u56db\u56db\u7ae0 \u770b\u6b7b\u4eba \u7b2c\u56db\u5341\u56db\u7ae0 \u770b\u6b7b\u4eba \u2014\u2014\u4ed6\u4eec\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u2014\u2014\u662f\u8c01\u6740\u6b7b\u4e86\u4ed6\u4eec\uff1f \u2014\u2014\u6740\u4eba\u7684\u4eba\u5462\uff1f \u5415\u4e09\u4e00\u76f4\u7ad9\u5728\u8fd9\u4e09\u53e3\u68fa\u6750\u65c1\uff0c\u805a\u7cbe\u4f1a\u795e\u5730\u770b\u7740\u68fa\u6751\u91cc\u8fd9\u4e09\u4e2a\u6b7b\u4eba\u3002 \u4ed6\u7684\u8138\u4e0a\u4e00\u5411\u5f88\u5c11\u6709\u7684\u8868\u60c5\u3002 \u4e00\u4e2a\u6709\u4fee\u517b\u7684\u7ec5\u58eb\u672c\u6765\u5c31\u4e0d\u8be5\u628a\u5fc3\u91cc\u7684\u611f\u89c9\u76f4\u9732\u5728\u8138\u4e0a\u8ba9\u4eba\u770b\u51fa\u6765\u3002 \u73b0\u5728\u4ed6\u8138\u4e0a\u5374\u6709\u4e86\u4eba\u4eba\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\u7684\u8868\u60c5\u3002 \u5947\u602a\u7684\u662f\uff0c\u4ed6\u7684\u8868\u60c5\u65e2\u4e0d\u662f\u60b2\u75db\u611f\u4f24\u3002 \u4e5f\u4e0d\u662f\u60ca\u8bb6\u6124\u6012\u3002 \u53cd\u800c\u597d\u50cf\u89c9\u5f97\u5341\u5206\u6109\u5feb\u6b22\u559c\u3002 \u8fc7\u4e86\u5f88\u4e45\u4e4b\u540e\uff0c\u4ed6\u624d\u957f\u957f\u53f9\u5382\u53e3\u6c14\uff0c\u54fa\u54fa\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u5b66\u5251\u7684\u4eba\uff0c\u80fd\u6b7b\u5728\u8fd9 \u4e48\u6837\u4e00\u4e2a\u4eba\u7684\u5251\u4e0b\uff0c\u4e5f\u5e94\u8be5\u6b7b\u800c\u65e0\u61be\u4e86\u3002\u201d \u4ed6\u81ea\u5df1\u5927\u6982\u4e5f\u77e5\u9053\u81ea\u5df1\u8138\u4e0a\u7684\u8868\u60c5\u548c\u8bf4\u8bdd\u7684\u53e3\u6c14\u5f88\u4e0d\u914d\u5408\u3002 \u6240\u4ee5\u5ffd\u7136\u6539\u53d8\u4e86\u8bdd\u9898\uff0c\u5ffd\u7136\u95f4\u9f50\u5c0f\u71d5\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u770b\u51fa\u4ed6\u4eec\u81f4\u547d\u7684\u4f24\u53e3\u5728\u54ea\u91cc\uff1f\u201d \u9f50\u5c0f\u71d5\u5f53\u7136\u770b\u51fa\u6765\u4e86\u3002 \u4e09\u4e2a\u4eba\u81f4\u547d\u7684\u4f24\u53e3\u90fd\u5728\u5fc5\u7136\u81f4\u547d\u7684\u8981\u5bb3\u5904\u3002 \u662f\u5251\u4f24\u3002 \u6740\u4ed6\u4eec\u7684\u4eba\u4e00\u5251\u547d\u4e2d\u540e\uff0c\u5c31\u6ca1\u6709\u518d\u591a\u7528\u4e00\u5206\u529b\u3002 \u6240\u4ee5\u4f24\u53e3\u5e76\u4e0d\u5927\uff0c\u6d41\u7684\u8840\u4e5f\u4e0d\u591a\u3002 \u6740\u4eba\u7684\u8fd9\u4e2a\u4eba\u5251\u6cd5\u65e0\u7591\u5df2\u51fa\u795e\u5165\u5316\u3002 \u4e00\u5251\u523a\u51fa\u975e\u4f46\u7edd\u5bf9\u51c6\u786e\u81f4\u547d\u3002 \u529b\u91cf\u4e5f\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\uff0c\u7edd\u6ca1\u6709\u865a\u8017\u4e00\u5206\u529b\u6c14\u3002 \u9f50\u5c0f\u71d5\u65e0\u7591\u5df2\u7ecf\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\u3002 \u53ef\u662f\u5415\u4e09\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u5979\u4e5f\u6ca1\u6709\u8bf4\u3002 \u5415\u4e09\u5ffd\u7136\u53c8\u5c06\u5979\u5e26\u5230\u540e\u9762\u4e00\u6392\u53e6\u5916\u4e09\u53e3\u68fa\u6750\u524d\u9762\u3002 \u68fa\u6750\u91cc\u4e5f\u6709\u4e09\u4e2a\u6b7b\u4eba\u3002 \u4e00\u4e2a\u5e74\u8f7b\uff0c\u4e00\u4e2a\u5e74\u8f7b\u6bd4\u8f83\u5927\u4e9b\uff0c\u53e6\u4e00\u4e2a\u5df2\u8fd1\u4e2d\u5e74\u3002 \u4e0d\u4f46\u88c5\u675f\u5e74\u7eaa\u548c\u521a\u624d\u90a3\u4e09\u4e2a\u4eba\u5dee\u4e0d\u591a\uff0c\u800c\u4e14\u8eab\u4e0a\u4e5f\u6ca1\u6709\u9c9c\u8840\u6dcb\u6f13\u7684\u4f24\u53e3\u3002 \u8138\u4e0a\u4e5f\u6ca1\u6709\u4ec0\u4e48\u75db\u82e6\u7684\u8868\u60c5\u3002 \u663e\u7136\u4e5f\u662f\u88ab\u4eba\u4e00\u5251\u523a\u4f24\uff0c\u7acb\u523b\u81f4\u547d\u7684\u3002 \u552f\u4e00\u4e0d\u540c\u7684\u662f\uff1a \u8fd9\u4e09\u4e2a\u4eba\u90fd\u5df2\u6b7b\u4e86\u5f88\u4e45\uff0c\u6700\u5c11\u5df2\u7ecf\u6709\u4e00\u4e24\u5929\u4e86\u3002 \u9f50\u5c0f\u71d5\u4ece\u6765\u90fd\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e09\u4e2a\u4eba\u3002 \u4e5f\u4e0d\u60f3\u95ee\u4ed6\u4eec\u662f\u8c01\uff1f \u5415\u4e09\u5374\u4e3b\u52a8\u544a\u8bc9\u5979\uff1a \u201c\u4ed6\u4eec\u4e5f\u662f\u6211\u7684\u5c5e\u4e0b\u3002 \u4ed6\u4eec\u6d3b\u7740\u65f6\u7684\u4ee3\u53f7\u662f\u2018\u4e09\u53f7\u2019\u3001 \u2018\u5341\u4e09\u53f7\u2019\u3001\u2018\u4e8c\u5341\u4e09\u53f7\u2019\u3002\u4ed6\u4eec\u672c\u6765\u4e5f\u53ef\u4ee5\u7b97\u662f\u4e00\u6d41\u7684\u5251\u5ba2\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u624d\u4f1a\u6d3e\u4ed6\u4eec\u53bb\u523a\u6740\u5c0f\u65b9\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6b7b\u5728\u5c0f\u65b9\u5251\u4e0b\u7684\uff1f\u201d \u201c\u662f\u7684\u3002\u201d \u5415\u4e09\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6211\u6d3e\u4ed6\u4eec\u53bb\u523a\u6740\u5c0f\u65b9\u65f6\uff0c\u4e5f\u6b63\u5982\u6211\u521a\u624d\u6d3e\u90a3\u4e09\u4e2a\u5230\u8fd9\u91cc\u6765\u4e00 \u6837\uff0c\u65e9\u5df2\u77e5\u9053\u4ed6\u4eec\u5fc5\u6b7b\u65e0\u7591\u3002\u201d \u4ed6\u6de1\u6de1\u4ed6\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u8fde\u4e00\u70b9\u5185\u759a\u7684\u610f\u601d\u90fd\u6ca1\u6709\u3002 \u9f50\u5c0f\u71d5\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u4f60\u5fe0\u5fc3\u7684\u5c5e\u4e0b\uff0c\u4f60\u660e\u77e5\u4ed6\u4eec\u5fc5\u6b7b\uff0c\u4e3a\u4ec0\u4e48\u8981\u4ed6\u4eec \u53bb\u9001\u6b7b\uff1f\u201d \u5415\u4e09\u53c8\u6de1\u6de1\u5730\u7b11\u4e86\u7b11\uff01 \u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4ed6\u53cd\u6b63\u8fdf\u65e9\u8981\u4e3a\u6211\u6b7b\u7684\uff0c\u4ed6\u4eec\u81ea\u5df1\u90fd\u89c9\u5f97\u6b7b\u800c\u65e0\u61be\uff0c\u6211\u53c8\u4f55\u5fc5\u4e3a \u4ed6\u4eec\u96be\u53d7\uff1f\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u53ef\u662f\u4f60\u7edd\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u8ba9\u4f60\u516d\u4e2a\u5f97\u529b\u7684\u5c5e\u4e0b\u9001\u6b7b\u7684\u201d \u4e24\u4eba\u4e92\u76f8\u51dd\u89c6\u3002 \u773c\u4e2d\u90fd\u9732\u51fa\u4e00\u79cd\u4e92\u76f8\u4e86\u89e3\u7684\u8868\u60c5\u3002 \u5415\u4e09\u5374\u53c8\u6539\u53d8\u4e86\u8bdd\u9898\u95ee\uff1a\u201c\u4f60\u770b\u4e0d\u770b\u5f97\u51fa\u8fd9\u4e09\u4e2a\u4eba\u4eba\u7684\u81f4\u547d\u4f24\u53e3\u5728\u54ea\u91cc\uff1f\u201d \u8fd9\u4e09\u4e2a\u7684\u81f4\u547d\u4f24\u53e3\u4e5f\u5728\u5fc5\u7136\u81f4\u547d\u7684\u8981\u5bb3\u5904\u3002 \u4f24\u53e3\u5f88\u5c0f\uff0c\u6d41\u51fa\u7684\u8840\u4e5f\u4e0d\u591a\u3002 \u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e5f\u770b\u51fa\u6765\u4e86\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u518d\u591a\u770b\u51e0\u773c\uff0c\u770b\u5f97\u4ed4\u7ec6\u4e9b\u3002\u201d \u4ed6\u53c8\u8865\u5145\uff1a\u201c\u4ed6\u6700\u597d\u628a\u8fd9\u8fb9\u4e09\u4e2a\u4eba\u548c\u90a3\u8fb9\u4e09\u4e2a\u4eba\u81f4\u547d\u7684\u4f24\u53e3\u90fd\u4ed4\u7ec6\u518d\u770b\u770b\uff0c\u770b \u5f97\u8d8a\u4ed4\u7ec6\u8d8a\u597d\u3002\u201d \u9f50\u5c0f\u71d5\u6bd5\u7adf\u662f\u4e2a\u5973\u5b69\u5b50\u3002 \u5bf9\u6b7b\u4eba\u591a\u591a\u5c11\u5c11\u603b\u6709\u51e0\u5206\u618e\u538c\u6050\u60e7\u3002 \u5fc3\u91cc\u867d\u7136\u77e5\u9053\u5415\u4e09\u53eb\u5979\u8fd9\u6837\u505a\u5fc5\u6709\u6df1\u610f\u3002 \u5374\u8fd8\u662f\u6447\u4e86\u6447\u5934\u8bf4\uff1a\u201c\u6211\u4e0d\u770b\uff0c\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u770b\u3002\u201d \u5415\u4e09\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u522b\u7684\u6b7b\u4eba\u5f53\u7136\u6ca1\u4ec0\u4e48\u597d\u770b\uff0c\u8fd9\u91cc\u7684\u6b7b\u4eba\u5374\u597d\u770b\u5f97\u5f88\uff0c\u60f3\u6765\u770b \u770b\u4ed6\u4eec\u7684\u4eba\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u4f60\u82e5\u771f\u7684\u4e0d\u770b\uff0c\u5b9e\u5728\u662f\u75db\u5931\u826f\u673a\u3002\u201d \u8fd9\u4e9b\u8bdd\u542c\u6765\u867d\u7136\u8352\u8c2c\u3002 \u5415\u4e09\u8bf4\u5f97\u5374\u6781\u8bda\u6073\u3002 \u9f50\u5c0f\u71d5\u5374\u8fd8\u662f\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u4f60\u53bb\u95ee\u95ee\u72ec\u5b64\u75f4\u5c31\u4f1a\u76f8\u4fe1\u4e86\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u95ee\u4ed6\uff01\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u72ec\u5b64\u75f4\u4eba\u5982\u5176\u540d\uff0c\u4e0d\u4f46\u4e00\u5411\u72ec\u6765\u72ec\u5f80\uff0c\u4e00\u5411\u75f4\u5f97\u5f88\uff0c\u800c\u4e14\u75f4\u7684\u53ea\u662f \u5251\uff0c\u4e0d\u662f\u4eba\uff0c\u6240\u4ee5\u4e0d\u7ba1\u4f60\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff0c\u8ddf\u4ed6\u6709\u4ec0\u4e48\u4ea4\u60c5\uff0c\u90fd\u4f11\u60f3\u8bf4\u52a8\u4ed6\u4e3a\u4f60\u53bb\u505a \u4e00\u4ef6\u5c0f\u4e8b\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\u4ed6\u7684\u813e\u6c14\uff0c\u53ef\u662f\u4ed6\u5374\u505a\u4e86\u4e0d\u5c11\u4ef6\u5927\u4e8b\u3002\u201d \u5415\u4e09\u5fae\u7b11\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u201c\u4ed6\u4e3a\u7684\u5c31\u662f\u8981\u770b\u770b\u8fd9\u91cc\u7684\u6b7b\u4eba\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u4ed6\u672c\u6765\u79bb\u6211\u800c\u53bb\uff0c\u73b0\u5728\u53c8\u53bb\u800c\u590d\u8fd4\uff0c\u4e3a\u7684\u4e5f\u662f\u8981\u770b\u770b\u8fd9\u91cc\u7684\u6b7b\u4eba\u3002\u201d \u9f50\u5c0f\u71d5\u5fc3\u91cc\u867d\u7136\u5df2\u7ecf\u76f8\u4fe1\u4ed6\u8bf4\u7684\u4e0d\u5047\uff0c\u5634\u91cc\u5374\u8fd8\u662f\u8bf4\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u6b7b\u4eba\u6709\u4ec0\u4e48 \u597d\u770b\u7684\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6765\u770b\u8fd9\u4e9b\u6b7b\u4eba\uff1f\u201d \u5415\u4e09\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u5fc3\u91cc\u660e\u660e\u5df2\u7ecf\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u8fd8\u8981\u8bf4\u4e0d\u4fe1\uff1f\u201d \u5415\u4e09\u82e6\u7b11\uff1a\u201c\u5973\u4eba\u4eec\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u53e3\u662f\u5fc3\u975e\u5462\uff1f\u201d \u9f50\u5c0f\u71d5\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\uff01 \u201c\u56e0\u4e3a\u5973\u4eba\u5c31\u662f\u5973\u4eba\uff0c\u603b\u662f\u8ddf\u7537\u4eba\u6709\u70b9\u4e0d\u540c\u7684\uff0c\u4f55\u51b5\u7537\u4eba\u4eec\u8bf4\u8bdd\u53e3\u662f\u5fc3\u975e\u7684\u4e5f \u4e0d\u89c1\u5f97\u6bd4\u5973\u4eba\u5c11\u3002\u201d \u5415\u4e09\u5927\u7b11\u3002 \u201c\u597d\uff0c\u8bf4\u5f97\u597d\uff0c\u8bf4\u5f97\u6709\u7406\u3002\u201d \u4ed6\u5ffd\u7136\u62c9\u4f4f\u9f50\u5c0f\u71d5\u7684\u624b\uff1a\u201c\u6765\uff0c\u6211\u518d\u5e26\u4f60\u53bb\u770b\u4e00\u4e2a\u4eba\u3002\u201d \u8fd9\u4e2a\u4eba\u7684\u68fa\u6750\u5728\u540e\u9762\u7b2c\u4e09\u6392\u7684\u4e2d\u95f4\uff0c\u7d2b\u9762\u866c\u9aef\uff0c\u8eab\u6750\u96c4\u4f1f\u3002 \u867d\u7136\u5df2\u7ecf\u6b7b\u4e86\u5f88\u4e45\uff0c\u5c38\u4f53\u5374\u4ecd\u7136\u4fdd\u6301\u975e\u5e38\u5b8c\u597d\u3002 \u4f9d\u7a00\u53ef\u4ee5\u770b\u51fa\u4ed6\u6d3b\u7740\u65f6\u90a3\u79cd\u4e0d\u53ef\u4e00\u4e16\u7684\u5a01\u731b\u6840\u505a\u7684\u6c14\u52bf\u3002 \u5c38\u4f53\u4e0b\u57ab\u6ee1\u4e86\u4e0a\u597d\u7684\u9632\u8150\u9999\u6599\u3002 \u5728\u624b\u65c1\u8fb9\u653e\u7740\u6761\u5de8\u5927\u7684\u72fc\u7259\u68d2\u3002 \u5bd2\u5149\u95ea\u95ea\u3002 \u5c31\u50cf\u662f\u72fc\u53e3\u4e2d\u7684\u68ee\u68ee\u767d\u7259\uff0c\u663e\u7136\u5c31\u662f\u4ed6\u751f\u524d\u64c5\u4f7f\u7684\u5175\u5668\u3002 \u9f50\u5c0f\u71d5\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u77e5\u9053\u8fd9\u4ef6\u5175\u5668\u81f3\u5c11\u4e5f\u6709\u4e03\u516b\u5341\u65a4\u91cd\uff0c\u81c2\u4e0a\u82e5\u6ca1\u6709\u5343\u65a4\u795e \u529b\uff0c\u4f11\u60f3\u5c06\u5b83\u8fd0\u7528\u5982\u610f\u3002 \u5415\u4e09\u95ee\u5979\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u9f50\u5c0f\u71d5\u6447\u5934\u3002 \u201c\u4f60\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u7684\uff0c\u4f60\u7684\u5e74\u7eaa\u5927\u5c0f\u4e86\u3002\u201d \u5415\u4e09\u53f9\u606f\u9053\uff1a\u201c\u53ef\u662f\u5341\u5e74\u4e4b\u524d\uff0c\u2018\u5929\u72fc\u2019\u90ce\u96c4\u4ee5\u638c\u4e2d\u4e00\u6761\u72fc\u7259\u68d2\u7eb5\u6a2a\u5929\u4e0b\uff0c\u6c5f \u6e56\u4e2d\u8c01\u4eba\u4e0d\u77e5\uff1f\u54ea\u4e2a\u4e0d\u6653\uff0c\u5c24\u5176\u662f\u4f7f\u5251\u7684\u4eba\uff0c\u542c\u5230\u4e86\u4ed6\u7684\u540d\u5b57\u66f4\u662f\u8c08\u72fc\u8272\u53d8\uff0c\u6bd4\u5b69 \u5b50\u4eec\u6015\u8001\u864e\u8fd8\u8981\u6015\u5f97\u5389\u5bb3\u3002\u201d \u9f50\u5c0f\u71d5\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u8bf4\u5c24\u5176\u662f\u4f7f\u5251\u7684\u4eba\uff1f\u201d \u201c\u56e0\u4e3a\u4ed6\u7684\u7236\u6bcd\u90fd\u662f\u6b7b\u5728\u522b\u4eba\u7684\u5251\u4e0b\u7684\uff0c\u6240\u4ee5\u4ed6\u7279\u5730\u6253\u9020\u4e86\u8fd9\u6839\u4efd\u91cf\u5947\u91cd\u7684\u72fc \u7259\u68d2\uff0c\u800c\u4e14\u7ec3\u6210\u4e86\u4e00\u5957\u7279\u522b\u7684\u62db\u5f0f\uff0c\u4e13\u7834\u5929\u4e0b\u5404\u95e8\u5404\u6d3e\u7684\u5251\u6cd5\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u5251\u8d70\u8f7b\u7075\uff0c\u4ed6\u8fd9\u4ef6\u5175\u5668\u6b63\u662f\u5251\u7684\u514b\u661f\u3002\u201d \u5415\u4e09\u53c8\u8bf4\uff1a\u201c\u5f53\u5e74\u5929\u4e0b\u516c\u8ba4\u7684\u524d\u4e94\u5341\u4f4d\u5251\u6cd5\u540d\u5bb6\u4e2d\uff0c\u81f3\u5c11\u6709\u5341\u4e2a\u4eba\u662f\u6b7b\u5728\u4ed6\u8fd9 \u6761\u72fc\u7259\u68d2\u4e4b\u4e0b\u7684\uff0c\u8fde\u6b66\u5f53\u56db\u5251\u4e2d\u7684\u6e05\u98ce\u5b50\u90fd\u96be\u5e78\u514d\u3002\u201d \u9f50\u5c0f\u71d5\u5c45\u7136\u8fd8\u662f\u8bf4\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u5979\u51b7\u51b7\u4ed6\u8bf4\uff1a\u201c\u4ed6\u82e5\u771f\u7684\u8fd9\u4e48\u5389\u5bb3\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u4f1a\u6b7b\u5728\u522b\u4eba\u624b\u91cc\uff1f\u201d \u5415\u4e09\u4e5f\u4e0d\u56de\u7b54\u3002 \u5374\u5c06\u4ed6\u65c1\u8fb9\u7684\u5341\u53e3\u9ec4\u91d1\u68fa\u6750\u4e00\u4e00\u6253\u5f00\u3002 \u9732\u51fa\u4e86\u5341\u4e2a\u6b7b\u4eba\u7684\u5c38\u4f53\u3002 \u8fd9\u4e9b\u4eba\u7684\u5c38\u4f53\u867d\u7136\u4e5f\u90fd\u4fdd\u5b58\u5f97\u6781\u597d\u3002 \u4f46\u662f\u6b7b\u5f97\u5374\u6781\u60e8\u3002 \u5927\u591a\u90fd\u662f\u5934\u9885\u5df2\u88ab\u51fb\u788e\u3002 \u8fd8\u6709\u4e24\u4e2a\u524d\u80f8\u7684\u808b\u9aa8\u90fd\u5df2\u88ab\u51fb\u65ad\u3002 \u6240\u4ee5\u5c38\u4f53\u4fdd\u6301\u5f97\u8d8a\u5b8c\u7f8e\uff0c\u770b\u6765\u53cd\u800c\u66f4\u8be1\u5f02\u53ef\u6015\u3002 \u201c\u8fd9\u5c31\u662f\u6b7b\u5728\u4ed6\u624b\u4e0b\u7684\u5341\u5927\u5251\u6cd5\u9ad8\u624b\u3002\u201d \u5415\u4e09\u6307\u7740\u5176\u4e2d\u4e00\u4e2a\u9ec4\u51a0\u9053\u4eba\uff1a\u201c\u8fd9\u5c31\u662f\u6b66\u5f53\u56db\u5251\u4e2d\u51fa\u624b\u6700\u6bd2\u8fa3\u7280\u5229\u7684\u6e05\u98ce\u5b50\u3002\u201d \u4ed6\u95ee\u9f50\u5c0f\u71d5\uff1a\u201c\u73b0\u5728\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u9f50\u5c0f\u71d5\u95ed\u4e0a\u4e86\u5634\u3002 \u773c\u775b\u5374\u77aa\u5f97\u5927\u5927\u7684\u3002 \u76ef\u7740\u5929\u72fc\u54bd\u5589\u4e0a\u81f4\u547d\u7684\u4f24\u53e3\u3002 \u5ffd\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u662f\u4e0d\u4fe1\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u76f8\u4fe1\uff1f\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4ed6\u7684\u72fc\u7259\u68d2\u5982\u679c\u771f\u7684\u80fd\u7834\u5929\u4e0b\u5404\u79cd\u5251\u6cd5\uff0c\u4ed6\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e5f\u4f1a\u6b7b\u5728 \u522b\u4eba\u7684\u5251\u4e0b\uff1f\u201d \u90ce\u96c4\u54bd\u5589\u4e0a\u7684\u4f24\u53e3\u65e0\u7591\u662f\u5251\u4f24\u3002 \u65e0\u7591\u662f\u88ab\u4eba\u4e00\u5251\u523a\u6740\u800c\u6b7b\u7684\u3002 \u9f50\u5c0f\u71d5\u8fd9\u53e5\u8bdd\u65e0\u6b63\u95ee\u5728\u8282\u9aa8\u773c\u4e0a\u3002 \u4ee4\u4eba\u65e0\u8bdd\u53ef\u7b54\u3002 \u5415\u4e09\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff1a\u201c\u597d\uff0c\u95ee\u5f97\u597d\uff0c\u95ee\u5f97\u6709\u7406\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u95ee\u5f97\u5982\u679c\u771f\u6709\u7406\uff0c\u7b54\u7684\u6050\u6015\u5c31\u672a\u5fc5\u80fd\u6709\u7406\u4e86\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u672a\u5fc5\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u672a\u5fc5\u4ec0\u4e48\uff1f\u201d \u201c\u6709\u7406\u7684\u672a\u5fc5\u5c31\u662f\u6709\u7406\uff0c\u65e0\u7406\u7684\u4e5f\u672a\u5fc5\u5c31\u662f\u65e0\u7406\uff0c\u201d \u5415\u4e09\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u672c\u6765\u5c31\u6ca1\u6709\u5fc5\u7136\u4e0d\u53d8\u7684\u4e8b\uff0c\u6240\u4ee5\u4e13\u7834\u5929\u4e0b\u5251\u6cd5\u7684\u5929\u72fc\u4e5f \u672a\u5fc5\u5c31\u4e0d\u4f1a\u6b7b\u5728\u522b\u4eba\u7684\u5251\u4e0b\u3002\u201d \u9f50\u5c0f\u71d5\u95ee\uff1a\u201c\u4ed6\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u5415\u4e09\u9053\uff1a\u201c\u4ed6\u4f1a\u6b7b\u5728\u522b\u4eba\u7684\u5251\u4e0b\uff0c\u53ea\u56e0\u4e3a\u6709\u4e2a\u75f4\u4e8e\u5251\u7684\u4eba\u5df2\u7ecf\u5230\u4e86\u8fd9\u91cc\uff0c\u5c06\u6b7b \u5728\u4ed6\u624b\u4e0b\u7684\u5341\u4f4d\u5251\u6cd5\u9ad8\u624b\u7684\u5c38\u4f53\u4ed4\u7ec6\u7814\u7a76\u4e86\u4e09\u5e74\uff0c\u5df2\u7ecf\u4ece\u4ed6\u4eec\u81f4\u547d\u7684\u4f24\u53e3\u4e0a\uff0c\u770b\u51fa \u4e86\u5929\u72fc\u90a3\u81f4\u547d\u4e00\u51fb\u7684\u51fa\u624b\u65b9\u4f4d\u548c\u62db\u5f0f\u53d8\u5316\uff0c\u518d\u4ece\u4ed6\u4eec\u672c\u8eab\u7684\u5251\u6cd5\u53d8\u5316\u4e2d\uff0c\u609f\u51fa\u4e86\u5929 \u72fc\u514b\u5236\u4ed6\u4eec\u5251\u6cd5\u7528\u7684\u65b9\u6cd5\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u6240\u4ee5\u4e09\u5e74\u4e4b\u540e\u4ed6\u518d\u627e\u5929\u72fc\u51b3\u6218\u65f6\uff0c\u4e0d\u51fa\u5341\u62db\uff0c\u5c31\u5df2\u5c06\u5929\u72fc\u523a\u6740\u4e8e\u5251 \u4e0b\u3002\u201d \u9f50\u5c0f\u71d5\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u5979\u5f53\u7136\u5df2\u7ecf\u77e5\u9053\u5415\u4e09\u8bf4\u7684\u90a3\u4e2a\u201c\u75f4\u4e8e\u5251\u201d\u7684\u4eba\u662f\u8c01\u4e86\u3002 \u4e5f\u5df2\u7ecf\u77e5\u9053\u72ec\u5b64\u75f4\u4e3a\u4ec0\u4e48\u8981\u5230\u8fd9\u91cc\u6765\u770b\u8fd9\u4e9b\u5df2\u7ecf\u4e0d\u597d\u770b\u7684\u6b7b\u4eba\u3002 \u5415\u4e09\u5374\u8fd8\u662f\u89e3\u91ca\uff1a\u201c\u4e00\u4e2a\u6709\u7ecf\u9a8c\u7684\u4eba\uff0c\u5c31\u4e0d\u96be\u4ece\u4e86\u4e2a\u81f4\u547d\u7684\u4f24\u53e3\u4e0a\u770b\u51fa\u8fd9\u4e2a\u4eba \u5bf9\u624b\u7684\u6b66\u529f\u8def\u6570\uff0c\u751a\u81f3\u8fde\u4ed6\u62db\u5f0f\u7684\u53d8\u5316\uff0c\u51fa\u624b\u7684\u90e8\u4f4d\uff0c\u523a\u51fb\u7684\u65b9\u5411\uff0c\u6240\u7528\u7684\u529b\u91cf\u548c \u901f\u5ea6\u90fd\u4e0d\u96be\u770b\u5f97\u51fa\u6765\u3002\u201d \u4ed6\u53c8\u95ee\u9f50\u5c0f\u71d5\uff1a\u201c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u201c\u6211\u4e0d\u4fe1\u3002\u201d \u9f50\u5c0f\u71d5\u5ae3\u7136\u4e00\u7b11\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u6211\u5fc3\u91cc\u5c31\u7b97\u4e00\u5343\u4e00\u4e07\u4e2a\u76f8\u4fe1\u4e86\uff0c\u5634\u91cc\u4e5f\u8fd8\u662f\u8981 \u8bf4\u4e0d\u4fe1\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u95ee\uff1f\u201d \u72ec\u5b64\u75f4\u662f\u5251\u75f4\u3002 \u5982\u679c\u77e5\u9053\u4e16\u4e0a\u6709\u201c\u5929\u72fc\u201d\u90ce\u96c4\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u3002 \u5f53\u7136\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u90fd\u8981\u51fb\u8d25\u4ed6\u7684\uff0c\u800c\u4e14\u8981\u7528\u5251\u51fb\u8d25\u4ed6\u3002 \u6240\u4ee5\u4ed6\u751a\u81f3\u4e0d\u60dc\u7834\u574f\u81ea\u5df1\u7684\u539f\u5219\u3002 \u6765\u4e3a\u5415\u4e09\u8fd9\u79cd\u4eba\u505a\u4e8b\u3002 \u53ea\u4e0d\u8fc7\u4e8b\u6210\u4e4b\u540e\uff0c\u5c31\u7acb\u523b\u98d8\u7136\u800c\u53bb\u3002 \u5728\u4e24\u5e74\u524d\u90a3\u6b21\u7a7a\u524d\u672a\u6709\u7684\u98ce\u66b4\u4e2d\uff0c\u9ec4\u91d1\u5931\u52ab\u3002 \u94c1\u7ffc\u6218\u6b7b\u3002 \u5c0f\u65b9\u4e5f\u51e0\u4e4e\u88ab\u56f0\u5728\u6c99\u6f20\u91cc\u3002 \u98ce\u66b4\u540e\u521d\u9047\u535c\u9e70\uff0c\u7acb\u523b\u53c8\u88ab\u6c34\u94f6\u548c\u536b\u5929\u9e4f\u6240\u64d2\u3002 \u9001\u5230\u7eff\u6d32\u4e0a\u90a3\u4e2a\u795e\u79d8\u7684\u5e10\u7bf7\u91cc\u3002 \u7b2c\u4e00\u6b21\u89c1\u5230\u72ec\u5b64\u75f4\u7684\u65f6\u5019\uff0c\u4e5f\u6b63\u662f\u72ec\u5b64\u75f4\u5fc3\u613f\u5df2\u4e86\uff0c\u51c6\u5907\u8981\u8d70\u7684\u65f6\u5019\u3002 \u6240\u4ee5\u4ed6\u867d\u7136\u4e00\u76f4\u5728\u51b7\u773c\u65c1\u89c2\uff0c\u6700\u540e\u8fd8\u662f\u6551\u4e86\u5c0f\u65b9\u3002 \u536b\u5929\u9e4f\u548c\u6c34\u94f6\u5f53\u7136\u4e0d\u6562\u963b\u62e6\u3002\u56e0\u4e3a\u90a3\u65f6\u5019\u4ed6\u4eec\u5c31\u5df2\u77e5\u9053\u8fd9\u4e2a\u4eba\u7684\u53ef\u6015\uff0c\u4e5f\u77e5\u9053 \u4ed6\u6839\u672c\u5c31\u4e0d\u5c5e\u4e8e\u5415\u4e09\u201c\u91d1\u624b\u201d\u7684\u7ec4\u7ec7\u3002 \u4e0d\u7ba1\u4ed6\u8981\u505a\u4ec0\u4e48\u4e8b\uff0c\u90fd\u6ca1\u6709\u4eba\u80fd\u591f\u5236\u6b62\u7ba1\u8f96\u4ed6\u3002 \u2014\u2014\u90a3\u6b21\u4ed6\u65e2\u7136\u5df2\u7ecf\u8d70\u4e86\uff0c\u4e3a\u4ec0\u4e48\u53c8\u53bb\u800c\u590d\u8fd4\uff1f \u2014\u2014\u4ed6\u8fd9\u6b21\u56de\u6765\uff1f \u2014\u2014\u96be\u9053\u771f\u7684\u8fd8\u662f\u4e3a\u4e86\u8981\u770b\u770b\u8fd9\u91cc\u7684\u6b7b\u4eba\uff1f \u2014\u2014\u4ece\u8fd9\u4e9b\u6b7b\u4eba\u81f4\u547d\u7684\u4f24\u53e3\u4e0a\uff0c\u770b\u51fa\u53e6\u5916\u4e00\u4e2a\u4eba\u6b66\u529f\u7684\u53d8\u5316\uff0c\u597d\u53bb\u6740\u90a3\u4e2a\u4eba\u3002 \u2014\u2014\u4e0a\u6b21\u4ed6\u8981\u6740\u7684\u662f\u201c\u5929\u72fc\u201d\uff0c\u8fd9\u6b21\u4ed6\u8981\u6740\u7684\u662f\u8c01\uff1f \u2014\u2014\u5c0f\u65b9\uff0c\u8981\u547d\u7684\u5c0f\u65b9\u3002 \u2014\u2014\u4f60\u770b\u7740\u522b\u7684\u5973\u4eba\u65f6\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u662f\u90a3\u79cd\u629b\u4e0d\u5f00\u653e\u4e0d\u4e0b\u7684\u6837\u5b50\uff1f \u2014\u2014\u4f60\u4e3a\u4ec0\u4e48\u8981\u53bb\u770b\u7740\u5979\u4eec\uff1f \u2014\u2014\u4e3a\u4ec0\u4e48\u4e0d\u80af\u591a\u770b\u6211\u4e00\u773c\uff1f \u9f50\u5c0f\u71d5\u770b\u7740\u5415\u4e09\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u65e9\u5c31\u5e94\u8be5\u660e\u767d\uff0c\u6211\u5634\u91cc\u867d\u7136\u8bf4\u4e0d\u4fe1\uff0c\u5fc3\u91cc \u65e9\u5c31\u4e00\u5343\u4e00\u4e07\u4e2a\u76f8\u4fe1\u4e86\u3002\u201d \u5415\u4e09\u4e5f\u7b11\u4e86\uff01\u201c\u6211\u8bf4\u7684\u8bdd\u4f60\u90fd\u76f8\u4fe1\u4e86\uff1f\u201d \u201c\u4e0d\u76f8\u4fe1\u3002\u201d \u9f50\u5c0f\u71d5\u7728\u4e86\u7728\u773c\uff0c\u7b11\u5f97\u66f4\u751c\uff1a\u201c\u8fde\u4e00\u53e5\u90fd\u4e0d\u4fe1\u3002\u201d \u5415\u4e09\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u90a3\u4e48\u4f60\u4e5f\u4e0d\u5fc5\u542c\u6211\u7684\u8bdd\uff0c\u53bb\u770b\u90a3\u516d\u4e2a\u6b7b\u4eba\u5382\u3002\u201d \u9f50\u5c0f\u71d5\u4e5f\u6545\u610f\u677f\u8d77\u8138\uff01 \u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u53bb\u770b\uff0c\u7edd\u4e0d\u4f1a\u518d\u53bb\u770b\u4e00\u773c\uff0c\u56e0\u4e3a\u2026\u2026\u201d \u5979\u5ffd\u53c8\u5ae3\u7136\u800c\u7b11\uff1a\u201c\u56e0\u4e3a\u6211\u65e9\u5c31\u770b\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u53bb\u770b\u7684\uff1f\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u5c31\u5728\u6211\u5634\u91cc\u8bf4\u7edd\u4e0d\u53bb\u770b\u7684\u65f6\u5019\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u6211\u600e\u4e48\u4e0d\u77e5\u9053\uff1f\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u5973\u5b69\u5b50\u8981\u770b\u7537\u4eba\u7684\u65f6\u5019\uff0c\u600e\u4e48\u4f1a\u8ba9\u522b\u7684\u7537\u4eba\u77e5\u9053\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u53ef\u662f\u4ed6\u4eec\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u201c\u6b7b\u4e86\u4e5f\u662f\u7537\u4eba\u3002\u201d \u9f50\u5c0f\u71d5\u5403\u5403\u5730\u7b11\u9053\uff1a\u201c\u5728\u6211\u4eec\u5973\u5b69\u5b50\u773c\u91cc\u770b\u6765\uff0c\u7537\u4eba\u5c31\u662f\u7537\u4eba\uff0c\u4e0d\u7ba1\u6b7b\u6d3b\u90fd\u4e00 \u6837\u3002\u201d \u5415\u4e09\u5927\u7b11\uff01 \u201c\u597d\uff0c\u8bf4\u5f97\u597d\uff0c\u4e5f\u9a82\u5f97\u597d\u3002\u201d \u5415\u4e09\u5728\u7b11\u3002 \u9f50\u5c0f\u71d5\u5374\u4e0d\u7b11\u4e86\uff0c\u795e\u8272\u5ffd\u7136\u53d8\u5f97\u5f88\u4e25\u8083\u3002 \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u6211\u771f\u7684\u5df2\u7ecf\u4ed4\u7ec6\u770b\u8fc7\u90a3\u516d\u4e2a\u6b7b\u4eba\uff0c\u800c\u4e14\u5df2\u7ecf\u53d1\u73b0\u4e86\u4e00\u4ef6\u5f88\u5947\u602a\u7684 \u4e8b\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u90a3\u516d\u4e2a\u6b7b\u4eba\u8eab\u4e0a\u81f4\u547d\u7684\u4f24\u53e3\u7adf\u662f\u5b8c\u5168\u4e00\u6837\u7684\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\u51fa\u4e86\u8fd9\u53e5\u8bdd\uff0c\u7acb\u523b\u53c8\u52a0\u4ee5\u4fee\u6b63\uff1a \u201c\u4e0d\u662f\u516d\u4e2a\u4eba\u90fd\u4e00\u6837\uff0c\u800c\u662f\u4e09\u53f7\u548c\u56db\u53f7\u7684\u4e00\u6837\uff0c\u5341\u4e09\u53f7\u548c\u5341\u56db\u53f7\u7684\u4e00\u6837\uff0c\u4e8c\u5341 \u4e09\u53f7\u548c\u4e09\u5341\u56db\u53f7\u7684\u4e00\u6837\uff0c\u4e0d\u4ec5\u4f24\u53e3\u7684\u90e8\u4f4d\u5728\u4e00\u6837\u7684\u5730\u65b9\uff0c\u800c\u4e14\u8fde\u523a\u6740\u4ed6\u4eec\u90a3\u81f4\u547d\u7684 \u4e00\u51fb\u6240\u7528\u7684\u62db\u5f0f\u548c\u529b\u91cf\u90fd\u4e00\u6837\uff0c\u7edd\u5bf9\u662f\u7528\u540c\u6837\u4e00\u79cd\u624b\u6cd5\uff0c\u4ece\u540c\u6837\u4e00\u4e2a\u65b9\u5411\u5c06\u4ed6\u4eec\u523a \u6740\u4e8e\u5251\u4e0b\u7684\u3002\u201d \u5415\u4e09\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u540c\u4e00\u4e2a\u4eba\u5462\uff1f\u201d \u201c\u4e0d\u65e9\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u662f\uff0c\u201d \u9f50\u5c0f\u71d5\u53c8\u8bf4\uff1a\u201c\u5c31\u56e0\u4e3a\u6740\u4ed6\u7684\u4e0d\u662f\u540c\u4e00\u4e2a\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u89c9\u5f97\u5947\u602a\uff0c\u5c31\u56e0\u4e3a\u6211\u89c9 \u5f97\u5947\u602a\uff0c\u6240\u4ee5\u73b0\u5728\u6211\u624d\u4f1a\u604d\u7136\u5927\u609f\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u4f60\u609f\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4f60\u8981\u4e09\u53f7\u4ed6\u4eec\u90a3\u7ec4\u4eba\u53bb\u523a\u6740\u5c0f\u65b9\uff0c\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8981\u8bd5\u63a2\u5c0f\u65b9\u7684\u5251\u6cd5\uff1f\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u54e6\uff1f\u201d \u201c\u72ec\u5b64\u75f4\u8fd9\u6b21\u53bb\u800c\u590d\u8fd4\uff0c\u4e3a\u7684\u5c31\u662f\u5c0f\u65b9\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u5c06\u4ed6\u7684\u5251\u6cd5\u4e2d\u7684\u7cbe\u8981\u4f20\u7ed9\u4e86\u5c0f\u65b9\uff0c\u4ed6\u5bf9\u5c0f\u65b9\u7684\u5251\u6cd5\u6240\u77e5 \u5374\u4e0d\u591a\u3002\u201d \u9f50\u5c0f\u71d5\u63a5\u7740\u53c8\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4ed4\u7ec6\u7814\u7a76\u8fc7\u8fd9\u4e09\u4e2a\u6b7b\u4eba\u8eab\u4e0a\u81f4\u547d\u7684\u4f24\u53e3\u540e\uff0c\u60c5\u51b5\u5c31 \u4e0d\u540c\u4e86\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\uff0c\u73b0\u5728\u4ed6\u5bf9\u5c0f\u65b9\u7684\u5251\u6cd5\u5df2\u7ecf\u5b8c\u5168\u4e86\u89e3\uff1f\u201d \u9f50\u5c0f\u71d5\u6ca1\u6709\u6b63\u9762\u56de\u7b54\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u53ea\u8bf4\uff1b \u201c\u4f60\u6d3e\u56db\u53f7\u8fd9\u4e00\u7ec4\u4eba\u6765\u6740\u7684\u5c31\u662f\u72ec\u5b64\u75f4\uff0c\u56e0\u4e3a\u8fd9\u4e00\u7ec4\u4eba\u548c\u523a\u6740\u5c0f\u65b9\u7684\u90a3\u4e00\u7ec4\u4eba \u6b66\u529f\u51fa\u624b\u90e8\u6781\u76f8\u4f3c\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u72ec\u5b64\u75f4\u65e2\u7136\u80fd\u7528\u548c\u5c0f\u65b9\u4e00\u6837\u7684\u624b\u6cd5\uff0c\u5c06\u8fd9\u4e00\u7ec4\u4eba\u523a\u6740\u4e8e\u5251\u4e0b\uff0c\u8981 \u6740\u5c0f\u65b9\u597d\u50cf\u4e5f\u4e0d\u592a\u96be\u4e86\u3002\u201d \u5415\u4e09\u4e00\u76f4\u5728\u76ef\u7740\u5979\u770b\u3002 \u521a\u624d\u5df2\u7ecf\u770b\u4e86\u5f88\u4e45\uff0c\u73b0\u5728\u53c8\u770b\u4e86\u5f88\u4e45\u3002 \u4ece\u5979\u4e4c\u9ed1\u7684\u5934\u53d1\uff0c\u5bbd\u5e7f\u7684\u524d\u989d\uff0c\u4e00\u76f4\u770b\u5230\u5979\u7a7f\u7740\u53cc\u7f0e\u5b50\u978b\u7684\u7ea4\u5de7\u7684\u811a\uff0c\u7136\u540e\u624d \u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u201c\u50cf\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u4eba\uff0c\u5c0f\u65b9\u5c45\u7136\u4f1a\u8ba9\u4f60\u8d70\u3002\u201d \u5415\u4e09\u6447\u5934\u53f9\u606f\uff1a\u201c\u4ed6\u7a76\u7adf\u662f\u4e2a\u6df7\u86cb\uff1f\u8fd8\u662f\u53ea\u732a\uff1f\u201d \u9f50\u5c0f\u71d5\u5c45\u7136\u8fd8\u5728\u7b11\uff1a\u201c\u672c\u6765\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u5415\u4e09\u95ee\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u201c\u73b0\u5728\u6211\u603b\u7b97\u60f3\u901a\u4e86\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4ed6\u6839\u672c\u5c31\u4e0d\u662f\u4e1c\u897f\uff0c\u4ed6\u662f\u4e2a\u4eba\uff0c\u6b7b\u4eba\u3002\u201d \u3000\u3000------------------ \u3000\u3000 \u7b2c\u56db\u4e94\u7ae0 \u5c3e \u58f0 \u7b2c\u56db\u5341\u4e94\u7ae0 \u5c3e\u58f0 \u5979\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u5c31\u7b97\u73b0\u5728\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u548c\u6b7b\u4eba\u8fd8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u77e5\u9053\u8fd9\u4e2a\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u201c\u6211\u4e0d\u60f3\uff0c\u6211\u5bf9\u6b7b\u4eba\u4e00\u5411\u6ca1\u4ec0\u4e48\u5174\u8da3\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u6211\u53ea\u60f3\u77e5\u9053\u72ec\u5b64\u75f4\u5728\u54ea\u91cc\uff1f\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u4ed6\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u96be\u9053\u4e0d\u60f3\u89c1\u6211\uff1f\u201d \u5415\u4e09\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u60f3\uff0c\u662f\u4e0d\u6562\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6562\u89c1\u6211\uff1f\u201d \u201c\u4ed6\u6015\u7684\u4e0d\u662f\u4f60\uff0c\u662f\u4ed6\u81ea\u5df1\u3002\u201d \u5415\u4e09\u76ef\u7740\u5979\uff1a\u201c\u5176\u5b9e\u4ed6\u81ea\u5df1\u4e5f\u5e94\u8be5\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u5bb3\u6015\uff1f\u201d \u201c\u4f60\u4e5f\u77e5\u9053\uff1f\u201d \u9f50\u5c0f\u71d5\u4e5f\u5728\u76ef\u7740\u5415\u4e09\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u4ed6\u5df2\u7ecf\u4e0d\u662f\u4e2a\u771f\u6b63\u7684\u7537\u4eba\uff1f\u201d \u5415\u4e09\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6211\u5ac1\u7ed9\u4ed6\uff1f\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u77e5\u9053\u4ed6\u7684\u75c5\u5f88\u5feb\u5c31\u4f1a\u597d\u7684\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u201c\u8981\u5230\u4ed6\u4eb2\u624b\u5c06\u5c0f\u65b9\u523a\u6740\u5728\u4ed6\u7684\u5251\u4e0b\u4e4b\u540e\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u6211\u76f8\u4fe1\u4ed6\u73b0\u5728\u4e00\u5b9a\u5df2\u7ecf\u5f88\u6709\u628a\u63e1\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4ed6\u80fd\u627e\u5f97\u5230\u5c0f\u65b9\uff1f\u201d \u5415\u4e09\u9053\uff1a\u201c\u4ed6\u6839\u672c\u4e0d\u5fc5\u53bb\u627e\uff0c\u4ed6\u53ea\u8981\u5750\u5728\u90a3\u91cc\u7b49\u5c31\u884c\u4e86\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u5415\u4e09\u9053\uff1a\u201c\u56e0\u4e3a\u5c0f\u65b9\u4e00\u5b9a\u4f1a\u53bb\u627e\u4ed6\u7684\u3002\u201d \u9f50\u5c0f\u71d5\u8bf4\uff1a\u201c\u4f60\u6709\u628a\u63e1\uff1f\u201d \u5415\u4e09\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u51e0\u65f6\u770b\u89c1\u8fc7\u6211\u505a\u8fc7\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\uff1f\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u5c0f\u65b9\u662f\u4e0d\u662f\u80fd\u627e\u5f97\u5230\u4ed6\u5462\uff1f\u201d \u201c\u5982\u679c\u5c0f\u65b9\u4e0d\u592a\u7b28\uff0c\u5c31\u4e00\u5b9a\u80fd\u627e\u5f97\u5230\u3002\u201d \u5415\u4e09\u5fae\u7b11\uff1a\u201c\u5426\u5219\u4ed6\u5c31\u4e00\u5b9a\u4e0d\u662f\u4e2a\u6df7\u86cb\uff0c\u5c31\u4e00\u5b9a\u662f\u6761\u732a\u4e86\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u5230\u54ea\u91cc\u624d\u80fd\u627e\u5f97\u5230\u4ed6\uff1f\u201d \u5415\u4e09\u9053\uff1a\u201c\u80e1\u96c6\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u5230\u80e1\u96c6\u53bb\uff1f\u201d \u201c\u4f60\u7684\u60f3\u6cd5\u4e00\u5b9a\u4e5f\u8ddf\u73ed\u5bdf\u5df4\u90a3\u4e00\u6837\uff0c\u8ba4\u4e3a\u6211\u4e00\u5b9a\u4f1a\u5230\u80e1\u96c6\u53bb\uff0c\u7b49\u7740\u4eb2\u624b\u6740\u6b7b\u5c0f \u65b9\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u624d\u4f1a\u5b89\u6392\u8fd9\u4e00\u6218\uff0c\u56e0\u4e3a\u8fd9\u4e00\u6218\u7684\u7ed3\u679c\u5fc5\u5c06\u662f\u4e24\u8d25\u4ff1\u4f24\uff0c\u8d25\u7684 \u4e00\u65b9\u56fa\u7136\u5fc5\u6b7b\u65e0\u7591\uff0c\u80dc\u7684\u4e00\u65b9\u4e5f\u5fc5\u5c06\u4ed8\u51fa\u6781\u5927\u7684\u4ee3\u4ef7\uff0c\u7b49\u5230\u90a3\u65f6\u5019\u4ed6\u518d\u51fa\u624b\uff0c\u65e0\u8bba \u662f\u6211\u6740\u6b7b\u4e86\u5c0f\u65b9\u4e5f\u597d\uff0c\u662f\u5c0f\u65b9\u6740\u4e86\u6211\u4e5f\u597d\uff0c\u5269\u4e0b\u7684\u4e00\u4e2a\u8fd8\u662f\u4f1a\u6b7b\u5728\u4ed6\u624b\u91cc\u3002\u201d \u5415\u4e09\u53c8\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\u73ed\u5bdf\u5df4\u90a3\u4e5f\u8ddf\u4f60\u4e00\u6837\uff0c\u4f60\u4eec\u7684\u60f3\u6cd5\u90fd\u9517\u4e86\uff0c\u56e0\u4e3a\u6211\u6839\u672c\u5c31 \u4e0d\u4f1a\u5230\u80e1\u96c6\u53bb\uff0c\u6839\u672c\u5c31\u4e0d\u60f3\u4eb2\u624b\u6740\u6b7b\u5c0f\u65b9\uff0c\u800c\u4e14\u6211\u6839\u672c\u5c31\u4e0d\u6068\u4ed6\u3002\u201d \u9f50\u5c0f\u71d5\u5f53\u7136\u5f88\u60ca\u5947\uff1a\u201c\u96be\u9053\u4f60\u5fd8\u4e86\u4f60\u4eb2\u751f\u7684\u513f\u5b50\u662f\u6b7b\u5728\u8c01\u624b\u91cc\u7684\uff1f\u201d \u5979\u95ee\u7684\u662f\u4e2a\u5f88\u4f24\u4eba\u7684\u95ee\u9898\uff0c\u5415\u4e09\u51b7\u51b7\u5730\u770b\u7740\u5979\uff0c\u5c45\u7136\u53c8\u7b11\u4e86\uff1a\u201c\u96be\u9053\u4f60\u4ee5\u4e3a\u5c0f \u65b9\u6740\u6b7b\u7684\u5415\u5929\u5b9d\u771f\u662f\u6211\u4eb2\u751f\u7684\u513f\u5b50\uff1f\u201d \u9f50\u5c0f\u71d5\u6014\u4f4f\u4e86\u3002 \u5979\u60f3\u4e0d\u5230\u5415\u4e09\u5c45\u7136\u4f1a\u8bf4\u51fa\u8fd9\u4e48\u6837\u4e00\u53e5\u8bdd\uff0c\u4e5f\u60f3\u4e0d\u5230\u5415\u4e09\u5c45\u7136\u53c8\u5e26\u5979\u53bb\u770b\u53e6\u5916\u4e00 \u53e3\u68fa\u6750\u3002 \u8fd9\u53e3\u68fa\u6750\u91cc\u5c45\u7136\u6709\u4e24\u4e2a\u4eba\u7684\u5c38\u4f53\uff0c\u4e00\u4e2a\u662f\u4e30\u80f8\u5927\u4e73\u7ed3\u5b9e\u5065\u5eb7\u7684\u5987\u4eba\u3002 \u8eab\u65c1\u8fd8\u8eba\u7740\u4e2a\u53ea\u6709\u51e0\u4e2a\u6708\u5927\u7684\u5a74\u5b69\u3002 \u53ea\u8981\u7565\u6709\u7ecf\u9a8c\u7684\u4eba\u90fd\u770b\u5f97\u51fa\u8fd9\u4e2a\u5987\u4eba\u521a\u521a\u751f\u8fc7\u5b69\u5b50\uff0c\u8fd9\u4e2a\u5a74\u513f\u5374\u4e0d\u662f\u5979\u751f\u7684\u5b69 \u5b50\u3002 \u201c\u8fd9\u4e2a\u5973\u4eba\u662f\u8fd9\u4eba\u5b69\u5b50\u7684\u5976\u5988\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u5979\u5403\u5f97\u5927\u597d\uff0c\u5403\u5f97\u5927\u591a\uff0c\u4e00\u7761\u5c31\u50cf\u662f\u6b7b\u4eba\u4e00\u6837\uff0c\u6240\u4ee5\u73b0\u5728\u5979\u5c31\u771f\u7684 \u662f\u4e2a\u6b7b\u4eba\u4e86\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u201c\u56e0\u4e3a\u8fd9\u4e2a\u5b69\u5b50\u5c31\u662f\u88ab\u5979\u7761\u7740\u4e86\u7684\u65f6\u5019\u538b\u5728\u8eab\u5b50\u4e0b\u9762\u6d3b\u6d3b\u95f7\u6b7b\u7684\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u662f\u6211\u4eb2\u751f\u7684\u513f\u5b50\uff0c\u53ef\u662f\u5982\u679c\u4ed6\u80fd\u6d3b\u4e0b\u53bb\uff0c\u6211\u4e00\u5b9a\u4f1a\u6bd4\u8c01\u90fd\u5ba0 \u7231\u4ed6\uff0c\u4ed6\u8981\u4ec0\u4e48\uff0c\u6211\u5c31\u7ed9\u4ed6\u4ec0\u4e48\uff0c\u7b49\u5230\u5341\u4e03\u516b\u5e74\u4e4b\u540e\uff0c\u4ed6\u4e00\xb7\u5b9a\u4e5f\u4f1a\u6b7b\u5728\u522b\u4eba\u7684\u5251 \u4e0b\uff0c\u56e0\u4e3a\u90a3\u65f6\u5019\u4ed6\u4e00\u5b9a\u4e5f\u4f1a\u50cf\u5415\u5929\u5b9d\u4e00\u4e00\u6837\u88ab\u6211\u5ba0\u574f\u4e86\u3002\u201d \u9f50\u5c0f\u71d5\u6ca1\u6709\u518d\u95ee\uff1a\u201c\u8fd9\u4e2a\u5b69\u5b50\u662f\u8c01\u7684\u5b69\u5b50\u3002\u201d \u4e5f\u4e0d\u5fc5\u518d\u95ee\u3002 \u5979\u5ffd\u7136\u89c9\u5f97\u624b\u811a\u51b0\u51b7\uff0c\u51b7\u6c57\u53c8\u6e7f\u900f\u4e86\u8863\u88f3\u3002 \u73b0\u5728\u5979\u5f53\u7136\u5df2\u7ecf\u77e5\u9053\u8fd9\u4e2a\u5b69\u5b50\u5c31\u662f\u5c0f\u65b9\u7684\u5b69\u5b50\uff0c\u4f46\u5374\u6c38\u8fdc\u4e0d\u77e5\u9053\u8fd9\u4e2a\u5b69\u5b50\u7684\u592d \u6298\u7a76\u7adf\u662f\u4ed6\u7684\u5e78\u8fd0\u8fd8\u662f\u4e0d\u5e78\uff1f\u201d \u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u6211\u8fd9\u4e2a\u4eba\u505a\u7684\u4e8b\u592a\u53ef\u6015\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u5e78\u597d\u4e5f\u53ea\u6709\u4f60\u4f1a\u8fd9\u4e48\u60f3\uff0c\u56e0\u4e3a\u6211\u505a\u7684\u4e8b\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u4ece\u6765\u6ca1\u6709\u522b\u4eba \u4f1a\u77e5\u9053\uff0c\u751a\u81f3\u8fde\u60f3\u90fd\u60f3\u4e0d\u5230\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u6240\u4ee5\u73ed\u5bdf\u5df4\u90a3\u4e00\u76f4\u8ba4\u4e3a\u4f60\u6068\u6b7b\u4e86\u5c0f\u65b9\uff0c\u4e00\u5fc3\u60f3\u8981\u4eb2\u624b\u6740\u4e86\u4ed6\u3002\u201d \u201c\u6240\u4ee5\u4ed6\u624d\u4f1a\u5b89\u6392\u8fd9\u4e00\u6218\uff0c\u7b49\u5230\u6211\u548c\u5c0f\u65b9\u4e24\u8d25\u4ff1\u4f24\u65f6\uff0c\u4ed6\u5c31\u53ef\u4ee5\u5750\u6536\u6e14\u5229\u4e86\u3002\u201d \u5415\u4e09\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u6bd4\u4ed6\u60f3\u50cf\u4e2d\u8fd8\u8981\u806a\u660e\u4e00\u70b9\uff0c\u6240\u4ee5\u4e0a\u5f53\u7684\u4e0d\u4f1a\u662f\u6211\uff0c\u800c\u662f\u4ed6\u3002\u201d \u5415\u4e09\u53c8\u8bf4\uff1a\u201c\u73b0\u5728\u73ed\u5bdf\u5df4\u90a3\u4e00\u5b9a\u4e5f\u4f1a\u5230\u80e1\u96c6\u53bb\u7b49\u7740\u770b\u8fd9\u4e00\u6218\u7684\u7ed3\u679c\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u4f1a\u5728\u4ec0\u4e48\u5730\u65b9\u7b49\uff1f\u201d \u201c\u4e0d\u4f46\u6211\u77e5\u9053\uff0c\u72ec\u5b64\u75f4\u4e5f\u77e5\u9053\u3002\u201d \u5415\u4e09\u8bf4\uff1a\u201c\u7b49\u5230\u72ec\u5b64\u75f4\u6740\u4e86\u5c0f\u65b9\u540e\uff0c\u5c31\u4e00\u5b9a\u4f1a\u53bb\u627e\u4ed6\u7684\u3002\u201d \u201c\u90a3\u65f6\u72ec\u5b64\u75f4\u5c31\u7b97\u5df2\u7ecf\u6740\u4e86\u5c0f\u65b9\uff0c\u4e5f\u5fc5\u5b9a\u4ed8\u51fa\u4e86\u6781\u5927\u7684\u4ee3\u4ef7\uff0c\u7b49\u5230\u4ed6\u4eec\u4ea4\u8fc7\u624b \u4e4b\u540e\uff0c\u4e0d\u7ba1\u662f\u72ec\u5b64\u75f4\u6740\u4e86\u73ed\u5bdf\u5df4\u90a3\u4e5f\u597d\uff0c\u662f\u73ed\u5bdf\u5df4\u90a3\u6740\u4e86\u72ec\u5b64\u75f4\u4e5f\u597d\uff0c\u7b49\u5230\u90a3\u65f6\u5019 \u624d\u51fa\u624b\uff0c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u4e2d\u5269\u4e0b\u6765\u7684\u4e00\u4e2a\u8fd8\u662f\u5fc5\u5c06\u6b7b\u5728\u4f60\u624b\u91cc\u3002\u201d \u9f50\u5c0f\u71d5\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u8fd9\u4e00\u6218\u4e0d\u7ba1\u662f\u8c01\u80dc\u8c01\u8d1f\uff0c\u53ea\u6709\u4f60\u662f\u7edd\u4e0d\u4f1a\u8d25\u7684\u3002\u201d \u5728\u5927\u591a\u6570\u4eba\u5fc3\u76ee\u4e2d\uff0c\u80e1\u96c6\u53ea\u4e0d\u8fc7\u662f\u8fb9\u7761\u4e0a\u7684\u4e00\u4e2a\u5c0f\u9547\u3002 \u6839\u672c\u5b98\u65b9\u6700\u8fd1\u8c03\u67e5\u7684\u8bb0\u5f55\uff0c\u8fd9\u91cc\u4e00\u5171\u53ea\u6709\u4e03\u5341\u4e09\u6237\u4eba\u5bb6\uff0c\u5305\u62ec\u5987\u5b7a\u5728\u5185\uff0c\u4e00\u5171 \u4e5f\u53ea\u6709\u4e09\u767e\u4e00\u5341\u4e00\u540d\u4eba\u53e3\u3002 \u5176\u4e2d\u5927\u591a\u6570\u662f\u505a\u5c0f\u751f\u610f\u7684\u4eba\uff0c\u56e0\u4e3a\u8fd9\u5730\u65b9\u7684\u571f\u58e4\u65e2\u4e0d\u80a5\u6c83\uff0c\u5927\u65f6\u4e5f\u4e0d\u6b63\uff0c\u800c\u4e14 \u975e\u5e38\u504f\u50fb\uff0c\u65e2\u4e0d\u9002\u4e8e\u519c\u8015\uff0c\u4e5f\u4e0d\u9002\u4e8e\u505a\u5176\u4ed6\u4efb\u4f55\u4e8b\u3002 \u5927\u591a\u6570\u4eba\u751a\u81f3\u4ece\u672a\u542c\u8bf4\u8fc7\u8fd9\u5730\u65b9\u7684\u540d\u5b57\u3002 \u4e8b\u5b9e\u4e0a\u5374\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u8fd9\u5730\u65b9\u7684\u4eba\u53e3\u8fdc\u6bd4\u5b98\u65b9\u8bb0\u5f55\u4e0a\u591a\u5f97\u591a\uff0c\u91cd\u8981\u6027\u4e5f\u8fdc\u6bd4\u5927\u591a\u6570\u4eba\u60f3\u50cf\u4e2d\u5927\u5f97\u591a\u3002 \u5e02\u9762\u7684\u7e41\u8363\uff0c\u66f4\u4e0d\u662f\u90a3\u4e9b\u4eba\u6240\u80fd\u60f3\u50cf\u5f97\u5230\u7684\u3002 \u5c31\u56e0\u4e3a\u8fd9\u5730\u65b9\u592a\u504f\u50fb\uff0c\u4e0d\u4f1a\u5f15\u8d77\u5b98\u65b9\u7684\u6ce8\u610f\uff0c\u6240\u4ee5\u4e00\u4e9b\u65e0\u8def\u53ef\u8d70\u7684\u4eba\uff0c\u90fd\u4f1a\u6295 \u5954\u5230\u8fd9\u91cc\u6765\u3002 \u5e02\u9762\u4e0a\u5230\u5904\u90fd\u5145\u65a5\u7740\u4ece\u56db\u9762\u516b\u65b9\u6295\u5954\u6765\u7684\u6d41\u6c11\u6d41\u5b50\u7f6a\u72af\u548c\u6d41\u83ba\uff0c\u8fd9\u4e9b\u4eba\u901a\u5e38\u4e5f \u6b63\u662f\u6700\u820d\u5f97\u82b1\u94b1\u7684\uff0c\u6240\u4ee5\u624d\u4f1a\u9020\u6210\u8fd9\u5730\u65b9\u7578\u5f62\u7684\u7e41\u8363\u3002 \u4f4f\u5728\u5f53\u5730\u7684\u4e03\u5341\u4e09\u6237\u4eba\u5bb6\u4e2d\uff0c\u7adf\u6709\u4e00\u5927\u534a\u662f\u7ecf\u8425\u5ba2\u6808\u9152\u9986\u548c\u996d\u94fa\u7684\u3002 \u591c\u91cc\u7559\u5bbf\u5728\u6b64\uff0c\u8fd9\u91cc\u867d\u7136\u53ea\u6709\u4e03\u5341\u4e09\u6237\u4eba\u5bb6\uff0c\u5ba2\u6808\u9152\u697c\u548c\u996d\u94fa\u5374\u6709\u4e00\u767e\u53e6\u4e94\u5bb6\u3002 \u5176\u4e2d\u751f\u610f\u6700\u597d\u7684\u4e00\u5bb6\u53eb\u505a\u201c\u8fbe\u8bb0\u201d\u3002 \u4ece\u65e9\u5230\u665a\u90fd\u6324\u6ee1\u4e86\u4eba\uff0c\u8981\u8fdb\u53bb\u5403\u987f\u996d\u90fd\u5f97\u6392\u961f\u7b49\u4e0a\u534a\u5929\u3002 \u636e\u8bf4\u8fd9\u5bb6\u996d\u94fa\u91cc\u5356\u7684\u201c\u5976\u6cb9\u201d\u548c\u201c\u8471\u6ce5\u201d\u7edd\u5bf9\u662f\u9644\u8fd1\u516b\u767e\u91cc\u4e4b\u5185\u6700\u597d\u7684\u3002 \u867d\u7136\u6709\u5f88\u591a\u4eba\u90fd\u4f1a\u89c9\u5f97\u8fd9\u4e24\u79cd\u98df\u54c1\u81ed\u4e0d\u53ef\u95fb\uff0c\u53ef\u662f\u53ea\u8981\u5c1d\u8bd5\u8fc7\u4e00\u6b21\u4e4b\u540e\uff0c\u4e5f\u8bb8 \u5c31\u4f1a\u4e0a\u75db\u4e86\uff0c\u6ca1\u6709\u5b83\u4e5f\u8bb8\u8fde\u996d\u90fd\u5403\u4e0d\u4e0b\u3002 \u73ed\u5bdf\u5df4\u90a3\u544a\u8bc9\u5c0f\u65b9\uff1a\u201c\u5415\u4e09\u7684\u79d8\u5bc6\u5c31\u5728\u8fd9\u5730\u65b9\u6700\u70ed\u95f9\u7684\u4e00\u6761\u8857\u4e0a\u3002\u201d \u8fd9\u6761\u8857\u4e0a\u4e00\u5171\u6709\u4e5d\u5341\u516d\u5bb6\u5e97\u94fa\uff0c\u9664\u4e86\u4e00\u5bb6\u5356\u8102\u7c89\u9488\u7ebf\u7684\u201c\u8fdc\u9999\u9f50\u201d\u548c\u4e00\u5bb6\u7c73\u5e97 \u4e24\u5bb6\u6cb9\u574a\u5916\uff0c\u5176\u4e2d\u5927\u591a\u6570\u90fd\u662f\u9152\u697c\u996d\u94fa\u548c\u5ba2\u6808\u3002 \u8fde\u4e00\u6237\u4f4f\u5bb6\u7684\u4eba\u90fd\u6ca1\u6709\u3002 \u73ed\u5bdf\u5df4\u90a3\u95ee\u5c0f\u65b9\uff1a\u201c\u4f60\u731c\u4e0d\u731c\u5f97\u51fa\u5415\u4e09\u7684\u79d8\u7a9f\u662f\u54ea\u4e00\u5bb6\uff1f\u201d \u5c0f\u65b9\u6beb\u4e0d\u8003\u8651\u5c31\u56de\u7b54\uff1a\u201c\u662f\u8fbe\u8bb0\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u731c\u5415\u4e09\u5728\u90a3\u91cc\uff1f\u201d \u201c\u56e0\u4e3a\u90a3\u91cc\u7684\u4eba\u6700\u591a\u3002\u201d \u5c0f\u65b9\u7684\u56de\u7b54\u5f88\u7b80\u5355\uff0c\u4e5f\u5f88\u6b63\u786e\u3002 \u5415\u4e09\u968f\u65f6\u90fd\u8981\u542c\u53d6\u4ed6\u5c5e\u4e0b\u4f20\u6765\u7684\u6d88\u606f\uff0c\u4ed6\u7684\u5c5e\u4e0b\u6765\u81ea\u56db\u65b9\uff0c\u6bcf\u4e00\u4e2a\u5230\u201c\u8fbe\u8bb0\u201d \u6765\u5403\u996d\u7684\u4eba\uff0c\u90fd\u53ef\u80fd\u662f\u4ed6\u7684\u5c5e\u4e0b\uff0c\u90fd\u4f1a\u62fc\u547d\u4fdd\u62a4\u4ed6\u7684\u5b89\u5168\u3002 \u800c\u4e14\u201c\u5927\u9690\u9690\u4e8e\u5e02\u201d\uff0c\u8fd9\u9053\u7406\u5415\u4e09\u5f53\u7136\u4e5f\u660e\u767d\uff0c\u73ed\u5bdf\u5df4\u90a3\u4e5f\u660e\u767d\u3002 \u6240\u4ee5\u4ed6\u4eec\u5728\u9547\u5916\u7684\u67a3\u6797\u96c6\u4f1a\u4e4b\u540e\uff0c\u73ed\u5bdf\u5df2\u90a3\u5c31\u544a\u8bc9\u5c0f\u65b9\uff1a \u201c\u4eca\u5929\u5348\u65f6\uff0c\u4f60\u4e5f\u5230\u90a3\u91cc\u53bb\u5403\u996d\uff0c\u53ea\u8981\u542c\u89c1\u6709\u4eba\u558a\u4e00\u58f0\u2018\u8fd9\u5976\u6cb9\u662f\u81ed\u7684\u2019\uff0c\u4f60 \u5c31\u51b2\u8fdb\u540e\u9762\u7684\u53a8\u623f\u53bb\uff0c\u628a\u5927\u7076\u4e0a\u90a3\u53e3\u84b8\u9752\u7a1e\u997c\u7684\u5927\u94c1\u9505\u6380\u5f00\uff0c\u6cfc\u4e00\u76c6\u51b7\u6c34\u628a\u7076\u91cc\u7684 \u706b\u6d47\u706d\uff0c\u518d\u8df3\u8fdb\u53bb\uff0c\u94bb\u5165\u7076\u53e3\u65c1\u8fb9\u7684\u4e00\u4e2a\u4e24\u5c3a\u89c1\u65b9\u7684\u6d1e\uff0c\u6211\u5c31\u53ef\u4ee5\u627e\u5230\u5415\u4e09\u4e86\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4f60\u53ea\u8981\u8fd9\u4e48\u6837\u505a\uff0c\u522b\u7684\u4e8b\u4f60\u90fd\u4e0d\u7ba1\uff0c\u5c31\u662f\u5916\u9762\u6253\u7ffb\u4e86\u5929\u4f60\u4e5f\u4e0d \u5fc5\u7ba1\uff0c\u5c31\u7b97\u5929\u584c\u4e0b\u6765\u4e5f\u6709\u522b\u4eba\u4f1a\u66ff\u4f60\u53bb\u9876\u4f4f\u3002\u201d \u8fdc\u8fdc\u5730\u770b\u5230\u5c0f\u65b9\u8d70\u8fdb\u201c\u8fbe\u8bb0\u201d\uff0c\u542c\u89c1\u6709\u4eba\u5927\u558a\u4e00\u58f0\u201c\u8fd9\u5976\u6cb9\u662f\u81ed\u7684\u201d\u4e4b\u540e\uff0c\u73ed \u5bdf\u5df4\u90a3\u5c31\u8d70\u4e86\uff0c\u56e0\u4e3a\u8fd9\u4ee5\u540e\u7684\u6bcf\u4e00\u6b65\u53d1\u5c55\uff0c\u6bcf\u4e00\u4e2a\u53d8\u5316\uff0c\u90fd\u5df2\u5728\u4ed6\u9884\u7b97\u4e4b\u4e2d\uff0c\u4ed6\u5df2 \u7ecf\u7528\u4e0d\u7740\u518d\u542c\u518d\u770b\u3002 \u4ed6\u4ece\u4e00\u6761\u504f\u50fb\u7684\u5c0f\u8def\u4e0a\u7ed5\u8fc7\u4ed6\u4eec\u521a\u624d\u805a\u4f1a\u7684\u67a3\u6811\u6797\uff0c\u8d70\u4e0a\u4e00\u4e2a\u5c71\u5761\uff0c\u5728\u4e00\u5757\u51f8 \u8d77\u5982\u9e64\u9888\u7684\u5371\u77f3\u4e0a\u5750\u4e0b\u6765\uff0c\u8fd9\u91cc\u8ddd\u79bb\u90a3\u6761\u70ed\u95f9\u7684\u8001\u8857\u867d\u7136\u5df2\u5f88\u9065\u8fdc\uff0c\u4f46\u5374\u6070\u5de7\u521a\u597d \u80fd\u770b\u89c1\u90a3\u5bb6\u5356\u5976\u6cb9\u548c\u8471\u6ce5\u7684\u996d\u94fa\u3002 \u867d\u7136\u770b\u4e0d\u6e05\u695a\uff0c\u53ef\u662f\u4ee5\u4ed6\u7684\u773c\u529b\uff0c\u8fd8\u662f\u80fd\u770b\u5f97\u89c1\u3002 \u8fd9\u5730\u65b9\u5f53\u7136\u4e5f\u662f\u4ed6\u65e9\u5c31\u9009\u597d\u7684\uff0c\u8fd9\u65f6\u5019\u90a3\u996d\u94fa\u91cc\u679c\u7136\u5df2\u6253\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u8001\u8857\u4e0a \u7684\u4eba\u90fd\u5df2\u6d8c\u5230\u8fd9\u8fb9\u6765\uff0c\u6709\u7684\u5728\u770b\u70ed\u95f9\uff0c\u6709\u7684\u4e5f\u52a0\u5165\u4e86\u6218\u6597\uff0c\u6574\u4e2a\u8001\u8857\u90fd\u5df2\u4e71\u5f97\u50cf\u662f \u9505\u716e\u70c2\u4e86\u7684\u70ed\u7ca5\u3002 \u73ed\u5bdf\u5df4\u90a3\u89c9\u5f97\u5f88\u6ee1\u610f\uff0c\u5916\u9762\u8d8a\u4e71\u8d8a\u597d\u3002 \u5916\u9762\u8d8a\u4e71\uff0c\u91cc\u9762\u8d8a\u9759\uff0c\u6740\u4eba\u7684\u4eba\u9700\u8981\u5b89\u9759\uff0c\u88ab\u6740\u7684\u4eba\u4e5f\u540c\u6837\u9700\u8981\u5b89\u9759\uff0c\u4e0d\u7ba1\u662f \u8c01\u6740\u4e86\u8c01\uff0c\u5bf9\u4ed6\u6765\u8bf4\u90fd\u6ca1\u6709\u4ec0\u4e48\u5206\u522b\u3002 \u56e0\u4e3a\u4ed6\u5df2\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002 \u8fd9\u4e00\u5207\u5f53\u7136\u90fd\u662f\u4ed6\u65e9\u5df2\u5b89\u6392\u597d\u7684\uff0c\u4ed6\u5df2\u8ba1\u5212\u4e86\u591a\u5e74\uff0c\u4ed6\u76f8\u4fe1\u6bcf\u3002\u4e00\u4e2a\u7ec6\u8282\uff0c\u6bcf \u4e00\u4e2a\u884c\u52a8\uff0c\u90fd\u7cbe\u5bc6\u51c6\u786e\u5982\u897f\u6d0b\u81ea\u9e23\u949f\u5185\u7684\u673a\u4ef6\u3002 \u5c31\u5728\u4ed6\u6b63\u51c6\u5907\u8eba\u4e0b\u53bb\u6b47\u4e00\u53e3\u6c14\u7684\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u542c\u89c1\u4ed6\u8eab\u540e\u6709\u4eba\u7528\u4e00\u79cd\u6781\u8be1\u79d8\u7684 \u53e3\u6c14\u8f7b\u8f7b\u5730\u5bf9\u4ed6\u8bf4\u4e86\u53e5\u975e\u5e38\u5947\u602a\u7684\u8bdd\uff1a \u201c\u5b8c\u4e86\uff01\u201d\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u7ecf\u5feb\u5b8c\u4e86\uff1f\u201d \u73ed\u5bdf\u5df4\u90a3\u6ca1\u6709\u56de\u5934\uff0c\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u56e0\u4e3a\u4ed6\u65e9\u5c31\u77e5\u9053\u8fd9\u4e2a\u4eba\u6765\u4e86\uff0c\u4e5f\u77e5\u9053 \u6765\u7684\u662f\u8c01\u3002 \u201c\u662f\u7684\u3002\u73b0\u5728\u5df2\u7ecf\u5feb\u5b8c\u4e86\u3002\u201d\u4ed6\u53ea\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u6240\u6709\u7684\u4e8b\u73b0\u5728\u90fd\u5df2\u7ecf\u5230\u4e86\u5e94\u8be5 \u7ed3\u675f\u7684\u65f6\u5019\u3002\u201d \u6765\u7684\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u5230\u4e86\u4ed6\u8eab\u540e\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u8fd9\u4e9b\u4e8b\u4f1a\u600e\u4e48\u6837\u7ed3\u675f\uff1f\u201d \u201c\u662f\u4e00\u79cd\u5f88\u5706\u6ee1\u7684\u7ed3\u675f\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u5415\u4e09\u8fd9\u91cc\u7684\u79d8\u7a9f\u5728\u5730\u4e0b\uff0c\u867d\u7136\u6709\u4e09\u4e2a\u51fa\u53e3\uff0c\u53ef\u662f\u6211\u4eec\u5982\u679c\u80fd\u628a \u4ed6\u4e09\u4e2a\u51fa\u53e3\u90fd\u5c01\u6b7b\uff0c\u90a3\u91cc\u5c31\u662f\u4e2a\u6b7b\u5730\u3002\u201d \u5c31\u5728\u4ed6\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u9644\u8fd1\u4e09\u5341\u91cc\u4e4b\u5185\u7684\u4eba\u90fd\u53ef\u4ee5\u542c\u89c1\u4e00\u201d\u58f0\u9707\u8033\u7684\u7206\u70b8\uff0c \u90fd\u53ef\u4ee5\u770b\u89c1\u4e00\u9053\u6d53\u70df\u4ece\u201c\u8fbe\u8bb0\u201d\u5347\u8d77\uff0c\u63a5\u7740\u7684\u4e24\u58f0\u7206\u70b8\u6765\u81ea\u53e6\u5916\u4e24\u4e2a\u4e0d\u540c\u7684\u5730\u65b9\uff0c \u7136\u540e\u53c8\u6709\u4e24\u9053\u6d53\u70df\u5347\u8d77\u3002 \u73ed\u5bdf\u5df4\u90a3\u5fae\u7b11\uff1a\u201c\u73b0\u5728\u90a3\u91cc\u7684\u4e09\u4e2a\u51fa\u53e3\u90fd\u5df2\u88ab\u5c01\u6b7b\uff0c\u90a3\u91cc\u7684\u4eba\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd \u6d3b\u7740\u51fa\u6765\u4e86\uff0c\u65e0\u8bba\u72ec\u5b64\u75f4\u548c\u5c0f\u65b9\u662f\u8c01\u80dc\u8c01\u8d1f\uff0c\u90fd\u5fc5\u5c06\u88ab\u6d3b\u57cb\u5728\u5730\u4e0b\u3002\u201d \u201c\u662f\u72ec\u5b64\u75f4\u548c\u5c0f\u65b9\uff1f\u5415\u4e09\u5462\uff1f\u201d \u201c\u5415\u4e09\u4e0d\u4f1a\u5728\u90a3\u91cc\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4ed6\u4e00\u5411\u8ba4\u4e3a\u53ea\u6709\u6211\u624d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4e5f\u77e5 \u9053\u6211\u7edd\u4e0d\u4f1a\u5230\u90a3\u91cc\u53bb\uff0c\u4ed6\u600e\u4e48\u4f1a\u53bb\u3002\u201d \u6765\u7684\u8fd9\u4e2a\u4eba\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u5b9e\u5728\u5f88\u4e86\u89e3\u4ed6\uff0c\u6bd4\u4ed6\u81ea\u5df1\u60f3\u50cf\u4e2d\u8fd8\u8981\u591a\u5f97\u591a\u3002\u201d \u201c\u73b0\u5728\u535c\u9e70\u548c\u6ce2\u5a03\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u82cf\u82cf\u79bb\u5f00\u4e86\u5415\u4e09\u4e4b\u540e\uff0c\u5df2\u7ecf\u662f\u4e2a\u65e0\u8db3\u8f7b\u91cd\u7684\u4eba\uff0c \u6b7b\u6d3b\u90fd\u4e0d\u91cd\u8981\u4e86\u3002\u201d \u201c\u9633\u5149\u662f\u6211\u7684\u65b0\u4eba\uff0c\u5979\u4f1a\u4e86\u89e3\u6211\uff0c\u867d\u7136\u5979\u5fc3\u91cc\u4e5f\u4f1a\u89c9\u5f97\u6211\u7684\u624b\u6bb5\u5927\u8fc7\u4efd\uff0c\u4e5f\u4f1a \u4e3a\u535c\u9e70\u548c\u5c0f\u65b9\u60b2\u4f24\uff0c\u4f46\u662f\u5979\u4e00\u5b9a\u4f1a\u5047\u88c5\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u77e5\u9053\u7684\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u4ee5\u540e\u5979\u8bf4\u4e0d\u5b9a\u4f1a\u5ac1\u7ed9\u6211\u3002\u201d \u201c\u5979\u4e00\u5b9a\u4f1a\u5ac1\u7ed9\u4f60\u3002\u201d\u6765\u7684\u8fd9\u4e2a\u4eba\u8bf4\uff1b \u201c\u56e0\u4e3a\u5979\u4e5f\u662f\u4e2a\u975e\u5e38\u806a\u660e\u7684\u5973\u4eba\uff0c\u5e94\u8be5\u77e5\u9053\u53ea\u6709\u5ac1\u7ed9\u4f60\u624d\u662f\u6700\u806a\u660e\u7684\u505a\u6cd5\u3002\u201d \u4ed6\u5c45\u7136\u6ca1\u6709\u95ee\u5415\u4e09\u548c\u9f50\u5c0f\u71d5\u7684\u4e0b\u573a\uff0c\u56e0\u4e3a\u4ed6\u5c31\u662f\u5415\u4e09\u8eab\u8fb9\u6700\u4eb2\u4fe1\u7684\u5c5e\u4e0b\u5415\u606d\u3002 \u201c\u8fd9\u6b21\u4e09\u7237\u786e\u5b9e\u5df2\u5c06\u4ed6\u5c5e\u4e0b\u7684\u7cbe\u9510\u5927\u591a\u6570\u8c03\u96c6\u5230\u8fd9\u91cc\uff0c\u4ed6\u8fd9\u4e48\u505a\u6709\u4e24\u79cd\u7528\u610f\u3002\u201d \u5415\u606d\u8bf4\uff1a\u201c\u7b2c\u4e00\uff0c\u4ed6\u5f53\u7136\u662f\u8981\u4f60\u76f8\u4fe1\u4ed6\u5230\u4e86\u8fd9\u91cc\uff0c\u8981\u4f60\u5c06\u4f60\u5c5e\u4e0b\u7684\u7cbe\u9510\u4e5f\u8c03\u96c6\u5230\u8fd9 \u91cc\u6765\uff1b\u7b2c\u4e8c\uff0c\u4ed6\u7684\u5c5e\u4e0b\u672c\u6765\u90fd\u662f\u6c5f\u6e56\u4e2d\u7684\u4ea1\u547d\u5f92\uff0c\u4ed6\u4ece\u6765\u90fd\u6ca1\u6709\u771f\u6b63\u4fe1\u4efb\u8fc7\u4ed6\u4eec\uff0c \u6839\u672c\u5c31\u6ca1\u6709\u628a\u4ed6\u4eec\u7684\u6b7b\u6d3b\u653e\u5728\u5fc3\u4e0a\uff0c\u6240\u4ee5\u536b\u5929\u9e4f\u65ad\u81c2\u4e4b\u540e\uff0c\u5f88\u5feb\u5c31\u5931\u8e2a\u4e86\uff0c\u56e0\u4e3a\u4ed6 \u5df2\u6ca1\u6709\u7528\u3002\u201d \u201c\u6211\u660e\u767d\u4ed6\u7684\u60f3\u6cd5\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u7559\u7740\u8fd9\u4e48\u6837\u4e00\u6279\u4eba\u5728\u8eab\u8fb9\uff0c\u5c31\u597d\u50cf\u517b\u7740 \u4e00\u6279\u864e\u72fc\u5728\u8eab\u8fb9\u4e00\u6837\uff0c\u968f\u65f6\u90fd\u5f97\u63d0\u9632\u7740\u4ed6\u4eec\u53cd\u54ac\u4e00\u53e3\uff0c\u4ed6\u517b\u7740\u4ed6\u4eec\u53ea\u4e0d\u8fc7\u662f\u8981\u7528\u6765 \u5bf9\u4ed8\u6211\u7684\uff0c\u73b0\u5728\u6b63\u597d\u5229\u7528\u6211\u6765\u9664\u53bb\u4ed6\u4eec\uff0c\u8ba9\u6211\u4eec\u540c\u5f52\u4e8e\u5c3d\uff0c\u4ed6\u5c31\u53ef\u9ad8\u6795\u65e0\u5fe7\u4e86\u3002\u201d \u201c\u4f60\u5462\uff1f\u201d\u5415\u606d\u95ee\uff1a\u201c\u4f60\u7684\u60f3\u6cd5\u662f\u4e0d\u662f\u4e5f\u8ddf\u4ed6\u4e00\u6837\uff0c\u4e5f\u60f3\u5229\u7528\u8fd9\u6b21\u673a\u4f1a\u6765\u9664\u53bb \u4e00\u4e9b\u4f60\u89c9\u5f97\u6709\u95ee\u9898\u7684\u4eba\u3002\u201d \u201c\u662f\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u5c45\u7136\u627f\u8ba4\uff1a\u201c\u6211\u7684\u60f3\u6cd5\u4e5f\u8ddf\u4ed6\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u6bd4\u4ed6\u597d\u4e00\u70b9\u800c \u5df2\uff0c\u56e0\u4e3a\u6211\u7684\u8eab\u8fb9\u6ca1\u6709\u50cf\u4f60\u548c\u6c99\u5e73\u8fd9\u6837\u7684\u4eba\u3002\u201d \u201c\u4f60\u4e5f\u77e5\u9053\u6c99\u5e73\u7684\u4e8b\uff1f\u201d \u201c\u6211\u65e9\u5c31\u7b97\u51c6\u4ed6\u4f1a\u8d70\u7684\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\u4ed6\u4e3a\u81ea\u5df1\u7559\u4e0b\u7684\u94b1\u8d22\uff0c\u5df2 \u7ecf\u8db3\u591f\u8ba9\u4ed6\u7684\u7070\u5b59\u5b50\u5750\u5403\u4e00\u751f\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u66ff\u5415\u4e09\u5356\u547d\uff1f\u201d \u5415\u606d\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u5982\u679c\u4f60\u771f\u7684\u8ba4\u4e3a\u6c99\u5e73\u80fd\u8d70\uff0c\u4f60\u5c31\u9519\u4e86\uff0c\u4e09\u7237\u4e5f\u65e9\u5c31\u7b97\u51c6\u4ed6 \u505a\u5b8c\u90a3\u4ef6\u4e8b\u4e4b\u540e\u5c31\u4f1a\u8d70\u7684\uff0c\u4ed6\u5728\u80e1\u5927\u9e9f\u4ed6\u4eec\u7684\u575f\u524d\u559d\u7684\u90a3\u4e09\u676f\u9152\u4e2d\uff0c\u5c31\u6709\u4e00\u676f\u662f\u5fc5 \u6b7b\u65e0\u6551\u7684\u65ad\u80a0\u6bd2\u9152\u3002\u201d \u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u96be\u9053\u662f\u4f60\u5728\u9152\u4e2d\u4e0b\u7684\u6bd2\uff1f\u201d \u201c\u5f53\u7136\u662f\u6211\u3002\u201d\u5415\u606d\u4e5f\u4e0d\u5426\u8ba4\uff1a\u201c\u53ea\u6709\u6211\u624d\u80fd\u505a\u8fd9\u79cd\u4e8b\uff0c\u56e0\u4e3a\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6ca1 \u6709\u7528\u7684\u5974\u624d\u800c\u5df2\uff0c\u6211\u7684\u6b66\u529f\u5728\u6c5f\u6e56\u4e2d\u53ea\u80fd\u7b97\u662f\u7b2c\u516b\u6d41\u7684\uff0c\u968f\u4fbf\u4ec0\u4e48\u4eba\u7528\u4e00\u6839\u624b\u6307\u5934 \u5c31\u53ef\u4ee5\u6740\u4e86\u6211\uff0c\u76f4\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u79c1\u4eba\u7684\u79ef\u84c4\u53ea\u6709\u4e09\u767e\u4e8c\u5341\u4e24\u94f6\u5b50\uff0c\u6240\u4ee5\u4ece\u6765\u4e5f\u6ca1 \u6709\u4eba\u6000\u7591\u8fc7\u6211\u3002\u201d \u201c\u4f46\u662f\u73b0\u5728\u4f60\u5df2\u7ecf\u662f\u4e2a\u975e\u5e38\u6709\u94b1\u7684\u4eba\u4e86\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u8bf4\uff1a\u201c\u6211\u5df2\u7ecf\u6309\u7167\u4f60\u7684\u610f\u601d\uff0c\u5c06\u4e94\u5341\u4e07\u4e24\u94f6\u5b50\u7528\u4f60\u7684\u540d\u4e49\u5206\u522b\u5b58\u5165\u4e86 \u4f60\u6307\u5b9a\u7684\u90a3\u5341\u516b\u5bb6\u94b1\u5e84\uff0c\u5b58\u6298\u4e5f\u5df2\u6446\u5728\u4f60\u6307\u5b9a\u7684\u5730\u65b9\u3002\u201d \u201c\u6211\u77e5\u9053\u3002\u201d \u201c\u4f60\u7b54\u5e94\u6211\u7684\u4e8b\u5462\uff1f\u201d \u5415\u606d\u53cd\u95ee\uff1a\u201c\u5982\u679c\u6211\u544a\u8bc9\u4f60\u5415\u4e09\u6b64\u523b\u5728\u54ea\u91cc\uff0c\u4f60\u6709\u628a\u63e1\u80fd\u6740\u4ed6\uff1f\u201d \u201c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u6211\u4ece\u6765\u90fd\u4e0d\u4f1a\u505a\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u5728\u8fd9\u4e00\u6218\u4e2d\uff0c\u6211\u7684\u635f\u5931\u672c\u6765\u5c31\u6bd4\u4ed6\u5c11\uff0c\u4f55\u51b5\u6211\u8fd8\u6709\u4e2a\u6700\u597d\u7684\u52a9 \u624b\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u5fae\u7b11\u89e3\u91ca\uff1a\u201c\u9f50\u5c0f\u71d5\u4e5f\u662f\u4e2a\u806a\u660e\u7684\u5973\u4eba\uff0c\u73b0\u5728\u5979\u7684\u5251\u6cd5\u5df2\u4e0d\u6bd4\u5c0f\u65b9\u5dee\u3002\u201d \u5415\u606d\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u518d\u95ee\u4e86\uff0c\u4ece\u8896\u5b50\u91cc\u62bd\u51fa\u4e2a\u7eb8\u5377\uff1a\u201c\u8fd9\u5f20\u56fe\u4e0a\u6807\u660e\u7684\uff0c\u5c31\u662f\u4e09\u7237 \u7684\u6839\u672c\u91cd\u5730\uff0c\u90a3\u6761\u5580\u5c14\u6e21\u91d1\u9c7c\uff0c\u5c31\u662f\u5f00\u542f\u90a3\u5730\u65b9\u79d8\u5bc6\u67a2\u7ebd\u7684\u94a5\u5319\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u63a5\u8fc7\u7eb8\u5377\uff0c\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u80af\u5982\u6b64\u8f7b\u6613\u5c31 \u628a\u8fd9\u79d8\u5bc6\u4ea4\u7ed9\u6211\uff1f\u96be\u9053\u4f60\u4e0d\u6015\u6211\u6740\u4e86\u4f60\uff1f\u201d \u5415\u606d\u7b11\u4e86\u7b11\uff1a\u201c\u90a3\u5341\u516b\u4e2a\u5b58\u6298\u90fd\u5df2\u88ab\u6211\u85cf\u5728\u4e00\u4e2a\u7edd\u5bf9\u6ca1\u6709\u522b\u4eba\u80fd\u627e\u5f97\u5230\u7684\u5730\u65b9\uff0c \u90a3\u5341\u516b\u5bb6\u94b1\u5e84\u90fd\u662f\u53ea\u8ba4\u5b58\u6298\u4e0d\u8ba4\u4eba\u7684\uff0c\u5bf9\u4f60\u6765\u8bf4\uff0c\u4e94\u5341\u4e07\u4e24\u94f6\u5b50\u53ea\u4e0d\u8fc7\u662f\u4e5d\u725b\u4e00\u6bdb \u800c\u5df2\uff0c\u4f60\u4ee5\u540e\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u7528\u5f97\u5230\u6211\u7684\u65f6\u5019\uff0c\u4f60\u8981\u6210\u5927\u4e8b\uff0c\u4f55\u82e6\u6740\u6211\u8fd9\u4e48\u6837\u4e00\u4e2a\u65e0\u8db3 \u8f7b\u91cd\u7684\u5c0f\u4eba\u7269\uff1f\u201d \u8d70\u51fa\u4e86\u5f88\u8fdc\u4e4b\u540e\uff0c\u5415\u606d\u5ffd\u7136\u53c8\u56de\u8fc7\u5934\u6765\u95ee\uff1a\u201c\u4f60\u771f\u7684\u6709\u628a\u63e1\u80fd\u786e\u5b9a\u8fd9\u4ef6\u4e8b\u7edd\u5bf9 \u4e00\u5b9a\u80fd\u8fd9\u4e48\u6837\u7ed3\u675f\uff1f\u201d \u73ed\u5bdf\u5df4\u90a3\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u201c\u8fd9\u4ef6\u4e8b\u6211\u5df2\u7ecf\u8ba1\u5212\u4e86\u5f88\u4e45\uff0c\u5f53\u7136\u5df2\u7ecf\u5f88\u6709\u628a\u63e1\u3002\u201d \u4ed6\u53c8\u7528\u8fd9\u79cd\u5947\u602a\u7684\u773c\u795e\u76ef\u7740\u5415\u606d\u770b\u4e86\u5f88\u4e45\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u8fd8\u6709\u4e2a\u79d8\u5bc6\u8981\u544a\u8bc9\u4f60\u3002\u201d \u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u201c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u2018\u7edd\u5bf9\u2019\u7684\u4e8b\u3002\u201d\u73ed\u5bdf\u5df4\u90a3\u9053\uff1a\u201c\u4ee5\u540e\u7684\u4e8b\u8c01\u4e5f\u6ca1\u6cd5 \u4e8e\u9884\u6d4b\u3002\u201d \u5415\u606d\u4e5f\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u524d\u6240\u672a\u6709\u7684\u5c0a\u656c\u4e4b\u8272\u3002 \u201c\u4f60\u8bf4\u7684\u5bf9\u6781\u4e86\uff0c\u201d\u5415\u606d\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u4f1a\u628a\u4f60\u8fd9\u53e5\u8bdd\u6c38\u8fdc\u8bb0\u5728\u5fc3\u91cc\u3002\u201d \u8bf4\u5b8c\u4e86\u8fd9\u53e5\u6d3b\uff0c\u4ed6\u5c31\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\uff0c\u73ed\u5bdf\u5df4\u90a3\u679c\u7136\u6ca1\u6709\u963b\u62e6\uff0c\u53ea\u4e0d\u8fc7\u8f7b\u8f7b\u5730 \u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u8fd8\u6709\u4e2a\u79d8\u5bc6\u8981\u544a\u8bc9\u4f60\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6709\u65f6\u5019\u6211\u5b9e\u5728\u4e5f\u5f88\u60f3\u505a\u4e00\u4e2a\u4f60\u8fd9 \u6837\u7684\u5c0f\u4eba\u7269\uff0c\u4f60\u7684\u65e5\u5b50\u8fc7\u5f97\u5b9e\u5728\u6bd4\u6211\u4eec\u5feb\u6d3b\u5f97\u591a\u3002\u201d \u73ed\u5bdf\u5df4\u90a3\u5b9e\u5728\u662f\u4eba\u6770\uff0c\u8bf4\u51fa\u7684\u6d3b\u5b9e\u5728\u5bf9\u6781\u4e86\u3002 \u8fd9\u4e16\u754c\u4e0a\u786e\u5b9e\u6ca1\u6709\u201c\u7edd\u5bf9\u201d\u7684\u4e8b\uff0c\u4ed6\u7684\u8ba1\u5212\u867d\u7136\u7cbe\u786e\u5468\u5bc6\uff0c\u53ef\u60dc\u4ed6\u6bd5\u7adf\u8fd8\u662f\u4eba\uff0c \u8fd8\u662f\u65e0\u6cd5\u5c06\u4eba\u7c7b\u7684\u601d\u60f3\u548c\u611f\u60c5\u8ba1\u7b97\u5f97\u5b8c\u5168\u51c6\u786e\u3002 \u5c24\u5176\u662f\u5c0f\u65b9\u548c\u72ec\u5b64\u75f4\u8fd9\u79cd\u4eba\u3002 \u4ed6\u4eec\u867d\u7136\u201c\u75f4\u201d\uff0c\u5374\u4e0d\u201c\u8822\u201d\uff0c\u5982\u679c\u6709\u4eba\u8ba4\u4e3a\u53ef\u4ee5\u5c06\u4ed6\u4eec\u50cf\u5080\u5121\u822c\u6446\u5e03\uff0c\u90a3\u4e2a \u4eba\u5c31\u65e0\u7591\u72af\u4e0b\u4e86\u81f4\u547d\u7684\u9519\u8bef\u3002 \u7b49\u5230\u73ed\u5bdf\u5df4\u90a3\u773c\u770b\u7740\u4ed6\u8981\u505a\u7684\u6bcf\u4ef6\u4e8b\u90fd\u51e0\u4e4e\u6309\u7167\u4ed6\u7684\u8ba1\u5212\u5b8c\u6210\u65f6\uff0c\u5ffd\u7136\u53d1\u73b0\u5c0f \u65b9\u548c\u72ec\u5b64\u75f4\u5e76\u6ca1\u6709\u6b7b\uff0c\u800c\u4e14\u5df2\u7ecf\u51fa\u73b0\u5728\u4ed6\u9762\u524d\u7684\u65f6\u5019\uff0c\u4ed6\u624d\u77e5\u9053\u81ea\u5df1\u72af\u4e0b\u7684\u9519\u8bef\u591a \u4e48\u53ef\u6015\u3002 \u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u6028\u5929\u5c24\u4eba\u3002 \u4ed6\u4e34\u6b7b\u7684\u65f6\u5019\uff0c\u53ea\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff1a\u201c\u8fd9\u662f\u6211\u81ea\u627e\u7684\uff0c\u6211\u6b7b\u800c\u65e0\u6028\u3002\u201d \u662f\u81ea\u5df1\u505a\u9519\u7684\u4e8b\uff0c\u81ea\u5df1\u5c31\u8981\u6709\u52c7\u6c14\u627f\u62c5\u3002\u65e2\u4e0d\u5fc5\u6028\u5929\u5c24\u4eba\uff0c\u4e5f\u4e0d\u5fc5\u63a8\u502d\u8d23\u4efb\uff0c \u5c31\u7b97\u9519\u5f97\u6ca1\u6709\u522b\u4eba\u60f3\u50cf\u4e2d\u90a3\u4e48\u591a\uff0c\u4e5f\u4e0d\u5fc5\u5b66\u6cfc\u5987\u9a82\u8857\uff0c\u4e5e\u4e10\u544a\u5730\u72b6\uff0c\u5230\u5904\u53bb\u5411\u4eba\u89e3 \u91ca\u3002 \u6240\u4ee5\u73ed\u5bdf\u5df4\u90a3\u8fd8\u662f\u4e0d\u6127\u4e3a\u4eba\u6770\uff0c\u4e0d\u7ba1\u4ed6\u4eba\u662f\u6b7b\u662f\u6d3b\uff0c\u4ed6\u81f3\u5c11\u8fd8\u6ca1\u6709\u505a\u8fc7\u4e22\u4eba\u73b0 \u773c\u3001\u8ba9\u4eba\u770b\u4e0d\u8d77\u7684\u4e8b\u3002"}),showOnHomepage:!0}]}}]);