"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[36215],{72986:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u6740\u695a",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u7ae0\u3000\u5bc2\u5bde\u3001\u51c4\u843d\u800c\u5e7d\u7f8e\u7684\u6b4c \u3000 \u3000\u3000\u201c\u6740\u7981\uff01\u201d \u3000\u3000\u201c\u751a\u4e48\u662f\u6740\u695a\uff1f\u201d \u3000\u3000\u201c\u6740\u695a\u662f\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u8fd8\u662f\u4e00\u4ef6\u4e1c\u897f\uff0c\u4e00\u53e5\u6697\u53f7\uff0c\u4e00\u9879\u884c\u52a8\uff0c\u8fd8\u662f\u751a\u4e48\u90fd\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5d14\u7565\u5546\u548c\u65b9\u90aa\u771f\u672c\u6765\u6839\u672c\u6ca1\u542c\u8bf4\u8fc7\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u5b57\u6709\u751a\u4e48\u7279\u6b8a\u7684\u610f\u4e49\u3002\u4ed6\u4eec\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u4e24\u4e2a\u5b57\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u770b\u89c1\u6b7b\u4eba\u548c\u6d41\u8840\uff1b\u7b49\u5230\u4ed6\u4eec\u660e\u767d\u8fd9\u4e24\u4e2a\u5b57\u7684\u771f\u6b63\u610f\u601d\uff0c\u5f88\u591a\u4e8b\u60c5\u5df2\u7ecf\u83ab\u53ef\u633d\u56de\u3001\u8ffd\u6094\u65e0\u53ca\u4e86\u3002 \u3000\u3000\u5d14\u7565\u5546\u548c\u65b9\u90aa\u771f\u672c\u6765\u4e5f\u5e76\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u662f\u7b2c\u4e00\u6b21\u540c\u65f6\u542c\u5230\u201c\u6740\u695a\u201d\u4e24\u4e2a\u5b57\uff0c\u800c\u4e14\u662f\u4e00\u6837\u7684\u611f\u89c9\u5230\u6478\u4e0d\u7740\u8111\u888b\u3002 \u3000\u3000\u5d14\u7565\u5546\u6b63\u5728\u559d\u9152\u3002 \u3000\u3000\u5927\u70ed\u7684\u5929\u6c14\u3002\u70ed\u5f97\u8def\u9762\u4e0a\u90fd\u84b8\u817e\u7740\u70df\u96fe\uff0c\u62c9\u8f66\u7684\u3001\u8d76\u8def\u7684\u3001\u529e\u8d27\u7684\u3001\u9a91\u9a6c\u7684\u3001\u7275\u9a74\u7684\uff0c\u6253\u4ece\u8fdc\u5904\u7684\u6765\uff0c\u5168\u5728\u8fd9\u70ed\u96fe\u4e2d\u53d8\u4e86\u5f62\uff0c\u4e00\u622a\u4e00\u626d\u7684\uff0c\u50cf\u5728\u70c8\u65e5\u66dd\u6652\u4e0b\u7684\u82bd\u866b\u3002\u4eba\u4eba\u90fd\u53ea\u60f3\u5feb\u4e9b\u6323\u5f97\u51e0\u6b65\u8def\uff0c\u65e9\u4e9b\u5230\u8fd9\u9a7f\u7ad9\u7684\u8336\u5bee\u6765\u8eb2\u4e00\u8eb2\u51f6\u66b4\u7684\u70c8\u9633\u3002 \u3000\u3000\u5916\u9762\u7684\u4eae\u70c8\u523a\u773c\uff0c\u663e\u5f97\u8336\u5bee\u91cc\u5206\u5916\u9634\u51c9\u3002\u5d14\u7565\u5546\u5fae\u5e26\u9189\u610f\u7684\u773c\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u5fc3\u4e2d\u53ea\u60f3\uff1a \u3000\u3000\u5927\u5730\u82cd\u751f\uff0c\u8c01\u4e0d\u51c4\u60f6\uff1f\u8c01\u4e0d\u5eb8\u788c\uff1f\u8c01\u90fd\u5728\u8d76\u7740\u81ea\u5df1\u7684\u8def\uff0c\u53ea\u4e0d\u8fc7\u770b\u8def\u597d\u4e0d\u597d\u8d70\uff0c\u6cbf\u8def\u98ce\u666f\u5982\u4f55\uff0c\u98ce\u96e8\u5982\u4f55\uff01 \u3000\u3000\u4ed6\u7ee7\u7eed\u559d\u4ed6\u7684\u9152\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u55dc\u676f\u4e2d\u7269\uff0c\u4f46\u4eca\u5929\u6ca1\u6709\u591a\u559d\u3002 \u3000\u3000\u56e0\u4e3a\u518d\u8fc7\u4e09\u5341\u91cc\u5730\uff0c\u5c31\u662f\u6d1b\u9633\u57ce\u3002 \u3000\u3000\u4ed6\u6b64\u884c\u662f\u8981\u6765\u4fa6\u67e5\u4e00\u4ef6\u6740\u6b7b\u5145\u519b\u671d\u5b98\u5b5f\u968f\u56ed\u7684\u6848\u5b50\uff0c\u4ed6\u8981\u4fdd\u6301\u6e05\u9192\uff0c\u6240\u4ee5\u4ed6\u4e0d\u80fd\u75db\u996e\uff0c\u4ed6\u4e0d\u80fd\u9189\u3002 \u3000\u3000\u5176\u5b9e\u4f17\u4eba\u7686\u9189\uff0c\u4f55\u5fc5\u72ec\u9192\uff1f\u4f17\u4eba\u7686\u9192\uff0c\u4f55\u5fc5\u6c42\u9189\uff1f\u4eba\u751f\u91cc\u4e0d\u59a8\u5fae\u9189\uff0c\u7565\u4f5c\u9169\u914a\uff0c\u773c\u91cc\u4e7e\u5764\uff0c\u624d\u662f\u6700\u5e7b\u4e2d\u6c42\u771f\u3001\u5982\u771f\u4f3c\u5e7b\u7684\u4e8b\u3002 \u3000\u3000\u5d14\u7565\u5546\u559d\u4e86\u51e0\u58f6\u9152\uff0c\u56e0\u5df2\u8d76\u4e86\u5341\u51e0\u5929\u7684\u8def\uff0c\u6709\u4e9b\u56f0\u4e4f\uff0c\u4fbf\u60f3\u778c\u4e00\u778c\u2026\u2026 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4f20\u6765\u9a6c\u8e44\u75be\u54cd\uff0c\u50cf\u884c\u96f7\u4e00\u822c\uff0c\u8fc5\u5373\u8feb\u8fd1\u3002 \u3000\u3000\u4e24\u540d\u7a84\u8863\u77ed\u6253\u3001\u655e\u895f\u7cfb\u5dfe\u7684\u5927\u6c49\uff0c\u7b56\u9a6c\u9a70\u5377\u800c\u81f3\uff0c\u9976\u662f\u5728\u767d\u65e5\u91cc\u3001\u5b98\u9053\u4e0a\uff0c\u4e5f\u5f88\u5c11\u89c1\u5230\u8fd9\u6837\u7684\u52b2\u9a91\u3001\u8fd9\u822c\u7684\u58ee\u6c49\uff01 \u3000\u3000\u770b\u8fd9\u4e24\u9a91\u5982\u8131\u5f29\u4e4b\u77e2\u7684\u6765\u52bf\uff0c\u4fbf\u53ef\u4ee5\u65ad\u5b9a\u5927\u90fd\u4e0d\u4f1a\u5728\u8fd9\u9a7f\u7ad9\u4f5c\u6b47\u3002\u7531\u4e8e\u4ed6\u4eec\u5954\u884c\u6781\u6025\uff0c\u5728\u9053\u4e0a\u6b63\u5411\u8336\u5bee\u8d70\u6765\u7684\u884c\u4eba\uff0c\u4e0d\u7ba1\u662f\u5f80\u57ce\u95e8\u65b9\u5411\u8fd8\u662f\u80cc\u5411\uff0c\u751f\u6050\u88ab\u98de\u9a91\u649e\u4e0a\uff0c\u7eb7\u7eb7\u8d70\u907f\u4e0d\u8fed\u3002 \u3000\u3000\u8fd9\u4f7f\u5f97\u8336\u5bee\u91cc\u7684\u5ba2\u4eba\u90fd\u60ca\u5f02\u7684\u6ce8\u89c6\u3002 \u3000\u3000\u5d14\u7565\u5546\u672c\u60f3\u6795\u9996\u81c2\u4e0a\uff0c\u5c0f\u606f\u7247\u523b\uff0c\u8fd9\u65f6\uff0c\u4e5f\u9661\u7136\u7741\u5f00\u795e\u5149\u6e5b\u7136\u7684\u53cc\u76ee\uff0c\u633a\u8d77\u53cc\u7709\uff0c\u5f80\u5916\u671b\u53bb\uff0c\u4f46\u4f0f\u5728\u684c\u4e0a\u5348\u5bd0\u7684\u59ff\u6001\u5b8c\u5168\u4e0d\u53d8\u3002 \u3000\u3000\u4e24\u9a91\u5df2\u9a70\u8fd1\u8336\u5bee\uff0c\u9014\u4eba\u60ca\u547c\u3001\u8d70\u907f\uff0c\u62f4\u5728\u8336\u5bee\u9644\u8fd1\u7684\u7272\u53e3\u4e5f\u88ab\u60ca\u5f97\u5e0c\u807f\u807f\u4e00\u9635\u987f\u8e44\u3002 \u3000\u3000\u5d14\u7565\u5546\u7684\u89c6\u7ebf\uff0c\u5374\u4e0d\u5728\u90a3\u4e24\u540d\u52b2\u88c5\u5927\u6c49\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u53d1\u73b0\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u9014\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u9752\u5e74\u4e66\u751f\uff0c\u7a7f\u7740\u4e00\u8eab\u6d01\u767d\u7684\u888d\u5b50\uff0c\u8fdc\u8fdc\u770b\u53bb\uff0c\u771f\u662f\u767d\u8863\u80dc\u96ea\uff0c\u8863\u767d\u4e0d\u6cbe\u5c18\uff0c\u7d20\u51c0\u5f88\u50cf\u6df1\u5c71\u5e7d\u8c37\u4e2d\u4e00\u9053\u6e05\u7011\uff0c\u5d14\u7565\u5546\u4e00\u773c\u671b\u53bb\uff0c\u5c31\u611f\u89c9\u5230\u8fd9\u4eff\u4f5b\u662f\u4e2a\u4e0d\u98df\u4eba\u95f4\u70df\u706b\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u5d14\u7565\u5546\u5bf9\u65b9\u90aa\u771f\u7684\u7b2c\u4e00\u5370\u8c61\u3002 \u3000\u3000\u4e24\u5339\u5065\u9a6c\u75be\u9a70\u7684\u65f6\u5019\uff0c\u6709\u4e00\u4e2a\u5f2f\u8170\u9a7c\u80cc\u7684\u8001\u4eba\u5bb6\uff0c\u8d70\u6162\u4e86\u4e00\u4e9b\u4e9b\uff0c\u8ddf\u7740\u5c31\u8981\u88ab\u94c1\u8e44\u649e\u5012\uff0c\u4f46\u662f\u90a3\u81ea\u8863\u4e66\u751f\u5ffd\u7136\u4e00\u95ea\uff0c\u518d\u4e0b\u6765\u5c31\u53d1\u73b0\u90a3\u8001\u4eba\u5bb6\u597d\u7aef\u7aef\u7684\u5df2\u5230\u4e86\u8def\u65c1\uff0c\u5728\u767d\u8863\u4e66\u751f\u633d\u6276\u4e0b\u5e73\u5e73\u5b89\u5b89\u7684\u5728\u8d70\u8def\uff0c\u53ea\u4e0d\u8fc7\u8138\u4e0a\u5374\u9732\u51fa\u5341\u5206\u832b\u7136\u4e0d\u89e3\u7684\u795e\u60c5\u6765\u3002 \u3000\u3000\u90a3\u4e24\u5339\u9a6c\u4e0a\u7684\u5927\u6c49\uff0c\u56e0\u4e3a\u8d76\u8def\u5306\u5fd9\uff0c\u4e5f\u6ca1\u6ce8\u610f\u5230\u8fd9\u53d1\u751f\u5728\u77ac\u95f4\u7684\u53d8\u5f02\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u53d1\u73b0\u5728\u90a3\u4e00\u5239\u95f4\uff0c\u6709\u4e00\u4e2a\u770b\u6765\u5f31\u4e0d\u7981\u98ce\u7684\u4e66\u751f\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e0b\u65bd\u5c55\u4e86\u60ca\u4eba\u7684\u8f7b\u529f\uff0c\u6551\u4e86\u4e00\u6761\u4eba\u547d\u3002 \u3000\u3000\u9664\u4e86\u5d14\u7565\u5546\u3002 \u3000\u3000\u4ed6\u53d1\u73b0\u767d\u8863\u4e66\u751f\u5728\u77ac\u606f\u95f4\u65bd\u5c55\u4e86\u8f7b\u529f\u3002 \u3000\u3000\u800c\u4e14\u8fd8\u662f\u4e00\u79cd\u7edd\u4e16\u7684\u8f7b\u529f\u3002 \u3000\u3000\u201c\u4e07\u53e4\u4e91\u9704\u4e00\u7fbd\u6bdb\u201d\u2014\u2014\u4e09\u5341\u5e74\u524d\uff0c\u4e00\u4ee3\u5947\u4fa0\u65b9\u6b4c\u541f\uff0c\u4fbf\u4ed7\u8fd9\u4e00\u79cd\u63c9\u5408\u4e86\u4e03\u516b\u79cd\u8f7b\u529f\u4e4b\u5927\u6210\u7684\u8eab\u6cd5\uff0c\u98de\u8d8a\u6570\u5341\u4e08\u7684\u58d1\u8c37\uff0c\u6765\u62ef\u6551\u5404\u6d3e\u6b66\u6797\u540c\u9053\u4e8e\u6c34\u6df1\u706b\u70ed\u4e4b\u4e2d\uff0c\u800c\u4eca\uff0c\u7adf\u7136\uff0c\u5728\u8fd9\u6d1b\u9633\u53e4\u9053\u4e0a\uff0c\u65e5\u6b63\u5f53\u7a7a\u4e0b\uff0c\u4f17\u4eba\u4e0d\u515a\u4e2d\uff0c\u5728\u4e00\u4e2a\u767d\u8863\u4e66\u751f\u8eab\u4e0a\u91cd\u73b0\u3002 \u3000\u3000\u5d14\u7565\u5546\u7684\u773c\u775b\u4eae\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u9189\u4e86\u7684\u4eba\uff0c\u8c01\u90fd\u6ca1\u6709\u8fd9\u4e48\u4eae\u7684\u773c\u775b\u3002 \u3000\u3000\u90a3\u4e24\u5339\u75be\u9a70\u7684\u9a6c\uff0c\u4e0d\u610f\u5374\u5728\u8336\u5bee\u524d\u9aa4\u6b62\uff0c\u7531\u4e8e\u52d2\u9a6c\u592a\u6025\uff0c\u4e24\u9a6c\u4e00\u9f50\u4eba\u7acb\u957f\u5636\uff0c\u5e97\u91cc\u7684\u5ba2\u4eba\u5185\u5fc3\u6014\u5fe1\uff0c\u4e0d\u77e5\u8fd9\u4e24\u4eba\u662f\u751a\u4e48\u6765\u8def\uff0c\u5e97\u91cc\u7684\u4f19\u8ba1\u89c1\u4e24\u9a91\u6765\u52bf\u6c79\u6c79\uff0c\u90fd\u4e0d\u6562\u4e0a\u524d\u62db\u547c\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u540d\u6d53\u7709\u6d53\u80e1\u7684\u5927\u6c49\u4fef\u8eab\u5927\u58f0\u5730\u95ee\uff1a\u201c\u5582\uff0c\u638c\u67dc\u7684\uff0c\u501f\u4e2a\u8baf\u513f\uff0c\u53ef\u89c1\u4e24\u9876\u5927\u8f7f\u3001\u4e00\u884c\u5b98\u5bb6\u4f8d\u4ece\uff0c\u6253\u8fd9\u513f\u6765\u8fc7\uff1f\u201d\u4ed6\u55d3\u95e8\u867d\u5927\uff0c\u8bf4\u8bdd\u5012\u8fd8\u633a\u5ba2\u6c14\u7684\u3002 \u3000\u3000\u638c\u67dc\u7684\u5fd9\u7740\u8d77\u8eab\u51fa\u8fce\uff0c\u56e0\u4e3a\u4e0d\u77e5\u5bf9\u65b9\u662f\u751a\u4e48\u6765\u8def\uff0c\u6240\u4ee5\u8d8a\u53d1\u5ba2\u6c14\uff1a\u201c\u56de\u5927\u7237\u7684\u8bdd\uff0c\u4eca\u5929\u6709\u9556\u884c\u7684\u3001\u5546\u961f\u7684\u3001\u4e70\u5356\u7684\u6765\u8fc7\uff0c\u5c31\u4e0d\u89c1\u6709\u60a8\u8bf4\u7684\u5b98\u7737\u961f\u884c\u6765\u8fc7\u3002\u201d\u53ea\u4e0d\u6562\u9080\u4ed6\u4eec\u4e0b\u9a6c\u8fdb\u5e97\u91cc\u6b47\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u9c7c\u773c\u72ee\u5507\u7684\u5927\u6c49\u602a\u773c\u4e00\u77aa\uff0c\u5495\u54dd\u9053\uff1a\u201c\u600e\u4e48\u8fd8\u6ca1\u6765\uff0c\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u5148\u524d\u90a3\u540d\u6d53\u7709\u5927\u6c49\u5fd9\u4f7f\u773c\u8272\u5236\u6b62\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u4fbf\u62f1\u624b\u9053\uff1a\u201c\u6211\u8c22\u4f60\u5566\uff0c\u4e5f\u8bb8\u662f\u9519\u8fc7\u4e86\u9a7f\u5934\uff0c\u53e8\u6270\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4e24\u4eba\u5406\u559d\u4e00\u58f0\uff0c\u6253\u9a6c\u6025\u9a70\u800c\u53bb\uff0c\u53ea\u662f\u6c99\u5c18\u6eda\u626c\uff0c\u4e00\u5ffd\u513f\u4fbf\u6ca1\u4e86\u8e2a\u5f71\u3002 \u3000\u3000\u90a3\u767d\u8863\u4e66\u751f\u5374\u5df2\u884c\u5165\u4e86\u5e97\u5185\uff0c\u627e\u4e86\u5f20\u6700\u5e72\u51c0\u7684\u4f4d\u5b50\uff0c\u5750\u4e0b\uff0c\u4f19\u8ba1\u4e0a\u6765\u5012\u8336\uff0c\u4ed6\u5374\u53ea\u8981\u4e86\u4e00\u676f\u6c34\uff0c\u7ec6\u7ec6\u7684\u54c1\u5c1d\u7740\uff0c\u4eff\u4f5b\u6c34\u91cc\u6709\u56de\u5473\u65e0\u7a77\u3002 \u3000\u3000\u5d14\u7565\u5546\u5fcd\u4e0d\u4f4f\u53c8\u671b\u4e86\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u4e00\u773c\uff0c\u53ea\u6709\u4e00\u4e2a\u611f\u89c9\uff1a \u3000\u3000\u597d\u4e00\u4e2a\u4fca\u79c0\u800c\u5fe7\u6092\u7684\u4eba\uff01 \u3000\u3000\u8fd9\u4eba\u7684\u4e00\u4e3e\u624b\uff0c\u4e00\u6295\u8db3\uff0c\u4e00\u5c55\u7709\uff0c\u4e00\u56de\u7738\uff0c\u90fd\u6709\u8bf4\u4e0d\u51fa\u7684\u50b2\u5cb8\u548c\u5fe7\u6101\uff0c\u5c31\u50cf\u9ad8\u5c71\u7684\u767d\u96ea\uff0c\u9057\u4e16\u800c\u72ec\u7acb\uff0c\u90a3\u79cd\u4e0d\u6c42\u4e16\u95f4\u4e88\u540c\u60c5\u3001\u5bc2\u5929\u5bde\u5730\u7684\u51b7\u50b2\u548c\u5fe7\u6101\u3002 \u3000\u3000\u5c24\u5176\u90a3\u4e00\u53cc\u773c\u775b\u3002 \u3000\u3000\u5d14\u7565\u5546\u5fc3\u5934\u5fae\u5fae\u4e00\u9707\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u89c1\u8fc7\u5fe7\u6092\u5f97\u90a3\u4e48\u4e0d\u5728\u4e4e\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u624b\u4e0a\u4e00\u4e2a\u65e7\u65e7\u7684\u84dd\u5305\u88b1\uff0c\u7528\u4e00\u628a\u957f\u5f62\u7269\u4f53\u6311\u4e32\u7740\uff0c\u90a3\u957f\u5f62\u7684\u7269\u4f53\u88f9\u7740\u4e00\u5c42\u6d17\u5f97\u892a\u4e86\u516b\u6210\u989c\u8272\u7684\u84dd\u5e03\uff0c\u60f3\u5fc5\u662f\u5251\u3002 \u3000\u3000\u4e00\u53bb\u5df4\u65e7\u5e03\u7d27\u88f9\u7740\u7684\u5251\u3002 \u3000\u3000\u53ea\u542c\u5728\u8336\u5c45\u91cc\u6709\u4e24\u4e2a\u9556\u5e08\u5728\u4ea4\u6362\u610f\u89c1\uff1a \u3000\u3000\u201c\u4f60\u770b\u662f\u751a\u4e48\u6765\u8def\uff1f\u201d \u3000\u3000\u201c\u6839\u672c\u5c31\u4e0d\u5bf9\u8def\uff0c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u51c6\u662f\u6765\u6478\u5e95\u63a2\u9053\u7684\u3002\u201d \u3000\u3000\u201c\u7167\u5440\uff0c\u6211\u770b\u89c1\u4ed6\u4eec\u662f\u5148\u6765\u653e\u54e8\uff0c\u5f85\u4f1a\u513f\u5c11\u4e0d\u514d\u6709\u4e8b\u3002\u8fd9\u7b49\u660e\u76ee\u5f20\u80c6\uff0c\u6240\u8c13\u4e0d\u662f\u731b\u9f99\u4e0d\u8fc7\u6c5f\uff0c\u624b\u5e95\u4e0b\u81ea\u7136\u6709\u65a4\u4e24\u3002\u201d \u3000\u3000\u201c\u8fd9\u53ef\u602a\u5440\uff0c\u770b\u4ed6\u4eec\u662f\u6478\u4e0a\u4e86\u5b98\u8def\uff0c\u8fd9\u53ef\u4e0d\u662f\u5bfb\u5e38\u7684\u4e70\u5356\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u8fd8\u662f\u907f\u4e00\u907f\u7f62\uff0c\u54b1\u4eec\u2018\u4e94\u82b1\u9556\u5c40\uff0c\u53ef\u72af\u4e0d\u7740\u5728\u8fd9\u513f\u80e1\u91cc\u80e1\u6d82\u7684\u6328\u7ea2\u5200\u767d\u5200\u3002\u201d \u3000\u3000\u201c\u7167\u5440\u201d \u3000\u3000\u90a3\u4e00\u80a5\u4e00\u7626\u7684\u4e24\u4e2a\u9556\u5e08\uff0c\u6b63\u60f3\u8d77\u8eab\u7ed3\u8d26\uff0c\u5ffd\u7136\u89c1\u5e97\u95e8\u8fdb\u6765\u4e86\u4e00\u4e2a\u7cbe\u795e\u77cd\u70c1\u3002\u7626\u9aa8\u5ce5\u5d58\u7684\u767d\u80e1\u5b50\u8001\u5934\uff0c\u4e00\u53cc\u70af\u70af\u6709\u795e\u7684\u9510\u76ee\uff0c\u4e00\u8fdb\u6765\u5c31\u795e\u5a01\u5730\u9021\u89c6\u5e97\u91cc\u4e00\u9053\uff0c\u8fd9\u4e00\u5239\u90a3\uff0c\u5e97\u91cc\u6bcf\u4e00\u4e2a\u4eba\u4eff\u4f5b\u90fd\u7ed9\u4ed6\u5982\u51b7\u7535\u7684\u773c\u795e\u903c\u4e86\u4e00\u903c\uff0c\u7136\u540e\u8fd9\u8001\u5934\u5411\u638c\u67dc\u95ee\uff1a\u201c\u6709\u6ca1\u6709\u770b\u89c1\u6c60\u516c\u5b50\u7684\u961f\u4f0d\u6765\u8fc7\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u4e5f\u770b\u51fa\u52bf\u5934\u4e0d\u597d\uff0c\u8e51\u5685\u9053\uff1a\u201c\u751a\u4e48\u6c60\u516c\u5b50\u2026\u2026\u201d\u5fc3\u4e2d\u4e00\u76f4\u5728\u6253\u7a81\u3002\u6d1b\u9633\u57ce\u91cc\uff0c\u6709\u201c\u56db\u5927\u516c\u5b50\u201d\uff0c\u90a3\u662f\u201c\u5c0f\u516c\u5b50\u201d\u6c60\u65e5\u66ae\u3001\u201c\u591a\u60c5\u516c\u5b50\u201d\u6e38\u7389\u906e\u3001\u201c\u8001\u516c\u5b50\u201d\u56de\u767e\u5e94\u3001\u201c\u5973\u516c\u5b50\u201d\u845b\u94c3\u94c3\u3002 \u3000\u3000\u8fd9\u201c\u6d1b\u9633\u56db\u516c\u5b50\u201d\uff0c\u95e8\u4e0b\u65e0\u4e0d\u517b\u58eb\uff0c\u5c11\u5219\u6709\u4e24\u4e09\u767e\uff0c\u591a\u5219\u903e\u5343\uff0c\u800c\u4e14\u7ed3\u4ea4\u5f02\u58eb\u5947\u4eba\uff0c\u8ddf\u5b98\u8859\u53c8\u6709\u5f80\u6765\uff0c\u671d\u4e2d\u4e5f\u6709\u4ed7\u836b\uff0c\u90fd\u662f\u65e2\u5bcc\u4e14\u8d35\u3001\u6781\u6709\u58f0\u540d\u3001\u751a\u5177\u5f71\u54cd\u529b\u7684\u4eba\u7269\uff0c\u5c31\u7b97\u662f\u53bf\u5b98\u3001\u5fa1\u53f2\uff0c\u4e5f\u5bf9\u8fd9\u56db\u5927\u516c\u5b50\u523b\u610f\u7ed3\u7eb3\uff0c\u8fd9\u56db\u4f4d\u516c\u5b50\u672c\u8eab\u5728\u6587\u624d\u3002\u6b66\u827a\u4e0a\uff0c\u5404\u6709\u9020\u8be3\uff0c\u8fd9\u8336\u94fa\u638c\u67dc\uff0c\u4e00\u542c\u8fd9\u5e72\u201c\u4e0d\u901f\u4e4b\u5ba2\u201d\uff0c\u4f3c\u662f\u51b2\u7740\u201c\u56db\u5927\u516c\u5b50\u201d\u4e2d\u6700\u5f97\u4eba\u7f18\u7684\u6c60\u65e5\u66ae\u6c60\u5c11\u516c\u5b50\u800c\u6765\uff0c\u5fc3\u4e2d\u65e9\u5c31\u614c\u5f97\u60ac\u5728\u534a\u7a7a\uff0c\u4e0d\u6562\u5b9e\u8bdd\u5b9e\u5e94\u3002 \u3000\u3000\u90a3\u77cd\u5065\u7684\u8001\u5934\u5b50\u5374\u5ffd\u7136\u81ea\u8896\u91cc\u6478\u51fa\u4e00\u9762\u8170\u724c\uff0c\u5728\u638c\u67dc\u9762\u524d\u8fc5\u5feb\u7684\u6643\u4e86\u4e00\u6643\uff0c\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u6211\u662f\u90bb\u53bf\u6355\u5feb\uff0c\u5949\u547d\u6765\u8ffd\u67e5\u4e00\u6869\u6848\u5b50\uff0c\u4f60\u53ef\u522b\u6b3a\u5b98\u7792\u516c\uff01\u201d \u3000\u3000\u90a3\u638c\u67dc\u4e00\u89c1\u662f\u8859\u95e8\u6765\u7684\u4eba\uff0c\u5fd9\u8bf4\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\uff0c\u6c60\u516c\u5b50\u8fd8\u6ca1\u6709\u5230\u6765\uff0c\u4f46\u65e9\u5148\u6709\u6c60\u5e9c\u7684\u4eba\u6765\u8fc7\uff0c\u9884\u5148\u6253\u70b9\u597d\u4e86\uff0c\u6c60\u516c\u5b50\u7684\u961f\u4f0d\u5f85\u4f1a\u513f\u5c31\u8981\u7ecf\u8fc7\uff0c\u6211\u4eec\u656c\u5907\u6c34\u9152\uff0c\u4ee5\u4f9b\u4ed6\u4eec\u4f11\u6b47\u996e\u7528\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u773c\u795e\u4e00\u4eae\uff0c\u53ea\u8bf4\uff1a\u201c\u679c\u7136\uff0c\u597d\uff0c\u5f88\u597d\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u542c\u4e00\u9635\u5406\u559d\u4e4b\u58f0\uff0c\u4e24\u4e2a\u811a\u592b\uff0c\u8d64\u818a\u642d\u4e2d\uff0c\u62ac\u7740\u4e00\u9876\u9ed1\u7cca\u7cca\u7684\u5c0f\u8f7f\u5b50\uff0c\u8d70\u8fd1\u8336\u5bee\u6765\uff0c\u811a\u592b\u7ecf\u8fc7\u65f6\uff0c\u626d\u5934\u671b\u5411\u5e97\u91cc\uff0c\u53ea\u89c1\u90a3\u661f\u94c4\u8001\u5934\u4e00\u9894\u9996\uff0c\u811a\u592b\u4fbf\u5728\u69d0\u6811\u836b\u4e0b\u505c\u8f7f\uff0c\u62b9\u6c57\u6b47\u606f\u3002 \u3000\u3000\u8fd9\u4e00\u8def\u731b\u70ed\u7684\u5929\uff0c\u4e24\u4e2a\u811a\u592b\u62ac\u4e86\u8fd9\u4e48\u4e00\u9876\u8f7f\u5b50\uff0c\u5954\u884c\u957f\u9014\uff0c\u5c45\u7136\u8138\u4e0d\u7ea2\u3001\u6c14\u4e0d\u5598\uff0c\u53ea\u662f\u51fa\u4e86\u4e00\u8eab\u7684\u6c57\uff0c\u731b\u70c8\u7684\u9633\u5149\uff0c\u7167\u7099\u5728\u4ed6\u4eec\u808c\u8089\u8d4f\u7a81\u7684\u81c2\u808c\u4e0a\uff0c\u8d8a\u53d1\u4ee4\u4eba\u611f\u5230\u4e00\u79cd\u903c\u4eba\u7684\u521a\u70c8\u4e4b\u6c14\u3002 \u3000\u3000\u800c\u8f7f\u5b50\u5750\u7684\u4e5f\u4e0d\u77e5\u662f\u751a\u4e48\u4eba\uff0c\u5927\u70ed\u7684\u5929\uff0c\u5df2\u5728\u91cc\u9762\u618b\u4e86\u90a3\u4e48\u4e45\uff0c\u4e5f\u4e0d\u51fa\u6765\u51c9\u5feb\u51c9\u5feb\u3001\u900f\u900f\u6c14\u3002 \u3000\u3000\u90a3\u4e24\u540d\u9556\u5e08\u672c\u6765\u6b63\u8981\u79bb\u5f00\uff0c\u4f46\u89c1\u6709\u5b98\u8859\u7684\u8001\u624b\u6765\u4e86\uff0c\u5012\u7559\u4e86\u4e0b\u6765\uff0c\u60f3\u770b\u770b\u70ed\u95f9\u3002 \u3000\u3000\u767d\u8863\u4e66\u751f\u8fd8\u5728\u54c1\u5c1d\u7740\u676f\u91cc\u7684\u6c34\uff0c\u7709\u5b87\u95f4\u8fd8\u662f\u6d0b\u6ea2\u7740\u4e00\u80a1\u6de1\u6de1\u7684\u90c1\u8272\u3002 \u3000\u3000\u5d14\u7565\u5546\u8fd9\u6837\u591a\u770b\u4e86\u51e0\u773c\uff0c\u5ffd\u7136\u4e4b\u95f4\uff0c\u767d\u8863\u4e66\u751f\u4f3c\u6709\u6240\u8b66\u89c9\uff0c\u76ee\u5149\u4e5f\u5411\u4ed6\u8fd9\u8fb9\u770b\u6765\u3002 \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u4e00\u961f\u4eba\u9a6c\uff0c\u5ffd\u5728\u9ec4\u5c18\u6f2b\u5929\u7684\u5c3d\u5934\u51fa\u73b0\u3002 \u3000\u3000\u8fd9\u4e00\u961f\u4eba\u9a6c\uff0c\u603b\u5171\u5341\u4e00\u4eba\u3002 \u3000\u3000\u56db\u9a91\u5728\u524d\uff0c\u56db\u9a91\u5728\u540e\uff0c\u4e09\u9a91\u5c45\u4e2d\u3002 \u3000\u3000\u524d\u540e\u516b\u9a91\uff0c\u4e00\u6982\u7384\u8863\u88b1\u5934\uff0c\u795e\u5bb9\u65e0\u4e0d\u7cbe\u608d\u4fca\u79c0\u3002 \u3000\u3000\u4e2d\u95f4\u4e09\u9a91\uff0c\u5de6\u8fb9\u662f\u4e00\u540d\u6587\u58eb\uff0c\u4e94\u7edc\u957f\u9aef\uff0c\u53ca\u80f8\u800c\u6b62\uff0c\u8138\u5982\u51a0\u7389\uff1b\u53f3\u8fb9\u7684\u662f\u4e00\u540d\u6b66\u58eb\uff0c\u4e00\u526f\u52c7\u608d\u575a\u5fcd\u7684\u6c14\u6982\uff0c\u9a91\u5728\u9a6c\u4e0a\uff0c\u5c31\u50cf\u4e00\u4e2a\u6218\u795e\u3002 \u3000\u3000\u8fd9\u6574\u652f\u961f\u4f0d\uff0c\u90fd\u53ea\u610f\u5473\u7740\u4e00\u4ef6\u4e8b\u60c5\uff1a\u4ed6\u4eec\u90fd\u5728\u5b88\u62a4\u7740\u6700\u4e2d\u95f4\u7684\u90a3\u4f4d\u516c\u5b50\u3002 \u3000\u3000\u90a3\u4f4d\u738b\u5b59\u516c\u5b50\u822c\u7684\u5e74\u8f7b\u4eba\uff0c\u9a91\u7740\u6beb\u65e0\u6742\u8272\u7684\u4e4c\u7762\u9a6c\uff0c\u91d1\u978d\u73e0\u4f69\uff0c\u9a6c\u4e0a\u8fd8\u6491\u7740\u4e00\u65b9\u9ec4\u5e54\uff0c\u663e\u7136\u662f\u7528\u6765\u906e\u63a9\u9633\u5149\u7684\u3002\u9a6c\u4e0a\u7684\u516c\u5b50\uff0c\u88ab\u9ec4\u5e54\u9634\u5f71\u906e\u63a9\u7740\uff0c\u8138\u76ee\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u89c1\u4ed6\u7ef8\u888d\u7f0e\u9774\uff0c\u4f69\u5251\u9576\u7fe0\uff0c\u4e00\u53ea\u624b\u642d\u5728\u7f13\u8f94\u4e0a\uff0c\u81ea\u751f\u751f\u7684\u5f88\u662f\u597d\u770b\u3002 \u3000\u3000\u90a3\u5728\u7ec6\u8317\u767d\u5f00\u6c34\u7684\u4e66\u751f\uff0c\u5374\u4f4e\u4f4e\u7684\u54fc\u4e86\u4e00\u58f0\uff0c\u5fae\u5fae\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u5341\u4e00\u9a91\u5954\u8fd1\u8336\u5bee\uff0c\u901f\u5ea6\u4e5f\u7f13\u4e86\u4e0b\u6765\uff0c\u9a6c\u4e0a\u90a3\u540d\u575a\u5fcd\u7684\u6b66\u58eb\u9053\uff1a\u201c\u5218\u7237\uff0c\u4f60\u53ef\u662f\u5b89\u6392\u5728\u8fd9\u513f\u6b47\u6b47\uff1f\u201d \u3000\u3000\u90a3\u6587\u58eb\u5fd9\u9053\uff1a\u201c\u6b63\u662f\u3002\u4e0d\u77e5\u516c\u5b50\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u516c\u5b50\u9053\uff1a\u201c\u597d\u554a\uff0c\u5927\u70ed\u7684\u5929\uff0c\u4e5f\u4e0d\u8d76\u5728\u4e00\u65f6\uff0c\u53ea\u8981\u5728\u57ce\u95e8\u5173\u95ed\u524d\u5165\u5173\u4fbf\u884c\u3002\u201d\u4ed6\u8fd9\u6837\u4e00\u5f00\u53e3\uff0c\u8c01\u90fd\u542c\u5f97\u51fa\u4ed6\u662f\u4e2a\u968f\u548c\u7684\u4eba\u3002 \u3000\u3000\u6587\u58eb\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u7cbe\u660e\u7684\u7528\u773c\u775b\u8fc5\u901f\u5730\u9021\u5de1\u5468\u906d\u4e00\u904d\uff1b\u624d\u6325\u6325\u624b\uff0c\u524d\u9762\u4e8c\u9a91\u52b2\u6c49\uff0c\u7acb\u523b\u4e0b\u9a6c\uff0c\u4e3a\u90a3\u540d\u516c\u5b50\u7275\u7f30\u76f8\u6276\uff0c\u90a3\u516c\u5b50\u4e5f\u4e0d\u8981\u4ed6\u4eec\u7275\u5f15\uff0c\u4e00\u8038\u8eab\u4fbf\u843d\u4e86\u5730\uff0c\u8f7b\u5f97\u50cf\u56db\u4e24\u68c9\u82b1\u3002 \u3000\u3000\u90a3\u6587\u58eb\u9053\uff1a\u201c\u8fd9\u513f\u79bb\u6d1b\u9633\u4e0d\u53ca\u4e09\u5341\u91cc\u5730\uff0c\u7533\u724c\u65f6\u5206\u524d\u51c6\u53ef\u5230\u5f97\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8336\u5c45\u4e2d\u7684\u4eba\u65e0\u4e0d\u76f4\u52fe\u52fe\u7684\u770b\u7740\u8fd9\u4e00\u884c\u4eba\uff0c\u76ee\u5149\u5c24\u5176\u96c6\u4e2d\u5728\u90a3\u6c14\u8d28\u9ad8\u8d35\u7684\u516c\u5b50\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u6d1b\u9633\u56db\u516c\u5b50\u201d\u540d\u52a8\u5929\u4e0b\uff0c\u4e0d\u8bba\u662f\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u83ab\u4e0d\u66fe\u542c\u8bf4\u8fc7\uff0c\u90fd\u60f3\u8d81\u8fd9\u96be\u9022\u96be\u9047\u7684\u673a\u4f1a\uff0c\u591a\u770b\u4ed6\u51e0\u773c\u3002 \u3000\u3000\u53ea\u542c\u90a3\u80cc\u540e\u6302\u4e86\u4e2a\u7bad\u58f6\u7684\u9556\u5e08\u4f4e\u58f0\u9053\uff1a\u201c\u4eba\u8bf4\u6d1b\u9633\u6c60\u516c\u5b50\u662f\u4eba\u4e2d\u9f99\u98ce\uff0c\u6b64\u8bed\u679c\u7136\u4e0d\u5dee\uff0c\u4f60\u770b\u4ed6\uff0c\u6e05\u7709\u79c0\u76ee\uff0c\u7389\u6811\u4e34\u98ce\uff0c\u5c82\u662f\u5bfb\u5e38\u80fd\u6bd4\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u524d\u8170\u7cfb\u7740\u9556\u56ca\u7684\u9556\u5e08\u5374\u9053\uff1a\u201c\u6211\u770b\u4ed6\u8eab\u8fb9\u7684\u6587\u80c6\u6b66\u5c06\uff0c\u624d\u4e0d\u5f97\u4e86\uff0c\u4e0d\u6127\u662f\u4f17\u98df\u5ba2\u4e00\u5343\u4e94\u767e\u5f02\u4eba\u4e2d\u9009\u62d4\u7684\u3002\u201d \u3000\u3000\u80cc\u6302\u7bad\u58f6\u7684\u9556\u5e08\u9053\uff1a\u201c\u90a3\u4e2a\u7559\u957f\u987b\u6447\u5b54\u660e\u6247\u7684\uff0c\u4fbf\u662f\u6587\u80c6\u5218\u662f\u4e4b\u4e86\u7f62\uff1f\u8fd9\u4eba\u5c31\u51ed\u7740\u624d\u667a\u8ba1\u7b56\uff0c\u628a\u71d5\u84df\u4e09\u80a1\u6076\u532a\uff0c\u5168\u5728\u6d1b\u9633\u6c60\u516c\u5b50\u540d\u4e0b\u6549\u5e73\uff0c\u5efa\u529f\u4e0d\u5c11\u54e9\u2026\u2026\u201d \u3000\u3000\u90a3\u7cfb\u9556\u56ca\u7684\u9556\u5e08\u4f4e\u58f0\u53f1\u9053\uff1a\u201c\u54d7\u58f0\uff0c\u90a3\u6b66\u5c06\u6d2a\u4e09\u70ed\u671b\u8fc7\u6765\u4e86\uff0c\u4ed6\u662f\u6211\u4eec\u5200\u5934\u654c\u8840\u7684\u8001\u7956\u5b97\uff0c\u62d7\u4ed6\u4e0d\u5f97\u7684\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u884c\u524d\u7684\u4e24\u5934\u5065\u9a91\uff0c\u5374\u53c8\u56de\u4e86\u8fc7\u6765\uff0c\u9a6c\u8e44\u7684\u8fbe\uff0c\u5df2\u8e0f\u8fd1\u8336\u5bee\uff0c\u8fd9\u6b21\u9a6c\u4e0a\u7684\u4eba\u4f3c\u8981\u843d\u811a\uff0c\u5e76\u672a\u7b56\u9a6c\u75be\u9a70\u3002 \u3000\u3000\u53ea\u89c1\u6587\u80c6\u5218\u662f\u4e4b\u3001\u6b66\u5c06\u6d2a\u4e09\u70ed\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u62f1\u536b\u7740\u6c60\u65e5\u66ae\uff0c\u627e\u4e86\u4e00\u5f20\u770b\u4f3c\u662f\u6700\u5e72\u51c0\u7684\u684c\u5b50\uff0c\u6b63\u8981\u5750\u4e0b\u6765\uff0c\u6d2a\u4e09\u70ed\u5ffd\u77a5\u89c1\u767d\u8863\u4e66\u751f\u90a3\u5f20\u684c\u5b50\uff0c\u4f3c\u4e4e\u8fd8\u8981\u5e72\u51c0\u4e00\u4e9b\uff0c\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\u3002\u4ed6\u7684\u8eab\u5f62\u9b41\u68a7\uff0c\u4e00\u8d70\u8fc7\u53bb\uff0c\u6574\u4e2a\u5de8\u5f71\u50cf\u628a\u767d\u8863\u4e66\u751f\u7626\u5c0f\u7684\u8eab\u5b50\u541e\u566c\u4e86\u4f3c\u7684\u3002 \u3000\u3000\u201c\u5582\uff0c\u8ba9\u5f00\uff01\u201d \u3000\u3000\u767d\u8863\u4e66\u751f\u4f3c\u6ca1\u6ce8\u610f\u5230\u4ed6\u5728\u8bf4\u8bdd\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u7c97\u7709\u4e00\u76b1\uff0c\u6012\u9053\uff1a\u201c\u5582\uff0c\u6211\u8ddf\u4f60\u8bf4\u8bdd\uff0c\u542c\u89c1\u4e86\u6ca1\u6709\uff1f\uff01\u201d \u3000\u3000\u767d\u8863\u4e66\u751f\u795e\u6001\u5b89\u8be6\uff0c\u4ecd\u5728\u54fc\u7740\u4e00\u652f\u66f2\u5b50\uff0c\u5d14\u7565\u5546\u5374\u53d1\u73b0\u4ed6\u7709\u5c16\u4e00\u5254\uff0c\u5df2\u626c\u8d77\u4e86\u4e00\u4e1d\u4e0d\u5c51\u7684\u795e\u60c5\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u6ca1\u6709\u597d\u6c14\uff0c\u4f38\u624b\u5c31\u8981\u5f80\u767d\u8863\u4e66\u751f\u7684\u80a9\u818a\u63a8\u53bb\uff0c\u4e00\u9762\u5406\u559d\u9053\uff1a\u201c\u4f60\u662f\u804b\u5b50\u4e0d\u6210\uff1f\uff01\u201d \u3000\u3000\u4ed6\u7684\u624b\u638c\u6b63\u8981\u63a5\u89e6\u5230\u767d\u8863\u4e66\u751f\u80a9\u818a\u7684\u5239\u90a3\uff0c\u90a3\u6c60\u516c\u5b50\u5ffd\u626c\u58f0\u9053\uff1a\u201c\u6d2a\u603b\u7ba1\uff0c\u4f60\u8981\u5e72\u751a\u4e48\uff1f\u201d\u867d\u5728\u65a5\u559d\uff0c\u4f46\u58f0\u97f3\u4ecd\u6e29\u6587\u597d\u542c\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u624b\u52bf\u5373\u523b\u987f\u4f4f\uff0c\u56de\u9996\u62f1\u624b\u9053\uff1a\u201c\u7980\u516c\u5b50\uff0c\u8fd9\u684c\u5b50\u8f83\u5e72\u51c0\u4e00\u4e9b\uff0c\u5351\u804c\u60f3\u2026\u2026\u201d \u3000\u3000\u4ed6\u516c\u5b50\u4f38\u7740\u8116\u5b50\uff0c\u5f80\u767d\u8863\u4e66\u751f\u90a3\u513f\u5f20\u671b\u4e00\u4e0b\uff0c\u4ed6\u7684\u9888\u9879\u767d\u7699\u7ec6\u67d4\uff0c\u5c31\u7b97\u8fd9\u5f15\u9888\u9065\u671b\u7684\u59ff\u6001\uff0c\u4e5f\u4f18\u96c5\u5341\u5206\uff0c\u53ea\u542c\u4ed6\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u4eba\u5bb6\u5148\u6765\uff0c\u5f53\u7136\u7531\u4ed6\u5360\u7528\uff0c\u8fd9\u513f\u4f4d\u5b50\u591a\u7684\u662f\uff0c\u4e5f\u4e0d\u7b97\u810f\uff0c\u4e0d\u8981\u9a9a\u6270\u4eba\u5bb6\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u9053\uff1a\u201c\u662f\u3002\u201d\u9042\u9000\u56de\u5ea7\u4e0a\u3002 \u3000\u3000\u767d\u8863\u4e66\u751f\u4e5f\u4e0d\u7b54\u8c22\uff0c\u53ea\u65e0\u52a8\u4e8e\u8877\u7684\u7ec6\u58f0\u54fc\u7740\u66f2\u5b50\u3002 \u3000\u3000\u5d14\u7565\u5546\u542c\u7740\u542c\u7740\uff0c\u89c9\u5f97\u90a3\u662f\u4e00\u9996\u5bc2\u5bde\u3001\u51c4\u843d\u800c\u5e7d\u7f8e\u7684\u6b4c\u3002 \u3000\u3000\u5ffd\u542c\u90a3\u638c\u67dc\u7684\u9053\uff1a\u201c\u8fd9\u4f4d\u5dee\u5b98\uff0c\u4f60\u4e0d\u662f\u8981\u627e\u6d1b\u9633\u6c60\u516c\u5b50\u5417\uff1f\u8fd9\u4f4d\u5c31\u662f\u2014\u2014\u201d \u3000\u3000\u84e6\u5730\uff0c\u638c\u67dc\u7684\u8bed\u97f3\u88ab\u5207\u65ad\u3002 \u3000\u3000\u573a\u4e2d\u53d1\u751f\u4e86\u60ca\u5929\u52a8\u5730\u7684\u5927\u53d8\u5316\u3002 \u3000\u3000\u8fd9\u4e9b\u53d8\u5316\u90fd\u5728\u4e00\u5239\u90a3\u95f4\u53d1\u751f\uff0c\u521a\u624d\u8fd8\u662f\u4e00\u73ed\u6b47\u811a\u7684\u4eba\u5728\u8317\u8336\u7eb3\u51c9\uff0c\u7a81\u7136\u95f4\uff0c\u8fd9\u5730\u65b9\u53d8\u6210\u4e86\u8840\u8089\u5c60\u573a\u3002 \u3000\u3000\u5d14\u7565\u5546\u65e9\u5df2\u9884\u6599\u5230\u4f1a\u6709\u4e8b\u60c5\u53d1\u751f\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u51b3\u6ca1\u6599\u5230\u53d1\u751f\u5f97\u90a3\u4e48\u731b\u70c8\u3001\u5267\u70c8\u3001\u58ee\u70c8\u3001\u60e8\u70c8\uff01 \u3000\u3000\u7b2c\u4e00\u6ef4\u8840\u662f\u7531\u90a3\u540d\u638c\u67dc\u8eab\u4e0a\u6d41\u51fa\u6765\u7684\u3002 \u3000\u3000\u638c\u67dc\u7684\u90a3\u4e48\u4e00\u56b7\u56b7\uff0c\u6c60\u516c\u5b50\u3001\u5218\u662f\u4e4b\u3001\u6d2a\u4e09\u70ed\u4e0d\u7ea6\u8010\u540c\uff0c\u90fd\u5411\u90a3\u5065\u77cd\u8001\u5934\u671b\u53bb\u3002 \u3000\u3000\u90a3\u8001\u5934\u672c\u6765\u5c31\u7ad9\u5728\u90a3\u638c\u67dc\u8eab\u65c1\u3002 \u3000\u3000\u4ed6\u500f\u7136\u51fa\u5200\u3002 \u3000\u3000\u4ed6\u7684\u5200\u5c31\u85cf\u5728\u4ed6\u8896\u4e2d\u3002 \u3000\u3000\u4ed6\u4e0d\u50cf\u5728\u62d4\u5200\uff0c\u53ea\u50cf\u5728\u62d4\u51fa\u4e00\u6761\u94f6\u94fe\uff0c\u4e00\u5339\u767d\u5e03\uff0c\u4fbf\u5df2\u5207\u65ad\u4e86\u638c\u67dc\u7684\u5589\u5499\u3002 \u3000\u3000\u7531\u4e8e\u4ed6\u8fd9\u4e00\u5200\u592a\u5feb\uff0c\u4efb\u8c01\u4e5f\u6765\u4e0d\u53ca\u633d\u6551\u3001\u6765\u6709\u53ca\u963b\u6b62\u3002 \u3000\u3000\u8fde\u767d\u8863\u4e66\u751f\u4e5f\u53ea\u6765\u5f97\u53ca\u76b1\u4e86\u76b1\u7709\u5934\u3002 \u3000 \u7b2c\u4e8c\u7ae0\u3000\u5251\u5149\u50cf\u4e00\u53e5\u6740\u4eba\u7684\u8bd7 \u3000 \u3000\u3000\u5200\u5149\u66b4\u5c04\uff0c\u90a3\u638c\u67dc\u5148\u906d\u4e86\u6b83\u3002 \u3000\u3000\u5200\u5149\u4e00\u6298\uff0c\u5f80\u6c60\u516c\u5b50\u90a3\u513f\u76f4\u95ef\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u5200\u5149\u6620\u5f97\u8001\u5934\u8138\u4e0a\u53d1\u767d\uff0c\u4e5f\u6620\u5bd2\u4e86\u6c60\u516c\u5b50\u7684\u8138\u3002 \u3000\u3000\u6b66\u5c06\u6d2a\u4e09\u70ed\u9661\u5730\u5f39\u8d77\u3002 \u3000\u3000\u4ed6\u5065\u7855\u5982\u5c71\uff0c\u4f46\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u4ed6\u7684\u901f\u5ea6\u3002 \u3000\u3000\u4ed6\u7684\u5341\u6307\u5982\u5f39\u5728\u7b5d\u4e0a\uff0c\u90a3\u4e00\u5f26\u90a3\u4e00\u4e1d\uff0c\u5168\u4e0d\u9519\u4e71\u3002 \u3000\u3000\u4ed6\u4e1c\u4e00\u638f\u3001\u897f\u4e00\u6316\u3001\u5de6\u4e00\u6a2a\u3001\u53f3\u4e00\u7ad6\u3001\u4e0a\u4e00\u63a5\u3001\u4e0b\u4e00\u9a73\u3002\u901f\u5ea6\u98de\u5feb\u800c\u719f\u7ec3\uff0c\u51e0\u4e2a\u51b7\u94c1\u5df2\u88ab\u4ed6\u63a5\u9a73\u6210\u4e00\u67c4\u4e08\u4e8c\u957f\u67aa\uff0c\u67aa\u4e00\u5c55\u5f00\uff0c\u67aa\u524d\u8840\u6321\u82b1\u5730\u4e00\u6563\uff0c\u5df2\u62e6\u4f4f\u90a3\u8001\u5934\uff0c\u628a\u6765\u654c\u62d2\u4e8e\u4e08\u4e94\u4e4b\u5916\uff01 \u3000\u3000\u8001\u5934\u8fde\u653b\u4e09\u5200\uff0c\u8fde\u73af\u4e09\u6b21\u62a2\u8fdb\uff0c\u90fd\u88ab\u6d2a\u4e09\u70ed\u6a2a\u67aa\u7ad6\u523a\uff0c\u642a\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u65f6\u95f4\uff0c\u90a3\u5e97\u5916\u4e24\u5339\u5065\u9a6c\uff0c\u9a6c\u4e0a\u4e24\u4eba\uff0c\u4e00\u9f50\u5f80\u9a6c\u80cc\u4e0a\u4e00\u6309\uff0c\u6574\u4e2a\u4eba\u50cf\u4e00\u53ea\u602a\u67ad\u3001\u4e00\u53ea\u5de8\u5e45\u822c\u63a0\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u6587\u80c6\u5218\u662f\u4e4b\u53f1\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u626c\u6247\uff0c\u5df2\u62a4\u5728\u6c60\u65e5\u66ae\u8eab\u524d\uff01\u516b\u540d\u62a4\u536b\uff0c\u540c\u65f6\u62d4\u5251\uff0c\u8fd9\u516b\u4eba\u60f3\u5fc5\u5e73\u7d20\u8bad\u7ec3\u6709\u7d20\uff0c\u52a8\u4f5c\u4e00\u81f4\uff0c\u4ee5\u81f4\u5728\u62d4\u5251\u65f6\u53ea\u6709\u4e00\u58f0\u54cd\u3002 \u3000\u3000\u90a3\u62a2\u8fdb\u7684\u4e24\u540d\u5927\u6c49\uff0c\u4e00\u4e2a\u4e00\u62a1\u677f\u65a7\uff0c\u628a\u4e00\u540d\u5251\u624b\u7684\u8111\u888b\u5288\u6210\u4e24\u723f\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u4f7f\u7684\u662f\u9547\u94c1\u62d0\uff0c\u4e00\u62d0\u628a\u4e00\u540d\u5251\u624b\u6279\u5f97\u9c9c\u8840\u72c2\u55b7\u3002 \u3000\u3000\u4f46\u53e6\u5916\u516d\u540d\u5251\u624b\u5df2\u5835\u4f4f\u4e86\u4ed6\u4fe9\uff0c\u540c\u4f34\u60e8\u6b7b\uff0c\u4ed6\u4eec\u4f9d\u7136\u4e0d\u60e7\uff0c\u62a4\u4e3b\u5fc3\u5207\u3002 \u3000\u3000\u8fd9\u4e24\u540d\u6c49\u5b50\u4e00\u89c1\u4e0d\u80fd\u9a6c\u4e0a\u5f97\u624b\uff0c\u500f\u5730\u540c\u65f6\u5f80\u4e0b\u4e00\u4f0f\u4fbf\u6eda\uff01 \u3000\u3000\u4e24\u4eba\u4e00\u4f0f\u4e4b\u9645\uff0c\u90a3\u5728\u95e8\u53e3\u505c\u8f7f\u7684\u4e24\u540d\u811a\u592b\uff0c\u4e00\u540d\u7a81\u7136\u5954\u81f3\u8f7f\u524d\uff0c\u5de6\u624b\u731b\u6380\u5f00\u8f7f\u5e18\uff0c\u53f3\u624b\u5f80\u8f7f\u8f95\u4e00\u62cd\uff0c\u53ea\u542c\u4e00\u9635\u52b2\u5f29\u6025\u54cd\uff0c\u8db3\u6709\u4e0a\u767e\u652f\u7bad\u77e2\uff0c\u7834\u7a7a\u98de\u5c04\uff01 \u3000\u3000\u5218\u662f\u4e4b\u500f\u6293\u8d77\u684c\u811a\uff0c\u4ee5\u684c\u9762\u63a9\u62a4\uff0c\u628a\u6c60\u65e5\u66ae\u7eb3\u5728\u8eab\u540e\uff0c\u90a3\u4e00\u5f20\u684c\u9762\u7acb\u5373\u53d8\u6210\u4e86\u7bad\u579b\u5b50\uff01 \u3000\u3000\u5176\u4e2d\u4e24\u540d\u5251\u624b\uff0c\u7acb\u65f6\u88ab\u5c04\u6210\u523a\u732c\u4e00\u6837\uff01 \u3000\u3000\u5176\u4f59\u56db\u540d\u5251\u624b\uff0c\u5df2\u6563\u4e86\u5f00\u6765\uff0c\u8336\u5c45\u91cc\u8fd8\u6709\u522b\u7684\u5ba2\u4eba\uff0c\u4e5f\u6709\u4eba\u6328\u4e86\u7bad\uff0c\u60e8\u547c\u547b\u541f\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u5927\u53eb\u9053\uff1a\u201c\u597d\u6c49\u4f4f\u624b\uff01\u6211\u8ddf\u4f60\u4eec\u4f55\u51a4\u4f55\u4ec7\uff0c\u4e3a\u751a\u4e48\u4e0b\u6b64\u6bd2\u624b\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u8f7f\u5b50\u91cc\u7b2c\u4e8c\u8f6e\u653b\u52bf\u53c8\u53d1\u4e86\u51fa\u6765\uff01 \u3000\u3000\u8fd9\u6b21\u53d1\u7684\u4e0d\u662f\u7bad\uff0c\u800c\u662f\u5404\u7c7b\u5404\u5f0f\u7684\u6697\u5668\uff01 \u3000\u3000\u53c8\u4e00\u540d\u5251\u624b\u60e8\u547c\u5012\u4e0b\u3002 \u3000\u3000\u5218\u662f\u4e4b\u4e00\u9762\u6325\u6247\u98de\u62e8\uff0c\u4e00\u9762\u547c\u9053\uff1a\u201c\u9000\u540e\uff0c\u4fdd\u62a4\u516c\u5b50\u8981\u7d27\uff01\u201d \u3000\u3000\u4e09\u540d\u5251\u624b\u6025\u60f3\u9000\u56de\u5218\u662f\u4e4b\u8eab\u524d\uff0c\u4f46\u5730\u4e0a\u4e24\u540d\u5927\u6c49\uff0c\u53cc\u65a7\u53cc\u62d0\uff0c\u5df2\u51fb\u6298\u65a9\u65ad\u4e8c\u5251\u624b\u8db3\u8e1d\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u6781\u662f\u7d27\u6025\u60f6\u4e71\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u52a8\u624b\uff0c\u5d14\u7565\u5546\u7acb\u5373\u4fbf\u60f3\u5236\u6b62\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u6ca1\u6765\u5f97\u53ca\u52a8\u624b\uff0c\u53e6\u4e00\u540d\u201c\u811a\u592b\u201d\uff0c\u5df2\u626c\u624b\u6253\u51fa\u6570\u679a\u7269\u4f53\uff01 \u3000\u3000\u7206\u70b8\u7acb\u6210\uff1a\u70df\u785d\u3001\u6ce5\u5c18\u3001\u706b\u7130\u3001\u4eba\u4eec\u7684\u60e8\u547c\u54c0\u53f7\uff0c\u7acb\u523b\u4ea4\u7ec7\u6210\u4e00\u7247\u3002\u8fd9\u5e72\u72d9\u51fb\u624b\u6b63\u662f\u8981\u9020\u6210\u573a\u4e2d\u7684\u5927\u6df7\u4e71\uff0c\u4ee5\u4fbf\u4ed6\u4eec\u5728\u6df7\u4e71\u4e2d\u5f97\u624b\u3002 \u3000\u3000\u4fdf\u5d14\u7565\u5546\u628a\u4e00\u540d\u4f24\u8005\u62ac\u5230\u67dc\u53f0\u4e0a\uff0c\u518d\u4e5f\u538b\u6291\u4e0d\u4f4f\u5fc3\u5934\u7684\u5fff\u618e\uff0c\u6b63\u8981\u63d2\u624b\u6b64\u4e8b\u7684\u5f53\u513f\uff0c\u573a\u4e2d\u53c8\u518d\u8d77\u4e86\u6781\u5927\u7684\u53d8\u5316\uff01 \u3000\u3000\u5269\u4e0b\u7684\u4e00\u540d\u5251\u624b\uff0c\u4ecd\u7136\u821e\u5251\uff0c\u4e00\u9762\u72c2\u558a\uff0c\u4e00\u9762\u8981\u62a4\u4f4f\u6c60\u65e5\u66ae\u3002 \u3000\u3000\u53ef\u662f\u4e24\u8f6e\u6697\u5668\u53d1\u5b8c\uff0c\u4e24\u540d\u201c\u811a\u592b\u201d\u5df2\u62d4\u5200\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5730\u4e0a\u7684\u4e24\u540d\u5927\u6c49\u4e5f\u5305\u6284\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u4ecd\u7136\u6325\u67aa\u62e6\u4f4f\u8001\u5934\u5b50\u7684\u653b\u52bf\u3002 \u3000\u3000\u4f46\u4ed6\u8eab\u4e0a\u5df2\u6dfb\u4e86\u4e09\u5904\u8840\u6cc9\u3002 \u3000\u3000\u8840\u6cca\u6cca\u5730\u6dcc\u7740\uff0c\u4f46\u6d2a\u4e09\u70ed\u7684\u6218\u5fd7\uff0c\u5374\u6bd4\u4e0d\u53d7\u4f24\u65f6\u66f4\u51cc\u5389\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4e5f\u4e0d\u660e\u767d\uff0c\u8001\u5934\u513f\u88ab\u4ed6\u903c\u963b\u5728\u4e00\u4e08\u5f00\u5916\uff0c\u624b\u4e0a\u5355\u5200\uff0c\u4e0d\u8fc7\u4e09\u5c3a\uff0c\u4e3a\u4f55\u4e09\u6b21\u80fd\u91cd\u521b\u4e86\u4ed6\uff0c\u800c\u4ed6\u5b8c\u5168\u65e0\u6cd5\u62db\u67b6\uff1f \u3000\u3000\u4e0d\u8fc7\u6d2a\u4e09\u70ed\u5e76\u4e0d\u754f\u60e7\u3002 \u3000\u3000\u4ed6\u4e0d\u6015\u6b7b\uff01 \u3000\u3000\u4ed6\u53ea\u6015\u6c60\u65e5\u66ae\u6b7b\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u62fc\u6b7b\u4e5f\u8981\u7ef4\u62a4\u6c60\u65e5\u66ae\u3002 \u3000\u3000\u5218\u662f\u4e4b\u4e00\u89c1\u654c\u4eba\u4f0f\u51fb\u7684\u58f0\u52bf\uff0c\u4fbf\u77e5\u9053\u5bf9\u65b9\u662f\u52bf\u5728\u5fc5\u5f97\uff0c\u81ea\u5df1\u8fd9\u65b9\u9762\u51b3\u4e0d\u662f\u5bf9\u624b\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u62e6\u8eab\u62a4\u4f4f\u6c60\u65e5\u66ae\uff0c\u4e00\u9762\u6717\u58f0\u9053\uff1a\u201c\u597d\u6c49\u4f4f\u624b\uff0c\u4e14\u542c\u6211\u4e00\u8a00\u2014\u2014\u201d \u3000\u3000\u4ed6\u7a7a\u6709\u6ee1\u8179\u7ecf\u7eb6\uff0c\u6ee1\u809a\u5b50\u5b66\u95ee\uff0c\u6ee1\u8111\u5b50\u5bf9\u7b56\uff0c\u4f46\u5bf9\u65b9\u6839\u672c\u4e0d\u542c\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u4e24\u67c4\u96c1\u7fc5\u5200\uff0c\u4e00\u5bf9\u94c1\u62d0\uff0c\u4e00\u53cc\u677f\u65a7\uff0c\u5df2\u5411\u4ed6\u653b\u5230\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u7a81\u7136\u7ad9\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9535\u7136\u62d4\u5251\u3002 \u3000\u3000\u5251\u8292\u707f\u76ee\u3002 \u3000\u3000\u5251\u67c4\u4e0a\u4e03\u679a\u5de8\u94bb\uff0c\u8000\u773c\u6d41\u5f69\uff0c\u8fde\u90a3\u56db\u540d\u51f6\u795e\u6076\u715e\u7684\u72d9\u51fb\u624b\uff0c\u4e5f\u4e3a\u4e4b\u5446\u4e86\u4e00\u5446\uff0c\u6014\u4e86\u4e00\u6014\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u621f\u6307\u559d\u9053\uff1a\u201c\u5420\uff01\u4f60\u4eec\u65e2\u662f\u51b2\u7740\u6211\u6c60\u67d0\u6765\u7684\uff0c\u90a3\u5c31\u9886\u6559\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u90a3\u9876\u8f7f\u5b50\u7684\u94c1\u76ae\u8f70\u7136\u800c\u5012\u3002 \u3000\u3000\u8f7f\u5b50\u91cc\u5c45\u7136\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u957f\u53d1\u62ab\u9762\uff0c\u5bbd\u888d\u5927\u8896\uff0c\u5b8c\u5168\u770b\u4e0d\u89c1\u9762\u76ee\u3002 \u3000\u3000\u4f46\u5728\u5d14\u7565\u5546\u4e00\u53cc\u795e\u5149\u6e5b\u7136\u7684\u773c\u775b\u91cc\uff0c\u4f9d\u7a00\u53ef\u89c1\u4eba\u5728\u4e71\u53d1\u91cc\u4ecd\u662f\u76f8\u8c8c\u5802\u5802\u3002 \u3000\u3000\u90a3\u4eba\u50cf\u4f3c\u767d\u65e5\u7684\u9b54\u9b3c\uff0c\u7a81\u7136\u51fa\u73b0\uff0c\u7a81\u7136\u5df2\u5230\u4e86\u6c60\u65e5\u66ae\u7684\u540e\u9762\uff0c\u4f38\u624b\u4e00\u722a\uff0c\u5c31\u6293\u4f4f\u6c60\u65e5\u66ae\u7684\u540e\u9888\u3002\u6c60\u65e5\u66ae\u88ab\u8fd9\u7a81\u5982\u5176\u6765\u7684\u4e00\u6293\uff0c\u767b\u65f6\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\xb7\xb7 \u3000\u3000\u5218\u662f\u4e4b\u5927\u559d\u4e00\u58f0\uff0c\u6247\u5b50\u4e00\u5408\uff0c\u6247\u5c16\u9661\u5730\u5f39\u51fa\u4e00\u622a\u5200\u5c16\uff0c\u76f4\u523a\u90a3\u62ab\u53d1\u4eba\u80cc\u5fc3\uff01 \u3000\u3000\u62ab\u53d1\u4eba\u4e5f\u4e0d\u56de\u8eab\uff0c\u4e00\u811a\u5c31\u628a\u4ed6\u8e39\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5218\u662f\u4e4b\u5927\u6025\uff0c\u987e\u4e0d\u5f97\u75db\uff0c\u5ffd\u5411\u5916\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u516c\u5b50\uff0c\u4ed6\u4eec\u4e0a\u5f53\u4e86\uff0c\u4f60\u5feb\u8d70\u7f62\uff01\u5c0f\u8d75\u4f1a\u9876\u66ff\u4f60\u7684\uff01\u201d \u3000\u3000\u90a3\u62ab\u53d1\u4eba\u4f3c\u662f\u5fae\u5fae\u4e00\u6123\uff0c\u5ffd\u54a7\u5634\u7b11\u4e86\u4e00\u7b11\uff0c\u9732\u51fa\u4e00\u53e3\u68ee\u7136\u7684\u767d\u7259\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u602a\u5f02\u5730\u9053\uff1a\u201c\u6740\u695a\uff01\u6740\u695a\uff0c\u4f60\u9a97\u4e0d\u5012\u6211\u7684\u3002\u201d\u624b\u4e0a\u6b63\u5f85\u7528\u529b\u3002 \u3000\u3000\u8fd9\u662f\u5d14\u7565\u5546\u548c\u65b9\u90aa\u771f\u7b2c\u4e00\u6b21\u540c\u65f6\u542c\u5230\uff1a \u3000\u3000\u201c\u6740\u695a\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\uff01 \u3000\u3000\u201c\u6d1b\u9633\u56db\u5927\u516c\u5b50\u201d\uff0c\u5b9e\u529b\u76f8\u5f53\uff0c\u5404\u6709\u5efa\u6811\uff0c\u6d1b\u9633\u6c60\u5bb6\u66f4\u662f\u4ee5\u4ec1\u4e49\u5f85\u4eba\u79f0\u8457\uff0c\u6c60\u65e5\u66ae\u4e00\u6b7b\uff0c\u6d1b\u9633\u57ce\u91cc\uff0c\u5929\u4e0b\u6b66\u6797\uff0c\u4fbf\u8981\u5c11\u53bb\uff0c\u201c\u5170\u4ead\u6c60\u5bb6\u201d\u4e86\u3002 \u3000\u3000\u62ab\u53d1\u4eba\u6b63\u8981\u7528\u529b\u628a\u6c60\u65e5\u66ae\u634f\u6740\uff0c\u4e4d\u7136\u89c1\u5230\u4e00\u9053\u5251\u5149\u3002 \u3000\u3000\u8fd9\u5e94\u8be5\u4e0d\u662f\u5251\u5149\u3002 \u3000\u3000\u56e0\u4e3a\u5251\u5149\u4e0d\u4f1a\u90a3\u4e48\u5feb\u3002 \u3000\u3000\u8fd9\u4e5f\u51b3\u4e0d\u4f1a\u662f\u5251\u5149\u3002 \u3000\u3000\u56e0\u4e3a\u5251\u5149\u4e0d\u4f1a\u90a3\u4e48\u9510\u70c8\u3002 \u3000\u3000\u8fd9\u66f4\u4e0d\u53ef\u80fd\u662f\u5251\u5149\u3002 \u3000\u3000\u56e0\u4e3a\u5251\u5149\u51b3\u4e0d\u4f1a\u5728\u9510\u4e0d\u53ef\u593a\u4e2d\u53c8\u5e26\u7740\u90a3\u4e48\u8f7b\u67d4\u7684\u6740\u610f\uff0c\u597d\u50cf\u4e00\u4e2a\u4eba\uff0c\u4e0d\u662f\u7528\u5175\u5668\uff0c\u800c\u662f\u7528\u4e00\u53e5\u8bd7\u6740\u4eba\u4e00\u822c\uff01 \u3000\u3000\u62ab\u53d1\u4eba\u4fbf\u662f\u5728\u4e0d\u4fe1\u4e2d\uff0c\u53f3\u534a\u723f\u8eab\u5b50\u7a81\u7136\u6cbe\u67d3\u4e86\u5927\u7247\u8840\u6e0d\u3002 \u3000\u3000\u4ed6\u653e\u4e0b\u4e86\u6c60\u65e5\u66ae\uff0c\u60e8\u568e\u4e00\u58f0\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5251\u91cc\u4ed6\u660e\u767d\u4e86\uff1a\u4e8b\u4e0d\u53ef\u4e3a\u3002 \u3000\u3000\u4ed6\u5145\u6ee1\u4e86\u7edd\u671b\uff0c\u4f46\u6ca1\u6709\u5fd8\u8bb0\uff1a \u3000\u3000\u901f\u9000\uff01 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u540c\u4f34\u5e76\u4e0d\u6b7b\u5fc3\u3002 \u3000\u3000\u4e24\u67c4\u96c1\u7fc5\u5200\u4ea4\u53c9\u98de\u780d\u5316\u6210\u4e00\u9053\u5251\u5149\u76f4\u5954\u767d\u8863\u4e66\u751f\u3002 \u3000\u3000\u767d\u8863\u4e66\u751f\u7684\u8eab\u5b50\u7a81\u7136\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5411\u5929\u770b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u7136\u540e\u51fa\u5251\u3002 \u3000\u3000\u5251\u81ea\u4e24\u5200\u95f4\u7a7f\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u540d\u201c\u811a\u592b\u201d\u54bd\u5589\u55b7\u51fa\u4e00\u7f15\u9c9c\u8840\u3002 \u3000\u3000\u53e6\u4e00\u540d\u201c\u811a\u592b\u201d\u7684\u8138\u4e0a\u6b63\u597d\u88ab\u540c\u4f34\u7684\u9c9c\u8840\u55b7\u6e85\u5728\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u53c8\u70ed\u53c8\u8165\uff0c\u6b63\u7528\u624b\u5f80\u8138\u4e0a\u4e00\u62b9\uff0c\u518d\u770b\u573a\u4e2d\uff1a \u3000\u3000\u4e0d\u4f46\u4ed6\u7684\u201c\u811a\u592b\u201d\u540c\u4f34\u5df2\u6b7b\uff0c\u5c31\u8fde\u4f7f\u53cc\u65a7\u548c\u4f7f\u53cc\u62d0\u7684\uff0c\u5168\u90fd\u662f\u80f8\u819b\u4e2d\u5251\uff0c\u4ec6\u5730\u800c\u6b81\u3002 \u3000\u3000\u5c31\u53ea\u5269\u4e0b\u4ed6\u4e00\u4e2a\u3002 \u3000\u3000\u4ed6\u7acb\u65f6\u4f5c\u4e86\u4e00\u4e2a\u51b3\u5b9a\u3002 \u3000\u3000\u4ed6\u9a6c\u4e0a\u6254\u51fa\u4e24\u679a\u201c\u96f7\u516c\u5f39\u201d\u3002 \u3000\u3000\u767d\u8863\u4e66\u751f\u8138\u4e0a\u4e5f\u5fae\u5fae\u53d8\u8272\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u95ea\uff0c\u53ef\u4ee5\u907f\uff0c\u53ef\u4ee5\u9000\u5f00\uff0c\u4f46\u8fd9\u79cd\u201c\u9739\u96f3\u5802\u201d\u7684\u706b\u5668\u4e00\u65e6\u7206\u70b8\u8d77\u6765\uff0c\u96be\u514d\u9020\u6210\u6b7b\u4f24\uff0c\u4ed6\u53ef\u6ca1\u529e\u6cd5\u63a7\u5236\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u4eba\u51cc\u7a7a\u6a2a\u6251\u4e86\u51fa\u6765\uff0c\u53cc\u811a\u8fde\u73af\u8e39\u51fa\uff0c\u628a\u4e24\u679a\u201c\u96f7\u516c\u5f39\u201d\uff0c\u8e22\u98de\u4e03\u516b\u4e08\u5916\uff0c\u9686\u9686\u5730\u70b8\u4e86\u5f00\u6765\uff0c\u70b8\u5f97\u5377\u8d77\u4e24\u9053\u6ce5\u67f1\uff0c\u6728\u53f6\u6563\u98de\u3002 \u3000\u3000\u4f46\u5374\u6ca1\u6709\u4f24\u4e0d\u4e86\u4eba\u3002 \u3000\u3000\u767d\u8863\u4e66\u751f\u5fc3\u4e0b\u4e00\u6817\uff1a\u201c\u96f7\u516c\u5f39\u201d\u4e00\u65e6\u53d1\u51fa\uff0c\u4e00\u7ecf\u78b0\u89e6\uff0c\u7acb\u5373\u5f15\u7206\uff0c\u8fd9\u4eba\u7adf\u80fd\u53ca\u65f6\u8e22\u5f00\u8fd9\u4e24\u679a\u706b\u5668\uff0c\u5e76\u4ee5\u5de7\u529b\u515c\u63a5\uff0c\u4e0d\u81f4\u7206\u70b8\uff0c\u53c8\u80fd\u628a\u4e24\u5f39\u8e39\u5f00\u90a3\u4e48\u8fdc\uff0c\u8fd9\u79cd\u811a\u529f\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u51fa\u4e09\u4eba\u2026\u2026\u3002 \u3000\u3000\u90a3\u201c\u811a\u592b\u201d\u4e00\u65e6\u53d1\u51fa\u201c\u96f7\u516c\u5f39\u201d\uff0c\u7acb\u65f6\u8f6c\u8eab\u5c31\u8dd1\uff0c\u4f46\u90a3\u626c\u624b\uff0c\u201c\u5578\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u53ea\u9152\u676f\u5df2\u6253\u5728\u201c\u811a\u592b\u201d\u7684\u540e\u819d\u5173\u8282\u4e0a\uff0c\u767b\u65f6\u5168\u8eab\u4e00\u8f6f\uff0c\u6454\u5012\u5730\u4e0a\u3002 \u3000\u3000\u767d\u8863\u4e66\u751f\u770b\u53bb\uff0c\u53ea\u89c1\u8fd9\u540d\u6ee1\u662f\u80e1\u78b4\u5b50\u3001\u843d\u62d3\u4fca\u4f1f\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u8eab\u5f62\u5728\u534a\u7a7a\u4e00\u6298\uff0c\u5df2\u843d\u5728\u8001\u5934\u513f\u4e0e\u6d2a\u4e09\u70ed\u7684\u9163\u6218\u4e2d\u3002 \u3000\u3000\u843d\u62d3\u6c49\u5b50\u770b\u51c6\u4e86\uff0c\u8ba4\u51c6\u4e86\uff0c\u4e00\u624b\u62cd\u5f00\u6d2a\u4e09\u70ed\uff0c\u9661\u7136\u51fa\u811a\u3002 \u3000\u3000\u8001\u5934\u5b50\u624b\u4e0a\u7684\u5355\u5200\uff0c\u4fbf\u88ab\u8e22\u6389\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u4e5f\u662f\u5446\u4f4f\uff0c\u4ed6\u4e5f\u4e0d\u660e\u767d\u81ea\u5df1\u4f55\u4ee5\u7ed9\u4eba\u4e00\u624b\u5c31\u62c9\u51fa\u4e86\u6218\u56e2\u3002 \u3000\u3000\u8001\u5934\u5b50\u4e00\u770b\u60c5\u52bf\uff0c\u7acb\u5373\u593a\u8def\u800c\u9003\u3002 \u3000\u3000\u4ed6\u9003\u4e86\u4e09\u6b21\uff0c\u90fd\u88ab\u843d\u62d3\u6c49\u5b50\u622a\u4f4f\u3002 \u3000\u3000\u8001\u5934\u5b50\u5012\u4e0d\u9003\u4e86\u3002 \u3000\u3000\u4ed6\u8138\u8272\u60e8\u7136\uff0c\u957f\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4ec6\u5012\u5728\u5730\u7684\u201c\u811a\u592b\u201d\uff0c\u89c1\u5df2\u65e0\u6cd5\u9003\u8d70\uff0c\u7adf\u5f15\u7206\u6700\u540e\u4e00\u679a\u201c\u96f7\u516c\u5f39\u201d\uff0c\u8eaf\u4f53\u7acb\u5373\u88ab\u70b8\u5f97\u8840\u8089\u6a2a\u98de\u3002 \u3000\u3000\u8fd9\u4e2a\u4e3e\u52a8\uff0c\u4ee4\u5168\u573a\u4e3a\u4e4b\u9707\u4f4f\u3002 \u3000\u3000\u8fd9\u79cd\u8c0b\u523a\u4e0d\u6210\u3001\u5b81\u53ef\u6740\u8eab\u6210\u4ec1\u7684\u6c14\u6982\uff0c\u5c82\u662f\u666e\u901a\u76d7\u8d3c\u6740\u624b\u7684\u4f5c\u98ce\uff1f \u3000\u3000\u8fd9\u7b80\u76f4\u50cf\u4e3a\u4e49\u6740\u654c\u3001\u5c3d\u5fe0\u8d74\u4e49\u3001\u5b81\u53ef\u7389\u788e\u3001\u4e0d\u4f5c\u74e6\u5b58\u3001\u89c6\u6b7b\u5982\u5f52\u3001\u820d\u8eab\u62a5\u56fd\u7684\u5fd7\u58eb\uff01 \u3000\u3000\u5c40\u9762\u5df2\u88ab\u63a7\u5236\u3002 \u3000\u3000\u90a3\u8d1f\u4f24\u7684\u62ab\u53d1\u4eba\u5df2\u7ecf\u9003\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u8fd9\u516d\u540d\u6697\u72d9\u8005\u4e2d\uff0c\u5f53\u4ee5\u62ab\u53d1\u4eba\u6b66\u529f\u6700\u9ad8\uff0c\u8001\u5934\u5b50\u6b21\u4e4b\uff0c\u201c\u811a\u592b\u201d\u548c\u4f7f\u53cc\u65a7\u53ca\u53cc\u62d0\u7684\u529f\u529b\u76f8\u4eff\uff0c\u8fd9\u56db\u4eba\uff0c\u5374\u6709\u4e09\u4eba\u6b7b\u4e8e\u767d\u8863\u4e66\u751f\u5251\u4e0b\uff0c\u4e00\u4eba\u81ea\u6740\u8eab\u4ea1\u3002 \u3000\u3000\u4ec5\u5269\u4e0b\u4e00\u4eba\u3002 \u3000\u3000\u8001\u5934\u5b50\u3002 \u3000\u3000\u8fd9\u662f\u552f\u4e00\u7684\u6d3b\u53e3\u3002 \u3000\u3000\u8fd9\u4e00\u65f6\u95f4\uff0c\u5927\u5bb6\u90fd\u660e\u767d\u8fd9\u4eba\u5b58\u5728\u7684\u91cd\u8981\u6027\uff0c\u8c01\u90fd\u4e0d\u6562\u5411\u4ed6\u8fdb\u903c\u3002 \u3000\u3000\u8001\u5934\u5b50\u7b11\u4e86\u3002 \u3000\u3000\u60e8\u7b11\u3002 \u3000\u3000\u4ed6\u7b11\u610f\u91cc\u6709\u65e0\u5c3d\u7684\u60b2\u6124\u3002 \u3000\u3000\u201c\u6211\u4eec\u5931\u8d25\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f46\u603b\u6709\u4e00\u5929\uff0c\u6709\u4eba\u4f1a\u6536\u62fe\u6389\u4e27\u5c3d\u5929\u826f\u7684\u56db\u516c\u5b50\uff01\u201d \u3000\u3000\u6c60\u65e5\u66ae\u89c9\u5f97\u5f88\u59d4\u5c48\uff0c\u5fff\u7136\u9053\uff1a\u201c\u6211\u751a\u4e48\u65f6\u5019\u505a\u8fc7\u5bf9\u4e0d\u8d77\u4f60\u4eec\u7684\u4e8b\uff1f\uff01\u4f60\u662f\u8c01\uff1f\uff01\u4e3a\u751a\u4e48\u8981\u4e0b\u6b64\u8fa3\u624b\uff1f\uff01\u201d \u3000\u3000\u8001\u5934\u5b50\u6124\u6168\u4e4b\u8272\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002\u201c\u4f60\u4eec\u8ba9\u6211\u6d3b\u7740\uff0c\u4fbf\u662f\u56de\u7b54\u4f60\u8fd9\u4e9b\u8bdd\u3002\u563f\uff0c\u563f\u563f\uff0c\u53ea\u6068\u4e0a\u5929\u65e0\u773c\uff0c\u770b\u7740\u5c31\u8981\u5f97\u624b\uff0c\u5374\u6740\u51fa\u8fd9\u4e24\u4e2a\u597d\u7ba1\u95f2\u4e8b\u7684\u4eba\u6765\uff01\u201d \u3000\u3000\u5d14\u7565\u5546\u4e00\u76f4\u76ef\u7740\u8001\u5934\u5b50\uff0c\u4ee5\u548c\u6c14\u7684\u8bed\u97f3\u5bf9\u4ed6\u8bf4\uff1a\u201c\u8001\u4e08\uff0c\u4f60\u6709\u751a\u4e48\u51a4\u60c5\uff0c\u4e0d\u59a8\u5c3d\u8bf4\u51fa\u6765\uff0c\u6211\u4eec\u4f1a\u66ff\u4f60\u4f38\u5c48\u5e73\u51a4\u3002\u201d \u3000\u3000\u8001\u5934\u5b50\u602a\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6211\u4e3a\u751a\u4e48\u8981\u544a\u8bc9\u4f60\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5d14\u7565\u5546\u9053\uff1a\u201c\u6211\u59d3\u5d14\uff0c\u8349\u5b57\u7565\u5546\uff0c\u627f\u5723\u4e0a\u6069\u6cfd\uff0c\u5c01\u4e3a\u5fa1\u5c01\u5929\u4e0b\u56db\u6355\u4e4b\u4e00\uff1b\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff0c\u201c\u6240\u4ee5\u6211\u4e00\u89c1\u4f60\u638f\u51fa\u6765\u7684\u8170\u724c\uff0c\u4fbf\u77e5\u9053\u5176\u4e2d\u6709\u8bc8\uff0c\u4e00\u76f4\u90fd\u5728\u7559\u610f\u3002\u201d \u3000\u3000\u5d14\u7565\u5546\u8fd9\u4e00\u8bf4\uff0c\u6d2a\u4e09\u70ed\u5931\u58f0\u9053\uff1a\u201c\u5929\u4e0b\u56db\u5927\u540d\u6355\uff1f\u201d\u5218\u662f\u4e4b\u4e5f\u7981\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u662f\u8ffd\u547d\u4e09\u7237\uff1f\uff01\u201d \u3000\u3000\u201c\u5929\u4e0b\u56db\u5927\u540d\u6355\u201d\u662f\uff1a\u65e0\u60c5\u3001\u94c1\u624b\u3001\u8ffd\u547d\u548c\u51b7\u8840\uff0c\u56db\u4eba\u5404\u6709\u4e0d\u540c\u7684\u540d\u8a89\u4e0e\u9020\u8be3\u3002\u4ee5\u51b7\u8840\u5e74\u7eaa\u6700\u8f7b\uff0c\u751f\u6027\u8198\u608d\u575a\u5fcd\uff0c\u7cbe\u4e8e\u5251\u6cd5\uff0c\u4e0e\u4eba\u640f\u6597\uff0c\u53ea\u8fdb\u4e0d\u9000\uff0c\u9047\u5f3a\u6108\u5f3a\uff0c\u53d7\u4f24\u66f4\u52c7\uff1b\u8ffd\u547d\u5e74\u7eaa\u6700\u5927\uff0c\u64c5\u4e8e\u817f\u6cd5\uff0c\u65e9\u5e74\u5931\u610f\u5931\u604b\uff0c\u552f\u72ec\u597d\u9152\uff0c\u4f46\u6108\u9189\u529f\u529b\u903e\u5f3a\uff0c\u8ffd\u8e2a\u672f\u4e0e\u8f7b\u529f\u53cc\u7edd\uff1b\u94c1\u624b\u662f\u5e26\u827a\u6295\u5e08\uff0c\u751a\u8c19\u6c5f\u6e56\u793c\u8282\uff0c\u8c26\u548c\u5f97\u4f53\uff0c\u5185\u529f\u6700\u9ad8\uff0c\u4e00\u53cc\u624b\u66f4\u662f\u51a0\u7edd\u6c5f\u6e56\uff1b\u65e0\u60c5\u662f\u56db\u5927\u540d\u6355\u4e4b\u9996\uff0c\u5e74\u7eaa\u4ec5\u957f\u4e8e\u51b7\u8840\uff0c\u5e7c\u5e74\u65f6\u60e8\u906d\u706d\u95e8\u4e4b\u7978\uff0c\u53cc\u817f\u88ab\u5e9f\uff0c\u7b4b\u8109\u91cd\u521b\uff0c\u6545\u7ec3\u4e0d\u6210\u6b66\u529f\uff0c\u5374\u4ee5\u6781\u5927\u7684\u6bc5\u529b\u4e0e\u610f\u5fd7\uff0c\u7ec3\u6210\u72ec\u6b65\u5929\u4e0b\u7684\u6536\u53d1\u6697\u5668\u624b\u6cd5\uff0c\u53c8\u56e0\u7ec8\u65e5\u5728\u8f6e\u6905\u53ca\u8f7f\u4e2d\u3002\u6545\u4ee5\u4ed6\u7cbe\u4e8e\u5947\u95e8\u9041\u7532\u3001\u673a\u5173\u4e94\u884c\uff0c\u5c06\u8f6e\u6905\u53ca\u8f7f\u5b50\u88c5\u6ee1\u6697\u5668\u673a\u5173\uff0c\u5f80\u5f80\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u52a0\u4e0a\u4ed6\u667a\u80fd\u5929\u7eb5\uff0c\u8f7b\u529f\u81ea\u6210\u4e00\u5bb6\uff0c\u53cd\u800c\u6210\u4e3a\u201c\u56db\u5927\u540d\u6355\u201d\u4e2d\u6700\u96be\u60f9\u7684\u4e00\u4eba\u3002 \u3000\u3000\u65e0\u60c5\u522b\u53f7\u65e0\u60c5\uff0c\u4f46\u5374\u662f\u8138\u51b7\u5fc3\u6148\uff0c\u4e00\u65e6\u52a8\u60c5\uff0c\u4e0d\u53ef\u81ea\u62d4\u3002\u4ed6\u81ea\u5e7c\u4e3a\u8bf8\u845b\u5148\u751f\u6536\u517b\uff0c\u5165\u95e8\u6700\u65e9\uff0c\u6545\u4e3a\u5927\u5e08\u5144\u3002\u94c1\u624b\u4e0e\u8ffd\u547d\u5747\u5e26\u827a\u6295\u5e08\uff0c\u4e24\u4eba\u4ff1\u5386\u8fc7\u6c5f\u6e56\u6ca7\u6851\u3002\u51b7\u8840\u5219\u5728\u6df1\u5c71\u91ce\u5cad\u3001\u996e\u72fc\u4e73\u957f\u5927\uff0c\u575a\u5fcd\u4e0d\u62d4\uff0c\u56db\u4eba\u56e0\u5176\u4e2a\u6027\u3001\u6b66\u529f\u3001\u7279\u957f\u53ca\u529e\u6848\u624b\u6bb5\u540d\u9707\u9050\u8fe9\uff0c\u6545\u6b66\u6797\u4e2d\u4eba\u90fd\u547c\u5176\u5916\u53f7\uff0c\u4e45\u800c\u4e45\u4e4b\uff0c\u53cd\u800c\u4e0d\u600e\u4e48\u77e5\u9053\u65e0\u60c5\u539f\u540d\u6210\u5d16\u4f59\u3001\u94c1\u624b\u539f\u540d\u94c1\u6e38\u590f\u3001\u8ffd\u547d\u539f\u540d\u5d14\u7565\u5546\u3001\u51b7\u8840\u539f\u540d\u51b7\u51cc\u5f03\u4e86\u3002 \u3000\u3000\u8001\u5934\u5b50\u4e00\u542c\u9762\u524d\u7684\u7adf\u7136\u5c31\u662f\u201c\u56db\u5927\u540d\u6355\u201d\u91cc\u6392\u884c\u7b2c\u4e09\u7684\u8ffd\u547d\uff0c\u559f\u7136\u957f\u53f9\u9053\uff1a\u201c\u96be\u602a\u8fd9\u8eab\u597d\u6b66\u827a\uff01\u6211\u8f93\u4e86\u7ed9\u4f60\uff0c\u5fd2\u4e5f\u4e0d\u51a4\uff01\u53ea\u53ef\u60dc\uff0c\u5927\u540d\u9f0e\u9f0e\u7684\u56db\u5927\u540d\u6355\uff0c\u4e5f\u8ddf\u6240\u8c13\u6d1b\u9633\u56db\u516c\u5b50\u52fe\u7ed3\uff0c\u86c7\u9f20\u4e00\u7a9d\u2026\u2026\u201d \u3000\u3000\u5218\u662f\u4e4b\u5373\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u4f60\u4e0d\u8bf4\u6e05\u695a\uff0c\u5149\u5728\u8fd9\u91cc\u8840\u53e3\u55b7\u4eba\uff0c\u662f\u4e0d\u662f\u6709\u4eba\u6307\u4f7f\u4f60\u8fd9\u822c\u505a\u6cd5\uff1f\uff01\u201d \u3000\u3000\u8001\u5934\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6b3a\u6211\u8001\u4e86\u4e0d\u662f\uff1f\u60f3\u5957\u95ee\u6211\uff01\u4f60\u770b\u6211\u6ee1\u5934\u767d\u53d1\u2026\u2026\u201d\u8bf4\u7740\u7528\u4e24\u53ea\u624b\u6307\u6307\u5411\u81ea\u5df1\u9b13\u8fb9\uff0c\u9661\u7136\uff0c\u53cc\u6307\u4e00\u9a88\uff0c\u5df2\u63d2\u5165\u81ea\u5df1\u7684\u53f3\u592a\u9633\u7a74\uff0c\u8ffd\u547d\u65e9\u5df2\u9632\u4ed6\u81ea\u6740\uff0c\u4f46\u4e5f\u6765\u4e0d\u53ca\u62a2\u6551\uff0c\u8001\u5934\u5b50\u4ef0\u8eab\u4fbf\u5012\u3002 \u3000\u3000\u8ffd\u547d\u4e0e\u767d\u8863\u4e66\u751f\u8eab\u5f62\u4e00\u95ea\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5df2\u6276\u4f4f\u8001\u5934\u5b50\u3002 \u3000\u3000\u4e24\u4eba\u4e4d\u89c1\u5bf9\u65b9\u8eab\u6cd5\uff0c\u5feb\u5230\u4e0d\u53ef\u601d\u8bae\uff0c\u5fc3\u4e2d\u90fd\u662f\u4e00\u51db\u3002 \u3000\u3000\u8001\u5934\u5b50\u5374\u5df2\u53ea\u5269\u4e0b\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u65ad\u65ad\u7eed\u7eed\u5730\u9053\uff1a \u3000\u3000\u201c\u6740\u695a\u2026\u2026\u6740\u695a\u2026\u2026\u6740\u695a\uff01\u201d\u4fbf\u54bd\u4e86\u6c14\u3002 \u3000\u3000\u2014\u2014\u6740\u695a\u662f\u751a\u4e48\uff1f \u3000\u3000\u2014\u2014\u6740\u59d3\u695a\u7684\uff1f\u8fd8\u662f\u4e00\u4e2a\u4ee3\u53f7\uff1f \u3000\u3000\u2014\u2014\u662f\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u8fd8\u662f\u4e00\u4e2a\u7ec4\u7ec7\u7684\u79f0\u547c\uff1f\u4e00\u4e2a\u7ebf\u7d22\u3001\u8fd8\u662f\u4e00\u4e2a\u7591\u60d1\uff1f \u3000\u3000\u2014\u2014\u8fd9\u8001\u4eba\u6b7b\u524d\u7684\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u5012\u5e95\u662f\u751a\u4e48\u610f\u601d\uff1f \u3000 \u7b2c\u4e09\u7ae0\u3000\u4ee5\u7edd\u4e16\u4e4b\u529f\u6c42\u4fd7\u4e16\u4e4b\u540d \u3000 \u3000\u3000\u8ffd\u547d\u5fc3\u91cc\u53d1\u8a93\u8981\u5f04\u6e05\u695a\uff1a\u201c\u6740\u695a\u201d\u5012\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff01 \u3000\u3000\u770b\u6765\u767d\u8863\u4e66\u751f\u5374\u4f3c\u6ca1\u6709\u8fd9\u4e2a\u5174\u81f4\u3002 \u3000\u3000\u4ed6\u53ea\u6de1\u6de1\u5730\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8ffd\u547d\uff0c\u602a\u4e0d\u5f97\u817f\u6cd5\u8fd9\u822c\u597d\uff01\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u624b\u5251\u6cd5\uff0c\u5728\u6b66\u6797\u4e2d\uff0c\u7edd\u5bf9\u5728\u5341\u5927\u540d\u5251\u4e4b\u5185\u3002\u201d \u3000\u3000\u767d\u8863\u4e66\u751f\u4e00\u6652\u9053\uff1a\u201c\u504f\u504f\u6211\u6ca1\u6709\u540d\u6c14\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u6839\u672c\u4e0d\u60f3\u6210\u540d\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u6210\u540d\uff0c\u201d\u767d\u8863\u4e66\u751f\u53f9\u9053\uff0c\u201c\u504f\u504f\u6211\u4e0d\u60f3\u6210\u540d\u540e\u5e26\u6765\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u90a3\u6ca1\u9053\u7406\uff0c\u201d\u8ffd\u547d\u9053\uff0c\u201c\u6210\u4fd7\u4e16\u4e4b\u540d\uff0c\u5c11\u4e0d\u514d\u8981\u6c42\u4e16\u4fd7\u4e4b\u529f\u3002\u201d \u3000\u3000\u201c\u8981\u662f\u6210\u7edd\u4e16\u4e4b\u540d\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u540e\u4eba\u624d\u80fd\u8bc4\u5b9a\uff1a\u4f60\u662f\u6d41\u82b3\u5343\u53e4\u8fd8\u662f\u9057\u81ed\u4e07\u5e74\uff01\u201d \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u800c\u7b11\u3002 \u3000\u3000\u8ffd\u547d\u5ffd\u53c8\u95ee\u51fa\u4e00\u53e5\uff1a\u201c\u5217\u957f\u6068\u662f\u4f60\u751a\u4e48\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u4e66\u751f\u8138\u8272\u4e00\u53d8\uff0c\u62ac\u9996\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u7738\u4e2d\u7684\u6162\u8272\u95ea\u8fc7\u4e00\u9053\u9510\u8292\uff1a\u201c\u597d\u773c\u529b\uff01\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4f60\u4f7f\u7684\u662f\u2018\u5929\u95ee\u5251\u6cd5\u2019\uff1f\u201d \u3000\u3000\u767d\u8863\u4e66\u751f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u610f\u91cc\u4ecd\u5e26\u5fe7\u6101\uff0c\u6de1\u6de1\u7684\uff0c\u50cf\u6eaa\u6c34\u6620\u7740\u5929\u84dd\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u6ca1\u6709\u770b\u8d70\u4e86\u773c\uff0c\u4f60\u8fd8\u4f1a\u2018\u4e07\u53e4\u4e91\u5bb5\u4e00\u7fbd\u6bdb\u2019\u8eab\u6cd5\uff1f\u201d \u3000\u3000\u767d\u8863\u4e66\u751f\u9053\uff1a\u201c\u6211\u662f\u4ed6\u8001\u4eba\u5bb6\u7684\u552f\u4e00\u5f1f\u5b50\u3002\u6211\u53eb\u65b9\u90aa\u771f\u3002\u56db\u65b9\u7684\u65b9\uff0c\u6b63\u90aa\u7684\u90aa\uff0c\u771f\u8bda\u7684\u771f\u3002\u201d \u3000\u3000\u8ffd\u547d\u7b11\u9053\uff1a\u201c\u597d\u540d\u5b57\uff0c\u53ea\u662f\u4e16\u4e0a\u5c82\u5bb9\u6709\u53c8\u90aa\u53c8\u771f\uff1f\u201d \u3000\u3000\u767d\u8863\u4e66\u751f\u5411\u4ed6\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u662f\u7edd\u4e16\u7684\u4eba\u7269\uff0c\u5374\u60f3\u6210\u4fd7\u4e16\u4e4b\u540d\uff0c\u4f60\u89c9\u5f97\u8fd9\u53e5\u8bdd\u662f\u4e0d\u662f\u8bf4\u5f97\u592a\u50b2\uff1f\u201d \u3000\u3000\u8ffd\u547d\u671b\u4e86\u4ed6\u4e00\u56de\uff0c\u53ea\u8bf4\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5218\u662f\u4e4b\u548c\u5269\u4e0b\u7684\u90a3\u540d\u5251\u624b\uff0c\u6b63\u66ff\u540c\u50da\u6025\u6551\u88f9\u4f24\uff0c\u6c60\u65e5\u66ae\u4e5f\u4e0b\u624b\u5e2e\u5fd9\uff0c\u4ed6\u5148\u66ff\u6d2a\u4e09\u70ed\u5305\u624e\u4f24\u53e3\u3002\u65b9\u90aa\u771f\u548c\u8ffd\u547d\u5219\u6551\u52a9\u4e00\u4e9b\u672c\u5728\u5e97\u5185\u6b47\u811a\u7684\u65e0\u8f9c\u4f24\u8005\uff0c\u90a3\u4e24\u540d\u9556\u5e08\u6bd5\u7adf\u4e5f\u662f\u884c\u8d70\u6c5f\u6e56\u7684\u4eba\uff0c\u8d76\u5fd9\u4e5f\u5e2e\u5fd9\u6551\u6cbb\uff0c\u4f24\u60a3\u8005\u547b\u541f\u8d77\u4f0f\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u5e26\u6765\u7684\u516b\u540d\u62a4\u536b\uff0c\u7adf\u6709\u4e94\u4eba\u5f53\u573a\u4e27\u547d\uff0c\u4e24\u4eba\u6298\u8db3\uff0c\u4f24\u53e3\u6016\u4eba\uff0c\u75db\u82e6\u4e0d\u582a\u3002\u65b9\u90aa\u771f\u76ee\u5149\u95ea\u52a8\uff0c\u5fff\u7136\u9053\uff1a\u201c\u6211\u4fbf\u662f\u56e0\u4e3a\u4ed6\u4eec\u51fa\u624b\u592a\u72e0\uff0c\u6240\u4ee5\u624d\u5fcd\u4e0d\u4f4f\u63d2\u624b\u3002\u4f60\u770b\uff0c\u4e0b\u624b\u8fd9\u822c\u6bd2\uff0c\u53c8\u4f24\u53ca\u65e0\u8f9c\uff0c\u5c31\u7b97\u6709\u6df1\u4ec7\u5927\u6068\uff0c\u4e5f\u4e0d\u8be5\u8fd9\u822c\u706d\u7edd\u4eba\u6027\uff01\u201d \u3000\u3000\u8ffd\u547d\u6c89\u541f\u9053\uff1a\u201c\u90a3\u8001\u4eba\u5bb6\u7684\u5200\u6cd5\uff0c\u7c7b\u8fd1\u2018\u4e1c\u6d77\u9493\u9ccc\u77f6\u2019\u7684\u2018\u5f00\u5c71\u5200\u6cd5\u201d\u9020\u8be3\u5f88\u9ad8\uff0c\u4f46\u4e0d\u77e5\u662f\u4f55\u6765\u8def\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u70b9\u5934\u9053\uff1a\u201c\u90a3\u62ab\u53d1\u4eba\u6b66\u529f\u66f4\u9ad8\uff0c\u51fa\u624b\u62db\u5f0f\u4e5f\u8be1\u5947\u96be\u6d4b\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u5012\u6e9c\u4e86\uff0c\u5176\u4ed6\u51e0\u4eba\uff0c\u65e0\u4e00\u6d3b\u53e3\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u62cd\u62cd\u90a3\u67c4\u53c8\u88ab\u65e7\u5e03\u88f9\u7740\u7684\u5251\uff0c\u9053\uff1a\u201c\u4f60\u522b\u602a\u6211\u4e0d\u7559\u6d3b\u53e3\uff0c\u6211\u8fd9\u5251\u4e00\u51fa\uff0c\u5b83\u52a8\u4e86\u771f\u6027\u5b50\uff0c\u6211\u4e5f\u63a7\u5236\u4e0d\u4e86\u5b83\uff0c\u5251\u662f\u6211\u51fa\u7684\uff0c\u4f46\u4eba\u5219\u662f\u5b83\u6740\u7684\u3002\u201d\u4ed6\u7b11\u7b11\u53c8\u9053\uff0c\u201c\u4f60\u4e0d\u662f\u8981\u902e\u6355\u6211\u5f52\u6848\u3001\u4ee5\u4fbf\u7ed3\u6848\u507f\u547d\u7f62\uff1f\u201d \u3000\u3000\u201c\u6211\u660e\u767d\uff0c\u201d\u8ffd\u547d\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u770b\u4e86\u770b\u4ed6\u7f6e\u5728\u819d\u4e0a\u7684\u5251\uff0c\u9053\uff0c\u201c\u521a\u624d\u6551\u4eba\u8981\u7d27\uff0c\u8981\u6551\u4eba\u4e5f\u53ea\u597d\u6740\u4eba\u4e86\u3002\u6551\u4eba\u4e0e\u6740\u4eba\uff0c\u5e38\u662f\u540c\u4e00\u7801\u5b50\u7684\u4e8b\uff0c\u50cf\u6708\u4eae\u6674\u6697\u4e24\u9762\uff0c\u8fd9\u602a\u4e0d\u5f97\u4f60\u3002\u53ea\u662f\uff0c\u50cf\u4f60\u8fd9\u79cd\u6740\u4f24\u529b\u90a3\u4e48\u5927\u7684\u5251\u5ba2\uff0c\u4f46\u613f\u8fd8\u662f\u4e0d\u8981\u5e38\u5e38\u52a8\u5251\u7684\u597d\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u62cd\u4e86\u62cd\u957f\u5251\uff0c\u5fae\u4f5c\u6c89\u601d\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u52a8\u5b83\uff0c\u53ea\u8981\u6ca1\u6709\u4eba\u52a8\u6211\u3002\u201d \u3000\u3000\u53ea\u89c1\u6c60\u516c\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u5218\u662f\u4e4b\u7d27\u8ddf\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u6c60\u65e5\u66ae\u5bf9\u4e8c\u4eba\u5c31\u662f\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4e24\u4f4d\u4fa0\u58eb\u6551\u547d\u5927\u6069\u3002\u201d\u4ed6\u76ee\u4e2d\u6cea\u5149\u95ea\u6d41\uff0c\u4e24\u988a\u9690\u6709\u6cea\u75d5\uff1b\u539f\u6765\u4ed6\u89c1\u6b7b\u4f24\u72fc\u85c9\uff0c\u800c\u523a\u5ba2\u4e3b\u8981\u53ea\u662f\u4e3a\u4e86\u6740\u4ed6\uff0c\u4ee5\u81f4\u5bb3\u4e86\u90a3\u4e48\u591a\u4eba\u547d\u4f24\u4ea1\uff0c\u5fc3\u4e2d\u5927\u662f\u4e0d\u5fcd\uff0c\u7981\u4e0d\u4f4f\u8981\u843d\u6cea\u3002 \u3000\u3000\u4ed6\u5fcd\u60b2\u542b\u6078\u7684\u58f0\u97f3\uff0c\u66f4\u662f\u8bda\u631a\u52a8\u4eba\u3002 \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u522b\u5ba2\u6c14\uff0c\u8fd9\u662f\u6211\u7684\u672c\u4efd\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5374\u6ca1\u8bf4\u8bdd\uff0c\u9ed8\u9ed8\u4e3a\u4e00\u4e2a\u88ab\u706b\u836f\u70b8\u4f24\u7684\u8336\u5ba2\u88f9\u4f24\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u9a6c\u8e44\u7684\u5f97\uff0c\u90a3\u540d\u5251\u624b\u5df2\u6253\u9a6c\u800c\u53bb\uff0c\u60f3\u5fc5\u662f\u5218\u662f\u4e4b\u9063\u4ed6\u8d76\u8fd4\u6d1b\u9633\u8bf7\u52a8\u4eba\u624b\u8fc7\u6765\u63a5\u5e94\u3002 \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u4e09\u7237\uff0c\u8fd9\u6869\u6848\u5b50\u4f60\u4eb2\u773c\u89c1\u4e86\u3001\u4eb2\u624b\u7ba1\u4e86\uff0c\u4f46\u613f\u4f60\u80fd\u4e3a\u6211\u5bb6\u516c\u5b50\u8ffd\u67e5\u4e3b\u4f7f\uff0c\u4ee5\u6b63\u6cd5\u7eaa\u3002\u201d \u3000\u3000\u8ffd\u547d\u5ffd\u9053\uff1a\u201c\u6c60\u516c\u5b50\uff0c\u6709\u4e00\u4e8b\u8bf7\u6559\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5341\u5206\u8c26\u606d\uff0c\u5373\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u3002\u6709\u751a\u4e48\uff0c\u4e09\u7237\u7686\u8bf7\u4e0d\u5fc5\u89c1\u5916\uff0c\u5c3d\u8bf7\u5429\u5490\u5373\u53ef\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4f60\u53ef\u6709\u8fd9\u6837\u51f6\u6b8b\u7684\u4ec7\u5bb6\uff1f\u8fd9\u4e9b\u4eba\u4f3c\u8ddf\u4f60\u6709\u6df1\u4ec7\u5de8\u6068\uff0c\u4f60\u53ef\u6709\u5934\u7eea\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5728\u6b66\u6797\u4e2d\uff0c\u8c01\u6ca1\u6709\u4ec7\u5bb6\uff1f\u66f4\u4f55\u51b5\u6211\u8eab\u5728\u7ff0\u6797\u3001\u4ed5\u6797\u3001\u6b66\u6797\u91cc\uff0c\u7ed3\u6028\u96be\u514d\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u4eba\u90fd\u4f3c\u8eab\u8d1f\u8840\u6d77\u6df1\u4ec7\uff0c\u53ef\u6559\u4eba\u8d39\u89e3\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u6211\u770b\u8fd9\u6279\u4eba\uff0c\u4e5f\u4e0d\u53ea\u51b2\u7740\u6211\u5bb6\u516c\u5b50\u800c\u6765\u7684\uff0c\u4ed6\u4eec\u4e0d\u662f\u53e3\u53e3\u58f0\u58f0\u90fd\u662f\u6d1b\u9633\u56db\u516c\u5b50\u5417\uff1f\u6211\u770b\u9664\u4e86\u6211\u4eec\u2018\u5170\u4ead\u6c60\u5bb6\uff0c\u4e4b\u5916\uff0c\u2018\u5c0f\u78a7\u6e56\u6e38\u5bb6\uff0c\u3001\u2018\u5999\u624b\u5802\u56de\u5bb6\u2019\u3001\u2018\u5343\u53f6\u5c71\u5e84\u845b\u5bb6\u2019\uff0c\u83ab\u4e0d\u662f\u6cbe\u6709\u5173\u8054\uff0c\u6d1b\u9633\u56db\u516c\u5b50\u540d\u82e5\u5929\u65e5\uff0c\u96be\u514d\u906d\u4eba\u6240\u5ac9\uff0c\u8fd9\u90fd\u8981\u8bf7\u4e09\u7237\u591a\u52a0\u7559\u610f\u7684\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff1a\u8fd9\u6279\u4eba\u8981\u526a\u9664\u7684\uff0c\u4e0d\u53ea\u662f\u4f60\u4eec\uff0c\u8fd8\u6709\u5176\u4ed6\u4e09\u4f4d\u516c\u5b50\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u53cc\u773c\u4e00\u772f\uff0c\u7acb\u5373\u5728\u773c\u89d2\u88c1\u6210\u4e86\u4e24\u62b9\u5982\u5200\u5229\u7684\u7b11\u7eb9\uff0c\u201c\u4e5f\u53ef\u80fd\u662f\u5176\u4e2d\u4e00\u5bb6\uff0c\u4e3a\u5de9\u56fa\u52bf\u529b\uff0c\u53ea\u6c42\u72ec\u5c0a\uff0c\u4e0d\u8bb8\u5e76\u5b58\u3002\u201d \u3000\u3000\u8ffd\u547d\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u60f3\u5230\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u5947\u9053\uff1a\u201c\u4f60\u6ca1\u60f3\u5230\u751a\u4e48\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u8fde\u4ec1\u4e49\u6ee1\u5929\u4e0b\u7684\u2018\u6d1b\u9633\u56db\u516c\u5b50\u2019\uff0c\u4e5f\u4e00\u822c\u4eba\u4e00\u6837\u515a\u540c\u4f10\u5f02\u3001\u6392\u9664\u5f02\u5df1\uff1b\u5927\u597d\u6cb3\u5c71\uff0c\u4e3e\u76ee\u5e76\u975e\u6ca1\u6709\u4eba\u6750\uff0c\u800c\u662f\u6ca1\u6709\u5bb9\u4eba\u7684\u6c14\u91cf\uff0c\u4ee5\u81f4\u50cf\u4e00\u76d8\u6563\u6c99\uff0c\u8c01\u90fd\u4e0d\u80fd\u7ed3\u5408\u8d77\u6765\uff0c\u4e3a\u56fd\u4e3a\u6c11\uff0c\u505a\u70b9\u8e0f\u8e0f\u5b9e\u5b9e\u7684\u4e8b\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u51b7\u7b11\u9053\uff1a\u201c\u4e09\u7237\uff0c\u4f60\u8fd9\u53e5\u8bdd\uff0c\u53ea\u5bf9\u6211\u4eec\u516c\u5b50\u8bf4\uff0c\u53ef\u8d77\u4e0d\u4e86\u751a\u4e48\u4f5c\u7528\uff0c\u6211\u5bb6\u516c\u5b50\u4e5f\u603b\u4e0d\u80fd\u4e00\u53a2\u60c5\u613f\u3001\u5355\u65b9\u793a\u597d\u554a\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5982\u7389\u822c\u7684\u8138\u988a\uff0c\u5374\u51fa\u73b0\u4e86\u5fae\u5fae\u7684\u7ea2\u6655\uff0c\u60ed\u7136\u9053\uff1a\u201c\u4e09\u7237\uff0c\u4f60\u6559\u8bad\u7684\u662f\u3002\u201d \u3000\u3000\u8ffd\u547d\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u6211\u53ea\u662f\u7ebe\u8bf4\u5fc3\u4e2d\u7684\u90c1\u7ed3\u7f62\u4e86\uff0c\u6c60\u516c\u5b50\u4e07\u52ff\u89c1\u602a\u3002\u201d\u4ed6\u5fae\u5fae\u4e00\u505c\uff0c\u53c8\u9053\uff0c\u201c\u56db\u516c\u5b50\u5728\u6d1b\u9633\u751a\u6709\u52bf\u529b\uff0c\u6781\u5f97\u6c11\u5fc3\uff0c\u636e\u8bf4\u8fd1\u65e5\u7687\u4e0a\u8981\u9881\u4ee4\u4e0b\u6765\uff0c\u7504\u9009\u4f60\u4eec\u56db\u4f4d\u5176\u4e2d\u4e4b\u4e00\u4e3a\u2018\u6d1b\u9633\u738b\u2019\uff0c\u638c\u7ba1\u6d1b\u9633\u5175\u6743\u653f\u4e8b\uff0c\u4f60\u4eec\u56db\u4f4d\u5404\u6709\u5343\u79cb\uff0c\u96be\u5206\u8f69\u8f7b\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u6050\u6015\u76f8\u4e92\u503e\u8f67\u7684\u4e8b\uff0c\u5728\u6240\u96be\u514d\uff1b\u53ea\u671b\u6c60\u516c\u5b50\u80fd\u5fc3\u5b58\u5584\u5ff5\uff0c\u4ee5\u4e3a\u767e\u59d3\u798f\u7949\u4e3a\u91cd\uff0c\u5c3d\u91cf\u907f\u514d\u5377\u5165\u65e0\u8c13\u6597\u4e89\u4e2d\uff0c\u90a3\u5c31\u662f\u529f\u5fb7\u65e0\u91cf\u4e86\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u609a\u7136\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u5374\u95ee\uff1a\u201c\u4e0d\u77e5\u9053\u4e09\u7237\u6b64\u884c\u6765\u6d1b\u9633\uff0c\u4e3a\u7684\u662f\u751a\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8ffd\u547d\u770b\u4e86\u5218\u662f\u4e4b\u4e00\u773c\uff0c\u53c8\u770b\u770b\u6c60\u65e5\u66ae\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u542c\u8bf4\u8fc7\u7559\u53bf\u592a\u5b88\u5b5f\u968f\u56ed\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u832b\u7136\u3002 \u3000\u3000\u5218\u662f\u4e4b\u5373\u9053\uff1a\u201c\u6709\u3002\u5b5f\u592a\u5b88\u6e05\u5ec9\u4e0d\u963f\uff0c\u4e25\u660e\u5b88\u6b63\uff0c\u5f88\u6709\u540d\u671b\uff0c\u636e\u8bf4\u4ed6\u529e\u6848\u4e00\u5411\u79c9\u516c\u5904\u7406\uff0c\u6848\u65e0\u4f59\u728a\uff0c\u5e73\u53cd\u4e86\u4e0d\u5c11\u51a4\u6848\uff0c\u662d\u96ea\u4e86\u4e0d\u5c11\u51a4\u72f1\uff0c\u4e25\u529e\u4e86\u4e0d\u5c11\u52a3\u7ec5\uff0c\u7533\u8beb\u4e86\u4e0d\u5c11\u6076\u5ba6\uff0c\u53ef\u60dc\uff0c\u540e\u6765\u8fd8\u662f\u7ed9\u4eba\u53c2\u4e86\u4e00\u672c\uff0c\u4f3c\u88ab\u53d1\u914d\u5145\u519b\u5230\u6d82\u58c1\u53bb\u2026\u2026\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4ed6\u4e00\u5bb6\u5927\u5c0f\u5171\u5341\u4e00\u53e3\uff0c\u8fde\u5bb6\u4ec6\u5a62\u5f79\u4e09\u5341\u4e03\u4eba\uff0c\u5168\u6559\u4eba\u6740\u4e2a\u5e72\u51c0\uff0c\u4e8b\u60c5\u5c31\u53d1\u751f\u5728\u8fd9\u5f80\u6d1b\u9633\u7684\u9053\u4e0a\uff0c\u51f6\u5f92\u53ef\u8c13\u8d76\u5c3d\u6740\u7edd\u3002\u5b5f\u592a\u5b88\u4e25\u6b63\u4e0d\u5728\uff0c\u5728\u4efb\u671f\u95f4\u4ece\u4e0d\u8d2a\u8d43\u655b\u8d22\uff0c\u4eba\u79f0\u4e4b\u2018\u5b5f\u9752\u5929\u2019\uff0c\u800c\u4eca\u843d\u5f97\u8fd9\u79cd\u4e0b\u573a\uff0c\u6211\u603b\u8981\u8ddf\u4ed6\u67e5\u51fa\u51f6\u624b\uff0c\u4ee5\u796d\u4ed6\u5728\u5929\u4e4b\u7075\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u542c\u4e86\u4e5f\u6781\u6c14\u5fff\uff1a\u201c\u4e09\u7237\uff0c\u8fd9\u4ef6\u4e8b\u5b9e\u5728\u592a\u53ef\u6076\u4e86\uff0c\u5982\u7528\u5f97\u7740\u655d\u5e9c\u4e4b\u5904\uff0c\u8981\u4eba\u8981\u94b1\uff0c\u8bf7\u5c3d\u91cf\u5429\u5490\u3002\u201d \u3000\u3000\u8ffd\u547d\u77e5\u9053\u8fd9\u6c60\u65e5\u66ae\u5e74\u8f7b\u5fc3\u8f6f\uff0c\u5374\u53c8\u8840\u6c14\u65b9\u521a\uff0c\u4fbf\u8f9e\u8c22\u9053\uff1a\u201c\u73b0\u4e0b\u5c1a\u672a\u6709\u7709\u76ee\uff0c\u4eba\u591a\u53cd\u800c\u4e0d\u4fbf\uff0c\u6c60\u516c\u5b50\u597d\u610f\uff0c\u5728\u4e0b\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6570\u8def\u4eba\u9a6c\u9646\u7eed\u8d76\u5230\u3002\u539f\u6765\u8fd9\u9053\u4e0a\u65e9\u6709\u201c\u5170\u4ead\u6c60\u5e9c\u201d\u7684\u4eba\u51c6\u5907\u606d\u8fce\uff0c\u5269\u4e0b\u90a3\u540d\u5251\u624b\u6253\u9a6c\u8bf7\u63f4\uff0c\u8fd9\u4e9b\u5728\u9053\u4e0a\u82e6\u5019\u8fce\u8fd1\u7684\u4ec6\u4ece\u548c\u53cb\u670b\uff0c\u5168\u90fd\u8d76\u4e86\u8fc7\u6765\uff0c\u5176\u4e2d\u8fd8\u5305\u62ec\u4e86\u5728\u6c60\u5e9c\u95fb\u98ce\u800c\u6765\u6170\u95ee\u7684\u201c\u98df\u5ba2\u201d\u3001\u201c\u5b50\u5f1f\u201d\uff0c\u4e89\u76f8\u5df4\u7ed3\u9053\u5e78\uff0c\u8fd9\u5c0f\u5c0f\u7684\u8336\u5bee\u91cc\uff0c\u767b\u65f6\u70ed\u95f9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8ffd\u547d\u89c1\u6c60\u65e5\u66ae\u5fd9\u4e71\u4e2d\u4e0d\u5fd8\u5631\u5429\u4e0b\u5c5e\uff0c\u5b89\u987f\u8fd9\u8336\u5c45\u638c\u67dc\u7684\u540e\u4e8b\uff0c\u52a0\u4ee5\u629a\u6064\uff0c\u5e76\u534f\u5176\u91cd\u5efa\uff0c\u8fd8\u6709\u629a\u7597\u53d7\u4f24\u8336\u5ba2\u7b49\uff0c\u4fbf\u5411\u5728\u4e00\u65c1\u6de1\u7136\u5750\u770b\u4e00\u5207\u7684\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8fd9\u6c60\u516c\u5b50\uff0c\u603b\u7b97\u5bcc\u8d35\u800c\u4ecd\u7136\u8c26\u606d\uff0c\u53ea\u662f\u5fc3\u6027\u592a\u8106\u5f31\u4e00\u4e9b\uff0c\u6613\u52a8\u611f\u60c5\uff0c\u4f46\u5728\u5267\u70c8\u7684\u6c5f\u6e56\u6597\u4e89\u91cc\uff0c\u5bb9\u6613\u5403\u4e8f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5c3d\u7136\u3002\u6c60\u516c\u5b50\u8fd9\u7b49\u505a\u6cd5\uff0c\u6613\u640f\u4eba\u597d\u611f\uff0c\u751a\u5f97\u4eba\u7f18\u3002\u201d \u3000\u3000\u8ffd\u547d\u8be7\u5f02\u7684\u5411\u4ed6\u6295\u8fc7\u4e00\u773c\uff0c\u8bf4\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5e74\u7eaa\u8fd9\u4e48\u8f7b\uff0c\u770b\u4e16\u4e8b\u5374\u662f\u592a\u51b7\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5c31\u6015\u70ed\u3002\u6211\u559c\u6b22\u5bd2\u51ac\u3002\u8d8a\u51b7\uff0c\u6211\u5c31\u8d8a\u6109\u60a6\u3002\u6211\u5fc3\u5934\u4e00\u70ed\uff0c\u5c31\u4e0d\u6613\u6536\u62fe\u4e86\u3002\u6211\u6015\u6211\u63a7\u5236\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u8ffd\u547d\u4ed4\u7ec6\u7aef\u8be6\u4e86\u4ed6\u4e00\u9635\uff0c\u53ea\u9053\uff1a\u201c\u5f88\u50cf\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4fa7\u4e86\u4fa7\u9996\uff0c\u95ee\uff1a\u201c\u50cf\u8c01\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u6211\u5927\u5e08\u5144\uff0c\u65e0\u60c5\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u773c\u775b\u6709\u4e86\u7b11\u610f\uff0c\u90a3\u7b11\u610f\u9a71\u8d70\u4e86\u8bb8\u591a\u5fe7\u6092\uff0c\u4f46\u591a\u4e86\u4e00\u5c42\u6de1\u6de1\u7684\u54c0\u6101\uff0c\u201c\u662f\u4e48\uff1f\u201d \u3000\u3000\u8ffd\u547d\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u89c1\u602a\uff0c\u4f60\u6bd4\u4ed6\uff0c\u8fd8\u8981\u5e74\u8f7b\u3001\u8fd8\u8981\u4fca\u4fcf\uff0c\u8fd8\u8981\u50cf\u4e2a\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6c89\u601d\u4e00\u4e0b\uff0c\u4ed6\u7684\u7709\u5fae\u5fae\u8e59\u7740\uff0c\u50cf\u633d\u624b\u9501\u8d77\u4e00\u79cb\u7684\u6df1\u6028\u3002\uff0c\u2018\u4ed6\u8ddf\u6211\u4e0d\u540c\uff0c\u201d\u4ed6\u9053\uff1a\u201c\u4ed6\u5df2\u6295\u8eab\u5165\u5728\u8fd9\u7ea2\u5c18\u5341\u4e08\u91cc\uff0c\u7ffb\u8fc7\u3001\u6eda\u8fc7\u3001\u751a\u4e48\u4e16\u5c40\u90fd\u89c1\u8fc7\u3001\u751a\u4e48\u7ecf\u5386\u90fd\u9605\u8fc7\uff0c\u6240\u4ee5\u4ed6\u518d\u8106\u5f31\uff0c\u4e5f\u662f\u4e2a\u575a\u5f3a\u7684\u4eba\uff0c\u80fd\u51fa\u4e16\uff0c\u4e5f\u80fd\u5165\u4e16\u3002\u800c\u6211\u2026\u2026\u201d\u6b32\u8bf4\u8fd8\u6b62\u3002 \u3000\u3000\u7136\u540e\u4ed6\u63a5\u9053\uff1a\u201c\u4f46\u6211\u80fd\u51fa\u4fbf\u4e0d\u80fd\u5165\uff0c\u80fd\u5165\uff0c\u4fbf\u4e0d\u80fd\u51fa\u3002\u201d \u3000\u3000\u8ffd\u547d\u7b11\u7740\u62cd\u4e86\u62cd\u4ed6\u7626\u5c0f\u7684\u80a9\u818a\uff0c\u9053\uff1a\u201c\u4f60\u554a\uff0c\u4e00\u4e2a\u4eba\u81ea\u5df1\u770b\u81ea\u5df1\uff0c\u600e\u4e48\u80fd\u770b\u5f97\u6e05\u695a\uff1f\u81ea\u5df1\u770b\u5f97\u5927\u591a\u3002\u5927\u8fd1\uff0c\u4e0d\u4e00\u5b9a\u5c31\u662f\u81ea\u5df2\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u6539\u6362\u4e86\u8bdd\u9898\uff1a\u201c\u4f60\u8981\u53bb\u4fa6\u5bdf\u6740\u5bb3\u5b5f\u968f\u56ed\u5168\u5bb6\u7684\u6848\u5b50\u5417\uff1f\u201d \u3000\u3000\u8ffd\u547d\u773c\u775b\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u8981\u662f\u8001\u5f1f\u80af\u8ddf\u6211\u4e00\u9053\u7a3d\u67e5\uff0c\u8fd9\u4ef6\u6848\u5b50\u7684\u5143\u51f6\u52bf\u529b\u518d\u5f3a\u5927\uff0c\u6211\u4e5f\u4e0d\u5fc5\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u61d2\u6d0b\u6d0b\u7684\u671b\u4e86\u8ffd\u547d\u4e00\u773c\uff0c\u53ea\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u6839\u672c\u6ca1\u6709\u62c5\u5fc3\u8fc7\u3002\u516c\u5bb6\u4e8b\uff0c\u6211\u4e5f\u505a\u4e0d\u6765\uff0c\u800c\u4e14\uff0c\u4e5f\u65e0\u610f\u4e3a\u4e4b\u3002\u5982\u679c\u4f60\u6709\u4e8b\uff0c\u6211\u5012\u8981\u8bf7\u4f60\u5429\u5490\u4e00\u58f0\uff0c\u6211\u4e00\u5b9a\u5230\u3002\u201d \u3000\u3000\u8ffd\u547d\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u6211\u5c31\u4e0d\u52c9\u5f3a\u4e86\u3002\u201d\u53c8\u95ee\uff0c\u201c\u8001\u5f1f\u4e00\u8eab\u597d\u6b66\u672f\uff0c\u5374\u5728\u54ea\u91cc\u9ad8\u5c31\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u62cd\u62cd\u65e7\u5305\u88b1\uff1a\u201c\u6211\u5728\u8001\u5458\u5916\u5bb6\u91cc\u6559\u51e0\u4e2a\u5b69\u5b50\u8bfb\u4e66\uff0c\u5982\u6b64\u800c\u5df2\u3002\u201d \u3000\u3000\u8ffd\u547d\u957f\u53f9\u9053\uff1a\u201c\u8fd9\u53c8\u4f55\u5fc5\uff0c\u5b9e\u5728\u662f\u592a\u59d4\u5c48\u4f60\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5374\u6beb\u4e0d\u4ee5\u4e3a\u7136\uff1a\u201c\u4e00\u4e2a\u4eba\u53ea\u8981\u80fd\u5b89\u8eab\u7acb\u547d\uff0c\u4fbf\u53ef\u4ee5\u4e86\uff0c\u6211\u8981\u517b\u6d3b\u8001\u7236\uff0c\u5e72\u751a\u4e48\u6d3b\u513f\u90fd\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u8ffd\u547d\u4e00\u4e0b\u5b50\u89c9\u5f97\u8ddf\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u79bb\u5f97\u597d\u8fd1\uff0c\u53c8\u8ddd\u5f97\u597d\u8fdc\uff1b\u4f46\u65e0\u8bba\u662f\u8fd1\u662f\u8fdc\uff0c\u90fd\u5bf9\u4ed6\u5341\u5206\u73cd\u60dc\u3002 \u3000\u3000\u8fd9\u65f6\u53c8\u6765\u4e86\u4e00\u9a91\u3002 \u3000\u3000\u9a91\u5f97\u5e76\u4e0d\u6025\uff0c\u4f46\u5feb\u3002 \u3000\u3000\u9a6c\u9ed1\u3001\u4eba\u9ed1\u3001\u9ed1\u62ab\u98ce\uff0c\u50cf\u9aa4\u63a9\u6765\u4e86\u4e00\u6735\u9ed1\u4e91\u3002 \u3000\u3000\u9a6c\u9ed1\u5f97\u6ca1\u6709\u4e00\u4e1d\u6742\u6bdb\u3002 \u3000\u3000\u8863\u9ed1\u5f97\u8ddf\u9633\u5149\u5f62\u6210\u5f3a\u70c8\u7684\u5bf9\u7167\u3002 \u3000\u3000\u4eba\u5e73\u5b9e\u800c\u7c97\u58ee\uff0c\u76ae\u80a4\u9edd\u9ed1\uff0c\u6d53\u9ed1\u7684\u7709\u6bdb\uff0c\u6de1\u9ed1\u7684\u539a\u5507\uff0c\u6df1\u9ed1\u7684\u5feb\u9774\uff0c\u4e00\u628a\u9ed1\u8272\u7684\u5200\u9798\uff0c\u9798\u5916\u9732\u7740\u9752\u9ed1\u8272\u7684\u5200\u67c4\u3002 \u3000\u3000\u8ffd\u547d\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6c60\u516c\u5b50\uff0c\u6709\u7ef0\u53f7\u2018\u5218\u72ee\u5b50\u2019\u7684\u667a\u56ca\u5218\u662f\u4e4b\uff0c\u53c8\u6709\u624b\u5e95\u4e0b\u52c7\u731b\u7cbe\u8fdb\u7684\u2018\u62fc\u547d\u4e09\u90ce\u2019\u6d2a\u4e09\u70ed\uff0c\u52a0\u4e0a\u8fd9\u4e2a\u5b9e\u884c\u80fd\u529b\u6781\u9ad8\u7684\u529e\u4e8b\u5e72\u6750\u2018\u9ed1\u65cb\u98ce\u2019\u5c0f\u767d\uff0c\u8fd9\u2018\u5170\u4ead\u6c60\u5e9c\u2019\u7684\u58f0\u52bf\uff0c\u5176\u5b9e\u4ec5\u6b21\u4e8e\u2018\u5c0f\u78a7\u6e56\u6e38\u5bb6\u2019\u800c\u5df2\uff01\u201d \u3000\u3000\u53ea\u542c\u6c60\u65e5\u66ae\u559c\u9053\uff1a\u201c\u5c0f\u767d\uff0c\u4f60\u6765\u4e86\u5c31\u597d\u4e86\u3002\u201d\u4f3c\u5bf9\u4ed6\u5341\u5206\u6b23\u6170\u653e\u5fc3\u3002 \u3000\u3000\u5c0f\u767d\u8dea\u5730\u800c\u9053\uff1a\u201c\u516c\u5b50\u65e0\u6059\uff0c\u8bf7\u6055\u5c5e\u4e0b\u6765\u8fdf\u3002\u201d\u6c60\u65e5\u66ae\u8fde\u5fd9\u628a\u4ed6\u6276\u8d77\u3002 \u3000\u3000\u201c\u9ed1\u65cb\u98ce\u201d\u5c0f\u767d\u4e00\u81f3\uff0c\u4f24\u7684\u4eba\u88ab\u8201\u8d70\uff0c\u6b7b\u7684\u4eba\u88ab\u9a8c\u660e\uff0c\u5e97\u4e2d\u7d0a\u4e71\uff0c\u4e00\u4e00\u88ab\u6574\u7406\u51fa\u6765\uff0c\u5c0f\u767d\u8c03\u5ea6\u6709\u65b9\uff0c\u6beb\u4e0d\u614c\u4e71\u3002 \u3000\u3000\u5218\u662f\u4e4b\u5374\u9759\u6084\u6084\u5730\u5411\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u6869\u72d9\u6740\uff0c\u6050\u6015\uff0c\u8fd9\u53ea\u662f\u4e00\u4e2a\u5f00\u7aef\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u62c5\u5fc3\u5730\u9053\uff1a\u201c\u662f\u554a\uff0c\u6765\u7684\u51e0\u4eba\uff0c\u6b66\u529f\u90fd\u5f88\u9ad8\u5f3a\uff0c\u6211\u6015\u5218\u662f\u4e4b\u76f4\u89c6\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u516c\u5b50\u662f\u6015\u6211\u7b49\u4fdd\u9a7e\u4e0d\u529b\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fd9\u9053\uff1a\u201c\u5148\u751f\u5343\u4e07\u522b\u591a\u5fc3\u3002\u6211\u6015\u7684\u662f\u9632\u4e0d\u80dc\u9632\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u773c\u775b\u53c8\u772f\u6210\u4e00\u7ebf\uff1a\u201c\u516c\u5b50\uff0c\u60f3\u4e0d\u60f3\u6709\u5907\u65e0\u60a3\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5373\u9053\uff1a\u201c\u8bf7\u6559\u5148\u751f\uff0c\u5982\u4f55\u6709\u5907\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u7528\u7fbd\u6247\u9065\u6307\u8ffd\u547d\u4e0e\u65b9\u90aa\u771f\u5728\u8336\u5c45\u4e00\u9685\u7684\u80cc\u5f71\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7559\u4e0b\u4ed6\u4eec\u4e8c\u4eba\uff0c\u5373\u4e3a\u5f3a\u52a9\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u6b23\u7136\u9053\uff1a\u201c\u6211\u6b63\u6709\u6b64\u610f\uff0c\u201d\u53c8\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u65cb\u5373\u53c8\u9053\uff0c\u201c\u8ffd\u547d\u662f\u540d\u6355\uff0c\u6709\u516c\u4e8b\u5728\u8eab\uff0c\u6b64\u4eba\u4e00\u5411\u65e0\u89c6\u4e8e\u5bcc\u8d35\u529f\u540d\uff0c\u53ea\u6015\u96be\u4ee5\u7559\u5f97\u4f4f\u4ed6\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u5bf9\u8ffd\u547d\uff0c\u53ea\u4f5c\u8bd5\u63a2\uff1b\u8fd9\u5e74\u8f7b\u4eba\u6b66\u529f\u9ad8\u5230\u4e0d\u53ef\u601d\u8bae\uff0c\u800c\u4e14\u6f5c\u529b\u65e0\u53ef\u9650\u91cf\uff0c\u6b64\u4eba\u82e5\u4e0d\u6536\u4e8e\u95e8\u4e0b\uff0c\u4e07\u4e00\u7ed9\u6e38\u3001\u845b\u3001\u56de\u4e09\u5bb6\u8058\u53bb\uff0c\u5219\u662f\u4f7f\u6211\u4eec\u591a\u6dfb\u4e00\u53f7\u52b2\u654c\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u54ac\u4e86\u54ac\u5507\uff0c\u9053\uff1a\u201c\u5148\u751f\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5218\u662f\u4e4b\u4f4e\u58f0\u75be\u9053\uff1a\u201c\u8ffd\u547d\u5728\u8fd9\u91cc\u5f85\u4e0d\u4e45\uff0c\u4e00\u5b9a\u4f1a\u8d70\uff1b\u8fd9\u5e74\u8f7b\u4eba\u82e5\u633d\u4e0d\u4f4f\uff0c\u5219\u5b81\u53ef\u9664\u53bb\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u8138\u8272\u53d8\u4e86\u53d8\uff1a\u201c\u90a3\u4e0d\u884c\uff0c\u4ed6\u600e\u4e48\u8bf4\u4e5f\u6551\u8fc7\u6211\u4e00\u547d\uff0c\u600e\u53ef\u2014\u2014\u201d \u3000\u3000\u5218\u662f\u4e4b\u51b7\u51b7\u5730\u9053\uff1a\u201c\u516c\u5b50\uff0c\u65e0\u6bd2\u4e0d\u4e08\u592b\uff0c\u7559\u7740\u7978\u60a3\uff01\u201d \u3000\u3000\u6c60\u65e5\u66ae\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u8981\u6c42\u4f3c\u7684\u9053\uff1a\u201c\u6211\u4eec\u5148\u7559\u4ed6\u4e00\u7559\uff0c\u770b\u600e\u4e48\u6837\uff0c\u597d\u4e0d\u597d\uff1f\u6309\u7406\u8bf4\uff0c\u54b1\u4eec\u65bd\u4e8e\u91cd\u91d1\u793c\u5f85\u3001\u529f\u540d\u5bcc\u8d35\uff0c\u4ed6\u6ca1\u6709\u7406\u7531\u4e0d\u52a8\u5fc3\u7684\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u6c89\u7740\u8138\u8272\u563f\u7b11\u9053\uff1a\u201c\u5982\u4ed6\u7518\u8f9e\u539a\u5e02\uff0c\u5c1a\u4e0d\u52a8\u5bb9\uff0c\u6b64\u4eba\u66f4\u4e0d\u80fd\u4e0d\u9664\u3002\u201d \u3000\u3000\u201c\u82e5\u5230\u4e86\u90a3\u4e2a\u65f6\u5019\u2026\u2026\u201d\u6c60\u65e5\u66ae\u65e0\u5948\u4ed6\u8bf4\uff0c\u201c\u5c31\u542c\u51ed\u5148\u751f\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u8fd9\u624d\u6ee1\u610f\u7684\u70b9\u4e86\u70b9\u5934\u3002\u5218\u662f\u4e4b\u51ed\u4e00\u526f\u7cbe\u5bc6\u7684\u5934\u8111\u3001\u8fdb\u9000\u7684\u5206\u5bf8\u3001\u548c\u4e0d\u51e1\u7684\u6b66\u529f\uff0c\u5728\u4e0d\u5c11\u540d\u95e8\u671b\u65cf\u3001\u6b66\u6797\u4e16\u5bb6\u91cc\u4efb\u8fc7\u4e3e\u8db3\u8f7b\u91cd\u7684\u804c\u53f8\uff0c\u4f46\u201c\u5170\u4ead\u201d\u6c60\u65e5\u66ae\u5bf9\u4ed6\u4e00\u5411\u4fe1\u91cd\u501a\u91cd\uff0c\u89e3\u8863\u63a8\u98df\uff0c\u9047\u5927\u4e8b\u83ab\u4e0d\u8a00\u542c\u8ba1\u4ece\uff0c\u662f\u81f4\u4ee4\u4ed6\u4e00\u76f4\u7559\u5728\u6c60\u5bb6\u7684\u6700\u91cd\u8981\u539f\u56e0\u4e4b\u4e00\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c40\u9762\u5927\u81f4\u5df2\u6536\u62fe\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8ffd\u547d\u4e5f\u66ff\u4e24\u540d\u4f24\u8005\u63a5\u9a73\u597d\u65ad\u9aa8\uff0c\u5411\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u6c60\u516c\u5b50\uff0c\u4f60\u8fd9\u4f4d\u2018\u9ed1\u65cb\u98ce\u2019\u5904\u4e8b\u715e\u662f\u5feb\u5229\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fd9\u5f15\u89c1\u201c\u9ed1\u65cb\u98ce\u201d\u5c0f\u767d\u4e0e\u8ffd\u547d\uff0c\u987a\u52bf\u63a2\u95ee\uff1a\u201c\u8fd9\u4f4d\u5144\u53f0\u9ad8\u59d3\u5927\u540d\uff1f\u5e9c\u4e0a\u54ea\u91cc\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u61d2\u61d2\u5730\u7b54\uff1a\u201c\u6211\u59d3\u65b9\u3002\u201d\u5c31\u4e0d\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u7b49\u4e0d\u5f97\u8981\u9886\u3002\u8ffd\u547d\u5374\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u6211\u6709\u516c\u52a1\u5728\u8eab\uff0c\u8fd8\u8981\u8d76\u8def\uff0c\u5c31\u6b64\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fd9\u6073\u60c5\u633d\u7559\u3002\u8ffd\u547d\u575a\u6301\u8981\u8d70\u3002\u6c60\u65e5\u66ae\u53ea\u597d\u8bf4\uff1a\u201c\u4e09\u7237\u7684\u6551\u547d\u5927\u6069\uff0c\u6c60\u67d0\u94ed\u523b\u5728\u5fc3\uff0c\u6c38\u5fd7\u4e0d\u5fd8\u3002\u4e09\u7237\u82e5\u8fdb\u6d1b\u9633\uff1a\u83ab\u5fd8\u4e86\u5149\u4e34\u655d\u820d\uff0c\u518d\u4f5c\u957f\u53d9\uff0c\u6b64\u5916\uff0c\u4e09\u7237\u5982\u7528\u5f97\u7740\u2018\u5170\u4ead\u2019\u5b50\u5f1f\u4e4b\u5904\uff0c\u5c3d\u8bf7\u5429\u5490\u3002\u201d \u3000\u3000\u8ffd\u547d\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u4e00\u5b9a\u201d\u3002 \u3000\u3000\u8bf4\u7740\u4fbf\u8981\u79bb\u53bb\u3002\u8fd9\u65f6\u5df2\u8fd1\u5165\u66ae\uff0c\u65b9\u90aa\u771f\u4e5f\u8981\u8ddf\u4ed6\u4e00\u9053\u79bb\u5f00\u3002\u6c60\u65e5\u66ae\u6025\u4e86\uff0c\u4fbf\u53bb\u62c9\u4f4f\u65b9\u90aa\u771f\u7684\u624b\uff0c\u4e00\u4e2a\u52b2\u513f\u5730\u95ee\uff1a\u201c\u5144\u53f0\u5e9c\u4e0a\u90a3\u91cc\uff1f\u53ef\u6709\u4e8b\u4e48\uff1f\u600e\u4e48\u5306\u5306\u8981\u8d70\uff1f\u4e0d\u80af\u8ba9\u5728\u4e0b\u6073\u8c22\uff1f\u4e0d\u5982\u5230\u655d\u4e0b\u5904\u559d\u676f\u6c34\u9152\uff0c\u518d\u5411\u5144\u53f0\u8bf7\u76ca\uff1f\u5144\u53f0\u82e5\u575a\u6301\u8981\u8d70\uff0c\u5728\u4e0b\u76f8\u9001\u4e00\u7a0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u53ea\u50b2\u5cb8\u7684\u3001\u6de1\u7136\u7684\u3001\u6f47\u6d12\u7684\u542c\u7740\uff0c\u53ea\u5728\u8981\u7d27\u5173\u8282\u4e0a\uff0c\u624d\u4e0d\u7740\u8fb9\u9645\u7684\u5e94\u4e0a\u4e00\u5e94\u3002 \u3000\u3000\u8ffd\u547d\u77a7\u5728\u773c\u91cc\uff0c\u53ea\u7b11\u8bf4\uff1a\u201c\u4e0d\u5982\u65b9\u5144\u5f1f\u5c31\u8ddf\u6c60\u516c\u5b50\u591a\u53d9\u53d9\uff0c\u6211\u5012\u8981\u5148\u884c\u4e00\u6b65\u4e86\u3002\u201d\u9042\u4f4e\u58f0\u5411\u65b9\u90aa\u771f\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u5982\u679c\u4f60\u4e0d\u7518\u5c31\u6b64\u57cb\u6ca1\u4e00\u751f\uff0c\u610f\u6b32\u5e73\u6b65\u9752\u4e91\uff0c\u8fd9\u4ed6\u516c\u5b50\u5012\u662f\u5bc4\u91cd\u4e8e\u4f60\uff0c\u4f60\u5927\u6709\u53d1\u6325\u4f59\u5730\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53ea\u5026\u5026\u5730\u4e00\u7b11\uff0c\u968f\u5373\u8ddf\u8ffd\u547d\u6b65\u8d70\u3002 \u3000\u3000\u8ffd\u547d\u5fae\u559f\u4e00\u58f0\uff0c\u4e5f\u7531\u5f97\u65b9\u90aa\u771f\u8ddf\u4ed6\u4e00\u9053\u3002 \u3000\u3000\u5218\u662f\u4e4b\u4e00\u4f7f\u773c\u8272\uff0c\u6d2a\u4e09\u70ed\u8ddf\u5728\u65b9\u90aa\u771f\u540e\u9762\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u65b9\u90aa\u771f\u901f\u7136\u56de\u8eab\uff0c\u5251\u4ecd\u5728\u6c34\u84dd\u8272\u7684\u5e03\u5e1b\u4e2d\uff0c\u4f46\u5251\u6115\u5df2\u62b5\u5728\u6d2a\u4e09\u70ed\u80f8\u524d\uff0c\u628a\u4ed6\u7684\u6765\u52bf\u751f\u751f\u622a\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u65b9\u90aa\u771f\u7528\u4e00\u79cd\u575a\u5b9a\u5f97\u63a5\u8fd1\u51b7\u6f20\u7684\u58f0\u97f3\u9053\uff1a \u3000\u3000\u201c\u56de\u53bb\uff01\u4f60\u4eec\u4e0d\u8fc7\u662f\u8981\u6211\u4e3a\u6c60\u5bb6\u6548\u547d\uff0c\u4f46\u6211\u4e00\u70b9\u5174\u81f4\u90fd\u6ca1\u6709\uff01\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u7684\u52bf\u5b50\u786c\u786c\u987f\u4f4f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8fd9\u4e00\u53e5\u8bdd\uff0c\u4e5f\u628a\u4f17\u4eba\u9707\u4f4f\u3002 \u3000\u3000\u9ec4\u660f\u5165\u66ae\uff0c\u70c8\u9633\u5df2\u6210\u4e86\u5fae\u9189\u7684\u80ed\u8102\u3002 \u3000\u3000\u65b9\u90aa\u771f\u500f\u5730\u6536\u5251\uff0c\u8fd4\u8eab\u6b32\u884c\uff0c\u5ffd\u7136\u9ed1\u5f71\u5982\u9b45\uff0c\u95ea\u62e6\u5728\u524d\u3002 \u3000\u3000\u9ed1\u8863\u9ed1\u8138\u9ed1\u62ab\u98ce\u3002 \u3000\u3000\u5c0f\u767d\u3002 \u3000 \u7b2c\u56db\u7ae0\u3000\u9ed1\u65cb\u98ce\u5c0f\u767d \u3000 \u3000\u3000\u65b9\u90aa\u771f\u770b\u4e5f\u4e0d\u770b\uff0c\u7ee7\u7eed\u5f80\u524d\u8d70\u53bb\u3002 \u3000\u3000\u5c0f\u767d\u4f38\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6b63\u597d\u62e6\u4f4f\u65b9\u90aa\u771f\u7684\u53bb\u8def\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5e73\u5e73\u9759\u9759\u5730\u8bf4\uff1a\u201c\u4f60\u7684\u624b\u4e0d\u60f3\u8981\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u767d\u77b3\u5b54\u6536\u7f29\uff0c\u53ea\u9053\uff1a\u201c\u8bf7\u4e0d\u8981\u8d70\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u7b11\uff1a\u201c\u5982\u679c\u6211\u4e00\u5b9a\u8981\u8d70\u5462\uff1f\u201d \u3000\u3000\u5c0f\u767d\u9053\uff1a\u201c\u6211\u7559\u4f60\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u62ac\u9996\u671b\u4e86\u671b\u5929\uff0c\u5026\u7136\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u8ffd\u547d\u5728\u65c1\uff0c\u4e00\u89c1\u65b9\u90aa\u771f\u4ef0\u8138\u770b\u5929\uff0c\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u53ef\u662f\u65b9\u90aa\u771f\u5df2\u7136\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u8ffd\u547d\u3001\u6d2a\u4e09\u70ed\u3001\u5218\u662f\u4e4b\u3001\u6c60\u65e5\u66ae\u56db\u4eba\uff0c\u65e0\u4e0d\u4eb2\u773c\u76ee\u7779\u65b9\u90aa\u771f\u7684\u51fa\u624b\u3002 \u3000\u3000\u4e5f\u65e0\u4eba\u4e0d\u4e3a\u4e4b\u52a8\u5bb9\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51fa\u624b\u53ea\u4e00\u5251\u3002 \u3000\u3000\u4e00\u5251\u5c31\u65a9\u5f80\u5c0f\u767d\u7684\u624b\u3002 \u3000\u3000\u5c0f\u767d\u5e76\u4e0d\u7f29\u624b\u3002 \u3000\u3000\u4ed6\u7684\u77ed\u5200\u5728\u5343\u94a7\u4e00\u53d1\u95f4\uff0c\u53ca\u65f6\u67b6\u5728\u81c2\u4e0a\uff01 \u3000\u3000\u5175\u5668\u6709\u8c13\uff1a\u201c\u2018\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u201d\uff0c\u5c0f\u767d\u827a\u9ad8\u80c6\u5927\uff0c\u4e0e\u4eba\u4ea4\u624b\uff0c\u65e0\u8bba\u5bf9\u624b\u591a\u5f3a\uff0c\u83ab\u4e0d\u62a2\u8fdb\u4e2d\u950b\u3001\u8fd1\u8eab\u76f8\u640f\uff0c\u4ed6\u6839\u672c\u4e0d\u6015\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u5929\u751f\u4e0d\u77e5\u754f\u60e7\u4e3a\u4f55\u7269\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7684\u5251\u52bf\uff0c\u5374\u7a81\u7136\u53d8\u4e86\u3002 \u3000\u3000\u5251\u9537\u53cd\u649e\u5411\u5c0f\u767d\u7684\u80f8\u819b\u3002 \u3000\u3000\u5c0f\u767d\u5de6\u624b\u4f38\u51fa\uff0c\u53f3\u624b\u6301\u5203\u6551\u5de6\u81c2\uff0c\u80f8\u95e8\u9732\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7834\u7efd\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5c31\u51fb\u5728\u8fd9\u4e2a\u7a8d\u95e8\u4e0a\u3002 \u3000\u3000\u5c0f\u767d\u7684\u59ff\u52bf\u7a81\u7136\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5de6\u624b\u5df2\u95ea\u7535\u822c\u7f29\u4e86\u56de\u6765\uff0c\u95ea\u7535\u822c\u6293\u4f4f\u5251\u9537\uff0c\u5c31\u50cf\u4e00\u6761\u6bd2\u86c7\u53ea\u8981\u4ef0\u9996\u53d1\u51fa\u653b\u51fb\uff0c\u4ed6\u66f4\u8fc5\u75be\u7684\u6293\u4f4f\u5b83\u7684\u4e03\u5bf8\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u8ffd\u547d\u53f1\u4e86\u4e00\u58f0\uff1a\u201c\u4f7f\u4e0d\u5f97\uff01\u201d \u3000\u3000\u4e24\u4eba\u9661\u5730\u5206\u4e86\u5f00\u6765\uff0c\u5939\u7740\u51e0\u58f0\u88c2\u5e1b\u7684\u8106\u54cd\u3002 \u3000\u3000\u5c0f\u767d\u5df2\u5728\u516b\u5c3a\u5f00\u5916\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7684\u9ed1\u62ab\u98ce\uff0c\u5df2\u6709\u4e09\u5904\u88c2\u53e3\uff0c\u80f8\u524d\u7684\u9ed1\u8863\uff0c\u4e5f\u6709\u4e24\u5904\u88c2\u7f1d\u3002 \u3000\u3000\u90a3\u662f\u5251\u6c14\u5272\u7834\u7684\u3002 \u3000\u3000\u53ef\u662f\u65b9\u90aa\u771f\u5e76\u672a\u62d4\u5251\u3002 \u3000\u3000\u4ed6\u628a\u5251\u67b6\u5728\u80a9\u4e0a\uff0c\u6709\u8da3\u7684\u770b\u7740\u5c0f\u767d\uff0c\u5fae\u7b11\u8bf4\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u6b66\u529f\uff0c\u8fd8\u7b97\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c31\u7b97\u662f\u5218\u662f\u4e4b\uff0c\u4e5f\u66fe\u5bf9\u5c0f\u767d\u4e0b\u8fc7\u8fd9\u6837\u7684\u8bc4\u8bed\uff1a\u201c\u8fde\u5c0f\u767d\u90fd\u5bb3\u6015\u7684\u4e8b\uff0c\u4fbf\u51b3\u4e0d\u80fd\u505a\uff0c\u56e0\u4e3a\u90a3\u6839\u672c\u4e0d\u662f\u4eba\u505a\u7684\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u6b66\u529f\u4e0d\u80fd\u7b97\u9ad8\uff0c\u4fbf\u60dc\u8a00\u5982\u91d1\uff0c\u8bc6\u89c1\u6781\u9ad8\uff0c\u5411\u4e0d\u8f7b\u8bb8\u4eba\uff0c\u4ed6\u8bf4\u7684\u8bdd\u4e0d\u4ec5\u5728\u201c\u5170\u4ead\u6c60\u5bb6\u201d\u6709\u5206\u91cf\uff0c\u5728\u6b66\u6797\u4e2d\u4e00\u6837\u4e5f\u6709\u5206\u91cf\u3002 \u3000\u3000\u4eba\u4eba\u90fd\u77e5\u9053\u201c\u5170\u4ead\u6c60\u516c\u5b50\u201d\u5e10\u4e0b\uff0c\u6709\u4e09\u5927\u9ad8\u624b\uff1a\u8db3\u667a\u591a\u8c0b\u7684\u5218\u662f\u4e4b\uff0c\u6709\u52c7\u6709\u8c0b\u7684\u5c0f\u767d\uff0c\u6709\u52c7\u65e0\u8c0b\u7684\u6d2a\u4e09\u70ed\u3002\u6c60\u5bb6\u56e0\u800c\u58f0\u5f3a\u52bf\u58ee\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6b64\u523b\u8fde\u5c0f\u767d\u7684\u773c\u4e2d\u4e5f\u6d41\u9732\u51fa\u4e00\u79cd\u795e\u8272\u3002 \u3000\u3000\u6050\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7684\u5251\uff0c\u672a\u51fa\u9798\u5c31\u5212\u7834\u4e86\u4ed6\u8863\u886b\u4e03\u516b\u9053\u53e3\u5b50\uff0c\u800c\u4e14\u8fde\u4ed6\u90fd\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u600e\u4e48\u8d25\u5728\u5bf9\u65b9\u5251\u4e0b\u7684\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e00\u7b11\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53c8\u56de\u590d\u4e86\u90a3\u4e00\u80a1\u90c1\u8272\u3002 \u3000\u3000\u82f1\u6717\u7684\u6092\u8272\u3002 \u3000\u3000\u4ed6\u642d\u5251\u5728\u80a9\uff0c\u6d12\u7136\u884c\u53bb\u3002 \u3000\u3000\u5c0f\u767d\u7684\u8138\u8272\u66f4\u9ed1\u4e86\u3002 \u3000\u3000\u4ed6\u4f38\u624b\u3002 \u3000\u3000\u4f38\u51fa\u5de6\u624b\u3002 \u3000\u3000\u5de6\u624b\u4f9d\u7136\u62e6\u4f4f\u65b9\u90aa\u771f\u7684\u53bb\u8def\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5012\u6709\u4e9b\u8be7\u5f02\u8d77\u6765\uff1a\u201c\u4f60\u4e0d\u6015\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u767d\u9053\uff1a\u201c\u6cca\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u62e6\u6211\uff1f\u201d \u3000\u3000\u5c0f\u767d\u9053\uff1a\u201c\u516c\u5b50\u8981\u7559\u4f60\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u7559\u5f97\u4f4f\u6211\uff1f\u201d \u3000\u3000\u5c0f\u767d\u9053\uff1a\u201c\u7559\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u65e2\u7559\u4e0d\u4f4f\uff0c\u8fd8\u4e0d\u8ba9\u5f00\uff1f\u201d \u3000\u3000\u5c0f\u767d\u9053\uff1a\u201c\u7559\u4e0d\u4f4f\u4e5f\u5f97\u7559\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7684\u773c\u795e\u7a81\u7136\u5389\u70c8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u2014\u2014\u662f\u4ed6\u7684\u6df1\u90c1\u88ab\u5bf9\u65b9\u7684\u62d7\u6267\u6fc0\u8d77\u4e86\u6218\u5fd7\uff1f \u3000\u3000\u4ed6\u4e00\u6b65\u5c31\u8e0f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c0f\u767d\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u53d1\u51fa\u4e86\u4e03\u9053\u4ed6\u5e73\u751f\u6781\u5c11\u65bd\u4e3a\u7684\u6740\u7740\uff01 \u3000\u3000\u8fd9\u4e03\u9053\u6740\u7740\uff0c\u5e73\u65e5\u81f3\u5c11\u53ef\u4ee5\u6bc1\u53bb\u4e8c\u5341\u4e2a\u52b2\u654c\uff0c\u4f46\u800c\u4eca\u8fd9\u4e03\u9053\u6740\u7740\uff0c\u4e00\u9f50\u4f7f\u51fa\uff0c\u4e3a\u7684\u4e0d\u662f\u6740\u4eba\uff0c\u800c\u662f\u7559\u4eba\u3002 \u3000\u3000\u7559\u4f4f\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u7559\u5f97\u4f4f\u5417\uff1f \u3000\u3000\u5c0f\u767d\u95f7\u54fc\u4e00\u58f0\uff0c\u649e\u98de\u5341\u5c3a\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u62e6\u5728\u65b9\u90aa\u771f\u9762\u524d\u3002 \u3000\u3000\u4ed6\u7684\u5de6\u624b\u4f9d\u7136\u62e6\u4f38\uff0c\u53ef\u662f\u9f3b\u5b54\u5df2\u6e17\u51fa\u4e86\u4e24\u884c\u8840\u8ff9\uff0c\u5634\u89d2\u4e5f\u6709\u4e00\u884c\u8840\u4e1d\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5bf9\u4ed6\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u5c0f\u767d\u5782\u4e0b\u4e86\u5934\uff0c\u5ffd\u7136\uff0c\u4ed6\u53c8\u6df1\u6df1\u7684\u957f\u5438\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u7136\u540e\u7f13\u7f13\u7684\u628a\u6c14\u5410\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u53e3\u6c14\u5410\u51fa\u6765\u4e4b\u540e\uff0c\u4ed6\u7684\u773c\u795e\u50cf\u70c8\u706b\u4e00\u822c\u7684\u88ab\u70b9\u71c3\u8d77\u6765\uff0c\u633a\u8d77\u80f8\u819b\uff0c\u50cf\u4e00\u5ea7\u5c71\uff0c\u8138\u4e0a\u51fa\u73b0\u575a\u51b3\u65e0\u6bd4\u7684\u795e\u60c5\u3002 \u3000\u3000\u4ed6\u7684\u5de6\u624b\u4f38\u7740\uff0c\u4ecd\u62e6\u4f4f\u65b9\u90aa\u771f\u7684\u53bb\u8def\u3002 \u3000\u3000\u65b9\u90aa\u771f\u773c\u775b\u53d1\u4e86\u4eae\uff1a\u201c\u597d\uff0c\u5f88\u597d\u3002\u201d\u6b63\u8981\u62d4\u6b65\u524d\u884c\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u5ffd\u7136\u626c\u58f0\u9053\uff1a\u201c\u5c0f\u767d\uff0c\u9000\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u767d\u5411\u6c60\u65e5\u66ae\u62a5\u4ee5\u4e0d\u89e3\u7684\u773c\u5149\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u6d45\u53f9\u9053\uff1a\u201c\u7559\u4e0d\u4f4f\u7684\u3002\u201d \u3000\u3000\u5c0f\u767d\u5782\u4e0b\u4e86\u624b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fae\u5fae\u4e00\u7b11\u3002\u968f\u8ffd\u547d\u884c\u53bb\u3002 \u3000\u3000\u8ffd\u547d\u89c1\u65b9\u90aa\u771f\u4e0d\u518d\u51fa\u624b\uff0c\u8fd9\u624d\u653e\u4e86\u5fc3\u3002 \u3000\u3000\u4e24\u4eba\u884c\u51fa\u597d\u8fdc\uff0c\u5c06\u8fd1\u5230\u57ce\u95e8\uff0c\u8ffd\u547d\u624d\u95ee\uff1a\u201c\u4e3a\u751a\u4e48\u4e0d\u6295\u6548\u6c60\u65e5\u66ae\uff1f\u8fd9\u662f\u4e2a\u6700\u80fd\u5927\u5c55\u8eab\u624b\u4e4b\u5904\uff0c\u96be\u9053\u4f60\u60f3\u7a7a\u8d1f\u5927\u5fd7\u7684\u8fc7\u4e00\u8f88\u5b50\u5417\uff1f\u201d\u4ed6\u4eec\u4e00\u8def\u6765\u4e0a\u5929\u5165\u5730\u3002\u65e0\u6240\u4e0d\u8c08\uff0c\u4f46\u5c31\u662f\u6ca1\u6709\u518d\u8c08\u8d77\u521a\u624d\u8336\u5bee\u5b50\u91cc\u53d1\u751f\u7684\u4e8b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u76b1\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4e3a\u8fd9\u4e9b\u738b\u4faf\u516c\u5b50\u4e89\u540d\u593a\u5730\uff0c\u503c\u5f97\u5426\uff1f\u6211\u5c31\u7b97\u8981\u96c4\u56fe\u7ade\u80dc\uff0c\u4e5f\u8be5\u56fe\u5929\u4e0b\u4e4b\u529f\uff0c\u7acb\u81ea\u8eab\u4e4b\u4e1a\u3002\u201d \u3000\u3000\u8ffd\u547d\u542c\u53d6\uff0c\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u6709\u4f60\u7684\u60f3\u6cd5\uff0c\u6211\u4e0d\u52c9\u5f3a\u4f60\uff0c\u53ef\u662f\uff0c\u5728\u8fd9\u4e16\u95f4\uff0c\u60f3\u8981\u5f7b\u5e95\u7684\u81ea\u7acb\u95e8\u6237\uff0c\u4e0d\u4f9d\u508d\u4efb\u4f55\u4eba\uff0c\u8c08\u4f55\u5bb9\u6613\uff01\u201d \u3000\u3000\u201c\u5c31\u662f\u4e0d\u5bb9\u6613\uff0c\u6240\u4ee5\u624d\u6709\u8da3\u3002\u201d\u65b9\u90aa\u771f\u505c\u4e0b\u6b65\u6765\uff0c\u9053\uff1a\u201c\u4f60\u8981\u8fdb\u57ce\u4e86\uff1f\u201d \u3000\u3000\u8ffd\u547d\u4e5f\u671b\u5b9a\u4ed6\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u8be5\u5728\u6b64\u5730\u5206\u624b\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u6b64\u5730\u4e0d\u5206\u624b\uff0c\u4e5f\u603b\u6709\u5206\u624b\u7684\u65f6\u5019\uff0c\u4e0d\u5982\u5728\u6b64\u5730\u5206\u4e86\uff0c\u5e72\u51c0\u5229\u843d\u3002\u201d\u4ed6\u95ee\u65b9\u90aa\u771f\uff0c\u201c\u4f60\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6559\u4e66\u3002\u201d\u53cd\u95ee\uff0c\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u8ffd\u547d\u7b54\uff1a\u201c\u8859\u95e8\u3002\u201d\u8865\u4e86\u4e00\u53e5\uff1a\u201c\u4e0b\u6b21\u89c1\u9762\uff0c\u518d\u4e0e\u4f60\u75db\u996e\u4e09\u767e\u676f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u4e0d\u5e38\u559d\u9152\u3002\u201d\u4ed6\u8865\u5145\u4e00\u53e5\u8bf4\uff0c\u201c\u4f46\u4f60\u8bf7\uff0c\u6211\u4fbf\u559d\u3002\u201d \u3000\u3000\u8ffd\u547d\u773c\u4e2d\u5145\u6ee1\u4e86\u7b11\u610f\uff1a\u201c\u591a\u5c11\u90fd\u559d\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u773c\u4e2d\u4e5f\u6709\u7b11\u610f\uff1a\u201c\u591a\u5c11\u90fd\u559d\u3002\u201d \u3000\u3000\u8ffd\u547d\u9000\u540e\uff0c\u6325\u624b\uff1a\u201c\u522b\u5fd8\u4e86\u4f60\u6b20\u966a\u6211\u559d\u9152\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u9065\u58f0\u9053\uff1a\u201c\u522b\u5fd8\u4e86\u4f60\u6b20\u8bf7\u6211\u559d\u9152\u3002\u201d \u3000\u3000\u8ffd\u547d\u542b\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u6cbf\u897f\u6cb3\u8d70\u5230\u5927\u800c\u5c0f\u80e1\u540c\uff0c\u518d\u8f6c\u5165\u6485\u674e\u897f\u8857\uff0c\u4fbf\u662f\u718a\u5458\u5916\u7684\u5b85\u5b50\u3002\u718a\u5458\u5916\u539f\u672c\u662f\u4eac\u91cc\u7684\u540f\u90e8\u4e3b\u4e8b\uff0c\u800c\u4eca\u5e74\u7eaa\u5927\u4e86\uff0c\u8f9e\u5b98\u5f52\u6545\u91cc\uff0c\u5bb6\u91cc\u6709\u4e24\u4e2a\u5b69\u5b50\uff0c\u5206\u5916\u987d\u76ae\u597d\u6b66\uff0c\u603b\u627e\u4e0d\u5230\u597d\u8001\u5e08\u3002 \u3000\u3000\u718a\u5458\u5916\u5728\u5076\u7136\u7684\u673a\u9047\u4e0b\u89c1\u8fc7\u65b9\u90aa\u771f\uff0c\u4e00\u773c\u770b\u51fa\u4ed6\u662f\u4e2a\u5fd7\u6c14\u6e05\u5947\u3001\u5b66\u535a\u601d\u7cbe\u7684\u4eba\uff0c\u4e8e\u662f\u793c\u8058\u4ed6\u7ba1\u6559\u4e24\u4e2a\u5b69\u5b50\u3002\u8fd9\u4e24\u4e2a\u5b69\u5b50\uff0c\u5927\u7684\u53eb\u718a\u6587\u529f\uff0c\u5c0f\u7684\u53eb\u718a\u6b66\u5fb7\uff0c\u4e24\u4eba\u90fd\u88ab\u9a84\u7eb5\u60ef\u4e86\uff0c\u987d\u52a3\u5f02\u5e38\uff0c\u4ed7\u7740\u62a4\u9662\u6559\u4f1a\u7684\u51e0\u4e0b\u62f3\u811a\uff0c\u628a\u65b9\u90aa\u771f\u4e4b\u524d\u7684\u6559\u5e08\uff0c\u5168\u4e0d\u662f\u6c14\u8d70\uff0c\u4fbf\u662f\u6253\u8dd1\u4e86\u3002\u5012\u662f\u65b9\u90aa\u771f\u6765\u4e86\u4ee5\u540e\u3001\u628a\u4e00\u5bf9\u5c0f\u5b69\u5168\u6cbb\u5f97\u670d\u670d\u5e16\u5e16\uff0c\u718a\u5458\u5916\u5f53\u7136\u89c9\u5f97\u81ea\u5df1\u5e76\u672a\u770b\u8d70\u773c\uff0c\u5bf9\u65b9\u90aa\u771f\u81ea\u7136\u793c\u9047\u6709\u52a0\uff0c\u7136\u5219\u4ed6\u53ea\u77e5\u9053\u65b9\u90aa\u771f\u662f\u4e0d\u540c\u51e1\u54cd\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u4ed6\u5c82\u6b62\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u8fd9\u5929\uff0c\u65b9\u90aa\u771f\u50cf\u5f80\u5e38\u4e00\u6837\uff0c\u6263\u54cd\u4e86\u718a\u5bb6\u7684\u95e8\uff0c\u7ba1\u5bb6\u798f\u5934\u51fa\u6765\u5f20\u671b\uff0c\u4e00\u89c1\u662f\u65b9\u90aa\u771f\uff0c\u4fbf\u5ba2\u6c14\u53c8\u70ed\u70c8\u7684\u628a\u4ed6\u8fce\u8fdb\u4e86\u5385\u5802\uff0c\u4e00\u9762\u8bf7\u4ec6\u5f79\u4f20\u62a5\u718a\u5458\u5916\uff0c\u53e3\u91cc\u4e00\u53e0\u58f0\u4ed6\u8bf4\uff1a\u201c\u65b9\u592b\u5b50\uff0c\u4f60\u5750\u5750\uff0c\u4f60\u5148\u8bf7\u5750\u5750\uff0c\u6211\u5bb6\u8001\u7237\uff0c\u9a6c\u4e0a\u5c31\u6765\uff0c\u9a6c\u4e0a\u5c31\u6765\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u89c9\u5f97\u4eca\u5929\u718a\u5bb6\u4e0a\u4e0b\uff0c\u8ddf\u5e73\u5e38\u5927\u4e3a\u8fe5\u5f02\uff0c\u8be7\u9053\uff1a\u201c\u4eca\u5929\u4e24\u4f4d\u5c0f\u5c11\u7237\u4e0d\u5ff5\u4e66\u4e48\uff1f\u201d \u3000\u3000\u798f\u5934\u6447\u624b\u6446\u8111\u4ed6\u8bf4\uff1a\u201c\u554a\u554a\uff0c\u662f\u662f\u662f\uff0c\u4e0d\u662f\u4e0d\u662f\uff0c\u8fd9\u4e2a\u4e48\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u718a\u5458\u5916\u5306\u5306\u8e31\u4e86\u51fa\u6765\uff0c\u4e00\u89c1\u65b9\u90aa\u771f\uff0c\u5c31\u5806\u8d77\u7b11\u8138\uff0c\u201c\u957f\u63d6\u4e0d\u5df2\uff1a\u201c\u65b9\u5927\u4fa0\u6709\u602a\u83ab\u602a\uff0c\u8001\u673d\u76ee\u660f\u773c\u5eb8\uff0c\u4e0d\u8ba4\u8001\u54e5\u5a01\u540d\uff0c\u7adf\u6562\u8bf7\u5927\u4fa0\u5c48\u6b64\u7ba1\u6559\u5c0f\u72ac\uff0c\u5b9e\u5728\u662f\u2026\u2026\u8bf7\u6d77\u6db5\u539f\u5ba5\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u6014\uff1a\u201c\u4e1c\u7fc1\uff0c\u4f60\u8fd9\u8bdd\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u718a\u5458\u5916\u53ea\u662f\u4e00\u5473\u8d54\u7b11\uff1a\u201c\u6ca1\u6709\u610f\u601d\uff0c\u8001\u673d\u600e\u6562\u6709\u522b\u7684\u610f\u601d\uff0c\u53ea\u662f\u4ee4\u4fa0\u58eb\u59d4\u5c48\u4e86\u8fd9\u4e48\u6bb5\u65e5\u5b50\uff0c\u5b9e\u5728\u662f\u660f\u6627\u65e0\u8bc6\u4e4b\u81f3\uff0c\u8fd9\u513f\u662f\u2026\u2026\u201d\u4ed6\u53eb\u5c0f\u53ae\u539f\u672c\u51c6\u5907\u597d\u7684\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u201c\u4e00\u70b9\u5c0f\u5c0f\u610f\u601d\uff0c\u8bf7\u5148\u751f\u2026\u2026\u4e07\u8bf7\u65b9\u5927\u4fa0\u8d4f\u8138\u6536\u4e0b\u3002\u201d\u4fbf\u8981\u5c0f\u53ae\u628a\u94f6\u76d8\u5949\u5230\u65b9\u90aa\u771f\u9762\u524d\uff0c\u529b\u4fc3\u65b9\u90aa\u771f\u6536\u4e0b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fc3\u91cc\u5df2\u660e\u767d\u4e86\u51e0\u5206\u3002\u4ed6\u5728\u718a\u5e9c\u4efb\u6559\uff0c\u6da6\u916c\u5df2\u7b97\u539a\u5f85\uff0c\u6bcf\u5e74\u4e0d\u8fc7\u7ea6\u83ab\u4e09\u5341\u4e24\uff0c\u718a\u5458\u5916\u8fd9\u4e00\u8bb0\u5927\u624b\u7b14\uff0c\u81ea\u7136\u662f\u522b\u6709\u5185\u60c5\uff0c\u5f53\u4e0b\u4fbf\u9053\uff1a\u201c\u4e1c\u7fc1\uff0c\u6562\u60c5\u662f\u5728\u4e0b\u624d\u6d45\u8bc6\u8584\uff0c\u4f60\u8981\u8f9e\u9000\u5728\u4e0b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u718a\u5458\u5916\u6025\u5f97\u5e72\u62b9\u6c57\uff1a\u201c\u65b9\u4fa0\u58eb\uff0c\u4f60\u5343\u4e07\u522b\u8fd9\u822c\u8bf4\uff0c\u8001\u673d\u4ee5\u524d\u662f\u4e0d\u77e5\u4e4b\u7f6a\uff0c\u73b0\u5728\u5df2\u8bc6\u771f\u8eab\uff0c\u600e\u803d\u5f97\u8d77\u4f60\u7684\u524d\u7a0b\u2026\u2026\u65b9\u5927\u4fa0\uff0c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u8001\u673d\u600e\u6562\u8ddf\u6c60\u5bb6\u7684\u4eba\u76f8\u4e89\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u8fd9\u4e00\u542c\uff0c\u5df2\u628a\u4f4f\u4e86\u5e95\u8574\uff0c\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u6211\u51b3\u65e0\u610f\u8981\u8fc7\u6c60\u5bb6\uff0c\u4e1c\u7fc1\u53ef\u4ee5\u514d\u8651\u3002\u201d \u3000\u3000\u718a\u5458\u5916\u4e00\u542c\u66f4\u6025\uff0c\u53ea\u8f6f\u58f0\u6328\u6c14\u4ed6\u8bf4\uff1a\u201c\u8fd9\u53ef\u4e07\u4e07\u4e0d\u884c\u3002\u6c60\u4e8c\u516c\u5b50\u662f\u4eba\u4e2d\u9f99\u51e4\uff0c\u53c8\u662f\u6d1b\u9633\u9996\u5bcc\uff0c\u6700\u8fd1\u7687\u4e0a\u6b63\u62df\u8d50\u5c01\u2018\u6d1b\u9633\u738b\u2019\uff0c\u770b\u6765\u6c60\u516c\u5b50\u591a\u534a\u5b9e\u81f3\u540d\u5f52\uff0c\u6c60\u516c\u5b50\u8d4f\u91cd\u7684\u4eba\uff0c\u8001\u673d\u5929\u5927\u7684\u80c6\uff0c\u4e5f\u4e0d\u6562\u6cbe\uff0c\u8fd9\u4e07\u4e07\u4f7f\u4e0d\u5f97\u4e5f\uff0c\u53ea\u8bf7\u65b9\u5927\u4fa0\u80f8\u6000\u5927\u91cf\uff0c\u52ff\u8bb0\u65e7\u8fc7\uff0c\u5728\u6c60\u516c\u5b50\u524d\u591a\u7f8e\u8a00\u51e0\u53e5\uff0c\u4e0d\u4f7f\u8001\u673d\u4e3a\u96be\uff0c\u5df2\u7ecf\u611f\u6069\u6234\u5fb7 \u3000\u3000\u65b9\u90aa\u771f\u5e76\u6ca1\u6709\u6536\u4e0b\u718a\u5458\u5916\u7684\u94f6\u5b50\uff0c\u4fbf\u65ad\u7136\u79bb\u5f00\u4e86\u718a\u5b85\uff0c\u4e00\u8def\u4e0a\uff0c\u89c9\u5f97\u5f88\u6709\u4e9b\u618b\u6c14\uff0c\u4fbf\u5230\u201c\u4f9d\u4f9d\u697c\u201d\u53bb\u3002 \u3000\u3000\u201c\u4f9d\u4f9d\u697c\u201d\u662f\u57ce\u91cc\u6700\u51fa\u540d\u7684\u4e00\u5bb6\u9752\u697c\u3002 \u3000\u3000\u8001\u9e28\u4e00\u89c1\u5230\u4ed6\uff0c\u5c31\u77e5\u9053\u4ed6\u662f\u6765\u627e\u60dc\u60dc\u7684\uff0c\u4e8e\u662f\u8d54\u7740\u7b11\u8138\u5f15\u65b9\u90aa\u771f\u4e0a\u697c\u53bb\u89c1\u60dc\u60dc\u3002\u7531\u4e8e\u65b9\u90aa\u771f\u4e00\u5411\u5e76\u4e0d\u9614\u7ef0\uff0c\u4e5f\u4e0d\u7b97\u592a\u8fc7\u5bd2\u4f27\uff0c\u800c\u60dc\u60dc\u4e00\u5411\u5bf9\u4ed6\u53c8\u72ec\u5177\u6167\u773c\uff0c\u8001\u9e28\u548c\u697c\u5b50\u91cc\u7684\u4eba\uff0c\u5bf9\u65b9\u90aa\u771f\u65e2\u4e0d\u70ed\u70c8\uff0c\u4e5f\u4e0d\u51b7\u843d\u3002 \u3000\u3000\u5012\u662f\u8fd9\u4e9b\u9752\u697c\u5973\u5b50\uff0c\u5927\u90fd\u503e\u5fc3\u4e8e\u65b9\u90aa\u771f\u7684\u6f47\u6d12\u3001\u4fca\u4fcf\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u4e0d\u627e\u522b\u4eba\uff0c\u53ea\u627e\u60dc\u60dc\u3002 \u3000\u3000\u522b\u7684\u5973\u5b50\u77e5\u9053\u60dc\u60dc\u8ddf\u65b9\u90aa\u771f\u7684\u5173\u7cfb\uff0c\u4e5f\u4e0d\u4ece\u4e2d\u641e\u6270\u2014\u2014\u800c\u4e14\u5c31\u7b97\u8981\u641e\u6270\uff0c\u4e5f\u641e\u6270\u4e0d\u4e86\u3002 \u3000\u3000\u60dc\u60dc\u662f\u201c\u4f9d\u4f9d\u697c\u201d\u91cc\u6700\u51fa\u8272\u7684\u5973\u5b50\u3002 \u3000\u3000\u636e\u8bf4\u201c\u8001\u516c\u5b50\u201d\u56de\u767e\u5e94\u66fe\u60f3\u4ee5\u534a\u5ea7\u57ce\u6765\u83b7\u60dc\u60dc\u9752\u7750\uff0c\u60dc\u60dc\u6839\u672c\u5c31\u4e0d\u52a8\u5fc3\uff1b\u5362\u4f8d\u90ce\u66fe\u7528\u5341\u4e8c\u8f66\u7684\u73cd\u73e0\u7470\u5b9d\u6765\u8981\u5979\u4e0b\u5ac1\uff0c\u60dc\u60dc\u4e5f\u770b\u4e0d\u4e0a\u773c\u3002 \u3000\u3000\u5979\u5c31\u53ea\u5bf9\u5e76\u4e0d\u5f97\u610f\u7684\u65b9\u90aa\u771f\u53e6\u773c\u76f8\u770b\u3002 \u3000\u3000\u8fd9\u5929\u65b9\u90aa\u771f\u4e0a\u5f97\u697c\u5b50\u6765\uff0c\u60dc\u60dc\u8fce\u4ed6\u5165\u201c\u79cb\u8749\u8f69\u201d\uff0c\u65b9\u90aa\u771f\u4fbf\u5f00\u59cb\u559d\u9152\u3002 \u3000\u3000\u60dc\u60dc\u4e00\u773c\u4fbf\u770b\u51fa\u4ed6\u4e0d\u5feb\u4e50\u548c\u4ed6\u7684\u4e0d\u5feb\u4e50\u3002 \u3000\u3000\u60dc\u60dc\u4fbf\u60f3\u9017\u4ed6\u5feb\u4e50\u8d77\u6765\u3002 \u3000\u3000\u5979\u5f39\u7435\u7436\u3001\u5531\u6b4c\u3001\u8fd8\u628a\u4eb2\u624b\u505a\u7684\u7cd5\u997c\u9001\u5230\u65b9\u90aa\u771f\u7684\u5634\u91cc\u3002 \u3000\u3000\u5979\u770b\u5f97\u51fa\u6765\u65b9\u90aa\u771f\u662f\u5e94\u916c\u7740\u5403\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u5979\u5f88\u5feb\u7684\u4fbf\u77e5\u9053\u81ea\u5df1\u4eca\u5929\u662f\u6cbb\u4e0d\u597d\u65b9\u90aa\u771f\u4eca\u5929\u8fd9\u4e2a\u4e0d\u5feb\u4e50\u7684\u75c5\u3002 \u3000\u3000\u4ee5\u5f80\uff0c\u65b9\u90aa\u771f\u4e5f\u5e38\u5e38\u5e26\u70b9\u5fae\u6101\u6765\u8fd9\u91cc\uff0c\u53ef\u662f\u60dc\u60dc\u603b\u662f\u80fd\u4f7f\u4ed6\u5f00\u5fc3\u8d77\u6765\uff0c\u9664\u4e86\u4e00\u4ef6\u4e8b\uff0c\u60dc\u60dc\u77e5\u9053\u81ea\u5df1\u662f\u6cbb\u6108\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u4e8e\u662f\u5979\u95ee\uff1a\u201c\u53c8\u60f3\u5979\u4e86\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e3e\u676f\u7684\u624b\u4e00\u9707\uff0c\u4f46\u4ecd\u4ef0\u7740\u8116\u5b50\uff0c\u628a\u9152\u5e72\u5b8c\uff0c\u7528\u624b\u62b9\u4e86\u62b9\u5507\u89d2\u3002 \u3000\u3000\u5979\u51dd\u7738\u7740\u4ed6\uff1a\u201c\u4f60\u51e0\u65f6\u624d\u80fd\u5fd8\u4e86\u5979\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u60d8\u7136\u4e00\u7b11\uff0c\u53c8\u53bb\u659f\u9152\uff0c\u9152\u6e85\u51fa\u4e86\u4e9b\u5fae\uff0c\u5728\u676f\u6cbf\u5916\u3002 \u3000\u3000\u60dc\u60dc\u628a\u9152\u58f6\u62ff\u4e86\u8fc7\u6765\uff0c\u66ff\u4ed6\u5012\u9152\uff0c\u7528\u67d4\u5f97\u50cf\u5fae\u98ce\u4f3c\u7684\u3001\u6d41\u6c34\u4f3c\u7684\u58f0\u97f3\u5e7d\u5e7d\u5730\u95ee\uff1a\u201c\u4f60\u51e0\u65f6\u624d\u53ea\u6709\u6211\uff0c\u6ca1\u6709\u5979\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6447\u9996\uff0c\u5fc3\u5934\u5ffd\u751f\u4e00\u80a1\u601c\u60dc\u4e4b\u610f\uff0c\u7528\u624b\u638c\u8f7b\u67d4\u7684\u642d\u7740\u60dc\u60dc\u7684\u624b\u80cc\uff0c\u6e29\u548c\u5730\u9053\uff1a\u201c\u4e0d\u662f\u5979\uff0c\u4e0d\u662f\u60f3\u5979\u3002\u201d \u3000\u3000\u60dc\u60dc\u5012\u6709\u4e9b\u8bb6\u7136\u8d77\u6765\uff0c\u51dd\u7740\u7f8e\u76ee\uff0c\u659c\u659c\u7684\u7785\u7740\u4ed6\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5ffd\u6df1\u6df1\u5730\u95ee\uff1a\u201c\u6211\u8fd9\u822c\u6f66\u5012\uff0c\u8fd9\u822c\u843d\u9b44\uff0c\u4f60\u8ddf\u7740\u6211\uff0c\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u60dc\u60dc\u7b11\u4e86\u3002 \u3000\u3000\u5979\u7b11\u5f97\u8273\u8273\u7684\u3002 \u3000\u3000\u8c01\u770b\u4e86\u5979\u7684\u8273\uff0c\u662f\u7537\u4eba\u5fc3\u91cc\u90fd\u4f1a\u52a8\u3002 \u3000\u3000\u201c\u6211\u662f\u51a4\u9b3c\uff0c\u6211\u9009\u4e0a\u4f60\u4e86\u3002\u201d\u60dc\u60dc\u7528\u7ea4\u957f\u7684\u624b\u6307\u5728\u4ed6\u7709\u6bdb\u4e0a\u62b9\u4e86\u62b9\uff0c\u8bf4\uff1a\u3002\u2018\u6211\u559c\u6b22\u8fd9\u4e2a\u3002\u201d\u53c8\u7528\u624b\u6307\u629a\u4e86\u629a\u4ed6\u7684\u773c\u775b\uff0c\u73cd\u60dc\u4ed6\u8bf4\uff1a\u3001\u2018\u6211\u559c\u6b22\u8fd9\u4e2a\u3002\u201d\u518d\u7528\u624b\u6307\u62c8\u4e86\u62c8\u4ed6\u7684\u9f3b\u5b50\uff1a\u201c\u6211\u559c\u6b22\u8fd9\u4e2a\u3002\u201d\u6700\u540e\u7528\u624b\u63cf\u4e86\u63cf\u4ed6\u7684\u5634\u5507\uff0c\u201c\u6211\u559c\u6b22\u8fd9\u4e2a\u3002\u201d\u5979\u8bf4\u4e00\u6b21\uff0c\u773c\u91cc\u7684\u542b\u60c5\u53c8\u6df1\u4e86\u4e00\u4e9b\uff0c\u8bf4\u4e00\u53e5\uff0c\u66f4\u60c5\u52a8\u4e00\u4e9b\u3002\u201c\u5c31\u8fd9\u51e0\u4e2a\u597d\u5904\u3002\u201d\u8bf4\u7f62\u62bf\u5634\u4e00\u7b11\u3002 \u3000\u3000\u65b9\u90aa\u771f\u89c1\u5979\u8273\u5bb9\u7edd\u8272\uff0c\u5410\u6c14\u82e5\u5170\uff0c\u5fc3\u91cc\u4e5f\u4e00\u9635\u5fc3\u52a8\uff0c\u629a\u4e86\u629a\u5979\u7684\u53d1\u9b13\uff0c\u53d1\u89c9\u5979\u4e56\u9a6f\u5f97\u5c31\u50cf\u732b\u513f\uff1a\u201c\u5176\u5b9e\uff0c\u8ddf\u6211\u6ca1\u4ec0\u4e48\u597d\u5904\u7684\uff0c\u771f\u7684\u3002\u201d \u3000\u3000\u60dc\u60dc\u7cbe\u7075\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u5c31\u50cf\u5c0f\u5973\u5b69\u5b50\u5728\u542c\u5927\u4eba\u8bb2\u6545\u4e8b\uff0c\u4f46\u7b11\u5f97\u6709\u70b9\u75f4\uff0c\u4e5f\u6709\u70b9\u72e1\u733e\uff1a \u3000\u3000\u201c\u597d\uff0c\u4f60\u544a\u8bc9\u6211\uff0c\u4f60\u6700\u6709\u672c\u9886\uff0c\u4e0d\u8ddf\u4f60\uff0c\u6211\u8ddf\u8c01\u53bb\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u772f\u772f\u5730\u7b11\u4e86\uff1a\u201c\u8ddf\u5362\u4f8d\u90ce\uff0c\u6709\u4eab\u4e0d\u5c3d\u7684\u8363\u534e\u5bcc\u8d35\uff1b\u8ddf\u56de\u516c\u5b50\uff0c\u4e5f\u6709\u9526\u8863\u7389\u98df\uff0c\u8fd8\u6709\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u554a\uff0c\u4f60\u771f\u8981\u8bef\u4e86\u6211\u7684\u7ec8\u8eab\u54c7\u3002\u201d\u60dc\u501f\u72e1\u9ee0\u4ed6\u8bf4\uff0c\u201c\u4ed6\u4eec\u90a3\u4e48\u597d\uff0c\u4f60\u81ea\u5df1\u53c8\u4e0d\u5ac1\u53bb\uff1f \u3000\u3000\u5362\u4f8d\u90ce\u5e74\u7eaa\u505a\u5f97\u4e86\u6211\u516c\u516c\uff0c\u6ca1\u5ac1\u8fc7\u53bb\uff0c\u5f53\u7136\u8bb8\u4e0b\u5bcc\u8d35\u5343\u91d1\uff0c\u4e00\u65e6\u59d4\u8eab\u4e8e\u4eba\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c\u5355\u8ddf\u4ed6\u5341\u56db\u4e2a\u59e8\u5976\u5976\u6253\u4ea4\u9053\uff0c\u90a3\u5c31\u70e6\u6b7b\u4e86\uff1b\u56de\u516c\u5b50\u662f\u6d1b\u9633\u56db\u516c\u5b50\u91cc\u5e74\u7eaa\u6700\u5927\u7684\u4e00\u4e2a\uff0c\u6837\u5b50\u4e5f\u6700\u60f9\u4eba\u538c\uff0c\u4eba\u4eba\u80cc\u5730\u91cc\u90fd\u53eb\u4ed6\u2018\u6bd2\u624b\u516c\u5b50\u2019\uff0c\u4f60\u9ed1\u4e0d\u9ed1\u5fc3\uff0c\u8981\u6025\u7740\u903c\u6211\u5ac1\u7ed9\u4e2a\u8fa3\u624b\u90ce\u541b\uff0c\u54fc\u54fc\uff0c\u4ed6\u4eec\u771f\u5982\u5343\u4f9d\u767e\u987a\uff0c\u53c8\u534e\u8863\u53c8\u7f8e\u98df\u7684\uff0c\u8fd8\u6709\u8001\u5988\u5b50\u4f9b\u6211\u5dee\u9063\uff0c\u6211\u4e0d\u5ac1\u4e48\uff1f\u4f60\u8bf4\u7684\u90a3\u4e48\u597d\uff0c\u8981\u662f\u8ba8\u538c\u89c1\u5230\u6211\uff0c\u65b9\u516c\u5b50\u5c31\u4e0d\u5fc5\u52b3\u9a7e\u2018\u4f9d\u4f9d\u697c\u2019\uff0c\u5e38\u6765\u7737\u987e\u6211\u8fd9\u82e6\u547d\u5973\u5b50\u2026\u2026\u201d\u8bf4\u7740\u8bf4\u7740\uff0c\u5012\u662f\u5f53\u771f\u773c\u5708\u513f\u7ea2\u4e86\u8d77\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fd9\u4e0d\u8fed\u5730\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u6211\u8fd9\u662f\u81ea\u60ed\u8d2b\u5bd2\uff0c\u4e0d\u60f3\u7275\u7d2f\u4f60\u5440\u3002\u201d \u3000\u3000\u60dc\u60dc\u7834\u6d95\u4e3a\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e5f\u662f\u6709\u611f\u8eab\u4e16\uff0c\u6b63\u6101\u73b7\u8fb1\u4f60\u554a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u9053\uff1a\u201c\u8bf4\u771f\u7684\uff0c\u4f60\u60f3\u4e0d\u60f3\u6211\u6709\u529f\u540d\u5bcc\u8d35\uff1f\u201d \u3000\u3000\u60dc\u60dc\u9053\uff1a\u201c\u8bf4\u771f\u7684\uff0c\u4f60\u8c08\u4e0d\u4e0a\u751a\u4e48\u529f\u540d\u5bcc\u8d35\uff0c\u54b1\u4eec\u4e5f\u76f8\u4ea4\u4e86\u4e09\u5e74\u4e86\uff0c\u529f\u540d\u5bcc\u8d35\uff0c\u4e0d\u662f\u6211\u60f3\u4e0d\u60f3\uff0c\u800c\u662f\u770b\u4f60\u8981\u4e0d\u8981\u2026\u2026\u201d \u3000\u3000\u5ffd\u60f3\u8d77\u4e00\u4e8b\uff0c\u8273\u8273\u5730\u7b11\u9053\uff1a\u201c\u8bf4\u5230\u60f3\u5230\uff0c\u4eca\u5929\u597d\u597d\u51e0\u4e2a\u5b98\u7237\u4eec\u5230\u8fd9\u513f\u627e\u4f60\uff0c\u8fd8\u627e\u4e0a\u6211\u6253\u542c\u4f60\u7684\u4e8b\u513f\uff0c\u5176\u4e2d\u8fd8\u6709\u6c60\u516c\u5b50\u624b\u4e0a\u7684\u8bf8\u845b\u4eae\u5218\u5148\u751f\u5462\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u542c\uff0c\u8138\u8272\u5c31\u53d8\u4e86\u3002\u697c\u4e0b\u7684\u9e28\u6bcd\u6b63\u597d\u76f4\u7740\u55d3\u95e8\u559c\u6c14\u6d0b\u6d0b\u5730\u53eb\u9053\uff1a\u201c\u60dc\u60dc\uff0c\u60dc\u60dc\uff0c\u5feb\u8bf7\u65b9\u516c\u5b50\u79fb\u6b65\u51fa\u6765\uff0c\u6709\u5927\u8d35\u4eba\u8981\u89c1\u4ed6\u54e9\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u731b\u659f\u4e00\u676f\u9152\uff0c\u4ef0\u8116\u5b50\u5c31\u5012\u5165\u809a\u91cc\uff0c\u9152\u58f6\u5728\u684c\u4e0a\u4e00\u653e\uff0c\u201c\u4e52\u201d\u7684\u4e00\u58f0\uff0c\u7136\u540e\u5c31\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u60dc\u60dc\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u5979\u5f88\u5c11\u89c1\u8fc7\u4ed6\u53d1\u8fd9\u6837\u5927\u7684\u813e\u6c14\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5f20\u624b\u6253\u5f00\u4e86\u8f69\u95e8\u3002 \u3000\u3000\u9e28\u6bcd\u548c\u5c0f\u53ae\u6b63\u52ff\u52ff\u5f15\u51e0\u4eba\u4e0a\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8ddf\u6b63\u4e0a\u697c\u7684\u4eba\u731b\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c\u8d70\u5728\u6700\u524d\u9762\u7684\u4eba\u6b63\u662f\u5218\u662f\u4e4b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4f60\u4eec\u6765\u505a\u751a\u4e48\uff1f\u201d \u3000 \u7b2c\u4e94\u7ae0\u3000\u4f9d\u4f9d\u697c\u4e0a\u4e00\u60dc\u60dc \u3000 \u3000\u3000\u5218\u662f\u4e4b\u9a6c\u4e0a\u505c\u4e86\u4e0b\u6765\u3002\u4ed6\u6bd4\u65b9\u90aa\u771f\u77ee\u4e86\u51e0\u4e2a\u9636\u7ea7\uff0c\u4f46\u7b11\u6001\u4f9d\u7136\u3002 \u3000\u3000\u201c\u5728\u4e0b\u5949\u516c\u5b50\u4e4b\u547d\uff0c\u7279\u7ed9\u5c11\u4fa0\u9001\u793c\u6765\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u51b7\u7b11\u9053\uff1a\u201c\u751a\u4e48\u793c\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u4f3c\u6ca1\u542c\u51fa\u65b9\u90aa\u771f\u51b7\u8bee\u4e4b\u610f\uff0c\u53ea\u5411\u540e\u9762\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u6765\u5440\u3002\u201d\u767b\u65f6\u4e03\u516b\u540d\u8ddf\u73ed\u62ac\u7740\u7bb1\u5b50\u9c7c\u8d2f\u800c\u5165\uff0c\u5f15\u8d77\u697c\u5b50\u91cc\u4e0d\u5c11\u4eba\u5f15\u76ee\u6ce8\u89c6\u3002 \u3000\u3000\u5218\u662f\u4e4b\u5429\u5490\u9053\uff1a\u201c\u957f\u5bff\uff0c\u5f00\u7bb1\u3002\u201d \u3000\u3000\u7bb1\u5b50\u4e00\u5f00\uff0c\u8000\u773c\u751f\u82b1\u7684\u5c3d\u662f\u94f6\u5b50\u3002 \u3000\u3000\u5218\u662f\u4e4b\u8d8b\u8fd1\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u54b1\u5bb6\u516c\u5b50\u5bf9\u5c11\u4fa0\u7684\u4e00\u70b9\u5fc3\u610f\uff0c\u5176\u4f59\u516d\u7bb1\uff0c\u82e5\u8bba\u4ef7\u503c\uff0c\u7edd\u5bf9\u53ea\u5728\u8fd9\u7bb1\u4e4b\u4e0a\uff0c\u4e0d\u77e5\u5c11\u4fa0\u8981\u4e0d\u8981\u9a8c\u660e\uff1f\u201d \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u201c\u4f9d\u4f9d\u697c\u201d\u4eba\u4eba\u90fd\u8d5e\u7fa1\u7684\u7eb7\u7eb7\u79c1\u8bed\u7740\uff0c\u5c24\u5176\u8001\u9e28\u66f4\u7709\u82b1\u773c\u7b11\uff0c\u4e00\u5473\u4ed6\u8bf4\uff1a\u201c\u65b9\u516c\u5b50\u771f\u662f\u6709\u672c\u4e8b\uff0c\u80fd\u5f97\u6c60\u516c\u5b50\u8fd9\u822c\u5668\u91cd\uff0c\u6211\u4e00\u65e9\u5c31\u8bf4\u8fc7\uff0c\u65b9\u516c\u5b50\u5929\u751f\u8d35\u683c\uff0c\u9e64\u7acb\u9e21\u7fa4\uff0c\u51c6\u662f\u4e2a\u5927\u5bcc\u5927\u8d35\u7684\u4eba\uff01\u201d\u5176\u5b9e\uff0c\u5728\u4ed6\u4eec\u5fc3\u91cc\u7565\u4e00\u4f30\u8ba1\uff0c\u5149\u51ed\u8fd9\u51e0\u53e3\u5927\u7bb1\u5b50\uff0c\u8db3\u4ee5\u4f7f\u65b9\u90aa\u771f\u6210\u4e3a\u6d1b\u9633\u57ce\u91cc\u7684\u4e00\u4e2a\u4e2d\u5bcc\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u662f\u51e0\u751f\u4fee\u6765\u7684\u8fd0\uff0c\u5fc3\u91cc\u867d\u662f\u53c8\u5ac9\u53c8\u6068\uff0c\u4f46\u5634\u91cc\u5fd9\u4e0d\u8fed\u5148\u884c\u5949\u8fce\u5df4\u7ed3\u3002 \u3000\u3000\u60dc\u60dc\u53ea\u5728\u680f\u6746\u4e0a\u9065\u9065\u7684\u770b\uff0c\u4e0d\u77e5\u5728\u60f3\u4e9b\u751a\u4e48\u3002 \u3000\u3000\u5218\u662f\u4e4b\u89c2\u5f62\u5bdf\u52bf\uff0c\u6545\u610f\u5927\u58f0\u9053\uff1a\u201c\u82e5\u5c11\u4fa0\u80af\u63a5\u53d7\u5c11\u4e3b\u4eba\u7684\u793c\u8058\uff0c\u53ef\u968f\u5c11\u4fa0\u5f00\u4ef7\uff0c\u81f3\u4e8e\u5728\u5e9c\u4e2d\u53f8\u804c\uff0c\u4ea6\u4efb\u5c11\u4fa0\u81ea\u9009\u3002\u201d \u3000\u3000\u56f4\u89c2\u7684\u4eba\u90fd\u54d7\u7136\u51fa\u58f0\u3002\u5170\u4ead\u6c60\u5bb6\u8d22\u5b8f\u52bf\u5927\uff0c\u636e\u8bf4\u671d\u5ef7\u8981\u5c01\u8d50\u201c\u6d1b\u9633\u738b\u201d\u4e88\u6c60\u65e5\u66ae\uff0c\u8fd9\u4e00\u6765\uff0c\u65b9\u90aa\u771f\u5c31\u6210\u4e86\u57ce\u91cc\u7684\u5927\u7ea2\u4eba\u4e86\u3002\u9e28\u6bcd\u559c\u5f97\u4e09\u811a\u4e24\u6b65\u5730\u722c\u4e0a\u4e86\u697c\uff0c\u626f\u7740\u60dc\u60dc\u7684\u8863\u8896\u4e00\u5473\u9053\u559c\u3002 \u3000\u3000\u60dc\u60dc\u4e5f\u6ca1\u6b22\u559c\uff0c\u4e5f\u6ca1\u4e0d\u6b22\u559c\uff0c\u53ea\u8fdc\u8fdc\u7684\u770b\u5411\u767d\u8863\u5982\u96ea\u65b9\u90aa\u771f\u3002 \u3000\u3000\u5218\u662f\u4e4b\u6717\u58f0\u9053\uff1a\u201c\u6c60\u516c\u5b50\u8bf4\uff0c\u65b9\u5c11\u4fa0\u8981\u6c42\u7684\uff0c\u65e0\u4e0d\u76f8\u5141\uff0c\u5c31\u7b97\u8981\u4e70\u4e0b\u8fd9\u5ea7\u2018\u4f9d\u4f9d\u697c\u2019\uff0c\u4e5f\u53ef\u4ee5\u9a6c\u4e0a\u5151\u73b0\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8c22\u8c22\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u8138\u4e0a\u51fa\u73b0\u6b23\u7136\u7684\u795e\u8272\uff1a\u201c\u65b9\u5c11\u4fa0\u4e07\u52ff\u5ba2\u6c14\uff0c\u54b1\u4eec\u662f\u81ea\u5bb6\u4eba\u4e86\u2014\u2014\u201d \u3000\u3000\u65b9\u90aa\u771f\u622a\u65ad\u9053\uff1a\u201c\u6211\u662f\u6211\uff0c\u4f60\u662f\u4f60\uff0c\u6211\u4eec\u4e0d\u662f\u81ea\u5bb6\u4eba\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u5f3a\u7b11\u9053\uff1a\u201c\u65b9\u5c11\u4fa0\u4e0d\u59a8\u591a\u8003\u8651\u4e00\u4e0b\uff0c\u65e0\u987b\u9a6c\u4e0a\u4f5c\u590d\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u65e0\u9700\u8003\u8651\u3002\u628a\u7bb1\u5b50\u9000\u56de\u53bb\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u4e00\u65f6\u7b11\u4e0d\u51fa\u6765\u4e86\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u5b57\u4e00\u53e5\u5730\u9053\uff1a\u201c\u7bb1\u5b50\u9000\u56de\uff0c\u4eba\u4e5f\u56de\u53bb\uff01\u201d \u3000\u3000\u5218\u662f\u4e4b\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u53c8\u4f55\u5fc5\u5462\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u7684\u624b\u642d\u5728\u5251\u67c4\u4e0a\uff0c\u76ee\u5149\u5bd2\u4f3c\u51b0\u5c01\uff1a\u201c\u4f60\u8d70\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u770b\u770b\u4ed6\uff0c\u53c8\u770b\u770b\u4ed6\u7684\u5251\uff0c\u5ffd\u7136\u772f\u8d77\u773c\u6765\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u4e00\u8dfa\u8db3\uff0c\u8fd4\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u4e00\u884c\u4eba\uff0c\u8fde\u7740\u76db\u6ee1\u91d1\u94f6\u73e0\u5b9d\u7684\u7bb1\u5b50\uff0c\u5728\u4e00\u8f6c\u773c\u5168\u64a4\u8d70\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5728\u4f17\u4eba\u89c6\u4f5c\u9b3c\u602a\u7684\u819b\u76ee\u4e2d\u56de\u8f69\u3002 \u3000\u3000\u4ed6\u5750\u4e0b\uff0c\u5012\u9152\u3002 \u3000\u3000\u60dc\u60dc\u63a8\u95e8\u8fdb\u6765\uff0c\u7136\u540e\u80cc\u5411\u5173\u4e86\u7684\u4e24\u6247\u95e8\uff0c\u7565\u6014\u5fe1\u4e86\u9877\u523b\uff0c\u5373\u8fc7\u6765\uff0c\u66ff\u65b9\u90aa\u771f\u5012\u9152\uff0c\u6ca1\u6709\u591a\u8bf4\u534a\u53e5\u8bdd\uff0c\u4e5f\u6ca1\u6709\u591a\u95ee\u534a\u4e2a\u5b57\u3002 \u3000\u3000\u9694\u4e86\u534a\u664c\uff0c\u65b9\u90aa\u771f\u7a81\u7136\u95ee\u9053\uff1a\u201c\u4f60\u6c14\u82e6\u4e86\uff1f\u201d \u3000\u3000\u60dc\u60dc\u95ea\u7740\u6676\u4eae\u7684\u7738\u5b50\uff1a\u201c\u6211\u6c14\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u89c2\u5bdf\u4f3c\u7684\u770b\u7740\u5979\uff1a\u201c\u4f60\u89c9\u5f97\u6211\u50cf\u4e2a\u75af\u5b50\uff0c\u8fd8\u662f\u50cf\u4e2a\u50bb\u5b50\uff1f\u201d \u3000\u3000\u60dc\u60dc\u8fd9\u6b21\u7528\u624b\u642d\u4f4f\u65b9\u90aa\u771f\u7684\u624b\u80cc\uff0c\u8f7b\u8f7b\u629a\u638c\u7740\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u4ee5\u524d\u53ea\u77e5\u9053\u4f60\u662f\u4e2a\u5f88\u6709\u672c\u9886\u7684\u4eba\uff0c\u73b0\u5728\uff0c\u6211\u66f4\u77e5\u9053\u6211\u6ca1\u6709\u770b\u9519\uff1b\u4e00\u4e2a\u771f\u6b63\u6709\u672c\u9886\u7684\u4eba\uff0c\u5f53\u7136\u4e0d\u4f1a\u505a\u4ed6\u4e0d\u60f3\u505a\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u4e86\u3002 \u3000\u3000\u7b11\u610f\u91cc\u6092\u8272\u66f4\u6d53\u3002 \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u501f\u60dc\uff0c\u4f60\u53bb\u5f39\u4e00\u66f2\u2018\u9ad8\u5c71\u6d41\u6c34\u2019\uff0c\u53ef\u597d\uff1f\u201d \u3000\u3000\u60dc\u60dc\u76c8\u76c8\u5730\u5411\u7434\u53f0\u8d70\u53bb\uff0c\u867d\u7136\uff0c\u5728\u5979\u5fc3\u91cc\uff0c\u4e5f\u8bb8\u5e76\u4e0d\u660e\u767d\u65b9\u90aa\u771f\u4e3a\u4f55\u4e0d\u63a5\u53d7\u793c\u8058\u3001\u62d2\u7edd\u8d4f\u8d50\uff1b\u5728\u5979\u6df1\u5fc3\u5904\uff0c\u53ef\u80fd\u4e5f\u5e0c\u671b\u65b9\u90aa\u771f\u80fd\u5728\u6c60\u516c\u5b50\u5bb6\u91cc\u6210\u4e3a\u4e00\u4e2a\u72ec\u5f53\u4e00\u9762\u3001\u54a4\u53f1\u98ce\u4e91\u7684\u4eba\u7269\uff0c\u4f46\u5979\u77e5\u9053\uff0c\u65b9\u90aa\u771f\u662f\u4e00\u5b9a\u6709\u7406\u7531\u7684\uff0c\u4e00\u5b9a\u6709\u4ed6\u7684\u82e6\u8877\u7684\u3002 \u3000\u3000\u5218\u662f\u4e4b\u4ece\u201c\u4f9d\u4f9d\u697c\u201d\u91cc\u9000\u51fa\u6765\uff0c\u697c\u91cc\u7684\u51e0\u4e2a\u7ba1\u4e8b\u7684\uff0c\u751f\u6015\u5f00\u7f6a\u4e86\u8fd9\u6c60\u5e9c\u7684\u7ea2\u4eba\uff0c\u8d54\u7f6a\u4f5c\u63d6\u7684\uff0c\u628a\u5218\u662f\u4e4b\u606d\u9001\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5218\u662f\u4e4b\u8d70\u51fa\u4e86\u90a3\u4e00\u697c\u7684\u706f\u5149\uff0c\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u8138\u4e0d\u6539\u5bb9\u7684\u8d70\u5411\u5728\u9634\u9eef\u7684\u9752\u77f3\u677f\u9053\u4e0a\uff0c\u505c\u7740\u7684\u4e09\u8f86\u8c6a\u534e\u9a6c\u8f66\u3002 \u3000\u3000\u4ed6\u4e0a\u4e86\u7b2c\u4e8c\u90e8\u9a6c\u8f66\u3002 \u3000\u3000\u4e09\u90e8\u9a6c\u8f66\u8e0f\u8e0f\u800c\u884c\u3002 \u3000\u3000\u624d\u4e0d\u8fc7\u8d70\u8fc7\u4e00\u6761\u8857\u7684\u5149\u666f\uff0c\u53c8\u6709\u4e94\u90e8\u9a6c\u8f66\uff0c\u505c\u5728\u6697\u5904\uff0c\u8fd9\u4e94\u90e8\u9a6c\u8f66\u65e0\u8bba\u662f\u8f66\u5b50\u8fd8\u662f\u9a6c\u592b\u7684\u6c14\u6d3e\uff0c\u90fd\u8981\u6bd4\u539f\u5148\u4e09\u90e8\u534e\u8d35\u8bb8\u591a\u3002 \u3000\u3000\u5218\u662f\u4e4b\u4e0b\u8f66\uff0c\u8de8\u4e0a\u4e86\u7b2c\u4e09\u90e8\u9a6c\u8f66\u3002 \u3000\u3000\u8f66\u5b50\u91cc\u5750\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u7c89\u96d5\u7389\u7422\u822c\u7684\u738b\u5b59\u516c\u5b50\u3002 \u3000\u3000\u201c\u600e\u6837\u4e86\uff1f\u201d\u6c60\u65e5\u66ae\u95ee\u3002 \u3000\u3000\u201c\u4e0d\u6210\u3002\u5218\u662f\u4e4b\u7b54\uff0c\u201c\u8ddf\u63e3\u60f3\u4e2d\u4e00\u6837\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9759\u4e86\u4e00\u9759\uff0c\u624d\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d\u7136\u540e\u9053\uff0c\u201c\u4f60\u4e0a\u6765\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u8ddf\u6c60\u65e5\u66ae\u4e00\u5e76\u5750\u7740\uff0c\u8f66\u5b50\u53c8\u5f00\u59cb\u9a70\u884c\u3002 \u3000\u3000\u826f\u4e45\uff0c\u6c60\u65e5\u66ae\u624d\u9053\uff1a\u201c\u5218\u5148\u751f\uff0c\u4f60\u8fd8\u6709\u751a\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u53cd\u95ee\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u7528\u6b64\u4eba\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u2018\u6d1b\u9633\u738b\u2019\u5feb\u5219\u4e09\u4e2a\u6708\uff0c\u8fdf\u5219\u4e00\u5e74\uff0c\u4fbf\u4f1a\u9009\u5b9a\uff0c\u6211\u4eec\u82e5\u6ca1\u6709\u4ed6\uff0c\u5149\u662f\u2018\u591a\u60c5\u516c\u5b50\u2019\u6e38\u7389\u906e\uff0c\u6211\u4eec\u4fbf\u96be\u5360\u4e0a\u98ce\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u597d\uff0c\u5f88\u597d\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u5148\u751f\u7684\u610f\u601d\u662f\u2014\u2014\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e00\u5b9a\u8981\u7528\u6b64\u4eba\uff0c\u6211\u4fbf\u6709\u529e\u6cd5\u8ba9\u4ed6\u5f52\u9644\u4f60\u65d7\u4e0b\uff0c\u4e0d\u8fc7\uff0c\u6211\u53ea\u62c5\u5fc3\u2026\u2026\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5373\u9053\uff1a\u201c\u62c5\u5fc3\u751a\u4e48\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u53f9\u9053\uff1a\u201c\u6211\u62c5\u5fc3\uff0c\u8981\u662f\u4ed6\u5165\u4e86\u6c60\u5e9c\uff0c\u6211\u8fd8\u6709\u6ca1\u6709\u7ad9\u7684\u4f4d\u7f6e\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u7b11\u4e86\uff1a\u201c\u5148\u751f\u4f55\u51fa\u6b64\u8bed\uff0c\u6211\u5bf9\u5148\u751f\u7684\u91cd\u89c6\uff0c\u5148\u751f\u8fd8\u4e0d\u4e86\u89e3\u5417\uff1f\u603b\u4e4b\uff0c\u6709\u2018\u5170\u4ead\u6c60\u5bb6\u2019\u7684\u4e00\u65e5\uff0c\u4fbf\u4e00\u5b9a\u4f1a\u6709\u5148\u751f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4f4f\u5728\u8fd1\u6cd5\u95e8\u5bfa\u7684\u5c71\u4e18\u91cc\uff0c\u9752\u5c71\u78a7\u5d16\uff0c\u7fe0\u8272\u5982\u67d3\uff0c\u6811\u8272\u6cc9\u58f0\uff0c\u4ea4\u76f8\u6620\u5e26\uff0c\u65b9\u90aa\u771f\u7684\u517b\u7236\u4fbf\u5728\u6b64\u5730\u5f00\u7530\u5efa\u5c4b\uff0c\u82b1\u6797\u7af9\u820d\uff0c\u7eff\u67f3\u542b\u70df\uff0c\u867d\u8d2b\u4e0d\u80dc\u5bd2\uff0c\u4f46\u6cc9\u77f3\u6e05\u5e7d\uff0c\u522b\u6709\u610f\u8da3\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7684\u4e00\u8eab\u672c\u9886\uff0c\u5374\u4e0e\u517b\u7236\u65e0\u5173\u3002 \u3000\u3000\u65b9\u7236\u8fd8\u6709\u4e00\u4e2a\u4eb2\u513f\uff0c\u4e0d\u5230\u5341\u5c81\uff0c\u751a\u662f\u673a\u4f36\u53ef\u7231\uff0c\u53eb\u505a\u65b9\u7075\uff0c\u4eba\u4e5f\u5f88\u7075\u5de7\u806a\u654f\u3002 \u3000\u3000\u8fd9\u65e5\u65b9\u90aa\u771f\u624d\u56de\u6765\uff0c\u65b9\u7075\u5df2\u5728\u9621\u9053\u4e0a\u8ddf\u4ed6\u8bf4\uff1a\u201c\u5927\u54e5\u5927\u54e5\uff0c\u8fd9\u4e24\u5929\uff0c\u6765\u4e86\u597d\u591a\u4eba\uff0c\u603b\u662f\u8981\u627e\u4f60\uff0c\u9001\u4e86\u5f88\u591a\u793c\u6765\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u542c\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u8d76\u56de\u5bb6\u91cc\uff0c\u679c\u5c14\u770b\u89c1\u7bb1\u7b50\u793c\u76d2\u5806\u79ef\u5982\u5c71\u3002\u65b9\u90aa\u771f\u89c1\u4e86\u8001\u7236\uff0c\u8bf7\u5b89\u4e4b\u540e\uff0c\u5c31\u8bf4\uff1a\u201c\u8fd9\u793c\u662f\u4e0d\u662f\u6d1b\u9633\u6c60\u516c\u5b50\u9001\u6765\u7684\u3002\u201d \u3000\u3000\u65b9\u7236\u629a\u7740\u767d\u9aef\uff0c\u6148\u853c\u5730\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u8fc7\u597d\u51e0\u8d9f\u4e86\uff0c\u8fd8\u8bf4\u4e86\u4e0d\u5c11\u597d\u8bdd\uff0c\u8fde\u6c60\u516c\u5b50\u90fd\u4eb2\u8eab\u6765\u8fc7\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53c8\u6697\u5403\u4e00\u60ca\uff1a\u8fde\u6c60\u65e5\u66ae\u90fd\u4eb2\u81ea\u6765\u8fd9\u91cc\uff0c\u5df2\u7ecf\u53ef\u4ee5\u8bf4\u662f\u63a8\u91cd\u5df2\u6781\u3002 \u3000\u3000\u65b9\u7236\u89c2\u5bdf\u795e\u8272\uff0c\u5df2\u7136\u660e\u4e86\u5927\u534a\uff0c\u9053\uff1a\u201c\u8fd9\u4e8b\u4f60\u4e0d\u7528\u4e3a\u96be\u3002\u6211\u89c1\u4ed6\u4eec\u628a\u793c\u9001\u6765\u8fd9\u91cc\uff0c\u4e0d\u4eb2\u4ea4\u4e88\u4f60\uff0c\u5fc5\u6709\u4f60\u7684\u96be\u5904\uff0c\u6240\u4ee5\u6211\u751a\u4e48\u90fd\u6ca1\u7b54\u5141\uff0c\u53ea\u8bf4\u7b49\u4f60\u56de\u6765\u518d\u4f5c\u5904\u7406\uff0c\u8fd9\u4e9b\u793c\u54c1\u6211\u539f\u672c\u575a\u6301\u9000\u56de\uff0c\u4ed6\u4eec\u6267\u610f\u4e0d\u80af\uff0c\u6211\u53ea\u597d\u6682\u5b58\u5c4b\u91cc\uff0c\u4f46\u4ece\u672a\u52a8\u8fc7\uff0c\u8fde\u7075\u513f\u987d\u76ae\uff0c\u5c61\u8981\u62c6\u5c01\uff0c\u6211\u4e5f\u4e0d\u51c6\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5fc3\u4e2d\u611f\u6fc0\uff0c\u4e5f\u4e0d\u591a\u8bf4\u751a\u4e48\uff0c\u53ea\u9053\uff1a\u201c\u6c60\u5bb6\u662f\u6548\u4eff\u5f53\u5e74\u5218\u5907\u4e09\u987e\u8305\u82a6\u7684\u505a\u6cd5\uff0c\u4f46\u90a3\u662f\u6ca1\u6709\u7528\u7684\uff0c\u90a3\u662f\u4e2a\u6c34\u6df1\u706b\u70ed\u7684\u7076\u5b50\uff0c\u6211\u4e00\u811a\u8e29\u4e0b\u53bb\uff0c\u96be\u514d\u4e5f\u53d8\u4e86\u4e9b\u85aa\uff0c\u70e7\u4e86\u9635\u5b50\uff0c\u53ef\u53ea\u6cb8\u817e\u4e86\u6c34\uff0c\u4ee5\u6211\u7684\u813e\u6027\uff0c\u4e00\u65e6\u6cbe\u4e0a\u4e86\u706b\uff0c\u4e5f\u4e0d\u4f1a\u56de\u5934\u6d47\u6e7f\u81ea\u5df1\u7684\u3002\u201d \u3000\u3000\u65b9\u7236\u6148\u548c\u5730\u9053\uff1a\u201c\u771f\u513f\uff0c\u6211\u77e5\u9053\u4f60\u6709\u4e00\u8eab\u597d\u672c\u9886\uff0c\u4f60\u8981\u505a\u751a\u4e48\uff0c\u4e5f\u6709\u6ee1\u6000\u7684\u5fd7\u5411\uff0c\u4e00\u5207\u90fd\u7531\u4f60\uff0c\u53ef\u4e0d\u80fd\u4e3a\u4e86\u6211\u548c\u4f60\u5c0f\u5f1f\uff0c\u8bef\u4e86\u4f60\u7684\u5fd7\u4e1a\u3002\u201d \u3000\u3000\u679c\u5c14\uff0c\u5230\u4e86\u672a\u724c\u65f6\u5206\uff0c\u6c60\u65e5\u66ae\u548c\u5218\u662f\u4e4b\u53c8\u6765\u6cd5\u95e8\u5bfa\u540e\u5c71\uff0c\u575a\u8bf7\u62dc\u6664\u65b9\u90aa\u771f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5e76\u4e0d\u51fa\u89c1\uff0c\u53ea\u5dee\u65b9\u7075\u8bf4\u4ed6\u8fd8\u6ca1\u56de\u6765\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u7b49\u4e5f\u60c5\u77e5\u6b64\u4e43\u6258\u8f9e\uff0c\u4f46\u4ecd\u793c\u4eea\u5468\u81f3\u7684\u8ddf\u65b9\u7236\u548c\u65b9\u7075\u626f\u8c08\u4e86\u4e00\u4f1a\u624d\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u6b21\u65e5\u6c60\u65e5\u66ae\u53c8\u518d\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u8ddf\u201c\u9ed1\u65cb\u98ce\u201d\u5c0f\u767d\u4e00\u8d77\u6765\uff0c\u65b9\u90aa\u771f\u8bf4\u662f\u51fa\u6e38\u672a\u8fd4\uff0c\u672a\u4e88\u63a5\u89c1\u3002 \u3000\u3000\u8fd9\u56de\u4ed6\u662f\u508d\u665a\u65f6\u5206\u624d\u6765\uff0c\u6309\u7167\u9053\u7406\uff0c\u65b9\u5bb6\u5e94\u8be5\u7559\u4ed6\u8fc7\u5bbf\u624d\u662f\uff0c\u4f46\u65b9\u7236\u6ca1\u8fd9\u4e2a\u610f\u601d\uff0c\u6c60\u65e5\u66ae\u53ea\u597d\u81ea\u591c\u91cc\u6253\u9053\u56de\u5e9c\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u6c60\u65e5\u66ae\u53c8\u6765\u4e86\uff0c\u8fd9\u56de\u968f\u884c\u7684\u662f\u6d2a\u4e09\u70ed\u3002 \u3000\u3000\u65b9\u90aa\u771f\u63a8\u8bf4\u8eab\u4f53\u4e0d\u9002\uff0c\u4ecd\u7136\u4e0d\u51fa\u89c1\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4fbf\u8981\u53d1\u813e\u6c14\uff0c\u6c60\u65e5\u66ae\u597d\u8a00\u529d\u963b\uff0c\u4e0d\u610f\u5374\u53d1\u73b0\u6848\u4e0a\u65e9\u7559\u4e0b\u4e00\u5f20\u5b57\u7b3a\uff0c\u5927\u610f\u662f\u8bf4\uff1a\u6c60\u65e5\u66ae\u8fd9\u7b2c\u4e09\u56de\u9a7e\u4e34\uff0c\u5fc5\u4e0e\u6d2a\u4e09\u70ed\u76f8\u5055\u800c\u81f3\uff0c\u6d2a\u5fc5\u4f1a\u501f\u6545\u53d1\u4f5c\uff0c\u6c60\u5fc5\u5047\u610f\u76f8\u529d\uff0c\u5e76\u5728\u672a\u4e86\u529d\u8bf4\u6c60\u65e5\u66ae\uff0c\u4e0d\u5fc5\u6789\u8d39\u5fc3\u673a\u3001\u767d\u82b1\u65f6\u95f4\u4e91\u4e91\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u8bfb\u7f62\u6309\u7b3a\u957f\u53f9\u9053\uff1a\u201c\u65b9\u5c11\u4fa0\u3001\u65b9\u5c11\u4fa0\uff0c\u4f60\u65e2\u4e0d\u4fe1\u6211\u4e00\u7247\u82e6\u5fc3\uff0c\u4ee5\u4e3a\u6c60\u67d0\u9020\u4f5c\uff0c\u6211\u4fbf\u4e0d\u5728\u5e9c\u4e0a\u9a9a\u6270\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u5f00\u59cb\uff0c\u6c60\u65e5\u66ae\u679c\u7136\u4e0d\u518d\u767b\u95e8\u9020\u8bbf\u3002 \u3000\u3000\u65b9\u7236\u548c\u5e7c\u5b50\u4e0d\u7981\u90fd\u6709\u4e9b\u6014\u5fe1\uff0c\u8fd9\u51e0\u65e5\u6765\uff0c\u6c60\u65e5\u66ae\u548c\u4ed6\u4eec\u5df2\u6df7\u5f97\u53ae\u719f\uff0c\u65b9\u7236\u867d\u575a\u4e0d\u6536\u793c\uff0c\u4f46\u65b9\u7075\u8fd8\u662f\u514d\u4e0d\u4e86\u62ff\u4e86\u4e9b\u597d\u73a9\u6709\u8da3\u53c8\u4e0d\u600e\u4e48\u503c\u94b1\u7684\u5c0f\u73a9\u610f\uff0c\u6c60\u65e5\u66ae\u4e00\u65e6\u4e0d\u6765\uff0c\u4e24\u7236\u5b50\u672a\u514d\u6709\u70b9\u82e5\u6709\u6240\u5931\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u628a\u6b64\u4e8b\u544a\u8bc9\u65b9\u90aa\u771f\u7684\u65f6\u5019\uff0c\u65b9\u90aa\u771f\u53ea\u770b\u770b\u9634\u9706\u5bc6\u5e03\u7684\u5929\u8272\uff0c\u4e00\u7b11\u7f6e\u4e4b\u3002 \u3000\u3000\u4e0d\u4e45\u4fbf\u5f00\u59cb\u4e0b\u96e8\uff0c\u4e0b\u4e86\u4e24\u5929\u8fde\u7ef5\u6deb\u96e8\u4e4b\u540e\uff0c\u65b9\u7236\u548c\u65b9\u7075\u8981\u81f3\u3002\u5e02\u8086\u4e70\u8089\uff0c\u8fd9\u624d\u84e6\u7136\u53d1\u73b0\uff0c\u6c60\u65e5\u66ae\u7adf\u548c\u4e00\u4f17\u4f8d\u4ece\uff0c\u5728\u5401\u964c\u9647\u7bf1\u5916\u9065\u76f8\u82e6\u5019\uff0c\u90fd\u6ca1\u6709\u6301\u4f1e\uff0c\u6dcb\u6210\u4e86\u843d\u6c64\u9e21\u3002 \u3000\u3000\u65b9\u7236\u5927\u4e3a\u611f\u52a8\uff0c\u9a6c\u4e0a\u547d\u65b9\u7075\u4e3e\u4f1e\u8fc7\u53bb\uff0c\u4e00\u9762\u628a\u60c5\u5f62\u8f6c\u544a\u4e86\u65b9\u90aa\u771f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u542c\u4e86\uff0c\u53ea\u6de1\u6de1\u5730\u9053\uff1a\u201c\u4ed6\u4eec\u679c\u7136\u6ca1\u8d70\u3002\u201d \u3000\u3000\u65b9\u7236\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u771f\u513f\uff0c\u6211\u770b\u6c60\u516c\u5b50\u4e5f\u662f\u4e00\u756a\u8bda\u5fc3\u8bda\u610f\uff0c\u4ed6\u8981\u91cd\u7528\u4f60\uff0c\u6b63\u662f\u5343\u91cc\u9a6c\u5f97\u9022\u4f2f\u4e50\uff0c\u4f60\u53c8\u4f55\u82e6\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4ed6\u4eec\u8d8a\u662f\u9690\u5fcd\uff0c\u8d8a\u6709\u56fe\u8c0b\uff0c\u6211\u8fd9\u4e00\u811a\u8e29\u8fdb\u53bb\uff0c\u5e76\u975e\u8d2a\u751f\u754f\u6b7b\uff0c\u800c\u662f\u503c\u4e0d\u503c\u5f97\uff1f\u201d \u3000\u3000\u65b9\u7236\u89c1\u529d\u4ed6\u4e0d\u8fc7\uff0c\u4fbf\u6bc5\u7136\u5192\u96e8\u51fa\u53bb\u7bf1\u5916\uff0c\u628a\u6c60\u516c\u5b50\u4e00\u4f17\u8bf7\u56de\u8305\u820d\u6765\u3002\u6c60\u65e5\u66ae\u8eab\u5a07\u8089\u8d35\u3002 \u3000\u3000\u6795\u6696\u88f3\u8f6f\u60ef\u4e86\uff0c\u53ea\u89c1\u4ed6\u5df2\u51b7\u5f97\u53cc\u988a\u53d1\u9752\uff0c\u4e0d\u4f4f\u6253\u98a4\uff0c\u65b9\u7236\u5fd9\u751f\u706b\u7ed9\u4ed6\u53d6\u6696\uff0c\u6c60\u65e5\u66ae\u559d\u4e86\u51e0\u53e3\u70ed\u8336\uff0c\u624d\u80fd\u5f00\u59cb\u8c08\u7b11\u5e94\u5bf9\u3002\u65b9\u90aa\u771f\u4ecd\u79f0\u5934\u75db\u9ad8\u5367\uff0c\u5e76\u4e0d\u51fa\u89c1\u3002 \u3000\u3000\u7fcc\u65e5\uff0c\u6c60\u65e5\u66ae\u4ecd\u662f\u5e26\u75c5\u524d\u6765\uff0c\u4f46\u4ed6\u5e26\u6765\u7684\u624b\u4e0b\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u5c11\uff0c\u8fd9\u6b21\u53ea\u5e26\u6d2a\u4e09\u70ed\u548c\u4e09\u540d\u968f\u4ece\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5374\u5411\u65b9\u7236\u8a00\u660e\uff0c\u62df\u540e\u65e5\u5219\u6084\u6084\u51fa\u95e8\uff0c\u907f\u5f00\u6c60\u65e5\u66ae\u7684\u7ea0\u7f20\u3002 \u3000\u3000\u65b9\u7236\u77e5\u9053\u529d\u4e5f\u65e0\u6548\uff0c\u5fc3\u91cc\u53ea\u5bf9\u6c60\u65e5\u66ae\u6108\u6e10\u6b49\u759a\u3002\u65b9\u90aa\u771f\u8bf4\uff1a\u201c\u6211\u672c\u62df\u518d\u4e09\u8003\u9a8c\u6c60\u516c\u5b50\u7684\u8010\u5fc3\u4e0e\u6bc5\u529b\uff0c\u4f46\u7239\u7239\u5df2\u7136\u52a8\u5fc3\uff0c\u6211\u6015\u518d\u8fd9\u6837\u4e0b\u53bb\uff0c\u5c31\u7b97\u6211\u4e0d\u7b54\u5e94\uff0c\u7239\u4e5f\u4f1a\u751f\u4e0d\u5fcd\u4e4b\u5fc3\uff0c\u4ee3\u6211\u7b54\u5141\uff0c\u6211\u8fd8\u662f\u6682\u884c\u8fdc\u907f\u7684\u597d\u3002\u201d \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u65b9\u7075\u5bf9\u6c60\u65e5\u66ae\u5341\u5206\u597d\u611f\uff0c\u66fe\u628a\u8fd9\u4ef6\u4e8b\u5bf9\u5c0f\u767d\u8bf4\u4e86\u3002\u5c0f\u767d\u544a\u8bc9\u4e86\u5218\u662f\u4e4b\u3002\u5218\u662f\u4e4b\u544a\u8bc9\u4e86\u6c60\u65e5\u66ae\u3002 \u3000\u3000\u4ece\u8fd9\u5929\u8d77\uff0c\u6c60\u65e5\u66ae\u5c31\u6ca1\u6709\u518d\u6765\u4e86\uff0c\u53ea\u5dee\u4ec6\u5f79\u65f6\u6765\u95ee\u5019\u65b9\u7236\uff0c\u5e76\u4e0d\u5fd8\u5e26\u4e0a\u539a\u793c\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u65b9\u90aa\u771f\u8981\u8d74\u201c\u4f9d\u4f9d\u697c\u201d\u4e00\u8d9f\uff0c\u4ed6\u8981\u79bb\u5f00\u4e00\u5c0f\u6bb5\u65f6\u65e5\uff0c\u5c11\u4e0d\u514d\u8981\u8ddf\u60dc\u60dc\u4f9d\u4f9d\u53d9\u522b\u4e00\u756a\u3002 \u3000\u3000\u65b9\u90aa\u771f\u518d\u4e34\u201c\u4f9d\u4f9d\u697c\u201d\u7684\u65f6\u5019\uff0c\u771f\u662f\u6574\u4e2a\u4eba\u7684\u8eab\u4ef7\u5b8c\u5168\u4e0d\u4e00\u6837\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u65b9\u90aa\u771f\u4ecd\u662f\u65b9\u90aa\u771f\uff0c\u4f46\u53ea\u8981\u8ddf\u201c\u5170\u4ead\u6c60\u5bb6\u201d\u6cbe\u4e0a\u4e86\u8fb9\uff0c\u5728\u697c\u5b50\u91cc\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u90fd\u89c6\u4ed6\u5982\u8d35\u5bbe\u3002 \u3000\u3000\u4f46\u5728\u6697\u5e95\u91cc\uff0c\u4e5f\u89c6\u4ed6\u4e3a\u602a\u4eba\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u7adf\u7136\u62d2\u7edd\u201c\u8363\u534e\u5bcc\u8d35\u201d\u7684\u602a\u4eba\uff01 \u3000\u3000\u4e3a\u8fd9\u4e00\u70b9\uff0c\u60dc\u60dc\u4e0d\u77e5\u542c\u5c3d\u591a\u5c11\u4eba\u5bf9\u65b9\u90aa\u771f\u7684\u51b7\u8a00\u51b7\u8bed\u3001\u95f2\u8a00\u95f2\u8bed\u3002\u5f53\u65b9\u90aa\u771f\u544a\u8bc9\u5979\u201c\u8981\u79bb\u5f00\u4e00\u6bb5\u65f6\u95f4\u201d\u7684\u65f6\u5019\uff0c\u60dc\u60dc\u53ea\u662f\u7528\u8273\u4e3d\u7684\u773c\u795e\u6d41\u8f6c\u4e00\u4e0b\uff0c\u6de1\u6de1\u4ed6\u8bf4\uff1a\u201c\u4f60\u51b3\u5b9a\u4e86\uff1f\u201d \u3000\u3000\u201c\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u559c\u6b22\u5170\u4ead\u6c60\u5bb6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u8981\u662f\u522b\u5bb6\u627e\u4f60\uff0c\u4f60\u4e5f\u4e00\u6837\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5947\u9053\uff1a\u201c\u6709\u522b\u7684\u4eba\u627e\u8fc7\u6211\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u53d8\u5f97\u7099\u624b\u53ef\u70ed\u4e86\uff1b\u201d\u60dc\u60dc\u62bf\u5634\u7b11\u9053\uff0c\u201c\u8fd9\u51e0\u5929\uff0c\u6709\u597d\u4e9b\u4e0d\u540c\u7684\u4eba\u90fd\u627e\u8fc7\u4f60\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9677\u5165\u6c89\u601d\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5ffd\u7136\uff0c\u201c\u79cb\u8749\u8f69\u201d\u7684\u95e8\u88ab\u63a8\u4e86\u5f00\u6765\u3002 \u3000\u3000\u60dc\u60dc\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u7ad9\u8d77\u6765\uff0c\u65b9\u90aa\u771f\u5fae\u5fae\u62cd\u7740\u5979\u7684\u624b\uff0c\u60dc\u60dc\u4e0d\u7531\u81ea\u4e3b\u5730\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u80cc\u5411\u95e8\u53e3\uff0c\u4ed6\u5e76\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u80cc\u540e\u81f3\u5c11\u6709\u4e24\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u53ea\u6709\u4e24\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u4f46\u6709\u4e00\u4e2a\u5374\u5f00\u4e86\u53e3\uff1a\u201c\u5ea7\u4e0a\u7684\u662f\u4e0d\u662f\u65b9\u5c11\u4fa0\uff1f\u201d\u4ed6\u4e00\u5f00\u53e3\uff0c\u624d\u8ba9\u4eba\u53d1\u73b0\u6709\u7b2c\u4e09\u4e2a\u4eba\u7684\u5b58\u5728\u3002\u8fd9\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u4e0d\u4f46\u6ca1\u6709\u811a\u6b65\u58f0\uff0c\u7adf\u8fde\u547c\u5438\u58f0\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5374\u53ea\u7b54\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u773c\u775b\uff1f\u201d \u3000\u3000\u90a3\u4eba\u5c45\u7136\u7b54\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4ecd\u7136\u62ff\u7740\u9152\u676f\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u95e8\uff1f\u201d \u3000\u3000\u90a3\u4eba\u56de\u7b54\uff1a\u201c\u770b\u89c1\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5148\u6572\u95e8\uff0c\u5c31\u8dd1\u4e86\u8fdb\u6765\uff1f\u201d \u3000\u3000\u5176\u4ed6\u4e24\u4eba\u4e00\u542c\uff0c\u5df2\u5fc3\u5934\u706b\u8d77\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u90a3\u4eba\u5374\u62e6\u6b62\u4e86\uff0c\u9053\uff1a\u201c\u6211\u5fd8\u4e86\u3002\u201d\u4ed6\u5c45\u7136\u5e26\u90a3\u4e24\u4eba\u53c8\u91cd\u65b0\u8d70\u4e86\u51fa\u53bb\uff0c\u7136\u540e\u6572\u95e8\uff0c\u4e0d\u5f85\u65b9\u90aa\u771f\u5e94\u95e8\uff0c\u5df2\u63a8\u95e8\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u8fd9\u6837\u4f60\u603b\u6ee1\u610f\u4e86\u7f62\uff1f\u201d\u90a3\u4eba\u95ee\u3002 \u3000\u3000\u201c\u53ef\u60dc\u6211\u4eca\u5929\u6ca1\u6709\u5fc3\u60c5\u89c1\u5ba2\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4ecd\u7136\u4e0d\u56de\u5934\u3002 \u3000\u3000\u201c\u6211\u4e0d\u662f\u4f60\u7684\u5ba2\u4eba\uff0c\u6211\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u5170\u4ead\u6c60\u5bb6\u7684\u4eba\uff0c\u7b97\u4e0d\u4e0a\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u5170\u4ead\u6c60\u5bb6\u5f53\u7136\u4e0d\u914d\uff0c\u201d\u90a3\u4eba\u7b11\u9053\uff0c\u201c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u5219\u4e0d\u4e00\u6837\uff0c\u6e38\u516c\u5b50\u662f\u4f60\u7684\u597d\u670b\u53cb\uff0c\u6211\u662f\u4f60\u7684\u597d\u6218\u53cb\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u56de\u5934\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u4e86\u4e09\u4e2a\u4eba\uff0c\u5de6\u53f3\u4e24\u4eba\uff0c\u4e00\u526f\u7cbe\u608d\u8198\u72e0\u4e4b\u8272\uff0c\u5c31\u50cf\u4e24\u5934\u8c79\u5b50\uff0c\u53ea\u8981\u5728\u4e00\u58f0\u53f7\u4ee4\u4e4b\u4e0b\uff0c\u968f\u65f6\u652b\u4eba\u800c\u566c\uff0c\u53ef\u662f\u8fd9\u4e24\u4eba\u8ddf\u4e2d\u95f4\u7684\u90a3\u4eba\u4e00\u6bd4\uff0c\u5168\u77ee\u4e86\u4e0b\u53bb\uff0c\u6c14\u52bf\u5168\u6d88\u3002 \u3000\u3000\u4e2d\u95f4\u7684\u90a3\u4eba\u50cf\u4e00\u5ea7\u94c1\u5854\uff0c\u5168\u8eab\u6ca1\u6709\u4e00\u5757\u591a\u4f59\u7684\u808c\u8089\uff0c\u4e5f\u6ca1\u6709\u4e00\u5757\u4e0d\u7ed3\u5b9e\u7684\u808c\u8089\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u6a21\u6837\uff0c\u5374\u5f88\u6e29\u6587\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\uff0c\u4e5f\u5341\u5206\u4eb2\u5207\uff0c\u5e74\u7eaa\u4e5f\u5341\u5206\u8f7b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u77e5\u9053\u8ddf\u81ea\u5df1\u8bf4\u8bdd\u7684\u6b63\u662f\u6b64\u4eba\u3002 \u3000\u3000\u901a\u5e38\uff0c\u8fd9\u4e9b\u9ad8\u5927\u7855\u5065\u7684\u5927\u5757\u5934\uff0c\u90fd\u53ea\u5f92\u5177\u58f0\u52bf\uff0c\u9ad8\u624b\u5374\u5728\u540e\u5934\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5374\u77e5\u9053\u90a3\u4eba\u5c31\u662f\u201c\u540e\u5934\u201d\u3002 \u3000\u3000\u4eba\u8bf4\u201c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u201d\u5728\u6d1b\u9633\u57ce\u91cc\u7684\u58f0\u5a01\u4e4b\u6240\u4ee5\u80fd\u540e\u6765\u5c45\u4e0a\uff0c\u6d78\u6d78\u7136\u9752\u51fa\u4e8e\u84dd\uff0c\u9664\u4e86\u6e38\u7389\u906e\u6e38\u516c\u5b50\u5411\u80fd\u5584\u52a0\u7528\u4eba\uff0c\u672c\u8eab\u6b63\u76f4\u4efb\u4fa0\u4e4b\u5916\uff0c\u4ed6\u5171\u6709\u201c\u4e94\u53ea\u624b\u201d\u3002 \u3000\u3000\u201c\u4e94\u53ea\u624b\u201d\u91cc\uff0c\u9664\u4e86\u5c5e\u4e8e\u957f\u5728\u4ed6\u81ea\u5df1\u8eab\u4e0a\u7684\u4e00\u5bf9\u4e4b\u5916\uff0c\u8fd8\u6709\u201c\u4e09\u53ea\u624b\u201d\u3002 \u3000\u3000\u4e09\u4e2a\u597d\u5e2e\u624b\u3002 \u3000\u3000\u201c\u6a2a\u5200\u7acb\u9a6c\u201d\u987e\u4f5b\u5f71\u3001\u201c\u8c79\u5b50\u201d\u7b80\u8fc5\u3001\u8fd8\u6709\u82b1\u6cbe\u5507\u3002 \u3000\u3000\u636e\u8bf4\u6ca1\u6709\u8fd9\u4e09\u5927\u529f\u81e3\uff0c\u5c31\u4e0d\u4f1a\u6709\u6e38\u7389\u906e\u7684\u7a9c\u8d77\uff1b\u4e0d\u8fc7\uff0c\u6e38\u7389\u906e\u5728\u671d\u5ef7\u4e0a\u8fd8\u6709\u4e24\u5927\u91cd\u81e3\u7684\u7167\u5e94\uff0c\u5c40\u9762\u7684\u786e\u8981\u6bd4\u6c60\u65e5\u66ae\u6709\u5229\u4e00\u4e9b\uff0c\u5982\u679c\u5170\u4ead\u4ed6\u5bb6\u4e0d\u662f\u4e16\u88ad\u738b\u4faf\uff0c\u8fd9\u4e00\u573a\u5b9e\u529b\u62b5\u6301\uff0c\u6c60\u65e5\u66ae\u65e9\u8981\u5931\u8272\u4e86\u3002 \u3000\u3000\u800c\u773c\u524d\u8fd9\u4eba\uff0c\u4fbf\u662f\u201c\u8c79\u5b50\u201d\u7b80\u8fc5\u3002 \u3000\u3000\u2014\u2014\u4ed6\u6765\u505a\u4ec0\u4e48\uff1f \u3000 \u7b2c\u516d\u7ae0\u3000\u8eab\u5728\u6d1b\u9633\u91cc\uff0c\u5f53\u77e5\u6d1b\u9633\u4e8b \u3000 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u6765\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u7b11\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u8010\u5fc3\u542c\u6211\u7ec6\u8bf4\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7b80\u8fc5\u9053\uff1a\u201c\u90a3\u6211\u7b80\u5355\u7684\u8bf4\uff1a\u73b0\u5728\u6d1b\u9633\u57ce\u91cc\uff0c\u90fd\u4f20\u8bf4\u4f60\u662f\u4e00\u4e2a\u5f88\u672c\u9886\u7684\u4eba\uff0c\u6211\u4eec\u516c\u5b50\u60f3\u8058\u7528\u4f60\uff0c\u6761\u4ef6\u4efb\u7531\u4f60\u5f00\u3002\u4f60\u6709\u6ca1\u6709\u5174\u8da3\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u6ca1\u6709\u5174\u8da3\uff1b\u201d\u65b9\u90aa\u771f\u61d2\u6d0b\u6d0b\u5730\u9053\u3002 \u3000\u3000\u7b80\u8fc5\u773c\u775b\u4e00\u4eae\u3002 \u3000\u3000\u201c\u800c\u662f\u6ca1\u6709\u53ef\u80fd\u3002\u201d\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff0c\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u8fd8\u6ca1\u6709\u6253\u7b97\u5f53\u8c01\u7684\u8d70\u72d7\u3002\u201d \u3000\u3000\u7b80\u8fc5\u8eab\u65c1\u7684\u4e24\u4eba\uff0c\u8138\u8272\u9f50\u500f\uff0c\u4e00\u9f50\u62bd\u51fa\u8170\u95f4\u7684\u8c79\u5c3e\u97ad\uff0c\u4f46\u4ecd\u88ab\u7b80\u8fc5\u5236\u6b62\u3002 \u3000\u3000\u7b80\u8fc5\u9053\uff1a\u201c\u90a3\u6211\u79c1\u4e0b\u4e5f\u5e0c\u671b\u4f60\u80fd\u7b54\u5141\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u7b80\u8fc5\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u52a0\u76df\u5c0f\u78a7\u6e56\uff0c\u4e5f\u4e0d\u8981\u52a0\u5165\u5170\u4ead\u6c60\u5bb6\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u662f\u6211\u7684\u4e8b\u3002\u6211\u4e0d\u5fc5\u8981\u8c01\u6765\u7b54\u5e94\u3002\u201d \u3000\u3000\u7b80\u8fc5\u4e5f\u4e0d\u7981\u53d8\u8138\uff0c\u4f46\u4ecd\u7136\u6709\u793c\u7684\u7b11\u7740\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u7684\u4e24\u540d\u5927\u6c49\u65e9\u5df2\u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7528\u201c \u3000\u3000\u90a3\u584c\u9f3b\u7684\u5927\u6c49\u621f\u6307\u6012\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u8bc6\u62ac\u4e3e\u7684\u4e1c\u897f\uff0c\u4f60\u4fbf\u89c1\u597d\u4e0d\u5403\uff0c\u5403\u9a9a\u7684\uff01\u4f60\u5012\u4e00\u5f20\u7eb8\u753b\u4e2a\u9f3b\u5b50\uff0c\u5929\u5927\u7684\u9762\u5b50\uff0c\u4f60\u8fd8\u5f85\u6e38\u516c\u5b50\u96c7\u9876\u516b\u4eba\u5927\u8f7f\u6765\u62ac\u4f60\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u81ea\u987e\u81ea\u7684\u4e0e\u60dc\u60dc\u6d45\u914c\u4f4e\u7b11\uff0c\u6ca1\u53bb\u7406\u4f1a\u4ed6\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u94a9\u9f3b\u5927\u6c49\u66f4\u6012\u4e0d\u53ef\u6291\uff0c\u626c\u7740\u8c79\u5c3e\u97ad\u5406\u559d\u9053\uff1a\u201c\u4f60\u522b\u7a9d\u5728\u8fd9\u91cc\u7231\u7406\u4e0d\u7406\u7684\uff0c\u8001\u5b50\u4e00\u97ad\u7838\u4e0b\u53bb\uff0c\u4f60\u7684\u72d7\u8111\u888b\u8981\u53d8\u6210\u7834\u7f50\u5b50\uff0c\u90a3\u65f6\u518d\u8981\u540e\u6094\uff0c\u4e5f\u4e0d\u503c\u51e0\u4e2a\u94b1\u4e86\u3002\u201d \u3000\u3000\u60dc\u60dc\u89c1\u4e8c\u4eba\u52a8\u4e0a\u4e86\u5bb6\u4f19\uff0c\u51f6\u795e\u6076\u715e\uff0c\u4e0d\u89c9\u7565\u6709\u70b9\u614c\u60f6\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6e29\u67d4\u7684\u5411\u5979\u4e3e\u676f\uff0c\u8868\u793a\u8981\u5979\u4e0d\u5fc5\u60ca\u6015\u3002 \u3000\u3000\u4e24\u540d\u5927\u6c49\u89c1\u65b9\u90aa\u771f\u65e0\u52a8\u4e8e\u8877\u3001\u4e1d\u6beb\u4e0d\u60e7\uff0c\u5176\u4e2d\u90a3\u584c\u9f3b\u5927\u6c49\u4fbf\u5411\u60dc\u60dc\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u81ed\u5a4a\u5b50\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u4e0b\u53bb\uff0c\u90a3\u584c\u9f3b\u5927\u6c49\u8138\u4e0a\u5df2\u4e00\u8fde\u88ab\u5288\u5288\u62cd\u62cd\u7684\u6253\u4e86\u5341\u4e03\u516b\u8bb0\u8033\u5149\uff0c\u7136\u540e\u88ab\u4e00\u811a\u8e39\u98de\u51fa\u95e8\uff0c\u5df4\u767b\u5df4\u767b\u7684\u6eda\u5230\u697c\u4e0b\u53bb\uff0c\u534a\u664c\u8fd8\u8d77\u4e0d\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51fa\u624b\u592a\u5feb\uff0c\u584c\u9f3b\u5927\u6c49\u7684\u540c\u4f34\uff0c\u6839\u672c\u6765\u4e0d\u53ca\u770b\u6e05\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u584c\u9f3b\u5927\u6c49\u5df2\u8eba\u5728\u8fdc\u8fdc\u7684\u697c\u4e0b\u547b\u541f\u53eb\u75db\u3002 \u3000\u3000\u65b9\u90aa\u771f\u95ee\u4ed6\uff1a\u201c\u4f60\u8981\u81ea\u5df1\u6eda\u4e0b\u53bb\uff0c\u8fd8\u662f\u8981\u6211\u5e2e\u4f60\uff1f\u201d \u3000\u3000\u94a9\u9f3b\u5927\u6c49\u60f3\u4e86\u60f3\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u65bd\u5c55\u201c\u516b\u65b9\u98ce\u96e8\u201d\u8c79\u5c3e\u97ad\u7684\u7b2c\u4e00\u5f0f\u201c\u5929\u98ce\u7834\u6653\u201d\uff0c\u5411\u65b9\u90aa\u771f\u76f4\u7838\u4e0b\u53bb\u3002 \u3000\u3000\u65b9\u90aa\u771f\u770b\u5b9a\u4ed6\u7684\u6765\u52bf\uff0c\u53ea\u4e00\u95ea\u8eab\uff0c\u8c79\u5c3e\u97ad\u5df2\u843d\u5728\u4ed6\u7684\u624b\u4e0a\uff0c\u53cc\u624b\u4e00\u63c9\uff0c\u8fd9\u8c79\u5c3e\u97ad\u6413\u6210\u4e00\u5806\u5e9f\u94dc\u70c2\u94c1\u3002 \u3000\u3000\u94a9\u9f3b\u5927\u6c49\u6574\u4e2a\u4eba\u90fd\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u518d\u8bf4\u4e00\u6b21\uff0c\u4f60\u8981\u81ea\u5df1\u6eda\u4e0b\u697c\u53bb\uff0c\u8fd8\u662f\u8981\u6211\u52a8\u624b\uff1f\u201d \u3000\u3000\u94a9\u9f3b\u5927\u6c49\u671b\u671b\u65b9\u90aa\u771f\uff0c\u53c8\u770b\u770b\u7b80\u8fc5\uff0c\u7b80\u8fc5\u4ecd\u7136\u5fae\u7b11\uff0c\u5e76\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u94a9\u9f3b\u5927\u6c49\u5982\u91ca\u91cd\u8d1f\uff0c\u81ea\u5df1\u4e00\u4e2a\u5012\u683d\u8471\u5f80\u697c\u4e0b\u8dcc\u53bb\uff0c\u683c\u767b\u683c\u767b\u54cd\u4e2a\u4e0d\u505c\uff0c\u8fd9\u4e48\u4e24\u4e2a\u5927\u5757\u5934\u5148\u540e\u4f5c\u6eda\u5730\u846b\u82a6\uff0c\u4e00\u65f6\u6574\u680b\u201c\u4f9d\u4f9d\u697c\u201d\u4e3a\u4e4b\u9707\u52a8\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u60ca\u52a8\u4e86\u8bb8\u591a\u4eba\uff0c\u90fd\u51fa\u6765\u770b\u70ed\u95f9\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4e00\u89c1\u8fd9\u4e24\u540d\u8dcc\u5f97\u8364\u4e03\u516b\u7d20\u7684\u5927\u6c49\uff0c\u5168\u90fd\u5413\u5f97\u7f29\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u5728\u4ea4\u5934\u63a5\u8033\uff0c\u7a83\u7a83\u79c1\u8bed\u3002 \u3000\u3000\u201c\u4e0d\u5f97\u4e86\u5566\uff0c\u65b9\u516c\u5b50\u8ddf\u6e38\u516c\u5b50\u624b\u4e0b\u7684\u2018\u54fc\u54c8\u4e8c\u5c06\uff0c\u7530\u6c0f\u5144\u5f1f\u95f9\u4e86\u8d77\u6765\uff0c\u770b\u6765\u7530\u4e1c\u548c\u7530\u897f\u8fd8\u53d7\u4e86\u4f24\u5462\uff0c\u54ce\u5537\uff0c\u8fd9\u53ef\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u6e38\u516c\u5b50\u7684\u7ba1\u5bb6\u7b80\u5927\u7237\u4e5f\u6765\u4e86\uff0c\u5c31\u5728\u60dc\u60dc\u7684\u623f\u5b50\u91cc\u5462\uff0c\u770b\u6765\u65b9\u516c\u5b50\u8fd9\u6b21\u8981\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\uff0c\u5e78\u597d\u65b9\u516c\u5b50\u6709\u6c60\u516c\u5b50\u505a\u540e\u76fe\uff0c\u6e38\u516c\u5b50\u672a\u5fc5\u80fd\u62ff\u4ed6\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u53eb\u3001\u53cc\uff0c\u201c\u79cb\u8749\u8f69\u201d\u91cc\u7a76\u7adf\u53d1\u751f\u4e86\u751a\u4e48\u4e8b\uff0c\u4ed6\u4eec\u8c01\u4e5f\u4e0d\u6562\u4e0a\u53bb\u63a2\u770b\u3002 \u3000\u3000\u7b80\u8fc5\u89c1\u65b9\u90aa\u771f\u4e00\u51fa\u624b\u95f4\u5c31\u628a\u51ed\u4e00\u5bf9\u8c79\u5c3e\u97ad\u996e\u8a89\u9655\u897f\u7684\u7530\u6c0f\u53cc\u96c4\u6253\u53d1\u6389\uff0c\u5fc3\u91cc\u6709\u6570\uff0c\u53ea\u9053\uff1a\u201c\u6253\u5f97\u597d\uff0c\u6253\u5f97\u5999\u3002\u201d \u3000\u3000\u4ed6\u8865\u5145\u9053\uff1a\u201c\u4ed6\u4eec\u5634\u91cc\u4e0d\u5e72\u4e0d\u51c0\u7684\uff0c\u5f97\u7f6a\u4e86\u65b9\u5c11\u4fa0\uff0c\u8bf7\u52ff\u89c1\u602a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u7559\u5728\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u7b11\u9053\uff1a\u201c\u6211\u53ea\u662f\u8981\u656c\u4f60\u4e00\u676f\u9152\u3002\u201d\u4ed6\u6162\u6162\u7684\u8d70\u8fc7\u53bb\uff0c\u6162\u6162\u7684\u62ff\u8d77\u684c\u4e0a\u7684\u9152\u58f6\uff0c\u6162\u6162\u7684\u5012\u6ee1\u4e00\u676f\u9152\uff0c\u6162\u6162\u7684\u9012\u5411\u65b9\u90aa\u771f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u63a5\u8fc7\u4e86\u676f\u5b50\u3002 \u3000\u3000\u7b80\u8fc5\u5e76\u4e0d\u653e\u624b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51d1\u8fc7\u8138\u53bb\uff0c\u6162\u6162\u7684\u628a\u9152\u559d\u5b8c\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u653e\u624b\u3002 \u3000\u3000\u7b80\u8fc5\u4ecd\u62ff\u7740\u676f\u5b50\uff0c\u8138\u4e0a\u4ecd\u6709\u7b11\u5bb9\uff0c\u53ef\u662f\u4ed6\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u8ba9\u6211\u5728\u6e38\u516c\u5b50\u524d\u6709\u4e86\u4ea4\u4ee3\uff0c\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u7b80\u5144\uff0c\u4e0d\u9001\u3002\u201d \u3000\u3000\u7b80\u8fc5\u7684\u864e\u53e3\u662f\u62ff\u7740\u676f\u5b50\u79bb\u5f00\u201c\u79cb\u8749\u8f69\u201d\u7684\uff0c\u4ed6\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u8fd8\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u65b9\u5c11\u4fa0\uff0c\u6e38\u516c\u4e00\u5fc3\u60f3\u91cd\u7528\u4f60\uff0c\u4f60\u4e0d\u8d4f\u8138\uff0c\u90a3\u662f\u6211\u6709\u8fb1\u4f7f\u547d\u3002\u4e0d\u8fc7\uff0c\u5168\u57ce\u7684\u4eba\u90fd\u77e5\u9053\u4f60\u7ec8\u5fc5\u6295\u6548\u6c60\u516c\u5b50\uff0c\u56de\u767e\u5e94\u548c\u845b\u94c3\u94c3\uff0c\u90fd\u4e0d\u4f1a\u8896\u624b\u7684\u3002\u2026 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u63d0\u9192\u3002\u201d \u3000\u3000\u7b80\u8fc5\u70b9\u5934\u4e00\u7b11\uff0c\u8d70\u51fa\u4e86\u201c\u79cb\u8749\u8f69\u201d\u3002 \u3000\u3000\u60dc\u60dc\u7981\u4e0d\u4f4f\u4f9d\u5411\u65b9\u90aa\u771f\uff0c\u95ee\u3000\uff1a\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u76ee\u6ce8\u90a3\u4e00\u6247\u521a\u63a9\u4e0a\u7684\u95e8\uff0c\u5583\u5583\u5730\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u4e0d\u5931\u4e3a\u4e00\u4f4d\u5e72\u7ec3\u7684\u597d\u6c49\u3002\u201d \u3000\u3000\u7b80\u8fc5\u8d70\u4e0b\u697c\u6765\uff0c\u7530\u6c0f\u5144\u5f1f\u8bda\u60f6\u8bda\u6050\u7684\u5728\u697c\u4e0b\u5019\u7740\uff0c\u7b80\u8fc5\u7b11\u9053\uff1a\u201c\u8d70\u7f62\u3002\u201d\u624b\u91cc\u4ecd\u7aef\u7740\u676f\u5b50\u3002\u4e09\u4eba\u51fa\u4e86\u201c\u4f9d\u4f9d\u697c\u201d\u7684\u5927\u95e8\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u4e2a\u5546\u8d3e\u3002 \u3000\u3000\u4e00\u4e2a\u5355\u51ed\u773c\u795e\u5c31\u80fd\u4f24\u4eba\u7684\u5546\u4eba\u3002 \u3000\u3000\u7b80\u8fc5\u4e00\u89c1\u4ed6\uff0c\u5c31\u4ee5\u5c0f\u78a7\u6e56\u6e38\u6c0f\u7684\u5bb6\u89c4\u89c1\u793c\uff0c\u90a3\u4eba\u53ea\u671b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u5c31\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u4f60\u7684\u624b\u4f24\u5f97\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\u7684\u3002\u201d\u4ed6\u53f3\u624b\u864e\u53e3\u7aef\u62ff\u7740\u676f\u5b50\uff0c\u8d81\u65b9\u90aa\u771f\u51d1\u5507\u996e\u9152\u65f6\u6b63\u8981\u53d1\u52a8\u653b\u52bf\uff0c\u4f46\u65b9\u90aa\u771f\u5df2\u8f7b\u63cf\u6de1\u5199\u7684\u628a\u9152\u676f\u5207\u6210\u4e24\u622a\uff0c\u4e0a\u622a\u676f\u6cbf\u5d4c\u5165\u7b80\u8fc5\u98df\u6307\u7b2c\u4e8c\u4e09\u8282\u6307\u9aa8\u91cc\uff0c\u4e0b\u622a\u676f\u6cbf\u5219\u5272\u5165\u4ed6\u98df\u6307\u65c1\u808c\u91cc\uff0c\u5c01\u6740\u4e86\u7b80\u8fc5\u4e00\u5207\u5c06\u53d1\u800c\u672a\u53d1\u7684\u653b\u52bf\u3002 \u3000\u3000\u90a3\u5546\u4eba\u770b\u4e86\u4ed6\u624b\u4e0a\u7684\u4f24\uff0c\u6c89\u541f\u4e86\u4e00\u4e0b\u5b50\uff0c\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u516c\u5b50\u6240\u6599\uff0c\u4ed6\u4e0d\u80af\u52a0\u5165\u6211\u4eec\uff0c\u4e0d\u8fc7\u6211\u4eec\u5f97\u8981\u9a6c\u4e0a\u79bb\u5f00\u6b64\u5730\u3002\u201d \u3000\u3000\u7b80\u8fc5\u6115\u7136\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5546\u4eba\u9053\uff1a\u201c\u2018\u8001\u516c\u5b50\u2019\u7684\u2018\u5999\u624b\u5802\u2019\u5df2\u5728\u6b64\u5730\u57cb\u4f0f\uff0c\u52bf\u5fc5\u8981\u6740\u59d3\u65b9\u7684\u800c\u540e\u7518\uff01\u201d \u3000\u3000\u7b80\u8fc5\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u62ac\u5934\u770b\u4e86\u4e00\u770b\uff0c\u53ea\u89c1\u201c\u79cb\u8749\u8f69\u201d\u91cc\u706f\u706b\u4f9d\u7136\uff0c\u4e0d\u77e5\u603b\u7b97\u662f\u5bf9\u4ed6\u5df2\u7559\u4e86\u60c5\u9762\u7684\u65b9\u90aa\u771f\u53ef\u6709\u6ca1\u6709\u611f\u5230\u6740\u673a\u56db\u4f0f\uff1f\u7b80\u8fc5\u4e5f\u4e0d\u6562\u8ddf\u201c\u5999\u624b\u5802\u201d\u7684\u4eba\u6b63\u9762\u5bf9\u6297\uff0c\u8fde\u6e38\u516c\u5b50\u9ebe\u4e0b\u6700\u4fe1\u4efb\u7684\u987e\u4f5b\u5f71\u4e5f\u4e0d\u7ba1\u7684\u4e8b\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u60f3\u5192\u8fd9\u8d9f\u6d51\u6c34\u3002 \u3000\u3000\u8fd9\u201c\u5546\u4eba\u201d\u5f53\u7136\u4fbf\u662f\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u5728\u6b66\u6797\u3001\u4ed5\u6797\u4e2d\uff0c\u88ab\u5c0a\u4e3a\u201c\u987e\u76fc\u795e\u98ce\u201d\u7684\u987e\u4f5b\u5f71\uff0c\u4fbf\u662f\u8fd9\u4f4d\u770b\u6765\u53ea\u50cf\u4e00\u540d\u5e73\u5eb8\u5546\u8d3e\u7684\u4eba\u3002\u987e\u4f5b\u5f71\u8fd8\u6709\u4e00\u4e2a\u5916\u53f7\uff0c\u5c31\u53eb\u505a\uff1a\u201c\u6a2a\u5200\u7acb\u9a6c\uff0c\u9189\u5367\u5c71\u5c97\u201d\uff0c\u4ed6\u4e0d\u4ec5\u5200\u6cd5\u597d\uff0c\u9152\u91cf\u597d\uff0c\u667a\u8c0b\u4e5f\u7b97\u65e0\u9057\u7b56\uff0c\u6545\u6781\u53d7\u6e38\u7389\u906e\u5668\u91cd\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8d70\u51fa\u201c\u4f9d\u4f9d\u697c\u201d\u7684\u65f6\u5019\uff0c\u662f\u5e26\u7740\u9189\u610f\u7684\u3002 \u3000\u3000\u60dc\u60dc\u672c\u6765\u8981\u96c7\u8f66\u5b50\u9001\u4ed6\u56de\u53bb\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53ea\u53eb\u5979\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201c\u6211\u5e94\u4ed8\u5f97\u4e86\u5170\u4ead\u6c60\u5bb6\uff0c\u4e5f\u62d2\u7edd\u4e86\u5c0f\u78a7\u6e56\u6e38\u5bb6\uff0c\u4fbf\u4e0d\u5728\u4e4e\u591a\u6765\u4e2a\u59d3\u845b\u7684\u8fd8\u662f\u59d3\u56de\u7684\u3002\u201d \u3000\u3000\u60dc\u60dc\u9053\uff1a\u201c\u4f60\u539f\u4e0d\u662f\u6d1b\u9633\u4eba\uff0c\u4e0d\u77e5\u9053\u59d3\u56de\u7684\u624b\u6bb5\u3002\u6211\u5012\u4e0d\u6015\u2018\u5343\u53f6\u5c71\u5e84\u2019\uff0c\u6015\u53ea\u4f2f\u2018\u5999\u624b\u5802\u2019\u56de\u767e\u5e94\uff0c\u59d3\u56de\u7684\u53ef\u4e0d\u6bd4\u6e38\u516c\u5b50\u548c\u6c60\u516c\u5b50\uff0c\u4ed6\u4eec\u4e00\u662f\u6b63\u4eba\u541b\u5b50\u3001\u4e00\u662f\u5b85\u5fc3\u4ec1\u539a\uff0c\u59d3\u56de\u7684\u4e00\u8eab\u5fc3\u72e0\u624b\u8fa3\uff0c\u8ddf\u4ed6\u4eec\u4f5c\u5bf9\u7684\u4eba\uff0c\u8c01\u90fd\u4e0d\u4f1a\u6709\u597d\u65e5\u5b50\u8fc7\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u8981\u60dc\u60dc\u4f8b\u4e3e\u51fa\u4e00\u4e9b\u4ed6\u4eec\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u60dc\u60dc\u53ea\u8bf4\u4e86\u51e0\u4ef6\uff0c\u65b9\u90aa\u771f\u5df2\u5477\u7740\u9152\u731b\u51b7\u7b11\u3002 \u3000\u3000\u201c\u6211\u5012\u542c\u8bf4\u2018\u5999\u624b\u5802\u2019\u638c\u5b9e\u6743\u7684\uff0c\u90fd\u6ca1\u6709\u5916\u4eba\uff0c\u4e0d\u6bd4\u6c60\u65e5\u66ae\uff0c\u4ed6\u624b\u4e0a\u6709\u5218\u662f\u4e4b\u3001\u9ed1\u65cb\u98ce\u5c0f\u767d\u548c\u6d2a\u4e09\u70ed\uff0c\u6e38\u7389\u906e\u624b\u4e0b\u6709\u8c79\u5b50\u7b80\u8fc5\u3002\u6a2a\u5200\u7acb\u9a6c\u987e\u4f5b\u5f71\u3001\u82b1\u6cbe\u5507\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u5999\u624b\u5802\u7684\u56de\u4e07\u96f7\uff0c\u662f\u56de\u767e\u5e94\u7684\u8205\u8205\uff0c\u56de\u767e\u54cd\u5219\u662f\u4ed6\u7684\u80de\u5f1f\uff0c\u56de\u7edd\u5219\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u5168\u7531\u4eb2\u4fe1\u63fd\u5927\u6743\uff0c\u770b\u6765\u65e0\u602a\u4e4e\u5999\u624b\u5802\u5149\u5f97\u4e2a\u9738\u5b57\uff0c\u6c14\u52bf\u4e0a\u53cd\u4e0d\u5982\u5170\u4ead\u6c60\u5bb6\u53ca\u5c0f\u78a7\u6e56\u6e38\u5bb6\u4e86\u3002\u201d \u3000\u3000\u60dc\u60dc\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u5c11\u7b97\u51e0\u4eba\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5c55\u7709\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u60dc\u60dc\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6c60\u65e5\u66ae\u8fd8\u6709\u4e2a\u4e86\u4e0d\u8d77\u7684\u5ac2\u5b50\uff0c\u542c\u8bf4\u8fd8\u662f\u4f4d\u4eba\u95f4\u7edd\u8272\uff1b\u6e38\u7389\u906e\u5728\u671d\u4e2d\u6709\u4e00\u6587\u4e00\u6b66\u4e24\u5927\u540d\u81e3\u5927\u5c06\u8bc6\u91cd\uff0c\u8fd9\u4e9b\u90fd\u5f97\u8981\u7b97\u8fdb\u53bb\uff1b\u201d\u5979\u867d\u7136\u5728\u7b11\uff0c\u4f46\u6101\u5bb9\u4e0d\u51cf\uff0c\u201c\u6211\u8fd8\u662f\u62c5\u5fc3\u56de\u5bb6\u7684\u4eba\uff0c\u56de\u767e\u5e94\u3001\u56de\u4e07\u96f7\u3001\u56de\u767e\u54cd\u3001\u56de\u7edd\u90fd\u662f\u6d1b\u9633\u57ce\u91cc\u65e0\u6cd5\u65e0\u5929\u7684\u4eba\u7269\uff0c\u4ed6\u4eec\u4e00\u5bb6\u5b50\u5168\u662f\u6a2a\u5403\u9ed1\u767d\u4e24\u9053\u7684\u9ad8\u624b\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u6709\u94b1\u6709\u52bf\uff0c\u5728\u7eff\u6797\u9053\u4e0a\u672c\u6709\u4f4d\u4efd\uff0c\u5404\u8def\u6740\u624b\uff0c\u90fd\u542c\u547d\u4e8e\u5999\u624b\u5802\uff0c\u6211\u6015\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u7b11\u9053\uff1a\u201c\u60dc\u60dc\uff0c\u4f60\u77e5\u9053\u5f97\u5012\u4e0d\u5c11\u3002\u201d \u3000\u3000\u60dc\u60dc\u5e7d\u6028\u7684\u7768\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8eab\u5728\u6d1b\u9633\u57ce\uff0c\u600e\u4f1a\u4e0d\u77e5\u6d1b\u9633\u4e8b\uff1f\u8fd9\u513f\u6765\u7684\u4e0d\u5c11\u662f\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u9152\u9163\u7545\u8c08\u4e4b\u4f59\uff0c\u8fd9\u6d1b\u9633\u56db\u516c\u5b50\u4e4b\u4e89\u7684\u4e8b\uff0c\u771f\u662f\u4e0d\u4f1a\u5531\u4e5f\u4f1a\u5f39\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u53c8\u4e0d\u62c5\u5fc3\u5343\u53f6\u5c71\u5e84\u7684\u845b\u94c3\u94c3\uff1f\u201d \u3000\u3000\u60dc\u60dc\u4ee5\u8896\u63a9\u5634\uff0c\u55d4\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6d1b\u9633\u56db\u516c\u5b50\u91cc\u845b\u5bb6\u5b9e\u529b\u6700\u5f31\uff0c\u800c\u4e14\u4e5f\u662f\u552f\u4e00\u7684\u2018\u5973\u516c\u5b50\u2019\uff0c\u5979\u89c1\u7740\u4f60\uff0c\u624d\u2026\u2026\u6211\u624d\u4e0d\u76f8\u4fe1\u5979\u4f1a\u62ff\u4f60\u600e\u6837\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u7528\u624b\u62e7\u4e86\u62e7\u60dc\u60dc\u7684\u7389\u988a\uff0c\u75f4\u770b\u4e86\u4e00\u4f1a\uff0c\u5ffd\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u6211\u53bb\u770b\u770b\u60f3\u62ff\u6211\u600e\u6837\u7684\u4eba\u4f1a\u62ff\u6211\u600e\u6837\u3002\u201d \u3000\u3000\u60dc\u60dc\u4f9d\u4f9d\u4e0d\u820d\u5730\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6211\u518d\u4e0d\u4e0b\u53bb\uff0c\u4ed6\u4eec\u5c31\u8981\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u629a\u7740\u60dc\u60dc\u7684\u67d4\u80a9\uff1a\u201c\u8fd8\u662f\u4e0b\u53bb\u4f1a\u597d\u4e00\u4e9b\u3002\u201d \u3000\u3000\u60dc\u60dc\u62c5\u5fc3\u7684\u4f9d\u504e\u5728\u65b9\u90aa\u771f\u7684\u80f8\u524d\uff0c\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u6211\u80fd\u5e2e\u4f60\u751a\u4e48\uff1f\u6211\u600e\u6837\u624d\u77e5\u9053\u4f60\u65e0\u6059\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6e29\u67d4\u5730\u9053\uff1a\u201c\u80fd\u3002\u201d \u3000\u3000\u60dc\u60dc\u559c\u5ffb\u5730\u9053\uff1a\u201c\u600e\u6837\u5e2e\u4f60\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u5728\u680f\u4e0a\uff0c\u4e00\u89c1\u88f9\u7740\u6211\u7684\u6709\u7eff\u8272\u7684\u5251\u5149\u98de\u4e0a\u4e86\u5929\uff0c\u7acb\u5373\u5012\u4e00\u76c6\u6c34\u4e0b\u6765\uff1b\u5982\u679c\u4f60\u770b\u89c1\u8857\u5fc3\u6709\u4e00\u56e2\u706b\u5149\u63a0\u8fc7\uff0c\u4fbf\u7b49\u4e8e\u544a\u8bc9\u4f60\uff1a\u6211\u6b63\u8981\u56de\u5bb6\u7761\u5927\u89c9\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53cc\u773c\u6df1\u6df1\u7684\u671b\u8fdb\u4e86\u5979\u7684\u7738\u5b50\u91cc\uff1a\u201c\u5c31\u8fd9\u6837\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u60dc\u60dc\u770b\u89c1\u65b9\u90aa\u771f\u7684\u795e\u60c5\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4fbf\u77e5\u9053\u5929\u4e0b\u95f4\u6ca1\u6709\u4eba\u80fd\u51fb\u8d25\u4ed6\uff0c\u4e00\u79cd\u5bf9\u82f1\u96c4\u4fa0\u5c11\u7684\u5b7a\u6155\u4e4b\u60c5\uff0c\u63a0\u4e0a\u5fc3\u5934\uff0c\u7279\u522b\u6d53\u70c8\uff0c\u53ea\u4fef\u5728\u4ed6\u80a9\u8180\u4e0a\uff0c\u611f\u53d7\u90a3\u7537\u5b50\u7684\u4f53\u6e29\u548c\u6c14\u606f\uff0c\u559c\u5fd0\u5fd1\u5730\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u7b11\u3002 \u3000\u3000\u4ed6\u98d8\u7136\u4e0b\u4e86\u697c\u3002 \u3000\u3000\u6602\u7136\u8d70\u8fdb\u4e86\u9ed1\u6697\u7684\u8857\u5fc3\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u5728\u79bb\u5f00\u201c\u4f9d\u4f9d\u697c\u201d\u4e0d\u8fc7\u4e09\u6761\u8857\u9053\u4e4b\u9065\u7684\u201c\u5170\u4ead\u6c60\u5e9c\u201d\uff0c\u5218\u662f\u4e4b\u6b63\u5411\u6c60\u65e5\u66ae\u62a5\u544a\u4e86\u4e00\u4ef6\u4e8b\uff1b\u201c\u5c0f\u78a7\u6e56\u201d\u6e38\u5bb6\u5df2\u6d3e\u4eba\u5230\u201c\u4f9d\u4f9d\u697c\u201d\uff0c\u627e\u4e0a\u4e86\u65b9\u90aa\u771f\u5bc6\u8bae\u3002 \u3000\u3000\u4ea4\u8c08\u7684\u7ed3\u679c\u5982\u4f55\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\uff1b\u4f46\u7530\u6c0f\u53cc\u96c4\u662f\u4ece\u623f\u91cc\u76f4\u6eda\u4e0b\u68af\u6765\u7684\uff0c\u4e0d\u8fc7\uff0c\u53ea\u9694\u4e86\u4e00\u4f1a\u513f\u529f\u592b\uff0c\u201c\u8c79\u5b50\u201d\u7b80\u8fc5\u4ece\u623f\u91cc\u51fa\u6765\uff0c\u662f\u5e26\u7740\u7b11\u5bb9\u4ece\u5bb9\u79bb\u53bb\u7684\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u96be\u8fc7\u5730\u9053\uff1a\u201c\u65b9\u90aa\u771f\u4f1a\u4e0d\u4f1a\u5df2\u7b54\u5e94\u52a0\u76df\u5c0f\u78a7\u6e56\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5012\u4e0d\u4e00\u5b9a\uff0c\u5c0f\u78a7\u6e56\u7684\u6761\u4ef6\u5f88\u53ef\u80fd\u6bd4\u6211\u4eec\u66f4\u597d\uff0c\u201d\u5218\u662f\u4e4b\u76b1\u7740\u7709\uff0c\u772f\u7740\u773c\u9053\uff0c\u201c\u4f46\u5c0f\u78a7\u6e56\u627e\u4e0a\u4ed6\uff0c\u5343\u53f6\u5c71\u5e84\u548c\u5999\u624b\u5802\u4e5f\u5fc5\u4f1a\u627e\u4e0a\u4ed6\u7684\uff0c\u4ed6\u4eca\u5929\u4e0d\u7b54\u5e94\uff0c\u96be\u4fdd\u660e\u5929\u4e5f\u4f1a\u4e0d\u52a8\u5fc3\u2026\u2026\u201d \u3000\u3000\u4ed6\u9644\u52a0\u4e86\u4e00\u53e5\u201c\u538b\u8f74\u201d\u7684\uff1a\u201c\u7136\u800c\uff0c\u4ed6\u4e4b\u6240\u4ee5\u5ffd\u7136\u53d7\u5230\u91cd\u89c6\uff0c\u5b8c\u5168\u662f\u56e0\u4e3a\u6211\u4eec\u5148\u770b\u91cd\u4ed6\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u6101\u7709\u4e0d\u5c55\u5730\u9053\uff1a\u201c\u5148\u751f\u7684\u610f\u601d\u662f\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u4ecd\u772f\u7740\u773c\uff0c\u773c\u7f1d\u50cf\u4e24\u679d\u6a2a\u7740\u7684\u9488\uff0c\u4ed6\u7684\u8bdd\u4e5f\u50cf\u4e00\u53e3\u9488\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u5982\u4e0d\u80fd\u7528\uff0c\u4fbf\u4e0d\u80fd\u7559\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u4e00\u542c\uff0c\u5fc3\u91cc\u4e00\u9707\uff0c\u5fd9\u9053\uff1a\u201c\u5148\u751f\u53ef\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u559c\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u4f38\u51fa\u4e24\u53ea\u624b\u6307\uff1a\u201c\u65e2\u7136\u539a\u5e01\u7518\u8f9e\u3001\u8bda\u631a\u793c\u9047\uff0c\u90fd\u4e0d\u80fd\u6253\u52a8\u4ed6\uff0c\u90a3\u53ea\u5269\u4e0b\u4e24\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fd9\u9053\uff1a\u201c\u8bf7\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u4e00\u662f\u8981\u52b3\u5927\u592b\u4eba\u8d70\u4e00\u8d9f\u3002\u5927\u592b\u4eba\u867d\u975e\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4f46\u5979\u5f85\u4eba\u63a5\u7269\uff0c\u5f88\u80fd\u4e88\u4eba\u597d\u611f\uff0c\u6c60\u5e9c\u4e2d\u6709\u4e0d\u5c11\u4eba\u8a93\u6b7b\u6548\u547d\uff0c\u8bf7\u6055\u5c5e\u4e0b\u6597\u80c6\u8bf4\u4e00\u53e5\uff0c\u6709\u4e0d\u5c11\u4eba\u662f\u770b\u5728\u8fc7\u53bb\u5927\u5e84\u4e3b\u548c\u5927\u592b\u4eba\u7684\u9762\u5b50\uff1b\u5927\u592b\u4eba\u53c8\u662f\u5929\u751f\u4e3d\u8d28\uff0c\u95ed\u6708\u7f9e\u82b1\uff0c\u6c89\u9c7c\u843d\u96c1\uff0c\u8c8c\u7f8e\u5143\u53cc\uff0c\u7531\u5979\u51fa\u9762\uff0c\u65b9\u90aa\u771f\u4e5f\u662f\u4e2a\u4eba\uff0c\u662f\u4e2a\u5927\u5929\u665a\u4e0a\u90fd\u4e0a\u2018\u4f9d\u4f9d\u697c\u2019\u7684\u7537\u4eba\uff0c\u96be\u4fdd\u4e0d\u4f1a\u6539\u53d8\u4e3b\u610f\u3002\u201d\u4ed6\u8865\u5145\u4e86\u4e00\u53e5\uff1a\u201c\u8fd9\u53ef\u5f97\u8981\u845b\u94c3\u94c3\u6709\u6240\u884c\u52a8\u4e4b\u524d\u5148\u53d1\u5236\u4eba\u4e0d\u53ef\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u8138\u6709\u96be\u8272\u3002 \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u88ab\u79f0\u4e3a\u201c\u5c11\u516c\u5b50\u201d\uff0c\u4e3b\u8981\u662f\u56e0\u4e3a\u6c60\u5bb6\u7684\u5b97\u4e3b\uff0c\u672c\u662f\u5728\u957f\u4ed6\u4e94\u5c81\u7684\u80de\u5144\u4ed6\u65e5\u4e3d\u7684\u8eab\u4e0a\uff0c\u4f46\u5144\u957f\u5728\u8fce\u53d6\u5927\u5ac2\u4e4b\u540e\uff0c\u5ffd\u906d\u6b8b\u75be\uff0c\u98ce\u762b\u4e0d\u8d77\uff0c\u800c\u4eca\u8981\u4ed6\u5ac2\u5b50\u989c\u5915\u6765\u529e\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f3c\u6709\u4e9b\u4e0d\u59a5\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u751a\u4e3a\u5c0a\u91cd\u3001\u656c\u6155\u8fd9\u4f4d\u5584\u89e3\u4eba\u610f\u3002\u5584\u826f\u82f1\u6c14\u7684\u5ac2\u5b50\uff0c\u8981\u4e0d\u662f\u5979\u5728\u91cd\u8981\u5173\u5934\u633a\u8eab\u7ef4\u62a4\u6c60\u65e5\u66ae\u5728\u6c60\u5bb6\u7684\u5b97\u4e3b\u6743\uff0c\u6c60\u65e5\u66ae\u7684\u5927\u6743\uff0c\u53ef\u80fd\u65e9\u5df2\u4fdd\u4e0d\u4f4f\u4e86\u5462\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u72b9\u8c6b\u8d77\u6765\uff0c\u5ffd\u542c\u5e18\u5b50\u91cc\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u4f60\u65e2\u7136\u8ba4\u4e3a\u59d3\u65b9\u7684\u80fd\u632f\u5174\u6c60\u5bb6\u4e4b\u5927\u4e1a\uff0c\u7ed9\u4f60\u5ac2\u5b50\u53bb\u529d\u529d\u4ed6\u4e5f\u597d\u3002\u201d \u3000\u3000\u4e00\u4eba\u5750\u5728\u6728\u8f6e\u6905\u4e0a\uff0c\u81ea\u5e18\u5916\u63a8\u4e86\u8fdb\u6765\uff0c\u8138\u8272\u82cd\u81ea\uff0c\u5507\u65e0\u8840\u8272\uff0c\u8d62\u5f31\u65e0\u795e\uff0c\u8bf4\u8bdd\u4e5f\u6709\u6c14\u65e0\u529b\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u4e00\u770b\uff0c\u5728\u5144\u957f\u6905\u540e\u7684\u8fd8\u6709\u6e05\u4e3d\u82f1\u6717\u7684\u5927\u5ac2\uff0c\u5fc3\u77e5\u8fd9\u662f\u5218\u662f\u4e4b\u7684\u6446\u5e03\uff0c\u4ee5\u9632\u4ed6\u4e0d\u7b54\u5141\uff0c\u65e9\u5df2\u529d\u670d\u4e86\u5144\u957f\u9996\u80af\uff0c\u5e76\u5df2\u60ca\u52a8\u4e86\u5927\u5ac2\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u5347\u8d77\u4e00\u7247\u96be\u4f7f\u4eba\u5bdf\u89c9\u7684\u6012\u610f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5218\u662f\u4e4b\u8fd9\u90fd\u662f\u4e3a\u4ed6\u505a\u7684\u3002 \u3000\u3000\u53ef\u662f\u5f53\u4ed6\u770b\u89c1\u5218\u662f\u4e4b\u4e00\u526f\u201c\u65e9\u5df2\u5b89\u6392\u3001\u80f8\u6709\u6210\u7af9\u201d\u3001\u201c\u81ea\u4f5c\u806a\u660e\u3001\u81ea\u4ee5\u4e3a\u662f\u201d\u7684\u795e\u60c5\uff0c\u4ed6\u4fbf\u6709\u4e00\u79cd\u65e0\u4ee5\u8a00\u55bb\u7684\u605a\u6012\uff0c\u4eff\u4f5b\u88ab\u4eba\u6298\u8fb1\u3001\u595a\u843d\u4e86\u4f3c\u7684\uff1b\u4f46\u4ed6\u504f\u53c8\u77e5\u9053\u8fd9\u662f\u7528\u4eba\u4e4b\u9645\uff0c\u7591\u4eba\u4e0d\u7528\uff0c\u7528\u4eba\u4e0d\u7591\uff0c\u4ed6\u8fd9\u80a1\u79c1\u5fc3\u662f\u4e0d\u80fd\u53d1\u4f5c\uff0c\u53d1\u4f5c\u4e0d\u5f97\u7684\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u8138\u4e0a\u53ea\u9732\u51fa\u4ec4\u6127\u4e4b\u8272\uff0c\u53e3\u91cc\u53ea\u662f\u8bd5\u63a2\u5730\u9053\uff1a\u201c\u8fd9\u6837\u4e48\u2026\u2026\u4e0d\u77e5\u5ac2\u5b50\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000 \u7b2c\u4e03\u7ae0\u3000\u6df1\u78a7\u7684\u5251 \u3000 \u3000\u3000\u989c\u5915\u4fee\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u8fd9\u59d3\u65b9\u7684\u662f\u8c01\uff0c\u771f\u6709\u8fd9\u6837\u7684\u672c\u4e8b\uff1f\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u4ed6\u53eb\u65b9\u90aa\u771f\uff0c\u5e74\u7eaa\u5012\u8f7b\uff0c\u5927\u7ea6\u957f\u6211\u4e00\u4e8c\u5c81\uff0c\u4ed6\u7684\u6b66\u529f\u6781\u9ad8\uff0c\u5c0f\u767d\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\uff0c\u4ed6\u5374\u8fde\u5251\u4e5f\u672a\u62d4\u3002\u201d \u3000\u3000\u989c\u5915\u5fc3\u795e\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u662f\u7528\u5251\u7684\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u662f\u554a\uff0c\u4ed6\u6b66\u529f\u9ad8\uff0c\u5b9a\u529b\u4e5f\u5f3a\uff0c\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u5982\u5728\u6c60\u5bb6\uff0c\u5f53\u7136\u662f\u81c2\u52a9\uff0c\u82e5\u5728\u522b\u5bb6\uff0c\u53ef\u6210\u4e86\u52b2\u654c\u3002\u201d \u3000\u3000\u989c\u5915\u65e0\u5fc3\u542c\u5176\u4ed6\u7684\uff0c\u53ea\u95ee\uff1a\u201c\u4ed6\u7684\u5251\u53ef\u662f\u6df1\u78a7\u8272\u7684\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u671b\u671b\u5218\u662f\u4e4b\uff0c\u5218\u662f\u4e4b\u770b\u770b\u6c60\u65e5\u66ae\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u4eec\u8c01\u90fd\u672a\u66fe\u770b\u89c1\u8fc7\u4ed6\u62d4\u51fa\u5251\u6765\u3002\u201d \u3000\u3000\u989c\u5915\u53c8\u95ee\uff1a\u201c\u4ed6\u8155\u4e0a\u53ef\u6709\u4e00\u5bf9\u2026\u2026\u7fe0\u7389\u94dc\u5b50\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u60f3\u4e86\u4e00\u60f3\u9053\uff1a\u201c\u8fd9\u5012\u6ca1\u6709\u6ce8\u610f\u3002\u201d\u5218\u662f\u4e4b\u65ad\u7136\u9053\uff0c\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u989c\u5915\u624d\u8212\u4e86\u53e3\u6c14\u3002\u6c60\u65e5\u66ae\u5374\u60f3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4ed6\u624b\u8155\u4e0a\u5012\u6709\u2014\u2014\u201d\u4ed6\u4ed4\u7ec6\u7684\u56de\u60f3\uff0c\u7136\u540e\u51c6\u786e\u7684\u7528\u5b57\uff1a\u201c\u7cfb\u7740\u4e00\u6761\u6d45\u84dd\u8272\u7684\u4e1d\u4e2d\u3002\u201d\u4ed6\u8f6c\u8fc7\u5934\u53bb\u95ee\u5218\u662f\u4e4b\uff1a\u201c\u5bf9\u4e0d\u5bf9\uff1f\u201d\u5218\u662f\u4e4b\u8bf4\uff1a \u3000\u3000\u201c\u5bf9\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u662f\u4e1d\u4e2d\uff0c\u4e0d\u662f\u956f\u5b50\uff1f\u201d\u5218\u662f\u4e4b\u80af\u5b9a\u5730\u9053\uff1a\u201c\u662f\u6d45\u84dd\u8272\u7684\u4e1d\u4e2d\u3002\u201d\u989c\u5915\u9053\uff1a \u3000\u3000\u201c\u54e6\u3002\u201d\u6709\u70b9\u5931\u671b\u4f3c\u7684\u3002\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u5927\u5ac2\uff0c\u8fd9\u6709\u5173\u7cfb\u4e48\uff1f\u201d\u989c\u5915\u5fd9\u9053\uff1a\u201c\u6ca1\u6709\u5173\u7cfb\uff0c\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u770b\u6765\uff0c\u8fd9\u59d3\u65b9\u7684\u7528\u8f6f\u7684\u4e0d\u884c\uff0c\u53ea\u597d\u7528\u5f3a\u7684\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u5373\u9053\uff1a\u201c\u8ba9\u6211\u6765\uff0c\u6211\u53ef\u4ee5\u8bd5\u4e00\u8bd5\u3002\u201d\u6c60\u65e5\u66ae\u5fd9\u9053\uff1a\u201c\u5ac2\u5b50\u4e00\u5411\u6709\u4eba\u7f18\uff0c\u8bf4\u4e0d\u5b9a\u771f\u53ef\u4e3a\u6211\u4eec\u6c60\u5bb6\u89e3\u51b3\u4e86\u4e00\u5927\u96be\u9053\u3002\u201d\u6c60\u65e5\u4e3d\u7528\u624b\u8f7b\u8f7b\u63e1\u7740\u989c\u5915\u7684\u624b\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u53ef\u662f\u8ba9\u4f60\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u611f\u89c9\u5230\u4e08\u592b\u7626\u9aa8\u5d99\u5d99\u7684\u624b\uff0c\u60f3\u53ca\u8fc7\u53bb\u8fd9\u53cc\u624b\u66fe\u662f\u96c4\u8c6a\u6709\u529b\u7684\uff0c\u5fc3\u4e2d\u4e00\u9635\u96be\u8fc7\uff0c\u5fd9\u7528\u8bdd\u63a9\u9970\u9053\uff1a\u201c\u8fd9\u53c8\u7b97\u751a\u4e48\u8f9b\u82e6\uff01\u5f53\u5e74\u6d2a\u5144\u5f1f\u4e0d\u4e5f\u662f\u8fd9\u6837\u6536\u63fd\u8fc7\u6765\u7684\u5417\uff1f\u8fd9\u4ef6\u4e8b\u60c5\u5f53\u521d\u505a\u5f97\u5f00\u5fc3\uff0c\u73b0\u5728\u4e5f\u76f8\u5904\u5f97\u633a\u60ec\u610f\u7684\uff01\u201d \u3000\u3000\u6c60\u65e5\u66ae\u7b11\u9053\uff1a\u201c\u4e0a\u6b21\uff0c\u4f60\u7528\u4e00\u67c4\u5315\u9a73\u8f6f\u67c4\u795e\u67aa\u7559\u4f4f\u4e86\u6d2a\u4e09\u70ed\uff0c\u8fd9\u6b21\u5374\u8981\u7528\u751a\u4e48\u6765\u7559\u65b9\u90aa\u771f\uff1f\u201d \u3000\u3000\u989c\u5915\u4eae\u4e3d\u5730\u7b11\u9053\uff1a\u201c\u4e66\u3002\u201d \u3000\u3000\u6c60\u65e5\u4e3d\u3001\u4ed6\u65e5\u66ae\u4e00\u9f50\u8be7\u9053\uff1a\u201c\u4e66\uff1f\u201d \u3000\u3000\u201c\u6211\u4ee5\u524d\u6709\u4f4d\u670b\u53cb\uff0c\u8ddf\u8fd9\u4f4d\u65b9\u90aa\u771f\u6027\u60c5\u5f88\u6709\u4e9b\u63a5\u8fd1\uff0c\u4ed6\u751f\u5e73\u6240\u597d\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u5927\u623f\u7684\u53e4\u4e66\u5b57\u753b\u771f\u8ff9\uff1b\u201d\u989c\u5915\u6e05\u8106\u7684\u8bed\u97f3\u6e05\u8106\u7684\u89e3\u91ca\uff0c\u201c\u6211\u4eec\u7684\u4e66\u5e93\u91cc\u4e0d\u662f\u5c3d\u6709\u7684\u662f\u597d\u4e66\u597d\u753b\u597d\u5b57\u5417\uff1f\u4e14\u770b\u8fd9\u6cd5\u513f\u7075\u4e0d\u7075\uff01\u201d \u3000\u3000\u6c60\u65e5\u4e3d\u7b11\u9053\uff1a\u201c\u4e66\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u54c8\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e66\uff01\u201d \u3000\u3000\u5218\u662f\u4e4b\u62cd\u62cd\u540e\u8111\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6211\u6ca1\u6709\u60f3\u5230\uff1f\u5927\u592b\u4eba\u51c6\u5907\u751a\u4e48\u65f6\u5019\u53bb\uff1f\u201d \u3000\u3000\u989c\u5915\u63a8\u5f00\u4e24\u6247\u7a97\uff0c\u671b\u671b\u5929\u8272\uff1a\u201c\u90a3\u59d3\u65b9\u7684\u4f1a\u803d\u5728\u751a\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4f9d\u4f9d\u697c\u91cc\u6709\u4e00\u4e2a\u540d\u5993\uff0c\u53eb\u505a\u60dc\u60dc\uff0c\u59d3\u65b9\u7684\u591a\u7a9d\u5728\u5979\u90a3\u513f\uff0c\u4f46\u5f88\u5c11\u7559\u5bbf\uff1b\u201d\u5218\u662f\u4e4b\u9053\uff0c\u201c\u4eca\u665a\u620a\u4ea5\u65f6\u5206\uff0c\u4ed6\u5fc5\u56de\u6cd5\u95e8\u5bfa\u5927\u9690\u4e18\u7684\u8001\u5bb6\u53bb\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u6211\u4eca\u665a\u5c31\u53bb\u770b\u770b\u4ed6\u5982\u4f55\u4e09\u5934\u516d\u81c2\uff0c\u6211\u5e26\u6d2a\u5144\u5f1f\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4eca\u665a\uff1f\u201d \u3000\u3000\u989c\u5915\u62bf\u5634\u7b11\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\u561b\u3002\u201d \u3000\u3000\u6c60\u65e5\u4e3d\u594b\u4ea2\u7684\u63a8\u7740\u8f6e\u6905\uff0c\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u53bb\u4e66\u623f\u641c\u7f57\u641c\u7f57\u53bb\u3002\u201d \u3000\u3000\u989c\u5915\u770b\u89c1\u4e08\u592b\u594b\u60a6\uff0c\u4e5f\u89c9\u5f00\u5fc3\uff0c\u968f\u4ed6\u5230\u4e86\u5e18\u5b50\u4e4b\u540e\uff0c\u6c60\u65e5\u66ae\u89c1\u5218\u662f\u4e4b\u8fd8\u5728\u6478\u7740\u4e0b\u5df4\u6c89\u601d\uff0c\u4fbf\u95ee\uff1a\u201c\u4f60\u8bf4\u8fd8\u6709\u4e00\u4e2a\u6cd5\u5b50\uff0c\u662f\u751a\u4e48\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u5374\u76ee\u5149\u95ea\u52a8\uff1a\u201c\u5176\u5b9e\uff0c\u6700\u597d\u662f\u4e24\u4e2a\u6cd5\u5b50\u5e76\u65bd\uff0c\u90a3\u5c31\u4e07\u65e0\u4e00\u5931\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u542c\u51fa\u5bf9\u65b9\u4f3c\u6709\u70b9\u96be\u8a00\u4e4b\u9690\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u772f\u7740\u773c\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u8bf4\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5947\u9053\uff1a\u201c\u4f55\u89e3\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u4eec\u7740\u80e1\u811a\uff0c\u201c\u5982\u679c\u6211\u8bf4\u51fa\u6765\uff0c\u516c\u5b50\u4e07\u4e00\u4e2a\u602a\u6211\u592a\u72e0\uff0c\u6211\u53ef\u662f\u4e3a\u516c\u5b50\u5927\u4e1a\uff0c\u767d\u6328\u4e86\u51a4\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u7b11\u9053\uff1a\u201c\u54ea\u6709\u7684\u4e8b\uff01\u5148\u751f\u4e3a\u6c60\u5bb6\u5927\u5c40\u4e0d\u60dc\u6b9a\u7cbe\u7aed\u667a\u3001\u5904\u5fc3\u79ef\u8651\u7684\u60f3\u51fa\u5947\u8c0b\u5999\u8ba1\uff0c\u6211\u8981\u662f\u8bef\u89e3\u5148\u751f\u7684\u597d\u610f\uff0c\u8fd8\u662f\u4eba\u4e48\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u559f\u7136\u9053\uff1a\u201c\u516c\u5b50\u5bf9\u5c5e\u4e0b\u4e00\u5411\u4fe1\u91cd\uff0c\u5c5e\u4e0b\u4e00\u5411\u94ed\u611f\uff0c\u53ea\u6c42\u97a0\u8eac\u5c3d\u7601\uff0c\u6b7b\u800c\u540e\u5df1\uff0c\u4e0d\u8fc7\uff0c\u6211\u8fd9\u4e2a\u8ba1\u7b56\uff0c\u516c\u5b50\u8981\u662f\u900f\u9732\u51fa\u53bb\uff0c\u53ea\u6015\u96be\u514d\u8001\u547d\u4e0d\u4fdd\u2026\u2026\u201d \u3000\u3000\u6c60\u65e5\u66ae\u7b11\u9053\uff1a\u201c\u5148\u751f\u653e\u5fc3\uff0c\u6211\u4fdd\u7ba1\u4e0d\u8bf4\u51fa\u53bb\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u5ffd\u7136\u4e00\u53f9\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u5947\u9053\uff1a\u201c\u5148\u751f\u4ecd\u4e0d\u653e\u5fc3\u4e48\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u671b\u5b9a\u6c60\u65e5\u66ae\uff0c\u9053\uff1a\u201c\u6211\u5012\u4e0d\u662f\u4e0d\u653e\u5fc3\u516c\u5b50\uff0c\u800c\u662f\u8fd9\u8ba1\u7b56\u5982\u679c\u80fd\u6210\uff0c\u65b9\u90aa\u771f\u4e00\u65e6\u6295\u6548\u516c\u5b50\u95e8\u4e0b\uff0c\u53ea\u6015\u6211\u8fd9\u8001\u9aa8\u5934\u5c31\u8fde\u95e8\u69db\u90fd\u7ad9\u4e0d\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u8fd8\u9053\u662f\u4e3a\u4e86\u751a\u4e48\uff0c\u201d\u6c60\u65e5\u66ae\u8bda\u631a\u5730\u9053\uff0c\u201c\u4f60\u653e\u5fc3\uff0c\u5148\u751f\u5728\u4ed6\u5e9c\u52b3\u82e6\u529f\u9ad8\uff0c\u65b9\u90aa\u771f\u518d\u6709\u80fd\u4e3a\uff0c\u4e5f\u51b3\u4e0d\u53ef\u4ee5\u50ed\u8d8a\u8f88\u4efd\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u82e6\u7b11\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u8fd9\u8bdd\u513f\u4e00\u8bf4\uff0c\u4e00\u65e6\u4ed8\u8bf8\u884c\u52a8\uff0c\u516c\u5b50\u53ea\u8981\u5728\u4eba\u524d\u4e00\u63d0\u662f\u6211\u7684\u4e3b\u610f\uff0c\u6211\u53ef\u6210\u4e86\u9776\u5b50\u4e86\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fc3\u5fd6\uff1a\u539f\u6765\u4ed6\u8fd8\u662f\u4e0d\u653e\u5fc3\uff01\u4fbf\u4f38\u51fa\u4e24\u53ea\u624b\u6307\uff0c\u5f53\u4e0b\u8d77\u8a93\u9053\uff1a\u201c\u597d\uff0c\u5148\u751f\u65e2\u662f\u4e0d\u653e\u5fc3\uff0c\u6211\u4fbf\u5f53\u5929\u7acb\u4e2a\u8a93\u8a00\uff1a\u201c\u7687\u5929\u5728\u4e0a\uff0c\u6211\u5f85\u5148\u751f\u63a8\u5fc3\u7f6e\u8179\uff0c\u798f\u7978\u4e0e\u5171\uff0c\u5148\u751f\u4e3a\u6c60\u5bb6\u5929\u4e0b\u732e\u8ba1\uff0c\u6211\u51b3\u4e0d\u53cd\u6094\u98df\u8a00\uff0c\u8ba9\u4eba\u6000\u6028\u4e8e\u5148\u751f\uff0c\u5982\u6709\u8fdd\u80cc\uff0c\u5929\u6253\u96f7\u5288\uff0c\u4e0d\u5f97\u597d\u6b7b\uff01\u201d \u3000\u3000\u5218\u662f\u4e4b\u5f85\u4ed6\u8a93\u5b8c\uff0c\u624d\u5657\u5730\u8dea\u5730\u529d\u9053\uff1a\u201c\u516c\u5b50\u5feb\u522b\u8fd9\u6837\u8bf4\uff01\u771f\u662f\u6127\u715e\u5c5e\u4e0b\u4e86\u2026\u2026\u201d \u3000\u3000\u6c60\u65e5\u66ae\u6276\u4ed6\u8d77\u6765\uff0c\u7b11\u8bf4\uff1a\u201c\u5148\u751f\u53ef\u4ee5\u9053\u7834\u7384\u673a\u4e86\u7f62\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u6b63\u8272\u9053\uff1a\u201c\u6211\u518d\u95ee\u4e00\u6b21\uff1a\u516c\u5b50\u771f\u975e\u8981\u5f97\u65b9\u90aa\u771f\u4e4b\u52a9\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u53ef\u4e3a\u4ed6\u4eba\u6240\u5f97\uff0c\u81ea\u7136\u975e\u4e89\u53d6\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5218\u662f\u4e4b\u8083\u7136\u9053\uff1a\u201c\u4e0d\u60dc\u4ee3\u4ef7\uff01\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u4e3a\u6c42\u58ee\u58eb\uff0c\u5c82\u60dc\u4ee3\u4ef7\uff01\u201d \u3000\u3000\u5218\u662f\u4e4b\u63a8\u95e8\u63a2\u9996\uff0c\u770b\u4e86\u770b\u56db\u5468\uff0c\u7136\u540e\u63a9\u4e0a\u4e86\u95e8\uff0c\u51d1\u8fd1\u6c60\u65e5\u66ae\u8033\u8fb9\uff0c\u538b\u4f4e\u58f0\u97f3\uff0c\u4e00\u5b57\u4e00\u53e5\u5730\u9053\uff1a\u201c\u6740\u4e86\u65b9\u90aa\u771f\u5168\u5bb6\uff01\u201d \u3000\u3000\u6c60\u65e5\u66ae\u7740\u5b9e\u5413\u4e86\u4e00\u5927\u8df3\u3002 \u3000\u3000\u5218\u662f\u4e4b\u9634\u6c89\u7740\u8138\uff0c\u9053\uff1a\u201c\u53ea\u6709\u8fd9\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5931\u58f0\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u9053\uff1a\u201c\u5f53\u65e5\uff0c\u6211\u4eec\u66fe\u85c9\u6545\u6740\u9f9a\u5b9a\u5eb5\uff0c\u903c\u8d70\u4e95\u5982\u5e9c\uff0c\u4e5f\u7528\u8fc7\u6bd4\u8fd9\u66f4\u7edd\u7684\u624b\u6cd5\uff0c\u8fd9\u662f\u6ca1\u6cd5\u5b50\u7684\u4e8b\uff0c\u53ea\u662f\u6743\u5b9c\u4e4b\u8ba1\uff0c\u50cf\u65b9\u90aa\u771f\u8fd9\u79cd\u4eba\uff0c\u4e0d\u8feb\u4ed6\u662f\u4e0d\u51fa\u5c71\u7684\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u4e00\u65f6\u96be\u4ee5\u53d6\u51b3\uff0c\u5f77\u60f6\u5730\u9053\u201c\u975e\u6b64\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u68ee\u7136\u9053\uff1a\u201c\u975e\u5982\u6b64\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fc3\u4e71\u5982\u9ebb\uff1a\u201c\u53ef\u662f\u2026\u2026\u8fd9\u4e8b\u53eb\u8c01\u53bb\u505a\u662f\u597d\uff1f\u201d \u3000\u3000\u201c\u5c0f\u767d\u7edd\u5bf9\u670d\u4ece\u4f60\uff0c\u800c\u4e14\u673a\u8b66\uff0c\u5982\u7531\u4ed6\u4e0b\u624b\uff0c\u8ddf\u4ed6\u8131\u4e0d\u4e86\u5173\u7cfb\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u7b49\u5f97\u8bf4\u51fa\u6765\uff1b\u2019\u5218\u662f\u4e4b\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u4eec\u8fd8\u5f97\u627e\u4e00\u4e2a\u4eba\u6765\u8ba4\u5934\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u60da\u60da\u5730\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2026\u2026\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u772f\u7740\u9488\u773c\uff1a\u201c\u8fd9\u4ef6\u4e8b\u65e2\u662f\u6211\u4eec\u52a8\u624b\uff0c\u5c31\u8981\u5f04\u4e00\u4e2a\u5bf9\u5934\uff0c\u8ba9\u65b9\u90aa\u771f\u975e\u8ddf\u6211\u4eec\u7ed3\u5408\u6765\u627e\u4ed6\u7b97\u8d26\u4e0d\u53ef\uff01\u201d \u3000\u3000\u4ed6\u65e5\u66ae\u604d\u7136\u9053\uff1a\u201c\u56de\u767e\u54cd\uff1f\uff01\u201d \u3000\u3000\u5218\u662f\u4e4b\u9634\u9e37\u5730\u9053\uff1a\u201c\u56de\u767e\u54cd\u4e5f\u6536\u4e86\u6211\u4eec\u4e0d\u5c11\u94f6\u5b50\uff0c\u8fd9\u8be5\u662f\u4ed6\u56de\u62a5\u54cd\u5e94\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u521a\u624d\u6211\u5f00\u7a97\u770b\u5230\u4e86\u751a\u4e48\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fc3\u4e0d\u5728\u7109\u5730\u95ee\uff1a\u201c\u770b\u5230\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u5929\u6c14\u4e0d\u5927\u597d\uff0c\u6d6e\u4e91\u63a9\u76d6\u4e86\u6708\u5149\uff1b\u201d\u5218\u662f\u4e4b\u60a0\u7136\u9053\uff1a\u201c\u98ce\u662f\u5f88\u5927\u7684\uff0c\u4f46\u53ea\u8981\u4e0b\u4e0d\u6210\u96e8\uff0c\u4e00\u4e2a\u65f6\u8fb0\u540e\uff0c\u5c31\u53ef\u4ee5\u770b\u5230\u6708\u8272\u4e86\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5ffd\u7136\u89c9\u5f97\u8fd9\u8bdd\u4f3c\u4e4e\u6709\u4e9b\u8df7\u8e4a\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u2014\u2014\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u60f3\uff0c\u201d\u5218\u662f\u4e4b\u63a8\u5f00\u7a97\u5b50\uff0c\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u4f3c\u4eab\u53d7\u8fd9\u53e3\u6c14\u7684\u6e05\u9c9c\uff0c\u8d1f\u624b\u56de\u8eab\u9053\uff0c\u201c\u5728\u6708\u8272\u4e0b\uff0c\u770b\u6765\u4e00\u5411\u90fd\u4e0d\u52a8\u5fc3\u3001\u4e00\u5207\u90fd\u4e0d\u52a8\u5bb9\u7684\u65b9\u90aa\u771f\uff0c\u9047\u89c1\u5927\u592b\u4eba\uff0c\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u52a8\u5fc3\uff1f \u3000\u3000\u4f1a\u4e0d\u4f1a\u52a8\u5bb9\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5ffd\u6709\u8b66\u89c9\uff0c\u77a5\u89c1\u5218\u662f\u4e4b\u7684\u9488\u773c\uff0c\u4f3c\u770b\u7a7f\u900f\u4e86\u81ea\u5df1\uff0c\u5fc3\u91cc\u4e00\u609a\uff0c\u9053\uff1a\u201c\u5148\u751f\u4f55\u4f5c\u6b64\u8bed\uff1f\u201d \u3000\u3000\u5218\u662f\u4e4b\u7b11\u4e86\uff0c\u7b11\u5f97\u50cf\u4e00\u5934\u4fee\u70bc\u4e86\u4e09\u5343\u5e74\u7684\u72d0\u72f8\uff1a\u201c\u516c\u5b50\u5fc3\u91cc\u660e\u767d\u3002\u201d\u7136\u540e\u4ed6\u606d\u606d\u8c28\u8c28\u7684\u5411\u6c60\u65e5\u66ae\u6df1\u6df1\u4e00\u63d6\uff0c\u8fde\u8138\u4e0a\u90a3\u4e00\u70b9\u6d6e\u6ed1\u4e4b\u8272\u90fd\u5c3d\u9690\u4e0d\u89c1\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7684\u8eab\u5f71\uff0c\u6295\u5165\u4e86\u957f\u8857\u7684\u6697\u5904\u3002 \u3000\u3000\u697c\u5934\u4e0a\uff0c\u6311\u7740\u4e24\u76cf\u7ea2\u706f\u7b3c\u3002 \u3000\u3000\u60dc\u60dc\u7ad9\u5728\u5411\u665a\u8857\u53e3\u7684\u697c\u4e0a\u623f\u524d\u3002 \u3000\u3000\u5979\u80cc\u5411\u623f\u95e8\uff0c\u4f9d\u5728\u680f\u4e0a\uff0c\u773c\u5149\u9065\u9065\u7684\u843d\u5728\u8857\u4e0a\u3002 \u3000\u3000\u6e29\u6696\u7684\u706f\u5149\u9576\u5728\u5979\u8eab\u5f71\u7684\u8f6e\u5eca\u4e0a\uff0c\u67d4\u548c\u5f97\u5c31\u50cf\u4e00\u4f4d\u6df1\u60c5\u7684\u4ed9\u5973\u601d\u604b\u51e1\u5c18\u3002\u5176\u5b9e\uff0c\u5343\u53e4\u4ee5\u6765\uff0c\u6bcf\u4f4d\u771f\u60c5\u7684\u5c11\u5973\uff0c\u90fd\u66fe\u8fd9\u6837\u51dd\u76fc\u8fc7\u5979\u4eec\u8fdc\u53bb\u7684\u60c5\u90ce\uff0c\u6709\u7684\uff0c\u53bb\u4e86\u8fd8\u4f1a\u56de\u6765\uff0c\u6709\u7684\uff0c\u53bb\u4e86\u4e0d\u518d\u56de\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u77e5\u9053\u60dc\u60dc\u5728\u697c\u5934\u4e0a\u51dd\u6ce8\u7740\u4ed6\uff0c\u5e0c\u671b\u4ed6\u4e00\u4e2a\u56de\u8eab\uff0c\u4e00\u6b21\u56de\u987e\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u80fd\u56de\u8eab\u3002 \u3000\u3000\u4e0d\u80fd\u56de\u987e\u3002 \u3000\u3000\u4ed6\u6015\u81ea\u5df1\u4e00\u56de\u8eab\u5c31\u4f1a\u52a8\u601c\u3002 \u3000\u3000\u751a\u81f3\u52a8\u60c5\u3002 \u3000\u3000\u4f46\u5728\u8fd9\u65f6\u5019\uff0c\u4e0d\u7ba1\u52a8\u60c5\u6216\u52a8\u601c\uff0c\u90fd\u662f\u5251\u5ba2\u7684\u5927\u5fcc\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u5728\u8fd9\u9ed1\u6697\u7684\u8857\u9053\u4e0a\uff0c\u5df2\u6709\u7b97\u4e0d\u6e05\u7684\u52b2\u654c\u5728\u7b49\u4ed6\u5931\u795e\u3001\u5206\u5fc3\uff01 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u4e00\u4e2a\u4eba\u60f3\u8981\u7a81\u7834\u524d\u9762\u7684\u56f0\u5883\uff0c\u5c31\u4e0d\u80fd\u56de\u987e\uff01 \u3000\u3000\u4e07\u4e07\u4e0d\u80fd\u56de\u987e\u3002 \u3000\u3000\u98ce\u5f88\u5927\uff0c\u5439\u5f97\u4ed6\u8863\u8882\u730e\u730e\u98de\u821e\uff0c\u8fd9\u4e00\u5e26\u662f\u70df\u82b1\u573a\u6240\uff0c\u8fd9\u65f6\u5206\u4e0d\u53ef\u80fd\u51b7\u6e05\u82e5\u6b64\uff0c\u4f46\u8fd9\u5e7d\u5bc2\u60f3\u5fc5\u662f\u4e3a\u4e86\u81ea\u5df1\u800c\u8bbe\u7684\u7f62\uff1f\u2014\u2014\u65b9\u90aa\u771f\u7b11\u7740\uff0c\u501f\u9152\u610f\u8e09\u8dc4\u7740\u811a\u6b65\uff0c\u5531\u7740\u4e00\u9996\u60a0\u8fdc\u800c\u54c0\u4f24\u7684\u66f2\u5b50\uff0c\u7136\u540e\u4ed6\u5378\u4e0b\u4e86\u5305\u88b1\uff0c\u89e3\u4e0b\u4e86\u88f9\u5251\u7684\u84dd\u5e03\uff0c\u62e2\u5728\u8896\u5b50\u91cc\uff0c\u5411\u9ed1\u6697\u7684\u6700\u9ed1\u6697\u5904\uff0c\u6e05\u6e05\u695a\u695a\u5730\u9053\uff1a\u201c\u59d3\u56de\u7684\uff0c\u5982\u679c\u4f60\u4e0d\u7acb\u523b\u56de\u5bb6\u53bb\uff0c\u90a3\u5c31\u6eda\u51fa\u6765\u7f62\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u4e00\u8bdd\u5b8c\uff0c\u9ed1\u6697\u91cc\u4e00\u5207\u9ed1\u7684\u4e8b\u7269\uff0c\u90fd\u52a8\u4e86\u8d77\u6765\uff0c\u4e0d\u4f46\u52a8\uff0c\u800c\u4e14\u8fd8\u52a8\u5f97\u5f88\u5feb\uff0c\u52a8\u5f97\u5f88\u8be1\u5947\uff0c\u52a8\u5f97\u5f88\u53ef\u6015\uff1a \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u4eba\u3002 \u3000\u3000\u5168\u8eab\u88ab\u9ed1\u8272\u6d82\u5f97\u6f06\u6697\u7684\u4eba\uff01 \u3000\u3000\u65b9\u90aa\u771f\u9a6c\u4e0a\u53d1\u73b0\u4ed6\u524d\u3001\u540e\u3001\u5de6\u3001\u53f3\u90fd\u662f\u654c\u4eba\u3002 \u3000\u3000\u9ed1\u8272\u7684\u654c\u4eba\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u51e1\u662f\u9ed1\uff0c\u5c31\u662f\u654c\u4eba\u3002 \u3000\u3000\u654c\u4eba\u8fde\u5175\u5668\u90fd\u662f\u9ed1\u8272\u7684\u3002 \u3000\u3000\u5175\u5668\u867d\u662f\u6e05\u4e00\u8272\u7684\u9ed1\uff0c\u4f46\u5374\u6709\u5341\u516d\u516b\u79cd\u4e0d\u540c\u7684\u5175\u5668\uff0c\u751a\u81f3\u8fde\u5728\u4e00\u822c\u6b66\u6797\u4e2d\u9887\u4e3a\u5c11\u89c1\u7684\u6d41\u91d1\u6321\u3001\u8de8\u864e\u7bee\u3001\u65d2\u4e91\u62e8\u3001\u62d0\u5b50\u94a9\u90fd\u5728\u5176\u4e2d\u3002 \u3000\u3000\u800c\u4e14\u8fd8\u6709\u6697\u5668\u3002 \u3000\u3000\u8fde\u6697\u5668\u90fd\u662f\u9ed1\u8272\u7684\u3002 \u3000\u3000\u8fd9\u4e9b\u201c\u9ed1\u4eba\u201d\u5374\u4f3c\u4e4e\u6709\u4e00\u79cd\u8bc6\u8fa8\u81ea\u5df1\u4eba\u7684\u8bb0\u53f7\uff0c\u6240\u4ee5\uff0c\u6bd2\u62db\u6740\u7740\uff0c\u53ea\u5411\u65b9\u90aa\u771f\u8eab\u4e0a\u62db\u547c\uff0c\u4f46\u7edd\u5bf9\u4e0d\u4f1a\u8bef\u4f24\u4e86\u81ea\u5df1\u4eba\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e0d\u80fd\u5f80\u5730\u4e0b\u94bb\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u6709\u571f\u9041\u7684\u672c\u9886\uff0c\u4f46\u5730\u4e0b\u4f9d\u7136\u57cb\u4f0f\u7740\u654c\u4eba\u3002 \u3000\u3000\u654c\u4eba\u6839\u672c\u662f\u8981\u7f6e\u4ed6\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53ea\u6709\u5f80\u4e0a\u9661\u5347\u3002 \u3000\u3000\u4f46\u4ed6\u8eab\u5b50\u624d\u4e00\u632f\uff0c\u4e0a\u9762\u4fbf\u6709\u4e86\u58f0\u54cd\u3002 \u3000\u3000\u5899\u89d2\u3001\u5e18\u524d\u3001\u6881\u4e0a\u3001\u693d\u4e0b\u3001\u57a3\u540e\u3001\u67f1\u65c1\uff0c\u83ab\u4e0d\u662f\u57cb\u4f0f\u6709\u4eba\uff0c\u5c31\u5f85\u4ed6\u4e00\u8dc3\u800c\u8d77\u3002 \u3000\u3000\u65b9\u90aa\u771f\u957f\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u90a3\u4e9b\u201c\u9ed1\u4eba\u201d\u5df2\u906e\u706f\u853d\u6708\uff0c\u8981\u4e0d\u7136\uff0c\u4e00\u5b9a\u53ef\u4ee5\u770b\u89c1\u4ed6\u65e0\u5948\u7684\u795e\u60c5\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4ef0\u9996\u671b\u5929\u3002 \u3000\u3000\u4ed6\u4e00\u671b\u5929\u5c31\u62d4\u5251\u3002 \u3000\u3000\u6df1\u78a7\u7684\u5251\u3002 \u3000\u3000\u9ed1\u591c\u7684\u8857\u5fc3\uff0c\u6f06\u6697\u83ab\u8fa8\u7684\u5730\u65b9\uff0c\u84e6\u7136\u62b9\u8fc7\u593a\u76ee\u6676\u83b9\u7684\u78a7\u7f18\u3002 \u3000\u3000\u60dc\u60dc\u5728\u697c\u5934\u4e0a\uff0c\u770b\u89c1\u4e86\u8fd9\u4e00\u9053\u5251\u5149\u3002 \u3000\u3000\u7f8e\u4e3d\u7684\u5251\u5149\u3002 \u3000\u3000\u6d41\u661f\u822c\u7684\u5251\u5149\u3002 \u3000\u3000\u60dc\u60dc\u5ffd\u7136\u89c9\u5f97\u88ab\u4e00\u79cd\u65e0\u7531\u7684\u611f\u52a8\u6240\u5145\u6ee1\uff1a \u3000\u3000\u7edd\u4e16\u7684\u5251\u5149\u5e94\u8be5\u7528\u6765\u7167\u4eae\u7edd\u4e16\u7684\u5bb9\u989c\u7684\u3002 \u3000\u3000\u5979\u4e00\u770b\u5230\u8fd9\u9053\u5251\u5149\uff0c\u5979\u5c31\u50cf\u88ab\u6e29\u99a8\u8fce\u8138\u4e00\u51fb\uff1a\u53ea\u6709\u5979\u77e5\u9053\uff0c\u8fd9\u9053\u505a\u7edd\u5929\u4e0b\u65e0\u53ef\u6349\u6478\u7684\u5251\u5149\uff0c\u53ea\u6709\u5728\u65b9\u90aa\u771f\u7559\u5728\u201c\u79cb\u8749\u8f69\u201d\u7684\u706f\u4e0b\u629a\u638c\u628a\u73a9\uff0c\u5979\u4e5f\u66fe\u51d1\u8fc7\u8138\u53bb\uff0c\u4e3a\u90a3\u4ee4\u4eba\u9707\u98a4\u7684\u78a7\u8272\u950b\u8292\u53d1\u51fa\u7fa1\u53f9\u3002 \u3000\u3000\u2014\u2014\u8fd9\u67c4\u5929\u4e0b\u83ab\u654c\u7684\u5251\uff0c\u53ea\u6709\u5979\u770b\u8fc7\u3001\u89e6\u8fc7\u3001\u629a\u8fc7\u3001\u7231\u8fc7\uff0c\u5728\u591c\u6df1\u4eba\u9759\u65f6\uff0c\u6ce8\u89c6\u5b83\u7684\u7f8e\uff0c\u5206\u4eab\u5b83\u7684\u5bc2\u5bde\u3002 \u3000\u3000\u60dc\u60dc\u4e00\u5ff5\u53ca\u6b64\uff0c\u89c9\u5f97\u8138\u4e0a\u90fd\u70e7\u70ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u62ff\u7740\u6c34\u76c6\uff0c\u4e00\u515c\u8111\u513f\uff0c\u76f8\u5fd8\u4e8e\u6c5f\u6e56\u822c\u7684\u6cfc\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6c34\u82b1\uff0c\u6c34\u82b1\u3002 \u3000\u3000\u5728\u9ed1\u6f06\u91cc\u7565\u6620\u7740\u6676\u83b9\uff0c\u8fc5\u5373\u6ca1\u5165\u9edd\u6697\u91cc\u3002 \u3000\u3000\u6c34\u82b1\u6c34\u82b1\u3002 \u3000\u3000\u7f8e\u4e3d\u7684\u6c34\u82b1\u3002 \u3000\u3000\u7eda\u707f\u7684\u5251\u82b1\u3002 \u3000\u3000\u5251\u82b1\u8d77\uff0c\u9ed1\u6697\u91cc\u7684\u4eba\u90fd\u6d6a\u5206\u6d9b\u88c2\u7684\u5f00\u4e86\u4e00\u6761\u8def\uff0c\u65b9\u90aa\u771f\u8eab\u5f71\u6a2a\u7a7a\u800c\u8d77\uff0c\u8fce\u4e0a\u4e86\u6c34\u82b1\u3002 \u3000\u3000\u4ed6\u5728\u6c34\u82b1\u91cc\u626c\u8896\u6a2a\u626b\u3002 \u3000\u3000\u6c34\u82b1\u98de\u6e85\u3002 \u3000\u3000\u6c34\u82b1\u4f3c\u5343\u767e\u51b0\u523a\u822c\u7684\u6697\u5668\uff0c\u5c04\u5f80\u201c\u9ed1\u4eba\u201d\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u9ed1\u4eba\u201d\u60e8\u547c\u3001\u54c0\u568e\uff0c\u4e00\u65f6\u95f4\u7eb7\u7eb7\u6ca1\u5165\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u9ed1\u6697\u53c8\u6210\u4e86\u9ed1\u6697\u3002 \u3000\u3000\u9ed1\u6697\u91cc\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u6d6e\u4e91\u63a9\u6620\uff0c\u5c42\u4e91\u4e0b\u7684\u6708\u4eae\u9690\u9690\u5fae\u660e\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7b11\u4e86\uff0c\u4ed6\u7684\u5251\u53c8\u6536\u56de\u9798\u91cc\uff0c\u4ed6\u5728\u54fc\u4e00\u9996\u66f2\u5b50\uff0c\u628a\u66f2\u5b50\u54fc\u5230\u544a\u4e00\u6bb5\u843d\u4e4b\u540e\uff0c\u624d\u6beb\u4e0d\u5728\u610f\u5730\u9053\uff1a\u201c\u5982\u679c\u4f60\u662f\u56de\u4e07\u96f7\uff0c\u5c31\u7559\u4e0b\u4e24\u6761\u80f3\u81c2\u6765\uff0c\u5982\u679c\u6765\u7684\u662f\u56de\u767e\u54cd\uff0c\u7559\u4e00\u6761\u624b\u81c2\u5c31\u591f\u4e86\u3002\u201d\u4ed6\u671b\u671b\u5929\u8272\u9053\uff1a\u201c\u4f60\u4eec\u6240\u4f5c\u7684\u6076\u5b7d\uff0c\u5176\u5b9e\uff0c\u7559\u4e0b\u4e00\u767e\u6761\u80f3\u81c2\u90fd\u96be\u8d4e\u5176\u8f9c\u3002\u201d \u3000 \u7b2c\u516b\u7ae0\u3000\u90a3\u4e00\u523b\u7684\u5fc3\u52a8 \u3000 \u3000\u3000\u53ea\u542c\u9ed1\u6697\u91cc\uff0c\u4e00\u4eba\u68ee\u51b7\u5730\u9053\uff1a\u201c\u65b9\u90aa\u771f\uff0c\u4f60\u4ee5\u4e3a\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u7709\u6bdb\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e00\u5411\u4ee5\u4e3a\u559c\u6b22\u8eb2\u5728\u9ed1\u6697\u91cc\u7684\u5927\u90e8\u662f\u8017\u5b50\u548c\u8759\u8760\u90a3\u4e00\u7c7b\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u4e00\u8bf4\uff0c\u5c31\u770b\u89c1\u4e00\u5f20\u8138\u3002 \u3000\u3000\u4e00\u5f20\u4eba\u8138\u3002 \u3000\u3000\u4e00\u5f20\u4e0d\u50cf\u4eba\u7684\u4eba\u8138\u3002 \u3000\u3000\u8fd9\u5f20\u8138\u5176\u5b9e\u5e76\u4e0d\u4e11\u964b\uff0c\u4e94\u5b98\u4e5f\u76f8\u5f53\u7aef\u6b63\uff0c\u800c\u4e14\u8fd8\u76f8\u5f53\u5e74\u8f7b\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u5f20\u8138\u4e88\u4eba\u4e00\u79cd\u4e0d\u6b63\u5e38\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u7684\u7709\u6bdb\u7c97\u6d53\uff0c\u4f46\u6839\u6839\u7709\u6bdb\u901a\u4e71\uff1b\u4ed6\u7684\u8138\u8272\u60e8\u767d\uff0c\u5c31\u4f3c\u6d82\u4e0a\u4e86\u4e00\u5c42\u539a\u539a\u7684\u7c89\u5723\uff1b\u4ed6\u7684\u5634\u5507\u7d2b\u7ea2\u5e72\u71e5\u3001\u5507\u89d2\u5b8c\u5168\u4e0b\u5f2f\u3001\u518d\u7d27\u7d27\u7684\u62bf\u5408\uff1b\u4ed6\u7684\u773c\u795e\u6dec\u5389\uff0c\u5374\u4f3c\u628a\u6700\u540e\u4e00\u70b9\u5149\u534e\u90fd\u8981\u5728\u77ac\u95f4\u8017\u5c3d\uff1b\u4ed6\u6ee1\u816e\u80e1\u78b4\u5b50\uff0c\u6839\u6839\u5982\u523a\uff1b\u4ed6\u6563\u53d1\u84ec\u4e71\uff0c\u504f\u504f\u53d1\u4e0a\u53c8\u6234\u7740\u91d1\u7b8d\u3001\u4f69\u7389\uff0c\u4ed6\u7b11\u8d77\u6765\u7684\u65f6\u5019\u521a\u521a\u624d\u201c\u50cf\u4eba\u201d\u4e00\u4e9b\uff0c\u5374\u53c8\u9732\u51fa\u767d\u68ee\u68ee\u7684\u9510\u9f7f\u3002\u8fd8\u6709\u4e00\u5f20\u8840\u76c6\u5927\u53e3\u3002 \u3000\u3000\u8fd9\u5f20\u8138\u4ee4\u4eba\u5370\u8c61\u6700\u6df1\u523b\u7684\u4e0d\u662f\u4ed6\u7684\u8f6e\u5eca\u3001\u4ed6\u7684\u4e94\u5b98\u3001\u751a\u6216\u662f\u4ed6\u90a3\u4e00\u53ea\u6709\u4e00\u4e2a\u62f3\u773c\u822c\u5927\u88c2\u7eb9\u7684\u9f3b\u6881\u3002 \u3000\u3000\u800c\u662f\u4ed6\u8138\u4e0a\u5e03\u6ee1\u4e86\u9752\u7b4b\u3002 \u3000\u3000\u50cf\u5730\u56fe\u4e0a\u6cb3\u6d41\u7684\u5206\u5e03\u4e00\u822c\uff0c\u9519\u7efc\u590d\u6742\u7684\u5e03\u6ee1\u5728\u8fd9\u5f20\u5e74\u8f7b\u7684\u8138\u4e0a\uff0c\u4f7f\u4ed6\u770b\u6765\u50cf\u4e2a\u6050\u6016\u7684\u4eba\u3002 \u3000\u3000\u4eba\uff0c\u672c\u6765\u5c31\u8eb2\u5728\u9ed1\u6697\u4e4b\u4e2d\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5f00\u53e3\u8ba5\u8bbd\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u62b9\u4e0b\u8138\u4e0a\u7684\u9ed1\u5e03\u3002 \u3000\u3000\u6708\u4eae\u521a\u81ea\u4e91\u5c42\u91cc\u95f2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6708\u5149\u6b63\u597d\u5728\u4ed6\u8138\u4e0a\u4e00\u6620\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u6708\u8272\u6709\u77e5\uff0c\u6562\u60c5\u4e5f\u4f1a\u88ab\u8fd9\u5f20\u8138\u5b54\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5374\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u7740\u8bf4\uff1a\u201c\u539f\u6765\u662f\u56de\u7edd\u3002\u201d \u3000\u3000\u9ed1\u8863\u767d\u8138\u9752\u5e74\u68ee\u7136\u7b11\u9053\uff1a\u201c\u4f60\u5bb3\u6015\u4e86\u4e48\uff1f\u201d\u4ed6\u5c31\u662f\u201c\u8001\u516c\u5b50\u201d\u56de\u767e\u5e94\u7684\u72ec\u5b50\u56de\u7edd\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u592a\u8d2a\u529f\u4e86\u3002\u201d \u3000\u3000\u56de\u7edd\u7684\u773c\u775b\u91cc\u5e03\u6ee1\u8840\u4e1d\uff0c\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u751a\u4e48\uff1f\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff0c\u201c\u8fd9\u4e00\u5b9a\u4e0d\u662f\u4f60\u7236\u4eb2\u7684\u4e3b\u610f\u3002\u4f60\u7236\u4eb2\u89c1\u6211\u6765\u4e86\uff0c\u8fd8\u672a\u6253\u5b9a\u4e3b\u610f\u7a76\u610f\u8981\u6536\u63fd\u6211\u8fd8\u662f\u8981\u9664\u6389\u6211\uff0c\u72b9\u5728\u4e3e\u68cb\u4e0d\u5b9a\uff0c\u4f60\u4e0d\u670d\u6c14\uff0c\u8981\u6765\u6740\u6389\u6211\uff0c\u597d\u8bc1\u5b9e\u7ed9\u4f60\u8001\u5b50\u77e5\u9053\uff0c\u4f60\u81ea\u5df1\u5c31\u662f\u4eba\u624d\uff0c\u56de\u5bb6\u6839\u672c\u5c31\u4e0d\u9700\u8981\u53e6\u5916\u62db\u89c5\u4eba\u624d\u3002\u201d \u3000\u3000\u56de\u7edd\u7684\u5389\u76ee\u53d8\u5f97\u8be7\u7136\uff0c\u6012\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u5c31\u662f\u4eba\u624d\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u662f\u751f\u6c14\u4f60\u8001\u7239\u770b\u4e0d\u89c1\u4f60\u3002\u201d \u3000\u3000\u56de\u7edd\u6068\u610f\u5165\u9aa8\u5730\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u8981\u6740\u6389\u4f60\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u96be\u9053\u8981\u628a\u4f60\u8001\u5b50\u773c\u4e2d\u7684\u4eba\u624d\u5168\u90fd\u6740\u5149\u4e0d\u6210\uff1f\u201d \u3000\u3000\u56de\u7edd\u72de\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5c3d\u7136\uff0c\u5982\u679c\u4ed6\u4eec\u670d\u4ece\u6211\uff0c\u4e0d\u4f46\u4f1a\u6709\u6d3b\u8def\uff0c\u800c\u4e14\u5927\u6709\u524d\u7a0b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u56de\u7edd\u5947\u9053\uff0c\u201c\u660e\u767d\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u4e3a\u751a\u4e48\u4ee5\u56de\u767e\u5e94\u7684\u7cbe\u660e\u5f3a\u5e72\u3001\u4e0d\u4e16\u6b66\u529f\uff0c\u5c45\u7136\u6ca1\u5565\u53ef\u7528\u4e4b\u4eba\uff0c\u800c\u4e14\u8fd1\u5e74\u6765\u7684\u58f0\u52bf\uff0c\u5df2\u8fdc\u843d\u4e8e\u2018\u5c0f\u78a7\u6e56\u6e38\u5bb6\u2019\u4e4b\u540e\uff0c\u4e14\u6e10\u4e3a\u2018\u5170\u4ead\u6c60\u5bb6\u2019\u8d76\u4e0a\uff0c\u5c31\u7b97\u6bd4\u8bf8\u4e8e\u2018\u5343\u53f6\u5c71\u5e84\u845b\u5bb6\u2019\uff0c\u4e5f\u597d\u4e0d\u4e86\u591a\u5c11\u2026\u2026\u539f\u6765\uff0c\u56de\u767e\u5e94\u819d\u4e0b\u6709\u8fd9\u6837\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u56de\u7edd\u76ee\u5149\u8d64\u7ea2\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u751a\u4e48\uff1f\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u804b\u5b50\uff1f\uff01\u624d\u8bf4\u4e86\u4e03\u516b\u53e5\u8bdd\uff0c\u4f60\u95ee\u4e86\u4e24\u6b21\u8fd9\u79cd\u65e0\u804a\u8bdd\uff01\u201d \u3000\u3000\u56de\u7edd\u54ac\u7259\u5207\u9f7f\u5730\u9053\uff1a\u201c\u6211\u8981\u6740\u6389\u4f60\uff0c\u6211\u4e00\u5b9a\u8981\u6740\u6389\u4f60\uff0c\u6211\u8981\u4f60\u5c1d\u5c1d\u6211\u7684\u624b\u6bb5\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u4f3c\u60f3\u8d77\u4e00\u4e8b\uff0c\u9053\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4eec\u2018\u5999\u624b\u5802\u56de\u5bb6\u2019\u6709\u4e24\u95e8\u7edd\u827a\uff0c\u53eb\u505a\u2018\u56de\u5929\u4e4f\u672f\u2019\u548c\u2018\u5999\u624b\u56de\u6625\u2019\uff0c\u4e00\u4e2a\u662f\u533b\u4eba\u7684\u7edd\u6d3b\u513f\uff0c\u4e00\u4e2a\u662f\u6740\u4eba\u7684\u7edd\u62db\u3002 \u3000\u3000\u56de\u7edd\u8138\u4e0a\u7684\u9752\u7b4b\u90fd\u4f3c\u5728\u8dc3\u52a8\uff1a\u201c\u4f60\u5f85\u4f1a\u513f\u5c31\u53ef\u4ee5\u8bd5\u8bd5\u3002\u6211\u6740\u4e86\u4f60\u518d\u533b\u597d\u4f60\uff0c\u533b\u597d\u4f60\u518d\u6740\u4f60\uff0c\u8ba9\u4f60\u4e00\u4e2a\u4eba\u80fd\u5c1d\u5230\u6b7b\u5341\u6b21\u516b\u6b21\u7684\u6ecb\u5473\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u542c\u8bf4\u2018\u56de\u5929\u4e4f\u672f\u2019\u4e00\u5171\u53ea\u6709\u516d\u5f0f\uff0c\u4f46\u5df2\u63c9\u5408\u4e86\u5341\u4e00\u5927\u95e8\u6d3e\u7684\u4e09\u5341\u4e5d\u79cd\u6700\u7280\u5229\u7684\u7edd\u62db\uff0c\u53e6\u5916\u8fd8\u8574\u542b\u4e86\u5341\u4e5d\u79cd\u6b63\u90aa\u5939\u6742\u521a\u67d4\u5e76\u91cd\u7684\u5185\u529b\uff0c\u5982\u679c\u516d\u5f0f\u4ff1\u6210\uff0c\u4e00\u65e6\u53d1\u52a8\uff0c\u5c31\u7b97\u662f\u5f53\u5e74\u53f1\u54a4\u4eac\u57ce\u7684\u2018\u91d1\u98ce\u7ec6\u96e8\u697c\u2019\u697c\u4e3b\u82cf\u68a6\u6795\u4eb2\u81f3\uff0c\u4e5f\u672a\u5fc5\u62b5\u6321\u5f97\u4f4f\u3002\u201d \u3000\u3000\u56de\u7edd\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u6253\u63a2\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u5374\u6709\u4e00\u70b9\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u56de\u7edd\u505a\u7136\u9053\uff1a\u201c\u8d81\u4f60\u8fd8\u80fd\u8bf4\u5f97\u51fa\u8bdd\u6765\u7684\u65f6\u5019\uff0c\u53ef\u4ee5\u8bf7\u6559\u6211\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u50cf\u8fd9\u6837\u6df1\u5965\u7684\u6b66\u529f\uff0c\u50cf\u4f60\u8fd9\u4f60\u79cd\u4eba\uff0c\u80fd\u5b66\u6210\u51e0\u62db\uff1f\u201d \u3000\u3000\u56de\u7edd\u72c2\u6012\uff0c\u5486\u54ee\u9053\uff1a\u201c\u59d3\u65b9\u7684\uff0c\u6211\u6559\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e0d\u614c\u4e0d\u5fd9\u5730\u9053\uff1a\u201c\u8bf4\u5230\u4f60\u7684\u5389\u5bb3\uff0c\u6211\u6b63\u60f3\u77e5\u9053\uff1a\u542c\u8bf4\u4f60\u5f88\u559c\u6b22\u634f\u788e\u4eba\u7684\u9aa8\u5934\uff0c\u4e00\u6839\u4e00\u6839\u7684\u634f\u788e\uff0c\u76f4\u81f3\u4ed6\u75db\u6b7b\u4e3a\u6b62\uff1f\u201d \u3000\u3000\u56de\u7edd\u8138\u4e0a\u7684\u9752\u7b4b\u53c8\u4e00\u7a81\u4e00\u7a81\u7684\u8df3\u8dc3\u7740\uff0c\u773c\u775b\u95ea\u7740\u4e00\u6f20\u90aa\u5149\uff1a\u201c\u9519\u4e86\uff0c\u4e0d\u662f\u75db\u6b7b\uff0c\u800c\u662f\u5413\u6b7b\u3002\u6709\u4e00\u4e2a\u5f97\u7f6a\u8fc7\u6211\u7684\u4eba\uff0c\u88ab\u6211\u5413\u5f97\u6492\u4e86\u516b\u6b21\u7caa\uff0c\u624d\u5413\u7834\u80c6\u800c\u6b7b\u3002\u6211\u6740\u4e00\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u4e00\u5411\u9ad8\u5174\u624d\u6740\uff0c\u800c\u4e14\u559c\u6b22\u4ece\u4ed6\u7684\u6700\u4e0d\u91cd\u8981\u7684\u90e8\u5206\u634f\u8d77\uff0c\u8b6c\u5982\u4ece\u5c0f\u6307\u5934\u3002\u8033\u9aa8\u3001\u777e\u4e38\u634f\u8d77\uff0c\u4e00\u5206\u5206\u3001\u4e00\u5bf8\u5bf8\u7684\u634f\u788e\uff0c\u90a3\u8868\u60c5\u771f\u662f\u597d\u770b\u6781\u4e86\u3002\u6211\u4e0d\u9ad8\u5174\u7684\u65f6\u5019\uff0c\u5c31\u4e0d\u6740\uff0c\u7559\u4ed6\u5728\u90a3\u513f\uff0c\u7b49\u6211\u9ad8\u5174\u7684\u65f6\u5019\uff0c\u53c8\u8fc7\u53bb\u634f\u4ed6\u4e00\u4e24\u6839\u9aa8\u5934\u3002\u6709\u4e00\u4e2a\u4e0d\u542c\u8bdd\u7684\u5c0f\u59be\uff0c\u6211\u634f\u788e\u5979\u4e03\u516b\u6839\u9aa8\u5934\uff0c\u5c31\u628a\u5979\u7ed9\u5fd8\u4e86\uff0c\u9501\u4e86\u4e24\u4e09\u4e2a\u6708\uff0c\u5ffd\u53c8\u8bb0\u8d77\u4e86\u5979\uff0c\u8fc7\u53bb\u770b\u65f6\uff0c\u5979\u7684\u788e\u9aa8\u5c45\u7136\u53c8\u75ca\u5408\u4e86\uff0c\u6211\u518d\u8fc7\u53bb\u91cd\u65b0\u634f\u788e\uff0c\u8fd9\u6837\u788e\u4e86\u53c8\u5408\u3001\u5408\u4e86\u53c8\u788e\uff0c\u8db3\u8db3\u628a\u5979\u2018\u634f\u2019\u4e86\u4e00\u5e74\u53c8\u4e09\u4e2a\u6708\uff0c\u624d\u628a\u5979\u2018\u634f\u2019\u6b7b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u8138\u4e0a\u6e10\u6e10\u715e\u767d\u3002 \u3000\u3000\u4ed6\u4e00\u5b57\u4e00\u5b57\u5730\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5f88\u559c\u6b22\u5978\u6c61\u5973\u4eba\uff1f\u201d \u3000\u3000\u56de\u7edd\u8138\u4e0a\u7adf\u5145\u6ee1\u4e86\u5f97\u610f\u4e4b\u8272\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u76ee\u5149\u7684\u6092\u8272\uff0c\u5df2\u5316\u4f5c\u5bd2\u610f\uff1a\u201c\u542c\u8bf4\u4f60\u66f4\u559c\u6b22\u6740\u5973\u4eba\uff1f\u201d \u3000\u3000\u56de\u7edd\u5c45\u7136\u5b09\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u5728\u6740\u4eba\u7684\u65f6\u5019\uff0c\u542c\u5979\u4eec\u5a49\u8f6c\u54c0\u557c\uff0c\u770b\u5979\u4eec\u75db\u4e0d\u6b32\u751f\u7684\u8868\u60c5\uff0c\u662f\u4ef6\u7edd\u5999\u7684\u4eab\u53d7\uff01\u201d\u4ed6\u90a3\u5f20\u626d\u66f2\u7684\u8138\u4e00\u65e6\u563b\u7b11\u4e4b\u9645\uff0c\u770b\u53bb\u5c31\u4f3c\u75af\u5b50\u4e00\u822c\u3002\u4e00\u4e2a\u6708\u4e0b\u7684\u75af\u5b50\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fae\u53f9\u4e00\u58f0\uff0c\u6447\u624b\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u822c\u8bf4\u6cd5\uff0c\u6211\u771f\u7684\u4e0d\u80fd\u7559\u4e0b\u4f60\u4e00\u6761\u80f3\u81c2\uff0c\u6216\u4e24\u53ea\u624b\u638c\u3002\u4e0d\u80fd\u3002\u201d \u3000\u3000\u4ed6\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u628a\u8bdd\u5410\u51fa\u6765\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u4eba\uff0c\u6211\u53ea\u8981\u7559\u4e0b\u4f60\u5c11\u4e00\u70b9\uff0c\u90fd\u662f\u5bf9\u4e0d\u8d77\u6211\u81ea\u5df1\uff0c\u5bf9\u4e0d\u8d77\u6b7b\u53bb\u7684\u8fd9\u8bb8\u591a\u51a4\u9b42\u3002\u201d \u3000\u3000\u56de\u7edd\u9f87\u8d77\u4e86\u7259\u9f7f\u3002 \u3000\u3000\u4ed6\u7684\u6307\u9aa8\u5df2\u634f\u54cd\u3002 \u3000\u3000\u957f\u8857\u91cc\u5ffd\u7136\u54cd\u8d77\u4e00\u79cd\u6a61\u5b9d\u7206\u88c2\u822c\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u79cd\u58f0\u97f3\u5f88\u50cf\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6781\u50cf\u3002 \u3000\u3000\u73b0\u5728\u5df2\u6709\u6708\u8272\u3002 \u3000\u3000\u6708\u8272\u6a21\u7cca\u5f97\u5c31\u50cf\u6628\u591c\u7684\u68a6\uff0c\u64a9\u52a8\u7a97\u7eb1\u7684\u98ce\u3002 \u3000\u3000\u6708\u8272\u4e0d\u80fd\u8ba9\u957f\u8857\u7684\u666f\u8c61\u6e05\u6670\u4eba\u76ee\uff0c\u4f46\u81f3\u5c11\u53ef\u4ee5\u770b\u5230\u4e24\u4e2a\u5f71\u5b50\uff1a \u3000\u3000\u4e00\u9ed1\u4e00\u767d\u3002 \u3000\u3000\u56db\u5468\u90fd\u662f\u9ed1\u9edd\u7684\u6697\u5f71\u3002 \u3000\u3000\u5ffd\u7136\u9ed1\u5f71\u5b50\u547c\u5578\uff0c\u75be\u63a0\u4e86\u8d77\u6765\uff0c\u50cf\u4e00\u9635\u9f99\u5377\u98ce\u3002 \u3000\u3000\u9f99\u5377\u98ce\u6240\u8fc7\u4e4b\u5904\uff0c\u4efb\u4f55\u4e8b\u7269\u90fd\u8981\u88ab\u6bc1\u706d\u3002 \u3000\u3000\u5b8c\u5168\u4e0d\u80fd\u62b5\u6321\u7684\u6bc1\u706d\u3002 \u3000\u3000\u9ed1\u5f71\u5316\u4e3a\u9ed1\u98ce\u3002 \u3000\u3000\u9ed1\u98ce\u8f6c\u4e3a\u72c2\u98da\u3002 \u3000\u3000\u72c2\u98da\u8d8a\u65cb\u8d8a\u6025\uff0c\u8d8a\u8f6c\u8d8a\u5feb\u3002\u4f46\u8303\u56f4\u8d8a\u6765\u8d8a\u5927\u3002 \u3000\u3000\u767d\u5f71\u6108\u6e10\u7f29\u5c0f\uff0c\u5728\u9ed1\u6697\u7684\u6f29\u6da1\u91cc\uff0c\u5feb\u8981\u5b8c\u5168\u88ab\u541e\u566c\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u60dc\u60dc\u4e00\u5411\u4fe1\u4efb\u65b9\u90aa\u771f\u3002 \u3000\u3000\u4ed6\u8bf4\u6709\u529e\u6cd5\u89e3\u51b3\uff0c\u5929\u5927\u7684\u56f0\u96be\u90fd\u4f1a\u6709\u529e\u6cd5\u89e3\u51b3\u7684\u3002 \u3000\u3000\u2014\u2014\u4f46\u73b0\u5728\u7684\u60c5\u5f62\uff0c\u65b9\u90aa\u771f\u5c31\u7b97\u6709\u529e\u6cd5\uff0c\u4e5f\u89e3\u51b3\u4e0d\u4e86\u3002 \u3000\u3000\u51c6\u80fd\u89e3\u51b3\u5f97\u4e86\u9f99\u5377\u98ce\uff1f \u3000\u3000\u60dc\u60dc\u4e07\u5206\u7740\u6025\uff0c\u8fd9\u4e00\u4e0b\u4e0e\u4e0b\u4e00\u4e0b\u5fc3\u8df3\u4e4b\u95f4\u7d27\u5bc6\u5f97\u50cf\u8ff8\u51fa\u4e86\u706b\u82b1\u3002 \u3000\u3000\u5979\u6025\u5f97\u53c8\u60f3\u638f\u4e00\u76c6\u6c34\u5f80\u8857\u5fc3\u6dcb\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u521a\u624d\u7684\u4e00\u76c6\u6c34\u80fd\u52a9\u5f97\u4e86\u65b9\u90aa\u771f\uff0c\u73b0\u5728\u8fd8\u884c\u4e0d\u884c\uff1f \u3000\u3000\u60dc\u60dc\u89c9\u5f97\u8fd9\u662f\u6ca1\u6709\u529e\u6cd5\u4e2d\u7684\u529e\u6cd5\u3002 \u3000\u3000\u53ea\u8981\u80fd\u5bf9\u65b9\u90aa\u771f\u6709\u6240\u5e2e\u52a9\uff0c\u65e0\u8bba\u751a\u4e48\u5979\u90fd\u613f\u610f\u53bb\u505a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5ffd\u7136\u6709\u4eba\u5728\u5979\u8eab\u8fb9\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4f60\u8981\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u60dc\u60dc\u8f6c\u8fc7\u5934\u53bb\uff0c\u5c31\u770b\u89c1\u4e00\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u201c\u9ed1\u4eba\u201d\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5168\u8eab\u90fd\u7a7f\u7740\u9ed1\u8272\u9c7c\u76ae\u7d27\u9760\u52b2\u88c5\uff0c\u5c31\u8fde\u9f3b\u4e86\u901a\u98ce\u5c0f\u5b54\uff0c\u8fd8\u6709\u4e00\u5bf9\u6c11\u775b\uff0c\u90fd\u9ed1\u7cca\u7cca\u4e00\u7247\uff0c\u7adf\u4f3c\u8fde\u773c\u767d\u4e5f\u65e0\uff01 \u3000\u3000\u8fd9\u4eba\u624b\u4e0a\u62ff\u4e86\u4e00\u6839\u6448\u94c1\u6756\uff0c\u5f53\u7136\u4e5f\u662f\u9ed1\u8272\u7684\u3002 \u3000\u3000\u8fd9\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u56de\u516c\u5b50\u8981\u4f60\uff0c\u8d70\uff01\u201d \u3000\u3000\u60dc\u60dc\u4e00\u542c\uff0c\u5fc3\u7edd\u5982\u88c2\uff0c\u843d\u5728\u56de\u7edd\u624b\u91cc\uff0c\u771f\u4e0d\u5982\u901f\u6b7b\uff0c\u5979\u60f3\u5f80\u540e\u9000\uff0c\u5ffd\u89c9\u649e\u5728\u4e00\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u60ca\u53eb\u56de\u9996\uff0c\u53ea\u89c1\u53c8\u662f\u4e00\u540d\u201c\u9ed1\u4eba\u201d\u3002 \u3000\u3000\u8fd9\u201c\u9ed1\u4eba\u201d\u624b\u4e0a\u62ff\u7740\u5200\u3002 \u3000\u3000\u9ed1\u8272\u7684\u5200\u3002 \u3000\u3000\u8981\u4e0d\u662f\u4ed6\u6709\u5934\u6709\u624b\u4e5f\u6709\u811a\uff0c\u800c\u4e14\u623f\u5185\u7684\u706f\u5149\u9690\u7167\uff0c\u8981\u4e0d\u7136\uff0c\u5728\u591c\u8272\u91cc\uff0c\u4ed6\u5c31\u662f\u591c\u8272\uff0c\u4e0d\u53ef\u80fd\u5224\u522b\u5f97\u51fa\u8fd9\u7adf\u662f\u4e00\u4e2a\u201c\u4eba\u201d\uff01 \u3000\u3000\u8fd9\u540e\u9762\u7684\u201c\u9ed1\u4eba\u201d\u4e5f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4f60\u6700\u597d\u522b\u60f3\u81ea\u6740\uff0c\u516c\u5b50\u8981\u4f60\u6d3b\u7740\u53bb\u89c1\u4ed6\uff0c\u4f60\u8981\u662f\u6b7b\u4e86\uff0c\u6211\u4eec\u4e5f\u522b\u60f3\u6d3b\u4e86\u3002\u201d \u3000\u3000\u60dc\u60dc\u53ea\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u5979\u51b3\u5fc3\u8981\u6b7b\u3002 \u3000\u3000\u5979\u53ea\u60f3\u5f80\u697c\u4e0b\u8df3\u53bb\uff0c\u649e\u7740\u56de\u7edd\uff0c\u8ba9\u65b9\u90aa\u771f\u7f13\u5f97\u4e00\u53e3\u6c14\uff0c\u5979\u8fd9\u6837\u6b7b\u4e5f\u7b97\u503c\u5f97\u3002 \u3000\u3000\u5979\u59d4\u5a49\u4ed6\u8bf4\uff1a\u201c\u597d\u5427\u2026\u2026\u201d\u624b\u4e2d\u90a3\u76c6\u6c34\uff0c\u5ffd\u7136\u5411\u524d\u9762\u90a3\u4eba\u515c\u5934\u515c\u8138\u5c31\u6dcb\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7136\u540e\u5979\u8d34\u5728\u680f\u6746\u4e4b\u4e0a\uff0c\u51c6\u5907\u7ffb\u843d\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u5728\u5979\u4e00\u671b\u4e4b\u4e0b\uff0c\u5374\u662f\u6014\u4e86\u6014\uff1a \u3000\u3000\u6726\u80e7\u7684\u6708\u8272\u4e0b\uff0c\u6ca1\u6709\u4e86\u767d\u5f71\uff0c\u4e5f\u6ca1\u6709\u9ed1\u5f71\uff0c\u53ea\u6709\u4e00\u62b9\u707f\u4eae\u7684\u706b\u82b1\uff0c\u4f3c\u7ffb\u6eda\u3002\u4f3c\u8f97\u8f6c\u3001\u4f46\u80af\u5b9a\u8fc5\u75be\u7684\u8d8a\u5165\u4e86\u8fdc\u5904\u7684\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u201c\u4f60\u5728\u680f\u4e0a\uff0c\u4e00\u89c1\u7740\u7eff\u8272\u7684\u5251\u5149\u98de\u4e0a\u4e86\u5929\uff0c\u7acb\u5373\u5012\u4e00\u76c6\u6c34\u4e0b\u6765\uff1b\u5982\u679c\u4f60\u770b\u89c1\u8857\u5fc3\u6709\u4e00\u56e2\u706b\u5149\u63a0\u8fc7\uff0c\u4fbf\u7b49\u4e8e\u544a\u8bc9\u4f60\uff1b\u6211\u6b63\u8981\u56de\u5bb6\u7761\u5927\u89c9\u3002\u201d\u60dc\u60dc\u8bb0\u5f97\u65b9\u90aa\u771f\u521a\u624d\u5bf9\u5979\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\u3002 \u3000\u3000\u2014\u2014\u8857\u5fc3\u7684\u6218\u5c40\u7a76\u7adf\u600e\u4e48\uff1f \u3000\u3000\u2014\u2014\u96be\u9053\u65b9\u90aa\u771f\u771f\u7684\u56de\u4e86\u5bb6\u7761\u5927\u89c9\uff1f \u3000\u3000\u60dc\u60dc\u56e0\u4e3a\u5927\u8fc7\u5fc3\u60ac\u4e8e\u65b9\u90aa\u771f\u7684\u5b89\u5371\uff0c\u4e00\u65f6\u5fd8\u4e86\u81ea\u8eab\u7684\u5371\u673a\uff0c\u518d\u60f3\u8d77\u65f6\uff0c\u56de\u5934\u53ea\u89c1\u90a3\u88ab\u5979\u4e00\u76c6\u6c34\u6dcb\u7740\u7684\u4eba\uff0c\u5df2\u5012\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u5730\u4e0a\u6f6e\u6e7f\u3002 \u3000\u3000\u697c\u677f\u4e0a\u67d3\u7740\u8840\u8ff9\uff0c\u6df7\u548c\u7740\u6c34\u8ff9\uff0c\u6b63\u5f80\u697c\u89d2\u6ef4\u843d\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u201c\u9ed1\u4eba\u201d\u7adf\u7136\u6b7b\u4e86\uff01 \u3000\u3000\u2014\u2014\u96be\u9053\u5979\u624b\u4e2d\u90a3\u76c6\u6e05\u6c34\u771f\u80fd\u6740\u4eba\u4e0d\u6210\uff1f\uff01 \u3000\u3000\u60dc\u60dc\u5012\u662f\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u5979\u8bb0\u8d77\u8eab\u540e\u8fd8\u6709\u4e00\u4eba\u3002 \u3000\u3000\u5979\u84e6\u7136\u56de\u9996\uff0c\u90a3\u201c\u9ed1\u4eba\u201d\u6240\u7acb\u4e4b\u5904\uff0c\u7acb\u7740\u4e00\u4e2a\u8863\u767d\u4e0d\u6cbe\u5c18\u3001\u6d12\u8131\u6cbe\u5fae\u6101\u7684\u4eba\uff0c\u6b63\u4f3c\u7b11\u975e\u7b11\u7684\u671b\u7740\u5979\u3002 \u3000\u3000\u60dc\u60dc\u54c0\u5524\u4e86\u4e00\u58f0\uff0c\u773c\u6cea\u5c31\u7c41\u7c41\u5730\u843d\u5230\u8138\u988a\u4e0a\u6765\uff0c\u5979\u6b64\u65f6\u624d\u60f3\u5230\u60ca\u6015\uff0c\u60f3\u6251\u5230\u65b9\u90aa\u771f\u6000\u91cc\uff0c\u5374\u7ed9\u5730\u4e0a\u7684\u4eba\u7eca\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fd9\u6276\u7740\u5979\u3002 \u3000\u3000\u5730\u4e0a\u7684\u90a3\u540d\u201c\u9ed1\u4eba\u201d\uff0c\u5f53\u7136\u4e5f\u662f\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6276\u7740\u5f31\u67d4\u7684\u60dc\u60dc\uff0c\u53ea\u89c9\u5f97\u5979\u5f31\u4e0d\u80dc\u8863\uff0c\u5fc3\u4e2d\u8d77\u4e86\u4e00\u79cd\u4e0d\u5fcd\u7684\u611f\u89c9\u3002 \u3000\u3000\u2014\u2014\u6c5f\u6e56\u98ce\u9669\u591a\uff0c\u81ea\u5df1\u53ef\u51b3\u4e0d\u80fd\u8fde\u7d2f\u5979\uff0c\u53ef\u5343\u4e07\u4e0d\u8981\u8fde\u7d2f\u4e86\u5979\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u662f\u5df2\u7ecf\u8d70\u4e86\u5417\uff1f\u201d\u60dc\u60dc\u5f88\u4e0d\u597d\u610f\u601d\u5730\u63e9\u53bb\u8138\u4e0a\u7684\u6cea\uff0c\u65b9\u90aa\u771f\u6349\u4f4f\u5979\u7684\u624b\uff0c\u7ec6\u5fc3\u7684\u4e3a\u5979\u62ed\u53bb\uff0c\u4e13\u60c5\u5f97\u5c31\u50cf\u4e00\u9619\u4e3a\u4e00\u4e2a\u5343\u601d\u4e07\u5ff5\u7684\u4eba\u5199\u7684\u8bcd\u3002 \u3000\u3000\u60dc\u501f\u8fd8\u6ca1\u6765\u5f97\u53ca\u611f\u52a8\uff0c\u5c31\u88ab\u64bc\u52a8\u4e86\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51dd\u6ce8\u4e86\u5979\u4e00\u4e00\u4f1a\uff0c\u5ffd\u7136\u773c\u5149\u53c8\u4e0d\u7ecf\u610f\u4e86\u3002 \u3000\u3000\u4e0d\u7ecf\u610f\u5f97\u5c31\u50cf\u4e00\u62b9\u8fdc\u5c71\uff0c\u6de1\u5165\u5929\u9645\u95f2\u4e91\u95f4\u3002 \u3000\u3000\u60dc\u60dc\u56de\u5473\u90a3\u4e00\u523b\uff0c\u4ecd\u89c9\u4f9d\u4f9d\u3002 \u3000\u3000\u90a3\u4e00\u523b\u7684\u5fc3\u52a8\uff0c\u90a3\u4e00\u523b\u7684\u52a8\u5fc3\uff0c\u53ea\u6709\u60c5\u4eba\u7279\u522b\u591a\u60c5\u7684\u773c\u91cc\u80fd\u770b\u5f97\u5230\uff0c\u53ea\u6709\u60c5\u4eba\u7279\u522b\u8df3\u5f97\u5feb\u7684\u5fc3\u91cc\u80fd\u611f\u53d7\u5f97\u5230\uff0c\u53ea\u6709\u60c5\u4eba\u7279\u522b\u6d41\u5f97\u6fc0\u52a8\u7684\u8840\u91cc\u80fd\u591f\u4f53\u5473\u5f97\u5230\u3002 \u3000\u3000\u60dc\u60dc\u4f3c\u75f4\u4e86\u3002 \u3000\u3000\u597d\u4e00\u4f1a\u5979\u624d\u80fd\u63a5\u4e0b\u53bb\u8bf4\uff1a\u201c\u4f60\u4e0d\u662f\u5df2\u7ecf\u56de\u53bb\u4e86\u5417\uff1f\u6211\u4ee5\u4e3a\u4f60\u5df2\u7ecf\u56de\u5bb6\u7761\u5927\u89c9\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u4e86\uff0c\u770b\u5979\u8f7b\u55d4\u8584\u6012\u7684\u6028\uff0c\u6e29\u6696\u5730\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u56de\u53bb\u7761\u4e86\u89c9\uff0c\u53c8\u68a6\u91cc\u6e38\u9b42\u7684\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u60dc\u60dc\u9f13\u7740\u816e\u513f\u9053\uff1a\u201c\u591a\u96be\u542c\u3002\u201d\u5ffd\u53c8\u559c\u6ecb\u6ecb\u7684\u8de8\u8fc7\u4e86\u6b7b\u5c38\uff0c\u6b22\u5fed\u5fc4\u5730\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u628a\u56de\u7edd\u6253\u8dd1\u7684\uff1f\u90a3\u4e00\u4e1d\u706b\u5149\u53c8\u662f\u751a\u4e48\uff1f\u201d \u3000\u3000\u5979\u8fd9\u6837\u95ee\u7740\u7684\u65f6\u5019\uff0c\u773c\u8272\u662f\u975e\u5e38\u75f4\u8ff7\u7684\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e00\u4e2a\u6b63\u5728\u7231\u604b\u4e2d\u7684\u5973\u5b50\uff0c\u770b\u5979\u7684\u60c5\u90ce\uff0c\u591a\u662f\u8fd9\u79cd\u773c\u8272\uff0c\u5c24\u5176\u5979\u7684\u60c5\u4eba\u771f\u7684\u662f\u4e2a\u82f1\u96c4\u4fa0\u5ba2\u7684\u65f6\u5019\u3002\u8c6a\u60c5\u6fc0\u8d77\u51e0\u8bb8\u67d4\uff1f\u60ca\u8d77\u591a\u5c11\u5982\u75f4\u5982\u9189\uff1f\u5c31\u7b97\u82f1\u96c4\u4fa0\u5973\uff0c\u53c8\u6709\u8c01\u80fd\u5fd8\u60c5\uff1f \u3000\u3000\u65b9\u90aa\u771f\u655b\u5bb9\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6211\u6ca1\u6709\u628a\u4ed6\u6253\u8dd1\u3002\u201d \u3000\u3000\u60dc\u60dc\u4e0d\u660e\u767d\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u6740\u4e86\u4ed6\uff0c\u7136\u540e\u53eb\u4ed6\u71c3\u6210\u4e00\u56e2\u706b\u8d70\u7684\u3002\u201d \u3000\u3000\u60dc\u60dc\u66f4\u542c\u4e0d\u61c2\u3002 \u3000\u3000\u65b9\u90aa\u771f\u660e\u767d\u60dc\u60dc\u7684\u4e0d\u660e\u767d\u3002 \u3000\u3000\u201c\u5999\u624b\u5802\u56de\u5bb6\u7684\u7edd\u827a\u53eb\u505a\u2018\u56de\u5929\u4e4f\u672f\u2019\uff0c\u542c\u540d\u5b57\uff0c\u5341\u5206\u7684\u5e73\u51e1\uff0c\u4f46\u5374\u662f\u628a\u4e94\u5341\u516b\u79cd\u7280\u5229\u7edd\u62db\u3001\u6b63\u90aa\u5185\u529f\u63c9\u5408\u878d\u4f1a\u5728\u516d\u62db\u4ee5\u5185\uff0c\u5341\u5206\u53ef\u6015\uff0c\u6211\u60f3\u5148\u8feb\u4ed6\u65bd\u51fa\u6765\uff0c\u770b\u662f\u4e0d\u662f\u53ef\u4ee5\u5e94\u4ed8\u3002\u201d \u3000\u3000\u60dc\u60dc\u5947\u9053\uff1a\u201c\u8feb\u51fa\u4ed6\u7684\u7edd\u62db\u3001\u4e07\u4e00\u5e94\u4ed8\u4e0d\u4e86\uff0c\u5c82\u4e0d\u66f4\u52a0\u5371\u9669\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u5982\u679c\u63a5\u4e0d\u4e0b\u56de\u7edd\u7684\u6740\u624b\u950f\uff0c\u5c31\u66f4\u4e0d\u53ef\u80fd\u5e94\u4ed8\u56de\u767e\u5e94\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u9053\uff1a\u201c\u8fdf\u65e9\u90fd\u662f\u4e00\u6b7b\uff0c\u4e0d\u5982\u6b7b\u5728\u56de\u7edd\u624b\u4e0a\u2014\u2014\u81f3\u5c11\uff0c\u5728\u4ed6\u624b\u4e0a\u6211\u8fd8\u6765\u5f97\u53ca\u81ea\u6740\uff0c\u843d\u5728\u56de\u767e\u5e94\u624b\u4e0a\uff0c\u4e0d\u5f97\u4ed6\u540c\u610f\uff0c\u8c01\u8981\u6b7b\u90fd\u4e0d\u53ef\u4ee5\u3002\u201d \u3000\u3000\u60dc\u60dc\u53c8\u62c5\u5fc3\u4e86\u8d77\u6765\uff1a\u201c\u53cd\u6b63\u2026\u2026\u4f60\u90fd\u63a5\u4e0b\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6447\u9996\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u60dc\u60dc\u53c8\u5413\u4e86\u4e00\u8df3\uff1a\u201c\u6ca1\u6709\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6c89\u58f0\u9053\uff1a\u201c\u6211\u5f04\u9519\u4e86\u4e00\u70b9\uff0c\u2018\u56de\u5929\u4e4f\u672f\uff0c\u539f\u6765\u662f\u6709\u516d\u5341\u4e00\u79cd\u7684\u6b66\u529f\u63c9\u5408\u5176\u4e2d\uff0c\u800c\u4e0d\u662f\u4e94\u5341\u516b\u79cd\u3002\u2018\u56de\u5929\u516d\u5f0f\u2019\u662f\u8981\u7528\u4e00\u79cd\u53eb\u2018\u56de\u9b42\u5927\u6cd5\u2019\u7684\u5185\u529b\uff0c\u624d\u80fd\u4ee5\u4e94\u6627\u771f\u706b\u4e4b\u529b\uff0c\u8fd0\u884c\u5341\u4e5d\u79cd\u4e0d\u540c\u7684\u529f\u529b\uff0c\u4f7f\u51fa\u2018\u56de\u5929\u4e4f\u672f\u2019\u3002\u56de\u7edd\u5f88\u4e0d\u957f\u8fdb\uff0c\u529f\u529b\u4e0d\u6d4e\uff0c\u53ea\u4f7f\u5f97\u51fa\u4e24\u5f0f\u6765\u3002\u6211\u4e00\u5251\u7834\u4e86\u4ed6\u7684\u7384\u5173\uff0c\u518d\u4ee5\u4e00\u7247\u706b\u7bfe\u5f15\u53d1\u4e86\u4ed6\u7684\u4e94\u6627\u771f\u706b\uff0c\u4ed6\u6536\u84c4\u4e0d\u4f4f\uff0c\u771f\u706b\u81ea\u711a\uff0c\u6700\u591a\u53ea\u80fd\u71ac\u5230\u5999\u624b\u5802\uff0c\u56de\u767e\u5e94\u533b\u672f\u518d\u7cbe\u901a\uff0c\u4e5f\u65ad\u6551\u4e0d\u6d3b\u4e00\u4e2a\u4e94\u810f\u5168\u7126\u3001\u4e03\u5b54\u5c3d\u711a\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u60dc\u60dc\u542c\u5f97\u5fc3\u60ca\u80c6\u8df3\uff0c\u53ea\u8bf4\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u4e00\u4e0b\u697c\uff0c\u5c31\u51c6\u5907\u7528\u8fd9\u62db\u4e86\uff0c\u4e0d\u7136\u600e\u4f1a\u5429\u5490\u6211\u6cfc\u6c34\uff0c\u4ee5\u53ca\u53eb\u6211\u7b49\u7740\u770b\u90a3\u4e00\u63a0\u7684\u706b\u5149\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u662f\u3002\u4e0d\u8fc7\uff0c\u90a3\u65f6\u5019\uff0c\u6211\u4ee5\u4e3a\u6765\u7684\u662f\u56de\u767e\u54cd\u548c\u56de\u4e07\u96f7\u6765\u4e86\uff0c\u4ed6\u4eec\u53ea\u662f\u8be5\u6b7b\uff0c\u56de\u7edd\u5374\u662f\u8be5\u7edd\u3002\u201d \u3000\u3000\u60dc\u60dc\u60ca\u7c9f\u5730\u9053\uff1a\u201c\u4f60\u6740\u4e86\u56de\u7edd\uff0c\u56de\u767e\u5e94\u4ed6\u4eec\u662f\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u7684\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6740\u56de\u7edd\uff0c\u96be\u9053\u4ed6\u4eec\u5c31\u4f1a\u653e\u8fc7\u6211\u5417\uff1f\u201d\u4ed6\u5411\u60dc\u60dc\u6e9c\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff0c\u201c\u81f3\u5c11\uff0c\u56de\u7edd\u82e5\u6d3b\u7740\uff0c\u4fbf\u8fde\u4f60\u4e5f\u90fd\u4e0d\u4f1a\u653e\u8fc7\u3002\u201d \u3000\u3000\u60dc\u60dc\u5509\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5373\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u60dc\u60dc\u5fe7\u6101\u5730\u9053\uff1a\u201c\u73b0\u5728\u8981\u4f60\u53bb\u505a\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u5f53\u7136\u4e0d\u4f1a\u7b54\u5e94\u7684\u4e86\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d \u3000\u3000\u60dc\u60dc\u7528\u4e00\u79cd\u4f4e\u901f\u7684\u8bed\u97f3\u9053\uff1a\u201c\u5982\u679c\u73b0\u5728\u8981\u4f60\u59d4\u5c48\u4e00\u4e0b\uff0c\u53bb\u8eb2\u8eb2\uff0c\u907f\u4e00\u907f\u98ce\u5934\uff0c\u4f60\u662f\u51b3\u4e0d\u4f1a\u7b54\u5e94\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u3002\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u6211\u7b54\u5e94\u4f60\u3002\u201d \u3000 \u7b2c\u4e5d\u7ae0\u3000\u8fd9\u4e00\u523b\u7684\u52a8\u5fc3 \u3000 \u3000\u3000\u60dc\u60dc\u50bb\u4e86\u3002 \u3000\u3000\u706f\u5149\u5728\u5979\u80a9\u4e0a\u94fa\u4e0a\u4e00\u5c42\u6bd4\u67d4\u66f4\u67d4\u7684\u9ec4\u6655\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fc3\u4e2d\u66f4\u6dfb\u601c\u60dc\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5ffd\u7136\u6709\u4e2a\u5343\u547c\u4e07\u5524\u7684\u65e0\u58f0\uff1a\u4f60\u5ac1\u7ed9\u6211\u597d\u5417\uff1f\u4ed6\u60f3\u8fd9\u6837\u95ee\uff0c\u53ef\u662f\u5fc3\u4e2d\u5ffd\u7136\u63a0\u8fc7\u4e00\u4e2a\u4eae\u4e3d\u7684\u97f3\u5bb9\uff0c\u8bf4\u5230\u5634\u8fb9\u7684\u8bdd\u53d8\u6210\u4e86\uff1a\u201c\u6211\u6740\u4e86\u56de\u7edd\uff0c\u8fd9\u513f\u662f\u4e0d\u80fd\u518d\u7559\u4e86\u3002\u201d \u3000\u3000\u60dc\u60dc\u5ffd\u7136\u9eef\u7136\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u7537\u5b50\uff0c\u7ec8\u4e8e\u8981\u8d70\u4e86\uff0c\u4ed6\u96be\u9053\u4e00\u70b9\u90fd\u4e0d\u987e\u60dc\u5979\u5417\uff1f\u5979\u8fd9\u6837\u7684\u5fd6\u601d\uff0c\u968f\u540e\u53c8\u60f3\u5230\uff1a\u4e3a\u4e86\u4ed6\u7684\u5b89\u5371\uff0c\u4ed6\u662f\u5e94\u8be5\u8d70\u7684\uff0c\u4ed6\u5c82\u662f\u53ef\u4ee5\u7559\u5f97\u4f4f\u7684\uff1f\u4f55\u51b5\uff0c\u8981\u4ed6\u8d70\u662f\u5979\u81ea\u5df1\u63d0\u51fa\u6765\u7684\u3002 \u3000\u3000\u4eba\u751f\u91cc\u6709\u4e9b\u4e8b\uff0c\u4e00\u6b65\u8de8\u51fa\u53bb\u5373\u6210\u5929\u6daf\uff0c\u7eb5\u7136\u65e0\u6b4c\uff0c\u4f46\u80fd\u65e0\u6094\u3002 \u3000\u3000\u201c\u4f60\u51e0\u65f6\u8d70\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5f88\u60f3\u8bf4\uff1a\u201c\u6211\u5e26\u4f60\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u591a\u4e48\u60f3\u8bf4\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u2014\u2014\u4e3a\u4ec0\u4e48\u6ca1\u6709\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u539f\u56e0\u4e5f\u8bb8\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u751a\u6e05\u695a\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u63a0\u8fc7\u4e86\u4e00\u4e2a\u5ff5\u5934\uff1a\u5f85\u771f\u7684\u8d70\u65f6\uff0c\u518d\u628a\u5979\u4e00\u8d77\u5e26\u8d70\uff1b\u4fdf\u50a8\u591f\u94b1\u65f6\uff0c\u518d\u4e00\u8d77\u8d4e\u5979\uff0c\u90a3\u65f6\u5019\u5c82\u4e0d\u66f4\u5b9e\u5728\u3001\u66f4\u52a0\u60ca\u559c\u3001 \u3000\u3000\u6216\u8bb8\u56e0\u4e3a\u8fd9\u6837\uff0c\u4ed6\u624d\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u6216\u8bb8\u56e0\u4e3a\u90a3\u6837\u3002 \u3000\u3000\u4e0d\u8fc7\u4e0d\u7ba1\u4e3a\u4e86\u751a\u4e48\uff0c\u4eba\u751f\u91cc\uff0c\u80fd\u5bf9\u7740\u4e00\u4f4d\u7ea2\u7c89\u77e5\u97f3\uff0c\u6620\u7740\u665a\u706f\uff0c\u501a\u7740\u680f\u4edf\uff0c\u4f60\u8fd8\u80fd\u6c42\u751a\u4e48\uff1f\u4f60\u4e3a\u4f55\u4e0d\u53bb\u628a\u63e1\uff1f\u5982\u679c\u4e00\u5239\u662f\u6c38\u6052\uff0c\u90a3\u4e48\u6c38\u6052\u5c31\u662f\u4e00\u5239\u3002\u5982\u679c\u628a\u63e1\u4e0d\u4f4f\uff0c\u8ba9\u5b83\u6e9c\u4e86\uff0c\u518d\u6ca1\u6709\u6c38\uff0c\u518d\u6ca1\u6709\u6052\uff0c\u518d\u6ca1\u6709\u706f\u524d\u501a\u680f\u7684\u4eba\uff0c\u7a7a\u63b7\u4f24\u6000\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e00\u62b9\u81ea\u711a\u7684\u706b\u82b1\u3002 \u3000\u3000\u4e5f\u8bb8\u60dc\u60dc\u5fc3\u91cc\u6709\u5343\u547c\u4e07\u5524\u7684\u671f\u5f85\u3002 \u3000\u3000\u4e5f\u8bb8\u65b9\u90aa\u771f\u80f8\u91cc\u6709\u6b32\u8bed\u8fd8\u4f11\u7684\u771f\u60c5\u3002 \u3000\u3000\u4e0d\u8fc7\u90fd\u8fd8\u672a\u66fe\u8bf4\u51fa\u6765\uff0c\u5c31\u5df2\u7ecf\u542c\u89c1\u697c\u4e0b\u6709\u4eba\u8bf4\uff1a\u201c\u5dee\u5b98\uff0c\u521a\u624d\u5728\u8def\u4e0a\u8c0b\u8d22\u5bb3\u547d\u7684\uff0c\u5c31\u662f\u8fd9\u51e0\u4e2a\u9ed1\u8863\u4eba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e0d\u8ba4\u5f97\u8fd9\u4e2a\u751f\u610f\u4eba\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e2a\u751f\u610f\u4eba\u597d\u50cf\u5f88\u6709\u529e\u6cd5\u3002 \u3000\u3000\u5c24\u5176\u662f\u5e94\u4ed8\u8fd9\u79cd\u6b7b\u4eba\u7684\u4e8b\u4ef6\uff0c\u4ee5\u53ca\u5e94\u4ed8\u90a3\u5341\u51e0\u4f4d\u7761\u773c\u60fa\u677e\u7684\u5dee\u5b98\u2014\u2014\u770b\u6765\u90a3\u51e0\u4e2a\u5dee\u5f79\u53cd\u800c\u50cf\u662f\u53d7\u4ed6\u6307\u6325\u3002 \u3000\u3000\u90a3\u5546\u4eba\u5374\u5bf9\u65b9\u90aa\u771f\u5341\u5206\u719f\u7edc\uff0c\u50cf\u8ba4\u8bc6\u4e86\u4ed6\u5341\u5e74\u516b\u5e74\u4f3c\u7684\uff0c\u8ddf\u4ed6\u5171\u4f4f\u4e86\u5341\u6708\u516b\u6708\u4e00\u822c\u3002 \u3000\u3000\u636e\u90a3\u5546\u4eba\u7684\u8bf4\u8f9e\uff0c\u662f\uff1a\u4ed6\u505a\u4e86\u5355\u751f\u610f\uff0c\u6765\u201c\u4f9d\u4f9d\u697c\u201d\u5bfb\u4e50\uff0c\u9047\u4e0a\u4e86\u4e00\u7fa4\u201c\u52ab\u8d22\u5bb3\u547d\u201d \u3000\u3000\u7684\uff0c\u65b9\u90aa\u771f\u8def\u89c1\u4e0d\u5e73\u3001\u62d4\u5200\u76f8\u52a9\uff0c\u4ee5\u5be1\u654c\u4f17\uff0c\u8feb\u4e8e\u81ea\u536b\uff0c\u53ea\u597d\u6740\u4f24\u4e86\u51e0\u540d\u201c\u5927\u76d7\u201d\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u4f46\u90a3\u5546\u4eba\u4eb2\u773c\u77a7\u89c1\uff0c\u8fd8\u6709\u4e24\u540d\u4ec6\u50ee\u53ef\u4ee5\u4f5c\u8bc1\uff0c\u8fd8\u4e0d\u77e5\u600e\u4e48\u6765\u4e86\u4e03\u516b\u4e2a\u201c\u9014\u4eba\u201d\uff0c\u90fd\u8bf4\u53ef\u4ee5\u4e3a\u6b64\u4e8b\u89c1\u8bc1\uff0c\u8a00\u4e4b\u51ff\u51ff\uff0c\u542c\u5f97\u8fde\u60dc\u60dc\u90fd\u51e0\u4ee5\u4e3a\u81ea\u5df1\u662f\u505a\u4e86\u4e00\u573a\u68a6\uff0c\u771f\u7684\u6709\u8fd9\u56de\u4e8b\u4e00\u822c\u3002 \u3000\u3000\u5dee\u5b98\u53d6\u4e86\u4f9b\u8bcd\uff0c\u6e05\u7406\u5c38\u9ab8\uff0c\u5c45\u7136\u4e0d\u76d8\u67e5\u65b9\u90aa\u771f\uff0c\u4e5f\u4e0d\u843d\u4f9b\u5ba1\u8baf\uff0c\u66f4\u4e0d\u62ff\u4ed6\u56de\u8859\uff0c\u5c31\u8fd9\u6837\u8349\u8349\u4e86\u4e8b\uff0c\u8868\u793a\u7ed3\u6848\u3002 \u3000\u3000\u770b\u90a3\u4e9b\u8859\u5dee\u7684\u8868\u60c5\u548c\u542c\u4ed6\u4eec\u7684\u8bed\u6001\uff0c\u4eff\u4f5b\u65b9\u90aa\u771f\u6740\u4e86\u8fd9\u51e0\u4e2a\u4eba\uff0c\u8fd8\u7406\u5e94\u62ff\u4e2a\u751a\u4e48\u6a2a\u533e\u5956\u72b6\u4f3c\u7684\u3002 \u3000\u3000\u8fd9\u4e8b\u5f53\u7136\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u53ef\u662f\u4fdf\u90a3\u4f4d\u5546\u4eba\u6253\u53d1\u5dee\u5f79\u4eec\u8d70\u8fdc\u540e\uff0c\u4e0a\u5f97\u697c\u6765\uff0c\u8ddf\u65b9\u90aa\u771f\u7b11\u7740\u4e00\u70b9\u5934\uff0c\u5c31\u8981\u522b\u53bb\uff0c\u65b9\u90aa\u771f\u4e00\u89c1\u4ed6\u7684\u773c\u795e\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u626c\u58f0\u95ee\uff1a\u201c\u8bf7\u6559\u5c0a\u59d3\u5927\u540d\uff1f\u201d\u8fd9\u53e5\u8bdd\u4e00\u95ee\uff0c\u4e00\u5207\u90fd\u201c\u771f\u76f8\u5927\u767d\u201d\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u5546\u4eba\u7684\u56de\u7b54\u662f\uff1a \u3000\u3000\u201c\u987e\u4f5b\u5f71\u3002\u201d \u3000\u3000\u6709\u201c\u987e\u76fc\u795e\u98ce\u201d\u5728\uff0c\u54ea\u6709\u89e3\u51b3\u4e0d\u4e86\u7684\u4e8b\uff01 \u3000\u3000\u50cf\u8fd9\u79cd\u51e0\u6761\u4eba\u547d\u7684\u5c0f\u4e8b\uff0c\u5728\u6d1b\u9633\u57ce\u91cc\uff0c\u8981\u51fa\u52a8\u5230\u987e\u4f5b\u5f71\uff0c\u5b9e\u5728\u662f\u5c0f\u9898\u5927\u4f5c\uff0c\u5927\u6750\u5c0f\u7528\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u58f0\u5a01\uff0c\u53ea\u8981\u4ea4\u4ee3\u4e0b\u6765\u51e0\u53e5\u8bdd\u5c31\u53ef\u4ee5\u4e86\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u9053\u51fa\u81ea\u5df1\u7684\u540d\u5b57\u540e\uff0c\u7acb\u5373\u4fbf\u8d70\u3002 \u3000\u3000\u4e0d\u6c42\u5bf9\u65b9\u611f\u8c22\uff0c\u4e0d\u56fe\u62a5\uff0c\u9a6c\u4e0a\u79bb\u5f00\u3002 \u3000\u3000\u65b9\u90aa\u771f\u957f\u53f9\uff1a\u201c\u96be\u602a\u5c0f\u78a7\u6e56\u6e38\u5bb6\u4f1a\u65e5\u76ca\u58ee\u5927\uff0c\u6709\u7b80\u8fc5\u8fd9\u79cd\u5e72\u5458\uff0c\u53c8\u6709\u987e\u4f5b\u5f71\u8fd9\u79cd\u4eba\u7269\uff0c\u60f3\u4e0d\u5f3a\u76db\u4ea6\u96be\u77e3\u3002\u201d \u3000\u3000\u60dc\u60dc\u7528\u773c\u89d2\u6f02\u6f02\u4eae\u4eae\u7684\u52fe\u7740\u773c\u4ed6\uff0c\u7136\u540e\u8bf4\uff1a\u201c\u6240\u4ee5\u4f60\u53c8\u8d70\u4e0d\u6210\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u7559\u5728\u6d1b\u9633\u591a\u70e6\u5fe7\uff0c\u201d\u65b9\u90aa\u771f\u60f3\u4e86\u4e00\u9635\uff0c\u624d\u9053\uff0c\u201c\u6211\u8fd8\u662f\u8d70\u7684\u597d\uff0c\u514d\u4f60\u53d7\u7d2f\uff0c\u7239\u7239\u548c\u5c0f\u5f1f\u4e5f\u70e6\u607c\u3002\u201d \u3000\u3000\u60dc\u60dc\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8fc7\u53bb\u63e1\u7740\u5979\u7684\u624b\uff0c\u89c9\u5f97\u4f0a\u7684\u5c0f\u624b\u51b0\u51c9\u5982\u96ea\uff0c\u5fc3\u4e2d\u4e00\u75db\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u60dc\u60dc\u2026\u2026\u201d \u3000\u3000\u60dc\u60dc\u4e00\u9707\uff0c\u53cd\u8fc7\u53bb\u63e1\u7740\u4ed6\u7684\u624b\uff0c\u4e00\u53cc\u6676\u76ee\u90fd\u5659\u7740\u6676\u83b9\u7684\u6cea\u6c34\u3002 \u3000\u3000\u201c\u8981\u5c0f\u5fc3\u56de\u5bb6\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7528\u624b\u6e29\u6696\u7740\u5979\u7684\u624b\u3002 \u3000\u3000\u201c\u56de\u5bb6\uff1f\u4eca\u665a\u6211\u4e0d\u56de\u5bb6\u3002\u201d\u4ed6\u8fd9\u6837\u8c03\u7b11\u9053\u3002 \u3000\u3000\u60dc\u60dc\u5ffd\u7136\u53c8\u9ad8\u5174\u8d77\u6765\uff1a\u201c\u4f60\u65e2\u7136\u6740\u5f97\u4e86\u56de\u7edd\uff0c\u4fbf\u7edd\u4e0d\u6015\u56de\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u60dc\u60dc\u9a6c\u4e0a\u611f\u89c9\u5230\u4e86\uff0c\u6240\u4ee5\u5979\u9a6c\u4e0a\u95ee\uff1a\u201c\u662f\u4e0d\u662f\uff1f\u662f\u4e0d\u662f\u5462\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u77e5\u9053\uff1f\u201d \u3000\u3000\u60dc\u60dc\u8ba4\u771f\u7684\u70b9\u5934\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u56de\u7edd\u7eb5\u60c5\u58f0\u8272\uff0c\u5f88\u4e0d\u50cf\u8bdd\u3002\u4ed6\u7684\u6b66\u529f\u6015\u53ea\u5f97\u56de\u767e\u5e94\u7684\u4e00\u6210\uff0c\u800c\u2018\u56de\u5929\u4e4f\u672f\u2019\u516d\u8bb0\u7edd\u62db\uff0c\u56de\u7edd\u4e5f\u53ea\u7ec3\u6210\u4e8c\u5f0f\uff0c\u6211\u80fd\u5f15\u4ed6\u771f\u706b\u9006\u8d70\u81ea\u711a\uff0c\u81ea\u4e0d\u662f\u4ef6\u96be\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u773c\u91cc\u9664\u4e86\u6de1\u6de1\u7684\u6092\u8272\u4e4b\u5916\uff0c\u8fd8\u6709\u5fae\u5fae\u7684\u5fe7\u8272\uff0c\u201c\u5999\u624b\u5802\u56de\u5bb6\u7684\u4eba\u5f88\u9738\u9053\u3001\u5f88\u51f6\u72e0\uff0c\u53ef\u4ee5\u7b97\u5f97\u4e0a\u662f\u65e0\u6076\u4e0d\u4f5c\uff0c\u4f46\u56de\u767e\u5e94\u672c\u8eab\u5374\u5341\u5206\u81ea\u5f8b\u3001\u575a\u5fcd\u3001\u8282\u5236\uff0c\u4e00\u4e2a\u4eba\u80fd\u5728\u4e00\u56e2\u6c61\u70df\u7634\u6c14\u4e4b\u4e0b\u4ecd\u80fd\u81ea\u5f3a\u4e0d\u606f\uff0c\u81ea\u7136\u662f\u4e2a\u4eba\u7269\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u5401\u51fa\u4e00\u53e3\u6c14\uff1a\u201c\u56de\u767e\u5e94\u662f\u4e00\u4e2a\u5f88\u96be\u5e94\u4ed8\u7684\u4eba\uff0c\u6211\u6ca1\u6709\u628a\u63e1\u80dc\u4ed6\uff0c\u4f55\u51b5\u4ed6\u8fd8\u6709\u4e24\u5927\u91cd\u5c06\uff1a\u56de\u767e\u54cd\u548c\u56de\u4e07\u96f7\u3002\u201d\u4ed6\u5026\u5026\u7684\uff1a\u7b11\uff0c\u53c8\u9053\uff0c\u201c\u56de\u767e\u5e94\u73b0\u5728\u4e00\u5b9a\u5f88\u4f24\u5fc3\uff0c\u4e00\u5b9a\u4f1a\u5168\u529b\u62a5\u590d\uff0c\u518d\u8fd9\u6837\u70e6\u7f20\u4e0b\u53bb\uff0c\u4e00\u5b9a\u4f1a\u95f9\u51fa\u5927\u4e8b\u6765\uff0c\u6240\u4ee5\uff0c\u6211\u5148\u79bb\u5f00\u6d1b\u9633\u57ce\u4e00\u6bb5\u65f6\u95f4\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u7740\u62cd\u62cd\u60dc\u60dc\u7684\u80a9\u818a\uff0c\u56e0\u4e3a\u624b\u4e2d\u6240\u89e6\u662f\u8ba9\u4eba\u5fc3\u6298\u7684\u67d4\uff0c\u6240\u4ee5\u624b\u638c\u5c31\u4e0d\u5fcd\u632a\u5f00\uff1a\u201c\u4f60\u8981\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u6709\u4e8b\u7684\u3002\u201d \u3000\u3000\u60dc\u60dc\u957f\u957f\u7684\u776b\u6bdb\u98a4\u52a8\u7740\uff0c\u8ba4\u8ba4\u771f\u771f\u5730\u95ee\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u6015\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u505a\u7b11\u9053\uff1a\u201c\u6015\uff1f\u6211\u6015\u751a\u4e48\uff1f\u201d\u4ed6\u89c9\u5f97\u8981\u8bf4\u4e00\u4e9b\u8c03\u76ae\u8bdd\u8ba9\u60dc\u60dc\u7684\u60c5\u7eea\u5e73\u4f0f\u4e0b\u6765\uff0c\u6240\u4ee5\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\u7b11\u9053\uff0c\u201c\u5e73\u751f\u4e0d\u4f5c\u4e8f\u5fc3\u4e8b\uff0c\u534a\u591c\u6572\u95e8\u4e5f\u4e0d\u60ca\u3002\u201d \u3000\u3000\u5374\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u6709\u4eba\u6572\u54cd\u4e86\u95e8\u3002 \u3000\u3000\u6572\u95e8\u58f0\u5f88\u8f7b\uff0c\u5374\u80fd\u4f20\u5f97\u5f88\u8fdc\u3002 \u3000\u3000\u6572\u95e8\u7684\u662f\u8c01\uff1f \u3000\u3000\u2014\u2014\u51c6\u5728\u6572\u95e8\uff1f \u3000\u3000\u6b64\u523b\uff0c\u56de\u7edd\u7684\u5c38\u4f53\u5c31\u653e\u5728\u5802\u524d\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u90fd\u9ed1\u3002 \u3000\u3000\u7126\u9ed1\u3002 \u3000\u3000\u672c\u6765\u9752\u767d\u7684\u8138\u5bb9\uff0c\u4e5f\u70e7\u6210\u70ad\u8272\uff0c\u800c\u9752\u7b4b\u8d32\u7a81\u7684\u5730\u65b9\uff0c\u53d8\u6210\u4e00\u9053\u53c8\u4e00\u9053\u7684\u88c2\u7eb9\u3002 \u3000\u3000\u5728\u8fd9\u7126\u9ed1\u7684\u5c38\u9996\u524d\u9762\uff0c\u7ad9\u7740\u4e00\u540d\u8001\u4eba\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5e74\u7eaa\u5e76\u4e0d\u5927\u8001\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e94\u5341\u5f00\u5916\uff0c\u4f46\u4ed6\u90a3\u4e00\u5f20\u8138\u5b54\uff0c\u6709\u7740\u5927\u591a\u7684\u76b1\u7eb9\u3001\u592a\u591a\u7684\u6ca7\u6851\u3001\u592a\u591a\u7684\u714e\u71ac\u4e0e\u575a\u5fcd\u3001\u5927\u591a\u7684\u8fc7\u5f80\u3002\u65e0\u8bba\u662f\u8c01\uff0c\u4e00\u4e2a\u4eba\u53ea\u8981\u6709\u5927\u591a\u7684\u8fd9\u4e9b\uff0c\u770b\u53bb\u96be\u514d\u90fd\u89c9\u5f97\u8001\u3002 \u3000\u3000\u56de\u767e\u5e94\u624d\u4e94\u5341\u56db\u5c81\uff0c\u5bf9\u529f\u529b\u9ad8\u6df1\u3001\u8001\u5f53\u76ca\u58ee\u7684\u6b66\u6797\u9ad8\u624b\u800c\u8a00\uff0c\u8fd9\u5b9e\u5728\u4e0d\u7b97\u662f\u201c\u8001\u201d\u7684\u5e74\u7eaa\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u5728\u81ea\u5df1\u513f\u5b50\u7684\u5c38\u9996\u524d\uff0c\u5c31\u4e00\u5b9a\u4f1a\u89c9\u5f97\u8001\u3002 \u3000\u3000\u81f3\u5c11\u662f\u5fc3\u60c5\u4e0a\u7684\u82cd\u8001\u3002 \u3000\u3000\u2014\u2014\u4e3a\u751a\u4e48\u8001\u7684\u4e0d\u6b7b\uff0c\u5c11\u7684\u5148\u6b7b\uff1f \u3000\u3000\u2014\u2014\u4e3a\u751a\u4e48\u4e16\u4e0a\u603b\u6709\u767d\u5934\u4eba\u9001\u9ed1\u5934\u4eba\u7684\u4e8b\uff1f \u3000\u3000\u770b\u7740\u4ed6\u81ea\u5df1\u513f\u5b50\u7684\u5c38\u9996\uff0c\u4ed6\u5fc3\u91cc\u60f3\uff0c\u8981\u662f\u6709\u4eba\u7ed9\u4ed6\u9009\u62e9\uff0c\u4e00\u662f\u4ed6\u6b7b\uff0c\u4e00\u662f\u4ed6\u513f\u5b50\u6b7b\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u66ff\u4ee3\u4ed6\u513f\u5b50\u6b7b\u5462\uff1f\u4ed6\u81ea\u5df1\u8f9b\u8f9b\u82e6\u82e6\u95ef\u4e0b\u4e86\u8fd9\u4e00\u756a\u57fa\u4e1a\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u7684\u5b69\u5b50\u5374\u6b7b\u4e86\uff0c\u7531\u8c01\u6765\u627f\u7ee7\u5462\uff1f\u4eba\u751f\u4e0d\u8fc7\u767e\u5e74\uff0c\u8fd9\u4e9b\u57fa\u4e1a\u8fd8\u6709\u751a\u4e48\u610f\u4e49\u5462\uff1f \u3000\u3000\u4ed6\u7ad9\u5728\u90a3\u513f\uff0c\u8ddf\u56de\u7edd\u7684\u5c38\u9996\uff0c\u4e00\u76f4\u4e00\u6a2a\uff0c\u90fd\u5931\u53bb\u4e86\u8868\u60c5\u4f3c\u7684\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u56de\u767e\u54cd\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4f4d\u638c\u6709\u5927\u6743\u7684\u5144\u957f\uff0c\u662f\u4f24\u5fc3\uff1f\u8fd8\u662f\u6124\u6012\uff1f\u6291\u6216\u662f\u60b2\u75db\u6b32\u7edd\uff1f \u3000\u3000\u56de\u767e\u54cd\u53ea\u77e5\u9053\u4ed6\u7684\u76b1\u7eb9\u5c31\u662f\u4ed6\u7684\u8868\u60c5\u3002 \u3000\u3000\u56de\u767e\u5e94\u76b1\u7eb9\u6ee1\u8138\uff0c\u7eb5\u6a2a\u4ea4\u9519\uff0c\u50cf\u4ea4\u7ec7\u7740\u5bc6\u96c6\u7684\u5200\u75a4\u4e00\u822c\u3002 \u3000\u3000\u56de\u767e\u54cd\u8ddf\u968f\u4ed6\u591a\u5e74\uff0c\u4ecd\u4e0d\u77e5\u9053\u4ed6\u4e0b\u4e00\u6b65\u7684\u53cd\u5e94\u3001\u4ed6\u5fc3\u91cc\u7684\u60f3\u6cd5\u3001\u4ed6\u5c06\u4f1a\u91c7\u53d6\u7684\u884c\u52a8\u3002 \u3000\u3000\u6709\u4e00\u6b21\uff0c\u4e00\u540d\u5c0f\u53ae\u4e0d\u5c0f\u5fc3\u6298\u65ad\u4e86\u4ed6\u4eb2\u624b\u79cd\u690d\u7684\u4e00\u679d\u201c\u94c1\u5fc3\u5170\u201d\uff0c\u4ed6\u6124\u6012\u5f97\u6298\u4e0b\u90a3\u5c0f\u53ae\u7684\u5934\u53bb\u5582\u72ee\u5b50\u3002 \u3000\u3000\u4e5f\u6709\u4e00\u6b21\uff0c\u4ed6\u88ab\u6e38\u7389\u906e\u7684\u4eba\u8fde\u62d4\u5341\u4e00\u4e2a\u6697\u5361\uff0c\u5c45\u7136\u8fd8\u53ef\u4ee5\u5e26\u5341\u516d\u540d\u5c0f\u59be\u53bb\u770b\u706f\u8d4f\u6708\uff0c\u8fd8\u9644\u5eb8\u98ce\u96c5\u5730\u4e0e\u4eba\u541f\u8bd7\u4f5c\u5bf9\u3002 \u3000\u3000\u56de\u767e\u54cd\u5230\u73b0\u5728\u8fd8\u6478\u4e0d\u6e05\u4ed6\u7684\u813e\u6c14\uff0c\u6240\u4ee5\u5bf9\u4ed6\u4e00\u6837\u611f\u5230\u754f\u60e7\u3002 \u3000\u3000\u2014\u2014\u9886\u8896\u4eec\u5e38\u5927\u559c\u5927\u6012\u3001\u559c\u6012\u65e0\u5e38\uff0c\u83ab\u975e\u5c31\u662f\u8981\u4eba\u8bb3\u83ab\u5982\u6df1\u3002\u83ab\u6d4b\u9ad8\u6df1\uff0c\u56e0\u800c\u4ea7\u751f\u656c\u754f\uff1f \u3000\u3000\u56de\u767e\u54cd\u4e0d\u77e5\u9053\u3002\u4ed6\u53ea\u77e5\u9053\u4e00\u4e2a\u4eba\u4ed6\u8db3\u8db3\u8ddf\u4e86\u8fd1\u56db\u5341\u5e74\uff0c\u8fd8\u5f04\u4e0d\u6e05\u695a\u4ed6\u7684\u771f\u6b63\u6027\u60c5\uff0c\u662f\u4e00\u4ef6\u5f88\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u5c31\u8fde\u4ed6\u6b7b\u4e86\u513f\u5b50\uff0c\u5c45\u7136\u4e5f\u6349\u6478\u4e0d\u51c6\u662f\u60b2\u662f\u6012\uff0c\u662f\u4f24\u662f\u75db\uff0c\u751a\u6216\u662f\u6ca1\u6709\u611f\u89c9\uff0c\u5b9e\u5728\u662f\u4ef6\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u4e5f\u8bb8\u53ea\u6709\u4e00\u4ef6\u4e8b\u60c5\u4f7f\u56de\u767e\u54cd\u4e0d\u81f4\u611f\u5230\u592a\u5bb3\u6015\u7684\u3002 \u3000\u3000\u90a3\u662f\u56de\u767e\u5e94\u4e00\u5411\u90fd\u4fe1\u4efb\u4ed6\u3002 \u3000\u3000\u56de\u767e\u5e94\u4e00\u5411\u90fd\u5f88\u4fe1\u4efb\u201c\u81ea\u5df1\u4eba\u201d\u3002 \u3000\u3000\u2014\u2014\u6240\u4ee5\u201c\u5999\u624b\u5802\u201d\u51e0\u4e2a\u91cd\u8981\u90e8\u95e8\u7684\u8d1f\u8d23\u4eba\uff0c\u90fd\u662f\u201c\u81ea\u5df1\u4eba\u201d\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ea\u8981\u8fd8\u4fe1\u4efb\u4eba\uff0c\u8fd8\u987e\u6064\u4eb2\u670b\uff0c\u5c31\u4e0d\u4f1a\u662f\u4e2a\u592a\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u56de\u767e\u5e94\u5ffd\u9053\uff1a\u201c\u6211\u7684\u5b69\u5b50\uff0c\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u4e8b\u5b9e\u3002 \u3000\u3000\u94c1\u4e00\u822c\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u80fd\u633d\u56de\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u2014\u2014\u6218\u8d25\u53ef\u4ee5\u5377\u571f\u91cd\u6765\uff0c\u5931\u610f\u53ef\u4ee5\u91cd\u71c3\u6597\u5fd7\uff0c\u4f46\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u5343\u53e4\u4e0d\u6613\u3002 \u3000\u3000\u56de\u767e\u54cd\u53ea\u6709\u9053\uff1a\u201c\u662f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u9759\u9ed8\u4e86\u534a\u664c\uff0c\u56de\u767e\u5e94\u53c8\u9053\uff1a\u201c\u6740\u4ed6\u7684\u4eba\uff0c\u597d\u50cf\u53eb\u505a\u65b9\u90aa\u771f\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9a6c\u4e0a\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u4ed6\uff0c\u597d\u50cf\u662f\u4e00\u4e2a\u5f88\u6709\u672c\u9886\u7684\u4eba\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u4e5f\u662f\u4e00\u4e2a\u8be5\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u8138\u4e0a\u7684\u76b1\u7eb9\u50cf\u6d77\u6d9b\u822c\u7684\u6380\u52a8\u4e86\u4e00\u4e0b\uff0c\u53ea\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4ed6\u8fd8\u6ca1\u6709\u52a0\u5165\u6c60\u5bb6\u201d\uff1f\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u4e2a\u4e8b\u5b9e\u3002 \u3000\u3000\u4e0d\u5bb9\u5426\u8ba4\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u662f\u3002\u201d\u4ed6\u7d27\u63a5\u53c8\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u770b\uff0c\u4e5f\u5feb\u4e86\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u52a0\u5165\uff0c\u5c31\u662f\u6ca1\u6709\u52a0\u5165\uff0c\u4e00\u4e2a\u4eba\u5c06\u8981\u505a\u7684\u4e8b\uff0c\u5728\u4ed6\u771f\u6b63\u505a\u7684\u65f6\u5019\uff0c\u4e0d\u4e00\u5b9a\u4f1a\u505a\u6210\u751a\u4e48\u4e8b\u3002\u201d\u4ed6\u7684\u8bed\u6c14\u8fd1\u4e4e\u6559\u8bad\u3002 \u3000\u3000\u56de\u767e\u54cd\u5fd9\u9053\uff1a\u201c\u5802\u4e3b\u6559\u8bad\u7684\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u52a0\u5165\u6c60\u5bb6\uff0c\u90a3\u4e48\u4ed6\u6740\u6b7b\u5c0f\u7edd\uff0c\u5c31\u4e0d\u662f\u4e3a\u4e86\u6c60\u5bb6\u800c\u5e72\u7684\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u672c\u60f3\u7b54\uff1a\u201c\u90a3\u4e5f\u5dee\u4e0d\u591a\uff0c\u201d\u4f46\u4e0d\u6562\u8ddf\u4e00\u4e2a\u521a\u6b7b\u4e86\u5b69\u5b50\u800c\u53c8\u624b\u63e1\u91cd\u6743\u7684\u8001\u4eba\u9876\u649e\uff0c\u53ea\u8bf4\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u5507\u89d2\u7275\u52a8\uff0c\u9053\uff1a\u201c\u6211\u7684\u5b69\u5b50\uff0c\u4e0d\u6b7b\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u62a5\u4ec7\u4e5f\u6ca1\u6709\u7528\u4e86\uff0c\u603b\u4e0d\u80fd\u8d77\u6b7b\u56de\u751f\uff0c\u201d\u4ed6\u773c\u4e2d\u95ea\u8fc7\u4e00\u62b9\u6cea\u5149\uff0c\u201c\u4f60\u53bb\u544a\u8bc9\u65b9\u90aa\u771f\uff0c\u6211\u4e0d\u4f1a\u62a5\u590d\uff0c\u4f46\u8981\u4ed6\u52a0\u5165\u56de\u5bb6\uff0c\u5e2e\u6211\u6d88\u706d\u6389\u5170\u4ead\u6c60\u5bb6\uff0c\u6211\u4f1a\u597d\u597d\u7684\u91cd\u7528\u4ed6\uff0c\u7edd\u4e0d\u8bb0\u524d\u4ec7\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u4e3a\u4e4b\u9707\u52a8\uff0c\u4f46\u4e5f\u53ea\u80fd\u7b54\uff1a\u201c\u662f\u3002\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u56de\u767e\u5e94\u957f\u53f9\u9053\uff1a\uff1a\u2018\u5999\u624b\u5802\u4e5f\u786e\u6025\u9700\u4eba\u624b\uff0c\u8fd9\u51e0\u5e74\u6765\uff0c\u6709\u5c0f\u7edd\u5728\uff0c\u4ed6\u4e0d\u80af\u4efb\u7528\u4eba\u624d\uff0c\u5012\u662f\u59a8\u788d\u4e86\u5999\u624b\u5802\u7684\u53d1\u5c55\uff0c\u4ed6\u73b0\u5728\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5bf9\u5999\u624b\u5802\u800c\u8a00\uff0c\u672a\u5c1d\u4e0d\u662f\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u4e00\u53e5\u5730\u9053\uff1a\u201c\u6211\u4eec\u73b0\u5728\u8981\u7684\u662f\u4eba\u624d\uff0c\u4e0d\u662f\u6740\u4eba\u3002\u8fd9\u51e0\u5e74\u5c0f\u78a7\u6e56\u6e38\u5bb6\u53d1\u5c55\u5947\u901f\uff0c\u6211\u4eec\u4e0d\u80fd\u518d\u843d\u4eba\u540e\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u53ea\u6709\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u53c8\u770b\u770b\u81ea\u5df1\u7684\u513f\u5b50\uff0c\u7528\u624b\u53bb\u89e6\u4e86\u89e6\u4ed6\u7684\u8138\u5b54\uff0c\u8f7b\u5f97\u50cf\u629a\u4e00\u5934\u719f\u7761\u4e2d\u7684\u732b\u3002\u8fc7\u4e86\u826f\u4e45\uff0c\u624d\u9053\uff1a\u201c\u660e\u5929\uff0c\u6211\u4eec\u8bf7\u7684\u90a3\u4e2a\u4eba\u4e5f\u8be5\u5230\u4e86\u7f62\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u5373\u9053\uff1a\u201c\u2018\u65ad\u7709\u8001\u4e48\u2019\u660e\u5929\u51c6\u5230\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u6487\u4e86\u6487\u5507\uff0c\u4e5f\u4e0d\u77e5\u662f\u82e6\u7b11\uff0c\u8fd8\u662f\u5728\u5fcd\u60b2\uff1a\u201c\u6211\u672c\u6765\u62c5\u5fc3\u77f3\u65ad\u7709\u4e00\u6765\uff0c\u5c0f\u7edd\u51b3\u4e0d\u80fd\u5bb9\u4ed6\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u597d\u4e00\u4f1a\u513f\u624d\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u53ef\u4e0d\u5fc5\u987e\u8651\u8fd9\u4e2a\u4e86\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u89c9\u5f97\u5e94\u8be5\u8981\u628a\u8bdd\u9898\u5c94\u5f00\u53bb\uff0c\u4fbf\u8bf4\uff1a\u201c\u4e0d\u8fc7\uff0c\u65ad\u7709\u8001\u4e48\u7684\u6765\uff0c\u53ea\u6015\u96be\u514d\u8981\u60ca\u52a8\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u5373\u95ee\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u8ffd\u547d\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u76b1\u7709\u9053\uff1a\u201c\u56db\u5927\u540d\u6355\u4e2d\u7684\u5d14\u7565\u5546\uff1f\u201d\u4ed6\u4e00\u76b1\u201c\u7709\u201d\u7684\u65f6\u5019\uff0c\u6574\u5f20\u8138\u90fd\u51e0\u4e4e\u6298\u53e0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u95ee\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u662f\u6709\u5173\u592a\u5b88\u76c2\u968f\u56ed\u88ab\u53d1\u914d\u5145\u519b\u65f6\uff0c\u5728\u67af\u67f3\u5c6f\u9644\u8fd1\u5168\u5bb6\u88ab\u6740\u4e00\u6848\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u52a8\u5bb9\u9053\uff1a\u201c\u5b5f\u9752\u5929\uff1f\u600e\u4e48\u4f1a\u8ddf\u65ad\u7709\u8001\u4e48\u626f\u4e0a\u5173\u7cfb\uff1f\u201d \u3000\u3000\u56de\u767e\u547c\u9053\uff1a\u201c\u5f53\u65f6\u4ed6\u4e5f\u5728\u67af\u67f3\u5c6f\u4e00\u5e26\uff0c\u6848\u53d1\u4e4b\u540e\uff0c\u4ed6\u548c\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u4e00\u8d77\u6765\u8d74\u6d1b\u9633\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u5728\u76b1\u7eb9\u91cc\u85cf\u5f97\u6df1\u6df1\u7684\u7cbe\u76ee\u95ea\u52a8\uff1a\u201c\u4ed6\u4eec\u662f\u4e00\u9053\u7684\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u5176\u4ed6\u4e24\u4eba\u5f53\u7136\u4e0d\u662f\u6211\u4eec\u8bf7\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u4ed6\u4eec\u4e00\u4e2a\u53ef\u80fd\u662f\u5949\u5973\u516c\u5b50\u4e4b\u53ec\uff0c\u4e00\u4e2a\u5219\u662f\u53d7\u6e38\u7389\u906e\u4e4b\u547d\uff0c\u8d76\u6765\u6d1b\u9633\u8944\u52a9\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u201d\u56de\u767e\u5e94\u9053\uff0c\u201c\u6d1b\u9633\u57ce\u8fd9\u6b21\u53ef\u70ed\u95f9\u4e86\u3002\u201d\u7136\u540e\u624d\u95ee\uff0c\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000 \u7b2c\u5341\u7ae0\u3000\u4e03\u53d1\u65ad\u7709 \u3000 \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u4e03\u53d1\u7985\u5e08\u3002\u201d\u4e00\u8bf4\u5230\u6b64\u4eba\uff0c\u56de\u767e\u54cd\u8bed\u97f3\u6c89\u91cd\u3002 \u3000\u3000\u56de\u767e\u5e94\u4e00\u542c\uff0c\u7b2c\u4e8c\u6b21\u52a8\u5bb9\u3002 \u3000\u3000\u201c\u6b27\u9633\u4e03\u53d1\uff1f\u2019\u3001 \u3000\u3000\u56de\u767e\u54cd\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4fbf\u662f\u2018\u767e\u888b\u7ea2\u888d\u3001\u6b27\u9633\u4e03\u53d1\u2019\uff01\u201d \u3000\u3000\u56de\u767e\u5e94\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u60f3\u5fc5\u662f\u6e38\u7389\u906e\uff0c\u624d\u8bf7\u5f97\u52a8\u6b64\u4eba\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4e03\u53d1\u7985\u5e08\u53ea\u8981\u6709\u94b1\uff0c\u51c6\u90fd\u8bf7\u5f97\u5230\u4ed6\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u8981\u5f88\u591a\u7684\u94b1\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff0c\u201c\u4ed6\u5f53\u5e74\u66fe\u7acb\u5fd7\u8981\u5728\u5ce8\u5d4b\u5c71\u4e0a\u7b51\u4e5d\u5341\u4e5d\u5ea7\u5e99\u5b57\uff0c\u542c\u8bf4\u73b0\u5728\u4ed6\u5df1\u6709\u8db3\u591f\u7684\u94b1\u5efa\u8d77\u4e09\u5341\u516d\u95f4\u5927\u5e99\u5c0f\u5e99\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u4ee5\u51fa\u5bb6\u4eba\u800c\u8bba\uff0c\u8fd9\u4e03\u53d1\u5927\u5e08\u53ef\u8c13\u5bcc\u8c6a\u4e86\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u624d\u7a7f\u6709\u6570\u5341\u4e2a\u5927\u53e3\u888b\u7684\u8888\u88df\uff0c\u51fa\u5916\u5316\u7f18\uff0c\u6bcf\u6b21\u542c\u8bf4\u90fd\u80fd\u6ee1\u8f7d\u800c\u5f52\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u60f3\u4e86\u60f3\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u50cf\u4e03\u53d1\u5927\u5e08\u8fd9\u6837\u5b50\u7684\u5f3a\u52a9\uff0c\u6211\u4eec\u5341\u5206\u9700\u8981\u3002\u522b\u7684\u6211\u6ca1\u6709\uff0c\u4f46\u8981\u6350\u5efa\u4e00\u4e8c\u5341\u95f4\u5e99\u5b57\uff0c\u6211\u8fd8\u662f\u5e03\u65bd\u5f97\u8d77\u7684\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u606d\u8c28\u5730\u9053\uff1a\u201c\u662f\u3002\u201d\u5fc3\u91cc\u6697\u5fd6\uff1a\u82e5\u8981\u8bf7\u8fd9\u4f4d\u5f02\u50e7\u52a9\u9635\uff0c\u6240\u4ed8\u7684\u4ee3\u4ef7\u53ef\u4ee5\u7b97\u662f\u5999\u624b\u5802\u5386\u5e74\u7f55\u89c1\u7684\u4e00\u7b14\u652f\u51fa\u4e86\u3002 \u3000\u3000\u9664\u4e86\u7528\u4ee5\u8054\u7edc\u671d\u5ef7\u3001\u5df4\u7ed3\u5b98\u5e9c\uff0c\u5999\u624b\u5802\u4e00\u5411\u662f\u6536\u8d26\u65f6\u5019\u591a\uff0c\u5f88\u5c11\u8981\u4ed8\u51fa\u8fd9\u4e48\u591a\u7684\uff0c\u56de\u7edd\u624d\u6b7b\uff0c\u56de\u767e\u5e94\u9a6c\u4e0a\u53d8\u4e86\u3002\u770b\u6765\u4e0d\u4f46\u4e0d\u4e3a\u4e4b\u5fc3\u6cae\uff0c\u53cd\u800c\u51c6\u5907\u91cd\u65b0\u632f\u4f5c\u3002 \u3000\u3000\u2014\u2014\u53ea\u662f\u4e03\u53d1\u7985\u5e08\u503c\u4e0d\u503c\u5f97\u8fd9\u4e2a\u4ee3\u4ef7\uff1f \u3000\u3000\u56de\u767e\u54cd\u5f88\u6000\u7591\u3002 \u3000\u3000\u4ed6\u5728\u5fc3\u75bc\u8fd9\u7b14\u6b3e\u5b50\u3002 \u3000\u3000\u56de\u767e\u5e94\u8fde\u773c\u76ae\u5b50\u90fd\u6ca1\u6709\u62ac\uff0c\u5374\u4f3c\u770b\u900f\u4e86\u4ed6\u7684\u5fc3\u4e8b\u3002\u201c\u8981\u505a\u5927\u4e8b\uff0c\u5c31\u5f97\u4e0b\u82e6\u529f\u3002\u8981\u6210\u5927\u4e8b\uff0c\u4fbf\u5f97\u4e0b\u672c\u94b1\uff0c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u5d1b\u8d77\u5f97\u8fd9\u822c\u5feb\uff0c\u4fbf\u662f\u56e0\u4e3a\u770b\u5f97\u8fdc\u3001\u770b\u5f97\u51c6\uff0c\u800c\u4e14\u624b\u7b14\u5f88\u5927\uff0c\u9b44\u529b\u5341\u8db3\uff0c\u7528\u5f97\u8d77\u4eba\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\u53c8\u9053\uff1a\u201c\u654c\u4eba\u7684\u4f18\u70b9\uff0c\u6211\u4eec\u4e00\u5b9a\u8981\u7559\u5fc3\uff0c\u5e76\u8981\u7262\u7262\u8bb0\u4f4f\u3002\u6211\u4eec\u5e94\u8be5\u6293\u4f4f\u654c\u4eba\u7684\u7f3a\u70b9\uff0c\u4f46\u66f4\u91cd\u8981\u7684\u662f\u5b66\u4e60\u654c\u4eba\u7684\u957f\u5904\uff0c\u8fd9\u6837\u5b50\u5bf9\u654c\u3001\u624d\u4e0d\u662f\u8017\u635f\uff0c\u53cd\u800c\u6709\u8fdb\u76ca\u3002\u201d \u3000\u3000\u56de\u767e\u554a\u53ea\u89c9\u5f97\u4ece\u754f\u610f\u4e4b\u5916\uff0c\u53c8\u6cb9\u7136\u751f\u8d77\u4e00\u79cd\u656c\u610f\u3002 \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u8fd9\u624d\u6ee1\u610f\uff0c\u95ee\uff1a\u201c\u90a3\u845b\u94c3\u94c3\u53eb\u6765\u7684\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u5947\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u6211\u4eec\u53ea\u77e5\u9053\u4ed6\u662f\u4e00\u4e2a\u5e74\u8f7b\u4eba\uff0c\u989d\u4e0a\u6709\u4e00\u9897\u7070\u75e3\uff0c\u540d\u53eb\u8521\u65cb\u949f\uff0c\u6211\u4eec\u6000\u7591\u4ed6\u53e6\u5916\u6709\u540d\u5b57\uff0c\u6709\u4e03\u516b\u540d\u5e74\u8f7b\u4e00\u8f88\u9ad8\u624b\u8ddf\u4ed6\u90fd\u6709\u70b9\u76f8\u4f3c\uff0c\u4f46\u5374\u672a\u80fd\u8bc1\u5b9e\u7a76\u7adf\u662f\u4e0d\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u8521\u65cb\u949f\uff1f\u201d \u3000\u3000\u201c\u8521\u65cb\u949f\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7528\u751a\u4e48\u5175\u5668\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u8fd8\u6ca1\u89c1\u8fc7\u4ed6\u52a8\u5175\u5668\uff0c\u53ea\u77e5\u9053\u4ed6\u624b\u4e0a\u62ff\u7740\u4e00\u628a\u5251\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u5251\u662f\u6700\u666e\u904d\u7684\u5175\u5668\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u4f46\u8fd9\u662f\u4e00\u628a\u7279\u522b\u7684\u5251\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u600e\u4e48\u7279\u522b\u6cd5\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u4ed6\u90a3\u4e00\u67c4\u5251\uff0c\u81f3\u5c11\u6709\u4e5d\u5c3a\u957f\u3002\u201d \u3000\u3000\u5251\u901a\u5e38\u53ea\u4e09\u5c3a\u4e03\u5bf8\uff0c\u903e\u56db\u5c3a\u4fbf\u4e3a\u957f\u5251\uff0c\u800c\u4eca\u8fd9\u4e00\u628a\u5251\uff0c\u7adf\u957f\u6709\u4e5d\u5c3a\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c\u4f7f\u7528\u8d77\u6765\u5c31\u76f8\u5f53\u8d39\u4e8b\u3002 \u3000\u3000\u90a3\u662f\u751a\u4e48\u5251\u6cd5\uff0c\u624d\u9700\u8981\u8fd9\u6837\u4e00\u67c4\u957f\u5251\uff1f \u3000\u3000\u56de\u767e\u5e94\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u624d\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5927\u6982\u660e\u5929\u8fd9\u4e09\u4eba\u5c31\u4f1a\u9047\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u8fd8\u4f1a\u78b0\u4e0a\u4e86\u8ffd\u547d\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u8ffd\u547d\u4e00\u76f4\u90fd\u5728\u8ffd\u8e2a\u4ed6\u4eec\u4e09\u4eba\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u4ed6\u4e00\u4e2a\u4eba\u8ffd\u8e2a\u4ed6\u4eec\u4e09\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u56de\u767e\u5e94\u9053\uff1a\u201c\u4ee5\u8ffd\u547d\u7684\u6b66\u529f\uff0c\u4ee5\u4e00\u654c\u4e00\uff0c\u5e94\u8be5\u7edd\u4e0d\u6210\u95ee\u9898\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u5373\u54cd\u5e94\u9053\uff1a\u201c\u4ee5\u4e00\u654c\u4e09\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u660e\u5929\u6d1b\u9633\u57ce\u91cc\u53c8\u6709\u597d\u620f\u770b\u4e86\uff0c\u201d\u56de\u767e\u5e94\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u60f3\u4f38\u624b\u6478\u6478\u56de\u7edd\u7684\u8138\uff0c\u4f46\u53c8\u628a\u624b\u62e2\u5165\u8896\u5b50\u4e2d\uff0c\u58f0\u97f3\u91cc\u7ec8\u4e8e\u6d41\u9732\u51fa\u60b2\u75db\uff0c\u201c\u8981\u662f\u5c0f\u7edd\u5728\u5e73\u65f6\uff0c\u6709\u8fd9\u4e48\u70ed\u95f9\u7684\u4e8b\u513f\uff0c\u4ed6\u4e00\u5b9a\u4e89\u7740\u53bb\u77a7\u7684\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u8bed\u97f3\u4e00\u6574\uff0c\u53c8\u53d8\u4e3a\u51b7\u9759\u3002\u7a33\u5b9a\u3001\u4f4e\u6c89\u5f97\u7565\u5e26\u6c99\u54d1\u3001\u5145\u6ee1\u6743\u5a01\u548c\u98ce\u971c\uff0c\u201c\u65b9\u90aa\u771f\u90a3\u513f\uff0c\u5999\u624b\u5802\u8981\u7528\u4ed6\uff0c\u4e0d\u80fd\u7528\uff0c\u624d\u9664\u53bb\u3002\u4e03\u53d1\u7985\u5e08\uff0c\u5168\u529b\u4e89\u53d6\u3002\u65ad\u7709\u8001\u5e7a\uff0c\u7740\u4ed6\u5148\u6765\u89c1\u6211\u3002\u90a3\u8521\u65cb\u949f\u8981\u597d\u597d\u76ef\u7740\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u4f38\u624b\u642d\u5728\u513f\u5b50\u7684\u5c38\u9996\u4e0a\uff0c\u4eff\u4f5b\u8981\u611f\u89c9\u4ed6\u8fd8\u6709\u6ca1\u6709\u5fc3\u8df3\uff1a\u201c\u4f60\u5429\u5490\u4e0b\u53bb\u7f62\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\u9053\uff1a\u201c\u662f\u3002\u201d\u8eac\u8eab\u9000\u4e0b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u90a3\u4f4d\u201c\u8001\u4eba\u201d\u9700\u8981\u65f6\u95f4\u8ddf\u4ed6\u7684\u201c\u5b69\u5b50\u201d\u5728\u4e00\u8d77\uff0c\u4ed6\u77e5\u673a\u5730\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u9000\u51fa\u5ba4\u5185\uff0c\u4fbf\u5230\u4e86\u4e00\u4e2a\u8bae\u4e8b\u7684\u5385\u5802\u91cc\u3002 \u3000\u3000\u201c\u5999\u624b\u5802\u201d\u7684\u91cd\u8981\u4eba\u5458\u5168\u5728\u90a3\u513f\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u7b49\u7684\uff0c\u4e5f\u8bb8\u6839\u672c\u4e0d\u662f\u56de\u767e\u54cd\uff0c\u800c\u662f\u90a3\u4f4d\u72ec\u5b50\u521a\u8fc7\u4e16\u7684\u8001\u4eba\u6240\u53d1\u7684\u547d\u4ee4\u3002 \u3000\u3000\u5f88\u591a\u4eba\u90fd\u4ee5\u4e3a\u96be\u514d\u4f1a\u6709\u4e00\u573a\u51b3\u5b9a\u6027\u7684\u4f1a\u6218\uff0c\u5999\u624b\u5802\u8981\u94f2\u9664\u654c\u624b\u52bf\u529b\u7684\u65f6\u673a\u8981\u5230\u4e86\u2014\u2014\u5927\u591a\u6570\u4eba\u90fd\u5728\u6469\u62f3\u64e6\u638c\uff0c\u51c6\u5907\u706b\u62fc\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u5999\u624b\u5802\u5fe0\u5fc3\u803f\u803f\u7684\u5e72\u5458\uff0c\u56de\u7edd\u8eab\u4ea1\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u5168\u653e\u4e0b\u4e86\u624b\u8fb9\u7684\u91cd\u8981\u4e8b\u60c5\uff0c\u8d76\u6765\u8fd9\u513f\u805a\u96c6\uff0c\u53ea\u7b49\u5f85\u56de\u5927\u7237\u7684\u4e00\u53e5\u8bdd\uff0c\u4e00\u4e2a\u547d\u4ee4\u3002 \u3000\u3000\u4f46\u8fd9\u79cd\u201c\u547d\u4ee4\u201d\u901a\u5e38\u90fd\u662f\u7531\u56de\u767e\u54cd\u6765\u8f6c\u8fbe\u3002 \u3000\u3000\u6240\u4ee5\u5f53\u56de\u767e\u54cd\u4f20\u8fbe\u4e86\u56de\u767e\u5e94\u7684\u51b3\u7b56\uff1a\u201c\u4e0d\u8981\u62a5\u4ec7\uff0c\u62c9\u62e2\u65b9\u90aa\u771f\uff0c\u6536\u63fd\u4e03\u53d1\u5927\u5e08\uff0c\u91cd\u7528\u65ad\u7709\u8001\u4e48\uff0c\u62d3\u5c55\u5999\u624b\u5802\u3002\u201d\u8bb8\u591a\u4eba\u90fd\u89c9\u5f97\u5f88\u5931\u671b\uff0c\u751a\u81f3\u6709\u4e9b\u4e0d\u6ee1\u3002 \u3000\u3000\u2014\u2014\u4eba\u5fc3\u53ef\u7528\uff01 \u3000\u3000\u2014\u2014\u54c0\u5175\u5fc5\u80dc\uff01 \u3000\u3000\u2014\u2014\u600e\u4e48\u4e0d\u8d81\u8fd9\u65f6\u5019\u5927\u4e3e\u53cd\u51fb\u5170\u4ead\u6c60\u5bb6\uff0c\u81f3\u5c11\uff0c\u4e5f\u8be5\u628a\u6740\u4eba\u8005\u65b9\u90aa\u771f\u788e\u5c38\u4e07\u6bb5\uff01 \u3000\u3000\u2014\u2014\u81f3\u4e8e\u4eba\u624d\uff0c\u5802\u91cc\u5b50\u4f84\uff0c\u6709\u7684\u662f\u51fa\u8272\u4eba\u7269\uff0c\u5802\u4e3b\u7adf\u5047\u624b\u5916\u6c42\uff01 \u3000\u3000\u8bb8\u591a\u4eba\u90fd\u89c9\u5f97\u5f88\u6709\u4e9b\u6124\u6124\u96be\u5e73\u3002 \u3000\u3000\u5176\u5b9e\u4f20\u8fbe\u67d0\u4eba\u7684\u8bdd\uff0c\u7edd\u5bf9\u662f\u4ef6\u5927\u5b66\u95ee\u3002 \u3000\u3000\u4f60\u8981\u4e00\u4e2a\u4eba\u53bb\u505a\u4e00\u4ef6\u4e8b\uff0c\u672c\u6765\u662f\u6709\u5fc3\u683d\u57f9\u4ed6\uff0c\u7ed9\u4ed6\u673a\u4f1a\uff0c\u4f46\u5982\u679c\u4f20\u8fbe\u7684\u4eba\u628a\u63e1\u4e0d\u4f4f\u539f\u610f\u3002\u5f88\u53ef\u80fd\u4f1a\u8ba9\u5bf9\u65b9\u4ee5\u4e3a\u662f\u4f60\u53ea\u5728\u6d88\u9063\u4ed6\u3001\u7559\u96be\u4ed6\u3001\u751a\u81f3\u8ba4\u4e3a\u53ea\u662f\u5728\u9ebb\u70e6\u4ed6\u3001\u9a9a\u6270\u4ed6\uff1b\u540c\u6837\u7684\uff0c\u5982\u679c\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u4e00\u4ef6\u6709\u8da3\u6216\u6709\u610f\u4e49\u7684\u91cd\u5927\u4e8b\u60c5\uff0c\u7ed9\u6beb\u65e0\u8bda\u610f\u6216\u5168\u65e0\u60c5\u8da3\u7684\u4eba\u6765\u8f6c\u8ff0\uff0c\u3001\u5c31\u6210\u4e86\u67af\u71e5\u65e0\u5473\u95f7\u715e\u4eba\u7684\u672b\u8282\u3002 \u3000\u3000\u5927\u51e1\u6210\u529f\u7684\u9886\u8896\u90fd\u4f1a\u6709\u6781\u4f73\u7684\u201c\u4f20\u8fbe\u4eba\u201d\uff0c\u597d\u7684\u201c\u4f20\u8fbe\u8005\u201d\u53ef\u4ee5\u628a\u597d\u7684\u4e8b\u60c5\u53d8\u6210\u66f4\u597d\uff0c\u66ff\u8fc7\u5206\u7684\u8bdd\u8bed\u4f5c\u8865\u6551\u3001\u628a\u7834\u574f\u6027\u7684\u90e8\u5206\u5316\u89e3\u4e3a\u5efa\u8bbe\u7684\u3002\u6240\u4ee5\u4e00\u4e2a\u6210\u529f\u7684\u201c\u8f6c\u8fbe\u8005\u201d\u529f\u52b3\u4e4b\u9ad8\uff0c\u51b3\u4e0d\u5728\u5176\u4ed6\u3001\u201c\u529f\u81e3\u201d\u4e4b\u4e0b\u3002 \u3000\u3000\u4e00\u4e2a\u574f\u7684\u4f20\u8fbe\u4eba\uff0c\u5c0f\u53ef\u6bc1\u574f\u548c\u8c10\u7684\u5173\u7cfb\uff0c\u5927\u53ef\u6bc1\u56fd\u706d\u90a6\u3002 \u3000\u3000\u56de\u767e\u54cd\u53ea\u4f20\u8fbe\uff0c\u4f46\u4e0d\u4f5c\u89e3\u91ca\u3002 \u3000\u3000\u6709\u4e9b\u63aa\u65bd\uff0c\u4e0d\u7ecf\u89e3\u91ca\uff0c\u6709\u5f88\u591a\u4eba\u56e0\u667a\u529b\u4e0e\u7406\u89e3\u7684\u89d2\u5ea6\uff0c\u5f88\u53ef\u80fd\u4f1a\u4ea7\u751f\u8bef\u89e3\u3002 \u3000\u3000\u56de\u767e\u54cd\u53ef\u4e0d\u7ba1\u8fd9\u4e9b\u3002 \u3000\u3000\u4ed6\u53ea\u628a\u56de\u4e07\u96f7\u627e\u6765\u3002 \u3000\u3000\u56de\u4e07\u96f7\u662f\u201c\u5999\u624b\u5802\u201d\u91cc\u4e3b\u6301\u6b66\u529b\u884c\u52a8\u7684\u4eba\u3002\u4ed6\u5982\u679c\u8d70\u51fa\u201c\u5999\u624b\u5802\u201d\uff0c\u8eab\u4efd\u7edd\u5bf9\u4e0d\u5728\u5f53\u4eca\u6b66\u6797\u5341\u4e00\u5927\u95e8\u6d3e\u638c\u95e8\u4eba\u4e4b\u4e0b\uff0c\u800c\u6b66\u529f\u4e4b\u9ad8\uff0c\u53ea\u6015\u4ec5\u5728\u5c11\u6797\u3002\u6b66\u5f53\u3001\u98de\u9c7c\u5858\uff0c\u51e4\u5c3e\u5e2e\u3001\u91d1\u98ce\u7ec6\u96e8\u697c\u3001\u516d\u5206\u534a\u5802\uff0c\u5929\u6b32\u5bab\u7684\u9996\u9886\u4e4b\u540e\u3002 \u3000\u3000\u56de\u4e07\u96f7\u50cf\u4e00\u68f5\u6811\u3002 \u3000\u3000\u8001\u6811\u76d8\u6839\u3002 \u3000\u3000\u4e25\u683c\u6765\u8bf4\uff0c\u4ed6\u66f4\u50cf\u4e00\u682a\u795e\u6728\u3002 \u3000\u3000\u4e00\u682a\u66fe\u88ab\u96f7\u6b9b\u8fc7\u7684\u795e\u6728\u3002 \u3000\u3000\u56de\u4e07\u96f7\u4e88\u4eba\u7684\u5370\u8c61\uff0c\u662f\u96f7\u5288\u90fd\u4e0d\u6b7b\u7684\u4eba\uff0c\u800c\u4e14\u80fd\u5728\u96f7\u6b9b\u540e\u91cd\u751f\u3002\u800c\u4e8b\u5b9e\u4e0a\uff0c\u56de\u4e07\u96f7\u771f\u7684\u7740\u8fc7\u4e24\u6b21\u96f7\u6b9b\uff1a\u4e00\u6b21\u771f\u7684\u88ab\u7535\u5288\u4e2d\u800c\u4e0d\u6b7b\uff0c\u53e6\u4e00\u6b21\uff0c\u662f\u4e3a\u56de\u767e\u5e94\u5728\u5317\u4eac\u57ce\u91cc\u4e89\u5730\u76d8\uff0c\u7ed3\u679c\u88ab\u201c\u516d\u5206\u534a\u5802\u201d\u91cc\u7684\u7b2c\u4e00\u597d\u624b\u96f7\u52a8\u5929\u7528\u201c\u4e94\u96f7\u5929\u5fc3\u201d\u51fb\u4e2d\u95e8\u9876\uff0c\u8fde\u56de\u767e\u5e94\u90fd\u4ee5\u4e3a\u4ed6\u662f\u6b7b\u5b9a\u4e86\uff0c\u53ef\u662f\u4ed6\u5c45\u7136\u4e0d\u6b7b\u3002 \u3000\u3000\u6240\u4ee5\u56de\u4e07\u96f7\u5728\u6b66\u6797\u4e2d\uff0c\u4e5f\u88ab\u89c6\u4e3a\u4e00\u4e2a\u201c\u4e0d\u6b7b\u7684\u4eba\u201d\u3002 \u3000\u3000\u4ed6\u4e0d\u6b7b\uff0c\u4f46\u6b7b\u5728\u4ed6\u624b\u4e0a\u7684\u4eba\uff0c\u7740\u5b9e\u662f\u592a\u591a\u592a\u591a\u4e86\u3002 \u3000\u3000\u5f53\u201c\u5999\u624b\u5802\u201d\u521d\u5d1b\u4e4b\u9645\uff0c\u4ed6\u81ea\u89c9\u6740\u4eba\u5927\u591a\uff0c\u6545\u9650\u5236\u81ea\u5df1\uff0c\u4e00\u5929\u53ea\u51c6\u6740\u4e09\u4e2a\u4eba\uff0c\u53ef\u662f\u5230\u4e86\u540e\u6765\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u6562\u518d\u7b97\u4e0b\u53bb\u3002 \u3000\u3000\u518d\u7b97\u4e0b\u53bb\uff0c\u4ed6\u81ea\u5df1\u90fd\u4f1a\u611f\u5230\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u81ea\u5df1\u90fd\u7b97\u4e0d\u6e05\uff0c\u6709\u65f6\u5019\uff0c\u4ed6\u4e00\u5929\u5c31\u6740\u4e86\u672c\u662f\u4e00\u4e2a\u6708\u624d\u8be5\u8fbe\u5230\u7684\u4eba\u6570\u3002 \u3000\u3000\u56de\u767e\u54cd\u95ee\u4ed6\uff1a\u201c\u4f60\u89c9\u5f97\u5802\u4e3b\u7684\u51b3\u5b9a\u5982\u4f55\uff1f\u201d \u3000\u3000\u56de\u4e07\u96f7\u63e1\u7d27\u62f3\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u662f\u75af\u4e86\u3002\u201d\u7531\u4e8e\u4ed6\u5728\u5999\u624b\u5802\u91cc\u6709\u7740\u6781\u5d07\u9ad8\u7684\u5730\u4f4d\uff0c\u548c\u6781\u663e\u8d6b\u7684\u529f\u7ee9\uff0c\u4ee5\u53ca\u4e0e\u56de\u767e\u5e94\u6781\u4eb2\u5bc6\u7684\u5173\u7cfb\uff0c\u6240\u4ee5\u4ed6\u6bd4\u8f83\u65b9\u4fbf\u8bf4\u8bdd\uff0c\u751a\u81f3\u6279\u8bc4\u3002 \u3000\u3000\u2014\u2014\u81ea\u53e4\u4ee5\u6765\uff0c\u81ea\u4ee5\u4e3a\u201c\u80fd\u8bf4\u51e0\u53e5\u201d\u3001\u201c\u8be5\u8bf4\u51e0\u53e5\u201d\u800c\u906d\u7978\u7684\u4eba\uff0c\u4e0d\u8ba1\u5176\u6570\uff1b\u4eba\u4eba\u90fd\u4ee5\u4e3a\u8bf4\u51e0\u53e5\u8bdd\u5e94\u8be5\u201c\u6ca1\u6709\u751a\u4e48\u5173\u7cfb\u201d\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u8bf4\u51e0\u53e5\u6709\u65f6\u5019\u8db3\u53ef\u7b49\u4e8e\u523a\u522b\u4eba\u51e0\u5200\uff0c\u6216\u662f\u6345\u81ea\u5df1\u51e0\u5200\u4e00\u822c\u4e25\u91cd\u3002 \u3000\u3000\u56de\u767e\u54cd\u77e5\u9053\u8fd9\u79cd\u4e25\u91cd\u6027\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u66ff\u4eba\u8bf4\u597d\u8bdd\u662f\u4e00\u4ef6\u503c\u94b1\u7684\u4e8b\uff0c\u6240\u4ee5\u4ed6\u5341\u5206\u614e\u8a00\uff0c\u4e0d\u8bf4\u6ca1\u6709\u4ee3\u4ef7\u7684\u597d\u8bdd\u3002 \u3000\u3000\u504f\u504f\u56de\u767e\u5e94\u5f88\u4fe1\u4efb\u4ed6\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u7edd\u5bf9\u7684\u4fe1\u4efb\u4ed6\uff0c\u4f46\u5c31\u662f\u5728\u201c\u94b1\u201d\u5b57\u4e0a\uff0c\u5374\u662f\u7edd\u5bf9\u7684\u4e0d\u4fe1\u4efb\u4ed6\u3002 \u3000\u3000\u5728\u91d1\u94b1\u4e0a\uff0c\u56de\u767e\u5e94\u662f\u4fe1\u4efb\u4ed6\u7684\u592b\u4eba\u3002 \u3000\u3000\u56de\u592b\u4eba\u5374\u4e0d\u4fe1\u4efb\u4ed6\u3002 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\uff0c\u901a\u5e38\uff0c\u5728\u6743\u529b\u6597\u4e89\u7684\u8fd0\u4f5c\u91cc\uff0c\u53d4\u5ac2\u4e4b\u95f4\u672c\u5c31\u5bb9\u6613\u5f62\u6210\u5bf9\u6297\u4e0e\u51b2\u7a81\u3002 \u3000\u3000\u56de\u767e\u54cd\u6700\u9700\u8981\u7684\u5c31\u662f\u94f6\u5b50\u3002 \u3000\u3000\u8fd9\u70b9\u4ed6\u65e0\u6cd5\u5f97\u5230\u6ee1\u8db3\uff0c\u53ea\u597d\u5047\u624b\u5916\u6c42\uff0c\u5230\u540e\u6765\u53d1\u73b0\u552f\u4e00\u80fd\u4f7f\u4ed6\u624b\u5934\u5bbd\u7ef0\u81ea\u5982\u7684\uff0c\u5374\u662f\u5170\u4ead\u6c60\u5bb6\u3002 \u3000\u3000\u5f53\u4ed6\u53d1\u73b0\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u4e0d\u80fd\u56de\u5934\u3002 \u3000\u3000\u2014\u2014\u8981\u662f\u56de\u767e\u5e94\u53d1\u73b0\u4ed6\u6b20\u4e0b\u6c60\u5bb6\u90a3\u4e48\u591a\u94b1\uff0c\u53ea\u628a\u4ed6\u7684\u5934\u6495\u4e0b\u6765\u5582\u72d7\u4e5f\u7b97\u5e78\u8fd0\u3002 \u3000\u3000\u4ed6\u552f\u4e00\u7684\u529e\u6cd5\u5c31\u662f\u4f7f\u6c60\u5bb6\u4e0d\u5411\u4ed6\u8ba8\u507f\u3002 \u3000\u3000\u4e3a\u4e86\u8fd9\u70b9\uff0c\u4ed6\u96be\u514d\u8981\u4e3a\u6c60\u5bb6\u201c\u505a\u4e00\u70b9\u70b9\u5c0f\u4e8b\u201d\uff0c\u5305\u62ec\u6709\u65f6\u5019\u201c\u5c0f\u8bf4\u4e00\u4e24\u53e5\u8bdd\u201d\uff0c\u6709\u65f6\u5019\u8981\u201c\u591a\u8bf4\u4e00\u4e24\u53e5\u8bdd\u201d\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u6700\u7ec8\u6216\u6700\u5927\u7684\u76ee\u6807\uff0c\u4e5f\u8bb8\u662f\u6709\u4e00\u5929\uff0c\u53ef\u4ee5\u76f4\u63a5\u638c\u7ba1\u56de\u5bb6\u7684\u8d22\u5e93\uff0c\u751a\u81f3\u63a7\u5236\u56de\u5bb6\u7684\u6743\u529b\u91cd\u5fc3\uff0c\u8fd9\u4e00\u70b9\uff0c\u53ea\u8981\u56de\u767e\u5e94\u5728\u4e16\uff0c\u5bf9\u4ed6\u800c\u8a00\uff0c\u662f\u4e00\u4ef6\u7edd\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u9664\u975e\u662f\u6c60\u65e5\u66ae\u534f\u52a9\u4ed6\u3002 \u3000\u3000\u4ee5\u4e0b\u5c31\u662f\u56de\u767e\u54cd\u5bf9\u56de\u4e07\u96f7\u591a\u52a0\u7684\u51e0\u53e5\u8bdd\u3002 \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u4f60\u89c9\u4e0d\u89c9\u5f97\u65b9\u90aa\u771f\u8be5\u6740\uff1f\u201d \u3000\u3000\u56de\u4e07\u96f7\uff0c\u201c\u8be5\u6740\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u5c0f\u7edd\u6b7b\u4e86\uff0c\u4f60\u75db\u4e0d\u75db\u5fc3\uff1f\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u4e0d\u75db\u5fc3\u3002\u6211\u75db\u6068\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u8fde\u4f60\u4e5f\u75db\u5fc3\uff0c\u96be\u9053\u5802\u4e3b\u53cd\u800c\u4e0d\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u5802\u4e3b\u6700\u75bc\u5c0f\u7edd\uff0c\u600e\u53ef\u80fd\u4e0d\u4f24\u5fc3\uff01\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u4fbf\u662f\u3002\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u5802\u4e3b\u5fc5\u6bd4\u6211\u4eec\u66f4\u75db\u6068\u65b9\u90aa\u771f\u3002\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u53ea\u662f\u4ed6\u4e0d\u4fbf\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u65b9\u90aa\u771f\u6b66\u529f\u60f3\u5fc5\u5f88\u9ad8\u3002\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u9ad8\u53c8\u5982\u4f55\uff01\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u5802\u4e3b\u5f53\u7136\u4e0d\u5e0c\u671b\u6709\u4eba\u5e73\u767d\u727a\u7272\u3002\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u7b11\u8bdd\uff01\u6211\u4e14\u64b7\u4e0b\u4ed6\u7684\u5934\u6765\u89c1\u5802\u4e3b\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u4f60\u4e0d\u6015\uff1f\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u6015\uff1f\u6015\u751a\u4e48\uff01\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u597d\uff0c\u6709\u52c7\u6c14\uff01\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u5802\u4e3b\u5bf9\u6211\u6069\u91cd\u5982\u5c71\uff0c\u4e3a\u4ed6\u6548\u6b7b\uff0c\u8d23\u65e0\u65c1\u8d37\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u53ef\u662f\u751a\u4e48\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u5802\u4e3b\u5e76\u6ca1\u6709\u4e0b\u4ee4\u6740\u65b9\u90aa\u771f\uff0c\u4e07\u4e00\u2026\u2026\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u662f\u6211\u81ea\u5df1\u8981\u6740\u7684\uff0c\u4e07\u4e00\u51fa\u4e86\u4e8b\uff0c\u7531\u6211\u81ea\u5df1\u627f\u62c5\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u5927\u8205\u771f\u4e0d\u6127\u662f\u5999\u624b\u5802\u7b2c\u4e00\u597d\u6c49\uff01\u201d \u3000\u3000\u56de\u4e07\u96f7\uff1a\u201c\u6211\u53ea\u662f\u505a\u8be5\u505a\u7684\u4e8b\uff0c\u6740\u8be5\u6740\u7684\u4eba\u3002\u201d \u3000\u3000\u56de\u767e\u54cd\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u65b9\u90aa\u771f\u4e00\u5b9a\u8be5\u6740\uff0c\u4f60\u53ea\u8981\u6740\u4e86\u4ed6\uff0c\u4fbf\u7b97\u662f\u505a\u4e86\u4ef6\u8be5\u505a\u7684\u4e8b\uff0c\u4e07\u4e00\u6740\u4e0d\u4e86\u4ed6\uff0c\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u4f1a\u66ff\u4f60\u505a\u4e00\u4e9b\u8be5\u505a\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u53bb\u66ff\u56de\u7edd\u529e\u7406\u846c\u4e8b\u7684\u65f6\u5019\uff0c\u987a\u4fbf\u591a\u4e70\u4e86\u4e00\u526f\u68fa\u6750\u3002 \u3000\u3000\u68fa\u6750\u5e97\u8001\u677f\u95ee\u4ed6\u7075\u724c\u4e0a\u8981\u5199\u4e0a\u751a\u4e48\u540d\u5b57\uff0c\u56de\u767e\u54cd\u60f3\u4e86\u60f3\uff0c\u7b11\u7740\u53cd\u95ee\u68fa\u6750\u5e97\u8001\u677f\uff1a\u201c\u4f60\u731c\u731c\u770b\uff1f\u59d3\u65b9\u8fd8\u662f\u59d3\u56de\u7684\uff1f\u201d \u3000\u3000\u95e8\u8fd8\u662f\u6572\u54cd\u7740\u3002 \u3000\u3000\u8f7b\u8f7b\u3002 \u3000\u3000\u8f7b\u8f7b\u7684\u8f7b\u8f7b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53bb\u5f00\u95e8\u3002 \u3000\u3000\u4e00\u4e2a\u548c\u853c\u3001\u798f\u6cf0\u3001\u6709\u793c\uff0c\u4f46\u773c\u775b\u91cc\u6d41\u9732\u7684\u795e\u91c7\u8db3\u4ee5\u4f24\u4eba\u7684\u5546\u4eba\u3002 \u3000\u3000\u5546\u4eba\u5927\u591a\u548c\u6c14\u3002 \u3000\u3000\u2014\u2014\u4e5f\u8bb8\u4ed6\u4eec\u6df1\u77e5\u201c\u548c\u6c14\u751f\u8d22\u201d\u7684\u7a8d\u5999\uff0c\u201c\u548c\u201d\u662f\u4e00\u4e2a\u88ab\u5ffd\u7565\u4e86\u8fd1\u4e24\u5343\u5e74\u7684\u5b57\uff0c\u6240\u4ee5\u5728\u5386\u53f2\u4e0a\u6709\u7684\u662f\u5185\u4e89\u3001\u5185\u54c4\u4e0e\u5185\u6597\uff0c\u800c\u6ca1\u6709\u529e\u6cd5\u56e2\u7ed3\u8d77\u6765\uff0c\u201c\u4ee5\u548c\u4e3a\u8d35\u201d\u3002 \u3000\u3000\u5546\u4eba\u90fd\u77e5\u9053\uff0c\u8981\u505a\u4e8b\uff0c\u8ba9\u4eba\u4fbf\u5229\uff0c\u4f7f\u81ea\u5df1\u5f97\u5229\uff0c\u975e\u8981\u548c\u548c\u6c14\u6c14\u3001\u548c\u5e73\u5171\u5904\u4e0d\u53ef\u3002 \u3000\u3000\u8fd9\u4e2a\u968f\u548c\u5f97\u5f88\u7684\u5546\u4eba\uff0c\u5374\u6b63\u662f\u5f53\u4eca\u6b66\u6797\u79f0\u4e4b\u4e3a\u201c\u6a2a\u5200\u7acb\u9a6c\u3001\u9189\u5367\u5c71\u5c97\u201d\u7684\u201c\u987e\u76fc\u795e\u98ce\u201d \u3000\u3000\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u4ed6\u6765\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u6211\u4f1a\u4e0d\u4f1a\u9a9a\u6270\u5230\u4f60\u4eec\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u987e\u5148\u751f\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u53ea\u662f\u5fd8\u4e86\u544a\u8bc9\u65b9\u5c11\u4fa0\u4e00\u4ef6\u4e8b\u513f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u751a\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff0c\u201c\u65b9\u5c11\u4fa0\u542c\u8bf4\u8fc7\u2018\u7ea2\u888d\u767e\u888b\uff0c\u4e03\u53d1\u7985\u5e08\uff0c\u8fd9\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u77b3\u5b54\u6536\u7f29\uff1a\u201c\u6b27\u9633\u4e03\u53d1\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u5f88\u591a\u4eba\u90fd\u8bf4\uff0c\u2018\u5929\u6b32\u5bab\u2019\u8981\u4e0d\u662f\u6709\u4e03\u53d1\u5927\u5e08\u7684\u5f3a\u52a9\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u6709\u751a\u4e48\u7a81\u7834\u6027\u7684\u53d1\u5c55\uff0c\u2018\u957f\u7a7a\u5e2e\u2019\u82e5\u4e0d\u662f\u63a5\u7eb3\u4e86\u6b27\u9633\u4e03\u53d1\u7684\u610f\u89c1\uff0c\u4e0d\u53ef\u80fd\u5728\u8d22\u52a1\u4e0a\u7531\u4e8f\u8f6c\u76c8\uff0c\u2018\u5200\u67c4\u4f1a\uff0c\u82e5\u4e0d\u5438\u7eb3\u4e86\u767e\u888b\u4e03\u53d1\uff0c\u5c31\u4e0d\u4f1a\u9664\u4e86\u6b63\u9053\u4eba\u58eb\u4e4b\u5916\uff0c\u8fd8\u5f97\u5230\u7eff\u6797\u8c6a\u6770\u9f0e\u529b\u652f\u6301\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u2018\u5929\u6b32\u5bab\u2019\u3001\u2018\u957f\u7a7a\u5e2e\u2019\u3001\u2018\u5200\u67c4\u4f1a\u2019\u540e\u6765\u90fd\u53d8\u4e86\u8d28\u3001\u8fdd\u4e86\u521d\u8877\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u4e00\u7b11\u9053\uff1a\u201c\u4efb\u4f55\u4e8b\u7269\u82e5\u8981\u5b58\u5728\u4e0b\u53bb\uff0c\u90fd\u5f97\u53d8\uff0c\u4eba\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u662f\u8c01\u628a\u4ed6\u8bf7\u6765\u7684\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4f20\u8bf4\u90fd\u8bf4\u662f\u6e38\u516c\u5b50\u628a\u4e03\u53d1\u5927\u5e08\u8bf7\u6765\u7684\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u5176\u5b9e\u4e0d\u662f\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u4e48\u662f\u8c01\u8bf7\u8fd9\u7edd\u96be\u8bf7\u5f97\u52a8\u7684\u4eba\u6765\u6d1b\u9633\u5462\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u772f\u7740\u773c\u775b\u7b11\u9053\u3002\u201c\u8fd9\u4eba\u65b9\u5c11\u4fa0\u5e94\u8be5\u5f88\u719f\u6089\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6c60\u516c\u5b50\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u70b9\u70b9\u5934\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8fd9\u53ef\u597d\u4e86\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8fd9\u4eba\u6765\u4e86\uff0c\u6c60\u5bb6\u7684\u4eba\u4e5f\u8bb8\u5c31\u53ef\u4ee5\u5c11\u70e6\u6211\u4e00\u4e9b\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u6447\u6447\u9996\uff0c\u9053\uff1a\u201c\u6211\u770b\u5f88\u96be\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8bf7\u6559\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u6545\u4f5c\u795e\u79d8\u5730\u9053\uff1a\u201c\u56e0\u4e3a\u53c8\u6765\u4e86\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u65ad\u7709\u3002\u201d\u987e\u4f5b\u5f71\u8fd9\u6b21\u53ea\u8bf4\u4e86\u8fd9\u4e24\u4e2a\u5b57\u3002 \u3000 \u7b2c\u5341\u4e00\u7ae0\u3000\u4e09\u4e0d\u6740 \u3000 \u3000\u3000\u65b9\u90aa\u771f\u52a8\u5bb9\u9053\uff1a\u201c\u77f3\u65ad\u7709\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u6b63\u662f\u2018\u65ad\u7709\u591a\u77f3\u8001\u5e7a\u3002\u201d\u4ed6\u6c89\u7740\u5730\u9053\uff0c\u201c\u8fd1\u5e74\u6c5f\u6e56\u4e0a\u6700\u53ef\u6015\u7684\u4e00\u4e2a\u2018\u8001\u5e7a\u2019\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u6709\u5f88\u591a\u4eba\u90fd\u559c\u6b22\u5145\u5b57\u53f7\u3001\u79f0\u4e00\u54e5\uff0c\u4ec0\u4e48\u5927\u54e5\u3001\u8001\u5927\uff0c\u8fd8\u6709\u5927\u5927\u54e5\u3001\u5927\u54e5\u5927\u3001\u54e5\u5927\u5927\u3001\u5927\u54e5\u5927\u5927\uff0c\u636e\u8bf4\u77f3\u65ad\u7709\u5374\u575a\u4e0d\u5141\u4eba\u53eb\u4ed6\u4e3a\u5927\u54e5\uff0c\u4ed6\u5411\u79f0\u81ea\u5df1\u4e3a\u8001\u5e7a\uff0c\u4f46\u6c5f\u6e56\u4e0a\u65e0\u4eba\u4e0d\u77e5\u8fd9\u4f4d\u2018\u5e7a\u54e5\u2019\u624d\u662f\u5927\u54e5\u4e2d\u7684\u5927\u54e5\u3001\u8001\u5927\u91cc\u7684\u8001\u5927\u3002\u201d\u4ed6\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff0c\u201c\u4e3a\u4ec0\u4e48\u4eba\u603b\u559c\u6b22\u5f53\u8001\u5927\uff0c\u5176\u5b9e\u5f53\u8001\u5927\u6709\u4ec0\u4e48\u597d\uff1f\u770b\u8d77\u6765\u597d\u50cf\u62a2\u98ce\u5934\u3001\u6709\u7279\u6743\u3001\u547c\u98ce\u5524\u96e8\u3001\u9ad8\u4eba\u4e00\u7b49\uff0c\u53ef\u662f\u5012\u4e0b\u53bb\u6bd4\u8c01\u90fd\u5f7b\u5e95\uff0c\u98ce\u9669\u6bd4\u4ec0\u4e48\u4eba\u90fd\u5192\u5f97\u591a\uff0c\u800c\u4e14\u6b7b\u4e5f\u6bd4\u522b\u4eba\u6b7b\u5f97\u5feb\uff01\u201d \u3000\u3000\u987e\u4f5b\u5f71\u6e29\u548c\u5730\u7b11\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u5982\u6b64\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u53ea\u80fd\u53eb\u505a\u5929\u6027\uff0c\u662f\u6ca1\u6709\u529e\u6cd5\u626d\u8f6c\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u800c\u65ad\u7709\u77f3\u559c\u6b22\u6740\u4eba\uff0c\u4e5f\u662f\u5929\u6027\uff0c\u6539\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u65ad\u7709\u6740\u4eba\u7684\u624b\u6bb5\uff0c\u4e00\u5411\u5f88\u6050\u6016\uff0c\u800c\u4e14\u4ed6\u6709\u4e09\u4e0d\u6740\uff0c\u8fd9\u2018\u4e09\u4e0d\u6740\u2019\u53ef\u6bd4\u4ed6\u6740\u4eba\u8fd8\u6709\u540d\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7709\u6bdb\u4e00\u5254\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e00\u5411\u55dc\u6740\uff0c\u4e5f\u4f1a\u6709\u2018\u4e09\u4e0d\u6740\u2019\uff1f\u201d \u3000\u3000\u201c\u6709\u3002\u600e\u4e48\u6ca1\u6709\uff1f\u201d\u987e\u4f5b\u5f71\u9053\uff0c\u201c\u7b2c\u4e00\uff0c\u4e00\u4e2a\u4eba\u5728\u6b63\u5e38\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4ed6\u4e0d\u6740\u3002\u7b2c\u4e8c\uff0c\u4e00\u4e2a\u4eba\u6240\u71ac\u53d7\u7684\u75db\u82e6\u8fd8\u672a\u80fd\u4ee4\u4ed6\u6ee1\u610f\uff0c\u4ed6\u4e0d\u6740\u3002\u7b2c\u4e09\uff0c\u51e1\u7ed9\u4ed6\u5f3a\u5978\u8fc7\u7684\u5973\u5b50\uff0c\u4ed6\u4e0d\u6740\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u8bf4\u4e00\u53e5\u201c\u4e0d\u6740\u201d\uff0c\u65b9\u90aa\u771f\u7684\u8138\u8272\u5c31\u4e00\u6c89\uff0c\u6c89\u5230\u4e86\u7b2c\u4e09\u6b21\uff0c\u60dc\u60dc\u5728\u65c1\u5fcd\u4e0d\u4f4f\u5c31\u5e7d\u5e7d\u4e00\u53f9\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u65b9\u90aa\u771f\u5df2\u7ecf\u52a8\u4e86\u6012\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u7ee7\u7eed\u9053\uff1a\u201c\u4ed6\u7684\u7b2c\u4e00\u4e2a\u4e0d\u6740\uff0c\u662f\u56e0\u4e3a\u4ed6\u559c\u6b22\u6697\u7b97\u4eba\uff0c\u7b2c\u4e8c\u4e2a\u4e0d\u6740\u662f\u56e0\u4e3a\u4ed6\u559c\u6b22\u770b\u4eba\u53d7\u5c3d\u82e6\u695a\u624d\u6b7b\u53bb\uff0c\u7b2c\u4e09\u4e2a\u4e0d\u6740\u662f\u4ed6\u8981\u90a3\u4e9b\u5973\u5b50\u6d3b\u7740\u53d7\u82e6\u3001\u6068\u4ed6\u3001\u800c\u53c8\u6c42\u6b7b\u4e0d\u80fd\u3002\u201d\u4ed6\u644a\u644a\u624b\uff0c\u8868\u793a\u65e0\u5948\u5730\u9053\uff0c\u201c\u5176\u4f59\u7684\uff0c\u4e0d\u7ba1\u8001\u5f31\u5987\u5b7a\uff0c\u8d2b\u75c5\u5a74\u5b69\uff0c\u4e00\u6982\u7167\u6740\u4e0d\u8bef\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u76b1\u4e86\u76b1\u7709\uff1a\u201c\u4ed6\u662f\u54ea\u65b9\u9762\u8bf7\u6765\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u5999\u624b\u5802\u56de\u5bb6\u3002\u201d\u987e\u4f5b\u5f71\u5fae\u7b11\u9053\uff0c\u201c\u56de\u5bb6\u8fd9\u540d\u5b57\u53ef\u771f\u4e0d\u597d\u53eb\uff0c\u4eba\u4eba\u90fd\u4ee5\u4e3a\u662f\u56de\u5bb6\u7684\u56de\u5bb6\uff0c\u4e0d\u77e5\u9053\u662f\u2018\u56de\u5bb6\u2019\u7684\u56de\u5bb6\u3002\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8bb0\u8d77\u4ec0\u4e48\u624d\u8bf4\u4f3c\u7684\u9053\uff1a\u201c\u56de\u5bb6\u7684\u72ec\u5b50\uff0c\u53eb\u505a\u56de\u7edd\uff0c\u8fd9\u4f4d\u65e0\u6076\u4e0d\u4f5c\u7684\u5c0f\u5c11\u7237\uff0c\u672c\u6765\u662f\u88ab\u89c6\u4e3a\u5999\u624b\u5802\u9999\u706f\u7684\u7ee7\u627f\u4eba\uff0c\u4f46\u521a\u624d\u5df2\u6b7b\u4e8e\u4f60\u624b\u4e0b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u53cd\u6b63\uff0c\u6211\u5df2\u6253\u7b97\u79bb\u5f00\u6d1b\u9633\u57ce\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u8bb6\u9053\uff1a\u201c\u65b9\u5c11\u4fa0\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8fd8\u4e0d\u4e00\u5b9a\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u57ce\u91cc\u8fd9\u4e48\u70ed\u95f9\uff0c\u96be\u9053\u4f60\u4e0d\u60f3\u770b\u4e86\u70ed\u95f9\u624d\u8d70\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u70ed\u95f9\uff0c\u56e0\u4e3a\u5728\u70ed\u95f9\u91cc\uff0c\u603b\u6709\u9ebb\u70e6\u548c\u662f\u975e\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u53ef\u60dc\u53ea\u8981\u5728\u6709\u4eba\u7684\u4e16\u754c\u91cc\uff0c\u5c31\u4f1a\u6709\u9ebb\u70e6\u548c\u662f\u975e\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u57ce\u91cc\u7684\u9ebb\u70e6\u548c\u662f\u975e\u4f3c\u4e4e\u7279\u522b\u591a\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u8fd9\u6837\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e09\u4e2a\u4eba\u624d\u4f1a\u4e00\u8d77\u524d\u6765\u6d1b\u9633\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4e09\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u95ee\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u662f\u8c01\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u521a\u624d\u4ed6\u542c\u5230\u4e03\u53d1\u5927\u5e08\uff0c\u53ea\u89c9\u5f97\u8b66\u60d5\uff0c\u542c\u5230\u65ad\u7709\u4e5f\u6765\u4e86\uff0c\u7b97\u662f\u52a8\u5bb9\uff0c\u4f46\u4ece\u6ca1\u6709\u5403\u60ca\u8fc7\u3002 \u3000\u3000\u76f4\u81f3\u4ed6\u542c\u5230\u987e\u4f5b\u5f71\u8bf4\u201c\u4e0d\u77e5\u9053\u201d\u4e09\u4e2a\u5b57\uff0c\u4ed6\u624d\u6709\u70b9\u5403\u60ca\u3002 \u3000\u3000\u2014\u2014\u8fde\u201c\u987e\u76fc\u795e\u98ce\u201d\u987e\u4f5b\u5f71\u4e5f\u4e0d\u77e5\u9053\u800c\u53c8\u8981\u7279\u522b\u63d0\u8d77\u7684\u4eba\uff0c\u7a76\u7adf\u662f\u4f55\u65b9\u795e\u5723\uff1f \u3000\u3000\u65b9\u90aa\u771f\u76b1\u7709\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u540d\u5b57\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u7acb\u5373\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u4e0b\u53bb\uff1a\u201c\u4ed6\u73b0\u5df2\u4f4f\u5728\u548c\u80dc\u5ba2\u6808\u4e8c\u697c\u5bc5\u5b57\u623f\uff0c\u5728\u5ba2\u7c3f\u4e0a\u7b7e\u4e86\u2018\u8521\u65cb\u949f\u2019\u8fd9\u540d\u5b57\u3002\u201d \u3000\u3000\u2014\u2014\u987e\u4f5b\u5f71\u6253\u542c\u8fd9\u4eba\u7684\u540d\u5b57\uff0c\u5c45\u7136\u53ea\u80fd\u4ece\u4ed6\u81ea\u5df1\u7b7e\u586b\u7684\u5ba2\u4eba\u540d\u7c3f\u4e0a\u77e5\u9053\u7684\uff0c\u53ef\u89c1\u8fd9\u4eba\u7684\u771f\u5b9e\u59d3\u540d\uff0c\u60f3\u5fc5\u662f\u65e0\u4ece\u6253\u63a2\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53cc\u7709\u4e00\u8e59\uff1a\u201c\u8521\u65cb\u949f\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u76ee\u5149\u4e00\u957f\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u6211\u4e5f\u662f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4ed6\u53ef\u6709\u751a\u4e48\u7279\u5f81\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\uff0c\u989d\u4e0a\u4e00\u9897\u7070\u75e3\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u82e6\u7b11\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e16\u4e0a\u5e74\u8f7b\u4eba\u53ef\u771f\u4e0d\u5c11\uff0c\u51e0\u4e4e\u6bcf\u4e09\u4eba\u5c31\u6709\u4e00\u4e2a\u662f\u5341\u5206\u5e74\u8f7b\u7684\uff0c\u81f3\u4e8e\u989d\u4e0a\u6709\u75e3\uff0c\u4e5f\u4e0d\u662f\u751a\u4e48\u5947\u4e8b\uff0c\u5927\u591a\u6570\u4eba\u7684\u8138\u4e0a\uff0c\u603b\u4f1a\u6709\u4e00\u4e24\u9897\u75e3\uff0c\u4e0d\u7136\uff0c\u4e5f\u4f1a\u6709\u75a4\u6591\u6216\u9ed1\u5b50\uff0c\u8fd9\u4e5f\u5e76\u4e0d\u51fa\u5947\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u8fde\u8fd9\u70b9\u90fd\u5217\u4e3a\u201c\u7279\u5f81\u201d\uff0c\u663e\u7136\u662f\u56e0\u4e3a\u627e\u4e0d\u5230\u90a3\u4eba\u7684\u771f\u6b63\u201c\u7279\u5f81\u201d\u3002 \u3000\u3000\u4e00\u4e2a\u6ca1\u6709\u7279\u5f81\u7684\u4eba\uff0c\u4e5f\u4e0d\u5bb9\u6613\u627e\u5230\u4ed6\u7684\u7f3a\u70b9\uff1b\u540c\u7406\uff0c\u6240\u4ee5\u4e00\u4e2a\u5df2\u7ecf\u6210\u540d\u7684\u4eba\uff0c\u6bd4\u8f83\u597d\u5bf9\u4ed8\uff0c\u56e0\u4e3a\u4ed6\u7684\u6027\u547d\u6bd4\u8c01\u90fd\u5b9d\u8d35\uff0c\u5c31\u7b97\u4ed6\u53ef\u4ee5\u4e0d\u8981\u547d\uff0c\u4e5f\u5f88\u5c11\u4eba\u53ef\u4ee5\u4e0d\u8981\u9762\u5b50\u3002 \u3000\u3000\u672a\u6210\u540d\u7684\u4eba\u5374\u4e0d\u7136\u3002 \u3000\u3000\u4ed6\u4eec\u53ef\u4ee5\u540c\u65f6\u4e0d\u8981\u547d\uff0c\u4e5f\u4e0d\u8981\u9762\u5b50\u3002 \u3000\u3000\u6545\u6b64\uff0c\u5df2\u6210\u540d\u7684\u5251\u5ba2\u6700\u6015\u4e0e\u65e0\u540d\u7684\u523a\u5ba2\u4ea4\u624b\uff0c\u56e0\u4e3a\u6210\u540d\u7684\u5251\u5ba2\u5df2\u4e0d\u80fd\u8d25\uff0c\u65e0\u540d\u7684\u523a\u5ba2\u5374\u662f\u53ea\u6c42\u5f97\u624b \u3000\u3000\u65b9\u90aa\u771f\u5df2\u611f\u89c9\u5230\u201c\u8521\u65cb\u949f\u201d\u7684\u4fb5\u7565\uff0c\u751a\u81f3\u89c9\u5f97\uff0c\u201c\u8fd9\u4e2a\u4eba\u201d\u8ddf\u81ea\u5df1\u8d8a\u6765\u8d8a\u76f8\u8fd1\uff0c\u4f46\u4e5f\u6108\u6765\u6108\u903c\u8fd1\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u95ee\uff1a\u201c\u4ed6\u4f7f\u751a\u4e48\u5175\u5668\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u5251\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u95ee\uff1a\u201c\u751a\u4e48\u5251\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e5d\u5c3a\u4e03\u5bf8\u957f\u7684\u5251\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u4e48\u957f\u7684\u5251\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u6240\u4ee5\u52a8\u8d77\u5251\u6765\uff0c\u5341\u5206\u7684\u4e0d\u65b9\u4fbf\uff0c\u4ed6\u8981\u6740\u7684\u4eba\uff0c\u5fc5\u987b\u5728\u5341\u5c3a\u4ee5\u5916\uff0c\u5426\u5219\uff0c\u4e00\u65e6\u8ba9\u5bf9\u65b9\u51b2\u8fd1\u8eab\u524d\uff0c\u5c31\u4e0d\u5bb9\u6613\u56de\u5251\u81ea\u5b88\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u54fa\u54fa\u5730\u9053\uff1a\u201c\u901a\u5e38\u8fd9\u4e48\u957f\u7684\u5251\uff0c\u5df2\u7ecf\u4e0d\u662f\u5251\uff0c\u800c\u662f\u67aa\u3001\u77db\u6216\u621f\u2026\u2026\u9664\u975e\u2026\u2026\u83ab\u975e\u2026\u2026\u201d \u3000\u3000\u987e\u4f5b\u5f71\u51e0\u4e4e\u7ad6\u8d77\u4e86\u8033\u6735\uff1a\u201c\u9664\u975e\u751a\u4e48\uff1f\u83ab\u975e\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u79e6\u59cb\u7687\u5728\u4f4d\u4e09\u5341\u516d\u5e74\u65f6\uff0c\u4ee5\u4e09\u5e74\u5c81\u6b21\u4e01\u5df2\uff0c\u547d\u674e\u65af\u805a\u5f53\u4e16\u4e94\u5927\u94f8\u5251\u5e08\u5f80\u5317\u7957\u91c7\u94dc\uff0c\u954c\u5f97\u4e8c\u5251\uff0c\u540d\u4e3a\u201c\u5b9a\u79e6\u201d\uff0c\u7531\u4e1e\u76f8\u674e\u65af\u4eb2\u523b\u5c0f\u7bc6\u4e3a\u5fd7\uff0c\u4ee5\u8868\u79e6\u4e4b\u5929\u4e0b\u6c38\u5b9a\u4e4b\u610f\u7684\u4e8b\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u8138\u4e0a\u5df2\u6709\u4e86\u5d07\u656c\u4e4b\u8272\uff1a\u201c\u65b9\u5c11\u4fa0\u679c\u771f\u535a\u5b66\u5e7f\u8bc6\u3002\u5f53\u5176\u65f6\u4e94\u5927\u94f8\u5251\u5e08\u53ea\u91c7\u5f97\u8fd9\u5757\u94dc\u7cbe\uff0c\u5374\u65e0\u6cd5\u628a\u5b83\u94f8\u6210\u5b9d\u5251\uff1b\u53ea\u6709\u82e6\u6c42\u5317\u9099\u5c71\u7684\u5954\u9e7f\u5927\u5e08\u51fa\u624b\u954c\u51b6\uff0c\u5954\u9e7f\u5927\u5e08\u56e0\u987e\u5ff5\u8fd9\u4e94\u540d\u5251\u5e08\u7684\u65cf\u4eb2\u6027\u547d\uff0c\u4fbf\u7834\u4f8b\u5f00\u7089\u51b6\u5251\uff0c\u4f46\u5f97\u6b64\u4e8c\u5251\uff0c\u5404\u957f\u4e09\u5c3a\u516d\u5bf8\uff0c\u5954\u9e7f\u5927\u5e08\u4e00\u7b97\u6c14\u6570\uff0c\u5fc5\u987b\u8981\u91c7\u7cbe\u94dc\u954c\u51b6\u7b2c\u4e09\u628a\u5251\uff0c\u5251\u957f\u4e8c\u5c3a\u4e94\u5bf8\uff0c\u4e09\u5251\u5408\u4e00\uff0c\u5929\u4e0b\u59cb\u80fd\u5b9a\uff0c\u5e76\u7559\u4e0b\u201c\u5927\u9650\u5251\u201d\u5251\u8c31\uff0c\u5e0c\u671b\u79e6\u4e16\u5b50\u80fd\u591a\u7ec3\u5251\uff0c\u5c11\u80e1\u620f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u70b9\u5934\u9053\uff1a\u2018\u5927\u9650\u5251\u2019\u957f\u4e5d\u5c3a\u4e03\uff0c\u6b63\u662f\u4e09\u5251\u5408\u4e00\u7684\u957f\u5ea6\uff0c\u53ef\u60dc\u79e6\u4e8c\u4e16\u7167\u6837\u4f11\u620f\uff0c\u800c\u674e\u65af\u4e00\u542c\u2018\u5927\u9650\u2019\u4e8c\u5b57\uff0c\u6050\u89e6\u6012\u79e6\u738b\uff0c\u5fd9\u628a\u5954\u9e7f\u5927\u5e08\u8bf1\u9a97\u6bd2\u6740\uff0c\u6240\u4ee5\u4e16\u95f4\u3000\u53ea\u6709\u201c\u5b9a\u79e6\u5251\u201d\uff0c\u800c\u6ca1\u6709\u7b2c\u4e09\u67c4\u2018\u5927\u9650\u5251\u2019\u4e86\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u79e6\u4e8c\u4e16\u7684\u5927\u9650\u4e5f\u771f\u7684\u6765\u4e86\uff0c\u4e00\u70b9\u4e5f\u4e0d\u542b\u7cca\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f46\u662f\uff0c\u8fd9\u79cd\u5251\u6cd5\u5374\u4f20\u4e86\u4e0b\u6765\uff0c\u800c\u4e14\uff0c\u5728\u8d8a\u738b\u4ee5\u767d\u9a6c\u767d\u725b\u7940\u6606\u543e\u4e4b\u795e\uff0c\u91c7\u7cbe\u91d1\u94f8\u51b6\u516b\u5251\uff0c\u5176\u4e2d\u4e00\u5251\uff0c\u5373\u957f\u4e5d\u5c3a\u4e03\u5bf8\uff0c\u6b63\u597d\u53ef\u4f7f\u8fd9\u4e00\u5957\u5251\u6cd5\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u8d8a\u738b\u516b\u5251\uff1f\u4f60\u6307\u7684\u662f\uff1a\u63a9\u65e5\u3001\u65ad\u6c34\u3001\u8f6c\u9b44\u3001\u60ac\u7fe6\u3001\u60ca\u9cb5\u3001\u706d\u9b42\u3001\u5374\u90aa\u3001\u771f\u521a\u516b\u5927\u540d\u5251\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u542b\u7b11\u9053\uff1a\u201c\u662f\u3002\u53e4\u53f2\u8bb0\u8f7d\uff0c\u2018\u63a9\u65e5\u2019\u4e00\u51fa\uff0c\u6307\u65e5\u5219\u5149\u5c3d\u6697\u3002\u56e0\u91d1\u5c5e\u9634\uff0c\u9634\u76db\u6545\u9633\u706d\u3002\u2018\u65ad\u6c34\u2019\u4e00\u51fa\uff0c\u4ee5\u4e4b\u5212\u6c34\uff0c\u6c34\u5206\u800c\u4e0d\u5408\u3002\u2018\u8f6c\u9b44\uff0c\u4e00\u51fa\uff0c\u4ee5\u4e4b\u6307\u6708\uff0c\u8d61\u5154\u4e3a\u4e4b\u5012\u8f6c\u3002 \u3000\u3000\u2018\u60ac\u526a\u2019\u4e00\u51fa\uff0c\u98de\u9e1f\u6e38\u866b\uff0c\u81ea\u89e6\u5176\u5203\uff0c\u5982\u65a9\u8f7d\u4e5f\uff01\u201d\u4ed6\u5982\u6570\u5bb6\u73cd\u5730\u9053\uff0c\u201c\u81f3\u4e8e\u2018\u60ca\u9cb5\u2019\u795e\u5251\uff0c\u4ee5\u4e4b\u6cdb\u6d77\uff0c\u636e\u8bf4\u9cb8\u9cb5\u4e3a\u4e4b\u6df1\u5165\u3002\u201c\u706d\u9b42\u201d\u5219\u4e3a\u795e\u5175\uff0c\u631f\u4e4b\u591c\u884c\uff0c\u4e0d\u9022\u9b51\u9b45\u3002\u201c\u5374\u90aa\u201d\u66f4\u6709\u8f9f\u715e\u529f\u6548\uff0c\u5996\u9b45\u89c1\u4e4b\u5219\u4f0f\u3002\u8fd8\u6709\u4e00\u67c4\u201c\u771f\u521a\u5251\u201d\uff0c\u5207\u7389\u65ad\u91d1\uff0c\u5982\u524a\u571f\u6728\uff0c\u5439\u6bdb\u65ad\u53d1\uff0c\u6d88\u94c1\u5982\u6ce5\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u5782\u624b\u606d\u542c\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e00\u7b11\u9053\uff0c\u201c\u4e0d\u8fc7\u53e4\u4eba\u954c\u51b6\u540d\u5251\u548c\u521b\u7814\u5251\u8c31\u4e4b\u8bf4\uff0c\u5f80\u5f80\u4ee5\u8bb9\u4f20\u8bb9\uff0c\u8fc7\u4e8e\u795e\u5316\u3002\u82e5\u8bf4\u2018\u63a9\u65e5\u2019\u795e\u5251\u5251\u51fa\u800c\u9633\u5149\u5c3d\u6697\uff0c\u53ef\u80fd\u56e0\u5251\u5149\u5927\u76db\u800c\u593a\u76ee\u4e4b\u6545\uff0c\u8fd8\u7b97\u4eba\u60c5\u5408\u7406\uff0c\u4f46\u2018\u65ad\u6c34\u2019\u80fd\u5212\u6c34\u4e0d\u6d41\uff0c\u672a\u514d\u8fc7\u4e8e\u532a\u5937\u6240\u601d\u4e86\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u65b9\u5c11\u4fa0\u4ee5\u4e3a\uff0c\u80fd\u4f7f\u2018\u5927\u9650\u5251\u6cd5\u2019\u7684\uff0c\u662f\u54ea\u4e00\u628a\u540d\u5251\u5462\uff1f\u201d \u3000\u3000\u201c\u4e5d\u5c3a\u4e03\u5bf8\uff0c\u9664\u2018\u8f6c\u9b44\u2019\u5916\uff0c\u8fd8\u6709\u54ea\u4e00\u67c4\u5251\u662f\u8fd9\u4e2a\u957f\u5ea6\u5462\uff1f\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u2018\u8f6c\u9b44\u795e\u5251\u2019\uff0c\u76f8\u4f20\u4ee5\u5251\u6307\u6708\uff0c\u8d61\u5154\u5012\u8f6c\uff0c\u4f46\u8d61\u5154\u4e43\u6307\u6708\u4eae\u7684\u6697\u5757\uff0c\u5982\u4f55\u5012\u8f6c\uff1f\u6b64\u8bf4\u4e5f\u672a\u514d\u5938\u5f20\u3002\u8bb8\u662f\u56e0\u6b64\u5251\u592a\u957f\uff0c\u6545\u4ee5\u6b64\u4f5c\u4e3a\u5f62\u5bb9\uff0c\u6545\u6709\u6b64\u8bf4\uff0c\u4ea6\u6216\u672a\u5b9a\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9677\u5165\u6df1\u601d\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u5927\u9650\u5251\u8c31\uff1f\u8f6c\u9b44\u795e\u5251\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\uff0c\u7528\u8fd9\u4e48\u4e00\u628a\u5251\u5728\u6c5f\u6e56\u4e0a\u95ef\uff0c\u4e0d\u53ef\u80fd\u6ca1\u6709\u4e8b\u8ff9\u53ef\u67e5\u7684\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u3002\u2018\u6709\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e09\u5e74\u524d\uff0c\u2018\u5200\u67c4\u4f1a\u2019\u7684\u5916\u4e09\u5802\u4e3b\u2018\u4e0d\u6b7b\u94dc\u4eba\u2019\u5315\u91d1\u725b\u5315\u8001\u592a\u7237\uff0c\u4fbf\u662f\u6b7b\u5728\u8fd9\u4e00\u67c4\u5947\u5251\u4e0b\uff0c\u5f53\u65f6\u90a3\u4eba\u7559\u4e0b\u59d3\u540d\uff0c\u53ea\u8bf4\u662f\u53eb\u505a\u2018\u8521\u949f\uff0c\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8521\u949f\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u53c8\u9053\uff1a\u201c\u4e24\u5e74\u524d\uff0c\u2018\u5bcc\u8d35\u4e4b\u5bb6\u2019\u7684\u5927\u5f53\u5bb6\u3002\u98de\u9524\u91d1\u94b5\u2019\u5e2d\u79cb\u91ce\uff0c\u6446\u4e0b\u64c2\u53f0\uff0c\u5927\u4f1a\u7fa4\u82f1\uff0c\u8fde\u80dc\u4e8c\u5341\u4e03\u573a\uff0c\u6b63\u662f\u610f\u5174\u98ce\u53d1\u4e4b\u4f59\uff0c\u5374\u53eb\u4e00\u4e2a\u5c11\u5e74\u4eba\u7528\u4e00\u67c4\u957f\u5251\u8f7b\u6613\u51fb\u8d25\uff0c\u5e2d\u5bb6\u7684\u4eba\u591a\u65b9\u6253\u63a2\u4e4b\u4e0b\uff0c\u624d\u77e5\u9053\u90a3\u4eba\u53eb\u505a\u2018\u949f\u65cb\u8521\uff0c\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u76b1\u4e86\u76b1\u7709\uff1a\u201c\u949f\u65cb\u8521\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\uff0c\u201d\u987e\u4f5b\u5f71\u9053\uff0c\u201c\u4e00\u5e74\u524d\u8352\u5c71\u9053\u4eba\u6b7b\u4e8e\u9655\u897f\u9053\u4e0a\uff0c\u4ed6\u7684\u95e8\u5f92\u53d1\u73b0\u4ed6\u65f6\uff0c\u4ed6\u5df2\u5944\u5944\u4e00\u606f\uff0c\u95e8\u5f92\u53ea\u542c\u4ed6\u8bf4\u2018\u957f\u5251\uff01\u4e5d\u5c3a\u4f59\u7684\u957f\u5251\u2019\u4fbf\u6e98\u7136\u901d\u53bb\uff0c\u770b\u6765\u4e5f\u662f\u8fd9\u5e74\u8f7b\u5251\u624b\u6240\u4e3a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u70b9\u5934\u9053\uff1a\u201c\u4efb\u8c01\u60f3\u8981\u6740\u6b7b\u2018\u516d\u5408\u9752\u9f99\u3001\u4e00\u5251\u64ce\u5929\u2019\u7684\u8352\u5c71\u9053\u4eba\uff0c\u90fd\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u66f4\u4f55\u51b5\u662f\u8981\u8352\u5c71\u9053\u4eba\u6b7b\u4e8e\u4ed6\u81ea\u5df1\u6240\u6700\u7cbe\u957f\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u518d\u9053\uff1a\u201c\u53e6\u5916\uff0c\u524d\u4ee3\u5927\u4fa0\u2018\u5927\u68a6\u795e\u5251\u2019\u987e\u5915\u671d\u66fe\u88ab\u4e00\u5c11\u5e74\u5251\u5ba2\u6311\u6218\uff0c\u529b\u6597\u4e09\u767e\u62db\uff0c\u4e0d\u5206\u80dc\u8d1f\uff0c\u540e\u6765\u53ea\u77e5\u8fd9\u5c11\u5e74\u5251\u5ba2\u7559\u540d\u4e3a\uff1a\u2018\u949f\u8521\u2019\u4e8c\u5b57\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53c8\u76b1\u7709\u4e86\uff1a\u201c\u949f\u8521\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u6211\u5bf9\u6b64\u4eba\u6240\u77e5\uff0c\u5c31\u8fd9\u4e48\u591a\u4e86\u3002\u6bcf\u5e74\u6bcf\u6708\uff0c\u6c5f\u6e56\u4e0a\u90fd\u4f1a\u6709\u4e9b\u6b66\u6797\u4eba\u7269\u795e\u79d8\u4ea1\u6545\uff0c\u6216\u5403\u4e86\u8d25\u4ed7\uff0c\u4f46\u4e0d\u6562\u5f20\u626c\uff0c\u8fd9\u4e9b\u4e8b\u90fd\u65e0\u53ef\u7a3d\u8003\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6709\u8fd9\u4e9b\u8d44\u6599\uff0c\u4e5f\u5c31\u4e0d\u9519\u4e86\uff0c\u81f3\u5c11\u6211\u4eec\u5df2\u7ecf\u53ef\u4ee5\u4f5c\u51fa\u4e09\u4e2a\u63a8\u8bba\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u613f\u8046\u5176\u8be6\u3002\u201d \u3000\u3000\u201c\u4e5f\u6ca1\u751a\u4e48\uff0c\u201d\u65b9\u90aa\u771f\u4e00\u7b11\u9053\uff0c\u201c\u7b2c\u4e00\uff0c\u8fd9\u4eba\u5f88\u53ef\u80fd\u4f1a\u4f7f\u2018\u5927\u9650\u5251\u6cd5\u2019\uff1b\u7b2c\u4e8c\uff0c\u4ed6\u624b\u4e0a\u62ff\u7684\u53ef\u80fd\u4fbf\u662f\u2018\u8f6c\u9b44\u795e\u5251\u2019\uff1b\u7b2c\u4e09\uff0c\u8fd9\u662f\u4e00\u4f4d\u65e0\u540d\u7684\u5251\u5ba2\uff0c\u800c\u4e14\u662f\uff0c\u4e00\u6d41\u9ad8\u624b\uff0c\u4e00\u4f4d\u6b66\u4fa0\u575b\u4e0a\u7684\u524d\u8f88\u66fe\u7ecf\u8bf4\u8fc7\uff1a\u65e0\u540d\u7684\u9ad8\u624b\u6bd4\u6709\u540d\u7684\u9ad8\u624b\u66f4\u5371\u9669\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u597d\u50cf\u8fd8\u6709\u4e00\u4e2a\u63a8\u8bba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8bf7\u6559\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e24\u8005\u4e4b\u95f4\uff0c\u4e00\u5e2d\u8c08\u540e\uff0c\u663e\u5f97\u66f4\u4e3a\u5c0a\u656c\u3002\u4eba\u751f\u91cc\uff0c\u624d\u4eba\u4e0d\u4e00\u5b9a\u8981\u76f8\u8f7b\uff0c\u53cd\u800c\u5e94\u8be5\u60fa\u60fa\u60dc\u60fa\u60fa\u3002\u5982\u679c\u4eba\u624d\u90fd\u4e0d\u656c\u91cd\u4eba\u624d\uff0c\u4f60\u53eb\u4eba\u600e\u4e48\u80fd\u656c\u91cd\u4f60\u7684\u4eba\u548c\u624d\uff1f \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d\u4ed6\u5f90\u5f90\u5730\u9053\uff0c\u201c\u6211\u770b\uff0c\u8fd9\u79cd\u4eba\u6765\u6d1b\u9633\uff0c\u6562\u60c5\u662f\u6709\u4eba\u96c7\u7528\u7684\uff0c\u81f3\u4e8e\u662f\u8c01\uff0c\u5374\u8fd8\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u70b9\u9996\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u624d\uff0c\u82e5\u9002\u9022\u5176\u4f1a\uff0c\u8c01\u90fd\u5e94\u8be5\u4e89\u8058\u4ed6\uff0c\u8ba9\u4e00\u4e2a\u4eba\u6750\u57cb\u6ca1\u4e86\u8fd9\u4e48\u4e45\uff0c\u662f\u4ef6\u60b2\u54c0\u7684\u4e8b\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u7b11\u9053\uff1a\u201c\u5c31\u50cf\u9601\u4e0b\u4e00\u6837\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5374\u4e0d\u63a5\u4ed6\u8fd9\u4e2a\u8bdd\u9898\uff0c\u53ea\u8bf4\uff1a\u201c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u70b9\u53ef\u4ee5\u63a8\u8bba\u7684\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u76ee\u5149\u95ea\u52a8\uff0c\u95ee\u3000\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e3a\u751a\u4e48\u8fd9\u4e48\u559c\u6b22\u7528\u8fd9\u4e09\u4e2a\u5b57\u4f5c\u81ea\u79f0\uff1a\u8521\uff1f\u65cb\u3001\u949f\uff1f\u65e2\u8981\u9690\u7792\u8eab\u4efd\uff0c\u4e3a\u751a\u4e48\u4ed6\u4e0d\u968f\u4fbf\u634f\u9020\u4e2a\u540d\u5b57\uff1f\u963f\u732b\u963f\u72d7\uff1f\u5c0f\u866b\u5c0f\u725b\uff1f\u751a\u81f3\u53ef\u4ee5\u53eb\u2018\u65cb\u98ce\u2019\u3001\u2018\u79cd\u83dc\u2019\uff0c\u4e3a\u751a\u4e48\u504f\u8981\u53eb\u8fd9\u4e09\u4e2a\u5b57\u5462\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u5bf9\uff0c\u4e00\u5b9a\u6709\u539f\u6545\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u9053\uff1a\u201c\u53ef\u662f\u4e0d\u7ba1\u8fd9\u539f\u6545\u662f\u751a\u4e48\uff0c\u6211\u90fd\u4e0d\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u8bb6\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u8981\u8d70\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u8981\u8d70\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u8be7\u9053\uff1a\u201c\u4f60\u4e0d\u5173\u5fc3\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6211\u4e3a\u751a\u4e48\u8981\u5173\u5fc3\u8fd9\u4ef6\u4e8b\u513f\uff1f\u201d\u7136\u540e\u4ed6\u671b\u5b9a\u987e\u4f5b\u5f71\uff0c\u51b7\u51b7\u5730\u9053\uff0c\u201c\u4f60\u4e3a\u751a\u4e48\u8981\u544a\u8bc9\u6211\u8fd9\u4e9b\u4e8b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u516c\u5b50\u89c9\u5f97\u4ed6\u4eec\u53ef\u80fd\u8ddf\u4f60\u6709\u5173\uff0c\u201d\u987e\u4f5b\u5f71\u4e0d\u614c\u4e0d\u5fd9\u5730\u9053\uff0c\u201c\u6240\u4ee5\u6709\u5fc5\u8981\u901a\u77e5\u4f60\u4e00\u58f0\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5168\u65e0\u8c22\u610f\u5730\u9053\uff1a\u201c\u8c22\u8c22\u3002\u201d \u3000\u3000\u6709\u65f6\u5019\uff0c\u201c\u8c22\u8c22\u201d\u5e38\u4e0e\u201c\u518d\u89c1\u201d\u540c\u4e49\uff0c\u901a\u5e38\uff0c\u4e5f\u53ef\u80fd\u662f\u4e00\u756a\u8c08\u8bdd\u7684\u7ed3\u5c40\u8bed\u53e5\u3002 \u3000\u3000\u201c\u53e6\u5916\uff0c\u201d\u987e\u4f5b\u5f71\u8138\u4e0a\u4ecd\u662f\u4eb2\u5207\u800c\u8bda\u6073\u7684\uff0c\u8868\u60c5\u4e5f\u662f\u4eb2\u5207\u800c\u8bda\u6073\u7684\uff0c\u4f46\u773c\u91cc\u5374\u95ea\u8fc7\u4e00\u4e1d\u72e1\u70e9\u4e4b\u8272\uff0c\u201c\u6211\u4ee5\u4e3a\uff0c\u5c31\u7b97\u4f60\u4e0d\u5173\u5fc3\u4f60\u81ea\u5df1\uff0c\u4e5f\u603b\u4f1a\u5173\u5fc3\u4e00\u4e0b\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5c31\u62f1\u624b\u544a\u8f9e\uff0c\u8868\u793a\u8981\u8d70\uff0c\u4e00\u9762\u8d54\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u6211\u5f04\u9519\u4e86\uff0c\u53e8\u6270\u4e86\uff0c\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u76ee\u9001\u4ed6\u51fa\u95e8\u53e3\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u8fd8\u662f\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u8bf4\u6211\u7684\u670b\u53cb\u2026\u2026\u662f\u8c01\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u4e00\u9762\u8d70\u4e00\u9762\u629b\u4e0b\u4e86\u4e00\u53e5\u8bdd\uff1a \u3000\u3000\u201c\u8ffd\u547d\u3002\u201d \u3000 \u7b2c\u5341\u4e8c\u7ae0\u3000\u7f8e\u4e3d\u7684\u82b1 \u3000 \u3000\u3000\u65b9\u90aa\u771f\u4e00\u542c\uff0c\u626c\u58f0\u95ee\u3000\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u8ff3\u81ea\u5f80\u524d\u8d70\u53bb\uff1a\u201c\u4ed6\u8fd8\u672a\u5230\uff0c\u4e0d\u8fc7\u4ed6\u5728\u4e09\u4e2a\u6708\u524d\u5c31\u4e00\u76f4\u8ffd\u8e2a\u8fd9\u4e09\u4e2a\u4eba\u3002\u201d\u4ed6\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u201c\u6211\u4eec\u516c\u5b50\u4e0b\u4e86\u9053\u5e16\u5b50\uff0c\u7ea6\u4ed6\u4eec\u4e09\u4e2a\u4eba\u660e\u5929\u62c2\u6653\u5728\u2018\u5c0f\u78a7\u6e56\u2019\u7684\u2018\u76f8\u601d\u4ead\u2019\u4e00\u53d9\uff0c\u8ffd\u547d\u60f3\u5fc5\u4e5f\u4f1a\u672a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53ea\u597d\u95ee\uff1a\u201c\u4ed6\u4e3a\u751a\u4e48\u8981\u8ffd\u8e2a\u8fd9\u4e09\u4e2a\u4eba\uff1f\u201d\u987e\u4f5b\u5f71\u5df2\u8d70\u5230\u697c\u68af\u53e3\uff0c\u500f\u7136\u7ad9\u5b9a\uff0c\u56de\u9996\u7b54\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u60f3\u7834\u5b5f\u968f\u56ed\u5168\u5bb6\u88ab\u5c60\u6740\u4e00\u6848\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53c8\u95ee\u3000\uff1a\u201c\u8fd9\u4e09\u4e2a\u4eba\u662f\u51f6\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\uff0c\u4e5f\u8bb8\u4e09\u4e2a\u90fd\u662f\uff0c\u4e5f\u8bb8\u4e09\u4e2a\u90fd\u4e0d\u662f\u3002\u201d\u987e\u4f5b\u5f71\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u53ea\u8981\u8ffd\u547d\u4e00\u51fa\u73b0\uff0c\u8fd9\u4e09\u4e2a\u4eba\u5f88\u53ef\u80fd\u5c31\u4f1a\u540c\u65f6\u5411\u4ed6\u51fa\u624b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u518d\u95ee\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4e0d\u7ba1\u662f\u4e0d\u662f\u51f6\u624b\uff0c\u88ab\u6000\u7591\u548c\u88ab\u8ffd\u8e2a\u90fd\u662f\u4ef6\u5f88\u8ba8\u538c\u7684\u4e8b\u60c5\uff0c\u800c\u5bf9\u4ed8\u5b98\u5dee\uff0c\u4e00\u65e6\u51fa\u624b\uff0c\u5c31\u4e0d\u80fd\u7559\u4e0b\u6d3b\u53e3\uff0c\u201d\u987e\u4f5b\u5f71\u4f3c\u5fae\u5e26\u60cb\u60dc\u4e4b\u610f\uff0c\u201c\u5c24\u5176\u662f\u5bf9\u8ffd\u547d\u8fd9\u6837\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u4ed6\u7b11\u7b11\u53c8\u9053\uff1a\u201c\u6c5f\u6e56\u4eba\u79f0\uff1a\u2018\u94c1\u624b\u7684\u624b\uff0c\u8ffd\u547d\u7684\u817f\uff0c\u51b7\u8840\u7684\u5251\uff0c\u65e0\u60c5\u7684\u6697\u5668\u2019\u4e43\u4e0e\u6b66\u6797\u4e2d\uff1a\u2018\u5510\u4ec7\u7684\u6bd2\uff0c\u5c60\u665a\u7684\u9525\uff0c\u8d75\u597d\u7684\u5fc3\uff0c\u71d5\u8d75\u7684\u6b4c\u821e\u2019\u5e76\u79f0\u4e8e\u5929\u4e0b\uff0c\u6b64\u6240\u8c13\u5929\u4e0b\u2018\u56db\u5927\u540d\u6355\u2019\u4e0e\u2018\u56db\u5927\u51f6\u5f92\u2019\uff0c\u4e0d\u8fc7\uff0c\u65ad\u7709\u8001\u4e48\u7684\u94a2\u53c9\u3001\u65e0\u540d\u5c0f\u5b50\u7684\u53e4\u5251\uff0c\u4e03\u53d1\u7985\u5e08\u7684\u888b\u5b50\uff0c\u8fd8\u6709\u4f60\u90a3\u67c4\u6df1\u78a7\u7684\u5251\uff0c\u90fd\u53ef\u4ee5\u7b97\u662f\u6c5f\u6e56\u4e00\u7edd\uff0c\u7406\u5e94\u4e5f\u7b97\u8fdb\u53bb\u624d\u662f\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u772f\u7740\u773c\u775b\u7b11\u9053\uff1a\u201c\u5982\u679c\u6211\u8fd8\u6ca1\u6709\u8001\u773c\u660f\u82b1\uff0c\u9601\u4e0b\u8170\u95f4\u7684\u5251\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u516b\u5927\u540d\u5251\u4e2d\u7684\u2018\u706d\u9b42\u5251\u2019\u3002\u201d \u3000\u3000\u4ed6\u7b11\u7740\u4e00\u62f1\u624b\uff0c\u4fbf\u4e0b\u4e86\u697c\uff0c\u8fd8\u629b\u4e0b\u4e86\u4e00\u53e5\u8bdd\uff1a \u3000\u3000\u201c\u50cf\u660e\u5929\u2018\u76f8\u601d\u6797\u2019\u91cc\u2018\u76f8\u601d\u4ead\u2019\u8fd9\u6837\u7684\u76db\u4e8b\uff0c\u6e38\u516c\u5b50\u8bf4\uff0c\u4ed6\u60f3\u5728\u4e0b\u5fc5\u80fd\u5728\u90a3\u513f\u606d\u5019\u4f60\u7684\u5927\u9a7e\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5c31\u5df2\u7ecf\u8d70\u5230\u95e8\u5916\u3002 \u3000\u3000\u53ef\u662f\u58f0\u97f3\u72b9\u5728\u65b9\u90aa\u771f\u7684\u8033\u8fb9\u4f20\u6765\u3002 \u3000\u3000\u4e0d\u5f90\u4e0d\u75be\u3002 \u3000\u3000\u4e0d\u5f3a\u4e0d\u5f31\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6697\u5fd6\uff1a\u5355\u6b62\u987e\u4f5b\u5f71\u663e\u9732\u8fd9\u4e00\u624b\u60a0\u6e38\u7ef5\u957f\u5145\u6c9b\u6d51\u5b8f\u7684\u5185\u529f\uff0c\u5728\u6b66\u6797\u4e2d\u5185\u529f\u9ad8\u624b\u91cc\uff0c\u7edd\u5bf9\u53ef\u4ee5\u8110\u8eab\u4e8e\u5341\u540d\u4e4b\u5185\u3002 \u3000\u3000\u8fd9\u4e00\u79cd\u6c14\u529f\uff0c\u5c31\u53eb\u505a\u201c\u5927\u6c5f\u5357\u5317\u201d\u3002 \u3000\u3000\u8fd9\u79cd\u5185\u529b\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\uff0c\u53ea\u4e0d\u8fc7\u662f\u7cbe\u9009\u4e86\u5927\u6c5f\u5357\u5317\u4e8c\u5341\u4e03\u5bb6\u91cd\u8981\u7684\u5185\u529f\uff0c\u82e6\u7ec3\u6210\u5357\u5317\u4e8c\u7cfb\uff0c\u6210\u4e3a\u5185\u529b\u4e2d\u7684\u5185\u529b\uff0c\u5185\u529f\u91cc\u7684\u5185\u529f\u800c\u5df2\u3002 \u3000\u3000\u5f53\u4e16\u80fd\u591f\u7ec3\u6210\u8fd9\u79cd\u5185\u529f\u7684\uff0c\u53ea\u6709\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u7ec3\u6210\u4e86\uff0c\u5374\u6b7b\u4e86\uff0c\u8840\u8109\u7834\u88c2\u800c\u6b7b\uff0c\u542c\u8bf4\u662f\u771f\u6c14\u592a\u76db\uff0c\u65e0\u6cd5\u5f00\u6cc4\uff0c\u6ea2\u8840\u800c\u6b81\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u662f\u7ec3\u5230\u4e00\u534a\uff0c\u8d70\u706b\u5165\u9b54\uff0c\u771f\u6c14\u6e90\u6e90\u5916\u6cc4\uff0c\u4e0d\u4f46\u6210\u4e86\u6b8b\u5e9f\uff0c\u8fd8\u53d8\u6210\u4e86\u767d\u75f4\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u4fbf\u662f\u201c\u8ff7\u5929\u4e03\u5723\u201d\u91cc\u7684\u5173\u4e03\uff0c\u4ed6\u5df2\u6210\u4e3a\u4eac\u57ce\u91cc\u8db3\u53ef\u4e0e\u201c\u516d\u5206\u534a\u5802\u201d\u53ca\u201c\u91d1\u98ce\u7ec6\u96e8\u697c\u201d\u76f8\u6297\u8861\u7684\u7b2c\u4e09\u52bf\u529b\u3002 \u3000\u3000\u6700\u540e\u4e00\u4f4d\u4fbf\u662f\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u201c\u6a2a\u5200\u7acb\u9a6c\uff0c\u9189\u5367\u5c71\u5188\u201d\u7684\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u65b9\u90aa\u771f\u56de\u671b\u60dc\u60dc\u3002 \u3000\u3000\u60dc\u60dc\u4f9d\u4f9d\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u706f\u5149\u91cc\uff0c\u6ee1\u76ee\u5a07\u695a\u800c\u67d4\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fc3\u4e2d\u4e5f\u6e29\u67d4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u660e\u5929\u4e0d\u8d70\u4e86\u3002\u201d \u3000\u3000\u5979\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6211\u540e\u5929\u8fd8\u662f\u8981\u8d70\u7684\u3002\u201d \u3000\u3000\u5979\u8bf4\uff1a\u201c\u80fd\u591a\u7559\u4e00\u5929\u603b\u662f\u597d\u4e8b\u3002\u201d \u3000\u3000\u5979\u4f3c\u662f\u54c0\u6101\uff0c\u53c8\u4f3c\u662f\u60cb\u53f9\u4f3c\u5730\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u8fdf\u4e00\u4e9b\uff0c\u6216\u65e9\u4e00\u4e9b\uff0c\u90fd\u4f1a\u4e0d\u4e00\u6837\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53cc\u7709\u4e00\u8e59\uff1a\u201c\u4f60\u4e0d\u9ad8\u5174\uff1f\u201d \u3000\u3000\u60dc\u60dc\u7f8e\u4e3d\u5730\u7b11\u5f00\u4e86\uff0c\u770b\u53bb\u7eaf\u771f\u3001\u5a07\u6206\u3001\u800c\u65e0\u90aa\uff0c\u65b9\u90aa\u771f\u770b\u5f97\u6709\u4e9b\u75f4\u4e86\u3002 \u3000\u3000\u4ed6\u624b\u4e0a\u65e0\u9152\uff0c\u5374\u6709\u9189\u610f\u3002 \u3000\u3000\u201c\u4f60\u80fd\u7559\uff0c\u6211\u8fd8\u80fd\u4e0d\u9ad8\u5174\u54ea\u3002\u201d\u5979\u7b11\u76c8\u76c8\u5730\u9053\uff0c\u201c\u4f60\u8981\u8d70\uff0c\u6211\u96be\u9053\u53bb\u8fd8\u795e\u4e48\uff01\u201d \u3000\u3000\u60dc\u60dc\u8fd9\u6837\u8bf4\u3002 \u3000\u3000\u53ef\u662f\u5979\u603b\u89c9\u5f97\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u6709\u4e9b\u8fc7\u9519\uff0c\u8fd8\u662f\u6709\u6b64\u9519\u8fc7\uff0c\u5728\u5fc3\u5934\u4e00\u63a0\u800c\u8fc7\uff0c\u8f7b\u8f7b\u7684\u63a0\u8fc7\u5fc3\u5934\u3002\u5979\u660e\u660e\u6e34\u671b\u65b9\u90aa\u771f\u80fd\u7559\u5728\u6d1b\u9633\uff0c\u5374\u4e3a\u4f55\u4f1a\u751f\u8d77\u8fd9\u79cd\u60f3\u6cd5\u5462\uff1f\u5979\u4e0d\u77e5\u9053\uff0c\u5979\u4e0d\u660e\u767d\uff0c\u5979\u4e5f\u4e0d\u95ee\u4e3a\u751a\u4e48\uff1f \u3000\u3000\u65b9\u90aa\u771f\u4eca\u5929\u56de\u5f97\u6bd4\u8f83\u665a\u3002 \u3000\u3000\u4ed6\u672c\u6765\u901a\u5e38\u5728\u620a\u4ea5\u65f6\u5206\u5c31\u4f1a\u56de\u5927\u9690\u4e18\u7684\u6cd5\u95e8\u5bfa\u53bb\u3002\u65b9\u7236\u5c31\u4f4f\u5728\u540e\u5c71\u3002 \u3000\u3000\u4ed6\u4eca\u665a\u5374\u5728\u5b50\u65f6\u65b9\u56de\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u98ce\u5e73\u4e91\u6b62\u3002\u6708\u6717\u6e05\u5929\u3002 \u3000\u3000\u662f\u4e0d\u662f\u5929\u8272\u9634\u6c89\u7684\u65f6\u5019\uff0c\u603b\u4f1a\u53d1\u751f\u4e0d\u5982\u610f\u7684\u4e8b\uff1f\u662f\u4e0d\u662f\u5728\u5929\u6c14\u6e05\u6717\u7684\u65f6\u5019\uff0c\u603b\u6709\u6bd4\u8f83\u5982\u610f\u7684\u4e8b\u53d1\u751f\uff1f \u3000\u3000\u4e0d\u662f\u3002 \u3000\u3000\u5929\u6c14\u662f\u5929\u6c14\u3002 \u3000\u3000\u4e8b\u662f\u4e8b\u3002 \u3000\u3000\u4eba\u662f\u4eba\u3002 \u3000\u3000\u6b63\u5982\u4e00\u4e2a\u4eba\u5728\u6781\u5bd2\u51b7\u7684\u5929\u6c14\u91cc\u3002\u4ed6\u7684\u5fc3\u70ed\u5f97\u50cf\u4e00\u56e2\u706b\uff0c\u4e5f\u53ef\u4ee5\u5728\u590f\u65e5\u5982\u708e\u91cc\uff0c\u4e00\u9897\u5fc3\u5374\u51b7\u4f3c\u7ed3\u4e86\u51b0\u3002 \u3000\u3000\u989c\u5915\u7684\u5fc3\u8fd8\u672a\u6210\u51b0\uff0c\u4f46\u7ea4\u7ea4\u5341\u6307\u5df2\u5feb\u6bd4\u51b0\u8fd8\u51b0\u3002 \u3000\u3000\u672c\u6765\uff0c\u989c\u5915\u4e0e\u6d2a\u4e09\u70ed\u8ddf\u516b\u540d\u624b\u4e0b\u5c31\u5019\u5728\u5927\u9690\u4e18\u6cd5\u95e8\u5bfa\u524d\u7684\u4e09\u767e\u516d\u5341\u4e94\u7ea7\u77f3\u9636\u65c1\u3001\u724c\u697c\u4e0b\uff0c\u60f3\u7b49\u90a3\u4e2a\u7adf\u4e0d\u63a5\u53d7\u793c\u8058\u7684\u5e74\u8f7b\u4eba\u7ecf\u8fc7\uff0c\u597d\u597d\u7684\u8bd5\u4e00\u8bd5\u4ed6\u3001\u5413\u4e00\u5413\u4ed6\u3002 \u3000\u3000\u4e0d\u6599\u5374\u5413\u7740\u81ea\u5df1\u3002 \u3000\u3000\u989c\u5915\u773c\u770b\u65f6\u95f4\u5df1\u8fd1\u4ea5\u5c3e\uff0c\u51c9\u98ce\u98d2\u98d2\uff0c\u5fc3\u4e2d\u5f88\u4e0d\u662f\u6ecb\u5473\uff1a\u83ab\u975e\u662f\u5251\u592b\u5b50\u5728\u65f6\u95f4\u4e0a\u63a8\u6d4b\u9519\u8bef\uff1f\u6b63\u662f\u90a3\u4e48\u60f3\u7684\u65f6\u5019\uff0c\u6d2a\u4e09\u70ed\u5df2\u6ee1\u662f\u4e0d\u8010\u70e6\u4e86\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u6012\u9053\uff1a\u201c\u4ed6\u5a18\u7684\uff01\u6211\u53bb\u4f9d\u4f9d\u697c\u628a\u90a3\u5c0f\u5b50\u626f\u56de\u6765\uff0c\u5728\u8fd9\u513f\u6b7b\u7b49\u6d3b\u8d56\u7684\uff0c\u4ed6\u5374\u5728\u90a3\u513f\u98ce\u6d41\u5feb\u6d3b\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u756a\u6c14\u8bed\uff0c\u4e0d\u610f\u628a\u989c\u5915\u4e5f\u9a82\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u989c\u5915\u4e0d\u4ee5\u4e3a\u5fe4\u3002 \u3000\u3000\u5979\u6380\u5f00\u8f7f\u5e18\uff0c\u770b\u770b\u5929\u8272\uff0c\u5374\u671b\u89c1\u6708\u8272\u3002 \u3000\u3000\u6708\u8272\u7167\u5728\u5979\u7684\u8138\u4e0a\uff0c\u5979\u7684\u7389\u988a\u5c31\u4f3c\u6708\u5149\u4e00\u822c\u67d4\u548c\uff0c\u5979\u7684\u773c\u6ce2\u5c31\u50cf\u6708\u8272\u4e00\u822c\u5e7d\u6028\uff0c\u5979\u7684\u624b\u6307\u5c31\u8ddf\u6708\u4eae\u4e00\u822c\u4f18\u7f8e\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u5c31\u50cf\u662f\u4eba\u95f4\u7684\u6708\u4eae\u3002 \u3000\u3000\u6708\u4eae\u4e0d\u77e5\u4ee4\u5979\u60f3\u8d77\u751a\u4e48\u3002 \u3000\u3000\u5979\u60a0\u60a0\u51fa\u795e\u3002 \u3000\u3000\u4e5f\u5e7d\u5e7d\u5931\u795e\u3002 \u3000\u3000\u7136\u540e\u5fe7\u5fe7\u4e00\u53f9\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u5374\u4ee5\u4e3a\u662f\u5927\u592b\u4eba\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u8df3\u7740\u811a\u9053\uff1a\u201c\u6211\u53bb\u3002\u201d \u3000\u3000\u989c\u5915\u5947\u9053\uff1a\u201c\u4f60\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u9053\uff1a\u201c\u6211\u53bb\u628a\u90a3\u5c0f\u5b50\u4ece\u4f9d\u4f9d\u697c\u7684\u706b\u70ed\u88ab\u7a9d\u91cc\u63ea\u51fa\u6765\uff0c\u5e26\u4ed6\u6765\u89c1\u5927\u592b\u4eba\uff01\u201d \u3000\u3000\u989c\u5915\u5fcd\u7b11\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u4e09\u54e5\u662f\u591f\u795e\u52c7\u4e86\uff0c\u4f46\u65b9\u516c\u5b50\u5c82\u4e0d\u662f\u5a01\u98ce\u5c3d\u5931\uff1f\u8fd9\u6837\u4e00\u6765\uff0c\u5c31\u7b97\u4ed6\u60f3\u52a0\u5165\u6211\u4eec\u5170\u4ead\u6c60\u5bb6\uff0c\u6050\u6015\u4e5f\u6ca1\u8fd9\u4e2a\u989c\u9762\u4e86\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u6014\u4e86\u6014\uff0c\u6478\u6478\u4e0b\u5df4\u82e6\u601d\u9053\uff1a\u201c\u8fd9\u2026\u2026\u4e2a\u2026\u2026\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4e09\u54e5\u770b\u6765\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u4e3a\u96be\u5462\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u5927\u529b\u7684\u6413\u6478\u7740\u4e0b\u9894\uff1a\u201c\u662f\u6709\u4e3a\u4e9b\u96be\u2026\u2026\u4f55\u51b5\uff0c\u6211\u51fa\u624b\u4e00\u5411\u90fd\u592a\u91cd\u4e86\u4e9b\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6211\u4eec\u73b0\u5728\u662f\u793c\u8058\u4eba\u5bb6\u6765\u4e3a\u54b1\u4eec\u6548\u529b\uff0c\u8fd9\u628a\u4eba\u5bb6\u4ece\u70ed\u88ab\u7a9d\u91cc\u4e00\u6380\uff0c\u8001\u9e70\u6293\u5c0f\u9e21\u4f3c\u7684\u62ff\u4e86\u8fc7\u6765\uff0c\u518d\u6765\u6566\u8bf7\u53ec\u8058\uff0c\u672a\u514d\u6709\u70b9\u2026\u2026\u4f3c\u4e4e\u6709\u4e00\u70b9\u70b9\u4e0d\u5bf9\u52b2\uff0c\u4e09\u54e5\u53ef\u4ee5\u4e3a\u7136\u5426\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u6df1\u8c0b\u8fdc\u8651\u5730\u9053\uff1a\u201c\u6211\u65e9\u4e5f\u60f3\u5230\u4e86\uff0c\u4f3c\u4e4e\u786e\u6709\u4e00\u70b9\u70b9\u4e0d\u5927\u5bf9\u52b2\u3002\u201d \u3000\u3000\u989c\u5915\u62ca\u638c\u7b11\u9053\uff1a\u201c\u4e09\u54e5\u8ddf\u6211\u771f\u662f\u6240\u89c1\u7565\u540c\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u4e5f\u7b11\u5f97\u4e00\u5f20\u5927\u53e3\u5408\u4e0d\u62e2\uff0c\u4e00\u53cc\u5927\u624b\uff0c\u6414\u816e\u6293\u52c3\uff0c\u5f88\u662f\u9ad8\u5174\u3002 \u3000\u3000\u989c\u5915\u4eae\u7740\u773c\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u6014\u9053\uff1a\u201c\u6240\u4ee5\uff1f\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u6240\u4ee5\u4e3a\u4e86\u5927\u5c40\uff0c\u4e09\u54e5\u5c31\u4e0d\u5fc5\u52b3\u9a7e\u8fd9\u4e00\u8d9f\u4e86\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u5bf9\uff0c\u6211\u5c31\u4e0d\u5fc5\u52b3\u9a7e\u8fd9\u4e00\u8d9f\u3002\u201d \u3000\u3000\u5ffd\u542c\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5927\u592b\u4eba\u5374\u8fd8\u662f\u8981\u52b3\u9a7e\u8d70\u4e00\u8d9f\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u970d\u7136\u56de\u9996\u3002 \u3000\u3000\u4ed6\u56de\u9996\u7684\u540c\u65f6\uff0c\u62f3\u5934\u63e1\u7d27\uff0c\u62f3\u9aa8\u4e5f\u540c\u65f6\u53d1\u51fa\u88c2\u9769\u4f3c\u7684\u54cd\u58f0\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u4ed6\u56de\u5934\u7684\u5239\u95f4\uff0c\u5e26\u6765\u7684\u516b\u540d\u968f\u4ece\uff0c\u5df2\u5012\u4e86\u56db\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u58f0\u4e0d\u54cd\u7684\u5c31\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6708\u4eae\u4e0b\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u81ea\u6570\u767e\u7ea7\u77f3\u9636\u4e0a\u62fe\u6b65\u800c\u4e0b\uff0c\u8863\u8882\u6cbe\u98ce\u3002 \u3000\u3000\u8fd9\u4eba\u6ee1\u8138\u7b11\u5bb9\u3002 \u3000\u3000\u989c\u5915\u4e00\u770b\u89c1\u4ed6\uff0c\u5fc3\u5c31\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u7b11\u6001\u53ef\u63ac\u7684\u4eba\uff0c\u8981\u6bd4\u4e00\u5343\u4e2a\u7ef7\u7740\u8138\u7684\u4eba\u52a0\u8d77\u6765\u90fd\u96be\u4ee5\u5e94\u4ed8\u5f97\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u5c31\u662f\u201c\u5c0f\u78a7\u6e56\u201d\u6e38\u5bb6\u7684\u5927\u603b\u7ba1\u7b80\u8fc5\u3002 \u3000\u3000\u989c\u5915\u4e00\u770b\u5c31\u77e5\u9053\uff0c\u8fd9\u4eba\u5982\u679c\u662f\u6ca1\u6709\u6781\u5927\u7684\u628a\u63e1\uff0c\u662f\u4e0d\u4f1a\u51fa\u52a8\u7684\uff0c\u4e00\u65e6\u51fa\u52a8\uff0c\u5c31\u4e0d\u6613\u7a7a\u56de\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u4ed6\u4eca\u5929\u770b\u6765\u5df2\u7a7a\u56de\u4e86\u4e00\u8d9f\u3002 \u3000\u3000\u2014\u2014\u4f9d\u4f9d\u697c\u4e0a\u8ddf\u65b9\u90aa\u771f\u4e00\u4f1a\uff0c\u7b80\u8fc5\u867d\u8138\u9732\u7b11\u5bb9\u800c\u53bb\uff0c\u4f46\u603b\u4e0d\u4f3c\u5927\u83b7\u5168\u80dc\u800c\u8fd4\u7684\u6837\u5b50\u3002 \u3000\u3000\u2014\u2014\u65e2\u7136\u5df2\u201c\u5931\u624b\u201d\u8fc7\u4e00\u6b21\uff0c\u5c31\u4e0d\u4f1a\u4f5c\u7b2c\u4e8c\u6b21\u7684\u81ea\u8ba8\u6ca1\u8da3\u3002 \u3000\u3000\u5c24\u5176\u662f\u7b80\u8fc5\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u50cf\u8fd9\u6837\u5b50\u7684\u4eba\u53ea\u8981\u6765\u4e86\u4e00\u4e2a\uff0c\u5c31\u5df2\u7ecf\u5341\u5206\u7684\u4e0d\u597d\u5bf9\u4ed8\u3002 \u3000\u3000\u800c\u8fd9\u4eba\u8fd8\u6ca1\u6709\u4e0b\u6765\uff0c\u516b\u6307\u8f7b\u5f39\u4e0b\uff0c\u81ea\u5df1\u8fd9\u8fb9\u7684\u516b\u4e2a\u90e8\u5c5e\uff0c\u5df2\u5012\u4e86\u56db\u540d\u3002 \u3000\u3000\u5176\u4f59\u56db\u540d\uff0c\u662f\u8f7f\u592b\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u5171\u62ac\u4e86\u4e24\u9876\u8f7f\u5b50\u6765\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u5750\u7684\u662f\u9a6c\uff0c\u4e24\u9876\u8f7f\u5b50\uff0c\u4e00\u662f\u989c\u5915\u4e58\u5750\u7684\uff0c\u4e00\u662f\u51c6\u5907\u8981\u7540\u65b9\u90aa\u771f\u56de\u5170\u4ead\u7684\u3002 \u3000\u3000\u8fd9\u56db\u540d\u8f7f\u592b\u5f53\u7136\u4e5f\u4f1a\u4e24\u4e0b\u5b50\uff0c\u4f46\u8981\u6bd4\u8d77\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u5f53\u7136\u5c31\u4e0d\u6b62\u5dee\u4e0a\u4e03\u516b\u4e0b\u5b50\u3002 \u3000\u3000\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u7b80\u8fc5\u4e00\u4e0a\u6765\uff0c\u5c31\u628a\u81ea\u5df1\u8fd9\u8fb9\u8fd8\u80fd\u4e00\u6218\u7684\u4eba\u70b9\u5012\u4e86\uff0c\u53ea\u5269\u4e0b\u81ea\u5df1\u548c\u6d2a\u4e09\u70ed\u3002 \u3000\u3000\u989c\u5915\u8fd8\u6ca1\u6709\u60f3\u5230\u5bf9\u65b9\u662f\u7528\u751a\u4e48\u624b\u6cd5\u9694\u7a7a\u70b9\u5012\u8fd9\u51e0\u4eba\uff0c\u4f46\u786e\u77e5\u8fd9\u56db\u4eba\u867d\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u5374\u6ca1\u6709\u6bd9\u547d\u3002 \u3000\u3000\u7b80\u8fc5\u4f3c\u5e76\u4e0d\u60f3\u6740\u6b7b\u4ed6\u4eec\u3002 \u3000\u3000\u2014\u2014\u201c\u56db\u516c\u5b50\u201d\u4e2d\uff0c\u9664\u4e86\u201c\u5999\u624b\u5802\u201d\u6562\u4e0b\u6bd2\u624b\u4e4b\u5916\uff0c\u5176\u4ed6\u591a\u60f3\u7559\u4e00\u70b9\u4f59\u5730\uff0c\u8ba9\u5bf9\u65b9\u6709\u4e00\u4e1d\u9000\u8def\uff0c\u4ee5\u4fbf\u4ed6\u65e5\u81ea\u5df1\u4e5f\u6709\u4e2a\u8f6c\u571c\u7684\u673a\u4f1a\u3002 \u3000\u3000\u2014\u2014\u201c\u56db\u516c\u5b50\u201d\u4e4b\u4e89\uff0c\u6bd5\u7adf\u4e0d\u540c\u4e8e\u4e00\u822c\u7684\u6c5f\u6e56\u4ec7\u6740\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u989c\u5915\u4f3c\u7565\u4e3a\u5b89\u5fc3\u4e86\u4e00\u4e9b\u4e9b\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u5b89\u5fc3\u4e5f\u4ec5\u6b62\u662f\u4e00\u4e9b\u4e9b\u800c\u5df2\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u73b0\u5728\u7684\u5904\u5883\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5b89\u5168\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5b89\u7a33\u3002 \u3000\u3000\u5979\u53ea\u5e0c\u671b\u7b80\u8fc5\u53ea\u662f\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u6837\u7684\u8bdd\uff0c\u5979\u548c\u6d2a\u4e09\u70ed\u534f\u529b\uff0c\u4e5f\u8bb8\u8fd8\u5bf9\u6297\u5f97\u4e86\u8fd9\u5934\u201c\u8c79\u5b50\u201d\u3002 \u3000\u3000\u8fd9\u5934\u4f1a\u7b11\u7684\u201c\u8c79\u5b50\u201d\u3002 \u3000\u3000\u8c79\u5b50\u591a\u662f\u6124\u6012\u7684\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u591a\u7684\u662f\u201c\u6012\u8c79\u201d\u3001\u201c\u9ed1\u8c79\u201d\u3001\u201c\u98de\u8c79\u5b50\u201d\u3002\u201c\u91d1\u94b1\u8c79\u201d\u7684\u79f0\u8bb3\uff0c\u6709\u8fd9\u4e9b\u5916\u53f7\u7684\u9ad8\u624b\uff0c\u591a\u662f\u51fa\u624b\u8fc5\u75be\u3001\u529b\u6c89\u52bf\u731b\uff0c\u800c\u6027\u5b50\u66b4\u70c8\uff0c\u5c31\u50cf\u8c79\u5b50\u4e00\u822c\u3002 \u3000\u3000\u7b80\u8fc5\u5374\u4e0d\u662f\u3002 \u3000\u3000\u5982\u679c\u8bf4\u4ed6\u662f\u201c\u8c79\u5b50\u201d\uff0c\u4ed6\u662f\u4e00\u5934\u201c\u4f1a\u7b11\u7684\u8c79\u5b50\u201d\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5f6c\u5f6c\u6709\u793c\u3001\u8fd8\u8c26\u900a\u5f97\u4f53\uff0c\u770b\u53bb\u50cf\u4e00\u4e2a\u4ea4\u9645\u4eba\u6750\uff0c\u8fd8\u591a\u4e8e\u50cf\u4e00\u4e2a\u6b66\u6797\u4eba\u7269\u3002 \u3000\u3000\u201c\u6211\u5f53\u7136\u4e0d\u662f\u4e00\u4e2a\u4eba\u6765\u7684\u3002\u201d\u7b80\u8fc5\u7b2c\u4e00\u53e5\u5c31\u5c01\u6740\u4e86\u989c\u5915\u7684\u5e0c\u671b\uff0c\u201c\u6211\u8fd8\u6709\u4e24\u9876\u8f7f\u5b50\uff0c\u5019\u5728\u5c71\u540e\uff0c\u7b49\u4e24\u4f4d\u8fc7\u53bb\u4e58\u5750\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u4e00\u7b11\u53c8\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u8981\u662f\u4e24\u4f4d\u4e0d\u559c\u6b22\uff0c\u8981\u5750\u56de\u81ea\u5df1\u7684\u8f7f\u5b50\u4e5f\u884c\uff0c\u6240\u4ee5\uff0c\u6211\u628a\u8fd9\u56db\u4f4d\u5c0f\u5144\u5f1f\u7559\u4e0b\u4e86\uff0c\u5982\u679c\u4f60\u4eec\u8981\u81ea\u5df1\u4eba\u62ac\u8f7f\uff0c\u4e5f\u65e0\u4e0d\u4fbf\u5904\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u822c\u8bf4\u6cd5\uff0c\u4f3c\u5df2\u517c\u987e\u5468\u5230\uff0c\u7ed9\u4e86\u989c\u5915\u548c\u6d2a\u4e09\u70ed\u6781\u5927\u7684\u65b9\u4fbf\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u4e00\u542c\u5c31\u8981\u53d1\u4f5c\uff0c\u989c\u5915\u5374\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u7b80\u7ba1\u4e8b\u8981\u6211\u4eec\u5230\u751a\u4e48\u5730\u65b9\u53bb\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u5df2\u8d70\u8fd1\u79bb\u9636\u4e0b\u7ea6\u83ab\u5341\u4e94\u7ea7\uff0c\u4fbf\u505c\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u8fdc\u4e0d\u8fdc\uff0c\u53ea\u5230\u5c0f\u78a7\u6e56\u53bb\u4e00\u8d9f\u800c\u5df2\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u5230\u5c0f\u78a7\u6e56\u53bb\u4e48\uff1f\u6211\u53ef\u6ca1\u6709\u5907\u793c\uff0c\u800c\u4e14\uff0c\u8fd9\u6bb5\u8def\u4e5f\u6709\u4e03\u516b\u91cc\u8fdc\uff0c\u8981\u53bb\u4e5f\u5e94\u8be5\u6709\u4e9b\u51c6\u5907\uff0c\u518d\u8bf4\uff0c\u5924\u591c\u9020\u8bbf\u6e38\u516c\u5b50\uff0c\u6211\u662f\u5987\u9053\u4eba\u5bb6\uff0c\u603b\u662f\u6709\u4e9b\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u7b80\u8fc5\u9053\uff1a\u201c\u76f8\u8bf7\u4e0d\u5982\u5076\u9047\u3002\u6211\u4eec\u90fd\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u5927\u592b\u4eba\u5c24\u5176\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u4f55\u5fc5\u62d8\u8fd9\u79cd\u4fd7\u793c\uff01\u201d\u5374\u5de7\u5999\u7684\u628a\u6b64\u53bb\u662f\u4e0d\u662f\u89c1\u6e38\u516c\u5b50\u7684\u8bdd\u9898\u907f\u53bb\u4e0d\u8c08\u3002 \u3000\u3000\u989c\u5915\u5374\u4ecd\u662f\u8981\u95ee\uff1a\u201c\u5982\u679c\u8d35\u5e9c\u6709\u610f\u8981\u8bf7\u6211\u4eec\u8fc7\u53bb\u4f1a\u53d9\uff0c\u4f55\u4e0d\u62a5\u5e16\u655d\u5e84\uff0c\u8fd9\u822c\u7a81\u513f\u76f8\u8bf7\uff0c\u5c82\u4e0d\u6709\u4e9b\u5192\u6627\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7b97\u5b9a\u5927\u592b\u4eba\u591a\u4f1a\u5728\u6b64\u5730\u7b49\u5019\u65b9\u5c11\u4fa0\uff0c\u4e0d\u8fc7\uff0c\u770b\u6765\u65b9\u5c11\u4fa0\u4eca\u665a\u8981\u8fdf\u4e9b\u624d\u56de\u6765\uff0c\u6c60\u516c\u5b50\u8ddf\u655d\u5e9c\u7684\u4e3b\u4eba\u60c5\u540c\u624b\u8db3\uff0c\u8fd9\u4e9b\u513f\u793c\u6570\u6b20\u5468\u4e4b\u5904\uff0c\u5927\u592b\u4eba\u82f1\u777f\u4fa0\u9aa8\uff0c\u5b9a\u4e0d\u8ba1\u8f83\u3002\u201d \u3000\u3000\u989c\u5915\u7709\u6bdb\u4e00\u5254\uff0c\u5355\u5200\u76f4\u5165\u5730\u95ee\uff1a\u201c\u54e6\uff1f\u8fd9\u4e48\u8bf4\uff0c\u4eca\u665a\u4f60\u662f\u5949\u6e38\u516c\u5b50\u4e4b\u547d\uff0c\u6765\u5f3a\u9080\u6211\u4eec\u53bb\u5c0f\u78a7\u6e56\u7684\u4e86\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u4ecd\u662f\u4e0d\u76f4\u63a5\u7b54\u590d\uff0c\u53ea\u8bf4\uff1a\u201c\u5927\u592b\u4eba\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u77e5\u9053\u518d\u95ee\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u6709\u7ed3\u679c\uff0c\u56e0\u4e3a\u8fd9\u7b80\u8fc5\u7ef0\u53f7\u201c\u8c79\u5b50\u201d\uff0c\u4f46\u6bd4\u6ce5\u9cc5\u8fd8\u6ed1\uff0c\u4ed6\u65e2\u4e0d\u80af\u8bf4\u51fa\u662f\u6e38\u7389\u906e\u7684\u610f\u601d\uff0c\u4e07\u4e00\u5931\u624b\uff0c\u6e38\u5bb6\u7684\u4eba\u4e5f\u53ef\u4ee5\u77e2\u53e3\u5426\u8ba4\uff0c\u4e0e\u4ed6\u4eec\u65e0\u5173\uff0c\u5ba3\u79f0\u8fd9\u53ea\u662f\u7b80\u8fc5\u7684\u79c1\u4e0b\u884c\u52a8\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u624b\u638c\u3001\u8155\u3001\u6307\u95f4\u51e0\u4e0b\u5e72\u51c0\u5229\u843d\u7684\u52a8\u4f5c\uff0c\u5df2\u780c\u51fa\u4e00\u628a\u4e08\u4e8c\u957f\u67aa\uff0c\u4ed6\u628a\u67aa\u4e00\u5212\u3001\u5c71\u98ce\u4e2d\uff0c\u5929\u795e\u4e5f\u4f3c\u7684\u5a01\u98ce\u51db\u51db\uff0c\u9661\u5730\u4e00\u58f0\u5927\u559d\u9053\uff1a\u201c\u6211\u53bb\uff01\u201d \u3000\u3000\u7b80\u8fc5\u795e\u8272\u4e0d\u53d8\uff0c\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5927\u592b\u4eba\u60f3\u5fc5\u4e5f\u4e00\u9053\u4e0a\u8def\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff01\u201d\u6d2a\u4e09\u70ed\u96f7\u9706\u4f3c\u7684\u5486\u54ee\u9053\uff0c\u201c\u4e0d\u8fc7\u8981\u5148\u5f97\u95ee\u8fc7\u6211\u624b\u4e0a\u7684\u67aa\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u624b\u4e0a\u7684\u67aa\u5c31\u53d8\u6210\u4e86\u4e00\u6280\u82b1\u3002 \u3000\u3000\u4e00\u6746\u67aa\u5f53\u7136\u4e0d\u53ef\u80fd\u53d8\u6210\u4e00\u679d\u82b1\u3002 \u3000\u3000\u67aa\u5728\u6325\u4f7f\u7684\u65f6\u5019\uff0c\u624d\u4f1a\u5212\u51fa\u201c\u67aa\u82b1\u201d\u3002 \u3000\u3000\u90a3\u662f\u67aa\u82b1\uff0c\u67aa\u82b1\u4e0d\u662f\u82b1\uff0c\u6b63\u5982\u5929\u82b1\u4e0d\u662f\u82b1\uff0c\u70df\u82b1\u4e5f\u4e0d\u662f\u82b1\u4e00\u6837\u3002 \u3000\u3000\u53ef\u662f\u67aa\u672c\u6765\u5728\u6d2a\u4e09\u70ed\u624b\u91cc\uff0c\u73b0\u5728\u771f\u7684\u53d8\u6210\u4e86\u4e00\u6735\u82b1\u3002 \u3000\u3000\u90a3\u662f\u56e0\u4e3a\u6d2a\u4e09\u70ed\u4e00\u8bf4\u5b8c\u4e86\u90a3\u53e5\u8bdd\uff0c\u6709\u5341\u4e03\u516b\u4ef6\u201c\u6697\u5668\u201d\u5411\u4ed6\u653b\u4e86\u8fc7\u6765\uff01 \u3000\u3000\u8fd9\u4e9b\u201c\u6697\u5668\u201d\u5168\u4e0d\u5e26\u534a\u4e1d\u5389\u70c8\u7684\u98ce\u58f0\uff0c\u6240\u4ee5\u5f53\u6d2a\u4e09\u70ed\u53d1\u73b0\u65f6\uff0c\u201c\u6697\u5668\u201d\u5df2\u7136\u653b\u5230\u8138\u95e8\u3002 \u3000\u3000\u201c\u6697\u5668\u201d\u4e0d\u6b62\u653b\u5411\u4ed6\u7684\u9762\u524d\uff0c\u4ed6\u8eab\u524d\u8eab\u540e\u3001\u4e0a\u4e2d\u4e0b\u76d8\u81f3\u5c11\u6709\u5341\u5904\u8981\u5bb3\u90fd\u5728\u201c\u6697\u5668\u201d\u7684\u653b\u88ad\u8303\u56f4\u4e4b\u5185\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u624b\u4e0a\u7684\u67aa\u592a\u957f\uff0c\u96be\u4ee5\u62db\u67b6\uff0c\u201c\u6697\u5668\u201d\u53c8\u6765\u5f97\u592a\u5feb\uff0c\u4e0d\u53ca\u95ea\u8eb2\uff0c\u53ea\u6709\u7528\u7a7a\u7740\u7684\u4e00\u53ea\u624b\u6765\u63a5\u3001 \u3000\u3000\u5f53\u4ed6\u628a\u201c\u6697\u5668\u201d\u5c3d\u7686\u63a5\u5b8c\u65f6\uff0c\u53d1\u89c9\u624b\u4e0a\u7684\u67aa\u88ab\u593a\uff0c\u4ed6\u624b\u6307\u5fd9\u7d27\u4e86\u4e00\u7d27\uff0c\u5374\u63e1\u4f4f\u4e86\u4e00\u679d\u82b1\u3002 \u3000\u3000\u83b2\u82b1\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53d1\u73b0\u4ed6\u6240\u63a5\u6240\u67b6\u7684\u201c\u6697\u5668\u201d\uff0c\u5168\u662f\u82b1\u3002\u4e0d\u540c\u7684\u82b1\u3002 \u3000\u3000\u552f\u4e00\u76f8\u540c\u7684\u662f\uff1a \u3000\u3000\u82b1\u90fd\u7f8e\u4e3d\u3002 \u3000\u3000\u7f8e\u4e3d\u7684\u82b1\u3002 \u3000 \u7b2c\u5341\u4e09\u7ae0\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba \u3000 \u3000\u3000\u53ef\u662f\u8ddf\u8fd9\u4e2a\u5973\u4eba\u4e00\u5ab2\uff0c\u6240\u6709\u7684\u82b1\u90fd\u4e3a\u4e4b\u9eef\u6de1\u5931\u8272\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u7f8e\u8273\u5982\u73ab\u7470\u3002 \u3000\u3000\u6e29\u67d4\u5982\u591c\u3002 \u3000\u3000\u82b1\u8981\u5728\u9633\u5149\u706f\u8272\u4e0b\u624d\u5f00\u5f97\u7480\u707f\u593a\u8273\uff0c\u53ef\u662f\u8fd9\u5973\u4eba\u5728\u6216\u660e\u6216\u6697\u7684\u6708\u8272\u4e0b\u4f9d\u7136\u67d4\u5a9a\u5165\u9aa8\u3002 \u3000\u3000\u8fde\u989c\u5915\u770b\u4e86\uff0c\u4e5f\u4e0d\u7981\u5728\u5fc3\u91cc\u559d\u4e00\u58f0\u5f69\uff1a \u3000\u3000\u8fd9\u6837\u770b\u53bb\uff0c\u5979\u4e0d\u662f\u5341\u5168\u5341\u7f8e\uff0c\u5979\u7684\u9aa8\u67b6\u5b50\u6709\u70b9\u7565\u5927\uff0c\u98a7\u9aa8\u4e5f\u5acc\u7565\u4e30\u4e86\u4e9b\uff0c\u7b11\u7684\u65f6\u5019\u5634\u5df4\u4e5f\u7a0d\u9614\u4e86\u70b9\uff0c\u773c\u7738\u91cc\u6e34\u671b\u7684\u795e\u8272\u4e5f\u9732\u4e86\u4e9b\u2026\u2026\u53ef\u662f\uff0c\u8fd9\u6837\u770b\u53bb\uff0c\u5979\u5374\u660e\u660e\u6bd4\u5973\u4eba\u66f4\u50cf\u5973\u4eba\u3002 \u3000\u3000\u5c24\u5176\u90a3\u5507\u3002 \u3000\u3000\u7ea2\u800c\u8273\u3002 \u3000\u3000\u50cf\u4e94\u6708\u7684\u5c71\u82b1\u3002\u5341\u6708\u7684\u5c71\u706b\u3002\u604b\u4eba\u7684\u5fc3\u3002\u60c5\u4eba\u7684\u8840\u3002\u60ca\u5fc3\u52a8\u9b44\u4f46\u53c8\u67d4\u8273\u5165\u9aa8\u7684\u7ea2\u7740\u3002 \u3000\u3000\u90a3\u5973\u4eba\u7b11\u4e86\uff0c\u7f8e\u4e3d\u7684\u5507\u5212\u51fa\u7f8e\u4e3d\u7684\u5f27\u578b\uff0c\u5979\u7684\u8bed\u97f3\u4f4e\u67d4\u5982\u53f9\u606f\uff1a\u201c\u4f60\u7684\u624b\u4e0a\u662f\u67aa\u4e48\uff1f\u4e0d\u662f\u82b1\u5417\uff1f\u660e\u660e\u662f\u82b1\uff0c\u4e3a\u751a\u4e48\u4f60\u8bf4\u5b83\u662f\u67aa\u5462\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u6012\u543c\u3002 \u3000\u3000\u4ed6\u4e00\u817e\u8eab\uff0c\u6251\u5411\u90a3\u5973\u4eba\uff0c\u4e00\u62f3\u6253\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u4e4b\u58f0\u52bf\uff0c\u5c31\u7b97\u524d\u9762\u662f\u4e00\u5934\u5927\u8c61\uff0c\u4e5f\u4f1a\u7ed9\u4ed6\u4e00\u62f3\u51fb\u6bd9\uff1b\u5982\u679c\u662f\u4e00\u5757\u5de8\u5ca9\uff0c\u4e5f\u4f1a\u7ed9\u4ed6\u4e00\u62f3\u6253\u788e\u3002 \u3000\u3000\u90a3\u5973\u4eba\u5374\u504f\u504f\u4e0d\u95ea\u4e0d\u8eb2\u3002\u53cd\u800c\u4e00\u633a\u80f8\uff0c\u95ed\u8d77\u53cc\u76ee\uff0c\u5658\u8d77\u7ea2\u5507\uff0c\u4ef0\u7740\u8138\u513f\uff0c\u633a\u8d77\u4e30\u6ee1\u7684\u80f8\u812f\uff0c\u53ea\u8bf4\uff1a\u201c\u4f60\u65e2\u7136\u90a3\u4e48\u559c\u6b22\u6b3a\u8d1f\u5973\u4eba\uff0c\u4f60\u6253\u554a\u4f60\u6253\u554a\uff01\u201d \u3000\u3000\u901a\u5e38\u4e00\u4e2a\u5973\u4eba\u6709\u8fd9\u79cd\u8868\u60c5\u7684\u65f6\u5019\uff0c\u662f\u7ed9\u4eba\u4eb2\u543b\uff0c\u800c\u4e0d\u662f\u7ed9\u4eba\u75db\u6bb4\u7684\u3002 \u3000\u3000\u4f55\u51b5\u662f\u4e00\u4e2a\u90a3\u4e48\u7f8e\u8273\u7684\u5973\u4eba\uff0c\u8c01\u5fcd\u5fc3\u6253\u5979\uff1f\u522b\u8bf4\u6253\u5979\uff0c\u5c31\u7b97\u6cbe\u4e00\u6cbe\uff0c\u4e5f\u6015\u843d\u4e86\u82b1\u74e3\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u662f\u7537\u4eba\u3002 \u3000\u3000\u800c\u4e14\u8fd8\u662f\u6761\u597d\u6c49\u3002 \u3000\u3000\u597d\u6c49\u4e0d\u6253\u5973\u4eba\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u7684\u62f3\u5934\u786c\u751f\u751f\u987f\u4f4f\u3002 \u3000\u3000\u4ed6\u7684\u62f3\u52bf\u5927\u731b\u70c8\uff0c\u53ea\u80fd\u53d1\uff0c\u4e0d\u5b9c\u6536\uff0c\u8fd9\u786c\u751f\u751f\u6536\u52bf\uff0c\u4f7f\u5f97\u6d2a\u4e09\u70ed\u80f8\u819b\u5c31\u50cf\u7ed9\u81ea\u5df1\u72e0\u72e0\u7684\u64c2\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u53ea\u542c\u989c\u5915\u547c\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u970d\u7136\u8fd4\u8eab\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u652f\u5de8\u7bad\u6620\u7740\u6708\u8292\u5411\u4ed6\u76f4\u6295\u800c\u81f3\uff01 \u3000\u3000\u90a3\u5f53\u7136\u4e0d\u662f\u7bad\u3002 \u3000\u3000\u800c\u662f\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u7684\u901f\u5ea6\u592a\u5feb\u4e86\uff0c\u4ee5\u81f4\u8863\u886b\u53cd\u6620\u5728\u6708\u8272\u91cc\uff0c\u6f3e\u8d77\u4e00\u62b9\u6de1\u6de1\u7684\u534e\u5f69\u3001\u76f4\u5c04\u5411\u81ea\u5df1\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u8fd9\u624d\u660e\u767d\u7b80\u8fc5\u5916\u53f7\u4e3a\u4ec0\u4e48\u53eb\u201c\u8c79\u5b50\u201d\u3002 \u3000\u3000\u5982\u679c\u8bf4\u8c79\u5b50\u7684\u653b\u51fb\u5feb\u5f97\u50cf\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\uff0c\u90a3\u5c31\u9519\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u50cf\u7b80\u8fc5\u8fd9\u6837\u7684\u201c\u8c79\u5b50\u201d\uff0c\u522b\u8bf4\u63a9\u8033\uff0c\u8fde\u7728\u773c\u7684\u65f6\u95f4\u4e5f\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6d2a\u4e09\u70ed\u7684\u201c\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\u201d\uff0c\u5374\u80fd\u53ca\u65f6\u51dd\u805a\uff0c\u864e\u62f3\u9f99\u5544\uff0c\u4e5f\u7acb\u65f6\u53d1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u672c\u6765\uff0c\u5728\u6b66\u5b66\u4e0a\uff0c\u53ea\u6709\u201c\u864e\u722a\u201d\uff0c\u800c\u6ca1\u6709\u201c\u864e\u62f3\u201d\uff0c\u6d2a\u4e09\u70ed\u53e6\u521b\u4e00\u683c\uff0c\u628a\u201c\u5c11\u6797\u795e\u62f3\u201d \u3000\u3000\u548c\u201c\u864e\u722a\u201d\u4e8c\u5408\u4e3a\u4e00\uff0c\u540c\u6837\u672c\u6765\u53ea\u6709\u201c\u9f99\u722a\u201d\u548c\u201c\u9e64\u5544\u201d\uff0c\u6d2a\u4e09\u70ed\u4e5f\u628a\u4e8c\u8005\u6df7\u5408\u4f7f\u7528\uff0c\u53d8\u6210\u4e86\u4ed6\u7684\u770b\u5bb6\u672c\u9886\u3002 \u3000\u3000\u8fd9\u56db\u79cd\u62f3\u6cd5\u7684\u6df7\u5408\u4f7f\u7528\uff0c\u4ea6\u5373\u662f\u5c06\u8fd9\u56db\u79cd\u62f3\u6cd5\u7684\u7cbe\u534e\u63d0\u70bc\u4e86\u51fa\u6765\uff0c\u4e0d\u4f46\u7edd\u5bf9\u6709\u6548\uff0c\u800c\u4e14\u7b49\u4e8e\u628a\u8fd9\u56db\u79cd\u62f3\u6cd5\u7684\u5a01\u529b\u589e\u52a0\u4e86\u56db\u500d\uff0c\u52a0\u4e0a\u6d2a\u4e09\u70ed\u5929\u751f\u795e\u529b\uff0c\u5929\u7eb5\u795e\u52c7\uff0c\u662f\u4ee5\u4f7f\u4ed6\u6210\u4e3a\u5170\u4ead\u6c60\u5bb6\u9ebe\u4e0b\u7b2c\u4e00\u52c7\u58eb\u3002 \u3000\u3000\u4ed6\u970d\u7136\u8fd4\u8eab\uff0c\u4fbf\u8981\u8fd0\u529f\u51fa\u624b\u3002 \u3000\u3000\u53ef\u60dc\u7b80\u8fc5\u5e76\u4e0d\u662f\u653b\u5411\u4ed6\uff0c\u800c\u662f\u653b\u5411\u989c\u5915\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u53ea\u89c9\u80cc\u540e\u4e00\u9ebb\uff0c\u4e0a\u8eab\u4e00\u5bd2\uff0c\u4e0b\u8eab\u9aa4\u70ed\uff0c\u201c\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\u201d\u7684\u529f\u529b\uff0c\u7adf\u88ab\u7834\u53bb\u3002 \u3000\u3000\u90a3\u5973\u4eba\u7f13\u7f13\u7684\u7f29\u56de\u4e86\u624b\u6307\u3002 \u3000\u3000\u7ea4\u7ea4\u4e94\u6307\u3002 \u3000\u3000\u6307\u7532\u4e0a\u8fd8\u6d82\u7740\u51e4\u4ed9\u82b1\u6c41\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u7684\u201c\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\u201d\uff0c\u5df2\u65e0\u7834\u7efd\u53ef\u8a00\uff0c\u5982\u679c\u786c\u8981\u8bf4\u6709\uff0c\u90a3\u4e48\u53ea\u6709\u80cc\u90e8\u810a\u690e\u9aa8\u8fd1\u76d8\u9aa8\u5904\u7684\u5173\u5143\u4fde\u4e0e\u4e0a\u4f53\u7a74\u4e4b\u95f4\uff0c\u6709\u4e00\u5904\u6c14\u5b54\u3002 \u3000\u3000\u90a3\u5973\u4eba\u7684\u98df\u6307\u6307\u7532\uff0c\u4e0d\u504f\u4e0d\u501a\uff0c\u5c31\u5728\u6d2a\u4e09\u70ed\u8fd4\u8eab\u7684\u5239\u90a3\uff0c\u6070\u597d\u6233\u5728\u90a3\u513f\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u7acb\u5373\u5c31\u50cf\u4e00\u4e2a\u672c\u7a7f\u7740\u76d4\u7532\u7684\u6b66\u58eb\uff0c\u5374\u5ffd\u7136\u7ed9\u4eba\u62bd\u79bb\u4e86\u9aa8\u9abc\uff0c\u6574\u4e2a\u4eba\u90fd\u6563\u4e86\uff0c\u762b\u75ea\u4e8e\u5730\u3002 \u3000\u3000\u7b80\u8fc5\u7b2c\u516b\u6b21\u63a0\u5411\u989c\u5915\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6cd5\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u66f4\u5feb\u75be\u3002 \u3000\u3000\u4ed6\u98de\u63a0\u52bf\u5b50\u4e4b\u8fc5\u75be\uff0c\u773c\u770b\u5df2\u5230\u4e86\u901f\u5ea6\u7684\u6781\u9650\uff0c\u5374\u4e0d\u6599\u4ed6\u4e0b\u4e00\u6b21\u98de\u63a0\uff0c\u53c8\u6bd4\u4e0a\u4e00\u6b21\u66f4\u5feb\u66f4\u75be\u3002 \u3000\u3000\u4ed6\u594b\u8eab\u6251\u51fb\u989c\u5915\u3002 \u3000\u3000\u989c\u5915\u62d4\u5251\u53cd\u51fb\u3002 \u3000\u3000\u5251\u662f\u77ed\u5251\uff0c\u4ec5\u957f\u4e00\u5c3a\u4e00\u5bf8\u4e00\u5206\u4e00\u3002 \u3000\u3000\u5979\u4e00\u62d4\u5251\uff0c\u5251\u867d\u77ed\uff0c\u4f46\u65b9\u5706\u4e00\u4e08\u4e4b\u5185\uff0c\u5168\u5145\u6ea2\u7740\u5979\u7684\u5251\u610f\u3002 \u3000\u3000\u7b80\u8fc5\u4e00\u89e6\u5373\u9000\u3002 \u3000\u3000\u9000\u56de\u77f3\u9636\u4e4b\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u811a\u624d\u6cbe\u77f3\u9636\uff0c\u53c8\u518d\u4f5c\u7b2c\u4e8c\u5ea6\u6251\u51fb\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u5feb\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u51cc\u5389\u3002 \u3000\u3000\u4ed6\u8ddf\u989c\u5915\u7684\u5251\u610f\u7a0d\u4e00\u63a5\u89e6\uff0c\u7acb\u5373\u75be\u9000\uff0c\u6bcf\u4e00\u6b21\u98de\u9000\uff0c\u90fd\u9000\u5f97\u66f4\u8fdc\uff0c\u9000\u7acb\u5728\u66f4\u9ad8\u7684\u77f3\u9636\u4e0a\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u4e94\u6b21\u6251\u51fb\uff0c\u7b80\u8fc5\u5df2\u7ad9\u5728\u4e09\u5341\u4f59\u7ea7\u77f3\u9636\u4e0a\u4e0b\u6251\uff0c\u58f0\u52bf\u66f4\u5f3a\u3002 \u3000\u3000\u4f46\u989c\u5915\u624b\u4e2d\u7684\u5251\uff0c\u5374\u8d8a\u6218\u8d8a\u957f\u3002 \u3000\u3000\u5979\u7684\u5251\uff0c\u77ed\u7684\u65f6\u5019\u770b\u8d77\u6765\u539a\u5203\u8584\u950b\uff0c\u4f46\u5251\u8eab\u5374\u88c5\u6709\u7c27\u62ec\uff0c\u7531\u4e8e\u7b80\u8fc5\u7684\u6251\u51fb\u7331\u88ad\u592a\u5267\uff0c\u538b\u529b\u592a\u5de8\uff0c\u989c\u5915\u53ea\u597d\u628a\u5251\u950b\u5410\u73b0\uff0c\u5f53\u7b80\u8fc5\u7684\u7b2c\u516d\u51fb\u65f6\uff0c\u989c\u5915\u7684\u5251\u957f\u56db\u5c3a\u4e00\u3002 \u3000\u3000\u5979\u7684\u5251\u950b\u5c1a\u53ea\u4e00\u5c3a\u4e00\u65f6\uff0c\u4f59\u950b\u5df2\u53ca\u4e08\u8fdc\uff0c\u66f4\u4f55\u51b5\u662f\u5979\u7684\u5251\u5410\u4f38\u81f3\u56db\u5c3a\u4e00\u5bf8\u7684\u65f6\u5019\uff1f \u3000\u3000\u53ef\u662f\uff0c\u5979\u8fd8\u662f\u62b5\u6321\u4e0d\u4f4f\u7b80\u8fc5\u7684\u653b\u52bf\u3002 \u3000\u3000\u7b80\u8fc5\u8fde\u653b\u4e03\u6b21\uff0c\u518d\u731b\u70c8\u7684\u4e0b\u6251\u3002 \u3000\u3000\u989c\u5915\u594b\u5251\u62db\u67b6\uff0c\u5251\u5df2\u4e0d\u518d\u4f38\u957f\u3002 \u3000\u3000\u5251\u5df2\u5230\u6781\u9650\u3002 \u3000\u3000\u5251\u62db\u4e5f\u4f7f\u5c3d\u3002 \u3000\u3000\u7b80\u8fc5\u75be\u9000\u56de\u7b2c\u4e09\u5341\u4e94\u7ea7\u77f3\u9636\uff0c\u6d12\u7136\u7b11\u9053\uff1a\u201c\u5927\u592b\u4eba\uff0c\u4f60\u5c31\u8ddf\u6211\u4eec\u56de\u53bb\u4e00\u8d9f\u7f62\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5019\u6d2a\u4e09\u70ed\u5df2\u53d7\u5236\u3002 \u3000\u3000\u989c\u5915\u4e5f\u5598\u6c14\u5401\u5401\u3002 \u3000\u3000\u654c\u65b9\u8fd8\u6709\u90a3\u4f4d\u6bd4\u73ab\u7470\u82b1\u8fd8\u7f8e\u4e3d\u7684\u5973\u4eba\u3002 \u3000\u3000\u770b\u6765\u989c\u5915\u5df2\u522b\u65e0\u9009\u62e9\u3002 \u3000\u3000\u5979\u4e5f\u770b\u5f97\u51fa\u6765\uff1a\u7b80\u8fc5\u53ea\u662f\u5728\u6d88\u8017\u5979\u7684\u4f53\u529b\uff0c\u65e0\u610f\u8981\u6740\u4f24\u5979\uff0c\u800c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u5df2\u51fa\u52a8\u5230\u65d7\u4e0b\u4e24\u5458\u5927\u5c06\uff1a\u201c\u8c79\u5b50\u201d\u7b80\u8fc5\u548c\u82b1\u6cbe\u5507\uff0c\u5c31\u662f\u6446\u660e\u4e86\u4e0d\u5f97\u624b\u7edd\u4e0d\u7a7a\u8fd4\u3002 \u3000\u3000\u989c\u5915\u59d4\u60cb\u5730\u5fae\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u771f\u7684\u8981\u6211\u53bb\u5c0f\u78a7\u6e56\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u6e29\u548c\u4f46\u575a\u5b9a\u5730\u9053\uff1a\u201c\u5c11\u4e0d\u514d\u8981\u5927\u592b\u4eba\u52b3\u9a7e\u4e00\u8d9f\u3002\u201d \u3000\u3000\u989c\u5915\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\u6211\u80fd\u62d2\u7edd\u5417\uff1f\u201d \u3000\u3000\u5ffd\u542c\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u80fd\u3002\u201d \u3000\u3000\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u77f3\u9636\u4e0b\u7684\u5e7f\u573a\u4e0a\uff0c\u591a\u4e86\u516b\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u516b\u4e2a\u4eba\u5374\u4e0d\u662f\u81ea\u5df1\u8d70\u8fc7\u6765\u7684\u3002 \u3000\u3000\u800c\u662f\u88ab\u201c\u4e22\u201d\u8fc7\u6765\u7684\u3002 \u3000\u3000\u8fd9\u662f\u516b\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u4e00\u89c1\u8fd9\u516b\u540d\u6b7b\u4eba\uff0c\u4e00\u5411\u5904\u53d8\u4e0d\u60ca\u3001\u9047\u5371\u4e0d\u4e71\u7684\u7b80\u8fc5\uff0c\u4e5f\u53d8\u4e86\u8138\u8272\u3002 \u3000\u3000\u8fd9\u516b\u4e2a\u4eba\uff0c\u6b63\u662f\u62ac\u8f7f\u5019\u5728\u540e\u5c71\u7684\u516b\u540d\u8f7f\u592b\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u516b\u540d\u8f7f\u592b\u90fd\u6b7b\u4e86\uff0c\u8fd9\u8fd8\u4e0d\u53ef\u6015\uff0c\u53ef\u6015\u7684\u662f\u4ed6\u4eec\u7adf\u8fde\u4e00\u70b9\u58f0\u606f\u90fd\u542c\u4e0d\u89c1\uff0c\u8fd9\u4e5f\u4e0d\u53ef\u6015\uff0c\u53ef\u6015\u7684\u662f\u8fd9\u516b\u4eba\u672c\u6765\u4e5f\u662f\u201c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u201d\u7684\u9ad8\u624b\uff0c\u76f4\u63a5\u662f\u7531\u987e\u4f5b\u5f71\u8bad\u7ec3\u51fa\u6765\u7684\uff0c\u800c\u4eca\u7adf\u5982\u6b64\u4e0d\u6d4e\uff0c\u7ed9\u4eba\u6ca1\u58f0\u6ca1\u606f\u7684\u4fbf\u4e86\u4e86\u8d26\u3002 \u3000\u3000\u4f46\u8fd9\u4e5f\u8fd8\u4e0d\u7b97\u5f88\u53ef\u6015\u3002 \u3000\u3000\u53ef\u6015\u7684\u662f\uff0c\u8fd9\u4e9b\u4eba\u6b7b\u65f6\u7684\u60e8\u72b6\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u8138\u6216\u8eab\u4f53\uff0c\u88ab\u4eba\u523a\u622e\u5f97\u8840\u8089\u6a21\u7cca\uff0c\u5728\u672a\u6b7b\u524d\u66fe\u53d7\u8fc7\u6781\u5927\u7684\u75db\u82e6\uff0c\u8138\u5bb9\u90fd\u4e3a\u4e4b\u626d\u66f2\uff0c\u773c\u795e\u90fd\u6d41\u9732\u51fa\u6050\u60e7\u5df2\u6781\u3001\u75db\u82e6\u5df2\u6781\u7684\u795e\u8272\uff0c\u516b\u4eba\u4e2d\u7adf\u6ca1\u6709\u4e00\u4e2a\u4eba\u6b7b\u5f97\u7791\u76ee\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u8eab\u53d7\u90a3\u4e48\u53ef\u6015\u7684\u75db\u82e6\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u4eba\u53eb\u558a\u5f97\u51fa\u58f0\u97f3\u6765\uff1f\uff01 \u3000\u3000\u5bf9\u7b80\u8fc5\u800c\u8a00\uff0c\u8fd9\u4e9b\u90fd\u8fd8\u4e0d\u662f\u6700\u53ef\u6015\u7684\u3002 \u3000\u3000\u771f\u6b63\u53ef\u6015\u7684\u662f\u8fd9\u516b\u4e2a\u4eba\uff0c\u662f\u88ab\u4eba\u201c\u6254\u201d\u8fc7\u6765\u7684\uff0c\u50cf\u7834\u8863\u670d\u4e00\u822c\u5730\u968f\u624b\u201c\u6254\u201d\u5728\u5730\u4e0a\uff0c\u53ef\u662f\u6765\u7684\u53ea\u662f\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u4eba\u628a\u516b\u4e2a\u6b7b\u4eba\u201c\u6254\u201d\u8fc7\u6765\uff0c\u7adf\u597d\u50cf\u6bd4\u6254\u6389\u516b\u7c92\u81ed\u82f9\u679c\u8fd8\u8f7b\u6613\uff01 \u3000\u3000\u7b80\u8fc5\u8fce\u7740\u6708\u8272\u3002 \u3000\u3000\u8fd9\u4eba\u80cc\u7740\u6708\u5149\u3002 \u3000\u3000\u7b80\u8fc5\u770b\u4e0d\u89c1\u6765\u4eba\u7684\u8138\u5b54\u3002 \u3000\u3000\u82b1\u6cbe\u5507\u5374\u770b\u5f97\u89c1\u3002 \u3000\u3000\u5979\u7684\u5507\u5df2\u89c9\u5f97\u6709\u4e9b\u53d1\u5e72\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u8fd1\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u6ca1\u6709\u7709\u6bdb\u3002 \u3000\u3000\u8fd9\u4e2a\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\uff0c\u5374\u6709\u4e24\u6487\u80e1\u5b50\u3002 \u3000\u3000\u4e24\u6487\u5f88\u597d\u770b\u7684\u80e1\u5b50\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u80e1\u5b50\u5230\u4e86\u5507\u8fb9\u89d2\u4e0a\uff0c\u7a81\u7136\u5c11\u4e86\u4e00\u5c0f\u64ae\uff0c\u50cf\u5728\u9ed1\u8349\u4e1b\u4e2d\u5272\u5f00\u4e86\u4e00\u9053\u767d\u6c9f\u5b50\u3002 \u3000\u3000\u4e24\u8fb9\u90fd\u5982\u662f\u3002 \u3000\u3000\u8fd9\u4eba\u6ca1\u6709\u7709\u6bdb\uff0c\u5374\u6709\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u6b63\u843d\u5728\u82b1\u6cbe\u5507\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u5bf9\u82b1\u6cbe\u5507\u7684\u8138\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u53ea\u770b\u4e00\u773c\uff0c\u4ed6\u7acb\u5373\u5c31\u6709\u4e86\u5174\u8da3\uff0c\u7b2c\u4e8c\u773c\u4fbf\u662f\u770b\u5979\u7684\u80f8\u812f\uff0c\u7b2c\u4e09\u773c\u4fbf\u770b\u5979\u7684\u5c0f\u8179\u3002 \u3000\u3000\u4ed6\u773c\u91cc\u7684\u795e\u8272\u5c31\u50cf\u82b1\u6cbe\u5507\u4e0d\u66fe\u7a7f\u4e0a\u8863\u670d\u3002 \u3000\u3000\u82b1\u6cbe\u5507\u53ea\u89c9\u5f97\u88ab\u4ed6\u671b\u8fc7\u4e4b\u5904\uff0c\u5c31\u4f3c\u722c\u6ee1\u4e86\u5c0f\u866b\u5b50\uff0c\u6068\u4e0d\u5f97\u628a\u51e1\u88ab\u4ed6\u770b\u8fc7\u4e4b\u5904\u5168\u8981\u6d17\u6da4\u8fc7\u3002 \u3000\u3000\u8fd9\u4eba\u53ea\u770b\u4e86\u4e09\u773c\uff0c\u4fbf\u4e0d\u518d\u770b\u82b1\u6cbe\u5507\u3002 \u3000\u3000\u4eff\u4f5b\u8fd9\u5df2\u662f\u4ed6\u7684\u5973\u4eba\uff0c\u4ed6\u968f\u65f6\u53ef\u4ee5\u518d\u770b\u5230\u5979\uff0c\u800c\u4e14\u968f\u5730\u600e\u6837\u770b\u90fd\u53ef\u4ee5\uff0c\u4ed6\u5927\u53ef\u4ee5\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002 \u3000\u3000\u7136\u540e\u4ed6\u770b\u5411\u7b80\u8fc5\u3002 \u3000\u3000\u7b80\u8fc5\u4e5f\u5728\u770b\u4ed6\u3002 \u3000\u3000\u770b\u4ed6\u624b\u4e0a\u7684\u53c9\u5b50\u3002 \u3000\u3000\u770b\u5230\u8fd9\u67c4\u53c9\u5b50\uff0c\u7b80\u8fc5\u4fbf\u60f3\u5230\u90a3\u516b\u4e2a\u4eba\u8840\u8089\u6a21\u7cca\uff0c\u9aa8\u88c2\u808c\u6380\u7684\u4f24\u53e3\uff0c\u7b80\u8fc5\u89c9\u5f97\u5589\u54bd\u4e5f\u6709\u4e9b\u53d1\u5e72\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u95ee\u8bdd\u7684\u58f0\u97f3\u6709\u4e9b\u53d1\u786c\uff1a\u201c\u9601\u4e0b\u5c31\u662f\u65ad\u7709\u77f3\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u548c\u4ed6\uff0c\u201d\u4ed6\u6307\u4e86\u6307\u5730\u4e0a\u7684\u6d2a\u4e09\u70ed\uff0c\u201c\u90fd\u975e\u6b7b\u4e0d\u53ef\uff0c\u8fd9\u4e24\u4e2a\u5973\u7684\uff0c\u6211\u90fd\u8981\u5e26\u56de\u53bb\u3002\u201d\u4ed6\u8fd9\u6837\u8bf4\u7684\u65f6\u5019\uff0c\u4eff\u4f5b\u5728\u573a\u56db\u4e2a\u4eba\uff0c\u90fd\u4f1a\u63a5\u53d7\u4ed6\u7684\u5b89\u6392\u800c\u6beb\u65e0\u5f02\u8bae\u4f3c\u7684\u3002 \u3000\u3000\u7b80\u8fc5\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u660e\u5929\u624d\u8fdb\u6d1b\u9633\u57ce\u7684\u5417\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u4f60\u4eec\u4eba\u4eba\u90fd\u4ee5\u4e3a\u6211\u660e\u5929\u624d\u6765\uff0c\u6240\u4ee5\u6211\u4eca\u665a\u5c31\u5230\uff0c\u4e00\u4e2a\u4eba\u65e9\u5230\u4e00\u4e9b\uff0c\u770b\u5230\u7684\u4e8b\u60c5\uff0c\u603b\u4f1a\u6bd4\u522b\u4eba\u591a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u7b80\u8fc5\u627f\u8ba4\u65ad\u7709\u77f3\u8bf4\u5f97\u5f88\u6709\u9053\u7406\u3002\u4e00\u4e2a\u4eba\u82e5\u8fdf\u4e00\u4e9b\u6216\u65e9\u4e00\u4e9b\uff0c\u90fd\u4f1a\u6709\u4e00\u4e9b\u4e8b\u662f\u610f\u60f3\u4e0d\u5230\u7684\uff0c\u4e00\u4e2a\u6bcf\u6b21\u662f\u6070\u6070\u597d\u7684\u4eba\uff0c\u53ea\u542c\u4ed6\u8be5\u542c\u7684\uff0c\u53ea\u95fb\u4ed6\u8be5\u95fb\u7684\uff0c\u53ea\u770b\u4ed6\u8be5\u770b\u7684\uff0c\u4e5f\u8bb8\u80fd\u591f\u65e0\u5fe7\u65e0\u8651\uff0c\u4f46\u6c38\u8fdc\u65e0\u60ca\u65e0\u559c\u3002 \u3000\u3000\u7b80\u8fc5\u53ea\u597d\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6765\u4e86\uff0c\u4f55\u4e0d\u4e5f\u5230\u5c0f\u78a7\u6e56\u53bb\u4e00\u8d9f\uff0c\u4ee5\u4f60\u7684\u5927\u624d\uff0c\u6e38\u516c\u5b50\u5fc5\u4e88\u91cd\u4efb\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u4f60\u8fd9\u53e5\u8bdd\uff0c\u4e3a\u751a\u4e48\u4e0d\u65e9\u4e09\u4e2a\u6708\u8bf4\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u4e0d\u89e3\uff1a\u201c\u4e09\u4e2a\u6708\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u4e09\u4e2a\u6708\u524d\uff0c\u5999\u624b\u5802\u5df2\u96c7\u7528\u4e86\u6211\uff0c\u4ed6\u4eec\u51fa\u7684\u94f6\u5b50\uff0c\u53ef\u4f9b\u6211\u6325\u970d\u4e8c\u5341\u4e2a\u6708\u3002\u201d \u3000\u3000\u7b80\u8fc5\u9a6c\u4e0a\u9053\uff1a\u201c\u4f60\u8981\u662f\u89c1\u7740\u6e38\u516c\u5b50\uff0c\u4ed6\u53ef\u80fd\u51fa\u5f97\u8d77\u4e00\u500d\u7684\u4ef7\u94b1\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u6325\u970d\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d\u65ad\u7709\u77f3\u9053\uff1a\u201c\u6325\u970d\u4e0d\u6b62\u662f\u82b1\u3001\u4e5f\u4e0d\u53ea\u662f\u6d6a\u8d39\uff0c\u5c31\u7b97\u662f\u4e00\u4e2a\u4eba\u6316\u5230\u4e86\u91d1\u77ff\uff0c\u4e5f\u7981\u4e0d\u4f4f\u4ed6\u6beb\u65e0\u8282\u5236\u7684\u6325\u970d\uff0c\u6e38\u516c\u5b50\u8bf7\u5f97\u8d77\u6211\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u8bf7\u5f97\u8d77\uff0c\u201d\u7b80\u8fc5\u8138\u4e0a\u53c8\u6709\u4e86\u7b11\u5bb9\uff0c\u201c\u6e38\u516c\u5b50\u5bb6\u8d40\u4e07\u8d2f\uff0c\u800c\u4e14\u51fa\u624b\u4e00\u5411\u5927\u65b9\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u4f3c\u4e4e\u6709\u4e9b\u52a8\u5bb9\u3002 \u3000\u3000\u201c\u76f8\u8bf7\u4e0d\u5982\u5076\u9047\u3002\u201d\u7b80\u8fc5\u9053\uff0c\u201c\u4e0d\u5982\u8bf7\u5c0a\u9a7e\u4e5f\u5230\u5c0f\u78a7\u6e56\u53bb\u4e00\u8d9f\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u6765\u6d1b\u9633\uff0c\u4f60\u5c31\u8981\u6211\u80cc\u53db\u5999\u624b\u5802\uff1f\u201d\u65ad\u7709\u77f3\u6709\u4e9b\u72b9\u8c6b\u3002 \u3000\u3000\u7b80\u8fc5\u4e00\u9762\u62fe\u7ea7\u800c\u4e0b\uff0c\u4e00\u9762\u9053\uff1a\u201c\u96be\u9053\u4f60\u8981\u8fdb\u4e86\u5999\u624b\u5802\uff0c\u624d\u5f00\u59cb\u80cc\u53db\u4e0d\u6210\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u53cd\u95ee\u9053\uff1a\u201c\u6211\u600e\u80fd\u76f8\u4fe1\u4f60\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u5df2\u8d70\u4e0b\u77f3\u9636\uff0c\u201c\u4f60\u5c31\u7b97\u4fe1\u9519\u6211\uff0c\u5bf9\u4f60\u4e5f\u6ca1\u6709\u751a\u4e48\u635f\u5931\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5982\u679c\u6211\u4e00\u8fdb\u5c0f\u78a7\u6e56\uff0c\u4f60\u4eec\u5c31\u56f4\u6740\u6211\uff0c\u6211\u5c82\u4e0d\u662f\u6b7b\u8def\u4e00\u6761\uff1f\u201d \u3000\u3000\u7b80\u8fc5\u5728\u4ed6\u4e03\u5c3a\u4e4b\u5916\uff0c\u7ad9\u5b9a\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u4fe1\u6211\u4e00\u6b21\u3002\u201d \u3000\u3000\u989c\u5915\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u53bb\u5170\u4ead\u6c60\u5bb6\uff0c\u6211\u4eec\u4e00\u6837\u4f1a\u91cd\u7528\u4f60\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u8fde\u5934\u4e5f\u4e0d\u56de\uff1a\u201c\u4f60\u4eec\u6c60\u5bb6\u65e2\u6ca1\u6709\u94b1\u8d22\uff0c\u4e5f\u6ca1\u6709\u4eba\u624d\u3002\u201d \u3000\u3000\u989c\u5915\u6c14\u5f97\u7c89\u8138\u53d1\u5bd2\uff0c\u6012\u9053\uff1a\u201c\u4f60\u6562\u77a7\u4e0d\u8d77\u6c60\u5bb6\uff01\u201d \u3000\u3000\u65ad\u7709\u77f3\u60a0\u7136\u9053\uff1a\u201c\u6211\u5fc3\u76ee\u4e2d\u6839\u672c\u5c31\u6ca1\u6709\u5170\u4ead\u6c60\u5bb6\u3002\u201d\u4ed6\u51b7\u51b7\u5730\u63a5\u9053\uff0c\u201c\u6d1b\u9633\u57ce\u91cc\uff0c\u53ea\u6709\u5999\u624b\u5802\u56de\u5bb6\u548c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u3002\u201d \u3000\u3000\u989c\u5915\u53ea\u89c9\u6c60\u5bb6\u53d7\u8fb1\uff0c\u65e0\u8bba\u5982\u4f55\u5979\u90fd\u8981\u633a\u8eab\u7ef4\u62a4\uff0c\u5fff\u5fff\u5730\u9053\uff1a\u201c\u72d7\u773c\u770b\u4eba\u4f4e\uff01\u201d \u3000\u3000\u65ad\u7709\u77f3\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u7b11\u7684\u65f6\u5019\uff0c\u989d\u4e0a\u7adf\u9690\u73b0\u4e86\u4e00\u5bf9\u7709\u6bdb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\uff0c\u4ed6\u7a81\u7136\u6251\u5411\u989c\u5915\u3002 \u3000\u3000\u4ed6\u624b\u4e0a\u7684\u53c9\u5b50\uff0c\u76f4\u53d6\u989c\u5915\u7684\u5bb9\u989c\uff0c\u4eff\u4f5b\u8981\u628a\u8fd9\u82b1\u5bb9\u6708\u8c8c\u6363\u6bc1\u624d\u79f0\u610f\u3002 \u3000\u3000\u7b80\u8fc5\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u98de\u63a0\u800c\u51fa\uff0c\u8d76\u5728\u4e24\u4eba\u4e4b\u95f4\uff0c\u4f5c\u52bf\u4e00\u62e6\uff0c\u6025\u53eb\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bdd\uff0c\u5148\u522b\u52a8\u624b\u2014\u2014\u201d \u3000\u3000\u4ed6\u624d\u53eb\u51fa\u8fd9\u51e0\u4e2a\u5b57\uff0c\u4fbf\u77e5\u9053\u81ea\u5df1\u9519\u4e86\u3002 \u3000\u3000\u5f7b\u5e95\u7684\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u72af\u4e0a\u4e86\u4e2a\u4e0d\u53ef\u9976\u6055\u7684\u9519\u8bef\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u9a6c\u4e0a\u53d1\u73b0\uff0c\u65ad\u7709\u77f3\u7684\u76ee\u6807\u6839\u672c\u4e0d\u662f\u989c\u5915\u3002 \u3000\u3000\u800c\u662f\u4ed6\u3002"}),showOnHomepage:!0},{name:" \u7b2c\u5341\u56db\u7ae0\u3000\u82b1\u523a",content:(0,t.jsx)(t.Fragment,{children:"\u4ed6\u53d1\u73b0\u7684\u65f6\u5019\u5df2\u7ecf\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u4e0d\u662f\u65ad\u7709\u77f3\u4e4b\u654c\uff0c\u66f4\u4f55\u51b5\u4e00\u4e0a\u6765\u4ed6\u5c31\u5b8c\u5168\u5931\u4e86\u5148\u624b\u3002 \u3000\u3000\u65ad\u7709\u77f3\u7684\u6b66\u529f\u66f4\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\uff0c\u4e0d\u4f46\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u8981\u9ad8\uff0c\u5e76\u4e14\u9ad8\u51fa\u8bb8\u591a\u3002 \u3000\u3000\u65ad\u7709\u77f3\u624d\u8ddf\u4ed6\u5bf9\u4e86\u4e00\u638c\uff0c\u7b80\u8fc5\u8fd8\u6765\u4e0d\u53ca\u8fd0\u52b2\u53d1\u7b2c\u4e8c\u638c\uff0c\u7a81\u7136\u611f\u89c9\u5230\u8eab\u4e0a\u51e0\u5904\u7a74\u9053\u5df2\u88ab\u5c01\u5236\uff0c\u5305\u62ec\u54d1\u7a74\u3002 \u3000\u3000\u800c\u5bf9\u65b9\u53ea\u4e0d\u8fc7\u5728\u4ed6\u81c2\u4e0a\u6cbe\u4e86\u4e00\u6cbe\u800c\u5df2\u3002 \u3000\u3000\u65ad\u7709\u77f3\u7adf\u7136\u53ef\u4ee5\u4e0d\u900f\u8fc7\u6253\u7a74\u70b9\u7a74\uff0c\u800c\u53ea\u8981\u89e6\u53ca\u654c\u624b\u8eab\u4e0a\u4efb\u4f55\u4e00\u5904\uff0c\u5c31\u53ef\u4ee5\u5185\u52b2\u900f\u5165\u5bf9\u65b9\u4f53\u5185\uff0c\u9006\u5c01\u654c\u624b\u7684\u7a74\u9053\u3002 \u3000\u3000\u800c\u4e14\uff0c\u8fd8\u968f\u4ed6\u559c\u6b22\u5c01\u54ea\u4e00\u4e2a\u7a74\u9053\u3002 \u3000\u3000\u7b80\u8fc5\u8ddf\u65ad\u7709\u77f3\u624d\u52a8\u4e0a\u624b\uff0c\u82b1\u6cbe\u5507\u5df2\u63a0\u4e86\u8fc7\u6765\uff0c\u5979\u7684\u5175\u5668\u53eb\u505a\u201c\u82b1\u523a\uff0c\u201d\u770b\u6765\u5f88\u67d4\u5f31\uff0c\u4f7f\u7528\u65f6\u8fd8\u5e26\u7740\u4e00\u80a1\u751c\u9999\uff0c\u4f46\u53ea\u8981\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u7ed9\u5b83\u523a\u4e86\u4e00\u4e0b\uff0c\u624b\u4e0a\u7acb\u5373\u5c31\u4f1a\u51fa\u8840\uff0c\u4e0d\u7ba1\u523a\u7684\u5b54\u6709\u591a\u5c0f\uff0c\u90fd\u4f1a\u6d41\u8840\u4e0d\u6b62\uff0c\u800c\u4e14\u4f24\u53e3\u4f1a\u4e0d\u4f4f\u7684\u6269\u5927\uff0c\u76f4\u81f3\u8840\u6d41\u5e72\u4e3a\u6b62\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u8840\u6d41\u5149\u4e86\uff0c\u81ea\u7136\u5c31\u6d3b\u4e0d\u6210\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u4e9b\u5e74\u6765\uff0c\u5728\u82b1\u523a\u4e0b\u6b7b\uff0c\u505a\u9b3c\u4e5f\u83ab\u660e\u7684\u201c\u6b66\u6797\u9ad8\u624b\u201d\uff0c\u5b9e\u5728\u5c31\u50cf\u8ffd\u6c42\u82b1\u6cbe\u5507\u7684\u7537\u5b50\u90a3\u4e48\u591a\u3002 \u3000\u3000\u6bcf\u5929\u603b\u4f1a\u6709\u51e0\u5f20\u65b0\u8138\u5b54\uff0c\u4f46\u540c\u6837\u6000\u7740\u4e00\u4e2a\u81ea\u547d\u98ce\u6d41\u7684\u5fc3\uff0c\u6765\u8ffd\u6c42\u8fd9\u4f4d\u5341\u5206\u68d8\u624b\u7684\u82b1\u6cbe\u5507\u3002 \u3000\u3000\u7537\u4eba\u5c31\u662f\u8fd9\u6837\uff0c\u8d8a\u662f\u4e0d\u80fd\u6cbe\u624b\u7684\u5973\u4eba\uff0c\u8d8a\u662f\u60f3\u6cbe\uff0c\u4e00\u65e6\u6cbe\u4e0a\u4e86\uff0c\u53c8\u5fd9\u4e0d\u8fed\u628a\u5979\u7529\u8131\u3002 \u3000\u3000\u6240\u4ee5\u82b1\u6cbe\u5507\u4e00\u5411\u4e0d\u559c\u6b22\u4eba\u6cbe\u3002 \u3000\u3000\u5979\u53ea\u559c\u6b22\u523a\u4eba\u3002 \u3000\u3000\u7528\u5979\u90a3\u679d\u540d\u6ee1\u6b66\u6797\u7684\u201c\u82b1\u523a\u201d\u3002 \u3000\u3000\u53ef\u662f\u5979\u7684\u201c\u82b1\u523a\u201d\u624d\u521a\u51fa\u624b\uff0c\u7b80\u8fc5\u5df2\u7ecf\u5012\u5730\u3002 \u3000\u3000\u989c\u5915\u4e5f\u5728\u8fd9\u9877\u523b\u95f4\uff0c\u53d1\u73b0\u65ad\u7709\u77f3\u662f\u4ed6\u4eec\u5171\u540c\u7684\u654c\u4eba\uff1a \u3000\u3000\u2014\u2014\u5b81\u613f\u843d\u5728\u5c0f\u78a7\u6e56\u6e38\u5bb6\u7684\u624b\uff0c\u4e5f\u4e0d\u80fd\u843d\u5728\u8fd9\u4eba\u624b\u91cc\uff01 \u3000\u3000\u2014\u2014\u843d\u5728\u8fd9\u4eba\u624b\u91cc\uff0c\u6050\u6015\u6bd4\u6b7b\u8fd8\u4e0d\u5982\uff01 \u3000\u3000\u5979\u4e5f\u7acb\u523b\u51fa\u5251\uff0c\u5408\u653b\u65ad\u7709\u77f3\u3002 \u3000\u3000\u65ad\u7709\u77f3\u957f\u7b11\uff0c\u4e00\u9762\u7b11\u4e00\u9762\u6325\u53c9\uff0c\u8fb9\u6253\u8fb9\u8dd1\uff0c\u8eab\u6cd5\u8be1\u5947\u5df2\u6781\u3002 \u3000\u3000\u989c\u5915\u523a\u4e86\u5341\u56db\u5251\uff0c\u5251\u5251\u843d\u7a7a\u3002 \u3000\u3000\u82b1\u6cbe\u5507\u7684\u201c\u82b1\u523a\u201d\u8fde\u5bf9\u65b9\u7684\u8863\u895f\u90fd\u6cbe\u4e0d\u4e0a\u3002 \u3000\u3000\u7136\u540e\u5979\u4eec\u540c\u65f6\u90fd\u53d1\u73b0\uff0c\u90a3\u5269\u4e0b\u7684\u56db\u540d\u62ac\u8f7f\u4eba\uff0c\u4e5f\u90fd\u5012\u4e86\u4e0b\u53bb\uff0c\u4e0d\u8fc7\u90fd\u6ca1\u6709\u6b7b\uff0c\u8ddf\u7b80\u8fc5\u4e00\u6837\uff0c\u4e5f\u662f\u7a74\u9053\u53d7\u5236\u3002 \u3000\u3000\u65ad\u7709\u77f3\u5728\u8eb2\u907f\u5979\u4eec\u653b\u51fb\u7684\u5f53\u513f\uff0c\u201c\u987a\u4fbf\u201d\u5236\u4f4f\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u65ad\u7709\u77f3\u4e0d\u8dd1\u4e86\uff0c\u8eab\u5f62\u987f\u4f4f\uff0c\u4e5f\u4e0d\u56de\u8eab\u3002 \u3000\u3000\u82b1\u6cbe\u5507\u4e00\u54ac\u4e0b\u5507\uff0c\u624b\u4e2d\u201c\u82b1\u523a\u201d\uff0c\u75be\u523a\u8fc7\u53bb\uff01 \u3000\u3000\u65ad\u7709\u77f3\u731b\u56de\u8eab\uff0c\u5927\u559d\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u53c9\u6467\u51fa\uff01 \u3000\u3000\u8fd9\u4e00\u53c9\u8131\u624b\u98de\u51fa\uff0c\u58f0\u52bf\u9a87\u4eba\uff01 \u3000\u3000\u82b1\u6cbe\u5507\u624b\u4e2d\u523a\u79bb\u65ad\u7709\u77f3\u5c1a\u6709\u4e09\u5c3a\u8fdc\uff0c\u53c9\u5df2\u53ca\u9888\uff0c\u82b1\u6cbe\u5507\u552f\u6709\u98de\u9000\uff01 \u3000\u3000\u5979\u9000\u5f97\u5feb\uff0c\u53c9\u4e5f\u8ffd\u5f97\u5feb\uff01 \u3000\u3000\u82b1\u6cbe\u5507\u5168\u529b\u6025\u9000\uff0c\u5979\u5df2\u903c\u51fa\u4e86\u751f\u547d\u91cc\u6240\u6709\u7684\u6f5c\u80fd\u3002 \u3000\u3000\u989c\u5915\u5374\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u770b\u89c1\u4e86\u67f1\u5b50\u3002 \u3000\u3000\u770b\u89c1\u67f1\u5b50\u81ea\u7136\u6ca1\u751a\u4e48\u597d\u60ca\u614c\u7684\uff0c\u67f1\u5b50\u53c8\u4e0d\u4f1a\u6740\u4eba\uff0c\u4f46\u53ef\u60ca\u7684\u662f\uff0c\u65ad\u7709\u77f3\u56de\u8eab\u6467\u53c9\uff0c\u50cf\u4e00\u65e9\u5df2\u7b97\u51c6\u82b1\u6cbe\u5507\u7684\u9000\u8def\u4f3c\u7684\uff0c\u82b1\u6cbe\u5507\u60c5\u6025\u4e2d\u5168\u529b\u9000\u907f\uff0c\u6b63\u597d\u80cc\u5411\u724c\u697c\u7684\u77f3\u67f1\u5012\u63a0\u800c\u53bb\uff01 \u3000\u3000\u82b1\u6cbe\u5507\u53d1\u73b0\u65f6\uff0c\u80cc\u90e8\u5df2\u649e\u4e0a\u4e86\u77f3\u67f1\uff01 \u3000\u3000\u5979\u521a\u628a\u731b\u649e\u4e4b\u529b\u5378\u53bb\u4e00\u534a\uff0c\u94a2\u53c9\u5df2\u81f3\uff0c\u5979\u518d\u4e5f\u6765\u4e0d\u53ca\u95ea\uff0c\u4e5f\u4e0d\u53ca\u8eb2\uff0c\u66f4\u6765\u4e0d\u53ca\u907f\uff01 \u3000\u3000\u6240\u4ee5\u5979\u53ea\u6709\u6b7b\u3002 \u3000\u3000\u8fde\u5979\u81ea\u5df1\u4e5f\u60f3\u4e0d\u5230\uff1b\u81ea\u5df1\u7adf\u4f1a\u8fd9\u4e48\u5feb\u5c31\u6b7b\uff0c\u7adf\u4f1a\u6b7b\u5f97\u8fd9\u4e48\u5feb\uff01 \u3000\u3000\u5979\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u5979\u95ed\u4e0a\u4e86\u773c\uff0c\u4e5f\u53ef\u4ee5\u611f\u89c9\u5230\u8138\u988a\u4e00\u9635\u75d2\u75d2\uff0c\u60f3\u5fc5\u662f\u94a2\u53c9\u9489\u5165\u77f3\u67f1\u65f6\u6240\u4ea4\u8ff8\u7684\u661f\u706b\uff0c\u6cbe\u4e0a\u4e86\u5979\u7684\u8273\u9765\u3002 \u3000\u3000\u5979\u7741\u5f00\u773c\uff0c\u94a2\u53c9\u5c31\u53c9\u4f4f\u81ea\u5df1\u7684\u8116\u5b50\uff0c\u9489\u5165\u67f1\u4e2d\uff0c\u53c9\u950b\u79bb\u81ea\u5df1\u7684\u53cc\u988a\u548c\u9888\u4fa7\uff0c\u51b3\u4e0d\u5230\u534a\u5206\uff0c\u5c31\u53ea\u5dee\u8fd9\u534a\u5206\uff0c\u6240\u4ee5\u81ea\u5df1\u624d\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u5979\u6b63\u60f3\u62d4\u53c9\uff0c\u5ffd\u542c\u65ad\u7709\u77f3\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u4e3a\u751a\u4e48\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u82b1\u6cbe\u5507\u53d1\u89c9\u4e86\u4e00\u4e2a\u4e8b\u5b9e\u3002 \u3000\u3000\u5f88\u53ef\u6015\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u65ad\u7709\u77f3\u539f\u6765\u5c31\u7ad9\u5728\u67f1\u5b50\u7684\u540e\u9762\uff0c\u4ed6\u8bf4\u8bdd\u65f6\u7684\u53e3\u6c14\uff0c\u751a\u81f3\u53ef\u4ee5\u5439\u8d77\u81ea\u5df1\u7684\u53d1\u9b13\uff0c\u8fd8\u5e26\u4e00\u80a1\u8165\u5473\u3002 \u3000\u3000\u82b1\u6cbe\u5507\u89c9\u5f97\u6bd4\u6b7b\u8fd8\u96be\u53d7\u3002 \u3000\u3000\u5979\u4e5f\u7acb\u65f6\u660e\u767d\u4e86\u81ea\u5df1\u8fd8\u6ca1\u6709\u6b7b\u7684\u539f\u56e0\u3002 \u3000\u3000\u2014\u2014\u65ad\u7709\u77f3\u6839\u672c\u4e0d\u60f3\u8ba9\u5979\u6b7b\u3002 \u3000\u3000\u90a3\u662f\u6bd4\u6b7b\u8fd8\u53ef\u6015\u7684\u4e8b\u60c5\uff01 \u3000\u3000\u82b1\u6cbe\u5507\u6b63\u60f3\u8981\u4e0d\u8981\u81ea\u5c3d\u7684\u65f6\u5019\uff0c\u53ea\u89c9\u8170\u95f4\u4e00\u9ebb\uff0c\u5979\u8eab\u4e0a\u7684\u54d1\u7a74\u548c\u4e94\u5904\u7a74\u9053\uff0c\u90fd\u5df2\u88ab\u5c01\u3002 \u3000\u3000\u7136\u540e\uff0c\u65ad\u7709\u77f3\u81ea\u67f1\u540e\u6162\u6162\u8e31\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u6162\u6761\u65af\u7406\u7684\u62d4\u4e0b\u4e86\u94a2\u53c9\uff0c\u7528\u624b\u5f39\u4e86\u5f39\u53c9\u950b\uff0c\u7136\u540e\u95ee\u989c\u5915\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u6ca1\u6709\u8d70\uff1f\u201d \u3000\u3000\u989c\u5915\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u770b\u89c1\u7b80\u8fc5\u53d7\u5236\uff0c\u82b1\u6cbe\u5507\u88ab\u64d2\uff0c\u77e5\u9053\u8fd9\u4e24\u4eba\u7684\u906d\u9047\u5c06\u8981\u6bd4\u6b7b\u8fd8\u60b2\u60e8\uff0c\u8fd9\u5239\u90a3\u5979\u60f3\u8d70\uff0c\u53ef\u662f\u53c8\u4e0d\u5fcd\u8d70\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u8bb2\u6c42\u201c\u4fa0\u4e49\u201d\u4e8c\u5b57\uff0c\u6709\u4e9b\u4e8b\uff0c\u662f\u975e\u505a\u4e0d\u53ef\u7684\uff0c\u6709\u4e9b\u4e8b\uff0c\u5374\u662f\u51b3\u4e0d\u80fd\u4e3a\u7684\uff0c\u5c31\u7b97\u5979\u662f\u5973\u5b50\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u6240\u4ee5\u989c\u5915\u6697\u5438\u4e00\u53e3\u6c14\uff0c\u9762\u5bf9\u8fd9\u9b54\u9b3c\u4e00\u6837\u7684\u4eba\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5f88\u60f3\u6211\u8d70\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u4ecd\u5728\u770b\u4ed6\u7684\u53c9\u950b\uff0c\u53ea\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u559c\u6b22\u770b\u730e\u7269\u9003\u8dd1\uff0c\u4f60\u518d\u53bb\u628a\u5b83\u6293\u56de\u6765\uff0c\u6162\u6162\u5f04\u6b7b\uff0c\u8fd9\u624d\u80fd\u4f7f\u4f60\u6ee1\u610f\uff0c\u201d\u989c\u5915\u7684\u773c\u795e\u548c\u8bed\u6c14\u8981\u6bd4\u624b\u4e0a\u7684\u5251\u950b\u66f4\u6709\u5251\u6c14\uff0c\u201c\u5c31\u50cf\u732b\u6293\u8017\u5b50\u4e00\u6837\u3002\u201d \u3000\u3000\u7136\u540e\u5979\u6602\u7136\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8017\u5b50\u3002\u53cd\u6b63\u6211\u9003\u4e0d\u4e86\u3002\u6211\u4e0d\u9003\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u6211\uff1f\u201d\u4ed6\u8fd9\u624d\u62ac\u5934\uff0c\u7b2c\u4e00\u6b21\u8ddf\u989c\u5915\u7167\u9762\u3002 \u3000\u3000\u8fd9\u4e00\u671b\u4e4b\u4e0b\uff0c\u4ed6\u7684\u773c\u775b\u4f3c\u88ab\u5438\u4f4f\u4e86\uff0c\u518d\u4e5f\u79fb\u4e0d\u5f00\u3001\u632a\u4e0d\u6389\u3001\u6536\u4e0d\u56de\u6765\u4e86\u3002 \u3000\u3000\u5bf9\u65ad\u7709\u77f3\u800c\u8a00\uff0c\u8fd9\u7edd\u975e\u662f\u60ca\u8273\u3002 \u3000\u3000\u56e0\u4e3a\u989c\u5915\u6e05\u800c\u4e0d\u8273\u3002 \u3000\u3000\u5979\u5728\u6e05\u4e3d\u8131\u4fd7\u4e2d\u53c8\u8ba9\u4eba\u611f\u5230\u5fc3\u6298\uff0c\u5fcd\u4e0d\u4f4f\u751f\u8d77\u4e00\u79cd\u8fd1\u4e4e\u8654\u8bda\u7684\u5d07\u4ef0\uff0c\u4f46\u53c8\u53d1\u81ea\u5fc3\u5e95\u7684\u601c\u9999\u60dc\u7389\u3002 \u3000\u3000\u4ed6\u770b\u82b1\u6cbe\u5507\u7684\u65f6\u5019\uff0c\u662f\u4e00\u4e2a\u7537\u4eba\uff0c\u5728\u770b\u4e00\u4e2a\u5973\u4eba\uff0c\u4f46\u4ed6\u770b\u989c\u5915\u7684\u65f6\u5019\uff0c\u5374\u4f3c\u662f\u4e00\u4e2a\u5c11\u7537\uff0c\u5728\u770b\u4ed6\u6240\u4ef0\u6155\u7684\u5973\u5b50\u3002 \u3000\u3000\u8c01\u90fd\u77e5\u9053\u65ad\u7709\u77f3\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u6740\u4e00\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u7edd\u5bf9\u4f7f\u5bf9\u65b9\u53ea\u6c42\u6b7b\u5f97\u5feb\u4e00\u4e9b\uff1b\u4ed6\u5bf9\u4ed8\u4e00\u4e2a\u7537\u4eba\u7684\u65f6\u5019\uff0c\u80af\u5b9a\u53ef\u4ee5\u4f7f\u4ed6\u540e\u6094\u4e3a\u751a\u4e48\u8981\u751f\u4e0b\u6765\uff1b\u4ed6\u6298\u78e8\u4e00\u4e2a\u5973\u4eba\u7684\u65f6\u5019\uff0c\u5b8c\u5168\u53ef\u4ee5\u4f7f\u5979\u6068\u81ea\u5df1\u4e3a\u751a\u4e48\u662f\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u8fd9\u79cd\u4eba\u53ea\u6709\u517d\u6027\uff0c\u6ca1\u6709\u601c\u60af\u3002 \u3000\u3000\u53ef\u662f\u65ad\u7709\u77f3\u73b0\u5728\u4eff\u4f5b\u8fd8\u5f88\u6709\u60c5\u6000\u3002 \u3000\u3000\u201c\u5509\u3002\u201d\u4ed6\u5c45\u7136\u53d1\u51fa\u4e86\u4e00\u58f0\u53f9\u606f\uff1a\u201c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u989c\u5915\u4e0d\u660e\u767d\u4ed6\u8bf4\u751a\u4e48\u3002 \u3000\u3000\u201c\u770b\u6765\u8fd9\u6b21\u6d1b\u9633\u57ce\uff0c\u6211\u6ca1\u6709\u6765\u9519\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4eca\u665a\u6211\u6765\u5927\u9690\u4e18\uff0c\u66f4\u6ca1\u6709\u51b3\u5b9a\u9519\u3002\u201d \u3000\u3000\u989c\u5915\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u5b9a\u5f85\u4f1a\u513f\u5c31\u4f1a\u540e\u6094\uff0c\u8fd9\u51b3\u5b9a\u9519\u5f97\u6709\u591a\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u53d7\u5999\u624b\u5802\u4e4b\u9080\u800c\u6765\u6d1b\u9633\uff0c\u201d\u65ad\u7709\u77f3\u7684\u773c\u775b\u50cf\u9047\u4e0a\u4e86\u78c1\u94c1\uff0c\u770b\u4e86\u989c\u5915\u7b2c\u4e00\u773c\u4e4b\u540e\uff0c\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u4ecd\u662f\u7b2c\u4e00\u773c\uff0c\u56e0\u4e3a\u4e00\u76f4\u79fb\u4e0d\u5f00\u89c6\u7ebf\uff0c\u201c\u6211\u60f3\u672a\u5230\u56de\u5bb6\u4e4b\u524d\uff0c\u5148\u9886\u51e0\u4e2a\u5927\u529f\uff0c\u4fbf\u51b3\u5b9a\u63d0\u524d\u8d76\u6765\u8fd9\u513f\u4e00\u8d9f\u3002\u6211\u4e00\u8def\u8ddf\u8e2a\u8fd9\u6e38\u5bb6\u7684\u8d70\u72d7\uff0c\u4ed6\u4eec\u8fd8\u61f5\u7136\u4e0d\u77e5\u3002\u8fd9\u6b21\uff0c\u6211\u53ef\u662f\u628a\u5c0f\u78a7\u6e56\u7684\u4e24\u5927\u91cd\u5c06\uff1a\u7b80\u8fc5\u548c\u82b1\u6cbe\u5507\uff0c\u4ee5\u53ca\u5170\u4ead\u7684\u4e24\u5927\u8981\u4eba\uff1a\u5927\u592b\u4eba\u548c\u6d2a\u4e09\u70ed\uff0c\u5148\u64d2\u4e86\u56de\u53bb\uff0c\u7136\u540e\u8981\u4f1a\u4e00\u4f1a\u90a3\u4e2a\u5404\u65b9\u4e89\u53d6\u7684\u65b9\u90aa\u771f\u3002\u201d \u3000\u3000\u989c\u5915\u88ab\u4ed6\u770b\u5f97\u5fc3\u5934\u51c9\u98d5\u98d5\u7684\uff0c\u53ea\u89c9\u5934\u4e0a\u4e91\u6d8c\u6708\u79fb\uff0c\u5fc3\u4e2d\u5f88\u6709\u4e9b\u60ca\u60e7\uff0c\u5bd2\u7740\u8bed\u97f3\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4f60\u5f88\u5feb\u5c31\u4f1a\u4f1a\u4e0a\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u6211\u9047\u4e0a\u4f60\u4e86\u3002\u201d\u65ad\u7709\u77f3\u6168\u53f9\u5730\u9053\uff0c\u201c\u6211\u7ec8\u4e8e\u9047\u4e0a\u4f60\u4e86\u3002\u4eba\u8bf4\u5170\u4ead\u6c60\u5927\u592b\u4eba\u4e0d\u662f\u7f8e\u4eba\uff0c\u4f46\u5374\u80fd\u4ee4\u7f8e\u4eba\u90fd\u6298\u670d\u7684\u4e3d\u4eba\uff0c\u8fd9\u53e5\u8bdd\uff0c\u5012\u4ee4\u6211\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u7684\u7ed3\u8bba\u662f\uff1a\u201c\u6240\u4ee5\uff0c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u4f60\u5c31\u662f\u6211\u7684\u5973\u4eba\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u77e5\u9053\u5979\u81ea\u5df1\u6240\u9762\u4e34\u7684\u5c40\u9762\uff0c\u6050\u6015\u8981\u6bd4\u6d2a\u4e09\u70ed\u6240\u9762\u5bf9\u7684\u8fd8\u8981\u51f6\u9669\u5f97\u591a\u4e86\uff0c\u6240\u4ee5\u5979\u5c3d\u7ba1\u5fc3\u4e2d\u60ca\u60e7\uff0c\u4f46\u4ecd\u5f88\u51b7\u9759\u5730\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u610f\u601d\u3002\u4e0d\u8fc7\uff0c\u6211\u8fd8\u5728\u8fd9\u91cc\uff0c\u624b\u91cc\u8fd8\u6709\u5251\uff0c\u4f60\u672a\u5fc5\u80fd\u80dc\u5f97\u4e86\u6211\uff0c\u7eb5\u80dc\u5f97\u8fc7\u6211\uff0c\u6211\u4e5f\u53ef\u4ee5\u6b7b\uff0c\u201d\u989c\u5915\u8138\u82e5\u5bd2\u971c\u5730\u9053\uff0c\u201c\u6240\u4ee5\uff0c\u6211\u4e0d\u662f\u4f60\u7684\u4eba\u3002\u80af\u5b9a\u4e0d\u662f\u3002\u51b3\u4e0d\u662f\u3002\u7edd\u5bf9\u4e0d\u662f\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u671b\u5b9a\u4e86\u5979\uff0c\u597d\u4e00\u4f1a\uff0c\u624d\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u95ee\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4f60\u5b81\u53ef\u6b7b\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u6211\u5f97\u5230\u4f60\uff1f\u201d \u3000\u3000\u989c\u5915\u51b7\u7136\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u4f3c\u4e0b\u4e86\u6781\u5927\u7684\u51b3\u5fc3\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u51e0\u4eba\u6211\u5c31\u90fd\u653e\u4e86\uff0c\u6211\u53ea\u8981\u4f60\uff0c\u4f60\u8ddf\u4e0d\u8ddf\u6211\uff1f\u201d \u3000\u3000\u989c\u5915\u65ad\u7136\u9053\uff1a\u201c\u4e0d\u8ddf\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u60f3\u4e86\u60f3\uff0c\u53c8\u9053\uff1a\u201c\u5982\u679c\u6211\u6218\u5f97\u80dc\u4f60\uff0c\u4f60\u7acb\u523b\u81ea\u7edd\uff0c\u51b3\u4e0d\u8ba9\u6211\u5f97\u624b\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u989c\u5915\u50b2\u7136\u5730\u9053\uff1a\u201c\u4f60\u53ea\u4f1a\u9047\u5230\u4e00\u4e2a\u80dc\u5229\u8005\uff0c\u6216\u8005\u662f\u6b7b\u4eba\uff0c\u51b3\u4e0d\u4f1a\u662f\u4e2a\u6218\u8d25\u7684\u5973\u5b50\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u6df1\u6c89\u5730\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e5f\u522b\u5fd8\u4e86\uff0c\u6211\u8fd8\u662f\u53ef\u4ee5\u5f97\u5230\u4f60\u7684\u5c38\u4f53\uff0c\u4e3a\u6240\u6b32\u4e3a\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u7684\u8bf4\u6cd5\u4ee4\u4eba\u53d1\u6307\uff0c\u8fd9\u53e5\u8bdd\u7684\u5351\u9119\u548c\u6050\u5413\u610f\u5473\u4e4b\u6d53\uff0c\u6050\u6015\u662f\u989c\u5915\u4e00\u751f\u4eba\u6240\u542c\u5230\u7684\u6700\u65e0\u793c\u7684\u8bdd\u3002 \u3000\u3000\u989c\u5915\u51b7\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u4eba\u5df2\u6b7b\u4e86\uff0c\u4eba\u5728\u9ec4\u571f\u4e0b\uff0c\u4e00\u6837\u4f1a\u53d7\u866b\u556e\u8681\u566c\u3001\u72fc\u543b\u9f20\u54ac\uff0c\u6b7b\u4eba\u4e00\u65e0\u6240\u89c9\uff0c\u795e\u9b42\u90fd\u5df2\u7070\u98de\u70df\u706d\uff0c\u751a\u4e48\u4e1c\u897f\u6765\u6298\u8fb1\u6211\u7684\u5c38\u4f53\uff0c\u53ea\u662f\u6298\u8fb1\u4e86\u4ed6\u81ea\u5df1\u7684\u4eba\u683c\uff0c\u4e0e\u6211\u65e0\u5173\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u957f\u53f9\u4e09\u58f0\uff1a\u201c\u597d\uff0c\u597d\uff0c\u597d\uff01\u201d \u3000\u3000\u4ed6\u773c\u91cc\u5df2\u6d41\u9732\u51fa\u60cb\u60dc\u4e4b\u8272\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u51b3\u4e0d\u5fcd\u4f24\u4f60\u4e00\u53d1\u4e00\u6beb\uff0c\u4e3a\u4e86\u8ba9\u4f60\u4e0d\u6b7b\uff0c\u6211\u5c31\u4e0d\u8ddf\u4f60\u52a8\u624b\uff0c\u53ea\u5e0c\u671b\u4f60\u8ddf\u6211\u4ea4\u4e2a\u670b\u53cb\uff0c\u6211\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u6ca1\u6599\u65ad\u7709\u77f3\u7adf\u4f1a\u60c5\u75f4\u82e5\u6b64\uff0c\u4e0d\u52a8\u624b\u76f8\u5f3a\uff0c\u5fc3\u4e2d\u77e5\u9053\u6709\u5fc5\u8981\u6682\u65f6\u6577\u884d\u6b64\u4eba\uff0c\u4fbf\u9053\uff1a \u3000\u3000\u201c\u5170\u4ead\u6c60\u5bb6\uff0c\u4e00\u5411\u6709\u610f\u7ed3\u7eb3\u6b66\u6797\u8c6a\u6770\uff0c\u4f60\u82e5\u6709\u8bda\u610f\u5316\u654c\u4e3a\u53cb\uff0c\u4e0d\u59a8\u628a\u4ed6\u4eec\u7684\u7a74\u9053\u4e00\u4e00\u89e3\u53bb\uff0c\u90a3\u5c31\u4e07\u4e8b\u597d\u5546\u91cf\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u65e0\u5948\u5730\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u7684\uff0c\u6211\u90fd\u4f9d\u4f60\u3002\u201d \u3000\u3000\u9042\u8d70\u53bb\u82b1\u6cbe\u5507\u90a3\u513f\uff0c\u8981\u89e3\u5979\u8eab\u4e0a\u7684\u7a74\u9053\u3002\u82b1\u6cbe\u5507\u773c\u91cc\u9732\u51fa\u53c8\u559c\u53c8\u60e7\u7684\u795e\u8272\u3002 \u3000\u3000\u989c\u5915\u5ffd\u9053\uff1a\u201c\u6162\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u56de\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u989c\u5915\u77a5\u89c1\u82b1\u6cbe\u5507\u7684\u773c\u8272\uff0c\u987f\u60f3\u8d77\u8fd9\u65ad\u7709\u77f3\u662f\u6709\u540d\u8fa3\u624b\u6467\u82b1\u7684\u6deb\u5f92\uff0c\u82b1\u6cbe\u5507\u53ef\u80fd\u5f88\u4e0d\u613f\u610f\u518d\u7ed9\u4ed6\u6cbe\u4e0a\uff0c\u800c\u82b1\u6cbe\u5507\u4e5f\u4e0d\u662f\u81ea\u5df1\u8fd9\u8fb9\u7684\u4eba\uff0c\u4e07\u4e00\u5728\u5f97\u8131\u540e\u4e0e\u65ad\u7709\u77f3\u5408\u529b\u5bf9\u4ed8\u81ea\u5df1\uff0c\u5c82\u4e0d\u66f4\u4e3a\u51f6\u9669\uff1f\u8fd9\u70b9\u5012\u4e0d\u53ef\u4e0d\u8651\u3002 \u3000\u3000\u4e8e\u662f\u4fbf\u9053\uff1a\u201c\u4f60\u5148\u53bb\u89e3\u6d2a\u4e09\u54e5\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u8038\u8038\u80a9\u9053\uff1a\u201c\u4e5f\u65e0\u4e0d\u53ef\u3002\u201d\u9042\u6307\u6307\u5730\u4e0a\u7684\u6d2a\u4e09\u70ed\uff0c\u7b11\u9053\uff0c\u201c\u8fd9\u8d56\u5728\u5730\u4e0a\u7684\u5c31\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u2018\u94c1\u7532\u5f00\u5c71\u2019\u6d2a\u4e09\u70ed\u4e48\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u5f53\u7136\u6ca1\u6709\u5e94\u4ed6\u3002 \u3000\u3000\u65ad\u7709\u77f3\u7f13\u7f13\u7684\u4fef\u4e0b\u8eab\u53bb\uff0c\u8981\u4e3a\u4ed6\u89e3\u7a74\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u4e0a\u6708\u8272\u4e00\u9eef\u3002 \u3000\u3000\u4e00\u56e2\u4e4c\u4e91\uff0c\u53c8\u628a\u6708\u91cc\u7f69\u5176\u4e2d\uff0c\u53ea\u9732\u51fa\u94f6\u4eae\u7684\u9576\u8fb9\u3002 \u3000\u3000\u53ea\u542c\u65ad\u7709\u77f3\u8be7\u9053\uff1a\u201c\u600e\u4e48\uff1f\uff01\u201d \u3000\u3000\u989c\u5915\u4e5f\u662f\u4e00\u60ca\uff1a\u201c\u600e\u4e48\u4e86\uff1f\uff01\u201d \u3000\u3000\u65ad\u7709\u77f3\u60ca\u9053\uff1a\u201c\u6b7b\u4e86\uff01\u201d \u3000\u3000\u989c\u5915\u8bb6\u9053\uff1a\u201c\u751a\u4e48\uff1f\u6b7b\u4e86\uff1f\uff01\u201d \u3000\u3000\u65ad\u7709\u77f3\u6016\u7136\u56de\u9996\uff0c\u4e24\u9053\u6de1\u6de1\u7684\u6697\u5f71\u53c8\u9690\u73b0\u5728\u773c\u7751\u4e0a\u65b9\uff1a\u201c\u4ed6\u6b7b\u4e86\uff01\u662f\u8c01\u6740\u4e86\u4ed6\uff1f\uff01\u201d \u3000\u3000\u989c\u5915\u98de\u63a0\u4e0a\u524d\uff0c\u4fef\u8eab\u53eb\u9053\uff1a\u201c\u4e09\u54e5\u2026\u2026\u201d \u3000\u3000\u5374\u89c1\u6d2a\u4e09\u70ed\u4e00\u53cc\u5927\u76ee\uff0c\u5145\u6ee1\u60c5\u6025\u5f20\u7687\uff0c\u6b63\u4e0d\u4f4f\u5730\u5411\u5979\u7728\u52a8\uff0c\u989c\u5915\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u8fd8\u6ca1\u6765\u4e0d\u53ca\u53cd\u5e94\uff0c\u65ad\u7709\u77f3\u5df2\u4e00\u53c9\u6273\u98de\u4e86\u5979\u624b\u4e2d\u7684\u5251\uff0c\u5728\u989c\u5915\u8fd8\u6ca1\u6765\u5f97\u53ca\u4f5c\u51fa\u4efb\u4f55\u884c\u52a8\u4e4b\u524d\uff0c\u5df2\u4f38\u624b\u8fde\u5c01\u5979\u4e09\u5904\u7a74\u9053\u3002 \u3000\u3000\u989c\u5915\u7684\u8eab\u5b50\u8f6f\u5012\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u65ad\u7709\u77f3\u5c45\u7136\u8fd8\u4ee5\u6559\u8bad\u7684\u53e3\u543b\u9053\uff1a\u201c\u8fd9\u4e2a\u6545\u4e8b\u6559\u8bad\u4f60\uff0c\u6c38\u8fdc\u4e0d\u8981\u4ee5\u4e3a\u81ea\u5df1\u662f\u4e2a\u51fa\u8272\u7684\u5973\u4eba\uff0c\u4fbf\u53ef\u4ee5\u628a\u7537\u4eba\u63a7\u5236\u4f4f\u3002\u544a\u8bc9\u4f60\uff0c\u6ca1\u6709\u8fd9\u6837\u5b50\u7684\u4e8b\u3002\u201d\u4ed6\u53f9\u4e86\u4e00\u53e3\u6c14\u53c8\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u5b9e\u5728\u559c\u6b22\u4f60\uff0c\u4f60\u662f\u4e2a\u6700\u8ba9\u6211\u5fc3\u52a8\u7684\u5973\u5b50\u3002\u201d \u3000\u3000\u989c\u5915\u628a\u5934\u4e00\u6b6a\uff0c\u649e\u5411\u77f3\u9636\u3002 \u3000\u3000\u4f46\u65ad\u7709\u77f3\u66f4\u5feb\u3002 \u3000\u3000\u65ad\u7709\u77f3\u4e00\u4f38\u624b\uff0c\u5c31\u5c01\u4e86\u5979\u7684\u5ec9\u6cc9\u7a74\u548c\u5929\u7a97\u7a74\u3002 \u3000\u3000\u989c\u5915\u767b\u65f6\u8fde\u9888\u90e8\u90fd\u65e0\u6cd5\u8f6c\u52a8\u3002 \u3000\u3000\u65ad\u7709\u77f3\u4e00\u7b11\u95ee\u5979\uff1a\u201c\u4f60\u8fd8\u60f3\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u989c\u5915\u77e5\u9053\u8fd9\u662f\u5b81\u6b7b\u4e0d\u8fb1\u3001\u81ea\u7edd\u4fdd\u8282\u7684\u65f6\u5206\uff0c\u518d\u4e0d\u72b9\u8c6b\uff0c\u54ac\u820c\u81ea\u5c3d\u3002 \u3000\u3000\u53ef\u662f\u65ad\u7709\u77f3\u4f3c\u4e4e\u6d1e\u900f\u4e86\u989c\u5915\u7684\u610f\u56fe\u3002 \u3000\u3000\u4ed6\u6bd4\u5979\u66f4\u5feb\uff0c\u4e00\u5f39\u6307\uff0c\u5c31\u5c01\u4e86\u5979\u7684\u5929\u5bb9\u3001\u98a7\u9aa8\u3001\u627f\u6d46\u4e09\u7a74\u3002 \u3000\u3000\u989c\u5915\u7684\u4e0a\u4e0b\u989a\u7acb\u5373\u50cf\u8131\u4e86\u81fc\u4f3c\u7684\uff0c\u534a\u70b9\u529b\u6c14\u4e5f\u4f7f\u4e0d\u5f97\u3002 \u3000\u3000\u65ad\u7709\u77f3\u4f3c\u5728\u4ed4\u7ec6\u7aef\u8be6\u5c0f\u52a8\u7269\u5782\u6b7b\u6323\u624e\u5730\u95ee\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u751a\u4e48\u6cd5\u5b9d\uff1f\u201d \u3000\u3000\u989c\u5915\u8fde\u8bed\u97f3\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff1a\u201c\u4f60\u5351\u9119\uff01\u201d \u3000\u3000\u201c\u554a\u624d\u6211\u53ea\u662f\u52a0\u70b9\u4e86\u90a3\u53ea\u94c1\u7532\u4e4c\u9f9f\u7684\u54d1\u7a74\u3002\u201d\u65ad\u7709\u77f3\u6deb\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u751a\u4e48\u4e0d\u5c01\u4f4f\u4f60\u7684\u54d1\u7a74\uff1f\u201d \u3000\u3000\u6708\u4eae\u53c8\u8e31\u51fa\u4e91\u5c42\uff0c\u50cf\u4e00\u4e2a\u60a0\u95f2\u7684\u767d\u8863\u6587\u58eb\uff0c\u4f46\u6708\u5149\u7167\u5728\u65ad\u7709\u77f3\u7684\u8138\u4e0a\uff0c\u4ed6\u7684\u7b11\u5bb9\u4ee4\u4eba\u4e0d\u5bd2\u800c\u7c9f\u3002 \u3000\u3000\u4ed6\u867d\u6ca1\u628a\u610f\u601d\u8bf4\u51fa\u6765\uff0c\u4e0d\u8fc7\u53ea\u8981\u4e00\u89c1\u4ed6\u7684\u7b11\u5bb9\uff0c\u573a\u91cc\u6bcf\u4e00\u4e2a\u4e0d\u80fd\u52a8\u5f39\u7684\u4eba\uff0c\u90fd\u77e5\u9053\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u4ed6\u4eec\u73b0\u5728\u624d\u77e5\u9053\u65ad\u7709\u77f3\u7684\u53ef\u6015\u3002 \u3000\u3000\u522b\u4eba\u7684\u53ef\u6015\u53ef\u80fd\u662f\u56e0\u4e3a\u5fc3\u72e0\u624b\u8fa3\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u6b66\u529f\u9ad8\u5f3a\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u53e3\u871c\u8179\u5251\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u7ffb\u8138\u65e0\u60c5\uff0c\u53ef\u662f\uff0c\u65ad\u7709\u77f3\u7684\u53ef\u6015\u5374\u4e0d\u662f\u8fd9\u4e9b\u3002 \u3000\u3000\u65ad\u7709\u77f3\u7b80\u76f4\u4e0d\u80fd\u7b97\u662f\u4eba\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u7b97\u662f\u4e00\u53ea\u6709\u539f\u5219\u7684\u79bd\u517d\u3002 \u3000\u3000\u4ed6\u7684\u539f\u5219\u5f53\u7136\u662f\uff1a\u4ed6\u4e0d\u6740\u5728\u6b63\u5e38\u60c5\u51b5\u4e0b\u7684\u4eba\uff0c\u4e0d\u6740\u6298\u78e8\u5f97\u8fd8\u672a\u4ee4\u4ed6\u6ee1\u610f\u7684\u4eba\uff0c\u4e0d\u6740\u88ab\u4ed6\u5f3a\u5978\u8fc7\u7684\u5973\u4eba\u3002 \u3000\u3000\u73b0\u5728\u65ad\u7709\u77f3\u5df2\u5168\u9762\u80dc\u5229\u3002 \u3000\u3000\u4ed6\u5df2\u4e00\u53e3\u6c14\u6740\u4e86\u5170\u4ead\u6c60\u5bb6\u56db\u4eba\u3001\u5c0f\u78a7\u6e56\u6e38\u5bb6\u516b\u4eba\uff0c\u8fde\u773c\u4e5f\u4e0d\u591a\u7728\u4e00\u4e0b\uff0c\u5e76\u987a\u4fbf\u628a\u53e6\u5916\u5170\u4ead\u6c60\u5bb6\u7684\u56db\u4e2a\u7a74\u9053\u53d7\u5236\u7684\u4eba\u4e00\u5e76\u5c01\u4f4f\u4e86\u54d1\u7a74\u3002 \u3000\u3000\u800c\u4eca\u5170\u4ead\u6c60\u5bb6\u4e3e\u8db3\u8f7b\u91cd\u7684\u4eba\u7269\uff0c\u989c\u5915\u548c\u6d2a\u4e09\u70ed\uff0c\u90fd\u843d\u5728\u4ed6\u624b\u4e0a\uff0c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u7684\u82b1\u6cbe\u5507\u548c\u7b80\u8fc5\uff0c\u4e5f\u4e00\u6837\u5728\u4ed6\u638c\u63e1\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u5927\u53ef\u4e3a\u6240\u6b32\u4e3a\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u53d7\u5236\u7684\u7b80\u8fc5\u3001\u82b1\u6cbe\u5507\u548c\u6d2a\u4e09\u70ed\u3001\u989c\u5915\uff0c\u591a\u60f3\u5728\u4e00\u8d77\u5408\u4f5c\u5fa1\u654c\uff0c\u89e3\u51b3\u6389\u773c\u524d\u8fd9\u4e2a\u53ef\u6015\u7684\u9b54\u5934\uff0c\u53ef\u662f\uff0c\u4ed6\u4eec\u73b0\u5728\u90fd\u81ea\u987e\u4e0d\u6687\u3001\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u2014\u2014\u4eba\uff0c\u4e3a\u751a\u4e48\u8981\u5728\u9762\u4e34\u5371\u8270\u7684\u65f6\u5019\uff0c\u624d\u60f3\u5230\u5408\u4f5c\u56e2\u7ed3\u7684\u597d\u5904\uff1f\u800c\u5728\u5e73\u65f6\u4e3a\u751a\u4e48\u4e92\u76f8\u6b8b\u6740\u3001\u76f8\u4e92\u503e\u8f67\uff1f \u3000\u3000\u2014\u2014\u989c\u5915\u6709\u6ca1\u6709\u540e\u6094\uff1f \u3000\u3000\u2014\u2014\u6d2a\u4e09\u70ed\u6709\u6ca1\u6709\u540e\u6094\uff1f \u3000\u3000\u2014\u2014\u7b80\u8fc5\u6709\u6ca1\u6709\u540e\u6094\uff1f \u3000\u3000\u2014\u2014\u82b1\u6cbe\u5507\u6709\u6ca1\u6709\u540e\u6094\uff1f\u5982\u679c\u4ed6\u4eec\u80fd\u6d3b\u5f97\u4e0b\u6765\uff0c\u628a\u201c\u540e\u6094\u201d\u7684\u8baf\u606f\u5e26\u5230\u5170\u4ead\u3001\u5e26\u5230\u5c0f\u78a7\u6e56\uff0c\u201c\u6d1b\u9633\u56db\u516c\u5b50\u4e4b\u4e89\u201d\u662f\u4e0d\u662f\u5c31\u53ef\u4ee5\u5e73\u606f\uff1f\u6c5f\u6e56\u662f\u4e0d\u662f\u5c31\u53ef\u4ee5\u4e0d\u6380\u5343\u4e08\u6d6a\u4e07\u4e08\u6d9b\uff1f \u3000\u3000\u4eba\u7a81\u7136\u9047\u4e0a\u4e86\u7edd\u5883\uff0c\u5c31\u4f1a\u5f00\u59cb\u540e\u6094\u4ed6\u4eec\u5e73\u65f6\u7edd\u4e0d\u4f1a\u611f\u5230\u540e\u6094\u7684\u4e8b\u60c5\uff0c\u81f3\u5c11\uff0c\u4e5f\u4f1a\u601d\u7701\u5e73\u65e5\u4ed6\u4eec\u51b3\u4e0d\u4f1a\u53bb\u601d\u7701\u7684\u95ee\u9898\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u4e5f\u6ca1\u6709\u65f6\u95f4\u601d\u7d22\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u542c\u5230\u4e86\u6b4c\u3002 \u3000\u3000\u4e00\u9996\u51c4\u843d\u3001\u5fe7\u4f24\u800c\u751c\u7f8e\u7684\u6b4c\u3002 \u3000\u3000\u8fdc\u8fdc\u7684\u4f20\u6765\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u7b49\u7684\u5c82\u975e\u5c31\u662f\u8fd9\u4e2a\u4eba\uff1f \u3000\u3000\u2014\u2014\u4ed6\u4eec\u671f\u76fc\u7684\u5c82\u4e0d\u5c31\u662f\u8fd9\u9996\u6b4c\uff1f \u3000\u3000\u6b4c\u58f0\u8fd1\u4e86\uff0c\u4eba\u8fd8\u4f1a\u8fdc\u5417\uff1f \u3000\u3000\u65ad\u7709\u77f3\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u8be1\u5f02\u7684\u7709\u6bdb\u53c8\u5728\u989d\u4e0a\u6620\u73b0\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4f60\u4eec\u6240\u7b49\u5f85\u7684\u4eba\u7f62\uff1f\u201d \u3000 \u7b2c\u5341\u4e94\u7ae0\u3000\u82b1\u843d\u6ee1\u5730 \u3000 \u3000\u3000\u65b9\u90aa\u771f\u5531\u7740\u4e00\u9996\u4ed6\u5fc3\u91cc\u5e38\u5531\u7684\u6b4c\uff0c\u5c31\u50cf\u60f3\u5ff5\u7740\u4ed6\u4e00\u4e2a\u53e4\u8fdc\u7684\u56de\u5fc6\u3002 \u3000\u3000\u4ed6\u6bcf\u6b21\u54fc\u7740\u8fd9\u9996\u6b4c\u7684\u65f6\u5019\uff0c\u5c31\u60f3\u8d77\u4ece\u524d\u7684\u4eba\uff0c\u4ece\u524d\u7684\u4e8b\u3002\u6bcf\u5f53\u4ed6\u60f3\u8d77\u8fd9\u4e9b\uff0c\u4ed6\u5c31\u4f1a\u7528\u624b\u53bb\u89e6\u6478\u8155\u4e0a\u7cfb\u7740\u7684\u4e1d\u5dfe\u3002 \u3000\u3000\u84dd\u8272\u7684\u4e1d\u5dfe\u3002 \u3000\u3000\u4ed6\u7684\u624b\u8155\u5e38\u5728\u767d\u8272\u7684\u8863\u8896\u91cc\uff0c\u9664\u975e\u662f\u62d4\u5251\u3001\u4e3e\u676f\u3001\u5728\u5899\u4e0a\u9898\u8bd7\u7b49\u52a8\u4f5c\uff0c\u4e0d\u7136\uff0c\u770b\u89c1\u4ed6\u8155\u4e0a\u84dd\u4e1d\u5dfe\u7684\u4eba\uff0c\u4e5f\u4e0d\u80fd\u7b97\u591a\u3002 \u3000\u3000\u770b\u89c1\u4ed6\u7684\u5251\u7684\uff0c\u5f53\u7136\u66f4\u5c11\u3002 \u3000\u3000\u2014\u2014\u867d\u7136\u5f88\u6709\u4e9b\u4eba\u542c\u8fc7\u4ed6\u54fc\u7684\u6b4c\uff0c\u4f46\u6709\u8c01\u80fd\u542c\u51fa\u4ed6\u7684\u5fc3\u58f0\uff1f \u3000\u3000\u4ed6\u5230\u5e95\u5531\u7ed9\u8c01\u542c\u3001\u8fd8\u662f\u5531\u7ed9\u81ea\u5df1\u542c\uff1f \u3000\u3000\u6709\u8c01\u77e5\u9053\uff1f \u3000\u3000\u4e0d\u8fc7\uff0c\u65b9\u90aa\u771f\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff0c\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u6709\u4eba\u6b63\u542c\u7740\u4ed6\u7684\u6b4c\uff1a\u60ca\u5fc3\u52a8\u9b44\u7684\u542c\u7740\u4ed6\u7684\u6b4c\u3001\u809d\u80a0\u5bf8\u65ad\u7684\u542c\u7740\u4ed6\u7684\u6b4c\u3001\u4f24\u5fc3\u6b32\u7edd\u7684\u542c\u7740\u4ed6\u7684\u6b4c\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u4e0d\u77e5\u9053\u800c\u5df2\u3002 \u3000\u3000\u65b9\u90aa\u771f\u968f\u610f\u7684\u54fc\u7740\u4e00\u9996\u66f2\u5b50\u3002 \u3000\u3000\u4e00\u9996\u5e7d\u4f24\u800c\u54c0\u6028\u7684\u6b4c\uff1a \u3000\u3000\u8bb0\u8d77\u65f6\u6b63\u662f\u5fd8\u8bb0 \u3000\u3000\u6000\u5ff5\u6700\u6d53\u65f6 \u3000\u3000\u6ca1\u6709\u4e86\u6000\u5ff5\uff0c\u53ea\u6709\u518d\u89c1 \u3000\u3000\u50cf\u6d77\u5728\u6700\u6c79\u6d8c\u65f6 \u3000\u3000\u6ca1\u6709\u4e86\u6d6a\u53ea\u6709\u60ca\u5929\u52a8\u5730\u7684 \u3000\u3000\u5bc2\u5bde \u3000\u3000\u4ed6\u8fd9\u6837\u54fc\u5531\u7740\uff0c\u773c\u91cc\u7684\u795e\u8272\u66f4\u662f\u843d\u5bde\u3002\u4ed6\u4eca\u665a\u662f\u56de\u5f97\u8f83\u8fdf\u4e00\u4e9b\uff0c\u6708\u5df2\u897f\u659c\uff0c\u53ef\u662f\uff0c\u4ed6\u4e00\u751f\u4eba\u90fd\u8fdf\u4e86\uff0c\u4e5f\u4e0d\u5728\u4e4e\u518d\u8fdf\u4e0a\u4e00\u4e24\u56de\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5531\u7740\u90a3\u9996\u53eb\u505a\u201c\u5fd8\u8bb0\u201d\u7684\u6b4c\uff0c\u5fc3\u4e2d\u50cf\u88ab\u84dd\u8272\u4e1d\u5dfe\u7cfb\u7740\u7684\u624b\u8155\u4e00\u822c\uff0c\u89c9\u5f97\u4e00\u822c\u6df1\u6df1\u6df1\u6df1\u3001\u6df1\u6df1\u6df1\u6df1\u7684\u75db\u82e6\uff0c\u548c\u6d45\u6d45\u6d45\u6d45\u3001\u6d45\u6d45\u6d45\u6d45\u7684\u75db\u695a\u3002 \u3000\u3000\u6b4c\uff0c\u8fd8\u662f\u8981\u5531\u4e0b\u53bb\u7684\uff0c\u6b63\u5982\u8def\uff0c\u4ecd\u662f\u8981\u8d70\u4e0b\u53bb\uff1a \u3000\u3000\u65e5\u4e1c\u5347\u3002\u6708\u897f\u6c89\u3002\u6211\u8d70\u4e0b\u957f\u957f\u7684\u5c71\u5761\u3002 \u3000\u3000\u4e3a\u4e86\u8981\u4e0a\u53e6\u4e00\u5ea7\u81ea\u5df1\u4e5f\u671b\u4e0d\u89c1\u7684\u5c71\u3002 \u3000\u3000\u6216\u8005\u5c31\u5728\u8fd9\u4e00\u523b \u3000\u3000\u9ed1\u6697\u6765\u65f6\uff0c\u6e10\u6e10\u541e\u8680\u4e86\u6211 \u3000\u3000\u6211\u5ffd\u7136\u60f3\u8d77 \u3000\u3000\u60f3\u8d77\u6211\u662f\u88ab\u60f3\u8d77\u8005 \u3000\u3000\u5e76\u6ca1\u6709\u88ab\u5fd8\u8bb0\u3002 \u3000\u3000\u800c\u6211\u6839\u672c\u4e0e\u4f60\u5728\u4e00\u8d77 \u3000\u3000\u5728\u4e00\u8d77\u4e00\u8d77\u5fd8\u8bb0 \u3000\u3000\u65b9\u90aa\u771f\u5531\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u6709\u4e00\u79cd\u5f88\u5947\u7279\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u6709\u4eba\u5728\u548c\u7740\u4ed6\u5531\u3002 \u3000\u3000\u53ea\u6709\u98ce\u58f0\u3001\u53f6\u58f0\u3001\u8349\u58f0\u3001\u6670\u8734\u722c\u8fc7\u77f3\u9636\u7684\u58f0\u97f3\uff0c\u5e76\u6ca1\u6709\u4eba\u58f0\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u6709\u4eba\u6b63\u5728\u5fc3\u91cc\u5531\u7740\u8fd9\u9996\u6b4c\uff1f \u3000\u3000\u65b9\u90aa\u771f\u4e00\u6014\u505c\u6b65\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u770b\u89c1\u843d\u82b1\u3002 \u3000\u3000\u4e00\u6735\u751f\u957f\u5728\u724c\u697c\u65c1\u7684\u6d77\u68e0\uff0c\u6b63\u597d\u840e\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u82b1\u843d\u6ee1\u5730\u3002 \u3000\u3000\u867d\u7136\u5728\u6cd5\u95e8\u5bfa\u201c\u901a\u5929\u9636\u201d\u65c1\u7684\u786e\u79cd\u6709\u4e0d\u5c11\u82b1\u5349\uff0c\u4f46\u843d\u5728\u5730\u4e0a\u7684\u82b1\u6735\uff0c\u7edd\u5bf9\u8981\u6bd4\u77f3\u9636\u65c1\u6240\u690d\u7684\u82b1\u8981\u6765\u5f97\u66f4\u7e41\u6742\u3001\u66f4\u73cd\u8d35\u3001\u66f4\u597d\u770b\u3002 \u3000\u3000\u5982\u679c\u4f60\u79cd\u7684\u662f\u4e03\u91cc\u9999\uff0c\u4fbf\u4e0d\u53ef\u80fd\u7a81\u7136\u957f\u51fa\u4e00\u6735\u7d2b\u4e01\u9999\u6765\u3002 \u3000\u3000\u8c01\u90fd\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u4e9b\u82b1\u5927\u90e8\u5206\u90fd\u4e0d\u662f\u539f\u672c\u5c31\u957f\u5728\u8fd9\u513f\u7684\uff0c\u4e5f\u4e0d\u662f\u81ea\u7136\u6389\u843d\u7684\u3002 \u3000\u3000\u65b9\u90aa\u771f\u81ea\u7136\u4e5f\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4e5f\u770b\u5f97\u51fa\u6765\u8fd9\u513f\u66fe\u6709\u6218\u6597\u8fc7\u7684\u8ff9\u8c61\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e5f\u770b\u89c1\u90a3\u4e2a\u5728\u6708\u4e0b\u6258\u7740\u816e\u3001\u8138\u9732\u6101\u5bb9\u3001\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u3002 \u3000\u3000\u6240\u4ee5\u5f53\u90a3\u4e2a\u4eba\u4e00\u5f00\u53e3\u5c31\u8bf4\uff1a\u201c\u8fd9\u513f\u521a\u521a\u53d1\u751f\u8fc7\u4e8b\u60c5\u201d\u7684\u65f6\u5019\uff0c\u65b9\u90aa\u771f\u4e00\u70b9\u4e5f\u6ca1\u6709\u611f\u5230\u9707\u8bb6\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u6de1\u6de1\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u5c31\u7ee7\u7eed\u5411\u524d\u8d70\u3002 \u3000\u3000\u53cd\u800c\u662f\u90a3\u4e2a\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u8be7\u5f02\u8d77\u6765\u4e86\uff1a\u201c\u4f60\u4e0d\u95ee\u6211\u662f\u4e9b\u751a\u4e48\u4eba\u5728\u6b64\u5730\u6253\u6597\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6f20\u6f20\u5730\u9053\uff1a\u201c\u751a\u4e48\u4eba\u5728\u8fd9\u513f\u6253\u6597\uff0c\u8ddf\u6211\u53c8\u6709\u751a\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u4e00\u6014\u9053\uff1a\u201c\u662f\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53c8\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u6025\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5982\u679c\u4ed6\u4eec\u662f\u4e3a\u4f60\u800c\u6253\u67b6\u5462\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u53cd\u95ee\uff1a\u201c\u6211\u6709\u6ca1\u6709\u53eb\u4ed6\u4eec\u6253\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u53ea\u597d\u7b54\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4ed6\u4eec\u4fbf\u4e0d\u662f\u4e3a\u6211\u800c\u6253\u3002\u800c\u662f\u4e3a\u4e86\u4ed6\u4eec\u7684\u76ee\u6807\u3001\u610f\u56fe\u3001\u5229\u76ca\u800c\u6218\uff0c\u4ed6\u4eec\u81ea\u5df1\u6253\u4e86\u8d77\u6765\uff0c\u53c8\u600e\u80fd\u8bf4\u662f\u4e3a\u6211\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u53c8\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u770b\u6765\u65b9\u90aa\u771f\u53c8\u8981\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u53eb\u9053\uff1a\u201c\u4ed6\u4eec\u597d\u6b79\u4e5f\u662f\u56e0\u4e3a\u8981\u4e89\u53d6\u4f60\u624d\u6253\u4e86\u8d77\u6765\uff0c\u4f60\u8fde\u4ed6\u4eec\u662f\u8c01\u90fd\u4e0d\u60f3\u95ee\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u8f6c\u8eab\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u95ee\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5947\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u56e0\u4e3a\u6709\u4eba\u4f1a\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u95ee\u3000\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u4f60\u3002\u201d\u65b9\u90aa\u771f\u60a0\u95f2\u5730\u9053\uff0c\u201c\u4f60\u5728\u8fd9\u77f3\u9636\u5750\u4e86\u90a3\u4e48\u4e45\uff0c\u4e3a\u7684\u5c82\u4e0d\u5c31\u662f\u8981\u7b49\u6211\u6765\uff0c\u544a\u8bc9\u6211\u8fd9\u4e9b\uff01\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u6123\u4e86\u534a\u664c\uff0c\u624d\u53f9\u4e86\u53e3\u6c14\u9053\uff0c\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\uff0c\u4e3a\u751a\u4e48\u2018\u6d1b\u9633\u56db\u516c\u5b50\u2019\u90fd\u8981\u4e89\u53d6\u4f60\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u8fd9\u624d\u95ee\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u8bf4\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u542c\u8fc7\u695a\u6c49\u76f8\u4e89\u3001\u5927\u5c40\u672a\u5b9a\u4e4b\u65f6\uff0c\u8c0b\u58eb\u84af\u901a\u5982\u4f55\u5206\u6790\u97e9\u4fe1\u7684\u624d\u5e72\uff1f\u2018\u541b\u52a9\u6c49\u5219\u6c49\u5174\uff0c\u52a9\u695a\u5219\u695a\u9738\uff0c\u81ea\u7acb\u5219\u53ef\u5357\u9762\u79f0\u738b\uff0c\u4e09\u5206\u5929\u4e0b\u3002\u2019\u9601\u4e0b\u4e4b\u624d\uff0c\u5927\u6709\u6b64\u52bf\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53ea\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u97e9\u4fe1\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u4e3a\u4f60\u6253\u67b6\u7684\u4eba\uff0c\u662f\u5170\u4ead\u6c60\u5bb6\u548c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4e2d\u56fd\u4eba\u7684\u5bb6\u65cf\u6709\u4f60\u5c31\u6709\u4ed6\uff0c\u6709\u6211\u5c31\u6709\u654c\uff0c\u81ea\u5df1\u4eba\u6253\u81ea\u5df1\u4eba\uff0c\u6253\u4e86\u5343\u6570\u5e74\u4e86\uff0c\u4ecd\u7136\u5728\u6253\u4e2a\u4e0d\u4f11\uff0c\u4e0d\u6253\u7684\u65f6\u5019\uff0c\u4e5f\u4f1a\u76f8\u9a82\u4e2a\u4e0d\u4f11\uff0c\u8fd9\u662f\u81f3\u4e3a\u5e73\u5e38\u7684\u4e8b\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u6b21\u4e3a\u4f60\u800c\u6253\u7684\u90fd\u662f\u4e24\u5bb6\u7684\u7cbe\u82f1\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5254\u8d77\u4e00\u53ea\u7709\u6bdb\uff1a\u201c\u8b6c\u5982\u8bf4\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u8c79\u5b50\u7b80\u8fc5\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u77f3\u9636\u6709\u4e03\u516b\u4e2a\u6de1\u6de1\u7684\u8db3\u5370\uff0c\u82e5\u4e0d\u662f\u7b80\u8fc5\uff0c\u6d1b\u9633\u57ce\u4e2d\u6709\u8c01\u80fd\u591f\u85c9\u4e00\u70b9\u4e4b\u529b\uff0c\u63a0\u8eab\u653b\u5411\u654c\u4eba\uff0c\u518d\u9000\u56de\u4ece\u9636\u4e0a\u501f\u529b\u518d\u653b\uff0c\u8fd9\u79cd\u2018\u6674\u8712\u51b2\u9704\u2019\u7684\u8f7b\u529f\uff0c\u518d\u6ca1\u6709\u7b2c\u4e8c\u4eba\u80fd\u4f7f\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u4fa7\u5934\u770b\u53bb\uff0c\u679c\u89c1\u77f3\u9636\u4e0a\u6709\u51e0\u4e2a\u6de1\u6de1\u7684\u8db3\u5370\uff0c\u65e2\u4e0d\u662f\u6ce5\u5370\uff0c\u4e5f\u4e0d\u662f\u6e7f\u75d5\uff0c\u53ea\u662f\u7b80\u8fc5\u98de\u817e\u501f\u529b\u65f6\uff0c\u5728\u77f3\u9636\u4e0a\u522e\u843d\u4e00\u70b9\u70b9\u7684\u75d5\u8ff9\uff0c\u4e0d\u7ec6\u770b\u662f\u7edd\u770b\u4e0d\u51fa\u6765\u7684\u3002 \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u8fd8\u6709\u6d2a\u4e09\u70ed\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u5f53\u7136\u662f\u4ed6\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u7528\u624b\u5411\u724c\u697c\u4e0b\u7684\u77f3\u677f\u4e00\u6307\u9053\uff1a\u201c\u6d2a\u4e09\u70ed\u4f7f\u7684\u662f\u4e03\u9a73\u8f6f\u67c4\u67aa\uff0c\u4f60\u770b\u8fd9\u5730\u4e0a\u5212\u7684\u82b1\u7eb9\uff0c\u8981\u4e0d\u662f\u6d2a\u4e09\u70ed\u7684\u8182\u529b\uff0c\u8c01\u5f04\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u4e0d\u7981\u95ee\uff1a\u201c\u90a3\u4e48\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u773c\u5149\u7784\u7740\u5730\u4e0a\u7684\u82b1\uff1a\u201c\u5f53\u7136\u8fd8\u6709\u82b1\u6cbe\u5507\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u6c60\u5bb6\u4e5f\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002\u4ed6\u662f\u4e58\u8f7f\u5b50\u6765\u7684\u3002\u201d\u724c\u697c\u4e0b\u4ecd\u7aef\u7aef\u6574\u6574\u7684\u505c\u653e\u7740\u4e24\u9876\u8f7f\u5b50\u3002\u201c\u5982\u679c\u4e0d\u662f\u6c60\u65e5\u66ae\uff0c\u5c31\u662f\u6c60\u5927\u592b\u4eba\uff0c\u60f3\u5fc5\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u7ec8\u4e8e\u53d1\u73b0\u65b9\u90aa\u771f\u4e5f\u6709\u4e0d\u786e\u5b9a\u7684\u4e8b\u60c5\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u624b\u6309\u5251\u67c4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4e5f\u4e0d\u5173\u5fc3\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4e0d\u5173\u5fc3\u4ed6\u4eec\uff0c\u4f46\u4f60\u4e0d\u80fd\u4e0d\u5173\u5fc3\u4ee4\u5c0a\u548c\u4ee4\u5f1f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u9707\u9053\uff1a\u201c\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6c60\u5bb6\u548c\u6e38\u5bb6\u8bf7\u4f60\u7684\u65b9\u6cd5\uff1a\u65e2\u7136\u8bf7\u4e0d\u52a8\u4f60\uff0c\u53ea\u597d\u5148\u628a\u4ee4\u5c0a\u5927\u4eba\u8bf7\u4e86\u8fc7\u53bb\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6012\u9053\uff1a\u201c\u8fd9\u7b97\u751a\u4e48\uff1f\uff01\u201d\u9042\u53c8\u5e73\u4f0f\uff0c\u201c\u6c60\u65e5\u66ae\u4e0d\u662f\u4e2a\u5fc3\u72e0\u624b\u8fa3\u7684\u4eba\uff0c\u4ed6\u4e0d\u4f1a\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u53ef\u662f\u4f60\u522b\u5fd8\u4e86\u6c60\u65e5\u66ae\u6709\u4e2a\u519b\u5e08\u53eb\u505a\u5218\u662f\u4e4b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u5c31\u7b97\u662f\uff0c\u6e38\u7389\u906e\u7684\u8c0b\u4e0a\u987e\u4f5b\u5f71\u4e5f\u51b3\u4e0d\u662f\u628a\u597d\u4e8b\u529e\u6210\u6076\u4e8b\u4e4b\u8f88\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u8be1\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd9\u4ef6\u4e8b\u8fdb\u884c\u7684\u65f6\u5019\uff0c\u987e\u4f5b\u5f71\u5b8c\u5168\u88ab\u8499\u5728\u9f13\u91cc\u5462\u3002\u201d \u3000\u3000\u8fd9\u6b21\u8f6e\u5230\u65b9\u90aa\u771f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u7b11\u4e86\u3002 \u3000\u3000\u201c\u4f60\u7ec8\u4e8e\u8fd8\u662f\u8981\u95ee\u6211\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u80dc\u5229\u4e86\u3002 \u3000\u3000\u2014\u2014\u65b9\u90aa\u771f\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u8fd8\u662f\u5f97\u8981\u95ee\u4ed6\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u65b9\u90aa\u771f\u80af\u95ee\u4ed6\uff0c\u4e0b\u9762\u7684\u8ba1\u5212\uff0c\u81ea\u7136\u5c31\u53ef\u4ee5\u987a\u5229\u8fdb\u884c\u4e86\u3002 \u3000\u3000\u4ed6\u867d\u7136\u8fd8\u4e0d\u66fe\u8ddf\u65b9\u90aa\u771f\u52a8\u624b\uff0c\u4f46\u5df2\u77e5\u9053\u65b9\u90aa\u771f\u80af\u5b9a\u8981\u6bd4\u6d2a\u4e09\u70ed\u3001\u7b80\u8c79\u5b50\u3001\u82b1\u6cbe\u5507\u52a0\u8d77\u6765\u90fd\u96be\u60f9\u3002 \u3000\u3000\u800c\u4e14\u96be\u60f9\u5f97\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u5f97\u610f\uff0c\u989d\u4e0a\u53c8\u9690\u73b0\u4e86\u4e24\u9053\u8be1\u5f02\u7684\u7709\u6bdb\uff1a\u201c\u4f60\u60f3\u95ee\u6211\u4ed6\u4eec\u5728\u751a\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5c45\u7136\u6447\u9996\u3002 \u3000\u3000\u201c\u6211\u53ea\u8981\u95ee\uff1a\u4f60\u662f\u8c01\uff1f\u201d\u65b9\u90aa\u771f\u7684\u76ee\u5149\u5251\u4e00\u822c\u4f3c\u5730\u671b\u7740\u4ed6\uff0c\u201c\u6211\u53ea\u8981\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u4fbf\u53ef\u4ee5\u627e\u5230\u6211\u8981\u627e\u7684\u4eba\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u770b\u5f97\u51fa\u6765\uff0c\u6c60\u5bb6\u548c\u6e38\u5bb6\u7684\u4eba\u90fd\u6ca1\u6709\u6210\u529f\uff0c\u4f46\u5374\u7ed9\u4f60\u6216\u4f60\u4eec\u7684\u4eba\u5f97\u4e86\u624b\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u8138\u9732\u8be7\u5f02\u4e4b\u8272\uff0c\u4f46\u4ed6\u5fc3\u5883\u5374\u5f88\u6109\u5feb\uff1a\u4ed6\u5c31\u662f\u8981\u65b9\u90aa\u771f\u90a3\u4e48\u731c\uff0c\u4ed6\u679c\u7136\u5c31\u90a3\u4e48\u731c\u4e86\uff0c\u5f53\u4e00\u4e2a\u4eba\u4ee5\u4e3a\u4ed6\u5904\u5904\u90fd\u731c\u5f97\u5bf9\u7684\u65f6\u5019\uff0c\u5b9a\u5fc5\u611f\u5230\u5f88\u6ee1\u610f\uff0c\u5f88\u6ee1\u610f\u7684\u65f6\u5019\uff0c\u5b9a\u5fc5\u5f88\u6709\u4fe1\u5fc3\uff0c\u6b63\u5f53\u6700\u6709\u4fe1\u5fc3\u7684\u65f6\u5019\uff0c\u5c31\u96be\u514d\u4f1a\u6709\u4e00\u70b9\u513f\u758f\u5ffd\uff0c\u53ea\u8981\u6709\u4e00\u70b9\u758f\u5ffd\u2014\u2014 \u3000\u3000\u5c31\u5f97\u6b7b\u3002 \u3000\u3000\u4e00\u4e2a\u81ea\u4f5c\u806a\u660e\u7684\u4eba\u5f80\u5f80\u5c31\u662f\u6700\u7b28\u7684\u4eba\u3002 \u3000\u3000\u6240\u4ee5\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5f88\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u4ed6\u6709\u4fe1\u5fc3\u81ea\u5df1\u4e00\u5411\u90fd\u80fd\u628a\u63e1\u5230\u654c\u624b\u4e00\u4e1d\u5fae\u513f\u7684\u758f\u5ffd\uff0c\u4ece\u6765\u4e0d\u4f1a\u5931\u53bb\u8ba9\u5bf9\u65b9\u81f4\u6b7b\u7684\u826f\u673a\u3002 \u3000\u3000\u5c3d\u7ba1\u4ed6\u5fc3\u91cc\u975e\u5e38\u6ee1\u610f\uff0c\u5634\u91cc\u4ecd\u8bb6\u5f02\u5730\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\uff0c\u6240\u4ee5\u4f60\u8981\u95ee\u6211\u662f\u8c01\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u5df2\u4e0d\u5fc5\u95ee\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5947\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d\u4ed6\u5728\u65b9\u90aa\u771f\u9762\u524d\uff0c\u4f3c\u4e4e\u53ea\u5269\u4e0b\u4e86\u95ee\u201c\u4e3a\u751a\u4e48\u201d\u7684\u4efd\u513f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u8863\u895f\u4e0a\u7684\u5fbd\u53f7\u5df2\u7ecf\u544a\u8bc9\u4e86\u6211\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u8863\u8896\u65c1\u7ee3\u7740\u5c0f\u5c0f\u7684\u4e8c\u679d\u6a2a\u659c\u4e94\u6735\u91d1\u6885\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u662f\u2018\u5973\u516c\u5b50\uff0c\u845b\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u7acb\u65f6\u597d\u50cf\u88ab\u77a7\u7834\u4e86\u8eab\u4efd\uff0c\u5403\u4e86\u4e00\u60ca\u7684\u6837\u5b50\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e5f\u662f\u4e2a\u4eba\u624d\uff0c\u4e5f\u662f\u9ad8\u624b\u3002\u201d\u4ed6\u89c2\u5bdf\u7740\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u7684\u8868\u60c5\uff0c\u201c\u2018\u5343\u53f6\u5c71\u5e84\u2019\u9664\u4e86\u5973\u516c\u5b50\u845b\u94c3\u94c3\u548c\u4ed6\u7684\u5c0f\u8868\u59b9\u845b\u60f3\u60f3\u4e4b\u5916\uff0c\u79f0\u5f97\u4e0a\u9ad8\u624b\u7684\uff0c\u5c31\u53ea\u6709\u5e84\u91cc\u7cbe\u64c5\u2018\u5927\u6cc4\u795e\u529f\u2019\u7684\u53f8\u7a7a\u603b\u7ba1\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u4e00\u53e5\u5730\u9053\uff1a\u201c\u4f60\u662f\u9ad8\u624b\uff0c\u4e5f\u662f\u4eba\u624d\uff0c\u6240\u4ee5\uff0c\u4f60\u5fc5\u7136\u5c31\u662f\u53f8\u7a7a\u89c1\u60ef\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5148\u73b0\u6127\u8272\uff0c\u7136\u540e\u8d67\u7136\u5e72\u7b11\u9053\uff1a\u201c\u597d\u773c\u529b\uff01\u6211\u5c31\u662f\u53f8\u7a7a\u5251\u51a0\u3002\u201d \u3000\u3000\u201c\u5343\u53f6\u5c71\u5e84\u201d\u7684\u8001\u5e84\u4e3b\u845b\u5bd2\u706f\u901d\u4e16\u540e\uff0c\u628a\u7ee7\u627f\u706f\u706b\u91cd\u4efb\u4ea4\u7ed9\u845b\u94c3\u94c3\uff0c\u552f\u4e00\u80fd\u66ff\u201c\u5343\u53f6\u5c71\u5e84\u201d\u7e41\u7410\u6742\u52a1\u3001\u5927\u5c0f\u4e8b\u60c5\u90fd\u80fd\u6599\u7406\u59a5\u5f53\u7684\uff0c\u4fbf\u662f\u5f53\u5e74\u66fe\u5728\u6b66\u6797\u4e2d\u4ee5\u201c\u5927\u6cc4\u795e\u529f\u201d\u79f0\u7edd\u4e00\u65f6\uff0c\u540e\u53c8\u6619\u82b1\u4e00\u73b0\uff0c\u6295\u9760\u845b\u5bb6\u7684\u53f8\u7a7a\u89c1\u60ef\u3002 \u3000\u3000\u53f8\u7a7a\u89c1\u60ef\u539f\u540d\u53f8\u7a7a\u5251\u51a0\uff0c\u56e0\u97f3\u63a5\u8fd1\uff0c\u6c5f\u6e56\u4e0a\u4eba\u4eba\u90fd\u79f0\u4e4b\u4e3a\u201c\u89c1\u60ef\u201d\u3002 \u3000\u3000\u845b\u5bd2\u706f\u6b7b\u540e\uff0c\u201c\u5343\u53f6\u5c71\u5e84\u201d\u66f4\u663e\u51cb\u96f6\uff0c\u8bb8\u591a\u597d\u624b\u4e00\u4e00\u79bb\u6563\uff0c\u9ad8\u624b\u4ed6\u6295\uff0c\u53ea\u5269\u4e0b\u8fd9\u540d\u53f8\u7a7a\u89c1\u60ef\u4ecd\u803f\u803f\u5fe0\u5fc3\uff0c\u97a0\u8eac\u5c3d\u7601\uff0c\u4f9d\u7136\u7559\u5728\u845b\u5bb6\u6548\u547d\u3002 \u3000\u3000\u53f8\u7a7a\u89c1\u60ef\u5728\u6b66\u6797\u4e2d\uff0c\u662f\u51fa\u4e86\u540d\u7684\u597d\u4eba\u3002 \u3000\u3000\u8fd9\u4e5f\u53ef\u80fd\u662f\u81f4\u4f7f\u201c\u5343\u53f6\u5c71\u5e84\u201d\u5728\u8fd1\u5e74\u6765\u6ca1\u6709\u751a\u4e48\u8fdb\u5c55\u7684\u539f\u56e0\uff0c\u81f3\u5c11\uff0c\u5728\u201c\u6d1b\u9633\u56db\u516c\u5b50\u201d \u3000\u3000\u7684\u52bf\u529b\u4e2d\uff0c\u845b\u5bb6\u662f\u6700\u5f31\u7684\u4e00\u571c\u3002 \u3000\u3000\u56e0\u4e3a\u4e00\u4e2a\u592a\u597d\u7684\u4eba\uff0c\u901a\u5e38\u90fd\u4e0d\u80fd\u7b97\u662f\u5f3a\u4eba\u3002 \u3000\u3000\u201c\u5f3a\u4eba\u201d\u7684\u7279\u8272\u662f\uff1a\u9047\u5f3a\u6108\u5f3a\u3001\u9047\u632b\u66f4\u5f3a\u3001\u4ee5\u5f3a\u51cc\u5f31\u3001\u5f31\u8089\u5f3a\u98df\u3002 \u3000\u3000\u8fd9\u4e9b\u201c\u7279\u8272\u201d\u53f8\u7a7a\u89c1\u60ef\u4e5f\u8bb8\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6240\u4ee5\u65b9\u90aa\u771f\u4e00\u65e6\u5f97\u77e5\u4ed6\u773c\u524d\u7684\u4eba\u662f\u53f8\u7a7a\u89c1\u60ef\u4e4b\u540e\uff0c\u624b\u4e5f\u5c31\u79bb\u5f00\u4e86\u5251\u67c4\uff0c\u7136\u540e\u624d\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53ea\u8981\u544a\u8bc9\u6211\uff0c\u8fd9\u513f\u5230\u5e95\u53d1\u751f\u4e86\u4e9b\u751a\u4e48\u4e8b\uff0c\u5c31\u53ef\u4ee5\u4e86\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u6414\u6414\u8111\u888b\u53f9\u9053\uff1a\u201c\u770b\u6765\uff0c\u751a\u4e48\u90fd\u7792\u4e0d\u8fc7\u4f60\u4e86\u3002\u6c60\u5bb6\u7684\u5927\u592b\u4eba\u548c\u6d2a\u4e09\u70ed\u631f\u6301\u4e86\u65b9\u8001\u4f2f\u548c\u4ee4\u5f1f\uff0c\u7ecf\u8fc7\u6cd5\u95e8\u5bfa\u524d\uff0c\u88ab\u6e38\u5bb6\u7684\u7b80\u8fc5\u548c\u82b1\u6cbe\u5507\u62e6\u622a\uff0c\u4ea4\u624b\u4e86\u8001\u534a\u5929\uff0c\u5ffd\u7136\uff0c\u6765\u4e86\u4e2a\u77f3\u8001\u5e7a\u2014\u2014\u201d \u3000\u3000\u65b9\u90aa\u771f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u65ad\u7709\u77f3\u8001\u4e48\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u5929\u4e0b\u8fd8\u6709\u54ea\u4e2a\u77f3\u8001\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4ee5\u524d\u5012\u6709\u4e2a\u77f3\u8001\u4e48\uff0c\u662f\u4e2a\u6b66\u5b98\uff0c\u542c\u8bf4\u6dee\u5357\u6d3e\u4fbf\u662f\u56e0\u4e3a\u4ed6\u592a\u8fc7\u6a2a\u884c\u9738\u9053\uff0c\u51fa\u624b\u7ba1\u4e86\uff0c\u624d\u81f4\u4e0e\u51e4\u5c3e\u5e2e\u7ed3\u6028\u7684\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u90a3\u53ea\u662f\u4e2a\u5c0f\u811a\u8272\u800c\u5df2\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u5bf9\u3002\u8fd9\u4e2a\u65ad\u7709\u77f3\u662f\u6709\u540d\u7684\u8fa3\u624b\u4eba\u7269\uff0c\u4ed6\u7684\u2018\u4f24\u5929\u53c9\u2019\u56fa\u7136\u53ef\u6015\uff0c\u4f46\u4ed6\u8981\u6740\u4e00\u4e2a\u4eba\uff0c\u5f80\u5f80\u8fde\u53c9\u4e5f\u4e0d\u5fc5\u52a8\uff0c\u5bf9\u65b9\u5c31\u5df2\u7ecf\u6b7b\u5b9a\u4e86\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u6740\u4eba\u7684\u624b\u6cd5\uff0c\u6bd4\u4ed6\u7684\u7edd\u95e8\u6b66\u5668\u8fd8\u8981\u7edd\u3002\u201d\u4ed6\u4f3c\u4e4e\u6709\u70b9\u5fe7\u5fc3\u5fe1\u5fe1\u5730\u9053\uff0c\u201c\u800c\u4e14\uff0c\u6211\u8fd8\u542c\u8bf4\u8fc7\u6b64\u4eba\u5c31\u662f\u6700\u8fd1\u5d1b\u8d77\u6c5f\u6e56\u4e0a\u4e00\u4e2a\u795e\u79d8\u6740\u624b\u7ec4\u7ec7\u7684\u9886\u8896\u4e4b\u4e00\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u8be7\u7136\u9053\uff1a\u201c\u6740\u624b\u7ec4\u7ec7\uff1f\u53ef\u6709\u540d\u76ee\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u5f04\u4e0d\u6e05\u695a\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u53ea\u77e5\u9053\u4ed6\u4eec\u6709\u4e00\u4e2a\u975e\u5e38\u7b3c\u7edf\u7684\u540d\u5b57\uff0c\u5c31\u53eb\u2018\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\u2019\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u76b1\u7709\u9053\uff1a\u201c\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u628a\u8bdd\u9898\u4e00\u8f6c\uff1a\u201c\u65ad\u7709\u77f3\u53ef\u6709\u52a0\u5165\u6218\u56e2\uff1f\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5fd9\u628a\u8bdd\u9898\u63a5\u4e86\u4e0b\u53bb\uff1a\u201c\u4ed6\u73b0\u5728\u662f\u2018\u5999\u624b\u5802\u2019\u96c7\u7528\u7684\u4eba\uff0c\u5f53\u7136\u4f1a\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7709\u5fc3\u4e00\u8e59\u9053\uff1a\u201c\u4ed6\u82e5\u51fa\u624b\uff0c\u53ea\u6015\u7b80\u8fc5\u3001\u6d2a\u4e09\u70ed\u7b49\u90fd\u51b3\u975e\u662f\u4ed6\u654c\u624b\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5c31\u5728\u8fd9\u65f6\u5019\u6765\u4e86\u4e2a\u4e03\u53d1\u7985\u5e08\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u9053\uff1a\u201c\u4e03\u53d1\u6765\u4e86\uff0c\u6709\u4ed6\u7684\u6210\u540d\u6697\u5668\u2018\u5fc3\u7ec6\u5982\u53d1\u2019\u548c\u5947\u95e8\u5175\u5668\u2018\u888b\u888b\u5e73\u5b89\u2019\uff0c\u6e38\u5bb6\u7684\u4eba\u5927\u53ef\u4ee5\u53cd\u8d25\u4e3a\u80dc\u4e86\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u7b11\u9053\uff1a\u201c\u5374\u662f\u5de7\u597d\u8521\u65cb\u949f\u4e5f\u6765\u4e86\uff0c\u4ed6\u7684\u4e5d\u5c3a\u4e03\u5bf8\u957f\u5251\uff0c\u628a\u4e03\u53d1\u7985\u5e08\u903c\u51fa\u4e08\u5916\uff0c\u5e76\u514b\u5236\u4f4f\u77f3\u8001\u4e48\u7684\u4f24\u5929\u53c9\uff0c\u51e0\u4eba\u82e6\u6218\u4e0d\u4f11\uff0c\u7ed3\u679c\u8c01\u4e5f\u6ca1\u6709\u8ba8\u597d\uff0c\u6253\u5230\u5927\u9690\u4e18\u540e\u5c71\u9634\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5254\u7709\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u5728\u8fd9\u91cc\u6361\u4e86\u4fbf\u5b9c\uff1f\u201d \u3000\u3000\u201c\u54ea\u6709\u4fbf\u5b9c\u53ef\u6361\uff01\u6211\u53ea\u662f\u7559\u4e0b\u6765\u4fdd\u62a4\u65b9\u8001\u7239\u548c\u65b9\u5c0f\u5f1f\u3002\u201d\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u53d7\u4e86\u59d4\u5c48\u4f3c\u5730\u9053\uff0c\u201c\u6e38\u5bb6\u3001\u6c60\u5bb6\u3001\u56de\u5bb6\u90fd\u4e0d\u662f\u8822\u4eba\uff0c\u4ed6\u4eec\u81ea\u4e5f\u6d3e\u51fa\u9ad8\u624b\u6765\u52ab\u8d70\u4eba\u8d28\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4ed6\u4eec\u81ea\u7136\u90fd\u4e0d\u662f\u53f8\u7a7a\u89c1\u60ef\u4e4b\u654c\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u9053\uff1a\u201c\u6545\u6b64\u6211\u4e5f\u653e\u5012\u4e86\u5341\u4e8c\u4e2a\u4eba\uff0c\u5c31\u63a9\u5728\u8349\u4e1b\u91cc\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u66f4\u6b63\u9053\uff1a\u201c\u662f\u5341\u516d\u4e2a\u4eba\uff0c\u4e0d\u662f\u5341\u4e8c\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u65e0\u5948\u5730\u9053\uff1a\u201c\u53cd\u6b63\u4f60\u90fd\u770b\u51fa\u6765\u4e86\uff0c\u5374\u53ef\u77e5\u4ee4\u5c0a\u548c\u4ee4\u5f1f\u85cf\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u5728\u8f7f\u91cc\u3002\u201d \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u53d1\u51fa\u8d5e\u53f9\u9053\uff1a\u201c\u4f60\u5b9e\u5728\u662f\u4e2a\u806a\u660e\u7edd\u9876\u7684\u4eba\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u8d70\u5411\u8f7f\u5b50\u3002 \u3000\u3000\u7136\u540e\u6380\u5e18\u3002 \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u6253\u4ece\u5fc3\u91cc\u4e50\u4e86\u51fa\u6765\uff1a \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u81ea\u4ee5\u4e3a\u806a\u660e\u7684\u4eba\uff0c\u7ec8\u4e8e\u4e5f\u4e3a\u81ea\u4f5c\u806a\u660e\u800c\u4ed8\u51fa\u4ee3\u4ef7\uff01 \u3000 \u7b2c\u5341\u516d\u7ae0\u3000\u5f53\u8f7f\u5e18\u6380\u5f00\u7684\u65f6\u5019 \u3000 \u3000\u3000\u65b9\u90aa\u771f\u4e00\u6380\u5e18\u5b50\uff0c\u5c31\u770b\u89c1\u4ed6\u7edd\u5bf9\u4e0d\u53ef\u80fd\u60f3\u5230\u4f1a\u770b\u89c1\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u8f7f\u5b50\u91cc\u6709\u4eba\u3002 \u3000\u3000\u4e0d\u662f\u65b9\u7236\uff0c\u4e5f\u4e0d\u662f\u65b9\u7075\u3002 \u3000\u3000\u800c\u662f\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u7f8e\u8273\u7684\u5973\u4eba\u3002 \u3000\u3000\u5507\u7ea2\u5f97\u50cf\u4e94\u6708\u7684\u5c71\u82b1\uff0c\u5341\u6708\u7684\u5c71\u706b\uff0c\u98a7\u4e30\u988a\u6da6\uff0c\u672c\u6765\u5e94\u8be5\u662f\u4e00\u4e2a\u4ee4\u4eba\u8ff7\u60d1\u800c\u63e1\u6709\u6743\u52bf\u7684\u5973\u4eba\uff0c\u4f46\u5979\u7684\u773c\u8272\u5374\u662f\u7f9e\u6124\u800c\u53ef\u601c\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u8eab\u65e0\u5bf8\u7f15\uff0c\u4e0d\u8be5\u88ab\u7537\u4eba\u770b\u5230\u548c\u8be5\u88ab\u7537\u4eba\u770b\u5230\u7684\u5730\u65b9\uff0c\u90fd\u9732\u4e86\u51fa\u6765\uff0c\u8892\u5f00\u65e0\u906e\u63a9\u3002 \u3000\u3000\u8fd9\u80f4\u4f53\u4e4b\u7f8e\uff0c\u8db3\u4ee4\u4eba\u7a92\u606f\u3002 \u3000\u3000\u8f7f\u91cc\u5806\u6ee1\u4e86\u82b1\uff0c\u5374\u4e0d\u53ca\u4e00\u5bf8\u67d4\u808c\u3002 \u3000\u3000\u82b1\u74e3\u886c\u7740\u5145\u6ee1\u5f39\u6027\u7684\u80f4\u4f53\uff0c\u7f8e\u5f97\u50cf\u4e00\u5e45\u4ee4\u4eba\u9020\u601d\u800c\u4f7f\u4eba\u72af\u7f6a\u7684\u753b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6ca1\u6709\u60f3\u5230\u8f7f\u91cc\u662f\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u8fd9\u4e48\u7f8e\u8273\u7684\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u8ba4\u8bc6\u8fd9\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u53d1\u52a8\u4e86\u4ed6\u84c4\u52bf\u5df2\u4e45\u7684\u4e00\u51fb\uff01 \u3000\u3000\u4e16\u95f4\u6709\u5f88\u591a\u4e8b\u60c5\u662f\u5fc5\u987b\u4e5f\u5e94\u987b\u8981\u4ed8\u51fa\u4ee3\u4ef7\u7684\uff1a\u81ea\u4f5c\u5b7d\u662f\u4e00\u79cd\uff0c\u81ea\u4ee5\u4e3a\u662f\u662f\u4e00\u79cd\uff0c\u81ea\u4f5c\u806a\u660e\u4e5f\u662f\u4e00\u79cd\u3002 \u3000\u3000\u81ea\u4ee5\u4e3a\u80fd\u628a\u4eba\u9a97\u5012\u4ea6\u662f\u4e00\u79cd\u3002 \u3000\u3000\u65ad\u7709\u77f3\u7684\u53c9\u523a\u5411\u65b9\u90aa\u771f\u80cc\u540e\u7684\u540c\u65f6\uff0c\u65b9\u90aa\u771f\u5df2\u540c\u65f6\u51fa\u5251\u3002 \u3000\u3000\u6df1\u78a7\u7684\u5251\u3002 \u3000\u3000\u5251\u5149\u6620\u7167\u4e86\u8f7f\u4e2d\u5973\u5b50\u7684\u82b1\u5bb9\u3002 \u3000\u3000\u8f7f\u91cc\u7684\u5973\u5b50\u662f\u82b1\u6cbe\u5507\u3002 \u3000\u3000\u5728\u5979\u773c\u4e2d\u7f9e\u6124\u7684\u6cea\u5149\u91cc\uff0c\u6620\u8d77\u4e00\u7247\u9ad8\u6d01\u7684\u5251\u5149\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7684\u767d\u8863\u4e0a\uff0c\u6d12\u843d\u4e86\u51e0\u70b9\u9c9c\u8840\u3002\u9c9c\u7ea2\u7684\u8840\u8ff9\u5728\u94f6\u8272\u7684\u6708\u5149\u6620\u7167\u4e0b\uff0c\u50cf\u51e0\u70b9\u7070\u8910\u8272\u7684\u82d4\u75d5\u3002 \u3000\u3000\u65ad\u7709\u77f3\u7684\u8eab\u5f62\u4e00\u4e2a\u7389\u87d2\u602a\u7ffb\u8eab\uff0c\u6025\u8d77\u91d1\u9ca4\u5012\u7a7f\u6ce2\uff0c\u540c\u65f6\u5c55\u8709\u8763\u70b9\u620f\u6c34\uff0c\u5df2\u6d88\u5931\u5728\u9ed1\u6697\u91cc\uff0c\u53ea\u542c\u8fdc\u8fdc\u4f20\u6765\u6068\u6781\u4e86\u7684\u58f0\u97f3\uff1a\u201c\u65b9\u90aa\u771f\uff0c\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\uff0c\u4e0d\u6539\u9752\u5c71\u4e0d\u89e3\u6068\uff0c\u4f60\u7b49\u7740\u77a7\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u7f13\u7f13\u7684\u6536\u8d77\u5251\u3002 \u3000\u3000\u7136\u540e\u5ffd\u7136\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u51e0\u70b9\u8840\u8ff9\uff0c\u6d12\u5728\u8863\u895f\u4e0a\uff0c\u51e0\u4e0d\u8d77\u773c\u3002 \u3000\u3000\u2014\u2014\u7eb5\u7136\u521a\u624d\u6e85\u5728\u4ed6\u8863\u886b\u4e0a\u662f\u6697\u7b97\u4ed6\u7684\u4eba\u4e4b\u8840\uff0c\u4f46\u800c\u4eca\u6cbe\u5728\u4ed6\u8896\u895f\u4e0a\u7684\uff0c\u5374\u80af\u5b9a\u662f\u4ed6\u81ea\u5df1\u7684\u8840\u3002 \u3000\u3000\u65b9\u90aa\u771f\u957f\u5438\u4e00\u53e3\u6c14\u3002\u518d\u5ea6\u6380\u5f00\u5e18\u5b50\uff0c\u8f7f\u91cc\u7684\u5973\u5b50\u7741\u7740\u4e00\u53cc\u60ca\u60e7\u800c\u7f9e\u6124\u6b32\u7edd\u7684\u773c\uff0c\u4e5f\u671b\u7740\u4ed6\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6df1\u89c9\u5f97\u8fd9\u60c5\u5f62\u5f88\u8ba9\u81ea\u5df1\u6709\u4e00\u79cd\u76f8\u5f53\u52a8\u5fc3\u7684\u5fc3\u52a8\uff0c\u4ed6\u7acb\u523b\u9053\uff1a\u201c\u8fd9\u662f\u60c5\u975e\u5f97\u5df1\uff0c\u6211\u8981\u8ddf\u4f60\u89e3\u7a74\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u4e0d\u80fd\u52a8\u5f39\uff0c\u4e5f\u4f5c\u4e0d\u5f97\u58f0\uff0c\u53ea\u80fd\u7728\u4e86\u7728\u5979\u90a3\u6f3e\u7740\u6cea\u5149\u7684\u5927\u773c\u775b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8131\u53bb\u767d\u957f\u886b\uff0c\u8f7b\u8f7b\u62ab\u5728\u5979\u80f4\u4f53\u4e0a\uff0c\u90a3\u5973\u5b50\u773c\u91cc\u60ca\u60f6\u4e4b\u8272\u7a0d\u51cf\uff0c\u773c\u6cea\u50cf\u73cd\u73e0\u513f\u822c\u5730\u6389\u843d\u4e0b\u6765\uff0c\u65b9\u90aa\u771f\u95ed\u4e0a\u773c\u775b\uff0c\u9053\uff1a\u201c\u8bf7\u6055\u5192\u72af\u3002\u201d\u4f38\u624b\u8ff8\u70b9\uff0c\u4ed6\u773c\u775b\u867d\u5df2\u9616\u4e0a\uff0c\u4f46\u7a74\u4f4d\u5df2\u8ba4\u51c6\u4e86\uff0c\u867d\u9694\u7740\u888d\u5b50\uff0c\u89e6\u624b\u808c\u80a4\u4ecd\u89c9\u67d4\u817b\u5ae9\u6ed1\uff0c\u82b1\u9999\u88ad\u9f3b\uff0c\u5fc3\u5934\u4e5f\u4e0d\u7981\u6cdb\u8d77\u4e86\u4e00\u4e1d\u6d9f\u6f2a\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51e0\u4e0b\u201c\u9694\u888d\u8ba4\u7a74\u201d\uff0c\u82b1\u6cbe\u5507\u5df2\u201c\u5624\u549b\u201d\u4e00\u58f0\u4f4e\u547c\uff0c\u65b9\u90aa\u771f\u60c5\u77e5\u7a74\u9053\u5df2\u51b2\u5f00\uff0c\u81ea\u5df1\u518d\u5728\u8f7f\u524d\uff0c\u8fd9\u5973\u5b50\u6050\u66f4\u7f9e\u6127\u65e0\u5bb9\uff0c\u5373\u63a9\u4e0a\u8f7f\u5e18\uff0c\u8d70\u5411\u53e6\u4e00\u9876\u8f7f\u5b50\u3002 \u3000\u3000\u2014\u2014\u521a\u624d\u90a3\u9876\u8f7f\u5b50\u91cc\uff0c\u7adf\u6709\u4e00\u4e2a\u8d64\u88f8\u800c\u7f8e\u8273\u7684\u5973\u4eba\uff0c\u73b0\u5728\u8fd9\u9876\u8f7f\u5b50\u91cc\u4f1a\u6709\u751a\u4e48\uff1f \u3000\u3000\u524d\u9762\u4f1a\u6709\u751a\u4e48\uff1f\u6380\u5f00\u524d\u7a0b\u7684\u201c\u5e18\u5b50\u201d\u4f1a\u89c1\u5230\u751a\u4e48\uff1f\u8fd9\u5c82\u4e0d\u662f\u5728\u4eba\u751f\u8def\u7a0b\u91cc\u4e00\u76f4\u60f3\u5bfb\u6c42\u7684\u7b54\u6848\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u65b9\u90aa\u771f\u77e5\u9053\u4e86\u5e18\u5b50\u540e\u7684\u7b54\u6848\uff0c\u800c\u4e14\u4e5f\u77e5\u9053\u8fd9\u7b54\u6848\u540e\u7684\u4ee3\u4ef7\uff0c\u4ed6\u662f\u4e0d\u662f\u8fd8\u662f\u8981\u53bb\u6380\u5e18\u5b50\uff1f \u3000\u3000\u8c01\u77e5\u9053\uff1f \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u76f8\u4fe1\u65ad\u7709\u77f3\u7684\u8bdd\u3002 \u3000\u3000\u6253\u4ece\u65ad\u7709\u77f3\u5f00\u59cb\u8bf4\u8bdd\u8d77\uff0c\u4ed6\u5c31\u4e0d\u51c6\u5907\u4fe1\u4efb\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4e3a\u751a\u4e48\u4ed6\u4f1a\u9632\u8303\u8fd9\u4e2a\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u5462\uff1f\u4ed6\u4e5f\u8bf4\u4e0d\u4e0a\u6765\uff0c\u8fd9\u53ea\u662f\u5168\u51ed\u4ed6\u7684\u76f4\u89c9\u800c\u5df2\u3002 \u3000\u3000\u771f\u6b63\u6709\u95ef\u8361\u6c5f\u6e56\u7ffb\u8fc7\u98ce\u6d6a\u7684\u4eba\u4f1a\u77e5\u9053\uff0c\u6709\u65f6\u5019\uff0c\u76f4\u89c9\u8981\u6bd4\u5224\u65ad\u529b\u8fd8\u8981\u7ba1\u7528\u3002\u76f4\u89c9\u5f80\u5f80\u5df2\u5305\u62ec\u4e86\u5148\u5929\u7684\u654f\u611f\u548c\u540e\u5929\u7684\u7ecf\u9a8c\uff0c\u6b63\u5982\u91ce\u517d\u7684\u55c5\u89c9\u4e00\u6837\u7075\u654f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u4e0d\u77e5\u9053\u8fd9\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u662f\u8c01\uff0c\u4f46\u5374\u80fd\u80af\u5b9a\u5bf9\u65b9\u8981\u5bf9\u4ed8\u7684\u662f\u4ed6\uff0c\u8fd9\u4e00\u70b9\u4e00\u65e6\u786e\u5b9a\uff0c\u9976\u662f\u65ad\u7709\u77f3\u5343\u53d8\u4e07\u5316\u3001\u5de7\u8a00\u4ee4\u8272\uff0c\u4e5f\u4e00\u6837\u8d77\u4e0d\u4e86\u4efb\u4f55\u4f5c\u7528\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4fbf\u6545\u610f\u5760\u5165\u4e86\u4ed6\u7684\u5708\u5957\u4e2d\uff0c\u800c\u4e14\u6545\u4f5c\u806a\u660e\uff0c\u6545\u610f\u4f3c\u4e2d\u4e86\u4ed6\u7684\u5f40\uff0c\u4ee5\u4e3a\u4ed6\u662f\u5343\u53f6\u5c71\u5e84\u7684\u4eba\uff0c\u800c\u4e14\u5c31\u662f\u53f8\u7a7a\u89c1\u60ef\uff0c\u8001\u7236\u548c\u5c0f\u5f1f\u5c31\u5728\u8fd9\u9876\u8f7f\u5b50\u4e2d\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u57cb\u4f0f\u5c31\u5728\u8fd9\u4e24\u9876\u8f7f\u5b50\u91cc\u3002 \u3000\u3000\u4ed6\u6709\u610f\u8bf1\u5f15\u8fd9\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u51fa\u624b\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u5bf9\u65b9\u4e00\u51fa\u624b\uff0c\u4ed6\u4fbf\u53ef\u4ee5\u5224\u65ad\u5176\u4eba\u7a76\u7adf\u662f\u8c01\uff0c\u800c\u4e14\uff0c\u4ed6\u4e5f\u4f1a\u7acb\u65f6\u8fd8\u51fb\uff0c\u5c06\u4e4b\u683c\u6740\u3002 \u3000\u3000\u53ef\u662f\uff0c\u8f7f\u5b50\u91cc\u7684\u666f\u8c61\uff0c\u8fd8\u662f\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u56e0\u4e3a\u7f8e\u8272\uff0c\u8fd8\u662f\u592a\u8fc7\u7a81\u7136\uff0c\u4ed6\u867d\u6709\u5907\uff0c\u4f46\u4ecd\u662f\u4e00\u6014\uff0c\u8fd9\u4e00\u6014\u4e4b\u95f4\uff0c\u5bf9\u65b9\u5df1\u7136\u51fa\u624b\uff0c\u4ed6\u4e5f\u7acb\u65f6\u51fa\u5251\u3002 \u3000\u3000\u2014\u2014\u539f\u6765\u7adf\u5c31\u662f\u77f3\u65ad\u7709\uff01 \u3000\u3000\u65ad\u7709\u77f3\u53d7\u521b\uff0c\u7acb\u65f6\u64a4\u8d70\uff0c\u5f53\u673a\u7acb\u9000\uff0c\u529f\u8d25\u6c42\u5b58\uff0c\u4e0d\u6127\u4e3a\u4e00\u6d41\u6740\u624b\uff01 \u3000\u3000\u65ad\u7709\u77f3\u867d\u5403\u4e86\u65b9\u90aa\u771f\u4e00\u5251\uff0c\u4f46\u65b9\u90aa\u771f\u4e5f\u5e26\u4e86\u4f24\uff1a \u3000\u3000\u2014\u2014\u65ad\u7709\u77f3\u8001\u4e48\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01 \u3000\u3000\u2014\u2014\u53ea\u662f\uff0c\u4ed6\u53c8\u4f1a\u5728\u7b2c\u4e8c\u9876\u8f7f\u5b50\u91cc\u641e\u751a\u4e48\u82b1\u6837\u5462\uff1f \u3000\u3000\u662f\u4ee5\uff0c\u65b9\u90aa\u771f\u5728\u6380\u5e18\u7684\u65f6\u5019\uff0c\u5df2\u5168\u6709\u4e86\u5fc3\u7406\u51c6\u5907\uff0c\u5c31\u7b97\u662f\u4e07\u7bad\u9f50\u53d1\u3001\u767e\u517d\u9f50\u9e23\u3001\u751a\u6216\u6709\u4e94\u5341\u4e2a\u6ca1\u7a7f\u8863\u670d\u7684\u5973\u5b50\uff0c\u4ed6\u90fd\u51b3\u4e0d\u4e3a\u6240\u52a8\u3002 \u3000\u3000\u7ed3\u679c\uff0c\u4ed6\u6bd4\u6380\u7b2c\u4e00\u9876\u8f7f\u5b50\u7684\u65f6\u5019\uff0c\u66f4\u52a0\u52a8\u5bb9\u3002 \u3000\u3000\u4ed6\u628a\u8f7f\u5e18\u4e00\u6380\u4e4b\u540e\uff0c\u521d\u65f6\u6ca1\u6709\u751a\u4e48\uff0c\u4e91\u7834\u6708\u7a7f\uff0c\u4ed6\u9042\u800c\u770b\u6e05\u695a\u4e86\u8f7f\u91cc\u7684\u5bb9\u989c\uff0c\u5168\u8eab\u4e00\u9707\uff0c\u8138\u8272\u767d\u5f97\u8ddf\u6708\u513f\u4e00\u6837\uff0c\u201c\u970d\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u624b\u4e0d\u7981\u4e00\u677e\uff0c\u8f7f\u5e18\u7adf\u843d\u4e86\u4e0b\u6765\uff0c\u4ed6\u7684\u8eab\u5b50\u6447\u6447\u98a4\u98a4\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u53c8\u518d\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u4ed6\u7684\u767d\u8272\u957f\u886b\u5df1\u62ab\u5728\u82b1\u6cbe\u5507\u7684\u8eab\u4e0a\uff0c\u8eab\u4e0a\u7a7f\u7684\u662f\u4e00\u4ef6\u6de1\u7eff\u8272\u7684\u4e2d\u8863\uff0c\u8155\u4e0a\u7ed1\u7740\u4e00\u6761\u6de1\u84dd\u8272\u7684\u4e1d\u5dfe\uff0c\u4ed6\u7a7f\u767d\u8272\u957f\u886b\u65f6\uff0c\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u98d8\u9038\uff0c\u4f46\u73b0\u5728\u66f4\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6f47\u6d12\u3002 \u3000\u3000\u53ea\u662f\uff0c\u4ed6\u73b0\u5728\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u8ddf\u201c\u6f47\u6d12\u201d\u5168\u6cbe\u4e0d\u4e0a\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u50cf\u53d7\u4e86\u6781\u5927\u7684\u521b\u75db\u4f3c\u7684\uff0c\u8138\u808c\u4e5f\u62bd\u6410\u7740\uff0c\u597d\u4e00\u4f1a\u624d\u5e73\u4f0f\u4e0b\u6765\uff0c\u53c8\u8fc7\u4e86\u534a\u54cd\uff0c\u50cf\u662f\u8981\u7aed\u529b\u5e73\u5b9a\u81ea\u5df1\u7684\u60c5\u7eea\uff0c\u957f\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u624d\u9053\uff1a\u201c\u679c\u771f\u662f\u4f60\u4e48\uff1f\u201d \u3000\u3000\u8f7f\u91cc\u6ca1\u6709\u4eba\u5e94\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u4e86\u4e00\u58f0\uff1a\u201c\u963f\u5915\uff0c\u662f\u4e0d\u662f\u4f60\uff1f\u201d\u8bed\u97f3\u53c8\u6291\u538b\u4e0d\u4f4f\u6fc0\u52a8\u3002 \u3000\u3000\u8f7f\u91cc\u8fd8\u662f\u6ca1\u6709\u4eba\u56de\u5e94\u3002 \u3000\u3000\u53ea\u542c\u201c\u98d5\u201d\u7684\u4e00\u58f0\uff0c\u7b2c\u4e00\u9876\u8f7f\u91cc\u5df2\u95ea\u51fa\u4e00\u6761\u5a40\u5a1c\u591a\u59ff\u7684\u4eba\u5f71\uff0c\u63a0\u5165\u82b1\u6811\u8349\u4e1b\u91cc\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8fd9\u624d\u7701\u8d77\uff0c\u8f7f\u91cc\u7684\u4eba\u6781\u53ef\u80fd\u4e5f\u662f\u7a74\u9053\u53d7\u5236\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u95ee\uff1a\u201c\u963f\u5915\uff0c\u4f60\u662f\u4e0d\u662f\u7a74\u9053\u53d7\u5236\uff1f\u201d \u3000\u3000\u8f7f\u91cc\u6ca1\u6709\u4eba\u5e94\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7acb\u5373\u77e5\u9053\u81ea\u5df1\u591a\u6b64\u4e00\u95ee\u4e86\u3002\u989c\u5915\u5982\u679c\u80fd\u8d70\u52a8\uff0c\u600e\u4f1a\u4e0d\u8d70\u51fa\u8f7f\u5916\uff1f\u5982\u679c\u5979\u80fd\u8bf4\u8bdd\uff0c\u600e\u4f1a\u8fd8\u4e0d\u56de\u7b54\uff1f\u4ed6\u7acb\u5373\u60f3\u6380\u5f00\u8f7f\u5e18\uff0c\u66ff\u989c\u5915\u89e3\u5f00\u7a74\u9053\uff0c\u53ef\u662f\uff0c\u4ed6\u5374\u53c8\u5728\u6708\u4e0b\u8e0c\u8e87\u8d77\u6765\u3002 \u3000\u3000\u201c\u963f\u5915\uff0c\u8981\u771f\u7684\u662f\u4f60\uff0c\u4f60\u4e3a\u751a\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d\u65b9\u90aa\u771f\u671b\u7740\u8f7f\u5e18\uff0c\u4f4e\u58f0\u53f9\u9053\uff1a\u201c\u8fd9\u6050\u6015\u662f\u6211\u6700\u6015\u77e5\u9053\u7684\u7b54\u6848\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8bf4\u5b8c\uff0c\u5c31\u51fa\u624b\u3002 \u3000\u3000\u9694\u7740\u8f7f\u5e18\uff0c\u4e94\u6307\u6325\u5f39\u3002 \u3000\u3000\u5e18\u5b50\u88ab\u6307\u98ce\u6fc0\u5f39\u5f97\u5f80\u5185\u8212\u5377\uff0c\u663e\u51fa\u4e86\u4e00\u4e2a\u5a40\u5a1c\u7684\u4eba\u5f71\u3002 \u3000\u3000\u7136\u540e\u65b9\u90aa\u771f\u5c31\u56de\u8eab\uff0c\u8d1f\u624b\u4ef0\u671b\u9065\u9065\u7684\u6708\u8272\u3002 \u3000\u3000\u6708\u5728\u5929\u8fb9\u3002 \u3000\u3000\u4eba\u5462\uff1f \u3000\u3000\u5fc3\u5462\uff1f \u3000\u3000\u4eba\u5c31\u5728\u4ed6\u7684\u80cc\u540e\uff0c\u5df2\u6380\u5f00\u5e18\u5b50\uff0c\u90a3\u67d4\u8351\u5c31\u6482\u5f00\u4e86\u5e18\u5b50\u3001\u4eba\u8fd8\u5728\u8f7f\u91cc\uff0c\u76ee\u5149\u843d\u5728\u65b9\u90aa\u771f\u7684\u80cc\u5f71\u4e0a\u3002 \u3000\u3000\u6708\u8272\u6f20\u6f20\uff0c\u8c01\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u2014\u2014\u65b9\u90aa\u771f\u5728\u60f3\u751a\u4e48\uff1f \u3000\u3000\u2014\u2014\u989c\u5915\u53c8\u5728\u60f3\u751a\u4e48\uff1f \u3000\u3000\u4ed6\u4eec\u7684\u5fc3\u5883\uff0c\u50cf\u5ffd\u6697\u5ffd\u660e\u7684\u6708\u8272\uff1f\u8fd8\u662f\u50cf\u5ffd\u805a\u5ffd\u6563\u7684\u6d6e\u4e91\u3001\u50cf\u6ee1\u5730\u7684\u843d\u82b1\uff1f\u8fd8\u662f\u50cf\u77f3\u9636\u4e0a\u6c89\u5bc2\u7684\u53e4\u5e99\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u53e4\u5bfa\u91cc\u4f20\u6765\u949f\u58f0\u3002 \u3000\u3000\u94db\u94db\uff01 \u3000\u3000\u949f\u58f0\u60a0\u8fdc\u56de\u8361\uff0c\u628a\u4eba\u751f\u91cc\u8bb8\u591a\u4e0d\u7518\u6210\u7a7a\u548c\u4e0d\u613f\u843d\u7a7a\u7684\u610f\u65e8\uff0c\u90fd\u6572\u6210\u4e86\u66ae\u9f13\u6668\u949f\uff0c\u767e\u5e74\u6613\u8fc7\uff0c\u4e16\u4e8b\u4e00\u68a6\uff0c\u4e5f\u8bb8\u65b9\u90aa\u771f\u5fc3\u91cc\u5728\u60f3\uff1a\u4ed6\u5728\u6b64\u65f6\u6b64\u5730\u89c1\u7740\u989c\u5915\uff0c\u662f\u4e0d\u662f\u68a6\uff1f\u6216\u8bb8\u989c\u5915\u5fc3\u91cc\u4e5f\u5728\u60f3\uff1a\u5979\u5728\u6b64\u65f6\u6b64\u5883\u9047\u89c1\u65b9\u90aa\u771f\uff0c\u662f\u4e0d\u662f\u72b9\u5728\u68a6\u4e2d\uff1f \u3000\u3000\u65e2\u6709\u68a6\uff0c\u5c31\u6709\u68a6\u9192\u3002 \u3000\u3000\u65e2\u6709\u68a6\u9192\uff0c\u5c31\u6210\u7a7a\u3002 \u3000\u3000\u2014\u2014\u4e16\u4e0a\u6709\u4e9b\u5e0c\u671b\uff0c\u7ecf\u4e0d\u7ecf\u5f97\u8d77\u4e00\u518d\u843d\u7a7a\uff1f\u4e16\u95f4\u91cc\u6709\u4e9b\u4f24\uff0c\u80fd\u4e0d\u80fd\u5728\u5fc3\u91cc\u4e00\u5c1d\u518d\u5c1d\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u6d2a\u4e09\u70ed\u5df2\u5927\u6b65\u8e0f\u4e86\u8fc7\u6765\uff0c\u62e6\u5728\u989c\u5915\u548c\u65b9\u90aa\u771f\u4e4b\u95f4\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5927\u592b\u4eba\uff0c\u4f60\u2026\u2026\u4f60\u6709\u6ca1\u6709\u53d7\u4f24\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u7684\u80cc\u5f71\u7a81\u7136\u4e00\u9707\uff0c\u4f3c\u53d7\u4e86\u4e00\u8bb0\u91cd\u51fb\u3002 \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u6211\u6709\u4e9b\u4e8b\u60c5\uff0c\u8981\u8ddf\u2026\u2026\u8fd9\u4f4d\u65b9\u516c\u5b50\u8bf4\u8bf4\uff0c\u597d\u4e0d\u597d\u8bf7\u4f60\u5148\u53bb\u770b\u770b\u7a74\u9053\u53d7\u5236\u7684\u5f1f\u5144\u4eec\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u521a\u7ed9\u82b1\u6cbe\u5507\u89e3\u5f00\u4e86\u7a74\u9053\uff0c\u4fbf\u8fc7\u6765\u4fdd\u62a4\u989c\u5915\uff0c\u751f\u6015\u5979\u4e3a\u65b9\u90aa\u771f\u6240\u4f24\uff0c\u6b64\u523b\u542c\u989c\u5915\u90a3\u4e48\u4e00\u8bf4\uff0c\u53ea\u89c9\u66f4\u4e0d\u653e\u5fc3\uff0c\u8bf4\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e5f\u6ca1\u5b89\u7740\u597d\u5fc3\u773c\uff0c\u6211\u8fd8\u662f\u5728\u8fd9\u91cc\u7684\u597d\u3002\u201d \u3000\u3000\u989c\u5915\u6025\u4e86\uff1a\u201c\u4e09\u54e5\uff0c\u4f60\u5148\u79bb\u5f00\u7247\u523b\uff0c\u53ef\u597d\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u9053\uff1a\u201c\u6211\u8d70\u4e86\uff0c\u8c01\u6765\u4fdd\u62a4\u4f60\u554a\uff1f\u201d \u3000\u3000\u989c\u5915\u53ef\u8010\u4e0d\u4f4f\u6027\u5b50\uff0c\u8dfa\u8db3\u9053\uff1a\u201c\u4f60\u8d70\u5f00\uff01\u201d\u989c\u5915\u4ece\u6765\u6ca1\u6709\u8fd9\u4e48\u5927\u58f0\u5411\u4eba\u65a5\u559d\uff0c\u8fde\u6d2a\u4e09\u70ed\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u614c\u4e86\u624b\u811a\uff0c\u4e00\u65f6\u4e0d\u77e5\u600e\u4e48\u597d\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u9053\uff1a\u201c\u5927\u592b\u4eba\uff0c\u5982\u679c\u6ca1\u751a\u4e48\u4e8b\uff0c\u6211\u5c31\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u7701\u8d77\u81ea\u5df1\u7684\u5931\u6001\uff0c\u9042\u5411\u6d2a\u4e09\u70ed\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u70e6\u4f60\u5c31\u5148\u56de\u907f\u4e00\u4e0b\u53ef\u597d\uff0c\u6211\u4e0e\u65b9\u516c\u5b50\u6709\u8981\u4e8b\u5546\u8c08\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u518d\u4e5f\u4e0d\u6562\u6297\u9006\uff0c\u5634\u5df4\u865a\u60ac\u60ac\u7684\u5f20\u5f00\u7740\uff0c\u5589\u5934\u91cc\u95f7\u58f0\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d \u3000\u3000\u989c\u5915\u8f6c\u5411\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u662f\u4f60\u3002\u6211\u771f\u7684\u4e0d\u77e5\u9053\u662f\u4f60\u3002\u201d\u5979\u8138\u4e0a\u95ea\u8fc7\u4e86\u4e00\u9635\u60f3\u7b11\uff0c\u4f46\u53c8\u4f3c\u54ed\u7684\u795e\u60c5\uff0c\u201c\u6211\uff0c\u6211\u8fd8\u51c6\u5907\u9001\u4e00\u5927\u7bb1\u4e66\u7ed9\u65b9\u5c11\u4fa0\uff0c\u5c31\u5728\u8f7f\u91cc\u2026\u2026\u6ca1\u60f3\u5230\u679c\u771f\u662f\u4f60\uff0c\u5374\u662f\u4f60\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u8c01\uff1f\u201d \u3000\u3000\u989c\u5915\u60c5\u5207\u5730\u9053\uff1a\u201c\u6211\u95ee\u8fc7\uff0c\u53ef\u662f\u4ed6\u4eec\u8bf4\uff0c\u4f60\u5de6\u624b\u8155\u4e0a\u6ca1\u6709\u7fe0\u7389\u956f\uff0c\u53ea\u6709\u84dd\u4e1d\u5dfe\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7f13\u7f13\u4e3e\u8d77\u4e86\u5de6\u624b\u3002\u6708\u8272\u4e0b\uff0c\u4ed6\u7684\u8863\u8896\u5f90\u843d\u5728\u81c2\u4e0a\uff0c\u9732\u51fa\u4e86\u624b\u8155\u3002 \u3000\u3000\u4ed6\u7684\u624b\u8155\u7279\u522b\u7626\u5c0f\uff0c\u8155\u9aa8\u7a81\u9732\uff0c\u4e88\u4eba\u4e00\u79cd\u6587\u79c0\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u7684\u8155\u4e0a\u7cfb\u7740\u4e00\u6761\u84dd\u8272\u7684\u4e1d\u5dfe\u3002 \u3000\u3000\u4ed6\u7528\u53f3\u624b\u89e3\u5f00\u4e86\u4e1d\u5dfe\u3002 \u3000\u3000\u5dfe\u4e1d\u5230\u4e86\u53f3\u624b\uff0c\u5de6\u8155\u5374\u8d6b\u7136\u6709\u4e00\u5708\u7389\u956f\u3002 \u3000\u3000\u7cbe\u7ec6\u5c0f\u5de7\u7684\u7fe0\u7389\u956f\uff01 \u3000\u3000\u4ed6\u4ecd\u662f\u6ca1\u6709\u8f6c\u8fc7\u8eab\u6765\uff0c\u6240\u4ee5\u770b\u4e0d\u89c1\u989c\u5915\u773c\u4e2d\u6cdb\u8d77\u7684\u6cea\u82b1\u3002 \u3000\u3000\u989c\u5915\u98a4\u58f0\u9053\uff1a\u201c\u4e00\u70b9\u4f36\u4ed4\u7fe0\u7389\u6696\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6f2b\u58f0\u9053\uff1a\u201c\u4e00\u88ad\u6df1\u60c5\u8776\u8863\u8f7b\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u8fd9\u65f6\u77aa\u77aa\u65b9\u90aa\u771f\u3002\u671b\u671b\u989c\u5915\uff0c\u8fd9\u65f6\u624d\u77e5\u9053\u79bb\u5f00\uff0c\u5927\u6b65\u8de8\u4e86\u51fa\u53bb\uff0c\u4e00\u5f20\u5927\u8138\u90fd\u7168\u7126\u4e86\u4f3c\u7684\u3002 \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd8\u4fdd\u7559\u7740\u5b83\uff01\u201d \u3000\u3000\u201c\u6211\u65f6\u65f6\u628a\u5b83\u5e26\u5728\u8155\u4e0a\uff0c\u8fd9\u84dd\u4e1d\u5dfe\u4e5f\u662f\u4f60\u7684\uff0c\u5f53\u65e5\u6211\u9669\u9669\u6218\u80dc\u2018\u94c1\u77f3\u5fc3\u80a0\u2019\u56db\u5927\u540d\u5251\uff0c\u624b\u8155\u4f24\u4e86\uff0c\u4f60\u5c31\u4e3a\u6211\u624e\u4e0a\u8fd9\u6761\u4e1d\u5dfe\u3002 \u3000\u3000\u989c\u5915\u5fc3\u5f26\u9707\u52a8\uff0c\u6614\u65e5\u65b9\u90aa\u771f\u5728\u201c\u5341\u4e07\u5927\u5c71\u201d\uff0c\u767d\u8863\u98de\u8dc3\uff0c\u51b3\u6218\u201c\u95ea\u7535\u795e\u5251\u624b\u201d\u94c1\u788e\u67d4\u3001\u201c\u5251\u795e\u201d\u77f3\u5251\u5782\u3001\u201c\u795e\u5251\u201d\u4e00\u5fc3\u4e0a\u4eba\u3001\u201c\u9999\u6885\u6bd2\u5251\u201d\u65ad\u80a0\u8001\u5c3c\u7684\u79cd\u79cd\u60c5\u666f\uff0c\u8fd8\u6709\u548c\u81ea\u5df1\u7684\u79cd\u79cd\u5bbf\u7f18\uff0c\u5fc3\u6000\u6fc0\u8361\uff0c\u53ea\u95fb\u65b9\u90aa\u771f\u9053\uff1a\u201c\u53ef\u662f\u4f60\u90a3\u88ad\u8776\u8863\u4e00\u821e\u541b\u4ea6\u72c2\u5462\uff1f\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u2026\u2026\u5728\u7684\u3002\u201d \u3000\u3000\u201c\u8863\u5728\u3002\u201d\u65b9\u90aa\u771f\u7f13\u7f13\u56de\u9996\uff0c\u773c\u795e\u5947\u7279\uff0c\u671b\u7740\u5979\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d \u3000\u3000\u989c\u5915\u54fd\u54bd\u9053\uff1a\u201c\u65b9\u8c22\u8c22\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u65b9\u8c22\u8c22\u4e86\uff0c\u201d\u65b9\u90aa\u771f\u51b7\u5cfb\u5730\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u662f\u963f\u5915\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4ecd\u662f\u963f\u5915\u3002\u201d\u989c\u5915\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e3a\u751a\u4e48\u8981\u628a\u540d\u5b57\u6539\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u662f\u65b9\u90aa\u771f\uff0c\u6211\u4e0d\u8981\u4eba\u8c22\u6211\u3002\u201d\u65b9\u90aa\u771f\u773c\u91cc\u6d41\u9732\u51fa\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u60b2\u54c0\uff0c\u201c\u8fd9\u4e16\u4e0a\u8c01\u53c8\u5206\u5f97\u6e05\u751a\u4e48\u662f\u90aa\uff1f\u751a\u4e48\u662f\u771f\uff1f\u8c01\u624d\u662f\u90aa\uff1f\u8c01\u624d\u7b97\u771f\uff1f\u201d \u3000\u3000\u201c\u8c22\u8c22\u2026\u2026\u4f60\u2014\u2014\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u5df2\u662f\u5927\u592b\u4eba\u4e86\uff0c\u4e5f\u4e0d\u662f\u989c\u5915\u4e86\u3002\u201d\u65b9\u90aa\u771f\u51b7\u8bee\u7684\u9053\uff1a\u201c\u5927\u592b\u4eba\u540d\u52a8\u6c5f\u6e56\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u5170\u4ead\u6c60\u5bb6\u80fd\u591f\u6210\u4e3a\u4e00\u65b9\u4e4b\u96c4\uff0c\u4fbf\u662f\u56e0\u4e3a\u5927\u592b\u4eba\u7684\u624b\u8155\u9ad8\u3001\u773c\u5149\u51c6\u3001\u9762\u5b50\u591f\uff0c\u6211\u5012\u662f\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u592b\u4eba\u8fd8\u6709\u751a\u4e48\u5429\u5490\uff1f\u201d\u65b9\u90aa\u771f\u7279\u522b\u6709\u793c\u5730\u9053\uff0c\u201c\u5927\u592b\u4eba\u8981\u662f\u6ca1\u6709\u751a\u4e48\u5429\u5490\uff0c\u6211\u53ef\u6709\u4e8b\uff0c\u8981\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u5ffd\u7136\u5e73\u5b9a\u4e86\u60c5\u7eea\u3002 \u3000\u3000\u5979\u8981\u5e73\u5b9a\u60c5\u7eea\u7684\u65f6\u5019\uff0c\u672c\u6765\u6ce2\u6f9c\u8d77\u4f0f\u7684\u60c5\u7eea\uff0c\u5c31\u7a81\u7136\u5e73\u5b9a\u4e0b\u6765\u4e86\uff0c\u4f7f\u81ea\u5df1\u5728\u611f\u60c5\u7684\u6ce2\u6d9b\u4e2d\u5e73\u9759\u4e0b\u6765\uff0c\u4e0d\u662f\u4ef6\u6613\u4e8b\uff0c\u5947\u602a\u7684\u662f\uff0c\u611f\u60c5\u8106\u5f31\u7684\u5973\u5b50\uff0c\u5374\u5f80\u5f80\u505a\u5f97\u66f4\u52a0\u51b3\u7136\u3002 \u3000\u3000\u5979\u8bf4\uff1a\u201c\u65b9\u516c\u5b50\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u6211\u662f\u5170\u4ead\u6c60\u5bb6\u7684\u5927\u592b\u4eba\uff0c\u5f53\u7136\u4e5f\u77e5\u9053\u6211\u4eca\u665a\u7684\u6765\u610f\u4e86\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5012\u6ca1\u6709\u60f3\u5230\u989c\u5915\u5e73\u9759\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u773c\u8272\u63a9\u6291\u4e0d\u53bb\u4e00\u62b9\u5931\u843d\uff1a\u201c\u4f60\u8981\u6211\u52a0\u5165\u6c60\u5bb6\uff0c\u4e3a\u4f60\u4eec\u6548\u547d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d\u989c\u5915\u201c\u6062\u590d\u201d\u5f97\u4ee4\u4eba\u610f\u5916\u7684\u5feb\uff0c\u201c\u662f\u8bf7\u4f60\u5f15\u5bfc\u6211\u4eec\u6c60\u5bb6\uff0c\u8d70\u5411\u660c\u76db\u6b63\u9014\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u662f\u4f60\u4eec\u6c60\u5bb6\u7684\u4e8b\uff0c\u6211\u6ca1\u6709\u5174\u8da3\uff0c\u4e5f\u4e0d\u60f3\u5377\u5165\u6c5f\u6e56\u662f\u975e\u91cc\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u5df2\u5377\u5165\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u62bd\u8eab\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8eab\u5728\u6d1b\u9633\uff0c\u600e\u53ef\u4e0d\u7ba1\u6d1b\u9633\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u51b3\u7136\u9053\uff1a\u201c\u6211\u660e\u5929\u5c31\u8981\u79bb\u5f00\u6d1b\u9633\u3002\u201d \u3000\u3000\u989c\u5915\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u8d70\uff1f\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd8\u6ca1\u89c1\u5230\u4f60\u4e4b\u524d\uff0c\u5df1\u4e0b\u4e86\u8fd9\u4e2a\u51b3\u5fc3\u3002\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u73b0\u5728\u89c1\u5230\u4e86\u4f60\uff0c\u4ecd\u662f\u8fd9\u4e2a\u51b3\u5b9a\u3002\u201d \u3000\u3000\u989c\u5915\u82e6\u6da9\u7684\u4e00\u7b11\uff1a\u201c\u4f60\u5c31\u4e0d\u80af\u4e3a\u6211\u6539\u53d8\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u201c\u6211\u4e00\u751f\u90fd\u4e3a\u4f60\u6539\u53d8\u4e86\uff0c\u6211\u73b0\u5728\u4e0d\u60f3\u518d\u4e3a\u4f60\u4f5c\u4efb\u4f55\u6539\u53d8\u3002\u201d\u65b9\u90aa\u771f\u671b\u7740\u6708\u8272\u9053\uff0c\u201c\u4f55\u51b5\uff0c\u4e0d\u662f\u4f60\u81ea\u5df1\u5728\u8981\u6c42\u6211\uff0c\u800c\u662f\u4f60\u4e3a\u4e86\u6c60\u5bb6\uff0c\u624d\u4f1a\u6c42\u6211\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u4e00\u53e5\u5730\u63a5\u9053\uff1a\u201c\u4f60\u4e00\u5411\u90fd\u4e0d\u662f\u4e2a\u80af\u6c42\u4eba\u7684\u5973\u5b50\uff0c\u4e00\u5411\u90fd\u4e0d\u662f\uff0c\u4e00\u751f\u90fd\u4e0d\u662f\u3002\u201d \u7b2c\u5341\u4e03\u7ae0\u3000\u661f\u661f\xb7\u6676\u6676 \u3000 \u3000\u3000\u65b9\u90aa\u771f\u8bf4\u5b8c\u4e86\u90a3\u53e5\u8bdd\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u770b\u5230\u4ed6\u8f6c\u8eab\u800c\u53bb\uff0c\u989c\u5915\u60f3\u5524\u4f4f\u4ed6\uff0c\u5374\u6210\u4e86\u4e00\u4e2a\u5343\u547c\u4e07\u5524\u7684\u65e0\u58f0\u3002\u8981\u7559\u4f4f\u4e00\u4e2a\u4eba\uff0c\u9700\u8981\u7406\u7531\uff0c\u989c\u5915\u89c9\u5f97\u81ea\u5df1\u6ca1\u6709\u7406\u7531\uff0c\u800c\u4e14\uff0c\u4e5f\u5931\u53bb\u4e86\u7406\u7531\u3002 \u3000\u3000\u989c\u5915\u5ffd\u7136\u60f3\u5230\u4e86\u4e00\u4e2a\u770b\u6765\u5408\u7406\u7684\u7406\u7531\u3002 \u3000\u3000\u201c\u4f60\u53d7\u4f24\u4e86\u3002\u201d\u5979\u671b\u7740\u65b9\u90aa\u771f\u8863\u886b\u4e0a\u7684\u8840\u8ff9\uff0c\u627e\u5230\u8fd9\u4e2a\u201c\u540d\u6b63\u8a00\u987a\u201d\u7684\u7406\u7531\uff0c\u201c\u77f3\u65ad\u7709\u662f\u5999\u624b\u5802\u7684\u4eba\uff0c\u4f60\u8fd9\u6837\u8d70\uff0c\u5f88\u5371\u9669\uff0c\u4f55\u4e0d\u5230\u5170\u4ead\u53bb\uff0c\u5148\u628a\u4f24\u517b\u597d\u518d\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u4eca\u665a\u624d\u5f00\u59cb\u53d7\u4f24\u7684\u3002\u201d\u65b9\u90aa\u771f\u4f9d\u7136\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u989c\u5915\u5f53\u7136\u4e5f\u542c\u5f97\u61c2\u4ed6\u90a3\u53e5\u8bdd\u7684\u610f\u601d\u3002 \u3000\u3000\u2014\u2014\u6bd4\u8d77\u5f53\u5e74\u81ea\u5df1\u5bf9\u4ed6\u7684\u4f24\u5bb3\uff0c\u4ed6\u73b0\u5728\u8eab\u4e0a\u7684\u90a3\u4e00\u70b9\u4f24\uff0c\u7740\u5b9e\u4e0d\u7b97\u751a\u4e48\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u5927\u6b65\u8de8\u8fc7\u53bb\uff0c\u4ed6\u8981\u622a\u4f4f\u65b9\u90aa\u771f\uff0c\u4e00\u9762\u5411\u989c\u5915\u9053\uff1a\u201c\u5927\u592b\u4eba\uff0c\u8981\u4e0d\u8981\u628a\u8fd9\u53ae\u7559\u4f4f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\uff0c\u201d\u989c\u5915\u75b2\u4e4f\u7684\u6447\u9996\u9053\uff1a\u201c\u4ed6\u8981\u8d70\uff0c\u4fbf\u8c01\u4e5f\u7559\u4e0d\u4f4f\u7684\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u5782\u4e0b\u4e86\u624b\uff0c\u5fc3\u6709\u4e0d\u7518\u7684\u77aa\u7740\u65b9\u90aa\u771f\u3002 \u3000\u3000\u6708\u8272\u4e0b\uff0c\u7b80\u8fc5\u5728\u8896\u624b\u770b\u7740\uff0c\u50cf\u4e00\u5934\u6708\u4e0b\u6e29\u6587\u7684\u8c79\u5b50\u3002 \u3000\u3000\u82b1\u6cbe\u5507\u5374\u4e0d\u5728\u4e86\u3002 \u3000\u3000\u2014\u2014\u5979\u662f\u56e0\u4e3a\u7f9e\u6124\uff0c\u8fd8\u662f\u56e0\u4e3a\u4e0d\u60f3\u5728\u8fd9\u91cc\u591a\u5446\u7247\u523b\uff1f\u6291\u6216\u662f\u53e6\u6709\u4efb\u52a1\uff0c\u6240\u4ee5\u624d\u6ca1\u7559\u5728\u8fd9\u513f\uff1f \u3000\u3000\u989c\u5915\u6ca1\u6709\u7559\u4f4f\u65b9\u90aa\u771f\u3002 \u3000\u3000\u5f53\u5979\u89c1\u5230\u8fd9\u4e2a\u4eba\u4fbf\u662f\u65b9\u90aa\u771f\u7684\u65f6\u5019\uff0c\u5979\u5c31\u77e5\u9053\u8fd9\u662f\u4e2a\u7559\u4e0d\u4f4f\u7684\u4eba\u3002 \u3000\u3000\u6b63\u5982\u5f53\u5e74\u4ed6\u4e5f\u6ca1\u6709\u7559\u5f97\u4f4f\u5979\u4e00\u6837\u3002 \u3000\u3000\u5979\u79bb\u5f00\u4ed6\u7684\u65f6\u5019\uff0c\u5979\u4ee5\u4e3a\u81ea\u5df1\u96be\u4ee5\u6d3b\u5f97\u4e0b\u53bb\uff0c\u65b9\u90aa\u771f\u4e5f\u518d\u96be\u6709\u5feb\u4e50\u3002 \u3000\u3000\u53ef\u662f\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5979\u8fd8\u662f\u6d3b\u4e86\u4e0b\u6765\u3002\u800c\u4e14\uff0c\u53ea\u8981\u81ea\u5df1\u4e0d\u4e3b\u52a8\u5730\u8bb0\u8d77\u5f80\u4e8b\uff0c\u5176\u5b9e\u6d3b\u5f97\u5e76\u4e0d\u82e6\uff0c\u4e00\u6837\u53ef\u4ee5\u6b22\u6109\u3002\u4e00\u822c\u6b63\u5e38\u7684\u4eba\u751f\u91cc\uff0c\u53ea\u8981\u4e0d\u53bb\u96be\u4e3a\u81ea\u5df1\uff0c\u5b9e\u9645\u4e0a\u4e5f\u6ca1\u6709\u5927\u591a\u7684\u4e8b\u60c5\u53ef\u4ee5\u96be\u4e3a\u81ea\u5df1\u3002\u8861\u91cf\u51fa\u751a\u4e48\u4e8b\u60c5\u662f\u81ea\u5df1\u53ef\u4e3a\u7684\u3001\u751a\u4e48\u4e8b\u60c5\u662f\u4e0d\u53ef\u4e3a\u7684\uff0c\u60f3\u8be5\u60f3\u7684\u4e8b\u3001\u4e0d\u60f3\u4e0d\u8be5\u60f3\u7684\u4e8b\uff0c\u6bcf\u5929\u7ed9\u81ea\u5df1\u4e00\u4e2a\u5c0f\u6210\u529f\uff0c\u53ef\u662f\u5e76\u4e0d\u8d2a\u529f\uff0c\u8ddf\u8eab\u8fb9\u7684\u4eba\u76f8\u5904\u6109\u5feb\uff0c\u5e73\u5e38\u4eba\u4fbf\u53ef\u4ee5\u81ea\u6c42\u591a\u798f\u3002 \u3000\u3000\u4e0d\u662f\u5e73\u5e38\u4eba\u5219\u4e0d\u540c\uff0c\u547d\u8fd0\u4f1a\u8feb\u4f7f\u4ed6\u4eec\u8d70\u5411\u4e0d\u4e00\u5b9a\u662f\u4ed6\u4eec\u610f\u613f\u7684\u591a\u98ce\u591a\u6d6a\u7684\u8def\u3002 \u3000\u3000\u6709\u6bb5\u65f6\u5019\uff0c\u5979\u56e0\u4e3a\u65a9\u65ad\u4e86\u8fd9\u6bb5\u60c5\uff0c\u4ee5\u4e3a\u4e00\u8f88\u5b50\u90fd\u96be\u4ee5\u5fd8\u8bb0\uff0c\u4e0d\u8fc7\uff0c\u591a\u5e74\u4e0b\u6765\uff0c\u5979\u660e\u767d\u4e86\u7684\u786e\u662f\u7ec8\u751f\u4e0d\u5fd8\uff0c\u4f46\u65e0\u6cd5\u76f8\u5fd8\u4e0d\u7b49\u4e8e\u81ea\u5df1\u4e0d\u4f1a\u6709\u65b0\u7684\u8bb0\u5fc6\u3002 \u3000\u3000\u5979\u60f3\u8fc7\u6b7b\uff0c\u4f46\u4eba\u53ea\u8981\u90a3\u4e00\u6bb5\u60f3\u4e0d\u5f00\u7684\u65f6\u5019\u80fd\u6491\u8fc7\u6765\uff0c\u4fbf\u53ef\u4ee5\u6d3b\u5f97\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u77e5\u9053\u4ed6\u6068\u5979\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u4ed6\u4e86\u89e3\u81ea\u5df1\u7684\u82e6\u8877\u5417\uff1f \u3000\u3000\u989c\u5915\u5728\u56de\u5170\u4ead\u7684\u8def\u4e0a\uff0c\u5750\u5728\u6447\u6643\u7684\u8f7f\u5b50\u91cc\uff0c\u89c9\u5f97\u81ea\u5df1\u4eff\u4f5b\u662f\u4e00\u4e2a\u6ca1\u6709\u9b42\u9b44\u7684\u5e7d\u7075\uff0c\u5143\u795e\u5df2\u4e0d\u77e5\u8dcc\u8361\u5230\u54ea\u91cc\u53bb\u3002 \u3000\u3000\u8bb0\u5fc6\u65f6\u91cc\u7684\u4e16\u754c\u548c\u672a\u6765\u7684\u4e16\u754c\u90fd\u4e00\u6837\uff0c\u4e00\u4e2a\u53ea\u80fd\u5728\u56de\u60f3\u65f6\u611f\u53d7\uff0c\u4e00\u4e2a\u53ea\u80fd\u5728\u60f3\u8c61\u4e2d\u63e3\u6d4b\uff0c\u53ef\u662f\uff0c\u53ea\u6709\u73b0\u5728\u7684\u4e00\u5207\uff0c\u624d\u662f\u771f\u771f\u6b63\u6b63\u7684\u5b58\u5728\uff0c\u800c\u4e14\u6b63\u5f71\u54cd\u7740\u8fc7\u53bb\u3001\u6539\u53d8\u7740\u672a\u6765\u3002 \u3000\u3000\u521a\u624d\u65b9\u90aa\u771f\u6240\u770b\u89c1\u7684\u4eba\uff0c\u771f\u6b63\u624d\u662f\u6539\u53d8\u4ed6\u7684\u5fc3\u5883\u3001\u5f71\u54cd\u4ed6\u7684\u4fe1\u5ff5\u3001\u7c89\u788e\u4e86\u4ed6\u7684\u61a7\u61ac\u7684\u5973\u5b50\u3002 \u3000\u3000\u4ed6\u79bb\u5f00\u4e86\u6cd5\u95e8\u5bfa\uff0c\u6ca1\u6709\u7acb\u5373\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u50cf\u4e2a\u5931\u53bb\u8eaf\u58f3\u7684\u9b42\u9b44\uff0c\u5230\u5904\u95f2\u8361\u7740\uff0c\u76f4\u81f3\u6708\u6e10\u897f\u6c89\uff0c\u4ed6\u624d\u56de\u5230\u8305\u820d\u3002 \u3000\u3000\u4ed6\u662f\u5343\u5934\u4e07\u7eea\uff0c\u4f46\u51b3\u5b9a\u53ea\u6709\u4e00\u4e2a\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u51c6\u5907\u5148\u79bb\u5f00\u6d1b\u9633\u518d\u8bf4\u3002 \u3000\u3000\u56e0\u4e3a\u5bf9\u4ed6\u800c\u8a00\uff0c\u6d1b\u9633\u5df2\u4e0d\u6b62\u662f\u4e00\u4e2a\u662f\u975e\u5730\uff0c\u800c\u4e14\u8fd8\u662f\u4e00\u4e2a\u4f24\u5fc3\u5730\u3002 \u3000\u3000\u5728\u8fd9\u4e2a\u5730\u65b9\uff0c\u4e0d\u7ba1\u505a\u751a\u4e48\u4e8b\uff0c\u53ef\u80fd\u90fd\u4f1a\u5f15\u8d77\u662f\u975e\uff1b\u65e0\u8bba\u4e0b\u600e\u6837\u7684\u51b3\u5b9a\uff0c\u90fd\u6559\u4eba\u60c5\u4ee5\u4f55\u582a\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u79bb\u5f00\u3002 \u3000\u3000\u79bb\u5f00\u4e86\u518d\u8bf4\u3002 \u3000\u3000\u2014\u2014\u5728\u79bb\u5f00\u524d\uff0c\u4ed6\u8981\u5148\u8d74\u4e00\u8d9f\u76f8\u601d\u6797\u3002 \u3000\u3000\u4ed6\u867d\u7136\u8ddf\u8ffd\u547d\u5e76\u6ca1\u6709\u6df1\u4ea4\uff0c\u53ef\u662f\u4ed6\u4e5f\u4e0d\u5e0c\u671b\u8fd9\u4e2a\u4eba\u88ab\u4e03\u53d1\u7985\u5e08\u7684\u888b\u5b50\u7f69\u4f4f\u4e86\u5934\u3001\u8521\u65cb\u949f\u7684\u5251\u523a\u7a7f\u4e86\u5fc3\u810f\u3001\u65ad\u7709\u77f3\u7684\u94a2\u53c9\u53c9\u4f4f\u4e86\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u8ddf\u65ad\u7709\u77f3\u4ea4\u8fc7\u624b\u3002 \u3000\u3000\u4ea4\u624b\u4ec5\u4e00\u62db\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u62db\u91cc\u4ed6\u5df2\u5f88\u6e05\u695a\u7684\u77e5\u9053\uff1a\u65ad\u7709\u77f3\u662f\u4e2a\u53ef\u6015\u7684\u6740\u624b\uff0c\u8ffd\u547d\u8981\u72ec\u529b\u5e94\u4ed8\u4ed6\u4e5f\u9887\u8d39\u5468\u7ae0\uff0c\u82e5\u518d\u52a0\u4e0a\u8521\u65cb\u949f\u548c\u4e03\u53d1\u5927\u5e08\uff0c\u5c31\u7b97\u662f\u201c\u56db\u5927\u540d\u6355\u201d\u4e2d\u7684\u5927\u5e08\u5144\u201c\u65e0\u60c5\u201d\u4eb2\u81f3\uff0c\u4e5f\u4e00\u6837\u5e94\u4ed8\u4e0d\u6765\u3002 \u3000\u3000\u4ed6\u53ef\u4e0d\u60f3\u8ffd\u547d\u80e1\u91cc\u80e1\u6d82\u5c31\u6b7b\u5728\u6d1b\u9633\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u8fd9\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u6709\u7684\u4eba\u76f8\u4ea4\u867d\u6d45\uff0c\u76f8\u77e5\u5374\u6df1\u3002 \u3000\u3000\u4ed6\u66f4\u5e0c\u671b\u8ffd\u547d\u80fd\u4fa6\u7834\u5b5f\u968f\u56ed\u7684\u8840\u6848\u3002 \u3000\u3000\u5b5f\u968f\u56ed\u662f\u4e2a\u6e05\u5ec9\u6b63\u76f4\u7684\u597d\u5b98\uff0c\u4ed6\u88ab\u653e\u9010\uff0c\u5df2\u662f\u5929\u9053\u4e0d\u516c\uff0c\u66f4\u4f55\u51b5\u5728\u5145\u519b\u7684\u8def\u4e0a\u5168\u5bb6\u88ab\u6740\uff0c\u5982\u679c\u201c\u56db\u5927\u540d\u6355\u201d\u4e0d\u4e3b\u52a8\u7740\u624b\u8ffd\u67e5\uff0c\u53c8\u6709\u8c01\u6562\u5f97\u7f6a\u8bec\u9677\u5b5f\u592a\u5b88\u7684\u52bf\u529b\uff0c\u5f00\u7f6a\u8bb8\u591a\u63e1\u6709\u91cd\u6743\u7684\u671d\u5ef7\u547d\u5b98\uff0c\u751a\u81f3\u4e8e\u5192\u88ab\u6b66\u6797\u540c\u9053\u3001\u9ed1\u767d\u4e8c\u9053\u4eba\u7269\u72d9\u6740\u7684\u5947\u9669\uff0c\u6765\u529e\u7406\u8fd9\u4ef6\u65e0\u5934\u8840\u6848\uff1f \u3000\u3000\u6c5f\u6e56\u4e0a\uff0c\u6709\u4e9b\u4e8b\uff0c\u53ea\u8981\u5984\u63d2\u4e00\u8db3\uff0c\u4fbf\u6709\u6740\u8eab\u4e4b\u5371\uff1b\u5b98\u9014\u4e0a\uff0c\u4e00\u6837\u6709\u7684\u662f\u98ce\u6ce2\u9669\u6076\uff0c\u53ea\u8981\u5984\u53c2\u4e00\u8bed\uff0c\u5f88\u5bb9\u6613\u4fbf\u906d\u6765\u706d\u95e8\u4e4b\u7978\u3002 \u3000\u3000\u201c\u56db\u5927\u540d\u6355\u201d\u66fe\u53d7\u5929\u5b50\u5fa1\u5c01\uff0c\u53ef\u4e0d\u5fc5\u7980\u660e\u6c42\u6279\u5dde\u53bf\u5730\u65b9\u5b98\u5458\uff0c\u5373\u53ef\u7740\u624b\u660e\u67e5\u6697\u8bbf\uff0c\u5fc5\u8981\u65f6\u5c31\u5730\u7f09\u72af\u3001\u683c\u6740\u51f6\u5f92\uff0c\u5982\u679c\u8ffd\u547d\u90fd\u67e5\u4e0d\u51fa\u8fd9\u4ef6\u6848\u5b50\uff0c\u6216\u9047\u5230\u4e86\u751a\u4e48\u4e0d\u6d4b\uff0c\u5b5f\u968f\u56ed\u6848\u66f4\u52a0\u6c89\u51a4\u83ab\u96ea\u4e86\u3002 \u3000\u3000\u65b9\u90aa\u771f\u60f3\u8d77\u5f53\u65e5\u81ea\u5df1\u4e5f\u66fe\u4e0e\u5b5f\u968f\u56ed\u6709\u8fc7\u201c\u6e0a\u6e90\u201d\uff0c\u53d7\u8fc7\u4ed6\u7684\u201c\u6069\u60e0\u201d\uff0c\u4ed6\u5f53\u7136\u5e0c\u671b\u4e5f\u5c3d\u4e00\u5206\u529b\uff1a\u2014\u2014\u53ef\u662f\u6d1b\u9633\u4e0d\u53ef\u7559\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u518d\u63d2\u624b\u6c5f\u6e56\u4e8b\u3002 \u3000\u3000\u2014\u2014\u53ea\u5e0c\u671b\u8ffd\u547d\u80fd\u987a\u5229\u7834\u6848\u3002 \u3000\u3000\u6545\u6b64\u4ed6\u51b3\u5b9a\u5728\u672a\u79bb\u5f00\u6d1b\u9633\u524d\uff0c\u5148\u53bb\u5c0f\u78a7\u6e56\u76f8\u601d\u6797\u770b\u770b\uff0c\u800c\u4ed6\u4eca\u665a\uff0c\u5c31\u8981\u5411\u8001\u7239\u548c\u5c0f\u5f1f\u544a\u522b\u3002 \u3000\u3000\u2014\u2014\u8001\u7239\u548c\u5c0f\u5f1f\u60f3\u5fc5\u90fd\u7761\u7740\u4e86\uff0c\u8fd9\u6837\u5524\u9192\u4ed6\u4eec\uff0c\u5c82\u4e0d\u8ba9\u4ed6\u4eec\u53d7\u5230\u60ca\u5413\u3001\u611f\u5230\u6653\u5bd2\uff1f\u4e0d\u5982\u7b49\u65e5\u51fa\u518d\u8bf4\u7f62\u3002\u4e0d\u8fc7\uff0c\u65e5\u51fa\u7684\u65f6\u5019\uff0c\u81ea\u5df1\u5c31\u8981\u79bb\u5f00\u4e86\uff0c\u8d76\u5230\u5c0f\u78a7\u6e56\u53bb\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51b3\u5b9a\u53ea\u7559\u4e0b\u4e66\u67ec\uff0c\u7980\u544a\u8001\u7236\uff0c\u4ee5\u8ba9\u4ed6\u91ca\u6000\u5c31\u662f\u4e86\u3002 \u3000\u3000\u5f53\u9762\u544a\u522b\uff0c\u53ef\u80fd\u53ea\u60f9\u4f24\u60c5\u3002 \u3000\u3000\u7559\u4e0b\u5b57\u6761\uff0c\u53cd\u800c\u53ef\u4f5c\u4e3a\u201c\u8bc1\u636e\u201d\uff0c\u4ed6\u65e5\u63a8\u8f9e\u82e6\u7f20\u4e0d\u4f11\u7684\u201c\u6d1b\u9633\u56db\u516c\u5b50\u201d\u65f6\uff0c\u4e5f\u597d\u6709\u4e2a\u4ea4\u4ee3\u3002 \u3000\u3000\u65b9\u90aa\u771f\u51b3\u5b9a\u6084\u6084\u79bb\u53bb\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u56de\u5230\u8305\u820d\u7684\u65f6\u5019\uff0c\u5c31\u76ee\u7779\u4ed6\u4e00\u751f\u4eba\u91cc\uff0c\u6700\u5f71\u54cd\u4ed6\u7684\u8fc7\u53bb\u3001\u6539\u53d8\u4ed6\u7684\u672a\u6765\u3001\u7c89\u788e\u4e86\u4ed6\u7684\u4e00\u5207\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u8305\u820d\u91cc\u4e00\u7247\u51cc\u4e71\u3002 \u3000\u3000\u95e8\u5df2\u5012\u584c\uff0c\u7af9\u7bf1\u4ea6\u88ab\u63a8\u5012\uff0c\u65b9\u7075\u7626\u5f31\u7684\u8eab\u5b50\u6302\u5728\u524a\u5c16\u7684\u7af9\u7bf1\u4e0a\uff0c\u81f3\u5c11\u6709\u4e03\u516b\u652f\u7af9\u5df2\u523a\u7a7f\u4e86\u4ed6\u7684\u8eab\u5b50\uff0c\u663e\u7136\u662f\u5728\u4ed6\u7ffb\u7bf1\u8981\u9003\u8d70\u65f6\uff0c\u51f6\u624b\u628a\u4ed6\u7626\u5c0f\u7684\u8eab\u5b50\u5927\u529b\u538b\u5728\u7af9\u5c16\u4e0a\uff0c\u8840\u6ce8\u5165\u7af9\u91cc\uff0c\u6ce5\u571f\u90fd\u51dd\u6210\u7600\u7ea2\u7684\u56fa\u5757\u3002 \u3000\u3000\u65b9\u90aa\u771f\u773c\u90fd\u7ea2\u4e86\u3002 \u3000\u3000\u4ed6\u51b2\u8fdb\u5c4b\u91cc\u3002 \u3000\u3000\u7136\u540e\u4ed6\u9661\u7136\u9759\u6b62\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u65b9\u8001\u7239\u3002 \u3000\u3000\u65b9\u8001\u7239\u6b7b\u5f97\u6bd4\u65b9\u7075\u66f4\u60e8\u3002 \u3000\u3000\u5c4b\u91cc\u7684\u4e00\u5207\u90fd\u662f\u51cc\u4e71\u7684\uff0c\u6240\u6709\u7684\u8863\u670d\u3001\u6742\u7269\u3001\u519c\u5177\u3001\u684c\u6905\u3001\u7ba7\u67dc\u90fd\u88ab\u6380\u7ffb\uff0c\u65b9\u8001\u7239\u5c31\u5012\u5728\u7076\u9505\u4e0a\uff0c\u9505\u4e0a\u76db\u6ee1\u7740\u6c34\uff0c\u6c34\u8fd8\u5192\u7740\u4f59\u70df\uff0c\u9c9c\u8840\u67d3\u7ea2\u4e86\u4ed6\u767d\u82b1\u82b1\u7684\u80e1\u5b50\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7ea2\u4e86\u773c\uff0c\u51b2\u4e0a\u524d\uff0c\u4f38\u51fa\u624b\uff0c\u89e6\u53ca\u65b9\u8001\u7239\u7684\u5c38\u9996\uff0c\u60f3\u78b0\uff0c\u800c\u53c8\u4e0d\u6562\u78b0\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6307\u5f3a\u70c8\u7684\u9707\u98a4\u7740\uff0c\u4eba\u4e5f\u5728\u98a4\u6296\u7740\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4e24\u4e2a\u5168\u65e0\u58f0\u606f\u7684\u4eba\uff0c\u50cf\u8017\u5b50\u4e00\u822c\u7684\u5728\u8863\u5806\u548c\u6742\u7269\u5806\u91cc\u5192\u73b0\u3002 \u3000\u3000\u4ed6\u4eec\u65e0\u58f0\u65e0\u606f\u7684\u903c\u8fd1\u65b9\u90aa\u771f\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u4e2a\u63d0\u7740\u9554\u94c1\u7985\u6756\uff0c\u6756\u4e0a\u5d4c\u7740\u6212\u5200\uff0c\u662f\u4e24\u79cd\u5947\u95e8\u5175\u5668\u7684\u5408\u58c1\uff1b\u53e6\u4e00\u4e2a\u6267\u4e5d\u8033\u516b\u73af\u952f\u9f7f\u5200\uff0c\u81f3\u5c11\u91cd\u6709\u4e94\u5341\u65a4\uff0c\u5728\u4ed6\u624b\u4e0a\u62ce\u6765\uff0c\u8f7b\u82e5\u65e0\u7269\uff0c\u90fd\u662f\u5947\u95e8\u5175\u5668\u3001\u7edd\u95e8\u6b66\u5668\u3002 \u3000\u3000\u66f4\u53ef\u6015\u7684\u662f\u4ed6\u4eec\u7684\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u4e0d\u9a6c\u4e0a\u4f7f\u7528\u624b\u4e0a\u7684\u5175\u5668\u3002 \u3000\u3000\u800c\u662f\u7528\u7a7a\u7740\u7684\u4e00\u53ea\u624b\uff0c\u4e00\u626c\u4e4b\u4e0b\uff0c\u6253\u51fa\u661f\u661f\u70b9\u70b9\u3002 \u3000\u3000\u4f7f\u6212\u5200\u7684\u6253\u51fa\u7ea2\u661f\u3002 \u3000\u3000\u7528\u5200\u7684\u64a4\u51fa\u84dd\u661f\u3002 \u3000\u3000\u4e00\u5239\u90a3\uff0c\u6ee1\u5929\u661f\uff0c\u4eae\u6676\u6676\u3002 \u3000\u3000\u6ee1\u5730\u661f\u661f\uff0c\u4e5f\u4eae\u6676\u6676\u3002 \u3000\u3000\u4e24\u65c1\u666f\u7269\u5012\u9000\uff0c\u989c\u5915\u89c9\u5f97\u5f88\u60b2\u54c0\u3002 \u3000\u3000\u89c1\u7740\u4e86\u4ed6\uff0c\u624d\u77e5\u9053\u5979\u5728\u8fd9\u51e0\u5e74\uff0c\u5e76\u6ca1\u6709\u5fd8\u8bb0\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u628a\u4ed6\u85cf\u5728\u5fc3\u5e95\u91cc\uff0c\u5fc3\u7075\u91cc\u7684\u4e00\u4e2a\u66f4\u79d8\u5bc6\u7684\u6df1\u5904\uff0c\u4e5f\u8bb8\u53ea\u6709\u5728\u9192\u6765\u4fbf\u5b8c\u5168\u8bb0\u4e0d\u5f97\u7684\u68a6\u91cc\u624d\u4f1a\u6d6e\u73b0\u3002 \u3000\u3000\u5979\u89c9\u5f97\u65b9\u90aa\u771f\u89c1\u7740\u4e86\u5979\uff0c\u7aed\u529b\u8981\u88c5\u5f97\u51b7\u6f20\u9ad8\u505a\uff0c\u4f46\u5176\u5b9e\u5df2\u88ab\u5f7b\u5e95\u51fb\u6bc1\u3001\u5b8c\u5168\u51fb\u788e\u3002 \u3000\u3000\u5979\u591a\u60f3\u544a\u8bc9\u4ed6\uff0c\u5979\u79bb\u5f00\u4ed6\u7684\u771f\u6b63\u539f\u56e0\u3002 \u3000\u3000\u4ed6\u7528\u5f53\u5e74\u5979\u66ff\u4ed6\u88f9\u4f24\u7684\u84dd\u8272\u4e1d\u5dfe\uff0c\u56f4\u5728\u624b\u8155\u4e0a\uff0c\u906e\u53bb\u4e86\u4ed6\u6234\u7740\u5979\u9001\u7684\u7fe0\u7389\u956f\u5b50\u3002 \u3000\u3000\u4ed6\u9001\u7ed9\u5979\u7684\u8776\u8863\uff0c\u5979\u4e00\u76f4\u90fd\u73cd\u85cf\u7740\uff0c\u5f53\u7136\uff0c\u90a3\u662f\u821e\u8e48\u65f6\u7a7f\u7684\u8863\u886b\uff0c\u4e0d\u9002\u5408\u5728\u5e73\u65f6\u7a7f\uff0c\u53ef\u662f\uff0c\u5f53\u65e5\u4ed6\u5728\u5341\u4e07\u5927\u5c71\u529b\u6218\u94c1\u3001\u77f3\u3001\u5fc3\uff0c\u80a0\u56db\u5927\u5251\u624b\u540e\uff0c\u5979\u66ff\u4ed6\u88f9\u4f24\uff0c\u7528\u84dd\u8272\u7684\u4e1d\u5dfe\uff0c\u4ed6\u5374\u66ff\u5979\u63e9\u6c57\uff0c\u7528\u7684\u662f\u65ad\u843d\u7684\u8863\u8896\uff0c\u8fd8\u7b11\u5979\uff1a\u201c\u54ed\u751a\u4e48\uff1f\u6211\u547d\u798f\u4e24\u5927\uff0c\u8fd9\u4e48\u4f24\u8fd8\u6b7b\u4e0d\u4e86\uff0c\u770b\u4f60\u989d\u4e0a\u90fd\u6025\u51fa\u4e86\u6c57\uff01\u201d \u3000\u3000\u5979\u8bb0\u5f97\u5728\u90a3\u65f6\u5019\uff0c\u60f3\uff1a\u54ce\u5440\u4e0d\u597d\u4e86\uff0c\u8ba9\u4ed6\u770b\u89c1\u81ea\u5df1\u6025\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e00\u5b9a\u5f88\u4e0d\u597d\u770b\u7684\u4e86\u2026\u2026\u81ea\u5df1\u8d76\u5feb\u522b\u8fc7\u5934\u53bb\uff0c\u4e0d\u8ba9\u4ed6\u770b\uff0c\u5374\u4e0d\u5c0f\u5fc3\u6309\u5728\u4ed6\u7684\u4f24\u5904\uff0c\u4ed6\u201c\u5537\u201d\u7684\u4e00\u58f0\uff0c\u81ea\u5df1\u5fc3\u90fd\u75bc\u4e86\u3002 \u3000\u3000\u5979\u7a81\u7136\u6380\u5f00\u5e18\u5b50\uff0c\u63a2\u5934\u51fa\u53bb\uff0c\u95ee\u6b63\u7b56\u9a6c\u62a4\u5728\u8f7f\u65c1\u7684\u6d2a\u4e09\u70ed\uff1a\u201c\u4e09\u54e5\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u65b9\u516c\u5b50\u4f4f\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u6123\u4e86\u6123\uff0c\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u600e\u4e48\u8d70\u6cd5\uff1f\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u5927\u58f0\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u6211\u4eec\u5373\u523b\u53bb\u4e00\u8d9f\u3002\u201d \u3000\u3000\u6d2a\u4e09\u70ed\u632f\u594b\u5730\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u53bb\u5bb0\u4e86\u4ed6\uff01\u201d\u5373\u559d\u4ee4\u5269\u4e0b\u7684\u56db\u540d\u624b\u4e0b\uff0c\u6539\u9053\u800c\u884c\u3002 \u3000\u3000\u5728\u8f7f\u5ea7\u8dcc\u8361\u4e4b\u9645\uff0c\u989c\u5915\u9ed8\u9ed8\u81ea\u8896\u4e2d\u62bd\u51fa\u4e86\u4e00\u7247\u767d\u5e03\uff0c\u4e0a\u9762\u8fd8\u9690\u73b0\u51e0\u70b9\u8910\u8272\uff0c\u90a3\u66fe\u7ecf\u662f\u65b9\u90aa\u771f\u6614\u65e5\u9c9c\u7ea2\u7684\u8840\u8ff9\u3002 \u3000\u3000\u2014\u2014\u8fd9\u6b21\u4ed6\u79bb\u5f00\u540e\uff0c\u6211\u5c31\u518d\u4e5f\u89c1\u4e0d\u7740\u4ed6\u4e86\u2026\u2026 \u3000\u3000\u2014\u2014\u5c31\u7b97\u6211\u4e0d\u80fd\u8ba9\u4ed6\u660e\u767d\uff0c\u6211\u4e5f\u8981\u544a\u8bc9\u4ed6\uff0c\u5f53\u65e5\u6211\u4e3a\u751a\u4e48\u8981\u8d70\u2014\u2014 \u3000\u3000\u2014\u2014\u81f3\u5c11\u4e5f\u8981\u8ba9\u4ed6\u660e\u767d\uff0c\u4ed6\u8155\u4e0a\u6234\u7740\u6211\u7684\u7fe0\u7389\u9505\uff0c\u6211\u8896\u4e2d\u4e5f\u85cf\u7740\u4ed6\u7684\u767d\u8896\u886b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5728\u60b2\u6078\u548c\u72c2\u6012\u4e2d\uff0c\u9aa4\u7136\u53d7\u88ad\u3002 \u3000\u3000\u8fd9\u79cd\u7a81\u88ad\uff0c\u7edd\u5bf9\u8981\u6bd4\u4e09\u767e\u652f\u7bad\u4e00\u9f50\u5411\u4ed6\u5c04\u6765\u66f4\u53ef\u6015\u5341\u500d\uff01 \u3000\u3000\u5c31\u7b97\u4ed6\u5728\u60b2\u6124\u4e2d\uff0c\u4e5f\u8bb0\u5f97\u6c5f\u6e56\u4e0a\u6781\u5176\u5389\u5bb3\u7684\u6740\u624b\u7ec4\u7ec7\uff0c\u9664\u4e86\u201c\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\u201d\u4e4b\u5916\uff0c\u8fd8\u6709\u201c\u795e\u4e0d\u77e5\u201d\u3001\u201c\u9b3c\u4e0d\u89c9\u201d\uff0c\u548c\u201c\u6697\u5668\u738b\u201d\u79e6\u70b9\u3001\u4ee5\u53ca\u201c\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u201d\u8fd9\u4e9b\u53ef\u6015\u4eba\u7269\uff01 \u3000\u3000\u201c\u795e\u4e0d\u77e5\u201d\u548c\u201c\u9b3c\u4e0d\u89c9\u201d\u662f\u4e24\u5144\u5f1f\uff0c\u8fd9\u4e24\u4eba\u7684\u8f7b\u529f\u795e\u51fa\u9b3c\u6ca1\uff0c\u4f46\u5404\u884c\u5404\u8def\uff0c\u51b3\u4e0d\u4e92\u52a9\uff0c\u65e2\u662f\u540c\u80de\u5144\u5f1f\uff0c\u4e5f\u662f\u5bf9\u5934\u51a4\u5bb6\uff0c\u5f53\u65e5\u82e5\u4e0d\u662f\u4ed6\u4eec\u4e24\u4eba\u7ef4\u62a4\u8d2a\u5b98\u6076\u5ba6\u5434\u94c1\u7ffc\uff0c\u5c31\u4e0d\u4f1a\u4f7f\u8ffd\u547d\u5927\u8d39\u5468\u7ae0\u3001\u5927\u4f24\u8111\u7b4b\u4e86\u3002\u201c\u6697\u5668\u738b\u201d\u79e6\u70b9\u4e00\u51fa\uff0c\u540d\u6c14\u5df2\u63a9\u76d6\u4e86\u4ee5\u6697\u5668\u6210\u540d\u7684\u8700\u4e2d\u5510\u95e8\uff0c\u4e0e\u65e0\u60c5\u7684\u201c\u660e\u5668\u201d\u6210\u4e3a\u4e8c\u6c34\u5206\u6d41\uff0c\u53cc\u5cf0\u5e76\u5cd9\uff0c\u4e00\u65f6\u745c\u4eae\u3002 \u3000\u3000\u201c\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u201d\u5219\u4e0d\u53ea\u662f\u4e00\u4e2a\u4eba\uff0c\u800c\u662f\u4e00\u7fa4\u4eba\u3002\u4e00\u7fa4\u4eba\u91cc\uff0c\u90fd\u662f\u4e00\u6d41\u6697\u5668\u9ad8\u624b\uff0c\u4e5f\u5404\u7ec3\u5c31\u81ea\u5df1\u7684\u72ec\u95e8\u5175\u5668\uff1b\u4ed6\u4eec\u7684\u6697\u5668\uff0c\u4e00\u5982\u4ed6\u4eec\u7684\u5175\u5668\uff0c\u90fd\u662f\u6cbe\u4e0d\u5f97\u7684\u3002 \u3000\u3000\u8c01\u6cbe\u4e0a\u4e00\u70b9\uff0c\u5c31\u50cf\u88ab\u6700\u6bd2\u7684\u86c7\u54ac\u5728\u773c\u73e0\u5b50\u4e0a\uff1b\u8c01\u60f9\u4e0a\u4ed6\u4eec\u5176\u4e2d\u4e00\u4e2a\uff0c\u5c31\u50cf\u662f\u4ed6\u5934\u57cb\u5728\u9a6c\u8702\u7a9d\u91cc\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\u7684\u6c49\u5b50\uff0c\u867d\u7136\u80c6\u5927\uff0c\u4f46\u8c01\u90fd\u8981\u540d\u3001\u4e5f\u8c01\u90fd\u8981\u547d\u3002 \u3000\u3000\u6240\u4ee5\u8c01\u90fd\u4e0d\u6562\u5f97\u7f6a\u201c\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u201d\u3002 \u3000\u3000\u81f3\u4e8e\u8c01\u662f\u201c\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u201d\u7684\u9886\u8896\uff0c\u8c01\u4e5f\u4e0d\u77e5\u2014\u2014\u6709\u4eba\u7684\u8bf4\u662f\u4e00\u4e2a\u7537\u7684\uff0c\u53eb\u505a\u201c\u6ee1\u5929\u661f\u201d\uff0c\u6709\u7684\u4eba\u8bf4\u662f\u4e00\u4f4d\u5973\u7684\uff0c\u53eb\u505a\u201c\u4eae\u6676\u6676\u201d\u3002 \u3000\u3000\u8c01\u90fd\u4e0d\u77e5\u9053\u4ed6\u4eec\u7a76\u7adf\u662f\u8c01\u3001\u5230\u5e95\u662f\u8c01\u3002 \u3000\u3000\u53ef\u662f\u65b9\u90aa\u771f\u5374\u5728\u4ed6\u6700\u60b2\u6078\u7684\u65f6\u5019\uff0c\u9047\u4e0a\u4e86\u88ad\u51fb\u3002 \u3000\u3000\u81f3\u5c11\u6709\u4e24\u4e2a\u201c\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u201d\u3002 \u3000\u3000\u65b9\u90aa\u771f\u970d\u7136\u8fd4\u8eab\u7684\u65f6\u5019\uff0c\u5df2\u770b\u89c1\u6ee1\u5929\u661f\u3002 \u3000\u3000\u4eae\u6676\u6676\u3002 \u3000\u3000\u8d76\u53bb\u5927\u9690\u4e18\u7684\u8f7f\u5b50\u88ab\u622a\u4f4f\uff0c\u98ce\u5439\u6765\u5916\u9762\u7684\u5bf9\u8bdd\u58f0\u3002 \u3000\u3000\u989c\u5915\u4e00\u770b\uff0c\u76ee\u4e0b\u53ea\u89c1\uff0c\u6d2a\u4e09\u70ed\u548c\u6570\u4eba\u6b63\u5728\u5bf9\u8bdd\uff0c\u5176\u4e2d\u4e00\u4e2a\uff0c\u867d\u7136\u795e\u60c5\u6709\u70b9\u7126\u8651\uff0c\u4f46\u8fd9\u6837\u770b\u53bb\uff0c\u8fd8\u662f\u4f18\u96c5\u6587\u79c0\uff0c\u683c\u5916\u7684\u597d\u770b\u3002 \u3000\u3000\u90a3\u516c\u5b50\u4e00\u89c1\u989c\u5915\u63a2\u9996\u51fa\u8f7f\u5916\uff0c\u559c\u5fed\u5fed\u7684\u547c\u9053\uff1a\u201c\u5927\u5ac2\u3002\u201d \u3000\u3000\u989c\u5915\u89c1\u662f\u6c60\u65e5\u66ae\uff0c\u5fc3\u4e2d\u987f\u751f\u4eb2\u5207\u7684\u611f\u89c9\uff0c\u9053\uff1a\u201c\u4f60\u6765\u4e86\uff0c\u6b63\u597d\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u6211\u89c1\u5927\u5ac2\u8fdf\u8fdf\u672a\u5f52\uff0c\u751f\u6050\u51fa\u4e86\u610f\u5916\uff0c\u521a\u597d\u4e03\u53d1\u5927\u5e08\u5df2\u7ecf\u8d76\u5230\uff0c\u6211\u8bf7\u52a8\u4ed6\u4e00\u8d77\u8d76\u6765\u63a5\u5e94\u5927\u5ac2\u2014\u2014\u201d \u3000\u3000\u989c\u5915\u6253\u65ad\u9053\uff1a\u201c\u6211\u6ca1\u4e8b\u3002\u6211\u8981\u8d76\u53bb\u65b9\u5bb6\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5ac2\u5b50\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u5371\u9669\u7684\uff0c\u201d\u989c\u5915\u575a\u6301\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u4ee5\u4e0d\u53bb\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u4ece\u521a\u624d\u6d2a\u4e09\u70ed\u90a3\u756a\u4e0d\u6e05\u4e0d\u695a\u7684\u8f6c\u8ff0\u91cc\uff0c\u4e5f\u7565\u4e86\u89e3\u4e86\u60c5\u5f62\uff0c\u7565\u4e00\u6c89\u541f\uff0c\u5f53\u4e0b\u4fbf\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e0d\u5982\u6211\u4eec\u4e00\u9053\u513f\u53bb\u3002\u201d \u3000\u3000\u989c\u5915\u70b9\u9996\u9053\uff1a\u201c\u90a3\u4e5f\u968f\u4f60\uff0c\u4e0d\u8fc7\uff0c\u6211\u6709\u8bdd\u8981\u8ddf\u65b9\u516c\u5b50\u8bf4\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u4e8b\u60c5\u7531\u5927\u5ac2\u5904\u7406\uff0c\u6211\u4e0d\u8fc7\u95ee\u3002\u201d\u6c60\u65e5\u66ae\u5fd9\u9053\uff0c\u9042\u800c\u5429\u5490\u6d2a\u4e09\u70ed\u8c03\u6d3e\u6765\u63f4\u7684\u4e8c\u5341\u56db\u540d\u5170\u4ead\u5b50\u5f1f\u5f80\u65b9\u5bb6\u8fdb\u53d1\u3002 \u3000\u3000\u989c\u5915\u5fc3\u795e\u4eff\u4f5b\uff0c\u7a81\u7136\u89c9\u5f97\u5728\u9ed1\u5f71\u5e62\u5e62\u4e2d\uff0c\u6709\u4e24\u6735\u706b\u70ac\u822c\u7684\u773c\u795e\u5728\u903c\u89c6\u7740\u81ea\u5df1\u3002 \u3000\u3000\u5979\u4e0d\u7981\u62ac\u5934\u3002 \u3000\u3000\u76ee\u70ac\u9690\u53bb\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u7a7f\u7740\u5927\u7ea2\u888d\u7684\u82e6\u884c\u50e7\uff0c\u989d\u4e0a\u70e7\u7740\u516d\u4e2a\u6212\u75a4\uff0c\u8eab\u6750\u9b41\u68a7\uff0c\u7559\u7740\u77ed\u5982\u677e\u9488\u7684\u8584\u53d1\uff0c\u80cc\u4e0a\u80cc\u4e86\u4e2a\u5927\u53e3\u888b\uff0c\u773c\u775b\u5df2\u671b\u5230\u522b\u5904\u3002 \u3000\u3000\u4ed6\u5c31\u5728\u9ed1\u8863\u9ed1\u9a6c\u9ed1\u62ab\u98ce\u7684\u201c\u9ed1\u65cb\u98ce\u201d\u5c0f\u767d\u7684\u8eab\u8fb9\uff0c\u60f3\u6765\u4ed6\u5c31\u662f\u201c\u767e\u888b\u7ea2\u888d\uff0c\u4e03\u53d1\u5927\u5e08\u201d \u3000\u3000\u4e86\u7f62\u3002\u989c\u5915\u5fd6\u601d\uff1a \u3000\u3000\u600e\u4e48\u4ed6\u521a\u624d\u770b\u81ea\u5df1\u7684\u773c\u795e\u7adf\u5982\u6b64\u6d2a\u70bd\uff1f \u3000\u3000\u8fd9\u65f6\u8f66\u961f\u6d69\u6d69\u8361\u8361\u7684\u51fa\u53d1\u4e86\u3002 \u3000\u3000\u989c\u5915\u6ca1\u6709\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u53ea\u662f\u60f3\u5feb\u4e00\u70b9\u89c1\u5230\u65b9\u90aa\u771f\uff0c\u65e9\u4e00\u4e9b\u8ddf\u4ed6\u8bf4\u660e\u4e00\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u5979\u77e5\u9053\u5728\u4eba\u751f\u91cc\u6709\u4e9b\u4e8b\u60c5\uff0c\u65e0\u8bba\u600e\u4e48\u89e3\u91ca\uff0c\u90fd\u89e3\u91ca\u4e0d\u6e05\u695a\u7684\uff1b\u6709\u4e9b\u4e8b\uff0c\u53ea\u8981\u8fdf\u4e00\u6b65\uff0c\u5373\u6210\u5929\u6daf\uff1b\u53c8\u6709\u4e9b\u4e8b\uff0c\u65e0\u8bba\u4eba\u4e8b\u600e\u4e48\u5b89\u6392\uff0c\u90fd\u80dc\u4e0d\u8fc7\u5929\u610f\u7684\u4e00\u4e2a\u758f\u5931\u3002 \u3000\u3000\u5979\u66f4\u662f\u6025\u6025\u8981\u89c1\u65b9\u90aa\u771f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u62d4\u5251\u3002 \u3000\u3000\u4ed7\u5251\u5f80\u6700\u591a\u201c\u661f\u661f\u201d\u7684\u5730\u65b9\u51b2\u53bb\u3002 \u3000 \u7b2c\u5341\u516b\u7ae0\u3000\u78a7\u5251\u84dd\u661f \u3000 \u3000\u3000\u661f\u661f\u867d\u4eae\uff0c\u4f46\u7167\u4e0d\u6e05\u4eba\u7684\u4e00\u751f\u91cc\u8bb8\u8bb8\u591a\u591a\u7684\u98ce\u971c\u3002 \u3000\u3000\u53ef\u662f\u5f53\u4eba\u751f\u4e16\u76f8\u91cc\u7684\u7e41\u971c\u843d\u5c3d\uff0c\u5269\u4e0b\u7684\u662f\u4e0d\u662f\u53ea\u6709\u661f\u5149\uff0c\u81ea\u90a3\u5929\u7684\u5c3d\u5934\uff0c\u4f9d\u7136\u707f\u4eae\uff1f \u3000\u3000\u90a3\u8000\u707f\u800c\u5bc2\u5bde\u7684\u661f\u5149\u3002 \u3000\u3000\u989c\u5915\u7684\u5fc3\u968f\u661f\u5149\u3002 \u3000\u3000\u66fe\u7ecf\u662f\u90a3\u6735\u661f\u5149\u6e29\u70ed\u4e86\u5979\u7684\u8138\u3001\u5979\u7684\u80f8\u819b\uff1f\u600e\u4e48\u5ffd\u7136\u6e10\u884c\u6e10\u8fdc\u6e10\u65e0\u4e66\uff0c\u5ffd\u7136\u53c8\u5982\u54ab\u5c3a\u5929\u6daf\uff0c\u90a3\u4e48\u8fd1\u5f97\u8d76\u4e0d\u8fd4\uff1f \u3000\u3000\u65b9\u90aa\u771f\u51b2\u5411\u661f\u661f\u3002 \u3000\u3000\u661f\u661f\u56db\u6563\u3002 \u3000\u3000\u5251\u5149\u5374\u5728\u8fd9\u65f6\u5019\u63a0\u8d77\u3002 \u3000\u3000\u6df1\u78a7\u7684\u5251\u5149\uff0c\u4eff\u4f5b\u4e00\u7f15\u9500\u9b42\u3001\u4e00\u62b9\u76f8\u601d\uff0c\u4f46\u8fc5\u5373\u8f6c\u4e3a\u6740\u6c14\u3002 \u3000\u3000\u4e07\u7269\u7686\u6b7b\u7684\u6740\u610f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8fd9\u79cd\u6253\u6cd5\uff0c\u7b80\u76f4\u662f\u8ff9\u8fd1\u75f4\u72c2\uff0c\u800c\u4e14\u4e0d\u8981\u547d\u3002 \u3000\u3000\u8c01\u90fd\u77e5\u9053\u8fd9\u4e9b\u661f\u661f\u90fd\u662f\u6cbe\u4e0d\u5f97\u7684\u3002 \u3000\u3000\u96be\u9053\u65b9\u90aa\u771f\u5728\u8fd9\u4e00\u523b\u91cc\u5df2\u56e0\u60b2\u6012\u800c\u5931\u53bb\u7406\u667a\u3001\u56e0\u4f24\u6124\u800c\u4e71\u4e86\u65b9\u5bf8\uff1f\u8fd8\u662f\u4ed6\u5728\u5343\u94a7\u4e00\u53d1\u91cc\u51b3\u5b9a\u5f80\u6700\u5371\u9669\u91cc\u51b2\uff1f\u4e0d\u5165\u864e\u7a74\u7109\u5f97\u864e\u5b50\uff0c\u53f0\u98ce\u7684\u4e2d\u5fc3\uff0c\u662f\u5e73\u9759\u7684\u98ce\u773c\u3002\u53ea\u6709\u6562\u5f80\u9669\u91cc\u95ef\u7684\u4eba\uff0c\u624d\u80fd\u6e21\u9669\uff1b\u4e0d\u6015\u6b7b\u7684\u4eba\uff0c\u5f80\u5f80\u4e0d\u4f1a\u5148\u6b7b\u3002 \u3000\u3000\u6291\u6216\u662f\u65b9\u90aa\u771f\u5b81\u53ef\u6b7b\uff0c\u4e5f\u4e0d\u80af\u653e\u8fc7\u51f6\u624b\uff1f \u3000\u3000\u4ed6\u7684\u5251\u4e00\u8d77\uff0c\u654c\u4eba\u987b\u3001\u7709\u3001\u8863\u3001\u5c65\u5c3d\u78a7\u3002 \u3000\u3000\u661f\u661f\u90fd\u5438\u5728\u4ed6\u6df1\u78a7\u7684\u5251\u8eab\u4e0a\u3002 \u3000\u3000\u5251\u8272\u66f4\u78a7\u3002 \u3000\u3000\u78a7\u5251\u4e0a\u5d4c\u4e86\u661f\u661f\u70b9\u70b9\uff0c\u95ea\u707f\u7740\u6676\u84dd\u3002 \u3000\u3000\u4ed6\u7684\u5251\u4e00\u6325\u51fa\uff0c\u4e0d\u4f46\u6563\u53d1\u7740\u6781\u5927\u7684\u6740\u4f24\u529b\uff0c\u800c\u4e14\u8fd8\u51dd\u53d1\u51fa\u6781\u5f3a\u7684\u5438\u529b\uff0c\u90a3\u4e9b\u4eae\u5982\u661f\u661f\u7684\u6697\u5668\uff0c\u5982\u94c1\u9047\u78c1\uff0c\u5168\u9ecf\u5728\u4ed6\u7684\u5251\u4e0a\u3002 \u3000\u3000\u53ea\u6709\u4e00\u679a\u4f8b\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u679a\u661f\u661f\uff0c\u81ea\u65b9\u90aa\u771f\u5239\u7136\u8fd4\u8eab\u5e94\u654c\u65f6\uff0c\u624d\u65e0\u58f0\u65e0\u606f\u7684\u95ea\u4e86\u51fa\u6765\uff0c\u6df7\u5728\u90a3\u4e00\u5927\u5806\u661f\u661f\u91cc\uff0c\u95ea\u8000\u7740\u6e05\u7eaf\u7684\u84dd\u5149\uff0c\u5e76\u65e0\u4e1d\u6beb\u7279\u51fa\u7684\u5c04\u5411\u65b9\u90aa\u771f\uff01 \u3000\u3000\u5728\u65b9\u90aa\u771f\u62d4\u5251\u3002\u661f\u661f\u90fd\u50cf\u957f\u9cb8\u5438\u6c34\u4e00\u822c\u8d34\u5728\u78a7\u7eff\u7684\u5251\u8eab\u4e0a\u4e4b\u9645\uff0c\u90a3\u4e00\u70b9\u661f\u661f\uff0c\u7a81\u7136\u52a0\u5feb\u81ea\u4e0a\u800c\u4e0b\uff0c\u98de\u6253\u65b9\u90aa\u771f\u7684\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u9554\u94c1\u7985\u6756\u4e0a\u7684\u6212\u5200\u3001\u4e5d\u8033\u516b\u73af\u952f\u9f7f\u5200\uff0c\u5df2\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6df1\u78a7\u7684\u5251\u66f4\u78a7\u3002 \u3000\u3000\u4e00\u5ba4\u7686\u7eff\u3002 \u3000\u3000\u8840\u6e85\u7ea2\u3002 \u3000\u3000\u8840\u662f\u4ece\u90a3\u4f7f\u4e5d\u8033\u516b\u73af\u952f\u9f7f\u5200\u7684\u6c49\u5b50\u8eab\u4e0a\u6d8c\u51fa\u6765\u7684\uff0c\u4ed6\u6325\u821e\u7740\u5927\u5200\uff0c\u4e00\u8fde\u4e03\u516b\u4e5d\u4e2a\u65cb\u8f6c\uff0c\u98de\u8dcc\u51fa\u7af9\u7bf1\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u8d77\u6765\u3002 \u3000\u3000\u6df1\u78a7\u7684\u5251\u4e00\u6cbe\u4e0a\u4e86\u8840\uff0c\u795e\u5947\u4f3c\u7684\u4eae\u4e3d\u4e86\u8d77\u6765\uff0c\u5982\u679c\u5b83\u672c\u6765\u50cf\u4e00\u4e2a\u72ec\u5b88\u6df1\u95fa\u7684\u5973\u5b50\uff0c\u800c\u4eca\u5c31\u4f3c\u4e00\u4f4d\u5bb9\u5149\u7115\u53d1\u7684\u5c11\u5987\uff01 \u3000\u3000\u53ef\u662f\u90a3\u4e00\u70b9\u661f\u661f\uff0c\u4e5f\u5728\u6b64\u65f6\u98de\u5230\u65b9\u90aa\u771f\u7684\u54bd\u5589\uff0c\u8fd9\u65f6\u8ddd\u79bb\u5df2\u662f\u6781\u8fd1\uff0c\u8fd9\u4e00\u5c0f\u70b9\u201c\u661f\u661f\u201d\uff0c\u9aa4\u7136\u53d1\u51fa\u5c16\u5578\uff0c\u7528\u4e00\u79cd\u5feb\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u901f\u5ea6\u75be\u5c04\u800c\u81f3\uff0c\u800c\u4e14\u84dd\u8292\u9661\u7136\u6210\u4e86\u5389\u8292\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53d1\u89c9\u7684\u65f6\u5019\uff0c\u6697\u5668\u5df2\u8fd1\u54bd\u5589\u4e0d\u53ca\u4e00\u5bf8\u4e03\u5206\uff01 \u3000\u3000\u4ed6\u8fde\u5fd9\u4e00\u4e2a\u5927\u4ef0\u8eab\uff0c\u8170\u810a\u50cf\u9661\u7136\u6298\u65ad\u4e5f\u4f3c\u7684\uff0c\u661f\u661f\u9669\u9669\u64e6\u8fc7\u4ed6\u7684\u54bd\u5589\uff0c\u5fae\u5fae\u5212\u8fc7\u4ed6\u7684\u5de6\u8033\u8033\u5782\uff0c\u98de\u5165\u8305\u5c4b\u5185\u8fdb\u7684\u8336\u7076\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u4f7f\u9554\u94c1\u7985\u6756\u5d4c\u6212\u5200\u7684\u63c9\u8eab\u6251\u6765\uff0c\u60f3\u5728\u6b64\u65f6\u5236\u65b9\u90aa\u771f\u4e8e\u6b7b\u547d\u3002 \u3000\u3000\u65b9\u90aa\u771f\u624b\u4e0a\u7684\u5251\u5374\u7a81\u7136\u78a7\u8292\u5927\u76db\u3002 \u3000\u3000\u672c\u6765\u9ecf\u5728\u5251\u8eab\u4e0a\u7684\u661f\u661f\uff0c\u90fd\u4e00\u9f50\u201c\u70b8\u201d\u4e86\u5f00\u6765\uff0c\u75be\u5c04\u5411\u90a3\u4f7f\u9554\u6756\u6212\u5200\u7684\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u4eba\u5927\u53eb\u3002 \u3000\u3000\u53eb\u58f0\u5145\u6ee1\u4e86\u6050\u60e7\u3002 \u3000\u3000\u4ece\u6765\u53ea\u6709\u4ed6\u7528\u8fd9\u79cd\u6697\u5668\u53bb\u5bf9\u4ed8\u4eba\uff0c\u4ed6\u4e07\u672a\u6599\u5230\u6709\u4e00\u5929\u4ed6\u4f1a\u6210\u4e3a\u88ab\u8fd9\u79cd\u6697\u5668\u5bf9\u4ed8\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u53eb\u4e00\u9762\u9000\uff0c\u53ef\u662f\u6709\u4e24\u679a\u201c\u661f\u661f\u201d\u5df2\u5c04\u5165\u4ed6\u7684\u5634\u91cc\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u5c31\u5931\u53bb\u4e86\u58f0\u97f3\u3002 \u3000\u3000\u800c\u4e14\u8eab\u4e0a\u9a6c\u4e0a\u5d4c\u6ee1\u4e86\u661f\u661f\u3002 \u3000\u3000\u4ed6\u5012\u5728\u5730\u4e0a\uff0c\u5931\u53bb\u4e86\u8bf4\u8bdd\u7684\u80fd\u529b\uff0c\u5931\u53bb\u4e86\u52a8\u5f39\u7684\u80fd\u529b\uff0c\u5931\u53bb\u4e86\u63a7\u5236\u5927\u5c0f\u4fbf\u7684\u80fd\u529b\uff0c\u751a\u81f3\u8fde\u628a\u773c\u76ae\u95ed\u8d77\u6765\u7684\u529b\u91cf\u4e5f\u6d88\u5931\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5f88\u6e05\u695a\uff1a\u8fd9\u4e9b\u80fd\u529b\uff0c\u4ed6\u5df2\u5b8c\u5168\u6d88\u5931\uff0c\u53ef\u662f\u5728\u5341\u4e94\u516d\u5929\u5185\uff0c\u9664\u975e\u6709\u4eba\u628a\u4ed6\u4e00\u5200\u6740\u4e86\uff0c\u5426\u5219\u4ed6\u662f\u7edd\u5bf9\u6b7b\u4e0d\u53bb\u7684\u3002 \u3000\u3000\u9965\u997f\u4e0e\u9965\u6e34\u6839\u672c\u4e0d\u80fd\u4ee4\u4ed6\u81f4\u6b7b\u3002 \u3000\u3000\u5f53\u4ed6\u8eab\u4f53\u5df2\u5931\u53bb\u4e00\u5207\u80fd\u529b\u7684\u65f6\u5019\uff0c\u53ea\u662f\u4e00\u5177\u53d7\u82e6\u7684\u8eaf\u58f3\uff0c\u8fde\u6c34\u548c\u98df\u7269\uff0c\u90fd\u53d8\u5f97\u662f\u591a\u4f59\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u73b0\u523b\u5fc3\u91cc\u7684\u6050\u60e7\uff0c\u8fdc\u6bd4\u6b7b\u4ea1\u8fd8\u751a\u3002 \u3000\u3000\u4ed6\u5012\u5730\u7684\u65f6\u5019\uff0c\u65b9\u90aa\u771f\u5de6\u624b\u5f80\u8033\u5782\u4e00\u6478\uff0c\u8fd8\u597d\uff0c\u8033\u5782\u53ea\u522e\u7834\u4e86\u4e00\u70b9\u70b9\u8868\u76ae\uff0c\u5e76\u6ca1\u6709\u89c1\u8840\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u5251\u5df2\u523a\u5728\u7076\u5e95\u91cc\u3002 \u3000\u3000\u5251\u62d4\u51fa\u6765\u7684\u65f6\u5019\uff0c\u8840\u5c31\u8ddf\u7740\u6fc0\u55b7\u4e0a\u6765\u3002 \u3000\u3000\u7136\u540e\u4ed6\u8fd4\u8eab\u9762\u5bf9\u90a3\u4e00\u675f\u67f4\u85aa\u3002 \u3000\u3000\u67f4\u85aa\u84ec\u7136\u5411\u4ed6\u5288\u5934\u5288\u9762\u7f69\u6765\u3002 \u3000\u3000\u90a3\u6570\u767e\u5341\u6280\u65b0\u780d\u65e7\u4f10\u7684\u67f4\u679d\uff0c\u5168\u6ca1\u5934\u6ca1\u8111\u7684\u6253\u4e86\u4e0b\u6765\uff0c\u91cc\u9762\u8fd8\u631f\u7740\u51e0\u4e2a\u5c0f\u661f\u661f\u3002 \u3000\u3000\u84dd\u8272\u7684\u5c0f\u661f\u661f\u3002 \u3000\u3000\u5e7d\u84dd\u5982\u68a6\u3002 \u3000\u3000\u67f4\u85aa\u91cc\u7684\u4eba\uff1a\u4e00\u63a8\u51fa\u4e86\u90a3\u51e0\u6346\u67f4\u679d\uff1b\u7acb\u523b\u5982\u4e00\u6e9c\u70df\uff0c\u4e00\u4e2a\u65f1\u5730\u62d4\u8471\uff0c\u8f6c\u843d\u98de\u9e70\u640f\u5154\uff0c\u65cb\u5c55\u516b\u6b65\u8d76\u8d61\uff0c\u4e94\u87d2\u7ffb\u8eab\uff0c\u8f7b\u767b\u5de7\u8dc3\uff0c\u4e00\u53e3\u6c14\u65bd\u71d5\u5b50\u98de\u4e91\u7eb5\uff0c\u63a0\u51fa\u8305\u5c4b\uff0c\u76f4\u8981\u98de\u8d8a\u7af9\u7bf1\uff0c\u5ffd\u89c1\u6708\u8272\u4e0b\uff0c\u4e00\u4e2a\u8eab\u7740\u6de1\u7eff\u4e2d\u8863\u7684\u4eba\uff0c\u6301\u7740\u7fe0\u8272\u7684\u5251\uff0c\u5251\u5c16\u659c\u6307\u5730\u4e0a\uff0c\u624b\u8155\u4e0a\u624e\u7740\u6d45\u84dd\u8272\u7684\u4e1d\u4e2d\uff0c\u5fae\u8e59\u7740\u53cc\u7709\uff0c\u6ca1\u6709\u770b\u4ed6\uff0c\u4f46\u80af\u5b9a\u662f\u5728\u9759\u7b49\u4ed6\u98de\u63a0\u51fa\u6765\u3002 \u3000\u3000\u5251\u5bd2\u8db3\u4ee4\u4eba\u68a6\u9192\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5728\u534a\u7a7a\u4e2d\uff0c\u4e00\u9897\u5fc3\u7acb\u65f6\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u65b9\u90aa\u771f\u679c\u7136\u6bd4\u4f20\u8a00\u4e2d\u66f4\u96be\u5bf9\u4ed8\uff01 \u3000\u3000\u65b9\u90aa\u771f\u5636\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u8981\u6740\u6b7b\u6c60\u4eec\uff1f\u201d\u4ed6\u8bf4\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u8eab\u5b50\u5267\u70c8\u5730\u98a4\u6296\u7740\u3002 \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u662f\u4f60\u7684\u4eb2\u4eba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7684\u8eab\u4e86\u98a4\u52a8\u5f97\u66f4\u5389\u5bb3\u4e86\uff1a\u201c\u662f\u8c01\u53eb\u4f60\u8fd9\u6837\u505a\u7684\uff1f\uff01\u201d \u3000\u3000\u90a3\u4eba\u8bf4\uff1a\u201c\u8c01\u7ed9\u94f6\u5b50\uff0c\u6211\u4eec\u5c31\u6740\u8c01\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u731b\u62ac\u5934\uff0c\u773c\u775b\u8d64\u7ea2\uff0c\u5389\u76ee\u82e5\u7535\uff0c\u76ef\u4f4f\u90a3\u4eba\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6ee1\u5929\u661f\u3001\u8fd8\u662f\u4eae\u6676\u6676\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8bed\u97f3\u4f4e\u6c89\u7684\u9053\uff0c\u201c\u6211\u662f\u4f60\u7684\u715e\u661f\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u90a3\u4eba\u5c31\u51fa\u4e86\u624b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u51fa\u4e86\u624b\u3002 \u3000\u3000\u4ed6\u53cd\u624b\u51fa\u5251\uff0c\u523a\u5411\u80cc\u540e\uff01 \u3000\u3000\u540e\u9762\u9661\u53d1\u51fa\u4e00\u58f0\u60e8\u568e\uff01 \u3000\u3000\u6709\u4e00\u4e2a\u4eba\uff0c\u7a84\u8863\u7a84\u8896\u77ed\u886b\u88e4\u3002\u6253\u88f9\u817f\u3001\u6d12\u978b\u3001\u7ee2\u5e15\u5305\u5934\u8499\u8138\uff0c\u4e00\u8eab\u5168\u7f69\u9752\u9ed1\u8272\u7684\u6cb9\u7ef8\u5b50\u5e03\u8863\uff0c\u539f\u5df2\u8d34\u8fd1\u80cc\u540e\u65b9\u90aa\u771f\u56db\u5c3a\u7684\u8ddd\u79bb\uff0c\u6b63\u8981\u52a8\u624b\uff0c\u65b9\u90aa\u771f\u5df2\u4e00\u5251\u523a\u4e2d\u4e86\u4ed6\uff0c\u6ca1\u4eba\u4e86\u4ed6\u9ed1\u9edd\u9edd\u7684\u8863\u5185\uff0c\u968f\u7740\u65b9\u90aa\u771f\u731b\u7136\u62d4\u5251\uff0c\u54e7\u5730\u55b7\u6e85\u51fa\u8840\u73e0\u5b50\uff01 \u3000\u3000\u53ef\u662f\u65b9\u90aa\u771f\u7acb\u65f6\u7535\u611f\u89c9\u5230\u4e00\u80a1\u6781\u4e3a\u53ef\u6016\u7684\u5927\u529b\uff0c\u5f80\u81ea\u5df1\u80cc\u90e8\u88ad\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u7acb\u5373\u77e5\u9053\u81ea\u5df1\u72af\u4e86\u4e2a\u9519\u8bef\u3002 \u3000\u3000\u2014\u2014\u903c\u8fd1\u80cc\u540e\u7684\u4eba\u867d\u662f\u597d\u624b\uff0c\u4f46\u4e0d\u662f\u653b\u51fb\u7684\u4e3b\u529b\u3002 \u3000\u3000\u4ed6\u98de\u8dc3\u800c\u8d77\uff0c\u91d1\u9ca4\u7a7f\u6ce2\u3002\u4e00\u9e64\u51b2\u5929\u540c\u65f6\u5c55\u51fa\uff0c\u4e2d\u9014\u6539\u6362\u8eab\u6cd5\uff0c\u72e1\u5154\u7ffb\u6c99\u3001\u91d1\u8d61\u620f\u6c34\u3001\u8f6c\u9756\u8712\u4e09\u6284\u6c34\uff0c\u5982\u795e\u9f99\u6e38\u7a7a\uff0c\u715e\u662f\u597d\u770b\uff01 \u3000\u3000\u4ed6\u63a0\u8d77\u5f97\u5feb\uff0c\u4f46\u542c\u8f70\u7684\u4e00\u58f0\uff0c\u81ea\u5df1\u539f\u6765\u7ad9\u7684\u5730\u65b9\uff0c\u5df2\u88ab\u51fb\u4e86\u4e00\u4e2a\u5927\u7a9f\u7abf\uff0c\u4ed6\u80cc\u90e8\u7a81\u7136\u4e00\u632b\uff0c\u5fd9\u70bc\u6c14\u8c03\u5143\uff0c\u5fc3\u6f6e\u4e86\u5348\uff0c\u786c\u628a\u4e94\u810f\u53d7\u9707\u4e4b\u4f24\u5f3a\u81ea\u538b\u4e0b\uff0c\u731b\u62ac\u5934\uff0c\u84e6\u89c1\u661f\u661f\u70b9\u70b9\u3002 \u3000\u3000\u661f\u661f\u98d8\u8fc7\uff0c\u4e00\u5171\u4e03\u9897\u3002 \u3000\u3000\u6bcf\u9897\u7531\u5fae\u5149\uff0c\u907d\u53d8\u4f5c\u5389\u8292\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u7136\u77e5\u9053\u8fd9\u65bd\u653e\u201c\u661f\u661f\u201d\u4f5c\u6697\u5668\u7684\u4eba\u662f\u8c01\u4e86\uff01 \u3000\u3000\u2014\u2014\u5728\u201c\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u201d\u7684\u6740\u624b\u7ec4\u7ec7\u91cc\uff0c\u6709\u4e00\u4e2a\u91cd\u8981\u7684\u4eba\u7269\uff0c\u5c31\u53eb\u505a\u201c\u98de\u661f\u5b50\u201d\uff0c\u4ed6\u64c5\u4f7f\u201c\u4e00\u624b\u4e03\u661f\u201d\uff0c\u540d\u4e3a\u201c\u4e03\u661f\u4f34\u6708\u201d\uff0c\u70b8\u5f00\u6765\u5c31\u50cf\u591c\u7a7a\u4e2d\u7684\u70df\u82b1\uff0c\u8db3\u4ee4\u4eba\u201c\u707f\u4eae\u800c\u6b7b\u201d\uff0c\u6839\u672c\u65e0\u4ece\u95ea\u8eb2\u3002 \u3000\u3000\u636e\u8bf4\uff0c\u8fd9\u662f\u98de\u661f\u5b50\u81ea\u8700\u4e2d\u5510\u95e8\u9ad8\u624b\u5510\u6708\u4eae\u7684\u7edd\u95e8\u6697\u5668\u201c\u68a6\u88f3\u201d\u4e2d\u5f97\u6765\u7684\u7075\u611f\uff0c\u518d\u52a0\u4ee5\u6539\u826f\u3001\u7814\u5236\uff0c\u800c\u201c\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u201d\u8fd9\u64c5\u7528\u6697\u5668\u7684\u7ec4\u7ec7\uff0c\u8fd1\u65e5\u6765\u751a\u56a3\u5c18\u4e0a\uff0c\u8fde\u8700\u4e2d\u5510\u95e8\u4e5f\u4e3a\u4e4b\u9eef\u7136\u5931\u8272\u3002 \u3000\u3000\u98de\u661f\u5b50\u5c31\u5728\u773c\u524d\uff0c\u98de\u661f\u4e5f\u903c\u8fd1\u4e86\u9762\u524d\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6ca1\u6709\u9000\u7f29\u7684\u4f59\u5730\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u62fc\uff0c\u62fc\u7740\u6d3b\u547d\u3002 \u3000\u3000\u4ed6\u5411\u5929\u770b\u4e86\u4e00\u773c\uff0c\u5929\u4ecd\u9ed1\u6c89\u4e00\u7247\uff0c\u6668\u98ce\u6c81\u4eba\uff0c\u4ed6\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5251\u4e0a\u7684\u5149\u534e\u7acb\u76db\u3002 \u3000\u3000\u4ed6\u4eff\u4f3c\u4eba\u5251\u5df2\u5408\u800c\u4e3a\u4e00\uff0c\u7efd\u653e\u51fa\u60ca\u4eba\u7684\u6df1\u7eff\uff0c\u9065\u6307\u5411\u98de\u661f\u5b50\uff0c\u4f3c\u662f\u968f\u65f6\u5c31\u8981\u98de\u8eab\u51fa\u88ad\u3002 \u3000\u3000\u98de\u661f\u5b50\u4e00\u89c1\u65b9\u90aa\u771f\u5251\u9065\u6307\u5411\u81ea\u5df1\uff0c\u7acb\u5373\u5168\u795e\u6212\u5907\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u4e03\u9897\u98de\u661f\uff0c\u7acb\u65f6\u7206\u5f00\uff0c\u70b8\u51fa\u661f\u661f\u70b9\u70b9\u3002 \u3000\u3000\u53ef\u662f\uff0c\u8fd9\u4e9b\u5343\u767e\u70b9\u7480\u74a8\u7684\u661f\u82b1\uff0c\u660e\u660e\u8dc3\u7a7a\u800c\u8d77\u6765\uff0c\u5ffd\u6210\u4e86\u65e0\u529b\u7684\u82b1\u74e3\uff0c\u840e\u7136\u843d\u5730\u3002 \u3000\u3000\u56e0\u4e3a\u98de\u661f\u5b50\u7684\u8138\u95e8\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\u88c2\u5f00\uff0c\u88c2\u6210\u4e24\u723f\uff01 \u3000\u3000\u4ed6\u5728\u8fd9\u4e00\u5239\u90a3\u95ee\u5931\u53bb\u4e86\u751f\u547d\u3002 \u3000\u3000\u672c\u6765\u8981\u5168\u9762\u5168\u529b\u653b\u88ad\u7684\u6697\u5668\uff0c\u4e5f\u5931\u53bb\u4e86\u529b\u91cf\uff0c\u7eb7\u7eb7\u843d\u5730\uff0c\u5982\u4e00\u5730\u6b8b\u7ea2\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4eba\u4ecd\u5728\u539f\u5904\u3002 \u3000\u3000\u4ed6\u53ea\u4e0d\u8fc7\u4e3e\u8d77\u4e86\u5251\uff0c\u5251\u4ecd\u79bb\u98de\u661f\u5b50\u5341\u4e00\u5c3a\u4e4b\u9065\uff0c\u6f3e\u8d77\u593a\u76ee\u7684\u5149\u534e\uff0c\u5bf9\u51c6\u98de\u661f\u5b50\u7684\u8138\uff0c\u98de\u661f\u5b50\u5c31\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u751a\u4e48\u7f18\u6545\uff1f \u3000\u3000\u53ea\u542c\u4e00\u9635\u7a00\u843d\u7684\u638c\u58f0\u3002 \u3000\u3000\u201c\u597d\u5251\u3002\u201d\u6709\u4eba\u6c99\u560e\u4ed6\u8bf4\uff1a\u201c\u597d\u5251\u6cd5\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u56de\u5934\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u662f\u521a\u624d\u4e88\u81ea\u5df1\u80cc\u540e\u4e00\u51fb\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8fd9\u4eba\u867d\u4e00\u8bb0\u51fb\u7a7a\uff0c\u81ea\u5df1\u5df1\u98de\u8dc3\u95ea\u8fc7\uff0c\u4f46\u4ecd\u88ab\u638c\u529b\u4f59\u6ce2\u626b\u4e2d\uff0c\u4e94\u810f\u516d\u8151\u51e0\u79bb\u4e86\u4f4d\uff0c\u8fd9\u79cd\u638c\u529b\uff0c\u9664\u4e86\u5f53\u5e74\u201c\u516d\u5206\u534a\u5802\u201d\u91cc\u7684\u96f7\u52a8\u5929\uff0c\u6b66\u6797\u4e2d\u5df2\u6ca1\u6709\u51e0\u4eba\u80fd\u4f7f\u3002 \u3000\u3000\u4ed6\u66f4\u77e5\u9053\u5728\u4ed6\u4e0e\u98de\u661f\u5b50\u5bf9\u5cd9\u7684\u65f6\u5019\uff0c\u53ea\u8981\u8fd9\u4eba\u518d\u51fa\u624b\uff0c\u81ea\u5df1\u5c31\u5f88\u96be\u5728\u88ab\u8f70\u6210\u98de\u7070\u548c\u88ab\u98de\u661f\u9489\u6210\u523a\u732c\u95f4\u4f5c\u51fa\u6289\u62e9\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4eba\u662f\u654c\u662f\u53cb\uff1f \u3000\u3000\u2014\u2014\u82e5\u662f\u53cb\uff0c\u4e3a\u5565\u521a\u624d\u8981\u6697\u7b97\u800c\u9707\u4f24\u81ea\u5df1\uff1f \u3000\u3000\u2014\u2014\u82e5\u662f\u654c\uff0c\u4e3a\u4f55\u9002\u624d\u4ed6\u5168\u529b\u5bf9\u4ed8\u98de\u661f\u5b50\u65f6\uff0c\u4ed6\u53c8\u4e0d\u51fa\u624b\uff1f \u3000\u3000\u65b9\u90aa\u771f\u5374\u786e\u5b9a\u4e86\u4e00\u4ef6\u4e8b\uff1a \u3000\u3000\u65e0\u8bba\u662f\u654c\u662f\u53cb\uff0c\u8fd9\u4eba\u90fd\u6781\u96be\u5bf9\u4ed8\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u7684\u56de\u8fc7\u8eab\u53bb\u3002 \u3000\u3000\u4ed6\u56de\u8eab\u7684\u65f6\u5019\uff0c\u5f88\u8c28\u614e\u3001\u5f88\u5c0f\u5fc3\u3001\u4e5f\u5f88\u6e05\u695a\u4ed6\u7684\u6bcf\u4e00\u4e2a\u7ec6\u5fae\u7684\u52a8\u4f5c\uff0c\u80af\u5b9a\u90fd\u65e0\u7834\u7efd\u53ef\u5bfb\u3002 \u3000\u3000\u9762\u5bf9\u8fd9\u6837\u53ef\u6015\u7684\u4eba\uff0c\u53ea\u8981\u6709\u4e00\u4e1d\u7834\u7efd\uff0c\u90fd\u8db3\u4ee5\u7c89\u8eab\u788e\u9aa8\u3002 \u3000\u3000\u4e5f\u60b2\u6124\u3001\u4ed6\u72c2\u6012\uff0c\u4f46\u4ed6\u8981\u6740\u4eba\uff0c\u8981\u62a5\u4ec7\uff0c\u800c\u4e0d\u662f\u88ab\u4eba\u51fb\u8d25\u88ab\u4eba\u6740\u3002 \u3000\u3000\u8fd9\u70b9\u5f88\u91cd\u8981\u3002 \u3000\u3000\u6240\u4ee5\u4e00\u4e2a\u4eba\u5728\u8d8a\u6124\u6012\u7684\u65f6\u5019\uff0c\u8d8a\u5e94\u8be5\u8981\u51b7\u9759\uff0c\u8d8a\u662f\u91cd\u5927\u7684\u6218\u5f79\uff0c\u8d8a\u8981\u6c89\u7740\uff0c\u5e73\u65f6\u7684\u5927\u98a0\u5927\u72c2\u3001\u5c0f\u60b2\u5c0f\u54c0\uff0c\u5012\u4e0d\u91cd\u8981\uff0c\u662f\u4e0d\u662f\u4e2a\u5e94\u4ed8\u53d8\u5c40\u7684\u4eba\u624d\uff0c\u7aef\u770b\u4ed6\u5728\u5371\u96be\u60ca\u53d8\u65f6\u662f\u4e0d\u662f\u8fd8\u9547\u5b9a\u4ece\u5bb9\u3001\u9047\u53d8\u4e0d\u60ca\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6b64\u523b\u60b2\u3001\u6012\uff0c\u800c\u4e14\u8fd8\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u751a\u81f3\u5728\u8f6c\u8eab\u4e4b\u9645\uff0c\u53ef\u80fd\u56e0\u5185\u810f\u53d7\u4f24\u4e4b\u6545\uff0c\u611f\u89c9\u5230\u6709\u4e00\u70b9\u70b9\u7684\u660f\u7729\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u4e71\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u50cf\u4e07\u6761\u7ede\u7d22\u5728\u7ede\u7f20\uff0c\u5c24\u5176\u662f\u5ff5\u53ca\u8001\u7239\u548c\u5c0f\u5f1f\u4e4b\u60e8\u6b7b\uff0c\u4f46\u4ed6\u5bf9\u654c\u7684\u65f6\u5019\uff0c\u4ecd\u7136\u4e13\u6ce8\u96c6\u4e2d\u3002 \u3000\u3000\u4e16\u4e0a\u8981\u6210\u529f\u7acb\u4e1a\u3001\u5b8c\u6210\u4efb\u4f55\u5927\u4e8b\uff0c\u90fd\u9700\u8981\u4e13\u5fc3\u4e00\u81f4\uff1b\u4e0d\u4e13\u5fc3\uff0c\u5c31\u96be\u6709\u5b8c\u7f8e\u3002 \u3000\u3000\u4ed6\u56de\u8fc7\u8eab\u6765\uff0c\u5c31\u770b\u89c1\u5728\u66d9\u8272\u672a\u73b0\u3001\u6708\u8272\u672a\u843d\u3001\u591c\u8272\u6700\u6d53\u3001\u5bd2\u610f\u6700\u751a\u4e4b\u5904\uff0c\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5de8\u5927\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u5b58\u5728\uff0c\u5c31\u50cf\u4e00\u682a\u795e\u6728\u3002 \u3000\u3000\u4e00\u682a\u88ab\u96f7\u6b9b\u8fc7\u800c\u4e0d\u6b7b\u7684\u795e\u6728\u3002 \u3000\u3000\u201c\u6211\u662f\u56de\u4e07\u96f7\u3002\u201d\u8fd9\u4eba\u4ee5\u6c99\u560e\u7684\u53e3\u97f3\uff0c\u8bf4\u5f97\u5f88\u6162\uff0c\u50cf\u6b8b\u65e7\u800c\u9508\u8680\u7684\u9501\u94fe\u5728\u6c99\u77f3\u5730\u4e0a\u62d6\u66f3\u7740\uff0c\u201c\u6211\u662f\u4f60\u7684\u654c\u4eba\uff0c\u6211\u662f\u6765\u6740\u4f60\u7684\u3002\u201d\u4ed6\u4e00\u5f00\u59cb\u5c31\u8868\u660e\u4e86\u4ed6\u7684\u8eab\u4efd\u3002 \u3000\u3000\u65b9\u90aa\u771f\u770b\u7740\u4ed6\uff0c\u5c31\u50cf\u770b\u7740\u4e00\u8bb0\u60ca\u96f7\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u7b97\u77ee\u5c0f\uff0c\u7ad9\u5728\u4e00\u7fa4\u4eba\u91cc\uff0c\u4ed6\u7edd\u5bf9\u6f47\u6d12\u51fa\u7fa4\u3002 \u3000\u3000\u4f46\u4ed6\u53ea\u53ca\u56de\u4e07\u96f7\u7684\u8179\u90e8\u3002 \u3000\u3000\u529b\u90aa\u771f\u8fd9\u6837\u62ac\u76ee\u671b\u53bb\uff0c\u7adf\u89c9\u5f97\u6709\u4e9b\u6655\u7729\u3002 \u3000\u3000\u4ed6\u7acb\u65f6\u53d1\u95ee\u3002 \u3000\u3000\u95f4\u4e86\u4e00\u53e5\u6700\u91cd\u8981\u7684\u8bdd\u3002 \u3000\u3000\u201c\u6211\u7239\u548c\u5c0f\u5f1f\uff0c\u662f\u4e0d\u662f\u4f60\u6740\u7684\uff1f\u201d \u3000\u3000\u201c\u90fd\u4e00\u6837\u3002\u201d\u56de\u4e07\u96f7\u6c99\u54d1\u7684\u9053\uff0c\u201c\u8c01\u6740\u90fd\u4e00\u6837\uff0c\u4f60\u90fd\u5feb\u8981\u6b7b\u4e86\uff0c\u65e0\u8bba\u8c01\u6740\u6b7b\u4ed6\u4eec\uff0c\u5bf9\u4f60\u800c\u8a00\uff0c\u90fd\u6ca1\u6709\u5206\u522b\u3002\u201d \u3000\u3000\u201c\u53ea\u6709\u4e00\u4ef6\u4e8b\u6709\u5206\u522b\uff0c\u201d\u56de\u4e07\u96f7\u6d53\u6d4a\u7684\u8bed\u97f3\u9053\uff0c\u201c\u4f60\uff0c\u5219\u5fc5\u987b\u7531\u6211\u6740\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u91cd\u590d\uff1a\u201c\u6211\u5fc5\u987b\u4eb2\u624b\u6740\u4f60\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u95ee\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u56de\u4e07\u96f7\u773c\u4e2d\u95ea\u8fc7\u8ddf\u65b9\u90aa\u771f\u8fd1\u4f3c\u7684\u60b2\u6012\uff1a\u201c\u5c0f\u7edd\u662f\u6211\u7684\u5b50\u4f84\uff0c\u6211\u6bd4\u56de\u5802\u4e3b\u66f4\u75bc\u4ed6\u3002\u201d\u4ed6\u54a7\u4e86\u54a7\u5634\uff0c\u50cf\u4e00\u4e2a\u6811\u6d1e\uff0c\u53ea\u5269\u4e0b\u51e0\u53ea\u53c8\u9ec4\u53c8\u9ed1\u7684\u7259\u9f7f\uff1a\u201c\u6211\u770b\u4f60\u4e0d\u987a\u773c\u3002\u51e0\u4e2a\u4e16\u5bb6\u90fd\u5728\u62c9\u62e2\uff0c\u4f60\u8fd8\u771f\u4ee5\u4e3a\u81ea\u5df1\u4e0a\u4e86\u67b6\u5b50\uff0c\u90a3\u91cc\u90fd\u6ca1\u770b\u4e0a\u773c\uff01\u6211\u4eec\u5f53\u5e74\u521b\u5999\u624b\u5802\uff0c\u4e0d\u77e5\u5403\u5c3d\u591a\u5c11\u82e6\uff0c\u53d7\u5c3d\u51e0\u56de\u6c14\uff0c\u624d\u6709\u4eca\u5927\u7684\u5730\u4f4d\uff0c\u4f60\u7b97\u8001\u51e0\uff01\u6211\u5c31\u770b\u4e0d\u8d77\u4f60\uff0c\u6211\u8981\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u7a81\u7136\u53d1\u89c9\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4e00\u4ef6\u6050\u6016\u7684\u4e8b\u3002 \u3000\u3000\u4e00\u4ef6\u4ee4\u4ed6\u51e0\u4e4e\u5d29\u6e83\u3001\u5168\u7136\u4e27\u5931\u5e0c\u671b\u548c\u6218\u5fd7\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u6291\u5236\u81ea\u5df1\uff0c\u5b81\u795e\u9759\u6c14\uff0c\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\uff0c\u53cd\u800c\u95ee\uff1a\u201c\u4f60\u65e2\u8981\u6740\u6211\uff0c\u521a\u624d\u5728\u98de\u661f\u5b50\u5168\u529b\u51fa\u624b\u7684\u65f6\u5019\uff0c\u53c8\u4e0d\u4e0b\u6740\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u8f70\u7740\u4f60\u80cc\u540e\u4e00\u4e0b\u5b50\uff0c\u4f60\u5df2\u53d7\u4e86\u4f24\uff0c\u6211\u4e0d\u4fe1\u4f60\u80fd\u98de\u5f97\u4e0a\u5929\uff1f\u201d\u56de\u4e07\u96f7\u7c97\u949d\u7684\u8138\u5e9e\u5e97\u7136\u4e5f\u95ea\u8fc7\u4e00\u4e1d\u72e1\u733e\u4e4b\u8272\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u4e5f\u4e0d\u559c\u6b22\u98de\u661f\u5b50\uff0c\u518d\u8bf4\uff0c\u6211\u8fd8\u8981\u770b\u770b\u4f60\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u201c\u8bbe\u60f3\u5230\u4f60\u7684\u5251\u6cd5\u5df1\u5230\u4e86\u5341\u6b65\u4ee5\u5916\uff0c\u51dd\u795e\u7834\u7a7a\uff0c\u5251\u6c14\u6740\u4eba\u7684\u5730\u6b65\uff0c\u8fd9\u5e94\u8be5\u5c31\u662f\u4f20\u8bf4\u4e2d\u7684\u2018\u5929\u95ee\u5251\u6cd5\u2019\u7f62\uff1f\u201d\u56de\u4e07\u96f7\u706b\u7ea2\u7684\u773c\u775b\u76ef\u7740\u4ed6\u624b\u4e0a\u7684\u5251\uff0c\u65b9\u90aa\u771f\u624b\u4e0a\u7684\u5251\u5389\u8292\u5df2\u6d88\uff0c\u56de\u590d\u4e86\u539f\u6765\u7684\u6e5b\u78a7\u7684\u989c\u8272\u3002\u201c\u8fd9\u5c31\u662f\u2018\u706d\u9b42\u2019\u5251\u5427\uff01\u4e0d\u6127\u662f\u516b\u5927\u795e\u5175\u4e4b\u4e00\uff0c\u6ca1\u6709\u4e86\u4ed6\uff0c\u4f60\u7684\u2018\u4eba\u95f4\u5251\u6c14\u2019\u52a0\u4e0a\u2018\u5b50\u5348\u5fc3\u6f6e\u5927\u6cd5\u2019\uff0c\u4e5f\u672a\u5fc5\u6709\u8fd9\u6837\u7684\u5a01\u529b\u3002\u201d \u3000\u3000\u4ed6\u773c\u91cc\u5df2\u9732\u51fa\u8d2a\u5984\u4e4b\u8272\uff1a\u201c\u4e0d\u8fc7\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u8fd9\u6837\u7684\u597d\u5251\u5c31\u7b97\u5f52\u6211\u6240\u6709\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u770b\u770b\u81ea\u5df1\u624b\u4e0a\u7684\u5251\uff0c\u53c8\u671b\u671b\u5934\u4e0a\u7684\u5929\uff0c\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u8981\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u56de\u4e07\u96f7\u671b\u7740\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u521a\u624d\u6ca1\u6709\u628a\u63e1\u65f6\u673a\uff0c\u628a\u6211\u6740\u6b7b\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u662f\u4f60\u81ea\u5bfb\u6b7b\u8def\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u65b9\u90aa\u771f\u5c31\u53d1\u52a8\u4e86\u4ed6\u5168\u529b\u5168\u8eab\u5168\u610f\u5168\u795e\u5168\u9762\u7684\u653b\u52bf\u3002 \u3000\u3000\u5411\u56de\u4e07\u96f7\u3002 \u3000 \u7b2c\u5341\u4e5d\u7ae0\u3000\u884c\u96f7\u4e0e\u95ea\u7535 \u3000 \u3000\u3000\u6740\u6b7b\u56de\u4e07\u96f7\uff01 \u3000\u3000\u800c\u4e14\u5fc5\u987b\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u3001\u6700\u5c11\u7684\u65f6\u95f4\u6740\u6b7b\u4ed6\uff01 \u3000\u3000\u81ea\u5df1\u5982\u60f3\u4e0d\u6b7b\uff0c\u5c31\u5fc5\u987b\u8981\u6740\u6b7b\u56de\u4e07\u96f7\u3002 \u3000\u3000\u5c31\u7b97\u81ea\u5df1\u6b7b\u4e86\uff0c\u4e5f\u5fc5\u987b\u8981\u5148\u6740\u56de\u4e07\u96f7\u3002 \u3000\u3000\u56e0\u4e3a\u56de\u4e07\u96f7\u6781\u53ef\u80fd\u5c31\u662f\u6740\u6b7b\u7239\u7239\u548c\u5c0f\u5f1f\u7684\u51f6\u624b\u3002 \u3000\u3000\u6240\u4ee5\u65e0\u8bba\u5982\u4f55\uff0c\u90fd\u8981\u6740\u6389\u56de\u4e07\u96f7\u3002 \u3000\u3000\u53ef\u662f\u81ea\u5df1\u5df2\u6ca1\u6709\u4e86\u65f6\u95f4\u3002 \u3000\u3000\u5929\u65cb\u3001\u5730\u8f6c\u3002 \u3000\u3000\u81f3\u5c11\u6709\u4e09\u4e2a\u56de\u4e07\u96f7\uff0c\u5728\u81ea\u5df1\u8eab\u524d\uff1b\u800c\u56de\u4e07\u96f7\u8d8a\u957f\u8d8a\u9ad8\uff0c\u9ad8\u5927\u5982\u4e54\u6728\uff1b\u96f7\u8f70\u7535\u95ea\uff0c\u4ed6\u4f3c\u4e4e\u5df2\u5316\u6210\u4e86\u65f1\u96f7\uff0c\u4e0d\u65ad\u5730\u8f70\u70b8\u81ea\u5df1\u624b\u4e0a\u7684\u7535\u5251\u3002 \u3000\u3000\u5c31\u50cf\u6709\u4e09\u5341\u5934\u6076\u72fc\uff0c\u6b63\u6512\u5165\u81ea\u5df1\u4e94\u810f\u516d\u8151\u91cc\uff0c\u4e89\u566c\u81ea\u5df1\u7684\u5fc3\u809d\uff1b\u4e94\u5341\u56db\u679a\u94a2\u9488\uff0c\u4e00\u9f50\u81ea\u8033\u819c\u5bf9\u7a7f\uff0c\u4f1a\u5e08\u4e8e\u8111\u95e8\uff1b\u610f\u5fd7\u50cf\u788e\u88c2\u7684\u74f7\u5668\uff0c\u7838\u5f00\u4e03\u5341\u4e09\u7247\uff1b\u773c\u91cc\u7684\u4e16\u754c\uff0c\u5c45\u7136\u80fd\u591f\u770b\u89c1\u516d\u4e2a\u81ea\u5df1\uff0c\u5728\u88ab\u4ece\u4e09\u4e2a\u5e7b\u5316\u6210\u5341\u516d\u4e2a\u7684\u56de\u4e07\u96f7\u8ffd\u6253\uff1b\u800c\u773c\u524d\u4e00\u7247\u706b\u5149\uff0c\u96be\u9053\u81ea\u5df1\u662f\u5904\u8eab\u5728\u9b3c\u706b\u7684\u70bc\u72f1\u91cc\uff1f \u3000\u3000\u56de\u4e07\u96f7\u5f53\u7136\u53ea\u6709\u4e00\u4e2a\uff0c\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u9ad8\u5927\u5982\u5de8\u6811\uff0c\u4ed6\u66f4\u4e0d\u4f1a\u771f\u7684\u662f\u96f7\u795e\u3002 \u3000\u3000\u4f46\u662f\u6076\u72fc\u4e3a\u751a\u4e48\u4f1a\u8d70\u8fdb\u81ea\u5df1\u7684\u5fc3\u810f\u91cc\uff1f\u94a2\u9488\u4e3a\u4ec0\u4e48\u4f1a\u523a\u8fdb\u81ea\u5df1\u7684\u8111\u9ad3\u91cc\uff1f\u610f\u5fd7\u4e3a\u4f55\u788e\u88c2\u5982\u74f7\uff1f \u3000\u3000\u90a3\u662f\u4e2d\u4e86\u6bd2\u7684\u73b0\u8c61\uff01 \u3000\u3000\u7a76\u7adf\u662f\u90a3\u4e00\u638c\u3001\u8fd8\u662f\u90a3\u4e00\u70b9\u201c\u661f\u661f\u201d\u4e4b\u6545\uff1f\u4e5f\u65e0\u6687\u5206\u8fa8\u3002 \u3000\u3000\u5fc5\u987b\u901f\u6740\u56de\u4e07\u96f7\u3002 \u3000\u3000\u9664\u6b64\u65e0\u4ed6\u3002 \u3000\u3000\u2014\u2014\u65b9\u90aa\u771f\u5728\u5168\u529b\u53d1\u52a8\u653b\u51fb\u65f6\uff0c\u795e\u5fd7\u8ff7\u60da\uff0c\u8fd9\u6837\u5730\u60f3\u3002 \u3000\u3000\u8fd9\u4eba\u600e\u4e48\u8fd9\u6837\u72e0\u3002\u8fd9\u6837\u72c2\u3002\u8fd9\u6837\u62ff\u547d\u4e0d\u5f53\u547d\uff1f\uff01 \u3000\u3000\u81ea\u5df1\u7684\u201c\u4e94\u96f7\u8f70\u9876\u201d\uff0c\u968f\u65f6\u53ef\u4ee5\u628a\u4ed6\u8f70\u6210\u5343\u767e\u7247\uff0c\u53ef\u662f\u81ea\u5df1\u6bcf\u4e00\u6b21\u8574\u917f\u66b4\u96f7\u3002\u5c1a\u672a\u6210\u5f62\uff0c\u5251\u5149\u5df2\u81f3\uff01 \u3000\u3000\u5251\u5149\u5982\u7535\u3002 \u3000\u3000\u7535\u5149\u6c38\u8fdc\u95ea\u5728\u96f7\u9e23\u4e4b\u524d\u3002 \u3000\u3000\u7535\u5251\u5f15\u53d1\u4e86\u96f7\u9707\u3002 \u3000\u3000\u81ea\u5df1\u7684\u201c\u4e94\u96f7\u8f70\u9876\u201d\uff0c\u6bcf\u4e00\u6b21\u90fd\u53ea\u597d\u63d0\u524d\u53d1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u63d0\u524d\u53d1\u51fa\u7684\u96f7\uff0c\u58f0\u52bf\u8fdc\u4e0d\u53ca\u84c4\u52bf\u800c\u53d1\u7684\u96f7\u8f70\uff0c\u53ef\u662f\u96f7\u52a8\u6bcf\u4e00\u6b21\u90fd\u662f\u88ab\u903c\u53d1\u3002\u8bf1\u53d1\u6216\u5f15\u53d1\uff0c\u8fd9\u4f7f\u5f97\u81ea\u5df1\u7684\u96f7\u529f\u8d8a\u6765\u8d8a\u6563\u3001\u8d8a\u6765\u8d8a\u4e0d\u6613\u51dd\u805a\u3002 \u3000\u3000\u521a\u624d\u4e4b\u6240\u4ee5\u5bf9\u4ed6\u53ea\u53d1\u4e86\u4e00\u96f7\uff0c\u800c\u4e0d\u8fd8\u51fb\uff0c\u9664\u4e86\u4e3a\u4e86\u8981\u6478\u6e05\u695a\u4ed6\u7684\u5251\u672f\u5bb6\u6570\u4e4b\u5916\uff0c\u4e3b\u8981\u662f\u56e0\u4e3a\u201c\u4e94\u96f7\u8f70\u9876\u201d\uff0c\u5a01\u529b\u81f3\u5927\uff0c\u4f46\u4e0d\u6613\u51dd\u805a\u3002 \u3000\u3000\u81ea\u5df1\u5f53\u7136\u4e0d\u60f3\u81ea\u540e\u6697\u7b97\uff0c\u4ecd\u7136\u51fb\u7a7a\u3002 \u3000\u3000\u6211\u56de\u4e07\u96f7\u5c82\u662f\u80cc\u540e\u4f24\u4eba\u4e14\u5c45\u7136\u4f24\u4e0d\u7740\u4eba\u7684\u4eba\uff01 \u3000\u3000\u53ef\u662f\uff0c\u73b0\u5728\u81ea\u5df1\u5df2\u6570\u96f7\u51fb\u865a\uff0c\u518d\u8fd9\u6837\u4e0b\u53bb\uff0c\u5143\u6c14\u5c31\u8981\u8017\u5c3d\uff0c\u771f\u6c14\u5c31\u65e0\u6cd5\u51dd\u805a\u3002 \u3000\u3000\u66f4\u53ef\u6015\u7684\u662f\uff0c\u8fd9\u4e2a\u5e74\u8f7b\u5c0f\u5b50\uff0c\u811a\u6b65\u6d6e\u6447\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u5251\u6cd5\u6e38\u79fb\u4e0d\u5b9a\uff0c\u800c\u8eab\u6cd5\u4e5f\u8e09\u8dc4\u4e0d\u5df1\uff0c\u6709\u4e00\u6b21\u5c45\u7136\u8fd8\u633a\u8eab\u6328\u4e86\u81ea\u5df1\u534a\u9053\u96f7\u6b9b\u3002 \u3000\u3000\u8fd9\u662f\u4ec0\u4e48\u8eab\u6cd5\uff1f\uff01 \u3000\u3000\u8fd9\u662f\u4ec0\u4e48\u6253\u6cd5\uff1f\uff01 \u3000\u3000\u8fd9\u5c0f\u5b50\u96be\u9053\u5149\u8981\u81ea\u5df1\u7684\u547d\uff0c\u4e0d\u6653\u5f97\u4fdd\u547d\uff1f\uff01 \u3000\u3000\u706b\u5149\u718a\u718a\uff0c\u5feb\u70e7\u8fc7\u6765\u4e86\uff0c\u518d\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u53ef\u8ba8\u5f97\u4e86\u597d\uff1f\uff01 \u3000\u3000\u201c\u4e94\u96f7\u8f70\u9876\u201d\u56de\u4e07\u96f7\u7684\u540d\u5934\uff0c\u53ef\u4f1a\u7a7a\u63b7\u5728\u8fd9\u91cc\uff1f\uff01 \u3000\u3000\u2014\u2014\u56de\u4e07\u96f7\u5728\u96f7\u8f70\u65b9\u90aa\u771f\u7684\u65f6\u5019\uff0c\u5728\u4ed6\u7eb5\u6a2a\u6c5f\u6e56\u4e8c\u5341\u4e94\u5e74\u91cc\uff0c\u4ece\u5f53\u5e74\u4ed6\u529b\u6218\u96f7\u52a8\u5929\u800c\u540e\uff0c\u7b2c\u4e8c\u6b21\u6709\u4e86\u65e0\u7531\u7684\u60e7\u610f\u3002 \u3000\u3000\u9ed1\u65cb\u98ce\u5c0f\u767d\u8d76\u5728\u8f66\u961f\u7684\u524d\u5934\uff0c\u5728\u989c\u5915\u4e4b\u524d\u3001\u6c60\u65e5\u66ae\u4e4b\u524d\u3001\u6d2a\u4e09\u70ed\u4e4b\u524d\u3002 \u3000\u3000\u5f53\u5c0f\u767d\u53d1\u73b0\u524d\u9762\u6709\u706b\u5149\u51b2\u5929\u7684\u65f6\u5019\uff0c\u5df2\u4fc3\u9a6c\u5168\u529b\u51b2\u523a\u3002 \u3000\u3000\u4ed6\u4e00\u65e6\u53d1\u73b0\u5728\u706b\u5149\u4e2d\u6709\u4e24\u6761\u6fc0\u6597\u7684\u5f71\u5b50\uff0c\u7acb\u65f6\u5c31\u7ad9\u5728\u978d\u4e0a\uff0c\u6240\u4ee5\u6bd4\u8c01\u90fd\u77a7\u5f97\u6e05\u695a\u3002 \u3000\u3000\u539f\u6765\u65b9\u90aa\u771f\u7684\u4f4f\u5904\uff0c\u5df2\u6210\u4e86\u4e00\u7247\u706b\u6d77\u3002 \u3000\u3000\u5c0f\u767d\u6700\u8fd1\u5e38\u6765\u6b64\u5730\uff0c\u6709\u65f6\u662f\u6765\u9001\u793c\uff0c\u6709\u65f6\u662f\u6765\u76d1\u89c6\uff0c\u6709\u7684\u65f6\u5019\u662f\u966a\u6c60\u516c\u5b50\u6765\uff0c\u6709\u7684\u65f6\u5019\u662f\u966a\u5218\u519b\u5e08\u6765\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u975e\u719f\u6089\u8fd9\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u5730\u65b9\u53ea\u5269\u4e0b\u4e86\u5b9b\u4f3c\u5f20\u7259\u821e\u722a\u5410\u820c\u7684\u4e00\u7247\u706b\u6d77\u3002 \u3000\u3000\u706b\u6d77\u524d\u6709\u4e24\u4eba\u6b63\u4f5c\u6b8a\u6b7b\u6218\u3002 \u3000\u3000\u4e00\u4e2a\u50cf\u4e00\u56e2\u90c1\u53d1\u7684\u65f1\u96f7\uff0c\u6bd4\u706b\u7130\u8fd8\u8981\u731b\u70c8\u3002\u66f4\u9634\u90c1\u6016\u4eba\uff01 \u3000\u3000\u4e00\u4e2a\u50cf\u4e00\u9053\u7535\u5149\uff0c\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u68ee\u78a7\u7684\u5bd2\u7535\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u96f7\u9e23\u7535\u95ea\uff0c\u4e24\u4e2a\u4eba\u90fd\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u5d29\u5012\u5982\u5343\u5e74\u7684\u5de8\u6728\u3002 \u3000\u3000\u4e00\u4e2a\u50cf\u4e00\u9053\u6298\u65ad\u7684\u7535\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u4ed6\u90fd\u8ba4\u8bc6\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u5999\u624b\u5802\u56de\u5bb6\u7684\u56de\u4e07\u96f7\uff0c\u4e00\u4e2a\u662f\u5170\u4ead\u767e\u9080\u65e0\u529f\u7684\u65b9\u90aa\u771f\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u53c8\u6709\u4e24\u9053\u4eba\u5f71\u98de\u63a0\u51fa\u6765\u3002 \u3000\u3000\u4e00\u63a0\u5411\u56de\u4e07\u96f7\uff0c\u6276\u8d77\u6765\u3002 \u3000\u3000\u4e00\u63a0\u5411\u65b9\u90aa\u771f\uff0c\u4e00\u67aa\u523a\u843d\u3002 \u3000\u3000\u9ed1\u65cb\u98ce\u5c0f\u767d\u5927\u559d\u4e00\u58f0\uff0c\u201c\u4e0d\u8bb8\u6740\u4eba\uff01\u201d\u90a3\u4eba\u4e00\u6014\uff0c\u67aa\u8fd8\u6ca1\u6709\u523a\u4e0b\u53bb\u3002 \u3000\u3000\u989c\u5915\u5373\u4f38\u51fa\u5934\u6765\u75be\u95ee\uff1a\u201c\u751a\u4e48\u4e8b\u3002\u201d\u5c0f\u767d\u9053\uff1a\u201c\u65b9\u516c\u4e86\u53d7\u4f24\u4e86\uff0c\u6709\u4eba\u8981\u6740\u4ed6\u3002\u201d\u989c\u5915\u60c5\u6025\u5730\u9053\uff1a\u201c\u5feb\u6551\u4ed6\u3002\u201d\u4f46\u8f66\u961f\u79bb\u51fa\u4e8b\u7684\u5730\u65b9\u81f3\u5c11\u6709\u4e09\u56db\u5341\u4e08\u8fdc\uff0c\u5c0f\u767d\u7eb5\u8eab\u4e09\u8d77\u4e09\u843d\uff0c\u4ecd\u6709\u4e8c\u5341\u4e08\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u989c\u5915\u6025\u9053\uff1a\u201c\u4ed6\u4e0d\u80fd\u6b7b\u3002\u201d\u6c60\u65e5\u66ae\u5411\u4e03\u53d1\u7985\u5e08\u4e00\u70b9\u5934\u3002\u8fd9\u65f6\u8fde\u6d2a\u4e09\u70ed\u4e5f\u6253\u9a6c\u72c2\u5954\uff0c\u8981\u6025\u622a\u4f4f\u90a3\u4eba\u4e0b\u6bd2\u624b\uff0c\u53ef\u662f\u53c8\u600e\u6765\u5f97\u53ca\uff1f \u3000\u3000\u90a3\u4eba\u5df2\u4e00\u67aa\u523a\u843d\u3002 \u3000\u3000\u5730\u4e0a\u7684\u65b9\u90aa\u771f\u5374\u52c9\u529b\u7ffb\u4e86\u7ffb\u8eab\uff0c\u67aa\u523a\u7a7a\uff0c\u518d\u523a\u3002 \u3000\u3000\u4e03\u53d1\u7985\u5e08\u7684\u773c\u775b\u7a81\u7136\u7741\u5927\uff0c\u53d1\u51fa\u706b\u7130\u4e00\u822c\u7684\u5149\u8292\u3002 \u3000\u3000\u4ed6\u5728\u8eab\u524d\u60ac\u6302\u7684\u53e3\u888b\u91cc\u4e00\u638f\uff0c\u7adf\u638f\u51fa\u4e00\u628a\u5c0f\u5f13\u3002 \u3000\u3000\u706b\u7ea2\u8272\u7684\u5c0f\u5f13\u3002 \u3000\u3000\u4ed6\u53cd\u624b\u5f80\u53d1\u4e0a\u4e00\u62d4\uff0c\u7136\u540e\u642d\u7bad\u3002 \u3000\u3000\u4ed6\u7684\u201c\u7bad\u201d\u7adf\u662f\u4e00\u6839\u5934\u53d1\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u4ed6\u7684\u77ed\u53d1\u91cc\u7adf\u9690\u4f0f\u4e86\u8fd9\u4e48\u4e00\u6839\u957f\u53d1\u3002 \u3000\u3000\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u6839\u5934\u53d1\u5c04\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5934\u53d1\u5c45\u7136\u7ed9\u4ed6\u62c9\u6210\u4e00\u6761\u76f4\u7ebf\uff0c\u4e0d\u77e5\u662f\u56e0\u4e3a\u592a\u7ec6\u8fd8\u662f\u706b\u5149\u5927\u8000\u773c\u4e4b\u6545\uff0c\u5c31\u518d\u4e5f\u96be\u4ee5\u8fa8\u8ba4\u4e86\u3002 \u3000\u3000\u53ef\u662f\u90a3\u4f7f\u94a9\u9570\u67aa\u7684\u4eba\u5ffd\u7136\u629a\u81c2\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u90a3\u6400\u8d77\u56de\u4e07\u96f7\u7684\u4eba\uff0c\u5f88\u6025\u903c\u7684\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u4e5f\u6276\u7740\u8fd9\u4f7f\u67aa\u7684\u4eba\uff0c\u65bd\u5c55\u8f7b\u529f\uff0c\u98de\u63a0\u800c\u53bb\u3002 \u3000\u3000\u4e03\u53d1\u8fd8\u8981\u5f20\u5f13\uff0c\u6c60\u65e5\u66ae\u5927\u559d\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff01\u6551\u4eba\u8981\u7d27\uff01\u201d \u3000\u3000\u5c0f\u767d\u8fd9\u624d\u8d76\u5230\u3002 \u3000\u3000\u5730\u4e0a\u7684\u56de\u4e07\u96f7\uff0c\u8fd8\u6709\u90a3\u4e24\u4e2a\u6765\u5f97\u5feb\u53bb\u5f97\u4e5f\u5feb\u7684\u4eba\uff0c\u90fd\u4e0d\u5728\u4e86\uff0c\u5730\u4e0a\u53ea\u5269\u4e0b\u4e86\u65b9\u90aa\u771f\uff0c\u8fd8\u6709\u4e00\u5177\u5c11\u6389\u534a\u8fb9\u8138\u7684\u5c38\u4f53\uff0c\u5de6\u534a\u8eab\u5b50\u5df2\u6cbe\u7740\u4e86\u706b\u3002 \u3000\u3000\u989c\u5915\u4e5f\u63a0\u51fa\u4e86\u8f66\u6765\uff0c\u5979\u770b\u89c1\u65b9\u90aa\u771f\u5012\u5728\u5730\u4e0a\uff0c\u84dd\u4e1d\u5dfe\u534a\u677e\u7684\u624e\u7740\uff0c\u7693\u767d\u7684\u624b\u8155\u8fd8\u5e26\u7740\u5979\u7684\u7fe0\u7389\u956f\u5b50\uff0c\u4e00\u65f6\u60c5\u6025\uff0c\u4fef\u4e0b\u8eab\u53bb\uff0c\u53ea\u987e\u6447\u7740\u4ed6\u51c4\u5207\u5730\u95ee\uff1a\u201c\u65b9\u8c22\u8c22\uff0c\u8c22\u8c22\uff0c\u4f60\u9192\u9192\uff0c\u4f60\u9192\u9192\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u4e00\u770b\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u76b1\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u6d2a\u4e09\u70ed\u4e5f\u8d76\u8fc7\u6765\u770b\u4e86\u770b\uff0c\u6012\u9053\uff1a\u201c\u4f60\u82e5\u65e9\u4e9b\u52a0\u5165\u5170\u4ead\uff0c\u600e\u4f1a\u843d\u5f97\u5982\u6b64\u4e0b\u573a\uff01\u201d \u3000\u3000\u989c\u5915\u8f6c\u8fc7\u8eab\u6765\uff0c\u816e\u8fb9\u6709\u6cea\u75d5\uff0c\u95ee\uff1a\u201c\u4ed6\u662f\u88ab\u8c01\u6740\u7684\uff1f\u201d \u3000\u3000\u5c0f\u767d\u773c\u4e2d\u95ea\u8fc7\u5fff\u610f\uff1a\u201c\u6211\u53ea\u770b\u89c1\u56de\u4e07\u96f7\uff0c\u4f46\u4ed6\u4e5f\u5012\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5ffd\u6c89\u58f0\u9053\uff1a\u201c\u5927\u592b\u4eba\uff0c\u4ed6\u5e76\u6ca1\u6709\u6b7b\uff0c\u4ed6\u53ea\u662f\u4e2d\u4e86\u6bd2\u3002\u201d \u3000\u3000\u989c\u5915\u4e4d\u60ca\u4e4d\u559c\uff0c\u5fd9\u5411\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u6551\u6551\u4ed6\uff0c\u8bf7\u4f60\u6551\u6551\u4ed6\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9000\u540e\u4e00\u6b65\uff0c\u6709\u70b9\u8e0c\u8e87\u7684\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u989c\u5915\u8f6c\u5411\u6c60\u65e5\u66ae\uff0c\u773c\u4e2d\u5c3d\u662f\u60c5\u5207\u7684\u54c0\u6c42\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u5411\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u5927\u5e08\uff0c\u70e6\u4f60\u51fa\u624b\u76f8\u6551\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u4fef\u8eab\u628a\u8109\uff0c\u53c8\u89e3\u5f00\u8863\u895f\uff0c\u6309\u629a\u65b9\u90aa\u771f\u7684\u524d\u80f8\uff0c\u518d\u7ffb\u8f6c\u8fc7\u6765\uff0c\u89c6\u5bdf\u4ed6\u80cc\u540e\u7684\u4f24\u3002 \u3000\u3000\u7136\u540e\uff0c\u4e03\u53d1\u7985\u5e08\u6447\u4e86\u6447\u5934\uff0c\u53c8\u6447\u4e86\u6447\u5934\uff0c\u53d1\u51fa\u4e00\u58f0\u559f\u7136\u957f\u53f9\u3002 \u3000\u3000\u989c\u5915\u6ee1\u76ee\u662f\u6cea\u3002 \u3000\u3000\u65b9\u90aa\u771f\u53cc\u76ee\u7d27\u95ed\uff0c\u8138\u8272\u9752\u767d\uff0c\u4e0d\u7701\u4eba\u4e8b\u3002 \u3000\u3000\u4e03\u53d1\u7985\u5e08\u840e\u7136\u5730\u5f90\u7acb\u8d77\u6765\uff0c\u5411\u6c60\u65e5\u66ae\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8bf7\u8fc7\u6765\u7ec6\u8bae\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u8ddf\u4ed6\u8d70\u79bb\u4e86\u51e0\u6b65\uff0c\u5c0f\u767d\u4e0d\u653e\u5fc3\uff0c\u8981\u8ddf\u4e0a\u6765\uff0c\u6c60\u65e5\u66ae\u8bf4\uff1a\u201c\u6211\u4e0e\u5927\u5e08\u6709\u8981\u4e8b\u5546\u8bae\uff0c\u4f60\u5148\u66ff\u65b9\u5c11\u4fa0\u62a4\u6cd5\u3002\u201d \u3000\u3000\u5c0f\u767d\u9053\uff1a\u201c\u662f\u3002\u201d\u4ecd\u5728\u8fdc\u8fdc\u76d1\u89c6\u4e03\u53d1\u7985\u5e08\u3002\u4e03\u53d1\u7985\u5e08\u6765\u4e86\u53ea\u6709\u534a\u5929\uff0c\u9ed1\u65cb\u98ce\u5c0f\u767d\u548c\u6d2a\u4e09\u70ed\u5f53\u7136\u5bf9\u4ed6\u90fd\u4e0d\u751a\u4fe1\u4efb\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u89c1\u5c0f\u767d\u9000\u53bb\uff0c\u4fbf\u95ee\u3000\uff1a\u201c\u5927\u5e08\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u8eab\u4e0a\u7684\u8863\u888d\u3001\u5934\u4e0a\u7684\u77ed\u53d1\u3001\u773c\u91cc\u7684\u5389\u8292\uff0c\u88ab\u706b\u5149\u4e00\u6620\uff0c\u90fd\u73b0\u51fa\u5947\u5f02\u7684\u5e7b\u5f69\uff1a \u3000\u3000\u201c\u4f60\u771f\u7684\u8981\u6551\u65b9\u90aa\u771f\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u88ab\u95ee\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5927\u5e08\u4f55\u6709\u6b64\u95ee\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5408\u4ec0\u9053\uff1a\u201c\u81ea\u6765\u70e6\u607c\uff0c\u7531\u4eba\u81ea\u62db\uff0c\u516c\u5b50\u8981\u6551\u65b9\u90aa\u771f\uff0c\u5927\u592b\u4eba\u8ddf\u65b9\u90aa\u771f\u53ea\u6015\u8fd8\u6709\u4e9b\u5919\u7f18\u672a\u4e86\uff0c\u516c\u5b50\u6b64\u4e3e\uff0c\u4f1a\u4e0d\u4f1a\u662f\u81ea\u5bfb\u70e6\u607c\uff1f\u201d\u8bf4\u7f62\u7528\u4e00\u53cc\u5f02\u70c8\u7684\u773c\u795e\uff0c\u671b\u7740\u6c60\u65e5\u66ae\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u88ab\u4ed6\u8fd9\u4e00\u8bf4\uff0c\u518d\u4e00\u770b\uff0c\u5fc3\u5934\u63a0\u8d77\u597d\u4e00\u9635\u5b50\u7684\u7d0a\u4e71\uff0c\u7adf\u4e0d\u6562\u9762\u5bf9\u8fd9\u5947\u50e7\u7684\u773c\u795e\uff0c\u597d\u4e00\u4f1a\u624d\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u65b9\u5c11\u4fa0\u662f\u6211\u4eec\u6781\u9700\u8981\u7528\u7684\u5947\u624d\uff0c\u6211\u4eec\u4e0d\u80fd\u89c1\u6b7b\u4e0d\u6551\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u7b11\u4e86\u7b11\uff0c\u6148\u548c\u5730\u9053\uff1a\u201c\u6551\u4e86\u4ed6\uff0c\u65e5\u540e\u4ed6\u4ecd\u672a\u5fc5\u4e3a\u516c\u5b50\u6240\u7528\u3002\u5047\u5982\u73b0\u5728\u628a\u4ed6\u7f6e\u4e4b\u4e0d\u7406\uff0c\u6211\u4eec\u4e5f\u4e0d\u7b97\u6740\u4ed6\uff0c\u4f46\u4ed6\u5374\u662f\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u6c89\u541f\u9053\uff1a\u201c\u5927\u5e08\u7684\u610f\u601d\u662f\u8bf4\uff1a\u4ed6\u4e0d\u8be5\u6551\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5782\u76ee\u9053\uff1a\u201c\u6551\u4e0e\u4e0d\u6551\uff0c\u5168\u51ed\u516c\u5b50\u5b9a\u593a\u3002\u516c\u5b50\u662f\u4e2d\u5174\u660c\u5927\u6c60\u5bb6\u7684\u660e\u4e3b\uff0c\u8d2b\u50e7\u552f\u516c\u5b50\u4e4b\u610f\u662f\u4ece\u3002\u201d\u4ed6\u4f4e\u7709\u5782\u773c\u7684\u65f6\u5019\uff0c\u7acb\u5373\u6740\u6c14\u5168\u6d88\uff0c\u53d8\u4f5c\u4e00\u4fee\u884c\u6df1\u539a\u7684\u9ad8\u50e7\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u54ac\u4e00\u54ac\u5507\uff0c\u9053\uff1a\u201c\u4e14\u4e0d\u7ba1\u4ed6\u52a0\u4e0d\u52a0\u5165\u6211\u4eec\u6c60\u5bb6\uff0c\u81f3\u5c11\u4e0d\u4f1a\u4e0e\u6211\u4eec\u4e3a\u654c\uff0c\u6709\u4ed6\u5728\uff0c\u81f3\u5c11\u5bf9\u56de\u3001\u6e38\u3001\u845b\u4e09\u5bb6\u6709\u5a01\u80c1\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5408\u4ec0\uff0c\u7f13\u7f13\u7684\u9053\uff1a\u201c\u5047\u5982\u5728\u4ed6\u590d\u539f\u4e4b\u540e\uff0c\u7adf\u52a0\u5165\u845b\u3001\u56de\u3001\u6e38\u4e09\u5bb6\uff0c\u4e0e\u516c\u5b50\u4f5c\u5bf9\u5462\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u53d8\u8272\u9053\uff1a\u201c\u8fd9\u2026\u2026\u4e0d\u4f1a\u7f62\uff1f\u201d \u3000\u3000\u4e03\u53d1\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u516c\u5b50\u771f\u7684\u8981\u6551\u6d3b\u6b64\u4eba\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fc3\u91cc\u5341\u5206\u72b9\u7591\uff0c\u53e3\u91cc\u5374\u9053\uff1a\u201c\u8fd8\u662f\u6551\u4e86\u518d\u8bf4\u7f62\u3002\u201d\u4e03\u53d1\u76ee\u867d\u4f4e\u5782\uff0c\u4f46\u4f3c\u5728\u773c\u76ae\u5b50\u91cc\u89c2\u5bdf\u4ed6\uff0c\u53c8\u95ee\uff1a\u201c\u516c\u5b50\u7edd\u4e0d\u540e\u6094\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5ffd\u7136\u542c\u51fa\u4e03\u53d1\u7985\u5e08\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u559c\u9053\uff1a\u201c\u5927\u5e08\u7684\u610f\u601d\u662f\uff1a\u65b9\u5c11\u4fa0\u7684\u4f24\uff0c\u4ecd\u7136\u53ef\u6551\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5176\u5b9e\u4f24\u5f97\u5e76\u4e0d\u91cd\uff0c\u201d\u4e03\u53d1\u7985\u5e08\u4f4e\u58f0\u9053\uff0c\u201c\u56de\u4e07\u96f7\u7684\u2018\u4e94\u96f7\u8f70\u9876\u2019\uff0c\u6740\u4f24\u529b\u867d\u5927\uff0c\u707c\u4f24\u4e86\u4ed6\u51e0\u5904\uff0c\u4f46\u4ed6\u90fd\u628a\u8981\u5bb3\u8eb2\u8fc7\uff0c\u800c\u4e14\u5fc5\u7136\u4fee\u4e60\u8fc7\u2018\u5b50\u5348\u5fc3\u6f6e\u2019\u3001\u2018\u70bc\u6c14\u8c03\u5143\u2019\u7684\u5185\u529f\uff0c\u62a4\u4f4f\u5fc3\u8109\uff0c\u6240\u4f24\u4e0d\u5165\u80ba\u8151\uff0c\u53ea\u662f\u809d\u810f\u51fa\u8840\uff0c\u5e76\u4e0d\u635f\u53ca\u7ecf\u8109\uff0c\u4ed6\u660f\u5012\u662f\u56e0\u4e3a\u7740\u4e86\u98de\u661f\u5b50\u7684\u6697\u5668\uff0c\u8d2b\u50e7\u4ed4\u7ec6\u770b\u8fc7\uff0c\u4ed6\u5de6\u8033\u8fb9\u5782\u88ab\u5212\u5f00\u4e86\u4e00\u70b9\u8868\u76ae\uff0c\u5e76\u672a\u89c1\u8840\uff0c\u98de\u661f\u5b50\u7684\u2018\u4e03\u661f\u4f34\u6708\u2019\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u65b9\u5c11\u4fa0\u4ee5\u4e3a\u6ca1\u4e8b\uff0c\u4f46\u2018\u4e03\u661f\u62cc\u6708\u2019\uff0c\u4f55\u7b49\u5389\u5bb3\uff0c\u7eb5\u672a\u89c1\u8840\uff0c\u53ea\u8981\u8840\u6c14\u8fd0\u884c\u8fc7\u901f\uff0c\u4ecd\u5fc5\u81f4\u6655\u7729\u3001\u4ea7\u751f\u5e7b\u89c9\u7684\uff0c\u4e45\u6301\u5fc5\u5012\u2014\u2014\u201d \u3000\u3000\u4e03\u53d1\u8bf4\u5230\u8fd9\u91cc\uff0c\u76ee\u4e2d\u53c8\u7efd\u53d1\u51fa\u5f02\u91c7\uff1a\u201c\u65b9\u5c11\u4fa0\u80fd\u5728\u6b64\u65f6\u6b64\u5883\uff0c\u5c1a\u80fd\u51fb\u5012\u5999\u624b\u5802\u597d\u624b\u56de\u4e07\u96f7\uff0c\u4e0d\u4f46\u6b66\u529f\u4ee4\u4eba\u9707\u4f69\uff0c\u610f\u5fd7\u529b\u4e5f\u7aef\u7684\u662f\u8fc7\u4eba\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u4e00\u4ee5\u4e3a\u559c\uff0c\u4e00\u4ee5\u4e3a\u5fe7\uff0c\u201c\u5927\u5e08\u7684\u610f\u601d\u662f\u8bf4\uff1a\u4ed6\u80fd\u6551\u6d3b\u2026\u2026\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u975e\u4f46\u80fd\u6d3b\uff0c\u800c\u4e14\u4f24\u5f97\u5e76\u4e0d\u4e25\u91cd\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u60f3\u60f3\u8fd8\u662f\u9053\uff1a\u201c\u90a3\u6211\u4eec\u628a\u4ed6\u5f04\u9192\u8fc7\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u201c\u4f24\u5f97\u91cd\u4e0e\u4e0d\u91cd\uff0c\u65b9\u5c11\u4fa0\u81ea\u5df1\u4e5f\u672a\u5fc5\u77e5\u9053\uff0c\u516c\u5b50\u4f55\u4e0d\u9886\u4e00\u6b21\u4eba\u60c5\uff1f\u201d\u4e03\u53d1\u7985\u5e08\u5f90\u5f90\u5730\u9053\uff1a\u201c\u5982\u679c\u516c\u5b50\u771f\u7684\u8981\u6551\u4eba\uff0c\u8981\u88ab\u6551\u7684\u4eba\u611f\u6069\u56fe\u62a5\uff0c\u4f55\u4e0d\u5148\u628a\u4ed6\u9001\u56de\u5170\u4ead\u518d\u8bf4\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u604d\u7136\u9053\uff1a\u201c\u5927\u5e08\u9ad8\u89c1\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u8bf7\u516c\u5b50\u501f\u4e00\u6b65\u8bf4\u8bdd\u7684\u539f\u7531\u3002\u8d2b\u50e7\u5728\u5927\u5bb6\u9762\u524d\u5c31\u8bf4\u4ed6\u4f24\u91cd\uff0c\u4f46\u516c\u5b50\u4e00\u529b\u6073\u6c42\u76f8\u6551\uff0c\u8d2b\u50e7\u5c31\u5c3d\u529b\u800c\u4e3a\u2026\u2026\u8fd9\u79cd\u60c5\u5f62\uff0c\u5f85\u4f1a\u513f\u5f53\u5927\u592b\u4eba\u9762\u524d\uff0c\u4e0d\u59a8\u5c31\u8fd9\u6837\u642d\u914d\u4e00\u4e0b\uff0c\u53ef\u80fd\u6709\u76ca\u4e8e\u65e5\u540e\u884c\u4e8b\uff0c\u516c\u5b50\u4ee5\u4e3a\u5982\u4f55\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u7b11\u9053\uff1a\u201c\u5927\u5e08\u5904\u5904\u4e3a\u6211\u7740\u60f3\uff0c\u6211\u5f97\u5927\u5e08\u5f3a\u52a9\uff0c\u5982\u864e\u6dfb\u7ffc\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u8bed\u91cd\u6df1\u957f\u7684\u9053\uff1a\u201c\u516c\u5b50\u4f53\u6064\u90e8\u4e0b\uff0c\u5584\u7528\u4eba\u624d\u2026\u2026\u8d2b\u50e7\u89c1\u516c\u5b50\u5982\u6b64\u60dc\u91cd\u65b9\u5c11\u4fa0\uff0c\u5b9e\u5728\u975e\u5e38\u611f\u52a8\u3002\u9e1f\u79bd\u5c1a\u77e5\u62e9\u826f\u6728\u800c\u6816\uff0c\u66f4\u4f55\u51b5\u662f\u8d2b\u50e7\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fd9\u9053\uff1a\u201c\u53ea\u8981\u5927\u5e08\u80af\u4e3a\u6c60\u5bb6\u62d4\u5200\u76f8\u52a9\uff0c\u6211\u4e00\u5b9a\u5949\u5927\u5e08\u4e3a\u7236\u4e3a\u5e08\uff0c\u8363\u534e\u5bcc\u8d35\uff0c\u5f53\u4e0e\u5927\u5e08\u5171\u4eab\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u51fa\u5bb6\u4eba\uff0c\u65e9\u5df2\u770b\u7834\u540d\u5229\uff0c\u89c6\u5bcc\u8d35\u5982\u6d6e\u4e91\uff0c\u201d\u4e03\u53d1\u63b8\u5e08\u81fc\u4ec0\u957f\u58f0\u9053\uff0c\u201c\u5c31\u7b97\u516c\u5b50\u80fd\u91cd\u4efb\u6d12\u5bb6\uff0c\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u6c60\u65e5\u66ae\u77e5\u9053\u4ed6\u5e94\u8be5\u8ffd\u95ee\u4e0b\u53bb\uff1a\u201c\u5927\u5e08\u6709\u751a\u4e48\u7591\u96be\uff0c\u5c3d\u7ba1\u5f53\u9762\u8d50\u6559\u6307\u70b9\uff0c\u5728\u4e0b\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u4e03\u53d1\u7965\u5e08\u7b11\u4e86\u4e00\u7b11\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u9053\uff1a\u201c\u5c31\u7b97\u516c\u5b50\u5bb9\u5f97\u4e0b\u6d12\u5bb6\uff0c\u516c\u5b50\u7684\u9ebe\u4e0b\u519b\u5e08\uff0c\u5218\u72ee\u5b50\u4e5f\u672a\u5fc5\u653e\u6211\u5728\u773c\u91cc\u54e9\u3002\u201d \u3000\u3000\u201c\u8fd9\u54ea\u91cc\u7684\u8bdd\u3002\u201d\u6c60\u65e5\u66ae\u5fd9\u9053\uff0c\u201c\u5218\u5148\u751f\u4e5f\u662f\u4e2a\u80f8\u895f\u6491\u5f97\u5382\u8239\u7684\u667a\u8005\uff0c\u600e\u4f1a\u5bf9\u5927\u5e08\u4e0d\u6167\u773c\u76f8\u52a0\uff1f\u201d \u3000\u3000\u4e03\u53d1\u63b8\u5e08\u54c8\u54c8\u4e00\u7b11\uff0c\u201c\u6211\u53ea\u662f\u8bf4\u7b11\u800c\u5df2\u3002\u201d\u62c9\u7740\u6c60\u65e5\u66ae\u7684\u8896\u5b50\u9053\uff0c\u201c\u6211\u4eec\u8fd9\u5c31\u53bb\u6551\u65b9\u516c\u5b50\u7f62\u3002\u201d \u3000\u3000\u4ed6\u4eec\u56de\u5230\u573a\u4e0a\uff0c\u989c\u5915\u5df2\u903c\u4e0d\u53ca\u5f85\uff0c\u6c60\u65e5\u66ae\u5f53\u7740\u4f17\u4eba\u7684\u9762\uff0c\u8ddf\u4e03\u53d1\u7985\u5e08\u4e89\u6c42\u4e00\u756a\uff0c\u4e03\u53d1\u624d\u52c9\u4e3a\u5176\u96be\u4f3c\u7684\u53f9\u9053\uff0c\u201c\u5509\uff0c\u65b9\u516c\u5b50\u5148\u7740\u4e86\u56de\u4e07\u96f7\u7684\u2018\u4e94\u96f7\u8f70\u9876\u2019\uff0c\u53c8\u88ab\u98de\u661f\u5b50\u6dec\u6bd2\u6697\u5668\u2018\u4e03\u661f\u4f34\u6708\u2019\u6240\u4f24\uff0c\u8981\u6cbb\u597d\u4ed6\uff0c\u53ea\u597d\u8981\u8017\u8d39\u4e94\u5e74\u7684\u529f\u529b\uff0c\u8f6c\u6ce8\u5176\u8eab\uff0c\u4ee5\u53ca\u8981\u503e\u5c3d\u8d2b\u50e7\u6240\u5269\u4e0b\u7684\u4e09\u9897\u2018\u4e5d\u8f6c\u8fd8\u9b42\u4e39\u2019\uff0c\u624d\u80fd\u671b\u6709\u5fae\u6548\u3002\u201d\u4ed6\u8138\u6709\u96be\u8272\u4f3c\u7684\u9053\uff0c\u201c\u65e2\u7136\u516c\u5b50\u4e00\u518d\u6267\u610f\u6551\u4ed6\u4e00\u547d\uff0c\u8d2b\u618e\u4e5f\u4e0d\u5fcd\u773c\u89c1\u8fd9\u4f4d\u8db3\u80fd\u80a9\u8d1f\u6b66\u6797\u91cd\u4efb\u7684\u6b66\u6797\u5947\u624d\u6b7b\u5f97\u8fd9\u822c\u80e1\u91cc\u80e1\u6d82\u3001\u4e0d\u660e\u4e0d\u767d\uff0c\u5c31\u7b97\u518d\u5927\u7684\u4ee3\u4ef7\uff0c\u4e5f\u5f97\u8c41\u4e0a\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u89c1\u4e03\u53d1\u7985\u5e08\u7b54\u5141\u76f8\u6551\uff0c\u610f\u5373\u65b9\u90aa\u6d3b\u547d\u6709\u671b\uff0c\u81ea\u662f\u5fed\u559c\uff0c\u6c60\u65e5\u66ae\u4fbf\u9053\uff1a\u201c\u5927\u5e08\u8385\u4e34\u655d\u5e9c\u4e0d\u8fc7\u534a\u5929\uff0c\u5c31\u8981\u52b3\u795e\u8017\u529b\uff0c\u5728\u4e0b\u6b20\u5927\u5e08\u8fd9\u4e2a\u539a\u610f\uff0c\u5f53\u94ed\u8bb0\u4e8e\u5fc3\u3002\u201d \u3000\u3000\u989c\u5915\u542c\u4e86\u4e00\u9635\u611f\u52a8\uff0c\u9053\uff1a\u201c\u5927\u5e08\u6069\u91cd\uff0c\u4ed6\u65e5\u6211\u4eec\u5b9a\u5f53\u56fe\u62a5\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5fd9\u8bf4\uff1a\u201c\u8d2b\u50e7\u662f\u51fa\u5bb6\u4eba\uff0c\u7109\u53ef\u65bd\u6069\u671b\u62a5\uff1f\u8fd9\u90fd\u662f\u4e8c\u516c\u5b50\u7684\u60c5\u9762\uff0c\u5927\u592b\u4eba\u8981\u8c22\uff0c\u5c31\u8c22\u8be5\u8c22\u7684\u4eba\u7f62\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5373\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e00\u5bb6\u5b50\u7684\u4eba\uff0c\u6551\u65b9\u5c11\u4fa0\u662f\u6c60\u5bb6\u7684\u4e8b\uff0c\u6709\u4ec0\u4e48\u597d\u8c22\u7684\uff01\u201d \u3000\u3000\u4e8e\u662f\u4e00\u884c\u4eba\u7b49\uff0c\u6551\u7184\u4e86\u5927\u706b\uff0c\u7136\u540e\u628a\u65b9\u90aa\u771f\u6276\u4e0a\u9a6c\u8f66\uff0c\u5f80\u5170\u4ead\u7684\u65b9\u5411\u9a70\u53bb\u3002 \u3000\u3000\u7136\u800c\u5728\u8fdc\u79bb\u706b\u5149\u6620\u7167\u4e0d\u5230\u7684\u82c7\u5858\u91cc\uff0c\u8fd8\u4f0f\u7740\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u5176\u5b9e\u6709\u4e09\u4e2a\u4eba\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u5de8\u7075\u795e\u6837\u822c\u7684\u4eba\uff0c\u5df2\u8eba\u4e86\u4e0b\u53bb\uff0c\u8eab\u4e0a\u6709\u4e03\u9053\u4f24\u53e3\uff0c\u4ecd\u5728\u5192\u7740\u8840\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7684\u5176\u4e2d\u4e00\u4eba\uff0c\u6b63\u66ff\u56de\u4e07\u96f7\u643d\u7740\u836f\u818f\uff0c\u5305\u624e\u4f24\u53e3\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u4fbf\u662f\u56de\u767e\u54cd\u3002 \u3000\u3000\u4ed6\u770b\u7740\u706b\u5149\u6620\u7167\u4e0b\u8fdc\u53bb\u7684\u8f66\u961f\uff0c\u54ac\u7259\u5207\u9f7f\uff0c\u4ed6\u7684\u77ed\u67c4\u94a9\u9570\u67aa\u5c31\u538b\u5728\u5de6\u819d\u4e0b\uff0c\u4ed6\u53f3\u81c2\u4e0a\u6cbe\u7740\u8840\u8ff9\uff0c\u4e00\u6839\u94a2\u7ebf\u822c\u7684\u53d1\u4e1d\uff0c\u7a7f\u8fc7\u4e86\u4ed6\u7684\u53f3\u81c2\uff0c\u4ed6\u5e76\u6ca1\u6709\u5c06\u4e4b\u62d4\u51fa\u3002 \u3000\u3000\u4ed6\u65c1\u8fb9\u7684\u4eba\u95ee\u3000\uff1a\u201c\u56de\u603b\u7ba1\uff0c\u4f60\u7684\u4f24\u8981\u4e0d\u8981\u7d27\uff1f\u8981\u4e0d\u8981\u5148\u628a\u6697\u5668\u8d77\u51fa\u6765\uff0c\u518d\u6577\u4e9b\u2018\u4e07\u5e74\u65ad\u7eed\u2019\uff1f\u201d \u3000\u3000\u56de\u767e\u54cd\u51b7\u6c89\u5730\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u4e03\u53d1\u7985\u5e08\u7684\u2018\u4e00\u53d1\u795e\u523a\u2019\uff0c\u662f\u4e0d\u80fd\u62d4\u53d6\u7684\uff0c\u4e00\u62d4\u5219\u4f24\u8840\u8109\uff0c\u975e\u8981\u7b49\u8fc7\u4e03\u5929\u4e4b\u540e\uff0c\u53d1\u523a\u81ea\u7136\u8f6f\u840e\uff0c\u624d\u80fd\u53d6\u51fa\u6577\u836f\u3002\u5976\u5976\u7684\uff0c\u8fd9\u5b9e\u5728\u662f\u4e2a\u8fa3\u624b\u7684\u5bb6\u4f19\uff01\u201d \u3000\u3000\u4ed6\u8eab\u65c1\u7684\u758f\u53d1\u6c49\u5b50\u9053\uff1a\u201c\u521a\u624d\u53ea\u8981\u518d\u591a\u4e00\u4e0b\u5b50\uff0c\u5c31\u80fd\u6740\u4e86\u65b9\u90aa\u771f\uff0c\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u56de\u767e\u54cd\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u5170\u4ead\u6c60\u5bb6\u7684\u4eba\u8fd9\u6b21\u51e0\u4e4e\u503e\u5de2\u800c\u51fa\uff0c\u8fd8\u52a0\u4e86\u4e2a\u516d\u53d1\u7ea2\u888d\uff0c\u770b\u6765\u4ed6\u5df1\u53db\u79bb\u5343\u53f6\u5c71\u5e84\uff0c\u6539\u6295\u6c60\u5bb6\u4e86\uff0c\u6211\u4eec\u518d\u4e0b\u8fa3\u624b\uff0c\u53ea\u6015\u4e5f\u8981\u6298\u5728\u8fd9\u91cc\uff0c\u4e3a\u533a\u533a\u4e00\u4e2a\u65b9\u90aa\u771f\uff0c\u503c\u5f97\u4e48\u2026\u2026\uff01\u201d \u3000\u3000\u4ed6\u9065\u671b\u5df1\u70e7\u6210\u4e86\u4e00\u5806\u6b8b\u70ec\u3002\u5192\u7740\u7126\u70df\u7684\u5e9f\u589f\uff0c\u5583\u5583\u7684\u9053\uff1a\u201c\u66f4\u4f55\u51b5\uff0c\u6211\u4eec\u7684\u76ee\u7684\u5df2\u7ecf\u8fbe\u6210\uff0c\u4e0d\u8fc7\uff0c\u54fc\u54fc\uff0c\u8981\u6211\u6302\u8fd9\u9053\u5f69\uff0c\u6c60\u8001\u4e8c\u8be5\u600e\u4e48\u8d54\u507f\u6cd5\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u90a3\u758f\u987b\u7684\u6c49\u5b50\uff0c\u81ea\u7136\u6ca1\u542c\u6e05\u695a\uff0c\u540c\u65f6\u4ed6\u4e5f\u4e0d\u6562\u591a\u95ee\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u6709\u4e9b\u4e8b\u60c5\u77e5\u9053\u5f97\u8d8a\u5c11\uff0c\u603b\u6bd4\u77e5\u9053\u5f97\u591a\u6765\u5f97\u66f4\u597d\u3002 \u3000 \u7b2c\u4e8c\u5341\u7ae0\u3000\u68a6\u91cc\u7684\u98de\u661f \u3000 \u3000\u3000\u65b9\u90aa\u771f\u9192\u6765\u662f\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\u7684\u4e8b\u3002\u4ed6\u81ea\u4e00\u7247\u711a\u70e7\u7684\u706b\u6d77\u4e2d\u8f97\u8f6c\u6323\u624e\uff0c\u7a81\u7136\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u8377\u9999\u9635\u9635\uff0c\u9e1f\u60ca\u55a7\u3002 \u3000\u3000\u8549\u53f6\u5f62\u7684\u7a97\u6237\u4e4b\u5916\uff0c\u662f\u4e00\u6bb5\u77ee\u680f\u6746\uff0c\u8ddf\u7740\u516d\u5c3a\u591a\u6df1\u7684\u5c4b\u6a90\uff0c\u5e26\u51fa\u4e00\u7247\u5706\u5f62\u7684\u8d70\u5eca\uff0c\u7ea6\u83ab\u4e8c\u4e09\u5341\u4e08\u957f\uff0c\u5eca\u5916\u662f\u7ea2\u83b2\u7efd\u854a\u3002\u7fe0\u76d6\u6d6e\u6ce2\u7684\u83b2\u6c60\uff0c\u6e05\u98ce\u9001\u723d\u3002 \u3000\u3000\u4e66\u6848\u4e0a\u653e\u7740\u4e24\u652f\u4e09\u660e\u5b50\u8721\u53f0\uff0c\u7ea2\u70db\u9877\u5df2\u70e7\u5269\u6b8b\u8721\uff0c\u5c4b\u4e2d\u9648\u8bbe\u96c5\u6d01\uff0c\u623f\u91cc\u5341\u5206\u5bbd\u655e\uff0c\u9876\u6881\u5b50\u8fd8\u540a\u6709\u7409\u7483\u706f\uff1b\u81ea\u5df1\u5c31\u8eba\u5728\u69bb\u5b50\u4e0a\uff0c\u4fa7\u8fb9\u6709\u4e00\u5ea7\u5c0f\u7076\uff0c\u4e0a\u67b6\u7740\u5c0f\u94dc\u58f3\uff0c\u4e0b\u9762\u6e85\u7740\u661f\u706b\uff0c\u4f3c\u4e4e\u70e7\u5f97\u5f88\u65fa\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e00\u65e6\u9192\u6765\uff0c\u5c31\u77e5\u9053\u81ea\u5df1\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u4ed6\u5df2\u7ecf\u4e0d\u8ba4\u4e3a\u6d3b\u7740\u662f\u4ef6\u5feb\u4e50\u7684\u4e8b\uff0c\u867d\u7136\uff0c\u5728\u591a\u5e74\u4ee5\u524d\uff0c\u4ed6\u66fe\u5feb\u5feb\u4e50\u4e50\u3001\u5c3d\u60c5\u75db\u5feb\u5730\u6d3b\u8fc7\uff0c\u4e5f\u4e00\u5ea6\u4ee5\u4e3a\u751f\u547d\u662f\u5145\u6ee1\u6b22\u60a6\u7684\uff0c\u4ed6\u4eab\u53d7\u6bcf\u4e00\u5206\u9633\u5149\u7684\u70ed\u529b\uff0c\u6bcf\u4e00\u9635\u5fae\u98ce\u7684\u8f7b\u51c9\u3002\u6bcf\u4e00\u523b\u7684\u7f8e\u3001\u6bcf\u4e00\u4e2a\u4eba\u7684\u597d\u3002 \u3000\u3000\u4ed6\u66fe\u89c9\u5f97\u4ed6\u662f\u4e16\u95f4\u7684\u5e78\u8fd0\u513f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u73b0\u5728\u5df2\u4e0d\u90a3\u6837\u60f3\u3002 \u3000\u3000\u5f88\u4e45\u90fd\u4e0d\u518d\u8fd9\u6837\u60f3\u3002 \u3000\u3000\u4ed6\u66fe\u7ecf\u89c9\u5f97\u81ea\u5df1\u4e0d\u5e78\uff0c\u5fc3\u4e27\u6b32\u6b7b\u3002 \u3000\u3000\u2014\u2014\u53ef\u77e5\u9053\u5fc3\u4e27\u6b32\u6b7b\u662f\u751a\u4e48\u6ecb\u5473\uff1f\u90a3\u5c31\u662f\u6d3b\u7740\uff0c\u800c\u6ca1\u6709\u7b49\u5f85\u3002 \u3000\u3000\u6ca1\u6709\u4efb\u4f55\u671f\u5f85\u7684\u6d3b\u7740\u3002 \u3000\u3000\u81ea\u4ece\u90a3\u6b21\u60ca\u53d8\u4e4b\u540e\uff0c\u4ed6\u5df2\u53ea\u5269\u4e0b\u4e00\u526f\u6b8b\u7834\u7684\u8eab\u8eaf\u3002\u4f24\u900f\u7684\u5fc3\uff0c\u53ef\u662f\uff0c\u989c\u5915\u79bb\u5f00\u4ed6\u4ee5\u540e\uff0c\u4ed6\u53cd\u800c\u6ca1\u6709\u611f\u89c9\u5230\u5e78\u3002\u6216\u8005\u4e0d\u5e78\u4e86\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u5df1\u5931\u53bb\u4e86\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u751f\u547d\u7684\u8f89\u714c\uff0c\u5df2\u6c89\u5bc2\uff0c\u7eda\u70c2\u5df2\u6e10\u5265\u843d\uff0c\u5806\u7470\u800c\u593a\u76ee\u7684\uff0c\u5df2\u6e10\u892a\u8272\uff0c\u4ed6\u7684\u751f\u547d\u5df2\u50cf\u4e00\u58f0\u53f9\u606f\u7684\u540e\u534a\u622a\uff0c\u4e00\u5f20\u6b63\u5728\u79cb\u98ce\u4e2d\u98d8\u843d\u5730\u9762\u7684\u67af\u53f6\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u7684\u6b66\u529f\u548c\u5b66\u95ee\uff0c\u5374\u5728\u8fd9\u79cd\u4ed6\u81ea\u5632\u4e3a\u201c\u6d3b\u7740\u7684\u6b7b\u53bb\u201d\u7684\u60c5\u5f62\u4e0b\uff0c\u7a81\u98de\u731b\u8fdb\uff0c\u7ffb\u8d8a\u4e00\u5ea7\u53c8\u4e00\u5ea7\u7684\u9ad8\u5cf0\uff0c\u62b5\u8fbe\u8033\u76ee\u4e00\u65b0\u3001\u524d\u6240\u672a\u6709\u7684\u5883\u754c\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u4eba\u751f\u8981\u6709\u6240\u5f97\uff0c\u5fc5\u5148\u6709\u6240\u5931\uff1f \u3000\u3000\u2014\u2014\u96be\u9053\u975e\u8981\u6709\u6240\u5931\uff0c\u624d\u80fd\u6709\u6240\u5f97\uff1f \u3000\u3000\u2014\u2014\u7a76\u7adf\u5f97\u5931\u4e4b\u95f4\uff0c\u6709\u591a\u5c11\u5f97\u5931\uff1f \u3000\u3000\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u629b\u5f00\u4e86\u4e00\u5207\uff0c\u8fdb\u5165\u4e86\u65e0\u751f\u65e0\u6b7b\u3002\u65e0\u6b32\u65e0\u6c42\u7684\u5fc3\u5883\uff0c\u6452\u9664\u4e86\u4e00\u5207\u540e\u7684\u5251\u6cd5\uff0c\u4e5f\u5230\u8fbe\u4e86\u4e98\u53e4\u5bc2\u5bde\u3002\u9eef\u7136\u9500\u9b42\u3001\u95ee\u5929\u5929\u65e0\u8bed\u7684\u5883\u754c\u3002 \u3000\u3000\u4ed6\u771f\u7684\u4ece\u201c\u5929\u95ee\u5251\u6cd5\u201d\u518d\u7ec3\u6210\u4e86\u201c\u9500\u9b42\u5251\u6cd5\u201d\u3002 \u3000\u3000\u4e5f\u7ec3\u6210\u4e86\u8f7b\u529f\u63d0\u7eb5\u672f\u4e2d\u7684\u60ca\u4eba\u6210\u5c31\uff1a\u201c\u4e07\u53e4\u4e91\u9704\u4e00\u7fbd\u6bdb\u201d\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6ca1\u6709\u559c\u3002\u4e5f\u6ca1\u6709\u4e0d\u559c\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u4e00\u4e2a\u5e73\u5e38\u4eba\uff0c\u6709\u5e73\u5e38\u7684\u5fc3\uff0c\u60f3\u5e73\u5e38\u7684\u8fc7\u6d3b\uff0c\u5e73\u5e38\u7684\u8fc7\u53bb\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4ecd\u662f\u4e00\u4e2a\u4e0d\u51e1\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u4e0d\u51e1\u7684\u4eba\uff0c\u662f\u4e0d\u53ef\u80fd\u5e73\u51e1\u7684\u8fc7\u4e00\u8f88\u5b50\u7684\u3002 \u3000\u3000\u6d1b\u9633\u201c\u56db\u516c\u5b50\u201d\u4e4b\u4e89\uff0c\u7ec8\u4e8e\u50cf\u7076\u91cc\u7684\u706b\uff0c\u628a\u58f6\u5634\u903c\u51fa\u4e86\u6c34\u3002 \u3000\u3000\u4ed6\u4e5f\u903c\u51fa\u4e86\u5251\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4fbf\u89c1\u5230\u4e86\u4e00\u4e2a\u5343\u601d\u4e07\u76fc\u800c\u53c8\u6700\u4e0d\u60f3\u89c1\u7684\u4eba\u3002 \u3000\u3000\u989c\u5915\u3002 \u3000\u3000\u94dc\u58f3\u53d1\u51fa\u5636\u5636\u7684\u9e23\u54cd\u3002 \u3000\u3000\u65b9\u90aa\u771f\u89c9\u5f97\u4e00\u9635\u660f\u7729\uff0c\u8033\u9645\u8fd8\u6709\u70b9\u75d2\u75d2\u3002 \u3000\u3000\u2014\u2014\u90a3\u4e00\u70b9\u6d41\u661f\u5212\u7834\u4e86\u4ed6\u7684\u8033\u9645\uff0c\u4ed6\u7684\u751f\u547d\u4e5f\u51e0\u4e4e\u6ed1\u51fa\u4e86\u82cd\u7a79\u3002 \u3000\u3000\u6d3b\u7740\u4e0d\u662f\u4e00\u4ef6\u5feb\u4e50\u7684\u4e8b\uff0c\u4f46\u6b7b\u4e5f\u4e0d\u662f\u3002\u5bf9\u65b9\u90aa\u771f\u800c\u8a00\uff0c\u5feb\u4e50\u662f\u4ed6\u8fc7\u53bb\u7684\u7ea2\u7c89\uff1a\u989c\u5915\uff0c\u5e73\u9759\u662f\u4ed6\u73b0\u5728\u7684\u77e5\u97f3\uff1a\u60dc\u60dc\u3002 \u3000\u3000\u4ed6\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u6709\u672a\u6765\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u5ffd\u7136\u89c1\u5230\u8fc7\u53bb\u5411\u73b0\u5728\u8d70\u8fd1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4ece\u7eb1\u7a97\u89c1\u5230\u4e00\u4e2a\u4e3d\u5f71\u3002 \u3000\u3000\u4e00\u4e2a\u59d7\u59d7\u7684\u4eba\u5f71\u3002 \u3000\u3000\u4eba\u505c\u5728\u623f\u95e8\u524d\uff0c\u4e2b\u9b1f\u66ff\u5979\u63a8\u5f00\u4e86\u95e8\uff0c\u90a3\u58f0\u201c\u5440\u201d\u7684\u4e00\u58f0\uff0c\u5355\u8c03\u800c\u65e0\u60ca\u559c\uff0c\u4f46\u5728\u6668\u5149\u91cc\uff0c\u5374\u51fa\u73b0\u4e00\u4e2a\u5b9c\u55d4\u5b9c\u559c\u7684\u4eba\uff0c\u4e4d\u55d4\u4e4d\u559c\u7684\u8138\u3002 \u3000\u3000\u2014\u2014\u5c31\u662f\u8fd9\u5f20\u8138\uff0c\u4ee4\u4eba\u68a6\u9b42\u7275\u7cfb\u3002 \u3000\u3000\u4e00\u4e00\u5c31\u662f\u8fd9\u4e2a\u4eba\uff0c\u4f7f\u4ed6\u5931\u53bb\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u770b\u5230\u4e86\u8fd9\u5f20\u6e05\u6c34\u6837\u822c\u7684\u8138\u9765\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u5374\u662f\u5148\u60f3\u8d77\u4e86\u706b\u3002 \u3000\u3000\u706b\u6d77\u3002 \u3000\u3000\u6b7b\u5728\u7af9\u6805\u4e0a\u7684\u65b9\u7075\u3002 \u3000\u3000\u6b7b\u5728\u6cb8\u9505\u91cc\u7684\u8001\u7239\u3002 \u3000\u3000\u90a3\u4e00\u7247\u6bc1\u5c38\u706d\u8ff9\u7684\u706b\u7130\u3002 \u3000\u3000\u90a3\u4e2a\u50cf\u96f7\u6b9b\u4e0d\u6b7b\u795e\u6728\u822c\u7684\u5de8\u4eba\u3002 \u3000\u3000\u989c\u5915\u89c1\u4ed6\u5750\u8d77\uff0c\u8138\u4e0a\u6f3e\u8d77\u6b22\u5fed\u7684\u559c\u610f\uff0c\u201c\u4f60\u9192\u6765\u5566\uff1f\u201d\u5a62\u5973\u624b\u4e0a\u8fd8\u6258\u7740\u4e00\u4e2a\u9508\u91d1\u7684\u9ed1\u91c9\u6728\u76d8\uff0c\u76d8\u5b50\u4e0a\u8fd8\u653e\u7740\u4e00\u4e2a\u767d\u74f7\u84dd\u82b1\u7684\u76c5\u5b50\uff0c\u91cc\u9762\u6f2b\u7efd\u7740\u836f\u9999\uff1b\u989c\u5915\u7684\u795e\u8272\u5f88\u9ad8\u5174\uff0c\u4f46\u4e00\u5bf9\u7738\u5b50\uff0c\u5374\u6709\u4e9b\u7ea2\u4e1d\uff0c\u663e\u7136\u8fd9\u4e00\u591c\u95f4\uff0c\u5979\u6ca1\u6709\u4f11\u606f\u8fc7\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5f00\u53e3\u5c31\u95ee\uff1a\u201c\u6211\u7239\u7239\u5462\uff1f\u201d \u3000\u3000\u989c\u5915\u4e00\u6123\uff0c\u4e0b\u4e86\u6781\u5927\u51b3\u5fc3\u4f3c\u7684\uff0c\u5bf9\u4ed6\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8bed\u6c14\u6781\u51b7\uff0c\u201c\u5c0f\u5f1f\u5462\uff1f\u201d \u3000\u3000\u989c\u5915\u4e5f\u54ac\u7740\u5507\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u65b9\u90aa\u771f\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u518d\u95ee\uff1a\u201c\u56de\u4e07\u96f7\u5462\uff1f\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u91cd\u4f24\uff0c\u6709\u4eba\u628a\u4ed6\u6551\u4e86\u56de\u53bb\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7f04\u9ed8\u3002 \u3000\u3000\u4ed6\u633a\u8d77\u80cc\u810a\uff0c\u5750\u5728\u7af9\u69bb\u4e0a\uff0c\u592a\u9633\u8fd8\u672a\u5347\u8d77\uff0c\u6668\u5149\u82cd\u767d\u65e0\u529b\uff0c\u4ed6\u7684\u8f6e\u5ed3\u6df1\u523b\uff0c\u4f46\u770b\u53bb\u5374\u4e0d\u50cf\u4e00\u4e2a\u5251\u51fa\u4eba\u4ea1\u7684\u4fa0\u5ba2\uff0c\u53ea\u50cf\u4e00\u4e2a\u767d\u9996\u7a7a\u5e37\u7684\u6587\u5f31\u4e66\u751f\u3002 \u3000\u3000\u4e00\u4e2a\u6587\u79c0\u82cd\u767d\u7684\u4e66\u751f\u3002 \u3000\u3000\u65b9\u90aa\u771f\u597d\u4e00\u4f1a\u624d\u9053\uff1a\u201c\u6211\u7684\u5251\u5462\uff1f\u201d \u3000\u3000\u989c\u5915\u5fcd\u4e0d\u4f4f\u6447\u624b\uff0c\u5fcd\u4e0d\u4f4f\u628a\u624b\u642d\u5728\u4ed6\u624e\u7740\u84dd\u4e1d\u5dfe\u7684\u8155\u4e0a\u3002 \u3000\u3000\u7136\u540e\u5979\u7701\u89c9\u5230\uff0c\u629a\u5a11\u7740\u4e1d\u5dfe\uff0c\u7136\u540e\u8fd8\u662f\u7f29\u56de\u4e86\u624b\u3002\u201c\u4f60\u7684\u4f24\u672a\u597d\uff0c\u4f60\u4e0d\u80fd\u53bb\u3002\u201d \u3000\u3000\u65b9\u771f\u53ea\u662f\u518d\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u7684\u5251\u5462\uff1f\u201d \u3000\u3000\u989c\u5915\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u8fd8\u662f\u4ee5\u524d\u4e00\u6837\u7684\u813e\u6c14\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7ad9\u4e86\u8d77\u6765\uff0c\u989c\u5915\u5403\u60ca\u5730\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6f20\u7136\u9053\uff1a\u201c\u6ca1\u6709\u5251\uff0c\u6211\u4e5f\u4e00\u6837\u80fd\u53bb\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u8981\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u62a5\u4ec7\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u7136\u6709\u4e9b\u6fc0\u52a8\u4e86\u8d77\u6765\uff1a\u201c\u5982\u679c\u4f60\u7236\u4eb2\u65e0\u6545\u60e8\u6b7b\uff0c\u5f1f\u5f1f\u4e5f\u906d\u4eba\u6740\u5bb3\uff0c\u4f60\u80fd\u4e0d\u80fd\u4e0d\u53bb\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u989c\u5915\u4e0d\u80fd\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u56e0\u4e3a\u7b54\u6848\u592a\u8fc7\u660e\u663e\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u4e0d\u7b49\u5979\u7b54\u590d\uff0c\u5f80\u95e8\u5916\u8de8\u53bb\u3002 \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u627e\u8c01\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u201c\u4e00\u5207\u6709\u5173\u8fd9\u4e2a\u9634\u8c0b\u7684\u4eba\uff0c\u6240\u6709\u53c2\u4e0e\u6740\u5bb3\u7239\u7239\u548c\u5f1f\u5f1f\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u4f60\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u4f24\u6bd2\u672a\u597d\uff0c\u5c31\u8981\u53bb\u5999\u624b\u5802\uff0c\u8fd9\u4e0d\u662f\u62a5\u4ec7\uff0c\u800c\u662f\u9001\u6b7b\uff01\u201d \u3000\u3000\u201c\u8c01\u8bf4\u6211\u73b0\u5728\u8981\u53bb\u5999\u624b\u5802\uff1f\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u4e00\u4e2a\u4eba\u8981\u771f\u6b63\u7684\u62a5\u4ec7\uff0c\u53ef\u4ee5\u7b49\u4e00\u5e74\uff0c\u53ef\u4ee5\u7b49\u5341\u5e74\uff0c\u53ef\u4ee5\u7b49\u5230\u6700\u597d\u7684\u65f6\u673a\u3001\u6700\u9002\u5f53\u7684\u65f6\u5019\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u6025\u7740\u8981\u6740\u6b7b\u4ec7\u4eba\uff0c\u90a3\u4e0d\u662f\u62a5\u4ec7\uff0c\u800c\u662f\u6cc4\u6124\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u56de\u4e07\u96f7\u5728\u6740\u4eba\u7684\u5730\u65b9\u51fa\u73b0\uff0c\u4e0d\u4e00\u5b9a\u5c31\u662f\u4ed6\u6740\u4eba\u3002\u201d \u3000\u3000\u989c\u5915\u987f\u611f\u653e\u5fc3\uff1a\u65b9\u90aa\u771f\u5728\u6b64\u65f6\u6b64\u9645\u4ecd\u80fd\u4fdd\u6301\u7406\u667a\uff0c\u8fd9\u70b9\u82e5\u6362\u4f5c\u662f\u5979\uff0c\u81ea\u95ee\u4e5f\u4e0d\u4e00\u5b9a\u80fd\u505a\u5230\uff0c\u201c\u90a3\u4e48\u2026\u2026\u4f60\u8981\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u76f8\u601d\u6797\u3002\u201d \u3000\u3000\u201c\u6e38\u5bb6\uff1f\u201d \u3000\u3000\u201c\u5c0f\u78a7\u6e56\u3002\u201d \u3000\u3000\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u7239\u7239\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5c0f\u5f1f\u4e5f\u88ab\u7275\u7d2f\uff1b\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u6211\u8fd8\u6709\u4e00\u4e2a\u670b\u53cb\uff0c\u73b0\u5728\u53ef\u80fd\u5728\u76f8\u601d\u4ead\u4e0a\u4f5c\u6b8a\u6b7b\u6218\uff0c\u5371\u5728\u65e6\u5915\uff0c\u6211\u4e0d\u60f3\u8fde\u4ed6\u4e5f\u4e27\u5931\u6027\u547d\u3002\u201d \u3000\u3000\u989c\u5915\u60ca\u5f02\u5730\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u8ffd\u547d\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5df2\u8d70\u5230\u95e8\u524d\uff0c\u95e8\u4ecd\u662f\u655e\u5f00\u7740\uff0c\u5916\u9762\u957f\u5eca\u8377\u5858\uff0c\u5e7d\u96c5\u5982\u753b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u9635\u9690\u75db\uff1a\u60f3\u8fd9\u4e9b\u5e74\u6765\uff0c\u5979\u4f4f\u5728\u8fd9\u513f\uff0c\u7b97\u662f\u5929\u4e0a\u4eba\u95f4\u4e86\uff0c\u8fd9\u4e9b\u7f8e\u666f\u96c5\u9601\uff0c\u5927\u6982\u4e5f\u51fa\u81ea\u5979\u4e00\u624b\u5e03\u7f6e\u7684\u7f62\uff1f\u4ed6\u5374\u4eba\u5728\u964b\u5df7\uff0c\u8fde\u8ddf\u4ed6\u4e00\u7baa\u98df\u3001\u4e00\u74e2\u996e\u7684\u8001\u7236\u548c\u5c0f\u5f1f\uff0c\u7adf\u90fd\u6a2a\u906d\u6bd2\u624b\uff01 \u3000\u3000\u53ef\u89c1\u4eba\u751f\u91cc\uff0c\u771f\u7684\u4f1a\u6709\u5e78\u4e0e\u4e0d\u5e78\u7684\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u5f53\u65e5\u5979\u8ddf\u4e86\u7ed9\u81ea\u5df1\uff0c\u53c8\u662f\u600e\u6837\u4e00\u79cd\u5c40\u9762\u5462\uff1f \u3000\u3000\u4ed6\u8fd9\u6837\u60f3\u7740\u7684\u65f6\u5019\uff0c\u5fc3\u4e2d\u88ab\u65e0\u540d\u7684\u6012\u706b\u548c\u83ab\u540d\u7684\u5992\u706b\u4ea4\u7ec7\u7740\uff0c\u6ca1\u6709\u56de\u7b54\u989c\u5915\u7684\u8bdd\u3002 \u3000\u3000\u989c\u5915\u5374\u4ecd\u7136\u628a\u8bdd\u8bf4\u4e0b\u53bb\uff1a\u201c\u6d1b\u9633\u56db\u516c\u5b50\uff0c\u5343\u65b9\u767e\u8ba1\uff0c\u91cd\u91d1\u539a\u8058\uff0c\u5a01\u8feb\u5229\u8bf1\uff0c\u4f60\u90fd\u4e0d\u80af\u76f8\u52a9\u4e8e\u4e00\u6307\u4e4b\u529b\uff0c\u53ef\u662f\uff0c\u4f60\u8ddf\u8ffd\u547d\u53ea\u4e0d\u8fc7\u624d\u89c1\u8fc7\u4e00\u6b21\u9762\uff0c\u4f60\u660e\u77e5\u4ed6\u662f\u4e03\u53d1\u7985\u5e08\u3001\u8521\u65cb\u949f\u3001\u65ad\u7709\u77f3\u7b49\u4eba\u975e\u94f2\u9664\u4e0d\u53ef\u7684\u5bf9\u8c61\uff0c\u4f60\u4ecd\u662f\u8981\u4e3a\u4ed6\u5356\u547d\uff01\u4f60\u2026\u2026\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u6ca1\u6709\u53d8\u2026\u2026\u4f60\u8fd8\u662f\u90a3\u6837\u7684\u813e\u6027\uff01\u201d \u3000\u3000\u201c\u8fd9\u53e5\u8bdd\u4f60\u521a\u624d\u5df2\u7ecf\u8bf4\u8fc7\u4e86\u3002\u6211\u98ce\u6d41\u6210\u6027\u3001\u6d6e\u840d\u4e00\u822c\u7684\u4e0d\u5b89\u5b9a\uff0c\u4e0d\u6c42\u95fb\u8fbe\uff0c\u5b64\u82b3\u81ea\u8d4f\u2026\u2026\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u6211\u8fd8\u662f\u8001\u6837\u5b50\uff1a\u6211\u4ecd\u7136\u4f1a\u5bf9\u4eba\u6b7b\u5fc3\u584c\u5730\u505a\u50bb\u4e8b\uff0c\u53ea\u8981\u6211\u5fc3\u7518\u60c5\u613f\u4e0d\u60dc\u6d12\u5c3d\u4e00\u8eab\u70ed\u8840\u2026\u2026\u8fd9\u4e9b\u5f53\u65e5\u4f7f\u4f60\u79bb\u5f00\u6211\u7684\u574f\u813e\u6027\uff0c\u6211\u5012\u4e00\u6837\u4e0d\u7f3a\u3002\u201d \u3000\u3000\u989c\u5915\u770b\u7740\u4ed6\uff0c\u770b\u7740\u4ed6\uff0c\u770b\u7740\u4ed6\u597d\u4e00\u4f1a\u624d\u9053\uff1a\u201c\u4f60\u771f\u7684\u4ee5\u4e3a\u6211\u662f\u56e0\u4e3a\u8fd9\u6837\u624d\u79bb\u5f00\u4f60\u7684\u5417\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6478\u6478\u8033\u5782\uff0c\u770b\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u989c\u5915\u9053\uff1a\u201c\u4f60\u4e3a\u5565\u4e0d\u62ab\u4e0a\u957f\u886b\u624d\u8d70\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5faa\u5979\u624b\u6307\u5904\u770b\u53bb\uff0c\u53ea\u89c1\u8fd1\u5899\u7684\u7af9\u6905\u9760\u4e0a\u67b6\u7740\u4ed6\u90a3\u4e00\u4ef6\u767d\u886b\uff0c\u4ed6\u8fd9\u6837\u770b\u53bb\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u60f3\u8d77\u5f53\u65e5\u5f88\u591a\u4ed6\u548c\u5979\u5728\u4e00\u8d77\u7684\u60c5\u5883\uff0c\u4ed6\u89c9\u5f97\u5341\u5206\u9707\u8be7\uff1a\u8001\u7239\u548c\u5c0f\u5f1f\u521a\u906d\u4eba\u6bd2\u624b\uff0c\u4ed6\u600e\u4e48\u8fd8\u4f1a\u60f3\u8d77\u8fd9\u4e9b\u5f80\u65e5\u7f20\u7ef5\u3001\u8fc7\u53bb\u4f24\u60c5\u7684\u4e8b\uff1f \u3000\u3000\u4ed6\u62ff\u8d77\u767d\u886b\u7684\u65f6\u5019\uff0c\u624d\u53d1\u73b0\u886b\u670d\u4e4b\u4e0b\u5c31\u662f\u659c\u501a\u7740\u90a3\u628a\u5251\u3002 \u3000\u3000\u706d\u9b42\u5251\u3002 \u3000\u3000\u4ed6\u628a\u5251\u62ff\u5728\u624b\u91cc\uff0c\u4eff\u4f5b\u4e45\u8fdd\u4e86\u7684\u7231\u4eba\uff0c\u56de\u5230\u4ed6\u7684\u6000\u62b1\u91cc\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\u60dc\u60dc\u3002 \u3000\u3000\u4ed6\u5728\u8981\u8d70\u51fa\u95e8\u69db\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u505c\u4f4f\u4e86\u811a\u6b65\uff0c\u95ee\uff1a\u201c\u4f60\u771f\u7684\u8981\u6211\u52a0\u5165\u5170\u4ead\u6c60\u5bb6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u3002\u201d \u3000\u3000\u8fd9\u7b54\u6848\u51fa\u4e4e\u65b9\u90aa\u771f\u610f\u6599\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u7684\u786e\u662f\u4e2a\u662f\u975e\u4e4b\u5730\uff0c\u800c\u4e14\u662f\u4e00\u4e2a\u6781\u5927\u7684\u9677\u997c\uff0c\u8c01\u6389\u8fdb\u53bb\uff0c\u90fd\u4ee5\u4e3a\u81ea\u5df1\u5728\u5e03\u4e0b\u5927\u7f57\u5730\u7f51\uff0c\u5176\u5b9e\u6210\u4e86\u7f51\u4e2d\u4eba\u8fd8\u4e0d\u81ea\u77e5\u3002\u201d\u989c\u5915\u9053\uff0c\u201c\u8fd9\u513f\u4e0d\u9002\u5408\u4f60\uff0c\u91cc\u5934\u7684\u4eba\u90fd\u75af\u4e86\uff0c\u6ca1\u75af\u7684\u4eba\u722c\u4e0d\u51fa\u6765\uff0c\u9664\u975e\u75af\u5b50\u624d\u4f1a\u60f3\u8fdb\u53bb\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u89c2\u5bdf\u7740\u5979\uff1a\u201c\u4e3a\u751a\u4e48\u4f60\u5148\u524d\u53c8\u5e0c\u671b\u6211\u52a0\u5165\uff1f\u8fd8\u4eb2\u81ea\u8dd1\u5230\u5927\u9690\u4e18\u6765\u6e38\u8bf4\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5148\u524d\u4e0d\u77e5\u9053\u4f60\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u5728\u77e5\u9053\u662f\u6211\u540e\uff0c\u4ecd\u8981\u6211\u7559\u52a9\u6c60\u5bb6\u3002 \u3000\u3000\u201c\u6211\u4e4d\u89c1\u4f60\uff0c\u6211\u2026\u2026\u6ca1\u6709\u529e\u6cd5\u63a7\u5236\uff0c\u60f3\u8981\u4f60\u7559\u4e0b\u6765\uff0c\u73b0\u5728\u6211\u5df2\u51b7\u9759\u4e86\uff0c\u5e73\u9759\u4e86\uff0c\u60f3\u8fc7\u4e86\uff0c\u5f88\u660e\u767d\u4f60\u4f5c\u7684\u9009\u62e9\u662f\u5bf9\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u9009\u62e9\uff1f\u751a\u4e48\u9009\u62e9\uff1f\u201d \u3000\u3000\u201c\u7f6e\u8eab\u4e8b\u5916\uff0c\u8fdc\u79bb\u6d1b\u9633\u3002\u201d \u3000\u3000\u201c\u6211\u9009\u62e9\u4e86\u4e48\uff1f\u4e16\u4e8b\u80fd\u5bb9\u8ba9\u6211\u9009\u62e9\u5417\uff1f\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u597d\uff0c\u5982\u679c\u6211\u80fd\u591f\u9009\u62e9\uff0c\u6211\u5c31\u9009\u62e9\u4f60\u89c9\u5f97\u6211\u4e0d\u8be5\u9009\u62e9\u7684\uff0c\u6211\u8981\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u201d\u989c\u5915\u6c14\u767d\u4e86\u8138\uff0c\u201c\u4f60\u4e3a\u751a\u4e48\u504f\u8981\u2026\u2026\u90a3\u503c\u5f97\u5417\uff1f\uff01\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u6211\u4e2d\u4e86\u4f60\u7684\u6fc0\u5c06\u6cd5\u597d\u4e86\uff1a\u4f60\u8981\u6211\u7559\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u6211\u4e0d\u7559\uff1b\u4f60\u4e0d\u8981\u6211\u7559\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u6211\u504f\u7559\u3002\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u5c31\u50cf\u5f53\u65e5\u4f60\u5bf9\u6211\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u53ef\u4ee5\u7559\u4e0b\u6765\uff0c\u201d\u989c\u5915\u8bed\u65e0\u4f26\u6b21\u7684\u8bf4\uff0c\u201c\u4f60\u7559\u4e0b\u6765\u4f5c\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u6628\u591c\u4ee5\u524d\uff0c\u6211\u4e0d\u7559\u4e0b\u6765\uff0c\u662f\u6015\u8fde\u7d2f\u4e86\u4eba\uff0c\u6015\u8fde\u7d2f\u8001\u7239\u3001\u5c0f\u5f1f\u548c\u60dc\u60dc\u2026\u2026\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u73b0\u5728\u8001\u7239\u6b7b\u4e86\u3001\u5c0f\u5f1f\u4e5f\u90fd\u6b7b\u4e86\uff0c\u6211\u8981\u7559\u4e0b\u6765\u66ff\u4ed6\u4eec\u62a5\u4ec7\uff0c\u800c\u4e14\u51b3\u4e0d\u8ba9\u60dc\u60dc\u518d\u53d7\u7275\u7d2f\u3002\u2026 \u3000\u3000\u201c\u4f60\u8bb0\u4f4f\uff0c\u201d\u65b9\u90aa\u771f\u957f\u7b11\u51fa\u95e8\uff0c\u628a\u989c\u5915\u7559\u5728\u623f\u91cc\uff0c\u201c\u6211\u4e0d\u662f\u56e0\u4e3a\u4f60\u624d\u7559\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u6f2b\u7b11\u7740\u8d70\u51fa\u957f\u5eca\uff0c\u5f97\u610f\u975e\u51e1\u3002 \u3000\u3000\u53ea\u6709\u8fce\u9762\u89c1\u7740\u4ed6\u7684\u4eba\uff0c\u624d\u80fd\u770b\u89c1\u4ed6\u7b11\u5f97\u5341\u5206\u75db\u82e6\u7684\u8138\u5bb9\u3002 \u3000\u3000\u6b64\u9645\u624d\u662f\u536f\u672a\u8fb0\u521d\uff0c\u6c60\u65e5\u66ae\u5728\u4e00\u95f4\u5f88\u7279\u6b8a\u7684\u623f\u91cc\uff0c\u7cbe\u795e\u975e\u5e38\u7684\u597d\u3002 \u3000\u3000\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u6628\u591c\u6839\u672c\u6ca1\u6709\u4f11\u606f\u8fc7\u3002 \u3000\u3000\u4ed6\u5728\u805a\u7cbe\u4f1a\u795e\u7684\u770b\u4e00\u4ef6\u4e1c\u897f\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u7528\u624b\u62ff\u7740\u90a3\u4ef6\u4e1c\u897f\uff0c\u800c\u662f\u4e00\u679d\u767d\u94a2\u6253\u94f8\u7684\u7ec6\u94b3\uff0c\u94b3\u7740\u90a3\u4ef6\u4e8b\u7269\u7ec6\u770b\uff0c\u624b\u4e0a\u8fd8\u5e26\u7740\u4e09\u5c42\u7684\u5c0f\u725b\u76ae\u624b\u5957\u3002 \u3000\u3000\u81f3\u4e8e\u8bf4\u90a3\u662f\u4e00\u95f4\u5947\u7279\u7684\u623f\u5b50\uff0c\u90a3\u662f\u56e0\u4e3a\u8fd9\u95f4\u623f\u5b50\u6302\u6ee1\u4e86\u5404\u79cd\u5404\u7c7b\u3001\u5404\u5f0f\u5404\u6837\u7684\u5175\u5668\u3002 \u3000\u3000\u8fd9\u4e9b\u5175\u5668\u6709\u5e38\u89c1\u7684\uff0c\u6709\u4e0d\u5e38\u89c1\u7684\uff0c\u751a\u81f3\u6709\u7684\u6839\u672c\u8fd8\u672a\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u8fc7\u7684\uff0c\u6709\u7684\u8fd8\u5728\u5b9e\u9a8c\u4e2d\uff0c\u4ecd\u672a\u51fa\u4e16\u3002 \u3000\u3000\u6709\u7684\u5175\u5668\u6302\u5728\u5899\u4e0a\uff0c\u6709\u7684\u7f6e\u4e8e\u5175\u5668\u67b6\u4e0a\uff0c\u8fd9\u4e9b\u5175\u5668\u5e94\u6709\u5c3d\u6709\uff0c\u4e0d\u5e94\u6709\u4e5f\u5c3d\u6709\uff0c\u6709\u957f\u7684\u6709\u77ed\u7684\uff0c\u6709\u8f6f\u7684\u6709\u786c\u7684\uff0c\u8fde\u938f\u91d1\u51e4\u7fc5\u9557\u8fd9\u79cd\u72ec\u95e8\u5175\u5668\uff0c\u4e5f\u5360\u4e00\u5e2d\u4f4d\uff1b\u5c31\u8fde\u5b50\u6bcd\u79bb\u9b42\u5708\u8fd9\u7c7b\u7edd\u95e8\u6b66\u5668\uff0c\u4e5f\u4e00\u6837\u5217\u5728\u67b6\u4e0a\u3002\u751a\u81f3\u8fd8\u6709\u6c5f\u5357\u9739\u96f3\u5802\u7684\u201c\u96f7\u516c\u5f39\u201d\uff0c\u4ee5\u53ca\u5ddd\u4e2d\u9ad8\u624b\u5510\u6708\u4eae\u7684\u5947\u95e8\u6697\u5668\uff1a\u201c\u4e2d\u79cb\u6708\u91cc\u7684\u5c0f\u96e8\u201d\uff0c\u5728\u8fd9\u91cc\u7adf\u7136\u4e5f\u53ef\u4ee5\u89c1\u5f97\u5230\u3002\u8fd8\u6709\u4e00\u4e9b\u4e0d\u662f\u6b66\u5668\u7684\u6b66\u5668\uff0c\u5305\u62ec\u94c1\u7b1b\u3001\u7ee2\u5e15\u3001\u70db\u53f0\uff0c\u5982\u679c\u8fd9\u4e5f\u7b97\u662f\u201c\u5175\u5668\u201d\uff0c\u8fde\u65b9\u90aa\u771f\u4e5f\u4e0d\u77e5\u5982\u4f55\u4f7f\u7528\u6cd5\u3002 \u3000\u3000\u4e0d\u8fc7\u53ea\u8981\u4e00\u4e2a\u5bf9\u6b66\u672f\u7a0d\u7aa5\u95e8\u5f84\u7684\u6c5f\u6e56\u4eba\uff0c\u4e00\u65e6\u8e0f\u5165\u8fd9\u4e2a\u5730\u65b9\uff0c\u5fc5\u4f1a\u88ab\u8fd9\u4e9b\u7433\u7405\u6ee1\u76ee\u3001\u591a\u4e0d\u80dc\u6570\uff0c\u800c\u4e14\u7edd\u5bf9\u96be\u5f97\u4e00\u89c1\u7684\u5175\u5668\u6240\u6151\u4f4f\uff1a\u8981\u6536\u96c6\u8fd9\u4e9b\u5404\u5bb6\u5404\u6d3e\u7684\u5175\u5668\uff0c\u7a76\u7adf\u8981\u82b1\u591a\u5c11\u65f6\u95f4\u3001\u591a\u5c11\u7cbe\u529b\u3001\u591a\u5c11\u5fc3\u8840\u3001\u591a\u5c11\u91d1\u94b1\uff1f\u90a3\u662f\u96be\u4ee5\u4f30\u8ba1\u7684\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u7684\u5ea7\u4f4d\uff0c\u6b63\u9762\u5bf9\u7740\u7a97\u3002 \u3000\u3000\u4ed6\u7684\u4f4d\u7f6e\u4e5f\u975e\u5e38\u7279\u522b\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u65f6\u5206\uff0c\u53ea\u8981\u6709\u9633\u5149\u6216\u6708\u4eae\uff0c\u5149\u7ebf\u90fd\u5b9a\u80fd\u4f1a\u7167\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u73b0\u5728\u9633\u5149\u8fd8\u4e0d\u662f\u5f88\u5f3a\u70c8\uff0c\u6240\u4ee5\u4ed6\u70b9\u71c3\u4e86\u6848\u4e0a\u7684\u516b\u652f\u5de8\u70db\uff0c\u628a\u4ed6\u7684\u8138\u5bb9\uff0c\u6620\u7167\u5f97\u4e00\u7247\u660e\u9ec4\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u805a\u7cbe\u4f1a\u795e\u7684\u770b\u624b\u4e0a\u94b3\u7740\u4e00\u4ef6\u7ec6\u5fae\u7684\u7269\u4ef6\uff0c\u90a3\u4e8b\u7269\u5728\u70db\u5149\u548c\u9633\u5149\u6d41\u7167\u4e0b\uff0c\u5076\u7136\u7efd\u51fa\u5947\u5f02\u7684\u5149\u8292\u3002 \u3000\u3000\u4ed6\u770b\u5f97\u90a3\u672b\u4e13\u5fc3\uff0c\u4ee5\u81f4\u65b9\u90aa\u771f\u8d70\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u4f3c\u4e4e\u4e00\u70b9\u513f\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5728\u4ed6\u80cc\u540e\u4ec1\u7acb\u4e86\u597d\u4e45\u597d\u4e45\uff0c\u7136\u540e\u624d\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u50cf\u521a\u624d\u90a3\u6837\uff0c\u6211\u53ef\u4ee5\u6740\u6b7b\u4f60\u51e0\u6b21\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5c45\u7136\u6ca1\u6709\u5403\u60ca\uff0c\u4e5f\u6ca1\u6709\u56de\u5934\uff0c\u53ea\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u987f\u4e86\u4e00\u987f\uff0c\u8d1f\u624b\u770b\u5899\u4e0a\u7684\u5175\u5668\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\uff0c\u5982\u679c\u6ca1\u6709\u5170\u4ead\u6c60\u5bb6\u5927\u516c\u5b50\u3001\u4e8c\u516c\u5b50\u7684\u5141\u53ef\uff0c\u8c01\u4e5f\u4f11\u60f3\u8e0f\u5165\u2018\u5175\u5668\u623f\u2019\u534a\u6b65\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u8fd9\u513f\u662f\u6709\u57cb\u4f0f\uff0c\u5e73\u65f6\u5f53\u7136\u90fd\u4e0d\u663e\u9732\u51fa\u6765\uff0c\u4e0d\u8fc7\u5bf9\u65b9\u5c11\u4fa0\u4f8b\u5916\u3002\u201d\u6c60\u65e5\u66ae\u8bf4\uff0c\u201c\u6211\u5df2\u9881\u5e03\u4e0b\u53bb\uff0c\u5170\u4ead\u6c60\u5bb6\uff0c\u53ea\u8981\u65b9\u5c11\u4fa0\u559c\u6b22\uff0c\u5f80\u90a3\u91cc\u8d70\u3001\u505a\u751a\u4e48\u90fd\u884c\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6c89\u9ed8\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u5ffd\u9053\uff1a\u201c\u4f60\u597d\u4e86\u70b9\u6ca1\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u8981\u6551\u6211\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u95ee\u975e\u6240\u7b54\uff1a\u201c\u4e03\u53d1\u5927\u5e08\u5f88\u662f\u8d39\u4e86\u70b9\u529f\u592b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u90a3\u60f3\u5fc5\u662f\u56e0\u4e3a\u4f60\u7684\u547d\u4ee4\u4e4b\u6545\u3002\u201d \u3000\u3000\u201c\u4e03\u53d1\u5927\u5e08\u662f\u6211\u7684\u4e0a\u5bbe\uff0c\u6211\u53ea\u6562\u8981\u6c42\u4ed6\uff0c\u4e0d\u80fd\u8bf4\u662f\u547d\u4ee4\uff1b\u201d\u6c60\u65e5\u66ae\u9053\uff0c\u201c\u4f55\u51b5\uff0c\u5ac2\u5b50\u5bf9\u9601\u4e0b\uff0c\u5341\u5206\u5173\u5207\uff0c\u50cf\u8fd9\u6837\u4e00\u4f4d\u7edd\u4e16\u624d\u4eba\uff0c\u6211\u53c8\u600e\u80fd\u4e0d\u7aed\u529b\u4fdd\u5168\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u7b11\u9053\uff1a\u201c\u82e5\u662f\u4fdd\u5168\u4e0d\u4e86\uff0c\u90a3\u662f\u6c60\u5bb6\u7684\u4e0d\u5e78\uff0c\u6211\u7684\u803b\u8fb1\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u53ea\u95ee\uff1a\u201c\u4e03\u53d1\u5927\u5e08\u5462\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u4ed6\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7d27\u5f20\u4e86\u8d77\u6765\uff1a\u201c\u4ed6\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u5c0f\u78a7\u6e56\uff0c\u6e38\u5bb6\uff0c\u76f8\u601d\u6797\u4e2d\u76f8\u601d\u4ead\u3002\u201d \u3000\u3000\u201c\u4ed6\u53bb\u4e86\u591a\u4e45\uff1f\u201d\u65b9\u90aa\u771f\u7d27\u63a5\u7740\u95ee\u3002 \u3000\u3000\u201c\u4ed6\u8d70\u4e86\u624d\u4e0d\u8fc7\u662f\u4f60\u6765\u8fd9\u513f\u7684\u4e00\u76cf\u8336\u65f6\u95f4\uff0c\u4f60\u653e\u5fc3\uff0c\u76f8\u601d\u6797\u4e2d\u201c\u53e3\u679c\u8bbe\u5bb4\uff0c\u90a3\u4e48\u9e3f\u95e8\u5bb4\u5c1a\u672a\u5f00\u7b75\uff1b\u5982\u679c\u662f\u4e00\u573a\u6218\u5c40\uff0c\u90a3\u4e48\u6218\u7aef\u4ecd\u672a\u542f\u2026\u2026\u201d\u6c60\u65e5\u66ae\u8bed\u950b\u4e00\u8f6c\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5728\u770b\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6c60\u65e5\u66ae\u4e00\u5b9a\u4f1a\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u679c\u7136\u8bf4\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u98de\u661f\uff0c\u201d\u4ed6\u8d5e\u7fa1\u5730\u9053\uff1a\u201c\u68a6\u91cc\u7684\u98de\u661f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u76b1\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u4ed6\u4e0d\u660e\u767d\u6c60\u65e5\u66ae\u5728\u8bf4\u751a\u4e48\u3002 \u3000\u3000\u4f46\u4ed6\u77e5\u9053\u6c60\u65e5\u66ae\u8fd9\u6837\u8bf4\uff0c\u5fc5\u5b9a\u6709\u4ed6\u7684\u539f\u6545\u3002 \u3000\u3000\u2014\u2014\u8fd9\u6c60\u5bb6\u4e8c\u516c\u5b50\uff0c\u770b\u6765\u8981\u6bd4\u4ed6\u6240\u77e5\u9053\u7684\u66f4\u4e0d\u6613\u5e94\u4ed8\uff0c\u800c\u4e14\u4e0d\u6613\u5e94\u4ed8\u5f97\u591a\u4e86\u3002 \u3000\u3000\u201c\u6697\u5668\uff0c\u201d\u6c60\u65e5\u66ae\u4ecd\u7136\u611f\u53f9\u7684\u9053\uff0c\u201c\u90a3\u9897\u5212\u8fc7\u4f60\u7684\u8033\u9645\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u201c\u6211\u4ece\u6765\u6ca1\u6709\u770b\u8fc7\u90a3\u4e48\u7cbe\u5de7\u3001\u90a3\u4e48\u7ec6\u81f4\u3001\u90a3\u4e48\u53ef\u6015\u3001\u90a3\u4e48\u6bd2\u800c\u53c8\u90a3\u4e48\u7f8e\u4e3d\u7684\u6697\u5668\u3002\u201d\u6c60\u65e5\u66ae\u773c\u775b\u53d1\u7740\u4eae\uff0c\u4e0e\u4ed6\u624b\u4e2d\u7684\u98de\u661f\u5bf9\u95ea\uff1a\u201c\u7b80\u76f4\u50cf\u4e00\u9897\u98de\u661f\uff0c\u5728\u68a6\u4e2d\u624d\u4f1a\u95ea\u73b0\u3002\u201d \u3000 \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u51fb\u638c\u4e3a\u8a93 \u3000 \u3000\u3000\u65b9\u90aa\u771f\u6478\u6478\u81ea\u5df1\u7684\u5de6\u8033\u3002 \u3000\u3000\u8033\u5782\u4f3c\u4e4e\u8fd8\u6709\u70b9\u75d2\u75d2\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u90a3\u679a\u6697\u5668\u3002 \u3000\u3000\u4e00\u9897\u6084\u7136\u800c\u4fcf\u7136\u7684\u98de\u661f\uff0c\u5728\u751f\u6b7b\u4e00\u53d1\u95f4\u4f1a\u7a81\u7136\u80c0\u5927\u3001\u53d1\u51fa\u6dec\u5389\u7684\u5149\u8292\uff0c\u4ee5\u4e00\u79cd\u60ca\u4eba\u7684\u7f8e\u8273\uff0c\u8ba9\u4f60\u5c4f\u606f\u5728\u5b83\u7684\u5a01\u529b\uff0c\u76ee\u7729\u4e8e\u5b83\u7684\u5e7b\u5f69\uff0c\u5e76\u5728\u5b83\u7684\u60ca\u8273\u91cc\u5931\u53bb\u4e86\u6027\u547d\u3002 \u3000\u3000\u6bd4\u6619\u82b1\u8fd8\u7f8e\u3002 \u3000\u3000\u6bd4\u6d41\u661f\u8fd8\u5feb\u3002 \u3000\u3000\u6bd4\u65f6\u95f4\u8fd8\u5306\u5306\u3002 \u3000\u3000\u2014\u2014\u65f6\u95f4\uff0c\u662f\u6700\u5feb\u7684\u6d41\u901d\uff0c\u5f53\u4f60\u60f3\u5230\u201c\u65f6\u95f4\u201d\u7684\u65f6\u5019\uff0c\u60f3\u5230\u201c\u65f6\u95f4\u201d\u7684\u65f6\u95f4\u5df2\u7136\u901d\u53bb\uff0c\u800c\u4e14\u4e00\u901d\u6c38\u65e0\u8e2a\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8bb0\u5f97\u8fd9\u9897\u661f\u661f\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u51e0\u4e4e\u88ab\u8fd9\u9897\u98de\u901d\u7684\u661f\u5b50\uff0c\u9001\u8d70\u4e86\u6d41\u661f\u5728\u82cd\u7a79\u5212\u8fc7\u4e00\u822c\u707f\u4eae\u800c\u51c4\u843d\u7684\u751f\u547d\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u4e00\u9897\u661f\u661f\uff0c\u5728\u6c60\u65e5\u66ae\u7684\u624b\u4e0a\uff0c\u56de\u5230\u4e86\u5e73\u9759\u548c\u539f\u6765\u7684\u9762\u76ee\uff0c\u4f46\u4f9d\u7136\u662f\u90a3\u4e48\u7684\u7f8e\uff0c\u6d41\u9732\u51fa\u5f02\u91c7\uff0c\u95ea\u70c1\u7740\u5947\u8272\uff0c\u90a3\u4e48\u5c0f\u5c0f\u7684\u4e00\u9897\u50cf\u94bb\u77f3\u4f3c\u7684\u6697\u5668\uff0c\u7a76\u7adf\u662f\u751a\u4e48\u6784\u6210\u7684\u5462\uff1f\u7adf\u66fe\u53d1\u52a8\u8fd9\u822c\u53ef\u6015\u7684\u6740\u4f24\u529b\uff01 \u3000\u3000\u6240\u4ee5\u4ed6\u4e5f\u5fcd\u4e0d\u4f4f\u53d1\u51fa\u8d5e\u7fa1\uff1a\u201c\u597d\u4e00\u9897\u661f\u661f\uff01\u201d \u3000\u3000\u201c\u597d\u4e00\u9897\u98de\u661f\uff01\u201d\u6c60\u65e5\u66ae\u9053\uff0c\u201c\u8fd9\u6765\u81ea\u98de\u661f\u5b50\u7684\u2018\u4e03\u661f\u4f34\u6708\u2019\uff0c\u4e0d\u8fc7\u8fd8\u6ca1\u6709\u7206\u5f00\u6765\uff0c\u98de\u5165\u4e86\u7076\u5e95\u91cc\uff0c\u6211\u7b49\u706b\u573a\u6251\u706d\u540e\uff0c\u5c31\u8bf7\u4e13\u4eba\u53bb\u8d77\u51fa\u8fd9\u679a\u661f\u5b50\u3002\u5176\u4e2d\u6709\u4e24\u4e2a\u4eba\u4e0d\u5c0f\u5fc3\uff0c\u4e00\u4e2a\u6b7b\u4e86\uff0c\u4e00\u4e2a\u8981\u780d\u6389\u4e00\u6761\u80f3\u81c2\u3002\u201d \u3000\u3000\u2014\u2014\u4ed6\u6240\u8bf4\u7684\u201c\u4e13\u4eba\u201d\uff0c\u5b9a\u5fc5\u662f\u6b66\u5668\u3001\u5175\u5668\u3001\u6697\u5668\u751a\u81f3\u706b\u5668\u7684\u4e13\u5bb6\uff0c\u80fd\u8d77\u51fa\u8fd9\u4e00\u679a\u7684\u661f\u5b50\uff0c\u5e76\u80fd\u4fdd\u7559\u539f\u72b6\uff0c\u4e0d\u8ba9\u5b83\u70b8\u4e86\u5f00\u6765\uff0c\u80af\u5b9a\u662f\u4ef6\u5728\u8001\u864e\u5634\u91cc\u62d4\u7259\u4e00\u822c\u5371\u9669\u7684\u4e8b\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u5f88\u660e\u767d\uff1a\u8fd9\u679a\u98de\u661f\u867d\u672a\u66fe\u7206\u4e86\u5f00\u6765\uff0c\u4f46\u5df2\u53d1\u6325\u4e86\u6bd4\u7206\u70b8\u5f00\u6765\u66f4\u5927\u7684\u5a01\u529b\u4e0e\u707f\u4eae\u3002 \u3000\u3000\u201c\u8fd9\u662f\u98de\u661f\u5b50\u7684\u2018\u4e03\u661f\u4f34\u6708\u2019\uff0c\u542c\u8bf4\u662f\u6839\u636e\u5510\u6708\u4eae\u7684\u72ec\u95e8\u6697\u5668\u2018\u68a6\u88f3\u2019\u4eff\u5236\u7684\uff0c\u6211\u8fd9\u513f\u6536\u96c6\u6709\u8bb8\u591a\u8ba9\u4eba\u89c1\u6240\u672a\u89c1\u3001\u95fb\u6240\u672a\u95fb\u7684\u5175\u5668\u548c\u6697\u5668\uff0c\u6700\u4ee4\u6211\u81ea\u8c6a\u7684\u4e00\u4ef6\u6697\u5668\uff0c\u4fbf\u662f\u2018\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u2019\uff0c\u201d\u6c60\u65e5\u66ae\u5728\u770b\u638c\u4e2d\u90a3\u4ef6\u6697\u5668\u7684\u795e\u60c5\uff0c\u5c31\u4f3c\u662f\u4e00\u540d\u94f8\u5251\u5927\u5e08\u5728\u770b\u81ea\u5df1\u7a77\u4e00\u751f\u4e4b\u529b\u6240\u6253\u9020\u7684\u5175\u5668\u3001\u4e00\u4f4d\u75f4\u4e8e\u68cb\u827a\u7684\u5f08\u624b\u5728\u770b\u4e00\u76d8\u7a7a\u524d\u7edd\u540e\u7684\u68cb\u5c40\u4e00\u6837\u3002\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u53ef\u60dc\u751a\u4e48\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u53ef\u60dc\u7684\u662f\uff1a\u2018\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u2019\u6211\u603b\u7b97\u6709\u4e00\u5957\u5b8c\u6574\u7684\uff1b\u4e5f\u5c31\u662f\u8bf4\uff0c\u6211\u867d\u7136\u6ca1\u6709\u529e\u6cd5\u4eff\u5236\uff0c\u4f46\u81f3\u5c11\u8fd8\u6709\u53ef\u80fd\u6709\u4f7f\u7528\u5230\u5b83\u7684\u4e00\u65e5\u2026\u2026\u8fd9\u2018\u4e03\u661f\u4f34\u6708\u2019\u5374\u5df2\u4f7f\u8fc7\uff0c\u5df2\u7ecf\u662f\u4e00\u4ef6\u4f5c\u5e9f\u7684\u6697\u5668\u4e86\u3002\u201d \u3000\u3000\u201c\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u201d\u662f\u5f53\u5e74\u201c\u6743\u529b\u5e2e\u201d\u7684\u201c\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u4eba\u9b54\u201d\u6bcf\u4e00\u4eba\u5455\u5fc3\u6ca5\u8840\uff0c\u6240\u7814\u5236\u51fa\u6765\u7684\u72ec\u95e8\u6697\u5668\uff0c\u518d\u7cbe\u5236\u6210\u4e00\u6839\u9488\uff0c\u5171\u5341\u4e5d\u53e3\uff0c\u4ea4\u5e2e\u4e3b\u201c\u541b\u4e34\u5929\u4e0b\u201d\u674e\u6c89\u821f\uff0c\u6b63\u8bbe\u8ba1\u4e00\u79cd\u53d1\u653e\u7684\u673a\u7c27\uff0c\u80fd\u4e00\u53e3\u6c14\u6253\u51fa\u8fd9\u5341\u4e5d\u679a\u593a\u547d\u9488\uff0c\u53f7\u79f0\u201c\u60ca\u5929\u5730\u3001\u4f4d\u9b3c\u795e\u3001\u9b54\u9488\u51fa\u800c\u4eba\u8f9f\u6613\u201d\u3002 \u3000\u3000\u53ef\u60dc\u9488\u672a\u521b\u7814\u6210\u529f\uff0c\u8427\u79cb\u6c34\u6240\u7ec4\u5408\u7684\u201c\u795e\u5dde\u7ed3\u4e49\u201d\uff0c\u4ee5\u53ca\u6731\u5927\u5929\u738b\u7684\u52bf\u529b\uff0c\u5df2\u5f7b\u5e95\u7684\u6467\u6bc1\u4e86\u5341\u4e5d\u4eba\u9b54\uff1b\u6545\u6b64\u8fd9\u201c\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u201d\u7684\u5a01\u529b\u5982\u4f55\uff0c\u8fde\u674e\u6c89\u821f\u81ea\u5df1\u4ea6\u672a\u5f97\u77e5\u3002 \u3000\u3000\u5728\u591a\u5e74\u4e4b\u540e\uff0c\u4e00\u4e2a\u72e1\u8bc8\u8d2a\u5a6a\u3001\u6587\u6b66\u53cc\u5168\u7684\u9ad8\u5b98\u6587\u7ae0\uff0c\u66fe\u7528\u8fd9\u4e00\u84ec\u9488\uff0c\u4ee5\u56fe\u6740\u6b7b\u201c\u56db\u5927\u540d\u6355\u201d\u4e2d\u4ee5\u6697\u5668\u79f0\u8457\u7684\u65e0\u60c5\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u529f\u8d25\u5782\u6210\uff0c\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u90a3\u65f6\u5019\u4ed6\u5df2\u8eab\u53d7\u91cd\u4f24\uff0c\u53e6\u4e00\u65b9\u9762\u6c5f\u5357\u9739\u96f3\u5802\u9ad8\u624b\u96f7\u5377\u4ee5\u4ed6\u8eab\u4e0a\u957f\u5e74\u7d2f\u6708\u62ab\u7740\u7684\u6bdb\u88d8\uff0c\u5957\u4f4f\u4e86\u8fd9\u5341\u4e5d\u679a\u593a\u547d\u9488\uff0c\u6587\u7ae0\u624d\u6ca1\u6709\u5f97\u901e\u3002 \u3000\u3000\u4f46\u662f\u53c2\u52a0\u8fc7\u90a3\u4e00\u5f79\u7684\u6b63\u6d3e\u9ad8\u624b\uff0c\u5982\u65e0\u60c5\u3002\u5982\u96f7\u5377\u3001\u8fd8\u6709\u201c\u6bc1\u8bfa\u57ce\u201d\u5973\u5c06\u5510\u665a\u8bcd\uff0c\u6bcf\u60f3\u8d77\u90a3\u4e00\u84ec\u9488\uff0c\u90fd\u8c08\u201c\u9488\u201d\u8272\u53d8\u3002 \u3000\u3000\u201c\u90a3\u7b80\u76f4\u4e0d\u662f\u9488\uff0c\u201d\u5510\u665a\u8bcd\u66fe\u8fd9\u6837\u5f62\u5bb9\uff0c\u201c\u90a3\u662f\u9b54\uff0c\u5341\u4e5d\u4e2a\u7f29\u6210\u53d1\u4e1d\u4e00\u822c\u7ec6\u5c0f\u7684\u5996\u9b54\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u542c\u8bf4\u8fc7\u8fd9\u4e9b\u6c5f\u6e56\u4f20\u95fb\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e5f\u5174\u8d77\u60f3\u89c1\u8bc6\u4e00\u4e0b\u8fd9\u66fe\u540d\u52a8\u6c5f\u6e56\u7684\u6697\u5668\u4e4b\u5ff5\u5934\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4e5f\u77e5\u9053\uff0c\u50cf\u8fd9\u79cd\u5b9d\u8d35\u7684\u6697\u5668\uff0c\u53ef\u80fd\u662f\u6c60\u65e5\u66ae\u7684\u9547\u5c71\u4e4b\u5b9d\u3001\u6551\u547d\u7075\u7b26\uff0c\u81ea\u7136\u4e0d\u4f1a\u80e1\u4e71\u51fa\u793a\u4e8e\u4eba\u3002 \u3000\u3000\u201c\u4f60\u5728\u60cb\u60dc\u6211\u6740\u4e86\u98de\u661f\u5b50\uff0c\u4ee5\u81f4\u8fd9\u79cd\u6697\u5668\u5df2\u65e0\u4eba\u80fd\u4f7f\uff1f\u201d\u65b9\u90aa\u771f\u95ee\u3002 \u3000\u3000\u201c\u4e0d\uff0c\u201d\u6c60\u65e5\u66ae\u7b11\u9053\uff0c\u201c\u98de\u661f\u5b50\u7684\u547d\u8ddf\u4f60\u76f8\u6bd4\uff0c\u76f8\u5dee\u592a\u8fdc\u4e86\uff0c\u800c\u4e14\uff0c\u5982\u679c\u6628\u591c\u4f60\u6ca1\u6740\u5f97\u4e86\u4ed6\uff0c\u53ef\u80fd\u6709\u4e00\u5929\uff0c\u8fd9\u6697\u5668\u4e5f\u4f1a\u9489\u5728\u6211\u7684\u54bd\u5589\u4e0a\u3002\u4f60\u80fd\u6740\u6389\u98de\u661f\u5b50\uff0c\u90a3\u662f\u66ff\u6b66\u6797\u9664\u4e00\u5927\u5bb3\u3002\u201d \u3000\u3000\u201c\u751f\u547d\u6ca1\u6709\u4f18\u52a3\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u53ea\u6709\u5f3a\u5f31\u3002\u6211\u4fa5\u5e78\u6740\u5f97\u4e86\u4ed6\uff0c\u4e0d\u4ee3\u8868\u6211\u7684\u547d\u4fbf\u6bd4\u4ed6\u73cd\u8d35\u3002\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\u9053\uff0c\u201c\u98de\u661f\u5b50\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u8fd9\u79cd\u6697\u5668\u542c\u8bf4\u8fd8\u6709\u522b\u7684\u4eba\u4f1a\u4f7f\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5ffd\u7136\u53cd\u95ee\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u662f\u8c01\u4f1a\u4f7f\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e0d\u9632\u6709\u8fd9\u4e00\u95ee\uff0c\u72b9\u7591\u5730\u9053\uff1a\u201c\u5e94\u8be5\u662f\u2026\u2026\u2018\u6697\u5668\u738b\u2019\u79e6\u70b9\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u5bf9\uff0c\u201d\u6c60\u65e5\u66ae\u622a\u9053\uff0c\u201c\u2018\u6697\u5668\u738b\u2019\u79e6\u70b9\u7684\u6697\u5668\uff0c\u6bcf\u4e00\u4ef6\u90fd\u662f\u4ed6\u81ea\u884c\u521b\u5236\u7684\uff0c\u51b3\u4e0d\u6284\u88ad\u6a21\u4eff\u522b\u4eba\uff0c\u6240\u4ee5\u624d\u80fd\u53d6\u4ee3\u8700\u4e2d\u5510\u95e8\uff0c\u4e00\u5ea6\u4e0e\u65e0\u60c5\u7684\u2018\u660e\u5668\u2019\u5e76\u5217\u53cc\u7edd\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u644a\u4e86\u644a\u624b\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u6709\u522b\u70b9\u751a\u4e48\u7684\u957f\u5904\uff0c\u53ea\u6709\u4e24\u9879\u8fd8\u53ef\u4ee5\u81ea\u8c6a\u7684\u4e8b\u2026\u2026\u201d\u4ed6\u7b11\u7740\u8bf4\u4e0b\u53bb\uff0c\u201c\u5176\u4e2d\u4e4b\u4e00\u4fbf\u662f\u6211\u5bf9\u5175\u5668\u7684\u7814\u7a76\uff0c\u4e0d\u7ba1\u662f\u6b66\u5668\u7684\u6e90\u6d41\u6216\u5236\u6210\u8fd8\u662f\u4f55\u4eba\u4f7f\u7528\u548c\u4f7f\u7528\u7684\u65b9\u5f0f\uff0c\u6211\u90fd\u9887\u611f\u5174\u8da3\uff0c\u4e5f\u7b97\u6709\u70b9\u5fc3\u5f97\uff0c\u4f46\u53eb\u6211\u7528\uff0c\u6211\u53ef\u4e0d\u884c\u3002\u201d \u3000\u3000\u4ed6\u62cd\u4e86\u62cd\u8170\u7554\u90a3\u67c4\u9576\u7740\u540d\u8d35\u5b9d\u77f3\u7684\u5251\uff0c\u81ea\u5632\u7684\u9053\uff1a\u201c\u6211\u53ea\u914d\u7528\u8fd9\u79cd\u4ec5\u4f5c\u6446\u8bbe\u88c5\u9970\u7684\u4e1c\u897f\uff0c\u4f60\u4e5f\u89c1\u8fc7\u5b83\u7684\u7528\u9014\u3002\u201d\u5f53\u65e5\u5728\u6d1b\u9633\u57ce\u90ca\u8336\u5e97\u91cc\uff0c\u65b9\u90aa\u771f\u9996\u6b21\u9047\u89c1\u6c60\u65e5\u66ae\uff0c\u4fbf\u662f\u9047\u88ad\u7684\u65f6\u5019\uff0c\u8fd9\u628a\u5251\u771f\u7684\u6d3e\u4e0d\u4e0a\u751a\u4e48\u7528\u573a\u3002 \u3000\u3000\u4f46\u65b9\u90aa\u771f\u7684\u6001\u5ea6\u5374\u663e\u5f97\u5f88\u5c0a\u91cd\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u672a\u5bf9\u6c60\u65e5\u66ae\u663e\u51fa\u5982\u6b64\u5c0a\u656c\u7684\u795e\u6001\u3002 \u3000\u3000\u4e00\u2014\u4e00\u4e2a\u4eba\u80fd\u591f\u660e\u767d\u81ea\u5df1\u7684\u957f\u5904\uff0c\u5df2\u662f\u4e0d\u5bb9\u6613\u7684\u4e8b\uff0c\u8fd8\u80fd\u77e5\u9053\u81ea\u5df1\u7684\u5f31\u70b9\uff0c\u66f4\u662f\u4e0d\u7b80\u5355\uff0c\u4e00\u4e2a\u8eab\u5728\u9ad8\u4f4d\u3001\u88ab\u90e8\u5c5e\u5305\u56f4\u7684\u4eba\uff0c\u8fd8\u80fd\u5ba2\u89c2\u5224\u65ad\u81ea\u5df1\u7684\u4f18\u52a3\uff0c\u90a3\u662f\u6559\u4eba\u8083\u7136\u8d77\u656c\u7684\u4e8b\u3002 \u3000\u3000\u201c\u5f53\u4eca\u5929\u4e0b\uff0c\u8fd8\u80fd\u4f7f\u8fd9\u79cd\u6697\u5668\u7684\u662f\u2018\u795e\u4e0d\u77e5\u2019\u548c\u2018\u9b3c\u4e0d\u89c9\u2019\u4e24\u5144\u5f1f\uff0c\u542c\u8bf4\u98de\u661f\u5b50\u6539\u826f\u5510\u6708\u4eae\u7684\u2018\u68a6\u88f3\uff0c\u529b\u6709\u672a\u902e\uff0c\u66fe\u8bf7\u6559\u8fc7\u53e6\u4e24\u4f4d\u6697\u5668\u540d\u624b\u2018\u795e\u4e0d\u77e5\u2019\u4e0e\u2018\u9b3c\u4e0d\u89c9\u2019\uff0c\u7ed3\u679c\u2026\u2026\u201d \u3000\u3000\u6c60\u65e5\u66ae\u8138\u4e0a\u6d6e\u8d77\u4e00\u62b9\u7b11\u610f\uff0c\u4e5f\u4e0d\u77e5\u662f\u8ba5\u8bee\u8fd8\u662f\u6168\u53f9\uff0c\u201c\u7ed3\u679c\u8fd9\u5bf9\u5144\u5f1f\u4e5f\u5c31\u4f1a\u4f7f\u2018\u4e03\u661f\u4f34\u6708\u2019\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u6545\u4e8b\u662f\u544a\u8bc9\u6211\u4eec\u2026\u2026\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u628a\u91cd\u5927\u7684\u79d8\u5bc6\u544a\u8bc9\u4eba\uff0c\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7b11\u4e86\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u7b11\u91ca\u7136\u3002 \u3000\u3000\u201c\u6211\u529e\u4e0d\u5230\u3002\u201d\u6c60\u65e5\u66ae\u7b11\u9053\uff0c\u201c\u5982\u679c\u8fd9\u6837\u8bf4\uff0c\u9996\u5148\u5c31\u4e0d\u8be5\u8ba9\u4f60\u8fdb\u6765\u2018\u5175\u5668\u623f\u2019\u4e86\u3002\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e3a\u751a\u4e48\u8ba9\u6211\u8fdb\u6765\u8fd9\u91cc\uff1f\u201d\u65b9\u90aa\u771f\u6ca1\u6709\u56de\u7b54\u4ed6\u7684\u8bdd\u7684\uff0c\u5374\u53cd\u95ee\u9053\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u60f3\u6566\u8bf7\u4f60\u4e3b\u6301\u5170\u4ead\u6c60\u5bb6\uff0c\u5c45\u7136\u8fd9\u91cc\u90a3\u513f\u90fd\u6210\u4e3a\u7981\u5730\uff0c\u8bd5\u95ee\u8fd9\u6837\u7684\u5c0f\u5668\u80fd\u5bb9\u5f97\u4e0b\u4f60\u7684\u5927\u624d\u5417\uff1f\u201d\u6c60\u65e5\u66ae\u5373\u7b54\uff0c\u773c\u91cc\u95ea\u52a8\u7740\u8bda\u631a\uff0c\u8c01\u90fd\u80fd\u770b\u5f97\u51fa\u6765\u4ed6\u8bf4\u7684\u662f\u5fc3\u91cc\u5934\u7684\u8bdd\uff0c\u201c\u6240\u4ee5\u6211\u5df2\u7ecf\u5429\u5490\u4e0b\u53bb\uff0c\u51e1\u662f\u6211\u80fd\u5230\u4e4b\u5904\uff0c\u65b9\u5c11\u4fa0\u4e5f\u80fd\u5230\uff0c\u8c01\u6562\u76f8\u963b\u4fbf\u6740\u8c01\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u5782\u4e0b\u4e86\u773c\u5e18\uff0c\u9053\uff1a\u201c\u96be\u602a\u6cbf\u9014\u4e0a\u65e0\u4e00\u4eba\u963b\u62e6\uff0c\u6211\u770b\u89c1\u5c0f\u767d\uff0c\u95ee\u4f60\u5728\u90a3\u91cc\uff0c\u4ed6\u4e5f\u76f4\u8a00\u65e0\u8bb3\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u5927\u6982\u4f1a\u5728\u8fd9\u4e2a\u65f6\u5019\u8f6c\u9192\u8fc7\u6765\uff0c\u6240\u4ee5\u7279\u522b\u5728\u8fd9\u91cc\u7b49\u4f60\u3002 \u3000\u3000\u201c\u7b49\u6211\uff1f\u201d \u3000\u3000\u201c\u5bf9\u3002\u201d \u3000\u3000\u201c\u7b49\u6211\u505a\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\u6697\u5668\u3002\u201d\u6c60\u65e5\u66ae\u7684\u773c\u775b\u95ea\u8000\u7740\u661f\u661f\u822c\u7684\u594b\u60a6\uff0c\u201c\u770b\u8fd9\u679a\u661f\u661f\u4e00\u822c\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u6211\u5bf9\u6697\u5668\u7684\u5174\u8da3\u4e0d\u5982\u4f60\u3002\u201d \u3000\u3000\u201c\u4f46\u4f60\u5bf9\u6740\u4f60\u7684\u4eba\u4e00\u5b9a\u5f88\u6709\u5174\u8da3\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6740\u6211\u7684\u4eba\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u98de\u661f\u5b50\u4e0d\u9519\u5df2\u7ecf\u6b7b\u4e86\uff0c\u201d\u6c60\u65e5\u66ae\u7b11\u5f97\u5f88\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u4f46\u6697\u5668\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u89c9\u5f97\u4ed6\u5e94\u8be5\u5f00\u59cb\u8981\u91cd\u4f30\u6c60\u65e5\u66ae\u4e86\uff1a\u201c\u4ed6\u7684\u6697\u5668\uff1f\u201d \u3000\u3000\u201c\u8fd9\u79cd\u6697\u5668\u662f\u7528\u4e00\u79cd\u7edd\u4e16\u7684\u77ff\u77f3\u7814\u5236\u7684\uff0c\u5f53\u7136\u8fd8\u8981\u52a0\u5de5\u3001\u5582\u6bd2\u3001\u88c5\u673a\u62ec\uff0c\u4f46\u6700\u91cd\u8981\u662f\u8fd9\u79cd\u77ff\u77f3\uff0c\u975e\u94bb\u4f3c\u94bb\uff0c\u65e2\u4e0d\u662f\u732b\u773c\u78a7\uff0c\u4e5f\u4e0d\u662f\u95ea\u5c71\u4e91\uff0c\u8fd9\u79cd\u77ff\u77f3\u5df2\u6210\u4e86\u7edd\u4e16\u5947\u73cd\uff0c\u542c\u8bf4\u9664\u4e86\u2018\u795e\u4e0d\u77e5\u3001\u9b3c\u4e0d\u89c9\u2019\u4e24\u5144\u5f1f\u51fa\u751f\u7684\u71d5\u4e91\u4e4b\u5916\uff0c\u5c31\u53ea\u6709\u7687\u5bab\u91cc\u6709\u4e00\u5757\uff0c\u201d\u6c60\u65e5\u66ae\u9053\uff0c\u201c\u4e00\u5927\u5757\u3002\u201d \u3000\u3000\u201c\u7687\u5bab\uff1f\u201d \u3000\u3000\u201c\u8fd9\u79cd\u6697\u5668\u4e4b\u6240\u4ee5\u65e0\u6cd5\u63a8\u5e7f\u6d41\u4f20\uff0c\u53ef\u80fd\u4fbf\u662f\u56e0\u4e3a\u6750\u6599\u5927\u4e0d\u6613\u6289\u5f97\u4e4b\u6545\u3002\u201d\u6c60\u65e5\u66ae\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u4f1a\u95ee\uff1a\u98de\u661f\u5b50\u53c8\u662f\u600e\u4f1a\u83b7\u5f97\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u70b9\u5934\u3002 \u3000\u3000\u201c\u7b54\u6848\u5f88\u7b80\u5355\uff0c\u201d\u6c60\u65e5\u66ae\u9053\uff0c\u201c\u98de\u661f\u5b50\u662f\u7687\u57ce\u91cc\u7684\u6bbf\u524d\u53f8\uff0c\u66fe\u4efb\u526f\u90e8\u6307\u6325\u4f7f\u4e00\u804c\u3002\u4ed6\u7684\u804c\u4f4d\u662f\u638c\u6bbf\u524d\u8bf8\u73ed\u503c\uff0c\u53ca\u6b65\u9a91\u8bf8\u6307\u6325\uff0c\u51e1\u7edf\u5236\u3002\u8bad\u7ec3\uff0c\u5ba1\u536b\u3001\u620d\u5b88\u3001\u8fc1\u6355\u3001\u8d4f\u7f5a\uff0c\u7686\u662f\u4ed6\u7684\u53f8\u804c\u3002\u201d \u3000\u3000\u4ed6\u671b\u5b9a\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4e00\u4e2a\u7687\u5e1d\u6bbf\u4e0a\u7684\u526f\u6307\u6325\uff0c\u5343\u91cc\u8fe2\u8fe2\u7684\u8fc7\u6765\u6740\u4f60\uff0c\u4f60\u4e0d\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u5417\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6014\u6014\u5fe1\u5fe1\u7684\u5446\u4e86\u4e00\u9635\uff0c\u624d\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u6211\u7684\u9ebb\u70e6\u8fd8\u4e0d\u6b62\u5728\u6d1b\u9633\u57ce\u3002\u201d \u3000\u3000\u201c\u6709\u4e9b\u4eba\u53bb\u5230\u90a3\u91cc\uff0c\u90a3\u91cc\u5c31\u6709\u9ebb\u70e6\uff1b\u53bb\u5230\u751a\u4e48\u5730\u65b9\uff0c\u90a3\u5730\u65b9\u5c31\u4f1a\u53d1\u751f\u5927\u4e8b\u3002\u201d\u6c60\u65e5\u66ae\u9053\uff0c\u201c\u66f4\u5947\u7279\u7684\u662f\u90a3\u4e24\u4e2a\u4f7f\u4e5d\u8033\u516b\u73af\u952f\u9f7f\u5200\u548c\u4f7f\u9547\u94c1\u63b8\u6756\u7684\u3002\u4ee5\u53ca\u90a3\u4e2a\u88f9\u817f\u6d12\u978b\u7a7f\u6cb9\u7ef8\u5b50\u5e03\u8863\u7684\u6740\u624b \u3000\u3000\u65b9\u90aa\u771f\u65e0\u5948\u5730\u7b11\u9053\uff1a\u201d\u4ed6\u4eec\u603b\u4e0d\u4f1a\u662f\u7687\u4eb2\u56fd\u621a\u7f62\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u4ed6\u4eec\u53ea\u662f\u2018\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u2019\u7684\u6210\u5458\uff0c\u53ef\u80fd\u8ddf\u98de\u661f\u5b50\u662f\u540c\u95e8\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8fd9\u70b9\u5012\u5e76\u4e0d\u610f\u5916\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u5218\u519b\u5e08\u63a8\u6d4b\u5728\u6d1b\u9633\u57ce\u90ca\u72d9\u88ad\u6211\u4eec\u7684\uff0c\u4e5f\u662f\u2018\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\u2019\u7684\u4eba\uff0c\u4f60\u77e5\u9053\u2018\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173'\u7f62\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u5f00\u59cb\u611f\u89c9\u5230\u5934\u75db\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u90a3\u4e2a\u6740\u624b\u96c6\u56e2\uff1f\u201d\u4ed6\u5f00\u59cb\u5728\u8111\u91cc\u6574\u7406\u4e86\u4e00\u4e0b\uff0c\u201c\u7b49\u4e00\u7b49\uff0c\u73b0\u5728\u603b\u5171\u6709\uff1a\u5170\u4ead\u548c\u5c0f\u78a7\u6e56\u7684\u4eba\u8981\u627e\u6211\uff0c\u5999\u624b\u5802\u5219\u60f3\u8981\u6211\u7684\u547d\uff1b\u98de\u661f\u5b50\u662f\u2018\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676'\u7684\u4e00\u5458\uff0c\u4f46\u53c8\u66fe\u4efb\u804c\u7687\u57ce\u620d\u5b88\u53f8\uff0c\u2018\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676\u2019\u4f3c\u4e5f\u975e\u8981\u628a\u6211\u6740\u6b7b\u4e0d\u7518\u4f11\uff1b\u2018\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\u2019\u4ea6\u66fe\u72d9\u51fb\u8fc7\u4f60\uff0c\u88ab\u6211\u6740\u4e86\u51e0\u4eba\uff0c\u65ad\u7709\u77f3\u5f88\u53ef\u80fd\u662f\u8fd9\u7ec4\u7ec7\u4e2d\u7684\u5934\u9886\u4e4b\u4e00\uff0c\u4ed6\u4e5f\u8bbe\u8ba1\u6740\u6211\uff0c\u800c\u4ed6\u5df2\u52a0\u5165\u4e86\u5999\u624b\u5802\u2026\u2026\u201d \u3000\u3000\u4ed6\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u8fd9\u4e9b\u8981\u7f6e\u6211\u4e8e\u6b7b\u547d\u7684\u4eba\uff0c\u7275\u8fde\u53ef\u771f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u5c82\u6b62\u4e8e\u4f60\uff0c\u5c31\u7b97\u6d1b\u9633\u56db\u516c\u5b50\u4e4b\u4e89\uff0c\u53ea\u6015\u4e5f\u6709\u4e0d\u5c11\u7275\u8fde\uff0c\u5e55\u540e\u4e5f\u6709\u4e0d\u5c11\u4eba\u64cd\u7eb5\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7709\u6bdb\u4e00\u6311\u9053\uff1a\u201c\u8fd8\u6d89\u53ca\u671d\u5ef7\u6743\u515a\u3001\u5bab\u5ef7\u5185\u4e89\uff1f\u201d \u3000\u3000\u201c\u5bb6\u5144\u867d\u7136\u662f\u4e16\u88ad\u738b\u4faf\uff0c\u4f46\u82e5\u8bba\u7ed3\u4ea4\u6743\u5bab\uff0c\u5170\u4ead\u4e0d\u5982\u5c0f\u78a7\u6e56\u7684\u6e38\u516c\u5b50\uff0c\u82e5\u8bba\u79c1\u4e88\u671d\u81e3\u539a\u5229\uff0c\u4ed6\u5bb6\u4ea6\u4e0d\u53ca\u5999\u624b\u5802\u56de\u767e\u5e94\u3002\u201d\u4ed6\u65e5\u66ae\u8bed\u97f3\u975e\u5e38\u5e73\u9759\uff0c\u201c\u6211\u4eec\u8981\u7ef4\u6301\u8fd9\u4e2a\u5c40\u9762\uff0c\u81f3\u5c11\u8981\u6bd4\u4eba\u66f4\u8270\u96be\u4e0a\u4e09\u56db\u500d\u4ee5\u4e0a\uff0c\u5c31\u7b97\u6bd4\u8bf8\u4e8e\u5343\u53f6\u5c71\u5e84\uff0c\u4e5f\u4e0d\u5982\u4eba\uff0c\u56e0\u4e3a\u845b\u94c3\u94c3\u6bd5\u7adf\u662f\u5f53\u4eca\u5fa1\u53f2\u7684\u672a\u6765\u5ab3\u5987\uff0c\u6211\u4eec\u53ef\u751a\u4e48\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6253\u65ad\u6c60\u65e5\u66ae\u7684\u8bdd\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u8981\u544a\u8bc9\u6211\u8fd9\u4e9b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u6211\u7559\u4e0d\u4f4f\u4f60\u3002\u4f60\u4e00\u5b9a\u89c9\u662f\u5947\u602a\uff0c\u6211\u4eec\u4e3a\u4f3a\u975e\u8981\u7528\u4f60\u4e0d\u53ef\uff1f\u8fd9\u7b54\u6848\u662f\uff1a\u6211\u89c9\u5f97\u4f60\u662f\u6211\u4eec\u552f\u4e00\u7684\u5bc4\u671b\uff1a\u5218\u519b\u5e08\u667a\u6167\u8fc7\u4eba\uff0c\u4f46\u6b66\u529f\u5e73\u5e73\uff1b\u5c0f\u767d\u5fe0\u5fc3\u803f\u803f\uff0c\u4eba\u751f\u9605\u5386\u5374\u662f\u4e0d\u8db3\uff1b\u6d2a\u4e09\u70ed\u82f1\u52c7\u5fc3\u70ed\uff0c\u4f46\u884c\u4e8b\u8fc7\u4e8e\u9c81\u83bd\uff1b\u52a0\u4e0a\u5bb6\u5144\u6b8b\u5e9f\uff0c\u5927\u5ac2\u662f\u5f31\u8d28\u5973\u6d41\uff0c\u6211\u53c8\u2026\u2026\u5509\u4e0d\u6210\u6750\u2026\u2026\u53ea\u6709\u5c11\u4fa0\u80fd\u5149\u5927\u5170\u4ead\uff0c\u58ee\u5927\u6c60\u5bb6\u2026\u2026\u201d\u6c60\u65e5\u66ae\u5766\u8bda\u5730\u9053\uff1a\u201c\u6211\u5c31\u662f\u77e5\u9053\u5170\u4ead\u7684\u5c0f\u5c0f\u683c\u5c40\uff0c\u5bb9\u4e0d\u4e0b\u65b9\u5c11\u4fa0\u7684\u6c14\u5b57\u6c14\u6982\uff0c\u6211\u53ea\u60f3\u8ba9\u65b9\u5c11\u4fa0\u4e34\u8d70\u4e4b\u524d\uff0c\u6bcb\u8981\u8bef\u4f1a\u4e86\u6211\u7684\u5fc3\u610f\u5c31\u597d\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5ffd\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u6211\u8981\u8d70\uff0c\u4f60\u8fd8\u544a\u8bc9\u6211\u8fd9\u4e9b\uff1f\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u65b9\u8001\u4f2f\u548c\u5c0f\u5f1f\u7684\u6b7b\uff0c\u6211\u603b\u89c9\u5f97\u6709\u6127\u4e8e\u5fc3\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4eec\u7684\u672c\u610f\u539f\u975e\u5982\u6b64\uff0c\u53d1\u751f\u8fd9\u79cd\u4e8b\u66f4\u59cb\u6599\u975e\u53ca\uff0c\u66f4\u89c9\u5f97\u6709\u5fc5\u8981\u5bf9\u4f60\u4f46\u8a00\u3002\u65e2\u53d1\u751f\u8fd9\u6837\u7684\u60e8\u7978\uff0c\u6211\u5df2\u5631\u5c0f\u767d\u4eb2\u53bb\u628a\u5c11\u4fa0\u7684\u7ea2\u7c89\u77e5\u97f3\u60dc\u60dc\u4e5f\u4fdd\u62a4\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u671b\u5b9a\u6c60\u65e5\u66ae\uff0c\u4e00\u5b57\u4e00\u53e5\u7684\u9053\uff1a\u201c\u6211\u867d\u4e0d\u63a5\u53d7\u4f60\u7684\u8058\u7528\uff0c\u4f46\u8bf4\u4e0d\u51c6\u6211\u4f1a\u8fc7\u53bb\u5c0f\u78a7\u6e56\u3001\u5999\u624b\u5802\u3001\u5343\u53f6\u5c71\u5e84\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u8fd8\u5bf9\u6211\u5410\u9732\u6c60\u5bb6\u7684\u5371\u673a\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5c11\u4fa0\uff0c\u65e0\u4e8b\u4e0d\u53ef\u76f4\u8a00\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u5170\u4ead\u7684\u5b88\u536b\uff0c\u4efb\u7531\u6211\u6765\u53bb\uff0c\u4e0d\u6015\u6211\u6740\u4e86\u4f60\uff0c\u5411\u4f60\u7684\u654c\u4eba\u9080\u529f\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u201c\u5047\u5982\u6211\u662f\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u81ea\u627e\u7684\uff0c\u6211\u8ba4\u547d\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u6210\u529f\u4e86\u3002\u201d \u3000\u3000\u201c\u751a\u4e48\uff1f\u201d\u6c60\u65e5\u66ae\u4e0d\u89e3\u3002 \u3000\u3000\u201c\u6211\u4f1a\u7559\u5728\u5170\u4ead\uff0c\u4e3a\u4f60\u6548\u547d\u3002\u201d\u65b9\u90aa\u771f\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u9053\uff1a\u201c\u6211\u4f1a\u52a0\u5165\u5170\u4ead\uff0c\u66ff\u4f60\u626b\u9664\u969c\u788d\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9ad8\u5174\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u559c\u6781\u5fd8\u5f62\uff0c\u7adf\u4e00\u5c48\u819d\u3001\u4e00\u5934\u5c31\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8fde\u5fd9\u622a\u4f4f\u3002\u201c\u53ef\u662f\u6211\u6709\u51e0\u4e2a\u6761\u4ef6\uff0c\u4f60\u5fc5\u987b\u8981\u7b54\u5e94\u6211\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u6b22\u559c\u5f97\u53e3\u9f7f\u4e0d\u6e05\uff0c\u53ea\u8bf4\uff1a\u201c\u522b\u8bf4\u51e0\u4e2a\u6761\u4ef6\uff0c\u7eb5\u662f\u5343\u4e2a\u767e\u4e2a\uff0c\u6211\u4e5f\u7b54\u5e94\u4f60\u3002\u201d\u8bf4\u5230\u8fd9\u513f\uff0c\u731b\u7701\u4e86\u7701\uff0c\u624d\u8865\u5145\u9053\uff1a\u201c\u9664\u4e86\u5bb6\u5144\u548c\u5927\u5ac2\u4e4b\u5916\uff0c\u5c31\u7b97\u4f60\u8981\u5728\u4e8b\u6210\u540e\u53d6\u6211\u6027\u547d\uff0c\u6211\u4e5f\u7edd\u65e0\u5c24\u6028\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff1f \u3000\u3000\u201c\u771f\u7684\u3002\u201d\u6c60\u65e5\u66ae\u8bf4\u5f97\u7edd\u65e0\u8f6c\u5708\u4f59\u5730\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5fc3\u91cc\u5f88\u6709\u4e9b\u611f\u52a8\uff0c\u5ffd\u4e5f\u4e00\u62dc\u5012\u5730\uff0c\u6c60\u65e5\u66ae\u614c\u5fd9\u6276\u4f4f\uff0c\u6025\u5f97\u5192\u6c57\u7684\u8bf4\uff1a\u201c\u5c11\u4fa0\u5728\u5170\u4ead\uff0c\u597d\u6bd4\u6211\u6069\u516c\uff0c\u597d\u6bd4\u6211\u5e08\u7236\uff0c\u600e\u80fd\u5982\u6b64\uff0c\u600e\u80fd\u5982\u6b64\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u6b63\u8272\u9053\uff1a\u201c\u6211\u52a0\u5165\u5170\u4ead\u7684\u7b2c\u4e00\u4e2a\u6761\u4ef6\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8bf4\uff0c\u201d\u6c60\u65e5\u66ae\u6025\u5207\u7684\u8bf4\uff1a\u201c\u6211\u90fd\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u201c\u4fbf\u662f\u8981\u4f60\u6210\u4e3a\u6211\u4eec\u7684\u4e3b\u5b50\uff0c\u51b3\u4e0d\u53ef\u592a\u793c\u5f85\u6211\u3002\u201d\u65b9\u90aa\u771f\u8083\u7136\u9053\uff0c\u201c\u519b\u4ee4\u65e0\u5a01\u4e0d\u884c\uff0c\u81e3\u547d\u65e0\u541b\u4e0d\u4ece\u3002\u4f60\u8981\u5149\u5927\u5170\u4ead\uff0c\u91cd\u632f\u58f0\u5a01\uff0c\u5c31\u5fc5\u987b\u8981\u50cf\u4e2a\u5a01\u4e25\u7684\u660e\u4e3b\uff0c\u624d\u80fd\u773c\u4f17\u3002\u518d\u8bf4\uff0c\u53ea\u8981\u6211\u7b54\u5e94\u52a0\u5165\u5170\u4ead\uff0c\u6211\u5c31\u662f\u5170\u4ead\u4e00\u5458\uff0c\u65e0\u8bba\u8d4f\u7f5a\uff0c\u4e0e\u4eba\u65e0\u5f02\uff0c\u516c\u5b50\u82e5\u7834\u683c\u65bd\u6069\uff0c\u53cd\u4ee4\u516c\u5b50\u5931\u5374\u5a01\u4fe1\uff0c\u4ea6\u4f7f\u6211\u62db\u5992\u81f4\u5371\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u88ab\u8fd9\u756a\u8bdd\u8bf4\u5f97\u6c57\u6d94\u6d94\u4e0b\uff1a\u201c\u662f\uff0c\u662f\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u8bda\u631a\u5730\u9053\uff1a\u201c\u65e5\u540e\u516c\u5b50\u5c31\u5f85\u6211\u4e3a\u4e00\u540d\u90e8\u5c5e\u5c31\u597d\uff0c\u5e76\u8bf7\u52ff\u4ee5\u5c11\u4fa0\u76f8\u79f0\uff0c\u76f4\u547c\u6211\u540d\u5b57\u4fbf\u662f\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u60f3\u4e86\u60f3\uff0c\u8fd8\u662f\u6301\u610f\u7684\u9053\uff1a\u201c\u5c11\u2026\u2026\u4f60\u7684\u5e74\u7eaa\u53ef\u80fd\u6bd4\u6211\u7a0d\u957f\uff0c\u4e0d\u5982\u2026\u2026\u4e0d\u5982\u6211\u5c31\u79f0\u5c11\u4fa0\u4e3a\u5144\u2026\u2026\u6211\u5bf9\u65b9\u5144\u63a8\u5fc3\u7f6e\u8179\uff0c\u809d\u80c6\u76f8\u7167\uff0c\u5b9e\u65e0\u5f02\u5144\u5f1f\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u89c1\u4ed6\u8bf4\u5f97\u771f\u8bda\uff0c\u8fde\u773c\u7736\u90fd\u6e7f\u4e86\uff0c\u5fc3\u4e2d\u7535\u5fcd\u4e0d\u4f4f\u6fc0\u8d77\u4e00\u4efd\u60c5\u4e49\u7684\u60ca\u6d9b\uff0c\u70b9\u5934\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u54b1\u4eec\u5fc3\u91cc\u662f\u7978\u798f\u76f8\u5171\u7684\u5144\u5f1f\u3002\u5bf9\u5916\uff0c\u4ecd\u6267\u4e3b\u4ec6\u4e4b\u793c\uff0c\u5982\u8499\u4fe1\u4efb\uff0c\u53ea\u5f53\u6211\u662f\u516c\u5b50\u8eab\u8fb9\u7231\u5c06\u4fbf\u662f\u4e86\u3002\u7ed3\u4e49\u4e00\u4e8b\uff0c\u83ab\u8ba9\u5916\u95f4\u5f97\u6089\u4fbf\u6210\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5927\u559c\u4f0f\u62dc\u9053\uff1a\u201c\u65b9\u5927\u54e5\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5fcd\u4e0d\u4f4f\u5fc3\u6000\u6fc0\u52a8\uff0c\u4e5f\u8dea\u5730\u76f8\u5524\uff1a\u201c\u6c60\u5f1f\u3002\u201d \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u800c\u7b11\uff0c\u4e0d\u7981\u51fb\u638c\u4e3a\u7ea6\u3002 \u3000 \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173 \u3000 \u3000\u3000\u65b9\u90aa\u771f\u957f\u53f9\u9053\uff0c\u201c\u5176\u5b9e\uff0c\u6211\u8fd8\u6b20\u516c\u5b50\u4e00\u6b21\u6551\u547d\u4e4b\u6069\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5374\u8d67\u7136\u9053\uff0c\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5feb\u83ab\u8981\u518d\u63d0\u4e86\uff0c\u4f60\u4e5f\u5728\u6d1b\u9633\u9053\u4e0a\uff0c\u6551\u4e86\u6211\u4e00\u547d\u2026\u2026\u6211\u603b\u89c9\u5f97\u81ea\u5df1\u505a\u9519\u4e86\u4e00\u4e9b\u4e8b\uff0c\u5f88\u5bf9\u4e0d\u8d77\u65b9\u5144\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5de6\u7709\u4e00\u6311\u9053\uff1a\u201c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u8b6c\u5982\u2026\u2026\u201d\u6c60\u65e5\u66ae\u8fdf\u7591\u7684\u9053\uff0c\u201c\u6ca1\u6709\u6211\u5bf9\u65b9\u5144\u529b\u9080\uff0c\u4e5f\u8bb8\uff0c\u65b9\u5144\u5bb6\u4eba\u5c31\u4e0d\u81f4\u906d\u5384\u8fd0\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6740\u6211\u7236\u548c\u5c0f\u5f1f\u7684\u51f6\u624b\uff0c\u8ba9\u6211\u627e\u51fa\u6765\uff0c\u6211\u5fc5\u4e0d\u5bb9\u4ed6\uff01\u201d\u65b9\u90aa\u771f\u76ee\u4e2d\u6740\u673a\u5927\u73b0\uff0c\u201c\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u4e5f\u8bb8\u90fd\u662f\u5728\u52a8\u96be\u9003\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u4e0d\u5982\u6211\u65e9\u4e9b\u52a0\u5165\u5170\u4ead\u2026\u2026\u73b0\u5728\uff0c\u8bf4\u53e5\u5766\u8bda\u7684\u8bdd\uff0c\u6211\u4e5f\u8981\u51ed\u85c9\u6c60\u5bb6\u7684\u5b9e\u529b\uff0c\u6765\u62a5\u6211\u8fd9\u4e2a\u8840\u6d77\u6df1\u4ec7\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u8138\u8272\u5f88\u6709\u4e9b\u60f6\u6127\uff0c\u534a\u664c\u624d\u95ee\uff1a\u201c\u2026\u2026\u4f60\u8bf4\u7684\uff0c\u8fd8\u6709\u90a3\u4e9b\u6761\u4ef6\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u8138\u4e0a\u51fa\u73b0\u6bc5\u7136\u4e4b\u8272\uff1a\u201c\u6211\u884c\u4e8b\uff0c\u5fc5\u5148\u8bf7\u793a\u4e88\u4f60\uff0c\u4f46\u6211\u7948\u6c42\u516c\u5b50\u8ba9\u6211\u638c\u6709\u5b9e\u6743\uff0c\u5fc5\u8981\u65f6\u53ef\u5148\u65a9\u540e\u594f\uff0c\u5bf9\u5170\u4ead\u6c60\u5e9c\u7ec4\u7ec7\u7684\u4eba\u4e8b\uff0c\u80fd\u5168\u9762\u9769\u65b0\u3001\u9664\u65e7\u5e03\u65b0\uff01\u201d \u3000\u3000\u4ed6\u51b3\u7136\u5730\u9053\uff1a\u201c\u5170\u4ead\u4e4b\u6240\u4ee5\u4e0d\u5982\u4eba\uff0c\u4e3a\u5c0f\u78a7\u6e56\u7b49\u4e4b\u52bf\u529b\u4fb5\u6270\uff0c\u4e3b\u8981\u662f\u56e0\u4e3a\u672a\u80fd\u81ea\u5f3a\uff0c\u5185\u60a3\u767e\u75c5\uff01\u8981\u6210\u5927\u4e1a\uff0c\u592b\u6b32\u5584\u5176\u5de5\uff0c\u5fc5\u5148\u5229\u5176\u5668\uff0c\u6c60\u5e9c\u7ed3\u6784\u5df2\u75c5\u4eba\u818f\u76f2\uff0c\u5fc5\u987b\u8981\u5f7b\u5e95\u533b\u6cbb\u5f88\u9664\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6c60\u65e5\u66ae\u8138\u4e0a\u9732\u51fa\u91cd\u6258\u7684\u795e\u8272\uff0c\u575a\u51b3\u5730\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6837\u4f7f\u6c60\u5bb6\u8d77\u6b7b\u56de\u751f\u7684\u4e8b\uff0c\u5c31\u4ea4\u7ed9\u65b9\u5144\u4e86\uff0c\u6211\u6c60\u65e5\u66ae\u6709\u4e00\u65e5\u547d\uff0c\u5c31\u820d\u547d\u652f\u6301\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u773c\u775b\u53d1\u4e86\u4eae\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u88ab\u4fe1\u4efb\uff0c\u662f\u4ef6\u79f0\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u80fd\u88ab\u91cd\u7528\uff0c\u66f4\u662f\u8d4f\u5fc3\u4e50\u4e8b\u3002 \u3000\u3000\u8fde\u65b9\u90aa\u771f\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u2014\u2014\u4ed6\u7684\u907f\u4e16\uff0c\u4e5f\u8bb8\u53ea\u662f\u4e3a\u4e86\u4e0d\u60f3\u5377\u5165\u65e0\u8c13\u7684\u662f\u975e\u548c\u6597\u4e89\u91cc\uff0c\u8981\u662f\u80fd\u88ab\u4eba\u4fe1\u91cd\uff0c\u63e1\u6709\u547c\u98ce\u5524\u96e8\u7684\u6743\u529b\uff0c\u8c01\u4e0d\u613f\u628a\u70ed\u8840\u4e4b\u5fc3\u3001\u4e00\u8eab\u672c\u9886\uff0c\u4f5c\u5168\u529b\u4ee5\u8d74\u7684\u6295\u6ce8\u3001\u201c\u6211\u8fd8\u6709\u4e00\u4e2a\u6761\u4ef6\u3002\u201d\u65b9\u90aa\u771f\u8bf4\u8fd9\u4ef6\u4e8b\u65f6\u7279\u522b\u51dd\u91cd\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d\u6c60\u65e5\u66ae\u77e5\u9053\u662f\u5927\u4e8b\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u4eec\u624b\u8155\u88ab\u6bd2\u86c7\u54ac\u4e86\uff0c\u4e3a\u4e86\u8981\u9a71\u6bd2\uff0c\u5fc5\u987b\u8981\u653e\u8840\u53bb\u6bd2\uff1b\u5047\u82e5\u6211\u4eec\u88ab\u6bd2\u7bad\u5c04\u4e2d\uff0c\u4e3a\u4e86\u8981\u795b\u52a8\uff0c\u4e5f\u5b9a\u8981\u525c\u53bb\u8150\u8089\uff0c\u624d\u80fd\u7597\u6bd2\u3002\u201d\u65b9\u90aa\u771f\u5148\u4f5c\u8b6c\u55bb\uff0c\u7136\u540e\u624d\u8bf4\u51fa\u91cd\u70b9\uff1a\u201c\u5170\u4ead\u6c60\u5bb6\u4e4b\u6240\u4ee5\u4e0d\u591f\u522b\u4eba\u5f3a\uff0c\u662f\u56e0\u4e3a\u7600\u8840\u5927\u591a\uff0c\u75c5\u60c5\u5927\u91cd\uff0c\u4ee5\u81f4\u6bd2\u529b\u8513\u5ef6\uff0c\u79ef\u5f31\u96be\u8fd4\uff0c\u8981\u5f7b\u5e95\u6539\u5934\u6362\u9762\uff0c\u5fc5\u987b\u8981\u4e0d\u6015\u727a\u7272\uff0c\u4e0d\u60dc\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u957f\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u773c\u775b\u53d1\u51fa\u51b3\u7136\u7684\u5149\u8292\uff1a\u201c\u6709\u65b9\u5144\u534f\u52a9\uff0c\u6211\u4e0d\u6015\u727a\u7272\u3001\u4e0d\u60dc\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u903c\u89c6\u4ed6\u9053\uff1a\u201c\u4f60\u6709\u51b3\u5fc3\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\uff01\u201d\u6c60\u65e5\u66ae\u5373\u9053\u3002 \u3000\u3000\u201c\u4f60\u6562\u58ee\u58eb\u65ad\u8155\uff1f\u201d \u3000\u3000\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u6c60\u65e5\u66ae\u62bd\u51fa\u4e86\u5251\uff0c\u4f38\u51fa\u5de6\u8155\uff0c\u4e3e\u8d77\u4e86\u5251\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5047\u5982\u6211\u8fd9\u8155\u5b50\u6709\u6bd2\uff0c\u53ea\u8981\u65b9\u5144\u793a\u4e0b\uff0c\u6211\u7acb\u5373\u65a9\u65ad\uff0c\u51b3\u4e0d\u987e\u60dc\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e00\u628a\u6293\u4f4f\u4ed6\u7684\u624b\uff0c\u201c\u6bd2\u4e0d\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u201d\u6c60\u65e5\u66ae\u8fd8\u5728\u6fc0\u52a8\u7684\u5598\u6c14\uff0c\u65b9\u90aa\u771f\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u4e2d\u5174\u6c60\u5bb6\u7684\u4eba\uff0c\u4e0d\u53ef\u5984\u81ea\u6b8b\u5bb3\u81ea\u5df1\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u95ee\uff1a\u201c\u8bf7\u95ee\u65b9\u5144\uff0c\u6bd2\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u201c\u9a71\u6bd2\u7684\u4e8b\uff0c\u7531\u6211\u6765\u505a\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u53ea\u662f\u8981\u516c\u5b50\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6740\u6389\u6c60\u5e9c\u4e2d\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u4e00\u6014\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u51dd\u89c6\u4ed6\u9053\uff0c\u201c\u8fd9\u53ef\u80fd\u662f\u4f60\u7684\u4eb2\u4eba\uff0c\u4e5f\u53ef\u80fd\u662f\u4f60\u7684\u7231\u5c06\uff0c\u53ef\u90fd\u662f\u6bd2\u7d20\u7684\u6765\u6e90\uff0c\u4f60\u820d\u4e0d\u820d\u5f97\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d\u6c60\u65e5\u66ae\u8fdf\u7591\u4e86\u3002 \u3000\u3000\u201c\u522b\u5fd8\u4e86\uff0c\u53ea\u8981\u6bd2\u7d20\u5728\u4f53\u5185\uff0c\u5c31\u6ca1\u6709\u5f7b\u5e95\u75ca\u6108\u8fd9\u56de\u4e8b\u3002\u201d\u65b9\u90aa\u771f\u65ad\u7136\u5730\u9053\u3002 \u3000\u3000\u201c\u597d\u3002\u201d\u6c60\u65e5\u66ae\u54ac\u7259\u9053\uff0c\u201c\u6211\u8bf4\u8fc7\uff0c\u9664\u4e86\u5927\u54e5\u548c\u5927\u5ac2\uff0c\u4f60\u9ad8\u5174\u8981\u6740\u8c01\u90fd\u53ef\u4ee5\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u9ad8\u5174\u3002\u6740\u4eba\u53ea\u662f\u4ef6\u903c\u4e0d\u5f97\u5df2\u7684\u4e8b\uff0c\u51b3\u4e0d\u662f\u4ee4\u4eba\u9ad8\u5174\u7684\u4e8b\u60c5\u3002\u201d\u65b9\u90aa\u771f\u622a\u9053\uff0c\u201c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6c60\u5927\u516c\u5b50\u53cc\u8db3\u6b8b\u5e9f\uff0c\u65e0\u6cd5\u4e3b\u4e8b\uff0c\u4f46\u5bf9\u5170\u4ead\u8fd0\u4f5c\u5e76\u65e0\u59a8\u788d\u3002\u5927\u592b\u4eba\u66f4\u662f\u5170\u8d28\u6167\u5fc3\uff0c\u4eba\u7f18\u7d20\u4f73\uff0c\u5bf9\u6c60\u5bb6\u53ea\u6709\u5229\u65e0\u5bb3\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u6000\u7591\u5730\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u90a3\u4e48\u4f60\u8981\u6740\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4f60\u53ef\u522b\u7ba1\uff0c\u4ee5\u514d\u6253\u8349\u60ca\u86c7\u3002\u201d\u65b9\u90aa\u771f\u4e00\u7b11\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u8fd8\u4e0d\u77e5\u6709\u6ca1\u6709\u547d\u6765\u6740\u4ed6\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u66f4\u662f\u72d0\u7591\uff0c\u53ea\u9053\uff1a\u201c\u597d\uff0c\u6211\u53ef\u4ee5\u4e0d\u95ee\uff0c\u4e0d\u8fc7\u2026\u2026\u6211\u4e0d\u660e\u767d\u65b9\u5144\u7684\u6700\u540e\u4e00\u53e5\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5728\u5168\u8eab\u6295\u5165\u6c60\u5bb6\u4e4b\u524d\uff0c\u8fd8\u5f97\u5148\u53bb\u505a\u4e00\u4ef6\u4e8b\uff0c\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u6211\u505a\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u4e00\u5b9a\u8fd8\u4f1a\u6709\u547d\u56de\u6765\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u60f3\u4e86\u60f3\uff0c\u604d\u609f\u9053\uff1a\u201c\u4f60\u8981\u53bb\u76f8\u601d\u6797\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u9053\uff1a\u201c\u65b9\u5144\uff0c\u76c2\u968f\u56ed\u6848\u5176\u5b9e\u4e0e\u4f60\u65e0\u5173\uff0c\u4f60\u662f\u4e0d\u5fc5\u53bb\u5192\u8fd9\u4e2a\u9669\u7684\uff0c\u4e03\u53d1\u867d\u7136\u662f\u6211\u4eec\u7684\u4eba\uff0c\u4e0d\u8fc7\uff0c\u4e07\u4e00\u5b5f\u6848\u8ddf\u4ed6\u6709\u5173\uff0c\u4ed6\u4e5f\u4f1a\u4e3a\u4fdd\u5b58\u81ea\u5df1\u6027\u547d\u800c\u4e0d\u7518\u53d7\u622e\u7684\uff0c\u52a0\u4e0a\u8521\u65cb\u949f\u548c\u77f3\u65ad\u7709\uff0c\u90fd\u662f\u6b66\u6797\u4e2d\u6709\u6570\u7684\u9ad8\u624b\uff0c\u4f60\u8fd9\u6837\u5192\u9669\u72af\u96be\uff0c\u503c\u4e0d\u503c\u5f97\u2026\u2026\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u6ca1\u6709\u542c\u4e0b\u53bb\uff1a\u201c\u6211\u4e5f\u4e0d\u53ea\u4e3a\u4e86\u8981\u5f04\u6e05\u695a\u5b5f\u968f\u56ed\u7684\u706d\u95e8\u60e8\u6848\uff0c\u540c\u65f6\u4e5f\u4e0d\u613f\u89c1\u8ffd\u547d\u5b64\u8eab\u4f5c\u6218\uff0c\u6211\u610f\u5df2\u51b3\uff0c\u4f60\u4e0d\u5fc5\u76f8\u529d\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u4e0d\u529d\u4f60\uff0c\u201d\u6c60\u65e5\u66ae\u5373\u9053\uff0c\u201c\u6c60\u5bb6\u7684\u4eba\uff0c\u5982\u4f60\u9700\u8981\uff0c\u53ef\u4efb\u7531\u4f60\u8c03\u52a8\uff0c\u6216\u80fd\u4f5c\u81c2\u52a9\u3002\u201d \u3000\u3000\u201c\u4e0d\u9700\u8981\uff0c\u4eba\u591a\u53cd\u7d2f\u4e8b\u3002\u201d\u65b9\u90aa\u771f\u9053\uff1a\u201c\u5728\u6211\u8d76\u53bb\u76f8\u601d\u6797\u4e4b\u95ee\uff0c\u5012\u6709\u4e00\u4e2a\u8981\u6c42\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u662f\u6761\u4ef6\uff0c\u201d\u65b9\u90aa\u771f\u89e3\u91ca\u9053\uff0c\u201c\u8fd9\u53ea\u662f\u8981\u6c42\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u770b\u770b\u540d\u52a8\u5929\u4e0b\u7684\u2018\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u2019\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u8fd9\u79cd\u7edd\u809a\u6697\u5668\uff0c\u5728\u522b\u5904\u53ef\u6ca1\u5f97\u770b\uff0c\u800c\u4e14\uff0c\u8d81\u6211\u8fd8\u4e0d\u77e5\u6709\u6ca1\u6709\u547d\u5728\u4e4b\u524d\uff0c\u770b\u770b\u8fd9\u6837\u5b50\u7684\u6697\u5668\uff0c\u4e5f\u662f\u4ef6\u53ef\u4ee5\u7791\u76ee\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u5343\u4e07\u4e0d\u8981\u8fd9\u6837\u8bf4\u3002\u201d\u6c60\u65e5\u66ae\u6709\u70b9\u751f\u6c14\u9053\uff0c\u201c\u5927\u54e5\u4e00\u5b9a\u8981\u6d3b\u7740\u56de\u6765\uff0c\u6211\u5907\u7b75\u76f8\u5019\u3002\u81f3\u4e8e\u2018\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u2019\u2026\u2026\u5927\u54e5\u8981\u770b\uff0c\u7edd\u65e0\u95ee\u9898\u2014\u2014\u201d \u3000\u3000\u201c\u5170\u4ead\u2018\u5175\u5668\u623f\u2019\u91cc\u7684\u6b66\u5668\uff0c\u53ea\u8981\u5f97\u6211\u540c\u610f\uff0c\u9047\u6709\u8981\u4e8b\uff0c\u5373\u53ef\u53d6\u7528\uff0c\u53ea\u8981\u4e8b\u5148\u767b\u8bb0\u5177\u6848\u4fbf\u884c\u3002\u5f53\u7136\uff0c\u5982\u679c\u662f\u7f55\u4e16\u5947\u5175\uff0c\u90a3\u5c31\u975e\u5230\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u4e0d\u80fd\u64c5\u7528\u3002\u201d\u6c60\u65e5\u66ae\u62c2\u62c2\u8896\u5b50\uff0c\u8d70\u5230\u4e00\u5f20\u6a80\u6728\u7ea2\u7f0e\u5de8\u684c\u524d\uff0c\u201c\u2026\u2026\u6211\u4e00\u5411\u559c\u6b22\u597d\u7684\u5175\u5668\uff0c\u4f46\u4e5f\u4e00\u76f4\u90fd\u8ba4\u4e3a\uff0c\u597d\u7684\u4eba\u624d\u6bd4\u597d\u7684\u6b66\u5668\u66f4\u91cd\u8981\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u4e0d\u4f1a\u62ff\u5175\u5668\u6362\u4eba\u6027\u547d\u3002\u201d\u4ed6\u6284\u8d77\u684c\u4e0a\u4e00\u652f\u94c1\u7b1b\uff0c\u9012\u7ed9\u65b9\u90aa\u771f\u9053\uff0c\u201c\u5982\u4f60\u8981\u5b83\uff0c\u6211\u5c31\u9001\u7ed9\u65b9\u5144\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5403\u4e86\u4e00\u60ca\u3002\u539f\u6765\u8fd9\u6839\u94c1\u7b1b\uff0c\u7adf\u5c31\u85cf\u6709\u201c\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u201d\uff0c\u770b\u6765\u53ea\u4e0d\u8fc7\u662f\u4e00\u6839\u666e\u901a\u7684\u7b1b\u5b50\uff0c\u5982\u679c\u521a\u624d\u6c60\u65e5\u66ae\u5728\u63e1\u94c1\u7b1b\u65f6\u5bf9\u81ea\u5df1\u731d\u7136\u51fa\u624b\uff0c\u65bd\u653e\u8fd9\u60ca\u5929\u52a8\u5730\u7684\u5341\u4e5d\u795e\u9488\uff0c\u53ea\u6015\u8fde\u81ea\u5df1\u4e5f\u65ad\u96be\u5c3d\u6570\u8eb2\u5f97\u5f00\u53bb\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u63a5\u8fc7\u94c1\u7b1b\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u7aef\u8be6\u94c1\u7b1b\u6784\u9020\uff0c\u77a5\u89c1\u94c1\u7b1b\u4e0a\u7528\u7ec6\u4e1d\u7cfb\u7740\u4e00\u5f20\u5c0f\u7eb8\u6761\uff0c\u6284\u8d77\u6765\u7ec6\u770b\u4e86\u770b\uff0c\u53ea\u89c1\u90fd\u662f\u65e5\u671f\u548c\u4eba\u540d\u53ca\u6848\u4f8b\uff0c\u8b6c\u5982\uff1a\u67d0\u6708\u67d0\u65e5\uff0c\u6c60\u65e5\u66ae\u4e0e\u6e38\u7389\u906e\u5bb4\u53d9\uff0c\u56e0\u751f\u6050\u6e38\u5bb6\u6697\u65bd\u8fa3\u624b\uff0c\u6545\u53d6\u7528\u201c\u94c1\u7b1b\u201d\u4ee5\u4f5c\u9632\u8303\u7b49\u7b49\u2026\u2026\u65b9\u90aa\u771f\u76ee\u5149\u4e00\u51dd\uff0c\u7a81\u7136\u8138\u8272\u4e00\u53d8\u3002 \u3000\u3000\u6c60\u65e5\u66ae\u751a\u4e3a\u654f\u611f\uff0c\u9a6c\u4e0a\u89c9\u5bdf\uff0c\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u4e8b\u3002\u542c\u8bf4\u8fd9\u2018\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\uff0c\u539f\u5148\u4e0d\u662f\u88c5\u8bbe\u5728\u94c1\u7b1b\u7684\u673a\u7c27\u7247\u536b\uff0c\u8fd9\u662f\u540e\u6765\u4e00\u4e2a\u53eb\u505a\u4e49\u96ea\u5cb8\u7684\u5e74\u9752\u4eba\u6539\u88c5\u7684\u2026\u2026\u8fd9\u6837\u7cbe\u5de7\uff0c\u4e5f\u7740\u5b9e\u4e0d\u7b80\u5355\u3002\u201d\u65b9\u90aa\u771f\u628a\u73a9\u7740\u94c1\u7b1b\uff0c\u7136\u540e\u8c03\u8f6c\u8fc7\u6765\uff0c\u606d\u606d\u656c\u656c\u7684\u53cc\u624b\u9012\u7ed9\u6c60\u65e5\u66ae\uff0c\u9053\uff0c\u201c\u6211\u770b\u8fc7\u4e86\uff0c\u6211\u8981\u8d70\u4e86\uff0c\u8c22\u8c22\u3002\u201d \u3000\u3000\u6c60\u65e5\u66ae\u5fd9\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u2026\u2026\uff1f\u201d\u60f3\u628a\u94c1\u7b1b\u585e\u7ed9\u65b9\u90aa\u771f\u5e26\u53bb\u5907\u7528\u3002 \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\u3002\u201d\u65b9\u90aa\u771f\u6d12\u7136\u7b11\u9053\uff0c\u201c\u6211\u672a\u66ff\u6c60\u5bb6\u5c3d\u4e00\u5206\u4e00\u661f\u529b\u4e4b\u524d\uff0c\u7109\u6562\u5148\u52a8\u7528\u4ed6\u5bb6\u7684\u4e00\u4e8b\u4e00\u7269\uff1f\u201d \u3000\u3000\u201c\u65b9\u5144\u4fdd\u91cd\u3002\u201d\u6c60\u65e5\u66ae\u53ea\u6709\u8fd9\u6837\u8bf4\u3002 \u3000\u3000\u65b9\u90aa\u771f\u5411\u6c60\u65e5\u66ae\u6df1\u6df1\u4e00\u4e00\u63d6\uff1a\u201c\u613f\u80fd\u6709\u4e3a\u516c\u5b50\u6548\u529b\u4e4b\u65e5\u3002\u201d\u8bf4\u7f62\u98d8\u7136\u800c\u53bb\uff0c\u53ea\u5269\u4e0b\u6c60\u65e5\u66ae\u4e00\u4eba\u6014\u6014\u5fe1\u5fe1\u7684\u7ad9\u5728\u5175\u5668\u623f\u91cc\uff0c\u9762\u5411\u521a\u5192\u4e0a\u6765\u7684\u65ed\u9633\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u7a76\u7adf\u4ed6\u8981\u6740\u7684\u662f\u751a\u4e48\u4eba\u5462\uff1f\u201d\u53c8\u770b\u4e86\u770b\u8fdc\u5904\u60ca\u98de\u7684\u9e1f\u7fa4\uff1a\u201c\u4ed6\u662f\u5426\u80fd\u5b89\u7136\u65e0\u6059\uff1f\u201d \u3000\u3000\u76f8\u601d\u6797\u867d\u5c5e\u5c0f\u78a7\u6e56\u6e38\u5bb6\u4e4b\u5730\uff0c\u4f46\u6e38\u5bb6\u5e76\u6ca1\u6709\u5728\u8fd9\u5730\u65b9\u5e03\u9632\u3002 \u3000\u3000\u8fc7\u4e86\u76f8\u601d\u6797\uff0c\u624d\u662f\u76f8\u601d\u4ead\uff0c\u4ece\u76f8\u601d\u4ead\u53ef\u4ee5\u642d\u8239\u8d8a\u5c0f\u78a7\u6e56\uff0c\u8fd9\u624d\u662f\u6e38\u5bb6\u7684\u91cd\u5730\u3002 \u3000\u3000\u76f8\u601d\u4ead\u662f\u4e00\u4e2a\u7f8e\u4e3d\u3001\u5e7d\u96c5\u7684\u5730\u65b9\uff0c\u9664\u4e86\u76f8\u601d\u6811\u3001\u8fd8\u6709\u6ee1\u6811\u76f8\u601d\u5b50\uff0c\u6ee1\u5730\u7684\u76f8\u601d\u53f6\uff0c\u90a3\u4e00\u89d2\u5982\u753b\u6674\u7a7a\uff0c\u4eff\u4f5b\u4e5f\u5fd2\u715e\u60c5\u591a\uff01 \u3000\u3000\u76f8\u601d\u6797\u7684\u5c3d\u5904\u4fbf\u662f\u76f8\u601d\u4ead\u3002\u6c5f\u4e0a\u8f7b\u821f\u6e05\u5999\u5165\u773c\uff0c\u6e56\u6c34\u78a7\u843d\uff0c\u5fd8\u5374\u51e1\u5c18\uff1b\u6c5f\u8fb9\u78a7\u67f3\u6210\u884c\uff0c\u9ea6\u7566\u5410\u7eff\uff0c\u67f3\u5824\u4e0a\u843d\u82f1\u98d8\u7eb7\uff0c\u7af9\u53f6\u542b\u9752\u3002\u66f4\u6709\u519c\u4eba\u53e3\u5531\u8bb4\u6b4c\uff0c\u6e56\u821f\u5f20\u7f51\u6355\u9c7c\uff0c\u8fd8\u6709\u9a9a\u4eba\u58a8\u5ba2\uff0c\u5bf9\u666f\u611f\u6000\uff0c\u5316\u4f5c\u80f8\u4e2d\u8bd7\u5343\u884c\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4e2a\u98ce\u666f\u5982\u753b\u7684\u5730\u65b9\u3002 \u3000\u3000\u5355\u770b\u78a7\u6e56\u5bf9\u5cb8\u7684\u201c\u5c0f\u78a7\u6e56\u5c71\u5e84\u201d\uff0c\u6c14\u52bf\u6062\u5b8f\uff0c\u6c14\u8c61\u4e07\u5343\uff0c\u7ea2\u5899\u78a7\u74e6\uff0c\u68ee\u7136\u58c1\u7acb\uff0c\u5c31\u77e5\u9053\u6e38\u5bb6\u5b9a\u5fc5\u4ee3\u6709\u4eba\u624d\uff0c\u800c\u4e14\u51b3\u975e\u6b62\u4e8e\u4e00\u65b9\u96c4\u6770\u800c\u5df2\u3002 \u3000\u3000\u4e03\u53d1\u5927\u5e08\u65e0\u7591\u4e5f\u662f\u8fd9\u6837\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u624d\u8981\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u5f97\u4e0d\u6765\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u52a9\u5170\u4ead\u6c60\u5bb6\u5bf9\u4ed8\u6e38\u5bb6\uff0c\u4f46\u4ed6\u4e0d\u80fd\u518d\u5192\u4e0a\u201c\u56e0\u4e3a\u4f5c\u8d3c\u5fc3\u865a\uff0c\u6240\u4ee5\u624d\u4e0d\u6562\u6765\u76f8\u601d\u4ead\uff0c\u5f53\u9762\u5bf9\u8d28\u5b5f\u592a\u5b88\u706d\u95e8\u8840\u6848\u7684\u4e8b\u3002\u201d\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6c60\u65e5\u66ae\u5f88\u5668\u91cd\u4ed6\uff0c\u800c\u4e14\u66fe\u529b\u9080\u4ed6\u52a0\u76df\uff0c\u4f46\u662f\uff0c\u4e00\u5b9a\u8981\u7b49\u4ed6\u5f04\u6e05\u695a\u4e0e\u201c\u5b5f\u5927\u5b88\u6848\u201d \u3000\u3000\u65e0\u5173\u3001\u5f04\u4e2a\u6c34\u843d\u77f3\u51fa\u540e\uff0c\u624d\u4f1a\u91cd\u4efb\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u6295\u5165\u5c0f\u78a7\u6e56\uff0c\u867d\u7136\uff0c\u770b\u8d77\u6765\uff0c\u5c0f\u78a7\u6e56\u7684\u6761\u4ef6\u597d\u50cf\u8981\u6bd4\u5170\u4ead\u66f4\u597d\uff0c\u800c\u4e14\u4e5f\u529b\u9080\u8fc7\u4ed6\u3002 \u3000\u3000\u56e0\u4e3a\u5c0f\u78a7\u6e56\u6709\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u201c\u6a2a\u5200\u7acb\u9a6c\u3001\u9189\u5367\u5c71\u5c97\u201d\u7684\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u2014\u2014\u6709\u987e\u4f5b\u5f71\u5728\uff0c\u5c31\u4e0d\u4f1a\u5bb9\u5f97\u4e0b\u81ea\u5df1\uff01 \u3000\u3000\u8fd9\u70b9\u4e03\u53d1\u5927\u5e08\u6bd4\u8c01\u90fd\u66f4\u6e05\u695a\u3002 \u3000\u3000\u56e0\u4e3a\u987e\u4f5b\u5f71\u5176\u5b9e\u5c31\u662f\u4ed6\u7684\u5e08\u5144\uff0c\u5728\u4e03\u53d1\u5927\u5e08\u8fd8\u662f\u53eb\u505a\u6b27\u9633\u4e03\u53d1\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u662f\u540c\u4e00\u5e08\u7236\u95e8\u4e0b\u7684\u5e08\u5144\u5f1f\u3002 \u3000\u3000\u4e03\u53d1\u5927\u5e08\u4e00\u76f4\u5e0c\u671b\u81ea\u5df1\u80fd\u6bd4\u987e\u4f5b\u5f71\u66f4\u5f3a\uff0c\u4ed6\u82e5\u52a0\u5165\u5c0f\u78a7\u6e56\u6e38\u5bb6\uff0c\u8eab\u4efd\u80af\u5b9a\u4f1a\u5728\u987e\u4f5b\u5f71\u4e4b\u4e0b\uff0c\u4e03\u53d1\u5927\u5e08\u662f\u7edd\u5bf9\u4e0d\u4f5c\u8fd9\u79cd\u201c\u5c48\u5c31\u201d\u7684\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5b81\u53ef\u52a0\u76df\u5170\u4ead\u6c60\u5bb6\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u8fd8\u6709\u4ed6\u7684\u7406\u7531\u7684\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5f53\u4ed6\u770b\u5230\u5c0f\u78a7\u6e56\u6e38\u5bb6\u5821\u4f9d\u6c34\u9760\u5c71\u5efa\u7acb\u7684\u6c14\u6d3e\u65f6\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u4e3a\u8fd9\u5e84\u4e25\u3001\u5b8f\u4f1f\u3001\u4f18\u96c5\u3001\u7075\u79c0\u517c\u5f97\u7684\u5947\u666f\u800c\u8d5e\u7fa1\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4ed6\u5c31\u542c\u89c1\u6709\u4e00\u4e2a\u4eba\u957f\u541f\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u957f\u541f\u7684\u58f0\u97f3\uff0c\u5341\u5206\u96be\u542c\uff0c\u50cf\u4e00\u4e2a\u75c5\u5f97\u5feb\u8981\u65ad\u6c14\u7684\u4eba\u547b\u541f\u4e00\u822c\uff1a \u3000\u3000\u201c\u4e0d\u6539\u9752\u5c71\u4e0d\u89e3\u6068\uff0c\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\u3002\u201d \u3000\u3000\u201c\u2018\u4e09\u4e0d\u6740'\u77f3\u65ad\u7709\u3002\u201d\u4e03\u53d1\u5927\u5e08\u7ea2\u888d\u752b\u5c55\u53c8\u9616\uff0c\u5408\u5341\u9882\u9053\uff1a\u201c\u4f60\u6765\u4e86\u3002\u201d \u3000\u3000\u4ead\u5916\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u76f8\u601d\u9053\u4e0a\u4e5f\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u4eba\u90fd\u5728\u8fdc\u5904\uff0c\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u3002 \u3000\u3000\u4eca\u5929\u4eff\u4f5b\u4e0d\u662f\u4e0e\u4f1a\u7684\u4e3b\u8981\u4eba\u7269\uff0c\u8c01\u90fd\u4e0d\u6562\u3001\u6216\u4e0d\u80fd\u8d70\u8fd1\u76f8\u601d\u4ead\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u58f0\u97f3\u662f\u54ea\u91cc\u4f20\u6765\u7684\uff1f \u3000\u3000\u201c\u4f60\u5176\u5b9e\u4e0d\u8be5\u53eb\u505a\u2018\u4e09\u4e0d\u6740\u2019\uff0c\u201d\u53ea\u542c\u4e03\u53d1\u5927\u5e08\u53c8\u9053\uff0c\u201c\u4f60\u5e94\u53eb\u505a\u2018\u4e09\u4e0d\u8be5\u2019\u3002\u201d \u3000\u3000\u90a3\u6c99\u560e\u7684\u58f0\u97f3\u6709\u4e9b\u8be7\u5f02\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u5728\u8fd9\u51e0\u4e2a\u65f6\u8fb0\u4e4b\u5185\u81f3\u5c11\u72af\u4e86\u4e09\u4e2a\u4e0d\u8be5\uff0c\u201d\u4e03\u53d1\u5927\u5e08\u7709\u76ee\u4e0d\u62ac\u5730\u9053\uff0c\u201c\u7b2c\u4e00\uff0c\u4f60\u4e0d\u8be5\u79c1\u81ea\u4e0e\u65b9\u90aa\u771f\u5bf9\u51b3\uff1b\u7b2c\u4e8c\uff0c\u8fd9\u7ea6\u4f1a\u4f60\u4e0d\u8be5\u6765\uff1b\u7b2c\u4e09\u2026\u2026\u201d \u3000\u3000\u4ed6\u628a\u5408\u4ec0\u7684\u624b\u7f13\u7f13\u79fb\u5f00\uff0c\u63d2\u5165\u4e86\u4ed6\u618e\u888d\u524d\u7684\u5e03\u888b\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6765\u4e86\uff0c\u90a3\u5c31\u4e0d\u8be5\u7ad9\u5728\u8d2b\u50e7\u7684\u5934\u4e0a\u3002\u201d \u3000\u3000\u53ea\u89c1\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u4e2a\u5934\u9885\uff0c\u5728\u4ead\u6a90\u51fa\u73b0\uff0c\u662f\u5012\u770b\u8fdb\u6765\u7684\uff0c\u7136\u540e\u53c8\u55d6\u5730\u4e00\u58f0\uff0c\u4eba\u5df2\u6e9c\u5230\u4e86\u4ead\u4e2d\uff0c\u8fd9\u662f\u4e00\u4e2a\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u3002 \u3000\u3000\u6ca1\u6709\u7709\u6bdb\u7684\u4eba\u51b7\u54fc\u9053\uff1a\u201c\u6211\u5012\u5fd8\u4e86\u4e03\u53d1\u7985\u5e08\u540d\u9707\u6b66\u6797\u7684\u2018\u4e00\u53d1\u795e\u523a\u201d\uff0c\u5c45\u7136\u8dd1\u5230\u5927\u5e08\u7684\u5934\u4e0a\u53bb\uff0c\u4e5f\u4e0d\u6015\u88ab\u5c04\u7a7f\u51e0\u4e2a\u7a9f\u7abf\uff01\u201d\u65ad\u7709\u77f3\u7684\u5de6\u80a9\u81f3\u53f3\u80c1\uff0c\u5305\u624e\u7740\u5e03\u5e1b\uff0c\u4ecd\u6e17\u7740\u8840\u8ff9\uff0c\u8fd9\u4e00\u9053\u4f24\u75d5\uff0c\u9690\u900f\u7740\u6b7b\u91cc\u9003\u751f\u4e4b\u60ca\u9669\u548c\u51c4\u70c8\uff1a\u201c\u53ef\u4f60\u662f\u600e\u77e5\u9053\u6211\u8ddf\u59d3\u65b9\u7684\u5bf9\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\uff0c\u8d2b\u50e7\u662f\u5170\u4ead\u6c60\u5bb6\u8fdc\u9053\u8bf7\u6765\u7684\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u604d\u7136\u9053\uff1a\u201c\u6211\u5012\u5fd8\u4e86\uff0c\u5170\u4ead\u6c60\u5bb6\u4e0d\u8fdc\u5343\u91cc\uff0c\u8bf7\u4f60\u8fd9\u4f4d\u5927\u548c\u5c1a\u6765\u4e3a\u4ed6\u4eec\u8d85\u6e21\u7684\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5012\u4e5f\u4e0d\u4ee5\u4e3a\u5fe4\uff1a\u201c\u9601\u4e0b\u53c8\u4f55\u5fc5\u5fff\u8a00\u4f24\u4eba\u3002\u4f60\u53d7\u4f24\u4e0d\u8f7b\uff0c\u4eca\u5929\u7531\u5c0f\u78a7\u6e56\u6e38\u516c\u5b50\u4f5c\u4e3b\u4e3a\u540d\u6355\u8ffd\u547d\u63d0\u51fa\u6765\u7684\u7ea6\u6664\uff0c\u4f60\u65e2\u5df2\u662f\u5999\u624b\u5802\u7684\u8d35\u5bbe\uff0c\u4f55\u5fc5\u6765\u5e94\u8fd9\u8d9f\u6d51\u6c34\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u5170\u4ead\u6c60\u5bb6\u7684\u5ea7\u4e0a\u5ba2\uff0c\u53c8\u4f55\u5fc5\u6765\u8d74\u76f8\u601d\u4ead\u4e4b\u7ea6\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u8d2b\u50e7\u4ece\u672a\u505a\u4f24\u5929\u5bb3\u7406\u7684\u4e8b\uff0c\u5b5f\u592a\u5b88\u706d\u95e8\u8840\u6848\uff0c\u4e0e\u8d2b\u50e7\u65e0\u5173\uff0c\u8d2b\u50e7\u4e5f\u60f3\u5f04\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u7a76\u7adf\u662f\u8c01\u5bf9\u5b5f\u592a\u5b88\u4e0b\u60e8\u7edd\u4eba\u571c\u7684\u6bd2\u624b\uff1f\u5982\u6b64\u826f\u673a\uff0c\u4e3a\u4f55\u4e0d\u6765\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u7709\u9aa8\u4e0a\u53c8\u9690\u9690\u51fa\u73b0\u4e24\u7247\u7070\u5f71\uff1a\u201c\u4fbf\u662f\u8fd9\u6837\uff0c\u8fd9\u76f8\u601d\u4ead\u4e4b\u7ea6\uff0c\u8c01\u8981\u662f\u4e0d\u6562\u6765\uff0c\u8c01\u4fbf\u597d\u50cf\u662f\u4f5c\u4e86\u90a3\u4ef6\u4e8f\u5fc3\u4e8b\u2026\u2026\u6240\u4ee5\u9664\u975e\u662f\u771f\u7684\u81ea\u5df1\u5e72\u4e86\u90a3\u4ef6\u4e8b\uff0c\u5426\u5219\u53ea\u8981\u6709\u817f\u7684\uff0c\u8c01\u90fd\u5f97\u6765\u4e00\u8d9f\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8ffd\u547d\u65e2\u80fd\u4fc3\u4f7f\u6e38\u7389\u906e\u53d1\u4fa0\u4e49\u67ec\uff0c\u5e7f\u4f20\u6c5f\u6e56\u9ed1\u767d\u9053\uff0c\u8981\u6211\u4eec\u6765\u6b64\u4e00\u53d9\uff0c\u53ea\u6015\u8fd9\u4f4d\u540d\u6355\u65e9\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u6709\u529e\u6cd5\u4ee4\u51f6\u624b\u65e0\u53ef\u9041\u5f62\u4e86\u3002\u65bd\u4e3b\u96be\u9053\u4e0d\u62c5\u5fe7\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u4ec0\u4e48\u597d\u62c5\u5fe7\u7684\uff1f\u201d\u65ad\u7709\u77f3\u563f\u563f\u7b11\u9053\uff1a\u201c\u5927\u5e08\u96be\u9053\u6ca1\u6709\u770b\u89c1\u5230\u5e95\u662f\u8c01\u6ca1\u6765\uff1f\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u5ffd\u7136\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u60a0\u60a0\u7684\u8bf4\uff1a\u201c\u8c01\u8bf4\u4ed6\u6ca1\u6765\uff1f\u201d \u3000 \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u7834\u4f53\u65e0\u5f62\u5251\u6c14 \u3000 \u3000\u3000\u4e03\u53d1\u5927\u5e08\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u76f8\u601d\u6797\u9053\u4e0a\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u5e74\u8f7b\u4eba\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u628a\u5251\u3002 \u3000\u3000\u4e00\u628a\u6781\u957f\u6781\u957f\u7684\u5251\u3002 \u3000\u3000\u4ed6\u989d\u4e0a\u6709\u75e3\uff0c\u4e00\u9897\u5927\u7070\u75e3 \u3000\u3000\u4ed6\u8f6e\u5ed3\u6781\u6df1\uff0c\u80a4\u8272\u9ed4\u9ed1\uff0c\u773c\u795e\u6709\u529b\uff0c\u8138\u4e0a\u51fa\u73b0\u6781\u5176\u575a\u6bc5\u7684\u795e\u8272\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4eba\u8e3d\u8e3d\u884c\u6765\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u4e3e\u52a8\uff0c\u4f46\u4e03\u53d1\u7985\u5e08\u548c\u65ad\u7709\u77f3\u5374\u611f\u89c9\u5230\u4e00\u80a1\u903c\u4eba\u7684\u6740\u6c14\uff0c\u903c\u4eba\u800c\u6765\u3002 \u3000\u3000\u6781\u4e3a\u53ef\u6015\u7684\u6740\u6c14\u3002 \u3000\u3000\u65ad\u7709\u77f3\u4e00\u751f\u90fd\u5728\u6740\u4eba\u3002\u81ea\u4ed6\u5728\u5341\u4e00\u5c81\u8d77\u5077\u5077\u628a\u4e00\u4e2a\u8bfb\u4e66\u4e60\u6b66\u90fd\u6bd4\u81ea\u5df1\u5f3a\u7684\u8868\u5144\u63a8\u843d\u4e95\u91cc\u53bb\u4e4b\u540e\uff0c\u4ed6\u5c31\u4e0d\u65ad\u7684\u6740\u4eba\uff0c\u4e0d\u65ad\u7684\u7528\u5404\u79cd\u9119\u6076\u7684\u65b9\u6cd5\u6740\u4eba\uff0c\u800c\u4e14\u4e0d\u65ad\u7684\u7528\u65b0\u7684\u65b9\u5f0f\u6740\u4eba\uff0c\u6740\u5f97\u4ee4\u81ea\u5df1\u89c9\u5f97\u6ee1\u610f\u4e3a\u6b62\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5df2\u7528\u8fc7\u51e0\u4e4e\u6240\u6709\u6740\u4eba\u7684\u65b9\u6cd5\uff0c\u5bf9\u6740\u4eba\u800c\u8a00\uff0c\u5df2\u8d8a\u6765\u8d8a\u6ca1\u6709\u65b0\u9c9c\u611f\uff0c\u6ca1\u6709\u5148\u524d\u7684\u8da3\u5473\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6740\u4e86\u90a3\u4e48\u591a\u4eba\uff0c\u7eb5\u662f\u4ed6\u4e0d\u6740\u4eba\u7684\u65f6\u5019\uff0c\u6740\u6c14\u4e5f\u8db3\u4ee5\u51cc\u4eba\u3002 \u3000\u3000\u4e03\u53d1\u5927\u5e08\u7a76\u7adf\u6740\u4e86\u591a\u5c11\u4eba\uff0c\u751a\u81f3\u6709\u6ca1\u6709\u6740\u8fc7\u4eba\uff0c\u8c01\u4e5f\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u4efd\u672c\u5c31\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u4e2a\u79d8\u5bc6\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6b66\u6797\u4e2d\u8bb8\u591a\u91cd\u8981\u7684\u4e8b\u4ef6\uff0c\u4e3e\u8db3\u8f7b\u91cd\u7684\u4e8b\u4ef6\uff0c\u96be\u514d\u90fd\u8ddf\u4ed6\u6cbe\u4e0a\u4e86\u70b9\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u8eab\u7740\u5927\u7ea2\u888d\uff0c\u53d1\u5982\u77ed\u9488\uff0c\u4eff\u4f5b\u9690\u6f3e\u5f02\u91c7\uff0c\u4e00\u53cc\u773c\u775b\uff0c\u5c31\u50cf\u5730\u72f1\u91cc\u7684\u70bc\u706b\uff0c\u5492\u8bed\u4e2d\u7684\u7075\u9b42\uff0c\u751a\u81f3\u6709\u4eba\u8bf4\uff0c\u53ea\u8981\u7ed9\u4e03\u53d1\u7985\u5e08\u77aa\u4f60\u4e00\u773c\uff0c\u4f60\u7684\u9b42\u9b44\u5c31\u4f1a\u88ab\u4ed6\u6151\u53bb\u3002 \u3000\u3000\u50cf\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u5c31\u7b97\u51fa\u5bb6\u5341\u6b21\uff0c\u715e\u6c14\u4e5f\u4e00\u6837\u4e0d\u62d8\u50e7\u4fa3\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5f53\u8fd9\u9752\u5e74\u4e00\u65e6\u903c\u8fd1\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u90fd\u611f\u89c9\u5f97\u5230\uff0c\u81ea\u5df1\u8eab\u4e0a\u7684\u6740\u6c14\u4eff\u4f5b\u6d88\u5931\u4e86\u3002 \u3000\u3000\u6740\u6c14\u4eff\u4f5b\u90fd\u5230\u4e86\u90a3\u9752\u5e74\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u751a\u81f3\u4e09\u4e2a\u4eba\u7684\u6740\u6c14\uff0c\u65e9\u5df2\u5728\u90a3\u9752\u5e74\u7684\u8eab\u4e0a\u6740\u5f97\u9b3c\u54ed\u795e\u53f7\u3001\u98ce\u4e91\u8272\u53d8\u3002 \u3000\u3000\u7136\u800c\u4e09\u4eba\u5176\u5b9e\u8fd8\u6ca1\u6709\u52a8\u624b\u3002 \u3000\u3000\u81f3\u5c11\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u3002 \u3000\u3000\u65ad\u7709\u77f3\u770b\u7740\u8fd9\u9752\u5e74\u624b\u4e0a\u7684\u957f\u5251\uff0c\u5583\u54fa\u5730\u9053\uff1a\u201c\u672a\u51fa\u5251\u5c31\u6709\u8fd9\u822c\u6740\u6c14\uff0c\u597d\u4e00\u67c4\u5251\uff01\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u5374\u9053\uff1a\u201c\u672a\u52a8\u624b\u5df2\u6709\u8fd9\u6837\u715e\u6c14\uff0c\u597d\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u90a3\u9752\u5e74\u5df2\u8d70\u5230\u4ead\u524d\uff0c\u7ad9\u4f4f\uff0c\u9053\uff1a\u201c\u8ffd\u547d\u8fd8\u6ca1\u6709\u5230\u4e48\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u8138\u4e0a\u6709\u4e00\u4e2a\u8be1\u5f02\u7684\u8868\u60c5\uff1a\u201c\u4e5f\u8bb8\uff0c\u6211\u4eec\u6765\u9f50\u4e86\uff0c\u4ed6\u53cd\u800c\u4e0d\u6562\u6765\u4e86\u3002\u8521\u8001\u5f1f\u6ca1\u5fd8\u4e86\u6211\u4eec\u4e0a\u6b21\u7684\u7ea6\u5b9a\u7f62\uff1f\u201d \u3000\u3000\u90a3\u9752\u5e74\u70b9\u5934\u9053\uff1a\u201c\u4e00\u9f50\u8054\u624b\uff0c\u6740\u4e86\u540d\u6355\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u5c55\u989c\u9053\uff1a\u201c\u5bf9\u6781\u4e86\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5408\u4ec0\u9053\uff1a\u201c\u8521\u5c11\u4fa0\u8fd9\u56de\u53c8\u6bd4\u4e0a\u6b21\u89c1\u9762\uff0c\u6740\u6c14\u66f4\u70c8\u3001\u9510\u6c14\u66f4\u52b1\u3001\u5251\u6c14\u66f4\u70bd\uff0c\u5f53\u771f\u662f\u53ef\u559c\u53ef\u8d3a\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u5f53\u7136\u5c31\u662f\u65b0\u8fd1\u5d1b\u8d77\u6c5f\u6e56\u4e0a\u7684\u795e\u79d8\u5251\u5ba2\u8521\u65cb\u949f\uff0c\u53ea\u89c1\u4ed6\u53cc\u7709\u4e00\u8f69\uff0c\u9053\uff1a\u201c\u8fd9\u53c8\u4f55\u559c\u4e4b\u6709\uff1f\u4f55\u8d3a\u4e4b\u6709\uff1f\u201d \u3000\u3000\u4e03\u53d1\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u901a\u5e38\uff0c\u8fd9\u79cd\u6c14\u52bf\u80fd\u591f\u9661\u589e\uff0c\u662f\u8981\u6b66\u529f\u53d8\u5f3a\u624d\u4f1a\u5916\u73b0\uff0c\u9601\u4e0b\u5728\u54b1\u4eec\u5206\u624b\u7684\u77ed\u77ed\u65f6\u65e5\u5185\uff0c\u529f\u529b\u80fd\u4e00\u518d\u63d0\u5347\uff0c\u8fdb\u6b65\u795e\u901f\uff0c\u4e0d\u4f46\u53ef\u559c\u53ef\u8d3a\uff0c\u7b80\u76f4\u53ef\u656c\u53ef\u4f69\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u542c\u4e86\uff0c\u8138\u4e0a\u4f3c\u9732\u51fa\u4e00\u4e1d\u5f97\u610f\u4e4b\u8272\uff0c\u5ffd\u53c8\u56de\u590d\u5e38\u6001\uff0c\u6de1\u6de1\u7684\u9053\uff1a\u201c\u5e9f\u8bdd\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u5374\u4e0d\u52a8\u6c14\uff0c\u53cd\u95ee\uff1a\u201c\u5c11\u4fa0\u8ba4\u4e3a\u8d2b\u50e7\u8bf4\u9519\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff0c\u201c\u4f60\u7684\u8bdd\u65e2\u4e0d\u53ef\u80fd\u9519\uff0c\u4e5f\u6ca1\u6709\u5206\u660e\u7684\u5bf9\uff0c\u6839\u672c\u65e0\u5bf9\u9519\u53ef\u8a00\uff0c\u53ea\u542c\u4e86\u8ba9\u4eba\u5fc3\u91cc\u8212\u670d\uff0c\u6240\u4ee5\u662f\u5e9f\u8bdd\u3002\u201d \u3000\u3000\u4e03\u53d1\u5475\u5475\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4eba\u4e0e\u4eba\u5728\u4e00\u8d77\uff0c\u8bf4\u7684\u5927\u90fd\u662f\u8fd9\u79cd\u5e9f\u8bdd\uff0c\u96be\u9053\u4e00\u89c1\u9762\u5c31\u8bf4\u770b\u5bf9\u65b9\u4e0d\u987a\u773c\uff0c\u4e00\u4e0a\u6765\u5c31\u522e\u5bf9\u65b9\u8033\u5149\u4e48\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff0c\u201c\u65b0\u8fd1\u4e5f\u8fd8\u6709\u4e00\u7c7b\u4eba\u5192\u7a9c\u8d77\u6765\uff0c\u4ed6\u4eec\u559c\u6b22\u8bf4\u4e00\u4e9b\u8bbd\u4eba\u3001\u81ea\u5632\u7684\u8bdd\uff0c\u559c\u6b22\u628a\u81ea\u5df1\u548c\u522b\u4eba\u90fd\u8d2c\u4f4e\u4e0b\u53bb\uff0c\u4e5f\u85c9\u6b64\u72e0\u72e0\u7684\u523a\u4eba\u89c1\u8840\uff0c\u8fd9\u6837\u6765\u8868\u793a\u4ed6\u4eec\u5f88\u667a\u6167\u3001\u5f88\u4e0d\u865a\u4f2a\u3001\u5f88\u6709\u89c1\u8bc6\u3001\u5f88\u6709\u4e2a\u6027\u3001\u5f88\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u8fd9\u4e0d\u53eb\u4e2a\u6027\uff0c\u4e5f\u4e0d\u662f\u81ea\u91cf\uff0c\u8fd9\u662f\u6ca1\u6709\u81ea\u4fe1\uff0c\u8fd9\u53eb\u505a\u8822\uff01\u56fe\u4ee5\u6807\u65b0\u7acb\u5f02\uff0c\u81ea\u5efa\u5f62\u8c61\uff0c\u4e0d\u60dc\u628a\u4eba\u4e0e\u4eba\u4e4b\u95f4\u4e00\u5207\u539f\u6709\u800c\u8f83\u548c\u8c10\u7684\u4ea4\u5f80\u65b9\u5f0f\u6253\u7ffb\uff0c\u6765\u6807\u7acb\u81ea\u5df1\u7684\u4e0e\u4f17\u4e0d\u540c\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u53f9\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4e16\u4eba\u597d\u8bdd\u8bf4\u7684\u5927\u591a\uff0c\u5df2\u4e0d\u53d7\u91cd\u89c6\uff0c\u73b0\u5728\u7684\u4eba\u5df2\u4e0d\u559c\u6b22\u5fe0\u539a\u7684\u5f62\u8c61\uff0c\u90fd\u8981\u4e89\u7740\u5f53\u597d\u4eba\uff0c\u53cd\u800c\u4ee4\u4eba\u5370\u8c61\u96be\u5fd8\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u4e0d\u8fc7\u8fd9\u53ea\u662f\u53d8\uff0c\u4e0d\u662f\u5e38\u3002\u4e88\u4eba\u5370\u8c61\u6df1\u523b\uff0c\u4e0d\u4ee3\u8868\u5c31\u662f\u597d\u5370\u8c61\u3002\u522b\u4eba\u542c\u4e86\u8fd9\u4e9b\u5c16\u9178\u523b\u8584\u7684\u6d3b\uff0c\u597d\u50cf\u90fd\u5f88\u6b23\u8d4f\u3001\u8d5e\u7fa1\u4f60\u6709\u6027\u683c\uff0c\u5176\u5b9e\uff0c\u5fc3\u91cc\u53ea\u5728\u6697\u9a82\uff1a\u523b\u8584\u9b3c\uff01\u53c9\u4e4c\u5a46\u5a18\uff01\u8fd9\u53eb\u5f97\u4e0d\u507f\u5931\u3002\u5e38\u662f\u5e38\u6001\uff0c\u6c38\u6052\u4e5f\u8bb8\u662f\u53d8\u5e7b\u7684\uff0c\u4f46\u53d8\u5e7b\u6c38\u8fdc\u4e0d\u662f\u6c38\u6052\u3002\u201d \u3000\u3000\u201c\u6709\u7406\u3002\u201d\u4e03\u53d1\u7985\u5e08\u9053\uff0c\u201c\u4e00\u4e2a\u771f\u6b63\u6210\u529f\u7684\u4eba\uff0c\u4e00\u4e2a\u771f\u6b63\u6709\u4fee\u517b\u7684\u4eba\uff0c\u662f\u4e0d\u4f1a\u4e0e\u4eba\u5904\u5904\u4e89\u950b\uff0c\u5984\u901e\u53e3\u820c\u4e4b\u5229\u7684\u3002\u6807\u5947\u7acb\u5f02\u3001\u82e6\u5fc3\u5b64\u8be3\u6765\u7a81\u51fa\u81ea\u5df1\uff0c\u8bf4\u4e0d\u5b9a\u53ea\u662f\u81ea\u62c6\u957f\u57ce\u3001\u81ea\u6bc1\u5f62\u8c61\u3002\u8bed\u8a00\u523b\u8584\u5c16\u5229\uff0c\u53ea\u662f\u5c0f\u4eba\u7269\u6492\u8d56\u65f6\u7684\u5229\u5668\uff0c\u4f60\u51e0\u65f6\u770b\u8fc7\u771f\u6b63\u7684\u5927\u4eba\u7269\uff0c\u8eab\u8d1f\u91cd\u4efb\u3002\u8eab\u5c45\u9ad8\u4f4d\u7684\u65f6\u5019\uff0c\u8bf4\u8bdd\u8fd8\u5982\u6b64\u4e0d\u68c0\u70b9\u3001\u53ea\u6709\u5fae\u4e0d\u8db3\u9053\u800c\u53c8\u597d\u51fa\u950b\u5934\u7684\u4eba\uff0c\u624d\u4e0d\u60dc\u5728\u8a00\u8f9e\u4e0a\u62db\u62db\u62fc\u547d\uff0c\u53e5\u53e5\u4e0d\u60dc\u7389\u77f3\u4ff1\u711a\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u8bb8\u56e0\u4e3a\u4ed6\u662f\u77f3\uff0c\u4eba\u5bb6\u624d\u662f\u7389\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u4e5f\u8be1\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5728\u8fd9\u4e16\u4e0a\uff0c\u5f53\u5fe0\u7684\u5df2\u4e0d\u7a00\u5947\uff0c\u88ab\u4eba\u8ba4\u4e3a\u662f\u865a\u4f2a\u9020\u4f5c\uff0c\u5f53\u5978\u7684\u624d\u5f15\u4eba\u5f80\u610f\uff0c\u6240\u4ee5\u6211\u624d\u5f53\u5927\u5978\u5927\u6076\u4e4b\u5f92\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u53ea\u662f\uff0c\u8fd9\u5f15\u4eba\u6ce8\u76ee\u7684\u4ee3\u4ef7\u4e5f\u672a\u514d\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u4e03\u53d1\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u4eec\u4eca\u5929\u662f\u672a\u8fd9\u513f\u8c08\u5fe0\u8bba\u5978\u7684\u5566\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5fe0\u5978\uff0c\u4e16\u95f4\u7684\u4e00\u4e9b\u5bf9\u7acb\uff0c\u7686\u56e0\u5404\u6709\u7acb\u573a\u3002\u5404\u6709\u6240\u56fe\u3001\u5404\u4e3a\u5df1\u5229\u3001\u5404\u53f8\u5176\u804c\u800c\u5df2\uff0c\u50cf\u4eca\u5929\uff0c\u77f3\u5144\u662f\u2018\u5999\u624b\u5802\u2019\u56de\u5bb6\u7684\u5ea7\u4e0a\u9ad8\u624b\uff0c\u4e03\u53d1\u5927\u5e08\u662f\u2018\u5170\u4ead\u2019\u6c60\u5bb6\u6240\u501a\u91cd\u7684\u4eba\uff0c\u6211\u4e5f\u52a0\u5165\u4e86\u2018\u5343\u53f6\u5c71\u5e84\u2019\u845b\u5bb6\uff0c\u7acb\u573a\u4fbf\u90fd\u4e0d\u4e00\u6837\u4e86\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u6211\u4eec\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u4e5f\u5f97\u8981\u672a\u4e00\u573a\u5bf9\u51b3\u3002\u201d \u3000\u3000\u4e03\u53d1\u53f9\u9053\uff1a\u201c\u8521\u5c11\u4fa0\u6240\u8a00\u751a\u662f\u3002\u4e0a\u6b21\u6211\u4eec\u89c1\u9762\uff0c\u8fd8\u662f\u4e00\u540c\u9003\u907f\u90a3\u540d\u6355\u8ffd\u547d\u7684\u8ffd\u8e2a\uff0c\u76f8\u7ea6\u4e0d\u7ba1\u662f\u8c01\u5e72\u90a3\u5b97\u6848\u5b50\uff0c\u90fd\u8981\u8054\u624b\u9664\u53bb\u90a3\u8ba8\u538c\u7684\u6355\u5feb\uff0c\u2026\u2026\u4eca\u5929\u54b1\u4eec\u518d\u76f8\u89c1\uff0c\u5374\u662f\u5404\u4e8b\u5176\u4e3b\uff0c\u6562\u4e0d\u6210\u4e0b\u56de\u76f8\u89c1\uff0c\u54b1\u4eec\u8981\u52a8\u5200\u52a8\u67aa\u4e86\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u4e16\u4e8b\u672c\u5c31\u96be\u4ee5\u9006\u6599\uff0c\u4eca\u5929\u6211\u4eec\u4e09\u4eba\u805a\u5728\u8fd9\u513f\uff0c\u5f85\u4f1a\u513f\u7a76\u7adf\u6709\u51e0\u4eba\u80fd\u79bb\u5f00\u6b64\u5730\uff0c\u8fd8\u662f\u6b8a\u672a\u53ef\u77e5\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u4eca\u56de\u7684\u4e8b\uff0c\u672c\u5c31\u662f\u4e00\u573a\u9e3f\u95e8\u5bb4\uff0c\u8fd9\u513f\u4e5f\u6b63\u662f\u662f\u975e\u4e4b\u5730\u3002\u8c01\u77e5\u9053\u90a3\u4f4d\u540d\u6355\u6b64\u4e3e\u662f\u5565\u7528\u610f\uff1f\u4ed6\u67e5\u5230\u7ed3\u679c\u6ca1\u6709\uff1f\u4ed6\u6709\u6ca1\u6709\u67e5\u9519\uff1f\u8981\u662f\u67e5\u5bf9\u4e86\u4eba\uff0c\u51f6\u624b\u4f1a\u4e0d\u4f1a\u675f\u624b\u5c31\u64d2\uff1f\u8981\u662f\u67e5\u9519\u4e86\uff0c\u51a4\u6789\u597d\u4eba\uff0c\u5bf9\u65b9\u662f\u4e0d\u662f\u5c31\u6b64\u8ba4\u683d\uff1f\u770b\u6765\uff0c\u4eca\u5927\u7684\u4e8b\uff0c\u51b3\u65e0\u5584\u4e86\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u4eec\u66f4\u662f\u7ea6\u597d\u4e86\uff0c\u4e0d\u7ba1\u8ffd\u547d\u6253\u8c01\u7684\u9709\u5934\uff0c\u54b1\u4eec\u90fd\u7ad9\u5728\u540c\u4e00\u9635\u7ebf\uff0c\u5408\u529b\u5148\u628a\u4ed6\u9664\u53bb\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u5ffd\u9053\uff1a\u201c\u5c31\u7b97\u6ca1\u6709\u8fd9\u4e2a\u7ea6\u5b9a\uff0c\u6211\u4e5f\u5bb9\u4e0d\u5f97\u4ed6\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u5947\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u56e0\u4e3a\u845b\u5e84\u4e3b\u8981\u6211\u9664\u6389\u8fd9\u4e2a\u4eba\uff0c\u6211\u8eab\u5728\u845b\u5bb6\uff0c\u8fd9\u662f\u6211\u7b2c\u4e00\u4ef6\u4efb\u52a1\uff0c\u603b\u4e0d\u80fd\u4e0d\u529e\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u6c89\u601d\u9877\u523b\uff0c\u9053\uff1a\u201c\u5514\u3002\u5343\u53f6\u5c71\u5e84\u672c\u6765\u6709\u9274\u4e8e\u540e\u7ee7\u65e0\u7537\uff0c\u66fe\u6536\u517b\u4e86\u4e00\u540d\u4e49\u5b50\uff0c\u53eb\u845b\u7c89\u513f\uff0c\u7cbe\u64c5\u6613\u5bb9\uff0c\u4e0d\u5e72\u597d\u4e8b\uff0c\u7ec8\u5728\u2018\u9707\u5173\u4e1c\u2019\u4e4b\u5f79\uff0c\u4e3a\u8ffd\u547d\u7b49\u6240\u6355\uff0c\u4e0b\u5728\u7262\u91cc\uff0c\u8fc4\u4eca\u4ecd\u672a\u5f00\u91ca\uff0c\u60f3\u5fc5\u662f\u4e3a\u4e86\u6b64\u4e8b\uff0c\u845b\u94c3\u94c3\u5bf9\u8ffd\u547d\u7b49\u56db\u5927\u540d\u6355\u6068\u4e4b\u5165\u9aa8\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff0c\u6211\u4eec\u4e09\u4eba\uff0c\u4e0d\u4f46\u7ea6\u5b9a\u8981\u6740\u8ffd\u547d\uff0c\u800c\u4e14\u672c\u6765\u4e5f\u60f3\u6740\u6389\u8ffd\u547d\uff0c\u90a3\u662f\u5fd7\u540c\u9053\u5408\uff0c\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6740\u8ffd\u547d\uff0c\u662f\u5949\u547d\uff0c\u6211\u8ddf\u4f60\u9053\u4e0d\u540c\uff0c\u5fd7\u4e0d\u4e00\uff0c\u4f60\u6740\u4f60\u7684\u540d\u6355\uff0c\u6211\u6740\u6211\u7684\u8ffd\u547d\uff0c\u662f\u4e24\u4e0d\u76f8\u5e72\u7684\u4e8b\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u4e2a\u6027\uff0c\u4f60\u6709\u4e2a\u6027\u53c8\u53bb\u5f53\u5343\u53f6\u5c71\u5e84\u7684\u8d70\u72d7\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u63e1\u5251\u7684\u624b\u7a81\u7136\u7d27\u4e86\u3002\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\uff01\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u5374\u622a\u95ee\u65ad\u7709\u77f3\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u6740\u6389\u8ffd\u547d\uff1f\u201d \u3000\u3000\u201c\u4e03\u53d1\uff0c\u522b\u4eba\u4e0d\u77e5\u4f60\u7684\u5e95\u7ec6\uff0c\u6211\u5374\u6e05\u695a\u5f97\u5f88\uff0c\u201d\u65ad\u7709\u77f3\u8138\u4e0a\u4f3c\u7b11\u975e\u7b11\uff0c\u201c\u4e94\u53f0\u5c71\u591a\u6307\u5934\u9640\u5c31\u662f\u4f60\u7684\u5e08\u5144\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7684\u773c\u795e\u7a81\u7136\u71c3\u70e7\u4e86\u8d77\u6765\uff0c\u5410\u51fa\u4e24\u4e2a\u5b57\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u591a\u6307\u5934\u9640\u6709\u56db\u540d\u5f1f\u5b50\uff0c\u4ed6\u4eec\u5728\u6c5f\u6e56\u4e0a\u5916\u53f7\u4eba\u79f0\u2018\u98ce\u96e8\u96f7\u7535\u2019\uff0c\u8fd9\u56db\u4eba\u4e5f\u53ef\u4ee5\u7b97\u4f5c\u662f\u4f60\u7684\u5e08\u4f84\uff0c\u4f46\u4ed6\u4eec\u4e0d\u4f5c\u597d\u4e8b\uff0c\u8ddf\u6bd4\u76d7\u532a\u8fd8\u4e0d\u5982\u7684\u72d7\u5b98\u5434\u94c1\u7ffc\u72fc\u72c8\u4e3a\u597d\uff0c\u7ed3\u679c\uff0c\u867d\u7136\u4e0d\u80fd\u7b97\u662f\u6b7b\u5728\u8ffd\u547d\u7684\u624b\u91cc\uff0c\u4f46\u4e5f\u53ef\u4ee5\u7b97\u662f\u8ffd\u547d\u95f4\u63a5\u4f7f\u4ed6\u4eec\u6b7b\u4e8e\u975e\u547d\u7684\uff1b\u201d\u65ad\u7709\u77f3\u659c\u7740\u5c0f\u773c\uff0c\u9488\u4e00\u822c\u7684\u523a\u7740\u4e03\u53d1\u5927\u5e08\uff0c\u201c\u4f60\u6ca1\u6709\u7406\u7531\u4e0d\u751f\u6c14\u3002\u5c31\u662f\u56e0\u4e3a\u4f60\u4eec\u90fd\u60f3\u6740\u8ffd\u547d\uff0c\u4eca\u5929\u6211\u624d\u4f1a\u6765\u3002\u53ea\u4e0d\u8fc7\uff0c\u6211\u6bd4\u4f60\u8001\u5b9e\u4e00\u4e9b\uff0c\u6211\u60f3\u6740\u4eba\uff0c\u5c31\u6562\u627f\u8ba4\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u8ffd\u547d\u4eb2\u624b\u6740\u6b7b\u2018\u98ce\u96e8\u96f7\u7535\u2019\uff0c\u53ea\u8981\u4ed6\u4eec\u8be5\u6b7b\uff0c\u8d2b\u50e7\u4e5f\u65e0\u602a\u8d23\u4e4b\u610f\uff1b\u201d\u4e03\u53d1\u5927\u5e08\u9053\uff0c\u201c\u8c01\u8bf4\u6211\u5c31\u4e3a\u6b64\u975e\u6740\u8ffd\u547d\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u4e00\u6014\u3002 \u3000\u3000\u8521\u65cb\u949f\u5ffd\u9053\uff1a\u201c\u4e03\u53d1\uff0c\u6211\u4e00\u76f4\u89c9\u5f97\u4f60\u662f\u4e24\u79cd\u4eba\u7684\u5176\u4e2d\u4e00\u79cd\uff0c\u4f46\u4e00\u76f4\u4e0d\u80fd\u80af\u5b9a\u4f60\u662f\u90a3\u4e00\u79cd\u4eba\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5b89\u8be6\u5730\u9053\uff1a\u201c\u8d2b\u50e7\u5fae\u4e0d\u8db3\u9053\uff0c\u4e0d\u503c\u5c11\u4fa0\u591a\u8d39\u601d\u91cf\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u76f4\u89c6\u4e03\u53d1\u7985\u5e08\uff1a\u201c\u5982\u679c\u4f60\u4e0d\u662f\u4e2a\u5fe0\u539a\u8001\u5b9e\u7684\u5927\u597d\u4eba\uff0c\u4f60\u5c31\u662f\u4e2a\u5927\u597d\u5927\u6076\u7684\u4eba\uff0c\u8981\u6bd4\u6211\u4eec\u4e24\u4e2a\u90fd\u5351\u9119\u9634\u9669\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u5fd9\u4e0d\u8fed\u63a5\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u662f\u7b2c\u4e8c\u79cd\u4eba\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u795e\u8272\u4e0d\u53d8\uff0c\u6148\u548c\u5730\u9053\uff0c\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u8d2b\u50e7\u53ea\u662f\u51fa\u5bb6\u4eba\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u51fa\u5bb6\u4eba\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u8ffd\u547d\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u5947\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u4ed6\u679c\u7136\u6765\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e00\u53f6\u6241\u821f\uff0c\u5212\u6c34\u800c\u6765\u3002 \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u9664\u4ed6\u4ee5\u5916\uff0c\u8fd8\u6709\u987e\u4f5b\u5f71\u3002\u201d\u4ed6\u6c89\u58f0\u9053\uff0c\u201c\u5c0f\u78a7\u6e56\u6e38\u5bb6\u5bf9\u5b5f\u592a\u5b88\u7684\u6848\u5b50\uff0c\u7acb\u573a\u4e00\u76f4\u90fd\u5341\u5206\u6696\u6627\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u987e\u4f5b\u5f71\u662f\u8fd9\u513f\u7684\u4e3b\u7ba1\uff0c\u4ed6\u6765\u8fd8\u4e0d\u7b97\u610f\u5916\uff0c\u4f55\u51b5\u4ed6\u8fd8\u8981\u5e26\u8ffd\u547d\u8fc7\u6765\u76f8\u601d\u4ead\uff0c\u201d\u73b0\u5728\u4e09\u4eba\u90fd\u7ad9\u5728\u4e00\u8d77\uff0c\u9762\u5411\u78a7\u6e56\uff0c\u201c\u4f46\u821f\u4e0a\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u4ed6\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u53ea\u6015\u662f\u4e00\u4e2a\u6211\u4eec\u90fd\u4e0d\u8ba4\u8bc6\u7684\u4eba\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u4e0d\u7ba1\u8fd9\u4eba\u662f\u987e\u795e\u98ce\u5e26\u6765\u7684\uff0c\u8fd8\u662f\u8ffd\u547d\u5e26\u6765\u7684\uff0c\u53ea\u8981\u5728\u8fd9\u65f6\u5019\u51fa\u73b0\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u91cd\u8981\u7684\u4eba\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u53ea\u6015\uff0c\u8ddf\u8fd9\u4ef6\u6848\u5b50\u4e0d\u591a\u4e0d\u5c11\u90fd\u4f1a\u6709\u4e9b\u5173\u7cfb\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u73b0\u5728\uff0c\u771f\u6b63\u6740\u6b7b\u5b5f\u968f\u56ed\u5168\u5bb6\u5927\u5c0f\u7684\u4eba\uff0c\u7406\u5e94\u62c5\u5fc3\u624d\u662f\uff0c\u8d2b\u50e7\u5374\u4e0d\u62c5\u5fc3\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u4f60\u522b\u7f6e\u8eab\u4e8b\u5916\u3002\u4eca\u5929\u6765\u5230\u76f8\u601d\u4ead\u7684\uff0c\u6050\u6015\u90fd\u662f\u6ca1\u6709\u8d44\u683c\u7f6e\u8eab\u4e8b\u5916\u7684\u4eba\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u5ffd\u8212\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd8\u597d\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u53ea\u8981\u4e0d\u662f\u65b9\u90aa\u771f\uff0c\u90a3\u5c31\u4e0d\u8db3\u4e3a\u60a3\u4e86\uff0c\u8981\u4e0d\u7136\uff0c\u8ffd\u547d\u4e0e\u65b9\u90aa\u771f\u8054\u624b\uff0c\u8fd9\u9635\u5bb9\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u7528\u98df\u3001\u4e2d\u4e8c\u6307\uff0c\u8f7b\u629a\u4f69\u5251\uff0c\u5ffd\u95ee\uff1a\u201c\u542c\u8bf4\u65b9\u90aa\u771f\u4e5f\u662f\u7528\u5251\u7684\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u9a6c\u4e0a\u9053\uff1a\u201c\u800c\u4e14\u662f\u4e00\u628a\u540d\u5251\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u540d\u5251\u4e0d\u4e00\u5b9a\u5c31\u662f\u597d\u5251\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u5373\u9053\uff1a\u201c\u4f46\u4ed6\u90a3\u628a\u65e2\u662f\u540d\u5251\uff0c\u4e5f\u662f\u597d\u5251\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u7b11\u9053\uff1a\u201c\u6709\u4e00\u67c4\u540d\u5251\uff0c\u63e1\u4e00\u628a\u597d\u5251\uff0c\u4f46\u6ca1\u6709\u597d\u5251\u6cd5\uff0c\u4e5f\u65e0\u5f02\u4e8e\u5e9f\u94c1\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u5fd9\u9053\uff1a\u201c\u4ed6\u7684\u5251\u6cd5\u5982\u679c\u4e0d\u597d\uff0c\u90a3\u628a\u5251\u65e9\u5c31\u662f\u6211\u7684\u4e86\uff0c\u53c8\u600e\u4f24\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u7d27\u63e1\u7740\u5251\u8eab\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u76f8\u6fc0\u3002\u4f60\u4f24\u5728\u65b9\u90aa\u771f\u7684\u5251\u4e0b\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u662f\u56e0\u4e3a\u4f60\u7684\u6b66\u529f\u592a\u5dee\u4e4b\u6545\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u957f\u5438\u4e00\u53e3\u6c14\uff0c\u8be1\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u6fc0\u6211\uff0c\u5927\u654c\u5f53\u524d\uff0c\u54b1\u4eec\u4e0d\u5fc5\u5148\u6765\u5507\u67aa\u820c\u5251\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8bf4\u7740\u7684\u65f6\u5019\uff0c\u821f\u5b50\u5df2\u9760\u5cb8\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u5f53\u5148\u5f15\u8def\uff0c\u548c\u548c\u6c14\u6c14\u7684\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u4e2a\u4e39\u51e4\u773c\u3001\u7d2b\u819b\u8138\u3001\u957f\u987b\u53ca\u80f8\u3001\u76f8\u8c8c\u5802\u5802\u7684\u4eba\uff0c\u8d70\u5728\u4e2d\u95f4\uff0c\u800c\u8ffd\u547d\u4f9d\u7136\u62ab\u53d1\u6d12\u978b\uff0c\u8d70\u5728\u6700\u540e\u3002 \u3000\u3000\u770b\u4ed6\u4eec\u7684\u795e\u6001\uff0c\u4eff\u4f5b\u662f\u6765\u8d74\u5bb4\uff0c\u559d\u9152\u804a\u5929\uff0c\u800c\u4e0d\u662f\u6765\u8d74\u6218\uff0c\u67e5\u529e\u51f6\u624b\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u8d70\u8fd1\uff0c\u5411\u4e03\u53d1\u3001\u8521\u65cb\u949f\u3002\u65ad\u7709\u77f3\u4e09\u4eba\u4e00\u62f1\u624b\u9053\uff1a\u201c\u4e09\u4f4d\u4e45\u5019\u4e86\u3002\u201d\u9042\u5411\u8ffd\u547d\u4e00\u5f15\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u540d\u6355\u8ffd\u547d\uff0c\u770b\u6765\u4e0d\u5fc5\u6211\u591a\u4f5c\u4ecb\u7ecd\u4e86\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u51b7\u54fc\u9053\uff1a\u201c\u8fd9\u4e00\u8def\u6765\uff0c\u4ed6\u90fd\u5728\u8ffd\u6211\u4eec\u8981\u547d\uff0c\u6211\u4eec\u7b97\u662f\u8001\u76f8\u597d\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u4e00\u7b11\u9053\uff1a\u201c\u5b5f\u5bb6\u4e09\u5341\u516d\u6761\u4eba\u547d\uff0c\u5728\u68a6\u91cc\u8ffd\u7740\u6211\u627e\u51f6\u624b\u7d22\u547d\uff0c\u6211\u53ea\u597d\u5728\u9192\u7740\u7684\u65f6\u5019\u8ffd\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u4e00\u5f00\u53e3\u5c31\u5207\u5165\u4e3b\u9898\uff0c\u8521\u65cb\u949f\u7acb\u5373\u53cd\u95ee\u4e00\u53e5\uff1a\u201c\u4f60\u8bf4\u6211\u4eec\u90fd\u662f\u6740\u5b5f\u592a\u5b88\u7684\u51f6\u624b\uff1f\u6709\u4f55\u8bc1\u636e\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u5728\u4e00\u65c1\u7b11\u7740\uff0c\u6b64\u65f6\u5ffd\u7136\u622a\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u6211\u5df2\u5907\u597d\u4e86\u9152\u83dc\uff0c\u201d\u4ed6\u62cd\u4e86\u4e24\u4e0b\u624b\u638c\uff0c\u5373\u6709\u5bb6\u4ec6\u81ea\u76f8\u601d\u6797\u9c7c\u8d2f\u8d70\u51fa\uff0c\u6311\u6765\u4e86\u51e0\u4e2a\u5927\u7af9\u7bee\u3001\u56db\u4e2a\u5927\u9152\u575b\uff0c\u6ea2\u51fa\u9152\u83dc\u9999\u5473\uff1b\u4ec6\u5f79\u6446\u597d\u7897\u7b77\u676f\u789f\uff0c\u7136\u540e\u9010\u4e00\u9000\u53bb\u3002\u201c\u8bf8\u4f4d\u8981\u529e\u6848\u4e4b\u524d\uff0c\u5148\u7528\u9152\u83dc\uff0c\u8fd8\u662f\u5728\u529e\u6848\u4e4b\u540e\uff0c\u624d\u6765\u5403\u559d\uff1f\u201d \u3000\u3000\u63a5\u7740\u5411\u4e03\u53d1\u5927\u5e08\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u516c\u5b50\u4e5f\u7279\u522b\u4e3a\u5927\u5e08\u51c6\u5907\u4e86\u51e0\u8272\u7d20\u83dc\u3002\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u5408\u4ec0\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u548c\u5c1a\uff0c\u662f\u4e0d\u5fcc\u8364\uff0c\u4e0d\u907f\u8272\u7684\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u4ee5\u624b\u8f7b\u53e9\u989d\u89d2\u9053\uff1a\u201c\u54ce\u5537\uff0c\u5927\u5e08\u8d85\u51e1\u5165\u5723\uff0c\u53cd\u800c\u4e0d\u907f\u5fcc\u3001\u4e0d\u53d7\u6212\uff0c\u6211\u5012\u5fd8\u4e86\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u9053\uff1a\u201c\u5176\u5b9e\u53ea\u8981\u5fc3\u4e2d\u65e0\u5ff5\uff0c\u5929\u4e0b\u53c8\u4f55\u5c1d\u6709\u7269\uff1f\u5982\u679c\u5fc3\u4e2d\u8d77\u5ff5\uff0c\u53ea\u4f5c\u8eab\u5916\u7981\u5236\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff0c\u4fd7\u4e16\u7981\u5fcc\uff0c\u539f\u5c5e\u65e0\u804a\uff0c\u5927\u5e08\u8bf7\u968f\u4fbf\u5403\u7528\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u53f9\u9053\uff1a\u201c\u53ea\u662f\u8840\u6848\u672a\u7834\uff0c\u53c8\u6709\u8c01\u80fd\u5403\u5f97\u4e0b\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u5c31\u7b97\u5de8\u6848\u4fa6\u7834\uff0c\u8fd9\u513f\u8fd8\u5269\u4e0b\u51e0\u4eba\u80fd\u5403\u5f97\u4e0b\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u6240\u4ee5\u65e0\u8bba\u6848\u5b50\u7834\u4e0d\u7834\uff0c\u6211\u4eec\u90fd\u5403\u4e0d\u4e0b\uff0c\u6211\u4eec\u662f\u6765\u8d74\u4f1a\u7684\uff0c\u4e0d\u662f\u6765\u5403\u5403\u559d\u559d\u7684\u3002\u201d \u3000\u3000\u8ffd\u547d\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u8bf4\u7684\u597d\u3002\u529e\u6848\u56fa\u7136\u8981\u7d27\uff0c\u4f46\u662f\uff0c\u653e\u7740\u7f8e\u9152\u4e0d\u559d\uff0c\u5c82\u662f\u5728\u4e0b\u6240\u4e3a\uff01\u201d\u8bf4\u7740\u4e00\u811a\u8e22\u7834\uff0c\u4e00\u575b\u9152\uff0c\u8c41\u7405\u4e00\u58f0\uff0c\u74f7\u788e\u9152\u6ea2\uff0c\u8ffd\u547d\u6367\u62b1\u7740\u4ef0\u9879\u5495\u565c\u565c\u5730\u9cb8\u541e\u7740\uff0c\u9152\u9999\u56db\u6ea2\uff0c\u9152\u6cc9\u76f4\u704c\uff0c\u8ffd\u547d\u8116\u5b50\u3001\u8863\u4e0a\u90fd\u4e3a\u9152\u6240\u6e7f\u3002 \u3000\u3000\u8ffd\u547d\u4e00\u62b9\u5634\u9053\uff1a\u201c\u597d\u9152\uff0c\u597d\u9152\u3002\u201d\u53c8\u518d\u75db\u996e\u3002 \u3000\u3000\u4e03\u53d1\u7985\u5e08\u4f4e\u58f0\u9053\uff1a\u201c\u8ffd\u547d\u5411\u6765\u662f\u9152\u559d\u5f97\u8d8a\u591a\uff0c\u6b66\u529f\u8d8a\u80fd\u53d1\u6325\uff0c\u770b\u6765\uff0c\u4eca\u756a\u4ed6\u662f\u8981\u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u8138\u8272\u5fae\u5fae\u4e00\u53d8\u3002 \u3000\u3000\u8521\u65cb\u949f\u5374\u5927\u6b65\u884c\u51fa\uff0c\u62b1\u8d77\u5176\u4e2d\u4e00\u4e2a\u9152\u575b\uff0c\u7528\u624b\u6307\u5728\u9152\u575b\u4e0a\u8f7b\u8f7b\u4e00\u64ae\uff0c\u5c31\u62ce\u8d77\u4e86\u4e00\u5757\u9676\u7247\uff0c\u5c31\u50cf\u9152\u575b\u662f\u7eb8\u7cca\u7684\u4e00\u822c\uff0c\u9152\u6cc9\u9a6c\u4e0a\u4ece\u7834\u6d1e\u6ea2\u51fa\u6765\uff0c\u8521\u65cb\u949f\u4e5f\u51d1\u5634\u4e0b\u53bb\uff0c\u731b\u541e\u4e86\u51e0\u53e3\uff0c\u6b47\u4e00\u6b47\uff0c\u9053\uff1a\u201c\u597d\u9152\uff01\u6211\u966a\u4f60\u559d\uff01\u201d \u3000\u3000\u8ffd\u547d\u513f\u53e3\u70c8\u9152\u4e0b\u809a\uff0c\u6b63\u662f\u6696\u6d0b\u6d0b\u7684\u5341\u5206\u8212\u670d\uff0c\u4e00\u89c1\u6709\u4eba\u966a\u996e\uff0c\u53c8\u731b\u559d\u4e86\u51e0\u53e3\uff0c\u8c6a\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\u9152\uff01\u597d\u9152\u91cf\uff01\u597d\u4e2a\u2018\u7834\u4f53\u65e0\u5f62\u5251\u6c14'\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u4e00\u51fa\uff0c\u4e0d\u4f46\u65ad\u7709\u77f3\u60ca\uff0c\u4e03\u53d1\u5927\u5e08\u5947\uff0c\u8fde\u987e\u4f5b\u5f71\u4e5f\u611f\u8be7\u5f02\u3002 \u3000\u3000\u8521\u65cb\u949f\u8138\u8272\u4e5f\u53d8\u4e86\u53d8\u3002 \u3000 \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u53ea\u51b3\u6597\uff0c\u4e0d\u6740\u4eba \u3000 \u3000\u3000\u62cd\u788e\u9152\u575b\uff0c\u672c\u6765\u5c31\u4e0d\u662f\u4e00\u4ef6\u96be\u4e8b\u3002 \u3000\u3000\u5c31\u7b97\u4e0d\u66fe\u7ec3\u8fc7\u6b66\u7684\u4eba\uff0c\u4e5f\u53ef\u4ee5\u62f3\u6216\u811a\uff0c\u51fb\u788e\u9152\u575b\u3002 \u3000\u3000\u53ef\u662f\u8521\u65cb\u949f\u53ea\u7528\u4e24\u53ea\u624b\u6307\uff0c\u5728\u9152\u575b\u4e2d\u8f7b\u8f7b\u4e00\u62c8\uff0c\u535c\u7684\u4e00\u58f0\uff0c\u5c31\u62ce\u8d77\u4e86\u4e00\u5757\u9676\u7247\uff0c\u7834\u53e3\u5904\u51fa\u73b0\u4e00\u4e2a\u5b8c\u6574\u7684\u5706\u5b54\uff0c\u8fd9\u79cd\u529f\u529b\u4fee\u4e3a\u5c31\u975e\u540c\u5c0f\u53ef\u4e86\u3002 \u3000\u3000\u8ffd\u547d\u4e00\u76f4\u90fd\u5728\u559d\u9152\uff0c\u6ca1\u6709\u770b\u4ed6\u3002 \u3000\u3000\u9152\u575b\u51e0\u4e4e\u5b8c\u5168\u906e\u76d6\u4e86\u8ffd\u547d\u7684\u8138\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8521\u65cb\u949f\u53cc\u6307\u6316\u51fa\u9676\u7247\u4e4b\u540e\uff0c\u8ffd\u547d\u5c31\u558a\u51fa\u4e86\u201c\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u201d\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u8521\u65cb\u949f\u52a8\u7684\u662f\u624b\u6307\u3002 \u3000\u3000\u8ffd\u547d\u5374\u9a6c\u4e0a\u611f\u89c9\u5230\u5251\u6c14\u3002 \u3000\u3000\u800c\u4e14\u7acb\u5373\u5224\u65ad\u51fa\u8fd9\u5c31\u662f\u5931\u4f20\u591a\u5e74\u3001\u540d\u9707\u5929\u4e0b\u7684\u201c\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u201d\u3002 \u3000\u3000\u8521\u65cb\u949f\u8fd0\u52b2\u5728\u6307\uff0c\u5185\u529b\u5df2\u8fbe\u4f69\u5251\uff0c\u867d\u7136\u53ea\u662f\u4e00\u4e2a\u6781\u7ec6\u5fae\u7684\u52a8\u4f5c\uff0c\u5df2\u6559\u8ffd\u547d\u770b\u7834\u4e86\u4ed6\u7684\u6b66\u529f\u5bb6\u6570\u3002 \u3000\u3000\u201c\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u201d\u4e00\u51fa\u53e3\uff0c\u987e\u4f5b\u5f71\u3001\u65ad\u7709\u77f3\u3001\u4e03\u53d1\u5927\u5e08\u4e00\u9f50\u5fc3\u4e2d\u6697\u9707\u3002 \u3000\u3000\u201c\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u201d\u5df2\u6210\u4e86\u4f20\u8bf4\u91cc\u7684\u6b66\u529f\uff0c\u8fd1\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u53ea\u6709\u4e00\u4eba\u80fd\u7ec3\u6210\u8fd9\u79cd\u7edd\u4e16\u7684\u6b66\u529f\uff0c\u8fd9\u4eba\u4fbf\u662f\u201c\u8ff7\u5929\u4e03\u5723\u201d\u4e4b\u9886\u8896\u5173\u4e03\u3002\u5173\u4e03\u7684\u4e00\u751f\uff0c\u5df2\u6210\u4e86\u4f20\u5947\uff0c\u4ed6\u7684\u6b66\u529f\uff0c\u66f4\u6210\u4e86\u4f20\u5947\u91cc\u7684\u4f20\u5947\u3002 \u3000\u3000\u8ddf\u524d\u8fd9\u4e2a\u521d\u5d1b\u6c5f\u6e56\u7684\u4eba\uff0c\u7adf\u7136\u7ec3\u6210\u4e86\u201c\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u201d\uff01 \u3000\u3000\u987e\u4f5b\u5f71\u3001\u65ad\u7709\u77f3\u3001\u4e03\u53d1\u5927\u5e08\u4e00\u542c\u4e4b\u4e0b\uff0c\u96be\u514d\u90fd\u60f3\u8d77\u6614\u5e74\u540d\u9707\u5929\u4e0b\u7684\u51e0\u573a\u5927\u6218\u5f79\uff1a\u201c\u68a6\u6795\u7ea2\u8896\u7b2c\u4e00\u5200\u201d\u7684\u82cf\u68a6\u6795\u529b\u6218\u201c\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u201d\uff0c\u201c\u516d\u5206\u534a\u5802\u201d\u603b\u5802\u4e3b\u96f7\u635f\u4ee5\u201c\u5feb\u6162\u4e5d\u5b57\u8bc0\u6cd5\u201d\u82e6\u6597\u5173\u4e03\uff0c\u738b\u5c0f\u77f3\u4ee5\u201c\u51cc\u7a7a\u9500\u9b42\u5251\u201d\u4e0e\u201c\u9694\u7a7a\u76f8\u601d\u5200\u201d\u529b\u62fc\u201c\u8ff7\u5929\u4e03\u5723\u201d\u7684\u5173\u4e03\u5723\uff0c\u767d\u6101\u98de\u4ee5\u201c\u4e09\u6307\u5f39\u5929\u201d\u51b3\u6218\u5173\u4e03\u7684\u65e0\u5f62\u5251\u6c14\uff0c\u5168\u90fd\u662f\u6cb8\u52a8\u6c5f\u6e56\u3001\u4ee4\u4eba\u60ca\u5fc3\u52a8\u9b44\u3001\u4e5f\u7709\u98de\u8272\u821e\u7684\u6218\u5f79\uff0c\u4f46\u8fd9\u4e9b\u6218\u5f79\uff0c\u65e0\u4e00\u4e0d\u8ddf\u5173\u4e03\u6709\u5173\uff0c\u65e0\u4e00\u4e0d\u662f\u201c\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u5ffd\u7136\u4e00\u50f5\u3002 \u3000\u3000\u4ed6\u53ea\u7528\u53cc\u6307\u6316\u7834\u4e86\u9152\u575b\uff0c\u5c31\u8ba9\u8ffd\u547d\u77a7\u51fa\u4e86\u6b66\u529f\u6839\u5e95\uff0c\u8fd9\u662f\u4ed6\u6240\u59cb\u6599\u672a\u53ca\u7684\u4e8b\u3002 \u3000\u3000\u201c\u5982\u679c\u7528\u2018\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u2019\u6765\u6740\u5b5f\u968f\u56ed\u4e00\u5bb6\u4e09\u5341\u516d\u53e3\uff0c\u90a3\u662f\u4ef6\u80dc\u4efb\u6709\u4f59\u7684\u4e8b\uff0c\u201d\u8ffd\u547d\u659c\u76ef\u7740\u8521\u65cb\u949f\u8170\u7554\u957f\u5251\uff0c\u9053\uff0c\u201c\u4f55\u51b5\uff0c\u4f60\u8fd8\u6709\u4e00\u628a\u597d\u5251\uff0c\u4ee5\u4e5d\u5c3a\u516d\u5bf8\u7684\u2018\u8f6c\u9b44\u795e\u5251\u2019\u4f7f\u65e0\u5f62\u5251\u6c14\uff0c\u5c31\u7b97\u5b5f\u968f\u56ed\u7684\u2018\u843d\u82b1\u5f71\u5251\u2019\uff0c\u4e5f\u5fc5\u8d25\u65e0\u7591\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6211\u4f1a\u2018\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u2019\uff0c\u4e5f\u6709\u2018\u8f6c\u9b44\u795e\u5251\u2019\uff0c\u4f46\u4e0d\u89c1\u5f97\u6211\u5c31\u662f\u6740\u6b7b\u5b5f\u968f\u56ed\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4e09\u5e74\u524d\uff0c\u2018\u5200\u67c4\u4f1a\u2019\u7684\u5916\u4e09\u5802\u4e3b\u2018\u4e0d\u6b7b\u94dc\u4eba\u2019\u4e03\u91d1\u725b\u5315\u8001\u5927\u7237\uff0c\u662f\u4e0d\u662f\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u5315\u91d1\u725b\u4f5c\u6076\u591a\u7aef\uff0c\u4f46\u53c8\u5047\u4ec1\u5047\u4e49\uff0c\u8981\u4e0d\u662f\u4ed6\u6b7b\u4e86\uff0c\u8c01\u4f1a\u731c\u5f97\u5230\u4ed6\u5bb6\u91cc\u7adf\u662f\u4e2a\u85cf\u8d43\u7a9d\uff0c\u6bcf\u5e74\u81f3\u5c11\u76d7\u7528\u4e86\u4e09\u5341\u4e07\u4e24\u516c\u6b3e\uff0c\u8fd8\u56da\u7981\u4e86\u591a\u5c11\u826f\u5bb6\u5987\u5973\uff0c\u4f9b\u4ed6\u6deb\u8fb1\uff01\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4e24\u5e74\u524d\uff0c\u2018\u5bcc\u8d35\u4e4b\u5bb6\u2019\u7684\u5927\u5f53\u5bb6\u2018\u98de\u9524\u91d1\u94b5\u2019\u5e2d\u79cb\u91ce\uff0c\u5728\u64c2\u53f0\u4f1a\u7fa4\u82f1\u8fde\u6218\u4e8c\u5341\u516d\u573a\u540e\uff0c\u662f\u4e0d\u662f\u4f60\u4e0a\u53f0\u53bb\u628a\u4ed6\u51fb\u8d25\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u4ed6\u90a3\u79cd\u4e09\u811a\u732b\u529f\u592b\uff0c\u4e5f\u914d\u79f0\u9738\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4e00\u5e74\u524d\uff0c\u8352\u5c71\u9053\u4eba\u88ab\u6740\u4e8e\u9655\u897f\u9053\u4e0a\uff0c\u4ed6\u5916\u53f7\u2018\u516d\u5408\u9752\u9f99\u3001\u4e00\u5251\u64ce\u5927\u2019\uff0c\u53ef\u662f\u4e00\u6837\u654c\u4e0d\u8fc7\u4f60\u7684\u2018\u4e5d\u4e03\u5927\u9650\u795e\u5251\u201d\u3002\u201d \u3000\u3000\u201c\u6211\u4f7f\u7684\u662f\u4e5d\u5c3a\u4e03\u5bf8\u7684\u2018\u8f6c\u9b44\u795e\u5251\u201d\uff0c\u201c\u5927\u9650\u201d\u4e8c\u5b57\u662f\u6307\u79e6\u671d\u8986\u706d\u7684\u5371\u673a\uff0c\u73b0\u5728\u4e0d\u662f\u79e6\u4ee3\uff0c\u4fbf\u4e0d\u8be5\u7528\u2018\u5927\u9650\u201d\u4e8c\u5b57\u3002\u6211\u4f7f\u7684\u662f\u2018\u4e5d\u4e03\u5251\u6cd5'\uff0c\u517c\u4fee\u2018\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u2019\uff0c\u8352\u5c71\u9053\u4eba\u8981\u4ee5\u4ed6\u7684\u2018\u516d\u5408\u9752\u9f99\u64ce\u5929\u5251\u201d\u548c\u6211\u6bd4\u8bd5\uff0c\u6211\u539f\u8bf4\u4e0d\u5fc5\uff0c\u4ed6\u575a\u6301\uff0c\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u7ed3\u679c\uff0c\u4ed6\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u70b9\u70b9\u5934\u9053\uff0c\u201c\u4e0d\u7ba1\u662f\u4f60\u8fd8\u662f\u8352\u5c71\u9053\u4eba\uff0c\u4e00\u65e6\u5168\u529b\u6bd4\u62fc\uff0c\u5c31\u5f88\u96be\u7559\u5f97\u4f4f\u6027\u547d\uff0c\u56e0\u4e3a\u4f60\u4eec\u7684\u5251\u6cd5\uff0c\u4e00\u65e6\u88ab\u6fc0\u53d1\uff0c\u53ea\u6015\u53ef\u53d1\u96be\u6536\u3002\u4e0d\u89c1\u8840\u662f\u96be\u4ee5\u56de\u9798\u7684\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u5982\u679c\u6211\u8d25\u4e86\uff0c\u4e5f\u5f97\u6b7b\u3002\u201d \u3000\u3000\u8ffd\u547d\u53f9\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u53c8\u4f55\u82e6\u5462\uff1f\u4e3a\u4e89\u80dc\u8d1f\uff0c\u800c\u62da\u751f\u6b7b\uff01\u4e0d\u8fc7\uff0c\u4f60\u4eec\u4e3a\u4e00\u8f83\u9ad8\u4e0b\u800c\u62fc\u547d\uff0c\u8fd9\u662f\u6c5f\u6e56\u4e0a\u7684\u4e8b\uff1b\u6b66\u6797\u4e2d\u7684\u6069\u6028\uff0c\u6211\u53ef\u7ba1\u4e0d\u4e86\uff01\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u4e16\u4e0a\u6709\u5f88\u591a\u4e8b\uff0c\u4f60\u53ef\u80fd\u770b\u4e0d\u987a\u773c\uff0c\u4f46\u90fd\u672a\u5fc5\u80fd\u7ba1\u5f97\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5b5f\u592a\u5b88\u7684\u706d\u95e8\u8840\u6848\uff0c\u6211\u5374\u7ba1\u5f97\u4e86\uff0c\u800c\u4e14\u7ba1\u5b9a\u4e86\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u6211\u6740\u4e86\u5315\u8001\u592a\u7237\uff0c\u51fb\u8d25\u5e2d\u79cb\u91ce\u3002\u4e0e\u8352\u5c71\u9053\u4eba\u4e00\u51b3\u751f\u6b7b\uff0c\u4e0d\u7b49\u4e8e\u6211\u5c31\u662f\u6740\u6b7b\u5b5f\u968f\u56ed\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u5b5f\u968f\u56ed\u521a\u5ec9\u5b88\u6b63\uff0c\u5f97\u7f6a\u4e86\u5f53\u671d\u6743\u5ba6\uff0c\u7f79\u4ee5\u91cd\u7f6a\uff0c\u5168\u5bb6\u53d1\u914d\u6d82\u58c1\uff0c\u8981\u5230\u6d82\u58c1\uff0c\u5148\u7ecf\u6d1b\u9633\uff0c\u542c\u8bf4\u6d1b\u9633\u56db\u5927\u4e16\u5bb6\u4e2d\u6709\u4eba\u82b1\u4e86\u4e00\u5927\u7b14\u94f6\u5b50\uff0c\u4e70\u4e86\u4ed6\u7684\u5ea6\u7252\uff0c\u4f7f\u4ed6\u80fd\u5728\u6d1b\u9633\u57ce\u91cc\u9752\u83b2\u5bfa\u51fa\u5bb6\uff0c\u53ef\u60dc\uff0c\u4ed6\u5728\u79bb\u6d1b\u9633\u4e0d\u5230\u4e03\u5341\u91cc\u7684\u67af\u67f3\u5c6f\u88ab\u4eba\u6740\u5bb3\uff0c\u5927\u6982\u662f\u5341\u5929\u524d\u53d1\u751f\u7684\u4e8b\uff0c\u8bf7\u95ee\uff0c\u90a3\u65f6\u5019\uff0c\u4f60\u4eba\u5728\u54ea\u91cc\uff0c \u3000\u3000\u201c\u67af\u67f3\u5c6f\u3002\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u53ef\u662f\u6211\u5728\u67af\u67f3\u5c6f\uff0c\u4e5f\u4e0d\u7b49\u4e8e\u6211\u5c31\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4e03\u53d1\u5927\u5e08\u548c\u77f3\u8001\u5e7a\uff0c\u4e5f\u5728\u67af\u67f3\u5c6f\u3002\u201d \u3000\u3000\u65ad\u7709\u77f3\u9053\uff1a\u201c\u6211\u5bf9\u9601\u4e0b\u4f55\u4ee5\u80af\u5b9a\u6211\u4eec\u5728\u67af\u67f3\u5c6f\uff0c\u5012\u662f\u9887\u611f\u5174\u8da3\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5b5f\u592a\u5b88\u88ab\u5145\u519b\u6d41\u653e\uff0c\u6211\u56e0\u6015\u8fd8\u662f\u6709\u4eba\u4e0d\u653e\u8fc7\u4ed6\uff0c\u6240\u4ee5\u5df2\u5728\u4e00\u8def\u4e0a\u6697\u4e2d\u62a4\u9001\uff0c\u4e0d\u6599\u2026\u2026\u8fd8\u662f\u51fa\u4e86\u8fd9\u8840\u7978\uff0c\u6211\u5230\u8fdf\u4e00\u6b65\uff0c\u867d\u7136\u60e8\u7978\u5df2\u751f\uff0c\u4f46\u6bd5\u7adf\u4ecd\u53ef\u4eb2\u773c\u76ee\u7779\u4f60\u4eec\u4e09\u4f4d\uff0c\u79bb\u5f00\u67af\u67f3\u5c6f\u3002\u201d \u3000\u3000\u77f3\u65ad\u7709\u9053\uff1a\u201c\u90a3\u4f60\u5f53\u65f6\u4e3a\u4f55\u53c8\u4e0d\u628a\u6211\u4eec\u7f09\u6355\uff0c\u800c\u8981\u7b49\u5230\u4eca\u5929\uff1f\u201d \u3000\u3000\u201c\u5f53\u65f6\u6211\u82e6\u65e0\u8bc1\u636e\uff0c\u800c\u4e14\u4e5f\u4e0d\u77e5\u9053\u4f60\u4eec\u4e09\u4eba\u4e2d\u7a76\u7adf\u662f\u8c01\u4e0b\u7684\u624b\uff1b\u201d\u8ffd\u547d\u9053\uff0c\u201c\u6211\u53ea\u6709\u7b49\uff0c\u6211\u53ea\u6709\u67e5\uff0c\u6211\u53ea\u6709\u5fcd\u3002\u8fd9\u4e48\u5de7\uff0c\u4f60\u4eec\u4e5f\u4e00\u9f50\u5230\u6d1b\u9633\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u4e00\u7b11\uff0c\u704c\u4e86\u4e24\u53e3\u9152\uff0c\u53c8\u9053\uff1a\u201c\u6700\u8fd1\u6d1b\u9633\u4e5f\u53d1\u751f\u4e86\u4e0d\u5c11\u4e8b\u60c5\uff0c\u770b\u6765\uff0c\u4e8b\u60c5\u8fd8\u4f1a\u7ee7\u7eed\u53d1\u751f\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u7709\u6bdb\u4e00\u6311\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4e09\u7237\u5982\u4eca\u5df2\u67e5\u5230\u51f6\u624b\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u8ffd\u547d\u4e00\u7b11\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u77e5\u9053\u4f60\u4eec\u6765\u6d1b\u9633\uff0c\u90fd\u662f\u53d7\u4eba\u91cd\u91d1\u793c\u8058\uff0c\u5982\u4eca\uff0c\u5927\u5e08\u8eab\u5728\u5170\u4ead\u4ed6\u5bb6\uff0c\u77f3\u8001\u5e7a\u4e3a\u5999\u624b\u5802\u56de\u5bb6\u6548\u547d\uff0c\u8521\u5144\u5374\u6295\u5165\u5343\u53f6\u5c71\u5e84\u845b\u5bb6\uff0c\u56e0\u4e3a\u8fd9\u4e09\u5bb6\u7684\u5fae\u5999\u5173\u7cfb\uff0c\u6211\u53ea\u597d\u501f\u5c0f\u78a7\u6e56\u6e38\u5bb6\u7684\u5730\u65b9\uff0c\u6765\u8ddf\u8bf8\u4f4d\u4e00\u53d9\u3002\u201d \u3000\u3000\u4ed6\u8865\u5145\u4e00\u53e5\u9053\uff1a\u201c\u5927\u5e08\u548c\u4ee4\u5e08\u5144\u987e\u795e\u98ce\uff0c\u4e5f\u597d\u4e45\u672a\u7545\u805a\u4e86\u7f62\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u5e08\u5f1f\uff0c\u65e0\u8bba\u6b66\u529f\u667a\u6167\uff0c\u90fd\u5728\u6211\u4e4b\u4e0a\uff0c\u5982\u4eca\u4ed6\u65e2\u4e3a\u6c60\u5bb6\u52a9\u9635\uff0c\u770b\u6765\u6211\u90fd\u662f\u8981\u5377\u94fa\u76d6\u65e9\u8d70\u65e9\u7740\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u5fd9\u9053\uff1a\u201c\u6709\u5e08\u5144\u5728\u8fd9\u91cc\uff0c\u6d1b\u9633\u90a3\u6709\u6211\u7acb\u8db3\u5904\u8eab\u7684\u4f59\u5730\uff0c\u770b\u6765\uff0c\u8d2b\u50e7\u8fd8\u662f\u5411\u6c60\u516c\u5b50\u8bf7\u8f9e\u7684\u597d\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u51b7\u7684\u9053\uff1a\u201c\u660e\u4eba\u4e0d\u505a\u6697\u4e8b\uff0c\u4e09\u7237\u65e2\u5df2\u67e5\u5230\u51f6\u624b\uff0c\u4e3a\u4f55\u4e0d\u76f4\u63a5\u6307\u9648\uff0c\u8981\u5927\u5bb6\u5728\u8fd9\u513f\u4f60\u865e\u6211\u8bc8\uff0c\u5f92\u8d39\u5fc3\u601d\u3002\u201d \u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\uff0c\u6211\u4ecd\u4e0d\u77e5\u9053\u8c01\u662f\u51f6\u624b\uff1b\u8bf4\u4e0d\u5b9a\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u4f46\u4ecd\u9700\u6c42\u8bc1\uff1b\u201d\u8ffd\u547d\u9053\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4eec\u4e3a\u4f55\u800c\u6765\u6d1b\u9633\uff0c\u5374\u4e0d\u660e\u767d\u4f60\u4eec\u4e3a\u4f55\u8981\u5148\u5728\u67af\u67f3\u5c6f\u8fc7\u5bbf\u4e00\u5bb5\uff1f\u201d \u3000\u3000\u4ed6\u7b11\u772f\u772f\u7684\u671b\u7740\u8521\u65cb\u949f\uff0c\u773c\u7f1d\u5730\u4f3c\u4e00\u6839\u9488\u3002 \u3000\u3000\u5229\u9488\u3002 \u3000\u3000\u5c16\u9488\u3002 \u3000\u3000\u4ed6\u7684\u95ee\u9898\u4e5f\u50cf\u4e00\u6839\u9488\u3002 \u3000\u3000\u4e00\u6839\u62c6\u7ebf\u7684\u9488\u3002 \u3000\u3000\u201c\u5c24\u5176\u662f\u4f60\u3002\u4f60\u521d\u5d1b\u6c5f\u6e56\uff0c\u4f46\u5df1\u88ab\u6b66\u6797\u4e2d\u5217\u4e3a\u4e09\u5927\u795e\u79d8\u9ad8\u624b\u4e4b\u4e00\uff0c\u4f60\u6bcf\u6b21\u90fd\u4ee5\u4e00\u8eab\u672c\u9886\u3001\u51b3\u6597\u8005\u7684\u8eab\u4efd\u51fa\u73b0\uff0c\u5374\u4e3a\u4f55\u8981\u5343\u91cc\u8fe2\u8fe2\u7684\u8dd1\u5230\u67af\u67f3\u5c6f\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u5f88\u7b80\u5355\uff0c\u6211\u6240\u505a\u7684\u4e00\u5207\uff0c\u90fd\u662f\u4e3a\u4e86\u51b3\u6597\u548c\u751f\u5b58\uff1b\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u51b3\u6597\u662f\u6211\u6d3b\u7740\u7684\u610f\u4e49\uff0c\u6211\u5fc5\u987b\u5148\u80fd\u6d3b\u7740\uff0c\u624d\u80fd\u5bfb\u6c42\u610f\u4e49\u3002\u5982\u679c\u6211\u4e3a\u4e86\u6d3b\u7740\uff0c\u800c\u7528\u6211\u51b3\u6597\u7684\u529b\u91cf\uff0c\u6765\u5077\u62a2\u76d7\u52ab\uff0c\u90a3\u6211\u6d3b\u4e0b\u53bb\u4e5f\u5931\u53bb\u610f\u4e49\u4e86\uff0c\u6240\u4ee5\u6211\u4e0d\u5e72\uff0c\u53ef\u662f\uff0c\u6211\u8981\u6d3b\u4e0b\u53bb\uff0c\u6240\u4ee5\uff0c\u4e00\u8eab\u672c\u9886\u3001\u4e00\u8154\u70ed\u8840\uff0c\u53ea\u5356\u4e0e\u8bc6\u8d27\u4e4b\u4eba\u3002\u201d \u3000\u3000\u201c\u6765\u6d1b\u9633\uff0c\u56e0\u4e3a\u6709\u4eba\u7ed9\u6211\u94b1\uff0c\u8ba9\u6211\u53ef\u4ee5\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\uff0c\u800c\u53c8\u53ef\u4ee5\u85c9\u6b64\u4e0e\u4e0d\u540c\u7684\u9ad8\u624b\u51b3\u6597\uff0c\u4ed6\u4eec\u65e2\u7136\u4eba\u5728\u6c5f\u6e56\u3001\u8eab\u6000\u6b66\u827a\uff0c\u5c31\u77e5\u9053\u6240\u4ed8\u51fa\u7684\u4ee3\u4ef7\uff0c\u662f\u968f\u65f6\u90fd\u6709\u53ef\u80fd\u8d25\u3001\u53ef\u80fd\u6b7b\uff0c\u800c\u4e14\u6028\u4e0d\u5f97\u4eba\u3002\u201d\u8521\u65cb\u949f\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5f88\u5b9e\u5728\uff0c\u8c01\u90fd\u542c\u5f97\u51fa\u6765\u4ed6\u4e1d\u6beb\u6ca1\u6709\u4f59\u8f9e\uff0c\u5c31\u7b97\u4ed6\u6280\u4e0d\u5982\u4eba\uff0c\u7ed9\u4eba\u6740\u4e86\uff0c\u4ed6\u4e5f\u6beb\u65e0\u6028\u8a00\uff0c\u201c\u6211\u5230\u67af\u67f3\u5c6f\uff0c\u4e5f\u662f\u4e3a\u8fd9\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u6307\u2026\u2026\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u6709\u4eba\u7ed9\u6211\u4e00\u7b14\u94f6\u5b50\uff0c\u8981\u6211\u67d0\u65f6\u67d0\u65e5\uff0c\u5230\u67af\u67f3\u5c6f\uff0c\u627e\u4e00\u4e2a\u4eba\u51b3\u6597\u3002\u201d \u3000\u3000\u8ffd\u547d\u95ee\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u5b5f\u968f\u56ed\u3002\u201d \u3000\u3000\u201c\u5b5f\u592a\u5b88\u662f\u4e2a\u597d\u5b98\uff0c\u4f60\u4e0d\u5e94\u8be5\u5bf9\u4ed8\u4ed6\uff01\u201d\u8ffd\u547d\u9053\uff0c\u201c\u522b\u4eba\u7ed9\u94b1\uff0c\u4f60\u5c31\u53bb\uff0c\u8fd9\u662f\u5f53\u6740\u624b\uff0c\u4e0d\u662f\u51b3\u6597\u8005\u6240\u4e3a\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u6211\u53ea\u4e3a\u94b1\u4e0e\u4eba\u51b3\u6597\uff0c\u6211\u4e0d\u4e3a\u94b1\u6740\u4eba\u3002\u6211\u51fb\u8d25\u5bf9\u65b9\uff0c\u4f46\u4e0d\u6740\u4eba\uff0c\u9664\u975e\uff0c\u5927\u5bb6\u5728\u5b9a\u80dc\u8d1f\u65f6\u4e0d\u5f97\u5df2\u8981\u51b3\u751f\u6b7b\uff0c\u6211\u624d\u6740\u4eba\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u538b\u6839\u513f\u5c31\u4e0d\u77e5\u9053\uff0c\u5b5f\u968f\u56ed\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u53ea\u77e5\u9053\u4ed6\u7684\u2018\u843d\u82b1\u5f71\u5251\u201d\u662f\u5f88\u597d\u7684\u5251\u624b\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\uff0c\u201d\u8ffd\u547d\u6c89\u541f\u9053\uff0c\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff1a\u4f60\u6839\u672c\u6ca1\u6709\u6740\u5bb3\u5b5f\u968f\u56ed\uff1f\u201d \u3000\u3000\u201c\u6211\u6839\u672c\u4e0d\u66fe\u627e\u4ed6\u51b3\u6597\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e0d\u662f\u8822\u6750\u3002\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u522b\u4eba\u7ed9\u6211\u94f6\u5b50\uff0c\u6211\u53bb\u627e\u4eba\u51b3\u6597\uff0c\u51b3\u6597\u4e4b\u524d\uff0c\u6211\u4e5f\u603b\u4f1a\u53bb\u5f04\u6e05\u695a\u4e00\u4e9b\u5fc5\u987b\u8981\u5f04\u6e05\u695a\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u6240\u4ee5\u5728\u4f60\u672a\u52a8\u624b\u524d\uff0c\u5148\u884c\u53bb\u5f04\u6e05\u695a\u4ea4\u624b\u7684\u5bf9\u8c61\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u6211\u5f04\u6e05\u695a\u4e86\uff0c\u6240\u4ee5\u4fbf\u4e0d\u60f3\u627e\u4ed6\u51b3\u6597\u3002\u201d \u3000\u3000\u8ffd\u547d\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u662f\u4e00\u4e2a\u88ab\u5145\u519b\u7684\u4eba\uff0c\u8fd8\u6709\u4e00\u5bb6\u5927\u5c0f\u540c\u4e00\u547d\u8fd0\uff0c\u5168\u65e0\u6597\u5fd7\uff0c\u6211\u51b3\u4e0d\u80fd\u8ddf\u4e00\u4e2a\u5931\u610f\u7684\u4eba\u8f83\u91cf\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u4f60\u6839\u672c\u8fd8\u672a\u548c\u4ed6\u4ea4\u624b\u3002\u201d \u3000\u3000\u201c\u6211\u5f53\u665a\u5c31\u79bb\u5f00\u4e86\u67af\u67f3\u5c6f\u3002\u201d \u3000\u3000\u201c\u6740\u4e86\u4eba\u4e5f\u4e00\u6837\u8981\u79bb\u5f00\u67af\u67f3\u5c6f\u3002\u201d\u65ad\u7709\u77f3\u4e0d\u6000\u597d\u610f\u5730\u52a0\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u4e00\u6837\u79bb\u5f00\u4e86\u67af\u67f3\u5c6f\u3002\u201d\u8521\u65cb\u949f\u53cd\u51fb\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u6211\u5f53\u7136\u4e0d\u60f3\u5728\u67af\u67f3\u5c6f\u8fc7\u4e00\u8f88\u5b50\u3002\u201d\u65ad\u7709\u77f3\u8f7b\u677e\u5730\u9053\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u6536\u4e86\u522b\u4eba\u7684\u94f6\u5b50\u3002\u201d\u8ffd\u547d\u6293\u4f4f\u91cd\u70b9\uff0c\u95ee\u3002 \u3000\u3000\u201c\u6211\u628a\u94f6\u5b50\u9000\u56de\u3002\u201d\u8521\u65cb\u949f\u5373\u7b54\u3002 \u3000\u3000\u201c\u770b\u6765\u4f60\u7684\u786e\u4e0d\u662f\u4e2a\u8822\u6750\uff0c\u201d\u65ad\u7709\u77f3\u9053\uff0c\u201c\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u8822\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u60f3\u6b7b\uff1b\u201d\u8521\u65cb\u949f\u51b7\u51b7\u7684\u9053\uff0c\u201c\u53ef\u662f\u4f60\u662f\u5728\u627e\u6b7b\u3002\u201d \u3000\u3000\u201c\u4e09\u6355\u5934\uff0c\u8d2b\u50e7\u5012\u6709\u4e00\u4e8b\u4e0d\u89e3\uff1b\u201d\u4e03\u53d1\u5927\u5e08\u4f3c\u4e0d\u5e0c\u671b\u77f3\u8001\u5e7a\u548c\u8521\u65cb\u949f\u4e4b\u95f4\u53d1\u751f\u5927\u5927\u7684\u4e89\u6267\uff0c\u5c94\u5f00\u8bdd\u9898\u9053\uff0c\u201c\u4f60\u65e2\u7136\u5230\u4e86\u67af\u67f3\u5c6f\uff0c\u53c8\u600e\u4f1a\u8ba9\u706d\u95e8\u8840\u6848\u53d1\u751f\uff1f\u201d \u3000\u3000\u8ffd\u547d\u957f\u53f9\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u201c\u51f6\u624b\u8ba1\u5212\u5468\u5bc6\uff0c\u5e03\u5c40\u5468\u8be6\uff1b\u201d\u8ffd\u547d\u62b1\u7740\u575b\u5b50\u5495\u565c\u5495\u565c\u7684\u53c8\u559d\u4e86\u51e0\u53e3\u9152\uff0c\u628a\u9152\u575b\u5f80\u5730\u4e0a\u91cd\u91cd\u7684\u4e00\u653e\uff0c\u201c\u5f53\u65f6\u6211\u88ab\u4e00\u4e2a\u8499\u9762\u7684\u9ed1\u8863\u4eba\u5f15\u8d70\uff0c\u6211\u7740\u4e86\u4ed6\u7684\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u201c\u96be\u5f97\u96be\u5f97\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u201d\u77f3\u8001\u4e48\u559c\u6ecb\u6ecb\u7684\u9053\uff1a\u201c\u8be5\u4e0d\u4f1a\u662f\u6211\u542c\u9519\uff0c\u8fde\u56db\u5927\u540d\u6355\u4e5f\u4f1a\u4e2d\u522b\u4eba\u7684\u8ba1\uff01\u201d \u3000\u3000\u8ffd\u547d\u54fc\u4e00\u58f0\u3002 \u3000\u3000\u5728\u4e00\u65c1\u7684\u987e\u4f5b\u5f71\u5ffd\u9053\uff1a\u201c\u6309\u7167\u5e38\u7406\uff0c\u666e\u5927\u4e4b\u4e0b\uff0c\u53ea\u6015\u96be\u6709\u51e0\u4eba\u53ef\u4ee5\u5728\u4e09\u6355\u5934\u7684\u8ffd\u8e2a\u4e4b\u4e0b\uff0c\u9003\u5f97\u5f00\u53bb\u3002\u201d \u3000\u3000\u201c\u4ed6\u8f7b\u529f\u597d\uff0c\u5f88\u597d\uff0c\u201d\u8ffd\u547d\u9053\uff0c\u201c\u4f46\u4ed6\u8fd8\u662f\u9003\u4e0d\u4e86\u3002\u201d \u3000\u3000\u201c\u4e09\u7237\u53ef\u6709\u8ddf\u4ed6\u4ea4\u624b\uff1f\u201d\u987e\u4f5b\u5f71\u95ee\u3002 \u3000\u3000\u201c\u6709\u201d \u3000\u3000\u201c\u4ed6\u7684\u6b66\u529f\u5bb6\u6570\uff0c\u4e09\u7237\u53ef\u770b\u5f97\u51fa\u6765\uff1f\u201d\u987e\u4f5b\u5f71\u8fd9\u6837\u4e00\u95ee\uff0c\u5728\u573a\u7684\u4eba\u90fd\u6709\u540c\u611f\uff0c\u56e0\u4e3a\u8ffd\u547d\u521a\u624d\u4e00\u773c\u4fbf\u770b\u7834\u8521\u65cb\u949f\u7684\u6b66\u529f\u6765\u5386\uff0c\u548c\u4ed6\u4ea4\u624b\u7684\u4eba\uff0c\u5c31\u597d\u50cf\u628a\u81ea\u5df1\u7684\u751f\u8fb0\u516b\u5b57\u4ea4\u7ed9\u4e00\u4e2a\u6d1e\u900f\u5929\u673a\u7684\u535c\u8005\u4e00\u822c\u3002 \u3000\u3000\u201c\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u8ffd\u547d\u8fd9\u4e2a\u56de\u7b54\uff0c\u4f7f\u4f17\u4eba\u90fd\u5927\u51fa\u610f\u6599\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8ddf\u4ed6\u6253\u4e86\u4e09\u56de\u5408\uff0c\u640f\u6218\u4e8c\u5341\u4e03\u62db\uff0c\u4ed6\u603b\u5171\u7528\u4e86\u5341\u4e00\u4e2a\u5b8c\u5168\u4e0d\u540c\u95e8\u6d3e\u7684\u7edd\u62db\u6765\u5bf9\u4ed8\u6211\uff0c\u6211\u4e0d\u77e5\u9053\u90a3\u4e00\u95e8\u624d\u662f\u4ed6\u7684\u770b\u5bb6\u672c\u9886\uff1b\u201d\u8ffd\u547d\u8bf4\uff0c\u201c\u7136\u540e\uff0c\u63a5\u5e94\u4ed6\u7684\u4eba\u5c31\u51fa\u73b0\u4e86\uff0c\u51fa\u8a00\u8b66\u793a\uff0c\u4f7f\u6211\u77e5\u9053\u4ed6\u4eec\u7528\u7684\u662f\u58f0\u4e1c\u51fb\u897f\u7684\u624b\u6cd5\uff1a\u5b5f\u5927\u5b88\u90a3\u513f\u51fa\u4e8b\u4e86\uff01\u6211\u4e0d\u6562\u604b\u6218\uff0c\u9a6c\u4e0a\u6298\u8fd4\uff0c\u4f46\u5927\u9519\u5df2\u6210\uff0c\u4e00\u5207\u90fd\u6765\u4e0d\u53ca\u633d\u6551\u4e86\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u9053\uff1a\u201c\u770b\u6765\uff0c\u80fd\u5728\u4e09\u7237\u811a\u4e0b\u8d70\u5f97\u8fc7\u4e8c\u5341\u4e03\u62db\u800c\u4e0d\u73b0\u51fa\u539f\u5f62\u7684\u4eba\uff0c\u80af\u5b9a\u662f\u4e2a\u9ad8\u624b\uff0c\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u8fd9\u513f\u9ad8\u624b\u5c31\u6709\u597d\u51e0\u4f4d\uff0c\u6070\u597d\u4e5f\u90fd\u662f\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\uff0c\u201d\u8ffd\u547d\u73af\u987e\u5168\u573a\uff0c\u7136\u540e\u671b\u5b9a\u8521\u65cb\u949f\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u5148\u6536\u5230\u4e00\u7b14\u94b1\uff0c\u8bf7\u4f60\u53bb\u8ddf\u5b5f\u968f\u56ed\u51b3\u6218\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u70b9\u5934\u3002\u4ed6\u4f3c\u4e4e\u4e0d\u4e60\u60ef\u56de\u7b54\u201c\u662f\u201d\u5b57\u3002 \u3000\u3000\u201c\u4f60\u5f53\u7136\u4f1a\u77e5\u9053\u4ea4\u6b3e\u5b50\u7ed9\u4f60\u7684\u4eba\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u8521\u65cb\u949f\u6447\u5934\u3002 \u3000\u3000\u201c\u51e1\u662f\u8981\u4eba\u505a\u8fd9\u79cd\u4e8b\uff0c\u5c31\u4e00\u5b9a\u4e0d\u60f3\u8ba9\u4eba\u77e5\u9053\u4ed6\u662f\u8c01\uff1b\u6240\u4ee5\u4ed6\u4eec\u627e\u6211\u5bb9\u6613\uff0c\u6211\u627e\u4ed6\u4eec\u5374\u96be\uff1b\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u800c\u4e14\uff0c\u82b1\u4e00\u5927\u7b14\u6b3e\u5b50\u53eb\u4eba\u6740\u4eba\uff0c\u5e72\u8fd9\u79cd\u5927\u4e70\u5356\u7684\u591a\uff0c\u53ea\u8981\u4eba\u53bb\u6253\u8d25\u53e6\u4e00\u4e2a\u4eba\u7684\u5c11\uff0c\u6240\u4ee5\uff0c\u6211\u8fd9\u7b97\u662f\u51b7\u95e8\u751f\u610f\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u4f60\u7684\u751f\u610f\u53ef\u771f\u7684\u4e0d\u6613\u505a\u3002\u201d\u8ffd\u547d\u7b11\u9053\u3002 \u3000\u3000\u201c\u6740\u4eba\u653e\u706b\u91d1\u8170\u5e26\uff0c\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u6211\u8fd9\u95e8\u751f\u610f\u5374\u95e8\u582a\u7f57\u96c0\uff0c\u6240\u4ee5\u6211\u7684\u751f\u6d3b\u8fc7\u5f97\u5e76\u4e0d\u597d\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fc7\uff0c\u4f60\u8fd9\u95e8\u751f\u610f\u4e5f\u6709\u597d\u5904\uff0c\u201d\u77f3\u65ad\u7709\u53e3\u5934\u4e0a\u59cb\u7ec8\u4e0d\u653e\u8fc7\uff1a\u201c\u81f3\u5c11\u53ef\u4ee5\u7528\u6765\u8bc1\u660e\u4f60\u662f\u65e0\u8f9c\u7684\u3002\u201d \u3000\u3000\u8ffd\u547d\u5ffd\u95ee\uff1a\u201c\u5c31\u7b97\u4f60\u4e0d\u77e5\u9053\u662f\u8c01\u53eb\u4f60\u505a\u8fd9\u4e9b\u4e70\u5356\uff0c\u4f46\u4f60\u628a\u94f6\u7968\u6216\u94f6\u5b50\u9000\u56de\u53bb\u7684\u65f6\u5019\uff0c\u603b\u4f1a\u900f\u8fc7\u4e9b\u65b9\u6cd5\uff0c\u8ddf\u90a3\u4e9b\u4eba\u63a5\u89e6\u7684\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u4e00\u53e5\u5730\u9053\uff1a\u201c\u4f60\u7528\u7684\u662f\u4ec0\u4e48\u65b9\u6cd5\uff1f\u4f60\u63a5\u89e6\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000 \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u6b7b\u4eba\u672a\u6b7b \u3000 \u3000\u3000\u201c\u8fd9\u7684\u786e\u662f\u4e00\u6761\u53ef\u4ee5\u8ffd\u67e5\u7684\u7ebf\u7d22\uff0c\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u53ef\u60dc\uff0c\u627e\u6211\u8ddf\u4eba\u51b3\u6597\u7684\u4eba\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4f55\u65f6\u6765\u3001\u4f55\u65f6\u53bb\uff0c\u4ed6\u8499\u7740\u8138\uff0c\u542c\u53e3\u97f3\uff0c\u6bcf\u6b21\u6765\u4eba\u90fd\u4e0d\u540c\uff0c\u6839\u672c\u65e0\u6cd5\u8ffd\u67e5\uff0c\u53ef\u80fd\u662f\u540c\u4e00\u4f19\u4eba\u9a6c\uff0c\u4e5f\u53ef\u80fd\u662f\u6839\u672c\u4e0d\u76f8\u5e72\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u4e0d\u63a5\u53d7\u4e70\u5356\uff0c\u53ea\u8981\u628a\u5b9a\u94f6\u9000\u56de\u5c31\u884c\u4e86\u3002\u6211\u5f97\u8981\u5148\u627e\u5230\u6700\u9760\u8fd1\u51b3\u6597\u5730\u70b9\u7684\u571f\u5730\u5e99\uff0c\u6380\u5f00\u9999\u7089\u4e0b\u7684\u77f3\u7816\uff0c\u628a\u94f6\u7968\u585e\u8fdb\u53bb\uff0c\u4fbf\u81ea\u4f1a\u6709\u4eba\u53d6\u3002\u201d\u8521\u65cb\u949f\u63a5\u9053\uff0c\u201c\u81f3\u4e8e\u662f\u8c01\u53d6\u56de\u3001\u4f55\u65f6\u53d6\u56de\uff0c\u6211\u4e5f\u4e0d\u5f97\u800c\u77e5\uff0c\u800c\u4e14\uff0c\u6211\u53d7\u547d\u4e8e\u4eba\uff0c\u5c31\u7b97\u77e5\u9053\uff0c\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e0b\u7ed3\u8bba\uff1a\u201c\u6240\u4ee5\uff0c\u8fd9\u6761\u7ebf\u7d22\uff0c\u5b8c\u5168\u65e0\u7528\u3002\u201d \u3000\u3000\u201c\u7167\u4f60\u7684\u8bf4\u6cd5\uff0c\u4f60\u7a76\u7adf\u6709\u6ca1\u6709\u628a\u94f6\u7968\u9000\u56de\uff0c\u4e5f\u662f\u6beb\u65e0\u8bc1\u636e\u7684\u4e8b\u4e86\u3002\u201d\u8ffd\u547d\u9053\uff0c\u201c\u56e0\u6b64\uff0c\u4f60\u4e5f\u65e0\u6cd5\u8bc1\u660e\uff0c\u662f\u5426\u66fe\u4e0e\u5b5f\u968f\u56ed\u51b3\u6218\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\u6211\u540c\u65f6\u4e5f\u65e0\u6cd5\u8bc1\u5b9e\uff0c\u7a76\u7adf\u6709\u6ca1\u6709\u6740\u6b7b\u5b5f\u968f\u56ed\u3002\u201d \u3000\u3000\u201c\u8fd9\u5b9e\u5728\u662f\u4ef6\u9057\u61be\u7684\u4e8b\u3002\u201d\u8ffd\u547d\u9053\uff0c\u201c\u56e0\u4e3a\u6211\u5b9e\u5728\u4e0d\u60f3\u8ddf\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u5f88\u9057\u61be\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u56e0\u4e3a\u6211\u4e5f\u4e0d\u60f3\u4e0e\u56db\u5927\u540d\u6355\u4e3a\u654c\u3002\u201d \u3000\u3000\u201c\u53ea\u4e0d\u8fc7\u8fd9\u9057\u61be\u4e0d\u53ea\u4f60\u6211\uff0c\u201d\u8ffd\u547d\u9053\uff0c\u201c\u5f53\u7136\u8fd8\u6709\u77f3\u5144\u3002\u201d \u3000\u3000\u77f3\u65ad\u7709\u9053\uff1a\u201c\u53ef\u60dc\u8fd9\u4e9b\u4e8b\u8ddf\u6211\u5b8c\u5168\u65e0\u5173\uff0c\u6240\u4ee5\u6211\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u9057\u61be\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u90a3\u5929\u665a\u4e0a\uff0c\u77f3\u5144\u4e5f\u4e00\u6837\u8eab\u5728\u67af\u67f3\u5c6f\u3002\u201d\u8ffd\u547d\u53c8\u5f00\u59cb\u559d\u9152\u3002 \u3000\u3000\u201c\u6211\u5728\u67af\u67f3\u5c6f\u662f\u6709\u76ee\u7684\u7684\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u76ee\u7684\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u3002\u201d \u3000\u3000\u8fd9\u7b54\u6848\u4e0d\u4ec5\u610f\u5916\uff0c\u7b80\u76f4\u6709\u70b9\u60ca\u4eba\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\uff1f\u201d\u8ffd\u547d\u95ee\u3002 \u3000\u3000\u201c\u6211\u662f\u4e2a\u6740\u624b\uff0c\u8fd9\u70b9\u8c01\u90fd\u77e5\u9053\u3002\u201d\u77f3\u65ad\u7709\u9053\uff0c\u201c\u90a3\u5929\uff0c\u6211\u7684\u2018\u8001\u677f'\u544a\u8bc9\u6211\uff0c\u6709\u4eba\u7ed9\u4e86\u6211\u4e00\u5927\u7b14\u94b1\uff0c\u8bf4\u8981\u6740\u4e00\u4e2a\u8170\u624e\u846b\u82a6\u3001\u6d12\u978b\u3001\u6563\u53d1\uff0c\u770b\u6765\u50cf\u4e2a\u9189\u732b\uff0c\u4f46\u773c\u775b\u6e05\u9192\u5f97\u5c31\u50cf\u4e2a\u9a97\u5b50\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u542c\u6765\uff0c\u4f60\u5f62\u5bb9\u7684\u4e11\u516b\u602a\u5e94\u8be5\u5c31\u662f\u6211\u3002\u201d\u8ffd\u547d\u7b11\u9053\uff0c\u201c\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\uff0c\u5f53\u5b98\u7684\u4eba\u662f\u8001\u5343\uff0c\u5f53\u5dee\u7684\u4eba\u662f\u9a97\u5b50\uff0c\u5176\u5b9e\u5b98\u597d\u5f53\u3001\u540f\u96be\u4e3a\uff1b\u201d \u3000\u3000\u8ffd\u547d\u987f\u4e86\u4e00\u987f\uff0c\u7b11\u772f\u772f\u7684\u9053\uff1a\u201c\u6211\u5f88\u5947\u602a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u6ca1\u6709\u6740\u6211\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u63a5\u4e0b\u8fd9\u7b14\u751f\u610f\u7684\u65f6\u5019\uff0c\u4e0d\u77e5\u9053\u662f\u4f60\uff0c\u540e\u6765\u6211\u53bb\u4e86\uff0c\u770b\u89c1\u4f60\u8ffd\u8e2a\u4e00\u7fa4\u62bc\u89e3\u7684\u72af\u4eba\uff0c\u518d\u6697\u4e2d\u89c2\u5bdf\u4f60\u7684\u8eab\u6cd5\uff0c\u4fbf\u77e5\u9053\u4f60\u662f\u8ffd\u547d\u3002\u201d\u77f3\u65ad\u7709\u9053\uff0c\u201c\u77e5\u9053\u4f60\u662f\u8ffd\u547d\u540e\uff0c\u4fbf\u4e0d\u80fd\u6740\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u60a0\u95f2\u7684\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u6740\u4e0d\u4e86\u4f60\uff0c\u6211\u5c31\u662f\u81ea\u5bfb\u6b7b\u8def\u3002\u8981\u662f\u6211\u6740\u5f97\u4e86\u4f60\uff0c\u6211\u8fd8\u8981\u6740\u597d\u51e0\u4e2a\u4eba\uff0c\u201d\u77f3\u65ad\u7709\u6101\u7709\u82e6\u8138\u7684\u8bf4\uff0c\u201c\u4ed6\u4eec\u662f\u51b7\u8840\u3001\u94c1\u624b\u3001\u65e0\u60c5\uff0c\u5c31\u7b97\u6211\u6740\u5f97\u4e86\u4ed6\u4eec\uff0c\u8fd8\u6709\u8bf8\u845b\u5148\u751f\u3002\u201d \u3000\u3000\u4ed6\u82e6\u7b11\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u4eba\uff0c\u975e\u5230\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u6211\u600e\u6562\u6740\uff1f\u201d \u3000\u3000\u8ffd\u547d\u4eec\u7740\u4e0b\u5df4\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e5f\u628a\u94b1\u9000\u4e86\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u9000\u94b1\uff1f\u90a3\u662f\u50bb\u5b50\u624d\u5e72\u7684\u4e8b\uff0c\u201d\u77f3\u65ad\u7709\u6447\u624b\u6446\u8111\u9053\uff0c\u201c\u6211\u62ff\u94b1\u5c31\u9003\uff0c\u518d\u627e\u4e00\u4e2a\u65b0\u8001\u677f\uff0c\u5f53\u7136\u5c31\u662f\u5999\u624b\u5802\u56de\u5bb6\u3002\u542c\u8bf4\u6211\u7684\u65e7\u8001\u677f\uff0c\u4ed8\u51fa\u4e86\u53cc\u500d\u4ef7\u94b1\uff0c\u6b63\u5728\u627e\u53e6\u4e00\u4e2a\u4eba\u6765\u8ffd\u6740\u6211\u3002\u201d \u3000\u3000\u4ed6\u7b11\u8d77\u6765\u7684\u65f6\u5019\uff0c\u989d\u89d2\u7adf\u6709\u4e24\u9053\u7070\u5f71\u4e00\u573a\uff0c\u5c31\u50cf\u7709\u6bdb\u7684\u5e7d\u9b42\u4e00\u822c\uff1a\u201c\u73b0\u5728\u6211\u7684\u4ef7\u94b1\uff0c\u8fd8\u6bd4\u4f60\u9ad8\u54a7\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d\u8ffd\u547d\u9053\uff1a\u201c\u4f60\u6740\u4eba\u6bd4\u6211\u591a\uff0c\u6068\u4f60\u7684\u4eba\uff0c\u4e5f\u6bd4\u6068\u6211\u7684\u591a\uff0c\u4ef7\u94b1\u5f53\u7136\u5e94\u8be5\u6bd4\u6211\u9ad8\uff1b\u201d \u3000\u3000\u201c\u53ef\u60dc\u6211\u5374\u4e0d\u80fd\u76f8\u4fe1\u4f60\u53e6\u4e00\u4ef6\u4e8b\uff1b\u201d\u8ffd\u547d\u4f4e\u5934\u770b\u4ed6\u81ea\u5df1\u7684\u4e00\u5bf9\u811a\uff0c\u201c\u4f60\u6ca1\u6709\u6740\u6211\uff0c\u662f\u4e8b\u5b9e\uff0c\u4f46\u6ca1\u6709\u6740\u6211\u5e76\u4e0d\u7b49\u4e8e\u4f60\u4e5f\u6ca1\u6709\u6740\u5b5f\u968f\u56ed\u3002\u201d \u3000\u3000\u201c\u5f88\u6709\u9053\u7406\uff0c\u201d\u4e03\u53d1\u7985\u5e08\u9053\uff1a\u201c\u8be5\u6211\u4e86\u7f62\uff1f\u201d \u3000\u3000\u8ffd\u547d\u772f\u7740\u773c\u53cd\u95ee\uff1a\u201c\u8be5\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8be5\u4f60\u95ee\u6211\uff0c\u4e00\u4e2a\u51fa\u5bb6\u4eba\uff0c\u4e09\u66f4\u534a\u591c\u5230\u67af\u67f3\u5c6f\u5e72\u4ec0\u4e48\uff1f\u201d\u4e03\u53d1\u7528\u539a\u638c\u629a\u629a\u4ed6\u7684\u621f\u53d1\uff1a \u3000\u3000\u201c\u4f60\u8981\u662f\u95ee\u6211\uff0c\u4e0d\u5982\u95ee\u4ed6\u3002\u201d\u4ed6\u7528\u624b\u4e00\u6307\u3002 \u3000\u3000\u4ed6\u6307\u7684\u662f\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u201c\u662f\u6211\u53eb\u4ed6\u53bb\u7684\u3002\u201d\u987e\u4f5b\u5f71\u9053\u3002 \u3000\u3000\u8ffd\u547d\u5fae\u7b11\u7740\u9759\u5f85\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u6211\u8bf7\u4ed6\u53bb\u67af\u67f3\u5c6f\uff0c\u4ea4\u7ed9\u5b5f\u592a\u5b88\u4e00\u5c01\u4fe1\uff0c\u201d\u987e\u4f5b\u5f71\u9053\uff0c\u201c\u8fd9\u5c01\u4fe1\uff0c\u662f\u6e38\u516c\u5b50\u5199\u7ed9\u5b5f\u5927\u5b88\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u8ffd\u547d\u7b11\u7b11\u9053\u3002 \u3000\u3000\u201c\u4f60\u77e5\u9053\u7684\u5230\u5e95\u6709\u591a\u5c11\uff1f\u201d\u4e03\u53d1\u5374\u95ee\u3002\u4ed6\u95ee\u5f97\u5f88\u76f4\u63a5\uff0c\u56e0\u4e3a\u4ed6\u4e00\u5411\u8ba4\u4e3a\uff0c\u5f53\u95ee\u9898\u6765\u4e34\u7684\u65f6\u5019\uff0c\u6293\u4f4f\u95ee\u9898\u7684\u6838\u5fc3\u5207\u4e2d\u8981\u5bb3\uff0c\u624d\u662f\u89e3\u51b3\u95ee\u9898\u7684\u6700\u4f73\u65b9\u6cd5\u3002\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u5728\u5f53\u665a\u9001\u8fc7\u4e86\u4fe1\uff0c\u4fbf\u7acb\u5373\u79bb\u5f00\uff1f\u8840\u6848\u662f\u5728\u6211\u8d70\u540e\u53d1\u751f\u7684\uff01\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u5728\u6d1b\u9633\u57ce\u91cc\uff0c\u66ff\u5b5f\u592a\u5b88\u4e70\u4e86\u5ea6\u7252\u3001\u5243\u5ea6\u51fa\u5bb6\u7684\u5c31\u662f\u6e38\u7389\u906e\u6e38\u516c\u5b50\uff0c\u6240\u4ee5\uff0c\u53ea\u8981\u5b5f\u968f\u56ed\u4e00\u65e6\u8fdb\u5165\u6d1b\u9633\uff0c\u5c31\u7b49\u4e8e\u662f\u5c0f\u78a7\u6e56\u7684\u8d35\u5bbe\uff0c\u800c\u4e14\u4e5f\u662f\u5f3a\u52a9\u3002\u201d\u8ffd\u547d\u772f\u772f\u7b11\u7740\uff0c\u773c\u89d2\u6298\u8d77\u7684\u76b1\u7eb9\uff0c\u65e2\u4f3c\u6ca7\u6851\u7684\u8bb0\u53f7\uff0c\u4e5f\u50cf\u7231\u7b11\u7684\u8868\u75c7\uff0c\u201c\u6211\u4e5f\u77e5\u9053\uff0c\u6b66\u6797\u4e2d\uff0c\u5355\u53ea\u4e94\u53f0\u5c71\u4e00\u5b97\uff0c\u5c31\u51fa\u4e86\u4e09\u5927\u9ad8\u624b\uff0c\u90a3\u662f\uff1a\u2018\u591a\u6307\u6a2a\u5200\u4e03\u53d1\uff0c\u7b11\u770b\u6d9b\u751f\u4e91\u706d\u2019\uff0c\u540e\u4e00\u53e5\uff0c\u7cfb\u6307\u5f53\u4eca\u6c5f\u5357\u9739\u96f3\u5802\u7684\u4e09\u5927\u9ad8\u624b\uff0c\u800c\u524d\u53e5\u5219\u662f\u6307\u591a\u6307\u5934\u9640\u3001\u987e\u5144\u548c\u5927\u5e08\u3002\u201d \u3000\u3000\u201c\u6b66\u6797\u540c\u9053\uff0c\u8138\u4e0a\u8d34\u91d1\uff0c\u201d\u4e03\u53d1\u5927\u5e08\u5408\u4ec0\u9053\uff1a\u201c\u699c\u4e0a\u6709\u540d\uff0c\u53d7\u4e4b\u6709\u6127\u3002\u201d \u3000\u3000\u201c\u90a3\u5c01\u4fe1\uff0c\u4ecd\u7559\u5728\u8840\u6848\u73b0\u573a\uff0c\u6211\u4e5f\u770b\u5230\uff0c\u5927\u610f\u662f\u95ee\u5019\u5b5f\u592a\u5b88\uff0c\u8981\u4ed6\u8def\u4e0a\u591a\u52a0\u5c0f\u5fc3\uff0c\u5e76\u5728\u5c0f\u78a7\u6e56\u606d\u5019\u5927\u9a7e\u4e91\u4e91\u2026\u2026\u201d\u8ffd\u547d\u4e0d\u7406\u4f1a\u4e03\u53d1\u5927\u5e08\u7684\u8c26\u900a\uff0c\u201c\u4ed6\u53eb\u4eba\u9001\u4fe1\u7ed9\u5b5f\u5927\u4eba\uff0c\u7406\u6240\u5f53\u7136\uff0c\u56e0\u4e3a\u5c0f\u78a7\u6e56\u5982\u6709\u5b5f\u968f\u56ed\u81c2\u52a9\uff0c\u4ee5\u5b5f\u968f\u56ed\u7684\u6e05\u660e\u58f0\u8a89\u3001\u624d\u667a\u6b66\u529f\uff0c\u5fc5\u80fd\u4ee4\u6e38\u5bb6\u5982\u864e\u6dfb\u7ffc\uff1b\u987e\u5144\u8bf7\u52a8\u5927\u5e08\u524d\u5f80\uff0c\u65e2\u662f\u540c\u95e8\uff0c\u4e5f\u5c5e\u5408\u7406\uff0c\u53ea\u662f\uff0c\u201d \u3000\u3000\u8ffd\u547d\u76ef\u7740\u4e03\u53d1\u5927\u5e08\u9053\uff1a\u201c\u4f60\u5df2\u6295\u5165\u5170\u4ead\u6c60\u5bb6\uff0c\u4e3a\u4f55\u8fd8\u8981\u66ff\u5c0f\u78a7\u6e56\u6e38\u5bb6\u9001\u4fe1\uff1f\u201d \u3000\u3000\u201c\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u201d\u8fd9\u6b21\u4e03\u53d1\u8fd8\u6ca1\u6709\u56de\u7b54\uff0c\u987e\u4f5b\u5f71\u5df2\u62a2\u7740\u56de\u7b54\u4e86\uff1a\u201c\u4ed6\u5728\u9001\u4fe1\u7684\u65f6\u5019\uff0c\u8fd8\u672a\u6295\u5165\u6c60\u5bb6\uff0c\u9001\u4fe1\u4e4b\u540e\uff0c\u6c60\u65e5\u66ae\u53d1\u73b0\u4ed6\u7684\u884c\u8e2a\uff0c\u529b\u9080\u4ed6\u52a0\u76df\uff0c\u4ed6\u4fbf\u8fc7\u53bb\u5170\u4ead\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u602a\u6709\u8da3\u7684\u9053\uff1a\u201c\u4e3a\u5565\u4ed6\u4e0d\u5165\u5c0f\u78a7\u6e56\uff0c\u53cd\u52a0\u76df\u5170\u4ead\u5462\uff1f\u201d \u3000\u3000\u4e03\u53d1\u7acb\u5373\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5728\u3002\u201d \u3000\u3000\u201c\u4ed6\u201d\u6307\u7684\u5f53\u7136\u662f\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u8ffd\u547d\u9a6c\u4e0a\u5c31\u660e\u767d\u8fc7\u6765\u3002\u53e4\u6765\u8bb8\u591a\u6253\u4e0b\u6c5f\u5c71\u7684\u82f1\u96c4\u541b\u4e3b\uff0c\u5bf9\u8270\u8f9b\u521b\u4e1a\u3001\u5e76\u80a9\u594b\u95e8\u7684\u8001\u6218\u53cb\uff0c\u5f80\u5f80\u8d76\u5c3d\u6740\u7edd\uff1b\u540c\u4e00\u9053\u4e0a\u3001\u4e00\u540c\u51fa\u8eab\u7684\u65e7\u76df\u53cb\uff0c\u8d8a\u53d1\u5bb9\u6613\u5ac9\u5fcc\u5bf9\u65b9\u7684\u6210\u5c31\u3002\u8ffd\u547d\u4e86\u89e3\u8fd9\u4e9b\uff0c\u4ed6\u4e0d\u60f3\u8ffd\u7a76\u662f\u4e03\u53d1\u8fd8\u662f\u987e\u4f5b\u5f71\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u53ea\u8bf4\uff1a\u201c\u5f53\u5929\u665a\u4e0a\uff0c\u5728\u7a77\u4e61\u50fb\u58e4\u7684\u67af\u67f3\u5c6f\u91cc\uff0c\u80fd\u6740\u6b7b\u5b5f\u968f\u56ed\u4e00\u5bb6\u4e09\u5341\u516d\u53e3\u7684\uff0c\u53ea\u6709\u5927\u5e08\u3001\u77f3\u5144\u548c\u8521\u5c11\u4fa0\uff0c\u6709\u8fd9\u4e2a\u672c\u9886\u3002\u201d \u3000\u3000\u201c\u5230\u5e95\uff0c\u4f60\u4eec\u4e09\u4f4d\u4e4b\u4e2d\uff0c\u8c01\u624d\u662f\u51f6\u624b\uff1f\u201d\u8ffd\u547d\u6e38\u76ee\u9021\u89c6\u4e09\u4eba\uff1a\u201c\u8fd8\u662f\u4f60\u4eec\u4e09\u4eba\u90fd\u66fe\u52a8\u624b\uff1f\u201d \u3000\u3000\u77f3\u8001\u5e7a\u7728\u7728\u773c\u775b\u9053\uff1a\u201c\u8ffd\u547d\u4e09\u7237\u53ef\u67e5\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u4e5f\u795e\u8272\u4e0d\u53d8\uff1a\u201c\u88ab\u4e09\u6355\u5934\u70b9\u540d\uff0c\u4e5f\u4e0d\u77e5\u662f\u8363\u8000\u52a0\u8eab\uff0c\u8fd8\u662f\u5927\u7978\u4e34\u5934\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\uff0c\u4f60\u95ee\u6211\u4eec\uff0c\u6211\u4eec\u95ee\u8c01\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u559f\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u5b5f\u5927\u5b88\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8c01\u624d\u662f\u51f6\u624b\uff0c\u53ea\u6015\u6ca1\u6709\u4eba\u80fd\u8bf4\u5f97\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u5ffd\u9053\uff1a\u201c\u8fd8\u662f\u6709\u4eba\u53ef\u4ee5\u8bf4\u5f97\u4e0a\u6765\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u5947\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u5b5f\u968f\u56ed\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u5df2\u7ecf\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u8ffd\u547d\u60a0\u7136\u4ed6\u8bf4\u9053\uff1a\u201c\u5982\u679c\u4ed6\u5df2\u6b7b\u4e86\uff0c\u90a3\u4e48\uff0c\u7ad9\u5728\u6211\u8eab\u8fb9\u7684\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u8ffd\u547d\u8fd9\u53e5\u8bdd\u4e00\u51fa\u53e3\uff0c\u5168\u90e8\u4eba\u7684\u6ce8\u610f\u529b\uff0c\u90fd\u96c6\u4e2d\u5728\u90a3\u540d\u5a01\u4eea\u5802\u5802\u3001\u76d8\u53d1\u957f\u9aef\u7684\u6c49\u5b50\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u6e05\u4e86\u6e05\u5589\u5499\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u597d\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u770b\u76f4\u4e86\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5df2\u7ecf\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u662f\u6b7b\u4e86\uff0c\u201d\u6c49\u5b50\u5ffd\u7136\u6252\u5f00\u4e86\u81ea\u5df1\u7684\u524d\u895f\uff0c\u4ed6\u5c48\u8098\u65f6\u5df2\u975e\u5e38\u4e0d\u4fbf\uff0c\u80f8\u524d\u8d6b\u7136\u6709\u4e00\u9053\u51c4\u5389\u7684\u4f24\u75d5\uff01\u201c\u6070\u5de7\u6211\u7684\u5fc3\u810f\u6709\u5f02\u4e8e\u5e38\u4eba\uff0c\u5fc3\u623f\u504f\u53f3\uff0c\u6240\u4ee5\u90a3\u4e00\u51fb\uff0c\u6b6a\u4e86\u534a\u5bf8\uff0c\u6211\u8fd8\u5269\u4e00\u53e3\u6c14\uff0c\u4fbf\u6b7b\u4e0d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u54ac\u7259\u5207\u9f7f\u5730\u9053\uff1a\u201c\u5982\u679c\u6211\u4e5f\u6b7b\u4e86\uff0c\u51f6\u624b\u5c31\u53ef\u4ee5\u900d\u9065\u6cd5\u5916\u4e86\uff0c\u6240\u4ee5\u6211\u66f4\u4e0d\u80fd\u6b7b\u3002\u201d \u3000\u3000\u4e03\u53d1\u4e5f\u76ee\u5b9a\u53e3\u5446\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u662f\u5b5f\u968f\u56ed\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u5b5f\u968f\u56ed\uff0c\u8c01\u624d\u662f\u5b5f\u968f\u56ed\uff1f\u201d\u90a3\u6c49\u5b50\u60e8\u7b11\u9053\uff0c\u201c\u5b5f\u968f\u56ed\u9047\u4e0a\u8fd9\u6837\u7684\u4e8b\uff0c\u8c01\u90fd\u4e0d\u987e\u610f\u5f53\u5b5f\u968f\u56ed\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u9759\u4e86\u4e0b\u6765\uff0c\u8138\u4e0a\u795e\u8272\uff0c\u60ca\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u8ffd\u547d\u5ffd\u9053\uff1a\u201c\u6211\u60f3\uff0c\u5927\u5bb6\u90fd\u5df2\u660e\u767d\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6709\u6b7b\uff0c\u73b0\u5728\uff0c\u5c31\u7b49\u4f60\u6307\u51fa\u8c01\u624d\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u5b5f\u968f\u56ed\u770b\u7740\u5728\u573a\u7684\u6bcf\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u90a3\u5728\u665a\u4e0a\uff0c\u51f6\u624b\u662f\u8499\u9762\u7684\uff0c\u53ef\u662f\uff0c\u4ed6\u7684\u8eab\u5f62\uff0c\u6211\u4f9d\u7a00\u53ef\u4ee5\u8ba4\u5f97\u51fa\u6765\u3002\u201d\u5b5f\u968f\u56ed\u5389\u58f0\u9053\uff0c\u201c\u6613\u5bb9\u672f\u6700\u591a\u53ea\u80fd\u9a97\u9a97\u4e0d\u76f8\u719f\u7684\u4eba\uff0c\u6216\u53ea\u80fd\u7792\u9a97\u4e00\u65f6\uff0c\u5374\u7792\u4e0d\u8fc7\u6211\u4eec\u8fd9\u4e9b\u884c\u5bb6\uff01\u201d \u3000\u3000\u201c\u6613\u5bb9\u672f\u5c24\u5176\u96be\u4ee5\u5728\u8eab\u5f62\u4e0a\u8bb9\u4eba\uff01\u6613\u5bb9\uff0c\u81f3\u591a\u53ef\u4ee5\u9c7c\u76ee\u6df7\u73e0\uff0c\u4e0d\u80fd\u4ee5\u5047\u4e71\u771f\uff0c\u5f88\u591a\u6b66\u6797\u4f20\u8bf4\u91cc\u65e0\u6687\u53ef\u88ad\u7684\u6613\u5bb9\u624b\u6bb5\uff0c\u5176\u5b9e\u53ea\u662f\u8bf4\u8005\u7684\u51ed\u7a7a\u60f3\u8c61\u3002\u201d\u8ffd\u547d\u9894\u9996\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u51f6\u624b\u7684\u8eab\u5f62\u6700\u50cf\u8c01\uff1f\u201d \u3000\u3000\u5b5f\u968f\u56ed\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u6307\u7684\u662f\u4e03\u53d1\u5927\u5e08\u3002 \u3000\u3000\u4e03\u53d1\u5927\u5e08\uff0c\u53c8\u60ca\u53c8\u6012\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u957f\u53f9\u9053\uff1a\u201c\u4e09\u5e08\u5f1f\uff0c\u4f60\u600e\u4e48\u80fd\u505a\u51fa\u8fd9\u7b49\u4e8b\u60c5\u6765\u2026\u2026\u201d \u3000\u3000\u4e03\u53d1\u5ffd\u7136\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4ef0\u5929\u72c2\u7b11\u3002 \u3000\u3000\u201c\u539f\u6765\u4f60\u4eec\u90fd\u662f\u5408\u5728\u4e00\u8d77\u6765\u5751\u6211\u7684\uff01\u201d\u4e03\u53d1\u8c6a\u7b11\u9053\uff0c\u201c\u8fd9\u6837\u8d2b\u50e7\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff01\u201d \u3000\u3000\u77f3\u65ad\u7709\u7b2c\u4e00\u4e2a\u5c31\u8df3\u4e86\u8d77\u6765\uff1a\u201c\u8d3c\u79c3\u9a74\uff01\u539f\u6765\u662f\u4f60\u5e72\u7684\u597d\u4e8b\uff0c\u4f60\u5bb3\u5f97\u6211\u4eec\u51e0\u4e4e\u8981\u66ff\u4f60\u9876\u7f6a\uff01\u201d \u3000\u3000\u4e03\u53d1\u7985\u5e08\u7684\u77ed\u53d1\u6839\u6839\u7ad6\u7acb\u5982\u621f\uff0c\u4e00\u5b57\u4e00\u53e5\u5730\u9053\uff1a\u201c\u8d2b\u50e7\u843d\u5165\u4f60\u4eec\u7684\u5c40\u91cc\uff0c\u65e0\u8bdd\u53ef\u8bf4\uff01\u201d \u3000\u3000\u201c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u76c2\u968f\u56ed\u5ffd\u9053\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u53ea\u5f85\u5927\u4eba\u4e00\u58f0\u4ee4\u4e0b\u3002\u201d \u3000\u3000\u201c\u51f6\u624b\u7684\u8eab\u5f71\u4e0d\u9519\u662f\u50cf\u4e03\u53d1\u5927\u5e08\uff0c\u201d\u5b5f\u968f\u56ed\u9053\uff0c\u201c\u53ef\u662f\u90a3\u51f6\u624b\u8bf4\u8bdd\u7684\u58f0\u8c03\uff0c\u5374\u5f88\u50cf\u8fd9\u4f4d\u59d3\u8521\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4f17\u4eba\u7684\u76ee\u5149\uff0c\u53c8\u671b\u5411\u8521\u65cb\u949f\u3002 \u3000\u3000\u8521\u65cb\u949f\u6478\u6478\u9f3b\u5b50\uff1a\u201c\u4f60\u7684\u5934\u53d1\u5f88\u957f\u3002\u201d \u3000\u3000\u5b5f\u968f\u56ed\u9053\uff1a\u201c\u6211\u4e00\u5411\u4e0d\u559c\u6b22\u526a\u53d1\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u7684\u820c\u5934\u4e00\u5b9a\u66f4\u957f\u3002\u201d \u3000\u3000\u5b5f\u968f\u56ed\u5c45\u7136\u4e5f\u8138\u4e0d\u6539\u5bb9\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u5148\u524d\u6709\u51a4\uff1f\u201d \u3000\u3000\u5b5f\u968f\u56ed\u9053\uff1a\u201c\u5728\u6740\u6211\u5168\u5bb6\u4e4b\u524d\uff0c\u54b1\u4eec\u65e0\u51a4\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u6709\u4ec7\uff1f\u201d \u3000\u3000\u5b5f\u968f\u56ed\u6447\u9996\u3002 \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u90a3\u6211\u60f3\u4e0d\u900f\u4f60\u4e3a\u4f55\u8981\u8bec\u9677\u6211\u3002\u50cf\u4f60\u8fd9\u79cd\u4eba\u3002\u820c\u5934\u8981\u4e0d\u662f\u592a\u957f\uff0c\u600e\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u60f3\u8bec\u9677\u4f60\uff0c\u201d\u5b5f\u968f\u56ed\u9053\uff0c\u201c\u53ef\u662f\u6211\u660e\u660e\u542c\u89c1\u662f\u4f60\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u77f3\u65ad\u7709\u5ffd\u9053\uff1a\u201c\u51f6\u624b\u5230\u5e95\u6709\u51e0\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u7b49\u4e00\u7b49\u3002\u201d\u5b5f\u968f\u56ed\u4e0d\u56de\u7b54\u4ed6\u7684\u95ee\u8bdd\uff0c\u53cd\u8bf4\uff0c\u201c\u6709\u4e00\u70b9\u5f88\u91cd\u8981\uff1a\u51f6\u624b\u7684\u6b66\u5668\uff0c\u5374\u662f\u4e00\u67c4\u94a2\u53c9\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u4e00\u51fa\uff0c\u6bcf\u4e2a\u4eba\u7684\u76ee\u5149\uff0c\u90fd\u843d\u5728\u77f3\u65ad\u7709\u80cc\u540e\u659c\u63d2\u7684\u94a2\u53c9\u4e0a\u3002 \u3000\u3000\u77f3\u65ad\u7709\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u201c\u7edd\u5bf9\u4e0d\u53ef\u80fd\u3002\u201d\u77f3\u65ad\u7709\u5927\u58f0\u5730\u9053\uff0c\u201c\u4ed6\u8bf4\u8c0e\uff01\u201d \u3000\u3000\u5b5f\u968f\u56ed\u53cd\u95ee\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u77f3\u65ad\u7709\u6012\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u662f\u51f6\u624b\uff01\u201d \u3000\u3000\u5b5f\u968f\u56ed\u75be\u95ee\uff1a\u201c\u4f60\u7684\u786e\u7528\u8fd9\u67c4\u53c9\u6740\u6211\u3002\u201d \u3000\u3000\u201c\u5b5f\u5bb6\u7684\u4eba\u6839\u672c\u5c31\u4e0d\u662f\u6b7b\u5728\u94a2\u53c9\u4e0b\uff0c\u201d\u77f3\u65ad\u7709\u543c\u9053\uff0c\u201c\u5982\u679c\u662f\u6211\u52a8\u7684\u624b\uff0c\u4ed6\u7684\u80f8\u819b\u5c82\u6b62\u4e00\u4e2a\u8840\u6d1e\u800c\u5df2\uff01\u201d \u3000\u3000\u8ffd\u547d\u5ffd\u9053\uff1a\u201c\u53ef\u662f\u5728\u573a\u4e00\u540d\u62bc\u89e3\u5dee\u5b98\uff0c\u7684\u786e\u662f\u80cc\u540e\u7740\u4e86\u4e00\u53c9\uff0c\u7834\u80f8\u800c\u6b81\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u522b\u542b\u8840\u55b7\u4eba\uff01\u201d\u77f3\u65ad\u7709\u6012\u4e0d\u53ef\u904f\uff0c\u201c\u62bc\u89e3\u7684\u4e03\u540d\u5dee\u5f79\uff0c\u65e0\u4e00\u662f\u88ab\u53c9\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u6709\u8bc1\u636e\uff01\u201d\u5b5f\u968f\u56ed\u7a81\u7136\u5927\u58f0\u9053\uff0c\u201c\u4f60\u522b\u51b2\u52a8\uff01\u201d \u3000\u3000\u4e03\u53d1\u3001\u65ad\u7709\u3001\u8521\u65cb\u949f\u4e00\u9f50\u95ee\uff1a\u201c\u4ec0\u4e48\u8bc1\u636e\uff1f\uff01\u201d \u3000\u3000\u5b5f\u968f\u56ed\u5ffd\u7136\u7b11\u4e86\uff1a\u201c\u6740\u4eba\u7684\u8bc1\u636e\u3002\u201d \u3000\u3000\u4ed6\u7b11\u610f\u8be1\u5f02\uff0c\u7a81\u7136\u51fa\u624b\uff0c\u6293\u4f4f\u987e\u4f5b\u5f71\u7684\u6709\u624b\uff0c\u201c\u5636\u201d\u5730\u4e00\u58f0\uff0c\u626f\u4e0b\u4e86\u4ed6\u4e00\u7247\u8896\u5b50\u3002 \u3000\u3000\u53ea\u89c1\u987e\u4f5b\u5f71\u53f3\u8155\u4e0a\uff0c\u8d6b\u7136\u6709\u4e00\u9053\u4f24\u75d5\uff0c\u65b0\u75f4\u521a\u7ed3\uff0c\u5c1a\u672a\u75ca\u6108\u3002 \u3000\u3000\u5b5f\u968f\u56ed\u5389\u58f0\u9053\uff1a\u201c\u90a3\u5929\u4ed6\u6697\u7b97\u6211\uff0c\u6211\u8d1f\u4f24\u4e4b\u4f59\uff0c\u4e5f\u523a\u4e86\u51f6\u624b\u4e00\u5251\uff0c\u5c31\u5728\u4ed6\u7684\u53f3\u8155\u4e0a\u3002\u201d \u3000\u3000\u77f3\u65ad\u7709\u731b\u7136\u559d\u9053\uff1a\u201c\u597d\u5bb6\u4f19\uff01\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u987e\u4f5b\u5f71\u7528\u529b\u4e00\u6323\uff0c\u5b5f\u968f\u56ed\u53cc\u624b\u64d2\u62ff\uff0c\u7d27\u7d27\u4e0d\u653e\uff0c\u987e\u4f5b\u5f71\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u4e0d\u662f\u6211\uff01\u51fa\u4e8b\u90a3\u5929\uff0c\u6211\u6839\u672c\u4e0d\u5728\u67af\u67f3\u5c6f\uff01\u201d \u3000\u3000\u77f3\u65ad\u7709\u53f1\u9053\uff1a\u201c\u53e3\u8bf4\u65e0\u51ed\uff01\u4f60\u8fd8\u662f\u8d81\u65e9\u8ba4\u4e86\uff01\u201d \u3000\u3000\u987e\u4f5b\u5f71\u6323\u624e\u9053\uff1a\u201c\u6211\u6709\u4eba\u8bc1\u3002\u201d \u3000\u3000\u8ffd\u547d\u5373\u95ee\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u6025\u5f97\u989d\u4e0a\u5192\u6c57\uff1a\u201c\u6e38\u516c\u5b50\u3002\u201d \u3000\u3000\u77f3\u65ad\u7709\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e00\u4f19\u4eba\uff0c\u4ed6\u81ea\u7136\u4f1a\u5e2e\u4f60\u8bf4\u597d\u8bdd\uff01\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4eba\u4e00\u5b9a\u4e0d\u4f1a\u5e2e\u6211\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u8fd9\u6b21\u8f6e\u5230\u5b5f\u968f\u56ed\u95f4\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u4f60\u513f\u5b50\u3002\u201d\u987e\u4f5b\u5f71\u5fd9\u4e0d\u8fed\u5730\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u5b5f\u6055\u660e\u3002\u201d \u3000\u3000\u201c\u4ed6\uff1f\u201d\u5b5f\u968f\u56ed\u4e00\u6014\u3002 \u3000\u3000\u201c\u8840\u6848\u90a3\u5929\u665a\u4e0a\uff0c\u201d\u987e\u4f5b\u5f71\u5982\u5373\u5c06\u6c89\u6eba\u7684\u4eba\u6293\u4f4f\u4e00\u622a\u6d6e\u6728\uff0c\u201c\u6211\u5c31\u8ddf\u4ed6\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5b5f\u968f\u56ed\u6014\u6014\u5730\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u2026\u2026\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u5343\u771f\u4e07\u786e\u3002\u201d\u987e\u4f5b\u5f71\u5927\u58f0\u9053\u3002 \u3000\u3000\u201c\u4e0d\u5bf9\uff0c\u201d\u77f3\u65ad\u7709\u543c\u7740\u8bf4\uff0c\u201c\u4ed6\u8bf4\u7684\u5168\u662f\u9a97\u4eba\u7684\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u8ffd\u547d\u7acb\u5373\u95ee\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u5b5f\u6055\u660e\u5df2\u7ecf\u6b7b\u4e86\u201d\u201d\u77f3\u65ad\u7709\u7cbe\u660e\u8001\u7ec3\u4ed6\u8bf4\uff0c\u201c\u5b5f\u6055\u660e\u5c31\u6b7b\u5728\u8840\u6848\u7684\u73b0\u573a\uff0c\u4ed6\u2014 \u3000\u3000\u2014\u201d \u3000\u3000\u5ffd\u7136\u4e4b\u95f4\uff0c\u4ed6\u53d1\u73b0\u4e0d\u5927\u5bf9\u52b2\u3002 \u3000\u3000\u8c01\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4eba\u4eba\u90fd\u7528\u4e00\u79cd\u5947\u602a\u7684\u773c\u8272\u671b\u7740\u4ed6\u3002 \u3000\u3000\u773c\u795e\u91cc\u6709\u9119\u5937\u3001\u6709\u6124\u6012\u3001\u6709\u5e78\u707e\u4e50\u7978\u3002\u6709\u604d\u7136\u5927\u609f\u3002 \u3000\u3000\u4ed6\u4e5f\u7acb\u5373\u4f4f\u53e3\u3002 \u3000\u3000\u4ed6\u5df2\u660e\u767d\u539f\u7531\u3002 \u3000\u3000\u4ed6\u8bf4\u5f97\u5927\u591a\u4e86\u3002 \u3000\u3000\u201c\u5c31\u7b97\u4ed6\u5728\u8bf4\u8c0e\uff0c\u201d\u8ffd\u547d\u5b57\u53e5\u6e05\u6670\u5730\u9053\uff0c\u201c\u53ef\u662f\uff0c\u4f60\u65e2\u6ca1\u5230\u8fc7\u8840\u6848\u7684\u73b0\u573a\uff0c\u53c8\u600e\u4e48\u77e5\u9053\u5b5f\u6055\u660e\u5c31\u6b7b\u5728\u5176\u95f4\u5462\uff1f\u201d \u3000 \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u6a2a\u5200\u7acb\u9a6c\uff0c\u9189\u5367\u5c71\u5c97 \u3000 \u3000\u3000\u77f3\u65ad\u7709\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u5f97\u5341\u5206\u523b\u610f\uff0c\u4ee5\u81f4\u8c01\u90fd\u77e5\u9053\u4ed6\u5728\u7b11\u3002\u4ed6\u90a3\u5c0f\u5c0f\u7684\u4e00\u5f20\u8138\uff0c\u4e94\u5b98\u90fd\u6324\u5728\u4e00\u8d77\uff0c\u5c0f\u80e1\u5b50\uff0c\u4eff\u4f5b\u4e5f\u98de\u5230\u773c\u89d2\u6210\u4e86\u7709\u6bdb\u3002 \u3000\u3000\u201c\u6211\u8fd9\u756a\u53ea\u662f\u7528\u6765\u8bd5\u63a2\u4ed6\u662f\u4e0d\u662f\u5728\u8bf4\u5047\u8bdd\uff1b\u201d\u77f3\u65ad\u7709\u8be1\u7b11\u7740\u8bf4\uff0c\u201c\u8bf8\u4f4d\u600e\u4e48\u53cd\u8fc7\u6765\u95ee\u6211\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u4f60\u8fd9\u53e5\u8bdd\u662f\u5e2e\u6211\u8bd5\u63a2\u4ed6\u7684\uff0c\u201d\u8ffd\u547d\u4e5f\u7b11\u7740\uff0c\u53ef\u662f\u8bed\u950b\u6bd4\u5200\u5251\u8fd8\u950b\u5229\uff1a\u201c\u53ef\u662f\u6211\u600e\u4e48\u90fd\u60f3\u4e0d\u900f\uff0c\u4f60\u662f\u5982\u4f55\u53ef\u4ee5\u8fd9\u822c\u80af\u5b9a\uff0c\u5b5f\u968f\u56ed\u5168\u5bb6\u90fd\u4e0d\u662f\u6b7b\u4e8e\u53c9\u4e0b\u7684\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u80fd\u56de\u7b54\u6211\u8fd9\u4e2a\u95ee\u9898\uff0c\u4e0d\u59a8\u8fde\u4e0b\u4e00\u4e2a\u95ee\u9898\u4e00\u9f50\u4f5c\u7b54\uff1a\u201d\u8ffd\u547d\u62b9\u62b9\u5634\u8fb9\u7684\u9152\u6e0d\u9053\uff0c\u201c\u4f60\u53c8\u662f\u600e\u6837\u77e5\u9053\u62bc\u89e3\u5b5f\u5bb6\u7684\u4eba\uff0c\u4e00\u5171\u662f\u4e03\u540d\u5dee\u5f79\u5462\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u55b7\u58f0\u63a5\u9053\uff0c\u201c\u62bc\u89e3\u7684\u5dee\u5f79\uff0c\u6709\u4e09\u4eba\u5728\u4e00\u8def\u4e0a\u6839\u672c\u6ca1\u4eae\u51fa\u8eab\u4efd\uff0c\u4e5f\u4e0d\u7a7f\u516c\u670d\uff0c\u5c31\u7b97\u5728\u5b5f\u6848\u53d1\u751f\u4e4b\u540e\uff0c\u53bf\u8859\u4e5f\u53ea\u516c\u5e03\u727a\u7272\u4e86\u4e94\u540d\u5b98\u5dee\uff0c\u5728\u4e0b\u771f\u8981\u5411\u4f60\u8bf7\u6559\uff0c\u4f55\u4ee5\u77e5\u9053\u5f97\u8fd9\u822c\u6e05\u695a\uff1f\u201d \u3000\u3000\u8ffd\u547d\u6253\u4e86\u4e00\u4e2a\u9152\u5443\uff0c\u9053\uff0c\u201c\u5f53\u665a\u8840\u6848\u73b0\u573a\uff0c\u4e5f\u8bb8\u51f6\u624b\u751f\u6050\u6709\u6f0f\u7f51\u4e4b\u9c7c\uff0c\u66fe\u9010\u4e00\u7ffb\u67e5\u8fc7\u5c38\u9996\uff0c\u5dee\u5f79\u8eab\u4e0a\u7684\u516c\u6587\u548c\u4ee4\u724c\uff0c\u4e5f\u88ab\u626f\u4e86\u51fa\u6765\uff0c\u4ed6\u5f53\u7136\u77e5\u9053\u62bc\u89e3\u7684\u603b\u5171\u6709\u51e0\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u6211\u503c\u5f97\u6000\u7591\uff0c\u6211\u4e5f\u4e0d\u8fc7\u662f\u4f60\u4eec\u6000\u7591\u7684\u4eba\u4e4b\u4e00\uff1b\u201d\u77f3\u65ad\u7709\u6307\u7740\u4e03\u53d1\u5927\u5e08\u3001\u8521\u65cb\u949f\u3001\u987e\u4f5b\u5f71\u7b49\u9053\uff0c\u201c\u4ed6\u4eec\u4e5f\u662f\u53ef\u7591\u7684\u4eba\uff0c\u4f60\u4eec\u6ca1\u6709\u7406\u7531\u65ad\u5b9a\u662f\u6211\u5e72\u7684\u3002\u201d \u3000\u3000\u8ffd\u547d\u51b7\u7b11\u53f1\u9053\uff1a\u201c\u77f3\u8001\u5e7a\uff0c\u662f\u4e0d\u662f\u4f60\u5e72\u7684\uff0c\u4f60\u5fc3\u91cc\u81ea\u662f\u660e\u767d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u5ffd\u9053\uff1a\u201c\u4ed6\u662f\u6709\u8bed\u75c5\uff0c\u53ef\u662f\uff0c\u8fd9\u91cc\u4eba\u4eba\u90fd\u53ef\u7591\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8ba4\u4e3a\u662f\u4ed6\uff1f\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\u53c8\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u5b5f\u5927\u4eba\u8bf4\u6211\u7684\u58f0\u97f3\u5f88\u50cf\u51f6\u624b\uff0c\u51f6\u624b\u7684\u8eab\u5f62\u8ddf\u4e03\u53d1\u5927\u5e08\u4e00\u6837\uff0c\u800c\u987e\u4f5b\u5f71\u624b\u8155\u4e0a\u7684\u4f24\u75d5\u4e5f\u4e0e\u5b5f\u5927\u4eba\u6240\u8bf4\u7684\u543b\u5408\uff0c\u6211\u4eec\u4eba\u4eba\u90fd\u6709\u53ef\u80fd\u662f\u51f6\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u201d\u987e\u4f5b\u5f71\u62a8\u8d77\u8896\u5b50\uff0c\u5de6\u624b\u5728\u53f3\u8155\u4e0a\u4e00\u62b9\uff0c\u90a3\u9053\u4f24\u75d5\u7acb\u5373\u5c31\u6de1\u4e86\uff0c\u518d\u62b9\u51e0\u62b9\uff0c\u4f24\u75d5\u5c31\u5947\u8ff9\u822c\u6d88\u5931\u4e86\uff0c\u201c\u6211\u6839\u672c\u6ca1\u6709\u53d7\u4f24\uff0c\u6613\u5bb9\u672f\u867d\u9a97\u4e0d\u8fc7\u660e\u773c\u4eba\uff0c\u4f46\u8981\u5212\u9053\u4f24\u75d5\u5012\u4e0d\u662f\u4ef6\u96be\u4e8b\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u51f6\u624b\u7684\u8eab\u5f62\u5e76\u4e0d\u50cf\u4e03\u53d1\u5927\u5e08\uff0c\u201d\u8521\u65cb\u949f\u604d\u609f\u4f3c\u7684\u9053\uff0c\u201c\u51f6\u624b\u7684\u58f0\u97f3\u4e5f\u5e76\u4e0d\u4f3c\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5bf9\u4e86\u3002\u201d\u8ffd\u547d\u8d5e\u8d4f\u4f3c\u7684\u9053\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u4ecd\u7136\u4e0d\u660e\u767d\uff0c\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u5b5f\u5927\u4eba\u4e3a\u4f55\u8981\u8fd9\u6837\u8bf4\uff1f\u201d \u3000\u3000\u5b5f\u968f\u56ed\u6de1\u6de1\u5730\u4e00\u7b11\u3002\u4ed6\u7684\u7b11\u5bb9\u4f3c\u6781\u5ea6\u5e73\u9759\uff0c\u53c8\u4f3c\u6781\u5ea6\u75af\u72c2\u3002\u5947\u602a\u7684\u662f\uff0c\u4e16\u4e0a\u7684\u201c\u4e24\u6781\u201d\uff0c\u5f80\u5f80\u975e\u5e38\u8fd1\u4f3c\uff0c\u5927\u5978\u4e0e\u5927\u5fe0\uff0c\u5f88\u53ef\u80fd\u6210\u4e00\u4f53\uff0c\u81f3\u771f\u4e0e\u81f3\u5047\uff0c\u6709\u65f6\u5019\u662f\u540c\u4e00\u56de\u4e8b\uff0c\u6709\u4eba\u8bf4\u4eba\u4e00\u76f4\u5f80\u524d\u8d70\uff0c\u53ef\u80fd\u4f1a\u8d70\u5230\u540e\u5934\uff0c\u6b63\u5982\u4e00\u76f4\u5411\u5de6\u8d70\uff0c\u53ef\u80fd\u4f1a\u5230\u4e86\u53f3\u8fb9\u7684\u5f00\u5934\u3002\u5b5f\u968f\u56ed\u7684\u7b11\uff0c\u5c31\u7b97\u4e24\u8005\u7686\u4e0d\u662f\uff0c\u4e5f\u662f\u7f6e\u8eab\u4e8b\u5916\u7684\u4e00\u79cd\u6de1\u6f20\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u5728\u5168\u5bb6\u88ab\u6740\u540e\uff0c\u8fd8\u80fd\u5982\u6b64\u6f20\u4e0d\u5173\u5fc3\u3002 \u3000\u3000\u8521\u65cb\u949f\u4e00\u76f4\u89c9\u5f97\u6709\u4e9b\u4e0d\u59a5\uff0c\u53ef\u662f\u5230\u5e95\u662f\u4ec0\u4e48\u4e0d\u59a5\uff0c\u4ed6\u4e5f\u8bf4\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u624d\u53d1\u89c9\uff0c\u5b5f\u968f\u56ed\u4ece\u5f00\u59cb\u5230\u73b0\u5728\uff0c\u4e00\u76f4\u672a\u66fe\u6fc0\u52a8\u8fc7\u3002 \u3000\u3000\u66f4\u6ca1\u6709\u51b2\u52a8\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u6839\u672c\u5c31\u4e0d\u662f\u5b5f\u968f\u56ed\u3002\u201d\u8ffd\u547d\u7ec8\u4e8e\u8bf4\u3002 \u3000\u3000\u201c\u4ed6\u4e0d\u662f\u5b5f\u968f\u56ed\uff0c\u5b5f\u968f\u56ed\u65e9\u5df2\u6b7b\u4e86\uff0c\u5c31\u6b7b\u5728\u8840\u6848\u91cc\uff0c\u201d\u8ffd\u547d\u8bf4\uff0c\u201c\u6211\u627e\u4ed6\u524d\u6765\uff0c\u4e3a\u7684\u662f\u8981\u628a\u63e1\u4f4f\u4e00\u4e2a\u8981\u5bb3\uff1a\u5982\u679c\u4f60\u4eec\u4e09\u4eba\u4e4b\u4e2d\uff0c\u5176\u4e2d\u4e00\u4e2a\u662f\u771f\u51f6\uff0c\u5fc5\u5b9a\u4f1a\u77e5\u9053\uff0c\u4f60\u4eec\u5df2\u4eb2\u624b\u6740\u6b7b\u5b5f\u968f\u56ed\uff0c\u773c\u524d\u8fd9\u4eba\uff0c\u51b3\u4e0d\u662f\u5b5f\u968f\u56ed\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4e09\u6355\u5934\u8ddf\u6211\u4eec\u7ea6\u597d\uff0c\u5e26\u4e86\u8fd9\u4f4d\u670b\u53cb\u6765\uff0c\u8bf4\u8fd9\u4e00\u756a\u8bdd\uff0c\u4f7f\u4eba\u4eba\u90fd\u88ab\u7591\u4e3a\u51f6\u624b\uff0c\u4ed6\u6240\u80ea\u5217\u7684\u7591\u70b9\uff0c\u8bf1\u4f7f\u51f6\u624b\u63d0\u51fa\u8840\u6848\u73b0\u573a\u7684\u6709\u529b\u8fa9\u8bc1\uff1b\u201d\u987e\u4f5b\u5f71\u63a5\u8ffd\u547d\u7684\u8bdd\u9898\uff1a\u201c\u7136\u540e\uff0c\u5176\u4e2d\u53c8\u4ee5\u6211\u5acc\u7591\u6700\u91cd\uff0c\u51f6\u624b\u81ea\u7136\u5df4\u4e0d\u5f97\u843d\u4e95\u4e0b\u77f3\uff0c\u628a\u6211\u5b9a\u6848\uff0c\u5fc5\u4f1a\u62c6\u7834\u6211\u4eba\u8bc1\u4e0a\u7684\u8c0e\u8a00\u4e0b\u2014\u2014\u6b8a\u4e0d\u77e5\u4ed6\u5728\u4e3a\u81ea\u5df1\u8fa9\u89e3\u7684\u65f6\u5019\uff0c\u6b63\u662f\u9732\u51fa\u72d0\u72f8\u5c3e\u5df4\u4e4b\u9645\uff1b\u4ed6\u5728\u62c6\u7a7f\u6211\u7684\u8c0e\u8a00\u7684\u65f6\u5019\uff0c\u5c31\u662f\u4ed6\u7684\u8c0e\u8a00\u88ab\u63ed\u7a7f\u4e4b\u65f6\u3002\u201d \u3000\u3000\u201c\u56e0\u6b64\uff0c\u51f6\u624b\u662f\u6211\uff1b\u201d\u77f3\u65ad\u7709\u6168\u53f9\u4e5f\u4f3c\u7684\u9053\uff1a\u201c\u6211\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u201c\u4f60\u6740\u5b5f\u968f\u56ed\u5168\u5bb6\uff0c\u7684\u786e\u6ca1\u6709\u7528\u8fc7\u4f60\u6210\u540d\u7684\u6b66\u5668\uff0c\u4f46\u6bcf\u4e2a\u4eba\u90fd\u6b7b\u6cd5\u4e0d\u540c\uff0c\u624b\u6cd5\u592a\u50cf\u4f60\u6240\u4e3a\u4e86\uff0c\u800c\u4f60\u53c8\u592a\u6076\u540d\u662d\u5f70\u4e86\uff0c\u201d\u8ffd\u547d\u4f3c\u4e5f\u4e3a\u4ed6\u60cb\u60dc\u5730\u9053\uff0c\u201c\u53ef\u662f\u6211\u4eec\u6848\u5b50\u529e\u591a\u4e86\uff0c\u4e5f\u6709\u4e9b\u79ef\u4e60\uff0c\u8b6c\u5982\uff1a\u5e38\u4ee5\u4e3a\u8d8a\u4e0d\u53ef\u80fd\u7684\u4eba\uff0c\u624d\u662f\u51f6\u624b\uff0c\u4f60\u592a\u50cf\u51f6\u624b\u4e86\uff0c\u6240\u4ee5\u6211\u6700\u6000\u7591\u7684\u53cd\u800c\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u521a\u624d\u4e0d\u662f\u592a\u591a\u8bdd\uff0c\u4f60\u8fd8\u662f\u4e0d\u80fd\u80af\u5b9a\u662f\u6211\uff1b\u201d\u77f3\u8001\u5e7a\u867d\u7136\u6ca1\u6709\u7709\u6bdb\uff0c\u4f46\u7709\u5fc3\u5374\u76b1\u4e86\u8d77\u6765\uff0c\u201c\u75c5\u4ece\u53e3\u5165\uff0c\u7978\u4ece\u53e3\u51fa\uff0c\u8fd9\u53e5\u8bdd\u771f\u4e00\u70b9\u513f\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u5df2\u627f\u8ba4\u4e86\uff0c\u8be5\u6211\u95ee\u8bdd\u4e86\u3002\u201d\u8ffd\u547d\u9053\u3002 \u3000\u3000\u201c\u4f60\u95ee\u95ee\u770b\u3002\u201d\u77f3\u65ad\u7709\u9053\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u5b5f\u968f\u56ed\u5168\u5bb6\uff0c\u8fde\u62bc\u89e3\u7684\u5dee\u5b98\u90fd\u4e0d\u653e\u8fc7\u3002\u201d \u3000\u3000\u201c\u5c31\u8fd9\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\uff0c\u5f15\u6211\u79bb\u5f00\u7684\u8499\u9762\u9ed1\u8863\u4eba\uff0c\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u6ca1\u6709\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u4f60\u53ea\u6709\u4e09\u6761\u8def\u53ef\u8d70\uff1a\u4e00\u662f\u62d2\u6355\uff0c\u6211\u53ea\u597d\u7acb\u5373\u6740\u4e86\u4f60\u3002\u672c\u6765\u4f60\u8fd9\u79cd\u4eba\u5c31\u5f88\u8be5\u6b7b\uff0c\u62bc\u4e0a\u4eac\u5e08\uff0c\u66f4\u6050\u591c\u957f\u68a6\u591a\uff1b\u4e8c\u662f\u5c31\u6355\uff0c\u6211\u62bc\u4f60\u56de\u4eac\u53d7\u5ba1\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u8def\u4e0a\u80af\u5b9a\u4e0d\u4f1a\u5e73\u9759\u201d\u56e0\u4e3a\u4f60\u7684\u4e0a\u7ea7\u6015\u4f60\u8d70\u6f0f\u98ce\u58f0\uff0c\u52bf\u5fc5\u8981\u5c06\u4f60\u706d\u53e3\uff0c\u4f60\u7684\u540c\u4f34\u4e5f\u4f1a\u8bbe\u6cd5\u6551\u4f60\uff1b\u7b2c\u4e09\u6761\u8def\u5c31\u662f\u4f60\u80fd\u9003\u5f97\u8fc7\u6211\u7684\u8ffd\u6355\u3002\u4f60\u9009\u90a3\u4e00\u6761\uff1f\u201d \u3000\u3000\u201c\u4f60\u95ee\u7684\u6211\u90fd\u4e0d\u7b54\uff0c\u4f46\u6709\u4e09\u53e5\u8bdd\u60f3\u8bf4\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u730e\u72ac\u7ec8\u987b\u5c71\u4e0a\u4e27\uff0c\u5c06\u519b\u6700\u540e\u9635\u4e2d\u4ea1\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u53ea\u662f\u5ffd\u7136\u751f\u8d77\u7684\u4e00\u79cd\u5174\u53f9\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u53e5\u5462\uff1f\u201d \u3000\u3000\u201c\u989c\u5915\u771f\u662f\u4e2a\u6f02\u4eae\u7684\u5973\u5b50\uff0c\u53ef\u60dc\u6211\u5f97\u4e0d\u5230\u5979\u3002\u201d \u3000\u3000\u201c\u8fd9\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u4e5f\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u5728\u6628\u665a\u4ee5\u540e\uff0c\u8fd9\u6210\u4e86\u6211\u5fc3\u4e2d\u7684\u4e00\u53e5\u771f\u8bdd\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u53e5\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53e5\u6bd4\u8f83\u6709\u610f\u601d\uff1a\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u4e0d\u77e5\u8bf8\u4f4d\u91cc\u53ef\u6709\u4eba\u4ed7\u4e49\u4ee3\u8f6c\u6211\u80de\u5f1f\u77f3\u5fc3\u80a0\u4e00\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\uff0c\u6211\u4f20\u8fbe\u3002\u201d\u8ffd\u547d\u5373\u9053\u3002 \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\uff0c\u56db\u5927\u540d\u6355\u4e00\u5411\u8a00\u800c\u6709\u4fe1\u3002\u4f60\u53ea\u8981\u544a\u8bc9\u4ed6\uff1a\u5730\u4e45\u5929\u957f\uff0c\u56db\u5b57\u5373\u53ef\u3002\u201d\u77f3\u65ad\u7709\u4e0d\u653e\u5fc3\u7684\u53c8\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u77f3\u5fc3\u80a0\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u2018\u94c1\u77f3\u5fc3\u80a0\uff0c\u5929\u4e0b\u95fb\u540d\u3002\u81ea\u4ece\u2018\u94c1\u3001\u77f3\u3001\u5fc3\u3001\u80a0\u2019\u56db\u5927\u9ad8\u624b\u4e3a\u65b9\u90aa\u771f\u4e00\u4eba\u6240\u8d25\u540e\uff0c\u4e5f\u53ea\u6709\u4ee4\u5f1f\uff0c\u6562\u4e00\u4eba\u72ec\u63fd\u8fd9\u4e2a\u5916\u53f7\u3002\u201d\u8ffd\u547d\u9053\uff0c\u201c\u5c31\u7b97\u4e0d\u77e5\u9053\u4ed6\u4f4f\u5728\u54ea\u91cc\uff0c\u627e\u4ed6\u4e5f\u4e0d\u7b97\u96be\uff0c\u4f60\u5728\u6b64\u65f6\u6b64\u9645\u8fd8\u8bb0\u5f97\u8fd9\u4e2a\u80de\u5f1f\uff0c\u8db3\u89c1\u5c1a\u5ff5\u4eb2\u60c5\uff0c\u8fd9\u5fc5\u7136\u662f\u53e5\u91cd\u8981\u7684\u8bdd\uff0c\u6211\u4e00\u5b9a\u5e26\u5230\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u9519\u662f\u53e5\u91cd\u8981\u7684\u8bdd\uff0c\u867d\u7136\u4f60\u5e76\u4e0d\u660e\u767d\uff1b\u201d\u77f3\u8001\u5e7a\u559f\u7136\u9053\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u9057\u8a00\uff0c\u6211\u4e5f\u53ef\u4ee5\u66ff\u4f60\u8f6c\u5230\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\u3002\u201d\u8ffd\u547d\u8c41\u7136\u9053\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u4f60\u4e00\u5b9a\u80fd\u80dc\u6211\uff1f\u201d\u77f3\u65ad\u7709\u6012\u9053\u3002 \u3000\u3000\u8ffd\u547d\u6367\u575b\u75db\u996e\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u62ff\u8fc7\u8521\u65cb\u949f\u559d\u5269\u7684\u9152\u575b\uff0c\u4e5f\u4ef0\u9996\u9cb8\u541e\u3002 \u3000\u3000\u77f3\u65ad\u7709\u8138\u8272\u9634\u6674\u4e0d\u5b9a\uff0c\u989d\u4e0a\u7709\u5f71\uff0c\u5ffd\u9690\u5ffd\u73b0\uff0c\u5bf9\u8521\u65cb\u949f\u4e0e\u4e03\u53d1\u5927\u5e08\u6da9\u58f0\u9053\uff1a\u201c\u8bb0\u5f97\u6211\u4eec\u5148\u524d\u7684\u7ea6\u5b9a\u5417\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u96be\u602a\u4f60\u4eca\u5929\u4e00\u4e0a\u6765\u5c31\u63d0\u8fc7\u8fd9\u4e2a\u95ee\u9898\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u6414\u6414\u77ed\u53d1\u9053\uff1a\u201c\u6700\u8fd1\u6211\u7684\u8bb0\u5fc6\u529b\u5b9e\u5728\u5f88\u574f\u3002\u65e9\u4e0a\u53bb\u8fc7\u7684\u5730\u65b9\uff0c\u5230\u665a\u4e0a\u5c31\u8bb0\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\u3002\u201d\u77f3\u65ad\u7709\u5c45\u7136\u4e5f\u6d6e\u8d77\u4e86\u4e00\u4e2a\u4e0d\u5c51\u7684\u7b11\u5bb9\uff1a\u201c\u4f60\u4eec\u771f\u662f\u6211\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u771f\u7684\u662f\u597d\u670b\u53cb\uff0c\u201d\u8521\u65cb\u949f\u5766\u8361\u7684\u8bf4\uff0c\u201c\u4f60\u4e00\u65e9\u5c31\u8be5\u76f4\u8ba4\u4e0d\u8bb3\uff0c\u624d\u4e0d\u81f4\u6211\u4eec\u5dee\u4e9b\u66ff\u4f60\u80cc\u9ed1\u9505\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u8fd9\u9ed1\u9505\u5df2\u6446\u660e\u662f\u6211\u7684\u4e86\uff0c\u201d\u77f3\u65ad\u7709\u51b7\u7b11\u9053\uff0c\u201c\u4f60\u4eec\u5f53\u7136\u8c01\u90fd\u4e0d\u5fc5\u80cc\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5bf9\u4e86\uff0c\u4e5f\u8bf4\u9519\u4e86\uff1b\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u9ed1\u9505\u662f\u4f60\u7684\uff0c\u6211\u5f53\u7136\u4e0d\u6350\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u7684\u7ea6\u5b9a\uff0c\u4ecd\u7136\u6709\u6548\u3002\u201d \u3000\u3000\u90a3\u4e2a\u5047\u626e\u5b5f\u968f\u56ed\u7684\u4eba\u5ffd\u7136\u5f80\u540e\u9000\u3002 \u3000\u3000\u4e00\u6b65\u4e00\u6b65\u7684\u5f80\u540e\u9000\u3002 \u3000\u3000\u9000\u5230\u4e86\u4e09\u4e08\u4e4b\u5916\uff0c\u4ed6\u624d\u5411\u8ffd\u547d\u8bf4\uff1b\u201c\u4f60\u8981\u6211\u505a\u7684\u4e8b\uff0c\u6211\u5df2\u7ecf\u505a\u4e86\uff1b\u73b0\u5728\u662f\u4f60\u4eec\u7684\u4e8b\uff0c\u6ca1\u6211\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u662f\u6ca1\u4f60\u7684\u4e8b\u4e86\u3002\u201d\u8ffd\u547d\u5ffd\u53cd\u95ee\u8521\u65cb\u949f\u9053\uff0c\u201c\u5374\u600e\u4e48\u4f1a\u6709\u4f60\u7684\u4e8b\u5462\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u6709\u7ea6\u5b9a\u3002\u201d \u3000\u3000\u8ffd\u547d\u95ee\uff1a\u201c\u4f60\u4eec\uff1f\u201d \u3000\u3000\u65ad\u7709\u77f3\u62a2\u7740\u9053\uff1a\u201c\u4e03\u53d1\u5927\u5e08\u3001\u8521\u5c11\u4fa0\u548c\u6211\u3002\u201d \u3000\u3000\u8ffd\u547d\u53c8\u95ee\uff1a\u201c\u4ec0\u4e48\u7ea6\u5b9a\uff1f\u201d \u3000\u3000\u8521\u65cb\u949f\u9053\uff1a\u201c\u6740\u4f60\u7684\u7ea6\u5b9a\u3002\u201d \u3000\u3000\u8ffd\u547d\u7b11\u4e86\uff1a\u201c\u4f60\u4eec\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u201c\u6709\u4eba\u8981\u6211\u9664\u6389\u4f60\uff0c\u4f46\u6211\u4e00\u5411\u53ea\u627e\u4eba\u51b3\u6597\uff0c\u4e0d\u6740\u4eba\uff0c\u9664\u975e\u201c\u9664\u975e\u4f60\u5728\u6bd4\u6597\u4e2d\uff0c\u63a7\u5236\u4e0d\u4f4f\u3002\u201d\u8ffd\u547d\u7b11\u7740\u63a5\u9053\uff0c\u201c\u6240\u4ee5\u6211\u4e0d\u4f1a\u7ed9\u4f60\u673a\u4f1a\u7684\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u673a\u4f1a\uff1f\u201d \u3000\u3000\u201c\u6740\u6211\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u53ea\u627e\u4f60\u51b3\u6597\uff0c\u201d\u8521\u65cb\u949f\u7684\u624b\u5df2\u6309\u5728\u5251\u9537\u4e0a\uff0c\u201c\u4f60\u5f88\u96be\u62d2\u7edd\u7684\u3002\u201d \u3000\u3000\u8ffd\u547d\u5ffd\u7136\u611f\u89c9\u5230\u6740\u6c14\u3002 \u3000\u3000\u52a8\u4eba\u5fc3\u5f26\u7684\u6740\u6c14\u3002 \u3000\u3000\u8fd8\u6709\u5251\u6c14\u3002 \u3000\u3000\u5272\u4f53\u800c\u7834\u4f53\u7684\u5251\u6c14\u3002 \u3000\u3000\u8521\u65cb\u949f\u7684\u5251\u672a\u51fa\u9798\uff0c\u4f46\u6bd4\u51fa\u9798\u4e86\u7684\u5251\u66f4\u903c\u4eba\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u6781\u957f\uff0c\u8ffd\u547d\u4e0e\u8521\u65cb\u949f\u8ddd\u79bb\u672c\u6709\u4e08\u8fdc\uff0c\u4f46\u8521\u65cb\u949f\u53ea\u8981\u4e00\u4f38\u624b\uff0c\u5c31\u53ef\u4ee5\u51fb\u4e2d\u4ed6\uff0c\u6839\u672c\u4e0d\u5fc5\u79fb\u52a8\u811a\u6b65\u3002 \u3000\u3000\u9ad8\u624b\u8fc7\u62db\uff0c\u591a\u4e00\u6b65\u548c\u5c11\u4e00\u6b65\uff0c\u8db3\u4ee5\u5206\u751f\u6b7b\u3001\u5b9a\u80dc\u8d1f\uff1b\u6b65\u6cd5\u518d\u5feb\uff0c\u4e5f\u4e0d\u5982\u4e0d\u5fc5\u6b65\u6cd5\u3002 \u3000\u3000\u8ffd\u547d\u9a6c\u4e0a\u62b1\u8d77\u9152\u575b\u5b50\uff0c\u547c\u565c\u547c\u565c\u7684\u75db\u996e\u3002 \u3000\u3000\u4ed6\u62b1\u7740\u575b\u5b50\u559d\u9152\u7684\u65f6\u5019\uff0c\u8521\u65cb\u949f\u5ffd\u7136\u611f\u89c9\u5230\uff0c\u8ffd\u547d\u8fd9\u624d\u662f\u5b8c\u5168\u65e0\u6687\u53ef\u88ad\u7684\u65f6\u5019\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u53d1\u52a8\u4efb\u4f55\u653b\u52bf\uff0c\u4ed6\u90fd\u5f88\u96be\u4ee5\u5e94\u4ed8\u8ffd\u547d\u5ffd\u628a\u575b\u5b50\u629b\u5411\u4ed6\uff0c\u800c\u53cc\u817f\u540c\u65f6\u6025\u8e22\u7684\u653b\u52bf\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u89c2\u5bdf\u4e0d\u5230\u5bf9\u65b9\u7684\u795e\u8272\u3002 \u3000\u3000\u6740\u4e00\u4e2a\u4eba\u3001\u6216\u51fb\u8d25\u4e00\u4e2a\u4eba\uff0c\u5f80\u5f80\u8981\u770b\u5bf9\u65b9\u7684\u8138\u8272\u3001\u795e\u6c14\uff0c\u53ea\u8981\u5bf9\u65b9\u4e00\u6709\u6b7b\u610f\u3001\u8d25\u8c61\uff0c\u53ea\u8981\u9a6c\u4e0a\u628a\u63e1\u65f6\u673a\uff0c\u591a\u80fd\u4e00\u51fb\u5f97\u624b\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u628a\u653b\u52bf\u5ef6\u540e\u3002 \u3000\u3000\u9152\u603b\u6709\u559d\u5b8c\u4e86\u7684\u65f6\u5019\u3002 \u3000\u3000\u8ffd\u547d\u4e00\u53e3\u6c14\u559d\u5b8c\u4e86\u9152\uff0c\u7528\u8863\u8896\u62b9\u62b9\u5634\u5c31\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u4e0d\u4f1a\u5728\u8fd9\u65f6\u5019\u627e\u6211\u6bd4\u6597\u7684\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u63e1\u5251\u7684\u624b\u53c8\u7d27\u4e86\u4e00\u7d27\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8ffd\u547d\u773c\u775b\u53d1\u7740\u4eae\uff1a\u201c\u56e0\u4e3a\u4f60\u5df2\u627e\u5230\u6bd4\u6211\u66f4\u597d\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u8521\u65cb\u949f\u7a81\u7136\u62d4\u5251\uff0c\u9661\u5730\u4e00\u58f0\u66b4\u559d\uff1a\u201c\u8fd8\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u5269\u4e0b\u4e00\u53ea\u9152\u575b\uff0c\u6446\u5728\u4e24\u4e08\u4f59\u5916\uff0c\u7a81\u7136\u7206\u6210\u788e\u7247\u3002 \u3000\u3000\u90a3\u662f\u8521\u65cb\u949f\u62d4\u5251\u4e00\u6307\u7684\u529b\u91cf\u3002 \u3000\u3000\u53ef\u662f\u5251\u4f9d\u7136\u6ca1\u6709\u62d4\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u7684\u52b2\u9053\uff0c\u662f\u8fde\u7740\u5251\u9798\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u8fde\u9798\u5251\u5df2\u6709\u8fd9\u4e48\u5927\u7684\u5a01\u529b\uff0c\u62d4\u5251\u51fa\u9798\u5462\uff1f \u3000\u3000\u9152\u575b\u5b50\u88ab\u5251\u6c14\u51fb\u788e\u3002 \u3000\u3000\u91cc\u9762\u6709\u9152\uff0c\u5374\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u9152\u6d12\u4e86\u4e00\u5730\uff0c\u4f17\u4eba\u5927\u6115\uff0c\u8fd9\u53d8\u5316\u4e00\u8d77\uff0c\u77f3\u65ad\u7709\u5df2\u7acb\u5373\u505a\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4ed6\u4e00\u811a\u8e22\u98de\u90a3\u4e00\u53e3\u987e\u4f5b\u5f71\u559d\u8fc7\u7684\u9152\u575b\u5b50\uff0c\u98de\u649e\u5411\u8ffd\u547d\uff0c\u4eba\u4e00\u95ea\u8eab\uff0c\u5df2\u5230\u4e86\u4e09\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u8ffd\u547d\u624b\u4e2d\u7684\u9152\u575b\u5e72\u98de\u51fa\uff0c\u8ddf\u649e\u6765\u7684\u9152\u575b\u5b50\u534a\u7a7a\u4e2d\u7838\u788e\uff0c\u4ed6\u7684\u4eba\u5df2\u7d27\u8d34\u77f3\u8001\u5e7a\u8eab\u540e\u3002 \u3000\u3000\u77f3\u8001\u5e7a\u4e00\u52a8\uff0c\u4e03\u53d1\u5927\u5e08\u5c31\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u53cd\u624b\uff0c\u62d4\u51fa\u4e00\u6839\u9488\u523a\u4e5f\u4f3c\u7684\u5947\u53d1\uff0c\u4e00\u62bd\u624b\uff0c\u5c31\u642d\u5728\u706b\u7ea2\u8272\u7684\u5c0f\u5f13\u4e0a\u3002 \u3000\u3000\u2014\u2014\u4ed6\u60f3\u5c04\u8c01\uff1f \u3000\u3000\u4ed6\u624d\u5f20\u5f13\u642d\u7bad\uff0c\u987e\u4f5b\u5f71\u5c31\u5df2\u7ecf\u9189\u4e86\u3002 \u3000\u3000\u4ed6\u521a\u624d\u4e5f\u559d\u4e86\u4e0d\u5c11\u9152\uff0c\u4f46\u521a\u624d\u4e0d\u9189\uff0c\u73b0\u5728\u624d\u9189\uff0c\u4eff\u4f5b\u5230\u73b0\u5728\u9152\u610f\u624d\u5192\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u9189\u7740\u62bd\u5200\u3002 \u3000\u3000\u4e00\u628a\u8584\u8584\u7684\u5927\u5200\u3002 \u3000\u3000\u4ece\u6765\u6ca1\u6709\u8fd9\u6837\u5bbd\u9614\u7684\u5927\u5200\uff0c\u5374\u4ee5\u8fd9\u6837\u8584\u7684\u7cbe\u94a2\u6253\u9020\u3002 \u3000\u3000\u8fd9\u67c4\u5200\u8fd9\u822c\u7684\u8584\uff0c\u5728\u987e\u4f5b\u5f71\u624b\u4e2d\u62ff\u6765\uff0c\u4eff\u4f5b\u5c31\u50cf\u4e00\u5f20\u968f\u98ce\u800c\u53bb\u7684\u7eb8\u4e00\u822c\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u9189\u4e86\uff0c\u4ed6\u624b\u4e0a\u7684\u8fd9\u67c4\u5200\uff0c\u4e5f\u50cf\u662f\u9189\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u65e0\u8bba\u4ed6\u600e\u4e48\u9189\uff0c\u90fd\u4e0d\u4f1a\u6709\u4eba\u6562\u5fd8\u8bb0\u987e\u4f5b\u5f71\u7684\u5916\u53f7\uff1a \u3000\u3000\u201c\u6a2a\u5200\u7acb\u9a6c\uff0c\u9189\u5367\u5c71\u5c97\u201d\u3002 \u3000\u3000\u4e03\u53d1\u5927\u5e08\u624b\u4e0a\u7684\u7bad\uff0c\u6b63\u7784\u51c6\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u201c\u987e\u76fc\u795e\u98ce\u201d\u987e\u4f5b\u5f71\u5374\u6ca1\u6709\u987e\u76fc\uff0c\u53ea\u9189\u773c\u6726\u80e7\u7684\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5e72\u5417\u8981\u559d\u90a3\u4e48\u591a\u7684\u9152\uff1f\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u4ecd\u4e0d\u7b54\u4ed6\uff0c\u53ea\u662f\u4ed6\u7684\u773c\u795e\u3002\u5f13\u548c\u7bad\u7684\u989c\u8272\u90fd\u5341\u5206\u8be1\u5f02\uff0c\u4eff\u4f5b\u878d\u4e3a\u4e00\u4f53\uff0c\u53c8\u4f3c\u672c\u6765\u5c31\u662f\u4e00\u4f53\u3002 \u3000\u3000\u4ed6\u7684\u53d1\u7bad\u4ecd\u76ef\u7740\u987e\u4f5b\u5f71\u7684\u5fc3\u623f\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u7684\u80f8\u819b\u5374\u6a2a\u7740\u4e00\u628a\u5200\u3002 \u3000\u3000\u4e00\u628a\u6bd4\u7eb8\u8fd8\u8584\u7684\u5927\u5200\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u4e3a\u77f3\u8001\u5e7a\u800c\u51fa\u624b\uff0c\u201d\u987e\u4f5b\u5f71\u8ff3\u81ea\u8bf4\u4e0b\u53bb\uff0c\u201c\u53ef\u662f\u4f60\u5374\u4f1a\u4e3a\u6740\u6211\u800c\u51fa\u624b\u3002\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u989d\u4e0a\u5df2\u6e17\u51fa\u4e86\u6c57\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e00\u7bad\uff0c\u8981\u4e0d\u8981\u5c04\uff1f \u3000\u3000\u2014\u2014\u5c04\u51fa\u4e86\u4e4b\u540e\uff0c\u80fd\u4e0d\u80fd\u594f\u6548\uff1f \u3000\u3000\u2014\u2014\u8981\u662f\u7bad\u51fa\u800c\u65e0\u529f\uff0c\u540e\u679c\u4f1a\u600e\u6837\uff1f \u3000\u3000\u4e03\u53d1\u5927\u5e08\u4e0e\u987e\u4f5b\u5f71\u6597\u4e89\u4e86\u4e00\u5341\u516d\u5e74\uff0c\u6570\u65e5\u524d\u7b54\u5e94\u66ff\u6e38\u5bb6\u9001\u4fe1\u7ed9\u5b5f\u968f\u56ed\uff0c\u5e76\u6ca1\u89c1\u7740\u5b5f\u592a\u5b88\uff0c\u53ea\u9001\u5230\u62bc\u89e3\u7684\u8859\u5dee\u624b\u91cc\uff1b\u4ed6\u4e4b\u6240\u4ee5\u7b54\u5e94\u8fd9\u4e48\u505a\uff0c\u662f\u56e0\u4e3a\u8d70\u6295\u65e0\u8def\uff0c\u8981\u664b\u8eab\u5c0f\u78a7\u6e56\u6548\u529b\uff0c\u4e0d\u5f97\u4e0d\u5fcd\u6c14\u541e\u58f0\uff0c\u5f53\u987e\u4f5b\u5f71\u7684\u90e8\u5c5e\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5982\u4eca\u4ed6\u4e00\u65e6\u6709\u4e86\u6816\u8eab\u4e4b\u5730\uff0c\u7b2c\u4e00\u4e2a\u4e0d\u80fd\u5bb9\u7684\uff0c\u5c31\u662f\u4e8c\u5e08\u5144\u987e\u4f5b\u5f71\u3002 \u3000\u3000\u201c\u4f60\u5c04\u5427\uff0c\u201d\u987e\u4f5b\u5f71\u9189\u610f\u9611\u73ca\u5730\u9053\uff0c\u201c\u8fd9\u4e00\u7bad\uff0c\u4f60\u60f3\u5c04\u4e86\u5f88\u4e45\u4e86\uff0c\u5f53\u5e74\u2018\u8001\u4e2d\u9752\u2019\u4e09\u5927\u9ad8\u624b\u5728\u96ea\u6865\u4e0a\u5bf9\u4ed8\u8bf8\u845b\u5148\u751f\uff0c\u4e5f\u662f\u4f60\u8fd9\u4e00\u7bad\u59cb\u7ec8\u4e0d\u53d1\uff0c\u5e76\u5f97\u4ee5\u5168\u8eab\u800c\u9000\uff0c\u4eca\u5929\u4f60\u653e\u4e86\u8fd9\u7bad\u5427\uff0c\u770b\u5230\u5e95\u8c01\u80fd\u5168\u8eab\u800c\u9000\uff1f\u201d \u3000\u3000\u4e03\u53d1\u5927\u5e08\u53d1\u811a\u4e0b\u7ec6\u6c57\u5bc6\u5e03\u3002 \u3000\u3000\u4ed6\u7684\u6c57\u4eff\u4f5b\u4e5f\u662f\u5f02\u8272\u7684\u3002 \u3000\u3000\u4ed6\u7684\u53d1\u7bad\uff0c\u4f9d\u7136\u7a33\u5b9a\u3002 \u3000\u3000\u4ed6\u624b\u4e0a\u7684\u706b\u5f13\uff0c\u4ecd\u7136\u5168\u4e0d\u8f7b\u98a4\u3002 \u3000\u3000\u4ed6\u7684\u53cc\u76ee\uff0c\u6b63\u53d1\u51fa\u4ee4\u4eba\u5fc3\u5f26\u9707\u8361\u7684\u5f02\u5149\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u4ed6\u90a3\u4e00\u7bad\uff0c\u53d1\u662f\u4e0d\u53d1\uff0c\u653e\u662f\u4e0d\u653e\uff1f \u3000\u3000\u5f53\u5e74\uff0c\u5728\u201c\u9ab7\u9ac5\u753b\u201d\u4e00\u5f79\u4e2d\uff0c\u6743\u5ba6\u5085\u5b97\u4e66\u66fe\u6d3e\u9063\u624b\u4e0b\u4e09\u5458\u5927\u5c06\uff1a\u201c\u8001\u4e0d\u6b7b\u201d\u3001\u201c\u4e2d\u95f4\u4eba\u201d\u3001\u201c\u9752\u6885\u7af9\u201d\uff0c\u5728\u96ea\u6865\u4e0a\u56f4\u6740\u8bf8\u845b\u5148\u751f\uff0c\u4f46\u201c\u8001\u4e0d\u6b7b\u201d\u548c\u201c\u9752\u6885\u7af9\u201d\u5168\u7686\u6218\u6b7b\uff0c\u201c\u4e2d\u95f4\u4eba\u201d\u8fdf\u4e0d\u51fa\u624b\uff0c\u4e0d\u6218\u800c\u9000\uff0c\u800c\u5f97\u5e78\u514d\u3002 \u3000\u3000\u53ef\u662f\u4ece\u90a3\u65f6\u8d77\uff0c\u201c\u4e2d\u95f4\u4eba\u201d\u4e5f\u906d\u5085\u5b97\u4e66\u4e00\u515a\u5f03\u800c\u4e0d\u7528\uff0c\u751a\u81f3\u4f20\u4ee4\u683c\u6740\u3002 \u3000\u3000\u6240\u8c13\u201c\u8001\u4e0d\u6b7b\u201d\u3001\u201c\u4e2d\u95f4\u4eba\u201d\u3001\u201c\u9752\u6885\u7af9\u201d\u5f53\u7136\u90fd\u662f\u4ee3\u53f7\uff0c\u800c\u201c\u4e2d\u95f4\u4eba\u201d\u5c31\u662f\u4e03\u53d1\u5927\u5e08\u3002 \u3000\u3000\u4e03\u53d1\u5927\u5e08\u4e00\u8def\u9003\u907f\u8ffd\u6740\uff0c\u8fde\u201c\u5200\u67c4\u4f1a\u201d\u3001\u201c\u5929\u6b32\u5bab\u201d\u90fd\u4e0d\u6562\u518d\u6536\u5bb9\u4ed6\uff0c\u51e0\u6210\u4e27\u5bb6\u4e4b\u72ac\uff0c\u76f4\u81f3\u4ed6\u6295\u5165\u4e86\u5170\u4ead\u6c60\u5bb6\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4e0e\u5170\u4ead\u6c60\u5bb6\u5bf9\u7acb\u7684\u5c0f\u78a7\u6e56\u6e38\u5bb6\uff0c\u6709\u4e00\u4e2a\u6267\u638c\u5927\u6743\u3002\u6d1b\u9633\u57ce\u91cc\u9664\u56db\u5927\u516c\u5b50\u4e4b\u5916\u6700\u6709\u52bf\u529b\u7684\u4eba\u7269\uff0c\u4fbf\u662f\u8ddf\u4ed6\u6597\u4e89\u4e86\u5341\u4e03\u5e74\u4e00\u76f4\u5360\u5c3d\u4e0a\u98ce\u7684\u4e8c\u5e08\u5144\u3002 \u3000\u3000\u987e\u76fc\u795e\u98ce\uff01 \u3000 \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u76f8\u601d\u4ead\u4e00\u6218 \u3000 \u3000\u3000\u9152\u575b\u788e\u88c2\u3002 \u3000\u3000\u9152\u575b\u91cc\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u8521\u65cb\u949f\u6309\u5251\u4e0d\u62d4\uff0c\u8138\u4e0a\u4e5f\u51fa\u73b0\u4e86\u575a\u6bc5\u4e0d\u62d4\u4e4b\u8272\uff0c\u84e6\u7136\u62ac\u5934\uff0c\u201c\u539f\u6765\u4f60\u5728\u4ead\u9876\uff0c\u201d\u4ed6\u9053\uff0c\u201c\u53ef\u662f\u4f60\u7684\u547c\u5438\u548c\u5fc3\u8df3\uff0c\u5374\u81ea\u9152\u575b\u91cc\u53d1\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u9152\u575b\u592a\u72ed\u5c0f\uff0c\u6211\u4e00\u5411\u4e0d\u559c\u72ed\u4ec4\u7684\u5730\u65b9\uff0c\u201d\u4ead\u4e0a\u6709\u6e05\u6717\u7684\u8bed\u97f3\u7b54\uff0c\u201c\u6211\u7684\u8f7b\u529f\u53ef\u4ee5\u505a\u5230\u843d\u5730\u65e0\u58f0\uff0c\u4f46\u4eba\u4e0d\u80fd\u505c\u6b62\u547c\u606f\u548c\u5fc3\u8df3\uff0c\u6240\u4ee5\u6211\u53ea\u6709\u628a\u547c\u5438\u58f0\u548c\u5fc3\u8df3\u58f0\u8f6c\u4f20\u5230\u9152\u575b\u5b50\u91cc\u53bb\u3002\u201d \u3000\u3000\u8521\u65cb\u949f\u7684\u8863\u886b\u5f88\u8d34\u8eab\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u8863\u670d\u4e00\u5982\u5251\u9798\uff0c\u597d\u5251\u5fc5\u987b\u8981\u597d\u7684\u5251\u9798\uff0c\u4eba\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u663e\u7136\u5728\u5438\u6c14\u3002 \u3000\u3000\u6df1\u6df1\u7684\u5438\u6c14\u3002 \u3000\u3000\u7136\u540e\u5728\u5410\u6c14\u3002 \u3000\u3000\u7f13\u7f13\u7684\u5728\u5410\u6c14\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u8fd0\u6c14\u4f1a\u795e\u3001\u517b\u7cbe\u84c4\u9510\u3002 \u3000\u3000\u56e0\u4e3a\u4ead\u4e0a\u7684\u4eba\uff0c\u8fd8\u6ca1\u6709\u51fa\u73b0\uff0c\u4ed6\u5c31\u611f\u89c9\u5230\u4e00\u80a1\u51cc\u5389\u7684\u5251\u6c14\uff0c\u51e0\u4e4e\u8981\u903c\u5165\u4ed6\u8eab\u4e0a\u6bcf\u4e00\u5904\u7684\u6bdb\u5b54\u91cc\uff0c\u751a\u81f3\u76f4\u4f3c\u8981\u628a\u4ed6\u7684\u776b\u6bdb\u903c\u5165\u81ea\u5df1\u7684\u773c\u5e18\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u786e\u53d1\u73b0\u573a\u4e2d\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e0d\u662f\u4ed6\u542c\u5230\u4e86\u4ec0\u4e48\u58f0\u54cd\uff0c\u800c\u662f\u611f\u89c9\u5230\u4e86\u5251\u6c14\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u4ed4\u7ec6\u53bb\u5206\u8fa8\u5fc3\u8df3\u548c\u547c\u606f\u58f0\u3002 \u3000\u3000\u5c31\u7b97\u662f\u518d\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u4e5f\u6709\u5fc3\u8df3\u548c\u547c\u5438\u3002 \u3000\u3000\u4ed6\u9a6c\u4e0a\u5c31\u53d1\u73b0\u547c\u606f\u548c\u5fc3\u8df3\u58f0\uff0c\u81ea\u9152\u575b\u91cc\u4f20\u6765\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u6765\u4eba\u5c31\u533f\u85cf\u5728\u7b2c\u56db\u53e3\u9152\u575b\u5b50\u91cc\u3002 \u3000\u3000\u4eba\u672a\u51fa\u73b0\u5c31\u6709\u8fd9\u6837\u5389\u70c8\u7684\u5251\u6c14\uff0c\u6765\u4eba\u5f53\u7136\u662f\u66f4\u5f3a\u7684\u5bf9\u624b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u9519\u4e86\u3002 \u3000\u3000\u4eba\u5728\u4ead\u4e0a\u3002 \u3000\u3000\u4eba\u672a\u51fa\u73b0\uff0c\u5df2\u4f7f\u4ed6\u7a7a\u81ea\u53d1\u51fa\u4e00\u51fb\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u8f7b\u529f\uff0c\u8fd8\u4e0d\u80fd\u4f7f\u8521\u65cb\u949f\u89c9\u5f97\u53ef\u6015\u3002\u6765\u4eba\u7684\u5fc3\u8df3\u548c\u547c\u606f\uff0c\u80fd\u4f20\u9001\u5165\u9152\u575b\u91cc\uff0c\u8521\u65cb\u949f\u4e5f\u53ea\u8ba4\u4e3a\u81ea\u5df1\u662f\u4e00\u65f6\u8f7b\u654c\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u8fd9\u4eba\u662f\u4ec0\u4e48\u65f6\u5019\u5230\u4e86\u4ead\u4e0a\u7684\u5462\uff1f \u3000\u3000\u8fd9\u624d\u662f\u53ef\u6015\u4e4b\u5904\u3002 \u3000\u3000\u4e03\u53d1\u5927\u5e08\u672c\u6b63\u4e0e\u987e\u4f5b\u5f71\u5bf9\u5cd9\u3002 \u3000\u3000\u8fde\u4ed6\u5fc3\u4e2d\u4e5f\u611f\u89c9\u5230\u9707\u52a8\uff1a \u3000\u3000\u2014\u2014\u8fd9\u4eba\u5230\u4e86\u4ead\u4e0a\uff0c\u9664\u4e86\u8ffd\u547d\uff0c\u4f3c\u8c01\u4e5f\u4e0d\u66fe\u89c9\u5bdf\u3002 \u3000\u3000\u2014\u2014\u6765\u4eba\u5b9a\u5fc5\u5728\u77f3\u65ad\u7709\u51fa\u73b0\u4e4b\u540e\uff0c\u624d\u63a0\u5230\u4ead\u5b50\u4e0a\u7684\uff0c\u53ef\u662f\uff0c\u90a3\u65f6\u5019\uff0c\u4ead\u91cc\u5df2\u805a\u6ee1\u4e86\u9ad8\u624b\uff0c\u600e\u4e48\u5168\u90fd\u6ca1\u6709\u53d1\u73b0\uff1f\uff01 \u3000\u3000\u8521\u65cb\u949f\u7d27\u63e1\u7740\u5251\u3002 \u3000\u3000\u4ed6\u7684\u5251\u4ecd\u672a\u79bb\u9798\u3002 \u3000\u3000\u5251\u5c16\u4e0b\u5782\uff0c\u659c\u6307\u4e03\u661f\u3002 \u3000\u3000\u201c\u4f60\u8981\u6740\u8ffd\u547d\uff1f\u201d\u4ead\u4e0a\u7684\u4eba\u95ee\u3002 \u3000\u3000\u201c\u6211\u5949\u547d\u627e\u4ed6\u6bd4\u6597\u3002\u201d\u8521\u65cb\u949f\u7b54\u3002 \u3000\u3000\u201c\u90a3\u4f60\u5f97\u5148\u80dc\u4e86\u6211\u3002\u201d\u4ead\u4e0a\u7684\u4eba\u9053\u3002 \u3000\u3000\u201c\u4f60\u662f\u65b9\u90aa\u771f\uff1f\u201d\u8521\u65cb\u949f\u95ee\u3002 \u3000\u3000\u201c\u6211\u662f\u3002\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u6211\u60f3\u9886\u6559\u4f60\u7684\u4e5d\u4e03\u5251\u6cd5\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u201d\u8521\u65cb\u949f\u9053\uff0c\u201c\u4f60\u5728\u4ead\u4e0a\uff0c\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u7136\u540e\u4e24\u4eba\u5c31\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u4e03\u53d1\u548c\u987e\u4f5b\u5f71\u4ecd\u5728\u4ead\u5916\u5bf9\u5cd9\u3002 \u3000\u3000\u4ed6\u4eec\u79bb\u4ead\u5b50\u7ea6\u516d\u5c3a\uff0c\u5de6\u534a\u8eab\u5b50\u5411\u7740\u4ead\u5b50\u3002 \u3000\u3000\u4e0d\u77e5\u600e\u5730\uff0c\u4ed6\u4eec\u4e0d\u7ea6\u800c\u540c\uff0c\u90fd\u5411\u5916\u884c\u51fa\u4e03\u516b\u6b65\uff0c\u7136\u540e\u624d\u80fd\u7acb\u5b9a\uff0c\u7ee7\u7eed\u5bf9\u5cd9\u3002 \u3000\u3000\u56e0\u4e3a\u4e03\u53d1\u5927\u5e08\u5de6\u534a\u8eab\u5b50\u5982\u906d\u5251\u523a\uff0c\u68ee\u5bd2\u3001\u4f46\u53c8\u9510\u70c8\u65e0\u6bd4\uff0c\u90a3\u79cd\u611f\u89c9\u5c31\u597d\u50cf\u7a81\u7136\u6709\u4e00\u628a\u521a\u51fa\u7089\u7684\u5229\u5251\u6b63\u5728\u7814\u78e8\u7740\u4ed6\u7684\u7259\u9f7f\u3002 \u3000\u3000\u90a3\u662f\u5251\u6c14\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u5374\u89c9\u5f97\u5de6\u723f\u8eab\u5b50\u5ffd\u7136\u9ebb\u75f9\uff0c\u4e00\u80a1\u9177\u70c8\u800c\u51b7\u51bd\u7684\u51b0\u9488\uff0c\u4f3c\u5df2\u6233\u5165\u4ed6\u7684\u6bdb\u5b54\u91cc\uff0c\u800c\u518d\u5316\u4f5c\u5343\u7247\u70c8\u9633\uff0c\u81ea\u8840\u8109\u91cc\u70b8\u4e86\u5f00\u6765\u3002 \u3000\u3000\u90a3\u662f\u6740\u6c14\u3002 \u3000\u3000\u90a3\u5047\u5192\u5b5f\u968f\u56ed\u7684\u4eba\uff0c\u672c\u6765\u5df2\u9000\u51fa\u4e08\u5916\uff0c\u6b63\u9762\u5411\u7740\u4ead\u5b50\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5ffd\u7136\u89c9\u5f97\u660f\u7729\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u65e0\u6cd5\u7741\u5f00\u773c\u6765\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u4eff\u4f5b\u662f\u5251\u6c14\u548c\u6740\u6c14\uff0c\u540c\u65f6\u5230\u4e86\u4ed6\u7684\u5934\u4e0a\u53ae\u6740\uff0c\u4f7f\u4ed6\u60c5\u4e0d\u81ea\u7981\u5730\u4e3e\u8d77\u8863\u8896\uff0c\u906e\u63a9\u7740\u8138\u3002 \u3000\u3000\u53ef\u662f\u65b9\u90aa\u771f\u548c\u8521\u65cb\u949f\u8fd8\u6ca1\u6709\u52a8\u624b\u3002 \u3000\u3000\u81f3\u5c11\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u3002 \u3000\u3000\u8521\u65cb\u949f\u5782\u9996\u51dd\u5251\u3002 \u3000\u3000\u5251\u6307\u4f55\u5904\uff1f \u3000\u3000\u8fd9\u4e5d\u5c3a\u4e03\u5bf8\u957f\u7684\u5251\uff0c\u6307\u5728\u4e00\u4e2a\u65e0\u5173\u91cd\u8981\u4e4b\u5904\uff0c\u6216\u4efb\u4f55\u5730\u65b9\u3002 \u3000\u3000\u90a3\u5c31\u662f\u65e0\u3002 \u3000\u3000\u4e00\u79cd\u201c\u65e0\u201d\u7684\u5251\u6cd5\uff0c\u4e00\u65e6\u52a8\u5251\uff0c\u5b83\u7684\u529b\u91cf\u5f88\u53ef\u80fd\u5c31\u662f\u65e0\u6240\u4e0d\u6709\u2014\u2014\u5c31\u50cf\u6c34\u964d\u5230\u6700\u4f4e\u70b9\uff0c\u552f\u6709\u9ad8\u5347\uff0c\u800c\u4e14\u964d\u5f97\u8d8a\u4f4e\uff0c\u5c31\u4f1a\u6da8\u5f97\u8d8a\u9ad8\u3002\u6392\u5c71\u5012\u6d77\u7684\u5de8\u6d6a\uff0c\u5c31\u6765\u81ea\u6df1\u5982\u58d1\u8c37\u7684\u4f4e\u6f6e\u3002 \u3000\u3000\u65e0\u63a5\u8fd1\u4e8e\u6b7b\u3002 \u3000\u3000\u8fd9\u79cd\u6b7b\u7684\u5251\u6cd5\uff0c\u4e00\u65e6\u6d3b\u4e86\u8d77\u6765\uff0c\u53ea\u6015\u6ca1\u6709\u4eba\u80fd\u591f\u5728\u5251\u4e0b\u6d3b\u7740\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4ef0\u9996\u671b\u5929\u3002 \u3000\u3000\u4ed6\u80cc\u8d1f\u53cc\u624b\uff0c\u795e\u6001\u6fc0\u8d8a\u800c\u60a0\u95f2\u3002 \u3000\u3000\u2014\u2014\u2014\u4e2a\u4eba\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u600e\u6837\u624d\u4f1a\u53c8\u6fc0\u8d8a\u53c8\u60a0\u95f2\u5462\uff1f \u3000\u3000\u65b9\u90aa\u771f\u5c31\u662f\u8fd9\u6837\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u5c31\u4f3c\u6b63\u4f5c\u201c\u5929\u95ee\u201d\u7684\u5c48\u5927\u592b\uff0c\u4e3a\u5929\u4e0b\u82cd\u751f\u3001\u9ece\u6c11\u767e\u59d3\u800c\u6fc0\u8d8a\uff0c\u4e3a\u7f6e\u4e2a\u4eba\u6b7b\u751f\u4e8e\u5ea6\u5916\u800c\u795e\u95f2\u3002 \u3000\u3000\u4ed6\u7684\u5251\u60ac\u5728\u4ed6\u8170\u7554\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u6b63\u5728\u95ee\u5929\u3002 \u3000\u3000\u5982\u679c\u4ed6\u62d4\u5251\uff0c\u8fd9\u628a\u5251\u5c31\u4e0d\u6b62\u662f\u4ed6\u7684\u5251\uff0c\u4e5f\u4e0d\u53ea\u662f\u4ed6\u7684\u5fc3\u5251\uff0c\u66f4\u662f\u5929\u7684\u5251\u3002 \u3000\u3000\u5929\u5251\u65e0\u4eba\u53ef\u654c\u3002 \u3000\u3000\u2014\u2014\u201c\u5929\u95ee\u5251\u6cd5\u201d\u5462\uff1f \u3000\u3000\u4e03\u53d1\u5927\u5e08\u7684\u53d1\u7bad\uff0c\u8f6c\u800c\u7784\u51c6\u987e\u4f5b\u5f71\u7684\u7709\u5fc3\uff0c\u7136\u800c\u4ed6\u7684\u773c\uff0c\u6b63\u76ef\u7740\u987e\u4f5b\u5f71\u80f8\u524d\u6a2a\u7740\u7684\u5200\u3002 \u3000\u3000\u90a3\u67c4\u4eae\u4e3d\u7684\u5927\u5200\u3002 \u3000\u3000\u5927\u5200\u4e0a\uff0c\u6b63\u5e7b\u6f3e\u5f02\u8292\uff0c\u5012\u6620\u51fa\u4ead\u4e0a\u7684\u767d\u8863\u4eba\u65b9\u90aa\u771f\uff0c\u4ead\u5fc3\u7a7f\u52b2\u88c5\u7684\u8521\u65cb\u949f\u3002 \u3000\u3000\u4ead\u4e2d\u7684\u4eba\u5f71\u52a8\u4e86\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u770b\u6e05\u695a\u4ed6\u662f\u600e\u4e48\u52a8\u7684\u3002 \u3000\u3000\u773c\u5feb\u7684\u4eba\u53ea\u611f\u89c9\u5230\u4ed6\u52a8\u8fc7\uff0c\u773c\u5c16\u7684\u4eba\u53ea\u89c9\u5f97\u4eba\u5f71\u4e00\u95ea\uff0c\u53ef\u662f\u8c01\u90fd\u4e0d\u80fd\u8bf4\u51fa\u6765\uff0c\u4ed6\u662f\u600e\u4e48\u4e2a\u52a8\u6cd5\uff01 \u3000\u3000\u2014\u2014\u52a8\u7684\u662f\u4eba\uff1f\u8eab\u5b50\uff1f\u8fd8\u662f\u5251\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ead\u4e0a\u7684\u767d\u8863\u4eba\u4eae\u5251\u3002 \u3000\u3000\u8c01\u8005\u6ca1\u6709\u770b\u6e05\u695a\u4ed6\u5982\u4f55\u62d4\u5251\u3001\u5982\u4f55\u6536\u5251\u3002 \u3000\u3000\u53ea\u89c1\u78a7\u8292\u6a2a\u7a7a\u4e00\u95ea\u3002 \u3000\u3000\u800c\u706d\u3002 \u3000\u3000\u4e24\u4eba\u987f\u4f4f\u3002 \u3000\u3000\u4ead\u4e0a\u7684\u4eba\u4f9d\u7136\u5728\u4ead\u4e0a\u3002 \u3000\u3000\u4ead\u5fc3\u7684\u4eba\u4f9d\u7136\u5728\u4ead\u5fc3\u3002 \u3000\u3000\u4ead\u4e0a\u7684\u4eba\u4f9d\u7136\u60a0\u95f2\u671b\u5929\uff0c\u5251\u5728\u8170\u95f4\u3002 \u3000\u3000\u4ead\u5fc3\u7684\u4eba\u4f9d\u7136\u5251\u9065\u6307\u4e00\u4e2a\u5168\u65e0\u610f\u4e49\u7684\u65b9\u5411\uff0c\u4fef\u9996\u671b\u5730\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u52a8\u8fc7\u5251\u3001\u4ea4\u8fc7\u624b\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u9694\u7740\u77f3\u4ead\u5c4f\u9876\uff0c\u5982\u4f55\u4ea4\u6218\uff1f \u3000\u3000\u4e03\u53d1\u5927\u5e08\u3001\u987e\u4f5b\u5f71\uff0c\u5168\u5fd8\u4e86\u6df1\u96e0\u3001\u5fd8\u4e86\u5bbf\u6028\uff0c\u5fd8\u4e86\u81ea\u5df1\u4e5f\u6b63\u5728\u5bf9\u5cd9\u3001\u51b3\u6218\uff0c\u5168\u5fc3\u5173\u6ce8\u5728\u4ead\u4e0a\u4ead\u5fc3\u3002 \u3000\u3000\u2014\u2014\u6218\u5c40\u5230\u5e95\u600e\u6837\uff1f \u3000\u3000\u4ead\u5fc3\u7684\u4eba\u9053\uff1a\u201c\u597d\u4e2a\u2018\u5929\u95ee\u5251\u6cd5\u2019\u3002\u201d \u3000\u3000\u4ead\u9876\u7684\u4eba\u9053\uff1a\u201c\u597d\u4e2a\u2018\u7834\u4f53\u65e0\u5f62\u5251\u6c14\u2019\u3002\u201d \u3000\u3000\u4ead\u5fc3\u7684\u4eba\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6210\u540d\u7684\u2018\u9500\u9b42\u5251\u6cd5\u2019\uff0c\u4ecd\u672a\u51fa\u624b\u3002\u201d \u3000\u3000\u4ead\u9876\u7684\u4eba\u9053\uff1a\u201c\u4f60\u7684\u2018\u4e5d\u4e03\u5251\u6cd5\u2019\uff0c\u4ea6\u672a\u53d1\u6325\u3002\u201d \u3000\u3000\u4ead\u5fc3\u7684\u4eba\u9053\uff1a\u201c\u4f60\u6839\u672c\u65e0\u5fc3\u51b3\u6218\uff0c\u610f\u5728\u963b\u6211\uff0c\u4e0d\u8ba9\u6211\u5bf9\u4ed8\u8ffd\u547d\u3002\u201d \u3000\u3000\u4ead\u9876\u7684\u4eba\u9053\uff1a\u201c\u4f60\u5374\u8fde\u5251\u4e5f\u672a\u51fa\u9798\u3002\u201d \u3000\u3000\u4ead\u5fc3\u7684\u4eba\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4e0b\u4e00\u6218\uff0c\u5e0c\u671b\u4f60\u4e13\u5fc3\u4e00\u70b9\uff0c\u800c\u4e14\uff0c\u4e0d\u8981\u592a\u8fc7\u75b2\u4e4f\uff0c\u5e76\u4e14\u53d7\u4f24\u5728\u5148\u3002\u201d \u3000\u3000\u4ead\u4e0a\u7684\u4eba\u559f\u53f9\u9053\uff1a\u201c\u5e0c\u671b\u6211\u4eec\u6ca1\u6709\u4e0b\u4e00\u6218\u3002\u201d \u3000\u3000\u4ead\u5fc3\u7684\u4eba\u6c89\u9ed8\u4e00\u9635\u5b50\uff0c\u624d\u9053\uff1a\u201c\u867d\u7136\u4f60\u662f\u6781\u96be\u5f97\u7684\u5bf9\u624b\uff0c\u4f46\u6211\u8fd8\u662f\u4e0d\u5e0c\u671b\u6709\u4f60\u8fd9\u6837\u5b50\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5c31\u8d70\u51fa\u4ead\u5916\uff0c\u7b14\u633a\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u518d\u4e5f\u4e0d\u56de\u5934\u3002 \u3000\u3000\u7136\u800c\uff0c\u4e03\u53d1\u5927\u5e08\u548c\u987e\u4f5b\u5f71\u5374\u6ce8\u610f\u5230\uff1a\u5730\u4e0a\u4e00\u8def\u90fd\u662f\u70b9\u70b9\u8840\u8ff9\u3002 \u3000\u3000\u2014\u2014\u4ed6\u53d7\u4f24\u4e86\uff1f \u3000\u3000\u2014\u2014\u65b9\u90aa\u771f\u7684\u5251\u662f\u600e\u6837\u900f\u8fc7\u77f3\u4ead\uff0c\u7a7f\u65a9\u4e0b\u6765\u7684\u5462\uff1f \u3000\u3000\u4e03\u53d1\u7985\u5e08\u957f\u53f9\uff0c\u5ffd\u7136\u6536\u5f13\u3001\u62bd\u7bad\uff0c\u8dfa\u8dfa\u8db3\uff0c\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u4e5f\u6ca1\u6709\u7559\u4ed6\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u6b63\u96c6\u4e2d\u5728\u65b9\u90aa\u771f\u8eab\u4e0a\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e0d\u77e5\u5728\u4f55\u65f6\u5df2\u4e0b\u4e86\u4ead\u5b50\u3002 \u3000\u3000\u4ed6\u80f8\u9645\u7684\u767d\u8863\u4e0a\uff0c\u6b63\u6cbe\u7740\u4e00\u56e2\u9c9c\u8840\uff0c\u6b63\u6162\u6162\u7684\u6269\u67d3\u5f00\u6765\u3002 \u3000\u3000\u2014\u2014\u4ed6\u53d7\u4e86\u4f24\uff1f \u3000\u3000\u2014\u2014\u8521\u65cb\u949f\u7684\u5251\uff0c\u53c8\u5982\u4f55\u900f\u8fc7\u77f3\u4ead\u9876\u5b50\uff0c\u900f\u523a\u4e2d\u4e0a\u9762\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u624d\u9732\u51fa\u4e00\u70b9\u5173\u6000\u4e4b\u8272\uff0c\u65b9\u90aa\u771f\u5df2\u6447\u624b\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\u7684\u3002\u201d \u3000\u3000\u4ed6\u521a\u8bf4\u5b8c\u4e86\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5c31\u53d1\u751f\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u77f3\u4ead\u584c\u4e86\u3002 \u3000\u3000\u5148\u662f\u4ead\u9876\uff0c\u7136\u540e\u662f\u6574\u4e2a\u4ead\u5b50\uff0c\u90fd\u584c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6218\uff0c\u5404\u53d1\u4e00\u5251\uff0c\u4e24\u4eba\u4ff1\u4f24\uff0c\u76f8\u601d\u4ead\u5c3d\u6bc1\u3002 \u3000\u3000\u65e5\u540e\u6c5f\u6e56\u4e2d\u4eba\uff0c\u5c31\u79f0\u8fd9\u540d\u52a8\u6c5f\u6e56\u7684\u4e00\u6218\u4e3a\uff1a\u201c\u4e5d\u4e03\u95ee\u5929\u3001\u76f8\u601d\u4e00\u6218\u201d\u3002 \u3000\u3000\u9152\u575b\u788e\u88c2\u7684\u5239\u90a3\u95f4\uff0c\u76f8\u601d\u4ead\u5185\u5916\u524d\u540e\u7684\u4eba\uff0c\u90fd\u53d1\u751f\u4e86\u4e0d\u540c\u7684\u53d8\u5316\uff1a\u4e03\u53d1\u5927\u5e08\u5f20\u5f13\u642d\u7bad\uff0c\u4e0e\u987e\u4f5b\u5f71\u5bf9\u5cd9\uff1b\u8521\u65cb\u949f\u53d1\u73b0\u4e86\u4ed6\u5e73\u751f\u672a\u9047\u7684\u52b2\u654c\uff0c\u5c31\u5728\u4ead\u4e0a\uff1b\u81f3\u4e8e\u77f3\u65ad\u7709\uff0c\u7acb\u5373\u9003\u8d70\u3002 \u3000\u3000\u8ffd\u547d\u9a6c\u4e0a\u5c31\u8ffd\u3002 \u3000\u3000\u65ad\u7709\u77f3\u8001\u5e7a\u7528\u5c3d\u4e00\u5207\u65b9\u6cd5\u6240\u6709\u6c14\u529b\u9003\u8d70\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u9003\u4e0d\u6389\u3002 \u3000\u3000\u8ffd\u547d\u4e4b\u6240\u4ee5\u88ab\u79f0\u4e3a\u8ffd\u547d\uff0c\u4fbf\u662f\u56e0\u4e3a\u4ed6\u7684\u8ffd\u672f\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u7edd\u5bf9\u5728\u4e09\u540d\u4ee5\u5185\u3002 \u3000\u3000\u65ad\u7709\u77f3\u6cbf\u7740\u5c0f\u78a7\u6e56\u9003\u4ea1\uff0c\u4e00\u53e3\u6c14\u9003\u4e86\u5341\u4e00\u91cc\uff0c\u8ffd\u547d\u4ecd\u7d27\u7d27\u8ddf\u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u65ad\u7709\u77f3\u731b\u7136\u6b62\u6b65\uff0c\u4ed6\u7684\u94a2\u53c9\u81ea\u80c1\u4e0b\u9661\u7136\u523a\u51fa\uff01 \u3000\u3000\u53ea\u8981\u8ffd\u547d\u6536\u4e0d\u4f4f\u6b65\u4f10\u3002\u649e\u4e86\u4e0a\u6765\uff0c\u90a3\u5c31\u53ef\u4ee5\u4e00\u51fb\u5f97\u624b\u3002 \u3000\u3000\u5f85\u4ed6\u53d1\u73b0\u8fd9\u4e00\u53c9\u843d\u7a7a\u7684\u65f6\u5019\uff0c\u8ffd\u547d\u5df2\u8d8a\u8fc7\u4ed6\u7684\u5934\u9876\uff0c\u5230\u4e86\u4ed6\u7684\u8eab\u524d\uff0c\u622a\u4f4f\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u77f3\u8001\u5e7a\u4f4e\u58f0\u4e0b\u6c14\u9053\uff1a\u201c\u4e09\u7237\uff0c\u53ef\u4e0d\u53ef\u4ee5\u653e\u8fc7\u6211\u4e00\u9a6c\uff1f\u6211\u6740\u4e86\u90a3\u4e48\u591a\u7684\u4eba\uff0c\u6740\u4e86\u90a3\u4e48\u4e45\u7684\u4eba\uff0c\u6211\u6240\u5f97\u7684\u94b1\u8d22\uff0c\u4e5f\u4e0d\u7b97\u5c11\u4e86\uff0c\u6211\u4eec\u4e00\u4eba\u5206\u4e00\u534a\uff0c\u5982\u4f55\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u4f60\u6740\u7684\u4eba\u5927\u591a\u4e86\uff0c\u4e5f\u6740\u4e86\u592a\u4e45\u7684\u4eba\u4e86\uff0c\u4eca\u5929\u6211\u66f4\u52a0\u4e0d\u80fd\u5bb9\u4f60\u3002\u201d \u3000\u3000\u77f3\u8001\u5e7a\u7f13\u58f0\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\uff0c\u53ef\u6ca1\u6709\u4ec0\u4e48\u597d\u5904\uff0c\u53ea\u4e0d\u8fc7\u529e\u597d\u4e86\u4e00\u4ef6\u516c\u4e8b\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u4e16\u95f4\u8fd9\u79cd\u516c\u4e8b\uff0c\u529e\u597d\u5f97\u8d8a\u591a\uff0c\u624d\u4f1a\u6709\u516c\u7406\uff0c\u6240\u4ee5\u53ea\u8981\u529e\u597d\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u662f\u6211\u6700\u5927\u7684\u62a5\u916c\u3002\u201d\u8ffd\u547d\u7528\u4e00\u53ea\u624b\u6307\u5728\u9762\u524d\u6447\u4e86\u6447\uff1a\u201c\u77f3\u8001\u5e7a\uff0c\u4f60\u5728\u9053\u4e0a\u4e5f\u7b97\u662f\u4e2a\u4eba\u7269\uff0c\u4e0d\u5fc5\u6447\u5c3e\u4e5e\u601c\uff0c\u522b\u8fd9\u822c\u4e0d\u4e0a\u9053\u3002\u201d \u3000\u3000\u201c\u6211\u4eca\u5929\u53d7\u4e86\u4f24\uff0c\u201d\u77f3\u8001\u5e7a\u4f9d\u7136\u8f6f\u58f0\u6c42\u9053\uff0c\u201c\u4f60\u6740\u4e86\u6211\u4e5f\u4e0d\u82f1\u96c4\uff01\u201d \u3000\u3000\u201c\u6211\u53ea\u6293\u4f60\uff0c\u4e0d\u6740\u4f60\uff0c\u5982\u679c\u5230\u975e\u6740\u6b7b\u4f60\u4e0d\u53ef\u7684\u65f6\u5019\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4f1a\u505a\u7684\uff0c\u4f60\u5c11\u6765\u6fc0\u6211\u653e\u8fc7\u4f60\uff1b\u201d\u8ffd\u547d\u53cd\u8bd8\u9053\uff1a\u201c\u5f53\u65e5\uff0c\u5b5f\u968f\u56ed\u7a77\u9014\u65e5\u66ae\uff0c\u4f60\u4e3a\u4e86\u4e00\u70b9\u94f6\u5b50\uff0c\u5c31\u628a\u4eba\u5168\u5bb6\u8d76\u5c3d\u6740\u7edd\uff0c\u53c8\u4e0d\u89c1\u5f97\u60f3\u60f3\u81ea\u5df1\u8fd9\u7b49\u4f5c\u4e3a\uff0c\u79f0\u4e0d\u79f0\u5f97\u4e0a\u6761\u597d\u6c49\uff01\u201d \u3000\u3000\u201c\u5d14\u7565\u5546\uff01\u201d\u77f3\u8001\u5e7a\u72e0\u58f0\u9053\uff0c\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u662f\u5949\u671d\u5ef7\u4e2d\u7684\u5927\u5b98\u6765\u527f\u706d\u53db\u515a\u5b5f\u968f\u56ed\u7684\uff0c\u4f60\u6293\u6211\u56de\u53bb\uff0c\u90a3\u662f\u81ea\u53d6\u706d\u4ea1\u3002\u201d \u3000\u3000\u201c\u6211\u628a\u4f60\u6293\u56de\u4eac\u57ce\uff0c\u8bf8\u845b\u5148\u751f\u81ea\u7136\u6709\u529e\u6cd5\u79c9\u516c\u5904\u7f6e\u4f60\uff0c\u5e76\u4f1a\u8ffd\u7a76\u5e55\u540e\u4e3b\u4f7f\u4eba\uff0c\u201d\u8ffd\u547d\u4e1d\u6beb\u4e0d\u52a8\u5bb9\uff0c\u201c\u6240\u4ee5\u4e0d\u8bba\u4f60\u80cc\u540e\u7684\u5e95\u5b50\u6709\u591a\u786c\uff0c\u4f60\u6709\u591a\u6ed1\uff0c\u4eca\u5929\u51b3\u4e0d\u6559\u4f60\u9003\u4e86\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u4f60\u80fd\u6293\u5f97\u4e86\u6211\uff1f\u4f60\u4ee5\u4e3a\u6211\u6740\u4e0d\u4e86\u4f60\uff1f\u201d\u77f3\u65ad\u7709\u54ac\u7259\u5207\u9f7f\uff0c\u72f0\u72de\u5730\u9053\uff0c\u201c\u5c31\u7b97\u4f60\u64d2\u5f97\u4f4f\u6211\uff0c\u4f60\u4ee5\u4e3a\u4f60\u80fd\u4e00\u8def\u5b89\u7a33\u8fd4\u4eac\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u2018\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173'\u7684\u7ec4\u7ec7\uff0c\u5171\u6709\u4e09\u4e2a\u5934\u9886\uff0c\u4f60\u53ea\u662f\u5176\u4e2d\u4e00\u4e2a\uff1b\u201d\u8ffd\u547d\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6211\u4eec\u4f55\u4e0d\u73b0\u5728\u5c31\u8bd5\u8bd5\u770b\uff0c\u95f2\u8bdd\u5c11\u8bf4\uff1f\u201d \u3000\u3000\u77f3\u8001\u5e7a\u77e5\u9053\u518d\u4e5f\u9003\u4e0d\u4e86\u8fd9\u4e00\u6218\u3002 \u3000\u3000\u8ffd\u547d\u5374\u5728\u8fd9\u65f6\u53c8\u544a\u8bc9\u4e86\u4ed6\u4e00\u4ef6\u4e8b\uff1a\u201c\u4f60\u4eca\u5929\u6562\u6765\u8d74\u5c0f\u78a7\u6e56\u4e4b\u4f1a\uff0c\u4e00\u662f\u4ee5\u4e3a\u6211\u67e5\u4e0d\u51fa\u8bc1\u636e\uff0c\u65e0\u6cd5\u7f09\u51f6\uff0c\u4f60\u4e0d\u80fd\u4e0d\u6765\uff0c\u4ee5\u514d\u4e0d\u6253\u81ea\u62db\uff1b\u53e6\u5916\u4e00\u4e2a\u539f\u56e0\u662f\u6709\u6043\u65e0\u6050\uff0c\u4ee5\u4e3a\u4f60\u90a3\u4e24\u540d\u4f19\u4f34\u79e6\u660e\u6708\u548c\u5173\u6c49\u4f1a\u6765\u52a9\u4f60\uff0c\u53ef\u60dc\uff0c\u4f60\u5374\u592a\u5ffd\u89c6\u4e86\u5c0f\u78a7\u6e56\u6e38\u5bb6\u662f\u6d1b\u9633\u56db\u5927\u516c\u5b50\u4e2d\uff0c\u6700\u6709\u52bf\u529b\u7684\u4e00\u4e2a\u4e16\u5bb6\uff1b\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u53d1\u73b0\uff0c\u4eca\u5929\u82b1\u6cbe\u5507\u3001\u8c79\u5b50\u7b80\u8fc5\u4ed6\u4eec\u90fd\u6ca1\u6709\u51fa\u73b0\uff1f\u201d\u8ffd\u547d\u9053\uff0c\u201c\u56e0\u4e3a\u6e38\u516c\u5b50\u6bd4\u4efb\u4f55\u4eba\u90fd\u60f3\u67e5\u51fa\u6740\u5b5f\u592a\u5b88\u7684\u51f6\u624b\uff0c\u5e76\u627e\u51fa\u5728\u671d\u4e2d\u8ddf\u4ed6\u4f5c\u5bf9\u7684\u4eba\uff1b\u4ed6\u4eec\u5df2\u8ddf\u6e38\u516c\u5b50\u5728\u5c0f\u78a7\u6e56\u4e4b\u5916\u5e03\u9632\uff0c\u4f60\u7684\u4f19\u4f34\u6b66\u529f\u518d\u9ad8\uff0c\u4eca\u5929\u4e5f\u95ef\u4e0d\u8fdb\u8fd9\u770b\u6765\u5168\u4e0d\u8bbe\u9632\u4e4b\u5730\u3002\u201d \u3000\u3000\u7136\u540e\u4ed6\u51b7\u51b7\u7684\u4f5c\u51fa\u603b\u7ed3\uff1a\u201c\u6240\u4ee5\uff0c\u4eca\u5929\u4f60\u9762\u5bf9\u6211\uff0c\u662f\u4e00\u5bf9\u4e00\uff0c\u5b64\u519b\u4f5c\u6218\u3002\u201d \u3000\u3000\u77f3\u8001\u5e7a\u7684\u6c14\u606f\u4e71\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u5fc3\u4e71\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u4fe1\u5fc3\u5df2\u5f00\u59cb\u5728\u5d29\u6e83\u4e86\u3002 \u3000\u3000\u4ed6\u6323\u624e\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6df7\u5165\u76f8\u601d\u6797\u3001\u5230\u4e86\u76f8\u601d\u4ead\u4e0a\u7684\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u65b9\u90aa\u771f\u3002\u201d\u8ffd\u547d\u7684\u773c\u4e2d\u5145\u6ee1\u4e86\u6e29\u6696\u4e4b\u610f\uff0c\u201c\u4ed6\u53ea\u8981\u77e5\u9053\u6211\u5728\u8fd9\u513f\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u6765\u8fd9\u4e00\u8d9f\u7684\uff0c\u4f55\u51b5\u987e\u76fc\u795e\u98ce\u8fd8\u4eb2\u81ea\u53bb\u7ea6\u4e86\u4ed6\u3002\u201d \u3000\u3000\u77f3\u65ad\u7709\u660e\u767d\u8ffd\u547d\u7684\u610f\u601d\u3002 \u3000\u3000\u8fd9\u770b\u6765\u5e73\u9759\u8be6\u548c\u7684\u6e38\u5bb6\u5e84\uff0c\u5176\u5b9e\u6697\u6869\u5bc6\u5e03\u3001\u6740\u673a\u6697\u4f0f\uff0c\u53ea\u8ba9\u53ef\u4ee5\u8fdb\u6765\u7684\u4eba\u8fdb\u6765\uff0c\u8981\u662f\u4e0d\u653e\u4eba\uff0c\u8c01\u4e5f\u51fa\u4e0d\u53bb\u3002 \u3000\u3000\u77f3\u65ad\u7709\u77e5\u9053\uff0c\u8fd9\u5f88\u53ef\u80fd\u662f\u4e2a\u4e8b\u5b9e\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u80fd\u76f8\u4fe1\u3002 \u3000\u3000\u56e0\u4e3a\u4e00\u65e6\u76f8\u4fe1\uff0c\u4ed6\u4fbf\u5b8c\u4e86\u3002 \u3000\u3000\u8fde\u4f5c\u6218\u7684\u4fe1\u5fc3\u4e5f\u7c89\u788e\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e0d\u7ba1\u4e00\u5207\u7684\u51fa\u4e86\u624b\u3002 \u3000\u3000\u73b0\u5728\u5df2\u6ca1\u6709\u9000\u8def\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u88ab\u903c\u4e0a\u7edd\u8def\u7684\u65f6\u5019\uff0c\u552f\u6709\u54ac\u7259\u82e6\u6324\uff0c\u6740\u51fa\u4e00\u6761\u8840\u8def\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\u90fd\u662f\u6b7b\u91cc\u6c42\u6d3b\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u6709\u65f6\u5019\u4e3a\u4e86\u81ea\u5df1\u6d3b\u4e0b\u53bb\uff0c\u4e0d\u60dc\u593a\u53d6\u4e86\u5bf9\u65b9\u751f\u5b58\u6743\u5229\u7684\u4eba\u3002 \u3000\u3000\u77f3\u65ad\u7709\u7edd\u5bf9\u662f\u8fd9\u79cd\u4eba\u3002 \u3000 \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u5cb8\u4e0a\u4e0e\u6c34\u91cc\u7684\u654c\u4eba \u3000 \u3000\u3000\u77f3\u65ad\u7709\u4e00\u751f\u90fd\u4e60\u201c\u60ef\u7528\u4e0d\u540c\u7684\u65b9\u5f0f\u53bb\u6740\u4eba\uff0c\u4ed6\u4e5f\u559c\u6b22\u7528\u4e0d\u540c\u7684\u65b9\u5f0f\u53bb\u6740\u4eba\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u662f\u51b3\u6597\uff0c\u800c\u4e0d\u662f\u6697\u6740\u3002 \u3000\u3000\u51b3\u6597\u53cd\u800c\u662f\u8521\u65cb\u949f\u60ef\u7528\u7684\u65b9\u5f0f\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u4ed6\u7684\u65b9\u5f0f\uff0c\u4ed6\u7684\u64c5\u957f\uff1a\u7cbe\u4e8e\u7ed8\u753b\u7684\u672a\u5fc5\u7cbe\u901a\u97f5\u5f8b\uff0c\u7cbe\u901a\u97f5\u5f8b\u7684\u4e5f\u672a\u5fc5\u7cbe\u5e72\u7ed8\u753b\uff1b\u540c\u7406\uff0c\u80fd\u6551\u4eba\u7684\u4e0d\u4e00\u5b9a\u6562\u6740\u4eba\uff0c\u6562\u6740\u4eba\u7684\u4e0d\u4e00\u5b9a\u4e5f\u80fd\u6551\u4eba\u3002 \u3000\u3000\u77f3\u65ad\u7709\u64c5\u4e8e\u6697\u6740\uff0c\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u4e0d\u957f\u4e8e\u51b3\u6597\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5728\u4e0a\u4e00\u6b21\u7684\u51b3\u6218\u91cc\uff0c\u4ed6\u5df2\u4f24\u5728\u65b9\u90aa\u771f\u7684\u5251\u4e0b\uff0c\u6b66\u529f\u5927\u5927\u6253\u4e86\u4e00\u4e2a\u6298\u6263\u3002 \u3000\u3000\u66f4\u6253\u6298\u6263\u7684\u662f\u4ed6\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u2014\u2014\u4ed6\u72af\u7684\u6848\u4ef6\uff0c\u5df2\u88ab\u6293\u5230\u4e86\u8bc1\u636e\uff0c\u66f4\u7cdf\u7684\u662f\u4ed6\u81ea\u5df1\u4eb2\u53e3\u4f9b\u51fa\u6765\u7684\uff0c\u800c\u4e14\uff0c\u5728\u51b3\u6218\u524d\u4ed6\u53c8\u77e5\u9053\u4e86\u4e24\u4e2a\u6b66\u529f\u9ad8\u5f3a\u7684\u4f19\u4f34\uff0c\u90fd\u65e0\u6cd5\u524d\u6765\u6551\u52a9\u4ed6\u3002 \u3000\u3000\u8fd9\u4e9b\u5728\u90fd\u5f71\u54cd\u4ed6\u7684\u6597\u5fd7\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u51b3\u5b9a\u8981\u5148\u628a\u81ea\u5df1\u7684\u6597\u5fd7\u6fc0\u53d1\u8d77\u6765\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5168\u529b\u62a2\u653b\u3002 \u3000\u3000\u82e5\u5355\u8bba\u53c9\u6cd5\uff0c\u5728\u5f53\u4ee3\u6b66\u6797\u4e2d\uff0c\u77f3\u65ad\u7709\u7edd\u5bf9\u53ef\u4ee5\u79f0\u5f97\u4e0a\u662f\u6b66\u6797\u7b2c\u4e00\u4eba\u3002 \u3000\u3000\u5728\u53c9\u6cd5\u4e0a\uff0c\u6ca1\u6709\u4eba\u4f7f\u5f97\u6bd4\u4ed6\u66f4\u7cbe\u66f4\u5999\uff0c\u4e5f\u51b3\u6ca1\u6709\u4eba\u6bd4\u4ed6\u7528\u5f97\u66f4\u7eaf\u66f4\u719f\uff0c\u66f4\u6ca1\u6709\u4eba\u80fd\u6bd4\u4ed6\u65bd\u5c55\u5f97\u66f4\u72e0\u66f4\u7edd\u3002 \u3000\u3000\u679c\u7136\u8ffd\u547d\u53ea\u80fd\u594b\u6218\u3001\u62db\u67b6\uff0c\u65e0\u6cd5\u8fd8\u51fb\u3002 \u3000\u3000\u4e00\u62db\u4e5f\u65e0\u6cd5\u8fd8\u51fb\u3002 \u3000\u3000\u77f3\u8001\u5e7a\u8db3\u8db3\u653b\u4e86\u4e03\u5341\u516b\u62db\uff0c\u624d\u6e10\u6e10\u53d1\u89c9\u6709\u4e9b\u4e0d\u5bf9\u52b2\u3002 \u3000\u3000\u8fd9\u6218\u5c40\u770b\u6765\u662f\u8ffd\u547d\u5168\u9762\u6328\u6253\uff0c\u4f46\u4e8b\u5b9e\u4e0a\uff0c\u53ea\u6709\u81ea\u5df1\u5728\u8017\u8d39\u6c14\u529b\u3002 \u3000\u3000\u4ed6\u7684\u94a2\u53c9\uff0c\u59cb\u7ec8\u8fde\u8ffd\u547d\u7684\u8863\u8882\u90fd\u6cbe\u4e0d\u4e0a\u3002 \u3000\u3000\u53ef\u6015\u7684\u662f\uff0c\u81ea\u5df1\u6bcf\u4e00\u62db\u653b\u52bf\uff0c\u90fd\u662f\u5bf9\u65b9\u8bf1\u53d1\u7684\uff1b\u66f4\u53ef\u6016\u7684\u662f\uff0c\u4ed6\u5df2\u65e0\u6cd5\u63a7\u5236\uff0c\u4e0d\u80fd\u505c\u6b62\u653b\u51fb\u2014\u2014\u56e0\u4e3a\u4e00\u65e6\u505c\u6b62\uff0c\u521a\u624d\u81ea\u5df1\u6240\u53d1\u51fa\u53bb\u800c\u843d\u7a7a\u7684\u6740\u7740\uff0c\u4fbf\u4f1a\u6392\u5c71\u5012\u6d77\u7684\u53cd\u5377\u56de\u6765\u2014\u2014\u8fd9\u79cd\u53cd\u632b\u4e4b\u529b\uff0c\u8fde\u77f3\u65ad\u7709\u81ea\u5df1\u4e5f\u65ad\u7136\u627f\u53d7\u4e0d\u4f4f\u7684\u3002 \u3000\u3000\u4ed6\u53cd\u800c\u5e0c\u671b\u8ffd\u547d\u65e9\u4e9b\u4f5c\u51fa\u53cd\u51fb\u3002 \u3000\u3000\u8ffd\u547d\u6108\u65e9\u53d1\u51fa\u53cd\u51fb\uff0c\u53cd\u51fb\u4e4b\u529b\u5c31\u4e0d\u81f4\u90a3\u4e48\u5de8\u5927\uff1b\u77f3\u65ad\u7709\u89c9\u5f97\u81ea\u5df1\u6240\u4f5c\u51fa\u653b\u52bf\uff0c\u5c31\u50cf\u6c34\u575d\u50a8\u5835\u4e86\u6d41\u6c34\u4e00\u822c\uff0c\u6c34\u6d41\u6108\u79ef\u6108\u591a\uff0c\u4e00\u65e6\u51b3\u5824\u51b2\u7834\uff0c\u5c31\u52bf\u65e0\u53ef\u633d\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u653b\u52bf\u53c8\u4e0d\u80fd\u51cf\u5f31\u4e0b\u6765\u3002 \u3000\u3000\u653b\u52bf\u4e00\u65e6\u51cf\u5f31\uff0c\u5c31\u62b5\u6321\u4e0d\u4f4f\u8ffd\u547d\u7684\u53cd\u51fb\u3002 \u3000\u3000\u540c\u7406\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u52a0\u5f3a\u653b\u51fb\uff0c\u56e0\u653b\u52bf\u6108\u5f3a\uff0c\u53cd\u51fb\u529b\u5c31\u6108\u5927\u3002 \u3000\u3000\u77f3\u65ad\u7709\u8fdb\u9000\u4e24\u96be\uff0c\u653b\u5b88\u7686\u4e0d\u662f\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u5f03\u53c9\u3002 \u3000\u3000\u8fd9\u94a2\u53c9\u662f\u77f3\u65ad\u7709\u7684\u72ec\u95e8\u6b66\u5668\uff0c\u5728\u8fd9\u751f\u6b7b\u5b58\u4ea1\u7684\u5173\u5934\uff0c\u66f4\u662f\u4ed6\u7684\u4f9d\u51ed\u3002 \u3000\u3000\u4ed6\u539f\u4e0d\u53ef\u80fd\u653e\u5f03\u5b83\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u6bc5\u7136\u628a\u5b83\u653e\u5f03\u3002 \u3000\u3000\u77f3\u65ad\u7709\u5c06\u624b\u4e2d\u53c9\u98de\u63b7\uff0c\u7535\u5c04\u8ffd\u547d\uff0c\u88ab\u8feb\u547d\u4e00\u811a\u8e22\u5f00\uff0c\u4f46\u8feb\u547d\u6240\u8574\u917f\u8d2e\u84c4\u7684\u53cd\u51fb\u529b\uff0c\u4e5f\u7a81\u7136\u9047\u5230\u4e86\u5824\u575d\u5d29\u7f3a\u7684\u7f1d\u7f45\u4e00\u822c\uff0c\u5168\u6d8c\u53d1\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u77f3\u65ad\u7709\u63a5\u4e86\u4e03\u516b\u62db\uff0c\u5df2\u62b5\u6321\u4e0d\u4f4f\uff0c\u7a81\u7136\u5927\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u6211\u6709\u8bdd\u8bf4\uff01\u201d \u3000\u3000\u8ffd\u547d\u7adf\u80fd\u628a\u6240\u6709\u7684\u653b\u52bf\u90fd\u786c\u751f\u751f\u7684\u715e\u4f4f\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u80fd\u591f\u628a\u770b\u6765\u5168\u529b\u4ee5\u8d74\u7684\u653b\u52bf\u9661\u7136\u6b62\u4f4f\uff0c\u5c31\u662f\u8bf4\u4ed6\u6839\u672c\u8fd8\u672a\u5168\u529b\u4ee5\u8d74\uff0c\u672a\u5168\u529b\u4ee5\u8d74\u7684\u653b\u52bf\u5c31\u5982\u6b64\u5730\u6b65\uff0c\u77f3\u65ad\u7709\u5fc3\u4e2d\u66f4\u4e3a\u9707\u6016\u3002 \u3000\u3000\u201c\u4f60\u8fd8\u8981\u8bf4\u4ec0\u4e48\uff1f\u201d\u8ffd\u547d\u95ee\u3002 \u3000\u3000\u201c\u65e9\u77e5\u9053\uff0c\u6211\u4e0d\u5982\u7b54\u5e94\u8001\u677f\u6697\u6740\u4e86\u4f60\uff1b\u201d\u77f3\u65ad\u7709\u6c14\u5598\u5401\u5401\u7684\u9053\uff0c\u201c\u6211\u5c31\u662f\u56e0\u4e3a\u4e0d\u60f3\u8ddf\u56db\u5927\u540d\u6355\u7ed3\u4ec7\uff0c\u6240\u4ee5\u624d\u53bb\u6740\u5b5f\u968f\u56ed\uff0c\u6ca1\u60f3\u5230\u73b0\u5728\u8fd8\u662f\u7ed9\u4f60\u7f20\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u60c5\u613f\u4f60\u6765\u6740\u6211\uff0c\u6740\u5b5f\u592a\u5b88\u5168\u5bb6\uff0c\u90a3\u5927\u65e0\u8f9c\u4e86\uff1b\u201d\u8ffd\u547d\u6c89\u58f0\u9053\uff0c\u201c\u4f60\u73b0\u5728\u552f\u4e00\u8d4e\u507f\u90e8\u5206\u7f6a\u5b7d\u7684\u65b9\u6cd5\uff0c\u4fbf\u662f\u544a\u8bc9\u6211\uff0c\u8c01\u662f\u4f60\u7684\u8001\u677f\uff0c\u4f60\u7684\u4f19\u4f34\u53c8\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u77e5\u9053\uff1f\u201d \u3000\u3000\u8ffd\u547d\u7b49\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u521a\u624d\uff0c\u6211\u53eb\u4f60\u505c\u624b\uff0c\u4f60\u9a6c\u4e0a\u5c31\u4f4f\u4e86\u624b\uff0c\u8fd9\u6b63\u5408\u4e4e\u4e86\u4e00\u4e2a\u5b57\uff0c\u201d\u77f3\u65ad\u7709\u5ffd\u5c94\u4e86\u4e00\u4e2a\u8bdd\u9898\uff0c\u7136\u540e\u95ee\uff1a\u201c\u4f60\u731c\u662f\u4ec0\u4e48\u5b57\uff1f\u201d \u3000\u3000\u8ffd\u547d\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6211\u4e0d\u731c\uff0c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u77f3\u65ad\u7709\u9a6c\u4e0a\u8bf4\u51fa\u4e86\u7b54\u6848\uff1a \u3000\u3000\u201c\u7b28\uff01\u201d \u3000\u3000\u4ed6\u4e5f\u9a6c\u4e0a\u4f5c\u51fa\u4e86\u4e00\u4e2a\u884c\u52a8\uff1a \u3000\u3000\u8df3\u8fdb\u6e56\u91cc\uff01 \u3000\u3000\u77f3\u65ad\u7709\u4e00\u7a9c\u5165\u6e56\u91cc\uff0c\u8eab\u5b50\u7acb\u5373\u6bd4\u4e00\u6761\u9c7c\u8fd8\u6ed1\u8fd8\u7075\u8fd8\u81ea\u5982\uff0c\u4ed6\u6ca1\u5165\u6c34\u4e2d\uff0c\u4e0d\u518d\u6d6e\u8d77\u3002 \u3000\u3000\u8ffd\u547d\u4e5f\u7acb\u65f6\u8dc3\u5165\u6c34\u4e2d\u3002 \u3000\u3000\u4ed6\u65e2\u662f\u8ffd\u8e2a\u5927\u738b\uff0c\u6cc5\u6cf3\u672f\u81ea\u7136\u4e5f\u4e0d\u5dee\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u4eba\u843d\u6c34\u4e2d\uff0c\u5c31\u53d1\u73b0\u4e0d\u5999\u3002 \u3000\u3000\u4e24\u80a1\u6697\u6d41\uff0c\u6f8e\u6e43\u6c79\u6d8c\uff0c\u5411\u4ed6\u538b\u6765\u3002 \u3000\u3000\u8ffd\u547d\u7cbe\u64c5\u7684\u662f\u817f\u6cd5\uff0c\u4e0d\u662f\u5185\u529f\uff0c\u4f55\u51b5\u5728\u6c34\u91cc\uff0c\u817f\u6cd5\u4e0d\u6613\u65bd\u5c55\uff0c\u5c31\u7b97\u638c\u6cd5\uff0c\u4e5f\u5927\u6253\u6298\u6263\uff0c\u8fde\u95ea\u8eb2\u90fd\u5341\u5206\u4e0d\u4fbf\u3002 \u3000\u3000\u51b5\u4e14\u8fd9\u4e24\u80a1\u638c\u529b\uff0c\u975e\u540c\u5c0f\u53ef\u3002\u7b80\u76f4\u4f3c\u628a\u6574\u4e2a\u5c0f\u78a7\u6e56\u7684\u6c34\u529b\uff0c\u90fd\u5411\u4ed6\u6324\u538b\u8fc7\u6765\uff0c\u4f3c\u975e\u8981\u628a\u4ed6\u538b\u5f97\u7c89\u8eab\u788e\u9aa8\u4e0d\u53ef\uff01 \u3000\u3000\u8ffd\u547d\u5f53\u673a\u7acb\u65ad\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u5343\u65a4\u5760\u3001\u4e07\u94a7\u95f8\u3001\u6a2a\u65ad\u7d2b\u91d1\u6869\uff0c\u540c\u65f6\u5e76\u65bd\uff0c\u75be\u6c89\u5341\u4e94\u5c3a\uff0c\u811a\u8e0f\u6e56\u5e95\u6d6e\u6ce5\uff0c\u731b\u5730\u4e00\u8e29\uff0c\u85c9\u529b\u4e0a\u8dc3\uff0c\u540c\u65f6\u65bd\u5c55\u98de\u9e1f\u817e\u7a7a\u3001\u6f5c\u9f99\u5347\u5929\u3001\u9c7c\u8dc3\u9f99\u95e8\u5f0f\uff0c\u5916\u52a0\u5de7\u71d5\u7a7f\u4e91\u7eb5\uff0c\u201c\u55d6\u201d\u5730\u62d4\u51fa\u6c34\u9762\u4e00\u4e08\u6709\u4f59\uff0c\u8fd9\u65f6\u90a3\u4e24\u80a1\u6f5c\u529b\u59cb\u5728\u539f\u6765\u8ffd\u547d\u7acb\u8eab\u4e4b\u6c34\u91cc\u7206\u4e86\u5f00\u6765\uff0c\u6fc0\u6d6a\u6ed4\u5929\uff0c\u70b8\u8d77\u4e00\u4e2a\u4e2a\u6d6a\u5c71\u6ce2\u58d1\uff01 \u3000\u3000\u8ffd\u547d\u51b2\u5929\u800c\u8d77\uff0c\u4f46\u5fc3\u77e5\u4e0d\u80fd\u843d\u56de\u6e56\u4e2d\u3002 \u3000\u3000\u4ed6\u51cc\u7a7a\u53cc\u817f\u4ea4\u526a\u4e00\u8e22\uff0c\u4e00\u53cc\u978b\u5b50\uff0c\u659c\u98de\u800c\u843d\uff0c\u5e73\u5e73\u5730\u843d\u5728\u6c34\u9762\u4e0a\uff0c\u6e56\u6c34\u6ce2\u6d9b\u518d\u70c8\uff0c\u4e24\u53ea\u6d12\u978b\u4ea6\u5982\u6012\u6d9b\u4e2d\u7684\u8f7b\u821f\uff0c\u6d6e\u4e8e\u6c34\u9762\u3002 \u3000\u3000\u8ffd\u547d\u957f\u5438\u4e00\u53e3\u6c14\uff0c\u8eab\u5b50\u5f90\u964d\uff0c\u521a\u597d\u843d\u5728\u5176\u4e2d\u4e00\u53ea\u6d12\u978b\u4e4b\u4e0a\uff0c\u8db3\u5c16\u4e00\u70b9\uff0c\u63d0\u7eb5\u6709\u6240\u501f\u529b\uff0c\u7acb\u5373\u5982\u9e70\u4f3c\u9e6b\uff0c\u817e\u8eab\u957f\u63a0\uff0c\u4e00\u53bb\u4e08\u4f59\uff0c\u534a\u7a7a\u518d\u98de\u51fa\u4e00\u53ea\u8292\u978b\uff0c\u8eab\u5b50\u53c8\u6c89\uff0c\u8db3\u5c16\u70b9\u5728\u7b2c\u4e8c\u53ea\u6c34\u9762\u7684\u6d12\u978b\u4e0a\uff0c\u51ed\u6b64\u518d\u6b21\u501f\u529b\uff0c\u5df1\u582a\u582a\u8dc3\u56de\u5cb8\u4e0a\u3002 \u3000\u3000\u8ffd\u547d\u4e00\u5230\u5cb8\u4e0a\uff0c\u7acb\u5373\u8fd4\u8eab\uff0c\u5168\u8eab\u8d2f\u6ce8\uff0c\u6ce8\u89c6\u6e56\u9762\u3002 \u3000\u3000\u77f3\u65ad\u7709\u5728\u6c34\u91cc\u7684\u6b66\u529f\uff0c\u7adf\u5982\u6b64\u51fa\u795e\u5165\u5316\uff0c\u662f\u8ffd\u547d\u610f\u6599\u672a\u53ca\u7684\u3002 \u3000\u3000\u53ea\u662f\u4efb\u4f55\u4eba\u90fd\u8981\u547c\u5438\u3002 \u3000\u3000\u77f3\u65ad\u7709\u6cf3\u672f\u518d\u5f3a\uff0c\u4e5f\u5f97\u8981\u6d6e\u4e0a\u6765\u6362\u6c14\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u6d6e\u4e0a\u6765\uff0c\u5c31\u7b97\u53ea\u662f\u6362\u4e00\u53e3\u6c14\u7684\u77ac\u95f4\uff0c\u8ffd\u547d\u90fd\u4e0d\u4f1a\u653e\u8fc7\u3002 \u3000\u3000\u2014\u2014\u4ed6\u5df2\u9519\u4e86\u4e00\u6b21\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e0d\u8be5\u505c\u624b\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e0d\u80fd\u518d\u9519\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e00\u5b9a\u8981\u902e\u6355\u77f3\u65ad\u7709\u5f52\u6848\u3002 \u3000\u3000\u2014\u2014\u65ad\u7709\u77f3\u4f60\u5728\u54ea\u91cc\uff0c\u600e\u4e48\u8fd8\u4e0d\u5347\u4e0a\u6765\uff1f \u3000\u3000\u8ffd\u547d\u76ef\u7740\u6e56\u9762\uff0c\u989d\u4e0a\u3001\u8138\u4e0a\uff0c\u4e0d\u77e5\u662f\u6e56\u6c34\u3001\u8fd8\u662f\u6c57\u6c34\uff1f \u3000\u3000\u8fdc\u8fdc\u6709\u5973\u5b50\u6cdb\u821f\uff0c\u6b4c\u58f0\u9690\u7ea6\u4f20\u6765\u3002 \u3000\u3000\u5979\u4eec\u663e\u7136\u5728\u6b22\u6b4c\u563b\u7b11\uff0c\u4e0d\u77e5\u8fd9\u8fb9\u6e56\u9762\u7684\u683c\u6740\uff0c\u5df2\u8fdb\u5165\u4e86\u66f4\u5927\u7684\u5371\u5883\u3002 \u3000\u3000\u8ffd\u547d\u7ad9\u7acb\u6e56\u8fb9\u3002 \u3000\u3000\u2014\u2014\u522b\u8bf4\u662f\u65ad\u7709\u77f3\u8001\u5e7a\u6d6e\u4e0a\u6765\uff0c\u5c31\u7b97\u662f\u4e00\u6761\u9c7c\u5192\u51fa\u6c34\u9762\u5410\u6c14\uff0c\u4ed6\u90fd\u5206\u8fa8\u5f97\u4e00\u6e05\u4e8c\u695a\u3002 \u3000\u3000\u4ed6\u53ea\u7b49\u77f3\u8001\u5e7a\u5192\u4e0a\u6765\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u4ed6\u600e\u4e48\u8fd8\u4e0d\u4e0a\u6765\uff1f \u3000\u3000\u77f3\u65ad\u7709\u7ec8\u4e8e\u6d6e\u4e0a\u6e56\u9762\uff0c\u6362\u6c14\u3002 \u3000\u3000\u4ed6\u5df2\u618b\u5f97\u592a\u4e45\u4e86\uff0c\u4ed6\u4e00\u53e3\u6c14\u6e38\u4e86\u8fd1\u5341\u4f59\u4e08\u8fdc\uff0c\u624d\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u6d6e\u4e0a\u6765\u5410\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8ffd\u547d\u5df2\u7136\u53d1\u52a8\u3002 \u3000\u3000\u8fd9\u6b21\u662f\u5168\u529b\u7684\u53d1\u52a8\u3002 \u3000\u3000\u5168\u9762\u7684\u53d1\u52a8\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u62d4\u8d77\uff0c\u540c\u65f6\u4e00\u638c\u62cd\u788e\u4e86\u8170\u7554\u7cfb\u7740\u7684\u76db\u9152\u846b\u82a6\uff01 \u3000\u3000\u846b\u82a6\u788e\u6210\u56db\u5757\uff0c\u4ed6\u4e00\u63a0\u4e09\u4e08\u4f59\u8fdc\uff0c\u624b\u4e2d\u846b\u82a6\u74e2\u5b50\u6254\u51fa\uff0c\u659c\u843d\u5728\u6c34\u9762\u4e0a\uff0c\u4ed6\u8db3\u5c16\u4e00\u70b9\uff0c\u501f\u529b\u4e00\u7a9c\uff0c\u5982\u6b64\u4e00\u8fde\u56db\u6b21\uff0c\u85c9\u529b\u98de\u7eb5\uff0c\u6bcf\u4e00\u6b21\u63a0\u8d77\u65f6\uff0c\u90fd\u5148\u5f39\u51fa\u846b\u82a6\u7247\uff0c\u5728\u6c34\u9762\u4e0a\u501f\u529b\u518d\u8d77\uff0c\u5154\u8d77\u9e5e\u843d\uff0c\u4e0d\u8fc7\u970e\u65f6\u95f4\u7684\u529f\u592b\uff0c\u5df2\u5230\u4e86\u77f3\u65ad\u7709\u6d6e\u8d77\u4e4b\u5904\u3002 \u3000\u3000\u77f3\u65ad\u7709\u731b\u89c9\u52b2\u98ce\u6251\u5411\u540e\u8111\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u7acb\u65f6\u4e00\u4e2a\u6c34\u91cc\u7ffb\u633a\uff0c\u8fde\u6ce1\u4e5f\u4e0d\u5192\uff0c\u731b\u5730\u6ca1\u5165\u6c34\u4e2d\u3002 \u3000\u3000\u8ffd\u547d\u77e5\u9053\u8fd9\u56de\u82e5\u53c8\u6559\u4ed6\u8d70\u8131\uff0c\u5c31\u96be\u4ee5\u518d\u8ffd\u4e86\uff0c\u6240\u4ee5\u5c31\u5728\u4ed6\u7b2c\u56db\u5ea6\u63a0\u8d77\u3001\u8eab\u5f62\u75be\u5411\u4e0b\u6c89\u4e4b\u9645\uff0c\u4ed6\u53f3\u811a\u5c31\u5148\u8e0f\u4e86\u4e0b\u53bb\uff0c\u5de6\u8db3\u5012\u5212\u8f6c\u8e74\uff0c\u4e00\u5148\u4e00\u540e\uff0c\u5f80\u77f3\u65ad\u7709\u539f\u5148\u5192\u4e0a\u6765\u7684\u5730\u65b9\u53d1\u62db\u3002 \u3000\u3000\u8ffd\u547d\u5728\u5168\u65e0\u8e0f\u8db3\u4e4b\u5904\u7684\u6e56\u4e2d\u65bd\u5c55\u6e21\u6c34\u767b\u840d\u7684\u6b66\u529f\uff0c\u6b63\u662f\u4ed6\u8f7b\u529f\u8fc7\u4eba\u7684\u5730\u65b9\uff0c\u4f46\u5728\u534a\u7a7a\u4e0b\u6c34\u7684\u5239\u95f4\u4f7f\u51fa\u8fde\u73af\u817f\u6cd5\uff0c\u624d\u662f\u4ed6\u817f\u529f\u7684\u771f\u6b63\u9ad8\u660e\u5904\u3002 \u3000\u3000\u6c34\u91cc\u7684\u4eba\u95f7\u54fc\u4e00\u58f0\uff0c\u5df2\u7ed9\u4ed6\u4e00\u811a\u8e29\u4e2d\u3002 \u3000\u3000\u867d\u7136\u77f3\u8001\u5e7a\u5728\u6c34\u91cc\u7ffb\u8eab\u3002\u4f46\u8ffd\u547d\u5728\u534a\u7a7a\u4e2d\u65e9\u5df2\u8ba4\u5b9a\u7a74\u4f4d\uff0c\u8fd9\u4e00\u811a\uff0c\u6b63\u597d\u8e29\u5728\u77f3\u65ad\u7709\u80cc\u810a\u7684\u201c\u8eab\u67f1\u7a74\u201d\u4e0a\u3002 \u3000\u3000\u540c\u65f6\u95f4\uff0c\u8ffd\u547d\u7684\u7b2c\u4e8c\u811a\u53c8\u5728\u6c34\u91cc\u8e39\u7740\u4e86\u4ed6\uff0c\u628a\u4ed6\u6574\u4e2a\u4eba\u6311\u8e22\u79bb\u6c34\u9762\u3002 \u3000\u3000\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u65ad\u7709\u79bb\u6c34\u800c\u8d77\uff0c\u8ffd\u547d\u4e00\u624b\u515c\u63fd\u4f4f\u4ed6\u7684\u80f8\u8179\uff0c\u53e6\u4e00\u624b\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u626f\u4e0b\u4e00\u7247\u8863\u8896\uff0c\u77ac\u95f4\u53c8\u6495\u6210\u56db\u7247\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u624b\u91cc\u4e00\u6325\uff0c\u90a3\u7247\u5e03\u5e1b\u7adf\u4f3c\u94c1\u7247\u4e00\u822c\u659c\u98de\u800c\u53bb\uff0c\u843d\u5728\u6c34\u9762\u4e0a\uff0c\u8ffd\u547d\u957f\u5438\u4e00\u53e3\u6c14\uff0c\u98d8\u8eab\u98de\u8de8\uff0c\u501f\u5728\u6c34\u9762\u4e0a\u5e03\u5e1b\u7684\u4e00\u6d6e\u4e4b\u529b\uff0c\u4e00\u8fde\u56db\u8d77\u56db\u4f0f\uff0c\u4e0d\u6d88\u7247\u523b\u5df2\u8dc3\u56de\u5cb8\u4e0a\u3002 \u3000\u3000\u8ffd\u547d\u5728\u5cb8\u4e0a\u5230\u6e56\u5fc3\u3001\u6e56\u5fc3\u518d\u56de\u5230\u5cb8\u4e0a\uff0c\u6765\u56de\u4e09\u5341\u4f59\u4e08\uff0c\u4ed6\u4ee5\u5fae\u7269\u501f\u529b\uff0c\u5f80\u8fd4\u5982\u98de\uff0c\u5374\u5df2\u628a\u77f3\u65ad\u7709\u5230\u624b\u64d2\u6765\uff0c\u81c2\u5f2f\u631f\u4e86\u4e00\u4e2a\u4eba\uff0c\u8eab\u6cd5\u4f9d\u7136\u8f7b\u6377\u3002 \u3000\u3000\u8ffd\u547d\u521a\u624d\u5598\u5f97\u534a\u53e3\u6c14\uff0c\u84e6\u7136\uff0c\u89c9\u5f97\u80cc\u540e\u6709\u4e00\u4e9b\u5fae\u7684\u58f0\u54cd\uff0c\u8fd9\u58f0\u54cd\u5341\u5206\u4e4b\u7ec6\uff0c\u5341\u5206\u4e4b\u8f7b\uff0c\u5c31\u50cf\u4e00\u74e3\u82b1\u843d\u5230\u539a\u539a\u7684\u96ea\u5730\u4e0a\u4e00\u822c\uff0c\u4ed6\u751a\u81f3\u8fd8\u95fb\u5230\u4e00\u79cd\u7c7b\u8fd1\u843d\u82b1\u7684\u9999\u6c14\u3002 \u3000\u3000\u8ffd\u547d\u5374\u731b\u7136\u4e00\u9707\uff0c\u4ed6\u4e4d\u7136\u89c9\u5f97\u5371\u673a\u4fb5\u80cc\u3002 \u3000\u3000\u4e0d\u4f46\u662f\u5371\u673a\uff0c\u800c\u4e14\u8fd8\u662f\u6740\u673a\u3002\u4ed6\u970d\u7136\u56de\u9996\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u770b\u89c1\u4e00\u4e2a\u4eba\uff0c\u5411\u4ed6\u4e00\u626c\u624b\u3002 \u3000\u3000\u5929\u8272\u9aa4\u9eef\u3002 \u3000\u3000\u6f2b\u5929\u7684\u9ed1\u70b9\uff0c\u50cf\u5343\u4e07\u53ea\u8757\u866b\uff0c\u98de\u53ee\u5411\u8ffd\u547d\u3002 \u3000\u3000\u8fd9\u79cd\u53ef\u6015\u7684\u6bd2\u7802\uff0c\u5b8c\u5168\u4e0d\u80fd\u62b5\u6321\u3001\u62db\u67b6\u3001\u95ea\u8eb2\uff0c\u8ffd\u547d\u767e\u5fd9\u4e2d\u54c7\u5730\u4e00\u58f0\uff0c\u4e00\u53e3\u9152\u75be\u55b7\u800c\u51fa\uff0c\u5316\u6210\u4e07\u5343\u7bad\u96e8\uff0c\u5c04\u5411\u90a3\u4eba\u8eab\u3001\u8138\uff0c\u4ed6\u4e5f\u4e0d\u53ca\u7406\u4f1a\u6709\u65e0\u547d\u4e2d\uff0c\u4e00\u4e2a\u5012\u8dc3\uff0c\u201c\u55d6\u201d\u5730\u843d\u56de\u8eab\u540e\u7684\u6e56\u91cc\u3002 \u3000\u3000\u6c34\u91cc\u5ffd\u7136\u53c8\u5377\u8d77\u4e24\u9053\u72c2\u6d41\u3002 \u3000\u3000\u8ffd\u547d\u500f\u7136\u56de\u8eab\uff0c\u5c31\u770b\u89c1\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u9ed1\u8272\u8863\u9760\u8499\u8138\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u773c\u775b\u7684\u4eba\uff0c\u8fd9\u53cc\u773c\u775b\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u6d78\u5728\u6c34\u91cc\u4e4b\u6545\uff0c\u7adf\u6cdb\u7740\u78a7\u7eff\u7684\u989c\u8272\u3002 \u3000\u3000\u6c34\u91cc\u5de8\u5927\u7684\u6f5c\u529b\uff0c\u5c31\u6765\u81ea\u4ed6\u53cc\u624b\u7ffb\u65cb\u95f4\u3002 \u3000\u3000\u2014\u2014\u539f\u6765\u521a\u624d\u6c34\u4e2d\u7684\u5de8\u529b\uff0c\u4e0d\u662f\u6765\u81ea\u65ad\u7709\u77f3\uff0c\u800c\u662f\u6765\u81ea\u6b64\u4eba\u638c\u5e95\u3002 \u3000\u3000\u8ffd\u547d\u5728\u6c34\u4e2d\uff0c\u529f\u529b\u6253\u4e86\u4e2a\u6298\u6263\uff0c\u8981\u5bf9\u4ed8\u8fd9\u4e2a\u7cbe\u901a\u6c34\u6027\u7684\u4eba\uff0c\u53ea\u6015\u8981\u7cdf\uff0c\u4f55\u51b5\u814b\u4e0b\u53c8\u631f\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7acb\u5373\u505a\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u79bb\u6e56\u8fb9\u4e0d\u8fc7\u4e24\u4e09\u5c3a\uff0c\u6e56\u5e95\u751a\u6d45\uff0c\u6e56\u6c34\u4e5f\u53ea\u521a\u903e\u4eba\u5934\uff0c\u8ffd\u547d\u7a81\u7136\u53cc\u811a\u4e00\u9635\u6025\u8e22\uff0c\u8e74\u8d77\u6e56\u5e95\u6ce5\u6c99\uff0c\u4e00\u65f6\u95f4\uff0c\u8fd9\u5341\u6570\u5c3a\u7684\u6e56\u8fb9\u5c3d\u6d4a\uff0c\u654c\u4eba\u89c1\u4e0d\u7740\u8ffd\u547d\uff0c\u8ffd\u547d\u4e5f\u89c1\u4e0d\u7740\u5bf9\u65b9\u3002 \u3000\u3000\u4ed6\u5df2\u9759\u6084\u6084\u7684\u6d6e\u8fd1\u5cb8\u8fb9\uff0c\u8155\u5e95\u5728\u5cb8\u4e0a\u53d1\u529b\u4e00\u6309\uff0c\u4eba\u5411\u4e0a\u4e00\u8dc3\uff0c\u811a\u672a\u6cbe\u5730\uff0c\u5df2\u5355\u638c\u5f53\u80f8\uff0c\u6697\u81ea\u60d5\u6212\uff0c\u4f46\u5cb8\u4e0a\u5df2\u9759\u6084\u6084\u5730\uff0c\u534a\u4e2a\u4eba\u5f71\u4e5f\u65e0\u3002 \u3000\u3000\u5730\u4e0a\u6ee1\u5e03\u4e86\u7ec6\u5982\u6bdb\u5b54\u7684\u5c0f\u9ed1\u70b9\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u4ee4\u6c5f\u6e56\u4e0a\u4eba\u90fd\u95fb\u540d\u4e27\u80c6\u7684\u201c\u4e94\u6bd2\u795e\u7802\u201d\u3002 \u3000\u3000\u2014\u2014\u654c\u4eba\u4f55\u5728\uff1f \u3000\u3000\u2014\u2014\u662f\u4e0d\u662f\u88ab\u4ed6\u7684\u9152\u7bad\u5c04\u4e2a\u6b63\u7740\uff0c\u8d1f\u4f24\u800c\u9003\uff1f \u3000\u3000\u2014\u2014\u6c34\u91cc\u7684\u654c\u4eba\u5462\uff1f \u3000\u3000\u8ffd\u547d\u5df2\u7ba1\u4e0d\u4e86\u90a3\u4e48\u591a\uff0c\u4fef\u9996\u4e00\u770b\uff0c\u6b64\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u539f\u6765\u4ed6\u81c2\u5f2f\u6240\u631f\u7684\u65ad\u7709\u77f3\u8001\u5e7a\uff0c\u8138\u76ee\u6d6e\u80bf\uff0c\u65e9\u5df2\u6c14\u7edd\u8eab\u4ea1\uff01 \u3000\u3000\u77f3\u65ad\u7709\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u9888\u4e0a\u6709\u4e00\u679a\u5c0f\u5c0f\u7684\u9ed1\u523a\uff0c\u9c9c\u8840\u548c\u6e56\u6c34\u60e8\u548c\u7740\u6d41\u4e0b\u6765\uff0c\u6d41\u4e0d\u5230\u4e00\u534a\u5c31\u53d8\u6210\u4e86\u9ed1\u8272\uff0c\u4e94\u5b98\u626d\u66f2\uff0c\u4e0d\u6210\u539f\u5f62\uff01 \u3000\u3000\u2014\u2014\u8fd9\u7a76\u7adf\u662f\u5cb8\u4e0a\u654c\u4eba\u6240\u4e0b\u7684\u624b\uff1f\u8fd8\u662f\u6c34\u4e2d\u6f5c\u4f0f\u7684\u654c\u4eba\u6240\u65bd\u7684\u6bd2\u624b\uff1f \u3000\u3000\u8ffd\u547d\u8fd9\u624d\u7701\u89c9\uff0c\u8fd9\u4e24\u4e2a\u6b66\u529f\u9ad8\u5f3a\u3001\u51fa\u624b\u8be1\u6bd2\u7684\u6765\u8005\uff0c\u6765\u610f\u4f3c\u4e4e\u5fd7\u4e0d\u5728\u4ed6\u81ea\u5df1\uff0c\u800c\u662f\u4ed6\u624b\u4e2d\u6240\u64d2\u4f4f\u4e86\u7684\u65ad\u7709\u77f3\u3002 \u3000\u3000\u2014\u2014\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u8981\u6740\u77f3\u8001\u5e7a\uff1f \u3000\u3000\u8fd9\u7406\u7531\u81f3\u4e3a\u660e\u663e\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u5e0c\u671b\u8ffd\u547d\u64d2\u4f4f\u6d3b\u7740\u7684\u77f3\u8001\u5e7a\u3002 \u3000\u3000\u6d3b\u7740\u7684\u4eba\u4f1a\u8bf4\u8bdd\u3001\u4f1a\u6c42\u751f\u3001\u4f1a\u51fa\u5356\u4eba\uff0c\u6b7b\u4e86\u7684\u4eba\uff0c\u5c31\u4ec0\u4e48\u4e5f\u4e0d\u4f1a\u3002 \u3000\u3000\u6240\u4ee5\u8ffd\u547d\u73b0\u5728\u53ea\u62ff\u4f4f\u4e86\u4e2a\u5df2\u65ad\u4e86\u6c14\u7684\u4eba\u3002 \u3000\u3000\u4e00\u5411\u55dc\u4e8e\u6697\u6740\u4eba\u7684\u65ad\u7709\u77f3\u8001\u5e7a\uff0c\u800c\u4eca\uff0c\u7adf\u6b7b\u5728\u4ed6\u4eba\u7684\u6697\u6740\u4e0b\uff0c\u4ee4\u8ffd\u547d\u500d\u89c9\u8352\u8c2c\u7684\u662f\uff0c\u65ad\u7709\u77f3\u7684\u7a74\u9053\u662f\u4e3a\u4ed6\u6240\u5236\u7684\uff1b\u5982\u679c\u4e0d\u662f\u4ed6\u5236\u4f4f\u77f3\u8001\u5e7a\uff0c\u77f3\u65ad\u7709\u5c31\u672a\u5fc5\u6b7b\u5f97\u8fd9\u4e48\u5bb9\u6613\u3002 \u3000\u3000\u5c60\u6740\u5b5f\u968f\u56ed\u5168\u5bb6\u7684\u6848\u5b50\uff0c\u4ed6\u7b97\u662f\u4fa6\u7834\u4e86\uff0c\u4f46\u4fa6\u7834\u7684\u7ed3\u679c\uff0c\u4f7f\u4ed6\u611f\u89c9\u5230\u4ed6\u53ea\u63ed\u53d1\u4e86\u771f\u76f8\u7684\u5916\u5c42\uff0c\u4ed6\u5fc3\u91cc\u53d1\u8a93\u8981\u7ee7\u7eed\u67e5\u4e0b\u53bb\uff0c\u76f4\u81f3\u8981\u628a\u63e1\u4f4f\u771f\u76f8\u7684\u6838\u5fc3\u3001\u63ed\u9732\u771f\u76f8\u7684\u5168\u90e8\u624d\u80fd\u79f0\u5fc3\u3002 \u3000\u3000\u2014\u2014\u751a\u81f3\u727a\u7272\u6027\u547d\u4e5f\u5728\u6240\u4e0d\u60dc\uff01 \u3000 \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u7b54\u6848\u4e0e\u7591\u95ee \u3000 \u3000\u3000\u65b9\u90aa\u771f\u5e76\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u4ed6\u5728\u7b49\u8ffd\u547d\u56de\u6765\u3002 \u3000\u3000\u4ed6\u4e86\u89e3\u77f3\u65ad\u7709\u7684\u6b66\u529f\uff0c\u4ed6\u8ddf\u77f3\u8001\u5e7a\u6362\u8fc7\u4e00\u62db\uff0c\u6240\u4ee5\u4ed6\u8d8a\u53d1\u80af\u5b9a\uff0c\u8ffd\u547d\u4e00\u5b9a\u4f1a\u56de\u6765\u7684\u3002 \u3000\u3000\u987e\u4f5b\u5f71\u5bf9\u8ffd\u547d\u4f3c\u4e4e\u4e5f\u4e00\u6837\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u201c\u6e38\u516c\u5b50\u4e00\u5411\u656c\u91cd\u5b5f\u592a\u5b88\u7684\u624d\u667a\u548c\u4e3a\u4eba\uff0c\u4ed6\u4e5f\u6709\u80fd\u529b\u4f7f\u671d\u5ef7\u8ba9\u5b5f\u592a\u5b88\u5145\u519b\u6539\u4e3a\u6d1b\u9633\u51fa\u5bb6\uff0c\u5176\u5b9e\u662f\u6697\u91cc\u8f6c\u5165\u52a9\u5c0f\u78a7\u6e56\u6e38\u5bb6\uff1b\u6ca1\u60f3\u5230\uff0c\u6e38\u516c\u5b50\u7684\u60dc\u91cd\uff0c\u53cd\u800c\u53d8\u6210\u5bb3\u4e86\u4ed6\u3002\u201d\u987e\u4f5b\u5f71\u53f9\u606f\u9053\uff0c\u201c\u5ba6\u515a\u751f\u6015\u5b5f\u592a\u5b88\u4ed6\u65e5\u4f1a\u4e1c\u5c71\u518d\u8d77\u3001\u5377\u571f\u91cd\u6765\uff0c\u6240\u4ee5\u66f4\u8981\u75db\u4e0b\u706d\u95e8\u6bd2\u624b\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u5e2e\u4e00\u4e2a\u4eba\u5e94\u8be5\u8981\u5f88\u5c0f\u5fc3\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u6709\u65f6\u5019\u5e2e\u4e00\u4e2a\u4eba\uff0c\u53ef\u80fd\u53cd\u800c\u662f\u5bb3\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u4ee5\u524d\u5e2e\u8fc7\u6b27\u9633\u4e03\u53d1\uff0c\u201d\u987e\u4f5b\u5f71\u9887\u6709\u611f\u89e6\uff1a\u201c\u53ef\u662f\u4ed6\u73b0\u5728\u6700\u6068\u7684\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u6210\u529f\u4e4b\u540e\uff0c\u5f88\u4e0d\u559c\u6b22\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u5e95\u7ec6\uff0c\u6216\u4ee4\u4ed6\u60f3\u8d77\u8fc7\u53bb\uff0c\u6216\u5206\u8584\u4ed6\u7684\u529f\u7ee9\uff1b\u201d\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u5386\u4ee3\u541b\u738b\uff0c\u4e00\u5f97\u5929\u4e0b\uff0c\u5927\u8bdb\u529f\u81e3\uff0c\u9e1f\u5c3d\u5f13\u85cf\uff0c\u5154\u6b7b\u72d7\u70f9\uff0c\u5728\u6240\u591a\u6709\u3002\u770b\u6765\u4f60\u548c\u4e03\u53d1\u5927\u5e08\u79ef\u6028\u4e5f\u4e0d\u7b97\u6d45\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u4e09\u4eba\uff0c\u540c\u51fa\u5e08\u95e8\uff0c\u4f46\u5374\u5404\u6709\u5bbf\u6028\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5730\u4f3c\u4e4e\u4e0d\u60f3\u77e5\u9053\u5f97\u592a\u591a\uff0c\u53cd\u95ee\uff1a\u201c\u8fd9\u4f4d\u65e2\u4e0d\u662f\u5b5f\u968f\u56ed\uff0c\u5374\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u987e\u4f5b\u5f71\u7b11\u9053\uff1a\u201c\u4ed6\uff1f\u4ed6\u8bf4\u8ddf\u65b9\u5c11\u4fa0\u662f\u7d20\u8bc6\u3002\u201d \u3000\u3000\u201c\u7d20\u8bc6\uff1f\u201d\u65b9\u90aa\u771f\u5012\u89c9\u5f97\u8fd9\u4eba\u6709\u70b9\u773c\u719f\uff0c\u5374\u60f3\u4e0d\u8d77\u66fe\u5728\u54ea\u91cc\u89c1\u8fc7\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u5417\uff1f\u201d\u90a3\u4eba\u5e26\u7740\u6068\u610f\u5730\u9053\uff0c\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u6211\u7c98\u4e86\u80e1\u5b50\uff0c\u675f\u8d77\u4e86\u957f\u53d1\uff1f\u8fd8\u662f\u56e0\u4e3a\u90a3\u4e00\u5251\uff0c\u662f\u4f60\u780d\u6211\uff0c\u800c\u4e0d\u662f\u6211\u780d\u4f60\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u77b3\u5b54\u5ffd\u7136\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e00\u573a\u53ae\u6740\u3002 \u3000\u3000\u90a3\u573a\u53ae\u6740\u91cc\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u5c31\u542c\u89c1\u4e00\u4e2a\u6e29\u548c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6211\u4eec\u90fd\u77e5\u9053\uff0c\u6613\u5bb9\u672f\u662f\u9a97\u4e0d\u4e86\u76f8\u719f\u7684\u4eba\u4e0e\u884c\u5bb6\u7684\uff1b\u4f46\u5bf9\u4e0d\u76f8\u719f\u7684\u4eba\u548c\u5916\u884c\uff0c\u81f3\u5c11\u8fd8\u53ef\u4ee5\u4e00\u65f6\u7ba1\u7528\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u56de\u8fc7\u5934\u6765\uff0c\u5c31\u770b\u89c1\u8ffd\u547d\u80cc\u7740\u5df2\u7ecf\u65ad\u4e86\u6c14\u7684\u65ad\u7709\u77f3\uff0c\u8138\u4e0a\u5e26\u7740\u82e6\u7b11\u3001\u773c\u91cc\u900f\u9732\u7740\u70ed\u8bda\uff0c\u6b63\u628a\u8bdd\u8bf4\u4e0b\u53bb\uff1a \u3000\u3000\u201c\u4ed6\u5c31\u662f\u90a3\u4e2a\u62ab\u53d1\u4eba\uff1b\u201d\u8ffd\u547d\u8bf4\uff0c\u201c\u90a3\u4e2a\u5728\u6d1b\u9633\u9053\u4e0a\u8336\u94fa\u4e2d\uff0c\u56e0\u8981\u6697\u6740\u6c60\u65e5\u66ae\u800c\u88ab\u4f60\u65a9\u4e86\u4e00\u5251\u72b9\u672a\u6b7b\u7684\u62ab\u53d1\u4eba\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u8bb6\u5f02\u3002 \u3000\u3000\u4f46\u6ca1\u6709\u592a\u5927\u7684\u9707\u60ca\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u773c\u524d\u7684\u8fd9\u4f4d\u540d\u6355\u8ffd\u547d\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u90fd\u4e00\u5b9a\u6709\u4ed6\u7684\u6df1\u610f\u3001\u6709\u4ed6\u7684\u7406\u7531\u3001\u548c\u6709\u4ed6\u7684\u76ee\u7684\u548c\u539f\u5219\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u8bf4\uff1a\u201c\u4ed6\u5f53\u7136\u4e0d\u662f\u59d3\u62ab\u3002\u201d \u3000\u3000\u8ffd\u547d\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u540d\u5b57\u5f53\u7136\u4e5f\u4e0d\u53eb\u505a\u53d1\u4eba\u3002\u201d \u3000\u3000\u90a3\u4eba\u89e3\u5f00\u4e86\u5934\u53d1\uff0c\u5934\u53d1\u53c8\u62ab\u6563\u4e86\u4e0b\u6765\uff0c\u4ed6\u626a\u53bb\u4e86\u5047\u987b\uff0c\u62e7\u65ad\u4e86\u8170\u5e26\uff0c\u5bbd\u888d\u677e\u8f6f\uff0c\u5c31\u8ddf\u5f53\u65e5\u5728\u6d1b\u9633\u9053\u4e0a\u53ae\u62fc\u7684\u62ab\u53d1\u4eba\uff0c\u5168\u65e0\u4e24\u6837\u4e86\uff1b\u90a3\u4eba\u9053\uff1a\u201c\u6211\u59d3\u6797\uff0c\u540d\u9189\uff0c\u5b57\u8fdc\u7b11\uff0c\u53f7\u4e03\u60c5\u5c45\u58eb\uff0c\u4eba\u79f0\u4e00\u62e9\u6563\u4eba\u3002\u201d \u3000\u3000\u201c\u592a\u591a\u540d\u5b57\uff0c\u4e0d\u662f\u597d\u4e8b\uff0c\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u6211\u5230\u5e95\u8981\u53eb\u4f60\u90a3\u4e00\u4e2a\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u5728\u5f80\u6614\uff0c\u4eba\u4eba\u90fd\u79f0\u4ed6\u4e3a\u6797\u4e09\u516c\u5b50\uff0c\u6797\u8fdc\u7b11\u3002\u201d\u8ffd\u547d\u5411\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u4f60\u8fc1\u6765\u6d1b\u9633\uff0c\u65f6\u95f4\u4e0d\u957f\uff0c\u5bf9\u6d1b\u9633\u6b66\u6797\u65e7\u4e8b\u6240\u77e5\u4e0d\u8be6\uff0c\u4f46\u50cf\u987e\u5144\uff0c\u5c31\u6e05\u695a\u5f97\u5f88\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u8138\u4e0a\u795e\u8272\uff0c\u5341\u5206\u9707\u52a8\u3002 \u3000\u3000\u201c\u539f\u6765\u662f\u6797\u4e09\u516c\u5b50\uff01\u201d\u987e\u4f5b\u5f71\u5f3a\u7b11\u9053\uff0c\u201c\u6709\u5931\u8fdc\u8fce\uff0c\u5c1a\u7948\u6055\u7f6a\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d\u65b9\u90aa\u771f\u611f\u89c9\u5230\u8ffd\u547d\u5e26\u8fd9\u4e2a\u4eba\u6765\uff0c\u662f\u6709\u4e9b\u8bdd\u60f3\u544a\u8bc9\u4ed6\uff0c\u6240\u4ee5\u4ed6\u76f4\u63a5\u7684\u95ee\u3002 \u3000\u3000\u201c\u5341\u516d\u5e74\u524d\uff0c\u6d1b\u9633\u6ca1\u6709\u2018\u56db\u516c\u5b50\u2019\uff0c\u53ea\u6709\u2018\u4e09\u5927\u5e9c\u2019\uff0c\u5373\u662f\u6797\u3001\u56de\u3001\u845b\u4e09\u5bb6\u3002\u201d\u8ffd\u547d\u9053\uff0c\u201c\u56de\u5e9c\u5f53\u7136\u5c31\u662f\u73b0\u5728\u53d8\u6210\u4e86\u2018\u8001\u516c\u5b50\u2019\u7684\u56de\u767e\u5e94\uff0c\u845b\u5bb6\u5219\u662f\u2018\u4e0d\u7720\u5c71\u4eba\u2019\u845b\u5bd2\u706f\u3002\u201d \u3000\u3000\u201c\u6797\u5e9c\u5462\uff1f\u201d\u65b9\u90aa\u771f\u95ee\u3002 \u3000\u3000\u201c\u6797\u51e4\u516c\u3002\u201d \u3000\u3000\u201c\u554a\uff0c\u5927\u6daf\u4e00\u8def\u95fb\u98ce\u8427\uff0c\u6c5f\u6e56\u4e0d\u53ef\u65e0\u6b64\u516c\u2014\u2014\u6797\u51e4\u516c\uff1f\uff01\u201d \u3000\u3000\u201c\u6b63\u662f\u4ed6\u3002\u672c\u6765\u4ed6\u624d\u662f\u6d1b\u9633\u4e16\u5bb6\u4e2d\u6700\u6709\u5b9e\u529b\u7684\u4eba\u3002\u53ef\u662f\uff0c\u540e\u6765\uff0c\u6797\u6c0f\u5bb6\u65cf\u6240\u5efa\u7acb\u7684\u2018\u4e0d\u6101\u95e8\u2019\uff0c\u6743\u529b\u548c\u8d22\u5bcc\uff0c\u5168\u7ed9\u4eba\u74dc\u5206\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u6307\u6e38\u5bb6\u548c\u6c60\u5bb6\uff1f\u201d \u3000\u3000\u201c\u6797\u51e4\u516c\u4e0d\u8be5\u4fe1\u9519\u4e86\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u6c60\u6563\u6728\uff0c\u4e00\u4e2a\u662f\u6e38\u5367\u519c\u3002\u201d\u8ffd\u547d\u60a0\u60a0\u5730\u9053\uff0c\u201c\u4ed6\u4eec\u4e24\u4e2a\uff0c\u90fd\u662f\u6797\u51e4\u516c\u4e00\u624b\u683d\u57f9\u548c\u53d1\u6398\u7684\uff0c\u6e38\u5367\u519c\u8fd8\u5f53\u4e86\u6797\u5e9c\u5927\u603b\u7ba1\uff0c\u6c60\u6563\u6728\u662f\u6797\u51e4\u516c\u7684\u4e49\u5f1f\uff0c\u7ed3\u679c\uff0c\u4ed6\u4eec\u8054\u5408\u8d77\u6765\uff0c\u5728\u4e0a\u6c9f\u901a\uff0c\u5728\u4e0b\u7ea0\u515a\uff0c\u53db\u4e86\u6797\u51e4\u516c\uff0c\u8fd8\u8d76\u5c3d\u6740\u7edd\uff0c\u6740\u4e86\u6797\u51e4\u516c\u5168\u5bb6\uff0c\u706d\u4e86\u2018\u4e0d\u6101\u95e8'\u3002\u201d \u3000\u3000\u201c\u5168\u5bb6\uff1f\u6ee1\u95e8\uff01\u201d \u3000\u3000\u201c\u6797\u51e4\u516c\u6709\u4e09\u5b50\u4e00\u5973\uff0c\u5927\u513f\u5b50\u65e9\u6b7b\uff0c\u4e8c\u5b50\u548c\u6797\u6c0f\u592b\u5987\u5168\u4e27\u547d\u4e86\uff0c\u53ea\u6709\u6797\u4e09\u516c\u5b50\u548c\u5e74\u5e7c\u7684\u59b9\u59b9\uff0c\u4fa5\u5e78\u9003\u51fa\u751f\u5929\uff1b\u201d\u8ffd\u547d\u53f9\u9053\uff1a\u201c\u4e4b\u540e\uff0c\u6e38\u3001\u4ed6\u4e8c\u5bb6\uff0c\u74dc\u5206\u6797\u5bb6\u5929\u4e0b\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e24\u4eba\u5f7c\u6b64\u4e4b\u95f4\uff0c\u53c8\u53d1\u751f\u4e89\u6743\u593a\u5229\uff0c\u6545\u5404\u636e\u5c0f\u78a7\u6e56\u4e0e\u5170\u4ead\uff0c\u4e24\u96c4\u76f8\u5cd9\uff0c\u5f62\u6210\u4e86\u6d1b\u9633\u56db\u5927\u5bb6\u65cf\u7684\u6f2b\u957f\u6597\u4e89\u3002\u201d \u3000\u3000\u201c\u6c60\u5bb6\u4e0e\u6e38\u5bb6\u7be1\u593a\u4e86\u6797\u5bb6\u2018\u4e0d\u6101\u95e8\u2019\u7684\u4e00\u5207\uff0c\u6797\u5bb6\u7684\u4eba\u4e00\u5b9a\u6068\u6b7b\u8fd9\u4e24\u5bb6\u7684\u4eba\u4e86\uff1b\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u53ef\u662f\uff0c\u8fd9\u90fd\u662f\u4ed6\u4eec\u4e0a\u4e00\u4ee3\u7684\u4e8b\uff0c\u73b0\u5728\uff0c\u7406\u4e8b\u7684\u4eba\u90fd\u662f\u4e24\u5bb6\u7684\u540e\u4ee3\uff0c\u6797\u516c\u5b50\u5982\u679c\u8fd8\u4e9f\u4e9f\u4e8e\u590d\u4ec7\uff0c\u662f\u5426\u6709\u6b64\u5fc5\u8981\u5462\uff1f\u51a4\u51a4\u76f8\u62a5\uff0c\u4f55\u65f6\u65b9\u4e86\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u4f60\u7684\u5bb6\u4eba\u88ab\u6740\u4e86\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u5168\u4e0d\u601d\u62a5\u4ec7\uff1f\u770b\u4e0d\u8d77\u522b\u4eba\u62a5\u4ec7\u96ea\u6068\u3002\u529d\u4eba\u4f55\u82e6\u8840\u503a\u8840\u507f\u7684\u4eba\uff0c\u8bf7\u95ee\u95ee\u81ea\u5df1\u826f\u5fc3\uff0c\u600e\u4e48\u56de\u7b54\u8fd9\u53e5\u8bdd\uff1f\u201d\u6797\u8fdc\u7b11\u51b7\u7b11\u7740\u6124\u6012\uff1a\u201c\u4f60\u7684\u6240\u6709\u3001\u6240\u7231\uff0c\u4e3a\u4eba\u6240\u593a\uff0c\u4f60\u4ecd\u5728\u51c4\u98ce\u82e6\u96e8\u3001\u6323\u624e\u6c42\u5b58\uff0c\u90a3\u4e9b\u5bb3\u4f60\u7684\u4eba\u5374\u5728\u4eab\u53d7\u672c\u6765\u5c5e\u4e8e\u4f60\u7684\u5bcc\u8d35\u8363\u534e\uff0c\u800c\u4e14\u8fd8\u4e0d\u653e\u8fc7\u4f60\uff0c\u4f60\u53c8\u4f1a\u6709\u4ec0\u4e48\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u201c\u62a5\u4ec7\uff1b\u201d\u65b9\u90aa\u771f\u76f4\u63a5\u4e86\u5f53\u7684\u8bf4\uff1a\u201c\u6211\u7684\u4eb2\u4eba\uff0c\u4e5f\u521a\u521a\u9047\u5bb3\uff0c\u6211\u4e5f\u4f1a\u66ff\u4ed6\u4eec\u62a5\u4ec7\u3002\u53ea\u662f\uff0c\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u5411\u4ec7\u4eba\u7684\u4e0b\u4e00\u4ee3\u62a5\u590d\uff0c\u90a3\u662f\u4e0d\u662f\u592a\u4e0d\u516c\u5e73\u3001\u592a\u65e0\u7406\u4e86\u4e00\u4e9b\u5462\uff1f\u201d \u3000\u3000\u201c\u8c01\u8bf4\u65e0\u7406\uff01\u201d\u6797\u8fdc\u7b11\u773c\u90fd\u7ea2\u4e86\uff0c\u201c\u6e38\u5367\u519c\u53ea\u662f\u60a3\u5931\u5fc3\u75af\u75c7\uff0c\u5176\u5b9e\u8fd8\u6ca1\u6b7b\uff1b\u6c60\u6563\u6728\u8fd9\u8001\u8d3c\u5012\u6492\u624b\u5f97\u5feb\uff0c\u4e0d\u8fc7\uff0c\u5f53\u5e74\u80cc\u53db\u6211\u7239\u7684\u65f6\u5019\uff0c\u6c60\u5927\u516c\u5b50\u6c60\u65e5\u4e3d\uff0c\u4e5f\u6709\u53c2\u4e0e\u4e8b\u4ef6\uff0c\u6211\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u5929\u516c\u5730\u9053\uff01\u201d \u3000\u3000\u201c\u4f55\u51b5\uff0c\u5c0f\u78a7\u6e56\u662f\u6211\u7684\uff0c\u5170\u4ead\u4e5f\u672c\u662f\u6211\u4eec\u6797\u5bb6\u7684\uff0c\u6211\u8981\u628a\u8fd9\u4e9b\u90fd\u6536\u56de\u6765\uff0c\u8fd9\u624d\u662f\u516c\u5e73\uff01 \u3000\u3000\u8fd9\u624d\u7b97\u5408\u7406\uff01\u201d\u6797\u8fdc\u7b11\u8138\u4e0a\u51fa\u73b0\u4e86\u4e00\u79cd\u51c4\u5389\u7684\u795e\u60c5\uff0c\u201c\u6211\u8981\u4eb2\u773c\u770b\u7740\u6e38\u5bb6\u548c\u6c60\u5bb6\u53d7\u5230\u62a5\u5e94\uff0c\u5bb6\u7834\u4eba\u4ea1\uff0c\u6211\u624d\u7518\u5fc3\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u624d\u7387\u4f17\u4f0f\u51fb\u6c60\u65e5\u66ae\uff1f\u201d \u3000\u3000\u201c\u8981\u6740\u6c60\u65e5\u66ae\u548c\u6e38\u7389\u906e\u7684\u4eba\uff0c\u591a\u4e0d\u80dc\u6570\uff0c\u56db\u516c\u5b50\u4e4b\u95f4\uff0c\u4e5f\u662f\u660e\u4e89\u6697\u6597\uff0c\u6211\u6740\u4ed6\u4eec\uff0c\u662f\u66ff\u5929\u884c\u9053\uff0c\u90a3\u5929\u5728\u8336\u9986\u4f0f\u51fb\u7684\u4eba\uff0c\u90fd\u662f\u4ee5\u524d\u201c\u4e0d\u6101\u95e8\u201d\u7684\u65e7\u90e8\uff0c\u4f46\u6211\u4eec\u7684\u884c\u52a8\u5374\u8ba9\u4f60\u548c\u4ed6\u4e00\u624b\u7834\u574f\u4e86\uff01\u201d\u6797\u8fdc\u7b11\u6307\u7684\u201c\u4ed6\u201d\uff0c\u5f53\u7136\u5c31\u662f\u8ffd\u547d\uff0c\u201c\u4f60\u4eec\u52a9\u7ea3\u4e3a\u8650\uff0c\u591a\u7ba1\u95f2\u4e8b\uff0c\u6709\u671d\u4e00\u65e5\uff0c\u6211\u4e5f\u4f1a\u62a5\u590d\u7684\uff0c\u800c\u4e14\uff0c\u4f60\u8fd9\u6837\u505a\uff0c\u4e5f\u4e00\u6837\u6551\u4e0d\u4e86\u8fd9\u56db\u4e2a\u8150\u8d25\u7684\u4e16\u5bb6\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4e0d\u4f46\u671d\u5ef7\u6743\u5ba6\u5df2\u63d2\u624b\u6b64\u4e8b\uff0c\u8fde\u3002\u795e\u4e0d\u77e5\u3001\u9b3c\u4e0d\u89c9\uff0c\u548c\u2018\u79e6\u660e\u660e\u6708\u6c49\u65f6\u5173\u2019\u4e5f\u51fa\u52a8\u4e86\uff0c\u56db\u516c\u5b50\u4e0d\u4e45\u4e4b\u540e\uff0c\u5c31\u8981\u6210\u4e86\u6b7b\u516c\u5b50\uff01\u201d \u3000\u3000\u6797\u8fdc\u7b11\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ef0\u5929\u72c2\u7b11\u8d77\u6765\uff0c\u957f\u53d1\u4e0d\u4f4f\u7684\u6410\u52a8\u7740\uff0c\u770b\u53bb\u53cd\u800c\u6709\u70b9\u50cf\u5728\u62bd\u6ce3\u3002 \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4e5f\u662f\u2018\u79e6\u660e\u660e\u6708\u6c49\u65f6\u5173\u2019\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u8ffd\u547d\u8bb6\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6c60\u65e5\u66ae\u81ea\u5df1\u63a8\u6d4b\u7684\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4ed6\u7684\u60c5\u62a5\u9519\u8bef\uff0c\u6797\u8fdc\u7b11\u548c\u4ed6\u90a3\u4e00\u73ed\u624b\u4e0b\uff0c\u786e\u662f\u6797\u65cf\u65e7\u90e8\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6c89\u541f\u4e86\u9877\u523b\uff1a\u201c\u6211\u60f3\u6c60\u516c\u5b50\u7684\u6d88\u606f\u662f\u6765\u81ea\u5218\u662f\u4e4b\u7684\u5634\u91cc\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u2018\u6ee1\u5929\u661f\u3001\u4eae\u6676\u6676'\u7684\u4eba\uff0c\u786e\u6709\u4eba\u5230\u4e86\u6d1b\u9633\u57ce\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u662f\u98de\u661f\u5b50\u2026\u2026\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u98de\u661f\u5b50\u5df2\u7ed9\u6211\u6740\u4e86\u3002\u4ed6\u548c\u5999\u624b\u5802\u7684\u4eba\uff0c\u6740\u4e86\u6211\u7239\u7239\u548c\u5f1f\u5f1f\u3002\u201d \u3000\u3000\u8ffd\u547d\u95fb\u8a00\u4e00\u9707\uff0c\u4e00\u65f6\u4e0d\u77e5\u5982\u4f55\u8bf4\u662f\u597d\u3002 \u3000\u3000\u201c\u62a5\u5e94\uff0c\u62a5\u5e94\uff01\u201d\u6797\u8fdc\u7b11\u5728\u4e00\u65c1\u7b11\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u51e0\u4e2a\u624b\u4e0b\uff0c\u522b\u4eba\u6740\u4e86\u4f60\u7684\u4eb2\u4eba\uff0c\u8fd9\u5c31\u662f\u62a5\u5e94\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u4e0d\u605a\u6012\uff0c\u53cd\u95ee\uff1a\u201c\u90a3\u4e48\u2018\u6740\u695a\u2019\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6797\u8fdc\u7b11\u4e00\u6014\uff0c\u60e8\u7b11\u9053\uff1a\u201c\u6740\u2026\u2026\u695a\u2026\u2026\uff1f\u201d \u3000\u3000\u8ffd\u547d\u5728\u65c1\u63d2\u53e3\u9053\uff1a\u201c\u5f53\u5e74\uff0c\u6e38\u5367\u519c\u548c\u6c60\u6563\u6728\u5bc6\u8c0b\u80cc\u53db\u6797\u51e4\u516c\uff0c\u4e0e\u4eba\u7b79\u7b56\u8d77\u4e8b\u7684\u6697\u8bed\uff0c\u4fbf\u662f\u2018\u6740\u695a'\u4e8c\u5b57\u3002\u201d \u3000\u3000\u201c\u6740\u695a\uff1f\u201d\u65b9\u90aa\u771f\u4ecd\u662f\u4e0d\u89e3\uff1a\u201c\u4e3a\u5565\u8981\u7528\u6740\u695a\u4e8c\u5b57\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u2018\u695a\u2019\u5b57\u662f\u2018\u6797\u2019\u5b57\u548c\u2018\u6b63\uff0c\u5b57\u7684\u5408\u5e76\uff0c\u201d\u8ffd\u547d\u9053\uff1a\u201c\u6797\u51e4\u516c\u59d3\u6797\uff0c\u6797\u592b\u4eba\u4e5f\u662f\u6b66\u6797\u82f1\u6770\uff0c\u53eb\u5c91\u6b63\u513f\uff0c\u2018\u6740\u695a'\u4e00\u8bed\uff0c\u6b63\u662f\u8981\u6740\u4ed6\u4eec\u4e24\u4e2a\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5fc3\u4e2d\u4ecd\u6709\u4e9b\u72d0\u7591\uff0c\u4e0d\u7981\u95ee\uff1a\u201c\u2018\u6740\u695a'\u5c31\u53ea\u662f\u8fd9\u4e2a\u610f\u601d\uff1f\u201d \u3000\u3000\u8ffd\u547d\u8038\u8038\u80a9\u3001\u644a\u644a\u624b\uff0c\u9053\uff1a\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u6240\u77e5\u7684\u4e5f\u4ec5\u662f\u90a3\u4e48\u591a\u3002\u2018\u6740\u695a\u2019\u662f\u5f53\u5e74\u6e38\u3001\u6c60\u4e24\u5bb6\u6740\u4e3b\u593a\u6743\u7684\u6697\u53f7\uff0c\u8fd9\u4e24\u4e2a\u5b57\u5374\u53cd\u800c\u6210\u4e86\u6797\u4e09\u516c\u5b50\u90a3\u4e00\u6279\u5ff5\u5ff5\u4e0d\u5fd8\u590d\u8d77\u62a5\u4ec7\u7684\u4ee3\u53f7\uff1a\u2018\u6740\u695a'\u3002\u2018\u4e0d\u6101\u95e8\u2019\u7684\u4eba\uff0c\u4ea6\u6539\u53f7\u4e3a\u2018\u767e\u4ec7\u95e8'\uff0c\u4ee5\u793a\u62a5\u4ec7\u7684\u51b3\u5fc3\uff01\u201d \u3000\u3000\u65b9\u90aa\u771f\u95ee\uff1a\u201c\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u2018\u6740\u695a\u2019\u5374\u5df2\u6210\u4e86\u6d88\u706d\u6c60\u3001\u6e38\u4e8c\u5bb6\u7684\u4e00\u53e5\u53f7\u4ee4\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u201c\u6211\u4ecd\u662f\u6709\u70b9\u4e0d\u660e\u767d\uff1b\u201d\u65b9\u90aa\u771f\u9053\uff0c\u201c\u4f60\u662f\u600e\u4e48\u627e\u7740\u6797\u4e09\u516c\u5b50\u7684\uff1f\u4ed6\u600e\u4e48\u4f1a\u7b54\u5e94\u66ff\u4f60\u5192\u5145\u5b5f\u968f\u56ed\u7684\uff1f\u5b5f\u592a\u5b88\u7684\u8840\u6848\uff0c\u8ddf\u2018\u6740\u695a\u2019\u53c8\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u90a3\u5929\uff0c\u5728\u6d1b\u9633\u9053\u4e0a\u522b\u540e\uff0c\u6211\u9664\u4e86\u8ffd\u67e5\u5b5f\u5927\u5b88\u8840\u6848\u7684\u7591\u51f6\u4e4b\u5916\uff0c\u4fbf\u4e5f\u5bf9\u90a3\u5929\u72d9\u6740\u6c60\u65e5\u66ae\u7684\u523a\u5ba2\u7ec6\u52a0\u52d8\u67e5\u2026\u2026\u201d\u4ed6\u7b11\u4e86\u4e00\u7b11\u9053\uff0c\u201c\u7b97\u662f\u5e78\u8fd0\uff0c\u4e09\u540d\u7591\u72af\uff0c\u90fd\u6765\u4e86\u6d1b\u9633\uff0c\u51cf\u7701\u6211\u4e0d\u5c11\u65f6\u95f4\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4ee5\u4e09\u54e5\u7684\u8feb\u8e2a\u672f\uff0c\u8ffd\u67e5\u51f6\u5acc\u9003\u72af\uff0c\u81ea\u7136\u624b\u5230\u64d2\u6765\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u65b9\u5144\u5f1f\u5c11\u6765\u5632\u7b11\u6211\uff01\u201d \u3000\u3000\u6797\u8fdc\u7b11\u6012\u9053\uff1a\u201c\u6211\u90a3\u65f6\u82e5\u4e0d\u662f\u53d7\u4e86\u4f24\uff0c\u4ed6\u54ea\u91cc\u8ffd\u5f97\u4e0a\u6211\uff01 \u3000\u3000\u8ffd\u547d\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e00\u8def\u8ddf\u8e2a\u6797\u4e09\u516c\u5b50\uff0c\u4ed6\u53d7\u4e86\u4f60\u4e00\u5251\uff0c\u4f24\u5f97\u9887\u91cd\uff0c\u53ea\u597d\u56de\u5230\u6797\u6c0f\u65e7\u90e8\u7684\u5927\u672c\u8425\uff0c\u6211\u4e0d\u52a8\u58f0\u8272\uff0c\u542c\u4ed6\u4eec\u60b2\u6012\u6124\u9a82\uff0c\u624d\u5927\u6982\u731c\u7740\u5927\u6982\uff0c\u4fbf\u73b0\u8eab\u62dc\u89c1\u2014\u2014\u201d \u3000\u3000\u6797\u8fdc\u7b11\u51b7\u54fc\u9053\uff1a\u201c\u8bf4\u7684\u597d\u542c\uff01\u751a\u4e48\u62dc\u89c1\uff01\u4e0d\u8fc7\u662f\u60f3\u64d2\u6211\u7acb\u529f\uff01\u201d \u3000\u3000\u8ffd\u547d\u6c89\u58f0\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u4e5f\u5e76\u65e0\u4ed6\u610f\uff0c\u65e2\u77e5\u6797\u4e09\u516c\u5b50\u662f\u4e3a\u4e86\u62a5\u4ec7\u96ea\u6068\uff0c\u800c\u5c0f\u78a7\u6e56\u4e0e\u5170\u4ead\u7684\u5bb6\u4e1a\uff0c\u4f3c\u4e4e\u4e5f\u771f\u7684\u6765\u5f97\u4e0d\u751a\u5149\u660e\uff0c\u8fd9\u4ef6\u6848\u5b50\u65e2\u4e0d\u662f\u6211\u529e\u7684\uff0c\u6211\u4e5f\u529e\u4e0d\u4e86\uff0c\u6211\u53ea\u60f3\u4ece\u4e2d\u8c03\u89e3\uff0c\u5e0c\u671b\u4ec7\u83ab\u8981\u8d8a\u7ed3\u8d8a\u6df1\uff0c\u6068\u4e0d\u8981\u8d8a\u53d1\u96be\u586b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6797\u4e09\u516c\u5b50\u81ea\u7136\u4e0d\u4f1a\u7b54\u5e94\u3002\u201d \u3000\u3000\u6797\u8fdc\u7b11\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7684\u6df1\u4ec7\uff0c\u5c82\u662f\u4ed6\u4e09\u8a00\u4e24\u8bed\u5316\u89e3\u5f97\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u4e5f\u77e5\u9053\u6211\u5316\u89e3\u4e0d\u4e86\uff0c\u6240\u4ee5\uff0c\u6d1b\u9633\u56db\u516c\u5b50\u7684\u6597\u4e89\uff0c\u6211\u53ea\u597d\u7f6e\u8eab\u4e8b\u5916\uff0c\u53ea\u4e13\u5fc3\u627e\u51fa\u6740\u76c2\u6848\u7684\u51f6\u624b\uff1b\u201d\u8ffd\u547d\u559f\u606f\u9053\uff0c\u201c\u6240\u4ee5\uff0c\u6211\u6c42\u4ed6\u52a9\u6211\u4e00\u4e8b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u95ee\uff1a\u201c\u751a\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u6797\u8fdc\u7b11\u9053\uff1a\u201c\u4ed6\u8981\u6211\u5047\u626e\u5b5f\u968f\u56ed\uff0c\u66ff\u4ed6\u627e\u51fa\u771f\u51f6\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u7709\u5fc3\u4e00\u76b1\uff0c\u53c8\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u975e\u4f60\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u957f\u76f8\u5f88\u6709\u70b9\u50cf\u5b5f\u968f\u56ed\uff0c\u4e0d\u8bba\u662f\u4e0d\u662f\u771f\u51f6\uff0c\u8ddf\u5b5f\u592a\u5b88\u7167\u8fc7\u9762\uff0c\u867d\u7136\u5fc5\u7136\u660e\u767d\uff0c\u771f\u7684\u5b5f\u968f\u56ed\u5df2\u6b7b\u5728\u4ed6\u624b\u4e0a\uff0c\u4f46\u5bf9\u5176\u4ed6\u4e0d\u662f\u51f6\u624b\u7684\u4eba\uff0c\u627e\u4e2a\u6837\u5b50\u9177\u4f3c\u5b5f\u968f\u56ed\u7684\uff0c\u6bd4\u8f83\u594f\u6548\uff0c\u5bf9\u771f\u51f6\u4e5f\u8f83\u80fd\u9020\u6210\u7591\u60d1\uff1b\u201d\u8ffd\u547d\u9053\uff0c\u201c\u4f55\u51b5\u4ed6\u80f8\u9645\u53d7\u8fc7\u4f60\u7684\u5251\u4f24\uff0c\u662f\u4e0d\u662f\u771f\u7684\u53d7\u4f24\uff0c\u8981\u662f\u771f\u7684\u7ec6\u52a0\u67e5\u770b\uff0c\u65ad\u96be\u7792\u8fc7\u884c\u5bb6\uff0c\u987e\u5144\u624b\u8155\u4e0a\u7684\u4f24\uff0c\u8981\u4e0d\u662f\u5feb\u6253\u5feb\u7740\uff0c\u6050\u6015\u4e5f\u9a97\u4e0d\u7740\u77f3\u8001\u5e7a\uff0c\u800c\u4e14\uff0c\u4eca\u5929\u6211\u8bf7\u6797\u4e09\u516c\u5b50\u6765\uff0c\u987a\u4fbf\u4e5f\u8981\u8ba9\u4f60\u591a\u4e86\u89e3\u6709\u5173\u6d1b\u9633\u56db\u516c\u5b50\u7684\u4e00\u4e9b\u5e95\u7ec6\u3002\u800c\u4e14\uff0c\u6211\u8fd8\u6709\u73b0\u5728\u4e0d\u4fbf\u9053\u51fa\u7684\u539f\u7531\u3002\u201d \u3000\u3000\u6797\u8fdc\u7b11\u63a5\u9053\uff1a\u201c\u6211\u7b54\u5e94\u4e86\u4ed6\uff0c\u4f46\u6211\u6709\u6761\u4ef6\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u751a\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u4ed6\u8981\u6211\u4e0d\u53ef\u9053\u51fa\u4ed6\u4eec\u2018\u767e\u4ec7\u95e8\u2019\u7684\u4f1a\u96c6\u4e4b\u5904\uff0c\u8fd9\u70b9\uff0c\u6211\u4e5f\u4e0d\u503c\u5f53\u5e74\u6e38\u3001\u6c60\u4e24\u5bb6\u6240\u4e3a\uff0c\u6797\u51e4\u516c\u6211\u4e5f\u4e00\u5411\u656c\u4ef0\uff1a\u6211\u5f53\u7136\u4e0d\u4f1a\u4e71\u8bf4\u3002\u201d \u3000\u3000\u6797\u8fdc\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u8981\u4ed6\u8d1f\u8d23\u6211\u7684\u5b89\u5371\uff0c\u5e73\u5b89\u8fdb\u51fa\u5c0f\u78a7\u6e56\u3002\u201d \u3000\u3000\u8ffd\u547d\u671b\u5411\u987e\u4f5b\u5f71\uff1a\u201c\u6211\u5df2\u7ecf\u7b54\u5e94\u4ed6\u4e86\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u516c\u5b50\u4e5f\u5b9a\u5fc5\u660e\u767d\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5374\u5411\u6797\u8fdc\u7b11\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u8fd9\u6837\u505a\uff0c\u539f\u56e0\u53ea\u6015\u662f\u4e3a\u4e86\u4e0d\u7ba1\u51f6\u624b\u662f\u8521\u65cb\u949f\u3001\u77f3\u65ad\u7709\u3001\u8fd8\u662f\u4e03\u53d1\u5927\u5e08\uff0c\u4f60\u90fd\u5df4\u4e0d\u5f97\u9664\u53bb\u56db\u5927\u516c\u5b50\u7684\u8eab\u8fb9\u91cd\u5c06\u3002\u201d \u3000\u3000\u6797\u8fdc\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\u3002\u6211\u672c\u5e0c\u671b\u662f\u4e03\u53d1\u7985\u5e08\uff0c\u6211\u66f4\u5e0c\u671b\u5c31\u662f\u987e\u4f5b\u5f71\uff01\u201d \u3000\u3000\u987e\u4f5b\u5f71\u5fae\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u6797\u8fdc\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u3002\u201d \u3000\u3000\u8ffd\u547d\u8fd9\u6b21\u5411\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u770b\u5230\u4e86\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u770b\u5230\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u90a3\u5929\uff0c\u5728\u6d1b\u9633\u9053\u4e0a\uff0c\u6211\u5012\u662f\u529d\u52b1\u8fc7\u65b9\u5144\u5f1f\u4f60\uff0c\u4e0d\u59a8\u4e3a\u6c60\u516c\u5b50\u6548\u529b\uff0c\u53ef\u4ee5\u4e00\u5c55\u9e3f\u56fe\uff0c\u6211\u8bf4\u4e86\u4e4b\u540e\uff0c\u53c8\u6015\u4e0d\u59a5\uff0c\u6240\u4ee5\u5bf9\u6d1b\u9633\u56db\u516c\u5b50\u7684\u5e95\u7ec6\uff0c\u4e5f\u683c\u5916\u7559\u610f\uff0c\u7559\u610f\u7684\u7ed3\u679c\uff0c\u4fbf\u662f\u53d1\u73b0\u4e86\u8fd9\u4e9b\u79cd\u79cd\u7684\u4e8b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8eab\u5728\u6d1b\u9633\u591a\u70e6\u5fe7\uff1b\u201d\u8ffd\u547d\u541f\u9053\uff1a\u201c\u53ea\u6050\u6d1b\u9633\u4e0d\u53ef\u7559\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u8ffd\u547d\u9053\uff1a\u201c\u6211\u7b97\u662f\u66ff\u5b5f\u6848\u7f09\u62ff\u4e86\u771f\u51f6\uff0c\u4f46\u51f6\u624b\u53c8\u88ab\u4eba\u6740\u4e86\uff0c\u6211\u4f1a\u8ffd\u67e5\u4e0b\u53bb\u7684\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u4ecd\u4f1a\u7559\u5728\u6d1b\u9633\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u8ffd\u547d\u6de1\u7709\u4e00\u626c\uff0c\u201c\u4e3a\u751a\u4e48\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u7ecf\u8eab\u5728\u6d1b\u9633\uff0c\u5fc3\u5728\u6d1b\u9633\uff0c\u4e0d\u7ba1\u5584\u6076\u7f8e\u4e11\uff0c\u6211\u90fd\u662f\u5176\u4e2d\u4e00\u4efd\u5b50\uff0c\u6211\u53ea\u80fd\u4e0e\u4e4b\u540c\u6d6e\u5171\u6c89\uff0c\u8d70\u4e0d\u4e86\u4e86\u3002\u201d \u3000\u3000\u8ffd\u547d\u5fae\u5fae\u53f9\u4e86\u4e00\u58f0\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e0d\u8d70\uff1b\u201d\u6797\u8fdc\u7b11\u9510\u58f0\u9053\uff0c\u201c\u6211\u53ef\u662f\u8981\u79bb\u5f00\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4e09\u6355\u7237\u8bf4\u8fc7\u7684\u8bdd\uff0c\u6211\u4eec\u4e00\u5b9a\u4e0d\u4f1a\u4e3a\u96be\u4f60\u7684\u3002\u201d \u3000\u3000\u6797\u8fdc\u7b11\u72e0\u72e0\u5730\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u76ef\u4e86\u65b9\u90aa\u771f\u4e00\u773c\uff0c\u201c\u4f60\u4eec\u8fd9\u5e72\u4e3a\u864e\u4f5c\u6005\u7684\u4e1c\u897f\uff0c\u6211\u4f1a\u518d\u56de\u6765\u7684\u3002\u4f60\u5288\u4e86\u6211\u4e00\u5251\uff0c\u53c8\u6740\u4e86\u6211\u4eec\u4e0d\u5c11\u4eba\uff0c\u4f60\u6b20\u6211\u7684\uff0c\u6211\u4f1a\u8bb0\u4f4f\u7684\uff0c\u2018\u767e\u4ec7\u95e8\u2019\u4e5f\u4f1a\u8bb0\u7740\u7684\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u4f60\u8bb0\u7740\u5427\uff0c\u7b49\u4f60\u6709\u80fd\u529b\u6765\u7b97\u5e10\u7684\u65f6\u5019\uff0c\u5c3d\u7ba1\u6765\u627e\u6211\u7b97\u5e10\u3002\u201d \u3000\u3000\u201c\u6211\u5148\u9001\u6797\u4e09\u516c\u5b50\u56de\u53bb\uff0c\u201d\u8ffd\u547d\u5411\u65b9\u90aa\u771f\u3001\u987e\u4f5b\u5f71\u9053\uff1a\u201c\u6211\u4e5f\u8981\u627e\u6740\u77f3\u65ad\u7709\u7684\u51f6\u624b\uff0c\u4ee5\u53ca\u627e\u51fa\u90a3\u53eb\u77f3\u8001\u5e7a\u5f53\u51f6\u624b\u7684\u4eba\u7b97\u5e10\u3002 \u3000\u3000\u201c\u4e09\u6355\u7237\u653e\u5fc3\uff0c\u201d\u987e\u4f5b\u5f71\u5782\u624b\u7b11\u9053\uff0c\u6211\u4eec\u51b3\u4e0d\u4f1a\u4f7f\u4eba\u8ddf\u7740\u6797\u4e09\u516c\u5b50\u7684\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u8c01\u80fd\u8ddf\u8e2a\u8ffd\u547d\uff1f\u65e0\u7591\u73ed\u95e8\u5f04\u65a7\u3002\u201d \u3000\u3000\u8ffd\u547d\u53cd\u95ee\uff1a\u201c\u90a3\u4f60\u5462\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6211\u56de\u5170\u4ead\u3002\u201d \u3000\u3000\u8ffd\u547d\u770b\u4e86\u4ed6\u4e00\u9635\uff0c\u624d\u8bf4\uff1a\u201c\u4f60\u8138\u4e0a\u6740\u6c14\u5f88\u76db\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u662f\u8981\u56de\u53bb\u6740\u4eba\u7684\uff1b\u201d\u65b9\u90aa\u771f\u9053\uff1a\u201c\u6740\u4e00\u4e2a\u672c\u6765\u8be5\u6b7b\u4f46\u5374\u4e0d\u8be5\u6740\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u6ca1\u542c\u5230\uff1b\u201d\u8ffd\u547d\u7b11\u7740\u4e0e\u6797\u8fdc\u7b11\u542f\u6b65\uff0c\u201c\u6211\u5f53\u4e86\u90a3\u4e48\u591a\u5e74\u6355\u5feb\uff0c\u7b97\u662f\u5b66\u4f1a\u4e86\u4e00\u4ef6\u4e8b\uff1a \u3000\u3000\u6709\u4e9b\u4e0d\u8be5\u770b\u5230\u548c\u542c\u5230\u7684\u4e8b\uff0c\u6211\u5c31\u770b\u4e0d\u89c1\u3001\u542c\u4e0d\u5230\uff0c\u8fde\u4f60\u521a\u624d\u7684\u90a3\u53e5\u6d3b\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u4ed6\u629b\u4e0b\u6765\u6700\u540e\u7684\u4e00\u53e5\u8bdd\u662f\uff1a \u3000\u3000\u201c\u4fdd\u91cd\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u2014\u2014\u4fdd\u91cd\u3002 \u3000\u3000\u5218\u662f\u4e4b\u4e00\u5411\u5f88\u61c2\u5f97\u5982\u4f55\u4fdd\u517b\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u5728\u5170\u4ead\u5ead\u9662\u7684\u7af9\u6797\u5b50\u91cc\uff0c\u5728\u4e24\u682a\u5de8\u7af9\u5e72\u4e0a\u67b6\u8d77\u4e86\u4e00\u5f20\u7ef3\u7ed3\u7684\u5e8a\uff0c\u4ed6\u5c31\u7761\u5728\u4e0a\u9762\uff0c\u9762\u5411\u7740\u5170\u4ead\u7684\u7ea2\u5899\u78a7\u74e6\u3002\u897f\u9662\u7684\u6708\u6d1e\u95e8\uff0c\u6447\u6765\u6643\u53bb\uff0c\u5348\u95f4\u5bc2\u5bc2\uff0c\u53ef\u662f\u70c8\u9633\u7167\u4e0d\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u8749\u58f0\u4f34\u7740\u4ed6\u7684\u601d\u6f6e\u8d77\u4f0f\u2014\u2014 \u3000\u3000\u4ed6\u6b63\u5728\u8ba1\u5212\u7740\uff0c\u5982\u4f55\u8fdb\u4e00\u6b65\u62d3\u5c55\u201c\u5170\u4ead\u6c60\u5bb6\u201d\u7684\u4e8b\u4e1a\u3002 \u3000\u3000\u4ed6\u867d\u7136\u59d3\u5218\uff0c\u4e0d\u59d3\u6c60\uff0c\u5170\u4ead\u867d\u7136\u4ecd\u662f\u6c60\u5bb6\u7684\uff0c\u53ef\u662f\u4ed6\u603b\u89c9\u5f97\uff0c\u5170\u4ead\u8fd9\u5927\u597d\u5e84\u56ed\uff0c\u6709\u4e00\u5929\u53ef\u80fd\u5c31\u662f\u4ed6\u5218\u662f\u4e4b\u7684\u3002 \u3000\u3000\u2014\u2014\u53ef\u4e0d\u662f\u5417\uff1f\u5f53\u5e74\u6797\u51e4\u516c\u72ec\u9738\u4e00\u65b9\uff0c\u7ed3\u679c\uff0c\u4ed6\u7684\u52bf\u529b\u8fd8\u4e0d\u662f\u7531\u4ed6\u7684\u4e24\u4e2a\u5fc3\u8179\u7231\u5c06\u6240\u74dc\u5206\u4e86\uff0c\u5176\u4e2d\u4e00\u4e2a\uff0c\u8fd8\u662f\u4eca\u5929\u6c60\u5bb6\u4e0a\u4e00\u4ee3\u7684\u4e3b\u4eba\u5462\uff01 \u3000\u3000\u5218\u662f\u4e4b\u60f3\u5230\u8fd9\u91cc\uff0c\u5634\u89d2\u4e0d\u7981\u6709\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4f1a\u8fd9\u6837\u505a\u5417\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u6c60\u516c\u5b50\u4e00\u76f4\u91cd\u7528\u4ed6\uff0c\u4e00\u76f4\u5f85\u4ed6\u597d\uff0c\u4ed6\u5c31\u4e0d\u4f1a\u2026\u2026 \u3000\u3000\u2014\u2014\u5982\u679c\u4e0d\u662f\u5462\uff1f \u3000\u3000\u4ed6\u7528\u7eb8\u6247\u6247\u554a\u6247\u7684\uff0c\u5ffd\u7136\u89c9\u5f97\u601d\u7eea\u6709\u4e9b\u4e71\uff0c\u7136\u540e\uff0c\u5ffd\u7136\u7c41\u7c41\u7684\u98d8\u4e0b\u51e0\u53f6\u7af9\u53f6\u6765\u3002 \u3000\u3000\u4ed6\u8eba\u5728\u7ef3\u5e8a\u4e0a\u7684\u8eaf\u4f53\uff0c\u7a81\u7136\u7ef7\u7d27\u4e86\u8d77\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7a81\u7136\u611f\u89c9\u5230\u4e00\u80a1\u6740\u6c14\u3002 \u3000\u3000\u4ed6\u521a\u8981\u50cf\u9192\u72ee\u822c\u5f39\u8d77\uff0c\u7ef3\u5e8a\u5c31\u584c\u4e86\u3002 \u3000\u3000\u4e24\u8fb9\u7684\u7ef3\u7ed3\u4e00\u9f50\u800c\u4e14\u662f\u540c\u65f6\u7684\u65ad\u843d\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u5200\u5149\u5251\u5f71\u90fd\u672a\u66fe\u770b\u89c1\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u5728\u7ef3\u5e8a\u672a\u584c\u524d\u7684\u5239\u90a3\uff0c\u5df2\u501f\u4e86\u529b\uff0c\u98de\u8dc3\u4e0a\u4e00\u68f5\u5de8\u7af9\u5e72\u4e0a\uff0c\u5de6\u624b\u62b1\u4f4f\u7af9\u5b50\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5bdf\u770b\u60c5\u52bf\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u53d1\u73b0\u5728\u4ed6\u624b\u62b1\u7684\u7af9\u5b50\u516b\u4e5d\u5c3a\u5916\uff0c\u4e5f\u6709\u4e00\u4e2a\u4eba\uff0c\u4e00\u624b\u6263\u4f4f\u7af9\u5b50\uff0c\u51b7\u51b7\u7684\u671b\u7740\u4ed6\u3002 \u3000\u3000\u7af9\u5b50\u82cd\u7eff\u3002 \u3000\u3000\u9633\u5149\u628a\u7af9\u5b50\u9876\u7aef\u7684\u7af9\u53f6\uff0c\u7b5b\u5f97\u9ec4\u4eae\u3002 \u3000\u3000\u90a3\u4eba\u7684\u4e00\u8eab\u767d\u8863\uff0c\u4eff\u4f5b\u4e5f\u6620\u7740\u7eff\u610f\u3002 \u3000\u3000\u751a\u81f3\u8138\u8272\u4e5f\u6709\u70b9\u5fae\u7eff\u3002 \u3000\u3000\u5218\u662f\u4e4b\u4e0d\u77e5\u9053\u81ea\u5df1\u73b0\u5728\u7684\u8138\u8272\u600e\u6837\uff0c\u4f46\u7d27\u63e1\u7740\u6298\u6247\u7684\u624b\u6307\uff0c\u7531\u4e8e\u592a\u7528\u529b\u4e4b\u6545\uff0c\u6240\u4ee5\u5448\u4e00\u7247\u9752\u767d\u3002 \u3000\u3000\u90a3\u4eba\u5f53\u7136\u5c31\u662f\u65b9\u90aa\u771f\u3002 \u3000\u3000\u9633\u5149\u4f9d\u65e7\u7af9\u53f6\u9752\u3002 \u3000\u3000\u8749\u58f0\u77e5\u4e86\u3002 \u3000\u3000\u5218\u662f\u4e4b\u5ffd\u7136\u611f\u5230\u9707\u6016\u3002 \u3000\u3000\u4ed6\u611f\u89c9\u5230\u65b9\u90aa\u771f\u662f\u6765\u6740\u4ed6\u7684\u3002 \u3000\u3000\u201c\u4f60\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u6765\u6740\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u6765\u6740\u4f60\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u5ffd\u7136\u89c9\u5f97\u8fc7\u53bb\u4e3a\u5170\u4ead\u6c60\u5bb6\u6240\u505a\u7684\u4e00\u5207\uff0c\u90fd\u662f\u90a3\u4e48\u8352\u8c2c\u53ef\u7b11\u3002 \u3000\u3000\u201c\u4f60\u65e2\u7136\u5df2\u8fdb\u4e86\u6c60\u5bb6\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u56e0\u4e3a\u6211\u8fdb\u4e86\u6c60\u5bb6\uff0c\u6211\u4eec\u884c\u4e8b\u7684\u65b9\u5f0f\u6839\u672c\u4e0d\u540c\uff0c\u76ee\u6807\u5404\u5f02\uff0c\u6211\u4eec\u4e4b\u95f4\uff0c\u8fdf\u65e9\u90fd\u4f1a\u6740\u60bc\u5bf9\u65b9\uff0c\u53ea\u6709\u4e00\u4eba\u80fd\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u5bf9\u3002\u201d\u5218\u662f\u4e4b\u82e6\u7b11\u9053\uff0c\u201c\u8fd9\u8bf4\u6765\u662f\u6211\u81ea\u4f5c\u5b7d\u7684\u7ed3\u679c\u3002\u201d \u3000\u3000\u201c\u65e0\u8bba\u5170\u4ead\u6c60\u5bb6\u600e\u4e48\u53d1\u5c55\uff0c\u4f60\u548c\u6211\u59cb\u7ec8\u90fd\u4f1a\u5f62\u6210\u5bf9\u7acb\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u957f\u4e45\u5bb9\u5f97\u4e0b\u6211\u7684\uff1b\u201d\u65b9\u90aa\u771f\u51b7\u5cfb\u5730\u9053\uff1a\u201c\u4e0e\u5176\u65e5\u540e\u624d\u4e92\u76f8\u6b8b\u6740\uff0c\u4e0d\u5982\u73b0\u5728\u5c31\u51b3\u4e00\u751f\u6b7b\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u60f3\u4e86\u60f3\uff0c\u95ee\uff1a\u201c\u4e0d\u80fd\u53ea\u5b9a\u80dc\u8d1f\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u7528\u7684\uff0c\u201d\u65b9\u90aa\u771f\u575a\u51b3\u5730\u9053\uff1a\u201c\u5982\u679c\u662f\u6211\u8d25\u4e86\uff0c\u4f60\u51b3\u4e0d\u4f1a\u8ba9\u6211\u6d3b\u7740\uff1b\u8981\u662f\u4f60\u8d25\u4e86\uff0c\u4f60\u4e5f\u4e00\u5b9a\u4f1a\u6295\u9760\u522b\u5904\uff0c\u5343\u65b9\u767e\u8ba1\u7684\u6d88\u706d\u6211\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u679c\u7136\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u6211\u771f\u7684\u5e94\u8be5\u529b\u963b\u4f60\u8fdb\u6765\u7684\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u806a\u660e\u5f97\u505a\u9519\u4e86\u522b\u4eba\u53cd\u800c\u4e0d\u4f1a\u505a\u9519\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u5bf9\uff0c\u806a\u660e\u4eba\u6613\u88ab\u806a\u660e\u8bef\uff0c\u201d\u5218\u662f\u4e4b\u6c89\u541f\u4f3c\u7684\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4e00\u6837\uff0c\u8b6c\u5982\uff0c\u4f60\u73b0\u5728\u5c31\u505a\u4e86\u4e00\u4ef6\u5f88\u9519\u7684\u4e8b\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u95ee\uff1a\u201c\u751a\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u542c\u8fc7\u6b66\u6797\u4e2d\u4e00\u4ef6\u7280\u5229\u3001\u9738\u9053\u3001\u53ef\u6015\u7684\u6697\u5668\uff1f\u201d\u5218\u662f\u4e4b\u8138\u4e0a\u6709\u4e00\u4e2a\u8be1\u5f02\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u6697\u5668\uff1f\u201d \u3000\u3000\u201c\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u3002\u201d\u5218\u662f\u4e4b\u624b\u8155\u4e00\u63a3\uff0c\u5df2\u6478\u51fa\u4e86\u4e00\u652f\u94c1\u7b1b\uff0c\u5145\u6ee1\u81ea\u4fe1\u7684\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u9519\u5728\u4e0d\u8be5\u8ba9\u6211\u4eae\u51fa\u8fd9\u6839\u7b1b\u5b50\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u4e00\u5b57\u4e00\u53e5\u5730\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u5c31\u662f\u6839\u5411\u4f60\u7d22\u547d\u7684\u7b1b\u5b50\u3002\u201d \u3000\u3000\u65b9\u90aa\u771f\u5f53\u7136\u89c1\u8fc7\u8fd9\u652f\u94c1\u7b1b\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u201c\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u201d\u7684\u5a01\u529b\u3002 \u3000\u3000\u4ed6\u76ef\u7740\u8fd9\u652f\u7b1b\uff0c\u624b\u6309\u7740\u5251\u628a\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u662f\u4e00\u624b\u62b1\u7740\u7af9\u5e72\uff0c\u9065\u76f8\u5bf9\u7740\uff0c\u76f4\u81f3\u5218\u662f\u4e4b\u7ec8\u4e8e\u7387\u5148\u53d1\u52a8\u3001\u6309\u4e0b\u4e86\u94c1\u7b1b\u673a\u62ec\uff01 \u3000\u3000\u4eba\u751f\u91cc\u5e38\u5e38\u4f1a\u6709\u8fd9\u79cd\u5c40\u9762\uff0c\u4e24\u4e2a\u4eba\u4e0d\u5f97\u5df2\u8981\u4f5c\u4e00\u573a\u5bf9\u51b3\uff0c\u80dc\u7684\u4eba\u5c31\u80fd\u6109\u5feb\u7684\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u867d\u7136\uff0c\u4e5f\u8bb8\u80dc\u7684\u4eba\u6d3b\u5f97\u4e0d\u4e00\u5b9a\u201c\u6109\u5feb\u201d\uff0c\u8d25\u7684\u4eba\u4e5f\u4e0d\u4e00\u5b9a\u5c31\u4e0d\u80fd\u201c\u6d3b\u4e0b\u53bb\u201d\uff0c\u53ef\u662f\uff0c\u4eba\u5728\u4e16\u95f4\uff0c\u6709\u4e9b\u4ed7\uff0c\u603b\u4e0d\u80fd\u4e0d\u6253\uff0c\u4e0d\u80fd\u4e0d\u5206\u80dc\u8d1f\u2014\u2014\u3002 \u3000\u3000\u5218\u662f\u4e4b\u63a2\u8eab\u4e00\u4fef\u3001\u6273\u52a8\u94c1\u7b1b\u4e0a\u673a\u94ae\u7684\u65f6\u5019\uff0c\u65b9\u90aa\u771f\u5df2\u957f\u7a7a\u98de\u63a0\uff0c\u4e00\u5251\u81ea\u4e0a\u800c\u4e0b\u76f4\u5212\uff0c\u5218\u662f\u4e4b\u540e\u9762\u7684\u7af9\u5b50\uff0c\u556a\u5587\u5587\u4e00\u9635\u7206\u54cd\uff0c\u81ea\u4e2d\u76f4\u5206\u4e3a\u4e8c\uff0c\u5207\u88c2\u5904\u5206\u5de6\u53f3\u800c\u5012\u3002 \u3000\u3000\u5218\u662f\u4e4b\u90a3\u4e00\u6309\uff0c\u94c1\u7b1b\u7adf\u6ca1\u6709\u5c04\u51fa\u6697\u5668\uff01 \u3000\u3000\u7af9\u867d\u88c2\u5f00\uff0c\u5218\u662f\u4e4b\u4eba\u4ecd\u8d34\u5728\u7af9\u5e72\u4e0a\uff0c\u4f46\u4ed6\u7684\u4eba\u5374\u4e5f\u6ca1\u4e8b\u3002 \u3000\u3000\u4ed6\u8138\u8272\u5927\u53d8\uff0c\u7acb\u5373\u5f03\u7b1b\uff0c\u6298\u6247\u5d29\u5730\u5f39\u51fa\u5c16\u5203\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e00\u5251\u6ca1\u80fd\u6740\u4e86\u5218\u662f\u4e4b\uff0c\u4e5f\u662f\u4e00\u9707\uff0c\u4e24\u4eba\u8eab\u5b50\u540c\u65f6\u90fd\u843d\u4e86\u4e0b\u6765\uff0c\u5404\u6362\u4e86\u4e00\u62db\uff0c\u4e24\u4eba\u811a\u540c\u65f6\u6cbe\u5730\uff0c\u7af9\u5b50\u4e5f\u5206\u4e24\u723f\u584c\u5728\u5730\u4e0a\uff0c\u7af9\u679d\u7af9\u53f6\uff0c\u626b\u62c2\u8fc7\u4e24\u4eba\u8eab\u4e0a\u8863\u8882\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u7136\u540e\u5218\u662f\u4e4b\u7684\u5589\u5499\u683c\u683c\u4f5c\u54cd\u3002 \u3000\u3000\u4ed6\u4e22\u6389\u4e86\u6298\u6247\uff0c\u75db\u82e6\u7684\u6293\u7740\u54bd\u5589\uff0c\u65b9\u90aa\u771f\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e00\u51fb\u65e0\u529f\uff0c\u4e0d\u8be5\u9a6c\u4e0a\u53bb\u5f03\u4e86\u94c1\u7b1b\u7684\u3002\u65e9\u4e0a\u6211\u5230\u8fc7\u5175\u5668\u623f\uff0c\u51ed\u5175\u5668\u9644\u7740\u7684\u8bb0\u5f55\uff0c\u77e5\u9053\u4f60\u5e38\u501f\u7528\u8fd9\u652f\u6697\u5668\uff0c\u56e0\u800c\u63a8\u6d4b\u4f60\u5728\u6d1b\u9633\u9053\u4e0a\uff0c\u6c60\u4e8c\u516c\u5b50\u9047\u72d9\u4e4b\u65f6\uff0c\u4f60\u867d\u5e26\u4e86\u51fa\u6765\uff0c\u5728\u90a3\u79cd\u5371\u6025\u7684\u60c5\u5f62\u4e0b\uff0c\u5374\u4ecd\u6ca1\u4f7f\u7528\u5b83\uff0c\u5206\u660e\u662f\u5b58\u6709\u81ea\u4fdd\u7684\u79c1\u5fc3\u3002\u8fd9\u94c1\u7b1b\u51e0\u4e4e\u5df2\u6210\u4e86\u4f60\u7684\u4e13\u7528\u54c1\uff0c\u6240\u4ee5\uff0c\u6211\u505a\u4e86\u70b9\u624b\u811a\uff0c\u8ba9\u5b83\u7b2c\u4e00\u6309\u4e0d\u80fd\u53d1\u5c04\uff0c\u7b2c\u4e8c\u6b21\u6309\u5c31\u80fd\u5982\u5e38\u5c04\u51fa\u2018\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u2019\u4e86\uff0c\u53ef\u60dc\u4f60\u2026\u2026\u201d \u3000\u3000\u5218\u662f\u4e4b\u8270\u8f9b\u5730\u9053\uff1a\u201c\u4f60\u6740\u6211\uff0c\u6c60\u65e5\u66ae\u77e5\u4e0d\u2026\u2026\u77e5\u9053\u2026\u2026\uff1f\u201d \u3000\u3000\u65b9\u90aa\u771f\u9053\uff1a\u201c\u77e5\u9053\u6211\u6740\u4eba\uff0c\u4f46\u4e0d\u77e5\u9053\u662f\u4f60\u3002\u201d \u3000\u3000\u5218\u662f\u4e4b\u75db\u82e6\u5f97\u4e94\u5b98\u90fd\u62bd\u6410\u5728\u4e00\u8d77\uff0c\u60e8\u7b11\u4e86\u4e00\u58f0\uff1a\u201c\u6740\u695a\u2026\u2026\u201d\u53c8\u52c9\u529b\u8bf4\uff1a\u201c\u4f60\u2026\u2026\u77e5\u4e0d\u77e5\u9053\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u4e5f\u662f\u2026\u2026\u662f\u6740\u2026\u2026\u201d\u4ed6\u4e00\u9762\u8bf4\uff0c\u5589\u5499\u7684\u4f24\u53e3\u4e0d\u4f4f\u7684\u6ea2\u51fa\u8840\u6765\uff0c\u4f46\u4ed6\u7aed\u529b\u60f3\u628a\u8bdd\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8749\u58f0\u4f3c\u4e4e\u662f\u79bb\u4ed6\u8d8a\u6765\u8d8a\u8fdc\u4e86\u3002 \u3000\u3000\u4ed6\u6ca1\u529e\u6cd5\u628a\u8bdd\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u65b9\u90aa\u771f\u4e5f\u60f3\u542c\u3002 \u3000\u3000\u4ed6\u4e5f\u5f88\u60f3\u77e5\u9053\u5218\u662f\u4e4b\u4e34\u6b7b\u524d\u7a76\u7adf\u60f3\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4e5f\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u8749\u58f0\u9759\u5bc2\u3002 \u3000\u3000\u5218\u662f\u4e4b\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u5218\u662f\u4e4b\u5012\u4e0b\u53bb\u4e4b\u540e\uff0c\u4ed6\u6380\u5f00\u5218\u662f\u4e4b\u7684\u8863\u895f\uff0c\u624d\u77e5\u9053\u4ed6\u8eab\u4e0a\u7a7f\u7740\u91d1\u4e1d\u62a4\u7532\uff0c\u4ed6\u53d1\u51fa\u7b2c\u4e00\u5251\u4e4b\u9645\uff0c\u5218\u662f\u4e4b\u5934\u9888\u524d\u4fef\uff0c\u5251\u5c16\u81ea\u4ed6\u80f8\u895f\u76f4\u5212\u81ea\u5c0f\u8179\uff0c\u867d\u4ecd\u5212\u7834\u4e86\u62a4\u7532\uff0c\u4f46\u5374\u672a\u4f24\u53ca\u76ae\u8089\u3002\u6c60\u65e5\u66ae\u628a\u5f53\u5e74\u6c60\u6563\u6728\u7684\u81f3\u5b9d\u62a4\u8eab\u7532\u4e5f\u4ea4\u7ed9\u4e86\u5218\u662f\u4e4b\uff0c\u5bf9\u4ed6\u793c\u91cd\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u5982\u679c\u5218\u662f\u4e4b\u4e0d\u653e\u5f03\u94c1\u7b1b\uff0c\u518d\u6309\u7b2c\u4e8c\u6b21\uff0c\u65b9\u90aa\u771f\u7eb5\u6740\u5f97\u4e86\u4ed6\uff0c\u4e5f\u8981\u9762\u5bf9\u201c\u4e5d\u5929\u5341\u5730\u3001\u5341\u4e5d\u795e\u9488\u201d\u7684\u53ef\u6016\u5a01\u529b\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u6ca1\u6709\u628a\u63e1\uff0c\u662f\u4e0d\u662f\u80fd\u8eb2\u5f97\u8fc7\u3001\u907f\u5f97\u5f00\u3001\u63a5\u5f97\u4e0b\u3001\u6321\u5f88\u4e86\uff1f \u3000\u3000\u4ed6\u4e00\u9762\u60f3\u7740\uff0c\u4e00\u9762\u53d6\u4e86\u94c1\u7b1b\uff0c\u7528\u62c7\u98df\u4e8c\u6307\u4e00\u6311\u4e00\u631f\uff0c\u628a\u4e00\u7247\u539f\u5148\u5361\u7b1b\u5b54\u95f4\u7684\u6307\u7532\uff0c\u5f39\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u51c6\u5907\u628a\u8fd9\u6839\u94c1\u7b1b\uff0c\u4ea4\u8fd8\u6c60\u65e5\u66ae\u3002 \u3000\u3000\u4ed6\u4e5f\u51c6\u5907\u628a\u81ea\u5df1\u7684\u751f\u547d\u4e0e\u529b\u91cf\uff0c\u4ea4\u7ed9\u5170\u4ead\uff1b\u5170\u4ead\u4e5f\u8bb8\u4e0d\u662f\u4e00\u4e2a\u5341\u5206\u503c\u5f97\u6295\u8eab\u4e4b\u5904\uff0c\u4f46\u552f\u6709\u5c3d\u529b\u6295\u8eab\uff0c\u624d\u6709\u53ef\u80fd\u628a\u5170\u4ead\u5efa\u7acb\u5f97\u66f4\u5b8c\u5584\u65e0\u61be\uff1b\u5176\u5b9e\u653e\u773c\u6d1b\u9633\u57ce\u91cc\uff0c\u4e3e\u76ee\u82cd\u832b\uff0c\u53c8\u6709\u4f55\u5904\u662f\u503c\u5f97\u6295\u8eab\u7684\uff1f\u5c31\u7b97\u5170\u4ead\u53ea\u662f\u4e00\u6c60\u81ed\u6c34\uff0c\u4e5f\u552f\u6709\u6e05\u6c34\u7684\u6ce8\u5165\uff0c\u624d\u80fd\u4f7f\u5b83\u9010\u6e10\u6062\u590d\u6e05\u6f88\u3002 \u3000\u3000\u65b9\u90aa\u771f\u8fd9\u6837\u8d70\u5411\u5170\u4ead\u7684\u7ea2\u5899\u7eff\u5e18\u4e4b\u65f6\uff0c\u8749\u58f0\u53c8\u54cd\u8d77\u6765\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u8d77\u4f0f\u7740\u4e00\u4e9b\u7591\u60d1\u3001\u4e00\u4e9b\u5bfb\u601d\uff1a\u201c\u6740\u695a\u201d\u7a76\u7adf\u662f\u4e0d\u662f\u8ffd\u547d\u6240\u67e5\u5f97\u7684\u610f\u601d\uff1f\u5218\u662f\u4e4b\u4e34\u6b7b\u524d\u5230\u5e95\u662f\u60f3\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u4ed6\u4e34\u6b7b\u524d\u7684\u90a3\u4e00\u53e5\u201c\u6740\u695a\u201d\u53c8\u662f\u4f55\u6240\u6307\uff1f\u4ed6\u6295\u8eab\u5170\u4ead\uff0c\u9762\u5bf9\u5c0f\u78a7\u6e56\u3001\u5999\u624b\u5802\u548c\u5343\u53f6\u5c71\u5e84\u7684\u6597\u4e89\uff0c\u80fd\u591f\u6539\u53d8\u4e9b\u4ec0\u4e48\uff1f\u201c\u767e\u4ec7\u95e8\u201d\u7684\u65e7\u90e8\uff0c\u80fd\u591f\u91cd\u5efa\u201c\u4e0d\u6101\u95e8\u201d\u5417\uff1f\u5230\u5e95\u662f\u8c01\u6740\u6b7b\u7239\u7239\u548c\u7075\u5f1f\u7684\uff1f\u4ed6\u548c\u989c\u5915\u3001\u6c60\u5bb6\u5144\u5f1f\u65e5\u540e\u53c8\u5982\u4f55\u76f8\u5904\uff1f \u3000\u3000\u8fd9\u4e9b\uff0c\u4ed6\u90fd\u8fd8\u6ca1\u6709\u7b54\u6848\u3002 \u3000\u3000\u7b54\u6848\u603b\u662f\u5728\u4eba\u751f\u7684\u524d\u9762\uff0c\u7591\u95ee\u90fd\u7559\u5728\u540e\u5934\u3002 \u3000\u3000\u4ed6\u624b\u8155\u4e0a\u7cfb\u7740\u7684\u84dd\u4e1d\u4e2d\u5fae\u98d8\uff0c\u767d\u8863\u6cbe\u7740\u5fae\u5c18\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\u90a3\u9996\u5fe7\u4f24\u7684\u6b4c\uff0c\u4e0d\u7981\u4f4e\u58f0\u54fc\u7740\uff0c\u8d70\u51fa\u7af9\u6797\u3002 \uff08\u5b8c\uff09"}),showOnHomepage:!0}]}}]);